"use strict";(self.webpackChunkwebsite=self.webpackChunkwebsite||[]).push([[33742],{46556:(e,n,s)=>{s.r(n),s.d(n,{default:()=>a});var t=s(74848);const a=[{name:"\u6708\u5f02\u661f\u90aa",content:(0,t.jsx)(t.Fragment,{children:"\u7b2c\u4e00\u7ae0\u3000\u4eba\u5947\u517d\u5f02 \u3000\u3000\u6708\u534e\u6e05\u7f8e\uff0c\u78a7\u7a7a\u6f84\u96fe\u3002 \u3000\u3000\u7696\u5357\u9ec4\u5c71\uff0c\u59cb\u4fe1\u5cf0\u4e0b\u7684\u5c71\u5d16\u5de8\u77f3\uff0c\u88ab\u6708\u8272\u6240\u6d17\uff0c\u8fdc\u8fdc\u671b\u53bb\uff0c\u76f4\u5982\u9752\u7389\u3002\u8349\u8272\u5982\u82b1\uff0c\u82b1\u8272 \u5982\u73af\uff0c\u6b63\u662f\u9020\u7269\u8005\u7075\u79c0\u7684\u80dc\u5883\u3002 \u3000\u3000\u79cb\u610f\u867d\u5df2\u4fb5\u4eba\uff0c\u4f46\u665a\u98ce\u4e2d\u4ecd\u65e0\u51db\u51bd\u7684\u5bd2\u6c14\uff0c\u5c71\u5761\u4e0b\u9661\u7136\u8e31\u4e0a\u4e00\u6761\u4eba\u5f71\uff0c\u7fbd\u8863\u661f\u51a0\u3001\u4e30\u795e \u51b2\u5937\uff0c\u76ee\u5149\u56db\u5468\u4e00\u8f6c\uff0c\u5ffd\u5730\u56de\u9996\u7b11\u9053\uff1a\u201c\u5b69\u5b50\u4eec\uff0c\u6c5f\u5357\u6c34\u79c0\u5c71\u9752\uff0c\u73b0\u5728\u4f60\u4eec\u53ef\u77e5\u9053\u4e86\u5427\uff0c\u82e5 \u4e0d\u662f\u4e3a\u5e08\u5e26\u4f60\u4eec\u79bb\u5f00\u6346\u67f1\u4e00\u6837\u7684\u5bb6\uff0c\u6050\u6015\u4f60\u4eec\u4e00\u8f88\u5b50\u4e5f\u65e0\u6cd5\u9886\u7565\u8fd9\u4e9b\u4ed9\u5883\u3002\u201d \u3000\u3000\u8bdd\u58f0\u867d\u6e05\u6717\uff0c\u4f46\u7ec6\u7ec6\u542c\u6765\uff0c\u5176\u4e2d\u5374\u6709\u4e00\u79cd\u4ee4\u4eba\u60ca\u5578\u7684\u5bd2\u610f\u3002 \u3000\u3000\u4ed6\u8bdd\u58f0\u4e00\u843d\uff0c\u540e\u9762\u7acb\u523b\u6709\u51e0\u58f0\u4f4e\u4f4e\u7684\u56de\u5e94\u4e4b\u58f0\uff0c\u63a5\u7740\u53c8\u8d70\u4e0a\u4e09\u4e2a\u7a1a\u9f84\u7684\u7ae5\u5b50\uff0c\u68b3\u7740\u51b2\u5929 \u8fab\u5b50\uff0c\u4e00\u773c\u671b\u53bb\uff0c\u4ff1\u662f\u6ee1\u8138\u4f36\u4fd0\u4e4b\u8272\u3002\u516d\u53cc\u773c\u775b\uff0c\u5728\u591c\u8272\u4e2d\u4e00\u7728\u4e00\u7728\u5730\uff0c\u5b9b\u5982\u661f\u5149\u3002 \u3000\u3000\u5176\u4e2d\u4e00\u4e2a\u7a7f\u7740\u9ec4\u8863\u7684\u7ae5\u5b50\uff0c\u76ee\u5149\u671d\u90a3\u63a9\u6620\u5728\u6708\u8272\u4e91\u6d77\u91cc\u7684\u5c71\u5cf0\u4e00\u671b\uff0c\u4e24\u53ea\u660e\u4eae\u7684\u5927\u773c\u775b \u8f6c\u4e86\u4e24\u8f6c\uff0c\u4e5f\u81ea\u5f00\u53e3\u7b11\u9053\uff1a\u201c\u5e08\u7236\uff0c\u4f60\u8001\u4eba\u5bb6\u662f\u4e0d\u662f\u5c31\u4f4f\u5728\u4e0a\u9762\u7684\u5c71\u9876\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u5e26\u5f92\u513f\u5feb \u4e9b\u4e0a\u53bb\uff1f\u8fd9\u91cc\u7684\u98ce\u666f\u867d\u7136\u597d\u770b\uff0c\u53ef\u662f\u7b49\u6211\u4eec\u5b66\u597d\u672c\u9886\uff0c\u518d\u770b\u4e5f\u4e0d\u8fdf\u3002\u201d \u3000\u3000\u90a3\u9053\u4eba\u54c8\u54c8\u4e00\u7b11\uff0c\u7b11\u58f0\u65b9\u4f4f\uff0c\u5ffd\u5730\u9762\u5bb9\u9aa4\u53d8\uff0c\u5fae\u64a9\u9053\u888d\uff0c\u5de6\u624b\u4e00\u63fd\u90a3\u9ec4\u8863\u7ae5\u5b50\uff0c\u53f3\u624b\u5fae \u6284\uff0c\u5c06\u53e6\u4e24\u4e2a\u7ae5\u5b50\u4e5f\u6284\u5728\u6000\u91cc\uff0c\u811a\u5c16\u987f\u5904\uff0c\u5506\u7684\u4e00\u58f0\uff0c\u9880\u957f\u7684\u8eab\u8eaf\uff0c\u500f\u7136\u5411\u5c71\u8def\u5de6\u4fa7\u7684\u4e00\u5904 \u5c71\u5d16\u63a0\u53bb\uff0c\u5bbd\u5927\u7684\u9053\u888d\u51cc\u7a7a\u800c\u821e\uff0c\u5374\u4e0d\u5e26\u4e1d\u6beb\u98ce\u58f0\u3002 \u3000\u3000\u591c\u8272\u672c\u6df1\uff0c\u4e07\u7c41\u4ff1\u5bc2\u3002 \u3000\u3000\u8fd9\u6df1\u5c71\u91cc\u6b64\u523b\u4f3c\u4e4e\u6ca1\u6709\u4efb\u4f55\u58f0\u97f3\uff0c\u4f46\u95fb\u5c71\u98ce\u7c41\u7c41\uff0c\u79cb\u866b\u4f4e\u8bed\u3002 \u3000\u3000\u4f46\u82e5\u4f60\u8033\u529b\u500d\u4e8e\u5e38\u4eba\uff0c\u4f60\u5c31\u53ef\u4ee5\u542c\u51fa\u5df2\u6709\u7b11\u8bed\u4e4b\u58f0\u968f\u98ce\u800c\u6765\uff0c\u800c\u4e14\u6765\u5f97\u6781\u5feb\uff0c\u7728\u773c\u95f4\uff0c \u5df2\u6709\u4e09\u6761\u4eba\u5f71\u63a0\u4e0a\u5c71\u5761\u3002 \u3000\u3000\u5f53\u5148\u4e00\u4eba\uff0c\u4e5f\u662f\u4e00\u4e2a\u5782\u9aeb\u7ae5\u5b50\uff0c\u5374\u7a7f\u7740\u4e00\u88ad\u957f\u886b\uff0c\u50cf\u662f\u4e00\u4e2a\u51db\u4e32\u4e2d\u7684\u7ae5\u751f\uff0c\u4f46\u8eab\u624b\u5374\u751a \u5feb\uff0c\u7adf\u4f3c\u6b66\u529f\u5df2\u9887\u6709\u6839\u57fa\u3002 \u3000\u3000\u540e\u9762\u4e24\u4eba\uff0c\u4e00\u7537\u4e00\u5973\uff0c\u867d\u662f\u98de\u8eab\u6025\u884c\uff0c\u4f46\u6b65\u5c65\u4e4b\u95f4\uff0c\u671b\u4e0a\u53bb\u5374\u662f\u90a3\u6837\u5b89\u95f2\u4ece\u5bb9\uff0c\u7537\u7684\u8eab \u6750\u4e0d\u9ad8\uff0c\u5e74\u7eaa\u5df2\u8fc7\u4e2d\u65ec\uff0c\u4f46\u795e\u91c7\u98de\u626c\uff0c\u7709\u76ee\u4e4b\u95f4\uff0c\u6b63\u6c14\u903c\u4eba\uff0c\u5374\u662f\u4ee4\u4eba\u4e0d\u7981\u4e3a\u4e4b\u5fc3\u6298\u7684\u7537\u5b50 \u6c49\u3002 \u3000\u3000\u5973\u7684\u5927\u7ea6\u4e09\u5341\u5c81\u4eba\uff0c\u4f53\u6001\u5a40\u5a1c\uff0c\u7709\u76ee\u5982\u753b\uff0c\u5de6\u624b\u8f7b\u8f7b\u633d\u4f4f\u90a3\u7537\u5b50\u7684\u53f3\u81c2\uff0c\u7ea4\u8170\u5fae\u626d\uff0c\u4fbf \u5df2\u500f\u7136\u63a0\u8fc7\u4e09\u56db\u4e08\u8fdc\u8fd1\u3002 \u3000\u3000\u8fd9\u4e09\u4eba\u4e00\u63a0\u4e0a\u5c71\u5761\uff0c\u5371\u5d16\u4e0a\u4e00\u5757\u5de8\u5927\u7684\u5c71\u77f3\u540e\u9762\uff0c\u90a3\u7fbd\u8863\u661f\u51a0\u7684\u9053\u4eba\u9762\u4e0a\uff0c\u7acb\u523b\u6cdb\u8d77\u4e00 \u4e1d\u51b7\u6d88\u7684\u7b11\u5bb9\uff0c\u7adf\u4f3c\u9690\u542b\u6740\u673a\u3002 \u3000\u3000\u90a3\u4e2d\u5e74\u6c49\u5b50\u4e00\u63a0\u4e0a\u5c71\u5761\uff0c\u4e5f\u81ea\u653e\u773c\u4e00\u773c\uff0c\u5de6\u624b\u8f7b\u8f7b\u6263\u4f4f\u90a3\u7f8e\u5987\u7684\u7ea4\u624b\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u5c06\u90a3 \u53cc\u6625\u8471\u822c\u7684\u67d4\u83ab\u5f80\u81ea\u5df1\u81c2\u5f2f\u5904\u4e00\u6309\uff0c\u66fc\u58f0\u7b11\u9053\uff1a\u201c\u9ec4\u5c71\u9634\u5cad\u79c0\uff0c\u6708\u534e\u6d6e\u4e91\u7aef\uff0c\u6797\u8868\u660e\u9701\u8272\uff0c \u57ce\u4e2d\u8fd8\u672a\u5bd2\u3002\u201d \u3000\u3000\u97f3\u8282\u9535\u7136\uff0c\u4eba\u8033\u82e5\u9e23\uff0c\u90a3\u7f8e\u5987\u542c\u4e86\uff0c\u5374\u201c\u6251\u54e7\u201d\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u8fd9\u4eba\u603b\u662f\u8fd9\u6837\u5b50\uff0c\u4e0a\u6b21 \u548c\u6606\u4ed1\u638c\u6559\u5bf9\u638c\u65f6\uff0c\u628a\u4eba\u5bb6\u7684\u9547\u5c71\u638c\u6cd5\u5c11\u9633\u516b\u5341\u4e00\u5f0f\u7a0d\u5fae\u53d8\u5316\u4e86\u4e00\u4e0b\uff0c\u5c31\u7528\u6765\u5bf9\u4ed8\u4eba\u5bb6\uff0c\u6c14 \u5f97\u90a3\u4e09\u7075\u8001\u9053\u53d1\u4e0b\u95ed\u5173\u5341\u5e74\u7684\u91cd\u8a93\uff0c\u8bf4\u4e0d\u5b9a\u4ece\u6b64\u545c\u547c\u54c0\u54c9\uff0c\u73b0\u5728\u5979\u68a8\u7a9d\u53c8\u6d45\u6d45\u4e00\u73b0\uff0c\u63a5\u7740\u53c8 \u9053\uff1a\u201c\u5374\u628a\u4eba\u5bb6\u5510\u671d\u5927\u8bd7\u4eba\u541f\u548f\u7ec8\u752b\u4f59\u96ea\u7684\u8bd7\u53e5\uff0c\u6539\u4e86\u6539\u62ff\u6765\u541f\u548f\u8fd9\u9ec4\u5c71\u79cb\u8272\uff0c\u591c\u548f\u9634\u7075\u82e5 \u6709\u77e5\uff0c\u6015\u4e0d\u6253\u4f60\u4e24\u4e2a\u5634\u5df4\u624d\u602a\u3002\u201d \u3000\u3000\u4e24\u4eba\u65b9\u81ea\u7b11\u8bed\uff0c\u5148\u884c\u7684\u90a3\u5782\u7f72\u7ae5\u5b50\u5ffd\u5730\u8f6c\u8fc7\u8eab\u6765\uff0c\u4e00\u5f20\u6e05\u79c0\u633a\u9038\u7684\u5c0f\u8138\u4e0a\uff0c\u7adf\u4f3c\u7565\u663e\u60ca \u614c\u4e4b\u8272\u3002\u90a3\u7f8e\u5987\u89c1\u4e86\uff0c\u5fae\u98a6\u9edb\u7709\uff0c\u95ee\u9053\uff1a\u201c\u957f\u537f\uff0c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u90a3\u53eb\u957f\u537f\u7684\u7ae5\u5b50\uff0c\u4f38\u624b\u671d\u5371\u5d16\u540e\u9762\u4e00\u6307\uff0c\u50cf\u662f\u6709\u4e9b\u60ca\u60f6\u4ed6\u8bf4\u9053\uff1a\u201c\u5988\uff0c\u4f60\u542c\u90a3\u9762\u600e\u4e48\u5ffd \u7136\u4f20\u6765\u8fd9\u4e9b\u58f0\u97f3\uff0c\u662f\u4e0d\u662f\u6709\u4e9b\u5947\u602a\u5440\uff1f\u201d \u3000\u3000\u8fd9\u4e00\u5bf9\u5b9b\u5982\u4e34\u98ce\u7389\u6811\u7684\u58c1\u4eba\u7709\u5934\u5404\u81ea\u5fae\u76b1\uff0c\u679c\u7136\u542c\u5230\u5371\u5d16\u540e\u9762\u8fdc\u8fdc\u7adf\u4f20\u6765\u5404\u79cd\u91ce\u517d\u7684\u5578 \u58f0\uff0c\u751a\u662f\u51c4\u51c9\uff0c\u5374\u53c8\u6781\u4e3a\u7e41\u6742\uff0c\u5176\u4e2d\u8fd8\u50cf\u662f\u6742\u6709\u864e\u8c79\u8c7a\u72fc\u4e4b\u7c7b\u731b\u517d\u7684\u543c\u58f0\uff1a\u5954\u6d8c\u800c\u6765\u3002 \u3000\u3000\u90a3\u4e2d\u5e74\u6c49\u5b50\u7b11\u5bb9\u4fbf\u500f\u7136\u6536\u655b\uff0c\u51dd\u795e\u542c\u4e86\u534a\u664c\uff0c\u4e0d\u7981\u6df9\u9053\uff1a\u201c\u9ec4\u5c71\u867d\u7ef5\u5ef6\u751a\u5e7f\uff0c\u4f46\u8fd9\u7c7b\u731b \u517d\uff0c\u5374\u5e76\u4e0d\u5927\u591a\uff0c\u5c31\u662f\u6709\u51fa\u6765\u89c5\u98df\u7684\uff0c\u4e5f\u662f\u5728\u65e5\u843d\u524d\u540e\uff0c\u800c\u4e14\u8fd8\u662f\u5728\u4e1b\u83bd\u504f\u50fb\u4e4b\u5904\u51fa\u5f79\uff0c\u73b0 \u5728\u5df2\u662f\u591c\u6df1\uff0c\u4e07\u7c41\u65e9\u5e94\u5168\u5bc2\uff0c\u600e\u4f1a\u7a81\u7136\u5982\u6b64\u543c\u53eb\u3002\u201d \u3000\u3000\u6b64\u65f6\u8fd9\u4e09\u4eba\u90fd\u5df2\u8d70\u5230\u90a3\u5371\u5d16\u4e4b\u4e0b\uff0c\u5c31\u90fd\u505c\u4e0b\u811a\u6b65\uff0c\u5371\u5d16\u4e0a\u7684\u90a3\u4e2a\u9053\u4eba\uff0c\u4ee5\u76ee\u793a\u610f\uff0c\u53eb\u90a3 \u4e09\u4e2a\u7ae5\u5b50\u90fd\u5c4f\u4f4f\u58f0\u606f\uff0c\u81ea\u5df1\u5374\u4e0d\u514d\u4e5f\u4e3a\u8fd9\u79cd\u51c4\u51c9\u79bb\u4e71\u7684\u517d\u543c\u4e4b\u58f0\u5927\u611f\u60ca\u5f02\uff0c\u9762\u8272\u4e5f\u81ea\u5f02\u5e38\u51dd \u91cd\u3002 \u3000\u3000\u867d\u6709\u79cb\u51e4\uff0c\u4f46\u5e76\u4e0d\u751a\u5927\uff0c\u54ea\u77e5\u77ac\u606f\u4e4b\u95f4\uff0c\u5d16\u4e0b\u5ffd\u5730\u5c71\u51e4\u5927\u4f5c\uff0c\u547c\u547c\u4f5c\u54cd\uff0c\u98ce\u52bf\u6781\u4e3a\u731b \u4e25\uff0c\u4f46\u662f\u5c71\u5761\u9644\u8fd1\uff0c\u8fd9\u4e9b\u4eba\u7684\u6765\u8def\u4e00\u5e26\uff0c\u5374\u4ecd\u7136\u662f\u98ce\u8f7b\u800c\u67d4\uff0c\u8fde\u6811\u679d\u8349\u6728\u90fd\u6ca1\u6709\u4ec0\u4e48\u5439\u52a8\u7684 \u8ff9\u8c61\u3002 \u3000\u3000\u8fd9\u4e00\u5bf9\u592b\u5987\uff0c\u4e43\u6b66\u6797\u4e2d\u7684\u4e00\u4ee3\u5927\u4fa0\uff0c\u58f0\u540d\u6f2b\u5e03\u5b87\u5185\uff0c\u8fd9\u4e2d\u5e74\u6c49\u5b50\u5353\u6d69\u7136\uff0c\u81ea\u591c\u95ef\u5c11\u6797\u5341 \u516b\u7f57\u6c49\u5802\uff0c\u7b11\u632b\u6606\u4ed1\u638c\u6559\u4e09\u7075\u9053\u4eba\uff0c\u4ee5\u8170\u4e2d\u4e00\u67c4\u7075\u86c7\u8f6f\u5251\uff0c\u6012\u626b\u9ed1\u9053\u4e2d\u58f0\u540d\u8d6b\u8d6b\u7684\u9634\u5c71\u4e09\u5341 \u4e8c\u8235\u4e4b\u540e\uff0c\u5728\u6b66\u6797\u4e2d\u4e45\u5df2\u88ab\u5c0a\u4e3a\u7b2c\u4e00\u9ad8\u624b\u3002 \u3000\u3000\u4ed6\u5e74\u7eaa\u867d\u4e0d\u751a\u5927\uff0c\u4f46\u4fa0\u8e2a\u6240\u53ca\uff0c\u5173\u5185\u5173\u5916\uff0c\u81ea\u5c71\u9ed1\u6c34\uff0c\u659c\u9633\u53e4\u9053\uff0c\u5c0f\u6865\u519c\u820d\uff0c\u5cb1\u5b97\u897f \u79c0\uff0c\u90fd\u65e9\u5df2\u7545\u6e38\u4e00\u904d\uff0c\u81ea\u662f\u4e45\u60ef\u5c71\u884c\uff0c\u6b64\u65f6\u864e\u76ee\u56db\u8f6c\uff0c\u671b\u89c1\u9694\u5761\u90a3\u9762\u5c18\u4e0a\u98de\u626c\uff0c\u6eda\u6eda\u9ad8\u8d77\uff0c \u4e0a\u7a7a\u5929\u8272\uff0c\u5374\u4ecd\u7136\u6708\u534e\u6f84\u78a7\uff0c\u7fa4\u661f\u95ea\u70c1\uff0c\u77e5\u9053\u60c5\u5f62\u6709\u5f02\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u76ee\u5149\u4e00\u51db\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u6b64\u523b\u60c5\u5f62\u5927\u4e0d\u5bfb\u5e38\uff0c\u5c71\u4e2d\u5fc5\u5df2\u751f\u51fa\u5de8\u53d8\uff0c\u6211\u4eec\u4e07\u4e07\u524d\u884c\u4e0d \u5f97\uff0c\u8fd8\u662f\u5148\u627e\u4e2a\u5730\u65b9\uff0c\u89c2\u671b\u4e00\u4e0b\uff0c\u518d\u51b3\u5b9a\u884c\u6b62\u597d\u4e86\u3002\u201d \u3000\u3000\u5c71\u5d16\u4e0a\u7684\u90a3\u9053\u4eba\u5fc3\u4e2d\u4e0d\u7981\u9661\u7136\u4e00\u60ca\uff0c\u6697\u5fd6\u9053\uff1a\u201c\u83ab\u8981\u8fd9\u59d3\u5353\u7684\u4e5f\u63a0\u5411\u8fd9\u91cc\u6765\u2014\u2014\u201d\u54ea\u77e5 \u4ed6\u5ff5\u5934\u5c1a\u672a\u8f6c\u5b8c\uff0c\u5374\u89c1\u8fd9\u4e2d\u539f\u5927\u4fa0\u5353\u6d69\u7136\uff0c\u4e00\u624b\u643a\u7740\u4ed6\u7684\u7231\u5b50\uff0c\u8eab\u5f62\u4e00\u52a8\uff0c\u500f\u7136\u62d4\u8d77\u56db\u4e08\uff0c \u53f3\u624b\u4e00\u62a2\uff0c\u7adf\u5728\u7a7a\u4e2d\u5c06\u4ed6\u7684\u7231\u5b50\u7528\u529b\u9001\u4e0a\u4e86\u81ea\u5df1\u5bf9\u9762\u4e00\u5904\uff0c\u6bd4\u81ea\u5df1\u8eab\u5904\u7684\u8fd9\u5c71\u5d16\u8fd8\u8981\u9ad8\u4e9b\u7684 \u5761\u5934\u4e0a\u53bb\u3002 \u3000\u3000\u8fd9\u4e2d\u539f\u5927\u4fa0\u5353\u6d69\u7136\uff0c\u4ee5\u5185\u529b\u96c4\u539a\u79f0\u8a89\u6b66\u6797\uff0c\u54ea\u77e5\u8f7b\u529f\u5374\u4e5f\u9ad8\u7edd\uff0c\u53f3\u624b\u4e00\u62a1\u4e4b\u540e\uff0c\u8eab\u5f62\u501f \u7740\u8fd9\u4e00\u62a1\u4e4b\u52bf\uff0c\u7adf\u53c8\u4e0a\u5347\u4e09\u4e08\u3002 \u3000\u3000\u7136\u540e\u4ed6\u4e00\u58f0\u957f\u541f\uff0c\u811a\u5c16\u627e\u7740\u5761\u4fa7\u751f\u51fa\u7684\u4e00\u682a\u6811\u679d\u8f7b\u8f7b\u4e00\u70b9\uff0c\u4fbf\u8dc3\u81f3\u5761\u9876\u3002\u8fd9\u4e00\u624b\u5999\u7edd\u4eba \u5bf0\u7684\u51cc\u7a7a\u4e0a\u5929\u68af\uff0c\u4e0d\u4f46\u4f7f\u5f97\u5bf9\u9762\u5c71\u5d16\u4e0a\u5de8\u77f3\u540e\u7684\u90a3\u4e09\u4e2a\u5b69\u5b50\u4e3a\u4e4b\u5931\u8272\uff0c\u9669\u4e9b\u8131\u53e3\u5524\u51fa\u201c\u597d\u201d \u6765\uff0c\u5c31\u662f\u90a3\u4e2a\u7fbd\u8863\u661f\u51a0\u7684\u9053\u4eba\uff0c\u81ea\u8d1f\u8f7b\u3001\u8f6f\u4e4b\u529f\u5929\u4e0b\u65e0\u53cc\uff0c\u4f46\u6b64\u523b\u89c1\u4e86\uff0c\u9762\u4e0a\u4e5f\u4e0d\u7981\u52a8\u5bb9\uff0c \u8d8a\u53d1\u5c4f\u4f4f\u547c\u5438\uff0c\u4e0d\u6562\u53d1\u51fa\u58f0\u6765\u3002 \u3000\u3000\u8fd9\u5353\u6d69\u7136\u4e00\u8dc3\u4e0a\u5761\u5934\uff0c\u7acb\u523b\u4ece\u8170\u95f4\u7684\u4e00\u4e2a\u9769\u56ca\u91cc\u53d6\u51fa\u4e00\u6761\u8f6f\u7d20\u6765\uff0c\u8fce\u51e4\u4e00\u6296\uff0c\u5341\u4f59\u4e08\u957f \u7684\u4e00\u6761\u8f6f\u7d22\u7adf\u4f38\u5f97\u7b14\u76f4\uff0c\u7136\u540e\u4fbf\u671d\u5761\u4e0b\u843d\u53bb\uff0c\u90a3\u7f8e\u5987\u5a07\u8eaf\u5fae\u6298\uff0c\u62d4\u8d77\u4e09\u4e08\uff0c\u521a\u597d\u6293\u4f4f\u8fd9\u8f6f\u7d20 \u7684\u5934\u7aef\u3002 \u3000\u3000\u5353\u6d69\u7136\u5065\u8155\u4e00\u632b\uff0c\u53cc\u624b\u4ea4\u66ff\u7740\u5f80\u4e0a\u62bd\u4e86\u4e24\u4e09\u6b21\uff0c\u90a3\u7f8e\u5987\u4fbf\u4e5f\u5982\u60ca\u9e3f\u822c\u63a0\u4e0a\u5c71\u5761\uff0c\u4e24\u4eba\u4e4b \u95f4\uff0c\u914d\u5408\u5f97\u4e25\u5bc6\u3001\u66fc\u5999\uff0c\u5df2\u81fb\u7edd\u9876\u3002 \u3000\u3000\u8fd9\u79cd\u60ca\u4e16\u9a87\u4fd7\u7684\u6b66\u529f\uff0c\u770b\u5f97\u5bf9\u9762\u5c71\u5d16\u4e0a\u7684\u9053\u5165\u4e0d\u7981\u4e3a\u4e4b\u6697\u53f9\uff0c\u5fd6\u9053\uff1a\u201c\u770b\u6765\u4e0d\u4f46\u8fd9\u4e2a\u59d3 \u5353\u7684\u6b66\u529f\u9ad8\u5f3a\uff0c\u5c31\u8fde\u8fd9\u98de\u51e4\u51f0\u675c\u4e00\u5a18\u4e5f\u540d\u4e0d\u865a\u4f20\uff0c\u4e00\u522b\u591a\u5e74\uff0c\u60f3\u4e0d\u5230\u8fd9\u5bf9\u592b\u5987\u7684\u529f\u592b\u53c8\u589e\u8fdb \u5982\u8bb8\uff0c\u6211\u8fd9\u4e48\u591a\u5e74\u7684\u6614\u5fc3\u5b64\u8be3\uff0c\u96be\u9053\u53c8\u662f\u5168\u90e8\u767d\u8d39\u4e86\u5417\uff1f\u201d \u3000\u3000\u53cc\u7709\u53c8\u8d8a\u53d1\u7d27\u76b1\uff0c\u4f46\u770b\u4e86\u4ed6\u8eab\u4fa7\u7684\u4e24\u4e2a\u5b69\u5b50\u4e00\u773c\uff0c\u5374\u4f3c\u9690\u9690\u6cdb\u51fa\u559c\u8272\u3002 \u3000\u3000\u4f46\u8fd9\u65f6\u517d\u5578\u4e4b\u58f0\uff0c\u6108\u543c\u6108\u5389\uff0c\u4ed6\u4e0d\u7981\u4e5f\u6682\u505c\u601d\u7d22\uff0c\u4fa7\u9996\u5411\u5d16\u4e0b\u671b\u53bb\uff0c\u53ea\u89c1\u524d\u9762\u662f\u4e00\u7247\u9887 \u4e3a\u5bbd\u9614\u7684\u76c6\u5730\uff0c\u5a49\u8712\u68ad\u7740\u4e00\u6761\u53bb\u59cb\u4fe1\u5cf0\u7684\u5c71\u5f84\uff0c\u518d\u8fc7\u53bb\u5c31\u662f\u4e00\u7247\u5c71\u5cad\uff0c\u659c\u659c\u5730\u4f38\u5411\u8fdc\u65b9\uff0c\u4e0d \u4f46\u7ef5\u4e98\u4e0d\u65ad\uff0c\u800c\u4e14\u5176\u4e2d\u5371\u5cf0\u5ced\u58c1\uff0c\u5c71\u52bf\u9ad8\u9661\uff0c\u96c4\u9669\u5f02\u5e38\u3002 \u3000\u3000\u90a3\u8fb9\u7684\u5353\u6d69\u7136\u592b\u5987\uff0c\u9664\u4e86\u8fd9\u4e9b\uff0c\u5374\u8fd8\u770b\u5230\u8fd9\u7247\u5c71\u5d16\uff08\u5c31\u662f\u90a3\u7fbd\u8863\u661f\u51a0\u7684\u9053\u4eba\u5b58\u8eab\u4e4b\u5904\uff09 \u548c\u90a3\u5c71\u5cad\u6210\u5e73\u884c\u4e4b\u52bf\uff0c\u5faa\u77f3\u4f38\u51fa\uff0c\u5bf9\u5761\u4e4b\u5904\uff0c\u5c31\u662f\u5c18\u96fe\u7684\u8d77\u5904\uff0c\u4e00\u9635\u9635\u7684\u65cb\u98ce\uff0c\u5377\u8d77\u5341\u591a\u4e08 \u9ad8\u7684\u5c18\u96fe\uff0c\u7531\u5d16\u8fd9\u8fb9\uff0c\u671d\u5bf9\u9762\u6012\u6d9b\u4f3c\u5730\u9a76\u8fc7\u3002 \u3000\u3000\u6700\u602a\u7684\u662f\uff0c\u8fd9\u98ce\u5c18\u7adf\u4e00\u9635\u63a5\u7740\u4e00\u9635\uff0c\u5954\u6d8c\u4e0d\u5df2\uff0c\u5353\u6d69\u7136\u7684\u7231\u5b50\u957f\u537f\uff0c\u4eca\u5e74\u65b9\u53ea\u5341\u5c81\u5de6 \u53f3\uff0c\u6b64\u523b\u89c1\u72b6\u4e0d\u7981\u6709\u4e9b\u5403\u60ca\uff0c\u95ee\u9053\uff1a\u201c\u7239\u7239\uff0c\u8fd9\u5c71\u98ce\u600e\u5730\u8fd9\u4e48\u5947\u602a\uff1f\u201d \u3000\u3000\u5353\u6d69\u7136\u6d53\u7709\u4e00\u76b1\uff0c\u5374\u8f6c\u8eab\u5411\u4ed6\u7684\u7231\u59bb\u9053\uff1a\u201c\u4e00\u5a18\uff0c\u4f60\u770b\u6e05\u4e86\u6ca1\u6709\uff0c\u60f3\u4e0d\u5230\u5e08\u7236\u6614\u5e74\u5bf9\u6211 \u8bf4\u8fc7\u7684\u8bdd\uff0c\u4eca\u5929\u771f\u7ed9\u6211\u89c1\u7740\uff0c\u73b0\u5728\u867d\u7136\u6211\u8fd8\u62ff\u4e0d\u51c6\uff0c\u4f46\u603b\u4e5f\u516b\u4e5d\u4e0d\u79bb\u5341\u4e86\u3002\u201d \u3000\u3000\u98de\u51e4\u51f0\u675c\u4e00\u5a18\u8fd8\u6ca1\u5341\u5206\u6ce8\u610f\uff0c\u6b64\u523b\u5b9a\u6674\u671b\u53bb\uff0c\u679c\u7136\u770b\u5230\u90a3\u98ce\u5c18\u4e4b\u4e2d\uff0c\u7adf\u7136\u6709\u91ce\u517d\u5728\u5185\uff0c \u5148\u524d\u6240\u8fc7\u7684\uff0c\u6ca1\u6709\u770b\u5230\uff0c\u6b64\u523b\u5374\u662f\u9e7f\u514d\u5c71\u7f8a\u4e4b\u7c7b\uff0c\u767e\u5341\u4e3a\u7fa4\uff0c\u7bad\u4e5f\u4f3c\u7684\u671d\u524d\u9762\u7a9c\u53bb\u3002 \u3000\u3000\u675c\u4e00\u5a18\u4e5f\u662f\u4e45\u8d70\u6c5f\u6e56\u7684\u4fa0\u5973\uff0c\u6b64\u523b\u89c1\u72b6\uff0c\u4e0d\u7981\u76b1\u7709\u95ee\u9053\uff1a\u201c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff0c\u662f\u4e0d\u662f\u90a3\u9762 \u5c71\u6797\u8d77\u706b\uff0c\u53ef\u662f\u5374\u600e\u5730\u6ca1\u6709\u770b\u5230\u706b\u5934\u5462\uff1f\u201d \u3000\u3000\u5353\u6d3d\u7136\u6447\u4e86\u6447\u5934\uff0c\u5374\u6ca1\u6709\u7b54\u8bdd\uff0c\u5353\u957f\u537f\u770b\u5230\u4ed6\u7239\u7239\u9762\u8272\u5982\u6b64\u51dd\u91cd\uff0c\u4e5f\u5c31\u4e0d\u6562\u518d\u95ee\u3002 \u3000\u3000\u653e\u773c\u671b\u53bb\uff0c\u5374\u89c1\u90a3\u8fb9\u5341\u51e0\u9635\u5c18\u5934\u8fc7\u53bb\u4e4b\u540e\uff0c\u8fd8\u672a\u505c\u5f97\u77ac\u606f\uff0c\u540e\u9762\u98ce\u6c99\u53c8\u8d77\uff0c\u5c18\u96fe\u5374\u6bd4\u5148 \u524d\u4f4e\u4e9b\u3002 \u3000\u3000\u4ed6\u518d\u5b9a\u775b\u4e00\u770b\uff0c\u5374\u4e0d\u514d\u4e3a\u4e4b\u60ca\u5524\u51fa\u58f0\u3002 \u3000\u3000\u539f\u6765\u8fd9\u9635\u98ce\u6c99\u91cc\uff0c\u7ade\u662f\u5343\u767e\u6761\u5927\u5c0f\u86c7\u87d2\uff0c\u4e00\u6761\u6761\uff0c\u4ee5\u65e0\u6bd4\u7684\u901f\u5ea6\uff0c\u5339\u7ec3\u4f3c\u7684\u5f80\u524d\u7a9c\u53bb\uff0c \u6709\u7684\u4e94\u8272\u6591\u6593\uff0c\u6709\u7684\u94f6\u5149\u95ea\u95ee\uff0c\u800c\u4e14\u8d8a\u5230\u540e\u9762\uff0c\u86c7\u8eab\u4e5f\u5c31\u8d8a\u957f\u5927\uff0c\u7ade\u6709\u957f\u8fbe\u5341\u4e08\u7684\u3002 \u3000\u3000\u8fd9\u4e9b\u86c7\u87d2\u6fc0\u8d77\u7684\u98ce\u6c99\uff0c\u7adf\u6bd4\u5148\u524d\u91ce\u517d\u884c\u8fc7\u4e4b\u65f6\u8fd8\u76db\uff0c\u6240\u8fc7\u4e4b\u5904\uff0c\u6fc0\u5f97\u5730\u4e0a\u5c18\u96fe\u6d6e\u7a7a\uff0c\u7adf \u50cf\u662f\u4e00\u6761\u6a2a\u4e98\u534a\u5c71\u7684\u7070\u8272\u957f\u8679\u3002 \u3000\u3000\u5353\u957f\u537f\u4f2a\u5e74\u7eaa\u867d\u8f7b\uff0c\u4f46\u81ea\u751f\u4e0b\u4e4b\u540e\uff0c\u88ab\u5176\u7236\u8033\u63d0\u9762\u547d\uff0c\u8fd9\u4e00\u4ee3\u5927\u4fa0\u7684\u7231\u5b50\uff0c\u6b66\u529f\u81ea\u4e5f\u4e0d \u51e1\uff0c\u4e0d\u4f46\u5982\u6b64\uff0c\u800c\u4e14\u6df1\u5177\u4e43\u7236\u7684\u4fa0\u4e49\u4e4b\u98ce\u3002 \u3000\u3000\u6b64\u523b\u89c1\u4e86\u8fd9\u79cd\u60c5\u5f62\uff0c\u5fcd\u4e0d\u4f4f\u9053\uff1a\u201c\u7239\u7239\uff0c\u5c71\u6797\u867d\u7136\u6ca1\u6709\u5931\u706b\uff0c\u5b69\u513f\u770b\u8fd9\u4e00\u5b9a\u662f\u8fd9\u4e9b\u51f6\u6b8b \u7684\u5927\u86c7\uff0c\u53bb\u8ffd\u6740\u90a3\u4e9b\u9a6f\u517d\uff0c\u6240\u4ee5\u624d\u6709\u8fd9\u79cd\u60c5\u51b5\u53d1\u751f\uff0c\u800c\u4e14\u7239\u7239\u5e38\u8bf4\u8fd9\u9ec4\u5c71\u662f\u4e2a\u540d\u5c71\uff0c\u5c71\u4e2d\u7684 \u5bfa\u89c2\u4e00\u5b9a\u5f88\u591a\u3001\u90a3\u4e48\u4e00\u5b9a\u5c31\u6709\u4e00\u4e9b\u50e7\u4eba\u548c\u6a35\u592b\u3002\u8fd9\u4e9b\u5927\u86c7\u76d8\u8e1e\u5728\u8fd9\u91cc\uff0c\u5c82\u975e\u5927\u5bb3\uff0c\u7239\u7239\u4f60\u65e2 \u7136\u8def\u8fc7\u770b\u5230\u4e86\uff0c\u4e0d\u5982\u5c31\u60f3\u6cd5\u5b50\u628a\u5b83\u4eec\u9664\u53bb\u5427\uff01\u201d \u3000\u3000\u8fd9\u5929\u8d44\u7edd\u9876\uff0c\u800c\u53c8\u751f\u5177\u4fa0\u5fc3\u7684\u7ae5\u5b50\uff0c\u4f83\u4f83\u800c\u8a00\uff0c\u4e24\u53ea\u5927\u773c\u775b\uff0c\u7728\u4e5f\u4e0d\u7728\u5730\u671b\u5728\u4ed6\u7239\u7239\u8138 \u4e0a\uff0c\u89c2\u671b\u4ed6\u7239\u7239\u7684\u9762\u8272\u3001\u54ea\u77e5\u5353\u6d69\u7136\u9762\u8272\u94c1\u9752\uff0c\u542c\u4e86\u5374\u6ca1\u6709\u4efb\u4f55\u8868\u793a\uff0c\u6c89\u541f\u4e86\u534a\u664c\uff0c\u5ffd\u7136 \u9053\uff1a\u201c\u6211\u4eec\u518d\u5230\u524d\u9762\u770b\u770b\uff0c\u4e0d\u8fc7\u53ef\u8981\u5c0f\u5fc3\u4e9b\uff0c\u90a3\u4e9b\u86c7\u87d2\uff0c\u4e00\u5b9a\u4ff1\u90fd\u6709\u6bd2\uff0c\u751a\u81f3\u8fd8\u6709\u6bd2\u6c14\u55b7 \u51fa\uff0c\u55c5\u7740\u4e00\u70b9\uff0c\u4fbf\u662f\u4e0d\u5f97\u4e86\u3002\u201d \u3000\u3000\u8bf4\u7740\uff0c\u4ed6\u81ea\u6000\u4e2d\u53d6\u51fa\u4e00\u4e2a\u78a7\u7eff\u8272\u7684\u74f6\u5b50\uff0c\u5012\u51fa\u513f\u7c92\u78a7\u7eff\u8272\u7684\u4e38\u836f\uff0c\u53c8\u9053\uff1a\u201c\u4f60\u4eec\u5c06\u8fd9\u907f \u6bd2\u4e39\uff0c\u5728\u9f3b\u5b54\u91cc\u5404\u585e\u4e0a\u4e00\u7c92\uff0c\u7136\u540e\u518d\u5728\u53e3\u91cc\u542b\u4e00\u7c92\uff0c\u7b49\u4f1a\u5230\u4e86\u524d\u9762\uff0c\u4e5f\u8981\u7559\u5fc3\u4e9b\uff0c\u7ad9\u5f97\u8fdc\u4e00 \u4e9b\u624d\u597d\u3002\u201d \u3000\u3000\u675c\u4e00\u5a18\u76b1\u7740\u7709\uff0c\u8f7b\u58f0\u9053\uff1a\u201c\u90a3\u4e48\u5c31\u53eb\u537f\u513f\u5bb5\u5728\u8fd9\u91cc\u4e0d\u8981\u53bb\u5427\uff0c\u514d\u5f97\u7b49\u4f1a\u513f\u51fa\u4e86\u610f\u5916\u3002\u201d \u3000\u3000\u6148\u6bcd\u5173\u5207\u7231\u5b50\u4e4b\u60c5\uff0c\u6ea2\u4e8e\u8a00\u8868\uff0c\u5353\u6d69\u7136\u671b\u4e86\u671b\u90a3\u5b69\u5b50\u4e00\u773c\uff0c\u5374\u89c1\u4ed6\u6ee1\u8138\u90fd\u662f\u6e34\u671b\u7684\u795e \u60c5\uff0c\u4e25\u5cfb\u7684\u8138\u4e0a\uff0c\u4e0d\u7981\u6cdb\u8d77\u7b11\u5bb9\uff0c\u9053\uff1a\u201c\u537f\u513f\u8fd9\u4e24\u5e74\u6765\u5185\u529f\u8fdb\u5883\u4e0d\u6162\uff0c\u8f7b\u529f\u4e5f\u86ee\u597d\uff0c\u522b\u7684\u4e0d \u8bf4\uff0c\u8981\u9003\u547d\u603b\u8fd8\u53ef\u4ee5\uff0c\u6211\u770b\u5c31\u8ba9\u4ed6\u53bb\u5427\uff0c\u514d\u5f97\u4e00\u4e2a\u4eba\u7559\u5728\u8fd9\u91cc\uff0c\u4e5f\u4e0d\u59a5\u5f53\u3002\u201d \u3000\u3000\u537f\u513f\u542c\u4e86\uff0c\u81ea\u7136\u96c0\u8dc3\u4e09\u4e08\uff0c\u675c\u4e00\u5a18\u62bf\u5634\u4e00\u7b11\uff0c\u4f6f\u55d4\u9053\uff1a\u201c\u4f60\u770b\u4f60\u628a\u4ed6\u60ef\u6210\u8fd9\u526f\u6837\u513f\uff0c\u957f \u5927\u4e86\uff0c\u6015\u4e0d\u53c8\u662f\u4e00\u4e2a\u9b54\u661f\u3002\u201d \u3000\u3000\u5353\u6d69\u7136\u53c8\u81ea\u6717\u58f0\u4e00\u7b11\uff0c\u8fd9\u5c71\u5761\u867d\u7136\u751a\u9661\uff0c\u4f46\u662f\u8fd8\u662f\u7565\u6709\u5761\u5ea6\uff0c\u4ed6\u5f53\u5148\u8dc3\u4e86\u4e0b\u53bb\uff0c\u90a3\u6bcd\u5b50 \u4e24\u4eba\uff0c\u7adf\u4e5f\u80fd\u76f8\u7ee7\u7eb5\u4e0b\u3002 \u3000\u3000\u8fd9\u4e09\u4eba\u7565\u4e00\u505c\u7559\uff0c\u4fbf\u76f8\u7ee7\u671d\u90a3\u5c18\u96fe\u63a0\u8fc7\u4e4b\u5904\u98de\u7eb5\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u90a3\u5c71\u5d16\u4e0a\u7684\u4e09\u4e2a\u5e7c\u7ae5\u624d\u900f\u51fa\u4e00\u53e3\u6c14\uff0c\u53c8\u662f\u90a3\u7a7f\u9ec4\u886b\u7684\u7ae5\u5b50\u9053\uff1a\u201c\u5e08\u7236\uff0c\u90a3\u7236\u5b50\u4e09 \u4e2a\u4eba\u662f\u8c01\uff0c\u6b66\u529f\u600e\u4e48\u90a3\u6837\u9ad8\uff0c\u597d\u50cf\u548c\u5e08\u7236\u5dee\u4e0d\u591a\u561b\uff0c\u90a3\u8fb9\u53c8\u662f\u51fa\u4e86\u4ec0\u4e48\u4e8b\uff0c\u600e\u4e48\u90a3\u4e48\u591a\u7684\u91ce \u517d\u5954\u8fc7\u53bb\u3002\u201d \u3000\u3000\u8fd9\u9ec4\u886b\u7ae5\u5b50\u806a\u660e\u4f36\u4fd0\u4e4b\u8272\u6ea2\u4e8e\u8a00\u8868\uff0c\u90a3\u9053\u4eba\u76b1\u7709\u6697\u601d\uff0c\u5374\u597d\u50cf\u6ca1\u6709\u542c\u5230\u4ed6\u8bb2\u7684\u8bdd\uff0c\u8fc7\u4e86 \u534a\u664c\uff0c\u4ed6\u5ffd\u7136\u4e00\u62cd\u5927\u817f\uff0c\u4f4e\u8bed\u9053\uff1a\u201c\u8fd9\u59d3\u5353\u7684\u81ea\u547d\u4fa0\u4e49\uff0c\u53bb\u62db\u60f9\u90a3\u4e9b\u4e1c\u897f\uff0c\u5927\u6982\u662f\u4ed6\u6d3b\u5f97\u4e0d \u8010\u70e6\u4e86\u3002\u201d \u3000\u3000\u5634\u89d2\u6302\u8d77\u4e00\u4e1d\u51b7\u9177\u7684\u7b11\u610f\uff0c\u50cf\u662f\u5bf9\u90a3\u4e2d\u539f\u5927\u4fa0\u79ef\u6028\u9887\u6df1\u3002 \u3000\u3000\u7136\u540e\uff0c\u4ed6\u53c8\u8f6c\u8fc7\u5934\u53bb\uff0c\u5bf9\u90a3\u4e09\u4e2a\u7ae5\u5b50\u9053\uff1a\u201c\u4f60\u4eec\u5728\u8fd9\u91cc\u5446\u4e00\u4e0b\uff0c\u4e0d\u8981\u52a8\uff0c\u4e3a\u5e08\u8fc7\u53bb\u4e00 \u4e0b\uff0c\u9a6c\u4e0a\u5c31\u53e3\u6765\uff0c\u65e0\u8bba\u9047\u7740\u4ec0\u4e48\u4e8b\uff0c\u5207\u4e0d\u8981\u79bb\u5f00\uff0c\u77e5\u9053\u4e86\u5417\uff1f\u201d \u3000\u3000\u90a3\u9ec4\u886b\u7ae5\u5b50\u201c\u2018\u55ef\u201d\u4e86\u4e00\u58f0\u4e4b\u540e\uff0c\u5374\u53c8\u95ee\u9053\uff1a\u201c\u5e08\u7236\uff0c\u4f60\u662f\u4e0d\u662f\u8981\u53bb\u9664\u6389\u90a3\u4e9b\u6bd2\u866b\uff0c\u4f60 \u8001\u4eba\u5bb6\u653e\u5fc3\u597d\u4e86\uff0c\u65e0\u8bba\u9047\u7740\u4ec0\u4e48\u4e8b\uff0c\u6211\u4eec\u90fd\u4e0d\u4f1a\u79bb\u5f00\u7684\uff0c\u4e00\u5b9a\u7b49\u7740\u4f60\u8001\u4eba\u5bb6\u53e3\u6765\u3002\u201d \u3000\u3000\u9053\u4eba\u51b7\u7b11\u4e86\u4e00\u58f0\uff0c\u672c\u6765\u9887\u4e3a\u6e05\u9038\u7684\u8138\u4e0a\uff0c\u7a81\u7136\u9732\u51fa\u4e00\u80a1\u90aa\u6076\u4e4b\u6c14\uff0c\u51b7\u6d88\u4ed6\u8bf4\u9053\uff1a\u201c\u5b69 \u5b50\uff0c\u4f60\u4eec\u61c2\u5f97\u4ec0\u4e48\uff0c\u8fd9\u4e9b\u86c7\u87d2\u867d\u7136\u51f6\u6bd2\uff0c\u524d\u9762\u53ef\u8fd8\u6709\u6bd4\u5b83\u4eec\u51f6\u6bd2\u5341\u500d\u7684\u4e1c\u897f\uff0c\u8fd9\u4e9b\u86c7\u87d2\u731b\u517d \u8dd1\u5f97\u90a3\u4e48\u5feb\uff0c\u5374\u591a\u534a\u662f\u5f80\u524d\u9762\u9001\u6b7b\u7684\uff0c\u800c\u4e14\u8d8a\u662f\u957f\u5927\u51f6\u72e0\u7684\uff0c\u4e5f\u8bb8\u6b7b\u5f97\u8d8a\u7094\u3002\u201d \u3000\u3000\u8bdd\u5230\u8fd9\u91cc\uff0c\u4ed6\u7a0d\u5fae\u505c\u987f\u4e00\u4e0b\uff0c\u90a3\u9ec4\u8863\u7ae5\u5b50\u7728\u7740\u5927\u773c\u775b\uff0c\u53c8\u95ee\u9053\uff1a\u201c\u771f\u7684\u5417\uff1f\u201d \u3000\u3000\u90a3\u9053\u4eba\u672c\u6765\u5df2\u81ea\u98de\u8eab\u6b32\u53bb\uff0c\u671b\u4e86\u8fd9\u5b69\u5b50\u4e00\u773c\uff0c\u4f3c\u4e4e\u89c9\u5f97\u9887\u4e3a\u559c\u7231\uff0c\u4e8e\u662f\u987f\u4f4f\u8eab\u5f62\uff0c\u9053\uff1a \u201c\u4e3a\u5e08\u4e45\u5c45\u9ec4\u5c71\uff0c\u65e9\u5df2\u770b\u51fa\u90a3\u91cc\u4e00\u4e2a\u7edd\u8c37\u91cc\uff0c\u751f\u6709\u5947\u6bd2\u4e4b\u7269\uff0c\u867d\u7136\u6ca1\u6709\u53bb\u770b\u662f\u4ec0\u4e48\u4e1c\u897f\uff0c\u5927 \u6982\u662f\u4e0a\u53e4\u76d8\u8703\u661f\u870d\u4e00\u7c7b\u7684\u4e1c\u897f\uff0c\u8fd9\u79cd\u4e1c\u897f\u5176\u6bd2\u51a0\u7edd\u5929\u4e0b\uff0c\u6bcf\u9022\u8179\u9965\u601d\u98df\u7684\u65f6\u5019\uff0c\u53ea\u8981\u51e0\u58f0\u602a \u53eb\uff0c\u6216\u662f\u653e\u51fa\u5b83\u7279\u6709\u7684\u6bd2\u6c14\uff0c\u9644\u8fd1\u4e09\u6570\u767e\u91cc\u4e4b\u5185\u7684\u6bd2\u86c7\u731b\u517d\uff0c\u5c31\u4f1a\u4e56\u4e56\u5730\u8dd1\u8fc7\u53bb\uff0c\u4fef\u9996\u9001 \u6b7b\u3002\u201d \u3000\u3000\u90a3\u4e09\u4e2a\u7ae5\u5b50\u542c\u5230\u8fd9\u91cc\uff0c\u4e0d\u7981\u90fd\u7741\u5927\u773c\u775b\uff0c\u9732\u51fa\u60ca\u5f02\u4e4b\u8272\u3002 \u3000\u3000\u90a3\u9053\u4eba\u51b7\u7b11\u4e00\u58f0\uff0c\u53c8\u9053\uff1a\u201c\u6bcf\u5f53\u4e00\u4e2a\u5730\u65b9\u6bd2\u866b\u86c7\u87d2\u7e41\u6b96\u592a\u591a\u7684\u65f6\u5019\uff0c\u5c31\u4f1a\u6709\u8fd9\u4e48\u4e00\u4e2a\u602a \u7269\u51fa\u6765\uff0c\u7ed9\u5b83\u4e00\u626b\u800c\u5149\uff0c\u5403\u5b8c\u4e86\u8fd9\u9053\u4eba\u53c8\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u8fd9\u4e9b\u4e1c\u897f\u4ee5\u6bd2\u653b\u6bd2\uff0c\u81ea\u76f8\u6b8b\u6740\uff0c \u53c8\u5173\u6211\u4ec0\u4e48\u4e8b\uff0c\u6211\u53c8\u4f55\u5fc5\u5192\u7740\u4e07\u96be\u53bb\u9664\u6389\u5b83\u4eec\uff0c\u8fd9\u4e9b\u4e8b\u81ea\u7136\u6709\u90a3\u4e9b\u81ea\u547d\u4e0d\u51e1\u7684\u8822\u624d\u53bb\u505a\uff0c\u4eba \u4e0d\u72af\u6211\uff0c\u6211\u4e0d\u72af\u4eba\uff0c\u4f46\u662f\u53ea\u8981\u6709\u4eba\u751f\u4e8b\u5f97\u7f6a\u5230\u4e3a\u5e08\u5934\u4e0a\uff0c\u90a3\u4e48\u4ed6\u5c31\u7b97\u4e09\u5934\u516d\u81c2\uff0c\u4e5f\u9003\u4e0d\u51fa\u4e3a \u5e08\u7684\u624b\u91cc\u3002\u201d \u3000\u3000\u90a3\u9ec4\u8863\u7ae5\u5b50\u201c\u55ef\u201d\u4e00\u58f0\uff0c\u4ed6\u5e74\u5c81\u5c1a\u5e7c\uff0c\u5f53\u7136\u5206\u4e0d\u6e05\u90aa\u6b63\uff0c\u53ea\u89c9\u4ed6\u5e08\u7236\u7684\u8bdd\u867d\u7136\u548c\u81ea\u5df1\u5e7c \u65f6\u6240\u8bfb\u7684\u5723\u8d24\u4e4b\u4e66\u5927\u76f8\u5f84\u5ead\uff0c\u4f46\u542c\u6765\u5374\u75db\u5feb\u5f97\u5f88\uff0c\u8138\u4e0a\u66f4\u662f\u9732\u51fa\u4e0d\u80dc\u94a6\u670d\u7684\u795e\u8272\u6765\u3002 \u3000\u3000\u8fd9\u9053\u4eba\u76ee\u5149\u626b\u8fc7\uff0c\u9887\u4e3a\u6ee1\u610f\u7684\u4e00\u7b11\uff0c\u4f38\u624b\u8f7b\u8f7b\u629a\u6478\u4e86\u4e00\u4e0b\u8fd9\u9ec4\u8863\u7ae5\u5b50\u7684\u9888\u9879\uff0c\u53c8\u5631\u5490\u4e86 \u4e00\u53e5\uff0c\u9053\u888d\u98d8\u5904\uff0c\u4eba\u4e5f\u5728\u5d16\u4e0a\u671d\u90a3\u8fb9\u63a0\u53bb\u3002 \u3000\u3000\u4ed6\u8eab\u5f62\u52a8\u5904\uff0c\u7adf\u5b9b\u5982\u4e00\u9053\u8f7b\u70df\uff0c\u8f7b\u8eab\u4e4b\u672f\uff0c\u679c\u7136\u5df2\u53ef\u8c13\u4e4b\u767b\u5cf0\u9020\u6781\uff0c\u51e0\u4e2a\u8d77\u843d\u4e4b\u540e\uff0c\u4ed6 \u5ffd\u7136\u987f\u4f4f\u8eab\u5f62\uff0c\u4e5f\u4ece\u6000\u4e2d\u638f\u51fa\u51e0\u7c92\u4e39\u836f\u653e\u5728\u5634\u91cc\u9762\uff0c\u7136\u540e\u76ee\u5149\u56db\u626b\uff0c\u5ffd\u53c8\u8eab\u5f62\u659c\u63a0\uff0c\u9000\u5230\u5d16 \u8fb9\u7684\u4e00\u5904\u7a81\u51fa\u7684\u5c71\u77f3\u4e4b\u540e\uff0c\u9732\u51fa\u534a\u8fb9\u9762\u5b54\u671d\u524d\u9762\u7aa5\u89c6\u3002 \u3000\u3000\u539f\u6765\u5353\u6d69\u7136\u592b\u59bb\u3001\u7236\u5b50\u4e09\u4eba\uff0c\u63a0\u5230\u524d\u9762\u540e\uff0c\u4e5f\u7a9c\u5230\u8fd9\u7247\u5c71\u5d16\u4e0a\u3002 \u3000\u3000\u5353\u6d69\u7136\u4e4b\u5e08\uff0c\u6b63\u662f\u767e\u5341\u5e74\u6765\uff0c\u6c5f\u6e56\u4e0a\u7d20\u6709\u7b2c\u4e00\u5947\u4eba\u4e4b\u8a89\uff0c\u98ce\u4f20\u5df2\u6210\u4e0d\u6b7b\u4e4b\u8eab\u7684\u5730\u4ed9\u53e4 \u9cb2\u3002 \u3000\u3000\u5e87\u4eba\u4e0d\u4f46\u529f\u53c2\u9020\u5316\uff0c\u800c\u4e14\u5b66\u7a76\u5929\u4e0b\uff0c\u5353\u6d69\u7136\u867d\u56e0\u5929\u6027\u6240\u9650\uff0c\u9664\u4e86\u6b66\u529f\u4e4b\u5916\uff0c\u53e4\u9cb2\u8001\u4eba\u522b \u7684\u7edd\u5b66\uff0c\u4ed6\u5e76\u6ca1\u6709\u5b66\u5f97\u4ec0\u4e48\uff0c\u4f46\u662f\u591a\u5e74\u6765\u8033\u6fe1\u76ee\u67d3\uff0c\u4ed6\u89c1\u8bc6\u81ea\u4e5f\u8d85\u4eba\u4e00\u7b49\uff0c\u6b64\u65f6\u89c1\u4e86\u8fd9\u79cd\u60c5 \u51b5\uff0c\u4e5f\u5df2\u6d4b\u51fa\u4e00\u4e2a\u5927\u6982\u6765\uff0c\u5374\u4e5f\u548c\u90a3\u9053\u4eba\u6240\u89c1\u76f8\u540c\u3002 \u3000\u3000\u6b64\u523b\uff0c\u86c7\u7fa4\u5df2\u8fc7\uff0c\u4ed6\u65b9\u5c06\u8fd9\u4e9b\u548c\u4ed6\u59bb\u3001\u5b50\u8bf4\u4e86\uff0c\u5ffd\u7136\u542c\u5230\u8fdc\u8fdc\u53c8\u8d77\u4e86\u4e00\u9635\u5bb9\u5949\u722c\u6c99\u4e4b \u58f0\uff0c\u63a5\u7740\u7fa4\u54cd\u9a9a\u7136\uff0c\u98d8\u98d8\u4e4b\u58f0\uff0c\u500f\u7136\u800c\u8d77\u3002 \u3000\u3000\u4ed6\u4eec\u4e09\u4eba\u7684\u7acb\u5904\uff0c\u5c31\u5728\u9053\u65c1\u7684\u5c71\u5d16\u4e4b\u4e0a\uff0c\u4e0b\u9762\u7684\u6742\u8349\uff0c\u672c\u751a\u7e41\u8302\uff0c\u4f46\u56e0\u7ecf\u8fc7\u4e86\u65b9\u624d\u90a3\u4e00 \u9635\u86c7\u517d\u7684\u8df5\u8e0f\uff0c\u5df2\u538b\u6210\u4e00\u6761\u9a70\u8def\uff0c\u800c\u4e14\u6709\u4e9b\u5730\u65b9\uff0c\u8349\u5df2\u67af\u9ed1\uff0c\u81ea\u7136\u662f\u56e0\u4e3a\u88ab\u4e00\u4e9b\u6bd2\u86c7\u7684\u6bd2\u6d8e \u6240\u67d3\u800c\u81f4\u3002 \u3000\u3000\u6b64\u523b\u5f02\u58f0\u518d\u8d77\uff0c\u4ed6\u4eec\u5faa\u58f0\u4e00\u770b\uff0c\u7adf\u6709\u8bb8\u591a\u8708\u86a3\uff0c\u5212\u884c\u5982\u98de\uff0c\u767e\u5341\u6210\u7fa4\u800c\u6765\uff0c\u5176\u4e2d\u6700\u5927 \u7684\uff0c\u513f\u8fbe\u4e24\u4e09\u5c3a\uff0c\u6602\u9996\u5f20\u94b3\uff0c\u76ee\u5c04\u91d1\u78a7\u4e4b\u5149\uff0c\u8eab\u4e0a\u88ab\u6708\u5149\u6240\u6620\uff0c\u66f4\u95ea\u7740\u6781\u4e3a\u4e11\u6076\u800c\u96be\u4ee5\u5f62\u5bb9 \u7684\u8272\u5f69\uff0c\u7adf\u50cf\u662f\u4e00\u7247\u9526\u4e91\uff0c\u8d34\u7740\u5730\u9762\u500f\u5730\u98de\u6765\u3002 \u3000\u3000\u675c\u4e00\u5a18\u53ea\u89c9\u4e00\u822c\u5bd2\u610f\uff0c\u81ea\u80cc\u810a\u76f4\u900f\u524d\u80f8\uff0c\u4e0d\u7981\u7d27\u7d27\u4f9d\u504e\u5728\u4ed6\u4e08\u592b\u80f8\u524d\uff0c\u67d4\u8351\u4e5f\u88ab\u5353\u6d3d\u7136 \u7d27\u7d27\u63e1\u5728\u4ed6\u90a3\u5bbd\u5927\u7684\u624b\u638c\u91cc\u3002 \u3000\u3000\u5353\u6d69\u7136\u53ea\u89c9\u5f97\u4ed6\u7231\u59bb\u638c\u5fc3\u6ee1\u662f\u51b7\u6c57\uff0c\u4e0d\u7981\u5b89\u6170\u7684\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e00\u5a18\uff0c\u522b\u6015\u3002\u201d \u3000\u3000\u53c8\u7d27\u7d27\u63e1\u4e86\u63e1\u624b\u638c\uff0c\u76ee\u5149\u52a8\u5904\uff0c\u5374\u89c1\u5353\u957f\u537f\u8138\u4e0a\u7adf\u6ca1\u6709\u534a\u4e1d\u60e7\u5bb9\uff0c\u4e0d\u7981\u5e26\u7740\u4e9b\u5b89\u6170\uff0c\u53c8 \u5e26\u7740\u4e9b\u8d5e\u8bb8\u5730\u5fae\u7b11\u4e00\u4e0b\u3002 \u3000\u3000\u8708\u86a3\u8fc7\u540e\uff0c\u540e\u9762\u8ddf\u7740\u6765\u7684\u7adf\u662f\u4e00\u7fa4\u874e\u5b50\uff0c\u591a\u534a\u662f\u7070\u8272\u7684\uff0c\u524d\u9762\u6447\u7740\u94c1\u53c9\u822c\u7684\u957f\u94b3\uff0c\u5c3e\u540e \u6bd2\u94a9\u4e0a\u7fd8\uff0c\u4e5f\u662f\u6210\u7fa4\u671d\u524d\u98de\u63a0\u3002 \u3000\u3000\u874e\u5b50\u8fc7\u540e\uff0c\u7adf\u8fd8\u6709\u5b88\u5bab\u3001\u58c1\u864e\u4e4b\u7c7b\u7684\u6bd2\u7269\uff0c\u4e5f\u662f\u5982\u98de\u822c\u5730\u63a0\u8fc7\u3002 \u3000\u3000\u86c7\u7fa4\u8fc7\u540e\uff0c\u672c\u6765\u5c18\u96fe\u5c31\u672a\u6d88\uff0c\u518d\u7ecf\u8fd9\u4e9b\u8708\u86a3\u874e\u5b50\u7b49\u5947\u6bd2\u7684\u6076\u866b\u63a0\u8fc7\uff0c\u6f2b\u5929\u96fe\u5f71\u4e2d\uff0c\u53c8\u6dfb \u4e0a\u4e1d\u4e1d\u7f15\u7f15\u7684\u7eff\u70df\u5f69\u6c14\uff0c\u5189\u5189\u800c\u5347\u3002 \u3000\u3000\u8fdc\u8fdc\u671b\u53bb\uff0c\u4f46\u89c9\u6f2b\u5929\u745e\u6c14\u6c24\u6c32\uff0c\u4f46\u5374\u4e0d\u77e5\u8fd9\u4e9b\u90fd\u662f\u8981\u547d\u7684\u6bd2\u6c14\u5462\u3002 \u3000\u3000\u5353\u6d69\u7136\u592b\u59bb\u3001\u7236\u5b50\u4e09\u4eba\u7684\u7acb\u5904\uff0c\u867d\u7136\u5f88\u9ad8\uff0c\u800c\u4e14\u8ddd\u79bb\u90a3\u4e9b\u86c7\u866b\u7684\u96fe\u9635\uff0c\u8fd8\u6709\u5341\u4f59\u4e08\u8fdc \u8fd1\uff0c\u4f46\u6b64\u523b\u5df2\u4e0d\u65f6\u95fb\u5230\u6bd2\u8165\u4e4b\u6c14\u6251\u9f3b\u800c\u6765\uff0c\u5934\u8111\u7adf\u7136\u5df2\u89c9\u5f97\u6709\u70b9\u53d1\u95f7\u548c\u60f3\u5455\u5410\u7684\u611f\u89c9\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u96fe\u6c14\u5947\u6bd2\uff0c\u8fdc\u5904\u5df2\u662f\u5982\u6b64\uff0c\u8fd8\u662f\u65e9\u5df2\u542b\u6709\u6781\u7075\u5999\u7684\u907f\u6bd2\u4e39\u4e38\uff0c\u5982\u679c\u8eab\u5728\u8fd9\u6bd2\u96fe\u4e4b \u4e2d\uff0c\u60f3\u5fc5\u5b9a\u7136\u662f\u51f6\u591a\u5409\u5c11\u3002 \u3000\u3000\u5353\u6d69\u7136\u4f4e\u5934\u601d\u5fd6\u4e86\u534a\u664c\uff0c\u7b49\u90a3\u5404\u7c7b\u5947\u6bd2\u7684\u86c7\u866b\u5168\u90fd\u8fc7\u5c3d\uff0c\u6f2b\u5929\u6c16\u6c22\u7684\u6bd2\u96fe\uff0c\u4e5f\u6d88\u6c89\u4e86\u5341 \u4e4b\u4e03\u516b\uff0c\u624d\u4fa7\u76ee\u6c89\u58f0\u9053\uff1a\u201c\u4e00\u5a18\uff0c\u8fd9\u4e9b\u6bd2\u866b\u867d\u7136\u5b8c\u5168\u96be\u9003\u52ab\u6570\uff0c\u4f46\u5269\u4e0b\u7684\uff0c\u5fc5\u5b9a\u8fd8\u5b58\u751a\u591a\uff0c \u4e5f\u96be\u514d\u4e3a\u7978\u4eba\u7c7b\uff0c\u800c\u4e14\u8e1e\u4f0f\u5728\u524d\u9762\u8c37\u4e2d\u7684\u6bd2\u7269\uff0c\u53c8\u4e0d\u77e5\u662f\u4ec0\u4e48\uff0c\u4f46\u613f\u5b83\u5927\u56bc\u8fc7\u540e\uff0c\u50cf\u5e08\u7236\u6240 \u8bf4\uff0c\u80fd\u957f\u7720\u4e0b\u9192\uff0c\u90a3\u4e48\u6211\u5c31\u53ef\u4ee5\u76f8\u673a\u9664\u53bb\uff0c\u4e5f\u4e3a\u4e16\u95ee\u9664\u4e00\u5927\u5bb3\u3002\u201d \u3000\u3000\u4ed6\u8bed\u58f0\u4e00\u987f\uff0c\u95ea\u8574\u795e\u5149\u7684\u53cc\u76ee\uff0c\u5728\u4ed6\u7231\u59bb\u7231\u5b50\u7684\u9762\u4e0a\u4e00\u626b\u3002 \u3000\u3000\u7136\u540e\u4ed6\u4fbf\u53c8\u8bf4\u9053\uff1a\u201c\u4f46\u662f\u65e0\u8bba\u5982\u4f55\uff0c\u6b64\u884c\u603b\u662f\u6781\u4e3a\u51f6\u9669\uff0c\u6211\u53c8\u4e0d\u80fd\u5750\u89c6\u4e0d\u7406\uff0c\u4f60\u548c\u537f\u513f \u6700\u597d\u7559\u5728\u8fd9\u91cc\uff0c\u6211\u5faa\u7740\u8fd9\u4e9b\u6bd2\u86c7\u6240\u7ecf\u4e4b\u8def\u524d\u53bb\u770b\u770b\u3002\u201d \u3000\u3000\u675c\u4e00\u5a18\u5c06\u5979\u4e08\u592b\u7684\u624b\u6293\u5f97\u66f4\u7d27\uff0c\u5e26\u7740\u60f6\u6025\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4f60\u4e00\u4e2a\u4eba\u53bb\u6050\u6015\u4e0d\u884c \u5427\uff0c\u6211\u2014\u2014\u6211\u53c8\u6709\u4e9b\u5bb3\u6015\uff0c\u524d\u9762\u90a3\u6bd2\u7269\u4f60\u65e2\u7136\u8bf4\u5f97\u90a3\u4e48\u5389\u5bb3\uff0c\u4f60\u53bb\u4e86\uff0c\u4e07\u4e00\u6709\u4ec0\u4e48\u2014\u2014\u201d\u5979 \u8bdd\u672a\u8bf4\u5b8c\uff0c\u5353\u6d69\u7136\u5df2\u5fae\u5fae\u4e00\u7b11\uff0c\u622a\u4f4f\u4e86\u5979\u7684\u8bdd\uff0c\u67d4\u58f0\u8bf4\u9053\uff1a\u201c\u4e00\u5a18\uff0c\u4f60\u8bf4\u8fd9\u4e9b\u8bdd\u5c31\u9519\u4e86\uff0c\u96be \u9053\u4f60\u8fd8\u4e0d\u77e5\u9053\u6211\u7684\u813e\u6c14\u3002\u201d \u3000\u3000\u4ed6\u53c8\u4e00\u7b11\uff0c\u7b11\u58f0\u4e2d\u5fae\u5fae\u5e26\u7740\u4e9b\u81ea\u4fe1\u7684\u505a\u610f\uff0c\u63a5\u7740\u53c8\u9053\uff1a\u201c\u800c\u4e14\u81ea\u4ece\u6211\u7ec3\u6210\u5341\u4e8c\u90fd\u5929\u795e\u529f \u4e4b\u540e\uff0c\u5c31\u59cb\u7ec8\u6ca1\u6709\u673a\u4f1a\u8bd5\u8fc7\u5a01\u529b\uff0c\u8fd9\u6b21\u6b63\u597d\u62ff\u8fd9\u6bd2\u7269\u8bd5\u8bd5\u624b\uff0c\u4f60\u653e\u5fc3\u597d\u4e86\uff0c\u6211\u4e0d\u4f1a\u600e\u6837 \u7684\u3002\u201d \u3000\u3000\u675c\u4e00\u5a18\u5fc3\u91cc\u867d\u7136\u4e00\u767e\u4e8c\u5341\u4e07\u4e2a\u4e0d\u613f\u610f\uff0c\u4f46\u81ea\u7ed3\u5a5a\u4ee5\u6765\uff0c\u5979\u77e5\u9053\u4ed6\u53ea\u8981\u81ea\u5df1\u8bf4\u8fc7\u7684\u8bdd\uff0c\u4ece \u6765\u6ca1\u6709\u4e00\u53e5\u8bf4\u51fa\u540e\u4e0d\u7b97\u7684\uff0c\u5979\u5f53\u7136\u4e3a\u5979\u4e08\u592b\u7684\u5b89\u5371\u7740\u6025\uff0c\u4f46\u5fc3\u91cc\u5374\u4e5f\u6697\u6697\u4e3a\u81ea\u5df1\u6709\u8fd9\u6837\u7684\u4e08 \u592b\u800c\u6b22\u559c\u3002 \u3000\u3000\u4e8e\u662f\u5979\u7d27\u63e1\u4e86\u63e1\u5979\u4e08\u592b\u7684\u624b\uff0c\u53f9\u606f\u7740\u6d45\u6d45\u4e00\u7b11\uff0c\u70b9\u5934\u9053\uff1a\u201c\u5927\u54e5\uff0c\u6211\u77e5\u9053\u4f60\u8981\u505a\u7684\u4e8b\u603b \u662f\u5bf9\u7684\uff0c\u4e0d\u8fc7\u4f60\u4e00\u5b9a\u8981\u5c0f\u5fc3\u4e9b\uff0c\u4f60\u867d\u7136\u529f\u529b\u5df2\u5165\u5316\u5883\uff0c\u53ef\u662f\u5bf9\u4ed8\u90a3\u4e9b\u6bd2\u7269\uff0c\u5374\u6ca1\u6709\u4ec0\u4e48\u7ecf \u9a8c\uff0c\u8fd9\u91cc\uff0c\u4f60\u4e0d\u7528\u70e6\u5fc3\uff0c\u6211\u548c\u537f\u513f\u7edd\u5bf9\u4e0d\u4f1a\u51fa\u4ec0\u4e48\u4e8b\u7684\u3002\u201d \u3000\u3000\u5353\u6d69\u7136\u5fc3\u80f8\u4e4b\u95f4\uff0c\u4f46\u89c9\u6e29\u99a8\u65e0\u6bd4\uff0c\u4e5f\u7d27\u7d27\u4e00\u63e1\u7231\u59bb\u7684\u624b\uff0c\u7b11\u9053\uff1a\u201c\u6211\u5a36\u4f60\u4e3a\u59bb\uff0c\u518d\u52a0\u4e0a \u537f\u513f\u53c8\u4e56\uff0c\u53ef\u8bf4\u4e00\u751f\u65e0\u61be\uff0c\u4e00\u5207\u4e8b\u6211\u81ea\u4f1a\u5c0f\u5fc3\uff0c\u4f60\u4e5f\u4e0d\u5fc5\u6302\u5ff5\uff0c\u4e0d\u7528\u591a\u4e45\uff0c\u6211\u5c31\u56de\u6765\u7684\u3002\u201d \u3000\u3000\u8bf4\u7f62\uff0c\u4ed6\u53c8\u8d70\u8fc7\u53bb\u629a\u4e86\u629a\u4ed6\u7231\u5b50\u7684\u5934\uff0c\u56de\u987e\u4e00\u7b11\uff0c\u811a\u5c16\u987f\u5904\uff0c\u8eab\u5f62\u4e4d\u5c55\uff0c\u77eb\u5065\u7684\u8eab\u8eaf\uff0c \u4fbf\u50cf\u4e00\u53ea\u5de7\u71d5\u4f3c\u7684\u6cbf\u7740\u86c7\u866b\u7684\u53bb\u8def\u63a0\u53bb\u3002 \u3000\u3000\u675c\u4e00\u5a18\u671b\u7740\u5979\u4e08\u592b\u66fc\u5999\u800c\u8f7b\u7075\u7684\u8eab\u5f62\uff0c\u5e78\u798f\u5730\u5fae\u5531\u4e00\u58f0\uff0c\u62cd\u7740\u5979\u7231\u5b50\u7684\u624b\u9053\uff1a\u201c\u537f\u513f\uff0c \u4f60\u8981\u597d\u597d\u7684\u505a\u4eba\uff0c\u957f\u5927\u4e86\u8ddf\u4f60\u7239\u7239\u4e00\u6837\uff0c\u505a\u4e2a\u9876\u5929\u7acb\u5730\u7684\u7537\u5b50\u6c49\u5927\u4e08\u592b\uff0c\u5f7c\u5929\u4e0b\u6b66\u6797\u540c\u9053\u6240 \u5c0a\u656c\uff0c\u77e5\u9053\u5417\uff1f\u201d \u3000\u3000\u5353\u957f\u537f\u53ea\u89c9\u81ea\u5df1\u70ed\u8840\u5954\u817e\uff0c\u6068\u4e0d\u5f97\u81ea\u5df1\u9a6c\u4e0a\u5c31\u957f\u5927\u6210\u4eba\u3002 \u3000\u3000\u6b65\u7740\u4ed6\u7236\u4eb2\u7684\u540e\u5c18\uff0c\u5728\u6b66\u6797\u4e2d\u505a\u4e00\u756a\u8f70\u8f70\u70c8\u70c8\u7684\u5927\u4e8b\u4e1a\u51fa\u6765\u3002 \u3000\u3000\u4ed6\u575a\u6bc5\u5730\u70b9\u4e86\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u5988\uff0c\u4f60\u653e\u5fc3\u597d\u4e86\uff0c\u5c06\u6765\u6211\u957f\u5927\u4e86\uff0c\u51b3\u4e0d\u4f1a\u4e22\u7239\u7239\u5988\u5988\u7684 \u8138\u3002\u201d \u3000\u3000\u675c\u4e00\u5a18\u53c8\u8f7b\u8f7b\u4e00\u7b11\uff0c\u6697\u81ea\u5fd6\u9053\uff1a\u201c\u6211\u6709\u8fd9\u4e48\u6837\u7684\u4e08\u592b\u3001\u8fd9\u4e48\u4e56\u7684\u5b69\u5b50\uff0c\u6211\u771f\u662f\u4e16\u754c\u4e0a\u6700 \u5e78\u798f\u7684\u5973\u4eba\u4e86\u3002\u201d \u3000\u3000\u5979\u4eec\u6bcd\u5b50\u4e24\u4eba\uff0c\u7d27\u63e1\u7740\u624b\uff0c\u7ad9\u5728\u8fd9\u5c71\u5d16\u7684\u8fb9\u4e0a\uff0c\u6b63\u6ee1\u6000\u5e78\u798f\uff0c\u5374\u4e0d\u77e5\u5728\u4ed6\u4eec\u8eab\u540e\uff0c\u6b63\u6ee1 \u9762\u72de\u7b11\u7684\u7ad9\u7740\u4e00\u4e2a\u8981\u6bc1\u53bb\u5979\u4eec\u5e78\u798f\u7684\u4eba\u3002 \u3000\u3000\u800c\u8fd9\u4eba\uff0c\u4e5f\u662f\u98de\u51e4\u51f0\u675c\u4e00\u5a18\u7684\u65e7\u53cb\uff0c\u6b66\u6797\u4e2d\u7684\u9b3c\u5934\uff0c\u4e07\u5999\u771f\u4eba\u5c39\u51e1\u3002\u4e5f\u5c31\u662f\u90a3\u770b\u6765\u4e30\u795e \u51b2\u5937\u3001\u7fbd\u8863\u661f\u51a0\u7684\u9053\u4eba\u3002 \u3000\u3000\u5353\u6d69\u7136\u65bd\u5c55\u5f00\u8eab\u6cd5\uff0c\u5feb\u5982\u6d41\u661f\u822c\u5730\u6cbf\u7740\u5730\u4e0a\u7684\u6b8b\u8349\u75d5\u8ff9\uff0c\u5192\u7740\u7a7a\u4e2d\u5c1a\u672a\u5b8c\u5168\u6d88\u6563\u7684\u6bd2\u5c18 \u98de\u6c99\uff0c\u671d\u90a3\u8fde\u7ef5\u4e0d\u7edd\u7684\u5c71\u5cad\u63a0\u53bb\u3002 \u3000\u3000\u4ed6\u8eab\u5f62\u5982\u71d5\uff0c\u5fae\u4e00\u8d77\u843d\uff0c\u4fbf\u662f\u56db\u4e94\u4e08\u8fdc\u8fd1\uff0c\u4e0d\u6d88\u7247\u523b\uff0c\u4fbf\u5df2\u8d70\u5230\u4e00\u5904\u5ce1\u8c37\u7684\u8c37\u53e3\uff0c\u8fdc\u8fdc \u671b\u53bb\uff0c\u4ece\u8c37\u53e3\u6811\u9699\u4e4b\u4e2d\uff0c\u5c31\u53ef\u4ee5\u770b\u5230\u4e00\u7f15\u7f15\u7684\u5f69\u70df\uff0c\u8885\u5a1c\u6447\u66f3\u7a7a\u9645\uff0c\u6708\u534e\u6f2b\u5730\uff0c\u661f\u5149\u95ea\u70c1\uff0c \u6620\u5f97\u8fd9\u4e9b\u5f69\u70df\uff0c\u5e7b\u6210\u4e00\u79cd\u65e0\u6cd5\u63cf\u6479\u7684\u5f02\u8272\uff0c\u597d\u770b\u5df2\u6781\u3002 \u3000\u3000\u5353\u6d69\u7136\u867d\u7136\u542b\u6709\u6781\u5999\u7684\u907f\u6bd2\u7075\u836f\uff0c\u4f46\u6b64\u523b\u5374\u4ecd\u7136\u4e0d\u6562\u6709\u4e1d\u6beb\u5927\u610f\uff0c\u8eab\u5f62\u4e00\u5c55\uff0c\u63a0\u4e0a\u4e86\u8c37 \u53e3\u4e24\u65c1\u7684\u5c71\u5d16\uff0c\u6cbf\u7740\u5c71\u5d16\u7684\u9876\u7aef\uff0c\u98de\u63a0\u4e86\u6570\u91cc\uff0c\u624d\u53d1\u73b0\u8fd9\u6761\u5ce1\u8c37\u7adf\u6709\u4e03\u516b\u91cc\u6df1\uff0c\u5f53\u4e2d\u6709\u4e00\u7247 \u76c6\u5730\uff0c\u5c3d\u5934\u4e4b\u5904\uff0c\u5374\u662f\u4e00\u4e2a\u524d\u65e0\u901a\u8def\u7684\u6b7b\u8c37\u3002 \u3000\u3000\u6b7b\u8c37\u8fd1\u5e95\u4e4b\u5904\uff0c\u4e24\u8fb9\u7684\u5c71\u5d16\uff0c\u7a81\u7136\u5411\u91cc\u67ec\u7d27\uff0c\u5f62\u6210\u4e00\u6761\u50cf\u662f\u76f4\u62f1\u7684\u6b7b\u8c37\uff0c\u4e24\u8fb9\u5d16\u9876\uff0c\u9f50 \u5e73\u76f8\u5411\uff0c\u5374\u6e10\u6e10\u5411\u524d\u9ad8\u8d77\uff0c\u76f4\u5230\u8c37\u5e95\u6a2a\u58c1\uff0c\u7adf\u6709\u4e9b\u50cf\u662f\u4e00\u6761\u5927\u8239\u4e24\u8fb9\u7684\u8239\u8237\uff0c\u90a3\u8c37\u5e95\u4e4b\u5904\u4fbf \u662f\u8239\u5934\u4e86\u3002 \u3000\u3000\u5353\u6d69\u7136\u5fc3\u5934\u4e00\u52a8\uff0c\u5fd6\u9053\uff1a\u201c\u83ab\u975e\u8fd9\u91cc\u5c31\u662f\u9ec4\u5c71\u7edd\u9669\u4e4b\u4e00\u7684\u94c1\u8239\u5934\u5417\uff1f\u201d \u3000\u3000\u540c\u5149\u518d\u5f80\u524d\u671b\uff0c\u8c37\u5e95\u5d16\u6df1\u4e4b\u5904\uff0c\u7adf\u6709\u4e00\u5927\u9ed1\u6d1e\uff0c\u9ed1\u6d1e\u65c1\u8fb9\u7684\u5c71\u77f3\uff0c\u72fc\u85c9\u98de\u5217\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u53c8\u4e00\u52a8\uff0c\u5fd6\u9053\uff1a\u201c\u96be\u9053\u8fd9\u602a\u7269\u5c31\u662f\u4ece\u91cc\u9762\u88c2\u5c71\u7a7f\u7a74\uff0c\u5f3a\u81ea\u7834\u5c71\u7a7f\u51fa\u6765\u7684\u5417\uff1f\u201d \u3000\u3000\u5fc3\u5ff5\u81f3\u6b64\uff0c\u4e0d\u7981\u987f\u4f4f\u8eab\u5f62\uff0c\u4f46\u5947\u602a\u7684\u662f\u6b64\u65f6\u6b64\u5730\u7adf\u8fde\u4e00\u4e1d\u58f0\u97f3\u90fd\u6ca1\u6709\uff0c\u8fd9\u504c\u5927\u7684\u4e00\u5904\u5c71 \u8c37\uff0c\u7adf\u50cf\u662f\u4e00\u5ea7\u575f\u5893\u4e00\u6837\u3002 \u3000\u3000\u4ed6\u65b9\u81ea\u987f\u4f4f\u8eab\u5f62\uff0c\u5947\u602a\u7740\u8fd9\u56db\u5468\u6b7b\u5bc2\u7684\u65f6\u5019\uff0c\u5ffd\u7136\u2014\u2014\u8c37\u5e95\u90a3\u76c6\u5730\u5de6\u53f3\uff0c\u4f20\u6765\u4e00\u58f0\u6709\u4e9b \u50cf\u662f\u513f\u557c\u822c\u7684\u5389\u5578\uff0c\u5578\u58f0\u60a0\u957f\u51c4\u5389\uff0c\u8fde\u5353\u6d69\u7136\u8fd9\u79cd\u4eba\u7269\u542c\u4e86\uff0c\u90fd\u4e0d\u7981\u4e3a\u4e4b\u60ca\u6817\u3002 \u3000\u3000\u4ed6\u7a0d\u4e00\u8fdf\u7591\uff0c\u4fbf\u53c8\u4e00\u63a0\u800c\u524d\uff0c\u624d\u4e24\u4e2a\u8d77\u843d\uff0c\u76ee\u5149\u89e6\u5904\uff0c\u4fbf\u770b\u5230\u4e00\u4ef6\u4ed6\u8fd9\u540d\u6ee1\u6b66\u6797\u3001\u4fa0\u8e2a \u904d\u53ca\u5b57\u5185\u7684\u5927\u4fa0\u5e73\u751f\u672a\u89c1\u7684\u5947\u4e8b\u3002 \u3000\u3000\u539f\u6765\u6b64\u65f6\uff0c\u8c37\u5e95\u90a3\u5c71\u77f3\u72fc\u85c9\u7684\u5d16\u6d1e\u524d\u4e00\u7247\u5e7f\u5927\u7684\u76c6\u5730\u4e0a\uff0c\u7adf\u6ee1\u5e03\u7740\u86c7\u866b\u731b\u517d\uff0c\u4e4d\u89c1\u53ea\u89c9 \u70df\u5c18\u6d6e\u52a8\uff0c\u50cf\u662f\u975e\u5e38\u7d20\u4e71\u3002 \u3000\u3000\u4f46\u4ed4\u7ec6\u4e00\u770b\uff0c\u8fd9\u4e9b\u86c7\u866b\u731b\u517d\uff0c\u5374\u662f\u5404\u4f9d\u5176\u7c7b\uff0c\u6709\u7684\u505a\u4e00\u5806\u4e00\u76d8\uff0c\u6709\u7684\u8e1e\u4f0f\u5730\u4e0a\u3002 \u3000\u3000\u86c7\u3001\u866b\u3001\u517d\u7684\u884c\u5217\uff0c\u6781\u5176\u5206\u660e\u3002 \u3000\u3000\u8fd9\u4e9b\u86c7\u866b\u731b\u517d\uff0c\u4e00\u8d77\u90fd\u662f\u5934\u5411\u7740\u8c37\u5e95\u90a3\u9762\uff0c\u6700\u524d\u9762\u662f\u86c7\u866b\u548c\u8708\u86a3\u4e4b\u7c7b\u7684\u6781\u6bd2\u4e4b\u7269\uff0c\u540e\u9762 \u4f9d\u6b21\u800c\u4e0b\uff0c\u90a3\u4e9b\u731b\u517d\u90fd\u8fdc\u8fdc\u7f29\u5728\u540e\u9762\u3002 \u3000\u3000\u8fd9\u4e9b\u866b\u517d\u4e3a\u6570\u4e4b\u591a\uff0c\u76f4\u4e0d\u53ef\u6570\u8ba1\uff0c\u5947\u602a\u7684\u662f\uff0c\u8fd9\u4e9b\u86c7\u866b\u731b\u517d\u4e4b\u4e2d\uff0c\u5374\u6709\u4e00\u6761\u9053\u8def\u3002 \u3000\u3000\u66f4\u5947\u602a\u7684\u662f\uff0c\u8fd9\u4e48\u6210\u5343\u6210\u4e07\u3001\u5e73\u65e5\u53ea\u8981\u5355\u72ec\u76f8\u9047\u3001\u5c31\u7acb\u523b\u4f1a\u8d77\u6076\u6597\u51f6\u6740\u7684\u86c7\u866b\u731b\u517d\uff0c\u6b64 \u65f6\u540c\u96c6\u4e00\u5904\u7adf\u7136\u90fd\u4e92\u4e0d\u76f8\u6270\uff0c\u9759\u4fcf\u6084\u7684\uff0c\u50cf\u662f\u6ce5\u5851\u6728\u96d5\u7684\u4e00\u6837\uff0c\u5446\u5446\u7684\u6392\u5217\u5982\u6b7b\u53bb\u4e00\u822c\u3002 \u3000\u3000\u5353\u6d69\u7136\u5168\u8eab\u4e0d\u7981\u4e5f\u8d77\u4e86\u4e00\u9635\u60ca\u6817\uff0c\u4ed4\u7ec6\u518d\u4e00\u671b\uff0c\u518d\u770b\u5230\u6700\u524d\u9762\u7684\u90a3\u4e9b\u957f\u8fbe\u5341\u4e08\u7684\u5de8\u87d2\uff0c \u5df2\u6b7b\u4e86\u597d\u51e0\u6761\uff0c\u6ee1\u5730\u8840\u8165\u72fc\u85c9\uff0c\u86c7\u8eab\u867d\u7136\u8fd8\u90fd\u5b8c\u6574\uff0c\u4f46\u662f\u86c7\u5934\u4e0a\u5374\u90fd\u5df2\u7834\u788e\u8840\u6c61\u4e86\u3002 \u3000\u3000\u6c61\u8840\u6ee9\u4e2d\uff0c\u7adf\u76d8\u7740\u4e00\u6761\u602a\u86c7\uff0c\u867d\u4e0d\u5341\u5206\u957f\u5927\uff0c\u4f46\u5f62\u72b6\u6781\u602a\uff0c\u86c7\u8179\u5947\u5927\uff0c\u8d8a\u5230\u4e0a\u9762\u8d8a\u7ec6\uff0c \u53ea\u662f\u4e00\u4e2a\u86c7\u5934\uff0c\u5374\u53c8\u5927\u5982\u82ad\u6597\uff0c\u5934\u4e0a\u7ade\u8fd8\u6709\u4e00\u4e2a\u9ad8\u6602\u7740\u7684\u8089\u51a0\uff0c\u4e24\u816e\u6012\u9f13\uff0c\u4e5f\u51f8\u51fa\u751a\u591a\u3002 \u3000\u3000\u8fd9\u6761\u602a\u7269\u4e00\u7ecf\u4eba\u76ee\uff0c\u5353\u6d69\u7136\u4fbf\u5fc3\u4e2d\u6709\u6570\uff0c\u77e5\u9053\u8fd9\u662f\u5148\u524d\u6df7\u5728\u86c7\u7fa4\u91cc\u6765\uff0c\u5bfb\u627e\u8c37\u4e2d\u602a\u7269\u6076 \u6597\u7684\u6bd2\u7269\uff0c\u5fc3\u4e2d\u4e0d\u7981\u6697\u559c\u3002 \u3000\u3000\u201c\u770b\u6765\u4eca\u65e5\u6211\u80fd\u6210\u529f\u4e5f\u672a\u5fc5\u53ef\u77e5\uff0c\u8fd9\u4e24\u4e2a\u602a\u7269\u6076\u6597\u4e4b\u4e0b\uff0c\u5fc5\u6709\u4e00\u6b7b\uff0c\u4e0d\u6b7b\u7684\u90a3\u4e2a\uff0c\u4e5f\u5fc5 \u7136\u5143\u6c14\u5927\u4f24\uff0c\u6211\u5c82\u975e\u53ef\u4ee5\u5750\u6536\u6e14\u5229\u3002\u201d \u3000\u3000\u4ed6\u6b63\u81ea\u6697\u4e2d\u601d\u5fd6\uff0c\u5374\u89c1\u90a3\u602a\u7269\u5ffd\u53c8\u4e00\u58f0\u6781\u4e3a\u51c4\u5389\u7684\u957f\u5578\u3002 \u3000\u3000\u5578\u58f0\u65b9\u4f4f\u4e00-- \u3000\u3000\u5371\u5d16\u4e4b\u4e0b\uff0c\u77f3\u571f\u4e71\u6742\u7684\u6697\u6d1e\u4e4b\u4e2d\uff0c\u84e6\u5730\u98de\u7a9c\u51fa\u4e00\u4e2a\u602a\u7269\uff0c\u8fdc\u770b\u7adf\u4f3c\u4e00\u6761\u6d77\u4e2d\u7684\u661f\u9c7c\uff0c\u884c \u52a8\u5982\u98ce\uff0c\u8eab\u4e0a\u7adf\u5e26\u7740\u51e0\u5904\u60e8\u7eff\u7684\u9edd\u5149\uff0c\u800c\u4e14\u4e92\u76f8\u968f\u673a\u95ea\u53d8\uff0c\u5947\u5f62\u602a\u72b6\uff0c\u771f\u975e\u8a00\u8bed\u6240\u80fd\u5f62\u5bb9\u3002 \u3000\u3000\u5353\u6d69\u7136\u4ee5\u6b66\u6797\u4e2d\u4e00\u4ee3\u5947\u624d\uff0c\u6b64\u65f6\u5374\u4e5f\u4e0d\u6562\u884c\u5f97\u592a\u8fd1\uff0c\u8fdc\u8fdc\u671b\u53bb\uff0c\u53ea\u89c1\u8fd9\u602a\u7269\u7adf\u4f5c\u4e94\u89d2\u661f \u5f62\uff0c\u53ea\u524d\u9762\u7a81\u51fa\u4e00\u4e2a\u6241\u5706\u7684\u602a\u5934\uff0c\u5634\u5927\u5982\u76c6\uff0c\u4e0a\u9762\u7adf\u751f\u7740\u4e00\u6392\u602a\u773c\uff0c\u548c\u4e00\u4e2a\u51f8\u51fa\u5982\u575f\u3001\u4e0a\u751f \u4e09\u5b54\u7684\u602a\u9f3b\u3002 \u3000\u3000\u8fd9\u602a\u7269\u6ee1\u8eab\u65e0\u4e00\u4e0d\u602a\uff0c\u8eab\u4e0a\u4e94\u4e2a\u661f\u89d2\uff0c\u5206\u5411\u4e94\u65b9\u7a81\u51fa\uff0c\u8fb9\u4e0a\u8fd8\u751f\u7740\u4e94\u6839\u94a9\u722a\uff0c\u5f53\u4e2d\u8fd8\u6709 \u4e00\u4e2a\u661f\u5f62\u4e4b\u773c\uff0c\u53d1\u7740\u4e00\u4e1d\u60e8\u60e8\u7684\u5149\u8292\u3002 \u3000\u3000\u5b83\u5168\u8eab\u5e76\u65e0\u817f\u8db3\uff0c\u884c\u52a8\u65f6\u4fbf\u7528\u8fd9\u4e94\u6839\u94a9\u722a\u7740\u5730\uff0c\u4e94\u4e2a\u661f\u89d2\u6328\u6b21\u7740\u5730\uff0c\u6b64\u8d77\u5f7c\u843d\uff0c\u5728\u5730\u4e0a \u7ffb\u6eda\u800c\u51fa\uff0c\u770b\u53bb\u7adf\u7075\u6d3b\u5df2\u6781\u3002 \u3000\u3000\u5353\u6d69\u7136\u8fdc\u8fdc\u7687\u53bb\uff0c\u53ea\u770b\u6676\u5149\u95ea\u95ea\uff0c\u4e00\u5927\u56e2\u58a8\u7eff\u8272\u7684\u5f71\u5b50\uff0c\u7535\u9a70\u661f\u98de\uff0c\u7b14\u76f4\u5730\u5f80\u86c7\u524d\u5377 \u53bb\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u5feb\u5982\u7535\u5149\u706b\u77f3\u7684\u4e00\u5239\u90a3\uff0c\u90a3\u6761\u602a\u7269\uff0c\u65e9\u5c31\u84c4\u52bf\u5f85\u53d1\uff0c\u6b64\u523b\u5168\u8eab\u7adf\u4f3c\u4e00\u6761\u957f\u97ad\uff0c\u659c \u7740\u5411\u4e0a\uff0c\u5f80\u524d\u9762\u66b4\u4f38\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u8fd9\u4e24\u4e0b\u52bf\u5b50\u90fd\u6025\uff0c\u7728\u773c\u4fbf\u7ea0\u7f20\u5728\u4e00\u56e2\uff0c\u7ffb\u6eda\u640f\u6597\uff0c\u53bb\u52bf\u4e4b\u731b\uff0c\u7aef\u7684\u60ca\u4eba\u5df2\u6781\uff0c\u56db\u4e0b\u7684\u6bd2 \u86c7\u6bd2\u866b\uff0c\u88ab\u8fd9\u4e24\u4e2a\u602a\u7269\u7684\u8eab\u5b50\u538b\u8fc7\uff0c\u7acb\u523b\u4fbf\u6210\u8089\u6ce5\uff0c\u6709\u7684\u6b8b\u80a2\u65ad\u9aa8\u8fd8\u88ab\u5e26\u4e86\u8d77\u6765\uff0c\u51cc\u7a7a\u98de \u821e\u3002 \u3000\u3000\u4f46\u662f\u86c7\u866b\u4e4b\u4e2d\uff0c\u5c31\u6709\u8fd9\u4e48\u5947\u602a\u7684\u514b\u6027\uff0c\u8fd9\u4e48\u4e00\u5927\u7247\u86c7\u866b\u6b64\u65f6\u7adf\u8fde\u4e00\u4e2a\u6562\u9003\u7684\u90fd\u6ca1\u6709\uff0c\u4ff1 \u662f\u6218\u6218\u5162\u5162\u5728\u90a3\u91cc\u7b49\u6b7b\u3002 \u3000\u3000\u5353\u6d69\u7136\u6e38\u4fa0\u56db\u6d77\uff0c\u8db3\u8ff9\u6240\u81f3\uff0c\u540d\u5c71\u5927\u6cfd\uff0c\u9761\u4e0d\u767b\u4e34\uff0c\u4f46\u8fd9\u79cd\u51c4\u5389\u60e8\u6de1\u3001\u50cf\u5730\u72f1\u822c\u7684\u5149 \u666f\uff0c\u4e5f\u8fd8\u662f\u7b2c\u4e00\u6b21\u89c1\u5230\u3002 \u3000\u3000\u7247\u523b\u4e4b\u95f4\uff0c\u90a3\u4e9b\u5947\u51f6\u6076\u6bd2\u7684\u86c7\u874e\uff0c\u7adf\u5df2\u88ab\u8fd9\u4e24\u4e2a\u602a\u7269\u6b8b\u6740\u4e86\u5927\u534a\uff0c\u5353\u6d69\u7136\u60ca\u60b8\u4e4b\u4f59\uff0c\u6697 \u6697\u53f9\u6c14\uff0c\u53ea\u5e0c\u671b\u8fd9\u4e24\u4e2a\u602a\u7269\u5728\u5bb3\u53ca\u53e6\u3002\u4e9b\u7f8a\u9e7f\u9a6f\u517d\u4e4b\u524d\u5c31\u5206\u51fa\u7ed3\u679c\u6765\uff0c\u4e0d\u7136\u81ea\u5df1\u53c8\u600e\u80fd\u5750 \u89c6\u3002 \u3000\u3000\u53c8\u8fc7\u4e86\u534a\u664c\uff0c\u8fd9\u4e2a\u602a\u7269\u7684\u52bf\u529b\u679c\u7136\u8d8a\u6765\u8d8a\u7f13\uff0c\u5728\u8fd9\u79cd\u60c5\u51b5\u4e4b\u4e0b\uff0c\u5353\u6d69\u7136\u7adf\u7136\u60f3\u8d77\u4ed6\u7684\u59bb \u513f\u6765\uff0c\u4e00\u77ac\u95f4\uff0c\u5fc3\u4e2d\u7adf\u4e0d\u80fd\u81ea\u4e3b\u3002 \u3000\u3000\u8fd9\u5c31\u662f\u4eba\u6027\u7684\u503c\u5f97\u60b2\u54c0\u3001\u4f46\u4e5f\u662f\u503c\u5f97\u8d5e\u7f8e\u7684\u5730\u65b9\uff0c\u4eba\u4eec\u65e0\u8bba\u5728\u4efb\u4f55\u4e00\u79cd\u60c5\u51b5\u4e0b\uff0c\u5bf9\u4e8e\u4ed6 \u6240\u7231\u7740\u7684\u4eba\u4eec\uff0c\u6c38\u8fdc\u662f\u65e0\u6cd5\u5fd8\u6000\u7684\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u601d\u6f6e\u7ffb\u6d8c\uff0c\u5ffd\u7136\uff0c\u53c8\u542c\u5f97\u4e00\u58f0\u6781\u51c4\u5389\u7684\u602a\u5578\u4e4b\u58f0\u3002 \u3000\u3000\u4ed6\u8fd9\u624d\u5f3a\u81ea\u6536\u6444\u4f4f\u81ea\u5df1\u5bf9\u59bb\u513f\u7684\u5173\u6000\u601d\u5ff5\uff0c\u5b9a\u775b\u671d\u524d\u9762\u671b\u53bb\uff0c\u53ea\u89c1\u6b64\u523b\u90a3\u6761\u6bd2\u86c7\u7684\u86c7 \u5934\uff0c\u5df2\u88ab\u90a3\u661f\u5f62\u602a\u7269\u7684\u4e24\u53ea\u8089\u89d2\u5939\u4f4f\uff0c\u540e\u9762\u4e09\u89d2\uff0c\u51cc\u7a7a\u98de\u821e\uff0c\u4e00\u9762\u628a\u90a3\u86c7\u8eab\u957f\u97ad\u4f3c\u7684\u671d\u5730\u4e0a \u4e71\u6253\u3002 \u3000\u3000\u8fd9\u4e00\u6765\uff0c\u6ee1\u5730\u7684\u866b\u86c7\uff0c\u66f4\u662f\u906d\u4e86\u60e8\u52ab\uff0c\u8fde\u864e\u8c79\u4e4b\u7c7b\u7684\u731b\u517d\uff0c\u88ab\u8fd9\u957f\u97ad\u4f3c\u7684\u86c7\u8eab\u4e00\u51fb\uff0c\u4e5f \u5c31\u7acb\u523b\u53d8\u6210\u8089\u6ce5\uff0c\u8fde\u60e8\u543c\u90fd\u672a\u53ca\u53d1\u51fa\u3002 \u3000\u3000\u5353\u6d69\u7136\u77e5\u9053\u8fd9\u4e24\u4e2a\u602a\u7269\u5df2\u7ecf\u5206\u51fa\u80dc\u8d1f\uff0c\u76ee\u5149\u56db\u4e0b\u4e00\u626b\uff0c\u8eab\u5f62\u53c8\u63a0\u524d\u6570\u4e08\uff0c\u53f3\u638c\u4e00\u626c\uff0c\u8f70 \u7136\u4e00\u58f0\uff0c\u7adf\u5c06\u5c71\u5d16\u8fb9\u4e00\u5757\u65b9\u5706\u51e0\u8fbe\u4e08\u8bb8\u7684\u5de8\u77f3\uff0c\u51fb\u5f97\u6d77\u7897\u5927\u5c0f\u7684\u77f3\u5757\uff0c\u5947\u5999\u7684\u662f\uff0c\u8fd9\u5c71\u77f3\u88ab \u51fb\u788e\u4e4b\u540e\uff0c\u5e76\u4e0d\u56db\u4e0b\u98de\u6e85\uff0c\u800c\u53ea\u662f\u5728\u5730\u4e0a\u6563\u505a\u4e00\u5806\u3002 \u3000\u3000\u5353\u6d69\u7136\u6697\u4e2d\u6ee1\u610f\u7684\u4e00\u7b11\uff0c\u77e5\u9053\u81ea\u5df1\u81ea\u5e7c\u82e6\u7ec3\u7684\u65e0\u4e0a\u795e\u529f\u5341\u4e8c\u90fd\u5929\u795e\u529f\uff0c\u5df2\u6709\u4e86\u6210\u5c31\uff0c\u8fd9 \u79cd\u795e\u529f\uff0c\u4e5f\u5c31\u662f\u9053\u5bb6\u6240\u8c13\u7684\u7f61\u98ce\uff0c\u4f5b\u5bb6\u6240\u8c13\u7684\u822c\u82e5\u638c\u529b\uff0c\u7ec3\u7684\u65b9\u6cd5\u867d\u4e0d\u540c\uff0c\u4f46\u6b8a\u9014\u540c\u5f52\uff0c\u4e0d \u4f46\u5f97\u5230\u7684\u5883\u754c\u4e00\u6837\uff0c\u53d1\u51fa\u7684\u529f\u80fd\u4e5f\u5927\u540c\u5c0f\u5f02\uff0c\u6b63\u662f\u65e0\u575a\u4e0d\u6467\u3001\u81f3\u521a\u81f3\u731b\u5148\u5929\u4e4b\u771f\u6c14\u3002 \u3000\u3000\u4ed6\u4ee5\u65e0\u6bd4\u8270\u82e6\u7684\u5fc3\u5fd7\u3001\u6bc5\u529b\uff0c\u6d78\u6deb\u6b64\u9053\u8fd1\u4e09\u5341\u5e74\uff0c\u6b64\u523b\u77e5\u9053\u81ea\u5df1\u5df2\u7565\u6709\u6210\u5c31\uff0c\u5fc3\u91cc\u6b22\u559c \u7684\u611f\u89c9\uff0c\u81ea\u7136\u662f\u65e0\u53ef\u6bd4\u62df\u7684\u3002 \u3000\u3000\u54ea\u77e5\u5c31\u5728\u6b64\u523b\uff0c\u4ed6\u9f3b\u7aef\u7a81\u7136\u5438\u8fdb\u4e00\u4e1d\u5176\u8165\u65e0\u6bd4\u7684\u6c14\u606f\u3002 \u3000\u3000\u4ed6\u8eab\u968f\u610f\u52a8\uff0c\u968f\u624b\u6293\u8d77\u4e24\u5757\u77f3\u5757\uff0c\u8eab\u5f62\u4fbf\u500f\u7136\u51cc\u7a7a\u800c\u8d77\uff0c\u659c\u659c\u5411\u540e\u63a0\u53bb\uff0c\u8170\u8eab\u5728\u7a7a\u4e2d\u5fae \u4e00\u8f6c\u6298\uff0c\u76ee\u5149\u95ea\u5904\uff0c\u4e0d\u7981\u53c8\u4e3a\u4e4b\u8272\u53d8\u3002 \u3000\u3000\u539f\u6765\u6b64\u523b\u90a3\u661f\u5f62\u7684\u602a\u7269\uff0c\u5df2\u6325\u52a8\u7740\u90a3\u6761\u6b7b\u86c7\u7684\u86c7\u8eab\u98de\u817e\u800c\u6765\uff0c\u60f3\u662f\u88ab\u65b9\u624d\u4ed6\u9707\u788e\u5de8\u77f3\u65f6 \u90a3\u4e00\u58f0\u5de8\u54cd\u6240\u60ca\uff0c\u6b64\u65f6\u8ddd\u79bb\u4ed6\u8eab\u4fa7\u5df2\u8fd1\u5341\u4e08\uff0c\u4f46\u5b83\u53e3\u4e2d\u6240\u55b7\u7684\u90a3\u79cd\u60e8\u7eff\u7684\u6bd2\u6c14\uff0c\u5374\u5df2\u51e0\u8fd1\u5353 \u6d69\u7136\u8eab\u4fa7\u3002 \u3000\u3000\u5353\u6d69\u7136\u4e00\u773c\u7779\u89c1\u8fd9\u79cd\u60c5\u51b5\uff0c\u8eab\u5f62\u8f6c\u6298\u4e4b\u95f4\uff0c\u53e3\u4e2d\u66b4\u559d\u4e00\u58f0\uff0c\u53cc\u624b\u8fde\u626c\uff0c\u4ed6\u638c\u4e2d\u6240\u6301\u7684\u90a3 \u4e24\u5757\u5c71\u77f3\uff0c\u7acb\u523b\u8131\u624b\u98de\u53bb\u3002 \u3000\u3000\u4ed6\u53d1\u77f3\u6240\u7528\u7684\u624b\u6cd5\uff0c\u867d\u4e5f\u5e73\u5e38\uff0c\u4f46\u662f\u8fd9\u79cd\u88ab\u5185\u5bb6\u5148\u5929\u771f\u6c14\u6240\u53d1\u7684\u529b\u9053\uff0c\u5374\u662f\u7aef\u7684\u60ca\u4eba\uff0c \u8fd9\u4e24\u5757\u5c71\u77f3\u7adf\u5e26\u7740\u65e0\u6bd4\u51cc\u5389\u7684\u98ce\u58f0\uff0c\u7a7f\u8fc7\u90a3\u661f\u5f62\u602a\u7269\u55b7\u51fa\u7684\u6bd2\u96fe\uff0c\u500f\u7136\u51fb\u5411\u5b83\u90a3\u6241\u5706\u7684\u602a\u7269 \u8eab\u4e0a\u3002 \u3000\u3000\u90a3\u602a\u7269\u4f3c\u4e5f\u77e5\u9053\u5389\u5bb3\uff0c\u7adf\u731b\u7136\u5c06\u8eab\u5b50\u505c\u4f4f\uff0c\u4e94\u89d2\u661f\u5f62\u8089\u89d2\u4e00\u5c55\uff0c\u90a3\u6761\u6b7b\u86c7\u7684\u86c7\u8eab\u4fbf\u53c8\u957f \u97ad\u822c\u88ab\u5b83\u6325\u821e\u800c\u8d77\uff0c\u7adf\u5c06\u8fd9\u4e24\u5757\u5c71\u77f3\u6325\u843d\u4e86\uff0c\u8fdc\u8fdc\u542c\u5230\u5c71\u5ca9\u4e0a\uff0c\u53d1\u51fa\u4e24\u58f0\u5de8\u54cd\u3002 \u3000\u3000\u8fd9\u65f6\u5353\u6d69\u7136\u4fbf\u4e5f\u56e0\u7740\u8fd9\u602a\u7269\u7684\u7a0d\u4e00\u505c\u987f\uff0c\u5f97\u4ee5\u5598\u606f\u4e00\u4e0b\uff0c\u731b\u5438\u4e00\u53e3\u771f\u6c14\uff0c\u53f3\u624b\u500f\u7136\u81ea\u8170 \u4e2d\u62bd\u51fa\u4e00\u6761\u8f6f\u5251\uff0c\u8fce\u98ce\u4e00\u6296\uff0c\u4fbf\u81ea\u7b14\u76f4\u3002 \u3000\u3000\u8fd9\u67c4\u8f6f\u5251\u4e00\u51fa\u9798\uff0c\u4fbf\u5e26\u8d77\u4e00\u6e9c\u51b7\u68ee\u7684\u9752\u5149\uff0c\u5b9b\u5982\u9752\u8679\u4e00\u62b9\uff0c\u6b63\u662f\u4e2d\u539f\u5927\u4fa0\u54b8\u9707\u6b66\u6797\u7684\u7075 \u86c7\u8f6f\u5251\u3002 \u3000\u3000\u6b64\u65f6\u5353\u6d69\u7136\u5168\u8eab\u771f\u6c14\u6ee1\u5e03\uff0c\u5df2\u903e\u7cbe\u94a2\uff0c\u53cc\u811a\u9489\u5728\u5730\u4e0a\uff0c\u4eff\u4f5b\u662f\u4e24\u6761\u77f3\u693f\u4f3c\u7684\uff0c\u751f\u50cf\u662f\u6ca1 \u6709\u4efb\u4f55\u4e00\u79cd\u529b\u91cf\u80fd\u5c06\u4ed6\u79fb\u52a8\u5206\u6beb\u3002 \u3000\u3000\u90a3\u661f\u5f62\u602a\u7269\u7a0d\u5fae\u505c\u987f\uff0c\u4fbf\u53c8\u7ffb\u6eda\u800c\u6765\uff0c\u5353\u6d69\u7136\u53ea\u89c9\u5f97\u90a3\u79cd\u523a\u81ed\u5455\u5fc3\u7684\u8165\u6c14\u6108\u6765\u6108\u6d53\uff0c\u4fbf \u731b\u7136\u820c\u7efd\u6625\u96f7\uff0c\u66b4\u559d\u4e00\u58f0\uff0c\u864e\u8170\u4e00\u632b\uff0c\u4e00\u53ea\u94c1\u638c\u786c\u751f\u751f\u5730\u63d2\u5165\u5c71\u5d16\uff0c\u7adf\u5c06\u5d16\u77f3\u6293\u8d77\u4e86\u4e00\u5927 \u7247\u3002 \u3000\u3000\u4ed6\u5f20\u53e3\u4e00\u54ac\uff0c\u5c06\u90a3\u67c4\u8f6f\u5251\u7684\u5251\u67c4\u54ac\u5728\u5634\u91cc\uff0c\u53cc\u624b\u626c\u5904\uff0c\u4f46\u89c1\u6ee1\u5929\u77f3\u96e8\u7eb7\u98de\uff0c\u88ab\u4ed6\u90a3\u5f00\u5c71 \u88c2\u77f3\u7684\u771f\u529b\u6240\u63a8\uff0c\u5404\u81ea\u201c\u55d6\u55d6\u201d\u51fb\u5411\u90a3\u602a\u7269\u3002 \u3000\u3000\u53ea\u542c\u90a3\u602a\u7269\u5c16\u7ec6\u800c\u6781\u4e3a\u523a\u8033\u5730\u5389\u5578\u4e86\u4e00\u58f0\uff0c\u5ffd\u7136\u5982\u98ce\u5411\u540e\u9000\u53bb\uff0c\u539f\u6765\u5b83\u90a3\u661f\u89d2\u4e0a\u7684\u70b9\u70b9 \u7eff\u5149\uff0c\u5df2\u88ab\u8fd9\u96f9\u96e8\u4f3c\u7684\u77f3\u5757\u6253\u4e2d\u4e00\u6307\uff0c\u7136\u800c\u5176\u4f59\u7684\u77f3\u5757\u51fb\u5728\u5b83\u8eab\u4e0a\uff0c\u5374\u7acb\u5373\u88ab\u5b83\u8eab\u4e0a\u90a3\u5bc6\u5e03 \u7684\u575a\u9cde\u6240\u53cd\u6fc0\u56de\u6765\u3002 \u3000\u3000\u5353\u6d69\u7136\u518d\u6b21\u5927\u559d\u4e00\u58f0\uff0c\u8eab\u5f62\u500f\u7136\u800c\u8d77\uff0c\u7adf\u968f\u7740\u90a3\u602a\u7269\u7684\u9000\u52bf\u63a0\u4e86\u8fc7\u53bb\uff0c\u638c\u4e2d\u957f\u5251\u4e00\u6325\uff0c \u4f46\u89c1\u4e00\u9053\u50cf\u662f\u7ecf\u5929\u800c\u8fc7\u7684\u957f\u8679\uff0c\u8fce\u7740\u90a3\u602a\u7269\u5411\u524d\u821e\u52a8\u7684\u661f\u89d2\u548c\u86c7\u8eab\u51fb\u53bb\uff0c\u4fbf\u542c\u53c8\u662f\u4e00\u58f0\u5389 \u5578\u3002 \u3000\u3000\u4f46\u6b64\u523b\u4ed6\u8eab\u5f62\u5df2\u81f3\u5d16\u8fb9\uff0c\u4e0b\u9762\u5373\u662f\u6f2b\u5929\u866b\u86c7\u6b8b\u6b7b\u548c\u8165\u98ce\u6c61\u8840\uff0c\u5353\u6d69\u7136\u5982\u6d41\u661f\u98de\u63a0\u7684\u8eab \u5f62\uff0c\u5230\u4e86\u8fd9\u5371\u5d16\u4e4b\u8fb9\uff0c\u500f\u7136\u9489\u4f4f\uff0c\u8fd9\u79cd\u8eab\u6cd5\u7684\u8fd0\u7528\uff0c\u53c8\u786e\u5b9e\u662f\u8db3\u4ee5\u60ca\u4e16\u800c\u9a87\u4fd7\u7684\u3002 \u3000\u3000\u4ed6\u8eab\u5f62\u4e00\u987f\uff0c\u76ee\u5149\u518d\u5411\u524d\u63a0\uff0c\u5374\u89c1\u90a3\u661f\u5f62\u7684\u602a\u7269\uff0c\u5e26\u7740\u90a3\u79cd\u5c16\u9510\u800c\u523a\u8033\u7684\u5389\u5578\u4e4b\u58f0\uff0c\u50cf \u662f\u4e00\u56e2\u78a7\u7eff\u7684\u5149\u9edd\uff0c\u7ffb\u6eda\u817e\u8d77\u7740\uff0c\u53c8\u63a0\u53e3\u5b83\u51fa\u6765\u65f6\u90a3\u9edd\u9ed1\u5bbd\u5927\u3001\u5c71\u77f3\u9cde\u5ccb\u7684\u5d16\u6d1e\u91cc\u53bb\u3002 \u3000\u3000\u5578\u58f0\u8d8a\u6765\u8d8a\u8fdc\uff0c\u50cf\u662f\u53c8\u5df2\u7a9c\u53e3\u5c71\u8179\uff0c\u5353\u6d69\u7136\u6697\u6697\u53f9\u606f\uff0c\u77e5\u9053\u8fd9\u602a\u7269\u548c\u90a3\u602a\u7269\u5de8\u6597\u529b\u4e4f\u4e4b \u4e0b\uff0c\u867d\u88ab\u81ea\u5df1\u4e00\u5251\u800c\u5de8\u521b\uff0c\u4f46\u5374\u4ecd\u672a\u5224\u5176\u6b7b\u547d\uff0c\u8fd9\uff1d\u4e0b\u7a9c\u56de\u5c71\u8179\uff0c\u60ca\u60b8\u4e4b\u4f59\uff0c\u5fc5\u5b9a\u53c8\u6709\u591a\u5e74 \u4e0d\u6562\u51fa\u6765\u3002 \u3000\u3000\u52a0\u4ee5\u8fd9\u5c71\u6d1e\u9edd\u9ed1\u65e0\u6bd4\uff0c\u5176\u4e2d\u53c8\u53ef\u80fd\u66f2\u6298\u5965\u5999\uff0c\u6df1\u4e0d\u89c1\u5e95\uff0c\u7eb5\u662f\u6b66\u529f\u518d\u5f3a\u4e4b\u4eba\uff0c\u4e5f\u7edd\u96be\u7a9c \u8fdb\u8fd9\u5c71\u8179\u53bb\u548c\u8fd9\u661f\u5f62\u7684\u5947\u6bd2\u4e4b\u7269\u640f\u6597\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u52a8\u5ff5\uff0c\u5ffd\u89c9\u5934\u8111\u4e00\u9635\u660f\u6697\uff0c\u53e3\u8179\u4e4b\u95f4\uff0c\u4e5f\u6781\u4e3a\u70e6\u6e34\uff0c\u8bd5\u4e00\u8fd0\u6c14\u7adf\u4e5f\u9a71\u4e4b\u4e0d\u6563\uff0c\u4e0d \u7531\u5927\u60ca\uff0c\u77e5\u9053\u81ea\u5df1\u65b9\u624d\u7a0d\u4e00\u4e0d\u614e\uff0c\u4fbf\u5df2\u4e2d\u4e86\u90a3\u661f\u5f62\u602a\u7269\u7684\u5de8\u6bd2\uff0c\u7acb\u5373\u76d8\u819d\u8fd0\u529f\u903c\u53bb\u3002 \u3000\u3000\u54ea\u77e5\u80cc\u540e\u7a81\u7136\u4f20\u6765\u4e00\u58f0\u51b7\u5f7b\u5165\u9aa8\u7684\u7b11\u58f0\uff0c\u4e00\u4eba\u68ee\u51b7\u4ed6\u8bf4\u9053\uff1a\u201c\u591a\u5e74\u4e0d\u89c1\uff0c\u6545\u4eba\u65e0\u6059\uff0c\u771f \u6559\u6211\u5c39\u67d0\u4eba\u559c\u4e0d\u81ea\u80dc\uff0c\u54c8\u54c8\uff0c\u559c\u4e0d\u81ea\u80dc\u3002\u201d \u3000\u3000\u8bdd\u58f0\u4e00\u5165\u5353\u6d69\u7136\u4e4b\u8033\uff0c\u4ed6\u8eab\u8eaf\u7acb\u5373\u65cb\u98ce\u822c\u7684\u4e00\u8f6c\uff0c\u811a\u8ddf\u7262\u7262\u9489\u5728\u5730\u4e0a\uff0c\u53cc\u638c\u5fae\u9519\uff0c\u76ee\u5149 \u51dd\u6ce8\uff0c\u7adf\u662f\u5168\u795e\u5f85\u654c\u4e4b\u52bf\u3002 \u3000\u3000\u80fd\u4f7f\u5f97\u540d\u626c\u5929\u4e0b\u3001\u53f7\u79f0\u6b66\u6797\u7b2c\u4e00\u9ad8\u624b\u4e4b\u79f0\u7684\u5353\u6d69\u7136\u5982\u6b64\u6212\u5907\u7684\u4eba\uff0c\u81ea\u4e5f\u4e0d\u540c\u51e1\u54cd\u3002 \u3000\u3000\u6b64\u4eba\u7fbd\u8863\u661f\u51a0\uff0c\u5374\u6b63\u662f\u4e07\u5999\u771f\u4eba\u5c39\u51e1\u3002\u6b64\u523b\u4ed6\u89c1\u5353\u6d3d\u7136\u9aa4\u7136\u53e3\u8eab\uff0c\u811a\u4e0b\u7acb\u523b\u4e5f\u4e00\u9519\u811a \u6b65\uff0c\u76ee\u5149\u5374\u5728\u5353\u6d69\u7136\u9762\u4e0a\u4e00\u8f6c\uff0c\u5ffd\u7136\u53c8\u4ef0\u5929\u957f\u7b11\u4e86\u8d77\u6765\uff0c\u7b11\u58f0\u9ad8\u5f7b\u5165\u4e91\uff0c\u76f4\u53ef\u7a7f\u91d1\u88c2\u77f3\u3002 \u3000\u3000\u7136\u540e\uff0c\u4ed6\u7b11\u58f0\u500f\u7136\u800c\u4f4f\uff0c\u76ee\u5149\u4ecd\u7136\u76ef\u5728\u5353\u6d69\u7136\u8138\u4e0a\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u4f60\u591a\u5e74\u4e0d\u89c1\uff0c\u4e4d \u4e00\u76f8\u9047\uff0c\u6211\u5374\u53c8\u8bf4\u9519\u4e86\u8bdd\uff0c\u6545\u4eba\u65e0\u6059\u8fd9\u56db\u5b57\uff0c\u4f3c\u4e4e\u8be5\u6539\u4e3a\u6545\u4eba\u6709\u6059\u624d\u5bf9\u54e9\u2014\u2014\u201d\u4ed6\u54e9\u4e4b\u4e00 \u5b57\uff0c\u62d6\u5f97\u6781\u957f\uff0c\u7136\u540e\u4fbf\u53c8\u8f6c\u53d8\u6210\u4e00\u79cd\u68ee\u51b7\u7684\u7b11\u58f0\u3002 \u3000\u3000\u5353\u6d69\u7136\u5389\u53f1\u4e00\u58f0\uff0c\u559d\u9053\uff1a\u201c\u59d3\u5c39\u7684\uff0c\u4e03\u5e74\u4ee5\u524d\uff0c\u4f60\u81ea\u8a93\u4eca\u751f\u518d\u4e5f\u4e0d\u5728\u6211\u9762\u524d\u51fa\u73b0\uff0c\u5426\u5219 \u5c31\u4efb\u51ed\u6211\u5904\u7406\uff0c\u8fd9\u8bdd\u96be\u9053\u4f60\u5df2\u5fd8\u8bb0\u4e86\u5417\uff1f\u201d \u3000\u3000\u5c39\u51e1\u7b11\u58f0\u672a\u4f4f\uff0c\u8fde\u8fde\u70b9\u5934\u9053\uff1a\u201c\u5c0f\u5f1f\u867d\u7136\u4e0d\u624d\uff0c\u4f46\u8bf4\u8fc7\u7684\u6d3b\uff0c\u5374\u518d\u4e5f\u4e0d\u4f1a\u5fd8\u8bb0\uff0c\u6b64\u523b\u5c0f \u5f1f\u5c31\u7ad9\u5728\u8fd9\u91cc\uff0c\u5353\u5927\u4fa0\u5c31\u8bf7\u8fc7\u6765\u968f\u610f\u5904\u7f6e\u533a\u533a\u5728\u4e0b\u5427\uff01\u201d \u3000\u3000\u7b11\u58f0\u4e2d\u7684\u90a3\u79cd\u8ba5\u8bbd\u800c\u53c8\u6709\u4f8d\u65e0\u6050\u7684\u610f\u5473\uff0c\u4f7f\u5f97\u961c\u6d3d\u7136\u5fc3\u4e2d\u4e0d\u7981\u4e00\u51db\uff0c\u534a\u664c\u8bf4\u4e0d\u51fa\u8bdd\u6765\uff0c \u7adf\u4f3c\u5df2\u6115\u4f4f\u4e86\u3002 \u3000\u3000\u4e07\u5999\u771f\u4eba\u5c39\u51e1\u51b7\u54fc\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5353\u5927\u4fa0\u600e\u4e0d\u4e0b\u6765\u5904\u7f6e\u533a\u533a\u5728\u4e0b\u5440\uff1f\u54e6\u3001\u54e6\uff0c\u539f\u6765\u5353\u5927 \u4fa0\u4ed7\u4e49\u9664\u5bb3\uff0c\u5374\u4e2d\u4e86\u90a3\u602a\u7269\u7684\u5de8\u6bd2\uff0c\u6b64\u523b\u2014\u2014\u54fc\uff0c\u53ea\u6015\u533a\u533a\u5728\u4e0b\u8981\u6765\u5904\u7f6e\u540d\u6ee1\u5929\u4e0b\u7684\u7b2c\u4e00\u9ad8 \u4eba\u5353\u5927\u4fa0\u4e86\u3002\u201d \u3000\u3000\u5353\u6d69\u7136\u5fc3\u4e2d\u53c8\u6025\u53c8\u6c14\uff0c\u5374\u5f3a\u81ea\u6309\u637a\u7740\uff0c\u6697\u4e2d\u8c03\u606f\u7740\u6c14\uff0c\u5e0c\u5180\u81ea\u5df1\u80fd\u9a71\u53bb\u4f53\u5185\u7684\u5de8\u6bd2\u3002 \u3000\u3000\u987b\u77e5\u5353\u6d69\u7136\u6b64\u523b\u867d\u5df2\u4e2d\u6bd2\uff0c\u4f46\u529f\u529b\u5e76\u672a\u5b8c\u5168\u5931\u53bb\uff0c\u666e\u901a\u6b66\u6797\u9ad8\u624b\uff0c\u4e5f\u4e0d\u4f1a\u5728\u4ed6\u773c\u4e0b\uff0c\u53ea \u662f\u8fd9\u5c39\u51e1\uff0c\u81ea\u79f0\u4e07\u5999\u771f\u4eba\uff0c\u4e5f\u786e\u6709\u4e9b\u771f\u624d\u5b9e\u5b66\uff0c\u5c24\u5176\u8eab\u6cd5\u4e4b\u7075\u5feb\uff0c\u66f4\u662f\u4e45\u79f0\u4e00\u7edd\u3002 \u3000\u3000\u5fc3\u4e2d\u539f\u5927\u4fa0\u5353\u6d69\u7136\uff0c\u5e73\u65f6\u81ea\u53ef\u80dc\u5f97\u4e86\u4ed6\uff0c\u4f46\u5353\u6d69\u7136\u6b64\u523b\u8eab\u4e2d\u5947\u6bd2\uff0c\u529f\u529b\u4e00\u6253\u6298\u6263\uff0c\u5982\u679c \u5bf9\u654c\u4e4b\u4e0b\uff0c\u4fbf\u662f\u51f6\u591a\u5409\u5c11\u4e86\u3002 \u3000\u3000\u90a3\u5c39\u51e1\u662f\u4f55\u7b49\u4eba\u7269\uff0c\u4e00\u7779\u5353\u6d69\u7136\u4e4b\u9762\uff0c\u4fbf\u77e5\u4ed6\u8eab\u5df2\u4e2d\u6bd2\uff0c\u662f\u4ee5\u8a00\u8bed\u8ba5\u8bbd\uff0c\u50cf\u662f\u6839\u672c\u6ca1\u6709 \u5c06\u8fd9\u4e2d\u539f\u5927\u4fa0\u653e\u5728\u773c\u91cc\u3002 \u3000\u3000\u6b64\u523b\u4ed6\u7565\u4e00\u987f\uff0c\u53c8\u81ea\u51b7\u7b11\u9053\uff1a\u201c\u5353\u5927\u4fa0\u591a\u5e74\u524d\u5c31\u66fe\u75db\u8d23\u8fc7\u533a\u533a\u5728\u4e0b\u9634\u9669\u72e1\u8bc8\uff0c\u4e00\u522b\u591a \u5e74\uff0c\u5728\u4e0b\u8fd9\u79cd\u5fc3\u6027\u8fd8\u662f\u672a\u6539\uff0c\u65b9\u624d\u56e0\u4e3a\u4e0d\u77e5\u9053\u5353\u5927\u4fa0\u8eab\u5b50\u6b20\u5b89\uff0c\u552f\u6050\u5353\u5927\u4fa0\u9664\u6bd2\u4e4b\u540e\uff0c\u5c06\u5728 \u4e0b\u4e5f\u968f\u4fbf\u9664\u53bb\uff0c\u662f\u4ee5\u5c31\u5c06\u5c0a\u592b\u4eba\u548c\u4ee4\u516c\u5b50\u5c48\u9a7e\u4e00\u5730\uff0c\u54ea\u77e5\u5728\u4e0b\u6b64\u4e3e\uff0c\u5374\u662f\u591a\u4f59\u4e86\u3002\u201d \u3000\u3000\u8a00\u4e0b\u4e4b\u610f\uff0c\u5c31\u662f\u6b64\u523b\u6211\u6839\u672c\u5c31\u53ef\u4ee5\u5bf9\u4ed8\u4f60\uff0c\u4e0d\u987b\u8981\u62ff\u4f60\u59bb\u513f\u4f5c\u4eba\u8d28\u4e86\u3002 \u3000\u3000\u5353\u6d69\u7136\u7eb5\u662f\u6db5\u517b\u529f\u6df1\uff0c\u5728\u8fd9\u79cd\u60c5\u51b5\u4e0b\uff0c\u4ecd\u80fd\u6309\u637a\u5f97\u4f4f\u81ea\u5df1\u7684\u5fc3\u6027\uff0c\u4f46\u4e00\u95fb\u7231\u59bb\u7231\u5b50\u4ff1\u5df2 \u843d\u4eba\u81ea\u5df1\u8fd9\u6700\u5927\u7684\u5bf9\u5934\u4e4b\u624b\uff0c\u60c5\u6025\u5173\u5fc3\u4e4b\u4e0b\uff0c\u81ea\u8eab\u7684\u5b89\u5371\uff0c\u65e9\u5df2\u7f6e\u4e4b\u5ea6\u5916\uff0c\u66b4\u559d\u4e00\u58f0\uff0c\u811a\u6b65 \u5fae\u60dc\uff0c\u8eab\u5f62\u5df2\u5982\u884c\u4e91\u6d41\u6c34\u822c\u63a0\u4e86\u4e0a\u6765\uff0c\u4e00\u9762\u5389\u58f0\u9053\uff1a\u201c\u59d3\u5c39\u7684\uff0c\u4f60\u82e5\u52a8\u4e86\u4e00\u5a18\u6bcd\u5b50\u4e00\u6839\u6beb \u6bdb\uff0c\u6211\u5353\u6d69\u7136\u62fc\u7740\u5316\u9aa8\u626c\u7070\uff0c\u4e5f\u8981\u5c06\u4f60\u5241\u6b7b\u4e07\u6bb5\uff01\u201d \u3000\u3000\u968f\u7740\u559d\u58f0\uff0c\u5de6\u638c\u5df2\u500f\u7136\u4f38\u51fa\uff0c\u4e94\u6307\u5fae\u5f20\uff0c\u5176\u75be\u5982\u51e4\uff0c\u4f46\u76f4\u5230\u638c\u5df2\u9012\u51fa\uff0c\u5374\u4ecd\u5e26\u4e00\u4e1d\u98ce \u58f0\u3002 \u3000\u3000\u968f\u7740\u5de6\u638c\u8fd9\u4e00\u6325\u4e4b\u52bf\uff0c\u5c24\u81ea\u6301\u5728\u53f3\u638c\u7684\u957f\u5251\uff0c\u5df2\u5e26\u7740\u4e00\u6e9c\u80b2\u84dd\u7684\u5149\u5f69\u659c\u659c\u5212\u51fa\uff0c\u5251\u52bf\u534e \u534e\uff0c\u5f84\u5212\u5c39\u51e1\u524d\u8138\u3002 \u3000\u3000\u8fd9\u4e00\u62db\u4e24\u5f0f\uff0c\u5feb\u82e5\u5954\u96f7\uff0c\u4ed6\u867d\u5df2\u529f\u529b\u53d7\u635f\uff0c\u4f46\u6b64\u523b\u60c5\u6025\u4e4b\u4e0b\uff0c\u5168\u529b\u4e00\u51fb\uff0c\u58f0\u52bf\u4e4b\u76db\uff0c\u5374 \u4ecd\u6709\u8d85\u51e1\u7edd\u4fd7\u7684\u5185\u529b\u542b\u8574\u7740\u3002 \u3000\u3000\u5c39\u51e1\u51b7\u7b11\u4e86\u4e00\u58f0\uff0c\u8eab\u5f62\u5fae\u626c\uff1b\u80a9\u4e0d\u52a8\uff0c\u817f\u4e0d\u66f2\uff0c\u8eab\u5f62\u4fbf\u5df2\u6a2a\u63a0\u4e03\u5c3a\uff0c\u51b7\u7b11\u4e00\u58f0\uff0c\u4e5f\u8d8a\u53d1 \u68ee\u51b7\u60e8\u5389\uff0c\u7adf\u5982\u67ad\u9e1f\u591c\u557c\u3002 \u3000\u3000\u5353\u6d69\u7136\u4e00\u62db\u843d\u7a7a\uff0c\u624d\u77e5\u9053\u81ea\u5df1\u771f\u529b\u53d7\u635f\u5df2\u5de8\uff0c\u95ee\u54fc\u4e00\u58f0\uff0c\u811a\u6b65\u4e00\u60dc\uff0c\u957f\u5251\u4e00\u5708\u4e00\u6296\uff0c\u7728 \u773c\u95f4\u53ea\u89c9\u5251\u70b9\u5982\u96ea\uff0c\u6f2b\u5929\u671d\u5c39\u51e1\u7f69\u4e0b\u3002 \u3000\u3000\u5c39\u51e1\u4ecd\u7136\u5374\u800c\u4e0d\u653b\uff0c\u5e26\u7740\u51c4\u5389\u7684\u7b11\u58f0\uff0c\u8eab\u5f62\u53c8\u6ed1\u5f00\u6570\u5c3a\uff0c\u4e00\u9762\u559d\u9053\uff1a\u201c\u597d\u3001\u597d\uff0c\u4f60\u65e2\u7136 \u5982\u6b64\uff0c\u5c31\u602a\u4e0d\u5f97\u6211\u59d3\u5c39\u7684\u5fc3\u72e0\u624b\u8fa3\uff0c\u8981\u4e58\u7740\u4f60\u4e2d\u6bd2\u7684\u65f6\u5019\u6740\u4f60\u3002\u201d \u3000\u3000\u4ed6\u7b11\u58f0\u8d8a\u53d1\u9ad8\u6602\uff0c\u8eab\u5f62\u5982\u98ce\u4e2d\u67f3\u7d6e\uff0c\u5de6\u6298\u53f3\u56de\uff0c\u500f\u7136\u5728\u90a3\u7f24\u7eb7\u5982\u96e8\u7684\u5251\u5f71\u4e2d\u95ea\u907f\u3002\u63a5\u7740 \u6068\u58f0\u53c8\u9053\uff1a\u201c\u4f60\u6211\u4ec7\u6df1\u4f3c\u6d77\uff0c\u4eca\u5929\u4e5f\u4e0d\u5fc5\u591a\u8bf4\u4e86\uff0c\u4f60\u5c31\u628a\u547d\u6401\u4e0b\u5427\u3002\u201d \u3000\u3000\u638c\u5f71\u7ffb\u98de\uff0c\u77ac\u606f\u4e4b\u95f4\u53c8\u62a2\u653b\u6570\u62db\uff0c\u4f46\u662f\u770b\u51fa\u8fd9\u4e2d\u539f\u5927\u4fa0\u5353\u6d69\u7136\u5df2\u8eab\u53d7\u5de8\u6bd2\uff0c\u7eb5\u7136\u529f\u529b\u518d \u6df1\uff0c\u4e5f\u7edd\u4e0d\u662f\u81ea\u5df1\u7684\u654c\u624b\u4e86\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u6b63\u90fd\u662f\u6b66\u6797\u4e2d\u7684\u7edd\u9876\u9ad8\u624b\uff0c\u8eab\u624b\u4e4b\u5feb\uff0c\u7684\u786e\u65e0\u6cd5\u5f62\u5bb9\uff0c\u4f46\u5341\u6570\u4e2a\u7167\u9762\u4e00\u8fc7\uff0c\u4e2d\u539f \u5927\u4fa0\u5353\u6d69\u7136\u624b\u5e95\u4e0b\u53ef\u5c31\u900f\u51fa\u4e0d\u652f\u6765\u4e86\u3002 \u3000\u3000\u4ed6\u4e5f\u77e5\u9053\u8fd9\u5c39\u51e1\u6b64\u8a00\u4e0d\u865a\uff0c\u81ea\u5df1\u53ea\u6015\u5df2\u6bd2\u5165\u9aa8\u9ad3\uff0c\u5c11\u65f6\u6bd2\u6027\u4e00\u53d1\u4f5c\uff0c\u81ea\u5df1\u4f7f\u5f97\u683d\u5728\u8fd9\u6c5f \u6e56\u4e0a\u7d20\u79f0\u6bd2\u624b\u7684\u4e07\u5999\u771f\u4eba\u624b\u4e0a\u3002 \u3000\u3000\u6700\u4ee4\u4ed6\u62c5\u5fc3\u7684\uff0c\u81ea\u7136\u8fd8\u662f\u4ed6\u7684\u7231\u59bb\u7231\u5b50\uff0c\u843d\u4eba\u8fd9\u9b54\u5934\u624b\u4e2d\uff0c\u5b9e\u662f\u53ef\u8651\u3002 \u3000\u3000\u6b64\u523b\u8fd9\u4e2d\u539f\u5927\u4fa0\u6b63\u662f\u5fc3\u4e2d\u601d\u6f6e\u7d0a\u4e71\uff0c\u5fc3\u795e\u4e00\u5206\uff0c\u624b\u5e95\u4e0b\u771f\u6c14\u4e5f\u5c31\u8d8a\u53d1\u4e0d\u7ee7\uff0c\u518d\u52a0\u4e0a\u4e07\u5999 \u771f\u4eba\u8f7b\u529f\u5999\u7edd\u5929\u4e0b\uff0c\u8eab\u5f62\u4e00\u6e38\u8d70\u5f00\u6765\uff0c\u4f46\u89c9\u56db\u9762\u516b\u65b9\u90fd\u662f\u4ed6\u90a3\u5bbd\u5927\u7fbd\u8863\u7684\u98d8\u98d8\u5f71\u5b50\u3002 \u3000\u3000\u5353\u6d69\u7136\u6697\u53f9\u4e00\u58f0\uff0c\u77e5\u9053\u81ea\u5df1\u4eca\u65e5\u5df2\u96be\u514d\u906d\u8fd9\u9b54\u5934\u7684\u6bd2\u4e4e\uff0c\u81ea\u5df1\u8d70\u5357\u95ef\u5317\uff0c\u51fa\u5e08\u4ee5\u6765\uff0c\u4fa0 \u540d\u4fbf\u5df2\u9707\u52a8\u5929\u4e0b\uff0c\u60f3\u4e0d\u5230\u4eca\u65e5\u5374\u683d\u5728\u8fd9\u8352\u5c71\u4e4b\u4e2d\uff0c\u683d\u5728\u8fd9\u4e00\u4e2a\u6614\u65e5\u66fe\u5728\u81ea\u5df1\u624b\u4e0b\u9003\u751f\u7684\u8d3c\u5b50 \u624b\u4e0a\u3002 \u3000\u3000\u539f\u6765\u8fd9\u4e07\u5999\u771f\u4eba\u548c\u5353\u6d69\u7136\u7684\u7231\u59bb\u675c\u4e00\u5a18\uff0c\u76f8\u8bc6\u8fd8\u5728\u5353\u6d69\u7136\u4e4b\u524d\uff0c\u5c39\u51e1\u4ed7\u7740\u81ea\u5df1\u5916\u8c8c\u4fca \u9038\uff0c\u6614\u65e5\u5728\u6c5f\u6e56\u4e0a\u9887\u6709\u74a7\u4eba\u4e4b\u8a89\uff0c\u53ea\u662f\u4ed6\u5185\u5fc3\u5374\u8fdc\u6bd4\u5916\u8c8c\u4e11\u6076\uff0c\u4e5f\u4e0d\u77e5\u6709\u591a\u5c11\u4e2a\u7389\u6d01\u51b0\u6e05\u7684 \u5c11\u5973\u6bc1\u5728\u4ed6\u7684\u624b\u4e0a\u3002 \u3000\u3000\u81ea\u4ece\u4ed6\u76f8\u8bc6\u98de\u51e4\u51f0\u4e4b\u540e\uff0c\u675c\u4e00\u5a18\u5148\u524d\u4e5f\u51e0\u4e4e\u4e3a\u4ed6\u6240\u52a8\uff0c\u4f46\u65e0\u8bba\u5982\u4f55\u4e00\u4e2a\u4eba\uff0c\u4ed6\u603b\u662f\u65e0\u6cd5 \u5c06\u81ea\u5df1\u7684\u4e11\u884c\u9690\u85cf\u5f97\u4f4f\u7684\uff0c\u5957\u53e5\u4fd7\u8bdd\uff0c\u8fd9\u4e5f\u6b63\u5982\u7eb8\u91cc\u662f\u6c38\u8fdc\u5305\u4e0d\u4f4f\u706b\u7684\uff0c\u65e5\u5b50\u4e00\u4e45\uff0c\u5c39\u51e1\u662d \u5f70\u7684\u6076\u8ff9\u81ea\u7136\u4f7f\u663e\u9732\u51fa\u6765\uff0c\u675c\u4e00\u5a18\u81ea\u7136\u4e5f\u4e0d\u4f1a\u518d\u5bf9\u4ed6\u5047\u4ee5\u534a\u70b9\u8f9e\u8272\u3002 \u3000\u3000\u4f46\u5c39\u51e1\u4e5f\u6b63\u5982\u5927\u591a\u6570\u8d2a\u6deb\u597d\u8272\u7684\u7537\u4eba\u4e00\u6837\uff0c\u5f97\u4e0d\u5230\u624b\u7684\uff0c\u6c38\u8fdc\u6700\u662f\u8bf1\u4eba\uff0c\u4ed6\u7adf\u60f3\u904d\u4e86\u5343 \u767e\u79cd\u65b9\u6cd5\uff0c\u76ef\u5728\u4e00\u5a18\u540e\u9762\uff0c\u4ee5\u671f\u80fd\u83b7\u5f97\u7f8e\u4eba\u82b3\u5fc3\u3002 \u3000\u3000\u675c\u4e00\u5a18\u5fc3\u5e95\u867d\u538c\u6076\uff0c\u4f46\u662f\u81ea\u5df1\u6b66\u529f\u5374\u4e0d\u5982\u4eba\uff0c\u6446\u8131\u53c8\u65e0\u6cd5\u6446\u8131\u5f97\u6389\uff0c\u6b63\u5728\u8fd9\u88ab\u81ea\u5df1\u7684\u7f8e \u4e3d\u62db\u6765\u4e00\u8eab\u70e6\u607c\u7684\u5c11\u5973\uff0c\u4e3a\u8fd9\u79cd\u5351\u4e0b\u7684\u7ea0\u7f20\u800c\u70e6\u607c\u7684\u65f6\u5019\uff0c\u5979\u9047\u7740\u4e86\u4e2d\u539f\u5927\u4fa0\u5353\u6d69\u7136\u3002 \u3000\u3000\u5f88\u5feb\u7684\uff0c\u5979\u5c31\u88ab\u5353\u6d69\u7136\u7684\u82f1\u98ce\u4fa0\u9aa8\u6240\u52a8\uff0c\u4e24\u4eba\u5728\u829c\u6e56\u5927\u8c6a\u4e91\u8c26\u7684\u64ae\u5408\u4e0b\uff0c\u7ed3\u6210\u8fde\u7406\uff0c\u5f53 \u65f6\u6c5f\u6e56\u4e2d\u4eba\u90fd\u5728\u4e3a\u8fd9\u5bf9\u59fb\u7f18\u6b23\u559c\u2014\u2014\u5f53\u7136\uff0c\u8981\u9664\u53bb\u90a3\u6076\u8ff9\u662d\u5f70\u3001\u6ee1\u6000\u90aa\u5ff5\u7684\u5c39\u51e1\u4e86\u3002 \u3000\u3000\u8fd9\u5c39\u51e1\u89c1\u5230\u81ea\u5df1\u7684\u6614\u5fc3\u79ef\u8651\u5168\u90e8\u6210\u7a7a\uff0c\u7f9e\u607c\u4e4b\u4e0b\uff0c\u7adf\u5728\u5353\u6d01\u7136\u548c\u675c\u4e00\u5a18\u7684\u82b1\u70db\u591c\uff0c\u6f5c\u5165 \u65b0\u5b85\uff0c\u60f3\u4ee5\u5351\u8d31\u65e0\u803b\u7684\u4e0b\u4e09\u95e8\u4f0e\u4fe9\u2014\u2014\u4e94\u9f13\u9e21\u9e23\u8fd4\u9b42\u9999\uff0c\u8ff7\u5012\u8fd9\u4e00\u5bf9\u65b0\u4eba\u3002 \u3000\u3000\u4f46\u4e2d\u539f\u5927\u4fa0\u90a3\u65f6\u5e74\u7eaa\u867d\u5c1a\u8f7b\uff0c\u9605\u5386\u5374\u5df2\u4e0d\u51e1\uff0c\u600e\u4f1a\u8ba9\u4ed6\u5f97\u624b\uff0c\u5c39\u51e1\u7684\u4ed9\u9e64\u5634\u5c1a\u672a\u6247\u52a8\u2014 \u2014\u90a3\u79cd\u6c5f\u6e56\u4e0a\u6700\u8457\u6076\u540d\u7684\u4e0b\u4e09\u95e8\u6697\u5668\u201c\u4e94\u9f13\u9e21\u9e23\u8fd4\u9b42\u9999\u201d\uff0c\u901a\u5e38\u90fd\u662f\u88c5\u5728\u4e00\u4e2a\u94dc\u5236\u7684\u4ed9\u9e64\u91cc \u9762\uff0c\u4e00\u70b9\u4e0a\u706b\uff0c\u4e24\u7fc5\u4e00\u6247\u52a8\u4eba\u8ff7\u9999\u5c31\u88ab\u9001\u51fa\u2014\u2014\u4ed6\u88ab\u5353\u6d69\u7136\u76db\u6012\u4e4b\u4e0b\u7684\u8fde\u73af\u4e09\u638c\uff0c\u51fb\u4f24\u4e86\u53f3 \u81c2\uff0c\u8fd8\u5e78\u597d\u5353\u6d69\u7136\u5728\u559c\u671f\u4e4b\u5185\u4e0d\u613f\u4f24\u4eba\uff0c\u53c8\u987e\u7740\u675c\u4e00\u5a18\u7684\u9762\u5b50\uff0c\u624d\u5bb9\u4ed6\u9003\u8d70\u3002 \u3000\u3000\u81ea\u6b64\u4ee5\u540e\uff0c\u5c39\u51e1\u77e5\u9053\u81ea\u5df1\u65e0\u8bba\u5728\u54ea\u4e00\u65b9\u9762\u3001\u90fd\u4e0d\u662f\u8fd9\u4e2d\u539f\u5927\u4fa0\u7684\u654c\u624b\uff0c\u7f9e\u6012\u5992\u6068\u4e4b\u4e0b\uff0c \u4ed6\u7adf\u8fdc\u4eba\u82d7\u8352\uff0c\u82e6\u6c42\u79d8\u6280\uff0c\u518d\u4eba\u6c5f\u6e56\u7684\u65f6\u5019\uff0c\u8fd9\u6b66\u6797\u4e2d\u7684\u6d6a\u5b50\u7adf\u7136\u6362\u4e86\u4e00\u8eab\u9053\u88c5\uff0c\u6b66\u529f\u4e5f\u66f4 \u4e3a\u4e0d\u51e1\uff0c\u884c\u4e8b\u4e5f\u66f4\u4e3a\u6b79\u6bd2\uff0c\u53ef\u662f\u4ed6\u5374\u4ecd\u7136\u4e0d\u662f\u5353\u6d69\u7136\u7684\u5bf9\u624b\u3002 \u3000\u3000\u4ed6\u5bf9\u5353\u6d69\u7136\u592b\u5987\u7ea0\u7f20\u591a\u5e74\uff0c\u5353\u6d69\u7136\u603b\u662f\u4f53\u8c05\u7740\u4ed6\u548c\u81ea\u5df1\u7684\u7231\u59bb\u662f\u76f8\u8bc6\u3002\u4e3a\u7740\u514d\u5f97\u843d\u4e0b\u4e00 \u4e2a\u6c14\u91cf\u72ed\u5c0f\u7684\u53e3\u5b9e\uff0c\u4ed6\u603b\u662f\u7559\u7ed9\u5c39\u51e1\u4e00\u6761\u751f\u8def\uff0c\u5c39\u51e1\u81ea\u5df1\u5fd6\u91cf\uff0c\u8fd1\u5e74\u6765\u4e5f\u5c31\u77e5\u8da3\u4e00\u4e9b\u4e86\u3002 \u3000\u3000\u54ea\u77e5\u9053\u6b64\u523b\u5353\u6d3d\u7136\u7adf\u5728\u529b\u9664\u5de8\u5bb3\uff0c\u81ea\u5df1\u4e5f\u4e2d\u4e86\u6df1\u6bd2\u7684\u65f6\u5019\uff0c\u548c\u8fd9\u79ef\u6028\u591a\u5e74\u7684\u5bbf\u4ec7\u72ed\u8def\u76f8 \u9022\uff0c\u66f4\u7cdf\u7684\u662f\u8fd9\u4e00\u4ee3\u5927\u5feb\u7684\u7231\u59bb\u7231\u5b50\u5168\u843d\u5165\u4e86\u8fd9\u9b54\u5934\u7684\u624b\u91cc\uff0c\u540e\u679c\u6b63\u662f\u4e0d\u582a\u8bbe\u60f3\u3002 \u3000\u3000\u5353\u6d69\u7136\u5251\u52bf\u5982\u8679\uff0c\u5251\u82b1\u9519\u4e71\uff0c\u4f46\u4ed6\u81ea\u5df1\u53ef\u4e5f\u77e5\u9053\u8fd9\u79cd\u5728\u6b66\u6797\u4e2d\u5df2\u53ef\u626b\u8361\u7fa4\u9b54\u7684\u5251\u6cd5\uff0c\u6b64 \u523b\u5df2\u56e0\u4f53\u5185\u7684\u5de8\u6bd2\u800c\u4f7f\u529f\u529b\u5927\u5927\u5730\u6253\u4e86\u4e2a\u6298\u6263\uff0c\u5df2\u62ff\u8fd9\u79cd\u8f7b\u529f\u5999\u7edd\u7684\u9b54\u5934\u4e07\u5999\u771f\u4eba\u65e0\u53ef\u5948\u4f55 \u4e86\u3002 \u3000\u3000\u4ed6\u53cc\u76ee\u706b\u8d64\uff0c\u84e6\u7136\u5927\u559d\u4e00\u58f0\uff0c\u5251\u5c3e\u5bd2\u8292\u66b4\u957f\uff0c\u811a\u4e0b\u65b9\u4f4d\u5fae\u9519\u4e4b\u95f4\uff0c\u957f\u5251\u5237\u5237\uff0c\u63a5\u8fde\u62a2\u653b \u6570\u5251\uff0c\u5b9b\u5982\u9635\u9635\u7535\u95ee\u3002 \u3000\u3000\u5728\u8fd9\u79cd\u7684\u60c5\u5f62\u4e0b\uff0c\u8fd9\u4e00\u4ee3\u5927\u4fa0\u7684\u84c4\u529b\u6570\u5251\uff0c\u52bf\u631f\u4f59\u54b8\uff0c\u4ecd\u7136\u4e0d\u540c\u51e1\u54cd\uff0c\u5c39\u51e1\u6697\u6697\u5fc3\u60ca\u4e4b \u4e2d\uff0c\u957f\u8896\u8fde\u6325\uff0c\u8eab\u5f62\u500f\u7136\u6ed1\u5f00\u4e00\u4e08\u3002 \u3000\u3000\u4ed6\u65b9\u81ea\u4ed7\u7740\u7edd\u9876\u7684\u8f7b\u529f\u907f\u5f00\u8fd9\u6570\u5251\uff0c\u5374\u89c1\u5353\u6d69\u7136\u5251\u52bf\u5374\u731b\u7136\u4e00\u6536\uff0c\u5251\u5c16\u5fae\u5fae\u4e0b\u5782\uff0c\u77aa\u7740 \u706b\u8d64\u7684\u53cc\u76ee\uff0c\u5411\u4ed6\u5389\u58f0\u559d\u9053\uff1a\u201c\u59d3\u5c39\u7684\uff0c\u4eca\u65e5\u6211\u5353\u6d69\u7136\u547d\u8be5\u4e27\u6b64\uff0c\u53ea\u602a\u6211\u59d3\u5353\u7684\u6614\u5e74\u5fc3\u6148\u624b \u8f83\uff0c\u602a\u4e0d\u4e86\u522b\u4eba\uff0c\u53ea\u8981\u4f60\u59d3\u5c39\u7684\u82e5\u8fd8\u6709\u70b9\u4eba\u5fc3\uff0c\u6211\u5353\u6d69\u7136\u5c31\u846c\u9001\u5728\u4f60\u624b\u91cc\uff0c\u4e5f\u7edd\u4e0d\u4f1a\u76b1\u4e00\u76b1 \u7709\u5934\uff0c\u53ef\u662f\u4f60\u4e00--\u201d\u5c39\u51e1\u655e\u58f0\u4e00\u9635\u5c16\u7b11\uff0c\u957f\u8896\u5fae\u62c2\uff0c\u500f\u7136\u987f\u4f4f\u7b11\u58f0\uff0c\u9634\u607b\u607b\u5730\u63a5\u53e3\u7b11\u9053\uff1a \u201c\u597d\u8bf4\uff0c\u597d\u8bf4\uff0c\u5353\u5927\u4fa0\u6b7b\u5728\u533a\u533a\u5728\u4e0b\u624b\u4e0a\uff0c\u53ef\u771f\u6709\u70b9\u51a4\u6789\u3002\u201d \u3000\u3000\u4ed6\u80dc\u7b97\u5728\u63e1\uff0c\u77e5\u9053\u65f6\u95f4\u6bcf\u8fc7\u4e00\u523b\uff0c\u90a3\u5353\u6d69\u7136\u8eab\u53d7\u7684\u5de8\u6bd2\u4e5f\u5c31\u53d1\u4f5c\u5f97\u66f4\u5389\u5bb3\uff0c\u56e0\u6b64\u4ed6\u4e5f\u8fdc \u8fdc\u5730\u7ad9\u7740\uff0c\u9634\u9634\u5730\u51b7\u7b11\uff0c\u5e76\u4e0d\u51fa\u624b\uff0c\u5374\u53ea\u8bf4\u4e9b\u8ba5\u5632\u7684\u8a00\u8bed\uff0c\u6765\u6fc0\u53d1\u8fd9\u4fa0\u5fc3\u78ca\u843d\u7684\u5353\u6d69\u7136\u7684\u6012 \u6c14\u3002 \u3000\u3000\u5353\u6d69\u7136\u6d51\u8eab\u98a4\u6296\uff0c\u53cc\u7709\u4e00\u6839\u6839\u5012\u7acb\u7740\uff0c\u4f46\u662f\u4ecd\u5f3a\u81ea\u6309\u637a\uff0c\u5389\u58f0\u9053\uff1a\u201c\u6211\u5353\u6d69\u7136\u548c\u4f60\u7eb5\u7136 \u4ec7\u6df1\u4f3c\u6d77\uff0c\u597d\u670b\u53cb\u53ea\u7ba1\u628a\u5e10\u7b97\u5230\u6211\u4e00\u4e2a\u4eba\u8eab\u4e0a\uff0c\u4f60\u59d3\u5c39\u7684\u53ea\u8981\u8bf4\u4e00\u53e5\u8bdd\uff0c\u8ba9\u6211\u5353\u6d3d\u7136\u600e\u4e48\u6b7b \u6cd5\u90e8\u53ef\u4ee5\uff0c\u53ea\u662f\u2014\u2014\u201d\u5c39\u51e1\u518d\u6b21\u4e00\u9635\u957f\u7b11\uff0c\u6253\u65ad\u4e86\u4ed6\u7684\u8bdd\uff0c\u76ee\u5149\u90aa\u6076\u5730\u4e00\u8f6c\uff0c\u9053\uff1a\u201c\u5353\u5927 \u4fa0\uff0c\u4f60\u653e\u5fc3\uff0c\u6211\u5c39\u67d0\u4eba\u867d\u7136\u5728\u4f60\u4e00\u4ee3\u5927\u4fa0\u773c\u4e2d\u4ec5\u53ea\u662f\u4e2a\u8df3\u6881\u5c0f\u4e11\uff0c\u53ef\u662f\u8fd8\u4e0d\u81f4\u4e8e\u5bf9\u4ed8\u4e00\u4e2a\u5c0f \u5b69\u5b50\uff0c\u5353\u5927\u4fa0\u7684\u4ee4\u90ce\uff0c\u6b64\u523b\u6b63\u5b89\u5b89\u7a33\u7a33\u5730\u548c\u5c0f\u5f92\u4eec\u7761\u5728\u4e00\u8d77\uff0c\u5982\u679c\u5353\u5927\u4fa0\u6492\u624b\u897f\u53bb\uff0c\u4ed6\u4e5f\u4f1a \u6d3b\u5f97\u597d\u597d\u7684\uff0c\u4e00\u70b9\u4e5f\u51fa\u4e0d\u4e86\u9519\uff0c\u81f3\u4e8e\u2014\u2014\u201d\u4ed6\u6545\u610f\u7a0d\u7a0d\u4e00\u987f\uff0c\u770b\u5230\u8fd9\u5df2\u6210\u6d45\u6c34\u4e4b\u9f99\u7684\u4e00\u4ee3\u5927 \u4fa0\u7684\u8138\u4e0a\uff0c\u679c\u7136\u95ea\u8fc7\u4e86\u4e00\u4e1d\u5b89\u6170\u7684\u8868\u60c5\u3002 \u3000\u3000\u5c39\u51e1\u5634\u89d2\u72de\u7b11\u4e00\u4e0b\uff0c\u53c8\u63a5\u7740\u9053\uff1a\u201c\u81f3\u4e8e\u5353\u5927\u4fa0\u7684\u592b\u4eba\uff0c\u90a3\u5c0f\u53ef\u66f4\u53ef\u4ee5\u62c5\u4fdd\u5979\u5728\u5353\u5927\u4fa0\u5f52 \u897f\u4e4b\u540e\uff0c\u6d3b\u5f97\u4f1a\u66f4\u52a0\u8212\u670d\uff0c\u6211\u59d3\u5c39\u7684\u4e00\u5b9a\u628a\u5979\u670d\u4f8d\u5f97\u8212\u8212\u670d\u670d\u7684\uff0c\u4f60\u653e\u5fc3\u2014\u2014\u201d\u4ed6\u8bdd\u58f0\u672a \u843d\uff0c\u5353\u6d69\u7136\u53c8\u5df2\u5389\u543c\u4e00\u58f0\uff0c\u6251\u4e86\u4e0a\u6765\uff0c\u638c\u4e2d\u7ffb\u98de\u6251\u6253\uff0c\u662f\u8fdb\u8eab\u81f4\u547d\u4e0d\u8981\u547d\u7684\u62db\u672f\uff0c\u663e\u7136\u5c06\u81ea \u5df1\u751f\u6b7b\u7f6e\u4e4b\u5ea6\u5916\u3002 \u3000\u3000\u5c39\u51e1\u5374\u4ecd\u662f\u8fde\u8fde\u9634\u7b11\uff0c\u8eab\u5f62\u5982\u884c\u4e91\u6d41\u6c34\u822c\u5730\u95ea\u907f\u7740\uff0c\u5076\u5c14\u957f\u8896\u4e00\u6325\uff0c\u53d1\u51fa\u597d\u733e\u800c\u9634\u6bd2\u7684 \u4e00\u62db\uff0c\u65e6\u4e0d\u5230\u62db\u5f0f\u7528\u8001\uff0c\u4fbf\u53c8\u7acb\u523b\u64a4\u8eab\u800c\u9000\uff0c\u8fd9\u9b54\u5934\u6b64\u523b\u7adf\u60f3\u5c06\u5353\u6d69\u7136\u7f20\u5f97\u5de8\u6bd2\u5168\u53d1\uff0c\u4e0d\u652f \u5012\u5730\uff0c\u518d\u6162\u6162\u5730\u51fa\u624b\uff0c\u8ba9\u8fd9\u4e2d\u539f\u5927\u4fa0\u53d7\u5c3d\u4e86\u51cc\u8fb1\u518d\u6b7b\u3002 \u3000\u3000\u6b64\u65f6\u5c71\u98ce\u4f4e\u5578\uff0c\u4f46\u5374\u66d9\u5149\u5df2\u9732\u3002 \u3000\u3000\u5c71\u5d16\u4e0b\u65b9\u7ecf\u60e8\u52ab\u7684\u767e\u517d\uff0c\u6b63\u90fd\u7531\u90a3\u6761\u5c71\u8def\u9000\u51fa\uff0c\u4e00\u4e2a\u4e2a\u5782\u7740\u5934\uff0c\u5939\u7740\u5c3e\u5df4\uff0c\u4f3c\u4e4e\u5bf9\u65b9\u624d \u7684\u90a3\u4e00\u573a\u60e8\u52ab\uff0c\u6b64\u65f6\u72b9\u6709\u4f59\u60b8\u3002 \u3000\u3000\u5c31\u8fde\u864e\u8c79\u8c7a\u72fc\u8fd9\u7c7b\u7684\u6076\u517d\uff0c\u6b64\u523b\u4e5f\u662f\u65e0\u7cbe\u6253\u91c7\u7684\uff0c\u5a01\u98ce\u5c3d\u715e\uff0c\u5374\u50cf\u662f\u4e00\u53ea\u53ea\u4e27\u4e86\u5bb6\u7684\u72d7 \u4e00\u6837\uff0c\u751a\u81f3\u72b9\u6709\u8fc7\u4e4b\u3002 \u3000\u3000\u7a81\u7136\u2014\u2014 \u3000\u3000\u8fdc\u5904\u63a0\u6765\u4e00\u5927\u4e00\u5c0f\u4e24\u6761\u4eba\u5f71\uff0c\u8fdc\u8fdc\u770b\u53bb\uff0c\u53ea\u89c1\u8fd9\u4e24\u4eba\u4eff\u4f5b\u662f\u5fa1\u98ce\u800c\u884c\uff0c\u8fde\u811a\u5c16\u90fd\u6ca1\u6709\u671d \u5730\u9762\u4e0a\u70b9\u4e00\u4e0b\uff0c\u5feb\u5f97\u96be\u4ee5\u63cf\u8ff0\u3002 \u3000\u3000\u8d70\u8fd1\u4e86\u624d\u770b\u51fa\u8fd9\u4e24\u4eba\u5176\u4e2d\u9ad8\u7684\u4e00\u4eba\uff0c\u7a7f\u7740\u4e00\u8eab\u5927\u7ea2\u7684\u8863\u88f3\uff0c\u8863\u670d\u53c8\u7d27\uff0c\u7d27\u7d27\u5730\u5305\u5728\u5979\u90a3 \u72b9\u5982\u4e00\u6bb5\u67af\u7af9\u822c\u7684\u8eab\u8eaf\u4e0a\u3002 \u3000\u3000\u5934\u4e0a\u4e91\u9b13\u9ad8\u633d\uff0c\u68b3\u7684\u5374\u662f\u968f\u5510\u4e00\u4ee3\u95fa\u4e2d\u5c11\u5987\u6700\u4e3a\u76db\u884c\u7684\u5760\u9a6c\u888b\uff0c\u73af\u4f69\u53ee\u5f53\uff0c\u5728\u5c71\u98ce\u4e2d\u53d1 \u7740\u6781\u4e3a\u60a6\u8033\u7684\u58f0\u97f3\u3002 \u3000\u3000\u8fd9\u88c5\u675f\u672c\u5df2\u4e0d\u4f26\u4e0d\u7c7b\u5df2\u6781\uff0c\u518d\u4e00\u770b\u90a3\u8138\u4e0a\uff0c\u5374\u66f4\u662f\u4e11\u5f97\u5413\u4eba\uff0c\u4e00\u5f20\u51e0\u4e4e\u88c2\u5230\u4e24\u816e\u7684\u5927\u5634 \u4e0a\uff0c\u5374\u53c8\u504f\u504f\u6d82\u6ee1\u4e86\u80ed\u8102\uff0c\u770b\u4e0a\u53bb\u66f4\u72b9\u5982\u8840\u76c6\u4f3c\u7684\uff0c\u6df1\u591c\u4e4b\u4e2d\u89c1\u4e86\uff0c\u6015\u4e0d\u628a\u5979\u8ba4\u4f5c\u591c\u8fdb\u624d \u602a\u3002 \u3000\u3000\u53ea\u662f\u8fd9\u53c8\u4e11\u53c8\u602a\u7684\u5973\u4eba\uff0c\u6b66\u529f\u5374\u4f3c\u597d\u5230\u6781\u5904\uff0c\u8eab\u5f62\u5c55\u52a8\u5904\uff0c\u4e0d\u4f46\u80a9\u4e0d\u52a8\uff0c\u8170\u4e0d\u66f2\uff0c\u5c31\u8fde \u4e24\u6761\u817f\u90fd\u751f\u50cf\u6ca1\u6709\u5f2f\u66f2\u4e00\u4e0b\u4f3c\u7684\u3002 \u3000\u3000\u6b64\u523b\u5979\u53f3\u624b\u633d\u7740\u4e00\u4e2a\u5e74\u7eaa\u4e5f\u5927\u7ea6\u53ea\u6709\u5341\u4e8c\u4e09\u5c81\u7684\u5973\u5b69\u5b50\u8d34\u5730\u63a0\u6765\uff0c\u8fd9\u5973\u5b69\u5b50\u5374\u6070\u6070\u548c\u5979 \u6210\u4e86\u4e00\u4e2a\u6781\u5f3a\u70c8\u7684\u5bf9\u6bd4\uff0c\u660e\u7738\u6a31\u5507\uff0c\u68a8\u7a9d\u9690\u73b0\uff0c\u7ade\u7f8e\u4e3d\u5f97\u6709\u5982\u897f\u5929\u738b\u6bcd\u7476\u6c60\u8fb9\u7684\u7389\u5973\u3002 \u3000\u3000\u8fd9\u7ea2\u88f3\u4e11\u5987\u63a0\u81f3\u6b64\u5730\u540e\uff0c\u5bf9\u6b63\u5728\u6fc0\u6597\u4e2d\u7684\u4e24\u4eba\u773c\u89d2\u90fd\u6ca1\u6709\u671b\u4e0a\u4e00\u773c\uff0c\u597d\u50cf\u662f\u8fd9\u60ca\u592d\u52a8\u5730 \u7684\u5de8\u6597\uff0c\u5e76\u672a\u66fe\u653e\u5728\u5979\u773c\u4e0b\u4f3c\u7684\u3002 \u3000\u3000\u5979\u63a0\u5230\u5c71\u5d16\u8fb9\uff0c\u76ee\u5149\u5411\u4e0b\u9762\u4e00\u626b\uff0c\u6b64\u65f6\u90a3\u4e00\u7247\u76c6\u5730\u4e0a\uff0c\u53ea\u5269\u4e0b\u4e86\u4e0d\u77e5\u591a\u5c11\u6761\u6bd2\u866b\u86c7\u517d\u8840 \u6c61\u72fc\u85c9\u7684\u5c38\u8eab\uff0c\u548c\u90a3\u4e2a\u5c71\u74a7\u4e0a\u7684\u5de8\u6d1e\u3002 \u3000\u3000\u5979\u76ee\u5149\u4e00\u626b\u4e4b\u4e0b\uff0c\u7709\u5934\u4f3c\u4e4e\u8f7b\u8f7b\u4e00\u76b1\uff0c\u7136\u540e\u8f6c\u8fc7\u8eab\u53bb\uff0c\u671d\u90a3\u6fc0\u6597\u4e2d\u7684\u4e24\u4eba\u671b\u4e86\u4e00\u773c\uff0c\u4e24 \u6761\u626b\u628a\u4f3c\u7684\u7709\u6bdb\uff0c\u5374\u53c8\u8f7b\u8f7b\u4e00\u76b1\u3002 \u3000\u3000\u7136\u540e\u5979\u4fa7\u8eab\u671d\u90a3\u6b63\u7728\u52a8\u7740\u4e24\u53ea\u5927\u773c\u775b\u7684\u7f8e\u4e3d\u5973\u5b69\u5b50\u8bf4\u9063\uff1a\u201c\u747e\u513f\uff0c\u4f60\u6015\u4e0d\u6015\uff1f\u201d \u3000\u3000\u58f0\u97f3\u867d\u4e5f\u96be\u542c\u5f97\u5413\u4eba\uff0c\u4f46\u8bed\u8c03\u5374\u662f\u6e29\u67d4\u7684\uff0c\u5c31\u50cf\u662f\u6148\u6bcd\u5728\u5bf9\u7231\u5973\u8bf4\u8bdd\u4f3c\u7684\u3002 \u3000\u3000\u90a3\u5973\u5b69\u5b50\u7684\u4e24\u53ea\u660e\u7738\u6b63\u4e00\u8f6c\u4e00\u8f6c\u7684\uff0c\u4e00\u4f1a\u513f\u8f6c\u5230\u5c71\u5d16\u4e0b\u7684\u90a3\u4e00\u7247\u60e8\u70c8\u666f\u8c61\u4e0a\uff0c\u4e00\u4f1a\u513f\u53c8 \u8f6c\u5230\u90a3\u6b63\u5728\u5c71\u5d16\u4e0a\u5de8\u6597\u7684\u4e24\u4eba\u8eab\u4e0a\u3002 \u3000\u3000\u5979\u76ee\u5149\u4e2d\uff0c\u663e\u7136\u6709\u4e9b\u5bb3\u6015\u7684\u666f\u8c61\uff0c\u4f46\u542c\u4e86\u90a3\u7ea2\u8863\u4e11\u5973\u95ee\u5979\u7684\u8bdd\uff0c\u5374\u5c06\u5979\u90a3\u7f8e\u4e3d\u7684\u5934\u6447\u4e86 \u51e0\u4e0b\uff0c\u62ac\u5934\u671b\u4e86\u90a3\u4e11\u5973\u4e00\u773c\uff0c\u8f7b\u58f0\u9053\uff1a\u201c\u5a18\u5a18\uff0c\u6211\u4e0d\u6015\u3002\u201d \u3000\u3000\u90a3\u7ea2\u8863\u4e11\u5973\u7b11\u4e00\u7b11\uff0c\u8fd9\u4e00\u4e0b\u5634\u89d2\u771f\u7684\u54a7\u5411\u4e24\u816e\u4e86\uff0c\u7136\u540e\u624d\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u5c31\u7ad9\u5728\u8fd9\u91cc\u522b \u52a8\uff0c\u6211\u8fc7\u53bb\u95ee\u90a3\u4e24\u4e2a\u81ed\u7537\u4eba\u4e00\u53e5\u8bdd\u3002\u201d \u3000\u3000\u5973\u5b69\u5b50\u70b9\u4e86\u70b9\u5934\uff0c\u7ea2\u8863\u4e11\u5973\u8eab\u5f62\u4e00\u52a8\uff0c\u4f7f\u5df2\u63a0\u5230\u5353\u6d69\u7136\u548c\u5c39\u51e1\u7684\u8eab\u65c1\uff0c\u53cc\u638c\u865a\u7a7a\u671d\u4e24\u4eba \u4e2d\u95f4\u4e00\u63a8\uff0c\u5374\u5e26\u53bb\u4e00\u822c\u65e0\u5f62\u7684\u52b2\u6c14\u3002 \u3000\u3000\u6b64\u523b\u90a3\u5353\u6d69\u7136\u4f53\u5185\u7684\u6bd2\u6027\u5df2\u66f4\u89c1\u53d1\u4f5c\uff0c\u6b64\u523b\u53ea\u4e0d\u8fc7\u662f\u5728\u6323\u547d\u7f62\u4e86\uff0c\u4ed6\u5bf9\u8fd9\u7ea2\u8863\u5987\u5973\u7684\u524d \u6765\uff0c\u8d77\u5148\u6839\u672c\u6ca1\u6709\u6ce8\u610f\u5230\u3002 \u3000\u3000\u4f46\u662f\u8fd9\u4e11\u5973\u53cc\u638c\u4e00\u53d1\uff0c\u4ed6\u548c\u5c39\u51e1\u53ef\u4fbf\u90fd\u611f\u89c9\u51fa\u90a3\u80a1\u60ca\u4eba\u7684\u529b\u9053\u4e86\uff0c\u53cc\u65b9\u90fd\u4ee5\u4e3a\u5bf9\u65b9\u6765\u4e86 \u5e2e\u624b\uff0c\u5fc3\u4e2d\u4e00\u60ca\u4e4b\u4e0b\uff0c\u5404\u5404\u8eab\u5f62\u6ed1\u5f00\u6570\u5c3a\uff0c\u76ee\u5149\u4e0d\u671f\u7136\u7684\u843d\u5728\u8fd9\u4e11\u5973\u8eab\u4e0a\uff0c\u81ea\u7136\u4e5f\u5168\u90e8\u4f4f\u4e86 \u624b\u3002 \u3000\u3000\u4e07\u5999\u771f\u4eba\u76ee\u5149\u4e00\u63a5\u89e6\u5230\u8fd9\u7ea2\u8863\u4e11\u5973\uff0c\u7acb\u523b\u5c55\u989c\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u539f\u6765\u662f\u6e29\u59d1\u5a18\u6765\u4e86\uff0c\u60f3\u4e0d \u5230\uff0c\u60f3\u4e0d\u5230\uff0c\u6e29\u59d1\u5a18\u4e0d\u5728\u82d7\u7586\u7eb3\u798f\uff0c\u5374\u5230\u4e86\u8fd9\u91cc\u6765\uff0c\u5c0f\u53ef\u81ea\u4ece\u591a\u5e74\u524d\u548c\u6e29\u59d1\u5a18\u89c1\u8fc7\u4e00\u9762\uff0c\u4e00 \u76f4\u6df1\u94ed\u5728\u5fc3\uff0c\u66f4\u60f3\u4e0d\u5230\u8fd9\u4e48\u591a\u5e74\u6765\u6e29\u59d1\u5a18\u8fd8\u662f\u6731\u989c\u672a\u6539\uff0c\u771f\u662f\u4e00\u5982\u4ed9\u5b50\u54e9\u3002\u201d \u3000\u3000\u90a3\u88ab\u79f0\u4e3a\u6e29\u59d1\u5a18\u7684\u4e11\u5973\u4e24\u53ea\u773c\u775b\u77aa\u5728\u4ed6\u8eab\u4e0a\uff0c\u5c39\u51e1\u8bf4\u8bdd\u7684\u65f6\u5019\uff0c\u5979\u59cb\u7ec8\u58f0\u8272\u672a\u52a8\uff0c\u4e0d\u559c \u4e0d\u6012\uff0c\u76f4\u5230\u4ed6\u8bdd\u8bf4\u5b8c\u4e86\uff0c\u624d\u51b7\u54fc\u4e00\u58f0\u9053\uff1a\u201c\u5c0f\u5b50\uff0c\u4f60\u5c11\u62cd\u6211\u6e29\u5982\u7389\u7684\u9a6c\u5c41\uff0c\u6211\u6e29\u5982\u7389\u53ef\u4e0d\u5403 \u8fd9\u4e00\u5957\u3002\u201d \u3000\u3000\u8fd9\u4e11\u5973\u5c45\u7136\u53eb\u5982\u7389\uff0c\u4f46\u662f\u5c39\u51e1\u8138\u4e0a\u5374\u6ca1\u6709\u4e00\u4e1d\u73a9\u7b11\u7684\u795e\u8272\uff0c\u6bd5\u606d\u6bd5\u656c\u5730\u9053\uff1a\u201c\u6e29\u59d1\u5a18\uff0c \u4f60\u6765\u8fd9\u91cc\uff0c\u6709\u4f55\u89c1\u6559\u5417\uff1f\u201d \u3000\u3000\u90a3\u6e29\u5982\u7389\u53c8\u54fc\u4e86\u4e00\u58f0\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u4eec\u6253\u4f60\u4eec\u7684\uff0c\u6211\u53ef\u4e0d\u7ba1\uff0c\u6211\u53ea\u95ee\u4f60\uff0c\u521a\u521a\u90a3\u5c71\u6d1e\u91cc \u662f\u4e0d\u662f\u6709\u4e00\u4e2a\u50cf\u4e94\u89d2\u661f\u4e00\u6837\u7684\u602a\u7269\u8dd1\u51fa\u6765\uff0c\u73b0\u5728\u8dd1\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u201d \u3000\u3000\u5c39\u51e1\u201c\u54e6\u201d\u4e86\u4e00\u58f0\uff0c\u773c\u73e0\u56db\u4e0b\u4e00\u8f6c\uff0c\u624d\u5e26\u7740\u4e00\u8138\u7b11\u5bb9\u9053\uff1a\u201c\u8fd9\u4e2a\u5c0f\u53ef\u4e5f\u4e0d\u592a\u6e05\u695a\uff0c\u6e29\u59d1 \u5a18\u6700\u597d\u8fd8\u662f\u95ee\u95ee\u8fd9\u4f4d\u5427\u2014\u2014\u201d\u4ed6\u624b\u6307\u4e00\u6307\u5353\u6d69\u7136\uff0c\u53c8\u9053\uff1a\u201c\u8fd9\u4f4d\u5c31\u662f\u540d\u9707\u4e2d\u539f\u7684\u4e00\u4ee3\u5927\u4fa0\u5353 \u6d69\u7136\uff0c\u6e29\u59d1\u5a18\u53ef\u66fe\u89c1\u8fc7\uff1f\u201d \u3000\u3000\u81ea\u4ece\u8fd9\u7ea2\u8863\u4e11\u5973\u51fa\u73b0\u4e4b\u540e\uff0c\u5353\u6d69\u7136\u5c31\u95ed\u8d77\u773c\u775b\uff0c\u6697\u6697\u8c03\u606f\u771f\u6c14\uff0c\u4ed6\u6e38\u4fa0\u5929\u4e0b\uff0c\u4e5f\u77e5\u9053\u8fd9 \u7ea2\u8863\u4e11\u5973\u5c31\u662f\u4e45\u5c45\u82d7\u7586\u3001\u6b66\u6797\u4e2d\u6700\u602a\u7684\u602a\u4eba\u4e4b\u4e00\uff0c\u81ea\u79f0\u662f\u4e11\u5973\u7684\u7ea2\u8863\u5a18\u5a18\u6e29\u5982\u7389\u3002 \u3000\u3000\u8fd9\u6e29\u5982\u7389\u867d\u7136\u81ea\u79f0\u4e11\u5973\uff0c\u4e3b\u5e73\u6700\u72af\u5fcc\u7684\uff0c\u5374\u5c31\u662f\u522b\u4eba\u8bf4\u5979\u4e11\uff0c\u65e0\u8bba\u662f\u8c01\uff0c\u4e00\u72af\u5979\u8fd9\u5fcc\u8bb3 \u7684\uff0c\u5979\u82e5\u77e5\u9053\uff0c\u60f3\u5c3d\u529e\u6cd5\u4e5f\u8981\u5c06\u90a3\u4eba\u7f6e\u4e4b\u4e8e\u6b7b\u5730\u3002 \u3000\u3000\u9664\u6b64\u4e4b\u5916\uff0c\u5979\u4ec0\u4e48\u4e8b\u90fd\u4e0d\u7ba1\uff0c\u53ea\u8981\u4e0d\u5f97\u7f6a\u5979\uff0c\u5c31\u662f\u6709\u4eba\u5728\u5979\u9762\u524d\u6740\u4e86\u5979\u7238\u7238\uff0c\u5979\u8fde\u773c\u89d2 \u90fd\u4e0d\u4f1a\u779f\u4e00\u773c\uff0c\u53ef\u662f\u5979\u81ea\u5df1\u5374\u4e5f\u4ece\u6765\u4e0d\u53bb\u884c\u6076\u3002 \u3000\u3000\u6b66\u6797\u4e2d\uff0c\u5dee\u4e0d\u591a\u5168\u90e8\u77e5\u9053\u5979\u8fd9\u6bdb\u75c5\uff0c\u56e0\u6b64\u8c01\u4e5f\u4e0d\u613f\u610f\u53bb\u60f9\u5979\uff0c\u8fd9\u813e\u6c14\u602a\u5230\u6781\u5904\u3001\u6b66\u529f\u5374 \u4e5f\u9ad8\u5230\u6781\u5904\u7684\u602a\u4eba\uff0c\u65e0\u8bba\u4eba\u524d\u80cc\u540e\uff0c\u5927\u5bb6\u90fd\u662f\u79f0\u5979\u4e3a\u7ea2\u8863\u59d1\u5a18\uff0c\u751a\u81f3\u662f\u7ea2\u8863\u4ed9\u5b50\u3002 \u3000\u3000\u56e0\u6b64\u5353\u6d69\u7136\u77e5\u9053\u5979\u51b3\u4e0d\u4f1a\u4f38\u624b\u5e2e\u54ea\u4e00\u65b9\uff0c\u662f\u4ee5\u4ed6\u7acb\u523b\u8fd0\u6c14\u8c03\u606f\uff0c\u518d\u6c42\u4e00\u62fc\uff0c\u56e0\u4e3a\u4ed6\u77e5\u9053 \u4eca\u65e5\u751f\u65e2\u4e0d\u80fd\uff0c\u6b7b\u4e5f\u6b7b\u4e0d\u5f97\uff0c\u9664\u4e86\u5c3d\u529b\u4e00\u62fc\uff0c\u4ee5\u671f\u80fd\u548c\u8fd9\u9b54\u5934\u5c39\u51e1\u540c\u5f52\u4e8e\u5c3d\u4e4b\u5916\u6839\u672c\u522b\u65e0\u4ed6 \u6cd5\u3002 \u3000\u3000\u6b64\u523b\u90a3\u6e29\u5982\u7389\u542c\u4e86\u5c39\u51e1\u7684\u8bdd\uff0c\u5634\u89d2\u4e0d\u5c51\u5730\u6487\u4e86\u4e00\u6487\uff0c\u76ee\u5149\u5c31\u8f6c\u5230\u4ed6\u8eab\u4e0a\uff0c\u4e0a\u4e0a\u4e0b\u4e0b\u5730\u671d \u4ed6\u6253\u91cf\u4e86\u51e0\u773c\uff0c\u624d\u51b7\u51b7\u4ed6\u8bf4\u9053\uff1a\u201c\u5582\uff0c\u521a\u624d\u6211\u8bf4\u7684\u8bdd\uff0c\u4f60\u542c\u5230\u4e86\u6ca1\u6709\uff1f\u201d \u3000\u3000\u5353\u6d69\u7136\u53cc\u76ee\u4e00\u5f20\uff0c\u6115\u4e86\u4e00\u6115\uff0c\u4ed6\u59d4\u5b9e\u6ca1\u6709\u6ce8\u610f\u8fd9\u5973\u602a\u4eba\u65b9\u624d\u8bf4\u7684\u4ec0\u4e48\u8bdd\uff0c\u52c9\u5f3a\u5c06\u53cc\u624b\u62f1 \u4e86\u62f1\uff0c\u65b9\u60f3\u8bf4\u4e24\u53e5\u8bdd\uff0c\u514d\u5f97\u62db\u60f9\u6b64\u4eba\uff0c\u987b\u77e5\u4ed6\u6b64\u65f6\u6b64\u523b\uff0c\u662f\u518d\u4e5f\u4e0d\u80fd\u591a\u7ed3\u5f3a\u654c\u7684\u4e86\u3002 \u3000\u3000\u54ea\u77e5\u5c39\u51e1\u5374\u7a81\u7136\u51b7\u7b11\u4e00\u58f0\uff0c\u62a2\u7740\u8bf4\u9053\uff1a\u201c\u6e29\u59d1\u5a18\uff0c\u5353\u5927\u4fa0\u5a01\u540d\u8d6b\u8d6b\uff0c\u522b\u4eba\u7684\u8bdd\uff0c\u5353\u5927\u4fa0 \u662f\u61d2\u5f97\u53bb\u542c\u7684\uff01\u201d \u3000\u3000\u90a3\u6e29\u5982\u7389\u679c\u7136\u53c8\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u76ee\u5149\u53c8\u4e0a\u4e0a\u4e0b\u4e0b\u671d\u5353\u6d69\u7136\u626b\u89c6\u7740\uff0c\u53c8\u51b7\u51b7\u5730\u91cd\u590d\u4e86\u4e00 \u53e5\uff1a\u201c\u521a\u624d\u6211\u8bf4\u7684\u8bdd\uff0c\u4f60\u542c\u5230\u4e86\u5417\uff1f\u201d \u3000\u3000\u4e2d\u539f\u5927\u4fa0\u540d\u9707\u5929\u4e0b\uff0c\u51e0\u65f6\u53d7\u8fc7\u8fd9\u6837\u7684\u6c14\uff0c\u51e0\u65f6\u88ab\u4eba\u5bb6\u9047\u5230\u8fd9\u79cd\u6c42\u751f\u4e0d\u5f97\u3001\u6c42\u6b7b\u4ea6\u4e0d\u80fd\u7684 \u60c5\u51b5\u8fc7\u3002 \u3000\u3000\u6b64\u523b\u4ed6\u53ea\u89c9\u5f97\u5fc3\u80f8\u4e4b\u95f4\uff0c\u4eff\u4f5b\u5835\u585e\u4e86\u4e00\u5757\u6781\u5927\u7684\u77f3\u5757\uff0c\u60b2\u6124\u3001\u6028\u6068\u3001\u6c14\u5fff\uff0c\u4f7f\u5f97\u8fd9\u4e00\u751f \u820d\u5df1\u4e3a\u4eba\u3001\u4ed7\u4e49\u884c\u4fa0\u7684\u5353\u6d69\u7136\u82e5\u4e0d\u662f\u987e\u53ca\u81ea\u5df1\u7684\u7231\u59bb\u7231\u5b50\uff0c\u771f\u8981\u5f53\u573a\u6a2a\u5251\u81ea\u520e\u5728\u8fd9\u9ec4\u5c71\u59cb\u4fe1 \u5cf0\u4e0b\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u8fd9\u65f6\u53ea\u80fd\u5f3a\u81ea\u6309\u637a\u7740\uff0c\u9053\uff1a\u201c\u6e29\u5927\u4fa0\uff0c\u5c0f\u53ef\u8eab\u53d7\u5de8\u6bd2\uff0c\u4e00\u65f6\u758f\u5ffd\uff0c\u4ee5\u81f4\u6ca1\u6709\u7559\u610f \u9601\u4e0b\u7684\u8bdd\uff0c\u8fd8\u2026\u2026\u201d \u3000\u3000\u4ed6\u4e00\u751f\u78ca\u843d\uff0c\u8fd9\u6837\u59d4\u5c48\u7684\u8bdd\uff0c\u4ece\u672a\u8bf4\u8fc7\uff0c\u53eb\u4ed6\u518d\u8bf4\u201c\u8bf7\u6055\u7f6a\u201d\u4e00\u7c7b\u7684\u8bdd\uff0c\u4ed6\u5982\u4f55\u8bf4\u5f97\u51fa \u6765\uff0c\u56e0\u6b64\u4ed6\u53ea\u5f97\u987f\u4f4f\u4e86\u3002 \u3000\u3000\u90a3\u5c39\u51e1\u51b7\u54fc\u4e00\u58f0\uff0c\u65b9\u60f3\u518d\u8bf4\u51e0\u53e5\u6311\u62e8\u7684\u8bdd\uff0c\u8ba9\u8fd9\u7d20\u79f0\u96be\u60f9\u7684\u7ea2\u8863\u5a18\u5a18\u5148\u51fa\u4e8e\u6765\u5bf9\u4ed8\u8fd9\u5df2 \u662f\u5f3a\u5f29\u4e4b\u672a\u7684\u5353\u6d69\u7136\u3002 \u3000\u3000\u90a3\u4e48\u6839\u672c\u4e0d\u7528\u81ea\u5df1\u51fa\u624b\uff0c\u8fd9\u4e00\u4ee3\u5927\u4fa0\u4fbf\u8ba4\u4e86\u5e10\uff0c\u81ea\u5df1\u975e\u4f46\u6bcb\u987b\u80cc\u4e0a\u6740\u6b7b\u4e2d\u539f\u5927\u4fa0\u7684\u6076 \u540d\uff0c\u751a\u81f3\u8fd8\u53ef\u4ee5\u5728\u522b\u4eba\u9762\u524d\u5356\u5356\u597d\uff0c\u518d\u8005\u81ea\u5df1\u4ee5\u540e\u4e5f\u4e0d\u5fc5\u62c5\u5fc3\u6709\u4eba\u6765\u66ff\u5353\u6d69\u7136\u62a5\u4ec7\u3002 \u3000\u3000\u54ea\u77e5\u4ed6\u5982\u610f\u7b97\u76d8\u6b63\u5728\u6253\u5f97\u53ee\u5f53\u4f5c\u54cd\u7684\u65f6\u5019\uff0c\u5374\u542c\u6e29\u5982\u7389\u5df2\u5728\u8bf4\u9053\uff1a\u201c\u6211\u95ee\u4f60\u65b9\u624d\u7a7f\u5c71\u800c \u51fa\u7684\u90a3\u53ea\u5343\u5e74\u661f\u870d\uff0c\u6b64\u523b\u8dd1\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u201d \u3000\u3000\u5353\u6d69\u7136\u5fc3\u91cc\u6697\u53f9\u4e00\u58f0\uff0c\u5fd6\u9053\uff1a\u201c\u8fd9\u6e29\u5982\u7389\u679c\u7136\u662f\u4e00\u4ee3\u5f02\u4eba\uff0c\u5979\u6839\u672c\u521a\u6765\uff0c\u5374\u5df2\u77e5\u9053\u90a3\u7a7f \u5c71\u800c\u51fa\u7684\u602a\u517d\u7684\u540d\u5b57\uff0c\u770b\u6765\u8fd9\u6b66\u6797\u754f\u60e7\u7684\u5973\u9b54\u5934\uff0c\u771f\u7684\u540d\u4e0d\u865a\u4f20\u54e9\u3002\u201d \u3000\u3000\u4ed6\u4e00\u9762\u5728\u5fc3\u4e2d\u601d\u5fd6\uff0c\u4e00\u9762\u9053\uff1a\u201c\u90a3\u661f\u870d\u88ab\u5c0f\u53ef\u594b\u529b\u51fb\u4f24\u4e24\u5904\uff0c\u53c8\u4ece\u5b83\u51fa\u6765\u4e4b\u5904\u7a7f\u5165\u5c71\u8179 \u4e86\u3002\u201d \u3000\u3000\u6e29\u5982\u7389\u76ee\u5149\u4e00\u8f6c\uff0c\u5374\u53c8\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u6ee1\u8138\u4e0d\u4fe1\u4efb\u4ed6\u8bf4\u9053\uff1a\u201c\u771f\u7684\u5417\uff1f\u201d \u3000\u3000\u5353\u6d69\u7136\u52c9\u5f3a\u5fcd\u4f4f\u6c14\uff0c\u5c06\u65b9\u624d\u5982\u4f55\u6709\u53e6\u4e00\u602a\u86c7\u4e0e\u90a3\u661f\u870d\u6076\u6597\uff0c\u5982\u4f55\u4e24\u8d25\u4ff1\u4f24\uff0c\u81ea\u5df1\u53c8\u5982\u4f55 \u4ee5\u638c\u4e2d\u5251\u529b\u521b\u661f\u870d\u7684\u4e8b\uff0c\u6e90\u6e90\u672c\u672c\u4ed6\u8bf4\u4e86\u51fa\u6765\u3002 \u3000\u3000\u8fd9\u6e29\u52a0\u7389\u4e00\u9762\u51dd\u89c6\u503e\u542c\u4e00\u4e00\u9762\u8138\u4e0a\u5c31\u9732\u51fa\u4eff\u4f5b\u6781\u4e3a\u559c\u60a6\u7684\u5149\u91c7\uff0c\u4f46\u4e2d\u539f\u5927\u4fa0\u4e00\u53e3\u6c14\u8bf4\u4e86 \u8fd9\u4e48\u591a\u8bdd\uff0c\u6c14\u529b\u5374\u66f4\u4e0d\u652f\u4e86\u3002 \u3000\u3000\u6e29\u5982\u7389\u4e00\u8f6c\u8eab\uff0c\u5934\u4e0a\u7684\u73af\u9970\u54cd\u4e86\u4e00\u4e0b\uff0c\u5374\u53c8\u56de\u5934\u6765\uff0c\u95ee\u9053\uff1a\u201c\u90a3\u6761\u602a\u7269\u53ef\u662f\u8fd8\u5728\u4e0b \u9762\uff1f\u201d \u3000\u3000\u5353\u6d69\u7136\u70b9\u4e86\u70b9\u5934\uff0c\u6e29\u5982\u7389\u8eab\u5f62\u52a8\u5904\uff0c\u7acb\u523b\u63a0\u5230\u5d16\u8fb9\uff0c\u671d\u90a3\u7f8e\u8c8c\u5982\u82b1\u7684\u5c11\u5973\u4f4e\u4f4e\u5631\u5490\u4e86\u513f \u53e5\uff0c\u7adf\u7136\u7eb5\u8eab\u671d\u5d16\u4e0b\u8dc3\u53bb\u3002 \u3000\u3000\u8fd9\u8fb9\u5c39\u51e1\u7b49\u6e29\u5982\u7389\u8f6c\u8eab\u79bb\u53bb\uff0c\u773c\u73e0\u4e00\u4eae\uff0c\u4eff\u4f5b\u4e5f\u7a81\u7136\u60f3\u8d77\u4e00\u4e8b\uff0c\u671b\u4e86\u5353\u6d69\u7136\u4e00\u773c\uff0c\u51b7\u7b11 \u51e0\u58f0\uff0c\u7adf\u4e5f\u671d\u5c71\u5d16\u4e0b\u63a0\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u8fd9\u4e00\u6765\uff0c\u5374\u4ee4\u5353\u6d3d\u7136\u4e00\u6115\uff0c\u4f46\u4ed6\u968f\u5373\u60f3\u5230\uff0c\u90a3\u602a\u86c7\u5c38\u8eab\u4e2d\uff0c\u5fc5\u5b9a\u6709\u7740\u4ec0\u4e48\u6781\u4e3a\u96be\u5f97\u7684\u5947 \u73cd\u5f02\u5b9d\uff0c\u4ee5\u81f4\u5f15\u8d77\u4e86\u8fd9\u7537\u5973\u9b54\u5934\u4e24\u4eba\u7684\u8d2a\u5fc3\uff0c\u4ee4\u5f97\u5c39\u51e1\u7adf\u6682\u653e\u4e0b\u4e86\u81ea\u5df1\uff0c\u524d\u53bb\u593a\u5b9d\u3002 \u3000\u3000\u4ed6\u5fc3\u5ff5\u4e00\u8f6c\u4e4b\u4e0b\uff0c\u7acb\u523b\u53d1\u72c2\u4e86\u4f3c\u7684\u671d\u5148\u524d\u675c\u4e00\u5a18\u6bcd\u5b50\u5b58\u8eab\u4e4b\u5904\u5954\u53bb\uff0c\u6b64\u523b\u4ed6\u5df2\u77e5\u9053\u81ea\u5df1 \u8eab\u4e2d\u5947\u6bd2\uff0c\u6d3b\u547d\u5df2\u7136\u65e0\u671b\u3002 \u3000\u3000\u4ed6\u4ec5\u4ec5\u5e0c\u671b\u5728\u81ea\u5df1\u8eab\u6b7b\u4e4b\u524d\uff0c\u80fd\u628a\u81ea\u5df1\u7684\u7231\u59bb\u7231\u5b50\u9001\u5230\u5b89\u5168\u4e4b\u5904\uff0c\u80fd\u591f\u9003\u51fa\u9b54\u5934\u5c39\u51e1\u7684 \u6bd2\u624b\uff0c\u5c06\u6765\u4e5f\u597d\u4e3a\u81ea\u5df1\u590d\u4ec7\u3002 \u3000\u3000\u56e0\u4e4b\u4ed6\u62fc\u5c3d\u6700\u540e\u4e00\u4e1d\u4f59\u529b\uff0c\u53d1\u72c2\u800c\u5954\uff0c\u8fd9\u4e00\u6bb5\u8def\u4ee5\u4ed6\u8fd9\u79cd\u8f7b\u529f\u7684\u4eba\u8bf4\u6765\uff0c\u5e76\u4e0d\u751a\u957f\uff0c\u4f46 \u4ed6\u6b64\u523b\u5374\u72b9\u5982\u5343\u4e07\u91cc\u822c\u9065\u8fdc\u3002 \u3000\u3000\u4f46\u7ec8\u7a76\u4ed6\u8fd8\u662f\u5230\u4e86\uff0c\u4ed6\u53ea\u89c9\u5f97\u5fc3\u80f8\u4e4b\u4e2d\uff0c\u4e00\u9635\u4e00\u9635\u7684\u8165\u6c14\u7ffb\u6d8c\uff0c\u76ee\u5149\u56de\u626b\u4e4b\u5904\uff0c\u81ea\u5df1\u7684 \u7231\u59bb\u7231\u5b50\u5374\u5df2\u5931\u53bb\u4e86\u8e2a\u8ff9\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u4e00\u6025\uff0c\u90a3\u79cd\u6076\u81ed\u7684\u8165\u6c14\u5c31\u53d1\u4f5c\u5f97\u66f4\u5389\u5bb3\uff0c\u771f\u6c14\u4e5f\u66f4\u4e0d\u7ee7\u3002 \u3000\u3000\u4f46\u662f\u7236\u5b50\u3001\u592b\u59bb\u4e4b\u95f4\u7684\u6df1\u539a\u7684\u60c5\u611f\uff0c\u5374\u50cf\u4e00\u79cd\u65e0\u6bd4\u795e\u5947\u7684\u529b\u91cf\u5728\u652f\u6301\u7740\u4ed6\uff0c\u4ed6\u7a0d\u5fae\u5598\u4e86 \u4e24\u53e3\u6c14\uff0c\u4fbf\u7acb\u523b\u8eab\u5f62\u518d\u8d77\uff0c\u671d\u524d\u9762\u5954\u53bb\u3002 \u3000\u3000\u4ed6\u4eff\u4f5b\u662f\u4e00\u53ea\u4e2d\u4e86\u7bad\u7684\u82cd\u9e70\u4f3c\u7684\uff0c\u5728\u8fd9\u7247\u5c71\u5d16\u7684\u4e0a\u4e0b\u56db\u5468\u641c\u5bfb\u7740\uff0c\u8fd9\u65f6\u4ed6\u5598\u7740\u6c14\u7684\u58f0 \u97f3\uff0c\u5df2\u6e10\u6e10\u53d8\u5f97\u66f4\u4e3a\u7c97\u5927\u4e86\u3002 \u3000\u3000\u7a81\u7136\u2014\u2014 \u3000\u3000\u4ed6\u542c\u5230\u4e00\u9635\u4eba\u8bed\uff0c\u987b\u77e5\u4ed6\u4fee\u4e3a\u591a\u5e74\uff0c\u5728\u8fd9\u79cd\u60c5\u51b5\u4e0b\uff0c\u795e\u667a\u4ecd\u672a\u660f\u4e71\uff0c\u4e8e\u662f\u4ed6\u7acb\u523b\u5faa\u7740\u90a3 \u58f0\u97f3\u7684\u6765\u8def\u98de\u5feb\u7684\u63a0\u53bb\u3002 \u3000\u3000\u5728\u4e00\u5757\u5de8\u77f3\u7684\u540e\u9762\uff0c\u4ed6\u770b\u5230\u6709\u4e09\u4e2a\u5782\u9aeb\u7ae5\u5b50\u6b63\u5728\u4f4e\u58f0\u8bf4\u7740\u8bdd\uff0c\u770b\u5230\u4ed6\u6765\u4e86\uff0c\u4fbf\u90fd\u4e00\u8d77\u4f4f \u53e3\uff0c\u516d\u53ea\u773c\u775b\u60ca\u5413\u5730\u671b\u7740\u4ed6\u3002 \u3000\u3000\u4ed6\u76ee\u5149\u4e00\u8f6c\uff0c\u5fc3\u5934\u4e0d\u7981\u731b\u7136\u4e00\u9635\u5de8\u8df3\uff0c\u98d3\u5730\uff0c\u8eab\u5f62\u7a9c\u4e86\u4e0a\u53bb\u3002 \u3000\u3000\u539f\u6765\u4ed6\u770b\u5230\u5728\u8fd9\u4e09\u4e2a\u5782\u9aeb\u7ae5\u5b50\u7684\u8eab\u4fa7\uff0c\u626d\u66f2\u7684\u5367\u7740\u4e24\u4eba\uff0c\u663e\u7136\u88ab\u4eba\u70b9\u4e2d\u4e86\u7a74\u9053\uff0c\u8fd9\u4e24 \u4eba\uff0c\u5374\u6b63\u662f\u5353\u6d69\u7136\u7684\u7231\u59bb\u548c\u7231\u5b50\u3002 \u3000\u3000\u4ed6\u72c2\u543c\uff0c\u6251\u5230\u675c\u4e00\u5a18\u8eab\u4e0a\uff0c\u6d51\u8eab\u9aa8\u8282\u5374\u50cf\u662f\u5df2\u7ecf\u677e\u6563\u4e86\u4f3c\u7684\uff0c\u8111\u4e2d\u4e5f\u4e00\u9635\u6655\u7729\uff0c\u54c7\u7684\u4e00 \u58f0\uff0c\u5f20\u53e3\u5410\u51fa\u4e00\u80a1\u5e26\u7740\u9c9c\u8840\u7684\u9178\u6c34\u6765\uff0c\u5374\u6b63\u5410\u5728\u90a3\u6dec\u4e0d\u53ca\u9632\u4e4b\u4e0b\u3001\u88ab\u5c39\u51e1\u70b9\u4e2d\u4e86\u7a74\u9053\u7684\u675c\u4e00 \u5a18\u8eab\u4e0a\u3002 \u3000\u3000\u675c\u4e00\u5a18\u611f\u5230\u81ea\u5df1\u7684\u4e08\u592b\u6765\u5230\uff0c\u82b3\u5fc3\u65b9\u81ea\u4e00\u9635\u60ca\u559c\uff0c\u6084\u7136\u7741\u5f00\u773c\u6765\uff0c\u5374\u770b\u89c1\u81ea\u5df1\u7684\u4e08\u592b\u7adf \u50cf\u662f\u53d7\u4e86\u91cd\u4f24\uff0c\u7adf\u7136\u5410\u51fa\u8840\u6765\u3002 \u3000\u3000\u5979\u5fc3\u4e2d\u4e0d\u7981\u5927\u9a87\uff0c\u4f46\u662f\u81ea\u5df1\u6b64\u523b\u7a74\u9053\u88ab\u70b9\uff0c\u9664\u4e86\u773c\u775b\u5c1a\u80fd\u52a8\u4e4b\u5916\uff0c\u8fde\u4e00\u53e5\u8bdd\u90fd\u8bf4\u4e0d\u51fa\uff0c \u53ea\u80fd\u5c06\u773c\u5149\u6e29\u67d4\u800c\u60b2\u54c0\u5730\u6295\u5728\u5353\u6d69\u7136\u8eab\u4e0a\u3002 \u3000\u3000\u5353\u6d69\u7136\u77e5\u9053\u8fd9\u5df2\u662f\u751f\u6b7b\u4e00\u7ebf\u7684\u5173\u5934\u4e86\uff0c\u81ea\u5df1\u82e5\u4e0d\u80fd\u5728\u6781\u5feb\u7684\u65f6\u95f4\u4e4b\u5185\u81ea\u6551\uff0c\u90a3\u4e48\u81ea\u5df1\u4e0d \u4f46\u8981\u547d\u4e27\u6b64\u5904\uff0c\u6700\u60e8\u7684\u8fd8\u662f\u8fde\u7231\u59bb\u4e5f\u4f1a\u53d7\u8fb1\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u52c9\u5f3a\u6323\u624e\u7740\uff0c\u60f3\u5148\u66ff\u59bb\u5b50\u89e3\u5f00\u7a74\u9053\uff0c\u4f46\u662f\u6d51\u8eab\u7684\u9aa8\u8282\u50cf\u662f\u88ab\u5480\u56bc\u4f3c\u7684\u75db\u82e6\uff0c\u751f\u50cf \u662f\u6709\u866b\u8681\u5728\u91cc\u9762\u6512\u884c\u7740\u4f3c\u7684\uff1f \u3000\u3000\u4ed6\u7ec8\u7a76\u6323\u624e\u7740\uff0c\u76ee\u5149\u6295\u5728\u7231\u59bb\u8eab\u4e0a\u4e00\u626b\uff0c\u77e5\u9053\u5979\u6240\u88ab\u70b9\u4e2d\u4e86\u7684\uff0c\u6b63\u662f\u6c14\u6d77\u4fde\u7a74\uff0c\u77e5\u9053\u5979 \u5f53\u65f6\u672a\u53ca\u8f6c\u8eab\uff0c\u5c31\u5df2\u88ab\u70b9\u4e2d\u7a74\u9053\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u6697\u9a82\u4e00\u58f0\uff0c\u65b9\u81ea\u4f38\u624b\u66ff\u4ed6\u7684\u7231\u59bb\u89e3\u5f00\u7a74\u9053\u2014\u2014\u54ea\u77e5\u8eab\u540e\u7a81\u7136\u98ce\u58f0\u98d3\u7136\uff0c\u81ea\u5df1\u4e24\u81c2 \u540c\u65f6\u88ab\u4eba\u6293\u4f4f\uff0c\u5c31\u50cf\u662f\u7a81\u7136\u52a0\u4e86\u4e24\u9053\u94c1\u7b8d\u4f3c\u7684\uff0c\u5176\u75db\u5f7b\u9aa8\u3002 \u3000\u3000\u968f\u5373\uff0c\u8eab\u540e\u6709\u4e24\u4e2a\u4eba\u7684\u58f0\u97f3\u540c\u65f6\u95f4\u9053\uff1a\u201c\u90a3\u6761\u86c7\u54ea\u91cc\u53bb\u4e86\uff1f\u201d \u3000\u3000\u5353\u6d69\u7136\u4e0d\u7528\u56de\u5934\uff0c\u5c31\u77e5\u9053\u8fd9\u4e24\u4e2a\u58f0\u97f3\u4e00\u4e2a\u53d1\u81ea\u5c39\u51e1\uff0c\u4e00\u4e2a\u5374\u662f\u53d1\u81ea\u90a3\u7ea2\u8863\u5a18\u5a18\u6e29\u5982\u7389\u3002 \u3000\u3000\u800c\u5c31\u5728\u8fd9\u540c\u4e00\u5239\u90a3\uff0c\u98de\u51e4\u51f0\u5df2\u652f\u8d77\u4e86\u8eab\u5b50\uff0c\u674f\u773c\u5706\u7741\uff0c\u6307\u7740\u5c39\u51e0\u9a82\u9053\uff1a\u201c\u4f60\u8fd9\u8be5\u788e\u5c38\u4e07 \u6bb5\u7684\u8d3c\u5b50\uff0c\u4f60\u2014\u2014\u4f60\u7b80\u76f4\u732a\u72d7\u4e0d\u5982\u2026\u2026\u4f60\u2026\u2026\u201d \u3000\u3000\u8fd9\u98de\u51e4\u51f0\u675c\u4e00\u5a18\u867d\u662f\u6c5f\u6e56\u5973\u5b50\uff0c\u4f46\u751f\u6027\u5982\u83b2\uff0c\u6e05\u9999\u96c5\u51c0\uff0c\u9a82\u4eba\u7684\u8bdd\uff0c\u8bf4\u4e0d\u51fa\u53e3\uff0c\u6c14\u6124\u4e4b \u4e2d\uff0c\u9a82\u4e86\u4e24\u53e5\uff0c\u5374\u9a82\u4e0d\u4e0b\u53bb\u4e86\u3002 \u3000\u3000\u90a3\u6e29\u5982\u7389\u773c\u89d2\u4e00\u6a2a\u5c39\u51e1\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u628a\u4f60\u7684\u624b\u653e\u5f00\u3002\u201d \u3000\u3000\u539f\u6765\u65b9\u624d\u4ed6\u4e24\u4eba\u5728\u5d16\u4e0b\u641c\u5bfb\u4e00\u904d\uff0c\u6839\u672c\u6ca1\u6709\u90a3\u602a\u517d\u7684\u5f71\u5b50\uff0c\u4e24\u4eba\u6025\u6012\u4e4b\u4e2d\uff0c\u53c8\u7acb\u523b\u8d76 \u6765\uff0c\u7adf\u7136\u4e00\u4eba\u4e00\u624b\uff0c\u6293\u4f4f\u529f\u529b\u5df2\u5931\u7684\u5353\u6d69\u7136\u7684\u53cc\u81c2\u3002 \u3000\u3000\u5c39\u51e1\u5fc3\u4e2d\u4e00\u8f6c\uff0c\u4e8e\u7b11\u4e00\u58f0\uff0c\u653e\u4e0b\u4e86\u6293\u7740\u5353\u6d69\u7136\u7684\u624b\uff0c\u90a3\u9ec4\u8863\u7ae5\u4e8e\u5df2\u6251\u5230\u4ed6\u8eab\u4e0a\uff0c\u4ed6\u5c31\u7528 \u90a3\u53ea\u624b\u5728\u8fd9\u7ae5\u4e8e\u5934\u4e0a\u62cd\u4e86\u4e00\u62cd\u3002 \u3000\u3000\u90a3\u6e29\u5982\u7389\u5374\u5c06\u5353\u6d69\u7136\u8f6c\u4e86\u4e2a\u9762\uff0c\u76ee\u5149\u68ee\u51b7\u5982\u5200\uff0c\u5389\u58f0\u95ee\u9053\uff1a\u201c\u6211\u95ee\u4f60\u7684\u8bdd\u4f60\u542c\u5230\u6ca1 \u6709\uff1f\u201d \u3000\u3000\u54ea\u77e5\u5353\u6d3d\u7136\u5374\u4ecd\u7136\u5782\u7740\u5934\uff0c\u6ca1\u6709\u56de\u7b54\uff0c\u6e29\u5982\u7389\u90a3\u672c\u5df2\u4e11\u602a\u5df2\u6781\u7684\u8138\u4e0a\uff0c\u6b64\u523b\u66f4\u72b9\u5982\u5c71\u7cbe \u9b3c\u602a\u822c\u3001\u56e0\u6124\u6012\u800c\u53d8\u5f97\u901a\u7ea2\u4e86\u3002 \u3000\u3000\u5979\u624b\u8155\u4e00\u6296\uff0c\u9634\u6bd2\u7684\u5185\u529b\uff0c\u4fbf\u4f20\u5230\u5353\u6d69\u7136\u8eab\u4e0a\u53bb\uff0c\u4e00\u9762\u9053\uff1a\u201c\u6211\u5148\u8ba9\u4f60\u5c1d\u5c1d\u8fd9\u4e5d\u9634\u641c\u9aa8 \u624b\u7684\u5473\u9053\uff0c\u4f60\u8981\u662f\u518d\u4e0d\u8bf4\uff0c\u53ef\u522b\u602a\u59d1\u5a18\u518d\u7ed9\u4f60\u597d\u53d7\u7684\u3002\u201d \u3000\u3000\u54ea\u77e5\u5353\u6d69\u7136\u5782\u7740\u5934\uff0c\u8fde\u58f0\u606f\u90fd\u6ca1\u6709\uff0c\u6e29\u5982\u7389\u4f4e\u5934\u4e00\u770b\uff0c\u539f\u6765\u8fd9\u540d\u9707\u5929\u4e0b\u7684\u4e00\u4ee3\u5927\u4fa0\uff0c\u8eab \u4e2d\u5947\u6bd2\u4e4b\u540e\uff0c\u53c8\u5984\u7528\u771f\u529b\uff0c\u518d\u52a0\u4e0a\u5fc3\u4e2d\u7684\u6025\u607c\uff0c\u600e\u7981\u5f97\u8d77\u8fd9\u4e24\u4eba\u7684\u4e00\u6293\uff0c\u6b64\u523b\u5fc3\u8109\u5df2\u65ad\uff0c\u8fd9 \u820d\u5df1\u4e3a\u4eba\u3001\u78ca\u843d\u7684\u5947\u7537\u5b50\uff0c\u7adf\u4e27\u751f\u5728\u8fd9\u9ec4\u5c71\u91cc\u3002 \u3000\u3000\u90a3\u98de\u51e4\u51f0\u60e8\u53eb\u4e00\u58f0\uff0c\u548c\u8eab\u6251\u4e86\u4e0a\u6765\uff0c\u8840\u6cea\u4ea4\u6d41\uff0c\u4e00\u9762\u60e8\u5389\u5730\u559d\u53eb\u9053\uff1a\u201c\u4f60\u8fd9\u4e2a\u2026\u2026\u201d\u5979 \u6c14\u8840\u65b9\u901a\uff0c\u5c31\u6251\u4e0a\u53bb\uff0c\u5374\u8fd8\u4e0d\u77e5\u9053\u5979\u4e08\u592b\u5df2\u7ecf\u6b7b\u4e86\u3002 \u3000\u3000\u5979\u8fd9\u4e00\u9a82\uff0c\u5374\u6b63\u89e6\u4e86\u4e11\u4eba\u6e29\u5982\u7389\u7684\u5de8\u6012\uff0c\u65b9\u624d\u5979\u904d\u5bfb\u90a3\u8eab\u6709\u5947\u5b9d\u7684\u602a\u86c7\u4e0d\u5f97\uff0c\u5df2\u662f\u6ee1\u542b \u6012\u706b\uff0c\u6b64\u523b\u66f4\u662f\u706b\u5192\u4e09\u4e08\u3002 \u3000\u3000\u8fd9\u5a01\u6151\u6b66\u6797\u7684\u9b54\u5934\u6b64\u523b\u51b7\u54fc\u4e00\u58f0\uff0c\u53f3\u638c\u4e00\u626c\uff0c\u5c06\u5353\u6d69\u7136\u7684\u5c38\u8eab\u8fdc\u8fdc\u629b\u5f00\uff0c\u624b\u638c\u4e00\u7ffb\u5c31\u671d \u98de\u8eab\u6251\u6765\u7684\u675c\u4e00\u5a18\u5288\u53bb\u3002 \u3000\u3000\u98de\u51e4\u51f0\u675c\u4e00\u5a18\u4ea6\u662f\u5973\u4e2d\u8c6a\u6770\uff0c\u6b66\u529f\u672c\u4e5f\u4e0d\u5f31\uff0c\u600e\u5948\u5979\u6b64\u523b\u9047\u7740\u5374\u662f\u8fd9\u79cd\u5f02\u4eba\uff0c\u53c8\u52a0\u4e0a\u5979 \u6c14\u8840\u65b9\u901a\uff0c\u5fc3\u795e\u7d0a\u4e71\uff0c\u6b66\u529f\u66f4\u4e0d\u53ca\u672c\u6765\u3002 \u3000\u3000\u5979\u773c\u89c1\u6e29\u5982\u7389\u8fd9\u4e00\u638c\u5288\u6765\uff0c\u4e0d\u907f\u4e0d\u95ea\uff0c\u7adf\u60f3\u786c\u63a5\u8fd9\u4e00\u638c\u3002 \u3000\u3000\u4e07\u5999\u771f\u4eba\u5728\u65c1\u8fb9\u770b\u5f97\u795e\u9b42\u4ff1\u5931\uff0c\u5927\u559d\u4e00\u58f0\uff1a\u201c\u6e29\u59d1\u5a18\u4e14\u6162\u3002\u201d \u3000\u3000\u968f\u5373\u8eab\u5f62\u4e00\u52a8\uff0c\u5df2\u8d76\u8fc7\u53bb\uff0c\u60f3\u5c06\u4ed6\u90a3\u59cb\u7ec8\u75f4\u5fc3\u5984\u60f3\u7740\u7684\u7f8e\u4e3d\u5987\u4eba\u6551\u51fa\u82d7\u7586\u5f02\u4eba\u7ea2\u8863\u59d1\u5a18 \u4e11\u4eba\u6e29\u5982\u7389\u7684\u638c\u4e0b\u3002 \u3000\u3000\u4f46\u662f\uff0c\u4ed6\u8fd8\u662f\u8fdf\u4e86\u4e00\u6b65\u3002 \u3000\u3000\u98de\u51e4\u51f0\u624b\u638c\u752b\u51fa\uff0c\u5c31\u88ab\u6e29\u5982\u7389\u90a3\u79cd\u60ca\u4eba\u7684\u638c\u529b\uff0c\u9707\u5f97\u76f4\u98de\u4e86\u51fa\u53bb\uff0c\u7830\u7136\u4e00\u58f0\uff0c\u8fdc\u8fdc\u843d\u5230 \u5730\u4e0a\u3002 \u3000\u3000\u4e07\u5999\u771f\u4eba\u5c39\u51e1\uff0c\u8dfa\u811a\u957f\u53f9\u4e00\u58f0\uff0c\u8170\u8eab\u4e00\u62e7\uff0c\u63a0\u4e86\u8fc7\u53bb\uff0c\u4ed6\u671d\u675c\u4e00\u5a18\u7684\u8eab\u65c1\u8e72\u4e86\u4e0b\u6765\uff0c\u76ee \u5149\u4e00\u626b\uff0c\u5c31\u77e5\u9053\u8fd9\u98de\u51e4\u51f0\u675c\u4e00\u5a18\u867d\u4e0d\u80fd\u548c\u5979\u4e08\u592b\u540c\u751f\uff0c\u7adf\u7136\u4e5f\u548c\u4e08\u592b\u540c\u65f6\u6b7b\u4e86\u3002 \u3000\u3000\u4e07\u5999\u771f\u4eba\u75f4\u5fc3\u5984\u60f3\u4e86\u5341\u591a\u5e74\uff0c\u4e0d\u77e5\u8d39\u4e86\u591a\u5c11\u5fc3\u8840\uff0c\u4e0d\u501f\u4ee5\u597d\u8ba1\u3001\u9634\u8c0b\uff0c\u4ee5\u5404\u79cd\u65b9\u6cd5\u6765\u8c0b \u6c42\uff0c\u4f46\u662f\uff0c\u5230\u5934\u6765\u4ed6\u4ecd\u7136\u662f\u4e00\u573a\u7a7a\u3002 \u3000\u3000\u6b64\u523b\u4ed6\u7f13\u7f13\u7ad9\u4e86\u8d77\u6765\uff0c\u76ee\u5149\u7f13\u7f13\u5730\u8f6c\u5230\u4e86\u90a3\u94c1\u9752\u4e11\u8138\u7684\u6e29\u5982\u7389\u8eab\u4e0a\u3002 \u3000\u3000\u6e29\u5982\u7389\u7684\u76ee\u5149\uff0c\u5374\u4e5f\u6b63\u68ee\u51b7\u5730\u6ce8\u89c6\u7740\u4ed6\uff0c\u4e00\u9762\u7f13\u7f13\u9053\uff1a\u201c\u5c0f\u5b50\uff0c\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u4e24\u4eba\u76ee\u5149\u76f8\u5bf9\uff0c\u4e45\u4e45\u4e0d\u5206\uff0c\u5728\u65c1\u770b\u7740\u7684\u90a3\u7537\u5973\u56db\u4e2a\u5b69\u5b50\uff0c\u5fc3\u91cc\u5374\u5e0c\u671b\u4ed6\u4eec\u7684\u5e08\u7236\u73b0\u5728\u5c31 \u6253\u4e0a\u4e00\u573a\uff0c\u628a\u5bf9\u65b9\u6253\u6b7b\u3002 \u3000\u3000\u8fd9\u4e9b\u5e74\u9f84\u624d\u5341\u4e00\u4e8c\u5c81\u7684\u7ae5\u5b50\uff0c\u89c1\u4e86\u8fd9\u79cd\u573a\u5408\uff0c\u5fc3\u91cc\u7adf\u7136\u6ca1\u6709\u4e00\u70b9\u5bb3\u6015\u7684\u610f\u601d\uff0c\u867d\u7136\u90a3\u4f4d \u7f8e\u4e3d\u7684\u5973\u5b69\u5b50\u5728\u5979\u5e08\u7236\u5c06\u675c\u4e00\u5a18\u5288\u51fa\u53bb\u7684\u65f6\u5019\uff0c\u5979\u90a3\u4e24\u53ea\u5927\u773c\u775b\uff0c\u66fe\u7ecf\u95ed\u8d77\u8fc7\u4e00\u4e0b\u3002 \u3000\u3000\u4f46\u662f\uff0c\u7b49\u5979\u773c\u775b\u7741\u5f00\u7684\u65f6\u5019\uff0c\u4ecd\u662f\u6e05\u6f88\u6676\u83b9\uff0c\u53ea\u662f\u6709\u4e00\u4e1d\u601c\u60dc\u7f62\u4e86\u3002 \u3000\u3000\u6700\u60e8\u7684\u662f\uff0c\u90a3\u88ab\u70b9\u4e2d\u7a74\u9053\u3001\u8eba\u5728\u5730\u4e0a\u7684\u4e2d\u539f\u5927\u4fa0\u7684\u72ec\u5b50\u5353\u957f\u537f\u3002 \u3000\u3000\u8fd9\u53ef\u601c\u7684\u5b69\u5b50\u867d\u7136\u7a74\u9053\u88ab\u70b9\uff0c\u4f46\u77e5\u89c9\u672a\u5931\uff0c\u4ed6\u7236\u6bcd\u6240\u906d\u9047\u7684\u4e00\u5207\uff0c\u4ed6\u5168\u90fd\u770b\u5728\u773c\u91cc\uff0c\u53ea \u662f\u4ed6\u624b\u811a\u4e0d\u80fd\u52a8\u5f39\uff0c\u4e5f\u4e0d\u80fd\u4e3a\u4ed6\u7236\u6bcd\u62fc\u547d\u7f62\u4e86\u3002 \u3000\u3000\u4f46\u662f\uff0c\u5728\u4ed6\u90a3\u5e7c\u5c0f\u7684\u5fc3\u7075\u4e2d\uff0c\u5374\u5df2\u56e0\u8fd9\u79cd\u4ec7\u6068\u800c\u75db\u82e6\u5f97\u6ef4\u8840\u4e86\uff0c\u8fd9\u79cd\u75db\u82e6\u548c\u4ec7\u6068\uff0c\u4fbf\u50cf \u5200\u523b\u4e5f\u4f3c\u7684\u6df1\u94ed\u5728\u4ed6\u5fc3\u91cc\u3002 \u3000\u3000\u76f4\u5230\u8bb8\u591a\u5e74\u540e\uff0c\u8fd9\u79cd\u75db\u82e6\u548c\u4ec7\u6068\uff0c\u4fbf\u53d8\u4e3a\u4e00\u822c\u5de8\u5927\u7684\u62a5\u590d\u529b\u91cf\uff0c\u4f7f\u5f97\u6b66\u6797\u4e2d\u8bb8\u591a\u4eba\uff0c\u56e0 \u7740\u8fd9\u75db\u82e6\u548c\u4ec7\u6068\u800c\u4e27\u5931\u5176\u6027\u547d\u3002 \u3000\u3000\u8fd9\u65f6\u5929\u5df2\u5927\u4eae\uff0c\u4f46\u662f\u65e5\u5149\u672a\u5347\uff0c\u5c71\u98ce\u52b2\u6025\uff0c\u662f\u4e2a\u9634\u9eef\u7684\u5929\u5c39\u51e1\u6076\u6bd2\u5730\u671b\u7740\u6e29\u5982\u7389\uff0c\u4f46\u662f \u5fc3\u5ff5\u6570\u8f6c\u4e4b\u4e0b\uff0c\u4e0d\u7981\u6697\u5fd6\u9053\uff1a\u201c\u6b64\u523b\u4e00\u5a18\u4eba\u4e5f\u6b7b\u4e86\uff0c\u6211\u53c8\u4f55\u82e6\u4e3a\u8fd9\u4e8b\u7ed3\u4e0b\u8fd9\u79cd\u5f3a\u654c\u5462\uff1f\u201d \u3000\u3000\u4e00\u5ff5\u81f3\u6b64\uff0c\u7adf\u5f3a\u7b11\u4e00\u58f0\uff0c\u671b\u7740\u6e29\u5982\u7389\u60f3\u8bf4\u8bdd\uff0c\u54ea\u77e5\u2014\u2014\u7a81\u7136\u54cd\u8d77\u4e00\u9635\u957f\u7b11\uff0c\u7b11\u58f0\u7a7f\u91d1\u88c2 \u77f3\uff0c\u9707\u5f97\u6e29\u5982\u7389\u5934\u4e0a\u7684\u73af\u4f69\u90fd\u4e3a\u4e4b\u53ee\u5f53\u4f5c\u54cd\uff0c\u90a3\u4e09\u4e2a\u7537\u5b69\u7adf\u90fd\u7528\u53cc\u624b\u5c06\u8033\u6735\u5835\u4e86\u8d77\u6765\u3002 \u3000\u3000\u5c39\u51e1\u548c\u6e29\u5982\u7389\u4e00\u8d77\u88ab\u8fd9\u7b11\u58f0\u6240\u60ca\uff0c\u987b\u77e5\u8fd9\u79cd\u7b11\u58f0\u4e00\u7ecf\u4eba\u8033\uff0c\u50cf\u4ed6\u4eec\u8fd9\u79cd\u5185\u529b\uff0c\u4fbf\u7acb\u523b\u77e5 \u9053\u53d1\u51fa\u8fd9\u79cd\u7b11\u58f0\u7684\u4eba\uff0c\u529f\u529b\u4e4b\u6df1\uff0c\u7adf\u7136\u65e0\u4e0e\u4f26\u6bd4\u3002 \u3000\u3000\u4ed6\u4eec\u65b9\u81ea\u5927\u60ca\uff0c\u76ee\u5149\u52a8\u5904\uff0c\u53ea\u89c1\u4e00\u4eba\u968f\u7740\u8fd9\u7b11\u58f0\u500f\u7136\u800c\u6765\uff0c\u4ee5\u4e07\u5999\u771f\u4eba\u548c\u7ea2\u8863\u5a18\u5a18\u8fd9\u79cd \u8eab\u4efd\u6b66\u529f\uff0c\u7adf\u4e0d\u77e5\u6b64\u4eba\u4ece\u4f55\u800c\u6765\u3002 \u3000\u3000\u53ea\u89c1\u6b64\u4eba\u8eab\u4e0a\u7a7f\u7740\u7684\uff0c\u7ade\u662f\u4e00\u88ad\u4e0d\u77e5\u540d\u7684\u7ec6\u8349\u7f16\u6210\u7684\u84d1\u8863\uff0c\u811a\u4e0a\u4e00\u53cc\u591a\u8033\u9ebb\u978b\uff0c\u8eab\u91cf\u5947 \u9ad8\uff0c\u5374\u662f\u9a7c\u80cc\uff0c\u9762\u4e0a\u866c\u987b\u6ee1\u5e03\uff0c\u53cc\u76ee\u4e4b\u4e2d\uff0c\u7cbe\u5149\u66b4\u5c04\uff0c\u72b9\u5982\u5229\u526a\u3002 \u3000\u3000\u800c\u6b64\u4eba\u53f3\u624b\u4e4b\u4e2d\uff0c\u5374\u5012\u63d0\u7740\u4e00\u6761\u602a\u86c7\u7684\u5c38\u8eab\uff0c\u8840\u8ff9\u6dcb\u6c61\uff0c\u6b63\u662f\u65b9\u624d\u90a3\u6761\u548c\u602a\u7269\u661f\u870d\u6076\u6597 \u7684\u602a\u86c7\u3002 \u3000\u3000\u6b64\u4eba\u4e00\u843d\u5730\uff0c\u7b11\u58f0\u72b9\u81ea\u672a\u6b47\uff0c\u800c\u5c39\u51e1\u548c\u6e29\u5982\u7389\u5374\u5df2\u9762\u76ee\u53d8\u8272\u3002 \u3000\u3000\u56e0\u4e3a\u666e\u5929\u4e4b\u4e0b\uff0c\u9664\u4e86\u4e00\u4eba\u4e4b\u5916\uff0c\u518d\u65e0\u522b\u4eba\u6709\u8fd9\u79cd\u88c5\u675f\uff0c\u4e5f\u518d\u65e0\u4e00\u4eba\u6709\u6b64\u6c14\u6982\uff0c\u6e29\u5982\u7389\u76ee \u5149\u4e00\u8f6c\uff0c\u8eab\u5f62\u500f\u7136\u800c\u52a8\uff0c\u5012\u9000\u4e00\u4e08\uff0c\u62c9\u8d77\u90a3\u5973\u7ae5\u7684\u624b\u81c2\uff0c\u4e00\u8a00\u4e0d\u53d1\u5730\u5982\u98de\u9038\u53bb\u3002 \u3000\u3000\u4e07\u5999\u771f\u4eba\u6115\u4e86\u534a\u664c\uff0c\u671d\u8fd9\u7a81\u6765\u7684\u5947\u4eba\u8eac\u8eab\u65bd\u4e86\u4e00\u793c\u5012\u9000\u4e09\u6b65\uff0c\u671d\u90a3\u4e09\u4e2a\u7537\u5b69\u5fae\u4e00\u62db\u624b\u3002 \u3000\u3000\u90a3\u4e09\u4e2a\u7537\u5b69\u7acb\u523b\u8dd1\u5230\u4ed6\u9762\u524d\uff0c\u8fd9\u4e07\u5999\u771f\u4eba\u7adf\u5939\u8d77\u4e09\u4e2a\u7537\u5b69\uff0c\u4e5f\u4e00\u58f0\u4e0d\u54cd\u5730\u671d\u5c71\u5d16\u4e0b\u63a0 \u53bb\uff0c\u4e24\u4e09\u4e2a\u8d77\u843d\uff0c\u4f7f\u65e0\u8e2a\u5f71\u3002 \u3000\u3000\u8fd9\u866c\u987b\u9a7c\u80cc\u8001\u4eba\u50cf\u662f\u4e00\u5c0a\u5de8\u5927\u7684\u5929\u795e\u4e4b\u50cf\u4f3c\u7684\uff0c\u7ad9\u5728\u90a3\u91cc\uff0c\u8eab\u4e0a\u7684\u84d1\u8863\uff0c\u5728\u5c71\u98ce\u4e2d\u98d2\u7136 \u4f5c\u54cd\u3002 \u3000\u3000\u6b64\u523b\u4ed6\u7b11\u58f0\u4e00\u4f4f\uff0c\u76ee\u5149\u653e\u5728\u90a3\u4e24\u4e2a\u4e00\u89c1\u4ed6\u9762\u5c31\u9ed8\u7136\u9038\u53bb\u7684\u9b54\u5934\u80cc\u5f71\u4e0a\u4e00\u8f6c\uff0c\u4e24\u9053\u6d53\u7709\u5fae \u5fae\u4e00\u76b1\uff0c\u7136\u540e\u62c2\u7136\u5fae\u54b1\u4e00\u58f0\uff0c\u76ee\u5149\u626b\u8fc7\u5730\u4e0a\u7684\u90a3\u4e24\u5177\u5c38\u8eab\u4e4b\u4e0a\uff0c\u4e0d\u7981\u5fae\u5531\u7740\u6447\u4e86\u6447\u5934\u3002 \u3000\u3000\u7ec8\u4e8e\uff0c\u4ed6\u770b\u5230\u4e86\u90a3\u53ef\u601c\u800c\u65e0\u52a9\u5730\u8eba\u5728\u5730\u4e0a\u7684\u4e2d\u539f\u5927\u4fa0\u4e4b\u5b50\u5353\u957f\u537f\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u8d70\u4e86\u8fc7\u53bb\uff0c\u5bbd\u5927\u7684\u5de6\u638c\u865a\u7a7a\u5728\u5353\u957f\u537f\u8eab\u4e0a\u6325\u4e86\u4e24\u4e0b\uff0c\u5353\u957f\u537f\u53ea\u89c9\u5f97\u4e00\u822c\u5947\u5f02\u7684\u6696 \u98ce\u62c2\u53bb\uff0c\u5589\u95f4\u4e00\u54b3\uff0c\u4fbf\u5df2\u80fd\u52a8\u8f6c\u4e86\u3002 \u3000\u3000\u4ed6\u722c\u4e86\u8d77\u6765\uff0c\u6ee1\u7736\u7684\u773c\u6cea\uff0c\u4fbf\u50cf\u65ad\u4e86\u7ebf\u4f3c\u7684\u73cd\u73e0\uff0c\u843d\u5230\u4ed6\u7684\u8eab\u4e0a\uff0c\u4ed6\u6709\u751f\u4ee5\u6765\uff0c\u7b2c\u4e00\u6b21 \u771f\u6b63\u5730\u4e86\u89e3\u5230\u60b2\u54c0\u7684\u6ecb\u5473\uff0c\u53ea\u662f\u8fd9\u79cd\u60b2\u54c0\u5bf9\u4e00\u4e2a\u5e74\u65b9\u5341\u4e00\u5c81\u7684\u7ae5\u5b50\u672a\u8bf4\uff0c\u662f\u592a\u8fc7\u6df1\u9083\u548c\u5f3a\u70c8 \u4e9b\u4e86\u3002 \u3000\u3000\u8fd9\u53ef\u601c\u7684\u5b69\u5b50\u90a3\u6ee1\u542b\u6cea\u73e0\u7684\u53cc\u76ee\u5728\u90a3\u866c\u987b\u5947\u4eba\u8eab\u4e0a\u4e00\u8f6c\uff0c\u5f3a\u81ea\u5fcd\u8010\u7740\uff0c\u4e0d\u8ba9\u81ea\u5df1\u653e\u58f0\u54ed \u51fa\u6765\uff0c\u56e0\u4e3a\u4ed6\u77e5\u9053\u4ed6\u81ea\u5df1\u7684\u7236\u4eb2\u662f\u4e2a\u94c1\u8840\u7537\u513f\uff0c\u662f\u4ee5\uff0c\u4ed6\u4e5f\u8981\u5b66\u4ed6\u7236\u4eb2\u7684\u699c\u6837\uff0c\u5728\u8fd9\u964c\u751f\u7684 \u4eba\u524d\u9762\u505a\u4e2a\u5927\u4e08\u592b\u3002 \u3000\u3000\u4ed6\u8e09\u8dc4\u524d\u884c\u4e86\u4e00\u6b65\uff0c\u6251\u5730\u8dea\u5230\u5730\u4e0a\uff0c\u671d\u90a3\u866c\u987b\u7684\u5947\u4eba\u606d\u606d\u656c\u656c\u5730\u53e9\u4e86\u4e00\u4e2a\u5934\uff0c\u54fd\u54bd\u7740 \u9053\uff1a\u201c\u591a\u8c22\u4f2f\u4f2f\u7684\u6551\u547d\u4e4b\u6069\u3002\u201d \u3000\u3000\u5f53\u4e00\u4e2a\u5b69\u5b50\u5fcd\u7740\u6cea\u8bf4\u8bdd\u7684\u65f6\u5019\uff0c\u90a3\u79cd\u60c5\u666f\u662f\u6700\u503c\u5f97\u4eba\u4eec\u601c\u60dc\u7684\uff0c\u8fd9\u9aeb\u9f84\u7684\u7ae5\u5b50\u6b64\u523b\u8bf4\u8bdd \u7684\u6837\u5b50\uff0c\u94c1\u77f3\u4eba\u89c1\u4e86\u90fd\u96be\u514d\u4e3a\u4e4b\u4e0b\u6cea\u3002 \u3000\u3000\u90a3\u866c\u987b\u9a7c\u80cc\u7684\u5a01\u731b\u8001\u4eba\u53cc\u7709\u4e00\u8f69\uff0c\u6b63\u5f85\u8bf4\u8bdd\uff0c\u54ea\u77e5\u8fd9\u7ae5\u5b50\u5728\u53e9\u8c22\u4e86\u6551\u547d\u4e4b\u6069\u4ee5\u540e\uff0c\u7acb\u523b \u722c\u8d77\u6765\uff0c\u6251\u5230\u4ed6\u6bcd\u4eb2\u8eab\u4e0a\uff0c\u54c0\u54c0\u75db\u54ed\u8d77\u6765\u3002 \u3000\u3000\u866c\u987b\u8001\u4eba\u95ea\u7535\u822c\u7684\u76ee\u5149\u4e2d\u9732\u51fa\u4e86\u548c\u853c\u800c\u601c\u501f\u7684\u795e\u8272\uff0c\u4ed6\u671b\u8fd9\u5b69\u5b50\u4e00\u9762\u75db\u54ed\u7740\uff0c\u4e00\u9762\u62b1\u8d77 \u4ed6\u6bcd\u4eb2\u7684\u5c38\u8eab\uff0c\u653e\u5230\u4ed6\u7236\u4eb2\u7684\u5c38\u8eab\u65c1\u3002 \u3000\u3000\u7136\u540e\u8fd9\u5b69\u5b50\u7ad9\u5728\u4ed6\u7236\u6bcd\u7684\u5c38\u8eab\u524d\uff0c\u53ef\u601c\u800c\u65e0\u52a9\u5730\u53c8\u75db\u54ed\u8d77\u6765\u3002 \u3000\u3000\u98ce\u58f0\u5fae\u5f31\u4e86\u4e9b\uff0c\u5927\u5730\u4f3c\u4e4e\u4e5f\u88ab\u8fd9\u79cd\u60b2\u54c0\u7684\u54ed\u58f0\uff0c\u611f\u67d3\u5f97\u6709\u4e9b\u60b2\u54c0\u8d77\u6765\uff0c\u79cb\u98ce\u5377\u8d77\u4e86\u5c71\u5d16 \u65c1\u7684\u4e00\u4e9b\u843d\u53f6\uff0c\u5728\u7a7a\u4e2d\u98d8\u821e\u7740\u3002 \u3000\u3000\u866c\u987b\u8001\u4eba\u76ee\u5149\u4e2d\u548c\u853c\u7684\u795e\u8272\u4e5f\u8d8a\u53d1\u6d53\u539a\uff0c\u4ed6\u671d\u524d\u9762\u968f\u610f\u4e00\u8de8\u6b65\uff0c\u4fbf\u5df2\u5230\u4e86\u5353\u957f\u537f\u8eab\u65c1\uff0c \u7136\u540e\u4ed6\u53c8\u4ef2\u51fa\u5de8\u638c\u6e29\u67d4\u5730\u629a\u4e86\u629a\u8fd9\u5b69\u5b50\u7684\u5934\u3002 \u3000\u3000\u5353\u957f\u537f\u56de\u8fc7\u5934\u6765\uff0c\u5374\u89c1\u8fd9\u9ad8\u5927\u5a01\u731b\u3001\u6709\u5982\u5929\u795e\u822c\u7684\u8001\u4eba\uff0c\u6b63\u671b\u7740\u81ea\u5df1\uff0c\u5e76\u4e14\u7528\u4e00\u79cd\u8fd1\u4e4e \u6148\u7236\u822c\u7684\u4eb2\u5207\u8bed\u8c03\u8bf4\uff1a\u201c\u5b69\u5b50\uff0c\u4e0d\u8981\u54ed\u4e86\uff0c\u4eba\u6b7b\u4e0d\u80fd\u590d\u751f\uff0c\u4f60\u54ed\u4e5f\u6ca1\u6709\u4ec0\u4e48\u7528\uff0c\u4f60\u8981\u77e5\u9053\u4f60 \u7236\u4eb2\u867d\u7136\u6b7b\u4e86\uff0c\u4f46\u662f\u4ed6\u4e0a\u4e0d\u6127\u5bf9\u5929\u5730\uff0c\u4e0b\u4e0d\u6127\u5bf9\u82cd\u751f\uff0c\u867d\u7136\u6b7b\u4e86\uff0c\u5374\u6bd4\u90a3\u4e9b\u6d3b\u7740\u7684\u4eba\u66f4\u4f1f \u5927\uff0c\u66f4\u503c\u5f97\u4f60\u656c\u4f69\uff0c\u4f60\u4e5f\u8be5\u5b66\u5b66\u4f60\u7236\u4eb2\u7684\u699c\u6837\uff0c\u5728\u4e16\u4e0a\u505a\u4e2a\u6b63\u6b63\u5f53\u5f53\u7684\u5927\u4e08\u592b\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u70b9\u4e86\u70b9\u5934\uff0c\u4f46\u773c\u6cea\u4ecd\u5fcd\u4e0d\u4f4f\u5f80\u4e0b\u843d\uff0c\u51c4\u695a\u7684\u6837\u5b50\uff0c\u4f7f\u5f97\u8fd9\u8001\u4eba\u4e5f\u4e0d\u7981\u4e3a\u4e4b\u957f\u53f9\u4e00 \u58f0\uff0c\u50cf\u662f\u81ea\u8bed\u822c\u54fa\u54fa\u4ed6\u8bf4\u9053\uff1a\u201c\u5929\u547d\uff0c\u5929\u547d\uff0c\u6211\u8981\u662f\u4e0d\u5148\u8bbe\u6cd5\u5835\u4f4f\u90a3\u6d1e\u7a74\uff0c\u8fd9\u4e8b\u4e5f\u5c31\u4e0d\u4f1a\u53d1 \u751f\uff0c\u5509\uff01\u6211\u4e09\u5341\u5e74\u6765\uff0c\u672a\u518d\u4f24\u751f\uff0c\u4eca\u65e5\u5374\u9669\u4e9b\u5fcd\u4e0d\u4f4f\u8981\u52a8\u6740\u6212\u2026\u2026\u201d \u3000\u3000\u4ed6\u8bf4\u8bdd\u7684\u58f0\u97f3\uff0c\u9010\u6e10\u5fae\u5f31\uff0c\u7136\u540e\u4ed6\u731b\u4e00\u5b9a\u775b\uff0c\u671b\u7740\u8fd9\u5b69\u5b50\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u522b\u54ed\u4e86\uff0c \u633a\u8d77\u80f8\u819b\uff0c\u505a\u4e2a\u7537\u5b50\u6c49\uff0c\u8001\u592b\u5148\u548c\u4f60\u5c06\u4f60\u7236\u6bcd\u7684\u5c38\u8eab\u5b89\u846c\u8d77\u6765\uff0c\u7136\u540e\u2014\u2014\u201d\u8fd9\u866c\u987b\u8001\u4eba\u4f3c\u4e4e \u8fdf\u7591\u4e86\u4e00\u4e0b\uff0c\u7136\u540e\u4e00\u62ac\u5934\uff0c\u65ad\u7136\u8bf4\u9053\uff0c\u201c\u53ea\u8981\u4f60\u6709\u51b3\u5fc3\u3001\u6bc5\u529b\uff0c\u4f60\u5c31\u8ddf\u7740\u6211\u56de\u53bb\uff0c\u6211\u4f1a\u8ba9\u4f60 \u5b66\u6210\u4e00\u8eab\u672c\u9886\uff0c\u5c06\u6765\uff0c\u4f60\u5c31\u53ef\u4ee5\u66ff\u4f60\u7684\u7236\u6bcd\u62a5\u4ec7\uff0c\u4e5f\u53ef\u4ee5\u505a\u4e00\u756a\u8f70\u8f70\u70c8\u70c8\u7684\u4e8b\u4e1a\u3002\u201d \u3000\u3000\u8fd9\u866c\u987b\u8001\u4eba\u8bdd\u672a\u8bf4\u5b8c\uff0c\u5353\u957f\u537f\u5c31\u53c8\u6251\u5230\u8dea\u5230\u5730\u4e0a\u3002 \u3000\u3000\u8fd9\u5b69\u5b50\u5929\u8d44\u7edd\u9876\uff0c\u4f55\u5c1d\u4e0d\u77e5\u9053\u8fd9\u8001\u4eba\u662f\u4e2a\u7edd\u4e16\u7684\u5947\u4eba\uff0c\u53c8\u4f55\u5c1d\u4e0d\u613f\u610f\u62dc\u5728\u8fd9\u7edd\u4e16\u5947\u4eba\u7684 \u95e8\u4e0b\uff0c\u5b66\u4e00\u8eab\u60ca\u5929\u52a8\u5730\u7684\u672c\u4e8b\uff0c\u4e3a\u7236\u6bcd\u5bfb\u4ec7\u3002 \u3000\u3000\u4f46\u662f\uff0c\u4ed6\u8bb0\u5f97\u4ed6\u7236\u4eb2\u66fe\u7ecf\u5bf9\u4ed6\u8bf4\u8fc7\uff1a\u4e00\u4e2a\u7537\u5b50\u6c49\u4e0d\u5e94\u8be5\u5411\u4efb\u4f55\u4e00\u4e2a\u4eba\u8981\u6c42\u4ec0\u4e48\uff0c\u9664\u975e\u4f60 \u6709\u8db3\u591f\u7684\u529b\u91cf\u53bb\u62a5\u7b54\u4eba\u5bb6\u3002 \u3000\u3000\u56e0\u6b64\uff0c\u7eb5\u7136\u4ed6\u5fc3\u91cc\u518d\u6e34\u671b\uff0c\u53e3\u4e2d\u5374\u7edd\u5bf9\u4e0d\u6d41\u9732\u51fa\u6765\uff0c\u8fd9\u5b69\u5b50\u5e74\u7eaa\u867d\u8f7b\uff0c\u5374\u5df2\u6709\u4e86\u4ed6\u7236\u4eb2 \u90a3\u79cd\u521a\u76f4\u3001\u803f\u4ecb\u800c\u5014\u5f3a\u7684\u6027\u683c\u3002 \u3000\u3000\u7136\u800c\u6b64\u523b\u8fd9\u866b\u3001\u987b\u9a7c\u80cc\u7684\u5947\u4eba\u81ea\u5df1\u8bf4\u4e86\u51fa\u6765\uff0c\u8fd9\u5b69\u5b50\u518d\u4e5f\u5fcd\u4e0d\u4f4f\u4e86\uff0c\u8dea\u5728\u5730\u4e0a\uff0c\u8fde\u8fde\u53e9 \u9996\u9053\uff1a\u201c\u4f2f\u4f2f\uff0c\u6211\u65e0\u8bba\u5403\u4ec0\u4e48\u82e6\uff0c\u4e5f\u8981\u5b66\u6210\u672c\u4e8b\uff0c\u5c06\u90a3\u4e9b\u6076\u4eba\u6740\u6b7b\uff0c\u62a5\u6b64\u6df1\u4ec7\uff0c\u4f2f\u4f2f\uff0c\u65e0\u8bba \u4ec0\u4e48\u5730\u65b9\uff0c\u6211\u90fd\u613f\u610f\u8ddf\u7740\u4f60\u53bb\u3002\u201d \u3000\u3000\u866c\u987b\u8001\u4eba\u70b9\u4e86\u70b9\u5934\uff0c\u671b\u7740\u8fd9\u5014\u5f3a\u3001\u5b5d\u987a\u3001\u800c\u53c8\u806a\u9896\u7684\u5b69\u5b50\uff0c\u53ea\u89c1\u4ed6\u6cea\u75d5\u867d\u4ecd\u672a\u5e72\uff0c\u4f46\u5c0f \u8138\u4e0a\u5df2\u6ee1\u8138\u9732\u51fa\u575a\u5f3a\u7684\u795e\u8272\u3002\u4e8e\u662f\u4ed6\u62c9\u8d77\u8fd9\u5b69\u5b50\uff0c\u4ed6\u77e5\u9053\u5341\u5e74\u4e4b\u540e\uff0c\u6b66\u6797\u4e2d\u53c8\u5c06\u51fa\u73b0\u4e00\u4e2a\u6069 \u6028\u5206\u660e\u3001\u4e49\u8282\u5f70\u7136\u7684\u4fa0\u58eb\uff0c\u4e8e\u662f\u4ed6\u5200\u3002\u4e25\u5cfb\u7684\u8138\u4e0a\uff0c\u53c8\u5fae\u7b11\u4e86\u4e00\u4e0b\u3002 \u3000\u3000\u8fd9\u5fae\u7b11\u5728\u4ed6\u8138\u4e0a\u9010\u6e10\u6269\u6563\uff0c\u7ec8\u4e8e\uff0c\u4ed6\u5927\u7b11\u4e86\u8d77\u6765\uff0c\u9053\uff1a\u201c\u597d\uff0c\u597d\uff0c\u60f3\u4e0d\u5230\u6211\u53f8\u7a7a\u5c27\u65e5\u5df2 \u8fd1\u6b8b\u5e74\uff0c\u5374\u53c8\u6536\u4e86\u4e2a\u597d\u5f92\u5f1f\uff01\u201d \u3000\u3000\u7b11\u58f0\u9ad8\u6602\uff0c\u5728\u8fd9\u65e0\u4eba\u7684\u5c71\u8c37\u91cc\u98de\u626c\u7740\u3002 \u3000\u3000\u9634\u973e\u6e10\u9038\uff0c\u4e1c\u65b9\u6709\u91d1\u5149\u5c04\u51fa\uff0c\u7167\u7740\u8fd9\u4e00\u8001\u4e00\u5c11\u4e24\u4e2a\u8eab\u8eaf\uff0c\u4f7f\u4eba\u4eec\u770b\u8d77\u6765\uff0c\u751f\u50cf\u662f\u4e24\u5c0a\u95ea \u8000\u7740\u91d1\u5149\u7684\u795e\u50cf\u3002 \u7b2c\u4e8c\u7ae0\u3000\u829c\u6e56\u5927\u8c6a>> \u53e4\u9f99\u300a\u6708\u5f02\u661f\u90aa\u300b \u7b2c\u4e8c\u7ae0\u3000\u829c\u6e56\u5927\u8c6a \u3000\u3000\u6c5f\u5357\u5de8\u57e0\uff0c\u829c\u6e56\u57ce\u5317\uff0c\u4e00\u6761\u5df7\u53e3\u671d\u5357\u7684\u6a2a\u5df7\u4e2d\uff0c\u5374\u6709\u4e00\u5ea7\u5de8\u5b85\u3002 \u3000\u3000\u8fd9\u5ea7\u5de8\u5b85\u51e0\u4e4e\u5360\u4e86\u8fd9\u6761\u957f\u7ea6\u6570\u5341\u4e08\u7684\u6a2a\u5df7\u4e00\u5927\u534a\u7684\u5730\u65b9\uff0c\u9ed1\u6f06\u7684\u5927\u95e8\u4e4c\u9ed1\u53d1\u4eae\uff0c\u56e0\u4e3a\u521a \u8fc7\u5b8c\u5e74\uff0c\u6b64\u523b\u95e8\u4e0a\u8fd8\u8d34\u7740\u5927\u7ea2\u7684\u6625\u8054\u3002 \u3000\u3000\u5927\u95e8\u65c1\u8e72\u8e1e\u7740\u4e24\u5ea7\u9ad8\u7adf\u8fbe\u4e08\u7684\u77f3\u72ee\u5b50\uff0c\u8fd9\u79cd\u77f3\u72ee\u5b50\u5728\u4eac\u57ce\u8fbe\u5b98\u8d35\u4eba\u7684\u5e9c\u90aa\u95e8\u53e3\uff0c\u8fd8\u5012\u5e38 \u89c1\uff0c\u53ea\u662f\u5728\u8fd9\u79cd\u6c5f\u5357\u4f4f\u5bb6\u7684\u623f\u524d\uff0c\u5c31\u663e\u5f97\u6709\u4e9b\u7279\u8272\uff0c\u660e\u773c\u4eba\u4e00\u671b\u5c31\u77e5\uff0c\u8fd9\u6837\u5de8\u5b85\u91cc\u4f4f\u7684\u4e0d\u662f \u5bfb\u5e38\u4eba\u7269\u3002 \u3000\u3000\u8fd9\u5929\u9ec4\u660f\uff0c\u521d\u6625\u7684\u659c\u9633\u5c06\u95e8\u53e3\u90a3\u4e24\u5ea7\u77f3\u72ee\u5b50\u7684\u5f71\u5b50\uff0c\u957f\u957f\u5730\u62d6\u5230\u4e1c\u8fb9\u53bb\uff0c\u8fd9\u5ea7\u5de8\u5b85\u95e8 \u53e3\uff0c\u6b64\u523b\u7adf\u662f\u8f66\u6c34\u9a6c\u9f99\uff0c\u70ed\u95f9\u5df2\u6781\u3002 \u3000\u3000\u90a3\u4e24\u6247\u9ed1\u5927\u95e8\uff0c\u6b64\u65f6\u4e5f\u662f\u5411\u5916\u5927\u655e\u7740\uff0c\u95e8\u53e3\u5ddd\u6d41\u4e0d\u606f\u7684\u8fdb\u51fa\u7740\u4eba\uff0c\u867d\u7136\u6709\u4e9b\u662f\u666e\u901a\u5546 \u8d3e\uff0c\u4f46\u5927\u591a\u6570\u5374\u662f\u7ec6\u8170\u5bbd\u80a9\u7684\u9556\u608d\u4eba\u7269\uff0c\u4e00\u671b\u800c\u77e5\uff0c\u8fd9\u4e9b\u4eba\u5168\u90fd\u662f\u6b66\u6797\u7684\u8c6a\u58eb\u3002 \u3000\u3000\u539f\u6765\u8fd9\u5ea7\u5de8\u5b85\u91cc\u4f4f\u7740\u7684\uff0c\u5c31\u662f\u6c5f\u5357\u540d\u6b66\u5e08\uff0c\u829c\u6e56\u5927\u8c6a\uff0c\u591a\u81c2\u795e\u5251\u4e91\u8c26\u3002 \u3000\u3000\u4eca\u5929\uff0c\u5c31\u662f\u8fd9\u4e91\u8001\u6b66\u5e08\u7684\u4e03\u65ec\u5927\u5bff\uff0c\u4e0d\u4f46\u829c\u6e56\u5883\u91cc\u6709\u5934\u6709\u9762\u7684\u4eba\u7269\uff0c\u5168\u90fd\u5230\u9f50\uff0c\u5929\u4e0b\u5404 \u5730\u7684\u6b66\u6797\u8c6a\u58eb\uff0c\u4e5f\u90e8\u6709\u8d76\u7740\u6765\u66ff\u4e91\u8001\u6b66\u5e08\u795d\u5bff\u7684\u3002 \u3000\u3000\u591a\u81c2\u795e\u5251\u4e0d\u4f46\u58f0\u540d\u663e\u8d6b\uff0c\u4ed6\u7684\u957f\u5b50\u4e91\u4e2d\u7a0b\u66f4\u662f\u6b64\u523b\u6b66\u6797\u4e2d\u7099\u624b\u53ef\u70ed\u7684\u4eba\u7269\uff0c\u7edf\u9886\u7740\u6c5f\u5357 \u5341\u516b\u5730\u7684\u4e8c\u5341\u516d\u5bb6\u9556\u5c40\uff0c\u5df2\u9690\u7136\u4e3a\u6c5f\u5357\u4fa0\u4e49\u9053\u7684\u9886\u8896\u4eba\u7269\uff0c\u56e0\u6b64\u8fd9\u4e91\u8001\u7237\u5b50\u7684\u4e03\u65ec\u6574\u5bff\uff0c\u70ed \u95f9\u53ef\u60f3\u800c\u77e5\u3002 \u3000\u3000\u4ece\u8fd9\u6761\u6a2a\u5df7\u7684\u5df7\u53e3\u5f00\u59cb\uff0c\u5c31\u7ad9\u6ee1\u4e86\u63a5\u5f85\u5ba2\u4eba\u7684\u5f6a\u5f62\u5927\u6c49\uff0c\u8fd9\u4e9b\u4eba\u867d\u7136\u90fd\u7a7f\u7740\u957f\u886b\uff0c\u53ef\u662f \u4e00\u4e2a\u4e2a\u76ee\u5149\u51dd\u7ec3\uff0c\u795e\u8272\u6c14\u8db3\uff0c\u663e\u89c1\u5f97\u90fd\u662f\u624b\u5e95\u4e0b\u6709\u4e24\u4e0b\u5b50\u7684\u7ec3\u5bb6\u5b50\uff0c\u539f\u6765\u8fd9\u4e9b\u4eba\uff0c\u7adf\u90fd\u662f\u6c5f \u5357\u5404\u9556\u5c40\u7684\u9556\u5e08\u3002 \u3000\u3000\u8fd9\u4e91\u5b85\u7684\u9662\u5b50\u5171\u5206\u4e94\u8fdb\uff0c\u5bff\u5802\u5c31\u8bbe\u5728\u7b2c\u4e00\u8fdb\u7684\u5927\u5385\u4e0a\uff0c\u8fd9\u79cd\u6b66\u6797\u5927\u8c6a\u5bb6\u4e2d\u7684\u623f\u5b50\u5f0f\u6837\u5c31 \u76d6\u5f97\u7279\u522b\u53e4\u602a\uff0c\u4e91\u5b85\u7684\u8fd9\u95f4\u524d\u5385\uff0c\u524d\u540e\u5de6\u53f3\u7adf\u8fbe\u4e8c\u4e09\u5341\u4e08\uff0c\u5bcc\u5bcc\u88d5\u88d5\u7684\u53ef\u4ee5\u653e\u4e0b\u51e0\u5341\u5f20\u5706\u684c \u9762\u3002 \u3000\u3000\u539f\u6765\u591a\u81c2\u795e\u5251\u5929\u6027\u597d\u5ba2\uff0c\u5c24\u5176\u559c\u6b22\u6210\u4eba\u4e4b\u7f8e\uff0c\u4e91\u8001\u7237\u5b50\u65e0\u8bba\u5728\u6b66\u6797\u4e2d\u9ed1\u767d\u4e24\u9053\uff0c\u4eba\u7f18\u90fd \u662f\u6781\u597d\uff0c\u7aef\u7684\u662f\u798f\u5bff\u53cc\u5168\u7684\u8001\u82f1\u96c4\u3002 \u3000\u3000\u6b64\u523b\u8fd9\u5927\u5385\u91cc\u4eae\u5982\u767d\u663c\uff0c\u5f53\u4e2d\u70e7\u7740\u4e24\u652f\u5de8\u5927\u7684\u7ea2\u70db\uff0c\u4e00\u4e2a\u5bff\u6843\uff0c\u505a\u7684\u7ade\u6709\u4e00\u5f20\u516b\u4ed9\u684c\u5b50 \u90a3\u6837\u5927\uff0c\u5374\u662f\u5168\u7528\u7cef\u7c73\u505a\u7684\u3002 \u3000\u3000\u5750\u5728\u8fd9\u5f20\u4f9b\u5bff\u6843\u7684\u684c\u5b50\u65c1\u7684\u4e00\u5f20\u5927\u5e08\u6905\u4e0a\u767d\u53d1\u8001\u8005\uff0c\u81ea\u7136\u5c31\u662f\u90a3\u540d\u6ee1\u6b66\u6797\u7684\u591a\u81c2\u795e\u5251\u4e91 \u8c26\u4e86\uff0c\u8fd9\u4e03\u65ec\u8001\u4eba\u867d\u7136\u987b\u53d1\u7686\u767d\uff0c\u53ef\u662f\u6837\u5b50\u5374\u6ca1\u6709\u534a\u70b9\u8001\u6001\uff0c\u7aef\u5750\u5728\u6905\u4e0a\uff0c\u54c8\u54c8\u5730\u7b11\u7740\uff0c\u5e94 \u916c\u7740\u6765\u62dc\u5bff\u7684\u6b66\u6797\u540e\u8f88\uff0c\u4e0d\u4f46\u8bdd\u58f0\u6709\u5982\u6d2a\u949f\uff0c\u7b11\u58f0\u4e5f\u6e05\u6f88\u5df2\u6781\u3002 \u3000\u3000\u4ed6\u7684\u957f\u5b50\u4ec1\u4e49\u5251\u5ba2\u4e91\u4e2d\u7a0b\u606d\u8c28\u5730\u7ad9\u5728\u8eab\u65c1\uff0c\u7a7f\u7740\u7d2b\u8272\u7684\u7f0e\u5b50\u957f\u886b\uff0c\u9894\u4e0b\u7559\u7740\u5fae\u987b\uff0c\u82e5\u4e0d \u662f\u4e8b\u5148\u8bf4\u660e\uff0c\u8c01\u4e5f\u770b\u4e0d\u51fa\u8fd9\u65af\u65af\u6587\u6587\u3001\u50cf\u4e2a\u5728\u5b66\u7684\u79c0\u624d\u4f3c\u7684\u4e2d\u5e74\u4eba\uff0c\u7adf\u4f1a\u662f\u8dfa\u8dfa\u811a\u6c5f\u5357\u4e71\u98a4 \u7684\u6b66\u6797\u5065\u8005\u3002 \u3000\u3000\u6765\u62dc\u5bff\u7684\u4eba\uff0c\u6709\u4e91\u8001\u7237\u5b50\u8ba4\u8bc6\u7684\uff0c\u53ef\u4e5f\u6709\u4e91\u8001\u7237\u5b50\u4e0d\u8ba4\u8bc6\u7684\uff0c\u65e0\u8bba\u8ba4\u4e0d\u8ba4\u8bc6\uff0c\u4e91\u8001\u7237\u5b50 \u5168\u90e8\u5ba2\u5ba2\u6c14\u6c14\u5730\u62db\u547c\u7740\uff0c\u6709\u7684\u8981\u884c\u5927\u793c\u7684\uff0c\u4ed6\u8001\u4eba\u5bb6\u5c31\u5c3d\u91cf\u62e6\u7740\uff0c\u53ef\u662f\u9664\u4e86\u548c\u4ed6\u8001\u4eba\u5bb6\u540c\u8f88 \u7684\u6709\u6570\u51e0\u4e2a\u8001\u82f1\u96c4\uff0c\u5929\u4e0b\u5404\u5730\u7684\u6b66\u6797\u8c6a\u58eb\uff0c\u5728\u8fd9\u4f4d\u8001\u82f1\u96c4\u9762\u524d\uff0c\u90fd\u662f\u606d\u606d\u656c\u656c\u5730\u53e9\u4e0b\u5934\u53bb\uff0c \u4e0d\u6562\u6709\u534a\u70b9\u9a6c\u864e\u3002 \u3000\u3000\u5bff\u5802\u4e0a\u7684\u7fa4\u8c6a\u867d\u5df2\u6d4e\u6d4e\u4e00\u5802\uff0c\u4f46\u540e\u9762\u8fdb\u6765\u7684\u4eba\u4ecd\u7136\u5ddd\u6d41\u4e0d\u606f\uff0c\u53ef\u662f\u5c31\u5728\u9152\u7b75\u5c06\u5f00\u7684\u65f6 \u5019\uff0c\u95e8\u5916\u8d70\u8fdb\u4e00\u4e2a\u6ee1\u8eab\u9ec4\u886b\u7684\u9880\u957f\u5c11\u5e74\uff0c\u8d70\u5230\u8fd9\u8001\u5bff\u661f\u9762\u524d\uff0c\u5374\u4ec5\u4ec5\u8f7b\u8f7b\u4e00\u63d6\uff0c\u8fde\u53e9\u4e0b\u53bb\u7684 \u610f\u601d\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u4e91\u8001\u82f1\u96c4\u5929\u6027\u51b2\u548c\uff0c\u4e00\u70b9\u513f\u90fd\u6ca1\u6709\u653e\u5728\u5fc3\u4e0a\uff0c\u53ef\u662f\u7ad9\u5728\u4ed6\u540e\u9762\u7684\u4ec1\u4e49\u5251\u5ba2\u4e91\u4e2d\u7a0b\u5fc3\u91cc\u5374 \u6709\u4e9b\u4e0d\u6ee1\u610f\u4e86\uff0c\u4e0d\u7981\u95ea\u76ee\u4e00\u6253\u91cf\u8fd9\u9ec4\u886b\u5c11\u5e74\u3002 \u3000\u3000\u53ea\u89c1\u8fd9\u5c11\u5e74\u957f\u8eab\u7389\u7acb\uff0c\u733f\u80cc\u8702\u8170\uff0c\u80cc\u810a\u633a\u5f97\u7b14\u76f4\uff0c\u4e24\u76ee\u795e\u5149\u5145\u8db3\uff0c\u4f46\u5374\u6beb\u4e0d\u5916\u6cc4\uff0c\u53ea\u662f \u5634\u89d2\u773c\u89d2\u7a0d\u5e26\u7740\u51e0\u5206\u8bf4\u4e0d\u51fa\u6765\u7684\u50b2\u6c14\u3002 \u3000\u3000\u4e91\u4e2d\u7a0b\u5fc3\u4e2d\u4e00\u52a8\uff0c\u6697\u6697\u5fd6\u9053\uff1a\u201c\u8fd9\u5c11\u5e74\u5185\u529f\u5df2\u9887\u6709\u706b\u5019\uff0c\u867d\u8fd8\u770b\u4e0d\u51fa\u6df1\u6d45\u6765\uff0c\u4f46\u529f\u529b\u9887 \u9ad8\uff0c\u5374\u65e0\u7591\u95ee\uff0c\u53ea\u662f\u8fd9\u5c11\u5e74\u9762\u5b54\u5f88\u751f\uff0c\u5b64\u8eab\u800c\u6765\uff0c\u65e2\u65e0\u540d\u5e16\uff0c\u4e5f\u6ca1\u6709\u62a5\u51fa\u5e08\u957f\u7684\u540d\u53f7\uff0c\u795e\u8272 \u504f\u53c8\u8fd9\u4e48\u50b2\u6162\uff0c\u5374\u53c8\u662f\u8c01\u5462\uff1f\u201d \u3000\u3000\u4ec1\u4e49\u5251\u5ba2\u5fc3\u4e2d\u601d\u7591\uff0c\u4f46\u5634\u91cc\u81ea\u7136\u4e0d\u4f1a\u8bf4\u51fa\u6765\uff0c\u518d\u52a0\u4e0a\u8d3a\u5ba2\u76c8\u95e8\uff0c\u4e8b\u60c5\u53c8\u591a\uff0c\u8fc7\u4e86\u534a\u664c\uff0c \u8fd9\u7d20\u79f0\u8c28\u614e\u7684\u4e91\u4e2d\u7a0b\u5c31\u5c06\u6b64\u4e8b\u5fd8\u4e86\u3002 \u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\uff0c\u8fd9\u5927\u5385\u4e0a\u9152\u7b75\u5927\u5f20\uff0c\u7adf\u6446\u51fa\u4e09\u5341\u516d\u684c\u9152\u5e2d\uff0c\u5728\u5ea7\u7684\u8fd9\u4e09\u767e\u591a\u4f4d\u6b66\u6797\u8c6a\u58eb\uff0c\u5341 \u5206\u4e4b\u4e5d\u5728\u6b66\u6797\u90fd\u6709\u4e2a\u4e0d\u5c0f\u7684\u4e07\u513f\u3002 \u3000\u3000\u548c\u4e91\u8001\u82f1\u96c4\u540c\u5750\u5728\u5f53\u4e2d\u90a3\u5f20\u684c\u5b50\u4e0a\u7684\uff0c\u66f4\u90fd\u662f\u5f53\u4eca\u6b66\u6797\u4e2d\u7684\u4e00\u6d41\u4eba\u7269\uff0c\u4e00\u4e2a\u4e2a\u987b\u53d1\u4ff1\u5df2 \u82cd\u81ea\uff0c\u5168\u90e8\u5df2\u8fc7\u4e86\u77e5\u547d\u4e4b\u5e74\u3002 \u3000\u3000\u8fd9\u4e9b\u90fd\u662f\u6614\u5e74\u548c\u591a\u81c2\u795e\u5251\u628a\u81c2\u521b\u4e1a\u7684\u670b\u53cb\uff0c\u5982\u4eca\u5df2\u540d\u6210\u4e1a\u5c31\uff0c\u91d1\u76c6\u6d17\u624b\uff0c\u5728\u5bb6\u4e2d\u4e50\u4eab\u4f59 \u5e74\u4e86\uff0c\u6240\u4ee5\u8bf4\uff0c\u8fd9\u5f20\u684c\u5b50\u5750\u7740\u7684\u4e03\u4e2a\u4eba\uff0c\u5168\u90fd\u662f\u798f\u5bff\u53cc\u5168\u7684\u4eba\u7269\uff0c\u53ea\u9664\u4e86\u4e00\u4e2a\u9e70\u9f3b\u9e5e\u76ee\u7684\u8001 \u8005\u4e4b\u5916\u3002 \u3000\u3000\u8bf4\u8fd9\u4eba\u662f\u8001\u8005\uff0c\u4e5f\u8bb8\u8fd8\u592a\u65e9\u4e86\u4e9b\uff0c\u56e0\u4e3a\u8fd9\u4eba\u65b9\u53ea\u56db\u5341\u5de6\u53f3\uff0c\u6b64\u523b\u4ed6\u7ade\u5750\u5728\u5bff\u8005\u4e91\u8c26\u548c\u957f \u6c5f\u6c34\u8def\u4e0a\u7684\u5de8\u5b50\u6a2a\u6c5f\u91d1\u7d22\u695a\u5360\u9f99\u4e2d\u95f4\uff0c\u53ef\u89c1\u8fd9\u4eba\u5e74\u7eaa\u867d\u4e0d\u5927\uff0c\u4f46\u6b66\u6797\u4e2d\u7684\u8eab\u4efd\u5374\u5f88\u9ad8\u3002 \u3000\u3000\u6ee1\u5385\u8c6a\u58eb\uff0c\u5341\u4e2d\u6709\u4e5d\u90fd\u77e5\u9053\u8fd9\u4eba\uff0c\u4e0d\u77e5\u9053\u7684\u542c\u522b\u4eba\u4e00\u8bf4\uff0c\u4e5f\u90fd\u5578\u7136\u52a8\u5bb9\uff0c\u539f\u6765\u6b64\u4eba\u7adf\u662f \u6c5f\u5357\u9ed1\u7c73\u5e2e\u7684\u603b\u8235\u4e3b\uff0c\u65e0\u7fc5\u795e\u9e70\u7ba1\u4e00\u67f4\u3002 \u3000\u3000\u8fd9\u7ba1\u4e00\u67f4\u4eca\u65e5\u7adf\u7136\u6765\u5411\u4e91\u8c26\u62dc\u5bff\uff0c\u7fa4\u8c6a\u53ef\u90fd\u6709\u70b9\u5947\u602a\uff0c\u6709\u4e9b\u4eba\u5c31\u5728\u7a83\u7a83\u8bae\u8bba\u201c\u7ba1\u795e\u9e70\u600e \u4e48\u4e5f\u6765\u4e86\uff0c\u8fd9\u4e3b\u513f\u5e73\u65e5\u773c\u9ad8\u4e8e\u9876\uff0c\u5929\u4e0b\u4eba\u90fd\u6ca1\u6709\u653e\u5728\u4ed6\u773c\u91cc\uff0c\u6211\u770b\u4ed6\u53ef\u662f\u9ec4\u9f20\u72fc\u7ed9\u9e21\u62dc\u5e74\uff0c \u4eca\u5929\u6015\u53c8\u522b\u6709\u6240\u56fe\u5427\uff01\u201d \u3000\u3000\u6709\u7684\u4eba\u5c31\u8fa9\u9053\uff1a\u201c\u7ba1\u795e\u9e70\u867d\u7136\u53c8\u72c2\u53c8\u505a\uff0c\u53ef\u662f\u4e91\u8001\u7237\u5b50\u662f\u4ec0\u4e48\u4eba\u7269\uff0c\u8fd9\u5f53\u7136\u53e6\u5f53\u522b\u8bba\uff0c \u6211\u770b\u4f60\u8fd8\u662f\u5c11\u8bf4\u4e24\u53e5\uff0c\u591a\u7167\u987e\u7167\u987e\u9e21\u817f\u5427\uff01\u201d \u3000\u3000\u8fd8\u6709\u7684\u4eba\u5c31\u56e0\u6b64\u800c\u53d1\u51fa\u611f\u6168\uff1a\u201c\u6b66\u6797\u91cc\u592a\u5e73\u65e5\u5b50\u6050\u6015\u90fd\u8fc7\u4e0d\u957f\u4e86\uff0c\u60a8\u770b\u770b\uff0c\u5149\u662f\u8fd9\u4e09\u5e74 \u91cc\uff0c\u6c5f\u5357\u6c5f\u5317\uff0c\u5927\u6cb3\u4e24\u5cb8\u65b0\u521b\u7acb\u4e86\u591a\u5c11\u7684\u5b97\u6d3e\u3001\u5e2e\u4f1a\uff0c\u53c8\u5168\u90fd\u662f\u5e26\u7740\u4e09\u5206\u90aa\u6c14\u7684\uff0c\u60a8\u770b\u770b \u5427\uff0c\u6b66\u6797\u4e4b\u4e2d\uff0c\u5c31\u8981\u5927\u4e71\u4e86\u3002\u201d \u3000\u3000\u4ed6\u7684\u670b\u53cb\u5c31\u8d76\u7d27\u62c9\u4ed6\u7684\u8896\u5b50\uff0c\u963b\u6b62\u7740\uff1a\u201c\u670b\u53cb\uff0c\u4f60\u5c11\u8bf4\u51e0\u53e5\u5427\uff0c\u4f60\u80fd\u62c5\u4fdd\u8fd9\u9644\u8fd1\u7684\u684c\u5b50 \u4e0a\u5c31\u6ca1\u6709\u8fd9\u4e9b\u89d2\u8272\uff0c\u4f60\u8fd9\u8bdd\u8981\u662f\u88ab\u4eba\u5bb6\u542c\u4e86\u4e22\uff0c\u90a3\u53ef\u5c31\u5403\u4e0d\u4e86\uff0c\u515c\u7740\u8dd1\u5566\u3002\u201d \u3000\u3000\u8fd9\u4e9b\u8349\u83bd\u8c6a\u58eb\u79c1\u5e95\u4e0b\u8bae\u8bba\u7eb7\u7eb7\uff0c\u5750\u5728\u5f53\u4e2d\u7684\u8001\u5bff\u661f\u591a\u81c2\u795e\u5251\u4e91\u8c26\u81ea\u7136\u4e0d\u4f1a\u542c\u89c1\uff0c\u8fd9\u9ad8 \u5927\u3001\u77cd\u70c1\u7684\u8001\u4eba\u7aef\u8d77\u9152\u676f\uff0c\u7ad9\u8d77\u6765\uff0c\u671d\u56db\u5ea7\u7fa4\u8c6a\u4f5c\u4e86\u4e00\u4e2a\u7f57\u5708\u63d6\uff0c\u7136\u540e\u58f0\u82e5\u6d2a\u949f\u4ed6\u8bf4\u9053\uff1a \u201c\u5404\u4f4d\u8fdc\u9053\u524d\u6765\uff0c\u5e86\u8d3a\u4e91\u8c26\u7684\u8d31\u8fb0\uff0c\u4e91\u8c26\u5b9e\u5728\u9ad8\u5174\u5f97\u5f88\uff0c\u53ea\u662f\u4e91\u8c26\u662f\u4e2a\u7c97\u4eba\uff0c\u4e0d\u4f1a\u8bf4\u4ec0\u4e48\u5ba2 \u5957\u7684\u8bdd\uff0c\u5404\u4f4d\u591a\u5403\u70b9\uff0c\u591a\u559d\u70b9\uff0c\u5c31\u662f\u770b\u5f97\u8d77\u6211\u4e91\u8c26\uff0c\u6211\u4e91\u8c26\u4e00\u9ad8\u5174\uff0c\u8fd8\u5f97\u518d\u6d3b\u5341\u5e74\u3002\u201d \u3000\u3000\u8fd9\u767d\u53d1\u8001\u4eba\u8bf4\u5b8c\u4e86\u8bdd\u5c31\u4ef0\u5929\u957f\u7b11\uff0c\u610f\u6c14\u8c6a\u98de\uff0c\u4e0d\u4e9a\u4e8e\u5c11\u5e74\u3002 \u3000\u3000\u5802\u4e0b\u7fa4\u8c6a\u4e5f\u7acb\u523b\u54cd\u8d77\u4e86\u4e00\u7247\u70ed\u70c8\u7684\u638c\u58f0\uff0c\u638c\u58f0\u4e2d\u53c8\u5939\u6742\u7740\u7b11\u58f0\uff0c\u7b11\u58f0\u4e2d\u53c8\u63ba\u5408\u7740\u4e91\u8c26\u90a3 \u9ad8\u4ea2\u7684\u7b11\u58f0\uff0c\u6df7\u5408\u6210\u4e00\u7247\u5409\u7965\u5bcc\u6cf0\u7684\u58f0\u97f3\u3002 \u3000\u3000\u7136\u540e\uff0c\u8fd9\u5fc3\u6ee1\u610f\u8db3\u7684\u8001\u5bff\u661f\u5c31\u5750\u4e86\u4e0b\u6765\uff0c\u7ad9\u5728\u4ed6\u65c1\u8fb9\u7684\u4e00\u4e2a\u957f\u886b\u58ee\u6c49\u53c8\u66ff\u4ed6\u659f\u6ee1\u4e86\u9152\uff0c \u4ed6\u518d\u7aef\u8d77\u9152\u676f\uff0c\u671d\u8fd9\u5f20\u684c\u5b50\u7684\u8c6a\u58eb\u9053\uff1a\u201c\u4f60\u6211\u8001\u5f1f\u5144\u4eec\u4e5f\u5e72\u4e00\u676f\u5427\uff01\u201d \u3000\u3000\u957f\u7709\u4e00\u6a2a\u5750\u5728\u4ed6\u8eab\u65c1\u7684\u65e0\u7fc5\u795e\u9e70\uff0c\u53c8\u7b11\u9053\uff1a\u201c\u7ba1\u8235\u4e3b\u8fdc\u9053\u800c\u6765\uff0c\u8001\u592b\u66f4\u5e94\u656c\u4e0a\u4e00\u676f\u3002\u201d \u3000\u3000\u90a3\u7ba1\u4e00\u67f4\u9e70\u76ee\u95ea\u52a8\uff0c\u4e5f\u7aef\u8d77\u676f\u6765\uff0c\u5374\u4f3c\u7b11\u975e\u7b11\u4ed6\u8bf4\u9053\uff1a\u201c\u4e91\u8001\u82f1\u96c4\u540d\u6ee1\u5929\u4e0b\uff0c\u6211\u7ba1\u4e00\u67f4 \u65e9\u8be5\u6765\u62dc\u8bbf\u4e86\uff0c\u600e\u5f53\u5f97\u8d77\u4e91\u8001\u82f1\u96c4\u7684\u656c\u9152\uff0c\u54c8\u54c8\u54c8\u3002\u201d \u3000\u3000\u4ed6\u5e72\u7b11\u4e86\u51e0\u58f0\uff0c\u4ef0\u7740\u5e72\u4e86\u90a3\u676f\u9152\uff0c\u4e00\u3001\u9762\u53c8\u9053\uff1a\u201c\u6211\u7ba1\u4e00\u67f4\u5148\u5e72\u4e3a\u656c\u4e86\u3002\u201d \u3000\u3000\u8fd9\u65e0\u7fc5\u795e\u9e70\u5634\u91cc\u8bf4\u7740\uff0c\u8eab\u5b50\u53ef\u4e00\u76f4\u6ca1\u6709\u7ad9\u8d77\u6765\uff0c\u4e91\u8c26\u54c8\u54c8\u4e00\u7b11\uff0c\u5fc3\u91cc\u5374\u591a\u591a\u5c11\u5c11\u6709\u4e9b\u4e0d \u6ee1\u610f\uff0c\u4e5f\u4ef0\u9996\u5e72\u4e86\u676f\u4e2d\u7684\u9152\uff0c\u7a81\u7136\u4e00\u76b1\u53cc\u7709\uff0c\u53ed\u7684\u4e00\u58f0\uff0c\u5c06\u9152\u676f\u91cd\u91cd\u653e\u5230\u684c\u4e0a\u957f\u53f9\u9053\uff1a\u201c\u4eca \u65e5\u6ee1\u5802\u670b\u6770\uff0c\u4ff1\u662f\u82f1\u624d\uff0c\u53ef\u662f\u2014\u2014\u5509\uff0c\u8fd9\u5176\u4e2d\u7adf\u5c11\u4e86\u4e00\u4eba\uff0c\u5509\uff0c\u867d\u7136\u4ec5\u4ec5\u5c11\u4e86\u4e00\u4eba\uff0c\u8001\u592b\u5374 \u89c9\u5f97\u6709\u4e9b\u2014\u2014\u5509\u3002\u201d \u3000\u3000\u8fd9\u591a\u81c2\u795e\u5251\u5ffd\u7136\u50cf\u662f\u60f3\u8d77\u4e86\u4ec0\u4e48\uff0c\u7adf\u8fde\u8fde\u53f9\u8d77\u6c14\u6765\uff0c\u4e24\u9053\u82cd\u767d\u7684\u5bff\u7709\uff0c\u4e5f\u7d27\u7d27\u76b1\u5230\u4e00 \u8d77\uff0c\u5de8\u5927\u7684\u624b\u638c\u7d27\u7d27\u634f\u7740\u9152\u676f\uff0c\u53ed\u7684\u4e00\u58f0\uff0c\u8fd9\u53ea\u6c5f\u897f\u7ec6\u74f7\u505a\u6210\u7684\u9152\u676f\uff0c\u7adf\u88ab\u4ed6\u634f\u7834\u4e86\u3002 \u3000\u3000\u5ea7\u4e0a\u7fa4\u8c6a\uff0c\u4e0d\u7981\u4e3a\u4e4b\u6115\u4e86\u4e00\u4e0b\uff0c\u5176\u4e2d\u6709\u4e2a\u8eab\u8eaf\u77ee\u80d6\u7684\u8001\u8005\uff0c\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8001\u54e5\u54e5\uff0c \u4f60\u7684\u5fc3\u4e8b\u8ba9\u5c0f\u5f1f\u731c\u4e0a\u4e00\u731c\uff0c\u4fdd\u51c6\u662f\u516b\u4e5d\u4e0d\u4f1a\u79bb\u5341\u3002\u201d \u3000\u3000\u4e91\u8c26\u671b\u4e86\u8fd9\u8001\u8005\u4e00\u773c\uff0c\u6682\u655b\u6101\u5bb9\uff0c\u7b11\u9053\uff1a\u201c\u597d\uff0c\u597d\uff0c\u8001\u592b\u5012\u8981\u770b\u770b\u4f60\u8fd9\u53ea\u8001\u72d0\u72f8\u731c\u4e0d\u731c \u5f97\u4e2d\u8001\u592b\u7684\u5fc3\u4e8b\uff0c\u4f60\u8981\u662f\u731c\u4e0d\u4e2d\u7684\u8bdd\uff0c\u6211\u770b\u4f60\u90a3\u7075\u72d0\u7684\u5916\u53f7\uff0c\u4ece\u4eca\u5929\u8d77\u5c31\u5f97\u6539\u6389\u3002\u201d \u3000\u3000\u539f\u6765\u8fd9\u77ee\u80d6\u8001\u8005\uff0c\u6b63\u662f\u4fa0\u4e49\u9053\u4e2d\u6709\u540d\u7684\u667a\u56ca\u2014\u2014\u7075\u72d0\u667a\u4e66\u3002 \u3000\u3000\u8fd9\u7075\u72d0\u667a\u4e66\u53c8\u54c8\u54c8\u4e00\u7b11\uff0c\u4f38\u8d77\u5927\u62c7\u6307\uff0c\u4e0a\u4e0b\u6643\u4e86\u6643\uff0c\u7b11\u9053\uff1a\u201c\u8001\u54e5\u54e5\u5fc3\u91cc\u60f3\u7684\uff0c\u662f\u4e0d\u662f \u5c31\u662f\u90a3\u4e00\u53bb\u9ec4\u5c71\u3001\u4ece\u6b64\u4e0d\u56de\u7684\u5353\u6d69\u7136\u5440\uff1f\u201d \u3000\u3000\u4e91\u8c26\u731b\u7136\u4e00\u62cd\u684c\u5b50\uff0c\u8fde\u8fde\u9053\uff1a\u201c\u597d\u4f60\u4e2a\u72d0\u72f8\uff0c\u771f\u7684\u53c8\u88ab\u4f60\u731c\u7740\u4e86\uff0c\u53ea\u662f\u2014\u2014\u5509\uff0c\u6d69\u7136\u8001 \u5f1f\u8fd9\u4e00\u53bb\u5341\u5e74\uff0c\u7adf\u8fde\u4e00\u70b9\u97f3\u8baf\u90fd\u6ca1\u6709\u4e86\uff0c\u82e5\u8bf4\u50cf\u4ed6\u90a3\u6837\u7684\u4eba\u4f1a\u65e0\u58f0\u65e0\u606f\u7684\u6b7b\u4e86\uff0c\u53ef\u771f\u6559\u6211\u6709 \u4e9b\u4e0d\u76f8\u4fe1\uff0c\u82e5\u8bf4\u4ed6\u6ca1\u6709\u6b7b\uff0c\u5509\u2014\u2014\u201d\u8fd9\u80f8\u6000\u78ca\u843d\u7684\u8001\u4eba\u7ade\u53c8\u957f\u53f9\u4e00\u58f0\uff0c\u518d\u4e8e\u4e86\u4e00\u676f\u9152\uff0c\u63a5\u7740 \u9053\uff1a\u201c\u4ed6\u53c8\u600e\u4f1a\u4e00\u70b9\u6d88\u606f\u90fd\u6ca1\u6709\uff0c\u96be\u9053\u4ed6\u7adf\u628a\u6211\u8fd9\u4e2a\u8001\u54e5\u54e5\u5fd8\u4e86\u3002\u201d \u3000\u3000\u539f\u6765\u6614\u5e74\u9ec4\u5c71\u59cb\u4fe1\u5cf0\u4e0b\uff0c\u90a3\u4e00\u6bb5\u60ca\u5fc3\u52a8\u9b44\u7684\u5f80\u4e8b\uff0c\u5e76\u6ca1\u6709\u4f20\u5165\u6c5f\u6e56\uff0c\u662f\u4ee5\u6b66\u6797\u4e2d\u4eba\uff0c\u6839 \u672c\u4e0d\u77e5\u9053\u4e2d\u539f\u5927\u4fa0\u5353\u6d69\u7136\u65e9\u5df2\u6b7b\u4e86\u3002 \u3000\u3000\u6b64\u523b\u6a2a\u6c5f\u91d1\u7d22\u695a\u5360\u9f99\u7b11\u7740\u63a5\u53e3\u9053\uff1a\u201c\u4e91\u5927\u54e5\uff0c\u4f60\u5c3d\u7ba1\u653e\u5fc3\uff0c\u60f3\u90a3\u4e2d\u539f\u5353\u5927\u4fa0\uff0c\u662f\u4f55\u7b49\u7684 \u6b66\u529f\uff0c\u5929\u4e0b\u53c8\u6709\u4ec0\u4e48\u4eba\u80fd\u5236\u6b7b\u4ed6\uff0c\u4e91\u5927\u54e5\uff0c\u4eca\u5929\u662f\u4f60\u7684\u5bff\u8fb0\uff0c\u5927\u5bb6\u4e0d\u8bb8\u8bf4\u626b\u5174\u7684\u8bdd\uff0c\u6765\uff0c \u6765\uff0c\u6765\uff0c\u5c0f\u5f1f\u518d\u656c\u5927\u54e5\u4e00\u676f\u3002\u201d \u3000\u3000\u8fd9\u8001\u5144\u5f1f\u4e24\u4eba\u6b63\u81ea\u4e3e\u676f\uff0c\u5750\u5728\u4e2d\u95f4\u7684\u7ba1\u4e00\u67f4\u5374\u7a81\u7136\u51b7\u7b11\u4e00\u58f0\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u60f3\u90a3\u5353\u6d69\u7136\u6b66 \u529f\u867d\u9ad8\uff0c\u82e5\u8bf4\u666e\u5929\u4e4b\u4e0b\uff0c\u6ca1\u6709\u4eba\u80fd\u5236\u5f97\u4ed6\u7684\u6b7b\u547d\uff0c\u53ea\u6015\u4e5f\u672a\u5fc5\u89c1\u5f97\uff0c\u5982\u82e5\u4e0d\u7136\uff0c\u90a3\u5353\u6d3d\u7136 \u8fd9\u3001\u2018\u5e74\u6765\uff0c\u53c8\u662f\u8dd1\u5230\u54ea\u91cc\u53bb\u4e86\uff0c\u8fde\u5f71\u5b50\u90fd\u4e0d\u89c1\uff0c\u96be\u9053\u4ed6\u4e0a\u5929\u5165\u5730\u4e86\u5417\uff1f\u201d\u4e91\u8c26\u4e24\u9053\u767d\u7709\uff0c \u500f\u7136\u5012\u7acb\u8d77\u6765\uff0c\u7a81\u53c8\u4ef0\u5929\u4e00\u9635\u957f\u7b11\uff0c\u6717\u58f0\u9053\uff1a\u201c\u53ef\u61be\u5440\uff0c\u53ef\u61be\uff0c\u9ed1\u7c73\u5e2e\u5d1b\u8d77\u6c5f\u6e56\uff0c\u624d\u53ea\u662f\u8fd9 \u4e24\u5e74\u7684\u4e8b\uff0c\u7ba1\u8235\u4e3b\u7684\u5927\u540d\uff0c\u4e5f\u53ea\u662f\u8fd1\u51e0\u5e74\u6765\u624d\u4f20\u52a8\u6c5f\u6e56\uff0c\u5982\u82e5\u7ba1\u8235\u4e3b\u65e9\u51fa\u9053\u4e2a\u56db\u4e94\u5e74\uff0c\u60f3\u90a3 \u5353\u6d69\u7136\u5929\u4e0b\u7b2c\u4e00\u9ad8\u624b\u7684\u58f0\u8a89\uff0c\u4ea6\u5fc5\u8981\u8ba9\u7ed9\u7ba1\u8235\u4e3b\u4e86\u3002\u201d \u3000\u3000\u7ba1\u4e00\u67f4\u9e70\u76ee\u4e00\u5f20\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u8fd9\u4e5f\u662f\u6781\u6709\u53ef\u80fd\u7684\u4e8b\u3002\u201d \u3000\u3000\u591a\u81c2\u795e\u5251\u6012\u6781\u800c\u7b11\uff0c\u731b\u7136\u4e00\u62cd\u684c\u5b50\uff0c\u9ad8\u5927\u7684\u8eab\u8eaf\uff0c\u7ad9\u4e86\u8d77\u6765\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u7ba1\u8235\u4e3b\uff0c\u4eca\u65e5 \u4f60\u66ff\u8001\u592b\u4e0a\u5bff\uff0c\u8001\u592b\u591a\u8c22\u4e86\uff0c\u6b64\u523b\u5bff\u5df2\u795d\u8fc7\uff0c\u8001\u592b\u4e5f\u4e0d\u6562\u591a\u7559\u7ba1\u8235\u4e3b\u7684\u5927\u9a7e\uff0c\u8bf7\uff0c\u8bf7\uff0c \u8bf7\u3002\u201d \u3000\u3000\u8f6c\u5934\u53c8\u559d\u9053\uff1a\u201c\u4e2d\u7a0b\uff0c\u4f60\u66ff\u8001\u592b\u9001\u5ba2\uff01\u201d \u3000\u3000\u8fd9\u591a\u81c2\u795e\u5251\uff0c\u6b64\u523b\u7adf\u4e0b\u8d77\u9010\u5ba2\u4ee4\u6765\u4e86\u3002 \u3000\u3000\u8fd9\u65e0\u7fc5\u795e\u9e70\u7ba1\u4e00\u67f4\uff0c\u51fa\u9053\u672c\u65e9\uff0c\u672c\u65e0\u85c9\u85c9\u4e4b\u540d\uff0c\u540e\u6765\u4e0d\u77e5\u600e\u7684\uff0c\u5374\u88ab\u4ed6\u5b66\u6765\u4e00\u8eab\u795e\u51fa\u9b3c \u6ca1\u7684\u672c\u4e8b\uff0c\u5728\u6cb3\u4e1c\u5efa\u8d77\u9ed1\u7c73\u5e2e\u3002 \u3000\u3000\u9ed1\u7c73\u5e2e\u5728\u6c5f\u5357\u6b66\u6797\u4e2d\uff0c\u5f88\u505a\u4e86\u51e0\u4ef6\u5927\u4e8b\uff0c\u8fd9\u65e0\u7fc5\u795e\u9e70\u7ba1\u4e00\u67f4\uff0c\u540d\u58f0\u4e5f\u7acb\u523b\u9707\u52a8\u6c5f\u6e56\uff0c\u53ef \u8bf4\u662f\u5f53\u4eca\u6b66\u6797\u4e2d\u9876\u5c16\u513f\u7684\u4eba\u7269\u4e4b\u6b64\u523b\u8fd9\u9ed1\u7c73\u5e2e\u4e3b\u6c14\u5f97\u8138\u4e0a\u9752\u4e00\u9635\uff0c\u767d\u4e00\u9635\uff0c\u4e5f\u653e\u58f0\u5927\u7b11\u4e86\u8d77 \u6765\uff0c\u6307\u7740\u4e91\u8c26\u9ad8\u58f0\u559d\u9053\uff1a\u201c\u59d3\u4e91\u7684\uff0c\u4f60\u53ef\u4f30\u91cf\u4f30\u91cf\uff0c\u4eca\u5929\u4f60\u6562\u5bf9\u6211\u7ba1\u4e00\u67f4\u8fd9\u4e48\u5356\u72c2\uff0c\u4f60\u8fd9\u7cdf \u8001\u5934\u5b50\u60f3\u662f\u6d3b\u5f97\u4e0d\u8010\u70e6\u4e86\uff0c\u6211\u7ba1\u5927\u7237\u4eca\u5929\u53ef\u59a5\u5f53\u7740\u5929\u4e0b\u7fa4\u8c6a\u6559\u8bad\u6559\u8bad\u4f60\uff0c\u201d\u8bf4\u7740\uff0c\u4e00\u633d\u8896 \u5b50\uff0c\u5c31\u7ad9\u4e86\u8d77\u6765\u3002 \u3000\u3000\u4e91\u8c26\u864e\u76ee\u6012\u5f20\uff0c\u53cc\u624b\u4e00\u63a8\uff0c\u7adf\u5c06\u4e00\u5f20\u684c\u5b50\u90fd\u9669\u4e9b\u63a8\u7ffb\u4e86\uff0c\u676f\u76d8\u7b49\u4ef6\uff0c\u72fc\u85c9\u4e00\u5730\uff0c\u5e78\u597d\u5728 \u5ea7\u7684\u4ff1\u662f\u827a\u4e1a\u9ad8\u5f3a\u4e4b\u58eb\uff0c\u65e9\u5c31\u53ca\u65f6\u8eb2\u5f00\u3002 \u3000\u3000\u8fd9\u4e00\u6765\u6ee1\u5385\u7fa4\u8c6a\u90fd\u7ad9\u4e86\u8d77\u6765\uff0c\u60ca\u7136\u52a8\u5bb9\uff0c\u4e91\u4e2d\u7a0b\u6c14\u5f97\u9762\u76ee\u53d8\u8272\uff0c\u5389\u559d\u9053\uff1a\u201c\u7ba1\u670b\u53cb\uff0c\u4f60 \u8fd9\u662f\u5e72\u4ec0\u4e48\uff0c\u4f60\u8fd9\u7b80\u76f4\u662f\u8981\u6211\u4e91\u67d0\u7684\u597d\u770b\u2014\u2014\u201d\u7ba1\u4e00\u67f4\u51b7\u7b11\u7740\uff0c\u63a5\u53e3\u9053\uff1a\u201c\u8981\u4f60\u597d\u770b\u53c8\u600e\u4e48 \u6837\uff0c\u522b\u4eba\u754f\u60e7\u4f60\u4e91\u6c0f\u7236\u5b50\u4e09\u4eba\uff0c\u6211\u7ba1\u4e00\u67f4\u53ef\u4e0d\u4e70\u8fd9\u4e2a\u5e10\uff0c\u59d3\u4e91\u7684\u5c0f\u5b50\uff0c\u4ece\u4eca\u5929\u8d77\uff0c\u4f60\u4eec\u90a3\u51e0 \u4e2a\u9556\u5c40\u5b50\u5374\u8981\u662f\u8fd8\u505a\u5f97\u4e86\u4e70\u5356\u7684\uff0c\u6211\u7ba1\u4e00\u67f4\u8fd9\u4e2a\u7ba1\u59d3\uff0c\u4ece\u6b64\u5c31\u5012\u8fc7\u6765\u5199\uff01\u201d \u3000\u3000\u8fd9\u7ba1\u4e00\u67f4\u827a\u9ad8\u5fc3\u72c2\uff0c\u5728\u8fd9\u79cd\u5730\u65b9\uff0c\u7ade\u6562\u8bf4\u51fa\u5982\u6b64\u72c2\u8bdd\u6765\uff0c\u4e91\u6c0f\u7236\u5b50\u4ff1\u90fd\u6c14\u5f97\u9762\u8272\u94c1\u9752\uff0c \u90a3\u7075\u72d0\u667a\u4e66\u5374\u6446\u7740\u624b\uff0c\u8fde\u8fde\u9053\uff1a\u201c\u7ba1\u8235\u4e3b\uff0c\u4f60\u770b\u6211\u667a\u4e66\u7684\u9762\uff0c\u5c11\u8bf4\u4e00\u53e5\uff01\u201d \u3000\u3000\u53c8\u9053\uff1a\u201c\u8001\u54e5\u54e5\uff0c\u6211\u8bf4\u4f60\u8fd9\u662f\u5e72\u4ec0\u4e48\uff0c\u4eca\u5929\u662f\u4f60\u5927\u559c\u7684\u65e5\u5b50\uff0c\u4f60\u53c8\u4f55\u82e6\uff01\u201d \u3000\u3000\u4e00\u9762\u56db\u4e0b\u4e71\u6446\u624b\uff1a\u201c\u6765\uff0c\u6765\uff0c\u5927\u5bb6\u5750\u4e0b\u6765\uff0c\u656c\u6211\u4eec\u5bff\u661f\u4e00\u676f\u3002\u201d \u3000\u3000\u8fd9\u7075\u72d0\u667a\u4e66\u4e00\u770b\u4e8b\u60c5\u5982\u6b64\u7cdf\uff0c\u751f\u6015\u597d\u597d\u4e00\u4e2a\u5bff\u5bb4\uff0c\u5f04\u5f97\u4e0d\u6210\u7ae0\u6cd5\uff0c\u5c31\u8fde\u8fde\u529d\u963b\u7740\uff0c\u53ef\u662f \u6b64\u65f6\u56db\u4e0b\u65e9\u5df2\u4e71\u6210\u4e00\u7247\u4e86\u3002 \u3000\u3000\u90a3\u591a\u81c2\u795e\u5251\u6c14\u543c\u543c\u4ed6\u8bf4\uff1a\u201c\u6709\u4eba\u6307\u7740\u6211\u4e91\u67d0\u7684\u9f3b\u5b50\u9a82\u6211\u90fd\u884c\uff0c\u53ef\u662f\u8981\u662f\u6709\u4eba\u7f16\u6392\u6211\u6d69\u7136 \u8001\u5f1f\uff0c\u6211\u4e91\u67d0\u4eba\u5c31\u662f\u62fc\u6389\u8fd9\u4e2a\u8001\u9aa8\u5934\uff0c\u4e5f\u5f97\u4f38\u91cf\u4ed6\u662f\u4ec0\u4e48\u53d8\u7684\uff01\u201d \u3000\u3000\u4ec1\u4e49\u5251\u5ba2\u4e91\u4e2d\u7a0b\u4e00\u897f\u529d\u7740\u81ea\u5df1\u8001\u7236\uff0c\u4e00\u9762\u5411\u7ba1\u4e00\u67f4\u559d\u9a82\u3002 \u3000\u3000\u7ba1\u4e00\u67f4\u5374\u53ea\u662f\u51b7\u7b11\u7740\uff0c\u5353\u7136\u800c\u7acb\uff0c\u8fd9\u9ed1\u7c73\u5e2e\u4e3b\u679c\u7136\u6709\u70b9\u4e00\u4ee3\u67ad\u96c4\u7684\u6c14\u6d3e\uff0c\u5728\u8fd9\u79cd\u9635\u4ed7 \u4e0b\uff0c\u5012\u6ca1\u6709\u4e00\u4e1d\u5fc3\u614c\u7684\u6837\u5b50\u9732\u51fa\u6765\u3002 \u3000\u3000\u4ec1\u4e49\u5251\u5ba2\u867d\u7136\u6c14\u6027\u51b2\u548c\uff0c\u6b64\u523b\u4e5f\u5fcd\u65e0\u53ef\u5fcd\uff0c\u6307\u7740\u7ba1\u4e00\u67f4\u559d\u9053\uff1a\u201c\u59d3\u7ba1\u7684\uff0c\u4f60\u4eca\u5929\u8fd9\u4e48\u6363 \u4e71\uff0c\u60f3\u5fc5\u662f\u4ed7\u7740\u624b\u5e95\u4e0b\u6709\u4e24\u4e0b\u5b50\uff0c\u6765\uff0c\u6765\uff0c\u6211\u4e91\u4e2d\u7a0b\u4eca\u5929\u5c31\u4f38\u91cf\u4f38\u91cf\u4f60\uff0c\u6211\u4eec\u51fa\u53bb\u52a8\u624b \u53bb\u3002\u201d \u3000\u3000\u8bf4\u7740\u8bdd\uff0c\u8fd9\u6c5f\u5357\u4fa0\u4e49\u9053\u4e2d\u7684\u7b2c\u4e00\u4eba\u5c31\u5c06\u957f\u886b\u4e00\u64a9\uff0c\u8dfa\u811a\uff0c\u55d6\u5730\uff0c\u5c31\u5e73\u5730\u62e8\u4e86\u8d77\u6765\uff0c\u53cc\u817f \u4e00\u8e6c\uff0c\u8eab\u5f62\u5c31\u7a9c\u5230\u4e86\u9662\u5b50\u91cc\u3002 \u3000\u3000\u4ec1\u4e49\u5251\u5ba2\u9732\u4e86\u8fd9\u624b\u8f7b\u529f\uff0c\u5728\u5ea7\u7fa4\u8c6a\u5c31\u54c4\u7136\u559d\u8d77\u597d\u6765\uff0c\u6697\u9053\uff1a\u201c\u8fd8\u662f\u4e91\u8001\u7236\u5b50\u7684\u529f\u592b\u4fca\uff0c \u4f60\u770b\uff0c\u5c31\u51b2\u4e91\u5c11\u4fa0\u7684\u8fd9\u4e00\u624b\uff0c\u5c31\u591f\u77a7\u597d\u534a\u5929\u4e86\uff0c\u65e0\u602a\u4eba\u5bb6\u80fd\u7edf\u7387\u90a3\u4e48\u591a\u9556\u5c40\u5b50\uff0c\u4eba\u5bb6\u662f\u771f \u884c\u3002\u201d \u3000\u3000\u5927\u5bb6\u6697\u4e2d\u6b63\u81ea\u5938\u5956\u7740\uff0c\u54ea\u77e5\u65e0\u7fc5\u795e\u9e70\u51b7\u7b11\u4e00\u58f0\uff0c\u8eab\u5f62\u50cf\u662f\u52a8\u90fd\u6ca1\u6709\u52a8\uff0c\u5c31\u8fd9\u4e48\u6837\u7a9c\u4e86\u8d77 \u6765\uff0c\u5728\u7a7a\u4e2d\u4e00\u62e7\u8170\uff0c\u5c31\u50cf\u662f\u4e00\u652f\u7bad\u4f3c\u7684\uff0c\u5c04\u5230\u9662\u5b50\u7684\u4e0a\u7a7a\uff0c\u7136\u540e\u5fae\u4e00\u8f6c\u6298\uff0c\u8f7b\u98d8\u98d8\u5730\u843d\u4e86\u4e0b \u6765\u3002 \u3000\u3000\u8fd9\u65e0\u7fc5\u795e\u9e70\u4e00\u65bd\u5c55\u51fa\u5982\u6b64\u7684\u8eab\u624b\uff0c\u7fa4\u8c6a\u53c8\u4ff1\u90fd\u8272\u53d8\uff0c\u4e91\u8c26\u4e00\u634b\u957f\u987b\uff0c\u8ddf\u4e86\u51fa\u53bb\uff0c\u6ee1\u5ea7\u7fa4 \u8c6a\u996d\u4e5f\u4e0d\u5403\u4e86\uff0c\u90fd\u6324\u5230\u9662\u5b50\u91cc\u53bb\u4e86\u3002 \u3000\u3000\u4f46\u662f\uff0c\u5728\u8fd9\u5927\u5385\u89d2\u4e0a\u7684\u4e00\u5f20\u684c\u5b50\u4e0a\uff0c\u5374\u4ecd\u7136\u8fd8\u6709\u4e00\u4eba\u65c1\u82e5\u65e0\u4eba\u5730\u5927\u5403\u5927\u559d\u7740\uff0c\u8138\u4e0a\u4e1d\u6beb \u65e0\u52a8\u4e8e\u8877\uff0c\u751f\u50cf\u662f\u65b9\u624d\u7684\u4e8b\uff0c\u4ed6\u65e2\u6ca1\u6709\u770b\u5230\uff0c\u4e5f\u6ca1\u6709\u542c\u5230\u4f3c\u7684\uff0c\u6839\u672c\u6ca1\u6709\u5c06\u8fd9\u4e8b\u653e\u5728\u5fc3\u4e0a\u3002 \u3000\u3000\u8fd9\u4eba\u4e00\u88ad\u9ec4\u886b\uff0c\u9762\u76ee\u82f1\u4fca\uff0c\u7adf\u7136\u5c31\u662f\u90a3\u4e2a\u964c\u751f\u800c\u72c2\u50b2\u7684\u5c11\u5e74\u3002 \u3000\u3000\u6b64\u523b\uff0c\u4ed6\u50cf\u662f\u5403\u5b8c\u4e86\uff0c\u7ad9\u4e86\u8d77\u6765\uff0c\u62b9\u4e86\u62b9\u5634\uff0c\u76ee\u5149\u5f80\u76d8\u4e2d\u653e\u7740\u7684\u90a3\u53ea\u5269\u4e0b\u7684\u4e00\u534a\u9165\u70b8\u5b50 \u9e21\u4e0a\u4e00\u626b\uff0c\u5fae\u53f9\u4e86\u53e3\u6c14\uff0c\u50cf\u662f\u610f\u72b9\u672a\u5c3d\u4f3c\u7684\uff0c\u53c8\u6495\u4e86\u4e00\u5757\uff0c\u653e\u5230\u5634\u91cc\u5480\u56bc\u7740\u3002 \u3000\u3000\u7136\u540e\uff0c\u4ed6\u6162\u541e\u541e\u5730\u8d70\u5230\u5ead\u53e3\uff0c\u6162\u541e\u541e\u5730\u5206\u5f00\u644a\u5728\u95e8\u53e3\u7684\u7fa4\u8c6a\uff0c\u6162\u541e\u541e\u5730\u8d70\u4e86\u51fa\u53bb\uff0c\u7adf\u7136 \u9759\u4fcf\u4fcf\u7684\u2014\u2014\u539f\u6765\u90a3\u6c5f\u5357\u4fa0\u4e49\u9053\u7684\u9886\u8896\uff0c\u548c\u6cb3\u4e1c\u9ed1\u7c73\u5e2e\u7684\u603b\u74e2\u628a\u5b50\u5df2\u7ecf\u52a8\u4e0a\u624b\u4e86\u3002 \u3000\u3000\u9ec4\u886b\u5c11\u5e74\u7f13\u7f13\u8e31\u51fa\u5927\u5385\uff0c\u53ea\u89c1\u9662\u5b50\u91cc\u4fcf\u7136\u65e0\u58f0\uff0c\u6570\u767e\u53ea\u773c\u775b\u90fd\u6ce8\u89c6\u7740\u6b63\u5728\u52a8\u624b\u7684\u4ec1\u4e49\u5251 \u5ba2\u4e91\u4e2d\u7a0b\u548c\u65e0\u7fc5\u795e\u9e70\u7ba1\u4e00\u67f4\u8eab\u4e0a\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u90fd\u662f\u6b66\u6797\u4e2d\u4e07\u513f\u6781\u54cd\u7684\u4eba\u7269\uff0c\u5728\u8fd9\u79cd\u751f\u6b7b\u640f\u6597\u7684\u60c5\u51b5\u4e0b\uff0c\u8fd9\u800c\u4eba\u7adf\u7136\u672a\u8131\u4e0b\u957f \u886b\uff0c\u4ec5\u5c06\u957f\u886b\u7684\u4e0b\u6446\uff0c\u6396\u5728\u8170\u95f4\u7684\u4e1d\u5e26\u4e0a\uff0c\u811a\u4e0b\u4e5f\u4ecd\u7136\u7a7f\u7740\u7c89\u5e95\u6731\u9762\u7684\u5b98\u5c65\u3002 \u3000\u3000\u4f46\u662f\u8fd9\u79cd\u88c5\u675f\uff0c\u5374\u50cf\u662f\u4e1d\u6beb\u6ca1\u6709\u5f71\u54cd\u5230\u4ed6\u4eec\u8eab\u5f62\u7684\u7075\u5de7\uff0c\u5c31\u5728\u8fd9\u56db\u5468\u90fd\u7ad9\u6ee1\u4e86\u6b66\u6797\u7fa4 \u8c6a\u3001\u5f53\u4e2d\u65b9\u5706\u4e0d\u5230\u4e09\u4e08\u7684\u9662\u5b50\u91cc\uff0c\u4f46\u89c1\u8fd9\u4ec1\u4e49\u5251\u5ba2\u4e91\u4e2d\u7a0b\u8eab\u5f62\u6d41\u8f6c\uff0c\u886b\u8896\u98d8\u98d8\uff0c\u59ff\u6001\u6f47\u6d12\u5df2 \u6781\uff0c\u7adf\u548c\u4ed6\u5e73\u65e5\u4e3a\u4eba\u62d8\u8c28\u7684\u6837\u5b50\uff0c\u622a\u7136\u800c\u5f02\u3002 \u3000\u3000\u4f46\u662f\u8fd9\u65e0\u7fc5\u795e\u9e70\u7ba1\u4e00\u67f4\uff0c\u8eab\u6cd5\u7684\u8f7b\u7075\u3001\u5feb\u6377\uff0c\u5374\u5c24\u4f3c\u5728\u4ed6\u4e4b\u4e0a\uff0c\u56db\u4e0b\u7fa4\u8c6a\u53ea\u89c9\u773c\u82b1\u9519 \u843d\uff0c\u6ee1\u76ee\u4ff1\u662f\u8fd9\u4e24\u4eba\u8eab\u5f71\u3002 \u3000\u3000\u957f\u6c5f\u6c34\u8def\u5927\u8c6a\u6a2a\u6c5f\u91d1\u7d22\u695a\u5360\u9f99\uff0c\u7d27\u7d27\u5730\u7ad9\u5728\u5bff\u661f\u4e91\u8c26\u8eab\u4fa7\uff0c\u8fd9\u4e24\u4e2a\u987b\u53d1\u90fd\u5df2\u8fd1\u5168\u767d\u7684\u6b66 \u6797\u5065\u8005\uff0c\u6b64\u523b\u5374\u90fd\u662f\u9762\u9732\u7d27\u5f20\u4e4b\u8272\uff0c\u56e0\u4e3a\u6b63\u5728\u640f\u6597\u7684\u4e24\u4eba\uff0c\u65e0\u8bba\u662f\u8c01\u80dc\u8c01\u8d1f\uff0c\u5374\u90fd\u662f\u4e0d\u4e86\u4e4b \u5c40\uff0c\u52bf\u5fc5\u8981\u5728\u6c5f\u6e56\u60f9\u51fa\u6781\u5927\u7684\u98ce\u6ce2\u6765\u3002 \u3000\u3000\u56db\u4e0b\u8083\u7136\u7ad9\u7740\u7684\u6b66\u6797\u7fa4\u8c6a\uff0c\u867d\u7136\u90fd\u548c\u4e91\u6c0f\u7236\u5b50\u7684\u5173\u7cfb\u8f83\u6df1\uff0c\u4f46\u5374\u4e5f\u6ca1\u6709\u4e00\u4e2a\u4eba\u6562\u51fa\u5934\u5e72 \u9884\u6b64\u4e8b\uff0c\u53ea\u662f\u5728\u79c1\u5fc3\u4e0b\u6697\u6697\u5e0c\u671b\u4e91\u4e2d\u7a0b\u5f97\u80dc\u7f62\u4e86\u3002 \u3000\u3000\u4f46\u8fd9\u4e24\u4eba\u7684\u8eab\u624b\uff0c\u5728\u6b66\u6797\u4e2d\u53c8\u53ef\u7b97\u5f97\u4e0a\u90fd\u662f\u4e00\u6d41\u9ad8\u624b\uff0c\u80dc\u8d1f\u5374\u4e0d\u662f\u4e00\u65f6\u534a\u523b\u4e4b\u95f4\u80fd\u591f\u5206 \u5224\u5f97\u51fa\u7684\u3002 \u3000\u3000\u6b64\u523b\u591c\u5df2\u9887\u6df1\uff0c\u9662\u4e2d\u56db\u4fa7\u7684\u9ad8\u5899\u4e0a\uff0c\u65e9\u5df2\u9646\u7eed\u6dfb\u4e0a\u6570\u5341\u652f\u677e\u679d\u624e\u6210\u7684\u706b\u628a\uff0c\u706b\u628a\u4e0a\u5c3a\u8bb8 \u9ad8\u7684\u706b\u7130\uff0c\u987a\u7740\u4e1c\u5357\u5439\u6765\u7684\u6625\u98ce\uff0c\u659c\u659c\u5730\u5411\u897f\u5317\u5012\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u677e\u679d\u71c3\u70e7\u65f6\uff0c\u53d1\u51fa\u7684\u6bd5\u5265\u4e4b\u58f0\uff0c\u5728\u8fd9\u56db\u4e0b\u7684\u9662\u91cc\u9762\uff0c\u4e0e\u8fd9\u4e24\u4eba\u52a8\u624b\u65f6\u53d1\u51fa\u7684\u864e\u864e\u638c\u58f0\uff0c \u5f62\u6210\u4e86\u4e00\u79cd\u6781\u4e0d\u534f\u8c03\u7684\u58f0\u54cd\u3002 \u3000\u3000\u77ac\u606f\u4e4b\u95f4\uff0c\u8fd9\u4e24\u4eba\u5df2\u62c6\u4e86\u6570\u767e\u62db\u4ee5\u4e0a\uff0c\u4f46\u4ece\u4ed6\u4eec\u638c\u4e0a\u6325\u51fa\u7684\u638c\u98ce\uff0c\u5374\u50cf\u662f\u6bd4\u521a\u521a\u52a8\u624b\u65f6 \u66f4\u4e3a\u51cc\u5389\uff0c\u65e0\u7fc5\u795e\u9e70\u7ba1\u4e00\u67f4\u6d41\u52a8\u7740\u7684\u8eab\u5f62\uff0c\u500f\u7136\u4e00\u987f\uff0c\u5cf0\u8170\u4e00\u632b\uff0c\u8eab\u5f62\u62e7\u8f6c\u5f00\uff0c\u53cc\u638c\u547c\u7684\u4e00 \u58f0\uff0c\u6ee1\u805a\u771f\u529b\uff0c\u5411\u90a3\u6b63\u4ee5\u4e00\u62db\u5982\u5c01\u4f3c\u95ed\u62a4\u7740\u524d\u80f8\u7684\u4e91\u4e2d\u7a0b\u51fb\u51fa\u3002 \u3000\u3000\u4ed6\u4e45\u6218\u65e0\u529f\uff0c\u6b64\u523b\u5df2\u89c9\u4e0d\u8010\uff0c\u662f\u4ee5\u7adf\u820d\u5f03\u62db\u5f0f\u7684\u53d8\u5316\uff0c\u800c\u60f3\u4ee5\u771f\u529b\u7684\u5f3a\u5f31\u6765\u5206\u5224\u80dc\u8d1f \u4e86\u3002 \u3000\u3000\u56f4\u89c2\u7740\u7684\u4eba\uff0c\u5927\u591a\u90fd\u662f\u7ec3\u5bb6\u5b50\uff0c\u5f53\u7136\u77e5\u9053\u7ba1\u4e00\u67f4\u51fa\u8fd9\u4e00\u62db\u7684\u7528\u610f\uff0c\u4e5f\u77e5\u9053\u53ea\u8981\u8fd9\u4ec1\u4e49\u5251 \u5ba2\u4f38\u624b\u53bb\u63a5\u8fd9\u4e00\u638c\uff0c\u90a3\u4e48\u8fd9\u4e00\u6210\u5206\u5224\u80dc\u8d1f\u7684\u65f6\u5019\u4fbf\u5230\u4e86\uff0c\u56db\u4e0b\u4f17\u8c6a\u7684\u6570\u767e\u53ea\u773c\u775b\uff0c\u4e0d\u7981\u90fd\u4e00 \u8d77\u671b\u5230\u90a3\u4ec1\u4e49\u5251\u5ba2\u4e91\u4e2d\u7a0b\u7684\u4e00\u53cc\u624b\u5b66\u4e0a\u3002 \u3000\u3000\u591a\u81c2\u795e\u5251\u53f3\u624b\u634b\u7740\u957f\u987b\uff0c\u5de6\u624b\u6258\u7740\u53f3\u8098\uff0c\u8fd9\u95ef\u8361\u6c5f\u6e56\u5df2\u6709\u6570\u5341\u5e74\u7684\u6b66\u6797\u5065\u8005\uff0c\u6b64\u523b\uff0c\u867d \u50cf\u662f\u4ecd\u7136\u5fcd\u5f97\u4f4f\u5fc3\u4e2d\u6fc0\u52a8\uff0c\u5176\u5b9e\u4ed6\u8170\u817f\u5374\u90fd\u5df2\u6ee1\u805a\u771f\u529b\uff0c\u53ea\u8981\u4e91\u4e2d\u7a0b\u4e00\u4e2a\u843d\u8d25\uff0c\u4ed6\u4fbf\u7acb\u523b\u98de \u8eab\u63f4\u6551\u3002 \u3000\u3000\u65e0\u7fc5\u795e\u9e70\u7ba1\u4e00\u67f4\u8fd9\u4e00\u53cc\u624b\u638c\u521a\u521a\u5410\u51fa\uff0c\u54ea\u77e5\u4e91\u4e2d\u7a0b\u95f7\u54fc\u4e00\u58f0\uff0c\u811a\u4e0b\u8fde\u8dfa\u4e03\u661f\u6b65\uff0c\u8eab\u5f62\u6ed1 \u6e9c\u6e9c\u4e00\u8f6c\uff0c\u7ade\u8f6c\u5230\u7ba1\u4e00\u67f4\u8eab\u540e\u53bb\u4e86\u3002 \u3000\u3000\u8fd9\u65e0\u7fc5\u795e\u9e70\u638c\u4e0a\u7684\u771f\u529b\uff0c\u5374\u5df2\u5982\u7bad\u5728\u5f26\u4e0a\uff0c\u4e0d\u5f97\u4e0d\u53d1\uff0c\u53ea\u542c\u547c\u7684\u4e00\u58f0\uff0c\u9662\u4e2d\u5149\u5f71\u5206\u82b1\uff0c \u5899\u4e0a\u7684\u706b\u628a\u4e0a\uff0c\u7ade\u88ab\u4ed6\u8fd9\u8fdc\u9694\u7740\u4e09\u56db\u4e08\u7684\u638c\u98ce\uff0c\u51fb\u5f97\u706b\u7130\u4e00\u9eef\uff0c\u9669\u4e9b\u7184\u706d\u3002 \u3000\u3000\u8fd9\u5149\u5f71\u5fae\u9eef\uff0c\u7fa4\u8c6a\u7d27\u6263\u7740\u7684\u5fc3\u5f26\u677e\u4e86\u53e3\u6c14\uff0c\u4f46\u89c1\u65e0\u7fc5\u795e\u9e70\u7ba1\u4e00\u67f4\u4e00\u638c\u51fb\u7a7a\u540e\uff0c\u8eab\u5f62\u7edd\u4e0d \u505c\u987f\uff0c\u5728\u8fd9\u65e7\u529b\u5df2\u5c3d\u3001\u65b0\u529b\u672a\u751f\u7684\u4e00\u5239\u90a3\u91cc\uff0c\u4ed6\u811a\u4e0b\u7ade\u8fd8\u80fd\u786c\u751f\u751f\u4e00\u8f6c\uff0c\u7529\u8155\u62e7\u8170\uff0c\u5929\u738b\u5378 \u7532\uff0c\u5728\u95f4\u4e0d\u5bb9\u53d1\u4e4b\u4e0b\uff0c\u903c\u5f00\u4e86\u4ec1\u4e49\u5251\u5ba2\u7531\u8eab\u540e\u51fb\u6765\u7684\u4e00\u62db\u3002 \u3000\u3000\u5bff\u7fc1\u4e91\u8c26\u7684\u53f3\u624b\u987a\u7740\u957f\u987b\u4e00\u6ed1\uff0c\u843d\u5230\u8170\u95f4\u7684\u4e1d\u5e26\u4e0a\uff0c\u5fc3\u4e2d\u867d\u4e5f\u677e\u4e86\u53e3\u6c14\uff0c\u5374\u53c8\u4e0d\u7981\u6697\u6697 \u5fc3\u60b8\uff0c\u8fd9\u6c5f\u5357\u9ed1\u7c73\u5e2e\u7684\u74e2\u628a\u5b50\u3002 \u3000\u3000\u5728\u6b66\u529f\u4e0a\u7684\u9020\u8be3\uff0c\u786e\u4e4e\u5df2\u5230\u4e86\u7089\u706b\u7eaf\u9752\u7684\u5730\u6b65\uff0c\u65e0\u602a\u5728\u8fd9\u5e76\u4e0d\u592a\u957f\u7684\u4e00\u6bb5\u65e5\u5b50\u91cc\uff0c\u58f0\u8a89 \u80fd\u96c0\u7136\u800c\u8d77\uff0c\u4eab\u6709\u5927\u540d\u3002 \u3000\u3000\u81ea\u5df1\u7684\u7231\u5b50\u4e91\u4e2d\u7a0b\uff0c\u6b66\u529f\u867d\u5df2\u5c3d\u5f97\u81ea\u5df1\u7684\u771f\u4f20\uff0c\u867d\u4ee5\u5251\u5ba2\u800c\u540d\uff0c\u638c\u4e0a\u529f\u592b\uff0c\u4e5f\u7edd\u4e0d\u5f31\uff0c \u4f46\u6b64\u523b\u7528\u6765\u5bf9\u4ed8\u8fd9\u65e0\u7fc5\u795e\u9e70\u7ba1\u4e00\u67f4\uff0c\u65e0\u8bba\u8eab\u6cd5\u3001\u529f\u529b\u4e0a\uff0c\u5e76\u672a\u80fd\u80dc\u7740\u4eba\u5bb6\u534a\u7b79\u3002 \u3000\u3000\u591a\u81c2\u795e\u5251\u4e91\u8c26\u6614\u5e74\u95ef\u8361\u6c5f\u6e56\u65f6\uff0c\u548c\u4eba\u5bb6\u8fc7\u62db\u52a8\u624b\uff0c\u4e0d\u77e5\u5df2\u6709\u591a\u5c11\u6b21\u4e86\uff0c\u6b64\u523b\u5bf9\u773c\u4e0b\u7684\u60c5 \u52bf\uff0c\u5c82\u6709\u770b\u4e0d\u51fa\u6765\u7684\u9053\u7406\uff0c\u4ed6\u5fc3\u91cc\u4e0d\u7981\u61ca\u4e27\uff0c\u81ea\u5df1\u597d\u597d\u4e00\u4e2a\u5bff\u7b75\uff0c\u7adf\u751f\u751f\u88ab\u8fd9\u7ba1\u4e00\u67f4\u6270\u4e71 \u4e86\u3002 \u3000\u3000\u9662\u4e2d\u53c8\u590d\u8083\u7136\uff0c\u6bcf\u4e2a\u4eba\u7684\u6bcf\u4e00\u53cc\u773c\u775b\uff0c\u4ff1\u7728\u4e5f\u4e0d\u7728\u5730\u968f\u7740\u8fd9\u65e0\u7fc5\u795e\u9e70\u7ba1\u4e00\u67f4\u548c\u4ec1\u4e49\u5251\u5ba2 \u4e91\u4e2d\u7a0b\u7684\u8eab\u5f62\u6253\u8f6c\u3002 \u3000\u3000\u6709\u7684\u6b66\u529f\u8f83\u5dee\uff0c\u773c\u795e\u8f83\u5f31\u7684\uff0c\u6839\u672c\u5c31\u770b\u4e0d\u6e05\u695a\u8fd9\u4e24\u4eba\u7684\u62db\u5f0f\u6765\u8def\uff0c\u4f46\u5374\u8d8a\u53d1\u5c4f\u7740\u58f0\u606f\uff0c \u5bf9\u8fd9\u4e24\u4eba\u7684\u6b66\u529f\uff0c\u5728\u6697\u4e2d\u8d5e\u7f8e\u7740\u3002 \u3000\u3000\u6709\u7684\u80fd\u770b\u5f97\u6e05\u4ed6\u4eec\u7684\u62db\u5f0f\u7684\uff0c\u66f4\u662f\u4e0d\u80af\u653e\u5f03\u89c2\u6469\u8fd9\u79cd\u9ad8\u624b\u8f83\u6280\u7684\u673a\u4f1a\uff0c\u66f4\u6709\u7684\u5fc3\u667a\u8f83\u9ad8 \u7684\uff0c\u751a\u81f3\u8fd8\u4ece\u5176\u4e2d\u5077\u5b66\u5230\u4e00\u62db\u534a\u5f0f\u3002 \u3000\u3000\u4f17\u8c6a\u51dd\u76ee\u4e4b\u4e2d\uff0c\u54ea\u77e5\u5728\u90a3\u5927\u5385\u95e8\u53e3\uff0c\u5374\u7a81\u7136\u4f20\u6765\u4e00\u58f0\u51b7\u7b11\u3002 \u3000\u3000\u8fd9\u51b7\u7b11\u7684\u58f0\u97f3\uff0c\u6781\u4e3a\u9ad8\u4eae\u523a\u8033\uff0c\u63a5\u7740\u4e00\u4e2a\u6e05\u6717\u7684\u58f0\u97f3\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u8fd9\u79cd\u6253\u6cd5\uff0c\u53c8\u6709\u4ec0 \u4e48\u610f\u601d\uff0c\u533a\u533a\u5728\u4e0b\u771f\u96be\u4e3a\u4f60\u4eec\u8fd9\u4e00\u8eab\u6b66\u529f\u662f\u4ece\u54ea\u91cc\u5b66\u6765\u7684\uff0c\u660e\u660e\u4e24\u4eba\u7684\u8eab\u6cd5\u90fd\u662f\u7a7a\u95e8\u767e\u9732\uff0c \u5374\u6ca1\u6709\u4e00\u4e2a\u4eba\u80fd\u770b\u5f97\u51fa\u6765\u3002\u201d \u3000\u3000\u8fd9\u8bdd\u58f0\u4e00\u51fa\uff0c\u7fa4\u8c6a\u4e0d\u7981\u90fd\u76f8\u987e\u5931\u8272\uff0c\u4e00\u8d77\u8f6c\u5934\u671b\u53bb\u3002\u53ea\u89c1\u5927\u5385\u95e8\u53e3\u7684\u77f3\u9636\u4e0a\uff0c\u8d1f\u624b\u4f2b\u7acb \u7740\u4e00\u4e2a\u795e\u60c5\u5028\u505a\u7684\u9ec4\u886b\u5c11\u5e74\u3002 \u3000\u3000\u8fd9\u5c11\u5e74\u957f\u8eab\u7389\u7acb\uff0c\u7ad9\u5728\u90a3\u91cc\u6bd4\u8eab\u4fa7\u7684\u4eba\u90fd\u9ad8\u7740\u534a\u4e2a\u5934\uff0c\u8702\u8170\u7a84\u80cc\uff0c\u7709\u68a2\u773c\u89d2\uff0c\u50b2\u6c14\u51cc \u4eba\uff0c\u5634\u89d2\u4ecd\u7136\u6302\u7740\u4e00\u4e1d\u51b7\u7b11\u3002 \u3000\u3000\u8fd9\u8bed\u60ca\u4f17\u8c6a\uff0c\u7adf\u662f\u4e00\u4e2a\u5728\u6b66\u6797\u4e2d\u85c9\u85c9\u65e0\u540d\u7684\u964c\u751f\u5c11\u5e74\u3002 \u3000\u3000\u4f17\u8c6a\u7684\u6570\u767e\u9053\u773c\u5149\uff0c\u90fd\u50cf\u5229\u5203\u4f3c\u7684\u77aa\u5230\u4ed6\u7684\u8138\u4e0a\uff0c\u4f46\u662f\u8fd9\u795e\u60c5\u5028\u505a\u7684\u5c11\u5e74\uff0c\u5374\u4ecd\u7136\u82e5\u65e0 \u5176\u4e8b\uff0c\u5634\u89d2\u7684\u51b7\u7b11\u75d5\u8ff9\uff0c\u53c8\u590d\u663e\u9732\u4e86\u51fa\u6765\u3002 \u3000\u3000\u4ed6\u7684\u8bdd\u58f0\u5b57\u5b57\u6e05\u6717\uff0c\u6b63\u5728\u52a8\u7740\u624b\u7684\u65e0\u7fc5\u795e\u9e70\u7ba1\u4e00\u67f4\u548c\u4ec1\u4e49\u5251\u5ba2\u4e91\u4e2d\u7a0b\uff0c\u867d\u7136\u5fc3\u65e0\u522b\u9a9b\uff0c \u5374\u4e5f\u4e00\u5b57\u4e0d\u6f0f\u5730\u542c\u5230\u4e86\u3002 \u3000\u3000\u4ee5\u8fd9\u4e24\u4eba\u5728\u6b66\u6797\u4e2d\u7684\u8eab\u4efd\u5730\u4f4d\uff0c\u4e0d\u7ba1\u8fd9\u8bdd\u662f\u8c01\u8bf4\u7684\uff0c\u90fd\u662f\u4ef6\u4e0d\u80fd\u5fcd\u53d7\u7684\u4e8b\uff0c\u8fd9\u4e24\u4eba\u64a4\u56de \u62db\u5f0f\uff0c\u8eab\u5f62\u540e\u7eb5\uff0c\u7adf\u4e00\u8d77\u4f4f\u4e0b\u4e86\u624b\u3002 \u3000\u3000\u6ee1\u9662\u4e2d\u7684\u8c6a\u58eb\uff0c\u6b64\u523b\u6ca1\u6709\u4e00\u4eba\u4e0d\u662f\u6115\u7136\u5931\u8272\u7684\uff0c\u6709\u7684\u5fc3\u4e2d\u731c\u6d4b\u8fd9\u9ec4\u886b\u5c11\u5e74\u7684\u6765\u8def\uff0c\u6709\u7684 \u5374\u5728\u5fc3\u4e2d\u6697\u9a82\uff0c\u4ee5\u4e3a\u8bf4\u51fa\u8fd9\u8bdd\u7684\u4eba\uff0c\u4e00\u5b9a\u662f\u4e2a\u75af\u5b50\uff0c\u5c31\u51ed\u7ba1\u4e00\u67f4\u3001\u4e91\u4e2d\u7a0b\u7684\u6b66\u529f\uff0c\u666e\u5929\u4e4b \u4e0b\uff0c\u53c8\u6709\u51e0\u4eba\u80fd\u8bf4\u51fa\u8fd9\u79cd\u8bdd\u6765\uff0c\u8fd9\u5c11\u5e74\u4e0d\u662f\u75af\u5b50\u662f\u4ec0\u4e48\uff1f \u3000\u3000\u65e0\u7fc5\u795e\u9e70\u7ba1\u4e00\u67f4\uff0c\u548c\u90a3\u4f4d\u4ec1\u4e49\u5251\u5ba2\u4e91\u4e2d\u7a0b\u6b64\u523b\u7684\u8138\u8272\uff0c\u81ea\u7136\u66f4\u662f\u96be\u770b\uff0c\u56db\u9053\u76ee\u5149\uff0c\u81ea\u7136 \u5145\u6ee1\u7740\u68ee\u51b7\u4e4b\u610f\u77aa\u7740\u4ed6\u3002 \u3000\u3000\u53ea\u6709\u5bff\u7fc1\u4e91\u8c26\u5fc3\u4e2d\u5374\u662f\u53e6\u4e00\u79cd\u60f3\u6cd5\uff0c\u8fd9\u5c11\u5e74\u7eb5\u7136\u975e\u75c5\u5373\u72c2\uff0c\u4f46\u4ed6\u8fd9\u51e0\u53e5\u72c2\u8bed\uff0c\u5374\u4f7f\u5f97\u81ea \u5df1\u7684\u5fc3\u4e8b\uff0c\u653e\u4e0b\u4e00\u534a\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u6b64\u523b\u770b\u51fa\uff0c\u81ea\u5df1\u7684\u7231\u5b50\u8eab\u624b\u4e4b\u95f4\uff0c\u5df2\u4e0d\u5982\u5148\u524d\u7684\u77eb\u5065\uff0c\u518d\u4e00\u4e2a\u5931\u624b\uff0c\u8bb8\u591a\u5e74\u6323\u624e \u5f97\u6765\u7684\u58f0\u540d\uff0c\u5c82\u975e\u8981\u6bc1\u4e8e\u4e00\u65e6\u3002 \u3000\u3000\u5728\u8fd9\u4e00\u5239\u90a3\u95f4\uff0c\u9662\u4e2d\u7adf\u7136\u53c8\u590d\u8083\u7136\uff0c\u987b\u77e5\u8fd9\u9ec4\u886b\u5c11\u5e74\u8bf4\u7684\u8bdd\uff0c\u7684\u786e\u592a\u8fc7\u60ca\u4eba\uff0c\u7fa4\u8c6a\u76f8\u987e \u5931\u8272\u4e4b\u4e0b\uff0c\u7adf\u90fd\u6123\u4f4f\u4e86\u3002 \u3000\u3000\u7ba1\u4e00\u67f4\u3001\u4e91\u4e2d\u7a0b\u4e24\u4eba\uff0c\u5fc3\u4e2d\u5374\u662f\u5927\u6012\uff0c\u4f46\u4ee5\u4ed6\u4eec\u4e4b\u8eab\u4efd\uff0c\u81ea\u4e5f\u4e0d\u4f1a\u7834\u53e3\u6f2b\u9a82\u3002 \u3000\u3000\u8083\u7136\u4e4b\u4e0b\uff0c\u4f46\u89c1\u8fd9\u9ec4\u886b\u5c11\u5e74\u4e00\u6325\u8863\u8896\uff0c\u7f13\u6b65\u8d70\u4e0b\u9636\u6765\u3002 \u3000\u3000\u65e0\u7fc5\u795e\u9e70\u7ba1\u4e00\u67f4\u7a81\u7136\u563f\u563f\u51b7\u7b11\u4e00\u58f0\uff0c\u6c89\u58f0\u8bf4\u9053\uff1a\u201c\u65b9\u624d\u7684\u9ad8\u8bba\uff0c\u60f3\u5fc5\u5c31\u662f\u8fd9\u4f4d\u670b\u53cb\u8bf4\u51fa \u6765\u7684\u4e86\uff0c\u6211\u7ba1\u4e00\u67f4\u786e\u5b9e\u94a6\u4f69\u5f97\u5f88\uff0c\u6211\u7ba1\u4e00\u67f4\u6280\u827a\u4e0d\u7cbe\uff0c\u81ea\u77e5\u6b66\u529f\u592a\u5dee\uff0c\u4eca\u65e5\u80fd\u9047\u89c1\u670b\u53cb\uff0c\u5b9e \u662f\u9ad8\u5174\u6781\u4e86\uff0c\u8fd8\u671b\u670b\u53cb\u4e0d\u541d\u8d50\u6559\uff0c\u5c06\u5728\u4e0b\u62db\u5f0f\u7684\u7a7a\u95e8\u4e00\u4e00\u8d50\u544a\u5728\u4e0b\uff0c\u8ba9\u5728\u4e0b\u4e5f\u597d\u5b66\u5b66\u9ad8 \u62db\u3002\u201d \u3000\u3000\u90a3\u9ec4\u6749\u5c11\u5e74\u6717\u58f0\u5927\u7b11\u4e86\u8d77\u6765\uff0c\u8fde\u8fde\u9053\uff1a\u201c\u597d\uff0c\u597d\uff0c\u9601\u4e0b\u7684\u786e\u865a\u5fc3\u5f97\u5f88\uff0c\u4e0d\u8fc7\u4f60\u90a3\u8d9f\u638c \u6cd5\uff0c\u867d\u7136\u770b\u4f3c\u82b1\u5999\uff0c\u5374\u5b9e\u5728\u7a7a\u95e8\u5927\u591a\uff0c\u53eb\u6211\u4e00\u65f6\u4e4b\u95f4\uff0c\u53c8\u600e\u80fd\u8bf4\u5f97\u5b8c\u5462\uff1f\u201d \u3000\u3000\u4ed6\u8f6c\u5934\u53c8\u5411\u4e91\u4e2d\u7a0b\u7b11\u9053\uff1a\u201c\u4f60\u7684\u638c\u6cd5\uff0c\u548c\u4ed6\u4e5f\u662f\u534a\u65a4\u516b\u4e24\uff0c\u8981\u4e0d\u597d\u597d\u53bb\u7ec3\u7ec3\uff0c\u53ea\u6015\u5c06\u6765 \u9047\u7740\u9ad8\u624b\uff0c\u8fde\u4eba\u5bb6\u7684\u4e09\u62db\u90fd\u6321\u4e0d\u4e86\uff0c\u90a3\u5c82\u975e\u96be\u770b\u3002\u201d \u3000\u3000\u8fd9\u9ec4\u886b\u5c11\u5e74\uff0c\u7adf\u8001\u6c14\u6a2a\u79cb\u4ed6\u8bf4\u51fa\u8fd9\u79cd\u8bdd\u6765\uff0c\u7ba1\u4e00\u67f4\u3001\u4e91\u4e2d\u7a0b\uff0c\u4ff1\u90fd\u9762\u76ee\u53d8\u8272\uff0c\u53cc\u7709\u5012 \u7acb\u3002 \u3000\u3000\u4e91\u4e2d\u7a0b\u5251\u7709\u7ad6\u5904\uff0c\u51b7\u7b11\u4e00\u58f0\uff0c\u65b9\u5f85\u8bf4\u8bdd\uff0c\u54ea\u77e5\u5374\u542c\u4ed6\u7236\u4eb2\u7a81\u7136\u5e72\u54b3\u4e00\u58f0\uff0c\u50cf\u662f\u963b\u6b62\u81ea \u5df1\uff0c\u4fbf\u53c8\u5c06\u53e3\u4e2d\u7684\u8bdd\u5fcd\u4e0b\u53bb\u4e86\u3002 \u3000\u3000\u4f46\u662f\u8fd9\u6c5f\u5357\u9ed1\u7c73\u5e2e\u7684\u9b41\u9996\uff0c\u9a84\u6a2a\u8dcb\u6248\uff0c\u5374\u4e07\u4e07\u5fcd\u4e0d\u4e0b\u8fd9\u53e3\u6c14\u3002 \u3000\u3000\u4ed6\u5df2\u81ea\u51b7\u7b11\u4e00\u58f0\uff0c\u53f1\u9053\uff1a\u201c\u597d\uff0c\u670b\u53cb\u8bf4\u7684\u8bdd\uff0c\u60f3\u5fc5\u670b\u53cb\u4e5f\u7b97\u662f\u5546\u4eba\u4e86\uff0c\u90a3\u4e48\u5c31\u8bf7\u670b\u53cb\u8ba9 \u5929\u4e0b\u6b66\u6797\u82f1\u96c4\u770b\u770b\uff0c\u6211\u7ba1\u4e00\u67f4\u7684\u6b66\u529f\u5982\u4f55\u4e0d\u6d4e\u4e8b\uff0c\u8fde\u4eba\u5bb6\u4e09\u62db\u90fd\u6321\u4e0d\u8fc7\u3002\u201d \u3000\u3000\u4ed6\u628a\u624b\u4e00\u7ffb\uff0c\u5c06\u53f3\u624b\u7684\u8896\u5b50\u53c8\u633d\u4e86\u633d\uff0c\u8fd9\u65e0\u7fc5\u795e\u9e70\u663e\u7136\u5df2\u52a8\u4e86\u771f\u6012\uff0c\u7acb\u523b\u5c31\u8981\u51fa\u624b\u4e86\u3002 \u3000\u3000\u56f4\u89c2\u7740\u7684\u7fa4\u8c6a\uff0c\u867d\u7136\u90fd\u5bf9\u8fd9\u9ec4\u886b\u5c11\u5e74\u8bf4\u7684\u8bdd\u4e0d\u6ee1\uff0c\u4f46\u6b64\u523b\u5374\u53c8\u4e0d\u7981\u5728\u6697\u6697\u4e3a\u4ed6\u62c5\u5fc3\uff0c\u8fd9 \u65e0\u7fc5\u795e\u9e70\u4e00\u51fa\u624b\uff0c\u53ea\u6015\u8fd9\u5c11\u5e74\u4fbf\u5f97\u4e27\u547d\uff0c\u56e0\u4e3a\u6b64\u523b\u8fd9\u7ba1\u795e\u9e70\u7684\u51fa\u624b\uff0c\u662f\u7edd\u4e0d\u4f1a\u7559\u60c5\u7684\u4e86\u3002 \u3000\u3000\u4f46\u662f\u8fd9\u9ec4\u886b\u5c11\u5e74\uff0c\u5374\u53c8\u81ea\u6717\u58f0\u5927\u7b11\u4e86\u8d77\u6765\uff0c\u4e00\u9762\u6717\u58f0\u8bf4\u9053\uff0c\u201c\u533a\u533a\u5728\u4e0b\u867d\u7b97\u4e0d\u5f97\u9ad8\u4eba\uff0c \u4f46\u82e5\u8981\u5bf9\u4ed8\u9601\u4e0b\u8fd9\u79cd\u8eab\u624b\uff0c\u53ea\u6015\u6709\u4e2a\u4e09\u4e94\u62db\u4e5f\u8db3\u4ee5\u591f\u4e86\uff0c\u9601\u4e0b\u82e5\u4e0d\u76f8\u4fe1\uff0c\u4e0d\u59a8\u8bd5\u8bd5\u770b\uff0c\u53ea\u662f \u4ee5\u533a\u533a\u4e4b\u610f\uff0c\u9601\u4e0b\u6700\u597d\u8fd8\u662f\u7b97\u4e86\u5427\uff0c\u5f53\u7740\u8fd9\u4e48\u591a\u4eba\u9762\u524d\u73b0\u773c\uff0c\u5374\u53c8\u662f\u4f55\u82e6\u5462\uff1f\u201d \u3000\u3000\u8bf4\u7f62\uff0c\u53c8\u81ea\u626c\u58f0\u5927\u7b11\u4e86\u8d77\u6765\u3002 \u3000\u3000\u8fd9\u4e9b\u7fa4\u8c6a\u867d\u7136\u60ca\u8be7\uff0c\u4f46\u6709\u4e9b\u7ecf\u9a8c\u8001\u5230\u7684\u8001\u6c5f\u6e56\uff0c\u50cf\u6a2a\u6c5f\u91d1\u7d20\u695a\u5360\u9f99\u3001\u7075\u72d0\u667a\u4e66\u3001\u591a\u81c2\u795e \u5251\u4e91\u8c26\u7b49\u4eba\uff0c\u5374\u90fd\u5df2\u770b\u51fa\u8fd9\u9ec4\u886b\u5c11\u5e74\u867d\u7136\u72c2\u9a84\u65e0\u6bd4\uff0c\u4f46\u4ed6\u65e2\u6562\u5982\u6b64\uff0c\u5c31\u7edd\u975e\u6ca1\u6709\u6765\u5386\u7684\u3002 \u3000\u3000\u662f\u4ee5\u4e91\u8c26\u65b9\u624d\u6697\u6697\u963b\u6b62\u4f4f\u81ea\u5df1\u7231\u5b50\u7684\u76db\u6012\uff0c\u53cd\u6b63\u4ed6\u77e5\u9053\u7ba1\u4e00\u67f4\u7edd\u4e0d\u4f1a\u653e\u8fc7\u8fd9\u5c11\u5e74\uff0c\u53ea\u8981 \u8fd9\u5c11\u5e74\u548c\u7ba1\u4e00\u67f4\u4e00\u52a8\u4e0a\u4e86\u624b\uff0c\u90a3\u4e48\u4ee5\u81ea\u5df1\u7684\u773c\u5149\u3001\u7ecf\u9a8c\uff0c\u8fd9\u5c11\u5e74\u7684\u6765\u5386\u81ea\u5df1\u662f\u7edd\u4e0d\u4f1a\u770b\u4e0d\u51fa \u6765\u3002 \u3000\u3000\u679c\u7136\uff0c\u8fd9\u7ba1\u795e\u9e70\u76db\u6012\u4e4b\u4e0b\uff0c\u5df2\u81ea\u53f1\u9053\uff1a\u201c\u627f\u670b\u53cb\u7684\u597d\u610f\uff0c\u4f46\u6211\u59d3\u7ba1\u7684\u5929\u751f\u7684\u662f\u8fd9\u79cd\u813e \u6c14\uff0c\u4e0d\u5230\u9ec4\u6cb3\u5fc3\u4e0d\u6b7b\uff0c\u670b\u53cb\uff0c\u4f60\u82e5\u4e0d\u8ba9\u6211\u89c1\u8bc6\u89c1\u8bc6\u4f60\u7684\u8eab\u624b\u662f\u600e\u4e48\u4e2a\u9ad8\u6cd5\uff0c\u5c31\u5728\u8fd9\u91cc\u80e1\u5439\u4e71 \u5420\uff0c\u90a3\u6211\u59d3\u7ba1\u7684\u53ef\u8981\u5bf9\u670b\u53cb\u4f60\u4e0d\u5ba2\u6c14\u4e86\u3002\u201d \u3000\u3000\u8fd9\u9ec4\u886b\u5c11\u5e74\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4e0d\u5230\u9ec4\u6cb3\u5fc3\u4e0d\u6b7b\u2026\u2026\u597d\uff0c\u597d\uff0c\u9601\u4e0b\u65e2\u7136\u6267\u610f\u5982\u6b64\u2014\u2014\u201d\u4ed6\u8bdd\u58f0 \u7f13\u7f13\u4e00\u987f\uff0c\u7b11\u58f0\u500f\u7136\u800c\u4f4f\uff0c\u76ee\u5149\u53d8\u5f97\u68ee\u51b7\u800c\u5bd2\u5389\uff0c\u51b7\u51b7\u53c8\u9053\uff1a\u201c\u90a3\u5374\u602a\u4e0d\u5f97\u5728\u4e0b\u4e86\uff01\u201d \u3000\u3000\u4ed6\u5bd2\u51b7\u7684\u76ee\u5149\u56db\u8f6c\uff1a\u201c\u54ea\u4f4d\u670b\u53cb\u51fa\u6765\u505a\u4e2a\u89c1\u8bc1\uff0c\u533a\u533a\u5728\u4e0b\u82e5\u4e0d\u80fd\u5728\u4e09\u62db\u4e2d\uff0c\u8ba9\u8fd9\u4f4d\u670b\u53cb \u843d\u8d25\uff0c\u90a3\u4e48\u5728\u4e0b\u5c31\u4ece\u8fd9\u9662\u5b50\u91cc\uff0c\u4e00\u76f4\u722c\u5c06\u51fa\u53bb\uff0c\u4f46\u82e5\u662f\u2014\u2014\u201d\u4ed6\u8bed\u58f0\u4e00\u987f\uff0c\u76ee\u5149\u53c8\u590d\u843d\u5728\u7ba1 \u795e\u9e70\u8eab\u4e0a\uff0c\u68ee\u51b7\u7684\u63a5\u7740\u53c8\u9053\uff1a\u201c\u4f46\u82e5\u662f\u670b\u53cb\u5728\u4e09\u62db\u4e4b\u5185\u4e00\u201d\u7ba1\u795e\u9e70\u77a0\u76ee\u5927\u559d\u4e00\u58f0\uff0c\u622a\u65ad\u4e86\u8fd9 \u9ec4\u886b\u5c11\u5e74\u7684\u8bdd\uff0c\u5389\u58f0\u9053\uff1a\u201c\u90a3\u6211\u5c31\u968f\u4fbf\u4f60\u5904\u7f6e\u597d\u4e86\u3002\u201d \u3000\u3000\u7565\u6574\u4e0a\u8eab\uff0c\u62d7\u6b65\u8fdb\u8eab\uff1a\u201c\u670b\u53cb\uff0c\u4f60\u5c31\u63a5\u62db\u5427\u3002\u201d \u3000\u3000\u8eab\u5f62\u500f\u7136\u4e00\u8f6c\uff0c\u8f6c\u5230\u8fd9\u9ec4\u886b\u5c11\u5e74\u7684\u5de6\u4fa7\uff0c\u53f3\u638c\u6a2a\u5207\u5c11\u5e74\u7684\u80a9\u5934\uff0c\u5de6\u638c\u5374\u4ece\u53f3\u65f6\u4e0b\u7a7f\u51fa\uff0c \u4ee5\u98df\u4e2d\u4e24\u6307\uff0c\u731b\u70b9\u4ed6\u808b\u4e0b\u7684\u8840\u6d77\u7a74\uff0c\u638c\u5fc3\u5185\u9677\uff0c\u5374\u53c8\u6ee1\u84c4\u5c0f\u5929\u661f\u7684\u638c\u529b\u3002 \u3000\u3000\u8fd9\u65e0\u7fc5\u795e\u9e70\u867d\u662f\u9a84\u72c2\u8dcb\u6248\uff0c\u4f46\u4e00\u52a8\u4e0a\u624b\uff0c\u5374\u53ef\u4ee5\u770b\u51fa\u4ed6\u5e76\u6ca1\u6709\u534a\u70b9\u8f7b\u654c\u4e4b\u6001\uff0c\u7528\u7684\u4e5f\u7edd \u4e0d\u662f\u90a3\u79cd\u8e0f\u6d2a\u95e8\u3001\u8d70\u4e2d\u5bab\u4e00\u5934\u4ee5\u5f3a\u51fb\u5f31\u7684\u8eab\u6cd5\uff0c\u4ed6\u7adf\u907f\u91cd\u5c31\u8f7b\uff0c\u5148\u7ed5\u5230\u8fd9\u5c11\u5e74\u7684\u8eab\u5de6\uff0c\u51fa\u62db \u4e4b\u95f4\uff0c\u867d\u653b\u5b9e\u5b88\uff0c\u65e9\u5c31\u5148\u628a\u81ea\u5df1\u7684\u9000\u8def\u7559\u597d\u4e86\u3002 \u3000\u3000\u8fd9\u7ba1\u795e\u9e70\u6b64\u523b\u51fa\u62db\u4e4b\u95f4\uff0c\u7adf\u663e\u51fa\u6765\u6bd4\u5148\u524d\u548c\u4e91\u4e2d\u7a0b\u52a8\u624b\u65f6\u66f4\u5c0f\u5fc3\u3002 \u3000\u3000\u4ed6\u8fd9\u4e00\u62db\u4e24\u5f0f\uff0c\u5feb\u5982\u7535\u706b\uff0c\u90a3\u9ec4\u886b\u5c11\u5e74\u957f\u7b11\u58f0\u4e2d\uff0c\u8eab\u5f62\u7565\u5c55\u3002 \u3000\u3000\u7ba1\u795e\u9e70\u638c\u65b9\u9012\u51fa\uff0c\u5ffd\u7136\u89c9\u5f97\u773c\u524d\u7a7a\u7a7a\uff0c\u5c31\u5728\u8fd9\u4e00\u5239\u90a3\uff0c\u8fd9\u9ec4\u886b\u5c11\u5e74\u7adf\u7136\u5f62\u5982\u9b3c\u9b45\uff0c\u8eab\u5f62 \u5c55\u52a8\u95f4\uff0c\u5df2\u4e0d\u77e5\u8dd1\u5230\u54ea\u91cc\u53bb\u4e86\u3002 \u3000\u3000\u4ed6\u5927\u60ca\u4e4b\u4e0b\uff0c\u5df2\u542c\u5230\u81ea\u5df1\u8eab\u540e\u66b4\u559d\u4e00\u58f0\uff1a\u201c\u7b2c\u4e00\u62db\u3002\u201d\u7ba1\u4e00\u67f4\u5fc3\u9b44\u7686\u5931\uff0c\u987e\u4e0d\u5f97\u8f6c\u8eab\u56de \u987e\uff0c\u731b\u7136\u5411\u524d\u4e00\u683d\uff0c\u5c31\u5730\u8fde\u7ffb\u51e0\u4e2a\u7b4b\u6597\uff0c\u8fd9\u6c5f\u5357\u5927\u8c6a\uff0c\u9ed1\u7c73\u5e2e\u9996\uff0c\u6b64\u523b\u7adf\u4f7f\u51fa\u201c\u61d2\u9a74\u6253\u6eda\u201d \u8fd9\u79cd\u89c1\u4e0d\u5f97\u4eba\u7684\u62db\u5f0f\u6765\uff0c\u7b80\u76f4\u662f\u65e0\u8d56\u4eec\u7684\u8eab\u6cd5\u4e86\u3002 \u3000\u3000\u7fa4\u8c6a\u5927\u54d7\uff0c\u8fd9\u4e9b\u95ef\u8361\u6b66\u6797\u591a\u5e74\u7684\u8c6a\u58eb\uff0c\u6240\u9047\u4e4b\u4e8b\uff0c\u5374\u4ece\u672a\u6709\u4e00\u4ef6\u66f4\u5947\u4e8e\u6b64\u4e8b\u7684\u3002\u4e00\u4e2a\u540d \u4e0d\u89c1\u7ecf\u4f20\u7684\u5c11\u5e74\u7adf\u5728\u4e00\u62db\u4e4b\u4e0b\uff0c\u4f7f\u5f97\u6b66\u6797\u4fa7\u76ee\u7684\u9ed1\u7c73\u5e2e\u603b\u74e2\u628a\u5b50\u7ba1\u795e\u9e70\uff0c\u867d\u672a\u843d\u8d25\uff0c\u5374\u5df2\u4e22 \u4e86\u5927\u8138\u4e86\u3002 \u3000\u3000\u7fa4\u8c6a\u54d7\u7136\u58f0\u4e2d\uff0c\u7ba1\u795e\u9e70\u7ad9\u8d77\u8eab\u5f62\uff0c\u53ea\u89c1\u90a3\u9ec4\u886b\u5c11\u5e74\uff0c\u6b63\u7ad9\u5728\u81ea\u5df1\u8eab\u524d\uff0c\u5e26\u7740\u6ee1\u8138\u4e0d\u5c51\u7684 \u5fae\u7b11\uff0c\u671b\u7740\u81ea\u5df1\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u8fd8\u80b2\u4e24\u62db\u3002\u201d \u3000\u3000\u6b64\u523b\u8fd9\u65e0\u7fc5\u795e\u9e70\u5fc3\u4e2d\uff0c\u6b63\u662f\u7f9e\u6127\u4e24\u5ff5\uff0c\u5982\u6f6e\u7ffb\u6d8c\uff0c\u884c\u5bb6\u4e00\u4f38\u624b\u4e0d\u7528\u591a\u770b\uff0c\u5c31\u53ef\u4ee5\u5206\u8fa8\u51fa \u8eab\u624b\u7684\u5f3a\u5f31\u6765\u3002 \u3000\u3000\u8fd9\u7ba1\u795e\u9e70\u5e76\u975e\u4e0d\u662f\u660e\u773c\u4eba\uff0c\u4eba\u5bb6\u8fd9\u79cd\u8eab\u6cd5\uff0c\u81ea\u5df1\u4e0d\u4f46\u89c1\u6240\u672a\u89c1\uff0c\u5c31\u8fde\u542c\u8bf4\u90fd\u6ca1\u6709\u542c\u8bf4 \u8fc7\uff0c\u81ea\u5df1\u4e00\u5411\u9887\u4e3a\u81ea\u50b2\u4e8e\u81ea\u5df1\u7684\u8eab\u624b\uff0c\u4f46\u6b64\u523b\u4e00\u62db\u4e4b\u4e0b\uff0c\u8fde\u4eba\u5bb6\u7684\u8eab\u6cd5\u90e8\u6ca1\u6709\u770b\u6e05\u695a\uff0c\u5c31\u843d \u4e0b\u4e86\u8d25\u8ff9\u3002 \u3000\u3000\u90a3\u591a\u81c2\u795e\u5251\u6b64\u523b\u4ea6\u662f\u9762\u8272\u5927\u53d8\uff0c\u56e0\u4e3a\u4ed6\u5df2\u4ece\u8fd9\u9ec4\u886b\u5c11\u5e74\u7684\u8eab\u4e0a\uff0c\u60f3\u8d77\u4e00\u4e2a\u4eba\u6765\uff0c\u4ed6\u786e\u4fe1 \u81ea\u5df1\u8001\u773c\u65e0\u82b1\uff0c\u81ea\u5df1\u770b\u51fa\u7684\u4e8b\uff0c\u662f\u7edd\u5bf9\u9519\u4e0d\u4e86\u7684\u3002 \u3000\u3000\u90a3\u9ec4\u886b\u5c11\u5e74\u7f13\u7f13\u6602\u8d77\u5934\u6765\uff0c\u76ee\u5149\u4ece\u90a3\u7ba1\u4e00\u67f4\u8eab\u4e0a\uff0c\u8f6c\u671b\u82cd\u7a79\uff0c\u5634\u89d2\u7684\u7b11\u5bb9\uff0c\u6269\u6563\u5f97\u8d8a\u53d1 \u5f00\u6717\u4e86\u3002\u7136\u540e\uff0c\u4ed6\u4f4e\u4e0b\u5934\uff0c\u6717\u58f0\u53c8\u9053\uff1a\u201c\u8fd8\u6709\u4e24\u62db\u3002\u201d \u3000\u3000\u8fd9\u56db\u4e2a\u5b57\uff0c\u50cf\u7bad\u4e5f\u4f3c\u7684\uff0c\u5c04\u8fdb\u90a3\u6c5f\u5357\u9ed1\u7c73\u5e2e\u9b41\u9996\u7ba1\u795e\u9e70\u7684\u5fc3\uff0c\u4ed6\u611f\u89c9\u5f97\u5230\uff0c\u6ee1\u9662\u7fa4\u8c6a\uff0c \u4f3c\u4e4e\u4e5f\u90fd\u5e26\u7740\u4e00\u79cd\u51b7\u524a\u7684\u76ee\u5149\u5728\u671b\u7740\u81ea\u5df1\uff0c\u4ed6\u82e5\u50cf\u4e8c\u5341\u5e74\u524d\u90a3\u4e48\u5e74\u8f7b\uff0c\u4ed6\u4e00\u5b9a\u4f1a\u52bf\u82e5\u75af\u864e\u822c \u6251\u4e0a\u53bb\u3002 \u3000\u3000\u53ea\u662f\uff0c\u4ed6\u6b64\u65f6\u7684\u5e74\u9f84\u5df2\u7ecf\u591f\u5927\u4e86\uff0c\u4eba\u751f\u7684\u4f53\u9a8c\uff0c\u4e5f\u4f7f\u4ed6\u53d8\u5f97\u8db3\u591f\u7684\u4e16\u6545\uff0c\u4ed6\u6b63\u662f\u6240\u8c13\u4e00 \u70b9\u5c31\u900f\u7684\u8001\u6c5f\u6e56\uff0c\u6df1\u77e5\u81ea\u5df1\u90a3\u4e00\u8eab\u4ed7\u4ee5\u79f0\u96c4\u6b66\u6797\u7684\u6b66\u529f\uff0c\u5728\u8fd9\u5c11\u5e74\u7684\u8be1\u5f02\u8eab\u6cd5\u9762\u524d\uff0c\u6709\u5982\u7693 \u6708\u5f53\u7a7a\u4e0b\u7684\u8424\u706b\u4e4b\u5149\uff0c\u81ea\u5df1\u7eb5\u7136\u518d\u8fd8\u80fd\u51fa\u624b\uff0c\u4e5f\u662f\u843d\u5f97\u81ea\u53d6\u5176\u8fb1\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u957f\u53f9\u4e00\u58f0\uff0c\u76ee\u5149\u5446\u6ede\u5730\u671b\u7740\u8fd9\u9ec4\u886b\u5c11\u5e74\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u6211\u7ba1\u4e00\u67f4\u6709\u773c\u65e0\u73e0\uff0c\u770b\u4e0d\u51fa \u670b\u53cb\u662f\u4f4d\u9ad8\u4eba\uff0c\u4f46\u6211\u7ba1\u4e00\u67f4\u8fd8\u4e0d\u662f\u778e\u5b50\uff0c\u6b64\u523b\u5df2\u4f4e\u5934\u8ba4\u683d\uff0c\u670b\u53cb\u7684\u4e0b\u4f59\u4e24\u62db\uff0c\u4e5f\u4e0d\u5fc5\u65bd\u5c55\u51fa \u6765\u4e86\u3002\u201d \u3000\u3000\u7fa4\u8c6a\u53c8\u54d7\u7136\u53d1\u51fa\u4e00\u9635\u54cd\u52a8\uff0c\u591a\u81c2\u795e\u5251\u4e91\u8c26\u7684\u4e24\u9053\u6d53\u7709\uff0c\u76b1\u5f97\u66f4\u7d27\uff0c\u7a81\u7136\u9644\u8033\u5411\u6a2a\u6c5f\u91d1\u7d20 \u695a\u5360\u9f99\u4f4e\u4f4e\u8bf4\u4e86\u4e24\u53e5\u8bdd\uff0c\u90a3\u6c34\u8def\u5927\u8c6a\u7684\u4e24\u9053\u76ee\u5149\uff0c\u7acb\u523b\u4e5f\u5728\u8fd9\u9ec4\u886b\u5c11\u5e74\u4e0a\u4e0b\u4e00\u626b\u3002 \u3000\u3000\u53ea\u89c1\u9ec4\u886b\u5c11\u5e74\u4e24\u773c\u4e0a\u7ffb\uff0c\u53ea\u5fae\u5fae\u201c\u54e6\u201d\u4e86\u4e00\u58f0\uff0c\u5bf9\u8fd9\u65e0\u7fc5\u795e\u9e70\u7ba1\u4e00\u67f4\u7684\u8fd9\u79cd\u8ba4\u683d\u7684\u8bdd\uff0c \u6ca1\u6709\u4e1d\u6beb\u53cd\u5e94\u3002 \u3000\u3000\u7ba1\u795e\u9e70\u5e72\u54b3\u4e86\u4e00\u58f0\uff0c\u9053\uff0c\u201c\u6211\u7ba1\u4e00\u67f4\u81ea\u77e5\u5b66\u827a\u4e0d\u7cbe\uff0c\u53ef\u4e5f\u4e0d\u662f\u4e2a\u5eb8\u624d\uff0c\u50cf\u670b\u53cb\u8fd9\u79cd\u8eab \u624b\uff0c\u5728\u4e0b\u6562\u8bf4\u7684\u786e\u51fa\u7c7b\u62e8\u9769\uff0c\u4e0d\u77e5\u9053\u9601\u4e0b\u80fd\u4e0d\u80fd\u5c06\u5927\u540d\u89c1\u8d50\uff0c\u8ba9\u5929\u4e0b\u6b66\u6797\u5bbe\u670b\uff0c\u4e5f\u597d\u77e5\u9053\u5f53 \u4eca\u6b66\u6797\u4e2d\uff0c\u53c8\u51fa\u73b0\u4e00\u9897\u5f02\u661f\u3002\u201d \u3000\u3000\u8fd9\u7ba1\u4e00\u67f4\u80fd\u6210\u4e3a\u4e00\u5e2e\u4e4b\u4e3b\uff0c\u679c\u7136\u9664\u4e86\u7a0d\u5fae\u9a84\u72c2\u8dcb\u6248\u4e9b\u5916\uff0c\u57ce\u5e9c\u5374\u662f\u6781\u6df1\uff0c\u6b64\u523b\u4ed6\u5fc3\u5ff5\u8f6c \u5904\uff0c\u7a81\u7136\u5bf9\u8fd9\u9ec4\u886b\u5c11\u5e74\u606d\u7ef4\u8d77\u6765\u3002 \u3000\u3000\u4ed6\u5982\u6b64\u4e00\u8bf4\uff0c\u7fa4\u8c6a\u4e5f\u4e0d\u7981\u90e8\u7ad6\u8d77\u6765\u8033\u6735\uff0c\u60f3\u542c\u542c\u8fd9\u6b66\u529f\u8be1\u5f02\u9ad8\u7edd\u7684\u5c11\u5e74\u7684\u5927\u540d\uff0c\u8fd9\u4e9b\u8349 \u83bd\u8c6a\u5ba2\uff0c\u90fd\u662f\u76f4\u80a0\u6c49\u5b50\uff0c\u5148\u524d\u867d\u7136\u4e0d\u6ee1\u4e8e\u5c11\u5e74\u7684\u72c2\u505a\uff0c\u4f46\u6b64\u523b\u4e3a\u5176\u6b66\u529f\u6240\u6151\uff0c\u5374\u4e0d\u7981\u5bf9\u4ed6\u6709 \u4e9b\u503e\u5012\u4e86\u3002 \u3000\u3000\u8fd9\u9ec4\u886b\u5c11\u5e74\u5ffd\u7136\u6717\u58f0\u5927\u7b11\u4e86\u8d77\u6765\uff0c\u957f\u7b11\u58f0\u4e2d\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u7ba1\u670b\u53cb\u4e0d\u4ee5\u80dc\u8d1f\u4e3a\u5ff5\uff0c\u7684\u786e \u662f\u80f8\u6000\u78ca\u843d\u7684\u597d\u6c49\uff0c\u5728\u4e0b\u65b9\u624d\u591a\u6709\u5f97\u7f6a\u4e86\uff01\u201d \u3000\u3000\u7ba1\u4e00\u67f4\u76ee\u5149\u4e00\u8f6c\uff0c\u5df2\u77e5\u9053\u8fd9\u9ec4\u886b\u5c11\u5e74\uff0c\u867d\u7136\u6b66\u529f\u7edd\u9ad8\uff0c\u5374\u662f\u521d\u51fa\u8305\u5e90\uff0c\u662f\u4e2a\u559c\u6b22\u4eba\u6367\u7684 \u89d2\u8272\uff0c\u4ed6\u77e5\u9053\u81ea\u5df1\u8fd9\u4e00\u7740\u68cb\uff0c\u65e0\u7591\u662f\u4e0b\u5bf9\u4e86\u3002 \u3000\u3000\u5374\u542c\u4ed6\u8bed\u58f0\u5fae\u5fae\u4e00\u987f\u4e4b\u540e\uff0c\u660e\u4eae\u7684\u76ee\u5149\u626b\u89c6\u7fa4\u8c6a\uff0c\u63a5\u7740\u53c8\u9053\uff1a\u201c\u5728\u4e0b\u5c91\u7cb2\uff0c\u521d\u51fa\u6c5f\u6e56\uff0c \u6765\u65e5\u8fd8\u8981\u8bf7\u7ba1\u670b\u53cb\u591a\u591a\u7167\u987e\uff0c\u5f02\u661f\u8fd9\u4e24\u5b57\uff0c\u5374\u662f\u5728\u4e0b\u4e07\u4e07\u62c5\u5f53\u4e0d\u8d77\u7684\u3002\u201d \u3000\u3000\u8bf4\u7f62\u53c8\u5927\u7b11\uff0c\u7136\u5219\u5728\u8fd9\u5927\u7b11\u4e4b\u4e2d\uff0c\u76ee\u5149\u5374\u53c8\u626b\u89c6\u7fa4\u8c6a\uff0c\u50cf\u662f\u5728\u7559\u610f\u522b\u4eba\u5bf9\u81ea\u5df1\u7684\u8868\u60c5\u3002 \u3000\u3000\u6ee1\u9662\u706b\u5149\u95ea\u52a8\u4e2d\uff0c\u53ea\u89c1\u9662\u4e2d\u7fa4\u8c6a\u90fd\u51dd\u76ee\u6cd5\u89c6\u7740\u4ed6\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u7684\u7b11\u58f0\u66f4\u52a0\u5f00\u6717\u4e86\uff0c\u54ea\u77e5\u5c31\u5728\u8fd9\u79cd\u7b11\u58f0\u4e2d\uff0c\u95e8\u5916\u7a81\u7136\u98de\u6b65\u62a2\u8fdb\u4e00\u4e2a\u4eba\u6765\uff0c\u8fde\u8fde\u558a \u9053\uff1a\u201c\u4e54\u67d0\u6765\u665a\u4e86\uff0c\u8be5\u6b7b\uff0c\u8be5\u6b7b\u53c8\u558a\u7740\uff1a\u201c\u4e91\u8001\u7237\u5b50\uff0c\u5c0f\u7684\u6765\u7ed9\u60a8\u8001\u4eba\u5bb6\u62dc\u5bff\u6765\u4e86\u3002\u201d \u3000\u3000\u4f17\u8c6a\u77a0\u76ee\u4e4b\u4e2d\uff0c\u5df2\u89c1\u95e8\u53e3\u62a2\u8fdb\u4e00\u4e2a\u6ee1\u8eab\u9526\u8863\u7684\u7626\u5c0f\u6c49\u5b50\uff0c\u4e00\u624b\u6258\u7740\u4e00\u4e2a\u6a80\u6728\u5323\uff0c\u53e6\u4e00\u53ea \u624b\u5374\u5939\u7740\u4e09\u8f74\u4e66\u5377\uff0c\u98de\u4e5f\u4f3c\u5730\u5954\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u7fa4\u8c6a\u53c8\u7acb\u523b\u4e00\u9635\u54d7\u7b11\uff0c\u56e0\u4e3a\u53ea\u8981\u5728\u6c5f\u6e56\u803d\u8fc7\u7684\u5927\u90e8\u8bc6\u5f97\u6b64\u4eba\uff0c\u90a3\u957f\u7b11\u58f0\u4e2d\u9ec4\u886b\u5c11\u5e74\u4e00\u53cc \u5251\u7709\u76b1\u4e86\u76b1\uff0c\u7b11\u58f0\u500f\u7136\u987f\u4f4f\u4e86\u3002 \u3000\u3000\u8fd9\u6ee1\u8eab\u9526\u8863\u7684\u7626\u5c0f\u6c49\u5b50\u4e00\u5954\u8fdb\u6765\uff0c\u5c31\u5728\u4e91\u8c26\u8eab\u524d\u7ffb\u8eab\u62dc\u5012\uff0c\u4e00\u9762\u7b11\u9053\uff1a\u201c\u5c0f\u4f84\u4e54\u8fc1\uff0c\u8c28 \u795d\u4e91\u8001\u7237\u5b50\u798f\u5982\u4e1c\u6d77\uff0c\u5bff\u6bd4\u5357\u5c71\u3002\u201d \u3000\u3000\u90a3\u5bff\u661f\u4e91\u8c26\u4e00\u9762\u54c8\u54c8\u5927\u7b11\u7740\uff0c\u4e00\u9762\u5f2f\u8eab\u53bb\u6289\uff0c\u9053\uff1a\u201c\u597d\u8bf4\uff0c\u597d\u8bf4\uff0c\u8d24\u4f84\u5feb\u8d77\u6765\u3002\u201d\u4e00\u9762 \u53c8\u9053\uff1a\u201c\u4e2d\u7a0b\uff0c\u8fd8\u4e0d\u5feb\u628a\u4f60\u4e54\u4e09\u54e5\u6276\u8d77\u6765\u3002\u201d \u3000\u3000\u4e91\u4e2d\u7a0b\u62a2\u8fc7\u51e0\u6b65\uff0c\u4ea6\u7b11\u9053\uff1a\u201c\u4e09\u54e5\uff0c\u5feb\u8bf7\u8d77\u6765\uff0c\u770b\u4f60\u624b\u91cc\u62ff\u7740\u4e1c\u897f\uff0c\u53c8\u7ed9\u6211\u4eec\u8001\u7237\u5b50\u5e26 \u4e86\u4ec0\u4e48\u597d\u4e1c\u897f\u6765\u4e86\u3002\u201d \u3000\u3000\u90a3\u6ee1\u8eab\u9526\u8863\u7684\u7626\u5c0f\u6c49\u5b50\uff0c\u6b63\u662f\u6b66\u6797\u4e2d\u4eba\u7f18\u6700\u597d\u7684\u9b3c\u5f71\u513f\u4e54\u8fc1\uff0c\u9664\u4ee5\u8f7b\u529f\u8df3\u7eb5\u672f\u9a70\u8a89\u6c5f\u6e56 \u5916\uff0c\u66f4\u662f\u6c5f\u6e56\u4e2d\u7684\u795e\u5077\u3002 \u3000\u3000\u53ea\u662f\u8fd9\u9b3c\u5f71\u513f\u4e54\u8fc1\uff0c\u51fa\u8eab\u4e16\u5bb6\uff0c\u672c\u6765\u5c31\u662f\u767e\u4e07\u5de8\u5bcc\u7684\u516c\u5b50\uff0c\u867d\u7136\u5584\u5077\uff0c\u5374\u4e0d\u6109\u4eba\uff0c\u800c\u4e14 \u6177\u6168\u5c1a\u4e49\uff0c\u867d\u7136\u5f62\u5bb9\u72e0\u7410\uff0c\u5374\u662f\u6761\u6ca1\u906e\u62e6\u7684\u6c49\u5b50\u3002 \u3000\u3000\u8fd9\u4e54\u8fc1\u6b64\u523b\u819d\u5934\u4e00\u7528\u529b\uff0c\u4eba\u5df2\u4ece\u5730\u4e0a\u7ad9\u4e86\u8d77\u6765\uff0c\u76ee\u5149\u56db\u987e\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u770b\u770b\uff0c\u6211 \u4eec\u4e91\u8001\u7237\u4e8e\u662f\u4e0d\u662f\u5fb7\u9ad8\u671b\u91cd\uff0c\u6211\u4e54\u8fc1\u662f\u4e0d\u662f\u8be5\u6b7b\uff0c\u8fd9\u4e48\u591a\u6b66\u6797\u670b\u53cb\u5168\u90e8\u6765\u4e86\uff0c\u6211\u4e54\u8fc1\u5374\u6765\u5f97 \u6700\u665a\u2014\u2014\u201d\u4ed6\u76ee\u5149\u4e00\u8f6c\uff0c\u8f6c\u90a3\u5353\u7acb\u5728\u9662\u4e2d\uff0c\u9762\u4e0a\u6ee1\u5e26\u4e0d\u6109\u4e4b\u8272\u7684\u9ec4\u886b\u5c11\u5e74\u8eab\u4e0a\uff0c\u8bdd\u58f0\u4e0d\u81ea\u89c9 \u7684\u4e00\u987f\uff0c\u7136\u540e\u53c8\u8198\u4e86\u7ba1\u795e\u9e70\u4e00\u773c\uff0c\u773c\u73e0\u4e00\u8f6c\uff0c\u50cf\u662f\u5df2\u731c\u77e5\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff0c\u8fde\u5fd9\u5927\u7b11\u7740\u63a5\u9053\uff1a \u201c\u5148\u524d\u5c0f\u5f1f\u8fd8\u5728\u5947\u602a\uff0c\u670b\u53cb\u4eec\u600e\u4e48\u4e0d\u5728\u5385\u91cc\u559d\u9152\uff0c\u5374\u7ad9\u5728\u9662\u5b50\u91cc\u6765\u4e86\uff0c\u539f\u6765\u662f\u6709\u4eba\u5728\u8fd9\u91cc\u6bd4 \u6b66\u66ff\u8001\u7237\u5b50\u4e0a\u5bff\uff0c\u8bf7\uff0c\u8bf7\uff0c\u8bf7\uff0c\u7ba1\u5927\u7239\uff0c\u4f60\u53ea\u7ba1\u5f00\u59cb\uff0c\u5c0f\u5f1f\u7ad9\u5230\u4e00\u8fb9\u53bb\u3002\u201d \u3000\u3000\u4e91\u4e2d\u7a0b\u4f4e\u54b3\u4e00\u58f0\uff0c\u6697\u5fd6\u8fd9\u4e54\u8fc1\u5e74\u7eaa\u6709\u4e86\u4e00\u628a\uff0c\u5374\u8fd8\u662f\u5c0f\u5b69\u5b50\u813e\u6c14\uff0c\u600e\u7684\u4e8b\u60c5\u6ca1\u6709\u5f04\u6e05 \u695a\uff0c\u5c31\u5148\u56b7\u4e86\u8d77\u6765\uff0c\u5fd9\u5f3a\u7b11\u6253\u5c94\u9053\uff1a\u201c\u4e54\u4e09\u54e5\uff0c\u4f60\u5f04\u9519\u4e86\u2014\u2014\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u54ea\u7ba1\u795e\u9e70\u5374\u7a81\u7136 \u5927\u7b11\u8d77\u6765\uff0c\u6717\u58f0\u9053\uff1a\u201c\u4e91\u4e2d\u7a0b\uff0c\u4f60\u522b\u66ff\u6211\u5706\u8138\uff0c\u6211\u7ba1\u4e00\u67f4\u53ef\u4e0d\u9886\u4f60\u7684\u8fd9\u4e2a\u60c5\uff0c\u4e54\u8001\u4e09\uff0c\u6211\u8001 \u5b9e\u544a\u8bc9\u4f60\uff0c\u6211\u5148\u524d\u5df2\u548c\u8fd9\u4f4d\u5c91\u5c11\u5e74\u82f1\u96c4\u52a8\u8fc7\u624b\u4e86\u3002\u201d \u3000\u3000\u9b3c\u5f71\u513f\u4e54\u8fc1\u773c\u73e0\u53c8\u8f6c\u4e86\u51e0\u8f6c\uff0c\u5fc3\u4e0b\u65b9\u81ea\u6709\u4e9b\u8be7\u5f02\uff0c\u5374\u542c\u7ba1\u4e00\u67f4\u53c8\u9053\uff1a\u201c\u53ef\u662f\uff0c\u4e54\u8001\u4e09\uff0c \u6211\u544a\u8bc9\u4f60\uff0c\u52a8\u624b\u624d\u4e00\u62db\uff0c\u6211\u5c31\u5403\u4e86\u8d25\u4ed7\uff0c\u4e54\u8001\u4e09\u4eca\u5929\u662f\u4f60\u8d70\u8fd0\uff0c\u6765\uff0c\u6765\uff0c\u8ba9\u6211\u66ff\u4f60\u5f13\uff1b\u89c1\u8fd9 \u4f4d\u60ca\u5929\u52a8\u5730\u7684\u5c11\u5e74\u82f1\u96c4\uff0c\u8fd9\u4f4d\u5c31\u662f\u4e0a\u5c91\u4e0a\u7cb2\uff0c\u5c91\u5c11\u82f1\u96c4\u3002\u201d \u3000\u3000\u9b3c\u5f71\u513f\u4e54\u8fc1\u4e0d\u7981\u4e5f\u7741\u5927\u773c\u775b\uff0c\u65e0\u7fc5\u795e\u9e70\u7ba1\u4e00\u67f4\uff0c\u4e00\u62db\u4e4b\u4e0b\uff0c\u5c31\u683d\u5728\u8fd9\u9ec4\u886b\u5c11\u5e74\u624b\u4e0a\uff0c\u8fd9 \u7b80\u76f4\u4ee4\u4eba\u6709\u4e9b\u4e0d\u4fe1\u3002 \u3000\u3000\u9ec4\u886b\u5c11\u5e74\u5c91\u7cb2\u88ab\u8fd9\u9b3c\u5f71\u513f\u8dd1\u6765\uff0c\u8fd9\u4e48\u4e00\u6270\uff0c\u4f7f\u5f97\u7fa4\u8c6a\u7684\u6ce8\u610f\u529b\u90fd\u4ece\u81ea\u5df1\u8eab\u4e0a\u8f6c\u4e86\u5f00\u53bb\uff0c \u5fc3\u4e0b\u65b9\u81ea\u6709\u4e9b\u4e0d\u6109\uff0c\u4f46\u8fd9\u7ba1\u795e\u9e70\uff0c\u201c\u6b64\u4e00\u8bf4\uff0c\u505a\u7136\u7684\u5fae\u7b11\uff0c\u53c8\u590d\u6cdb\u8d77\uff0c\u5fc3\u4e0b\u4e0d\u7981\u53c8\u5bf9\u7ba1\u795e\u9e70 \u589e\u52a0\u4e86\u51e0\u5206\u597d\u611f\u3002\u4ed6\u5e7c\u5e74\u4e4b\u9645\uff0c\u5c31\u88ab\u4e00\u4f4d\u6b66\u6797\u5f02\u4eba\uff0c\u81ea\u5bb6\u4e2d\u5e26\u8d70\uff0c\u5341\u4f59\u5e74\u6765\uff0c\u5b66\u5f97\u4e00\u8eab\u7edd \u827a\uff0c\u6b64\u523b\u752b\u51fa\u6c5f\u6e56\uff0c\u5374\u5df2\u67d3\u5f97\u5176\u5e08\u90a3\u79cd\u8fe5\u5f02\u5e38\u4eba\u7684\u813e\u6c14\uff0c\u884c\u4e8b\u4f46\u51ed\u81ea\u5df1\u7684\u597d\u6076\uff0c\u81f3\u4e8e\u90a3\u4ef6\u4e8b \u5bf9\u4e0d\u5bf9\uff0c\u4ed6\u5168\u7136\u4e0d\u7ba1\u3002\u4e54\u8fc1\u6115\u4e86\u534a\u664c\uff0c\u5374\u89c1\u8fd9\u7ba1\u795e\u9e70\u56db\u4e0b\u4f5c\u4e86\u4e2a\u7f57\u5708\u63d6\uff0c\u6717\u58f0\u9053\uff1a\u201c\u5404\u4f4d\uff0c \u7ba1\u67d0\u544a\u8f9e\u4e86\u3002\u201d \u3000\u3000\u8d70\u5230\u90a3\u9ec4\u886b\u5c11\u5e74\u5c91\u7cb2\u8eab\u4fa7\uff0c\u4f4e\u4f4e\u8bf4\u4e86\u4e24\u53e5\u8bdd\uff0c\u5c91\u7cb2\u5fae\u5fae\u4e00\u7b11\uff0c\u4e54\u8fc1\u5fc3\u4e2d\u53c8\u81ea\u5947\u602a\uff0c\u8fd9\u7ba1 \u795e\u9e70\u5e73\u65e5\u90a3\u79cd\u813e\u6c14\uff0c\u6b64\u523b\u683d\u5728\u4eba\u5bb6\u624b\u4e0a\uff0c\u5374\u600e\u4e48\u8fd8\u5bf9\u4eba\u5bb6\u8fd9\u6837\u3002 \u3000\u3000\u4ed6\u6b63\u81ea\u601d\u5fd6\u4e2d\uff0c\u5374\u89c1\u7ba1\u4e00\u67f4\u5c06\u6396\u5728\u8170\u4e2d\u7684\u957f\u886b\u4e0b\u6446\u653e\u4e86\u4e0b\u6765\uff0c\u671b\u4e5f\u672a\u671b\u4e91\u6c0f\u7236\u5b50\u4e00\u773c\uff0c \u5c31\u81ea\u8f6c\u8eab\u5934\u4e5f\u4e0d\u56de\u8d70\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u4ec1\u4e49\u5251\u5ba2\u9762\u8272\u53c8\u590d\u5927\u53d8\uff0c\u76ee\u5149\u76ef\u5728\u8fd9\u7ba1\u4e00\u67f4\u7684\u540e\u5f71\u4e0a\uff0c\u7a81\u7136\u5f80\u524d\u4e00\u8de8\u6b65\uff0c\u54ea\u77e5\u81c2\u8180\u88ab\u4eba \u4e00\u62c9\uff0c\u5374\u88ab\u4ed6\u7236\u4eb2\u591a\u81c2\u795e\u5251\u62c9\u4f4f\u4e86\u3002 \u3000\u3000\u9b3c\u5f71\u513f\u4e54\u8fc1\u773c\u73e0\u53c8\u4e00\u8f6c\uff0c\u51b7\u51b7\u7b11\u9053\uff1a\u201c\u5404\u4f4d\uff0c\u4f60\u4eec\u7ad9\u5728\u8fd9\u91cc\u4f5c\u5565\uff0c\u8fd8\u4e0d\u8fdb\u53bb\u559d\u9152\uff0c\u6211\u9664 \u4e86\u5e26\u6765\u4e00\u6837\u4e1c\u897f\u7ed9\u4e91\u8001\u7237\u4e0a\u5bff\u4e4b\u5916\uff0c\u8fd8\u6709\u4e00\u6837\u65b0\u9c9c\u4e8b\uff0c\u8981\u544a\u8bc9\u5404\u4f4d\u5462\u3002\u201d \u3000\u3000\u4e91\u4e2d\u7a0b\u5b9a\u4e86\u5b9a\u795e\uff0c\u52c9\u5f3a\u5c06\u795e\u8272\u6062\u590d\u8fc7\u6765\uff0c\u4e5f\u81ea\u62db\u547c\u7740\u7fa4\u8c6a\u5165\u5ea7\uff0c\u90a3\u591a\u81c2\u795e\u5251\u4e91\u8c26\u548c\u6a2a\u6c5f \u91d1\u7d22\u695a\u5360\u9f99\u5bf9\u89c6\u4e86\u4e00\u773c\uff0c\u7f13\u7f13\u8d70\u5230\u5c91\u7cb2\u8eab\u4fa7\uff0c\u5fae\u5fae\u4e00\u63d6\uff0c\u6717\u58f0\u7b11\u7740\u8bf4\u9053\uff1a\u201c\u5144\u53f0\u597d\u4fca\u7684\u8eab \u624b\uff0c\u771f\u662f\u82f1\u96c4\u51fa\u5728\u5c11\u5e74\uff0c\u6559\u8001\u592b\u4ef0\u6155\u5f97\u5f88\u3002\u201d \u3000\u3000\u9ec4\u886b\u5c11\u5e74\u5c91\u7cb2\u4e5f\u62f1\u4e86\u62f1\u624b\uff0c\u7b11\u9053\uff1a\u201c\u4e91\u8001\u524d\u8f88\u5bf9\u5c0f\u53ef\u65b9\u624d\u7684\u4e3e\u52a8\uff0c\u662f\u5426\u6709\u4e9b\u4e0d\u6ee1\u5462\uff1f\u201d \u3000\u3000\u4e91\u8c26\u76ee\u4e2d\u5149\u534e\u95ea\u52a8\uff0c\u4f46\u77ac\u5373\u53c8\u53e3\u590d\u5b89\u7136\uff0c\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u5c91\u5c11\u4fa0\u8bf4\u8fd9\u6837\u7684\u8bdd\u5c31\u662f\u89c1\u5916 \u4e86\uff0c\u4f60\u770b\uff0c\u5927\u5bb6\u90fd\u5df2\u8fdb\u5385\u53bb\u4e86\uff0c\u5c91\u5c11\u4fa0\u4f55\u4e0d\u4e5f\u8fdb\u53bb\u518d\u559d\u4e24\u676f\uff0c\u8001\u592b\u8fd8\u6709\u4e00\u4e8b\uff0c\u8981\u8bf7\u6559\u5c91\u5c11\u4fa0 \u54e9\u3002\u201d \u3000\u3000\u5c91\u7cb2\u6717\u58f0\u7b11\u9053\uff1a\u201c\u8fd9\u4e2a\u81ea\u7136\u3002\u201d \u3000\u3000\u6602\u9996\u8d70\u5165\u5927\u5385\uff0c\u5373\u7b14\u76f4\u8d70\u5230\u9996\u5e2d\uff0c\u5728\u7ba1\u795e\u9e70\u65b9\u624d\u5750\u7684\u90a3\u4e2a\u7a7a\u4f4d\u5b50\u6602\u7136\u5750\u4e86\u4e0b\u6765\uff0c\u76ee\u5149\u626b \u89c6\u95f4\uff0c\u7fa4\u8c6a\u53c8\u5728\u5bf9\u4ed6\u4fa7\u76ee\u4e86\u3002 \u3000\u3000\u5bff\u661f\u4e91\u8c26\u5fae\u4e00\u634b\u987b\uff0c\u8d70\u5230\u9996\u5ea7\u4e0a\uff0c\u65b9\u81ea\u7aef\u8d77\u9152\u676f\uff0c\u5374\u770b\u89c1\u672c\u548c\u4ec1\u4e49\u5251\u5ba2\u4e91\u4e2d\u7a0b\u3001\u7075\u72d0\u667a \u4e66\u7ad9\u5728\u4e00\u8d77\u7684\u9b3c\u5f71\u513f\u4e54\u8fc1\u624b\u91cc\u6367\u7740\u6728\u5323\uff0c\u53c8\u590d\u8d70\u4e0a\u524d\u6765\uff0c\u5c06\u90a3\u4e09\u8f74\u753b\u5377\u5939\u5230\u808b\u4e0b\uff0c\u53cc\u624b\u6367\u8d77 \u6728\u5323\uff0c\u4e00\u9762\u7b11\u7740\u8bf4\u9053\uff1a\u201c\u5c0f\u4f84\u4e54\u8fc1\uff0c\u8c28\u4ee5\u4e00\u53cc\u87e0\u6843\u7ed9\u60a8\u8001\u4eba\u5bb6\u4e0a\u5bff\u3002\u201d \u3000\u3000\u4e91\u8c26\u5927\u7b11\u7740\uff0c\u53cc\u624b\u63a5\u4e86\u8fc7\u6765\uff0c\u7fa4\u8c6a\u7684\u76ee\u5149\uff0c\u4e0d\u7981\u53c8\u8f6c\u5230\u8fd9\u4e00\u6728\u5323\u4e0a\u53bb\uff0c\u60f3\u770b\u770b\u8fd9\u4f4d\u5de8\u5bcc \u795e\u5077\uff0c\u8fd9\u6b21\u9001\u51fa\u6765\u7684\u662f\u4ec0\u4e48\u4e1c\u897f\u3002 \u3000\u3000\u53ea\u89c1\u4e91\u8c26\u4e00\u6253\u5f00\u5323\u5b50\uff0c\u5c31\u542c\u5230\u201c\u54af\u201d\u7684\u4e00\u58f0\u8f7b\u54cd\uff0c\u7a81\u7136\u4ece\u5323\u4e2d\u7ad9\u8d77\u4e24\u9ad8\u672a\u8fc7\u5c3a\u7684\u73a9\u5076 \u6765\uff0c\u4ff1\u90fd\u5851\u9020\u5f97\u6709\u5982\u7c89\u88c5\u7389\u7422\uff0c\u4e00\u7537\u4e00\u5973\uff0c\u624b\u91cc\u6367\u7740\u4e00\u5bf9\u78a7\u7389\u87e0\u6843\uff0c\u6b63\u662f\u4e3a\u738b\u6bcd\u4e0a\u5bff\u7684\u91d1\u7ae5 \u7389\u5973\u3002 \u3000\u3000\u7fa4\u8c6a\u4e0d\u7981\u4ff1\u90fd\u5927\u4e50\uff0c\u5bff\u661f\u4e91\u8c26\u7b11\u58f0\u66f4\u6717\uff0c\u8f6c\u8eab\u5c06\u8fd9\u7cbe\u5de7\u7684\u5bff\u793c\uff0c\u653e\u5230\u4f9b\u684c\u4e0a\uff0c\u5374\u542c\u90a3\u9b3c \u5f71\u513f\u5df2\u81ea\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u6309\u7406\u8bf4\uff0c\u4eca\u5929\u662f\u4e91\u8001\u7237\u5b50\u7684\u534e\u8bde\uff0c\u522b\u4eba\u6765\u665a\uff0c\u72b9\u6709\u53ef\u8bf4\uff0c\u6211\u4e54\u8fc1\u600e\u4f1a \u6765\u7684\u8fd9\u4e48\u665a\u5462\uff1f\u54c8\uff0c\u8fd9\u662f\u6709\u4e2a\u539f\u56e0\u7684\u3002\u201d \u3000\u3000\u4ed6\u4f38\u51fa\u4e00\u6839\u624b\u6307\uff0c\u53c8\u9053\uff1a\u201c\u56e0\u4e3a\u533a\u533a\u5728\u4e0b\uff0c\u7a81\u7136\u542c\u5230\u4e86\u4e00\u4ef6\u6d88\u606f\uff0c\u8fd9\u6d88\u606f\uff0c\u6211\u6562\u8bf4\u662f\u5929 \u4e0b\u6b66\u6797\u670b\u53cb\u90fd\u4e50\u4e8e\u542c\u5230\u7684\uff0c\u53ef\u662f\u5728\u5f53\u65f6\uff0c\u6211\u5374\u6709\u4e9b\u4e0d\u4fe1\uff0c\u6240\u4ee5\u7279\u522b\u8dd1\u5230\u5929\u76ee\u5c71\u4e0a\u53bb\u4e00\u770b\uff0c\u8fd9 \u624d\u77e5\u9053\uff0c\u8fd9\u6d88\u606f\u7adf\u662f\u771f\u7684\u3002\u201d \u3000\u3000\u4ed6\u6ed4\u6ed4\u8bf4\u5230\u8fd9\u91cc\uff0c\u7fa4\u8c6a\u5df2\u6e10\u52a8\u5bb9\uff0c\u90a3\u9ec4\u886b\u5c11\u5e74\u9762\u4e0a\u4e0d\u7981\u9732\u51fa\u6ce8\u610f\u7684\u6837\u5b50\uff0c\u53ea\u662f\u8fd9\u9b3c\u5f71\u513f \u7f29\u56de\u624b\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u53c8\u9053\uff1a\u201c\u5404\u4f4d\uff0c\u53e4\u8bed\u8bf4\u5f97\u597d\uff1a\u5b66\u5f97\u60ca\u4eba\u827a\uff0c\u552e\u4e88\u8bc6\u8d27\u5bb6\u3002\u5404\u4f4d\uff0c\u4f60\u4eec\u53ea \u8981\u81ea\u95ee\u624b\u5e95\u4e0b\u8fd8\u6709\u4e24\u4e0b\u5b50\uff0c\u8d76\u7d27\u6536\u62fe\u5305\u88b1\uff0c\u5230\u5929\u76ee\u5c71\u53bb\uff0c\u6211\u4e54\u8fc1\u5305\u51c6\u4f60\u4eec\u7edd\u5bf9\u4e0d\u4f1a\u51a4\u6789\u8dd1\u8fd9 \u4e00\u8d9f\u7684\u3002\u201d \u3000\u3000\u4ed6\u987f\u4f4f\u8bdd\uff0c\u773c\u73e0\u56db\u4e0b\u4e71\u8f6c\uff0c\u7fa4\u8c6a\u679c\u7136\u4ff1\u90fd\u8038\u7136\u52a8\u5bb9\u3002 \u3000\u3000\u5bff\u7fc1\u4e91\u8c26\u4e00\u62cd\u4ed6\u7684\u80a9\u8180\uff0c\u54c8\u54c8\u4e00\u7b11\u9053\uff1a\u201c\u8d24\u4f84\uff0c\u4f60\u6709\u4ec0\u4e48\u8bdd\uff0c\u5c31\u75db\u5feb\u70b9\u5168\u8bf4\u51fa\u6765\u5427\uff0c\u4f55 \u5fc5\u53eb\u4eba\u5bb6\u7740\u6025\u3002\u201d \u3000\u3000\u4e54\u8fc1\u563b\u7684\u4e00\u54a7\u5634\uff0c\u7b11\u9053\uff1a\u201c\u53ea\u4e0d\u8fc7\u6211\u8fd9\u6d88\u606f\u4e00\u8bf4\u51fa\u6765\uff0c\u5404\u4f4d\u603b\u5f97\u9001\u6211\u4e00\u70b9\u4ec0\u4e48\u4e1c\u897f\u624d \u597d\uff0c\u5404\u4f4d\uff0c\u6211\u8fd9\u5929\u76ee\u5c71\u6765\u56de\u5954\u4e86\u8fd9\u4e48\u4e00\u8d9f\uff0c\u53ef\u4e5f\u4e0d\u80fd\u767d\u8dd1\u5440\u3002\u201d \u3000\u3000\u7fa4\u8c6a\u54d7\u7136\u5927\u7b11\uff0c\u6709\u7684\u548c\u8fd9\u4e54\u8fc1\u8f83\u719f\u7684\uff0c\u5c31\u5728\u7b11\u58f0\u4e2d\u53eb\u9053\uff1a\u201c\u4e54\u4e09\u7237\uff0c\u6211\u4eec\u662f\u60f3\u9001\u4f60\u4e1c \u897f\uff0c\u53ef\u662f\u6211\u4eec\u9001\u7684\u4e1c\u897f\uff0c\u4f60\u80fd\u770b\u5f97\u4e0a\u773c\u5417\uff1f\u201d \u3000\u3000\u6709\u7684\u53c8\u53eb\u9053\uff1a\u201c\u4e54\u7237\uff0c\u4f60\u8001\u5e73\u65e5\u7231\u8bf4\u7b11\uff0c\u6211\u770b\u8fd9\u516b\u6210\u513f\u53c8\u662f\u7b11\u8bdd\uff0c\u6211\u5728\u6c5f\u6e56\u8dd1\u4e86\u8fd9\u4e48\u591a \u5e74\uff0c\u53ef\u4e5f\u4e0d\u77e5\u9053\u5929\u76ee\u5c71\u4e0a\u4f1a\u7a81\u7136\u6389\u4e0b\u6708\u4eae\u6765\u3002\u201d \u3000\u3000\u6b64\u523b\u6ee1\u5385\u7b11\u58f0\uff0c\u663e\u7136\u5df2\u5c06\u65b9\u624d\u7684\u4e0d\u6109\u5feb\u4e4b\u4e8b\u5fd8\u5374\u4e86\uff0c\u4e91\u8c26\u65b9\u5728\u6697\u4e2d\u8f6c\u5ff5\uff0c\u4ee5\u4e3a\u8fd9\u4e54\u8fc1\u771f \u7684\u662f\u5728\u8bf4\u7b11\uff0c\u501f\u4ee5\u4f7f\u5927\u5bb6\u9ad8\u5174\u4e9b\u3002 \u3000\u3000\u54ea\u77e5\u5374\u89c1\u8fd9\u4f4d\u5de8\u5bcc\u795e\u5077\uff0c\u7a81\u7136\u4e00\u672c\u6b63\u7ecf\u5730\u5c06\u684c\u4e0a\u7684\u676f\u76cf\u632a\u5230\u4e00\u8fb9\uff0c\u7a7a\u51fa\u4e00\u5757\u5730\u65b9\u6765\uff0c\u5c06 \u808b\u4e0b\u5939\u7740\u7684\u4e09\u5e45\u753b\u5377\uff0c\u5c0f\u5c0f\u5fc3\u5fc3\u5730\u653e\u5728\u684c\u4e0a\uff0c\u4e00\u9762\u9053\uff1a\u201c\u5404\u4f4d\uff0c\u4f60\u8981\u8ba4\u4e3a\u6211\u8fd9\u662f\u8bf4\u7b11\uff0c\u90a3\u4f60 \u53ef\u8bf4\u9519\u4e86\uff0c\u5404\u4f4d\uff0c\u8001\u5b9e\u544a\u8bc9\u4f60\uff0c\u5929\u76ee\u5c71\u4e0a\uff0c\u6b64\u523b\u6b63\u5728\u642d\u7740\u64c2\u53f0\uff0c\u5404\u4f4d\u53ea\u8981\u80fd\u5728\u8fd9\u64c2\u53f0\u4e0a\u6280\u538b \u5f53\u573a\uff0c\u79f0\u96c4\u9732\u8138\uff0c\u54ea\uff0c\u54ea\uff0c\u8fd9\u4e9b\u5c31\u662f\u4f60\u7684\u4e86\u3002\u201d \u3000\u3000\u8bf4\u7740\uff0c\u4ed6\u4ece\u684c\u4e0a\u62ff\u8d77\u4e00\u5e45\u753b\uff0c\u5377\u4e0a\u91d1\u5149\u707f\u70c2\uff0c\u7adf\u753b\u7740\u4e0d\u8ba1\u5176\u6570\u7684\u91d1\u952d\u3002 \u3000\u3000\u9ec4\u886b\u5c11\u5e74\u5c91\u7cb2\u7aef\u8d77\u9762\u524d\u7684\u9152\u676f\u4ef0\u9996\u559d\u4e86\u4e00\u53e3\uff0c\u4f38\u51fa\u7b77\u5b50\u5939\u4e86\u4e00\u5757\u6d77\u53c2\u653e\u5728\u5634\u91cc\u5480\u56bc\uff0c\u5bf9 \u8fd9\u5e45\u50cf\u662f\u7528\u771f\u91d1\u8d34\u4e0a\u53bb\u7684\u753b\u5377\uff0c\u518d\u4e5f\u4e0d\u671b\u4e00\u773c\u3002 \u3000\u3000\u7fa4\u8c6a\u4e4b\u4e2d\uff0c\u5750\u5728\u540e\u9762\u7684\uff0c\u5df2\u6709\u4eba\u7ad9\u4e86\u8d77\u6765\uff0c\u5f15\u9888\u800c\u671b\u3002 \u3000\u3000\u8fd9\u9b3c\u5f71\u513f\u4e54\u8fc1\u4e00\u9762\u5c0f\u5fc3\u5730\u5377\u8d77\uff0c\u4e00\u9762\u53c8\u9053\uff1a\u201c\u8fd9\u8fd8\u4e0d\u7b97\u7a00\u5947\uff0c\u5404\u4f4d\u518d\u770b\u8fd9\u4e2a\u3002\u201d \u3000\u3000\u968f\u7740\uff0c\u53c8\u62ff\u8d77\u4e00\u5377\u753b\uff0c\u6253\u4e86\u5f00\u6765\uff0c\u7fa4\u8c6a\u53c8\u5374\u54d7\u7136\u4e00\u58f0\uff0c\u773c\u775b\u7741\u5f97\u66f4\u5927\u4e86\u4e9b\u3002 \u3000\u3000\u90a3\u9ec4\u886b\u5c11\u5e74\u5c91\u7cb2\uff0c\u76ee\u5149\u5fae\u659c\uff0c\u4e5f\u4e0d\u7981\u8198\u4e86\u8fd9\u5e45\u753b\u4e00\u773c\u3002 \u3000\u3000\u53ea\u89c1\u8fd9\u4e0a\u9762\uff0c\u6676\u5149\u8000\u76ee\uff0c\u7adf\u4e0d\u77e5\u7528\u4ec0\u4e48\u5728\u4e0a\u9762\u753b\u4e86\u8bb8\u591a\u67c4\u957f\u5251\u3002 \u3000\u3000\u987b\u77e5\u597d\u6b66\u4e4b\u4eba\uff0c\u5f80\u5f80\u5c06\u4e00\u4e9b\u5229\u5668\u795e\u5175\u770b\u5f97\u5c24\u91cd\u4e8e\u8d22\u7269\u73cd\u5b9d\uff0c\u9b3c\u5f71\u513f\u4e54\u8fc1\u6253\u5f00\u7684\u8fd9\u7b2c\u4e8c\u5e45 \u753b\uff0c\u663e\u7136\u6bd4\u7b2c\u4e00\u5e45\u66f4\u4ee4\u4eba\u8038\u52a8\u3002 \u3000\u3000\u4e54\u8fc1\u7528\u5de6\u624b\u62ff\u7740\u8fd9\u753b\u5e45\u7684\u4e0a\u7aef\uff0c\u4f38\u8d77\u53f3\u624b\u7684\u98df\u6307\uff0c\u6307\u7740\u753b\u4e0a\u7684\u957f\u5251\uff0c\u7f13\u7f13\u7b11\u9053\uff1a\u201c\u91d1 \u86c7\u3001\u817e\u86c7\u3001\u98de\u51e4\u3001\u866c\u9f99\uff0c\u5404\u4f4d\u4f60\u4eec\u603b\u8be5\u542c\u8fc7\u8fd9\u51e0\u67c4\u5251\u7684\u540d\u5b57\u5427\uff0c\u53ef\u662f\u4f60\u4eec\u53c8\u6709\u8c01\u89c1\u8fc7\u5462\uff1f\u201d \u3000\u3000\u4ed6\u6545\u610f\u62d6\u7740\u957f\u5c3e\u97f3\uff0c\u54c8\u54c8\u4e00\u7b11\uff0c\u53c8\u9053\uff1a\u201c\u53ef\u662f\u5404\u4f4d\u82e5\u4e0a\u4e86\u5929\u76ee\u5c71\uff0c\u80fd\u5728\u4eba\u5bb6\u8bbe\u4e0b\u7684\u51e0\u6837 \u73a9\u610f\u91cc\u9732\u4e00\u624b\uff0c\u54c8\uff0c\u90a3\u8fd9\u51e0\u53e3\u5251\uff0c\u5176\u4e2d\u5c31\u6709\u4e00\u53e3\u662f\u4f60\u7684\u4e86\u3002\u201d \u3000\u3000\u4e00\u4e2a\u7c97\u5927\u58f0\u97f3\uff0c\u5728\u4eba\u4e1b\u4e2d\u543c\u9053\uff1a\u201c\u4e54\u4e09\u7237\uff0c\u4f60\u8fd9\u4e0d\u662f\u9a97\u6211\u7684\u5427\uff1f\u201d \u3000\u3000\u4e54\u8fc1\u95ea\u76ee\u4e00\u671b\uff0c\u53ea\u89c1\u53d1\u8bdd\u7684\u4eba\uff0c\u6b63\u662f\u6c5f\u5357\u4e09\u624d\u5251\u7684\u540d\u5bb6\u90ed\u62d3\u5e73\uff0c\u4e0d\u7981\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u90ed\u5927 \u7237\uff0c\u6211\u4e54\u4e09\u51e0\u65f6\u9a97\u8fc7\u4f60\u6765\uff0c\u4f60\u8981\u662f\u5f97\u4e86\u90a3\u53e3\u98de\u51e4\u5251\uff0c\u90a3\u4f60\u4f7f\u8d77\u5251\u6765\uff0c\u53ef\u5c31\u66f4\u6ca1\u6709\u4eba\u80fd\u62b5\u6321\u5f97 \u4f4f\u4e86\u2026\u2026\u201d \u3000\u3000\u8bdd\u72b9\u672a\u4e86\uff0c\u90a3\u90ed\u62d3\u5e73\u5df2\u8dc3\u8eab\u800c\u8d77\uff0c\u8d70\u4e86\u51fa\u6765\uff0c\u671d\u8fd9\u753b\u72e0\u72e0\u76ef\u4e86\u4e24\u773c\uff0c\u53c8\u671d\u5bff\u7fc1\u4e91\u8c26\u5f53\u5934 \u4e00\u63d6\uff0c\u7adf\u81ea\u7c97\u7740\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u4e91\u8001\u7237\u5b50\uff0c\u5c0f\u4f84\u5148\u8d70\u4e00\u6b65\u4e86\u3002\u201d \u3000\u3000\u671d\u56db\u5ea7\u62f1\u4e86\u62f1\u624b\uff0c\u7adf\u4e0d\u7b49\u4e91\u8c26\u7684\u633d\u7559\uff0c\u5c31\u5927\u6b65\u8d70\u4e86\u51fa\u53bb\uff0c\u8fd9\u90ed\u62d3\u5e73\u539f\u6765\u662f\u4e2a\u706b\u70e7\u7709\u6bdb\u7684 \u6025\u813e\u6c14\u3002 \u3000\u3000\u4f46\u662f\u90a3\u9ec4\u886b\u5c11\u5e74\uff0c\u5374\u4ecd\u7136\u81ea\u987e\u5403\u559d\u7740\uff0c\u8fd9\u4e9b\u6b66\u6797\u4e2d\u4eba\u4eba\u5782\u6d8e\u7684\u5229\u5668\u795e\u5175\uff0c\u7adf\u4e5f\u5f15\u4e0d\u8d77\u4ed6 \u7684\u5174\u8da3\uff0c\u50cf\u662f\u4ed6\u6839\u672c\u4e0d\u9700\u8981\u8fd9\u4e9b\u4f3c\u7684\u3002 \u3000\u3000\u4e91\u8001\u7237\u5b50\u8f7b\u8f7b\u76b1\u4e86\u76b1\u7709\uff0c\u5411\u4e54\u8fc1\u9053\uff1a\u201c\u8d24\u4f84\uff0c\u4f60\u8fd9\u53ef\u4e0d\u662f\u6545\u4f5c\u60ca\u4eba\u5427\uff0c\u5426\u5219\u73a9\u7b11\u53ef\u5c31\u771f \u5f00\u5f97\u592a\u5927\u4e86\u5427\uff01\u201d \u3000\u3000\u4e54\u8fc1\u53c8\u6536\u8d77\u8fd9\u5e45\u753b\uff0c\u62ff\u8d77\u7b2c\u4e09\u5e45\u6765\uff0c\u4e00\u9762\u7b11\u9053\uff1a\u201c\u4e91\u8001\u7237\uff0c\u60a8\u8001\u4eba\u5bb6\u653e\u5fc3\uff0c\u8981\u662f\u5c0f\u4f84\u8fd9 \u662f\u5f00\u73a9\u7b11\uff0c\u60a8\u5c31\u53eb\u4e91\u4e2d\u7a0b\u628a\u6211\u8111\u888b\u5207\u4e0b\u6765\u597d\u4e86\u3002\u201d \u3000\u3000\u8bf4\u7740\u4ed6\u53c8\u7f13\u7f13\u5c55\u5f00\u7b2c\u4e09\u5e45\u753b\uff0c\u8fd9\u4e00\u6b21\uff0c\u7adf\u8fde\u90a3\u7d20\u6765\u4e0d\u52a8\u58f0\u8272\u7684\u9ec4\u886b\u5c11\u5e74\u5c91\u7cb2\u90fd\u4e0d\u7981\u9762\u8272 \u5927\u52a8\uff0c\u63a8\u676f\u800c\u8d77\uff0c\u7fa4\u8c6a\u7684\u54d7\u7136\u4e4b\u58f0\uff0c\u54cd\u5f97\u4e5f\u81ea\u66f4\u5389\u5bb3\u4e86\u3002 \u7b2c\u4e09\u7ae0\u3000\u7edd\u8272\u4e3d\u4eba>> \u53e4\u9f99\u300a\u6708\u5f02\u661f\u90aa\u300b \u7b2c\u4e09\u7ae0\u3000\u7edd\u8272\u4e3d\u4eba \u3000\u3000\u6cb3\u6714\u5de8\u5bcc\uff0c\u6b66\u6797\u795e\u5077\uff0c\u9b3c\u5f71\u513f\u4e54\u8fc1\u8fd9\u4e00\u5c55\u5f00\u7b2c\u4e09\u5e45\u753b\u6765\uff0c\u6ee1\u5385\u7fa4\u8c6a\uff0c\u66f4\u662f\u8038\u7136\u52a8\u5bb9\uff0c\u5c31 \u8fde\u90a3\u4e00\u5411\u65e0\u52a8\u4e8e\u8877\u7684\u9ec4\u886b\u5c11\u5e74\u5c91\u7cb2\uff0c\u90a3\u4e00\u53cc\u70af\u70af\u53d1\u7740\u5149\u5f69\u7684\u6717\u76ee\uff0c\u4e5f\u4e0d\u7981\u7728\u4e5f\u4e0d\u7728\u5730\u77aa\u7740\u8fd9 \u5e45\u753b\u4e0a\u3002 \u3000\u3000\u53ea\u89c1\u8fd9\u5e45\u6de1\u9ec4\u7684\u7d20\u7ee2\u4e0a\uff0c\u753b\u7684\u7adf\u662f\u4e00\u4f4d\u7edd\u8272\u7684\u4e3d\u4eba\uff0c\u4e91\u9b13\u9ad8\u633d\uff0c\u7c89\u9762\u6843\u816e\uff0c\u7709\u5982\u6625\u5c71\uff0c \u9f3b\u5982\u60ac\u80c6\uff0c\u4e00\u53cc\u5982\u6708\u660e\u7738\uff0c\u5e7d\u5e7d\u5730\u671b\u7740\u81ea\u5df1\u7684\u4e00\u53cc\u6625\u8471\uff0c\u534a\u70b9\u6a31\u6843\uff0c\u5fae\u5fae\u9732\u51fa\u5507\u4e2d\u7684\u534a\u884c\u7389 \u5177\uff0c\u4e00\u88ad\u8f7b\u7ea2\u7f57\u886b\uff0c\u66f4\u886c\u5f97\u53d1\u5982\u9752\u4e1d\uff0c\u80a4\u82e5\u83b9\u7389\uff0c\u6ee1\u5ea7\u7fa4\u8c6a\uff0c\u867d\u7136\u4e45\u5386\u6c5f\u6e56\uff0c\u5317\u5730\u80ed\u8102\uff0c\u5357 \u56fd\u4f73\u4e3d\uff0c\u90fd\u4e5f\u66fe\u89c1\u8fc7\u4e0d\u5c11\uff0c\u4f46\u62ff\u6765\u548c\u753b\u4e2d\u7684\u8fd9\u7edd\u8272\u4e3d\u4eba\u4e00\u6bd4\uff0c\u7acb\u5373\u4fbf\u5168\u90e8\u9eef\u7136\u5931\u8272\u3002 \u3000\u3000\u8fd9\u65f6\u504c\u5927\u7684\u4e00\u5ea7\u5385\u5802\uff0c\u51e0\u4e4e\u9759\u5f97\u6709\u5982\u8352\u90ca\uff0c\u4f46\u95fb\u7fa4\u8c6a\u7684\u547c\u5438\u4e4b\u58f0\uff0c\u6b64\u8d77\u5f7c\u843d\u3002 \u3000\u3000\u9b3c\u5f71\u513f\u4e54\u8fc1\u5de6\u624b\u4ecd\u63d0\u7740\u753b\u5e45\uff0c\u53f3\u624b\u671d\u81ea\u5df1\u9894\u4e0b\u7684\u77ed\u987b\u8f7b\u8f7b\u4e00\u62b9\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4e0d\u7792\u5404 \u4f4d\uff0c\u6211\u4e54\u8001\u4e09\u8981\u4e0d\u662f\u771f\u89c1\u8fc7\u753b\u4e2d\u4eba\uff0c\u53ef\u4e5f\u771f\u4e0d\u76f8\u4fe1\u5c18\u8870\u4e2d\u4f1a\u6709\u8fd9\u79cd\u4f73\u4e3d\uff0c\u800c\u4e14\u8fd9\u5e45\u753b\u867d\u662f\u4f20 \u795e\uff0c\u53ef\u662f\u4e16\u95f4\u518d\u9ad8\u7684\u4e39\u9752\u5999\u624b\uff0c\u5374\u4e5f\u753b\u4e0d\u51fa\u8fd9\u753b\u4e2d\u4e4b\u4eba\u7684\u7edd\u8272\u6765\u3002\u201d \u3000\u3000\u9759\u5bc2\u4e86\u8bb8\u4e45\u7684\u4eba\u8bed\u58f0\u53c8\u590d\u5927\u4f5c\uff0c\u9ec4\u886b\u5c11\u5e74\u5c91\u7cb2\u76ee\u5149\u4e2d\u5e26\u7740\u6df1\u601d\u4e4b\u8272\uff0c\u7f13\u7f13\u53c8\u5750\u56de\u6905\u4e0a\uff0c \u8fd9\u753b\u4e2d\u4e3d\u4eba\u7684\u7edd\u8272\uff0c\u56fa\u7136\u4ee4\u4ed6\u795e\u9a70\u76ee\u7729\uff0c\u4f46\u66f4\u4ee4\u4ed6\u60ca\u5f02\u7684\uff0c\u5374\u662f\u8fd9\u753b\u4e2d\u4e3d\u4eba\u7684\u9762\u5b54\uff0c\u50cf\u662f\u4f3c \u66fe\u76f8\u8bc6\uff0c\u53ea\u662f\u4ed6\u641c\u8ffd\u8bb0\u5fc6\uff0c\u5374\u4e5f\u60f3\u4e0d\u51fa\u5230\u5e95\u662f\u5728\u54ea\u91cc\u89c1\u8fc7\u800c\u5df2\u3002 \u3000\u3000\u4e54\u4e09\u7237\u53c8\u662f\u54c8\u54c8\u4e00\u7b11\uff0c\u5de6\u624b\u4e00\u626c\uff0c\u5c06\u90a3\u5e45\u753b\u66f4\u63d0\u9ad8\u4e86\u4e9b\uff0c\u7b11\u9053\uff1a\u201c\u5404\u4f4d\uff0c\u60a8\u8981\u662f\u4e0d\u4f46\u80fd \u5728\u5929\u76ee\u5c71\u4e2d\u8bbe\u4e0b\u7684\u51e0\u6837\u7edd\u6280\u4e2d\uff0c\u51fa\u5165\u5934\u5730\uff0c\u8fd8\u80fd\u6280\u538b\u5f53\u573a\uff0c\u5927\u9b41\u7fa4\u96c4\uff0c\u90a3\u4e48\u2014\u2014\u201d\u4ed6\u53f3\u624b\u671d \u753b\u5e45\u4e00\u6307\uff0c\u63a5\u9053\uff1a\u201c\u4e0d\u4f46\u660e\u73e0\u5343\u6735\uff0c\u9ec4\u91d1\u4e07\u4e24\uff0c\u90fd\u5c06\u5f52\u60a8\u6240\u6709\uff0c\u753b\u4e2d\u7684\u8fd9\u4f4d\u4e3d\u4eba\uff0c\u4e5f\u5c31\u53d8\u6210 \u4f60\u7684\u91d1\u5c4b\u4e2d\u4eba\uff0c\u4e0d\u8fc7\uff0c\u53ea\u662f\u4e00\u6837\u2014\u2014\u201d\u4ed6\u6545\u610f\u4e00\u987f\u8bdd\u58f0\uff0c\u7f13\u7f13\u5730\u5377\u8d77\u8fd9\u5e45\u753b\u6765\uff0c\u53cc\u76ee\u95ea\u52a8 \u5904\uff0c\u53ea\u89c1\u6ee1\u5385\u7fa4\u8c6a\uff0c\u5927\u591a\u5df2\u7ad9\u4e86\u8d77\u6765\uff0c\u4f38\u957f\u8116\u5b50\uff0c\u9759\u542c\u81ea\u5df1\u7684\u4e0b\u6587\u3002 \u3000\u3000\u591a\u81c2\u795e\u5251\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e54\u8d24\u4f84\uff0c\u4f60\u6709\u4ec0\u4e48\u8bdd\uff0c\u5c31\u5feb\u8bf4\u51fa\u6765\u5427\uff0c\u522b\u53eb\u5927\u5bb6\u7740\u6025\u3002\u201d \u3000\u3000\u9b3c\u5f71\u513f\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4e0d\u8fc7\u60f3\u8981\u505a\u8fd9\u4f4d\u7edd\u4ee3\u4f73\u4eba\u7684\u4e58\u9f99\u5feb\u5a7f\uff0c\u4e00\u5b9a\u5f97\u8981\u5e74\u7eaa\u4e0d\u5927\uff0c\u8fd8\u672a\u5a36 \u8fc7\u5bb6\u5ba4\u7684\uff0c\u50cf\u6211\u8fd9\u53f7\u4eba\u7269\uff0c\u522b\u8bf4\u6b66\u529f\u8fd8\u5dee\u5f97\u5927\u8fdc\uff0c\u5c31\u7b97\u6b66\u529f\u771f\u6210\uff0c\u4e5f\u53ea\u6709\u5e72\u77aa\u773c\uff0c\u90a3\u53ea\u662f\u56e0 \u4e3a\u533a\u533a\u5728\u4e0b\u5df2\u7ecf\u6210\u4e86\u5bb6\uff0c\u8fde\u513f\u5b50\u90e8\u751f\u51fa\u6765\u4e86\uff0c\u6211\u8981\u662f\u65e9\u77e5\u9053\u6709\u8fd9\u79cd\u4e8b\uff0c\u90a3\u5c31\u662f\u62ff\u5200\u67b6\u5728\u6211\u8116 \u5b50\u4e0a\uff0c\u6211\u53ef\u4e5f\u4e0d\u4f1a\u90a3\u4e48\u65e9\u5c31\u5a36\u4eb2\u7684\u3002\u201d \u3000\u3000\u7fa4\u8c6a\u54d7\u7b11\u58f0\u4e2d\uff0c\u7a81\u6709\u4e2a\u54cd\u4eae\u7684\u58f0\u97f3\u9053\uff1a\u201c\u662f\u4e0d\u662f\u9664\u4e86\u7ed3\u8fc7\u4eb2\u7684\u4e4b\u5916\uff0c\u4efb\u4ec0\u4e48\u4eba\u90fd\u6709\u8d44\u683c \u5462\uff1f\u201d \u3000\u3000\u9b3c\u5f71\u513f\u4e54\u8fc1\u76ee\u5149\u52a8\u5904\uff0c\u53ea\u89c1\u53d1\u8bdd\u7684\u8fd9\u4eba\u8eab\u9ad8\u4f53\u58ee\uff0c\u6ee1\u9762\u7ea2\u5149\uff0c\u5934\u4e0a\u624e\u7740\u4e00\u65b9\u201c\u738b\u201d\u5b57\u6b66 \u751f\u4e2d\uff0c\u6b63\u662f\u6c5f\u5317\u5730\u65b9\u6210\u540d\u7684\u6b66\u5e08\u79c3\u9e70\u6bb7\u8001\u4e94\u3002\u4e54\u8fc1\u4e0d\u7981\u54c8\u54c8\u53c8\u4e00\u7b11\uff0c\u53c8\u9053\uff1a\u201c\u5bf9\u4e86\uff0c\u4e00\u70b9\u4e5f \u4e0d\u9519\uff0c\u522b\u8bf4\u50cf\u6bb7\u8001\u4e94\uff0c\u4f60\u8fd9\u6837\u7684\u4e00\u8868\u4eba\u6750\uff0c\u5c31\u7b97\u662f\u5927\u9ebb\u5b50\uff0c\u72ec\u773c\u9f99\uff0c\u751a\u81f3\u7f3a\u6761\u817f\uff0c\u65ad\u53ea\u624b \u7684\uff0c\u53ea\u8981\u662f\u624b\u5e95\u4e0b\u6709\u4e24\u4e0b\uff0c\u4e00\u6837\u4e5f\u80fd\u5f97\u5230\u8fd9\u4f4d\u7f8e\u4eba\u513f\u7684\u9752\u7750\u3002\u201d \u3000\u3000\u79c3\u9e70\u6bb7\u8001\u4e94\u4e00\u62cd\u8111\u95e8\uff0c\u672c\u5df2\u662f\u6ee1\u662f\u6cb9\u5149\u7684\u8138\u4e0a\uff0c\u66f4\u5192\u51fa\u7ea2\u4eae\u4eae\u7684\u4e00\u5c42\u5149\u6765\uff0c\u4e00\u9762\u7b54\u9053\uff1a \u201c\u6709\u8fd9\u79cd\u4e8b\uff0c\u90a3\u6211\u6bb7\u8001\u4e94\u8bf4\u4e0d\u5f97\u4e5f\u8981\u4e0a\u5929\u76ee\u5c71\u53bb\u8d70\u8d70\u4e86\u3002\u201d \u3000\u3000\u6251\u5730\u5750\u4e86\u6765\uff0c\u62ff\u8d77\u4e00\u5927\u676f\u9152\uff0c\u5495\u561f\u559d\u4e86\u4e0b\u53bb\uff0c\u53f3\u624b\u968f\u624b\u4e00\u62b9\uff0c\u5c31\u5c06\u5934\u4e0a\u7684\uff0c\u201c\u5e72\u201d\u5b57\u6b66 \u751f\u4e2d\u62b9\u4e86\u4e0b\u6765\uff0c\u5374\u9732\u51fa\u91cc\u9762\u7684\u4e00\u9897\u79c3\u5934\u3002 \u3000\u3000\u7fa4\u8c6a\u53c8\u90fd\u54c4\u7136\u5c31\u5ea7\uff0c\u9b3c\u5f71\u513f\u4e54\u8fc1\u5c06\u8fd9\u4e09\u5e45\u753b\u4ed4\u7ec6\u5730\u653e\u5728\u81ea\u5df1\u7684\u65f6\u8fb9\uff0c\u624d\u5750\u4e86\u4e0b\u6765\uff0c\u5374\u89c1 \u591a\u81c2\u795e\u5251\u4e91\u8001\u7237\u5b50\u6b63\u8272\u8bf4\u9053\uff1a\u201c\u4e54\u8d24\u4f84\uff0c\u73b0\u5728\u4f60\u8bf4\u4e5f\u8bf4\u51fa\u53bb\u4e86\uff0c\u6211\u53ef\u8981\u95ee\u963f\u4f60\uff0c\u8fd9\u4ef6\u4e8b\u5230\u5e95 \u662f\u600e\u4e48\u56de\u4e8b\uff1f\u5728\u5929\u76ee\u5c71\u91cc\u9762\u5f04\u51fa\u8fd9\u4e48\u4ef6\u8f70\u8f70\u70c8\u70c8\u7684\u5927\u4e8b\u6765\u7684\uff0c\u5230\u5e95\u662f\u8c01\uff0c\u4e0d\u7792\u4e54\u8d24\u4f84\u4f60\u8bf4\uff0c \u8fd9\u4ef6\u4e8b\u8001\u592b\u770b\u6765\uff0c\u786e\u5b9e\u6709\u70b9\u900f\u7740\u5947\u602a\uff0c\u5929\u4e0b\u54ea\u6709\u628a\u91d1\u5143\u5b9d\u786c\u5f80\u4eba\u8eab\u4e0a\u9001\u7684\u4eba\u5462\uff1f\u201d \u3000\u3000\u9b3c\u5f71\u513f\u4e54\u8fc1\u626c\u8d77\u676f\u6765\uff0c\u5927\u5927\u5730\u5438\u4e86\u53e3\u9152\uff0c\u65b9\u81ea\u7b11\u9053\uff1a\u201c\u4e91\u8001\u7237\u5b50\uff0c\u4e0d\u7792\u60a8\u8001\u4eba\u5bb6\u8bf4\uff0c\u5929 \u76ee\u5c71\u91cc\u4eba\u7684\u5230\u5e95\u662f\u4ec0\u4e48\u6765\u5386\uff0c\u5c0f\u4f84\u73b0\u5728\u53ef\u4e5f\u4e0d\u80fd\u8bf4\u51fa\u6765\uff0c\u4e0d\u8fc7\u8fd9\u4ef6\u4e8b\u5012\u7684\u786e\u5343\u771f\u4e07\u786e\u7684\uff0c\u5230 \u5929\u76ee\u5c71\u4e0a\u53bb\u7684\u4eba\uff0c\u5c31\u7b97\u6b66\u529f\u4e0d\u6210\uff0c\u7a7a\u624b\u800c\u8fd4\uff0c\u53ef\u4e5f\u7edd\u4e0d\u4f1a\u5403\u4e8f\u3002\u201d \u3000\u3000\u591a\u81c2\u795e\u5251\u4e24\u6761\u6d53\u7709\u5fae\u76b1\uff0c\u7a81\u7136\u7b11\u9053\uff1a\u201c\u65e2\u7136\u662f\u5982\u6b64\uff0c\u8001\u592b\u8bf4\u4e0d\u5b9a\u4e5f\u8981\u53bb\u770b\u770b\u4e86\uff0c\u5927\u7ea6\u4e0d \u51fa\u4e24\u4e2a\u6708\uff0c\u5929\u76ee\u5c71\u4e0a\uff0c\u51a0\u76d6\u4e91\u96c6\uff0c\u6b66\u6797\u4e2d\u6210\u540d\u9732\u8138\u7684\u4eba\u7269\uff0c\u6050\u6015\u90fd\u8981\u5728\u90a3\u91cc\u9732\u4e00\u9732\u4e86\u3002\u201d \u3000\u3000\u8bdd\u58f0\u65b9\u4e86\uff0c\u5e2d\u4e0a\u7a81\u7136\u54cd\u8d77\u4e00\u9635\u6717\u7b11\u4e4b\u58f0\uff0c\u53ea\u89c1\u90a3\u9ec4\u886b\u5c11\u5e74\u5c91\u7cb2\u6717\u7b11\u9053\uff1a\u201c\u5176\u5b9e\u81ea\u95ee\u6b66\u529f \u4e0d\u6210\u7684\uff0c\u5012\u662f\u4e0d\u53bb\u66f4\u597d\uff0c\u4e0d\u7136\u53cd\u800c\u8d34\u4e0a\u8def\u8d39\uff0c\u5077\u9e21\u4e0d\u7740\uff0c\u53cd\u800c\u5012\u8680\u628a\u7c73\uff0c\u90a3\u624d\u53eb\u51a4\u6789\uff01\u201d \u3000\u3000\u59cb\u7ec8\u7acb\u5728\u4e91\u8c26\u8eab\u540e\u7684\u4ec1\u4e49\u5251\u5ba2\u4e91\u4e2d\u7a0b\uff0c\u6b64\u523b\u8f69\u7709\u8bf4\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u5c82\u975e\u53ea\u8981\u9601\u4e0b\u4e00\u4eba \u53bb\u5c31\u8db3\u591f\u4e86\u5417\uff1f\u201d \u3000\u3000\u4e91\u8001\u7237\u5b50\u6d53\u7709\u53c8\u4e00\u76b1\uff0c\u56de\u9996\u542b\u55d4\u671b\u4e86\u90a3\u4e91\u4e2d\u7a0b\u3001\u4f3c\u4e4e\u5728\u8d23\u602a\u4ed6\u4e0d\u5e94\u62db\u60f9\u8fd9\u9ec4\u886b\u5c11\u5e74\uff0c\u56f0 \u4e3a\u8fd9\u8001\u6c5f\u6e56\u5df2\u4ece\u8fd9\u5c11\u5e74\u65b9\u624d\u65bd\u51fa\u7684\u8eab\u6cd5\uff0c\u770b\u51fa\u4ed6\u7684\u6765\u5386\u3002 \u3000\u3000\u54ea\u77e5\u9ec4\u886b\u5c11\u5e74\u5c91\u7cb2\u53c8\u51b7\u7b11\u9053\uff1a\u201c\u6b63\u662f\uff0c\u6b63\u662f\uff0c\u5c31\u50cf\u9601\u4e0b\u8fd9\u79cd\u8eab\u6cd5\uff0c\u8fd8\u771f\u4e0d\u5982\u4e0d\u53bb\u4e5f \u7f62\u3002\u201d \u3000\u3000\u4e91\u4e2d\u7a0b\u5251\u7709\u4e00\u8f69\uff0c\u5e2d\u4e0a\u7684\u8fd9\u73ed\u4ff1\u662f\u6b66\u6797\u4e2d\u4e00\u6d41\u4eba\u7269\u7684\u8001\u8005\uff0c\u4e5f\u4ff1\u90fd\u4e3a\u4e4b\u8272\u53d8\uff0c\u4f46\u90a3\u9ec4\u886b \u5c11\u5e74\uff0c\u5374\u4ecd\u7136\u82e5\u65e0\u5176\u4e8b\uff0c\u751f\u50cf\u662f\u6839\u672c\u5c31\u6ca1\u6709\u5c06\u8fd9\u4e9b\u6b66\u6797\u9ad8\u624b\u653e\u5728\u773c\u91cc\u4f3c\u7684\u3002 \u3000\u3000\u4ed6\u76ee\u5149\u4e00\u8f6c\uff0c\u8f6c\u5230\u9b3c\u5f71\u513f\u4e54\u8fc1\u65f6\u8fb9\u7684\u4e09\u5e45\u753b\u4e0a\uff0c\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u9601\u4e0b\u7684\u8fd9\u4e09\u5e45\u753b\uff0c\u4e5f\u4e0d\u5fc5 \u5e26\u5728\u8eab\u4e0a\u5230\u5904\u4f20\u8bf4\u4e86\uff0d\uff0d\u201d\u8bf4\u8bdd\u58f0\u4e2d\uff0c\u7f13\u7f13\u4f38\u51fa\u5de6\u624b\u6765\uff0c\u5c31\u671d\u90a3\u4e09\u5e45\u753b\u4e0a\u6293\u53bb\u3002 \u3000\u3000\u9b3c\u5f71\u513f\u4e54\u8fc1\u6b64\u523b\u4e5f\u4e0d\u7981\u9762\u8272\u5927\u53d8\uff0c\u51b7\u53f1\u9053\uff1a\u201c\u8fd9\u4e2a\u8fd8\u4e0d\u52b3\u9601\u4e0b\u8d39\u5fc3\u3002\u201d \u3000\u3000\u626c\u7740\u9152\u676f\u7684\u53f3\u624b\uff0c\u7a81\u7136\u4fbf\u538b\u5728\u8fd9\u4e09\u5e45\u753b\u5377\u4e0a\u3002 \u3000\u3000\u9ec4\u886b\u5c11\u5e74\u5c91\u7cb2\u51b7\u7b11\u4e00\u58f0\uff0c\u5de6\u624b\u4e5f\u5df2\u642d\u4e0a\u753b\u5377\uff0c\u4e54\u8fc1\u53ea\u89c9\u538b\u5728\u753b\u5377\u4e0a\u7684\u53f3\u624b\uff0c\u7a81\u7136\u4e00\u70ed\uff0c \u676f\u4e2d\u7684\u9152\uff0c\u50cf\u55b7\u6cc9\u822c\u6d8c\u4e86\u51fa\u6765\uff0c\u6e85\u5f97\u4ed6\u4e00\u8eab\u3002 \u3000\u3000\u5e2d\u4e0a\u7fa4\u8c6a\uff0c\u4e0d\u7981\u53c8\u4e3a\u4e4b\u8083\u7136\uff0c\u9ec4\u886b\u5c11\u5e74\u5c91\u7cb2\u51b7\u7b11\u58f0\u4e2d\uff0c\u5df2\u5c06\u4e09\u5e45\u753b\u5377\u62ff\u5728\u624b\u91cc\uff0c\u4e00\u9762\u51b7 \u7b11\u9053\uff1a\u201c\u8fd9\u8fd8\u662f\u4ea4\u7ed9\u5728\u4e0b\u597d\u4e9b\u3002\u201d \u3000\u3000\u9b3c\u5f71\u513f\u4e54\u8fc1\u4e00\u751f\u95ef\u8361\uff0c\u4ea4\u904d\u4e86\u5929\u4e0b\u6b66\u6797\u4e2d\u9ed1\u3001\u767d\u4e24\u9053\u7684\u670b\u53cb\uff0c\u4e0d\u5230\u4e07\u4e0d\u5f97\u5df2\uff0c\u7edd\u4e0d\u548c\u4eba \u52a8\u624b\uff0c\u6b64\u523b\u5374\u4e5f\u4e0d\u7981\u9762\u76ee\u53d8\u8272\uff0c\u539f\u5730\u5750\u5728\u6905\u4e0a\uff0c\u5fae\u4e00\u62e7\u8170\uff0c\u53cc\u624b\u75be\u4f38\uff0c\u55d6\u5730\u51fb\u5411\u8fd9\u5c11\u5e74\u5c91\u7cb2 \u7684\u808b\u4e0b\u3002 \u3000\u3000\u53e3\u4e2d\u4e00\u9762\u5389\u53f1\u9053\uff1a\u201c\u670b\u53cb\uff0c\u4f60\u672a\u514d\u4e5f\u592a\u72c2\u4e86\u5427\u3002\u201d \u3000\u3000\u9ec4\u886b\u5c11\u5e74\u5c91\u7cb2\u76ee\u5149\u4e00\u51db\uff0c\u51b7\u53f1\u9053\uff1a\u201c\u4f60\u60f3\u52a8\u624b\u3002\u201d \u3000\u3000\u5de6\u624b\u6293\u4f4f\u753b\u5377\uff0c\u6a2a\u5730\u4e00\u5212\uff0c\u4fbf\u500f\u7136\u5212\u5411\u8fd9\u9b3c\u5f71\u513f\u4e54\u8fc1\u4e00\u53cc\u624b\u638c\u7684\u8109\u95e8\uff0c\u5e94\u53d8\u4e4b\u8fc5\uff0c\u53ef\u8bf4 \u662f\u6709\u5982\u95ea\u7535\u4e00\u822c\u3002 \u3000\u3000\u4e54\u8fc1\u6c89\u8098\u626c\u8155\uff0c\u638c\u7f18\u53d8\u5f0f\u5207\u5411\u8fd9\u5c11\u5e74\u7684\u80a9\u5934\uff0c\u8fd9\u5728\u6b66\u6797\u4e2d\u7d20\u6709\u795e\u5077\u4e4b\u8a89\u7684\u9b3c\u5f71\u513f\u3002\u6b64\u523b \u4e00\u51fa\u624b\uff0c\u53d8\u62db\u679c\u7136\u5feb\u6781\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u4ff1\u90fd\u4ecd\u7aef\u5750\u5728\u6905\u4e0a\uff0c\u4f46\u77ac\u606f\u4e4b\u95f4\uff0c\u5374\u5df2\u62c6\u4e86\u6570\u62db\uff0c\u8fd9\u79cd\u8d34\u8eab\u8fd1\u640f\u7684\u62db\u5f0f\uff0c\u770b\u6765\u867d \u4e0d\u60ca\u4eba\uff0c\u4f46\u5374\u4ff1\u90fd\u662f\u7acb\u53ef\u5224\u51fa\u80dc\u8d1f\u7684\u5999\u7740\u3002 \u3000\u3000\u5750\u5728\u8fd9\u9ec4\u886b\u5c11\u5e74\u8eab\u4fa7\u7684\uff0c\u6b63\u662f\u957f\u6c5f\u6c34\u8def\u5927\u8c6a\u3001\u6a2a\u6c5f\u91d1\u7d22\u695a\u5360\u9f99\uff0c\u6b64\u523b\u6d53\u7709\u4e00\u8f69\uff0c\u51b7\u53f1 \u9053\uff1a\u201c\u670b\u53cb\uff0c\u8fd9\u91cc\u53ef\u4e0d\u662f\u4f60\u52a8\u624b\u7684\u5730\u65b9\u3002\u201d \u3000\u3000\u5de6\u624b\u624b\u8098\u4e00\u6c89\uff0c\u4e00\u4e2a\u8098\u62f3\uff0c\u649e\u5411\u90a3\u9ec4\u886b\u5c11\u5e74\u53f3\u808b\u3002 \u3000\u3000\u9ec4\u886b\u5c11\u5e74\u5c91\u7cb2\u5de6\u624b\u6293\u7740\u753b\u5377\uff0c\u5411\u5916\u4e00\u5c01\uff0c\u5c01\u4f4f\u4e86\u4e54\u8fc1\u7684\u4e00\u53cc\u624b\u638c\uff0c\u53f3\u638c\u7a81\u7136\u5411\u5185\u4e00\u56de\uff0c \u5e76\u6307\u5982\u5251\uff0c\u6307\u5411\u695a\u5360\u9f99\u65f6\u95f4\u7684\u66f2\u6c60\u7a74\u3002 \u3000\u3000\u8fd9\u9ec4\u886b\u5c11\u5e74\u5de6\u53f3\u53cc\u624b\uff0c\u7adf\u7136\u5206\u5411\u51fb\u51fa\uff0c\u800c\u4e14\u4ff1\u662f\u4ee5\u653b\u5236\u653b\u3001\u5236\u654c\u673a\u5148\u7684\u5999\u7740\uff0c\u8eab\u624b\u4e4b\u60ca \u4eba\uff0c\u4e5f\u65e0\u602a\u4ed6\u8fd9\u4e48\u72c2\u5984\u4e86\u3002 \u3000\u3000\u54ea\u77e5\u5c31\u5728\u8fd9\u540c\u4e00\u5239\u90a3\u91cc\uff0c\u4ed6\u773c\u524d\u7a81\u7136\u94f6\u5149\u4e00\u95ea\uff0c\u4e24\u9053\u5bd2\u98ce\uff0c\u5288\u9762\u800c\u6765\u3002 \u3000\u3000\u8fd9\u4e00\u4e0b\u4ed6\u4e09\u9762\u53d7\u654c\uff0c\u800c\u4e14\u90fd\u662f\u5feb\u5982\u8fc5\u96f7\uff0c\u5e2d\u4e0a\u7684\u6b66\u6797\u5065\u8005\uff0c\u773c\u770b\u8fd9\u72c2\u5984\u7684\u5c11\u5e74\u5df2\u5c06\u4e27\u5728 \u8fd9\u4e09\u9762\u5939\u653b\u4e4b\u4e0b\u2014\u2014\u54ea\u77e5\u7fa4\u8c6a\u53ea\u89c9\u773c\u524d\uff5e\u82b1\uff0c\u9ec4\u886b\u5c11\u5e74\u4fbf\u5df2\u5931\u53bb\u8e2a\u8ff9\uff0c\u6a2a\u6c5f\u91d1\u7d22\u695a\u5360\u9f99\u548c\u9b3c \u5f71\u513f\u4e54\u8fc1\u7684\u62f3\u638c\uff0c\u7adf\u9f50\u90fd\u843d\u7a7a\uff0c\u90a3\u5288\u9762\u5411\u4ed6\u6253\u6765\u7684\u4e24\u70b9\u94f6\u5149\uff0c\u53bb\u52bf\u72b9\u52b2\uff0c\u7adf\u5e26\u7740\u98ce\u58f0\uff0c\u98de\u5411 \u90bb\u684c\uff0c\u4e0d\u504f\u4e0d\u501a\u7684\u7adf\u6070\u5de7\u51fb\u5411\u90a3\u79c3\u9e70\u6bb7\u8001\u4e94\u7684\u79c3\u5934\u3002 \u3000\u3000\u79c3\u9e70\u6bb7\u8001\u4e94\u9762\u8272\u4e00\u53d8\uff0c\u957f\u8eab\u800c\u8d77\uff0c\u94c1\u638c\u62e9\u5904\uff0c\u5c06\u8fd9\u4e24\u9053\u94f6\u661f\u6325\u51fa\u4e86\u5385\u5916\uff0c\u6ee1\u5385\u54d7\u7136\u58f0 \u4e2d\uff0c\u53ea\u89c9\u591a\u81c2\u795e\u5251\u53d8\u8272\u4f4e\u547c\u4e00\u58f0\uff1a\u201c\u8ff7\u8e2a\u4e03\u53d8\u3002\u201d \u3000\u3000\u65b9\u624d\u76db\u6012\u4e2d\uff0c\u5c06\u684c\u4e0a\u7684\u4e00\u53cc\u94f6\u7b77\u5f53\u6697\u5668\u53d1\u51fa\uff0c\u51fb\u5411\u90a3\u9ec4\u6749\u5c11\u5e74\u9762\u95e8\u7684\u4ec1\u4e49\u5251\u5ba2\u4e91\u4e2d\u7a0b\uff0c \u6b64\u523b\u76ee\u5149\u52a8\u5904\uff0c\u770b\u89c1\u90a3\u9ec4\u886b\u5c11\u5e74\u5c91\u7cb2\uff0c\u7adf\u8fde\u4eba\u5e26\u6905\u7aef\u5750\u5728\u90a3\u5f20\u4e0a\u9762\u4f9b\u7740\u5bff\u6843\u7684\u516b\u4ed9\u684c\u4e0a\u524d \u9762\uff0c\u5634\u89d2\u5140\u81ea\u5e26\u7740\u4e00\u4e1d\u51b7\u7b11\u3002 \u3000\u3000\u6b64\u523b\u5385\u4e0a\u53c8\u662f\u4e00\u9635\u5927\u4e71\uff0c\u6a2a\u6c5f\u4f59\u7d22\u695a\u5360\u9f99\u3001\u9b3c\u5f71\u513f\u4e54\u8fc1\u5df2\u81ea\u63a8\u676f\u800c\u8d77\uff0c\u90a3\u9ec4\u886b\u5c11\u5e74\u867d\u4ecd \u7aef\u5750\u4e0d\u52a8\uff0c\u6b63\u5728\u7f13\u7f13\u5c55\u770b\u753b\u5377\uff0c\u4f46\u662f\u9762\u4e0a\u5251\u7709\u6012\u5206\uff0c\u76ee\u5149\u51db\u7136\uff0c\u5df2\u9732\u51fa\u6740\u673a\u6765\u3002 \u3000\u3000\u63e1\u7740\u83dc\u7897\u3001\u6b63\u5f85\u4e0a\u83dc\u7684\u957f\u886b\u5065\u6c49\uff0c\u6b64\u523b\u4e0d\u7981\u4e5f\u505c\u4f4f\u811a\u6b65\uff0c\u4ed6\u4eec\u624b\u91cc\u6367\u7740\u7684\uff0c\u867d\u7136\u662f\u975e\u5f97 \u4e58\u70ed\u5403\u7684\u9c8d\u9c7c\u5927\u7fc5\uff0c\u4f46\u6b64\u65f6\u5374\u4e5f\u53ea\u80fd\u8ba9\u8fd9\u83dc\u51c9\u7740\uff0c\u56e0\u4e3a\u6b64\u523b\u5927\u5385\u4e2d\u5251\u62d4\u5f29\u5f20\uff0c\u5df2\u662f\u4e00\u89e6\u5373\u53d1 \u7684\u5c40\u9762\u3002 \u3000\u3000\u54ea\u77e5\u6b64\u523b\u5385\u5916\u7a81\u7136\u4f20\u672a\u4e00\u9635\u94f6\u94c3\u822c\u7684\u7b11\u58f0\uff0c\u4e00\u4e2a\u5a07\u67d4\u7684\u53e3\u97f3\u7b11\u9053\uff1a\u201c\u8fd9\u4e48\u597d\u4e00\u53cc\u7b77\u5b50\uff0c \u4e22\u4e86\u53ef\u6709\u591a\u53ef\u60dc\u5440\uff01\u201d \u3000\u3000\u7fa4\u8c6a\u7acb\u523b\u8be7\u7136\u56de\u987e\uff0c\u53ea\u89c1\u5927\u5385\u4e4b\u5916\uff0c\u8885\u5a1c\u8d70\u8fdb\u4e24\u4e2a\u7ea2\u88f3\u4e3d\u4eba\u6765\u3002\u6ee1\u5934\u9752\u4e1d\uff0c\u9ad8\u9ad8\u633d\u8d77\uff0c \u5a07\u58f0\u5a49\u8f6c\uff0c\u4f53\u6001\u5982\u67f3\uff0c\u4e00\u4eba\u4f38\u51fa\u4e00\u53ea\u6b3a\u971c\u8d5b\u96ea\u7684\u7389\u624b\uff0c\u624b\u91cc\u62ff\u7740\u7684\uff0c\u5374\u662f\u65b9\u624d\u88ab\u79c3\u9e70\u6325\u51fa\u5385 \u5916\u7684\u94f6\u7b77\u3002 \u3000\u3000\u9b3c\u5f71\u513f\u4e54\u8fc1\u7684\u76ee\u5149\uff0c\u6b64\u523b\u4e0d\u7981\u4e5f\u4ece\u90a3\u9ec4\u886b\u5c11\u5e74\u8eab\u4e0a\u8f6c\u4e86\u8fc7\u6765\uff0c\u4ed6\u76ee\u5149\u4e00\u89e6\u5230\u8fd9\u4e24\u4e2a\u7ea2\u886b \u5c11\u5973\u4e4b\u9762\uff0c\u7a81\u7136\u4e00\u6115\uff0c\u7ade\u62a2\u6b65\u8fce\u4e86\u4e0a\u53bb\u3002 \u3000\u3000\u8fd9\u4e24\u4e2a\u7ea2\u88f3\u5c11\u5973\uff0c\u53f3\u624b\u5907\u62ff\u7740\u94f6\u7b77\uff0c\u79cb\u6ce2\u56db\u4e0b\u4e00\u8f6c\uff0c\u77a5\u89c1\u4e54\u8fc1\uff0c\u4fbf\u4e00\u8d77\u4f38\u51fa\u5728\u624b\uff0c\u63a9\u53e3 \u4e00\u7b11\uff0c\u5a07\u58f0\u9053\uff1a\u201c\u539f\u6765\u4e54\u4e09\u7237\u4e5f\u5728\u8fd9\u91cc\u5440\uff01\u201d \u3000\u3000\u8f7b\u7ea2\u7f57\u886b\u7684\u5bbd\u5927\u8863\u8896\uff0c\u5fae\u5fae\u843d\u4e0b\u534a\u622a\uff0c\u9732\u51fa\u91cc\u9762\u4e00\u53cc\u81ea\u5982\u83b9\u7389\u7684\u624b\u8155\uff0c\u7b11\u5bb9\u4e4b\u7f8e\uff0c\u4e0d\u53ef \u65b9\u626c\u3002 \u3000\u3000\u6ee1\u5385\u7fa4\u8c6a\u88ab\u8fd9\u4e24\u4e2a\u7ea2\u88f3\u5c11\u5973\u7684\u8f7b\u8f7b\u4e00\u7b11\uff0c\u53ea\u89c9\u610f\u7729\u795e\u9a70\uff0c\u6570\u767e\u53ea\u773c\u775b\uff0c\u4e0d\u7981\u90fd\u7728\u4e5f\u4e0d\u7791 \u7684\u76ef\u5728\u8fd9\u4e24\u4e2a\u5c11\u5973\u8eab\u4e0a\u3002 \u3000\u3000\u9b3c\u5f71\u513f\u4e54\u8fc1\u62a2\u6b65\u5230\u8fd9\u5546\u4e2a\u5c11\u5973\u7684\u8eab\u4fa7\uff0c\u7adf\u7136\u8eac\u8eab\u65bd\u4e86\u4e00\u793c\uff0c\u9053\uff1a\u201c\u4e24\u4f4d\u59d1\u5a18\u600e\u4e48\u4e5f\u6765 \u4e86\uff1f\u201d \u3000\u3000\u8fd9\u4e24\u4e2a\u7ea2\u88f3\u5c11\u5973\u4e00\u8d77\u4f38\u51fa\u53f3\u624b\uff0c\u5c06\u624b\u4e2d\u7684\u94f6\u7b77\u9012\u5728\u8fd9\u4e54\u4e09\u7237\u624b\u4e0a\uff0c\u5de6\u624b\u8f7b\u8f7b\u5411\u4e0a\u4e00\u63d0\uff0c \u7406\u4e86\u7406\u9b13\u8fb9\u7684\u4e71\u53d1\uff0c\u9f50\u58f0\u5a07\u7b11\u9053\uff1a\u201c\u6211\u4eec\u662f\u6765\u62dc\u5bff\u6765\u4e86\uff0c\u4e54\u4e09\u7237\uff0c\u60a8\u7ed9\u6211\u4eec\u5f15\u89c1\u5f15\u89c1\uff0c\u505a\u5bff \u7684\u4e91\u8001\u7237\u5b50\u662f\u54ea\u4e00\u4f4d\u5440\uff1f\u201d \u3000\u3000\u6ee1\u5385\u706f\u5149\u901a\u660e\uff0c\u65b9\u624d\u63d2\u5728\u9662\u5899\u91cc\u7684\u706b\u628a\uff0c\u4e5f\u672a\u64a4\u4e0b\uff0c\u6b64\u523b\u8fd9\u5927\u5385\u91cc\u91cc\u5916\u5916\uff0c\u4ff1\u90fd\u4eae\u5982\u767d \u663c\uff0c\u5385\u4e0a\u7fa4\u8c6a\u6115\u7136\u76ee\u6ce8\u4e4b\u4e2d\uff0c\u53d1\u89c9\u8fd9\u4e24\u4e2a\u7ea2\u88f3\u4e3d\u4eba\uff0c\u4e0d\u4f46\u4f53\u6001\u3001\u7b11\u8c8c\uff0c\u4ff1\u90fd\u4e00\u6837\u7684\u5a07\u7f8e\u52a8\u4eba \u4e00\u8fd9\u4e24\u4eba\u7684\u9762\u8c8c\uff0c\u7adf\u4e5f\u5b8c\u5168\u4e00\u6837\uff0c\u751f\u50cf\u662f\u4e0a\u82cd\u9020\u7269\uff0c\u4e5f\u9020\u4e86\u8fd9\u4e48\u4e00\u4f4d\u4e3d\u4eba\u6765\uff0c\u5374\u4ecd\u89c9\u5f97\u610f\u72b9 \u672a\u5c3d\uff0c\u7adf\u53c8\u7167\u7740\u8fd9\u526f\u6837\u5b50\uff0c\u4e00\u6a21\u4e00\u6837\u7684\u53c8\u9020\u4e86\u4e00\u4e2a\uff0c\u53ea\u82e6\u4e86\u6ee1\u5385\u7fa4\u8c6a\u7684\u773c\u775b\uff0c\u7adf\u4e0d\u77e5\u7a76\u7adf\u770b \u5728\u8c01\u8eab\u4e0a\u624d\u597d\u3002 \u3000\u3000\u5bff\u7fc1\u4e91\u8c26\u6b64\u523b\u5df2\u7f13\u6b65\u8d70\u4e86\u51fa\u6765\uff0c\u4ed6\u65b9\u624d\u89c1\u5230\u8fd9\u4e24\u4e2a\u7ea2\u88f3\u5c11\u5973\u7684\u88c5\u675f\u6253\u626e\uff0c\u5fc3\u4e2d\u8f6c\u5904\u4fbf\u5df2 \u731c\u51fa\uff0c\u8fd9\u6709\u5982\u5929\u5916\u98de\u6765\u3001\u7a81\u7136\u51fa\u73b0\u7684\u4e24\u4e2a\u5c11\u5973\uff0c\u5fc5\u5b9a\u662f\u548c\u90a3\u753b\u4e2d\u7684\u4e3d\u4eba\u6709\u7740\u5173\u7cfb\u3002 \u3000\u3000\u4ed6\u5373\u6b65\u51fa\u7b75\u95f4\uff0c\u90a3\u4e24\u4e2a\u7ea2\u88f3\u5c11\u5973\u6ce2\u56de\u8f6c\u5904\uff0c\u4e5f\u5df2\u8fce\u524d\u4e00\u6b65\uff0c\u4e00\u8d77\u4f38\u51fa\u7389\u624b\uff0c\u5728\u8170\u95f4\u4e00 \u642d\uff0c\u6df1\u6df1\u5730\u798f\u4e86\u4e0b\u53bb\uff0c\u4e00\u9762\u5a07\u7b11\u7740\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u60f3\u5fc5\u5c31\u662f\u4e91\u8001\u7237\u5b50\uff0c\u6211\u4eec\u59d0\u59b9\u4fe9\u4eba\u62dc\u5bff\u6765\u5f97 \u8fdf\u4e86\uff0c\u8fd8\u8bf7\u60a8\u8001\u4eba\u5bb6\u6055\u7f6a\u3002\u201d \u3000\u3000\u5bff\u7fc1\u4e91\u8c26\u6380\u987b\u4e00\u7b11\uff0c\u7b11\u9053\uff1a\u201c\u597d\u8bf4\uff0c\u597d\u8bf4\uff0c\u8001\u592b\u7684\u8d31\u8fb0\uff0c\u600e\u6562\u52b3\u52a8\u4e24\u4f4d\u59d1\u5a18\u7684\u5927\u9a7e\u3002\u201d \u201c\u8fd9\u4f4d\u591a\u81c2\u795e\u5251\uff0c\u5728\u81ea\u5df1\u751f\u8fb0\u4e2d\uff0c\u5df2\u9047\u5230\u8fd9\u4e48\u591a\u6a2a\u751f\u7684\u53d8\u653e\uff0c\u4f46\u8fd9\u540d\u6ee1\u6c5f\u6e56\u7684\u8001\u4eba\uff0c\u6b64\u523b\u5374 \u4ecd\u7136\u7b11\u8bed\u4ece\u5bb9\uff0c\u7684\u786e\u662f\u6027\u60c5\u8c41\u8fbe\u4e4b\u4eba\u3002\u8fd9\u4e24\u4e2a\u7ea2\u88f3\u5c11\u5973\u4e00\u8d77\u5a77\u5a77\u7ad9\u4e86\u8d77\u6765\uff0c\u63a9\u53e3\u7b11\u9053\uff1a\u201c\u4e91 \u8001\u7237\u5b50\u8981\u8fd9\u4e48\u8bf4\uff0c\u53ef\u6559\u6211\u4eec\u59d0\u59b9\u4fe9\u4eba\u6298\u715e\u4e86\uff0c\u6211\u4eec\u5bb6\u5c0f\u59d0\u5e38\u8ddf\u6211\u4eec\u8bf4\uff0c\u5f53\u4eca\u6b66\u6797\u4e2d\uff0c\u53ea\u6709\u4e91 \u8001\u7237\u5b50\u662f\u4e86\u4e0d\u8d77\u7684\u8001\u524d\u8f88\uff0c\u8fd9\u6b21\u6211\u4eec\u5c0f\u59d0\u5dee\u6211\u4eec\u59d0\u59b9\u6765\u7ed9\u4e91\u8001\u7237\u5b50\u62dc\u5bff\uff0c\u6211\u4eec\u59d0\u59b9\u90fd\u9ad8\u5174\u5f97 \u4e0d\u5f97\u4e86\uff0c\u56e0\u4e3a\u6211\u4eec\u603b\u7b97\u89c1\u7740\u4e91\u8001\u7237\u5b50\u4e86\uff0c\u60a8\u8001\u4eba\u5bb6\u7434\u662f\u4e0d\u5acc\u5f03\u6211\u4eec\u59d0\u59b9\uff0c\u5c31\u5343\u4e07\u522b\u8fd9\u4e48\u5ba2 \u6c14\u3002\u201d \u3000\u3000\u8fd9\u4e24\u4e2a\u7ea2\u88f3\u5c11\u5973\u5de7\u7b11\u60c5\u7136\uff0c\u8bed\u82e5\u9ec4\u83ba\uff0c\u5ae1\u54fa\u5495\u5495\u8bf4\u4e86\u8fd9\u4e48\u4e00\u5927\u7bc7\uff0c\u6ee1\u5385\u7fa4\u8c6a\u5374\u90fd\u4e0d\u7981\u6697 \u5403\u4e00\u60ca\uff0c\u5fc3\u4e2d\u540c\u65f6\u5347\u8d77\u4e00\u4e2a\u60f3\u6cd5\uff1a\u201c\u539f\u6765\u8fd9\u4e24\u4e2a\u5c11\u5973\u4ec5\u662f\u4e2b\u73af\u800c\u5df2\uff0c\u90a3\u4e48\u5979\u4eec\u5c0f\u59d0\uff0c\u53c8\u8be5\u662f \u600e\u6837\u7684\u4e00\u4e2a\u4eba\u5462\uff1f\u201d \u3000\u3000\u4e8e\u662f\u7fa4\u8c6a\u5fc3\u4e2d\uff0c\u4e0d\u7ea6\u800c\u540c\u7684\u5c31\u8054\u60f3\u5230\u90a3\u753b\u4e2d\u7684\u4e3d\u4eba\u8eab\u4e0a\uff0c\u5bf9\u5929\u76ee\u5c71\u4e4b\u884c\uff0c\u66f4\u52a0\u4e86\u51e0\u5206\u4fe1 \u5ff5\u3002 \u3000\u3000\u5bff\u7fc1\u4e91\u8c26\u54c8\u54c8\u4e00\u7b11\uff0c\u65b9\u81ea\u5f85\u8a00\uff0c\u54ea\u77e5\u8fd9\u4e24\u4e2a\u5c11\u5973\u53c8\u5a07\u58f0\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u4eec\u53ea\u987e\u81ea\u5df1\u8bf4 \u8bdd\uff0c\u5374\u628a\u6b63\u7ecf\u4e8b\u7ed9\u5fd8\u4e86\u3002\u201d \u3000\u3000\u4e00\u8d77\u6084\u7136\u8f6c\u8eab\uff0c\u8f7b\u79fb\u83b2\u6b65\uff0c\u8d70\u5230\u5385\u53e3\uff0c\u4f38\u51fa\u56db\u53ea\u7389\u638c\u6765\uff0c\u8f7b\u8106\u5730\u62cd\u4e86\u51e0\u4e0b\u3002 \u3000\u3000\u4e00\u9762\u5374\u53c8\u56de\u9996\u5a07\u7b11\u9053\uff1a\u201c\u6211\u4eec\u5c0f\u59d0\u8fd8\u53eb\u6211\u4eec\u5e26\u6765\u51e0\u6837\u8584\u793c\uff0c\u7ed9\u4e91\u8001\u7237\u5b50\u60a8\u8001\u4eba\u5bb6\u4e0a\u5bff\uff0c \u53eb\u6211\u4eec\u7980\u544a\u60a8\u8001\u4eba\u5bb6\uff0c\u8bf4\u5979\u4e0d\u80fd\u4eb2\u6765\uff0c\u8bf7\u60a8\u8001\u4eba\u5bb6\u6055\u7f6a\u3002\u201d \u3000\u3000\u4e91\u8c26\u957f\u7b11\u8c26\u8c22\uff0c\u5374\u89c1\u90a3\u4e24\u6247\u4e00\u76f4\u655e\u5f00\u7684\u5927\u95e8\u4e2d\uff0c\u5df2\u8885\u5a1c\u8d70\u8ff8\u4e24\u4e2a\u4ea6\u662f\u4e00\u8eab\u7ea2\u7f57\u88f3\u7684\u5782\u820d \u5c11\u5973\u6765\uff0c\u624b\u4e2d\u6367\u7740\u4e00\u53ea\u91d1\u5149\u95ea\u95ea\u7684\u62dc\u76d2\uff0c\u4e0d\u8bba\u91cc\u9762\u7684\u662f\u4ec0\u4e48\u4e1c\u897f\uff0c\u5c31\u5355\u5355\u662f\u8fd9\u4e24\u53ea\u62dc\u76d2\uff0c\u5df2 \u662f\u4ef7\u503c\u4e0d\u83f2\u4e86\u3002 \u3000\u3000\u7fa4\u8c6a\u65b9\u81ea\u6697\u4e2d\u77a5\u8d5e\uff0c\u54ea\u77e5\u8fd9\u4e24\u4e2a\u5782\u820d\u5c11\u5973\u65b9\u81ea\u8d70\u5230\u9662\u4e2d\uff0c\u95e8\u5916\u5374\u53c8\u8f6c\u5165\u4e00\u5bf9\u7ea2\u88f3\u5782\u9aeb\u5c11 \u5973\uff0c\u624b\u91cc\u4e5f\u6367\u7740\u4e00\u5bf9\u7eaf\u91d1\u62dc\u76d2\u3002 \u3000\u3000\u5bff\u7fc1\u4e91\u8c26\u4e00\u634b\u957f\u987b\uff0c\u8d70\u5230\u5385\u53e3\uff0c\u8fde\u58f0\u9053\uff0c\u201c\u4e24\u4f4d\u59d1\u5a18\uff01\u8fd9\u2026\u2026\u8001\u592b\u600e\u62c5\u5f53\u5f97\u8d77\u3002\u201d \u3000\u3000\u8bed\u72b9\u672a\u4e86\uff0c\u95e8\u5916\u5df2\u9646\u9646\u7eed\u7eed\u8885\u5a1c\u5730\u8d70\u8fdb\u516b\u5bf9\u6367\u7740\u7eaf\u91d1\u62dc\u76d2\u4e3a\u7ea2\u88f3\u5782\u8b6c\u5c11\u5973\u6765\uff0c\u4e00\u4e2a\u4e2a\u83b2 \u6b65\u73ca\u73ca\uff0c\u4e00\u8d77\u8d70\u5230\u5385\u53e3\uff0c\u4e00\u624b\u4e3e\u7740\u62dc\u76d2\uff0c\u4e00\u624b\u642d\u5728\u8170\u4e0a\u671d\u5bff\u7fc1\u4e91\u8c26\uff0c\u6df1\u6df1\u5730\u4e00\u798f\u3002 \u3000\u3000\u6ee1\u5385\u7fa4\u8c6a\uff0c\u4e0d\u7981\u4ff1\u90fd\u76f8\u987e\u52a8\u5bb9\uff0c\u53ea\u6709\u90a3\u9ec4\u886b\u5c11\u5e74\uff0c\u5374\u4ecd\u7aef\u5750\u5728\u6905\u4e0a\uff0c\u624b\u91cc\u5df2\u5c55\u5f00\u90a3\u5e45\u7ee2 \u753b\uff0c\u773c\u775b\u76ef\u5728\u753b\u4e2d\u90a3\u7edd\u8272\u4e3d\u4eba\u8eab\u4e0a\uff0c\u4eff\u4f5b\u5728\u56de\u5fc6\u7740\u4ec0\u4e48\u3002 \u3000\u3000\u90a3\u4e24\u4e2a\u7ea2\u88f3\u5c11\u5973\u4e00\u8d77\u8f6c\u8fc7\u8eab\u6765\uff0c\u4e00\u9762\u5a07\u7b11\u9053\uff1a\u201c\u8fd9\u4e48\u51e0\u6837\u8584\u793c\uff0c\u7b97\u4e0d\u4e86\u4ec0\u4e48\uff0c\u4e91\u8001\u7237\u5b50 \u5343\u4e07\u522b\u5ba2\u6c14\uff0c\u6211\u4eec\u59d0\u59b9\u6765\u7ed9\u8001\u4eba\u5bb6\u62dc\u5bff\uff0c\u5374\u6839\u672c\u5f79\u5e26\u4ec0\u4e48\uff0c\u53ea\u6709\u518d\u656c\u60a8\u8001\u4eba\u5bb6\u4e00\u676f\u5bff\u9152 \u4e86\u3002\u201d\u8885\u5a1c\u8d70\u5230\u7b75\u524d\uff0c\u5df2\u6709\u4e00\u4e2a\u957f\u886b\u5065\u6c49\uff0c\u9012\u6765\u4e24\u53ea\u9152\u676f\uff0c\u5bff\u7fc1\u4e91\u8c26\u4ea6\u5927\u6b65\u8d76\u672a\uff0c\u5927\u7b11\u9053\uff1a \u201c\u597d\u3001\u597d\uff0c\u4e24\u4f4d\u59d1\u5a18\u65e2\u7136\u5982\u6b64\u8bf4\uff0c\u8001\u592b\u5c31\u5148\u53d7\u4e86\u3002\u201d \u3000\u3000\u4e3e\u8d77\u9152\u676f\uff0c\u4e00\u996e\u800c\u5c3d\u3002 \u3000\u3000\u8fd9\u4e24\u4e2a\u7ea2\u88f3\u5c11\u5973\u5728\u676f\u4e2d\u6d45\u6d45\u555c\u4e86\u4e00\u53e3\uff0c\u53c8\u81ea\u5a07\u7b11\u9053\uff1a\u201c\u4eca\u5929\u4e91\u8001\u7237\u5b50\u505a\u5bff\uff0c\u5929\u4e0b\u6b66\u6797\u597d \u6c49\uff0c\u77e5\u9053\u7684\u60f3\u5fc5\u90fd\u8d76\u6765\u4e86\uff0c\u6211\u59d0\u59b9\u4e24\u4e2a\u501f\u82b1\u732e\u4f5b\uff0c\u4e5f\u656c\u5404\u4f4d\u4e00\u676f\u3002\u201d \u3000\u3000\u7fa4\u8c6a\u6b64\u523b\u5927\u534a\u5df2\u88ab\u5979\u4eec\u795e\u91c7\u6240\u593a\uff0c\u81ea\u7136\u5168\u90e8\u4e3e\u8d77\u676f\u6765\u3002 \u3000\u3000\u8fd9\u4e24\u4e2a\u7ea2\u88f3\u5c11\u5973\u6d45\u6d45\u4e00\u7b11\uff0c\u79cb\u6ce2\u4e00\u8f6c\uff0c\u7a81\u7136\u7b11\u5bb9\u987f\u655b\uff0c\u56db\u53ea\u660e\u5982\u79cb\u6c34\u7684\u660e\u7738\uff0c\u5374\u4e00\u8d77\u76ef \u5728\u90a3\u7aef\u5750\u672a\u52a8\u3001\u624b\u91cc\u62ff\u7740\u753b\u5377\u7684\u9ec4\u886b\u5c11\u5e74\u5c91\u7cb2\u8eab\u4e0a\u3002 \u3000\u3000\u9b3c\u5f71\u513f\u4e54\u8fc1\u62a2\u4e0a\u4e09\u6b65\uff0c\u9644\u5728\u8fd9\u4e24\u4e2a\u7ea2\u88f3\u5c11\u5973\u8eab\u4fa7\u8f7b\u8f7b\u8bf4\u4e86\u51e0\u53e5\u8bdd\uff0c\u53ea\u89c1\u8fd9\u4e24\u4e2a\u5c11\u5973\u67f3\u7709 \u7a81\u7136\u4e00\u8f69\uff0c\u77ac\u53c8\u5a07\u7b11\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u6211\u4eec\u59d0\u59b9\u6765\u5f97\u8fd9\u4e48\u5de7\uff0c\u8fd8\u8d76\u5f97\u4e0a\u770b\u5230\u8fd9\u4e48\u4e00\u4f4d\u5c11\u5e74\u82f1\u96c4\uff0c \u8fd9\u4e48\u8bf4\u6765\uff0c\u6211\u4eec\u59d0\u59b9\u66f4\u8981\u656c\u4e00\u676f\u4e86\u3002\u201d \u3000\u3000\u7acb\u5728\u53f3\u89d2\u7684\u5c11\u5973\uff0c\u7a81\u7136\u53f3\u624b\u4e00\u626c\uff0c\u201c\u94ee\u201d\u7684\u4e00\u58f0\uff0c\u5c06\u624b\u4e2d\u7684\u9752\u74f7\u676f\u5f39\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u7fa4\u8c6a\u4fbf\u89c1\u8fd9\u53ea\u9152\u676f\uff0c\u50cf\u662f\u9640\u87ba\u4f3c\u7684\uff0c\u65cb\u8f6c\u4e0d\u606f\u5730\u76f4\u98de\u5230\u90a3\u9ec4\u886b\u5c11\u5e74\u7684\u9762\u524d\uff0c\u7a81\u7136\u5212\u4e86\u4e2a \u534a\u5f27\u7ed5\u8fc7\u5c55\u5728\u4ed6\u9762\u524d\u7684\u753b\u5377\uff0c\u5ffd\u7136\u51fb\u5411\u4ed6\u9762\u988a\u4e0a\uff0c\u52bf\u9053\u867d\u6025\uff0c\u676f\u4e2d\u7684\u9152\uff0c\u5374\u672a\u6ea2\u51fa\u534a\u70b9\u3002 \u3000\u3000\u7fa4\u8c6a\u4e0d\u7981\u5931\u58f0\u559d\u8d77\u91c7\u6765\uff0c\u54ea\u77e5\u90a3\u9ec4\u886b\u5c11\u5e74\u5374\u4ecd\u7136\u52a8\u4e5f\u4e0d\u52a8\uff0c\u751f\u50cf\u662f\u6839\u672c\u6ca1\u6709\u770b\u5230\u4f3c\u7684\u3002 \u3000\u3000\u53ea\u89c1\u90a3\u5e26\u7740\u98ce\u58f0\u7684\u9152\u676f\uff0c\u5df2\u582a\u582a\u51fb\u5728\u4ed6\u9762\u988a\u4e0a\uff0c\u4ed6\u7adf\u5fae\u4e00\u4fa7\u9762\uff0c\u5f20\u53e3\u4e00\u5439\u4e00\u5438\uff0c\u90a3\u9752\u74f7 \u9152\u676f\u7adf\u50cf\u7bad\u4e5f\u4f3c\u5730\u76f4\u98de\u4e86\u56de\u6765\u3002 \u3000\u3000\u800c\u90a3\u676f\u4e2d\u7684\u9152\uff0c\u5374\u5982\u4e00\u6761\u94f6\u7ebf\u822c\uff0c\u6295\u5165\u4e86\u4ed6\u5f20\u5f00\u7684\u5634\u91cc\u3002 \u3000\u3000\u8fd9\u79cd\u532a\u5937\u6240\u601d\u7684\u529f\u529b\uff0c\u5f53\u7136\u4f7f\u5f97\u7fa4\u8c6a\u518d\u6b21\u8131\u53e3\u559d\u8d77\u91c7\u6765\u3002 \u3000\u3000\u90a3\u4e24\u4e2a\u7ea2\u88f3\u5c11\u5973\uff0c\u4e5f\u4ea6\u7389\u5bb9\u9aa4\u53d8\uff0c\u53f3\u9762\u7684\u90a3\u5c11\u5973\u7ea4\u624b\u4e00\u62db\uff0c\u5c06\u9152\u676f\u63a5\u5728\u624b\u91cc\uff0c\u5374\u89c1\u90a3\u9ec4 \u886b\u5c11\u5e74\u5df2\u957f\u7b11\u800c\u8d77\uff0c\u6717\u58f0\u7b11\u9053\uff1a\u201c\u597d\u9152\uff0c\u597d\u9152\u3002\u201d \u3000\u3000\u4e00\u9762\u53c8\u7b11\u9053\uff1a\u201c\u6208\u6208\u4e00\u753b\uff0c\u9601\u4e0b\u65e2\u7136\u4e0d\u80af\u5272\u7231\uff0c\u5c0f\u53ef\u53ea\u6709\u539f\u7269\u5949\u56de\u4e86\u3002\u201d \u3000\u3000\u8fd9\u8584\u8584\u4e00\u5f20\u7ee2\u753b\uff0c\u6b64\u523b\u5374\u50cf\u52bf\u631f\u5343\u94a9\uff0c\u90a3\u4e24\u4e2a\u7ea2\u88f3\u5c11\u5973\uff0c\u8fdc\u8fdc\u5373\u5df2\u89c9\u51fa\u98ce\u58f0\u51db\u7136\uff0c\u5979\u4fe9 \u4eba\u6b66\u529f\u867d\u4e0d\u5f31\uff0c\u5374\u4e0d\u6562\u4f38\u624b\u53bb\u63a5\u3002 \u3000\u3000\u6b64\u523b\u7b14\u4e0b\u5199\u867d\u6162\uff0c\u5f53\u65f6\u5374\u662f\u7094\u5982\u95ea\u7535\uff0c\u7728\u773c\u4e4b\u95f4\uff0c\u8fd9\u5e45\u88ab\u90a3\u9ec4\u886b\u5c11\u5e74\u4ee5\u7edd\u9876\u5185\u5bb6\u771f\u529b\u6325 \u51fa\u7684\u7ee2\u753b\uff0c\u4fbf\u5df2\u631f\u7740\u98ce\u58f0\u98de\u5230\u7ea2\u88f3\u5c11\u5973\u7684\u773c\u524d\u3002 \u3000\u3000\u6a2a\u6c5f\u91d1\u7d22\u695a\u5360\u9f99\u987b\u53d1\u7686\u5f20\uff0c\u5927\u559d\u4e86\u4e00\u58f0\uff0c\u6b63\u5f85\u6325\u638c\uff0c\u54ea\u77e5\u90a3\u4e24\u4e2a\u7ea2\u88f3\u5c11\u5973\u7a81\u7136\u54af\u54af\u4e00\u58f0 \u8f7b\u7b11\uff0c\u67f3\u8170\u4e00\u62e7\uff0c\u7adf\u50cf\u662f\u4e24\u53ea\u5f69\u51e4\uff0c\u6bd4\u7ffc\u98de\u5230\u8fd9\u5e45\u7ee2\u753b\u4e0a\u3002 \u3000\u3000\u8fd9\u5e45\u753b\u53bb\u52bf\u4ecd\u6025\uff0c\u7b14\u76f4\u5730\u98de\u5411\u5385\u5916\uff0c\u90a3\u4e24\u4e2a\u5c11\u5973\u7ea2\u88f3\u98d8\u98d8\uff0c\u7adf\u4e5f\u968f\u7740\u8fd9\u5e45\u753b\u98de\u5411\u5385\u5916\u3002 \u3000\u3000\u9ec4\u886b\u5c11\u5e74\u62d7\u638c\u5927\u7b11\u9053\uff1a\u201c\u656c\u6211\u4e00\u676f\u9152\uff0c\u8fd8\u541b\u4e00\u7247\u4e91\uff0c\u4e91\u9001\u4ed9\u5b50\u53bb\uff0c\u98ce\u5439\u4ed9\u5b50\u88d9\u3002\u201d \u3000\u3000\u6717\u541f\u58f0\u4e2d\uff0c\u8eab\u5f62\u66b4\u957f\uff0c\u5df2\u81ea\u63a0\u51fa\u5385\u5916\u3002 \u3000\u3000\u6ee1\u5385\u7fa4\u8c6a\u773c\u89c1\u8fd9\u79cd\u5947\u666f\uff0c\u8033\u95fb\u8fd9\u79cd\u6717\u541f\uff0c\u76ee\u5149\u8f6c\u5411\u5385\u5916\uff0c\u5374\u89c1\u90a3\u4e00\u7247\u201c\u5f69\u4e91\u201d\u53bb\u52bf\u867d\u7136 \u7f13\u4e86\u4e0b\u6765\uff0c\u5374\u672a\u4e0b\u843d\uff0c\u5fae\u5fae\u8f6c\u4e86\u65b9\u5411\uff0c\u771f\u7684\u751f\u50cf\u662f\u4e00\u7247\u5f69\u4e91\u4f3c\u7684\uff0c\u5728\u9662\u4e2d\u76d8\u65cb\u800c\u821e\u3002 \u3000\u3000\u9662\u4e2d\u505c\u7acb\u7740\u5341\u516d\u4e2a\u7ea2\u88f3\u5782\u9aeb\u5c11\u5973\uff0c\u6b64\u523b\u7adf\u90fd\u5a07\u7b11\u4e00\u58f0\uff0c\u4e00\u8d77\u653e\u4e0b\u624b\u4e2d\u7684\u62dc\u76d2\uff0c\u8f7b\u62e7\u67f3 \u8170\uff0c\u968f\u7740\u8fd9\u7247\u201c\u5f69\u4e91\u201d\u98d8\u98d8\u800c\u821e\uff0c\u7389\u624b\u62db\u626c\u5904\uff0c\u624b\u638c\u4e2d\u5404\u5404\u6325\u51fa\u4e00\u80a1\u52b2\u98ce\uff0c\u6258\u5f97\u8fd9\u7247\u201c\u5f69 \u4e91\u201d\u9ad8\u9ad8\u98de\u8d77\u3002 \u3000\u3000\u7fa4\u8c6a\u4f46\u89c1\u6ee1\u9662\u7ea2\u8896\u98de\u626c\u4e2d\uff0c\u4e00\u7247\u5f69\u4e91\uff0c\u62e5\u7740\u4e24\u4e2a\u4ed9\u5b50\uff0c\u5189\u5189\u51cc\u7a7a\u800c\u821e\uff0c\u65e9\u5df2\u4ff1\u90fd\u62e5\u5230\u5385 \u53e3\uff0c\u4f38\u957f\u8116\u5b50\u671b\u7740\u8fd9\u5e45\u5947\u666f\uff0c\u4e00\u4e2a\u4e2a\u53ea\u89c9\u76ee\u7729\u795e\u9a70\uff0c\u4e0d\u80fd\u81ea\u4e3b\uff0c\u5c31\u8fde\u559d\u91c7\u90fd\u5168\u7136\u5fd8\u8bb0\u4e86\u3002 \u3000\u3000\u90a3\u9ec4\u886b\u5c11\u5e74\u76ee\u5149\u56de\u626b\uff0c\u6717\u58f0\u7b11\u9053\uff1a\u201c\u597d\u4e00\u4e2a\u5f69\u4e91\u4ed9\u5b50\uff01\u201d \u3000\u3000\u811a\u5c16\u5fae\u70b9\uff0c\u7adf\u4e5f\u6251\u4e0a\u8fd9\u7247\u201c\u5f69\u4e91\u201d\uff0c\u7ea2\u88f3\u5c11\u5973\u54af\u54af\u5a07\u7b11\u4e00\u58f0\uff0c\u7a81\u7136\u6325\u51fa\u56db\u53ea\u7389\u638c\uff0c\u51fb\u5411 \u8fd9\u9ec4\u886b\u5c11\u5e74\u5c91\u7cb2\u7684\u80a9\u3001\u80f8\u3002 \u3000\u3000\u8fd9\u7247\u201c\u5f69\u4e91\u201d\u957f\u4e0d\u8fc7\u56db\u5c3a\uff0c\u5bbd\u4e0d\u8fc7\u4e24\u5c3a\uff0c\u6b64\u523b\u4e0a\u9762\u7ad9\u4e86\u4e09\u4eba\uff0c\u5df2\u662f\u95f4\u4e0d\u5bb9\u9699\uff0c\u8fd9\u4e24\u4e2a\u7ea2 \u88f3\u5c11\u5973\u5fae\u4e00\u6325\u88f3\uff0c\u4fbf\u5df2\u582a\u582a\u51fb\u5728\u8fd9\u5c11\u5e74\u5c91\u7cb2\u7684\u8eab\u4e0a\u3002 \u3000\u3000\u5c91\u7cb2\u4f46\u89c9\u6f2b\u957f\u7ea2\u8896\u5f71\u4e2d\uff0c\u56db\u53ea\u767d\u751f\u751f\u7684\u624b\u638c\uff0c\u5feb\u5982\u98d8\u51e4\u822c\u7684\u51fb\u4e86\u8fc7\u6765\uff0c\u80f8\u8179\u5fd9\u81ea\u4e00\u5438\uff0c \u638c\u5f71\u867d\u5df2\u843d\u7a7a\uff0c\u81ea\u5df1\u811a\u4e0b\u501f\u4ee5\u7740\u529b\u7684\u4e00\u7247\u5f69\u4e91\uff0c\u5374\u53c8\u5df2\u5189\u5189\u98de\u4e86\u5f00\u53bb\uff0c\u81ea\u5df1\u8f7b\u529f\u518d\u9ad8\uff0c\u5374\u4e5f \u65e0\u6cd5\u51cc\u7a7a\u800c\u7acb\uff0c\u52bf\u5fc5\u8981\u843d\u5230\u5730\u9762\u4e0a\u53bb\u3002 \u3000\u3000\u90a3\u4e24\u4e2a\u7ea2\u88f3\u5c11\u5973\u811a\u8ddf\u65cb\u5904\uff0c\u4e58\u7740\u5176\u6325\u638c\u4e4b\u52bf\uff0c\u5c06\u201c\u5f69\u4e91\u201d\u5e26\u5f00\uff0c\u98d8\u98d8\u843d\u5411\u5730\u4e0a\uff0c\u6b64\u523b\u5374 \u4e00\u8d77\u4f38\u51fa\u7389\u638c\uff0c\u53c8\u6e05\u8106\u5730\u62cd\u4e86\u4e24\u4e0b\uff0c\u6536\u8d77\u5df2\u843d\u5728\u5730\u4e0a\u7684\u7ee2\u753b\uff0c\u7acb\u5217\u5899\u89d2\uff0c\u7a81\u7136\u6162\u58f0\u4f4e\u5531\u8d77\u6765 \u2014\u2014\u90a3\u5341\u516d\u4e2a\u7ea2\u88f3\u5782\u9aeb\u5c11\u5973\uff0c\u5c31\u5728\u9ec4\u886b\u5c11\u5e74\u8eab\u5f62\u843d\u4e0b\u7684\u90a3\u4e00\u5239\u90a3\uff0c\u5404\u4e2a\u8f7b\u62cd\u7740\u7389\u624b\uff0c\u8eab\u5f62\u52a8 \u5904\uff0c\u7ea2\u8896\u98d8\u98d8\uff0c\u8863\u88d9\u98de\u626c\uff0c\u968f\u7740\u8fd9\u4e24\u4e2a\u7edd\u8272\u5c11\u5973\u7684\u6b4c\u58f0\uff0c\u8885\u632a\u8d77\u821e\uff0c\u7728\u773c\u4e4b\u95f4\uff0c\u53ea\u89c1\u6ee1\u9662\u4e2d \u7684\u7ea2\u5f71\uff0c\u5982\u6f29\u5149\u6d41\u8f6c\uff0c\u5f53\u4e2d\u5374\u88f9\u7740\u4e00\u4e2a\u6de1\u9ec4\u4eba\u5f71\uff0c\u5b9b\u5982\u96be\u5149\u4e2d\u7684\u4e00\u6839\u652f\u67f1\u3002 \u3000\u3000\u6b4c\u58f0\u66fc\u5999\uff0c\u821e\u5f71\u7fe9\u7fe9\uff0c\u5929\u4e0a\u6708\u660e\u661f\u7a00\uff0c\u9662\u4e2d\u5374\u4eae\u5982\u81ea\u663c\uff0c\u7fa4\u8c6a\u4f46\u89c9\u76ee\u7729\u795e\u9a70\uff0c\u51e0\u4e0d\u77e5\u4eba \u4e16\u4e4b\u95f4\uff0c\u4f55\u6765\u6b64\u6e05\u6b4c\u5999\u821e\u3002 \u3000\u3000\u4f46\u5728\u8fd9\u4e00\u7247\u8f7b\u7ea2\u821e\u5f62\u4e2d\u7684\u9ec4\u886b\u5c11\u5e74\uff0c\u6b64\u523b\u5374\u662f\u51e0\u7acb\u5982\u5c71\uff0c\u9762\u8272\u51dd\u91cd\uff0c\u56e0\u4e3a\u53ea\u6709\u4ed6\u77e5\u9053\uff0c \u8fd9\u4e9b\u5782\u9aeb\u5c11\u5973\uff0c\u821e\u59ff\u867d\u7136\u66fc\u5999\uff0c\u4f46\u5728\u5979\u4eec\u7ea2\u7f57\u8863\u8896\u4e2d\u7684\u4e00\u53cc\u7389\u624b\uff0c\u5374\u662f\u6bcf\u4e00\u626c\u52a8\u5904\uff0c\u5c31\u662f\u5f80 \u81ea\u5df1\u8eab\u4e0a\u81f4\u547d\u7684\u5730\u65b9\u62db\u547c\u3002 \u3000\u3000\u7487\u5149\u6bcf\u4e00\u65cb\u8f6c\uff0c\u5c31\u6709\u6570\u5341\u53ea\u7ea4\u7ea4\u7389\u624b\uff0c\u4ee5\u65e0\u6bd4\u66fc\u5999\u7684\u821e\u59ff\uff0c\u5176\u4e2d\u5374\u5939\u7740\u65e0\u6bd4\u51cc\u5389\u7684\u62db \u5f0f\uff0c\u7535\u4e5f\u4f3c\u7684\u88ad\u5411\u8fd9\u9ec4\u886b\u5c11\u5e74\u5c91\u7cb2\u7684\u8eab\u4e0a\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u811a\u4e0b\u8e29\u7740\u7ea2\u788e\u7684\u6b65\u5b50\uff0c\u8eab\u5f62\u5fae\u5fae\u626d\u8f6c\u5904\uff0c\u8fd9\u6570\u5341\u53ea\u7ea4\u7ea4\u7389\u624b\uff0c\u7adf\u8fde\u4ed6\u7684\u888d\u89d2\u90fd\u6cbe \u4e0d\u5230\u534a\u70b9\u3002 \u3000\u3000\u63d2\u5728\u5899\u4e0a\u7684\u5de8\u5927\u706b\u628a\u4e4b\u706b\u7130\uff0c\u4e0d\u505c\u7684\u6447\u821e\u7740\uff0c\u5149\u5f71\u500f\u5ffd\u4e2d\uff0c\u53ea\u89c1\u90a3\u591a\u81c2\u795e\u5251\u4e91\u8c26\uff0c\u634b\u7740 \u957f\u987b\uff0c\u7ad9\u5728\u5385\u5802\u7684\u77f3\u9636\u4e0a\uff0c\u672c\u662f\u8d64\u7ea2\u7684\u9762\u819b\uff0c\u6b64\u523b\u5374\u4eff\u4f5b\u53d8\u4e86\u989c\u8272\uff0c\u4e00\u53cc\u864e\u76ee\uff0c\u7728\u4e5f\u4e0d\u7728\u5730 \u671b\u5728\u8fd9\u4e00\u7247\u821e\u5f71\u4e0a\uff0c\u7a81\u7136\u957f\u53f9\u4e00\u58f0\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u8fd9\u7adf\u771f\u7684\u662f\u7edd\u8ff9\u6b66\u6797\u5df2\u6709\u591a\u5e74\u7684\u9713\u88f3 \u4ed9\u821e\u2014\u2014\u201d\u4ed6\u8bdd\u58f0\u672a\u843d\uff0c\u7ad9\u5728\u4ed6\u65c1\u8fb9\u7684\u7075\u72d0\u667a\u4e66\uff0c\u6a2a\u6c5f\u91d1\u7d22\u695a\u5360\u9f99\u5df2\u7136\u4e00\u8d77\u4f38\u8fc7\u5934\u6765\uff0c\u8131\u53e3 \u60ca\u547c\u9053\uff1a\u201c\u9713\u88f3\u4ed9\u821e\uff1f\uff01\u201d \u3000\u3000\u591a\u81c2\u795e\u5251\u5fae\u4e00\u9894\u9996\uff0c\u53f9\u9053\uff1a\u201c\u65b9\u624d\u6211\u770b\u8fd9\u72c2\u505a\u5c11\u5e74\u7684\u8eab\u6cd5\uff0c\u5df2\u770b\u51fa\u4ed6\u7adf\u662f\u6614\u65e5\u4e07\u5999\u771f\u4eba \u72fc\u6b65\u6c5f\u6e56\u7684\u8ff7\u8e2a\u4e03\u53d8\uff0c\u54ea\u77e5\u9053\u6b64\u523b\u8fd9\u51e0\u4e2a\u5c11\u5973\uff0c\u5374\u662f\u82d7\u7586\u90a3\u4e2a\u5973\u9b54\u5934\u7684\u4f20\u4eba\uff0c\u770b\u51fa\u6c5f\u6e56\u4e4b \u4e2d\uff0c\u5e73\u9759\u5df2\u4e45\uff0c\u5374\u53c8\u5c06\u751f\u51fa\u53d8\u4e71\u4e86\u3002\u201d \u3000\u3000\u695a\u5360\u9f99\u3001\u7075\u72d0\u667a\u4e66\uff0c\u4e0d\u7981\u4e5f\u4ff1\u90fd\u4e3a\u4e4b\u9762\u76ee\u53d8\u8272\uff0c\u6a2a\u6c5f\u91d1\u7d22\u5e72\u54b3\u4e86\u4e00\u58f0\uff0c\u4f4e\u4f4e\u9053\uff1a\u201c\u4e0d\u4f1a \u5427\uff0c\u8fd9\u4e24\u4e2a\u9b54\u5934\uff0c\u90fd\u672a\u542c\u6709\u8fc7\u4f20\u4eba\u2014\u2014\u5509\uff0c\u4e0d\u8fc7\u8fd9\u5341\u5e74\u4e4b\u4e2d\u7684\u53d8\u5316\uff0c\u53c8\u662f\u8c01\u80fd\u9884\u6d4b\u7684\u5462\uff1f\u201d \u3000\u3000\u4ed6\u957f\u53f9\u58f0\u4e2d\uff0c\u4e5f\u81ea\u627f\u8ba4\u4e86\u4e91\u8c26\u7684\u770b\u6cd5\uff0c\u4e24\u9053\u6d53\u7709\uff0c\u5374\u6df1\u6df1\u76b1\u5230\u4e00\u5904\uff0c\u4f46\u72ec\u81ea\u8bf4\u9053\uff1a\u201c\u4e0d \u8fc7\u2014\u2014\u8fd9\u51e0\u4e2a\u5c11\u5973\u7684\u8eab\u6cd5\uff0c\u867d\u7136\u50cf\u662f\u4f20\u81ea\u82d7\u7586\uff0c\u4f46\u8fd9\u9ec4\u886b\u5c11\u5e74\uff0c\u5374\u672a\u5fc5\u662f\u4e07\u5999\u771f\u4eba\u7684\u5f1f\u5b50\u2014 \u2014\u201d\u54ea\u77e5\u9662\u4e2d\u7a81\u7136\u54cd\u8d77\u4e00\u9635\u957f\u7b11\uff0c\u6253\u65ad\u4e86\u4ed6\u8fd9\u5e26\u7740\u51e0\u5206\u81ea\u6211\u5b89\u6170\u4e3a\u8bdd\uff0c\u957f\u7b11\u58f0\u4e2d\uff0c\u53ea\u89c1\u9662\u4e2d \u7684\u6de1\u7ea2\u6f29\u5149\u4e2d\u7684\u90a3\u6761\u9ec4\u886b\u4eba\u5f71\uff0c\u5df2\u662f\u51b2\u5929\u800c\u8d77\u3002 \u3000\u3000\u7b11\u58f0\u672a\u4f4f\uff0c\u8fd9\u9ec4\u886b\u5c11\u5e74\u7684\u8eab\u5f62\uff0c\u7adf\u51cc\u7a7a\u4e00\u8f6c\uff0c\u500f\u7136\u5934\u4e0b\u811a\u4e0a\uff0c\u7bad\u4e5f\u4f3c\u7684\u63a0\u4e86\u4e0b\u6765\uff0c\u94c1\u638c \u4f38\u5904\uff0c\u7535\u4e5f\u4f3c\u7684\u5288\u5411\u4e24\u4e2a\u5782\u9aeb\u5c11\u5973\u7684\u80a9\u5934\u3002 \u3000\u3000\u4f46\u8fd9\u4e24\u4e2a\u5c11\u5973\u811a\u4e0b\u5e76\u672a\u505c\u6b65\uff0c\u4f9d\u7136\u7ed5\u6b65\u800c\u821e\uff0c\u54ea\u77e5\u8fd9\u9ec4\u886b\u5c11\u5e74\u5c91\u7cb2\u7684\u8eab\u5f62\uff0c\u5728\u7a7a\u4e2d\u7adf\u80fd \u968f\u610f\u8f6c\u79fb\uff0c\u5fae\u4e00\u62e7\u8170\uff0c\u4e24\u53ea\u94c1\u638c\uff0c\u5df2\u5206\u5411\u6293\u5165\u4e24\u4e2a\u5782\u9aeb\u5c11\u5973\u98de\u626c\u7740\u7684\u8896\u91cc\u3002 \u3000\u3000\u4f46\u542c\u4e00\u58f0\u5a07\u547c\uff0c\u5c91\u7cb2\u957f\u7b11\u4e4b\u58f0\uff0c\u518d\u6b21\u5927\u4f5c\uff0c\u53cc\u817f\u5411\u540e\u75be\u4f38\uff0c\u501f\u7740\u624b\u4e0a\u7684\u8fd9\u4e00\u6293\u4e4b\u529b\uff0c\u8eab \u5f62\u53c8\u817e\u7a7a\u800c\u8d77\uff0c\u5237\u3001\u5237\uff0c\u4e24\u638c\uff0c\u5e26\u7740\u51cc\u5389\u7684\u638c\u98ce\uff0c\u6325\u5411\u53e6\u4e24\u4e2a\u5782\u9aeb\u7ea2\u88f3\u5c11\u5973\u3002 \u3000\u3000\u8fd9\u79cd\u60ca\u4eba\u7684\u8f7b\u529f\uff0c\u7acb\u523b\u5524\u5f97\u7fa4\u8c6a\u7684\u7eb7\u7eb7\u60ca\u547c\uff0c\u5341\u516d\u4e2a\u5782\u820d\u5c11\u5973\u7684\u821e\u6b65\uff0c\u4e5f\u7acb\u523b\u4e3a\u4e4b\u5927 \u4e71\u3002 \u3000\u3000\u90a3\u4e24\u4e2a\u7ea2\u88f3\u5c11\u5973\u7684\u6b4c\u58f0\uff0c\u4e5f\u81ea\u6108\u5531\u6108\u6025\uff0c\u672c\u662f\u6ee1\u9662\u65cb\u8f6c\u7740\u7684\u821e\u5f71\uff0c\u6b64\u523b\u5374\u53ea\u5269\u4e0b\u4e86\u90a3\u5c91 \u7cb2\u7684\u6de1\u9ec4\u8863\u5f71\uff0c\u6f2b\u5929\u98de\u821e\u3002 \u3000\u3000\u591a\u81c2\u795e\u5251\u6d53\u7709\u76b1\u5904\uff0c\u8f6c\u8138\u5411\u6a2a\u6c5f\u91d1\u7d22\u695a\u5360\u9f99\u4f4e\u8bed\u9053\uff1a\u201c\u666e\u5929\u4e4b\u4e0b\uff0c\u9664\u4e86\u5929\u5c71\u4e00\u8109\u4f20\u4e0b\u7684 \u4e03\u79bd\u8eab\u6cd5\u3001\u98de\u9f99\u4e94\u5f0f\uff0c\u548c\u9752\u5e74\u661f\u6708\u53cc\u5251\u72ec\u6b65\u6b66\u6797\u7684\u2018\u82cd\u7a79\u5341\u4e09\u5f0f\u2019\u5916\uff0c\u80fd\u591f\u51cc\u7a7a\u51fb\u654c\uff0c\u800c\u80fd \u501f\u52bf\u817e\u8d8a\u7684\uff0c\u53ea\u6709\u4e07\u5999\u771f\u4eba\u501f\u4ee5\u626c\u540d\u5929\u4e0b\u7684\u8ff7\u8e2a\u4e03\u53d8\u4e2d\u7684\u82cd\u9e70\u53d8\u4e86\uff0c\u695a\u5144\uff0c\u73b0\u5728\u4f60\u8be5\u4e5f\u770b\u51fa \u8fd9\u5c11\u5e74\u662f\u5426\u90a3\u9b54\u5934\u7684\u4f20\u4eba\u4e86\u5427\uff1f\u201d \u3000\u3000\u695a\u5360\u9f99\u957f\u53f9\u4e00\u58f0\uff0c\u65b9\u5f85\u7b54\u8bdd\uff0c\u5374\u89c1\u90a3\u4e24\u4e2a\u66fc\u6b4c\u7740\u7684\u7ea2\u88f3\u5c11\u5973\uff0c\u7a81\u7136\u7389\u638c\u8f7b\u62cd\uff0c\u6b4c\u58f0\u590f\u7136 \u800c\u4f4f\u3002 \u3000\u3000\u5782\u9aeb\u5c11\u5973\u4eec\u7684\u821e\u6b65\u672c\u5df2\u4e03\u96f6\u516b\u843d\uff0c\u6b4c\u58f0\u4e00\u4f4f\uff0c\u8fd9\u4e9b\u5782\u88f3\u5c11\u5973\u4eec\u7684\u8eab\u5f62\uff0c\u4fbf\u7acb\u523b\u56db\u4e0b\u6563 \u5f00\uff0c\u5176\u4e2d\u6709\u51e0\u4eba\u8f7b\u9891\u9edb\u7709\uff0c\u6697\u54ac\u6731\u5507\uff0c\u6367\u7740\u7389\u7897\uff0c\u663e\u89c1\u624b\u8155\u5df2\u7ecf\u53d7\u4e86\u4f24\uff0c\u53ea\u662f\u9ec4\u886b\u5c11\u5e74\u4f3c\u4e4e \u751a\u4e3a\u601c\u9999\u60dc\u7389\uff0c\u4e0b\u624b\u5e76\u4e0d\u91cd\uff0c\u662f\u4ee5\u5979\u4eec\u4f24\u5f97\u5e76\u4e0d\u5389\u5bb3\u7f62\u4e86\u3002 \u3000\u3000\u9ec4\u886b\u5c11\u5e74\u5c91\u7cb2\u76ee\u5149\u505a\u7136\u56db\u626b\uff0c\u8f7b\u8f7b\u4e00\u62c2\u8863\u8882\uff0c\u53c8\u590d\u6717\u7b11\u9053\uff1a\u201c\u6c5f\u5357\u6625\u591c\uff0c\u4ed9\u5b50\u5b50\u6563\u82b1\uff0c \u518d\u52a0\u4e0a\u8fd9\u4e24\u4f4d\u7edd\u4ee3\u4f73\u4eba\u7684\u6e05\u6b4c\u66fc\u5531\uff0c\u771f\u662f\u9ad8\u6b4c\u5999\u821e\uff0c\u53cc\u7edd\u4eba\u95f4\uff0c\u4e0d\u60f3\u533a\u533a\u4eca\u65e5\uff0c\u5374\u4e5f\u8eac\u4e34\u6b64 \u76db\u60c5\uff0c\u5f00\u4e86\u8fd9\u7b49\u773c\u754c\u3002\u201d \u3000\u3000\u90a3\u4e24\u4e2a\u7ea2\u88f3\u5c11\u5973\uff0c\u4e5f\u81ea\u5a07\u7b11\u4e00\u58f0\uff0c\u4f38\u51fa\u7389\u624b\uff0c\u8f7b\u8f7b\u63a9\u4f4f\u5e26\u7b11\u7684\u5634\u89d2\uff0c\u8885\u5a1c\u5730\u8d70\u4e86\u8fc7\u6765\uff0c \u53e3\u4e2d\u5a07\u58f0\u7b11\u9053\uff1a\u201c\u54ce\u54df\uff0c\u60a8\u600e\u4e48\u8fd9\u6837\u5ba2\u6c14\uff0c\u6211\u4eec\u59d0\u59b9\u8fd9\u526f\u7c97\u5589\u5499\u3001\u7834\u55d3\u5b50\u5531\u51fa\u6765\u7684\u4e1c\u897f\uff0c\u8fd8 \u8bf4\u662f\u6e05\u6b4c\u66fc\u5531\u54e9\uff0c\u8fd9\u53ef\u771f\u6559\u6211\u4eec\u4e0d\u597d\u610f\u601d\u3002\u201d \u3000\u3000\u5a07\u7b11\u58f0\u4e2d\uff0c\u63a9\u7740\u5634\u89d2\u7684\u7389\u624b\uff0c\u7a81\u7136\u95ea\u7535\u4f3c\u5730\u5f80\u5916\u4e00\u4f38\uff0c\u5341\u53ea\u738b\u8471\u822c\u7684\u7389\u6307\uff0c\u6b64\u523b\u7ade\u6709\u5982 \u5229\u5203\uff0c\u75be\u7136\u70b9\u5411\u8fd9\u9ec4\u886b\u5c11\u5e74\u9762\u4e0a\u7684\u95fb\u9999\u3001\u56db\u767d\u3001\u5730\u4ed3\u3001\u4e0b\u5173\u548c\u5de6\u80a9\u7684\u80a9\u4e95\u3001\u80a9\u8d1e\u516d\u5904\u5927\u7a74\uff0c \u8ba4\u7a74\u4e4b\u51c6\uff0c\u65e0\u4e0e\u4f26\u6bd4\u3002 \u3000\u3000\u8fd9\u4e00\u4e0b\u4e0d\u4f46\u7a81\u5140\u5176\u6765\uff0c\u800c\u4e14\u6765\u52bf\u5982\u98ce\uff0c\u773c\u770b\u8fd9\u53ea\u7ea4\u7ea4\u7389\u6307\uff0c\u5df2\u662f\u89e6\u5230\u8fd9\u9ec4\u886b\u5c11\u5e74\u7684\u7a74\u9053 \u4e0a\uff0c\u6ee1\u9662\u7fa4\u8c6a\u60ca\u5531\u4e00\u58f0\uff0c\u4e0d\u7981\u90fd\u5728\u5fc3\u4e2d\u6697\u5fd6\u9053\uff1a\u201c\u8fd9\u4e24\u4e2a\u5c11\u5973\u597d\u5feb\u4e3a\u8eab\u624b\uff0c\u597d\u72e0\u7684\u5fc3\u80a0\uff0c\u7adf \u5728\u8c08\u7b11\u4e4b\u4e2d\uff0c\u90fd\u80fd\u5236\u4eba\u6b7b\u547d\u3002\u201d \u3000\u3000\u54ea\u77e5\u9ec4\u886b\u5c11\u5e74\u770b\u4f3c\u731d\u4e0d\u53ca\u9632\uff0c\u5176\u5b9e\u5374\u662f\u6210\u7af9\u5728\u80f8\uff0c\u500f\u53c8\u957f\u7b11\u9053\uff1a\u201c\u6211\u975e\u7ef4\u6469\u4ed9\uff0c\u96be\u5f53\u6563 \u82b1\u624b\uff0c\u4e24\u4f4d\u59d1\u5a18\u7684\u76db\u60c5\uff0c\u5728\u4e0b\u4e0d\u6562\u5f53\u7684\u5f88\u3002\u201d \u3000\u3000\u957f\u7b11\u4e2d\uff0c\u8eab\u5f62\u5df2\u81ea\u6ed1\u5f00\u4e94\u5c3a\uff0c\u8fd9\u4e24\u4e2a\u7ea2\u88f3\u5c11\u5973\u7684\u4e24\u53ea\u7389\u624b\uff0c\u4fbf\u53c8\u843d\u7a7a\u3002 \u3000\u3000\u4ec1\u7acb\u9636\u524d\u7684\u591a\u81c2\u795e\u5251\u4e91\u8c26\u59cb\u7ec8\u76b1\u7740\u53cc\u7709\uff0c\u6b64\u523b\u957f\u53f9\u4e00\u58f0\u53c8\u9053\uff1a\u201c\u6b64\u5341\u5e74\u4e4b\u4e2d\uff0c\u770b\u6765\u90a3\u4e07 \u5999\u9b54\u5934\uff0c\u529f\u529b\u4e0d\u77e5\u53c8\u52a0\u6df1\u4e86\u51e0\u8bb8\uff0c\u7adf\u8fde\u4ed6\u7684\u8fd9\u4e2a\u5f1f\u5b50\uff0c\u6b66\u529f\u5df2\u4e0d\u5728\u5f53\u5e74\u4e43\u5e08\u4e4b\u4e0b\uff0c\u7adf\u8fde\u88f3\u4ed9 \u821e\u9635\u90fd\u96be\u4e0d\u5012\u4ed6\u4e86\uff0c\u5509\u2014\u2014\u5341\u5e74\u5c81\u6708\uff0c\u672c\u975e\u7b49\u95f2\uff0c\u53ea\u662f\u6211\u90a3\u6d69\u7136\u8001\u5f1f\u5462\uff1f\u600e\u4e48\u4e00\u53bb\u65e0\u8e2a\uff0c\u4f60 \u662f\u5426\u4e5f\u7ec3\u6210\u4e86\u51e0\u6837\u7edd\u6280\u5440\uff1f\u201d \u3000\u3000\u8fd9\u80f8\u6000\u78ca\u843d\u7684\u8001\u4eba\uff0c\u4e0d\u7981\u6cb9\u7136\u6ca7\u6000\uff0c\u76ee\u5149\u4e00\u62ac\uff0c\u53ea\u89c1\u9662\u4e2d\u638c\u5f71\u7ffb\u98de\uff0c\u638c\u98ce\u547c\u547c\uff0c\u90a3\u4e24\u4e2a \u7ea2\u88f3\u5c11\u5973\u5728\u8fd9\u77ac\u606f\u4e4b\u95f4\uff0c\u7adf\u4e5f\u8fde\u653b\u4e86\u6570\u5341\u62db\uff0c\u53ea\u662f\u5c91\u7cb2\u8eab\u5f62\u95ea\u52a8\uff0c\u52a8\u5982\u98d8\u98ce\uff0c\u867d\u7136\u5e76\u672a\u4f7f\u51fa \u5168\u529b\uff0c\u4f46\u5374\u5e94\u4ed8\u5f97\u4ece\u5bb9\u5df2\u6781\u3002 \u3000\u3000\u8fd9\u4e24\u4e2a\u7ea2\u88f3\u5c11\u5973\u5fc3\u4e2d\u4e0d\u7981\u6697\u9a87\uff0c\u5bf9\u624b\u6b66\u529f\u4e4b\u5f3a\uff0c\u8fdc\u8fdc\u51fa\u4e4e\u4e86\u5979\u4eec\u7684\u610f\u6599\u4e4b\u5916\uff0c\u5c24\u5176\u66f4\u4ee4 \u5979\u4eec\u7740\u6025\u7684\u662f\uff0c\u5bf9\u65b9\u5e94\u654c\u867d\u4f3c\u6f47\u6d12\uff0c\u4f46\u51fa\u624b\u5374\u72e0\u8fa3\u5df2\u6781\uff0c\u81ea\u5df1\u59d0\u59b9\u4e24\u4eba\u591a\u5e74\u82e6\u7ec3\u7684\u8fde\u51fb\u4e4b \u52bf\uff0c\u7adf\u88ab\u8fd9\u5c11\u5e74\u4e3e\u624b\u6295\u8db3\u95f4\u7834\u53bb\uff0c\u4ed6\u4e00\u7247\u6de1\u9ec4\u7684\u8eab\u5f71\uff0c\u7adf\u751f\u50cf\u662f\u505c\u7559\u5728\u81ea\u5df1\u59d0\u59b9\u4e24\u4eba\u4e4b\u95f4\uff0c \u4f46\u81ea\u5df1\u4e00\u638c\u51fb\u53bb\uff0c\u5374\u53c8\u603b\u662f\u51fb\u7a7a\u3002 \u3000\u3000\u8fd9\u4e24\u4e2a\u7ea2\u88f3\u5c11\u5973\u867d\u7136\u624b\u6325\u4e94\u6307\uff0c\u76ee\u9001\u98de\u9e3f\uff0c\u8eab\u6cd5\u4e4b\u66fc\u5999\uff0c\u4ee4\u5f97\u6ee1\u9662\u7fa4\u8c6a\u5fc3\u4e2d\u65e2\u60ca\u4e14\u4f69\uff0c \u4f46\u662f\u5979\u4eec\u6b64\u523b\u5374\u5df2\u662f\u5fc3\u4e2d\u6709\u6570\uff0c\u77e5\u9053\u81ea\u5df1\u7edd\u975e\u8fd9\u9ec4\u886b\u5c11\u5e74\u7684\u654c\u624b\u3002 \u3000\u3000\u9ec4\u886b\u5c11\u5e74\u6717\u7b11\u4e00\u58f0\uff0c\u8eab\u5f62\u8f6c\u79fb\u5904\uff0c\u907f\u5f00\u4e86\u5de6\u9762\u5c11\u5973\u7684\u4e00\u62db\uff0c\u5de6\u638c\u201c\u547c\u201d\u7684\u4e00\u51fb\uff0c\u8eab\u5f62\u5374 \u8f6c\u5230\u53f3\u9762\u5c11\u5973\u8eab\u4fa7\uff0c\u542b\u7b11\u4f4e\u8bed\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4f60\u8fd9\u53c8\u662f\u4f55\u82e6\u5462\uff1f\u7d2f\u574f\u4e86\u8eab\u5b50\uff0c\u53eb\u5728\u4e0b\u4e5f\u770b\u7740\u96be \u53d7\u3002\u201d \u3000\u3000\u53f3\u9762\u8fd9\u5c11\u5973\u68a8\u7a9d\u5fae\u73b0\uff0c\u5a07\u58f0\u4e00\u7b11\uff0c\u4e5f\u81ea\u4fcf\u58f0\u9053\uff1a\u201c\u8c22\u8c22\u60a8\u5450\u3002\u201d \u3000\u3000\u7ea4\u8170\u8f6c\u626d\uff0c\u5de7\u7b11\u5b9c\u4eba\uff0c\u5410\u6c14\u5982\u5170\uff0c\u4f46\u5c31\u5728\u8fd9\u5de7\u7b11\u4fcf\u8bed\u4e2d\uff0c\u4e00\u53cc\u7389\u624b\uff0c\u5374\u5df2\u62a2\u51fa\u5982\u98ce\uff0c\u968f \u7740\u7ea4\u8170\u7684\u4e00\u626d\uff0c\u4e00\u53ea\u7389\u8db3\uff0c\u4e5f\u81ea\u8e22\u51fa\uff0c\u7728\u773c\u4e4b\u95f4\uff0c\u7adf\u653b\u51fa\u4e09\u62db\u3002 \u3000\u3000\u5c91\u7cb2\u54c8\u54c8\u5927\u7b11\uff0c\u8eab\u5f62\u5982\u884c\u4e91\u6d41\u6c34\u822c\u53c8\u81ea\u6ed1\u5f00\uff0c\u53e3\u4e2d\u7b11\u7740\u9053\uff1a\u201c\u597d\u72e0\u7684\u4e2b\u5934\u3002\u201d \u3000\u3000\u888d\u8896\u8fde\u5c55\uff0c\u90a3\u4e24\u4e2a\u7ea2\u88f3\u5c11\u5973\uff0c\u53ea\u89c9\u5f3a\u52b2\u7684\u638c\u98ce\uff0c\u6392\u5c71\u5012\u6d77\u822c\u5411\u81ea\u5df1\u538b\u4e86\u4e0b\u6765\uff0c\u4e24\u4eba\u773c\u73e0 \u4e00\u8f6c\uff0c\u5bf9\u671b\u4e00\u773c\uff0c\u7a81\u7136\u5a07\u8eaf\u540c\u65f6\u4e00\u8f6c\uff0c\u54af\u54af\u4e00\u7b11\uff0c\u5de6\u638c\u643a\u4f4f\u5de6\u638c\uff0c\u53f3\u638c\u9f50\u5f80\u5916\u4e00\u63a8\uff0c\u8eab\u5f62\u5374 \u501f\u7740\u8fd9\u4e00\u63a8\u4e4b\u52bf\uff0c\u60ca\u9e3f\u822c\u9000\u5230\u5899\u89d2\u3002 \u3000\u3000\u7fa4\u8c6a\u65b9\u81ea\u4e00\u6115\uff0c\u54ea\u77e5\u8fd9\u4e24\u4e2a\u7ea2\u88f3\u5c11\u5973\u7ade\u53c8\u63a9\u53e3\u4e00\u7b11\u5a07\u58f0\u9053\uff1a\u201c\u6211\u4eec\u7d2f\u4e86\uff0c\u4e0d\u6253\u4e86\uff0c\u4f60\u8981 \u6253\u5c31\u4e00\u4e2a\u4eba\u6253\u5427\u3002\u201d \u3000\u3000\u5899\u4e0a\u7684\u706b\u628a\uff0c\u5df2\u70e7\u8fd1\u5c3e\u7aef\uff0c\u706b\u7130\u5374\u4f3c\u8f83\u524d\u66f4\u5f3a\uff0c\u95ea\u52a8\u7740\u7684\u5149\u5f71\uff0c\u804c\u5728\u8fd9\u53cc\u7ea2\u88f3\u5c11\u5973\u7684\u9762 \u4e0a\uff0c\u53ea\u89c1\u5979\u4eec\u5634\u89d2\u5e26\u7740\u6d45\u7b11\uff0c\u773c\u6ce2\u56db\u4e0b\u6d41\u52a8\uff0c\u5c31\u50cf\u662f\u5782\u9aeb\u7684\u73a9\u7ae5\uff0c\u548c\u7537\u4f34\u9a91\u9752\u7af9\u9a6c\u8dd1\u7d2f\u4e86\uff0c \u628a\u7af9\u7aff\u4e00\u4e22\uff0c\u5c31\u4e0d\u6765\u4e86\u4f3c\u7684\u3002 \u3000\u3000\u53c8\u50cf\u662f\u73a9\u6293\u7c73\u888b\u73a9\u8f93\u4e86\uff0c\u5c31\u5c06\u7c73\u888b\u4e00\u4e22\uff0c\u6492\u5a07\u6492\u8d56\u7684\u6837\u5b50\uff0c\u5374\u54ea\u91cc\u50cf\u662f\u6b66\u6797\u9ad8\u624b\u6bd4\u6597\u540e \u7684\u795e\u60c5\uff0c\u6ee1\u9662\u7fa4\u8c6a\u76ee\u5b9a\u53e3\u5446\uff0c\u5fc3\u4e2d\u5374\u5728\u6697\u7b11\uff0c\u671b\u7740\u90a3\u9ec4\u886b\u5c11\u5e74\uff0c\u770b\u4ed6\u7a76\u7adf\u5982\u4f55\u5bf9\u4ed8\u8fd9\u5a07\u61a8\u5929 \u771f\uff0c\u5374\u53c8\u5201\u86ee\u72e0\u8fa3\u7684\u5c11\u5973\u3002 \u3000\u3000\u6b64\u523b\u53c8\u6709\u5341\u6570\u4e2a\u7a7f\u7740\u957f\u886b\u7684\u5927\u6c49\uff0c\u9760\u7740\u5899\u811a\u4fef\u9996\u6025\u884c\uff0c\u6362\u4e0b\u5df2\u5c06\u71c3\u5c3d\u7684\u706b\u628a\uff0c\u90a3\u4e24\u4e2a\u7ea2 \u88f3\u5c11\u5973\uff0c\u5374\u5728\u5899\u811a\u4e0b\uff0c\u7406\u7740\u4e91\u9b13\uff0c\u6574\u7740\u7f57\u88f3\uff0c\u504c\u5927\u7684\u4e00\u4e2a\u9662\u5b50\u91cc\uff0c\u5c31\u53ea\u5269\u4e0b\u90a3\u9ec4\u886b\u5c11\u5e74\u4e00\u4eba \u7ad9\u5728\u4e2d\u592e\uff0c\u76ee\u5149\u56db\u4e0b\u8f6c\u52a8\uff0c\u4f3c\u4e4e\u4e5f\u4e0d\u77e5\u9053\u8be5\u5982\u4f55\u5e94\u4ed8\u8fd9\u4e24\u4f4d\u5201\u86ee\u5c11\u5973\u3002 \u3000\u3000\u5341\u516d\u4e2a\u95ea\u7740\u91d1\u5149\u7684\u62dc\u76d2\uff0c\u4ecd\u4e00\u6392\u6392\u5728\u9636\u524d\uff0c\u5374\u662f\u90a3\u4e24\u4e2a\u7ea2\u88f3\u5c11\u5973\uff0c\u7a81\u7136\u8f7b\u8f7b\u4e00\u7b11\uff0c\u8885\u5a1c \u884c\u81f3\u62dc\u76d2\u4e4b\u524d\uff0c\u5a07\u58f0\u9053\uff1a\u201c\u6211\u4eec\u59d0\u59b9\u4e24\u4e2a\u7279\u6765\u7ed9\u4e91\u8001\u7237\u5b50\u62dc\u5bff\uff0c\u6ca1\u60f3\u5230\u5374\u7ed9\u4e91\u8001\u7237\u5e26\u6765\u8fd9\u4e48 \u591a\u9ebb\u70e6\uff0c\u6211\u4eec\u672c\u6765\u8fd8\u60f3\u5728\u8fd9\u513f\u591a\u803d\u4e00\u4f1a\u513f\uff0c\u53ef\u662f\u53c8\u6015\u5c0f\u59d0\u7b49\u7740\u6025\u4e86\u2014\u2014\u201d\u8bf4\u7740\uff0c\u53c8\u6df1\u6df1\u4e00 \u798f\uff0c\u5a07\u7b11\u9053\uff1a\u201c\u6211\u4eec\u59d0\u59b9\u5c31\u6b64\u544a\u9000\u4e86\u3002\u201d\u67f3\u8170\u4e00\u6298\uff0c\u4e5f\u4e0d\u7b49\u4e91\u8c26\u7b54\u8bdd\uff0c\u5c31\u8f6c\u9996\u8d70\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u9ec4\u886b\u5c11\u5e74\u5c91\u7cb2\u5251\u7709\u4e00\u8f69\uff0c\u6a2a\u8de8\u4e00\u6b65\uff0c\u5374\u89c1\u8fd9\u4e24\u4e2a\u5c11\u5973\u7adf\u53c8\u7b11\u9053\uff1a\u201c\u60a8\u6b66\u529f\u65e2\u9ad8\uff0c\u957f\u5f97\u53c8 \u82f1\u4fca\uff0c\u5343\u4e07\u522b\u5fd8\u4e86\u5728\u516b\u6708\u4e2d\u79cb\u4e4b\u524d\uff0c\u5230\u5929\u76ee\u5c71\u53bb\u4e00\u8d9f\uff0c\u8bf4\u4e0d\u5b9a\u2014\u2014\u201d\u63a9\u53e3\u4e00\u7b11\uff1a\u201c\u60a8\u5c06\u6765\u5c31 \u662f\u6211\u4eec\u5bb6\u5c0f\u59d0\u7684\u65b0\u59d1\u7237\u54e9\u3002\u201d \u3000\u3000\u8fd9\u4e24\u4e2a\u5c11\u5973\u5de7\u7b11\u5b9c\u4eba\uff0c\u5a07\u8bed\u5982\u73e0\uff0c\u9ec4\u886b\u5c11\u5e74\u5c91\u7cb2\u773c\u73e0\u8f6c\u4e86\u51e0\u8f6c\uff0c\u7a81\u53c9\u653e\u58f0\u957f\u7b11\u9053\uff1a \u201c\u597d\uff0c\u597d\uff0c\u5728\u4e0b\u4e00\u5b9a\u9075\u547d\u8d74\u7ea6\uff0c\u4e0d\u8fc7\u82e5\u662f\u4f60\u5bb6\u5c0f\u59d0\u4e5f\u50cf\u4e24\u4e2a\u59d1\u5a18\u8fd9\u4e48\u72e0\u5fc3\uff0c\u5728\u4e0b\u5374\u5148\u5c31\u6709\u70b9 \u80c6\u5bd2\u4e86\u3002\u201d \u3000\u3000\u957f\u7b11\u58f0\u4e2d\uff0c\u76ee\u5149\u5728\u6ee1\u9662\u7fa4\u8c6a\u9762\u4e0a\u4e00\u626b\uff0c\u7a81\u7136\u98de\u8d77\u8eab\u5f62\uff0c\u5982\u71d5\u63a0\u8d77\u3002\u9b3c\u5f71\u513f\u4e54\u8fc1\u4e00\u76f4\u7ad9\u5728 \u5385\u524d\u9636\u4e0a\uff0c\u6b64\u523b\u770b\u5230\u7ea2\u88f3\u5c11\u5973\u4eec\u8981\u8d70\u4e86\uff0c\u5fae\u64a9\u8863\u89d2\uff0c\u8d70\u4e86\u4e0b\u6765\uff0c\u54ea\u77e5\u773c\u524d\u7a81\u7136\u4e00\u82b1\uff0c\u201c\u556a\u201d \u7684\u4e00\u58f0\uff0c\u9762\u988a\u4e0a\u7adf\u88ab\u4eba\u6e05\u8106\u7684\u6253\u4e86\u4e00\u638c\uff0c\u4ed6\u60ca\u53f1\u4e00\u58f0\uff0c\u5374\u89c1\u4e00\u6761\u9ec4\u5f71\uff0c\u5df2\u5e26\u7740\u957f\u7b11\u4e5f\u4f3c\u5730\u63a0 \u51fa\u5899\u53bb\uff0c\u7728\u773c\u4e4b\u95f4\uff0c\u4fbf\u6d88\u5931\u8e2a\u8ff9\u3002 \u3000\u3000\u4e54\u8fc1\u867d\u4ee5\u8f7b\u529f\u9a70\u8a89\u6c5f\u6e56\uff0c\u4f46\u7b49\u5230\u53d1\u89c9\u8fd9\u6761\u4eba\u5f71\u65f6\uff0c\u4eba\u5bb6\u5374\u65e9\u5df2\u9038\u53bb\u65e0\u8e2a\u4e86\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c \u4ed6\u60ca\u6115\u7ad9\u5728\u9662\u4e2d\uff0c\u8138\u4e0a\u7531\u9752\u8f6c\u7ea2\uff0c\u7ec8\u4e8e\u957f\u53f9\u4e00\u58f0\uff0c\u4e00\u8dfa\u8db3\uff0c\u4e5f\u81ea\u63a0\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u4ec1\u4e49\u5251\u5ba2\u4e91\u4e2d\u7a0b\u4e00\u4e2a\u7bad\u6b65\uff0c\u7a9c\u4e86\u8fc7\u6765\uff0c\u53e3\u4e2d\u6025\u558a\u9053\uff1a\u201c\u4e54\u4e09\u54e5\uff0c\u4e54\u4e09\u54e5\u2026\u2026\u201d \u3000\u3000\u4f46\u4e54\u8fc1\u7f9e\u6012\u4e4b\u4e0b\uff0c\u8fde\u5934\u90fd\u672a\u56de\uff0c\u811a\u5c16\u5728\u9662\u5899\u4e0a\u4e00\u70b9\uff0c\u8eab\u5f62\u4f7f\u4e5f\u6d88\u5931\u5728\u82cd\u832b\u591c\u8272\u91cc\u3002 \u3000\u3000\u9b3c\u5f71\u513f\u4e54\u8fc1\u4e00\u751f\u884c\u8d70\u6c5f\u6e56\uff0c\u4eba\u7f18\u4e4b\u597d\uff0c\u6b66\u6797\u4e2d\u65e0\u51fa\u5176\u53f3\u8005\uff0c\u6b64\u523b\u53d7\u4e86\u8fd9\u79cd\u5c48\u8fb1\uff0c\u6ee1\u9662\u7fa4 \u8c6a\uff0c\u60e7\u90fd\u4e3a\u4e4b\u53f9\u606f\u4e0d\u5df2\u3002 \u3000\u3000\u90a3\u4e24\u4e2a\u7ea2\u88f3\u5c11\u5973\u5bf9\u671b\u4e86\u4e00\u773c\uff0c\u8f7b\u79fb\u83b2\u6b65\uff0c\u7f13\u7f13\u8d70\u51fa\u95e8\u5916\uff0c\u90a3\u5341\u516d\u4e2a\u6367\u7740\u91d1\u76d2\u7684\u5782\u8f94\u5973 \u7ae5\uff0c\u4e00\u6392\u8ddf\u5728\u8eab\u540e\uff0c\u591a\u81c2\u795e\u5251\u957f\u53f9\u4e00\u58f0\uff0c\u5927\u8e0f\u6b65\u8d70\u5230\u95e8\u53e3\uff0c\u5374\u89c1\u5979\u4eec\u5df2\u81ea\u8de8\u4e0a\u4e86\u56db\u8f86\u6f06\u7740\u7ea2 \u6f06\u7684\u534e\u4e3d\u9a6c\u8f66\uff0c\u9a6c\u8f66\u7684\u8f66\u95e8\uff0c\u90fd\u5df2\u5173\u4e0a\u4e86\u3002 \u3000\u3000\u8f66\u58f0\u4e00\u8d77\uff0c\u8fd9\u56db\u8f86\u9a6c\u8f66\u4fbf\u9a70\u51fa\u5df7\u5916\uff0c\u591a\u81c2\u795e\u5251\u671b\u7740\u8f66\u8f6e\u5728\u5730\u4e0a\u626c\u8d77\u7684\u7070\u5c18\uff0c\u5e72\u54b3\u4e00\u58f0\uff0c \u5fc3\u4e2d\u61ca\u607c\u4e0d\u5df2\u3002 \u3000\u3000\u4ed6\u8d1f\u624b\u8d70\u4eba\u9662\u4e2d\uff0c\u53ea\u89c9\u6ee1\u9662\u7fa4\u8c6a\uff0c\u6b63\u81ea\u4e09\u4e09\u4e24\u4e24\uff0c\u805a\u9996\u4f4e\u8bed\uff0c\u7075\u72d0\u667a\u4e66\u548c\u6a2a\u6c5f\u91d1\u7d22\u5e76\u80a9 \u884c\u6765\uff0c\u4f3c\u4e4e\u60f3\u8bf4\u51e0\u53e5\u6170\u89e3\u8fd9\u5bff\u7fc1\u7684\u8bdd\uff0c\u4f46\u5374\u4e5f\u4e0d\u77e5\u8be5\u600e\u4e48\u8bf4\u597d\uff0c\u65e0\u8bba\u4efb\u4f55\u4eba\uff0c\u5728\u81ea\u5df1\u5bff\u8bde\u4e4b \u671f\uff0c\u9047\u5230\u8fd9\u79cd\u4e0d\u987a\u5fc3\u7684\u4e8b\uff0c\u5c31\u7b97\u4ed6\u5fc3\u6000\u8c41\u8fbe\u4e5f\u96be\u514d\u61ca\u607c\u3002 \u3000\u3000\u4ec1\u4e49\u5251\u5ba2\u4e91\u4e2d\u7a0b\u671b\u89c1\u4ed6\u7239\u7239\u9762\u4e0a\u7684\u795e\u8272\uff0c\u54c8\u54c8\u5f3a\u7b11\u9053\uff1a\u9152\u83dc\u867d\u51b7\uff0c\u4ecd\u53ef\u91cd\u6e29\uff0c\u5404\u4f4d\u4e0d\u59a8 \u518d\u8bf7\u8fdb\u5385\u6765\uff0c\u7545\u996e\u51e0\u676f\uff0c\u6b64\u523b\u5df2\u8fd1\u5929\u660e\uff0c\u6211\u4eec\u8fd9\u771f\u662f\u591c\u996e\u8fbe\u65e6\u4e86\u3002\u201c\u7fa4\u8c6a\u54c4\u7136\u4e00\u58f0\uff0c\u53c8\u590d\u805a \u4eba\u4e86\u5927\u5385\uff0c\u4e91\u8c26\u76ee\u5149\u56db\u8f6c\uff0c\u5fae\u54bd\u9053\uff1a\u201c\u957f\u6c5f\u540e\u6d6a\u63a8\u524d\u6d6a\uff0c\u4e00\u8f88\u65b0\u4eba\u6362\u65e7\u4eba\uff0c\u5509\u2014\u2014\u667a\u5144\u695a \u5144\uff0c\u4f60\u6211\u771f\u7684\u662f\u8001\u4e86\uff0c\u4e0d\u4e2d\u7528\u4e86\uff0c\u770b\u770b\u65b9\u624d\u90a3\u51e0\u4e2a\u5c11\u5e74\u7684\u8eab\u624b\uff0c\u4eca\u65e5\u6c5f\u6e56\uff0c\u6050\u6015\u5c31\u5c06\u662f\u4ed6\u4eec \u7684\u5929\u4e0b\u4e86\u3002\u201d \u3000\u3000\u8a00\u4e0b\u4e0d\u80dc\u550f\u5618\u3002 \u3000\u3000\u7075\u72d0\u667a\u4e66\u7f13\u7f13\u6b65\u4e0a\u53f0\u9636\uff0c\u5374\u7b11\u9053\uff1a\u201c\u4e91\u8001\u54e5\uff0c\u4e0d\u662f\u5c0f\u5f1f\u81ea\u5938\uff0c\u4f60\u6211\u5e74\u7eaa\u867d\u8001\uff0c\u7b4b\u9aa8\u8fd8\u672a \u8001\u54e9\u3002\u771f\u9047\u7740\u4e8b\uff0c\u4ecd\u53ef\u4e0e\u8fd9\u822c\u513f\u8f88\u4e00\u8f83\u8eab\u624b\uff0c\u4e91\u8001\u54e5\uff0c\u4f60\u53c8\u4f55\u5fc5\u957f\u4ed6\u4eec\u7684\u5fd7\u6c14\u5462\uff1f\u201d \u3000\u3000\u6a2a\u6c5f\u91d1\u7d22\u6d53\u7709\u6df1\u76b1\uff0c\u4ea6\u81ea\u5fae\u5531\u9053\uff1a\u201c\u667a\u5144\u4e4b\u8bdd\u867d\u4e0d\u9519\uff0c\u4f46\u90a3\u9ec4\u886b\u59d3\u5c91\u5c11\u5e74\u7684\u6b66\u529f\uff0c\u8001\u592b \u884c\u8d70\u6c5f\u6e56\u591a\u5e74\uff0c\u5012\u8fd8\u771f\u672a\u89c1\u8fc7\uff0c\u5c31\u7b97\u6614\u5e74\u4e2d\u539f\u5927\u4fa0\u5353\u5927\u7237\u7684\u5168\u76db\u4e4b\u65f6\uff0c\u8eab\u624b\u4e5f\u4e0d\u8fc7\u548c\u4ed6\u5728\u4f2f \u4ef2\u4e4b\u95f4\uff0c\u5176\u4f59\u7684\u4eba\uff0c\u66f4\u4e0d\u8db3\u8bba\u4e86\u3002\u201d \u3000\u3000\u591a\u81c2\u795e\u5251\u957f\u7709\u864e\u76ee\u4e00\u8f69\uff0c\u54ea\u77e5\u5385\u524d\u5c4b\u6a90\u4e0b\uff0c\u7a81\u7136\u7f13\u7f13\u8d70\u51fa\u4e00\u4e2a\u4eba\u6765\uff0c\u6717\u58f0\u9053\uff1a\u201c\u65b9\u624d\u90a3 \u72c2\u50b2\u5c11\u5e74\u6b66\u529f\u867d\u9ad8\uff0c\u4f46\u82e5\u8bf4\u4ed6\u5c31\u662f\u5f53\u4eca\u6b66\u6797\u7b2c\u4e00\uff0c\u5c0f\u4f84\u5374\u8ba4\u4e3a\u8fd8\u5dee\u5f97\u8fdc\u54e9\u3002\u201d \u3000\u3000\u4e91\u8c26\u3001\u695a\u5360\u9f99\u3001\u667a\u4e66\u4ff1\u90fd\u4e00\u60ca\uff0c\u95ea\u76ee\u671b\u53bb\uff0c\u53ea\u89c9\u8fd9\u4eba\u7a7f\u7740\u4e00\u88ad\u6de1\u84dd\u957f\u886b\uff0c\u8eab\u8eaf\u81c3\u80bf\uff0c\u8179 \u5927\u817f\u77ed\uff0c\u4e4d\u773c\u671b\u53bb\uff0c\u5c31\u50cf\u4e2a\u8292\u679c\u4f3c\u7684\u3002 \u3000\u3000\u591a\u81c2\u795e\u5251\u5fae\u5fae\u4e00\u7a81\uff0c\u9053\uff1a\u201c\u6211\u5f53\u662f\u8c01\u5462\uff1f\u539f\u6765\u662f\u82cf\u8d24\u4f84\u3002\u201d \u3000\u3000\u695a\u5360\u9f99\u3001\u667a\u4e66\u5fc3\u4e2d\u5374\u4e0d\u60a6\u5730\u6697\u54fc\u4e00\u58f0\uff0c\u539f\u6765\u4ed6\u4eec\u4e5f\u8ba4\u5f97\u6b64\u4eba\uff0c\u53ea\u4e0d\u8fc7\u662f\u6c5f\u5357\u4e03\u7701\u4e2d\u4e00\u95f4 \u6700\u5c0f\u7684\u9556\u5c40\u4e2d\u7684\u4e00\u4e2a\u9556\u5934\u800c\u5df2\uff0c\u5728\u6b66\u6797\u867d\u4e5f\u5c0f\u6709\u540d\u671b\uff0c\u4f46\u5f53\u7740\u81ea\u5df1\u8bf4\u51fa\u8fd9\u79cd\u8bdd\u6765\uff0c\u5374\u603b\u6709\u4e9b \u4e0d\u59a5\u3002 \u3000\u3000\u8fd9\u77ee\u800c\u81c3\u80bf\u7684\u80d6\u5b50\uff0c\u54c8\u54c8\u4e00\u7b11\u53c8\u9053\uff1a\u201c\u4e91\u8001\u7237\u5b50\uff0c\u4f60\u8001\u4eba\u5bb6\u53ef\u77e5\u9053\uff0c\u6c5f\u5357\u5730\u9762\u4e0a\uff0c\u6700\u8fd1 \u53c8\u51fa\u4e86\u4e2a\u5947\u4eba\uff0c\u82e5\u62ff\u65b9\u624d\u90a3\u59d3\u5c91\u548c\u4eba\u5bb6\u4e00\u6bd4\uff0c\u8fde\u7ed9\u4eba\u5bb6\u8131\u9774\u90fd\u8fd8\u5dee\u5f97\u8fdc\u54e9\u3002\u201d \u3000\u3000\u695a\u5360\u9f99\u5fae\u54fc\u4e00\u58f0\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u82cf\u4e16\u5e73\uff0c\u96be\u9053\u4f60\u53c8\u89c1\u8fc7\u6b64\u4eba\u4e86\u5417\uff1f\u600e\u4e48\u8001\u592b\u5374\u672a\u66fe\u542c \u8fc7\uff1f\u201d \u3000\u3000\u82cf\u4e16\u5e73\u54a7\u5634\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5c0f\u53ef\u82e5\u672a\u4eb2\u773c\u89c1\u8fc7\uff0c\u53c8\u600e\u6562\u5728\u8001\u524d\u8f88\u4eec\u9762\u524d\u8bf4\u51fa\u6765\uff01\u201d \u3000\u3000\u4ed6\u8bed\u58f0\u4e00\u987f\uff0c\u80a5\u8138\u4e0a\u7684\u5c0f\u773c\u775b\u5728\u695a\u5360\u9f99\u8138\u4e0a\u4e00\u8f6c\uff0c\u542b\u7b11\u53c8\u9053\uff1a\u201c\u8bf4\u6765\u4e5f\u786e\u4ee4\u4eba\u96be\u4ee5\u76f8 \u4fe1\uff0c\u4f46\u5c0f\u4f84\u773c\u89c1\u7684\u8fd9\u4eba\uff0c\u522b\u7684\u6b66\u529f\u4e0d\u8bf4\uff0c\u5c31\u5355\u53ea\u8f7b\u529f\u4e00\u6837\uff0c\u51cc\u7a7a\u4e00\u8dc3\uff0c\u7adf\u7136\u80fd\u591f\u6a2a\u98de\u4e94\u4e08\uff0c \u4e91\u8001\u7237\u5b50\uff0c\u4f60\u8001\u4eba\u5bb6\u8bf4\u8bf4\u770b\uff0c\u4eba\u5bb6\u8fd9\u4efd\u8f7b\u529f\uff0c\u662f\u4e0d\u662f\u6709\u4e9b\u9a87\u4eba\u542c\u95fb\uff1f\u201d \u3000\u3000\u4e91\u8c26\u53cc\u7709\u5fae\u76b1\uff0c\u5fc3\u4e2d\u4e00\u52a8\uff0c\u8fde\u5fd9\u95ee\u9053\uff1a\u201c\u4f60\u770b\u6e05\u6b64\u4eba\u7684\u5bb9\u8c8c\u6ca1\u6709\uff0c\u4ed6\u6709\u591a\u5927\u5e74\u7eaa\uff0c\u662f\u4e0d \u662f\u4e2a\u8eab\u6750\u4e0d\u9ad8\uff0c\u9894\u4e0a\u7559\u7740\u4e9b\u77ed\u987b\uff0c\u56fd\u5b57\u53e3\u8138\uff0c\u5927\u7ea6\u6709\u4e94\u5341\u4f59\u5c81\u7684\u4e2d\u5e74\u4eba\u3002\u201d \u3000\u3000\u82cf\u4e16\u5e73\u4f38\u51fa\u4e00\u53cc\u80a5\u624b\u6765\uff0c\u8fde\u6447\u4e86\u51e0\u6447\uff0c\u9053\uff1a\u201c\u4e0d\u5bf9\uff0c\u4e0d\u5bf9\uff0c\u90a3\u4eba\u5e74\u7eaa\u5e76\u4e0d\u5927\uff0c\u6700\u591a\u4e5f\u662f \u53ea\u6709\u4e8c\u5341\u6765\u5c81\uff0c\u957f\u5f97\u6f02\u6f02\u4eae\u4eae\u7684\uff0c\u800c\u4e14\u2014\u2014\u800c\u4e14\u4ed6\u7a7f\u7684\u4e5f\u662f\u4e00\u4ef6\u9ec4\u989c\u8272\u7684\u888d\uff0c\u5c31\u548c\u65b9\u624d\u90a3\u59d3 \u5c91\u7684\u4e00\u6837\uff0c\u53ea\u4e0d\u8fc7\u8eab\u6750\u8f83\u77ed\uff0c\u4e5f\u8f83\u4e3a\u80d6\u4e9b\u3002\u201d \u3000\u3000\u4e91\u8c26\u95fb\u8a00\u957f\u53f9\u4e00\u58f0\uff0c\u4e00\u811a\u8e0f\u8fdb\u95e8\u69db\uff0c\u4f4e\u8bed\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u6b64\u4eba\u53c8\u4e0d\u662f\u6211\u90a3\u6d69\u7136\u8001\u5f1f \u4e86\u3002\u201d \u3000\u3000\u7075\u72d0\u667a\u4e66\u5374\u53cc\u7709\u4e00\u76b1\uff0c\u95ee\u9053\uff1a\u2018\u4f60\u770b\u5230\u7684\u90a3\u4eba\uff0c\u4e5f\u662f\u7a7f\u7740\u9ec4\u8272\u957f\u886b\u5417\uff1f\u201c\u82cf\u4e16\u5e73\u8fde\u8fde\u70b9 \u5934\uff0c\u695a\u5360\u9f99\u51b7\u54fc\u53c8\u9053\uff1a\u201c\u4f60\u65e2\u7136\u89c1\u8fc7\u6b64\u4eba\uff0c\u4f60\u53ef\u77e5\u4ed6\u59d3\u4ec0\u4e48\uff1f\u53eb\u4ec0\u4e48\uff1f\u4f60\u53ef\u8ba4\u4e0d\u8ba4\u8bc6\u4ed6\uff1f\u201d \u3000\u3000\u82cf\u4e16\u5e73\u4e00\u54a7\u5634\uff0c\u53c8\u81ea\u7b11\u9053\uff1a\u201c\u8fd9\u4e2a\u5c0f\u4f84\u5374\u4e0d\u6e05\u695a\u4e86\uff0c\u8001\u5b9e\u8bf4\uff0c\u5c0f\u4f84\u53ea\u89c1\u8fc7\u6b64\u4eba\u4e00\u9762\u800c \u5df2\uff0c\u4e5f\u4e0d\u8ba4\u8bc6\u4ed6\uff0c\u53ea\u662f\u90a3\u5929\u5c0f\u4f84\u4fdd\u4e86\u8d9f\u9556\uff0c\u7ecf\u8fc7\u96c1\u8361\u5c71\uff0c\u7a81\u7136\u2014\u2014\u201d\u695a\u5360\u9f99\u4e0d\u8010\u70e6\u7684\u54fc\u4e86\u4e00 \u58f0\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u4e0d\u8ba4\u8bc6\u4ed6\uff0c\u5c31\u4e0d\u5fc5\u591a\u8bf4\u4e86\u3002\u201d \u3000\u3000\u5927\u6b65\u8d70\u4eba\u5385\u4e2d\uff0c\u82cf\u4e16\u5e73\u6697\u4e2d\u4e00\u6487\u5634\uff0c\u5fc3\u91cc\u9a82\u7740\uff1a\u201c\u4f60\u8fd9\u8001\u5bb6\u4f19\uff0c\u6709\u4ec0\u4e48\u4e86\u4e0d\u8d77\uff01\u201d \u3000\u3000\u4e5f\u81ea\u8d70\u5165\u5385\uff0c\u5bfb\u4e86\u4e2a\u7a7a\u4f4d\u5750\u4e0b\uff0c\u5927\u5403\u5927\u559d\u8d77\u6765\u3002 \u3000\u3000\u66d9\u5149\u6e10\u9732\uff0c\u9662\u4e2d\u7684\u706b\u628a\u4e5f\u64a4\u4e86\u4e0b\u53bb\uff0c\u5217\u5728\u9636\u524d\u7684\u4e00\u6392\u91d1\u8272\u62dc\u76d2\uff0c\u88ab\u9001\u5165\u4e86\u5185\u5b85\uff0c\u6362\u5f97\u4e86 \u5185\u5b85\u5973\u7737\u7684\u65e0\u6570\u58f0\u60ca\u8d5e\uff0c\u6697\u4e2d\u731c\u6d4b\u7740\uff0c\u662f\u8c01\u6709\u8fd9\u4e48\u8c6a\u9614\u7684\u51fa\u624b\uff0c\u9001\u6765\u4e86\u5982\u6b64\u91cd\u7684\u5bff\u793c\u3002 \u3000\u3000\u62dc\u5bff\u7fa4\u8c6a\uff0c\u867d\u7136\u6709\u4e9b\u662f\u829c\u6e56\u5f53\u5730\u7684\u8c6a\u58eb\uff0c\u4f46\u5374\u5927\u534a\u662f\u6765\u81ea\u5176\u4ed6\u5404\u5730\uff0c\u6b64\u523b\u6b63\u5bff\u65e5\u671f\u4e00 \u8fc7\uff0c\u4e5f\u5c31\u5927\u591a\u5e26\u7740\u4e03\u5206\u9152\u610f\uff0c\u8e0f\u7740\u6653\u8272\uff0c\u79bb\u5f00\u4e86\u4e91\u5b85\uff0c\u4f46\u8fd9\u4e9b\u6b66\u6797\u8c6a\u5ba2\u4e4b\u5fc3\u4e2d\uff0c\u5374\u51e0\u4e4e\u4e0d\u7ea6 \u800c\u540c\u7684\u6709\u7740\u4e00\u79cd\u5ff5\u5934\uff0c\u90a3\u5c31\u662f\u5728\u516b\u6708\u4ee5\u524d\uff0c\u8d76\u5230\u5929\u76ee\u5c71\u53bb\uff0c\u7eb5\u7136\u81ea\u5df1\u6b66\u529f\u4e0d\u6d4e\uff0c\u4f46\u8fd9\u4efd\u70ed\u95f9 \u603b\u662f\u8981\u770b\u7684\u3002 \u3000\u3000\u4e91\u5b85\u5927\u5385\u4e2d\uff0c\u6b64\u523b\u9664\u4e86\u4e00\u4e9b\u6253\u626b\u6536\u62fe\u7684\u5bb6\u4ec6\u5916\uff0c\u5c31\u522b\u65e0\u4e00\u4eba\uff0c\u4f46\u5728\u4e91\u5b85\u540e\u9662\u7684\u4e00\u95f4\u96c5\u5ba4 \u91cc\uff0c\u5374\u53e6\u5916\u6446\u4e86\u4e00\u684c\u7cbe\u81f4\u7684\u9152\u7b75\u3002 \u3000\u3000\u867d\u7136\u5f7b\u591c\u672a\u7720\uff0c\u4f46\u6b64\u523b\u5750\u5728\u8fd9\u684c\u9152\u7b75\u65c1\u7684\u51e0\u4e2a\u8001\u4eba\uff0c\u5374\u90fd\u4e1d\u6beb\u6ca1\u6709\u5026\u5bb9\uff0c\u4ec1\u4e49\u5251\u5ba2\u4e91\u4e2d \u7a0b\u606d\u8c28\u5730\u5750\u5728\u672a\u5ea7\uff0c\u4e3a\u4ed6\u7239\u7239\u7684\u8fd9\u4e9b\u8fc7\u547d\u77e5\u4ea4\u4e0d\u65f6\u5730\u6dfb\u7740\u997f\u4e2d\u7684\u9152\uff0c\u800c\u8fd9\u4e9b\u90fd\u662f\u65e9\u5c31\u540d\u6ee1\u5929 \u4e0b\u7684\u8001\u82f1\u96c4\u4eec\uff0c\u53e3\u4e2d\u6240\u8c08\u8bba\u7684\uff0c\u5374\u5168\u90e8\u6709\u5173\u5929\u76ee\u5c71\u4e2d\uff0c\u8fd9\u4e00\u6b21\u795e\u79d8\u7684\u884c\u52a8\uff0c\u548c\u4e3b\u6301\u8fd9\u4ef6\u4e8b\u7684 \u795e\u79d8\u4eba\u7269\u3002 \u3000\u3000\u4ed6\u4eec\u867d\u90fd\u5df2\u77e5\u9053\uff0c\u8fd9\u4ef6\u4e8b\u662f\u5fc5\u5b9a\u6709\u5173\u6614\u5e74\u6b66\u6797\u4e2d\u7684\u602a\u4eba\u6e29\u5982\u7389\u7684\uff0c\u4f46\u8fd9\u4ef6\u4e8b\u7684\u80cc\u540e\uff0c\u7a76 \u7adf\u9690\u85cf\u7740\u4ec0\u4e48\u7528\u610f\uff0c\u5374\u4e0d\u662f\u4ed6\u4eec\u6240\u80fd\u731c\u6d4b\u5f97\u5230\u7684\u4e86\uff01 \u7b2c\u56db\u7ae0\u3000\u98ce\u4e91\u9645\u4f1a>> \u53e4\u9f99\u300a\u6708\u5f02\u661f\u90aa\u300b \u7b2c\u56db\u7ae0\u3000\u98ce\u4e91\u9645\u4f1a \u3000\u3000\u4e0d\u51fa\u4e00\u6708\uff0c\u5927\u6c5f\u5357\u5317\uff0c\u4e24\u6cb3\u4e1c\u897f\uff0c\u53ea\u8981\u662f\u7a0d\u5fae\u6d89\u8db3\u6b66\u6797\u7684\u4eba\uff0c\u5c31\u6ca1\u6709\u4e00\u4e2a\u4e0d\u77e5\u9053\u5929\u76ee\u5c71 \u4e2d\uff0c\u6709\u7740\u4e00\u4e2a\u7edd\u4e16\u7684\u4eba\uff0c\u8fd8\u6709\u7740\u5de8\u4e07\u91d1\u73e0\uff0c\u6570\u53e3\u795e\u5175\uff0c\u666e\u5929\u4e4b\u4e0b\uff0c\u6b66\u6797\u8c6a\u58eb\u7684\u8bdd\u9898\uff0c\u4e5f\u51e0\u4e4e \u90fd\u4ee5\u6b64\u4e8b\u4e3a\u4e3b\u3002 \u3000\u3000\u6c5f\u5357\u9053\u4e0a\uff0c\u9a6c\u8e44\u7eb7\u7eb7\uff0c\u4fa0\u8e2a\u9aa4\u73b0\uff0c\u6765\u81ea\u5404\u5730\u7684\u6b66\u6797\u9ad8\u624b\uff0c\u8349\u83bd\u8c6a\u5ba2\uff0c\u9a91\u7740\u5065\u9a6c\uff0c\u4f69\u7740\u957f \u5251\uff0c\u7531\u7696\u5165\u82cf\uff0c\u7531\u9c81\u5165\u82cf\uff0c\u7531\u8d63\u4eba\u82cf\uff0c\u7531\u95fd\u4eba\u82cf\uff0c\u56db\u9762\u516b\u65b9\u7684\u8d76\u5230\u6c5f\u82cf\u6765\u3002 \u3000\u3000\u6c89\u5bc2\u5df2\u4e45\u7684\u6b66\u6797\uff0c\u4fbf\u56e0\u4e3a\u6b64\u4e8b\uff0c\u800c\u7a81\u7136\u6380\u8d77\u4e86\u4e00\u9635\u7a7a\u524d\u7684\u70ed\u6f6e\uff0c\u8fd9\u5176\u4e2d\u6709\u7684\u81ea\u7136\u662f\u81ea\u6043 \u8eab\u624b\uff0c\u60f3\u5728\u8fd9\u5929\u76ee\u5c71\u4e0a\uff0c\u626c\u540d\u7acb\u4e07\u7684\uff0c\u6709\u7684\u81ea\u4e5f\u8fd8\u5b58\u7740\u4e00\u4efd\u8d2a\u5fc3\uff0c\u5e0c\u671b\u81ea\u5df1\u540d\u5229\u4ff1\u6536\uff0c\u4e5f\u6709 \u7684\u53ea\u662f\u60f3\u6765\u8d76\u8fd9\u573a\u6b66\u6797\u4e2d\u767e\u5e74\u96be\u89c1\u7684\u70ed\u95f9\u3002 \u3000\u3000\u6b64\u523b\u6b63\u662f\u76db\u590f\uff0c\u8ddd\u79bb\u516b\u6708\u4e2d\u79cb\uff0c\u4e5f\u53ea\u8fd8\u6709\u4e00\u4e2a\u591a\u6708\u4e86\uff0c\u5929\u76ee\u5c71\u90bb\u8fd1\u7684\u5dde\u53bf\uff0c\u5ba2\u6808\u5168\u90e8\u4f4f \u5f97\u6ee1\u6ee1\u7684\uff0c\u4e0d\u65f6\u6709\u52b2\u670d\u4f69\u5203\u7684\u7cbe\u608d\u6c49\u5b50\uff0c\u6602\u9996\u9614\u6b65\u5728\u95f9\u5e02\u4e4b\u4e2d\uff0c\u672c\u6765\u53ea\u662f\u95fb\u540d\uff0c\u800c\u672a\u89c1\u9762\u7684 \u6b66\u6797\u8c6a\u5ba2\u4eec\uff0c\u4e5f\u90fd\u501f\u7740\u8fd9\u4e2a\u673a\u4f1a\uff0c\u80fd\u591f\u63e1\u624b\u8a00\u6b22\uff0c\u4e92\u9053\u4ef0\u6155\u3002 \u3000\u3000\u4f46\u4e5f\u6709\u79ef\u6028\u591a\u5e74\u7684\u4ec7\u5bb6\uff0c\u6b64\u523b\u7a84\u5730\u76f8\u9022\uff0c\u81ea\u7136\u5c31\u5f97\u7acb\u523b\u8840\u6e85\u5f53\u5730\uff0c\u62fc\u4e2a\u4f60\u6b7b\u6211\u6d3b\u3002 \u3000\u3000\u8fd9\u4e9b\u4eba\u5404\u6709\u6765\u5386\uff0c\u5404\u6000\u7edd\u6280\uff0c\u4f46\u90fd\u662f\u5750\u9547\uff0c\uff0c\u65b9\u7684\u8c6a\u5ba2\uff0c\u6b64\u523b\u805a\u5728\u4e00\u5904\uff0c\u81ea\u7136\u96be\u514d\u751f\u51fa \u597d\u4e9b\u4e8b\u7aef\uff0c\u5f04\u5f97\u5f53\u5730\u7684\u4e09\u73ed\u6355\u5934\uff0c\u4f1a\u91cc\uff0c\u51fa\u4eba\u5934\u5730\uff0c\u626c\u7709\u5410\u6c14\u3002 \u3000\u3000\u4e03\u6708\u5c06\u8fc7\uff0c\u6c5f\u5357\u9053\u4e0a\u66f4\u662f\u9a6c\u8e44\u5306\u5fd9\uff0c\u5929\u76ee\u5c71\u53f3\uff0c\u4e34\u5b89\u57ce\u91cc\uff0c\u591c\u5e02\u65b9\u5347\uff0c\u4e34\u8857\u7684\u4e00\u5bb6\u9152\u98df \u517c\u8336\u9986\u91cc\uff0c\u9ad8\u670b\u6ee1\u5ea7\uff0c\u5ea7\u4e0a\u7684\u5374\u90fd\u662f\u83ba\u80a9\u624e\u8170\u7684\u7ec3\u5bb6\u5b50\uff0c\u4f46\u95fb\u4eba\u8a00\u7eb7\u7eb7\uff0c\u8c08\u7740\u7684\u4ff1\u662f\u6b66\u6797\u95f4 \u4e8b\u3002 \u3000\u3000\u9ad8\u5927\u7684\u79c3\u5934\u5927\u6c49\uff0c\u8fce\u95e8\u5750\u5728\u4e00\u5f20\u516b\u4ed9\u684c\u4e0a\uff0c\u6b63\u81ea\u7aef\u7740\u9152\u676f\uff0c\u5927\u58f0\u9053\uff1a\u201c\u4e0d\u662f\u6211\u6bb7\u8001\u4e94\u5728 \u706d\u81ea\u5df1\u7684\u5a01\u98ce\uff0c\u53ef\u662f\u90a3\u5929\u90a3\u4e2a\u4e00\u8eab\u9ec4\u886b\u7684\u5c11\u5e74\u670b\u53cb\uff0c\u624b\u5e95\u4e0b\u53ef\u771f\u6709\u4e24\u4e0b\u5b50\uff0c\u8fde\u7ba1\u795e\u9e70\u90a3\u79cd\u89d2 \u8272\uff0c\u4e0d\u51fa\u4e09\u62db\uff0c\u5c31\u8ba4\u683d\u670d\u8f93\uff0c\u6768\u8001\u5f1f\uff0c\u4f60\u7684\u4e00\u624b\u5ce8\u5d4b\u5251\u6cd5\uff0c\u867d\u7136\u4f7f\u5f97\u6f02\u4eae\uff0c\u4f46\u6bd4\u8d77\u4eba\u5bb6\u6765\u2014 \u2014\u77a7\uff0c\u8fd8\u5dee\u5f97\u597d\u5927\u4e00\u622a\u54e9\u3002\u201d \u3000\u3000\u5750\u5728\u4ed6\u8eab\u4fa7\u7684\u4e00\u4e2a\u7626\u524a\u6c49\u5b50\uff0c\u6df1\u76ee\u5e7f\u9886\uff0c\u9762\u4e0a\u4e1d\u6beb\u4e0d\u52a8\u58f0\u8272\uff0c\u7aef\u8d77\u9152\u676f\u6765\uff0c\u6d45\u6d45\u559d\u4e86\u4e00 \u53e3\uff0c\u5fae\u5fae\u7b11\u7740\u9053\uff1a\u201c\u6bb7\u4e94\u54e5\u65e2\u7136\u8fd9\u4e48\u8bf4\uff0c\u60f3\u5fc5\u4e0d\u4f1a\u5dee\u7684\u4e86\uff0c\u4f46\u662f\uff0c\u6bb7\u4e94\u54e5\uff0c\u4f60\u53ef\u77e5\u9053\uff0c\u522b\u7684 \u5730\u65b9\u4e0d\u8bf4\uff0c\u5c31\u5728\u8fd9\u4e34\u5b89\u57ce\u91cc\uff0c\u624e\u624b\u7684\u811a\u8272\uff0c\u5c11\u8bf4\u4e5f\u6709\u5341\u4e2a\uff0c\u96c1\u8361\u7ea2\u4e2d\u4f1a\u3001\u592a\u884c\u5feb\u5200\u4f1a\u7684\u603b\u74e2 \u628a\u5b50\uff0c\u8fd9\u6b21\u7adf\u4e5f\u90fd\u4eb2\u81ea\u6765\u4e86\uff0c\u4f60\u8bf4\u7684\u8fd9\u4e2a\u59d3\u5c91\u7684\u5c11\u5e74\u670b\u53cb\uff0c\u867d\u7136\u624b\u628a\u5b50\u786c\uff0c\u4f46\u8fd9\u6b21\u60f3\u538b\u5012\u7fa4 \u96c4\uff0c\u72ec\u5360\u9ccc\u5934\uff0c\u53ea\u6015\u4e5f\u4e0d\u53ef\u80fd\u5427\uff1f\u201d \u3000\u3000\u79c3\u9e70\u6bb7\u8001\u4e94\u563f\u563f\u5927\u7b11\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u8fd9\u53ef\u4e5f\u8bf4\u4e0d\u5b9a\uff0c\u6768\u8001\u5f1f\uff0c\u4f60\u662f\u6ca1\u6709\u8d76\u4e0a\u90a3\u573a\u70ed\u95f9\uff0c \u8981\u662f\u90a3\u5929\u4f60\u4e5f\u5728\u573a\u7684\u8bdd\uff0c\u4f60\u5c31\u4f1a\u77e5\u9053\uff0c\u6211\u6bb7\u8001\u4e94\u8bf4\u7684\u8bdd\u4e0d\u662f\u4e71\u6253\u9ad8\u7a7a\u4e86\u3002\u201d \u3000\u3000\u4ed6\u8fd9\u4e00\u5927\u58f0\u56b7\u56b7\uff0c\u8336\u9986\u4e2d\u7684\u4eba\uff0c\u4e0d\u7981\u4ff1\u90fd\u4e3a\u4e4b\u4fa7\u76ee\u3002 \u3000\u3000\u4f46\u79c3\u9e70\u6bb7\u8001\u4e94\uff0c\u5374\u4e00\u70b9\u513f\u4e5f\u4e0d\u5728\u4e4e\uff0c\u65b9\u81ea\u5927\u53e3\u559d\u4e86\u53e3\u9152\uff0c\u7a81\u7136\u76ee\u5149\u4e00\u8f6c\uff0c\u770b\u5230\u4e24\u4eba\u5e76\u80a9 \u8d70\u4eba\u5e97\u672a\uff0c\u201c\u5472\u201d\u7684\u4e00\u58f0\uff0c\u5589\u4e2d\u7684\u9152\uff0c\u90fd\u4ece\u9f3b\u5b50\u91cc\u545b\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u4e00\u8d70\u8fdb\u8fd9\u95f4\u8336\u94fa\uff0c\u5ea7\u4e0a\u7684\u4eba\uff0c\u5341\u4e2a\u4e4b\u4e2d\uff0c\u5012\u6709\u4e5d\u4e2a\u5168\u7ad9\u4e86\u8d77\u6765\uff0c\u8138\u4e0a\u5806\u7740\u7b11\uff0c\u6253 \u7740\u62db\u547c\uff0c\u90fd\u5f80\u81ea\u5df1\u7684\u4f4d\u5b50\u4e0a\u8ba9\u3002 \u3000\u3000\u90a3\u79c3\u9e70\u6bb7\u8001\u4e94\u4f38\u51fa\u9752\u7b4b\u66b4\u9732\u7684\u5de8\u638c\uff0c\u4e00\u62b9\u8138\u4e0a\u7684\u6d95\u6cea\uff0c\u5c31\u62a2\u5148\u56b7\u9053\uff1a\u201c\u4e91\u8001\u7237\u5b50\uff0c\u4f60\u8001 \u4eba\u5bb6\u4e5f\u6765\u4e86\u5440\u3002\u201d \u3000\u3000\u8d76\u7d27\u7ad9\u4e86\u8d77\u6765\uff0c\u8fde\u8fde\u8ba9\u5ea7\uff0c\u8fdb\u6765\u7684\u8fd9\u4e24\u4e2a\u4eba\uff0c\u6b63\u662f\u591a\u81c2\u795e\u5251\u4e91\u8c26\u3001\u4ec1\u4e49\u5251\u5ba2\u4e91\u4e2d\u7a0b\u7236 \u5b50\uff0c\u6b64\u523b\u4e24\u4eba\u76ee\u5149\u56db\u626b\uff0c\u542b\u7b11\u5411\u56db\u5ea7\u6253\u7740\u62db\u547c\uff0c\u5374\u5728\u6bb7\u8001\u4e94\u7684\u684c\u4e0a\uff0c\u5750\u4e86\u4e0b\u6765\uff0c\u5374\u89c1\u5728\u8fd9\u5f20 \u684c\u4e0a\uff0c\u7adf\u6709\u4e00\u4eba\uff0c\u7aef\u5750\u672a\u52a8\uff0c\u4e91\u4e2d\u7a0b\u9762\u8272\u4e0d\u7981\u5fae\u53d8\uff0c\u76ee\u5149\u5411\u6bb7\u8001\u4e94\u4e00\u626b\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u8fd9\u4f4d\u5144 \u5f1f\u662f\u8c01\uff1f\u5c0f\u5f1f\u5012\u9762\u751f\u5f97\u5f88\u3002\u201d \u3000\u3000\u79c3\u9e70\u6bb7\u8001\u4e94\u4e00\u9762\u53f1\u559d\u7740\u5e97\u5c0f\u4e8c\u6dfb\u676f\u52a0\u83dc\uff0c\u4e00\u9762\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4e91\u5927\u54e5\uff0c\u4eca\u5929\u8ba9\u5c0f\u5f1f\u7ed9\u5f15\u89c1 \u4e00\u4f4d\u6210\u540d\u9732\u8138\u7684\u670b\u53cb\u3002\u201d \u3000\u3000\u53c8\u9053\uff1a\u201c\u6768\u8001\u5f1f\uff0c\u4f60\u53ef\u77e5\u9053\uff0c\u5750\u5728\u4f60\u5bf9\u9762\u7684\uff0c\u5c31\u662f\u540d\u6ee1\u5929\u4e0b\u7684\u591a\u81c2\u795e\u5251\u4e91\u8001\u7237\u5b50\uff0c\u548c\u4ec1 \u4e49\u5251\u5ba2\u4e91\u5927\u54e5\u3002\u201d \u3000\u3000\u7b11\u7740\u53c8\u9053\uff1a\u201c\u8fd9\u4f4d\u6768\u8001\u5f1f\uff0c\u5c31\u662f\u5ce8\u5d4b\u6d3e\u7684\u638c\u95e8\u5f1f\u5b50\uff0c\u626c\u540d\u8700\u4e2d\u7684\u6768\u4e00\u5251\u6768\u632f\uff0c\u54c8\u54c8\uff0c\u60f3 \u4e0d\u5230\u4f60\u4eec\u4e8c\u4f4d\u5c45\u7136\u6ca1\u6709\u4f1a\u8fc7\u9762\uff0c\u66f4\u60f3\u4e0d\u5230\u4eca\u5929\u6211\u6bb7\u8001\u4e94\u80fd\u591f\u5f15\u89c1\u4f60\u4eec\u4e8c\u4f4d\u3002\u201d \u3000\u3000\u5f97\u610f\u4e4b\u8272\uff0c\u663e\u4e8e\u8a00\u8868\u3002 \u3000\u3000\u591a\u81c2\u795e\u5251\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8001\u592b\u65e9\u5c31\u542c\u5f97\u5ce8\u5d4b\u9759\u6ce2\u4e0a\u4eba\u6709\u4e2a\u51fa\u7c7b\u62d4\u8403\u7684\u5f1f\u5b50\uff0c\u4eca\u65e5\u4e00 \u89c1\uff0c\u6c14\u5b57\u679c\u81ea\u4e0d\u51e1\uff0c\u6545\u4eba\u7edd\u6280\u5f97\u4f20\uff0c\u771f\u53eb\u8001\u592b\u9ad8\u5174\u5f97\u5f88\u3002\u201d \u3000\u3000\u6768\u632f\u624b\u91cc\u4ecd\u7aef\u7740\u9152\u676f\uff0c\u5fae\u5fae\u6b20\u4e86\u6b20\u8eab\u5b50\uff0c\u5fae\u7b11\u9053\uff1a\u201c\u8001\u524d\u8f88\u8fc7\u5956\u4e86\u3002\u201d \u3000\u3000\u4e91\u4e2d\u7a0b\u5fc3\u4e2d\u4e0d\u60a6\u5730\u6697\u54fc\u4e00\u58f0\uff0c\u5374\u4e5f\u6ca1\u6709\u53d1\u4f5c\u51fa\u6765\uff0c\u56de\u8fc7\u5934\u53bb\uff0c\u671b\u7740\u95e8\u5916\uff0c\u8fde\u5bd2\u6684\u90fd\u6ca1\u6709 \u5bd2\u6684\u534a\u53e5\u3002 \u3000\u3000\u4e91\u6c0f\u7236\u5b50\u4e00\u4eba\u4e34\u5b89\uff0c\u4e0d\u5230\u4e00\u4e2a\u65f6\u8fb0\uff0c\u4e34\u5b89\u57ce\u91cc\u7684\u6b66\u6797\u8c6a\u5ba2\uff0c\u5c31\u90fd\u77e5\u9053\u5df2\u7ecf\u9690\u5f52\u591a\u5e74\uff0c\u5728 \u5bb6\u7eb3\u798f\u7684\u591a\u81c2\u795e\u5251\uff0c\u8fd9\u6b21\u7adf\u4e5f\u51fa\u5c71\u4e86\u3002 \u3000\u3000\u4e8e\u662f\u5c31\u6709\u4eba\u79c1\u4e0b\u731c\u6d4b\uff0c\u8fd9\u6b21\u5929\u76ee\u5c71\u4e4b\u4f1a\uff0c\u7a76\u7adf\u80fd\u5f15\u51fa\u591a\u5c11\u4e2a\u6b66\u6797\u8004\u8896\u6765\uff0c\u6709\u7684\u548c\u4e91\u6c0f\u7236 \u5b50\u4ea4\u60c5\u8f83\u6df1\u7684\uff0c\u5c31\u7eb7\u7eb7\u8d76\u5230\u9f99\u95e8\u5c45\u90a3\u95f4\u8336\u9986\u53bb\uff0c\u548c\u4e91\u6c0f\u7236\u5b50\u53d9\u522b\uff0c\u90a3\u7ee7\u627f\u5ce8\u5d4b\u4e00\u6d3e\u672a\u6765\u7684\u638c \u95e8\u5e0c\u671b\u6700\u6d53\u7684\u5ddd\u4e2d\u5251\u5ba2\u6768\u4e00\u5251\uff0c\u5374\u62c2\u8896\u8d70\u51fa\u4e86\u9f99\u95e8\u5c45\u3002 \u3000\u3000\u4e91\u4e2d\u7a0b\u51b7\u51b7\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6bb7\u4e94\u7237\uff0c\u54ea\u91cc\u4ea4\u6765\u8fd9\u4e48\u597d\u7684\u670b\u53cb\uff1f\u201d \u3000\u3000\u79c3\u9e70\u6bb7\u8001\u4e94\u867d\u7136\u4e5f\u662f\u5728\u6c5f\u5357\u5730\u9762\u4e0a\u6210\u540d\u9732\u8138\u7684\u4eba\u7269\uff0c\u4f46\u6b64\u523b\u5374\u53ea\u6709\u8d54\u7740\u7b11\uff0c\u656c\u7740\u9152\uff0c\u5728 \u4e91\u6c0f\u7236\u5b50\u9762\u524d\uff0c\u4ed6\u867d\u7136\u96c9\u505a\uff0c\u5374\u4e5f\u4e0d\u5f97\u4e0d\u9a6f\u4e0b\u6765\u3002 \u3000\u3000\u591a\u81c2\u795e\u5251\u5374\u5fae\u5fae\u957f\u7709\uff0c\u8f7b\u53f1\u9053\uff1a\u201c\u4e2d\u7a0b\uff0c\u4f60\u7684\u6db5\u517b\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u201d \u3000\u3000\u4ed6\u4eba\u60c5\u5b8f\u8fbe\uff0c\u77e5\u9053\u8fd9\u4e34\u5b89\u4e00\u5730\uff0c\u6b64\u523b\u5df2\u662f\u85cf\u9f99\u5367\u864e\uff0c\u98ce\u4e91\u9645\u4f1a\uff0c\u8a00\u8bed\u7a0d\u4e00\u4e0d\u614e\uff0c\u4fbf\u662f\u65e0 \u7a77\u98ce\u6ce2\uff0c\u54ea\u77e5\u4ed6\u867d\u662f\u5982\u6b64\u8c28\u614e\uff0c\u4ec1\u4e49\u5251\u5ba2\u7684\u591a\u5e74\u76db\u540d\uff0c\u8fd8\u662f\u9669\u4e9b\u683d\u5728\u8fd9\u4e2a\u5c0f\u5c0f\u7684\u4e00\u4e2a\u4e34\u5b89\u57ce \u91cc\u3002 \u3000\u3000\u4ec1\u4e49\u5251\u5ba2\u4fef\u9996\u65e0\u8bed\uff0c\u4e91\u8001\u7237\u5b50\u5e72\u54b3\u4e00\u58f0\uff0c\u7aef\u8d77\u9152\u676f\uff0c\u53c8\u81ea\u548c\u6155\u540d\u800c\u6765\u7684\u4e00\u4e9b\u6b66\u6797\u540e\u8f88\uff0c \u5fae\u7b11\u5bd2\u6684\uff0c\u9f99\u95e8\u5c45\u4e2d\uff0c\u4f46\u95fb\u7b11\u8bed\u7eb7\u7eb7\uff0c\u54ea\u77e5\u2014\u2014\u7a81\u7136\u5916\u9762\u53f7\u58f0\u5927\u4f5c\uff0c\u56db\u9762\u516b\u65b9\uff0c\u5ffd\u7136\u54cd\u8d77\u4e86 \u4e00\u9635\u5947\u5f02\u7684\u53f7\u89d2\u4e4b\u58f0\uff0c\u79c3\u9e70\u6bb7\u8001\u4e94\u9762\u8272\u5927\u53d8\uff0c\u500f\u7136\u63a8\u676f\u800c\u8d77\uff0c\u8131\u53e3\u8bf4\u9053\uff1a\u201c\u7ea2\u5dfe\u53f7\u3002\u201d \u3000\u3000\u4e91\u4e2d\u7a0b\u4e5f\u81ea\u4e3a\u4e4b\u76b1\u7709\u9053\uff1a\u201c\u96c1\u8361\u7ea2\u5dfe\u4f1a\uff0c\u600e\u4f1a\u5728\u8fd9\u4e34\u5b89\u57ce\u91cc\u5f00\u8d77\u575b\u6765\uff0c\u96be\u9053\u7ea2\u4e2d\u4e09\u8c6a\uff0c \u6b64\u523b\u5168\u90fd\u5230\u4e86\u4e34\u5b89\u57ce\u5417\uff1f\u201d \u3000\u3000\u8bed\u72b9\u672a\u4e86\uff0c\u8fd9\u5947\u5f02\u7684\u53f7\u89d2\u58f0\u4e2d\uff0c\u7a81\u7136\u53c8\u54cd\u8d77\u4e86\u4e00\u8fde\u4e32\u60e8\u5389\u7684\u53eb\u58f0\uff0c\u5947\u602a\u7684\u662f\u8fd9\u60e8\u53eb\u58f0\u7adf \u4e5f\u662f\u4ece\u56db\u9762\u4f20\u6765\uff0c\u800c\u4e14\u6b64\u8d77\u5f7c\u843d\uff0c\u4e00\u58f0\u8fde\u7740\u4e00\u58f0\uff0c\u7531\u8fdc\u800c\u8fd1\uff0c\u7531\u8fd1\u53c8\u8fdc\uff0c\u9f99\u95e8\u5c45\u4e2d\u7684\u7b11\u8bed\uff0c \u7acb\u5373\u5168\u90fd\u5bc2\u7136\u3002 \u3000\u3000\u95e8\u5916\u591c\u5e02\u672c\u7e41\uff0c\u8d70\u5728\u8def\u4e0a\u7684\u884c\u4eba\uff0c\u6b64\u523b\u4e5f\u5927\u534a\u9a7b\u8db3\u800c\u542c\u7a81\u7136\uff0c\u9a6c\u8e44\u4e4b\u58f0\uff0c\u7eb7\u6c93\u800c\u6765\uff0c\u8fd9 \u6761\u7e41\u76db\u81f3\u6781\u7684\u8857\u4e0a\uff0c\u884c\u4eba\u672c\u591a\uff0c\u4e0d\u7981\u90fd\u715e\u7136\u56db\u4e0b\u8d70\u907f\uff0c\u4e00\u7fa4\u5065\u9a6c\uff0c\u98de\u4e5f\u4f3c\u7684\u4ece\u8857\u4e0a\u5954\u9a70\u800c \u8fc7\uff0c\u7070\u5c18\u98ce\u626c\u4e4b\u4e2d\uff0c\u4f9d\u7a00\u53ef\u4ee5\u89c1\u5230\u9a6c\u4e0a\u7684\u9a91\u58eb\u90fd\u624e\u7740\u7ea2\u4e2d\uff0c\u4f46\u5374\u7adf\u90fd\u4e0d\u662f\u7b14\u76f4\u5730\u5750\u5728\u9a6c\u4e0a\u3002 \u3000\u3000\u4ec1\u4e49\u5251\u5ba2\u53d8\u8272\u800c\u8d77\uff0c\u6324\u51fa\u95e8\u53e3\u4e00\u770b\uff0c\u9762\u8272\u66f4\u662f\u5927\u53d8\uff0c\u539f\u6765\u6b64\u523b\u7b14\u76f4\u7684\u4e00\u6761\u8857\u4e0a\uff0c\u7adf\u7136\u591a \u4e86\u4e00\u6761\u9c9c\u7ea2\u7684\u8840\u8ff9\uff0c\u88ab\u4e24\u65c1\u5e97\u94fa\u95e8\u53e3\u6392\u51fa\u7684\u98ce\u706f\u7684\u706f\u5149\u4e00\u95ea\uff0c\u66f4\u662f\u4ee4\u4eba\u4e3a\u4e4b\u8083\u7136\u3002 \u3000\u3000\u4ed6\u56de\u9996\u6c89\u58f0\u9053\uff1a\u201c\u7239\u7239\uff0c\u60a8\u8001\u4eba\u5bb6\u5728\u6b64\u7a0d\u5fae\u6b47\u4e00\u6b47\uff0c\u6211\u51fa\u53bb\u770b\u770b\u3002\u201d \u3000\u3000\u5fae\u64a9\u888d\u89d2\uff0c\u6cbf\u7740\u8857\u4e0a\u7684\u8840\u8ff9\uff0c\u5927\u6b65\u8d70\u4e86\u8fc7\u53bb\uff0c\u53ea\u89c1\u8840\u8ff9\u8d8a\u6765\u8d8a\u7a00\u3002 \u3000\u3000\u6b64\u523b\u4e34\u5b89\u57ce\u91cc\uff0c\u4eba\u5fc3\u60f6\u60f6\uff0c\u90a3\u79cd\u5947\u5f02\u7684\u53f7\u89d2\u58f0\uff0c\u867d\u5df2\u4e0d\u590d\u518d\u54cd\uff0c\u4f46\u662f\u60e8\u547c\u4e4b\u58f0\uff0c\u4ecd\u7136\u65f6 \u6709\u6240\u95fb\u3002 \u3000\u3000\u4ec1\u4e49\u5251\u5ba2\u4e91\u4e2d\u7a0b\u5fc3\u4e2d\u7591\u4e91\u5982\u6d8c\uff0c\u6025\u6b65\u8d70\u51fa\u8fd9\u6761\u76f4\u8857\uff0c\u76ee\u5149\u626b\u5904\uff0c\u4f46\u89c9\u81ea\u5df1\u63d0\u888d\u89d2\u7684\u624b\uff0c \u90fd\u6709\u4e9b\u53d1\u9ebb\u4e86\u2014\u2014\u8fd9\u5341\u5b57\u8def\u53e3\uff0c\u524d\u540e\u5de6\u53f3\u56db\u6761\u5927\u8857\uff0c\u8857\u9762\u4e0a\u7adf\u7136\u6ee1\u6cbe\u7740\u8840\u8ff9\uff0c\u4e09\u4e2a\u9ed1\u8863\u52b2 \u88c5\u3001\u5934\u624e\u7ea2\u4e2d\u7684\u5927\u6c49\uff0c\u6ee1\u8eab\u6d74\u8840\uff0c\u6b63\u530d\u4f18\u5728\u5730\u9762\u4e0a\u6323\u624e\u7740\uff0c\u4e24\u5339\u6709\u978d\u65e0\u4eba\u7684\u5065\u9a6c\uff0c\u7acb\u5728\u8857 \u5fc3\uff0c\u6602\u9996\u4f4e\u5636\uff0c\u8857\u4e0a\u7684\u884c\u4eba\u6b64\u523b\u90fd\u6014\u5728\u8857\u89d2\uff0c\u9762\u8272\u4ff1\u90fd\u6709\u5982\u6b7b\u7070\uff0c\u4e00\u773c\u671b\u53bb\uff0c\u4f46\u89c9\u51c4\u60e8\u4e4b \u72b6\uff0c\u4e0d\u5fcd\u5352\u7779\u3002 \u3000\u3000\u4ec1\u4e49\u5251\u5ba2\u95ef\u8361\u6c5f\u6e56\uff0c\u624b\u4e0a\u81ea\u7136\u4e5f\u96be\u514d\u67d3\u6709\u8840\u8165\uff0c\u4f46\u6b64\u523b\u4ed6\u5374\u4ecd\u7981\u4e0d\u4f4f\u5fc3\u5934\u72af\u6076\uff0c\u4e00\u4e2a\u7bad \u6b65\u7a9c\u5230\u4e86\u8857\u5fc3\uff0c\u8e72\u4e0b\u8eab\u53bb\uff0c\u6276\u8d77\u4e00\u4e2a\u9ed1\u8863\u5927\u6c49\uff0c\u6c89\u58f0\u95ee\u9053\uff1a\u201c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff0c\u4f60\u4eec\u600e\u6837\u53d7\u7684 \u4f24\uff1f\u201d \u3000\u3000\u8fd9\u9ed1\u8863\u5927\u6c49\uff0c\u9762\u4e0a\u8840\u8ff9\u6591\u6591\uff0c\u65e0\u529b\u7684\u5f20\u5f00\u773c\u6765\uff0c\u547b\u541f\u7740\u9053\uff09\u201c\u597d\u72e0\u7684\u5fc3\u2026\u2026\u597d\u72e0\u7684 \u5fc3\u2026\u2026\u6211\u2026\u2026\u201d \u3000\u3000\u8bdd\u672a\u8bf4\u5b8c\uff0c\u53cc\u817f\u4e00\u4f38\uff0c\u53cc\u773c\u4e00\u7a81\uff0c\u7adf\u7136\u54bd\u6c14\u4e86\uff0c\u5374\u4ecd\u77aa\u7740\u4e00\u53cc\u5389\u76ee\uff0c\u5634\u89d2\u6c69\u6c69\u6d41\u51fa\u9c9c\u8840 \u6765\u3002 \u3000\u3000\u4e91\u4e2d\u7a0b\u4e00\u54ac\u94a2\u7259\uff0c\u957f\u8eab\u800c\u8d77\uff0c\u63a2\u5230\u53e6\u4e24\u4e2a\u9ed1\u8863\u5927\u6c49\u7684\u8eab\u4fa7\uff0c\u5374\u89c1\u8fd9\u4e24\u4eba\u7adf\u65e9\u5df2\u54bd\u6c14\u4e86\u3002 \u3000\u3000\u4ed6\u957f\u53f9\u4e00\u58f0\uff0c\u671b\u7740\u6ee1\u8857\u7684\u8840\u8ff9\uff0c\u5fc3\u4e2d\u4f46\u89c9\u70ed\u8840\u7ffb\u6d8c\uff0c\u4e0d\u80fd\u81ea\u4e3b\u3002 \u3000\u3000\u96c1\u8361\u7ea2\u4e2d\u4f1a\u6a2a\u884c\u6d59\u4e1c\uff0c\u867d\u662f\u591a\u884c\u4e0d\u4e49\uff0c\u4f46\u6b64\u523b\u843d\u5f97\u8fd9\u79cd\u5730\u6b65\uff0c\u5374\u4e5f\u672a\u514d\u5927\u60e8\u4e86\u4e9b\u3002 \u3000\u3000\u4eba\u7fa4\uff0c\u6e10\u6e10\u56f4\u805a\u4e86\u8fc7\u6765\uff0c\u5374\u8fd8\u662f\u7ad9\u5f97\u8fdc\u8fdc\u7684\uff0c\u4e0d\u6562\u8e29\u7740\u8857\u4e0a\u7684\u8840\u8ff9\uff0c\u4e91\u4e2d\u7a0b\u7acb\u5728\u8857\u5fc3\uff0c \u6115\u4e86\u534a\u664c\uff0c\u8033\u65c1\u7a81\u7136\u54cd\u8d77\u4e00\u58f0\u9a6c\u5636\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u4e00\u52a8\uff0c\u4e00\u4e2a\u7bad\u6b65\uff0c\u7a9c\u5230\u9a6c\u4fa7\uff0c\u98de\u8eab\u4e0a\u4e86\u9a6c\uff0c\u53cd\u638c\u4e00\u62cd\u9a6c\u80a1\uff0c\u4eba\u7fa4\u7acb\u523b\u53c8\u56db\u6563\u8d70 \u907f\uff0c\u4ed6\u677e\u7740\u9a6c\u7f30\uff0c\u4f46\u51ed\u8fd9\u5339\u9a6c\uff0c\u4efb\u610f\u98de\u5954\u3002 \u3000\u3000\u9a6c\u884c\u751a\u6025\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u4fbf\u9a70\u8fc7\u6570\u6761\u8857\u9053\uff0c\u53ea\u89c1\u8857\u4e0a\u7684\u8840\u8ff9\uff0c\u65f6\u6d53\u65f6\u7a00\uff0c\u4f46\u5374\u4e00\u8def\u4e0d\u66fe\u65ad \u8fc7\u3002 \u3000\u3000\u84e6\u5730\uff0c\u60e8\u547c\u4e4b\u58f0\uff0c\u53c8\u590d\u5927\u4f5c\uff0c\u4f46\u8fd9\u6b21\u5374\u975e\u7531\u56db\u9762\u4f20\u6765\uff0c\u800c\u662f\u805a\u5728\u4e00\u5904\u3002 \u3000\u3000\u706f\u5149\u6620\u5c04\u4e0b\uff0c\u4f46\u89c1\u8857\u4e0a\u884c\u4eba\uff0c\u4e00\u4e2a\u4e2a\u90fd\u9762\u8272\u6b7b\u767d\uff0c\u60f6\u60f6\u7136\u5982\u5927\u7978\u5c06\u4e34\uff0c\u5374\u53c8\u4e0d\u77e5\u9053\u8fd9\u60e8 \u547c\u7531\u6765\u7684\u7a76\u7adf\u3002 \u3000\u3000\u4e91\u4e2d\u7a0b\u5fae\u4e00\u52d2\u9a6c\uff0c\u8fa8\u4e86\u8fa8\u8fd9\u60e8\u547c\u58f0\u4f20\u6765\u7684\u65b9\u5411\uff0c\u53c8\u6253\u9a6c\u9a70\u53bb\u3002 \u3000\u3000\u4ed6\u867d\u7136\u660e\u77e5\u9053\u524d\u884c\u5fc5\u662f\u7edd\u9669\u4e4b\u5730\uff0c\u4f46\u662f\u4ed6\u8033\u4e2d\u542c\u5f97\u8fd9\u79cd\u51c4\u60e8\u7684\u547c\u58f0\uff0c\u76ee\u4e2d\u89c1\u5230\u8fd9\u4e9b\u9c9c\u8840 \u7684\u8840\u8ff9\uff0c\u4fbf\u518d\u4e5f\u65e0\u6cd5\u63a7\u5236\u81ea\u5df1\u7684\u4fa0\u5fc3\uff0c\u7eb5\u7136\u524d\u9762\u662f\u9f99\u6f6d\u864e\u7a74\uff0c\u4ed6\u4e5f\u8981\u9a6c\u95ef\u4e00\u95ef\u3002 \u3000\u3000\u4ed6\u6240\u5947\u602a\u7684\u53ea\u662f\uff0c\u96c1\u8361\u7ea2\u4e2d\u4f1a\u5a01\u9738\u4e00\u65b9\uff0c\u9664\u4e86\u7ea2\u4e2d\u4e09\u6770\u5916\uff0c\u4f1a\u4e2d\u7684\u575b\u4e3b\u3001\u9999\u4e3b\uff0c\u4e5f\u90fd\u4ff1 \u662f\u786c\u624b\uff0c\u6b64\u523b\u4e00\u8d25\u5982\u6b64\uff0c\u90a3\u4e48\u4ed6\u4eec\u7684\u654c\u624b\uff0c\u5c82\u975e\u53ef\u6015\u5f97\u4e0d\u53ef\u601d\u8bae\u4e86\u5417\uff1f\u8fd9\u4e9b\u4eba\u5374\u53c8\u662f\u8c01\u5462\uff1f \u3000\u3000\u9a6c\u884c\u5982\u7bad\uff0c\u7728\u773c\u4fbf\u7a7f\u8fc7\u95f9\u5e02\uff0c\u6108\u884c\u6108\u89c1\u8352\u50fb\uff0c\u800c\u4e14\u6e10\u6e10\u5df2\u5c06\u51fa\u57ce\u3002 \u3000\u3000\u4e91\u4e2d\u7a0b\u6293\u7740\u9a6c\u7f30\u7684\u624b\uff0c\u6b64\u523b\u7adf\u5fae\u5fae\u6709\u4e9b\u98a4\u6296\uff0c\u4ed6\u95ef\u8361\u6c5f\u6e56\u534a\u751f\uff0c\u51fa\u5165\u751f\u6b7b\u95f4\uff0c\u4e0d\u77e5\u6709\u591a \u5c11\u6b21\uff0c\u4f46\u5374\u4ece\u672a\u6709\u8fc7\u5982\u6b64\u65f6\u7684\u7d27\u5f20\u5fc3\u60c5\u3002 \u3000\u3000\u8857\u7684\u8f6c\u89d2\u5904\uff0c\u7a81\u7136\u63a0\u51fa\u4e00\u6761\u4eba\u5f71\uff0c\u4e91\u4e2d\u7a0b\u7684\u80ef\u4e0b\u9a6c\uff0c\u550f\u5f8b\u4e00\u58f0\u957f\u5636\uff0c\u6602\u9996\u4eba\u7acb\u800c\u8d77\uff0c\u4e91 \u4e2d\u7a0b\u53cc\u817f\u52a0\u52b2\uff0c\u5939\u5728\u9a6c\u978d\u4e0a\u3002 \u3000\u3000\u5929\u4e0a\u661f\u5149\u95ea\u70c1\uff0c\u4e91\u4e2d\u7a0b\u4f0f\u5728\u9a6c\u4e0a\uff0c\u95ea\u76ee\u800c\u671b\uff0c\u53ea\u89c1\u9a6c\u9996\u524d\u5353\u7136\u7ad9\u7740\u4e00\u4eba\uff0c\u5934\u4e0a\u53d1\u9afb\u6563 \u4e71\uff0c\u8eab\u4e0a\u8863\u88f3\u51cc\u843d\uff0c\u5012\u63d0\u7740\u4e00\u53e3\u6676\u5149\u8000\u76ee\u7684\u957f\u5251\uff0c\u661f\u5149\u4e4b\u4e0b\uff0c\u867d\u770b\u4e0d\u6e05\u4ed6\u7684\u9762\u8272\uff0c\u4f46\u4e00\u773c\u671b \u53bb\uff0c\u53ea\u89c9\u6b64\u4eba\u9762\u8272\u7070\u767d\uff0c\u884c\u60c5\u60ca\u9a87\uff0c\u50cf\u662f\u521a\u521a\u53d7\u4e86\u4e00\u79cd\u5de8\u5927\u7684\u60ca\u6050\uff0c\u6b64\u523b\u5c1a\u672a\u5e73\u590d\u4f3c\u7684\u3002 \u3000\u3000\u4e91\u4e2d\u7a0b\u80ef\u4e0b\u6240\u4e58\u7684\u9a6c\uff0c\u663e\u7136\u7ecf\u8fc7\u957f\u671f\u7684\u8bad\u7ec3\uff0c\u65b9\u624d\u867d\u56e0\u8fd9\u6761\u7a81\u6765\u7684\u4eba\u5f71\uff0c\u800c\u60ca\u5636\u4e00\u58f0\uff0c \u4f46\u6b64\u523b\u5374\u7acb\u9a6c\u5982\u6869\uff0c\u5df2\u53c8\u56de\u590d\u9547\u9759\u3002 \u3000\u3000\u4e91\u4e2d\u7a0b\u7aef\u5750\u9a6c\u4e0a\uff0c\u51dd\u76ee\u826f\u4e45\uff0c\u65b9\u624d\u770b\u51fa\u8fd9\u9762\u5e26\u60ca\u60f6\u7684\u591c\u884c\u4eba\uff0c\u7adf\u7136\u5c31\u662f\u65b9\u624d\u90a3\u72c2\u50b2\u9a84\u5028 \u7684\u5ce8\u5d4b\u5f1f\u5b50\uff0c\u6768\u4e00\u5251\u6768\u632f\u3002 \u3000\u3000\u4e24\u4eba\u76ee\u5149\u76f8\u5bf9\uff0c\u6768\u4e00\u5251\u624b\u8155\u4e00\u7ffb\uff0c\u4f38\u51fa\u5de6\u624b\u98df\u3001\u4e2d\u3001\u62c7\u4e09\u6307\u634f\u4f4f\u5251\u5c16\uff0c\u53cd\u624b\u4e00\u63d2\uff0c\u5c06\u5251 \u63d2\u5165\u80cc\u540e\u7684\u5251\u9798\u91cc\u51b7\u51b7\u9053\uff1a\u201c\u4e91\u5927\u4fa0\u9a70\u9a6c\u72c2\u5954\uff0c\u662f\u5426\u4e5f\u662f\u4e3a\u7740\u90a3\u60e8\u547c\u4e4b\u58f0\uff01\u201d \u3000\u3000\u4e91\u4e2d\u7a0b\u5fc3\u4e2d\u4e00\u52a8\uff0c\u53e3\u4e2d\u5374\u6c89\u58f0\u9053\uff1a\u201c\u6b63\u662f\u3002\u201d \u3000\u3000\u4f46\u89c1\u5230\u8fd9\u6768\u4e00\u5251\u7684\u795e\u60c5\uff0c\u77e5\u9053\u4ed6\u5fc5\u7136\u6765\u81ea\u81ea\u5df1\u8981\u53bb\u7684\u5730\u65b9\uff0c\u672c\u6765\u4e5f\u60f3\u63a2\u95ee\u4e00\u4e0b\uff0c\u4f46\u81ea\u5df1 \u5374\u548c\u6b64\u4eba\u843d\u843d\u96be\u5408\uff0c\u6781\u4e0d\u6295\u7f18\uff0c\u662f\u4ee5\u53c8\u5c06\u53e3\u8fb9\u5c06\u8981\u8bf4\u51fa\u7684\u8bdd\uff0c\u5fcd\u4e86\u56de\u53bb\u3002 \u3000\u3000\u5374\u89c1\u8fd9\u6768\u4e00\u5251\u70af\u70af\u7684\u76ee\u5149\u4e2d\uff0c\u7a81\u7136\u63a0\u8fc7\u4e00\u4e1d\u96be\u4ee5\u6349\u6478\u7684\u5149\u5f69\uff0c\u4f46\u77ac\u5373\u6062\u590d\u5e73\u5e38\uff0c\u51b7\u51b7\u4e00 \u7b11\uff0c\u53c8\u9053\uff1a\u201c\u4e91\u5927\u4fa0\u8981\u53bb\uff0c\u90a3\u597d\u6781\u4e86\u3002\u201d \u3000\u3000\u53cc\u81c2\u4e00\u5f20\uff0c\u8eab\u5f62\u4e4d\u5c55\uff0c\u53c8\u6295\u5165\u8857\u8fb9\u7684\u9634\u5f71\u4e2d\u3002 \u3000\u3000\u4e91\u4e2d\u7a0b\u6697\u53f9\u4e00\u58f0\uff0c\u5fd6\u9053\uff1a\u201c\u6b64\u4eba\u867d\u7136\u72c2\u505a\uff0c\u4f46\u8eab\u624b\u7684\u786e\u4e0d\u5f31\uff0c\u65e0\u602a\u80fd\u5728\u8700\u4e2d\u4eab\u6709\u76db\u540d\uff0c \u4f46\u65b9\u624d\u89c1\u4ed6\u7684\u795e\u8272\uff0c\u5374\u53c8\u6ee1\u9732\u60ca\u60f6\uff0c\u90a3\u4e48\u524d\u884c\u4e4b\u5904\uff0c\u53c8\u6709\u4ec0\u4e48\u503c\u5f97\u4ed6\u5982\u6b64\u60ca\u6050\u7684\u4e8b\u5462\uff1f\u201d \u3000\u3000\u4ed6\u5fc3\u4e2d\u601d\u6f6e\u53cd\u590d\uff0c\u4efb\u51ed\u80ef\u4e0b\u7684\u9a6c\u5728\u8857\u5fc3\u7acb\u4e86\u8bb8\u4e45\uff0c\u7a81\u7136\u94c1\u638c\u53cd\u6325\uff0c\u51fb\u5728\u9a6c\u80a1\u4e0a\u3002 \u3000\u3000\u90a3\u5339\u9a6c\u4fbf\u53c8\u7bad\u4e5f\u4f3c\u5730\u671d\u524d\u9762\u7a9c\u53bb\uff0c\u77ac\u606f\u4e4b\u95f4\uff0c\u4fbf\u9a70\u51fa\u57ce\u5916\uff0c\u4e91\u4e2d\u7a0b\u53f3\u624b\u4e00\u5e26\u7f30\u7ef3\uff0c\u76ee\u5149 \u56db\u4e0b\u4e00\u626b\uff0c\u4f46\u89c1\u4e1c\u5317\u4e0d\u8fdc\u4e4b\u5904\uff0c\u706b\u82b1\u7a81\u7136\u51b2\u5929\u800c\u8d77\uff0c\u5c06\u5929\u7554\u90fd\u67d3\u5f97\u4e00\xb7\u7247\u9c9c\u8840\u822c\u7684\u7ea2\u8272\u3002 \u3000\u3000\u4ed6\u5fae\u4e00\u6253\u9a6c\uff0c\u518d\u5f80\u524d\u9a70\uff0c\u5954\u51fa\u4e00\u7bad\u591a\u5730\uff0c\u7a81\u7136\u52d2\u4f4f\u9a6c\uff0c\u77eb\u5065\u7684\u8eab\u5f62\uff0c\u500f\u7136\u4ece\u9a6c\u978d\u63a0\u8d77\uff0c \u201c\u55d6\u3001\u55d6\u201d\u51e0\u4e2a\u8d77\u843d\uff0c\u4fbf\u5f80\u8d77\u706b\u5904\u5954\u53bb\u3002 \u3000\u3000\u706b\u5149\u4e4b\u4e2d\uff0c\u4f46\u89c1\u9ed1\u5f71\u5e62\u5e62\uff0c\u60e8\u547c\u4e4b\u58f0\uff0c\u66f4\u662f\u4e0d\u7edd\u4e8e\u8033\u3002 \u3000\u3000\u5ffd\u7136\u4e09\u6761\u4eba\u5f71\u81ea\u706b\u5149\u4e2d\u51b2\u5929\u800c\u8d77\uff0c\u8f7b\u529f\u4e4b\u60ca\u4eba\uff0c\u7adf\u662f\u65e0\u4e0e\u4f26\u6bd4\uff0c\u51cc\u7a7a\u4e09\u4e08\uff0c\u5728\u7a7a\u4e2d\u9f50\u4e00 \u8f6c\u6298\uff0c\u4fbf\u95ea\u7535\u822c\u7684\u6d88\u5931\u4e86\u3002 \u3000\u3000\u4e91\u4e2d\u7a0b\u53f3\u624b\u201c\u537f\u201d\u7684\u4e00\u626f\uff0c\u5c06\u624b\u4e0a\u7684\u957f\u886b\u626f\u5f00\u6765\uff0c\u6293\u8d77\u957f\u886b\u7684\u4e0b\u6446\uff0c\u5728\u8170\u7554\u6253\u4e86\u4e2a\u7ed3\uff0c \u5de6\u624b\u63a2\u624b\u5165\u6000\uff0c\u4f46\u542c\u201c\u545b\u561f\u201d\u4e00\u58f0\uff0c\u4ed6\u638c\u4e2d\u5df2\u591a\u4e86\u4e00\u53e3\u957f\u7ea6\u4e09\u5c3a\u3001\u7cbe\u5149\u593a\u76ee\u7684\u5229\u5251\u3002 \u3000\u3000\u8fd9\u6b63\u662f\u6614\u5e74\u591a\u81c2\u795e\u5251\u4ed7\u4ee5\u626c\u540d\u5929\u4e0b\u7684\u5229\u5203\uff0c\u9f99\u7eb9\u8f6f\u5251\uff0c\u4e5f\u662f\u829c\u6e56\u4e91\u95e8\u4ee3\u4ee3\u76f8\u4f20\u7684\u5229\u5668\u3002 \u3000\u3000\u4e91\u4e2d\u7a0b\u4e00\u5251\u5728\u624b\uff0c\u8c6a\u6c14\u9038\u98de\uff0c\u5fae\u4e00\u584c\u8170\uff0c\u8eab\u5f62\u66b4\u957f\uff0c\u71d5\u5b50\u4e09\u6284\u6c34\uff0c\u201c\u55d6\u3001\u55d6\u3001\u55d6\u201d\u4e09\u4e2a \u8d77\u843d\uff0c\u53c8\u524d\u62e7\u5341\u4e08\u3002 \u3000\u3000\u53ea\u8d1d\u4e00\u7247\u90ca\u91ce\u4e4b\u4fa7\uff0c\u8822\u7acb\u7740\u4e00\u5ea7\u9ad8\u5927\u7684\u697c\u9601\uff0c\u5374\u5168\u5df2\u88ab\u706b\u71c3\u8d77\uff0c\u4e00\u4e2a\u6ee1\u8eab\u5e26\u7740\u706b\u7130\u7684\u5927 \u6c49\uff0c\u60e8\u53eb\u7740\u7531\u70c8\u706b\u4e2d\u7a9c\u4e86\u51fa\u6765\uff0c\u53cc\u624b\u63a9\u7740\u9762\u76ee\uff0c\u5728\u5730\u4e0a\u8fde\u6eda\u4e86\u51e0\u6eda\uff0c\u4f46\u5374\u4ecd\u672a\u5c06\u8863\u88f3\u71c3\u8d77\u7684 \u706b\u7130\u538b\u706d\u3002 \u3000\u3000\u4ec1\u4e49\u5251\u5ba2\u4e00\u4e2a\u7bad\u6b65\uff0c\u7a9c\u5230\u8fd9\u4eba\u8eab\u4e0a\uff0c\u53ea\u89c1\u8fd9\u4eba\u5728\u5730\u4e0a\u6eda\u52a8\u7684\u52bf\u5b50\u8d8a\u6765\u8d8a\u5f31\uff0c\u7ec8\u4e8e\u4f0f\u5728\u5730 \u4e0a\uff0c\u4e0d\u80fd\u52a8\u5f39\u4e86\u3002 \u3000\u3000\u706b\u52bf\u8d8a\u6765\u8d8a\u65fa\uff0c\u706b\u5149\u4e2d\u5374\u518d\u4e5f\u6ca1\u6709\u60e8\u547c\u7684\u58f0\u97f3\u4f20\u51fa\uff0c\u6ee1\u5929\u706b\u5f71\u4e2d\uff0c\u53ea\u89c1\u5730\u4e0a\u6a2a\u4e03\u575a\u516b\u7684 \u5012\u7740\u4e00\u4e9b\u5c38\u8eab\uff0c\u6709\u7684\u867d\u7136\u8fd8\u6709\u547b\u541f\u58f0\uff0c\u5374\u5df2\u5fae\u5f31\u5f97\u51e0\u4e4e\u542c\u4e0d\u751a\u6e05\u4e86\u3002 \u3000\u3000\u8f70\u7684\u4e00\u58f0\uff0c\u4e00\u6839\u6881\u6728\u843d\u4e0b\uff0c\u63a5\u7740\u54d7\u7136\u4e00\u58f0\u5de8\u9707\uff0c\u90a3\u680b\u71c3\u70e7\u7740\u7684\u697c\u9601\uff0c\u4fbf\u5df2\u5012\u584c\u4e00\u534a\u3002 \u3000\u3000\u4f46\u662f\u7f6e\u8eab\u8fd9\u4e00\u7247\u5c38\u8eab\u4e2d\u7684\u4e91\u4e2d\u7a0b\uff0c\u5374\u751f\u50cf\u662f\u6ca1\u6709\u542c\u89c1\u8fd9\u58f0\u5de8\u9707\u4f3c\u7684\uff0c\u4ed6\u4e00\u751f\u95ef\u8361\u6c5f\u6e56\uff0c \u4f46\u662f\u8fd9\u79cd\u51c4\u60e8\u7684\u666f\u8c61\uff0c\u5374\u8fd8\u662f\u7b2c\u4e00\u6b21\u89c1\u5230\u3002 \u3000\u3000\u706b\u52bf\u718a\u718a\uff0c\u4f7f\u5f97\u5468\u8f6c\u6570\u5341\u4e08\u5730\u65b9\u53d8\u5f97\u96be\u4ee5\u5fcd\u53d7\u7684\u9177\u70ed\uff0c\u4f46\u8fd9\u4ec1\u4e49\u5251\u5ba2\u5374\u53ea\u89c9\u624b\u8db3\u51b0\u51b7\uff0c \u9635\u9635\u5bd2\u610f\u5b9c\u900f\u80cc\u810a\u3002 \u3000\u3000\u4ed6\u7f13\u7f13\u79fb\u52a8\u7740\u811a\u6b65\uff0c\u8d70\u5230\u53e6\u4e00\u4e2a\u4ecd\u6709\u547b\u541f\u4e4b\u58f0\u53d1\u51fa\u7684\u5927\u6c49\u65c1\u8fb9\uff0c\u5de6\u624b\u5012\u63d0\u7740\u5251\uff0c\u53f3\u624b\u8f7b \u8f7b\u6284\u8d77\u8fd9\u4eba\u7684\u80a9\u5934\uff0c\u53ea\u89c1\u8fd9\u6761\u672c\u6765\u7cbe\u608d\u65e0\u6bd4\u7684\u6c49\u5b50\uff0c\u6b64\u523b\u8eab\u4e0a\u7684\u8863\u886b\uff0c\u90fd\u5df2\u88ab\u70e7\u5f97\u516d\u96f6\u516b \u843d\uff0c\u9739\u51fa\u91cc\u9762\u7684\u6f06\u9ed1\u7684\u80a4\u8089\u6765\uff0c\u524d\u80f8\u4e00\u5904\u4f24\u75d5\uff0c\u4ecd\u4e0d\u4f4f\u7684\u5f80\u5916\u6d41\u7740\u9c9c\u8840\uff0c\u8eab\u5b50\u65b9\u88ab\u4e91\u4e2d\u7a0b\u6276 \u8d77\uff0c\u5c31\u53c8\u4e00\u58f0\u60e8\u547c\uff0c\u7741\u5f00\u90a3\u53cc\u6ee1\u5e03\u8840\u4e1d\u7684\u773c\u775b\uff0c\u5728\u4e91\u4e2d\u7a0b\u8eab\u4e0a\u8f6c\u4e86\u4e24\u8f6c\uff0c\u5fae\u5f31\u7684\u5f20\u5f00\u53e3\uff0c\u50cf \u662f\u60f3\u8bf4\u4ec0\u4e48\uff0c\u5374\u53c8\u65e0\u529b\u8bf4\u51fa\u6765\u3002 \u3000\u3000\u4e91\u4e2d\u7a0b\u76ee\u5149\u5728\u8fd9\u4eba\u8eab\u4e0a\u51dd\u6ce8\u4e86\u534a\u664c\uff0c\u4e0d\u7981\u53c8\u4ece\u5fc3\u4e2d\u957f\u957f\u53f9\u51fa\u4e00\u53e3\u6c14\u6765\uff0c\u6b64\u523b\u81ea\u5df1\u4f38\u624b\u6240 \u6276\u6301\u7684\u8fd9\u5782\u6b7b\u7684\u6c49\u5b50\uff0c\u7adf\u5c31\u662f\u6614\u65e5\u540d\u9707\u6c5f\u6e56\u7684\u7ea2\u4e2d\u4e09\u6770\u4e2d\u7684\u4e86\u5927\u7237\uff0c\u4e0d\u4e45\u4ee5\u524d\uff0c\u81ea\u5df1\u8fd8\u4eb2\u773c \u89c1\u5230\u6b64\u4eba\u624b\u626c\u4e1d\u97ad\uff0c\u5feb\u9a6c\u9a70\u4e8e\u6c5f\u5357\u9053\u4e0b\uff0c\u800c\u6b64\u523b\u2026\u2026 \u3000\u3000\u201c\u4e16\u4e8b\u7684\u53d8\u5e7b\uff0c\u662f\u591a\u4e48\u5de8\u5927\u5440\u3002\u201d \u3000\u3000\u8fd9\u7ea2\u5dfe\u4e09\u6770\u5728\u6c5f\u6e56\u4e2d\u867d\u662f\u51f6\u6a2a\u7684\u89d2\u8272\uff0c\u4f46\u7ec8\u7a76\u4ed6\u4e5f\u662f\u4eba\u5440\u3002\u4e91\u4e2d\u7a0b\u89c1\u4e86\u4ed6\u8fd9\u7b49\u6b7b\u72b6\uff0c\u4e5f \u4e0d\u7981\u5154\u6b7b\u72d0\u60b2\uff0c\u7269\u4f24\u5176\u7c7b\uff0c\u9ed8\u7136\u957f\u53f9\u4e86\u4e00\u58f0\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u4e01\u5144\uff0c\u4f60\u53ef\u8fd8\u8ba4\u8bc6\u5c0f\u5f1f\uff1f\u65b9 \u624d\u2026\u2026\u8fd9\u6869\u4e8b\uff0c\u7a76\u7adf\u662f\u8c01\u5e72\u51fa\u6765\u7684\uff1f\u201d \u3000\u3000\u8fd9\u7ea2\u5dfe\u4e09\u6770\u4e4b\u9996\u773c\u775b\u53c8\u8f6c\u52a8\u4e86\u4e24\u4e0b\uff0c\u5fae\u5fae\u52a8\u4e86\u52a8\u5634\u5df4\uff0c\u4f46\u8c01\u4e5f\u65e0\u6cd5\u4e86\u89e3\u4ed6\u5634\u5507\u8fd9\u513f\u4e2a\u8f7b \u5fae\u7684\u52a8\u4f5c\uff0c\u6240\u8868\u793a\u7684\u610f\u601d\u3002 \u3000\u3000\u4e91\u4e2d\u7a0b\u6c89\u58f0\u53c8\u9053\uff1a\u201c\u662f\u4e0d\u662f\u5feb\u5200\u4f1a\uff1f\u201d \u3000\u3000\u4e01\u7ea2\u5dfe\u865a\u5f31\u5730\u5c06\u5934\u6447\u52a8\u4e24\u4e0b\u3002 \u3000\u3000\u4e91\u4e2d\u7a0b\u4fef\u9996\u6c89\u601d\u4e00\u4e0b\uff0c\u53c8\u9053\uff1a\u201c\u662f\u4e0d\u662f\u9ed1\u7c73\u5e2e\uff1f\u2026\u2026\u54e6\u2026\xb7\xb7\u96be\u9053\u662f\u592a\u6e56\u4e09\u5341\u516d\u5be8 \u5417\uff1f\u201d \u3000\u3000\u4ed6\u4e00\u62cd\u524d\u989d\uff1a\u201c\u5357\u6cb3\u90a3\u8fb9\u7684\u5929\u9634\u6559\uff0c\u548c\u4e86\u5144\u4e5f\u7ed3\u6709\u6881\u5b50\u5427\uff1f\u201d \u3000\u3000\u4f46\u662f\uff0c\u4ed6\u6240\u5f97\u5230\u7684\u7b54\u6848\uff0c\u53ea\u662f\u5343\u7bc7\u4e00\u5f8b\u7684\u6447\u5934\uff0c\u4ed6\u5fc3\u91cc\u7684\u7591\u60d1\uff0c\u4e0d\u7981\u4e5f\u8d8a\u6765\u8d8a\u91cd\uff1a\u201c\u8fd9 \u53c8\u4f1a\u662f\u54ea\u4e9b\u4eba\u4e0b\u7684\u8fa3\u624b\u5462\uff1f\u201d \u3000\u3000\u53ea\u89c1\u8fd9\u4e86\u7ea2\u4e2d\u773c\u4e2d\u63a0\u8fc7\u4e00\u62b9\u9eef\u6de1\u7684\u5149\u91c7\uff0c\u50cf\u662f\u60b2\u54c0\u81ea\u5df1\u81f3\u6b7b\u8fd8\u4e0d\u80fd\u5c06\u81ea\u5df1\u7684\u4ec7\u5bb6\u8bf4\u51fa \u6765\uff0c\u7ec8\u4e8e\u4e24\u817f\u5fae\u4f38\uff0c\u4ea6\u81ea\u6c14\u7edd\u4e86\u3002 \u3000\u3000\u4e91\u4e2d\u7a0b\u53c8\u957f\u58f0\u4e00\u53f9\uff0c\u8f7b\u8f7b\u653e\u4e0b\u5c38\u8eab\uff0c\u5374\u89c1\u8fd9\u4e5f\u66fe\u5728\u6c5f\u6e56\u53f1\u54a4\u4e00\u65f6\u7684\u7ea2\u5dfe\u4f1a\u603b\u74e2\u628a\u5b50\uff0c\u867d \u5df2\u6c14\u7edd\u3001\u4f46\u4e00\u53cc\u6ee1\u5e03\u8840\u4e1d\u7684\u5389\u773c\uff0c\u5374\u4ecd\u6ca1\u6709\u95ed\u4e0a\uff0c\u800c\u662f\u51dd\u6ce8\u4e00\u5904\uff0c\u50cf\u662f\u4ed6\u4e34\u7ec8\u4e4b\u9645\uff0c\u53c8\u53d1\u73b0 \u4e86\u4ec0\u4e48\uff0c\u53ea\u662f\u4ed6\u5374\u65e9\u5df2\u65e0\u529b\u8bf4\u51fa\u6765\u7f62\u4e86\u3002 \u3000\u3000\u4e91\u4e2d\u7a0b\u76ee\u4e2d\u4e00\u52a8\uff0c\u62e7\u8f6c\u8eab\u8eaf\uff0c\u76ee\u5149\u95ea\u7535\u5730\u4e00\u8f6c\uff0c\u53ea\u89c1\u5fae\u98ce\u5439\u52a8\u5904\uff0c\u4e00\u7c92\u7ec6\u5c0f\u7684\u73e0\u7c92\uff0c\u5728 \u5730\u9762\u4e0a\u7f13\u7f13\u6eda\u52a8\u7740\uff0c\u5728\u6f2b\u5929\u706b\u7130\u6620\u5f71\u4e2d\uff0c\u53d1\u51fa\u593a\u76ee\u7684\u8840\u7ea2\u8272\u3002 \u3000\u3000\u4ed6\u811a\u5c16\u4e00\u987f\uff0c\u8eab\u5f62\u671d\u8fd9\u7c92\u7ea2\u73e0\u63a0\u53bb\uff0c\u54ea\u77e5\u773c\u524d\u7a81\u7136\u53c8\u6709\u4eba\u5f71\u4e00\u95ea\uff0c\u6765\u52bf\u4e4b\u6025\uff0c\u7adf\u6bd4\u81ea\u5df1 \u8fd8\u5feb\u7740\u534a\u6b65\u3002 \u3000\u3000\u8fd9\u7a81\u73b0\u7684\u4eba\u5f71\uff0c\u4f7f\u5f97\u4ed6\u5fc3\u4e2d\u4e00\u63a0\uff0c\u771f\u6c14\u731b\u6c89\uff0c\u786c\u751f\u751f\u5c06\u524d\u8fdb\u7684\u52bf\u9053\u987f\u4e86\u4e0b\u6765\uff0c\u76ee\u5149\u52a8 \u5904\uff0c\u53ea\u89c1\u65e5\u524d\u5728\u829c\u6e56\u62dc\u5bff\uff0c\u90a3\u4e24\u4e2a\u795e\u79d8\u800c\u7f8e\u8273\u7684\u7ea2\u88f3\u5c11\u5973\uff0c\u6b64\u523b\u7adf\u53c8\u8d6b\u7136\u7ad9\u5728\u81ea\u5df1\u7684\u9762\u524d\uff0c \u5e26\u7740\u4e00\u8138\u6e29\u67d4\u800c\u751c\u871c\u7684\u7b11\u5bb9\uff0c\u5de6\u4fa7\u5c11\u5973\u7684\u4e00\u53ea\u7389\u624b\u91cc\uff0c\u6b64\u523b\u5170\u82b1\u4f3c\u5730\u4f38\u51fa\u4e24\u53ea\u6625\u8471\u7389\u6307\uff0c\u5939 \u7740\u90a3\u7c92\u9c9c\u660e\u7684\u7ea2\u73e0\u3002 \u3000\u3000\u8fd9\u4e24\u4e2a\u7ea2\u88f3\u5c11\u5973\u79cb\u6ce2\u6d41\u8f6c\uff0c\u63a9\u53e3\u4e00\u7b11\uff0c\u8eac\u4e0b\u8170\u53bb\uff0c\u671d\u4e91\u4e2d\u7a0b\u4e00\u798f\uff0c\u5a07\u58f0\u7b11\u9053\uff1a\u201c\u6211\u5f53\u662f \u8c01\u5462\uff1f\u539f\u6765\u662f\u4e91\u5c11\u4fa0\uff0c\u60a8\u600e\u4e48\u4e5f\u6765\u4e86\uff0c\u60a8\u770b\uff0c\u8fd9\u9897\u5c0f\u73e0\u5b50\u591a\u597d\u73a9\uff0c\u662f\u60a8\u7684\u5417\uff1f\u9001\u7ed9\u6211\u4eec\u59d0\u59b9 \u4e24\u4e2a\u597d\u4e0d\u597d\uff1f\u201d \u3000\u3000\u4e91\u4e2d\u7a0b\u5fc3\u4e2d\u867d\u7136\u60ca\u7591\u4e0d\u5b89\uff0c\u4f46\u8fd9\u4ec1\u4e49\u5251\u5ba2\uff0c\u6bd5\u7adf\u4e0d\u662f\u7b49\u95f2\u7684\u89d2\u8272\uff0c\u9762\u8272\u5fae\u53d8\u4e4b\u540e\uff0c\u77ac\u5373 \u6062\u590d\u9547\u9759\uff0c\u4ea6\u81ea\u62b1\u62f3\u7b11\u4e86\u7b11\u9053\uff1a\u201c\u591a\u65e5\u672a\u89c1\uff0c\u4e24\u4f4d\u59d1\u5a18\u8d8a\u53d1\u5a07\u8273\u4e86\uff0c\u8fd9\u79cd\u9c9c\u8840\u6dcb\u6f13\u7684\u5730\u65b9\uff0c \u4e24\u4f4d\u600e\u4e48\u4e5f\u6709\u5174\u8da3\u524d\u6765\u5462\uff1f\u201d \u3000\u3000\u8fd9\u4e24\u4e2a\u7ea2\u88f3\u5c11\u5973\u54af\u54af\u4e00\u7b11\uff0c\u5de6\u4fa7\u90a3\u4e2a\u7ea4\u624b\u4e00\u7f29\uff0c\u5c06\u624b\u4e2d\u7684\u7ea2\u73e0\u6536\u5165\u6000\u91cc\uff0c\u4e91\u4e2d\u7a0b\u53cc\u7709\u6697 \u76b1\uff0c\u5374\u89c1\u5979\u4eec\u5df2\u5a07\u7b11\u9053\uff1a\u201c\u4e91\u5c11\u4fa0\uff0c\u60a8\u4e0d\u8bf4\u8fd9\u73e0\u4e8e\u662f\u4e0d\u662f\u60a8\u7684\uff0c\u6211\u4eec\u53ef\u5c31\u6536\u4e0b\u4e86\u3002\u201d \u3000\u3000\u5de6\u4fa7\u90a3\u5c11\u5973\u4f38\u51fa\u4e00\u6839\u624b\u6307\uff0c\u5728\u81ea\u5df1\u5ae3\u7ea2\u7684\u9762\u988a\u4e0a\u5212\u4e86\u5212\uff0c\u7b11\u9053\uff1a\u201c\u4e91\u5c11\u4fa0\uff0c\u60a8\u770b\u8fd9\u4e2a\u4e2b \u5934\u8138\u76ae\u539a\u4e0d\u539a\uff0c\u968f\u4fbf\u5728\u5730\u4e0a\u6361\u8d77\u4e00\u6837\u4e1c\u897f\uff0c\u5c45\u7136\u5c31\u7b97\u662f\u81ea\u5df1\u7684\u4e86\u3002\u201d \u3000\u3000\u5de6\u4fa7\u7684\u5c11\u5973\u4e00\u6487\u5634\uff0c\u9053\uff1a\u201c\u4f60\u5462\uff01\u4f60\u521a\u624d\u4e0d\u662f\u4e5f\u548c\u6211\u5728\u62a2\uff0c\u73b0\u5728\u6ca1\u6709\u62a2\u5230\uff0c\u5c31\u773c\u7ea2\u4e86\u662f \u4e0d\u662f\uff1f\u4e91\u5c11\u4fa0\uff0c\u6211\u544a\u8bc9\u4f60\uff0c\u666e\u5929\u4e4b\u4e0b\uff0c\u5c31\u6570\u5979\u7684\u8138\u76ae\u6700\u539a\u4e86\u3002\u201d \u3000\u3000\u4e91\u4e2d\u7a0b\u5e72\u54b3\u4e86\u4e00\u58f0\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u8fd9\u7c92\u73e0\u5b50\uff0c\u867d\u975e\u5728\u4e0b\u6240\u6709\u4e4b\u7269\uff0c\u4f46\u5374\u2014\u2014\u201d\u4ed6\u5fc3\u4e2d\u5ffd\u7136 \u4e00\u52a8\uff0c\u5c06\u81ea\u5df1\u5df2\u7ecf\u8bf4\u5230\u53e3\u8fb9\u7684\u8bdd\uff0c\u54bd\u4e86\u56de\u53bb\uff0c\u6539\u53e3\u9053\uff1a\u201c\u662f\u81ea\u7136\u5e94\u8be5\u5f52\u4e24\u4f4d\u6240\u6709\u4e86\u3002\u201d \u3000\u3000\u5de6\u4fa7\u90a3\u5c11\u5973\u79cb\u6ce2\u6d41\u52a8\uff0c\u5a07\u7b11\u9053\uff1a\u201c\u8c22\u8c22\u60a8\u5566\u2014\u2014\u201d\u8bed\u72b9\u672a\u4e86\uff0c\u7a81\u7136\u9762\u8272\u5927\u53d8\uff0c\u76ee\u5149\u76f4\u52fe \u52fe\u77aa\u5728\u4e00\u5904\u3002 \u3000\u3000\u53e6\u4e00\u4e2a\u5c11\u5973\u773c\u775b\u968f\u7740\u5979\u4e00\u8f6c\uff0c\u5ae3\u7ea2\u7684\u9762\u988a\uff0c\u53c8\u7acb\u523b\u6cdb\u51fa\u4e00\u9635\u60ca\u6050\u4e4b\u8272\u3002 \u3000\u3000\u4ec1\u4e49\u5251\u5ba2\u62e7\u8170\u8f6c\u8eab\uff0c\u76ee\u5149\u4e00\u77a5\uff0c\u5374\u4e5f\u4e0d\u7981\u5927\u5403\u4e00\u60ca\uff0c\u51e0\u4e4e\u4e0d\u76f8\u4fe1\u81ea\u5df1\u7684\u773c\u775b\u3002 \u3000\u3000\u53ea\u89c1\u90a3\u680b\u4ecd\u5728\u71c3\u70e7\u7740\u697c\u9601\u7684\u718a\u718a\u706b\u7130\u4e4b\u4e2d\uff0c\u6b64\u523b\u7adf\u7f13\u7f13\u8d70\u51fa\u4e00\u4e2a\u4eba\u6765\uff0c\u957f\u8eab\u7389\u7acb\uff0c\u76ee\u5982 \u6717\u661f\uff0c\u8eab\u4e0a\u7a7f\u7740\u4e00\u4ef6\u9690\u5e26\u5149\u6cfd\u7684\u7384\u8272\u957f\u886b\u548c\u90a3\u9876\u7384\u8272\u65b9\u4e2d\uff0c\u7ade\u8fde\u534a\u70b9\u706b\u661f\u5b50\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u53ea\u89c1\u4ed6\u7f13\u7f13\u8d70\u51fa\u706b\u7a9f\uff0c\u6781\u4e3a\u6f47\u6d12\u4ece\u5bb9\u5730\u4e3e\u6b65\u800c\u6765\uff0c\u70af\u70af\u751f\u5149\u7684\u4e00\u53cc\u4fca\u76ee\uff0c\u5728\u90a3\u4e24\u4e2a\u7ea2\u88f3 \u5c11\u5973\u8eab\u4e0a\u4e00\u8f6c\uff0c\u968f\u5373\u76ef\u5230\u4e91\u4e2d\u7a0b\u6240\u6301\u7684\u90a3\u53e3\u8fdc\u8f83\u5bfb\u5e38\u5b9d\u5251\u4e3a\u77ed\u7684\u9f99\u7eb9\u8f6f\u5251\u4e0a\u3002 \u3000\u3000\u4e24\u4e2a\u7ea2\u88f3\u5c11\u5973\u5bf9\u671b\u4e86\u4e00\u773c\uff0c\u9762\u4e0a\u4fbf\u53c8\u56de\u590d\u5979\u4eec\u4ec5\u6709\u7684\u90a3\u79cd\u6e29\u67d4\u751c\u7b11\uff0c\u671d\u4e91\u4e2d\u7a0b\u7b11\u9053\uff1a \u201c\u4e91\u5c11\u4fa0\uff0c\u6211\u4eec\u8d70\u4e86\uff0c\u8fc7\u4e24\u5929\u6211\u4eec\u518d\u4e0b\u5c71\u6765\u62dc\u8c12\u4e91\u8001\u7237\uff0c\u8bf7\u60a8\u56de\u53bb\u4ee3\u6211\u4eec\u5411\u4ed6\u8001\u4eba\u5bb6\u95ee \u597d\u3002\u201d \u3000\u3000\u56db\u9053\u79cb\u6ce2\uff0c\u7535\u4e5f\u4f3c\u7684\u5411\u90a3\u7384\u886b\u5c11\u5e74\u8eab\u4e0a\u4e00\u626b\uff0c\u8138\u4e0a\u53c8\u4e00\u626b\uff0c\u67f3\u8170\u8f7b\u6446\uff0c\u4e00\u8d77\u5982\u98de\u63a0\u53bb\u3002 \u3000\u3000\u90a3\u7384\u886b\u5c11\u5e74\u5fae\u5fae\u4e00\u7b11\uff0c\u76ee\u5149\u4e2d\u5fae\u5fae\u6709\u4e9b\u8d5e\u8d4f\u7684\u610f\u5473\uff0c\u50cf\u662f\u5728\u8d5e\u8d4f\u8fd9\u4e24\u4e2a\u7ea2\u88f3\u5c11\u5973\u7684\u8f7b\u529f \u4e4b\u9ad8\uff0c\u53c8\u50cf\u662f\u5728\u8d5e\u8d4f\u7740\u5979\u4eec\u7684\u806a\u660e\u3002 \u3000\u3000\u7136\u540e\uff0c\u4ed6\u8f6c\u56de\u8eab\uff0c\u671d\u4e91\u4e2d\u7a0b\u5f53\u5934\u4e00\u63d6\uff0c\u6717\u58f0\u7b11\u7740\u8bf4\u9053\uff1a\u201c\u5c0f\u53ef\u5192\u6627\uff0c\u9601\u4e0b\u60f3\u5fc5\u5c31\u662f\u4ec1\u4e49 \u5251\u5ba2\uff0c\u4e91\u4e2d\u7a0b\u5927\u4fa0\u5427\uff1f\u201d \u3000\u3000\u4e91\u4e2d\u7a0b\u5fae\u5fae\u4e00\u6123\uff0c\u65b9\u624d\u4ed6\u773c\u770b\u8fd9\u5c11\u5e74\u5b89\u6b65\u81ea\u706b\u4e2d\u884c\u51fa\uff0c\u6b64\u523b\u53c8\u89c1\u6b64\u4eba\u4e00\u89c1\u81ea\u5df1\u4e4b\u9762\uff0c\u5c31 \u80fd\u76f4\u547c\u51fa\u81ea\u5df1\u7684\u540d\u5b57\u6765\uff0c\u5fc3\u4e2d\u4e0d\u7981\u65e2\u60ca\u4e14\u602a\uff0c\u5446\u5446\u7684\u6123\u4e86\u534a\u664c\uff0c\u7adf\u6ca1\u6709\u8bf4\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u8fd9\u7384\u886b\u5c11\u5e74\u5fae\u5fae\u4e00\u7b11\uff0c\u53c8\u9053\uff1a\u201c\u5c0f\u5f1f\u521d\u5165\u6c5f\u6e56\uff0c\u5bf9\u6b66\u6797\u4fa0\u8e2a\uff0c\u867d\u7136\u751f\u758f\u5f97\u5f88\uff0c\u4f46\u4e91\u5927\u4fa0 \u624b\u4e2d\u7684\u8fd9\u67c4\u6bd4\u5bfb\u5e38\u5251\u77ed\u4e86\u516d\u5bf8\u5374\u6bd4\u5e38\u5251\u950b\u5229\u767e\u500d\u7684\u9f99\u7eb9\u8f6f\u5251\uff0c\u5c0f\u5f1f\u5374\u65e9\u5c31\u4ece\u5148\u7236\u548c\u5bb6\u5e08\u53e3\u4e2d \u542c\u5230\u8fc7\uff0c\u662f\u4ee5\u5c0f\u5f1f\u4e00\u89c1\u6b64\u5251\uff0c\u4fbf\u731c\u51fa\u9601\u4e0b\u5fc5\u5b9a\u5c31\u662f\u4ec1\u4e49\u5251\u5ba2\u4e0b\u201d\u4e91\u4e2d\u7a0b\u5fc3\u4e2d\u6697\u5fd6\u9053\uff1a\u201c\u539f\u6765 \u4ed6\u662f\u8ba4\u5f97\u8fd9\u53e3\u5251\u3002\u201d \u3000\u3000\u76ee\u5149\u4e0a\u4e0a\u4e0b\u4e0b\u5728\u8fd9\u4f4d\u7384\u886b\u5c11\u5e74\u8eab\u4e0a\u4e00\u8f6c\uff0c\u53ea\u89c1\u4ed6\u6f47\u6d12\u633a\u7acb\uff0c\u6709\u5982\u4e34\u98ce\u7389\u6811\uff0c\u8a00\u7b11\u8c26\u8c26\uff0c \u5374\u5e26\u7740\u4e09\u5206\u5112\u96c5\u4e4b\u6c14\uff0c\u4e0d\u7981\u5927\u8d77\u597d\u611f\uff0c\u53c8\u81ea\u5fd6\u9053\uff1a\u201c\u8fd9\u5c11\u5e74\u7684\u6b66\u529f\uff0c\u867d\u7136\u8fd8\u4e0d\u77e5\u9053\u6df1\u6d45\uff0c\u53ef \u5c31\u4ece\u4ed6\u65b9\u624d\u4ece\u706b\u4e2d\u5b89\u6b65\u800c\u8d70\u7684\u795e\u6001\u770b\u6765\uff0c\u8fd9\u5c11\u5e74\u663e\u7136\u6000\u6709\u4e00\u8eab\u7edd\u6280\uff0c\u5374\u504f\u504f\u53c8\u6ca1\u6709\u534a\u70b9\u72c2 \u6001\uff0c\u5509\uff0c\u8fd1\u5e74\u6765\u6c5f\u6e56\u4e2d\uff0c\u540e\u8d77\u9ad8\u624b\uff0c\u56fa\u7136\u6781\u591a\uff0c\u53ef\u662f\u8fd9\u5c11\u5e74\u6c14\u5ea6\u4e4b\u9ad8\uff0c\u5374\u4e0d\u662f\u4efb\u4f55\u4eba\u80fd\u53ca \u7684\u3002\u201d \u3000\u3000\u8fd9\u5ff5\u5934\u5728\u4ed6\u5fc3\u4e2d\u4e00\u8f6c\uff0c\u76ee\u5149\u62ac\u5904\uff0c\u53ea\u89c1\u8fd9\u4f4d\u7384\u886b\u5c11\u5e74\u4ecd\u542b\u7b11\u671b\u7740\u81ea\u5df1\uff0c\u5fd9\u4e5f\u7b11\u9053\uff1a\u201c\u5c0f \u53ef\u6b63\u662f\u4e91\u4e2d\u7a0b\uff0c\u4e0d\u77e5\u5144\u53f0\u9ad8\u59d3\uff0c\u4ee4\u5e08\u662f\u54ea\u4e00\u4f4d\uff1f\u201d \u3000\u3000\u5de6\u624b\u5fae\u62ac\uff0c\u53f3\u624b\u7684\u98df\u4e2d\u4e8c\u6307\uff0c\u631f\u7740\u5251\u5c16\u4e00\u5f2f\uff0c\u5c06\u638c\u4e2d\u5251\u56f4\u5728\u8170\u91cc\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u7a81\u7136\u957f\u53f9\u4e00\u58f0\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u4e91\u5927\u54e5\uff0c\u4f60\u96be\u9053\u4e0d\u8bb0\u5f97\uff0c\u5341\u4f59\u5e74\u524d\uff0c\u90a3\u7f20\u5728\u4f60\u8eab\u8fb9\u6c42 \u4f60\u6388\u4e24\u62db\u4e91\u95e8\u5251\u6cd5\u7684\u957f\u537f\u4e86\u5417\uff1f\u201d \u3000\u3000\u4e91\u4e2d\u7a0b\u5fc3\u5934\u6251\u5730\u4e00\u8df3\uff0c\u9000\u4e86\u4e24\u6b65\uff0c\u7a81\u53c9\u4e00\u63a0\u800c\u524d\uff0c\u7d27\u7d27\u63e1\u4f4f\u8fd9\u5c11\u5e74\u7684\u53cc\u624b\uff0c\u8fde\u58f0\u9053\uff1a \u201c\u539f\u6765\u4f60\u5c31\u662f\u957f\u537f\u5f1f\uff0c\u5341\u5e74\u4e0d\u89c1\uff0c\u53ef\u60f3\u6b7b\u54e5\u54e5\u6211\u4e86\uff0c\u957f\u537f\u5f1f\uff0c\u4f60\u600e\u4e48\u4e5f\u6765\u5230\u8fd9\u91cc\u4e86\uff0c\u8fd9\u5341\u5e74 \u6765\uff0c\u4f60\u90fd\u5728\u54ea\u91cc\u53bb\u4e86\uff0c\u8001\u4f2f\u4ed6\u53ef\u597d\u5417\uff1f\u5509\u2014\u2014\u5c81\u6708\u5982\u68ad\uff0c\u957f\u537f\u5f1f\uff0c\u4f60\u5df2\u51fa\u843d\u5f97\u4e00\u8868\u4eba\u6750\uff0c\u53c8 \u6709\u4e00\u8eab\u7edd\u6280\uff0c\u53ef\u662f\u2014\u2014\u54e5\u54e5\u6211\u5374\u5df2\u8001\u4e86\u3002\u201d \u3000\u3000\u4ed6\u8bed\u58f0\u6025\u5207\uff0c\u663e\u89c1\u5f97\u5fc3\u4e2d\u6781\u4e3a\u5174\u594b\uff0c\u56e0\u4e3a\u4ed6\u6b64\u523b\u5df2\u77e5\u9053\u7ad9\u5728\u4ed6\u9762\u524d\uff0c\u8fd9\u6c14\u7387\u8c26\u8c26\u7684\u7384\u886b \u5c11\u5e74\uff0c\u5c31\u662f\u81ea\u5df1\u7236\u4eb2\u751f\u5e73\u6700\u6700\u94a6\u4f69\u7684\u4eba\u7269\u2014\u2014\u4e2d\u539f\u5927\u4fa0\u5353\u6d69\u7136\u7684\u7231\u5b50\u5353\u957f\u537f\u3002 \u3000\u3000\u4ed6\u5927\u559c\u4e4b\u4e0b\uff0c\u5fc3\u60c5\u65e0\u6bd4\u7684\u6fc0\u52a8\uff0c\u76ee\u5149\u559c\u60a6\u5730\u51dd\u6ce8\u5728\u5353\u957f\u537f\u8138\u4e0a\uff0c\u54ea\u77e5\u5374\u770b\u5230\u4ed6\u9762\u4e0a\u6b64\u523b \u7adf\u6d41\u9732\u51fa\u4e00\u79cd\u6781\u4e3a\u60b2\u54c0\u67aa\u75db\u7684\u795e\u8272\u672a\uff0c\u800c\u4ed6\u88ab\u81ea\u5df1\u63e1\u5728\u624b\u4e2d\u7684\u4e00\u53cc\u624b\uff0c\u6b64\u523b\u5728\u5fae\u5fae\u98a4\u6296\u7740\u3002 \u3000\u3000\u4e00\u9635\u4e0d\u7965\u7684\u611f\u89c9\uff0c\u4f7f\u5f97\u4e91\u4e2d\u7a0b\u7684\u5fc3\u731b\u70c8\u5730\u8df3\u52a8\u4e86\u4e00\u4e0b\uff0c\u6025\u5207\u5730\u53c8\u95ee\u9053\uff1a\u201c\u957f\u537f\u5f1f\uff0c\u4f60\u600e \u4e48\u4e86\uff0c\u96be\u9053\u2026\u2026\u96be\u9053\u8001\u4f2f\u2026\u2026\u201d \u3000\u3000\u5353\u957f\u537f\u4e00\u53cc\u4fca\u76ee\u4e4b\u4e2d\uff0c\u6cea\u73e0\u76c8\u76c8\uff0c\u5fae\u5fae\u70b9\u4e86\u70b9\u5934\uff0c\u6676\u83b9\u7684\u6cea\u73e0\uff0c\u7ec8\u4e8e\u6cbf\u7740\u4ed6\u4fca\u9038\u7684\u9762 \u988a\uff0c\u6ed1\u843d\u4e0b\u6765\u3002 \u3000\u3000\u4e91\u4e2d\u7a0b\u5927\u559d\u4e00\u58f0\uff1a\u201c\u771f\u7684\uff1f\u201d \u3000\u3000\u5353\u957f\u537f\u4efb\u51ed\u51b0\u6e05\u7684\u6cea\u73e0\uff0c\u5728\u81ea\u5df1\u9762\u988a\u4e0a\u6ed1\u52a8\u7740\uff0c\u5341\u5e74\u524d\u9ec4\u5c71\u59cb\u4fe1\u5cf0\u4e0b\uff0c\u90a3\u4e00\u6bb5\u60e8\u7edd\u4eba\u8870 \u7684\u5f80\u4e8b\uff0c\u53c8\u590d\u50cf\u6012\u6f6e\u4e00\u6837\u7684\u5728\u4ed6\u5fc3\u91cc\u6f8e\u6e43\u8d77\u6765\uff0c\u4e8e\u662f\u4ed6\u7684\u773c\u6cea\u6d41\u5f97\u66f4\u5feb\u4e86\u3002 \u3000\u3000\u8fd9\u5341\u5e74\u672a\uff0c\u65e0\u6bd4\u8270\u82e6\u7684\u953b\u70bc\uff0c\u4f7f\u5f97\u4ed6\u7531\u201c\u5e38\u4eba\u201d\u800c\u53d8\u4e3a\u201c\u975e\u5e38\u4eba\u201d\u3002\u4ed6\u81ea\u4fe1\u81ea\u5df1\u7684\u60c5 \u611f\uff0c\u5df2\u7ecf\u8db3\u591f\u575a\u5f3a\u5f97\u80fd\u591f\u5fcd\u53d7\u4efb\u4f55\u6253\u51fb\uff0c\u4f46\u6b64\u523b\uff0c\u4ed6\u9762\u5bf9\u7740\u6545\u4eba\uff0c\u5fc3\u6000\u7740\u5f80\u4e8b\uff0c\u4e00\u79cd\u6df1\u6c89\u800c \u5f3a\u70c8\u7684\u4ec7\u6068\u548c\u54c0\u75db\uff0c\u4fbf\u4f7f\u5f97\u4ed6\u81ea\u5df1\u4e5f\u65e0\u6cd5\u63a7\u5236\u81ea\u5df1\u4e86\u3002 \u3000\u3000\u4ed6\u65e0\u58f0\u5730\u6d41\u7740\u6cea\uff0c\u65ad\u7eed\u4ed6\u8bf4\u9053\uff1a\u201c\u5927\u54e5\uff0c\u6211\u7239\u7239\u548c\u2026\u2026\u6211\u5988\u5988\uff0c\u5728\u5341\u5e74\u4ee5\u524d\uff0c\u5c31\u2026\u2026\u5728 \u9ec4\u5c71\u2026\u2026\u59cb\u4fe1\u5cf0\u4e0b\uff0c\u906d\u2026\u2026\u906d\u4e86\u522b\u4eba\u2026\u2026\u7684\u6bd2\u624b\u4e86\u3002\u201d \u3000\u3000\u8fd9\u867d\u662f\u5be5\u5be5\u6570\u5341\u6765\u5b57\uff0c\u53ef\u662f\u4ed6\u5374\u50cf\u662f\u82b1\u5c3d\u4e86\u6c14\u529b\uff0c\u624d\u5c06\u5b83\u8bf4\u51fa\u6765\u3002 \u3000\u3000\u800c\u542c\u4e86\u8fd9\u6570\u5341\u5b57\u7684\u4e91\u4e2d\u7a0b\u5462\uff1f\u4ed6\u66f4\u50cf\u662f\u88ab\u4e00\u4e2a\u5de8\u5927\u7684\u9739\u96f3\uff0c\u5f53\u5934\u8f70\u4e86\u4e00\u4e0b\uff0c\u4f7f\u5f97\u4ed6\u7684\u795e \u667a\uff0c\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u7adf\u5168\u90e8\u51dd\u4f4f\u4e86\u3002 \u3000\u3000\u4ed6\u4ecd\u7136\u4e0d\u76f8\u4fe1\u8fd9\u662f\u4e8b\u5b9e\uff0c\u4f46\u6b8b\u9177\u7684\u662f\uff0c\u4ed6\u5374\u65e0\u6cd5\u4e0d\u76f8\u4fe1\u3002 \u3000\u3000\u800c\u5165\u65e0\u8a00\u76f8\u5bf9\uff0c\u826f\u4e45\u826f\u4e45\uff0c\u5353\u957f\u537f\u53ea\u89c9\u5f97\u4e00\u79cd\u65e0\u6bd4\u6e29\u6696\u7684\u611f\u60c5\uff0c\u4ece\u7ad9\u5728\u81ea\u5df1\u5bf9\u9762\u8fd9\u78ca\u843d \u7684\u7537\u5b50\u63e1\u5728\u81ea\u5df1\u624b\u4e0a\u7684\u4e00\u53cc\u94c1\u638c\u4e2d\u4f20\u4e86\u8fc7\u6765\uff0c\u800c\u8fd9\u79cd\u60c5\u611f\uff0c\u662f\u4e16\u95f4\u6240\u6709\u7684\u8a00\u8bed\u90fd\u65e0\u6cd5\u8868\u8fbe \u7684\u3002 \u3000\u3000\u7ec8\u4e8e\uff0c\u5353\u957f\u537f\u5fcd\u4f4f\u4e86\u773c\u6cea\uff0c\u8f7b\u8f7b\u8bf4\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4f60\u5e26\u6211\u53bb\u89c1\u89c1\u8001\u4f2f\u5427\u3002\u201d \u3000\u3000\u4e91\u4e2d\u7a0b\u7f13\u7f13\u8f6c\u56de\u8eab\uff0c\u5f80\u6765\u8def\u884c\u53bb\uff0c\u5728\u8fd9\u4e00\u523b\u95f4\uff0c\u4ed6\u7adf\u4f3c\u5df2\u5c06\u65b9\u624d\u6240\u53d1\u751f\u7684\u4e00\u5207\uff0c\u90fd\u5fd8\u53bb \u4e86\uff0c\u56e0\u4e3a\u4ed6\u7684\u6574\u4e2a\u60c5\u611f\uff0c\u90fd\u5df2\u4e3a\u60b2\u54c0\u548c\u60ca\u75db\u5145\u6ee1\uff0c\u518d\u4e5f\u6ca1\u6709\u7a7a\u9699\u6765\u5bb9\u7eb3\u522b\u7684\u4e86\u3002\u6ee1\u5929\u7684\u706b \u5149\u3002 \u3000\u3000\u5c06\u4ed6\u4eec\u5e76\u80a9\u800c\u53bb\u7684\u8eab\u5f71\uff0c\u62d6\u5f97\u8001\u957f\u2014\u2014\u4e24\u4eba\u9ed8\u9ed8\u524d\u884c\uff0c\u5404\u81ea\u90fd\u89c9\u5f97\u5bf9\u65b9\u88ab\u81ea\u5df1\u63e1\u7740\u7684\u624b \u662f\u51b0\u51c9\u7684\uff0c\u51b0\u51c9\u5f97\u5c31\u50cf\u662f\u5bd2\u6c34\u4e00\u6837\u3002 \u3000\u3000\u4e91\u4e2d\u7a0b\u7a81\u7136\u505c\u4e0b\u811a\u6b65\uff0c\u9053\uff1a\u201c\u957f\u537f\u5f1f\uff0c\u7b49\u4e00\u4f1a\uff0c\u4f60\u89c1\u4e86\u7239\u7239\uff0c\u5343\u4e07\u4e0d\u8981\u5c06\u8001\u4f2f\u7684\u5669\u8017\u5bf9 \u4ed6\u8001\u4eba\u5bb6\u8bf4\u51fa\u6765\uff0c\u4ed6\u8001\u4eba\u5bb6\u2026\u2026\u5e74\u9f84\u5927\u4e86\uff0c\u6050\u6015\u2026\u2026\u6050\u6015\u53d7\u4e0d\u4e86\u2026\u2026\u201d \u3000\u3000\u5353\u957f\u537f\u4e86\u89e3\u5730\u4e00\u70b9\u5934\uff0c\u4ed6\u6614\u5e74\u5e74\u7eaa\u867d\u5e7c\uff0c\u5374\u4e5f\u77e5\u9053\u591a\u81c2\u795e\u5251\u5bf9\u81ea\u5df1\u7236\u4eb2\u7684\u611f\u60c5\uff0c\u8fd9\u79cd\u60c5 \u611f\u867d\u662f\u5927\u90e8\u5206\u6b66\u6797\u4eba\u58eb\u5bf9\u81ea\u5df1\u7684\u7236\u4eb2\u90fd\u62b1\u6709\u7684\uff0c\u4f46\u90fd\u8fdc\u8fdc\u4e0d\u53ca\u591a\u81c2\u795e\u5251\u6765\u5f97\u5f3a\u70c8\u800c\u6df1\u539a\u3002\u4ece \u90a3\u5929\u5728\u9ec4\u5c71\u59cb\u4fe1\u5cf0\u4e0b\uff0c\u4e00\u76f4\u5230\u73b0\u5728\uff0c\u4ed6\u5bf9\u4ed6\u7239\u7239\u7684\u6b7b\uff0c\u9664\u4e86\u65e0\u6bd4\u7684\u60b2\u75db\u4e4b\u5916\uff0c\u8fd8\u6709\u7740\u4e00\u4efd\u9690 \u542b\u5728\u60b2\u75db\u91cc\u7684\u9a84\u505a\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u77e5\u9053\uff0c\u81ea\u5df1\u7684\u7236\u4eb2\u662f\u503c\u5f97\u81ea\u5df1\u9a84\u505a\u7684\uff0c\u800c\u4ed6\u4e5f\u65e0\u65f6\u65e0\u523b\u4e0d\u5728\u544a\u8bc9\u81ea\u5df1\uff0c\u4efb\u4f55\u4e00\u4e2a \u7236\u4eb2\u4f20\u7ed9\u513f\u5973\u7684\u4e1c\u897f\uff0c\u90fd\u8fdc\u8fdc\u4e0d\u53ca\u81ea\u5df1\u7684\u7239\u7239\u7559\u7ed9\u81ea\u5df1\u7684\u73cd\u8d35\uff0c\u56e0\u4e3a\uff0c\u4ed6\u5df2\u4ece\u7236\u4eb2\u624b\u4e2d\u83b7\u5f97 \u4e86\u5149\u8363\u3002 \u3000\u3000\u201c\u53ea\u662f\u8fd9\u4efd\u5149\u8363\u7684\u4ee3\u4ef7\uff0c\u4e3a\u4ec0\u4e48\u8981\u5982\u6b64\u5de8\u5927\u5462\uff1f\u53c8\u4e3a\u4ec0\u4e48\u5982\u6b64\u60e8\u9177\u5462\uff1f\u201d \u3000\u3000\u4ed6\u6697\u95ee\u81ea\u5df1\uff0c\u6697\u6068\u7740\u82cd\u5929\uff0c\u82cd\u5929\u5bf9\u4e8e\u4e16\u4eba\uff0c\u4e0d\u5c31\u6709\u4e9b\u4e0d\u516c\u5e73\u5417\uff1f\uff01 \u3000\u3000\u4e24\u4eba\u8d8a\u8d70\u8d8a\u5feb\uff0c\u5230\u540e\u6765\uff0c\u4fbf\u5404\u81ea\u5c55\u52a8\u8eab\u5f62\uff0c\u65bd\u51fa\u8f7b\u529f\u6765\uff0c\u4e91\u4e2d\u7a0b\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u4e0d\u77e5\u6211\u8fd9 \u957f\u537f\u5f1f\u8f7b\u529f\u600e\u6837\uff1f\u201d \u3000\u3000\u811a\u4e0b\u52a0\u52b2\uff0c\u55d6\u7136\u4e09\u4e2a\u8d77\u843d\uff0c\u63a0\u51fa\u516b\u4e08\u8fdc\u8fd1\uff0c\u6b63\u662f\u6b66\u6797\u7f55\u89c1\u7684\u8f7b\u529f\u7edd\u6280\u6674\u8712\u4e09\u6284\u6c34\u3002 \u3000\u3000\u4f46\u4fa7\u76ee\u4e00\u671b\uff0c\u5353\u957f\u537f\u5374\u4e0d\u5373\u4e0d\u79bb\u5730\u8ddf\u5728\u4ed6\u8eab\u540e\u534a\u80a9\u4e4b\u5904\uff0c\u6f2b\u65e0\u58f0\u606f\u5730\u79fb\u52a8\u7740\u8eab\u5f62\uff0c\u4e91\u4e2d \u7a0b\u5fc3\u4e2d\u6697\u53f9\u4e00\u58f0\uff0c\u548c\u4ed6\u5e76\u80a9\u5165\u4e86\u4e34\u5b89\u57ce\u3002 \u3000\u3000\u7e41\u534e\u7684\u591c\u5e02\uff0c\u5df2\u5168\u7136\u51b7\u843d\u4e86\u4e0b\u6765\uff0c\u8857\u65c1\u7684\u5e97\u5bb6\uff0c\u90fd\u65e9\u5c31\u5173\u4e0a\u5e97\u95e8\uff0c\u4ee5\u6c42\u907f\u7978\uff0c\u7a7f\u7740\u7682 \u8863\uff0c\u5e26\u7740\u7f28\u5e3d\u7684\u5b98\u5dee\uff0c\u7126\u8651\u800c\u614c\u4e71\u5730\u5728\u8857\u9053\u4e0a\u51b2\u6d17\u7740\u8840\u8ff9\uff0c\u68c0\u9a8c\u7740\u5c38\u8eab\uff0c\u4ed6\u4eec\u7ec8\u65e5\u5fe7\u90c1\u7740\u7684 \u4e8b\uff0c\u73b0\u5728\u7ec8\u4e8e\u8ba9\u4ed6\u4eec\u9047\u4e0a\u4e86\uff0c\u751a\u81f3\u8fd8\u8fdc\u8f83\u4ed6\u4eec\u5fe7\u5fc3\u7740\u7684\u4e25\u91cd\u3002 \u3000\u3000\u4e91\u4e2d\u7a0b\u548c\u5353\u957f\u537f\uff0c\u81ea\u7136\u65e9\u5df2\u653e\u7f13\u4e86\u811a\u6b65\uff0c\u4f46\u4ecd\u4e0d\u65f6\u6709\u5b98\u5dee\u9510\u5229\u7684\u76ee\u5149\uff0c\u6000\u7591\u5730\u671b\u5728\u4ed6\u4eec \u8eab\u4e0a\uff0c\u4e91\u4e2d\u7a0b\u8f7b\u54b3\u4e00\u58f0\uff0c\u62c9\u7740\u5353\u957f\u537f\u8d70\u5230\u8857\u8fb9\u7684\u5c4b\u6a90\u4e0b\uff0c\u50cf\u4e00\u4e2a\u614c\u4e71\u7684\u8def\u4eba\u4f3c\u7684\uff0c\u6025\u6025\u884c\u8d70 \u7740\u3002 \u3000\u3000\u4ed6\u867d\u4e0d\u719f\u6089\u4e34\u5b89\u57ce\u91cc\u7684\u9053\u8def\uff0c\u4f46\u51ed\u7740\u7531\u65e0\u6570\u78e8\u7ec3\u548c\u7ecf\u9a8c\u5f97\u6765\u7684\u89c2\u5bdf\u548c\u8fa8\u522b\u7684\u80fd\u529b\uff0c\u4f7f\u5f97 \u5f88\u7094\u7684\u5c31\u627e\u5230\u4e86\u90a3\u95f4\u53eb\u201c\u9f99\u95e8\u5c45\u201d\u7684\u9152\u98df\u8336\u94fa\uff0c\u53ea\u89c1\u95e8\u5916\u5411\u9ad8\u6311\u8d77\u7684\u4e24\u4e2a\u5927\u6cb9\u7eb8\u706f\u7b3c\uff0c\u867d\u4ecd \u53d1\u7740\u4eae\uff0c\u8fd9\u95f4\u94fa\u5b50\u7684\u5927\u95e8\uff0c\u5374\u4e5f\u5173\u4e0a\u4e86\u3002 \u3000\u3000\u4e91\u4e2d\u7a0b\u76ee\u5149\u4e00\u8f6c\uff0c\u770b\u5230\u5927\u95e8\u7684\u7a7a\u9699\u4e2d\uff0c\u4ecd\u6709\u706f\u5149\u9732\u51fa\uff0c\u4e5f\u9690\u9690\u53ef\u4ee5\u542c\u5230\u8f7b\u5fae\u7684\u4eba\u8bed\u58f0\uff0c \u4ece\u7d27\u95ed\u7684\u5927\u95e8\u4e2d\u4f20\u51fa\u6765\u3002 \u3000\u3000\u4ed6\u53c8\u4e00\u62c9\u5353\u957f\u537f\uff0c\u7a7f\u8fc7\u90a3\u6761\u8840\u8ff9\u5df2\u88ab\u51b2\u6d17\u5f97\u5e72\u51c0\u3001\u6b64\u523b\u4ecd\u662f\u6f6e\u6e7f\u7684\u8857\u9053\uff0c\u4f38\u624b\u8f7b\u8f7b\u4e00\u62cd \u5e97\u95ee\uff0c\u91cc\u9762\u968f\u5373\u4f20\u51fa\u4e00\u4e2a\u82cd\u8001\u7684\u58f0\u97f3\uff1a\u201c\u662f\u4e2d\u7a0b\u5417\uff1f\u201d \u3000\u3000\u8bdd\u58f0\u65b9\u843d\uff0c\u95e8\u5df2\u5f00\u4e86\u4e00\u7ebf\uff0c\u660e\u4eae\u7684\u706f\u5149\uff0c\u7167\u5230\u4ed6\u7684\u8138\u4e0a\uff0c\u4f7f\u5f97\u4ed6\u51e0\u4e4e\u770b\u4e0d\u6e05\u5f00\u95e8\u7684\u662f \u8c01\uff0c\u4f46\u662f\u6293\u5728\u4ed6\u81c2\u4e0a\u7684\u624b\uff0c\u5374\u662f\u4ed6\u6240\u719f\u6089\u7684\uff0c\u4ed6\u4ece\u8fd9\u53cc\u624b\u4e0a\uff0c\u5c31\u53ef\u4ee5\u4f53\u4f1a\u51fa\u4e00\u4e2a\u6148\u7236\u5173\u6000\u7231 \u5b50\u7684\u5fc3\u60c5\u3002 \u3000\u3000\u9f99\u95e8\u5c45\u91cc\u8f7b\u5fae\u7684\u4eba\u8bed\u58f0\uff0c\u968f\u7740\u4ed6\u4eec\u8fdb\u6765\u800c\u53d8\u5f97\u5608\u6742\u3002 \u3000\u3000\u591a\u81c2\u795e\u5251\u7684\u4e00\u53cc\u624b\uff0c\u4ecd\u7136\u6293\u4f4f\u4ed6\u7231\u5b50\u7684\u81c2\u4e0a\uff0c\u8fde\u8fde\u95ee\u9053\uff1a\u201c\u4e2d\u7a0b\uff0c\u4f60\u53ef\u770b\u5230\u4e86\u4ec0\u4e48\u5417\uff1f \u600e\u4e48\u53bb\u4e86\u8fd9\u4e48\u4e45\uff1f\u201d \u3000\u3000\u4e00\u77ac\u95f4\uff0c\u4e91\u4e2d\u7a0b\u4eff\u4f5b\u53c8\u56de\u5230\u90a3\u5145\u6ee1\u91d1\u9ec4\u8272\u7684\u68a6\u65f6\u7ae5\u5e74\uff0c\u8fd9\u79cd\u6148\u7236\u7684\u5173\u5207\uff0c\u4ed6\u5df2\u4e45\u4e45\u6ca1\u6709 \u4eab\u53d7\u5230\u4e86\uff0c\u800c\u6b64\u523b\u4ed6\u77e5\u9053\u4e86\u539f\u56e0\uff0c\u90a3\u5e76\u975e\u7236\u4eb2\u5df2\u4e0d\u518d\u5bf9\u4ed6\u5173\u5207\uff0c\u53ea\u662f\u6ca1\u6709\u503c\u5f97\u5173\u5207\u7684\u539f\u56e0\u2014 \u2014\u513f\u5b50\u5728\u7236\u4eb2\u773c\u4e2d\uff0c\u6c38\u8fdc\u662f\u6ca1\u6709\u957f\u6210\u7684\uff0c\u7eb5\u7136\u4ed6\u5df2\u662f\u80fd\u591f\u7edf\u7387\u7fa4\u8c6a\u7684\u6b66\u6797\u5065\u8005\u3002\u5353\u957f\u537f\u5fae\u5fae \u5782\u4e0b\u5934\uff0c\u4fca\u9038\u7684\u9762\u5e9e\u4e0a\uff0c\u9732\u51fa\u9eef\u7136\u4e4b\u8272\uff0c\u6709\u4ec0\u4e48\u5176\u4ed6\u7684\u4e8b\u80fd\u6bd4\u8fd9\u79cd\u7236\u5b50\u7684\u4eb2\u60c5\u66f4\u6613\u4ee4\u4e00\u4e2a\u65e0 \u7236\u7684\u5b69\u5b50\u611f\u52a8\u7684\u5462\uff1f \u3000\u3000\u4f46\u662f\u4ed6\u5374\u4e0d\u77e5\u9053\uff0c\u6b64\u523b\u5e97\u4e2d\u7fa4\u8c6a\u7684\u773c\u775b\uff0c\u5df2\u5927\u591a\u90fd\u51dd\u89c6\u5728\u4ed6\u8eab\u4e0a\uff0c\u4e00\u4e2a\u5353\u5c14\u4e0d\u7fa4\u7684\u4eba\uff0c \u65e0\u8bba\u8d70\u5230\u54ea\u91cc\uff0c\u90fd\u662f\u4f1a\u5f15\u8d77\u522b\u4eba\u6ce8\u610f\u7684\u3002 \u3000\u3000\u4e91\u4e2d\u7a0b\u9762\u4e0a\uff0c\u52c9\u5f3a\u5730\u7efd\u5f00\u4e86\u4e00\u4e1d\u7b11\u5bb9\uff0c\u6307\u7740\u5353\u957f\u537f\u9053\uff1a\u201c\u7239\u7239\uff0c\u4f60\u8001\u4eba\u5bb6\u731c\u731c\u770b\uff0c\u8fd9\u4f4d \u5c11\u5e74\u82f1\u96c4\u662f\u8c01\uff1f\u201d \u3000\u3000\u591a\u81c2\u795e\u5251\u76ee\u5149\u4e00\u8f6c\uff0c\u4f46\u89c1\u7ad9\u5728\u81ea\u5df1\u7231\u5b50\u8eab\u4fa7\u7684\uff0c\u662f\u4e00\u4e2a\u957f\u8eab\u7389\u7acb\u7684\u5c11\u5e74\uff0c\u8eab\u4e0a\u7a7f\u7740\u4e00\u88ad \u4f3c\u4e1d\u975e\u5e1b\u3001\u4f3c\u7ee2\u975e\u7ee2\uff0c\u8bf4\u4e0d\u51fa\u662f\u4ec0\u4e48\u8d28\u6599\u5236\u6210\u7684\u7384\u8272\u957f\u886b\uff0c\u76ee\u5982\u6717\u661f\uff0c\u9f3b\u4f3c\u60ac\u80c6\uff0c\u8fd9\u9762\u8c8c\u4f3c \u4e4e\u662f\u81ea\u5df1\u719f\u6089\u7684\uff0c\u5c24\u5176\u662f\u90a3\u6ee1\u542b\u575a\u6bc5\u548c\u5014\u5f3a\u7684\u5634\uff0c\u66f4\u4f7f\u4ed6\u548c\u81ea\u5df1\u7ec8\u65e5\u60e6\u8bb0\u7684\u4e00\u4eba\u76f8\u4f3c\uff0c\u4f46 \u662f\u2026\u2026 \u3000\u3000\u8fd9\u8001\u4eba\u7684\u4e00\u53cc\u773c\u775b\u7728\u4e5f\u4e0d\u7728\u5730\u51dd\u6ce8\u5728\u8fd9\u5f20\u8138\u3002\u4e0a\uff0c\u7ec8\u4e8e\uff0c\u4ed6\u6355\u6349\u4e86\u81ea\u5df1\u7684\u8bb0\u5fc6\uff0c\u4e00\u4e2a\u864e \u6b65\u7a9c\u8fc7\u53bb\uff0c\u72c2\u559c\u7740\u9053\uff1a\u201c\u957f\u537f\uff0c\u4f60\u662f\u4e0d\u662f\u957f\u537f\uff1f\u201d \u3000\u3000\u6b64\u523b\uff0c\u4ece\u8fd9\u8001\u4eba\u8eab\u4e0a\u4f20\u51fa\u7684\u60c5\u611f\uff0c\u5353\u957f\u537f\u4e5f\u611f\u89c9\u5230\u4e86\uff0c\u8fd9\u79cd\u51e0\u4e4e\u76f8\u8fd1\u4e8e\u7236\u5b50\u4e4b\u60c5\u7684\u60c5 \u611f\uff0c\u4f7f\u5f97\u8fd9\u81ea\u4ee5\u4e3a\u60c5\u611f\u5df2\u8db3\u591f\u575a\u5f3a\u7684\u5c11\u5e74\uff0c\u773c\u7736\u518d\u4e00\u6b21\u6e7f\u6da6\u8d77\u6765\u4e00\u6ca1\u6709\u4e00\u4e2a\u60c5\u611f\u4e30\u5bcc\u7684\u4eba\uff0c \u80fd\u957f\u671f\u63a7\u5236\u81ea\u5df1\u7684\u60c5\u611f\u7684\uff0c\u7eb5\u7136\u4ed6\u5df2\u7ecf\u8fc7\u78e8\u7ec3\u3002 \u3000\u3000\u201c\u535c\u201d\u7684\u4e00\u58f0\uff0c\u8fd9\u5c11\u5e74\u8dea\u4e86\u4e0b\u53bb\uff0c\u52c9\u5f3a\u5fcd\u4f4f\u4e86\u81ea\u5df1\u5589\u5934\u7684\u54fd\u54bd\uff0c\u9053\uff1a\u201c\u8001\u4f2f\uff0c\u5c0f\u4f84\u6b63\u662f \u957f\u537f\uff0c\u5341\u5e74\u6765\u2026\u2026\u8001\u6015\u7cbe\u795e\u8d8a\u53d1\u77cd\u70c1\u3002\u201d \u3000\u3000\u4e91\u8c26\u4e00\u628a\u62c9\u8d77\u4ed6\uff0c\u8fde\u58f0\u9053\uff1a\u201c\u5feb\u8d77\u6765\uff0c\u5feb\u8d77\u6765\u2014\u2014\u201d\u8fd9\u8001\u4eba\u7684\u58f0\u97f3\uff0c\u5df2\u56e0\u60c5\u611f\u7684\u6fc0\u52a8\uff0c \u800c\u53d8\u5f97\u6709\u4e9b\u98a4\u6296\u4e86\uff0c\u4ed6\u7d27\u7d27\u6293\u4f4f\u5c11\u5e74\u7684\u81c2\u8180\uff0c\u50cf\u6293\u7740\u81ea\u5df1\u7684\u7231\u5b50\u4e00\u6837\uff0c\u76ee\u5149\u4e0a\u4e0b\u6253\u91cf\u7740\uff0c\u53c8 \u542b\u7b11\u9053\uff1a\u201c\u60f3\u4e0d\u5230\uff0c\u60f3\u4e0d\u5230\uff0c\u4f60\u4e5f\u957f\u5f97\u8fd9\u4e48\u9ad8\u5927\u4e86\uff0c\u4f60\u7239\u7239\u5462\uff1f\u600e\u4e48\u4e5f\u4e0d\u6765\u770b\u770b\u6211\u8fd9\u8001\u5934 \u5b50\uff0c\u96be\u9053\u4ed6\u5df2\u7ecf\u628a\u6211\u5fd8\u4e86\u5417\uff1f\u201d \u3000\u3000\u5353\u957f\u537f\u5f3a\u5fcd\u7740\u6cea\uff0c\u76ee\u5149\u4e00\u8f6c\uff0c\u89c1\u5230\u4e91\u4e2d\u7a0b\u6b63\u7126\u5207\u5730\u671b\u7740\u81ea\u5df1\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u57c2\u54bd\u7740\u9053\uff1a\u201c\u5bb6\u7236\u4ed6\u8001\u4eba\u5bb6\u2026\u2026\u8fd9\u4e9b\u5e74\u2026\u2026\u90fd\u6ca1\u6709\u51fa\u6765\uff0c\u7279\u5730\u53eb\u5c0f\u4f84\u95ee\u5019\u60a8\u8001\u4eba \u5bb6\u597d\u3002\u201d \u3000\u3000\u8ba9\u4e00\u4e2a\u8bda\u5b9e\u7684\u4eba\u8bf4\u8c0e\uff0c\u672c\u5c31\u662f\u4ef6\u975e\u5e38\u75db\u82e6\u7684\u4e8b\uff0c\u800c\u6b64\u523b\u7684\u5353\u957f\u537f\uff0c\u81ea\u7136\u75db\u82e6\u5f97\u66f4\u4e3a\u5389 \u5bb3\uff0c\u4f46\u662f\uff0c\u4ed6\u7ec8\u4e8e\u8fd8\u662f\u8bf4\u4e86\u51fa\u6765\u3002 \u3000\u3000\u591a\u81c2\u795e\u5251\u5927\u559d\u4e00\u58f0\uff0c\u5389\u58f0\u9053\uff1a\u201c\u597d\uff0c\u597d\uff0c\u8fd9\u4e48\u591a\u5e74\u90fd\u6ca1\u51fa\u6765\uff0c\u8001\u670b\u53cb\u662f\u4ec0\u4e48\u4e1c\u897f\uff0c\u53ea\u8981 \u4ed6\u5353\u5927\u7237\u4f4f\u5f97\u8212\u670d\u5c31\u6210\u4e86\u2014\u2014\u201d\u4ed6\u7a81\u53c8\u957f\u53f9\u4e00\u58f0\uff0c\u773c\u4e2d\u5a01\u5149\u5c3d\u655b\uff0c\u6148\u7965\u5730\u843d\u5230\u5353\u957f\u537f\u8eab\u4e0a\uff0c \u957f\u53f9\u53c8\u9053\uff1a\u201c\u5b69\u4e8e\uff0c\u4e0d\u8981\u5403\u60ca\uff0c\u6211\u2026\u2026\u6211\u53ea\u662f\u60f3\u4f60\u7239\u7239\uff0c\u60f3\u5f97\u592a\u5389\u5bb3\u4e86\u3002\u201d \u3000\u3000\u53cb\u60c5\uff0c\u8fd9\u4e00\u77ac\u95f4\uff0c\u5353\u957f\u537f\u7a81\u7136\u4e86\u89e3\u5230\u4e86\u53cb\u60c5\u7684\u4ef7\u503c\uff0c\u4e5f\u4e86\u89e3\u5230\u4e91\u4e2d\u7a0b\u4e3a\u4ec0\u4e48\u4e0d\u8ba9\u81ea\u5df1\u5c06 \u90a3\u5669\u8017\u544a\u8bc9\u8fd9\u8001\u4eba\u7684\u539f\u56e0\u3002 \u3000\u3000\u4ed6\u6697\u4e2d\u957f\u53f9\uff0c\u5fc3\u5934\u6d8c\u8fc7\u4e86\u5343\u4e07\u53e5\u60f3\u8bf4\u7684\u6d3b\uff0c\u5374\u53ea\u8bf4\u4e86\u53e5\uff1a\u201c\u8001\u4f2f\uff0c\u4f60\u8001\u4eba\u5bb6\u662f\u5bb6\u7236\u7684\u77e5 \u5df1\uff0c\u5509\u2014\u2014\u5bb6\u7236\u5b9e\u662f\u6709\u96be\u8a00\u7684\u82e6\u8877\uff0c\u4f60\u8001\u4eba\u5bb6\u4e0d\u4f1a\u602a\u5427\u3002\u201d \u3000\u3000\u591a\u81c2\u795e\u5251\u4e00\u624b\u6293\u7740\u4ed6\u7684\u5de6\u81c2\uff0c\u53c8\u81ea\u957f\u53f9\u4e86\u4e00\u58f0\uff0c\u5c06\u4ed6\u62d6\u5230\u81ea\u5df1\u5750\u7684\u684c\u65c1\u5750\u4e0b\uff0c\u4e00\u9762\u9053\uff1a \u201c\u957f\u537f\uff0c\u6211\u548c\u4f60\u7239\u7239\u6570\u5341\u5e74\u8fc7\u547d\u7684\u4ea4\u60c5\uff0c\u8fd8\u6709\u4ec0\u4e48\u89c1\u602a\u4e0d\u89c1\u602a\u7684\u3002\u201d \u3000\u3000\u4ed6\u8bdd\u58f0\u4e00\u987f\uff0c\u6d53\u7709\u5fae\u8f69\uff0c\u76ee\u5149\u4e2d\u7a81\u7136\u9732\u51fa\u559c\u8272\uff0c\u63a5\u7740\u9053\uff1a\u201c\u6765\uff0c\u544a\u8bc9\u6211\uff0c\u4f60\u662f\u600e\u4e48\u4e5f\u6765 \u5230\u8fd9\u91cc\u7684\uff0c\u53c8\u662f\u600e\u4e48\u9047\u7740\u4e86\u4e2d\u7a0b\uff0c\u8fd9\u4e9b\u5e74\u6765\uff0c\u60f3\u5fc5\u4f60\u5df2\u4ece\u4f60\u7239\u7239\u90a3\u91cc\u5b66\u5f97\u4e86\u4e00\u8eab\u529f\u592b\uff0c\u6b64\u523b \u5012\u662f\u4f60\u4e00\u5c55\u8eab\u624b\u7684\u673a\u4f1a\u4e86\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u76ee\u5149\u4e00\u8f6c\uff0c\u5374\u89c1\u4e91\u4e2d\u7a0b\u5df2\u88ab\u4eba\u62c9\u5230\u4e00\u8fb9\uff0c\u4e03\u5634\u516b\u820c\u5730\u95ee\u7740\u4ed6\u65b9\u624d\u7684\u7ecf\u5386\uff0c\u4f46\u89c1\u4e91 \u4e2d\u7a0b\u6bcf\u8bf4\u4e00\u65ec\u8bdd\uff0c\u56db\u5ea7\u5c31\u4f20\u6765\u4e00\u9635\u60ca\u5531\u4e4b\u58f0\uff0c\u800c\u4e14\u9762\u4e0a\u5404\u4e2a\u5e26\u7740\u60ca\u6050\u4e4b\u8272\uff0c\u8fd9\u95f4\u55a7\u4e71\u7684\u8336 \u9986\u3002 \u3000\u3000\u6b64\u523b\u867d\u4ecd\u9ad8\u670b\u6ee1\u5ea7\uff0c\u70db\u706b\u901a\u660e\uff0c\u4f46\u4e0d\u77e5\u600e\u7684\uff0c\u5374\u6709\u7740\u4e00\u822c\u4ee4\u4eba\u4e0d\u7981\u4e3a\u4e4b\u60b8\u60ca\u7684\u51c4\u60c5\u4e4b \u610f\uff0c\u548c\u53e6\u5916\u7684\u4e00\u5207\u90fd\u7edd\u4e0d\u76f8\u79f0\u3002 \u3000\u3000\u4e00\u7741\u5f97\u6eda\u5706\u773c\u775b\u7684\u5e97\u4f19\uff0c\u6014\u6014\u671b\u7740\u6b63\u5728\u8bf4\u8bdd\u7684\u4e91\u4e2d\u7a0b\uff0c\u4e3a\u5353\u957f\u537f\u7aef\u6765\u4e00\u676f\u8336\uff0c\u201c\u7830\u201d\u7684 \u4e00\u58f0\uff0c\u653e\u5728\u684c\u4e0a\uff0c\u663e\u89c1\u8fd9\u4e0e\u6b66\u6797\u4e1d\u6beb\u65e0\u5173\u7684\u5e02\u4e95\u4e4b\u4eba\uff0c\u6b64\u523b\u4ea6\u88ab\u4e91\u4e2d\u7a0b\u7684\u8bf4\u8bdd\u6240\u5438\u5f15\uff0c\u5168\u795e \u90fd\u653e\u5728\u90a3\u9762\u53bb\u4e86\u3002 \u3000\u3000\u4f46\u591a\u81c2\u795e\u5251\u4e91\u8c26\u7684\u4e00\u53cc\u864e\u56fd\uff0c\u5374\u59cb\u7ec8\u51dd\u6ce8\u5728\u5353\u957f\u537f\u8eab\u4e0a\u3002 \u3000\u3000\u5353\u957f\u537f\u7f13\u7f13\u4e3a\u81ea\u5df1\u659f\u4e86\u676f\u8336\uff0c\u6de1\u6de1\u555c\u4e86\u4e00\u53e3\uff0c\u81ea\u4ece\u90a3\u5929\u5728\u9ec4\u5c71\u59cb\u4fe1\u5cf0\u4e0b\uff0c\u4ed6\u4eb2\u624b\u57cb\u846c\u4e86 \u4ed6\u7684\u53cc\u4eb2\u4e4b\u540e\uff0c\u4ed6\u7684\u5fc3\u60c5\uff0c\u5c31\u4ece\u672a\u6709\u5982\u6b64\u523b\u8fd9\u4e48\u6fc0\u52a8\u8fc7\u3002\u751a\u81f3\u5f53\u4ed6\u77e5\u9053\u5c06\u4ed6\u5e26\u5230\u6a2a\u5cad\u5173\u4fa7\u4e2d \u6761\u5c71\u53f3\u7684\u738b\u5c4b\u5c71\u4e0a\uff0c\u90a3\u5a01\u731b\u9ad8\u5927\u7684\u8001\u4eba\uff0c\u7adf\u662f\u663e\u800c\u6613\u89c1\u767e\u5e74\u6765\u540d\u4f20\u4e8e\u5929\u4e0b\u7684\u6b66\u6797\u5947\u4eba\u4e4b\u4e00\uff0c \u88ab\u5929\u4e0b\u6b66\u6797\u540c\u9053\u8d3a\u53f7\u5929\u4ed9\u7684\u53f8\u7a7a\u5c27\u65e5\u4e4b\u65f6\uff0c\u4ed6\u7684\u5fc3\u60c5\uff0c\u4e5f\u4ec5\u662f\u9ad8\u5174\u548c\u611f\u6fc0\u800c\u5df2\u3002 \u3000\u3000\u4f46\u6b64\u523b\uff0c\u4ed6\u9762\u5bf9\u7740\u8fd9\u4ea1\u7236\u7684\u77e5\u4ea4\uff0c\u9762\u5bf9\u7740\u8fd9\u548c\u4ed6\u4ee5\u5f80\u7684\u65f6\u65e5\u552f\u4e00\u6709\u7740\u5173\u8fde\u7684\u8001\u4eba\uff0c\u4ed6\u7684 \u5fc3\u60c5\u9664\u4e86\u5174\u594b\u548c\u611f\u6fc0\u4e4b\u5916\uff0c\u5374\u8fd8\u6df7\u6742\u7740\u8bb8\u591a\u522b\u7684\u60c5\u611f\uff0c\u5c31\u8fde\u4ed6\u81ea\u5df1\u4e5f\u65e0\u6cd5\u5c06\u8fd9\u4e9b\u60c5\u611f\u4e00\u4e00\u5206 \u6790\u3002 \u3000\u3000\u4ed6\u7684\u601d\u6f6e\uff0c\u53c8\u4e0d\u81ea\u7981\u5730\u56de\u5230\u5f88\u4e45\u5f88\u4e45\u4ee5\u524d\u2014\u2014\u90a3\u65f6\u4ed6\u8fd8\u662f\u4e2a\u5929\u771f\u800c\u4e0d\u89e3\u4e8b\u7684\u5b69\u5b50\uff0c\u90a3\u65f6 \u4ed6\u66fe\u6709\u8fc7\u4e00\u6bb5\u6b22\u4e50\u7684\u65f6\u5149\uff0c\u4f46\u662f\u8fd9\u4e00\u5207\uff0c\u6b64\u523b\u5374\u90fd\u5df2\u968f\u7740\u4ed6\u53cc\u4eb2\u7684\u5c38\u9aa8\uff0c\u57cb\u846c\u5728\u59cb\u4fe1\u5cf0\u4e0b\u3002 \u3000\u3000\u6b64\u540e\uff0c\u5728\u738b\u5c4b\u5c71\u5cad\uff0c\u90a3\u5341\u5e74\u7684\u5c81\u6708\uff0c\u8fd9\u672c\u5e94\u4eab\u53d7\u9752\u6625\u7684\u5c11\u5e74\uff0c\u5374\u51e0\u4e4e\u548c\u90a3\u201c\u6b22\u4e50\u201d\u4e8c \u5b57\uff0c\u5b8c\u5168\u7edd\u4e86\u7f18\u3002 \u3000\u3000\u4ed6\u4e0d\u505c\u5730\u97ad\u7b56\u7740\u81ea\u5df1\uff0c\u6ca1\u6709\u4e00\u65f6\u4e00\u523b\u7684\u677e\u61c8\u3002 \u3000\u3000\u5341\u5e74\u7684\u5c81\u6708\uff0c\u5c31\u5728\u8fd9\u4f3c\u4e4e\u6c38\u65e0\u4f11\u6b62\u7684\u953b\u70bc\u4e0b\uff0c\u5f88\u5feb\u5730\u8fc7\u53bb\u4e86\u3002 \u3000\u3000\u5341\u5e74\u7a7a\u5c71\u7684\u5c81\u6708\uff0c\u867d\u7136\u4f7f\u5f97\u4ed6\u8868\u9762\u53d8\u5f97\u5f02\u5e38\u51b7\u6f20\uff0c\u50cf\u662f\u5df2\u5c06\u4efb\u4f55\u4e8b\u90fd\u4e0d\u518d\u653e\u5728\u5fc3\u4e0a\uff0c\u4f46 \u662f\u4ed6\u5185\u5fc3\u7684\u601d\u6f6e\uff0c\u5374\u968f\u7740\u5e74\u9f84\u7684\u589e\u957f\uff0c\u800c\u65e5\u76ca\u7d0a\u4e71\u3002 \u3000\u3000\u4f46\u662f\uff0c\u771f\u6b63\u5230\u4e86\u4e0b\u5c71\u7684\u65f6\u5019\uff0c\u4ed6\u5374\u53c8\u5bf9\u90a3\u738b\u5c4b\u5c71\u5dc5\u7684\u4e00\u5207\uff0c\u7559\u604b\u4e0d\u5df2\u3002 \u3000\u3000\u9752\u77f3\u7684\u5e8a\u51e0\uff0c\u9752\u77f3\u7684\u684c\u6905\uff0c\u9752\u77f3\u7684\u5899\u58c1\u2014\u2014\u90a3\u4e9b\u5728\u4ed6\u773c\u4e2d\uff0c\u539f\u672c\u662f\u5355\u8c03\u800c\u5446\u677f\u7684\u4e1c\u897f\uff0c \u5728\u4ed6\u5c06\u8981\u79bb\u53bb\u7684\u65e5\u5b50\u91cc\uff0c\u5374\u90fd\u6210\u4e86\u4ed6\u6700\u503c\u5f97\u7559\u604b\u7684\u4e1c\u897f\u4e86\u3002 \u3000\u3000\u800c\u53f8\u7a7a\u8001\u4eba\u4e25\u5cfb\u7684\u9762\u5bb9\uff0c\u4e5f\u53d8\u5f97\u90a3\u4e48\u4eb2\u5207\uff0c\u53ea\u662f\u4ed6\u4e5f\u77e5\u9053\uff0c\u81ea\u5df1\u8fd8\u6709\u7740\u5927\u591a\u7684\u6ca1\u6709\u505a\u800c \u5e94\u8be5\u505a\u7684\u4e8b\uff0c\u4e8e\u662f\u5728\u4e00\u65e5\u6b8b\u51ac\u65e2\u53bb\u3001\u6625\u65e5\u5374\u8fd8\u672a\u6765\u4e34\u7684\u6e05\u6668\uff0c\u4ed6\u8e0f\u7740\u6ee1\u5f84\u7684\u971c\u8ff9\uff0c\u4e0b\u4e86\u738b\u5c4b \u5c71\u3002 \u3000\u3000\u50cf\u4efb\u4f55\u4e00\u4e2a\u521d\u5165\u6c5f\u6e56\u7684\u5c11\u5e74\u4e00\u6837\uff0c\u9762\u5bf9\u7740\u56a3\u6270\u7684\u7ea2\u5c18\uff0c\u4ed6\u6709\u7740\u4e00\u4efd\u4e0d\u77e5\u6240\u4ece\u7684\u611f\u89c9\uff0c\u5f53 \u7136\uff0c\u4ed6\u4e5f\u50cf\u4efb\u4f55\u4e00\u4e2a\u5fc3\u6000\u4eb2\u5207\u7684\u5c11\u5e74\u4e00\u6837\uff0c\u5fc3\u4e2d\u94ed\u8bb0\u6700\u6df1\u7684\uff0c\u5c31\u662f\u81ea\u5df1\u4e0d\u5171\u6234\u5929\u7684\u4ec7\u4eba\u3002 \u3000\u3000\u591a\u81c2\u795e\u5251\u4e91\u8c26\u53ea\u89c1\u5750\u5728\u4ed6\u5bf9\u9762\u7684\u5c11\u5e74\uff0c\u624b\u91cc\u7aef\u7740\u8336\u676f\uff0c\u4e45\u4e45\u90fd\u672a\u653e\u4e0b\uff0c\u9762\u4e0a\u7684\u795e\u8272\u4ea6\u81ea \u500f\u5ffd\u4e0d\u5b9a\uff0c\u4e0d\u77e5\u5fc3\u91cc\u6b63\u60f3\u7740\u4ec0\u4e48\uff0c\u4e0d\u7981\u5e72\u54b3\u4e00\u58f0\uff0c\u60a6\u58f0\u9053\uff1a\u201c\u957f\u537f\uff0c\u4f60\u5fc3\u4e2d\u82e5\u6709\u5fe7\u90c1\u4e4b\u4e8b\uff0c \u4e0d\u59a8\u8bf4\u7ed9\u6211\u542c\u542c\uff0c\u6b64\u523b\u4f60\u65e2\u5df2\u79bb\u5f00\u4e86\u4f60\u7684\u7239\u7239\u2014\u2014\uff0c\u4e0d\u59a8\u2014\u2014\u5c31\u5c06\u6211\u770b\u505a\u4f60\u7684\u7239\u7239\u4e00 \u6837\u2026\u2026\u201d \u3000\u3000\u5353\u957f\u537f\u832b\u7136\u62ac\u8d77\u5934\u6765\uff0c\u53ea\u89c1\u4e91\u8c26\u773c\u4e2d\u6ee1\u662f\u5173\u5207\u4e4b\u60c5\uff0c\u5fc3\u4e2d\u4e00\u9635\u60c5\u611f\u6fc0\u52a8\uff0c\u6cea\u73e0\u7a81\u7136\u593a\u7736 \u800c\u51fa\u2026\u2026 \u3000\u3000\u591a\u81c2\u795e\u5251\u6d53\u7709\u4e00\u76b1\uff0c\u6025\u58f0\u9053\uff1a\u201c\u957f\u537f\uff0c\u4f60\u8fd9\u662f\u600e\u4e48\u4e86\uff0c\u6709\u4ec0\u4e48\u4e8b\u53ea\u7ba1\u8bf4\u51fa\u6765\uff0c\u8001\u592b\u62fc\u5374 \u6027\u547d\uff0c\u4e5f\u5f97\u4e3a\u4f60\u505a\u4e3b\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u53ea\u89c9\u773c\u524d\u4e00\u7247\u6a21\u7cca\uff0c\u6068\u4e0d\u5f97\u5c06\u5fc3\u4e2d\u6240\u6709\u7684\u4e8b\u90fd\u5728\u8fd9\u4f4d\u6148\u7965\u7684\u8001\u4eba\u9762\u524d\u503e\u8bc9\u51fa\u6765\uff0c \u4f38\u624b\u4e00\u62b9\u9762\u988a\u7684\u6cea\u773c\uff0c\u4e0d\u7981\u8131\u53e3\u8bf4\u9053\uff1a\u201c\u8001\u4f2f\uff0c\u5c0f\u4f84\u2026\u2026\u201d \u3000\u3000\u76ee\u5149\u4e00\u8f6c\uff0c\u53ea\u89c1\u4e91\u4e2d\u7a0b\u6b63\u51dd\u76ee\u671b\u7740\u81ea\u5df1\uff0c\u5fc3\u4e2d\u957f\u53f9\u4e00\u58f0\uff0c\u6539\u53e3\u9053\uff1a\u201c\u5c0f\u4f84\u79bb\u5f00\u4e86\u7239\u7239\u4ee5 \u540e\u2014\u2014\u201d\u4f46\u8bf4\u5230\u8fbe\u91cc\uff0c\u5374\u518d\u4e5f\u8bf4\u4e0d\u4e0b\u53bb\uff0c\u5fc3\u80f8\u4e4b\u95f4\uff0c\u751f\u50cf\u662f\u88ab\u585e\u7740\u4e00\u5757\u5343\u65a4\u5de8\u77f3\uff0c\u538b\u5f97\u81ea\u5df1 \u900f\u4e0d\u8fc7\u6c14\u6765\u3002 \u3000\u3000\u4e91\u8c26\u76ee\u5149\u51db\u7136\uff0c\u7728\u4e5f\u4e0d\u7728\u5730\u51dd\u6ce8\u5728\u4ed6\u9762\u4e0a\uff0c\u8ffd\u95ee\u9053\uff1a\u201c\u957f\u537f\uff0c\u7a76\u7adf\u662f\u600e\u4e48\u56de\u4e8b\u2014\u2014\u201d\u8bed \u58f0\u672a\u4e86\uff0c\u5374\u89c1\u4e91\u4e2d\u7a0b\u5df2\u5927\u6b65\u8d70\u4e86\u8fc7\u6765\uff0c\u4e00\u9762\u542b\u7b11\u9053\uff1a\u201c\u957f\u537f\u5f1f\u60f3\u5fc5\u662f\u79bb\u5bb6\u65e5\u4e45\uff0c\u5fc3\u91cc\u6709\u4e86\u4e9b \u96be\u53d7\uff0c\u4e0d\u8fc7\uff0c\u957f\u537f\u5f1f\uff0c\u6b64\u523b\u4f60\u65e2\u7136\u5df2\u6765\u5230\u8fd9\u91cc\uff0c\u6211\u5374\u8981\u591a\u7559\u4f60\u4e00\u4e9b\u65e5\u5b50\u4e86\u3002\u201d\u4ed6\u8bdd\u58f0\u5fae\u987f\uff0c \u76ee\u5149\u4e00\u8f6c\uff0c\u5411\u5353\u957f\u537f\u4f7f\u4e86\u4e2a\u773c\u8272\uff0c\u63a5\u7740\u53c8\u9053\uff1a\u201c\u6b64\u523b\u8fd9\u4e34\u5b89\u57ce\uff0c\u4e0d\u4f46\u98ce\u4e91\u9645\u4f1a\uff0c\u7fa4\u8c6a\u6bd5\u81f3\uff0c \u800c\u4e14\u602a\u5f02\u4e4b\u4e8b\uff0c\u5c42\u51fa\u4e0d\u7a77\uff0c\u8d24\u5f1f\u82e5\u6ca1\u6709\u6765\uff0c\u6211\u8fd8\u771f\u4e0d\u77e5\u9053\u8be5\u5982\u4f55\u662f\u597d\u54e9\u3002\u201d \u3000\u3000\u4ed6\u8bed\u58f0\u65b9\u4f4f\uff0c\u5374\u53c8\u7d27\u63a5\u7740\u5c06\u81ea\u5df1\u6240\u9047\u8bf4\u4e86\u51fa\u6765\uff0c\u53c8\u81ea\u53f9\u9053\uff1a\u201c\u96c1\u8361\u7ea2\u5dfe\u4f1a\uff0c\u5d1b\u8d77\u6c5f\u6e56\u7684 \u65f6\u65e5\u867d\u77ed\uff0c\u4f46\u4f1a\u4e2d\u4eba\u624b\u5374\u6781\u6574\u9f50\uff0c\u52bf\u529b\u5e76\u975e\u7b49\u95f2\uff0c\u54ea\u77e5\u4eca\u65e5\u5728\u8fd9\u4e34\u5b89\u57ce\u91cc\u4e00\u8d25\u6d82\u5730\uff0c\u6b64\u4e8b\u4e0d \u4ec5\u5947\u602a\uff0c\u800c\u4e14\u7b80\u76f4\u6709\u4e9b\u4e0d\u53ef\u601d\u4eea\uff0c\u8bd5\u60f3\u80fd\u5c06\u8fd9\u7ea2\u4e2d\u4f1a\u4e00\u4e3e\u800c\u706d\u7684\u4eba\uff0c\u53c8\u8be5\u662f\u5982\u4f55\u4eba\u7269\u5462\uff1f\u201d \u3000\u3000\u4ed6\u6ed4\u6ed4\u4e00\u5e2d\u8bdd\uff0c\u679c\u7136\u5c06\u65b9\u624d\u4e4b\u4e8b\u8f7b\u8f7b\u5e26\u8fc7\uff0c\u591a\u81c2\u795e\u5251\u76b1\u7709\u53f9\u9053\uff1a\u201c\u81ea\u4ece\u90a3\u5929\u8001\u592b\u773c\u89c1\u4e07 \u5999\u771f\u541b\u548c\u7ea2\u8863\u59d1\u5a18\u7684\u4f20\u4eba\u4e00\u8d77\u51fa\u73b0\uff0c\u8001\u592b\u5c31\u77e5\u9053\uff0c\u82b8\u82b8\u6b66\u6797\uff0c\u5fc5\u5b9a\u53c8\u5c06\u591a\u4e8b\uff0c\u957f\u537f\u2014\u2014\u201d\u4ed6 \u76ee\u5149\u4e00\u8f6c\uff0c\u5374\u89c1\u90a3\u5353\u957f\u537f\u9762\u4e0a\u663e\u51fa\u4e00\u7247\u6124\u6068\u4e4b\u8272\uff0c\u53cc\u624b\u7d27\u7d27\u63e1\u7740\u62f3\u5934\uff0c\u76ee\u5149\u4e2d\u4ea6\u6ee1\u662f\u8083\u6740\u4e4b \u610f\u3002 \u3000\u3000\u591a\u81c2\u795e\u5251\u5fc3\u4e2d\u53c8\u662f\u4e00\u52a8\uff0c\u6697\u81ea\u5947\u602a\u8fd9\u5c11\u5e74\u600e\u4e48\u5982\u6b64\uff0c\u4ed6\u5374\u4e0d\u77e5\u9053\u5fc3\u6000\u7236\u4ec7\u7684\u5353\u957f\u537f\uff0c\u5c31 \u662f\u56e0\u4e3a\u542c\u5f97\u6c5f\u6e56\u4f20\u8a00\uff0c\u5929\u76ee\u5c71\u4e0b\uff0c\u8bbe\u4e0b\u5982\u6b64\u6218\u4f1a\uff0c\u800c\u6b64\u4f1a\u4e3b\u4eba\uff0c\u5374\u662f\u90a3\u4e11\u4eba\u6e29\u5982\u7389\u7684\u5f1f\u5b50\uff0c \u624d\u4e13\u7a0b\u8d76\u5230\u4e34\u5b89\u7684\u3002 \u7b2c\u4e94\u7ae0\u3000\u5feb\u5200\u5982\u6797>> \u53e4\u9f99\u300a\u6708\u5f02\u661f\u90aa\u300b \u7b2c\u4e94\u7ae0\u3000\u5feb\u5200\u5982\u6797 \u3000\u3000\u5353\u957f\u537f\u5728\u9ec4\u5c71\u59cb\u4fe1\u5cf0\u4e0b\uff0c\u773c\u770b\u81ea\u5df1\u53cc\u4eb2\u88ab\u90a3\u4e11\u4eba\u6e29\u5982\u7389\u51fb\u6bd9\uff0c\u827a\u6210\u4e0b\u5c71\u540e\uff0c\u81ea\u7136\u7b2c\u4e00\u4e2a \u8981\u6211\u7684\u5c31\u662f\u8fd9\u540d\u6ee1\u5929\u4e0b\u7684\u5973\u9b54\u5934\u3002 \u3000\u3000\u53ea\u662f\u8fd9\u7ea2\u8863\u4ed9\u5b50\uff0c\u8fd1\u5e74\u6765\u5374\u7a81\u7136\u9500\u58f0\u533f\u8ff9\uff0c\u6c5f\u6e56\u4e2d\u6839\u672c\u6ca1\u6709\u4eba\u77e5\u9053\u5979\u7684\u884c\u8ff9\u3002\u5353\u957f\u537f\u5b50 \u7136\u4e00\u8eab\uff0c\u968f\u610f\u98d8\u6cca\uff0c\u5230\u4e86\u6c5f\u5357\uff0c\u77e5\u9053\u4e86\u6b64\u4e8b\uff0c\u81ea\u7136\u5c31\u6beb\u4e0d\u72b9\u7591\u8d76\u6765\uff0c\u65b9\u81ea\u5230\u4e86\u4e34\u5b89\uff0c\u4ea6\u662f\u4e3a \u4e86\u90a3\u6ee1\u57ce\u5f02\u58f0\u6240\u60ca\uff0c\u8ffd\u53bb\u67e5\u5bfb\uff0c\u5374\u4e0d\u60f3\u9047\u7740\u4e86\u4ec1\u4e49\u5251\u5ba2\u4e91\u4e2d\u7a0b\u3002 \u3000\u3000\u4e91\u4e2d\u7a0b\u5173\u6000\u8001\u7236\uff0c\u751f\u6015\u5353\u957f\u537f\u82e5\u662f\u8bf4\u51fa\u4e2d\u539f\u5927\u4fa0\u7684\u5669\u8017\u6765\uff0c\u81ea\u5df1\u7684\u7236\u4eb2\u4f1a\u7ecf\u4e0d\u8d77\u8fd9\u79cd\u5de8 \u5927\u7684\u60b2\u75db\uff0c\u6b64\u523b\u89c1\u4e86\u5353\u957f\u537f\u7684\u795e\u8272\uff0c\u8fde\u5fd9\u9053\uff1a\u201c\u957f\u537f\u5f1f\uff0c\u4f60\u6bd4\u611a\u5144\u5148\u5230\u90a3\u91cc\uff0c\u4f60\u53ef\u66fe\u53d1\u73b0\uff0c \u7a76\u7adf\u662f\u8c01\u5c06\u90a3\u7ea2\u4e2d\u4f1a\u6b8b\u6740\u81f3\u6b64\u7684\u5462\uff1f\u201d \u3000\u3000\u5353\u957f\u537f\u52c9\u5f3a\u6309\u637a\u4f4f\u5fc3\u4e2d\u7684\u60b2\u6124\u4e4b\u6c14\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u5c0f\u5f1f\u672c\u5df2\u5c31\u5bdd\uff0c\u624b\u7279\u957f\u5251\uff0c\u4ece\u90a3\u680b\u706b \u5b85\u4e2d\u7a9c\u51fa\u6765\uff0c\u5c0f\u5f1f\u4f7f\u53bb\u67e5\u95ee\u7a76\u7adf\uff0c\u54ea\u77e5\u90a3\u5c11\u5e74\u4e0d\u5206\u7682\u767d\uff0c\u5c31\u548c\u5c0f\u5f1f\u52a8\u4e86\u4e0a\u624b\u2014\u2014\u201d\u4e91\u4e2d\u7a0b \u201c\u54e6\u201d\u4e86\u4e00\u58f0\uff0c\u63a5\u53e3\u9053\uff1a\u201c\u6b64\u4eba\u60f3\u5fc5\u5c31\u662f\u90a3\u8700\u4e2d\u6768\u4e00\u5251\u4e86\uff0c\u6211\u4e5f\u66fe\u770b\u89c1\u4ed6\u4e00\u526f\u72fc\u72c8\u4e4b\u6001\uff0c\u60f3 \u5fc5\u662f\u88ab\u8d24\u5f1f\u6559\u8bad\u4e86\u4e00\u987f\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u6447\u9996\u9053\uff1a\u201c\u8fd9\u5012\u4e0d\u662f\uff0c\u6b64\u4eba\u4ece\u706b\u5b85\u4e2d\u7a9c\u51fa\u65f6\uff0c\u5f62\u6001\u5c31\u7b2c\u4e94\u7ae0\u5feb\u5200\u5982\u6797\u5353\u957f\u537f\u5728 \u9ec4\u5c71\u59cb\u4fe1\u5cf0\u4e0b\uff0c\u773c\u770b\u81ea\u5df1\u53cc\u4eb2\u88ab\u90a3\u4e11\u4eba\u6e29\u5982\u7389\u51fb\u6bd9\uff0c\u827a\u6210\u4e0b\u5c71\u540e\uff0c\u81ea\u7136\u7b2c\u4e00\u4e2a\u8981\u6211\u7684\u5c31\u662f\u8fd9 \u540d\u6ee1\u5929\u4e0b\u7684\u5973\u9b54\u5934\u3002\u53ea\u662f\u8fd9\u7ea2\u8863\u4ed9\u5b50\uff0c\u8fd1\u5e74\u6765\u5374\u7a81\u7136\u9500\u58f0\u533f\u8ff9\uff0c\u6c5f\u6e56\u4e2d\u6839\u672c\u6ca1\u6709\u4eba\u77e5\u9053\u5979\u7684 \u884c\u8ff9\u3002\u5353\u957f\u537f\u5b50\u7136\u4e00\u8eab\uff0c\u968f\u610f\u98d8\u6cca\uff0c\u5230\u4e86\u6c5f\u5357\uff0c\u77e5\u9053\u4e86\u6b64\u4e8b\uff0c\u81ea\u7136\u5c31\u6beb\u4e0d\u72b9\u7591\u8d76\u6765\uff0c\u65b9\u81ea\u5230 \u4e86\u4e34\u5b89\uff0c\u4ea6\u662f\u4e3a\u4e86\u90a3\u6ee1\u57ce\u5f02\u58f0\u6240\u60ca\uff0c\u8ffd\u53bb\u67e5\u5bfb\uff0c\u5374\u4e0d\u60f3\u9047\u7740\u4e86\u4ec1\u4e49\u5251\u5ba2\u4e91\u4e2d\u7a0b\u3002\u4e91\u4e2d\u7a0b\u5173\u6000 \u8001\u7236\uff0c\u751f\u6015\u5353\u957f\u537f\u82e5\u662f\u8bf4\u51fa\u4e2d\u539f\u5927\u4fa0\u7684\u5669\u8017\u6765\uff0c\u81ea\u5df1\u7684\u7236\u4eb2\u4f1a\u7ecf\u4e0d\u8d77\u8fd9\u79cd\u5de8\u5927\u7684\u60b2\u75db\uff0c\u6b64\u523b \u89c1\u4e86\u5353\u957f\u537f\u7684\u795e\u8272\uff0c\u8fde\u5fd9\u9053\uff1a\u201c\u957f\u537f\u5f1f\uff0c\u4f60\u6bd4\u611a\u5144\u5148\u5230\u90a3\u91cc\uff0c\u4f60\u53ef\u66fe\u53d1\u73b0\uff0c\u7a76\u7adf\u662f\u8c01\u5c06\u90a3\u7ea2 \u4e2d\u4f1a\u6b8b\u6740\u81f3\u6b64\u7684\u5462\uff1f\u201d \u3000\u3000\u5353\u957f\u537f\u52c9\u5f3a\u6309\u637a\u4f4f\u5fc3\u4e2d\u7684\u60b2\u6124\u4e4b\u6c14\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u5c0f\u5f1f\u672c\u5df2\u5c31\u5bdd\uff0c\u624b\u7279\u957f\u5251\uff0c\u4ece\u90a3\u680b\u706b \u5b85\u4e2d\u7a9c\u51fa\u6765\uff0c\u5c0f\u5f1f\u4f7f\u53bb\u67e5\u95ee\u7a76\u7adf\uff0c\u54ea\u77e5\u90a3\u5c11\u5e74\u4e0d\u5206\u7682\u767d\uff0c\u5c31\u548c\u5c0f\u5f1f\u52a8\u4e86\u4e0a\u624b\u2014\u2014\u201d\u4e91\u4e2d\u7a0b \u201c\u54e6\u201d\u4e86\u4e00\u58f0\uff0c\u63a5\u53e3\u9053\uff1a\u201c\u6b64\u4eba\u60f3\u5fc5\u5c31\u662f\u90a3\u8700\u4e2d\u6768\u4e00\u5251\u4e86\uff0c\u6211\u4e5f\u66fe\u770b\u89c1\u4ed6\u4e00\u526f\u72fc\u72c8\u4e4b\u6001\uff0c\u60f3 \u5fc5\u662f\u88ab\u8d24\u5f1f\u6559\u8bad\u4e86\u4e00\u987f\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u6447\u9996\u9053\uff1a\u201c\u8fd9\u5012\u4e0d\u662f\uff0c\u6b64\u4eba\u4ece\u706b\u5b85\u4e2d\u7a9c\u51fa\u65f6\uff0c\u5f62\u6001\u5c31\u5df2\u72fc\u72c8\u4e0d\u582a\uff0c\u5c0f\u5f1f\u867d\u89c9\u6b64\u4eba \u5927\u6709\u53ef\u7591\uff0c\u4f46\u89c1\u4ed6\u51fa\u624b\uff0c\u5374\u662f\u6b63\u5b97\u7684\u5ce8\u5d4b\u5251\u6cd5\uff0c\u8eab\u624b\u4ea6\u81ea\u4e0d\u5f31\uff0c\u662f\u4ee5\u4e5f\u6ca1\u6709\u600e\u4e48\u96be\u4e3a\u4ed6\u2014\u2014 \u4ed6\u5306\u5306\u53d1\u4e86\u51e0\u62db\uff0c\u4e5f\u5c31\u8d70\u4e86\u3002\u201d \u3000\u3000\u591a\u81c2\u795e\u5251\u6697\u4e2d\u4e00\u53f9\uff0c\u77e5\u9053\u90a3\u6768\u4e00\u5251\u5fc5\u5b9a\u8d25\u5728\u8fd9\u5353\u957f\u537f\u7684\u624b\u4e0b\uff0c\u53ea\u662f\u5353\u957f\u537f\u53e3\u4e0b\u7559\u610f\uff0c\u6ca1 \u6709\u8bf4\u51fa\u6765\u800c\u5df2\uff0c\u5fc3\u4e2d\u6697\u81ea\u8d5e\u53f9\u4e4b\u4f59\uff0c\u4e0d\u7981\u5bf9\u8fd9\u6545\u4eba\u4e4b\u5b50\uff0c\u53c8\u52a0\u4e86\u51e0\u8bb8\u597d\u611f\u3002 \u3000\u3000\u684c\u4e0a\u7ea2\u70db\u5c06\u5c3d\uff0c\u58c1\u95f4\u706f\u6cb9\u4ea6\u5c06\u67af\uff0c\u661f\u65e0\u66f4\u9f13\u4e4b\u58f0\uff0c\u513f\u523b\u591c\u5fc5\u5b9a\u5df2\u5f88\u6df1\u4e86\u3002 \u3000\u3000\u51e0\u4e2a\u5f6a\u5f62\u5927\u6c49\u957f\u8eab\u7ad9\u4e86\u8d77\u6765\uff0c\u5411\u591a\u81c2\u795e\u5251\u4e91\u6c0f\u7236\u5b50\u5f53\u5934\u4e00\u63d6\uff0c\u5f00\u4e86\u5927\u95e8\uff0c\u65b9\u8d70\u5230\u95e8\u5916\uff0c \u5374\u53c8\u4e00\u8d77\u9000\u4e86\u8fdb\u6765\uff0c\u9875\u4e0a\u5374\u5df2\u53d8\u4e86\u989c\u8272\u3002 \u3000\u3000\u4e91\u4e2d\u7a0b\u5fc3\u4e2d\u4e00\u52a8\u62a2\u6b65\u8d70\u5230\u95e8\u53e3\uff0c\u63a2\u9996\u5916\u671b\uff0c\u53ea\u89c1\u5916\u9762\u7b14\u76f4\u7684\u4e00\u6761\u8857\u4e0a\uff0c\u4e0d\u77e5\u4f55\u65f6\uff0c\u7adf\u7136 \u7ad9\u6ee1\u4e86\u52b2\u88c5\u5305\u4e2d\u7684\u5927\u6c49\uff0c\u624b\u4e2d\u5404\u4e2a\u6a2a\u7279\u957f\u5200\uff0c\u88ab\u6708\u5149\u4e00\u6620\uff0c\u66f4\u89c9\u5200\u5149\u68ee\u7136\uff0c\u5bd2\u6c14\u4fb5\u4eba\u3002 \u3000\u3000\u8fd9\u4e9b\u52b2\u88c5\u5927\u6c49\u5e76\u80a9\u800c\u7acb\uff0c\u4e3a\u6570\u7adf\u5728\u767e\u4eba\u4ee5\u4e0a\uff0c\u5206\u522b\u7ad9\u6210\u4e24\u6392\uff0c\u4e00\u6392\u9762\u5411\u8857\u5de6\uff0c\u4e00\u6392\u5411\u8857 \u53f3\uff0c\u8fd9\u4e48\u591a\u4eba\u7ad9\u5728\u4e00\u8d77\uff0c\u7adf\u8fde\u534a\u4e1d\u58f0\u97f3\u90fd\u6ca1\u6709\u53d1\u51fa\u6765\u3002 \u3000\u3000\u4e91\u4e2d\u7a0b\u5251\u7709\u5fae\u76b1\uff0c\u56de\u9996\u6c89\u58f0\u9053\uff1a\u201c\u592a\u884c\u4fa0\u5200\u4f1a\uff0c\u4e00\u5411\u4ece\u4e0d\u7275\u52a8\u5b98\u5e9c\uff0c\u6b64\u523b\u600e\u4e48\u5728\u8fd9\u95f9\u5e02 \u8857\u4e0a\uff0c\u5c31\u6446\u51fa\u8fd9\u7b49\u9635\u4ed7\u6765\u2026\u2026\u201d \u3000\u3000\u4ed6\u8bed\u58f0\u4e00\u987f\uff0c\u76ee\u5149\u53c8\u5411\u5916\u671b\uff0c\u53ea\u89c1\u6ee1\u8857\u5927\u6c49\u4e00\u4e2a\u4e2a\u76ee\u5149\u70af\u7136\uff0c\u56db\u4e0b\u641c\u7d22\u7740\uff0c\u8eab\u8eaf\u5374\u6709\u5982 \u6ce5\u5851\u6728\u96d5\uff0c\u4e1d\u6beb\u6ca1\u6709\u52a8\u5f39\u4e00\u4e0b\u3002 \u3000\u3000\u65b9\u624d\u5728\u8857\u4e0a\u6765\u56de\u67e5\u770b\u7684\u5b98\u5dee\uff0c\u6b64\u523b\u65e9\u5c31\u4e0d\u77e5\u9053\u8dd1\u5230\u54ea\u91cc\u53bb\u4e86\uff0c\u4f46\u542c\u5f97\u6c89\u91cd\u7684\u547c\u5438\u4e4b\u58f0\uff0c \u6b64\u8d77\u5f7c\u843d\uff0c\u663e\u7136\u8fd9\u4e9b\u5feb\u5200\u5e2e\u4f17\uff0c\u4eba\u4eba\u5fc3\u4e2d\u90fd\u81ea\u5177\u6709\u5341\u5206\u7684\u6212\u5907\uff0c\u53ea\u662f\u4e0d\u77e5\u9053\u4ed6\u4eec\u6212\u5907\u4ec0\u4e48\u800c \u5df2\u3002 \u3000\u3000\u4ec1\u4e49\u5251\u5ba2\u5fc3\u4e2d\u7591\u7aa6\u4e1b\u751f\uff0c\u4ed6\u548c\u8fd9\u5feb\u5200\u4f1a\u867d\u7136\u7d20\u65e0\u4ea4\u5f80\uff0c\u4f46\u8fd1\u5e74\u6765\uff0c\u4ed6\u5df2\u9690\u7136\u6210\u4e3a\u6c5f\u5357\u4fa0 \u6797\u4e2d\u7684\u9886\u8896\u4eba\u7269\uff0c\u5bf9\u8fd9\u4e9b\u4e8b\uff0c\u81ea\u7136\u4e0d\u80fd\u89c6\u82e5\u65e0\u7779\uff0c\u5fc3\u4e2d\u601d\u4ed8\u4e86\u534a\u664c\uff0c\u53c8\u81ea\u56de\u9996\u9053\uff1a\u201c\u7239\u7239\uff0c \u6211\u518d\u51fa\u53bb\u770b\u770b\uff0c\u60a8\u8001\u4eba\u5bb6\u2014\u2014\u8fd8\u662f\u56de\u5e97\u53bb\u4f11\u606f\u5427\u3002\u201d \u3000\u3000\u591a\u81c2\u795e\u5251\u4e00\u634b\u957f\u987b\uff0c\u970d\u7136\u7ad9\u4e86\u8d77\u6765\uff0c\u5fae\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u7239\u7239\u867d\u7136\u8001\u4e86\uff0c\u53ef\u662f\u8fd8 \u6ca1\u6709\u5230\u4f11\u606f\u7684\u65f6\u5019\u3002\u201d \u3000\u3000\u5927\u6b65\u8d70\u51fa\u95e8\u5916\u3002\u76ee\u5149\u56db\u626b\uff0c\u8fd9\u591a\u81c2\u795e\u5251\u6b63\u662f\u59dc\u6842\u4e4b\u6027\uff0c\u8001\u800c\u5f25\u8fa3\uff0c\u867d\u7136\u4e45\u5df2\u4e0d\u5728\u6c5f\u6e56\u8d70 \u52a8\uff0c\u4f46\u6b64\u523b\u5374\u53c8\u72af\u4e86\u6614\u65e5\u7684\u8c6a\u6c14\uff0c\u7adf\u4e0d\u7406\u4f1a\u4ed6\u7231\u5b50\u7684\u597d\u610f\uff0c\u7b14\u76f4\u5411\u8857\u5934\u8d70\u53bb\u3002 \u3000\u3000\u4e91\u4e2d\u7a0b\u8f7b\u53f9\u4e00\u58f0\uff0c\u548c\u5353\u957f\u537f\u4e92\u89c6\u4e00\u773c\uff0c\u5feb\u6b65\u8ddf\u4e86\u8fc7\u53bb\uff0c\u53ea\u89c1\u6ee1\u8857\u7684\u52b2\u88c5\u5927\u6c49\uff0c\u76ee\u5149\u9f50\u90fd \u8f6c\u5230\u81ea\u5df1\u4e09\u4eba\u8eab\u4e0a\uff0c\u5374\u9ed8\u7136\u4ff1\u90fd\u8083\u7acb\u4e0d\u52a8\uff0c\u4e5f\u6ca1\u6709\u4e00\u4e2a\u4eba\u8d70\u51fa\u6765\u5411\u81ea\u5df1\u95ee\u8bdd\u7684\u3002 \u3000\u3000\u591a\u81c2\u795e\u5251\u8170\u6746\u633a\u5f97\u7b14\u76f4\uff0c\u5927\u6b65\u8d70\u5728\u524d\u9762\uff0c\u665a\u98ce\u5439\u5f97\u4ed6\u9894\u4e0b\u94f6\u987b\u4e1d\u4e1d\u98d8\u821e\u3002 \u3000\u3000\u5929\u4e0a\u6708\u660e\u661f\u7a00\uff0c\u5730\u4e0a\u5200\u5149\u5982\u96ea\uff0c\u8fd9\u5e74\u5df2\u53e4\u7a00\u7684\u6b66\u6797\u5065\u8005\uff0c\u53ea\u89c9\u8c6a\u6c14\u987f\u751f\uff0c\u4eff\u4f5b\u53c8\u56de\u5230\u5c11 \u5e74\u65f6\u8dc3\u9a6c\u6a2a\u5200\uff0c\u7b11\u505a\u6c5f\u6e56\u7684\u5149\u666f\uff0c\u56de\u5934\u6717\u58f0\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e2d\u7a0b\uff0c\u4f60\u8981\u662f\u7d2f\u4e86\uff0c\u5c31\u5feb\u56de\u5e97\u53bb\u4f11 \u606f\u5427\uff0c\u53eb\u957f\u537f\u966a\u7740\u6211\u4e5f\u662f\u4e00\u6837\u3002\u201d \u3000\u3000\u53c8\u81ea\u4e00\u7b11\uff1a\u201c\u6211\u8001\u4e86\uff0c\u6d3b\u7684\u65e5\u5b50\u4e5f\u4e0d\u957f\u4e86\uff0c\u603b\u820d\u4e0d\u5f97\u5c06\u5927\u597d\u5149\u9634\u6d6a\u8d39\u5728\u7761\u89c9\u4e0a\uff0c\u4f60\u4eec\u5e74 \u8f7b\u4eba\uff0c\u5012\u662f\u8981\u591a\u7761\u4e00\u4f1a\u513f\u3002\u201d \u3000\u3000\u4e91\u4e2d\u7a0b\u65e0\u53ef\u5948\u4f55\u5730\u82e6\u7b11\u4e00\u4e0b\uff0c\u4e00\u8a00\u4e0d\u53d1\u5730\u8ddf\u5728\u7239\u7239\u8eab\u540e\uff0c\u5353\u957f\u537f\u773c\u770b\u8fd9\u7236\u5b50\u4fe9\u7684\u76f8\u4e92\u5173 \u6000\u4e4b\u60c5\uff0c\u5fc3\u4e2d\u611f\u6168\u4e07\u5206\uff0c\u4e0d\u77e5\u662f\u4ec0\u4e48\u6ecb\u5473\uff0c\u4fef\u9996\u800c\u671b\uff0c\u5730\u4e0a\u4eba\u5f71\u5982\u6797\uff0c\u81ea\u5df1\u548c\u4e91\u4e2d\u7a0b\u7684\u8eab \u5f71\uff0c\u5374\u957f\u957f\u5730\u6620\u5728\u8857\u4fa7\u7684\u95e8\u677f\u4e0a\uff0c\u539f\u6765\u6b64\u523b\u6708\u5df2\u897f\u6c89\uff0c\u591c\u8272\u5c06\u5c3d\uff0c\u53c8\u662f\u5feb\u8981\u7834\u6653\u7684\u65f6\u5019\u3002 \u3000\u3000\u8fd9\u4e09\u4eba\u8d70\u5f97\u4ff1\u90fd\u6781\u5feb\uff0c\u6643\u773c\u5df2\u8d70\u5230\u8857\u7684\u8f6c\u89d2\u5904\uff0c\u4e00\u8d77\u505c\u8db3\u800c\u671b\uff0c\u5374\u89c1\u5de6\u53f3\u4e24\u6761\u8857\u4e0a\uff0c\u7adf \u8fde\u534a\u4e2a\u4eba\u5f71\u90e8\u6ca1\u6709\uff0c\u9752\u77f3\u677f\u94fa\u6210\u7684\u8857\u9762\u4e0a\uff0c\u8840\u8ff9\u5df2\u51c0\uff0c\u6c34\u75d5\u4ea6\u5e72\uff0c\u4e24\u65c1\u7684\u5e97\u94fa\uff0c\u95e8\u677f\u7d27\u95ed\uff0c \u9759\u5f97\u4f3c\u4e4e\u8fde\u81ea\u5df1\u5fc3\u8df3\u7684\u58f0\u97f3\u90fd\u542c\u5f97\u51fa\u6765\u3002 \u3000\u3000\u4e91\u8c26\u6d53\u7709\u4e00\u76b1\uff0c\u624b\u634b\u957f\u987b\uff0c\u56de\u9996\u5411\u8857\u7684\u53e6\u4e00\u5934\u8d70\u53bb\uff0c\u65b9\u81ea\u8d70\u5230\u4e00\u534a\uff0c\u90a3\u8fb9\u5374\u5df2\u8fce\u4e86\u51e0\u4e2a \u4eba\u6765\uff0c\u624b\u4e2d\u4ea6\u81ea\u5404\u6301\u5175\u5203\uff0c\u8fdc\u8fdc\u5c31\u547c\u559d\u9053\uff1a\u201c\u670b\u53cb\u662f\u54ea\u6761\u7ebf\u4e0a\u7684\uff0c\u4eae\u4e2a\u4e07\u513f\u51fa\u6765\uff0c\u514d\u5f97\u5144\u5f1f \u4eec\u7167\u4e86\u4e0d\u4eae\uff0c\u4f24\u4e86\u548c\u6c14\u3002\u201d \u3000\u3000\u4e91\u4e2d\u7a0b\u8eab\u5f62\u4e00\u52a8\uff0c\u4e00\u4e2a\u7bad\u6b65\uff0c\u7a9c\u5230\u4ed6\u7239\u7239\u9762\u524d\uff0c\u53cc\u624b\u4e00\u5f20\uff0c\u6717\u58f0\u9053\uff1a\u201c\u5728\u4e0b\u4e91\u4e2d\u7a0b\uff0c\u548c \u4f60\u4eec\u4e86\u5f53\u5bb6\u7684\u662f\u670b\u53cb\u2014\u2014\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u90a3\u8fb9\u98de\u6b65\u800c\u6765\u4e00\u4e2a\u9880\u957f\u6c49\u5b50\uff0c\u5df2\u81ea\u6717\u58f0\u9053\uff1a\u201c\u5927\u884c\uff01 \u5c71\u91cc\u4e09\u628a\u5200\u2014\u2014\u201d\u6ee1\u8857\u7684\u52b2\u88c5\u5927\u6c49\uff0c\u8f70\u7136\u4e00\u58f0\uff0c\u9f50\u53e3\u9053\uff1a\u201c\u795e\u9b3c\u89c1\u4e86\u90fd\u5f2f\u8170\u3002\u201d \u3000\u3000\u4e91\u4e2d\u7a0b\u54c8\u54c8\u4e00\u7b11\uff0c\u63a5\u53e3\u9053\uff1a\u201c\u5feb\u5200\u795e\u5200\u5939\u98de\u5200\u3002\u201d \u3000\u3000\u90a3\u9880\u957f\u6c49\u5b50\u4e00\u4e2a\u7bad\u6b65\u7a9c\u4e0a\u6765\uff0c\u5927\u58f0\u7b11\u9053\uff1a\u201c\u679c\u7136\u662f\u4e91\u5927\u4fa0\u3002\u201d \u3000\u3000\u76ee\u5149\u4e00\u8f6c\uff0c\u53c8\u9053\uff1a\u201c\u8fd9\u4f4d\u60f3\u5fc5\u662f\u4e91\u8001\u7237\u5b50\u4e86\u3002\u201d \u3000\u3000\u606d\u8eab\u4e00\u63d6\uff1a\u201c\u5c0f\u53ef\u9f9a\u5947\uff0c\u4e0d\u60f3\u4eca\u65e5\u80fd\u89c1\u5230\u8d24\u7236\u5b50\uff0c\u5b9e\u4e43\u655d\u4f1a\u4e4b\u5e78\u3002\u201d \u3000\u3000\u4e91\u4e2d\u7a0b\u4ea6\u81ea\u8eac\u8eab\u7b54\u793c\uff0c\u542b\u7b11\u9053\uff1a\u201c\u539f\u6765\u5144\u53f0\u5c31\u662f\u9f9a\u7237\uff0c\u5c0f\u53ef\u4e45\u95fb\u5927\u540d\uff0c\u4eca\u65e5\u65b9\u5f97\u8bc6\u8346\uff0c \u5b9e\u5728\u9ad8\u5174\u5f97\u5f88\u3002\u201d \u3000\u3000\u591a\u81c2\u795e\u5251\u4ea6\u634b\u987b\u7b11\u9053\uff1a\u201c\u8001\u592b\u5e38\u542c\u6b66\u6797\u4e2d\u4eba\u4f20\u544a\uff0c\u592a\u884c\u5feb\u5200\u4f1a\u91cc\u6709\u4f4d\u795e\u5200\u5962\u906e\u7684\u6c49\u5b50\uff0c \u4eca\u65e5\u4e00\u89c1\uff0c\u679c\u662f\u540d\u4e0b\u65e0\u865a\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u8fdc\u8fdc\u7ad9\u5728\u4e00\u8fb9\uff0c\u6b64\u523b\u6697\u5fd6\uff1a\u201c\u4e91\u6c0f\u7236\u5b50\u4e4b\u6b66\u529f\u5982\u4f55\uff0c\u59d1\u4e14\u4e0d\u8bf4\uff0c\u5c31\u51ed\u4eba\u5bb6\u8fd9\u79cd\u5904 \u4e16\u5bf9\u4eba\u7684\u70ed\u5ff1\u548c\u8c0e\u865a\uff0c\u5c31\u4e0d\u662f\u666e\u901a\u6b66\u6797\u4e2d\u4eba\u80fd\u671b\u5176\u9879\u80cc\uff0c\u829c\u6e56\u4e91\u95e8\uff0c\u540d\u95fb\u5929\u4e0b\uff0c\u5b9e\u975e\u4fa5\u5e78 \u54e9\u3002\u201d\u8d5e\u53f9\u4e4b\u4f59\uff0c\u5374\u89c1\u90a3\u795e\u5200\u9f9a\u5947\u542b\u7b11\u53c8\u9053\uff1a\u201c\u4e91\u8001\u7237\u5b50\u8fd9\u4e48\u8bf4\uff0c\u5b9e\u5728\u53eb\u5c0f\u53ef\u6c57\u989c\u5f97\u5f88\u3002\u201d \u3000\u3000\u4e91\u4e2d\u7a0b\u76ee\u5149\u4e00\u8f6c\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u4e01\u4e03\u7237\u53ef\u5728\u6b64\u5730\uff0c\u5144\u53f0\u5982\u679c\u4e0d\u5acc\u5c0f\u53ef\u5192\u6627\uff0c\u5c0f\u53ef\u5012\u60f3\u8bf7 \u6559\uff0c\u8d35\u4f1a\u5728\u8fd9\u4e34\u5b89\u57ce\u91cc\uff0c\u83ab\u975e\u53c8\u7ed9\u4e0a\u4ec0\u4e48\u6881\u5b50\u2014\u2014\u201d\u591a\u81c2\u795e\u5251\u4e91\u8c26\u63a5\u53e3\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u5982\u679c\u6709 \u4ec0\u4e48\u5730\u65b9\u9700\u8981\u8001\u592b\u7236\u5b50\u4fe9\u7a0d\u5c3d\u7ef5\u8584\u7684\uff0c\u9f9a\u4e09\u7237\u53ea\u7ba1\u8bf4\u51fa\u6765\u597d\u4e86\u3002\u201d \u3000\u3000\u201c\u795e\u5200\u201d\u9f9a\u5947\u53f9\u4e00\u58f0\uff0c\u9762\u4e0a\u7b11\u5bb9\u5c3d\u655b\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u4e0d\u7792\u4e91\u8001\u7237\u5b50\u8bf4\uff0c\u655d\u4f1a\u4eca\u591c\uff0c\u5b9e\u5df2\u5927 \u96be\u4e34\u5934\uff0c\u8bf4\u4e0d\u5b9a\u8fd9\u4efd\u60e8\u6de1\u7ecf\u8425\u7684\u57fa\u4e1a\uff0c\u4eca\u591c\u4ea6\u8981\u548c\u96c1\u8361\u7ea2\u4e2d\u4f1a\u4e00\u6837\uff0c\u846c\u9001\u5728\u8fd9\u4e34\u5b89\u57ce\u91cc\u3002\u201d \u3000\u3000\u4ed6\u76ee\u5149\u51db\u7136\u56db\u626b\uff0c\u53c8\u9053\uff1a\u201c\u4e91\u8001\u7237\u5b50\u5982\u80fd\u4ed7\u4e49\u63f4\u624b\uff0c\u5219\u975e\u4f46\u662f\u5c0f\u53ef\u4e4b\u5e78\uff0c\u4ea6\u662f\u795e\u5200\u4f1a\u4e0a \u4e0b\u5343\u767e\u5f1f\u5144\u4e4b\u5e78\uff0c\u53ea\u662f\u2014\u2014\u6b64\u5730\u6050\u975e\u8c08\u8bdd\u4e4b\u5904\uff0c\u4e0d\u77e5\u4f60\u8001\u4eba\u5bb6\u53ef\u5426\u968f\u5c0f\u53ef\u524d\u884c\u51e0\u6b65\uff0c\u655d\u4f1a\u7684 \u4e01\u4e03\u54e5\u4e5f\u5728\u90a3\u91cc\uff0c\u4ed6\u4ea6\u662f\u4e45\u4ef0\u4f60\u8001\u4eba\u5bb6\u7684\u82f1\u540d\uff0c\u603b\u6068\u65e0\u7f18\u62dc\u89c1\uff0c\u770b\u5230\u4e91\u8001\u7237\u5b50\u53bb\u4e86\uff0c\u4e0d\u77e5\u8981 \u5982\u4f55\u9ad8\u5174\u54e9\u3002\u201d \u3000\u3000\u8fd9\u795e\u5200\u9f9a\u5947\uff0c\u8eab\u6750\u9880\u957f\uff0c\u9762\u76ee\u575a\u6bc5\uff0c\u9894\u4e0b\u5df2\u6709\u5fae\u987b\uff0c\u4e00\u773c\u671b\u53bb\uff0c\u82f1\u633a\u5f97\u5f88\uff0c\u73b0\u65f6\u4ed6\u867d\u662f \u795e\u60c5\u4e0d\u5b89\uff0c\u4f46\u8bf4\u8d77\u8bdd\u6765\uff0c\u5374\u4ecd\u7136\u662f\u6781\u4e3a\u5f97\u4f53\uff0c\u663e\u89c1\u5f97\u662f\u4e2a\u7cbe\u5e72\u89d2\u8272\u3002 \u3000\u3000\u591a\u81c2\u795e\u5251\u4e00\u634b\u957f\u987b\uff0c\u5927\u6b65\u8d70\u5230\u524d\u9762\uff0c\u8bf4\u9053\uff1a\u201c\u9f9a\u4e09\u7237\uff0c\u5feb\u5e26\u8001\u592b\u53bb\u89c1\u4e01\u603b\u74e2\u628a\u5b50\uff0c\u6211\u5012 \u8981\u6597\u6597\u770b\uff0c\u90a3\u662f\u4ec0\u4e48\u5389\u5bb3\u89d2\u8272\uff0c\u7adf\u6562\u5c06\u5929\u4e0b\u6b66\u6797\u540c\u9053\u90fd\u4e0d\u770b\u5728\u773c\u91cc\u3002\u201d \u3000\u3000\u795e\u5200\u9f9a\u5947\u9762\u4e0a\u53c8\u590d\u6cdb\u5f00\u4e86\u7b11\u5bb9\uff0c\u548c\u4e91\u8c26\u5e76\u80a9\u800c\u884c\uff0c\u8d70\u5230\u4e00\u5bb6\u95e8\u677f\u50cf\u662f\u5df2\u88ab\u70df\u706b\u718f\u5f97\u9eef\u9ed1 \u4e86\u7684\u5e97\u94fa\u524d\u9762\uff0c\u4f38\u624b\u8f7b\u8f7b\u6572\u4e86\u4e24\u4e0b\uff0c\u91cc\u9762\u4f20\u51fa\u4e00\u4e2a\u6c89\u91cd\u7684\u58f0\u97f3\uff0c\u95ee\u9053\uff1a\u201c\u662f\u8c01\uff1f\u201d \u3000\u3000\u9f9a\u5947\u4e8e\u54b3\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4e09\u628a\u5200\u3002\u201d \u3000\u3000\u5927\u95e8\u968f\u5373\u5f00\u4e86\u4e00\u7ebf\uff0c\u591a\u81c2\u795e\u5251\u5f53\u5148\u8d70\u4e86\u8fdb\u53bb\uff0c\u795e\u5200\u9f9a\u5947\u5fae\u4e00\u9a7b\u8db3\uff0c\u5411\u540e\u9762\u548c\u4e91\u4e2d\u7a0b\u540c\u6765 \u7684\u5353\u957f\u537f\u4e0a\u4e0b\u6253\u91cf\u4e86\u4e24\u773c\uff0c\u542b\u7b11\u9053\uff1a\u201c\u8fd9\u4f4d\u5144\u53f0\u9762\u751f\u5f97\u5f88\uff0c\u4e91\u5927\u54e5\u53ef\u5426\u4e3a\u5c0f\u53ef\u5f15\u89c1\u5f15\u89c1\u3002\u201d \u3000\u3000\u4e91\u4e2d\u7a0b\u7b11\u9053\uff1a\u201c\u5784\u4e09\u7237\uff0c\u4f60\u53ef\u66fe\u542c\u5230\u6614\u5e74\u6709\u4f4d\u540d\u9707\u4e00--\u201d\u5353\u957f\u537f\u7a81\u7136\u8f7b\u54b3\u4e00\u58f0\uff0c\u4e91\u4e2d\u7a0b \u76ee\u5149\u4e00\u8f6c\uff0c\u54c8\u54c8\u4e00\u7b11\uff0c\u7acb\u523b\u6539\u53e3\u9053\uff1a\u201c\u8fd9\u4f4d\u5353\u957f\u537f\u5353\u8001\u5f1f\uff0c\u662f\u5728\u4e0b\u7684\u81f3\u4eb2\uff0c\u4f60\u4eec\u4ee5\u540e\u5012\u8981\u591a \u4eb2\u8fd1\u4eb2\u8fd1\u3002\u201d \u3000\u3000\u795e\u5200\u9f9a\u5947\u4e45\u95ef\u6c5f\u6e56\uff0c\u662f\u4f55\u7b49\u7cbe\u5e72\u7684\u89d2\u8272\uff0c\u6b64\u523b\u76ee\u5149\u4e00\u8f6c\uff0c\u5df2\u77e5\u9053\u8fd9\u82f1\u4fca\u7684\u5c11\u5e74\u5fc5\u5b9a\u5927\u6709 \u6765\u5934\uff0c\u5f53\u5934\u4e00\u63d6\uff0c\u542b\u7b11\u63d6\u5ba2\u3002 \u3000\u3000\u5353\u957f\u537f\u76ee\u5149\u4e00\u8f6c\uff0c\u53ea\u89c1\u8fd9\u95ee\u94fa\u5b50\u91cc\u706f\u5149\u83b9\u83b9\uff0c\u62e5\u6324\u4e0d\u582a\uff0c\u4e00\u8fdb\u95e8\u5c31\u6709\u79cd\u6df7\u5408\u7740\u70df\u718f\u548c\u6e7f \u70ed\u4e4b\u6c14\uff0c\u76f4\u51b2\u9f3b\u7aef\u3002\u518d\u4e00\u6253\u91cf\uff0c\u624d\u77e5\u9053\u6b64\u5730\u7adf\u662f\u95f4\u94c1\u5668\u5e97\u3002 \u3000\u3000\u591a\u81c2\u795e\u5251\u4e00\u624b\u634b\u7740\u957f\u987b\uff0c\u5353\u7acb\u5728\u4e00\u4e2a\u9ad8\u5927\u7684\u7827\u65c1\u8fb9\uff0c\u4e00\u4e2a\u6380\u7740\u8863\u895f\u7684\u9b41\u4f1f\u5927\u6c49\uff0c\u6b63\u5728\u4e3a \u4ed6\u5f15\u89c1\u56db\u4e0b\u7684\u6b66\u6797\u670b\u53cb\uff0c\u90a3\u4e9b\u540d\u5b57\u5353\u957f\u537f\u867d\u4e0d\u719f\u6089\uff0c\u4f46\u60f3\u5fc5\u662f\u6b66\u6797\u4e2d\u6210\u540d\u7acb\u4e07\u7684\u89d2\u8272\u3002 \u3000\u3000\u4e00\u9635\u5fc5\u6709\u7684\u5bd2\u6684\u8fc7\u540e\uff0c\u8bdd\u624d\u5f00\u59cb\u8f6c\u5165\u6b63\u9898\uff0c\u90a3\u62ab\u895f\u7684\u5927\u6c49\uff0c\u6b63\u662f\u7edf\u9886\u592a\u884c\u5feb\u5200\u4f1a\u7684\u9886\u8896 \u4eba\u7269\uff0c\u5feb\u5200\u4e01\u4e03\u3002 \u3000\u3000\u6b64\u523b\uff0c\u4ed6\u6d53\u7709\u6df1\u76b1\uff0c\u76ee\u5149\u6df1\u6c89\uff0c\u5353\u7acb\u5728\u7fa4\u8c6a\u4e4b\u95f4\uff0c\u6c89\u58f0\u800c\u9053\uff1a\u201c\u5feb\u5200\u4f1a\u521b\u4e1a\u81f3\u4eca\uff0c\u867d\u7136 \u8bf4\u5f00\u4e86\u53ea\u662f\u4e00\u4e9b\u7a77\u670b\u53cb\u51d1\u5728\u4e00\u5757\u513f\u6df7\u996d\u5403\u7684\uff0c\u4f46\u5144\u5f1f\u81ea\u95ee\uff0c\u5374\u6ca1\u6709\u505a\u51fa\u4ec0\u4e48\u89c1\u4e0d\u5f97\u4eba\u7684\u4e8b \u6765\u3002\u8fd9\u6b21\u5929\u76ee\u5c71\u7684\u76db\u4f1a\uff0c\u5144\u5f1f\u4eec\u4e5f\u53ea\u662f\u60f3\u6765\u51d1\u51d1\u70ed\u95f9\uff0c\u4e95\u6ca1\u6709\u4ec0\u4e48\u4eba\u8d22\u4e24\u5f97\u7684\u91ce\u5fc3\uff0c\u54ea\u77e5\u2014 \u2014\u201d\u8fd9\u5feb\u5200\u4e01\u4e03\uff0c\u8eab\u6750\u9b41\u4f1f\uff0c\u58f0\u82e5\u6d2a\u949f\uff0c\u4e00\u53e3\u6c14\u8bf4\u5230\u8fd9\u91cc\uff0c\u7a81\u7136\u4ef0\u5929\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u5fc3\u80f8\u4e4b \u95f4\uff0c\u4eff\u4f5b\u79ef\u90c1\u9887\u91cd\u3002 \u3000\u3000\u5353\u957f\u537f\u51b7\u773c\u800c\u89c2\uff0c\u5fc3\u91cc\u4e0d\u7981\u5947\u602a\uff1a\u201c\u4ece\u8fd9\u5feb\u5200\u4e01\u4e03\u795e\u60c5\u770b\u6765\uff0c\u663e\u7136\u6b64\u4eba\u6027\u60c5\u723d\u76f4\uff0c\u662f\u4e2a \u6807\u51c6\u7684\u8349\u6cfd\u82f1\u96c4\uff0c\u6b64\u523b\u53c8\u6709\u4ec0\u4e48\u4f1a\u4ee4\u5f97\u4ed6\u5982\u6b64\u957f\u5401\u77ed\u53f9\u5462\uff1f\u201d \u3000\u3000\u5374\u542c\u4ed6\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u524d\u5929\u665a\u4e0a\uff0c\u6211\u548c\u6a80\u8001\u4e8c\u7761\u5728\u4e00\u8d77\uff0c\u534a\u591c\u91cc\u61f5\u61f5\u61c2\u61c2\u7684\uff0c\u53ea\u89c9\u6709\u4e2a\u4eba \u5728\u52a8\u6211\u7684\u5934\u53d1\uff0c\u5f53\u65f6\u6211\u5fc3\u91cc\u4e00\u60ca\uff0c\u5927\u53eb\u4e00\u58f0\uff0c\u5f20\u5f00\u773c\u6765\uff0c\u53ea\u89c1\u7a97\u5b50\u662f\u5f00\u7740\u7684\uff0c\u6708\u5149\u4ece\u7a97\u5916\u7528 \u8fdb\u6765\uff0c\u5374\u6709\u4e00\u6761\u4eba\u5f71\uff0c\u50cf\u7535\u4e5f\u4f3c\u7684\u4ece\u7a97\u5b50\u91cc\u63a0\u4e86\u51fa\u53bb\uff0c\u6211\u4e86\u4e03\u4e0d\u662f\u957f\u4eba\u5bb6\u5fd7\u6c14\u706d\u81ea\u5df1\u7684\u5a01 \u98ce\uff0c\u53ef\u662f\u6211\u957f\u5f97\u8fd9\u4e48\u5927\u4e86\uff0c\u95ef\u8361\u6c5f\u6e56\u4e5f\u6709\u534a\u751f\uff0c\u5374\u4ece\u6765\u6ca1\u6709\u89c1\u8fc7\u8fd9\u7b49\u8eab\u624b\uff0c\u6709\u5982\u6b64\u4e4b\u5feb \u7684\u3002\u201d \u3000\u3000\u4ed6\u53c8\u81ea\u957f\u53f9\u4e00\u58f0\uff0c\u53c8\u9053\uff1a\u201c\u5f53\u65f6\u6211\u5fc3\u91cc\u771f\u662f\u60ca\u6050\u4ea4\u96c6\uff0c\u8d64\u7740\u811a\u5c31\u60f3\u4ece\u5e8a\u4e0a\u8df3\u4e0b\u6765\uff0c\u54ea\u77e5 \u5934\u9876\u7a81\u7136\u4e00\u75db\uff0c\u50cf\u662f\u88ab\u4ec0\u4e48\u4eba\u5c06\u5934\u53d1\u62c9\u4f4f\u4e86\u3002\u201d \u3000\u3000\u4ed6\u773c\u4e2d\u95ea\u8fc7\u4e00\u4e1d\u60ca\u6050\u7684\u795e\u8272\uff0c\u50cf\u662f\u5f53\u65f6\u7684\u60c5\u666f\u72b9\u5728\u773c\u524d\uff0c\u5fae\u53f9\u53c8\u9053\uff1a\u201c\u6211\u5927\u60ca\u4e4b\u4e0b\uff0c\u4e00 \u4e2a\u864e\u6251\u671d\u5e8a\u5934\u6251\u4e86\u8fc7\u53bb\uff0c\u624d\u53d1\u73b0\u54ea\u91cc\u6709\u4ec0\u4e48\u62c9\u4f4f\u6211\u7684\u5934\u53d1\uff0c\u53ea\u662f\u90a3\u4eba\u5df2\u795e\u4e0d\u77e5\u3001\u9b3c\u4e0d\u89c9\u7684\u5c06 \u6211\u7684\u5934\u53d1\u548c\u6a80\u8001\u4e8c\u7684\u7ed3\u5728\u4e00\u8d77\u4e86\u3002\u201d \u3000\u3000\u4ed6\u4e0b\u610f\u8bc6\u5730\u4f38\u624b\u6478\u4e86\u6478\u5934\uff0c\u8138\u4e0a\u6ee1\u662f\u6cae\u4e27\u7684\u795e\u8272\uff0c\u53c8\u9053\uff1a\u201c\u90a3\u65f6\u6211\u548c\u6a80\u8001\u4e8c\u7684\u5fc3\u91cc\u771f\u4e0d \u77e5\u662f\u4ec0\u4e48\u6ecb\u5473\uff0c\u8bd5\u60f3\u6211\u4eec\u5728\u6c5f\u6e56\u4e0a\u4e5f\u7b97\u6709\u7740\u70b9\u4e07\u513f\u4e86\uff0c\u6b64\u523b\u88ab\u4eba\u5bb6\u5728\u81ea\u5df1\u5934\u4e0a\u505a\u4e86\u5982\u6b64\u7684\u624b \u811a\uff0c\u6211\u4eec\u5374\u8fde\u4eba\u5bb6\u7684\u5f71\u5b50\u90fd\u6ca1\u6709\u78b0\u5230\uff0c\u4eba\u5bb6\u771f\u8981\u662f\u628a\u6211\u7684\u8111\u888b\u5272\u4e0b\u6765\uff0c\u6211\u4eec\u8fd8\u4e0d\u662f\u7167\u6837\u4e0d\u77e5 \u9053\u3002\u672c\u6765\uff0c\u6211\u8fd8\u5728\u5947\u602a\uff0c\u8fd9\u4eba\u4f1a\u662f\u8c01\u5462\uff1f\u6041\u5730\u6349\u5f04\u6211\uff0c\u6211\u5f1f\u5144\u4eec\u5728\u6b66\u6797\u4e2d\u867d\u4e5f\u7ed3\u4e0b\u8fc7\u4e0d\u5c11\u6881 \u5b50\uff0c\u53ef\u7edd\u4e0d\u4f1a\u6709\u5982\u6b64\u6b66\u529f\u7684\u4eba\u5440\uff0c\u6211\u4eec\u5fc3\u91cc\u65e2\u60ca\u53c8\u602a\uff0c\u53ef\u662f\u7b49\u5230\u6211\u548c\u6a80\u8001\u4e8c\u53bb\u89e3\u5934\u53d1\u7684\u65f6 \u5019\uff0c\u6211\u624d\u77e5\u9053\u539f\u6765\u662f\u8fd9\u4e48\u56de\u4e8b\u3002\u201d \u3000\u3000\u4ed6\u4e00\u9762\u8bf4\u7740\uff0c\u4e00\u9762\u4ece\u6000\u4e2d\u53d6\u51fa\u4e00\u5f20\u6d45\u9ec4\u7684\u7eb8\u67ec\u6765\uff0c\u53cc\u624b\u4ea4\u4e0e\u4e91\u8c26\uff0c\u53ea\u89c1\u4e0a\u9762\u5199\u9053\uff1a\u201c\u4e24 \u65e5\u4e4b\u5185\uff0c\u901f\u79bb\u4e34\u5b89\uff0c\u4e0d\u9075\u6211\u547d\uff0c\u9e21\u5927\u96be\u5b89\u3002\u201d \u3000\u3000\u591a\u81c2\u795e\u5251\u6d53\u7709\u4e00\u76b1\uff0c\u5374\u89c1\u90a3\u5feb\u5200\u4e86\u4e03\u53c8\u81ea\u8bf4\u9053\uff1a\u201c\u8fd9\u5f20\u5b57\u67ec\uff0c\u5c31\u662f\u7ed3\u5728\u6211\u548c\u6a80\u8001\u4e8c\u5934\u53d1 \u4e2d\u95f4\u7684\uff0c\u4e0b\u9762\u65e2\u6ca1\u6709\u5177\u540d\uff0c\u4e5f\u6ca1\u6709\u753b\u4e0a\u82b1\u62bc\u3002\u6211\u4eec\u60f3\u6765\u60f3\u53bb\uff0c\u4e5f\u4e0d\u77e5\u9053\u8fd9\u5b57\u67ec\u7a76\u7adf\u662f\u8c01\u5199 \u7684\uff1f\u201d \u3000\u3000\u591a\u81c2\u795e\u5251\u624b\u634b\u957f\u987b\uff0c\u5389\u58f0\u9053\uff1a\u201c\u8fd9\u7b97\u662f\u4ec0\u4e48\u4e1c\u897f\uff0c\u4e34\u5b89\u57ce\u662f\u4eba\u7686\u53ef\u6765\u5f97\uff0c\u8fd9\u53ae\u53c8\u51ed\u7740\u4ec0 \u4e48\uff0c\u80fd\u6559\u4f60\u4eec\u8d70\u3002\u201d \u3000\u3000\u4ed6\u51b7\u54fc\u4e00\u58f0\uff0c\u5de6\u638c\u63e1\u62f3\uff0c\u201c\u7830\u201d\u7684\u4e00\u58f0\u5728\u8eab\u65c1\u7684\u94c1\u7827\u4e0a\u731b\u51fb\u4e00\u4e0b\uff0c\u53c8\u9053\uff1a\u201c\u6211\u8001\u5934\u5b50\u5012 \u8981\u770b\u770b\u4ed6\u6709\u591a\u5927\u7684\u9053\u884c\uff0c\u80fd\u5728\u8fd9\u91cc\u6041\u5730\u5356\u72c2\u3002\u201d \u3000\u3000\u4e91\u4e2d\u7a0b\u4fa7\u773c\u671b\u53bb\uff0c\u53ea\u89c1\u4ed6\u7239\u7239\u76ee\u4e2d\u5a01\u5149\u5c3d\u9732\uff0c\u4e24\u9053\u5df2\u8fd1\u4e4e\u5168\u767d\u7684\u6d53\u7709\uff0c\u4e5f\u81ea\u659c\u659c\u626c\u8d77\uff0c \u5fc3\u4e2d\u6697\u53f9\u4e00\u58f0\uff0c\u77e5\u9053\u4ed6\u7239\u7239\u53c8\u5df2\u52a8\u4e86\u771f\u6012\u3002 \u3000\u3000\u5feb\u5200\u4e86\u4e03\u957f\u53f9\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u539f\u5148\u6211\u4e5f\u662f\u5982\u6b64\u60f3\u6cd5\uff0c\u5c31\u51ed\u6211\u4eec\u2018\u5feb\u5200\u4f1a\uff0c\u91cc\u7684\u5343\u767e\u4e2a\u5f1f \u5144\uff0c\u96be\u9053\u8fd8\u4f1a\u6015\u4e86\u8c01\uff1f\u662f\u4ee5\u6211\u4eec\u5f1f\u5144\u4e00\u5546\u8bae\uff0c\u90fd\u51b3\u5b9a\u4e0d\u7406\u4f1a\u8fd9\u6761\u7eb8\u793a\uff0c\u9759\u89c2\u5f85\u53d8\uff0c\u54ea\u77e5\u5230\u4e86 \u6628\u5929\u665a\u4e0a\uff0c\u5374\u51fa\u4e86\u4ef6\u602a\u4e8b\u3002\u201d\u4ed6\u773c\u524d\u53c8\u590d\u95ea\u8fc7\u65b9\u624d\u90a3\u79cd\u60ca\u6050\u7684\u795e\u60c5\u4e00\u4f38\u540e\u4e00\u6478\u5934\u9876\uff0c\u63a5\u7740\u8bf4 \u9053\uff1a\u201c\u6628\u5929\u665a\u4e0a\u6211\u4eec\u4e09\u5144\u5f1f\u53ef\u90fd\u6ca1\u6709\u7761\uff0c\u559d\u4e86\u70b9\u9152\uff0c\u5b88\u5728\u623f\u91cc\uff0c\u542c\u7740\u5916\u9762\u7684\u66f4\u9f13\uff0c\u4e00\u66f4\u3001\u4e8c \u66f4\u7684\u6572\u4e86\u8fc7\u53bb\uff0c\u4e09\u66f4\u4ee5\u540e\uff0c\u6211\u4eec\u5144\u5f1f\u90fd\u60f3\uff0c\u4eca\u5929\u665a\u4e0a\u5927\u6982\u4e0d\u4f1a\u51fa\u4ec0\u4e48\u4e8b\u4e86\uff0c\u6a80\u8001\u4e8c\u7b11\u7740\u7ad9\u4e86 \u8d77\u6765\uff0c\u8d70\u5230\u5916\u9762\u53bb\u89e3\u624b\u3002\u54ea\u77e5\u4ed6\u8fd9\u4e00\u53bb\uff0c\u7adf\u53bb\u4e86\u534a\u4e2a\u65f6\u8fb0\uff0c\u6211\u548c\u9f9a\u8001\u4e09\u672c\u6765\u8fd8\u5728\u7b11\u4ed6\uff0c\u5230\u540e \u6765\u53ef\u4e5f\u77e5\u9053\u4e8b\u60c5\u6709\u4e9b\u4e0d\u5bf9\u4e86\uff0c\u8dd1\u51fa\u53bb\u4e00\u770b\uff0c\u53ea\u89c1\u6a80\u8001\u4e8c\u5012\u5728\u5929\u4e95\u91cc\uff0c\u8eab\u4e0a\u4e00\u70b9\u4f24\u75d5\u90fd\u6ca1\u6709\uff0c \u5c31\u8fd9\u4e48\u4e0d\u660e\u4e0d\u767d\u7684\u6b7b\u4e86\uff0c\u6b7b\u524d\u8fde\u53eb\u90fd\u6ca1\u6709\u53eb\u51fa\u58f0\u6765\uff0c\u6708\u5149\u7167\u5728\u4ed6\u8138\u4e0a\uff0c\u4ed6\u773c\u775b\u7741\u5f97\u5927\u5927\u7684\uff0c \u597d\u50cf\u8fd8\u5728\u671b\u7740\u6211\u4eec\uff0c\u53eb\u6211\u4eec\u66ff\u4ed6\u62a5\u4ec7\u3002\u201d \u3000\u3000\u4e91\u4e2d\u7a0b\u4e00\u7d27\u624b\u638c\uff0c\u53ea\u89c9\u638c\u5fc3\u6e7f\u6e7f\u7684\uff0c\u4e0d\u77e5\u4f55\u65f6\u5df2\u6c81\u51fa\u4e86\u4e00\u624b\u51b7\u6c57\uff0c\u4fa7\u76ee\u671b\u53bb\uff0c\u4e91\u8c26\u624b\u634b \u957f\u987b\u6d53\u7709\u7d27\u76b1\uff0c\u6ee1\u5c4b\u7fa4\u8c6a\uff0c\u4e00\u4e2a\u4e2a\u90fd\u4f38\u51fa\u624b\u638c\uff0c\u4e0d\u4f4f\u7684\u62ed\u62b9\u7740\u989d\u4e0a\u7684\u6c57\u73e0\uff0c\u90a3\u795e\u5200\u9f9a\u5947\u77aa\u7740 \u4e00\u53cc\u5927\u773c\u775b\uff0c\u773c\u5185\u6ee1\u5e03\u8840\u4e1d\uff0c\u53ea\u6709\u7ad9\u5728\u4e00\u65c1\u7684\u5353\u957f\u537f\u795e\u8272\u4eff\u6d95\u6ca1\u6709\u4e00\u4e1d\u53d8\u5316\uff0c\u53ea\u662f\u51dd\u795e\u800c \u542c\uff0c\u6709\u65f6\u7528\u4ed6\u90a3\u7ec6\u957f\u7684\u624b\u6307\u8f7b\u6572\u81ea\u5df1\u7684\u624b\u80cc\uff0c\u4e0d\u77e5\u5728\u60f3\u7740\u4ec0\u4e48\u3002 \u3000\u3000\u591c\u8272\u66f4\u6df1\uff0c\u8ddd\u79bb\u7834\u6653\u4e5f\u66f4\u8fd1\u4e86\uff0c\u5feb\u5200\u4e01\u4e03\u957f\u53f9\u53c8\u9053\uff1a\u201c\u6211\u548c\u9f9a\u8001\u4e09\u5f53\u65f6\u90fd\u6115\u5728\u9662\u5b50\u91cc\uff0c \u53ea\u89c9\u5f97\u4e00\u9635\u9635\u7684\u5bd2\u610f\uff0c\u4ece\u80cc\u810a\u76f4\u5f80\u4e0a\u5192\uff0c\u62b1\u8d77\u6a80\u8001\u4e8c\u7684\u5c38\u8eab\uff0c\u8d70\u56de\u623f\u91cc\uff0c\u5374\u89c1\u5c4b\u91cc\u90a3\u5f20\u516b\u4ed9 \u684c\u4e0a\uff0c\u53c8\u591a\u4e86\u4e00\u5f20\u6de1\u9ec4\u7684\u5b57\u675f\uff0c\u4e0a\u9762\u6e05\u6e05\u695a\u695a\u5730\u5199\u7740\u5341\u516d\u4e2a\u5b57\uff1a\u2018\u660e\u591c\u4ee5\u524d\uff0c\u901f\u79bb\u4e34\u5b89\uff0c\u4e0d \u79bb\u4e34\u5b89\uff0c\u65e0\u75be\u5f52\u5929\u3002\u2019\u201c\u4e00\u9635\u98ce\u4ece\u95e8\u9699\u4e2d\u5439\u8fdb\u6765\uff0c\u5439\u5f97\u60ac\u5728\u5c4b\u9876\u7684\u6cb9\u706f\uff0c\u6765\u56de\u6643\u4e86\u4e24\u6643\uff0c\u5feb \u5200\u4e01\u4e03\u63a9\u4e0a\u655e\u5f00\u7684\u8863\u895f\uff0c\u63a5\u7740\u53c8\u9053\uff1a\u201c\u6211\u4e86\u8001\u4e03\u95ef\u8361\u6c5f\u6e56\u4e8c\u5341\u591a\u5e74\uff0c\u81ea\u5200\u5b50\u8fdb\uff0c\u7ea2\u5200\u5b50\u51fa\uff0c \u6709\u4eba\u5728\u6211\u8eab\u4e0a\u624e\u4e2a\u4e09\u5200\uff0c\u6211\u4e01\u8001\u4e03\u4e5f\u4e0d\u4f1a\u76b1\u4e00\u76b1\u7709\u5934\uff0c\u53ef\u662f\u90a3\u65f6\u5019\u4e0d\u7792\u5404\u4f4d\u8bf4\uff0c\u6211\u53ef\u771f\u6709\u70b9 \u80c6\u5bd2\uff0c\u6068\u4e0d\u5f97\u9a6c\u4e0a\u79bb\u5f00\u4e34\u5b89\uff0c\u518d\u597d\u7684\u70ed\u95f9\uff0c\u6211\u4e5f\u4e0d\u60f3\u770b\u4e86\u3002\u201d \u3000\u3000\u4ed6\u957f\u957f\u900f\u4e86\u53e3\u6c14\uff0c\u5c06\u8863\u895f\u4e0a\u7684\u6263\u5b50\u6263\u597d\uff0c\u4e00\u9762\u53c8\u9053\uff1a\u201c\u7b2c\u4e8c\u5929\u5929\u4e00\u4eae\uff0c\u6211\u5c31\u544a\u8bc9\u5f1f\u5144 \u4eec\uff0c\u4e58\u65e9\u6536\u62fe\u597d\u884c\u674e\uff0c\u56de\u5230\u592a\u884c\u5c71\u53bb\uff0c\u6211\u751a\u81f3\u60f3\u4ece\u6b64\u6d17\u624b\u4e0d\u5e72\u4e86\uff0c\u74e6\u7f50\u4e0d\u79bb\u4e95\u8fb9\u7834\uff0c\u5e72\u6211\u4eec \u8fd9\u4e00\u884c\u7684\uff0c\u6709\u51e0\u4e2a\u80fd\u6709\u597d\u6536\u573a\uff0c\u4f55\u51b5\u6211\u4eec\u592a\u884c\u4e09\u628a\u5200\u4ece\u6b64\u53ea\u5269\u4e24\u628a\uff0c\u522b\u8bf4\u62a5\u4ec7\uff0c\u8fde\u4ec7\u4eba\u662f\u8c01 \u6211\u4eec\u90fd\u4e0d\u77e5\u9053\uff0c\u8fd8\u6709\u4ec0\u4e48\u8138\u518d\u5728\u6c5f\u6e56\u4e0a\u9650\u4eba\u5bb6\u4e89\u5f3a\u6597\u80dc\u2014\u2014\u201d\u591a\u81c2\u795e\u5251\u5e72\u54b3\u4e00\u58f0\uff0c\u63a5\u53e3\u9053\uff1a \u201c\u660e\u67aa\u6613\u8eb2\uff0c\u6697\u7bad\u96be\u9632\uff0c\u8fd9\u53ae\u5982\u6b64\u884c\u4e3a\uff0c\u4e5f\u7b97\u4e0d\u5f97\u4ec0\u4e48\u597d\u6c49\u3002\u201d \u3000\u3000\u5feb\u5200\u4e01\u4e03\u957f\u53f9\u9053\uff1a\u201c\u4e91\u8001\u7237\u5b50\uff0c\u6d3b\u867d\u662f\u8fd9\u4e48\u8bf4\uff0c\u53ef\u662f\u2014\u2014\u5509\uff0c\u6a80\u8001\u4e8c\u5728\u6211\u4eec\u5f1f\u5144\u4e09\u4e2a\u91cc \u9762\uff0c\u624b\u628a\u5b50\u53ef\u662f\u6700\u786c\u7684\u4e00\u4e2a\uff0c\u80fd\u591f\u65e0\u58f0\u65e0\u606f\u5730\u5c31\u628a\u4ed6\u5236\u6b7b\u7684\u4eba\uff0c\u8fd9\u4efd\u8eab\u624b\uff0c\u53eb\u4eba\u5bb6\u60f3\u8d77\u6765\uff0c \u53ef\u771f\u6709\u70b9\u80c6\u5bd2\u3002\u5f53\u65f6\u6211\u662f\u5fc3\u7070\u5f97\u5f88\uff0c\u773c\u770b\u7740\u5f1f\u5144\u4eec\u4e00\u4e2a\u4e2a\u6536\u62fe\u597d\u884c\u674e\uff0c\u54ea\u77e5\u95e8\u5916\u7a81\u7136\u8d70\u8fdb\u4e24 \u4e2a\u7a7f\u7740\u9c9c\u7ea2\u8863\u888b\u7684\u5c0f\u59d1\u5a18\uff0c\u6ee1\u8138\u90fd\u662f\u7b11\u5bb9\uff0c\u4e00\u8d70\u8fdb\u6765\uff0c\u5c31\u671d\u6211\u4e00\u5f2f\u8170\uff0c\u95ee\u6211\u4e3a\u4ec0\u4e48\u4e0d\u4e0a\u5929\u76ee \u5c71\u5c31\u8981\u8d70\u4e86\u3002\u4f60\u60f3\u60f3\uff0c\u6211\u5802\u5802\u4e00\u4e2a\u7537\u5b50\u6c49\uff0c\u53c8\u600e\u80fd\u5728\u4e2a\u4e09\u628a\u68b3\u5934\u3001\u4e24\u622a\u7a7f\u8863\u7684\u5c0f\u59d1\u5a18\u9762\u524d\uff0c \u8bf4\u51fa\u4e22\u4eba\u7684\u8bdd\u6765\uff0c\u5c31\u542b\u542b\u7cca\u7cca\u6577\u884d\u4e86\u5979\u4eec\u4e24\u53e5\uff0c\u54ea\u77e5\u8fd9\u4e24\u4e2a\u5c0f\u59d1\u5a18\u5374\u5bf9\u6211\u8bf4\uff0c\u6211\u4eec\u5343\u4e07\u4e0d\u80fd \u8d70\uff0c\u4e0d\u4e0a\u5929\u76ee\u5c71\u5c31\u8d70\uff0c\u5c31\u7b97\u662f\u770b\u4e0d\u8d77\u5979\u4eec\u7684\u4e3b\u4eba\u3002\u201d \u3000\u3000\u4e91\u8c26\u7236\u5b50\u5bf9\u671b\u4e00\u773c\uff0c\u77e5\u9053\u8fd9\u5feb\u5200\u4e01\u4e03\u53e3\u4e2d\u7684\u4e24\u4e2a\u7ea2\u88f3\u5c11\u5973\uff0c\u5fc5\u5b9a\u5c31\u662f\u81ea\u5df1\u5bff\u8bde\u4e4b\u65e5\u6765\u795d \u5bff\u7684\u4e24\u4e2a\u5c11\u5973\u4e86\u3002\u4e91\u4e2d\u7a0b\u60f3\u5230\u81ea\u5df1\u65b9\u624d\u5728\u706b\u5b85\u8fb9\u770b\u89c1\u8fd9\u4e24\u4e2a\u5c11\u5973\u7684\u60c5\u5f62\uff0c\u5fc3\u4e2d\u7a81\u7136\u4e00\u52a8\uff0c\u5374 \u542c\u90a3\u4e01\u4e03\u5df2\u63a5\u7740\u9053\uff1a\u201c\u6211\u5fc3\u91cc\u6b63\u6709\u6c14\uff0c\u54ea\u91cc\u6709\u7a7a\u548c\u8fd9\u4e24\u4e2a\u5c0f\u59d1\u5a18\u55dc\u7d20\uff0c\u5c31\u6c89\u7740\u8138\u9053\uff1a\u2018\u975e\u8d70 \u4e0d\u53ef\u3002\u2019\u8fd9\u4e24\u4e2a\u5c0f\u59d1\u5a18\u5374\u5a07\u6ef4\u6ef4\u7684\u4e00\u7b11\uff0c\u8885\u8885\u5a1c\u5a1c\u7684\u8d70\u4e86\u8fc7\u6765\uff0c\u7a81\u7136\u4e00\u4f38\u624b\uff0c\u4e0d\u77e5\u600e\u4e48\uff0c\u6211 \u5c31\u88ab\u5979\u4eec\u5f04\u4e86\u4e2a\u5927\u7b4b\u6597\u3002\u201c\u5353\u957f\u537f\u6697\u4e2d\u4e00\u7b11\uff0c\u5fd6\u9053\uff1a\u201c\u8fd9\u5feb\u5200\u4e01\u516d\u679c\u7136\u662f\u6761\u6027\u60c5\u723d\u76f4\u7684\u6c49 \u5b50\uff0c\u628a\u81ea\u5df1\u4e22\u4eba\u7684\u4e8b\uff0c\u90fd\u6beb\u4e0d\u4fdd\u7559\u7684\u8bf4\u51fa\u53bb\uff0c\u5c31\u51ed\u8fd9\u4efd\u52c7\u6c14\uff0c\u5c31\u65e0\u602a\u4ed6\u80fd\u7edf\u7387\u7fa4\u96c4\uff0c\u521b\u7acb\u51fa \u5feb\u5200\u4f1a\u6765\u3002\u201d \u3000\u3000\u4e00\u5ff5\u81f3\u6b64\uff0c\u4e0d\u7981\u5bf9\u4ed6\u591a\u770b\u4e86\u4e24\u773c\uff0c\u53ea\u770b\u4ed6\u644a\u5f00\u4e00\u53cc\u94c1\u638c\uff0c\u4e00\u9762\u6bd4\u7740\u624b\u5f0f\uff0c\u4e00\u9762\u53c8\u9053\uff1a \u201c\u6211\u90a3\u65f6\u65e2\u60ca\u53c8\u6012\uff0c\u7ffb\u8d77\u8eab\u6765\uff0c\u5374\u89c1\u9f9a\u8001\u4e09\u5df2\u548c\u5979\u4eec\u52a8\u4e0a\u4e86\u624b\uff0c\u4e5f\u662f\u4e0d\u51fa\u4e09\u4e2a\u7167\u9762\uff0c\u5c31\u88ab\u5979 \u4eec\u5176\u4e2d\u4e00\u4eba\u6253\u4e86\u4e2a\u7b4b\u6597\u3002\u201d \u3000\u3000\u5f53\u65f6\u6211\u4eec\u90fd\u5728\u4e07\u5b89\u8001\u5ba2\u897f\u8de8\u9662\u7684\u4e00\u95f4\u5ba2\u5385\u91cc\uff0c\u5ba2\u5385\u91cc\u4e00\u5171\u6709\u5341\u591a\u4e2a\u5feb\u5200\u4f1a\u7684\u5f1f\u5144\uff0c\u800c\u4e14 \u90fd\u662f\u597d\u624b\uff0c\u53ef\u662f\u6211\u4eec\u8fd9\u5341\u591a\u4e2a\u7537\u5b50\u6c49\uff0c\u5374\u88ab\u90a3\u4e24\u4e2a\u770b\u4e0a\u53bb\u5f31\u4e0d\u7981\u98ce\u7684\u5c0f\u59d1\u5a18\u6253\u4e86\u4e2a\u4e0d\u4ea6\u4e50 \u4e4e\uff0c\u5230\u540e\u6765\u6211\u4eec\u7ade\u90fd\u88ab\u4ed6\u4eec\u70b9\u4e86\u7a74\u9053\uff0c\u8eba\u5728\u5730\u4e0a\uff0c\u8fde\u52a8\u90fd\u52a8\u4e0d\u4e86\u4e00\u4e00\u4e0b\uff0c\u5509\uff0c\u5f53\u65f6\u6211\u771f\u6068\u4e0d \u5f97\u6b7b\u4e86\u7b97\u4e86\u3002\u6211\u4e86\u8001\u4e03\u51fa\u5165\u5200\u5c71\u5251\u6d77\u4e5f\u4e0d\u77e5\u591a\u5c11\u6b21\u4e86\uff0c\u53ef\u8fd8\u6ca1\u6709\u683d\u8fc7\u8fd9\u79cd\u7b4b\u6597\u3002\u201c\u4ed6\u53cc\u638c\u201d \u556a\u201c\u7684\u4e92\u51fb\u4e00\u4e0b\uff0c\u53c8\u9053\uff1a\u201c\u53ea\u542c\u8fd9\u4e24\u4e2a\u5c0f\u59d1\u5a18\uff0c\u7b11\u563b\u563b\u5730\u5bf9\u6211\u4eec\u8bf4\u9053\uff1a\u2018\u6765\u5230\u4e34\u5b89\u7684\u4eba\uff0c\u8981 \u662f\u4e0d\u4e0a\u5929\u76ee\u5c71\u53bb\u89c1\u5979\u4eec\u7684\u4e3b\u4eba\uff0c\u8c01\u4e5f\u4e0d\u80fd\u8d70\uff0c\u8c01\u8981\u662f\u60f3\u8d70\uff0c\u9664\u975e\u662f\u54bd\u4e86\u6c14\uff0c\u624d\u80fd\u51fa\u5f97\u4e86\u4e34\u5b89 \u57ce\u3002\u2019\u8bf4\u7740\uff0c\u5979\u4eec\u8eab\u5b50\u4e00\u52a8\uff0c\u6211\u53ea\u89c9\u773c\u524d\u4e00\u82b1\uff0c\u7a74\u9053\u88ab\u89e3\u5f00\u4e86\uff0c\u62ac\u773c\u4e00\u671b\uff0c\u53ea\u662f\u5979\u4eec\u7684\u80cc \u5f71\uff0c\u5df2\u7f13\u7f13\u8d70\u51fa\u4e86\u897f\u8de8\u9662\u7684\u95e8\u3002\u201c\u591a\u81c2\u795e\u5251\u957f\u53f9\u4e00\u58f0\uff0c\u4ed6\u4eb2\u773c\u89c1\u8fc7\u90a3\u4e24\u4e2a\u7ea2\u88f3\u5c11\u5973\u7684\u6b66\u529f\uff0c \u6b64\u523b\u518d\u4e5f\u8bf4\u4e0d\u51fa\u4ec0\u4e48\u8bdd\u6765\u3002\u5353\u957f\u537f\u76ee\u5149\u70af\u7136\uff0c\u50cf\u662f\u60f3\u95ee\u4ec0\u4e48\u8bdd\uff0c\u5374\u53c8\u5fcd\u4f4f\u4e86\u3002\u5feb\u5200\u4e86\u4e03\u624b\u638c \u4e00\u644a\uff0c\u957f\u53f9\u9053\uff1a\u201c\u4e91\u8001\u7237\u5b50\uff0c\u60a8\u8bf4\uff0c\u6211\u8be5\u600e\u4e48\u529e\uff0c\u8d70\u53c8\u4e0d\u884c\uff0c\u4e0d\u8d70\u53c8\u4e0d\u884c\uff0c\u524d\u6709\u72fc\u540e\u6709\u864e\uff0c \u6211\u548c\u9f9a\u8001\u4e09\u4e00\u60f3\uff0c\u53ea\u6709\u62fc\u4e86\u3002\u4f46\u662f\u2014\u2014\u201d\u4ed6\u76ee\u5149\u53c8\u590d\u53d8\u5f97\u5341\u5206\u9eef\u6de1\uff0c\u63a5\u7740\u9053\uff1a\u201c\u521a\u624d\u96c1\u8361\u7ea2 \u5dfe\u4f1a\u90a3\u6863\u5b50\u4e8b\uff0c\u4e91\u8001\u7237\u5b50\u60f3\u5fc5\u4e5f\u77e5\u9053\uff0c\u6211\u4eec\u548c\u4ed6\u4eec\u867d\u7136\u4ece\u4e0d\u5f80\u6765\uff0c\u4e5f\u4e0d\u77e5\u9053\u4ed6\u4eec\u7a76\u7adf\u4e3a\u4ec0\u4e48 \u906d\u7684\u6b83\uff0c\u4f46\u6211\u548c\u9f9a\u8001\u4e09\u5fc3\u91cc\u4e00\u7422\u78e8\uff0c\u5c31\u731c\u51fa\u4ed6\u4eec\u5927\u65e2\u4e5f\u548c\u6211\u4eec\u4e00\u6837\u3002\u201d \u3000\u3000\u201c\u672c\u6765\u6211\u548c\u9f9a\u8001\u4e09\u60f3\uff0c\u6700\u591a\u6211\u4eec\u4e24\u4e2a\u6b7b\u4e86\u7b97\u4e86\uff0c\u73b0\u5728\u4e00\u770b\uff0c\u624d\u77e5\u9053\u4e8b\u60c5\u4e0d\u90a3\u4e48\u7b80\u5355\uff0c\u90a3 \u5bb6\u4f19\u53ef\u771f\u662f\u8d76\u5c3d\u6740\u7edd\uff0c\u6211\u4e86\u8001\u4e03\u6b7b\u867d\u4e0d\u8db3\u60dc\uff0c\u53ef\u662f\u8981\u6211\u7d2f\u53ca\u8fd9\u4e48\u591a\u5f1f\u5144\u4e5f\u4e00\u8d77\u906d\u6b83\uff0c\u90a3\u6211\u4e86 \u8001\u4e03\u53ef\u4e0d\u80fd\u5c31\u8fd9\u4e48\u675f\u624b\u5c31\u7f1a\uff0c\u597d\u6b79\u4e5f\u5f97\u62fc\u4e0a\u4e00\u4e0b\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u6697\u4e2d\u70b9\u5934\uff0c\u53ea\u89c1\u8fd9\u4e2a\u8349\u83bd\u8c6a\u58eb\u80f8\u819b\u4e00\u633a\uff0c\u795e\u60c5\u4e2d\u4eff\u4f5b\u53c8\u6062\u590d\u4e86\u4ed6\u90a3\u60ef\u6709\u7684\u527d\u608d\u4e4b \u6c14\uff0c\u76ee\u5149\u4e00\u8f6c\uff0c\u63a5\u7740\u53c8\u9053\uff1a\u201c\u662f\u4ee5\u6211\u5c31\u5c06\u5f1f\u5144\u4eec\u90e8\u53ec\u96c6\u8d77\u6765\uff0c\u805a\u5728\u8857\u4e0a\uff0c\u770b\u770b\u90a3\u4e9b\u4eba\u5230\u5e95\u6709 \u4ec0\u4e48\u6cd5\u5b50\uff0c\u80fd\u6559\u6211\u4eec\u7094\u5200\u4f1a\u8fd9\u4e24\u767e\u4e2a\u5f1f\u5144\u4e00\u8d77\u6b7b\u53bb\u3002\u201d \u3000\u3000\u4ed6\u8138\u4e0a\u52c9\u5f3a\u6cdb\u8d77\u4e86\u4e00\u4e1d\u7b11\u5bb9\uff0c\u4f38\u4e86\u94c1\u638c\uff0c\u56db\u4e0b\u4e00\u6307\uff0c\u63a5\u7740\u53c8\u9053\uff1a\u201c\u4f55\u51b5\uff0c\u6211\u4e01\u8001\u4e03\u8fd8\u6709 \u8fd9\u4e48\u591a\u670b\u53cb\uff0c\u73b0\u5728\u53c8\u627f\u8499\u4f60\u4e91\u8001\u7237\u5b50\u548c\u4e91\u5927\u4fa0\u62d4\u5200\u76f8\u52a9\uff0c\u8fd9\u66f4\u7ed9\u4e86\u6211\u4e01\u8001\u4e03\u4e0d\u5c11\u52c7\u6c14\u3002\u201d \u3000\u3000\u591a\u81c2\u795e\u5251\u6c89\u91cd\u5730\u53f9\u606f\u4e00\u58f0\uff0c\u671b\u4e86\u671b\u95e8\u9699\u5916\u7684\u5929\u8272\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u6b64\u523b\u5929\u5df2\u5feb\u4eae\u4e86\uff0c\u5927\u6982\u2014 \u2014\u201d\u8bed\u72b9\u672a\u4e86\uff0c\u95e8\u5916\u7a81\u7136\u4f20\u6765\u4e00\u58f0\u60e8\u547c\uff0c\u5c4b\u5185\u7fa4\u8c6a\u9762\u5bb9\u4ff1\u53d8\u3002 \u3000\u3000\u5feb\u5200\u4e01\u4e03\u4e00\u4e2a\u7bad\u6b65\u7a9c\u5230\u95e8\u53e3\uff0c\u53cc\u638c\u731b\u7136\u5f80\u5916\u4e00\u6325\uff0c\u201c\u7830\u201d\u7684\u4e00\u58f0\uff0c\u7adf\u786c\u751f\u751f\u5c06\u90a3\u4e24\u7247\u6728 \u677f\u5927\u95e8\u51fb\u5f97\u76f4\u98de\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u4ed6\u4e00\u63a0\u800c\u51fa\u95e8\u5916\uff0c\u76ee\u5149\u56db\u4e0b\u4e00\u626b\uff0c\u53ea\u89c9\u95e8\u5916\u7684\u4e00\u6392\u5feb\u5200\u5927\u6c49\uff0c\u8eab\u5f62\u4ecd\u7136\u7ad9\u5f97\u7b14\u76f4\uff0c\u819d\u9647 \u591c\u8272\u4e4b\u4e2d\uff0c\u5374\u89c1\u4ed6\u4eec\u9762\u4e0a\u5df2\u5404\u4e2a\u9732\u51fa\u60ca\u60e7\u4e4b\u8272\u3002 \u3000\u3000\u8857\u7684\u90a3\u5934\uff0c\u961f\u5f62\u5df2\u51cc\u4e71\uff0c\u5200\u5149\u6b64\u8d77\u5f7c\u843d\uff0c\u4f46\u7b14\u76f4\u7684\u4e00\u6761\u8857\u4e0a\uff0c\u9664\u4e86\u4ed6\u81ea\u5df1\u5feb\u5200\u4f1a\u7684\u5f1f\u5144 \u5916\uff0c\u5374\u770b\u4e0d\u5230\u522b\u7684\u4eba\u5f71\u3002 \u3000\u3000\u4ed6\u8eab\u5f62\u4e00\u6298\uff0c\u98de\u4e5f\u4f3c\u7684\u671d\u90a3\u5934\u7a9c\u4e86\u8fc7\u53bb\uff0c\u8033\u7554\u4f46\u89c9\u60e8\u547c\u4e4b\u58f0\u4e0d\u7edd\u4e8e\u8033\uff0c\u624b\u6301\u957f\u5200\u7684\u5927 \u6c49\uff0c\u4e00\u4e2a\u4e2a\u5730\u5012\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u4f46\u56db\u4e0b\u4ecd\u7136\u4e0d\u89c1\u4eba\u5f71\uff0c\u90bb\u5c45\u7684\u5927\u95e8\u672c\u6765\u5f00\u4e86\u4e00\u7ebf\uff0c\u6b64\u65f6\u53c8\u201c\u7830\u201d\u7684\u5173\u4e0a\u4e86\uff0c\u663e\u89c1\u5f97\u95e8\u91cc \u4eba\u4f46\u6c42\u81ea\u4fdd\uff0c\u8c01\u4e5f\u4e0d\u60f3\u8e09\u8fd9\u8d9f\u6d51\u6c34\u3002 \u3000\u3000\u795e\u5200\u9f9a\u5947\u76ee\u5149\u4e00\u626b\uff0c\u4e00\u62e7\u8eab\uff0c\u201c\u55d6\u201d\u7684\u7a9c\u4e0a\u4e86\u5c4b\u9762\uff0c\u4e91\u6c0f\u7236\u5b50\u8eab\u5f62\u5982\u98de\uff0c\u63a0\u5230\u5feb\u5200\u4e01\u4e03 \u8eab\u4fa7\uff0c\u4e00\u9762\u56db\u4e0b\u67e5\u770b\uff0c\u4e00\u9762\u68c0\u67e5\u7740\u5df2\u7ecf\u5012\u5728\u5730\u4e0a\u7684\u7094\u5200\u4f1a\u4f17\u7684\u4f24\u52bf\u3002 \u3000\u3000\u53ea\u89c1\u8fd9\u4e9b\u5927\u6c49\u7684\u80f8\u524d\uff0c\u90fd\u6709\u4e2a\u94b1\u773c\u5927\u7684\u4f24\u53e3\uff0c\u6c69\u6c69\u7684\u5f80\u5916\u5192\u7740\u9c9c\u8840\uff0c\u663e\u89c1\u90fd\u662f\u4e2d\u4e86\u6697 \u5668\uff0c\u4f46\u8fd9\u4e9b\u6697\u5668\u662f\u4ec0\u4e48\uff1f\u4ece\u54ea\u91cc\u53d1\u51fa\u6765\u7684\uff1f\u5374\u6ca1\u6709\u4e00\u4e2a\u4eba\u770b\u5230\u3002\u4e91\u4e2d\u7a0b\u624b\u8155\u4e00\u53cd\uff0c\u5c06\u8170\u95f4\u7684 \u9f99\u7eb9\u8f6f\u5251\uff0c\u6492\u5230\u624b\u4e0a\uff0c\u8eab\u5f62\u63a9\u5728\u4ed6\u7239\u7239\u8eab\u65c1\uff0c\u76ee\u5149\u95ea\u7535\u822c\u56db\u626b\uff0c\u53ea\u89c1\u8fd9\u4e9b\u5927\u6c49\u4ecd\u7136\u4e0d\u4f4f\u5730\u4e00 \u4e2a\u4e2a\u5012\u4e0b\u53bb\uff0c\u4f46\u53d1\u6697\u5668\u7684\u4eba\u5728\u54ea\u91cc\uff0c\u4ed6\u7eb5\u7136\u7528\u5c3d\u76ee\u529b\uff0c\u5374\u8fde\u4e00\u4e2a\u65b9\u5411\u90fd\u8fa8\u4e0d\u51fa\u3002 \u3000\u3000\u4ed6\u4e0d\u7531\u81ea\u4e3b\u5730\u4ece\u5fc3\u5e95\u751f\u51fa\u4e00\u822c\u5bd2\u6c14\uff0c\u5feb\u5200\u4e01\u4e03\u5df2\u53cc\u76ee\u5c3d\u8d64\uff0c\u624b\u4e2d\u5200\u5149\u8fde\u95ea\uff0c\u75af\u4e86\u4f3c\u7684\u56db \u4e0b\u98de\u63a0\u7740\uff0c\u624b\u4e2d\u7684\u5200\u5149\u6709\u5982\u4e00\u56e2\u745e\u96ea\uff0c\u62a4\u5728\u8eab\u5f62\u56db\u4fa7\uff0c\u53ea\u662f\u81ea\u5df1\u7684\u5f1f\u5144\u80cc\u5bf9\u7740\u80cc\u5730\u6325\u821e\u7740\u624b \u4e2d\u957f\u5200\uff0c\u4f46\u90a3\u4e9b\u4f3c\u4e4e\u65e0\u5f71\u800c\u6765\u7684\u6697\u5668\uff0c\u597d\u4f3c\u662f\u957f\u4e86\u773c\u775b\uff0c\u7adf\u80fd\u4ece\u5200\u5149\u4e2d\u7a7f\u8fc7\u53bb\uff0c\u65e0\u58f0\u65e0\u606f\u5730 \u6253\u5728\u4eba\u8eab\u4e0a\u3002 \u3000\u3000\u6ee1\u8857\u5200\u5149\u80dc\u96ea\uff0c\u60e8\u547c\u8fde\u8fde\uff0c\u4f46\u90a3\u4e9b\u7094\u5200\u5927\u4ec5\uff0c\u4ecd\u7136\u80cc\u80cc\u76f8\u62b5\uff0c\u7acb\u5728\u8857\u5fc3\uff0c\u7ade\u6ca1\u6709\u4e00\u4e2a\u56db \u6563\u5954\u9003\u7684\uff0c\u5353\u957f\u537f\u6697\u4e2d\u8d5e\u4f69\u8fd9\u5feb\u5200\u4f1a\u7eaa\u5f8b\u7684\u7cbe\u4e25\uff0c\u7a81\u5730\u98de\u8eab\u4e00\u63a0\uff0c\u6025\u5982\u7535\u95ea\uff0c\u63a0\u5728\u4e00\u4e2a\u5feb\u5200 \u5927\u6c49\u7684\u8eab\u524d\uff0c\u500f\u7136\u4f38\u624b\u4e00\u6284\uff0c\u76ee\u5149\u5982\u7535\uff0c\u56db\u4e0b\u4e00\u626b\uff0c\u53c8\u500f\u7136\u9000\u56de\u8857\u9996\uff0c\u644a\u5f00\u624b\u638c\u4e00\u770b\uff0c\u53ea\u89c1 \u4e00\u4e2a\u5c0f\u82e5\u868a\u8681\u7684\u9ed1\u8272\u94c1\u4e38\uff0c\u7a81\u7136\u4ece\u638c\u5fc3\u5f39\u4e86\u5f00\u6765\uff0c\u56db\u4fa7\u5f39\u51fa\u516b\u6839\u8292\u523a\u3002 \u3000\u3000\u4ed6\u867d\u662f\u521d\u5165\u6c5f\u6e56\uff0c\u4f46\u5341\u5e74\u6614\u7ec3\uff0c\u5374\u4f7f\u4ed6\u6210\u4e86\u5929\u4e0b\u5404\u95e8\u6d3e\u6b66\u529f\u7684\u5927\u884c\u5bb6\uff0c\u662f\u4ee5\u90a3\u5ddd\u4e2d\u6768\u4e00 \u5251\u7a0d\u4e00\u51fa\u624b\uff0c\u4ed6\u4fbf\u77e5\u9053\u90a3\u662f\u5ce8\u5d4b\u95e8\u4e0b\u3002 \u3000\u3000\u4f46\u6b64\u523b\u4ed6\u5374\u53c8\u4e0d\u7981\u6697\u4e2d\u4e00\u76b1\u5251\u7709\uff0c\u7eb5\u7136\u4ed6\u641c\u904d\u8bb0\u5fc6\uff0c\u53ef\u4e5f\u60f3\u4e0d\u51fa\u6b64\u523b\u5728\u4ed6\u638c\u5fc3\u8fd9\u6697\u5668\u7684 \u6765\u8def\uff0c\u800c\u8fd9\u6697\u5668\u7684\u5236\u4f5c\u4e4b\u7cbe\u5de7\uff0c\u5a01\u529b\u4e4b\u9738\u9053\uff0c\u5374\u4e0d\u7981\u4ee4\u4ed6\u5fc3\u4e2d\u751f\u51fa\u4e00\u4e1d\u5bd2\u610f\u3002 \u3000\u3000\u6b64\u523b\u6708\u5149\u5df2\u6c89\uff0c\u5929\u5374\u4ecd\u672a\u7834\u6653\uff0c\u5927\u5730\u6b63\u662f\u65e5\u5b50\u6700\u9edd\u9ed1\u7684\u65f6\u5019\uff0c\u8fd9\u79cd\u7ec6\u5c0f\u7684\u6697\u5668\uff0c\u901a\u4f53\u9edd \u9ed1\uff0c\u591c\u8272\u4e2d\u76ee\u529b\u81ea\u96be\u5206\u8fa8\uff0c\u518d\u52a0\u4e0a\u5c0f\u800c\u6d51\u5706\uff0c\u7834\u51e4\u4e4b\u58f0\uff0c\u53ef\u8bf4\u8f7b\u5fae\u5230\u6781\u5904\uff0c\u82e5\u4e0d\u662f\u4ed6\u8fd9\u79cd\u6709 \u7740\u975e\u51e1\u548c\u8d85\u4eba\u7684\u542c\u89c9\u7684\u9ad8\u624b\uff0c\u81ea\u7136\u96be\u4ee5\u89c9\u5bdf\uff0c\u4f46\u53ef\u6015\u7684\u662f\u8fd9\u79cd\u6697\u5668\u4e00\u63a5\u89e6\u5230\u4eba\u8eab\u4e0a\uff0c\u7acb\u523b\u4fbf \u4f1a\u5f39\u51fa\u8292\u523a\uff0c\u8fd9\u5c0f\u5c0f\u4e00\xb7\u7c92\u6697\u5668\uff0c\u7eb5\u662f\u94c1\u6c49\uff0c\u53ef\u4e5f\u7ecf\u53d7\u4e0d\u4f4f\u3002 \u3000\u3000\u8fd9\u6761\u5927\u8857\u7b14\u76f4\u800c\u957f\uff0c\u4e24\u65c1\u7684\u5e97\u94fa\u5374\u7d27\u7d27\u5730\u95ed\u7740\u95e8\uff0c\u90a3\u5feb\u5200\u4e01\u4e03\u672c\u4ee5\u4e3a\u81ea\u5df1\u4eba\u591a\uff0c\u82e5\u662f\u90fd \u56f4\u5728\u4e00\u95f4\u623f\u91cc\uff0c\u7a81\u7136\u53d7\u5230\u88ad\u51fb\u65f6\uff0c\u4fbf\u4f1a\u7f1a\u624b\u7f1a\u811a\uff0c\u65bd\u5c55\u4e0d\u5f00\u3002 \u3000\u3000\u662f\u4ee5\u4ed6\u624d\u5c06\u81ea\u5df1\u7684\u5f1f\u5144\u90fd\u805a\u5728\u8857\u4e0a\uff0c\u4f46\u6b64\u523b\u8fd9\u4e9b\u5feb\u5200\u4f1a\u4f17\u4eba\uff0c\u805a\u5728\u8fd9\u6761\u8857\u4e0a\uff0c\u5374\u6210\u4e86\u4eba \u5bb6\u6697\u5668\u7684\u6d3b\u9776\u5b50\uff0c\u8fde\u9003\u90fd\u9003\u4e0d\u4e86\uff0c\u8eb2\u4e5f\u65e0\u6cd5\u8eb2\uff0c\u5feb\u5200\u4e01\u4e03\u867d\u7136\u540e\u6094\uff0c\u5374\u5df2\u6765\u4e0d\u53ca\u4e86\u3002 \u3000\u3000\u6ee1\u8857\u95ea\u70c1\u7684\u5200\u5149\uff0c\u6b64\u523b\u7adf\u5df2\u5012\u4e86\u51e0\u8fd1\u4e00\u534a\uff0c\u4ec1\u4e49\u5251\u5ba2\u5fc3\u91cc\u8d8a\u6765\u8d8a\u5bd2\uff0c\u5927\u559d\u4e00\u58f0\uff0c\u5251\u5149\u66b4 \u957f\uff0c\u4e00\u9053\u9752\u84dd\u5251\u5149\uff0c\u50cf\u5339\u7ec3\u822c\u98de\u821e\u5728\u4ed6\u81ea\u5df1\u7684\u8eab\u4fa7\uff0c\u501f\u4ee5\u9632\u62a4\u90a3\u4e9b\u4f3c\u4e4e\u65e0\u5f71\u800c\u6765\u7684\u6697\u5668\u3002 \u3000\u3000\u5feb\u5200\u4e01\u4e03\u4e00\u9762\u6325\u821e\u7740\u5200\u5149\uff0c\u5c55\u52a8\u7740\u8eab\u5f62\uff0c\u56db\u4e0b\u67e5\u770b\uff0c\u4e00\u9762\u5389\u58f0\u53f1\u9053\uff1a\u201c\u662f\u597d\u670b\u53cb\u5c31\u73b0\u51fa \u8eab\u6765\uff0c\u9762\u5bf9\u9762\u548c\u6211\u4e01\u8001\u4e03\u5343\u4e00\u573a\uff0c\u8981\u662f\u518d\u8fd9\u4e48\u5077\u5077\u6478\u6478\uff0c\u6211\u4e01\u8001\u516d\u53ef\u8981\u8fde\u7956\u5b97\u516b\u4ee3\u90fd\u9a82\u4e0a \u4e86\u3002\u201d \u3000\u3000\u4f46\u4ed6\u7a7a\u81ea\u53f1\u9a82\uff0c\u56db\u4e0b\u5374\u8fde\u534a\u58f0\u56de\u5e94\u90fd\u6ca1\u6709\uff0c\u7ad9\u5728\u8857\u5fc3\u7684\u5927\u6c49\u4eec\uff0c\u7ec8\u4e8e\u5fcd\u53d7\u4e0d\u4f4f\u5fc3\u91cc\u7684\u6050 \u60e7\uff0c\u54d7\u7136\u4e00\u58f0\uff0c\u56db\u4e0b\u9003\u4e86\u5f00\u53bb\u3002 \u3000\u3000\u4f46\u8fd9\u5374\u66f4\u52a0\u901f\u4e86\u4ed6\u4eec\u7684\u6b7b\u4ea1\uff0c\u6df7\u4e71\u7684\u8857\u4e0a\uff0c\u53ea\u6709\u5353\u957f\u537f\u4e00\u4eba\u662f\u51b7\u9759\u7684\uff0c\u4ed6\u76ee\u5149\u5982\u7535\uff0c\u56db \u4e0b\u641c\u7d22\u7740\uff0c\u53ea\u89c1\u8fd9\u4e9b\u6697\u5668\uff0c\u751f\u751f\u50cf\u662f\u4ece\u56db\u9762\u516b\u65b9\u5c04\u6765\uff0c\u4f46\u4ed6\u5374\u4e0d\u80fd\u627e\u51fa\u5b83\u4eec\u51c6\u786e\u7684\u65b9\u5411\u3002 \u3000\u3000\u81ea\u53e4\u4ee5\u6765\uff0c\u6b66\u6797\u4e4b\u4e2d\u4ece\u672a\u6709\u5982\u6b64\u51b7\u9177\u7684\u5c60\u6740\uff0c\u4e5f\u4ece\u672a\u6709\u8fc7\u5982\u6b64\u9738\u9053\u7684\u6697\u5668\uff0c\u987b\u77e5\u8fd9\u79cd\u6697 \u5668\uff0c\u53ea\u8981\u5236\u4e0a\u4e00\u7c92\uff0c\u5df2\u4e0d\u77e5\u8981\u82b1\u53bb\u591a\u5c11\u4eba\u529b\uff0c\u6b64\u523b\u8fd9\u6f2b\u5929\u5c04\u6765\u7684\uff0c\u771f\u4e0d\u77e5\u662f\u5982\u4f55\u9020\u51fa\u6765\u7684\u3002 \u3000\u3000\u7a81\u7136\u2014\u2014 \u3000\u3000\u5353\u957f\u537f\u6e05\u5578\u4e00\u8eab\uff0c\u8eab\u5f62\u5b9b\u5982\u9f99\u5347\u4e5d\u5929\uff0c\u5e73\u5730\u62e8\u4e86\u4e0a\u53bb\uff0c\u51cc\u7a7a\u4e00\u4e2a\u8f6c\u6298\uff0c\u7adf\u5728\u7a7a\u4e2d\u6a2a\u79fb\u4e09 \u5c3a\uff0c\u7136\u540e\u6709\u5982\u96f7\u51fb\u7535\u95ea\uff0c\u500f\u7136\u98de\u5411\u8857\u4fa7\uff0c\u4e00\u5bb6\u5e97\u94fa\u5c4b\u6a90\u4e0b\u7684\u9634\u5f71\uff0c\u626c\u624b\u4e00\u638c\u2014\u2014\u4e00\u80a1\u6fc0\u70c8\u7684 \u638c\u98ce\uff0c\u6392\u5c71\u5012\u6d77\u822c\u5411\u90a3\u8fb9\u51fb\u53bb\uff0c\u53ea\u542c\u8f70\u7136\u4e00\u58f0\uff0c\u8fd9\u5bb6\u5e97\u94fa\u4f38\u51fa\u5916\u9762\u7684\u5c4b\u6a90\uff0c\u7acb\u523b\u968f\u4e4b\u5012\u584c\uff0c \u843d\u4e0b\u65e0\u6570\u6728\u77f3\uff0c\u626c\u8d77\u6f2b\u5929\u7070\u5c18\u3002 \u3000\u3000\u5353\u957f\u537f\u7684\u8eab\u5f62\uff0c\u4e5f\u968f\u5373\u63a0\u4e86\u8fc7\u53bb\uff0c\u70df\u5c18\u6f2b\u5929\u4e4b\u4e2d\uff0c\u7a81\u7136\u659c\u659c\u63a0\u8d77\u4e00\u6761\u4eba\u5f71\uff0c\u8eab\u5f62\u4e4b\u5feb\uff0c \u7adf\u975e\u4eba\u7c7b\u76ee\u529b\u80fd\u53ca\uff0c\u5c31\u5728\u5353\u957f\u537f\u8eab\u5f62\u5230\u8fbe\u7684\u4e00\u5239\u90a3\uff0c\u4ed6\u5df2\u4ece\u53e6\u4e00\u4e2a\u65b9\u5411\uff0c\u7535\u4e5f\u4f3c\u7684\u63a0\u4e86\u5f00 \u53bb\u3002 \u3000\u3000\u6709\u5f88\u591a\u5feb\u5982\u7535\u5149\u77f3\u706b\u822c\u7684\u4e8b\uff0c\u5728\u7b14\u4e0b\u5199\u6765\uff0c\u4fbf\u751f\u50cf\u662f\u6781\u6162\uff0c\u6b64\u523b\u4e5f\u6b63\u662f\u5982\u6b64\u60c5\u5f62\uff0c\u5353\u957f \u537f\u8eab\u5f62\u65b9\u4e00\u63a0\u800c\u81f3\uff0c\u811a\u5c16\u5fae\u70b9\u6b8b\u8d25\u7684\u5c4b\u6a90\uff0c\u4fbf\u53c8\u50cf\u7bad\u4e5f\u4f3c\u7684\u5c04\u4e86\u51fa\u53bb\uff0c\u5982\u5f71\u9644\u5f62\u822c\u8ffd\u5411\u90a3\u6761 \u4eba\u5f71\u3002 \u3000\u3000\u4ed6\u76ee\u5149\u4e00\u626b\uff0c\u53ea\u89c1\u5c4b\u9762\u4e0a\u5012\u7740\u4e00\u5177\u5c38\u8eab\uff0c\u4e00\u67c4\u96ea\u4eae\u7684\u957f\u5200\uff0c\u6a2a\u5728\u90a3\u5177\u5c38\u8eab\u4e4b\u4fa7\uff0c\u4ed6\u4e0d\u7528 \u518d\u770b\u7b2c\u4e8c\u773c\uff0c\u4fbf\u77e5\u9053\u90a3\u5c31\u662f\u65b9\u624d\u8fd8\u6d3b\u751f\u751f\u7684\u795e\u5200\u9f9a\u5947\u3002 \u3000\u3000\u4e00\u9635\u60b2\u54c0\u548c\u601c\u60dc\u7684\u611f\u89c9\u500f\u7136\u6d8c\u5411\u5fc3\u5934\uff0c\u4f46\u4ed6\u5374\u6ca1\u6709\u65f6\u95f4\u53bb\u67e5\u770b\u4e00\u4e0b\uff0c\u56e0\u4e3a\u524d\u9762\u90a3\u6761\u4eba \u5f71\uff0c\u6b64\u523b\u5fae\u4e00\u8d77\u843d\uff0c\u4fbf\u5df2\u8fdc\u8fdc\u63a0\u53bb\u3002 \u3000\u3000\u76f4\u5230\u6b64\u523b\uff0c\u5353\u957f\u537f\u8fd8\u4ece\u672a\u548c\u4eba\u5bb6\u771f\u6b63\u52a8\u8fc7\u624b\uff0c\u4f46\u4ed6\u5374\u4e00\u76f4\u6df1\u77e5\u81ea\u5df1\u7684\u6b66\u529f\uff0c\u867d\u4e0d\u80fd\u8bf4\u5df2 \u8d85\u51e1\u4eba\u5723\uff0c\u4f46\u5728\u82b8\u82b8\u6b66\u6797\u4e2d\uff0c\u5df2\u662f\u9876\u5c16\u9ad8\u624b\u4e86\u3002 \u3000\u3000\u800c\u6b64\u523b\u4ed6\u5374\u5bf9\u81ea\u5df1\u7684\u4fe1\u5fc3\uff0c\u5fae\u6709\u52a8\u6447\uff0c\u56e0\u4e3a\u773c\u524d\u8fd9\u4e2a\u5bf9\u624b\uff0c\u8f7b\u529f\u4e4b\u66fc\u5999\uff0c\u7adf\u7edd\u4e0d\u5728\u4ed6\u4e4b \u4e0b\uff0c\u591c\u8272\u4e4b\u4e2d\uff0c\u53ea\u89c1\u8fd9\u6761\u4eba\u5f71\u6709\u5982\u4e00\u9053\u8f7b\u70df\uff0c\u968f\u98ce\u800c\u53bb\uff0c\u4ed6\u53ea\u80fd\u770b\u5230\u4e00\u6761\u5f71\u5b50\uff0c\u5374\u5206\u4e0d\u51fa\u6b64 \u4eba\u7684\u8eab\u5f62\u3002 \u3000\u3000\u591c\u8272\u5982\u58a8\uff0c\u8fd9\u6b63\u662f\u7834\u6653\u524d\u5fc5\u6709\u7684\u73b0\u8c61\uff0c\u4e0d\u7528\u591a\u4e45\u592a\u9633\u5c31\u4f1a\u5347\u51fa\u6765\u4e86\u3002 \u3000\u3000\u9ed1\u6697\u4e4b\u4e2d\uff0c\u53ea\u89c1\u524d\u540e\u4e24\u6761\u4eba\u5f71\uff0c\u7535\u4e5f\u4f3c\u7684\u63a0\u4e86\u8fc7\u53bb\uff0c\u90a3\u79cd\u60ca\u4eba\u7684\u901f\u5ea6\uff0c\u5c31\u662f\u98de\u884c\u7edd\u8ff9\u7684 \u82cd\u9e70\uff0c\u4f3c\u4e5f\u65e0\u6cd5\u4f01\u53ca\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u4e24\u6761\u4eba\u5f71\u9038\u53bb\u4e4b\u540e\u7684\u7247\u523b\uff0c\u8fd9\u6761\u957f\u957f\u7684\u5c4b\u6a90\u4e0b\u7adf\u53c8\u63a0\u8d77\u4e24\u4eb2\u4eba\u5f71\uff0c\u5411\u4ed6\u4eec\u6d88\u5931\u7684 \u65b9\u5411\u500f\u7136\u8ffd\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u8fd9\u4e24\u6761\u4eba\u5f71\u8f7b\u529f\u867d\u8f83\u4ed6\u4eec\u5f31\u3002\u4f46\u5374\u4e5f\u4ecd\u7136\u662f\u8db3\u4ee5\u60ca\u4e16\u800c\u9a87\u4fd7\u7684\uff0c\u4e91\u4e2d\u7a0b\u4e00\u6325\u624b\u4e2d\u5229\u5251\uff0c \u7acb\u5373\u817e\u8eab\u800c\u4e0a\uff0c\u5374\u5df2\u65e0\u6cd5\u8ffd\u53ca\u4e86\u3002 \u3000\u3000\u957f\u8857\u4e0a\u7684\u6df7\u4e71\u4e0e\u60e8\u547c\uff0c\u4e5f\u7acb\u5373\u5e73\u606f\u4e86\uff0c\u5feb\u5200\u4e01\u4e03\u6a2a\u4e98\u624b\u4e2d\u7684\u957f\u5200\uff0c\u76ee\u5149\u7a7a\u6d1e\u5730\u671b\u5411\u82cd \u7a79\uff0c\u4e1c\u65b9\u5df2\u6e10\u6cdb\u51fa\u9c7c\u9752\u3002 \u3000\u3000\u5341\u5e74\u672a\u8270\u82e6\u7684\u953b\u70bc\uff0c\u518d\u52a0\u4e0a\u4ed6\u8d85\u4e8e\u5e38\u4eba\u7684\u5929\u8d44\uff0c\u4ee5\u53ca\u53f8\u7a7a\u8001\u4eba\u90a3\u6d69\u5982\u6ca7\u6d77\u7684\u6b66\u529f\u7684\u4f20 \u6388\uff0c\u4f7f\u5f97\u5353\u957f\u537f\u6b64\u523b\u5185\u5728\u7684\u529f\u529b\u6709\u5982\u6d77\u4e2d\u7684\u6d6a\u6d9b\uff0c\u6b64\u6d88\u5f7c\u957f\uff0c\u751f\u751f\u4e0d\u606f\u3002 \u3000\u3000\u4ed6\u7684\u8eab\u5f62\u8d8a\u6765\u8d8a\u5feb\uff0c\u548c\u524d\u9762\u90a3\u6761\u4eba\u5f71\u7684\u8ddd\u79bb\u4e5f\u8d8a\u6765\u8d8a\u77ed\uff0c\u4f46\u662f\u4ed6\u8d77\u6b65\u8f83\u8fdf\uff0c\u53c8\u56e0\u795e\u5251\u9f9a \u5947\u4e4b\u6b7b\uff0c\u5fc3\u795e\u7565\u5206\uff0c\u662f\u4ee5\u6b64\u523b\u4ed6\u4ecd\u7136\u548c\u524d\u9762\u7684\u4eba\u5f71\u9694\u7740\u7ea6\u83ab\u4e09\u4e08\u8fdc\u8fd1\uff0c\u4e09\u4e08\u8fdc\u8fd1\uff0c\u81ea\u7136\u4e0d\u7b97 \u5927\u957f\uff0c\u4f46\u6b64\u65f6\u6b64\u523b\uff0c\u5374\u4e5f\u4e0d\u662f\u6613\u4e8e\u8ffd\u53ca\u7684\u3002 \u3000\u3000\u7728\u773c\u4e4b\u95f4\uff0c\u4e34\u5b89\u7684\u57ce\u5ed3\uff0c\u5df2\u5728\u773c\u524d\uff0c\u524d\u9762\u90a3\u6761\u4eba\u5f71\u5411\u5de6\u4e00\u6298\uff0c\u7a81\u53c8\u51cc\u7a7a\u800c\u8d77\uff0c\u4e00\u62d4\u4e4b \u52bf\uff0c\u7adf\u7136\u51e0\u8fbe\u4e09\u4e08\u3002 \u3000\u3000\u4e34\u5b89\u4e43\u53e4\u4ee3\u540d\u57ce\uff0c\u57ce\u5ed3\u4e4b\u9ad8\uff0c\u5e76\u4e0d\u6bd4\u889c\u68f1\u4eac\u90fd\u900a\u8272\uff0c\u90a3\u6761\u4eba\u5f71\u867d\u7136\u4e00\u63a0\u4e09\u4e08\uff0c\u5374\u4ecd\u7136\u548c \u57ce\u5934\u6709\u7740\u4e00\u6bb5\u8ddd\u79bb\u3002 \u3000\u3000\u5353\u957f\u537f\u5fc3\u4e2d\u6697\u559c\uff0c\u811a\u4e0b\u731b\u4e00\u52a0\u52b2\uff0c\u55d6\u5730\u7a9c\u4e86\u8fc7\u53bb\uff0c\u53ea\u89c9\u524d\u9762\u90a3\u6761\u4eba\u5f71\u8eab\u5f62\u7adf\u5f80\u57ce\u5899\u4e0a\u4e00 \u8d34\uff0c\u7728\u773c\u4e4b\u95f4\uff0c\u4fbf\u5df1\u5347\u81f3\u57ce\u5934\u3002 \u3000\u3000\u6b64\u523b\u5353\u957f\u537f\u7684\u8eab\u5f62\uff0c\u4ea6\u81ea\u62d4\u8d77\uff0c\u4ed6\u867d\u4e5f\u77e5\u9053\u81ea\u5df1\u8fd9\u6837\u7684\u7a9c\u4e0a\u53bb\uff0c\u975e\u5e38\u5bb9\u6613\u53d7\u5230\u522b\u7684\u4eba\u6697 \u7b97\uff0c\u4f46\u6b64\u523b\u53ea\u8981\u4ed6\u7a0d\u4e00\u72b9\u7591\uff0c\u524d\u9762\u90a3\u6761\u4eba\u5f71\u4fbf\u81ea\u65e0\u6cd5\u8ffd\u53bb\uff0c\u8fd9\u6b63\u662f\u7a0d\u7eb5\u5373\u901d\u7684\u5173\u5934\uff0c\u6839\u672c\u4e0d \u5bb9\u4ed6\u52a0\u4ee5\u8003\u8651\u3002 \u3000\u3000\u4ed6\u8fd9\u5168\u529b\u4e00\u62d4\uff0c\u6709\u5982\u51b2\u5929\u4e4b\u9e64\uff0c\u4e0a\u5347\u4ea6\u6709\u4e09\u4e08\uff0c\u8863\u8882\u7834\u98ce\uff0c\u98ce\u58f0\u730e\u730e\uff0c\u8eab\u5f62\u62d4\u5230\u6781\u5904\uff0c \u7a81\u7136\u53cc\u81c2\u4e00\u632f\uff0c\u773c\u770b\u52bf\u9053\u5df2\u7aed\u7684\u8eab\u5f62\uff0c\u7adf\u7a81\u53c8\u51b2\u5929\u800c\u8d77\uff0c\u8fd9\u79cd\u6b66\u6797\u7f55\u89c1\u7684\u4e0a\u5929\u8f7b\u529f\uff0c\u4f7f\u5f97\u4ed6 \u663e\u6bd4\u524d\u9762\u90a3\u6761\u4eba\u5f71\u7684\u8f7b\u529f\u53c8\u5999\u4e0a\u4e00\u7b79\u3002 \u3000\u3000\u57ce\u5934\u4e4b\u4e0a\uff0c\u7a81\u7136\u54cd\u8d77\u4e00\u4e2a\u6e05\u8106\u7684\u58f0\u97f3\uff0c\u8f7b\u8f7b\u559d\u4e86\u58f0\uff1a\u201c\u597d\uff01\u2018\u5353\u957f\u537f\u5fae\u5fae\u4e00\u60ca\uff0c\u7aed\u5c3d\u5168 \u529b\uff0c\u5c06\u81ea\u5df1\u7684\u8eab\u5f62\u5411\u53f3\u8f7b\u6298\u4e00\u4e0b\uff0c\u66fc\u5999\u800c\u60ca\u4eba\u5730\u843d\u5728\u4e00\u4e2a\u7a81\u8d77\u7684\u57ce\u579b\u4e0a\uff0c\u76ee\u5149\u968f\u5373\u4e00\u626b\u3002\u53ea \u89c1\u81ea\u5df1\u5bf9\u9762\u7684\u53e6\u4e00\u4e2a\u57ce\u579b\u4e0a\u4fcf\u751f\u751f\u7ad9\u7740\u4e00\u6761\u4eba\u5f71\uff0c\u9ad8\u9b13\u5982\u4e91\uff0c\u8863\u8882\u98d8\u98d8\uff0c\u5728\u6726\u6726\u4e4b\u4e2d\uff0c\u4e00\u773c \u671b\u53bb\uff0c\u9762\u76ee\u867d\u770b\u4e0d\u751a\u6e05\uff0c\u4f46\u4ed6\u5df2\u89c9\u5f97\u6b64\u4eba\u4e4b\u7f8e\uff0c\u4e0d\u53ef\u65b9\u7269\uff0c\u7adf\u662f\u81ea\u5df1\u751f\u5e73\u672a\u7779\u3002\u4ed6\u4e0d\u7981\u6014\u4e86 \u4e00\u6014\uff0c\u56e0\u4e3a\u4ed6\u518d\u4e5f\u60f3\u4e0d\u5230\uff0c\u8fd9\u8f7b\u529f\u7edd\u5999\u4e4b\u4eba\uff0c\u7adf\u662f\u4e2a\u7f8e\u5982\u5929\u4ed9\u7684\u4e3d\u4eba\uff0c\u8fd9\u7edd\u8272\u4e3d\u4eba\u7ea4\u8170\u5fae \u626d\uff0c\u8f7b\u8f7b\u4e00\u7b11\uff0c\u7a81\u7136\u7b11\u9053\uff1a\u201c\u4f60\u8ffd\u6211\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5353\u957f\u537f\u4e0d\u7981\u4e3a\u4e4b\u4e00\u6014\uff0c\u6b64\u523b\u4ed6\u7adf\u65e0\u6cd5\u5c06\u773c\u524d\u8fd9\u4eff\u4f5b\u5c06\u8981\u968f\u98ce\u800c\u53bb\u7684\u5929\u4ed9\u4e3d\u4eba\uff0c\u548c\u65b9\u624d\u90a3 \u51b7\u9177\u6b8b\u5fcd\u7684\u51f6\u624b\u8054\u60f3\u5728\u4e00\u8d77\u3002 \u3000\u3000\u7247\u523b\u4e4b\u95f4\uff0c\u4ed6\u80f8\u4e2d\u4e00\u7247\u6df7\u4e71\uff0c\u7adf\u8bf4\u4e0d\u51fa\u8bdd\u6765\uff0c\u987b\u77e5\u4ed6\u867d\u662f\u806a\u660e\u7edd\u9876\u4e4b\u4eba\uff0c\u4f46\u6bd5\u7adf\u521d\u6d89\u7ea2 \u5c18\uff0c\u5bf9\u4eba\u5bf9\u4e8b\u7684\u5e94\u53d8\uff0c\u81ea\u7136\u751f\u758f\u5f97\u5f88\uff0c\u4f55\u51b5\u8fd9\u4e2a\u53d8\u6545\uff0c\u53c8\u662f\u5927\u5927\u7684\u51fa\u4e4e\u4ed6\u610f\u6599\u4e4b\u5916\u54e9\u3002 \u3000\u3000\u8fd9\u7edd\u8272\u4e3d\u4eba\u79cb\u6ce2\u6d41\u8f6c\uff0c\u5634\u89d2\u53c8\u81ea\u6cdb\u8d77\u4e00\u4e2a\u751c\u7f8e\u7edd\u4f26\u7684\u7b11\u9765\uff0c\u5a07\u7b11\u7740\u9053\uff1a\u201c\u5929\u8fd9\u4e48\u9ed1\u4e86\uff0c \u4f60\u548c\u6211\u65e0\u51a4\u65e0\u4ec7\uff0c\u8fd9\u4e48\u82e6\u82e6\u5730\u8ffd\u5728\u6211\u540e\u9762\uff0c\u662f\u60f3\u5e72\u4ec0\u4e48\u5440\uff1f\u201d \u3000\u3000\u4f38\u51fa\u624b\u638c\uff0c\u8f7b\u8f7b\u63a9\u7740\u5634\u89d2\u3002 \u3000\u3000\u5353\u957f\u537f\u53ea\u89c9\u5979\u9732\u5728\u8863\u8896\u5916\u7684\u4e00\u6bb5\u624b\u81c2\uff0c\u72b9\u5982\u83b9\u83b9\u767d\u7389\uff0c\u81f4\u81f4\u751f\u5149\uff0c\u5b9a\u4e86\u5b9a\u795e\uff0c\u6697\u6697\u900f\u4e86 \u53e3\u6c14\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u5c0f\u53ef\u867d\u548c\u59d1\u5a18\u65e0\u51a4\u65e0\u4ec7\uff0c\u4f46\u5c0f\u53ef\u5374\u8981\u8bf7\u6559\u4e00\u53e5\uff0c\u90a3\u5feb\u5200\u4f1a\u7684\u5f1f\u5144\u4eec\uff0c\u53c8 \u548c\u59d1\u5a18\u6709\u4f55\u4ec7\u6068\uff0c\u59d1\u5a18\u7adf\u8981\u5982\u6b64\u8d76\u5c3d\u6740\u7edd\u3002\u201d \u3000\u3000\u90a3\u7edd\u8272\u4e3d\u4eba\u7a81\u7136\u201c\u5916\u5473\u201d\u4e00\u7b11\uff0c\u53f3\u624b\u8f7b\u8f7b\u4e00\u7406\u9b13\u8fb9\u968f\u98ce\u626c\u8d77\u7684\u4e71\u53d1\uff0c\u5a07\u7b11\u9053\uff1a\u201c\u4f60\u8bf4\u7684 \u4ec0\u4e48\u8bdd\u5440\uff1f\u6211\u4e0d\u61c2\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u60f3\u5230\u65b9\u624d\u90a3\u4e9b\u5feb\u5200\u4f1a\u4f17\u60e8\u6b7b\u7684\u60c5\u51b5\uff0c\u4e00\u822c\u6012\u706b\u76f4\u51b2\u800c\u4e0a\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u65b9\u624d\u9601\u4e0b\u8eb2 \u5728\u6697\u5904\uff0c\u5c06\u90a3\u4e9b\u6beb\u65e0\u62b5\u6297\u4e4b\u529b\u7684\u6c49\u5b50\u4e00\u4e2a\u4e2a\u5c04\u6b7b\u5728\u9601\u4e0b\u7684\u6697\u5668\u4e4b\u4e0b\uff0c\u6b64\u523b\u9601\u4e0b\u5374\u53c8\u8bf4\u51fa\u8fd9\u79cd \u8bdd\u6765\uff0c\u8fd9\u624d\u771f\u662f\u6559\u5728\u4e0b\u96be\u4ee5\u7406\u89e3\u3002\u201d \u3000\u3000\u54ea\u77e5\u8fd9\u7edd\u8272\u4e3d\u4eba\uff0c\u4e00\u624b\u6367\u7740\u6843\u816e\uff0c\u5fae\u5782\u8793\u9996\uff0c\u4f3c\u4e4e\u6839\u672c\u6ca1\u6709\u542c\u5230\u4ed6\u7684\u8bdd\uff0c\u8fc7\u4e86\u534a\u664c\uff0c\u624d \u62ac\u8d77\u5934\u6765\uff0c\u5a07\u7b11\u9053\uff1a\u201c\u6211\u60f3\u8d77\u6765\u4e86\uff0c\u6211\u59d1\u59d1\u4ee5\u524d\u8ddf\u6211\u8bf4\u8fc7\u5feb\u5200\u4f1a\uff0c\u8bf4\u4ed6\u4eec\u90fd\u4e0d\u662f\u597d\u4e1c\u897f\uff0c\u4e13 \u95e8\u62a2\u4eba\u5bb6\u7684\u94b1\uff0c\u96be\u9053\u521a\u521a\u90a3\u4e9b\u88ab\u4eba\u5bb6\u4e00\u4e2a\u4e2a\u5f04\u6b7b\u7684\u5927\u6c49\u5c31\u662f\u5feb\u5200\u4f1a\u91cc\u7684\u4eba\u5417\uff1f\u201d \u3000\u3000\u5979\u4f38\u51fa\u4e00\u53cc\u7ea4\u638c\uff0c\u8f7b\u8f7b\u4e00\u62cd\uff0c\u53c8\u9053\uff1a\u201c\u6211\u771f\u5f00\u5fc3\u5440\uff01\u539f\u6765\u90a3\u4e9b\u4eba\u90fd\u662f\u5f3a\u76d7\uff0c\u6211\u672c\u6765\u8fd8\u5728 \u66ff\u4ed6\u4eec\u96be\u53d7\u54e9\u3002\u201d \u3000\u3000\u795e\u60c5\u4e4b\u95f4\uff0c\u7ade\u50cf\u662f\u4e2a\u65b9\u83b7\u65b0\u8863\u7684\u65e0\u90aa\u7ae5\u5b50\u3002 \u3000\u3000\u5353\u957f\u537f\u51b7\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u65b9\u624d\u88ab\u9601\u4e0b\u6697\u5668\u5c04\u6b7b\u7684\uff0c\u5c31\u662f\u5feb\u5200\u4f1a\u91cc\u7684\u6c49\u5b50\u3002\u201d \u3000\u3000\u90a3\u7edd\u8272\u4e3d\u4eba\u5374\u201c\u5440\u201d\u7684\u60ca\u5524\u4e86\u4e00\u58f0\uff0c\u4f38\u7740\u4e00\u53cc\u6625\u8471\u7389\u6307\uff0c\u6307\u7740\u5979\u90a3\u633a\u76f4\u800c\u79c0\u4e3d\u7684\u9f3b\u5b50\uff0c \u50cf\u662f\u4e0d\u80dc\u60ca\u8bb6\u7684\u8bf4\u9053\uff1a\u201c\u4ec0\u4e48\uff0c\u4f60\u8bf4\u6211\u6740\u4e86\u4ed6\u4eec\uff1f\u201d \u3000\u3000\u7389\u8155\u4e00\u626c\uff0c\u4ece\u9f3b\u5b50\u4e0a\u79fb\u5f00\uff0c\u5374\u53c8\u585e\u4f4f\u4e86\u81ea\u5df1\u7684\u8033\u6735\uff0c\u95ed\u8d77\u773c\u775b\uff0c\u957f\u957f\u7684\u776b\u6bdb\uff0c\u8986\u76d6\u5728\u773c \u7751\u4e0a\uff0c\u63a5\u7740\u53c8\u9053\uff1a\u201c\u8fd9\u8bdd\u6211\u53ef\u4e0d\u6562\u542c\uff0c\u4ece\u5c0f\u5230\u5927\uff0c\u6211\u8fde\u53ea\u8682\u8681\u90fd\u6ca1\u6709\u5f04\u6b7b\u8fc7\uff0c\u4f60\u5374\u8bf4\u6211\u6740\u4e86 \u4eba\u3002\u201d \u3000\u3000\u7a81\u7136\u5c06\u4e00\u53cc\u7389\u638c\u7b14\u76f4\u5730\u4f38\u5728\u5353\u957f\u537f\u9762\u524d\u9053\uff1a\u201c\u4f60\u770b\uff0c\u6211\u8fd9\u53cc\u624b\u50cf\u662f\u6740\u4eba\u7684\u5417\uff1f\u201d \u3000\u3000\u5353\u957f\u537f\u4e0d\u7531\u81ea\u4e3b\u7684\u4e00\u671b\uff0c\u53ea\u89c1\u8fd9\u53cc\u624b\u638c\uff0c\u7389\u6da6\u73e0\u5706\uff0c\u5341\u53ea\u6709\u5982\u6625\u8471\u822c\u7684\u624b\u6307\u659c\u659c\u5782\u4e0b\uff0c \u548c\u624b\u80cc\u5f62\u6210\u4e00\u79cd\u7f8e\u5999\u7684\u5f27\u7ebf\uff0c\u6307\u7532\u4e0a\u6d82\u7740\u9c9c\u7ea2\u7684\u73ab\u7470\u82b1\u6c41\uff0c\u66f4\u6620\u5f97\u80a4\u8272\u767d\u5982\u83b9\u7389\u3002 \u3000\u3000\u4ed6\u4e0d\u7981\u6697\u53f9\u4e00\u58f0\uff0c\u5b9e\u5728\u81ea\u5df1\u4e5f\u4e0d\u76f8\u4fe1\u8fd9\u53cc\u624b\u4f1a\u6740\u4eba\uff0c\u4f46\u65b9\u624d\u4e4b\u4eab\uff0c\u5374\u53c8\u662f\u81ea\u5df1\u4eb2\u76ee\u6240 \u7779\uff0c\u5374\u53c8\u4ee4\u4ed6\u4e0d\u80fd\u76f8\u4fe1\u3002 \u3000\u3000\u65b9\u624d\u4ed6\u5353\u7acb\u5728\u8857\u65c1\uff0c\u76ee\u5149\u56db\u626b\uff0c\u773c\u89c1\u6709\u4e00\u70b9\u9edd\u9ed1\u5f97\u51e0\u975e\u76ee\u529b\u80fd\u8fa8\u7684\u5149\u5f71\uff0c\u4ece\u5c4b\u6a90\u4e0b\u5c04 \u51fa\uff0c\u662f\u4ee5\u7eb5\u8eab\u53d1\u51fa\u4e00\u638c\u3002 \u3000\u3000\u4ed6\u53c8\u7a0d\u5fae\u4e00\u5b9a\u795e\uff0c\u5c06\u65b9\u624d\u7684\u60c5\u51b5\u6781\u5feb\u5730\u601d\u5fd6\u4e86\u4e00\u904d\uff0c\u65ad\u7136\u4ed6\u8bf4\u9053\uff1a\u201c\u8fd9\u53cc\u624b\u638c\u5b9e\u5728\u4e0d\u50cf \u6740\u4eba\u7684\uff0c\u4f46\u59d1\u5a18\u597d\u751f\u751f\u7684\u8eb2\u5728\u5c4b\u6a90\u4e0b\u9762\uff0c\u5374\u53c8\u662f\u4e3a\u7740\u4ec0\u4e48\u5462\uff1f\u59d1\u5a18\u82e5\u662f\u8fde\u53ea\u8682\u8681\u90fd\u4e0d\u5fcd\u5f04 \u6740\uff0c\u90a3\u4e48\u59d1\u5a18\u770b\u90a3\u4e48\u591a\u4eba\u6b7b\u5728\u4f60\u9762\u524d\uff0c\u5374\u53c8\u4e3a\u4ec0\u4e48\u4e0d\u6015\u4e86\u5462\uff1f\u201d \u3000\u3000\u90a3\u7edd\u8272\u4e3d\u4eba\u54af\u54af\u4e00\u7b11\u3001\u5c06\u90a3\u53cc\u7389\u638c\u7f29\u56de\u8896\u91cc\uff0c\u5a07\u7b11\u9053\uff1a\u201c\u54df\uff0c\u5012\u770b\u4e0d\u51fa\u4f60\u4e00\u8138\u8001\u8001\u5b9e\u5b9e \u7684\u6837\u5b50\uff0c\u5374\u5c45\u7136\u4e5f\u8fd9\u4e48\u4f1a\u8bf4\u8bdd\uff0c\u8fd9\u5012\u771f\u662f\u4eba\u4e0d\u53ef\u8c8c\u76f8\u4e86\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u9762\u8272\u4e00\u6c89\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u5c0f\u53ef\u6240\u8bf4\u7684\u8bdd\uff0c\u53e5\u53e5\u90fd\u6781\u4e3a\u4e25\u91cd\uff0c\u59d1\u5a18\u82e5\u8fd8\u662f\u5982\u6b64\u620f\u5f04\u4e8e \u6211\uff0c\u5374\u83ab\u602a\u6211\u8981\u4e0d\u5ba2\u6c14\u4e86\u3002\u201d \u3000\u3000\u8fd9\u5c11\u5973\u81ea\u8d1f\u7edd\u8272\u65e0\u53cc\uff0c\u5e73\u751f\u6240\u89c1\u7684\u7537\u4eba\u4e00\u89c1\u5979\u4e4b\u9762\uff0c\u83ab\u4e0d\u795e\u9b42\u98a0\u5012\u3002\u6b64\u523b\u5353\u957f\u537f\u9762\u76ee\u5982 \u94c1\uff0c\u51b7\u51b0\u51b0\u8bf4\u51fa\u8fd9\u756a\u8bdd\u6765\uff0c\u4e0d\u7981\u4ee4\u5979\u5fae\u5fae\u6014\u4e86\u4e00\u6014\uff0c\u51e0\u4e4e\u4ee5\u4e3a\u81ea\u5df1\u5bf9\u9762\u8fd9\u82f1\u633a\u7684\u5c11\u5e74\u662f\u4e2a\u778e \u5b50\u3002 \u3000\u3000\u4f46\u7565\u5fae\u4e00\u6014\u4e4b\u540e\uff0c\u5979\u77ac\u5373\u6062\u590d\u5e38\u6001\uff0c\u8f7b\u8f7b\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u8bf4\u7684\u8bdd\uff0c\u53ef\u4e5f\u53e5\u53e5\u90fd\u662f\u771f\u7684 \u5440\uff01\u4f60\u8981\u662f\u4e0d\u76f8\u4fe1\uff0c\u4f60\u5c31\u641c\u641c\u6211\u8eab\u4e0a\u770b\uff0c\u770b\u770b\u6211\u8eab\u4e0a\u6709\u6ca1\u6709\u5e26\u7740\u4ec0\u4e48\u6697\u5668\u3002\u201d \u3000\u3000\u7f57\u8896\u4e00\u626c\uff0c\u4e24\u81c2\u9ad8\u9ad8\u5f20\u8d77\uff0c\u5c06\u8eab\u4e0a\u7684\u8f7b\u7f57\u8863\u88d9\uff0c\u90fd\u63d0\u4e86\u8d77\u6765\u3002 \u3000\u3000\u4e00\u9635\u98ce\u5439\u8fc7\uff0c\u5c06\u90a3\u4ef6\u8f7b\u7ea2\u7f57\u886b\u5439\u5f97\u7d27\u7d27\u8d34\u5728\u5979\u8eab\u4e0a\uff0c\u53ea\u89c1\u5979\u8eab\u6750\u5b9b\u8f6c\u8d77\u4f0f\uff0c\u67f3\u8170\u8f7b\u8f7b\u4e00 \u62e7\uff0c\u7aef\u7684\u70af\u5a1c\u52a8\u4eba\u3002 \u3000\u3000\u5353\u957f\u537f\u4e43\u7edd\u9876\u806a\u660e\u4e4b\u4eba\uff0c\u600e\u4f1a\u662f\u4e2a\u4e0d\u8bc6\u7f8e\u8272\u7684\u9c81\u83bd\u7537\u5b50\uff0c\u53ea\u662f\u4ed6\u751f\u5177\u5176\u7236\u4e4b\u7980\u6027\uff0c\u6b63\u662f \u81f3\u9633\u81f3\u521a\u7684\u7537\u513f\uff0c\u5bf9\u4e8e\u5584\u6076\u4e4b\u5206\uff0c\u8fdc\u6bd4\u7f8e\u4e11\u4e4b\u522b\u770b\u5f97\u91cd\u4e9b\uff0c\u4ed6\u867d\u7136\u77e5\u9053\u773c\u524d\u8fd9\u5c11\u5973\u662f\u4e3e\u4e16\u96be \u5bfb\u7684\u7edd\u8272\uff0c\u4f46\u4ed6\u53ea\u8981\u4e00\u60f3\u8d77\u65b9\u624d\u90a3\u4e9b\u5927\u6c49\u7684\u60e8\u547c\uff0c\u773c\u524d\u8fd9\u65e0\u53cc\u7edd\u8272\uff0c\u5c31\u50cf\u662f\u53d8\u5f97\u5341\u5206\u4e11\u964b \u4e86\u3002 \u3000\u3000\u8fd9\u4e5f\u8bb8\u662f\u4ed6\u5bf9\u7f8e\u4e11\u4e24\u5b57\u7684\u770b\u6cd5\uff0c\u548c\u522b\u4eba\u6709\u4e9b\u4e24\u6837\uff0c\u4f46\u806a\u660e\u7684\u4eba\u5bf9\u5185\u5728\u7684\u7f8e\uff0c\u4e0d\u90fd\u662f\u770b\u5f97 \u6bd4\u5916\u5728\u7684\u7f8e\u91cd\u8981\u5417\uff1f \u3000\u3000\u4ed6\u51b7\u54fc\u4e00\u58f0\uff0c\u76ee\u5149\u907f\u5f00\u90a3\u7f8e\u5999\u7684\u80f4\u4f53\uff0c\u51b7\u6da9\u4ed6\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u59d1\u5a18\u662f\u5426\u5c06\u4eba\u547d\u770b\u5f97\u975e\u5e38 \u8f7b\u8d31\uff0c\u6740\u6b7b\u90a3\u4e48\u591a\u4eba\u4e4b\u540e\uff0c\u8fd8\u80fd\u6041\u4ed6\u8bf4\u7b11\u2014\u2014\u201d\u90a3\u7edd\u8272\u4e3d\u4eba\u7a81\u7136\u8f7b\u98a6\u9edb\u7709\uff0c\u5e7d\u5e7d\u53f9\u4e86\u53e3\u6c14\uff0c \u8f7b\u8f7b\u8bf4\u9053\uff1a\u201c\u4f60\u8fd9\u4eba\u600e\u4e48\u603b\u662f\u4e0d\u76f8\u4fe1\u6211\uff0c\u5509\uff0c\u4f60\u77e5\u4e0d\u77e5\u9053\uff0c\u6211\u5e73\u751f\u4ece\u672a\u5bf9\u7537\u5b50\u8bf4\u7b11\u8fc7\u3002\u201d \u3000\u3000\u4e00\u53cc\u79cb\u6ce2\uff0c\u4f3c\u55d4\u4f3c\u6028\uff0c\u51dd\u6ce8\u5728\u5353\u957f\u537f\u8eab\u4e0a\u3002 \u3000\u3000\u5353\u957f\u537f\u53ea\u89c9\u5fc3\u5934\u4e00\u8df3\uff0c\u4e00\u9635\u6e29\u99a8\u7684\u611f\u89c9\uff0c\u9690\u9690\u4ece\u5fc3\u5e95\u95ea\u8fc7\uff0c\u8fd9\u79cd\u96be\u8a00\u7684\u6ecb\u5473\uff0c\u7adf\u662f\u4ed6\u6709 \u751f\u4ee5\u6765\uff0c\u4ece\u672a\u6709\u8fc7\u7684\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u5728\u5fc3\u5e95\u957f\u53f9\u4e00\u58f0\uff0c\u4e00\u77ac\u4e4b\u95f4\u4ed6\u4eff\u4f5b\u53c8\u89c9\u5f97\u773c\u524d\u8fd9\u72b9\u5982\u4f9d\u4eba\u5c0f\u9e1f\u822c\u7684\u5c11\u5973\uff0c\u4e0d\u53ef\u80fd \u505a\u51fa\u65b9\u624d\u90a3\u79cd\u8840\u6dcb\u6dcb\u7684\u4e8b\u6765\u3002 \u3000\u3000\u6b64\u523b\u4e1c\u65b9\u5df2\u9732\u66d9\u8272\uff0c\u5927\u5730\u5df2\u7531\u9edd\u9ed1\u800c\u6e10\u6e10\u53d8\u5f97\u5149\u4eae\u4e86\u8d77\u6765\u3002 \u3000\u3000\u90a3\u7edd\u8272\u4e3d\u4eba\u79cb\u6ce2\u4e00\u8f6c\uff0c\u770b\u5230\u57ce\u5ed3\u4e0b\u7684\u90ca\u91ce\u4e0a\uff0c\u7535\u4e5f\u4f3c\u7684\u9a70\u6765\u4e86\u4e24\u6761\u6de1\u7ea2\u4eba\u5f71\uff0c\u5634\u89d2\u7a81\u7136 \u6cdb\u8d77\u4e00\u4e1d\u51b7\u7b11\uff0c\u5a07\u67d4\u7684\u5e7d\u6028\u4e4b\u8272\u7728\u773c\u4e4b\u95f4\u4e00\u626b\u800c\u7a7a\uff0c\u84e6\u5730\u4e00\u6298\u67f3\u8170\uff0c\u51b7\u7b11\u7740\u9053\uff1a\u201c\u4f60\u8981\u662f\u4e0d \u76f8\u4fe1\u6211\u7684\u8bdd\uff0c\u90a3\u4e9b\u4eba\u5c31\u7b97\u662f\u6211\u6740\u7684\u597d\u4e86\u3002\u201d \u3000\u3000\u7ea4\u638c\u4e00\u626c\uff0c\u7389\u6307\u5fae\u98de\u5982\u5170\uff0c\u7a81\u7136\u76f4\u5212\u5230\u5353\u957f\u537f\u7684\u773c\u524d\uff0c\u5353\u957f\u537f\u65b9\u81ea\u4e00\u6014\uff0c\u5374\u89c1\u8fd9\u53cc\u5170\u82b1 \u822c\u7684\u7389\u638c\u5df2\u81ea\u5212\u5230\u81ea\u5df1\u9f3b\u4fa7\u7684\u6c89\u9999\u524d\u3002 \u3000\u3000\u8fd9\u4e00\u62db\u6765\u52bf\u6709\u5982\u95ea\u7535\uff0c\u4e0d\u4f46\u4e1d\u6beb\u6ca1\u6709\u5148\u673a\uff0c\u800c\u4e14\u5353\u957f\u537f\u518d\u4e5f\u4e0d\u4f1a\u60f3\u5230\u8fd9\u4f4d\u6e29\u67d4\u7b11\u8bed\u3001\u8e69 \u7709\u8f7b\u98a6\u7684\u5c11\u5973\u4f1a\u5bf9\u81ea\u5df1\u9aa4\u4e0b\u6740\u624b\u3002 \u3000\u3000\u4ed6\u5927\u60ca\u4e4b\u4e0b\uff0c\u8eab\u5f62\u500f\u7136\u800c\u9000\uff0c\u5374\u89c1\u90a3\u7edd\u8272\u4e3d\u4eba\u51b7\u7b11\u4e00\u58f0\uff0c\u53e0\u5728\u8155\u4e0a\u7684\u8896\u5b50\uff0c\u7a81\u7136\u50cf\u6d41\u4e91 \u4e00\u6837\u98de\u4e86\u51fa\u6765\uff0c\u5e26\u7740\u4e00\u80a1\u4fb5\u4eba\u7684\u51b7\u98ce\uff0c\u53c8\u6325\u5411\u5353\u957f\u537f\u7684\u9762\u95e8\uff0c\u811a\u4e0b\u83b2\u8db3\u8f7b\u70b9\uff0c\u5df2\u7531\u5979\u81ea\u5df1\u65b9 \u624d\u7acb\u8db3\u7684\u90a3\u57ce\u579b\uff0c\u8f7b\u7075\u5730\u63a0\u5230\u5353\u957f\u537f\u65b9\u624d\u7acb\u8db3\u7684\u57ce\u579b\u4e4b\u4e0a\u3002 \u3000\u3000\u8fd9\u4e00\u62db\u66f4\u662f\u5927\u51fa\u5353\u957f\u537f\u610f\u6599\u4e4b\u5916\uff0c\u6b64\u523b\u4ed6\u811a\u4e0b\u4e1a\u5df2\u662f\u60ac\u7a7a\uff0c\u800c\u4e14\u773c\u770b\u53bb\u52bf\u5df2\u7aed\uff0c\u90a3\u7edd\u8272 \u4e3d\u4eba\u770b\u5230\u773c\u91cc\uff0c\u76ee\u4e2d\u9732\u51fa\u5f97\u610f\u4e4b\u8272\u3002 \u3000\u3000\u54ea\u77e5\u5353\u957f\u537f\u7a81\u7136\u51cc\u7a7a\u5fae\u4e00\u62e7\u8eab\uff0c\u53cd\u624b\u4e00\u62db\u6325\u51e4\u624b\uff0c\u7adf\u786c\u751f\u751f\u7684\u5212\u5411\u90a3\u7247\u6709\u5982\u6d41\u4e91\u822c\u7684\u7f57 \u8896\uff0c\u638c\u51e4\u5982\u5200\uff0c\u5506\u7136\u4f5c\u54cd\u3002 \u3000\u3000\u90a3\u7edd\u8272\u5c11\u5973\u76ee\u5149\u4e00\u53d8\uff0c\u7f57\u8896\u53cd\u5377\uff0c\u67f3\u8170\u8f7b\u62e7\u95f4\u5374\u7528\u53e6\u4e00\u53ea\u624b\u201c\u54a7\u201d\u7684\u51fb\u51fa\u4e24\u638c\uff0c\u83b2\u8db3\u5728 \u57ce\u579b\u4e0a\u4e00\u70b9\uff0c\u500f\u7136\u53c8\u81ea\u659c\u8e22\u4e00\u817f\u3002 \u3000\u3000\u8fd9\u7edd\u8272\u5c11\u5973\u4e0d\u4f46\u8eab\u6cd5\u5947\u8be1\uff0c\u62db\u5f0f\u95f4\u53d8\u5316\u4e4b\u5feb\uff0c\u66f4\u662f\u65e0\u4e0e\u4f26\u6bd4\uff0c\u8fd9\u4e24\u638c\u4e00\u817f\uff0c\u7adf\u751f\u50cf\u662f\u5728 \u540c\u4e00\u5239\u90a3\u95f4\u53d1\u51fa\u7684\u3002\u800c\u4e14\u638c\u867d\u7ea4\u67d4\u5982\u7389\uff0c\u638c\u98ce\u5374\u662f\u864e\u864e\u60ca\u4eba\uff0c\u663e\u89c1\u62db\u62db\u542b\u8574\u5185\u529b\u3002 \u3000\u3000\u5353\u957f\u537f\u5251\u7709\u5fae\u6311\uff0c\u80a9\u5934\u5fae\u6643\uff0c\u624b\u638c\u7a81\u7136\u4e00\u7a7f\uff0c\u8eab\u5f62\u8fc5\u5982\u98d8\u98ce\u822c\u659c\u659c\u4e00\u7a9c\uff0c\u7adf\u4ece\u90a3\u7edd\u8272\u5c11 \u5973\u7684\u638c\u51e4\u817f\u5f71\u4e2d\u659c\u63a0\u51fa\u53bb\u3002 \u3000\u3000\u8fd9\u4e00\u63a0\u4e4b\u52bf\uff0c\u7adf\u6709\u4e24\u4e08\uff0c\u90a3\u7edd\u8272\u5c11\u5973\u4f3c\u4e4e\u5fae\u5403\u4e00\u60ca\uff0c\u500f\u7136\u4f4f\u624b\uff0c\u8f6c\u8eab\u671b\u53bb\uff0c\u5374\u89c1\u8fd9\u82f1\u633a \u5c11\u5e74\u5df2\u5353\u7acb\u7ad9\u5728\u81ea\u5df1\u8eab\u540e\u7684\u57ce\u579b\u4e4b\u4e0a\u3002 \u3000\u3000\u5979\u5634\u89d2\u5411\u4e0b\u4e00\u6487\uff0c\u51b7\u7b11\u7740\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u8981\u6349\u4f4f\u6211\u66ff\u90a3\u4ec0\u4e48\u5feb\u5200\u4f1a\u62a5\u4ec7\u5417\uff1f\u73b0\u5728\u4f60\u600e\u4e48\u4e0d \u2014\u2014\u201d\u54ea\u77e5\u5353\u957f\u537f\u7a81\u7136\u5389\u53f1\u4e00\u58f0\u9053\uff1a\u201c\u6b63\u662f\u3002\u201d \u3000\u3000\u5de6\u638c\u500f\u626c\uff0c\u98df\u3001\u4e2d\u4e24\u6307\u5fae\u66f2\uff0c\u63a2\u4fea\u53d6\u73e0\uff0c\u75be\u70b9\u90a3\u7edd\u8272\u5c11\u5973\u7684\u53cc\u76ee\uff0c\u53f3\u638c\u638c\u7f18\u659c\u7acb\uff0c \u201c\u5530\u201d\u7684\u51fb\u5411\u5de6\u80a9\u3002 \u3000\u3000\u90a3\u7edd\u8272\u5c11\u5973\u8bed\u72b9\u672a\u4e86\uff0c\u4ea6\u81ea\u60f3\u4e0d\u5230\u5bf9\u624b\u8bf4\u6253\u5c31\u6253\uff0c\u5979\u5e74\u7eaa\u867d\u8f7b\uff0c\u4f46\u5374\u8fdc\u6bd4\u5353\u957f\u537f\u72e1\u9ee0\uff0c \u65b9\u624d\u5353\u957f\u537f\u4e00\u8def\u72c2\u8ffd\uff0c\u5979\u867d\u4e0d\u613f\u548c\u6765\u4eba\u671d\u76f8\uff0c\u4f46\u81ea\u6043\u8f7b\u529f\uff0c\u8ba4\u4e3a\u522b\u4eba\u5b9a\u7136\u65e0\u6cd5\u8ffd\u53ca\u81ea\u5df1\uff0c\u662f \u4ee5\u4e5f\u4e0d\u4ee5\u4e3a\u610f\uff0c\u53ea\u60f3\u5c06\u90a3\u4eba\u8fdc\u8fdc\u629b\u5f00\u3002 \u3000\u3000\u54ea\u77e5\u5353\u957f\u537f\u8d8a\u8ffd\u8d8a\u8fd1\uff0c\u5979\u4fcf\u6084\u56de\u7738\u4e00\u671b\uff0c\u624d\u53d1\u73b0\u8ffd\u81ea\u5df1\u7684\u8fd9\u4eba\u8f7b\u529f\u4e4b\u9ad8\u5999\uff0c\u7b80\u76f4\u60ca\u4e16\u9a87 \u4fd7\uff0c\u5979\u4e43\u7edd\u9876\u806a\u660e\u4e4b\u4eba\uff0c\u5fc3\u4e0b\u4e00\u601d\u5fd6\uff0c\u77e5\u9053\u81ea\u5df1\u5e76\u4e0d\u80fd\u5c06\u4eba\u5bb6\u629b\u5f00\uff0c\u662f\u4ee5\u5c31\u5728\u57ce\u5899\u4e0a\u9a7b\u8db3\u800c \u5019\u3002 \u3000\u3000\u672c\u6765\u5979\u8fd8\u60f3\u4e58\u7740\u90a3\u4eba\u63a0\u4e0a\u57ce\u5899\u65f6\uff0c\u731d\u7136\u51fb\u51fa\u4e00\u638c\uff0c\u5c06\u6765\u4eba\u6bd9\u4e8e\u638c\u4e0b\uff0c\u4f46\u5979\u4e00\u770b\u5230\u4eba\u5bb6\u63a0 \u4e0a\u5899\u5934\u65f6\u7684\u8eab\u6cd5\uff0c\u5374\u53c8\u6539\u53d8\u4e86\u4e3b\u610f\u3002 \u3000\u3000\u7b49\u5230\u5353\u957f\u537f\u75be\u8a00\u76f8\u8be2\uff0c\u5979\u60ca\u4e8e\u8fd9\u5c11\u5e74\u6b66\u529f\u4e4b\u9ad8\uff0c\u662f\u4ee5\u5e76\u672a\u51fa\u624b\uff0c\u53ef\u662f\u5374\u5df2\u6697\u85cf\u6740\u673a\uff0c\u540e \u6765\u5979\u671b\u5230\u8fdc\u8fdc\u5954\u6765\u7684\u4e24\u4eba\u662f\u81ea\u5df1\u7684\u5e2e\u624b\uff0c\u4fbf\u6beb\u4e0d\u72b9\u7591\u5730\u731d\u7136\u53d1\u51fa\u4e00\u638c\u3002 \u3000\u3000\u4f46\u6b64\u523b\u5979\u4e00\u89c1\u5353\u957f\u537f\u4e4b\u51fa\u624b\uff0c\u4e0d\u7981\u82b3\u5fc3\u6697\u9a87\uff0c\u53ea\u89c9\u5bf9\u65b9\u51fb\u6765\u7684\u638c\u52bf\u4e4b\u4e2d\u6f5c\u529b\u521a\u731b\uff0c\u7adf\u53c8 \u5927\u51fa\u81ea\u5df1\u7684\u610f\u6599\u4e4b\u5916\u3002 \u3000\u3000\u5979\u54ea\u91cc\u77e5\u9053\u5353\u957f\u537f\u8f7b\u529f\u867d\u5999\uff0c\u5374\u975e\u6240\u957f\uff0c\u82e5\u5355\u8bba\u8f7b\u529f\uff0c\u4ed6\u5e76\u4e0d\u6bd4\u8fd9\u5c11\u5973\u9ad8\u51fa\u8bb8\u591a\uff0c\u4f46\u82e5 \u8bba\u53ca\u5185\u529b\uff0c\u90a3\u5c31\u8fdc\u975e\u8fd9\u5c11\u5973\u6240\u80fd\u53ca\u4e86\u3002 \u3000\u3000\u4ed6\u5168\u529b\u51fb\u51fa\u4e24\u638c\uff0c\u773c\u89c1\u5df2\u582a\u582a\u89e6\u5230\u90a3\u5c11\u5973\u7684\u5a07\u8eaf\uff0c\u5979\u5374\u4ecd\u7136\u5446\u5446\u5730\u7ad9\u5728\u90a3\u91cc\uff0c\u4e0d\u907f\u4e0d \u95ea\uff0c\u5fc3\u4e2d\u4e0d\u7981\u6709\u4e9b\u540e\u6094\uff0c\u751f\u6015\u81ea\u5df1\u7684\u8fd9\u4e00\u638c\u4e00\u6307\uff0c\u51fa\u529b\u8fc7\u731b\uff0c\u800c\u5c06\u8fd9\u5c11\u5973\u51fb\u6bd9\u3002 \u3000\u3000\u987b\u77e5\u4ed6\u9762\u4e0a\u867d\u56e0\u8eab\u4e16\u4e4b\u60e8\u75db\uff0c\u4ee5\u53ca\u591a\u5e74\u7684\u7a7a\u5c71\u82e6\u7ec3\uff0c\u800c\u663e\u5f97\u6709\u4e9b\u51b7\u9177\uff0c\u5176\u5b9e\u4ed6\u5374\u662f\u81f3\u60c5 \u81f3\u6027\u4e4b\u4eba\uff0c\u6b64\u523b\u867d\u89c9\u5f97\u8fd9\u5c11\u5973\u8a00\u7b11\u65e0\u5e38\uff0c\u6027\u60c5\u4eff\u4f5b\u751a\u4e3a\u72e0\u8fa3\uff0c\u4f46\u4ed6\u5374\u7ec8\u4e0d\u5fcd\u5fc3\u5c06\u4e00\u4e2a\u521d\u6b21\u89c1 \u9762\u7684\u5c11\u5973\u4f24\u5728\u638c\u4e0b\u3002 \u3000\u3000\u4ed6\u6b64\u5ff5\u65e2\u751f\uff0c\u65b9\u60f3\u64a4\u56de\u638c\u529b\uff0c\u54ea\u77e5\u8fd9\u5c11\u5973\u7a81\u7136\u5a07\u8eaf\u4e00\u4ef0\uff0c\u4e24\u53ea\u7f57\u8896\uff0c\u7a81\u53c8\u5012\u5377\u800c\u51fa\uff0c\u7728 \u773c\u4e4b\u95f4\uff0c\u4f46\u89c9\u7ea2\u5f71\u6f2b\u5929\uff0c\u4e24\u53ea\u5e26\u7740\u5bd2\u51e4\u7684\u7f57\u8896\uff0c\u5df2\u56db\u9762\u516b\u65b9\u7684\u5411\u4ed6\u6325\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u6b64\u523b\u4ed6\u4eec\u7acb\u8db3\u4e4b\u5904\uff0c\u4ff1\u5728\u57ce\u5934\u4e4b\u4e0a\uff0c\u90a3\u57ce\u579b\u5468\u56f4\u4e0d\u8fc7\u4e09\u6570\u5c3a\uff0c\u867d\u662f\u6809\u6bd4\u800c\u7acb\uff0c\u4f46\u4e2d\u95f4\u5374 \u4e5f\u7a7a\u7740\u4e09\u6570\u5c3a\u4e00\u6bb5\u8ddd\u79bb\u3002 \u3000\u3000\u662f\u4ee5\u4ed6\u4eec\u52a8\u624b\u4e4b\u65f6\uff0c\u66f4\u65f6\u65f6\u7167\u987e\u5230\u811a\u4e0b\uff0c\u4e0d\u7136\u4e00\u4e2a\u8e0f\u7a7a\uff0c\u81ea\u5df1\u7eb5\u7136\u8eab\u624b\u9ad8\u5999\uff0c\u4f46\u8eab\u6cd5\u4e4b \u95f4\u5374\u4e5f\u96be\u514d\u56e0\u4e4b\u53d7\u5230\u4f24\u5bb3\u3002 \u3000\u3000\u4f46\u8fd9\u5c11\u5973\u7684\u4e24\u53ea\u7f57\u8896\uff0c\u6b64\u523b\u65bd\u5c55\u5f00\u6765\uff0c\u65e0\u5f02\u4e24\u4ef6\u7280\u5229\u7684\u5916\u95e8\u5175\u5203\uff0c\u52a8\u624b\u4e4b\u95f4\uff0c\u65e0\u7591\u8981\u5360 \u8bb8\u591a\u4fbf\u5b9c\u3002 \u7b2c\u516d\u7ae0\u3000\u65e0\u53cc\u7f57\u8896>> \u53e4\u9f99\u300a\u6708\u5f02\u661f\u90aa\u300b \u7b2c\u516d\u7ae0\u3000\u65e0\u53cc\u7f57\u8896 \u3000\u3000\u5353\u957f\u537f\u601c\u610f\u65b9\u751f\uff0c\u4eba\u5bb6\u4e24\u53ea\u7f57\u8896\u5df2\u81ea\u6325\u6765\uff0c\u5251\u7709\u5fae\u8f69\uff0c\u53cc\u638c\u4e00\u53cd\uff0c\u638c\u98ce\u4fbf\u81ea\u51b2\u5929\u800c\u8d77\uff0c \u547c\u5730\u5c06\u6f2b\u5929\u8896\u5f71\u6321\u4e86\u56de\u53bb\u3002 \u3000\u3000\u4f46\u8fd9\u7edd\u8272\u5c11\u5973\u7684\u4e24\u53ea\u7f57\u8896\uff0c\u957f\u513f\u8fbe\u4e08\uff0c\u98de\u821e\u4e4b\u95f4\u4e0d\u4f46\u62db\u5f0f\u8be1\u5f02\uff0c\u800c\u4e14\u6536\u62db\u53d8\u62db\u4e4b\u95f4\uff0c\u5947 \u8be1\u8fc5\u5feb\uff0c\u66f4\u662f\u6b66\u6797\u7f55\u89c2\u3002 \u3000\u3000\u5353\u957f\u537f\u6b64\u523b\u8eab\u624b\u5df2\u662f\u5c55\u5f00\uff0c\u53cc\u817f\u5c79\u7acb\u5982\u5c71\uff0c\u62db\u5f0f\u867d\u7136\u63a8\u52a8\u5f97\u8f83\u7f13\uff0c\u4f46\u4ece\u4ed6\u53cc\u638c\u4e2d\u5e26\u51fa\u7684 \u638c\u51e4\uff0c\u5374\u50cf\u662f\u4e00\u9053\u94dc\u58c1\uff0c\u5835\u5728\u90a3\u7edd\u8272\u5c11\u5973\u7684\u8896\u5f71\u524d\u9762\uff0c\u4f46\u4e00\u65f6\u4e4b\u95f4\uff0c\u8fd8\u662f\u5b88\u591a\u653b\u5c11\u3002 \u3000\u3000\u90a3\u5c11\u5973\u79cb\u6ce2\u6d41\u8f6c\uff0c\u671b\u5230\u57ce\u4e0b\u7684\u4e24\u6761\u6de1\u7ea2\u4eba\u5f71\uff0c\u6b64\u523b\u5df2\u81ea\u63a0\u81f3\u57ce\u811a\uff0c\u76ee\u5149\u7a81\u7136\u4e00\u51db\uff0c\u5de6\u624b \u7f57\u8896\u547c\u7684\u4e00\u58f0\uff0c\u6709\u5982\u4e00\u9053\u7ecf\u5929\u5f69\u8679\uff0c\u659c\u659c\u7684\u5212\u4e86\u4e2a\u534a\u5f27\uff0c\u7535\u4e5f\u4f3c\u7684\u5377\u5411\u5353\u957f\u537f\u7684\u53f3\u81c2\u3002 \u3000\u3000\u53f3\u624b\u7f57\u8896\u5374\u7a81\u7136\u4e00\u6536\uff0c\u4fbf\u53c8\u9f50\u8155\u53e0\u8d77\uff0c\u9732\u51fa\u4e00\u53cc\u83b9\u767d\u5982\u7389\u7684\u7ea4\u624b\u6765\uff0c\u5a07\u8eaf\u5fae\u62e7\uff0c\u7389\u8155\u7a0d \u6c89\uff0c\u9a84\u6307\u75be\u70b9\u5353\u957f\u537f\u80a9\u4e95\u3002 \u3000\u3000\u8fd9\u6837\u4e00\u6765\uff0c\u5979\u8eab\u6cd5\u4e5f\u968f\u4e4b\u5927\u53d8\uff0c\u987b\u77e5\u5979\u5de6\u8896\u957f\u6325\uff0c\u53f3\u624b\u77ed\u653b\uff0c\u4e00\u957f\u4e00\u77ed\uff0c\u8ddd\u79bb\u5dee\u7740\u8001 \u8fdc\uff0c\u4f46\u51fa\u62db\u4e4b\u95f4\uff0c\u5374\u672a\u56e0\u4e4b\u800c\u4e1d\u6beb\u4e0d\u4fbf\uff0c\u53ea\u89c1\u5979\u5a07\u8eaf\u5b9b\u8f6c\uff0c\u7a81\u800c\u8fdc\u653b\uff0c\u7a81\u800c\u8fd1\u53d6\uff0c\u8eab\u6cd5\u4e4b\u8be1 \u5f02\u3001\u5947\u5999\uff0c\u53c8\u8fdc\u5728\u65b9\u624d\u4e4b\u4e0a\u3002 \u3000\u3000\u5353\u957f\u537f\u4e00\u4ee3\u5927\u4fa0\u4e4b\u5b50\uff0c\u81ea\u51fa\u751f\u4e4b\u65e5\u59cb\uff0c\u4fbf\u53d7\u4e43\u7236\u4eb2\u718f\u7099\uff0c\u624e\u4e0b\u4e86\u6781\u597d\u7684\u6b66\u529f\u6839\u57fa\uff0c\u6b64\u540e \u66f4\u5f97\u5230\u6b66\u6797\u4e2d\u7684\u6cf0\u5c71\u5317\u6597\u4eba\u7269\u9752\u7750\uff0c\u7834\u4f8b\u6536\u4e3a\u95e8\u4e0b\uff0c\u5341\u5e74\u82e6\u7ec3\uff0c\u6210\u5c31\u5c82\u5728\u5c0f\u53ef\u3002 \u3000\u3000\u4e03\u5341\u5e74\u524d\uff0c\u6b66\u6797\u6b63\u503c\u6700\u4e3a\u7eb7\u4e71\u4e4b\u65f6\uff0c\u5176\u65f6\u6b63\u90aa\u4e24\u6d3e\uff0c\u9ad8\u624b\u8f88\u51fa\uff0c\u4e0d\u4f46\u6b66\u5f53\u3001\u5c11\u6797\u3001\u6606\u4ed1 \u8fd9\u51e0\u4e2a\u4e45\u5028\u6b66\u6797\u9738\u4e1a\u7684\u540d\u95e8\u6b63\u6d3e\uff0c\u4eba\u6750\u6d4e\u6d4e\uff0c\u90aa\u6d3e\u4e4b\u4e2d\uff0c\u66f4\u662f\u51fa\u4e86\u51e0\u4e2a\u5929\u4e0b\u4fa7\u65e5\u7684\u9b54\u5934\uff0c\u6380 \u52a8\u7740\u98ce\u6d6a\uff0c\u4f7f\u5f97\u6b66\u6797\u4e2d\u4eba\uff0c\u4e2a\u4e2a\u60f6\u7136\u96be\u5b89\u3002 \u3000\u3000\u800c\u53f8\u7a7a\u5c27\u65e5\u5374\u5c31\u5728\u8fd9\u65f6\u5019\uff0c\u51fa\u9053\u6c5f\u6e56\uff0c\u4e0d\u5230\u6570\u5e74\u65f6\u95f4\uff0c\u4e0d\u77e5\u505a\u8fc7\u591a\u5c11\u4ef6\u60ca\u5929\u52a8\u5730\u7684\u4e8b \u6765\uff0c\u638c\u6bd9\u5927\u6c49\u4e09\u51f6\uff0c\u521b\u5288\u5357\u8352\u4e00\u602a\uff0c\u5341\u4e8c\u8fde\u73af\u575e\u4e2d\uff0c\u5355\u8eab\u5b64\u5251\uff0c\u626b\u8361\u7fa4\u9b54\uff0c\u4f7f\u5f97\u4ed6\u548c\u5f53\u65f6\u6b66 \u6797\u4e2d\u53e6\u4e00\u4f4d\u9ad8\u624b\u53e4\u9cb2\uff0c\u88ab\u5929\u4e0b\u6b66\u6797\u5c0a\u4e3a\u5929\u5730\u53cc\u4ed9\u3002 \u3000\u3000\u8fd9\u5929\u4ed9\u53f8\u7a7a\u5c27\uff0c\u81ea\u759a\u5e72\u65f1\u5e74\u6740\u5b7d\u592a\u91cd\uff0c\u665a\u5e74\u4fbf\u6df1\u81ea\u6536\u655b\uff0c\u53ea\u662f\u4ed6\u751f\u5177\u5b64\u6d01\u4e4b\u6027\uff0c\u4e00\u751f\u4e4b \u4e2d\uff0c\u72ec\u6765\u72ec\u5f80\uff0c\u76f4\u5230\u665a\u5e74\uff0c\u975e\u4f46\u65e0\u59bb\u65e0\u5b50\uff0c\u5c31\u8fde\u5f92\u5f1f\uff0c\u90fd\u6ca1\u6709\u6536\u8fc7\u3002 \u3000\u3000\u4f46\u4ed6\u5728\u9ec4\u5c71\u59cb\u4fe1\u5cf0\u4e0b\uff0c\u56e0\u7a0d\u8fdf\u4e00\u6b65\uff0c\u800c\u4f7f\u4ed6\u6545\u53cb\u5730\u4ed9\u53e4\u9cb2\u4e4b\u5f92\u5353\u6d69\u7136\u592b\u5987\u53cc\u53cc\u6bd9\u547d\uff0c\u5fc3 \u91cc\u6b63\u6709\u4e9b\u81ea\u8d23\uff0c\u518d\u52a0\u4e0a\u5353\u957f\u537f\u8fc7\u4eba\u7684\u5929\u8d44\u3001\u81f3\u6027\u548c\u6027\u683c\uff0c\u7adf\u5f97\u5230\u8fd9\u4ece\u4e0d\u8f7b\u6613\u4f20\u4eba\u7684\u6b66\u6797\u5f02\u4eba \u7684\u9752\u7750\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u624d\u52a8\u4e86\u6536\u5f92\u4e4b\u5ff5\uff0c\u800c\u5929\u4ed9\u53f8\u7a7a\u8001\u4eba\u7684\u4e00\u8eab\u7edd\u6280\u4e5f\u56e0\u4e4b\u6709\u4f20\u3002 \u3000\u3000\u987b\u77e5\u8fd9\u53f8\u7a7a\u8001\u4eba\u6b66\u529f\u6e0a\u535a\u5982\u6d77\uff0c\u5929\u4e0b\u5404\u95e8\u5404\u6d3e\u7684\u6b66\u529f\uff0c\u4ed6\u90fd\u6709\u6240\u6df1\u730e\uff0c\u665a\u5e74\u6536\u5f92\uff0c\u81ea\u7136 \u7231\u62a4\u500d\u52a0\uff0c\u5353\u957f\u537f\u4e5f\u56e0\u4e4b\u4e0d\u4f46\u6b66\u529f\uff0c\u95e8\u6d3e\u4e4b\u77e5\u8bc6\uff0c\u4ea6\u662f\u8d85\u4eba\u4e00\u7b79\u3002 \u3000\u3000\u4f46\u6b64\u523b\u8fd9\u7edd\u8272\u5c11\u5973\u8fd9\u79cd\u8be1\u5f02\u7684\u8eab\u6cd5\uff0c\u5353\u957f\u537f\u641c\u904d\u8bb0\u5fc6\uff0c\u5374\u8fd8\u662f\u770b\u4e0d\u51fa\u5979\u7684\u6d3e\u522b\u6765\u3002 \u3000\u3000\u671d\u5915\u521d\u5347\uff0c\u4f7f\u5f97\u5979\u7684\u8eab\u5f62\uff0c\u770b\u6765\u6709\u5982\u4e00\u56e2\u6d41\u52a8\u7684\u706b\u7130\uff0c\u5353\u957f\u537f\u5fc3\u4e2d\u4e00\u52a8\uff0c\u7a81\u8d77\u957f\u5578\u4e00 \u58f0\uff0c\u8eab\u5f62\u6709\u5982\u795e\u9f99\u822c\u51b2\u5929\u800c\u8d77\u3002 \u3000\u3000\u90a3\u7edd\u8272\u5c11\u5973\u81fb\u9996\u5fae\u62ac\uff0c\u53ea\u89c1\u4ed6\u8fd9\u4e00\u62d4\u4e4b\u52bf\uff0c\u7adf\u7136\u9ad8\u8fbe\u4e09\u4e08\uff0c\u4ed6\u90a3\u51cc\u7a7a\u98d8\u821e\u7684\u8863\u886b\uff0c\u867d\u662f \u4e00\u7247\u9ed1\u8272\uff0c\u4f7f\u5979\u770b\u6765\u72b9\u5982\u4e00\u53ea\u7384\u9e64\uff0c\u4f46\u4ed6\u811a\u4e0b\u90a3\u53cc\u6731\u5c65\u4e4b\u5e95\u5374\u662f\u4ecd\u7136\u6d01\u767d\uff0c\u4ec5\u662f\u4e9b\u8bb8\u5c18\u8ff9\uff0c \u663e\u89c1\u4ed6\u8d70\u4e4b\u65f6\uff0c\u811a\u5e95\u5b8c\u5168\u8e0f\u5728\u5730\u9762\u4e0a\u7684\u65f6\u5019\u4e0d\u591a\u3002 \u3000\u3000\u5979\u82b3\u5fc3\u65b9\u624d\u6697\u9a87\uff0c\u4e0d\u77e5\u5bf9\u65b9\u6b64\u4e3e\uff0c\u85cf\u7740\u4ec0\u4e48\u5389\u5bb3\u7684\u540e\u7740\uff0c\u8eab\u5f62\u4e0d\u7981\u5fae\u5fae\u4e00\u4ef0\uff0c\u5411\u540e\u6ed1\u5f00 \u4e94\u5c3a\uff0c\u5168\u795e\u51dd\u6ce8\uff0c\u89c2\u5176\u540e\u53d8\u3002 \u3000\u3000\u54ea\u77e5\u5353\u957f\u537f\u8eab\u5f62\u5728\u7a7a\u4e2d\u6beb\u65e0\u53d8\u5316\uff0c\u5c31\u53c8\u98d8\u98d8\u843d\u4e86\u4e0b\u6765\uff0c\u90a3\u7edd\u8272\u5c11\u5973\u53c8\u81ea\u4e00\u6014\uff0c\u5374\u89c1\u4ed6\u90a3 \u82f1\u4fca\u7684\u5703\u65e5\u4e0a\uff0c\u6b64\u523b\u671b\u53bb\u6709\u5982\u5bd2\u51b0\u3002 \u3000\u3000\u6b64\u523b\u90a3\u4e24\u6761\u8fdc\u8fdc\u63a0\u6765\u7684\u6de1\u7ea2\u4eba\u5f71\uff0c\u5df2\u662f\u63a0\u81f3\u57ce\u811a\uff0c\u5374\u6b63\u662f\u90a3\u5728\u591a\u81c2\u795e\u5251\u4e91\u8c26\u5bff\u8bde\u4e4b\u65e5\u7fe9 \u7136\u800c\u6765\uff0c\u6280\u60ca\u7fa4\u8c6a\u7684\u4e00\u53cc\u7ea2\u88f3\u5c11\u5973\u3002 \u3000\u3000\u8fd9\u4e24\u4e2a\u7ea2\u88f3\u5c11\u5973\u4e00\u8def\u8ffd\u6765\uff0c\u867d\u7136\u7ed5\u4e86\u4e0d\u5c11\u5708\u5b50\uff0c\u4f46\u7ec8\u4e8e\u627e\u5230\u5979\u4eec\u8981\u6211\u7684\u4eba\uff0c\u5609\u5fae\u7684\u6668\u5149 \u4e2d\uff0c\u53ea\u89c1\u5979\u4eec\u9762\u8272\u5ae3\u7ea2\uff0c\u6709\u5982\u6843\u82b1\uff0c\u88f9\u5728\u90a3\u8f7b\u7eb1\u7ea2\u88f3\u4e4b\u4e2d\u7684\u9165\u80f8\uff0c\u4e5f\u4e0d\u4f4f\u8d77\u4f0f\u7740\uff0c\u663e\u89c1\u662f\u5954 \u9a70\u8fc7\u6025\u3002 \u3000\u3000\u4f46\u7a0d\u4e00\u9a7b\u8db3\uff0c\u5979\u4eec\u4fbf\u53c8\u56de\u590d\u8fc7\u6765\uff0c\u62ac\u773c\u4e00\u671b\u90a3\u8038\u7acb\u7684\u57ce\u5899\uff0c\u4e24\u4eba\u4e92\u671b\u4e00\u773c\uff0c\u7a81\u7136\u5e76\u80a9\u8dc3 \u8d77\uff0c\u7f57\u88d9\u98d8\u98d8\uff0c\u671b\u4e4b\u76f4\u5982\u4e00\u53cc\u5f69\u8776\u3002 \u3000\u3000\u4e24\u4eba\u9f50\u9f50\u63a0\u81f3\u4e24\u4e08\uff0c\u773c\u770b\u52bf\u9053\u5c06\u7aed\uff0c\u5de6\u4fa7\u5c11\u5973\u7a81\u7136\u4f38\u51fa\u53f3\u638c\uff0c\u8f7b\u8f7b\u4e00\u6309\u53f3\u4fa7\u5c11\u5973\u7684\u5de6 \u80a9\uff0c\u5a07\u8eaf\u4fbf\u53c8\u501f\u52bf\u800c\u8d77\uff0c\u53f3\u4fa7\u5c11\u5973\u5374\u843d\u5230\u5730\u4e0a\u3002 \u3000\u3000\u5de6\u4fa7\u5c11\u5973\u51cc\u7a7a\u501f\u52bf\uff0c\u63a0\u4e0a\u57ce\u5899\uff0c\u79cb\u6ce2\u4e00\u8f6c\uff0c\u89c1\u5230\u81ea\u5df1\u7684\u4e3b\u4eba\u8f7b\u8f7b\u4f38\u624b\u5411\u81ea\u5df1\u6253\u4e86\u4e2a\u624b \u52bf\uff0c\u4fbf\u4e5f\u5fae\u4e00\u9894\u9996\uff0c\u4e00\u9762\u4f38\u624b\u5165\u6000\uff0c\u4ece\u6000\u4e2d\u53d6\u51fa\u4e00\u6761\u6781\u957f\u7684\u7ea2\u8272\u5f69\u7d22\u6765\uff0c\u5782\u4e0b\u4e00\u7aef\u3002 \u3000\u3000\u57ce\u4e0b\u90a3\u5c11\u5973\u5a07\u8eaf\u4e00\u957f\uff0c\u51cc\u7a7a\u6293\u4f4f\u90a3\u5f69\u5e26\uff0c\u6709\u5982\u60ca\u9e3f\u822c\u8dc3\u4e0a\u57ce\u5899\u3002 \u3000\u3000\u5353\u957f\u537f\u957f\u5578\u800c\u8d77\uff0c\u7fe9\u7136\u800c\u843d\uff0c\u76ee\u5149\u68ee\u51b7\u5730\u5728\u90a3\u7edd\u8272\u5c11\u5973\u8eab\u4e0a\u4e00\u626b\uff0c\u51b7\u51b7\u5730\u9053\uff1a\u201c\u6e29\u5982\u7389 \u662f\u4f60\u7684\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u539f\u6765\u4ed6\u65b9\u624d\u641c\u904d\u8bb0\u5fc6\uff0c\u5374\u4ecd\u770b\u4e0d\u51fa\u8fd9\u7edd\u8272\u5c11\u5973\u7684\u8eab\u6cd5\uff0c\u4e0d\u7981\u5927\u4e3a\u60ca\u8be7\u3002 \u3000\u3000\u4ed6\u6df1\u77e5\u81ea\u5df1\u7684\u5e08\u7236\u4e4b\u6e0a\u535a\uff0c\u90a3\u4e48\u6b64\u4e8b\u53ea\u6709\u4e00\u4e2a\u89e3\u91ca\uff0c\u5c31\u662f\u8fd9\u5c11\u5973\u7684\u8fd9\u79cd\u8be1\u5f02\u7684\u8eab\u6cd5\u662f\u67d0 \u4e00\u4e2a\u6b66\u6797\u9ad8\u624b\u8fd1\u5e74\u624d\u521b\u51fa\u6765\u6765\u3002 \u3000\u3000\u82e6\u601d\u4e4b\u4e0b\uff0c\u4ed6\u89c1\u5230\u8fd9\u5c11\u5973\u7684\u4e00\u8eab\u7ea2\u88f3\uff0c\u5341\u5e74\u4e4b\u524d\uff0c\u9ec4\u5c71\u59cb\u4fe1\u5cf0\u4e0b\u90a3\u51c4\u60e8\u7684\u4e00\u5e55\uff0c\u7a81\u53c8\u7535 \u4e5f\u4f3c\u5730\u4ece\u4ed6\u5fc3\u91cc\u95ea\u8fc7\u3002 \u3000\u3000\u90a3\u4e00\u8863\u7ea2\u88f3\u3001\u9ad8\u633d\u4e91\u9b13\u7684\u5947\u4e11\u5987\u4eba\uff0c\u548c\u90a3\u7f8e\u4e3d\u7684\u5c0f\u5973\u5b69\u5b50\u7684\u8eab\u5f62\u9762\u5bb9\u4fbf\u53c8\u5386\u5386\u5982\u5728\u773c \u524d\u3002\u4ed6\u4eff\u4f5b\u53c8\u89c1\u5230\u90a3\u7ea2\u88f3\u5947\u4e11\u5987\u4eba\u2014\u2014\u540e\u6765\u4ed6\u5df2\u77e5\u9053\u90a3\u5c31\u662f\u4e11\u4eba\u6e29\u5982\u7389\uff0c\u6b63\u4f38\u51fa\u5979\u90a3\u5e72\u67af\u7684 \u624b\u638c\uff0c\u51b7\u9177\u5730\u6740\u6b7b\u4e86\u81ea\u5df1\u7684\u53cc\u4eb2\uff0c\u4e8e\u662f\u7728\u76ee\u4e4b\u95f4\uff0c\u4ed6\u53ea\u89c9\u5fc3\u80f8\u4e4b\u4e2d\uff0c\u70ed\u8840\u7ffb\u6d8c\uff0c\u4fbf\u81ea\u957f\u5578\u4e00 \u58f0\uff0c\u51b2\u5929\u62d4\u8d77\u3002 \u3000\u3000\u90a3\u7edd\u8272\u5c11\u5973\u95fb\u8a00\u4e5f\u4e0d\u7981\u5fae\u5fae\u4e00\u6014\uff0c\u79cb\u6ce2\u8f7b\u8f6c\uff0c\u770b\u5230\u81ea\u5df1\u7684\u5e2e\u624b\u5df2\u81ea\u63a0\u4e0a\u57ce\u6765\uff0c\u8f7b\u8f7b\u4f38\u51fa \u7389\u638c\uff0c\u62e2\u4e86\u62e2\u4e91\u9b13\uff0c\u5374\u4e58\u4fbf\u6253\u4e86\u4e2a\u624b\u52bf\uff0c\u7a81\u53c8\u5a07\u7b11\u8d77\u6765\u3002 \u3000\u3000\u5353\u957f\u537f\u76ee\u5149\u77ac\u4e5f\u4e0d\u77ac\u5730\u51dd\u6ce8\u5728\u5bf9\u9762\u7684\u8fd9\u5c11\u5973\u7684\u8eab\u4e0a\uff0c\u867d\u7136\u5fc3\u5207\u4eb2\u4ec7\uff0c\u795e\u667a\u7565\u6709\u6df7\u4e71\uff0c\u4f46 \u50cf\u4ed6\u8fd9\u79cd\u5185\u5916\u517c\u4fee\u7684\u6b66\u6797\u9ad8\u624b\uff0c\u542c\u89c9\u6bd5\u7adf\u4e0d\u540c\u51e1\u54cd\uff0c\u8fd9\u79cd\u60c5\u5f62\u4e01\u201c\uff0c\u4ed6\u8fd8\u662f\u5bdf\u89c9\u5230\u8eab\u540e\u53c8\u6709 \u4eba\u6765\u3002\u4f46\u662f\u4ed6\u76ee\u5149\u5374\u5e76\u672a\u56e0\u4e4b\u800c\u4ece\u90a3\u7edd\u8272\u5c11\u5973\u8eab\u4e0a\u79fb\u5f00\uff0c\u53ea\u89c1\u5979\u90a3\u5a07\u5a9a\u7684\u9762\u76ee\u4e0a\uff0c\u7a81\u53c9\u6cdb\u51fa \u6625\u82b1\u4e00\u822c\u7684\u7b11\u5bb9\uff0c\u5a07\u7b11\u7740\u9053\uff1a\u201c\u4f60\u8ba4\u5f97\u6e29\u5982\u7389\u5417\uff1f\u201d \u3000\u3000\u7f13\u7f13\u81ea\u9b13\u89d2\u653e\u4e0b\u7389\u624b\uff0c\u53c8\u9053\uff1a\u201c\u4f60\u95ee\u6211\u8fd9\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5353\u957f\u537f\u5251\u7709\u4e00\u6311\uff0c\u5389\u58f0\u9053\uff1a\u201c\u5728\u4e0b\u65b9\u624d\u6240\u95ee\u4e4b\u4e8b\uff0c\u4f60\u82e5\u4e0d\u597d\u597d\u7b54\u590d\uff0c\u5c31\u83ab\u602a\u5728\u4e0b\u4e0d\u5ba2\u6c14 \u4e86\u3002\u201d \u3000\u3000\u90a3\u7edd\u8272\u5c11\u5973\u7f57\u8896\u5fae\u626c\uff0c\u54af\u54af\u4e00\u9635\u5a07\u7b11\uff0c\u6307\u7740\u5353\u957f\u537f\u9053\uff1a\u201c\u4f60\u8fd9\u4eba\u5012\u51f6\u5f97\u5f88\uff0c\u4f60\u95ee\u6211\u7684 \u8bdd\uff0c\u6211\u4e0d\u7b54\u590d\u53c8\u600e\u6837\u2014\u2014\u201d\u5979\u8bdd\u58f0\u4e00\u987f\uff0c\u672c\u6765\u5a07\u7b11\u5982\u753b\u7684\u9762\u9765\u7a81\u7136\u53c8\u4e00\u6c89\uff0c\u51b7\u51b7\u9053\uff1a\u53fc\u3001 \u743c\u3001\u5c0f\u73b2\uff0c\u4f60\u4eec\u7094\u66ff\u6211\u628a\u8fd9\u53ae\u6293\u4e0b\u6765\u3002\u201c\u5353\u957f\u537f\u51b7\u7b11\u4e00\u58f0\uff0c\u8eab\u5f62\u7a81\u53c8\u51b2\u5929\u62d4\u8d77\uff0c\u987b\u77e5\u4ed6\u6c5f\u6e56 \u5386\u7ec3\u867d\u5c11\uff0c\u5374\u662f\u806a\u660e\u7edd\u9876\u4e4b\u4eba\uff0c\u65e9\u5c31\u77e5\u9053\u8eab\u540e\u7684\u6765\u4eba\uff0c\u548c\u8fd9\u7edd\u8272\u5c11\u5973\u5fc5\u662f\u4e00\u8def\uff0c\u662f\u4ee5\u4ed6\u9762\u4e0a \u867d\u4ecd\u4e00\u4e1d\u672a\u53d8\uff0c\u6697\u4e2d\u5374\u65e9\u6709\u9632\u5907\u3002\u4ed6\u76ee\u5149\u4e00\u5782\uff0c\u679c\u7136\u770b\u5230\u4e24\u6761\u7ea2\u886b\u4eba\u5f71\uff0c\u7535\u4e5f\u4f3c\u5730\u4ece\u4ed6\u8eab\u540e \u63a0\u6765\uff0c\u4f46\u6b64\u523b\u4ed6\u8eab\u5f62\u5df2\u9ad8\u9ad8\u5728\u4e0a\uff0c\u8fd9\u4e24\u4eba\u81ea\u7136\u6251\u4e86\u4e2a\u7a7a\u3002\u90a3\u7edd\u8272\u5c11\u5973\u67f3\u7709\u4e00\u7ad6\uff0c\u51b7\u7b11\u9053\uff1a \u201c\u4f60\u4e0a\u5f97\u53bb\u96be\u9053\u522b\u4eba\u5c31\u4e0a\u4e0d\u53bb\uff1f\u201d \u3000\u3000\u5a07\u8eaf\u4e00\u626d\uff0c\u4fbf\u4e5f\u51b2\u5929\u62d4\u8d77\uff0c\u201c\u547c\u3001\u547c\u201d\u4e24\u58f0\uff0c\u4e24\u6761\u7f57\u8896\uff0c\u53c8\u81ea\u6325\u51fa\u3002 \u3000\u3000\u8fd9\u79cd\u5947\u8be1\u7684\u6b66\u529f\uff0c\u867d\u8131\u80ce\u4e8e\u6b66\u5f53\u7edd\u6280\u6d41\u4e91\u98de\u8896\uff0c\u4f46\u53c8\u548c\u8fd9\u79cd\u6b63\u5b97\u5185\u5bb6\u7edd\u6280\u6709\u4e9b\u4e0d\u540c\uff0c\u5374 \u539f\u6765\u6b63\u662f\u90a3\u7ea2\u8863\u4ed9\u5b50\u6e29\u5982\u7389\u665a\u5e74\u82e6\u7814\u800c\u6210\u7684\u7edd\u6280\u2014\u2014\u65e0\u53cc\u7f57\u8896\u3002 \u3000\u3000\u5353\u957f\u537f\u81ea\u7136\u4e0d\u4f1a\u77e5\u9053\u8fd9\u79cd\u8eab\u6cd5\u7684\u7531\u6765\uff0c\u4f46\u6b64\u523b\u5374\u5df2\u77e5\u9053\u8fd9\u4e09\u4e2a\u7ea2\u88f3\u5c11\u5973\u5fc5\u5b9a\u548c\u81ea\u5df1\u7684\u6740 \u7236\u4ec7\u4eba\u6709\u7740\u4e0d\u540c\u5bfb\u5e38\u7684\u5173\u7cfb\u3002 \u3000\u3000\u4ed6\u8eab\u5f62\u51cc\u7a7a\u4e00\u6298\uff0c\u7a81\u7136\u53cc\u638c\u9f50\u51fa\uff0c\u4e94\u6307\u5982\u94a9\uff0c\u7535\u4e5f\u4f3c\u7684\u6293\u4f4f\u8fd9\u4e24\u53cc\u7f57\u8896\uff0c\u53e3\u4e2d\u731b\u201c\u5543\u201d \u4e00\u58f0\uff0c\u624b\u8155\u731b\u7136\u4e00\u6296\u3001\u4e00\u626f\u3002 \u3000\u3000\u53ea\u542c\u201c\u5636\u201d\u7684\u4e00\u58f0\uff0c\u90a3\u4e24\u53cc\u7edb\u7ea2\u8863\u8896\uff0c\u7adf\u786c\u751f\u751f\u88ab\u4ed6\u4e00\u6296\u4e24\u534a\uff0c\u9732\u51fa\u90a3\u7edd\u8272\u5c11\u5973\u6709\u5982\u7389 \u85d5\u822c\u7684\u534a\u6bb5\u624b\u81c2\u6765\u3002 \u3000\u3000\u90a3\u7edd\u8272\u5c11\u5973\u5624\u549b\u4e00\u58f0\uff0c\u7389\u5bb9\u5927\u53d8\uff0c\u8eab\u5f62\u53c8\u843d\u5728\u57ce\u5899\u4e0a\uff0c\u5353\u957f\u537f\u624b\u638c\u4e00\u626c\uff0c\u5c06\u624b\u4e2d\u7684\u4e24\u622a \u65ad\u8896\u201c\u547c\u201d\u7684\u629b\u4e86\u5f00\u53bb\uff0c\u8eab\u5f62\u4ea6\u968f\u5373\u98d8\u4e0b\u3002 \u3000\u3000\u4ed6\u7528\u5c3d\u5168\u529b\uff0c\u4e00\u62db\u5f97\u624b\uff0c\u4fbf\u518d\u4e5f\u4e0d\u80af\u7ed9\u5979\u5598\u606f\u7684\u673a\u4f1a\uff0c\u7728\u773c\u4e4b\u95f4\uff0c\u4fbf\u53c8\u653b\u51fa\u6570\u638c\uff0c\u4e0d\u4f46 \u638c\u638c\u542b\u8574\u5185\u529b\uff0c\u800c\u4e14\u7740\u7740\u90fd\u662f\u653b\u5411\u8981\u5bb3\u3002 \u3000\u3000\u90a3\u7edd\u8272\u5c11\u5973\u6b64\u523b\u7389\u5bb9\u82cd\u767d\uff0c\u67f3\u8170\u8fde\u95ea\uff0c\u907f\u5f00\u4ed6\u8fd9\u6fc0\u5389\u65e0\u5339\u7684\u6570\u638c\uff0c\u82b3\u5fc3\u4e4b\u4e2d\u60ca\u6012\u4ea4\u96c6\uff0c \u5979\u4e00\u751f\u4e4b\u4e2d\uff0c\u4ece\u672a\u53d7\u8fc7\u6709\u5982\u6b64\u523b\u4e4b\u632b\u8fb1\uff0c\u5374\u53c8\u4e0d\u77e5\u9053\u8fd9\u5c11\u5e74\u4e3a\u4ec0\u4e48\u8981\u5982\u6b64\u5bf9\u4ed8\u81ea\u5df1\u3002 \u3000\u3000\u5979\u5a07\u7eb5\u5df2\u60ef\uff0c\u4ece\u6765\u4e0d\u77e5\u6709\u4eba\uff0c\u53ea\u77e5\u6709\u5df1\uff0c\u6b64\u523b\u53d7\u4e86\u8fd9\u79cd\u632b\u8fb1\uff0c\u54ea\u91cc\u8fd8\u6709\u5fc3\u601d\u53bb\u60f3\u522b\u7684\uff0c \u5a07\u53f1\u8fde\u58f0\uff0c\u7389\u638c\u8fde\u626c\uff0c\u7728\u773c\u4e4b\u95f4\u4fbf\u548c\u5353\u957f\u537f\u62c6\u4e86\u5341\u6570\u62db\u3002 \u3000\u3000\u90a3\u4e24\u4e2a\u7ea2\u88f3\u5c11\u5973\u5c0f\u743c\u5c0f\u73b2\uff0c\u76ee\u4e2d\u4ea6\u81ea\u5404\u73b0\u60ca\u9a87\u4e4b\u8272\uff0c\u5979\u4eec\u4e00\u5411\u8ba4\u4e3a\u81ea\u5df1\u5c0f\u59d0\u7684\u6b66\u529f\u5929\u4e0b \u65e0\u53cc\uff0c\u5374\u518d\u4e5f\u60f3\u4e0d\u5230\u8fbe\u5e74\u8f7b\u800c\u82f1\u4fca\u7684\u5c11\u5e74\uff0c\u7adf\u6709\u5982\u6b64\u9ad8\u7684\u6b66\u529f\uff0c\u7adf\u628a\u5979\u7684\u65e0\u53cc\u7f57\u8896\u786c\u751f\u751f\u626f \u4e86\u4e0b\u6765\u3002 \u3000\u3000\u5979\u4eec\u7a0d\u5fae\u4e00\u6014\uff0c\u5404\u81ea\u5a07\u53f1\u4e00\u58f0\uff0c\u4e5f\u81ea\u5c55\u5a07\u8eaf\uff0c\u626c\u7389\u638c\uff0c\u4e00\u8fde\u6570\u638c\uff0c\u5411\u5353\u957f\u537f\u62cd\u4e86\u8fc7\u53bb\uff0c \u7728\u773c\u4e4b\u95f4\uff0c\u4f46\u89c1\u90a3\u4e09\u6761\u4eba\u5f71\u5982\u706b\u7130\uff0c\u6f2b\u5929\u800c\u8d77\uff0c\u800c\u4ed6\u4eec\u90a3\u79cd\u6fc0\u5389\u7684\u638c\u98ce\uff0c\u4e5f\u4f7f\u5f7c\u6b64\u8eab\u4e0a\u7684\u8863 \u8882\u4e0d\u65ad\u7684\u98d8\u821e\u8d77\u6765\u3002 \u3000\u3000\u5979\u4eec\u7684\u8eab\u5f62\u867d\u7136\u52a8\u5982\u6d41\u4e91\uff0c\u5353\u957f\u537f\u5374\u662f\u9759\u5982\u5c71\u5cb3\uff0c\u50cf\u4e00\u5ea7\u7384\u51b0\u4f3c\u7684\uff0c\u5c79\u7acb\u5728\u8fd9\u7247\u706b\u7130\u4e4b \u4e2d\u3002 \u3000\u3000\u4ed6\u4eec\u539f\u5148\u672c\u6765\u81ea\u6043\u8eab\u624b\uff0c\u5404\u6709\u8f7b\u89c6\u4e4b\u610f\uff0c\u4f46\u6b64\u523b\u4ea4\u624b\u4e4b\u540e\uff0c\u5374\u4e0d\u7981\u5404\u81ea\u5fc3\u6709\u6212\u60d5\uff0c\u90a3\u7edd \u8272\u5c11\u5973\u65b9\u624d\u867d\u88ab\u5353\u957f\u537f\u626f\u65ad\u8863\u8896\uff0c\u4f46\u90a3\u53ea\u4e0d\u8fc7\u662f\u56e0\u4e3a\u5979\u51fa\u624b\u4e4b\u95f4\uff0c\u7565\u6709\u758f\u5ffd\uff0c\u800c\u4e14\u4e5f\u4e07\u4e07\u60f3 \u4e0d\u5230\u5353\u957f\u537f\u8eab\u5728\u7a7a\u4e2d\uff0c\u8fd8\u80fd\u65bd\u51fa\u5185\u529b\u3002 \u3000\u3000\u6b64\u523b\u5979\u8b66\u60d5\u4e4b\u5fc3\u4e00\u8d77\uff0c\u51fa\u624b\u867d\u4ecd\u7136\u5947\u8be1\u800c\u72e0\u8fa3\uff0c\u4f46\u5374\u663e\u89c1\u5f97\u8f83\u5148\u524d\u8c28\u614e\uff0c\u518d\u52a0\u4e0a\u90a3\u4e24\u4e2a \u7ea2\u88f3\u5c11\u5973\u5c0f\u743c\u3001\u5c0f\u73b2\uff0c\u8eab\u5982\u98de\u71d5\uff0c\u88f3\u5982\u98d8\u7d6e\uff0c\u529f\u529b\u867d\u4e0d\u6df1\uff0c\u62db\u5f0f\u5374\u9887\u9ad8\uff0c\u90a3\u5353\u957f\u537f\u52a8\u529b\u4e4b \u6df1\uff0c\u867d\u5df2\u5982\u7eaf\u9752\u4e4b\u7089\u706b\uff0c\u4f46\u6b64\u523b\u4ee5\u4e00\u654c\u4e09\uff0c\u5374\u672a\u89c1\u5360\u5f97\u4e0a\u98ce\u3002 \u3000\u3000\u671d\u9732\u5c06\u4e8e\uff0c\u65ed\u65e5\u5df2\u5347\uff0c\u9053\u9053\u9633\u5149\uff0c\u5982\u652f\u652f\u91d1\u7bad\uff0c\u4ece\u4e1c\u65b9\u4e91\u5c42\u7684\u7a7a\u9699\u4e2d\u5c04\u4e86\u51fa\u6765\uff0c\u65b0\u7684\u4e00 \u65e5\uff0c\u5df2\u7ecf\u6765\u4e34\uff0c\u4f46\u5728\u8fd9\u65b0\u7684\u65e5\u5b50\u91cc\uff0c\u6b66\u6797\u4e2d\u53c8\u5c06\u751f\u51fa\u4ec0\u4e48\u65b0\u7684\u53d8\u6545\u5462\uff1f \u3000\u3000\u5353\u957f\u537f\u8eab\u5f62\u5982\u5c71\uff0c\u53cc\u638c\u5982\u7535\uff0c\u867d\u7136\u88ab\u56f4\u5728\u8fd9\u4e09\u4e2a\u7ea2\u88f3\u5c11\u5973\u7684\u6f2b\u5929\u8896\u5f71\u638c\u98ce\u4e4b\u4e2d\uff0c\u5374\u6ca1\u6709 \u73b0\u51fa\u4e1d\u6beb\u4e00\u4e9b\u8d25\u8c61\u3002 \u3000\u3000\u53ef\u662f\u4ea4\u624b\u4e00\u4e45\uff0c\u4ed6\u5fc3\u91cc\u5374\u4e0d\u7981\u6709\u4e9b\u607c\u8e81\uff0c\u6697\u53f9\u4e00\u58f0\uff0c\u5fd6\u9053\uff1a\u201c\u8fd9\u4e09\u4e2a\u5973\u5b50\u82e5\u771f\u662f\u90a3\u4e11\u4eba \u6e29\u5982\u7389\u7684\u95e8\u4e0b\uff0c\u6b64\u523b\u6211\u90fd\u4e0d\u80fd\u53d6\u80dc\uff0c\u8fd8\u6709\u4ec0\u4e48\u5e0c\u671b\u80dc\u5f97\u4e86\u5979\u4eec\u7684\u5e08\u7236\uff0c\u8fd8\u8c08\u4ec0\u4e48\u62a5\u4ec7\uff1f\u201d \u3000\u3000\u5ff5\u5934\u8f6c\u5230\u8fd9\u91cc\uff0c\u4e0d\u7981\u53c8\u81ea\u65a5\u8d77\u6765\uff1a\u201c\u5509\uff0c\u5e08\u7236\u53eb\u6211\u518d\u8fc7\u4e09\u5e74\u624d\u80fd\u4e0b\u5c71\uff0c\u6211\u6094\u4e0d\u8be5\u6ca1\u6709\u542c \u4ed6\u8001\u4eba\u5bb6\u7684\u8bdd\u2014\u2014\u201d\u4ed6\u5fc3\u91cc\u8fd9\u4e00\u81ea\u8d23\u81ea\u6028\uff0c\u8eab\u624b\u81ea\u7136\u5c31\u6162\u4e86\u4e0b\u6765\uff0c\u90a3\u7edd\u8272\u4e3d\u4eba\u5a07\u53f1\u4e00\u58f0\uff0c\u4e00 \u53cc\u83b9\u83b9\u5982\u7389\u7684\u624b\u638c\u5ffd\u7136\u5728\u90a3\u53cc\u7834\u8896\u4e2d\u4e00\u4f38\u4e00\u7f29\uff0c\u8f7b\u98d8\u98d8\u7684\u62cd\u51fa\u4e94\u638c\uff0c\u51fa\u638c\u65f6\u867d\u6709\u5148\u540e\uff0c\u638c\u5230 \u65f6\u5374\u6d51\u5982\u4e00\u4f53\u3002\u5353\u957f\u537f\u76ee\u4e00\u77ac\uff0c\u53ea\u89c1\u4e94\u53ea\u4fcf\u751f\u751f\u7684\u638c\u5f71\u2014\u2014\u51e0\u4e4e\u5728\u540c\u4e00\u5239\u90a3\u95f4\u5411\u81ea\u5df1\u524d\u80f8\u3001 \u53cc\u80a9\u62cd\u6765\uff0c\u62db\u6570\u4e4b\u5201\u94bb\u8be1\u5f02\uff0c\u524d\u6240\u672a\u89c1\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u4e0d\u7981\u5fae\u5fae\u4e00\u60ca\uff0c\u811a\u8ddf\u534a\u65cb\uff0c\u659c\u8eab\u4e00\u8ba9\uff0c\u54ea\u77e5\u773c\u524d\u7a81\u53c8\u638c\u98ce\u5927\u4f5c\uff0c\u90a3\u5c0f\u743c\u5c0f\u73b2\u7684\u56db \u53ea\u7389\u638c\uff0c\u4e5f\u5df2\u62cd\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u987b\u77e5\u9ad8\u624b\u8fc7\u62db\uff0c\u5dee\u4e4b\u6beb\u5398\uff0c\u4fbf\u53ef\u5931\u4e4b\u5343\u91cc\uff0c\u5353\u957f\u537f\u65b9\u624d\u5fc3\u795e\u5c1a\u758f\uff0c\u6b64\u523b\u4fbf\u8ba9\u5bf9\u65b9\u5360\u4e86\u5148 \u673a\uff0c\u773c\u89c1\u5f97\u56db\u9762\u516b\u65b9\u90fd\u662f\u4eba\u5bb6\u7684\u638c\u5f71\uff0c\u8fd9\u4e9b\u638c\u5f71\u4e5f\u90fd\u5df2\u582a\u582a\u62cd\u5230\u81ea\u5df1\u7684\u8eab\u4e0a\u3002 \u3000\u3000\u90a3\u7edd\u8272\u4e3d\u4eba\u5634\u89d2\u65b9\u663e\u4e00\u4e1d\u5f97\u610f\u7684\u7b11\u5bb9\uff0c\u54ea\u77e5\u5353\u957f\u537f\u7a81\u7136\u80a9\u5934\u5fae\u584c\uff0c\u624b\u8155\u5411\u4e0a\u4e00\u6296\uff0c\u4ed6\u4e24 \u53ea\u5bbd\u5927\u7684\u8863\u8896\uff0c\u5c31\u7a81\u7136\u515c\u4e86\u4e0a\u6765\uff0c\u5e26\u7740\u51cc\u5389\u7684\u98ce\u58f0\uff0c\u201c\u547c\u201d\u7684\u5212\u4e86\u4e2a\u5708\u5b50\u3002 \u3000\u3000\u90a3\u7edd\u8272\u4e3d\u4eba\u7b11\u5bb9\u987f\u655b\uff0c\u67f3\u8170\u4e00\u6298\uff0c\u500f\u7136\u9000\u4e86\u4e09\u6b65\uff0c\u5374\u542c\u5c0f\u743c\u5c0f\u73b2\u540c\u58f0\u5a07\u547c\uff0c\u539f\u672a\u5979\u4eec\u64a4 \u62db\u4e0d\u53ca\uff0c\u7389\u8155\u88ab\u8863\u8896\u626b\u7740\u4e00\u70b9\uff0c\u53ea\u89c9\u5b9b\u5982\u5200\u5212\uff0c\u75db\u5f7b\u5fc3\u9aa8\u3002 \u3000\u3000\u5353\u957f\u537f\u51b7\u7b11\u4e00\u58f0\uff0c\u84e6\u7136\u53cc\u624b\u4ece\u8896\u4e2d\u4f38\u51fa\uff0c\u4ed6\u4ee5\u4e00\u62db\u6b63\u5b97\u7684\u6d41\u4e91\u98de\u8896\u53c8\u590d\u62a2\u5f97\u5148\u673a\uff0c\u811a\u6b65 \u5fae\u9519\uff0c\u6b63\u5f85\u5411\u90a3\u7edd\u8272\u4e3d\u4eba\u62cd\u53bb\uff0c\u54ea\u77e5\u57ce\u4e0b\u7a81\u7136\u4f20\u6765\u8f70\u7136\u4e00\u9635\u957f\u58f0\uff0c\u4e00\u4e2a\u4e2d\u6c14\u9887\u8db3\u7684\u82cd\u8001\u58f0\u97f3 \u5728\u4e0b\u9762\u559d\u9053\uff1a\u201c\u957f\u537f\uff0c\u597d\u4fca\u7684\u529f\u592b\uff01\u201d \u3000\u3000\u5353\u957f\u537f\u4e0d\u7981\u5fae\u5fae\u4e00\u6014\uff0c\u53cc\u638c\u659c\u6325\uff0c\u5b54\u96c0\u5f00\u5c4f\uff0c\u201c\u5530\u201d\u7684\u5411\u5c0f\u743c\u3001\u5c0f\u73b2\u4ee5\u53ca\u90a3\u7edd\u8272\u4e3d\u4eba\u5404 \u4e2a\u62cd\u51fa\u4e00\u638c\uff0c\u8eab\u5f62\u5fae\u504f\uff0c\u76ee\u5149\u4e0b\u626b\uff0c\u5374\u89c1\u57ce\u4e0b\u7adf\u7ad9\u7740\u4e00\u7247\u9ed1\u538b\u538b\u7684\u4eba\u7fa4\uff0c\u4e00\u4e2a\u6ee1\u5934\u767d\u53d1\u7684\u8001 \u8005\uff0c\u6392\u4f17\u5f53\u5148\u800c\u7acb\uff0c\u5374\u6b63\u662f\u90a3\u591a\u81c2\u795e\u5251\u4e91\u8c26\u3002 \u3000\u3000\u539f\u6765\u5353\u957f\u537f\u548c\u8fd9\u4e09\u4e2a\u7ea2\u88f3\u5c11\u5973\u5728\u57ce\u5934\u4e0a\u6fc0\u6218\uff0c\u638c\u98ce\u7ea2\u5f71\uff0c\u81ea\u7136\u6781\u4e3a\u663e\u76ee\uff0c\u6709\u4eba\u8fdc\u8fdc\u770b \u89c1\uff0c\u5c31\u5954\u6765\u770b\u70ed\u95f9\u3002\u4e91\u8c26\u7236\u5b50\u5e2e\u7740\u5feb\u5200\u4f1a\u7684\u5feb\u5200\u4e01\u4e03\u6599\u7406\u4e86\u4e00\u4e0b\u5584\u540e\uff0c\u672c\u5728\u7740\u6025\u7740\u5353\u957f\u537f\u7684 \u4e0b\u843d\uff0c\u4e00\u542c\u57ce\u4e0a\u6709\u4eba\u6fc0\u6597\uff0c\u5c31\u98de\u4e5f\u4f3c\u7684\u5954\u4e86\u8fc7\u6765\uff0c\u679c\u7136\u770b\u5230\u5353\u957f\u537f\u7ad9\u5728\u4e00\u4e2a\u57ce\u579b\u4e0a\uff0c\u548c\u4e09\u4e2a \u8eab\u5f62\u6d41\u8d70\u7684\u7ea2\u88f3\u5c11\u5973\u5728\u52a8\u624b\u3002 \u3000\u3000\u8fd9\u65f6\u6b63\u5f53\u5353\u957f\u537f\u53cc\u8896\u62c2\u9000\u4e86\u8fd9\u4e09\u4e2a\u7ea2\u88f3\u5c11\u5973\u7684\u653b\u52bf\uff0c\u4e91\u8c26\u4e00\u89c1\u6545\u4eba\u4e4b\u5b50\u6b66\u529f\u5982\u6b64\uff0c\u7981\u4e0d \u4f4f\u9ad8\u58f0\u559d\u8d77\u5f69\u6765\u3002 \u3000\u3000\u4e34\u5b89\u57ce\u4e2d\uff0c\u6b66\u6797\u8c6a\u58eb\u4e91\u96c6\uff0c\u6b64\u523b\u8d76\u6765\u770b\u70ed\u95f9\u7684\uff0c\u81ea\u7136\u5927\u534a\u662f\u7ec3\u5bb6\u5b50\uff0c\u770b\u5230\u5353\u957f\u537f\u8fd9\u4e00\u62db \u201c\u6d41\u4e91\u98de\u8896\u201d\u81ea\u7136\u4e5f\u90fd\u8bc6\u8d27\u3002 \u3000\u3000\u8fd9\u4e00\u58f0\u559d\u5f69\u58f0\uff0c\u53eb\u5f97\u5353\u957f\u537f\u7cbe\u795e\u4e00\u632f\uff0c\u53e3\u89d2\u542b\u7b11\uff0c\u624b\u638c\u7531\u5916\u800c\u5185\uff0c\u201c\u547c\u201d\u7684\u53c8\u5212\u4e86\u4e00\u4e2a \u534a\u5708\uff0c\u5f53\u80f8\u4e00\u5408\uff0c\u7531\u5408\u800c\u5206\uff0c\u7a81\u53c8\u6325\u4e86\u51fa\u53bb\uff0c\u521a\u597d\u548c\u90a3\u7edd\u8272\u5c11\u5973\u51fb\u6765\u7684\u4e00\u638c\u76f8\u51fb\uff0c\u90a3\u7edd\u8272\u5c11 \u5973\u53e3\u4e2d\u95f7\u54fc\u4e00\u58f0\uff0c\u98d8\u98d8\u5411\u540e\u9000\u4e86\u4e94\u5c3a\uff0c\u9000\u5230\u53e6\u4e00\u57ce\u579b\u4e0a\u3002 \u3000\u3000\u5353\u957f\u537f\u8fd9\u4e00\u62db\u4e0d\u4f46\u59ff\u52bf\u66fc\u5999\uff0c\u653b\u5b88\u517c\u5907\uff0c\u800c\u4e14\u4ed6\u8fd9\u53cc\u638c\u4e00\u5408\uff0c\u663e\u89c1\u662f\u5728\u5411\u57ce\u4e0b\u7684\u7fa4\u8c6a\u89c1 \u793c\uff0c\u7fa4\u8c6a\u89c1\u8fd9\u5c11\u5e74\u7adf\u5728\u8fd9\u79cd\u60c5\u5f62\u4e0b\u65bd\u51fa\u8fd9\u79cd\u62db\u5f0f\u6765\uff0c\u5374\u53c8\u8fd0\u7528\u5f97\u90a3\u4e48\u6070\u5230\u597d\u5904\uff0c\u4e0d\u7981\u53c8\u8f70\u7136 \u559d\u8d77\u5f69\u6765\u3002 \u3000\u3000\u591a\u81c2\u795e\u5251\u624b\u634b\u957f\u987b\uff0c\u54c8\u54c8\u5927\u7b11\uff0c\u4fa7\u987e\u4e91\u4e2d\u7a0b\u5927\u58f0\u9053\uff1a\u201c\u4e2d\u7a0b\uff0c\u4f60\u770b\u770b\uff0c\u4eba\u5bb6\u8fd9\u624d\u53eb\u864e\u7236 \u65e0\u72ac\u5b50\uff0c\u53ea\u6709\u8fd9\u4e48\u6837\u7684\u513f\u5b50\uff0c\u624d\u914d\u5f97\u8d77\u6211\u5353\u6d69\u7136\u5353\u8001\u5f1f\u90a3\u6837\u7684\u7236\u4eb2\uff0c\u5c31\u51b2\u4ed6\u8fd9\u4e00\u62db\u6d41\u4e91\u98de \u8896\uff0c\u6b66\u5f53\u5c71\u4e0a\u7684\u767d\u77f3\u9053\u4eba\u90fd\u672a\u5fc5\u80fd\u5f3a\u4ed6\u591a\u5c11\uff0c\u5509\uff0c\u771f\u96be\u4e3a\u4ed6\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u600e\u4e48\u5b66\u6765\u7684\uff1f\u201d \u3000\u3000\u8fd9\u8c6a\u8fc8\u7684\u8001\u4eba\u89c1\u5230\u6545\u4eba\u6709\u540e\uff0c\u4e0d\u7981\u8001\u6000\u5927\u653e\uff0c\u5927\u58f0\u79f0\u8d5e\u8d77\u6765\uff0c\u65c1\u8fb9\u7684\u6b66\u6797\u8c6a\u58eb\u4e00\u542c\u5728\u57ce \u4e0a\u52a8\u624b\u7684\u5c11\u5e74\u7adf\u662f\u6614\u65e5\u540d\u9707\u5929\u4e0b\u7684\u4e2d\u539f\u5927\u4fa0\u4e4b\u5b50\uff0c\u4e0d\u7981\u6697\u4e2d\u4f20\u8bed\uff0c\u90fd\u9053\u6b64\u5c11\u5e74\u4e86\u5f97\u3002 \u3000\u3000\u90a3\u7edd\u8272\u4e3d\u4eba\u7c89\u9762\u51dd\u971c\uff0c\u5168\u795e\u653b\u654c\uff0c\u4e0b\u9762\u7684\u8bdd\uff0c\u5979\u6839\u672c\u6ca1\u6709\u542c\u89c1\uff0c\u5c0f\u743c\u5c0f\u73b2\u8fdc\u8fdc\u63a0\u5230\u53e6\u4e00 \u4e2a\u57ce\u579b\u4e0a\uff0c\u4f38\u51fa\u624b\u8155\uff0c\u53ea\u89c1\u90a3\u7389\u4e5f\u4f3c\u7684\u808c\u80a4\u4e0a\uff0c\u6b64\u523b\u5df2\u591a\u4e86\u4e00\u9053\u9752\u7d2b\u7684\u4f24\u75d5\uff0c\u5fc3\u4e2d\u4e0d\u7981\u6697\u81ea \u4e00\u9a87\uff0c\u81ea\u5df1\u624d\u4e0d\u8fc7\u88ab\u8863\u8896\u6cbe\u7740\u4e00\u70b9\uff0c\u5c31\u5df2\u5982\u6b64\uff0c\u82e5\u662f\u5b8c\u5168\u8ba9\u90a3\u53cc\u8863\u8896\u626b\u7740\uff0c\u6b64\u523b\u6015\u4e0d\u65e9\u5df2\u8155 \u9aa8\u5c3d\u6298\u3002 \u3000\u3000\u5979\u4eec\u4e92\u671b\u4e00\u773c\uff0c\u5404\u4e2a\u4ff1\u90fd\u82b1\u5bb9\u5931\u8272\uff0c\u4f46\u90a3\u7edd\u8272\u4e3d\u4eba\u4e1d\u6beb\u6ca1\u6709\u9000\u610f\uff0c\u51fa\u624b\u53cd\u5012\u66f4\u89c1\u6fc0\u5389\uff0c \u5979\u5fc3\u4e2d\u867d\u5df2\u6709\u60c5\u610f\uff0c\u4f46\u4e5f\u4e0d\u5f97\u4e0d\u4e00\u633a\u7ea4\u8170\uff0c\u518d\u626c\u7389\u638c\uff0c\u53c8\u81ea\u548c\u5353\u957f\u537f\u52a8\u8d77\u624b\u6765\u3002 \u3000\u3000\u57ce\u4e0b\u7fa4\u8c6a\uff0c\u6307\u6307\u70b9\u70b9\uff0c\u867d\u5728\u6697\u4e2d\u5938\u5956\u7740\u5353\u957f\u537f\uff0c\u5374\u4e5f\u4e0d\u7981\u4e3a\u8fd9\u4e09\u4e2a\u7ea2\u88f3\u5c11\u5973\u7684\u6b66\u529f\u6240 \u8c05\uff0c\u6697\u4e2d\u5404\u81ea\u5947\u602a\uff0c\u6b66\u6797\u4e4b\u4e2d\u600e\u5730\u4f1a\u7a81\u7136\u51fa\u6765\u5982\u8bb8\u5e74\u8f7b\u9ad8\u624b\u3002 \u3000\u3000\u5927\u5bb6\u4ef0\u9996\u800c\u89c2\uff0c\u53ea\u89c1\u57ce\u4e0a\u7684\u4eba\u5f71\uff0c\u8eab\u6cd5\u53d8\u5316\u5f97\u8d8a\u6765\u8d8a\u5feb\uff0c\u5c0f\u743c\u3001\u5c0f\u73b2\u5fcd\u7740\u624b\u8155\u4e4b\u75db\uff0c\u548c \u90a3\u7edd\u8272\u5c11\u5973\u5c55\u5f00\u6709\u5982\u72c2\u98ce\u51c9\u98d8\u822c\u7684\u638c\u6cd5\uff0c\u867d\u7136\u597d\u50cf\u5df2\u5c06\u5353\u957f\u537f\u7b3c\u7f69\u5728\u5979\u4eec\u7684\u638c\u98ce\u5a01\u529b\u4e4b\u4e0b\u3002 \u3000\u3000\u4f46\u5353\u957f\u537f\u5c79\u7acb\u5982\u5c71\uff0c\u53cc\u638c\u4e00\u6325\uff0c\u5c31\u662f\u653b\u654c\u4e4b\u6240\u5fc5\u6551\uff0c\u90a3\u7ea2\u638c\u5c11\u5973\u7684\u638c\u6cd5\u867d\u662f\u5947\u8be1\u60ca\u4eba\uff0c \u4f46\u5374\u90fd\u88ab\u4ed6\u8f7b\u63cf\u6de1\u5199\u5730\u4e00\u4e00\u5316\u5f00\u3002 \u3000\u3000\u591a\u81c2\u795e\u5251\u4e45\u95ef\u6c5f\u6e56\uff0c\u6b66\u529f\u867d\u7136\u5e76\u975e\u767b\u5cf0\u9020\u6781\uff0c\u4f46\u4ed6\u6570\u5341\u5e74\u6765\uff0c\u8eab\u7ecf\u767e\u6218\uff0c\u9605\u5386\u4e4b\u4e30\uff0c\u5374 \u662f\u4e30\u5bcc\u5230\u6781\u70b9\uff0c\u6b64\u523b\u770b\u5230\u4ed6\u4eec\u7684\u52a8\u624b\u7684\u60c5\u5f62\uff0c\u77e5\u9053\u5353\u957f\u537f\u5df2\u5360\u4e0a\u98ce\uff0c\u4ed6\u6709\u5fc3\u8ba9\u8fd9\u521d\u51fa\u6c5f\u6e56\u7684 \u5c11\u5e74\u5728\u4eba\u524d\u626c\u54b8\u9732\u8138\uff0c\u662f\u4ee5\u54c8\u54c8\u53c8\u81ea\u7b11\u9053\uff1a\u201c\u4e2d\u7a0b\uff0c\u4f60\u770b\u770b\uff0c\u8fd9\u4e09\u4e2a\u5973\u5b69\u5b50\u7684\u6b66\u529f\u600e\u6837\uff1f\u201d \u3000\u3000\u4e91\u4e2d\u7a0b\u5fae\u5fae\u4e00\u6014\uff0c\u8fd8\u672a\u6765\u5f97\u53ca\u7b54\u8bdd\uff0c\u5374\u89c1\u4e91\u8c26\u53c8\u6717\u7b11\u9053\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u5979\u4eec\u5c31\u662f\u6614\u5e74\u7ea2 \u8863\u5a18\u5a18\u7684\u5f1f\u5b50\uff0c\u4f60\u770b\u5979\u8fd9\u4e00\u62db\u62c2\u4e91\u624b\uff0c\u4f7f\u5f97\u53c8\u6709\u591a\u9ad8\uff0c\u563f\uff0c\u8fd9\u4e8f\u4e86\u662f\u957f\u537f\u5728\u4e0a\u9762\uff0c\u82e5\u662f\u522b\u4eba \u7684\u8bdd\u4ed6\u8bed\u58f0\u4e00\u987f\uff0c\u4e91\u4e2d\u7a0b\u6697\u4e2d\u4e00\u7b11\uff0c\u5df2\u77e5\u9053\u4ed6\u7239\u7239\u6545\u610f\u8bf4\u51fa\u8fd9\u4e09\u4e2a\u5c11\u5973\u7684\u6765\u5386\u3001\u6b66\u529f\u53ea\u662f\u4e3a \u4e86\u663e\u51fa\u5353\u957f\u537f\u7684\u6b66\u529f\u4e4b\u9ad8\u6765\uff0c\u9042\u63a5\u53e3\u7b11\u9053\uff1a\u201c\u8fd9\u8981\u662f\u6362\u4e86\u5b69\u513f\u6211\u4e0a\u53bb\u7684\u8bdd\uff0c\u4e0d\u7528\u5341\u4e2a\u7167\u9762\uff0c \u5c31\u5f97\u88ab\u5979\u4eec\u6253\u4e0b\u6765\u3002\u201d \u3000\u3000\u4ed6\u6b64\u8a00\u4e00\u51fa\uff0c\u7fa4\u8c6a\u4e0d\u7981\u53c8\u76f8\u987e\u8272\u53d8\uff0c\u987b\u77e5\u829c\u6e56\u4e91\u95e8\u5728\u6b66\u6797\u4e2d\u7684\u5730\u4f4d\u6781\u9ad8\uff0c\u4ec1\u4e49\u5251\u5ba2\u4e91\u4e2d \u7a0b\u66f4\u662f\u6c5f\u5357\u6b66\u6797\u4e2d\u5c48\u6307\u53ef\u6570\u7684\u4eba\u7269\uff0c\u6b64\u523b\u4ed6\u4eec\u5982\u6b64\u4e00\u8bf4\uff0c\u7fa4\u8c6a\u5bf9\u5353\u957f\u537f\u7684\u770b\u6cd5\uff0c\u679c\u81ea\u53c8\u662f\u4e0d \u540c\u3002 \u3000\u3000\u591a\u81c2\u795e\u5251\u58f0\u5982\u6d2a\u949f\uff0c\u4ed6\u8bf4\u7684\u8bdd\uff0c\u5b57\u5b57\u53e5\u53e5\u90fd\u4f20\u4eba\u5353\u957f\u537f\u7684\u8033\u4e2d\uff0c\u4ed6\u8033\u4e2d\u542c\u5f97\u8fd9\u4e09\u4e2a\u5c11\u5973 \u679c\u7136\u5c31\u662f\u81ea\u5df1\u4ec7\u4eba\u7684\u5f1f\u5b50\uff0c\u5fc3\u91cc\u4e0d\u89c9\u70ed\u8840\u6cb8\u817e\uff0c\u5fc3\u795e\u4e0d\u7981\u53c8\u5fae\u5fae\u4e00\u758f\u3002 \u3000\u3000\u90a3\u7edd\u8272\u4e3d\u4eba\u4e00\u58f0\u5a07\u53f1\uff0c\u5c0f\u743c\u5c0f\u73b2\u7ea2\u8896\u4e00\u62c2\uff0c\u201c\u5530\u201d\u7684\u653b\u51fa\u56db\u62db\uff0c\u5979\u5374\u8eab\u5f62\u4e00\u8f6c\uff0c\u8f6c\u5230\u5353 \u957f\u537f\u7684\u5de6\u4fa7\u3002 \u3000\u3000\u5353\u957f\u537f\u8eab\u968f\u5ff5\u8f6c\uff0c\u907f\u5f00\u5c0f\u743c\u5c0f\u73b2\u7684\u56db\u62db\uff0c\u54ea\u77e5\u5374\u6070\u597d\u8f6c\u5230\u90a3\u7edd\u8272\u4e3d\u4eba\u7684\u8eab\u524d\u3002 \u3000\u3000\u90a3\u7edd\u8272\u4e3d\u4eba\u5de6\u638c\u5f53\u80f8\u4e00\u63a8\uff0c\u53f3\u624b\u4e94\u6307\uff0c\u5374\u5fae\u5fae\u5206\u5f00\uff0c\u201c\u5530\u201d\u7684\u70b9\u5411\u5353\u957f\u537f\u80f8\u524d\u7684\u56db\u5904\u5927 \u7a74\uff0c\u65ed\u65e5\u5149\u4e0b\uff0c\u53ea\u89c1\u5979\u8fd9\u5341\u53ea\u7ea4\u7ea4\u7389\u6307\u4e0a\u7684\u82b1\u6597\uff0c\u81f4\u81f4\u751f\u5149\uff0c\u4f46\u5353\u957f\u537f\u81ea\u5df1\u5fc3\u91cc\u6709\u6570\uff0c\u77e5\u9053 \u53ea\u8981\u8ba9\u5979\u8fd9\u5341\u53ea\u72b9\u5982\u6625\u8471\u822c\u7684\u7389\u6307\u6cbe\u4e0a\u4e00\u70b9\uff0c\u4fbf\u7acb\u65f6\u5c31\u4f1a\u4e0d\u5f97\u4e86\u3002 \u3000\u3000\u987b\u77e5\u4ed6\u5fd6\u91cf\u60c5\u5f62\uff0c\u65e9\u5c31\u770b\u51fa\u5c0f\u743c\u5c0f\u73b2\u4e0d\u8fc7\u4ec5\u662f\u8fd9\u7edd\u8272\u4e3d\u4eba\u7684\u4e2b\u73af\u800c\u5df2\u3001\u662f\u4ee5\u51fa\u624b\u65f6\uff0c\u4fbf \u5bf9\u8fd9\u4e24\u4e2a\u5782\u9aef\u5c11\u5973\u7559\u4e86\u51e0\u5206\u60c5\u3002 \u3000\u3000\u4f46\u6b64\u523b\u4ed6\u5374\u56e0\u5979\u4eec\u4e4b\u7275\u5236\uff0c\u800c\u5c61\u9047\u9669\u62db\uff0c\u5251\u7709\u4e00\u8f69\uff0c\u84e6\u5730\u66b4\u559d\u4e00\u58f0\uff0c\u5de6\u638c\u547c\u7684\u53cd\u6325\u4e86\u51fa \u53bb\uff0c\u4e00\u822c\u6fc0\u70c8\u7684\u638c\u51e4\u5c06\u53c8\u81ea\u4ed6\u8eab\u540e\u88ad\u6765\u7684\u5c0f\u743c\u5c0f\u73b2\u6325\u5f00\u4e94\u5c3a\uff0c\u53f3\u638c\u4e00\u6c89\u4e00\u66f2\uff0c\u4e94\u6307\u5982\u94a9\uff0c\u53bb \u5201\u90a3\u7edd\u8272\u5c11\u5973\u7684\u53f3\u624b\u8109\u95e8\u3002 \u3000\u3000\u90a3\u7edd\u8272\u5c11\u5973\u77e5\u9053\u5353\u957f\u537f\u7684\u529f\u529b\uff0c\u4e0d\u6562\u548c\u4ed6\u5bf9\u638c\uff0c\u7ea4\u6307\u4e00\u626c\uff0c\u5c06\u53f3\u624b\u7f29\u4e86\u56de\u53bb\uff0c\u5de6\u638c\u4ecd\u539f \u5f0f\u51fb\u51fa\u3002 \u3000\u3000\u54ea\u77e5\u5353\u957f\u537f\u53f3\u65f6\u7a81\u53c8\u4e00\u66f2\uff0c\u4e00\u4e2a\u65f6\u62f3\u649e\u5411\u5979\u7684\u5de6\u638c\uff0c\u90a3\u7edd\u8272\u4e3d\u4eba\u4e00\u60ca\uff0c\u6536\u62db\uff0c\u5374\u89c1\u5353\u957f \u537f\u4e00\u53cc\u94c1\u638c\u7a81\u53c8\u4f38\u51fa\u4e94\u6307\u7b95\u5f20\uff0c\u7adf\u4ee5\u5185\u5bb6\u5c0f\u5929\u661f\u7684\u638c\u529b\uff0c\u51fb\u5411\u81ea\u5df1\u524d\u80f8\u3002 \u3000\u3000\u5353\u957f\u537f\u8fd9\u53ea\u53f3\u624b\u4e00\u6293\u3001\u4e00\u649e\u3001\u4e00\u51fb\uff0c\u62c6\u62db\uff0c\u6d51\u5982\u4e00\u4f53\uff0c\u62db\u5f0f\u4e4b\u5999\uff0c\u53ef\u8bf4\u5999\u5230\u6beb\u5dc5\uff0c\u51fa\u62db \u4e4b\u5feb\uff0c\u66f4\u662f\u5feb\u5982\u95ea\u7535\uff0c\u6b63\u662f\u90a3\u5929\u4ed9\u53f8\u7a7a\u8001\u4eba\u6614\u5e74\u540d\u626c\u5929\u4e0b\u7684\u795e\u9f99\u516b\u5f0f\u4e2d\u7684\u4e00\u62db\u5929\u9f99\u884c\u7a7a\u3002 \u3000\u3000\u5353\u957f\u537f\u638c\u5230\u4e2d\u9014\uff0c\u76ee\u5149\u52a8\u5904\uff0c\u5ffd\u7136\u7779\u89c1\u90a3\u7edd\u8272\u4e3d\u4eba\u7684\u9165\u80f8\u5fae\u5fae\u9686\u8d77\u5728\u90a3\u8f7b\u7eb1\u7ea2\u7f57\u886b\u91cc\uff0c \u8d77\u4f0f\u4e4b\u95f4\uff0c\u7729\u76ee\u52a8\u5fc3\uff0c\u800c\u81ea\u5df1\u8fd9\u4e00\u62db\u5929\u9f99\u884c\u7a7a\u7adf\u662f\u5f80\u4eba\u5bb6\u7684\u9165\u80f8\u4e0a\u51fb\u53bb\u3002 \u3000\u3000\u4ed6\u6b64\u523b\u867d\u5df2\u529b\u900f\u638c\u6307\uff0c\u4f46\u4e00\u7779\u4e4b\u4e0b\uff0c\u6b64\u638c\u4fbf\u518d\u4e5f\u65e0\u6cd5\u51fb\u51fa\uff0c\u53e3\u4e2d\u95f7\u54fc\u4e00\u58f0\uff0c\u786c\u751f\u751f\u5c06\u624b \u638c\u4e00\u987f\u3002 \u3000\u3000\u90a3\u7edd\u8272\u4e3d\u4eba\u5fae\u4e00\u51b7\u7b11\uff0c\u7389\u638c\u4fbf\u53c8\u5982\u7535\u51fb\u51fa\uff0c\u5c0f\u73b2\u5c0f\u743c\u8eab\u5f62\u4e00\u9000\uff0c\u6b64\u523b\u53c8\u5df2\u5982\u884c\u4e91\u6d41\u6c34\u822c \u63a0\u4e86\u8fc7\u6765\uff0c\u500f\u7136\u62cd\u51fa\u56db\u638c\u3002 \u3000\u3000\u5353\u957f\u537f\u5927\u559d\u4e00\u58f0\uff0c\u8eab\u8eaf\u731b\u62e7\uff0c\u4f46\u53f3\u65f6\u66f2\u6c60\u7a74\u95f4\uff0c\u5df2\u88ab\u90a3\u7edd\u8272\u4e3d\u5165\u7684\u638c\u7f18\u626b\u4e2d\u3002 \u3000\u3000\u53f3\u81c2\u987f\u65f6\u53d1\u9ebb\uff0c\u4f46\u4eba\u5bb6\u600e\u80af\u518d\u7ed9\u5598\u606f\u7684\u673a\u4f1a\uff0c\u201c\u5530\u201d\u7684\u53c8\u662f\u6570\u638c\uff0c\u5353\u957f\u537f\u5927\u8f6c\u8eab\uff0c\u8fde\u9000 \u56db\u6b65\uff0c\u54ea\u77e5\u811a\u4e0b\u7a81\u5730\u4e00\u811a\u8e0f\u7a7a\uff0c\u53f3\u80a9\u53c8\u4e2d\u4e86\u5c0f\u743c\u4e00\u638c\uff0c\u4fbf\u518d\u4e5f\u7a33\u4e0d\u4f4f\u8eab\u5f62\uff0c\u7adf\u4ece\u57ce\u5934\u6389\u4e86\u4e0b \u6765\uff0c\u4f17\u4eba\u4e0d\u7981\u9f50\u7684\u53d1\u51fa\u4e00\u58f0\u60ca\u547c\uff0c\u591a\u81c2\u795e\u5251\u9762\u5bb9\u9aa4\u53d8\uff0c\u4e00\u64a9\u957f\u886b\uff0c\u8dfa\u811a\u7eb5\u4e86\u8fc7\u53bb\uff0c\u54ea\u77e5\u5353\u957f \u537f\u72b9\u5982\u6d41\u661f\u4e0b\u5760\u7684\u8eab\u5f62\uff0c\u65b9\u5230\u4e86\u4e2d\u9014\uff0c\u7a81\u7136\u4e00\u7f13\uff0c\u5934\u4e0a\u811a\u4e0b\uff0c\u98d8\u7136\u843d\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u591a\u81c2\u795e\u5251\u4e00\u634b\u957f\u987b\uff0c\u6025\u58f0\u95ee\u9053\uff1a\u201c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d \u3000\u3000\u5353\u957f\u537f\u5251\u7709\u5fae\u76b1\uff0c\u4f38\u51fa\u5de6\u638c\uff0c\u5728\u81ea\u5df1\u53f3\u80a9\u3001\u808b\u4e0b\uff0c\u6781\u5feb\u5730\u62cd\u4e86\u4e24\u4e0b\uff0c\u4e00\u9762\u9053\uff1a\u201c\u4e0d\u59a8\u4e8b \u7684\u3002\u201d \u3000\u3000\u62ac\u5934\u4e00\u671b\uff0c\u53ea\u89c1\u57ce\u5934\u4e4b\u4e0a\uff0c\u7ea2\u886b\u98d8\u98d8\uff0c\u5728\u4ed6\u7acb\u5904\u5374\u56e0\u4e3a\u7ad9\u5728\u5899\u89d2\uff0c\u662f\u4ee5\u5979\u4eec\u6b64\u523b\u7a76\u7adf\u5728 \u505a\u4ec0\u4e48\uff0c\u4ed6\u5374\u4e00\u70b9\u4e5f\u770b\u4e0d\u5230\u3002 \u3000\u3000\u591a\u81c2\u795e\u5251\u6c89\u58f0\u9053\uff1a\u201c\u8fd9\u4e09\u4e2a\u5c11\u5973\u662f\u7ea2\u8863\u5a18\u5a18\u7684\u95e8\u4e0b\uff0c\u4f60\u8981\u5c0f\u5fc3\u4e9b\u65b9\u662f\uff0c\u5982\u679c\u65e0\u751a\u6028\u4ec7\uff0c \u4e5f\u4e0d\u5fc5\u548c\u4ed6\u4eec\u82e6\u6597\uff0c\u514d\u5f97\u591a\u60f9\u4ec7\u5bb6\u3002\u201d \u3000\u3000\u4ed6\u6839\u672c\u4e0d\u77e5\u9053\u6b64\u4e8b\u7684\u771f\u76f8\uff0c\u662f\u4ee5\u624d\u8bf4\u51fa\u8fd9\u79cd\u529f\u6170\u7684\u8bdd\u6765\u3002 \u3000\u3000\u5353\u957f\u537f\u5251\u7709\u4e00\u8f69\uff0c\u7a81\u53c8\u8f7b\u53f9\u4e00\u58f0\uff0c\u53cc\u81c2\u5fae\u5f20\uff0c\u55d6\u5730\u53c8\u4e00\u7a9c\u800c\u4e0a\uff0c\u4ed6\u65b9\u624d\u4e00\u62db\u5931\u7740\uff0c\u88ab\u4eba \u5bb6\u903c\u4e0b\u57ce\u6765\uff0c\u867d\u662f\u56e0\u4e3a\u81ea\u5df1\u70d9\u4e8e\u672d\u6570\uff0c\u4e0d\u5fcd\u4e0b\u624b\uff0c\u4f46\u5728\u8fd9\u4e48\u591a\u53cc\u773c\u775b\u4e0b\u906d\u53d7\u6b64\u8fb1\uff0c\u5fc3\u4e2d\u81ea\u662f \u4e0d\u670d\uff0c\u6b64\u523b\u4fbf\u751f\u50cf\u662f\u5728\u8eab\u6cd5\u4e0a\u5356\u5f04\u4e00\u4e0b\uff0c\u8fd9\u7eb5\u8eab\u4e00\u8dc3\uff0c\u7adf\u7136\u9ad8\u8fbe\u4e09\u4e08\u3002 \u3000\u3000\u4ed6\u6839\u57fa\u672c\u4f73\uff0c\u518d\u52a0\u4e0a\u6240\u4e60\u5185\u529f\uff0c\u53c8\u662f\u7384\u95e8\u6b63\u5b97\uff0c\u662f\u4ee5\u6b64\u523b\u4ed6\u867d\u7ecf\u6fc0\u6218\uff0c\u4f46\u662f\u5185\u52b2\u5374\u65e0\u663e \u8457\u7684\u635f\u8017\uff0c\u8eab\u5f62\u51cc\u7a7a\u4e00\u8d77\uff0c\u8033\u4e2d\u5374\u53c8\u542c\u5230\u57ce\u4e0b\u7fa4\u8c6a\u9f50\u58f0\u6240\u53d1\u51fa\u7684\u8f70\u7136\u7684\u559d\u5f69\u58f0\uff0c\u90a3\u591a\u81c2\u795e\u5251 \u5148\u81ea\u5927\u58f0\u559d\u9053\uff1a\u201c\u957f\u537f\uff0c\u5c0f\u5fc3\u4e86\u3002\u201d \u3000\u3000\u4ed6\u4e0d\u7981\u53c8\u6697\u53f9\u4e00\u58f0\uff0c\u4e00\u53cc\u5bbd\u5927\u7684\u8863\u8896\u731b\u7136\u5f80\u5916\u4e00\u62c2\uff0c\u8eab\u5f62\u4e00\u6298\uff0c\u53cc\u638c\u53c8\u5728\u5899\u8fb9\u4e00\u6309\uff0c\u501f \u52bf\u518d\u6b21\u62d4\u8d77\u3002 \u3000\u3000\u54ea\u77e5\u57ce\u5934\u4e4b\u4e0a\uff0c\u7a81\u7136\u4f20\u4e0b\u4e00\u9635\u6717\u7b11\u4e4b\u58f0\uff0c\u7b11\u58f0\u6e05\u8d8a\uff0c\u7a7f\u4e91\u88c2\u77f3\u2014\u2014\u7b11\u58f0\u65b9\u81ea\u4eba\u5353\u957f\u537f\u4e4b \u8033\uff0c\u4ed6\u7684\u8eab\u5f62\u4f7f\u4e5f\u7a9c\u5230\u57ce\u5934\uff0c\u76ee\u5149\u56db\u626b\uff0c\u53ea\u89c1\u90a3\u7edd\u8272\u5c11\u5973\u51cc\u98ce\u800c\u7acb\uff0c\u6b63\u5728\u633d\u7740\u90a3\u53cc\u5df2\u7ecf\u88ab\u626f \u65ad\u5c0f\u534a\u7684\u8863\u8896\uff0c\u5c0f\u743c\u5c0f\u73b2\u4f9d\u4f9d\u7684\u7ad9\u5728\u5979\u7684\u8eab\u4fa7\uff0c\u4e09\u4eba\u7684\u516d\u9053\u79cb\u6ce2\uff0c\u5374\u90fd\u51dd\u6ce8\u5728\u4e00\u4e2a\u4e0d\u77e5\u4f55\u65f6 \u63a0\u4e0a\u57ce\u5934\u7684\u9ec4\u886b\u5c11\u5e74\u7684\u8eab\u4e0a\u3002 \u3000\u3000\u8fd9\u9ec4\u886b\u5c11\u5e74\u7b11\u58f0\u672a\u7edd\uff0c\u5374\u662f\u80cc\u5411\u5353\u957f\u537f\u800c\u7acb\uff0c\u5353\u957f\u537f\u53ea\u89c1\u4ed6\u957f\u886b\u98d8\u98d8\uff0c\u8eab\u6750\u9880\u957f\uff0c\u5374\u672a \u770b\u5230\u4ed6\u7684\u9762\u8c8c\u3002 \u3000\u3000\u8fd9\u9ec4\u886b\u5c11\u5e74\u7b11\u58f0\u7a81\u7136\u4e00\u987f\uff0c\u56de\u8fc7\u5934\u6765\uff0c\u51b7\u51b7\u5411\u5353\u957f\u537f\u77a5\u4e86\u4e00\u773c\uff0c\u4e24\u4eba\u76ee\u5149\u76f8\u5bf9\uff0c\u5353\u957f\u537f \u4e0d\u7981\u5728\u5fc3\u91cc\u6697\u8d5e\u4e00\u58f0\uff1a\u201c\u597d\u4e2a\u6f02\u4eae\u4eba\u7269\uff01\u201d \u3000\u3000\u201c\u76f8\u60dc\u4e4b\u5fc3\uff0c\u6cb9\u7136\u800c\u751f\u3001\u54ea\u77e5\u90a3\u9ec4\u886b\u5c11\u5e74\u51b7\u51b7\u6253\u91cf\u4e86\u5353\u957f\u537f\u51e0\u773c\uff0c\u773c\u76ae\u4e00\u7ffb\uff0c\u5374\u53c8\u56de\u8fc7 \u5934\u53bb\uff0c\u6717\u58f0\u9053\uff1a\u201c\u4e24\u4f4d\u59d1\u5a18\u5306\u5306\u800c\u522b\uff0c\u5728\u4e0b\u6b63\u81ea\u60ac\u5ff5\u5f97\u7d27\u2018\u4e0d\u60f3\u4eca\u65e5\u5374\u53c8\u5728\u6b64\u76f8\u9047\uff0c\u54c8\uff0c\u8fd9 \u771f\u8ba9\u5728\u4e0b\u9ad8\u5174\u5f97\u5f88\uff0c\u9ad8\u5174\u5f97\u5f88\u3002\u201d\u4ed6\u4e00\u8fde\u8bf4\u4e86\u4e24\u4e2a\u9ad8\u5174\uff0c\u6717\u7b11\u4e4b\u58f0\uff0c\u53c8\u590d\u5927\u4f5c\uff0c\u5353\u957f\u537f\u5251\u7709 \u5fae\u76b1\uff0c\u6697\u5fd6\uff1a\u201c\u8fd9\u5c11\u5e74\u597d\u751f\u5028\u50b2\u3002\u201d \u3000\u3000\u5fae\u4e3e\u4e00\u6b65\uff0c\u4ea6\u81ea\u63a0\u5230\u4ed6\u5353\u7acb\u7684\u57ce\u579b\u4e0a\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u5144\u53f0\u4e14\u6162\u53d9\u65e7\uff0c\u5728\u4e0b\u4e0e\u8fd9\u4e09\u4f4d\u59d1\u5a18\u8fd8 \u6709\u4e8b\u672a\u4e86\uff0c\u8bf7\u5144\u53f0\u6682\u9000\u4e00\u6b65\u3002\u201d \u3000\u3000\u90a3\u9ec4\u886b\u5c11\u5e74\u773c\u76ae\u4e00\u7ffb\uff0c\u671b\u4e5f\u4e0d\u671b\u5353\u957f\u537f\u4e00\u773c\uff0c\u6717\u58f0\u9053\uff1a\u201c\u65b9\u624d\u5728\u4e0b\u4ece\u57ce\u5916\u884c\u6765\uff0c\u8fdc\u8fdc\u5c31 \u770b\u5230\u57ce\u5934\u4e4b\u4e0a\u7ea2\u8863\u98d8\u52a8\uff0c\u5728\u4e0b\u5fc3\u91cc\u5c31\u60f3\uff0c\u8fd9\u5fc5\u5b9a\u662f\u59d1\u5a18\u4eec\u4e86\uff0c\u8d76\u6765\u4e00\u770b\uff0c\u679c\u7136\u4e0d\u51fa\u6240\u6599\u3002\u201d \u4ed6\u54c8\u54c8\u4e00\u58f0\uff0c\u76ee\u5149\u5728\u4e2d\u95f4\u90a3\u7edd\u8272\u4e3d\u4eba\u8eab\u4e0a\u8f6c\u4e86\u51e0\u8f6c\uff0c\u4fbf\u518d\u4e5f\u820d\u4e0d\u5f97\u79bb\u5f00\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u8fd9\u4f4d\u59d1 \u5a18\u600e\u4e48\u5982\u6b64\u9762\u719f\u2014\u2014\u201d\u7a81\u7136\u4f38\u51fa\u53f3\u638c\uff0c\u5728\u81ea\u5df1\u524d\u989d\u731b\u7684\u4e00\u62cd\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u539f\u6765\u59d1\u5a18\u5c31\u662f\u90a3 \u4f4d\u753b\u4e2d\u4e4b\u4eba\uff0c\u5728\u4e0b\u81ea\u4ece\u89c1\u4e86\u59d1\u5a18\u7684\u753b\u4e2d\u5029\u5f71\u4e4b\u540e\uff0c\u5c31\u7ec8\u65e5\u795e\u9b42\u7275\u7cfb\uff0c\u53ef\u4e0d\u7981\u6709\u4e9b\u7591\u60d1\uff0c\u4e16\u95f4 \u7109\u6709\u5982\u6b64\u7f8e\u4eba\uff0c\u53ea\u6015\u662f\u90a3\u753b\u5de5\u7684\u4e00\u652f\u4e39\u9752\u5999\u7b14\uff0c\u6545\u610f\u6e32\u67d3\u51fa\u6765\u7684\uff0c\u4eca\u65e5\u89c1\u4e86\u59d1\u5a18\u4e4b\u9762\uff0c\u624d\u77e5 \u9053\u90a3\u753b\u5de5\u4e4b\u7b14\uff0c\u5b9e\u662f\u5eb8\u624b\uff0c\u90a3\u5e45\u753b\u53c8\u4f55\u66fe\u5c06\u59d1\u5a18\u4e4b\u7f8e\u753b\u51fa\u4e07\u4e00\uff0c\u4e0b\u6b21\u6211\u82e5\u89c1\u4e86\u4ed6\u2014\u2014\u54fc\u3002\u201d \u3000\u3000\u8fd9\u9ec4\u886b\u5c11\u5e74\u6307\u624b\u5212\u811a\uff0c\u65c1\u82e5\u65e0\u4eba\uff0c\u6ed4\u6ed4\u4e0d\u7edd\u5730\u653e\u8086\u800c\u8a00\uff0c\u5353\u957f\u537f\u7684\u4e00\u53cc\u5251\u7709\u76b1\u5230\u4e00\u5904\u3002 \u3000\u3000\u4ed6\u65b9\u624d\u89c1\u8fd9\u9ec4\u886b\u5c11\u5e74\u8eab\u6750\u633a\u79c0\uff0c\u672c\u81ea\u6709\u4e9b\u597d\u610f\uff0c\u4f46\u6b64\u523b\u5374\u4e0d\u7981\u538c\u6076\u4e07\u5206\uff0c\u6697\u6697\u5fd6\u9053\uff1a \u201c\u8fd9\u771f\u662f\u4eba\u4e0d\u80fd\u8c8c\u76f8\u4e86\uff0c\u8fd9\u5c11\u5e74\u770b\u6765\u867d\u662f\u4e2a\u597d\u7537\u513f\uff0c\u54ea\u77e5\u7adf\u5982\u6b64\u4fd7\u6076\uff0c\u5374\u53c8\u5982\u6b64\u7316\u72c2\u3002\u201d \u3000\u3000\u60f3\u5230\u4ed6\u65b9\u624d\u5bf9\u81ea\u5df1\u7684\u6001\u5ea6\uff0c\u5251\u7709\u4e00\u8f69\uff0c\u624d\u65b9\u6b32\u53d1\u4f5c\uff0c\u54ea\u77e5\u9ec4\u886b\u5c11\u5e74\u8bdd\u58f0\u65b9\u987f\uff0c\u90a3\u7edd\u8272\u4e3d \u4eba\u5374\u67f3\u7709\u4e00\u5c55\uff0c\u68a8\u7a9d\u6d45\u73b0\uff0c\u4f38\u51fa\u7389\u638c\uff0c\u4e00\u63a0\u9b13\u811a\uff0c\u7a81\u7136\u5a07\u58f0\u7b11\u9053\uff1a\u201c\u4f60\u82e5\u89c1\u4e86\u4ed6\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u90a3\u9ec4\u886b\u5c11\u5e74\u5fae\u5fae\u4e00\u6014\uff0c\u4fbf\u53c8\u4ef0\u5929\u5927\u7b11\u9053\uff1a\u201c\u65e5\u540e\u6211\u82e5\u89c1\u4e86\u90a3\u8822\u624d\uff0c\u6211\u5148\u8981\u5c06\u4ed6\u53cc\u624b\u5241 \u4e0b\uff0c\u8ba9\u4ed6\u6c38\u8fdc\u2014\u2014\u201d\u90a3\u7edd\u8272\u4e3d\u4eba\u7a81\u53c8\u54af\u54af\u4e00\u9635\u5a07\u7b11\uff0c\u622a\u65ad\u4e86\u4ed6\u7684\u8bdd\uff0c\u5374\u5c06\u4e00\u53cc\u7389\u624b\u7b14\u76f4\u7684\u4f38 \u4e86\u51fa\u6765\uff0c\u79cb\u6ce2\u56db\u8f6c\uff0c\u5a07\u7b11\u53c8\u9053\uff1a\u201c\u90a3\u4f60\u5c31\u8d76\u5feb\u6765\u5241\u5427\uff0c\u753b\u90a3\u5e45\u753b\u7684\uff0c\u53ef\u4e0d\u662f\u522b\u4eba\uff0c\u5c31\u662f\u6211 \u5440\uff01\u201d \u3000\u3000\u5c0f\u743c\u5c0f\u73b2\u4e00\u76f4\u63a9\u53e3\u76f8\u89c6\uff0c\u6b64\u523b\u518d\u4e5f\u5fcd\u4e0d\u4f4f\u201c\u6251\u54e7\u201d\u4e00\u58f0\u7b11\u51fa\u58f0\u6765\uff0c\u5353\u957f\u537f\u867d\u662f\u6ee1\u8179\u6012 \u706b\uff0c\u4f46\u6b64\u523b\u5374\u4e5f\u4e0d\u7981\u6697\u4e2d\u4e00\u7b11\uff0c\u5fc3\u60f3\u8fd9\u5c11\u5973\u5012\u662f\u4e2a\u53ef\u4eba\uff0c\u6545\u5bf9\u5979\u7684\u6076\u611f\u7adf\u4e5f\u6d88\u53bb\u51e0\u5206\u3002 \u3000\u3000\u5176\u5b9e\u8fd9\u5c11\u5973\u662f\u4ed6\u4ec7\u4eba\u95e8\u4e0b\uff0c\u65b9\u624d\u53c8\u4e58\u9699\u51fb\u4e86\u4ed6\u4e00\u638c\uff0c\u90a3\u9ec4\u886b\u5c11\u5e74\u5374\u548c\u4ed6\u7d20\u4e0d\u76f8\u8bc6\uff0c\u4ed6\u5bf9 \u8fd9\u5c11\u5973\u7684\u6076\u611f\uff0c\u672c\u5e94\u8fdc\u5728\u90a3\u9ec4\u886b\u5c11\u5e74\u4e4b\u4e0a\uff0c\u4f46\u4eba\u4eec\u7684\u60c5\u611f\uff0c\u5374\u662f\u90a3\u4e48\u5947\u602a\uff0c\u5353\u957f\u537f\u53ea\u89c9\u8fd9\u5c11 \u5973\u548c\u81ea\u5df1\u7684\u4ec7\u6068\u53c8\u662f\u53e6\u5916\u4e00\u56de\u4e8b\uff0c\u81f3\u5c11\u5979\u672c\u8eab\uff0c\u5e76\u65e0\u53ef\u538c\u53ef\u6068\u4e4b\u5904\uff0c\u800c\u90a3\u9ec4\u886b\u5c11\u5e74\u5728\u4ed6\u773c\u4e2d \u770b\u6765\uff0c\u6b64\u523b\u5374\u662f\u9762\u76ee\u53ef\u618e\uff0c\u8fd9\u5c11\u5973\u7528\u8a00\u8bcd\u4f24\u523a\u4e8e\u4ed6\uff0c\u5353\u957f\u537f\u5c31\u89c9\u5f97\u975e\u5e38\u75db\u5feb\u3002 \u3000\u3000\u4eba\u4eec\u7684\u559c\u6076\uff0c\u672c\u662f\u51fa\u4e8e\u672c\u6027\u7684\u76f4\u89c9\uff0c\u800c\u5e76\u975e\u51fa\u4e8e\u7406\u667a\u7684\u5224\u65ad\uff0c\u800c\u559c\u6076\u4e4b\u4e0e\u6069\u4ec7\uff0c\u6027\u8d28\u4e5f \u662f\u622a\u7136\u800c\u5f02\u7684\uff0c\u56e0\u4e3a\u6069\u4ec7\u7684\u5224\u522b\u5374\u5168\u7136\u662f\u51fa\u4e8e\u7406\u667a\uff0c\u8fd9\u5176\u4e2d\u7684\u5173\u7cfb\uff0c\u867d\u7136\u5fae\u5999\uff0c\u5374\u80fd\u89e3\u91ca\u3002 \u3000\u3000\u5353\u957f\u537f\u5fc3\u4e2d\u6697\u7b11\uff0c\u4fa7\u76ee\u4e00\u671b\uff0c\u53ea\u89c1\u90a3\u9ec4\u886b\u5c11\u5e74\u7ad9\u5728\u90a3\u91cc\uff0c\u9762\u4e0a\u7b11\u5bb9\u65b9\u655b\uff0c\u773c\u775b\u77aa\u7740\u90a3\u7edd \u8272\u4e3d\u4eba\u7684\u4e00\u53cc\u7389\u624b\u4e0a\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u518d\u4e5f\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u90a3\u7edd\u8272\u4e3d\u4eba\u79cb\u6ce2\u4e00\u7b11\uff0c\u660e\u7738\u5982\u7535\uff0c\u5728\u5353\u957f\u537f\u8eab\u4e0a\u4e00\u8f6c\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u6025\u4ec0\u4e48\uff0c\u4ed6\u8981\u662f\u80fd\u628a \u6211\u7684\u624b\u5241\u4e0b\u6765\uff0c\u4f60\u7684\u6c14\u4e0d\u662f\u4e5f\u51fa\u4e86\u5417\uff1f\u201d \u3000\u3000\u591a\u81c2\u795e\u5251\u7ad9\u5728\u57ce\u4e0b\uff0c\u770b\u5230\u90a3\u72c2\u50b2\u7684\u5c11\u5e74\u5c91\u7cb2\uff0c\u7a81\u7136\u5728\u57ce\u5934\u4e0a\u51fa\u73b0\uff0c\u7adf\u7136\u548c\u90a3\u7ea2\u88f3\u5c11\u5973\u4eec \u8c08\u7b11\u8d77\u6765\uff0c\u4ed6\u867d\u7136\u80fd\u591f\u5f88\u6e05\u695a\u5730\u542c\u5230\u5c91\u7cb2\u7684\u7b11\u58f0\uff0c\u5374\u542c\u4e0d\u6e05\u4ed6\u4eec\u7684\u8c08\u8bdd\u7684\u5185\u5bb9\u3002\u987b\u77e5\u5c91\u7cb2\u7b49 \u4eba\u7acb\u5728\u9ad8\u5904\uff0c\u8bdd\u58f0\u53c8\u4e0d\u751a\u9ad8\uff0c\u81ea\u6613\u88ab\u5f3a\u70c8\u7684\u6668\u98ce\u5439\u6563\uff0c\u662f\u4ee5\u4e24\u4eba\u82e5\u7acb\u5728\u5730\u52bf\u9ad8\u4f4e\u60ac\u6b8a\u7684\u5730\u65b9 \u901a\u8bdd\uff0c\u8fdc\u8f83\u7acb\u5728\u5e73\u5730\u7684\u76f8\u540c\u8ddd\u79bb\u56f0\u96be\u3002 \u3000\u3000\u591a\u81c2\u795e\u5251\u5fc3\u6025\u5982\u711a\uff0c\u6697\u5fd6\uff1a\u201c\u8fd9\u5c91\u7cb2\u82e5\u548c\u90a3\u4e9b\u5973\u5b50\u8054\u624b\uff0c\u957f\u537f\u4fbf\u6050\u4e0d\u662f\u653e\u624b\u2014\u2014\u201d\u5ff5\u5934 \u5c1a\u672a\u8f6c\u5b8c\uff0c\u53ea\u89c1\u5c91\u7cb2\u548c\u5353\u957f\u537f\u679c\u7136\u52a8\u8d77\u624b\u6765\u4e86\u3002 \u3000\u3000\u539f\u6765\u90a3\u9ec4\u886b\u5c11\u5e74\u5c91\u7cb2\u81ea\u4ee5\u4e3a\u975e\u5e38\u4fef\u6d12\u98ce\u8da3\u4ed6\u8bf4\u51fa\u8fd9\u756a\u8bdd\u6765\uff0c\u7ed3\u679c\u5374\u8ba8\u5f97\u4e2a\u6ca1\u8da3\u3002 \u3000\u3000\u4ed6\u4e43\u5341\u5206\u81ea\u6ee1\u81ea\u50b2\u4e4b\u4eba\uff0c\u6b64\u523b\u5fc3\u4e2d\u81ea\u662f\u7f9e\u607c\u4ea4\u96c6\uff0c\u5374\u53c8\u5c06\u90a3\u5c11\u5973\u65e0\u53ef\u5948\u4f55\uff0c\u76ee\u5149\u4e00\u8f6c\uff0c \u770b\u5230\u65c1\u8fb9\u4e00\u4e2a\u5c11\u5e74\u4f3c\u7b11\u975e\u7b11\u5730\u671b\u7740\u81ea\u5df1\uff0c\u4e0d\u7981\u5c06\u6ee1\u8154\u6012\u706b\u90fd\u53d1\u4f5c\u51fa\u672a\uff0c\u5389\u58f0\u9053\uff1a\u201c\u4f60\u7b11\u4ec0 \u4e48\uff1f\u201d\u5353\u957f\u537f\u5251\u7709\u4e00\u7ad6\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u9601\u4e0b\u8a00\u8bed\u653e\u5e84\u91cd\u4e9b\uff0c\u81ea\u7136\u4fbf\u5c31\u65e0\u4eba\u7b11\u4f60\u3002\u201d \u3000\u3000\u5c91\u7cb2\u5927\u559d\u4e00\u58f0\uff0c\u9661\u7136\u5411\u5353\u957f\u537f\u51b2\u4e86\u8fc7\u53bb\uff0c\u626c\u624b\u4e00\u638c\uff0c\u63b4\u5411\u5353\u957f\u537f\u7684\u9762\u988a\u3002 \u3000\u3000\u5353\u957f\u537f\u4e0d\u7981\u5927\u6012\uff0c\u624b\u8155\u4e00\u7ffb\uff0c\u53cd\u624b\u53bb\u5201\u5c91\u7cb2\u7684\u624b\u8155\uff0c\u5de6\u638c\u5374\u4ece\u53f3\u65f6\u4e0b\u7a7f\u51fa\uff0c\u9a84\u6307\u5982\u5251\uff0c \u6307\u5411\u4ed6\u7684\u808b\u4e0b\u3002 \u3000\u3000\u4ed6\u8eab\u5f62\u672a\u52a8\uff0c\u5374\u75be\u5982\u95ea\u7535\u822c\u53d1\u51fa\u4e24\u62db\uff0c\u6b63\u662f\u653b\u5b88\u4ff1\u4f73\u7684\u5999\u7740\uff0c\u90a3\u9ec4\u886b\u5c11\u5e74\u5c91\u7cb2\u4f3c\u4e4e\u5fae\u5fae \u4e00\u6014\uff0c\u60f3\u4e0d\u5230\u8fd9\u5bf9\u624b\u7adf\u662f\u5982\u6b64\u9ad8\u624b\uff0c\u4e0d\u7981\u5c3d\u53bb\u8f7b\u654c\u4e4b\u5ff5\uff0c\u53f3\u638c\u731b\u4e00\u4f38\u7f29\uff0c\u500f\u53c8\u62cd\u51fa\u4e24\u638c\u3002\u4e24 \u4eba\u7ad9\u5728\u540c\u4e00\u57ce\u579b\u4e4b\u4e0a\uff0c\u811a\u4e0b\u4ff1\u672a\u66fe\u52a8\uff0c\u77ac\u606f\u4e4b\u95f4\uff0c\u5374\u5df2\u62c6\u4e86\u5341\u4f59\u62db\uff0c\u90a3\u7edd\u8272\u5c11\u5973\u8f7b\u8f7b\u4e00\u7b11\uff0c \u548c\u5c0f\u73b2\u5c0f\u743c\u8fdc\u8fdc\u7ad9\u4e86\u5f00\u53bb\uff0c\u7b11\u541f\u541f\u5730\u770b\u7740\u4ed6\u4eec\u52a8\u624b\u3002 \u3000\u3000\u4f46\u5979\u9762\u4e0a\u867d\u5e26\u7740\u7b11\u5bb9\uff0c\u5fc3\u4e2d\u5374\u4e0d\u7981\u6697\u5730\u5403\u60ca\uff0c\u987b\u77e5\u5c91\u7cb2\u548c\u5353\u957f\u537f\u6b64\u523b\u52a8\u624b\uff0c\u770b\u6765\u867d\u6781\u5e73 \u6de1\uff0c\u5176\u5b9e\u8fd9\u79cd\u8fd1\u8eab\u800c\u6597\uff0c\u5374\u8fdc\u6bd4\u56db\u5904\u6e38\u8d70\u6765\u5f97\u51f6\u9669\uff0c\u8fd9\u4e24\u4eba\u4e3e\u624b\u6295\u8db3\u95f4\uff0c\u6240\u4f7f\u7684\u7adf\u90fd\u662f\u6700\u4e0a \u4e58\u7684\u529f\u592b\uff0c\u53ea\u8981\u7a0d\u6709\u758f\u5ffd\uff0c\u4fbf\u7acb\u523b\u5c31\u8981\u88ab\u5bf9\u65b9\u4f24\u5728\u638c\u4e0b\u3002 \u3000\u3000\u8fd9\u7edd\u8272\u4e3d\u4eba\u81ea\u5df1\u8eab\u6000\u7edd\u6280\uff0c\u6b64\u523b\u7109\u6709\u770b\u4e0d\u51fa\u6765\u7684\u9053\u7406\u3002 \u3000\u3000\u5979\u79cb\u6ce2\u56db\u8f6c\uff0c\u76ee\u5149\u4e00\u4f1a\u51dd\u6ce8\u5728\u57ce\u4e0a\uff0c\u4e00\u4f1a\u53c8\u8f6c\u5230\u57ce\u4e0b\uff0c\u7a81\u7136\u8f7b\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4f4d \u5728\u8fd9\u91cc\u591a\u73a9\u4e00\u4f1a\u513f\u5427\uff0c\u5c0f\u743c\u3001\u5c0f\u73b2\uff0c\u6211\u4eec\u53ef\u5f97\u8d70\u4e86\u3002\u201d \u3000\u3000\u67f3\u8170\u4e00\u62e7\uff0c\u7adf\u84e6\u5730\u671d\u57ce\u5916\u7eb5\u843d\u3002 \u3000\u3000\u5c0f\u743c\u5c0f\u73b2\u63a2\u9996\u4e00\u671b\u57ce\u4e0b\uff0c\u8f7b\u8f7b\u4e00\u76b1\u7709\u5934\uff0c\u4e5f\u968f\u4e4b\u63a0\u4e86\u4e0b\u53bb\uff0c\u4e00\u9762\u5a07\u559d\u9053\uff1a\u201c\u8c28\u59d1\u5a18\uff0c\u60a8 \u53ef\u5f97\u63a5\u7740\u6211\u4eec\u4e00\u70b9\u3002\uff0c\u5353\u957f\u537f\u76ee\u5149\u4e00\u8f6c\uff0c\u5927\u559d\u9053\uff1a\u201c\u4e14\u6162\u3002\u201d \u3000\u3000\u201c\u547c\u201d\u7684\u5288\u51fa\u4e00\u638c\uff0c\u5c06\u5c91\u7cb2\u903c\u5f00\u4e00\u6b65\uff0c\u731b\u4e00\u957f\u8eab\uff0c\u4ea6\u81ea\u63a0\u5411\u57ce\u4e0b\u3002 \u3000\u3000\u90a3\u9ec4\u886b\u5c11\u5e74\u5fae\u5fae\u4e00\u6014\uff0c\u8f6c\u8eab\u8fc7\u53bb\uff0c\u53ea\u89c1\u524d\u9762\u4e09\u6761\u7ea2\u5f71\uff0c\u6709\u5982\u6d41\u661f\u7ecf\u5929\uff0c\u5982\u98de\u5730\u5411\u57ce\u5916\u7684 \u4e00\u5ea7\u4e1b\u6797\u63a0\u53bb\uff0c\u540e\u9762\u4e00\u6761\u4e4c\u5f71\uff0c\u8854\u5c3e\u6025\u8ffd\uff0c\u7728\u773c\u4e4b\u95f4\uff0c\u8fd9\u56db\u6761\u4eba\u5f71\u7adf\u90fd\u5df2\u63a0\u53bb\u5f88\u8fdc\u3002 \u3000\u3000\u4ed6\u6697\u53f9\u4e00\u58f0\uff0c\u5fc3\u4e2d\u7684\u50b2\u6c14\u7adf\u4e3a\u4e4b\u6d88\u53bb\u4e00\u4e9b\uff0c\u4ea6\u81ea\u5411\u57ce\u4e0b\u63a0\u53bb\u3002 \u3000\u3000\u591a\u81c2\u795e\u5251\u4e91\u8c26\u672c\u5728\u5173\u5fc3\u7740\u5353\u957f\u537f\u7684\u5b89\u5371\uff0c\u6b63\u5f85\u8bbe\u6cd5\u4e0a\u57ce\u52a9\u4ed6\u4e00\u81c2\u4e4b\u529b\uff0c\u54ea\u77e5\u77ac\u606f\u4e4b\u95f4\uff0c \u60c5\u5f62\u7adf\u7136\u53d8\u5316\u5982\u6b64\u3002\u5353\u957f\u537f\u7b49\u4eba\u63a0\u5230\u57ce\u5916\u4e4b\u540e\u7684\u60c5\u5f62\u5982\u4f55\uff0c\u4ed6\u5728\u57ce\u5185\u81ea\u7136\u65e0\u6cd5\u770b\u5230\u3002 \u3000\u3000\u4e91\u4e2d\u7a0b\u53cc\u7709\u7d27\u76b1\uff0c\u7ad9\u5728\u4ed6\u7239\u7239\u8eab\u4fa7\uff0c\u56de\u76ee\u56db\u671b\uff0c\u53ea\u89c1\u7fa4\u8c6a\u591a\u5df2\u9646\u7eed\u6563\u53bb\uff0c\u5404\u4e2a\u90fd\u5728\u60ca\u8bb6 \u4f4e\u8bed\uff0c\u4e0d\u77e5\u9053\u65b9\u624d\u8fd9\u573a\u6fc0\u6597\uff0c\u7a76\u7adf\u662f\u4e3a\u7740\u4ec0\u4e48\uff0c\u5374\u53c8\u7cca\u91cc\u7cca\u6d82\u5730\u4e0d\u4e86\u4e86\u4e4b\u3002 \u3000\u3000\u591a\u81c2\u795e\u5251\u624b\u634b\u957f\u987b\uff0c\u5fae\u4e00\u8dfa\u8db3\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u4e2d\u7a0b\uff0c\u5230\u57ce\u5916\u770b\u770b\u3002\u201d \u3000\u3000\u4e00\u64a9\u957f\u886b\uff0c\u5927\u6b65\u5411\u57ce\u5916\u8d70\u53bb\u3002 \u3000\u3000\u6b64\u523b\u65e9\u5e02\u5df2\u8d77\uff0c\u57ce\u95e8\u5185\u5916\uff0c\u4eba\u7fa4\u7199\u6765\u6518\u5f80\uff0c\u4e91\u8c26\u6b32\u6025\u6b65\u800c\u5954\uff0c\u867d\u672a\u65bd\u51fa\u8f7b\u529f\uff0c\u5374\u5df2\u4f7f\u5f97 \u884c\u4eba\u9a7b\u76ee\u800c\u89c6\uff0c\u5fc3\u91cc\u5947\u602a\uff0c\u4ee5\u4e3a\u8fd9\u8001\u5934\u5b50\u75af\u4e86\u3002 \u3000\u3000\u4e00\u4e2a\u6311\u7740\u62c5\u5b50\u7684\u83dc\u8d29\uff0c\u88ab\u4ed6\u8f7b\u8f7b\u4e00\u649e\uff0c\u8e6c\u3001\u8e6c\u3001\u8e6c\uff0c\u8fde\u9000\u51e0\u6b65\uff0c\u9669\u4e9b\u5012\u5728\u5730\u4e0a\uff0c\u65b9\u81ea\u9a82 \u4e86\u65ec\uff1a\u201c\u8fd9\u4e2a\u8001\u75af\u5b50\u2014\u2014\u201d\u54ea\u77e5\u4e00\u4e2a\u767d\u9762\u5fae\u987b\u7684\u6c49\u5b50\u7a81\u4e5f\u5954\u4e86\u8fc7\u6765\uff0c\u4f38\u624b\u5728\u4ed6\u80a9\u4e0a\u4e00\u62cd\uff0c \u9053\uff1a\u201c\u5634\u91cc\u5e72\u51c0\u4e9b\u3002\u201d \u3000\u3000\u4ed6\u62ac\u5934\u4e00\u671b\uff0c\u53ea\u89c1\u8fd9\u6c49\u5b50\u76ee\u5149\u4e2d\u5a01\u68f1\u95ea\u73b0\uff0c\u5413\u5f97\u5c06\u672a\u9a7e\u5b8c\u7684\u8bdd\u90fd\u54bd\u56de\u809a\u91cc\u3002 \u3000\u3000\u4e91\u4e2d\u7a0b\u968f\u624b\u4e00\u638f\uff0c\u638f\u51fa\u534a\u952d\u94f6\u5b50\uff0c\u629b\u5728\u8fd9\u83b1\u8d29\u811a\u4e0b\uff0c\u8f6c\u8eab\u5954\u51fa\u57ce\u5916\uff0c\u53ea\u89c1\u4ed6\u7239\u7239\u7ad9\u5728\u4e00 \u5757\u77f3\u58a9\u4e0a\uff0c\u4f38\u9888\u56db\u671b\uff0c\u4f46\u6b64\u523b\u9664\u4e86\u8fd9\u6761\u5411\u57ce\u5916\u7684\u4e00\u6761\u5b98\u9053\u4e0a\uff0c\u4e0d\u65f6\u6709\u725b\u8f66\u83b1\u8d29\u3001\u884c\u5546\u8d70\u5352\u5f80 \u6765\u800c\u884c\u4e4b\u5916\uff0c\u90a3\u5353\u957f\u537f\u548c\u7ea2\u88f3\u5c11\u5973\u4eec\uff0c\u5374\u8fde\u5f71\u5b50\u90fd\u770b\u4e0d\u5230\u4e86\u3002 \u3000\u3000\u6b66\u6797\u4e2d\u7684\u6069\u4ec7\u6b8b\u6740\uff0c\u4f7f\u5f97\u4e34\u5b89\u57ce\u5916\u7684\u5b89\u5206\u5c45\u6c11\uff0c\u5fc3\u4e2d\u90fd\u6709\u4e9b\u60ca\u60f6\uff0c\u5bf9\u4e8e\u884c\u72b6\u7565\u4e3a\u624e\u773c\u7684 \u4eba\uff0c\u8fde\u6b63\u773c\u90fd\u4e0d\u6562\u91cc\u4e00\u773c\uff0c\u57ce\u95e8\u53e3\u7684\u5175\u5352\u4e5f\u591a\u4e86\u8d77\u6765\uff0c\u625b\u7740\u7ea2\u7f28\u67aa\uff0c\u56da\u4e0b\u67e5\u5de1\uff0c\u5176\u5b9e\u4ed6\u4eec\u4e5f \u5728\u5fc3\u91cc\u53d1\u614c\uff0c\u770b\u5230\u4e91\u6c0f\u7236\u5b50\uff0c\u90fd\u6545\u610f\u8d70\u5230\u53e6\u4e00\u8fb9\u53bb\uff0c\u751f\u6015\u7978\u4e8b\u4e34\u5230\u81ea\u5df1\u5934\u4e0a\u3002 \u3000\u3000\u591a\u81c2\u795e\u5251\u6781\u65e5\u56db\u987e\uff0c\u56db\u91ce\u4e00\u7247\u9752\u7eff\uff0c\u8def\u4e0a\u6765\u5f80\u7684\u884c\u4eba\uff0c\u4e5f\u6709\u4e9b\u5c06\u8eab\u4e0a\u5355\u8584\u8863\u886b\u7684\u8896\u5b50\uff0c \u9ad8\u9ad8\u633d\u4e86\u8d77\u6765\uff0c\u4f46\u8fd9\u5df2\u7ecf\u5782\u66ae\u7684\u6b66\u6797\u5065\u8005\u5fc3\u4e2d\u5374\u4e0d\u7981\u6697\u53f9\uff0c\u77e5\u9053\u6b64\u523b\u867d\u662f\u76db\u590f\uff0c\u53ea\u662f\u8ddd\u79bb\u79cb \u5929\uff0c\u5374\u4e00\u5929\u6bd4\u4e00\u5929\u7684\u8fd1\u4e86\u3002 \u3000\u3000\u4e8e\u662f\u6709\u8bb8\u591a\u4ed6\u672c\u6781\u4e3a\u770b\u91cd\u7684\u4e8b\uff0c\u5728\u8fd9\u4e00\u5239\u90a3\u91cc\uff0c\u5374\u4f3c\u4e4e\u5df2\u90fd\u4e0d\u518d\u653e\u5728\u5fc3\u4e0a\uff0c\u957f\u53f9\u4e00\u58f0\uff0c \u6c89\u58f0\u9053\uff1a\u201c\u4e2d\u7a0b\uff0c\u6211\u770b\u2014\u2014\u6211\u4eec\u8fd8\u662f\u8fdb\u57ce\u5427\uff0c\u53cd\u6b63\u957f\u537f\uff0c\u4ed6\u2014\u2014\u4ed6\u4e5f\u4e0d\u4f1a\u51fa\u4ec0\u4e48\u4e8b\u7684\u3002\u201d \u3000\u3000\u4e91\u4e2d\u7a0b\u5fae\u5fae\u4e00\u6014\uff0c\u62ac\u8d77\u5934\u6765\uff0c\u76db\u590f\u7684\u65ed\u65e5\u4e4b\u5149\uff0c\u521a\u597d\u7167\u5728\u4ed6\u7239\u7239\u7684\u9762\u4e0a\uff0c\u4e8e\u662f\u8fd9\u8001\u4eba\u9762 \u4e0a\u7684\u76b1\u7eb9\u4e5f\u8d8a\u53d1\u6e05\u6670\u4e86\u3002 \u3000\u3000\u8fd9\u4e00\u77ac\u95f4\uff0c\u4e91\u4e2d\u7a0b\u89c9\u5f97\u4ed6\u7239\u7239\u4eff\u4f5b\u53c8\u82cd\u8001\u4e86\u8bb8\u591a\uff0c\u4ed6\u604d\u60da\u5fc6\u53ca\u5f53\u5e74\u4ed6\u5e74\u7eaa\u8fd8\u5f88\u5c0f\u7684\u65f6 \u5019\uff0c\u4e5f\u66fe\u7ecf\u4e0d\u6b62\u4e00\u6b21\u5730\u62ac\u5934\u671b\u7740\u4ed6\u7239\u7239\u7684\u9762\u5e9e\uff0c\u90a3\u65f6\uff0c\u8fd9\u5f20\u9762\u5b54\u5728\u4ed6\u773c\u4e2d\uff0c\u6709\u5982\u5929\u795e\u822c\u8f89 \u714c\u3002 \u3000\u3000\u7136\u800c\u6b64\u523b\uff0c\u90a3\u79cd\u8f89\u714c\u7684\u5149\u5f69\uff0c\u5374\u6c38\u8fdc\u5728\u8fd9\u5f20\u9762\u5b54\u4e0a\u6d88\u5931\u4e86\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u4e5f\u5728\u5fc3\u4e2d\u957f\u53f9\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u7239\u7239\uff0c\u6211\u4eec\u8fd8\u662f\u56de\u53bb\u5427\u8fde\u65e5\u6765\u4e1b\u751f\u7684\u53d8\u6545\uff0c\u4f7f\u5f97\u8fd9\u5014 \u5f3a\u7684\u8001\u4eba\u53e3\u5934\u867d\u4e0d\u670d\u8001\uff0c\u4f46\u5fc3\u4e2d\u8c6a\u6c14\u5374\u6d88\u53bb\u4e86\u8bb8\u591a\uff0c\u4ed6\u8f6c\u76ee\u4e00\u671b\u4e91\u4e2d\u7a0b\uff0c\u76ee\u5149\u500f\u7136\u95ea\u8fc7\u4e00\u4e1d \u96be\u8a00\u7684\u5149\u8292\uff0c\u54fa\u54fa\u53f9\u9053\uff1a\u201c\u58ee\u58eb\u66ae\u5e74\uff0c\u96c4\u5fc3\u672a\u5df2\u2014\u2014\u96c4\u5fc3\u672a\u5df2\u2014\u2014\u5509\uff0c\u4e2d\u7a0b\uff0c\u56de\u53bb\u4e5f\u597d\u3002\u201d \u3000\u3000\u4f38\u51fa\u4e00\u53cc\u90a3\u5df2\u56e0\u5c81\u6708\u7684\u6d88\u78e8\u800c\u53d8\u5f97\u6709\u4e9b\u677e\u5f1b\u7684\u624b\u638c\uff0c\u8f7b\u8f7b\u642d\u5728\u4ed6\u7231\u5b50\u7684\u80a9\u4e0a\uff0c\u7f13\u6b65\u5411\u57ce \u5185\u8d70\u53bb\u3002 \u3000\u3000\u6b64\u523b\u867d\u662f\u76db\u5413\uff0c\u4f46\u540d\u503e\u6c5f\u5357\u7684\u829c\u6e56\u4e91\u95e8\u7236\u5b50\uff0c\u5374\u6709\u7740\u66ae\u79cb\u822c\u7684\u5fc3\u60c5\uff0c\u70bd\u70ed\u7684\u9633\u5149\u7167\u5728\u4ed6 \u4eec\u8eab\u4e0a\uff0c\u5374\u4e5f\u751f\u50cf\u662f\u518d\u4e5f\u6ca1\u6709\u4ec0\u4e48\u6696\u610f\u3002 \u3000\u3000\u4e91\u8c26\u4fa7\u76ee\u4e00\u987e\uff0c\u4e0d\u7981\u53c8\u81ea\u53f9\u9053\uff1a\u201c\u4e2d\u7a0b\uff0c\u957f\u6c5f\u540e\u6d6a\u63a8\u524d\u6d6a\uff0c\u6211\u770b\u2014\u2014\u4f60\u4e5f\u65e9\u4e9b\u6d17\u624b\u7b97 \u4e86\uff0c\u4eca\u65e5\u4e4b\u6c5f\u6e56\uff0c\u5509\u2014\u2014\u5df2\u4e0d\u518d\u662f\u8bdd\u72b9\u672a\u4e86\uff0c\u8eab\u540e\u7a81\u5730\u54cd\u8d77\u4e00\u58f0\u9ad8\u4ea2\u7684\u547c\u58f0\uff0c\u559d\u9053\uff1a\u201c\u524d\u9762 \u7684\u53ef\u662f\u4e91\u8001\u7237\u5b50\u5417\uff1f\u201d \u3000\u3000\u547c\u559d\u4e4b\u58f0\uff0c\u968f\u7740\u6025\u8fd9\u7684\u9a6c\u8e44\u58f0\u987a\u98ce\u4f20\u6765\uff0c\u591a\u81c2\u795e\u5251\u9a7b\u8db3\u56de\u987e\uff0c\u53ea\u89c1\u4e09\u5339\u5065\u9a6c\u7bad\u4e5f\u4f3c\u7684\u5728 \u5b98\u9053\u4e0a\u6025\u9a70\u800c\u6765\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u5fae\u4e00\u9a7b\u8db3\u95f4\uff0c\u8fd9\u51e0\u5339\u9a6c\u90fd\u5df2\u51b2\u5230\u4ed6\u9762\u524d\u3002 \u3000\u3000\u5065\u9a6c\u626c\u8e44\u6602\u9996\u95f4\uff0c\u557c\u5f8b\u4e00\u58f0\u957f\u5636\uff0c\u9a6c\u4e0a\u7684\u9a91\u58eb\uff0c\u77eb\u5065\u5730\u63a0\u4e0b\u9a6c\u6765\uff0c\u7adf\u4e0d\u518d\u7406\u4f1a\u90a3\u957f\u5636\u7740 \u7684\u5750\u9a91\uff0c\u201c\u55d6\u201d\u7684\u4e00\u4e2a\u7bad\u6b65\u7a9c\u4e86\u8fc7\u6765\uff0c\u4e91\u8c26\u53cc\u7709\u65b9\u81ea\u4e00\u76b1\uff0c\u54ea\u77e5\u8fd9\u6761\u6c49\u5b50\u5c31\u5728\u8fd9\u5b98\u9053\u4e0a\uff0c\u7adf \u201c\u5413\u201d\u7684\u4e00\u58f0\uff0c\u5411\u81ea\u5df1\u8dea\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u4ed6\u4e0d\u7981\u4e3a\u4e4b\u4e00\u6014\uff0c\u76ee\u5149\u8f6c\u5904\uff0c\u53ea\u89c1\u8fd9\u6c49\u5b50\u8863\u886b\u51cc\u4e71\uff0c\u98ce\u5c18\u6ee1\u9762\uff0c\u76ee\u5149\u4e4b\u4e2d\u66f4\u662f\u6ee1\u5e26\u60ca\u60f6 \u4e4b\u8272\uff0c\u50cf\u662f\u5929\u906d\u5de8\u53d8\uff0c\u5fc3\u4e2d\u65b9\u81ea\u4e00\u52a8\u3002 \u3000\u3000\u54ea\u77e5\u8fd9\u6761\u6c49\u5b50\u5df2\u8fde\u8fde\u53e9\u9996\u9053\uff1a\u201c\u4e91\u8001\u7237\u5b50\uff0c\u4f60\u8001\u4eba\u5bb6\u5927\u6982\u4e0d\u8bb0\u5f97\u5c0f\u4eba\u662f\u8c01\uff0c\u5c0f\u4eba\u5374\u5728\u592a \u6e56\u603b\u5be8\u91cc\u89c1\u8fc7\u4f60\u8001\u4eba\u5bb6\u4e00\u9762\u591a\u81c2\u795e\u5251\u54e6\u4e86\u4e00\u58f0\uff0c\u63a5\u53e3\u9053\uff1a\u201c\u539f\u6765\u5144\u53f0\u662f\u8d3a\u4e09\u7237\u7684\u95e8\u4e0b\uff0c\u6709\u8bdd \u597d\u8bf4\uff0c\u5feb\u5feb\u8d77\u6765\uff0c\u8d3a\u4e09\u7237\u8fd9\u4e00\u5411\u53ef\u597d\u5417\uff1f\u5509\uff01\u592a\u6e56\u4e00\u522b\uff0c\u4e00\u522b\u591a\u5e74\uff0c\u8001\u592b\u5df2\u6709\u8bb8\u591a\u65e5\u5b50\u6ca1\u6709 \u770b\u5230\u4ed6\u4e86\u3002\u201d \u3000\u3000\u90a3\u6761\u6c49\u5b50\u5374\u4ecd\u8dea\u5728\u5730\u4e0a\uff0c\u9762\u4e0a\u84e6\u5730\u6cdb\u51fa\u60b2\u6070\u4e4b\u8272\uff0c\u957f\u53f9\u9053\uff1a\u201c\u4f60\u8001\u4eba\u5bb6\u6050\u6015\u518d\u4e5f\u89c1\u4e0d\u5230 \u6211\u4eec\u7684\u8d3a\u4e09\u7237\u4e86\u3002\u201d \u3000\u3000\u591a\u81c2\u795e\u5251\u9762\u76ee\u9aa4\u53d8\uff0c\u6025\u58f0\u95ee\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d \u3000\u3000\u90a3\u6c49\u5b50\u4f38\u624b\u4e00\u62b9\u9762\u4e0a\u7684\u6c57\u73e0\uff0c\u63a5\u7740\u9053\uff1a\u201c\u4ed6\u8001\u4eba\u5bb6\uff0c\u5728\u4f59\u676d\u57ce\u91cc\u2014\u2014\u5df2\u906d\u4e86\u522b\u4eba\u7684\u6bd2 \u624b\uff0c\u5c0f\u4eba\u4eec\u65e0\u80fd\uff0c\u8fde\u5bb3\u6b7b\u4ed6\u8001\u4eba\u5bb6\u7684\u4ec7\u5bb6\u662f\u8c01\u90fd\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u4e91\u4e2d\u7a0b\u76ee\u5149\u56db\u8f6c\uff0c\u53ea\u89c1\u6765\u5f80\u7684\u884c\u4eba\uff0c\u90fd\u7981\u4e0d\u4f4f\u5411\u81ea\u5df1\u8fd9\u8fb9\u6295\u6765\u60ca\u8be7\u7684\u76ee\u5149\uff0c\u5251\u7709\u5fae\u76b1\uff0c \u4f38\u624b\u62c9\u8d77\u8fd9\u6c14\u6025\u8d25\u574f\u7684\u6c49\u5b50\uff0c\u9053\uff1a\u201c\u5144\u53f0\u4e14\u5b9a\u5b9a\u795e\uff0c\u6709\u8bdd\u4e0d\u59a8\u4eba\u57ce\u518d\u8bf4\u2014\u2014\u201d\u90a3\u6c49\u5b50\u53cc\u624b\u636e \u5730\uff0c\u5374\u4f0f\u5728\u5730\u4e0a\u4e0d\u80af\u8d77\u6765\uff0c\u4e00\u9762\u8fde\u58f0\u9053\uff1a\u201c\u4e91\u8001\u7237\u5b50\uff0c\u60a8\u8ddf\u6211\u4eec\u603b\u8235\u4e3b\u662f\u9053\u4e49\u4e4b\u4ea4\uff0c\u8fd9\u4ef6\u4e8b \u5c31\u5168\u51ed\u60a8\u8001\u4eba\u5bb6\u505a\u4e3b\u4e86\u3002\u201d \u3000\u3000\u591a\u81c2\u795e\u5251\u957f\u53f9\u4e00\u58f0\uff0c\u8fde\u8fde\u8dfa\u811a\uff0c\u4e91\u4e2d\u7a0b\u624b\u4e0a\u5fae\u4e00\u65bd\u52b2\uff0c\u5c06\u90a3\u6c49\u5b50\u4ece\u5730\u4e0a\u62c9\u4e86\u8d77\u6765\uff0c\u4e00\u8d77 \u8d70\u53e3\u57ce\u91cc\uff0c\u6b64\u523b\u4e34\u5b89\u57ce\u91cc\u7684\u6b66\u6797\u8c6a\u58eb\uff0c\u6b63\u662f\u4eba\u4eba\u60f6\u6050\u4e0d\u5b89\uff0c\u751f\u6015\u53c8\u6709\u4ec0\u4e48\u7978\u4e8b\u8f6e\u5230\u81ea\u5df1\u5934\u4e0a \u6765\u3002\u5230\u4e86\u4e91\u6c0f\u7236\u5b50\u843d\u811a\u4e4b\u5904\uff0c\u90a3\u6c49\u5b50\u5c31\u5c06\u4f59\u676d\u7684\u53d8\u6545\u6ed4\u6ed4\u4e0d\u7edd\u8bf4\u4e86\u51fa\u6765\uff0c\u4e91\u6c0f\u7236\u5b50\u8fd9\u624d\u77e5 \u9053\uff0c\u5929\u76ee\u5c71\u9e93\u7684\u90bb\u8fd1\u5404\u57ce\uff0c\u8fd9\u51e0\u5929\u6765\u7adf\u90fd\u662f\u8fed\u751f\u60e8\u53d8\uff0c\u90a3\u8fb9\u7684\u906d\u9047\u7adf\u4e5f\u548c\u4e34\u5b89\u57ce\u91cc\u7684\u5feb\u5200\u4f1a \u548c\u7ea2\u4e2d\u4f1a\u4e00\u6837\uff0c\u4e0d\u660e\u4e0d\u767d\u5730\u4e27\u4e86\u6027\u547d\u3002 \u3000\u3000\u6c5f\u6e56\u98ce\u6ce2\uff0c\u867d\u672c\u9669\u6076\uff0c\u4f46\u767e\u5341\u5e74\u6765\uff0c\u6b66\u6797\u4e2d\u5374\u4ece\u592b\u53d1\u751f\u8fc7\u5982\u6b64\u60e8\u9177\u7684\u5c60\u6740\uff0c\u56e0\u4e3a\u5728\u5c60\u6740 \u8fc7\u540e\uff0c\u8fd9\u51f6\u624b\u7a76\u7adf\u662f\u8c01\uff1f\u666e\u5929\u4e4b\u4e0b\u7adf\u6ca1\u6709\u4e00\u4e2a\u77e5\u9053\u771f\u76f8\u7684\u3002 \u3000\u3000\u591a\u81c2\u795e\u5251\u4e91\u8c26\u5386\u7ecf\u98ce\u5c18\uff0c\u53ef\u8bf4\u662f\u6b66\u6797\u7ecf\u9a8c\u4e30\u5bcc\u5230\u4e86\u6781\u70b9\u7684\u8001\u6c5f\u6e56\u4e86\uff0c\u6b64\u523b\u5374\u4e5f\u4e0d\u7981\u5168\u7136 \u6ca1\u4e86\u4e3b\u610f\uff0c\u4ed6\u867d\u6709\u4e3a\u6c5f\u6e56\u4e3b\u6301\u516c\u9053\u4e4b\u5fc3\uff0c\u4f46\u5374\u65e0\u4e3a\u6b66\u6797\u4f38\u5f20\u6b63\u4e49\u4e4b\u529b\uff0c\u4f55\u51b5\uff0c\u4ed6\u5373\u4f7f\u6709\u7740\u8fd9 \u4efd\u529b\u91cf\uff0c\u5374\u4e5f\u65e0\u6cd5\u5bfb\u5f97\u90a3\u51b7\u9177\u800c\u795e\u79d8\u7684\u51f6\u624b\u5440\uff01 \u3000\u3000\u4ed6\u5e0c\u671b\u5353\u957f\u537f\u56de\u6765\u65f6\u5019\uff0c\u80fd\u5e26\u56de\u4e00\u4e9b\u522b\u4eba\u4e0d\u77e5\u9053\u7684\u6d88\u606f\u3002 \u3000\u3000\u4f46\u7531\u6e05\u6668\u800c\u508d\u665a\uff0c\u7531\u508d\u665a\u800c\u6df1\u591c\u2014\u2014\u4e00\u76f4\u5230\u591c\u5df2\u5f88\u6df1\u4e86\uff0c\u5353\u957f\u537f\u5374\u4ecd\u7136\u6ca1\u6709\u56de\u6765\uff0c\u4e8e \u662f\uff0c\u591a\u81c2\u795e\u5251\u5728\u79cd\u79cd\u5fe7\u8651\u4e4b\u4e0b\uff0c\u53c8\u5f00\u59cb\u4e3a\u8fd9\u5c11\u5e74\u7684\u5b89\u5168\u800c\u5fe7\u8651\u4e86\u3002 \u3000\u3000\u5728\u8fd9\u4e00\u6574\u5929\u7126\u6025\u7684\u7b49\u5f85\u4e4b\u540e\uff0c\u4ed6\u53d1\u89c9\u8fd9\u4ef6\u4e8b\u4ece\u4e00\u5f00\u59cb\uff0c\u5c31\u6709\u8bb8\u591a\u503c\u5f97\u7591\u60d1\u4e4b\u5904\uff0c\u6b64\u672c\u7531 \u90a3\u6c5f\u6e56\u5de8\u5bcc\u3001\u6b66\u6797\u795e\u5077\u4e54\u8fc1\u624b\u4e0a\u7684\u4e09\u5e45\u753b\u5377\u5f00\u7aef\uff0c\u76f4\u5230\u6b64\u523b\uff0c\u8fd9\u4e54\u8fc1\u5374\u59cb\u7ec8\u672a\u518d\u73b0\u8fc7\u884c\u8e2a\u3002 \u3000\u3000\u4e8e\u662f\uff0c\u4ed6\u5bf9\u8fd9\u4e8b\u771f\u5b9e\u7684\u76ee\u7684\u5f00\u59cb\u53d1\u751f\u4e86\u6000\u7591\uff0c\u96be\u9053\u90a3\u4e09\u5e45\u4e00\u5377\u53ea\u662f\u90a3\u9b54\u5934\u4e11\u4eba\u6e29\u5982\u7389\u7684 \u9999\u9975\uff0c\u76ee\u7684\u53ea\u662f\u8981\u5c06\u5929\u4e0b\u6b66\u6797\u8c6a\u58eb\u90fd\u8bf1\u5230\u8fd9\u5929\u76ee\u5c71\u6765\uff0c\u7136\u540e\u518d\u9010\u4e2a\u51fb\u6740\uff0c\u4e00\u7f51\u6253\u5c3d\u3002 \u3000\u3000\u8fd9\u5ff5\u5934\u4e00\u7ecf\u5728\u4ed6\u5fc3\u4e2d\u95ea\u8fc7\uff0c\u8fd9\u4e45\u7ecf\u4e16\u6545\u7684\u8001\u4eba\u5fc3\u4e2d\uff0c\u4e5f\u4e0d\u7981\u5f00\u59cb\u6cdb\u51fa\u4e00\u9635\u9635\u5bd2\u610f\u3002 \u3000\u3000\u201c\u56e0\u6b64\u90a3\u4e24\u4e2a\u7ea2\u88f3\u5c11\u5973\u624d\u4f1a\u7981\u6b62\u5728\u6ca1\u6709\u4e0a\u5c71\u53c2\u4e0e\u6b64\u4f1a\u4e4b\u524d\uff0c\u5c31\u4e0d\u5f97\u64c5\u81ea\u79bb\u53bb\u2014\u2014\u201d\u4ed6\u6697 \u4e2d\u601d\u5fd6\u7740\uff0c\u63a8\u7a76\u7740\u6b64\u4e8b\u7684\u771f\u76f8\u3002 \u3000\u3000\u201c\u4f46\u65e2\u662f\u5982\u6b64\uff0c\u90a3\u4e48\u90a3\u9650\u4ee4\u4ed6\u4eec\u5728\u4e24\u65e5\u4e4b\u4e2d\u79bb\u5f00\u6b64\u57ce\u7684\uff0c\u53c8\u662f\u4ec0\u4e48\u4eba\u5462\uff1f\u201d \u3000\u3000\u4e8e\u662f\u4ed6\u53c8\u5f00\u59cb\u9677\u5165\u8ff7\u4e71\u7684\u7591\u4e91\u4e4b\u4e2d\uff0c\u56e0\u4e3a\u6b64\u4e8b\u4ece\u5934\u5230\u5c3e\uff0c\u770b\u6765\u7adf\u90fd\u5927\u60bb\u5e38\u7406\uff0c\u81ea\u7136\u4e0d\u662f \u4efb\u4f55\u4eba\u80fd\u591f\u63a8\u6d4b\u5f97\u51fa\u7684\u3002 \u3000\u3000\u591a\u81c2\u795e\u5251\u957f\u53f9\u4e86\u4e00\u58f0\uff0c\u671b\u7740\u7a97\u5916\u7684\u591c\u8272\uff0c\u6c89\u91cd\u4ed6\u8bf4\u9053\uff1a\u201c\u770b\u6765\u6211\u4eec\u53ea\u6709\u7b49\u5230\u53e6\u4e00\u4ef6\u6d41\u8840 \u7684\u53d8\u6545\u751f\u51fa\u4e86\uff0c\u9664\u6b64\u4e4b\u5916\u4e00\u5509\uff01\u201d \u3000\u3000\u4ed6\u6c89\u91cd\u5730\u7ed3\u675f\u4e86\u81ea\u5df1\u7684\u8bdd\uff0c\u53c8\u4e3a\u4e4b\u843d\u4eba\u6c89\u601d\u91cc\u3002 \u3000\u3000\u7b49\u5f85\uff0c\u662f\u5168\u7136\u4e0d\u540c\u4e8e\u8ffd\u5bfb\u7684\uff0c\u5bf9\u4e00\u4e2a\u5c1a\u672a\u53ef\u77e5\u7684\u8c1c\u56e2\uff0c\u6709\u4e9b\u4eba\u5b89\u4e8e\u7b49\u5f85\uff0c\u53e6\u5916\u4e00\u4e9b\u4eba\u5374 \u6025\u4e8e\u8ffd\u5bfb\u3002 \u3000\u3000\u591a\u81c2\u795e\u5251\u53f1\u54a4\u6c5f\u6e56\uff0c\u5e76\u4e0d\u662f\u5b89\u4e8e\u7b49\u5f85\u7684\u4eba\uff0c\u53ea\u662f\u6b64\u523b\u4ed6\u8fde\u8ffd\u5bfb\u7684\u76ee\u6807\u90fd\u6ca1\u6709\uff0c\u9664\u4e86\u7b49 \u5f85\uff0c\u4ed6\u662f\u5168\u7136\u65e0\u80fd\u4e3a\u529b\u4e86\u3002 \u3000\u3000\u800c\u5353\u957f\u537f\u5462\uff1f \u3000\u3000\u8fd9\u521d\u5165\u6c5f\u6e56\u7684\u6b66\u6797\u9ad8\u624b\uff0c\u5374\u662f\u5728\u79ef\u6781\u5730\u8ffd\u5bfb\u7740\u4ed6\u4eec\u6025\u4e8e\u77e5\u9053\u7684\u89e3\u7b54\u2014\u2014\u90a3\u4e9b\u51b7\u9177\u3001\u51f6\u6b8b \u7684\u5c60\u6740\uff0c\u662f\u4e0d\u662f\u8fd9\u4e09\u4e2a\u7ea2\u88f3\u5c11\u5973\u505a\u51fa\u7684\u5462\uff1f\u8fd9\u4e09\u4e2a\u7ea2\u88f3\u5c11\u5973\uff0c\u4e3a\u4ec0\u4e48\u4f1a\u505a\u51fa\u8fd9\u4e9b\u4e8b\uff1f\u5979\u4eec\u662f \u9650\u4ee4\u5feb\u5200\u4f1a\u4f17\u4eba\u5728\u4e24\u65e5\u4e4b\u5185\u79bb\u5f00\u4e34\u5b89\u7684\uff1f\u6291\u6216\u662f\u7981\u6b62\u4ed6\u4eec\u79bb\u5f00\u4e34\u5b89\u7684\uff1f \u3000\u3000\u800c\u6700\u91cd\u8981\u7684\uff0c\u4ed6\u8fd8\u662f\u5728\u6025\u6b32\u77e5\u9053\u8fd9\u4e09\u4e2a\u7ea2\u88f3\u5c11\u5973\u548c\u81ea\u5df1\u7684\u4ec7\u4eba\u6e29\u5982\u7389\u7a76\u7adf\u6709\u7740\u4ec0\u4e48\u5173 \u7cfb\uff0c\u5982\u679c\u5979\u4eec\u771f\u662f\u6e29\u5982\u7389\u7684\u95e8\u4e0b\uff0c\u90a3\u4e48\u81ea\u5df1\u90a3\u4e0d\u5171\u6234\u5929\u7684\u4ec7\u4eba\u7684\u4e0b\u843d\uff0c\u4e0d\u662f\u53ef\u4ee5\u4ece\u5979\u4eec\u8eab\u4e0a \u77e5\u9053\u4e86\u5417\uff1f \u3000\u3000\u8fd9\u4e9b\u9519\u7efc\u590d\u6742\u7684\u95ee\u9898\uff0c\u4f7f\u5f97\u4ed6\u4e0d\u987e\u4e00\u5207\u7684\u671d\u4e09\u4e2a\u7ea2\u88f3\u5c11\u5973\u7684\u53bb\u5411\u8ffd\u4e86\u8fc7\u53bb\uff0c\u90a3\u65f6\u8fd8\u662f\u6e05 \u6668\uff0c\u76db\u590f\u7684\u9633\u5149\u751a\u81f3\u8fd8\u6ca1\u6709\u5b8c\u5168\u5347\u8d77\u6765\u3002 \u7b2c\u4e03\u7ae0\u3000\u591a\u4e8b\u5934\u9640>> \u53e4\u9f99\u300a\u6708\u5f02\u661f\u90aa\u300b \u7b2c\u4e03\u7ae0\u3000\u591a\u4e8b\u5934\u9640 \u3000\u3000\u5353\u957f\u537f\u6781\u65e5\u800c\u671b\uff0c\u53ea\u89c1\u90a3\u4e24\u4e2a\u7ea2\u88f3\u5c11\u5973\u4e00\u5de6\u4e00\u53f3\uff0c\u642d\u5728\u90a3\u7edd\u8272\u4e3d\u4eba\u7684\u80a9\u4e0a\uff0c\u7eb5\u8dc3\u5982\u98de\u5730 \u5411\u57ce\u90ca\u5916\u4e00\u7247\u5927\u6811\u6797\u91cc\u63a0\u53bb\u3002 \u3000\u3000\u8fdc\u8fdc\u671b\u53bb\uff0c\u53ea\u89c1\u8fd9\u4e09\u6761\u4eba\u5f71\uff0c\u5728\u76db\u590f\u9752\u8471\u7684\u90ca\u91ce\u4e0a\uff0c\u51e0\u4e4e\u53d8\u6210\u4e00\u62b9\u7ea2\u5149\uff0c\u6d41\u661f\u822c\u5730\u4e00\u63a0 \u800c\u901d\u3002 \u3000\u3000\u5353\u957f\u537f\u63a0\u4e0b\u57ce\u65f6\uff0c\u8fdc\u5728\u5979\u4eec\u4e4b\u540e\uff0c\u6b64\u523b\u4fbf\u5df2\u843d\u540e\u4e86\u5341\u6570\u4e08\uff0c\u8fd9\u6bb5\u8ddd\u79bb\u8bf4\u957f\u4e0d\u957f\uff0c\u8bf4\u77ed\u4e0d \u77ed\uff0c\u5353\u957f\u537f\u4e0d\u518d\u8fdf\u7591\uff0c\u8fde\u90a3\u9ec4\u886b\u5c11\u5e74\u7684\u884c\u6b62\u90fd\u987e\u4e0d\u5f97\u770b\u4e86\uff0c\u5c55\u52a8\u8eab\u5f62\uff0c\u98d3\u7136\u8ffd\u53bb\u3002 \u3000\u3000\u5239\u90a3\u95f4\uff0c\u90a3\u4e00\u56e2\u7ea2\u5f71\uff0c\u5df2\u7ecf\u95ea\u5165\u6797\u6728\u4e4b\u4e2d\uff0c\u5353\u957f\u537f\u4e0d\u7531\u5fc3\u4e2d\u5927\u6025\uff0c\u53cc\u81c2\u4e00\u5f20\uff0c\u8eab\u5f62\u6709\u5982 \u9e70\u96bc\u822c\u63a0\u4e86\u8d77\u6765\uff0c\u63a0\u5165\u6797\u53bb\u2014\u2014\u54ea\u77e5\u4ed6\u8eab\u5f62\u65b9\u843d\uff0c\u4e00\u56e2\u5149\u5f71\uff0c\u5e26\u7740\u6fc0\u5389\u7684\u98ce\u58f0\uff0c\u84e6\u5730\u5f53\u5934\u5411 \u4ed6\u538b\u4e86\u4e0b\u6765\uff0c\u4e00\u4e2a\u6709\u5982\u6d2a\u949f\u822c\u7684\u58f0\u97f3\u5389\u53f1\u9053\uff1a\u201c\u7ad9\u4f4f\uff01\u201d \u3000\u3000\u5353\u957f\u537f\u500f\u7136\u4e00\u60ca\uff0c\u773c\u770b\u81ea\u5df1\u7bad\u4e00\u822c\u7684\u8eab\u5f62\uff0c\u5df2\u582a\u582a\u88ab\u90a3\u56e2\u9752\u84dd\u7684\u5149\u5f71\u5377\u5165\uff0c\u53e3\u4e2d\u95f7\u54fc\u4e00 \u58f0\uff0c\u8eab\u5f62\u84e6\u7136\u4e00\u632b\uff0c\u7adf\u501f\u7740\u4f53\u5185\u771f\u6c14\u7684\u6536\u8f6c\uff0c\u786c\u751f\u751f\u5730\u5c06\u81ea\u5df1\u524d\u8fdb\u7684\u529b\u9053\u53d8\u4e3a\u540e\u9000\uff0c\u5cf0\u8170\u5fae \u62e7\uff0c\u884c\u4e91\u6d41\u6c34\u822c\u5730\u540e\u9000\u4e86\u4e09\u6b65\u3002 \u3000\u3000\u4ed6\u8fd9\u79cd\u8eab\u5f62\u7684\u8f6c\u6298\u53d8\u5316\uff0c\u53ef\u8bf4\u662f\u8db3\u4ee5\u60ca\u4e16\u800c\u9a87\u4fd7\u7684\uff0c\u53ea\u542c\u90a3\u56e2\u5149\u5f71\u4e4b\u4e2d\uff0c\u4e5f\u4e0d\u7981\u4e3a\u4e4b\u53d1 \u51fa\u4e00\u58f0\u8f7b\u8f7b\u7684\u60ca\u8bb6\u58f0\u3002 \u3000\u3000\u5353\u957f\u537f\u957f\u8896\u4e00\u62c2\uff0c\u633a\u9038\u7684\u8eab\u5f62\uff0c\u4fbf\u81ea\u500f\u7136\u987f\u4f4f\uff0c\u53ea\u6709\u8eab\u4e0a\u7684\u957f\u886b\uff0c\u4ecd\u5728\u4e0d\u4f4f\u6ce2\u52a8\u8d77\u4f0f\uff0c \u770b\u6765\u50cf\u6b32\u968f\u98ce\u800c\u53bb\u3002 \u3000\u3000\u4ed6\u5168\u8eab\u7684\u771f\u6c14\u81ea\u968f\u7740\u8863\u8896\u4e4b\u4e00\u62c2\u800c\u6ee1\u805a\u81c2\u4e0a\uff0c\u4f46\u90a3\u56e2\u5149\u5f71\uff0c\u5374\u672a\u8ddf\u8e2a\u51fb\u6765\uff0c\u4ed6\u5fc3\u4e2d\u4e0d\u7981 \u5fae\u5fae\u4e00\u6014\uff0c\u95ee\u76ee\u671b\u53bb\uff0c\u53ea\u89c1\u4e00\u682a\u6811\u5e72\u7c97\u5927\u3001\u679d\u53f6\u6d53\u5bc6\u7684\u6811\u524d\uff0c\u5353\u7136\u7ad9\u7740\u4e00\u4e2a\u8eab\u8eaf\u9b41\u4f1f\u9ad8\u5927\u7684 \u548c\u5c1a\uff0c\u53cc\u81c2\u5411\u524d\u4f38\u5f97\u7b14\u76f4\uff0c\u624b\u4e2d\u6a2a\u6301\u7740\u4e00\u53ea\u7cbe\u5149\u96ea\u4eae\u7684\u4f5b\u95e8\u5175\u5203\u201c\u5982\u610f\u65b9\u4fbf\u94f2\u201d\uff0c\u94f2\u4e0a\u7684\u94dc \u73af\uff0c\u5140\u81ea\u53ee\u54e8\u4f5c\u54cd\u3002 \u3000\u3000\u5353\u957f\u537f\u4e0d\u7981\u53c8\u4e3a\u4e4b\u4e00\u6115\uff0c\u4e0d\u77e5\u9053\u8fd9\u9b41\u4f1f\u7684\u618e\u4eba\uff0c\u4e3a\u4f55\u7a81\u7136\u5411\u81ea\u5df1\u51fa\u624b\uff0c\u76ee\u5149\u8f6c\u52a8\u5904\uff0c\u53ea \u89c1\u8fd9\u9b41\u4f1f\u7684\u50e7\u4eba\uff0c\u8138\u4e0a\u6012\u5bb9\u6ee1\u9762\uff0c\u4e00\u53cc\u73af\u76ee\uff0c\u5a01\u5149\u6bd5\u9732\uff0c\u6b63\u81ea\u77ac\u4e5f\u4e0d\u77ac\u5730\u671b\u7740\u81ea\u5df1\u3002 \u3000\u3000\u201c\u5353\u957f\u537f\u4e0d\u7981\u53c8\u662f\u597d\u7b11\uff0c\u53c8\u662f\u597d\u6c14\uff0c\u5374\u53c8\u5947\u602a\uff0c\u54ea\u77e5\u90a3\u9b41\u4f1f\u50e7\u4eba\u7d27\u7d27\u5730\u77aa\u4e86\u4ed6\u534a\u664c\uff0c\u7a81 \u7136\u66b4\u559d\u4e00\u58f0\uff0c\u624b\u8155\u4e00\u7ffb\uff0c\u5c06\u638c\u4e2d\u7684\u5982\u610f\u65b9\u4fbf\u94f2\u821e\u8d77\u4e00\u56e2\u5149\u5f71\uff0c\u4e00\u9762\u5389\u58f0\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u4f19 \u5b50\uff0c\u770b\u6765\u5012\u86ee\u50cf\u4eba\u7684\uff0c\u54ea\u77e5\u5374\u662f\u4e2a\u8863\u51a0\u79bd\u517d\u3002\u201d \u3000\u3000\u624b\u8155\u5fae\u4f38\uff0c\u54d7\u7136\u4e00\u770b\uff0c\u90a3\u53ea\u7cbe\u5149\u96ea\u4eae\u7684\u65b9\u4fbf\u94f2\uff0c\u53c8\u81ea\u7b14\u76f4\u5730\u4f38\u4e86\u51fa\u6765\u3002 \u3000\u3000\u90a3\u50e7\u4eba\u5374\u53c8\u559d\u9053\uff1a\u201c\u6d12\u5bb6\u4eca\u5929\u975e\u6559\u8bad\u6559\u8bad\u4f60\u4e0d\u53ef\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u811a\u6b65\u5fae\u63aa\uff0c\u500f\u7136\u6ed1\u5f00\u4e94\u6b65\uff0c\u5fc3\u4e2d\u66f4\u662f\u60ca\u8be7\u83ab\u540d\uff0c\u4e0d\u77e5\u9053\u8fd9\u9b41\u4f1f\u7684\u50e7\u4eba\u600e\u7684\u597d\u7aef\u7aef \u9a82\u81ea\u5df1\u662f\u201c\u8863\u51a0\u79bd\u517d\u201d\u3002 \u3000\u3000\u4ed6\u5fc3\u5ff5\u4e00\u8f6c\uff0c\u5251\u7709\u5fae\u8f69\uff0c\u6717\u58f0\u53f1\u9053\uff1a\u201c\u5c0f\u53ef\u4e0e\u5927\u5e08\u7d20\u4e0d\u76f8\u8bc6\uff0c\u5927\u5e08\u5982\u6b64\u5927\u9a82\uff0c\u4e0d\u77e5\u6240\u4e3a \u4f55\u6765\u2014\u2014\u5927\u5e08\u82e5\u662f\u90a3\u4e09\u4f4d\u59d1\u5a18\u4e00\u8def\u2014\u2014\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u90a3\u9b41\u4f1f\u618e\u4eba\u5374\u53c8\u66b4\u559d\u4e00\u58f0\uff0c\u5706\u7741\u73af\u76ee\uff0c \u53f1\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u5b50\u771f\u6b63\u6c14\u715e\u6d12\u5bb6\u4e86\uff0c\u6d12\u5bb6\u4e14\u95ee\u4f60\uff0c\u5149\u5929\u5316\u65e5\u4e4b\u4e0b\u4f60\u7adf\u6562\u5bf9\u4eba\u5bb6\u5c11\u5973\u65e0\u793c\uff0c\u4f60 \u4e0d\u662f\u4e2a\u8863\u51a0\u79bd\u517d\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u8bed\u58f0\u65b9\u843d\uff0c\u90a3\u6761\u7cbe\u5149\u96ea\u4eae\u7684\u65b9\u4fbf\u94f2\u5df2\u81ea\u6ee1\u5e26\u98ce\u58f0\uff0c\u671d\u5353\u957f\u537f\u62e6\u8170\u4e00\u626b\u3002 \u3000\u3000\u5353\u957f\u537f\u65e2\u60ca\u4e14\u6012\uff0c\u5fae\u4e00\u503e\u8eab\uff0c\u90a3\u6761\u65b9\u4fbf\u94f2\uff0c\u4fbf\u5df2\u582a\u582a\u4ece\u8eab\u4fa7\u626b\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u6811\u540e\u7684\u90a3\u4e24\u4e2a\u7ea2\u88f3\u5c11\u5973\u201c\u6251\u54e7\u201d\u63a9\u53e3\u4e00\u7b11\uff0c\u53c8\u5c06\u8793\u9996\u7f29\u56de\u6811\u540e\uff0c\u5353\u957f\u537f\u5fc3\u5ff5\u8f6c\u5904\uff0c\u77e5\u9053 \u8fd9\u9c81\u83bd\u7684\u5934\u9640\u5fc5\u5b9a\u662f\u53d7\u4e86\u8fd9\u4e9b\u72e1\u9ee0\u7684\u7ea2\u88f3\u5c11\u5973\u7684\u611a\u5f04\uff0c\u662f\u4ee5\u4e0d\u5206\u9752\u7ea2\u7682\u767d\u5730\u5c31\u5411\u81ea\u5df1\u52a8\u624b\u3002 \u3000\u3000\u4ed6\u4e0d\u7981\u5728\u5fc3\u4e2d\u6697\u9a82\u8fd9\u50e7\u4eba\u7684\u9c81\u83bd\uff1a\u201c\u82e5\u6362\u4e86\u522b\u4eba\u5c82\u4e0d\u8981\u88ab\u8fd9\u4e00\u94f2\u6253\u5f97\u62c6\u9996\u3002\u201d \u3000\u3000\u957f\u8896\u518d\u62c2\uff0c\u8eab\u5f62\u731b\u8f6c\uff0c\u4e58\u7740\u8fd9\u65b9\u4fbf\u94f2\u53bb\u52bf\u5df2\u5c06\u7aed\uff0c\u55d6\u5730\u5f80\u6811\u540e\u63a0\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u54ea\u77e5\u9053\u9b41\u4f1f\u7684\u50e7\u4eba\u867d\u9c81\u83bd\uff0c\u6b66\u529f\u5374\u7edd\u9ad8\uff0c\u624b\u8155\u4e00\u632b\uff0c\u7adf\u786c\u751f\u751f\u5c06\u8fd9\u6761\u65b9\u4fbf\u94f2\u5e26\u4e86\u56de\u6765\uff0c \u5bd2\u5149\u4e00\u6e9c\uff0c\u53c8\u81ea\u6321\u5728\u5353\u957f\u537f\u8eab\u524d\u3002 \u3000\u3000\u5353\u957f\u537f\u867d\u4e0d\u613f\u548c\u8fd9\u591a\u4e8b\u7684\u5934\u9640\u591a\u4f5c\u7ea0\u7f20\uff0c\u60f9\u4e9b\u6ca1\u6765\u7531\u7684\u662f\u975e\uff0c\u4f46\u4e8e\u6b64\u523b\u5374\u4ecd\u4e0d\u7981\u63a7\u5236\u4e0d \u4f4f\u81ea\u5df1\u7684\u6012\u6c14\uff0c\u5927\u559d\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u54ea\u89c1\u4f60\u8fd9\u50e7\u4eba\u600e\u4e48\u5982\u6b64\u9c81\u83bd\uff0c\u8fde\u8bdd\u90fd\u4e0d\u95ee\u6e05\u695a\uff0c\u5c31\u80e1\u4e71\u2014 \u2014\u201d\u90a3\u9b41\u4f1f\u7684\u618e\u4eba\u66b4\u559d\u4e00\u58f0\uff0c\u622a\u65ad\u4e86\u4ed6\u7684\u8bdd\uff0c\u6a2a\u65f6\u4e00\u5e26\uff0c\u5de6\u624b\u4e00\u6284\uff0c\u9634\u9633\u628a\u5f0f\u4e00\u5408\uff0c\u5c06\u90a3\u6761 \u91cd\u91cf\u51e0\u8fbe\u767e\u65a4\u7684\u65b9\u4fbf\u94f2\uff0c\u6325\u52a8\u5f97\u72b9\u5982\u8349\u82a5\u94f2\u5934\uff0c\u94dc\u73af\u8fde\u58f0\u54cd\u52a8\u95f4\uff0c\u5df2\u53c8\u51fb\u51fa\u6570\u62db\u3002 \u3000\u3000\u5239\u90a3\u4e4b\u95f4\uff0c\u98ce\u58f0\u6ee1\u6797\uff0c\u5bd2\u5149\u6325\u52a8\u95f4\uff0c\u6811\u68a2\u7684\u679d\u53f6\u7eb7\u7eb7\u5760\u843d\uff0c\u4f46\u88ab\u5353\u957f\u537f\u7684\u638c\u98ce\u4e00\u6fc0\uff0c\u53c8 \u8fdc\u8fdc\u98de\u4e86\u51fa\u53bb\uff0c\u751f\u50cf\u662f\u79cb\u98ce\u4e2d\u7684\u843d\u53f6\u3002 \u3000\u3000\u5353\u957f\u537f\u957f\u886b\u98d8\u98d8\uff0c\u6f47\u6d12\u800c\u66fc\u5999\u5730\u5c06\u8fd9\u76d6\u5929\u538b\u4e0b\u7684\u94f2\u5f71\u8f7b\u6613\u5730\u5316\u89e3\u5f00\u53bb\uff0c\u76ee\u5149\u5374\u4e0d\u65f6\u626b\u5411 \u6811\u540e\uff0c\u751f\u6015\u90a3\u4e09\u4e2a\u7ea2\u88f3\u5c11\u5973\u4e58\u9699\u9038\u8d70\u3002 \u3000\u3000\u4f46\u90a3\u682a\u5de8\u6811\u5468\u56f4\u7ade\u513f\u8fbe\u4e09\u4eba\u5408\u62b1\uff0c\u6811\u540e\u9762\u7684\u7ea2\u88f3\u5c11\u5973\u7a76\u7adf\u8d70\u4e86\u6ca1\u6709\uff0c\u5353\u957f\u537f\u6839\u672c\u65e0\u6cd5\u770b \u5230\uff0c\u4ed6\u7f13\u7f13\u79fb\u52a8\u8eab\u5f62\uff0c\u60f3\u5f80\u6811\u540e\u79fb\u53bb\uff0c\u53ea\u662f\u90a3\u50e7\u6325\u821e\u51fa\u7684\u94f2\u5f71\uff0c\u5374\u72b9\u5982\u4e00\u5835\u5149\u5899\uff0c\u6321\u4f4f\u6811\u8eab \u524d\u9762\u3002 \u3000\u3000\u6570\u5341\u62db\u4e00\u8fc7\uff0c\u5353\u957f\u537f\u5df2\u81ea\u770b\u51fa\u8fd9\u50e7\u4eba\u6240\u65bd\u7684\u62db\u5f0f\uff0c\u4e0d\u4f46\u529f\u529b\u6781\u6df1\uff0c\u800c\u4e14\u662f\u5ae1\u4f20\u7684\u5c11\u6797\u5fc3 \u6cd5\u964d\u9f99\u7f57\u6c49\u94f2\u3002 \u3000\u3000\u8fd9\u79cd\u6c89\u91cd\u7684\u5916\u95e8\u5175\u5203\uff0c\u914d\u5408\u7740\u8fd9\u79cd\u5916\u5bb6\u767b\u5cf0\u9020\u6781\u7684\u6b66\u529f\uff0c\u4e00\u7ecf\u65bd\u5c55\uff0c\u5a01\u529b\u53ef\u8bf4\u9738\u9053\u5df2 \u6781\u3002\u8fd9\u79cd\u521a\u731b\u7684\u6b66\u529f\uff0c\u6b63\u6709\u5982\u4e00\u4e2a\u521a\u5f3a\u7684\u6c49\u5b50\uff0c\u5b81\u6298\u800c\u6bcb\u66f2\uff0c\u5353\u957f\u537f\u77e5\u9053\u9664\u975e\u81ea\u5df1\u4ee5\u7edd\u9876\u7684 \u5185\u5bb6\u52a8\u529b\uff0c\u5c06\u8fd9\u9c81\u83bd\u50e7\u4eba\u9707\u4f24\uff0c\u5426\u5219\u53ea\u6709\u5b88\u800c\u4e0d\u653b\uff0c\u9664\u6b64\u4e4b\u5916\uff0c\u4f60\u7740\u60f3\u4ee5\u62db\u5f0f\u6765\u7834\u89e3\uff0c\u5374\u4e0d \u662f\u5bb9\u6613\u7684\u4e8b\u3002 \u3000\u3000\u4ed6\u867d\u7136\u6c14\u607c\u8fd9\u50e7\u4eba\u7684\u9c81\u83bd\u591a\u4e8b\uff0c\u5374\u4e5f\u4e0d\u613f\u5c06\u4e2a\u7d20\u65e0\u6028\u4ec7\u7684\u4eba\u4f24\u5728\u81ea\u5df1\u638c\u4e0b\uff0c\u53c8\u62c6\u4e86\u5341\u6570 \u4e2a\u7167\u9762\uff0c\u4ed6\u5fc3\u91cc\u8d8a\u52a0\u6025\u8e81\uff0c\u62db\u5f0f\u7684\u65bd\u5c55\u95ee\uff0c\u4e5f\u4e0d\u89c9\u52a0\u4e86\u51e0\u5206\u529b\u9053\uff0c\u53ea\u5c06\u90a3\u6761\u91cd\u8fbe\u767e\u65a4\u7684\u5982\u610f \u65b9\u4fbf\u94f2\uff0c\u6709\u65f6\u4e00\u62db\u5c1a\u672a\u65bd\u5c55\u5f00\uff0c\u5c31\u88ab\u9707\u5f97\u98de\u4e86\u5f00\u53bb\u3002 \u3000\u3000\u4f46\u662f\u50e7\u4eba\u5927\u543c\u4e00\u58f0\uff0c\u8155\u65f6\u4f38\u7f29\u95f4\uff0c\u5374\u53c8\u7acb\u523b\u5c06\u8fd9\u7a7a\u9699\u586b\u6ee1\uff0c\u53ea\u89c1\u4ed6\u5bbd\u5927\u7684\u8888\u88df\uff0c\u90fd\u7f29\u5230 \u65f6\u4e0a\uff0c\u9732\u51fa\u4e00\u53cc\u866c\u7b4b\u7ea0\u7ed3\u7684\u94c1\u81c2\u6765\u3002\u663e\u89c1\u4ed6\u7684\u5916\u5bb6\u529f\u529b\uff0c\u5df2\u662f\u767b\u5cf0\u9020\u6781\u3002 \u3000\u3000\u53c8\u662f\u6570\u62db\u62c6\u8fc7\uff0c\u5353\u957f\u537f\u957f\u8896\u4e00\u62c2\uff0c\u8eab\u5f62\u7a81\u7136\u6e9c\u5f00\uff0c\u8fdc\u8fdc\u9000\u5230\u4e03\u5c3a\u5f00\u5916\uff0c\u90a3\u9b41\u4f1f\u7684\u50e7\u4eba\u6115 \u4e86\u4e00\u6115\uff0c\u94f2\u8eab\u4e00\u6a2a\uff0c\u65b9\u5f85\u8ffd\u51fb\uff0c\u5374\u89c1\u5353\u957f\u537f\u8f69\u7709\u4e00\u7b11\uff0c\u7528\u4e00\u6839\u624b\u6307\u6307\u7740\u4ed6\u7b11\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60 \u662f\u8c01\u4e86\uff0c\u4f60\u53ef\u662f\u5d69\u5c71\u5c11\u6797\u3001\u8fbe\u6469\u9662\u9996\u5ea7\u4e0a\u4eba\u7a7a\u6f84\u5927\u5e08\u7684\u5f1f\u5b50\uff1f\u201d \u3000\u3000\u90a3\u50e7\u4eba\u679c\u81ea\u4e00\u6014\uff0c\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\u6d12\u5bb6\u7684\u5e08\u627f\uff1f\u201d \u3000\u3000\u5353\u957f\u537f\u7b11\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u9053\u6211\u662f\u8c01\u5417\uff1f\u201d \u3000\u3000\u90a3\u50e7\u4eba\u53c8\u4e3a\u4e4b\u4e00\u6014\uff0c\u534a\u664c\u8bf4\u4e0d\u51fa\u8bdd\u6765\uff0c\u5353\u957f\u537f\u76ee\u5149\u4e00\u8f6c\uff0c\u9053\uff1a\u201c\u4f60\u65e2\u7136\u4e0d\u77e5\u9053\u6211\u662f\u8c01\uff0c \u600e\u6562\u548c\u6211\u52a8\u624b\uff1f\u201d \u3000\u3000\u90a3\u50e7\u4eba\u76ee\u5149\u4e00\u5446\uff0c\u5a01\u5149\u5c3d\u655b\uff0c\u6697\u4e2d\u5fd6\u9053\uff1a\u201c\u662f\u5440\uff0c\u8fd9\u53ae\u5e74\u7eaa\u867d\u8f7b\uff0c\u6b66\u529f\u5374\u9ad8\uff0c\u8bf4\u4e0d\u5b9a\u6709 \u4ec0\u4e48\u7279\u522b\u6765\u5386\uff0c\u201d\u5353\u957f\u537f\u53c8\u81ea\u51b7\u51b7\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u9053\u65b9\u624d\u90a3\u4e09\u4e2a\u7ea2\u88f3\u5c11\u5973\u662f\u8c01\u5417\uff1f\u201d \u3000\u3000\u90a3\u50e7\u4eba\u4f38\u51fa\u5de8\u638c\uff0c\u6478\u4e86\u6478\u524d\u989d\uff0c\u5374\u542c\u5353\u957f\u537f\u53c8\u81ea\u51b7\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u8fde\u5979\u4eec\u7684\u59d3\u540d\u6765\u5386\u90fd\u4e0d \u77e5\u9053\uff0c\u5c31\u6562\u80e1\u4e71\u5e2e\u5979\u4eec\u51fa\u624b\uff0c\u4f60\u53ef\u77e5\u9053\u65b9\u624d\u90a3\u4e09\u4e2a\u7ea2\u88f3\u5c11\u5973\uff0c\u5176\u5b9e\u662f\u4e09\u4e2a\u5973\u5f3a\u76d7\u5417\uff1f\u201d \u3000\u3000\u90a3\u50e7\u4eba\u6697\u53f9\u4e00\u58f0\uff0c\u5fd6\u9053\uff1a\u201c\u662f\u5440\uff01\u6211\u8fde\u5979\u4eec\u540d\u59d3\u6765\u5386\u90fd\u4e0d\u77e5\u9053\uff0c\u600e\u4e48\u5c31\u80e1\u4e71\u542c\u4fe1\u4e86\u5979\u4eec \u7684\u8bdd\u5462\uff1f\u8fd9\u5c11\u5e74\u770b\u6765\u4e5f\u4e0d\u50cf\u662f\u4e2a\u574f\u4eba\u5440\uff01\u201d \u3000\u3000\u76ee\u5149\u4e00\u62ac\uff0c\u55eb\u5685\u7740\u95ee\u9053\uff1a\u201c\u9601\u4e0b\u662f\u8c01\uff1f\u6b64\u8bdd\u53ef\u679c\u7136\u662f\u771f\u7684\u5417\uff1f\u201d \u3000\u3000\u5353\u957f\u537f\u5634\u89d2\u6cdb\u8d77\u4e00\u4e1d\u7b11\u610f\uff0c\u50cf\u662f\u5728\u6697\u7b11\u8fd9\u618e\u4eba\u7684\u83bd\u649e\uff0c\u9762\u4e0a\u5374\u6545\u610f\u68ee\u51b7\u5730\u7b11\u9053\uff1a\u201c\u4f60\u5feb \u5e2e\u6211\u628a\u90a3\u4e09\u4e2a\u5973\u5f3a\u76d7\u6293\u4f4f\u518d\u8bf4\uff0c\u5426\u5219\u2014\u2014\u54fc\u3002\u201d \u3000\u3000\u201c\u54fc\u201d\u58f0\u72b9\u81ea\u672a\u843d\uff0c\u4ed6\u7684\u8eab\u5f62\u5df2\u5982\u79bb\u5f26\u4e4b\u7bad\u822c\u7a9c\u5230\u6811\u540e\uff0c\u76ee\u5149\u626b\u5904\uff0c\u5374\u89c1\u6811\u540e\u7a7a\u7a7a\uff0c\u54ea \u91cc\u8fd8\u6709\u90a3\u4e09\u4e2a\u7ea2\u88f3\u5c11\u5973\u7684\u4eba\u5f71\u3002 \u3000\u3000\u4ed6\u6697\u4e2d\u4e00\u8dfa\u811a\uff0c\u4e5f\u987e\u4e0d\u5f97\u518d\u548c\u90a3\u50e7\u4eba\u591a\u8bf4\uff0c\u8eab\u5f62\u8f7b\u6298\uff0c\u671d\u6811\u6797\u6df1\u5904\u98de\u63a0\u800c\u53bb\u3002 \u3000\u3000\u90a3\u50e7\u4eba\u6014\u4e86\u534a\u664c\uff0c\u671b\u7740\u5353\u957f\u537f\u7684\u4eba\u5f71\uff0c\u6d88\u5931\u5728\u6797\u6728\u6df1\u5904\uff0c\u5fc3\u4e2d\u5374\u4e0d\u7981\u6697\u9a82\u81ea\u5df1\u600e\u4e48\u4eca\u65e5 \u53c8\u505a\u4e86\u65e0\u5934\u65e0\u5c3e\u7684\u7cca\u6d82\u4e8b\u3002 \u3000\u3000\u539f\u6765\u4ed6\u884c\u811a\u81f3\u6b64\uff0c\u8d2a\u56fe\u98ce\u51c9\uff0c\u53c8\u61d2\u5f97\u6302\u5355\uff0c\u6628\u591c\u5c31\u5728\u8fd9\u6d53\u5bc6\u7684\u6797\u6728\u4e2d\u6b47\u4e0b\u4e86\uff0c\u4eca\u6668\u4e00\u89c9 \u9192\u6765\uff0c\u5374\u89c1\u6709\u4e09\u4e2a\u7ea2\u88f3\u5c11\u5973\u98de\u4e5f\u4f3c\u5730\u63a0\u8fdb\u6811\u6797\u91cc\uff0c\u50cf\u662f\u5728\u9003\u907f\u7740\u4ec0\u4e48\u4e1c\u897f\u4f3c\u7684\u3002 \u3000\u3000\u90a3\u4e09\u4e2a\u5c11\u5973\u4e00\u4eba\u6797\u4e2d\uff0c\u4e00\u773c\u671b\u5230\u6797\u4e2d\u7684\u5de8\u6811\u4e0b\uff0c\u8eba\u7740\u4e00\u4e2a\u957f\u5927\u618e\u4eba\uff0c\u8eab\u65c1\u6a2a\u653e\u7740\u4e00\u67c4\u7cbe \u5149\u96ea\u4eae\u7684\u65b9\u4fbf\u94f2\uff0c\u4f3c\u4e4e\u4e5f\u5fae\u5fae\u4e00\u60ca\uff0c\u516d\u9053\u79cb\u6ce2\u4e00\u8d77\u5728\u4ed6\u8eab\u4fa7\u7684\u65b9\u4fbf\u94f2\u4e0a\u626b\u4e86\u51e0\u773c\u3002 \u3000\u3000\u5176\u4e2d\u4e00\u7ea2\u88f3\u5c11\u5973\u5c31\u5fae\u9891\u9edb\u7709\uff0c\u671d\u4ed6\u6df1\u6df1\u4e00\u798f\uff0c\u9053\uff1a\u201c\u5927\u5e08\u6551\u547d\uff0c\u540e\u9762\u6709\u4e2a\u4eba\u8981\u2026\u2026\u8981\u6b3a \u8d1f\u6211\u4eec\uff0c\u5df2\u7ecf\u8ffd\u8fc7\u6765\u4e86\u3002\u201d \u3000\u3000\u8fd9\u9b41\u4f1f\u7684\u50e7\u4eba\u751f\u6027\u6700\u662f\u559c\u6b22\u591a\u7ba1\u95f2\u4e8b\uff0c\u51fa\u9053\u4ee5\u6765\uff0c\u5df2\u7ecf\u4e0d\u77e5\u60f9\u4e0b\u591a\u5c11\u4e8b\u7aef\uff0c\u6b64\u523b\u4e00\u542c\u6b64 \u8bdd\uff0c\u7acb\u523b\u7ffb\u8eab\u8df3\u4e86\u8d77\u6765\uff0c\u4f38\u624b\u4e00\u6284\u8eab\u4fa7\u7684\u65b9\u4fbf\u94f2\uff0c\u62cd\u80f8\u9053\uff1a\u201c\u6709\u6d12\u5bb6\u5728\u8fd9\u91cc\uff0c\u4f60\u4eec\u8fd8\u6015\u4ec0 \u4e48\uff0c\u6709\u4ec0\u4e48\u4e8b\uff0c\u6d12\u5bb6\u5b8c\u5168\u505a\u4e3b\u3002\u201d \u3000\u3000\u90a3\u4e09\u4e2a\u7ea2\u88f3\u5c11\u5973\u5a9a\u76ee\u4e00\u8f6c\uff0c\u5374\u89c1\u5353\u957f\u537f\u5df2\u5982\u98de\u63a0\u6765\uff0c\u8fde\u5fd9\u8eb2\u5728\u6811\u540e\uff0c\u5374\u6559\u8fd9\u50e7\u4eba\u548c\u5353\u957f \u537f\u7cca\u91cc\u7cca\u6d82\u5730\u6253\u4e86\u573a\u67b6\u3002 \u3000\u3000\u6b64\u523b\uff0c\u4ed6\u5446\u5446\u5730\u7ad9\u5728\u6811\u4e0b\uff0c\u8111\u4e2d\u5374\u4ecd\u7136\u662f\u6df7\u6df7\u9968\u6df9\u7684\uff0c\u4e0d\u77e5\u9053\u5728\u7384\u886b\u5c11\u5e74\u548c\u90a3\u4e09\u4e2a\u7ea2\u88f3 \u5c11\u5973\u4e4b\u95f4\u7a76\u7adf\u662f\u6709\u7740\u4ec0\u4e48\u7ea0\u7eb7\u3002 \u3000\u3000\u6b64\u523b\uff0c\u4ed6\u867d\u5df2\u4e0d\u5b8c\u5168\u76f8\u4fe1\u90a3\u4e09\u4e2a\u7ea2\u88f3\u5c11\u5973\u7684\u8bdd\uff0c\u53ef\u662f\u5bf9\u5353\u957f\u537f\u7684\u8bdd\uff0c\u4ed6\u4e5f\u6709\u4e9b\u7591\u60d1\uff0c\u77e5 \u4ed6\u6b66\u529f\u867d\u5df2\u767b\u5802\u4eba\u5ba4\uff0c\u4e34\u4e8b\u5374\u5e76\u4e0d\u8001\u7ec3\uff0c\u6c5f\u6e56\u4e0a\u6709\u8bb8\u591a\u4eba\u6545\u610f\u6349\u5f04\u4ed6\uff0c\u4ed6\u5403\u4e86\u4e8f\u53bb\u4e5f\u4e0d\u77e5 \u9053\u3002 \u3000\u3000\u4ed6\u6014\u4e86\u534a\u664c\uff0c\u5c06\u53f3\u638c\u7684\u65b9\u4fbf\u94f2\uff0c\u5012\u66f3\u5728\u5730\uff0c\u5de6\u638c\u53c8\u81ea\u4e00\u62cd\u524d\u989d\uff0c\u6447\u5934\u53f9\u9053\uff1a\u201c\u771f\u5947\u602a\uff0c \u90a3\u5c11\u5e74\u600e\u4f1a\u77e5\u9053\u6211\u7684\u5e08\u627f\u7684\uff0c\u4ed6\u53c8\u4e0d\u8ba4\u5f97\u6211\u3002\u201d \u3000\u3000\u5012\u66f3\u7740\u7684\u65b9\u4fbf\u94f2\uff0c\u65b9\u4e00\u8f6c\u8eab\uff0c\u54ea\u77e5\u6811\u68a2\u6797\u53f6\u6df1\u5904\uff0c\u7a81\u7136\u4f20\u6765\u201c\u5657\u5473\u201d\u4e00\u7b11\uff0c\u7b11\u58f0\u4e4b\u5a07\u67d4 \u8f7b\u8106\uff0c\u751f\u50cf\u767e\u556d\u9ec4\u83ba\u3002 \u3000\u3000\u4ed6\u5fae\u5403\u4e00\u60ca\uff0c\u6a2a\u6301\u8d77\u65b9\u4fbf\u94f2\uff0c\u62ac\u5934\u671b\u53bb\uff0c\u4e00\u4e2a\u6ee1\u8eab\u7ea2\u88f3\u7684\u7edd\u8272\u4e3d\u4eba\u4f38\u51fa\u4e00\u53ea\u7ea4\u7ea4\u7389\u638c\uff0c \u6293\u7740\u4e00\u53ea\u67d4\u5f31\u7684\u6811\u679d\uff0c\u5168\u8eab\u7adf\u7b14\u76f4\u5730\u5782\u4e86\u4e0b\u6765\uff0c\u5374\u7528\u53e6\u4e00\u53ea\u7389\u624b\uff0c\u6574\u7406\u7740\u9b13\u8fb9\u7684\u53d1\u811a\uff0c\u6b63\u81ea \u5782\u9996\u5ae3\u7136\u542b\u7b11\u3002 \u3000\u3000\u7fe0\u7eff\u7684\u53f6\u6728\u63a9\u6620\u4e2d\uff0c\u53ea\u89c1\u8fd9\u7ea2\u88f3\u5c11\u5973\uff0c\u66f4\u662f\u7f8e\u5982\u5929\u4ed9\uff0c\u751f\u50cf\u662f\u7eff\u53f6\u4e4b\u4e2d\u4e00\u6735\u5a07\u8273\u7684\u82b1\u3002 \u3000\u3000\u6709\u98ce\u7a7f\u6797\u800c\u8fc7\uff0c\u5439\u5f97\u6811\u68a2\u7684\u679d\u53f6\uff0c\u7c41\u7136\u53d1\u51fa\u9635\u9635\u6e05\u7c41\u3002\u90a3\u7edd\u8272\u4e3d\u4eba\u7684\u8f7b\u7ea2\u7f57\u886b\uff0c\u4e5f\u968f\u7740 \u5fae\u98ce\u6e05\u67d4\u5730\u98d8\u8d77\u3002 \u3000\u3000\u7f57\u8896\u5782\u843d\uff0c\u7389\u81c2\u83b9\u83b9\uff0c\u66f4\u50cf\u662f\u5728\u8fd9\u7ea2\u82b1\u7eff\u53f6\u4e4b\u4e2d\uff0c\u591a\u6dfb\u4e86\u4e00\u8282\u6625\u85d5\uff0c\u90a3\u4e00\u53cc\u660e\u4eae\u7684\u79cb \u6ce2\uff0c\u5982\u679c\u91cc\u6c6a\u4f60\u8138\u4e0a\uff0c\u90a3\u4e48\u7eb5\u7136\u662f\u76db\u590f\u6e05\u6668\u7684\u5fae\u98ce\uff0c\u4e5f\u4f1a\u8fdc\u8fdc\u4e0d\u53ca\u8fd9\u79cb\u6ce2\u7684\u52a8\u4eba\u4e86\u3002 \u3000\u3000\u90a3\u9c81\u83bd\u7684\u50e7\u4eba\u76ee\u5149\u62ac\u671b\u5904\uff0c\u4e5f\u4e0d\u7981\u4e3a\u4e4b\u51dd\u76ee\u534a\u664c\uff0c\u65b9\u81ea\u95ee\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u59d1\u5a18\uff0c\u6c55\u7b11\u6d12\u5bb6 \u4ec0\u4e48\uff1f\u201d \u3000\u3000\u90a3\u7edd\u8272\u4e3d\u4eba\u201c\u6251\u54e7\u201d\u53c8\u662f\u4e00\u7b11\uff0c\u7389\u638c\u5fae\u677e\uff0c\u98d8\u7136\u4ece\u6811\u68a2\u843d\u4e86\u4e0b\u6765\uff0c\u7f57\u886b\u7684\u8863\u8882\uff0c\u5fae\u5fae\u626c \u8d77\u4e00\u4e9b\uff0c\u53e6\u4e00\u53ea\u7ea4\u624b\u5374\u4ecd\u7406\u7740\u9b13\u89d2\u5de7\u7b11\u9053\uff1a\u201c\u6211\u7b11\u5927\u5e08\u771f\u662f\u6709\u70b9\u7cca\u6d82\u3002\u201d \u3000\u3000\u90a3\u50e7\u4eba\u9762\u8272\u4e00\u51db\uff0c\u5706\u7741\u73af\u76ee\uff0c\u5389\u58f0\u9053\uff1a\u201c\u6d12\u5bb6\u521a\u521a\u5e2e\u4e86\u4f60\u7684\u5fd9\uff0c\u4f60\u5374\u8bf4\u6d12\u5bb6\u7cca\u6d82\uff0c\u96be\u9053 \u6d12\u5bb6\u5e2e\u5fd9\u8fd8\u5e2e\u9519\u4e86\u4e0d\u6210\uff1f\u201d \u3000\u3000\u90a3\u7edd\u8272\u4e3d\u4eba\u653e\u4e0b\u7ea4\u638c\uff0c\u8f7b\u6298\u67f3\u8170\uff0c\u5fae\u5fae\u4e00\u798f\uff0c\u5a07\u58f0\u9053\uff1a\u201c\u5927\u5e08\u65b9\u624d\u4ed7\u4e49\u63f4\u624b\uff0c\u6211\u5148\u8c22\u8fc7 \u4e86\uff0c\u53ea\u4e0d\u8fc7\u2014\u2014\u201d\u5979\u7adf\u53c8\u5ae3\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5927\u5e08\u7684\u786e\u4e5f\u6709\u4e9b\u7cca\u6d82\uff0c\u65b9\u624d\u90a3\u4e2a\u7a7f\u7740\u4e00\u8eab\u9ed1\u8863\u88f3 \u7684\u9ed1\u5fc3\u80a0\uff0c\u731c\u5230\u4e86\u5927\u5e08\u7684\u5e08\u627f\uff0c\u53c8\u6709\u4ec0\u4e48\u503c\u5f97\u5947\u602a\u7684\uff0c\u6211\u975e\u4f46\u77e5\u9053\u5927\u5e08\u7684\u5e08\u627f\uff0c\u8fd8\u77e5\u9053\u5927\u5e08 \u7684\u540d\u5b57\u54e9\u3002\u201d\u5979\u8bed\u58f0\u5fae\u5fae\u4e00\u987f\uff0c\u79cb\u6ce2\u5728\u90a3\u50e7\u4eba\u8eab\u4e0a\u4e00\u8f6c\uff0c\u63a9\u53e3\u5a07\u7b11\u9053\uff1a\u201c\u5927\u5e08\u53ef\u5c31\u662f\u540d\u95fb\u5929 \u4e0b\u7684\u591a\u4e8b\u5934\u9640\u4e0a\u65e0\u4e0b\u6839\uff0c\u65e0\u6839\u5927\u5e08\uff1f\u201d \u3000\u3000\u90a3\u50e7\u4eba\u591a\u4e8b\u5934\u9640\u65e0\u6839\uff0c\u4e00\u987f\u638c\u4e2d\u7684\u65b9\u4fbf\u94f2\uff0c\u8fde\u58f0\u9053\uff1a\u201c\u8fd9\u5012\u5947\u602a\u4e86\uff0c\u600e\u4e48\u4f60\u4eec\u90fd\u8ba4\u5f97\u6d12 \u5bb6\uff0c\u6d12\u5bb6\u5374\u4e0d\u8ba4\u5f97\u4f60\u4eec\uff1f\u201d \u3000\u3000\u90a3\u7edd\u8272\u4e3d\u4eba\u54af\u54af\u7b11\u9053\uff1a\u201c\u6211\u4eec\u53c8\u4f55\u5c1d\u8ba4\u5f97\u5927\u5e08\uff0c\u53ea\u4e0d\u8fc7\u4ece\u5927\u5e08\u7684\u62db\u6cd5\u8eab\u6bb5\u4e0a\u731c\u51fa\u6765\u7684\u7f62 \u4e86\u3002\u201d \u3000\u3000\u5979\u7f13\u7f13\u4f38\u4e86\u4e09\u53ea\u6625\u8471\u822c\u7684\u7389\u6307\uff0c\u53c8\u81ea\u7b11\u9053\uff1a\u201c\u5929\u4e0b\u6b66\u6797\u4e2d\u4eba\u8c01\u4e0d\u77e5\u9053\u5c11\u5ba4\u5d69\u5c71\u7684\u5c11\u6797\u4e09 \u8001\uff0c\u4ed6\u4eec\u4e09\u4f4d\u8001\u4eba\u5bb6\u867d\u7136\u7ec8\u5e74\u9690\u8ff9\u6df1\u5c71\uff0c\u6b66\u6797\u4e2d\u4eba\u5374\u4e5f\u90fd\u77e5\u9053\uff0c\u4e09\u8001\u4e2d\u82e5\u8bba\u5185\u529f\u4fee\u4e3a\uff0c\u81ea\u7136 \u8981\u6570\u85cf\u7ecf\u9601\u7684\u7a7a\u7075\u4e0a\u4eba\uff0c\u82e5\u8bba\u62f3\u638c\u8f7b\u529f\uff0c\u5374\u8981\u6570\u7f57\u6c49\u5802\u7684\u9996\u5ea7\u7a7a\u6167\u4e0a\u4eba\uff0c\u53ef\u662f\u8981\u8bba\u5c11\u6797\u7684\u9547 \u5c71\u8361\u9b54\u5982\u610f\u65b9\u4fbf\u94f2\u6cd5\uff0c\u90a3\u5c31\u5f97\u6570\u8fbe\u6469\u9662\u7684\u7a7a\u6f84\u4e0a\u4eba\u4e86\u2014\u2014\u4f60\u8bf4\u6211\u8fd9\u8bdd\u5bf9\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u591a\u4e8b\u5934\u9640\u65e0\u6839\u8bb7\u8bb7\u5730\u70b9\u4e86\u70b9\u5934\uff0c\u5374\u542c\u90a3\u7edd\u8272\u4e3d\u4eba\u53c8\u81ea\u7b11\u9053\uff1a\u201c\u5927\u5e08\u65b9\u624d\u6240\u4f7f\u7684\u90a3\u79cd\u964d\u9f99 \u7f57\u6c49\u94f2\uff0c\u53ea\u8981\u662f\u7a0d\u4f1a\u6b66\u529f\u7684\u4eba\u5c31\u53ef\u4ee5\u770b\u5f97\u51fa\u6765\uff0c\u54ea\u6709\u4ec0\u4e48\u9ad8\u5999\uff0c\u9664\u4e86\u7a7a\u6f84\u4e0a\u4eba\u4e4b\u5916\uff0c\u53c8\u6709\u8c01 \u4f20\u6388\u5f97\u51fa\u50cf\u5927\u5e08\u8fd9\u6837\u7684\u5f1f\u5b50\u54e9\u2014\u2014\u4f60\u8bf4\u8fd9\u8bdd\u53ef\u5bf9\u5417\uff1f\u201d \u3000\u3000\u591a\u4e8b\u5934\u9640\u76ee\u4e2d\u7981\u4e0d\u4f4f\u95ea\u8fc7\u4e00\u4e1d\u559c\u60a6\u7684\u5149\u5f69\uff0c\u5374\u5140\u81ea\u95ee\u9053\uff1a\u201c\u53ef\u662f\u4f60\u5374\u53c8\u600e\u4e48\u4f1a\u77e5\u9053\u6d12\u5bb6 \u5c31\u662f\u591a\u4e8b\u5934\u9640\u65e0\u6839\u5462\uff1f\u201d \u3000\u3000\u90a3\u7edd\u8272\u4e3d\u4eba\u63a9\u53e3\u7b11\u9053\uff1a\u201c\u9664\u4e86\u591a\u4e8b\u5934\u9640\u65e0\u5f88\u5927\u5e08\u4e4b\u5916\uff0c\u82b8\u82b8\u5929\u4e0b\uff0c\u53c8\u6709\u8c01\u4f1a\u8def\u89c1\u4e0d\u5e73\uff0c \u62d4\u5200\u6765\u5e2e\u6211\u4eec\u8fd9\u4e09\u4e2a\u5f31\u5973\u5b50\u7684\u5fd9\u5462\uff1f\u201d \u3000\u3000\u591a\u4e8b\u5934\u9640\u4e00\u62cd\u524d\u989d\uff0c\u4ef0\u5929\u5927\u7b11\u4e86\u8d77\u6765\uff0c\u4e00\u9762\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u5e74\u8f7b\u4eba\u771f\u662f\u8d8a\u6765\u8d8a\u806a\u660e\u4e86\uff0c\u8fd9 \u4e9b\u9053\u7406\u6d12\u5bb6\u600e\u4e48\u60f3\u4e0d\u51fa\u6765\uff1f\u201d \u3000\u3000\u8bed\u97f3\u5fae\u987f\uff0c\u7a81\u7136\u5927\u559d\u4e00\u58f0\uff0c\u7528\u4e00\u53ea\u84b2\u6247\u822c\u5927\u7684\u624b\u638c\u4e00\u6307\u90a3\u5c11\u5973\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4f60\u662f\u5426\u5728\u9a97 \u6d12\u5bb6\uff1f\u201d \u3000\u3000\u90a3\u7edd\u8272\u5c11\u5973\u5fae\u5fae\u4e00\u6014\uff0c\u5374\u89c1\u8fd9\u9c81\u83bd\u7684\u5934\u9640\u53cc\u624b\u4e00\u6284\uff0c\u53c8\u5c06\u90a3\u7cbe\u5149\u96ea\u4eae\u7684\u65b9\u4fbf\u94f2\u6a2a\u6301\u4e8e\u624b \u4e2d\uff0c\u5fae\u4e00\u6296\u52a8\uff0c\u94dc\u73af\u53ee\u54af\u3002 \u3000\u3000\u800c\u90a3\u7edd\u8272\u4e3d\u4eba\u9762\u4e0a\uff0c\u5374\u7acb\u523b\u53c8\u6cdb\u51fa\u6625\u82b1\u822c\u7684\u7b11\u5bb9\uff0c\u4fcf\u58f0\u9053\uff1a\u201c\u5927\u5e08\uff0c\u96be\u9053\u4f60\u4e5f\u8981\u6b3a\u8d1f\u6211 \u8fd9\u4e2a\u5f31\u5973\u5b50\u5417\uff1f\u201d \u3000\u3000\u591a\u4e8b\u5934\u9640\u76ee\u5149\u751f\u50cf\u4e3a\u4e4b\u5446\u6ede\u4e86\u4e00\u4e0b\uff0c\u7136\u800c\u7ec8\u4e8e\u5389\u58f0\u559d\u9053\uff1a\u201c\u4ec0\u4e48\u5f31\u5973\u5b50\uff0c\u96be\u9053\u4f60\u628a\u6d12\u5bb6 \u5f53\u6210\u679c\u5b50\uff0c\u770b\u4e0d\u51fa\u4f60\u6709\u6b66\u529f\u6765\uff0c\u54fc\u2014\u2014\u5c31\u51ed\u4f60\u8fd9\u8eab\u6b66\u529f\uff0c\u5929\u4e0b\u8fd8\u6709\u4ec0\u4e48\u4eba\u80fd\u6b3a\u8d1f\u4f60\uff0c\u54fc\u4e00-- \u90a3\u5c0f\u5b50\u7684\u6b66\u529f\u4e5f\u672a\u89c1\u80fd\u9ad8\u51fa\u4f60\uff0c\u96be\u602a\u4ed6\u8bf4\u4f60\u662f\u4e2a\u5973\u5f3a\u76d7\u3002\u201d \u3000\u3000\u4ed6\u4e00\u8fde\u201c\u54fc\u201d\u4e86\u4e24\u58f0\uff0c\u4f46\u8bed\u58f0\u5374\u8d8a\u672a\u8d8a\u4f4e\uff0c\u76f4\u5230\u6700\u540e\u8bf4\u51fa\u5973\u5f3a\u76d7\u4e09\u5b57\uff0c\u90a3\u8bed\u58f0\u66f4\u662f\u51e0\u4e4e \u5fae\u5f31\u5f97\u65e0\u6cd5\u542c\u5230\uff0c\u4ed6\u867d\u7136\u9c81\u83bd\uff0c\u5374\u4e5f\u770b\u51fa\u8fd9\u5c11\u5973\u8bed\u4e2d\u9887\u591a\u4e0d\u5c3d\u4e0d\u5b9e\u4e4b\u5904\uff0c\u53ea\u662f\u4e0d\u77e5\u600e\u4e48\uff0c\u4ed6 \u5374\u4e0d\u613f\u8bf4\u51fa\u4e00\u4e9b\u4ee4\u8fd9\u5c11\u5973\u4f24\u5fc3\u96be\u53d7\u7684\u8bdd\u6765\uff0c\u5c24\u5176\u662f\u5f53\u5979\u6e29\u67d4\u7684\u7b11\u7740\u7684\u65f6\u5019\u3002 \u3000\u3000\u90a3\u7edd\u8272\u5c11\u5973\u679c\u7136\u4f38\u51fa\u7389\u638c\uff0c\u8f7b\u8f7b\u4e00\u62b9\u773c\u7751\uff0c\u7136\u540e\u5a07\u67d4\u5730\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5927\u5e08\uff0c\u4e0d\u7792\u60a8 \u8bf4\uff0c\u6211\u786e\u5b9e\u4f1a\u4e9b\u6b66\u529f\uff0c\u4f46\u662f\u5374\u4e07\u4e07\u6bd4\u4e0d\u4e0a\u90a3\u4e2a\u7a7f\u7740\u9ed1\u8863\u670d\u7684\u5bb6\u4f19\uff0c\u81ea\u7136\u2014\u2014\u4e5f\u4e07\u4e07\u6bd4\u4e0d\u4e0a\u5927 \u5e08\u60a8\u4e86\u3002\u201d \u3000\u3000\u591a\u4e8b\u5934\u9640\u7f13\u7f13\u653e\u4e0b\u624b\u4e2d\u6a2a\u6301\u7740\u7684\u5982\u610f\u65b9\u4fbf\u94f2\uff0c\u8138\u4e0a\u9732\u51fa\u81ea\u79cd\u601c\u501f\u7684\u795e\u8272\u6765\uff0c\u90a3\u7edd\u8272\u4e3d\u4eba \u79cb\u6ce2\u4e00\u8f6c\uff0c\u8f7b\u8f7b\u5782\u4e0b\u7f57\u8896\uff0c\u5c06\u81ea\u5df1\u5a07\u67d4\u800c\u7ea4\u5f31\u7684\u8eab\u8eaf\u5a49\u8f6c\u4e00\u6298\uff0c\u53c8\u53f9\u9053\uff1a\u201c\u5176\u5b9e\u5927\u5e08\u60a8\u4e5f\u8be5 \u770b\u5f97\u51fa\u6765\uff0c\u6211\u2014\u2014\u603b\u4e0d\u8be5\u50cf\u4e2a\u5973\u5f3a\u76d7\u5427\u3002\u201d \u3000\u3000\u591a\u4e8b\u5934\u9640\u4e00\u53cc\u795e\u5149\u68f1\u68f1\u7684\u73af\u76ee\uff0c\u6b64\u523b\u4e0d\u7981\u4e3a\u4e4b\u5c3d\u655b\u5a01\u626c\uff0c\u4e00\u62cd\u524d\u989d\uff0c\u7ec8\u4e8e\u53c8\u5c06\u5fc3\u4e2d\u6700\u540e \u4e00\u4e2a\u7591\u95ee\u95ee\u4e86\u51fa\u6765\uff0c\u201c\u4e0d\u8fc7\uff0c\u59d1\u5a18\u65b9\u624d\u5b58\u8eab\u5728\u8fd9\u6811\u4e0a\u9762\uff0c\u6d12\u5bb6\u548c\u90a3\u5c0f\u5b50\u7adf\u7136\u5168\u4e0d\u77e5\u9053\uff0c\u59d1\u5a18 \u8fd9\u8eab\u8bdd\u58f0\u672a\u4e86\uff0c\u90a3\u7edd\u8272\u5c11\u5973\u53c8\u54af\u54af\u7b11\u4e86\u8d77\u6765\uff0c\u63a9\u53e3\u9053\uff1a\u201c\u5927\u5e08\uff0c\u60a8\u53c8\u7cca\u6d82\u8d77\u6765\uff0c\u60a8\u770b\uff0c\u8fd9\u6811 \u6797\u91cc\u9762\u6797\u53f6\u8fd9\u4e48\u6d53\u5bc6\uff0c\u98ce\u53c8\u5f88\u5927\uff0c\u98ce\u5439\u5f97\u6811\u53f6\u5b50\u7c41\u7c41\u5730\u54cd\uff0c\u522b\u8bf4\u6211\u4e86\uff0c\u5c31\u7b97\u6bd4\u6211\u518d\u7b28\u4e00\u70b9\u7684 \u4eba\u722c\u4e0a\u6811\uff0c\u6050\u6015\u5927\u5e08\u4e5f\u672a\u5fc5\u542c\u5f97\u51fa\u6765\u54e9\u3002\u201d \u3000\u3000\u5979\u5a07\u4e3d\u5982\u82b1\uff0c\u8bed\u97f3\u5982\u83ba\uff0c\u5a49\u8f6c\u5a07\u67d4\u4ed6\u8bf4\u51fa\u8fd9\u756a\u8bdd\u6765\uff0c\u770b\u8fd9\u9c81\u83bd\u7684\u5934\u9640\u518d\u4e5f\u6df1\u4fe1\u4e0d\u7591\uff0c\u79cb \u6ce2\u4e2d\u4e0d\u7981\u9732\u51fa\u5f97\u610f\u7684\u795e\u91c7\u6765\uff0c\u4f46\u5979\u5374\u4e0d\u77e5\u9053\u5728\u5979\u8bf4\u8fd9\u8bdd\u7684\u65f6\u5019\uff0c\u6811\u68a2\u679c\u7136\u722c\u4e0a\u4e00\u4e2a\u4eba\u53bb\uff0c\u6b63 \u5982\u5979\u81ea\u5df1\u6240\u8bf4\uff0c\u6b64\u523b\u98ce\u5439\u6797\u6728\uff0c\u5979\u6839\u672c\u65e0\u6cd5\u542c\u5f97\u51fa\u6765\u3002 \u3000\u3000\u539f\u6765\u5353\u957f\u537f\u63a0\u5230\u6811\u540e\uff0c\u773c\u89c1\u6811\u540e\u7a7a\u7a7a\uff0c\u5fc3\u4e2d\u4e00\u6025\uff0c\u5c31\u8ffd\u4e0b\u53bb\u3002 \u3000\u3000\u4f46\u8ffd\u4e86\u4e24\u6b65\uff0c\u4ed6\u5fc3\u4e2d\u4e00\u52a8\uff0c\u6697\u60f3\u4eba\u5bb6\u5df2\u8d70\u4e86\u4e0d\u77e5\u591a\u4e45\u4e86\uff0c\u81ea\u5df1\u6839\u672c\u5c31\u672a\u5fc5\u8ffd\u5f97\u4e0a\uff0c\u800c\u4e14 \u5728\u8fd9\u79cd\u6f2b\u5bc6\u7684\u4e1b\u6797\u91cc\uff0c\u81ea\u5df1\u7eb5\u7136\u8ffd\u4e0a\uff0c\u8bf4\u4e0d\u5b9a\u53cd\u800c\u4f1a\u53d7\u5230\u4eba\u5bb6\u6697\u7b97\u3002 \u3000\u3000\u5fc3\u5ff5\u81f3\u6b64\uff0c\u4ed6\u811a\u6b65\u4e0d\u7981\u505c\u4e86\u4e0b\u6765\uff0c\u54ea\u77e5\u5374\u7a81\u7136\u542c\u5230\u4e00\u58f0\u5927\u559d\uff0c\u50cf\u662f\u90a3\u9c81\u83bd\u7684\u5934\u9640\u53d1\u51fa \u7684\uff0c\u4ed6\u5fc3\u4e2d\u4e00\u52a8\uff0c\u4fbf\u53c8\u6298\u4e86\u56de\u6765\u3002 \u3000\u3000\u8d8a\u884c\u8d8a\u8fd1\u65b9\u624d\u90a3\u682a\u5927\u6811\uff0c\u4ed6\u679c\u81ea\u53c8\u542c\u5230\u90a3\u5c11\u5973\u5a07\u67d4\u7684\u7b11\u58f0\uff0c\u6b63\u548c\u90a3\u9c81\u83bd\u7684\u5934\u9640\u8bf4\u9053\uff1a \u201c\u2026\u2026\u81ea\u7136\uff0c\u4e5f\u4e07\u4e07\u6bd4\u4e0d\u4e0a\u5927\u5e08\u60a8\u4e86\u2026\u2026\u201d \u3000\u3000\u5353\u957f\u537f\u5251\u7709\u4e00\u76b1\uff0c\u6c89\u541f\u7247\u523b\uff0c\u201c\u5530\u201d\u5730\u63a0\u4e0a\u6811\u53bb\uff0c\u522b\u8bf4\u6709\u98ce\u58f0\u63a9\u9970\uff0c\u5c31\u7b97\u6ca1\u6709\u98ce\u58f0\uff0c\u4e5f \u65e0\u4eba\u80fd\u591f\u542c\u51fa\u4ed6\u8eab\u5f62\u63a0\u65f6\u7684\u58f0\u97f3\u6765\u3002 \u3000\u3000\u4ed6\u5c45\u9ad8\u4e34\u4e0b\uff0c\u53ea\u89c1\u90a3\u5c11\u5973\u626d\u8f6c\u5a07\u4f53\uff0c\u6b63\u53c8\u67d4\u58f0\u9053\uff1a\u201c\u60a8\u4e5f\u8be5\u770b\u5f97\u51fa\u6765\uff0c\u6211\u2014\u2014\u603b\u4e0d\u8be5\u50cf \u4e2a\u5973\u5f3a\u76d7\u5427\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u542c\u5728\u8033\u91cc\uff0c\u518d\u60f3\u5230\u5979\u65b9\u624d\u4e0d\u662f\u4e5f\u548c\u81ea\u5df1\u8bf4\u7740\u7c7b\u4f3c\u7684\u8bdd\uff1a\u201c\u2026\u2026\u4f60\u770b\uff0c\u6211\u8fd9\u53cc\u624b\u50cf \u662f\u6740\u4eba\u7684\u5417\uff1f\u201d \u3000\u3000\u5fc3\u91cc\u4e0d\u77e5\u662f\u7b11\u662f\u6012\u3002 \u3000\u3000\u53c8\u542c\u5230\u90a3\u5c11\u5973\u8bf4\uff1a\u201c\u2026\u2026\u5c31\u7b97\u518d\u7b28\u4e00\u4e9b\u7684\u4eba\u722c\u4e0a\u53bb\u2026\u2026\u201d \u3000\u3000\u4ed6\u51e0\u4e4e\u5fcd\u4e0d\u4f4f\u8981\u8dc3\u4e0b\u6811\u53bb\uff0c\u4f46\u8f6c\u5ff5\u4e00\u60f3\uff0c\u6b64\u523b\u8fd9\u9c81\u83bd\u7684\u5934\u9640\u60f3\u5fc5\u5df2\u53d7\u8fd9\u5c11\u5973\u4e4b\u611a\uff0c\u81ea\u5df1 \u8dc3\u4e0b\u6811\u53bb\uff0c\u4ed6\u4e00\u5b9a\u4f1a\u5e2e\u7740\u8fd9\u72e1\u9ee0\u7684\u7f8e\u4e3d\u7684\u5c11\u5973\u8054\u624b\u5bf9\u4ed8\u81ea\u5df1\u4e00\u9042\u5c31\u5c4f\u4f4f\u58f0\u606f\uff0c\u5728\u8fd9\u6d53\u5bc6\u7684\u6811 \u53f6\u91cc\u770b\u7740\u8fd9\u5c11\u5973\u5bf9\u90a3\u5934\u9640\u5728\u73a9\u4ec0\u4e48\u82b1\u6837\u3002 \u3000\u3000\u591a\u4e8b\u5934\u9640\u4e00\u624b\u6301\u7740\u65b9\u4fbf\u94f2\uff0c\u5e9e\u5927\u7684\u8eab\u8eaf\uff0c\u4fbf\u659c\u659c\u5728\u90a3\u53ea\u53ef\u521a\u53ef\u67d4\u7684\u65b9\u4fbf\u94f2\u4e0a\uff0c\u50cf\u662f\u5728\u601d \u7d22\u7740\u4ec0\u4e48\u7684\u6837\u5b50\u3002 \u3000\u3000\u90a3\u7edd\u8272\u4e3d\u4eba\u5374\u5fae\u4f38\u7389\u624b\uff0c\u629a\u5f04\u7740\u53d1\u8fb9\u7684\u4e71\u53d1\uff0c\u7a81\u53c8\u95ee\u9053\uff1a\u201c\u5927\u5e08\uff0c\u60a8\u8fd9\u6b21\u6765\uff0c\u662f\u4e0d\u662f\u4e5f \u4e3a\u7740\u90a3\u5929\u76ee\u5c71\u7684\u76db\u4f1a\u5440\uff1f\u201d \u3000\u3000\u591a\u4e8b\u5934\u9640\u53cc\u76ee\u4e00\u5f20\uff0c\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\uff1f\u201d \u3000\u3000\u90a3\u7edd\u8272\u4e3d\u4eba\u201c\u6251\u54e7\u201d\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u60a8\u8fd9\u6b21\u6765\u662f\u4e3a\u4e86\u60f3\u5f04\u628a\u5b9d\u5251\u5462\uff0c\u8fd8\u662f\u60f3\u5f97\u5230\u90a3\u4f4d\u7f8e\u4eba \u5462\uff1f\u201d \u3000\u3000\u591a\u4e8b\u5934\u9640\u7a81\u7136\u4ef0\u5929\u957f\u7b11\uff0c\u4e00\u9762\u7528\u624b\u62cd\u7740\u524d\u989d\uff0c\u8fde\u58f0\u9053\uff1a\u201c\u4eba\u4eba\u90fd\u9053\u6d12\u5bb6\u2018\u591a\u4e8b\u2019\uff0c\u4f60\u8fd9 \u5c0f\u59d1\u5a18\u5374\u6bd4\u6d12\u5bb6\u8fd8\u8981\u591a\u4e8b\uff0c\u8fde\u6d12\u5bb6\u7684\u4e8b\u90fd\u7ba1\u4e86\u8d77\u6765\uff0c\u6d12\u5bb6\u65e2\u975e\u4e3a\u5251\uff0c\u4ea6\u975e\u4e3a\u4eba\uff0c\u5374\u53ea\u60f3\u5f04\u51e0 \u4e24\u94f6\u5b50\u3002\u201d \u3000\u3000\u8fd9\u6b21\u8f6e\u5230\u90a3\u7edd\u8272\u4e3d\u4eba\u4e00\u6014\uff0c\u5374\u542c\u591a\u4e8b\u5934\u9640\u53c8\u590d\u7b11\u9053\uff1a\u201c\u6d12\u5bb6\u6b64\u6b21\u5357\u6e38\u4ee5\u6765\uff0c\u53c8\u7ba1\u4e86\u4e0d\u5c11 \u7684\u95f2\u4e8b\uff0c\u522b\u7684\u4e0d\u8bf4\uff0c\u6d12\u5bb6\u7adf\u6b20\u4e86\u522b\u4eba\u7684\u4e00\u4e07\u4e24\u94f6\u5b50\u7684\u503a\uff0c\u5c0f\u59d1\u5a18\uff0c\u4f60\u60f3\u60f3\uff0c\u6d12\u5bb6\u8eab\u4e0a\u9664\u4e86\u8fd9 \u65b9\u4fbf\u94f2\u8fd8\u503c\u51e0\u4e2a\u94b1\u4e4b\u5916\uff0c\u8fd8\u6709\u4ec0\u4e48\u4e1c\u897f\uff0c\u600e\u4e48\u8fd8\u5f97\u4e86\u4eba\u5bb6\u7684\u503a\uff0c\u6240\u4ee5\u4e48\u2026\u2026\u54c8\u54c8\uff0c\u542c\u5230\u5929\u76ee \u5c71\u4e0a\u8fd9\u7b49\u4e8b\uff0c\u6d12\u5bb6\u5c31\u8d76\u6765\u4e86\u3002\u201d \u3000\u3000\u90a3\u7edd\u8272\u4e3d\u4eba\u5a07\u7f8e\u7684\u8138\u5e9e\u4e0a\u559c\u52a8\u989c\u8272\uff0c\u79cb\u6ce2\u4e00\u8f6c\uff0c\u5a07\u7b11\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u6211\u82e5\u662f\u66ff\u5927\u5e08\u8fd8\u4e86 \u503a\uff0c\u5927\u5e08\u53ef\u4e0d\u53ef\u4ee5\u518d\u5e2e\u6211\u4e00\u4e2a\u5fd9\u5462\uff1f\u201d \u3000\u3000\u591a\u4e8b\u5934\u9640\u8eab\u8eaf\u4e00\u76f4\uff0c\u5927\u58f0\u9053\uff1a\u201c\u90a3\u82e5\u662f\u597d\u4e8b\uff0c\u6d12\u5bb6\u4e0d\u8981\u4f60\u7684\u94f6\u5b50\u4e5f\u884c\uff0c\u53ef\u662f\u4f60\u82e5\u8981\u60f3\u53eb \u6d12\u5bb6\u505a\u4e9b\u4e0d\u4ec1\u4e0d\u4e49\u4e4b\u4e8b\uff0c\u2014\u2014\u6d12\u5bb6\u5148\u4e00\u94f2\u6253\u6241\u4f60\u3002\u201d \u3000\u3000\u8eb2\u5728\u6797\u53f6\u4e2d\u7684\u5353\u957f\u537f\u4e0d\u7981\u6697\u8d5e\u4e00\u58f0\uff1a\u201c\u8fd9\u591a\u4e8b\u5934\u9640\u867d\u7136\u9c81\u83bd\uff0c\u5374\u4e0d\u5931\u662f\u6761\u9876\u5929\u7acb\u5730\u7684\u6c49 \u5b50\u3002\u201d \u3000\u3000\u76ee\u5149\u4e0b\u671b\uff0c\u5374\u89c1\u90a3\u7edd\u8272\u5c11\u5973\u53c8\u7b11\u9053\uff1a\u201c\u6211\u600e\u4f1a\u8bf7\u5927\u5e08\u505a\u4e0d\u4ec1\u4e0d\u4e49\u7684\u4e8b\u5462\uff1f\u201d \u3000\u3000\u79cb\u6ce2\u4e00\u8f6c\uff0c\u8885\u5a1c\u524d\u884c\u4e24\u6b65\uff0c\u53c8\u7b11\u9053\uff1a\u201c\u5927\u5e08\uff0c\u4f60\u6709\u6ca1\u6709\u770b\u8fc7\u90a3\u4e09\u5e45\u753b\u5440\u2014\u2014\u5c31\u662f\u4e0a\u9762\u753b \u7740\u5b9d\u5251\u3001\u9ec4\u91d1\u548c\u4e00\u4e2a\u5973\u5b69\u5b50\u7684\u90a3\u4e09\u5e45\u753b\u3002\u201d \u3000\u3000\u591a\u4e8b\u5934\u9640\u4e00\u53cc\u73af\u76ee\u5728\u90a3\u5c11\u5973\u9762\u524d\u4e00\u626b\uff0c\u7a81\u53c8\u54c8\u54c8\u5927\u7b11\u4e86\u8d77\u6765\uff0c\u8fde\u58f0\u9053\uff1a\u201c\u6d12\u5bb6\u771f\u662f\u7cca \u6d82\uff0c\u9152\u5bb6\u771f\u662f\u7cca\u6d82\uff0c\u96be\u602a\u770b\u7740\u4f60\u597d\u751f\u9762\u719f\uff0c\u539f\u6765\u4f60\u5c31\u662f\u90a3\u5e45\u753b\u4e0a\u7684\u5973\u5b50\uff0c\u597d\u6781\u597d\u6781\uff0c\u6d12\u5bb6\u6b63 \u597d\u95ee\u4f60\uff0c\u4f60\u5728\u5929\u76ee\u5c71\u4e0a\uff0c\u7a76\u7adf\u5f04\u4e9b\u4ec0\u4e48\u82b1\u6837\uff0c\u800c\u80fd\u96be\u5012\u8fd9\u4e9b\u4e0d\u8fdc\u5343\u91cc\u800c\u6765\u7684\u6b66\u6797\u7fa4\u8c6a\uff1f\u4f60\u90a3 \u4e9b\u9ec4\u91d1\uff0c\u7a76\u7adf\u662f\u4ece\u54ea\u91cc\u6765\u7684\uff1f\u8fd8\u6709\uff0c\u4f60\u8fd9\u6837\u505a\u7a76\u7adf\u662f\u4e3a\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u591a\u4e8b\u5934\u9640\u4e00\u8fde\u4e32\u95ee\u4e86\u4e09\u53e5\uff0c\u5374\u4e5f\u662f\u8eb2\u5728\u6811\u4e0a\u7684\u5353\u957f\u537f\u4ee5\u53ca\u4e0d\u8fdc\u5343\u91cc\u8dcb\u6d89\u800c\u6765\u7684\u5929\u4e0b\u6b66\u6797 \u7fa4\u8c6a\u5fc3\u91cc\u60f3\u95ee\u672a\u95ee\u51fa\u6765\u7684\u8bdd\u3002 \u3000\u3000\u90a3\u7edd\u8272\u4e3d\u4eba\u79cb\u6ce2\u8f6c\u4e86\u4e24\u8f6c\uff0c\u5ffd\u53c8\u201c\u6251\u54e7\u201d\u4e00\u58f0\uff0c\u5a07\u7b11\u8d77\u6765\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u60a8\u4e00\u8fde\u4e32\u95ee\u4e86 \u4eba\u5bb6\u8fd9\u4e48\u591a\u95ee\u9898\uff0c\u53eb\u6211\u600e\u4e48\u56de\u7b54\u60a8\u624d\u597d\u5462\u2014\u2014\u8fd9\u6837\u597d\u4e86\uff0c\u6211\u7d22\u6027\u5e26\u60a8\u53bb\u770b\u770b\uff0c\u8fd9\u4e48\u60a8\u4e0d\u5c31\u5168 \u77e5\u9053\u4e86\u5417\uff1f\u201d \u3000\u3000\u5353\u957f\u537f\u5c45\u9ad8\u4e34\u4e0b\uff0c\u53ea\u89c1\u8fd9\u5c11\u5973\u7b11\u8d77\u6765\u6709\u5982\u82b1\u679d\u4e71\u98a4\uff0c\u5934\u4e0a\u7684\u9b13\u53d1\uff0c\u4e5f\u4e0d\u4f4f\u968f\u98ce\u98d8\u821e\uff0c\u4e0d \u7981\u6697\u4e2d\u81ea\u5fd6\u9053\uff1a\u201c\u6211\u5728\u4e66\u7c4d\u4e0a\u5e38\u5e38\u770b\u5230\u5c24\u7269\u4e8c\u5b57\uff0c\u5374\u59cb\u7ec8\u4e0d\u77e5\u9053\u8981\u600e\u6837\u7684\u4eba\u80fd\u79f0\u5f97\u4e0a\u5c24\u7269\uff0c \u4eca\u89c1\u4e86\u8fd9\u5c11\u5973\uff0c\u624d\u77e5\u9053\u5c24\u7269\u662f\u4ec0\u4e48\u6837\u5b50\uff0c\u5509\u2014\u2014\u770b\u6765\u666e\u5929\u4e4b\u4e0b\u9664\u4e86\u5979\u4e4b\u5916\uff0c\u6050\u6015\u4e5f\u518d\u96be\u627e\u51fa \u4e00\u4e2a\u8fd9\u6837\u7684\u4eba\u6765\u4e86\u3002\u201d \u3000\u3000\u4e00\u5ff5\u81f3\u6b64\uff0c\u5ffd\u53c8\u60f3\u5230\u81ea\u5df1\u7684\u7239\u7239\u5728\u6559\u81ea\u5df1\u5ff5\u4e66\u4e4b\u65f6\uff0c\u5e38\u5e38\u8bf4\u7684\u51e0\u53e5\u8bdd\u6765\u3002 \u3000\u3000\u4e00\u65f6\u4e4b\u95f4\uff0c\u4ed6\u50cf\u53c8\u770b\u5230\u4ed6\u7239\u7239\u6b63\u5e26\u7740\u6ee1\u8138\u6148\u7965\u4eb2\u5207\uff0c\u5374\u53c8\u6b63\u6c14\u8083\u7136\u7684\u795e\u60c5\uff0c\u7ad9\u5728\u4ed6\u773c \u524d\uff0c\u624b\u91cc\u62ff\u7740\u4e00\u672c\u4e66\uff0c\u53cd\u53cd\u590d\u590d\u5730\u6559\u4ed6\u5ff5\u7740\u4e66\u4e0a\u7684\u8bcd\u53e5\uff0c\u6bcf\u5f53\u8bfb\u5230\u201c\u5b54\u65e5\u6210\u4ec1\u5b5f\u65e5\u53d6\u4e49\uff0c\u552f \u5176\u4e49\u5c3d\uff0c\u6240\u4ee5\u4ec1\u81f3\u2026\u2026\u800c\u4eca\u800c\u540e\uff0c\u5eb6\u51e0\u65e0\u6127\u3002\u201d\u8fd9\u4e00\u7c7b\u8bdd\u65f6\uff0c\u7239\u7239\u5c31\u4f1a\u4e3a\u8fd9\u63a9\u5377\u53f9\u606f\u3002 \u3000\u3000\u201c\u7239\u7239\u7ec8\u4e8e\u6210\u4ec1\u53d6\u4e49\u4e86\uff0c\u4ed6\u4e00\u751f\u4e4b\u4e2d\uff0c\u8be5\u6ca1\u6709\u4ec0\u4e48\u53f9\u606f\u3001\u60ed\u6127\u7684\u4e8b\u4e86\u5427\uff0c\u4f46\u662f\u7239\u7239\u4e3a\u4f55 \u53c8\u6b7b\u5f97\u90a3\u4e48\u4e0d\u503c\u5f97\u5462\uff1f\u60a8\u8001\u4eba\u5bb6\u4e3a\u522b\u4eba\u4e4b\u6b7b\u53f9\u606f\uff0c\u53ef\u662f\u6b64\u523b\u832b\u832b\u5929\u4e0b\uff0c\u53c8\u6709\u8c01\u4f1a\u4e3a\u60a8\u8001\u4eba\u5bb6 \u7684\u6b7b\u53f9\u606f\u5462\uff1f\u201d \u3000\u3000\u4ed6\u5fc3\u4e2d\u601d\u6f6e\u7ffb\u6d8c\uff0c\u4e00\u4f1a\u513f\u60f3\u5230\u7239\u7239\u5988\u5988\uff0c\u4e00\u4f1a\u513f\u53c8\u60f3\u5230\u81ea\u5df1\u5feb\u4e50\u7684\u7ae5\u5e74\uff0c\u4f46\u5feb\u4e50\u7684\u7ae5\u5e74 \u901d\u53bb\u6c38\u4e0d\u518d\u6765\uff0c\u6b7b\u53bb\u7684\u53cc\u4eb2\u4e5f\u6c38\u4e0d\u4f1a\u590d\u751f\u4e86\u3002 \u3000\u3000\u5728\u8fd9\u7ffb\u6d8c\u7684\u601d\u6f6e\u4e2d\uff0c\u5374\u4f3c\u4e4e\u6709\u4e00\u70b9\u7ea2\u8272\u7684\u5f71\u5b50\u8d8a\u6765\u8d8a\u5927\uff0c\u7ec8\u4e8e\u51dd\u6210\u90a3\u7edd\u8272\u4e3d\u4eba\u7684\u8eab\u5f62\uff0c \u4f3c\u4e4e\u53c8\u5a07\u7b11\u7740\u4f38\u51fa\u4e00\u53cc\u6709\u5982\u6625\u8471\u7684\u7389\u624b\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u8fd9\u50cf\u4e00\u53cc\u6740\u4eba\u7684\u624b\u5417\uff1f\u201d \u3000\u3000\u201c\u8fd9\u50cf\u4e00\u53cc\u6740\u4eba\u7684\u624b\u5417\uff1f\u8fd9\u50cf\u4e00\u53cc\u6740\u4eba\u7684\u624b\u5417\uff1f\u2026\u2026\u201d\u8fd9\u8bdd\u4f3c\u4e4e\u4e00\u65ec\u8fde\u7740\u4e00\u53e5\uff0c\u5728\u5353\u957f \u537f\u7684\u8111\u6d77\u4e2d\u649e\u51fb\u7740\u3001\u6269\u6563\u7740\uff0c\u4ed6\u832b\u7136\u95ee\u8d77\u773c\u775b\uff0c\u54ea\u77e5\u773c\u524d\u5374\u53c8\u6d6e\u52a8\u51fa\u81ea\u5df1\u7239\u7239\u7684\u8eab\u5f71\uff0c\u6ee1\u8eab \u6d74\u8840\uff0c\u6b63\u81ea\u6233\u6307\u4eba\u9a82\uff1a\u201c\u6211\u6b7b\u4e86\uff0c\u4f60\u8fd9\u4e0d\u5b5d\u7684\u513f\u5b50\u4e5f\u4e0d\u66ff\u6211\u62a5\u4ec7\uff0c\u5fc3\u91cc\u5374\u5728\u60f3\u7740\u4ec7\u4eba\u7684\u5f1f \u4e8e\uff0c\u5728\u60f3\u7740\u5979\u662f\u4e2a\u5c24\u7269\uff0c\u6211\u8981\u4f60\u8fd9\u4e0d\u5b5d\u7684\u513f\u5b50\u53c8\u6709\u4f55\u7528\u3002\u201d \u3000\u3000\u731b\u7136\u4e00\u62f3\uff0c\u6253\u5728\u81ea\u5df1\u8138\u4e0a\u3002 \u3000\u3000\u4ed6\u5927\u53eb\u4e00\u58f0\uff0c\u4ece\u6811\u6860\u4e0b\u6eda\u4e86\u4e0b\u53bb\uff0c\u5f20\u76ee\u56db\u987e\uff0c\u6797\u4e2d\u7a7a\u7a7a\uff0c\u4e0d\u4f46\u81ea\u5df1\u7239\u7239\u7684\u5f71\u5b50\u4e0d\u89c1\u4e86\uff0c \u90a3\u5c11\u5973\u548c\u591a\u4e8b\u5934\u9640\u4e5f\u5931\u53bb\u8e2a\u8ff9\u3002\u4f38\u624b\u4e00\u63e1\uff0c\u53ea\u89c9\u638c\u5fc3\u6e7f\u6e7f\u7684\uff0c\u6ee1\u662f\u51b7\u6c57\uff0c\u65b9\u624d\u7adf\u4f3c\u505a\u4e86\u4e00\u573a \u5669\u68a6\u3002\u4f46\u6b64\u523b\u5669\u68a6\u5df2\u9192\uff0c\u4ed6\u5374\u4e0d\u7981\u6697\u9a82\u81ea\u5df1\uff0c\u600e\u4e48\u5728\u8fd9\u7d27\u8981\u5173\u5934\u4e0a\uff0c\u5374\u60f3\u8d77\u5fc3\u4e8b\u6765\uff0c\u6b64\u523b\u90a3 \u5c11\u5973\u65e9\u5df2\u8d70\u5f97\u4e0d\u77e5\u54ea\u91cc\u53bb\uff0c\u5374\u6559\u81ea\u5df1\u5982\u4f55\u627e\u53bb\u3002 \u3000\u3000\u53c8\u60f3\u5230\u90a3\u5c11\u5973\u6c42\u591a\u4e8b\u5934\u9640\u4e00\u4e8b\uff0c\u5374\u4e0d\u77e5\u53c8\u662f\u4ec0\u4e48\u4e8b\uff0c\u591a\u4e8b\u5934\u9640\u65b9\u624d\u95ee\u5979\u7684\u4e09\u4e2a\u95ee\u9898\uff0c\u53c8 \u4e0d\u77e5\u5979\u5230\u5e95\u5982\u4f55\u56de\u7b54\u3002 \u3000\u3000\u5353\u957f\u537f\u867d\u662f\u806a\u660e\u7edd\u9876\u4e4b\u4eba\uff0c\u4f46\u5230\u5e95\u5e74\u7eaa\u8fd8\u8f7b\uff0c\u53c8\u662f\u521d\u5165\u6c5f\u6e56\uff0c\u6b64\u523b\u9762\u4e34\u7740\u8bb8\u591a\u9519\u7efc\u590d\u6742 \u4e4b\u4e8b\uff0c\u4e0d\u7981\u5446\u5446\u5730\u6115\u4f4f\u4e86\uff0c\u832b\u7136\u6ca1\u6709\u5934\u7eea\u3002 \u7b2c\u516b\u7ae0\u3000\u9999\u8f66\u5b9d\u76d6>> \u53e4\u9f99\u300a\u6708\u5f02\u661f\u90aa\u300b \u7b2c\u516b\u7ae0\u3000\u9999\u8f66\u5b9d\u76d6 \u3000\u3000\u4ed6\u5446\u5446\u5730\u6115\u4e86\u534a\u664c\uff0c\u672c\u60f3\u7b14\u76f4\u8d70\u5411\u592d\u76ee\u5c71\uff0c\u53bb\u5bfb\u90a3\u7edd\u8272\u5c11\u5973\uff0c\u4f46\u8f6c\u5ff5\u4e00\u60f3\uff0c\u81ea\u5df1\u5c31\u7b97\u627e \u5230\u4e86\u5979\u53c8\u5f53\u5982\u4f55\uff0c\u4f55\u51b5\u504c\u5927\u4e00\u5ea7\u5929\u76ee\u5c71\uff0c\u81ea\u5df1\u6839\u672c\u5c31\u672a\u5fc5\u627e\u5f97\u5230\uff0c\u60f3\u4e86\u60f3\uff0c\u4e0d\u7981\u5fd6\u9053\uff1a\u201c\u6211 \u8fd8\u662f\u5148\u53bb\u627e\u5230\u4e91\u8001\u4f2f\u7236\u5b50\u624d\u662f\u3002\u201d \u3000\u3000\u4ed6\u5c31\u50cf\u4e00\u4e2a\u65e0\u4e3b\u610f\u7684\u5b69\u5b50\uff0c\u6781\u9700\u6709\u4e2a\u4eba\u80fd\u4e3a\u4ed6\u5206\u89e3\u5fc3\u4e2d\u7d0a\u4e71\uff0c\u4ed6\u5929\u6027\u672c\u751a\u575a\u6bc5\uff0c\u5341\u5e74\u6df1 \u5c71\u82e6\u7ec3\uff0c\u66f4\u4f7f\u5f97\u4ed6\u6709\u7740\u8d85\u4e8e\u5e38\u4eba\u7684\u667a\u6167\uff0c\u4f46\u6b64\u523b\u5fc3\u7eea\u5374\u4e00\u4e71\u5982\u662f\uff0c\u4ed6\u53ea\u5f53\u662f\u81ea\u5df1\u5904\u4e16\u7ecf\u9a8c\u4e0d \u591f\uff0c\u4e34\u4e8b\u96be\u514d\u5982\u6b64\uff0c\u5374\u4e0d\u77e5\u81ea\u5df1\u5df2\u5bf9\u90a3\u5c11\u5973\u6709\u4e86\u4e00\u79cd\u96be\u4ee5\u89e3\u91ca\u7684\u60c5\u611f\uff0c\u8fd9\u79cd\u60c5\u611f\u662f\u4ed6\u8fde\u505a\u68a6 \u90fd\u6ca1\u6709\u60f3\u5230\u7684\u3002 \u3000\u3000\u987b\u77e5\u4eba\u4eec\u5c06\u81ea\u5df1\u7684\u60c5\u611f\u538b\u5236\uff0c\u60c5\u611f\u53cd\u4f1a\u5728\u4e0d\u77e5\u4e0d\u89c9\u4e2d\u5954\u53d1\u51fa\u6765\uff0c\u7b49\u5230\u81ea\u5df1\u53d1\u89c9\u7684\u65f6\u5019\uff0c \u8fd9\u79cd\u60c5\u611f\u5374\u65e9\u5df2\u50cf\u6d2a\u6c34\u822c\u5c06\u81ea\u5df1\u541e\u6ca1\u4e86\u3002 \u3000\u3000\u4ed6\u957f\u53f9\u4e00\u58f0\uff0c\u8d70\u51fa\u6797\u5916\uff0c\u54ea\u77e5\u8eab\u540e\u7a81\u7136\u54cd\u8d77\u4e00\u4e2a\u51b7\u51b7\u7684\u7b11\u58f0\uff0c\u56de\u5934\u671b\u53bb\uff0c\u53ea\u89c1\u65b9\u624d\u5728\u57ce \u579b\u4e0a\u548c\u81ea\u5df1\u52a8\u624b\u7684\u9ec4\u886b\u5c11\u5e74\uff0c\u5de6\u624b\u629a\u7740\u4e0b\u9894\uff0c\u53f3\u624b\u653e\u5728\u5de6\u808b\u4e0b\uff0c\u6b63\u671b\u7740\u81ea\u5df1\u563f\u563f\u51b7\u7b11\u3002 \u3000\u3000\u4ed6\u548c\u8fd9\u9ec4\u886b\u5c11\u5e74\u672c\u6765\u7d20\u4e0d\u76f8\u8bc6\uff0c\u65b9\u624d\u867d\u5df2\u52a8\u8fc7\u624b\uff0c\u4f46\u5f7c\u6b64\u4e4b\u95f4\uff0c\u5374\u65e0\u7ea0\u845b\uff0c\u6b64\u65f6\u4ed6\u5fc3\u4e2d \u4e71\u6210\u5982\u9ebb\uff0c\u54ea\u6709\u5fc3\u60c5\u518d\u591a\u60f9\u9ebb\u70e6\uff0c\u671b\u4e86\u4e00\u773c\uff0c\u4fbf\u53c8\u56de\u8eab\u8d70\u53bb\uff0c\u4e00\u9762\u5728\u5fc3\u4e2d\u5bfb\u601d\uff0c\u8981\u600e\u6837\u4ece\u90a3 \u5c11\u5973\u8eab\u4e0a\uff0c\u627e\u7740\u5979\u5e08\u7236\u4e11\u4eba\u6e29\u5982\u7389\u7684\u4e0b\u843d\u6765\u3002 \u3000\u3000\u201c\u597d\u5927\u7684\u67b6\u5b50\uff0c\u5374\u8fde\u4e2a\u5973\u5b50\u4e5f\u8ffd\u4e0d\u4e0a\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u6115\u7136\u56de\u987e\uff0c\u5fc3\u60f3\u6211\u4e0e\u6b64\u4eba\u7d20\u4e0d\u76f8\u8bc6\uff0c\u4ed6\u600e\u4e48\u5904\u5904\u627e\u9ebb\u70e6\uff0c\u90a3\u9ec4\u886b\u5c11\u5e74\u89c1\u4ed6\u8f6c\u56de \u5934\uff0c\u4e24\u773c\u4e0a\u7ffb\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u9601\u4e0b\u5e74\u7eaa\u867d\u8f7b\uff0c\u6b66\u529f\u5374\u4e0d\u5f31\uff0c\u771f\u662f\u96be\u5f97\u7684\u5f88\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u53c8\u662f\u4e00\u6115\uff0c\u5fc3\u60f3\u6b64\u4eba\u600e\u4e48\u5982\u6b64\u5947\u602a\uff0c\u65b9\u624d\u51fa\u8a00\u8ba5\u5632\u81ea\u5df1\uff0c\u6b64\u523b\u53c8\u6367\u8d77\u81ea\u5df1\u6765\uff0c\u4f46 \u8bed\u6c14\u4e4b\u4e2d\uff0c\u8001\u6c14\u6a2a\u79cb\uff0c\u5374\u53c8\u6ca1\u6709\u534a\u70b9\u6367\u4eba\u7684\u610f\u601d\u3002 \u3000\u3000\u5374\u89c1\u8fd9\u9ec4\u886b\u5c11\u5e74\u653e\u4e0b\u53cc\u624b\uff0c\u8d1f\u5728\u8eab\u540e\uff0c\u4e24\u773c\u671b\u5728\u5929\u4e0a\u7f13\u7f13\u8e31\u8d77\u65b9\u6b65\u6765\uff0c\u4e00\u9762\u53c8\u9053\uff1a\u201c\u53ea \u662f\u9601\u4e0b\u82e5\u60f3\u51ed\u7740\u8fd9\u70b9\u8eab\u624b\uff0c\u5c31\u60f3\u72ec\u5360\u9b41\u9996\uff0c\u54fc\uff0c\u90a3\u8fd8\u5dee\u5f97\u8fdc\u5462\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u518d\u5fcd\u4e0d\u4f4f\u5fc3\u4e2d\u7684\u6028\u6c14\uff0c\u5389\u58f0\u9053\uff1a\u201c\u5728\u4e0b\u4e0e\u5144\u53f0\u7d20\u4e0d\u76f8\u8bc6\uff0c\u5144\u53f0\u5c61\u5c61\u4ee5\u8a00\u76f8\u6b3a\uff0c\u5374 \u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u90a3\u9ec4\u886b\u5c11\u5e74\u671b\u4e5f\u4e0d\u671b\u5353\u957f\u537f\u4e00\u773c\uff0c\u51b7\u51b7\u63a5\u9053\uff1a\u201c\u5728\u4e0b\u7684\u610f\u601d\u5c31\u662f\u8bf7\u9601\u4e0b\u5c11\u60f9\u9ebb\u70e6\uff0c\u9601\u4e0b \u4ece\u4f55\u5904\u6765\uff0c\u5c31\u5feb\u4e9b\u56de\u4f55\u5904\u53bb\uff0c\u4e0d\u7136\u2014\u2014\u54fc\u54fc\uff0c\u771f\u5f97\u2014\u2014\u54fc\u54fc\u3002\u201d \u3000\u3000\u4ed6\u4e00\u8fde\u201c\u54fc\u201d\u4e86\u56db\u58f0\uff0c\u867d\u672a\u8bf4\u51fa\u4e0b\u6587\u6765\uff0c\u4f46\u8a00\u4e0b\u4e4b\u610f\uff0c\u5353\u957f\u537f\u53c8\u4e0d\u662f\u5446\u5b50\uff0c\u54ea\u6709\u4e0d\u660e\u4e4b \u7406\uff0c\u5251\u7709\u4e00\u8f69\uff0c\u4ea6\u81ea\u51b7\u7b11\u8bf4\u9053\uff1a\uff1a\uff1a\u8fd9\u53ef\u602a\u4e86\uff0c\u5728\u4e0b\u4ece\u4f55\u5904\u6765\uff0c\u5f80\u4f55\u5904\u53bb\uff0c\u53c8\u4e0e\u9601\u4e0b\u4f55\u5e72\uff0c \u81f3\u4e8e\u5728\u4e0b\u4f1a\u4e0d\u4f1a\u60f9\u4e0a\u9ebb\u70e6\uff0c\u90a3\u66f4\u662f\u5728\u4e0b\u81ea\u5df1\u4e4b\u4e8b\u4e86\u3002\u201c\u90a3\u9ec4\u886b\u5c11\u5e74\u53cc\u76ee\u4e00\u5f20\uff0c\u76ee\u5149\u4fbf\u6709\u5982\u4e24 \u9053\u5229\u7bad\uff0c\u5c04\u5728\u5353\u957f\u537f\u8eab\u4e0a\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u9601\u4e0b\u4e24\u65e5\u4e4b\u5185\u82e5\u4e0d\u79bb\u5f00\u8fd9\u4e34\u5b89\u57ce\uff0c\u54fc\u2014\u2014\u53ea\u6015\u518d\u60f3\u8d70 \u5c31\u5acc\u665a\u4e86\u3002\u201d \u3000\u3000\u957f\u8896\u4e00\u62c2\uff0c\u56de\u5934\u5c31\u8d70\uff0c\u54ea\u77e5\u773c\u524d\u4e00\u82b1\uff0c\u90a3\u5353\u957f\u537f\u7adf\u7a81\u7136\u6321\u5728\u4ed6\u8eab\u540e\uff0c\u8eab\u5f62\u4e4b\u75be\uff0c\u6709\u5982\u82cd \u9e70\u3002 \u3000\u3000\u8fd9\u4e00\u6765\u5374\u4ee4\u5f97\u90a3\u9ec4\u886b\u5c11\u5e74\u5c91\u7cb2\u4e3a\u4e4b\u4e00\u6014\uff0c\u53ea\u89c1\u5353\u957f\u537f\u9762\u5e26\u5bd2\u971c\uff0c\u773c\u5982\u5229\u7bad\uff0c\u5389\u58f0\u9053\uff1a \u201c\u4f60\u65b9\u624d\u8bf4\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u90a3\u9ec4\u886b\u5c11\u5e74\u5c91\u7cb2\u867d\u89c9\u5bf9\u65b9\u795e\u52bf\u8d6b\u8d6b\uff0c\u6b63\u6c14\u51db\u7136\uff0c\u4f46\u4ed6\u81ea\u6043\u8eab\u624b\uff0c\u4e14\u53c8\u662f\u6781\u7aef\u5028\u505a\u81ea\u5927\u4e4b \u4eba\uff0c\u53cc\u76ee\u5fae\u7ffb\uff0c\u51b7\u54fc\u4e00\u58f0\uff0c\u53c8\u81ea\u8bf4\u9053\uff1a\u201c\u4e24\u65e5\u4e4b\u5185\u82e5\u4e0d\u79bb\u5f00\u8fd9\u4e34\u5b89\u57ce\uff0c\u54fc\u2014\u2014\u201d\u54ea\u77e5\u4ed6\u8bed\u72b9 \u672a\u4e86\uff0c\u5353\u957f\u537f\u7a81\u7136\u5389\u53f1\u4e00\u58f0\uff0c\u53f3\u624b\u4e00\u4f38\uff0c\u5feb\u5982\u95ee\u7535\u822c\u6293\u4f4f\u4ed6\u7684\u8863\u895f\uff0c\u5389\u58f0\u9053\uff1a\u201c\u4e24\u65e5\u4e4b\u524d\uff0c \u5728\u90a3\u5feb\u5200\u4f1a\u4e0e\u7ea2\u5dfe\u4f1a\u623f\u4e2d\u7559\u4e0b\u5b57\u67ec\u7684\uff0c\u662f\u4e0d\u662f\u4f60\uff1f\u201d \u3000\u3000\u9ec4\u886b\u5c11\u5e74\u5c91\u7cb2\u518d\u4e5f\u60f3\u4e0d\u5230\u4ed6\u4f1a\u7a81\u7136\u51fa\u624b\uff0c\u6b64\u523b\u88ab\u4ed6\u6293\u4f4f\u8863\u895f\uff0c\u7adf\u6014\u4e86\u4e00\u6014\uff0c\u968f\u5373\u5251\u7709\u6012 \u8f69\uff0c\u53f3\u624b\u624b\u8155\u4e00\u7ffb\uff0c\u53bb\u6263\u5353\u957f\u537f\u7684\u8109\u95e8\uff0c\u5de6\u624b\u5e76\u6307\u5982\u5251\uff0c\u75be\u70b9\u5411\u4ed6\u814b\u4e0b\u4e09\u5bf8\uff0c\u4e73\u540e\u4e00\u5bf8\uff0c\u7740 \u808b\u76f4\u814b\u3001\u6485\u808b\u95f4\u7684\u5929\u6c60\u5927\u7a74\uff0c\u4e00\u9762\u53e3\u4e2d\u559d\u9053\uff1a\u201c\u662f\u6211\u53c8\u600e\u6837\uff0c\u4e0d\u662f\u6211\u53c8\u600e\u6837\uff1f\u201d \u3000\u3000\u5353\u957f\u537f\u53f3\u81c2\u4e00\u7f29\uff0c\u751f\u50cf\u662f\u4e00\u5c3e\u6e38\u9c7c\u822c\u4ece\u4ed6\u4e24\u638c\u95ee\u7f29\u4e86\u51fa\u53bb\u3002\u53ea\u542c\u556a\u7684\u4e00\u58f0\uff0c\u9ec4\u886b\u5c11\u5e74\u5c91 \u7cb2\uff0c\u8e6c\u3001\u8e6c\u3001\u8e6c\u8fde\u9000\u4e09\u6b65\uff0c\u5353\u957f\u537f\u8eab\u5f62\u4e5f\u4e0d\u7981\u4e3a\u4e4b\u6643\u4e86\u6643\uff0c\u539f\u6765\u4ed6\u53f3\u81c2\u4e00\u7f29\uff0c\u4fbf\u5373\u5411\u90a3\u9ec4\u886b \u5c11\u5e74\u7684\u5de6\u624b\u624b\u80cc\u4e0a\u62cd\u53bb\uff0c\u90a3\u9ec4\u886b\u5c11\u5e74\u6765\u4e0d\u53ca\u53d8\u62db\uff0c\u53ea\u5f97\u624b\u8155\u4e00\u7ffb\uff0c\u7acb\u638c\u4e00\u626c\uff0c\u53cc\u638c\u76f8\u4ea4\uff0c\u7adf \u5404\u81ea\u5bf9\u4e86\u4e00\u638c\u3002 \u3000\u3000\u9ec4\u886b\u5c11\u5e74\u5c91\u7cb2\u5185\u529b\u5c31\u7a0d\u900a\u4e00\u7b79\uff0c\u7528\u7684\u53c8\u662f\u5de6\u638c\uff0c\u8fde\u8fde\u9000\u51fa\u4e09\u6b65\uff0c\u65b9\u81ea\u7acb\u7a33\u6869\uff0c\u9762\u8272\u4e00 \u53d8\uff0c\u65b9\u5f85\u5f00\u53e3\uff0c\u90a3\u5353\u957f\u537f\u53c8\u5389\u58f0\u559d\u9053\uff1a\u201c\u90a3\u4e48\u5feb\u5200\u4f1a\u548c\u7ea2\u4e2d\u4f1a\u7684\u6570\u767e\u4e2a\u5144\u5f1f\u7684\u60e8\u6b7b\uff0c\u4e5f\u662f\u4f60 \u4e00\u624b\u4e8e\u7684\u4e8b\u4e86\uff1f\u201d \u3000\u3000\u5c91\u7cb2\u9762\u8272\u53c8\u662f\u4e00\u53d8\uff0c\u4f3c\u4e4e\u6014\u4e86\u4e00\u6014\uff0c\u968f\u5373\u5927\u559d\u4e00\u58f0\uff0c\u548c\u8eab\u6251\u4e0a\uff0c\u53cc\u81c2\u4e00\u4f38\u4e00\u7f29\u4e4b\u95f4\uff0c\u5df2 \u81ea\u5411\u5353\u957f\u537f\u524d\u80f8\u3001\u53cc\u81c2\u62cd\u4e86\u4e09\u638c\uff0c\u4e00\u9762\u559d\u9053\uff1a\u201c\u662f\u6211\u6740\u7684\u53c8\u600e\u6837\u3002\u4e0d\u662f\u6211\u6740\u7684\u53c8\u600e\u6837\uff1f\u201d \u3000\u3000\u5353\u957f\u537f\u5389\u559d\u4e00\u58f0\uff1a\u201c\u5982\u6b64\u5c31\u597d\u3002\u201d \u3000\u3000\u773c\u770b\u8fd9\u9ec4\u886b\u5c11\u5e74\u7684\u53cc\u638c\uff0c\u5df2\u582a\u582a\u62cd\u5230\u4ed6\u8eab\u4e0a\uff0c\u7a81\u7136\u80f8\u8179\u4e00\u5438\uff0c\u4e0a\u8eab\u7adf\u500f\u7136\u9000\u540e\u534a\u5c3a\uff0c\u53cc \u811a\u5374\u4ecd\u50cf\u77f3\u6869\u4f3c\u7684\u9489\u5728\u5730\u4e0a\uff0c\u53ea\u542c\u53c8\u662f\u201c\u556a\u201d\u7684\u4e00\u58f0\uff0c\u5353\u957f\u537f\u53cc\u638c\u4e00\u626c\uff0c\u548c\u90a3\u9ec4\u886b\u5c11\u5e74\u53c8\u81ea \u5bf9\u4e86\u4e00\u638c\u3002 \u3000\u3000\u6b64\u523b\u4ed6\u5df2\u8ba4\u5b9a\u8fd9\u9ec4\u886b\u5c11\u5e74\u5c31\u662f\u6628\u591c\u7684\u51f6\u624b\uff0c\u5fc3\u4e2d\u4e0d\u7981\u5bf9\u90a3\u7edd\u8272\u5c11\u5973\u6709\u4e9b\u6b49\u759a\uff0c\u81ea\u5df1\u9519\u602a \u4e86\u4eba\u5bb6\uff0c\u662f\u4ee5\u5bf9\u8fd9\u9ec4\u886b\u5c11\u5e74\u4e5f\u5c31\u66f4\u4e3a\u6124\u6068\uff0c\u51fa\u624b\u4e4b\u95f4\uff0c\u7adf\u5c3d\u4e86\u5168\u529b\uff0c\u53cc\u638c\u76f8\u4ea4\u4e4b\u4e0b\uff0c\u90a3\u9ec4\u886b \u5c11\u5e74\u4fbf\u53c8\u5012\u9000\u4e00\u6b65\uff0c\u8eab\u5f62\u65b9\u81fc\u4e00\u6643\uff0c\u5353\u957f\u537f\u7684\u53cc\u638c\u4fbf\u53c8\u6f2b\u5929\u5411\u4ed6\u62cd\u4e86\u4e0b\u672a\uff0c\u638c\u98ce\u547c\u547c\uff0c\u51cc\u5389 \u5f02\u5e38\u3002 \u3000\u3000\u5c91\u7cb2\u65b9\u624d\u548c\u4ed6\u5bf9\u4e86\u4e00\u638c\uff0c\u5fc3\u77e5\u4eba\u5bb6\u7684\u638c\u529b\u5728\u81ea\u5df1\u4e4b\u4e0a\uff0c\u6b64\u523b\u638c\u6cd5\u65bd\u5c55\u5f00\u6765\uff0c\u4fbf\u4e0d\u6562\u8d70 \u5288\u3001\u649e\u3001\u5c01\u3001\u6253\u3001\u780d\u3001\u63a8\u7b49\u521a\u731b\u7684\u8def\u5b50\uff0c\u53ea\u662f\u5230\u5904\u6e38\u8d70\uff0c\u907f\u5f00\u5353\u957f\u537f\u7684\u6b63\u950b\uff0c\u4e13\u4ee5\u95ea\u5c55\u817e \u6930\u3001\u7075\u5de7\u7684\u62db\u5f0f\u53d6\u80dc\uff0c\u4ed6\u8eab\u6cd5\u672c\u662f\u4ee5\u8f7b\u7075\u89c1\u957f\uff0c\u6b64\u523b\u8eab\u624b\u4e00\u5c55\u5f00\u6765\uff0c\u53ea\u89c1\u5353\u957f\u537f\u8eab\u524d\u8eab\u540e\uff0c \u8eab\u5de6\u8eab\u53f3\uff0c\u56db\u9762\u516b\u65b9\u90fd\u662f\u4ed6\u7684\u5f71\u5b50\uff0c\u4f46\u6bcf\u4e00\u51fa\u624b\uff0c\u4fbf\u65e0\u4e00\u4e0d\u662f\u51fb\u5411\u5353\u957f\u537f\u8eab\u4e0a\u7684\u8981\u7a74\uff0c\u8ba4\u7a74 \u4e4b\u7a33\u3001\u51c6\u3001\u72e0\u8fa3\uff0c\u7aef\u7684\u60ca\u4eba\u65e0\u6bd4\u3002 \u3000\u3000\u65b9\u624d\u5728\u57ce\u5934\u4e0a\u4e4b\u5353\u957f\u537f\u5df2\u548c\u4ed6\u52a8\u4e86\u6b21\u624b\uff0c\u65e9\u5c31\u77e5\u9053\u8fd9\u5c11\u5e74\u6b66\u529f\u4e0d\u5f31\uff0c\u4f46\u57ce\u5934\u4e0a\u9762\u7a76\u7adf\u5927 \u5c0f\uff0c\u4e24\u4eba\u7684\u8eab\u624b\u90fd\u672a\u65bd\u5c55\u5f00\uff0c\u6b64\u523b\u4ed6\u89c1\u8fd9\u5c11\u5e74\u8f7b\u529f\u7adf\u5982\u6b64\u4e4b\u5999\uff0c\u5fc3\u4e2d\u4e5f\u4e0d\u7981\u4e3a\u4e4b\u6697\u60ca\uff0c\u8d8a\u53d1 \u8ba4\u5b9a\u90a3\u5feb\u5200\u4f1a\u548c\u7ea2\u5dfe\u4f1a\u4e2d\u5f1f\u5b50\u4e4b\u60e8\u6b7b\uff0c\u5fc5\u662f\u8fd9\u5c11\u5e74\u5e72\u51fa\u7684\u4e8b\uff0c\u53ea\u662f\u4e24\u4eba\u6b66\u529f\u76f8\u5dee\u5e76\u4e0d\u8fdc\uff0c\u4e00 \u65f6\u4e4b\u95f4\uff0c\u4ed6\u4e5f\u672a\u80fd\u5c31\u5c06\u8fd9\u9ec4\u886b\u5c11\u5e74\u4f24\u5728\u81ea\u5df1\u638c\u4e0b\u3002 \u3000\u3000\u4e24\u4eba\u65b9\u81ea\u8fc7\u4e86\u6570\u5341\u62db\uff0c\u54ea\u77e5\u8fdc\u5904\u7a81\u7136\u98d8\u6765\u4e00\u9635\u9635\u60a0\u626c\u7684\u4e50\u58f0\uff0c\u4ed6\u4eec\u52a8\u624b\u6b63\u6025\uff0c\u5148\u524d\u4e95\u672a \u5728\u610f\uff0c\u4f46\u90a3\u4e50\u58f0\u5374\u8d8a\u6765\u8d8a\u8fd1\uff0c\u800c\u4e14\u58f0\u97f3\u6781\u4e3a\u5947\u7279\uff0c\u65e2\u975e\u5f04\u8427\uff0c\u4ea6\u975e\u5439\u7b1b\uff0c\u4e5f\u4e0d\u662f\u5578\u7b06\u7ba1\u5f26\u4e4b \u58f0\uff0c\u53ea\u542c\u8fd9\u4e50\u58f0\u5c16\u7ec6\u9ad8\u4ea2\uff0c\u5374\u53c8\u6781\u4e3a\u7f8e\u5999\u52a8\u542c\uff0c\u4e24\u4eba\u5fc3\u4e2d\u5927\u5f02\uff0c\u90e8\u4e0d\u77e5\u8fd9\u4e50\u58f0\u662f\u4ec0\u4e48\u4e50\u5668\u594f \u51fa\u7684\u3002 \u3000\u3000\u53c8\u5f53\u9ad8\u624b\u8fc7\u62db\uff0c\u5fc3\u795e\u4e00\u4e1d\u90fd\u677e\u61c8\u4e0d\u5f97\uff0c\u4e24\u4eba\u5fc3\u4e2d\u867d\u7136\u5947\u602a\uff0c\u5374\u8c01\u4e5f\u4e0d\u5411\u4e50\u58f0\u4f20\u6765\u4e4b\u5904\u53bb \u671b\u4e00\u773c\uff0c\u54ea\u77e5\u53c8\u62fc\u4e86\u5341\u6570\u62db\uff0c\u4e50\u58f0\u7adf\u7a81\u7136\u4e00\u987f\uff0c\u4e00\u4e2a\u5a07\u67d4\u7684\u58f0\u97f3\u559d\u9053\uff1a\u201c\u662f\u8c01\u6562\u5728\u8fd9\u91cc\u52a8 \u624b\uff0c\u8fd8\u4e0d\u5feb\u505c\u4f4f\uff0c\u4f60\u4eec\u6709\u51e0\u4e2a\u8111\u888b\uff0c\u80c6\u6562\u60ca\u52a8\u5a18\u5a18\u7684\u51e4\u9a7e\u3002\u201d \u3000\u3000\u58f0\u97f3\u867d\u7136\u5a07\u67d4\uff0c\u4f46\u5374\u4e00\u5b57\u4e00\u53e5\uff0c\u6e05\u6670\u65e0\u6bd4\uff0c\u5353\u957f\u537f\u548c\u5c91\u7cb2\u542c\u5728\u8033\u91cc\uff0c\u5fc3\u4e2d\u90fd\u4e0d\u7981\u4e00\u52a8\uff0c \u6697\u6697\u5fd6\u9053\uff1a\u201c\u5a18\u5a18\u7684\u51e4\u9a7e\uff0c\u8be5\u4e0d\u662f\u7687\u5e1d\u5a18\u5a18\u524d\u6765\u51fa\u5de1\uff0c\u8fd9\u5012\u51b2\u649e\u4e0d\u5f97\u3002\u201d \u3000\u3000\u4e24\u4eba\u540c\u4e00\u5fc3\u5ff5\uff0c\u5404\u81ea\u5927\u559d\u4e00\u58f0\uff0c\u9000\u5f00\u4e94\u6b65\uff0c\u8f6c\u76ee\u671b\u53bb\uff0c\u53ea\u89c1\u4e00\u884c\u7a7f\u7740\u8f7b\u7ea2\u7f57\u886b\u7684\u5c11\u5973\uff0c \u8885\u5a1c\u884c\u6765\uff0c\u624b\u91cc\u5404\u81ea\u62ff\u7740\u4e00\u6bb5\u9752\u8272\u7684\u7af9\u5b50\uff0c\u4f46\u7af9\u5b50\u5374\u6709\u957f\u6709\u77ed\uff0c\u4e5f\u6ca1\u6709\u97f3\u5b54\uff0c\u4e24\u4eba\u65b9\u624d\u867d\u662f \u52a8\u624b\u62fc\u547d\uff0c\u4f46\u6b64\u523b\u5374\u4e0d\u7981\u5bf9\u671b\u4e00\u773c\uff0c\u6697\u5fd6\u9053\uff1a\u201c\u8fd9\u53c8\u662f\u4ec0\u4e48\u4e1c\u897f\uff0c\u600e\u4e48\u5439\u594f\u5f97\u51fa\u6765\u90a3\u4e48\u597d\u542c \u7684\u4e50\u58f0\uff1f\u201d \u3000\u3000\u539f\u6765\u4e24\u4eba\u90fd\u662f\u521d\u5165\u6c5f\u6e56\uff0c\u8db3\u8ff9\u53c8\u672a\u79bb\u5f00\u8fc7\u4e2d\u5dde\uff0c\u5374\u4e0d\u77e5\u9053\u8fd9\u4e9b\u5c11\u5973\u624b\u4e2d\u6240\u6301\u7684\u201c\u4e50\u5668\u201d \u867d\u662f\u4e00\u6bb5\u666e\u901a\u7684\u7af9\u5b50\uff0c\u4f46\u5f7c\u6b64\u957f\u77ed\u4e0d\u4e00\uff0c\u5439\u594f\u8d77\u6765\u5b98\u5546\u81ea\u4e5f\u5404\u5f02\uff0c\u518d\u52a0\u4e0a\u5979\u4eec\u4e45\u5c45\u82d7\u7586\uff0c\u90fd \u5f97\u8bf7\u82d7\u4eba\u7684\u5439\u7af9\u4e4b\u6280\uff0c\u53c8\u90fd\u4e45\u7ecf\u8bad\u7ec3\uff0c\u5f7c\u6b64\u914d\u5408\u5f97\u6781\u4e3a\u548c\u8c10\uff0c\u5439\u51fa\u4e50\u58f0\u6765\uff0c\u81ea\u7136\u662f\u6781\u4e3a\u5947\u7279 \u800c\u7f8e\u5999\u7684\u4e86\u3002 \u3000\u3000\u4e24\u4eba\u9762\u9762\u76f8\u89d1\uff0c\u90a3\u9ec4\u886b\u5c11\u5e74\u7a81\u7136\u4e24\u773c\u4e00\u7ffb\uff0c\u5634\u89d2\u671d\u4e0b\u4e00\u64a9\uff0c\u4f5c\u4e86\u4e2a\u8f7b\u8511\u7684\u795e\u8272\uff0c\u8f6c\u8fc7\u5934 \u53bb\uff0c\u518d\u4e5f\u4e0d\u671b\u5353\u957f\u537f\u4e00\u773c\u3002 \u3000\u3000\u5353\u957f\u537f\u5fae\u5fae\u4e00\u6014\uff0c\u5fc3\u4e2d\u4e0d\u77e5\u662f\u7b11\u662f\u6012\uff0c\u4ea6\u81ea\u8f6c\u5934\u53bb\uff0c\u5374\u89c1\u8fd9\u4e9b\u624b\u6301\u9752\u7af9\u7684\u7ea2\u88f3\u5c11\u5973\u4e4b \u540e\uff0c\u7adf\u662f\u4e00\u8f86\u9999\u8f66\uff0c\u5b9d\u76d6\u6d41\u82cf\uff0c\u6402\u51e4\u96d5\u9f99\uff0c\u886c\u7740\u8f66\u4e0a\u7684\u9c9c\u8840\u7f0e\u57ab\uff0c\u66f4\u663e\u5f97\u90c1\u4e3d\u534e\u8d35\uff0c\u4e0d\u53ef\u65b9 \u7269\u3002 \u3000\u3000\u8f66\u884c\u6781\u7f13\uff0c\u8f66\u8f95\u4e24\u4fa7\uff0c\u5374\u6709\u56db\u4e2a\u7ea2\u88f3\u5c11\u5973\uff0c\u4e00\u624b\u63a8\u7740\u8f66\u5b50\uff0c\u53e6\u4e00\u624b\u5c06\u624b\u4e2d\u6240\u6301\u7684\u9e45\u6bdb\u7fbd \u6247\uff0c\u5411\u8f66\u4e0a\u8f7b\u8f7b\u6247\u52a8\u3002 \u3000\u3000\u8fd9\u4e9b\u7ea2\u88f3\u5c11\u5973\u770b\u5230\u5353\u957f\u537f\u548c\u5c91\u7cb2\u6115\u6115\u5730\u7ad9\u5728\u65c1\u8fb9\uff0c\u4e00\u4e2a\u4e2a\u9762\u4e0a\u90fd\u9732\u51fa\u7b11\u610f\uff0c\u4f46\u5374\u6ca1\u6709\u4e00 \u4eba\u6562\u7b11\u51fa\u58f0\u6765\uff0c\u8f7b\u62c8\u7389\u624b\uff0c\u53c8\u5c06\u624b\u4e2d\u7684\u9752\u7af9\u653e\u5230\u5507\u8fb9\uff0c\u64ae\u53e3\u800c\u5439\uff0c\u7728\u773c\u4e4b\u95f4\u4e50\u58f0\u53c8\u590d\u5927\u4f5c\uff0c \u8fd9\u4e9b\u7ea2\u88f3\u5c11\u5973\u65b9\u81ea\u7f13\u7f13\u524d\u884c\uff0c\u6570\u5341\u53cc\u5a9a\u76ee\u5374\u6709\u610f\u65e0\u610f\u95f4\u5411\u5353\u957f\u537f\u548c\u90a3\u9ec4\u886b\u5c11\u5e74\u5c91\u7cb2\u779f\u4e0a\u4e00 \u773c\u3002 \u3000\u3000\u90a3\u5c91\u7cb2\u98de\u626c\u67b6\u5028\uff0c\u5e73\u65e5\u81ea\u547d\u501c\u60a6\u98ce\u6d41\uff0c\u4f46\u6b64\u523b\u4e0d\u77e5\u600e\u4e48\uff0c\u7adf\u4f3c\u4e3a\u8fd9\u79cd\u6c14\u6d3e\u6240\u6151\uff0c\u4e24\u53ea\u773c \u775b\u5374\u662f\u7728\u4e5f\u4e0d\u7728\u5730\u671b\u5728\u8fd9\u4e9b\u5c11\u5973\u8eab\u4e0a\uff0c\u4f46\u5374\u4e0d\u6562\u9732\u51fa\u4e00\u4e9b\u8f7b\u8584\u4e4b\u610f\uff0c\u90a3\u5353\u957f\u537f\u751f\u6027\u575a\u6bc5\u65b9 \u6b63\uff0c\u66f4\u662f\u8fde\u671b\u4e5f\u4e0d\u671b\u4e00\u773c\uff0c\u773c\u89c2\u9f3b\u3001\u9f3b\u89c2\u5fc3\u5730\u7ad9\u5728\u8def\u65c1\uff0c\u4f46\u5fc3\u91cc\u5374\u81ea\u6697\u6697\u731c\u6d4b\uff0c\u4e0d\u77e5\u8fd9\u4e9b\u5c11 \u5973\u7a76\u7adf\u662f\u4f55\u8def\u9053\u3002 \u3000\u3000\u7247\u523b\u4e4b\u95f4\uff0c\u8fd9\u884c\u5947\u5f02\u7684\u884c\u5217\uff0c\u4fbf\u7f13\u7f13\u5728\u4ed6\u4eec\u8eab\u524d\u884c\u8fc7\u2026\u2026 \u3000\u3000\u5353\u957f\u537f\u6b63\u81ea\u731c\u7591\uff0c\u5fc3\u4e2d\u5ffd\u7136\u95ea\u7535\u822c\u63a0\u8fc7\u4e00\u4e2a\u5ff5\u5934\uff0c\u53c8\u81ea\u4e3e\u76ee\u671b\u53bb\uff0c\u53ea\u89c1\u90a3\u8f86\u9999\u8f66\u4e4b\u4e0a\uff0c \u5750\u7740\u7684\u7adf\u662f\u4e00\u4e2a\u5168\u8eab\u7ea2\u8863\u7684\u8001\u5987\uff0c\u5979\u90a3\u67af\u7626\u7684\u8eab\u8eaf\uff0c\u6df1\u6df1\u57cb\u5728\u90a3\u5806\u67d4\u8f6f\u7684\u7f0e\u57ab\u4e4b\u4e2d\uff0c\u8863\u886b\u9c9c \u7ea2\uff0c\u7f0e\u57ab\u4ea6\u662f\u9c9c\u7ea2\uff0c\u662f\u4ee5\u8fdc\u8fdc\u671b\u53bb\uff0c\u7adf\u5206\u8fa8\u4e0d\u51fa\u8fd9\u8001\u5987\u7684\u8eab\u5f62\u6765\u3002 \u3000\u3000\u90a3\u56db\u4e2a\u7f13\u63a8\u9999\u8f66\u3001\u8f7b\u6447\u7fbd\u6247\u7684\u7ea2\u88f3\u5c11\u5973\uff0c\u516b\u9053\u79cb\u6ce2\uff0c\u4e5f\u671b\u5728\u8fd9\u4e24\u4e2a\u5c11\u5e74\u8eab\u4e0a\uff0c\u4f46\u811a\u6b65\u672a \u505c\uff0c\u5f84\u81ea\u5c06\u9999\u8f66\u63a8\u8fc7\u3002 \u3000\u3000\u8fd9\u56db\u4e2a\u5c11\u5973\u4eff\u4f5b\u6bd4\u524d\u9762\u5439\u7af9\u7684\u5c11\u5973\u90fd\u8f83\u4e3a\u5927\u4e9b\uff0c\u671b\u53bb\u66f4\u662f\u82b1\u5bb9\u7389\u8c8c\uff0c\u98ce\u59ff\u7ef0\u7ea6\uff0c\u90a3\u79cd\u6210 \u719f\u5c11\u5973\u7684\u98ce\u97f5\uff0c\u4efb\u4f55\u5c11\u5e74\u89c1\u4e86\u90fd\u4f1a\u5fc3\u52a8\u3002 \u3000\u3000\u4f46\u5353\u957f\u537f\u7684\u76ee\u5149\uff0c\u5374\u8d8a\u8fc7\u8fd9\u4e9b\u5c11\u5973\u5a07\u7f8e\u5982\u82b1\u7684\u9762\u5e9e\uff0c\u505c\u7559\u5728\u90a3\u67af\u7626\u7684\u7ea2\u886b\u8001\u5987\u8eab\u4e0a\u3002 \u3000\u3000\u8fd9\u8001\u5987\u4e0d\u4f46\u901a\u4f53\u7ea2\u886b\uff0c\u5934\u4e0a\u7ade\u4e5f\u68b3\u7740\u5f53\u4eca\u95fa\u4e2d\u5c11\u5973\u6700\u4e3a\u6574\u884c\u7684\u5760\u9a6c\u53d1\uff0c\u4e91\u9b13\u5982\u96fe\uff0c\u659c\u659c \u633d\u8d77\uff0c\u4ecd\u7136\u6f06\u9ed1\u7684\u5934\u53d1\u4e0a\uff0c\u7f00\u7740\u4e86\u73e0\u4f69\u91d1\u73af\uff0c\u5728\u65e5\u5149\u4e4b\u4e2d\uff0c\u95ea\u95ea\u751f\u5149\u3002 \u3000\u3000\u4f46\u5728\u8fd9\u7f8e\u4e3d\u7684\u5934\u53d1\u4e0b\u9762\uff0c\u5374\u662f\u4e00\u5f20\u5176\u4e11\u65e0\u6bd4\u7684\u9762\u5bb9\uff0c\u6b63\u81ea\u95ed\u7740\u53cc\u76ee\uff0c\u6709\u6c14\u65e0\u529b\u5730\u517b\u7740 \u795e\uff0c\u90a3\u79cd\u8870\u8001\u7684\u6837\u5b50\uff0c\u548c\u5979\u8eab\u4e0a\u7684\u8863\u886b\uff0c\u5934\u4e0a\u7684\u53d1\u5f0f\uff0c\u5f62\u6210\u4e00\u79cd\u4e11\u6076\u800c\u53ef\u7b11\u7684\u5bf9\u6bd4\u3002 \u3000\u3000\u5353\u957f\u537f\u6115\u6115\u5730\u601d\u7d22\u534a\u664c\uff0c\u8fd9\u8f86\u9999\u8f66\u5df2\u7f13\u7f13\u7531\u4ed6\u8eab\u524d\u63a8\u4e86\u8fc7\u53bb\uff0c\u5c91\u7cb2\u7684\u76ee\u5149\uff0c\u4e5f\u8fd8\u7559\u604b\u7684 \u671b\u5728\u90a3\u4e9b\u7ea2\u88f3\u5c11\u5973\u7684\u80cc\u5f71\u4e0a\uff0c\u9635\u9635\u6e05\u51e4\uff0c\u5439\u5f97\u5979\u4eec\u8eab\u4e0a\u7684\u8863\u886b\u5fae\u5fae\u98d8\u52a8\uff0c\u548c\u5728\u5730\u4e0a\u7684\u4e00\u7247\u7fe0 \u7eff\uff0c\u6620\u5f71\u6210\u4e00\u5e45\u7edd\u7f8e\u7684\u56fe\u753b\u3002 \u3000\u3000\u5c91\u7cb2\u56de\u8fc7\u5934\u6765\uff0c\u51b7\u7b11\u4e00\u58f0\uff0c\u53c8\u7f13\u7f13\u5411\u5353\u957f\u537f\u884c\u53bb\uff0c\u54ea\u77e5\u5353\u957f\u537f\u7a81\u7136\u5927\u559d\u4e00\u58f0\uff1a\u201c\u7ad9 \u4f4f\u3002\u201d \u3000\u3000\u58f0\u5982\u9739\u96f3\uff0c\u5165\u8033\u9abc\u7136\uff0c\u5c91\u7cb2\u4e0d\u7981\u4e3a\u4e4b\u4e00\u60ca\uff0c\u5374\u89c1\u4ed6\u559d\u58f0\u65b9\u4f4f\uff0c\u8eab\u5f62\u5df2\u5982\u82cd\u9e70\u822c\u5730\u5411\u90a3\u8f86 \u9999\u8f66\u63a0\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u90a3\u4e9b\u7ea2\u88f3\u5c11\u5973\u4e00\u8d77\u60ca\u8bb6\u5730\u56de\u8fc7\u5934\uff0c\u5439\u7af9\u7684\u505c\u4e86\u5439\u7af9\uff0c\u6447\u6247\u7684\u505c\u4e86\u6447\u6247\uff0c\u5c91\u7cb2\u6697\u5fd6\uff1a\u201c\u8fd9 \u53ae\u53c8\u5728\u73a9\u4ec0\u4e48\u82b1\u6837\uff1f\u201d \u3000\u3000\u53cc\u8db3\u4e00\u987f\uff0c\u4ea6\u81ea\u5982\u98de\u8ddf\u4e86\u8fc7\u53bb\uff0c\u5374\u89c1\u5353\u957f\u537f\u5df2\u62e6\u5728\u8f66\u524d\uff0c\u53cc\u76ee\u51db\u7136\u53d1\u7740\u5bd2\u5149\uff0c\u671b\u7740\u90a3\u8f66 \u4e0a\u7684\u7ea2\u886b\u8001\u5987\u3002 \u3000\u3000\u4ed6\u751f\u6027\u65b9\u6b63\uff0c\u76ee\u4e0d\u90aa\u89c6\uff0c\u89c1\u5230\u8fd9\u884c\u5c11\u5973\u4e00\u4e2a\u4e2a\u9762\u76ee\u5982\u82b1\uff0c\u79cb\u6ce2\u5982\u6c34\uff0c\u800c\u4e14\u90fd\u503c\u5999\u9f84\uff0c\u4fbf \u4e0d\u6562\u53bb\u671b\u4eba\u5bb6\uff0c\u4f46\u5fc3\u4e2d\u5374\u6697\u5fd6\u9053\uff1a\u201c\u8fd9\u4e9b\u5c11\u5973\u600e\u4e48\u90fd\u7a7f\u7740\u7ea2\u886b\uff1f\u201d \u3000\u3000\u4fbf\u4e3e\u76ee\u671b\u53bb\uff0c\u53c8\u89c1\u5230\u8f66\u4e0a\u7684\u8001\u5987\u90a3\u79cd\u8be1\u5f02\u7684\u88c5\u675f\uff0c\u5ffd\u7136\u60f3\u8d77\u5341\u5e74\u4e4b\u524d\u5728\u5929\u76ee\u5c71\u4e0b\u7684\u5947\u4e11 \u5987\u4eba\u6765\uff0c\u5fc3\u4e2d\u4e0d\u7981\u53c8\u4e00\u52a8\uff1a\u201c\u96be\u9053\u5979\u5c31\u662f\u4e11\u4eba\u6e29\u5982\u7389\uff1f\u201d \u3000\u3000\u4f46\u773c\u524d\u8fd9\u7ea2\u886b\u8001\u5987\u5374\u82cd\u8001\u5f97\u5f88\uff0c\u4eff\u4f5b\u5e74\u5df2\u53e4\u7a00\uff0c\u4ed6\u4e0d\u7981\u6709\u4e9b\u6000\u7591\u3002 \u3000\u3000\u201c\u5341\u5e74\u65f6\u65e5\u867d\u957f\uff0c\u4f46\u4e11\u4eba\u6e29\u5982\u7389\u5185\u529f\u6df1\u6e5b\uff0c\u4e0d\u8be5\u82cd\u8001\u5f97\u5982\u6b64\u6a21\u6837\u5440\uff1f\u201d \u3000\u3000\u72b9\u7591\u534a\u664c\uff0c\u5ffd\u7136\u60f3\u5230\u65b9\u624d\u90a3\u5a07\u67d4\u7684\u58f0\u97f3\u558a\u201c\u2026\u2026\u5a18\u5a18\u7684\u98ce\u9a7e\u2026\u2026\u201d\uff0c\u6e29\u5982\u7389\u4e0d\u662f\u4e5f\u53eb\u7ea2 \u8863\u5a18\u5a18\u5417\uff1f \u3000\u3000\u4ed6\u518d\u65e0\u7591\u5ff5\uff0c\u5927\u559d\u4e00\u58f0\uff0c\u8eab\u5f62\u66b4\u8d77\uff0c\u6321\u5728\u8fd9\u8f86\u9999\u8f66\u524d\u9762\uff0c\u4fbf\u53c8\u559d\u9053\uff1a\u201c\u9601\u4e0b\u53ef\u662f\u59d3 \u3000\u3000\u6e29\uff1f\u201d \u3000\u3000\u8d70\u5728\u6700\u524d\u7684\u4e24\u4e2a\u7ea2\u88f3\u5c11\u5973\uff0c\u6b64\u523b\u7a81\u7136\u4e00\u8d77\u6298\u4e86\u56de\u6765\uff0c\u7ea4\u8170\u5fae\u62e7\uff0c\u4fbf\u81ea\u4e00\u8fb9\u4e00\u4e2a\uff0c\u7ad9\u5728\u5353 \u957f\u537f\u8eab\u65c1\uff0c\u5404\u81ea\u4f38\u51fa\u4e00\u53ea\u7ea4\u638c\u6765\uff0c\u62cd\u5411\u5353\u957f\u537f\u7684\u80a9\u4e0a\uff0c\u53e6\u4e00\u53ea\u624b\u62ff\u7740\u7684\u9752\u7af9\uff0c\u7535\u5149\u4e5f\u4f3c\u7684\u70b9 \u5411\u4ed6\u53cc\u4e73\u4e0a\u4e00\u5bf8\u516d\u5206\u5904\u7684\u81ba\u7a97\u5927\u7a74\uff0c\u53e3\u4e2d\u5374\u5a07\u58f0\u7b11\u9053\uff1a\u201c\u5a18\u5a18\u7761\u7740\u4e86\uff0c\u4f60\u4e71\u53eb\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5353\u957f\u537f\u53e3\u4e2d\u95f7\u54fc\u4e00\u58f0\uff0c\u53cc\u81c2\u4e00\u632f\uff0c\u90a3\u4e24\u4e2a\u5c11\u5973\u4fbf\u5df2\u62b5\u53d7\u4e0d\u4f4f\uff0c\u5411\u540e\u8fde\u9000\u4e09\u6b65\uff0c\u65b9\u624d\u7ad9 \u4f4f\uff0c\u82b1\u5bb9\u5374\u5df2\u53d8\u4e86\u989c\u8272\u3002 \u3000\u3000\u4f46\u90a3\u8f66\u4e0a\u7684\u8001\u5987\uff0c\u5374\u4ecd\u52a8\u4e5f\u4e0d\u52a8\uff0c\u5353\u957f\u537f\u51b7\u54fc\u4e00\u58f0\uff0c\u8de8\u524d\u534a\u6b65\uff0c\u53cc\u81c2\u659c\u659c\u5212\u4e86\u534a\u5708\uff0c\u7a81 \u7136\u7535\u4e5f\u4f3c\u4ed6\u5f53\u80f8\u63a8\u51fa\uff0c\u53e3\u4e2d\u559d\u9053\uff1a\u201c\u59d3\u6e29\u7684\uff0c\u5341\u5e74\u4e4b\u524d\uff0c\u59cb\u4fe1\u5cf0\u4e0b\u7684\u4e8b\u4f60\u5fd8\u4e86\u5417\uff1f\u201d \u3000\u3000\u638c\u98ce\u864e\u864e\uff0c\u4f59\u950b\u6240\u53ca\uff0c\u7acb\u5728\u8f66\u8f95\u65c1\u7684\u7ea2\u88f3\u5c11\u5973\u8eab\u4e0a\uff0c\u7adf\u90fd\u4e0d\u89c9\u6cdb\u51fa\u4e00\u9635\u5bd2\u610f\uff0c\u8eab\u4e0a\u7684\u8863 \u886b\u4e5f\u88ab\u9707\u5f97\u98de\u626c\u4e86\u8d77\u6765\u3002 \u3000\u3000\u90a3\u7ea2\u88f3\u8001\u5987\u53cc\u76ee\u4ecd\u672a\u5f20\uff0c\u8eab\u5f62\u4ea6\u672a\u52a8\uff0c\u4f46\u4e00\u53cc\u672c\u5df2\u843d\u5728\u7f0e\u57ab\u4e0a\u7684\u957f\u8896\uff0c\u5374\u201c\u547c\u201d\u7684\u4e00 \u58f0\uff0c\u53cd\u5377\u4e86\u8d77\u6765\uff0c\u50cf\u662f\u957f\u4e86\u773c\u775b\u4f3c\u7684\u5377\u5411\u5353\u957f\u537f\u7684\u53cc\u638c\u3002 \u3000\u3000\u5353\u957f\u537f\u5927\u559d\u4e00\u58f0\uff0c\u53cc\u638c\u4e00\u7ffb\uff0c\u4e0d\u907f\u53cd\u8fce\uff0c\u4e94\u6307\u7b95\u5f20\uff0c\u7535\u4e5f\u4f3c\u7684\u6293\u5411\u90a3\u4e24\u6761\u957f\u8896\u3002 \u3000\u3000\u4ed6\u53cc\u624b\u8fd9\u4e00\u7ffb\uff0c\u4e00\u6293\uff0c\u770b\u4f3c\u4e4e\u5e73\u6de1\u65e0\u5947\uff0c\u5176\u5b9e\u5374\u7094\u5982\u5954\u7535\uff0c\u52b2\u900f\u6307\u7aef\uff0c\u6b63\u662f\u6dee\u5357\u9e70\u722a\u95e8 \u4e2d\u767b\u5cf0\u9020\u6781\u7684\u624b\u6cd5\uff0c\u5c31\u7b97\u6dee\u5357\u9e70\u722a\u95e8\u5f53\u4eca\u7684\u638c\u95e8\u4eba\u4eb2\u81ea\u4f7f\u51fa\u8fd9\u62db\u6765\uff0c\u4e5f\u672a\u5fc5\u80fd\u5f3a\u80dc\u4e8e\u4ed6\uff0c\u65b9 \u624d\u5728\u57ce\u579b\u4e0a\uff0c\u4ed6\u4fbf\u4ee5\u8fd9\u540c\u6837\u7684\u624b\u6cd5\u6495\u843d\u4e86\u90a3\u7edd\u8272\u5c11\u5973\u7684\u4e00\u53cc\u7f57\u8896\u3002 \u3000\u3000\u6b64\u523b\u4ed6\u7acb\u5728\u5730\u4e0a\uff0c\u53c8\u662f\u5168\u529b\u800c\u53d1\uff0c\u52b2\u529b\u66f4\u4f55\u6b62\u6bd4\u65b9\u624d\u5f3a\u4e86\u4e00\u500d\uff0c\u539f\u60f3\u53ea\u4e00\u62db\u5c31\u8981\u5c06\u8fd9\u8001 \u53d1\u7684\u957f\u8896\u626f\u843d\u3002\u54ea\u77e5\u8fd9\u53cc\u957f\u8896\u7adf\u751f\u50cf\u662f\u957f\u4e86\u773c\u775b\uff0c\u7a81\u7136\u4e00\u4f38\u4e00\u7f29\uff0c\u7adf\u81ea\u4ece\u4ed6\u53cc\u638c\u4e2d\u7a7f\u4e86\u8fc7 \u53bb\uff0c\u8896\u811a\u7b14\u76f4\u5730\u626b\u5411\u4ed6\u80f8\u524d\u7684\u4e73\u6cc9\u7a74\u4e0a\u3002 \u3000\u3000\u5353\u957f\u537f\u5fc3\u5934\u4e00\u51db\uff0c\u62e7\u8eab\u9519\u6b65\uff0c\u5237\u5730\u5411\u540e\u9000\u51fa\u4e00\u6b65\uff0c\u5374\u89c1\u90a3\u8001\u5987\u51b7\u7b11\u4e00\u58f0\u9053\uff1a\u201c\u4f60\u4eec\u8fd8\u4e0d \u7ed9\u6211\u628a\u8fd9\u5c0f\u5b50\u62ff\u4e0b\u6765\u3002\u201d \u3000\u3000\u957f\u8896\u4e00\u7f29\uff0c\u53c8\u81ea\u843d\u5728\u57ab\u4e0a\uff0c\u7acb\u5728\u8f66\u8f95\u4e24\u4fa7\u7684\u5c11\u5973\uff0c\u5374\u7a81\u7136\u63a0\u5411\u5353\u957f\u537f\uff0c\u56db\u67c4\u7ebf\u81ea\u7684\u7fbd \u6247\uff0c\u5206\u505a\u56db\u5904\uff0c\u5374\u5728\u540c\u4e00\u5239\u90a3\u95f4\u5411\u4ed6\u62cd\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u5353\u957f\u537f\u53cc\u76ee\u5df2\u8d64\uff0c\u56e0\u4e3a\u4ed6\u77e5\u9053\u81ea\u5df1\u4e0d\u5171\u6234\u5929\u7684\u4ec7\u4eba\uff0c\u6b64\u523b\u6b63\u597d\u6574\u4ee5\u6687\u5730\u5750\u5728\u81ea\u5df1\u9762\u524d\uff0c \u5341\u5e74\u90c1\u79ef\u5728\u5fc3\u4e2d\u7684\u4ec7\u6068\uff0c\u6b64\u523b\u4fbf\u50cf\u5c71\u6d2a\u4f3c\u5730\u7206\u53d1\u4e86\u51fa\u6765\uff0c\u4ee5\u81c2\u4e00\u5708\uff0c\u5df2\u5728\u8fd9\u56db\u4e2a\u624b\u6301\u7fbd\u6247\u7684 \u7ea2\u88f3\u5c11\u5973\u7684\u56db\u53ea\u7389\u8155\u4e4b\u4e0a\uff0c\u5404\u4e2a\u5212\u51fa\u4e00\u638c\u3002 \u3000\u3000\u56db\u4e2a\u7ea2\u88f3\u5c11\u5973\u4e07\u4e07\u60f3\u4e0d\u5230\u8fd9\u5c11\u5e74\u62db\u5f0f\u7ade\u662f\u5982\u6b64\u4e4b\u5feb\uff0c\u7389\u8155\u4e00\u7f29\uff0c\u5404\u81ea\u540e\u9000\u4e00\u6b65\u3002 \u3000\u3000\u5353\u957f\u537f\u5927\u559d\u4e00\u58f0\uff0c\u5e76\u4e0d\u8ffd\u51fb\uff0c\u5374\u53c8\u5411\u8f66\u4e0a\u7684\u8001\u5987\u6251\u4e86\u8fc7\u7384\u3002 \u3000\u3000\u54ea\u77e5\u4ed6\u8eab\u5f62\u624d\u5c55\uff0c\u5df2\u6709\u4e94\u6839\u9752\u7af9\u5e76\u6392\u5411\u4ed6\u70b9\u4e86\u8fc7\u53bb\uff0c\u5f53\u4e2d\u4e09\u6839\u70b9\u5411\u4ed6\u524d\u80f8\u534e\u76d6\u7487\u6781\u4e09\u5904 \u8981\u7a74\uff0c\u65c1\u8fb9\u4e24\u6839\u51fa\u624b\u7684\u90e8\u4f4d\u66f4\u662f\u5201\u94bb\uff0c\u867d\u662f\u843d\u7a7a\u800c\u51fa\uff0c\u5374\u751f\u50cf\u662f\u7b49\u7740\u4ed6\u8eab\u5b50\u81ea\u5df1\u9001\u4e0a\u53bb\u4f3c \u7684\u3002 \u3000\u3000\u5353\u957f\u537f\u563f\u563f\u51b7\u7b11\u4e00\u58f0\uff0c\u6839\u672c\u672a\u5c06\u8fd9\u4e94\u6839\u9752\u7af9\u653e\u5728\u5fc3\u4e0a\uff0c\u53cc\u638c\u4e00\u626c\uff0c\u53c8\u662f\u201c\u547c\u201d\u7684\u4e00\u58f0\uff0c \u9762\u524d\u7684\u4e09\u6839\u9752\u7af9\u4fbf\u7535\u4e5f\u4f3c\u5730\u9000\u4e86\u56de\u53bb\u3002 \u3000\u3000\u4ed6\u638c\u529b\u5c1a\u672a\u4f7f\u5c3d\uff0c\u8eab\u540e\u53ef\u662f\u540c\u58f0\u88ad\u6765\uff0c\u4ed6\u5934\u4e5f\u4e0d\u56de\uff0c\u53cd\u624b\u4e00\u638c\uff0c\u54ea\u77e5\u65b9\u624d\u5411\u4ed6\u8eab\u4fa7\u7684\u4e24 \u62db\u9752\u7af9\u6b64\u523b\u5374\u7a81\u5730\u5411\u5185\u4e00\u5708\uff0c\u5b9b\u5982\u4e24\u6761\u98de\u9a70\u800c\u6765\u7684\u9752\u86c7\uff0c\u566c\u5411\u4ed6\u5de6\u53f3\u4e24\u808b\u4e4b\u4e0b\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u4e00\u52a8\uff0c\u77e5\u9053\u81ea\u5df1\u6b64\u523b\u5df2\u843d\u4eba\u4eba\u5bb6\u914d\u5408\u5f97\u5341\u5206\u5de7\u5999\u7684\u9635\u5f0f\u4e2d\uff0c\u8fd9\u4e9b\u5c11\u5973\u7684\u6b66\u529f\u867d\u4e0d \u53ef\u754f\uff0c\u4f46\u81ea\u5df1\u82e5\u88ab\u8fd9\u9635\u5f0f\u56f0\u4f4f\uff0c\u518d\u8981\u60f3\u8131\u8eab\u51fa\u6765\uff0c\u786e\u662f\u5927\u4e3a\u4e0d\u6613\uff0c\u987b\u77e5\u4ed6\u52a8\u624b\u7ecf\u9a8c\u867d\u4e0d\u5927 \u591a\uff0c\u4f46\u53f8\u7a7a\u8001\u4eba\u5341\u5e74\u7684\u6559\u5bfc\uff0c\u5374\u4f7f\u5f97\u4ed6\u5bf9\u9ad8\u624b\u65f6\u60c5\u51b5\u7684\u5224\u65ad\uff0c\u5927\u5f02\u5e38\u4eba\u3002 \u3000\u3000\u4f46\u6b64\u523b\u5374\u5bb9\u4e0d\u5f97\u4ed6\u591a\u52a0\u601d\u7d22\uff0c\u4ed6\u8eab\u8eaf\u4e00\u62e7\uff0c\u65b9\u81ea\u907f\u5f00\u8eab\u4fa7\u7684\u4e24\u6761\u9752\u86c7\uff0c\u90a3\u56db\u67c4\u5176\u767d\u5982\u96ea \u7684\u7fbd\u6247\uff0c\u4fbf\u53c8\u56db\u9762\u516b\u65b9\u7684\u62cd\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u6f2b\u5929\u6247\u5f71\u4e4b\u4e2d\uff0c\u8fd8\u5939\u6742\u7740\u6839\u6839\u9752\u7af9\uff0c\u53ea\u8981\u4ed6\u8eab\u6cd5\u7a0d\u6709\u7a7a\u9699\uff0c\u8fd9\u4e9b\u9752\u7af9\u4fbf\u4f1a\u8bf4\u4e0d\u5b9a\u70b9\u5728\u4ed6 \u8eab\u4e0a\u90a3\u4e00\u5904\u91cd\u7a74\u4e4b\u4e0a\u3002 \u3000\u3000\u5c91\u7cb2\u4ee5\u4ed6\u8eab\u624b\u800c\u89c2\uff0c\u6b64\u523b\u4e5f\u5df2\u786e\u5b9a\u8fd9\u5750\u5728\u8f66\u4e0a\u7684\u8001\u5987\u5fc5\u5b9a\u5c31\u662f\u90a3\u7ea2\u8863\u5a18\u5a18\u6e29\u5982\u7389\uff0c\u56e0\u4e3a \u666e\u5929\u4e4b\u4e0b\uff0c\u80fd\u591f\u5c06\u8896\u4e0a\u7684\u529f\u592b\u7ec3\u5165\u5316\u5883\u7684\uff0c\u9648\u4e86\u8fd9\u8be1\u5f02\u6bd2\u8fa3\u7684\u5973\u9b54\u5934\u5916\uff0c\u5b9e\u5728\u518d\u4e5f\u627e\u4e0d\u51fa\u522b \u4eba\u6765\u3002 \u3000\u3000\u4ed6\u773c\u89c1\u5353\u957f\u537f\u88ab\u90a3\u4e9b\u7ea2\u88f3\u5c11\u5973\u56f0\u4f4f\uff0c\u5fc3\u4e0b\u5927\u4e3a\u5f97\u610f\uff0c\u800c\u4e14\u4ed6\u4e5f\u770b\u51fa\u8fd9\u4e9b\u5c11\u5973\u6240\u65bd\u5c55\u7684\u8eab \u6cd5\uff0c\u867d\u7136\u548c\u81ea\u5df1\u5728\u829c\u6e56\u4e91\u5b85\u6240\u9047\u7684\u76f8\u540c\uff0c\u4f46\u8eab\u624b\u914d\u5408\u7684\u5de7\u5999\uff0c\u5374\u53c8\u8fdc\u5728\u90a3\u4e9b\u5c11\u5973\u4e4b\u4e0a\uff0c\u4e0d\u7981 \u6697\u9053\u4e00\u58f0\u4fa5\u5e78\u3002 \u3000\u3000\u8d77\u5148\u4ed6\u8fd8\u4ee5\u4e3a\u7ea2\u8863\u5a18\u5a18\u540d\u9707\u6b66\u6797\u4e4b\u9713\u88f3\u4ed9\u821e\u9635\u4e5f\u4e0d\u8fc7\u5982\u6b64\uff0c\u4eca\u65e5\u4e00\u89c1\uff0c\u624d\u77e5\u9053\u4ed6\u90a3\u6b21\u4e0d \u8fc7\u662f\u8f83\u4e3a\u5e78\u8fd0\u800c\u5df2\uff0c\u4e0d\u4f46\u90a3\u4e9b\u5f0f\u91cc\uff0c\u4ed6\u6b66\u529f\u518d\u597d\uff0c\u53ea\u6015\u4e5f\u62b5\u53d7\u4e0d\u4f4f\u5427\uff1f\u201c\u5e78\u707e\u4e50\u7978\u4e4b\u5fc3\uff0c\u4f7f \u4ed6\u66f4\u5f80\u524d\u8d70\u4e86\u513f\u6b65\uff0c\u60f3\u770b\u5f97\u66f4\u4ed4\u7ec6\u4e9b\u3002\u54ea\u77e5\u88ab\u56f0\u5728\u9635\u91cc\u7684\u5353\u957f\u537f\uff0c\u60c5\u51b5\u5e76\u4e0d\u77e5\u4ed6\u6240\u60f3\u8c61\u7684\u4e0d \u582a\uff0c\u6b64\u523b\u4ed6\u867d\u5df2\u91c7\u53d6\u5b88\u52bf\uff0c\u4f46\u7cbe\u5999\u7684\u6b65\u6cd5\u548c\u51cc\u5389\u7684\u638c\u98ce\uff0c\u5374\u4f7f\u5f97\u90a3\u56db\u67c4\u7fbd\u6247\uff0c\u5341\u56db\u53ea\u9752\u7af9\u3001 \u7a7a\u81ea\u821e\u8d77\u6ee1\u5929\u821e\u5f71\uff0c\u5374\u4e5f\u65e0\u6cd5\u903c\u8fdb\u4ed6\u8eab\u524d\u534a\u6b65\uff0c\u4f46\u4e00\u65f6\u534a\u523b\uff0c\u4ed6\u5374\u65e0\u6cd5\u8131\u8eab\u800c\u51fa\u3002\u8fd9\u65f6\u5c91\u7cb2 \u4e0d\u89c9\u95f4\uff0c\u5df2\u884c\u8fd1\u90a3\u8f86\u9999\u8f66\u4e4b\u4fa7\uff0c\u54ea\u77e5\u8eab\u4fa7\u7a81\u7136\u54cd\u8d77\u4e86\u4e00\u4e2a\u5c16\u9510\u800c\u523a\u8033\u7684\u58f0\u97f3\uff0c\u559d\u9053\uff1a\u201c\u4f4f \u624b\u3002\u201d \u3000\u3000\u58f0\u8c03\u867d\u4e0d\u751a\u9ad8\uff0c\u4f46\u5c91\u7cb2\u8033\u4e2d\u5374\u4e3a\u4e4b\u751f\u51fa\u4e00\u79cd\u9707\u8361\u7684\u611f\u89c9\uff0c\u4eff\u4f5b\u6709\u4eba\u7528\u53ea\u6781\u5c16\u9510\u7684\u9488\uff0c\u5728 \u4ed6\u8033\u4e2d\u6233\u4e86\u4e00\u4e0b\u3002 \u3000\u3000\u90a3\u4e9b\u7ea2\u88f3\u5c11\u5973\u8eab\u5f62\u672c\u81ea\u65cb\u821e\u4e0d\u606f\uff0c\u4f46\u559d\u58f0\u65b9\u4f4f\uff0c\u5c91\u7cb2\u53ea\u89c9\u773c\u524d\u4e00\u82b1\uff0c\u6f2b\u5929\u7ea2\u5f71\u7f24\u7eb7\uff0c\u8fd9 \u4e9b\u7ea2\u88f3\u5c11\u5973\u7adf\u90fd\u56db\u4e0b\u98d8\u4e86\u5f00\u53bb\uff0c\u5728\u5353\u7136\u800c\u7acb\u7684\u5353\u957f\u537f\u56db\u4fa7\u56f4\u6210\u4e00\u9053\u5706\u5706\u7684\u5708\u5b50\u3002 \u3000\u3000\u53e3\u65e5\u4e00\u671b\uff0c\u53ea\u89c1\u90a3\u7ea2\u88f3\u8001\u5987\uff0c\u7f13\u7f13\u81ea\u8f66\u4e0a\u7ad9\u4e86\u8d77\u6765\uff0c\u53cc\u76ee\u4e00\u5f20\uff0c\u795e\u5149\u70af\u7136\uff0c\u5979\u9762\u4e0a\u90a3\u79cd \u8870\u8001\u4e4b\u6c14\uff0c\u7adf\u4e3a\u4e4b\u4e00\u626b\u800c\u7a7a\u3002 \u3000\u3000\u5353\u957f\u537f\u5fae\u5fae\u4e00\u6014\uff0c\u5374\u89c1\u8fd9\u8001\u5987\u7f13\u7f13\u8d70\u5230\u81ea\u5df1\u8eab\u524d\u6765\uff0c\u67af\u7626\u7684\u8eab\u6750\u5728\u5bbd\u5927\u7684\u8863\u886b\u4e2d\uff0c\u5b9b\u5982 \u4e00\u6839\u67af\u7af9\u3002 \u3000\u3000\u5979\u7f13\u7f13\u800c\u884c\uff0c\u8863\u886b\u7684\u4e0b\u895f\u4e00\u76f4\u62d6\u5230\u811a\u9762\uff0c\u4f7f\u4ed6\u770b\u6765\u6709\u5982\u8e51\u7a7a\u800c\u884c\uff0c\u5353\u957f\u537f\u5fc3\u4e2d\u4e0d\u77e5\u600e \u7684\uff0c\u7ade\u7a81\u7136\u6cdb\u51fa\u4e00\u9635\u65e0\u6cd5\u8bf4\u51fa\u7684\u5bd2\u610f\uff0c\u5fae\u4e00\u5b9a\u795e\uff0c\u65b9\u5f85\u5f00\u53e3\uff0c\u54ea\u77e2\u201c\u8fd9\u8001\u5987\u5df2\u68ee\u51b7\u4ed6\u8bf4\u9053\uff1a \u201c\u65b9\u624d\u4f2f\uff1b\u8bf4\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5353\u957f\u537f\u4e00\u633a\u80f8\u819b\uff0c\u5927\u559d\u9053\uff1a\u201c\u6211\u95ee\u4f60\u5341\u5e74\u524d\u59cb\u4fe1\u5cf0\u4e0b\u7684\u8840\u503a\uff0c\u4f60\u53ef\u66fe\u5fd8\u4e86\uff1f\u201d \u3000\u3000\u8fd9\u8001\u5987\u5229\u5982\u9e70\u96bc\u7684\u76ee\u5149\uff0c\u50cf\u5229\u7bad\u822c\u5728\u5353\u957f\u537f\u8eab\u4e0a\u4e00\u626b\uff0c\u51b7\u51b7\u7684\u8bf4\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u5c31\u662f\u90a3\u59d3 \u5353\u7684\u540e\u4ee3\u4e86\uff1f\u201d \u3000\u3000\u5353\u957f\u537f\u9053\uff1a\u201c\u6b63\u662f\u3002\u201d \u3000\u3000\u54ea\u77e5\u9053\u8001\u5987\u76ee\u5149\u4e00\u77ac\uff0c\u7ade\u7a81\u7136\u4ef0\u5929\u957f\u7b11\u8d77\u6765\uff0c\u7b11\u6709\u5982\u67ad\u9e1f\u591c\u557c\uff0c\u4ee4\u4eba\u96be\u4ee5\u76f8\u4fe1\u8fd9\u67af\u7626\u800c \u8870\u8001\u7684\u5987\u4eba\uff0c\u600e\u80fd\u53d1\u51fa\u5982\u6b64\u9ad8\u4ea2\u7684\u7b11\u58f0\u6765\u3002 \u3000\u3000\u7b11\u58f0\u4e00\u987f\uff0c\u90a3\u88ab\u7b11\u58f0\u9707\u5f97\u51e0\u4e4e\u6447\u6447\u6b32\u5760\u7684\u679d\u53f6\uff0c\u4e5f\u500f\u7136\u800c\u9759\uff0c\u5374\u542c\u8fd9\u8001\u5987\u5df2\u81ea\u7f13\u7f13\u9053\uff1a \u201c\u8fd9\u6570\u5341\u5e74\u6765\uff0c\u6b7b\u5728\u6211\u624b\u4e0b\u4e4b\u4eba\uff0c\u4f55\u6b62\u5343\u6570\uff0c\u6211\u6b63\u81ea\u5947\u602a\uff0c\u600e\u4e48\u8fd9\u4e9b\u4eba\u7684\u95e8\u4eba\u6216\u540e\u4ee3\uff0c\u7adf\u4ece \u65e0\u4e00\u4eba\u627e\u6211\u590d\u4ec7\u7684\uff0c\u54ea\u77e5\u9053\u2014\u2014\u563f\u563f\uff0c\u4eca\u65e5\u5374\u8ba9\u6211\u89c1\u7740\u4e00\u4e2a\u3002\u201d \u3000\u3000\u76ee\u5149\u4e00\u4fa7\uff0c\u53c8\u81ea\u671b\u7740\u5c91\u7cb2\u559d\u9053\uff1a\u201c\u4f60\u53c8\u662f\u8c01\uff1f\u662f\u5426\u4e5f\u662f\u5e2e\u7740\u4ed6\u6765\u590d\u4ec7\u7684\uff1f\u201d \u3000\u3000\u5c91\u7cb2\u5fc3\u4e2d\u4e00\u51db\uff0c\u8d70\u524d\u4e09\u6b65\uff0c\u8eac\u8eab\u4e00\u793c\uff0c\u9053\uff1a\u201c\u665a\u8f88\u548c\u6b64\u4eba\u4e0d\u4f46\u7d20\u4e0d\u76f8\u8bc6\uff0c\u800c\u4e14\u2014\u2014\u201d\u90a3 \u7ea2\u88f3\u8001\u5987\u51b7\u54fc\u4e00\u58f0\uff0c\u68ee\u51b7\u7684\u76ee\u5149\uff0c\u51dd\u6ce8\u5728\u4ed6\u9762\u4e0a\uff0c\u63a5\u53e3\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u4f60\u7ad9\u5728\u65c1\u8fb9\uff0c\u662f\u5b58 \u5fc3\u60f3\u770b\u770b\u70ed\u95f9\u7684\u4e86\u3002\u201d \u3000\u3000\u8bed\u58f0\u867d\u662f\u6781\u4e3a\u5e73\u6de1\uff0c\u4f46\u5c91\u7cb2\u542c\u5728\u8033\u91cc\uff0c\u5374\u89c9\u4e00\u80a1\u5bd2\u610f\uff0c\u76f4\u900f\u80cc\u810a\uff0c\u5028\u505a\u4e4b\u6c14\u4e3a\u4e4b\u5c3d\u6d88\uff0c \u6014\u4e86\u534a\u5929\uff0c\u65b9\u81ea\u606d\u58f0\u7b54\u9053\uff1a\u201c\u665a\u8f88\u548c\u6b64\u4eba\u6709\u4e9b\u8fc7\u8282\u672a\u4e86\uff0c\u662f\u4ee5\u2014\u2014\u201d\u54ea\u77e5\u7ea2\u88f3\u8001\u5987\u4e0d\u7b49\u4ed6\u8bdd \u8bf4\u5b8c\uff0c\u53c8\u81ea\u63a5\u53e3\u9053\uff1a\u201c\u4f60\u662f\u5426\u60f3\u7b49\u4ed6\u4e0e\u6211\u4e4b\u95f4\u7684\u4e8b\u60c5\u4e86\u540e\uff0c\u518d\u5bfb\u4ed6\u4e86\u5374\u4e0e\u4f60\u4e4b\u95f4\u7684\u8fc7\u8282\u3002\u201d \u3000\u3000\u5c91\u7cb2\u5fae\u4e00\u9894\u9996\uff0c\u5374\u89c1\u5979\u53c8\u7eb5\u58f0\u72c2\u7b11\u8d77\u6765\uff0c\u4e00\u9762\u8bf4\u9053\uff1a\u201c\u597d\u6781\uff0c\u597d\u6781\uff0c\u770b\u4e0d\u51fa\u4f60\u5e74\u7eaa\u8f7b \u8f7b\uff0c\u5012\u8fd8\u806a\u660e\u5f97\u5f88\u2014\u2014\u201d\u5979\u8bdd\u867d\u53ea\u8bf4\u4e00\u534a\uff0c\u4f46\u5c91\u7cb2\u6b63\u662f\u7edd\u9876\u806a\u660e\u4e4b\u4eba\uff0c\u5f53\u7136\u5df2\u4e86\u89e3\u5979\u8bdd\u4e2d\u7684 \u542b\u610f\uff0c\u662f\u8bf4\u7b49\u4f1a\u6839\u672c\u65e0\u987b\u81ea\u5df1\u52a8\u624b\u4e86\uff0c\u5353\u957f\u537f\u5df2\u518d\u65e0\u6d3b\u8def\uff0c\u81ea\u5df1\u5c82\u975e\u6361\u4e86\u4e2a\u4fbf\u5b9c\uff0c\u76ee\u5149\u4e00 \u8f6c\uff0c\u5374\u89c1\u8fd9\u7ea2\u88f3\u8001\u5987\u76ee\u5149\u53c8\u51db\u7136\u56de\u5230\u5353\u957f\u537f\u7684\u8eab\u4e0a\uff0c\u4f38\u51fa\u4e00\u53ea\u67af\u7626\u7684\u624b\u6765\uff0c\u4e00\u6574\u5934\u4e0a\u9b13\u53d1\uff0c \u7f13\u7f13\u5411\u4ed6\u903c\u8fd1\u4e86\u53bb\u3002 \u3000\u3000\u4e00\u9635\u98ce\u5439\u52a8\uff0c\u5c91\u7cb2\u8eab\u4e0a\u4f3c\u4e4e\u89c9\u5f97\u6709\u4e9b\u5bd2\u610f\uff0c\u4ed6\u77e5\u9053\u5239\u90a3\u4e4b\u95f4\uff0c\u6b64\u5730\u4fbf\u8981\u7acb\u523b\u6f14\u51fa\u4e00\u573a\u6d41 \u8840\u60e8\u5267\u4e86\u3002 \u3000\u3000\u5353\u957f\u537f\u53ea\u89c9\u5fc3\u4e2d\u70ed\u8840\u5954\u817e\uff0c\u6fc0\u52a8\u96be\u5b89\uff0c\u5341\u5e74\u6765\uff0c\u4ed6\u65e0\u65f6\u65e0\u523b\u4e0d\u5728\u7b49\u5f85\u8fd9\u4e0e\u4ec7\u4eba\u76f8\u5bf9\u7684\u4e00 \u523b\uff0c\u4e8e\u662f\u5341\u5e74\u7684\u79ef\u90c1\uff0c\u6b64\u523b\u4fbf\u5982\u5c71\u6d2a\u822c\u7684\u7206\u53d1\u51fa\u6765\u3002 \u3000\u3000\u53ea\u662f\u591a\u5e74\u7684\u953b\u70bc\uff0c\u5374\u4f7f\u4ed6\u5728\u8fd9\u79cd\u60c5\u51b5\u4e0b\u72b9\u80fd\u4fdd\u6301\u9547\u9759\uff0c\u56e0\u4e3a\u4ed6\u77e5\u9053\uff0c\u6b64\u523b\u6b63\u662f\u751f\u6b7b\u5b58\u4ea1 \u60ac\u4e8e\u4e00\u7ebf\u4e4b\u65f6\uff0c\u81ea\u5df1\u82e5\u80fd\u80dc\u5f97\u4e86\u8fd9\u4e0d\u5171\u6234\u5929\u7684\u4ec7\u4eba\uff0c\u4e00\u671d\u5f97\u62a5\uff0c\u5fc3\u4e2d\u4fbf\u518d\u65e0\u7275\u6302\u4e4b\u4e8b\uff0c\u5426 \u5219\uff0c\u8fd9\u4e11\u4eba\u6e29\u5982\u7389\u4e5f\u7edd\u4e0d\u4f1a\u653e\u8fc7\u81ea\u5df1\u3002 \u3000\u3000\u4ed6\u52aa\u529b\u5730\u5c06\u5fc3\u4e2d\u6fc0\u52a8\u4e4b\u60c5\uff0c\u6df1\u6df1\u538b\u5236\uff0c\u62ac\u65e5\u800c\u671b\uff0c\u53ea\u89c1\u90a3\u4e11\u4eba\u6e29\u5982\u7389\u4e5f\u6b63\u5728\u51dd\u89c6\u7740\u81ea \u5df1\uff0c\u4e00\u9762\u4e0d\u4f4f\u70b9\u9996\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u5b69\u5b50\u5012\u662f\u957f\u5f97\u6709\u51e0\u5206\u548c\u90a3\u59d3\u5353\u7684\u76f8\u50cf\uff0c\u53ea\u662f\u6bd4\u4ed6\u2014\u2014\u201d\u5353\u957f \u537f\u89c1\u8fd9\u4e11\u4eba\u6e29\u5982\u7389\u6b64\u523b\u7adf\u662f\u4e00\u526f\u6ee1\u4e0d\u5728\u4e4e\u7684\u6837\u5b50\uff0c\u751f\u50cf\u662f\u6839\u672c\u6ca1\u6709\u5c06\u81ea\u5df1\u653e\u5728\u773c\u91cc\uff0c\u53c8\u542c\u5f97 \u5979\u63d0\u53ca\u81ea\u5df1\u7684\u7236\u4eb2\uff0c\u8bf4\u8bdd\u4e4b\u65f6\uff0c\u795e\u6001\u81ea\u82e5\uff0c\u5c31\u50cf\u662f\u8bf4\u8d77\u81ea\u5df1\u7684\u77e5\u4ea4\u5e2e\u53cb\u4e00\u6837\uff0c\u54ea\u91cc\u50cf\u662f\u5728\u8bf4 \u4e00\u4e2a\u88ab\u5979\u6b8b\u5bb3\u7684\u4eba\u3002 \u3000\u3000\u4ed6\u66f4\u662f\u60b2\u4fa6\u586b\u80f8\uff0c\u6697\u4e2d\u8c03\u5300\u771f\u6c14\uff0c\u53ea\u5f85\u51fa\u624b\u4e00\u51fb\uff0c\u4fbf\u5c06\u5979\u4f24\u5728\u638c\u4e0b\u3002 \u3000\u3000\u54ea\u77e5\u7ea2\u88f3\u5a18\u5a18\u6e29\u5982\u738b\u8bdd\u8bf4\u5230\u4e00\u534a\uff0c\u8bed\u58f0\u7a81\u7136\u4e00\u987f\uff0c\u8eab\u5f62\u6beb\u672a\u4f5c\u52bf\uff0c\u53ea\u89c1\u5979\u5bbd\u5927\u7684\u8863\u8882\u5411 \u5de6\u4e00\u626c\uff0c\u4fbf\u7535\u4e5f\u4f3c\u5730\u671d\u7acb\u5728\u53f3\u8fb9\u7684\u5c91\u7cb2\u63a0\u4e86\u8fc7\u53bb\uff0c\u4f38\u51fa\u53f3\u638c\u500f\u7136\u5411\u5c91\u7cb2\u5f53\u80f8\u6293\u53bb\u3002 \u3000\u3000\u5c91\u7cb2\u5fc3\u5b89\u7406\u5f97\u5730\u7ad9\u5728\u4e00\u8fb9\uff0c\u6b63\u5f85\u9759\u89c2\u8fd9\u7384\u886b\u5c11\u5e74\u7684\u6d41\u8840\u60e8\u5267\uff0c\u54ea\u77e5\u8fd9\u7ea2\u8863\u5a18\u5a18\u7adf\u7a81\u7136\u5411 \u81ea\u5df1\u63a0\u4e86\u8fc7\u6765\uff0c\u5fc3\u4e2d\u4e0d\u7531\u5927\u60ca\uff0c\u65b9\u5f85\u62e7\u8eab\u9000\u5374\uff0c\u5feb\u5982\u98de\u77e2\uff0c\u53c8\u662f\u5728\u5c91\u7cb2\u4e07\u4e07\u6599\u60f3\u4e0d\u5230\u7684\u65f6\u5019 \u51fa\u624b\uff0c\u5c91\u7cb2\u8eab\u5f62\u8fd8\u672a\u6765\u5f97\u53ca\u5c55\u52a8\uff0c\u524d\u80f8\u7684\u8863\u895f\uff0c\u5df2\u88ab\u4e00\u628a\u6293\u4f4f\u3002 \u3000\u3000\u4ed6\u7247\u523b\u4e4b\u95f4\uff0c\u4e00\u8fde\u4e24\u6b21\u88ab\u4eba\u5bb6\u6293\u4f4f\u524d\u80f8\u7684\u8863\u895f\uff0c\u867d\u8bf4\u4e24\u6b21\u4ff1\u4e3a\u81ea\u5df1\u610f\u6599\u4e0d\u5230\uff0c\u662f\u4ee5\u731d\u4e0d \u53ca\u9632\uff0c\u4f46\u7ec8\u7a76\u662f\u5341\u5206\u4e22\u4eba\u4e4b\u4e8b\uff0c\u5fc3\u4e2d\u7f9e\u607c\u4ea4\u96c6\uff0c\u773c\u770b\u8fd9\u7ea2\u8863\u5a18\u5a18\u7684\u76ee\u5149\uff0c\u51b0\u51b7\u7684\u671b\u7740\u81ea\u5df1\uff0c \u65e2\u602f\u4e8e\u5979\u7684\u6b66\u529f\uff0c\u53c8\u602f\u4e8e\u5979\u7684\u58f0\u540d\uff0c\u4fbf\u4e0d\u6562\u8d38\u7136\u51fa\u624b\uff0c\u53ea\u5f97\u60f6\u58f0\u95ee\u9053\uff1a\u201c\u8001\u524d\u8f88\uff0c\u4f60\u8fd9\u662f\u5e72 \u4ec0\u4e48\uff1f\u201d \u3000\u3000\u7ea2\u8863\u5a18\u5a18\u6e29\u52a0\u7389\u9634\u4fa7\u607b\u5730\u4e00\u7b11\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u5341\u5e74\u4e4b\u524d\uff0c\u9ec4\u5c71\u59cb\u4fe1\u5cf0\u4e0b\uff0c\u4f60\u662f\u5426\u4e5f\u662f\u5728 \u573a\u4eba\u7684\u5176\u4e2d\u4e4b\u4e00\uff1f\u201d \u3000\u3000\u5c91\u7cb2\u5fc3\u4e2d\u4e00\u51db\uff0c\u5341\u5e74\u524d\u7684\u5f80\u4e8b\uff0c\u95ea\u7535\u822c\u5730\u5728\u5fc3\u5934\u4e00\u63a0\u800c\u8fc7\u90a3\u65f6\u4ed6\u8fd8\u662f\u4e2a\u5e74\u9f84\u6781\u5e7c\u7684\u7ae5 \u5b50\uff0c\u867d\u7136\u5728\u8c6a\u5bcc\u4e4b\u5bb6\uff0c\u4f46\u5374\u4e00\u76f4\u5f97\u4e0d\u5230\u7236\u6bcd\u7684\u6b22\u5fc3\uff0c\u4ed6\u751f\u6027\u504f\u6fc0\uff0c\u5c31\u4e5f\u8d8a\u53d1\u987d\u52a3\uff0c\u5e94\u8be5\u5165\u587e \u5ff5\u4e66\u7684\u65f6\u5019\uff0c\u4ed6\u5374\u5077\u5077\u5730\u8dd1\u5230\u8352\u575f\u91ce\u5730\u4e2d\u53bb\u72ec\u81ea\u5b09\u620f\u3002 \u3000\u3000\u54ea\u77e5\uff0c\u4e00\u5929\u5374\u6709\u4e2a\u7fbd\u8863\u661f\u51a0\u7684\u9053\u4eba\u7a81\u7136\u50cf\u795e\u4ed9\u4f3c\u7684\u81ea\u5929\u800c\u964d\uff0c\u95ee\u4ed6\u613f\u4e0d\u613f\u79bb\u5f00\u5bb6\u5ead\uff0c\u53bb \u5b66\u6b66\u529f\uff0c\u4ed6\u4e00\u60f3\u7236\u6bcd\u4e0e\u81ea\u5df1\u672c\u65e0\u60c5\u611f\uff0c\u81ea\u5df1\u7559\u5728\u5bb6\u91cc\u4e5f\u6beb\u65e0\u610f\u601d\uff0c\u5012\u4e0d\u5982\u5b66\u5f97\u4e00\u8eab\u672c\u4e8b\uff0c\u4e5f \u50cf\u8fd9\u9053\u4eba\u4e00\u6837\u7684\u80fd\u5728\u7a7a\u4e2d\u98de\u63a0\uff0c\u90a3\u8be5\u591a\u6709\u610f\u601d\uff0c\u4fbf\u6beb\u4e0d\u8003\u8651\u5730\u4e00\u53e3\u7b54\u5e94\u4e86\u3002 \u3000\u3000\u540e\u6765\u4ed6\u624d\u77e5\u9053\u8fd9\u9053\u5165\u4fbf\u662f\u540d\u9707\u6b66\u6797\u7684\u4e07\u5999\u771f\u541b\uff0c\u4fbf\u548c\u4e24\u4e2a\u548c\u81ea\u5df1\u5e74\u9f84\u76f8\u4eff\u7684\u5b69\u5b50\u8ddf\u7740\u4ed6 \u4e00\u8d77\u5230\u4e86\u9ec4\u5c71\u3002 \u3000\u3000\u4e8e\u662f\u5341\u5e74\u524d\u9ec4\u5c71\u59cb\u4fe1\u5cf0\u4e0b\u90a3\u4e00\u5e55\u60ca\u5fc3\u52a8\u9b44\u7684\u5f80\u4e8b\uff0c\u6b64\u523b\u4fbf\u53c8\u5386\u5386\u5982\u5728\u773c\u524d\u3002 \u3000\u3000\u98de\u626c\u7684\u5c18\u6c99\uff0c\u91ce\u517d\u7684\u5636\u9e23\uff0c\u6c14\u9b44\u6177\u6168\u7684\u4e2d\u5e74\u6c49\u5b50\uff0c\u6e29\u67d4\u7f8e\u4e3d\u7684\u4e2d\u5e74\u7f8e\u5987\uff0c\u8ddf\u5728\u4ed6\u8eab\u4fa7\u7684 \u5e7c\u7ae5\uff0c\u548c\u81ea\u5df1\u7684\u5e08\u7236\u89c1\u7740\u4ed6\u4eec\u65f6\u9762\u4e0a\u663e\u9732\u7684\u795e\u60c5\uff0c\u4fbf\u4e5f\u4e00\u5e55\u5e55\u81ea\u773c\u524d\u95ea\u8fc7\u3002 \u3000\u3000\u4ed6\u60f3\u8d77\u90a3\u9aa8\u7626\u5982\u67f4\u7684\u7ea2\u886b\u5987\u4eba\uff0c\u8c8c\u7f8e\u5982\u4ed9\u7684\u5929\u771f\u5973\u7ae5\uff0c\u548c\u6700\u540e\u53d1\u751f\u7684\u90a3\u4e00\u6bb5\u60e8\u5267\uff0c\u518d\u770b \u5230\u773c\u524d\u8fd9\u7384\u886b\u5c11\u5e74\u5bf9\u8fd9\u7ea2\u8863\u5a18\u5a18\u7684\u795e\u60c5\uff0c\u4e0d\u7981\u5fc3\u4e2d\u5927\u4e3a\u604d\u7136\uff0c\u5fd6\u9053\uff1a\u201c\u539f\u6765\u8fd9\u7384\u886b\u5c11\u5e74\u4fbf\u662f \u5341\u5e74\u524d\u8ddf\u5728\u90a3\u4e2d\u5e74\u7f8e\u5987\u8eab\u4fa7\u7684\u5b69\u5b50\uff0c\u8fd9\u7ea2\u8863\u5a18\u5a18\u4fbf\u662f\u6740\u4ed6\u7236\u6bcd\u7684\u4ec7\u4eba\u3002\u201d \u3000\u3000\u53c8\u5fd6\u9053\uff1a\u201c\u90a3\u4e09\u5e45\u753b\u5377\u4e2d\u7684\u7f8e\u5973\u4e4b\u50cf\uff0c\u4fbf\u662f\u65b9\u624d\u5728\u57ce\u5899\u4e0a\u6240\u89c1\u7684\u7edd\u8272\u5c11\u5973\uff0c\u800c\u8fd9\u7edd\u8272\u5c11 \u5973\uff0c\u60f3\u5fc5\u5c31\u662f\u5341\u5e74\u524d\u90a3\u8c8c\u7f8e\u5982\u4ed9\u7684\u7edd\u8272\u5973\u7ae5\u4e86\uff0c\u96be\u602a\u6211\u89c1\u7740\u90a3\u5e45\u753b\u65f6\uff0c\u4fbf\u89c9\u5f97\u5341\u5206\u773c\u719f\uff0c\u539f \u6765\u662f\u8fd9\u4e48\u56de\u4e8b\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u65b9\u624d\u89c1\u90a3\u4e11\u4eba\u6e29\u5982\u7389\u7ade\u9661\u7136\u820d\u5374\u81ea\u5df1\uff0c\u800c\u5411\u90a3\u9ec4\u886b\u5c11\u5e74\u51fa\u624b\uff0c\u5fc3\u4e2d\u65b9\u81ea\u4e00\u6014\uff0c\u4f46 \u542c\u5230\u6e29\u5982\u7389\u51b7\u51b7\u5411\u90a3\u9ec4\u886b\u5c11\u5e74\u95ee\u51fa\u6765\u7684\u8bdd\u4e4b\u540e\uff0c\u5fc3\u4e2d\u4e5f\u4e0d\u7981\u604d\u7136\u800c\u609f\uff0c\u5fd6\u9053\uff1a\u201c\u539f\u6765\u8fd9\u9ec4\u886b \u5c11\u5e74\u5c31\u662f\u5341\u5e74\u524d\u59cb\u4fe1\u5cf0\u4e0a\u7684\u9ec4\u886b\u7ae5\u5b50\u3002\u201d \u3000\u3000\u4fbf\u4e5f\u60f3\u5230\u81ea\u5df1\u65b9\u624d\u6240\u89c1\u7684\u7edd\u8272\u5c11\u5973\uff0c\u5fc5\u5b9a\u5c31\u662f\u90a3\u5a07\u7f8e\u5973\u7ae5\uff0c\u4e0d\u7981\u6697\u53f9\u4e00\u58f0\uff0c\u53c8\u5fd6\u9053\uff1a \u201c\u9020\u5316\u5b89\u6392\uff0c\u7684\u786e\u5f04\u4eba\uff0c\u5341\u5e74\u524d\u5728\u90a3\u5c0f\u5c0f\u7684\u4e00\u7247\u5c71\u5d16\u4e0a\u7684\u4eba\uff0c\u7ecf\u8fc7\u5341\u5e74\u4e4b\u4e45\uff0c\u7adf\u53c8\u805a\u96c6\u4e00 \u5904\u3002\u201d \u3000\u3000\u4ed6\u5374\u4e0d\u77e5\u9053\u9020\u5316\u5f04\u4eba\uff0c\u66f4\u4e0d\u6b62\u4e8e\u6b64\uff0c\u975e\u4f46\u5c06\u4ed6\u4eec\u805a\u505a\u4e00\u5904\uff0c\u66f4\u5c06\u4ed6\u4eec\u5f7c\u6b64\u4e4b\u95f4\u7684\u60c5\u4ec7\u6069 \u6028\uff0c\u5bc6\u5bc6\u7ea0\u7f20\uff0c\u4f7f\u5f97\u4ed6\u4eec\u81ea\u5df1\u4e5f\u51e0\u4e4e\u5316\u89e3\u4e0d\u5f00\u54e9\u3002 \u3000\u3000\u90a3\u7ea2\u8863\u5a18\u5a18\u4e00\u628a\u6293\u4f4f\u5c91\u7cb2\uff0c\u5374\u89c1\u4ed6\u7adf\u5446\u5446\u5730\u6115\u4f4f\u4e86\uff0c\u773c\u4e2d\u4ed6\u5360\u5c3d\u4e86\uff0c\u5341\u5e74\u4e4b\u524d\uff0c\u6211\u548c\u90a3 \u59d3\u5353\u7684\u65e0\u6028\u65e0\u4ec7\uff0c\u90fd\u662f\u4e3a\u4e86\u8fd9\u4e2a\u591a\u5e74\u6df1\u4ea4\uff0c\u624d\u2014\u2014\u201c\u5979\u8bed\u58f0\u7a81\u53c8\u4e00\u987f\uff0c\u8f6c\u8fc7\u5934\u53bb\uff0c\u5411\u5353\u957f\u537f \u68ee\u51b7\u4ed6\u8bf4\u9053\uff1a\u201c\u6211\u8bf4\u6211\u7684\uff0c\u4e0d\u7ba1\u4f60\u7684\u4e8b\uff0c\u4f60\u7239\u7239\u7684\u786e\u662f\u6211\u6740\u7684\uff0c\u4f60\u8981\u62a5\u4ec7\uff0c\u53ea\u7ba1\u51b2\u7740\u6211\u6765\u597d \u4e86\u3002\u201d \u3000\u3000\u76ee\u5149\u518d\u6b21\u8f6c\u5411\u5c91\u7cb2\uff0c\u6307\u9053\uff1a\u201c\u81ea\u4ece\u90a3\u65e5\u4e4b\u540e\uff0c\u4f60\u5e08\u7236\u53c8\u4e0d\u77e5\u7b97\u8ba1\u4e86\u591a\u5c11\u6b21\uff0c\u6211\u53ea\u9053\u662f\u5929 \u4e0b\u597d\u72e1\u4e4b\u4eba\uff0c\u518d\u4e5f\u83ab\u8fc7\u4e8e\u4e07\u5999\u771f\u541b\u4e86\uff0c\u563f\u563f\uff0c\u54ea\u77e5\u4f60\u8fd9\u5c0f\u9b3c\uff0c\u4e5f\u6bd4\u4ed6\u5dee\u4e0d\u591a\u5c11\uff0c\u6211\u95ee\u95f4\u4f60\uff0c \u4f60\u65b9\u624d\u65e2\u8bf4\u4e0e\u8fd9\u59d3\u5353\u7684\u540e\u4eba\u7d20\u4e0d\u76f8\u8bc6\uff0c\u600e\u4e48\u53c8\u8bf4\u548c\u4ed6\u6709\u7740\u8fc7\u8282\u672a\u4e86\uff0c\u4f60\u548c\u8fd9\u7d20\u4e0d\u76f8\u8bc6\u4e4b\u4eba\u7a76 \u7adf\u6709\u4ec0\u4e48\u4ec7\u6068\uff0c\u4f60\u5012\u8bf4\u7ed9\u6211\u542c\u542c\u770b\u3002\u201d \u3000\u3000\u5c91\u7cb2\u4e0d\u89c9\u4e3a\u4e4b\u4e00\u6014\uff0c\u6697\u95ee\u81ea\u5df1\uff1a\u201c\u6211\u548c\u8fd9\u59d3\u5353\u7684\u6709\u4f55\u4ec7\u6068\uff1f\u201d \u3000\u3000\u5374\u8fde\u81ea\u5df1\u4e5f\u53e3\u7b54\u4e0d\u51fa\uff0c\u987b\u77e5\u4ed6\u5bf9\u5353\u957f\u537f\u6781\u4e3a\u5992\u6068\uff0c\u4f46\u8fd9\u79cd\u5992\u6068\u53c8\u5c82\u80fd\u5728\u522b\u4eba\u9762\u524d\u8bf4\u51fa \u6765\uff0c\u53c8\u600e\u80fd\u7b97\u5f97\u4e0a\u662f\u8fc7\u8282\u5462\uff1f \u3000\u3000\u7ea2\u8863\u5a18\u5a18\u6e29\u5982\u7389\u671b\u7740\u4ed6\u9762\u4e0a\u7684\u795e\u60c5\uff0c\u51b7\u7b11\u4e00\u58f0\uff0c\u53c8\u9053\uff1a\u201c\u4f60\u5fc3\u91cc\u5230\u5e95\u5728\u6253\u7740\u4ec0\u4e48\u7b97\u76d8\uff1f \u5feb\u8ddf\u6211\u8001\u8001\u5b9e\u5b9e\u4ed6\u8bf4\u6765\uff0c\u5426\u5219\u2026\u2026\u563f\u563f\uff01\u201d \u3000\u3000\u624b\u8155\u4e00\u7d27\uff0c\u51e0\u4e4e\u5c06\u5c91\u7cb2\u79bb\u5730\u626f\u8d77\u3002 \u3000\u3000\u5c91\u7cb2\u5251\u7709\u4e00\u8f69\uff0c\u6297\u58f0\u9053\uff1a\u201c\u665a\u8f88\u6240\u8bf4\u53e5\u53e5\u4ff1\u662f\u5b9e\u8a00\uff0c\u665a\u8f88\u7d20\u4ef0\u8001\u524d\u8f88\u82f1\u540d\uff0c\u53c8\u600e\u4f1a\u5bf9\u8001 \u524d\u8f88\u6000\u6709\u4e0d\u8f68\u4e4b\u5fc3\u2014\u2014\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u731b\u7136\u6b3a\u8eab\u4e00\u8fdb\uff0c\u6307\u6233\u65f6\u649e\uff0c\u53cc\u624b\u5404\u51fb\u51fa\u4e24\u62db\uff0c\u5de6\u817f\u4e5f\u540c \u65f6\u98de\u8d77\u6a2a\u626b\u6e29\u52a0\u7389\u53f3\u819d\u3002 \u3000\u3000\u6e29\u5982\u7389\u4e0d\u7981\u4e3a\u4e4b\u4e00\u60ca\uff0c\u518d\u4e5f\u60f3\u4e0d\u5230\u8fd9\u5c11\u5e74\u4f1a\u6597\u80c6\u5411\u81ea\u5df1\u51fa\u624b\uff0c\u800c\u4e14\u62db\u62db\u72e0\u8fa3\uff0c\u65e0\u4e00\u4e0d\u662f \u51fb\u5411\u81ea\u5df1\u8981\u5bb3\uff0c\u5979\u6b66\u529f\u518d\u9ad8\uff0c\u4e5f\u4e0d\u80fd\u4e0d\u5148\u56fe\u81ea\u6551\uff0c\u624b\u8155\u4e00\u677e\uff0c\u9519\u6b65\u4ef0\u8eab\uff0c\u500f\u7136\u6ed1\u5f00\u6570\u6b65\u3002 \u3000\u3000\u5c91\u7cb2\u80f8\u524d\u4e00\u677e\uff0c\u4ea6\u81ea\u62e7\u8eab\u9519\u6b65\uff0c\u9000\u51fa\u4e94\u6b65\uff0c\u987b\u77e5\u4ed6\u4e43\u5341\u5206\u72c2\u505a\u4e4b\u4eba\uff0c\u867d\u5bf9\u7ea2\u8863\u5a18\u5a18\u6709\u6240 \u602f\u60e7\uff0c\u4f46\u5fc3\u4e0b\u4ea6\u5927\u4e3a\u6c14\u6124\uff0c\u6b64\u523b\u89c1\u81ea\u5df1\u5fae\u4e00\u51fa\u624b\uff0c\u4fbf\u4f7f\u5f97\u5979\u4e0d\u5f97\u4e0d\u653e\u677e\u624b\u638c\uff0c\u4e0d\u7981\u51b7\u7b11\u6697\u5fd6 \u9053\uff1a\u201c\u539f\u6765\u5979\u6b66\u529f\u4e5f\u4e0d\u8fc7\u5982\u6b64\u3002\u201d \u3000\u3000\u602f\u4ff1\u4e4b\u5fc3\uff0c\u4e3a\u4e4b\u5927\u51cf\uff0c\u53cc\u624b\u4e00\u6574\u8863\u886b\uff0c\u53c8\u9053\uff1a\u201c\u8001\u524d\u8f88\u53e3\u53e3\u58f0\u58f0\u8ba5\u5632\u8fb1\u9a82\u4e8e\u6211\uff0c\u5b9e\u4e0d\u77e5 \u662f\u4f55\u5c45\u5fc3\uff0c\u5bb6\u5e08\u7eb5\u7136\u5bf9\u8001\u524d\u8f88\u6709\u4e0d\u662f\u4e4b\u5904\uff0c\u4f46\u5bb6\u5e08\u5e76\u672a\u6b7b\u53bb\uff0c\u8001\u524d\u8f88\u5374\u4e5f\u4e0d\u8be5\u5c06\u8fd9\u7b14\u5e10\u7b97\u5728 \u665a\u8f88\u8eab\u4e0a\u5440\uff1f\u201d \u3000\u3000\u53e4\u4e0b\u4e4b\u610f\uff0c\u81ea\u662f\u6697\u8ba5\u8fd9\u4e11\u4eba\u6e29\u5982\u7389\u53ea\u52a0\u4ee5\u4e0a\u51cc\u4e0b\uff0c\u4ee5\u5f3a\u51cc\u5f31\uff0c\u5374\u4e0d\u6562\u53bb\u627e\u81ea\u5df1\u7684\u5e08\u7236\u7b97 \u5e10\u3002 \u3000\u3000\u5982\u6b64\u9732\u9aa8\u4e4b\u8bdd\uff0c\u6e29\u5982\u7389\u600e\u4f1a\u542c\u4e0d\u51fa\u6765\uff0c\u5c91\u7cb2\u76ee\u5149\u51dd\u6ce8\uff0c\u5fc3\u60f3\u5979\u5fc5\u5b9a\u53c8\u8981\u4ef0\u5929\u72c2\u7b11\uff0c\u6216\u662f \u66b4\u8df3\u5982\u96f7\uff0c\u54ea\u77e5\u9053\u671b\u4e86\u534a\u664c\uff0c\u8fd9\u8be1\u5f02\u6bd2\u8fa3\u7684\u5973\u9b54\u5934\u9762\u4e0a\uff0c\u4e0d\u4f46\u8fde\u534a\u70b9\u8868\u60c5\u90fd\u6ca1\u6709\uff0c\u800c\u4e14\u76ee\u5149 \u9eef\u6de1\uff0c\u60f3\u662f\u6b63\u5728\u60f3\u7740\u5fc3\u4e8b\uff0c\u53c8\u50cf\u662f\u6839\u672c\u6ca1\u6709\u542c\u5230\u81ea\u5df1\u7684\u6d3b\u3002 \u3000\u3000\u8fd9\u4e48\u4e00\u6765\uff0c\u81ea\u7136\u5927\u5927\u51fa\u4e86\u5c91\u7cb2\u610f\u6599\u4e4b\u5916\uff0c\u8f6c\u76ee\u4e00\u671b\uff0c\u5374\u89c1\u7384\u886b\u5c11\u5e74\u2014\u2014\u5353\u957f\u537f\u4ea6\u5728\u4fef\u9996 \u6df1\u601d\uff0c\u4ed6\u5fc3\u4e0b\u4e0d\u7981\u5927\u5947\uff0c\u81ea\u5fd6\u9053\uff1a\u201c\u8fd9\u53ae\u600e\u4e48\u5982\u6b64\u5947\u602a\uff0c\u8d77\u5148\u4e00\u526f\u58f0\u52bf\u6c79\u6c79\u3001\u76ee\u7726\u5c3d\u88c2\u7684\u6837 \u5b50\uff0c\u6b64\u523b\u5374\u53c8\u7ad9\u4efb\u8fd9\u91cc\u53d1\u5446\u2014\u2014\u201d\u7236\u5e74\u534e\u867d\u5df2\u8001\u53bb\uff0c\u5374\u4ecd\u98ce\u5ea6\u7fe9\u7fe9\uff0c\u4e0d\u77e5\u600e\u7684\u7adf\u4f1a\u642d\u4e0a\u8fd9\u79cd \u5973\u5b50\u3002 \u3000\u3000\u4ed6\u5374\u4e0d\u77e5\u9053\u90a3\u4e07\u5999\u771f\u541b\u5c39\u51e1\u4e4b\u9634\u9669\u72e1\u8bc8\uff0c\u4e16\u7f55\u5176\u5339\uff0c\u679c\u771f\u4e3a\u7740\u4e00\u4e8b\uff0c\u800c\u9a97\u4e86\u8fd9\u4e11\u4eba\u6e29\u5982 \u7389\u4e4b\u60c5\u611f\uff0c\u539f\u6765\u6e29\u5982\u7389\u6709\u751f\u4ee5\u6765\uff0c\u4ece\u672a\u6709\u8fc7\u4e00\u4e2a\u7537\u4eba\u559c\u6b22\u8fc7\u5979\uff0c\u5979\u9762\u4e0a\u867d\u7136\u6bd2\u8fa3\u602a\u50fb\uff0c\u5176\u5b9e \u5fc3\u4e2d\u53c8\u4f55\u5c1d\u4e0d\u5728\u6e34\u671b\u7740\u4e00\u4e2a\u7537\u4eba\u7684\u6e29\u60c5\u3002 \u3000\u3000\u800c\u5c39\u51e1\u5c31\u5229\u7528\u4e86\u5979\u8fd9\u4e2a\u5f31\u70b9\uff0c\u4f7f\u5f97\u5979\u5168\u5fc3\u5168\u610f\u5730\u7231\u4e0a\u81ea\u5df1\uff0c\u7b49\u5230\u4ed6\u89c9\u5f97\u5979\u4e0d\u518d\u503c\u5f97\u81ea\u5df1 \u5229\u7528\uff0c\u4fbf\u4e00\u811a\u5c06\u5979\u8e22\u5f00\u3002 \u3000\u3000\u8fd9\u5f53\u7136\u4f7f\u6e29\u5982\u7389\u75db\u82e6\u5230\u4e86\u6781\u5904\uff0c\u53ea\u662f\u60c5\u611f\u4e00\u4e8b\uff0c\u504f\u53c8\u90a3\u4e48\u5fae\u5999\uff0c\u5979\u867d\u7136\u5c06\u4ed6\u6068\u5230\u6781\u5904\uff0c \u5374\u504f\u504f\u53c8\u5fd8\u4e0d\u4e86\u4ed6\uff0c\u5e0c\u671b\u4ed6\u80fd\u56de\u5fc3\u8f6c\u610f\u3002 \u3000\u3000\u8fd9\u79cd\u590d\u6742\u800c\u5fae\u5999\u7684\u60c5\u611f\uff0c\u624d\u4f7f\u5f97\u5979\u65b9\u624d\u7684\u795e\u8272\uff0c\u751f\u51fa\u90a3\u4e48\u591a\u53d8\u5316\uff0c\u53ea\u662f\u5c91\u7cb2\u867d\u662f\u5c39\u51e1\u7684 \u5f1f\u5b50\uff0c\u5bf9\u8fd9\u6bb5\u4e8b\u5374\u4e00\u70b9\u4e5f\u4e0d\u77e5\u9053\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u5bf9\u9762\u800c\u7acb\uff0c\u5fc3\u4e2d\u5404\u6709\u6240\u601d\uff0c\u54ea\u77e5\u8fdc\u8fdc\u7ad9\u5728\u4e00\u8fb9\u7684\u5353\u957f\u537f\uff1a\u6b64\u523b\u7adf\u7a81\u7136\u4ee5\u62f3\u51fb\u638c\uff0c \u50cf\u662f\u5fc3\u4e2d\u6240\u601d\u5df2\u6709\u4e86\u51b3\u5b9a\uff0c\u62ac\u76ee\u56db\u671b\u4e00\u773c\uff0c\u4fbf\u81ea\u5982\u98de\u63a0\u6765\uff0c\u53e3\u4e2d\u5389\u559d\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u59d3\u6e29\u7684\uff0c \u4e0d\u7ba1\u4f60\u662f\u4e3a\u7740\u4ec0\u4e48\uff0c\u6211\u7239\u7239\u603b\u662f\u6b7b\u5728\u4f60\u7684\u624b\u4e0b\uff0c\u4eca\u65e5\u4f60\u6b66\u529f\u82e5\u5f3a\u80dc\u4e8e\u6211\uff0c\u90a3\u4e48\u4f60\u5c31\u4e00\u638c\u5c06\u6211 \u51fb\u6b7b\uff0c\u5426\u5219\u7684\u8bdd\uff0c\u6211\u5c31\u8981\u4ee5\u4f60\u9888\u4e0a\u4eba\u5934\uff0c\u6765\u796d\u7239\u7239\u5728\u5929\u4e4b\u7075\u3002\u201d \u3000\u3000\u6e29\u5982\u7389\u500f\u7136\u4ece\u751c\u871c\u7684\u68a6\u5e7b\u4e2d\u60ca\u9192\u8fc7\u6765\uff0c\u542c\u4ed6\u8bf4\u5b8c\u4e86\u6d3b\uff0c\u9762\u4f46\u8f6c\u5ff5\u4e00\u60f3\uff0c\u6b64\u523b\u5927\u4ec7\u5728\u524d\uff0c \u81ea\u5df1\u82e5\u754f\u7f29\u4e00\u8d70\uff0c\u53c8\u600e\u80fd\u518d\u79f0\u7537\u5b50\uff0c\u987b\u77e5\u4ed6\u672c\u662f\u81f3\u9633\u81f3\u521a\u4e4b\u4eba\uff0c\u6b63\u662f\u5b81\u6298\u6bcb\u5f2f\u7684\u6027\u683c\uff0c\u5fc3\u60f3 \u4fbf\u662f\u4eca\u65e5\u629b\u5374\u6027\u547d\uff0c\u4e5f\u8981\u548c\u8fd9\u7ea2\u8863\u5a18\u5a18\u62fc\u4e0a\u4e00\u62fc\uff0c\u4ed6\u5fc3\u4e2d\u552f\u4e00\u987e\u8651\u7684\uff0c\u53ea\u662f\u81ea\u5df1\u82e5\u6b7b\u4e86\uff0c\u53c8 \u6709\u8c01\u4f1a\u4e3a\u7239\u7239\u590d\u4ec7\u3002 \u3000\u3000\u6b64\u523b\u8fd9\u4e11\u4eba\u6e29\u5982\u7389\u7684\u8bdd\uff0c\u7adf\u8bb2\u5165\u4ed6\u7684\u5fc3\u91cc\uff0c\u4ed6\u4e00\u5446\u4e4b\u540e\uff0c\u5450\u5450\u8bf4\u9053\uff1a\u201c\u6211\u82e5\u6b7b\u4e86\uff0c\u6211\u7239 \u7239\u76f8\u77e5\u6ee1\u5929\u4e0b\uff0c\u81ea\u7136\u6709\u4eba\u4f1a\u4e3a\u4ed6\u590d\u4ec7\u7684\uff0c\u4f46\u4eca\u65e5\u6211\u82e5\u5c06\u4f60\u6740\u6b7b\uff0c\u53ea\u6015\u8fde\u4e2a\u590d\u4ec7\u7684\u4eba\u90fd\u4e0d\u4f1a\u6709 \u54e9\u3002\u201d \u3000\u3000\u4e11\u4eba\u6e29\u5982\u7389\u53cc\u76ee\u4e00\u5f20\uff0c\u5a01\u5149\u66b4\u73b0\uff0c\u4f46\u5374\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u597d\u4e2a\u76f8\u77e5\u6ee1\u5929\u4e0b\uff0c\u6211\u5012\u8981\u95ee\u95ee\u4f60\uff0c \u6211\u8001\u4eba\u5bb6\u5c06\u4f60\u7239\u7239\u51fb\u6bd9\u5df2\u6709\u5341\u5e74\uff0c\u600e\u4e48\u5c31\u6ca1\u6709\u4eba\u6765\u627e\u6211\u8001\u4eba\u5bb6\u4e3a\u4ed6\u62a5\u4ec7\u7684\uff1f\u201d \u3000\u3000\u5353\u957f\u537f\u4e0d\u7981\u53c8\u4e3a\u4e4b\u4e00\u6115\uff0c\u4e0d\u77e5\u9053\u5979\u8bf4\u6b64\u8bdd\u5230\u5e95\u662f\u4f55\u7528\u610f\uff0c\u6c89\u541f\u534a\u664c\uff0c\u7a81\u7136\u6717\u58f0\u9053\uff1a\u201c\u6211 \u4eec\u59d3\u5353\u7684\u4ee3\u4ee3\u76f8\u4f20\uff0c\u505a\u4e8b\u4f46\u6c42\u5fc3\u5b89\u800c\u5df2\uff0c\u4eca\u65e5\u6211\u82e5\u653e\u4e86\u4f60\uff0c\u4fbf\u5c06\u98df\u4e0d\u77e5\u5473\uff0c\u7761\u4e0d\u5b89\u5bdd\uff0c\u4f60\u591a \u8bf4\u4e5f\u65e0\u7528\uff0c\u4f55\u51b5\u2014\u2014\u54fc\uff0c\u4f60\u6b66\u529f\u867d\u9ad8\uff0c\u6211\u5374\u4e0d\u754f\u60e7\u4e8e\u4f60\u3002\u201d \u3000\u3000\u4e11\u4eba\u6e29\u5982\u7389\u54c8\u54c8\u5927\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u6781\uff0c\u597d\u6781\uff0c\u6211\u8001\u4eba\u5bb6\u5c31\u51b2\u7740\u4f60\u8fd9\u4efd\u5fd7\u6c14\uff0c\u5012\u662f\u8981\u7ed9\u4e2a \u4fbf\u5b9c\u7ed9\u4f60\u5360\u5360\u2014\u2014\u201d\u5979\u8bed\u58f0\u4e00\u987f\uff0c\u7b11\u5bb9\u5c3d\u655b\uff0c\u51b7\u51b7\u53c8\u9053\uff1a\u201c\u4eca\u65e5\u4f60\u82e5\u80dc\u4e0d\u4e86\u6211\u8001\u5353\u957f\u537f\u51b7\u51b7 \u4e00\u7b11\uff0c\u9053\uff1a\u201c\u9601\u4e0b\u540d\u6ee1\u5929\u4e0b\uff0c\u81ea\u7136\u4e0d\u4f1a\u5931\u4fe1\u4e8e\u6211\u4e00\u4e2a\u540e\u751f\u665a\u8f88\uff0c\u8fd9\u4e2a\u6211\u5012\u653e\u5fc3\u5f97\u5f88\uff0c\u53ea\u662f\u2014 \u2014\u201d\u4ed6\u76ee\u5149\u5411\u90a3\u4e9b\u56f4\u5728\u56db\u4fa7\u7684\u7ea2\u88f3\u5c11\u5973\u4e00\u626b\u3002 \u3000\u3000\u4e11\u4eba\u6e29\u5982\u7389\u5df2\u767d\u51b7\u53f1\u9053\uff0c\u201c\u4f60\u628a\u6211\u8001\u4eba\u5bb6\u5f53\u505a\u4ec0\u4e48\u4eba\uff0c\u96be\u9053\u6211\u8fd8\u8981\u8fd9\u4e9b\u5c0f\u4e2b\u5934\u5e2e\u5fd9\u4e0d \u6210\uff0c\u4eca\u65e5\u4f60\u6211\u4e24\u4eba\u52a8\u624b\uff0c\u8c01\u4e5f\u4e0d\u51c6\u6709\u4eba\u5e2e\u5fd9\uff0c\u5982\u679c\u4f60\u80dc\u4e86\uff0c\u4f60\u5927\u4ec7\u5f97\u62a5\uff0c\u4e5f\u2014\u2014\u201d\u5979\u8bed\u58f0\u4e00 \u987f\uff0c\u50cf\u662f\u8f7b\u5fae\u5730\u53f9\u6c14\u4e86\u4e00\u58f0\uff0c\u63a5\u9053\uff1a\u201c\u4e5f\u4e0d\u4f1a\u6709\u4eba\u627e\u4f60\u590d\u4ec7\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u4e00\u633a\u80f8\u819b\uff0c\u6717\u58f0\u63a5\u9053\uff1a\u201c\u5982\u679c\u9601\u4e0b\u80dc\u4e86\uff0c\u4e5f\u5c3d\u7ba1\u5c06\u5728\u4e0b\u9888\u4e0a\u4eba\u5934\u53d6\u53bb\u5c31\u662f\u2014\u2014\u201d \u6e29\u5982\u7389\u5fae\u4e00\u6446\u624b\uff0c\u51b7\u51b7\u7b11\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u6211\u8001\u4eba\u5bb6\u8fd8\u7b97\u7ed9\u4f60\u5360\u4ec0\u4e48\u4fbf\u5b9c\uff1f\u201d \u3000\u3000\u5353\u957f\u537f\u6014\u9053\uff1a\u201c\u90a3\u4fbf\u600e\u7684\uff1f\u201d \u3000\u3000\u5fc3\u4e2d\u4e0d\u7981\u5927\u4e3a\u5947\u602a\uff0c\u96be\u9053\u8fd9\u9b54\u5934\u5fc3\u80a0\u53d8\u4e86\u4e0d\u6210\u3002 \u3000\u3000\u5374\u542c\u6e29\u5982\u7389\u4e00\u7b11\u63a5\u9053\uff1a\u201c\u4f60\u82e5\u8d25\u5728\u6211\u7684\u624b\u4e0b\uff0c\u53ea\u8981\u4ee3\u6211\u505a\u6210\u4e00\u4e8b\uff0c\u65e5\u540e\u4f60\u518d\u7ec3\u6b66\u529f\uff0c\u4ecd \u53ef\u627e\u8001\u4eba\u5bb6\u6765\u590d\u4ec7\uff0c\u6211\u8001\u4eba\u5bb6\u4e5f\u4e0d\u4f1a\u6028\u4f60\u3002\u201d \u3000\u3000\u6b64\u8bdd\u4e00\u51fa\uff0c\u4e0d\u4f46\u5353\u957f\u537f\u5927\u51fa\u610f\u5916\uff0c\u90a3\u5c91\u7cb2\u5fc3\u4e2d\u4ea6\u81ea\u5927\u5947\uff0c\u8f6c\u5ff5\u5fd6\u9053\uff1a\u201c\u8fd9\u7ea2\u8863\u5a18\u5a18\u8981\u4ed6 \u505a\u7684\u4e8b\uff0c\u5fc5\u5b9a\u6bd4\u6b7b\u8fd8\u8981\u56f0\u96be\u5341\u500d\uff0c\u82e5\u662f\u5979\u8981\u4e0e\u6211\u8ba2\u6b64\u8d4c\u7ea6\uff0c\u6211\u518d\u4e5f\u4e0d\u4f1a\u7b54\u5e94\u5979\u7684\u3002\u201d \u3000\u3000\u4fa7\u76ee\u800c\u671b\uff0c\u53ea\u89c1\u90a3\u7384\u886b\u5c11\u5e74\u2014\u2014\u5353\u957f\u537f\u53cc\u62f3\u7d27\u63e1\uff0c\u76ee\u5149\u4f4e\u5782\uff0c\u6b63\u5728\u60f3\u7740\u5fc3\u4e8b\u3002 \u3000\u3000\u5353\u957f\u537f\u4f55\u5c1d\u4e0d\u77e5\u9053\u8fd9\u6e29\u5982\u7389\u6240\u63d0\u4e86\u4e4b\u4e8b\uff0c\u5fc5\u5b9a\u4e07\u5206\u56f0\u96be\uff0c\u4f46\u65e0\u8bba\u5982\u4f55\uff0c\u81ea\u5df1\u4eca\u65e5\u82e5\u8d25\u4e8e \u5979\u624b\u4e0b\uff0c\u4e5f\u53ea\u6709\u6b64\u6cd5\u624d\u80fd\u6709\u518d\u6b21\u5c91\u7cb2\u6697\u4e2d\u53c8\u4e00\u7b11\uff0c\u5fc3\u60f3\u8fd9\u7ea2\u8863\u5a18\u5a18\u679c\u7136\u96be\u7f20\uff0c\u5979\u8981\u662f\u8bf4\u51fa\u4e00 \u4e2a\u5353\u957f\u537f\u6839\u672c\u65e0\u6cd5\u529e\u6210\u4e4b\u4e8b\uff0c\u90a3\u5c82\u975e\u8fd8\u662f\u4e0e\u53eb\u5353\u957f\u537f\u4e0d\u80dc\u4fbf\u6b7b\u4e00\u6837\u3002 \u3000\u3000\u5353\u957f\u537f\u679c\u7136\u4ea6\u662f\u4e00\u6014\uff0c\u6717\u58f0\u9053\uff1a\u201c\u9601\u4e0b\u6240\u8bf4\u4e4b\u4e8b\uff0c\u8981\u662f\u6839\u672c\u5c31\u975e\u5728\u4e0b\u80fd\u505a\u4e4b\u4e8b\uff0c\u800c\u662f\u5f3a \u4eba\u6240\u96be\uff0c\u90a3\u4e48\u9601\u4e0b\u5c31\u6bcb\u987b\u8bf4\u51fa\u6765\uff0c\u53cd\u6b63\u6211\u5353\u957f\u537f\u6839\u672c\u672a\u5c06\u751f\u6b7b\u4e4b\u4e8b\u653e\u5728\u5fc3\u4e0a\u3002\u201d \u3000\u3000\u6e29\u5982\u7389\u62c2\u7136\u9053\uff1a\u201c\u6b64\u4e8b\u81ea\u662f\u4f60\u80fd\u529b\u6240\u53ca\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u633a\u80f8\u9053\uff1a\u201c\u6b64\u4e8b\u82e5\u662f\u5728\u4e0b\u80fd\u529b\u6240\u53ca\uff0c\u4ea6\u65e0\u4e8f\u4e8e\u5fe0\u4e49\uff0c\u5728\u4e0b\u867d\u4e0d\u624d\uff0c\u4f46\u6709\u751f\u4ee5\u6765\uff0c \u5374\u4ece\u672a\u8ba4\u4e3a\u4e00\u4e8b\u662f\u4eba\u529b\u65e0\u6cd5\u529e\u5230\u7684\u201d\u6e29\u5982\u7389\u68ee\u51b7\u7684\u9762\u76ee\u4e0a\uff0c\u6cdb\u8d77\u4e00\u4e1d\u7b11\u610f\uff0c\u9894\u9996\u9053\uff1a\u201c\u5982\u6b64 \u597d\u6781\u4e00\u4e00\u4e00\u201d\u8bdd\u58f0\u672a\u843d\uff0c\u7a81\u7136\u8eab\u5f62\u4e00\u5c55\uff0c\u7535\u4e5f\u4f3c\u7684\u63a0\u5230\u5353\u957f\u537f\u8eab\u524d\uff0c\u5de6\u638c\u659c\u5288\uff0c\u53f3\u638c\u6a2a\u5207\uff0c \u53ea\u5239\u90a3\u4e4b\u95f4\u4e24\u62db\u9f50\u51fa\u3002 \u3000\u3000\u5353\u957f\u537f\u590d\u5403\u4e00\u60ca\uff0c\u8fd9\u4e24\u62db\u4e4b\u7a81\u6765\uff0c\u867d\u7136\u5927\u51fa\u610f\u6599\u4e4b\u5916\uff0c\u4f46\u4ed6\u9762\u5bf9\u4ec7\u5bb6\uff0c\u65e9\u5df2\u6212\u5907\uff0c\u662f\u4ee5 \u6b64\u523b\u4e5f\u5e76\u4e0d\u614c\u4e71\uff0c\u53f3\u638c\u5fae\u4e00\u4f38\u7f29\uff0c\u5f15\u5f00\u5979\u659c\u51fb\u4e4b\u529b\uff0c\u811a\u4e0b\u9519\u6b65\u6ed1\u5f00\u4e09\u5c3a\uff0c\u53e3\u4e2d\u5374\u559d\u9053\uff1a\u201c\u9601 \u4e0b\u4e4b\u4e8b\u5c1a\u672a\u8bf4\u51fa\uff0c\u600e\u4e48\u5c31\u7a81\u7136\u52a8\u8d77\u624b\u6765\u3002\u201d \u3000\u3000\u6e29\u5982\u7389\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u82e5\u80dc\u4e86\u6211\uff0c\u6b64\u4e8b\u6839\u672c\u65e0\u5eb8\u518d\u8bf4\uff0c\u4f60\u82e5\u8d25\u4e86\uff0c\u6211\u4e5f\u7edd\u4e0d\u53d6\u4f60\u6027\u547d\uff0c \u5230\u90a3\u65f6\u518d\u8bf4\u4e0d\u8fdf\u3002\u201d \u3000\u3000\u53e3\u4e2d\u867d\u5728\u8bf4\u7740\u8bdd\uff0c\u4f46\u8eab\u624b\u5374\u672a\u56e0\u4e4b\u7a0d\u987f\uff0c\u7728\u773c\u4e4b\u95f4\uff0c\u638c\u5f71\u7ffb\u98de\uff0c\u5df2\u7136\u62cd\u51fa\u5341\u4f59\u638c\u3002 \u3000\u3000\u5c91\u7cb2\u672c\u5728\u9759\u542c\u8fd9\u6e29\u5982\u7389\u7a76\u7adf\u8981\u8bf4\u4ec0\u4e48\u4e8b\u6765\uff0c\u89c1\u5979\u7a81\u7136\u51fa\u624b\uff0c\u4ea6\u662f\u5927\u5947\uff0c\u4f46\u8f6c\u5ff5\u5fd6\u9053\uff1a \u201c\u8fd9\u7ea2\u8863\u5a18\u5a18\u679c\u7136\u72e0\u8fa3\uff0c\u9996\u5148\u903c\u5f97\u8fd9\u5353\u957f\u537f\u52a8\u624b\uff0c\u4ed6\u82e5\u8d25\u4e86\uff0c\u90a3\u65f6\u541b\u5b50\u4e00\u8a00\uff0c\u5feb\u9a6c\u4e00\u97ad\uff0c\u4f9d \u8fd9\u59d3\u5353\u7684\u4e2a\u6027\uff0c\u65e0\u8bba\u6e29\u5982\u7389\u8bf4\u51fa\u4efb\u4f55\u4e8b\u6765\uff0c\u4ed6\u90fd\u4e07\u4e07\u4e0d\u4f1a\u53cd\u6094\u4e0d\u505a\uff0c\u4f46\u662f\u8fd9\u7ea2\u8863\u5a18\u5a18\u8d39\u4e86\u5982 \u6b64\u5468\u7ae0\uff0c\u5374\u5230\u5e95\u662f\u8981\u90a3\u59d3\u5353\u7684\u505a\u4ec0\u4e48\u4e8b\u5462\uff1f\u201d \u3000\u3000\u5fc3\u5ff5\u81f3\u6b64\uff0c\u597d\u5947\u4e4b\u5fc3\u5927\u8d77\uff0c\u4f46\u7a81\u53c8\u60f3\u5230\u8fd9\u7ea2\u8863\u5a18\u5a18\u65b9\u624d\u559d\u4ee4\u81ea\u5df1\u7559\u4e0b\uff0c\u4e0d\u77e5\u8981\u5bf9\u81ea\u5df1\u73a9 \u4ec0\u4e48\u82b1\u6837\uff0c\u6b64\u523b\u4e58\u5979\u6b63\u5728\u52a8\u624b\u4e4b\u9645\uff0c\u81ea\u5df1\u82e5\u4e0d\u4e58\u9699\u4e00\u8d70\uff0c\u66f4\u5f85\u4f55\u65f6\uff0c\u53cd\u6b63\u662f\u65e0\u8bba\u8981\u90a3\u59d3\u5353\u7684 \u505a\u4ec0\u4e48\u4e8b\uff0c\u90fd\u4e0e\u81ea\u5df1\u65e0\u5173\uff0c\u81ea\u5df1\u53c8\u4f55\u82e6\u4e00\u5b9a\u8981\u77e5\u9053\u3002 \u3000\u3000\u4ed6\u7565\u4e00\u6743\u8861\u5229\u5bb3\uff0c\u4ec0\u4e48\u70ed\u95f9\u4e5f\u4e0d\u60f3\u770b\u4e86\uff0c\u8eab\u5f62\u4e00\u8f6c\uff0c\u65b9\u5f85\u63a0\u8d70\uff0c\u54ea\u77e5\u76ee\u5149\u52a8\u5904\uff0c\u90a3\u4e9b\u7ea2 \u88f3\u5c11\u5973\u5df2\u4e0d\u77e5\u4ec0\u4e48\u65f6\u5019\uff0c\u5728\u81ea\u5df1\u8eab\u4fa7\u56f4\u4e86\u5708\u5b50\uff0c\u4e0d\u7981\u6697\u53f9\u4e00\u58f0\uff0c\u7d22\u6027\u8d1f\u624b\u800c\u7acb\uff0c\u51dd\u76ee\u4e8e\u8fd9\u7ea2 \u8863\u5a18\u5a18\u548c\u5353\u957f\u537f\u7684\u6bd4\u6597\uff0c\u518d\u4e5f\u4e0d\u4f5c\u9003\u8d70\u7684\u5ff5\u5934\u3002 \u3000\u3000\u6e29\u5982\u7389\u500f\u7136\u62cd\u51fa\u5341\u638c\uff0c\u5979\u624b\u638c\u867d\u7136\u67af\u7626\uff0c\u4f46\u5176\u638c\u529b\u5374\u662f\u51cc\u5389\u65e0\u6bd4\u7684\uff0c\u5e26\u5f97\u5353\u957f\u537f\u5934\u4e0a\u7684 \u5934\u5dfe\uff0c\u730e\u730e\u98de\u821e\uff0c\u65b9\u624d\u5979\u548c\u8fd9\u5c11\u5e74\u7a0d\u4e00\u52a8\u624b\uff0c\u4fbf\u77e5\u9053\u4ed6\u5e74\u7eaa\u867d\u8f7b\uff0c\u6b66\u529f\u5374\u975e\u6bd4\u7b49\u95f2\uff0c\u662f\u4ee5\u62db \u62db\u4ff1\u662f\u6740\u624b\uff0c\u5341\u62db\u4e00\u8fc7\uff0c\u4fbf\u5df2\u5c3d\u5360\u5148\u673a\uff0c\u5c06\u5353\u957f\u537f\u538b\u5728\u6ee1\u5929\u638c\u5f71\u4e4b\u4e0b\uff0c\u51e0\u4e4e\u5bfb\u4e0d\u7740\u7a7a\u9699\u8fd8 \u624b\u3002 \u3000\u3000\u4f46\u4ed6\u8eab\u53d7\u4e45\u8d1f\u5929\u4e0b\u6b66\u6797\u7b2c\u4e00\u9ad8\u624b\u4e4b\u4e3e\u7684\u53f8\u7a7a\u8001\u4eba\u5341\u5e74\u4eb2\u7099\uff0c\u52a0\u4e0a\u5148\u5929\u4e4b\u8d44\uff0c\u540e\u5929\u4e4b\u8c03\uff0c \u4ff1\u662f\u597d\u5230\u6781\u5904\uff0c\u638c\u6325\u62f3\u51fb\uff0c\u5b88\u4e86\u5341\u6570\u62db\uff0c\u7a81\u7136\u5927\u559d\u4e00\u58f0\uff0c\u53cc\u638c\u4ff1\u51fa\uff0c\u5f53\u80f8\u731b\u51fb\u3002\u4ed6\u8fd9\u4e00\u62db\u867d \u7136\u7a7a\u95e8\u5927\u9732\uff0c\u5168\u8eab\u4e0a\u4e0b\u51e0\u65e0\u4e00\u5904\u4e0d\u5728\u5bf9\u65b9\u638c\u950b\u4e4b\u4e0b\uff0c\u4f46\u6e29\u5982\u7389\u76ee\u5149\u52a8\u5904\uff0c\u53ea\u89c1\u4ed6\u6307\u5c16\u659c\u5e76\uff0c \u638c\u5fc3\u5185\u9677\uff0c\u7adf\u662f\u5185\u5bb6\u767b\u5cf0\u9020\u6781\u7684\u638c\u529b\uff0c\u5fc3\u4e2d\u4e0d\u7981\u4e00\u51db\uff0c\u77e5\u9053\u81ea\u5df1\u7eb5\u7136\u80fd\u5c06\u4ed6\u4e00\u638c\u51fb\u6bd9\uff0c\u4f46\u81ea \u5df1\u524d\u80f8\u82e5\u88ab\u4ed6\u8fd9\u53cc\u638c\u51fb\u4e0b\uff0c\u4ea6\u662f\u518d\u65e0\u6d3b\u8def\u3002 \u3000\u3000\u5979\u76ee\u5149\u52a8\u5904\uff0c\u8eab\u5f62\u5df2\u968f\u638c\u98ce\u98d8\u51fa\uff0c\u4f46\u7b49\u5230\u5353\u957f\u537f\u4e00\u51fb\u4e4b\u52bf\uff0c\u5df2\u5c06\u52bf\u7aed\uff0c\u9042\u53c8\u4e00\u63a0\u800c\u524d\uff0c \u500f\u7136\u4e09\u638c\uff0c\u62cd\u5411\u4ed6\u7684\u9762\u95e8\u3002 \u3000\u3000\u5353\u957f\u537f\u95f7\u54fc\u4e00\u58f0\uff0c\u64a4\u638c\u62e7\u8eab\uff0c\u582a\u582a\u907f\u5f00\u8fd9\u4e09\u638c\uff0c\u7a81\u7136\u53cc\u638c\u540c\u51fb\uff0c\u4f46\u5374\u662f\u4e00\u4e0a\u4e00\u4e0b\uff0c\u53f3\u638c \u4e0a\u653b\u5de6\u989d\uff0c\u5de6\u638c\u4e0b\u5207\u53f3\u808b\uff0c\u4e0d\u4f46\u638c\u98ce\u547c\u547c\uff0c\u4e0d\u5728\u65b9\u624d\u90a3\u4e24\u638c\u4e4b\u4e0b\uff0c\u800c\u4e14\u638c\u5f0f\u53d8\u5316\u65e0\u4f26\uff0c\u6e29\u52a0 \u7389\u4eab\u540d\u6b66\u6797\u6570\u5341\u5e74\uff0c\u662f\u4f55\u7b49\u4eba\u7269\uff0c\u4f46\u6b64\u523b\u5374\u7adf\u4e5f\u770b\u4e0d\u51fa\u4ed6\u8fd9\u638c\u62db\u7684\u6765\u8def\uff0c\u5f53\u4e0b\u8eab\u5f62\u4e00\u52a8\uff0c\u5012 \u6253\u91d1\u949f\uff0c\u7adf\u53c8\u500f\u7136\u63a0\u51fa\u4e24\u4e08\u5f00\u5916\uff0c\u7ea2\u886b\u98d8\u821e\uff0c\u98ce\u58f0\u730e\u730e\uff0c\u5b9b\u5982\u884c\u4e91\u6d41\u6c34\u3002 \u3000\u3000\u5353\u957f\u537f\u89c1\u5979\u8eab\u5f62\u500f\u5ffd\u6765\u5f80\uff0c\u77ac\u76ee\u4e4b\u95f4\uff0c\u5df2\u8fdb\u9000\u6570\u6b21\uff0c\u5fc3\u4e0b\u4e5f\u4e0d\u7981\u9a87\u7136\uff0c\u53cc\u817f\u9489\u7acb\u5982\u67f1\uff0c \u53cc\u638c\u4e00\u62db\u8fde\u7740\u4e00\u62db\u7684\u731b\u51fb\u51fa\u6765\uff0c\u5c06\u5730\u4e0a\u7684\u7802\u571f\u90fd\u6fc0\u5f97\u98de\u626c\u800c\u8d77\uff0c\u90a3\u51dd\u65e5\u800c\u671b\u7684\u5c91\u7cb2\uff0c\u89c1\u5230\u4ed6 \u638c\u529b\u7adf\u5982\u6b64\u60ca\u4eba\uff0c\u5fc3\u4e2d\u60ca\u6012\u4ea4\u96c6\uff0c\u6697\u6697\u5fd6\u9053\uff1a\u201c\u4ee5\u4ed6\u8fd9\u79cd\u8eab\u624b\uff0c\u6b66\u6797\u4e2d\u9664\u4e86\u6709\u6570\u51e0\u4eba\u4e4b\u5916\uff0c \u8fd8\u6709\u8c01\u662f\u4ed6\u4e4b\u653e\u624b\uff0c\u60f3\u90a3\u5929\u76ee\u5c71\u4e4b\u4f1a\uff0c\u4e5f\u5fc5\u5b9a\u8981\u88ab\u4ed6\u72ec\u5360\u9ccc\u5934\u2014\u2014\u201d\u5992\u6012\u4e4b\u4e0b\uff0c\u66f4\u7acb\u5fc3\u8981\u5c06 \u6b64\u4eba\u9664\u53bb\u3002 \u3000\u3000\u5353\u957f\u537f\u8fd9\u4e00\u8f6e\u6025\u653b\uff0c\u770b\u4f3c\u867d\u5c06\u6e29\u5982\u7389\u903c\u9000\uff0c\u800c\u62a2\u5f97\u5148\u673a\uff0c\u4f46\u53ea\u8981\u81ea\u5df1\u638c\u529b\u7a0d\u6709\u7a7a\u9699\uff0c\u6e29 \u5982\u7389\u7acb\u5373\u5feb\u5982\u95ea\u7535\u7684\u6b3a\u8eab\u800c\u8fdb\uff0c\u82e5\u975e\u4ed6\u5e74\u8f7b\u529b\u5f3a\uff0c\u5185\u529b\u542b\u84c4\u53c8\u6df1\uff0c\u4fbf\u65e9\u5df2\u4e0d\u654c\u3002 \u3000\u3000\u4f46\u9976\u662f\u5982\u6b64\uff0c\u8fd9\u79cd\u5168\u51ed\u5185\u5bb6\u771f\u529b\u7684\u638c\u529b\uff0c\u7a76\u7adf\u5bb9\u6613\u4e8f\u635f\uff0c\u8d8a\u5230\u540e\u6765\uff0c\u4ed6\u5c31\u8d8a\u611f\u5403\u529b\uff0c\u53ea \u89c1\u6e29\u5982\u7389\u7ea2\u886b\u98d8\u98d8\uff0c\u8eab\u5f62\u4ece\u5bb9\u81ea\u82e5\uff0c\u800c\u4e14\u8d8a\u903c\u8d8a\u8fd1\uff0c\u4e0d\u6d88\u6570\u5341\u62db\uff0c\u5353\u957f\u537f\u4fbf\u53c8\u843d\u5728\u4e0b\u98ce\uff0c\u800c \u8fd9\u4e00\u6b21\uff0c\u4ed6\u5185\u529b\u5c06\u7aed\uff0c\u5374\u8fde\u5e73\u53cd\u4e4b\u529b\u90fd\u6ca1\u6709\u4e86\u3002 \u3000\u3000\u7ea2\u65e5\u65e2\u5347\uff0c\u9a84\u9633\u5982\u706b\uff0c\u5353\u957f\u537f\u7684\u989d\u89d2\u9f3b\u6d3c\uff0c\u4e5f\u5df2\u6c81\u51fa\u6c57\u73e0\uff0c\u4ed6\u4e0d\u7981\u6697\u4e2d\u957f\u53f9\uff0c\u77e5\u9053\u518d\u8fc7 \u6570\u5341\u62db\uff0c\u81ea\u5df1\u5c31\u5c06\u8fde\u8fd8\u624b\u4e4b\u529b\u90fd\u6ca1\u6709\u4e86\u3002 \u3000\u3000\u6b64\u523b\u4ed6\u867d\u5728\u52a8\u624b\uff0c\u4f46\u5fc3\u4e2d\u5374\u601d\u6f6e\u7ffb\u6d8c\uff0c\u60b2\u6124\u586b\u80f8\uff0c\u77e5\u9053\u4eca\u65e5\u81ea\u5df1\u590d\u4ec7\u5df2\u662f\u65e0\u671b\u4e86\u3002 \u3000\u3000\u53c8\u62c6\u4e86\u6570\u5341\u62db\uff0c\u5353\u957f\u537f\u6697\u9053\u4e00\u58f0\uff1a\u201c\u7f62\u4e86\u3002\u201d \u3000\u3000\u547c\u547c\u653b\u51fa\u4e24\u638c\uff0c\u7eb5\u8eab\u9000\u51fa\u5708\u5916\uff0c\u5782\u624b\u800c\u7acb\uff0c\u9eef\u7136\u9053\uff1a\u201c\u9601\u4e0b\u7a76\u7adf\u662f\u4f55\u4e8b\uff0c\u53ea\u7ba1\u8bf4\u51fa\u4fbf \u662f\u3002\u201d \u3000\u3000\u6e29\u5982\u7389\u957f\u8896\u4e00\u62c2\uff0c\u4ef0\u5929\u7b11\u9053\uff1a\u201c\u80dc\u5219\u80dc\uff0c\u8d25\u5219\u8d25\uff0c\u4f60\u8fd9\u5b69\u5b50\u5012\u786e\u662f\u4e2a\u78ca\u843d\u7684\u7537\u513f\u3002\u201d \u3000\u3000\u56de\u8eab\u4fa7\u76ee\u4e00\u671b\u5c91\u7cb2\uff0c\u9762\u4e0a\u7b11\u5bb9\u5c3d\u655b\uff0c\u53c8\u9053\uff1a\u201c\u6bd4\u4f60\u548c\u4f60\u5e08\u7236\u90fd\u5f3a\u5f97\u591a\u4e86\u3002\u201d \u3000\u3000\u5c91\u7cb2\u5fc3\u4e2d\u6697\u54fc\u4e00\u58f0\uff0c\u8f6c\u8fc7\u5934\u53bb\uff0c\u6545\u610f\u5411\u5bf9\u9762\u7ad9\u7740\u7684\u4e00\u4e2a\u7ea2\u88f3\u5c11\u5973\u5fae\u5fae\u4e00\u7b11\u3002 \u3000\u3000\u6e29\u5982\u7389\u76ee\u5149\u52a8\u5904\uff0c\u5bd2\u5149\u51db\u7136\uff0c\u6068\u58f0\u9053\uff1a\u201c\u679c\u771f\u4e0e\u4ed6\u5e08\u7236\u4e00\u4e2a\u6837\u5b50\u3002\u201d \u3000\u3000\u53cc\u638c\u4e00\u62cd\uff0c\u90a3\u5341\u4f59\u4e2a\u7ea2\u88f3\u5c11\u5973\u7a81\u7136\u540c\u65f6\u5a07\u7b11\u4e00\u58f0\uff0c\u5c91\u7cb2\u987f\u89c9\u773c\u524d\u5fae\u82b1\uff0c\u6f2b\u5929\u7684\u9752\u7af9\u3001\u7fbd \u6247\uff0c\u5df2\u81ea\u5f53\u5934\u538b\u4e0b\u6765\uff0c\u4ed6\u4e0d\u7528\u601d\u7d22\uff0c\u5c31\u77e5\u9053\u81ea\u5df1\u53c8\u9677\u5165\u4e86\u90a3\u9713\u88f3\u4ed9\u821e\u9635\u4e86\u3002 \u3000\u3000\u6e29\u5982\u7389\u51b7\u7b11\u4e00\u58f0\uff0c\u53cc\u638c\u53c8\u4e00\u62cd\uff0c\u90a3\u4e9b\u7ea2\u88f3\u5c11\u5973\u53e3\u4e2d\u7a81\u7136\u66fc\u58f0\u5531\u4e86\u8d77\u6765\uff0c\u8eab\u5f62\u4e5f\u8d8a\u821e\u8d8a \u75be\uff0c\u5c91\u7cb2\u53ea\u89c1\u4e00\u9053\u9053\u7ea2\u5899\u63a5\u4e8c\u8fde\u4e09\u5730\u5411\u81ea\u5df1\u538b\u4e86\u8fc7\u6765\uff0c\u65b9\u81ea\u51fb\u9000\u4e00\u9053\uff0c\u53e6\u4e00\u9053\u5c31\u8ddf\u8e2a\u800c\u6765\uff0c \u4ed6\u867d\u5df2\u9886\u6559\u8fc7\u9713\u88f3\u4ed9\u821e\u9635\u7684\u6ecb\u5473\uff0c\u4f46\u6b64\u523b\u4ea6\u4e0d\u7981\u9a87\u7136\u3002 \u3000\u3000\u5353\u957f\u537f\u95ea\u76ee\u800c\u89c6\uff0c\u53ea\u89c9\u8fd9\u4e9b\u5c11\u5973\u6b4c\u58f0\u4e00\u8d77\uff0c\u9635\u6cd5\u7684\u53d8\u5e7b\uff0c\u5c31\u66f4\u7384\u5999\u8fc5\u5feb\uff0c\u624d\u77e5\u9053\u65b9\u624d\u81ea \u5df1\u9677\u5165\u9635\u4e2d\u65f6\uff0c\u4eba\u5bb6\u4e95\u672a\u4f7f\u51fa\u5168\u529b\u6765\uff0c\u5fc3\u4e0b\u4e0d\u7981\u66f4\u60ca\uff0c\u77e5\u9053\u81ea\u5df1\u590d\u4ec7\uff0c\u53ea\u6015\u8d8a\u53d1\u56f0\u96be\u3002 \u3000\u3000\u5374\u89c1\u6e29\u5982\u7389\u773c\u671b\u7740\u56f0\u5728\u9635\u91cc\u7684\u5c91\u7cb2\uff0c\u9762\u4e0a\u53c8\u9732\u51fa\u6781\u4e3a\u5947\u7279\u7684\u795e\u8272\u6765\uff0c\u5782\u9996\u6c89\u541f\u4e86\u534a\u664c\uff0c \u65b9\u81ea\u4fa7\u76ee\u5411\u5353\u957f\u537f\uff1a\u201c\u6211\u6b64\u4e8b\u8bf4\u51fa\uff0c\u975e\u4f46\u4e0d\u662f\u52a0\u5bb3\u4e8e\u4f60\uff0c\u53cd\u5374\u662f\u4ef6\u522b\u4eba\u6c42\u4e4b\u4e0d\u5f97\u4e4b\u4e8b\uff0c\u4f60\u82e5 \u50cf\u4ed6\u4e00\u6837\u2014\u2014\u201d\u5979\u968f\u624b\u4e00\u6307\u5c91\u7cb2\uff0c\u51b7\u54fc\u4e00\u58f0\uff0c\u63a5\u9053\uff1a\u201c\u53ea\u6015\u4f60\u8dea\u5728\u5730\u4e0a\u6c42\u6211\uff0c\u6211\u8fd8\u4e0d\u7b54\u5e94 \u54e9\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u5fc3\u4e2d\u4e00\u6115\uff0c\u9762\u4e0a\u5374\u4ecd\u662f\u6728\u65e0\u8868\u60c5\uff0c\u987b\u77e5\u4ed6\u6b64\u523b\u65e2\u8d25\u4e8e\u81ea\u5df1\u4ec7\u4eba\u4e4b\u624b\uff0c\u53c8\u5f97\u542c\u547d\u4e8e \u5979\uff0c\u5fc3\u4e2d\u7f9e\u6127\u81ea\u8d23\u4e4b\u60c5\uff0c\u6b63\u662f\u65e0\u4ee5\u590d\u52a0\uff0c\u82e5\u4e0d\u662f\u5fd6\u5ff5\u81ea\u5df1\u7236\u4ec7\u672a\u62a5\uff0c\u8fde\u6b7b\u90fd\u4e0d\u80fd\uff0c\u53ea\u6015\u4ed6\u65e9 \u5df2\u5f15\u9888\u81ea\u51b3\u4e86\uff0c\u81f3\u4e8e\u6e29\u5982\u7389\u53eb\u4ed6\u6240\u505a\u4e4b\u4e8b\u662f\u597d\u662f\u574f\uff0c\u6839\u672c\u672a\u653e\u5728\u4ed6\u5fc3\u4e0a\u3002 \u3000\u3000\u4ed6\u51b7\u7136\u800c\u671b\uff0c\u53ea\u89c1\u7ea2\u8863\u5a18\u5a18\u6e29\u5982\u7389\u7a81\u7136\u957f\u53f9\u4e00\u58f0\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u6570\u5341\u5e74\u6765\uff0c\u6211\u8d39\u4e86\u65e0\u7a77\u5fc3 \u529b\uff0c\u641c\u5c3d\u5929\u4e0b\u7684\u5947\u73cd\u5f02\u5b9d\uff0c\u4e3a\u7740\u8fd9\u4e9b\u8eab\u5916\u4e4b\u7269\uff0c\u6211\u4e0d\u77e5\u9020\u4e0b\u591a\u5c11\u6740\u5b7d\uff0c\u5509\u2014\u2014\u2014\u76f4\u81f3\u6b64\u523b\uff0c \u5e74\u534e\u5df2\u53bb\uff0c\u90a3\u4e9b\u4e1c\u897f\u4ef7\u503c\u867d\u9ad8\uff0c\u5374\u53c8\u600e\u80fd\u633d\u56de\u65e2\u53bb\u7684\u9752\u6625\u2014\u2014\u201d\u5979\u8bdd\u58f0\u7a81\u7136\u4e00\u987f\uff0c\u53cc\u76ee\u51db\u7136 \u4e00\u5f20\uff0c\u7728\u4e5f\u4e0d\u7728\u5730\u671b\u5728\u5353\u957f\u537f\u9762\u4e0a\uff0c\u51b7\u7136\u63a5\u9053\uff1a\u201c\u53ea\u662f\u90a3\u4e9b\u4e1c\u897f\uff0c\u5374\u4ecd\u662f\u65e0\u4ef7\u4e4b\u5b9d\uff0c\u4e16\u4e0a\u60f3 \u6c42\u4e00\u4ef6\uff0c\u4ea6\u4e0d\u53ef\u5f97\uff0c\u6211\u8fd1\u5e74\u6765\u867d\u88ab\u4e00\u4eba\u9a97\u53bb\u4e0d\u5c11\uff0c\u4f46\u6240\u4f59\u4e4b\u7269\uff0c\u4ecd\u7136\u975e\u540c\u5c0f\u53ef\uff0c\u522b\u7684\u4e0d\u8bf4\uff0c \u5c31\u5355\u4ee5\u5b9d\u5251\u4e00\u6837\uff0c\u5c31\u5168\u90fd\u662f\u6b66\u6797\u4e2d\u4eba\u68a6\u5bd0\u4ee5\u6c42\u4e4b\u7269\uff0c\u4f60\u77e5\u9053\u5417\uff1f\u201d \u3000\u3000\u5353\u957f\u537f\u832b\u7136\u70b9\u4e86\u70b9\u5934\uff0c\u5979\u4fbf\u53c8\u63a5\u9053\uff1a\u201c\u6211\u4e4b\u4e00\u751f\uff0c\u5b64\u50fb\u5be1\u5408\uff0c\u5e38\u4eba\u53ea\u8981\u7a0d\u62c2\u6211\u610f\uff0c\u6211\u4fbf \u4e00\u638c\u51fb\u6bd9\uff0c\u662f\u4ee5\u6b66\u6797\u4e4b\u4eba\uff0c\u5f53\u7740\u6211\u9762\uff0c\u90fd\u5c0a\u79f0\u6211\u4e00\u58f0\u7ea2\u8863\u5a18\u5a18\u3001\u7ea2\u8863\u4ed9\u4e8e\uff0c\u4f46\u5374\u6ca1\u6709\u4e00\u4e2a\u4e0d \u5728\u80cc\u540e\u5c06\u6211\u9a82\u5f97\u4f53\u65e0\u5b8c\u80a4\uff0c\u54fc\uff0c\u53ea\u662f\uff0c\u90a3\u4e9b\u5bb6\u4f19\u4ff1\u662f\u732a\u72d7\u4e0d\u5982\uff0c\u65e0\u8bba\u4ed6\u4eec\u600e\u4e48\u9a82\u6211\u90fd\u4e0d\u653e\u5728 \u5fc3\u4e0a\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u89c1\u5979\u8d8a\u626f\u8d8a\u8fdc\uff0c\u5fc3\u4e0b\u6b63\u662f\u4e0d\u8010\uff0c\u5374\u542c\u5979\u53c8\u53f9\u9053\uff1a\u201c\u8fd9\u4e9b\u6d3b\u6211\u4e00\u751f\u4e4b\u4e2d\uff0c\u4ece\u672a\u5bf9\u4eba \u8bf4\u8fc7\uff0c\u4eca\u65e5\u4e0d\u77e5\u600e\u4e48\u7adf\u5bf9\u4f60\u8bf4\u4e86\u51fa\u6765\uff0c\u4e5f\u8bb8\u662f\u6211\u5e74\u8f7b\u7684\u65f6\u5019\uff0c\u813e\u6c14\u4e5f\u8ddf\u4f60\u4e00\u6837\uff0c\u662f\u4e2a\u5b81\u6298\u6bcb \u5f2f\u7684\u987b\u77e5\u4ed6\u60c5\u611f\u6781\u4e3a\u4e30\u5bcc\uff0c\u662f\u4ee5\u6b64\u523b\u624d\u6709\u8fd9\u79cd\u5fc3\u60c5\uff0c\u4ea6\u81ea\u7f13\u7f13\u79fb\u52a8\u811a\u6b65\uff0c\u8ddf\u4e86\u8fc7\u53bb\uff0c\u53ea\u89c1\u5979 \u6c89\u91cd\u5730\u5750\u5728\u8f66\u4e0a\uff0c\u50cf\u662f\u5979\u8870\u8001\u7684\u4e00\u751f\u4e4b\u4e2d\u7684\u4e00\u8fde\u4e32\u5bc2\u5bde\u7684\u5c81\u6708\uff0c\u5df2\u4f7f\u5f97\u5979\u6b64\u523b\u6781\u4e3a\u75b2\u5026\uff0c\u4e16 \u95f4\u65e0\u8bba\u4efb\u4f55\u4eba\uff0c\u53c8\u8fd8\u6709\u54ea\u4e00\u4ef6\u66f4\u6bd4\u5bc2\u5bde\u4ee4\u4eba\u96be\u4ee5\u5fcd\u53d7\u7684\u5462\uff1f\u54ea\u77e5\u5979\u65b9\u81ea\u5750\u5230\u8f66\u4e0a\uff0c\u76ee\u5149\u7a81\u53c8 \u4e00\u51db\uff0c\u68ee\u51b7\u7684\u9053\uff1a\u201c\u4f60\u82e5\u4e0d\u9075\u8bfa\u8a00\uff0c\u6211\u4e00\u6837\u8fd8\u662f\u8981\u4f60\u7684\u547d\uff0c\u54fc\uff0c\u4f60\u83ab\u4ee5\u4e3a\u6211\u771f\u7684\u5bf9\u4f60\u597d\u2014 \u2014\u201d\u5353\u957f\u537f\u4e0d\u7981\u53c8\u4e00\u6115\uff0c\u5fc3\u60f3\u8fd9\u7ea2\u8863\u5a18\u5a18\u6027\u60c5\u771f\u662f\u4ee4\u4eba\u96be\u4ee5\u6349\u6478\uff0c\u5374\u89c1\u5979\u8eab\u5f62\u4e00\u5012\u9760\u5728\u8f66\u7684 \u4e1d\u57ab\u4e0a\uff0c\u7728\u773c\u4e4b\u95f4\uff0c\u53c8\u4eff\u4f5b\u8870\u8001\u8bb8\u591a\uff0c\u8001\u5f97\u4ee4\u4eba\u96be\u4ee5\u76f8\u4fe1\u5979\u662f\u4e2a\u9707\u6151\u6b66\u6797\u7684\u9b54\u5934\u3002 \u3000\u3000\u53ea\u89c1\u5979\u53cc\u6708\u5f20\u5f00\u4e00\u7ebf\uff0c\u4ef0\u89c6\u7740\u767d\u4e91\u82cd\u7a79\uff0c\u6c89\u601d\u4e86\u7247\u523b\uff0c\u53c8\u9053\uff1a\u201c\u6211\u4e00\u751f\u4e4b\u4e2d\uff0c\u6068\u5c3d\u5929\u4e0b \u4eba\uff0c\u5929\u4e0b\u4eba\u4e5f\u6068\u5c3d\u6211\uff0c\u5012\u53ea\u6709\u4e00\u4eba\uff0c\u5374\u662f\u6211\u771f\u5fc3\u7231\u7740\u7684\uff0c\u4e3a\u4e86\u4ed6\uff0c\u53eb\u6211\u7acb\u523b\u53bb\u6b7b\uff0c\u6211\u4e5f\u4e0d\u4f1a \u7a0d\u6709\u72b9\u8c6b\u2014\u2014\u201d\u8bf4\u5230\u6b64\u5904\uff0c\u5979\u9762\u4e0a\u7adf\u53c8\u6ee1\u542b\u6e29\u60c5\u4e4b\u610f\uff0c\u5353\u957f\u537f\u6697\u53f9\u4e00\u58f0\uff0c\u5fc3\u91cc\u5374\u5947\u602a\uff0c\u80fd\u88ab \u8fd9\u5973\u9b54\u5934\u6df1\u6df1\u7231\u7740\u7684\uff0c\u53c8\u662f\u4ec0\u4e48\u4eba\u5462\uff1f\u8f6c\u5ff5\u4e00\u60f3\uff1a\u53c8\u60f3\u5230\u4e0d\u7ba1\u8fd9\u4eba\u662f\u8c01\uff0c\u4e0e\u6211\u53c8\u6709\u4f55\u5173\u7cfb\uff0c \u4e0d\u7981\u53c8\u6697\u9a82\u81ea\u5df1\uff0c\u600e\u4e48\u5bf9\u8fd9\u6740\u7236\u7684\u4ec7\u4eba\u751f\u51fa\u540c\u60c5\u4e4b\u5fc3\u6765\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u76ee\u5149\u4e00\u51db\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u9601\u4e0b\u7a76\u7adf\u6709\u4f55\u4e8b\u2014\u2014\u201d\u54ea\u77e5\u6e29\u5982\u7389\u6839\u672c\u6ca1\u6709\u542c\u5230\u4ed6\u7684\u8bdd\uff0c \u4ecd\u7136\u81ea\u7ba1\u81ea\u7684\u8bf4\u4e0b\u53bb\uff0c\u9053\uff1a\u201c\u4f60\u662f\u4e2a\u6b63\u76f4\u800c\u5014\u5f3a\u7684\u5b69\u5b50\uff0c\u6240\u4ee5\u6211\u624d\u544a\u8bc9\u4f60\uff0c\u6211\u6240\u6df1\u7231\u7684\u4eba\uff0c \u5c31\u662f\u6211\u90a3\u552f\u4e00\u201d\u7684\u5f92\u5f1f\uff0c\u90a3\u5929\u5728\u59cb\u4fe1\u5cf0\u4e0a\u4e0b\uff0c\u60f3\u5fc5\u4f60\u4e5f\u89c1\u8fc7\u4e86\u5979\uff0c\u53ea\u8981\u4f60\u4e0d\u662f\u778e\u5b50\uff0c\u4f60\u603b\u8be5 \u770b\u51fa\u5979\u662f\u591a\u4e48\u7f8e\u4e3d\uff0c\u6211\u4e00\u751f\u4e4b\u4e2d\u89c1\u8fc7\u7684\u5973\u4eba\u867d\u4e0d\u5c11\uff0c\u4f46\u662f\u5374\u4ece\u672a\u89c1\u8fc7\u4e00\u4e2a\u4eba\u6bd4\u5979\u66f4\u597d\u770b\u7684 \u4eba\u3002\u201c\u5979\u5fae\u5fae\u4e00\u53f9\uff0c\u53c8\u9053\uff1a\u201c\u53ea\u662f\u8fd9\u5b69\u5b50\u8868\u9762\u867d\u6e29\u67d4\uff0c\u9aa8\u5b50\u91cc\u5374\u5014\u5f3a\u5f97\u5f88\uff0c\u8ddf\u6211\u4e00\u6837\uff0c\u662f\u5929 \u751f\u7684\u574f\u813e\u6c14\uff0c\u6709\u8fd9\u6837\u7684\u813e\u6c14\u7684\u4eba\uff0c\u5c31\u7b97\u5979\u7684\u6b66\u529f\u518d\u9ad8\uff0c\u8fd8\u662f\u8981\u4e00\u751f\u53d7\u82e6\uff0c\u6211\u81ea\u5df1\u77e5\u9053\u6211\u5e74\u5df2 \u8001\u4e86\uff0c\u6d3b\u4e0d\u957f\u4e86\uff0c\u5c31\u5f00\u59cb\u4e3a\u5979\u62c5\u5fc3\uff0c\u4e0d\u77e5\u9053\u5979\u5c06\u6765\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u8fd9\u540d\u6151\u5929\u4e0b\u7684\u9b54\u5934\uff0c\u6b64\u523b\u659c\u501a\u9999\u8f66\u4e4b\u4e0a\uff0c\u7ade\u5a13\u5a13\u4e0e\u5353\u957f\u537f\u8bdd\u8d77\u5bb6\u5e38\u6765\u4e86\uff0c\u5374\u5c06\u5979\u7a76\u7adf\u8981 \u5353\u957f\u537f\u505a\u7684\u4ec0\u4e48\u4e8b\u4e00\u5b57\u4e0d\u63d0\u3002 \u3000\u3000\u5353\u957f\u537f\u5fc3\u4e2d\u8d8a\u542c\u8d8a\u662f\u4e0d\u8010\u70e6\uff0c\u4f46\u4e0d\u77e5\u600e\u4e48\u5374\u4e0d\u5fcd\u6253\u65ad\u5979\u7684\u8bdd\u3002 \u3000\u3000\u4ed6\u5374\u4e0d\u77e5\u90a3\u88ab\u56f0\u5728\u9713\u88f3\u4ed9\u821e\u9635\u4e2d\u7684\u5c91\u7cb2\uff0c\u5fc3\u4e2d\u7684\u6025\u8e81\uff0c\u66f4\u8fd8\u5728\u4ed6\u4e4b\u4e0a\uff0c\u53ea\u6068\u4e0d\u5f97\u4ece\u90a3\u7af9 \u98ce\u6247\u5f71\u4e4b\u4e2d\u98de\u8eab\u800c\u51fa\uff0c\u98de\u5230\u8fd9\u91cc\u6765\u542c\u6e29\u52a0\u7389\u5230\u5e95\u5728\u8bf4\u4e9b\u4ec0\u4e48\u3002 \u3000\u3000\u4f46\u4ed6\u8f7b\u529f\u867d\u9ad8\uff0c\u6b64\u523b\u5374\u88ab\u90a3\u4e9b\u65cb\u821e\u7740\u7684\u5c11\u5973\u903c\u5f97\u5bf8\u6b65\u96be\u884c\uff0c\u4ed6\u76ee\u5149\u659c\u8198\u5904\uff0c\u53ea\u89c1\u90a3\u7ea2\u8863 \u5a18\u5a18\u5a13\u5a13\u800c\u8a00\uff0c\u800c\u90a3\u5353\u957f\u537f\u5374\u5728\u5782\u80b2\xb7\u9759\u542c\uff0c\u5fc3\u91cc\u66f4\u5947\u602a\uff0c\u4e0d\u77e5\u5979\u7a76\u7adf\u5728\u8bf4\u4ec0\u4e48\uff0c\u6025\u8e81\u4e4b \u4e0b\uff0c\u51fa\u624b\u4fbf\u6025\uff0c\u4f46\u9976\u662f\u4ed6\u4f7f\u5c3d\u5168\u529b\uff0c\u5374\u4e5f\u4e0d\u80fd\u8131\u8eab\u800c\u51fa\u3002 \u3000\u3000\u4e00\u6bb5\u65f6\u95f4\u8fc7\u540e\uff0c\u4ed6\u53d1\u73b0\u8fd9\u4e9b\u7ea2\u886b\u5c11\u5973\u7684\u8eab\u5f62\u867d\u4ecd\u8f6c\u52a8\u4e0d\u606f\uff0c\u4f46\u5374\u5e76\u4e0d\u5b58\u5fc3\u4f24\u4ed6\uff0c\u53ea\u662f\u5c06 \u4ed6\u5c42\u5c42\u56f4\u4f4f\u800c\u5df2\uff0c\u4e8e\u662f\u4ed6\u51fa\u624b\u4e4b\u95f4\uff0c\u4fbf\u53ea\u653b\u4e0d\u5b88\uff0c\u8fd9\u4e48\u4e00\u6765\uff0c\u5a01\u529b\u867d\u589e\u5f3a\u4e00\u500d\uff0c\u5374\u4e5f\u4ecd\u7136\u65e0 \u6cd5\u4f24\u5f97\u4e86\u522b\u4eba\u3002 \u3000\u3000\u4ed6\u6b66\u529f\u867d\u4e0d\u5f31\uff0c\u6b64\u523b\u6c14\u529b\u5374\u5df2\u89c9\u7740\u4e0d\u652f\uff0c\u5fc3\u91cc\u60f3\u5230\uff0c\u65b9\u624d\u5353\u957f\u537f\u6492\u624b\u8ba4\u8f93\u4e8b\uff0c\u4ea6\u81ea\u6697\u53f9 \u4e00\u58f0\uff1a\u201c\u7f62\u4e86\u3002\u201d \u3000\u3000\u8eab\u5f62\u4e00\u505c\uff0c\u4e0d\u518d\u51fa\u624b\u3002 \u3000\u3000\u54ea\u77e5\u8eab\u524d\u8eab\u540e\uff0c\u8eab\u5de6\u8eab\u53f3\uff0c\u4e00\u4e9b\u5e76\u4e0d\u81f4\u547d\u7684\u5730\u65b9\uff0c\u5c31\u5728\u4ed6\u505c\u4e0b\u8eab\u5f62\u7684\u90a3\u4e00\u5239\u90a3\uff0c\u4fbf\u5df2\u8f7b \u8f7b\u7740\u4e86\u5341\u6570\u638c\uff0c\u8033\u7554\u53ea\u542c\u90a3\u4e9b\u5c11\u5973\u5a07\u58f0\u7b11\u9053\uff1a\u201c\u770b\u4f60\u8fd8\u86ee\u50cf\u6837\u7684\uff0c\u600e\u4e48\u8fd9\u4e48\u4e0d\u4e2d\u7528\u5440\uff1f\u201d \u3000\u3000\u6253\u5f97\u867d\u8f7b\uff0c\u7b11\u5f97\u867d\u751c\uff0c\u4f46\u6253\u5728\u5c91\u7cb2\u8eab\u4e0a\uff0c\u542c\u5728\u5c91\u7cb2\u8033\u91cc\uff0c\u76f4\u6bd4\u780d\u4ed6\u4e00\u5200\u8fd8\u96be\u53d7\uff0c\u6b64\u523b\u4ed6 \u7eb5\u7136\u8981\u88ab\u6d3b\u6d3b\u7d2f\u6b7b\uff0c\u5374\u518d\u4e5f\u4e0d\u4f1a\u505c\u624b\u7684\u4e86\uff0c\u72c2\u543c\u4e00\u58f0\uff0c\u653b\u51fa\u6570\u638c\uff0c\u4f46\u5f3a\u5f29\u4e4b\u672a\uff0c\u4e0d\u80fd\u7a7f\u9c81 \u7f1f\uff0c\u4ed6\u867d\u5b58\u5fc3\u62fc\u547d\uff0c\u5374\u4e5f\u65e0\u7528\u3002 \u7b2c\u4e5d\u7ae0\u3000\u5584\u6076\u96be\u5206>> \u53e4\u9f99\u300a\u6708\u5f02\u661f\u90aa\u300b \u7b2c\u4e5d\u7ae0\u3000\u5584\u6076\u96be\u5206 \u3000\u3000\u8fd9\u4e00\u58f0\u72c2\u543c\u5353\u957f\u537f\u5fae\u5fae\u4e00\u6014\uff0c\u65b9\u5f85\u8f6c\u9996\u800c\u671b\uff0c\u5374\u542c\u90a3\u7ea2\u8863\u5a18\u5a18\u6e29\u5982\u7389\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u542c \u5230\u6211\u8bf4\u7684\u6d3b\u6ca1\u6709\uff1f\u201d \u3000\u3000\u5353\u957f\u537f\u6697\u53f9\u4e00\u58f0\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u5c0f\u53ef\u6b63\u5728\u542c\u7740\u3002\u201d \u3000\u3000\u4ed6\u5fc3\u4e2d\u867d\u5bf9\u8fd9\u6e29\u5982\u7389\u51b7\u51b7\u800c\u53f1\u8d23\u7684\u8bed\u6c14\u6781\u4e3a\u4e0d\u6ee1\uff0c\u4f46\u662f\u4ed6\u4e43\u7980\u6027\u521a\u76f4\u4e4b\u4eba\uff0c\u60f3\u5230\u81ea\u5df1\u5df2 \u6bc1\u4e8e\u6b64\u4eba\u4e4b\u624b\uff0c\u53c8\u6709\u8bfa\u8a00\u5728\u5148\uff0c\u81ea\u5df1\u6b64\u523b\u4fbf\u5f97\u542c\u547d\u4e8e\u5979\uff0c\u662f\u4ee5\u4fbf\u5c06\u5fc3\u4e2d\u6012\u706b\u5f3a\u5fcd\u4e0b\u53bb\u3002 \u3000\u3000\u6e29\u5982\u7389\u51b7\u54fc\u4e00\u58f0\uff0c\u5ffd\u53c8\u53f9\u9053\uff1a\u201c\u6211\u90a3\u5f92\u5f1f\u5e74\u7eaa\u6781\u5c0f\u7684\u65f6\u5019\uff0c\u7239\u7239\u5988\u5988\u5c31\u5168\u90e8\u6b7b\u4e86\uff0c \u5979\u2026\u2026\u201d \u3000\u3000\u8bed\u58f0\u7a81\u7136\u4e00\u987f\uff0c\u5353\u957f\u537f\u62ac\u773c\u671b\u53bb\uff0c\u53ea\u89c1\u8fd9\u540d\u6ee1\u5929\u4e0b\u7684\u9b54\u5934\uff0c\u76ee\u5149\u4e4b\u4e2d\uff0c\u77ac\u606f\u4e4b\u95f4\u5df2\u6362\u4e86 \u6570\u79cd\u53d8\u5316\uff0c\u6b64\u523b\u76ee\u4e2d\u7adf\u6ee1\u542b\u7740\u4e00\u79cd\u5e7d\u6028\u3001\u81ea\u8d23\u7684\u795e\u8272\uff0c\u5353\u957f\u537f\u5fc3\u4e2d\u4e0d\u7981\u5927\u5947\uff1a\u201c\u8fd9\u9b54\u5934\u6614\u65e5 \u96be\u9053\u4e5f\u6709\u7740\u4ec0\u4e48\u4f24\u5fc3\u4e4b\u4e8b\uff1f\u201d \u3000\u3000\u5374\u89c1\u5979\u957f\u53f9\u4e00\u58f0\uff0c\u53c8\u9053\uff1a\u201c\u5979\u751a\u81f3\u8fde\u5979\u7684\u7239\u7239\u5988\u5988\u7684\u59d3\u540d\u90fd\u4e0d\u77e5\u9053\uff0c\u6211\u5c31\u66ff\u5979\u53d6\u4e86\u4e2a\u540d \u5b57\uff0c\u53eb\u505a\u6e29\u7406\uff0c\u4f60\u8bf4\uff0c\u6211\u53d6\u7684\u8fd9\u540d\u5b57\u53ef\u8fd8\u597d\u542c\u5417\uff1f\u201d \u3000\u3000\u5353\u957f\u537f\u53c8\u662f\u4e00\u6115\uff0c\u832b\u7136\u70b9\u4e86\u70b9\u5934\uff0c\u6e29\u5982\u7389\u4e11\u964b\u7684\u4e25\u5cfb\u7684\u8138\u4e0a\u5fae\u7b11\u4e00\u4e0b\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4e9b\u5e74 \u6765\uff0c\u747e\u513f\u4e00\u76f4\u8ddf\u7740\u6211\uff0c\u5e74\u7eaa\u4e00\u5e74\u6bd4\u4e00\u5e74\u5927\u4e86\uff0c\u8138\u4e0a\u7684\u7b11\u5bb9\u5374\u4e00\u5e74\u6bd4\u4e00\u5e74\u5c11\u4e86\uff0c\u5979\u8fd8\u4e0d\u5230\u5fe7\u90c1 \u7684\u5e74\u7eaa\uff0c\u5374\u8fd8\u6bd4\u522b\u4eba\u8981\u5fe7\u6101\u5f97\u591a\uff0c\u6211\u95f4\u5979\u4e3a\u4ec0\u4e48\uff0c\u5979\u5634\u91cc\u4e0d\u8bf4\uff0c\u6211\u5fc3\u91cc\u5374\u77e5\u9053\uff0c\u5979\u662f\u5728\u611f\u6000 \u8eab\u4e16\uff0c\u4f60\u60f3\u60f3\uff0c\u4e00\u4e2a\u5e74\u7eaa\u8f7b\u8f7b\u7684\u5b69\u5b50\uff0c\u6e05\u4e86\u8bb8\u591a\u5e74\uff0c\u5374\u8fde\u5979\u4eb2\u751f\u7236\u6bcd\u7684\u59d3\u540d\u90fd\u4e0d\u77e5\u9053\uff0c\u8fd9\u8be5 \u662f\u4ef6\u591a\u4e48\u60e8\u7684\u4e8b\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u6697\u53f9\u4e00\u58f0\uff0c\u5fd6\u9053\uff1a\u201c\u539f\u6765\u90a3\u5929\u771f\u5201\u86ee\u7684\u5973\u5b50\uff0c\u8eab\u4e16\u5374\u5982\u6b64\u51c4\u51c9\u53ef\u601c\uff01\u201d \u3000\u3000\u5fc3\u4e0b\u4e0d\u7981\u5bf9\u5979\u5927\u8d77\u540c\u60c5\u4e4b\u5fc3\uff0c\u8f6c\u5ff5\u4e00\u60f3\uff0c\u81ea\u5df1\u53c8\u4f55\u5c1d\u4e0d\u662f\u4e2a\u65e0\u7236\u65e0\u6bcd\u7684\u5b64\u513f\uff0c\u800c\u81ea\u5df1\u4e0d \u5171\u6234\u5929\u7684\u4ec7\u4eba\uff0c\u6b64\u523b\u5374\u6b63\u5728\u81ea\u5df1\u7684\u9762\u524d\u2026\u2026\u4e00\u65f6\u4e4b\u95f4\uff0c\u4ed6\u5fc3\u4e2d\u601d\u6f6e\u6570\u8f6c\uff0c\u4e0d\u89c9\u53c8\u60f3\u5f97\u75f4\u4e86\u3002 \u3000\u3000\u6e29\u5982\u7389\u76ee\u5149\u8f6c\u5904\uff0c\u7a81\u53c8\u68ee\u51b7\u5982\u5251\uff0c\u5728\u5353\u957f\u537f\u9762\u524d\u4e00\u626b\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u5fc3\u91cc\u5728\u60f3\u7740\u4ec0 \u4e48\uff1f\u201d\u5353\u957f\u537f\u9661\u7136\u4e00\u60ca\uff0c\u6e29\u5982\u7389\u53c8\u9053\uff1a\u201c\u96be\u9053\u4f60\u4ee5\u4e3a\u6211\u4e0d\u77e5\u9053\u5417\uff1f\u2014\u2014\u54fc\u54fc\uff0c\u6211\u8001\u4eba\u5bb6\u6740\u4eba \u65e0\u6570\uff0c\u53ef\u4ece\u672a\u6709\u8fc7\u4e00\u4eba\u6562\u6765\u590d\u4ec7\uff0c\u4f60\u65e2\u6709\u5982\u6b64\u5b5d\u5fc3\uff0c\u53c8\u6709\u5982\u6b64\u8c6a\u6c14\uff0c\u6211\u8001\u4eba\u5bb6\u603b\u6709\u6210\u5168\u4f60\u7684 \u4e00\u5929\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u5fc3\u4e2d\u53c8\u4e00\u6115\uff0c\u6697\u5fd6\u9053\uff1a\u201c\u6b64\u8bdd\u4f55\u610f\uff1f\u201d \u3000\u3000\u5374\u89c1\u5979\u51b7\u7b11\u4e00\u58f0\uff0c\u53c8\u9053\uff1a\u201c\u53ea\u662f\u73b0\u5728\u4f60\u5374\u5f97\u597d\u597d\u542c\u7740\u6211\u7684\u8bdd\uff0c\u4e0d\u4f46\u773c\u775b\u4e0d\u8981\u671b\u5411\u4ed6\u5904\uff0c \u5fc3\u91cc\u4e5f\u4e0d\u5f97\u4e71\u60f3\u5fc3\u601d\uff0c\u5982\u82e5\u4e0d\u7136\u2014\u2014\u54fc\u54fc\uff01\u201d \u3000\u3000\u5353\u957f\u537f\u5251\u7709\u4e00\u8f69\uff0c\u80f8\u4e2d\u6012\u6c14\u5927\u4f5c\uff0c\u4f46\u8f6c\u5ff5\u4e00\u60f3\uff0c\u4e0d\u7981\u53c8\u81ea\u957f\u53f9\u9053\uff1a\u201c\u90a3\u6e29\u747e\u7684\u8eab\u4e16\u6027 \u683c\uff0c\u4e0e\u5c0f\u53ef\u5e76\u65e0\u5173\u7cfb\uff0c\u9601\u4e0b\u8fd8\u662f\u5148\u5c06\u5bf9\u5c0f\u53ef\u7684\u5429\u5490\u8bf4\u51fa\u2014\u2014\u201d\u6e29\u5982\u7389\u7a81\u7136\u6cdb\u4e00\u4e2a\u5947\u602a\u7684\u7b11 \u5bb9\uff0c\u63a5\u53e3\u9053\uff1a\u201c\u747e\u513f\u7684\u8eab\u4e16\u6027\u683c\u6b64\u523b\u867d\u7136\u4e0e\u4f60\u65e0\u5173\uff0c\u53ef\u662f\u65e5\u540e\u5374\u5927\u6709\u5173\u7cfb\u4e86\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u5927\u5947\u9053\uff1a\u201c\u6b64\u8bdd\u600e\u8bb2\uff1f\u201d \u3000\u3000\u54ea\u77e5\u6e29\u5982\u7389\u4f38\u51fa\u67af\u7626\u7684\u624b\u638c\uff0c\u7406\u4e86\u7406\u88ab\u98ce\u5439\u4e71\u7684\u9b13\u53d1\uff0c\u5374\u4e0d\u56de\u7b54\u4ed6\u7684\u8bdd\uff0c\u53ea\u7ba1\u63a5\u7740\u8bf4 \u9053\uff1a\u201c\u6211\u4e45\u5c45\u82d7\u7586\uff0c\u8db3\u8ff9\u5f88\u5c11\u5230\u6c5f\u5357\u6765\uff0c\u747e\u513f\u4fbf\u4e5f\u8ddf\u7740\u6211\uff0c\u4ece\u6765\u6ca1\u6709\u79bb\u5f00\u8fc7\u4e00\u6b65\uff0c\u6211\u770b\u5979\u4e00 \u5e74\u6bd4\u4e00\u5e74\u5fe7\u90c1\uff0c\u5c31\u60f3\u5c3d\u4e86\u5404\u79cd\u529e\u6cd5\u6765\u4f7f\u5979\u5f00\u5fc3\u4e9b\uff0c\u54ea\u77e5\u5979\u8868\u9762\u9732\u51fa\u7b11\u5bb9\uff0c\u5fc3\u91cc\u5374\u8fd8\u662f\u4e0d\u5feb \u6d3b\uff01\u201d \u3000\u3000\u5353\u957f\u537f\u6697\u53f9\u4e00\u58f0\uff0c\u5fd6\u9053\uff1a\u201c\u8fd9\u4e11\u4eba\u6e29\u5982\u7389\u72e0\u6bd2\u4e00\u751f\uff0c\u5374\u6599\u4e0d\u5230\u5979\u7adf\u4f1a\u5bf9\u4e00\u4e2a\u5973\u5b69\u5982\u6b64\u6e29 \u67d4\uff0c\u5e08\u7236\u5e38\u8bf4\uff1a\u4e16\u4e0a\u65e0\u8bba\u4efb\u4f55\u51f6\u6b8b\u72e0\u6bd2\u4e4b\u4eba\uff0c\u5fc3\u4e2d\u5374\u603b\u6709\u5584\u826f\u7684\u4e00\u9762\u3002\u6211\u5148\u8fd8\u4e0d\u4fe1\uff0c\u6b64\u523b\u624d \u77e5\u9053\u8fd9\u8bdd\u662f\u679c\u7136\u5bf9\u7684\u4e86\u3002\u201d \u3000\u3000\u53c8\u60f3\u5230\uff1a\u201c\u6e29\u747e\u867d\u7136\u8eab\u4e16\u51c4\u6614\uff0c\u5374\u6709\u4e2a\u5e08\u7236\u5bf9\u5979\u5982\u6b64\u597d\uff0c\u5979\u4e5f\u7b97\u662f\u4e2a\u5e78\u798f\u7684\u4eba\u4e86\u3002\u201d \u3000\u3000\u6b64\u523b\u4ed6\u773c\u524d\u4f3c\u4e4e\u53c8\u6cdb\u51fa\u90a3\u7ea2\u88f3\u5c11\u5973\u6e29\u747e\u7f8e\u5982\u6625\u82b1\u822c\u7684\u7b11\u5bb9\uff0c\u8fd9\u6e29\u5982\u7389\u7684\u8a00\u8bed\u867d\u4e45\u4e45\u6ca1\u6709 \u5f52\u5165\u6b63\u9898\uff0c\u4ed6\u7adf\u4e5f\u672a\u89c9\u4e0d\u8010\u3002 \u3000\u3000\u6e29\u5982\u7389\u76ee\u5149\u4e00\u62ac\uff0c\u53c8\u9053\uff1a\u201c\u6709\u4e00\u5929\uff0c\u747e\u513f\u5ffd\u7136\u8dd1\u6765\u8981\u6c42\u6211\uff0c\u8bf4\u5979\u60f3\u8981\u89c1\u4e00\u89c1\u5929\u4e0b\u82f1\u96c4\uff0c \u6211\u548c\u5979\u81ea\u5e7c\u76f8\u5904\uff0c\u522b\u4eba\u4e0d\u6562\u5728\u6211\u9762\u524d\u8bf4\u7684\u8bdd\uff0c\u5979\u90fd\u6562\u8bf4\uff0c\u53ef\u662f\u63d0\u51fa\u8fd9\u4e2a\u8981\u6c42\u6765\uff0c\u6211\u5374\u6115\u4f4f \u4e86\uff0c\u8bd5\u60f3\u6211\u6e29\u52a0\u7389\u4e00\u751f\u4e4b\u4e2d\uff0c\u666e\u5929\u4e4b\u4e0b\uff0c\u90fd\u662f\u6068\u6211\u6015\u6211\u7684\u4eba\uff0c\u6211\u53c8\u600e\u80fd\u4e3a\u5979\u627e\u6765\u5929\u4e0b\u6240\u6709\u7684 \u82f1\u96c4\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u5979\u4ece\u6765\u6ca1\u6709\u5bf9\u6211\u63d0\u8fc7\u8981\u6c42\uff0c\u6b64\u523b\u5979\u65e2\u7136\u8bf4\u4e86\u51fa\u6765\uff0c\u6211\u53c8\u600e\u80fd\u62d2\u7edd\uff0c\u5f53\u65f6\u6211\u60f3\u4e86\u8bb8 \u4e45\uff0c\u4e5f\u6ca1\u6709\u60f3\u51fa\u4e00\u4e2a\u529e\u6cd5\u6765\u3002\u201d \u3000\u3000\u5979\u8bdd\u58f0\u5fae\u5fae\u4e00\u987f\uff0c\u53c8\u9053\uff1a\u201c\u6709\u4e00\u5929\uff0c\u6211\u9759\u5750\u4e4b\u4e2d\uff0c\u56de\u5ff5\u65e7\u4e8b\uff0c\u5ffd\u7136\u60f3\u5230\u90a3\u6b21\u9ec4\u5c71\u59cb\u4fe1\u5cf0 \u4e0b\u4e4b\u4e8b\u2026\u2026\u90a3\u5929\u7684\u4e8b\uff0c\u4f60\u603b\u8be5\u5f88\u6e05\u695a\u7684\u4e86\uff01\u201d \u3000\u3000\u5353\u957f\u537f\u6697\u54fc\u4e00\u58f0\uff0c\u6297\u58f0\u9053\uff1a\u201c\u90a3\u5929\u7684\u4e8b\uff0c\u5728\u4e0b\u5373\u662f\u7c89\u8eab\u788e\u9aa8\uff0c\u4e5f\u4e07\u4e07\u4e0d\u4f1a\u5fd8\u8bb0\u7684\u3002\u201d \u3000\u3000\u6e29\u5982\u7389\u76ee\u5149\u4e00\u51db\uff0c\u5728\u5353\u957f\u537f\u9762\u4e0a\u51dd\u6ce8\u534a\u664c\uff0c\u5ffd\u7136\u5fae\u5fae\u9894\u9996\u7b11\u9053\uff1a\u201c\u6211\u5c31\u559c\u6b22\u4f60\u8fd9\u79cd\u6709\u9aa8 \u6c14\u7684\u6b63\u76f4\u5b69\u5b50\uff0c\u5509\u4e00\u4e00\u4f60\u7239\u7239\u867d\u7136\u5df2\u6b7b\uff0c\u4f46\u4ed6\u82e5\u77e5\u9053\u6709\u4f60\u8fd9\u79cd\u513f\u5b50\uff0c\u4e5f\u8be5\u542b\u7b11\u4e5d\u6cc9\u4e86\u3002\u201d \u3000\u3000\u8bed\u58f0\u4e4b\u4e2d\uff0c\u7adf\u6ee1\u542b\u611f\u6168\u7fa1\u6155\u4e4b\u610f\uff0c\u53c8\u4f3c\u4e4e\u5fae\u5e26\u60c6\u6005\u3002 \u3000\u3000\u5353\u957f\u537f\u76ee\u5149\u4e00\u62ac\uff0c\u53ea\u89c1\u5979\u65e5\u5149\u4e4b\u4e2d\u7684\u8083\u6740\u51b7\u524a\u4e4b\u610f\u6b64\u523b\u7ade\u5df2\u5168\u7136\u6d88\u5931\uff0c\u5374\u50cf\u662f\u4e2a\u6148\u7965\u7684 \u8001\u5987\uff0c\u5728\u6e29\u67d4\u7684\u671b\u7740\u81ea\u5df1\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u4ed6\u5fc3\u4e2d\u767e\u611f\u4ea4\u96c6\uff0c\u4ea6\u4e0d\u77e5\u662f\u60ca\u662f\u6012\uff0c\u662f\u6068\u662f\u6101\u3002 \u3000\u3000\u5374\u542c\u6e29\u5982\u7389\u53c8\u9053\uff1a\u201c\u90a3\u5929\u5728\u9ec4\u5c71\u59cb\u4fe1\u5cf0\u7684\u94c1\u8239\u5934\u91cc\uff0c\u51fa\u4e86\u4ef6\u5947\u4e8b\uff0c\u4f60\u8be5\u4e5f\u770b\u5230\u9ec4\u5c71\u5468\u56f4 \u767e\u91cc\u7684\u86c7\u866b\u91ce\u517d\uff0c\u90fd\u75af\u4e86\u4f3c\u7684\u8dd1\u5230\u94c1\u8239\u5934\u53bb\uff0c\u5b83\u4eec\u867d\u7136\u660e\u77e5\u5728\u90a3\u91cc\u6709\u4e2a\u5b83\u4eec\u7684\u514b\u661f\uff0c\u5b83\u4eec\u53bb \u4e86\uff0c\u5fc5\u5b9a\u9001\u6b7b\uff0c\u4f46\u662f\u5b83\u4eec\u5374\u65e0\u6cd5\u514b\u5236\u81ea\u5df1\uff0c\u660e\u77e5\u9001\u6b7b\u4e5f\u8981\u8dd1\u53bb\u3002\u201d \u3000\u3000\u201c\u4f60\u6b66\u529f\u4e0d\u5f31\uff0c\u5f53\u7136\u662f\u6709\u540d\u5e08\u6307\u70b9\uff0c\u4f60\u53ef\u77e5\u9053\u90a3\u662f\u4e3a\u7740\u4ec0\u4e48\u5417\uff1f\u201d \u3000\u3000\u5353\u957f\u537f\u6c89\u541f\u534a\u664c\uff0c\u5fc3\u4e2d\u867d\u4e0d\u613f\u56de\u7b54\u5979\u7684\u8bdd\uff0c\u5374\u4ecd\u7136\u8bf4\u9053\uff1a\u201c\u90a3\u6f5c\u4f0f\u5728\u94c1\u8239\u5934\u4e2d\u7684\u5f02\u517d\uff0c \u4e43\u5929\u4e0b\u81f3\u6bd2\u4e4b\u7269\uff0c\u800c\u4e14\u80fd\u591f\u53d1\u51fa\u4e00\u79cd\u6781\u4e3a\u5947\u5f02\u7684\u9999\u5473\uff0c\u4f7f\u5f97\u4efb\u4f55\u4e00\u79cd\u86c7\u866b\u731b\u517d\u90fd\u65e0\u6cd5\u6297 \u62d2\u3002\u201d \u3000\u3000\u6e29\u5982\u7389\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5bf9\u4e86\uff0c\u5f53\u65f6\u6211\u5c31\u5728\u60f3\uff0c\u6211\u82e5\u62db\u96c6\u5929\u4e0b\u82f1\u96c4\uff0c\u522b\u4eba\u4e00\u5b9a\u4e0d\u4f1a\u8d76 \u6765\uff0c\u4f46\u6211\u82e5\u548c\u90a3\u661f\u870d\u4e00\u6837\uff0c\u8ba9\u5929\u4e0b\u82f1\u96c4\u90fd\u65e0\u6cd5\u6297\u62d2\u7684\u8bf1\u60d1\uff0c\u90a3\u4e48\u4ed6\u4eec\u7eb5\u7136\u6068\u6211\u3001\u6015\u6211\uff0c\u5374\u4e5f \u4e0d\u5f97\u4e0d\u6765\u4e86\u3002\u201d \u3000\u3000\u5979\u5f97\u610f\u5730\u7b11\u4e86\u4e00\u4e0b\uff0c\u53c8\u9053\uff1a\u201c\u6211\u867d\u4e0d\u80fd\u548c\u90a3\u661f\u870d\u4e00\u6837\uff0c\u4f53\u53d1\u5f02\u9999\uff0c\u4f46\u6211\u5374\u6709\u7740\u666e\u5929\u4e4b \u4e0b\uff0c\u6ca1\u6709\u4e00\u4eba\u89c1\u4e86\u4e0d\u52a8\u5fc3\u7684\u5947\u73cd\u5f02\u5b9d\uff0c\u8fd9\u4e9b\u73cd\u5b9d\u5c31\u662f\u6211\u53d1\u51fa\u7684\u9999\u6c14\uff0c\u51ed\u7740\u8fd9\u9999\u6c14\uff0c\u6211\u5c31\u80fd\u5c06 \u5929\u4e0b\u7684\u6b66\u6797\u8c6a\u58eb\uff0c\u90fd\u53eb\u5230\u6211\u90a3\u747e\u513f\u9762\u524d\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u5251\u7709\u5fae\u76b1\uff0c\u6697\u9053\u4e00\u58f0\uff1a\u201c\u539f\u6765\u5982\u6b64\u3002\u201d \u3000\u3000\u4ed6\u5148\u524d\u672c\u5728\u5947\u602a\uff0c\u5929\u76ee\u5c71\u4e0a\uff0c\u600e\u4f1a\u6709\u4e2a\u5982\u6b64\u76db\u4f1a\uff0c\u6b64\u523b\u4e00\u542c\u624d\u77e5\u9053\u771f\u76f8\u3002 \u3000\u3000\u6e29\u5982\u7389\u7b11\u5bb9\u4e00\u655b\uff0c\u7a81\u53c8\u53f9\u9053\uff1a\u201c\u54ea\u77e5\u9053\u747e\u513f\u542c\u4e86\u6211\u8fd9\u8ba1\u5212\uff0c\u5374\u9053\uff1a\u2018\u4f60\u8001\u4eba\u5bb6\u7684\u5947\u73cd\u5f02 \u5b9d\u867d\u7136\u90fd\u662f\u4e16\u4eba\u68a6\u5bd0\u4ee5\u6c42\u4e4b\u7269\uff0c\u5374\u4e5f\u672a\u89c1\u5f97\u80fd\u5c06\u5929\u4e0b\u82f1\u96c4\u90fd\u5f15\u4e86\u6765\uff0c\u6765\u7684\u82e5\u90fd\u662f\u4e00\u4e9b\u4e0d\u6210\u6750 \u7684\u89d2\u8272\uff0c\u90a3\u6211\u8fd8\u4e0d\u5982\u4e0d\u770b\u54e9\u3002\u2019\u6211\u60f3\u4e86\u8bb8\u4e45\uff0c\u624d\u60f3\u51fa\u8fd9\u4e2a\u529e\u6cd5\uff0c\u672c\u6765\u4ee5\u4e3a\u5df2\u7ecf\u5f88\u597d\u4e86\uff0c\u54ea\u77e5 \u5374\u88ab\u5979\u8fd9\u4e00\u53e5\u8bdd\u5168\u76d8\u63a8\u7ffb\uff0c\u4f46\u6211\u4ed4\u7ec6\u4e00\u60f3\uff0c\u5374\u53c8\u4e0d\u80fd\u4e0d\u627f\u8ba4\u5979\u8fd9\u79cd\u8bdd\u8bf4\u7684\u6709\u4e9b\u9053\u7406\u3002\u201c\u5353\u957f \u537f\u6697\u4e2d\u9894\u9996\uff0c\u5fd6\u9053\uff1a\u201c\u770b\u6765\u8fd9\u6e29\u5982\u7389\u8fd8\u662f\u4e2a\u806a\u660e\u7edd\u9876\u4e4b\u4eba\u3002\u201d \u3000\u3000\u5374\u542c\u6e29\u5982\u7389\u53c8\u9053\uff1a\u201c\u8fc7\u4e86\u51e0\u5929\uff0c\u5979\u5ffd\u7136\u81ea\u5df1\u753b\u4e86\u4e09\u5e45\u753b\uff0c\u62ff\u6765\u7ea6\u6211\u770b\uff0c\u53c8\u5bf9\u6211\u8bf4\u8981\u5728\u5929 \u76ee\u5c71\u5f00\u4e2a\u8f83\u6280\u4e4b\u4f1a\uff0c\u5979\u8bf4\uff1a\u2018\u8fd9\u4e48\u4e00\u6765\uff0c\u4e00\u4e9b\u8d2a\u8d22\u7231\u5b9d\u7684\u4eba\uff0c\u56fa\u7136\u662f\u975e\u6765\u4e0d\u53ef\uff0c\u53e6\u4e00\u4e9b\u8fd8\u672a \u6210\u5a5a\u7684\u5c11\u5e74\u8c6a\u6770\uff0c\u4e5f\u4e00\u5b9a\u4f1a\u6765\uff0c\u5c31\u7b97\u8fd8\u6709\u4e9b\u8fd9\u4e24\u6837\u90fd\u4e0d\u6253\u52a8\u7684\u4eba\uff0c\u4f46\u4ed6\u4eec\u53ea\u8981\u662f\u6b66\u6797\u4e2d\u4eba\uff0c \u5c31\u4e0d\u4f1a\u6ca1\u6709\u4e89\u540d\u597d\u80dc\u4e4b\u5fc3\uff0c\u4e00\u542c\u5929\u76ee\u5c71\u6709\u4e2a\u5982\u6b64\u7684\u8f83\u6280\u4e4b\u4f1a\uff0c\u5fc5\u5b9a\u4f1a\u8d76\u6765\u7684\u3002\u2019\u5979\u53c8\u8bf4\uff1a \u2018\u597d\u5229\u3001\u597d\u540d\u3001\u597d\u8272\u3001\u597d\u5947\uff0c\u672c\u662f\u4eba\u4eec\u7684\u6839\u6027\uff0c\u8fd9\u4e48\u4e00\u505a\uff0c\u6211\u5c31\u4e0d\u76f8\u4fe1\u4e16\u4eba\u8fd8\u6709\u65e2\u4e0d\u597d\u540d \u5229\uff0c\u4e5f\u4e0d\u597d\u5947\u7684\u4eba\uff01\u2019\u201c\u5353\u957f\u537f\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u60ed\u6127\u3002\u201d \u3000\u3000\u4ed6\u81ea\u5df1\u867d\u4e0d\u597d\u540d\u5229\u8d22\u8272\uff0c\u4f46\u597d\u5947\u4e4b\u5fc3\uff0c\u5374\u8fd8\u662f\u4e0d\u80fd\u514b\u5236\uff0c\u8fd9\u6e29\u747e\u5982\u6b64\u505a\u6765\uff0c\u786e\u5df2\u662f\u5c06\u4e16 \u4eba\u4e00\u7f51\u6253\u5c3d\u4e86\u3002 \u3000\u3000\u6e29\u5982\u7389\u7f13\u7f13\u53c8\u9053\uff1a\u201c\u6211\u5f53\u65f6\u542c\u4e86\uff0c\u5fc3\u91cc\u4e0d\u514d\u6709\u4e9b\u5947\u602a\uff0c\u5c31\u95ee\u5979\uff1a\u2018\u5047\u5982\u5728\u90a3\u8f83\u6280\u4e4b\u4f1a\u4e0a \u6b66\u529f\u6700\u5f3a\u7684\u4eba\uff0c\u662f\u4e2a\u79c3\u5b50\u9ebb\u5b50\uff0c\u90a3\u4e48\u4f60\u662f\u5426\u4e5f\u8981\u5ac1\u7ed9\u4ed6\u5462\uff1f\u2019\u5979\u5fae\u5fae\u4e00\u7b11\uff0c\u5374\u4e0d\u56de\u7b54\u6211\u7684 \u8bdd\uff0c\u53ea\u95ee\u6211\u80af\u4e0d\u80af\uff0c\u6211\u60f3\u6765\u60f3\u53bb\uff0c\u8fd8\u662f\u7b54\u5e94\u4e86\u5979\uff0c\u53ea\u662f\u7b54\u5e94\u4e86\u4e4b\u540e\uff0c\u53c8\u6709\u4e9b\u540e\u6094\uff0c\u5fc3\u60f3\u666e\u5929 \u4e4b\u4e0b\uff0c\u6b66\u529f\u82e5\u80fd\u80dc\u5f97\u4e86\u6211\u747e\u513f\u7684\uff0c\u672c\u4e0d\u4f1a\u5927\u591a\uff0c\u5373\u4f7f\u6709\u4e0a\u51e0\u4e2a\uff0c\u5e74\u9f84\u4e5f\u5fc5\u5b9a\u5f88\u5927\u4e86\uff0c\u54c1\u8c8c\u4e5f \u672a\u5fc5\u4f1a\u597d\uff0c\u747e\u513f\u5ac1\u7ed9\u4e86\u8fd9\u79cd\u4eba\uff0c\u5c82\u975e\u662f\u5f69\u51e4\u968f\u9e26\u3002\u201c\u5979\u76ee\u5149\u53c8\u81ea\u7f13\u7f13\u6ce8\u5411\u5353\u957f\u537f\u8eab\u4e0a\uff0c\u53c8 \u9053\uff1a\u201c\u53ef\u662f\u4eca\u65e5\u6211\u89c1\u4e86\u4f60\uff0c\u624d\u77e5\u9053\u5929\u4e0b\u679c\u7136\u662f\u5947\u4eba\u8f88\u51fa\uff0c\u80fd\u591f\u6559\u5f97\u51fa\u4f60\u8fd9\u4e00\u8eab\u6b66\u529f\u7684\u4eba\uff0c\u90a3 \u4ed6\u7684\u6b66\u529f\uff0c\u4e5f\u4e00\u5b9a\u6df1\u4e0d\u53ef\u6d4b\u4e86\uff0c\u6211\u867d\u7136\u4e0d\u77e5\u9053\u4ed6\u662f\u8c01\uff0c\u4f60\u4e5f\u4e00\u5b9a\u4e0d\u4f1a\u544a\u8bc9\u6211\uff0c\u53ef\u662f\u6211\u5374\u5f88\u94a6 \u4f69\u4ed6\uff0c\u56e0\u4e3a\u4ed6\u4e0d\u4f46\u5c06\u4f60\u6559\u6210\u4e00\u8eab\u6b66\u529f\uff0c\u8fd8\u5c06\u4f60\u6559\u6210\u4e00\u4e2a\u5927\u4e08\u592b\u3002\u54fc\uff01\u4e16\u4e0a\u6709\u4e9b\u4eba\u6b66\u529f\u867d\u9ad8\uff0c \u884c\u4e3a\u5374\u5351\u9119\u5f97\u5f88\u3002\u201d \u3000\u3000\u5979\u968f\u624b\u4e00\u6307\u90a3\u88ab\u56f0\u5728\u9713\u88f3\u4ed9\u821e\u9635\u4e2d\uff0c\u6b64\u523b\u8eab\u6cd5\u4e5f\u8d8a\u6765\u8d8a\u7f13\uff0c\u6c14\u529b\u4e5f\u6e10\u4e0d\u652f\u7684\u5c91\u7cb2\u53c8\u9053\uff1a \u201c\u4ed6\u548c\u4ed6\u7684\u5e08\u7236\uff0c\u90fd\u662f\u8fd9\u79cd\u4eba\u3002\u201d \u3000\u3000\u8bed\u6c14\u4e4b\u4e2d\uff0c\u6028\u6bd2\u4e4b\u610f\uff0c\u53c8\u590d\u5927\u4f5c\uff0c\u5353\u957f\u537f\u5fc3\u4e2d\u4e00\u52a8\uff0c\u4ed6\u542c\u4e86\u8fd9\u6e29\u52a0\u7389\u7684\u4e00\u5e2d\u8bdd\uff0c\u5fc3\u4e2d\u601d \u6f6e\u7ffb\u6d8c\uff0c\u51e0\u4e4e\u5df2\u5c06\u90a3\u8d4c\u547d\u4e4b\u4e8b\u5fd8\u4e86\u3002 \u3000\u3000\u6b64\u523b\u4ed6\u89c1\u6e29\u5982\u7389\u5bf9\u90a3\u9ec4\u886b\u5c11\u5e74\uff0c\u4f3c\u4e4e\u751a\u4e3a\u6068\u6bd2\uff0c\u5fc3\u4e0b\u53c8\u89c9\u5f97\u6709\u4e9b\u5947\u602a\uff0c\u5fc3\u60f3\u8fd9\u4e11\u4eba\u6e29\u5982 \u7389\u4e0e\u4ed6\u4eec\u5e08\u5f92\u672c\u662f\u4e00\u4e18\u4e4b\u8c89\uff0c\u5979\u5374\u8bf4\u51fa\u6b64\u8bdd\uff0c\u5c82\u975e\u6709\u4e9b\u5947\u602a\uff0c\u4ed6\u5374\u4e0d\u77e5\u8fd9\u6e29\u5982\u7389\u5fc3\u4e2d\u5bf9\u90a3\u4e07 \u5999\u771f\u541b\u513f\u7684\u6028\u6068\uff0c\u53ea\u6015\u8fd8\u5728\u4ed6\u81ea\u5df1\u4e4b\u4e0a\u5462\u3002 \u3000\u3000\u8f6c\u76ee\u671b\u53bb\uff0c\u53ea\u89c1\u6e29\u5982\u7389\u76ee\u5149\u4f4e\u5782\uff0c\u51dd\u6ce8\u5728\u81ea\u5df1\u7684\u624b\u6307\u4e0a\uff0c\u4f3c\u4e4e\u5728\u60f3\u7740\u4ec0\u4e48\u5fc3\u4e8b\uff0c\u800c\u4e14\u770b \u6765\u8fd8\u4e0d\u77e5\u8981\u60f3\u591a\u4e45\u7684\u6837\u5b50\u3002 \u3000\u3000\u5353\u957f\u537f\u5e72\u54b3\u4e00\u58f0\uff0c\u89c1\u5979\u4ecd\u7136\u6d51\u5982\u672a\u89c9\uff0c\u5fc3\u601d\u6570\u8f6c\uff0c\u60f3\u95ee\u5979\u8981\u81ea\u5df1\u6240\u505a\u7a76\u7adf\u662f\u4ec0\u4e48\u4e8b\uff0c\u4f46 \u76ee\u5149\u52a8\u5904\uff0c\u5374\u89c1\u5230\u5979\u6b64\u523b\u9762\u4e0a\u7adf\u662f\u4e00\u7247\u5b89\u5b81\u7965\u548c\u4e4b\u8272\uff0c\u5979\u8fd9\u5f20\u4e11\u964b\u4e0d\u582a\u7684\u9762\u5bb9\uff0c\u66b4\u623e\u4e4b\u6c14\u5df2 \u53bb\uff0c\u770b\u6765\u4e5f\u5c31\u4f3c\u4e4e\u6ca1\u6709\u90a3\u6837\u4e11\u964b\u4e86\uff0c\u5353\u957f\u537f\u5fc3\u4e2d\u4e0d\u7981\u6697\u53f9\u4e00\u58f0\uff0c\u5fd6\u9053\uff1a\u201c\u6b64\u523b\u5979\u5fc3\u4e2d\u6240\u601d\uff0c \u5fc5\u5b9a\u662f\u5341\u5206\u5584\u826f\u4e4b\u4e8b\uff0c\u5979\u4e00\u751f\u884c\u6076\uff0c\u4e00\u751f\u4e4b\u4e2d\uff0c\u5927\u7ea6\u6781\u4e3a\u96be\u5f97\u6709\u8fd9\u79cd\u5b89\u5b81\u7965\u548c\u4e4b\u8272\u3002\u201d \u3000\u3000\u4e00\u5ff5\u81f3\u6b64\uff0c\u9042\u5c06\u5df2\u5230\u53e3\u8fb9\u7684\u8bdd\u5fcd\u4f4f\u4e86\uff0c\u8f6c\u76ee\u671b\u5411\u90a3\u88ab\u56f0\u5728\u6f2b\u5929\u7ea2\u5f71\u4e2d\u7684\u9ec4\u886b\u5c11\u5e74\u3002 \u3000\u3000\u90a3\u4e9b\u7ea2\u88f3\u5c11\u5973\u4ecd\u7136\u662f\u886b\u8896\u98d8\u98d8\uff0c\u8eab\u5f62\u66fc\u5999\uff0c\u4e00\u526f\u66fc\u821e\u6e05\u6b4c\u7684\u6837\u5b50\uff0c\u4f46\u5979\u4eec\u8eab\u5f62\u7684\u4ea4\u66ff\u6d41 \u8f6c\uff0c\u5374\u662f\u6781\u4e3a\u8fc5\u5feb\uff0c\u5353\u957f\u537f\u4e00\u773c\u671b\u53bb\uff0c\u6839\u672c\u65e0\u6cd5\u770b\u6e05\u90a3\u9ec4\u886b\u5c11\u5e74\u7684\u8eab\u5f62\uff0c\u53ea\u515a\u8fd9\u4e00\u7247\u201c\u7ea2\u5f71 \u4e2d\u7684\u9ec4\u8272\u4eba\u5f62\uff0c\u5c55\u52a8\u8d8a\u6765\u8d8a\u7f13\uff0c\u663e\u89c1\u5df2\u662f\u96be\u4ee5\u652f\u6301\u4e86\u3002\u5353\u957f\u537f\u4e0e\u8fd9\u9ec4\u886b\u5c11\u5e74\u66fe\u7ecf\u4ea4\u624b\uff0c\u77e5\u9053 \u6b64\u4eba\u867d\u7136\u72c2\u505a\uff0c\u6b66\u529f\u5374\u6781\u4e3a\u4e0d\u5f31\uff0c\u5728\u6b66\u6797\u4e2d\u5df2\u53ef\u5217\u4e3a\u4e00\u6d41\u9ad8\u624b\u4e4b\u79f0\uff0c\u800c\u6b64\u523b\u5374\u88ab\u8fd9\u4e9b\u6b66\u529f\u5e76 \u4e0d\u751a\u9ad8\u7684\u5c11\u5973\u56f0\u5f97\u4e00\u7b79\u83ab\u5c55\uff0c\u5982\u6b64\u770b\u6765\uff0c\u663e\u89c1\u8fd9\u9713\u88f3\u4ed9\u821e\u9635\u7684\u786e\u6709\u7740\u4e0d\u540c\u51e1\u4fd7\u7684\u5a01\u529b\u3002\u4e00\u5ff5 \u81f3\u6b64\uff0c\u4ed6\u4fbf\u5b9a\u6674\u800c\u671b\uff0c\u7559\u610f\u53bb\u89c2\u5bdf\u8fd9\u4e9b\u5c11\u5973\u4eec\u6240\u65bd\u5c55\u7684\u8eab\u6cd5\uff0c\u53ea\u89c9\u5979\u4eec\u8eab\u6cd5\u914d\u5408\u7684\u786e\u662f\u5999\u5230 \u6beb\u5dc5\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u7ade\u65e0\u6cd5\u770b\u51fa\u5979\u4eec\u7684\u8eab\u5f62\uff0c\u662f\u5982\u4f55\u5c55\u52a8\u7684\u3002\u4ed6\u8fd9\u4e00\u5b9a\u775b\u800c\u671b\uff0c\u76ee\u5149\u4fbf\u518d\u4e5f\u820d \u4e0d\u5f97\u79bb\u5f00\uff0c\u987b\u77e5\u4efb\u4f55\u4e00\u4e2a\u5929\u6027\u597d\u6b66\u4e4b\u4eba\uff0c\u9047\u7740\u8fd9\u79cd\u6df1\u5965\u7684\u6b66\u529f\uff0c\u4fbf\u6709\u5982\u4e00\u4e2a\u7a1a\u9f84\u5e7c\u7ae5\u89c1\u7740\u4ed6 \u6700\u6700\u559c\u7231\u7684\u7cd6\u679c\u4e00\u6837\u3002\u4ed6\u5168\u795e\u51dd\u6ce8\u7740\u8fd9\u4e9b\u7ea2\u88f3\u5c11\u5973\u7684\u8eab\u5f62\u53d8\u5316\uff0c\u53ea\u89c9\u8fd9\u9713\u88f3\u4ed9\u821e\u9635\u4f3c\u4e4e\u548c\u90a3 \u6b66\u6797\u7b2c\u4e00\u5b97\u6d3e\uff0c\u6b66\u5f53\u6d3e\u7684\u9547\u5c71\u4e5d\u5b98\u516b\u5366\u9635\u6709\u4e9b\u76f8\u4f3c\uff0c\u4f46\u5176\u7e41\u590d\u53d8\u5316\uff0c\u5374\u72b9\u6709\u8fc7\u4e4b\uff0c\u4ed6\u867d\u662f\u7edd \u9876\u806a\u660e\u4e4b\u4eba\uff0c\u4f46\u770b\u4e86\u8bb8\u4e45\uff0c\u5374\u4ecd\u672a\u53c2\u900f\u5176\u4e2d\u7684\u5965\u5999\uff0c\u5fc3\u4e0b\u4e0d\u7981\u5927\u4e3a\u6025\u8e81\uff0c\u6697\u4e2d\u611f\u53f9\u4e00\u58f0\uff0c\u5fd6 \u9053\uff1a\u201c\u770b\u6765\u8fd9\u4e11\u4eba\u6e29\u5982\u7389\u7684\u806a\u660e\u624d\u667a\uff0c\u7684\u786e\u4e0d\u662f\u5e38\u4eba\u80fd\u53ca\uff0c\u5509\u2014\u2014\u65e5\u540e\u6211\u82e5\u60f3\u62a5\u6b64\u6df1\u4ec7\uff0c\u53ea \u6015\u4e0d\u662f\u6613\u4e8b\u5462\uff01\u201d \u3000\u3000\u4ed6\u5fc3\u4e2d\u6b63\u81ea\u7e41\u4e71\u96be\u5b89\uff0c\u54ea\u77e5\u8033\u4fa7\u54cd\u8d77\u4e00\u9635\u51b7\u7b11\uff0c\u53ea\u542c\u6e29\u5982\u7389\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6211\u8fd9\u9713\u88f3\u4ed9\u821e \u9635\u867d\u975e\u76d6\u7edd\u5929\u4e0b\uff0c\u5374\u4e5f\u4e0d\u662f\u4f60\u7565\u5fae\u4e00\u770b\u4fbf\u80fd\u53c2\u8be6\u5f97\u900f\u7684\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u5fc3\u4e2d\u4e00\u51db\uff0c\u5374\u542c\u6e29\u5982\u7389\u53c8\u9053\uff1a\u201c\u6211\u8fd9\u9635\u6cd5\u5173\u952e\u6240\u5728\uff0c\u5168\u5728\u811a\u6b65\u4e4b\u95f4\uff0c\u4f60\u82e5\u5355\u53ea\u6ce8 \u610f\u5979\u4eec\u7684\u8eab\u5f62\u638c\u6cd5\uff0c\u83ab\u8bf4\u5c31\u8fd9\u4e00\u65f6\u534a\u523b\uff0c\u53ea\u6015\u4f60\u518d\u770b\u4e0a\u4e00\u5e74\uff0c\u4e5f\u662f\u6789\u7136\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u6697\u9053\u4e00\u58f0\uff1a\u201c\u60ed\u6127\u3002\u201d \u3000\u3000\u5374\u89c1\u6e29\u5982\u7389\u7a81\u7136\u4f38\u51fa\u53cc\u638c\uff0c\u8f7b\u8f7b\u4e00\u54cd\uff0c\u638c\u58f0\u6e05\u8106\uff0c\u6709\u5982\u51fb\u7389\u3002 \u3000\u3000\u90a3\u4e9b\u7ea2\u88f3\u5c11\u5973\u4e00\u95fb\u638c\u58f0\uff0c\u8eab\u5f62\u7adf\u7a81\u7136\u6162\u4e86\u4e0b\u6765\uff0c\u5353\u957f\u537f\u5fc3\u4e2d\u4e00\u52a8\uff0c\u4e0d\u7981\u5927\u5947\uff0c\u5fd6\u9053\uff1a \u201c\u96be\u9053\u8fd9\u6e29\u5982\u7389\u6709\u610f\u5c06\u8fd9\u9635\u6cd5\u7684\u5965\u5999\uff0c\u8ba9\u6211\u53c2\u900f\u5417\uff1f\u201d \u3000\u3000\u8fd9\u60f3\u6cd5\u770b\u6765\u4e0d\u4f46\u4e0d\u5408\u60c5\u7406\uff0c\u800c\u4e14\u7b80\u76f4\u8352\u8c2c\u5f97\u8fd1\u4e8e\u7edd\u4e0d\u53ef\u80fd\uff0c\u4e00\u4e2a\u6bd2\u8fa3\u800c\u72e0\u5fc3\u7684\u9b54\u5934\uff0c\u600e \u80af\u5c06\u81ea\u5df1\u82e6\u5fc3\u7814\u6210\u7684\u4e0d\u4f20\u4e4b\u79d8\uff0c\u5982\u6b64\u8f7b\u6613\u5730\u4f20\u6388\u7ed9\u4e00\u4e2a\u660e\u77e5\u8981\u5411\u81ea\u5df1\u590d\u4ec7\u7684\u4ec7\u4eba\u4e4b\u5b50\u5462\uff1f \u3000\u3000\u4f46\u5353\u957f\u537f\u76ee\u5149\u52a8\u5904\uff0c\u5374\u89c1\u8fd9\u4e9b\u7ea2\u88f3\u5c11\u5973\uff0c\u4e0d\u4f46\u5df2\u5c06\u8eab\u5f62\u653e\u7f13\uff0c\u800c\u4e14\u4e3e\u624b\u6295\u8db3\u95f4\u3001\u8eab\u5f62\u3001 \u6b65\u6cd5\uff0c\u90fd\u6781\u6e05\u6670\u53ef\u89c1\uff0c\u5353\u957f\u537f\u867d\u5bf9\u65b9\u624d\u81ea\u5df1\u7684\u60f3\u6cd5\uff0c\u60ca\u5947\u96be\u4fe1\uff0c\u4f46\u6b64\u523b\u5374\u53c8\u4e0d\u5f97\u4e0d\u4fe1\u4e86\u3002 \u3000\u3000\u8fd9\u9713\u88f3\u4ed9\u821e\u9635\u6cd5\u4e00\u677e\uff0c\u5353\u957f\u537f\u56fa\u7136\u60ca\u5f02\u4ea4\u96c6\uff0c\u90a3\u9ec4\u886b\u5c11\u5e74\u5c91\u7cb2\uff0c\u66f4\u662f\u5927\u611f\u5947\u602a\uff0c\u4ed6\u6b64\u523b \u5df2\u662f\u7cbe\u7aed\u529b\u5c3d\uff0c\u5c31\u8fde\u53d1\u51fa\u7684\u62db\u5f0f\uff0c\u90fd\u8f6f\u5f31\u5f97\u6709\u5982\u6b66\u529f\u7c97\u6d45\u4e4b\u4eba\uff0c\u6b64\u523b\u5f97\u5230\u5598\u606f\u7684\u673a\u4f1a\uff0c\u7cbe\u795e \u7a81\u7136\u4e00\u632f\uff0c\u62fc\u5c3d\u4f59\u529b\uff0c\u547c\u547c\u653b\u51fa\u6570\u638c\uff0c\u5180\u6c42\u80fd\u591f\u51b2\u51fa\u9635\u5916\u3002 \u3000\u3000\u54ea\u77e5\u9635\u6cd5\u65b9\u81ea\u8f6c\u52a8\u4e09\u4e94\u6b21\uff0c\u6e29\u52a0\u7389\u7a81\u53c8\u4e00\u62cd\u624b\u638c\uff0c\u638c\u58f0\u65b9\u843d\uff0c\u90a3\u4e9b\u7ea2\u88f3\u5c11\u5973\u7684\u8eab\u5f62\u4fbf\u53c8 \u7535\u4f3c\u7684\u8f6c\u52a8\u8d77\u6765\u3002 \u3000\u3000\u6e29\u5982\u7389\u659c\u773c\u4e00\u779f\uff0c\u53ea\u89c1\u5353\u957f\u537f\u5140\u81ea\u5bf9\u7740\u9635\u6cd5\u51fa\u795e\uff0c\u5e72\u54b3\u4e00\u58f0\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u53ef\u770b\u6e05\u4e86\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u56de\u9996\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u591a\u627f\u6307\u6559\u3002\u201d \u3000\u3000\u4ed6\u5929\u8d44\u7edd\u9876\uff0c\u5c31\u5728\u65b9\u624d\u90a3\u4e00\u523b\u5185\uff0c\u4fbf\u5df2\u5c06\u8fd9\u9713\u88f3\u4ed9\u821e\u9635\u7684\u5965\u5999\uff0c\u7aa5\u51fa\u591a\u534a\uff0c\u6b64\u523b\u5fc3\u4e2d\u7a81 \u53c8\u4e00\u52a8\uff0c\u5fd6\u9053\uff1a\u201c\u8fd9\u6e29\u5982\u7389\u5c06\u6b64\u9635\u6cd5\u7684\u5965\u5999\u4f20\u6388\u4e8e\u6211\uff0c\u96be\u9053\u5c31\u662f\u4e3a\u4e86\u5979\u8981\u53eb\u6211\u505a\u7684\u90a3\u4e8b\uff0c\u4e0e \u6b64\u9635\u6cd5\u6709\u5173\u3002\u201d \u3000\u3000\u5ff5\u5934\u5c1a\u672a\u8f6c\u5b8c\uff0c\u5374\u542c\u6e29\u5982\u7389\u5df2\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6b64\u523b\u8ddd\u79bb\u516b\u6708\u4e2d\u79cb\u5c1a\u6709\u6570\u65e5\uff0c\u5728\u8fd9\u6570\u65e5\u4e4b \u95f4\uff0c\u4f60\u5207\u9700\u5bfb\u5f97\u4e00\u6cd5\u7834\u53bb\u6b64\u9635\uff0c\u5230\u4e86\u516b\u6708\u4e2d\u79cb\u90a3\u4e00\u5929\uff0c\u4f60\u4fbf\u8d76\u5230\u5929\u76ee\u5c71\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u5fae\u5fae\u4e00\u6014\uff0c\u8131\u53e3\u95ee\u9053\uff1a\u201c\u8fd9\u96be\u9053\u662f\u9601\u4e0b\u8981\u6211\u6240\u505a\u4e4b\u4e8b\u5417\uff1f\u201d \u3000\u3000\u6e29\u5982\u7389\u9762\u4e0a\uff0c\u867d\u7136\u6ca1\u6709\u4efb\u4f55\u8868\u60c5\uff0c\u597d\u50cf\u6ca1\u6709\u542c\u5230\u4ed6\u7684\u95ee\u8bdd\u4e00\u822c\uff0c\u5374\u53c8\u9053\uff1a\u201c\u8fd9\u6b21\u5929\u76ee\u5c71 \u4e0a\u7684\u8f83\u6280\u4e4b\u4f1a\uff0c\u5927\u6cb3\u4e24\u5cb8\uff0c\u957f\u6c5f\u5357\u5317\u7684\u6b66\u6797\u82f1\u8c6a\uff0c\u95fb\u8baf\u800c\u6765\u7684\uff0c\u51e0\u4e4e\u5df2\u5360\u4e86\u666e\u5929\u4e4b\u4e0b\u7684\u6b66\u6797 \u4fca\u989c\u5927\u534a\uff0c\u8fd9\u5176\u4e2d\u81ea\u7136\u4e0d\u4e4f\u8eab\u624b\u9ad8\u5f3a\u3001\u6b66\u529f\u7cbe\u7edd\u7684\u4eba\uff0c\u4f60\u5728\u516b\u6708\u5341\u4e94\u65e5\u90a3\u4e00\u5929\uff0c\u52a1\u987b\u5c06\u4ed6\u4eec \u5168\u90fd\u51fb\u8d25\u2026\u2026\u201d \u3000\u3000\u5979\u5fae\u5fae\u4e00\u7b11\uff0c\u53c8\u9053\uff1a\u201c\u4ee5\u4f60\u4e4b\u6b66\u529f\uff0c\u53ea\u8981\u6ca1\u6709\u610f\u5916\uff0c\u6b64\u4e8b\u5f53\u53ef\u6709\u516b\u5206\u628a\u63e1\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u8d8a\u542c\u8d8a\u89c9\u5947\u602a\uff0c\u4e0d\u77e5\u9053\u8fd9\u6e29\u5982\u7389\u6b64\u4e3e\uff0c\u7a76\u7adf\u4f55\u610f\u3002 \u3000\u3000\u6e29\u52a0\u7389\u76ee\u5149\u5fae\u626b\uff1b\u9762\u4e0a\u7ade\u53c8\u9732\u51fa\u4e00\u4e1d\u7b11\u5bb9\uff0c\u7f13\u7f13\u53c8\u9053\uff1a\u201c\u7136\u540e\u4f60\u4fbf\u5f97\u7834\u53bb\u9713\u88f3\u4ed9\u821e\u9635\uff0c \u6700\u540e\u4f60\u8fd8\u5f97\u5f53\u7740\u5929\u4e0b\u82f1\u96c4\u4e4b\u9762\uff0c\u548c\u6211\u90a3\u5f92\u513f\u6e29\u747e\u8f83\u4e00\u8f83\u8eab\u624b\uff0c\u53ea\u8981\u4f60\u80fd\u5c06\u5979\u51fb\u8d25\uff0c\u90a3 \u4e48\u2026\u2026\u201d \u3000\u3000\u5979\u53c8\u81ea\u4e00\u7b11\uff0c\u500f\u7136\u4e2d\u6b62\u4e86\u8bdd\uff0c\u5353\u957f\u537f\u5fc3\u4e2d\u731b\u7136\u4e00\u9635\u5267\u8df3\uff0c\u5f20\u5f00\u53e3\u6765\uff0c\u5374\u534a\u664c\u8bf4\u4e0d\u51fa\u8bdd\uff0c \u53ea\u89c1\u6e29\u5982\u7389\u76ee\u5149\u7f13\u7f13\u79fb\u5411\u81ea\u5df1\u9762\u4e0a\uff0c\u53c8\u9053\uff1a\u201c\u747e\u513f\u82e5\u662f\u5ac1\u7ed9\u4e86\u4f60\uff0c\u90a3\u4e48\u6211\u4e5f\u5c31\u653e\u5fc3\u4e86\uff0c\u5979\u813e \u6c14\u4e0d\u597d\uff0c\u51e1\u4e8b\u4f60\u90fd\u5f97\u8ba9\u7740\u5979\u4e00\u70b9\u2026\u2026\u201d \u3000\u3000\u5979\u8bed\u58f0\u7a81\u7136\u4e00\u51db\uff0c\u63a5\u9053\uff1a\u201c\u4f60\u82e5\u5bf9\u5979\u4e0d\u597d\uff0c\u6211\u5c31\u7b97\u6b7b\u4e86\uff0c\u505a\u9b3c\u4e5f\u5f97\u627e\u4f60\u7b97\u5e10\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u5fc3\u4e2d\u8f70\u7136\u4e00\u9707\uff0c\u5446\u5446\u5730\u6115\u4e86\u534a\u664c\uff0c\u6323\u624e\u7740\u8bf4\u9053\uff1a\u201c\u96be\u9053\u8fd9\u5c31\u662f\u9601\u4e0b\u8981\u6211\u6240\u505a\u4e4b\u4e8b \u5417\uff1f\u201d \u3000\u3000\u4ed6\u7eb5\u7136\u806a\u660e\u7edd\u9876\uff0c\u5374\u518d\u4e5f\u60f3\u4e0d\u5230\u8fd9\u6e29\u5982\u7389\u8981\u8ba9\u81ea\u5df1\u6240\u505a\u7684\uff0c\u7adf\u662f\u5982\u6b64\u4e4b\u4e8b\u3002 \u3000\u3000\u6e29\u5982\u7389\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6b63\u662f\u6b64\u4e8b\u2026\u2026\u82e5\u4e0d\u662f\u6211\u770b\u4f60\u806a\u660e\u6b63\u76f4\uff0c\u4f60\u8dea\u5728\u5730\u4e0a\u6c42\u6211\u4e09\u5929\u4e09 \u591c\uff0c\u6211\u5374\u4e5f\u4e0d\u4f1a\u7b54\u5e94\u4f60\u7684\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u5b9a\u4e86\u5b9a\u795e\uff0c\u4e00\u6e05\u5589\u5499\uff0c\u9053\uff1a\u201c\u5728\u4e0b\u65b9\u624d\u65e2\u7136\u5df2\u8d25\u5728\u9601\u4e0b\u4e4b\u624b\uff0c\u9601\u4e0b\u4fbf\u662f\u8ba9\u6211\u8d74\u6c64 \u8e48\u706b\uff0c\u5728\u4e0b\u4e5f\u4e0d\u4f1a\u76b1\u4e00\u7709\u5934\uff0c\u53ea\u662f\u6b64\u4e8b\u2026\u2026\u201d \u3000\u3000\u6e29\u5982\u7389\u51b7\u7b11\u4e86\u4e00\u58f0\uff0c\u63a5\u53e3\u8bf4\u9053\uff1a\u201c\u6b64\u4e8b\u4fbf\u53c8\u600e\u7684\uff0c\u96be\u9053\u6709\u8fdd\u4e8e\u4ec1\u4e49\u9053\u5fb7\uff0c\u96be\u9053\u662f\u4eba\u529b\u65e0 \u6cd5\u505a\u5230\u7684\u4e0d\u6210\uff1f\u201d \u3000\u3000\u5353\u957f\u537f\u5446\u4e86\u4e00\u5446\uff0c\u4fef\u4e86\u201c\u5934\u53bb\uff0c\u534a\u664c\u8bf4\u4e0d\u51fa\u8bdd\u6765\uff0c\u5fc3\u4e2d\u5343\u601d\u767e\u8f6c\uff0c\u5374\u4e5f\u60f3\u4e0d\u51fa\u8be5\u5982\u4f55\u56de \u7b54\u4eba\u5bb6\u7684\u8bdd\uff0c\u8981\u77e5\u9053\u6e29\u5982\u7389\u8ba9\u4ed6\u6240\u505a\u4e4b\u4e8b\uff0c\u7684\u786e\u662f\u65e2\u65e0\u4e8f\u4e8e\u4ec1\u4e49\u9053\u5fb7\uff0c\u4ea6\u975e\u4eba\u529b\u65e0\u6cd5\u505a\u5230\u4e4b \u4e8b\uff0c\u4ed6\u672c\u8be5\u9075\u5b88\u8bfa\u8a00\uff0c\u4e00\u53e3\u5e94\u5141\uff0c\u4f46\u90a3\u6e29\u747e\u5374\u53c8\u662f\u4ed6\u6740\u7236\u4ec7\u4eba\u5f92\u5f1f\u2026\u2026\u201d \u3000\u3000\u4e00\u65f6\u4e4b\u95f4\uff0c\u4ed6\u5fc3\u4e2d\u601d\u6f6e\u53cd\u590d\uff0c\u77db\u76fe\u96be\u5b89\uff0c\u4e0d\u77e5\u9053\u7a76\u7adf\u8be5\u5982\u4f55\u662f\u597d\uff0c\u53ea\u542c\u5f97\u90a3\u4e11\u4eba\u6e29\u5982\u7389 \u53c8\u81ea\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6b64\u4e8b\u662f\u4f60\u4eb2\u53e3\u7b54\u5e94\u4e8e\u6211\u7684\uff0c\u5927\u4e08\u592b\u4e00\u8a00\u65e2\u51fa\uff0c\u9a77\u9a6c\u96be\u8ffd\uff0c\u4e5f\u662f\u4f60\u4eb2\u53e3 \u6240\u8bf4\u4e4b\u8bdd\uff0c\u6211\u53ea\u5f53\u4f60\u771f\u662f\u4e2a\u8a00\u51fa\u5fc5\u884c\u7684\u5927\u4e08\u592b\uff0c\u54ea\u77e5\u9053\u2014\u2014\u54fc\u54fc\uff0c\u5982\u4eca\u4f60\u5374\u505a\u51fa\u8fd9\u79cd\u6a21\u6837 \u672a\uff0c\u8ba9\u6211\u8001\u4eba\u5bb6\u77a7\u89c1\u4e86\uff0c\u5b9e\u5728\u5931\u671b\u5f97\u5f88\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u76ee\u5149\u4e00\u62fe\uff0c\u53ea\u89c1\u8fd9\u6e29\u5982\u7389\u76ee\u5149\u4e4b\u4e2d\uff0c\u6ee1\u662f\u8ba5\u8bbd\u5632\u7b11\u4e4b\u610f\uff0c\u5fc3\u4e2d\u4e0d\u7531\u70ed\u8840\u4e0a\u6d8c\uff0c\u5fd6 \u9053\uff1a\u201c\u53e4\u4e4b\u5c3e\u751f\uff0c\u4e0e\u5973\u4e8e\u7ea6\u4e8e\u6865\u4e0b\uff0c\u5973\u5b50\u672a\u81f3\u6d2a\u6c34\u5374\u81f3\uff0c\u5c3e\u751f\u5b81\u6b7b\u800c\u4e0d\u5931\u4fe1\uff0c\u7adf\u62b1\u67f1\u800c\u6b7b\uff0c \u5176\u4eba\u867d\u6b7b\uff0c\u5176\u540d\u5374\u7559\u4e4b\u5343\u53e4\uff0c\u6211\u5353\u957f\u537f\u4e0d\u80fd\u5c3d\u5fe0\u4e8e\u56fd\uff0c\u53c8\u65e0\u6cd5\u627f\u6b22\u4e8e\u7236\u6bcd\u819d\u4e0b\uff0c\u8fd9\u4fe1\u4e4b\u4e00 \u5b57\uff0c\u65e0\u8bba\u5982\u4f55\u4e5f\u5f97\u5b88\u4ed6\u4e00\u5b88\uff0c\u6211\u7239\u7239\u6614\u5e74\u662f\u4f55\u7b49\u82f1\u96c4\uff0c\u4ed6\u8001\u4eba\u5bb6\u4e5d\u6cc9\u4e4b\u4e0b\u82e5\u6709\u77e5\uff0c\u60f3\u5fc5\u4e5f\u4e0d \u613f\u610f\u6211\u505a\u4e2a\u5931\u4fe1\u4e8e\u4eba\u7684\u61e6\u592b\uff0c\u8ba9\u8fd9\u6e29\u5982\u7389\u6765\u8baa\u7b11\u4e8e\u6211\u3002\u201d \u3000\u3000\u4e00\u5ff5\u81f3\u6b64\uff0c\u5fc3\u80f8\u4e4b\u95f4\uff0c\u4e0d\u89c9\u8c6a\u6c14\u5927\u4f5c\uff0c\u6717\u58f0\u9053\uff1a\u201c\u6b64\u4e8b\u65e2\u662f\u6211\u4eb2\u53e3\u6240\u8bf4\uff0c\u6211\u81ea\u7136\u7edd\u5bf9\u4e0d \u4f1a\u53cd\u6094\uff0c\u53ea\u662f\u6211\u7eb5\u7136\u5a36\u4e86\u4f60\u7684\u5f92\u5f1f\uff0c\u4e09\u5e74\u4e4b\u5185\uff0c\u6211\u4ecd\u5fc5\u5b9a\u5bfb\u4f60\u590d\u4ec7\uff0c\u4f60\u82e5\u4ee5\u4e3a\u6211\u4f1a\u5fd8\u4e86\u590d\u4ec7 \u4e4b\u4e8b\uff0c\u90a3\u4f60\u5374\u662f\u5927\u5927\u7684\u9519\u4e86\u3002\u201d \u3000\u3000\u6e29\u5982\u7389\u51b7\u51b7\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u83ab\u8bf4\u4e09\u5e74\uff0c\u5c31\u7b97\u4e09\u5341\u5e74\uff0c\u6211\u8001\u4eba\u5bb6\u4e00\u6837\u7b49\u7740\u4f60\u6765\u590d\u4ec7\uff0c\u53ea\u6015\u2014 \u2014\u54fc\u54fc\u3002\u201d \u3000\u3000\u5979\u51b7\u54fc\u4e24\u58f0\uff0c\u500f\u7136\u4e2d\u6b62\u4e86\u81ea\u5df1\u7684\u8bdd\uff0c\u8a00\u4e0b\u4e4b\u610f\uff0c\u5374\u662f\u53ea\u6015\u4f60\u8fd9\u4e00\u751f\u4e00\u4e16\uff0c\u82e5\u60f3\u627e\u6211\u590d \u4ec7\uff0c\u4ea6\u662f\u65e0\u671b\u7684\u3002 \u3000\u3000\u5353\u957f\u537f\u5fc3\u667a\u7edd\u9876\uff0c\u7109\u6709\u542c\u4e0d\u51fa\u5979\u8a00\u4e0b\u4e4b\u610f\u7684\u9053\u7406\uff0c\u5251\u7709\u5fae\u8f69\uff0c\u65b9\u6b32\u53cd\u5507\u76f8\u8ba5\uff0c\u5374\u89c1\u8fd9\u7ea2 \u8863\u5a18\u5a18\u7a81\u7136\u4e00\u62c2\u888d\u8896\uff0c\u957f\u8eab\u800c\u8d77\uff0c\u5411\u5353\u957f\u537f\u51b7\u51b7\u77a5\u4e86\u4e00\u773c\uff0c\u63a5\u7740\u53c8\u9053\uff1a\u201c\u516b\u6708\u4e2d\u79cb\u4e4b\u65e5\uff0c\u4f60 \u65e0\u8bba\u6709\u7740\u4f55\u4e8b\uff0c\u4e5f\u5f97\u7acb\u523b\u653e\u4e0b\uff0c\u5230\u5929\u76ee\u5c71\u53bb\u2026\u2026\u201d \u3000\u3000\u5353\u957f\u537f\u4e00\u633a\u80f8\u819b\uff0c\u6717\u58f0\u63a5\u53e3\u9053\uff1a\u201c\u7eb5\u7136\u6211\u5353\u957f\u537f\u5316\u9aa8\u626c\u7070\uff0c\u516b\u6708\u5341\u4e94\u90a3\u4e00\u5929\uff0c\u4e5f\u5b9a\u8981\u8d76 \u5230\u5929\u76ee\u5c71\u53bb\uff0c\u9601\u4e0b\u5927\u53ef\u653e\u5fc3\uff0c\u59d3\u5353\u7684\u4e16\u4ee3\u76f8\u4f20\uff0c\u4ece\u672a\u6709\u8fc7\u4e00\u4eba\u662f\u8a00\u800c\u65e0\u500c\u4e4b\u5f92\u3002\u201d \u3000\u3000\u6e29\u5982\u7389\u76ee\u5149\u4e4b\u4e0b\uff0c\u7adf\u4f3c\u53c8\u9690\u6cdb\u7b11\u610f\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u5982\u6b64\u4fbf\u597d\u3002\u201d \u3000\u3000\u76ee\u5149\u4e00\u8f6c\uff0c\u8f6c\u5411\u90a3\u8fb9\u89c1\u88ab\u56f0\u5728\u7ea2\u6749\u821e\u5f71\u4e2d\u9ec4\u886b\u5c11\u5e74\u5c91\u7cb2\uff0c\u773c\u4e2d\u6240\u9690\u6cdb\u7684\u7b11\u5bb9\uff0c\u7acb\u65f6\u4fbf\u53c8 \u6362\u4f5c\u51b7\u524a\u8083\u6740\u4e4b\u610f\uff0c\u7f13\u6b65\u8d70\u4e0b\u8f66\u5b50\uff0c\u7a81\u53c8\u8f7b\u8f7b\u4e00\u62cd\u624b\u638c\uff0c\u5353\u957f\u537f\u4e0d\u7531\u81ea\u4e3b\u5730\u987a\u7740\u4ed6\u7684\u76ee\u5149\u671b \u53bb\uff0c\u53ea\u89c1\u638c\u58f0\u65b9\u843d\uff0c\u90a3\u4e9b\u7ea2\u88f3\u5c11\u5973\u4fbf\u4e00\u8d77\u987f\u4f4f\u8eab\u5f62\uff0c\u52a8\u4f5c\u6d51\u5982\u4e00\u4f53\uff0c\u5168\u65e0\u5feb\u6162\u4e4b\u5206\u3002 \u3000\u3000\u800c\u90a3\u9ec4\u886b\u5c11\u5e74\u5c91\u7cb2\uff0c\u5374\u662f\u987b\u53d1\u51cc\u4e71\uff0c\u6ee1\u5934\u6c57\u73e0\uff0c\u6c14\u5598\u54bb\u54bb\u5730\u7ad9\u5728\u4e2d\u95f4\uff0c\u5148\u524d\u90a3\u79cd\u6f47\u6d12\u72c2 \u50b2\u4e4b\u6001\uff0c\u5982\u4eca\u5374\u5df2\u53d8\u5f97\u72fc\u72c8\u4e0d\u582a\uff0c\u7adf\u8fde\u90a3\u53cc\u70af\u7136\u6709\u5149\u7684\u773c\u775b\uff0c\u90fd\u5df2\u5931\u53bb\u539f\u6709\u5149\u91c7\uff0c\u671b\u7740\u6e29\u5982 \u7389\u98a4\u58f0\u9053\uff1a\u201c\u5bb6\u5e08\u7eb5\u7136\u4e0e\u4f60\u4e0d\u7766\uff0c\u4f60\u53c8\u4f55\u5fc5\u6041\u5730\u7f9e\u8fb1\u4e8e\u6211\u2026\u2026\u201d \u3000\u3000\u8bdd\u72b9\u672a\u4e86\uff0c\u7adf\u201c\u6251\u201d\u7684\u4e00\uff0c\u58f0\uff0c\u5750\u5230\u5730\u4e0a\uff0c\u663e\u89c1\u662f\u5c06\u5168\u8eab\u7cbe\u529b\uff0c\u5168\u90fd\u8017\u5c3d\uff0c\u6b64\u523b\u7eb5\u7136\u662f \u4e2a\u666e\u901a\u58ee\u6c49\u6253\u4ed6\u4e00\u3001\u62f3\uff0c\u53ea\u4f2f\u4ed6\u4e5f\u662f\u65e0\u6cd5\u8fd8\u624b\u7684\u4e86\u3002 \u3000\u3000\u5353\u957f\u537f\u4e0e\u4ed6\u867d\u7136\u662f\u654c\u975e\u53cb\uff0c\u4f46\u6b64\u523b\u89c1\u4e86\u4ed6\u8fd9\u79cd\u6a21\u6837\uff0c\u5fc3\u4e0b\u4ecd\u7136\u5927\u4e3a\u4e0d\u5fcd\uff0c\u7f13\u7f13\u8f6c\u8fc7\u8eab \u5b50\uff0c\u4e0d\u518d\u671b\u4ed6\u4e00\u773c\u3002 \u3000\u3000\u6e29\u5982\u7389\u51b7\u7b11\u4e00\u58f0\uff0c\u8f7b\u8f7b\u505a\u4e86\u4e2a\u624b\u52bf\uff0c\u4ea6\u81ea\u8f6c\u8eab\u56de\u5230\u8f66\u4e0a\uff0c\u90a3\u4e9b\u7ea2\u88f3\u5c11\u5973\u4f7f\u5c06\u5c91\u7cb2\u534a\u62c9\u534a \u626f\u5730\u6276\u4e86\u8d77\u6765\uff0c\u4e00\u4eba\u7ea4\u624b\u5fae\u62c2\uff0c\u5728\u4ed6\u80f8\u53e3\u7487\u5de9\u7a74\u4e0a\u8f7b\u8f7b\u4e00\u70b9\uff0c\u77ac\u606f\u4e4b\u95f4\uff0c\u8fd9\u884c\u5c11\u5973\uff0c\u4fbf\u53c8\u6276 \u8f66\u800c\u53bb\uff0c\u53ea\u542c\u90a3\u7ea2\u8863\u5a18\u5a18\u51b7\u7136\u56de\u9996\u9053\uff1a\u201c\u6b64\u523b\u8ddd\u79bb\u516b\u6708\u4e2d\u79cb\u5df2\u65e0\u591a\u4e45\uff0c\u4f60\u8fd8\u662f\u5bfb\u4e2a\u5730\u65b9\uff0c\u597d \u597d\u518d\u7ec3\u7ec3\u529f\u592b\u5427\uff0c\u5c31\u51ed\u4f60\u6b64\u523b\u7684\u8eab\u624b\u2026\u2026\u54fc\uff0c\u53ea\u6015\u8fd8\u672a\u5fc5\u6210\u5462\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u6014\u6014\u7684\u671b\u7740\u5979\u4eec\u7ea2\u8272\u8eab\u5f71\u6e10\u6e10\u6d88\u5931\u5728\u521d\u79cb\u7fe0\u7eff\u7684\u6797\u91ce\u91cc\uff0c\u6697\u4e2d\u957f\u53f9\u4e00\u58f0\uff0c\u53ea\u89c9\u81ea \u5df1\u4e00\u751f\u4e4b\u4e2d\uff0c\u906d\u9047\u4e4b\u5947\uff0c\u83ab\u8fc7\u4e8e\u65b9\u624d\u8fd9\u4e11\u4eba\u6e29\u52a0\u7389\u6240\u6253\u8d4c\u4e4b\u4e8b\u4e86\uff0c\u4ed6\u867d\u662f\u806a\u660e\u7edd\u9876\u4e4b\u4eba\uff0c\u5374 \u4e5f\u4e07\u4e07\u6599\u60f3\u4e0d\u5230\u81ea\u5df1\u8fd9\u4e0d\u5171\u6234\u5929\u7684\u4ec7\u4eba\uff0c\u4e0d\u60dc\u4ee5\u81ea\u5bb6\u6027\u547d\u672a\u8d4c\u4e4b\u4e8b\uff0c\u7adf\u662f\u8981\u8ba9\u81ea\u5df1\u6765\u5a36\u5979\u7684 \u5f92\u5f1f\u3002 \u3000\u3000\u4ed6\u4e0d\u6562\u60f3\u8c61\u6b64\u4e8b\u65e5\u540e\u5c06\u8981\u53d1\u5c55\u5230\u4f55\u79cd\u5730\u6b65\uff0c\u56e0\u4e3a\u6b64\u4e8b\u6839\u672c\u5c31\u4ee4\u4eba\u65e0\u6cd5\u601d\u8bae\uff0c\u7ad9\u5728\u521d\u79cb\u4ecd \u7136\u9177\u70ed\u7684\u9633\u5149\u91cc\uff0c\u4ed6\u5446\u5446\u5730\u6115\u4e86\u534a\u54cd\uff0c\u7a81\u53c9\u60f3\u9053\uff1a\u201c\u6628\u591c\u5feb\u5200\u4f1a\u4f17\u7684\u60e8\u6b7b\uff0c\u4e0d\u77e5\u7a76\u7adf\u662f\u8c01\u5e72 \u7684\uff0c\u96be\u9053\u6e29\u747e\u542c\u4e86\u9ec4\u5c71\u59cb\u4fe1\u5cf0\u4e0b\u94c1\u8239\u5934\u91cc\u5f02\u517d\u661f\u870d\u7684\u90a3\u4e00\u6bb5\u6545\u4e8b\uff0c\u4e5f\u60f3\u5c06\u5929\u4e0b\u6b66\u6797\u8c6a\u58eb\u90fd\u8bf1 \u5230\u8fd9\u5929\u76ee\u5c71\u4e0b\u6765\uff0c\u7136\u540e\u4e5f\u5b66\u90a3\u661f\u870d\u7684\u6837\u5b50\uff0c\u5c06\u4ed6\u4eec\u4e00\u4e2a\u4e2a\u6740\u6b7b\u5417\uff1f\u201d \u3000\u3000\u60f3\u5230\u8fd9\u91cc\uff0c\u4ed6\u5168\u8eab\u4e0d\u7981\u4e3a\u4e4b\u6cdb\u8d77\u4e00\u9635\u5bd2\u610f\uff0c\u773c\u524d\u4f3c\u4e4e\u53c8\u6cdb\u8d77\u5341\u5e74\u4e4b\u524d\uff0c\u59cb\u4fe1\u5cf0\u4e0b\uff0c\u90a3\u4e9b \u86c7\u866b\u731b\u517d\uff0c\u4e89\u5148\u6050\u540e\u7684\u5954\u5411\u94c1\u8239\u5934\u53bb\u7684\u60c5\u666f\uff0c\u4e0d\u7981\u957f\u53f9\u4e00\u58f0\uff0c\u5fd6\u9053\uff1a\u201c\u90a3\u4e9b\u866b\u517d\u4f55\u5c1d\u4e0d\u77e5\u9053 \u81ea\u5df1\u6b64\u53bb\u5b9e\u662f\u9001\u6b7b\uff0c\u4f46\u5374\u4ecd\u7136\u65e0\u6cd5\u6297\u62d2\u90a3\u661f\u870d\u6563\u53d1\u51fa\u7684\u9999\u6c14\uff0c\u660e\u77e5\u9001\u6b7b\uff0c\u8fd8\u662f\u7167\u53bb\u4e0d\u8bef\uff0c\u800c \u6b64\u523b\u8fd9\u4e9b\u4e0d\u8fdc\u5343\u91cc\u8dcb\u6d89\u800c\u6765\u7684\u6b66\u6797\u8c6a\u58eb\uff0c\u53c8\u4f55\u5c1d\u80fd\u6297\u62d2\u90a3\u6e29\u747e\u5929\u76ee\u5c71\u4e2d\u8bbe\u4e0b\u7684\u79cd\u79cd\u8bf1\u60d1\u5462\uff1f \u53ea\u6015\u4ed6\u4eec\u4e5f\u548c\u90a3\u4e9b\u65e0\u77e5\u866b\u517d\u4e00\u6837\uff0c\u660e\u77e5\u5982\u6b64\uff0c\u4e5f\u8981\u53bb\u8bd5\u4e0a\u4e00\u8bd5\u4e86\u3002\u201d \u3000\u3000\u4ed6\u5fc3\u5ff5\u6570\u8f6c\uff0c\u8d8a\u60f3\u8d8a\u89c9\u5f97\u8fd9\u5929\u76ee\u5c71\u4e2d\u7684\u6b66\u6797\u76db\u4f1a\uff0c\u5b9e\u662f\u4e00\u4e2a\u6781\u5927\u7684\u9677\u9631\uff0c\u5f53\u4e0b\u6253\u5b9a\u4e3b \u610f\uff0c\u65e0\u8bba\u5982\u4f55\uff0c\u81ea\u5df1\u65e2\u7136\u77e5\u9053\u6b64\u4e8b\uff0c\u5c31\u5f97\u5c06\u8fd9\u573a\u6b66\u6797\u6d69\u52ab\u6d88\u4e8e\u65e0\u5f62\uff0c\u53ea\u662f\u81ea\u5df1\u8be5\u5982\u4f55\u53bb\u505a \u5462\uff1f\u5374\u4ecd\u7136\u832b\u7136\u65e0\u5934\u7eea\u3002 \u3000\u3000\u6b64\u523b\u5728\u4ed6\u8eab\u540e\u7684\u6797\u6728\u4e4b\u4e2d\u7a81\u7136\u7f13\u7f13\u8e31\u51fa\u4e00\u4e2a\u7384\u670d\u9ad8\u51a0\u7684\u957f\u9aef\u8001\u8005\u6765\uff0c\u811a\u4e0b\u7a7f\u7740\u867d\u662f\u539a\u8fbe \u4e09\u5bf8\u7684\u539a\u5e95\u5b98\u9774\uff0c\u4f46\u884c\u8d70\u4e4b\u95f4\uff0c\u5374\u4ecd\u662f\u6f2b\u65e0\u58f0\u606f\uff0c\u800c\u4e14\u4ed6\u51fa\u73b0\u5f97\u53c8\u662f\u90a3\u4e48\u7a81\u7136\uff0c\u751f\u50cf\u662f\u6811\u6728 \u7684\u7cbe\u7075\uff0c\u7a81\u7136\u7531\u5730\u5e95\u6d8c\u73b0\uff0c\u53c8\u4f3c\u4e4e\u662f\u8bb8\u4e45\u4ee5\u524d\uff0c\u4ed6\u4fbf\u5df2\u5728\u90a3\u6811\u6797\u4e4b\u4e2d\uff0c\u53ea\u662f\u76f4\u5230\u6b64\u523b\uff0c\u4ed6\u65b9 \u81ea\u73b0\u51fa\u8eab\u5f62\u6765\u3002 \u3000\u3000\u4ed6\u7f13\u7f13\u8d70\u5230\u90a3\u4fef\u9996\u6c89\u601d\u7740\u7684\u5353\u957f\u537f\u8eab\u4fa7\uff0c\u7a81\u7136\u6717\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5144\u53f0\u53cc\u7709\u6df1\u76b1\uff0c\u9762\u5e26\u5fe7 \u8272\uff0c\u96be\u9053\u5fc3\u4e2d\u6709\u7740\u4ec0\u4e48\u5fe7\u6101\u4e4b\u4e8b\uff1f\u201d \u3000\u3000\u5353\u957f\u537f\u84e6\u5730\u4e00\u60ca\uff0c\u62ac\u76ee\u800c\u671b\uff0c\u53ea\u89c1\u81ea\u5df1\u8eab\u4fa7\u8d6b\u7136\u591a\u4e86\u4e00\u4e2a\u957f\u8eab\u7389\u7acb\u3001\u4e30\u795e\u51b2\u5937\u7684\u957f\u9aef\u8001 \u8005\uff0c\u6b63\u81ea\u542b\u7b11\u671b\u7740\u81ea\u5df1\u3002 \u3000\u3000\u9633\u5149\u8000\u76ee\uff0c\u5c06\u8fd9\u8001\u8005\u9894\u4e0b\u957f\u9aef\uff0c\u6620\u5f97\u6f06\u9ed1\u5149\u4eae\uff0c\u4e5f\u6620\u5f97\u4ed6\u90a3\u9690\u542b\u7b11\u610f\u53cc\u773c\uff0c\u795e\u5149\u5b9b\u5982\u5229 \u526a\uff0c\u4e00\u773c\u671b\u53bb\uff0c\u5353\u957f\u537f\u4f46\u89c9\u6b64\u4eba\u5e74\u7eaa\u867d\u4f3c\u5df2\u8fd1\u53e4\u7a00\uff0c\u4f46\u795e\u91c7\u4e4b\u95f4\uff0c\u5374\u4ecd\u6f47\u6d12\u65e0\u6bd4\uff0c\u5b9b\u7136\u5e26\u7740 \u51e0\u5206\u4ed9\u6c14\u3002 \u3000\u3000\u4ed6\u65b9\u624d\u867d\u662f\u51dd\u795e\u800c\u601d\uff0c\u4f46\u81ea\u4fe1\u8033\u76ee\u4ecd\u7136\u5f02\u5e38\u7075\u654f\uff0c\u6b64\u523b\u89c1\u8fd9\u8001\u8005\u5df2\u7ecf\u6765\u5230\u81ea\u5df1\u8eab\u4fa7\uff0c\u800c \u81ea\u5df1\u5374\u4ecd\u672a\u89c9\u5bdf\uff0c\u5fc3\u4e0b\u53c8\u4e0d\u7981\u4e3a\u4e4b\u9a87\u7136\uff0c\u5446\u5446\u5730\u6115\u4e86\u4e00\u6115\uff0c\u5374\u89c1\u90a3\u8001\u8005\u53c8\u81ea\u6717\u58f0\u7b11\u9053\uff1a\u201c\u5343 \u53e4\u4ee5\u6765\uff0c\u5c11\u5e74\u4eba\u591a\u534a\u672a\u66fe\u8bc6\u5f97\u6101\u4e2d\u6ecb\u5473\uff0c\u5144\u53f0\u867d\u7136\u6e29\u6587\u5c14\u96c5\uff0c\u4f46\u7709\u76ee\u4e4b\u95f4\uff0c\u5374\u662f\u82f1\u6c14\u903c\u4eba\uff0c \u8001\u592b\u81ea\u95ee\u53cc\u76ee\u4e0d\u8a00\uff0c\u4e00\u671b\u800c\u77e5\uff0c\u5144\u53f0\u5fc5\u5b9a\u662f\u4f4d\u8eab\u6000\u7edd\u6280\u7684\u5c11\u5e74\u82f1\u96c4\uff0c\u7edd\u975e\u90a3\u4e9b\u4e3a\u8d4b\u65b0\u8bcd\u5f3a\u8bf4 \u6101\u7684\u9178\u4e01\u53ef\u6bd4\uff0c\u6b64\u523b\u5374\u4e3a\u7740\u4f55\u4e8b\uff0c\u5982\u6b64\u6101\u7709\u4e0d\u5c55\u5462\uff1f\u201d \u3000\u3000\u8fd9\u8001\u8005\u4e0d\u4f46\u4e30\u795e\u51b2\u5937\uff0c\u800c\u4e14\u8a00\u8bed\u6e05\u6717\uff0c\u4ee4\u4eba\u89c1\u4e86\u65e0\u6cd5\u4e0d\u751f\u597d\u611f\u3002 \u3000\u3000\u5353\u957f\u537f\u6b64\u523b\u867d\u5bf9\u8fd9\u8001\u8005\u6709\u5982\u5e7d\u7075\u4e00\u822c\u7a81\u7136\u51fa\u73b0\u5927\u611f\u60ca\u5f02\uff0c\u5374\u53c8\u4e0d\u7981\u4e3a\u4ed6\u8fd9\u79cd\u6f47\u6d12\u795e\u6001\u6e05 \u6717\u8a00\u8bcd\u6240\u9189\uff0c\u542b\u7b11\u4e00\u63d6\uff0c\u4ea6\u81ea\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u957f\u8005\u5782\u8be2\uff0c\u5c0f\u53ef\u5fc3\u4e2d\u786e\u662f\u6101\u70e6\u7d0a\u4e71\uff0c\u4e0d\u80fd\u81ea \u5df2\u3002\u201d \u3000\u3000\u8fd9\u957f\u9aef\u8001\u8005\u6717\u58f0\u4e00\u7b11\uff0c\u634b\u987b\u7b11\u9053\uff1a\u201c\u5144\u53f0\u5982\u679c\u4e0d\u5acc\u8001\u592b\u5192\u6627\uff0c\u4e0d\u77e5\u53ef\u5426\u5c06\u5fc3\u4e2d\u70e6\u6101\u4e4b\u4e8b \u8bf4\u4e0e\u8001\u592b\u4e00\u542c\uff0c\u8001\u592b\u867d\u7136\u788c\u788c\u65e0\u80fd\uff0c\u5374\u7ec8\u662f\u75f4\u957f\u51e0\u5c81\uff0c\u4e5f\u8bb8\u80fd\u4e3a\u5144\u53f0\u5206\u4f18\u4e00\u4e8c\uff0c\u4ea6\u672a\u53ef \u77e5\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u62ac\u76ee\u800c\u671b\uff0c\u53ea\u89c9\u8fd9\u8001\u8005\u76ee\u5149\u4e4b\u4e2d\uff0c\u751f\u50cf\u662f\u6709\u79cd\u4ee4\u4eba\u65e0\u602f\u6297\u62d2\u7684\u529b\u91cf\uff0c\u957f\u53f9\u4e00\u58f0\uff0c \u9053\uff1a\u201c\u65e2\u627f\u957f\u8005\u5173\u6000\uff0c\u5c0f\u53ef\u6562\u4e0d\u4ece\u547d\u2026\u2026\u201d \u3000\u3000\u5fc3\u5ff5\u4e00\u8f6c\uff0c\u7a81\u7136\u60f3\u5230\u81ea\u5df1\u5fc3\u4e2d\u65e0\u6cd5\u5316\u89e3\u4e4b\u4e8b\uff0c\u4e0d\u4f46\u6709\u5173\u81ea\u5df1\u4e00\u751f\u547d\u8fd0\uff0c\u800c\u4e14\u662f\u6b66\u6797\u4e4b\u4e2d \u4e00\u4ef6\u7edd\u5927\u79d8\u5bc6\uff0c\u8fd9\u8001\u8005\u8a00\u8bed\u4e4b\u4e2d\uff0c\u867d\u4f3c\u5bf9\u81ea\u5df1\u6781\u4e3a\u5173\u6000\uff0c\u4f46\u81ea\u5df1\u5374\u53c8\u600e\u80fd\u5c06\u8fd9\u79cd\u6709\u5173\u6b66\u6797\u52ab \u8fd0\u751f\u6b7b\u5927\u4e8b\uff0c\u968f\u4fbf\u8bf4\u5c06\u51fa\u6765\uff0c\u4e00\u5ff5\u81f3\u6b64\uff0c\u4fbf\u987f\u4f4f\u4e86\u8bdd\u58f0\uff0c\u671b\u7740\u8fd9\u884c\u8e2a\u8be1\u5f02\u3001\u6b66\u529f\u5374\u4f3c\u7edd\u9ad8\u7684 \u8001\u4eba\uff0c\u534a\u664c\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u54ea\u77e5\u8fd9\u8001\u4eba\u7a81\u53c8\u6717\u58f0\u7b11\u9053\uff1a\u201c\u5144\u53f0\u5982\u4e0d\u613f\u8bf4\uff0c\u8001\u592b\u5b9e\u662f\u2026\u2026\u201d \u3000\u3000\u5353\u957f\u537f\u8f7b\u54bd\u4e00\u58f0\uff0c\u63a5\u53e3\u9053\uff1a\u201c\u5e76\u975e\u5c0f\u53ef\u4e0d\u613f\u8bf4\u4e0e\u8001\u4e08\u77e5\u9053\uff0c\u800c\u662f\u6b64\u4e8b\u5173\u7cfb\u592a\u5927\uff0c\u5982\u679c\u662f \u5c0f\u53ef\u4e00\u4eba\u4e4b\u4e8b\uff0c\u65e2\u627f\u8001\u4e08\u5173\u5207\uff0c\u5c0f\u53ef\u4e07\u65e0\u4e0d\u8bf4\u4e4b\u7406\u3002\u201d \u3000\u3000\u957f\u9aef\u8001\u4eba\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u5144\u53f0\u65e2\u5982\u6b64\u8bf4\uff0c\u8001\u592b\u81ea\u7136\u4e0d\u4fbf\u518d\u95ee\uff0c\u53ea\u662f\u5144\u53f0\u82e5\u5c06\u6b64\u7b49\u5173\u7cfb\u91cd \u5927\u4e4b\u4e8b\u9690\u85cf\u4e8e\u5fc3\uff0c\u4e0d\u53bb\u5bfb\u4eba\u5546\u91cf\u4e00\u4e0b\uff0c\u4ea6\u975e\u5584\u7b56\u2014\u2014\u201d\u4ed6\u4e00\u634b\u957f\u987b\uff0c\u63a5\u7740\u53c8\u9053\uff1a\u201c\u987b\u77e5\u4e00\u4eba \u667a\u6167\u6709\u9650\uff0c\u5144\u53f0\u7eb5\u7136\u662f\u806a\u660e\u7edd\u9876\uff0c\u6050\u4e5f\u65e0\u6cd5\u5c06\u8fd9\u7b49\u5173\u7cfb\u91cd\u5927\u4e4b\u4e8b\uff0c\u60f3\u51fa\u4e00\u4e2a\u9002\u5584\u5bf9\u7b56\u6765\uff0c\u4e0e \u5176\u7a7a\u5728\u8fd9\u91cc\u53d1\u6101\uff0c\u5012\u4e0d\u5982\u5bfb\u4e2a\u77e5\u5fc3\u4e4b\u4eba\u5546\u91cf\u5546\u91cf\uff0c\u8001\u592b\u4e0e\u5144\u53f0\u4ea4\u6d45\u800c\u8a00\u6df1\uff0c\u4f46\u671b\u5144\u53f0\u83ab \u602a\u3002\u201d \u3000\u3000\u4ed6\u53c8\u81ea\u54c8\u54c8\u4e00\u7b11\uff0c\u76ee\u5149\u70af\u7136\uff0c\u51dd\u795e\u671b\u5728\u5353\u957f\u537f\u9762\u4e0a\u3002 \u3000\u3000\u5353\u957f\u537f\u4f46\u89c9\u6b64\u4eba\u8a00\u8bed\u4e4b\u4e2d\u53e5\u53e5\u90fd\u6781\u4e3a\u6709\u7406\uff0c\u4f46\u4ed6\u751f\u6027\u8c28\u614e\uff0c\u7edd\u65e0\u4e00\u822c\u5c11\u5e74\u98de\u626c\u8df3\u8131\u4e4b \u6027\uff0c\u5fc3\u4e2d\u867d\u89c9\u8fd9\u8001\u8005\u4e4b\u606c\u6781\u4e3a\u6709\u7406\uff0c\u5374\u4ecd\u7136\u4e0d\u80af\u5c06\u6b64\u4e8b\u8d38\u7136\u8bf4\u4e86\u51fa\u6765\uff0c\u65b9\u81ea\u4fef\u9996\u6c89\u541f\uff0c\u5374\u542c \u8fd9\u9ad8\u51a0\u8001\u8005\u81ea\u7b11\u9053\uff1a\u201c\u5144\u53f0\u6bcb\u5eb8\u591a\u8651\uff0c\u8001\u592b\u5e76\u65e0\u63a2\u8be2\u5144\u53f0\u9690\u79d8\u4e4b\u610f\uff0c\u5144\u53f0\u5982\u4e0d\u613f\u8bf4\uff0c\u4e5f\u5c31\u7f62 \u4e86\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u6697\u4e2d\u4e00\u53f9\uff0c\u5fc3\u4e2d\u5927\u751f\u6b49\u759a\u4e4b\u610f\uff0c\u987b\u77e5\u51e1\u662f\u81f3\u60c5\u81f3\u6027\u4e4b\u4eba\uff0c\u4fbf\u53d7\u4e0d\u5f97\u4eba\u5bb6\u534a\u5206\u597d \u5904\uff0c\u82e5\u662f\u53d7\u4e86\u4eba\u5bb6\u7684\u597d\u5904\uff0c\u4ed6\u4fbf\u8981\u5343\u65b9\u767e\u8ba1\u5730\u53bb\u62a5\u7b54\u4eba\u5bb6\u7684\u597d\u5904\uff0c\u82e5\u6559\u4ed6\u5f97\u4e86\u4eba\u5bb6\u7684\u597d\u5904\u800c \u4e0d\u53bb\u62a5\u7b54\u4eba\u5bb6\uff0c\u90a3\u5374\u6bd4\u6559\u4ed6\u505a\u4efb\u4f55\u4e8b\u90fd\u8981\u4ee4\u4ed6\u96be\u53d7\u4e9b\u3002 \u3000\u3000\u6b64\u523b\u5353\u957f\u537f\u5fc3\u4e2d\u4fbf\u662f\u89c9\u5f97\u8fd9\u8001\u8005\u867d\u4e0e\u81ea\u5df1\u7d20\u4e0d\u76f8\u8bc6\uff0c\u4f46\u65e0\u8bba\u5982\u4f55\uff0c\u4eba\u5bb6\u5bf9\u81ea\u5df1\u603b\u662f\u4e00\u756a \u597d\u610f\uff0c\u800c\u81ea\u5df1\u5374\u65e0\u6cd5\u62a5\u7b54\u4eba\u5bb6\u8fd9\u756a\u597d\u610f\uff0c\u662f\u4ee5\u5fc3\u4e2d\u4fbf\u751f\u6b49\u759a\u4e4b\u5fc3\u6765\u3002 \u3000\u3000\u90a3\u957f\u9aef\u8001\u8005\u671b\u7740\u4ed6\u7684\u9762\u8272\uff0c\u5634\u89d2\u4e0d\u7981\u6cdb\u8d77\u4e00\u4e1d\u7b11\u5bb9\uff0c\u50cf\u662f\u5341\u5206\u5f97\u610f\uff0c\u53ea\u4ed6\u8fd9\u79cd\u7b11\u5bb9\u5374\u88ab \u4ed6\u7684\u63a9\u53e3\u957f\u987b\u4e00\u8d77\u63a9\u4f4f\uff0c\u5353\u957f\u537f\u518d\u4e5f\u65e0\u6cd5\u770b\u51fa\u6765\u800c\u5df2\u3002 \u3000\u3000\u4ed6\u5446\u5446\u7684\u6123\u4e86\u534a\u664c\uff0c\u5fc3\u4e2d\u5fcd\u4e0d\u4f4f\u8981\u5c06\u6b64\u4e8b\u8bf4\u4e86\u51fa\u6765\uff0c\u4f46\u5ffd\u800c\u53c8\u5fcd\u4e86\u4e0b\u53bb\uff0c\u6c89\u541f\u518d\u4e09\uff0c\u7ec8 \u4e8e\u53f9\u9053\uff1a\u201c\u8001\u4e08\u5982\u6b64\u5173\u6000\u4e8e\u6211\uff0c\u5c0f\u53ef\u5374\u6709\u8d1f\u8001\u4e08\u76db\u60c5\uff0c\u5b9e\u5728\u96be\u53d7\u5f97\u5f88\u2014\u2014\u201d\u957f\u9aef\u8001\u4eba\u634b\u987b\u4e00 \u7b11\uff0c\u622a\u65ad\u4e86\u4ed6\u7684\u8bdd\uff0c\u542b\u7b11\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u5144\u53f0\u5982\u6b64\u8bf4\uff0c\u5374\u662f\u89c1\u5916\u4e86\uff0c\u8001\u592b\u4e0e\u5144\u53f0\u867d\u662f\u840d\u6c34\u76f8 \u9022\uff0c\u5bf9\u5144\u53f0\u4e3a\u4eba\uff0c\u5374\u503e\u6155\u5f97\u5f88\uff0c\u5144\u53f0\u5982\u4e0d\u5acc\u5f03\uff0c\u4e0d\u77e5\u53ef\u5426\u8ba9\u8001\u592b\u505a\u4e2a\u5c0f\u5c0f\u4e1c\u9053\uff0c\u5bfb\u4e2a\u9e21\u9152\u91ce \u5e97\u653e\u6000\u4e00\u9189\uff0c\u4e00\u6765\u4e5f\u8ba9\u5144\u53f0\u6d88\u9063\u6101\u6000\uff0c\u518d\u8005\u8001\u592b\u4e5f\u53ef\u591a\u8046\u542c\u4e9b\u6559\u76ca\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u957f\u63d6\u8c22\u9053\uff1a\u201c\u606d\u656c\u4e0d\u5982\u4ece\u547d\uff0c\u53ea\u662f\u53e8\u6270\u8001\u4e08\u4e86\u3002\u201d \u3000\u3000\u4ed6\u5fc3\u4e2d\u5bf9\u8fd9\u9ad8\u51a0\u8001\u8005\u672c\u6709\u6b49\u759a\u4e4b\u610f\uff0c\u6b64\u523b\u81ea\u7136\u4e00\u53e3\u7b54\u5141\uff0c\u4e24\u4eba\u5e76\u80a9\u800c\u884c\uff0c\u90a3\u9ad8\u51a0\u957f\u9aef\u8001 \u8005\u8a00\u8c08\u98ce\u96c5\uff0c\u8bed\u58f0\u6e05\u6717\uff0c\u4e00\u8def\u4e4b\u4e0a\uff0c\u5a13\u5a13\u800c\u8c08\uff0c\u5374\u7edd\u53e3\u4e0d\u63d0\u65b9\u624d\u6240\u95ee\u4e4b\u4e8b\u3002 \u3000\u3000\u987f\u996d\u5149\u666f\uff0c\u4e34\u5b89\u57ce\u5ed3\uff0c\u4fbf\u5df2\u5728\u671b\uff0c\u5728\u8fd9\u6bb5\u65f6\u95f4\u91cc\uff0c\u5353\u957f\u537f\u4e0d\u89c9\u5df2\u5bf9\u9ad8\u51a0\u8001\u8005\u5927\u751f\u597d\u611f\uff0c \u53e3\u4e2d\u6697\u5fd6\uff1a\u201c\u8fd9\u8001\u8005\u4e0d\u4f46\u4e30\u795e\u51b2\u5937\uff0c\u8c08\u5410\u9ad8\u5999\uff0c\u800c\u4e14\u6b66\u529f\u4eff\u4f5b\u7edd\u9ad8\uff0c\u8f7b\u529f\u66f4\u4eff\u4f5b\u8fd8\u5728\u6211\u4e4b \u4e0a\uff0c\u50cf\u4ed6\u8fd9\u79cd\u4eba\u7269\uff0c\u5fc5\u5b9a\u662f\u6b66\u6797\u4e2d\u5927\u5927\u6709\u540d\u7684\u89d2\u8272\u3002\u201d \u3000\u3000\u4e00\u5ff5\u81f3\u6b64\uff0c\u4e0d\u7531\u8f6c\u9996\u542b\u7b11\u95ee\u9053\uff1a\u201c\u5c0f\u53ef\u5353\u957f\u537f\uff0c\u4e0d\u77e5\u8001\u4e08\u9ad8\u59d3\u5927\u540d\uff1f\u53ef\u5426\u89c1\u544a\u3002\u201d \u3000\u3000\u90a3\u957f\u9aef\u8001\u8005\u5fae\u5fae\u4e00\u7b11\uff1a\u201c\u8001\u592b\u98d8\u6cca\u98ce\u5c18\uff0c\u591a\u5e74\u4ee5\u524d\uff0c\u4fbf\u5c06\u59d3\u540d\u5fd8\u6000\u4e86\uff0c\u6c5f\u6e56\u4e2d\u4eba\u6709\u8bc6\u5f97 \u8001\u592b\u7684\uff0c\u591a\u79f0\u8001\u592b\u4e00\u58f0\u9ad8\u51a0\u7fbd\u58eb\uff0c\u7fbd\u58eb\u4e24\u5b57\uff0c\u8001\u592b\u6127\u4e0d\u6562\u5f53\uff0c\u8fd9\u9ad8\u51a0\u4e8c\u5b57\uff0c\u5374\u786e\u662f\u540d\u526f\u5176 \u5b9e\uff0c\u662f\u4ee5\u8001\u592b\u4fbf\u4e5f\u5374\u4e4b\u4e0d\u606d\uff0c\u4e5f\u81ea\u79f0\u4e3a\u9ad8\u51a0\u7fbd\u58eb\u4e86\u3002\u201d \u3000\u3000\u4ed6\u6717\u58f0\u4e00\u7b11\uff0c\u624b\u6307\u524d\u65b9\uff0c\u542b\u7b11\u53c8\u9053\uff1a\u201c\u524d\u9762\u9752\u5e18\u9ad8\u6311\uff0c\u60f3\u5fc5\u6709\u4e2a\u5c0f\u5c0f\u9152\u94fa\uff0c\u8fd9\u79cd\u8352\u6751\u91ce \u5e97\uff0c\u867d\u7136\u7c97\u964b\u4e9b\uff0c\u4f46\u4f60\u6211\u5374\u53ef\u8131\u7565\u5f62\u8ff9\uff0c\u653e\u6000\u7545\u8c08\uff0c\u5012\u6bd4\u90a3\u4e9b\u9152\u697c\u996d\u5e84\u8981\u597d\u5f97\u591a\u4e86\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u53e3\u4e2d\u81ea\u662f\u8fde\u58f0\u79f0\u662f\uff0c\u5fc3\u4e2d\u5374\u4e0d\u7981\u5927\u4e3a\u5947\u602a\uff0c\u8fd9\u9ad8\u51a0\u7fbd\u58eb\u56db\u5b57\uff0c\u867d\u4ea6\u6781\u4e3a\u9ad8\u96c5\uff0c\u4f46 \u5374\u4e0d\u662f\u58f0\u540d\u663e\u8d6b\u7684\u59d3\u6c0f\uff0c\u53f8\u7a7a\u8001\u4eba\u867d\u7136\u8db3\u8ff9\u4e45\u5df2\u4e0d\u5c65\u4eba\u4e16\uff0c\u4f46\u5bf9\u5929\u4e0b\u5404\u95e8\u5404\u6d3e\u7684\u5947\u4eba\u5f02\u58eb\uff0c \u90fd\u77e5\u4e4b\u751a\u8be6\uff0c\u4e5f\u66fe\u975e\u5e38\u4ed4\u7ec6\u5730\u5bf9\u5353\u957f\u537f\u8bf4\u4e86\u4e00\u904d\u3002 \u3000\u3000\u4f46\u5353\u957f\u537f\u6b64\u523b\u641c\u904d\u8bb0\u5fc6\uff0c\u5374\u4e5f\u60f3\u4e0d\u51fa\u8fd9\u9ad8\u51a0\u7fbd\u58eb\u56db\u5b57\u7684\u51fa\u6765\uff0c\u8fd9\u9ad8\u51a0\u7fbd\u58eb\u56db\u5b57\uff0c\u82e5\u662f\u90a3 \u9ec4\u886b\u5c11\u5e74\u7684\u540d\u5b57\uff0c\u5353\u957f\u537f\u4fbf\u4e0d\u4f1a\u751f\u51fa\u5947\u602a\u7684\u611f\u89c9\u6765\u3002 \u3000\u3000\u56e0\u4e3a\u90a3\u9ec4\u886b\u5c11\u5e74\u5c91\u7cb2\u7ec8\u7a76\u751a\u4e3a\u5e74\u8f7b\uff0c\u663e\u89c1\u662f\u521d\u5165\u6c5f\u6e56\u7684\u4eba\u7269\uff0c\u6b66\u529f\u867d\u9ad8\uff0c\u58f0\u540d\u5374\u4e0d\u54cd\uff0c \u81ea\u662f\u6781\u4e3a\u53ef\u80fd\u3002 \u3000\u3000\u800c\u6b64\u523b\u8fd9\u9ad8\u51a0\u957f\u9aef\u8001\u8005\uff0c\u4e0d\u4f46\u51fa\u73b0\u4e4b\u65f6\uff0c\u6709\u5982\u5e7d\u7075\u4e00\u822c\u5730\u7a81\u7136\u800c\u6765\uff0c\u5df2\u4f7f\u5353\u957f\u537f\u5fc3\u4e2d\u6697 \u9a87\uff0c\u540e\u6765\u4e0e\u5353\u957f\u537f\u4e95\u80a9\u800c\u884c\u4e4b\u65f6\uff0c\u80a9\u4e0d\u52a8\uff0c\u817f\u4e0d\u66f2\uff0c\u811a\u4e0b\u70b9\u5c18\u4e0d\u626c\uff0c\u5149\u5929\u5316\u65e5\u4e4b\u4e0b\uff0c\u8d70\u7684\u867d \u4e0d\u751a\u5feb\uff0c\u4f46\u5353\u957f\u537f\u5374\u4e00\u671b\u800c\u77e5\u6b64\u4eba\u8f7b\u529f\u6df1\u4e0d\u53ef\u6d4b\u3002 \u3000\u3000\u5982\u6b64\u4eba\u7269\u7684\u59d3\u540d\uff0c\u5374\u662f\u6b66\u6797\u4e2d\u4e00\u4e2a\u6781\u4e3a\u751f\u758f\u7684\u540d\u5b57\uff0c\u5353\u957f\u537f\u81ea\u7136\u89c9\u5f97\u5947\u602a\uff0c\u5fc3\u5ff5\u8f6c\u52a8\u4e4b \u4e2d\uff0c\u5374\u5df2\u89c1\u8fd9\u9ad8\u51a0\u7fbd\u58eb\u5df2\u81ea\u542b\u7b11\u63d6\u5ba2\u4eba\u5750\uff0c\u9042\u4e5f\u4e00\u5c4f\u5fc3\u795e\uff0c\u5750\u4e86\u4e0b\u6765\uff0c\u4e00\u9762\u5fc3\u4e2d\u6697\u5fd6\u9053\uff1a \u201c\u65e0\u8bba\u6b64\u4eba\u59d3\u540d\u662f\u771f\u662f\u5047\uff0c\u4eba\u5bb6\u5bf9\u6211\uff0c\u603b\u662f\u4e00\u756a\u597d\u610f\uff0c\u4e5f\u8bb8\u4ed6\u4ea6\u6709\u4e0d\u613f\u4e3a\u5916\u4eba\u5f97\u77e5\u7684\u9690\u79d8\uff0c \u662f\u4ee5\u4e0d\u613f\u5c06\u771f\u5b9e\u59d3\u540d\u8bf4\u51fa\u6765\uff0c\u6211\u53c8\u4f55\u82e6\u53bb\u8d39\u5fc3\u731c\u6d4b\u4eba\u5bb6\u7684\u9690\u79c1\u5462\uff1f\u201d \u3000\u3000\u4e00\u5ff5\u81f3\u6b64\uff0c\u5fc3\u4e0b\u987f\u89c9\u5766\u7136\u3002"}),showOnHomepage:!0},{name:"\u7b2c\u5341\u7ae0\u3000\u6069\u6028\u7f20\u7ed3",content:(0,t.jsx)(t.Fragment,{children:"\u6b64\u523b\u5df2\u662f\u672a\u672a\u7533\u521d\u4e4b\u4ea4\uff0c\u8fd9\u95f4\u751f\u610f\u672c\u662f\u4e0d\u4f73\u7684\u9152\u94fa\uff0c\u5728\u8fd9\u79cd\u5348\u996d\u5df2\u8fc7\u3001\u665a\u996d\u672a\u81f3\u7684\u65f6 \u5019\uff0c\u4e0a\u5ea7\u81ea\u7136\u66f4\u574f\u3002 \u3000\u3000\u8fd9\u95f4\u91cc\u9762\u53ea\u6446\u4e86\u4e03\u516b\u5f20\u767d\u6768\u6728\u684c\u7684\u5c0f\u5c0f\u9152\u94fa\uff0c\u6b64\u523b\u5ea7\u5ba2\u9664\u4e86\u5353\u957f\u537f\u548c\u90a3\u9ad8\u51a0\u7fbd\u58eb\u4e4b\u5916\uff0c \u4fbf\u518d\u65e0\u522b\u4eba\uff0c\u9152\u83dc\u66f4\u81ea\u7136\u4e5f\u505a\u5f97\u7cbe\u81f4\u4e9b\u3002 \u3000\u3000\u5bf9\u914c\u4e09\u676f\uff0c\u83dc\u7565\u52a8\u7740\uff0c\u9ad8\u51a0\u7fbd\u58eb\u4e3e\u8d77\u624b\u4e2d\u6728\u7b77\uff0c\u542b\u7b11\u8bf4\uff1a\u201c\u6b64\u95f4\u9152\u65e2\u4e0d\u7cbe\uff0c\u83dc\u4ea6\u4e0d\u7f8e\uff0c \u8001\u592b\u8fd9\u4e2a\u4e1c\u9053\uff0c\u505a\u7684\u5c82\u975e\u592a\u5acc\u4e0d\u656c\uff1f\u201d \u3000\u3000\u5353\u957f\u537f\u5fae\u5fae\u4e00\u7b11\uff0c\u65b9\u5f85\u8c26\u8c22\u4e24\u53e5\uff0c\u5374\u542c\u8fd9\u9ad8\u51a0\u7fbd\u58eb\u53c8\u7b11\u9053\uff1a\u201c\u4e0d\u8fc7\u8001\u592b\u5012\u53ef\u8bf4\u4e2a\u6545\u4e8b\u4e0e \u5144\u53f0\u542c\u542c\uff0c\u6743\u5145\u5144\u53f0\u4e4b\u4e0b\u9152\u4e4b\u7269\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u505c\u676f\u7b11\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u5c0f\u53ef\u4eca\u65e5\u7684\u53e3\u798f\u867d\u7136\u5dee\u4e9b\uff0c\u8033\u798f\u5374\u662f\u4e0d\u9519\u7684\u4e86\u3002\u201d \u3000\u3000\u9ad8\u51a0\u7fbd\u58eb\u6717\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u6545\u4e8b\u867d\u7136\u5e76\u4e0d\u5341\u5206\u7cbe\u5947\uff0c\u4f46\u5144\u53f0\u542c\u4e86\uff0c\u5374\u5b9a\u5fc5\u662f\u6781\u611f\u5174\u8da3 \u7684\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u5fae\u5fae\u4e00\u6123\uff0c\u653e\u4e0b\u624b\u4e2d\u7b77\u5b50\uff0c\u95ee\u9053\uff1a\u201c\u96be\u9053\u8fd9\u6545\u4e8b\u4e0e\u5c0f\u53ef\u6709\u5173\u4e0d\u6210\uff1f\u201d \u3000\u3000\u9ad8\u51a0\u58eb\u76ee\u5149\u4e4b\u4e2d\uff0c\u7a81\u5730\u63a0\u8fc7\u4e00\u4e1d\u4ee4\u4eba\u96be\u6d4b\u7684\u795e\u91c7\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u6b64\u4e8b\u4e0d\u4f46\u4e0e\u5144\u53f0\u6709\u5173\uff0c \u800c\u4e14\u5173\u7cfb\u9887\u5927\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u4e0d\u7981\u53c8\u4e3a\u4e4b\u4e00\u6123\uff0c\u6697\u9053\u81ea\u5fd6\uff1a\u201c\u8fd9\u9ad8\u51a0\u7fbd\u58eb\u4e0e\u6211\u672c\u6765\u7d20\u4e0d\u76f8\u8bc6\uff0c\u53c8\u600e\u77e5\u6b64\u4e8b\u4e0e\u6211 \u5927\u6709\u5173\u7cfb\u7684\uff0c\u66f4\u662f\u5c11\u800c\u53c8\u5c11\u2014\u2014\u201d\u4e00\u5ff5\u81f3\u6b64\uff0c\u5fc3\u4e0b\u4e0d\u89c9\u5927\u5947\uff0c\u5bf9\u8fd9\u201c\u9ad8\u51a0\u7fbd\u58eb\u201d\u7684\u8eab\u4efd\u6765 \u5386\uff0c\u5148\u524d\u867d\u5df2\u4f46\u7136\uff0c\u6b64\u523b\u5374\u53c8\u4e0d\u7981\u5f00\u59cb\u7591\u60d1\u8d77\u6765\u3002 \u3000\u3000\u9ad8\u51a0\u7fbd\u58eb\u76ee\u5149\u4e00\u8f6c\uff0c\u5634\u89d2\u4f3c\u53c8\u63a0\u8fc7\u4e00\u4e1d\u5f97\u610f\u7684\u7b11\u5bb9\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u4e09\u5341\u5e74\u524d\uff0c\u6b66\u6797\u4e4b\u4e2d \u6709\u7740\u4e00\u5bf9\u540d\u95fb\u5929\u4e0b\u7684\u4fa0\u4fa3\uff0c\u90a3\u65f6\u5144\u53f0\u2026\u2026\u54c8\u54c8\uff0c\u5144\u53f0\u5e74\u7eaa\u8f83\u8f7b\uff0c\u81ea\u7136\u4e0d\u4f1a\u77e5\u9053\u8fd9\u4e24\u4f4d\u7684\u5927 \u540d\uff0c\u53ef\u662f\u4e09\u5341\u5e74\u524d\u6b66\u4fa0\u4e2d\u63d0\u8d77\u6881\u5b5f\u53cc\u4fa0\uff0c\u5374\u7edd\u4e0d\u4f1a\u6ca1\u6709\u4e00\u4eba\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u4ed6\u8bed\u58f0\u5fae\u987f\uff0c\u5e97\u4f19\u6070\u597d\u53c8\u9001\u4e0a\u4e00\u6837\u83dc\u6765\uff0c\u4ed6\u4f38\u51fa\u7b77\u5b50\uff0c\u5939\u4e86\u4e00\u7b77\uff0c\u5480\u56bc\u534a\u664c\uff0c\u505c\u7740\u7b11 \u9053\uff1a\u201c\u8fd9\u9986\u5b50\u522b\u7684\u83dc\u505a\u7684\u867d\u4e0d\u751a\u4f73\uff0c\u8fd9\u9c7c\u6742\u8c46\u8150\u5374\u662f\u6781\u4e3a\u4e0d\u9519\u7684\uff0c\u5144\u53f0\u4e0d\u59a8\u5148\u5c1d\u4e24\u53e3\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u65e0\u53ef\u5948\u5730\u4f38\u51fa\u7b77\u5b50\uff0c\u5939\u4e86\u4e00\u7b77\uff0c\u5fc3\u4e2d\u5374\u662f\u601d\u6f6e\u767e\u8f6c\uff0c\u53c8\u662f\u60ca\u5947\uff0c\u53c8\u662f\u5947\u602a\uff0c\u54ea\u6709 \u5fc3\u60c5\u53bb\u5403\u8fd9\u6e10\u6c5f\u7701\u5185\uff0c\u4e34\u5b89\u57ce\u5916\u4e00\u95f4\u5c0f\u5c0f\u9102\u83dc\u9986\u5b50\u7684\u9c7c\u6742\u8c46\u8150\u3002 \u3000\u3000\u4ed6\u53e3\u4e2d\u4e00\u9762\u5480\u56bc\u7740\u9c7c\u6742\u8c46\u8150\uff0c\u4e00\u9762\u5374\u4e0d\u7981\u5728\u5fc3\u4e2d\u6697\u5730\u601d\u5fd6\uff1a\u201c\u8fd9\u6881\u5b5f\u53cc\u4fa0\u7eb5\u7136\u540d\u9707\u6c5f \u6e56\uff0c\u5374\u53c8\u4e0e\u6211\u6709\u4ec0\u4e48\u5173\u7cfb\u3002\u201d \u3000\u3000\u5374\u89c1\u8fd9\u9ad8\u51a0\u7fbd\u58eb\u597d\u6574\u4ee5\u6687\u5730\u6d45\u6d45\u5730\u5438\u4e86\u53e3\u9152\uff0c\u65b9\u81ea\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u8fd9\u6881\u5b5f\u53cc\u4fa0\u5728\u6b66\u6797\u4e4b \u4e2d\uff0c\u58f0\u540d\u663e\u8d6b\u65e0\u6bd4\uff0c\u6b66\u529f\u5374\u5e76\u4e0d\u751a\u9ad8\u5f3a\uff0c\u4ed6\u4eec\u5728\u6b66\u6797\u5f97\u4eab\u76db\u540d\u7684\u539f\u56e0\uff0c\u53ea\u662f\u56e0\u4e3a\u8fd9\u592b\u5987\u4e24 \u4eba\uff0c\u4ff1\u90fd\u7f8e\u7edd\u5929\u4eba\uff0c\u5973\u7684\u56fa\u7136\u662f\u6c89\u9c7c\u843d\u96c1\uff0c\u95ed\u6708\u7f9e\u82b1\uff0c\u7537\u7684\u66f4\u5982\u7389\u6811\u4e34\u98ce\u82f1\u59ff\u98d2\u723d\uff0c\u6b66\u6797\u4e2d \u4eba\u5148\u8fd8\u6709\u4e9b\u8361\u5987\u6deb\u5f92\uff0c\u60f3\u6253\u8fd9\u4e24\u4eba\u7684\u4e3b\u610f\uff0c\u53ea\u662f\u4ed6\u4eec\u592b\u5987\u4e24\u4eba\uff0c\u4e0d\u4f46\u60c5\u611f\u6781\u6df1\uff0c\u800c\u4e14\u5f7c\u6b64\u4e4b \u95f4\uff0c\u4ff1\u662f\u76f8\u656c\u5982\u5bbe\uff0c\u5341\u6570\u5e74\u6765\uff0c\u4ed6\u592b\u5987\u4e24\u4eba\u904d\u5386\u6c5f\u6e56\uff0c\u6b66\u6797\u4e2d\u5374\u4ece\u672a\u6709\u4eba\u89c1\u8fc7\u90a3\u6881\u540c\u9e3f\u5bf9\u5b5f \u5982\u5149\u5076\u51fa\u75be\u8a00\uff0c\u4e5f\u4ece\u672a\u6709\u4eba\u89c1\u8fc7\u90a3\u5b5f\u5982\u5149\u5bf9\u6881\u540c\u9e3f\u7a0d\u6709\u5389\u8272\u7684\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u5fc3\u4e2d\u6697\u53f9\u4e00\u58f0\uff0c\u5fd6\u9053\uff1a\u201c\u5f97\u59bb\u5982\u6b64\uff0c\u592b\u590d\u4f55\u61be\u3002\u201d \u3000\u3000\u8f6c\u5ff5\u5374\u53c8\u4e0d\u7981\u6697\u5fd6\u9053\uff1a\u201c\u53ea\u662f\u8fd9\u4e24\u4eba\u4e0e\u6211\u53c8\u6709\u4f55\u5e72\u7cfb\uff1f\u201d \u3000\u3000\u60f3\u6765\u60f3\u53bb\uff0c\u8fd8\u662f\u65e0\u6cd5\u731c\u51fa\u8fd9\u9ad8\u51a0\u7fbd\u58eb\u8bf4\u8fd9\u6545\u4e8b\u7684\u771f\u610f\u6765\uff0c\u53ea\u89c1\u4ed6\u8bed\u58f0\u5fae\u987f\uff0c\u7565\u5598\u4e86\u53e3 \u6c14\uff0c\u53c8\u9053\uff1a\u201c\u6b66\u6797\u4e2d\uff0c\u4e00\u4e9b\u6b63\u6d3e\u4fa0\u58eb\uff0c\u89c1\u5230\u83bd\u83bd\u6c5f\u6e56\u4e4b\u4e2d\uff0c\u5c45\u7136\u8fd8\u6709\u8fd9\u6837\u4e00\u5bf9\u592b\u59bb\uff0c\u5bf9\u8fd9\u6881 \u5b5f\u4e8c\u4eba\uff0c\u81ea\u662f\u5927\u751f\u597d\u611f\uff0c\u90a3\u4e9b\u8361\u5987\u6deb\u5f92\u89c1\u5230\u8fd9\u4e24\u4eba\u5728\u6c5f\u6e56\u4e2d\u4eba\u7f18\u5982\u6b64\u4e4b\u597d\uff0c\u4e5f\u5c31\u5c06\u6ee1\u8154\u90aa\u5fc3 \u6b32\u706b\uff0c\u5f3a\u81ea\u5fcd\u4e86\u4e0b\u53bb\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u6697\u76b1\u7709\u5934\uff0c\u5fc3\u4e2d\u8f6c\u5ff5\uff0c\u76f4\u5230\u6b64\u523b\uff0c\u8fd9\u9ad8\u51a0\u7fbd\u58eb\u6240\u8bf4\u7684\u6545\u4e8b\uff0c\u867d\u7136\u52a8\u542c\uff0c\u5374\u4ecd\u7136\u548c \u81ea\u5df1\u6beb\u65e0\u5173\u7cfb\uff0c\u5fc3\u4e0b\u65b9\u81ea\u5947\u602a\u3002 \u3000\u3000\u62ac\u76ee\u671b\u53bb\uff0c\u5374\u89c1\u8fd9\u9ad8\u51a0\u7fbd\u58eb\u7684\u4e00\u53cc\u7535\u76ee\uff0c\u6b63\u81ea\u51dd\u76ee\u671b\u7740\u81ea\u5df1\uff0c\u76ee\u5149\u4e4b\u4e2d\u4f3c\u7b11\u975e\u7b11\uff0c\u63a5\u7740 \u53c8\u9053\uff1a\u201c\u4ed6\u4eec\u592b\u5987\u4e24\u4eba\u5c06\u5927\u6cb3\u4e24\u5cb8\u3001\u957f\u6c5f\u5357\u5317\u6e38\u5386\u4e00\u904d\u4e4b\u540e\uff0c\u8db3\u8ff9\u4fbf\u8fdc\u81f3\u82d7\u7586\uff0c\u8fd9\u5bf9\u592b\u5987\u4e00 \u751f\u4e4b\u4e2d\uff0c\u5e73\u7a33\u5b89\u9759\uff0c\u4ed6\u4eec\u5374\u518d\u4e5f\u60f3\u4e0d\u5230\u5728\u7545\u6e38\u82d7\u7586\u4e4b\u9645\uff0c\u4f1a\u9047\u5230\u4e00\u4e2a\u4ee4\u8fd9\u5bf9\u88ab\u6b66\u6797\u8273\u7fa1\u4e0d\u5df2 \u7684\u4fa0\u4fa3\u592b\u5987\uff0c\u4ece\u6b64\u9b42\u5f52\u79bb\u6068\u7684\u6b66\u6797\u9b54\u5934\u3002\u201d\u201c\u542c\u5230\u8fd9\u91cc\uff0c\u5353\u957f\u537f\u4e0d\u7531\u5168\u8eab\u4e00\u9707\uff0c\u63a8\u676f\u800c\u8d77\uff0c \u8131\u53e3\u95ee\u9053\uff1a\u201c\u96be\u9053\u6b64\u4eba\u4fbf\u662f\u90a3\u4e11\u4eba\u6e29\u5982\u7389\uff01\u201d \u3000\u3000\u9ad8\u51a0\u7fbd\u58eb\u54c8\u54c8\u4e00\u7b11\uff0c\u5c06\u9762\u524d\u7684\u4e00\u676f\u82b1\u96d5\uff0c\u4ef0\u9996\u4e00\u4e8e\u800c\u5c3d\uff0c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6b64\u4eba\u6b63\u662f\u90a3\u88ab\u5929 \u4e0b\u6b66\u6797\u540c\u9053\u79f0\u4e3a\u7ea2\u8863\u59d1\u5a18\uff0c\u5374\u81ea\u79f0\u4e11\u4eba\u7684\u6e29\u5982\u7389\uff1a\u201c\u4e00\u65f6\u4e4b\u95f4\uff0c\u5353\u957f\u537f\u4f46\u89c9\u5fc3\u80f8\u4e4b\u4e2d\uff0c\u6012\u706b \u6cb8\u817e\uff0c\u51e0\u4e4e\u5fd8\u4e86\u8fd9\u9ad8\u51a0\u7fbd\u58eb\u600e\u4f1a\u77e5\u9053\u81ea\u5df1\u548c\u90a3\u4e11\u4eba\u6e29\u5982\u7389\u6709\u7740\u6df1\u4ec7\uff0c\u8131\u53e3\u53c8\u9053\uff1a\u201c\u8fd9\u4e11\u4eba\u6e29 \u5982\u7389\u96be\u9053\u53c8\u5c06\u8fd9\u795e\u4ed9\u4fa0\u4fa3\u53cc\u53cc\u5bb3\u6b7b\u4e86\u5417\uff1f\u201d \u3000\u3000\u9ad8\u51a0\u7fbd\u58eb\u5fae\u5fae\u4e00\u7b11\uff0c\u9894\u9996\u9053\uff1a\u201c\u8fd9\u6e29\u5982\u7389\u81ea\u79f0\u4e11\u4eba\uff0c\u5176\u5b9e\u4e11\u7684\u4e00\u5b57\uff0c\u8fd8\u8fdc\u4e0d\u8db3\u4ee5\u5f62\u5bb9\u5176 \u4eba\uff0c\u54ea\u77e5\u5979\u5374\u504f\u504f\u770b\u4e86\u4e0a\u90a3\u7f8e\u5982\u5b50\u5efa\u7684\u6881\u540c\u9e3f\uff0c\u8bd5\u60f3\u6881\u540c\u9e3f\u6709\u59bb\u5982\u82b1\uff0c\u800c\u4e14\u6e29\u67d4\u8d24\u6167\uff0c\u5374\u53c8 \u600e\u4f1a\u5bf9\u8fd9\u8c8c\u8d5b\u65e0\u76d0\u7684\u4e11\u4eba\u6e29\u5982\u7389\u7a0d\u5047\u8bcd\u8272\u5462\uff1f\u201d \u3000\u3000\u4ed6\u957f\u53f9\u4e00\u58f0\uff0c\u76ee\u5149\u4ef0\u89c6\uff0c\u63a5\u7740\u53c8\u9053\uff1a\u201c\u4e8e\u662f\u8fd9\u6e29\u5982\u7389\u56e0\u7231\u751f\u5992\uff0c\u56e0\u5992\u751f\u4ec7\uff0c\u7adf\u5c06\u4e00\u751f\u4e4b \u4e2d\uff0c\u8c26\u8c26\u81ea\u5b88\uff0c\u5728\u6b66\u6797\u91cc\u4ece\u672a\u4e0e\u4eba\u7ed3\u8fc7\u6881\u5b50\u7684\u6881\u540c\u9e3f\uff0c\u4e00\u638c\u51fb\u6bd9\u5728\u4ed6\u7684\u7231\u59bb\u9762\u524d\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u8033\u7554\u8f70\u7136\u4e00\u58f0\uff0c\u5168\u8eab\u4ea6\u4e0d\u7981\u4e3a\u4e4b\u4e00\u9707\uff0c\u5fc3\u80f8\u4e4b\u95f4\uff0c\u50cf\u662f\u88ab\u4eba\u91cd\u91cd\u5730\u51fb\u4e86\u4e00\u62f3\uff0c\u53cc \u76ee\u76f4\u89c6\uff0c\u832b\u7136\u5fd6\u9053\uff1a\u201c\u7239\u7239\u4ed6\u8001\u4eba\u5bb6\u4e00\u751f\u4e4b\u4e2d\uff0c\u4e0d\u4f46\u662f\u4e2a\u8c26\u8c26\u81ea\u5b88\u7684\u541b\u5b50\uff0c\u800c\u4e14\u662f\u4e2a\u6025\u4eba\u4e4b \u96be\u7684\u4fa0\u58eb\uff0c\u4f46\u662f\u2026\u2026\u4ed6\u8001\u4eba\u5bb6\u53c8\u4f55\u5c1d\u4e0d\u662f\u88ab\u8fd9\u4e07\u6076\u7684\u9b54\u5934\u4e00\u638c\u51fb\u6bd9\u5728\u81ea\u5df1\u7684\u7231\u59bb\u9762\u524d\u3002\u201d \u3000\u3000\u4e00\u5ff5\u81f3\u6b64\uff0c\u4e24\u884c\u6cea\u73e0\uff0c\u4fbf\u4e0d\u80fd\u81ea\u6b62\u5730\u6cbf\u7740\u9762\u988a\u7f13\u7f13\u843d\u4e86\u4e0b\u6765\uff0c\u843d\u5728\u4ed6\u8eab\u4e0a\u7a7f\u7740\u7684\u7384\u8272\u957f \u886b\u4e0a\uff0c\u5374\u53c8\u6beb\u4e0d\u505c\u7559\u5730\u4ece\u8863\u4e0a\u6ed1\u843d\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u90a3\u9ad8\u51a0\u7fbd\u58eb\u51dd\u6ce8\u5728\u5353\u957f\u537f\u9762\u4e0a\u7684\u76ee\u5149\uff0c\u4ea6\u968f\u7740\u4ed6\u7684\u6cea\u73e0\u7f13\u7f13\u79fb\u4e0b\uff0c\u4e00\u4e1d\u4ee4\u4eba\u96be\u6d4b\u7684\u5149 \u91c7\uff0c\u4fbf\u53c8\u5728\u4ed6\u7684\u65e5\u4e2d\u95ea\u8fc7\u3002 \u3000\u3000\u4f46\u7b49\u5230\u4ed6\u7684\u76ee\u5149\u8f6c\u5230\u90a3\u4e24\u6ef4\u7531\u5353\u957f\u537f\u7684\u7384\u8272\u8863\u886b\u4e0a\u6ed1\u843d\u7684\u6cea\u73e0\u65f6\uff0c\u4ed6\u53cc\u76ee\u4e2d\u6240\u663e\u793a\u7684\u795e \u91c7\uff0c\u5374\u5168\u7136\u53d8\u4e3a\u60ca\u6115\u4e86\u3002 \u3000\u3000\u8fd9\u51e0\u4e4e\u662f\u4e00\u4ef6\u65e0\u6cd5\u601d\u8bae\u7684\u4e8b\uff0c\u56e0\u4e3a\u90a3\u6cea\u73e0\u51e0\u4e4e\u662f\u6beb\u4e0d\u7559\u6ede\u5730\u81ea\u8863\u886b\u4e0a\u6ed1\u4e0b\uff0c\u90a3\u4e48\uff0c\u8fd9\u8be5 \u53c8\u662f\u4ec0\u4e48\u8d28\u6599\u5236\u6210\u7684\u8863\u6599\u5462\uff1f \u3000\u3000\u4e8e\u662f\u4ed6\u7684\u76ee\u5149\u4e0d\u7531\u81ea\u4e3b\u5730\u5728\u8fd9\u4ef6\u7384\u8272\u7684\u8863\u886b\u4e0a\u505c\u7559\u4e86\u534a\u664c\uff0c\u53cc\u7709\u5fae\u5fae\u4e00\u76b1\uff0c\u4f3c\u4e4e\u60f3\u8d77\u4e86 \u4ec0\u4e48\uff0c\u4f46\u77ac\u5373\u63a5\u7740\u53f9\u9053\uff1a\u201c\u6881\u540c\u9e3f\u4e00\u6b7b\uff0c\u5b5f\u5982\u5149\u81ea\u7136\u75db\u4e0d\u6b32\u751f\uff0c\u53ea\u662f\u8fd9\u53ef\u601c\u7684\u5973\u5b50\u90a3\u65f6\u5df2\u6709 \u4e86\u4e94\u4e2a\u6708\u7684\u8eab\u5b55\uff0c\u4e3a\u4e86\u8fd9\u70b9\u6881\u6c0f\u9aa8\u8089\uff0c\u5b5f\u5982\u5149\u7eb5\u7136\u60f3\u6b7b\uff0c\u4f46\u5728\u8fd9\u79cd\u60c5\u51b5\u4e0b\uff0c\u5374\u4e5f\u5bb9\u4e0d\u5f97\u5979\u5c31 \u6b64\u4e00\u6b7b\u4e86\u3002\u201d \u3000\u3000\u4ed6\u6c89\u91cd\u5730\u53f9\u606f\u4e00\u58f0\uff0c\u4f46\u4f60\u5982\u679c\u806a\u660e\uff0c\u4f60\u53ef\u4ee5\u53d1\u73b0\u4ed6\u8fd9\u58f0\u6c89\u91cd\u7684\u53f9\u606f\u58f0\u4e2d\uff0c\u51e0\u4e4e\u5168\u7136\u6ca1\u6709 \u60cb\u60dc\u548c\u54c0\u4f24\u7684\u610f\u5473\u3002 \u3000\u3000\u4f46\u5353\u957f\u537f\u6b64\u523b\u6b63\u662f\u60b2\u6124\u586b\u81ba\uff0c\u6cea\u5982\u6cc9\u6d8c\uff0c\u53c8\u600e\u80fd\u53d1\u89c9\u4ed6\u53f9\u606f\u58f0\u4e2d\u7684\u771f\u610f\u5462\uff1f \u3000\u3000\u9ad8\u51a0\u7fbd\u58eb\u5fae\u4e00\u634b\u987b\uff0c\u4fbf\u53c8\u53f9\u9053\uff1a\u201c\u751f\u6b7b\u4e4b\u4e8b\uff0c\u867d\u662f\u5343\u53e4\u4e4b\u4eba\u96be\u4ee5\u52d8\u7834\u4e4b\u4e8b\uff0c\u4f46\u6b32\u6b7b\u4e0d \u80fd\uff0c\u5374\u8fdc\u6bd4\u6c42\u751f\u4e0d\u5f97\u8fd8\u8981\u75db\u82e6\u5f97\u591a\u2014\u2014\u201d\u4ed6\u7ade\u53c8\u81ea\u5fae\u5fae\u4e00\u53f9\uff0c\u63a5\u9053\uff1a\u201c\u5144\u53f0\u5e74\u7eaa\u4e0d\u8f7b\uff0c\u867d\u662f \u7edd\u4e16\u5947\u624d\uff0c\u4f46\u5bf9\u4eba\u4e16\u4e4b\u95f4\u7684\u4e00\u4e9b\u51c4\u60e8\u4e4b\u4e8b\uff0c\u7ec8\u7a76\u4e0d\u5982\u6211\u8fd9\u5386\u5c3d\u6ca7\u6851\u7684\u4f24\u5fc3\u4eba\u4f53\u4f1a\u5f97\u591a\uff0c\u8bd5\u60f3 \u90a3\u6881\u540c\u9e3f\u4e0e\u5b5f\u5982\u5149\u672c\u662f\u6c5f\u6e56\u4e2d\u4eba\u4eba\u8273\u7fa1\u7684\u795e\u4ed9\u7737\u5c5e\uff0c\u4f46\u5982\u4eca\u9e33\u9e2f\u5931\u5076\uff0c\u672c\u5df2\u75db\u4e0d\u6b32\u751f\uff0c\u5982\u80fd \u540c\u7a74\u800c\u6b7b\uff0c\u5219\u60c5\u5929\u867d\u5df2\u5e38\u6068\uff0c\u6bd4\u7ffc\u4e4b\u9e1f\u53ef\u671f\uff0c\u4e5f\u8fd8\u80fd\u542b\u7b11\u4e8e\u4e5d\u6cc9\u4e4b\u4e0b\uff0c\u4f46\u5982\u4eca\u6b32\u6b7b\u5374\u4ea6\u4e0d \u80fd\uff0c\u5509\u2014\u2014\u4eba\u4e16\u95f4\u6700\u51c4\u60e8\u4e4b\u4e8b\uff0c\u6015\u4e5f\u83ab\u8fc7\u4e8e\u6b64\u4e86\u3002\u201d \u3000\u3000\u4ed6\u53cc\u76ee\u5fae\u5408\uff0c\u9762\u76ee\u4e4b\u4e0a\uff0c\u9732\u51fa\u4e86\u9887\u4e3a\u54c0\u75db\u7684\u8868\u60c5\u6765\uff0c\u7a0d\u5fae\u4e00\u987f\uff0c\u53c8\u9053\uff1a\u201c\u90a3\u5929\u4f3c\u4e4e\u662f\u51ac \u5929\uff0c\u82d7\u5c71\u4e4b\u5185\uff0c\u5929\u65f6\u867d\u8f83\u6696\uff0c\u4f46\u4ecd\u662f\u51db\u98ce\u6012\u543c\uff0c\u53f6\u843d\u6ee1\u5c71\uff0c\u53ea\u5dee\u6ca1\u6709\u4e0b\u96ea\u800c\u5df2\uff0c\u5b5f\u5982\u5149\u4f0f\u5728 \u6881\u540c\u9e3f\u7684\u5c38\u8eab\u4e0a\uff0c\u54c0\u54c0\u5730\u75db\u54ed\u7740\uff0c\u54ed\u58f0\u4e0e\u98ce\u58f0\u76f8\u548c\uff0c\u4fbf\u6df7\u5408\u6210\u4e00\u79cd\u4ee4\u4eba\u4e0d\u5fcd\u5352\u542c\u7684\u58f0\u97f3\u3002\u201d \u3000\u3000\u201c\u4f46\u662f\u90a3\u4e11\u4eba\u6e29\u5982\u7389\uff0c\u7adf\u5c06\u8fd9\u5bf9\u5df2\u6210\u6b7b\u522b\u7684\u9e33\u9e2f\uff0c\u8fd8\u8981\u751f\u751f\u62c6\u5f00\uff0c\u5c06\u90a3\u6881\u540c\u9e3f\u7684\u5c38\u8eab\uff0c \u846c\u5728\u8d21\u9ece\u5c71\u53f3\u7684\u7a74\u5730\u4e4b\u4e2d\uff0c\u5374\u5c06\u5b5f\u5982\u5149\u8f6f\u56da\u5728\u8d21\u9ece\u5c71\u5de6\u7684\u4e00\u4e2a\u6240\u5728\uff0c\u4e5f\u4e0d\u5c06\u5979\u7f6e\u4e4b\u6b7b\u5730\uff0c\u56e0 \u4e3a\u8fd9\u5fc3\u5982\u86c7\u874e\u7684\u9b54\u5934\u77e5\u9053\uff0c\u4e0e\u5176\u5c06\u5979\u6740\u6b7b\uff0c\u8fd8\u4e0d\u5982\u8fd9\u6837\u66f4\u8981\u4ee4\u5979\u75db\u82e6\u5f97\u591a\u3002\u201d \u3000\u3000\u4ed6\u4e00\u62cd\u684c\u5b50\uff0c\u53c8\u9053\uff1a\u201c\u4e0d\u4f46\u5982\u6b64\uff0c\u8fd9\u4e11\u4eba\u6e29\u5982\u7389\u66f4\u60f3\u5c3d\u4e86\u5343\u65b9\u767e\u8ba1\uff0c\u53bb\u6298\u78e8\u8fd9\u4e2a\u53ef\u601c\u7684 \u5973\u4e88\uff0c\u4f46\u662f\u5b5f\u5982\u5149\u5374\u90fd\u5fcd\u53d7\u4e86\u4e0b\u6765\u3002\u201d \u3000\u3000\u8fd9\u9ad8\u51a0\u7fbd\u58eb\u8bf4\u8bdd\u4e4b\u65f6\uff0c\u4e0d\u4f46\u8bed\u58f0\u6e05\u6717\uff0c\u800c\u4e14\u52a0\u4ee5\u624b\u52bf\u8868\u60c5\uff0c\u5c06\u8fd9\u4e2a\u672c\u5df2\u662f\u60e8\u7edd\u4eba\u8870\u7684\u6b66 \u6797\u6545\u4e8b\uff0c\u63cf\u8ff0\u5f97\u66f4\u662f\u51c4\u60e8\u7edd\u4f26\u3002 \u3000\u3000\u5353\u957f\u537f\u672c\u662f\u4f24\u5fc3\u4eba\uff0c\u542c\u5230\u8fd9\u79cd\u4f24\u5fc3\u4e8b\uff0c\u81ea\u7136\u66f4\u662f\u5982\u9189\u5982\u75f4\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u4f46\u89c9\u9189\u4ece\u4e2d\u6765\uff0c \u4e0d\u80fd\u81ea\u5df1\uff0c\u7adf\u5fd8\u4e86\u518d\u60f3\u8fd9\u6545\u4e8b\u7a76\u7adf\u4e0e\u81ea\u5df1\u6709\u4f55\u5173\u7cfb\u3002 \u3000\u3000\u9ad8\u51a0\u7fbd\u58eb\u76ee\u5149\u4e00\u8f6c\uff0c\u63a5\u7740\u53c8\u9053\uff1a\u201c\u76f4\u5230\u90a3\u7cb1\u540c\u9e3f\u7684\u4eb2\u751f\u9aa8\u8840\u751f\u4e0b\u6765\u7684\u90a3\u4e00\u5929\uff0c\u5b5f\u5982\u5149\u4fbf \u5c06\u90a3\u5973\u5b69\u5b50\u4ea4\u7ed9\u4e00\u4e2a\u5728\u8fd9\u6570\u6708\u5185\uff0c\u5728\u82d7\u7586\u4e2d\u7ed3\u8bc6\u7684\u4e00\u4e2a\u77e5\u5df1\uff0c\u518d\u4e09\u5631\u5490\u53ee\u549b\u4e4b\u540e\uff0c\u4fbf\u631f\u7740\u6ee1 \u8154\u60b2\u6124\uff0c\u53bb\u5bfb\u90a3\u4e11\u4eba\u6e29\u5982\u7389\uff0c\u53bb\u62a5\u90a3\u4e0d\u5171\u6234\u5929\u7684\u6740\u592b\u6df1\u4ec7\u3002\u201d \u3000\u3000\u201c\u53ea\u662f\u5979\u7684\u6b66\u529f\uff0c\u5374\u53c8\u600e\u6bd4\u5f97\u4e0a\u90a3\u751f\u6027\u5f02\u7980\uff0c\u6b66\u529f\u7edd\u4e16\u7684\u6e29\u5982\u7389\u5462\uff1f\u4e0d\u51fa\u4e09\u62db\uff0c\u8fd9\u6068\u6ee1 \u5fc3\u5934\u7684\u53ef\u601c\u5973\u5b50\uff0c\u4e5f\u5c31\u9b42\u5f52\u79bb\u6068\u5929\u4e86\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u5251\u7709\u6012\u8f69\uff0c\u518d\u4e5f\u5fcd\u4e0d\u4f4f\u5fc3\u4e2d\u6012\u706b\uff0c\u201c\u556a\u201d\u7684\u4e00\u58f0\uff0c\u91cd\u91cd\u4e00\u62cd\u684c\u5b50\uff0c\u5c06\u684c\u4e0a\u7684\u676f\u76cf \u7897\u7b77\uff0c\u90e8\u9707\u7684\u76f4\u98de\u4e86\u8d77\u6765\u3002 \u3000\u3000\u9ad8\u51a0\u7fbd\u58eb\u5fae\u5531\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4eba\u4e16\u4e4b\u4e2d\uff0c\u60b2\u60e8\u4e4b\u4e8b\u539f\u672c\u8fdc\u8f83\u6b22\u4e50\u4e4b\u4e8b\u4e3a\u591a\uff0c\u5144\u53f0\u4e5f\u4e0d\u5fc5\u4e3a \u6b64\u4e8b\u592a\u8fc7\u60b2\u6124\uff0c\u5509\u2014\u2014\u4e0d\u5982\u610f\u4e8b\u5341\u5e38\u516b\u4e5d\uff0c\u4eba\u751f\u5904\u4e16\u3001\u5f97\u8fc7\u4e14\u8fc7\uff0c\u82e5\u662f\u5341\u5206\u8ba4\u771f\u8d77\u6765\uff0c\u90a3\u53ea \u6015\u8c01\u4e5f\u4e0d\u613f\u5728\u4e16\u4e0a\u591a\u6d3b\u4e00\u65e5\u4e86\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u53cc\u7709\u5fae\u8e59\uff0c\u6717\u58f0\u9053\uff1a\u201c\u82e5\u662f\u4eba\u4eba\u4ff1\u505a\u5982\u6b64\u60f3\u6cd5\uff0c\u90a3\u4eba\u4e16\u95f4\uff0c\u9b45\u9b51\u5c82\u975e\u66f4\u52a0\u6a2a\u884c\uff0c \u7fa4\u9b54\u4e71\u821e\uff0c\u771f\u6b63\u5b89\u4efd\u5b88\u5df2\u4e4b\u4eba\uff0c\u8fd8\u6709\u5904\u8eab\u4e4b\u5730\u5417\uff1f\u201d \u3000\u3000\u9ad8\u51a0\u7fbd\u58eb\u6717\u58f0\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5144\u53f0\u65e2\u6709\u5982\u6b64\u4ec1\u4fa0\u4e4b\u5fc3\uff0c\u8001\u592b\u81ea\u7136\u94a6\u4f69\u5f97\u5f88\u3002\u201d \u3000\u3000\u4ed6\u7b11\u5bb9\u4e00\u655b\uff0c\u4fbf\u53c8\u53f9\u9053\uff1a\u201c\u53ea\u662f\u8001\u592b\u867d\u662f\u5982\u6b64\u8bf4\uff0c\u5bf9\u90a3\u6e29\u5982\u7389\u7684\u6124\u6012\u4e4b\u5fc3\uff0c\u5374\u4e5f\u672a\u89c1\u5c31 \u5728\u5144\u53f0\u4e4b\u4e0b\u54e9\u3002\u201d \u3000\u3000\u201c\u90a3\u6e29\u5982\u7389\u5c06\u5b5f\u5982\u5149\u51fb\u6b7b\u4e4b\u540e\uff0c\u7adf\u5c06\u5b5f\u5982\u5149\u7684\u5c38\u9aa8\uff0c\u706b\u5316\u6210\u7070\uff0c\u6492\u5728\u8d21\u9ece\u5c71\u53f3\uff0c\u8ba9\u5979\u968f \u98ce\u800c\u53bb\uff0c\u6c38\u751f\u6c38\u4e16\u4e5f\u4e0d\u80fd\u548c\u6881\u540c\u9e3f\u805a\u5728\u4e00\u5904\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u5fc3\u5ff5\u4e00\u8f6c\uff0c\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u96be\u9053\u5973\u9b54\u5934\u65a9\u8349\u4e0d\u9664\u6839\uff0c\u7adf\u5c06\u90a3\u6881\u540c\u9e3f\u7684\u4eb2\u751f\u9aa8\u8840\uff0c \u8f7b\u8f7b\u653e\u8fc7\uff1f\u201d \u3000\u3000\u9ad8\u51a0\u7fbd\u58eb\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5144\u53f0\u8fd9\u4e00\u95ee\uff0c\u5374\u4e5f\u672a\u514d\u5c06\u90a3\u6e29\u5982\u7389\u770b\u5f97\u592a\u8fc7\u7b80\u5355\u4e86\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u4fef\u9996\u6c89\u541f\u534a\u664c\uff0c\u5fc3\u4e2d\u7a81\u5730\u4e00\u52a8\uff0c\u9053\uff1a\u201c\u96be\u9053\u90a3\u5b5f\u5982\u5149\u81ea\u8ba4\u662f\u81ea\u5df1\u77e5\u5df1\u7684\u4eba\uff0c\u5374\u662f \u6e29\u5982\u7389\u65e9\u5df2\u9884\u5148\u5b89\u6392\u7684\u5417\uff1f\u201d \u3000\u3000\u9ad8\u51a0\u7fbd\u58eb\u731b\u5730\u4e00\u51fb\u624b\u638c\uff0c\u9894\u9996\u7b11\u9053\uff1a\u201c\u8001\u592b\u65e9\u8bf4\u5144\u53f0\u806a\u660e\u7edd\u9876\uff0c\u5fc3\u667a\u4e4b\u673a\u5de7\uff0c\u786e\u662f\u8d85\u4e8e \u5e38\u4eba\uff0c\u90a3\u4e11\u4eba\u6e29\u5982\u7389\u679c\u7136\u65e9\u5df2\u5c06\u81ea\u5df1\u7684\u5fc3\u8179\uff0c\u5b89\u6392\u5728\u5b5f\u5982\u5149\u5de6\u53f3\uff0c\u6545\u610f\u5bf9\u8fd9\u53ef\u601c\u5973\u5b50\u4f5c\u51fa\u540c \u60c5\u4e4b\u6001\uff0c\u90a3\u5b5f\u5982\u5149\u5728\u90a3\u79cd\u6fd2\u4e34\u7edd\u5883\u7684\u60c5\u51b5\u4e4b\u4e0b\uff0c\u6709\u4eba\u5bf9\u5979\u6709\u4e09\u5206\u597d\u5904\uff0c\u5979\u4fbf\u5f53\u4f5c\u5341\u5206\uff0c\u4f55\u51b5 \u8fd9\u4eba\u5bf9\u5979\u672c\u662f\u84c4\u610f\u7ed3\u7eb3\uff0c\u5979\u81ea\u7136\u4e5f\u5c31\u96be\u514d\u5c06\u8fd9\u4e9b\u4eba\u5f53\u4f5c\u81ea\u5df1\u7684\u60a3\u96be\u77e5\u5df2\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u957f\u53f9\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u90a3\u5b69\u5b50\u843d\u5230\u90a3\u4e11\u4eba\u6e29\u5982\u738b\u624b\u4e2d\uff0c\u5c82\u975e\u4ea6\u662f\u51f6\u591a\u5409\u5c11\uff1f\u201d \u3000\u3000\u9ad8\u51a0\u7fbd\u58eb\u6447\u9996\u7b11\u9053\uff1a\u201c\u5144\u53f0\u8fd9\u4e00\u731c\uff0c\u5374\u731c\u9519\u4e86\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u5fae\u5fae\u4e00\u6115\uff0c\u6697\u5730\u5bfb\u601d\u9053\uff1a\u201c\u96be\u9053\u8fd9\u5b69\u5b50\u4e5f\u548c\u6211\u4e00\u6837\uff0c\u88ab\u4e00\u6b66\u6797\u5f02\u4eba\uff0c\u6551\u51fa\u751f\u5929 \u5417\uff1f\u201d \u3000\u3000\u5374\u542c\u9ad8\u51a0\u7fbd\u58eb\u53c8\u9053\uff1a\u201c\u90a3\u6e29\u5982\u7389\u975e\u4f46\u672a\u5c06\u8fd9\u5b69\u5b50\u7f6e\u4e4b\u6b7b\u5730\uff0c\u5374\u53cd\u800c\u5bf9\u5979\u7231\u62a4\u6709\u52a0\u2014\u2014\u201d \u5353\u957f\u537f\u4e0d\u7981\u53c8\u81ea\u63a5\u53e3\u95ee\u9053\uff1a\u201c\u96be\u9053\u8fd9\u5b69\u5b50\u957f\u7684\u4e0e\u90a3\u6881\u540c\u9e3f\u5341\u5206\u76f8\u50cf\uff0c\u90a3\u6e29\u5982\u7389\u5c06\u81ea\u5df1\u5bf9\u4eba\u5bb6 \u7684\u5355\u9762\u76f8\u601d\uff0c\u90fd\u79fb\u5230\u8fd9\u5b69\u5b50\u8eab\u4e0a\u3002\u201d \u3000\u3000\u9ad8\u51a0\u7fbd\u58eb\u62ca\u638c\u53f9\u9053\uff1a\u201c\u5144\u53f0\u4e8b\u4e8b\u6d1e\u70db\u5148\u673a\uff0c\u786e\u662f\u9ad8\u4eba\u4e00\u7b79\uff0c\u8001\u5927\u7684\u786e\u94a6\u4f69\u5f97\u5f88\u2014\u2014\u201d\u4ed6 \u8bdd\u58f0\u4e00\u987f\uff0c\u53c8\u9053\uff1a\u201c\u6e29\u5982\u7389\u4e00\u751f\u4e4b\u4e2d\uff0c\u6068\u5c3d\u5929\u4e0b\u4e4b\u4eba\uff0c\u5bf9\u8fd9\u5b69\u5b50\uff0c\u5374\u662f\u7231\u62a4\u500d\u4e8e\u5e38\u4eba\uff0c\u7adf\u5c06 \u81ea\u5df1\u7684\u4e00\u8eab\u6b66\u529f\uff0c\u90fd\u4f20\u7ed9\u4e86\u8fd9\u5b69\u5b50\u2014\u2014\u201d\u5353\u957f\u537f\u5251\u7709\u4e00\u8f69\uff0c\u7a81\u5730\u957f\u8eab\u800c\u8d77\uff0c\u8131\u53e3\u95ee\u9053\uff1a\u201c\u96be \u9053\u8fd9\u5b69\u5b50\u5323\u662f\u5979\u90a3\u5f1f\u5b50\u6e29\u747e\u3002\u201d \u3000\u3000\u9ad8\u51a0\u7fbd\u58eb\u5fae\u4e00\u9887\u9996\uff0c\u76ee\u5149\u7f13\u7f13\u79fb\u6ce8\u5230\u4ed6\u9762\u76ee\u4e4b\u4e0a\uff0c\u53ea\u89c1\u4ed6\u886c\u8272\u4e4b\u4e2d\uff0c\u53c8\u662f\u60dc\u6115\uff0c\u53c8\u662f\u60ca \u5947\uff0c\u5374\u53c8\u6709\u79cd\u65e0\u6cd5\u63cf\u6d4b\u7684\u559c\u60a6\u4e4b\u610f\uff0c\u7adf\u5728\u8fd9\u5239\u90a3\u4e4b\u95f4\u5316\u89e3\u5f00\u4e86\u3002 \u3000\u3000\u9ad8\u51a0\u660e\u58eb\u4fbf\u4e00\u7a81\u8bf4\u9053\uff1a\u201c\u4eba\u9053\u4e3e\u5176\u4e00\u800c\u53cd\u4e4b\u4e09\uff0c\u4fbf\u662f\u4e16\u4e0a\u7edd\u9876\u806a\u660e\u4e4b\u4eba\uff0c\u4e0d\u60f3\u5144\u53f0\u4e4b\u806a \u660e\u624d\u667a\uff0c\u5c24\u5728\u6b64\u8f88\u4e4b\u4e0a\uff0c\u8001\u592b\u5b9e\u662f\u53e3\u670d\u5fc3\u670d\u7684\u4e86\u3002\u201d \u3000\u3000\u4ed6\u5fae\u4e00\u62ca\u638c\uff0c\u4fbf\u53c8\u6b63\u8272\u8bf4\u9053\uff1a\u201c\u6b64\u4e00\u53ef\u601c\u4e4b\u5b64\u5973\uff0c\u6b63\u662f\u88ab\u90a3\u4e11\u4eba\u6e29\u5982\u7389\u5c06\u5176\u7ec8\u8eab\u4ea4\u6258\u4e8e \u5144\u53f0\u7684\u6e29\u747e\u4e86\u2014\u2014\u201d\u5353\u957f\u537f\u9762\u5bb9\u4e00\u53d8\uff0c\u63a5\u53e3\u9053\uff1a\u201c\u96be\u9053\u8001\u4e08\u5148\u524d\u4fbf\u5728\u6811\u6797\u4e4b\u4e2d\uff0c\u5c06\u5c0f\u53ef\u65b9\u624d \u4e0e\u90a3\u4e11\u4eba\u7684\u8c08\u8bdd\uff0c\u5168\u90fd\u542c\u5230\u4e86\u3002\u201d \u3000\u3000\u9ad8\u51a0\u7fbd\u58eb\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e0d\u7792\u5144\u53f0\u8bf4\uff0c\u8001\u592b\u840d\u8e2a\u5bc4\u8ff9\uff0c\u5230\u5904\u4e3a\u5bb6\uff0c\u65b9\u624d\u8d70\u5f97\u7d2f\u4e86\uff0c\u4fbf \u5728\u90a3\u6811\u6797\u4e4b\u4e2d\uff0c\u5bfb\u4e86\u4e2a\u6728\u53f6\u6d53\u5bc6\u7684\u679d\u4e2b\uff0c\u6b47\u606f\u4e86\u4e0b\u6765\uff0c\u5374\u4e0d\u60f3\u65e0\u610f\u4e4b\u4e2d\uff0c\u7adf\u5c06\u5144\u53f0\u4e0e\u90a3\u4e11\u4eba \u6e29\u5982\u7389\u7684\u7b54\u8bdd\uff0c\u5168\u90fd\u542c\u5230\u8033\u91cc\uff0c\u4f46\u671b\u5144\u53f0\u4e0d\u8981\u602a\u7f6a\u4e8e\u6211\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u9880\u957f\u7684\u8eab\u8eaf\uff0c\u50cf\u662f\u987f\u7136\u5931\u53bb\u4e86\u652f\u6301\u7684\u529b\u91cf\uff0c\u7f13\u7f13\u5730\u53c8\u5750\u4e86\u4e0b\u6765\uff0c\u76ee\u5149\u8d8a\u8fc7\u684c\u5b50\uff0c \u5374\u4ecd\u7136\u505c\u7559\u5728\u90a3\u9ad8\u51a0\u7fbd\u58eb\u7684\u8eab\u4e0a\u3002 \u3000\u3000\u5728\u8fd9\u5239\u90a3\u4e4b\u95f4\uff0c\u4ed6\u5fc3\u4e2d\u6012\u6f6e\u822c\u5730\u7ffb\u6d8c\u8d77\u8bb8\u591a\u60ca\u8be7\u4e0e\u7591\u60d1\u3002 \u3000\u3000\u4ed6\u751a\u81f3\u5f00\u59cb\u6000\u7591\uff0c\u8fd9\u9ad8\u51a0\u7fbd\u58eb\u5c06\u8fd9\u6545\u4e8b\u544a\u8bc9\u81ea\u5df1\u7684\u610f\u4e49\u3002 \u3000\u3000\u6697\u4e2d\u5bfb\u601d\u9053\uff1a\u201c\u6b64\u4e8b\u7ea0\u7f20\u590d\u6742\uff0c\u53ef\u8bf4\u9690\u79d8\u5df2\u6781\uff0c\u8fd9\u9ad8\u51a0\u7fbd\u58eb\u53c8\u600e\u4f1a\u77e5\u9053\u7684\u5462\uff1f\u4ed6\u53e3\u53e3\u58f0 \u58f0\u8bf4\u81ea\u5df1\u662f\u4e2a\u98d8\u6cca\u98ce\u5c18\u7684\u6b66\u6797\u9690\u58eb\uff0c\u4f46\u4ee5\u4ed6\u7684\u8eab\u4efd\uff0c\u672c\u5e94\u4e07\u4e07\u4e0d\u4f1a\u77e5\u9053\u8fd9\u9b54\u5934\u6e29\u5982\u7389\u7684\u9690\u79d8 \u4e4b\u4e8b\u7684\u5440\uff01\u201d \u3000\u3000\u4e8e\u662f\u8fd9\u9ad8\u51a0\u7fbd\u58eb\u7684\u8eab\u4e16\u672a\u5386\uff0c\u4fbf\u518d\u4e00\u6b21\u6210\u4e3a\u4ed6\u5fc3\u4e2d\u56f0\u60d1\u96be\u89e3\u4e4b\u4e8b\u3002 \u3000\u3000\u201c\u4ed6\u5230\u5e95\u662f\u8c01\u5462\uff1f\u5982\u6b64\u4ea4\u7ed9\u4e8e\u6211\uff0c\u53c8\u6709\u4ec0\u4e48\u7528\u610f\uff1f\u201d \u3000\u3000\u5353\u957f\u537f\u6697\u95ee\u81ea\u5df1\uff0c\u53ea\u662f\u4ed6\u4ea6\u81ea\u77e5\u9053\u8fd9\u95ee\u9898\u4e95\u975e\u81ea\u5df1\u80fd\u89e3\u7b54\u7684\u3002 \u3000\u3000\u53ea\u89c1\u90a3\u9ad8\u51a0\u7fbd\u58eb\u4f38\u624b\u4e00\u634b\u9894\u4e0b\u6f06\u9ed1\u7684\u957f\u9aef\uff0c\u7b11\u5bb9\u655b\u5904\uff0c\u795e\u8272\u4e4b\u95f4\uff0c\u7a81\u5730\u53d8\u5f97\u5341\u5206\u5e84\u7a46\uff0c \u76ee\u5149\u4e4b\u4e2d\uff0c\u66f4\u662f\u6b63\u6c14\u6ea2\u7136\u3002 \u3000\u3000\u5353\u957f\u537f\u867d\u5bf9\u6b64\u4eba\u5927\u8d77\u7591\u60d1\u4e4b\u5fc3\uff0c\u4f46\u5374\u518d\u4e5f\u65e0\u6cd5\u4ece\u6b64\u4eba\u8eab\u4e0a\uff0c\u770b\u51fa\u4e00\u4e9b\u597d\u72e1\u4e4b\u6001\u6765\uff0c\u4fef\u9996 \u6c89\u541f\u534a\u664c\uff0c\u65b9\u81ea\u7b54\u9053\uff1a\u201c\u8001\u4e08\u5bf9\u6b64\u7b49\u9690\u79d8\u4e4b\u4e8b\uff0c\u5766\u8bda\u76f8\u544a\u4e8e\u6211\uff0c\u5c0f\u53ef\u611f\u6fc0\u8fd8\u6765\u4e0d\u53ca\uff0c\u7109\u6709\u602a \u7f6a\u8001\u4e08\u4e4b\u7406\u3002\u201d \u3000\u3000\u9ad8\u51a0\u7fbd\u58eb\u5fae\u5531\u4e00\u58f0\uff0c\u6b63\u5bb9\u8bf4\u9053\uff1a\u201c\u6b64\u4e8b\u4e0d\u4f46\u6781\u4e3a\u9690\u79d8\uff0c\u800c\u4e14\u5173\u7cfb\u9887\u5927\uff0c\u6b66\u6797\u4e4b\u4e2d\uff0c\u77e5\u9053 \u6b64\u4e8b\u7684\uff0c\u53ef\u8bf4\u662f\u5c11\u800c\u53c8\u5c11\uff0c\u5c31\u7b97\u90a3\u66fe\u7ecf\u53c2\u4e0e\u6b64\u4e8b\u7684\u6e29\u5982\u7389\u7684\u4eb2\u4fe1\u82d7\u4eba\uff0c\u4e8b\u540e\u4ea6\u90fd\u88ab\u8fd9\u5973\u9b54\u5934 \u6740\u5374\u706d\u53e3\uff0c\u8981\u77e5\u9053\u90a3\u6881\u5b5f\u53cc\u4fa0\u751f\u524d\u4ea4\u6e38\u9887\u4f17\uff0c\u6e29\u5982\u7389\u867d\u7136\u9a84\u6a2a\u8dcb\u6248\uff0c\u51f6\u7130\u751a\u9ad8\uff0c\u5374\u4e5f\u4e0d\u6562\u5c06 \u6b64\u4e8b\u6cc4\u9732\u51fa\u53bb\uff0c\u552f\u6050\u6709\u4eba\u5bfb\u5979\u590d\u4ec7\u3002\u201d \u3000\u3000\u4ed6\u8bdd\u58f0\u5fae\u5fae\u4e00\u987f\uff0c\u53c8\u9053\uff1a\u201c\u6b66\u6797\u4e2d\u4eba\u867d\u7136\u5947\u602a\u8fd9\u6881\u5b5f\u53cc\u4fa0\u600e\u4f1a\u7a81\u5730\u5931\u8e2a\uff0c\u4f46\u65f6\u65e5\u4e00\u4e45\uff0c \u4e5f\u90fd\u9010\u6e10\u6de1\u5fd8\uff0c\u7136\u800c\u90a3\u4e11\u4eba\u6e29\u5982\u7389\u5374\u5c06\u6b64\u4e8b\u9690\u85cf\u5f97\u8d8a\u53d1\u4e25\u5bc6\uff0c\u4e3a\u7684\u662f\u90a3\u5b64\u5973\u6e29\u747e\u5df2\u7ecf\u957f\u5927\u6210 \u4eba\uff0c\u6e29\u5982\u7389\u81ea\u7136\u4e0d\u613f\u8ba9\u5979\u77e5\u9053\u81ea\u5df1\u66fe\u7ecf\u5bb3\u6b7b\u5979\u7684\u7236\u6bcd\uff0c\u5509\u2014\u2014\u6881\u5b5f\u53cc\u4fa0\u4e5d\u6cc9\u4e4b\u4e0b\uff0c\u82e5\u8fd8\u6709 \u77e5\uff0c\u77e5\u9053\u81ea\u5df1\u7684\u72ec\u751f\u7231\u5973\uff0c\u7adf\u5bf9\u6e29\u5982\u7389\u5343\u4f9d\u767e\u987a\uff0c\u5949\u4e4b\u5982\u6bcd\uff0c\u771f\u662f\u6b7b\u96be\u7791\u76ee\u4e86\u2014\u2014\u201d\u4ed6\u53c8\u81ea \u957f\u53f9\u4e00\u58f0\uff0c\u50cf\u662f\u5341\u5206\u60b2\u54c0\u7684\u6837\u5b50\uff0c\u5353\u957f\u537f\u5251\u7709\u4e00\u8f69\uff0c\u7a81\u5730\u95ee\u9053\uff1a\u201c\u6b64\u4e8b\u65e2\u662f\u6041\u5730\u9690\u79d8\uff0c\u5374\u4e0d \u77e5\u8001\u4e08\u53c8\u662f\u600e\u4e48\u77e5\u9053\u7684\uff1f\u201d \u3000\u3000\u9ad8\u51a0\u7fbd\u58eb\u5fae\u5fae\u4e00\u7b11\uff0c\u795e\u8272\u4e4b\u95f4\uff0c\u4e1d\u6beb\u672a\u663e\u60ca\u614c\u4e4b\u6001\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u8001\u592b\u58ee\u5e74\u4e4b\u65f6\uff0c\u66fe\u7ecf \u6df1\u5165\u82d7\u7586\u91c7\u836f\uff0c\u5728\u8352\u5c71\u4e4b\u4e2d\uff0c\u9047\u89c1\u4e00\u4e2a\u5782\u6b7b\u7684\u82d7\u4eba\uff0c\u8fd9\u82d7\u4eba\u4fbf\u662f\u66fe\u7ecf\u53c2\u4e0e\u6b64\u4e8b\uff0c\u53c8\u88ab\u6e29\u5982\u7389 \u6740\u4e4b\u706d\u53e3\u7684\uff0c\u4ed6\u4e34\u6b7b\u4e4b\u9645\uff0c\u5c06\u8fd9\u4ef6\u4e8b\u544a\u8bc9\u4e86\u6211\uff0c\u8fd8\u8ba9\u6211\u4e3a\u4ed6\u590d\u4ec7\uff0c\u53ea\u662f\u2014\u2014\u201d\u4ed6\u8bed\u58f0\u5fae\u987f\uff0c \u53f9\u606f\u4e00\u58f0\uff0c\u65b9\u81ea\u63a5\u53e3\u9053\uff1a\u201c\u6211\u81ea\u95ee\u6b66\u529f\u4e0d\u662f\u90a3\u6e29\u5982\u738b\u7684\u654c\u624b\uff0c\u53c8\u4e0d\u6562\u5c06\u6b64\u4e8b\u968f\u4fbf\u544a\u8bc9\u522b\u4eba\uff0c \u662f\u4ee5\u4fbf\u53ea\u6709\u4efb\u51ed\u8fd9\u4ef6\u60e8\u7edd\u4eba\u8870\u4e4b\u4e8b\uff0c\u5728\u6b66\u6797\u4e2d\u9690\u85cf\u5982\u8bb8\u591a\u5e74\u3001\u5509\u2014\u2014\u5176\u5b9e\u8001\u592b\u5374\u662f\u65f6\u65f6\u523b\u523b \u60f3\u5c06\u6b64\u4e8b\u4e86\u5374\u7684\u3002\u201d \u3000\u3000\u4ed6\u76ee\u5149\u4e00\u62ac\uff0c\u7b14\u76f4\u5730\u671b\u5411\u5353\u957f\u537f\uff0c\u6c89\u58f0\u53c8\u9053\uff1a\u201c\u5982\u4eca\u6211\u5c06\u8fd9\u4ef6\u5728\u6b66\u6797\u4e2d\u5df2\u8fd1\u6e6e\u6ca1\u7684\u79d8\u4e8b \u544a\u8bc9\u5144\u53f0\uff0c\u5144\u53f0\u53ef\u77e5\u9053\u662f\u4e3a\u4ec0\u4e48\u5417\uff1f\u201d \u3000\u3000\u5353\u957f\u537f\u9053\uff1a\u201c\u6b63\u60f3\u8bf7\u6559\u3002\u201d \u3000\u3000\u9ad8\u51a0\u7fbd\u58eb\u76ee\u5149\u5fae\u8f6c\uff0c\u6b63\u8272\u53c8\u9053\uff1a\u201c\u5144\u53f0\u5c11\u5e74\u82f1\u4fca\uff0c\u4e0d\u4f46\u806a\u6167\u7edd\u4eba\uff0c\u800c\u4e14\u6b63\u6c14\u51db\u7136\uff0c\u8001\u592b \u81ea\u95ee\u53cc\u773c\u4e0d\u76f2\uff0c\u884c\u8d70\u6c5f\u6e56\uff0c\u4ea6\u6709\u6570\u5341\u5e74\uff0c\u5374\u4ece\u672a\u89c1\u8fc7\u6709\u5982\u5144\u53f0\u8fd9\u6837\u7684\u5c11\u5e74\u4fa0\u58eb\uff0c\u60f3\u90a3\u6e29\u5982\u7389 \u660e\u77e5\u4e0e\u5144\u53f0\u4ec7\u4e0d\u53ef\u89e3\uff0c\u5374\u4ecd\u7136\u5c06\u81ea\u5df1\u552f\u4e00\u7231\u62a4\u4e4b\u4eba\u6258\u4ed8\u7ed9\u5144\u53f0\uff0c\u56e0\u6b64\u53ef\u77e5\uff0c\u8fd9\u5973\u9b54\u5934\u867d\u7136\u662f \u9a84\u6a2a\u51f6\u9177\uff0c\u5bf9\u5144\u53f0\u5374\u4e5f\u662f\u5341\u5206\u5668\u91cd\u7684\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u5fae\u4e00\u6446\u624b\uff0c\u6b63\u5f85\u8c26\u8c22\u513f\u53e5\uff0c\u5374\u542c\u8fd9\u9ad8\u51a0\u7fbd\u58eb\u53c8\u9053\uff1a\u201c\u8001\u592b\u4e0e\u5144\u53f0\u840d\u6c34\u76f8\u9022\uff0c\u4fbf\u5c06 \u8fd9\u7b49\u91cd\u5927\u4e4b\u4e8b\uff0c\u544a\u8bc9\u5144\u53f0\uff0c\u4e3a\u7684\u662f\u60f3\u8bf7\u5144\u53f0\u5c06\u6b64\u4e8b\u4e86\u5374\uff0c\u4e5f\u514d\u5f97\u6881\u76c2\u53cc\u4fa0\u51a4\u6c89\u6d77\u5e95\uff0c\u8001\u592b\u867d \u5df2\u8001\u673d\uff0c\u4f46\u4e3a\u7740\u6b64\u4e8b\uff0c\u53ea\u8981\u5144\u53f0\u7528\u5f97\u7740\u8001\u592b\u4e4b\u5904\uff0c\u8001\u592b\u4e5f\u613f\u62fc\u5c3d\u5168\u529b\uff0c\u4ee5\u4f9b\u97ad\u7b56\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u5251\u7709\u5fae\u8f69\uff0c\u6717\u58f0\u9053\uff1a\u201c\u8fd9\u7b49\u51c4\u60e8\u4e4b\u4e8b\uff0c\u83ab\u8bf4\u4e0e\u5c0f\u53ef\u5c1a\u6709\u5173\u7cfb\uff0c\u53ea\u8981\u5c0f\u53ef\u77e5\u9053\uff0c\u4e5f \u4e07\u5143\u8896\u624b\u4e4b\u7406\uff0c\u53ea\u662f\u2014\u2014\u201d\u4ed6\u957f\u53f9\u4e00\u58f0\uff0c\u7f13\u7f13\u5782\u4e0b\u76ee\u5149\uff0c\u63a5\u53e3\u53c8\u9053\uff1a\u201c\u90a3\u6e29\u5982\u7389\u7684\u6b66\u529f\u7684\u786e \u662f\u60ca\u4eba\u65e0\u6bd4\uff0c\u5c0f\u53ef\u4e5f\u4e0d\u662f\u5979\u7684\u654c\u624b\uff0c\u662f\u4ee5\u2014\u2014\u5509\uff0c\u5c0f\u53ef\u8fde\u81ea\u5bb6\u7684\u6740\u7236\u6df1\u4ec7\u90e8\u65e0\u6cd5\u62a5\u5f97\uff0c\u53c8\u600e \u80fd\u66ff\u8001\u4e08\u6548\u529b\u5462\uff1f\u201d \u3000\u3000\u9ad8\u51a0\u7fbd\u58eb\u634b\u987b\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u4e2a\u8001\u592b\u4e5f\u77e5\u9053\u3002\u5144\u53f0\u6b66\u529f\u867d\u4e0d\u5982\u90a3\u4e11\u4eba\u6e29\u5982\u7389\uff0c\u5374\u4e5f\u672a\u89c1 \u76f8\u5dee\u591a\u8fdc\uff0c\u53ea\u8981\u5144\u53f0\u7a0d\u52a0\u667a\u8ba1\uff0c\u4fbf\u4e0d\u96be\u5c06\u6b64\u9b54\u5934\u9664\u53bb\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u5fae\u4e00\u76b1\u7709\uff0c\u5fc3\u5ff5\u6570\u8f6c\uff0c\u7a81\u4ed6\u8bf4\u9053\uff1a\u201c\u8001\u4e08\u53ef\u662f\u8981\u5c0f\u53ef\u5c06\u6b64\u4e8b\u544a\u8bc9\u6e29\u747e\uff0c\u8ba9\u5979\u4eec\u4e24 \u4eba\u4e4b\u95f4\uff0c\u5148\u8d77\u51b2\u7a81\uff0c\u7136\u540e\u2014\u2014\u201d\u9ad8\u51a0\u7fbd\u58eb\u62ca\u638c\u7b11\u9053\uff1a\u201c\u5144\u53f0\u786e\u662f\u60ca\u4e16\u7edd\u624d\uff0c\u4e07\u4e8b\u4ff1\u80fd\u6d1e\u6089\u5148 \u673a\uff0c\u60f3\u90a3\u6e29\u747e\u82e5\u662f\u77e5\u9053\u5979\u81ea\u5df1\u5949\u4e4b\u4ee5\u6bcd\u7684\u6069\u5e08\uff0c\u5374\u662f\u81ea\u5df1\u4e0d\u5171\u6234\u5929\u7684\u4ec7\u4eba\uff0c\u7109\u6709\u4e0d\u4e3a\u81ea\u5df1\u7236 \u6bcd\u590d\u4ec7\u4e4b\u7406\uff0c\u90a3\u6e29\u5982\u7389\u4e00\u751f\u5b64\u50fb\u51f6\u6b8b\uff0c\u5bf9\u5979\u5374\u662f\u5343\u771f\u4e07\u786e\u7684\u771f\u5fc3\u7231\u62a4\uff0c\u6e29\u747e\u7eb5\u7136\u5bf9\u5979\u52a8\u624b\uff0c \u5979\u5374\u662f\u5fc5\u5b9a\u4e0d\u4f1a\u4f24\u5bb3\u6e29\u747e\uff0c\u751a\u81f3\u8fd8\u4f1a\u5fc3\u7518\u60c5\u613f\u5730\u8ba9\u6e29\u747e\u6740\u6b7b\u4ea6\u672a\u53ef\u77e5\u2014\u2014\u201d\u5353\u957f\u537f\u76ee\u5149\u52a8 \u5904\uff0c\u53ea\u89c1\u8fd9\u9ad8\u51a0\u7fbd\u58eb\u76ee\u5149\u4e4b\u4e2d\uff0c\u5f97\u610f\u5df2\u6781\uff0c\u751f\u50cf\u662f\u4e0e\u90a3\u4e11\u4eba\u6e29\u5982\u7389\u4e5f\u6709\u7740\u4ec0\u4e48\u6df1\u4ec7\u5927\u6068\u4e00 \u6837\uff0c\u5fc3\u4e2d\u4e0d\u7981\u4e00\u52a8\uff0c\u63a5\u53e3\u95ee\u9053\uff1a\u201c\u65e2\u662f\u5982\u6b64\uff0c\u8001\u4e08\u4f55\u4e0d\u76f4\u63a5\u5c06\u6b64\u4e8b\u544a\u8bc9\u6e29\u747e\u3002\u201d \u3000\u3000\u9ad8\u51a0\u7fbd\u58eb\u4f38\u624b\u53d6\u8d77\u9762\u524d\u9152\u676f\uff0c\u5438\u4e86\u4e00\u53e3\uff0c\u795e\u8272\u4e0d\u53d8\u4ed6\u8bf4\u9053\uff1a\u201c\u8001\u592b\u82e5\u76f4\u63a5\u5c06\u6b64\u4e8b\u8bf4\u51fa\uff0c \u90a3\u6e29\u5982\u7389\u82e5\u662f\u77e5\u9053\uff0c\u5c82\u80af\u653e\u8fc7\u6211\uff0c\u5509\u2014\u2014\u8001\u592b\u8001\u77e3\uff0c\u6614\u5e74\u8c6a\u6c14\uff0c\u4eca\u5df2\u6d88\u53bb\uff0c\u4e5f\u53d8\u5f97\u6709\u4e9b\u8d2a\u751f \u754f\u6b7b\u8d77\u6765\uff0c\u5509\u2014\u2014\u8bf4\u6765\u7684\u786e\u6c57\u989c\u5f97\u5f88\u3002\u201d \u3000\u3000\u4ed6\u653e\u4e0b\u4e86\u9152\u676f\uff0c\u4e0d\u7b49\u5353\u957f\u537f\u8bf4\u8bdd\uff0c\u5374\u53c8\u81ea\u987e\u63a5\u7740\u5f80\u4e0b\u8bf4\u9053\uff1a\u201c\u65b9\u624d\u6211\u5728\u6797\u6728\u4e4b\u4e2d\uff0c\u89c1\u5230 \u5144\u53f0\u72ec\u7acb\u957f\u53f9\uff0c\u4fbf\u77e5\u9053\u5144\u53f0\u5fc3\u4e2d\u4e00\u5b9a\u662f\u4e3a\u7740\u4e24\u4e8b\u5fe7\u70e6\uff0c\u4e0d\u80fd\u81ea\u89e3\u2014\u2014\u201d\u4ed6\u5fae\u5fae\u4e00\u7b11\uff0c\u63a5\u9053\uff1a \u201c\u5144\u53f0\u6240\u70e6\u5fe7\u7684\u7b2c\u4e00\u4ef6\u4e8b\uff0c\u81ea\u662f\u4e3a\u4e86\u90a3\u6e29\u5982\u738b\u8981\u53eb\u9601\u4e0b\u5a36\u6e29\u747e\u4e3a\u59bb\uff0c\u90a3\u65f6\u5144\u53f0\u8fd8\u4e0d\u77e5\u9053\u6b64\u4e2d \u5185\u60c5\uff0c\u5fc3\u4e2d\u6781\u4e3a\u4e0d\u613f\u548c\u81ea\u5df1\u4e0d\u5171\u6234\u5929\u4ec7\u4eba\u7684\u5f92\u5f1f\u7ed3\u4e3a\u592b\u5987\uff0c\u4f46\u5374\u53c8\u7b54\u5e94\u4e86\u90a3\u6e29\u5982\u738b\uff0c\u56e0\u4e4b\u5fc3 \u4e2d\u70e6\u607c\uff0c\u5374\u53c8\u65e0\u6cd5\u5411\u4eba\u8bf4\u51fa\uff0c\u66f4\u65e0\u6cd5\u6c42\u4eba\u5e2e\u52a9\uff0c\u8001\u592b\u82e5\u662f\u731c\u7684\u4e0d\u9519\uff0c\u90a3\u4e48\u5144\u53f0\u5fc3\u4e2d\u8fd9\u4e00\u4ef6\u70e6 \u607c\uff0c\u6b64\u523b\u60f3\u5fc5\u4e0d\u4f1a\u518d\u6709\u4e86\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u8f69\u7709\u4e00\u53f9\uff0c\u6717\u58f0\u63a5\u9053\uff1a\u201c\u82e5\u8bba\u51e1\u4e8b\u4ff1\u80fd\u6d1e\u6089\u5148\u673a\uff0c\u53ea\u4f2f\u8001\u4e08\u8fd8\u8981\u8fdc\u5728\u5c0f\u53ef\u4e4b\u4e0a \u54e9\u3002\u201d\u5fc3\u4e2d\u5374\u5728\u6697\u4e2d\u5bfb\u601d\u9053\uff1a\u201c\u65b9\u624d\u6211\u4ec5\u53ea\u5728\u6797\u8fb9\u53f9\u606f\u4e00\u58f0\uff01\u8fd9\u9ad8\u51a0\u7fbd\u58eb\u4fbf\u5df2\u731c\u4e2d\u6211\u7684\u5fc3 \u4e8b\uff0c\u4f46\u4ed6\u660e\u660e\u5df2\u77e5\u6211\u662f\u4e3a\u4e86\u4f55\u4e8b\u53f9\u606f\uff0c\u5374\u53c8\u4e3a\u4f55\u8981\u518d\u4e09\u8ffd\u95ee\u6211\uff1f\u770b\u6765\u6b64\u4eba\u5916\u8c8c\u867d\u662f\u5149\u660e\u78ca \u843d\uff0c\u5fc3\uff01\u4e00\u5374\u4e0d\u77e5\u5bf9\u6211\u6697\u85cf\u7740\u4ec0\u4e48\u673a\u5fc3\u5462\uff1f\u201d \u3000\u3000\u76ee\u5149\u62ac\u5904\uff0c\u53ea\u89c1\u90a3\u9ad8\u51a0\u7fbd\u58eb\u53c8\u81ea\u634b\u987b\u4e00\u7b11\uff0c\u7f13\u7f13\u4ed6\u8bf4\u9053\uff1a\u201c\u8001\u592b\u9047\u4e8b\uff0c\u867d\u4e5f\u80fd\u4e8b\u5148\u731c\u7740 \u4e09\u5206\u5148\u673a\uff0c\u9047\u4eba\u4e5f\u80fd\u731c\u4e2d\u522b\u4eba\u4e09\u5206\u5fc3\u4e8b\uff0c\u4f46\u8fd9\u4e0d\u8fc7\u662f\u5168\u51ed\u8001\u592b\u98d8\u6cca\u4eba\u6d77\u6570\u5341\u5e74\uff0c\u79ef\u5f97\u7684\u4e00\u70b9 \u9605\u5386\u7ecf\u9a8c\u800c\u5df2\uff0c\u600e\u6bd4\u5f97\u5144\u53f0\u5e74\u8f7b\u82f1\u4fca\uff0c\u5929\u7eb5\u5947\u624d\uff0c\u5509\uff01\u5144\u53f0\u82e5\u662f\u5230\u4e86\u8001\u592b\u8fd9\u7b49\u5e74\u7eaa\uff0c\u666e\u5929\u4e4b \u4e0b\uff0c\u65e0\u8bba\u5fc3\u667a\u3001\u6b66\u529f\uff0c\u53ea\u6015\u518d\u4e5f\u627e\u4e0d\u5230\u4e00\u4e2a\u80fd\u4e0e\u5144\u53f0\u988c\u987d\u4e4b\u4eba\u4e86\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u5fae\u7b11\u4e00\u4e0b\uff0c\u53e3\u4e2d\u8c26\u8c22\u4e0d\u5df2\uff0c\u5fc3\u4e2d\u5374\u53c8\u81ea\u5bfb\u601d\u9053\uff1a\u201c\u8fd9\u9ad8\u51a0\u7fbd\u58eb\u81ea\u4ece\u4e00\u89c1\u6211\u9762\uff0c\u6bcf \u4e00\u53e5\u8bdd\u4e2d\u90fd\u5c11\u4e0d\u4e86\u606d\u7ef4\u6211\u4e24\u53e5\uff0c\u4ed6\u6b66\u529f\u663e\u7136\u8f83\u6211\u9ad8\u4e9b\uff0c\u5e74\u9f84\u66f4\u6bd4\u6211\u5927\u4e86\u8bb8\u591a\uff0c\u5bf9\u6211\u5982\u6b64\u5ba2 \u6c14\uff0c\u7adf\u5374\u53c8\u662f\u4e3a\u7684\u4ec0\u4e48\u5462\uff1f\u201d \u3000\u3000\u4ed6\u9605\u5386\u867d\u6d45\uff0c\u4f46\u65b9\u624d\u5df2\u89c9\u8fd9\u9ad8\u51a0\u7fbd\u58eb\u6709\u4e9b\u53ef\u7591\u4e4b\u5904\uff0c\u6b64\u523b\u66f4\u89c9\u5f97\u4ed6\u5982\u6b64\u7ed3\u4ea4\u81ea\u5df1\uff0c\u5fc5\u5b9a \u6709\u7740\u4ec0\u4e48\u6df1\u610f\u3002 \u3000\u3000\u9ad8\u51a0\u7fbd\u58eb\u624b\u4e2d\u8f7b\u637b\u7740\u957f\u9aef\uff0c\u89c1\u5230\u4ed6\u77aa\u7740\u773c\u775b\u51fa\u795e\uff0c\u4e00\u7b11\u800c\u9053\uff1a\u201c\u5144\u53f0\u5fc3\u4e2d\u6240\u5fe7\u8651\u7740\u7684\u7b2c \u4e8c\u4ef6\u4e8b\u4e48\uff1f\u8001\u592b\u6b64\u523b\u4e5f\u731c\u4e0a\u4e00\u731c\uff0c\u5982\u82e5\u8001\u592b\u731c\u7684\u4e0d\u9519\uff0c\u90a3\u4e48\u2014\u2014\u201d\u5353\u957f\u537f\u5fae\u7b11\u63a5\u53e3\u9053\uff1a\u201c\u83ab \u975e\u8001\u4e08\u5bf9\u5c0f\u53ef\u8fd9\u7b2c\u4e8c\u4ef6\u5fc3\u4e8b\uff0c\u4e5f\u6709\u4ec0\u4e48\u5316\u89e3\u7684\u65b9\u6cd5\u4e48\uff1f\u201d \u3000\u3000\u9ad8\u51a0\u7fbd\u58eb\u7b11\u5bb9\u4e00\u655b\uff0c\u6b63\u5bb9\u8bf4\u9053\uff1a\u201c\u8001\u592b\u4e0e\u5144\u53f0\u867d\u7136\u662f\u6d6e\u840d\u5076\u805a\uff0c\u76f8\u8bc6\u751a\u6d45\uff0c\u4f46\u4e5f\u5df2\u770b\u51fa \u5144\u53f0\u975e\u4f46\u5929\u8d44\u7edd\u9876\uff0c\u806a\u6167\u8d85\u4eba\uff0c\u800c\u4e14\u662f\u4e2a\u751f\u5177\u81f3\u60c5\u81f3\u6027\u7684\u70ed\u8840\u7537\u513f\uff0c\u5144\u53f0\u5fc3\u4e2d\u6240\u5fe7\u8651\u7740\u7684\u7b2c \u4e8c\u4ef6\u4e8b\uff0c\u5012\u4e0d\u662f\u4e3a\u7740\u5144\u53f0\u81ea\u5df1\uff0c\u5374\u662f\u4e3a\u7740\u6210\u5343\u6210\u767e\uff0c\u4e0d\u8fdc\u5343\u91cc\u8d76\u6765\u7684\u6b66\u6797\u8c6a\u58eb\u3002\u201d \u3000\u3000\u4ed6\u8bed\u58f0\u4e00\u987f\uff0c\u76ee\u5149\u76f4\u6ce8\u5353\u957f\u537f\u7684\u9762\u76ee\u4e4b\u4e0a\uff0c\u7f13\u7f13\u53c8\u9053\uff1a\u201c\u8001\u592b\u65b9\u624d\u6240\u8bf4\u7684\u8bdd\uff0c\u7edd\u975e\u6545\u610f \u606d\u7ef4\uff0c\u786e\u5b9e\u53e5\u53e5\u51fa\u81ea\u80ba\u8151\uff0c\u800c\u8001\u592b\u81ea\u4fe1\u53cc\u773c\u4e0d\u76f2\uff0c\u5bf9\u5144\u53f0\u7684\u4e3a\u4eba\uff0c\u4e5f\u4e0d\u4f1a\u770b\u63aa\uff0c\u662f\u4ee5\u2026\u2026\u201d \u3000\u3000\u4ed6\u5fae\u5fae\u4e00\u7b11\uff1a\u201c\u8001\u592b\u81ea\u4fe1\u8fd9\u7b2c\u4e8c\u4ef6\u4e8b\u4e48\uff0c\u4e5f\u4e07\u4e07\u4e0d\u4f1a\u731c\u9519\u3002\u201d \u3000\u3000\u4ed6\u76ee\u5149\u4e00\u8f6c\uff0c\u5374\u770b\u5353\u957f\u537f\u6b63\u81ea\u542b\u7b11\u51dd\u795e\u503e\u542c\uff0c\u5374\u5e76\u4e0d\u7b54\u8bdd\uff0c\u4fbf\u53c8\u63a5\u9053\uff1a\u201c\u7ea2\u8863\u5a18\u5a18\u6e29\u5982 \u7389\u86f0\u5c45\u82d7\u7586\u56db\u5341\u5e74\uff0c\u4e00\u5411\u4e0d\u5927\u8fc7\u95f4\u6b66\u6797\u4e2d\u4e8b\uff0c\u8fd9\u5374\u5e76\u975e\u56e0\u5979\u751f\u6027\u606c\u6de1\uff0c\u65e0\u610f\u540d\u5229\uff0c\u800c\u662f\u5979\u5bf9 \u6b66\u6797\u4e2d\u7684\u4e00\u4e9b\u524d\u8f88\u5f02\u4eba\uff0c\u5fc3\u5b58\u754f\u60e7\uff0c\u662f\u4ee5\u4e0d\u6562\u51fa\u6765\u4e3a\u975e\u4f5c\u6b79\u800c\u5df2\u3002\u201d \u3000\u3000\u201c\u4f46\u8fd1\u5e74\u6765\uff0c\u8fd9\u4e9b\u524d\u8f88\u5f02\u4eba\uff0c\u4e0d\u662f\u5df2\u7ecf\u7269\u5316\u4ed9\u53bb\uff0c\u4fbf\u662f\u5c01\u5251\u5df2\u4e45\uff0c\u518d\u4e5f\u4e0d\u95ee\u4e16\u4e8b\uff0c\u8fd9\u7ea2 \u8863\u5a18\u5a18\u9759\u6781\u601d\u52a8\uff0c\u65e9\u5c31\u60f3\u5728\u6c5f\u6e56\u95f4\u6380\u4e9b\u98ce\u6d6a\uff0c\u8fd9\u2018\u5929\u76ee\u4e4b\u4f1a\u2019\uff0c\u540d\u867d\u662f\u4e3a\u5176\u62e9\u5a7f\u4f1a\u53cb\uff0c\u5176\u5b9e \u5374\u662f\u8fd9\u4f4d\u9b54\u5934\u60f3\u501f\u673a\u5c06\u5929\u4e0b\u6b66\u6797\u8c6a\u58eb\u4e00\u7f51\u6253\u5c3d\uff0c\u8fd9\u70b9\u5144\u53f0\u60f3\u5fc5\u4e5f\u4ece\u5979\u8bf4\u8bdd\u4e4b\u95f4\u770b\u51fa\u6765\u4e86\uff0c\u662f \u4ee5\u5144\u53f0\u4fbf\u5728\u5fe7\u90c1\uff0c\u5982\u4f55\u624d\u80fd\u5c06\u6b66\u6797\u4e2d\u8fd9\u573a\u52ab\u96be\u6d88\u6d31\u3002\u201d \u3000\u3000\u4ed6\u7565\u4e3a\u6b47\u606f\u4e00\u4e0b\uff0c\u5353\u957f\u537f\u5fc3\u4e2d\u5374\u6026\u7136\u4e00\u52a8\uff0c\u63a5\u53e3\u95ee\u9053\uff1a\u201c\u96be\u9053\u8001\u4e08\u6709\u4f55\u5999\u7b56\uff0c\u80fd\u89e3\u5f00\u5c0f \u53ef\u5fc3\u4e2d\u8fd9\u4ef6\u5fe7\u90c1\u4e4b\u4eab\u5417\uff1f\u201d \u3000\u3000\u9ad8\u51a0\u7fbd\u58eb\u5fae\u7b11\u4e00\u4e0b\uff0c\u76ee\u5149\u4e4b\u4e2d\uff0c\u6de1\u6de1\u63a0\u8fc7\u4e00\u4e1d\u6781\u4e3a\u5f97\u610f\u7684\u795e\u91c7\uff0c\u7aef\u8d77\u9762\u524d\u9152\u676f\u4ef0\u9996\u4e00\u5e72 \u800c\u5c3d\uff0c\u542b\u7b11\u8bf4\u9053\uff1a\u201c\u8001\u592b\u8fd9\u7b2c\u4e8c\u4ef6\u4e8b\uff0c\u731c\u7684\u8fd8\u4e0d\u9519\u5427\uff1f\u201d \u3000\u3000\u5176\u5b9e\u5353\u957f\u537f\u65b9\u624d\u90a3\u53e5\u8bdd\uff0c\u5df2\u65e0\u6b8a\u544a\u8bc9\u4ed6\u81ea\u5df1\u5fc3\u4e2d\u6240\u5fe7\u8651\u7684\u6b63\u662f\u6b64\u4e8b\uff0c\u662f\u4ee5\u4ed6\u4fbf\u6839\u672c\u4e0d\u5fc5 \u7b49\u5f85\u56de\u7b54\uff0c\u53c8\u81ea\u659f\u4e86\u4e00\u676f\u9152\uff0c\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u6b64\u4e8b\u7684\u786e\u4e95\u975e\u6613\u4e0e\uff0c\u96be\u602a\u5144\u53f0\u5fc3\u4e2d\u5fe7\u90c1\uff0c\u60f3\u90a3\u7ea2 \u8863\u5a18\u5a18\u5728\u5929\u76ee\u5c71\u4e2d\u8bbe\u4e0b\u7684\u9999\u9975\uff0c\u4ff1\u662f\u6b66\u6797\u4e2d\u4eba\u68a6\u5bd0\u96be\u6c42\u4e4b\u7269\uff0c\u8fd9\u4e9b\u4eba\u4e0d\u60dc\u8fdc\u9053\u800c\u6765\uff0c\u5144\u53f0\u82e5 \u5728\u6b64\u523b\u52a0\u4ee5\u963b\u6b62\uff0c\u4ed6\u4eec\u53c8\u600e\u4f1a\u5fc3\u7518\u60c5\u613f\u7684\u653e\u5f03\uff0c\u53c8\u600e\u4f1a\u76f8\u4fe1\u5144\u53f0\u7684\u8bdd\uff0c\u53ea\u6015\u4ed6\u4eec\u8fd8\u5f53\u5144\u53f0\u60f3 \u72ec\u541e\u8fd9\u4e9b\u73cd\u5b9d\u5462\uff01\u201d \u3000\u3000\u5353\u957f\u537f\u4e00\u76b1\u53cc\u7709\u9053\uff1a\u201c\u662f\u4e86\uff0c\u60f3\u4ed6\u4eec\u53c8\u600e\u4f1a\u542c\u4ece\u6211\u7684\u8bdd\uff0c\u5fc3\u7518\u60c5\u613f\u5730\u653e\u5f03\u8fd9\u4e9b\u73cd\u5b9d\u5462\uff1f \u5509\u2014\u2014\u90a3\u4e11\u4eba\u6e29\u5982\u7389\u4e0d\u77e5\u5728\u5929\u76ee\u5c71\u4e2d\uff0c\u8bbe\u4e0b\u4ec0\u4e48\u53e4\u602a\u82b1\u6837\u3001\u6076\u6bd2\u9677\u9631\uff0c\u5374\u53ef\u601c\u8fd9\u4e9b\u4eba\u4e00\u70b9\u4e5f \u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u8fd9\u4e2a\u521d\u6d89\u6c5f\u6e56\u7684\u5c11\u5e74\uff0c\u867d\u7136\u5bf9\u90a3\u9ad8\u51a0\u7fbd\u58eb\u5df2\u751f\u7591\u60d1\u4e4b\u5fc3\uff0c\u4f46\u6b64\u523b\u5374\u53c8\u4e0d\u7981\u4e3a\u4ed6\u7684\u8fd9\u756a\u8a00 \u8bed\u6240\u52a8\uff0c\u7ade\u53c8\u5c06\u5fc3\u4e2d\u7684\u8bdd\u8bf4\u4e86\u51fa\u6765\u3002 \u3000\u3000\u9ad8\u51a0\u7fbd\u58eb\u6545\u610f\u4fef\u9996\u6c89\u541f\u534a\u664c\uff0c\u62ac\u5934\u4e00\u7b11\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u8001\u592b\u65b9\u624d\u5bf9\u5144\u53f0\u8bf4\u7684\u90a3\u4e2a\u6545\u4e8b\uff0c \u4e0d\u4f46\u80fd\u5c06\u5144\u53f0\u5fc3\u4e8b\u7b2c\u4e00\u4ef6\u5fe7\u8651\u4e4b\u4e8b\u5316\u89e3\uff0c\u5144\u53f0\u8fd9\u7b2c\u4e8c\u4ef6\u5fc3\u4e8b\uff0c\u5374\u4e5f\u8981\u4f9d\u9760\u8fd9\u4e2a\u6545\u4e8b\uff0c\u624d\u80fd\u5316 \u89e3\u5f97\u5f00\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u4e0d\u7981\u4e3a\u4e4b\u4e00\u6014\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u4e3a\u4e86\u4f55\u6545\u5462\uff1f\u201d \u3000\u3000\u9ad8\u51a0\u7fbd\u58eb\u4e00\u7b11\u9053\uff1a\u201c\u5144\u53f0\u82e5\u5728\u4f1a\u671f\u4e4b\u524d\uff0c\u8d76\u5230\u5929\u76ee\u5c71\u53bb\uff0c\u5c06\u8001\u592b\u65b9\u624d\u6240\u8bf4\u7684\u90a3\u4e2a\u6545\u4e8b\uff0c \u4e00\u5b57\u4e0d\u6f0f\u5730\u5bf9\u90a3\u6e29\u747e\u8bf4\u4e00\u904d\uff0c\u90a3\u4e48\u2014\u2014\u54c8\u54c8\uff01\u201d \u3000\u3000\u4ed6\u4ef0\u9996\u72c2\u7b11\u6570\u58f0\uff0c\u63a5\u7740\u53c8\u9053\uff1a\u201c\u60f3\u90a3\u6e29\u747e\u82e5\u662f\u7a0d\u6709\u4eba\u6027\uff0c\u600e\u4f1a\u518d\u6709\u534a\u523b\u8fdf\u7591\uff0c\u5fc5\u5b9a\u7acb\u5373 \u53bb\u5bfb\u90a3\u5973\u9b54\u5934\u62a5\u4ec7\uff0c\u5144\u53f0\u82e5\u5728\u65c1\u8fb9\u7a0d\u52a0\u63f4\u624b\uff0c\u90a3\u7ea2\u8863\u5a18\u5a18\u6b66\u529f\u518d\u9ad8\uff0c\u5374\u4e5f\u4e0d\u89c1\u80fd\u9003\u51fa\u4e24\u4f4d\u7684 \u624b\u4e0b\uff0c\u54c8\u54c8\u2014\u2014\u6614\u5e74\u6881\u5b5f\u53cc\u4fa0\uff0c\u592b\u5531\u5987\u968f\uff0c\u5929\u4e0b\u8273\u7fa1\uff0c\u4eca\u65e5\u5144\u53f0\u4e0e\u90a3\u4f4d\u6e29\u59d1\u5a18\uff0c\u4e0d\u4f46\u540c\u4ec7\u654c \u6c7d\uff0c\u800c\u4e14\u73e0\u8054\u58c1\u5408\uff0c\u6b64\u756a\u82e5\u80fd\u8054\u624b\u8bdb\u6b64\u9b45\u9b51\uff0c\u62a5\u5374\u6df1\u4ec7\uff0c\u53c8\u5c06\u4e3a\u6b66\u6797\u6dfb\u4e00\u4f73\u8bdd\u3002\uff0c\u4ed6\u7b11\u5bb9\u6ee1 \u9762\u5730\u4e3e\u8d77\u9762\u524d\u9152\u676f\uff0c\u5927\u7b11\u53c8\u9053\uff1a\u2018\u8fd9\u4e48\u4e00\u6765\uff0c\u5143\u51f6\u65e2\u9664\uff0c\u5929\u76ee\u4e4b\u4f1a\uff0c\u5c31\u7b97\u80fd\u591f\u5982\u671f\u4e3e\u884c\uff0c\u4f46 \u90a3\u9b54\u5934\u8bbe\u4e0b\u7684\u8bf8\u822c\u9677\u9631\uff0c\u60f3\u5fc5\u4e5f\u5c06\u53d8\u6210\u5144\u53f0\u4e0e\u6e29\u59d1\u5a18\u7684\u8fce\u5bbe\u6218\u5bb4\uff0c\u8fd9\u573a\u6b66\u6797\u52ab\u96be\uff0c\u5c82\u975e\u6d88\u6d31 \u4e8e\u65e0\u5f62\uff0c\u6765\uff0c\u6765\uff0c\u4e14\u5bb9\u8001\u592b\u5148\u656c\u5144\u53f0\u4e00\u676f\u3002\u201c\u4ef0\u9996\u4e00\u5e72\u800c\u5c3d\uff0c\u62ac\u76ee\u671b\u53bb\uff0c\u5374\u89c1\u5353\u957f\u537f\u53cc\u76ee\u671b \u7740\u9762\u524d\u7684\u9152\u676f\u51fa\u795e\uff0c\u53cc\u624b\u653e\u5728\u684c\u4e0a\uff0c\u52a8\u4e5f\u672a\u52a8\uff0c\u5bf9\u90a3\u9152\u676f\u78b0\u90fd\u6ca1\u6709\u78b0\u4e00\u4e0b\u3002\u9ad8\u51a0\u7fbd\u58eb\u9762\u5bb9\u5fae \u53d8\uff0c\u4e3e\u7740\u9152\u676f\u7684\u624b\uff0c\u534a\u664c\u653e\u4e0d\u4e0b\u53bb\uff0c\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u4ed6\u9762\u4e0a\u7684\u8868\u60c5\uff0c\u7a81\u5730\u53d8\u5f97\u5341\u5206\u72de\u6076\uff0c\u5148\u524d \u90a3\u79cd\u6d69\u7136\u7684\u6b63\u6c14\uff0c\u4e5f\u81ea\u6d88\u53bb\u65e0\u5f71\uff0c\u53ea\u662f\u5353\u957f\u537f\u76ee\u5149\u4f4e\u5782\uff0c\u5e76\u672a\u770b\u5230\u800c\u5df2\u3002\u7b49\u5230\u4ed6\u90a3\u53cc\u5fae\u5e26\u8ff7 \u60d1\u7684\u53cc\u76ee\u7f13\u7f13\u81ea\u9152\u676f\u79fb\u5230\u9ad8\u51a0\u7fbd\u58eb\u9762\u4e0a\u7684\u65f6\u5019\uff0c\u8fd9\u84bf\u51a0\u7fbd\u58eb\u9762\u4e0a\u7684\u72de\u6076\u4e4b\u8272\uff0c\u7adf\u53c8\u4ece\u4ed6\u5634\u89d2 \u6240\u6cdb\u8d77\u7684\u4e00\u4e1d\u5fae\u7b11\u4e2d\u5316\u53bb\u3002\u4e8e\u662f\uff0c\u76f4\u5230\u5f88\u4e45\u5f88\u4e45\u4ee5\u540e\uff0c\u4ed6\u8fd8\u662f\u65e0\u6cd5\u77e5\u9053\u8fd9\u9ad8\u51a0\u7fbd\u58eb\u7a76\u7adf\u662f\u4f55 \u8bb8\u4eba\u7269\uff1f\u4e5f\u672a\u80fd\u77e5\u9053\u6b64\u4eba\u7684\u771f\u6b63\u6765\u610f\u3002\u88ab\u6f6e\u6c34\u6df9\u6ca1\u7684\u6c99\u6ee9\uff0c\u7b49\u5230\u6f6e\u6c34\u9000\u53bb\u7684\u65f6\u5019\uff0c\u4f9d\u7136\u662f\u539f \u6765\u7684\u6837\u5b50\uff0c\u6c99\u6ee9\u4e0a\u7684\u6c99\u7c92\u548c\u8d1d\u58f3\uff0c\u867d\u7136\u4f1a\u56e0\u4e4b\u6f6e\u6e7f\uff0c\u4f46\u662f\u6f6e\u6c34\u4e5f\u4f1a\u5f88\u5feb\u5730\u9000\u53bb\u7684\uff0c\u90a3\u4e48\uff0c\u88ab \u865a\u5047\u63a9\u9970\u7740\u7684\u79d8\u5bc6\uff0c\u6050\u6015\u4e5f\u4e0d\u4f1a\u9690\u85cf\u591a\u4e45\u5427\uff1f\u5353\u957f\u537f\u62ac\u8d77\u5934\u6765\uff0c\u4e24\u4eba\u76ee\u5149\u76f8\u5bf9\uff0c\u9ad8\u51a0\u7fbd\u58eb\u7a81 \u53c8\u7b11\u9053\uff1a\u201c\u53ea\u662f\u8001\u592b\u8fd8\u5fd8\u4e86\u544a\u8bc9\u5144\u53f0\u4e00\u4e8b\uff0c\u6b64\u523b\u90a3\u5929\u76ee\u5c71\u4e0a\uff0c\u6b63\u5982\u5144\u53f0\u6240\u6599\uff0c\u65e9\u5df2\u57cb\u8bbe\u4e0b\u8bb8 \u591a\u867d\u662f\u8003\u8f83\u7fa4\u8c6a\u6b66\u529f\uff0c\u5176\u5b9e\u5374\u662f\u6697\u5bb3\u7fa4\u8c6a\u7684\u9677\u9631\u8bbe\u65bd\uff0c\u8fd9\u4e9b\u8bbe\u65bd\u4e4b\u4e2d\uff0c\u7a76\u7adf\u5305\u6db5\u7740\u4ec0\u4e48\u6076\u6bd2 \u82b1\u6837\uff0c\u8001\u592b\u867d\u7136\u4e0d\u751a\u6e05\u695a\uff0c\u4f46\u8001\u592b\u5374\u77e5\u9053\u90a3\u9b54\u5934\u6e29\u5982\u7389\uff0c\u4e0d\u4f46\u5728\u8fd9\u4e9b\u672c\u5e94\u5149\u660e\u6b63\u5927\uff0c\u7528\u505a\u8003 \u8f83\u6b66\u529f\u7684\u4e94\u832b\u73e0\u3001\u7f57\u6c49\u9635\u3001\u7ebf\u9999\u6e21\u4e00\u7c7b\u8bbe\u65bd\u4e4b\u4e2d\uff0c\u6697\u8bbe\u4e0b\u8bb8\u591a\u8be1\u8ba1\uff0c\u800c\u4e14\u8fd8\u552f\u6050\u8fd9\u4e9b\u8be1\u8ba1\u4e0d \u591f\u6076\u6bd2\uff0c\u5bb3\u4e0d\u5230\u522b\u4eba\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u610f\u52a8\u5fc3\u60ca\uff0c\u73b0\u4e8e\u795e\u8272\uff0c\u8f6c\u7709\u6012\u9053\uff1a\u201c\u5979\u4fbf\u53c8\u600e\u6837\uff1f\u201d \u3000\u3000\u9ad8\u51a0\u7fbd\u58eb\u751f\u50cf\u662f\u4e0d\u80dc\u611f\u6168\u5730\u957f\u53f9\u4e00\u58f0\uff0c\u63a5\u7740\u53c8\u9053\uff1a\u201c\u8fd9\u9b54\u5934\u7adf\u5728\u4e00\u5e74\u4e2d\uff0c\u5c06\u4e00\u4e9b\u4e45\u5df2\u91d1 \u76c6\u6d17\u624b\u7684\u7eff\u6797\u5de8\u5bc7\uff0c\u6216\u662f\u4e00\u4e9b\u86f0\u4f0f\u585e\u5916\u3001\u9041\u8ff9\u8fb9\u8352\u3001\u4e45\u5df2\u4e0d\u5bb9\u4e8e\u6b66\u6797\u7684\u6c5f\u6e56\u5996\u9b54\uff0c\u6697\u4e2d\u8bf7 \u6765\uff0c\u505a\u8fd9\u4e9b\u8bbe\u65bd\u7684\u4e3b\u6301\u4e4b\u4eba\uff0c\u4e00\u4e9b\u6b66\u529f\u7279\u9ad8\u7684\u6b66\u6797\u8c6a\u58eb\uff0c\u5c31\u7b97\u80fd\u4fa5\u5e78\u9003\u51fa\u5979\u4eec\u8bbe\u4e0b\u7684\u6076\u6bd2\u9677 \u9631\uff0c\u5374\u4e5f\u4e0d\u80fd\u9003\u6d59\u8fd9\u4e9b\u5de8\u5bc7\u5996\u9b54\u7684\u6bd2\u624b\uff0c\u5c31\u7b97\u4ed6\u4eec\u518d\u80fd\u9003\u51fa\u6bd2\u624b\uff0c\u751a\u81f3\u5c06\u8fd9\u4e9b\u5996\u9b54\u51fb\u6bd9\uff0c\u53ef \u662f\u7b49\u5230\u4ed6\u4eec\u6700\u540e\u5230\u8fbe\u90a3\u6e29\u5982\u738b\u8bbe\u4e0b\u7684\u4e3b\u64c2\u4e4b\u65f6\uff0c\u5374\u5df2\u65e9\u5c31\u7cbe\u75b2\u529b\u7aed\uff0c\u53ea\u6015\u8fde\u5979\u7684\u8f7b\u8f7b\u4e00\u51fb\uff0c \u90fd\u65e0\u6cd5\u62b5\u6321\u4e86\uff0c\u201d\u8fd9\u9ad8\u51a0\u7fbd\u58eb\u4e00\u53e3\u6c14\u8bf4\u5230\u8fd9\u91cc\uff0c\u53ea\u542c\u5f97\u5353\u957f\u537f\u5fc3\u80f8\u4e4b\u95f4\u65e2\u662f\u60ca\u60e7\uff0c\u53c8\u662f\u6124 \u6168\uff0c\u7adf\u4e5f\u6ca1\u6709\u518d\u53bb\u60f3\u4e00\u60f3\uff0c\u8fd9\u4e9b\u6781\u4e3a\u7a33\u79d8\u4e4b\u4e8b\uff0c\u8fd9\u4e0e\u4e16\u65e0\u4e89\u7684\u9ad8\u51a0\u7fbd\u58eb\u53c8\u600e\u4f1a\u77e5\u9053\u7684\u5462\uff1f \u3000\u3000\u5374\u542c\u9ad8\u51a0\u7fbd\u58eb\u53f9\u606f\u7740\u53c8\u9053\uff1a\u201c\u5979\u4e00\u8ba1\u8fde\u7740\u4e00\u8ba1\uff0c\u8fd9\u8fde\u73af\u6bd2\u8ba1\uff0c\u4e3a\u7684\u4e0d\u5355\u53ea\u8981\u5c06\u5929\u4e0b\u7684\u6b66 \u6797\u8c6a\u58eb\u4e00\u4e2a\u4e2a\u6253\u5c3d\uff0c\u800c\u4e14\u8fde\u90a3\u4e9b\u88ab\u5979\u6216\u4ee5\u5229\u8bf1\uff0c\u6216\u4ee5\u540d\u52a8\uff0c\u4ece\u5404\u5730\u8bf7\u6765\u7684\u5de8\u5bc7\u5996\u9b54\uff0c\u7adf\u4e5f\u5728 \u5979\u9664\u53bb\u4e4b\u5217\uff0c\u5230\u90a3\u65f6\u5019\u6b66\u6797\u4e4b\u4e2d\uff0c\u5979\u4e00\u4eba\u552f\u6211\u72ec\u5c0a\uff0c\u624d\u7b97\u79f0\u4e86\u5979\u7684\u5fc3\u610f\u3002\u201d \u3000\u3000\u4e00\u65f6\u4e4b\u95f4\uff0c\u5353\u957f\u537f\u9762\u5bb9\u9635\u60ca\uff0c\u9635\u6012\uff0c\u7a81\u5730\u957f\u53f9\u4e00\u58f0\uff0c\u590d\u53c8\u4f4e\u8bed\u9053\uff1a\u201c\u5c0f\u53ef\u5e74\u9f84\u6781\u5e7c\u4e4b \u65f6\uff0c\u66fe\u5728\u9ec4\u5c71\u59cb\u4fe1\u5cf0\u4e0b\uff0c\u9047\u7740\u4e00\u4ef6\u60ca\u4eba\u4e4b\u4e8b\uff0c\u5c0f\u53ef\u5f53\u65f6\u867d\u672a\u76ee\u7779\uff0c\u4f46\u8fd9\u4ef6\u4e8b\u5728\u5c0f\u53ef\u5fc3\u4e2d\uff0c\u5374 \u59cb\u7ec8\u8bb0\u5f97\u9c9c\u660e\u3002\u201d \u3000\u3000\u4ed6\u53c8\u81ea\u6c89\u58f0\u4e00\u53f9\uff0c\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u90a3\u662f\u5341\u591a\u5e74\u4ee5\u524d\u7684\u4e8b\u4e86\uff0c\u6211\u5374\u4e00\u76f4\u5728\u5947\u602a\uff0c\u90a3\u6bd2\u7269\u661f \u870d\uff0c\u4e3a\u4ec0\u4e48\u5728\u5c06\u4e00\u4e9b\u51f6\u66b4\u6076\u6bd2\u7684\u6bd2\u86c7\u731b\u517d\u9664\u53bb\u4e4b\u5916\uff0c\u5374\u53c8\u8981\u53bb\u6b8b\u5bb3\u90a3\u4e9b\u65e0\u5bb3\u4e88\u4eba\u7684\u7ef5\u7f8a\u9a6f \u9e7f\uff0c\u8fd9\u5c82\u975e\u662f\u4ef6\u96be\u4ee5\u7406\u89e3\u4e4b\u4e8b\uff0c\u5509\u4e00\u6b64\u523b\u6211\u624d\u77e5\u9053\uff0c\u539f\u6765\u4eba\u7c7b\u4e4b\u4e2d\u7adf\u4e5f\u6709\u7740\u50cf\u661f\u870d\u4e00\u6837\u7684\u90aa \u6076\u4e4b\u7269\u3002\u201d \u3000\u3000\u4ed6\u4f4e\u4f4e\u4ed6\u8bf4\u7740\uff0c\u800c\u4e14\u8bf4\u7684\u975e\u5e38\u51cc\u4e71\uff0c\u4f46\u5f53\u4ed6\u5728\u8bf4\u7740\u8fd9\u4e9b\u8bdd\u7684\u65f6\u5019\uff0c\u90a3\u9ad8\u51a0\u7fbd\u58eb\u9762\u4e0a\u7684\u795e \u60c5\uff0c\u5374\u50cf\u662f\u975e\u5e38\u6fc0\u52a8\uff0c\u5e97\u91cc\u7684\u5e97\u4f19\uff0c\u8fdc\u8fdc\u7ad9\u5728\u95e8\u53e3\uff0c\u538c\u6076\u5730\u770b\u7740\u8fd9\u4e24\u4e2a\u4e45\u5750\u4e0d\u8d70\u7684\u5ba2\u4eba\uff0c\u53ea \u89c1\u4ed6\u4eec\u5ffd\u800c\u5927\u7b11\uff0c\u5ffd\u800c\u957f\u53f9\uff0c\u5ffd\u53c8\u6ed4\u6ed4\u4e0d\u7edd\u4ed6\u8bf4\u7740\u8bdd\uff0c\u5fc3\u91cc\u5927\u4e3a\u5947\u602a\uff0c\u4e0d\u77e5\u9053\u8fd9\u4e00\u8001\u4e00\u5c11\u4e24 \u4eba\uff0c\u7a76\u7adf\u662f\u5e72\u4ec0\u4e48\u7684\u3002 \u3000\u3000\u9ad8\u51a0\u7fbd\u58eb\u5b9a\u4e86\u5b9a\u795e\uff0c\u65b9\u81ea\u8bf4\u9053\uff1a\u201c\u8001\u592b\u6b64\u523b\u53ea\u8981\u544a\u8bc9\u5144\u53f0\uff0c\u4fbf\u662f\u5144\u53f0\u6b64\u6b21\u82e5\u771f\u7684\u4e0d\u60dc\u5371 \u9669\uff0c\u5148\u5c31\u8d76\u5230\u5929\u76ee\u5c71\u53bb\uff0c\u7eb5\u7136\u90a3\u9b54\u5934\u6e29\u5982\u7389\u5df2\u5c06\u5144\u53f0\u770b\u6210\u5979\u7231\u5f92\u7684\u4e58\u9f99\u5feb\u5a7f\uff0c\u4e0d\u4f1a\u52a0\u5bb3\u4e8e \u4f60\uff0c\u4f46\u90a3\u4e9b\u79c9\u6027\u51f6\u6076\u7684\u5de8\u5bc7\u5996\u9b54\uff0c\u5374\u672a\u89c1\u4f1a\u653e\u8fc7\u5144\u53f0\uff0c\u5144\u53f0\u6b66\u529f\u867d\u9ad8\uff0c\u4f46\u53cc\u62f3\u4e0d\u654c\u56db\u624b\uff0c\u5509 \u2014\u2014\u201d\u4ed6\u6545\u610f\u957f\u53f9\u4e00\u58f0\uff0c\u65b9\u81ea\u63a5\u9053\uff1a\u201c\u8001\u592b\u4e0e\u5144\u53f0\u4e00\u89c1\u5982\u6545\uff0c\u4e3a\u7740\u5144\u53f0\u7740\u60f3\uff0c\u8fd9\u5929\u76ee\u5c71\u4e48\u2014 \u2014\u201d\u8bed\u58f0\u53c8\u4e00\u987f\uff1a\u201c\u4e0d\u53bb\u4e5f\u7f62\u3002\u201d \u3000\u3000\u6697\u4e2d\u4e00\u8198\uff0c\u773c\u89d2\u53ea\u89c1\u5353\u957f\u537f\u679c\u5df2\u5251\u7709\u6012\u8f69\uff0c\u4e49\u6124\u586b\u81ba\uff0c\u7adf\u81ea\u4f38\u51fa\u624b\u638c\uff0c\u5728\u684c\u4e0a\u731b\u5730\u4e00 \u62cd\uff0c\u6717\u58f0\u9053\uff1a\u201c\u8001\u4e08\u600e\u5730\u5982\u6b64\u8f7b\u89c6\u4e8e\u6211\uff0c\u90a3\u5929\u76ee\u5c71\u4e0a\u7eb5\u7136\u662f\u5200\u5c71\u5251\u6d77\uff0c\u6211\u6b64\u756a\u4e5f\u8981\u53bb\u95ef\u4ed6\u4e00 \u95ef\uff0c\u5353\u957f\u537f\u867d\u7136\u4e0d\u624d\uff0c\u4f46\u8def\u89c1\u4e0d\u5e73\uff0c\u5c1a\u8981\u62d4\u5200\u76f8\u52a9\uff0c\u4e3a\u7740\u5929\u4e0b\u6b66\u6797\u670b\u53cb\u7684\u547d\u8fd0\uff0c\u6211\u5353\u957f\u537f\u53c8 \u4f55\u60dc\u6027\u547d\uff0c\u5c31\u7b97\u662f\u4e24\u808b\u63d2\u5200\uff0c\u7c89\u8eab\u788e\u9aa8\uff0c\u6211\u4e5f\u4e0d\u4f1a\u76b1\u4e00\u76b1\u7709\u5934\u3002\u201d \u3000\u3000\u9ad8\u51a0\u7fbd\u58eb\u4fef\u8eab\u6574\u7406\u7740\u88ab\u5353\u957f\u537f\u4e00\u638c\u9707\u5012\u7684\u676f\u76cf\uff0c\u4e8e\u662f\uff0c\u4ed6\u773c\u4e2d\u6240\u6d41\u9732\u51fa\u7684\u90a3\u79cd\u5f97\u610f\u800c\u72de \u6076\u7684\u76ee\u5149\uff0c\u5353\u957f\u537f\u4fbf\u53c8\u65e0\u6cd5\u770b\u5230\u3002 \u3000\u3000\u4e14\u8bf4\u4e34\u5b89\u57ce\u91cc\u2014\u2014 \u3000\u3000\u591a\u81c2\u795e\u5251\u4e91\u8c26\u7236\u5b50\uff0c\u4ee5\u53ca\u90a3\u98de\u9a91\u5954\u6765\uff0c\u62a5\u51f6\u8baf\u3001\u6c42\u63f4\u624b\u7684\u5927\u6c49\uff0c\u53c8\u600e\u4f1a\u77e5\u9053\u4ed6\u4eec\u6240\u7126\u6025 \u7b49\u5f85\u7740\u7684\u5353\u957f\u537f\uff0c\u4e0d\u4f46\u5df2\u7ecf\u89c1\u7740\u4ed6\u542f\u5df1\u4e0d\u5171\u6234\u5929\u7684\u4ec7\u4eba\uff0c\u800c\u4e14\u8fd8\u906d\u9047\u5230\u8fd9\u4e9b\u590d\u6742\u800c\u5947\u5f02\u7684 \u4e8b\u3002 \u3000\u3000\u8fd9\u4e00\u65e5\u4e4b\u95f4\u6240\u53d1\u751f\u7684\u4e8b\uff0c\u4e0d\u4f46\u4f7f\u5f97\u5353\u957f\u537f\u7684\u751f\u547d\u4e3a\u4e4b\u6539\u89c2\uff0c\u751a\u81f3\u5929\u4e0b\u6b66\u6797\u4e2d\u4eba\u7684\u547d\u8fd0\uff0c \u4e5f\u53d7\u5230\u5f71\u54cd\uff0c\u8fd9\u5374\u4e5f\u662f\u4e34\u5b89\u57ce\u91cc\u7684\u4e91\u6c0f\u7236\u5b50\u65e0\u6cd5\u9884\u6599\u5f97\u5230\u7684\u3002 \u3000\u3000\u4e00\u9635\u98ce\u5439\u6765\uff0c\u5439\u6563\u4e86\u897f\u5929\u7684\u665a\u971e\uff0c\u6708\u4eae\u5374\u4ece\u4e1c\u8fb9\u5347\u8d77\u6765\u4e86\uff0c\u53c8\u662f\u4e00\u4e2a\u6709\u6708\u6709\u661f\u7684\u665a\u4e0a\u3002 \u3000\u3000\u5353\u957f\u537f\u4ece\u90a3\u5c0f\u5c0f\u7684\u9102\u83dc\u9152\u94fa\uff0c\u6f2b\u6b65\u8d70\u51fa\uff0c\u4ed6\u7684\u6001\u5ea6\u867d\u7136\u4ecd\u662f\u90a3\u4e48\u4ece\u5bb9\u800c\u5b89\u8be6\uff0c\u4f46\u662f\u4ed6\u7684 \u5fc3\u7eea\uff0c\u5374\u8fdc\u4e0d\u53ca\u5916\u8868\u7684\u5b89\u5b9a\u3002 \u3000\u3000\u65b9\u624d\uff0c\u592a\u9633\u521a\u521a\u9690\u6ca1\u7684\u65f6\u5019\uff0c\u90a3\u9ad8\u51a0\u7fbd\u58eb\u5c31\u8d77\u8eab\u544a\u8f9e\uff0c\u4e34\u8d70\u7684\u65f6\u5019\uff0c\u8fd8\u9053\uff1a\u201c\u8001\u592b\u4e0e\u541b \u4e00\u5e2d\u957f\u8c08\uff0c\u66f4\u89c9\u5f97\u5144\u53f0\u662f\u6b66\u6797\u4e2d\u767e\u5e74\u96be\u89c1\u3001\u4e0d\u53ef\u591a\u5f97\u7684\u5c11\u5e74\u4fa0\u58eb\uff0c\u5bf9\u6b64\u756a\u6b66\u6797\u6d69\u52ab\uff0c\u5144\u53f0\u60f3 \u5fc5\u80fd\u6709\u4e00\u59a5\u5584\u5b89\u6392\uff0c\u8001\u592b\u65b9\u624d\u7d6e\u7d6e\u6240\u8a00\uff0c\u4e0d\u8fc7\u662f\u7ed9\u5144\u53f0\u4e00\u4e2a\u53c2\u8003\u800c\u5df2\uff0c\u5144\u53f0\u5982\u80fd\u5c06\u6b64\u6d69\u52ab\u6d88 \u5f2d\uff0c\u5219\u4e0d\u4f46\u8001\u592b\u5e78\u751a\uff0c\u4ea6\u662f\u6b66\u6797\u4e2d\u5343\u767e\u540c\u9053\u4e4b\u5e78\u4e86\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u9ed8\u9ed8\u5730\u542c\u7740\u4ed6\u7684\u8bdd\uff0c\u957f\u63d6\u76f8\u9001\uff0c\u81ea\u5df1\u5374\u4ecd\u7136\u5750\u5728\u90a3\u95f4\u5c0f\u5c0f\u7684\u9152\u94fa\u91cc\uff0c\u6c89\u601d\u826f\u4e45\uff0c \u8fd9\u9ad8\u51a0\u7fbd\u58eb\u7684\u4e00\u5e2d\u8bdd\uff0c\u867d\u7136\u4f7f\u4ed6\u660e\u767d\u4e86\u8bb8\u591a\u4ed6\u4ee5\u524d\u4e0d\u77e5\u9053\u7684\u4e8b\uff0c\u5374\u4e5f\u66ff\u4ed6\u6dfb\u4e86\u8bb8\u591a\u7591\u4e91\u3002 \u3000\u3000\u5929\u5c31\u665a\uff0c\u66ae\u4e91\u56db\u5408\uff0c\u9152\u94fa\u4e2d\u7684\u98df\u5ba2\u4e5f\u591a\u4e86\u8d77\u6765\uff0c\u89c1\u5230\u4ed6\u4e00\u4e2a\u4eba\u5750\u7740\u53d1\u6115\uff0c\u90fd\u4e0d\u7981\u6295\u4ee5\u8be7 \u5f02\u7684\u773c\u8272\uff0c\u4ed6\u89c9\u5bdf\u5230\u4e86\uff0c\u4fbf\u4e5f\u8d70\u4e86\u51fa\u6765\uff0c\u98ce\u8d8a\u6765\u8d8a\u51c9\uff0c\u65e5\u95f4\u7684\u8925\u6691\u4e4b\u610f\uff0c\u6b64\u523b\u5df2\u4e3a\u4e4b\u5c3d\u6d88\uff0c \u4f46\u662f\u4ed6\u7684\u5fc3\uff0c\u5374\u4ecd\u7136\u6c89\u95f7\u5f97\u5f88\uff0c\u8fd8\u662f\u4e0d\u77e5\u9053\u81ea\u5df1\u6b64\u523b\u7a76\u7adf\u8be5\u5982\u4f55\u505a\u3002 \u3000\u3000\u65b9\u624d\u534a\u65e5\u4e4b\u95f4\uff0c\u90a3\u9ad8\u51a0\u7fbd\u58eb\u6ed4\u6ed4\u8fa9\u624d\uff0c\u867d\u7136\u4f7f\u5f97\u5353\u957f\u537f\u5c06\u81ea\u5df1\u5bf9\u4ed6\u7684\u7591\u60d1\u4e4b\u5fc3\u6d88\u53bb\u4e0d \u5c11\uff0c\u4f46\u6b64\u523b\u5353\u957f\u537f\u6c89\u601d\u4e4b\u4e0b\uff0c\u5374\u53c8\u4e0d\u7981\u5f00\u59cb\u89c9\u5f97\u6b64\u4eba\u53ef\u7591\uff0c\u4e0d\u4f4f\u5730\u6697\u81ea\u5bfb\u601d\u9053\uff1a\u201c\u6b64\u4eba\u5458\u662f \u53ef\u7591\uff0c\u4f46\u4ed6\u6240\u8bf4\u7684\u8bdd\uff0c\u5374\u662f\u6781\u4e3a\u5408\u7406\u7684\u5440\uff01\u6211\u82e5\u771f\u80fd\u5728\u4f1a\u671f\u4e4b\u524d\uff0c\u5c06\u90a3\u4e11\u4eba\u6e29\u5982\u7389\u9664\u53bb\uff0c\u90a3 \u4e48\u6b64\u573a\u52ab\u96be\uff0c\u4fbf\u5728\u65e0\u5f62\u4e4b\u4e2d\u5316\u66b4\u623e\u4e3a\u7965\u548c\uff0c\u751a\u81f3\u90a3\u6e29\u747e\u2026\u201d \u3000\u3000\u60f3\u5230\u6e29\u747e\uff0c\u4ed6\u4e0d\u7981\u6697\u4e2d\u53f9\u606f\u4e00\u58f0\uff0c\u4e2d\u6b62\u4e86\u81ea\u5df1\u601d\u6f6e\uff0c\u76ee\u5149\u62ac\u5904\uff0c\u53ea\u89c1\u66ae\u8272\u4e4b\u4e2d\uff0c\u5df2\u7136\u4f9d \u7a00\u663e\u51fa\u57ce\u5ed3\u7684\u5f71\u5b50\uff0c\u4ed6\u77e5\u9053\u4e34\u5b89\u5230\u4e86\u3002 \u3000\u3000\u8fdc\u8fdc\u671b\u53bb\uff0c\u4e34\u5b89\u57ce\u91cc\uff0c\u4e07\u5bb6\u706f\u706b\uff0c\u4f9d\u7a00\u53ef\u89c1\uff0c\u8fd9\u5728\u5f53\u65f6\u5c1a\u672a\u5341\u5206\u7e41\u534e\u7684\u5c71\u57ce\uff0c\u6b64\u523b\u5374\u662f \u51a0\u76d6\u4e91\u96c6\uff0c\u7b19\u6b4c\u5f7b\u591c\u4e0d\u7edd\u3002\u751a\u81f3\u767e\u91cc\u4ee5\u5916\u7684\u6d41\u8424\uff0c\u90fd\u98de\u5230\u8fd9\u91cc\u6765\uff0c\u4e54\u8fc1\u624b\u4e2d\u6240\u6301\u7684\u90a3\u4e09\u5e45\u753b \u5377\uff0c\u5728\u6c5f\u6e56\u4e4b\u4e2d\u6380\u8d77\u7684\u98ce\u6d6a\uff0c\u4e0d\u53ef\u8c13\u4e4b\u4e0d\u5927\u4e86\u3002 \u3000\u3000\u5353\u957f\u537f\u5f90\u7136\u8d70\u4eba\u4e34\u5b89\u57ce\uff0c\u53ea\u89c1\u57ce\u5e02\u95f9\u5e02\u4e4b\u4e0a\uff0c\u5bb6\u5bb6\u706f\u706b\u901a\u660e\uff0c\u4e0d\u65f6\u6709\u4e09\u4e94\u52b2\u88c5\u4f69\u5200\u7684\u5f6a \u5f62\u5927\u6c49\uff0c\u628a\u81c2\u9ad8\u6b4c\u800c\u6765\uff0c\u4ece\u9152\u697c\u9ad8\u5904\u98d8\u4e0b\u7684\u547c\u4e94\u559d\u516d\u4e4b\u58f0\uff0c\u66f4\u662f\u65f6\u65f6\u53ef\u95fb\uff0c\u6628\u591c\u7684\u6d41\u8840\u60e8 \u5267\uff0c\u867d\u7136\u4f7f\u5f97\u5c71\u57ce\u4e00\u5ea6\u9677\u4e8e\u6050\u60e7\u4e4b\u4e2d\uff0c\u4f46\u57ce\u4e2d\u7684\u8fd9\u4e9b\u6b66\u6797\u8c6a\u58eb\uff0c\u672c\u662f\u5200\u5934\u8214\u8840\u7684\u670b\u53cb\uff0c\u4ec5\u53ea \u4e00\u591c\uff0c\u4fbf\u751f\u50cf\u662f\u5c06\u90a3\u6d41\u8840\u7684\u666f\u8c61\u5fd8\u5374\u4e86\u3002 \u3000\u3000\u5353\u957f\u537f\u4e0d\u7981\u6697\u4e2d\u53f9\u606f\u4e00\u58f0\uff0c\u5fd6\u9053\uff1a\u201c\u8fd9\u4e9b\u4eba\u4e0d\u8fdc\u5343\u91cc\u800c\u6765\uff0c\u53ea\u671b\u540d\u5251\u7f8e\u4eba\uff0c\u4ff1\u5df2\u5728\u671b\uff0c \u81f3\u4e0d\u6d4e\u4e5f\u53ef\u770b\u4e00\u573a\u70ed\u95f9\uff0c\u5f04\u51e0\u767e\u4e24\u94f6\u5b50\u56de\u53bb\uff0c\u53c8\u6709\u8c01\u77e5\u9053\u81ea\u5df1\u5df2\u5c06\u5927\u7978\u4e34\u5934\u5462\uff1f\u201d \u3000\u3000\u5fc3\u5ff5\u4e00\u8f6c\uff0c\u4fbf\u53c8\u60f3\u5230\u591a\u81c2\u795e\u5251\u4e91\u6c0f\u7236\u5b50\uff0c\u5fd6\u9053\uff1a\u201c\u4e91\u8001\u7237\u5b50\u4ed6\u8001\u4eba\u5bb6\u89c1\u591a\u8bc6\u5e7f\uff0c\u4e0d\u77e5\u9053 \u6709\u6ca1\u6709\u770b\u51fa\u6b64\u4e8b\u7684\u7aef\u502a\u6765\u3002\u201d \u3000\u3000\u4ed6\u867d\u662f\u806a\u660e\u7edd\u9876\u4e4b\u4eba\uff0c\u4f46\u6b64\u523b\u5fc3\u4e2d\u5374\u6709\u7740\u4e00\u79cd\u832b\u7136\u4e0d\u77e5\u6240\u63aa\u7684\u611f\u89c9\uff0c\u5fc3\u91cc\u867d\u7136\u5f88\u60f3\u627e\u90a3 \u8001\u4e8e\u4e16\u6545\u7684\u591a\u81c2\u795e\u5251\u5546\u91cf\u4e00\u4e0b\uff0c\u4f46\u5374\u53c8\u89c9\u5f97\u6b64\u4e2d\u7275\u6d89\uff0c\u6709\u8bb8\u591a\u4e8b\u7adf\u96be\u4ee5\u51fa\u53e3\u3002 \u3000\u3000\u4e00\u65f6\u4e4b\u95f4\uff0c\u4ed6\u5fc3\u4e2d\u601d\u6f6e\u53c8\u81ea\u7ffb\u6d8c\uff0c\u4e0d\u80fd\u81ea\u51b3\uff0c\u6697\u53f9\u4e00\u58f0\uff0c\u53c8\u5fd6\u9053\uff1a\u201c\u65e0\u8bba\u5982\u4f55\uff0c\u6211\u603b\u8be5 \u5148\u627e\u4ed6\u8001\u4eba\u5bb6\u518d\u8bf4\uff0c\u53cd\u6b63\u6b64\u523b\u79bb\u4f1a\u671f\u8fd8\u6709\u51e0\u65e5\u5149\u666f\uff0c\u7a0d\u8fdf\u4e00\u65e5\uff0c\u6211\u518d\u4e0a\u5929\u76ee\u5c71\u53bb\uff0c\u4ea6\u4e0d\u4e3a\u8fdf \u2014\u2014\u201d\u4ed6\u7a81\u5730\u60ca\u8bb6\u5730\u963b\u6b62\u4f4f\u81ea\u5df1\u7684\u601d\u8651\uff0c\u56e0\u4e3a\u4ed6\u81ea\u5bb6\u4ea6\u4e0d\u77e5\u5728\u4ec0\u4e48\u65f6\u5019\uff0c\u4e5f\u81ea\u8ba4\u4e3a\u5982\u8981\u6d88\u53bb \u8fd9\u573a\u52ab\u96be\uff0c\u5c31\u975e\u5f97\u542c\u4ece\u90a3\u9ad8\u51a0\u7fbd\u58eb\u7684\u8bdd\u4e0d\u53ef\uff0c\u4f46\u662f\u4ed6\u5185\u5fc3\u9690\u9690\u7ea6\u7ea6\u4e4b\u95f4\uff0c\u5374\u53c8\u89c9\u5f97\u90a3\u9ad8\u51a0\u7fbd \u58eb\u4e0d\u751a\u53ef\u9760\uff0c\u751a\u81f3\u59d3\u540d\u90fd\u53ef\u80fd\u662f\u5047\u5192\u7684\u3002 \u3000\u3000\u662f\u4ee5\u4ed6\u6b64\u523b\u624d\u89c9\u5f97\u6709\u4e9b\u60ca\u8bb6\uff0c\u60ca\u8bb6\u4e4b\u4e2d\uff0c\u5374\u53c8\u4e0d\u7981\u5fd6\u9053\uff1a\u201c\u6211\u600e\u5730\u5982\u6b64\u7cca\u6d82\uff0c\u65b9\u624d\u7adf\u5fd8 \u4e86\u95ee\u4ed6\u90a3\u4e11\u4eba\u6e29\u5982\u7389\u5e03\u4e0b\u7684\u9677\u8bb2\u7a76\u7adf\u662f\u5728\u4f55\u5904\uff0c\u60f3\u90a3\u5929\u76ee\u5c71\u4e43\u6d77\u5185\u540d\u5c71\u4e4b\u4e00\uff0c\u7ef5\u4e98\u4f55\u6b62\u767e \u91cc\uff0c\u6211\u82e5\u6f2b\u65e0\u76ee\u7684\u5730\u53bb\u4e71\u627e\u4e00\u6c14\uff0c\u53ea\u6015\u627e\u4e2a\u4e94\u5929\u4e5f\u65e0\u6cd5\u627e\u5230\u3002\u201d \u3000\u3000\u53c8\u5fd6\u9053\uff1a\u201c\u5440\uff01\u6211\u751a\u81f3\u8fde\u4e91\u8001\u7237\u5b50\u6b64\u523b\u7a76\u7adf\u843d\u811a\u4f55\u5904\u90fd\u4e0d\u77e5\u9053\u5462\uff1f\u8fd9\u4e34\u5b89\u57ce\u5982\u6b64\u5927\uff0c\u8981 \u60f3\u627e\u4e00\u4e2a\u4eba\u7684\u4e0b\u843d\uff0c\u6015\u4e0d\u6bd4\u90a3\u66f4\u8981\u96be\u4e9b\u3002\u201d \u3000\u3000\u76b1\u7709\u6c89\u541f\uff0c\u6f2b\u6b65\u826f\u4e45\uff0c\u5fc3\u4e2d\u7a81\u53c8\u4e00\u52a8\uff0c\u4e0d\u7981\u6697\u4e2d\u5931\u7b11\u9053\uff1a\u201c\u6211\u600e\u5730\u5982\u6b64\u7b28\u6cd5\uff0c\u60f3\u90a3\u4e91\u8001 \u7237\u5b50\u4e43\u662f\u6b66\u6797\u4e2d\u5927\u5927\u6709\u540d\u4e4b\u4eba\uff0c\u4ed6\u4f4f\u5728\u4ec0\u4e48\u5730\u65b9\uff0c\u6211\u53ea\u8981\u95ee\u95ee\u4eba\uff0c\u60f3\u5fc5\u603b\u4f1a\u6709\u4eba\u77e5\u9053\u7684 \u5427\uff01\u201d \u3000\u3000\u8fd9\u5c11\u5e74\u6b64\u523b\u6b63\u662f\u601d\u6f6e\u767e\u8f6c\uff0c\u7d0a\u4e71\u4e0d\u582a\uff0c\u751a\u81f3\u8fde\u539f\u6709\u7684\u806a\u6167\u90fd\u6d88\u53bb\u51e0\u5206\uff0c\u6b64\u523b\u4e00\u5ff5\u81f3\u6b64\uff0c \u811a\u6b65\u5fae\u987f\uff0c\u65b9\u60f3\u627e\u4e2a\u6b66\u6797\u670b\u53cb\uff0c\u8be2\u95ee\u4e00\u4e0b\u90a3\u591a\u81c2\u5251\u4e91\u6c0f\u7236\u5b50\u7684\u843d\u811a\u4e4b\u5904\u3002 \u3000\u3000\u54ea\u77e5\u2014\u2014 \u3000\u3000\u4ed6\u76ee\u5149\u65b9\u81ea\u4e00\u8f6c\uff0c\u8033\u4e2d\u5374\u542c\u5f97\u4e00\u822c\u5947\u5f02\u7684\u4e50\u58f0\uff0c\u82e5\u6709\u82e5\u65e0\u5730\u4ece\u57ce\u5916\u4f20\u6765\uff0c\u6b64\u523b\u57ce\u4e2d\u867d\u7136 \u55a7\u54d7\uff0c\u4f46\u8fd9\u79cd\u4e50\u58f0\u4e00\u7ecf\u4eba\u8033\uff0c\u5353\u957f\u537f\u6bcb\u5eb8\u4ed4\u7ec6\u51dd\u542c\uff0c\u4fbf\u77e5\u9053\u53c8\u662f\u51fa\u81ea\u4eca\u6668\u6240\u89c1\u90a3\u4e9b\u7ea2\u886b\u5c11\u5973 \u624b\u4e2d\u6240\u6301\u7684\u4f3c\u8427\u975e\u8427\u3001\u4f3c\u7b1b\u975e\u7b1b\u7684\u9752\u7af9\u4e4b\u4e2d\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u4e0d\u7981\u4e3a\u4e4b\u4e00\u60ca\uff0c\u5fd6\u9053\uff1a\u201c\u96be\u9053\u90a3\u4e11\u4eba\u6e29\u5982\u7389\u6b64\u523b\u7adf\u4e5f\u5230\u8fd9\u4e34\u5b89\u57ce\u91cc\u6765\u4e86\uff1f\u201d \u3000\u3000\u5374\u542c\u8fd9\u79cd\u5947\u5f02\u7684\u4e50\u58f0\uff0c\u7531\u8fdc\u800c\u8fd1\uff0c\u8d8a\u6765\u8d8a\u4e3a\u6e05\u6670\uff0c\u4f55\u6d88\u7247\u523b\uff0c\u4e0d\u6b62\u5353\u957f\u537f\u542c\u7684\u6e05\u6e05\u695a \u695a\uff0c\u5c31\u8fde\u90a3\u4e9b\u6b63\u5728\u8857\u5934\u6f2b\u6b65\uff0c\u6216\u662f\u6b63\u5728\u9152\u697c\u70ed\u996e\u7684\u4eba\uff0c\u4e5f\u4ff1\u90fd\u542c\u5230\u8fd9\u79cd\u5947\u5f02\u7684\u4e50\u58f0\u4e86\u3002 \u3000\u3000\u4e8e\u662f\u8def\u4e0a\u7684\u884c\u4eba\uff0c\u4e3a\u4e4b\u9a7b\u8db3\uff0c\u9152\u697c\u4e2d\u7684\u98df\u5ba2\uff0c\u4e5f\u63a2\u51fa\u5934\u6765\uff0c\u867d\u7136\u770b\u6765\u4ff1\u90fd\u6d88\u95f2\u5bfb\u4e50\uff0c\u5176 \u5b9e\u5fc3\u91cc\u53c8\u4f55\u5c1d\u4e0d\u662f\u4eba\u4eba\u6697\u4e2d\u8b66\u6212\u7740\u3002\u8fd9\u4e34\u5b89\u57ce\u6b64\u523b\u6b63\u662f\u591a\u4e8b\u4e4b\u79cb\uff0c\u968f\u65f6\u90fd\u53ef\u80fd\u6709\u7a81\u6765\u7684\u707e \u7978\uff0c\u964d\u4e34\u5728\u5927\u5bb6\u5934\u4e0a\u3002 \u7b2c\u5341\u4e00\u7ae0\u3000\u7389\u5973\u91d1\u5e16>> \u53e4\u9f99\u300a\u6708\u5f02\u661f\u90aa\u300b \u7b2c\u5341\u4e00\u7ae0\u3000\u7389\u5973\u91d1\u5e16 \u3000\u3000\u4e00\u76cf\u7cbe\u81f4\u7684\u94dc\u706f\uff0c\u653e\u5728\u9760\u5899\u7684\u957f\u51e0\u4e0a\uff0c\u67d4\u548c\u7684\u706f\u5149\u5e03\u6ee1\u4e86\u8fd9\u95f4\u5385\u623f\u3002 \u3000\u3000\u5385\u623f\u7684\u540e\u9762\u662f\u4e00\u95f4\u5367\u5ba4\uff0c\u5385\u623f\u548c\u5367\u623f\u90fd\u4e0d\u5927\uff0c\u7136\u800c\u591a\u81c2\u795e\u5251\u80fd\u591f\u627e\u5230\u8fd9\u6837\u7684\u843d\u811a\u4e4b\u5904\uff0c \u5374\u4e5f\u5e76\u975e\u662f\u4ef6\u6613\u4e8b\u3002 \u3000\u3000\u56e0\u4e3a\uff0c\u6b64\u523b\u8fd9\u98ce\u4e91\u9645\u4f1a\u7684\u4e34\u5b89\u57ce\uff0c\u7684\u786e\u662f\u592a\u62e5\u6324\u4e86\uff0c\u4f60\u82e5\u4e0d\u662f\u50cf\u591a\u81c2\u795e\u5251\u4ee5\u53ca\u4e91\u4e2d\u7a0b\u8fd9 \u79cd\u5fb7\u9ad8\u671b\u91cd\u800c\u4e14\u540d\u91cd\u6b66\u6797\u7684\u6c5f\u6e56\u524d\u8f88\uff0c\u53ea\u6015\u8981\u627e\u4e00\u5e2d\u5b89\u8eab\u4e4b\u5730\u90fd\u6781\u4e3a\u56f0\u96be\uff0c\u4f55\u51b5\u662f\u8fd9\u6837\u6709\u5385 \u6709\u5ba4\u7684\u5957\u623f\u3002 \u3000\u3000\u6b64\u523b\uff0c\u591a\u81c2\u795e\u5251\u4e91\u8c26\u6b63\u5750\u5728\u9762\u5bf9\u7740\u7a97\u5b50\u7684\u5de8\u5927\u9760\u6905\u4e0a\uff0c\u7a97\u5916\u662f\u4e00\u4e2a\u5c0f\u5c0f\u7684\u9662\u5b50\uff0c\u4e0d\u65f6\u6709 \u6b22\u7b11\u7684\u58f0\u97f3\uff0c\u4ece\u7a97\u5916\u4f20\u6765\uff0c\u4f7f\u5f97\u90a3\u6c89\u91cd\u7684\u591c\u8272\uff0c\u770b\u6765\u6709\u79cd\u4ee4\u4eba\u5174\u594b\u7684\u5149\u91c7\u3002 \u3000\u3000\u4f46\u662f\uff0c\u8fd9\u66fe\u7ecf\u53f1\u54a4\u4e00\u65f6\u7684\u6b66\u6797\u524d\u8f88\u7684\u9762\u8272\uff0c\u5374\u662f\u5fe7\u90c1\u800c\u6c89\u91cd\u7684\u3002 \u3000\u3000\u5750\u5728\u4ed6\u5bf9\u9762\u7684\u4e91\u4e2d\u7a0b\u89c1\u5230\u4ed6\u7239\u7239\u7684\u795e\u8272\uff0c\u4e0d\u5b89\u5730\u95ee\u9053\uff1a\u7239\u7239\uff0c\u65f6\u5019\u5df2\u7ecf\u4e0d\u65e9\u4e86\uff0c\u4f60\u8001\u4eba \u5bb6\u53ef\u8981\u5230\u5916\u9762\u5403\u4e9b\u4e1c\u897f\uff1f\u201c\u4e91\u8c26\u7f13\u6162\u5730\u6447\u4e86\u6447\u5934\uff0c\u706f\u5149\u7167\u5728\u4ed6\u8138\u4e0a\uff0c\u4f7f\u5f97\u4ed6\u8138\u4e0a\u7684\u76b1\u7eb9\uff0c\u770b \u6765\u6781\u4e3a\u6e05\u6670\uff0c\u4e91\u4e2d\u7a0b\u957f\u957f\u5730\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u53c8\u9053\uff1a\u201c\u957f\u537f\u5f1f\u5e74\u7eaa\u867d\u8f7b\uff0c\u4f46\u662f\u6b66\u529f\u5374\u9ad8\u5f97\u60ca\u4eba\uff0c \u800c\u4e14\u53c8\u6781\u4e3a\u806a\u660e\uff0c\u65e0\u8bba\u5728\u4ec0\u4e48\u60c5\u51b5\u4e0b\uff0c\u90fd\u4e0d\u4f1a\u51fa\u4ec0\u4e48\u5dee\u9519\u7684\uff0c\u4f60\u8001\u4eba\u5bb6\u53c8\u4f55\u5fc5\u62c5\u5fc3\u5462\uff1f\u201d \u3000\u3000\u591a\u81c2\u795e\u5251\u6d53\u7709\u5fae\u76b1\uff0c\u7a81\u53c8\u53f9\u9053\uff1a\u201c\u6211\u62c5\u5fc3\u7684\u5012\u4e0d\u662f\u957f\u537f\uff0c\u800c\u662f\u2014\u2014\u201d\u8bdd\u58f0\u7a81\u5730\u4e00\u987f\uff1a \u201c\u4e2d\u7a0b\uff0c\u4f60\u53ef\u77e5\u9053\u4e54\u8fc1\u8fd9\u4e9b\u65e5\u5b50\u8dd1\u5230\u54ea\u91cc\u53bb\u4e86\uff0c\u6211\u60f3\u95ee\u95ee\u4ed6\u2014\u2014\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u4ed6\u8bdd\u58f0\u7adf\u53c8\u4e00 \u987f\uff0c\u4e91\u4e2d\u7a0b\u4e0d\u7981\u4ea6\u81ea\u4e00\u76b1\u5251\u7709\uff0c\u5947\u602a\u4ed6\u7239\u7239\u4eca\u5929\u8bf4\u8bdd\u600e\u7684\u4f1a\u5982\u6b64\u541e\u5410\uff0c\u54ea\u77e5\u5374\u542c\u4e91\u8c26\u6c89\u58f0\u53f1 \u9053\uff1a\u201c\u4e2d\u7a0b\uff0c\u4f60\u542c\u542c\uff0c\u8fd9\u662f\u4ec0\u4e48\u58f0\u97f3\uff1f\u201d \u3000\u3000\u665a\u98ce\uff0c\u7a7f\u8fc7\u5c0f\u9662\uff0c\u5439\u8fdb\u7a97\u6237\u3002 \u3000\u3000\u90a3\u79cd\u5947\u5f02\u7684\u4e50\u58f0\uff0c\u6b64\u523b\u7adf\u4e5f\u968f\u7740\u665a\u98ce\uff0c\u82e5\u65ad\u82e5\u7eed\u5730\u98d8\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u4e91\u6c0f\u7236\u5b50\u9762\u8272\u90fd\u4e0d\u7981\u4e3a\u4e4b\u5927\u53d8\uff0c\u4e91\u4e2d\u7a0b\u51dd\u795e\u542c\u4e86\u534a\u664c\uff0c\u65b9\u5f85\u7b54\u8bdd\uff0c\u4e91\u8c26\u5374\u53c8\u8bf4\u9053\uff1a\u201c\u8fd9 \u58f0\u97f3\u6211\u50cf\u662f\u66fe\u7ecf\u542c\u8fc7\u2014\u2014\u201d\u7a81\u5730\u4e00\u62cd\u524d\u989d\uff0c\u53c8\u9053\uff1a\u201c\u5bf9\u4e86\uff0c\u662f\u5728\u82d7\u7586\uff0c\u4e09\u5341\u591a\u5e74\u524d\uff0c\u6211\u5c31\u542c \u8fc7\u8fd9\u79cd\u58f0\u97f3\uff0c\u662f\u82d7\u4eba\u7684\u5439\u7af9\u4e4b\u58f0\uff0c\u90a3\u65f6\u2026\u2026\u6211\u5e74\u7eaa\u548c\u4f60\u5dee\u4e0d\u591a\uff0c\u73b0\u5728\u2026\u2026\u201d \u3000\u3000\u81ea\u60b2\u65e5\u66ae\u7684\u8001\u4eba\uff0c\u5e38\u4f1a\u5728\u4e0d\u77e5\u4e0d\u89c9\u4e2d\uff0c\u6d41\u9732\u51fa\u4ed6\u7684\u5fc3\u5883\u6765\u7684\u3002 \u3000\u3000\u4e91\u4e2d\u7a0b\u6123\u4e86\u4e00\u6123\uff0c\u62a2\u6b65\u8d70\u5230\u95e8\u53e3\uff0c\u53c8\u7a81\u7136\u9a7b\u8db3\u56de\u8eab\u8bf4\u9053\uff1a\u201c\u7239\u7239\uff0c\u6211\u5148\u51fa\u53bb\u770b\u770b\uff0c\u4e5f\u8bb8 \u662f\u2014\u2014\u201d\u4ed6\u542b\u84c4\u5730\u4e2d\u6b62\u4e86\u81ea\u5df1\u7684\u8bdd\uff0c\u56e0\u4e3a\u4ed6\u4e0d\u613f\u610f\u8bf4\u51fa\u4e11\u4eba\u6e29\u5982\u7389\u8fd9\u4e2a\u540d\u5b57\u6765\u3002 \u3000\u3000\u4f46\u662f\u4e45\u95ef\u6c5f\u6e56\u7684\u591a\u81c2\u795e\u5251\uff0c\u53c8\u4f55\u5c1d\u6ca1\u6709\u4ece\u8fd9\u5947\u5f02\u7684\u4e50\u58f0\u4e2d\u8054\u60f3\u5230\u8fd9\u4f4d\u4e45\u5c45\u82d7\u7586\u7684\u5973\u9b54\u5934 \u7ea2\u8863\u5a18\u5a18\u6e29\u5982\u7389\u6765\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u4eec\u4e00\u8d77\u8d70\u51fa\u4e86\u5ba2\u6808\u3002 \u3000\u3000\u8857\u9053\u4e0a\uff0c\u706f\u5149\u4f9d\u65e7\uff0c\u884c\u4eba\u4e5f\u4ecd\u7136\u5f88\u591a\uff0c\u4f46\u662f\uff0c\u55a7\u7b11\u58f0\u3001\u9ad8\u6b4c\u58f0\u3001\u8f70\u996e\u58f0\uff0c\u5374\u5168\u90fd\u6ca1\u6709 \u4e86\uff0c\u53ea\u5269\u4e0b\u90a3\u79cd\u5947\u5f02\u7684\u4e50\u58f0\uff0c\u8885\u8885\u5730\u98de\u626c\u7740\u3002 \u3000\u3000\u4ed6\u4eec\u987a\u7740\u8fd9\u4e50\u58f0\u7531\u6765\u7684\u65b9\u5411\uff0c\u5927\u6b65\u8d70\u4e86\u8fc7\u53bb\uff0c\u76f8\u8bc6\u7684\u6b66\u6797\u8c6a\u58eb\u6b64\u523b\u5fc3\u4e2d\u867d\u7136\u60ca\u8be7\u4e0d\u5b9a\uff0c \u4f46\u89c1\u4e86\u4ed6\u4eec\u7236\u5b50\u4ecd\u672a\u5fd8\u4e86\u8eac\u8eab\u4e3a\u793c\u3002 \u3000\u3000\u8f6c\u8fc7\u4e00\u6761\u8def\uff0c\u4e91\u4e2d\u7a0b\u76ee\u5149\u52a8\u5904\uff0c\u7a81\u7136\u89c1\u5230\u90a3\u7ad9\u7acb\u5728\u4eba\u7fa4\u4e4b\u4e2d\uff0c\u6709\u5982\u9e21\u7fa4\u4e4b\u9e64\uff0c\u4e00\u8eab\u7384\u886b \u7684\u5353\u957f\u537f\uff0c\u4e0d\u7981\u8131\u53e3\u9053\uff1a\u201c\u7239\u7239\uff0c\u957f\u537f\u5c31\u5728\u90a3\u91cc\u3002\u201d \u3000\u3000\u76ee\u5149\u9510\u5229\u7684\u5353\u957f\u537f\uff0c\u5374\u6ca1\u6709\u770b\u5230\u4ed6\u4eec\uff0c\u56e0\u4e3a\u4ed6\u6b63\u5728\u5446\u5446\u5730\u60f3\u7740\u5fc3\u4e8b\u3002 \u3000\u3000\u4f46\u662f\u4e91\u4e2d\u7a0b\u7684\u8fd9\u4e00\u558a\uff0c\u5374\u5c06\u4ed6\u4ece\u6c89\u6069\u4e2d\u60ca\u9192\uff0c\u4f46\u662f\u4e0d\u7b49\u4ed6\u8fce\u4e0a\u53bb\uff0c\u591a\u81c2\u795e\u5251\u5df2\u62a2\u6b65\u8d70\u4e86 \u8fc7\u6765\uff0c\u4e00\u628a\u6293\u7740\u4ed6\u7684\u81c2\u8180\uff0c\u5927\u58f0\u9053\uff1a\u201c\u957f\u537f\uff0c\u4f60\u6ca1\u4e8b\u5427\uff1f\u201d \u3000\u3000\u867d\u7136\u662f\u77ed\u77ed\u51e0\u4e2a\u5b57\uff0c\u7136\u800c\u5728\u8fd9\u51e0\u4e2a\u5b57\u91cc\uff0c\u5374\u53c8\u5305\u542b\u7740\u591a\u5c11\u5173\u6000\u4e0e\u60c5\u611f\u3002 \u3000\u3000\u5353\u957f\u537f\u6447\u4e86\u6447\u5934\uff0c\u5450\u5450\u4ed6\u8bf4\u9053\uff1a\u201c\u8001\u6015\uff0c\u4f60\u8001\u4eba\u5bb6\u653e\u5fc3\uff0c\u6211\u2026\u2026\u6211\u6ca1\u4e8b\u3002\u201d \u3000\u3000\u4ed6\u5589\u5934\u54fd\u54bd\u7740\u51e0\u4e4e\u4e0d\u80fd\u5c06\u8fd9\u53e5\u8bdd\u5f88\u5feb\u4ed6\u8bf4\u51fa\u6765\uff0c\u53ea\u89c9\u5f97\u6709\u4e00\uff0c\u79cd\u65e0\u6cd5\u5f62\u5bb9\u7684\u6e29\u60c5\uff0c\u4ece\u8fd9 \u8001\u4eba\u4e00\u53cc\u5bbd\u5927\u7684\u624b\u638c\u4e2d\u4f20\u5230\u4ed6\u8eab\u4e0a\uff0c\u8fd9\u79cd\u6e29\u60c5\uff0c\u6ca1\u6709\u4efb\u4f55\u8a00\u8bed\u80fd\u591f\u5f62\u5bb9\uff0c\u4e5f\u6ca1\u6709\u4efb\u4f55\u4e1c\u897f\u53ef \u4ee5\u66ff\u4ee3\u3002 \u3000\u3000\u4ed6\u611f\u6fc0\u5730\u7b11\u7740\uff0c\u4ed6\u4f38\u51fa\u624b\u63e1\u4f4f\u4e91\u4e2d\u7a0b\u7684\u624b\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u8fd9\u4e09\u4eba\u5f7c\u6b64\u4e4b\u95f4\uff0c\u5404\u90e8\u6709\u4e00\u79cd\u6e29 \u6696\u7684\u611f\u89c9\u5347\u8d77\uff0c\u53cb\u60c5\uff0c\u8fd9\u53c8\u662f\u591a\u4e48\u5947\u5999\u800c\u53ef\u8d35\u7684\u60c5\u64cd\u5440\u3002 \u3000\u3000\u4ed6\u4eec\u5f7c\u6b64\u63e1\u7740\u624b\uff0c\u5446\u5446\u5730\u6123\u4e86\u534a\u664c\uff0c\u8c01\u4e5f\u6ca1\u6709\u8bf4\u8bdd\uff0c\u56db\u4fa7\u7684\u4eba\u4eec\uff0c\u76ee\u5149\u671b\u5728\u4ed6\u4eec\u8eab\u4e0a\uff0c \u4e0d\u7981\u5374\u6709\u70b9\u5947\u602a\uff0c\u8fd9\u4e24\u4e2a\u540d\u91cd\u6b66\u6797\u7684\u6c5f\u6e56\u4fa0\u58eb\uff0c\u6b64\u523b\u600e\u4e48\u4f1a\u505a\u51fa\u6041\u5730\u6a21\u6837\u3002 \u3000\u3000\u4f46\u662f\u2014\u2014 \u3000\u3000\u90a3\u5947\u602a\u7684\u4e50\u58f0\uff0c\u5374\u66f4\u54cd\u4e86\u3002 \u3000\u3000\u4e8e\u662f\u5927\u5bb6\u7684\u76ee\u5149\uff0c\u53c8\u4e0d\u7981\u4ece\u4ed6\u4eec\u8eab\u4e0a\uff0c\u8f6c\u5411\u8fd9\u4e50\u58f0\u7684\u6765\u8def\u3002 \u3000\u3000\u5353\u957f\u537f\u5b9a\u4e86\u5b9a\u795e\uff0c\u8bf4\u9053\uff1a\u201c\u8001\u4f2f\uff0c\u5927\u54e5\uff0c\u8fd9\u58f0\u97f3\u5c31\u662f\u90a3\u4e11\u4eba\u6e29\u5982\u7389\u95e8\u4e0b\u7684\u7ea2\u886b\u5c11\u5973\u4eec\u6240 \u5439\u594f\u51fa\u6765\u7684\uff0c\u770b\u6765\u90a3\u6e29\u5982\u7389\u6b64\u523b\u5df2\u8fdb\u4e86\u4e34\u5b89\u57ce\u3002\u201d \u3000\u3000\u591a\u81c2\u795e\u5251\u4e00\u8f69\u8113\u7709\uff0c\u56de\u987e\u4e91\u4e2d\u7a0b\u4e00\u773c\uff0c\u6c89\u58f0\u8bf4\u9053\uff1a\u201c\u679c\u7136\u662f\u5979\uff01\u201d \u3000\u3000\u53c8\u8f6c\u5411\u5353\u957f\u537f\uff1a\u201c\u957f\u537f\uff0c\u4f60\u662f\u600e\u4e48\u77e5\u9053\u7684\uff1f\u201d \u3000\u3000\u201c\u5353\u957f\u537f\u6c89\u541f\u4e86\u4e00\u4e0b\uff0c\u4e0d\u77e5\u9053\u6b64\u523b\u8be5\u4e0d\u8be5\u5c06\u81ea\u5df1\u8fd9\u4e00\u65e5\u6240\u9047\u8bf4\u51fa\uff1b\u4ed6\u867d\u6bcb\u987b\u9690\u7792\u4e91\u6c0f\u7236 \u5b50\uff0c\u4f46\u5374\u4e0d\u613f\u88ab\u7ad9\u5728\u65c1\u8fb9\u7684\u4eba\u542c\u5230\u3002\u54ea\u77e5\u2014\u2014\u4ed6\u5fc3\u5ff5\u8f6c\u5904\uff0c\u5374\u542c\u5f97\u56db\u4fa7\u7684\u4eba\u7fa4\u7a81\u5730\u53d1\u51fa\u4e00\u9635 \u9a9a\u52a8\uff0c\u7ad9\u5728\u8def\u65c1\u7684\u4eba\uff0c\u6d8c\u5411\u8857\u5fc3\uff0c\u7ad9\u5728\u697c\u4e0b\u7684\u4eba\uff0c\u4e5f\u4f3c\u4e4e\u5954\u4e86\u4e0b\u6765\uff0c\u4ed6\u76ee\u5149\u4e00\u8f6c\uff0c\u4e5f\u4e0d\u7981\u8131 \u53e3\u9053\uff1a\u201c\u6765\u4e86\u3002\u201d \u3000\u3000\u591a\u81c2\u795e\u5251\u4e91\u8c26\u5fc3\u4e2d\u4e0d\u7981\u4e3a\u4e4b\u66ae\u5730\u4e00\u8df3\uff0c\u6570\u5341\u5e74\u6765\uff0c\u7ea2\u8863\u5a18\u5a18\u6e29\u5982\u7389\u4e4b\u540d\uff0c\u5728\u6c5f\u6e56\u4e2d\u4f20\u8a00 \u4e0d\u7edd\uff0c\u4f46\u662f\u5979\u8db3\u8ff9\u4ece\u672a\u79bb\u5f00\u82d7\u7586\u4e00\u6b65\uff0c\u6b64\u523b\u8fd9\u5e74\u5df2\u53e4\u7a00\u7684\u6b66\u6797\u8c6a\u58eb\u4e00\u60f3\u5230\u5979\u5373\u5c06\u5728\u81ea\u5df1\u9762\u524d \u51fa\u73b0\uff0c\u5fc3\u4e2d\u7adf\u4e0d\u7981\u6709\u79cd\u6014\u5fe1\u7684\u611f\u89c9\uff0c\u5fd6\u9053\uff1a\u201c\u96be\u9053\u8fd9\u5973\u9b54\u5934\u6b64\u523b\u771f\u7684\u5230\u6c5f\u5357\u6765\u4e86\uff0c\u800c\u4e14\u5df2\u4eba \u4e86\u4e34\u5b89\u57ce\u3002\u201d \u3000\u3000\u8f6c\u76ee\u671b\u53bb\uff0c\u53ea\u89c1\u8857\u9053\u5c3d\u5934\uff0c\u679c\u7136\u7f13\u7f13\u8d70\u6765\u4e00\u884c\u7ea2\u886b\u5973\u5b50\uff0c\u65b9\u624d\u6d8c\u81f3\u8857\u5fc3\u7684\u4eba\u7fa4\uff0c\u89c1\u5230\u8fd9 \u884c\u5973\u5b50\uff0c\u7adf\u53c8\u9f50\u9000\u5230\u8def\u8fb9\u3002 \u3000\u3000\u8857\u9053\u4e24\u8fb9\u7684\u706f\u5149\uff0c\u5c04\u5230\u8fd9\u884c\u5973\u5b50\u8eab\u4e0a\uff0c\u53ea\u89c1\u5979\u4eec\u4e00\u4e2a\u4e2a\u4ff1\u90fd\u8c8c\u7f8e\u5982\u82b1\uff0c\u80a4\u5982\u83b9\u7389\uff0c\u6ee1\u8eab \u7684\u7ea2\u886b\u88ab\u706f\u5149\u4e00\u6620\uff0c\u66f4\u662f\u660e\u8273\u7167\u4eba\uff0c\u4e0d\u53ef\u65b9\u7269\u3002 \u3000\u3000\u5353\u957f\u537f\u76ee\u5149\u52a8\u5904\uff0c\u4e0d\u7981\u5728\u5fc3\u4e2d\u6697\u9053\u4e00\u58f0\uff1a\u201c\u679c\u7136\u53c8\u662f\u5979\u4eec\uff01\u4f46\u90a3\u4e11\u4eba\u6e29\u5982\u7389\u7684\u9999\u8f66 \u5462\uff1f\u201d \u3000\u3000\u51dd\u76ee\u671b\u53bb\uff0c\u8fd9\u4e9b\u5c11\u5973\u4e91\u9b13\u9ad8\u633d\uff0c\u624b\u6301\u9752\u7af9\uff0c\u4e5f\u4f9d\u7136\u662f\u767d\u5929\u7684\u88c5\u675f\uff0c\u4f46\u662f\u5374\u5728\u6bcf\u4eba\u7684\u5de6 \u65f6\uff0c\u591a\u6302\u4e86\u4e00\u4e2a\u6ee1\u7f00\u7ea2\u82b1\u7684\u6781\u5927\u82b1\u7bee\u3002\u4e24\u4eba\u4e00\u6392\uff0c\u4e95\u80a9\u884c\u6765\uff0c\u8fdc\u8fdc\u671b\u53bb\uff0c\u4eff\u4f5b\u6709\u7740\u516b\u6392\uff0c\u4f46 \u662f\u5979\u4eec\u8eab\u540e\uff0c\u5374\u53ea\u662f\u4e00\u4e9b\u56e0\u597d\u5947\u800c\u8ddf\u5728\u540e\u9762\u7684\u4eba\u4eec\uff0c\u54ea\u91cc\u6709\u90a3\u7ea2\u8863\u5a18\u5a18\u6e29\u5982\u7389\u65e5\u95f4\u6240\u4e58\u7684\u5b9d \u76d6\u9999\u8f66\u7684\u5f71\u5b50\u3002 \u3000\u3000\u591a\u81c2\u795e\u5251\u4e91\u8c26\u51dd\u76ee\u671b\u4e86\u534a\u664c\uff0c\u7a81\u5730\u5fc3\u4e2d\u4e00\u52a8\uff0c\u53c8\u81ea\u56de\u987e\u4e91\u4e2d\u7a0b\u9053\uff1a\u201c\u4e2d\u7a0b\uff0c\u4f60\u770b\u8fd9\u4e9b\u5973 \u5b50\u53ef\u89c9\u773c\u719f\uff1f\u201d \u3000\u3000\u4e91\u4e2d\u7a0b\u989d\u9996\u9053\uff1a\u201c\u8fd9\u73ed\u5c11\u5973\u65e0\u8bba\u88c5\u675f\u6253\u626e\uff0c\u4ee5\u53ca\u4f53\u6001\u795e\u60c5\uff0c\u90fd\u548c\u90a3\u5929\u5230\u6211\u4eec\u5bb6\u91cc\u53bb\u9001\u5bff \u793c\u7684\u5c11\u5973\u6709\u4e9b\u76f8\u4f3c\uff0c\u4f46\u5e74\u9f84\u597d\u50cf\u7a0d\u5fae\u5927\u4e9b\u3002\u201d \u3000\u3000\u4e91\u8c26\u4e00\u634b\u957f\u987b\uff0c\u9053\uff1a\u201c\u662f\u4e86\uff0c\u90a3\u592d\u6211\u5c31\u770b\u51fa\uff0c\u90a3\u73ed\u5973\u5b50\u4e00\u5b9a\u662f\u6e29\u5982\u7389\u7684\u95e8\u4e0b\uff0c\u6b64\u523b\u770b \u6765\uff0c\u4f60\u7239\u7239\u7684\u4f30\u8ba1\uff0c\u4e00\u70b9\u4e5f\u4e0d\u9519\u3002\u201d \u3000\u3000\u8bed\u58f0\u5fae\u987f\u4e00\u4e0b\uff0c\u53c8\u9053\uff1a\u201c\u4f46\u600e\u4e48\u5374\u4e0d\u89c1\u90a3\u7ea2\u8863\u5a18\u5a18\u5462\uff1f\u90a3\u4e48\u8fd9\u73ed\u5973\u5b50\u53c8\u662f\u6765\u505a\u4ec0\u4e48\u7684\uff1f \u54fc\u2014\u2014\u4e00\u4e2a\u4e2a\u624b\u91cc\u8fd8\u63d0\u7740\u82b1\u7bee\uff0c\u96be\u9053\u662f\u6765\u6563\u82b1\u7684\u5417\uff1f\u201d \u3000\u3000\u8fd9\u751f\u5177\u59dc\u6842\u4e4b\u6027\u3001\u8001\u800c\u5f25\u8fa3\u7684\u8001\u4eba\uff0c\u5148\u5934\u51e0\u53e5\u8bdd\uff0c\u662f\u5bf9\u4ed6\u7231\u5b50\u4e91\u4e2d\u7a0b\u7684\uff1b\u540e\u6765\u51e0\u53e5\u8bdd\uff0c \u5374\u662f\u6697\u81ea\u5f97\u610f\u81ea\u5df1\u7684\u8001\u773c\u4e0d\u82b1\uff0c\u4e00\u987f\u4e4b\u540e\u6240\u8bf4\u7684\u8bdd\uff0c\u8fd9\u662f\u5728\u95ee\u5353\u957f\u537f\uff0c\u5230\u6700\u540e\u51e0\u53e5\uff0c\u5374\u662f\u5728 \u81ea\u8a00\u81ea\u8bed\uff0c\u53c8\u662f\u5728\u6697\u4e2d\u9a82\u4eba\u4e86\u3002 \u3000\u3000\u5353\u957f\u537f\u4e3a\u4e4b\u5fae\u5fae\u4e00\u7b11\uff0c\u5fc3\u4e2d\u5374\u4e5f\u6b63\u6697\u95ee\u81ea\u5df1\uff1a\u201c\u4e11\u4eba\u6e29\u5982\u7389\u6ca1\u6709\u6765\uff0c\u90a3\u8fd9\u73ed\u5c11\u5973\u5374\u53c8\u662f \u6765\u505a\u4ec0\u4e48\u5462\uff1f\u201d \u3000\u3000\u8033\u7554\u4e50\u58f0\uff0c\u7a81\u5730\u4e00\u505c\uff0c\u53ea\u89c1\u8fd9\u4e9b\u7ea2\u886b\u5c11\u5973\uff0c\u7adf\u4e5f\u968f\u7740\u4e50\u58f0\uff0c\u4e00\u8d77\u505c\u4f4f\u811a\u6b65\uff0c\u5c06\u624b\u4e2d\u7684\u9752 \u7af9\uff0c\u63d2\u5728\u8170\u95f4\u7684\u7ea2\u8272\u4e1d\u7ee6\u4e0a\u3002 \u3000\u3000\u7ad9\u5728\u8857\u8fb9\u7684\u4eba\u7fa4\uff0c\u51e0\u4e4e\u5df2\u5168\u90fd\u662f\u6b66\u6797\u4e2d\u4eba\uff0c\u56e0\u4e3a\u4e00\u4e9b\u5e73\u5e38\u767e\u59d3\u770b\u5230\u8fd9\u79cd\u9635\u4ed7\uff0c\u867d\u7136\u4e5f\u751f \u51fa\u597d\u5947\u4e4b\u5fc3\uff0c\u4f46\u60f3\u5230\u6628\u591c\u4e4b\u4e8b\uff0c\u53c8\u90fd\u4e0d\u7981\u5fc3\u91cc\u53d1\u6bdb\uff0c\u65e9\u5c31\u4e00\u4e2a\u63a5\u7740\u4e00\u4e2a\u5730\u6e9c\u4e86\u3002 \u3000\u3000\u6b64\u523b\u7fa4\u8c6a\u90fd\u4e0d\u7981\u4e3a\u4e4b\u4e00\u6123\uff0c\u4ed6\u4eec\u77e5\u9053\u7684\u4e8b\uff0c\u8fd8\u8fdc\u4e0d\u53ca\u4e91\u6c0f\u7236\u3001\u53ca\u5353\u957f\u537f\u7684\u591a\uff0c\u81ea\u7136\u66f4\u65e0 \u6cd5\u731c\u6d4b\u8fd9\u4e9b\u7ea2\u8863\u5c11\u5973\u7684\u7528\u610f\uff0c\u5374\u89c1\u5f53\u5934\u800c\u884c\u7684\u4e24\u4e2a\u7ea2\u886b\u5c11\u5973\uff0c\u7adf\u81ea\u5f2f\u4e0b\u8170\u53bb\uff0c\u5411\u4e24\u4fa7\u4eba\u7fa4\u4e00 \u655b\u793c\uff0c\u9f50\u5730\u5a07\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5a62\u5b50\u7b49\u5949\u5bb6\u4e3b\u4e4b\u547d\uff0c\u7279\u6765\u5411\u8bf8\u4f4d\u8bf7\u5b89\uff0c\u5e76\u4e14\u5949\u4e0a\u62dc\u5e16\u8bf7\u8bf8\u4f4d\u8fc7 \u76ee\u3002\u201d \u3000\u3000\u8fd9\u800c\u4eba\u8bf4\u8d77\u8bdd\u6765\uff0c\u7adf\u7136\u5feb\u6162\u4e00\u81f4\uff0c\u4e0d\u5dee\u5206\u5398\uff0c\u800c\u4e14\u5a07\u58f0\u5a49\u8f6c\uff0c\u5a07\u67d4\u6e05\u8106\uff0c\u518d\u914d\u7740\u5979\u4eec\u7684 \u7389\u8c8c\u82b1\u5bb9\uff0c\u5a40\u5a1c\u4f53\u6001\uff0c\u7fa4\u8c6a\u4e0d\u7981\u90fd\u542c\u5f97\u75f4\u4e86\uff0c\u4e5f\u770b\u5f97\u75f4\u4e86\u3002 \u3000\u3000\u591a\u81c2\u795e\u5251\u6d53\u7709\u4e00\u76b1\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u770b\u6765\u7ea2\u8863\u5a18\u5a18\u7684\u786e\u6709\u4e24\u624b\u3002\u4e0d\u8bf4\u522b\u7684\uff0c\u5c31\u770b\u5979\u8bad\u7ec3\u5f92 \u5f1f\uff0c\u7adf\u628a\u4e24\u4e2a\u4eba\u8bf4\u8bdd\u7684\u5feb\u6162\u8282\u8c03\u90fd\u8bad\u7ec3\u5f97\u4e00\u6a21\u4e00\u6837\uff0c\u867d\u662f\u4e24\u4e2a\u4eba\u8bf4\u8bdd\uff0c\u542c\u8d77\u6765\u5374\u50cf\u662f\u4e00\u4e2a\u4eba \u8bf4\u51fa\u6765\u7684\u3002\u201d \u3000\u3000\u4e91\u4e2d\u7a0b\u4ea6\u81ea\u63a5\u53e3\u9053\uff1a\u201c\u90a3\u5929\u53bb\u7ed9\u7239\u7239\u9001\u793c\u7684\uff0c\u4e0d\u662f\u4e5f\u6709\u4e24\u4e2a\u5973\u5b69\u5b50\uff0c\u8bf4\u8d77\u8bdd\u6765\uff0c\u5c31\u50cf\u662f \u4e00\u4e2a\u4eba\u8bf4\u7684\u5417\uff0c\u8d77\u5148\u6211\u8fd8\u4ee5\u4e3a\u5979\u4eec\u662f\u4e00\u6bcd\u53cc\u751f\u5462\uff01\u201d \u3000\u3000\u8bed\u72b9\u672a\u4e86\uff0c\u5374\u89c1\u8fd9\u4e24\u4e2a\u5c11\u5973\u7a81\u5730\u4e00\u62ac\u53cc\u624b\uff0c\u8ddf\u5728\u540e\u9762\u7684\u7ea2\u886b\u5c11\u5973\u7acb\u523b\u56db\u6563\u8d70\u5f00\uff0c\u5353\u957f\u537f \u6697\u4e2d\u4e00\u6570\uff0c\u4e0d\u591a\u4e0d\u5c11\uff0c\u6b63\u597d\u5341\u4e09\u4e2a\u3002 \u3000\u3000\u56db\u4fa7\u7fa4\u8c6a\u672c\u5df2\u76ee\u8ff7\u5fc3\u9189\u7684\u65f6\u5019\uff0c\u6b64\u523b\u89c1\u5230\u8fd9\u4e9b\u5c11\u5973\u7adf\u56db\u6563\u5206\u5f00\uff0c\u5a40\u5a1c\u5730\u8d70\u5230\u81ea\u5df1\u9762\u524d\uff0c \u897f\u4e0a\u4ff1\u90fd\u5e26\u7740\u5a07\u7f8e\u7684\u7b11\u5bb9\uff0c\u66f4\u4e0d\u7981\u90fd\u6123\u4f4f\u4e86\u3002 \u3000\u3000\u5353\u957f\u537f\u653e\u76ee\u4e00\u671b\uff0c\u5374\u89c1\u5f53\u5934\u7684\u4e24\u4e2a\u7ea2\u886b\u5c11\u5973\uff0c\u7adf\u5e76\u80a9\u5411\u81ea\u5df1\u8fd9\u8fb9\u8d70\u4e86\u8fc7\u7c73\uff0c\u79cb\u6ce2\u8f6c\u5904\uff0c \u7a81\u7136\u9f50\u5730\u9732\u9f7f\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u539f\u6765\u4f60\u4e5f\u5728\u8fd9\u91cc\u3002\u201d \u3000\u3000\u2018\u7ea4\u8170\u8f7b\u626d\uff0c\u7b14\u76f4\u5730\u8d70\u5230\u4ed6\u8eab\u524d\u3002\u591a\u81c2\u795e\u5251\u6d53\u7709\u4e00\u76b1\uff0c\u9053\uff1a\u201c\u4f60\u8ba4\u5f97\u5979\u4eec\uff1f\u201d \u3000\u3000\u5353\u957f\u537f\u6115\u4e86\u4e00\u6115\uff0c\u54ea\u77e5\u53f3\u4fa7\u7684\u5c11\u5973\u5374\u5df2\u5a07\u7b11\u9053\uff1a\u201c\u600e\u4e48\u4e0d\u8ba4\u5f97\uff0c\u4eca\u5929\u65e9\u4e0a\uff0c\u6211\u4eec\u8fd8\u89c1\u8fc7 \u9762\u54e9\u3002\u201d \u3000\u3000\u5a07\u7b11\u58f0\u4e2d\uff0c\u7389\u624b\u8f7b\u4f38\uff0c\u4ece\u90a3\u82b1\u7bee\u4e4b\u4e2d\u53d6\u51fa\u4e86\u4e00\u5f20\u7ea2\u8272\u7eb8\u7b3a\uff0c\u9012\u5230\u5353\u957f\u537f\u9762\u524d\uff0c\u79cb\u6ce2\u4e00 \u8f6c\uff0c\u7ea4\u8170\u4e00\u626d\uff0c\u7adf\u81ea\u8f6c\u8eab\u53bb\u4e86\u3002 \u3000\u3000\u5353\u957f\u537f\u5446\u5446\u5730\u4ece\u5979\u90a3\u53cc\u83b9\u767d\u5982\u7389\u7684\u7ea4\u638c\u4e2d\uff0c\u5c06\u90a3\u5f20\u50cf\u662f\u8bf7\u5e16\u6837\u5b50\u7ea2\u8272\u7eb8\u7b3a\u63a5\u4e86\u8fc7\u6765\uff0c\u76ee \u5149\u5782\u5904\uff0c\u53ea\u89c1\u4e0a\u9762\u5199\u7740\u6574\u6574\u9f50\u9f50\u7684\u5b57\u8ff9\uff1a\u201cx\u6708X\u65e5X\u523b\uff0c\u4e34\u5b89\u57ce\u5916\uff0c\u4e00\u51c9\u4ead\u7554\uff0c\u4e13\u4f7f\u63a5\u9a7e\u3002\u201d \u3000\u3000\u5b57\u8ff9\u975e\u884c\u975e\u8349\uff0c\u975e\u96b6\u975e\u7bc6\uff0c\u4ed4\u7ec6\u4e00\u770b\uff0c\u7adf\u5b8c\u5168\u662f\u7528\u91d1\u4e1d\u8d34\u4e0a\u7684\uff0c\u535e\u9762\u4e5f\u6ca1\u6709\u7f72\u540d\uff0c\u5374\u7528 \u91d1\u4e1d\uff0c\u7f20\u4e86\u4e2a\u5c0f\u5c0f\u7684\u201c\u5760\u4e4c\u9afb\u201d\u3002 \u3000\u3000\u8f6c\u773c\u671b\u53bb\uff0c\u90a3\u4e9b\u7ea2\u886b\u5c11\u5973\u4f53\u6001\u82e5\u67f3\uff0c\u8d8a\u884c\u8d8a\u8fdc\uff0c\u7ad9\u5728\u4e24\u4fa7\u7684\u6b66\u6797\u8c6a\u58eb\uff0c\u4e2a\u4e2a\u4ff1\u662f\u76ee\u5b9a\u53e3 \u5446\u5730\u5782\u9996\u800c\u89c6\uff0c\u624b\u4e0a\u4e5f\u90fd\u62ff\u7740\u4e00\u4efd\u8fd9\u79cd\u5962\u4f88\u5df2\u6781\u7684\u8bf7\u5e16\u3002 \u3000\u3000\u8bf7\u5e16\u7f00\u4ee5\u771f\u91d1\uff0c\u8fd9\u6c14\u6d3e\u7684\u786e\u975e\u540c\u5c0f\u53ef\uff0c\u8fd9\u4e9b\u6b66\u6797\u8c6a\u58eb\u867d\u7136\u4ff1\u90fd\u89c1\u8fc7\u4e0d\u77e5\u591a\u5c11\u5927\u573a\u9762\uff0c\u6b64 \u523b\u5fc3\u4e2d\u5374\u4e5f\u4e0d\u7981\u90fd\u6709\u4e9b\u5403\u60ca\u3002 \u3000\u3000\u591a\u81c2\u795e\u5251\u76ee\u5149\u4ea6\u81ea\u51dd\u6ce8\u5728\u624b\u4e0a\u7684\u8bf7\u5e16\u4e0a\uff0c\u4ed4\u7ec6\u770b\u4e86\u534a\u664c\uff0c\u7a81\u7136\u56de\u9996\u95ee\u9053\uff1a\u201c\u957f\u537f\uff0c\u8fd9\u4e00 \u5929\u6765\uff0c\u4f60\u7a76\u7adf\u9047\u7740\u4e86\u4ec0\u4e48\u4e8b\uff0c\u96be\u9053\u4f60\u4eca\u5929\u65e9\u4e0a\u5df2\u7ecf\u89c1\u8fc7\u90a3\u7ea2\u8863\u5a18\u5a18\u4e86\u5417\uff1f\u201d \u3000\u3000\u8fd9\u8001\u4eba\u867d\u7136\u4e5f\u5bf9\u8fd9\u5f20\u8bf7\u5e16\u6709\u4e9b\u5403\u60ca\uff0c\u4f46\u5fc3\u4e2d\u5374\u59cb\u7ec8\u6ca1\u6709\u5fd8\u8bb0\u65b9\u624d\u90a3\u7ea2\u886b\u5c11\u5973\u6240\u8bf4\u7684\u8bdd\uff0c \u6b64\u523b\u4e00\u5c06\u5e16\u4e0a\u5b57\u8ff9\u770b\u6e05\uff0c\u4fbf\u5fcd\u4e0d\u4f4f\u95ee\u4e86\u51fa\u6765\u3002 \u3000\u3000\u5353\u957f\u537f\u8f7b\u53f9\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4eca\u65e5\u5c0f\u4f84\u7684\u786e\u6240\u9047\u9887\u591a\uff0c\u7b49\u7b49\u4e00\u5b9a\u8be6\u7ec6\u7980\u544a\u8001\u4f2f\u2014\u2014\u201d\u8bdd\u58f0\u672a \u4e86\uff0c\u5374\u89c1\u90a3\u4e9b\u7ea2\u886b\u5c11\u5973\u7adf\u53c8\u6392\u6210\u4e94\u5217\uff0c\u5f53\u5934\u7684\u4e24\u4e2a\u5c11\u5973\u53c8\u5a07\u58f0\u8bf4\u9053\uff1a\u201c\u5a62\u4e8e\u4eec\u5306\u5306\u800c\u672a\uff0c\u5306 \u5306\u800c\u53bb\uff0c\u4e34\u5b89\u57ce\u91cc\u7684\u82f1\u96c4\u597d\u6c49\u8fd9\u4e48\u591a\uff0c\u5a62\u5b50\u4eec\u5b9e\u5728\u4e0d\u80fd\u6bcf\u4e2a\u90fd\u901a\u77e5\u5230\uff0c\u56e0\u6b64\u5a62\u5b50\u5012\u5e0c\u671b\u8bf8\u4f4d \u63a5\u5230\u5e16\u5b50\u7684\uff0c\u8f6c\u544a\u6ca1\u6709\u63a5\u5230\u5e16\u5b50\u7684\u82f1\u96c4\u4e00\u4e0b\uff0c\u5c31\u662fX\u6708x\u65e5X\u523b\uff0c\u5a62\u5b50\u4eec\u5728\u57ce\u5916\u7ea6\u4e94\u91cc\u5904\u4e00 \u51c9\u4ead\u90a3\u91cc\uff0c\u606d\u5019\u5404\u4f4d\u7684\u5927\u9a7e\u3002\u201d \u3000\u3000\u8bf4\u7f62\uff0c\u53c8\u81ea\u6df1\u6df1\u655b\u793c\uff0c\u79cb\u6ce2\u590d\u8f6c\uff0c\u518d\u4f38\u624b\u638c\uff0c\u8f7b\u63a9\u6a31\u5507\uff0c\u5a07\u58f0\u4e00\u7b11\u3002\u201c\u5a07\u7b11\u58f0\u4e2d\uff0c\u8fd9\u5341 \u516d\u4e2a\u7ea2\u886b\u5c11\u5973\u7adf\u7136\u4e00\u8d77\u65cb\u626d\u67f3\u8170\uff0c\u8f6c\u8eab\u800c\u53bb\u3002\u56db\u4fa7\u7fa4\u8c6a\uff0c\u671b\u7740\u5979\u4eec\u5a40\u5a1c\u7684\u80cc\u5f71\uff0c\u4f3c\u4e4e\u90fd\u770b\u5f97 \u75f4\u4e86\u3002\u591a\u81c2\u795e\u5251\u5e72\u54b3\u4e86\u4e00\u58f0\u53f9\u9053\uff1a\u201c\u8fd9\u7ea2\u8863\u5a18\u5a18\u5982\u6b64\u7684\u5927\u8d39\u5468\u7ae0\uff0c\u5230\u5e95\u662f\u4ec0\u4e48\u610f\u601d\u5462\uff1f\u96be\u9053 \u771f\u662f\u4e3a\u5f92\u62e9\u5a7f\uff0c\u5bb4\u4f1a\u82f1\u8c6a\u5417\uff1f\u201d \u3000\u3000\u8bed\u58f0\u4e00\u987f\uff0c\u53c8\u9053\uff1a\u201c\u53ea\u4f2f\u672a\u5fc5\u5427\uff01\u201d \u3000\u3000\u7fa4\u8c6a\u4e5f\u5f00\u59cb\u79c1\u4e0b\u7a83\u7a83\u8bae\u8bba\u7740\uff0c\u6839\u672c\u6ca1\u6709\u542c\u5230\u4ed6\u81ea\u8bed\u7740\u7684\u8bdd\uff0c\u6709\u51e0\u4e2a\u7ad9\u5728\u65c1\u8fb9\u51d1\u70ed\u95f9\u7684\u6df7 \u6df7\u513f\uff0c\u9aa4\u7136\u5f97\u7740\u4e0a\u9762\u7f00\u7740\u51e0\u4e4e\u6709\u4e00\u4e24\u591a\u91d1\u5b50\u7684\u8bf7\u5e16\uff0c\u4e50\u5f97\u8fde\u5634\u90fd\u5408\u4e0d\u62e2\u4e86\uff0c\u5927\u7b11\u7740\u8dd1\u4e86\u5f00 \u53bb\u3002 \u3000\u3000\u4e8e\u662f\u57ce\u5357\u5c0f\u5df7\u4e2d\u7684\u571f\u5a3c\u9986\u91cc\uff0c\u4eca\u591c\u4fbf\u591a\u4e86\u51e0\u4e2a\u8c6a\u5ba2\uff0c\u5e26\u7740\u60e8\u767d\u9762\u8272\u7684\u5993\u5973\u4eec\uff0c\u867d\u7136\u8fd9\u4e9b \u5e73\u65e5\u53ea\u4f1a\u624b\u5fc3\u671d\u4e0a\u7684\u6df7\u6df7\u513f\uff0c\u4eca\u65e5\u600e\u5730\u90fd\u53d8\u6210\u4e86\u5927\u7237\uff0c\u53ef\u662f\u5979\u4eec\u4e5f\u4e0d\u6562\u95ee\uff0c\u4e5f\u4e0d\u613f\u95ee\uff0c\u53ea\u662f \u5f3a\u989c\u6b22\u7b11\u7740\uff0c\u4e00\u9762\u53c8\u5077\u5077\u7528\u624b\u5e15\u62ed\u62b9\u7740\u9762\u988a\uff0c\u751f\u6015\u81ea\u5df1\u9762\u4e0a\u643d\u7740\u7684\u5927\u539a\u7684\u8102\u7c89\uff0c\u90fd\u56e0\u8fd9\u4e00\u7b11 \u800c\u9707\u843d\u4e0b\u6765\u3002 \u3000\u3000\u5927\u79e4\u5206\u94f6\u3001\u5c0f\u79e4\u5206\u91d1\u7684\u6b66\u6797\u8c6a\u58eb\uff0c\u867d\u7136\u6ca1\u6709\u5c06\u8fd9\u4e24\u4e2a\u91d1\u5b50\u770b\u5728\u773c\u91cc\uff0c\u4f46\u6b64\u523b\u4ea6\u4e0d\u7981\u5728\u5fc3 \u4e2d\u6697\u559c\uff1a\u201c\u5475\uff0c\u597d\u5927\u7684\u624b\u9762\uff0c\u5230\u4e86\u5929\u76ee\u5c71\u4e0a\uff0c\u6015\u4e0d\u6709\u6210\u5806\u7684\u91d1\u5b50\u5806\u5728\u5c71\u4e0a\u3002\u201d \u3000\u3000\u4e8e\u662f\u4ed6\u4eec\u66f4\u575a\u5b9a\u4e86\u4e0a\u5929\u76ee\u5c71\u7684\u51b3\u5fc3\uff0c\u4e16\u4e0a\u5927\u591a\u6570\u7684\u51b3\u5fc3\uff0c\u4e0d\u90fd\u662f\u5efa\u7acb\u5728\u4eae\u6676\u6676\u7684\u91d1\u94f6\u4e0a \u9762\u7684\u5417\uff1f\uff01 \u3000\u3000\u5a40\u5a1c\u7684\u7ea2\u8272\u8eab\u5f71\uff0c\u9010\u6e10\u53bb\u7684\u8fdc\u4e86\uff0c\u4f46\u7fa4\u8c6a\u7684\u76ee\u5149\u5374\u81ea\u7136\u8ffd\u968f\u7740\u5979\u4eec\uff0c\u53ea\u6709\u591a\u81c2\u795e\u5251\u4e91\u6c0f \u7236\u4e8e\u7684\u76ee\u5149\uff0c\u5374\u51dd\u6ce8\u5728\u5353\u957f\u537f\u8eab\u4e0a\u3002 \u3000\u3000\u800c\u5353\u957f\u537f\u5462\uff1f \u3000\u3000\u4ed6\u6b64\u523b\u6b63\u5782\u7740\u5934\uff0c\u843d\u5165\u6c89\u601d\u91cc\uff0c\u8c01\u4e5f\u4e0d\u77e5\u9053\u4ed6\u5fc3\u91cc\u5728\u60f3\u7740\u4ec0\u4e48\uff0c\u591a\u81c2\u795e\u5251\u867d\u7136\u60f3\u95ee\u4ed6\uff0c \u4f46\u770b\u5230\u4ed6\u7684\u6837\u5b50\uff0c\u4f3c\u4e4e\u5728\u51b3\u5b9a\u7740\u4e00\u4ef6\u91cd\u5927\u7684\u4e8b\uff0c\u4f46\u4e5f\u52c9\u5f3a\u5fcd\u7740\u5fc3\u91cc\u7684\u8bdd\uff0c\u5e0c\u671b\u4ed6\u5feb\u4e9b\u60f3\u5b8c\u3002 \u3000\u3000\u55a7\u54d7\u4e4b\u58f0\uff0c\u53c8\u5f00\u59cb\u54cd\u4e86\u8d77\u6765\u2014\u2014\u4e09\u4e2a\u8eab\u7a7f\u957f\u886b\uff0c\u811a\u4e0b\u5374\u8e6c\u7740\u5feb\u9774\uff0c\u88c5\u675f\u867d\u9887\u4e3a\u65af\u6587\uff0c\u6b65 \u5c65\u5374\u6781\u4e3a\u527d\u608d\u77eb\u5065\u7684\u6c49\u5b50\uff0c\u4ece\u8857\u7684\u5bf9\u9762\u8d70\u4e86\u8fc7\u6765\uff0c\u8d70\u5230\u4e91\u6c0f\u7236\u5b50\u8eab\u524d\uff0c\u4e0d\u7ea6\u800c\u540c\u5730\u606d\u8eab\u4e00 \u63d6\uff0c\u9f50\u58f0\u9053\uff1a\u201c\u4e91\u8001\u7237\u5b50\uff0c\u8fd9\u4e00\u5411\u60a8\u8001\u4eba\u5bb6\u53ef\u597d\uff1f\u201d \u3000\u3000\u591a\u81c2\u795e\u5251\u5fc3\u4e2d\u867d\u6709\u5fc3\u6574\u987f\uff0c\u4f46\u4e00\u89c1\u8fd9\u51e0\u4eba\u4e4b\u9762\uff0c\u4ea6\u4e0d\u7981\u4e3a\u4e4b\u5c55\u989c\u7b11\u9053\uff1a\u201c\u6211\u5f53\u662f\u8c01\uff0c\u539f \u6765\u662f\u77f3\u8001\u7237\u5b50\u7684\u9ad8\u8db3\u3002\u201d \u3000\u3000\u56de\u5934\u5411\u4e91\u4e2d\u7a0b\u7b11\u9053\uff1a\u201c\u4e2d\u7a0b\uff0c\u5feb\u8fc7\u6765\u89c1\u89c1\uff0c\u8fd9\u51e0\u4f4d\u5c31\u662f\u6211\u5e38\u8ddf\u4f60\u8bf4\u8d77\u7684\uff0c\u5317\u4eac\u57ce\u91cc\u9996\u5c48 \u4e00\u6307\u7684\u71d5\u6b66\u9556\u5c40\u77f3\u8001\u7237\u5b50\u7684\u95e8\u4e0b\uff0c\u5341\u5e74\u4e0d\u89c1\uff0c\u60f3\u4e0d\u5230\u5404\u4f4d\u90fd\u5982\u6b64\u82f1\u4fca\u4e86\uff0c\u77f3\u8001\u7237\u5b50\u4e45\u672a\u51fa \u4eac\uff0c\u8fd9\u4e00\u5411\u53ef\u597d\uff1f\u201d \u3000\u3000\u8fd9\u4e09\u6761\u6c49\u5b50\u9762\u4e0a\u4e00\u8d77\u9732\u51fa\u9eef\u7136\u4e4b\u8272\uff0c\u5782\u9996\u6c89\u58f0\u9053\uff1a\u201c\u5bb6\u5e08\u4ed6\u8001\u4eba\u5bb6\u5df2\u4e8e\u4e09\u5e74\u524d\u53bb\u4e16 \u4e86\u3002\u201d\u591a\u81c2\u795e\u5251\u53cc\u7709\u4e00\u76b1\uff0c\u53d8\u8272\u9053\uff1a\u201c\u771f\u7684\uff0c\u5509\u2014\u2014\u60f3\u4e0d\u5230\u5306\u5306\u6570\u5e74\uff0c\u6211\u8f88\u5144\u5f1f\uff0c\u7adf\u53c8\u5c11\u53bb \u4e00\u4e2a\uff0c\u5509\u4e00\u8001\u6210\u51cb\u96f6\uff0c\u6614\u65e5\u82f1\u96c4\uff0c\u4eca\u591a\u6545\u53bb\uff0c\u96be\u602a\u6c5f\u6e56\u4e0a\u98ce\u6ce2\u65e5\u76ca\u589e\u591a\u4e86\u3002\u201d \u3000\u3000\u9aa4\u89c1\u6545\u4eba\uff0c\u4e4d\u95fb\u5669\u8017\uff0c\u8fd9\u4ea6\u4f7f\u81ea\u60b2\u4e24\u9b13\u5df2\u6591\u3001\u5e74\u534e\u4e0d\u518d\u7684\u6b66\u6797\u8c6a\u5ba2\uff0c\u4e0d\u7981\u4e3a\u4e4b\u800c\u9eef\u7136\u795e \u4f24\uff0c\u557c\u5618\u4e0d\u5df2\uff0c\u4e91\u4e2d\u7a0b\u5728\u65c1\u8fb9\u89c1\u7740\u4ed6\u7239\u7239\u7684\u795e\u6001\uff0c\u5fc3\u91cc\u4f55\u5c1d\u4e0d\u77e5\u9053\u4ed6\u7239\u7239\u5fc3\u4e2d\u7684\u611f\u6168\uff0c\u4ea6\u81ea \u5782\u9996\u4e0d\u8bed\u3002 \u3000\u3000\u826f\u4e45\u826f\u4e45\u3002 \u3000\u3000\u591a\u81c2\u795e\u5251\u65b9\u81ea\u7f13\u7f13\u62ac\u8d77\u5934\u6765\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u8d24\u4f84\u4eec\u6b64\u6b21\u79bb\u4eac\u5357\u4e0b\uff0c\u53ef\u4e5f\u662f\u4e3a\u8fd9\u5929\u76ee\u4e4b \u4f1a\uff1f\u201d \u3000\u3000\u4e09\u6761\u6c49\u5b50\u4e00\u8d77\u9894\u9996\u79f0\u662f\uff0c\u4e91\u8c26\u5fae\u5fae\u4e00\u7b11\uff0c\u65e5\u5149\u8f6c\u5904\uff0c\u7a81\u5730\u9762\u8272\u4e00\u53d8\uff0c\u5927\u559d\u9053\uff1a\u201c\u957f\u537f \u5462\uff1f\u201d\u4e91\u4e2d\u7a0b\u5fc3\u5934\u4e00\u8df3\uff0c\u8f6c\u76ee\u671b\u53bb\uff0c\u53ea\u89c1\u6ee1\u8857\u4e4b\u4e0a\uff0c\u4eba\u58f0\u55a7\u6742\uff0c\u6518\u5f80\u7199\u6765\uff0c\u800c\u4e00\u76f4\u5c31\u7ad9\u5728\u81ea \u5df1\u8eab\u4fa7\u7684\u5353\u957f\u537f\uff0c\u5c31\u5728\u8fd9\u591a\u81c2\u795e\u5251\u548c\u6545\u4eba\u95e8\u4e0b\u5bd2\u6684\u6570\u8bed\u7684\u65f6\u5019\uff0c\u5df2\u7ecf\u4e0d\u77e5\u8d70\u5230\u54ea\u91cc\u53bb\u4e86\u3002 \u3000\u3000\u591a\u81c2\u795e\u5251\u957f\u7709\u76b1\u5904\uff0c\u4e00\u4e2a\u7bad\u6b65\u7a9c\u5230\u8857\u5fc3\uff0c\u987f\u8db3\u53f9\u9053\uff1a\u201c\u957f\u537f\u8fd9\u5b69\u5b50\uff0c\u8fd9\u7a76\u7adf\u662f\u600e\u4e48 \u4e86\uff1f\u201d \u3000\u3000\u64a9\u8d77\u957f\u886b\uff0c\u62d4\u8db3\u800c\u5954\uff0c\u9894\u4e0b\u7684\u957f\u9aef\uff0c\u4e0d\u4f4f\u6296\u52a8\uff0c\u4f46\u76f4\u5230\u8857\u7684\u5c3d\u5934\uff0c\u5374\u4ecd\u770b\u4e0d\u5230\u5353\u957f\u537f\u7684 \u5f71\u5b50\u3002 \u3000\u3000\u4e91\u4e2d\u7a0b\u5fc3\u4e2d\u4e5f\u81ea\u5947\u602a\uff1a\u201c\u957f\u537f\u5f1f\u600e\u5730\u505a\u4e8b\u5982\u6b64\u614c\u5f20\uff0c\u8d70\u4e86\u7adf\u90fd\u4e0d\u62db\u547c\u4e00\u58f0\u3002\u201d \u3000\u3000\u5fc3\u5ff5\u4e00\u8f6c\uff1a\u201c\u4ed6\u5e74\u7eaa\u661f\u8f7b\uff0c\u6027\u60c5\u5374\u6781\u6c89\u7a33\uff0c\u5982\u6b64\u505a\u6cd5\uff0c\u83ab\u975e\u662f\u53c8\u53d1\u73b0\u4e86\u4ec0\u4e48\u65b0\u7684\u4e8b \u6545\u3002\u201d \u3000\u3000\u968f\u7740\u4ed6\u7239\u7239\u8d70\u4e86\u4e24\u6b65\uff0c\u811a\u6b65\u7a81\u53c8\u4e00\u987f\uff0c\u56de\u5934\u5411\u90a3\u4e09\u6761\u6c49\u5b50\u6b49\u7136\u4e00\u7b11\uff0c\u8fd8\u672a\u8bf4\u8bdd\uff0c\u8fd9\u4e9b\u6c49 \u5b50\u5df2\u81ea\u62b1\u62f3\u9053\uff1a\u201c\u4e91\u5c11\u4fa0\u5982\u82e5\u6709\u4e8b\uff0c\u53ea\u7ba1\u8bf7\u4fbf\uff0c\u6211\u5f1f\u5144\u65e2\u7136\u77e5\u9053\u4e91\u5c11\u4fa0\u843d\u811a\u5904\uff0c\u660e\u65e5\u5c11\u4e0d\u5f97 \u8fd8\u8981\u62dc\u5019\u3002\u201d \u3000\u3000\u8fd9\u4e09\u6761\u6c49\u5b50\u4ea6\u662f\u4e45\u8d70\u6c5f\u6e56\u7684\u7cbe\u5e72\u89d2\u8272\uff0c\u89c1\u4e86\u4e91\u6c0f\u7236\u5b50\u7684\u795e\u6001\uff0c\u77e5\u9053\u5fc5\u662f\u8981\u4e8b\uff0c\u957f\u63d6\u5230 \u5730\uff0c\u4e5f\u57c2\u81ea\u544a\u8f9e\uff0c\u53ea\u662f\u4e91\u6c0f\u7236\u5b50\u5728\u8fd9\u4e34\u5b89\u57ce\u91cc\u7684\u5927\u5c0f\u8857\u9053\u90fd\u627e\u4e86\u4e00\u904d\uff0c\u5374\u8fd8\u662f\u6ca1\u6709\u627e\u5230\u5353\u957f \u537f\u7684\u884c\u8e2a\u3002 \u3000\u3000\u90a3\u4e48\uff0c\u65b9\u81ea\u4eba\u57ce\u7684\u5353\u957f\u537f\uff0c\u6b64\u523b\u4e3a\u4f55\u7a81\u53c8\u4e0d\u8f9e\u800c\u522b\uff0c\u4ed6\u8dd1\u5230\u54ea\u91cc\u53bb\u4e86\u5462\uff1f \u3000\u3000\u539f\u6765\u65b9\u624d\u5353\u957f\u537f\u671b\u7740\u90a3\u4e9b\u7ea2\u88f3\u5c11\u5973\u7684\u80cc\u5f71\uff0c\u4fef\u9996\u6c89\u541f\u534a\u664c\uff0c\u5fd6\u9053\uff1a\u201c\u90a3\u4e11\u4eba\u6e29\u5982\u7389\u8bbe\u4e0b \u7684\u79cd\u79cd\u9677\u9631\uff0c\u6211\u53ea\u77e5\u9053\u5728\u5929\u76ee\u5c71\u4e2d\uff0c\u5374\u4e0d\u77e5\u9053\u7a76\u7adf\u5728\u4ec0\u4e48\u5730\u65b9\uff0c\u5982\u679c\u6211\u8981\u7b49\u5230\u90a3\u4f1a\u671f\u4e4b\u65e5\u518d \u53bb\uff0c\u5c82\u975e\u592a\u8fdf\u3002\u201d \u3000\u3000\u4e00\u5ff5\u81f3\u6b64\uff0c\u4ed6\u5fc3\u4e2d\u4fbf\u65ad\u7136\u505a\u4e86\u4e2a\u51b3\u5b9a\uff1a\u201c\u8fd9\u4e9b\u5c11\u5973\u6b64\u523b\u60f3\u5fc5\u4e00\u5b9a\u4f1a\u56de\u5230\u6e29\u5982\u7389\u85cf\u8eab\u4e4b \u5904\uff0c\u6211\u4e0d\u5982\u6697\u4e2d\u8ddf\u5728\u5979\u4eec\u8eab\u540e\uff0c\u5bfb\u7740\u90a3\u4e2a\u5730\u65b9\uff0c\u5c06\u6b64\u4e8b\u65e9\u4e9b\u505a\u4e2a\u4e86\u65ad\u3002\u201d \u3000\u3000\u62ac\u76ee\u671b\u53bb\uff0c\u53ea\u89c1\u7ea2\u88f3\u5c11\u5973\u8d8a\u884c\u8d8a\u8fdc\uff0c\u5a40\u5a1c\u7684\u8eab\u5f62\u5df2\u5c06\u6d88\u5931\u5728\u8857\u7684\u5c3d\u5934\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u6beb\u4e0d\u8003\u8651\u5730\u4e00\u63a0\u886b\u811a\uff0c\u500f\u7136\u81ea\u6f2b\u6b65\u8857\u5fc3\u7684\u4eba\u7fa4\u4e2d\u7a7f\u8fc7\uff0c\u5c31\u50cf\u662f\u4e00\u53e3\u5288\u6c34\u7684\u94a2\u5200\uff0c \u7b14\u76f4\u5730\u5288\u5f00\u6d77\u6d6a\u4f3c\u7684\u3002 \u3000\u3000\u7b49\u5230\u88ab\u4ed6\u575a\u5982\u7cbe\u94a2\u7684\u624b\u81c2\u5206\u5f00\u7684\u4eba\u7fa4\u6115\u7136\u76f8\u987e\u7684\u65f6\u5019\u3002 \u3000\u3000\u4ed6\u5df2\u8d70\u5f00\u5f88\u8fdc\uff0c\u8d70\u5230\u57ce\u811a\uff0c\u4eba\u8ff9\u6e10\u5c11\uff0c\u4ed6\u4fbf\u5fae\u4e00\u8e0f\u6b65\uff0c\u500f\u7136\u7a7f\u51fa\u3002 \u3000\u3000\u57ce\u5916\u591c\u8272\u6df1\u6df1\uff0c\u5c31\u53ea\u8fd9\u4e00\u57ce\u4e4b\u9694\uff0c\u5374\u50cf\u662f\u622a\u7136\u4e0d\u540c\u7684\u4e24\u4e2a\u4e16\u754c\uff0c\u57ce\u5185\u706f\u706b\u901a\u660e\uff0c\u7b19\u6b4c\u5904 \u5904\uff0c\u5929\u65f6\u4eff\u4f5b\u4ecd\u7136\u751a\u65e9\uff0c\u57ce\u5916\u5374\u50cf\u662f\u591c\u5df2\u5f88\u6df1\u4e86\u3002 \u3000\u3000\u4ed6\u6df1\u957f\u5730\u5438\u4e86\u53e3\u6c14\uff0c\u8f6c\u65e5\u56db\u671b\uff0c\u8fdc\u5904\u6797\u6728\u6447\u66f3\uff0c\u8fd1\u5904\u4e71\u8349\u8d77\u4f0f\uff0c\u56db\u4e0b\u6e3a\u65e0\u4eba\u8ff9\uff0c\u90a3\u4e9b\u7ea2 \u88f3\u5c11\u5973\u660e\u660e\u662f\u7531\u6b64\u5904\u51fa\u57ce\uff0c\u4f46\u6b64\u523b\u5374\u6839\u672c\u4e0d\u77e5\u8d70\u5230\u4f55\u5904\uff0c\u53ea\u6709\u5fae\u98ce\u4e2d\u9690\u9690\u4f20\u6765\u4e00\u9635\u9635\u8f9a\u8f9a\u8f66 \u58f0\uff0c\u9010\u6e10\u8fdc\u53bb\u3002 \u3000\u3000\u5fae\u4e00\u9a7b\u8db3\uff0c\u4ed6\u4fbf\u6beb\u4e0d\u8003\u8651\u5730\u671d\u8fd9\u8f66\u58f0\u4f20\u6765\u7684\u65b9\u5411\u5982\u98de\u63a0\u53bb\u3002 \u3000\u3000\u591c\u8272\u4e4b\u4e2d\uff0c\u4ed6\u8eab\u5f62\u6709\u5982\u4e00\u6761\u6781\u6de1\u7684\u8f7b\u70df\uff0c\u4e00\u4e2a\u8fdf\u5f52\u7684\u4e1d\u8d29\uff0c\u53ea\u89c9\u773c\u524d\u4e00\u82b1\uff0c\u5fae\u98ce\u62c2\u9762\uff0c \u4f46\u4ece\u4ed6\u8eab\u4fa7\u63a0\u8fc7\u7684\u7a76\u7adf\u662f\u4ec0\u4e48\uff0c\u4ed6\u5374\u672a\u770b\u6e05\u695a\u3002 \u3000\u3000\u76cf\u8336\u4e4b\u95f4\uff0c\u5353\u957f\u537f\u5df2\u671b\u89c1\u524d\u9762\u8f66\u9a6c\u7684\u5f71\u5b50\uff0c\u4ed6\u8eab\u5f62\u51e0\u4e4e\u6ca1\u6709\u4efb\u4f55\u52a8\u4f5c\uff0c\u98de\u63a0\u4e4b\u52bf\uff0c\u4fbf\u53c8 \u52a0\u5feb\u513f\u8bb8\uff0c\u7728\u76ee\u95f4\u524d\u9762\u7684\u8f66\u9a6c\u8ddd\u79bb\u4ed6\u53ea\u6709\u5341\u6570\u4e08\u8fdc\u8fd1\uff0c\u751a\u81f3\u8fde\u9ad8\u9ad8\u5750\u5728\u9a6c\u524d\u5ea7\u7684\u5fa1\u8f66\u9a6c\u592b\u7684 \u8eab\u5f62\u8f6e\u5ed3\uff0c\u4ed6\u90fd\u80fd\u6781\u4e3a\u6e05\u695a\u5730\u770b\u5230\u3002 \u3000\u3000\u90a3\u662f\u4e24\u8f86\u9ed1\u6f06\u5d2d\u4eae\u7684\u9a6c\u725f\uff0c\u6f06\u5149\u5982\u955c\uff0c\u51e0\u53ef\u6620\u4eba\uff0c\u524d\u9762\u9a7e\u8f66\u7684\u56db\u5339\u9a8f\u9a6c\uff0c\u633d\u5957\u751a\u4e30\uff0c\u4e00 \u773c\u671b\u53bb\uff0c\u4e0d\u4f46\u9a6c\u9a8f\u5982\u9f99\uff0c\u8f66\u53a2\u4e5f\u6781\u4e3a\u534e\u4e3d\u3002 \u3000\u3000\u8f66\u7a97\u4e2d\u706f\u5149\u660f\u9ec4\uff0c\u4eba\u5f71\u9690\u7ea6\u53ef\u89c1\uff0c\u800c\u4e14\u4e0d\u65f6\u6709\u5a07\u7b11\u8bed\u58f0\uff0c\u5939\u5728\u8f9a\u8f9a\u8f66\u58f0\u4e4b\u4e2d\uff0c\u968f\u98ce\u4f20 \u6765\uff0c\u58f0\u97f3\u867d\u4e0d\u751a\u663e\uff0c\u4f46\u4ee5\u5353\u957f\u537f\u7684\u8033\u529b\uff0c\u542c\u5f97\u5374\u5df2\u6781\u4e3a\u6e05\u6670\u3002 \u3000\u3000\u4ed6\u5251\u7709\u5fae\u5c55\uff0c\u77e5\u9053\u81ea\u5df1\u8ffd\u9010\u7684\u53e3\u6807\uff0c\u5e76\u672a\u5f04\u9519\uff0c\u53cc\u81c2\u4e00\u957f\uff0c\u9880\u957f\u7684\u8eab\u5f62\uff0c\u84e6\u7136\u51b2\u5929\u800c \u8d77\uff0c\u51cc\u7a7a\u5fae\u4e00\u8f6c\u6298\uff0c\u4fbf\u98d8\u7136\u843d\u5728\u8f66\u540e\uff0c\u7ade\u65e0\u58f0\u65e0\u606f\u5730\u4f9d\u9644\u5728\u9a6c\u8f66\u4e0a\uff0c\u5c31\u50cf\u662f\u4e00\u7247\u843d\u53f6\u4f3c\u7684\uff0c \u83ab\u8bf4\u8f66\u5185\u5750\u7740\u7684\u4ec5\u662f\u4e9b\u5c11\u5973\uff0c\u4fbf\u662f\u7edd\u9876\u9ad8\u624b\uff0c\u53ea\u6015\u4e5f\u4e0d\u4f1a\u6709\u4e1d\u6beb\u611f\u89c9\uff0c\u653e\u773c\u5929\u4e0b\uff0c\u83bd\u83bd\u6c5f\u6e56 \u4e4b\u4e2d\uff0c\u5c31\u6050\u8fd9\u4efd\u8f7b\u529f\uff0c\u5df2\u8db3\u4ee5\u7738\u817a\u4e00\u65f6\u4e86\u3002 \u3000\u3000\u8f66\u9a6c\u4f9d\u65e7\u5411\u524d\u98de\u5954\uff0c\u8f66\u540e\u626c\u8d77\u4e00\u4e32\u7070\u9ec4\u7684\u5c18\u571f\uff0c\u4ed6\u5251\u7709\u5fae\u76b1\uff0c\u65b9\u5f85\u62c2\u8896\uff0c\u5374\u53c8\u5fcd\u4f4f\uff0c\u4e3a \u7740\u8fd9\u8bb8\u591a\u6b66\u6797\u8c6a\u58eb\u7684\u751f\u6b7b\uff0c\u4e3a\u7740\u81ea\u5df1\u4e0d\u5171\u6234\u5929\u7684\u6df1\u4ec7\uff0c\u5403\u4e9b\u7070\u5c18\uff0c\u53c8\u7b97\u5f97\u4ec0\u4e48\uff1f\uff01 \u3000\u3000\u9053\u4e0a\u7802\u77f3\u9887\u591a\uff0c\u5982\u6b64\u6025\u884c\u7684\u8f66\u9a6c\uff0c\u81ea\u7136\u98a0\u7c38\u5df2\u6781\uff0c\u4f46\u662f\u4ed6\u53ea\u8f7b\u8f7b\u7528\u624b\u638c\u8d34\u5728\u8f66\u53a2\u4e0a\uff0c\u5c31 \u662f\u518d\u5927\u7684\u98a0\u7c38\uff0c\u4fbf\u4e5f\u4e0d\u4f1a\u8dcc\u4e0b\uff0c\u8fd9\u9664\u4e86\u8f7b\u529f\u9020\u8be3\u4e4b\u5916\uff0c\u82e5\u6ca1\u6709\u6df1\u539a\u7684\u5185\u529b\uff0c\u4e5f\u662f\u65e0\u6cd5\u505a\u5230 \u7684\u3002 \u3000\u3000\u84e6\u5730\uff0c\u8f66\u53a2\u4e2d\u53c8\u8d77\u4e86\u4e00\u9635\u54c4\u7b11\uff0c\u4e00\u4e2a\u5a07\u67d4\u7684\u8bed\u58f0\uff0c\u4eff\u4f5b\u5728\u5e26\u7740\u7b11\u9053\uff1a\u201c\u4f60\u8bf4\u597d\u4e0d\u597d\u7b11\uff0c \u5c31\u51ed\u4ed6\u90a3\u526f\u5634\u8138\uff0c\u5c45\u7136\u5c31\u6253\u8d77\u5c0f\u59d0\u7684\u4e3b\u610f\u6765\u4e86\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u5fc3\u4e2d\u4e00\u52a8\uff0c\u4ed6\u867d\u4e0d\u60f3\u53bb\u542c\u8fd9\u4e9b\u5c0f\u5973\u5b50\u7684\u7b11\u95f9\uff0c\u4f46\u6b64\u65f6\u6b64\u523b\u6b64\u5730\uff0c\u4ed6\u5373\u4f7f\u4e0d\u60f3\u542c\uff0c \u5374\u4e5f\u65e0\u6cd5\u505a\u5230\uff0c\u4f55\u51b5\u8fd9\u7b11\u8bed\u58f0\u4e2d\u6240\u8bf4\u7684\u201c\u5c0f\u59d0\u201d\uff0c\u4ed6\u81ea\u7136\u77e5\u9053\u662f\u8c01\uff0c\u4e5f\u4e0d\u7981\u4e3a\u4e4b\u6697\u4e2d\u5fc3\u52a8\u3002 \u3000\u3000\u53ea\u542c\u53e6\u4e00\u4e2a\u58f0\u97f3\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u8fd9\u6b21\u7956\u59d1\u8bf7\u6765\u7684\u90a3\u6279\u4eba\uff0c\u867d\u7136\u4e00\u4e2a\u4e2a\u6ca1\u6709\u4e00\u4f4d\u957f\u7684\u50cf\u4eba\uff0c \u4f46\u5374\u90fd\u6709\u4e9b\u6c14\u6d3e\uff0c\u8c01\u4e5f\u6ca1\u6709\u8fd9\u5bb6\u4f19\u8fd9\u4e48\u8ba8\u538c\uff0c\u53ef\u662f\u2014\u2014\u563f\u563f\uff0c\u5374\u504f\u504f\u662f\u4ed6\u8981\u52a8\u6b6a\u5ff5\u5934\uff0c\u4e5f\u96be \u602a\u5c0f\u59d0\u8981\u628a\u4ed6\u9f3b\u5b50\u524a\u6389\u4e86\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u7709\u5934\u4e00\u76b1\uff0c\u6697\u9053\uff1a\u201c\u597d\u8fa3\u7684\u624b\u6bb5\u3002\u201d \u3000\u3000\u4f46\u5fc3\u4e2d\u5374\u53c8\u4e0d\u514d\u6697\u6697\u9ad8\u5174\uff0c\u9ad8\u5174\u7684\u4ec0\u4e48\uff0c\u4ed6\u81ea\u5df1\u4e5f\u65e0\u6cd5\u89e3\u91ca\u2014\u2014\u4e5f\u8bb8\u4ec5\u662f\u4e0d\u613f\u6765\u89e3\u91ca\u800c \u5df2\u3002\u5374\u53c8\u542c\u53e6\u4e00\u4e2a\u58f0\u97f3\u7b11\u9053\uff1a\u201c\u4f60\u522b\u8bf4\u4ed6\u96be\u770b\u8ba8\u538c\uff0c\u542c\u8bf4\u4ed6\u4e8c\u5341\u5e74\u524d\uff0c\u5374\u4e5f\u662f\u58f0\u540d\u8d6b\u8d6b\u7684\u4eba \u7269\uff0c\u6211\u4eec\u5e74\u7eaa\u8fd8\u8f7b\uff0c\u81ea\u7136\u4e0d\u4f1a\u77e5\u9053\u8fd9\u82b1\u90ce\u6bd5\u4e94\u7684\u540d\u5b57\uff0c\u53ef\u662f\u5728\u4e8c\u5341\u5e74\u524d\u5440\uff0c\u90a3\u53ef\u4e0d\u540c\u4e86\uff0c\u4e0d \u8bf4\u522b\u7684\uff0c\u4f60\u5c31\u770b\u4ed6\u90a3\u5929\u521a\u4e0a\u5c71\u65f6\u9732\u7684\u90a3\u624b\u51cc\u6ce2\u5341\u516b\u8f6c\u7684\u8f7b\u529f\uff0c\u563f\uff0c\u8fd9\u6b21\u5e78\u4e8f\u662f\u5c0f\u59d0\uff0c\u82e5\u8981\u662f \u6362\u4e86\u522b\u4eba\u7684\u8bdd\uff0c\u53ea\u6015\u2026\u2026\u53ea\u6015\u2026\u2026\u201d \u3000\u3000\u5979\u8fb9\u8bf4\u8fb9\u7b11\uff0c\u8bf4\u5230\u540e\u6765\uff0c\u5df2\u7b11\u5f97\u8bf4\u4e0d\u4e0b\u53bb\u4e86\uff0c\u53e6\u4e00\u4e2a\u58f0\u97f3\u7acb\u523b\u5403\u5403\u7b11\u9053\uff1a\u201c\u8981\u662f\u6362\u4e86\u4f60 \u7684\u8bdd\uff0c\u53ea\u6015\u4f60\u5c31\u8981\u88ab\u4ed6\u5265\u6210\u50cf\u53ea\u7f8a\u4f3c\u5730\u4e22\u5230\u5e8a\u4e0a\u4e86\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u9762\u988a\u4e00\u7ea2\uff0c\u53ea\u542c\u5f97\u8f66\u53a2\u5185\u7b11\u58f0\u5403\u5403\u4e0d\u7edd\uff0c\u5939\u6742\u7740\u5148\u524d\u8bf4\u8bdd\u90a3\u5973\u5b50\u7684\u5a07\u55d4\u7b11\u9a82\u58f0\uff1a \u201c\u4f60\u518d\u8bf4\uff0c\u518d\u8bf4\u770b\u6211\u4e0d\u6495\u4e86\u4f60\u7684\u5634\u3002\u201d \u3000\u3000\u4e00\u9635\u8f7b\u52a8\uff0c\u53e6\u4e00\u4eba\u4fbf\u53c8\u7b11\u9053\uff1a\u201c\u4f60\u5440\u2026\u2026\u4f60\u8fd9\u4e2a\u5c0f\u6d6a\u8e44\u5b50\u3002\u6211\u5c31\u77e5\u9053\u4f60\u6625\u5fc3\u52a8\u4e86\u2014\u2014\u4f60 \u4eec\u770b\uff0c\u5979\u5148\u524d\u89c1\u5230\u90a3\u4e2a\u7a7f\u9ed1\u8863\u670d\u7684\u9ad8\u4e2a\u5b50\uff0c\u5c31\u7b49\u4e0d\u53ca\u5730\u8dd1\u8fc7\u53bb\uff0c\u628a\u5e16\u5b50\u4ea4\u7ed9\u4eba\u5bb6\uff0c\u7adf\u8fd8\u539a\u7740 \u8138\u76ae\u53bb\u8ddf\u4eba\u5bb6\u8bf4\u2014\u2014\u54ce\u54df\uff0c\u4f60\u518d\u6765\uff0c\u6211\u504f\u8981\u8bf4\uff0c\u8bf4\u4f60\u770b\u4e2d\u4e86\u4eba\u5bb6\uff0c\u53ef\u662f\u4eba\u5bb6\u770b\u4e0d\u4e2d\u4f60\uff0c\u6240\u4ee5 \u5c31\u8fde\u82b1\u90ce\u6bd5\u4e94\u4e5f\u662f\u597d\u7684\u4e86\uff0c\u53ef\u662f\u5440\uff0c\u8fde\u6bd5\u4e94\u90fd\u770b\u4e0d\u4e0a\u4f60\u3002\u201d \u3000\u3000\u5979\u8fb9\u8bf4\u8fb9\u5598\u8fb9\u8bf4\uff0c\u5353\u957f\u537f\u5374\u53c8\u4e0d\u7981\u9762\u988a\u4e00\u7ea2\uff0c\u77e5\u9053\u8fd9\u5c11\u5973\u53e3\u4e2d\u201c\u7a7f\u9ed1\u8863\u670d\u7684\u9ad8\u4e2a\u5b50\u201d\uff0c \u5c31\u662f\u8bf4\u7684\u81ea\u5df1\uff0c\u5fc3\u4e2d\u53c8\u662f\u597d\u6c14\u53c8\u662f\u597d\u7b11\uff0c\u5374\u53c8\u6709\u4e00\u79cd\u6de1\u6de1\u7684\u6b23\u559c\uff0c\u5e74\u8f7b\u7684\u7537\u5b50\u5728\u542c\u5230\u4e00\u4e2a\u5c11 \u5973\u5938\u5956\u81ea\u5df1\u7684\u65f6\u5019\uff0c\u6709\u8c01\u5fc3\u91cc\u4f1a\u6ca1\u6709\u8fd9\u79cd\u611f\u89c9\uff1f\uff01 \u3000\u3000\u88ab\u8baa\u7b11\u7684\u5973\u5b69\u5b50\u663e\u7136\u662f\u6709\u4e9b\u607c\u5dee\u7f9e\u6210\u6012\u4e86\uff0c\u5927\u58f0\u53eb\u7740\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u597d\uff0c\u4f60\u4ee5\u4e3a\u6211\u4e0d\u77e5\u4f60 \u7684\u4e8b\uff0c\u5582\uff0c\u4f60\u4eec\u77e5\u4e0d\u77e5\u9053\u5979\u770b\u4e0a\u4e86\u8c01\uff1f\u5979\u770b\u4e0a\u7684\u5c31\u662f\u88ab\u90a3\u4e2a\u7956\u59d1\u6349\u56de\u53bb\uff0c\u5173\u5728\u5c71\u6d1e\u91cc\u90a3\u4e2a\u7a7f \u9ec4\u8863\u670d\u7684\u5c0f\u4f19\u5b50\uff0c\u90a3\u591c\u6211\u4eec\u628a\u8fd9\u5c0f\u4f19\u5b50\u56f0\u5728\u9713\u88f3\u4ed9\u821e\u9635\u91cc\u7684\u65f6\u5019\uff0c\u5979\u5c31\u770b\u4e0a\u4e86\u4ed6\uff0c\u6240\u4ee5\u624b\u4e0b \u5c31\u7279\u522b\u7559\u4e86\u70b9\u60c5\u2014\u2014\u201d\u5979\u60c5\u72b9\u672a\u7adf\uff0c\u8bdd\u58f0\u5374\u500f\u7136\u800c\u987f\uff0c\u4f3c\u4e4e\u5728\u60f3\u8be5\u518d\u7528\u4ec0\u4e48\u8bdd\u6765\u62a5\u590d\u3002 \u3000\u3000\u5353\u957f\u537f\u5374\u5fc3\u4e2d\u4e00\u52a8\uff0c\u5fd6\u9053\uff1a\u201c\u539f\u6765\u90a3\u9ec4\u886b\u5c11\u5e74\u5df2\u88ab\u6e29\u5982\u7389\u56da\u7981\u8d77\u6765\u3002\u201d \u3000\u3000\u53c8\u5fd6\u9053\uff1a\u201c\u8fd9\u9ec4\u886b\u5c11\u5e74\u7684\u5e08\u7236\u4e07\u5999\u771f\u541b\u4e0e\u6e29\u5982\u7389\u672c\u662f\u4e00\u9f3b\u5b54\u51fa\u6c14\u7684\u4eba\uff0c\u6e29\u5982\u7389\u5374\u53c8\u600e\u4f1a \u5982\u6b64\u5bf9\u5f85\u4e8e\u4ed6\uff0c\u8fd9\u5012\u7684\u786e\u6709\u4e9b\u5947\u602a\u4e86\u3002\u201d \u3000\u3000\u4ed6\u5fc3\u5ff5\u72b9\u672a\u8f6c\u5b8c\uff0c\u5374\u542c\u53e6\u4e00\u4e2a\u8f83\u4e3a\u7a33\u91cd\u4e9b\u7684\u8bed\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4e2a\u771f\u662f\u7684\uff0c\u8d70\u5230\u54ea\u91cc\u90fd \u8981\u6597\u53e3\uff0c\u771f\u662f\u592a\u6076\u52a3\u4e86\uff0c\u6211\u7b80\u76f4\u4ece\u672a\u6ca1\u6709\u770b\u89c1\u8fc7\u6bd4\u4f60\u4eec\u518d\u6076\u52a3\u7684\u4eba\uff0c\u518d\u5435\uff0c\u518d\u5435\u6211\u5c31\u8981\u4e8e\u662f \u4e24\u4e2a\u5a07\u67d4\u7684\u58f0\u97f3\u4fbf\u540c\u65f6\u54cd\u8d77\uff1a\u201c\u597d\u5927\u59ca\uff0c\u4e0d\u8981\u544a\u6211\u4eec\uff0c\u6211\u4eec\u4e0b\u6b21\u518d\u4e5f\u4e0d\u6562\u6597\u53e3\u4e86\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u867d\u7136\u751f\u6027\u521a\u76f4\uff0c\u521a\u6b63\u4e0d\u963f\uff0c\u4f46\u542c\u4e86\u8fd9\u4e9b\u5c11\u5973\u7684\u5a07\u55d4\u7b11\u95f9\uff0c\u5fc3\u91cc\u5374\u4e5f\u4e0d\u7981\u4e3a\u4e4b\u6697 \u7b11\uff0c\u4e00\u9762\u5374\u53c8\u4e0d\u7981\u6697\u4e2d\u611f\u6168\uff1a\u201c\u8fd9\u4e9b\u5c11\u5973\u672c\u6765\u90fd\u6781\u4e3a\u5929\u771f\uff0c\u53ea\u53ef\u60dc\u5374\u90fd\u88ab\u90a3\u5973\u9b54\u5934\u641c\u7f57\u4e86 \u53bb\uff0c\u5509\u2014\u2014\u5979\u4eec\u82e5\u662f\u77e5\u9053\uff0c\u65b9\u624d\u7531\u5979\u4eec\u81ea\u5df1\u624b\u4e2d\u9001\u51fa\u53bb\u7684\u8bf7\u5e16\uff0c\u5374\u65e0\u5f02\u662f\u522b\u4eba\u7684\u50ac\u547d\u4e4b\u7b26\uff0c \u5fc3\u4e2d\u53c8\u8be5\u5982\u4f55\u60f3\u6cd5\u5462\uff1f\u201d \u3000\u3000\u4e00\u9635\u6025\u8fd9\u7684\u8f6c\u5f2f\uff0c\u513f\u58f0\u5065\u9a6c\u7684\u957f\u5636\uff0c\u4e00\u9635\u76ae\u97ad\u7684\u547c\u5578\u3002 \u3000\u3000\u4ed6\u7684\u601d\u8def\u4e0d\u7981\u4e3a\u4e4b\u4e2d\u65ad\u4e00\u4e0b\uff0c\u5374\u542c\u90a3\u58f0\u97f3\u8f83\u4e3a\u7a33\u91cd\u7684\u5c11\u5973\u53c8\u81ea\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u77e5\u4e0d\u77e5\u9053\uff0c \u6211\u5fc3\u91cc\u4e5f\u6709\u4ef6\u5947\u602a\u7684\u4e8b\u2014\u2014\uff0c\u201d\u5979\u8bf4\u5230\u4e00\u534a\uff0c\u8bed\u58f0\u7adf\u7a81\u7136\u4e2d\u65ad\uff0c\u4f3c\u4e4e\u662f\u7a81\u7136\u60f3\u8d77\u81ea\u5df1\u4e0d\u8be5\u5c06 \u8fd9\u53e5\u8bdd\u8bf4\u51fa\u6765\u4f3c\u7684\uff0c\u53e6\u51e0\u4e2a\u5c11\u5973\u7acb\u523b\u4e03\u5634\u516b\u820c\u7684\u5a07\u55d4\u9053\uff0c\u201c\u5927\u59d0\u771f\u662f\u2014\u2014\u603b\u662f\u8fd9\u6837\uff0c\u8bdd\u8bf4\u5230 \u4e00\u534a\u5c31\u4e0d\u8bf4\u4e86\uff0c\u4f60\u77e5\u4e0d\u77e5\u9053\u4eba\u5bb6\u5fc3\u91cc\u591a\u96be\u53d7\u5440\u3002\u201d \u3000\u3000\u8fd9\u201c\u5927\u59ca\u201d\u4f3c\u4e4e\u88ab\u903c\u5f97\u6ca1\u6709\u529e\u6cd5\u4e86\uff0c\u8fde\u8fde\u9053\uff1a\u201c\u6211\u7684\u597d\u59d1\u5a18\uff0c\u4f60\u4eec\u522b\u5435\u597d\u4e0d\u597d\uff0c\u6211\u544a\u8bc9 \u4f60\u4eec\uff0c\u6211\u5fc3\u91cc\u5947\u602a\u7684\u5c31\u662f\u2014\u2014\u201d\u5979\u8bed\u58f0\u7adf\u53c8\u4e00\u987f\uff0c\u5353\u957f\u537f\u4e5f\u4e0d\u7981\u5728\u5fc3\u4e2d\u6697\u81ea\u5fd6\u9053\uff1a\u201c\u8fd9\u5973\u5b50 \u8bf4\u8bdd\u600e\u5730\u5982\u6b64\u541e\u5410\uff01\u201d \u3000\u3000\u4ed6\u5fc3\u4e2d\u4e5f\u4e0d\u7981\u6709\u4e9b\u597d\u5947\uff0c\u60f3\u542c\u542c\u8fd9\u5c11\u5973\u5fc3\u4e2d\u5947\u602a\u7684\u7a76\u7adf\u662f\u4ec0\u4e48\u3002 \u3000\u3000\u5374\u542c\u5979\u8bed\u58f0\u5fae\u987f\u4e4b\u540e\uff0c\u50cf\u662f\u4e5f\u6015\u90a3\u4e9b\u5c11\u5973\u518d\u5435\uff0c\u4fbf\u7acb\u523b\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u77e5\u4e0d\u77e5\u9053\uff0c\u90a3 \u59d3\u5c91\u7684\u9ec4\u886b\u5c11\u5e74\u662f\u8c01\u7684\u5f92\u5f1f\uff1f\u201d \u3000\u3000\u5148\u524d\u90a3\u5c11\u5973\u4fbf\u53c8\u5403\u5403\u7b11\u9053\uff1a\u201c\u8fd9\u4e2a\u6211\u4eec\u600e\u4f1a\u77e5\u9053\uff0c\u5927\u59ca\u8981\u95ee\u95ee\u5979\u5440\uff0c\u5979\u53ef\u662f\u4e00\u5b9a\u77e5\u9053 \u7684\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u6697\u4e2d\u4e00\u7b11\uff0c\u5fd6\u9053\uff1a\u201c\u8fd9\u5c11\u5973\u770b\u6765\u771f\u662f\u987d\u76ae\uff0c\u65b9\u624d\u8bf4\u4e0d\u6597\u53e3\uff0c\u6b64\u523b\u5374\u53c8\u6597\u8d77\u53e3 \u6765\u3002\u201d \u3000\u3000\u90a3\u201c\u5927\u59ca\u201d\u679c\u7136\u6c89\u58f0\u9053\uff1a\u201c\u6211\u8bf4\u4f60\u6076\u52a3\uff0c\u4f60\u679c\u7136\u6076\u52a3\uff0c\u73b0\u5728\u4eba\u5bb6\u8bf4\u6b63\u7ecf\u8bdd\uff0c\u4f60\u5373\u53c8\u8bf4\u8fd9 \u79cd\u6076\u52a3\u7684\u8bdd\u6765\uff0c\u544a\u8bc9\u4f60\uff0c\u4f60\u8981\u662f\u518d\u6076\u52a3\uff0c\u6211\u5c31\u4e0d\u8bf4\u4e86\u3002\u201d \u3000\u3000\u5979\u4e00\u53e5\u8bdd\u4e2d\u7adf\u4e00\u8fde\u8bf4\u4e86\u56db\u6b21\u201c\u6076\u52a3\u201d\uff0c\u5353\u957f\u537f\u51e0\u4e4e\u5fcd\u4e0d\u4f4f\u8981\u7b11\u51fa\u58f0\u6765\uff0c\u5fc3\u9053\uff1a\u201c\u666e\u65e0\u4e4b \u4e0b\uff0c\u53ea\u6015\u518d\u4e5f\u627e\u4e0d\u5230\u4e00\u4e2a\u6bd4\u5979\u66f4\u559c\u6b22\u7528\u2018\u6076\u52a3\u2019\u4e24\u5b57\u7684\u4e86\u3002\u201d \u3000\u3000\u672c\u5df2\u98a0\u7c38\u7684\u9a6c\u8f66\uff0c\u6b64\u523b\u66f4\u52a0\u98a0\u7c38\u8d77\u6765\uff0c\u4ed4\u7ec6\u4e00\u542c\uff0c\u8f66\u5185\u50cf\u662f\u53c8\u751f\u9a9a\u52a8\uff0c\u9a9a\u52a8\u4e2d\u5939\u6742\u7740\u90a3 \u5c11\u5973\u7684\u5403\u5403\u7b11\u58f0\uff0c\u6c42\u9976\u9053\uff1a\u201c\u597d\u5927\u59ca\uff0c\u4f60\u5feb\u8bf4\u5427\uff0c\u6211\u518d\u4e5f\u4e0d\u8bf4\u6076\u52a3\u7684\u8bdd\u4e86\u3002\u201d \u3000\u3000\u5979\u7adf\u4e5f\u53d7\u4e86\u4f20\u67d3\uff0c\u4e5f\u8bf4\u8d77\u201c\u6076\u52a3\u201d\u4e24\u5b57\u6765\u4e86\u3002 \u3000\u3000\u53ea\u542c\u8fd9\u201c\u5927\u59ca\u201d\u4f3c\u4e5f\u5fcd\u4e0d\u4f4f\u201c\u6251\u54e7\u201d\u4e00\u7b11\uff0c\u542b\u7b11\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u8bb0\u4e0d\u8bb0\u5f97\uff0c\u8bb8\u591a\u5e74\u4ee5\u524d\uff0c \u4f60\u4eec\u8fd8\u5f88\u5c0f\u7684\u65f6\u5019\uff0c\u6709\u4e00\u4e2a\u4e2a\u5b50\u9ad8\u9ad8\uff0c\u5e74\u7eaa\u5f88\u5927\uff0c\u4f46\u770b\u6765\u5374\u4e0d\u751a\u8001\u7684\u9053\u4eba\u4e0a\u5c71\u6765\u627e\u7956\u59d1 \u59d1\u3002\u201d\u5353\u957f\u537f\u5fc3\u4e2d\u4e00\u52a8\uff1a\u201c\u5979\u8bf4\u7684\u83ab\u975e\u662f\u4e07\u5999\u771f\u541b\u5c39\u51e1\uff1f\u201d \u3000\u3000\u4e00\u5ff5\u81f3\u6b64\uff0c\u4ed6\u542c\u7684\u4fbf\u66f4\u7559\u795e\uff0c\u8f66\u53a2\u5185\u4f4e\u8bed\u58f0\u53c8\u8d77\uff0c\u6709\u7684\u8bf4\uff1a\u201c\u5fd8\u8bb0\u4e86\u3002\u201d \u3000\u3000\u6709\u7684\u5374\u8bf4\uff1a\u201c\u662f\u6709\u8fd9\u4e48\u4e00\u4e2a\u4eba\u3002\u201d \u3000\u3000\u4f46\u8bed\u58f0\u4e4b\u4e2d\uff0c\u5927\u5bb6\u90fd\u4f3c\u5728\u5947\u602a\uff0c\u8fd9\u9053\u4eba\u548c\u201c\u5927\u59ca\u201d\u5fc3\u4e2d\u5947\u70fd\u7684\u4e8b\u53c8\u6709\u4ec0\u4e48\u5173\u7cfb\uff0c\u5374\u542c \u201c\u5927\u59ca\u201d\u53c8\u9053\uff1a\u201c\u90a3\u65f6\u5019\u6211\u5e74\u7eaa\u6bd4\u4f60\u4eec\u5927\u4e24\u5c81\uff0c\u6240\u4ee5\u8bb0\u5f97\u975e\u5e38\u6e05\u695a\uff0c\u8fd9\u4e2a\u9053\u4eba\u4e0a\u5c71\u4e4b\u540e\uff0c\u6211 \u5c31\u5947\u602a\uff0c\u4ed6\u80c6\u5b50\u597d\u5927\uff0c\u5c45\u7136\u6562\u627e\u7956\u59d1\u59d1\uff0c\u96be\u9053\u4ed6\u4e0d\u77e5\u9053\u7956\u59d1\u59d1\u6700\u8ba8\u538c\u7537\u4eba\uff0c\u4f46\u770b\u5230\u4ed6\u7684\u6837\u5b50 \u53c8\u548c\u6c14\uff0c\u8bf4\u8d77\u8bdd\u6765\u53c8\u597d\u542c\uff0c\u5c31\u628a\u4ed6\u5e26\u5230\u7956\u59d1\u59d1\u7684\u623f\u91cc\u3002\u201d \u3000\u3000\u5979\u8bed\u58f0\u7a0d\u6b47\uff0c\u4f3c\u4e4e\u5728\u56de\u5fc6\u7740\u5f53\u65f6\u7684\u60c5\u666f\uff0c\u65b9\u81ea\u7f13\u7f13\u63a5\u9053\uff1a\u201c\u7956\u59d1\u59d1\u4e00\u89c1\u4e86\u4ed6\uff0c\u679c\u7136\u73b0\u51fa \u6781\u4e3a\u8ba8\u538c\u7684\u6837\u5b50\uff0c\u6211\u4e0d\u6562\u8fdb\u53bb\uff0c\u5374\u53c8\u820d\u4e0d\u5f97\u8d70\uff0c\u5c31\u7ad9\u5728\u623f\u95e8\u5916\u9762\uff0c\u60f3\u5077\u5077\u5730\u542c\u4e00\u4e0b\u3002\u201d \u3000\u3000\u90a3\u7b11\u58f0\u5403\u5403\u7684\u58f0\u97f3\uff0c\u4e00\u542c\u8fd9\u8bdd\uff0c\u4fbf\u53c8\u7acb\u523b\u62a2\u7740\u9053\uff1a\u201c\u597d\uff0c\u539f\u6765\u5927\u59ca\u4e5f\u4e0d\u89c4\u77e9\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u6b63\u81ea\u51dd\u795e\u800c\u542c\uff0c\u7a81\u7136\u542c\u5230\u8fd9\u53e5\u8bdd\uff0c\u4e0d\u7981\u6697\u4e2d\u7b11\u9a82\uff1a\u201c\u8fd9\u5973\u5b50\u679c\u7136\u6076\u52a3\u3002\u201d \u3000\u3000\u54ea\u77e5\u8fd9\u6b21\u201c\u5927\u59ca\u201d\u7adf\u50cf\u662f\u6ca1\u6709\u542c\u5230\u5979\u7684\u8bdd\u4f3c\u7684\uff0c\u5140\u81ea\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u6211\u53ea\u542c\u5f97\u7956\u59d1\u59d1\u5389\u58f0 \u559d\u95ee\u4ed6\uff1a\u2018\u8dd1\u6765\u5e72\u4ec0\u4e48\uff1f\u2019\u4ed6\u56de\u7b54\u7684\u58f0\u97f3\u5374\u5f88\u5c0f\uff0c\u5c0f\u5f97\u6211\u6839\u672c\u542c\u4e0d\u89c1\uff0c\u7956\u59d1\u59d1\u8bf4\u8bdd\u7684\u58f0\u97f3\u5374 \u50cf\u662f\u5f88\u6124\u6012\u7684\u6837\u5b50\uff0c\u53eb\u4ed6\u8d76\u5feb\u2018\u6eda\u51fa\u53bb\uff01\u2019\u6211\u7ad9\u5728\u5916\u9762\uff0c\u7b49\u4e86\u8bb8\u4e45\uff0c\u5374\u8fd8\u6ca1\u6709\u770b\u5230\u4ed6\u51fa\u6765\uff0c \u5fc3\u4e2d\u4e0d\u7981\u53c8\u4e3a\u4ed6\u62c5\u5fc3\uff0c\u96be\u9053\u4ed6\u5df2\u88ab\u7956\u59d1\u59d1\u6740\u4e86\u3002\u201c\u8f66\u53a2\u4e2d\u7684\u5a07\u7b11\u58f0\uff0c\u6b64\u523b\u5df2\u5168\u90e8\u5f52\u4e8e\u5bc2\u9759\uff0c \u663e\u89c1\u5f97\u8fd9\u4e9b\u987d\u76ae\u7684\u5c11\u5973\u4e5f\u88ab\u8fd9\u201d\u5927\u59ca\u201c\u6240\u8bf4\u7684\u8bdd\u6240\u6df1\u6df1\u5438\u5f15\uff0c\u5353\u957f\u537f\u66f4\u662f\u542c\u5f97\u6026\u7136\u5fc3\u52a8\uff0c\u56e0 \u4e3a\u5979\u8bf4\u7684\u8bdd\uff0c\u65e0\u7591\u5730\u53c8\u662f\u4e00\u4ef6\u6781\u5927\u7684\u79d8\u5bc6\uff0c\u800c\u8fd9\u79d8\u5bc6\u5374\u53c8\u662f\u4e0e\u81ea\u5df1\u4e24\u4e2a\u4e0d\u5171\u6234\u5929\u7684\u4ec7\u4eba\u6709\u5173 \u7684\u3002\u53ea\u542c\u201d\u5927\u59ca\u201c\u63a5\u7740\u53c8\u9053\uff1a\u201c\u90a3\u65f6\u5019\u5c0f\u59d0\u5728\u540e\u5c71\uff0c\u4f60\u4eec\u4e5f\u90fd\u4e0d\u77e5\u9053\u8dd1\u5230\u54ea\u91cc\u53bb\u73a9\u53bb\u4e86\uff0c\u7956 \u59d1\u59d1\u7684\u623f\u95f4\u9644\u8fd1\uff0c\u5c31\u53ea\u5269\u4e0b\u6211\u4e00\u4e2a\u4eba\uff0c\u6211\u7ad9\u5728\u5916\u9762\uff0c\u53ea\u542c\u5f97\u79df\u59d1\u59d1\u5728\u623f\u91cc\u672c\u6765\u4e0d\u65ad\u5730\u5927\u58f0\u6012 \u9a7e\uff0c\u5230\u540e\u6765\u5374\u8fde\u8bf4\u8bdd\u7684\u58f0\u97f3\u90fd\u6ca1\u6709\u4e86\uff0c\u800c\u90a3\u4e2a\u9053\u4eba\u4e5f\u59cb\u7ec8\u6ca1\u6709\u201d\u6eda\u51fa\u6765\u2018\uff01\u201c\u5979\u8bf4\u5230\u8fd9\u91cc\uff0c \u7a81\u5730\u6c89\u58f0\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u5728\u6211\u809a\u5b50\u91cc\u9690\u85cf\u4e86\u8bb8\u591a\u5e74\uff0c\u6211\u73b0\u5728\u65e2\u7136\u8bf4\u4e86\u51fa\u6765\uff0c\u4f60\u4eec\u53ef\u4e07\u4e07\u4e0d\u80fd\u8bf4 \u7ed9\u522b\u4eba\u542c\uff0c\u5426\u5219\u2026\u2026\u5426\u5219\uff0c\u6211\u5c31\u6ca1\u547d\u4e86\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u6697\u53f9\u4e00\u58f0\uff1a\u201c\u8ba9\u5973\u4eba\u4fdd\u5b88\u79d8\u5bc6\uff0c\u7684\u786e\u662f\u4ef6\u6781\u4e3a\u56f0\u96be\u7684\u4e8b\u3002\u201d \u3000\u3000\u53ea\u542c\u5f97\u8f66\u53a2\u4e2d\u7684\u5c11\u5973\u9f50\u58f0\u53d1\u7740\u8a93\uff1a\u201c\u7edd\u5bf9\u4e0d\u8bf4\u51fa\u6765\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u4e0d\u7981\u6697\u7b11\uff1a\u201c\u8fd9\u5927\u59ca\u50cf\u662f\u9887\u4e3a\u7a33\u91cd\uff0c\u5176\u5b9e\u4e5f\u50bb\u5f97\u5f88\uff0c\u5979\u81ea\u5df1\u90fd\u4e0d\u80fd\u4fdd\u5b88\u79d8\u5bc6\uff0c\u522b \u4eba\u53c8\u600e\u4f1a\u4fdd\u5b88\u5462\uff1f\u201d \u3000\u3000\u54ea\u77e5\u8fd9\u201c\u5927\u59ca\uff0c\uff0c\u5bf9\u5979\u4eec\u7684\u8a93\u8a00\u5374\u50cf\u662f\u5df2\u6781\u4e3a\u6ee1\u610f\uff0c\u4fbf\u53c8\u63a5\u9053\uff1a\u201c\u6211\u5f53\u65f6\u771f\u60f3\u8fdb\u53bb\u5077\u770b \u4e00\u4e0b\uff0c\u4f46\u662f\u5374\u59cb\u7ec8\u6ca1\u6709\u8fd9\u4e2a\u80c6\u5b50\uff0c\u8fc7\u4e86\u8bb8\u4e45\uff0c\u624d\u542c\u5f97\u7956\u59d1\u59d1\u5728\u91cc\u9762\u53eb\u6211\uff0c\u6211\u5fc3\u91cc\u771f\u6709\u8bf4\u4e0d\u51fa \u7684\u5bb3\u6015\uff0c\u53ea\u6015\u7956\u59d1\u59d1\u77e5\u9053\u6211\u5728\u5916\u9762\u5077\u542c\uff0c\u53ef\u662f\u53c8\u4e0d\u6562\u4e0d\u8fdb\u53bb\u3002\u201d \u3000\u3000\u6b64\u523b\u5979\u8bf4\u8bdd\u7684\u58f0\u97f3\u5df2\u6781\u4e3a\u4f4e\u6c89\uff0c\u518d\u52a0\u8f9a\u8f9a\u9707\u8033\u7684\u8f66\u58f0\uff0c\u5353\u957f\u537f\u82e5\u975e\u8033\u529b\u7279\u5f02\uff0c\u53c8\u5728\u51dd\u795e \u800c\u542c\uff0c\u4fbf\u51e0\u4e4e\u4e00\u53e5\u4e5f\u542c\u4e0d\u89c1\u3002 \u3000\u3000\u4e30\u53a2\u4e2d\u7684\u5c11\u5973\u60ca\u53f9\u7740\uff0c\u6709\u7684\u5fcd\u4e0d\u4f4f\u63d2\u53e3\u95ee\u9053\uff0c\u201c\u7956\u59d1\u59d1\u53eb\u4f60\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6709\u7684\u8fd8\u540c\u60c5\u4ed6\u8bf4\u9053\uff1a\u201c\u6211\u8981\u662f\u4f60\u5440\uff0c\u53ef\u771f\u4e0d\u6562\u8fdb\u53bb\uff0c\u7956\u59d1\u59d1\u7f5a\u8d77\u4eba\u6765\uff0c\u53ef\u771f\u6559\u4eba\u5403\u4e0d \u6d88\u3002\u201d \u3000\u3000\u201c\u5927\u59ca\u201d\u5e7d\u5e7d\u957f\u53f9\u4e86\u4e00\u58f0\uff0c\u63a5\u9053\uff1a\u201c\u6211\u5f53\u65f6\u53c8\u4f55\u5c1d\u4e0d\u662f\u8ddf\u4f60\u4e00\u6837\u60f3\u6cd5\uff0c\u786c\u7740\u5934\u76ae\u8d70\u8fdb\u53bb \u4e00\u770b\uff0c\u54ea\u77e5\u7956\u59d1\u59d1\u5374\u5728\u548c\u90a3\u9053\u4eba\u8c08\u7740\u8bdd\uff0c\u4e00\u70b9\u6124\u6012\u7684\u6837\u5b50\u90fd\u6ca1\u6709\uff0c\u8138\u4e0a\u751a\u81f3\u8fd8\u6709\u7b11\u5bb9\uff0c\u6211\u4e03 \u5c81\u5c31\u88ab\u7956\u59d1\u59d1\u5e26\u56de\u5c71\uff0c\u4ece\u6765\u4e5f\u6ca1\u6709\u770b\u8fc7\u5979\u8001\u4eba\u5bb6\u7b11\uff0c\u66f4\u60f3\u4e0b\u5230\u5979\u8001\u4eba\u5bb6\u4f1a\u548c\u4e00\u4e2a\u7537\u4eba\u7b11\u7740\u8bf4 \u8bdd\uff0c\u5f53\u65f6\u89c1\u4e86\u8fd9\u60c5\u5f62\uff0c\u771f\u662f\u5947\u602a\u5f97\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002\u201d \u3000\u3000\u5979\u8bdd\u8bf4\u5230\u4e00\u534a\uff0c\u8f66\u53a2\u4e2d\u7684\u5c11\u5973\u5df2\u4e00\u8d77\u60ca\u8bb6\u5730\u4f4e\u547c\u8d77\u6765\uff0c\u7b49\u5230\u5979\u8bdd\u8bf4\u5b8c\uff0c\u8fd9\u4e9b\u5c11\u5973\u4e00\u4e2a\u4e2a \u90fd\u5fcd\u4e0d\u4f4f\u60ca\u8bb6\u5730\u95ee\u9053\uff1a\u201c\u771f\u7684\uff1f\u771f\u7684\uff1f\u2026\u2026\u201d \u3000\u3000\u201c\u5927\u59ca\u201d\u5374\u4e0d\u56de\u7b54\uff0c\u53ea\u662f\u63a5\u7740\u53c8\u9053\uff1a\u201c\u6211\u5fc3\u91cc\u867d\u7136\u5947\u602a\uff0c\u4f46\u662f\u5728\u9762\u4e0a\u5374\u4e0d\u6562\u9732\u51fa\u4e00\u70b9\uff0c \u7956\u59d1\u59d1\u89c1\u4e86\u6211\uff0c\u5c31\u53eb\u6211\u53bb\u51c6\u5907\u4e9b\u9152\u83dc\uff0c\u6211\u5fc3\u66f4\u5947\u602a\uff0c\u7956\u59d1\u59d1\u5c45\u7136\u8981\u548c\u7537\u4eba\u5403\u9152\u3002\u201d \u3000\u3000\u201c\u6211\u6ee1\u809a\u60ca\u8bb6\u5730\u628a\u9152\u83dc\u9001\u4e86\u6765\uff0c\u7956\u59d1\u59d1\u53c8\u5429\u5490\u6211\uff0c\u53eb\u6211\u5b88\u5728\u95e8\u5916\uff0c\u4efb\u4f55\u4eba\u6765\u4e86\uff0c\u90fd\u53eb\u6211 \u6321\u9a7e\uff0c\u4e0d\u51c6\u4ed6\u4eec\u8fdb\u6765\u3002\u90a3\u9053\u4eba\u7b11\u5b09\u5b09\u5730\u671b\u7740\u6211\uff0c\u50cf\u662f\u5f88\u5f97\u610f\u7684\u6837\u5b50\uff0c\u6211\u5fc3\u91cc\u672c\u6765\u5bf9\u8fd9\u9053\u4eba\u5f88 \u6709\u597d\u611f\uff0c\u4f46\u90a3\u65f6\u5374\u4e0d\u77e5\u600e\u5730\uff0c\u7a81\u7136\u5bf9\u4ed6\u8ba8\u538c\u8d77\u6765\u3002\u201d \u3000\u3000\u5979\u957f\u957f\u900f\u4e86\u53e3\u6c14\uff0c\u53c8\u9053\uff1a\u201c\u90a3\u9053\u4eba\u6765\u7684\u65f6\u5019\u8fd8\u662f\u4e0b\u5348\uff0c\u5c31\u662f\u5c0f\u59d0\u505a\u5348\u8bfe\u7684\u65f6\u5019\uff0c\u6211\u5728\u95e8 \u5916\u4e00\u76f4\u7b49\u5230\u5929\u9ed1\uff0c\u7b49\u5230\u809a\u5b50\u90fd\u997f\u5f97\u53d1\u614c\u4e86\uff0c\u90a3\u9053\u4eba\u8fd8\u6ca1\u6709\u51fa\u6765\uff0c\u623f\u95f4\u91cc\u4e0d\u65f6\u4f20\u51fa\u4ed6\u7684\u7b11\u58f0\uff0c \u548c\u4f4e\u4f4e\u7684\u8bdd\u58f0\uff0c\u79df\u59d1\u59d1\u4e5f\u5728\u4e0d\u65ad\u5730\u7b11\u7740\uff0c\u4f46\u662f\u7b11\u58f0\u3001\u8bdd\u58f0\u8d8a\u6765\u8d8a\u4f4e\uff0c\u5230\u540e\u6765\u623f\u95f4\u91cc\u7adf\u4e00\u70b9\u58f0 \u97f3\u90e8\u6ca1\u6709\u4e86\uff0c\u6211\u5fc3\u91cc\u5728\u60f3\uff0c\u4ed6\u4eec\u5728\u505a\u4ec0\u4e48\u5462\uff1f\u201d \u3000\u3000\u8bf4\u5230\u6700\u540e\u51e0\u5b57\uff0c\u5979\u8bed\u58f0\u62d6\u5f97\u6781\u957f\uff0c\u957f\u957f\u8bed\u58f0\u4e00\u987f\uff0c\u8f66\u53a2\u4e2d\u4fbf\u4e5f\u6ca1\u6709\u4e86\u58f0\u97f3\uff0c\u8fd9\u4e9b\u5c11\u5973\u7684 \u5fc3\u4e2d\uff0c\u50cf\u662f\u4e5f\u90fd\u5728\u60f3\u7740\uff1a\u201c\u4ed6\u4eec\u5728\u623f\u91cc\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u8fd9\u95ee\u9898\u7684\u7b54\u6848\u4e5f\u8bb8\u5927\u5bb6\u90fd\u77e5\u9053\uff0c\u53ef\u662f\u8c01\u4e5f\u6ca1\u6709\u8bf4\u51fa\u6765\u3002 \u3000\u3000\u9644\u5728\u8f66\u540e\u7684\u5353\u957f\u537f\uff0c\u542c\u7740\u5979\u7684\u8bdd\uff0c\u5fc3\u4e2d\u4e0d\u7981\u601d\u6f6e\u7ffb\u6d8c\uff0c\u51e0\u4e4e\u4e0d\u76f8\u4fe1\u81ea\u5df1\u7684\u8033\u6735\uff0c\u4ed6\u4ed4\u7ec6 \u5728\u5fc3\u4e2d\u601d\u5fd6\u4e86\u4e00\u904d\uff0c\u60f3\u5230\u90a3\u4e11\u4eba\u6e29\u5982\u7389\u6e05\u6668\u8bf4\u5230\u4e07\u5999\u771f\u541b\u65f6\u7684\u8868\u60c5\uff0c\u5fc3\u4e2d\u4e0d\u7981\u604d\u7136\u5927\u609f\uff1a \u201c\u96be\u9053\u8fd9\u4e2a\u4e11\u4eba\u6e29\u5982\u7389\u4e4b\u6240\u4ee5\u8ba8\u538c\u7537\u4eba\uff0c\u53ea\u662f\u56e0\u4e3a\u81ea\u5df1\u592a\u4e11\uff0c\u660e\u77e5\u6ca1\u6709\u7537\u4eba\u559c\u6b22\u81ea\u5df1\uff0c\u800c\u8fd9 \u2018\u5c39\u51e1\u2019\u5374\u6293\u4f4f\u4e86\u5979\u7684\u5f31\u70b9\uff0c\u56e0\u4e4b\u82b1\u8a00\u5de7\u8bed\u5730\u5c06\u5979\u6253\u52a8\u4e86\u3002\u2014\u2014\u770b\u6765\u8fd9\u4e07\u5999\u771f\u541b\u7684\u6076\u6bd2\uff0c\u771f \u662f\u4ee4\u4eba\u53d1\u6307\uff0c\u4ed6\u5982\u6b64\u505a\u6cd5\uff0c\u7b80\u76f4\u5351\u9119\u5f97\u6ca1\u6709\u4eba\u6027\u4e86\u2014\u2014\u4f46\u662f\uff0c\u4ed6\u8fd9\u53c8\u662f\u4e3a\u7740\u4ec0\u4e48\u5462\uff1f\u201d \u3000\u3000\u8fd9\u5ff5\u5934\u5728\u4ed6\u5fc3\u4e2d\u4e00\u95ea\u800c\u8fc7\uff0c\u53ea\u542c\u8f66\u53a2\u4e2d\u9ed8\u7136\u826f\u4e45\uff0c\u90a3\u201c\u5927\u59ca\u201d\u4fbf\u53c8\u63a5\u9053\uff1a\u201c\u7b49\u5230\u5929\u5df2\u7ecf \u5b8c\u5168\u9ed1\u4e86\uff0c\u5c0f\u59d0\u5c31\u4ece\u540e\u9662\u8dd1\u5230\u524d\u9762\u6765\uff0c\u6211\u8d76\u7d27\u6321\u5728\u5c0f\u59d0\u524d\u9762\uff0c\u53eb\u5c0f\u59d0\u4e0d\u8981\u8fdb\u53bb\uff0c\u53ef\u662f\u5c0f\u59d0\u7684 \u813e\u6c14\u4f60\u4eec\u662f\u77e5\u9053\u7684\uff0c\u6211\u600e\u80fd\u6321\u5f97\u4f4f\uff0c\u6211\u773c\u770b\u5c0f\u59d0\u8981\u51b2\u8fdb\u72d9\u59d1\u59d1\u7684\u623f\u91cc\uff0c\u5fc3\u91cc\u771f\u5bb3\u6015\uff0c\u751f \u6015\u2026\u2026\u751f\u6015\u2026\u2026\u623f\u5b50\u91cc\u9762\u2026\u2026\u201d \u3000\u3000\u5979\u4e00\u8fde\u8bf4\u4e86\u4e24\u53e5\u201c\u751f\u6015\u201d\uff0c\u4f46\u662f\u6015\u7684\u7a76\u7adf\u662f\u4ec0\u4e48\uff0c\u5374\u8fd8\u662f\u6ca1\u6709\u8bf4\u51fa\u6765\uff0c\u53ea\u662f\u5979\u7eb5\u4e0d\u8bf4 \u51fa\uff0c\u522b\u4eba\u4e5f\u90fd\u662f\u5f88\u6e05\u695a\u5730\u77e5\u9053\u7684\u3002 \u3000\u3000\u8f66\u53a2\u4e2d\u8fd8\u662f\u6ca1\u6709\u4eba\u8bf4\u8bdd\uff0c\u4f3c\u4e4e\u5927\u5bb6\u90fd\u5728\u62c5\u5fc3\uff0c\u201c\u5c0f\u59d0\u201d\u4f1a\u770b\u5230\u4e00\u4e9b\u5979\u4e0d\u8be5\u770b\u5230\u7684\u4e8b\u3002 \u3000\u3000\u8f66\u884c\u4e86\u8bb8\u4e45\uff0c\u79bb\u57ce\u5df2\u7ecf\u5f88\u8fdc\uff0c\u5df2\u5c06\u8d70\u5165\u5929\u76ee\u5c71\u7684\u5c71\u9e93\u4e86\u3002 \u3000\u3000\u987b\u77e5\u8fd9\u79cd\u56da\u9a6c\u5927\u8f66\uff0c\u867d\u7136\u8d70\u7684\u6781\u5feb\uff0c\u4f46\u8fd9\u6761\u4e0d\u4f46\u9a91\u5c96\u4e0d\u5e73\u800c\u4e14\u591a\u662f\u50fb\u9759\u7684\u5c0f\u9053\uff0c\u56e0\u4e4b\u4fbf \u5f71\u54cd\u4e86\u884c\u8f66\u7684\u901f\u5ea6\uff0c\u82e5\u662f\u5355\u4eba\u5339\u9a6c\u800c\u884c\uff0c\u53ea\u6015\u6b64\u523b\u5df2\u7ecf\u8d70\u5165\u5929\u76ee\u5c71\u4e86\u3002 \u3000\u3000\u53c8\u9759\u4e86\u8bb8\u4e45\uff0c\u201c\u5927\u59ca\u201d\u65b9\u81ea\u957f\u957f\u4e00\u53f9\uff0c\u7f13\u7f13\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u6211\u5fc3\u91cc\u53c8\u6025\u53c8\u6015\uff0c\u60f3\u62c9\u4f4f\u5c0f \u59d0\uff0c\u54ea\u77e5\u4e0d\u4f46\u6ca1\u6709\u62c9\u4f4f\u3002\u53cd\u88ab\u5c0f\u59d0\u62d6\u5165\u623f\u91cc\uff0c\u4e00\u8fdb\u623f\u95e8\u7684\u65f6\u5019\uff0c\u6211\u76f4\u60f3\u95ed\u8d77\u773c\u775b\uff0c\u4e0d\u6562\u53bb \u770b\uff0c\u53ea\u542c\u5f97\u7956\u59d1\u59d1\u95ee\u9053\uff1a\u2018\u62d6\u62d6\u62c9\u62c9\u5730\u5e72\u4ec0\u4e48\uff1f\u5feb\u653e\u5f00\u624b\uff01\u2019\u6211\u66f4\u5413\u5f97\u53d1\u660f\uff0c\u7741\u5f00\u773c\u775b\u4e00\u770b \u2014\u2014\u201c\u5979\u8bf4\u5230\u8fd9\u91cc\uff0c\u8bdd\u58f0\u53c8\u4e00\u987f\uff0c\u5353\u957f\u537f\u5fc3\u4e2d\u4e0d\u7981\u4e00\u8df3\uff0c\u51e0\u4e4e\u8981\u5fcd\u4e0d\u4f4f\u8131\u53e3\u95ee\u9053\uff1a\u201c\u600e \u7684\uff1f\u201d \u3000\u3000\u4ed6\u81ea\u7136\u4e0d\u4f1a\u95ee\u51fa\u6765\uff0c\u53ea\u662f\u8f66\u53a2\u4e2d\u7684\u5c11\u5973\u5374\u5df2\u4ee3\u4ed6\u95ee\u4e86\u51fa\u6765\uff0c\u4e00\u58f0\u8fde\u7740\u4e00\u58f0\uff1a\u201c\u600e\u7684\u2026\u2026 \u600e\u7684\uff1f\u201d \u3000\u3000\u5927\u59ca\u900f\u4e86\u53e3\u957f\u6c14\uff0c\u63a5\u9053\uff1a\u201c\u54ea\u77e5\u623f\u95f4\u91cc\u53ea\u6709\u7956\u59d1\u59d1\u4e00\u4e2a\u4eba\u659c\u659c\u5730\u9760\u5728\u4e91\u5e8a\u8fb9\uff0c\u90a3\u9053\u4eba\u5374 \u4e0d\u77e5\u5728\u4ec0\u4e48\u65f6\u5019\u5df2\u7ecf\u8d70\u4e86\u3002\u201d \u3000\u3000\u8f66\u53a2\u4e2d\u4fbf\u4e5f\u968f\u4e4b\u53d1\u51fa\u4e00\u9635\u900f\u6c14\u7684\u58f0\u97f3\uff0c\u201c\u5927\u59ca\u201d\u7f13\u7f13\u53c8\u9053\uff1a\u201c\u81ea\u6b64\u4ee5\u540e\uff0c\u4f60\u4eec\u4e5f\u8bb8\u4e0d\u89c9 \u5f97\uff0c\u6211\u5374\u89c9\u5f97\u7956\u59d1\u59d1\u7684\u813e\u6c14\u597d\u50cf\u53d8\u5f97\u6bd4\u4ee5\u524d\u66f4\u5947\u602a\u4e86\uff0c\u6709\u65f6\u7279\u522b\u6e29\u67d4\uff0c\u6709\u65f6\u5374\u53c8\u7279\u522b\u66b4\u8e81\uff0c \u6211\u5fc3\u91cc\u77e5\u9053\u662f\u4ec0\u4e48\u539f\u56e0\uff0c\u4f46\u662f\uff0c\u6211\u53c8\u600e\u4e48\u6562\u8bf4\u51fa\u6765\u5462\uff1f\u201d \u3000\u3000\u8bf4\u5230\u8fd9\u65f6\uff0c\u5353\u957f\u537f\u7eb5\u662f\u6781\u7b28\u4e4b\u4eba\uff0c\u4e5f\u5df2\u542c\u51fa\u8fd9\u4e11\u4eba\u6e29\u5982\u7389\u548c\u90a3\u4e07\u5999\u771f\u541b\u5c39\u51e1\u4e4b\u95f4\uff0c\u662f\u6709 \u7740\u5982\u4f55\u4e0d\u540c\u5bfb\u5e38\u7684\u5173\u7cfb\u3002\u53ea\u662f\u4ed6\u82e5\u975e\u4eb2\u8033\u542c\u5230\uff0c\u4ed6\u4fbf\u518d\u4e5f\u4e0d\u4f1a\u76f8\u4fe1\u8fd9\u51b7\u9177\u7684\u5973\u9b54\u5934\u4e11\u4eba\u6e29\u5982 \u7389\u4e00\u751f\u4e4b\u4e2d\uff0c\u7adf\u8fd8\u6709\u7740\u8fd9\u4e48\u4e00\u6bb5\u4e8b\u8ff9\u3002 \u3000\u3000\u6709\u8bb8\u591a\u4ed6\u5728\u6e05\u6668\u542c\u4e86\u8fd8\u4e0d\u660e\u4e86\u7684\u8bdd\uff0c\u6b64\u523b\u4ed6\u4fbf\u604d\u7136\u5927\u609f\u4e86\u3002 \u3000\u3000\u53ea\u542c\u8fd9\u5927\u59ca\u53c8\u81ea\u53f9\u9053\uff1a\u201c\u8fd9\u51e0\u5e74\u4ee5\u6765\uff0c\u6211\u6697\u4e2d\u7559\u5fc3\uff0c\u90a3\u9053\u4eba\u4e0d\u8fc7\u591a\u4e45\uff0c\u4fbf\u4f1a\u4e0a\u5c71\u4e00\u6b21\uff0c \u4ed6\u4e0a\u5c71\u7684\u65f6\u5019\uff0c\u4f60\u4eec\u4e5f\u8bb8\u6709\u65f6\u4e5f\u770b\u5230\u8fc7\uff0c\u4f46\u662f\u6211\u77e5\u9053\uff0c\u4f60\u4eec\u518d\u4e5f\u4e0d\u4f1a\u60f3\u5230\u4ed6\u548c\u7956\u59d1\u59d1\u2026\u2026 \u5509\uff0c\u4ed6\u4e0b\u5c71\u7684\u65f6\u5019\uff0c\u6211\u5077\u5077\u770b\u5230\u8fc7\u51e0\u6b21\uff0c\u603b\u662f\u5e26\u7740\u4e00\u4e2a\u5305\u88b1\uff0c\u800c\u7956\u59d1\u59d1\u5b9d\u5e93\u4e2d\u7684\u73cd\u5b9d\uff0c\u5374\u4e00 \u5929\u6bd4\u4e00\u5929\u5c11\uff0c\u6709\u65f6\u7956\u59d1\u59d1\u4e5f\u5355\u72ec\u4e0b\u5c71\u53bb\uff0c\u8981\u8fc7\u597d\u4e45\u624d\u56de\u6765\uff0c\u5979\u8001\u4eba\u5bb6\u867d\u7136\u4e0d\u8bf4\uff0c\u6211\u53ef\u4e5f\u77e5\u9053 \u5979\u8001\u4eba\u5bb6\u4e0b\u5c71\u662f\u53bb\u627e\u8c01\u3002\u201d \u3000\u3000\u9759\u5bc2\u8bb8\u4e45\u7684\u5403\u5403\u7b11\u58f0\uff0c\u6b64\u523b\u7adf\u53c8\u54cd\u8d77\uff0c\u90a3\u987d\u76ae\u7684\u5c11\u5973\u7adf\u81ea\u7b11\u9053\uff1a\u201c\u5927\u59ca\uff0c\u6211\u731c\u51fa\u6765\u4e86\uff0c \u8fd9\u9053\u4eba\u53ef\u5c31\u662f\u53eb\u505a\u4ec0\u4e48\u4e07\u5999\u771f\u541b\u7684\uff1f\u201d \u7b2c\u5341\u4e8c\u7ae0\u3000\u6e10\u5165\u864e\u7a74>> \u53e4\u9f99\u300a\u6708\u5f02\u661f\u90aa\u300b \u7b2c\u5341\u4e8c\u7ae0\u3000\u6e10\u5165\u864e\u7a74 \u3000\u3000\u8f66\u9a6c\u6e10\u6e10\u8d70\u4eba\u5c71\u533a\uff0c\u5c71\u8def\u66f4\u7a84\uff0c\u4e5f\u66f4\u4e3a\u5d0e\u5c96\uff0c\u9a7e\u8f66\u7684\u8f66\u592b\uff0c\u663e\u7136\u4e5f\u6709\u4e0d\u540c\u51e1\u4fd7\u7684\u8eab\u624b\uff0c \u5728\u8fd9\u72ed\u7a84\u3001\u5d0e\u5c96\uff0c\u800c\u4e14\u6e10\u6e10\u9661\u659c\u7684\u5c71\u9053\uff0c\u7ade\u4ecd\u80fd\u9a7e\u7740\u8fd9\u56db\u9a6c\u5927\u8f66\u653e\u8f94\u800c\u884c\uff0c\u867d\u7136\u884c\u9a70\u5f97\u4e5f\u8f83 \u6162\u4e9b\uff0c\u4f46\u5374\u5df2\u662f\u6781\u4e0d\u5bb9\u6613\u7684\u4e8b\u4e86\u3002 \u3000\u3000\u5353\u957f\u537f\u867d\u7136\u65e9\u5df2\u731c\u51fa\u8fd9\u5927\u59ca\u7684\u53e3\u4e2d\u7684\u9053\u4eba\uff0c\u5fc5\u5b9a\u5c31\u662f\u4e07\u5999\u771f\u541b\uff0c\u4f46\u6b64\u523b\u8fd9\u5c11\u5973\u8bf4\u4e86\u51fa \u6765\uff0c\u4ed6\u5fc3\u4e2d\u4ecd\u4e0d\u7981\u4e3a\u4e4b\u4e00\u8df3\u3002 \u3000\u3000\u53ea\u542c\u8fd9\u5927\u59ca\u51b7\u54fc\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u771f\u806a\u660e\uff0c\u96be\u9053\u9664\u4e86\u4f60\u4e4b\u5916\uff0c\u5c31\u6ca1\u6709\u522b\u4eba\u77e5\u9053\u4e86\u5417\uff1f\u54fc\u2014 \u2014\u6211\u771f\u4ece\u6765\u6ca1\u6709\u89c1\u8fc7\u6bd4\u4f60\u518d\u6076\u7684\u4eba\uff0c\u6211\u544a\u8bc9\u4f60\uff0c\u4f60\u8981\u662f\u628a\u4eca\u5929\u7684\u8bdd\u8bf4\u51fa\u53bb\u5440\u2014\u2014\u201d\u8fd9\u987d\u76ae\u7684 \u5c11\u5973\u7acb\u523b\u62a2\u7740\u9053\uff1a\u201c\u5927\u59ca\uff0c\u4f60\u653e\u5fc3\uff0c\u6211\u7edd\u5bf9\u4e0d\u4f1a\u8bf4\u7684\uff0c\u5c31\u662f\u6709\u4eba\u8981\u6740\u6b7b\u6211\uff0c\u6211\u4e5f\u4e0d\u8bf4\u3002\u201d \u3000\u3000\u5927\u59ca\u53c8\u54fc\u4e86\u4e00\u58f0\uff0c\u5374\u542c\u53e6\u4e00\u4e2a\u5c11\u5973\u7684\u58f0\u97f3\u5e7d\u5e7d\u53f9\u9053\uff1a\u201c\u8fd9\u771f\u6559\u4eba\u60f3\u4e0d\u5230\uff0c\u7956\u59d1\u59d1\u8fd8\u4f1a\u4e0a \u7537\u4eba\u7684\u5f53\uff0c\u6211\u65e9\u5c31\u77e5\u9053\u7537\u4eba\u90fd\u4e0d\u662f\u597d\u4e1c\u897f\uff0c\u6211\u5440\uff0c\u6211\u8fd9\u4e00\u8f88\u5b50\u8fde\u78b0\u90fd\u4e0d\u8981\u78b0\u7537\u4eba\u4e00\u4e0b\u3002\u201d \u3000\u3000\u8fd9\u58f0\u97f3\u4ee5\u524d\u4ece\u672a\u8bf4\u8fc7\u8bdd\uff0c\u8bf4\u8bdd\u7684\u58f0\u97f3\u53c8\u67d4\u8f6f\uff0c\u53c8\u7f13\u6162\uff0c\u201c\u5927\u59ca\u201d\u542c\u4e86\u50cf\u662f\u9887\u6709\u540c\u611f\u7684\u6837 \u5b50\uff0c\u4ea6\u81ea\u53f9\u9053\uff1a\u201c\u6211\u4f55\u5c1d\u4e0d\u77e5\u9053\u8fd9\u59d3\u5c39\u7684\u662f\u4e3a\u4e86\u8981\u9a97\u7956\u59d1\u59d1\u7684\u4e1c\u897f\uff0c\u4f46\u662f\u6211\u4e00\u60f3\uff0c\u7956\u59d1\u59d1\u7684 \u4e00\u751f\u5bc2\u5bde\uff0c\u6709\u4e2a\u7537\u4eba\u5b89\u6170\u5979\u8001\u4eba\u5bb6\uff0c\u4e5f\u662f\u597d\u7684\u3002\u201d \u3000\u3000\u8fd9\u65f6\u90a3\u987d\u76ae\u7684\u5c11\u5973\u4f3c\u4e4e\u53c8\u5fcd\u4e0d\u4f4f\u8981\u8bf4\u8bdd\u4e86\uff0c\u5c45\u7136\u4e5f\u51b7\u54fc\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6211\u624d\u4e0d\u5e0c\u7f55\u54e9\uff0c \u53ef\u662f\u2014\u2014\u5927\u59ca\uff0c\u8fd9\u4e8b\u4f60\u77e5\u9053\u7684\u8fd9\u6837\u6e05\u695a\uff0c\u53c8\u662f\u4ec0\u4e48\u597d\u5947\u602a\u7684\u5730\u65b9\u5462\uff1f\u201d \u3000\u3000\u5927\u59ca\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u53ef\u77e5\u9053\uff0c\u90a3\u7a7f\u9ec4\u8863\u670d\u7684\u5c11\u5e74\uff0c\u662f\u8c01\u7684\u5f92\u5f1f\u5462\uff1f\u201d \u3000\u3000\u5979\u7b2c\u4e8c\u6b21\u95ee\u51fa\u8fd9\u4e00\u53e5\u606c\uff0c\u8f66\u53a2\u4e2d\u7684\u5c11\u5973\u4fbf\u4e00\u8d77\u201c\u54e6\u201d\u4e86\u4e00\u58f0\uff0c\u604d\u7136\u9053\uff1a\u201c\u83ab\u975e\u4ed6\u5c31\u662f\u8fd9 \u59d3\u5c39\u7684\u5f92\u5f1f\u3002\u201d \u3000\u3000\u5927\u59ca\u7684\u58f0\u97f3\u8d8a\u53d1\u4f4e\u4e86\uff0c\u9053\uff1a\u201c\u662f\u4e86\uff0c\u4ed6\u65e2\u7136\u662f\u90a3\u59d3\u5c39\u7684\u5f92\u5f1f\uff0c\u800c\u90a3\u59d3\u5c39\u7684\uff0c\u53c8\u548c\u7956\u59d1\u2026 \u4f60\u4eec\u60f3\uff0c\u8fd9\u4e0d\u662f\u5947\u602a\u5417\uff0c\u7956\u59d1\u4e3a\u4ec0\u4e48\u8981\u628a\u4ed6\u5173\u8d77\u6765\u5462\uff1f\u201d \u3000\u3000\u8f66\u53a2\u4e2d\u54cd\u8d77\u7a83\u7a83\u4f4e\u8bed\u58f0\uff0c\u4f3c\u4e4e\u5728\u731c\u6d4b\u7740\u8fd9\u95ee\u9898\u7684\u7b54\u6848\uff0c\u4f46\u9644\u5728\u8f66\u540e\u6765\u7684\u5353\u957f\u537f\uff0c\u6b64\u523b\u5fc3 \u4e2d\u5374\u5df2\u5168\u90e8\u4e86\u7136\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u8fd9\u4e07\u5999\u771f\u541b\u76ee\u7684\u8fbe\u5230\u4e4b\u540e\uff0c\u600e\u4f1a\u518d\u548c\u8fd9\u5176\u4e11\u65e0\u6bd4\u7684\u4e11\u4eba\u6e29\u5982\u7389\u53ae\u7f20\u4e0b\u53bb\uff0c\u81ea\u7136\u4ece \u6b64\u5c31\u907f\u4e0d\u89c1\u9762\u3002 \u3000\u3000\u800c\u4e11\u4eba\u6e29\u5982\u7389\u4e00\u751f\u5bc2\u5bde\uff0c\u9aa4\u7136\u843d\u4eba\u8fd9\u60c5\u611f\u7684\u9677\u9631\uff0c\u4fbf\u4e0d\u80fd\u81ea\u62d4\u3002 \u3000\u3000\u987b\u77e5\u60c5\u611f\u4e00\u7269\uff0c\u5c31\u50cf\u5c71\u95f4\u7684\u6d2a\u6c34\u4f3c\u7684\uff0c\u4e0d\u7206\u53d1\u5219\u5df2\uff0c\u4e00\u7206\u53d1\u4fbf\u60ca\u4eba\uff0c\u800c\u4e14\u538b\u5236\u5f97\u8d8a\u4e45\uff0c \u7206\u53d1\u51fa\u6765\u4e5f\u5c31\u8d8a\u53d1\u4e0d\u53ef\u6536\u62fe\u3002 \u3000\u3000\u8fd9\u4e11\u4eba\u6e29\u5982\u7389\u4e4d\u52a8\u771f\u60c5\uff0c\u81ea\u7136\u662f\u5168\u5fc3\u5168\u610f\u5730\u7231\u7740\u5c39\u51e1\uff0c\u5f53\u5979\u77e5\u9053\u5c39\u51e1\u662f\u5728\u9a97\u81ea\u5df1\u7684\u65f6 \u5019\uff0c\u8fd9\u5f3a\u70c8\u7684\u7231\uff0c\u4fbf\u81ea\u7136\u53d8\u4e3a\u5f3a\u70c8\u7684\u6068\u4e86\u3002\u201c\u4ed6\u5fc3\u4e2d\u611f\u53f9\u7740\uff0c\u8f6c\u76ee\u800c\u671b\uff0c\u5c71\u9053\u65c1\u6811\u6728\u82cd\u90c1\uff0c \u5c71\u5761\u4e5f\u8d8a\u6765\u8d8a\u9661\uff0c\u4ed6\u77e5\u9053\u8ddd\u79bb\u81ea\u5df1\u7684\u76ee\u7684\uff0c\u5df2\u4e0d\u4f1a\u592a\u8fdc\u4e86\u3002\u4e00\u5207\u731c\u6d4b\uff0c\u4e00\u5207\u7b49\u5f85\uff0c\u4e5f\u5373\u5c06\u6709 \u6240\u7ed3\u675f\uff0c\u5728\u8fd9\u7ed3\u675f\u5c06\u8981\u5230\u6765\uff0c\u5374\u672a\u5230\u6765\u7684\u65f6\u5019\uff0c\u4ed6\u7684\u5fc3\u60c5\u662f\u7d27\u5f20\u800c\u5174\u594b\u7684\u3002\u8f66\u53a2\u4e2d\u4e45\u4e45\u90fd\u6ca1 \u6709\u58f0\u97f3\u4f20\u51fa\u6765\uff0c\u4ed6\u6697\u5fd6\u9053\uff1a\u201c\u8fd9\u4e9b\u5c11\u5973\u6b64\u523b\u662f\u5728\u4e3a\u5979\u4eec\u7684\u7956\u59d1\u96be\u53d7\u5462\uff1f\u8fd8\u662f\u5728\u60f3\u7740\u522b\u7684 \u4e8b\uff1f\u201d \u3000\u3000\u9a6c\u8f66\u98a0\u7c38\u66f4\u5267\uff0c\u8f66\u58f0\u4e5f\u66f4\u54cd\uff0c\u4e24\u65c1\u6d78\u6d74\u5728\u591c\u8272\u4e4b\u4e2d\u7684\u6797\u6728\uff0c\u5374\u662f\u6b7b\u4e00\u822c\u7684\u9759\u5bc2\uff0c\u7adf\u8fde\u4e00 \u58f0\u866b\u9e23\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u54ea\u77e5\u2014\u2014 \u3000\u3000\u9759\u5bc2\u7684\u6797\u6728\u4e2d\uff0c\u7a81\u5730\u54cd\u8d77\u4e00\xb7\u58f0\u65ad\u559d\uff1a\u201c\u505c\u4e0b\uff01\u201d \u3000\u3000\u5353\u957f\u537f\u4f46\u89c9\u8033\u65c1\u201c\u55e1\u201d\u7136\u4e00\u58f0\uff0c\u56db\u9762\u7a7a\u5c71\uff0c\u4f3c\u4e4e\u90fd\u88ab\u8fd9\u4e24\u5b57\u9707\u7684\u55e1\u55e1\u4f5c\u54cd\uff0c\u53ea\u542c\u5f97\uff1a \u201c\u505c\u4e0b\u2026\u2026\u505c\u4e0b\u2026\u2026\u201d \u3000\u3000\u4e0d\u65ad\u7684\u56de\u58f0\uff0c\u5728\u56db\u5c71\u4e2d\u98d8\u8361\u7740\u3002 \u3000\u3000\u8d76\u8f66\u7684\u9a6c\u592b\u6597\u7136\u4e00\u60ca\uff0c\u547c\u54e8\u4e00\u58f0\uff0c\u52d2\u4f4f\u9a6c\u7ec4\uff0c\u516b\u5339\u5065\u9a6c\u4e00\u8d77\u6602\u9996\u957f\u5636\uff0c\u9a6c\u8f66\u7f13\u7f13\u5012\u9000\u6570 \u5c3a\uff0c\u65b9\u81ea\u4e00\u8d77\u505c\u4f4f\u3002 \u3000\u3000\u8f66\u53a2\u5185\u8fde\u58f0\u5a07\u53f1\uff0c\u8f66\u95e8\u4e4d\u542f\uff0c\u5341\u6570\u6761\u7ea2\u5f71\uff0c\u7bad\u4e5f\u4f3c\u7684\u7a9c\u4e86\u51fa\u6765\uff0c\u53e3\u4e2d\u559d\u9053\uff1a\u201c\u662f\u8c01\uff1f\u201d \u3000\u3000\u6b7b\u9759\u4e4b\u4e2d\uff0c\u4f20\u51fa\u4e00\u4e2a\u51b7\u51b7\u7684\u58f0\u97f3\uff1a\u201c\u4f60\u4eec\u8fd9\u4e9b\u4e2b\u5934\uff0c\u96be\u9053\u90fd\u6b7b\u4e86\u4e0d\u6210\uff0c\u6709\u4eba\u5750\u5728\u4f60\u4eec\u8f66 \u5b50\u540e\u9762\uff0c\u4f60\u4eec\u96be\u9053\u90fd\u4e0d\u77e5\u9053\u5417\uff1f\u201d \u3000\u3000\u58f0\u97f3\u5c16\u7ec6\u9ad8\u4ea2\uff0c\u5728\u7a7a\u591c\u4e2d\u542c\u6765\uff0c\u6ee1\u542b\u68ee\u51b7\u4e4b\u610f\u3002 \u3000\u3000\u5353\u957f\u537f\u5fc3\u4e2d\u4e00\u51db\uff0c\u77e5\u9053\u81ea\u5df1\u884c\u85cf\u5df2\u9732\uff0c\u95ea\u76ee\u671b\u53bb\uff0c\u53ea\u662f\u8fd9\u4e9b\u5c11\u5973\u7ad9\u5728\u9a6c\u8f66\u4e24\u4fa7\uff0c\u4f3c\u4e4e\u90fd \u88ab\u8fd9\u7a81\u6765\u7684\u8bed\u58f0\u60ca\u7684\u6115\u4f4f\u4e86\u3002 \u3000\u3000\u6811\u6797\u4e4b\u4e2d\uff0c\u51b7\u7b11\u4e4b\u58f0\u9aa4\u8d77\uff0c\u53e6\u4e00\u4e00\u4e2a\u7c97\u8c6a\u5b8f\u4eae\uff0c\u6709\u5982\u9f13\u51fb\u949f\u9e23\u4e00\u822c\u7684\u58f0\u97f3\uff0c\u4e00\u5b57\u4e00\u5b57\u4ed6 \u8bf4\u9053\uff1a\u201c\u8eb2\u5728\u8f66\u540e\u7684\u670b\u53cb\uff0c\u8fd8\u4e0d\u4e0b\u6765\u4f5c\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5353\u957f\u537f\u5251\u7709\u4e00\u8f69\uff0c\u53cc\u638c\u5fae\u6309\u8f66\u8eab\uff0c\u8eab\u5f62\u7a81\u5730\u51b2\u5929\u800c\u8d77\uff0c\u5de6\u638c\u4e00\u5708\uff0c\u53f3\u638c\u5f53\u80f8\uff0c\u98d8\u98d8\u843d\u5728 \u8f66\u9876\u4e0a\uff0c\u76ee\u5149\u56db\u626b\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u8eb2\u5728\u6811\u6797\u91cc\u7684\u670b\u53cb\uff0c\u9601\u4e0b\u4e5f\u8be5\u51fa\u6765\u4e86\u5427\uff1f\u201d \u3000\u3000\u7ea2\u88f3\u5c11\u5973\u4eec\u8fde\u58f0\u5a07\u53f1\uff0c\u8f6c\u8eab\u4e00\u671b\u5353\u957f\u537f\uff0c\u4f3c\u4e4e\u90fd\u8981\u63a0\u5411\u8f66\u9876\u3002 \u3000\u3000\u54ea\u77e5\u6797\u6728\u4e2d\u53c8\u662f\u4e00\u58f0\u51b7\u53f1\uff1a\u201c\u4f4f\u624b\uff01\u201d \u3000\u3000\u53f1\u58f0\u65b9\u4f4f\uff0c\u6797\u6728\u7684\u9634\u5f71\u91cc\uff0c\u7adf\u51b7\u7b11\u7740\u7f13\u7f13\u8d70\u51fa\u4e24\u4e2a\u884c\u5bb9\u8be1\u5f02\u7684\u4eba\u6765\u8fd9\u4e24\u4eba\u4e00\u50e7\u4e00\u9053\uff0c\u4e00 \u9ad8\u4e00\u77ee\uff0c\u4e00\u7626\u4e00\u80d6\uff0c\u9ad8\u7684\u7626\u5982\u67af\u6728\uff0c\u4e00\u8eab\u9cde\u5ccb\u7626\u9aa8\uff0c\u5374\u7a7f\u7740\u4e00\u4ef6\u5bbd\u5927\u7684\u8888\u88df\uff0c\u8170\u7554\u659c\u6302\u4e00\u53e3 \u72ed\u957f\u7684\u6212\u5200\uff0c\u9aa4\u773c\u671b\u53bb\uff0c\u6709\u5982\u8349\u624e\u6728\u96d5\uff0c\u5168\u8eab\u4e0a\u4e0b\uff0c\u7ade\u627e\u4e0d\u51fa\u4e00\u4e1d\u6d3b\u4eba\u7684\u6c14\u606f\u3002 \u3000\u3000\u77ee\u7684\u5374\u80a5\u5982\u5f25\u9640\uff0c\u4e00\u8eab\u80a5\u8089\u4e4b\u4e0a\uff0c\u7a7f\u7684\u7adf\u662f\u4e00\u4ef6\u53c8\u7d27\u53c8\u77ed\u7684\u9053\u888d\uff0c\u5934\u4e0a\u9053\u9afb\u84ec\u4e71\uff0c\u751f\u50cf \u662f\u521a\u521a\u7761\u9192\u7684\u6837\u5b50\uff0c\u8170\u7554\u659c\u6302\u7740\u7684\u4e00\u53e3\u5251\uff0c\u4e5f\u6bd4\u5e38\u4eba\u6240\u7528\uff0c\u77ed\u4e0a\u4e00\u500d\uff0c\u5251\u9798\u4e4c\u5149\u95ea\u70c1\uff0c\u975e\u76ae \u975e\u9769\uff0c\u975e\u6728\u975e\u94c1\uff0c\u7ade\u770b\u4e0d\u51fa\u662f\u7528\u4ec0\u4e48\u4e1c\u897f\u505a\u7684\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u4e0d\u4f46\u4f53\u6001\u4e0d\u540c\uff0c\u795e\u6001\u5404\u5f02\uff0c\u51b7\u7b11\u7684\u58f0\u97f3\u4e5f\u662f\u4e00\u4e2a\u5c16\u7ec6\uff0c\u4e00\u4e2a\u6d2a\u4eae\uff0c\u8fd9\u4e24\u4e2a\u4eba\u5e76\u80a9 \u7ad9\u5728\u4e00\u8d77\u7684\u7b11\u58f0\uff0c\u8ba9\u4eba\u89c1\u4e86\u4e0d\u7531\u81ea\u4e3b\u7684\u4f1a\u4ece\u5fc3\u5e95\u6cdb\u8d77\u4e00\u9635\u96be\u53d7\u7684\u611f\u89c9\uff0c\u5c31\u50cf\u662f\u4e00\u4e2a\u80c6\u5c0f\u7684\u5973 \u5b50\u7a81\u7136\u89c1\u7740\u4e00\u6761\u7ec6\u957f\u7684\u6bd2\u86c7\uff0c\u548c\u4e00\u6761\u80a5\u80d6\u7684\u8725\u8734\u65f6\u7684\u611f\u89c9\u4e00\u6837\u3002 \u3000\u3000\u5353\u957f\u537f\u76ee\u5149\u52a8\u5904\uff0c\u5fc3\u4e2d\u4e5f\u4e0d\u7981\u4e3a\u4e4b\u6cdb\u8d77\u4e00\u9635\u96be\u4ee5\u63cf\u8ff0\u7684\u96be\u53d7\u4e4b\u610f\uff0c\u53ea\u89c9\u8fd9\u4e24\u4eba\u884c\u5bb9\u4e4b\u4e11 \u602a\uff0c\u771f\u662f\u666e\u5929\u4e4b\u4e0b\u518d\u4e5f\u96be\u4ee5\u627e\u51fa\u3002 \u3000\u3000\u90a3\u4e9b\u7ea2\u88f3\u5c11\u5973\u4e00\u7779\u8fd9\u4e8c\u4eba\u7684\u8eab\u5f62\uff0c\u5374\u9f50\u5a07\u5524\u4e00\u58f0\uff0c\u8eac\u4e0b\u8170\u53bb\uff0c\u795e\u6001\u4e4b\u95f4\uff0c\u7adf\u50cf\u662f\u5bf9\u8fd9\u4e24 \u4e2a\u4e11\u602a\u4e4b\u4eba\u6781\u4e3a\u606d\u656c\u3002 \u3000\u3000\u8fd9\u4e00\u50e7\u4e00\u9053\u51b7\u7b11\u8fde\u8fde\uff0c\u773c\u89d2\u4e0a\u7ffb\uff0c\u5374\u4f3c\u4e4e\u6839\u672c\u6ca1\u6709\u89c1\u5230\u8fd9\u4e9b\u5c11\u5973\u4e00\u6837\uff0c\u7b14\u76f4\u5730\u8d70\u5230\u8f66 \u524d\uff0c\u62ac\u5934\u5411\u5353\u957f\u537f\u671b\u53bb\uff0c\u90a3\u80a5\u80d6\u9053\u4eba\u201c\u5403\u201d\u5730\u4e00\u7b11\uff0c\u4fa7\u9996\u5411\u90a3\u7626\u50e7\u4eba\u7b11\u7740\u8bf4\u9053\uff1a\u201c\u539f\u6765\u662f\u8fd9 \u4e48\u4e00\u4e2a\u6f02\u4eae\u7684\u5c0f\u4f19\u5b50\uff0c\u8001\u548c\u5c1a\uff0c\u4f60\u5927\u6982\u53c8\u8981\u751f\u51fa\u601c\u9999\u60dc\u7389\u4e4b\u5fc3\u4e86\u5427\uff0c\u5509\uff0c\u53ea\u53ef\u60dc\u6211\u6740\u4eba\u7684\u75db \u53c8\u8fc7\u4e0d\u6210\u4e86\u3002\u201d \u3000\u3000\u7b11\u58f0\u4e4b\u4e2d\uff0c\u6ee1\u542b\u6deb\u90aa\u7325\u4eb5\u4e4b\u610f\uff0c\u90a3\u201c\u601c\u9999\u501f\u738b\u201d\u56db\u5b57\uff0c\u66f4\u662f\u7528\u5f97\u4e0d\u582a\uff0c\u5353\u957f\u537f\u867d\u7136\u5e76\u4e0d \u751a\u4e86\u89e3\u4ed6\u8a00\u4e2d\u4e4b\u610f\uff0c\u4f46\u5fc3\u4e2d\u4ea6\u4e0d\u7981\u52c3\u7136\u5927\u6012\uff0c\u5251\u7709\u4e00\u8f69\uff0c\u4fef\u9996\u5389\u53f1\u4e00\u58f0\uff0c\u6717\u58f0\u559d\u9053\uff1a\u201c\u4f60\u4eec \u4e24\u4eba\u9b3c\u9b3c\u795f\u795f\u5730\u8eb2\u5728\u6797\u4e2d\uff0c\u7a76\u7adf\u610f\u6b32\u4f55\u4e3a\uff0c\u770b\u4f60\u4e24\u4eba\u7684\u6837\u5b50\u4e5f\u50cf\u662f\u6b66\u6797\u4e2d\u6709\u5934\u6709\u8138\u7684\u4eba\u7269\uff0c \u600e\u4ed6\u8bf4\u51fa\u5982\u6b64\u2014\u2014\u201d\u8bf4\u5230\u8fd9\u4e9b\uff0c\u4ed6\u8bed\u58f0\u4e00\u987f\uff0c\u4e0b\u9762\u7684\u65e0\u803b\u4e24\u5b57\u7adf\u672a\u8bf4\u51fa\uff0c\u53ea\u56e0\u4ed6\u867d\u7136\u806a\u660e\u7edd \u9876\uff0c\u4f46\u6b63\u76f4\u7eaf\u6d01\uff0c\u53c8\u662f\u521d\u6d89\u6c5f\u6e56\uff0c\u600e\u4f1a\u4e86\u89e3\u8fd9\u77ee\u80d6\u9053\u4eba\u8a00\u8bed\u4e4b\u4e2d\u7684\u4e0d\u582a\u4e4b\u610f\uff0c\u662f\u4ee5\u4ed6\u4fbf\u4e5f\u4e0d \u77e5\u9053\u77ee\u80d6\u9053\u4eba\u65b9\u624d\u6240\u8bf4\u7684\u8bdd\uff0c\u7a76\u7adf\u662f\u5426\u65e0\u803b\u3002 \u3000\u3000\u5374\u542c\u8fd9\u77ee\u80d6\u9053\u4eba\u53c8\u662f\u201c\u54e7\u201d\u5730\u4e00\u7b11\uff0c\u90a3\u7626\u957f\u618e\u4eba\u5374\u4f38\u51fa\u4e00\u53cc\u67af\u7626\u5982\u67f4\u7684\u624b\u638c\u6765\uff0c\u7f13\u7f13\u6447 \u4e86\u4e24\u6447\uff0c\u50cf\u662f\u5728\u963b\u6b62\u7740\u8fd9\u77ee\u80d6\u9053\u4eba\u60f3\u8bf4\u7684\u8bdd\uff0c\u4e00\u9762\u7528\u4e00\u53cc\u6b64\u523b\u5df2\u81ea\u772f\u6210\u4e00\u7f1d\uff0c\u90a3\u4e24\u9053\u540a\u989d\u77ed \u7709\u4e0b\u7684\u4e09\u89d2\u602a\u773c\uff0c\u671b\u7740\u5353\u957f\u537f\uff0c\u4e00\u9762\u6162\u6761\u65af\u7406\u3001\u9634\u9633\u602a\u6c14\u4ed6\u8bf4\u9053\u201c\u4f60\u8fd9\u5c0f\u5a03\u5a03\uff0c\u8bf4\u8d77\u8bdd\u6765\u600e \u5730\u5982\u6b64\u4e0d\u8bb2\u7406\uff0c\u660e\u660e\u662f\u4f60\u9b3c\u9b3c\u795f\u795f\u7684\u8eb2\u5728\u4eba\u5bb6\u8f66\u540e\uff0c\u5374\u53c8\u600e\u4ed6\u8bf4\u8d77\u4eba\u5bb6\u9b3c\u9b3c\u795f\u795f\u4e86\u3002\u201d \u3000\u3000\u4ed6\u5fae\u4e00\u4f38\u624b\uff0c\u5411\u5353\u957f\u537f\u62db\u4e86\u4e24\u62db\uff0c\u5c16\u58f0\u5c16\u6c14\u5730\u63a5\u7740\u9053\uff1a\u201c\u4e0b\u6765\uff01\u4e0b\u6765\uff01\u8001\u8896\u5012\u8981\u95ee\u95ee \u4f60\uff0c\u4f60\u8eb2\u5728\u4eba\u5bb6\u8f66\u540e\uff0c\u60f3\u5bf9\u8fd9\u73ed\u5973\u5b69\u5b50\u975e\u793c\u5462\uff1f\u8fd8\u662f\u2014\u2014\u201d\u5353\u957f\u537f\u5927\u559d\u4e00\u58f0\uff1a\u201c\u4f4f\u5634\u3002\u201d \u3000\u3000\u90a3\u4e9b\u7ea2\u88f3\u5c11\u5973\u4e00\u8d77\u4f38\u624b\u63a9\u4f4f\u6a31\u5507\uff0c\u50cf\u662f\u5fcd\u4fca\u4e0d\u4f4f\u7684\u6837\u5b50\u3002 \u3000\u3000\u5353\u957f\u537f\u8fd9\u4e00\u58f0\u5927\u559d\uff0c\u867d\u7136\u559d\u65ad\u4e86\u8fd9\u7626\u957f\u50e7\u4eba\u7684\u8bdd\uff0c\u5374\u4ecd\u7136\u6beb\u4e0d\u5728\u610f\u5730\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u65e0\u8bba \u5982\u4f55\uff0c\u4f60\u8fd9\u4e2a\u5e74\u7eaa\u8f7b\u8f7b\u7684\u5c0f\u4f19\u5b50\u722c\u5728\u4eba\u5bb6\u8f66\u540e\uff0c\u603b\u6ca1\u6709\u5b89\u7740\u597d\u5fc3\uff0c\u82e5\u6362\u4e86\u4ee5\u5f80\uff0c\u5c31\u51ed\u4f60\u8fd9 \u70b9\uff0c\u8001\u94a0\u5c31\u8be5\u5c06\u4f60\u4e00\u5200\u6740\u5374\uff0c\u4f46\u8001\u8896\u81ea\u7688\u4f9d\u6211\u4f5b\u4ee5\u6765\uff0c\u5fc3\u80a0\u5df2\u6bd4\u4ee5\u524d\u8f6f\u5f97\u591a\u4e86\uff0c\u600e\u5fcd\u5fc3\u5c06\u4f60 \u4e00\u4e2a\u751f\u9f99\u6d3b\u864e\u822c\u7684\u5c0f\u4f19\u5b50\u5728\u8fd8\u6ca1\u6709\u4eab\u5230\u4eba\u751f\u4e50\u8da3\u4e4b\u524d\uff0c\u5c31\u51a4\u51a4\u6789\u5728\u7684\u9001\u4e86\u547d\u2014\u2014\u201d\u80d6\u77ee\u9053\u4eba \u7a81\u5730\u4e00\u58f0\u602a\u7b11\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6211\u8bf4\u4f60\u8fd9\u8001\u548c\u5c1a\u52a8\u4e86\u601c\u9999\u60dc\u7389\u4e4b\u5fc3\u662f\u4e0d\u662f\uff1f\u597d\uff0c\u597d\uff0c\u770b\u5728\u4f60\u7684 \u9762\u4e0a\uff0c\u6211\u4e0d\u6740\u4ed6\u5c31\u8fd9\u4e00\u50e7\u4e00\u9053\u8bf4\u8d77\u8bdd\u6765\uff0c\u5c31\u50cf\u662f\u5df2\u5c06\u5353\u957f\u537f\u7684\u751f\u6b7b\u4e4b\u4e8b\u634f\u5728\u638c\u5fc3\u4e00\u6837\uff0c\u5353\u957f \u537f\u4e0d\u7531\u5fc3\u4e2d\u5927\u6012\uff0c\u65b9\u5f85\u5389\u58f0\u53f1\u8d23\uff0c\u54ea\u77e5\u90a3\u7626\u957f\u50e7\u4eba\u7a81\u5730\u602a\u773c\u4e00\u7ffb\uff0c\u76ee\u5149\u51db\u7136\u5411\u9053\u4eba\u77aa\u4e86\u4e00 \u773c\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u8fd9\u8001\u9053\u600e\u5730\u8d8a\u8001\u8d8a\u4e0d\u6b63\u7ecf\uff0c\u54ea\u8fd8\u50cf\u4e2a\u51fa\u5bb6\u4eba\u7684\u6837\u5b50\u3002\u201d \u3000\u3000\u7ea2\u88f3\u5c11\u5973\u4e00\u4e2a\u4e2a\u5fcd\u4e0d\u4f4f\u201c\u5657\u54e7\u201d\u4e00\u58f0\uff0c\u7b11\u51fa\u58f0\u6765\uff0c\u90a3\u77ee\u80d6\u9053\u4eba\u773c\u775b\u4e0a\u7728\uff0c\u53c8\u8038\u8038\u80a9\u8180\uff0c \u505a\u4e86\u4e2a\u9b3c\u8138\u3002 \u3000\u3000\u4ed6\u9762\u4e0a\u80a5\u8089\u7d2f\u7d2f\uff0c\u8bf4\u8bdd\u7684\u65f6\u5019\uff0c\u8868\u60c5\u6781\u591a\uff0c\u90a3\u7626\u957f\u50e7\u4eba\u9762\u4e0a\u5374\u8fde\u4e00\u4e1d\u8089\u90fd\u6ca1\u6709\uff0c\u800c\u4e14\u6728 \u7136\u6ca1\u6709\u4efb\u4f55\u8868\u60c5\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u4e00\u9634\u4e00\u9633\uff0c\u5904\u5904\u90fd\u6781\u7aef\u76f8\u53cd\uff0c\u5374\u4e0d\u77e5\u600e\u5730\u7adf\u4f1a\u51d1\u5230\u4e00\u5904\uff0c\u4f46\u5353\u957f\u537f\u77e5\u9053\u81ea\u5df1\u6b64\u523b \u8eab\u5165\u864e\u7a74\uff0c\u8fd9\u4e24\u4eba\u5f62\u5bb9\u867d\u602a\u5f02\uff0c\u4f46\u6b66\u529f\u5b9a\u5fc5\u6781\u9ad8\uff0c\u4e5f\u5b9a\u5fc5\u5927\u6709\u6765\u5386\uff0c\u663e\u7136\u5c31\u662f\u4e11\u4eba\u6e29\u5982\u7389\u8bf7 \u6765\u7684\u4e45\u5df2\u5f52\u9690\u6d17\u624b\u7684\u9b54\u5934\u4e4b\u4e00\uff0c\u662f\u4ee5\u89c1\u4e86\u4ed6\u4e8c\u4eba\u8fd9\u79cd\u4e0d\u582a\u5165\u76ee\u7684\u6837\u5b50\uff0c\u5fc3\u91cc\u5e76\u65e0\u4e00\u4e1d\u8f7b\u8511\u4e4b \u610f\uff0c\u53cd\u800c\u5341\u5206\u6212\u5907\uff0c\u751a\u81f3\u8fde\u6012\u6c14\u90fd\u4e0d\u6562\u53d1\u4f5c\uff0c\u8981\u77e5\u9053\u9ad8\u624b\u5bf9\u654c\uff0c\u4e8b\u5148\u52a8\u6012\uff0c\u6b63\u662f\u72af\u4e86\u6b66\u5bb6\u4e2d \u7684\u5927\u5fcc\u3002 \u3000\u3000\u90a3\u7626\u50e7\u4eba\u76ee\u5149\u4e00\u8f6c\uff0c\u53cc\u76ee\u53c8\u81ea\u772f\u6210\u4e00\u7f1d\uff0c\u76ef\u5728\u5353\u957f\u537f\u8eab\u4e0a\uff0c\u63a5\u9053\uff1a\u201c\u8001\u8896\u867d\u7136\u4e0e\u4f60\u6280 \u7f18\uff0c\u4f46\u662f\u6b7b\u7f6a\u53ef\u514d\uff0c\u6cd5\u7f6a\u5374\u514d\u4e0d\u5f97\uff0c\u9664\u975e\u4f60\u80fd\u62dc\u5728\u8001\u8872\u95e8\u4e0b\uff0c\u90a3\u4e48\u8001\u8896\u4e0d\u4f46\u53ef\u4ee5\u4f20\u7ed9\u4f60\u4e00\u4e9b \u4f60\u8fde\u505a\u68a6\u90fd\u6ca1\u6709\u60f3\u5230\u8fc7\u7684\u529f\u592b\uff0c\u800c\u4e14\u8fd8\u53ef\u4ee5\u6559\u4f60\u4eab\u53d7\u4eba\u751f\u7684\u4e50\u8da3\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u5f3a\u81ea\u6309\u637a\u7740\u5fc3\u80f8\u4e4b\u95f4\u7684\u6012\u706b\uff0c\u5251\u7709\u8f69\u5904\uff0c\u4ef0\u5929\u72c2\u7b11\u9053\uff1a\u201c\u597d\u597d\uff0c\u8981\u53eb\u6211\u62dc\u5728\u4f60\u7684 \u95e8\u4e0b\uff0c\u4e5f\u5e76\u4e0d\u96be\uff0c\u53ea\u662f\u4f60\u5374\u5148\u8981\u8bf4\u8bf4\u4f60\u5012\u5e95\u662f\u8c01\uff1f\u4e5f\u8ba9\u6211\u770b\u770b\u62dc\u4f60\u4e3a\u5e08\u662f\u5426\u503c\u5f97\u3002\u201d \u3000\u3000\u7626\u957f\u50e7\u4eba\u9634\u607b\u607b\u4e00\u58f0\u957f\u7b11\uff0c\u7b11\u58f0\u4e00\u65e0\u8d77\u4f0f\uff0c\u4e5f\u4e0d\u77e5\u4ed6\u662f\u559c\u662f\u6012\u3002 \u3000\u3000\u591c\u98ce\u51db\u51db\uff0c\u518d\u52a0\u4e0a\u8fd9\u7b11\u58f0\uff0c\u4f7f\u5f97\u8fd9\u5bc2\u9759\u7684\u5c71\u9053\uff0c\u5e73\u6dfb\u4e86\u4e0d\u77e5\u51e0\u8bb8\u68ee\u68ee\u5bd2\u610f\uff0c\u53ea\u89c1\u8fd9\u7626\u957f \u6f33\u4eba\u4e00\u9762\u957f\u7b11\uff0c\u4e00\u9762\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u5e74\u7eaa\u8fd8\u592a\u8f7b\uff0c\u81ea\u7136\u4e0d\u77e5\u9053\u8001\u8896\u662f\u8c01\uff1f\u53ef\u662f\u4f60\u7684\u5e08\u957f\u96be\u9053 \u5c31\u4ece\u672a\u63d0\u8d77\u8fc7\u8001\u8896\u548c\u8fd9\u80d6\u9053\u4eba\u7684\u540d\u5b57\u3002\u201d \u3000\u3000\u7b11\u58f0\u7a81\u7136\u4e00\u655b\uff0c\u5353\u957f\u537f\u53ea\u542c\u201c\u545b\u5577\u201d\u4e00\u58f0\uff0c\u8fd9\u7626\u957f\u9053\u4eba\u53cd\u624b\u4e4b\u95f4\uff0c\u7adf\u81ea\u5c06\u4ed6\u8170\u95f4\u7684\u6212\u5200 \u62bd\u4e86\u51fa\u6765\uff0c\u8fce\u98ce\u4e00\u6296\uff0c\u5200\u5149\u5982\u96ea\uff0c\u8fd9\u53e3\u53c8\u72ed\u53c8\u957f\u7684\u6212\u5200\uff0c\u7adf\u7136\u957f\u8fbe\u4e94\u5c3a\uff0c\u6bd4\u5bfb\u5e38\u6212\u5200\u51e0\u4e4e\u957f \u4e86\u4e00\u534a\u3002 \u3000\u3000\u90a3\u77ee\u80d6\u9053\u4eba\u201c\u54e7\u201d\u5730\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u82e5\u662f\u8fd8\u4e0d\u77e5\u9053\uff0c\u6211\u8ba9\u4f60\u770b\u770b\u8fd9\u4e2a\u3002\u201d \u3000\u3000\u8bed\u58f0\u672a\u4e86\uff0c\u53c8\u662f\u201c\u545b\u5577\u201d\u4e00\u58f0\u6e05\u541f\uff0c\u5353\u957f\u537f\u53ea\u89c9\u773c\u524d\u5bd2\u5149\u66b4\u957f\uff0c\u8fd9\u77ee\u80d6\u9053\u4eba\u624b\u4e2d\u4fbf\u4e5f\u591a \u4e86\u4e00\u67c4\u6676\u5149\u83b9\u7136\u7684\u77ed\u5251\u3002 \u3000\u3000\u5947\u602a\u7684\u662f\u4ed6\u624b\u4e2d\u7684\u8fd9\u53e3\u5251\uff0c\u4e0d\u4f46\u5251\u8eab\u7279\u77ed\uff0c\u800c\u4e14\u53c8\u6241\u53c8\u5e73\uff0c\u8fde\u5251\u80cc\u90fd\u6ca1\u6709\uff0c\u5374\u53c8\u6bd4\u5bfb\u5e38 \u5229\u5251\u5bbd\u4e0a\u4e00\u500d\uff0c\u4e4d\u4e00\u770b\u53bb\uff0c\u7ade\u50cf\u662f\u6df7\u5143\u724c\u4e00\u7c7b\u7684\u5175\u5203\uff0c\u54ea\u91cc\u50cf\u662f\u5229\u5251\u3002 \u3000\u3000\u8fd9\u4e00\u9ad8\u4e00\u77ee\u3001\u4e00\uff0c\u80d6\u4e00\u7626\u7684\u4e24\u4e2a\u8be1\u5f02\u65e0\u6bd4\u7684\u50e7\u9053\u6240\u7528\u7684\u5175\u5203\uff0c\u7ade\u4e5f\u662f\u4e00\u957f\u4e00\u77ed\uff0c\u4e00\u5bbd\u4e00 \u7a84\uff0c\u5c31\u50cf\u662f\u4ed6\u4eec\u7684\u8eab\u5f62\u4e00\u6837\u3002 \u3000\u3000\u5353\u957f\u537f\u867d\u7136\u5bf9\u4e8e\u6b66\u5b66\u4e00\u9053\u7684\u77e5\u8bc6\u6781\u4e3a\u6e0a\u535a\uff0c\u53ef\u4e5f\u4ece\u672a\u89c1\u8fc7\u5982\u6b64\u5947\u602a\u7684\u5175\u5203\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c \u4e0d\u7531\u5446\u5446\u5730\u6123\u4f4f\u4e86\uff0c\u76ee\u5149\u77ac\u4e5f\u4e0d\u77ac\u5730\u77aa\u5728\u8fd9\u4e00\u50e7\u4e00\u9053\u624b\u4e2d\u7684\u4e00\u5200\u4e00\u5251\u4e0a\u3002 \u3000\u3000\u591c\u8272\u4e4b\u4e2d\uff0c\u53ea\u89c1\u8fd9\u4e00\u80a5\u4e00\u7626\u3001\u4e00\u9ad8\u4e00\u77ee\u3001\u4e00\u50e7\u4e00\u9053\u4e24\u4eba\u624b\u4e2d\u7684\u4e00\u957f\u4e00\u77ed\u3001\u4e00\u9614\u4e00\u7a84\u3001\u4e00 \u5200\u4e00\u5251\u4e24\u6837\u5175\u5203\uff0c\u4ff1\u90fd\u662f\u6676\u5149\u83b9\u7136\uff0c\u707f\u70c2\u5982\u94f6\uff0c\u6620\u5f97\u5353\u957f\u537f\u7684\u53cc\u773c\u90fd\u4f3c\u4e4e\u6cdb\u8d77\u4e86\u9635\u9635\u9752\u84dd\u7684 \u5149\u534e\u3002 \u3000\u3000\u77ee\u80d6\u9053\u4eba\u53c8\u662f\u201c\u54e7\u201d\u5730\u4e00\u58f0\u51b7\u7b11\uff0c\u624b\u81c2\u5fae\u6325\uff0c\u9752\u5149\u4e00\u63a0\u3002 \u3000\u3000\u4ed6\u77ee\u80d6\u800c\u81c3\u80bf\u7684\u8eab\u8eaf\uff0c\u5374\u975e\u5e38\u7075\u5de7\u7684\u5728\u5730\u9762\u4e0a\u79fb\u52a8\u4e86\u4e00\u4e2a\u4f4d\u7f6e\uff0c\u4e8e\u662f\u4ed6\u7684\u8eab\u8eaf\u8ddd\u79bb\u5353\u957f \u537f\u66f4\u8fd1\u4e86\uff0c\u51b7\u7b11\u7740\u559d\u9053\uff1a\u201c\u4f60\u8fd8\u672a\u60f3\u51fa\u6211\u4eec\u662f\u8c01\u5417\uff1f\u54fc\uff0c\u54fc\uff0c\u8fd9\u6837\u770b\u6765\uff0c\u4f60\u5e08\u7236\u4e5f\u662f\u4e2a\u5927\u5927 \u7684\u6a84\u6d82\u866b\uff0c\u8fde\u6211\u4eec\u4e24\u4eba\u7684\u540d\u5b57\u90fd\u4e0d\u5728\u4f60\u9762\u524d\u63d0\u63d0\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u5e7c\u906d\u60e8\u53d8\uff0c\u53cc\u4eb2\u7f79\u52ab\uff0c\u82e5\u4e0d\u662f\u4ed6\u6069\u5e08\u53f8\u7a7a\u8001\u4eba\uff0c\u7109\u6709\u4eca\u65e5\uff1f \u3000\u3000\u5e08\u6069\u65e2\u662f\u539a\u91cd\u5982\u5c71\uff0c\u4ed6\u5bf9\u53f8\u7a7a\u8001\u4eba\u7684\u60c5\u611f\uff0c\u81ea\u4e5f\u6781\u5176\u6df1\u539a\uff0c\u800c\u6b64\u523b\u542c\u89c1\u8fd9\u77ee\u80d6\u9053\u4eba\u7adf\u7136 \u8bf4\u51fa\u8fd9\u79cd\u8bdd\u6765\uff0c\u5fc3\u80f8\u4e4b\u4e2d\uff0c\u4e0d\u7981\u4e3a\u4e4b\u52c3\u7136\u5927\u6012\u3002 \u3000\u3000\u4f46\u662f\u5341\u6570\u5e74\u7684\u8270\u82e6\u78e8\u7ec3\u548c\u65e0\u6027\u7684\u6566\u539a\u8c28\u614e\uff0c\u81f4\u4f7f\u5f97\u4ed6\u5728\u6b64\u65f6\u6b64\u523b\uff0c\u8fd8\u80fd\u5fcd\u8010\u7740\u4e0d\u5c06\u5185\u5fc3 \u7684\u6124\u6012\u5316\u4e3a\u53e3\u5934\u7684\u6076\u9a82\u3002 \u3000\u3000\u4ed6\u53ea\u662f\u4ece\u9f3b\u5b54\u4e2d\u91cd\u91cd\u5730\u51b7\u51b7\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u76ee\u5149\u4e00\u7ffb\uff0c\u671b\u5411\u5929\u4e0a\uff0c\u751f\u50cf\u662f\u6839\u672c\u672a\u5c06\u8fd9\u4f3c \u724c\u77ed\u5251\uff0c\u5982\u97ad\u957f\u5200\uff0c\u4e24\u4ef6\u6b66\u6797\u7f55\u89c1\u7684\u5947\u5f62\u5175\u5203\uff0c\u548c\u8fd9\u4e00\u618e\u4e00\u9053\u4e24\u4e2a\u8be1\u5f02\u7684\u6b66\u6797\u9ad8\u624b\u653e\u5728\u5fc3 \u4e0a\u3002 \u3000\u3000\u8f7b\u8511\uff0c\u5bf9\u4e8e\u522b\u4eba\u65e0\u7406\u7684\u8fb1\u9a82\u6765\u8bf4\uff0c\u8be5\u7b97\u662f\u4e16\u95f4\u6700\u597d\u7684\u7b54\u590d\u4e86\u3002 \u3000\u3000\u8fd9\u79cd\u65e0\u8a00\u7684\u8f7b\u8511\uff0c\u679c\u7136\u4f7f\u5f97\u8fd9\u77ee\u80d6\u9053\u4eba\u591a\u8089\u800c\u5584\u4e8e\u53d8\u5316\u7684\u9762\u5e9e\u4e0a\u4e3a\u4e4b\u5927\u5927\u53d8\u4e86\u989c\u8272\uff0c\u539f \u6765\u8fd9\u4e00\u50e7\u4e00\u9053\u770b\u6765\u867d\u7136\u8a00\u4e0d\u51fa\u4f17\uff0c\u8c8c\u4e0d\u60ca\u4eba\uff0c\u4f46\u5374\u4e5f\u662f\u4e09\u5341\u5e74\u524d\u626c\u540d\u6b66\u6797\u3001\u53f1\u54a4\u6c5f\u6e56\u7684\u4eba \u7269\u3002 \u3000\u3000\u6614\u65e5\u8fd9\u80d6\u7626\u4e8c\u4eba\uff0c\u51fa\u6ca1\u4e8e\u6cb3\u6714\u9053\u4e0a\uff0c\u4ee5\u624b\u4e2d\u7684\u4e24\u4ef6\u5947\u5f62\u5175\u5203\uff0c\u5728\u6cb3\u6714\u9053\u4e0a\u7684\u786e\u66fe\u505a\u4e0b\u4e86 \u4e0d\u5c11\u60ca\u4eba\u4e4b\u4e8b\uff0c\u6b66\u6797\u4e2d\u4eba\u867d\u7136\u4e0d\u8bc6\u8fd9\u4e24\u4eba\u7684\u9762\u76ee\uff0c\u4f46\u63d0\u8d77\u724c\u5251\u97ad\u5200\u3001\u7626\u4f5b\u80d6\u4ed9\uff0c\u5374\u6781\u5c11\u6709\u4eba \u4e0d\u77e5\u9053\u7684\u3002\u8fd9\u539f\u56e0\u81ea\u7136\u56e0\u4e3a\u8fd9\u4e24\u4ef6\u5175\u5203\uff0c\u7684\u786e\u662f\u6b66\u6797\u7f55\u89c1\u4e4b\u7269\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u51fa\u8eab\u6d3e\u522b\u65e2\u4e0d\u76f8\u540c\uff0c\u751f\u6027\u4ea6\u662f\u8fe5\u7136\u800c\u5f02\uff0c\u80d6\u7eaf\u9633\u638c\u4e2d\u724c\u5251\uff0c\u827a\u51fa\u4e8e\u5c71\u4e1c\u7684\u7075\u9707\u5251 \u808c\u987e\u540d\u601d\u4e49\uff0c\u8d70\u7684\u81ea\u7136\u662f\u9633\u521a\u4e0a\u4e00\u8def\u5251\u6cd5\u3002\u800c\u90a3\u7626\u5f25\u9640\u5374\u662f\u4e94\u53f0\u7684\u5ae1\u4f20\u5f1f\u5b50\uff0c\u80d6\u7eaf\u9633\u8d2a\u5403\u8d2a \u8d22\uff0c\u7626\u5f25\u9640\u5374\u662f\u597d\u8272\u597d\u540d\uff0c\u4e24\u4eba\u51fa\u8eab\u751f\u6027\u90fd\u5927\u4e0d\u76f8\u540c\uff0c\u4f46\u591a\u5e74\u4ee5\u6765\uff0c\u8fd9\u4e24\u4eba\u5374\u4e00\u76f4\u662f\u751f\u6b7b\u8fc7 \u547d\u7684\u4ea4\u60c5\u3002 \u3000\u3000\u540e\u6765\u5353\u6d69\u7136\u5d1b\u8d77\u6b66\u6797\uff0c\u884c\u4fa0\u6c5f\u6e56\uff0c\u5728\u5f20\u5bb6\u53e3\u5916\uff0c\u9047\u7740\u8fd9\u4e24\u4eba\u6b63\u5728\u505a\u6848\uff0c\u800c\u4e14\u505a\u6848\u7684\u624b\u6bb5 \u5947\u6bd2\u5947\u8fa3\uff0c\u4e00\u6012\u4e4b\u4e0b\u4fbf\u4f38\u624b\u7ba1\u4e86\u8fd9\u8d9f\u4e8b\uff0c\u8fd9\u4e24\u4eba\u6b66\u529f\u867d\u9ad8\uff0c\u5374\u4e0d\u662f\u5353\u6d69\u7136\u7684\u654c\u624b\uff0c\u91cd\u521b\u4e4b \u4e0b\uff0c\u4fbf\u9690\u9041\u4e86\u3002 \u3000\u3000\u5341\u4f59\u5e74\u6765\uff0c\u4ed6\u4e24\u4eba\u4e00\u76f4\u672a\u5728\u6c5f\u6e56\u4e2d\u73b0\u8fc7\u884c\u8e2a\uff0c\u76f4\u5230\u6b64\u6b21\u7ea2\u8863\u5a18\u5a18\u4e11\u4eba\u6e29\u5982\u7389\u624d\u5c06\u8fd9\u4e24\u4e2a \u6614\u65e5\u79f0\u96c4\u4e00\u65f6\u7684\u5de8\u76d7\u627e\u4e86\u51fa\u6765\uff0c\u8fd9\u4e24\u4eba\u77e5\u9053\u5353\u6d69\u7136\u5df2\u6b7b\uff0c\u751a\u4e3a\u611f\u6fc0\u6e29\u5982\u7389\u4e3a\u4ed6\u4eec\u590d\u4e86\u4ec7\uff0c\u4fbf \u66ff\u5979\u5356\u8d77\u547d\u6765\uff0c\u53ea\u662f\u4ed6\u4eec\u5374\u4e5f\u672a\u66fe\u60f3\u5230\u6b64\u523b\u7ad9\u5728\u4ed6\u4eec\u9762\u524d\u7684\uff0c\u4fbf\u662f\u4e2d\u539f\u5927\u4fa0\u5353\u6d69\u7136\u7684\u7231\u5b50\u5353 \u957f\u537f\u3002 \u3000\u3000\u4ee5\u4ed6\u4eec\u8fd9\u79cd\u8eab\u4efd\u548c\u6b66\u529f\uff0c\u518d\u52a0\u4e0a\u8fd9\u5341\u4f59\u5e74\u7684\u82e6\u7ec3\uff0c\u4ed6\u4eec\u81ea\u7136\u4e0d\u4f1a\u5c06\u9762\u524d\u8fd9\u5f31\u51a0\u5c11\u5e74\u653e\u5728 \u5fc3\u4e0a\uff0c\u82e5\u4e0d\u662f\u7626\u5f25\u9640\u8fd9\u4e9b\u5e74\u9690\u4e8e\u8fb9\u8352\uff0c\u96be\u5bfb\u7edd\u8272\uff0c\u6b63\u5de7\u67d3\u4e0a\u4e86\u201c\u65ad\u91cc\u4e4b\u7663\uff0c\u4f59\u6311\u4e4b\u55dc\u201d\uff0c\u7adf \u5bf9\u9762\u524d\u7684\u715e\u661f\u52a8\u4e86\u6b32\u5ff5\uff0c\u4ed6\u4eec\u53ea\u6015\u4e5f\u65e9\u5df2\u52a8\u4e86\u6740\u624b\u4e86\u3002 \u3000\u3000\u80d6\u7eaf\u9633\u9762\u5bb9\u9aa4\u53d8\uff0c\u51b7\u7b11\u8fde\u8fde\uff0c\u7a81\u7136\u56de\u8fc7\u5934\u6765\uff0c\u5411\u90a3\u67af\u7626\u5982\u7af9\u7684\u50e7\u4eba\u7626\u5f25\u9640\u51b7\u7b11\u8bf4\u9053\uff1a \u201c\u8001\u548c\u5c1a\uff0c\u8fd9\u5c0f\u5b50\u867d\u7136\u751f\u7684\u4e0d\u9519\uff0c\u4f46\u6837\u5b50\u5374\u592a\u8ba8\u4eba\u538c\uff0c\u6211\u53ef\u8981\u5bf9\u4e0d\u4f4f\u4e86\uff0c\u62ff\u8fd9\u5c0f\u5b50\u6765\u5f00\u5341\u591a \u5e74\u6765\u7684\u6740\u6212\u4e86\u3002\u201d \u3000\u3000\u4ed6\u8bdd\u58f0\u65b9\u843d\uff0c\u7a81\u7136\u5927\u559d\u4e00\u58f0\uff0c\u53f3\u624b\u626c\u8d77\uff0c\u5251\u5149\u5982\u8679\uff0c\u5237\u5730\u4e00\u5251\uff0c\u4e94\u4e01\u5f00\u5c71\u5251\u52bf\u6709\u5982\u98ce\u4e91 \u4e4d\u8d77\uff0c\u5411\u5353\u957f\u537f\u5241\u53bb\u3002 \u3000\u3000\u4e00\u76f4\u9690\u5fcd\u7740\u5fc3\u4e2d\u6012\u706b\u7684\u5353\u957f\u537f\uff0c\u795e\u8272\u867d\u7136\u50cf\u662f\u672a\u5c06\u8fd9\u4e24\u4eba\u653e\u5728\u773c\u91cc\uff0c\u5176\u5b9e\u5374\u5df2\u65e9\u6709\u6212 \u5907\uff0c\u6b64\u523b\u76ee\u5149\u5fae\u77ac\u4e4b\u95f4\uff0c\u7626\u957f\u7684\u8eab\u5f62\uff0c\u4fbf\u51e0\u4e4e\u50cf\u4ed6\u76ee\u5149\u4e00\u6837\uff0c\u96ea\u6d8c\u5730\u5411\u5de6\u79fb\u5f00\u4e94\u5c3a\uff0c\u53f3\u638c\u4e00 \u4f38\uff0c\u7a81\u7136\u5e76\u6307\u5982\u5251\uff0c\u7535\u4e5f\u4f3c\u5730\u5411\u80d6\u7eaf\u9633\u53f3\u65f6\u95f4\u56de\u6c60\u5927\u7a74\u70b9\u53bb\u3002 \u3000\u3000\u7626\u5f25\u9640\u51b7\u773c\u65c1\u89c2\uff0c\u5353\u957f\u537f\u867d\u7136\u5982\u6b64\uff0c\u7626\u5f25\u9640\u5bf9\u4ed6\u5374\u5e76\u6ca1\u6709\u4ec0\u4e48\u6012\u610f\uff0c\u80d6\u7eaf\u9633\u867d\u7136\u51fa\u624b\uff0c \u7626\u5f25\u9640\u5fc3\u4e2d\u8fd8\u5728\u6697\u602a\u4ed6\u4e0d\u8be5\u5982\u6b64\u8fa3\u624b\u3002 \u3000\u3000\u4f46\u5353\u957f\u537f\u6b64\u523b\u8eab\u5f62\u4e00\u5c55\uff0c\u7626\u5f25\u9640\u67af\u7626\u7684\u9762\u5bb9\u4e0a\u5374\u4e5f\u4e0d\u7981\u4e3a\u4e4b\u53d8\u4e86\u989c\u8272\u3002 \u3000\u3000\u201c\u884c\u5bb6\u4e00\u4f38\u624b\uff0c\u4fbf\u77e5\u6709\u6ca1\u6709\u201d\uff0c\u867d\u7136\u662f\u4e00\u53e5\u901a\u4fd7\u5df2\u6781\u7684\u4fd7\u8bed\uff0c\u4f46\u8fd9\u53e5\u8bdd\u4e4b\u6240\u80fd\u591f\u5982\u6b64\u901a \u4fd7\uff0c\u5374\u662f\u56e0\u4e3a\u6b64\u8bdd\u5176\u4e2d\u7684\u786e\u542b\u8574\u7740\u4e0d\u53d8\u7684\u771f\u7406\uff0c\u4e00\u4e2a\u6b66\u529f\u5e73\u5e38\u7684\u4fd7\u624b\uff0c\u7eb5\u7136\u6709\u5fc3\u4f5c\u5185\u5bb6\u9ad8\u624b \u72b6\uff0c\u4f46\u5374\u96be\u4ee5\u7792\u5f97\u8fc7\u771f\u6b63\u6b66\u6797\u9ad8\u624b\u7684\u773c\u76ee\uff0c\u800c\u6b64\u523b\u5353\u957f\u537f\u51fa\u624b\u4e4b\u95f4\uff0c\u867d\u7136\u6709\u5fc3\u5c06\u81ea\u5df1\u6b66\u529f\u9690 \u85cf\u4e09\u5206\uff0c\u5374\u5df2\u662f\u591f\u4f7f\u5f97\u522b\u4eba\u4e3a\u4e4b\u5403\u60ca\u53d8\u8272\u7684\u4e86\u3002 \u3000\u3000\u80d6\u7eaf\u9633\u4e00\u62db\u843d\u7a7a\uff0c\u5fc3\u5934\u4ea6\u4e0d\u7981\u4e00\u9707\u3001\u4f46\u8fd9\u65f6\u4ed6\u5df2\u52a8\u4e86\u624b\uff0c\u54ea\u91cc\u8fd8\u6709\u65f6\u95f4\u5bb9\u4ed6\u6765\u601d\u7d22\u522b\u7684 \u95ee\u9898\uff0c\u53e3\u4e2d\u53c8\u81ea\u5927\u559d\u4e00\u58f0\uff0c\u7adf\u5c06\u81ea\u5df1\u65b9\u624d\u5df2\u7ecf\u9012\u51fa\u7684\u4e00\u62db\u4e94\u4e01\u5f00\u5c71\u786c\u751f\u751f\u64a4\u4e86\u53e3\u6765\uff0c\u5de6\u811a\u524d \u8e0f\u4e00\u6b65\uff0c\u53f3\u638c\u5251\u52bf\u6a2a\u5212\uff0c\u957f\u8679\u8d2f\u65e5\u5237\u5730\u53c8\u662f\u4e00\u62db\u7075\u9707\u5251\u6d3e\u4e2d\u7684\u7edd\u5999\u62db\u5f0f\u3002 \u3000\u3000\u6b64\u62db\u4e00\u51fa\uff0c\u5353\u957f\u537f\u5fc3\u4e2d\u5374\u4e0d\u7981\u5fae\u5fae\u6709\u4e9b\u5931\u671b\uff0c\u8981\u77e5\u9053\u957f\u8679\u8d2f\u65e5\u8fd9\u62db\u5251\u5f0f\uff0c\u867d\u7136\u9887\u4e3a\u7cbe \u5999\uff0c\u4f46\u8fd9\u80d6\u7eaf\u9633\u624b\u4e2d\u6240\u6301\u7684\u5175\u5203\uff0c\u957f\u4e0d\u53ca\u4e24\u5c3a\uff0c\u4ee5\u8fd9\u79cd\u5175\u5203\u6765\u65bd\u5c55\u8fd9\u79cd\u62db\u5f0f\uff0c\u5728\u5353\u957f\u537f\u773c\u4e2d \u770b\u6765\uff0c\u4e0d\u4f46\u6beb\u65e0\u5a01\u529b\uff0c\u800c\u4e14\u7834\u7efd\u767e\u51fa\u3002 \u3000\u3000\u4ed6\u5148\u524d\u539f\u672c\u5c06\u8fd9\u4e24\u4eba\u4f30\u8ba1\u5f97\u6781\u9ad8\uff0c\u6b64\u523b\u89c1\u4e86\u77ee\u80d6\u4eba\u7adf\u65bd\u51fa\u8fd9\u79cd\u62db\u5f0f\u6765\uff0c\u4fbf\u4e0d\u7981\u6709\u4e9b\u513f\u5931 \u671b\uff0c\u53e3\u4e2d\u51b7\u7b11\u4e00\u58f0\uff0c\u624b\u638c\u968f\u610f\u6298\u51fa\uff0c\u4e94\u6307\u4f38\u5f20\u5982\u722a\uff0c\u968f\u7740\u8fd9\u4e00\u62db\u957f\u8679\u8d2f\u65e5\u7684\u53bb\u52bf\uff0c\u5411\u80d6\u7eaf\u9633 \u624b\u8155\u6293\u53bb\uff0c\u80f8\u819b\u5fae\u7f29\u95f4\uff0c\u4fbf\u5df2\u907f\u5f00\u4e86\u5251\u950b\u3002 \u3000\u3000\u54ea\u77e5\u2014\u2014 \u3000\u3000\u957f\u8679\u8d2f\u65e5\u4e00\u62db\u5251\u5230\u4e2d\u9014\uff0c\u62db\u5f0f\u5c1a\u672a\u9012\u6ee1\uff0c\u8fd9\u53ea\u5982\u724c\u77ed\u5251\uff0c\u7a81\u7136\u53d8\u6325\u4e3a\u62cd\uff0c\u201c\u7830\u201d\u5730\u4e00 \u58f0\uff0c\u62cd\u5411\u5353\u957f\u537f\u4e0b\u8179\u3002 \u3000\u3000\u8fd9\u4e00\u62db\u4e0d\u4f46\u53d8\u62db\u4e4b\u5feb\uff0c\u5feb\u5982\u95ea\u7535\uff0c\u800c\u4e14\u5927\u51fa\u5353\u957f\u537f\u610f\u6599\u4e4b\u5916\uff0c\u4e5f\u5168\u7136\u6709\u5f02\u4e8e\u6b66\u5b66\u62db\u5f0f\u7684 \u89c4\u8303\uff0c\u7626\u5f25\u9640\u773c\u7751\u4f4e\u5782\uff0c\u4f4e\u5ff5\u4e00\u58f0\uff1a\u201c\u963f\u5f25\u9640\u4f5b\uff01\u201d \u3000\u3000\u7ad9\u5728\u4e00\u65c1\u7684\u7ea2\u88f3\u5c11\u5973\u4eec\uff0c\u4e5f\u81ea\u4e00\u58f0\u5a07\u55d4\uff0c\u773c\u770b\u8fd9\u82f1\u4fca\u5c11\u5e74\uff0c\u4fbf\u8981\u6bc1\u5728\u8fd9\u4e00\u67c4\u6614\u65e5\u540d\u9707\u6cb3 \u6714\u3001\u626c\u5a01\u6b66\u6797\u7684\u724c\u5251\u4e4b\u4e0b\u3002 \u3000\u3000\u54ea\u77e5\u4ed6\u4f5b\u53f7\u5c1a\u672a\u5ff5\u5b8c\uff0c\u53ea\u542c\u201c\u94c3\u201d\u7684\u4e00\u58f0\u6e05\u9e23\u3002 \u3000\u3000\u63a5\u7740\uff0c\u90a3\u80d6\u7eaf\u9633\u7adf\u8e6c\u8e6c\u8fde\u9000\u6570\u6b65\uff0c\u638c\u4e2d\u77ed\u5251\u659c\u626c\uff0c\u9669\u4e9b\u8131\u624b\u98de\u53bb\uff0c\u4ed6\u77ee\u80d6\u7684\u8eab\u5f62\uff0c\u4e5f\u9669 \u4e9b\u7acb\u8db3\u4e0d\u7a33\uff0c\u8dcc\u5230\u5730\u4e0a\u3002 \u3000\u3000\u5353\u957f\u537f\u773c\u770b\u8fd9\u53ea\u5947\u5f62\u724c\u5251\u5df2\u5c06\u62cd\u5728\u81ea\u5df1\u8eab\u4e0a\uff0c\u5fc3\u4e2d\u4ea6\u4e3a\u4e4b\u4e00\u60ca\uff0c\u4f46\u4ed6\u591a\u5e74\u82e6\u7ec3\uff0c\u867d\u60ca\u4e0d \u4e71\uff0c\u624b\u638c\u7a81\u7136\u4e00\u5708\uff0c\u4e94\u6307\u9f50\u5730\u5f39\u51fa\uff0c\u201c\u6323\u201d\u7684\u4e00\u58f0\uff0c\u7adf\u5c06\u80d6\u7eaf\u9633\u8fde\u4eba\u5e26\u5251\u9707\u51fa\u6570\u6b65\uff0c\u82e5\u4e0d\u662f \u80d6\u7eaf\u9633\u4ea6\u662f\u5185\u5916\u517c\u4fee\u7684\u5185\u5bb6\u9ad8\u624b\uff0c\u6b64\u523b\u4e0d\u4f46\u8981\u88ab\u8fd9\u4e00\u62db\u7edd\u6280\u9707\u98de\u624b\u4e2d\u957f\u5251\uff0c\u53ea\u6015\u8fde\u864e\u53e3\u4e5f\u8981 \u88ab\u9707\u88c2\uff0c\u5353\u957f\u537f\u4e00\u62db\u5f97\u624b\uff0c\u5374\u5e76\u4e0d\u8ddf\u8e2a\u8fdb\u51fb\uff0c\u4ee5\u62a2\u5148\u673a\uff0c\u53ea\u662f\u51b7\u7b11\u4e00\u58f0\uff0c\u8f7b\u8511\u7684\u8bf4\u9053\uff1a\u201c\u539f \u6765\u4e5f\u4e0d\u8fc7\u5982\u6b64\uff01\u201d \u3000\u3000\u80d6\u7eaf\u9633\u8fde\u9000\u6570\u6b65\uff0c\u65b9\u81ea\u62ff\u6869\u7ad9\u7a33\u8eab\u5f62\uff0c\u53ea\u542c\u56db\u4e0b\u7684\u7ea2\u88f3\u5c11\u5973\u60ca\u53f9\u4e4b\u58f0\u4e0d\u7edd\uff0c\u518d\u542c\u4e86\u5353\u957f \u537f\u5982\u6b64\u8f7b\u8511\u7684\u8bdd\uff0c\u4ed6\u5fc3\u4e2d\u65e2\u7f9e\u4e14\u6012\uff0c\u65b9\u624d\u4ed6\u773c\u770b\u81ea\u5df1\u4e00\u62db\u5df2\u5c06\u5f97\u624b\uff0c\u6b64\u523b\u4ed6\u8fde\u81ea\u5df1\u662f\u5982\u4f55\u8f93 \u7684\u62db\u90fd\u4e0d\u77e5\u9053\uff0c\u8981\u77e5\u9053\u5353\u957f\u537f\u65b9\u624b\u4e94\u6307\u659c\u98de\u4e00\u5f39\uff0c\u6b63\u662f\u53f8\u7a7a\u8001\u4eba\u7a77\u7814\u5965\u79d8\uff0c\u5c06\u8fbe\u6469\u7edd\u6280\u5f39\u6307 \u795e\u901a\u5316\u6210\u7684\u4e00\u62db\uff0c\u4e0d\u4f46\u8fd9\u8eab\u5386\u5176\u5883\u7684\u80d6\u7eaf\u9633\u770b\u4e0d\u6e05\u8fd9\u4e00\u62db\u7684\u6765\u5386\u53d8\u5316\uff0c\u5c31\u662f\u4e00\u65c1\u89c2\u6218\u7684\u7ea2\u88f3 \u5c11\u5973\u548c\u7626\u5f25\u9640\uff0c\u867d\u7136\u76ee\u5149\u4e00\u76f4\u77ac\u4e5f\u4e0d\u77ac\u5730\u671b\u7740\uff0c\u5374\u4e5f\u672a\u770b\u6e05\u8fd9\u4e00\u62db\u7684\u53d8\u5316\u3002 \u3000\u3000\u591c\u8272\u4e4b\u4e0b\uff0c\u53ea\u89c1\u8fd9\u80d6\u7eaf\u9633\u591a\u8089\u7684\u9762\u5e9e\u4e0a\u6a2a\u751f\u7684\u80a5\u8089\uff0c\u7adf\u4f3c\u8d77\u4e86\u9635\u9635\u62bd\u52a8\uff0c\u800c\u8fd9\u80a5\u8089\u4e0a\u6cdb \u8d77\u7684\u6cb9\u5149\uff0c\u4f3c\u4e4e\u53d8\u6210\u4e86\u6de1\u9752\u7684\u989c\u8272\uff0c\u4ed6\u53cc\u76ee\u5982\u706b\uff0c\u72e0\u72e0\u77aa\u7740\u51b7\u7b11\u4e0d\u7edd\u7684\u5353\u957f\u537f\uff0c\u5c31\u50cf\u662f\u4e00\u53ea \u521a\u4ece\u6cb3\u91cc\u635e\u8d77\u672a\u7684\u6bd4\u76ee\u80a5\u9c7c\u4e00\u6837\u3002 \u3000\u3000\u5353\u957f\u537f\u5374\u8fde\u773c\u89d2\u4e5f\u4e0d\u671b\u4ed6\u4e00\u773c\uff0c\u5374\u5bf9\u90a3\u67af\u7626\u5982\u7af9\u7684\u50e7\u4eba\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u5982\u53e6\u6709\u795e\u901a\uff0c\u4e0d\u59a8 \u4e5f\u6765\u8bd5\u8bd5\uff0c\u54fc\u54fc\uff0c\u770b\u4eca\u65e5\u6b64\u523b\uff0c\u7a76\u7adf\u662f\u8c01\u8981\u5f53\u8c01\u7684\u5f92\u5f1f\u3002\u201d \u3000\u3000\u8bed\u58f0\u672a\u4e86\uff0c\u80d6\u7eaf\u9633\u7a81\u7136\u5389\u543c\u4e00\u58f0\uff0c\u5353\u957f\u537f\u659c\u773c\u671b\u53bb\uff0c\u53ea\u89c1\u8fd9\u77ee\u80d6\u9053\u4eba\u7684\u4e00\u8eab\u80a5\u8089\u4e0a\u7a7f\u7740 \u7684\u90a3\u4ef6\u53c8\u7d27\u53c8\u77ed\u7684\u9053\u888d\uff0c\u7adf\u968f\u7740\u4ed6\u8fd9\u4e00\u58f0\u5389\u543c\uff0c\u201c\u5636\u201d\u5730\u88c2\u6210\u4e24\u534a\uff0c\u80d6\u7eaf\u9633\u5de6\u624b\u4e00\u6293\uff0c\u7adf\u5c06 \u8fd9\u4ef6\u9053\u888d\u6495\u4e86\u4e0b\u6765\uff0c\u91cd\u91cd\u4e00\u63b7\uff0c\u63b7\u5728\u5730\u4e0a\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u8eab\u4e0a\u5c31\u53ea\u5269\u4e0b\u4e86\u4e00\u6761\u9752\u5e03\u957f\u88e4\uff0c\u7d27\u7d27\u88f9\u7740\u4ed6\u90a3\u4e24\u6761\u7c97\u77ed\u7684\u8c61\u817f\uff0c\u800c\u4ed6\u8eab\u4e0a\u7684\u4e00\u8eab \u80a5\u8089\uff0c\u5374\u4e0d\u4f4f\u5730\u98a4\u6296\u7740\uff0c\u5728\u591c\u8272\u4e4b\u4e2d\u671b\u53bb\uff0c\u6d3b\u50cf\u662f\u79e6\u6dee\u4e0b\u6e38\u6c61\u79fd\u5f97\u4f7f\u4eba\u53d1\u5455\u7684\u6ce2\u6d6a\u3002 \u3000\u3000\u7ea2\u88f3\u5c11\u5973\u9f50\u5730\u4e00\u58f0\u5a07\u55d4\uff0c\u4f38\u4e86\u738b\u638c\uff0c\u63a9\u4f4f\u773c\u5e18\uff0c\u5353\u957f\u537f\u51b7\u7b11\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u662f\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u8fd9\u5176\u4e2d\u53ea\u6709\u7626\u5f25\u9640\u77e5\u9053\uff0c\u4ed6\u7684\u4f19\u4f34\u6b64\u523b\u5df2\u52a8\u4e86\u771f\u6012\uff0c\u82e5\u6ca1\u6709\u522b\u4eba\u7684\u9c9c\u8840\u67d3\u7ea2\u4ed6\u8eab\u4e0a\u7684\u80a5 \u8089\uff0c\u53ea\u6015\u4ed6\u8fd9\u6012\u6c14\u6c38\u8fdc\u4e0d\u4f1a\u6d88\u5931\u3002 \u3000\u3000\u5353\u957f\u537f\u53e3\u4e2d\u867d\u5728\u51b7\u7b11\uff0c\u5176\u5b9e\u4ed6\u5fc3\u4e2d\u5374\u53c8\u5927\u8d77\u6212\u5907\u4e4b\u5fc3\uff0c\u770b\u5230\u8fd9\u80d6\u7eaf\u9633\u8fd9\u79cd\u53ef\u7b11\u4e4b\u6001\uff0c\u5fc3 \u4e2d\u5e76\u6ca1\u6709\u534a\u5206\u53ef\u7b11\u4e4b\u610f\u3002 \u3000\u3000\u53ea\u89c1\u80d6\u7eaf\u9633\u8eab\u4e0a\u7684\u80a5\u8089\uff0c\u8d8a\u98a4\u8d8a\u6025\uff0c\u53cc\u76ee\u7684\u76ee\u5149\u4e5f\u8d8a\u6765\u8d8a\u72e0\uff0c\u800c\u4ed6\u53e3\u4e2d\u7684\u5389\u543c\u58f0\u5374\u9010\u6e10 \u4f4e\u5fae\u3002 \u3000\u3000\u4e8e\u662f\uff0c\u4ed6\u7c97\u77ed\u7684\u8c61\u817f\uff0c\u4fbf\u5f00\u59cb\u79fb\u52a8\u8d77\u6765\u3002\u4f46\u5374\u53c8\u79fb\u52a8\u5f97\u90a3\u4e48\u7f13\u6162\uff0c\u90a3\u4e48\u6c89\u91cd\uff0c\u5353\u957f\u537f\u76ee \u5149\u52a8\u5904\uff0c\u5fc3\u5934\u4e0d\u7981\u4e3a\u4e4b\u4e00\u51db\u3002 \u3000\u3000\u539f\u6765\u4ed6\u76ee\u529b\u5927\u5f02\u5e38\u4eba\uff0c\u5728\u8fd9\u6df1\u591c\u4e4b\u4e2d\u4e5f\u80fd\u770b\u51fa\u8fd9\u80d6\u9053\u4eba\u7684\u811a\u6b65\u6bcf\u4e00\u79fb\u52a8\uff0c\u7adf\u5728\u8fd9\u575a\u5b9e\u7684 \u5c71\u8def\u4e0a\uff0c\u7559\u4e0b\u4e00\u4e2a\u6df1\u6df1\u7684\u811a\u5370\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u7626\u957f\u800c\u6f47\u6d12\u7684\u8eab\u5f62\uff0c\u5374\u4ecd\u5353\u7acb\u5982\u5c71\u77f3\uff0c\u4ed6\u660e\u9510\u7684\u76ee\u5149\uff0c\u4e5f\u77ac\u4e5f\u4e0d\u77ac\u5730\u671b\u5728\u8fd9\u5f20\u4e11 \u964b\uff0c\u591a\u8089\uff0c\u800c\u6ee1\u542b\u6012\u610f\u7684\u9762\u5e9e\u3002 \u3000\u3000\u53ea\u89c1\u8fd9\u9762\u5e9e\u8ddd\u79bb\u81ea\u5df1\u8d8a\u6765\u8d8a\u8fd1\u2014\u2014 \u3000\u3000\u90a3\u91cd\u6e34\u7684\u547c\u5438\u58f0\uff0c\u542c\u6765\u4e5f\u50cf\u732a\u680f\u91cc\u7684\u4f4e\u9e23\uff0c\u53d8\u4e3a\u9634\u7a7a\u4e2d\u7684\u95f7\u96f7\u3002 \u3000\u3000\u90a3\u4e9b\u7ea2\u88f3\u5c11\u5973\uff0c\u5fcd\u4e0d\u4f4f\u79fb\u5f00\u63a9\u5728\u773c\u5e18\u4e0a\u7684\u7389\u624b\uff0c\u62ac\u76ee\u671b\u53bb\u3002 \u3000\u3000\u773c\u524d\u5251\u5149\u5ffd\u7136\u4e00\u4eae\u4e00 \u3000\u3000\u5353\u957f\u537f\u53ea\u89c9\u4e00\u9053\u91cd\u5982\u5c71\u5cb3\u7684\u98ce\u58f0\uff0c\u968f\u7740\u8fd9\u77ee\u80d6\u9053\u4eba\u7f13\u7f13\u6325\u52a8\u7684\u724c\u5251\uff0c\u5411\u81ea\u5df1\u5f53\u5934\u538b\u4e0b\u3002 \u3000\u3000\u800c\u5c31\u5728\u8fd9\u540c\u4e00\u5239\u90a3\u91cc\uff0c\u7626\u5f25\u9640\u7a81\u7136\u8eab\u5f62\u7a81\u8d77\uff0c\u5374\u4e5f\u63a0\u5411\u5353\u957f\u537f\u8eab\u540e\uff0c\u7075\u53f0\u98de\u7011\u3001\u5929\u7ec5\u5012 \u6302\uff0c\u201c\u5237\u5237\u201d\u4e24\u5200\uff0c\u7535\u4e5f\u4f3c\u7684\u5411\u5353\u957f\u537f\u80cc\u540e\u810a\u5173\u8282\u4e4b\u5904\u523a\u53bb\u3002 \u3000\u3000\u5353\u957f\u537f\u53cc\u638c\u4e00\u7ffb\uff0c\u500f\u7136\u8f6c\u8eab\uff0c\u811a\u4e0b\u6709\u5982\u7075\u9e6d\u5544\u9c7c\uff0c\u8fde\u8e29\u4e03\u6b65\u3002 \u3000\u3000\u811a\u6b65\u662f\u7ec6\u788e\u800c\u7e41\u6742\u7684\uff0c\u4ed6\u7626\u957f\u7684\u8eab\u5f62\uff0c\u4fbf\u5728\u8fd9\u7edd\u5999\u7684\u6b65\u6cd5\u95f4\u6ede\u6d12\u5730\u907f\u5f00\u4e86\u8fd9\u524d\u540e\u4e09\u62db\u3002 \u3000\u3000\u54ea\u77e5\uff0c\u80d6\u7eaf\u9633\u751f\u50cf\u662f\u65e9\u5c31\u77e5\u9053\u81ea\u5df1\u8fd9\u4e00\u655b\u523a\u4e0d\u4e2d\u4eba\u5bb6\u4f3c\u7684\uff0c\u76ee\u5149\u53ea\u7ba1\u6ce8\u5b9a\u5728\u5353\u957f\u537f\u8eab \u540e\uff0c\u4ed6\u4e00\u62db\u843d\u7a7a\uff0c\u76ee\u5149\u5374\u77ac\u4e5f\u4e0d\u77ac\uff0c\u7a81\u7136\u624b\u8155\u4e00\u7ffb\uff0c\u6251\u5730\u4e00\u5251\uff0c\u5411\u5353\u957f\u537f\u5de6\u80f8\u523a\u53bb\u3002 \u3000\u3000\u65b9\u624d\u4ed6\u90a3\u4e00\u5251\u4f3c\u7f13\u6162\u53c8\u6c89\u91cd\uff0c\u6b64\u523b\u8fd9\u4e00\u5251\u5374\u7094\u6377\u65e0\u6bd4\uff1a\u5353\u957f\u537f\u5fc3\u4e2d\u4e00\u60ca\u4e4b\u4e0b\uff0c\u53ea\u5f97\u5411\u53f3 \u4e00\u907f\uff0c\u54ea\u77e5\u90a3\u67af\u7626\u548c\u5c1a\u4e0e\u8fd9\u77ee\u80d6\u9053\u4eba\u6b66\u529f\u7adf\u914d\u5408\u5f97\u4e1d\u4e1d\u4eba\u6263\uff0c\u867d\u5206\u8fdb\u5374\u5982\u5408\u51fb\uff0c\u7adf\u500f\u7136\u4e00 \u5200\uff0c\u81ea\u53f3\u5411\u5de6\uff0c\u8fd9\u4e00\u5200\u4e00\u5251\u7adf\u5c06\u5353\u957f\u537f\u62e6\u5728\u4e2d\u95f4\uff0c\u5353\u957f\u537f\u82e5\u8981\u5411\u5de6\u9000\uff0c\u90a3\u724c\u5251\u5c31\u5728\u90a3\u8fb9\uff0c\u4f46 \u4ed6\u5982\u60f3\u53f3\u8fdb\uff0c\u5374\u53c8\u6709\u5982\u957f\u97ad\u7684\u5229\u5203\u6321\u5728\u524d\u9762\u3002 \u3000\u3000\u8fd9\u4e24\u62db\uff0c\u4e00\u62db\u7531\u5de6\u5411\u53f3\uff0c\u4e00\u62db\u81ea\u53f3\u5411\u5de6\uff0c\u867d\u4f3c\u4e24\u62db\uff0c\u6b63\u662f\u4e94\u53f0\u5251\u6d3e\u4e2d\u7684\u7edd\u6280\u5927\u95e9\u95e8\u5f0f\u52a0 \u4ee5\u53d8\u5316\u800c\u6210\u7684\u3002 \u3000\u3000\u5353\u957f\u537f\u867d\u7136\u6b66\u529f\u6df1\u4e0d\u53ef\u6d4b\uff0c\u4f46\u521d\u9047\u8fd9\u62db\uff0c\u5fc3\u4e2d\u4ea6\u4e0d\u7981\u4e00\u60ca\uff0c\u7a81\u7136\u53f3\u638c\u4e00\u6325\uff0c\u4e94\u6307\u9f50\u5f39\uff0c \u53ea\u542c\u53c8\u662f\u201c\u94ee\u3001\u94ee\u201d\u4e24\u54cd\uff0c\u4e00\u5200\u4e00\u5251\u53c8\u81ea\u9707\u5f00\uff0c\u53ea\u662f\u4ed6\u8fd9\u4e00\u62db\u53d1\u62db\u524d\u5e76\u4e0d\u51c6\u5907\uff0c\u662f\u4ee5\u51fa\u624b\u5e76 \u4e0d\u91cd\uff0c\u5426\u5219\u4fbf\u53c8\u5f97\u5c06\u8fd9\u4e00\u50e7\u4e00\u9053\u7684\u8eab\u5f62\u9707\u9000\u3002 \u3000\u3000\u724c\u5251\u97ad\u5200\uff0c\u80d6\u4ed9\u7626\u4f5b\u89c1\u81ea\u5df1\u82e6\u7ec3\u591a\u5e74\u7684\u7edd\u62db\uff0c\u6b64\u523b\u7adf\u53c8\u88ab\u4eba\u5bb6\u8f7b\u8f7b\u6613\u6613\u7684\u4e00\u6307\u5f39\u5f00\uff0c\u5fc3 \u4e2d\u8c05\u9a87\u65e0\u6bd4\uff0c\u4f46\u5374\u7edd\u4e0d\u8fdf\u7591\uff0c\u80d6\u7eaf\u9633\u54fc\u5730\u4e00\u58f0\uff0c\u77ed\u5251\u4e00\u504f\uff0c\u63a2\u6d77\u5c60\u9f99\u7adf\u659c\u659c\u524a\u5411\u5353\u957f\u537f\u4e0b \u76d8\uff0c\u7626\u5f25\u9640\u957f\u5200\u6a2a\u626b\uff0c\u5374\u662f\u4e00\u62db\u65e0\u98ce\u626b\u53f6\uff0c\u547c\u5730\u4e00\u5200\uff0c\u75be\u7136\u524a\u5411\u5353\u957f\u537f\u5de6\u80a9\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u65b9\u624d\u4e24\u62db\u4e00\u5de6\u4e00\u53f3\uff0c\u6b64\u523b\u4e24\u62db\u5374\u662f\u4e00\u4e0a\u4e00\u4e0b\uff0c\u62db\u62db\u4ff1\u662f\u72e0\u731b\u65e0\u6bd4\uff0c\u800c\u4e14\u53d8\u62db\u66f4\u662f \u5feb\u5982\u95ea\u7535\uff0c\u5353\u957f\u537f\u4ee5\u4e00\u654c\u4e8c\uff0c\u773c\u770b\u50cf\u662f\u53ea\u6709\u62db\u67b6\u4e4b\u529f\uff0c\u800c\u65e0\u8fd8\u624b\u4e4b\u529b\uff0c\u90a3\u4e9b\u7ea2\u88f3\u5c11\u5973\u5728\u591c\u8272 \u4e2d\u4e5f\u770b\u4e0d\u751a\u6e05\u695a\uff0c\u53ea\u770b\u5230\u4e24\u9053\u5149\u534e\uff0c\u76f4\u4e0a\u76f4\u4e0b\u5730\u5288\u5411\u5353\u957f\u537f\uff0c\u4e24\u4e2a\u7167\u9762\u8fc7\u53bb\uff0c\u5353\u957f\u537f\u7adf\u8fde\u4e00 \u62db\u4e5f\u6ca1\u6709\u8fd8\u51fa\uff0c\u5fc3\u4e0b\u53c8\u662f\u9ad8\u5174\uff0c\u53c8\u662f\u53ef\u60dc\uff0c\u9ad8\u5174\u7684\u662f\u773c\u89c1\u81ea\u5df1\u4eba\u5f97\u80dc\uff0c\u53ef\u60dc\u7684\u5374\u662f\u8fd9\u5c11\u5e74\u4eba \u54c1\u65e2\u4f73\uff0c\u5e74\u7eaa\u8fd8\u8f7b\uff0c\u6b7b\u4e86\u771f\u6709\u70b9\u51a4\u6789\u3002 \u3000\u3000\u54ea\u77e5\u5353\u957f\u537f\u6210\u7af9\u5728\u80f8\uff0c\u770b\u4e86\u8fd9\u618e\u9053\u4e24\u4eba\u7684\u8fd9\u79cd\u72e0\u8fa3\u7684\u62db\u5f0f\uff0c\u5fc3\u4e2d\u5374\u6709\u4e9b\u7740\u607c\uff1a\u201c\u6211\u4e0e\u4f60 \u4e8c\u4eba\u65e0\u51a4\u65e0\u4ec7\uff0c\u4f60\u4f55\u4ee5\u4e0b\u6b64\u6740\u624b\uff1f\u60f3\u6765\u4f60\u4eec\u5e73\u65e5\u5fc5\u5b9a\u662f\u6bd2\u8fa3\u6210\u6027\u3002\u201d \u3000\u3000\u5f53\u4e0b\u8eab\u8eaf\u5fae\u4fa7\uff0c\u5de6\u624b\u7a81\u7136\u95ea\u7535\u4f38\u51fa\uff0c\u7adf\u642d\u4e0a\u4e86\u80d6\u7eaf\u9633\u624b\u4e2d\u7684\u5251\u67c4\uff0c\u8f7b\u8f7b\u7684\u5411\u5de6\u4e00\u63a8\uff0c\u80d6 \u7eaf\u9633\u5927\u60ca\u4e4b\u4e0b\uff0c\u53ea\u89c9\u4e00\u822c\u5927\u529b\u6d8c\u51fa\uff0c\u638c\u4e2d\u5251\u5203\u7adf\u4e0d\u7531\u81ea\u4e3b\u5730\u987a\u7740\u4ed6\u624b\u52bf\u6251\u5212\u8fc7\u53bb\uff0c\u201c\u5f53\u201d\u7684 \u4e00\u58f0\uff0c\u7adf\u4e0e\u7626\u5f25\u9640\u957f\u5200\u76f8\u4ea4\uff0c\u88ab\u5353\u957f\u537f\u67b6\u5f00\u4e86\u4e00\u62db\u3002 \u3000\u3000\u5353\u957f\u537f\u8fd9\u4e00\u624b\u4ee5\u654c\u653b\u654c\u4e4b\u6280\uff0c\u867d\u7136\u4eff\u4f5b\u662f\u592a\u6781\u95e8\u4e2d\u7684\u7275\u7f18\u624b\u529f\u592b\uff0c\u7136\u5176\u4e2d\u5374\u6e17\u63c9\u4e86\u201c\u6b66 \u5f53\u201d\u7a7a\u624b\u5165\u81ea\u5203\u7684\u529f\u592b\uff0c\u83ab\u8bf4\u5bf9\u624b\u53ea\u6709\u4e24\u4eba\uff0c\u7eb5\u6709\u5341\u4eba\u516b\u4eba\u7684\u5200\u5251\u4e00\u8d77\u653b\u6765\uff0c\u4ed6\u4e5f\u80fd\u4ee5\u654c\u4eba \u4e4b\u5200\u653b\u654c\u4eba\u4e4b\u5251\uff0c\u518d\u4ee5\u654c\u4eba\u4e4b\u5251\u67b6\u654c\u4eba\u4e4b\u5200\u3002 \u3000\u3000\u4ed6\u9732\u4e86\u8fd9\u624b\u7edd\u6280\uff0c\u90a3\u4e9b\u7ea2\u88f3\u5c11\u5973\u5374\u770b\u5f97\u66f4\u662f\u83ab\u540d\u5176\u5999\uff0c\u8981\u77e5\u9053\u5979\u4eec\u867d\u4f1a\u6b66\u529f\uff0c\u4f46\u529f\u592b\u4e0d \u6df1\uff0c\u600e\u4f1a\u770b\u5f97\u51fa\u8fd9\u79cd\u6df7\u5408\u4e86\u4e24\u79cd\u529f\u592b\u7684\u5185\u5bb6\u7edd\u6280\uff0c\u5927\u5bb6\u5bf9\u671b\u4e00\u773c\uff0c\u7adf\u90fd\u88ab\u60ca\u5f97\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u9a7e\u8f66\u7684\u8f66\u592b\u770b\u5f97\u624b\u8155\u53d1\u9ebb\uff0c\u7adf\u4e0d\u89c9\u5c06\u7f30\u7ef3\u4e00\u677e\uff0c\u62c9\u8f66\u7684\u9a6c\u65e9\u5df2\u88ab\u8fd9\u9635\u5200\u5149\u5251\u5f71\u60ca\u5f97\u4e0d\u4f4f \u957f\u5636\uff0c\u6b64\u523b\u4fbf\u201c\u5636\u201d\u5730\u5411\u5c71\u4e0a\u51b2\u4e86\u8fc7\u53bb\uff0c\u4f46\u6b64\u884c\u9053\u4e0a\uff0c\u884c\u4e0a\u4e0d\u6613\uff0c\u5b83\u51b2\u4e86\u4e24\u6b65\uff0c\u53c8\u53ea\u5f97\u5728\u9053 \u65c1\u505c\u4e0b\uff0c\u90a3\u9a6c\u8f66\u592b\u60ca\u5413\u672a\u5b9a\uff0c\u6b64\u523b\u66f4\u662f\u60ca\u5f97\u8bf4\u4e0d\u51fa\u8bdd\u6765\u4e86\u3002 \u3000\u3000\u8fd9\u4e9b\u7ea2\u88f3\u5c11\u5973\u4e0e\u9a7e\u8f66\u592b\u5747\u5fc3\u4e2d\u60ca\u9a87\uff0c\u7626\u5f25\u9640\u4e0e\u80d6\u7eaf\u9633\u5fc3\u91cc\u81ea\u66f4\u53d1\u6bdb\uff0c\u8fd9\u4e24\u4eba\u529f\u529b\u76f8\u82e5\uff0c \u5200\u5251\u76f8\u4ea4\uff0c\u5747\u611f\u624b\u8155\u4e00\u9ebb\uff0c\u864e\u53e3\u4e5f\u9690\u9690\u4f5c\u75db\uff0c\u7acb\u523b\u659c\u8dc3\u8f6c\u8db3\uff0c\u9000\u540e\u4e00\u6b65\uff0c\u8fd9\u4e24\u4eba\u51fa\u9053\u6c5f\u6e56\u4ee5 \u6765\uff0c\u53ea\u6709\u5728\u4e2d\u539f\u5927\u4fa0\u5353\u6d69\u7136\u624b\u4e2d\u683d\u8fc7\u4e00\u6b21\u5927\u7b4b\u6597\uff0c\u6b64\u6b21\u89c1\u8fd9\u5c11\u5e74\uff0c\u5e74\u7eaa\u8fd8\u5728\u6614\u5e74\u7684\u5353\u6d69\u7136\u4e4b \u4e0b\uff0c\u6b66\u529f\u5374\u4f3c\u5728\u4ed6\u4e4b\u4e0a\uff0c\u4e24\u4eba\u5bf9\u671b\u4e00\u773c\uff0c\u5fc3\u91cc\u90fd\u5728\u6697\u95ee\u81ea\u5df1\uff1a\u201c\u8fd9\u5c11\u5e74\u662f\u8c01\uff1f\u600e\u5730\u6709\u5982\u6b64\u6b66 \u529f\uff1f\u201d\u80d6\u7eaf\u9633\u813e\u6c14\u66b4\u8e81\uff0c\u6027\u5982\u70c8\u706b\uff0c\u6b64\u523b\u5fc3\u91cc\u6697\u9a87\uff0c\u8eab\u4e0a\u7684\u80a5\u8089\u53d1\u6296\u5f97\u66f4\u52a0\u5389\u5bb3\uff0c\u6068\u4e0d\u5f97\u4e00 \u5251\u5c06\u5353\u957f\u537f\u5241\u4e2a\u900f\u660e\u7a9f\u7abf\u3002 \u3000\u3000\u5f53\u4e0b\u4ed6\u5927\u543c\u4e00\u58f0\uff0c\u6325\u5251\u53c8\u4e0a\uff0c\u7626\u5f25\u9640\u5446\u4e86\u4e00\u5446\uff0c\u4e5f\u81ea\u626c\u5200\u800c\u4e0a\u3002 \u3000\u3000\u5353\u957f\u537f\u65b9\u624d\u521d\u5c55\u7edd\u6280\uff0c\u53ea\u9053\u8fd9\u4e24\u4eba\u5fc3\u91cc\u6709\u6570\uff0c\u4f1a\u4e00\u8d77\u9000\u53bb\uff0c\u6b64\u523b\u89c1\u4e86\u4ed6\u4eec\u7684\u6a21\u6837\uff0c\u5b8c\u5168 \u662f\u4e00\u526f\u62fc\u547d\u59ff\u6001\uff0c\u4e0d\u7981\u5927\u559d\u9053\uff1a\u201c\u6211\u624b\u4e0b\u7559\u60c5\uff0c\u4f60\u4e24\u4eba\u8981\u662f\u518d\u4e0d\u77e5\u8fdb\u9000\uff0c\u53ef\u4e0d\u8981\u602a\u6211\u624b\u8fa3 \u4e86\u3002\u201d \u3000\u3000\u4ed6\u867d\u7136\u5fd7\u5207\u4eb2\u4ec7\uff0c\u4e0d\u60f3\u591a\u9020\u6740\u5b7d\uff0c\u662f\u4ee5\u6839\u672c\u4e0d\u60f3\u5c06\u8fd9\u4e24\u4eba\u4f24\u5728\u638c\u4e0b\uff0c\u4f46\u8fd9\u7626\u4f5b\u80d6\u4ed9\u4e24\u4eba \u5fc3\u91cc\u5374\u53e6\u6709\u60f3\u6cd5\u3002 \u3000\u3000\u4ed6\u4eec\u60f3\u8fd9\u5c11\u5e74\u6b66\u529f\u867d\u9ad8\uff0c\u4f46\u65b9\u624d\u4e5f\u8bb8\u53ea\u662f\u81ea\u5df1\u4e00\u65f6\u5927\u610f\uff0c\u662f\u4ee5\u624d\u4f1a\u5931\u624b\uff0c\u82e5\u8bf4\u81ea\u5df1\u4e24\u4eba \u8054\u624b\u8fd8\u654c\u4e0d\u8fc7\u8fd9\u5c11\u5e74\u7684\u8d64\u624b\u7a7a\u62f3\uff0c\u5b9e\u5728\u662f\u4ee4\u4eba\u96be\u4ee5\u7f6e\u4fe1\u4e4b\u4e8b\uff0c\u83ab\u8bf4\u4ed6\u4e24\u4eba\u4e0d\u4fe1\uff0c\u6b64\u523b\u4f7f\u662f\u6709 \u522b\u7684\u6b66\u6797\u4e2d\u4eba\u5728\u65c1\uff0c\u53ea\u6015\u4e5f\u4e07\u4e07\u4e0d\u4f1a\u76f8\u4fe1\u6b64\u4e8b\u3002 \u3000\u3000\u53c8\u662f\u6570\u62db\u5df2\u8fc7\uff0c\u90a3\u4e9b\u7ea2\u88f3\u5c11\u5973\u89c1\u5230\u8fd9\u7626\u4f5b\u80d6\u4ed9\u4e24\u4eba\u4e00\u5200\u4e00\u5251\u914d\u5408\u5f97\u7684\u786e\u5de7\u5999\uff0c\u770b\u6765\u4eff\u4f5b \u6709\u5982\u6c34\u94f6\u6cfb\u5730\u4e00\u822c\uff0c\u4e00\u7247\u5149\u5e55\u5c06\u5353\u957f\u537f\u5bc6\u4e0d\u900f\u51e4\u5730\u56f4\u5728\u4e2d\u95f4\uff0c\u5979\u4eec\u5b9e\u5728\u60f3\u4e0d\u900f\u5353\u957f\u537f\u662f\u600e\u4e48 \u5c06\u8fd9\u4e9b\u62db\u5f0f\u907f\u5f00\u7684\uff0c\u8fd9\u5b9e\u5728\u662f\u4ee4\u4eba\u532a\u5937\u6240\u601d\u4e4b\u4e8b\u3002 \u3000\u3000\u5353\u957f\u537f\u867d\u7136\u77e5\u9053\u81ea\u5df1\u6b64\u523b\u5df2\u5728\u864e\u7a74\u4e4b\u4e2d\uff0c\u968f\u65f6\u90fd\u6709\u4eba\u8d76\u6765\u52a9\u9635\uff0c\u4f46\u4ed6\u5fc3\u5b58\u5fe0\u539a\uff0c\u5374\u4e0d\u60f3 \u901f\u6218\u901f\u51b3\u5730\u5c06\u8fd9\u4e24\u4eba\u89e3\u51b3\uff0c\u53c8\u89c1\u5230\u8fd9\u4e24\u4eba\u7684\u5200\u5251\u62db\u5f0f\u4e0d\u4f46\u914d\u5408\u4f73\u5999\uff0c\u800c\u4e14\u4ff1\u90fd\u662f\u6b66\u6797\u7f55\u89c1\u7684 \u62db\u5f0f\uff0c\u4ed6\u751f\u6027\u597d\u6b66\uff0c\u4fbf\u53c8\u8d77\u4e86\u5c06\u8fd9\u4e9b\u62db\u5f0f\u591a\u770b\u4e0a\u4e00\u904d\u7684\u597d\u5947\u4e4b\u5fc3\uff0c\u662f\u4ee5\u8fd9\u4e24\u4eba\u867d\u7136\u5bf9\u4ed6\u62db\u62db \u4ff1\u4e0b\u8fa3\u624b\uff0c\u4ed6\u5374\u53ea\u662f\u4e00\u5473\u95ea\u907f\uff0c\u5e76\u4e0d\u8fd8\u624b\u3002 \u3000\u3000\u4f46\u8fd9\u7626\u4f5b\u80d6\u4ed9\u4e24\u4eba\u5374\u53d8\u5f97\u66f4\u7126\u8e81\u8d77\u6765\uff0c\u8fd9\u5929\u76ee\u5c71\u4e2d\uff0c\u6b64\u523b\u9ad8\u624b\u4e91\u96c6\uff0c\u867d\u7136\u90fd\u540c\u662f\u88ab\u90a3\u4e11 \u4eba\u6e29\u5982\u7389\u9080\u6765\u7684\uff0c\u4f46\u5176\u4e2d\u5374\u6709\u4e9b\u4eba\u7d20\u6765\u4e0e\u4ed6\u4e0d\u719f\uff0c\u6b64\u523b\u82e5\u662f\u89c1\u4e86\u4ed6\u4e24\u4eba\u4e45\u6218\u4e00\u4e2a\u5c11\u5e74\u4e0d\u4e0b\uff0c \u5fc5\u5b9a\u4f1a\u5bf9\u4ed6\u4e24\u4eba\u52a0\u4ee5\u6c55\u7b11\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u4e00\u5ff5\u81f3\u6b64\uff0c\u5ffd\u5730\u4e00\u8d77\u4f4e\u5578\u4e00\u58f0\uff0c\u62db\u5f0f\u53c8\u81ea\u4e00\u7d27\uff0c\u5237\u5237\u5237\u5237\u5237\uff0c\u4e00\u8fde\u6570\u5251\uff0c\u547c\u547c\u547c \u547c\uff0c\u4e00\u8fde\u6570\u5200\uff0c\u5200\u5200\u5251\u5251\uff0c\u90fd\u5f80\u5353\u957f\u537f\u524d\u80f8\u540e\u80cc\u523a\u53bb\uff0c\u5353\u957f\u537f\u5251\u7709\u8f69\u5904\uff0c\u5fc3\u4e2d\u5df2\u52a8\u771f\u6012\uff0c\u76ee \u5149\u4e94\u5206\uff0c\u53ea\u89c1\u77ee\u80d6\u9053\u4eba\u4e00\u5251\u5f53\u80f8\u523a\u6765\uff0c\u5de6\u638c\u7a81\u7136\u7a7f\u51fa\u3002 \u3000\u3000\u80d6\u7eaf\u9633\u53ea\u89c1\u4ed6\u5de6\u638c\u4e94\u6307\u4ff1\u90fd\u5fae\u5fae\u5c48\u8d77\uff0c\u53ea\u5f53\u4ed6\u53c8\u8981\u65bd\u5c55\u90a3\u4e00\u624b\u5f39\u6307\u7684\u7edd\u6280\uff0c\u5fc3\u4e2d\u4e00\u5413\uff0c \u5251\u950b\u4fbf\u659c\u659c\u5411\u53f3\u4e00\u504f\u3002 \u3000\u3000\u54ea\u77e5\u5353\u957f\u537f\u53f3\u638c\u53c8\u500f\u7136\u7a7f\u51fa\uff0c\u5de6\u638c\u4e94\u6307\u5e73\u4f38\uff0c\u53f3\u638c\u4ea6\u4e94\u6307\u53ea\u542c\u53c8\u662f\u201c\u94ee\u201d\u7684\u4e00\u58f0\u3002 \u3000\u3000\u7626\u5f25\u9640\u529b\u5288\u800c\u4e0b\u7684\u5200\u950b\uff0c\u88ab\u5353\u957f\u537f\u53cd\u5f39\u800c\u4e0a\u7684\u5251\u67c4\u4e00\u5f39\uff0c\u53ea\u89c9\u53f3\u81c2\u53d1\u70ed\uff0c\u5168\u8eab\u4e00\u9707\uff0c\u957f \u5200\u7adf\u8131\u624b\u98de\u4e86\u51fa\u53bb\uff0c\u98de\u5411\u90a3\u7fa4\u7ea2\u88f3\u5c11\u5973\u3002 \u3000\u3000\u7ea2\u88f3\u5c11\u5973\u9f50\u5730\u4e00\u58f0\u5a07\u5524\uff0c\u56db\u4e0b\u907f\u5f00\uff0c\u53ea\u89c1\u8fd9\u67c4\u957f\u5200\u5728\u591c\u5149\u4e4b\u4e2d\uff0c\u4ecd\u7136\u70c2\u707f\u5982\u94f6\uff0c\u6709\u5982\u4e00 \u9053\u94f6\u8292\u822c\u98de\u6765\u3002 \u3000\u3000\u5728\u8fd9\u5239\u90a3\u4e4b\u95f4\uff0c\u7626\u4f5b\u80d6\u4ed9\u4e24\u4eba\u638c\u4e2d\u7684\u5175\u5203\u7adf\u90fd\u5df2\u8131\u624b\uff0c\u4ed6\u4e24\u4eba\u7adf\u90fd\u9000\u5230\u4e00\u8fb9\uff0c\u77aa\u7740\u773c\u775b \u53d1\u6115\uff0c\u5fc3\u4e2d\u65e2\u662f\u60ca\u9a87\uff0c\u53c8\u89c9\u7f9e\u6124\uff0c\u5374\u53c8\u6709\u4e9b\u83ab\u540d\u5176\u5999\uff0c\u4e0d\u77e5\u5353\u957f\u537f\u8fd9\u4e00\u62db\u7a76\u7adf\u662f\u5982\u4f55\u53d1\u51fa \u7684\u3002 \u3000\u3000\u201c\u55e4\u201d\u5730\u4e00\u58f0\uff0c\u957f\u5200\u63d2\u5230\u5730\u4e0a\uff0c\u7626\u5f25\u9640\u76ee\u5149\u867d\u4ecd\u5411\u5353\u957f\u537f\u6012\u76ee\u800c\u89c6\uff0c\u5fc3\u4e2d\u5374\u5927\u751f\u602f\u610f\uff0c \u6068\u4e0d\u5f97\u811a\u5e95\u63e9\u6cb9\u4e00\u8d70\u4e86\u4e4b\u3002 \u3000\u3000\u80d6\u7eaf\u9633\u751f\u6027\u8f83\u70c8\uff0c\u72e0\u72e0\u7684\u77aa\u4e86\u5353\u957f\u537f\u51e0\u773c\uff0c\u7a81\u7136\u559d\u9053\uff1a\u201c\u4f60\u5feb\u6765\u5c06\u6211\u4e00\u5200\u6740\u6b7b\uff0c\u8981\u4e48\u4fbf \u8bf4\u51fa\u4f60\u7684\u59d3\u540d\uff0c\u603b\u6709\u4e00\u5929\uff0c\u6211\u8981\u6765\u590d\u4ec7\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u6de1\u6de1\u51b7\u7b11\u4e00\u58f0\uff0c\u8fd8\u672a\u7b54\u8bdd\u3002 \u3000\u3000\u54ea\u77e5\u2014\u2014 \u3000\u3000\u5c71\u9053\u4fa7\u88ab\u591c\u8272\u7b3c\u7f69\u7740\u7684\u5c71\u6797\u4e2d\uff0c\u7a81\u53c8\u4f20\u51fa\u4e00\u9635\u683c\u683c\u602a\u7b11\u3002 \u3000\u3000\u8fd9\u602a\u7b11\u4e4b\u58f0\u4e0d\u4f46\u6765\u5f97\u6781\u4e3a\u7a81\u7136\uff0c\u800c\u4e14\u7b11\u58f0\u4e4b\u68ee\u51b7\u602a\u5f02\uff0c\u5f53\u771f\u662f\u96be\u542c\u5230\u4e86\u6781\u5904\uff0c\u5c31\u7b97\u662f\u67ad \u67ad\u591c\u557c\u96be\u542c\u7684\u7a0b\u5ea6\u4e5f\u4e0d\u53ca\u8fd9\u7b11\u58f0\u4e00\u534a\uff0c\u53ea\u542c\u7740\u7ea2\u88f3\u5c11\u5973\u4eec\u4e00\u4e2a\u4e2a\u7d27\u63e1\u7740\u624b\u638c\uff0c\u6d51\u8eab\u60b8\u6817\uff0c\u7626 \u4f5b\u80d6\u4ed9\u4e24\u4eba\u5bf9\u671b\u4e86\u4e00\u773c\uff0c\u4e5f\u4e0d\u7981\u673a\u4f36\u4f36\u6253\u4e86\u4e2a\u5bd2\u5664\u3002 \u3000\u3000\u5353\u957f\u537f\u867d\u4ecd\u6602\u7136\u5353\u7acb\uff0c\u5fc3\u80f8\u4e4b\u95f4\u4e5f\u50cf\u662f\u7a81\u7136\u6cdb\u8d77\u4e00\u96be\u8a00\u7684\u611f\u89c9\u3002 \u3000\u3000\u53ea\u89c1\u8fd9\u5c71\u6797\u9634\u5f71\u4e4b\u4e2d\uff0c\u968f\u7740\u8fd9\u201c\u683c\u683c\u201d\u7684\u602a\u7b11\u4e4b\u58f0\uff0c\u7a81\u7136\u7f13\u7f13\u8d70\u51fa\u4e09\u4e2a\u53c8\u77ee\u53c8\u80d6\u7684\u4eba \u6765\uff0c\u5353\u957f\u537f\u5b9a\u775b\u671b\u53bb\uff0c\u53ea\u89c1\u8fd9\u4e09\u4eba\u4e0d\u4f46\u9ad8\u77ee\u5982\u4e00\uff0c\u80a5\u7626\u76f8\u540c\uff0c\u8eab\u4e0a\u7684\u88c5\u675f\u6253\u626e\uff0c\u7adf\u4e5f\u5b8c\u5168\u4e00 \u6a21\u4e00\u8fd9\u4e09\u4eba\u8eab\u4e0a\u7a7f\u7740\u7684\uff0c\u7ade\u90fd\u662f\u4e00\u88ad\u4e94\u8272\u73ed\u70c2\u7684\u5f69\u8863\uff0c\u867d\u5728\u6df1\u591c\u4e4b\u4e2d\uff0c\u8fd9\u4e09\u4eba\u8eab\u4e0a\u7684\u5f69\u8863\u770b \u6765\u5374\u4ecd\u7136\u95ea\u95ea\u751f\u5149\uff0c\u4e00\u9635\u98ce\u5439\u6765\uff0c\u5f69\u8863\u968f\u98ce\u98d8\u52a8\uff0c\u975e\u4e1d\u975e\u7f0e\uff0c\u4e5f\u770b\u4e0d\u51fa\u662f\u4f55\u7269\u6240\u505a\u3002 \u3000\u3000\u4ed6\u4eec\u8170\u7554\uff0c\u4ff1\u90fd\u60ac\u7740\u4e00\u67c4\u957f\u5251\uff0c\u5251\u9798\u4e4b\u4e0a\uff0c\u6ee1\u7f00\u73e0\u5b9d\uff0c\u886c\u7740\u8fd9\u95ea\u95ee\u751f\u5149\u7684\u5f69\u8863\uff0c\u66f4\u89c9\u7eda \u4e3d\u593a\u76ee\uff0c\u707f\u70c2\u5149\u8f89\uff0c\u4e0d\u53ef\u65b9\u7269\u3002 \u3000\u3000\u65b9\u624d\u5353\u957f\u537f\u89c1\u4e86\u80d6\u7eaf\u9633\uff0c\u53ea\u5f53\u4ed6\u5df2\u53ef\u7b97\u662f\u5168\u4e16\u754c\u6700\u77ee\u6700\u80d6\u7684\u4eba\u4e86\uff0c\u54ea\u77e5\u6b64\u523b\u4e00\u89c1\u8fd9\u4e09 \u4eba\uff0c\u7adf\u4f3c\u8fd8\u8981\u6bd4\u80d6\u7eaf\u9633\u80d6\u4e0a\u4e09\u5206\uff0c\u77ee\u4e0a\u4e09\u5206\uff0c\u4e00\u773c\u671b\u53bb\uff0c\u7ade\u50cf\u662f\u4e09\u4e2a\u53d1\u5149\u6eda\u6765\u7684\u5706\u7403\u3002 \u3000\u3000\u8fd9\u4e09\u4eba\u4e00\u8d77\u4e3e\u6b65\uff0c\u4e00\u8d77\u7f13\u7f13\u8d70\u5230\u8fd1\u524d\uff0c\u6700\u53f3\u7684\u4e00\u4eba\u7a81\u7136\u5f20\u53e3\u8bf4\u9053\uff1a\u201c\u6211\u662f\u9ece\u591a\u5927\uff01\u201d \u3000\u3000\u4e2d\u95f4\u7684\u4e00\u4eba\u968f\u5373\u63a5\u53e3\u9053\uff1a\u201c\u6211\u662f\u9ece\u591a\u4e8c\uff01\u201d \u3000\u3000\u5de6\u4fa7\u7684\u4e00\u4eba\u7adf\u4e5f\u7acb\u523b\u63a5\u9053\uff1a\u201c\u6211\u662f\u9ece\u591a\u4e09\uff01\u2018\u8fd9\u4e09\u4eba\u4e0d\u4f46\u55d3\u97f3\u602a\u5f02\uff0c\u800c\u4e14\u8bf4\u8bdd\u7684\u8bed\u58f0\u66f4 \u662f\u602a\u5f02\uff0c\u5353\u957f\u537f\u4e00\u6115\uff0c\u60f3\u4e86\u4e00\u4f1a\u513f\uff0c\u624d\u77e5\u9053\u8fd9\u4e09\u4eba\u539f\u6765\u662f\u5728\u81ea\u62a5\u59d3\u540d\u3002\u4ed6\u60f3\u8d77\u65b9\u624d\u90a3\u4e00\u50e7\u4e00 \u9053\u4e0d\u4f46\u4e0d\u8bf4\u81ea\u5df1\u7684\u59d3\u540d\uff0c\u8981\u53eb\u4eba\u53bb\u731c\uff0c\u800c\u76f4\u5230\u6b64\u523b\uff0c\u8fd8\u662f\u6ca1\u6709\u8bf4\u51fa\u4ed6\u4eec\u7684\u59d3\u540d\u6765\uff0c\u4f46\u8fd9\u4e09\u4eba \u5374\u4efb\u8bdd\u4e0d\u8bf4\uff0c\u5148\u5c31\u9053\u540d\uff0c\u518d\u52a0\u4e0a\u540d\u5b57\u7684\u53e4\u602a\uff0c\u5353\u957f\u537f\u5fc3\u91cc\u597d\u7b11\uff0c\u4f46\u60f3\u5230\u8fd9\u5929\u76ee\u5c71\u4e2d\u7adf\u6709\u8fd9\u4e48 \u591a\u602a\u4eba\uff0c\u800c\u4e14\u4e00\u4e2a\u602a\u80dc\u4e00\u4e2a\uff0c\u4e00\u4e2a\u5f3a\u80dc\u4e00\u4e2a\uff0c\u5374\u90fd\u662f\u4e0e\u81ea\u5df1\u4e3a\u654c\u7684\uff0c\u4e0d\u7981\u53c8\u7b11\u4e0d\u51fa\u6765\u3002\u54ea\u77e5 \u8fd9\u4e09\u4e2a\u59d3\u9ece\u7684\u602a\u4eba\u8bf4\u5b8c\u4e86\u606c\uff0c\u7a81\u7136\u53c8\u4e00\u8d77\u4f38\u51fa\u4e86\u5927\u62c7\u6307\uff0c\u5411\u5353\u957f\u537f\u4e00\u626c\uff0c\u9f50\u58f0\u9053\uff1a\u201c\u597d\u554a\uff0c \u597d\u554a\uff01\u201d \u3000\u3000\u5353\u957f\u537f\u53cd\u4e00\u6115\uff0c\u867d\u4e0d\u77e5\u9053\u4ed6\u4eec\u5728\u8bf4\u4ec0\u4e48\uff0c\u4f46\u770b\u6765\u5374\u50cf\u662f\u5728\u8d5e\u626c\u81ea\u5df1\u3002 \u3000\u3000\u53ea\u542c\u90a3\u9ece\u591a\u5927\u4f38\u7740\u5927\u6307\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u683c\u4eba\u54ea\uff0c\u6b66\u529f\u771f\u597d\u554a\uff0c\u5c45\u7136\u628a\u6276\u6851\u4e09\u5c9b\u4e0a\u9876\u9876\u597d\u7684 \u5927\u5251\u5ba2\u7684\u672c\u4e8b\u5b66\u4f1a\u4e86\uff0c\u81ea\u4ece\u6211\u4e0a\u6b21\u89c1\u8fc7\u67f3\u751f\u5200\u9a6c\u5b88\u7528\u8fc7\u8fd9\u4e00\u62db\u4e4b\u540e\uff0c\u6211\u5c31\u6ca1\u6709\u89c1\u5230\u6709\u4eba\u80fd\u5c06 \u8fd9\u4e00\u62db\u7528\u5f97\u8fd9\u4e48\u597d\u7684\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u8d77\u8bdd\u6765\uff0c\u751f\u50cf\u662f\u5377\u7740\u820c\u5934\uff0c\u5353\u957f\u537f\u542c\u5f97\u6ee1\u5934\u5927\u6c57\uff0c\u624d\u7b97\u542c\u61c2\u4e00\u4e9b\uff0c\u5fc3\u5934\u5374\u5df2\u5927\u9a87\u3002 \u3000\u3000\u539f\u6765\u4ed6\u65b9\u624d\u65bd\u51fa\u7684\u53cc\u638c\u5408\u62cd\u7684\u90a3\u4e00\u62db\uff0c\u6b63\u662f\u53f8\u7a7a\u8001\u4eba\u6614\u5e74\u4e1c\u6e38\u7ca4\u5883\u65f6\uff0c\u4ece\u4e00\u4e2a\u6d6a\u6e38\u81f3\u4e2d \u56fd\u7684\u6276\u6851\u6d6a\u4eba\u5b66\u5f97\uff0c\u518d\u52a0\u4ee5\u53d8\u5316\u6539\u826f\u7684\uff0c\u636e\u90a3\u6276\u6851\u6d6a\u4eba\u8bf4\uff0c\u8fd9\u4e00\u62db\u7684\u6765\u5386\uff0c\u662f\u65e5\u672c\u5929\u4e0b\u6b66\u672f \u603b\u6559\u7ec3\uff0c\u4e5f\u5c31\u662f\u65e5\u672c\u6b66\u672f\u7684\u7b2c\u4e00\u95e8\u6d3e\u67f3\u751f\u82f1\u96c4\u6d3e\u7684\u7edd\u6280\uff0c\u8fd9\u65e5\u672c\u6d6a\u4eba\u672c\u662f\u67f3\u751f\u95e8\u4e2d\u7684\u9ad8\u624b\uff0c \u56e0\u4e3a\u72af\u4e86\u95e8\u89c4\uff0c\u754f\u7f6a\u6f5c\u9003\uff0c\u624d\u9003\u5230\u4e2d\u56fd\u6765\uff0c\u5728\u53bf\u5883\u4e2d\u4e5f\u66fe\u51fa\u8fc7\u4e00\u9635\u98ce\u5934\uff0c\u540e\u6765\u89c1\u7740\u53f8\u7a7a\u8001 \u4eba\uff0c\u624d\u77e5\u9053\u4e2d\u539f\u6b66\u529f\u7684\u6df1\u5965\uff0c\u5b9e\u662f\u6df1\u5982\u6ca7\u6d77\uff0c\u81ea\u5df1\u7684\u8fd9\u70b9\u6b66\u529f\uff0c\u4e0d\u8fc7\u662f\u6ca7\u6d77\u4e2d\u7684\u4e00\u7c9f\u800c\u5df2\uff0c \u518d\u4e5f\u4e0d\u6562\u5728\u4e2d\u56fd\u79f0\u96c4\u4e86\u3002 \u3000\u3000\u53f8\u7a7a\u8001\u4eba\u5728\u4f20\u5353\u957f\u537f\u8fd9\u4e00\u62db\u7684\u65f6\u5019\uff0c\u4e5f\u66fe\u5bf9\u8fd9\u4e00\u62db\u7684\u6765\u5386\u8bf4\u51fa\uff0c\u800c\u4e14\u7b11\u7740\u8bf4\uff1a\u201c\u4e2d\u539f\u5883 \u5185\uff0c\u8c6a\u6770\u867d\u591a\uff0c\u4f46\u8bc6\u5f97\u8fd9\u4e00\u62db\u7684\uff0c\u53ea\u6015\u6ca1\u6709\u51e0\u4e2a\u3002\u201d\u201c\u3001\u5353\u957f\u537f\u65b9\u624d\u65bd\u51fa\u8fd9\u4e00\u62db\uff0c\u679c\u7136\u4f7f\u5f97 \u522b\u4eba\u83ab\u540d\u5176\u5999\u3002\u54ea\u77e5\u8fd9\u4e09\u4e2a\u5f69\u8863\u602a\u4eba\u4e00\u89c1\u9762\u5c31\u559d\u7834\u4e86\u8fd9\u4e00\u62db\u7684\u6765\u5386\uff0c\u5353\u957f\u537f\u81ea\u662f\u5927\u611f\u610f\u5916\uff0c \u5374\u542c\u5f97\u9ece\u591a\u5927\u683c\u683c\u4e00\u9635\u602a\u7b11\uff0c\u7adf\u5411\u90a3\u7626\u4f5b\u80d6\u4ed9\u9053\uff1a\u201c\u6211\u5148\u524d\u4ee5\u4e3a\u4f60\u4e24\u4e2a\u6b66\u529f\u597d\uff0c\u54ea\u77e5\u2014\u2014\u563b \u563b\uff0c\u5374\u4e00\u70b9\u513f\u7528\u4e5f\u6ca1\u6709\uff0c\u4f60\u4e24\u4e2a\u8fd8\u53d1\u4ec0\u4e48\u5a01\uff0c\u7094\u56de\u5bb6\u7b97\u4e86\u3002\u201d \u3000\u3000\u7626\u4f5b\u80d6\u4ed9\u4e24\u4eba\u9762\u4e0a\u9635\u9752\u9635\u767d\uff0c\u80d6\u7eaf\u9633\u8eab\u4e0a\u7684\u80a5\u8089\u4e5f\u52a8\u4e0d\u8d77\u6765\u4e86\uff0c\u50cf\u53ea\u6b7b\u732a\u4f3c\u7684\u5446\u7acb\u4e86\u826f \u4e45\uff0c\u5353\u957f\u537f\u671b\u4e86\u4ed6\u4e00\u773c\uff0c\u89c1\u4ed6\u5634\u76ae\u52a8\u4e86\u4e24\u52a8\uff0c\u4f3c\u4e4e\u8fd8\u60f3\u8bf4\u8bdd\u7684\u6837\u5b50\u3001\u4fbf\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u5728\u4e0b\u5353 \u957f\u537f\uff0c\u4e24\u4f4d\u5982\u679c\u6709\u610f\u590d\u4ec7\uff0c\u53ea\u7ba1\u6765\u5bfb\u6211\u4f7f\u662f\u3002\u201d \u3000\u3000\u80d6\u7eaf\u9633\u9762\u8272\u4e00\u53d8\uff0c\u8131\u53e3\u9053\uff1a\u201c\u4f60\u59d3\u5353\uff0c\u5353\u6d69\u7136\u662f\u4f60\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u5353\u957f\u537f\u8083\u7136\u9053\uff0c\u201c\u6b63\u662f\u5bb6\u7236\u3002\u201d \u3000\u3000\u7626\u4f5b\u80d6\u4ed9\u5bf9\u671b\u4e86\u4e00\u773c\uff0c\u9f50\u6697\u5730\u53f9\u4e00\u58f0\uff0c\u60f3\u5230\u81ea\u5df1\u4e24\u4eba\u867d\u7136\u79f0\u96c4\u4e00\u4e16\uff0c\u5374\u8d25\u5728\u4eba\u5bb6\u7236\u5b50\u4e24 \u4eba\u7684\u624b\u4e0a\uff0c\u5fc3\u91cc\u53c8\u662f\u96be\u8fc7\uff0c\u53c8\u662f\u7070\u5fc3\uff0c\u72e0\u72e0\u77aa\u4e86\u90a3\u7740\u5f69\u519c\u602a\u4eba\u4e00\u773c\uff0c\u6389\u5934\u5c31\u8d70\uff0c\u8fde\u843d\u5728\u5730\u4e0a \u7684\u5200\u5251\u90fd\u4e0d\u8981\u4e86\u3002 \u3000\u3000\u9ece\u62ff\u5927\u3001\u9ece\u591a\u4e8c\u3001\u9ece\u591a\u4e09\uff0c\u4e00\u8d77\u602a\u7b11\u4e86\u8d77\u6765\u3002\u9ece\u591a\u4e09\u602a\u7b11\u9053\uff1a\u201c\u8fd9\u79cd\u8870\u8d25\u8fd8\u51fa\u6765\u73b0\u8eab\uff0c \u771f\u662f\u4e22\u4eba\uff01\u201d \u3000\u3000\u5353\u957f\u537f\u539f\u6765\u4ee5\u4e3a\u8fd9\u4e09\u4eba\u4e0e\u90a3\u80d6\u7626\u50e7\u9053\u4e24\u4eba\u672c\u662f\u4e00\u8def\uff0c\u6b64\u523b\u89c1\u4ed6\u4eec\u5bf9\u81ea\u5df1\u5982\u6b64\u8d5e\u626c\uff0c\u5bf9\u90a3 \u50e7\u9053\u4e24\u4eba\u5374\u5982\u6b64\u8c29\u9a82\uff0c\u5fc3\u4e0b\u4e0d\u7981\u5927\u5947\u3002 \u3000\u3000\u4ed6\u5374\u4e0d\u77e5\u9053\u8fd9\u4e09\u4eba\u672c\u662f\u6d77\u5357\u5251\u6d3e\u4e2d\u7684\u9ad8\u624b\uff0c\u66fe\u7ecf\u8fdc\u6e38\u6276\u6851\uff0c\u662f\u4ee5\u4e00\u773c\u4fbf\u770b\u51fa\u5353\u957f\u537f\u90a3\u4e00 \u62db\u7684\u6765\u5386\u3002 \u3000\u3000\u8fd9\u4e09\u4eba\u6765\u5230\u4e2d\u539f\u540e\uff0c\u4ea6\u88ab\u4e11\u4eba\u6e29\u5982\u7389\u8bf7\u6765\u52a9\u9635\uff0c\u4f46\u4ed6\u4eec\u4e09\u4eba\u4e45\u5c45\u6d77\u5916\uff0c\u5bf9\u4e2d\u539f\u6b66\u6797\u4e2d\u4eba \u591a\u4e0d\u719f\u6089\uff0c\u4e5f\u770b\u4e0d\u8d77\uff0c\u8fd9\u5176\u4e2d\u4ed6\u4eec\u5c24\u5176\u770b\u4e0d\u8d77\u90a3\u80d6\u4ed9\u7626\u4f5b\u4e24\u4eba\uff0c\u5728\u8fd9\u6570\u65e5\u4e4b\u4e2d\u5df2\u51b7\u8a00\u70ed\u8bed\u76f8 \u4e92\u9a82\u4e86\u591a\u6b21\uff0c\u8fd9\u4e09\u4eba\u6b66\u529f\u867d\u4e0d\u9519\uff0c\u4f46\u5374\u4e0d\u8bc6\u4e2d\u539f\u8a00\u8bed\uff0c\u8bf4\u8d77\u606c\u6765\u5df2\u662f\u5431\u5431\u683c\u683c\u7684\u8ba9\u4eba\u542c\u4e0d\u6e05 \u695a\uff0c\u4e0e\u4eba\u76f8\u9a82\uff0c\u81ea\u7136\u66f4\u4e0d\u662f\u4eba\u5bb6\u7684\u654c\u624b\uff0c\u662f\u4ee5\u4fbf\u53d7\u4e86\u90a3\u7626\u4f5b\u80d6\u4ed9\u4e0d\u5c11\u6c14\u3002 \u3000\u3000\u56e0\u4e4b\u4ed6\u4e09\u4eba\u4fbf\u5bf9\u7626\u4f5b\u80d6\u4ed9\u5927\u6709\u6076\u611f\uff0c\u65b9\u624d\u5353\u957f\u537f\u4e0e\u7626\u4f5b\u80d6\u4ed9\u52a8\u624b\u4e4b\u9645\uff0c\u4ed6\u4e09\u4eba\u53ea\u5728\u6797\u4e2d \u770b\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u5374\u4e0d\u51fa\u6765\u5e2e\u52a9\uff0c\u53ea\u7b49\u5230\u7626\u4f5b\u80d6\u4ed9\u4e0d\u654c\uff0c\u4ed6\u4e09\u4eba\u624d\u6162\u6761\u65af\u7406\u5730\u8d70\u6765\uff0c\u4e00\u9762\u6545\u610f \u5bf9\u5353\u957f\u537f\u606d\u7ef4\uff0c\u4e00\u9762\u53c8\u5411\u7626\u4f5b\u80d6\u4ed9\u4e8c\u4eba\u7b11\u9a82\u3002 \u3000\u3000\u5353\u957f\u537f\u53ea\u89c1\u8fd9\u4e09\u4eba\u671b\u7740\u7626\u4f5b\u80d6\u4ed9\u4e00\u80a5\u4e00\u7626\u3001\u4e00\u9ad8\u4e00\u77ee\u4e24\u6761\u8eab\u5f71\u51e0\u4e2a\u8d77\u843d\u6d88\u5931\u5728\u591c\u8272\u4e2d\uff0c \u7b11\u5f97\u66f4\u662f\u5f97\u610f\uff0c\u5fc3\u4e2d\u4e0d\u7981\u6697\u5fd6\uff1a\u201c\u8fd9\u4e09\u4eba\u7a76\u7adf\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u8bf4\u8d77\u8bdd\u6765\u5374\u53c8\u4e0d\u50cf\u4eba\u8bf4\u7684\uff0c\u8d77\u7684 \u540d\u5b57\uff0c\u66f4\u4e0d\u50cf\u662f\u4eba\u7684\u540d\u5b57\uff0c\u4f46\u770b\u6765\u6b66\u529f\u5374\u50cf\u662f\u751a\u4e3a\u6e0a\u535a\uff0c\u4f46\u4e09\u4eba\u6b64\u523b\u7a81\u7136\u73b0\u8eab\uff0c\u7a76\u7adf\u4e0e\u6211\u662f \u53cb\u662f\u654c\u5462\uff1f\u201d \u3000\u3000\u76ee\u5149\u62ac\u5904\uff0c\u5374\u89c1\u8fd9\u4e09\u4eba\u7b11\u58f0\u7a81\u7136\u4e00\u8d77\u987f\u4f4f\uff0c\u9762\u5bb9\u7acb\u523b\u53d8\u5f97\u68ee\u51b7\u5f02\u5e38\uff0c\u516d\u9053\u51b0\u51b7\u7684\u76ee\u5149\uff0c \u4e00\u8d77\u671b\u5411\u5353\u957f\u537f\uff0c\u54ea\u91cc\u8fd8\u6709\u534a\u5206\u8d5e\u626c\u4e4b\u610f\u3002 \u3000\u3000\u4e8e\u662f\u5353\u957f\u537f\u4fbf\u53c8\u4e00\u6b21\u6212\u5907\u8d77\u6765\uff0c\u5bf9\u8fd9\u4e09\u4eba\u4ed6\u5e76\u65e0\u4e1d\u6beb\u754f\u610f\uff0c\u4f7f\u4ed6\u5fc3\u91cc\u6709\u4e9b\u7740\u614c\u7684\u662f\u8fd9\u5929 \u76ee\u5c71\u4e2d\u4e0d\u77e5\u8fd8\u6709\u591a\u5c11\u602a\u4eba\uff0c\u8981\u662f\u50cf\u8fd9\u6837\u4e00\u4e2a\u63a5\u7740\u4e00\u4e2a\u7684\u73b0\u8eab\uff0c\u8f66\u8f6e\u5927\u6218\uff0c\u5012\u7684\u786e\u662f\u4ef6\u8ba8\u538c\u7684 \u4e8b\u3002\u5353\u957f\u537f\u89c1\u8fd9\u4e09\u4eba\u9762\u8272\u7a81\u53d8\uff0c\u5fc3\u4e2d\u4ea6\u6709\u4e9b\u6000\u6068\uff0c\u53ea\u89c1\u5f53\u4e2d\u90a3\u9ece\u591a\u4e8c\u7a81\u5730\u6447\u6447\u6643\u6643\u5730\u5411\u81ea\u5df1 \u8d70\u4e86\u8fc7\u6765\uff0c\u4e14\u53c8\u6840\u6840\u602a\u7b11\u9053\uff1a\u201c\u4f60\u53eb\u4ec0\u4e48\u540d\u5b57\uff1f\u8dd1\u5230\u8fd9\u91cc\u5e72\u4e5c\u91ce\u2014--\u201d\u8bf4\u5230\u4e00\u534a\uff0c\u4ed6\u5ffd\u7136\u60f3 \u60f3\u201c\u4e5c\u91ce\u201d\u4e24\u5b57\u4e43\u662f\u81ea\u5df1\u5bb6\u4e61\u7684\u8bdd\uff0c\u522b\u4eba\u600e\u4f1a\u542c\u5f97\u61c2\uff0c\u53c8\u60f3\u4e86\u60f3\uff0c\u65b9\u81ea\u63a5\u7740\u53c8\u9053\uff1a\u201c\u8dd1\u5230\u8fd9 \u91cc\u5e72\u4ec0\u4e48\uff0c\u6211\u770b\u4f60\u6700\u597d\u4e5f\u50cf\u521a\u521a\u90a3\u4e24\u4e2a\u4eba\u4e00\u6837\uff0c\u5feb\u4e9b\u56de\u5bb6\u53bb\u5427\uff01\u201d \u3000\u3000\u5353\u957f\u537f\u5251\u7709\u4e00\u8f69\uff0c\u6717\u58f0\u9053\uff1a\u201c\u5728\u4e0b\u82e5\u662f\u8981\u4e0a\u6b64\u5c71\uff0c\u4e16\u4e0a\u4fbf\u65e0\u4e00\u4eba\u80fd\u53eb\u5728\u4e0b\u4e0b\u5c71\u7684\u3002\u201d \u3000\u3000\u90a3\u9ece\u591a\u4e8c\u683c\u683c\u7684\u53c8\u662f\u4e00\u9635\u602a\u7b11\uff0c\u4f38\u51fa\u624b\u638c\uff0c\u8fd9\u6b21\u5374\u5c06\u98df\u3001\u4e2d\u3001\u65e0\u540d\u4e09\u6307\u4e00\u8d77\u538b\u5728\u62c7\u6307\u4e4b \u4e0b\uff0c\u4f38\u4e86\u53ea\u5c0f\u6307\u51fa\u6765\uff0c\u5728\u5353\u957f\u537f\u9762\u524d\u6447\u4e86\u4e24\u6447\uff0c\u6307\u4e86\u4e24\u6307\uff0c\u65b9\u81ea\u602a\u7b11\u7740\u9053\uff1a\u201c\u4f60\u4e0d\u8981\u4ee5\u4e3a\u4f60 \u771f\u7684\u597d\uff0c\u5728\u6211\u4eec\u9762\u524d\uff0c\u4f60\u4e0d\u8fc7\u662f\u8fd9\u4e2a\uff1a\u201c\u5353\u957f\u537f\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u54ea\u4e2a\uff1f\u201d \u3000\u3000\u8f6c\u5ff5\u4e00\u60f3\uff0c\u65b9\u81ea\u56de\u8fc7\u610f\u6765\uff1a\u201c\u8fd9\u4e2a\u60f3\u5fc5\u5c31\u662f\u5c0f\u6307\u4e86\uff01\u201d \u3000\u3000\u4ed6\u5e7c\u906d\u5b64\u96f6\uff0c\u6210\u957f\u65f6\u5168\u5728\u6614\u7ec3\u6b66\u529f\uff0c\u6839\u672c\u6ca1\u6709\u548c\u987d\u7ae5\u5b09\u620f\u8fc7\uff0c\u8fd9\u79cd\u8bf4\u8bdd\u7684\u65b9\u5f0f\uff0c\u4ed6\u66f4\u662f \u4ece\u6765\u4e0d\u66fe\u542c\u8fc7\uff0c\u5fc3\u4e0b\u4e0d\u7981\u53d7\u607c\uff0c\u6697\u9053\uff1a\u201c\u65e0\u804a\uff01\u201d \u3000\u3000\u54ea\u77e5\u9053\u9ece\u591a\u4e8c\u602a\u7b11\u672a\u7edd\uff0c\u7a81\u7136\u53cd\u624b\u4e00\u62bd\uff0c\u62bd\u51fa\u8170\u7554\u957f\u5251\uff0c\u5de6\u811a\u4e00\u6e9c\uff0c\u53f3\u811a\u659c\u8fdb\uff0c\u8e0f\u5947 \u95e8\uff0c\u8d70\u504f\u950b\uff0c\u5237\u5730\u4e00\u5251\u523a\u5411\u5353\u957f\u537f\uff0c\u5251\u5149\u7f2d\u7ed5\uff0c\u5251\u5c16\u98a4\u52a8\uff0c\u5374\u505c\u7559\u5728\u5353\u957f\u537f\u9762\u524d\u4e09\u5bf8\u4e4b\u5904\uff0c \u4ed6\u7b11\u58f0\u65b9\u81ea\u4e00\u987f\uff0c\u53c8\u9053\uff1a\u201c\u4f60\u4e0b\u4e0d\u4e0b\u5c71\u53bb\uff1f\u201d \u3000\u3000\u5353\u957f\u537f\u5fc3\u91cc\u6709\u6c14\uff0c\u4ea6\u81ea\u4f38\u51fa\u624b\u638c\uff0c\u5c06\u98df\u3001\u4e2d\u3001\u65e0\u540d\u4e09\u6307\uff0c\u4e00\u8d77\u538b\u5728\u62c7\u6307\u4e4b\u4e0b\uff0c\u51b7\u7b11\u9053\uff1a \u201c\u6211\u4e0d\u4e0b\u5c71\u53bb\uff01\u201d \u3000\u3000\u53f3\u624b\u5c0f\u62c7\u6307\uff0c\u7a81\u5730\u5bf9\u51c6\u5251\u5c16\u4e00\u5f39\uff0c\u559d\u9053\uff1a\u201c\u4f60\u624d\u662f\u8fd9\u4e2a\uff01\u201d \u3000\u3000\u9ece\u591a\u4e8c\u65b9\u624d\u62bd\u5251\u51fa\u5251\uff0c\u518d\u52a0\u4e0a\u5251\u5c16\u7684\u8fd9\u4e00\u9635\u98a4\u52a8\uff0c\u4ff1\u90fd\u5feb\u5982\u95ea\u7535\uff0c\u7684\u786e\u662f\u8981\u767e\u6570\u5341\u5e74\u7cbe \u7eaf\u7684\u529f\u592b\uff0c\u4ed6\u53ea\u9053\u8fd9\u5c11\u5e74\u4f1a\u5bf9\u81ea\u5df1\u7684\u6b66\u529f\u60ca\u9a87\uff0c\u54ea\u77e5\u4eba\u5bb6\u5374\u4f9d\u7136\u6602\u7136\u5353\u7acb\uff0c\u65e0\u52a8\u4e8e\u8877\uff0c\u4ed6\u5fc3 \u91cc\u5df2\u6709\u4e9b\u5947\u602a\uff0c\u7b49\u5230\u5353\u957f\u537f\u50cf\u4ed6\u4e00\u6837\u4f38\u51fa\u624b\u638c\u6765\uff0c\u4ed6\u5fc3\u91cc\u4fbf\u66f4\u5927\u5947\uff0c\u65b9\u5f85\u559d\u95ee\uff0c\u54ea\u77e5\u53ea\u542c \u201c\u55e1\u201d\u7684\u4e00\u58f0\u6e05\u54b3\uff0c\u81ea\u5df1\u624b\u4e2d\u957f\u5251\u7adf\u4f3c\u7a81\u7136\u88ab\u5927\u529b\u4e00\u9707\uff0c\u518d\u4e5f\u628a\u6301\u4e0d\u5b9a\uff0c\u8e6c\u8e6c\u8fde\u9000\u4e24\u6b65\uff0c\u5251 \u8eab\u6447\u6447\u6b32\u5760\uff0c\u4ed6\u62fc\u547d\u63e1\u7d27\u624b\u638c\uff0c\u624d\u771f\u6ca1\u6709\u8131\u624b\u98de\u53bb\uff0c\u4f46\u89c9\u5f97\u53f3\u81c2\u53d1\u9ebb\uff0c\u864e\u53e3\u53d1\u70ed\uff0c\u5353\u957f\u537f\u82e5 \u662f\u518d\u6765\u4e00\u4e0b\uff0c\u957f\u5251\u4fbf\u8981\u98de\u51fa\u53bb\u4e86\u3002 \u3000\u3000\u4ed6\u5446\u5446\u5730\u6123\u4e86\u534a\u664c\uff0c\u5374\u8fd8\u662f\u4e0d\u660e\u767d\u5bf9\u65b9\u4f7f\u7684\u662f\u4f55\u624b\u6bb5\u3002 \u3000\u3000\u5353\u957f\u537f\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u8fd9\u4e00\u62db\u662f\u4ec0\u4e48\u6765\u5386\uff0c\u4f60\u53ef\u77e5\u9053\uff1f\u201d \u3000\u3000\u9ece\u591a\u5927\u3001\u9ece\u591a\u4e8c\u3001\u9ece\u591a\u4e09\u4e45\u5c45\u6d77\u5916\uff0c\u867d\u7136\u65b9\u624d\u559d\u7834\u4e86\u5353\u957f\u5496\u90a3\u4e00\u62db\u7684\u6765\u5386\uff0c\u4f46\u5353\u957f\u537f\u6b64 \u523b\u4f7f\u51fa\u8fd9\u79cd\u4e2d\u539f\u7684\u7cbe\u5fae\u6b66\u529f\uff0c\u4ed6\u4e09\u4eba\u5982\u4f55\u77e5\u9053\uff0c\u4e00\u65f6\u4e4b\u95f4\u4e09\u4eba\u9762\u9762\u76f8\u89d1\uff0c\u7adf\u90fd\u5446\u4f4f\u4e86\u3002 \u3000\u3000\u5353\u957f\u537f\u89c1\u4ed6\u4e09\u4eba\u5446\u77aa\uff0c\u53c8\u81ea\u51b7\u7b11\u4e00\u58f0\uff0c\u7f13\u6b65\u8d70\u8fc7\u9ece\u591a\u4e8c\u8eab\u4fa7\uff0c\u5411\u5c71\u4e0a\u8d70\u53bb\uff0c\u76ee\u5149\u62ac\u5904\uff0c \u5374\u89c1\u90a3\u4e9b\u7ea2\u88f3\u5c11\u5973\u5728\u8fd9\u4e00\u523b\u529f\u592b\u90fd\u8d70\u5f97\u4e0d\u77e5\u53bb\u5411\uff0c\u8fde\u8f66\u4e0a\u7684\u8f66\u592b\u90fd\u6ca1\u6709\u4e86\uff0c\u53ea\u5269\u4e0b\u4e00\u8f86\u7a7a \u8f66\uff0c\u505c\u5728\u9053\u65c1\u3002 \u3000\u3000\u6b64\u523b\u4ed6\u81ea\u77e5\u81ea\u5df1\u5411\u5c71\u4e0a\u6bcf\u8d70\u4e00\u6b65\uff0c\u4fbf\u8ddd\u79bb\u864e\u7a74\u66f4\u8fd1\u4e00\u6b65\uff0c\u4f46\u4e8b\u5df2\u81f3\u6b64\uff0c\u4ed6\u518d\u82e5\u4e0b\u5c71\uff0c\u5c82 \u975e\u8981\u8ba9\u522b\u4eba\u803b\u7b11\u3002 \u3000\u3000\u8981\u77e5\u9053\u4ed6\u751f\u6027\u672c\u662f\u5b81\u6298\u6bcb\u56de\u4e4b\u4eba\uff0c\u52c7\u5f80\u76f4\u524d\u4e0d\u80af\u56de\u6b65\uff0c\u5f53\u4e0b\u7f13\u7f13\u5411\u5c71\u4e0a\u8d70\u53bb\uff0c\u5fc3\u4e2d\u4e00\u9762 \u5728\u5bfb\u601d\u8be5\u5982\u4f55\u5e94\u4ed8\u5c71\u4e0a\u7684\u654c\u4eba\uff0c\u4e00\u9762\u5374\u5728\u6697\u4e2d\u7559\u610f\uff0c\u8eab\u540e\u7684\u8fd9\u4e09\u4eba\u4f1a\u6709\u4f55\u4e3e\u52a8\u3002 \u3000\u3000\u6765\u81ea\u6d77\u5357\u7684\u9ece\u6c0f\u4e09\u5251\uff0c\u4f60\u671b\u7740\u6211\uff0c\u6211\u671b\u7740\u4f60\uff0c\u5446\u5446\u5730\u6123\u4e86\u534a\u664c\uff0c\u4e09\u4eba\u89c1\u4e86\u5353\u957f\u537f\u8fd9\u6837\u6df1 \u4e0d\u53ef\u6d4b\u7684\u6b66\u529f\u540e\uff0c\u90fd\u5728\u6697\u95ee\u81ea\u5df1\uff1a\u201c\u8be5\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u4ed6\u4eec\u4fa7\u773c\u89c1\u5353\u957f\u537f\u5411\u5c71\u4e0a\u8d70\u53bb\uff0c\u81ea\u5df1\u82e5\u662f\u4e0d\u52a0\u62e6\u963b\uff0c\u5219\u6d77\u5357\u4e09\u5251\u7684\u989c\u9762\u4f55\u5b58\uff0c\u4f46\u81ea\u5df1\u82e5 \u662f\u52a0\u4ee5\u62e6\u963b\uff0c\u5374\u672a\u5fc5\u662f\u8fd9\u5c11\u5e74\u7684\u654c\u624b\uff0c\u82e5\u662f\u8d25\u5728\u8fd9\u5c11\u5e74\u7684\u624b\u4e0b\uff0c\u90a3\u5c82\u975e\u66f4\u52a0\u662f\u6c42\u8363\u53cd\u8fb1\uff1f \u3000\u3000\u4e09\u4eba\u56db\u4e0b\u770b\u4e86\u4e00\u773c\uff0c\u53ea\u89c1\u591c\u8272\u6c89\u6c89\uff0c\u7a7a\u5c71\u5bc2\u5bc2\uff0c\u9664\u4e86\u81ea\u5df1\u4e09\u4eba\u548c\u8fd9\u5c11\u5e74\u4e4b\u5916\uff0c\u4fbf\u518d\u65e0\u4eba \u8e2a\uff0c\u4e09\u4eba\u53c8\u5bf9\u671b\u4e00\u773c\uff0c\u5fc3\u91cc\u5404\u81ea\u60f3\u9053\uff1a\u201c\u8fd9\u91cc\u6ca1\u4eba\u770b\u89c1\uff0c\u6211\u8d70\u4e86\u4e5f\u6ca1\u6709\u4eba\u77e5\u9053\u3002\u201d \u3000\u3000\u8981\u77e5\u9053\u8fd9\u4e09\u4eba\u4e0e\u4e11\u4eba\u6e29\u5982\u7389\u672c\u975e\u6df1\u4ea4\uff0c\u4ed6\u4eec\u81ea\u7136\u4e0d\u4f1a\u4e3a\u5979\u5356\u547d\u3002 \u3000\u3000\u4e09\u4eba\u81ea\u5e7c\u751f\u957f\u4e00\u5904\uff0c\u5fc3\u610f\u672c\u5c31\u76f8\u901a\uff0c\u5404\u81ea\u6253\u4e86\u4e2a\u773c\u8272\uff0c\u4fbf\u9f50\u5730\u5411\u5c71\u4e0b\u60ca\u53bb\uff0c\u5353\u957f\u537f\u8d70\u5f97 \u6781\u6162\uff0c\u53ea\u9053\u8fd9\u4e09\u4eba\u4f1a\u4ece\u80cc\u540e\u5411\u81ea\u5df1\u88ad\u51fb\uff0c\u54ea\u77e5\u8d70\u4e86\u5341\u6570\u6b65\uff0c\u7b49\u4e86\u8bb8\u4e45\uff0c\u80cc\u540e\u4ecd\u662f\u5bc2\u65e0\u58f0\u54cd\uff0c \u4ed6\u5fc3\u91cc\u5947\u602a\uff0c\u987f\u8db3\u8f6c\u8eab\u671b\u53bb\uff0c\u53ea\u89c1\u4e00\u6761\u5c0f\u8def\uff0c\u5a49\u8712\u8fd4\u5411\u5c71\u4e0b\uff0c\u5939\u9053\u4e24\u884c\u6797\u6728\uff0c\u53f3\u9762\u6797\u6728\u659c \u4e0b\uff0c\u60f3\u662f\u5c71\u8fb9\uff0c\u5de6\u9762\u6797\u6728\u659c\u4e0a\uff0c\u60f3\u662f\u5c71\u5d16\uff0c\u8fd9\u4e24\u884c\u6797\u6728\uff0c\u6b64\u523b\u4ff1\u662f\u5bc2\u65e0\u4eba\u58f0\uff0c\u90a3\u4e09\u4e2a\u5f69\u8863\u602a \u4eba\u65e9\u5df2\u4e0d\u77e5\u6f5c\u5230\u54ea\u91cc\u53bb\u4e86\u3002 \u3000\u3000\u60f3\u5230\u65b9\u624d\u8fd9\u4e09\u4eba\u90a3\u79cd\u8dbe\u9ad8\u6c14\u626c\u7684\u6837\u5b50\uff0c\u4ed6\u5fc3\u91cc\u6709\u4e9b\u597d\u7b11\uff0c\u4f46\u8f6c\u8eab\u671b\u5411\u5c71\u4e0a\uff0c\u4ea6\u6709\u4e00\u6761\u5c71 \u8def\uff0c\u5a49\u8712\u7740\u901a\u4e86\u4e0a\u53bb\uff0c\u4ea6\u6709\u4e24\u884c\u6797\u6728\uff0c\u5939\u9053\u800c\u7acb\uff0c\u8fd9\u5c71\u4e0a\u6df1\u6c89\u7684\u591c\u8272\uff0c\u867d\u548c\u5c71\u4e0b\u5b8c\u5168\u4e00\u6837\uff0c \u4f46\u5728\u8fd9\u6df1\u6c89\u7684\u591c\u8272\u4e2d\u7a76\u7adf\u9690\u85cf\u7740\u4ec0\u4e48\uff0c\u5374\u4ee4\u4ed6\u96be\u4ee5\u63a8\u6d4b\uff0c\u4ed6\u811a\u6b65\u4e00\u987f\uff0c\u4eff\u4f5b\u6253\u4e86\u4e2a\u5bd2\u5664\uff0c\u6697 \u81ea\u5fd6\u9053\uff1a\u201c\u6b64\u5c71\u5982\u6b64\u4e4b\u6df1\uff0c\u90a3\u4e11\u4eba\u6e29\u5982\u738b\u7a76\u7adf\u5728\u5c71\u4e2d\u4f55\u5904\uff0c\u6211\u4e5f\u4e0d\u77e5\u9053\uff0c\u90a3\u4e9b\u7ea2\u88f3\u5c11\u5973\u4eec\u53c8 \u90fd\u8d70\u4e86\uff0c\u6211\u4e5f\u4e0d\u5982\u4e0b\u5c71\u53bb\u5427\u3002\u201d \u3000\u3000\u4f46\u5fc3\u5ff5\u8f6c\u5904\uff0c\u4ed6\u4e0d\u7981\u53c8\u6697\u7b11\u81ea\u5df1\uff1a\u201c\u5353\u957f\u537f\u5440\u5353\u957f\u537f\uff0c\u4f60\u82e5\u662f\u4e0d\u6562\u4e0a\u5c71\uff0c\u53ea\u7ba1\u4e5f\u5982\u90a3\u4e9b \u4eba\u4e00\u822c\u6e9c\u8d70\u597d\u4e86\uff0c\u53c8\u4f55\u6614\u66ff\u81ea\u5df1\u627e\u4e2a\u501f\u53e3\uff0c\u4f60\u6b64\u756a\u4e0a\u5c71\uff0c\u82e5\u7136\u627e\u4e0d\u7740\u4eba\u5bb6\uff0c\u96be\u9053\u4eba\u5bb6\u4fbf\u4e0d\u4f1a \u6765\u627e\u4f60\u5417\uff1f\u201d \u3000\u3000\u4e00\u5ff5\u81f3\u6b64\uff0c\u4ed6\u4e00\u633a\u80f8\u819b\uff0c\u5411\u5c71\u4e0a\u8d70\u53bb\u3002 \u7b2c\u5341\u4e09\u7ae0\u3000\u5929\u7985\u5bfa\u4e2d>> \u53e4\u9f99\u300a\u6708\u5f02\u661f\u90aa\u300b \u7b2c\u5341\u4e09\u7ae0\u3000\u5929\u7985\u5bfa\u4e2d \u3000\u3000\u5353\u957f\u537f\u620a\u672a\u65f6\u5206\u79bb\u5f00\u4e34\u5b89\u57ce\uff0c\u4e00\u8def\u884c\u6765\uff0c\u53c8\u9047\u7740\u8fd9\u4e9b\u53d8\u6545\uff0c\u4ea6\u4e0d\u77e5\u65f6\u95f4\u8fc7\u4e86\u591a\u4e45 \u3000\u3000\uff0c\u53ea\u89c9\u6b64\u523b\u591c\u8272\u8d8a\u6765\u8d8a\u6df1\uff0c\u5929\u4e0a\u661f\u6cb3\u803f\u803f\uff0c\u5730\u4e0a\u6797\u6728\u82cd\u82cd\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u4ed6\u4eff\u4f5b\u53c8\u89c9\u5f97 \u3000\u3000\u5929\u5730\u867d\u5927\uff0c\u5374\u53ea\u5269\u4e0b\u4e86\u4ed6\u4e00\u4e2a\u4eba\uff0c\u4e0d\u7981\u767e\u611f\u4e1b\u751f\uff0c\u7adf\u9ad8\u58f0\u6717\u53f9\u9053\uff1a \u3000\u3000\u201c\u98d3\u4f5c\u6d77\u6d51\uff0c\u5929\u6c34\u6e9f\u8352\uff0c \u3000\u3000\u4e91\u5c6f\u4e5d\u6cb3\uff0c\u96ea\u7acb\u4e09\u6c5f\uff0c \u3000\u3000\u68a6\u5e7b\u53bb\u6765\uff0c\u8c01\u5c11\u8c01\u591a\uff1f \u3000\u3000\u5f39\u6307\u592a\u606f\uff0c\u6d6e\u751f\u51e0\u4f55\uff01 \u3000\u3000\u8981\u77e5\u9053\u4ed6\u6b64\u523b\u672c\u60f3\u5f15\u51fa\u522b\u4eba\u6765\uff0c\u662f\u4ee5\u624d\u5c06\u8fd9\u6709\u5b8b\u4e00\u4ee3\u8bcd\u8c6a\u4e4b\u8a89\u82cf\u62ed\u7684\u56db\u8a00\u53e4\u8bd7\u968f\u610f\u62e9\u4e86 \u4e24\u6bb5\uff0c\u9ad8\u58f0\u5ff5\u51fa\uff0c\u4f46\u5ff5\u4e86\u51e0\u53e5\uff0c\u56db\u4e0b\u4ecd\u662f\u7a7a\u5c71\u5bc2\u5bc2\uff0c\u9759\u65e0\u4eba\u58f0\uff0c\u4ed6\u60f3\u5230\u201c\u5f39\u6307\u592a\u606f\uff0c\u6d6e\u751f\u51e0 \u4f55\uff01\u201d\u4e0d\u89c9\u5c06\u8fd9\u4e24\u53e5\u53c8\u4f4e\u8bf5\u4e24\u904d\uff0c\u610f\u5174\u7a81\u7136\u53d8\u5f97\u9611\u73ca\u8d77\u6765\u3002 \u3000\u3000\u6b64\u523b\u4ed6\u6f2b\u65e0\u76ee\u7684\u4e4b\u5730\uff0c\u4ea6\u4e0d\u77e5\u90a3\u4e11\u4eba\u6e29\u5982\u738b\u8bbe\u4e0b\u7684\u5927\u4f1a\u4f1a\u5740\uff0c\u7a76\u7adf\u662f\u5728\u4f55\u5904\uff0c\u662f\u4ee5\u4fbf\u672a \u65bd\u51fa\u8f7b\u529f\uff0c\u53ea\u662f\u4fe1\u6b65\u800c\u884c\uff0c\u7a81\u7136\u77a5\u89c1\u524d\u9762\u591c\u8272\u8c37\u4e2d\uff0c\u6709\u5e62\u5e62\u5c4b\u5f71\uff0c\u4ed6\u7cbe\u795e\u4e00\u632f\uff0c\u6025\u6b65\u8d70\u4e86\u8fc7 \u53bb\uff0c\u53ea\u89c1\u524d\u9762\u5c71\u9053\u65c1\u7684\u4e00\u7247\u571f\u5c97\u4e4b\u4e0a\uff0c\u7adf\u5efa\u7740\u4e00\u5ea7\u5bfa\u89c2\uff0c\u4ed6\u4e00\u63a0\u800c\u4e0a\uff0c\u5374\u89c1\u8fd9\u5ea7\u5bfa\u89c2\u5df2\u9887\u4e3a \u6b8b\u7834\uff0c\u5927\u95e8\u524d\u7684\u533e\u989d\u4e4b\u4e0a\uff0c\u4f9d\u7a00\u53ef\u4ee5\u8fa8\u51fa\u662f\u201c\u5929\u7985\u5bfa\u201d\u4e09\u4e2a\u91d1\u6f06\u5265\u843d\u7684\u5927\u5b57\u3002 \u3000\u3000\u4ed6\u5931\u671b\u5730\u53f9\u606f\u4e00\u58f0\uff0c\u77e5\u9053\u8fd9\u7834\u5e99\u4e0e\u8fd9\u4e11\u4eba\u6e29\u5982\u7389\u5b9a\u65e0\u5e72\u7cfb\uff0c\u4f46\u767e\u65e0\u804a\u8d56\u4e4b\u4e2d\uff0c\u4ed6\u8e0c\u8e87\u534a \u664c\uff0c\u7adf\u8d70\u8fdb\u5927\u6bbf\uff0c\u76ee\u5149\u671b\u5904\uff0c\u5374\u89c1\u8fd9\u6c89\u843d\u7684\u591c\u8272\u4e4b\u4e2d\u7684\u4f5b\u6bbf\uff0c\u795e\u53f0\u4f5b\u50cf\uff0c\u7ade\u8fd8\u4ff1\u5168\uff0c\u5f53\u4e2d\u4f9b \u7740\u4e00\u5c0a\u4e08\u4f59\u4f5b\u50cf\uff0c\u5782\u9996\u4f4e\u7709\uff0c\u4f3c\u4e4e\u5728\u4e3a\u4e16\u4eba\u9ed8\u7977\uff0c\u53c8\u4f3c\u4e4e\u5728\u601c\u60dc\u7740\u4e16\u4eba\u7684\u751f\u8001\u75c5\u6b7b\uff0c\u65e0\u9650\u6101 \u82e6\u3002 \u3000\u3000\u65b9\u4ece\u5341\u4e08\u7ea2\u5c18\u3001\u6c5f\u6e56\u4ec7\u6740\u4e2d\u8d70\u6765\u7684\u5353\u957f\u537f\uff0c\u6597\u7136\u6765\u5230\u8fd9\u6837\u6240\u5728\uff0c\u89c1\u4e86\u8fd9\u5c0a\u4f5b\u50cf\uff0c\u4e00\u65f6\u4e4b \u95f4\uff0c\u5fc3\u4e2d\u4ea6\u4e0d\u77e5\u4ec0\u4e48\u6ecb\u5473\uff0c\u76ee\u5149\u56db\u8f6c\uff0c\u53ea\u89c1\u8fd9\u4f5b\u6bbf\u56db\u58c1\u4f3c\u4e4e\u8fd8\u753b\u7740\u58c1\u753b\uff0c\u867d\u7136\u4ea6\u662f\u91d1\u6f06\u5265 \u843d\uff0c\u4f46\u4ea6\u53ef\u4f9d\u7a00\u8fa8\u51fa\u662f\u4f5b\u7956\u5f53\u5e74\u5728\u83e9\u63d0\u6811\u4e0b\u5f97\u9053\u6b63\u679c\u7684\u6545\u4e8b\u3002 \u3000\u3000\u4ed6\u65b9\u624d\u4e0d\u987e\u4e00\u5207\u5371\u9669\u4e4b\u4e0b\uff0c\u51b3\u5fc3\u8981\u5230\u8fd9\u5929\u76ee\u5c71\u6765\u7684\u65f6\u5019\uff0c\u53ea\u9053\u6765\u5230\u8fd9\u5929\u76ee\u5c71\u4e0a\uff0c\u5904\u5904\u4ff1 \u662f\u5bb3\u4eba\u7684\u9677\u9631\uff0c\u54ea\u77e5\u8d70\u4e86\u4e00\u6bb5\uff0c\u4ed6\u867d\u7136\u5927\u53eb\u5927\u56b7\uff0c\u5374\u65e0\u4eba\u6765\u776c\u4ed6\uff0c\u4ed6\u81ea\u5df1\u7adf\u6765\u5230\u8fd9\u79cd\u5730\u65b9\u3002 \u3000\u3000\u524d\u884c\u4e24\u6b65\uff0c\u4ed6\u79fb\u52a8\u7684\u4eba\u5f71\uff0c\u5212\u7834\u4e86\u6ee1\u6bbf\u7684\u661f\u6708\u4e4b\u5149\uff0c\u4e00\u9635\u591c\u98ce\u5439\u6765\uff0c\u4ed6\u671b\u7740\u8fd9\u4f5b\u50cf\uff0c\u8fd9 \u56fe\u753b\uff0c\u4e00\u65f6\u53d1\u6068\u55d4\u559c\uff0c\u767e\u611f\u4ff1\u751f\uff0c\u4ea4\u76f8\u7eb7\u66ff\uff0c\u4f46\u500f\u800c\u5347\u8d77\uff0c\u500f\u7136\u843d\u4e0b\uff0c\u6709\u65f6\u5fc3\u4e2d\u5374\u53c8\u7a7a\u7a7a\u6d1e \u6d1e\uff0c\u4f3c\u4e4e\u4ec0\u4e48\u4e5f\u60f3\u4e0d\u8d77\u4e86\uff0c\u4ed6\u957f\u53f9\u4e00\u58f0\uff0c\u5bfb\u4e86\u4e2a\u795e\u50cf\u524d\u7684\u6b8b\u7834\u84b2\u56e2\uff0c\u62cd\u4e86\u62cd\uff0c\u54ea\u77e5\u4e0a\u9762\u5374\u65e0 \u5c18\u4e0a\uff0c\u4ed6\u5fc3\u4e00\u5947\uff0c\u77ee\u8eab\u5750\u4e86\u4e0b\u53bb\uff0c\u65b9\u81ea\u6697\u4e2d\u5bfb\u601d\u3002 \u3000\u3000\u5374\u542c\u4e07\u7c41\u4ff1\u5bc2\u4e4b\u4e2d\u7684\u5927\u6bbf\uff0c\u7a81\u7136\u4f20\u6765\u201c\u7b03\u201d\u7684\u4e00\u58f0\u6728\u9c7c\u4e4b\u58f0\u3002 \u3000\u3000\u5353\u957f\u537f\u5fc3\u4e2d\u4e00\u9707\uff0c\u51dd\u795e\u542c\u53bb\uff0c\u53ea\u542c\u8fd9\u201c\u7b03\u7b03\u201d\u7684\u6728\u9c7c\u58f0\u4f3c\u4e4e\u6765\u81ea\u6bbf\u540e\u3002 \u3000\u3000\u5239\u90a3\u4e4b\u95f4\uff0c\u4ed6\u5fc3\u5f26\u4e3a\u4e4b\u5927\u60ca\uff0c\u5237\u7684\u7ad9\u4e86\u8d77\u6765\uff0c\u4f5b\u6bbf\u4e2d\u6709\u6728\u9c7c\u58f0\u4f20\u51fa\u672c\u662f\u5929\u7ecf\u5730\u4e49\u4e4b\u4e8b\uff0c \u4e00\u70b9\u7528\u4e0d\u7740\u60ca\u614c\uff0c\u5353\u957f\u537f\u773c\u4e2d\u770b\u6765\uff0c\u5728\u8fd9\u5929\u76ee\u5c71\u91cc\u4e00\u5207\u4fbf\u90fd\u4f3c\u4e4e\u6709\u4e9b\u5f02\u6837\uff0c\u4f55\u51b5\u8fd9\u4f5b\u5e99\u662f\u5982 \u6b64\u9893\u8d25\uff0c\u65f6\u5149\u662f\u5982\u6b64\u6df1\u591c\uff0c\u5728\u8fd9\u6df1\u591c\u7684\u7834\u5bfa\u4e2d\u4f1a\u6709\u6728\u9c7c\u4e4b\u58f0\uff0c\u4e5f\u786e\u975e\u5bfb\u5e38\u4e4b\u4e8b\u3002 \u3000\u3000\u542c\u4e86\u534a\u664c\uff0c\u90a3\u6728\u9c7c\u58f0\u4ecd\u7136\u201c\u7b03\u7b03\u201d\u6572\u4e2a\u4e0d\u505c\uff0c\u4ed6\u6697\u4e2d\u5438\u4e86\u53e3\u957f\u6c14\uff0c\u8863\u8896\u5fae\u62c2\uff0c\u5237\u7684\u63a0\u5165 \u540e\u9662\uff0c\u53ea\u89c1\u540e\u9662\u4e2d\u7684\u4e00\u4e2a\u504f\u6bbf\u7684\u7a97\u7eb8\u4e0a\uff0c\u679c\u7136\u6709\u660f\u9ec4\u7684\u706f\u5149\u6620\u51fa\uff0c\u800c\u8fd9\u7b03\u7b03\u7684\u6728\u9c7c\u58f0\u4fbf\u662f\u4ece \u8fd9\u504f\u6bbf\u4f20\u6765\uff0c\u5353\u957f\u537f\u8eab\u5f62\u4e0d\u505c\uff0c\u7b14\u76f4\u7684\u63a0\u4e86\u8fc7\u53bb\uff0c\u53ea\u89c1\u7a97\u6846\u7d27\u95ed\uff0c\u53ea\u6709\u6700\u4e0a\u9762\u4e00\u683c\u7a97\u7eb8\u4f3c\u4e4e \u6709\u4e2a\u8c46\u5927\u7684\u7834\u6d1e\u3002 \u3000\u3000\u6df1\u591c\u8352\u5bfa\u4e4b\u4e2d\u6709\u4eba\u5ff5\u7ecf\uff0c\u5df2\u662f\u5947\u4e8b\uff0c\u800c\u5728\u8fd9\u79cd\u8352\u5bfa\u4e2d\u7adf\u6709\u5982\u6b64\u5b8c\u6574\u7684\u7a97\u6237\uff0c\u4f3c\u4e4e\u66f4\u662f\u4ef6 \u5947\u4e8b\uff0c\u5353\u957f\u537f\u5fc3\u4e2d\u7591\u4e91\u5927\u8d77\uff0c\u6beb\u4e0d\u8003\u8651\u5730\u7eb5\u8eab\u8dc3\u4e0a\uff0c\u4e00\u624b\u642d\u4e0a\u5c4b\u6a90\uff0c\u51d1\u9996\u4ece\u90a3\u7834\u6d1e\u4e2d\u5f80\u91cc\u4e00 \u770b\uff0c\u5374\u89c1\u8fd9\u504f\u6bbf\u4e2d\u56db\u4e0b\u7a7a\u7a7a\u8361\u8361\u7684\uff0c\u53ea\u6709\u5f53\u4e2d\u4e00\u5f20\u795e\u684c\uff0c\u4e0a\u9762\u4f9b\u7740\u4e00\u9762\u7075\u724c\uff0c\u7075\u724c\u65c1\u4e00\u76cf\u5b64 \u706f\uff0c\u706f\u5149\u660f\u6697\uff0c\u7075\u724c\u4e0a\u7684\u5b57\u8ff9\u53c8\u5c0f\uff0c\u4e0a\u9762\u5199\u7684\u4ec0\u4e48\uff0c\u4e00\u65f6\u65e0\u6cd5\u770b\u6e05\uff0c\u4f46\u795e\u53f0\u524d\u8dea\u7740\u4e00\u4eba\uff0c\u867d \u5176\u80cc\u5411\u5353\u957f\u537f\uff0c\u4ed6\u5374\u53ef\u5206\u8fa8\u51fa\u662f\u4e2a\u5973\u5b50\u3002 \u3000\u3000\u8fd9\u5973\u5b50\u4e00\u8eab\u7384\u8272\u7d20\u670d\uff0c\u957f\u53d1\u62ab\u80a9\uff0c\u5982\u4e91\u5982\u96fe\uff0c\u5353\u957f\u537f\u5fc3\u4e2d\u4e00\u60ca\uff0c\u8fd9\u4f5b\u5bfa\u4e4b\u4e2d\u600e\u4e48\u4f1a\u6709\u4e2a \u957f\u53d1\u7684\u5973\u5b50\uff1f \u3000\u3000\u53ea\u89c1\u8fd9\u5973\u5b50\u53cc\u80a9\u8038\u52a8\uff0c\u4e0d\u4f4f\u5730\u6572\u54cd\u6728\u9c7c\uff0c\u53e3\u4e2d\u4f3c\u4e4e\u4e5f\u5728\u5ff5\u7740\u4f5b\u7ecf\uff0c\u6df1\u6c89\u7684\u591c\u8272\uff0c\u660f\u9ec4\u7684 \u706f\u5149\uff0c\u7a7a\u6d1e\u7684\u4f5b\u50cf\uff0c\u886c\u7740\u8fd9\u5b64\u5b64\u5355\u5355\u8dea\u5728\u8fd9\u91cc\u7684\u5973\u5b50\uff0c\u51c4\u51c4\u51c9\u51c9\u7684\u6728\u9c7c\u58f0\uff0c\u8ba9\u4eba\u542c\u4e86\uff0c\u5fc3\u5e95 \u4e0d\u7531\u81ea\u4e3b\u7684\u6cdb\u8d77\u6765\u4e00\u9635\u5bd2\u610f\u3002 \u3000\u3000\u5353\u957f\u537f\u624b\u638c\u4e00\u677e\uff0c\u98d8\u8eab\u843d\u5728\u5730\u4e0a\uff0c\u5fc3\u4e2d\u6697\u5fd6\uff1a\u201c\u8fd9\u5973\u5b50\u4e0d\u77e5\u662f\u8c01\uff0c\u600e\u5730\u6df1\u66f4\u534a\u591c\u5730\u8dd1\u5230 \u8fd9\u8352\u5bfa\u6765\u5ff5\u7ecf\u2014\u2014\u201d\u5fc3\u5ff5\u4e00\u8f6c\uff1a\u201c\u5662\uff0c\u662f\u4e86\uff0c\u8fd9\u5973\u5b50\u60f3\u662f\u4e2a\u5e26\u53d1\u4fee\u884c\u7684\u5c3c\u59d1\uff0c\u56e0\u770b\u8fd9\u8352\u5bfa\u65e0 \u4eba\uff0c\u4fbf\u5728\u6b64\u5904\u4f4f\u4e0b\u2014\u2014\u4e0d\u77e5\u5979\u77e5\u4e0d\u77e5\u9053\uff0c\u8fd9\u5929\u76ee\u5c71\u4e2d\u8f6c\u77ac\u4fbf\u8981\u53d8\u6210\u6c5f\u6e56\u51f6\u6740\u4e4b\u5730\uff0c\u518d\u4e5f\u5bb9\u4e0d \u5f97\u5979\u5728\u6b64\u6e05\u4fee\u3001\u201d\u4ed6\u5fc3\u5ff5\u6570\u8f6c\uff0c\u7a81\u5730\u60f3\u5230\u8fd9\u5973\u5b50\u65e2\u7136\u5728\u5929\u76ee\u5c71\u4e0a\u5c45\u4f4f\uff0c\u4e0d\u77e5\u662f\u5426\u77e5\u9053\u90a3\u4e11\u4eba \u6e29\u5982\u7389\u5728\u6b64\u7684\u884c\u52a8\uff0c\u4ed6\u5fc3\u4e2d\u4e00\u9762\u60f3\u7740\uff0c\u4e00\u9762\u4fbf\u505c\u6b65\u5411\u8fd9\u504f\u6bbf\u7684\u95e8\u6237\u8d70\u53bb\uff0c\u65b9\u4e14\u8d70\u5230\u95e8\u53e3\uff0c\u53ea \u542c\u91cc\u9762\u6728\u9c7c\u4e4b\u58f0\u672a\u505c\uff0c\u5374\u5df2\u4f20\u51fa\u4e00\u4e2a\u51b0\u51b7\u7684\u58f0\u97f3\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u8fdb\u6765\uff01\u201d \u3000\u3000\u6b64\u523b\u4ed6\u867d\u672a\u65bd\u5c55\u8f7b\u529f\uff0c\u4f46\u811a\u4e0a\u5374\u4ecd\u8d70\u5f97\u751a\u8f7b\uff0c\u8fd9\u504f\u6bbf\u4e2d\u8bf5\u7ecf\u7684\u5973\u5b50\uff0c\u7adf\u7136\u542c\u51fa\u4ed6\u7684\u811a\u6b65 \u58f0\uff0c\u5353\u957f\u537f\u5fc3\u4e2d\u4e0d\u7981\u53c8\u4e3a\u4e4b\u4e00\u9707\uff0c\u6c89\u58f0\u9053\uff0c\u201c\u5728\u4e0b\u6709\u4e00\u4e8b\u76f8\u95ee\uff0c\u6df1\u591c\u6253\u6270\uff0c\u8fd8\u671b\u5973\u5c45\u58eb\u6055 \u7f6a\u3002\u201d \u3000\u3000\u53ea\u542c\u91cc\u9762\u4f3c\u4e4e\u51b7\u51b7\u54fc\u4e86\u4e00\u58f0\uff0c\u6728\u9c7c\u4e4b\u58f0\u7a81\u7136\u987f\u4f4f\uff0c\u5353\u957f\u537f\u786c\u7740\u5934\u76ae\u63a8\u5f00\u4e86\u95e8\uff0c\u5374\u89c1\u91cc\u9762 \u7d20\u670d\u62ab\u53d1\u7684\u5973\u5b50\u4ecd\u7136\u80cc\u95e8\u800c\u8dea\uff0c\u52a8\u4e5f\u672a\u52a8\uff0c\u4f46\u795e\u53f0\u4e0a\u7684\u7075\u4f4d\uff0c\u5374\u5df2\u65e0\u5f71\u65e0\u8e2a\u4e86\u3002 \u3000\u3000\u5353\u957f\u537f\u5fc3\u4e2d\u72d0\u7591\uff0c\u8f7b\u8f7b\u5e72\u54b3\u4e00\u58f0\uff0c\u90a3\u5973\u5b50\u4e00\u63a0\u79c0\u53d1\uff0c\u7f13\u7f13\u56de\u8fc7\u5934\u6765\uff0c\u5353\u957f\u537f\u4e00\u89c1\u8fd9\u5973\u5b50 \u4e4b\u9762\uff0c\u5fc3\u4e2d\u4e0d\u7531\u66f4\u5927\u5403\u4e00\u60ca\uff0c\u5446\u5446\u5730\u6123\u5728\u90a3\u513f\u4e00\u53e5\u8bdd\u4e5f\u8bf4\u4e0d\u51fa\u6765\u4e86\u3002 \u3000\u3000\u8fd9\u5973\u5b50\u4e00\u773c\u671b\u89c1\u5353\u957f\u537f\uff0c\u795e\u8272\u4ea6\u7a81\u7136\u4e00\u53d8\uff0c\u4f46\u77ac\u5373\u8f7b\u8f7b\u53f9\u4e86\u53e3\u6c14\uff1a\u201c\u539f\u6765\u662f\u4f60\uff01\u201d \u3000\u3000\u5979\u8a00\u8bed\u4e4b\u95f4\u6beb\u65e0\u6545\u610f\uff0c\u5353\u957f\u537f\u4e0d\u7981\u53c8\u4e3a\u4e4b\u5927\u5947\uff0c\u539f\u6765\u8fd9\u4f4d\u5973\u5b50\u7ade\u662f\u90a3\u4e11\u4eba\u6e29\u5982\u7389\u6700\u949f\u7231 \u7684\u5f1f\u5b50\u6e29\u747e\u3002 \u3000\u3000\u5728\u8fd9\u5239\u90a3\u4e4b\u95f4\uff0c\u4ed6\u773c\u524d\u4f3c\u4e4e\u53c8\u6cdb\u8d77\u4e86\u6570\u65e5\u4e4b\u524d\uff0c\u521d\u89c1\u5230\u8fd9\u5c11\u5973\u7684\u666f\u8c61\u3002 \u3000\u3000\u90a3\u65f6\u5979\u5a9a\u7b11\u5982\u82b1\uff0c\u8a00\u8bed\u5982\u6c34\uff0c\u5374\u53c8\u80fd\u5728\u8a00\u7b11\u4e4b\u95f4\uff0c\u7f6e\u4eba\u6b7b\u547d\uff0c\u800c\u6b64\u523b\u5979\u5374\u662f\u4e00\u8eab\u7d20\u670d\uff0c \u7709\u5cf0\u655b\u6101\uff0c\u54ea\u91cc\u8fd8\u662f\u6570\u65e5\u524d\u7684\u6837\u5b50\uff0c\u5728\u8fd9\u77ed\u77ed\u6570\u65e5\u4e4b\u95f4\u7adf\u4f7f\u8fd9\u660e\u5a9a\u5201\u86ee\u7684\u5c11\u5973\u4e00\u53d8\u800c\u4e3a\u5982\u6b64 \u60b2\u6028\uff0c\u7684\u786e\u662f\u5353\u957f\u537f\u6599\u60f3\u4e0d\u900f\u4e4b\u4e8b\u3002 \u3000\u3000\u4ed6\u5446\u5446\u5730\u6123\u4e86\u534a\u664c\uff0c\u65b9\u81ea\u5e72\u54b3\u4e00\u58f0\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u539f\u6765\u662f\u6e29\u59d1\u5a18\u3002\u201d \u3000\u3000\u8fde\u9000\u4e09\u6b65\uff0c\u9000\u5230\u95e8\u8fb9\uff0c\u811a\u6b65\u7a81\u53c8\u505c\u4e0b\uff0c\u6697\u5fd6\u9053\uff1a\u201c\u5353\u957f\u537f\u5440\u5353\u957f\u537f\uff0c\u4f60\u5230\u8fd9\u5929\u76ee\u5c71\u4e0a\uff0c \u4e0d\u5c31\u662f\u4e3a\u7740\u8981\u89c1\u6b64\u4eba\u5417\uff1f\u600e\u7684\u4e00\u89c1\u5230\u5979\uff0c\u4f60\u5c31\u8981\u8d70\uff0c\u201d\u8de8\u524d\u4e00\u6b65\uff0c\u6c89\u58f0\u53c8\u9053\uff1a\u201c\u591c\u6df1\u5982\u6b64\uff0c \u6e29\u59d1\u5a18\u4e00\u4eba\u5728\u6b64\uff0c\u5374\u662f\u4e3a\u7740\u4ec0\u4e48\u5462\uff1f\u201d \u3000\u3000\u6e29\u747e\u56de\u8fc7\u5934\uff0c\u671b\u4e86\u671b\u9762\u524d\u7684\u6728\u9c7c\uff0c\u7a81\u5730\u82e6\u53f9\u4e00\u58f0\uff0c\u7f13\u7f13\u76f4\uff1a\u201c\u4f60\u4e0e\u6211\u6570\u65e5\u524d\u867d\u662f\u654c\u4eba\uff0c \u4f46\u73b0\u5728\u6211\u5df2\u4e0d\u60f3\u4e0e\u4f60\u4e3a\u654c\uff0c\u4e0d\u8fc7\u2014\u2014\u6211\u5728\u8fd9\u91cc\u4e8e\u4ec0\u4e48\uff0c\u4e5f\u4e0d\u5173\u4f60\u4e8b\uff0c\u4f60\u8fd8\u662f\u5feb\u4e9b\u8d70\u5427\uff01\u201d \u3000\u3000\u5979\u8bf4\u5230\u540e\u6765\uff0c\u8a00\u8bed\u4e2d\u53c8\u9732\u51fa\u4e86\u6614\u65e5\u7684\u950b\u8292\uff0c\u5353\u957f\u537f\u542c\u4e86\u53c8\u679c\u4e86\u4e00\u5446\uff0c\u4ed6\u5b9e\u5728\u4e0d\u77e5\u8be5\u5982\u4f55 \u4e0e\u8fd9\u5c11\u5973\u5e94\u5bf9\uff1b\u5446\u7acb\u534a\u664c\uff0c\u5fc3\u5ff5\u7a81\u7136\u4e00\uff0c\u52a8\uff0c\u8131\u53e3\u9053\uff1a\u201c\u59d1\u5a18\u5728\u6b64\u8bf5\u7ecf\uff0c\u4e0d\u77e5\u662f\u4e3a\u8c01\u5462\uff1f\u201d \u3000\u3000\u53ea\u89c1\u6e29\u747e\u731b\u4e00\u53e3\u5934\uff0c\u4e00\u53cc\u660e\u5a9a\u7684\u79cb\u6ce2\u4e2d\u7a81\u7136\u5c04\u51fa\u903c\u4eba\u7684\u5149\u8292\uff0c\u5353\u957f\u537f\u60f3\u5230\u90a3\u9ad8\u51a0\u7fbd\u58eb\u8bf4 \u7684\u6545\u4e8b\uff0c\u53c8\u60f3\u5230\u65b9\u624d\u5728\u795e\u53f0\u4e0a\u6b64\u523b\u7a81\u5730\u5931\u8e2a\u7684\u7075\u724c\u4e86\uff0c\u5fc3\u4e2d\u5df2\u6709\u6240\u609f\uff0c\u4fbf\u53c8\u957f\u53f9\u4e00\u58f0\u9053\uff1a \u201c\u5728\u4e0b\u66fe\u7ecf\u542c\u5f97\u6614\u65e5\u6c5f\u6e56\u95f4\uff0c\u6709\u4e24\u4f4d\u5927\u4fa0\uff0c\u90a3\u65f6\u6c5f\u6e56\u4e2d\u4eba\u79f0\u8fd9\u4e24\u4f4d\u5927\u4fa0\u53eb\u6881\u5b5f\u53cc\u4fa0\uff0c\u4e0d\u77e5\u59d1 \u5a18\u53ef\u66fe\u77e5\u9053\u8fd9\u4e24\u4f4d\u5927\u4fa0\u7684\u5927\u540d\u5417\uff1f\u201d \u3000\u3000\u4ed6\u4e00\u9762\u7f13\u7f13\u8bf4\u7740\uff0c\u4e00\u9762\u5374\u5728\u7559\u610f\u6e29\u747e\u7684\u9762\u8272\uff0c\u53ea\u89c1\u5979\u542c\u4e86\u8fd9\u6881\u5b5f\u53cc\u4fa0\u56db\u5b57\uff0c\u5168\u8eab\u7a81\u7136\u4e00 \u9707\uff0c\u76ee\u5149\u4e2d\u7684\u950b\u9510\uff0c\u5df2\u53d8\u4e3a\u4e00\u773c\u54c0\u6028\u4e4b\u8272\u3002 \u3000\u3000\u5353\u957f\u537f\u8bed\u58f0\u4e00\u4e86\uff0c\u5979\u7acb\u523b\u8131\u53e3\u63a5\u9053\uff1a\u201c\u4f60\u53ef\u5c31\u662f\u5353\u957f\u537f\uff1f\u201d \u3000\u3000\u8fd9\u6b21\u5374\u8f6e\u5230\u5353\u957f\u537f\u4e00\u9707\uff1a\u201c\u5979\u600e\u5730\u77e5\u9053\u6211\u7684\u540d\u5b57\uff1f\u201d \u3000\u3000\u65b9\u8981\u7b54\u8bdd\u3002\u54ea\u77e5\u2014\u2014\u95e8\u5916\u7a81\u7136\u54cd\u8d77\u4e00\u66b4\u559d\uff0c\u4e00\u6761\u957f\u5927\u7684\u4eba\u5f71\uff0c\u5939\u7740\u4e00\u822c\u5f3a\u70c8\u7684\u98ce\u58f0\uff0c\u548c \u4e00\u9635\u54d7\u7136\u7684\u91d1\u94c1\u4ea4\u545c\u4e4b\u58f0\uff0c\u65cb\u98ce\u822c\u7684\u6251\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u795e\u684c\u4e0a\u706f\u5149\u4e00\u82b1\uff0c\u5353\u957f\u537f\u5fc3\u4e2d\u4e00\u60ca\uff0c\u53ea\u89c9\u6b64\u4eba\u6765\u52bf\u731b\u6025\uff0c\u65b9\u81ea\u8f6c\u5236\u671b\u53bb\uff0c\u53ea\u89c9\u8eab\u524d\u98ce\u58f0 \u6fc0\u8361\uff0c\u5df2\u6709\u4e00\u6761\u957f\u6756\uff0c\u5288\u9762\u5411\u81ea\u5df1\u6253\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u5353\u957f\u537f\u5927\u559d\u4e00\u58f0\uff1a\u201c\u662f\u8c01\uff1f\u201d \u3000\u3000\u8eab\u8eaf\u731b\u65cb\u7f29\u5f00\u4e09\u5c3a\uff0c\u4f46\u542c\u201c\u7830\u201d\u7684\u4e00\u58f0\u5927\u9707\uff0c\u5730\u4e0a\u706b\u5149\u56db\u6e85\uff0c\u539f\u6765\u65b9\u624d\u8fd9\u4e00\u6756\u51fb\u4ed6\u4e0d \u7740\uff0c\u7adf\u51fb\u5728\u5730\u4e0a\uff0c\u5c06\u4e0a\u7684\u7855\u6c83\u820c\u5f97\u7c89\u788e\uff0c\u6fc0\u51fa\u706b\u82b1\uff0c\u8fd9\u4e00\u6756\u7684\u529b\u9053\u4e4b\u731b\uff0c\u53ef\u60f3\u800c\u77e5\u3002 \u3000\u3000\u5353\u957f\u537f\u83ab\u540d\u5176\u5999\u907f\u8fc7\u6765\u4eba\u51fb\u7684\u8fd9\u4e00\u6756\uff0c\u8fd8\u672a\u770b\u6e05\u8fd9\u4eba\u7a76\u7adf\u662f\u8c01\uff0c\u54ea\u77e5\u8fd9\u4eba\u52b2\u529b\u60ca\u4eba\uff0c\u4e00 \u6756\u867d\u7136\u51fb\u5728\u5730\u4e0a\uff0c\u4f46\u624b\u8155\u4e00\u6311\uff0c\u6b21\u62db\u968f\u4e0a\uff0c\u54d7\u5566\u5566\u4e00\u9635\u91d1\u94c1\u4ea4\u9e23\uff0c\u53c8\u662f\u4e00\u6756\uff0c\u5411\u5353\u957f\u537f\u62e6\u8170 \u626b\u53bb\u3002 \u3000\u3000\u82e5\u5728\u5e73\u65e5\uff0c\u8fd9\u4eba\u7684\u6756\u52bf\u867d\u7136\u60ca\u4eba\u731b\u70c8\uff0c\u4f46\u4ee5\u961c\u957f\u537f\u9971\u529f\u529b\uff0c\u4e0d\u96be\u65bd\u51fa\u56db\u4e24\u62e8\u5343\u65a4\u7684\u5185\u5bb6 \u529f\u592b\uff0c\u8f7b\u8f7b\u4e00\u5e26\uff0c\u4fbf\u53ef\u4f7f\u6b64\u4eba\u94c1\u6756\u8131\u624b\uff0c\u4f46\u4ed6\u4ece\u8fd9\u94c1\u6756\u4e0a\u53d1\u51fa\u7684\u8fd9\u9635\u91d1\u94c1\u4ea4\u9e23\u4e4b\u58f0\u4e2d\uff0c\u5374\u542c \u51fa\u6b64\u4eba\u662f\u8c01\u6765\uff0c\u4fbf\u4e0d\u65bd\u5c55\u6740\u624b\uff0c\u7eb5\u8eab\u4e00\u8dc3\uff0c\u8dc3\u8d77\u4e08\u4f59\uff0c\u53ea\u89c9\u4e00\u9635\u98ce\u58f0\u4ece\u811a\u5e95\u626b\u8fc7\u3002 \u3000\u3000\u4ed6\u5b9e\u4e0d\u613f\u4e0e\u6b64\u4eba\u4ea4\u624b\uff0c\u4f38\u624b\u4e00\u62db\uff0c\u638c\u5fc3\u7adf\u5438\u7740\u5c4b\u9876\uff0c\u4ed6\u8eab\u5f62\u4e00\u5f13\uff0c\u6574\u4e2a\u4eba\u7adf\u90fd\u8d34\u5230\u5c4b\u9876 \u4e0a\uff0c\u76ee\u5149\u4e0b\u626b\uff0c\u6717\u58f0\u559d\u9053\uff1a\u201c\u5927\u5e08\u8bf7\u6682\u4f4f\u624b\uff01\u201d \u3000\u3000\u90a3\u7a81\u7136\u95ea\u5165\u7684\u957f\u5927\u4eba\u5f71\uff0c\u8fde\u53d1\u4e24\u62db\uff0c\u4ff1\u90fd\u662f\u5c11\u6797\u5916\u5bb6\u7684\u7edd\u9876\u529f\u592b\uff0c\u53ea\u9053\u5bf9\u65b9\u5728\u8fd9\u95ee\u5e76\u4e0d \u751a\u5927\u7684\u623f\u95f4\u91cc\u4e00\u5b9a\u96be\u4ee5\u9003\u8fc7\u81ea\u5df1\u58f0\u5a01\u5982\u6b64\u60ca\u4eba\u7684\u4e24\u62db\uff0c\u54ea\u77e5\u4ed6\u4e24\u62db\u4e00\u53d1\uff0c\u5bf9\u65b9\u5374\u8fde\u4eba\u5f71\u90fd\u4e0d \u89c1\u4e86\u3002 \u3000\u3000\u53ea\u542c\u5230\u5353\u957f\u537f\u5728\u5c4b\u9876\u4e0a\u53d1\u58f0\uff0c\u4ed6\u65b9\u81ea\u62ac\u76ee\u671b\u53bb\uff0c\u89c1\u8671\u5353\u957f\u537f\u8fd9\u79cd\u7edd\u9876\u529f\u592b\uff0c\u5fc3\u4e2d\u4ea6\u4e0d\u7981 \u4e00\u60ca\uff1a\u201c\u54ea\u91cc\u6765\u7684\u6bdb\u5934\u5c0f\u5b50\uff0c\u7adf\u6709\u5982\u6b64\u529f\u592b\u3002\u201d\u4f46\u4ed6\u751f\u6027\u521a\u731b\u65f7\u5f3a\uff0c\u867d\u7136\u5fc3\u60ca\uff0c\u5374\u4ecd\u5927\u559d \u9053\uff1a\u201c\u81ed\u5c0f\u5b50\uff0c\u6709\u79cd\u7684\u5c31\u4e0b\u6765\uff0c\u4e0d\u7136\u6d12\u5bb6\u8df3\u4e0a\u53bb\u4e00\u6756\u628a\u4f60\u6253\u6b7b\u3002\u201d \u3000\u3000\u6e29\u747e\u81ea\u4ece\u542c\u4e86\u6881\u5b5f\u53cc\u4fa0\u7684\u540d\u5b57\u540e\uff0c\u795e\u60c5\u4e00\u76f4\u5982\u75f4\u5982\u9189\uff0c\u6b64\u523b\u65b9\u81ea\u62ac\u9996\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4e0b \u6765\uff0c\u6211\u6709\u8bdd\u8981\u95ee\u4f60\u3002\u201d \u3000\u3000\u53c8\u56de\u9996\u5bf9\u90a3\u4eba\u9053\uff1a\u201c\u5927\u5e08\uff0c\u4f60\u4e5f\u4e0d\u8981\u52a8\u624b\u4e86\u3002\u201d \u3000\u3000\u8fd9\u4eba\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u65b9\u624d\u6211\u4e00\u76f4\u5750\u5728\u5916\u9762\u7684\u84b2\u56e2\u4e0a\uff0c\u5750\u4e86\u4e00\u591c\uff0c\u521a\u521a\u51fa\u53bb\u65b9\u4fbf\u4e00\u4e0b\uff0c \u54ea\u77e5\u5c31\u88ab\u8fd9\u5c0f\u5f3a\u76d7\u95ef\u4e86\u8fdb\u6765\u2014\u2014\u201d\u5353\u957f\u537f\u5fc3\u4e2d\u4e00\u52a8\uff1a\u201c\u539f\u6765\u4ed6\u65b9\u624d\u5750\u5728\u5916\u9762\u7684\u84b2\u56e2\u4e0a\uff0c\u96be\u602a \u90a3\u4e0a\u9762\u6ca1\u6709\u5c18\u571f\u3002\u201d \u3000\u3000\u539f\u6765\u6b64\u4eba\u4fbf\u662f\u6c5f\u6e56\u4e0a\u6700\u6700\u559c\u6b22\u591a\u7ba1\u95f2\u4e8b\u7684\u5c11\u6797\u95e8\u4eba\u591a\u4e8b\u5934\u9640\u65e0\u6839\uff0c\u4ed6\u542c\u4e86\u6e29\u747e\u7684\u8bdd\uff0c\u548c \u5979\u4e00\u8d77\u6765\u5230\u5929\u76ee\u5c71\uff0c\u4f46\u5f53\u4ed6\u89c1\u4e86\u5929\u76ee\u5c71\u4e0a\u7684\u4e00\u4e9b\u90aa\u95e8\u5916\u9053\uff0c\u5374\u53c8\u76f8\u5904\u4e0d\u60ef\u4e86\uff0c\u672c\u6765\u65e9\u5c31\u8981\u4e0b \u5c71\u8d70\u4e86\uff0c\u4f46\u6e29\u747e\u5374\u8d39\u4e86\u5343\u8a00\u4e07\u8bed\uff0c\u5c06\u4ed6\u6316\u4f4f\uff0c\u4ed6\u5fc3\u91cc\u867d\u4e0d\u613f\uff0c\u4f46\u4e00\u6765\u5fc3\u6027\u559c\u6b22\u591a\u4e8b\uff0c\u4e8c\u6765\u5bf9 \u6e29\u747e\u4e5f\u6709\u4e9b\u559c\u7231\uff0c\u4fbf\u52c9\u5f3a\u7559\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u6b64\u523b\u6e29\u747e\u5728\u5185\u6bbf\u8bf5\u7ecf\uff0c\u4ed6\u5374\u5728\u5916\u9762\u671b\u51e4\uff0c\u4e0d\u51c6\u522b\u4eba\u8fdb\u6765\uff0c\u54ea\u77e5\u5c31\u5728\u4ed6\u51fa\u53bb\u65b9\u4fbf\u4e4b\u9645\uff0c\u5353 \u957f\u537f\u5374\u6070\u5de7\u95ef\u4e86\u8fdb\u6765\uff0c\u4ed6\u65b9\u4fbf\u8fc7\u540e\uff0c\u542c\u5230\u91cc\u9762\u6709\u4eba\u8bed\u4e4b\u58f0\uff0c\u8dd1\u6765\u4e00\u770b\uff0c\u7ade\u662f\u90a3\u4e2a\u88ab\u6e29\u747e\u6307\u505a \u5f3a\u76d7\u7684\u5c11\u5e74\uff0c\u4fbf\u4e0d\u5206\u9752\u7ea2\u7682\u767d\u7684\u6253\u4e86\u8fdb\u53bb\u3002 \u3000\u3000\u54ea\u77e5\u6e29\u747e\u6b64\u65f6\u5374\u53c8\u53eb\u4ed6\u4f4f\u624b\uff0c\u4ed6\u751f\u6027\u83bd\u649e\uff0c\u54ea\u91cc\u77e5\u9053\u5176\u4e2d\u66f2\u6298\uff0c\u602a\u6115\u5730\u671b\u7740\u6e29\u747e\uff0c\u5e0c\u671b \u5979\u80fd\u7ed9\u81ea\u5df1\u4e00\u4e2a\u89e3\u91ca\u3002 \u3000\u3000\u54ea\u77e5\u6e29\u747e\u5374\u53c8\u5e7d\u7136\u957f\u53f9\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u8fd9\u4eba\u4e0d\u662f\u5f3a\u76d7\uff0c\u6211\u2014\u2014\u6211\u548c\u4ed6\u8fd8\u6709\u8bdd\u8bf4\uff0c\u5927\u5e08\u8fd8\u662f \u51fa\u53bb\u5427\uff0c\u4e0d\u8981\u518d\u8ba9\u522b\u4eba\u8fdb\u6765\u4e86\u3002\u201d \u3000\u3000\u591a\u4e8b\u5934\u9640\u5fc3\u4e2d\u66f4\u5947\u602a\uff0c\u60f3\u4e86\u534a\u5929\uff0c\u72e0\u72e0\u4e00\u8dfa\u811a\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u8fd9\u4e9b\u5e74\u8f7b\u4eba\uff0c\u771f\u662f\u5947\u602a\u3002\u201d \u3000\u3000\u4e00\u6447\u65b9\u4fbf\u94f2\uff0c\u5927\u6b65\u8d70\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u5353\u957f\u537f\u89c1\u4e86\u8fd9\u9ad8\u5927\u5a01\u731b\u7684\u548c\u5c1a\u5bf9\u8fd9\u5c11\u5973\u7684\u8bdd\u7adf\u662f\u8a00\u542c\u8ba1\u4ece\uff0c\u4e0d\u7981\u6697\u4e2d\u4e00\u7b11\uff0c\u8f7b\u8eab\u843d\u4e86\u4e0b \u6765\uff0c\u5374\u542c\u6e29\u747e\u53c8\u518d\u95ee\u9053\uff1a\u201c\u4f60\u60f3\u6765\u5c31\u662f\u5353\u957f\u537f\u4e86\uff1f\u201d \u3000\u3000\u5353\u957f\u537f\u9894\u9996\u79f0\u662f\uff0c\u53ea\u89c1\u6e29\u747e\u957f\u53f9\u58f0\u4e2d\u7a81\u7136\u7f13\u7f13\u4ece\u8eab\u4e0a\u62ff\u51fa\u4e00\u7269\u6765\uff0c\u5353\u957f\u537f\u8f6c\u76ee\u671b\u53bb\uff0c\u53ea \u89c1\u7adf\u662f\u65b9\u624d\u653e\u5728\u684c\u4e0a\u7684\u81ea\u6728\u7075\u6e29\u747e\u5c06\u8fd9\u9762\u7075\u4f4d\u53c8\u653e\u5230\u684c\u4e0a\uff0c\u706f\u5149\u4e0b\uff0c\u5353\u957f\u537f\u53ea\u89c1\u4e0a\u9762\u5199\u7740\u7adf \u662f\uff1a\u201c\u5148\u7236\u6881\u516c\uff0c\u5148\u6bcd\u5b5f\u5927\u592b\u4eba\u4e4b\u4f4d\uff01\u201d \u3000\u3000\u4ed6\u5fc3\u4e2d\u4e0d\u7981\u4e00\u51db\uff0c\u5fd6\u9053\uff1a\u201c\u5979\u600e\u5730\u7adf\u5df2\u77e5\u9053\u4e86\u81ea\u5df1\u7684\u51fa\u8eab\u6765\u5386\uff0c\u53ef\u662f\u2014\u2014\u5979\u77e5\u4e0d\u77e5\u9053\u5979 \u7684\u6069\u5e08\u5c31\u662f\u6740\u6b7b\u5979\u7236\u6bcd\u7684\u4e0d\u5171\u6234\u5929\u7684\u4ec7\u4eba\u5462\uff1f\u201d \u3000\u3000\u53ea\u89c1\u5979\u76ee\u5149\u4e2d\u6ee1\u542b\u60b2\u4f24\uff0c\u776b\u6bdb\u4e0a\u6ee1\u6cbe\u6cea\u5149\uff0c\u773c\u5e18\u4e00\u5939\uff0c\u4e24\u7c92\u6676\u83b9\u7684\u6cea\u73e0\uff0c\u4fbf\u7f13\u7f13\u5730\u81ea\u9762 \u988a\u6d41\u4e0b\uff0c\u5979\u4e5f\u4e0d\u4f38\u624b\u64e6\u62ed\u4e00\u4e0b\uff0c\u53ea\u662f\u5e7d\u5e7d\u53f9\u9053\uff1a\u201c\u6211\u771f\u662f\u547d\u82e6\uff0c\u4e00\u76f4\u5230\u6628\u5929\u624d\u77e5\u9053\u6211\u7684\u4eb2\u751f \u7236\u6bcd\u662f\u8c01\uff0c\u53ef\u662f\u2014\u2014\u6211\u2026\u2026\u6211\u76f4\u5230\u73b0\u5728\uff0c\u8fd8\u4e0d\u77e5\u9053\u6211\u7239\u7239\u5988\u5988\u662f\u600e\u4e48\u6b7b\u7684\u2014\u2014\u201d\uff1d\u5979\u62bd\u6ce3\u7740 \u8bed\u58f0\u4e00\u987f\uff0c\u5353\u957f\u537f\u53ea\u89c1\u5979\u54ed\u5f97\u6709\u5982\u68a8\u82b1\u5e26\u96e8\uff0c\u5fc3\u4e2d\u4ea6\u5927\u611f\u51c4\u51c9\uff0c\u5374\u89c1\u5979\u8bed\u58f0\u4e00\u987f\uff0c\u7a81\u7136\u957f\u8eab \u7ad9\u4e86\u8d77\u6765\uff0c\u5411\u5353\u957f\u537f\u7f13\u7f13\u8d70\u4e86\u8fc7\u6765\uff0c\u5353\u957f\u537f\u89c1\u5979\u4e24\u773c\u76f4\u89c6\uff0c\u884c\u52a8\u50f5\u786c\uff0c\u50cf\u662f\u5165\u4e86\u9b54\u4f3c\u7684\u6837 \u5b50\uff0c\u5fc3\u91cc\u53c8\u662f\u601c\u60dc\uff0c\u53c8\u662f\u96be\u8fc7\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4f60\u8fd8\u662f\u2026\u2026\u8fd8\u662f\u2026\u2026\u201d \u3000\u3000\u4ed6\u672c\u60f3\u8bf4\u4e24\u53e5\u5b89\u6170\u7684\u8bdd\uff0c\u4f46\u8bf4\u4e86\u4e24\u58f0\u201c\u8fd8\u662f\u201d\u5374\u8fd8\u662f\u6ca1\u6709\u8bf4\u51fa\u6765\uff0c\u53ea\u89c1\u6e29\u747e\u7f13\u7f13\u8d70\u5230\u4ed6 \u9762\u524d\uff0c\u7a81\u7136\u53cc\u817f\u4e00\u66f2\uff0c\u8df5\u5730\u8dea\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u5353\u957f\u537f\u5927\u5403\u4e00\u60ca\uff0c\u8fde\u8fde\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u59d1\u5a18\uff0c\u4f60\u8fd9\u662f\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4fa7\u8eab\u4e00\u8ba9\uff0c\u8ba9\u5f00\u4e09\u6b65\uff0c\u60f3\u4f38\u624b\u6276\u8d77\u5979\u6765\uff0c\u53c8\u4e0d\u6562\u4f38\u624b\uff0c\u7ec8\u4e8e\u4e5f\u5657\u5730\u8dea\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u6df1\u591c\u4e4b\u4e2d\uff0c\u4f5b\u6bbf\u4e4b\u5185\uff0c\u7075\u53f0\u4e4b\u524d\uff0c\u8fd9\u5bf9\u5c11\u7537\u5c11\u5973\u7adf\u9762\u9762\u76f8\u5bf9\u5730\u8dea\u5728\u4e00\u8d77\uff0c\u591a\u4e8b\u5934\u9640\u65b9\u624d \u867d\u7136\u8d70\u4e86\u51fa\u53bb\uff0c\u4f46\u8d8a\u60f3\u8d8a\u89c9\u5f97\u4e0d\u662f\u6ecb\u5473\uff0c\u6b64\u523b\u53c8\u8dd1\u4e86\u8fdb\u6765\uff0c\u89c1\u5230\u8fd9\u79cd\u60c5\u51b5\uff0c\u4e0d\u7981\u5927\u611f\u5403\u60ca\uff0c \u5446\u5446\u5730\u6123\u4e86\u534a\u664c\uff0c\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u5e74\u8f7b\u4eba\u771f\u5947\u602a\u3002\u201d \u3000\u3000\u4f46\u5374\u7ec8\u53c8\u8e51\u624b\u8e51\u811a\u5730\u9000\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u5353\u957f\u537f\u8dea\u5728\u6e29\u747e\u5bf9\u9762\uff0c\u5fc3\u91cc\u867d\u6709\u8bb8\u591a\u8bdd\u8bf4\uff0c\u5374\u4e0d\u77e5\u8be5\u5148\u8bf4\u54ea\u53e5\u624d\u597d\u3002 \u3000\u3000\u53ea\u89c1\u6e29\u747e\u4e00\u53cc\u79cb\u6ce2\u4e4b\u4e2d\uff0c\u6cea\u73e0\u7c41\u7c41\u800c\u843d\uff0c\u826f\u4e45\u65b9\u624d\u5f3a\u5fcd\u54ed\u58f0\uff0c\u62bd\u6ce3\u7740\u9053\uff1a\u201c\u6211\u77e5\u9053\u2026\u2026 \u4f60\u77e5\u9053\uff0c\u6211\u77e5\u9053\u4f60\u77e5\u9053\u2026\u2026\u201d \u3000\u3000\u5353\u957f\u537f\u4e00\u6115\uff0c\u4ed6\u771f\u7684\u4e0d\u77e5\u9053\u8fd9\u516d\u5b57\u662f\u4ec0\u4e48\u610f\u601d\uff0c\u4e0d\u7981\u8131\u53e3\u9053\uff1a\u201c\u77e5\u9053\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6e29\u747e\u4f38\u51fa\u624b\u6765\uff0c\u7528\u624b\u8896\u64e6\u4e86\u64e6\u81ea\u5df1\u7684\u773c\u6cea\uff0c\u5979\u542c\u4e86\u5353\u957f\u537f\u7684\u95ee\u8bdd\uff0c\u518d\u60f3\u5230\u81ea\u5df1\u65b9\u624d\u8bf4\u7684 \u90a3\u516d\u4e2a\u5b57\uff0c\u5fc3\u91cc\u4e5f\u89c9\u5f97\u6709\u4e9b\u597d\u7b11\uff0c\u81ea\u5df1\u600e\u4f1a\u8bf4\u8fd9\u6837\u65e0\u5934\u65e0\u8111\u7684\u8bdd\u6765\uff0c\u4f46\u5979\u6b64\u523b\u6b63\u662f\u6ee1\u5fc3\u60b2 \u6614\u3001\u54c0\u75db\u6b32\u7edd\uff0c\u54ea\u91cc\u7b11\u5f97\u51fa\u6765\u3002 \u3000\u3000\u5979\u53c8\u81ea\u62bd\u6ce3\u534a\u664c\uff0c\u65b9\u81ea\u8bf4\u9053\uff1a\u201c\u6211\u77e5\u9053\u53ea\u6709\u4f60\u77e5\u9053\u6211\u7239\u7239\u5988\u5988\u662f\u600e\u4e48\u6b7b\u7684\uff0c\u4e5f\u53ea\u6709\u4f60\u77e5 \u9053\u6211\u7239\u7239\u5988\u5988\u7684\u4ec7\u4eba\u662f\u8c01\uff0c\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u5353\u957f\u537f\u5927\u5947\uff1a\u201c\u5979\u662f\u5982\u4f55\u77e5\u9053\u6211\u77e5\u9053\uff1f\u201d \u3000\u3000\u4e00\u65f6\u4e4b\u95f4\uff0c\u5fc3\u4e2d\u731c\u7591\u5927\u751f\uff0c\u7adf\u5fd8\u4e86\u56de\u7b54\u5979\u7684\u8bdd\u3002 \u3000\u3000\u201c\u96be\u9053\u5979\u4e5f\u9047\u7740\u4e86\u90a3\u4f4d\u9ad8\u51a0\u7fbd\u58eb\uff1f\u4f46\u4ed6\u65e2\u7136\u8bf4\u51fa\u4e86\u5979\u7236\u6bcd\u662f\u8c01\uff0c\u5374\u53c8\u600e\u7684\u4e0d\u5c06\u5979\u7684\u4ec7\u4eba \u662f\u8c01\u544a\u8bc9\u5979\u5462\uff1f\u201d \u3000\u3000\u6e29\u747e\u6cea\u773c\u6a21\u7cca\uff0c\u51dd\u89c6\u7740\u4ed6\uff0c\u89c1\u5230\u4ed6\u7684\u795e\u60c5\uff0c\u53c8\u81ea\u62bd\u6ce3\u7740\u8ffd\uff1a\u201c\u6211\u77e5\u9053\u6211\u4ee5\u524d\u4e0d\u597d\uff0c\u5bf9\u4e0d \u8d77\u4f60\uff0c\u4f46\u662f\u6211\u2026\u2026\u6211\u5e0c\u671b\u4f60\u4e0d\u8981\u653e\u5728\u5fc3\u4e0a\uff0c\u4f60\u8981\u662f\u544a\u8bc9\u4e86\u6211\uff0c\u6211\u2026\u2026\u6211\u4f1a\u611f\u6fc0\u4f60\u4e00\u8f88\u5b50\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u957f\u53f9\u4e00\u58f0\uff0c\u8fd9\u5201\u86ee\u9a84\u505a\u7684\u5c11\u5973\uff0c\u6b64\u523b\u7adf\u5bf9\u4ed6\u8bf4\u51fa\u8fd9\u6837\u54c0\u6073\u7684\u8bdd\u6765\uff0c\u4ed6\u975e\u4f46\u4e0d\u89c9\u5f97 \u610f\uff0c\u53cd\u800c\u6709\u4e9b\u96be\u53d7\uff0c\u957f\u53f9\u7740\u9053\uff1a\u201c\u59d1\u5a18\u53cc\u4eb2\u7684\u60e8\u6b7b\u4e4b\u4e8b\uff0c\u5728\u4e0b\u7684\u786e\u662f\u77e5\u9053\uff0c\u4f46\u6b64\u4e8b\u8bf4\u6765\u8bdd \u957f\uff0c\u5509\u2014\u2014\u4e0d\u77e5\u9053\u6b64\u4e8b\u662f\u8c01\u544a\u8bc9\u59d1\u5a18\u7684\uff1f\u662f\u5426\u4e00\u4e2a\u53eb\u9ad8\u51a0\u7fbd\u58eb\u7684\u957f\u8005\uff1f\u4ed6\u9664\u4e86\u544a\u8bc9\u59d1\u5a18\u8fd9\u4e9b \u4e4b\u5916\uff0c\u8fd8\u8bf4\u4e86\u4e9b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6e29\u747e\u53cc\u76ee\u4e00\u5f20\uff0c\u8bf4\u9053\uff1a\u201c\u9ad8\u51a0\u7fbd\u58eb\u662f\u8c01\uff1f\u6211\u8fde\u542c\u90fd\u6ca1\u6709\u542c\u8fc7\u8fd9\u4eba\u7684\u540d\u5b57\uff1f\u201d \u3000\u3000\u5353\u957f\u537f\u4e00\u6014\uff0c\u5374\u542c\u5979\u8bed\u58f0\u5fae\u987f\uff0c\u53c8\u9053\uff1a\u201c\u8fd9\u4e9b\u4e8b\uff0c\u5509\u2014\u2014\u6211\u8bf4\u7ed9\u4f60\u542c\u6ca1\u6709\u5173\u7cfb\uff0c\u4f60\u53ef\u5343 \u4e07\u4e0d\u8981\u544a\u8bc9\u522b\u4eba\uff0c\u6628\u5929\u665a\u4e0a\uff0c\u6211\u5df2\u7ecf\u7761\u4e86\uff0c\u7a97\u5916\u7a81\u7136\u6709\u6572\u7a97\u5b50\u7684\u58f0\u97f3\uff0c\u6211\u5927\u5403\u4e00\u60ca\uff0c\u8981\u77e5\u9053 \u6211\u7761\u7684\u5730\u65b9\u662f\u5728\u540e\u9762\uff0c\u524d\u9762\u7684\u4e00\u6392\u5ba2\u623f\u91cc\u4e0d\u77e5\u4f4f\u4e86\u591a\u5c11\u6b66\u6797\u9ad8\u624b\uff0c\u8fd9\u4eba\u7adf\u80fd\u8dd1\u5230\u6211\u7a97\u5916\u6765\u6572 \u7a97\u5b50\uff0c\u6211\u5fc3\u91cc\u53c8\u5403\u60ca\u53c8\u5947\u602a\uff0c\u4e0d\u77e5\u9053\u662f\u8c01\u6709\u8fd9\u4e48\u5927\u7684\u80c6\u5b50\u3002\u201d \u3000\u3000\u542c\u5979\u8bf4\u5230\u8fd9\u91cc\uff0c\u5353\u957f\u537f\u4e5f\u5728\u6697\u95ee\u81ea\u5df1\uff1a\u201c\u8fd9\u4eba\u4e0d\u662f\u9ad8\u51a0\u7fbd\u58eb\uff0c\u5374\u53c8\u662f\u8c01\u5462\uff1f\u4ed6\u600e\u4e48\u4f1a\u77e5 \u9053\u8fd9\u4e2a\u79d8\u5bc6\uff1f\u201d \u3000\u3000\u53ea\u542c\u6e29\u747e\u63a5\u7740\u9053\uff1a\u201c\u90a3\u65f6\u6211\u5fc3\u60f3\u8fd9\u4eba\u4e00\u5b9a\u4e0d\u662f\u5916\u6765\u7684\u4eba\uff0c\u56e0\u4e3a\u6c5f\u6e56\u4e2d\u80fd\u5728\u8fd9\u4e48\u591a\u6b66\u6797\u9ad8 \u624b\u4f4f\u7684\u5730\u65b9\u8dd1\u5230\u540e\u56ed\u6765\u7684\u4eba\uff0c\u7b80\u76f4\u592a\u5c11\u4e86\uff0c\u6211\u4ee5\u4e3a\u8fd9\u53c8\u662f\u90a3\u4e9b\u8ba8\u538c\u7684\u5bb6\u4f19\uff0c\u8dd1\u6765\u2026\u2026\u8dd1\u6765\u8ba8 \u538c\u4e86\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u5fc3\u4e2d\u4e00\u52a8\uff0c\u60f3\u5230\u8f66\u4e2d\u90a3\u4e9b\u5c11\u5973\u8bf4\u7684\u8bdd\uff0c\u53c8\u60f3\u5230\u90a3\u4e2a\u53eb\u505a\u4ec0\u4e48\u82b1\u90ce\u6bd5\u4e94\u7684\u4eba\uff0c\u5fc3\u91cc \u6709\u4e9b\u597d\u7b11\uff0c\u4f46\u4ed6\u6b64\u523b\u5fc3\u4e2d\u4ea6\u662f\u6c89\u91cd\u4e07\u5206\uff0c\u8fd9\u70b9\u597d\u7b11\u4e4b\u610f\uff0c\u5728\u5fc3\u4e2d\u4e00\u95ea\uff0c\u4fbf\u88ab\u90a3\u6c89\u91cd\u7684\u6101\u7eea\u538b \u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u8bf4\u5230\u8fd9\u91cc\uff0c\u6e29\u747e\u8bed\u58f0\u4ea6\u81ea\u4e00\u987f\uff0c\u50cf\u662f\u6709\u4e9b\u7f9e\u6da9\u4e4b\u610f\uff0c\u4f46\u77ac\u5373\u63a5\u9053\uff1a\u201c\u6211\u5fc3\u91cc\u53c8\u6068\u53c8\u6c14\uff0c \u6084\u6084\u62ab\u4e86\u4ef6\u5916\u8863\uff0c\u8df3\u4e0b\u4e86\u5e8a\uff0c\u5374\u4ece\u53e6\u4e00\u4e2a\u7a97\u53e3\u63a0\u4e86\u51fa\u53bb\uff0c\u51c6\u5907\u7ed9\u8fd9\u53ae\u4e00\u4e2a\u6559\u8bad\uff0c\u54ea\u77e5\u6211\u63a0\u5230 \u7a97\u5916\uff0c\u56db\u987e\u4e00\u773c\uff0c\u7a97\u5916\u7adf\u65e0\u4eba\u5f71\uff0c\u6211\u65b9\u81ea\u6709\u4e9b\u5947\u602a\uff0c\u54ea\u77e5\u80cc\u540e\u5374\u6709\u4eba\u8f7b\u8f7b\u4e00\u7b11\uff0c\u6c89\u58f0\u8bf4\u9053\uff1a \u2018\u6211\u5728\u8fd9\u91cc\u3002\u201c\u201d\u5979\u900f\u4e86\u53e3\u6c14\uff0c\u53c8\u9053\uff1a\u201c\u90a3\u65f6\u6211\u771f\u662f\u5413\u4e86\u4e00\u8df3\uff0c\u5fc3\u60f3\u8fd9\u4eba\u7684\u8f7b\u52a8\u7adf\u7136\u8fd9\u4e48 \u9ad8\uff0c\u8d76\u7d27\u56de\u8fc7\u5934\u53bb\u4e00\u770b\uff0c\u624d\u77e5\u9053\u8fd9\u4eba\u7adf\u5c31\u662f\u90a3\u6b66\u6797\u4e2d\u8f7b\u529f\u6700\u9ad8\u7684\u4eba\uff0c\u6240\u4ee5\u624d\u80fd\u5728\u8fd9\u4e48\u591a\u9ad8\u624b \u4f4f\u7684\u5730\u65b9\uff0c\u51fa\u5165\u81ea\u82e5\uff0c\u5509\u2014\u2014\u83ab\u8bf4\u662f\u6211\uff0c\u53ea\u6015\u5e08\u7236\u4e5f\u4e0d\u89c1\u5f97\u80fd\u6478\u5f97\u7740\u4ed6\u7684\u5f71\u5b50\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u53cc\u7709\u4e00\u76b1\uff0c\u4f4e\u8bed\u9053\uff1a\u201c\u6b66\u6797\u4e2d\u8f7b\u529f\u6700\u9ad8\u7684\u4eba\u2026\u2026\u662f\u8c01\uff1f\u201d \u3000\u3000\u4ed6\u5fc3\u60f3\u6b66\u529f\u4e2d\u8f7b\u529f\u6700\u9ad8\u7684\u662f\u6211\u5e08\u7236\uff0c\u83ab\u975e\u662f\u5e08\u7236\uff0c\u4f46\u90a3\u6e29\u747e\u63a5\u7740\u8bf4\u7684\u5374\u662f\uff1a\u201c\u8fd9\u4eba\u4f60\u5927 \u6982\u4e5f\u662f\u8ba4\u5f97\u7684\uff0c\u4ed6\u5c31\u662f\u90a3\u2018\u4e07\u5999\u771f\u541b\u2019\u5c39\u51e1\uff0c\u4ed6\u2014\u2014\u201d\u5353\u957f\u537f\u6d51\u8eab\u4e00\u9707\uff0c\u8131\u53e3\u547c\u9053\uff1a\u201c\u4e07\u5999 \u771f\u541b\u5c39\u51e1\uff01\u4ed6\u662f\u4e0d\u662f\u4e00\u4e2a\u8eab\u6750\u9ad8\u9ad8\uff0c\u4e94\u67f3\u957f\u987b\uff0c\u7a7f\u7740\u9053\u888d\uff0c\u6234\u7740\u9053\u51a0\u7684\u4eba\uff1f\u201d \u3000\u3000\u6e29\u747e\u70b9\u4e86\u70b9\u5934\uff0c\u5947\u602a\u5730\u95ee\u9053\uff1a\u201c\u4f60\u4e0d\u8ba4\u5f97\u4ed6\u5417\uff1f\u4ed6\u600e\u7684\u77e5\u9053\u4f60\u7684\uff1f\u201d \u3000\u3000\u76f4\u5230\u6b64\u523b\uff0c\u5353\u957f\u537f\u5fc3\u4e2d\u65b9\u81ea\u604d\u7136\u5927\u609f\uff0c\u90a3\u9ad8\u51a0\u7fbd\u58eb\u5b9e\u5728\u5c31\u662f\u4e07\u5999\u771f\u541b\uff0c\u4e5f\u5c31\u662f\u6740\u5bb3\u4ed6\u7236 \u6bcd\u7684\u4ec7\u4eba\u4e4b\u4e00\u3002 \u3000\u3000\u4e00\u65f6\u4e4b\u95f4\uff0c\u4ed6\u5fc3\u4e2d\u767e\u611f\u4ea4\u96c6\uff0c\u4f46\u60f3\u6765\u60f3\u53bb\uff0c\u5374\u5f04\u4e0d\u6e05\u4e07\u5999\u771f\u541b\u4e3a\u4ec0\u4e48\u8981\u5728\u81ea\u5df1\u9762\u524d\u5f04\u8fd9 \u624b\u7384\u865a\u3002\u8981\u77e5\u9053\u4ed6\u867d\u7136\u806a\u660e\u7edd\u9876\uff0c\u4f46\u5230\u5e95\u5e74\u7eaa\u592a\u8f7b\uff0c\u5bf9\u4e16\u95f4\u4e00\u4e9b\u9b3c\u872e\u4eba\u60c5\uff0c\u81ea\u7136\u8fd8\u4e0d\u6e05\u695a\u3002 \u3000\u3000\u90a3\u6e29\u747e\u5374\u4e0d\u77e5\u9053\u6b64\u4e2d\u7684\u66f2\u6298\uff0c\u89c1\u5230\u5353\u957f\u537f\u4e0d\u518d\u8bf4\u8bdd\uff0c\u4fbf\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u8fd9\u4e07\u5999\u771f\u541b\u5c39\u51e1\u548c \u5e08\u7236\u672c\u662f\u7d20\u8bc6\uff0c\u4ee5\u524d\u4e5f\u5e38\u6765\u5f80\uff0c\u76f4\u5230\u8fd1\u6765\u624d\u6ca1\u6709\u89c1\u8fc7\u4ed6\u7684\u4eba\uff0c\u6211\u4ece\u5e08\u7236\u53e3\u91cc\u8fd8\u65f6\u5e38\u542c\u5230\u5e08\u7236 \u8981\u627e\u4ed6\uff0c\u8fd9\u65f6\u6211\u89c1\u4ed6\u7a81\u7136\u6765\u4e86\uff0c\u4e0d\u53bb\u627e\u5e08\u7236\uff0c\u5374\u6765\u627e\u6211\uff0c\u5fc3\u91cc\u5927\u4e3a\u5947\u602a\uff0c\u4ed6\u770b\u4e86\u770b\u4e86\u6211\uff0c\u7b11 \u4e86\u7b11\uff0c\u5288\u5934\u7b2c\u4e00\u53e5\u8bdd\u7adf\u7136\u5c31\u662f\u95ee\u6211\uff1a\u2018\u4f60\u77e5\u4e0d\u77e5\u9053\u4f60\u7684\u7239\u7239\u5988\u5988\u662f\u8c01\uff1f\u8981\u4e0d\u8981\u6211\u544a\u8bc9\u4f60\uff1f\u2019 \u201c\u5979\u5e7d\u5e7d\u5730\u957f\u53f9\u4e00\u58f0\uff0c\u53c8\u9053\uff1a\u201c\u81ea\u4ece\u6211\u61c2\u4e8b\u4ee5\u6765\uff0c\u8fd9\u4e2a\u95ee\u9898\u6211\u5df2\u4e0d\u77e5\u5bf9\u81ea\u5df1\u95ee\u8fc7\u591a\u5c11\u904d\u4e86\uff0c \u6211\u5750\u7740\u4e5f\u597d\uff0c\u7ad9\u7740\u4e5f\u597d\uff0c\u5403\u996d\u4e5f\u597d\uff0c\u65e0\u65f6\u65e0\u523b\u4e0d\u5728\u60f3\u77e5\u9053\u8fd9\u4e2a\u95ee\u9898\u7684\u89e3\u7b54\uff0c\u6211\u5bf9\u8fd9\u4e07\u5999\u771f\u541b \u5fc3\u91cc\u867d\u7136\u6709\u4e9b\u6000\u7591\uff0c\u4f46\u4ed6\u8fd9\u7b2c\u4e00\u53e5\u8bdd\uff0c\u5374\u95ee\u8fdb\u4e86\u6211\u7684\u5fc3\u91cc\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u5fc3\u4e2d\u601d\u6f6e\u53cd\u590d\uff0c\u5446\u5446\u5730\u542c\u7740\u5979\u7684\u8bdd\uff0c\u8fd9\u4e24\u4eba\u4e00\u4e2a\u8bf4\u5f97\u51fa\u795e\uff0c\u4e00\u4e2a\u542c\u5f97\u51fa\u795e\uff0c\u7adf\u5fd8 \u4e86\u4e24\u4eba\u4ff1\u90fd\u8fd8\u8dea\u5728\u5730\u4e0a\uff0c\u8c01\u4e5f\u6ca1\u6709\u7ad9\u8d77\u6765\u7684\u610f\u601d\u3002 \u3000\u3000\u53ea\u89c1\u6e29\u747e\u53c8\u9053\uff1a\u201c\u5f53\u65f6\u6211\u5fc3\u91cc\u4e00\u52a8\uff0c\u5c31\u6c42\u4ed6\u544a\u8bc9\u6211\uff0c\u54ea\u77e5\u4ed6\u53c8\u5bf9\u6211\u7b11\u4e86\u7b11\uff0c\u8981\u6211\u5148\u628a\u5e08 \u7236\u6349\u56de\u5c71\u91cc\u6765\u7684\u4e00\u4e2a\u5c11\u5e74\u653e\u51fa\u6765\uff0c\u4ed6\u624d\u544a\u8bc9\u6211\u3002\u201d \u3000\u3000\u201c\u5509\uff0c\u6211\u867d\u7136\u77e5\u9053\u8fd9\u5bb6\u4f19\u4e00\u5b9a\u505a\u4e86\u5bf9\u4e0d\u8d77\u5e08\u7236\u7684\u4e8b\uff0c\u662f\u4ee5\u5e08\u7236\u624d\u4f1a\u628a\u4ed6\u7684\u5f92\u5f1f\u7981\u95ed\u8d77 \u6765\uff0c\u6211\u4e5f\u77e5\u9053\u4ed6\u867d\u7136\u6b66\u529f\u5f88\u9ad8\uff0c\u5374\u4e0d\u6562\u89c1\u5e08\u7236\u7684\u9762\uff0c\u4e5f\u4e0d\u6562\u5728\u8fd9\u79cd\u5730\u65b9\u5230\u5904\u641c\u7d22\uff0c\u662f\u4ee5\u624d\u6765 \u8981\u631f\u6211\uff0c\u4f46\u8fd9\u4ef6\u4e8b\u5374\u7684\u786e\u6253\u52a8\u4e86\u6211\u7684\u5fc3\uff0c\u83ab\u8bf4\u4ed6\u8981\u6211\u505a\u8fd9\u4ef6\u4e8b\uff0c\u4ed6\u5c31\u662f\u8981\u53eb\u6211\u505a\u6bd4\u8fd9\u518d\u56f0\u96be \u5341\u500d\u7684\u4e8b\uff0c\u6211\u4e5f\u4f1a\u7b54\u5e94\u7684\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u542c\u5230\u8fd9\u91cc\uff0c\u4e0d\u7981\u76b1\u7709\u53f9\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u5c31\u628a\u90a3\u59d3\u5c91\u7684\u653e\u4e86\uff1f\u201d \u3000\u3000\u6e29\u747e\u9894\u9996\u9053\uff1a\u201c\u6211\u5c31\u628a\u59d3\u5c91\u7684\u653e\u4e86\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u9053\uff1a\u201c\u7136\u540e\u5462\uff1f\u201d \u3000\u3000\u6e29\u747e\u7728\u4e86\u80c6\u773c\u775b\uff0c\u50cf\u662f\u5f3a\u5fcd\u7740\u773c\u4e2d\u7684\u6cea\u73e0\uff0c\u53c8\u81ea\u53f9\u9053\uff1a\u201c\u7136\u540e\u4ed6\u5c31\u544a\u8bc9\u4e86\u6211\u7239\u7239\u5988\u5988\u7684 \u540d\u5b57\uff0c\u8fd8\u8bf4\u6211\u7239\u7239\u5988\u5988\u662f\u88ab\u4eba\u5bb3\u6b7b\u7684\uff0c\u6211\u542c\u4e86\u8fd9\u8bdd\uff0c\u5fc3\u91cc\u771f\u6709\u8bf4\u4e0d\u51fa\u7684\u96be\u53d7\uff0c\u6068\u4e0d\u5f97\u9a6c\u4e0a\u5c31 \u627e\u7740\u5bb3\u6b7b\u6211\u7239\u7239\u5988\u5988\u7684\u4ec7\u4eba\uff0c\u53ea\u662f\u4ed6\u90a3\u5f92\u5f1f\u5728\u65c1\u8fb9\u4e0d\u6000\u597d\u610f\u5730\u671b\u7740\u6211\uff0c\u6211\u5fcd\u4f4f\u6c14\uff0c\u95ee\u4ed6\u6211\u4ec7 \u4eba\u662f\u8c01\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u5251\u7709\u4e00\u76b1\uff0c\u95ee\u9053\uff1a\u201c\u4ed6\u600e\u5730\u4e0d\u544a\u8bc9\u4f60\uff1f\u201d \u3000\u3000\u6e29\u747e\u5e7d\u5e7d\u4e00\u53f9\uff0c\u8bf4\u9053\uff1a\u201c\u4ed6\u542c\u4e86\u6211\u7684\u8bdd\uff0c\u8138\u4e0a\u5c31\u9732\u51fa\u5f88\u4e3a\u96be\u7684\u6837\u5b50\u6765\uff0c\u8fd9\u65f6\u5019\u65c1\u8fb9\u7a81\u7136 \u6709\u4eba\u58f0\u8d70\u52a8\uff0c\u4ed6\u4f3c\u4e4e\u5927\u5403\u4e00\u60ca\uff0c\u8fde\u5fd9\u62c9\u8d77\u4e86\u4ed6\u5f92\u5f1f\u7684\u624b\uff0c\u4e00\u9762\u5306\u5306\u9053\uff1a\u2018\u4f60\u53bb\u95ee\u5353\u957f\u537f\u597d \u4e86\u3002\u201c\u4e00\u9762\u4fbf\u5982\u98ce\u63a0\u8d70\u4e86\uff0c\u5509\u2014\u2014\u4ed6\u8f7b\u529f\u5b9e\u5728\u9ad8\u5999\uff0c\u624b\u91cc\u62c9\u7740\u4e00\u4e2a\u4eba\u3002\u6211\u4ecd\u7136\u8ffd\u4e0d\u5230\uff0c\u6211\u4e5f \u6015\u5e08\u7236\u53d1\u73b0\u6211\u5077\u5077\u653e\u8d70\u4e86\u4eba\uff0c\u53ea\u5f97\u8dd1\u56de\u623f\u91cc\uff0c\u4f46\u662f\u5353\u957f\u537f\u662f\u8c01\u5462\uff1f\u6211\u53c8\u4e0d\u77e5\u9053\uff0c\u6211\u5fc3\u91cc\u53c8\u6028 \u6068\uff0c\u53c8\u96be\u53d7\uff0c\u542c\u5916\u9762\u98ce\u5439\u6811\u6728\u7684\u58f0\u97f3\uff0c\u50cf\u662f\u6d77\u4e2d\u7684\u6ce2\u6d6a\u4e00\u6837\uff0c\u8d77\u4f0f\u4e0d\u5b9a\uff0c\u6211\u5fc3\u91cc\u4e5f\u8d77\u4f0f\u4e0d \u5b9a\uff0c\u76f4\u5230\u5929\u4eae\uff0c\u54ea\u91cc\u80fd\u591f\u4eba\u7761\u3002\u201d\u8bf4\u7740\u8bf4\u7740\uff0c\u5979\u773c\u6cea\u7ec8\u4e8e\u4e0d\u80fd\u81ea\u7981\u5730\u6d41\u4e86\u4e0b\u6765\uff0c\u5979\u53c8\u4f38\u624b\u4e00 \u62ed\uff0c\u63a5\u7740\u9053\uff1a\u201c\u4eca\u5929\u6211\u89c1\u7740\u5e08\u7236\uff0c\u5e08\u7236\u6b63\u5728\u4e3a\u7740\u7a81\u7136\u4e22\u4e86\u4e2a\u4eba\u800c\u5927\u53d1\u96f7\u9706\uff0c\u6211\u4e5f\u4e0d\u6562\u5c06\u8fd9\u4e8b \u8bf4\u51fa\u6765\uff0c\u53ea\u6709\u81ea\u5df1\u5077\u5077\u4e3a\u7239\u7239\u5988\u5988\u505a\u4e86\u4e2a\u7075\u4f4d\uff0c\u4e00\u4e2a\u4eba\u8dd1\u5230\u8fbe\u91cc\u6765\uff0c\u4e3a\u4ed6\u4eec\u5ff5\u7ecf\uff0c\u5509\u2014\u2014\u6211 \u5634\u91cc\u867d\u5728\u5ff5\u7ecf\uff0c\u5fc3\u91cc\u5374\u5728\u60f3\u7740\u5bb3\u6b7b\u6211\u7239\u7239\u5988\u5988\u7684\u4ec7\u4eba\u662f\u8c01\u5462\uff1f\u5353\u957f\u537f\u662f\u8c01\u5462\uff1f\u53eb\u6211\u600e\u4e48\u627e \u4ed6\uff1f\u201d \u3000\u3000\u5979\u76ee\u5149\u4e00\u779f\u5353\u957f\u537f\uff0c\u53c8\u9053\uff1a\u201c\u6211\u770b\u89c1\u4f60\u6765\u4e86\uff0c\u5fc3\u91cc\u96be\u53d7\u5f97\u5f88\uff0c\u4e5f\u4e0d\u60f3\u548c\u4f60\u4e3a\u654c\uff0c\u54ea \u77e5\u2026\u2026\u54ea\u77e5\u4f60\u5c31\u662f\u5353\u957f\u537f\u3002\u201d \u3000\u3000\u5979\u987f\u4f4f\u8bdd\u58f0\uff0c\u7f13\u7f13\u7684\u5782\u4e0b\u4e86\u5934\uff0c\u5353\u957f\u537f\u671b\u7740\u5979\u7684\u5934\u53d1\uff0c\u5fc3\u4e2d\u5374\u5728\u6697\u4e2d\u601d\u5fd6\uff1a\u201c\u90a3\u4e07\u5999\u771f \u541b\u5982\u6b64\u505a\u6cd5\uff0c\u60f3\u5fc5\u662f\u4e3a\u4e86\u60f3\u501f\u6211\u4e24\u4eba\u4e4b\u624b\uff0c\u9664\u53bb\u90a3\u4e11\u4eba\u6e29\u5982\u738b\uff0c\u56e0\u4e3a\u90a3\u6e29\u5982\u7389\u60f3\u5fc5\u5df2\u6068\u4ed6\u5165 \u9aa8\uff0c\u4e00\u5b9a\u8981\u6740\u4e86\u4ed6\u624d\u7518\u5fc3\uff0c\u4f46\u662f\uff0c\u4ed6\u53c8\u6015\u6211\u4e0d\u662f\u6e29\u5982\u7389\u7684\u654c\u624b\uff0c\u6e29\u5982\u7389\u5c06\u6211\u6740\u4e86\uff0c\u4ed6\u56fa\u4e5f\u79f0 \u5fc3\u5982\u613f\uff0c\u4f46\u6e29\u5982\u7389\u77e5\u9053\u4e86\u8fd9\u4e9b\u8bdd\u662f\u8c01\u8bf4\u7684\uff0c\u4ed6\u4fbf\u66f4\u662f\u4e0d\u5f97\u4e86\u4e86\uff0c\u662f\u4ee5\u4ed6\u4e0d\u4eb2\u53e3\u544a\u8bc9\u6e29\u747e\uff0c\u5374 \u53eb\u6e29\u747e\u6765\u95ee\u6211\uff0c\u5509\u2014\u2014\u6b64\u4eba\u7528\u5fc3\u4e4b\u6b79\u6bd2\uff0c\u5b9e\u5728\u6709\u5982\u86c7\u874e\uff01\u201d \u3000\u3000\u65b9\u624d\u6e29\u747e\u8bf4\u8bdd\u4e4b\u9645\uff0c\u4ed6\u4fbf\u4e00\u9762\u5728\u5fc3\u4e2d\u5bfb\u601d\uff0c\u8fd9\u4e9b\u63a8\u6d4b\uff0c\u5374\u662f\u4ed6\u7ecf\u8fc7\u591a\u6b21\u601d\u8003\u7136\u540e\u5f52\u7eb3\u6240 \u5f97\uff0c\u4e5f\u6b63\u662f\u90a3\u4e07\u5999\u771f\u541b\u7684\u7528\u5fc3\u6240\u5728\u3002 \u3000\u3000\u8981\u77e5\u9053\u4e07\u5999\u771f\u541b\u867d\u7136\u77e5\u9053\u5353\u957f\u537f\u5bf9\u81ea\u5df1\u4ea6\u6709\u4e0d\u5171\u6234\u5929\u7684\u5fc5\u62a5\u4e4b\u4ec7\uff0c\u4f46\u4ed6\u81ea\u6043\u6b66\u529f\u9ad8\u5f3a\uff0c \u77e5\u9053\u5353\u957f\u537f\u6b64\u523b\u4e0d\u662f\u81ea\u5df1\u7684\u654c\u624b\uff0c\u662f\u4ee5\u4ed6\u4fbf\u4e0d\u5c06\u5353\u957f\u537f\u653e\u5728\u5fc3\u4e0a\uff0c\u4f7f\u4ed6\u771f\u6b63\u5fc3\u5b58\u6050\u60e7\u7684\uff0c\u81ea \u7136\u4fbf\u662f\u90a3\u4e11\u4eba\u6e29\u5982\u7389\u3002 \u3000\u3000\u4ed6\u5982\u6b64\u505a\u6cd5\uff0c\u4e0d\u51fa\u5353\u957f\u537f\u6240\u6599\uff0c\u7684\u786e\u662f\u60f3\u5047\u5353\u957f\u537f\u4e0e\u6e29\u747e\u4e24\u4eba\u4e4b\u624b\uff0c\u9664\u53bb\u81ea\u5df1\u7684\u5fc3\u8179\u5927 \u5fcc\uff0c\u7eb5\u7136\u4ed6\u4e24\u4eba\u4e0d\u662f\u6e29\u5982\u7389\u7684\u654c\u624b\uff0c\u6781\u53ef\u80fd\u88ab\u6e29\u5982\u7389\u6740\u6b7b\uff0c\u4f46\u6e29\u5982\u7389\u6740\u4e86\u81ea\u5df1\u7684\u7231\u5f92\uff0c\u5fc3\u91cc \u4e5f\u4e0d\u4f1a\u597d\u53d7\uff0c\u4f55\u51b5\u5353\u957f\u537f\u4e5f\u662f\u4ed6\u6781\u601d\u9664\u53bb\u4e4b\u4eba\u3002 \u3000\u3000\u4e07\u5999\u771f\u541b\u5c39\u51e1\u4e00\u751f\u559c\u7528\u501f\u5200\u6740\u4eba\u4e4b\u8ba1\uff0c\u8fd9\u6b21\u4ed6\u505a\u5f97\u66f4\u662f\u5f97\u610f\uff0c\u4e0d\u7ba1\u6b64\u4e8b\u5982\u4f55\u53d1\u5c55\uff0c\u5bf9\u4ed6 \u5374\u53ea\u6709\u767e\u5229\u800c\u65e0\u4e00\u5bb3\u7684\u3002 \u3000\u3000\u4e00\u65f6\u4e4b\u95f4\uff0c\u5353\u957f\u537f\u7684\u5fc3\u4e2d\u6124\u6012\u586b\u81ba\uff0c\u5bf9\u8fd9\u4e07\u5999\u771f\u541b\u7684\u6028\u6068\u4e4b\u5fc3\uff0c\u7adf\u7136\u6bd4\u5bf9\u4e11\u4eba\u6e29\u5982\u7389\u8fd8 \u8981\u8d85\u8fc7\u4e09\u5206\u591a\u3002 \u3000\u3000\u53ea\u542c\u90a3\u6e29\u747e\u4e00\u53f9\u53c8\u9053\uff1a\u201c\u6211\u4ec0\u4e48\u90fd\u544a\u8bc9\u4e86\u4f60\uff0c\u4f60\u4e5f\u8be5\u544a\u8bc9\u6211\u4e86\u5427\uff1f\u201d \u3000\u3000\u5353\u957f\u537f\u671b\u7740\u5979\u90a3\u4e00\u53cc\u6ee1\u542b\u6073\u6c42\u671f\u5f85\u4e4b\u8272\u7684\u773c\u775b\uff0c\u65b9\u5f85\u5f20\u53e3\u3002 \u3000\u3000\u54ea\u77e5\u2014\u2014 \u3000\u3000\u524d\u6bbf\u4e2d\u7a81\u53c8\u4f20\u6765\u4e00\u58f0\u66b4\u559d\uff0c\u53ea\u542c\u90a3\u591a\u4e8b\u5934\u9640\u5927\u58f0\u543c\u9053\uff1a\u65e0\u8bba\u4f60\u662f\u8c01\uff0c\u82e5\u60f3\u5230\u91cc\u9762\u53bb\uff0c\u5148 \u5403\u6d12\u5bb6\u4e00\u6756\u3002\u201c\u5353\u957f\u537f\u3001\u6e29\u747e\u7a81\u5730\u4e00\u60ca\uff0c\u8fd9\u624d\u60f3\u8d77\u81ea\u5df1\u8fd8\u662f\u8dea\u5728\u5730\u4e0a\uff0c\u4e0d\u7ea6\u800c\u540c\u5730\u957f\u8eab\u800c \u8d77\uff0c\u4e24\u4eba\u9762\u9762\u76f8\u5bf9\uff0c\u65b9\u81ea\u5bf9\u4e86\u4e00\u773c\uff0c\u53ea\u542c\u9662\u4e2d\u5df2\u8dc3\u5165\u513f\u4e2a\u4eba\u6765\uff0c\u547c\u53f1\u76f8\u51fb\u4e4b\u58f0\uff0c\u4e5f\u4f20\u5165\u9662 \u4e2d\u3002\u5353\u957f\u537f\u6765\u4e0d\u53ca\u7b54\u6848\uff0c\u7acb\u638c\u4e00\u626c\uff0c\u201d\u547c\u201c\u5730\u717d\u706d\u4e86\u684c\u4e0a\u706f\u706b\uff0c\u5374\u5c06\u706f\u65c1\u7684\u7075\u4f4d\uff0c\u4e5f\u9707\u843d\u5230 \u5730\u4e0a\uff0c\u6e29\u747e\u6b64\u523b\u867d\u5fc3\u795e\u5927\u4e71\uff0c\u5374\u4ecd\u4f4e\u58f0\u95ee\u9053\uff1a\u201c\u662f\u8c01\uff1f\u662f\u8c01\uff1f\u201d \u3000\u3000\u6b64\u523b\u9662\u4e2d\u640f\u6597\u4e4b\u58f0\u66f4\u6025\uff0c\u591a\u4e8b\u5934\u9640\u8fde\u8fde\u5389\u543c\uff0c\u597d\u50cf\u9047\u7740\u4e86\u5f3a\u654c\uff0c\u5389\u543c\u58f0\u4e2d\uff0c\u4e00\u4e2a\u53c8\u5c16\u53c8 \u7ec6\u7684\u58f0\u97f3\u4e0d\u4f4f\u5730\u51b7\u7b11\u9053\uff1a\u201c\u6211\u65e9\u5c31\u77e5\u9053\u4f60\u8fd9\u548c\u5c1a\u4e0d\u662f\u597d\u4eba\uff0c\u60f3\u4e0d\u5230\u4f60\u8fd8\u662f\u5367\u5e95\u7684\u5978\u7ec6\u3002\u201d \u3000\u3000\u53e6\u4e00\u4e2a\u7834\u9523\u822c\u7684\u58f0\u97f3\u4ea6\u81ea\u559d\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4e2a\u5c0f\u5b50\u5feb\u6eda\u51fa\u6765\uff0c\u54fc\u54fc\u2014\u2014\u8981\u60f3\u5230\u8fd9\u91cc\u6765\u6492 \u91ce\uff0c\u771f\u662f\u778e\u4e86\u773c\u775b\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u5fc3\u4e2d\u4e00\u60ca\uff1a\u201c\u96be\u9053\u4ed6\u4eec\u5df2\u77e5\u9053\u6211\u4eec\u5728\u8fd9\u91cc\uff1f\u201d \u3000\u3000\u53c8\u5fae\u4e00\u8fdf\u7591\uff0c\u53ea\u542c\u5916\u9762\u8fdc\u8fdc\u4e00\u4e2a\u58f0\u97f3\u5927\u58f0\u53eb\u7740\u9053\uff1a\u201c\u5728\u8fd9\u91cc\uff0c\u5728\u8fd9\u91cc\uff0c\u725b\u5144\u3001\u8427\u5144\uff0c\u5feb \u51fa\u6765\uff0c\u8fd9\u4e24\u4e2a\u5c0f\u5b50\u8dd1\u4e0b\u5c71\u4e86\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u5fc3\u4e2d\u53c8\u81ea\u5927\u5947\uff1a\u201c\u662f\u8c01\u8dd1\u4e0b\u5c71\u4e86\uff0c\u96be\u9053\u4ed6\u4eec\u8ffd\u7684\u4e0d\u662f\u6211\u4eec\uff1f\u90a3\u4e48\u4ed6\u4eec\u53c8\u662f\u8c01 \u5462\uff1f\u201d \u3000\u3000\u6e29\u747e\u5fc3\u4e2d\uff0c\u6b64\u523b\u4ea6\u662f\u60ca\u7591\u4e0d\u5b9a\uff0c\u5979\u77e5\u9053\u5916\u9762\u7684\u4eba\u90fd\u662f\u81ea\u5df1\u5e08\u7236\u8bf7\u6765\u7684\u6b66\u6797\u9ad8\u624b\uff0c\u4e5f\u77e5\u9053 \u4ed6\u4eec\u8ffd\u6355\u7684\u4e0d\u662f\u81ea\u5df2\uff0c\u4f46\u81ea\u5df1\u6b64\u523b\u8fd9\u526f\u6a21\u6837\uff0c\u53c8\u548c\u8fd9\u5c11\u5e74\u5353\u957f\u537f\u5728\u4e00\u8d77\uff0c\u4ea6\u662f\u4e07\u4e07\u4e0d\u80fd\u8ba9\u4eba \u89c1\u7740\u7684\uff0c\u5979\u7acb\u5728\u9ed1\u6697\u4e4b\u4e2d\uff0c\u8fdb\u4ea6\u4e0d\u662f\uff0c\u9000\u4ea6\u4e0d\u662f\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u5374\u4e5f\u4e0d\u77e5\u8be5\u5982\u4f55\u662f\u597d\u3002 \u3000\u3000\u539f\u6765\u65b9\u624d\u591a\u4e8b\u5934\u9640\u89c1\u4e86\u5353\u957f\u537f\u4e0e\u6e29\u747e\u5bf9\u9762\u76f8\u8dea\uff0c\u6084\u6084\u9000\u5230\u5927\u6bbf\uff0c\u5fc3\u4e2d\u5374\u8d8a\u60f3\u8d8a\u89c9\u7eb3\u95f7\uff0c \u4e0d\u77e5\u8fd9\u4e24\u4e2a\u5e74\u8f7b\u4eba\u7a76\u7adf\u5728\u5e72\u4ec0\u4e48\u3002 \u3000\u3000\u4ed6\u672c\u662f\u751f\u6027\u61a8\u76f4\u9c81\u83bd\u4e4b\u4eba\uff0c\u53c8\u559c\u591a\u4e8b\uff0c\u8ba9\u4ed6\u5fc3\u91cc\u5b58\u4e2a\u79d8\u5bc6\uff0c\u5b9e\u5728\u662f\u975e\u5e38\u56f0\u96be\uff0c\u4ed6\u5728\u8fd9\u5927 \u6bbf\u91cc\u5750\u4e5f\u4e0d\u662f\uff0c\u7ad9\u4e5f\u4e0d\u662f\uff0c\u4e00\u4f1a\u513f\u7ad9\u5728\u95e8\u53e3\u51fa\u795e\uff0c\u4e00\u4f1a\u513f\u5728\u5927\u6bbf\u4e2d\u515c\u7740\u5708\u5b50\uff0c\u76f4\u6068\u5353\u957f\u537f\u6e29 \u747e\u4e8c\u4eba\u4e0d\u80fd\u5feb\u4e9b\u51fa\u6765\uff0c\u544a\u8bc9\u81ea\u5df1\u8fd9\u7a76\u7adf\u662f\u600e\u4e48\u56de\u4e8b\u3002 \u3000\u3000\u4f46\u662f\u65f6\u95f4\u4e00\u70b9\u4e00\u70b9\u7684\u8fc7\u53bb\uff0c\u4ed6\u4e24\u4eba\u8fd8\u662f\u6ca1\u6709\u51fa\u6765\uff0c\u591a\u4e8b\u5934\u9640\u6b63\u81ea\u4e0d\u8010\u70e6\uff0c\u6bbf\u5916\u7a81\u7136\u6084\u65e0 \u58f0\u606f\u7684\u63a0\u4eba\u4e24\u6761\u4eba\u5f71\u6765\u3002 \u3000\u3000\u4ed6\u76ee\u5149\u4e00\u95ea\uff0c\u9ed1\u6697\u4e2d\u770b\u4e0d\u6e05\u8fd9\u4e24\u4eba\u662f\u8c01\uff0c\u5f53\u4e0b\u4e00\u95ea\u8eab\u5f62\uff0c\u5728\u795e\u53f0\u524d\u6284\u8d77\u90a3\u6761\u6c89\u91cd\u903e\u6052\u7684 \u65b9\u4fbf\u94f2\uff0c\u62e6\u4f4f\u90a3\u4e24\u4eba\u7684\u53bb\u8def\uff0c\u4e00\u58f0\u5927\u559d\uff0c\u53c8\u559d\u9053\uff1a\u201c\u65e0\u8bba\u8c01\u8981\u8fdb\u53bb\uff0c\u5148\u5403\u6d12\u5bb6\u4e00\u6756\u3002\u201d \u3000\u3000\u8fd9\u4e00\u58f0\u4fbf\u662f\u8fdc\u5728\u540e\u9762\u7684\u5353\u957f\u537f\u4e0e\u6e29\u747e\u4e24\u4eba\u90fd\u542c\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u63a0\u5165\u80a1\u7684\u4e24\u4eba\u89c1\u5230\u7a81\u7136\u6709\u4eba \u6321\u4f4f\u81ea\u5df1\u7684\u53bb\u8def\uff0c\u53c8\u542c\u4e86\u8fd9\u4e00\u58f0\u5927\u559d\uff0c\u4ea6\u4e0d\u7981\u4e3a\u4e4b\u4e00\u60ca\uff0c\u500f\u7136\u987f\u4f4f\u8eab\u5f62\u3002 \u3000\u3000\u591a\u4e8b\u5934\u9640\u5927\u559d\u8fc7\u540e\uff0c\u5b9a\u775b\u4e00\u770b\uff0c\u53ea\u89c1\u8fd9\u4e24\u4eba\u4e00\u4e2a\u8eab\u8eaf\u7626\u957f\uff0c\u624b\u91cc\u5012\u63d0\u7740\u4e00\u67c4\u4e27\u95e8\u957f\u5251\uff0c \u4e00\u4e2a\u624b\u91cc\u63d0\u7740\u4e24\u6761\u7af9\u8282\u94a2\u97ad\uff0c\u5374\u662f\u4e2a\u9a7c\u5b50\u3002 \u3000\u3000\u4e09\u4eba\u516d\u53ea\u773c\u775b\u76ee\u5149\u4e00\u5bf9\uff0c\u53d1\u73b0\u5f7c\u6b64\u7ade\u90fd\u662f\u719f\u4eba\uff0c\u539f\u6765\u8fd9\u4e24\u4eba\u4e00\u662f\u6614\u5e74\u72ec\u884c\u6cb3\u897f\u7684\u5de8\u76d7\u5343 \u91cc\u660e\u9a7c\u725b\u4e00\u5c71\uff0c\u4e00\u662f\u897f\u6e56\u6b66\u6797\u7684\u5927\u8c6a\u65e0\u5f71\u7f57\u5239\u8427\u94c1\u98ce\uff0c\u8fd9\u4e24\u4eba\u867d\u7136\u4e00\u4e2a\u5728\u897f\uff0c\u4e00\u4e2a\u5728\u5357\uff0c\u4f46 \u6b64\u523b\u5374\u90fd\u662f\u88ab\u4e11\u4eba\u6e29\u5982\u7389\u8bf7\u6765\u7684\u8d35\u5bbe\u3002\u4ed6\u4eec\u4e0e\u591a\u4e8b\u5934\u9640\u867d\u7136\u6c14\u5473\u4e0d\u6295\uff0c\u4e0d\u76f8\u63a5\u8fd1\uff0c\u4f46\u5f7c\u6b64\u5374 \u90fd\u662f\u8ba4\u5f97\u7684\u3002 \u3000\u3000\u591a\u4e8b\u5934\u9640\u89c1\u4e86\u8fd9\u4e24\u4eba\u7a81\u7136\u8dd1\u6765\uff0c\u5fc3\u4e2d\u56fa\u662f\u4e00\u60ca\uff0c\u8fd9\u4e24\u4eba\u89c1\u4e86\u591a\u4e8b\u5934\u9640\u7a81\u7136\u5728\u6b64\u62e6\u4f4f\u53bb \u8def\uff0c\u5fc3\u4e2d\u4ea6\u662f\u4e00\u60ca\u3002 \u3000\u3000\u65e0\u5f71\u4e27\u95e8\u4eba\u8f83\u9634\u6c89\uff0c\u542c\u4e86\u591a\u4e8b\u5934\u9640\u7684\u8fd9\u58f0\u5927\u559d\uff0c\u53ea\u51b7\u51b7\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6709\u4eba\u5230\u5c71\u4e0a\u6492\u91ce\uff0c \u6211\u4e24\u4eba\u8ffd\u8e2a\u6765\u6b64\uff0c\u5927\u5e08\u4e3a\u4f55\u8981\u62e6\u4f4f\u53bb\u8def\uff1f\u201d \u3000\u3000\u591a\u4ead\u5934\u9640\u5176\u5b9e\u4e5f\u4e0d\u77e5\u9053\u6e29\u747e\u4e3a\u4ec0\u4e48\u8981\u81ea\u5df1\u62e6\u4f4f\u522b\u4eba\uff0c\u4f46\u4ed6\u65e2\u5df2\u7b54\u5141\u4e8e\u5979\uff0c\u4fbf\u662f\u5929\u738b\u8001\u5b50 \u524d\u6765\uff0c\u4ed6\u4e5f\u65ad\u65ad\u4e0d\u4f1a\u653e\u884c\u7684\uff0c\u5f53\u4e0b\u4e00\u6a2a\u624b\u4e2d\u65b9\u4fbf\u94f2\uff0c\u53cc\u76ee\u4e00\u5f20\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u8fd9\u91cc\u9762\u6ca1\u6709 \u4eba\uff0c\u4f60\u4eec\u8981\u627e\u4eba\uff0c\u8fd8\u662f\u8d76\u5feb\u5230\u522b\u5904\u53bb\u5427\uff01\u201d \u3000\u3000\u5343\u91cc\u660e\u9a7c\u725b\u4e00\u5c71\u4ea6\u662f\u6027\u5982\u70c8\u706b\uff0c\u54ea\u91cc\u53d7\u5f97\u4e0b\u8fd9\u79cd\u8154\u8c03\uff0c\u201c\u54c7\u201d\u7684\u4e00\u58f0\u5927\u559d\uff0c\u53cc\u7ba1\u9f50\u4e0b\uff0c \u4e24\u6761\u94a2\u97ad\uff0c\u6ca1\u5934\u6ca1\u8111\u7684\u6253\u4e86\u4e0b\u53bb\uff0c\u591a\u4e8b\u5934\u9640\u54c8\u54c8\u4e00\u7b11\uff0c\u5fd6\u9053\uff1a\u201c\u4f60\u8fd9\u662f\u8981\u627e\u5012\u6bd2\u3002\u201d \u3000\u3000\u4ed6\u5929\u751f\u795e\u529b\uff0c\u5bf9\u654c\u6700\u559c\u6897\u6253\u786c\u63a5\uff0c\u4e00\u6a2a\u65b9\u4fbf\u94f2\uff0c\u5de6\u624b\u9633\u628a\u62ff\u7740\u94f2\u5934\uff0c\u53f3\u624b\u9634\u628a\u62ff\u7740\u94f2 \u5c3e\uff0c\u6025\u7684\u8fce\u4e86\u4e0a\u53bb\u3002 \u3000\u3000\u53ea\u542c\u201c\u5f53\u201d\u7684\u4e00\u58f0\u5927\u9707\uff0c\u591a\u4e8b\u5934\u9640\u864e\u53e3\u4e00\u9178\uff0c\u5fc3\u4e2d\u201c\u6026\u201d\u5730\u4e00\u8df3\uff0c\u5fc3\u4e2d\u6697\u81ea\u5600\u5495\uff1a\u201c\u8fd9 \u5c0f\u5b50\u600e\u5730\u4e5f\u6709\u5982\u6b64\u529b\u6c14\uff1f\u201d \u3000\u3000\u5de6\u624b\u4e00\u677e\uff0c\u53f3\u624b\u201c\u547c\u201d\u5730\u62a1\u8d77\uff0c\u7acb\u5288\u534e\u5cb3\uff0c\u62a1\u4e86\u4e0b\u53bb\uff0c\u4ea6\u662f\u786c\u6458\u786c\u62ff\u7684\u521a\u731b\u62db\u5f0f\u3002 \u3000\u3000\u90a3\u5343\u91cc\u660e\u9a7c\u4ea6\u672c\u4ee5\u795e\u529b\u79f0\u8a89\u6c5f\u6e56\uff0c\u6b64\u523b\u5fc3\u4e2d\u4ea6\u5403\u4e86\u4e00\u60ca\u3002 \u3000\u3000\u5374\u89c1\u5bf9\u65b9\u7adf\u7acb\u523b\u8fd8\u4ee5\u989c\u8272\uff0c\u5fc3\u4e2d\u4ea6\u81ea\u6709\u6c14\uff0c\u53cc\u97ad\u4e00\u4ea4\uff0c\u5929\u738b\u6258\u5854\uff0c\u53c8\u662f\u201c\u5f53\u201d\u7684\u4e00\u58f0\u5927 \u9707\uff0c\u8fd9\u4e00\u4e0b\u4e24\u4eba\u90fd\u5012\u9000\u4e86\u4e09\u6b65\uff0c\u591a\u4e8b\u5934\u9640\u811a\u6b65\u65b9\u81ea\u7ad9\u7a33\uff0c\u50cf\u662f\u751f\u6015\u88ab\u4eba\u5360\u4e86\u5148\u4f3c\u7684\uff0c\u53f3\u624b\u4e00 \u5708\uff0c\u65b9\u4fbf\u94f2\u201c\u54d7\u5566\u5566\u201d\u6253\u4e86\u4e2a\u5708\u5b50\uff0c\u53c8\u662f\u4e00\u94f2\u62a1\u4e0b\uff0c\u54ea\u77e5\u5343\u91cc\u660e\u9a7c\u7adf\u53c8\u4e0d\u907f\u4e0d\u95ea\uff0c\u626c\u97ad\u63a5\u4e86 \u4e0a\u53bb\u3002 \u3000\u3000\u201c\u5f53\u3001\u5f53\u3001\u5f53\u201d\u4e09\u62db\u4e00\u8fc7\uff0c\u5343\u91cc\u660e\u9a7c\u867d\u7136\u597d\u4e9b\uff0c\u4f46\u4ea6\u88ab\u9707\u5f97\u864e\u53e3\u53d1\u75bc\uff0c\u65e0\u5f71\u7f57\u5239\u89c1\u8fd9\u4e24 \u4eba\u4ee5\u786c\u78b0\u786c\uff0c\u5bf9\u4e86\u4e09\u62db\uff0c\u5b8c\u5168\u4e0d\u8bb2\u62db\u5f0f\uff0c\u53c8\u662f\u597d\u6c14\uff0c\u53c8\u89c9\u597d\u7b11\uff0c\u5fc3\u4e2d\u6697\u9a82\u8fd9\u4e24\u4eba\u5168\u662f\u6d51\u4eba\uff0c \u624b\u8155\u4e00\u9707\uff0c\u9707\u5f97\u6735\u6735\u5251\u82b1\uff0c\u5374\u4ece\u591a\u4e8b\u5934\u9640\u8eab\u65c1\u4fa7\u8eab\u800c\u8fc7\uff0c\u60f3\u4e58\u4ed6\u529b\u6c14\u4e0d\u7ee7\u65f6\u63a0\u5230\u540e\u9662\u53bb\u3002 \u3000\u3000\u54ea\u77e5\u591a\u4e8b\u5934\u9640\u4eba\u867d\u6709\u4e9b\u6d51\u9968\uff0c\u4f46\u6b66\u529f\u5374\u6781\u662f\u7cbe\u7eaf\uff0c\u4e00\u8eab\u6a2a\u7ec3\uff0c\u66f4\u662f\u5916\u5bb6\u529f\u592b\u4e2d\u7684\u7edd\u9876\u4e4b \u5904\uff0c\u65e0\u5f71\u7f57\u5239\u8eab\u5f62\u65b9\u81ea\u63a0\u5230\u540e\u9662\uff0c\u4ed6\u53c8\u7acb\u523b\u8ddf\u4e86\u8fc7\u6765\uff0c\u4e00\u8a00\u4e0d\u53d1\uff0c\u6402\u5934\u5c31\u662f\u4e00\u94f2\uff0c\u65e0\u5f71\u7f57\u5239 \u53ef\u4e0d\u6562\u8ddf\u4ed6\u786c\u78b0\uff0c\u8eab\u5f62\u4e00\u95ea\uff0c\u53cd\u8eab\u4e00\u5251\uff0c\u5251\u5149\u70b9\u70b9\uff0c\u76f4\u523a\u591a\u4e8b\u5934\u9640\u7684\u53cc\u81c2\u808b\u4e0b\u3002 \u3000\u3000\u8fd9\u4e00\u5251\u6bd2\u8fa3\u51f6\u72e0\uff0c\u901f\u800c\u4e14\u731b\uff0c\u591a\u4e8b\u5934\u9640\u77e5\u9053\u9047\u7740\u4e86\u624e\u624b\u8d27\u8272\uff0c\u53e3\u4e2d\u559d\u53f1\u8fde\u58f0\uff0c\u65bd\u5c55\u5f00\u5c11 \u6797\u7edd\u827a\u8361\u9b54\u5982\u610f\u65b9\u4fbf\u94f2\u6cd5\uff0c\u94f2\u5f71\u5982\u5c71\uff0c\u91d1\u94c1\u4ea4\u9e23\uff0c\u548c\u8fd9\u4e24\u6e56\u5927\u8c6a\u6597\u5728\u4e00\u5904\u3002 \u3000\u3000\u65e0\u5f71\u7f57\u5239\u89c1\u5230\u8fd9\u548c\u5c1a\u5982\u6b64\u7ea0\u7f20\uff0c\u5fc3\u4e2d\u4fbf\u8ba4\u5b9a\u81ea\u5df1\u8ffd\u4e22\u7684\u4eba\u662f\u5728\u540e\u9662\uff0c\u8fd9\u548c\u5c1a\u4ea6\u662f\u5367\u5e95\u7684 \u5978\u7ec6\uff0c\u4fbf\u5c16\u58f0\u5927\u7b11\u7740\u559d\u9a82\u8d77\u6765\uff0c\u90a3\u5343\u91cc\u660e\u9a7c\u6b47\u606f\u534a\u664c\uff0c\u81ea\u89c9\u53cc\u81c2\u5df2\u53ef\u7528\u4e0a\u529b\u4e86\uff0c\u4fbf\u4e5f\u63a0\u4e86\u8fdb \u6765\uff0c\u4ea6\u81ea\u5927\u58f0\u559d\u9a82\uff0c\u4e24\u4eba\u4ee5\u4e8c\u654c\u4e09\uff0c\u5251\u5149\u97ad\u5f71\u5c06\u591a\u4e8b\u5934\u9640\u5c42\u5c42\u56f4\u4f4f\uff0c\u4f46\u4ecd\u662f\u672a\u80fd\u53d6\u80dc\u3002 \u3000\u3000\u54ea\u77e5\u8fd9\u65f6\u5bfa\u5916\u5374\u54cd\u8d77\u4e00\u4e2a\u8ffd\u654c\u4e4b\u4eba\u7684\u547c\u559d\u4e4b\u58f0\uff0c\u8bf4\u662f\u5728\u4e0b\u5c71\u7684\u9053\u8def\u4e0a\u53d1\u89c9\u654c\u8e2a\uff0c\u8fd9\u4e24\u4eba \u89c1\u8fd9\u591a\u4e8b\u5934\u9640\u8d8a\u6253\u8d8a\u6709\u52b2\uff0c\u4e5f\u4e0d\u613f\u548c\u4ed6\u7f20\u6218\uff0c\u4fbf\u8fdb\u4e00\u6b65\u5237\u5237\u4e24\u97ad\u4e00\u5251\uff0c\u770b\u6765\u867d\u7136\u72e0\u8fa3\uff0c\u5176\u5b9e \u5374\u662f\u865a\u6643\u4e00\u62db\uff0c\u62db\u5f0f\u8fd8\u672a\u4f7f\u5168\uff0c\u8eab\u5f62\u4fbf\u5df2\u63a0\u5411\u5bfa\u5916\u3002 \u3000\u3000\u591a\u4e8b\u5934\u9640\u547c\u547c\u7a7a\u62a2\u4e86\u51e0\u94f2\uff0c\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u5154\u5d3d\u5b50\u771f\u6ca1\u6709\u7528\uff0c\u6e9c\u4e86\u3002\u201d \u3000\u3000\u504f\u6bbf\u4e2d\u7684\u5353\u957f\u537f\u53ea\u542c\u6e29\u8c28\u8f7b\u8f7b\u53f9\u4e86\u53e3\u6c14\uff0c\u7136\u540e\u53c8\u8f7b\u8f7b\u8bf4\u9053\uff1a\u201c\u8d70\u4e86\u3002\u201d \u3000\u3000\u4ed6\u5fc3\u60c5\u4ea6\u81ea\u4e00\u677e\uff0c\u8981\u77e5\u9053\u4ed6\u5e76\u975e\u754f\u60e7\u4e8e\u4eba\uff0c\u800c\u662f\u89c9\u5f97\u81ea\u5df1\u5728\u6b64\u65f6\u6b64\u5730\u548c\u6e29\u747e\u5728\u4e00\u5904\uff0c\u88ab \u4eba\u89c1\u4e86\uff0c\u603b\u662f\u4e0d\u5b89\u3002 \u3000\u3000\u662f\u4ee5\u4ed6\u6b64\u523b\u4ea6\u4e0d\u89c9\u677e\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8d70\u4e86\uff01\u201d \u3000\u3000\u591a\u4e8b\u5934\u9640\u671b\u7740\u8427\u3001\u725b\u4e8c\u4eba\u7684\u8eab\u5f62\u6d88\u5931\u4e4b\u540e\uff0c\u5fcd\u4e0d\u4f4f\u5927\u53eb\u4e00\u58f0\uff1a\u201c\u4ed6\u4eec\u8d70\u4e86\uff01\u201d \u3000\u3000\u4ea6\u81ea\u63a0\u4eba\u504f\u6bbf\uff0c\u591c\u8272\u4e2d\u65b9\u4fbf\u94f2\u96ea\u4eae\u7684\u94f2\u5934\u95ea\u95ee\u53d1\u5149\uff0c\u6620\u7740\u4ed6\u7684\u9762\u5bb9\uff0c\u4ea6\u662f\u5f97\u610f\u5f02\u5e38\uff0c\u6e29 \u747e\u8f7b\u8f7b\u7684\u4e00\u53f9\uff0c\u8bf4\u9053\uff1a\u201c\u5927\u5e08\u771f\u597d\u529f\u592b\u3002\u201d \u3000\u3000\u591a\u4e8b\u5934\u9640\u54c8\u54c8\u5927\u7b11\u8d77\u6765\uff0c\u4e00\u624b\u63d0\u7740\u65b9\u4fbf\u94f2\uff0c\u4e00\u624b\u62cd\u7740\u80f8\u812f\uff0c\u5927\u7b11\u8bf4\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u6d12\u5bb6\u529f \u592b\u867d\u7b97\u4e0d\u5f97\u9ad8\uff0c\u4f46\u5c31\u51ed\u8fd9\u79cd\u5bb6\u4f19\uff0c\u518d\u6765\u4e24\u4e2a\u4e5f\u7b97\u4e0d\u4e86\u4ec0\u4e48\u3002\u201d \u3000\u3000\u4ed6\u53c8\u81ea\u4e00\u62cd\u80f8\u819b\uff1a\u201c\u59d1\u5a18\uff0c\u4f60\u653e\u5fc3\uff0c\u6709\u6d12\u5bb6\u5728\u8fd9\u91cc\uff0c\u4ec0\u4e48\u4eba\u4e5f\u6765\u4e0d\u4e86\uff0c\u4f60\u4e24\u4e2a\u82e5\u662f\u8fd8\u6709 \u8bdd\u8bf4\uff0c\u53ea\u7ba1\u653e\u5fc3\u2014\u2014\u201d\u54ea\u77e5\u4ed6\u8bdd\u72b9\u672a\u4e86\uff0c\u5353\u957f\u537f\u7a81\u7136\u51b7\u51b7\u9053\uff1a\u201c\u53ea\u6015\u672a\u5fc5\u5427\u3002\xb7\u591a\u4e8b\u5934\u9640\u5927 \u6012\u4e4b\u4e0b\uff0c\u4e00\u8f69\u6d53\u7709\uff0c\u6b63\u5f85\u559d\u95ee\uff0c\u4f46\u591c\u8272\u4e2d\uff0c\u53ea\u89c1\u5353\u957f\u537f\u6e29\u747e\u56db\u53ea\u53d1\u4eae\u7684\u773c\u775b\uff0c\u5374\u671b\u7740\u81ea\u5df1\u8eab \u540e\uff0c\u5fc3\u4e2d\u4e00\u51db\uff0c\u5fcd\u4e0d\u4f4f\u56de\u5934\u671b\u53bb\uff0c\u8fd9\u504f\u6bbf\u7684\u95e8\u69db\u4e0a\u7adf\u7a81\u7136\u591a\u4e86\u4e24\u6761\u4eba\u5f71\u3002\u8fd9\u4e24\u4eba\u4e00\u822c\u9ad8\u77ee\uff0c \u4e00\u822c\u80d6\u7626\uff0c\u5e76\u80a9\u5f53\u95e8\u800c\u7acb\uff0c\u671b\u7740\u6bbf\u5185\u7684\u4e09\u4eba\uff0c\u4f3c\u4e4e\u4ea6\u662f\u8fdb\u9000\u4e0d\u5f97\uff0c\u591a\u4e8b\u5934\u9640\u53cc\u76ee\u4e00\u5f20\uff0c\u5353\u957f \u537f\u5df2\u81ea\u6717\u58f0\u9053\uff1a\u201c\u670b\u53cb\u662f\u8c01\uff1f\u4f55\u4e0d\u8fdb\u6765\u4e00\u53d9\u3002\u201d \u3000\u3000\u539f\u6765\u8fd9\u4e09\u4eba\u4e2d\u9605\u5386\u867d\u4ee5\u5353\u957f\u537f\u6700\u6d45\uff0c\u4f46\u76ee\u529b\u4e4b\u654f\u9510\uff0c\u5374\u8fd8\u5728\u6e29\u747e\u4e0e\u591a\u4e8b\u5934\u9640\u4e4b\u4e0a\uff0c\u65b9\u624d \u8bf4\u8bdd\u4e4b\u9645\uff0c\u4ed6\u5df2\u77a5\u89c1\u9662\u4e2d\u7a81\u7136\u63a0\u4eba\u4e24\u6761\u4eba\u5f71\uff0c\u795e\u8272\u4f3c\u4e4e\u9887\u4e3a\u4ed3\u60f6\uff0c\u843d\u5730\u540e\u4fbf\u63a0\u4e86\u8fc7\u6765\uff0c\u591a\u4e8b \u5934\u9640\u8bdd\u58f0\u672a\u4e86\uff0c\u8fd9\u4e8c\u4eba\u5df2\u63a0\u81f3\u95e8\u53e3\uff0c\u770b\u89c1\u623f\u4e2d\u6709\u4eba\uff0c\u4f3c\u4e4e\u4ea6\u5403\u4e86\u4e00\u60ca\u3002 \u3000\u3000\u5353\u957f\u537f\u53ea\u89c1\u8fd9\u4e24\u4eba\u5e74\u7eaa\u4eff\u4f5b\u90fd\u5728\u5f31\u51a0\u5e74\u95f4\uff0c\u795e\u8272\u53c8\u5982\u6b64\u4ed3\u60f6\uff0c\u663e\u89c1\u5f97\u7edd\u975e\u4e11\u4eba\u6e29\u5982\u7389\u95e8 \u4e0b\uff0c\u5fc3\u4e2d\u4e00\u52a8\uff0c\u7a81\u7136\u60f3\u8d77\u65b9\u624d\u5bfa\u5916\u90a3\u4eba\u9065\u547c\u7684\u8bdd\uff0c\u4fbf\u65ad\u5b9a\u8fd9\u4e24\u4eba\u4fbf\u662f\u524d\u6765\u63a2\u5c71\u800c\u88ab\u6e29\u5982\u7389\u95e8 \u4e0b\u8ffd\u6355\u4e4b\u4eba\uff0c\u662f\u4ee5\u6b64\u523b\u624d\u4f1a\u8ba9\u4ed6\u4eec\u8fdb\u6765\u4e00\u53d9\u3002 \u3000\u3000\u90a3\u4e24\u4eba\u5bf9\u671b\u4e00\u773c\uff0c\u4f3c\u4e4e\u4e5f\u542c\u5f97\u51fa\u5353\u957f\u537f\u8bdd\u4e2d\u5e76\u65e0\u6076\u610f\uff0c\u4fbf\u4e00\u8d77\u8d70\u4e86\u8fdb\u6765\uff0c\u4f46\u4ea6\u4e0d\u77e5\u8bf4\u8bdd \u7684\u4eba\u662f\u8c01\uff0c\u8981\u77e5\u9053\u5353\u957f\u537f\u591a\u5e74\u82e6\u7ec3\uff0c\u76ee\u529b\u5927\u8d85\u5e38\u4eba\uff0c\u4ed6\u867d\u7136\u770b\u5f97\u6e05\u8fd9\u4e24\u4eba\u7684\u9762\u5bb9\uff0c\u8fd9\u4e24\u4eba\u5374 \u770b\u4e0d\u6e05\u4ed6\uff0c\u5176\u4e2d\u4e00\u4eba\u5fae\u4e00\u8fdf\u7591\uff0c\u7a81\u7136\u4f38\u624b\u53d6\u51fa\u706b\u6298\u5b50\uff0c\u201c\u5bdf\u201d\u7684\u4e00\u58f0\u6253\u4eae\uff0c\u56db\u9053\u76ee\u5149\u4e00\u8f6c\uff0c \u4fbf\u4e00\u8d77\u505c\u7559\u5728\u6e29\u747e\u9762\u4e0a\u3002 \u3000\u3000\u5353\u957f\u537f\u76ee\u5149\u52a8\u5904\uff0c\u53ea\u89c1\u8fd9\u4e24\u4eba\u679c\u7136\u4ff1\u6781\u5e74\u8f7b\uff0c\u5bb9\u8c8c\u4ea6\u90fd\u5341\u5206\u4fca\u79c0\uff0c\u4e24\u4eba\u5e76\u80a9\u800c\u7acb\uff0c\u867d\u7136 \u795e\u8272\u95f4\u6709\u4e9b\u72fc\u72c8\uff0c\u4f46\u5fae\u5f31\u7684\u706b\u5149\u4e2d\u5374\u4ecd\u90fd\u663e\u5f97\u82f1\u633a\u51fa\u7fa4\u3002 \u3000\u3000\u4f46\u5353\u957f\u537f\u4e00\u89c1\u8fd9\u4e24\u4eba\u4e4b\u9762\uff0c\u5fc3\u4e2d\u5374\u4e0d\u7981\u4e3a\u4e4b\u4e00\u8df3\u2014\u2014\u539f\u6765\u8fd9\u4e24\u4eba\u4ff1\u90fd\u662f\u82f1\u4fca\u633a\u9038\uff0c\u8eab\u4e0a \u5374\u4ff1\u90fd\u7a7f\u7740\u4e00\u88ad\u674f\u9ec4\u8272\u957f\u886b\uff0c\u9aa4\u773c\u671b\u53bb\uff0c\u7adf\u548c\u90a3\u5c91\u7cb2\u7b80\u76f4\u4e00\u6a21\u4e00\u6837\u3002 \u3000\u3000\u4ed6\u4eec\u5374\u4e0d\u77e5\u9053\u8fd9\u4e24\u4eba\u4e5f\u662f\u90a3\u4e07\u5999\u771f\u541b\u7684\u95e8\u4e0b\u5f1f\u5b50\uff0c\u4e5f\u5c31\u662f\u5341\u5e74\u4ee5\u524d\u548c\u5c91\u7cb2\u4e00\u8d77\u968f\u7740\u4e07\u5999 \u771f\u541b\u540c\u4e0a\u9ec4\u5c71\u7684\u7ae5\u5b50\uff0c\u500f\u5ffd\u5341\u5e74\uff0c\u8fd9\u4e24\u4eba\u4ea6\u90fd\u957f\u5927\u6210\u4eba\uff0c\u4e07\u5999\u771f\u541b\u884c\u8e2a\u4e0d\u5b9a\uff0c\u8fd9\u4e24\u5165\u827a\u6210\u540e \u4fbf\u4e5f\u548c\u5c91\u7cb2\u4e00\u8d77\u4e0b\u5c71\u95ef\u8361\u6c5f\u6e56\uff0c\u5c91\u7cb2\u5230\u4e86\u6c5f\u5357\uff0c\u4ed6\u4eec\u5374\u4e00\u4e2a\u5728\u4e24\u6cb3\uff0c\u4e00\u4e2a\u5728\u5ddd\u9655\u3002\u5f53\u65e5\u5728\u829c \u6e56\u57ce\u4e2d\u591a\u81c2\u795e\u5251\u5927\u5bff\u4e4b\u65f6\uff0c\u90a3\u6c5f\u5357\u9556\u5934\u82cf\u4e16\u5e73\u53e3\u4e2d\u6240\u8bf4\uff0c\u5728\u96c1\u8361\u5c71\u4e0b\u9047\u7740\u7684\u5c11\u5e74\uff0c\u4fbf\u4e5f\u662f\u8fd9 \u4e24\u4eba\u5176\u4e2d\u4e4b\u4e00\u2014\u2014\u94c1\u8fbe\u4eba\u3002 \u3000\u3000\u8fd9\u5e08\u5144\u5f1f\u4e09\u4eba\u6b66\u529f\u4ff1\u90fd\u5f97\u4e86\u4e07\u5999\u771f\u541b\u771f\u4f20\uff0c\u81ea\u7136\u8eab\u624b\u4ff1\u90fd\u4e0d\u5f31\uff0c\u4e09\u4eba\u867d\u7136\u884c\u8d70\u7684\u9053\u8def\u4e0d \u540c\uff0c\u4f46\u542c\u4e86\u5929\u76ee\u5c71\u8fd9\u4ef6\u8f70\u4f20\u6b66\u6797\u7684\u5927\u4e8b\uff0c\u5374\u4e00\u8d77\u5230\u4e86\u5929\u76ee\u5c71\u9e93\u6765\uff0c\u94c1\u8fbe\u4eba\u4e0e\u53e6\u4e00\u5c11\u5e74\u77f3\u5e73\u6765 \u5f97\u8f83\u8fdf\uff0c\u5374\u4e5f\u5728\u4e34\u5b89\u57ce\u4e2d\u89c1\u7740\u4e86\u4ed6\u5e08\u7236\u7559\u4e0b\u7684\u6697\u8bb0\uff0c\u5f53\u4e0b\u4fbf\u4e00\u8d77\u8d76\u5230\u4e07\u5999\u771f\u541b\u542c\u7ea6\u5b9a\u7684\u5730\u65b9 \u53bb\uff0c\u8fd9\u65f6\u5c39\u51e1\u65b9\u81ea\u5c06\u5c91\u7cb2\u6551\u51fa\uff0c\u4e00\u89c1\u8fd9\u4e24\u4eba\u4e4b\u9762\uff0c\u4fbf\u5631\u5490\u4ed6\u4eec\u5207\u5207\u4e0d\u53ef\u53c2\u4e0e\u8fd9\u5929\u76ee\u5c71\u4e4b\u4f1a\uff0c \u5374\u672a\u8bf4\u51fa\u662f\u4e3a\u4e86\u4ec0\u4e48\u6765\u3002 \u3000\u3000\u5c91\u7cb2\u5403\u8fc7\u82e6\u5934\uff0c\u5fc3\u4e2d\u867d\u4e0d\u613f\uff0c\u5012\u8fd8\u597d\u4e9b\uff0c\u8fd9\u94c1\u8fbe\u4eba\u3001\u77f3\u5e73\u4e24\u4eba\u81ea\u6043\u5e74\u5c11\u827a\u9ad8\uff0c\u65e9\u5df2\u8dc3\u8dc3 \u6b32\u8bd5\uff0c\u4e00\u5fc3\u60f3\u7740\u5728\u5929\u76ee\u5c71\u72ec\u5360\u9b41\u9996\uff0c\u542c\u4e86\u5c39\u51e1\u7684\u8bdd\uff0c\u53e3\u4e2d\u867d\u4e0d\u6562\u8bf4\uff0c\u4f46\u5fc3\u91cc\u5374\u662f\u4e00\u767e\u4e2a\u4e0d\u613f \u610f\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u867d\u7136\u90fd\u662f\u80c6\u5927\u5984\u4e3a\uff0c\u4f46\u5e08\u7236\u7684\u8bdd\uff0c\u5374\u53c8\u4e0d\u6562\u4e0d\u542c\uff0c\u4e24\u4eba\u6697\u4e2d\u4e00\u5546\u8bae\uff0c\u90fd\u9053\uff1a\u201c\u5e08 \u7236\u4e0d\u51c6\u6211\u4eec\u5728\u4f1a\u671f\u4e2d\u5230\u5929\u76ee\u5c71\u53bb\uff0c\u6211\u4eec\u5728\u4f1a\u671f\u524d\u53bb\u96be\u9053\u90fd\u4e0d\u884c\u5417\uff1f\u201d \u3000\u3000\u4e24\u4eba\u867d\u7136\u4e0d\u6562\u8fdd\u80cc\u5e08\u547d\uff0c\u4f46\u5374\u53c8\u62b5\u4e0d\u4f4f\u540d\u5251\u7f8e\u4eba\u7684\u8bf1\u60d1\uff0c\u5982\u6b64\u5546\u8bae\u4e4b\u4e0b\uff0c\u4fbf\u5077\u5077\u4e0a\u4e86\u5929 \u76ee\u5c71\uff0c\u4ed6\u4eec\u5374\u4e0d\u77e5\u9053\u5929\u76ee\u5c71\u4e0a\u9ad8\u624b\u4e91\u96c6\uff0c\u4ed6\u4e24\u4eba\u6b66\u529f\u867d\u9ad8\uff0c\u8f7b\u529f\u867d\u597d\uff0c\u4f46\u600e\u9003\u5f97\u8fc7\u8fd9\u4e9b\u4eba\u7684 \u8033\u76ee\u3002\u4ed6\u4eec\u4e00\u4e0a\u5c71\u4fbf\u88ab\u53d1\u89c9\uff0c\u4e24\u4eba\u4ee5\u4e8c\u654c\u4f17\uff0c\u4e11\u4eba\u6e29\u5982\u7389\u867d\u672a\u73b0\u8eab\uff0c\u8fd9\u4e24\u4eba\u5374\u5df2\u4e0d\u654c\uff0c\u8fd9\u65f6 \u6b63\u662f\u5353\u957f\u537f\u72ec\u6597\u80d6\u4ed9\u7626\u4f5b\u4ee5\u540e\u6d77\u5357\u4e09\u5251\u7684\u65f6\u5019\uff0c\u662f\u4ee5\u4ed6\u540e\u6765\u4e00\u8def\u4e0a\u5c71\uff0c\u90fd\u6ca1\u6709\u4eba\u963b\u6321\uff0c\u539f\u6765 \u8fd9\u65f6\u6b63\u662f\u94c1\u3001\u77f3\u4e24\u4eba\u5728\u5c71\u4e0a\u6614\u6597\u7684\u65f6\u5019\u3002 \u3000\u3000\u53cc\u62f3\u672c\u5c31\u96be\u654c\u56db\u624b\uff0c\u4f55\u51b5\u8fd9\u65f6\u5929\u76ee\u5c71\u4e0a\uff0c\u4ff1\u90fd\u6b66\u6797\u4e00\u6d41\u9ad8\u624b\uff0c\u8fd9\u4e24\u4eba\u4e00\u89c1\u4e0d\u5999\uff0c\u4fbf\u843d\u8352 \u9003\u4e86\u4e0b\u6765\uff0c\u4f46\u4ed6\u4eec\u9003\u5f97\u867d\u5feb\uff0c\u4eba\u5bb6\u8ffd\u7684\u5374\u4e5f\u4e0d\u6162\uff0c\u518d\u52a0\u4e0a\u641c\u7d22\u7684\u4eba\u591a\uff0c\u4e24\u4eba\u9003\u4e86\u4e00\u9635\uff0c\u7ade\u672a \u80fd\u9003\u51fa\u4eba\u5bb6\u7684\u638c\u63e1\u3002 \u3000\u3000\u4e8e\u662f\u8fd9\u4e24\u4eba\u60c5\u6025\u4e4b\u4e0b\uff0c\u4fbf\u7528\u4e86\u624b\u58f0\u4e1c\u51fb\u897f\u3001\u91d1\u8749\u8131\u58f3\u4e4b\u8ba1\uff0c\u81ea\u5df1\u8eb2\u5728\u6697\u5904\uff0c\u5374\u5411\u8fdc\u5904\u6295 \u77f3\uff0c\u90a3\u4e9b\u6c5f\u6e56\u8001\u624b\u518d\u4e5f\u60f3\u4e0d\u5230\u81ea\u5df1\u4f1a\u88ab\u4e24\u4e2a\u521d\u751f\u7684\u96cf\u513f\u6240\u611a\uff0c\u4e00\u8d77\u8ffd\u4e86\u53bb\uff0c\u4ed6\u4e24\u4eba\u5374\u53c8\u6298\u56de \u4e0a\u5c71\uff0c\u51c6\u5907\u5728\u8fd9\u7834\u5e99\u91cc\u6682\u907f\u4e00\u9635\uff0c\u7136\u540e\u518d\u601d\u9003\u8131\u4e4b\u8ba1\u3002 \u3000\u3000\u54ea\u77e5\u7834\u5e99\u4e2d\u4ea6\u6709\u4eba\u5728\uff0c\u8fd9\u4e24\u4eba\u4e00\u60ca\u4e4b\u4e0b\uff0c\u5353\u957f\u537f\u5df2\u81ea\u53d1\u89c9\uff0c\u8fd9\u4e24\u4eba\u672c\u5c31\u77e5\u9053\u9003\u4e0d\u8131\uff0c\u5fc3 \u60f3\u8fd9\u91cc\u53ea\u6709\u4e09\u4eba\uff0c\u5012\u53ef\u62fc\u4e0a\u4e00\u62fc\uff0c\u5374\u542c\u5353\u957f\u537f\u8bf4\u51fa\u90a3\u6beb\u65e0\u6545\u610f\u7684\u8bdd\u6765\uff0c\u8fd9\u4e24\u4eba\u4fbf\u4e00\u8d77\u8d70\u4eba\uff0c \u4ed6\u4eec\u867d\u662f\u60ca\u9b42\u521d\u5b9a\uff0c\u4f46\u4e00\u89c1\u4e86\u7f8e\u5982\u5929\u4ed9\u7684\u6e29\u747e\uff0c\u76ee\u5149\u4e0d\u7981\u53c8\u88ab\u5979\u5438\u5f15\u4f4f\u4e86\uff0c\u518d\u4e5f\u79fb\u4e0d\u5f00\u53bb\u3002 \u3000\u3000\u6e29\u747e\u76ee\u5149\u62ac\u5904\uff0c\u81ea\u7136\u4fbf\u9047\u7740\u8fd9\u4e24\u4eba\u77ac\u4e5f\u4e0d\u77ac\u7684\u773c\u775b\uff0c\u5979\u5728\u5982\u6b64\u5fc3\u60c5\u4e4b\u4e0b\uff0c\u600e\u53d7\u5f97\u4e86\u8fd9\u79cd \u5446\u89c6\uff0c\u7a81\u7136\u51b7\u54fc\u4e00\u58f0\uff0c\u7389\u638c\u8f7b\u6325\uff0c\u706b\u6298\u4e0a\u7684\u706b\u5149\u672c\u5c31\u5fae\u5f31\uff0c\u88ab\u5979\u638c\u98ce\u4e00\u6247\uff0c\u7acb\u5373\u706b\u706d\u4e86\uff0c\u504f \u6bbf\u4e2d\u7acb\u523b\u53c8\u53d8\u5f97\u4e00\u7247\u9edd\u9ed1\u3002 \u3000\u3000\u9ed1\u6697\u4e4b\u4e2d\uff0c\u5404\u4eba\u5f7c\u6b64\u547c\u5438\u76f8\u95fb\uff0c\u5230\u4e86\u6b64\u523b\uff0c\u4ed6\u4eec\u5374\u53c8\u4e0d\u80fd\u5206\u6e05\u654c\u53cb\uff0c\u5fc3\u4e2d\u4fbf\u5404\u81ea\u6709\u4e9b\u7d27 \u5f20\uff0c\u8981\u77e5\u9053\u4ed6\u4eec\u5fc3\u4e2d\u672c\u90fd\u6709\u7740\u62c5\u5fc3\u4e4b\u4e8b\uff0c\u6b64\u523b\u81ea\u7136\u5f7c\u6b64\u754f\u60e7\uff0c\u5353\u957f\u537f\u3001\u591a\u4e8b\u5934\u9640\u3001\u6e29\u747e\u8eab\u7554 \u4ff1\u65e0\u706b\u79cd\uff0c\u8fd9\u94c1\u8fbe\u4eba\u3001\u77f3\u5e73\u4e24\u4eba\u624b\u4e2d\u706b\u6298\u88ab\u638c\u98ce\u6240\u706d\uff0c\u4ed6\u4eec\u867d\u7136\u5fc3\u60f3\u518d\u591a\u770b\u6e29\u747e\u4e24\u773c\uff0c\u4f46\u6b64 \u65f6\u6b64\u523b\uff0c\u5374\u4e5f\u4e0d\u613f\u518d\u5c06\u624b\u4e2d\u706b\u6298\u6253\u4eae\u3002 \u3000\u3000\u54ea\u77e5\u5c31\u5728\u8fd9\u706b\u7130\u706d\u53bb\uff0c\u5149\u7ebf\u9aa4\u6697\u7684\u8fd9\u5239\u90a3\u4e4b\u95f4\uff0c\u4e00\u9053\u5f3a\u5149\uff0c\u7a81\u7136\u6f2b\u65e0\u58f0\u606f\u5730\u4ece\u5353\u957f\u537f\u3001 \u6e29\u747e\u8eab\u540e\u7167\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u4f17\u4eba\u5fc3\u4e2d\u4ff1\u90fd\u4e00\u9707\uff0c\u8c01\u4e5f\u4e0d\u77e5\u9053\u8fd9\u9053\u5f3a\u5149\u662f\u4ece\u54ea\u91cc\u6765\u7684\u3002 \u3000\u3000\u5353\u957f\u537f\u773c\u524d\u6597\u7136\u4e00\u4eae\uff0c\u5927\u60ca\u4e4b\u4e0b\uff0c\u6a2a\u63a0\u4e09\u6b65\uff0c\u95ea\u7535\u822c\u56de\u5934\u671b\u53bb\u3002 \u3000\u3000\u53ea\u89c1\u90a3\u4e4c\u6728\u795e\u684c\u4e4b\u4e0a\uff0c\u6b64\u523b\u7adf\u7aef\u5750\u7740\u4e00\u4e2a\u6ee1\u8eab\u7ea2\u8863\u3001\u4e91\u9b03\u9ad8\u633d\uff0c\u4f46\u5374\u9762\u5bb9\u5947\u4e11\u65e0\u6bd4\u7684\u8001 \u5987\u4eba\u3002 \u3000\u3000\u5979\u2014\u2014 \u3000\u3000\u81ea\u7136\u4fbf\u662f\u90a3\u7ea2\u8863\u5a18\u5a18\u6e29\u5982\u7389\u3002 \u3000\u3000\u6e29\u747e\u76ee\u5149\u52a8\u5904\uff0c\u60ca\u5524\u4e00\u58f0\uff1a\u201c\u5e08\u7236\uff01\u201d \u3000\u3000\u5979\u67f3\u8170\u4e00\u62e7\uff0c\u5237\u5730\u63a0\u5230\u795e\u684c\u524d\uff0c\u76f4\u5230\u6b64\u523b\u4e3a\u6b62\uff0c\u5979\u8fd8\u4e0d\u77e5\u9053\u5979\u4e0d\u5171\u6234\u5929\u7684\u4ec7\u4eba\u4fbf\u662f\u7231\u5979 \u5982\u5973\u7684\u6e29\u5982\u7389\u3002 \u3000\u3000\u591a\u4e8b\u5934\u9640\u5bf9\u6b64\u95f4\u7684\u4e00\u5207\u4e8b\uff0c\u5168\u7136\u90fd\u4e0d\u77e5\u9053\uff0c\u4ed6\u6b64\u523b\u5fc3\u4e2d\u867d\u4ea6\u4e00\u60ca\uff0c\u4f46\u968f\u5373\u5b89\u5fc3\uff0c\u602a\u773c\u4e00 \u7ffb\uff0c\u9000\u5230\u5899\u7554\uff0c\u5bf9\u8fd9\u7ea2\u8863\u5a18\u5a18\u6e29\u5982\u7389\uff0c\u4ed6\u867d\u65e0\u754f\u60e7\u4e4b\u5fc3\uff0c\u5374\u4e5f\u4e0d\u613f\u591a\u770b\u4e00\u773c\u3002 \u3000\u3000\u53ea\u6709\u94c1\u8fbe\u4eba\u4e0e\u77f3\u5e73\uff0c\u6b64\u523b\u5374\u771f\u7684\u60ca\u5f97\u6115\u4f4f\u4e86\uff0c\u4ed6\u4eec\u518d\u4e5f\u60f3\u4e0d\u51fa\u8fd9\u7ea2\u8863\u4e11\u5987\u662f\u600e\u4e48\u4f1a\u7a81\u7136 \u73b0\u8eab\u5728\u8fd9\u623f\u95f4\u91cc\u7684\u3002 \u3000\u3000\u4e24\u4eba\u5b9a\u4e86\u5b9a\u795e\uff0c\u76ee\u5149\u4e00\u8f6c\uff0c\u5634\u91cc\u867d\u672a\u8bf4\u51fa\uff0c\u4f46\u5374\u5df2\u90fd\u77e5\u9053\uff0c\u8fd9\u7ea2\u8863\u4e11\u5987\u4fbf\u662f\u4ed6\u4eec\u4e45\u5df2\u95fb \u540d\u7684\u9b54\u5934\u6e29\u5982\u7389\uff0c\u4ed6\u4eec\u867d\u4e5f\u4e0d\u613f\u5bf9\u8fd9\u540d\u95fb\u5929\u4e0b\u7684\u4e11\u4eba\u591a\u671b\u4e00\u773c\uff0c\u4f46\u5374\u7981\u4e0d\u4f4f\u53c8\u8981\u72e0\u72e0\u5411\u6e29\u5982 \u7389\u624b\u4e2d\u6240\u6301\u7684\u4e00\u7c92\u5de8\u73e0\u671b\u4e0a\u4e00\u773c\uff0c\u4ed6\u4eec\u5e73\u751f\u672a\u66fe\u89c1\u8fc7\u5982\u6b64\u5de8\u5927\u7684\u73e0\u5b50\uff0c\u66f4\u4ece\u672a\u89c1\u8fc7\u5982\u6b64\u5f3a\u70c8 \u7684\u73e0\u5149\u3002 \u3000\u3000\u7136\u540e\uff0c\u4ed6\u4eec\u4fbf\u60f3\u9003\u8d70\uff0c\u4f46\u662f\uff0c\u6e29\u5982\u7389\u4e24\u9053\u6bd4\u73e0\u5149\u8fd8\u8981\u5f3a\u70c8\u7684\u76ee\u5149\uff0c\u5374\u6b63\u77ac\u4e5f\u4e0d\u77ac\u5730\u671b\u5728 \u4ed6\u4eec\u9762\u4e0a\uff0c\u8fd9\u5f3a\u70c8\u7684\u76ee\u5149\u751f\u50cf\u662f\u4e00\u5ea7\u5149\u5c71\uff0c\u538b\u5728\u4ed6\u4eec\u8eab\u4e0a\uff0c\u4f7f\u5f97\u4ed6\u4eec\u51e0\u4e4e\u8fde\u6c14\u90fd\u5598\u4e0d\u8fc7\u6765\u3002 \u3000\u3000\u4e11\u4eba\u6e29\u5982\u7389\u7aef\u5750\u5728\u795e\u684c\u4e0a\uff0c\u52a8\u4e5f\u4e0d\u52a8\uff0c\u5f3a\u70c8\u7684\u73e0\u5149\u6620\u5728\u5979\u9634\u68ee\u800c\u4e11\u6076\u7684\u9762\u5bb9\u4e0a\uff0c\u4f7f\u5f97\u5979 \u7a81\u8d77\u7684\u53cc\u98a7\u770b\u6765\u7adf\u50cf\u662f\u6076\u86df\u5934\u4e0a\u7684\u4e24\u53ea\u7284\u89d2\u4f3c\u7684\uff0c\u518d\u52a0\u4e0a\u5979\u90a3\u5c16\u8038\u800c\u65e0\u8089\u7684\u9e70\u94a9\u957f\u9f3b\uff0c\u4e8e\u662f \u5979\u5c31\u5b9b\u7136\u53d8\u6210\u4e00\u5c0a\u77f3\u523b\u7684\u7f57\u5239\u795e\u50cf\u3002 \u3000\u3000\u77ed\u6682\u7684\u6c89\u9ed8\u3002 \u3000\u3000\u4f46\u6b64\u523b\u8fd9\u77ed\u6682\u7684\u6c89\u9ed8\u5728\u94c1\u8fbe\u4eba\u4e0e\u77f3\u5e73\u7684\u773c\u4e2d\uff0c\u5374\u751f\u50cf\u662f\u6709\u5982\u6c38\u6052\u822c\u957f\u4e45\uff0c\u4ed6\u4eec\u6c89\u7740\u5730\u5411 \u540e\u79fb\u52a8\u7740\u811a\u6b65\uff0c\u7f13\u6162\u5730\uff0c\u4ed4\u7ec6\u5730\uff0c\u4ed6\u4eec\u5168\u5fc3\u5730\u5e0c\u671b\u81ea\u5df1\u811a\u4e0b\u7684\u79fb\u52a8\u4e0d\u81f3\u5f15\u8d77\u522b\u4eba\u7684\u6ce8\u610f\u3002 \u3000\u3000\u4f46\u662f\u2014\u2014 \u3000\u3000\u4e11\u4eba\u6e29\u5982\u7389\u7a81\u7136\u51b7\u53f1\u4e00\u58f0\uff1a\u201c\u505c\u4f4f\uff01\u201d \u3000\u3000\u8fd9\u7b80\u77ed\u800c\u9634\u68ee\u7684\u53f1\u58f0\uff0c\u5176\u4e2d\u7adf\u50cf\u662f\u542b\u8574\u7740\u4e00\u4e07\u79cd\u4ee4\u4eba\u6cd5\u754f\u6151\u670d\u7684\u529b\u91cf\uff0c\u94c1\u8fbe\u4eba\u3001\u77f3\u5e73\u7adf \u5168\u8eab\u4e00\u9707\uff0c\u811a\u518d\u4e5f\u4e0d\u6562\u79fb\u52a8\u4e00\u4e0b\u3002 \u3000\u3000\u665a\u98ce\u4ece\u4ed6\u4eec\u8eab\u540e\u655e\u5f00\u7740\u7684\u95e8\u6237\u4e2d\u5439\u8fdb\u6765\uff0c\u5439\u5728\u4ed6\u4eec\u7684\u80cc\u810a\u4e0a\uff0c\u4ed6\u4eec\u7981\u4e0d\u4f4f\u673a\u4f36\u4f36\u6253\u4e86\u4e2a \u5bd2\u5664\uff0c\u5374\u542c\u6e29\u5982\u7389\u51b7\u51b7\u53c8\u9053\uff1a\u201c\u4eca\u5929\u665a\u4e0a\u8dd1\u5230\u5c71\u4e0a\u6765\u4e71\u95ef\u7684\u8bdd\uff0c\u5c31\u662f\u4f60\u4eec\u4e24\u4e2a\u4eba\u5417\uff1f\u201d \u3000\u3000\u94c1\u8fbe\u4eba\u3001\u77f3\u5e73\uff0c\u53ea\u89c9\u8eab\u540e\u7684\u5bd2\u610f\u8d8a\u6765\u8d8a\u91cd\uff0c\u4ed6\u4eec\u4e0d\u5b89\u5730\u8f6c\u52a8\u7740\u76ee\u5149\uff0c\u751f\u50cf\u662f\u4e00\u53cc\u87cb\u4f0f\u5728 \u96c4\u732b\u5229\u722a\u524d\u7684\u8001\u9f20\u3002 \u3000\u3000\u4e11\u4eba\u6e29\u5982\u7389\u51b7\u7b11\u7684\u58f0\u97f3\u66f4\u523a\u8033\u4e86\uff0c\u7adf\u4f7f\u5f97\u5979\u8eab\u65c1\u7684\u6e29\u747e\u5fc3\u91cc\u5374\u751f\u51fa\u4e00\u9635\u60ca\u6817\u7684\u611f\u89c9\uff0c\u76f4 \u5230\u6b64\u523b\uff0c\u6e29\u5982\u7389\u7ae0\u8fde\u671b\u90fd\u6ca1\u6709\u671b\u5979\u4e00\u773c\uff0c\u8fd9\u662f\u591a\u5e74\u6765\u4ece\u672a\u6709\u8fc7\u7684\u4e8b\uff0c\u5979\u4e0d\u77e5\u9053\u5979\u5e08\u7236\u662f\u4e0d\u662f \u4e5f\u5bf9\u5979\u751f\u4e86\u6c14\uff0c\u4e5f\u8865\u77e5\u9053\u662f\u4e3a\u4e86\u4ec0\u4e48\u5bf9\u5979\u751f\u4e86\u6c14\u3002 \u3000\u3000\u201c\u96be\u9053\u59d1\u59d1\u5df2\u7ecf\u77e5\u9053\u90a3\u59d3\u5c91\u7684\u662f\u6211\u653e\u8d70\u7684\uff1f\u201d \u3000\u3000\u5979\u4e0d\u5b89\u5730\u63e3\u6d4b\u7740\uff0c\u5374\u542c\u6e29\uff0c\u53e3\u7389\u51b7\u7b11\u7740\u9053\uff1a\u201c\u6211\u8d77\u5148\u8fd8\u4ee5\u4e3a\u4f60\u4eec\u65e2\u7136\u6562\u4e0a\u5c71\u6765\u4e71\u95ef\uff0c\u5c31 \u5fc5\u5b9a\u6709\u51e0\u5206\u80c6\u8272\uff0c\u54ea\u77e5\u2014\u2014\u563f\u563f\uff0c\u5374\u4e5f\u662f\u4e24\u4e2a\u80c6\u5c0f\u5982\u9f20\u7684\u9f20\u8f88\u3002\u201d \u3000\u3000\u94c1\u8fbe\u4eba\u3001\u77f3\u5e73\u9762\u988a\u4e00\u7ea2\uff0c\u60f3\u633a\u8d77\u80f8\u819b\uff0c\u8868\u793a\u4e00\u4e0b\u81ea\u5df1\u7684\u52c7\u6c14\uff0c\u4f46\u4e0d\u77e5\u600e\u5730\uff0c\u4ed6\u201c\uff1d\u5e73\u65f6 \u5728\u6bd4\u4ed6\u7af9\uff1a\u5f31\u7684\u654c\u4eba\u9762\u524d\u60ef\u6709\u7684\u52c7\u6c14\uff0c\u6b64\u523b\u7adf\u4e0d\u77e5\u8d70\u5230\u54ea\u91cc\u53bb\u4e86\u3002\u4e00\u4e2a\u52c7\u8005\u4e0e\u4e00\u4e2a\u61e6\u592b\u4e4b\u95f4 \u6700\u5927\u7684\u5dee\u5f02\uff0c\u90a3\u4fbf\u662f\u52c7\u8005\u7684\u52c7\u6c14\u9664\u4e86\u5728\u5fc5\u8981\u7684\u65f6\u5019\u6c38\u8fdc\u4e0d\u4f1a\u5728\u5e73\u65f6\u663e\u9732\uff0c\u800c\u61e6\u592b\u7684\u52c7\u6c14\u5374\u5728 \u6700\u9700\u8981\u52c7\u6c14\u7684\u65f6\u5019\uff0c\u53cd\u800c\u6d88\u5931\u4e86\uff0c\u4e0d\u662f\u5417\uff1f\u4ed6\u4eec\u568e\u5598\u7740\uff0c\u94c1\u8fbe\u4eba\u5fc3\u4e2d\u7a81\u7136\u4e00\u52a8\uff0c\u58ee\u7740\u80c6\u5b50\uff0c \u9053\uff1a\u201c\u665a\u8f88\u94c1\u8fbe\u4eba\u4e0e\u5e08\u5f1f\u77f3\u5e73\uff0c\u6b64\u6765\u5b9e\u5728\u662f\u5949\u4e86\u5bb6\u5e08\u2014\u2014\u201d\u4ed6\u7a81\u7136\u60f3\u8d77\u81ea\u5df1\u7684\u5e08\u7236\u548c\u8fd9\u4e11\u4eba \u6e29\u5982\u7389\u672c\u662f\u670b\u53cb\uff0c\u56e0\u4e4b\u4ed6\u8d76\u7d27\u8bf4\u51fa\u4e86\u5e08\u7236\u7684\u540d\u53f7\uff0c\u53ea\u5f53\u8fd9\u6e29\u5982\u7389\u4f1a\u5356\u51e0\u5206\u9762\u5b50\u3002 \u3000\u3000\u53ea\u89c1\u6e29\u5982\u7389\u76ee\u5149\u4e00\u95ea\uff0c\u622a\u65ad\u4e86\u4ed6\u7684\u6d3b\u9053\uff1a\u201c\u4f60\u4eec\u662f\u4e0a\u5c71\u6765\u62dc\u8c12\u6211\u7684\uff0c\u800c\u4e0d\u662f\u6765\u6363\u4e71\u7684\uff0c \u662f\u5417\uff1f\u201d \u3000\u3000\u94c1\u8fbe\u4eba\u3001\u77f3\u5e73\u8fde\u5fd9\u4e00\u8d77\u70b9\u5934\u5c0f\u6e29\u5982\u7389\u51b7\u51b7\u53c8\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u4eec\u7684\u5e08\u7236\u662f\u8c01\u5462\uff1f\u201d \u3000\u3000\u5979\u76ee\u5149\u95ea\u52a8\u7740\uff0c\u95ea\u52a8\u7740\u4e00\u9635\u9635\u5c16\u523b\u7684\u5632\u5f04\uff0c\u4f46\u662f\u94c1\u8fbe\u4eba\u4e0e\uff0c\u77f3\u5e73\u5374\u611a\u7b28\u5f97\u770b\u4e0d\u51fa\u5979\u6b64\u523b \u76ee\u5149\u4e2d\u7684\u795e\u8272\uff0c\u4ed6\u4eec\u5fc3\u4e2d\u53cd\u800c\u5927\u559c\uff0c\u4ee5\u4e3a\u6709\u4e86\u751f\u673a\u3002 \u3000\u3000\u4e24\u4eba\u7adf\u62a2\u7740\u9053\uff1a\u201c\u5bb6\u5e08\u4fbf\u662f\u8001\u524d\u8f88\u7684\u6545\u53cb\u4e07\u5999\u771f\u541b\u5c39\u51e1\uff01\u201d \u3000\u3000\u4ed6\u4eec\u60c5\u6025\u4e4b\u4e0b\uff0c\u7adf\u8fde\u81ea\u5df1\u5e08\u7236\u7684\u540d\u53f7\u90fd\u6beb\u4e0d\u907f\u8bb3\u7684\u76f4\u8bf4\u4e86\u51fa\u6765\u3002 \u3000\u3000\u4e11\u4eba\u6e29\u5982\u7389\u957f\u957f\u201c\u5662\u201d\u4e86\u4e00\u58f0\uff0c\u76ee\u5149\u5728\u4ed6\u4eec\u9762\u4e0a\u8f6c\u52a8\u7740\uff0c\u50cf\u662f\u8981\u770b\u900f\u4ed6\u4eec\u7684\u5fc3\u4f3c\u7684\u3002 \u3000\u3000\u5979\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u539f\u6765\u4f60\u4eec\u662f\u5c39\u51e1\u7684\u5f1f\u5b50\uff0c\u90a3\u96be\u602a\u4e00\u4e00\u201d\u67af\u7626\u7684\u8eab\u5f62\uff0c\u7a81\u7136\u6709\u5982\u5c71\u732b\u822c\u81ea \u795e\u684c\u4e0a\u5f39\u8d77\uff0c\u53f3\u624b\u624b\u6307\u4e00\u5f39\uff0c\u624b\u4e2d\u5f84\u5bf8\u660e\u73e0\uff0c\u7a81\u7136\u95ea\u7535\u822c\u7684\u8131\u624b\u98de\u53bb\uff0c\u5e26\u7740\u4e00\u7f15\u5c16\u9510\u7684\u98ce \u58f0\uff0c\u51fb\u5411\u77f3\u5e73\u80f8\u808b\u4e4b\u95f4\u7684\u5c06\u53f0\u5927\u7a74\u3002 \u3000\u3000\u800c\u5979\u7684\u8eab\u5f62\u7adf\u51e0\u4e4e\u6bd4\u8fd9\u8131\u624b\u800c\u98de\u7684\u73e0\u5149\u8fd8\u8981\u5feb\u901f\u5730\u63a0\u5230\u94c1\u8fbe\u4eba\u8eab\u524d\uff0c\u53f3\u624b\u75be\u4f38\uff0c\u4e95\u6307\u5982 \u5251\uff0c\u4ea6\u81ea\u70b9\u5411\u94c1\u8fbe\u4eba\u80f8\u808b\u95ee\u7684\u5c06\u53f0\u5927\u7a74\u3002 \u3000\u3000\u65b9\u624d\u4ece\u6e29\u5982\u7389\u8f83\u4e3a\u548c\u7f13\u4e9b\u7684\u8bed\u6c14\u4e2d\u542c\u51fa\u4e00\u4e9b\u8f6c\u673a\u6765\u7684\u94c1\u8fbe\u4eba\u4e0e\u77f3\u5e73\uff0c\u4ece\u4ed6\u4eec\u5934\u53d1\u672b\u68a2\u4e00 \u76f4\u5230\u811a\u5c16\u7684\u6bcf\u4e00\u6839\u795e\u7ecf\uff0c\u90fd\u5168\u7136\u88ab\u8fd9\u4e00\u4e2a\u7a81\u751f\u7684\u53d8\u6545\u60ca\u5f97\u5446\u4f4f\u4e86\u3002 \u3000\u3000\u4e00\u77ac\u95f4\uff0c\u5c31\u50cf\u662f\u4e00\u6ef4\u6c34\u63a5\u89e6\u5230\u5730\u9762\uff0c\u7136\u540e\u518d\u98de\u6e85\u5f00\u7684\u90a3\u4e00\u77ac\u95f4\u3002 \u3000\u3000\u4ed6\u4eec\u4e24\u4eba\u53ea\u89c9\u80f8\u808b\u4e4b\u95f4\u5fae\u5fae\u4e00\u9ebb\uff0c\u4fbf\u201c\u5657\u201d\u5730\u4e00\u58f0\uff0c\u5012\u5728\u5730\u4e0a\u3002 \u3000\u3000\u5353\u957f\u537f\u957f\u957f\u900f\u4e86\u53e3\u6c14\uff0c\u6697\u95ee\u81ea\u5df1\uff1a\u201c\u82e5\u6362\u4e86\u662f\u6211\uff0c\u6211\u80fd\u4e0d\u80fd\u907f\u5f00\u5979\u8fd9\u4e00\u62db\u7a81\u6765\u7684\u88ad \u51fb\uff1f\u201d \u3000\u3000\u4f46\u662f\u4ed6\u6ca1\u6709\u53bb\u5bfb\u6c42\u8fd9\u95ee\u9898\u7684\u89e3\u7b54\uff0c\u51fb\u4e2d\u77f3\u5e73\u540e\u843d\u4e0b\u7684\u660e\u73e0\uff0c\u843d\u5230\u5730\u4e0a\uff0c\u6b64\u523b\u6eda\u5230\u4e86\u5353\u957f \u537f\u7684\u811a\u8fb9\u3002 \u3000\u3000\u5353\u957f\u537f\u4e0b\u610f\u8bc6\u5730\u4fef\u8eab\u62fe\u8d77\u4e86\u5b83\uff0c\u4ed6\u770b\u5230\u6e29\u5982\u7389\u98de\u626c\u7684\u7ea2\u88d9\u81ea\u4ed6\u8eab\u8fb9\u98de\u8fc7\uff0c\u4ed6\u751a\u81f3\u6709\u70b9\u5e0c \u671b\u6e29\u5982\u7389\u4e5f\u7ed9\u81ea\u5df1\u6765\u4e00\u4e0b\u7a81\u672a\u7684\u88ad\u51fb\uff0c\u90a3\u4e48\u4ed6\u5c31\u80fd\u77e5\u9053\u81ea\u5df1\u65b9\u624d\u90a3\u95ee\u9898\u7684\u7b54\u6848\u4e86\u3002 \u3000\u3000\u4f46\u662f\u6e29\u5982\u7389\u6ca1\u6709\u8fd9\u6837\u505a\u3002 \u3000\u3000\u7b49\u5230\u5353\u957f\u537f\u62ac\u8d77\u5934\u6765\u7684\u65f6\u5019\uff0c\u5979\u5df2\u7aef\u7aef\u6b63\u6b63\u5730\u5750\u5728\u795e\u684c\u4e0a\u3002 \u3000\u3000\u5353\u957f\u537f\u6123\u4e86\u4e00\u6123\uff0c\u671b\u4e86\u671b\u6e29\u747e\u2014\u2014\u6e29\u747e\u5446\u5446\u5730\u7ad9\u5728\u684c\u8fb9\uff0c\u4e24\u773c\u7a7a\u865a\u5730\u51dd\u6ce8\u7740\u9752\u7070\u8272\u7684\u5730 \u9762\u3002\u7136\u540e\u4ed6\u7687\u4e86\u671b\u591a\u4e8b\u5934\u9640\u2014\u2014\u591a\u4e8b\u5934\u9640\u8d34\u5899\u800c\u7acb\uff0c\u4e00\u53cc\u8c79\u76ee\u5706\u6eda\u5730\u7741\u7740\uff0c\u671b\u5411\u6e29\u5982\u7389\uff0c\u76ee \u5149\u4e2d\u6ee1\u662f\u60ca\u5947\u4e4b\u610f\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u6697\u60f3\uff1a\u201c\u8fd9\u591a\u4e8b\u5934\u9640\u4e00\u5b9a\u662f\u521d\u6b21\u89c1\u5230\u6e29\u5982\u7389\u7684\u6b66\u529f\u3002\u201d \u3000\u3000\u4e8e\u662f\u4ed6\u53c8\u671b\u5411\u5730\u4e0a\u7684\u90a3\u4e24\u5177\u8eaf\u4f53\u2014\u2014\u94c1\u8fbe\u4eba\u4e0e\u77f3\u5e73\u90fd\u52a8\u4e5f\u4e0d\u52a8\u5730\u8737\u4f0f\u5728\u5730\u4e0a\uff0c\u5c31\u50cf\u662f\u4e24 \u5177\u5b8c\u5168\u51b7\u900f\u7684\u6b7b\u5c38\uff0c\u5353\u957f\u537f\u6697\u6697\u53f9\u606f\u4e00\u58f0\uff0c\u76ee\u5149\u56de\u5230\u81ea\u5df1\u7684\u773c\u775b\u3002 \u3000\u3000\u73e0\u5149\u5f88\u4eae\uff0c\u4ed6\u4f3c\u4e4e\u80fd\u5728\u8fd9\u7c92\u660e\u73e0\u91cc\u770b\u5230\u4ed6\u81ea\u5df1\u624b\u4e0a\u7684\u660e\u73e0\u3002 \u3000\u3000\u7136\u540e\uff0c\u4ed6\u7f13\u7f13\u5c06\u8fd9\u7c92\u660e\u73e0\u653e\u5728\u6e29\u5982\u7389\u5750\u7740\u7684\u90a3\u5f20\u795e\u684c\u4e0a\uff0c\u4ed6\u6781\u529b\u7684\u4e0d\u60f3\u62ac\u8d77\u81ea\u5df1\u7684\u773c \u775b\uff0c\u4f46\u662f\u4ed6\u4e0d\u80fd\uff0c\u4ed6\u7ec8\u4e8e\u62ac\u8d77\u4e86\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u53d1\u89c9\u6e29\u5982\u7389\u4e5f\u5728\u671b\u7740\u4ed6\u3002 \u3000\u3000\u9762\u5bf9\u4ed6\u7684\u662f\u4ed6\u4e0d\u5171\u6234\u5929\u7684\u4ec7\u4eba\uff0c\u4f46\u5947\u602a\u7684\u662f\uff0c\u4ed6\u6b64\u523b\u7adf\u4e0d\u77e5\u8be5\u600e\u4e48\u597d\uff0c\u4ed6\u60f3\u8d77\u4e86\u90a3\u5929\u81ea \u5df1\u4e0e\u6e29\u5982\u7389\u6240\u8ba2\u4e0b\u7684\u8a93\u7ea6\uff0c\u4ed6\u4e8e\u54b3\u4e86\u4e00\u58f0\uff0c\u56de\u8f6c\u5934\u53bb\uff0c\u53ea\u542c\u6e29\u5982\u7389\u5df2\u81ea\u51b7\u51b7\u7684\u8bf4\u9053\uff1a\u201c\u4f60\u4e5f \u6765\u4e86\uff0c\u5f88\u597d\u3002\u201d \u3000\u3000\u5979\u8bed\u6c14\u4e2d\u5c31\u751f\u50cf\u662f\u76f4\u5230\u6b64\u523b\u624d\u53d1\u89c9\u5230\u5353\u957f\u537f\u7684\u5b58\u5728\u4f3c\u7684\uff0c\u5353\u957f\u537f\u5934\u4e5f\u4e0d\u56de\uff0c\u4e5f\u751f\u50cf\u662f\u6839 \u672c\u6ca1\u6709\u542c\u5230\u5979\u7684\u8bdd\u3002 \u3000\u3000\u591a\u4e8b\u5934\u9640\u4e00\u6123\uff0c\u4ed6\u867d\u4e0d\u4e86\u89e3\u5979\u8bdd\u4e2d\u7684\u542b\u610f\uff0c\u4f46\u4ecd\u76f4\u7387\u5730\u7b54\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u4e9b\u90fd\u662f\u9a97\u4eba\u7684 \u9b3c\u8bdd\uff0c\u5c11\u6797\u4e00\u6d3e\u81ea\u8fbe\u6469\u7956\u5e08\u521b\u7acb\u5230\u73b0\u5728\u2014\u2014\u201d\u6e29\u52a0\u7389\u5fae\u5fae\u4e00\u7b11\uff0c\u63a5\u53e3\u9053\uff1a\u201c\u5c11\u6797\u4e00\u6d3e\uff0c\u540d\u626c \u5929\u4e0b\uff0c\u5c11\u6797\u6d3e\u7684\u5386\u53f2\uff0c\u6211\u65e9\u5df2\u77e5\u9053\u4e86\u3002\u201d \u3000\u3000\u591a\u4e8b\u5934\u9640\u4e00\u6123\uff0c\u5728\u8fd9\u540d\u95fb\u5929\u4e0b\u7684\u5973\u9b54\u5934\u7684\u9762\u524d\uff0c\u4ed6\u5ffd\u7136\u6709\u4e86\u4e00\u79cd\u7f1a\u624b\u7f1a\u811a\u7684\u611f\u89c9\uff0c\u4ed6\u53ea \u5f97\u95ed\u8d77\u5634\u5df4\uff0c\u4e0d\u518d\u8bf4\u8bdd\u3002 \u3000\u3000\u4f46\u6e29\u5982\u7389\u5374\u53c8\u63a5\u9053\uff1a\u201c\u5927\u5e08\u4f60\u8eab\u5f3a\u9aa8\u58ee\uff0c\u4e00\u773c\u671b\u53bb\uff0c\u77e5\u9053\u4f60\u7684\u5916\u5bb6\u529f\u592b\u5df2\u6709\u975e\u51e1\u7684\u6210 \u5c31\uff0c\u4f46\u662f\u5c11\u6797\u4e00\u5411\u5185\u5916\u517c\u4fee\uff0c\u5927\u5e08\u4f60\u5916\u529f\u65e2\u5df2\u5982\u6b64\uff0c\u5185\u5bb6\u529f\u592b\u60f3\u4e5f\u4e0d\u4f1a\u5dee\u5230\u54ea\u53bb\u4e86\uff0c\u662f \u5417\uff1f\u201d \u3000\u3000\u5728\u6b64\u65f6\u6b64\u523b\u5979\u7adf\u7a81\u7136\u95ee\u8d77\u8fd9\u4e9b\u8bdd\u6765\u4e86\uff0c\u4e0d\u4f46\u591a\u4e8b\u5934\u9640\u5fc3\u91cc\u5947\u602a\uff0c\u5353\u957f\u537f\u3001\u6e29\u747e\u5fc3\u91cc\u5947 \u602a\uff0c\u5c31\u8fde\u90a3\u5df2\u88ab\u6e29\u5982\u7389\u70b9\u4f4f\u91cd\u7a74\uff0c\u5468\u8eab\u4e0d\u80fd\u52a8\u5f39\uff0c\u4f46\u4ecd\u542c\u5f97\u89c1\u8bdd\u58f0\u7684\u94c1\u8fbe\u4eba\u4e0e\u77f3\u5e73\u5fc3\u91cc\u4e5f\u5728 \u5947\u602a\u3002 \u3000\u3000\u53ea\u542c\u591a\u4e8b\u5934\u9640\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u6d12\u5bb6\u2026\u2026\u6211\u81ea\u5e7c\u7ec3\u6b66\u5c31----\u201d\u6e29\u5982\u7389\u53c8\u81ea\u63a5\u53e3\u9053\uff1a\u201c\u5927 \u5e08\u4f60\u4e0d\u8bf4\u6211\u4e5f\u77e5\u9053\uff0c\u4f60\u5185\u5bb6\u529f\u592b\u4e00\u5b9a\u4e0d\u9519\uff0c\u5bf9\u70b9\u7a74\u4e00\u9053\uff0c\u4f60\u5927\u7ea6\u4e5f\u4e0d\u4f1a\u4e0d\u77e5\u9053\u4e86\uff0c\u662f\u5417\uff1f\u201d \u3000\u3000\u5979\u867d\u7136\u6bcf\u53e5\u90fd\u5728\u95ee\u8bdd\uff0c\u4f46\u5374\u6c38\u8fdc\u4e0d\u7b49\u522b\u4eba\u8bf4\u5b8c\u5c31\u5148\u5df2\u66ff\u522b\u4eba\u7b54\u4e86\uff0c\u56e0\u4e4b\u591a\u4e8b\u5934\u9640\u6b64\u523b\u4e5f \u53ea\u201c\u55ef\u201d\u4e86\u4e00\u58f0\uff0c\u5fae\u5fae\u9894\u9996\uff0c\u4e5f\u4e0d\u518d\u8bf4\u8bdd\u3002 \u3000\u3000\u6e29\u5982\u7389\u51b7\u51b7\u53c8\u9053\uff0c\u201c\u90a3\u4e48\u5c31\u8bf7\u5927\u5e08\u4f60\u5c06\u5de6\u9762\u90a3\u5c11\u5e74\u7684\u7a74\u9053\u7acb\u523b\u89e3\u5f00\uff0c\u8fd9\u70b9\u60f3\u5fc5\u5927\u5e08\u4e00\u5b9a \u80fd\u505a\u5f97\u5230\u4e86\uff0c\u662f\u5417\uff1f\u201d \u3000\u3000\u591a\u4e8b\u5934\u9640\u53c8\u6123\u4e86\u4e00\u6123\uff0c\u4ed6\u5b9e\u5728\u4e0d\u77e5\u9053\u8fd9\u5973\u9b54\u5934\u5728\u5f04\u4ec0\u4e48\u7384\u865a\uff0c\u4f46\u4ed6\u7ec8\u4e8e\u5c06\u624b\u4e2d\u7684\u65b9\u4fbf\u94f2 \u501a\u5728\u5899\u4e0a\uff0c\u8d70\u5230\u94c1\u8fbe\u4eba\u8eab\u4fa7\uff0c\u4e00\u628a\u5c06\u8fd9\u8eaf\u4f53\u5df2\u8f6f\u5f97\u6709\u5982\u4e00\u56e2\u68c9\u82b1\u4f3c\u7684\u5c11\u5e74\u4ece\u5730\u4e0a\u62c9\u8d77\uff0c\u4f38\u51fa \u84b2\u638c\u5927\u7684\u5de8\u638c\u201c\u556a\u201d\u5728\u4ed6\u8eab\u4e0a\u91cd\u91cd\u62cd\u4e86\u4e00\u638c\uff0c\u53c8\u5728\u4ed6\u808b\u4e0b\u8170\u7554\u63c9\u4e86\u4e24\u4e0b\uff0c\u8981\u77e5\u9053\u5c11\u6797\u6d3e\u6b66\u529f \u80fd\u4ee5\u540d\u626c\u5929\u4e0b\u7531\u6765\u6709\u65e5\uff0c\u5c11\u6797\u5f1f\u5b50\u7684\u786e\u4ff1\u662f\u5185\u5916\u517c\u4fee\u7684\u9ad8\u624b\uff0c\u8fd9\u591a\u4e8b\u5934\u9640\u5728\u4f38\u624b\u4e4b\u95f4\uff0c\u679c\u7136 \u5df2\u6beb\u65e0\u56f0\u96be\u5730\u89e3\u5f00\u4e86\u94c1\u8fbe\u4eba\u7684\u7a74\u9053\uff0c\u4ed6\u5de8\u638c\u4e00\u63a8\uff0c\u5c06\u94c1\u8fbe\u4eba\u63a8\u53bb\u6570\u6b65\uff0c\u9000\u56de\u5899\u8fb9\uff0c\u5bf9\u4e8e\u8fd9\u61e6 \u592b\u822c\u7684\u5c11\u5e74\uff0c\u4ed6\u5fc3\u4e2d\u5b9e\u5728\u8ba8\u538c\u5f97\u5f88\u3002 \u3000\u3000\u94c1\u8fbe\u4eba\u51b2\u51fa\u4e24\u6b65\uff0c\u7ad9\u7a33\u8eab\u5f62\uff0c\u65b9\u81ea\u201c\u54b3\u2018\u5730\u4e00\u58f0\uff0c\u5410\u51fa\u4e00\u53e3\u6d53\u75f0\uff0c\u4ed6\u832b\u7136\u5730\u671b\u4e86\u6e29\u5982\u7389 \u4e00\u773c\uff0c\u53c8\u7acb\u523b\u5782\u4e0b\u5934\u53bb\uff0c\u5fc3\u91cc\u5374\u5728\u5947\u602a\uff1a\u201c\u8fd9\u4e11\u4eba\u6e29\u5982\u7389\u65b9\u81ea\u70b9\u4e86\u6211\u7684\u7a74\u9053\uff0c\u6b64\u523b\u53c8\u53eb\u4eba\u66ff \u6211\u89e3\u5f00\u4f5c\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u800c\u4e11\u4eba\u6e29\u5982\u7389\u6b64\u523b\u7684\u76ee\u5149\uff0c\u5c31\u50cf\u662f\u4e00\u4e2a\u6ee1\u8db3\u7684\u730e\u4eba\u5728\u6b23\u8d4f\u7740\u5979\u7684\u730e\u83b7\u7269\u4f3c\u7684\uff0c\u4e00\u5206\u4e00\u5bf8 \u5730\u671b\u7740\u8fd9\u5782\u7740\u5934\u7684\u94c1\u8fbe\u4eba\u3002 \u3000\u3000\u5979\u5ffd\u7136\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u5927\u7ea6\u4e5f\u4f1a\u70b9\u7a74\u548c\u89e3\u7a74\u4e86\uff1f\u201d \u3000\u3000\u94c1\u8fbe\u4eba\u4ecd\u7136\u5782\u7740\u5934\uff0c\u6ca1\u6709\u7b54\u590d\uff0c\u56e0\u4e3a\u5979\u6839\u672c\u4e0d\u9700\u8981\u522b\u4eba\u7684\u7b54\u590d\uff0c\u5979\u53ea\u662f\u51b7\u7b11\u7740\u63a5\u53e3\u53c8 \u9053\uff1a\u201c\u8eba\u5728\u5730\u4e0a\u7684\u90a3\u53ea\u8001\u9f20\u662f\u4f60\u7684\u5e08\u5f1f\u5427\uff1f\u201d \u3000\u3000\u94c1\u8fbe\u4eba\u6124\u6012\u5730\u62ac\u8d77\u5934\uff0c\u4f46\u5934\u53ea\u62ac\u5230\u4e00\u534a\uff0c\u53c8\u7acb\u523b\u5782\u4e0b\u3002 \u3000\u3000\u6e29\u5982\u7389\u51b7\u51b7\u53c8\u9053\uff1a\u201c\u4f60\u73b0\u5728\u56de\u8f6c\u8eab\u53bb\uff0c\u628a\u4f60\u7684\u5e08\u5f1f\u4ece\u5730\u4e0a\u62c9\u8d77\u6765\uff0c\u66ff\u4ed6\u89e3\u5f00\u7a74\u9053\u3002\u201d \u3000\u3000\u94c1\u8fbe\u4eba\u731c\u7591\u7740\u3001\u72b9\u8c6b\u7740\uff0c\u4f46\u7ec8\u4e8e\u8f6c\u8eab\uff0c\u50cf\u591a\u4e8b\u5934\u9640\u4e3a\u4ed6\u89e3\u7a74\u65f6\u4e00\u6837\u5730\u4e3a\u4ed6\u5e08\u5f1f\u89e3\u5f00\u4e86\u7a74 \u9053\uff0c\u751a\u81f3\u6bd4\u591a\u4e8b\u5934\u9640\u8fd8\u5feb\u4e9b\u3002 \u3000\u3000\u6e29\u5982\u7389\u51b7\u201c\u54fc\u201d\u4e00\u58f0\uff0c\u56de\u8f6c\u5934\u53bb\uff0c\u518d\u4e5f\u4e0d\u671b\u8fd9\u5e08\u5144\u5f1f\u4e24\u4eba\u4e00\u773c\u3002 \u3000\u3000\u94c1\u8fbe\u4eba\u3001\u77f3\u5e73\u4e24\u4eba\u50cf\u5446\u5b50\u4e00\u6837\u5730\u6123\u5728\u90a3\u91cc\uff0c\u8fdb\u4ea6\u4e0d\u662f\uff0c\u9000\u4ea6\u4e0d\u662f\uff0c\u4ed6\u4eec\u53ef\u601c\u5730\u4ea4\u6362\u7740\u773c \u8272\uff0c\u5e0c\u671b\u5bf9\u65b9\u80fd\u544a\u8bc9\u81ea\u5df1\u8fd9\u5973\u9b54\u5934\u6b64\u523b\u7a76\u7adf\u662f\u4f55\u7528\u610f\uff0c\u4f46\u4ed6\u4eec\u5f7c\u6b64\u95f4\u7684\u76ee\u5149\u5374\u90fd\u662f\u4e00\u6837\u2014\u2014 \u832b\u7136\u800c\u65e0\u52a9\u3002 \u3000\u3000\u53c8\u662f\u4e00\u9635\u96be\u582a\u7684\u6c89\u9ed8\u3002 \u3000\u3000\u5927\u5bb6\u4f3c\u4e4e\u90fd\u5728\u7b49\u5f85\u7740\u6e29\u5982\u7389\u5f00\u53e3\uff0c\u53ea\u6709\u5353\u957f\u537f\u5728\u6697\u4e2d\u53ef\u601c\u8fd9\u4e24\u4e2a\u5c11\u5e74\uff0c\u4f46\u662f\uff0c\u6e29\u5982\u7389\u7ec8 \u4e8e\u5f00\u53e3\u4e86\u3002 \u3000\u3000\u5979\u50cf\u662f\u5728\u81ea\u8a00\u81ea\u8bed\uff1a\u201c\u6709\u4e9b\u4eba\u649e\u5728\u6211\u8eab\u91cc\uff0c\u4ece\u6765\u6ca1\u6709\u6d3b\u547d\uff0c\u7acb\u523b\u4fbf\u662f\u5c38\u6a2a\u6e85\u8840\uff0c\u6709\u4e9b\u8fd0 \u6c14\u5374\u597d\u4e9b\uff0c\u4ed6\u4eec\u81f3\u5c11\u8fd8\u6709\u4e03\u4e03\u56db\u5341\u4e5d\u4e2a\u65f6\u8fb0\u597d\u6599\u7406\u540e\u4e8b\uff0c\u800c\u4e14\u2014\u2014\u54fc\u54fc\uff0c\u5047\u5982\u4ed6\u4eec\u806a\u660e\u4e9b\uff0c \u8fd8\u53ef\u4ee5\u4e0d\u6b7b\u3002\u201d \u3000\u3000\u4f17\u4eba\u53c8\u81ea\u4e00\u6123\u3002 \u3000\u3000\u5353\u957f\u537f\u5251\u7709\u4e00\u8f69\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u4f60\u8bf4\u7684\u2014\u2014\u201d\u6e29\u5982\u7389\u76ee\u5149\u4e00\u8f6c\uff0c\u50cf\u5229\u5251\u822c\u626b\u4e86\u5353\u957f\u537f\u4e00 \u773c\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u542c\u8fc7\u5728\u6b66\u6797\u4e2d\u7edd\u4f20\u5df2\u6709\u767e\u4f59\u5e74\u7684\u4e03\u7edd\u91cd\u624b\u8fd9\u79cd\u529f\u592b\u5417\uff1f\u201d \u3000\u3000\u5353\u957f\u537f\u5fc3\u5934\u4e00\u9707\uff0c\u76ee\u5149\u8f6c\u5904\uff0c\u5374\u89c1\u90a3\u591a\u4e8b\u5934\u9640\u9762\u8272\u5df2\u53d8\uff0c\u94c1\u8fbe\u4eba\u3001\u77f3\u5e73\u4e24\u4eba\u4ea6\u662f\u9762\u5982\u6b7b \u7070\u3002\u6e29\u5982\u7389\u51b7\u51b7\u53c8\u9053\uff1a\u201c\u4e2d\u4e86\u4e03\u7edd\u91cd\u624b\u4e4b\u4eba\uff0c\u5f53\u65f6\u867d\u53ef\u4e0d\u6b7b\uff0c\u800c\u4e14\u770b\u6765\u6beb\u65e0\u5f02\u72b6\uff0c\u4f46\u4e03\u4e03\u56db \u5341\u4e5d\u4e2a\u65f6\u8fb0\u4e4b\u540e\uff0c\u7acb\u65f6\u4f7f\u5f97\u72c2\u55b7\u9c9c\u8840\u800c\u6b7b\uff0c\u800c\u4e14\u2014\u2014\u54fc\u54fc\uff0c\u6b7b\u65f6\u7684\u90a3\u79cd\u75db\u82e6\uff0c\u4fbf\u662f\u795e\u4ed9\u4e5f\u96be \u5fcd\u53d7\u3002\u201d \u3000\u3000\u5979\u7f13\u7f13\u8f6c\u8fc7\u76ee\u5149\u9053\uff1a\u201c\u6709\u4e9b\u4e2d\u4e86\u4e03\u7edd\u91cd\u624b\u7684\u4eba\uff0c\u5f53\u65f6\u7a74\u9053\u867d\u7136\u80fd\u88ab\u522b\u4eba\u89e3\u5f00\uff0c\u4ed6\u4eec\u4e5f\u4e0d \u4f1a\u81ea\u89c9\u81ea\u5df1\u662f\u4e2d\u4e86\u4e03\u7edd\u91cd\u624b\uff0c\u9664\u975e\u4ed6\u4eec\u80fd\u5728\u81ea\u5df1\u7684\u9888\u540e\u9aa8\u8282\uff0c\u810a\u4e0b\u7b2c\u4e03\u8282\u9aa8\u690e\u3001\u4e24\u808b\u3001\u4e24 \u819d\uff0c\u4ee5\u53ca\u2014\u2014\u54fc\u54fc\uff0c\u9f20\u8c41\u7a74\u4e0b\u90fd\u6478\u4e0a\u4e00\u6478\uff0c\u90a3\u4e48\u2026\u2026\u201d \u3000\u3000\u5979\u8bed\u58f0\u751f\u51b7\u800c\u7f13\u6162\uff0c\u4f46\u89c1\u5979\u4e00\u9762\u8bf4\u7740\uff0c\u90a3\u94c1\u8fbe\u4eba\u4e0e\u77f3\u5e73\u5c31\u90fd\u4e00\u9762\u5267\u70c8\u7684\u98a4\u6296\u7740\uff0c\u5f53\u5979\u8bf4 \u5230\u201c\u2026\u2026\u9664\u975e\u4ed6\u4eec\u80fd\u5728\u81ea\u5df1\u7684\u9876\u540e\u2026\u2026\u201d\u94c1\u8fbe\u4eba\u4e0e\u77f3\u5e73\u7684\u624b\u638c\u5c31\u7acb\u523b\u6478\u5230\u9888\u540e\uff0c\u5f53\u5979\u8bf4\u5230 \u201c\u810a\u4e0b\u7b2c\u4e03\u8282\u9aa8\u690e\u2026\u2026\u201d\u51e0\u4e4e\u50cf\u9b54\u672f\u4e00\u6837\uff0c\u94c1\u8fbe\u4eba\u4e0e\u77f3\u5e73\u7684\u624b\u638c\uff0c\u4e5f\u7acb\u523b\u6478\u5230\u81ea\u5df1\u7684\u810a\u4e0b\u7684 \u7b2c\u4e03\u8282\u9aa8\u690e\u2026\u2026 \u3000\u3000\u7b49\u5979\u8bdd\u8bf4\u5b8c\u4e86\uff0c\u94c1\u8fbe\u4eba\u4e0e\u77f3\u5e73\u7684\u9762\u5bb9\uff0c\u5df2\u50cf\u662f\u4e00\u5757\u88ab\u5c60\u5200\u5207\u4e0b\u7684\u8e44\u8180\u4f3c\u7684\u626d\u66f2\u4e86\u8d77\u6765\uff0c \u4ed6\u4eec\u77e5\u9053\u81ea\u5df1\u5df2\u88ab\u4eba\u70b9\u4e86\u4e03\u7edd\u91cd\u624b\uff0c\u56e0\u4e3a\u8fd9\u4e00\u79cd\u6b66\u6797\u4e2d\u4eba\u95fb\u4e4b\u8272\u53d8\u7684\u6b66\u529f\uff0c\u867d\u7136\u7edd\u4f20\u5df2\u4e45\uff0c \u4f46\u4ed6\u4eec\u5374\u4e5f\u542c\u4eba\u8bf4\u8fc7\uff0c\u77e5\u9053\u51e1\u662f\u8eab\u4e2d\u4e03\u7edd\u91cd\u624b\u7684\u4eba\uff0c\u8868\u9762\u4e00\u65e0\u5f81\u5146\uff0c\u4f46\u8eab\u4e0a\u5374\u6709\u4e03\u5904\u9aa8\u8282\u624b \u6307\u4e00\u6478\u4fbf\u9690\u9690\u53d1\u75db\u3002 \u3000\u3000\u4ed6\u4eec\u8eab\u4e0a\u7684\u8fd9\u4e03\u5904\u5730\u65b9\uff0c\u6b63\u5982\u4f20\u8a00\u4e2d\u4e00\u6837\uff0c\u5f53\u4ed6\u4eec\u6478\u5230\u90a3\u5730\u65b9\u7684\u65f6\u5019\uff0c\u4fbf\u6709\u4e00\u9635\u75bc\u75db\uff0c \u75bc\u75db\u867d\u8f7b\u5fae\uff0c\u4f46\u5374\u4e00\u76f4\u75db\u5230\u4ed6\u4eec\u5fc3\u91cc\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u4eec\u6df1\u77e5\u4e2d\u4e86\u4e03\u7edd\u91cd\u624b\u7684\u4eba\u6b7b\u72b6\u4e4b\u60e8\uff0c\u4e5f\u6df1\u77e5\u8fd9\u4e03\u7edd\u91cd\u624b\u5f53\u4eca\u5929\u4e0b\u8fd8\u65e0\u4e00\u80fd\u591f\u89e3 \u6551\u3002 \u3000\u3000\u73e0\u5149\u662f\u67d4\u548c\u7684\uff0c\u4f46\u5374\u6709\u79cd\u96be\u8a00\u7684\u9752\u7070\u8272\u3002 \u3000\u3000\u9752\u7070\u8272\u7684\u73e0\u5149\u6620\u5728\u56db\u5468\u9752\u7070\u8272\u7684\u5899\u58c1\u4e0a\uff0c\u6620\u7740\u90a3\u6ee1\u5e03\u7070\u5c18\u7684\u7a97\u7eb8\uff0c\u6620\u7740\u90a3\u9edd\u9ed1\u800c\u7a7a\u6d1e\u7684 \u95e8\u6237\uff0c\u6620\u7740\u90a3\u5982\u610f\u65b9\u4fbf\u94f2\u96ea\u4eae\u9634\u68ee\u7684\u94f2\u5934\uff0c\u6620\u7740\u90a3\u4e11\u4eba\u6e29\u5982\u7389\u5fae\u5e26\u72de\u7b11\u7684\u9762\u5bb9\u2026\u2026 \u3000\u3000\u201c\u5657\u201d\u7684\u4e00\u58f0\uff0c\u77f3\u5e73\u5fcd\u4e0d\u4f4f\u8dea\u4e86\u4e0b\u53bb\uff1a\u201c\u6211\u2026\u2026\u665a\u8f88\u662f\u2026\u2026\u662f\u2026\u2026\u201d \u3000\u3000\u6e29\u5982\u7389\u8f7b\u8511\u5730\u51b7\u7b11\u4e00\u4e0b\uff1a\u201c\u4f60\u662f\u806a\u660e\u7684\uff0c\u662f\u5417\uff1f\u201d \u3000\u3000\u77f3\u5e73\u5782\u4e0b\u5934\uff0c\u4ed6\u8fd8\u5e74\u8f7b\uff0c\u4ed6\u4e0d\u613f\u610f\u6b7b\uff0c\u4ed6\u54c0\u6c42\uff0c\u54c0\u6c42\u867d\u7136\u53ef\u803b\uff0c\u4f46\u5728\u4ed6\u773c\u4e2d\u770b\u6765\uff0c\u5374\u8fd8 \u6bd4\u201c\u6b7b\u4ea1\u201d\u8981\u597d\u5f97\u591a\u3002 \u3000\u3000\u5353\u957f\u537f\u56de\u8f6c\u5934\u53bb\uff0c\u4ed6\u4e0d\u613f\u770b\u5230\u8fd9\u5c11\u5e74\u8fd9\u79cd\u6837\u5b50\uff0c\u56e0\u4e3a\u4ed6\u6c38\u8fdc\u4e0d\u4f1a\u54c0\u6c42\uff0c\u5bf9\u8fd9\u602f\u61e6\u7684\u5c11 \u5e74\uff0c\u4ed6\u6709\u4e9b\u8f7b\u8511\uff0c\u4e5f\u6709\u4e9b\u601c\u60af\uff0c\u82e5\u662f\u6362\u4e86\u4e00\u4e9b\u4eba\uff0c\u82e5\u662f\u6362\u4e86\u4e00\u5904\u6240\u5728\uff0c\u4ed6\u6216\u8bb8\u4f1a\u4f38\u624b\u76f8\u52a9\uff0c \u4f46\u73b0\u5728\uff0c\u4ed6\u53ea\u5f97\u6697\u4e2d\u957f\u53f9\uff0c\u4ed6\u4e5f\u65e0\u80fd\u4e3a\u529b\uff0c\u4f55\u51b5\u5373\u4f7f\u4ed6\u6709\u529b\u91cf\uff0c\u4ed6\u4e5f\u672a\u89c1\u4f1a\u4f38\u624b\u3002 \u3000\u3000\u53c8\u662f\u201c\u5657\u201d\u7684\u4e00\u58f0\u3002 \u3000\u3000\u4ed6\u4e0d\u7528\u56de\u5934\uff0c\u5c31\u77e5\u9053\u53e6\u4e00\u4e2a\u5c11\u5e74\u4e5f\u8dea\u4e86\u4e0b\u53bb\uff0c\u53ea\u542c\u6e29\u5982\u7389\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u539f\u6765\u4f60\u4e5f\u4e0d\u7b28\uff0c \u77e5\u9053\u6b7b\u4e0d\u662f\u597d\u4e8b\u3002\u201d \u3000\u3000\u591a\u4e8b\u5934\u9640\u6d53\u5507\u4e00\u8f69\uff0c\u201c\u5484\u201d\u5730\u5410\u4e86\u4e00\u53e3\u957f\u6c14\uff0c\u63d0\u8d77\u65b9\u4fbf\u94f2\uff0c\u5927\u6b65\u8d70\u4e86\u51fa\u53bb\uff0c\u5934\u4e5f\u4e0d\u8f6c\uff0c \u4ed6\u4e0d\u806a\u660e\uff0c\u56e0\u4e3a\u4ed6\u5b81\u613f\u6b7b\u4e5f\u4e0d\u613f\u53d7\u5230\u8fd9\u79cd\u5c48\u8fb1\uff0c\u5bf9\u8fd9\u79cd\u5c48\u8fb1\uff0c\u4ed6\u751a\u81f3\u8fde\u770b\u90fd\u4e0d\u613f\u770b\u4e00\u773c\uff0c\u53ef \u662f\uff0c\u4e16\u4e0a\u50cf\u4ed6\u8fd9\u79cd\u4e0d\u806a\u660e\u7684\u4eba\u82e5\u662f\u591a\u4e00\u4e9b\uff0c\u90a3\u4e48\u8fd9\u4e16\u754c\u4fbf\u4e5f\u8bb8\u4f1a\u5149\u660e\u5f97\u591a\uff0c\u4e0d\u806a\u660e\u7684\u4eba\u4f60\u8bf4 \u662f\u5417\uff1f \u3000\u3000\u6e29\u5982\u7389\u8f7b\u8511\u5730\u51b7\u7b11\u7740\uff0c\u7f13\u7f13\u4f38\u624b\u5165\u6000\uff0c\u638f\u51fa\u4e00\u5305\u6de1\u7ea2\u8272\u7684\u7eb8\u5305\u6765\uff0c\u968f\u624b\u629b\u5728\u5730\u4e0a\uff0c\u51b7\u51b7 \u9053\uff1a\u201c\u8fd9\u5305\u91cc\u7684\u836f\u65e0\u8272\u65e0\u5473\uff0c\u968f\u4fbf\u653e\u5728\u8336\u91cc\u3001\u9152\u91cc\u3001\u6c64\u91cc\u90fd\u53ef\u4ee5\uff0c\u800c\u4e14--\u4e00\u5047\u5982\u5f92\u5f1f\u628a\u8fd9\u836f \u7ed9\u5e08\u7236\u5403\uff0c\u90a3\u4e48\u505a\u5e08\u7236\u7684\u66f4\u4e0d\u4f1a\u53d1\u89c9\u3002\u201d\u5979\u51b7\u7b11\u4e00\u58f0\uff0c\u63a5\u9053\uff1a\u201c\u4f60\u4eec\u77e5\u9053\u6211\u7684\u610f\u601d\u5417\uff1f\u201d \u3000\u3000\u94c1\u8fbe\u4eba\u4e0e\u77f3\u5e73\u8eab\u4e0a\u7684\u98a4\u6296\u66f4\u663e\u660e\u4e86\uff0c\u4ed6\u4eec\u7684\u773c\u775b\u671b\u7740\u8fd9\u5305\u6de1\u7ea2\u7684\u7eb8\u5305\uff0c\u5fc3\u5934\u5728\u6026\u6026\u5730\u8df3 \u52a8\u7740\u3002 \u3000\u3000\u751f\u547d\uff0c\u751f\u547d\u2026\u2026 \u3000\u3000\u751f\u547d\u6c38\u8fdc\u662f\u7f8e\u597d\u7684\u2014\u2014\u4ed6\u4eec\u5fc3\u5934\u7684\u8df3\u52a8\u66f4\u5267\u70c8\u4e86\u3002 \u3000\u3000\u9009\u62e9\uff01 \u3000\u3000\u81ea\u5df1\u7684\u751f\u547d\u8fd8\u662f\u5e08\u7236\u7684\u751f\u547d\uff1f \u3000\u3000\u5f31\u8005\u6c38\u8fdc\u662f\u5f31\u8005\uff0c\u61e6\u592b\u6c38\u8fdc\u662f\u61e6\u592b\uff0c\u4e07\u5999\u771f\u541b\u5e94\u8be5\u540e\u6094\uff0c\u56e0\u4e3a\u4ed6\u4f20\u6388\u7ed9\u4ed6\u5f92\u5f1f\u7684\uff0c\u662f\u51b7 \u9177\u7684\u6559\u8bad\uff0c\u800c\u51b7\u8840\u7684\u6559\u8bad\u6c38\u8fdc\u53ea\u5b58\u4e00\u4e2a\u9009\u62e9\uff1a\u201c\u522b\u4eba\u7684\u6027\u547d\uff0c\u603b\u4e0d\u4f1a\u6bd4\u81ea\u5df1\u7684\u751f\u547d\u7f8e\u597d\uff01\u201d \u3000\u3000\u94c1\u8fbe\u4eba\u3001\u77f3\u5e73\u4e00\u8d77\u7f13\u7f13\u4f38\u51fa\u624b\uff0c\u94c1\u8fbe\u4eba\u62a2\u5148\u4e00\u6b65\uff0c\u89e6\u5230\u7eb8\u5305\uff0c\u7136\u540e\u4ed6\u624b\u6307\u8f7b\u5fae\u5730\u98a4\u6296\u4e00 \u4e0b\uff0c\u5c06\u7eb8\u5305\u62e8\u5230\u77f3\u5e73\u7684\u624b\u6307\u4e0b\u3002 \u3000\u3000\u6e29\u5982\u7389\u8f7b\u8511\u5730\u5927\u7b11\u8d77\u6765\uff1a\u201c\u6211\u77e5\u9053\u4f60\u4eec\u662f\u806a\u660e\u4eba\u3002\u201d \u3000\u3000\u5979\u5927\u7b11\u7740\uff1a\u201c\u6709\u4e9b\u4eba\u5929\u751f\u662f\u806a\u660e\u4eba\uff0c\u8fd9\u7eb8\u5305\u62ff\u53bb\uff0c\u5341\u4e8c\u4e2a\u65f6\u8fb0\u4e4b\u5185\uff0c\u628a\u5b83\u9001\u5217\u4f60\u4eec\u5e08\u7236 \u7684\u8179\u91cc\uff0c\u4e0d\u7ba1\u7528\u4ec0\u4e48\u65b9\u6cd5\uff0c\u7136\u540e\u2014\u2014\u4f60\u4eec\u7684\u547d\u5c31\u6361\u56de\u6765\u4e86\u3002\u201d \u3000\u3000\u5979\u7b11\u58f0\u4e00\u987f\uff0c\u9762\u5bb9\u7a81\u7136\u53d8\u5f97\u5f02\u6837\u7684\u751f\u6c14\uff1a\u201c\u53ef\u662f\uff0c\u73b0\u5728\u4f60\u4eec\u5feb\u6eda\uff01\u5feb\u6eda\uff01\u201d \u3000\u3000\u5979\u5feb\u8fc5\u5730\u6325\u51fa\u90a3\u592a\u5bbd\u7684\u8863\u8896\u548c\u592a\u7626\u7684\u624b\u81c2\uff1a\u201c\u5feb\u6eda\uff01\u5feb\u6eda\uff01\u201d \u3000\u3000\u5979\u91cd\u590d\u5730\u53f1\u559d\u7740\uff0c\u94c1\u8fbe\u4eba\u548c\u77f3\u5e73\u4fbf\u50cf\u662f\u4e24\u53ea\u53d7\u4e86\u60ca\u7684\u5154\u5b50\uff0c\u4ece\u5730\u4e0a\u8df3\u8d77\u6765\uff0c\u62e7\u8eab\u63a0\u4e86\u51fa \u53bb\uff0c\u7728\u773c\u4fbf\u6d88\u5931\u5728\u95e8\u5916\u7684\u591c\u8272\u4e2d\u3002 \u3000\u3000\u6e29\u5982\u7389\u51b7\u54fc\u4e00\u58f0\uff0c\u54fa\u54fa\u81ea\u8bed\uff1a\u201c\u806a\u660e\u4eba\uff0c\u806a\u660e\u4eba\u2014\u2014\u54fc\uff01\u201d \u3000\u3000\u7a81\u7136\u8f6c\u8eab\u671b\u7740\u6e29\u747e\uff0c\u201c\u747e\u513f\uff0c\u4f60\u53bb\u8ddf\u7740\u90a3\u4e24\u4e2a\u61e6\u592b\uff0c\u770b\u770b\u4ed6\u4eec\u5230\u54ea\u91cc\u53bb\u4e86\uff0c\u597d\u5417\uff1f\u201d \u3000\u3000\u5f88\u5947\u602a\uff0c\u60ef\u4e8e\u53d1\u4ee4\u7684\u4eba\uff0c\u5374\u6c38\u8fdc\u559c\u6b22\u6545\u610f\u5f81\u6c42\u522b\u4eba\u7684\u610f\u89c1\uff0c\u800c\u5374\u53c8\u8ba9\u4eba\u6c38\u8fdc\u6ca1\u6709\u9009\u62e9\u7684 \u4f59\u5730\u3002 \u3000\u3000\u6e29\u747e\u7565\u4e3a\u8fdf\u7591\u4e86\u4e00\u4e0b\uff0c\u800c\u5979\u660e\u4eae\u800c\u5fe7\u90c1\u7684\u773c\u6ce2\uff0c\u5728\u5730\u4e0a\u7684\u767d\u6728\u7075\u4f4d\u548c\u5353\u957f\u537f\u9762\u4e0a\u4e00\u8f6c\uff0c \u7136\u540e\u8f7b\u8f7b\u201c\u55ef\u201d\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u662f\uff0c\u59d1\u59d1\uff0c\u6211\u2026\u2026\u201d \u3000\u3000\u6e29\u5982\u7389\u9634\u68ee\u7684\u9762\u5bb9\u626d\u66f2\u7740\u5fae\u7b11\u4e00\u4e0b\uff1a\u201c\u5feb\u53bb\uff0c\u4f60\u8f7b\u529f\u867d\u7136\u6bd4\u4ed6\u4eec\u9ad8\uff0c\u4f46\u662f\u4e5f\u8981\u7094\u53bb\uff0c\u522b \u7684\u4e8b\u7b49\u4f1a\u518d\u8bf4\u3002\u201d \u3000\u3000\u6e29\u747e\u53c8\u81ea\u8f7b\u8f7b\u201c\u55ef\u201d\u4e86\u4e00\u58f0\uff0c\u98de\u9e64\u822c\u63a0\u56de\u95e8\u53e3\uff0c\u7a81\u7136\u811a\u6b65\u4e00\u987f\uff0c\u50cf\u662f\u4e0b\u4e86\u6781\u4e3a\u91cd\u5927\u7684\u51b3 \u5b9a\uff0c\u5979\u7adf\u56de\u9996\u5411\u5353\u957f\u537f\u9053\uff1a\u201c\u4f60\u4e0d\u8981\u8d70\uff0c\u7b49\u6211\uff01\u201d \u3000\u3000\u7b49\u5230\u5979\u8bed\u58f0\u6d88\u5931\u7684\u65f6\u5019\uff0c\u5979\u70af\u632a\u7684\u8eab\u5f62\u4e0e\u98d8\u626c\u7684\u79c0\u53d1\uff0c\u4e5f\u90fd\u5df2\u6d88\u5931\u5728\u95e8\u53e3\u6c89\u91cd\u7684\u591c\u8272 \u91cc\u3002 \u3000\u3000\u5353\u957f\u537f\u5446\u671b\u5979\u80cc\u5f71\u7684\u6d88\u5931\uff0c\u4e0d\u77e5\u4e3a\u4e86\u4ec0\u4e48\uff0c\u4ed6\u4e0d\u6b62\u4e00\u6b21\u60f3\u8bf4\u51fa\u4ed6\u4ec7\u4eba\u7684\u540d\u5b57\u6e29\u5982\u7389\uff0c\u4f46 \u4ed6\u7adf\u7136\u6ca1\u6709\u8bf4\u51fa\u6765\uff0c\u8fd9\u7a76\u7adf\u662f\u4e3a\u4e86\u4ec0\u4e48\uff0c\u4ed6\u7684\u786e\u8fde\u81ea\u5df1\u4e5f\u4e0d\u77e5\u9053\u3002 \u3000\u3000\u4ed6\u7f13\u7f13\u8f6c\u8fc7\u76ee\u5149\uff0c\u6e29\u5982\u7389\u633a\u76f4\u7684\u8170\u677f\uff0c\u6b64\u523b\u7adf\u5f13\u66f2\u4e86\u4e0b\u6765\uff0c\u4ed6\u671b\u7740\u5979\u7684\u76ee\u5149\uff0c\u7a81\u7136\u53d1\u73b0 \u5979\u76ee\u5149\u4e2d\u7adf\u6709\u7740\u4e00\u79cd\u96be\u4ee5\u62c8\u4f30\u7684\u7231\u610f\uff0c\u53ea\u6709\u59bb\u5b50\u5bf9\u4e08\u592b\u3001\u6bcd\u4eb2\u5bf9\u5b50\u5973\u624d\u4f1a\u53d1\u51fa\u6765\u7684\u7231\u610f\u3002 \u3000\u3000\u4ed6\u5fc3\u5934\u4e00\u9707\uff0c\u53ea\u89c9\u8111\u6d77\u4e2d\u4e00\u7247\u6df7\u9968\uff0c\u800c\u6e29\u5982\u7389\u5374\u5df2\u7f13\u7f13\u56de\u8fc7\u5934\u6765\uff1a\u201c\u4f60\u4e0d\u662f\u806a\u660e\u4eba\uff01\u201d \u3000\u3000\u5979\u6c89\u91cd\u800c\u68ee\u51b7\u7684\u8bf4\u7740\uff0c\u4f46\u8bed\u6c14\u4e2d\u5374\u5df2\u6709\u4e86\u4e00\u4efd\u65e0\u6cd5\u63a9\u9970\u7684\u6fc0\u52a8\u3002 \u3000\u3000\u5353\u957f\u537f\u5251\u7709\u4e00\u8f69\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u4f60\u4ece\u54ea\u91cc\u6765\u7684\uff1f\u201d \u3000\u3000\u6e29\u5982\u7389\u51b7\u51b7\u4e00\u7b11\u9053\uff1a\u201c\u6709\u4e9b\u4eba\u4e3a\u4e86\u81ea\u5df1\u6700\u4eb2\u8fd1\u7684\u4eba\uff0c\u5e38\u4f1a\u53d7\u4e9b\u5c48\u8fb1\uff0c\u6211\u4e00\u751f\u4ece\u672a\u5077\u542c\u8fc7 \u522b\u4eba\u7684\u8bdd\uff0c\u53ef\u662f\u2014\u2014\u201d\u5979\u53c8\u81ea\u51b7\u7b11\u4e00\u58f0\uff0c\u4f38\u624b\u5411\u4e0a\u4e00\u6307\uff0c\u5353\u957f\u537f\u76ee\u5149\u968f\u4e4b\u671b\u53bb\uff0c\u53ea\u89c1\u5c4b\u9876\u4e0a \u7adf\u591a\u4e86\u4e00\u4e2a\u6d1e\u7a9f\u3002 \u3000\u3000\u4ed6\u5fc3\u5ff5\u4e00\u8f6c\uff0c\u6c89\u58f0\u53c8\u9053\uff1a\u201c\u90a3\u4e9b\u4f60\u5168\u77e5\u9053\u4e86\uff1f\u201d \u3000\u3000\u6e29\u5982\u7389\u6c89\u91cd\u5730\u70b9\u4e86\u70b9\u5934\u9053\uff1a\u201c\u6211\u5168\u542c\u89c1\u4e86\uff0c\u5168\u77e5\u9053\u4e86\uff0f\u5979\u624b\u638c\u4e00\u4f38\u4e00\u66f2\uff0c\u7a81\u7136\u53c8\u4ece\u8896\u4e2d \u4f38\u51fa\u624b\u6765\uff0c\u638c\u4e2d\u7adf\u591a\u4e86\u4e00\u4e2a\u91d1\u5149\u707f\u7136\u7684\u5706\u5f62\u5c0f\u7b52\u3002\u201d\u4e94\u4e91\u70d8\u65e5\u900f\u5fc3\u9488\uff01\u201c\u5979\u68ee\u51b7\u7684\u8bf4\u9053\uff1a \u201c\u6211\u4e00\u76f4\u7528\u8fd9\u5bf9\u7740\u4f60\uff0c\u53ea\u8981\u4f60\u8bf4\u51fa\u4e00\u4e2a\u5b57\uff0c\u2014\u2014\u54fc\uff0c\u4e94\u4e91\u70d8\u65e5\u900f\u5fc3\u9488\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u5fc3\u5934\u4e00\u51db\uff1a\u201c\u4e94\u4e91\u70d8\u65e5\u900f\u5fc3\u9488\uff01\u201d \u3000\u3000\u4ed6\u5148\u524d\u4e0d\u77e5\u9053\u8fd9\u5973\u9b54\u5934\u600e\u5730\u5b66\u5230\u90a3\u5931\u4f20\u5df2\u4e45\u7684\u7edd\u6bd2\u6b66\u4e03\u7edd\u91cd\u624b\uff0c\u6b64\u523b\u66f4\u4e0d\u77e5\u9053\u5979\u4ece\u54ea\u91cc \u5f97\u6765\u7684\u8fd9\u79cd\u7edd\u6bd2\u7684\u6697\u5668\uff0c\u751a\u81f3\u6bd4\u4e03\u7edd\u91cd\u624b\u8fd8\u8981\u6bd2\u4e0a\u4e09\u5206\u7684\u4e94\u4e91\u70d8\u65e5\u900f\u5fc3\u9488\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u5374\u4ecd\u7136\u6602\u7136\u9053\uff1a\u201c\u4e94\u4e91\u70d8\u65e5\u900f\u5fc3\u9488\u4e5f\u672a\u89c1\u80fd\u5948\u6211\u4f55\u3002\u201d \u3000\u3000\u6e29\u5982\u7389\u76ee\u5149\u4e00\u8f6c\uff0c\u7a81\u7136\u54c8\u54c8\u5927\u7b11\u8d77\u6765\uff1a\u201c\u4f60\u771f\u7684\u4e0d\u662f\u4e2a\u806a\u660e\u4eba\uff0c\u4f60\u96be\u9053\u4e0d\u77e5\u9053\u6211\u8981\u6740 \u4f60\uff1f\u201d\u5979\u7b11\u58f0\u4e00\u655b\uff0c\u91cd\u590d\u4e86\u51e0\u53e5\uff1a\u201c\u6211\u8981\u6740\u4f60\uff0c\u53ef\u662f\u4f60\u5374\u8fd8\u4e0d\u9003\u8d70\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u80f8\u819b\u4e00\u633a\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u53ea\u6015\u4e5f\u672a\u5fc5\u592a\u5bb9\u6613\u3002\u201d \u3000\u3000\u6e29\u5982\u7389\u76ee\u5149\u4e00\u8361\uff0c\u9053\uff1a\u201c\u65e0\u8bba\u5982\u4f55\uff0c\u6211\u4e5f\u8981\u6740\u4f60\uff0c\u4f60\u5c31\u662f\u60f3\u8981\u9003\uff0c\u4e5f\u6765\u4e0d\u53ca\u4e86\uff0c\u6211\u6740\u4e86 \u4f60\uff0c\u6740\u4e86\u5c39\u51e1\uff0c\u4e16\u4e0a\u5c31\u6c38\u8fdc\u6ca1\u6709\u4e00\u4e2a\u77e5\u9053\u6b64\u4e8b\u79d8\u5bc6\u7684\u4eba\u4e86\uff0c\u90a3\u4e48\uff0c\u747e\u513f\u5c31\u6c38\u8fdc\u662f\u6211\u7684\uff0c\u6c38\u8fdc \u662f\u6211\u7684\u2026\u2026\u201d \u3000\u3000\u5979\u7f13\u7f13\u5782\u4e0b\u76ee\u5149\uff0c\u82cd\u8001\u67af\u7626\u7684\u9762\u5bb9\u66f4\u82cd\u8001\u4e86\u3002 \u3000\u3000\u201c\u747e\u513f\u6c38\u8fdc\u662f\u6211\u7684\uff0c\u76f4\u5230\u6211\u6b7b\uff0c\u6ca1\u6709\u4e00\u4e2a\u4eba\u80fd\u62a2\u53bb\u747e\u513f\uff0c\u6ca1\u6709\u4efb\u4f55\u4e00\u4e2a\u4eba\u2026\u2026\u201d \u3000\u3000\u5979\u4ed4\u7ec6\u5730\u51dd\u6ce8\u7740\u624b\u4e2d\u7684\u91d1\u8272\u5706\u7b52\uff0c\u4ed4\u7ec6\u5730\u628a\u5f04\u7740\uff1a\u201c\u4f60\u4e0d\u662f\u806a\u660e\u4eba\uff0c\u662f\u806a\u660e\u4eba\uff0c\u4f60\u65e9\u5c31 \u8d70\u4e86\uff01\u201d \u3000\u3000\u5353\u957f\u537f\u7a81\u5730\u6602\u9996\u72c2\u7b11\u8d77\u6765\uff1a\u201c\u6c38\u8fdc\u6ca1\u6709\u4eba\u77e5\u9053\u6b64\u4e8b\u7684\u79d8\u5bc6\u2014\u2014\u54c8\u54c8\uff0c\u4f60\u8981\u77e5\u9053\uff0c\u4e16\u4e0a\u6c38 \u8fdc\u6ca1\u6709\u771f\u6b63\u7684\u79d8\u5bc6\uff0c\u9664\u975e\u2014\u2014\u201d\u6e29\u5982\u7389\u5927\u559d\u4e00\u58f0\uff1a\u201c\u9664\u975e\u6211\u6740\u4e86\u4f60\uff01\u201d \u3000\u3000\u888d\u8896\u4e00\u62c2\uff0c\u8eab\u5f62\u7a81\u53c8\u79bb\u6848\u800c\u8d77\u3002 \u3000\u3000\u5239\u90a3\u4e4b\u95f4\uff0c\u5353\u957f\u537f\u53ea\u89c9\u4e00\u7247\u7ea2\u4e91\uff0c\u5411\u81ea\u5df1\u5f53\u5934\u538b\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u4ed6\u8eab\u5f62\u4e00\u632b\uff0c\u53cc\u638c\u7a81\u7136\u5e73\u80f8\u63a8\u51fa\uff0c\u53ea\u542c\u201c\u547c\u201d\u7684\u4e00\u58f0\uff0c\u638c\u51e4\u6fc0\u8361\uff0c\u684c\u4e0a\u7684\u660e\u73e0\u53c8\u843d\u5230\u5730 \u4e0a\uff0c\u6e29\u52a0\u7389\u8eab\u5f62\u5411\u540e\u4e00\u7ffb\uff0c\u4f46\u77ac\u5373\u63a0\u4e0a\uff0c\u5389\u58f0\u7b11\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u7684\u6b66\u529f\uff0c\u4f60\u5728\u6211\u624b\u4e0b\u8d70\u4e0d\u4e86 \u4e94\u5341\u62db\uff0c\u90a3\u65f6\u747e\u513f\u8fd8\u672a\u56de\u6765\u2014\u2014\u54c8\u54c8\uff0c\u6211\u6bcb\u5eb8\u7528\u8fd9\u6697\u5668\u6740\u4f60\uff0c\u6211\u8981\u4eb2\u624b\u6740\u4f60\uff0c\u6c38\u8fdc\u6ca1\u6709\u4eba\u80fd \u6cc4\u9732\u6211\u7684\u79d8\u5bc6\uff0c\u6c38\u8fdc\u6ca1\u6709\u2026\u2026\u201d \u3000\u3000\u5979\u60e8\u5389\u5730\u72c2\u7b11\u7740\uff0c\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u5df2\u53d1\u72c2\u4e86\u4f3c\u5730\u5411\u5353\u957f\u537f\u653b\u51fb\u4e94\u62db\uff0c\u62db\u62db\u6bd2\u8fa3\uff0c\u62db\u62db\u81f4\u547d\uff0c \u5353\u957f\u537f\u5251\u7709\u6012\u8f69\uff0c\u5353\u7acb\u5982\u5c71\uff0c\u500f\u5ffd\u4e4b\u95f4\uff0c\u4e5f\u8fd8\u4e86\u4e94\u62db\uff0c\u4ed6\u81ea\u77e5\u81ea\u5df1\u6b64\u523b\u5df2\u4e34\u751f\u6b7b\u5b58\u4ea1\u4e4b\u9645\uff0c \u4f46\u4ed6\u5374\u4e1d\u6beb\u6ca1\u6709\u9003\u8d70\u4e4b\u5fc3\uff0c\u660e\u4eae\u7684\u73cd\u73e0\uff0c\u968f\u7740\u4ed6\u4eec\u7684\u638c\u98ce\u5728\u5730\u4e0a\u6eda\u52a8\u7740\uff0c\u6eda\u5f97\u6ee1\u5ba4\u7684\u5149\u534e\u4e71 \u95ea\uff0c\u6620\u5f97\u6e29\u5982\u7389\u7684\u9762\u5bb9\u9635\u9752\u9635\u767d\uff0c\u4f46\u500f\u5ffd\u5341\u62db\u8fc7\u53bb\uff0c\u5979\u89c1\u81ea\u5df1\u672a\u80fd\u5360\u5f97\u534a\u7740\u5148\u673a\u3002\u8981\u77e5\u9053\u5353 \u957f\u537f\u7684\u6b66\u529f\u867d\u56e0\u7ecf\u9a8c\u4e0e\u706b\u5019\u4e4b\u6545\u800c\u7565\u900a\u5979\u4e00\u7b79\uff0c\u4f46\u5dee\u5f97\u5e76\u4e0d\u751a\u8fdc\uff0c\u4f55\u51b5\u5353\u957f\u537f\u4e0a\u6b21\u5df2\u6709\u4e86\u548c \u5979\u5bf9\u654c\u7684\u7ecf\u9a8c\uff0c\u6b64\u756a\u52a8\u8d77\u624b\u6765\uff0c\u4fbf\u5360\u4e86\u51e0\u5206\u4fbf\u5b9c\u3002 \u3000\u3000\u4f46\u662f\u6e29\u5982\u7389\u6325\u51fa\u7684\u638c\u98ce\uff0c\u5374\u968f\u7740\u5979\u62db\u5f0f\u7684\u53d8\u6362\uff0c\u800c\u53d8\u5f97\u66f4\u6c89\u91cd\u4e86\uff0c\u6c89\u91cd\u5f97\u4f7f\u5f97\u5353\u957f\u537f\u6bcf \u4e00\u4e2a\u62db\u5f0f\u7684\u8fd0\u8f6c\uff0c\u90fd\u8981\u4f7f\u51fa\u4ed6\u5168\u8eab\u7684\u52b2\u529b\uff0c\u4ed6\u7a81\u7136\u5f00\u59cb\u6000\u7591\u81ea\u5df1\u662f\u5426\u771f\u7684\u6709\u529b\u91cf\u63a5\u4e0b\u8fd9\u5973\u9b54 \u5934\u7684\u6570\u767e\u62db\u3002 \u3000\u3000\u201c\u7830\u201d\u7684\u4e00\u58f0\uff0c\u575a\u5b9e\u800c\u539a\u91cd\u7684\u4e4c\u6728\u795e\u684c\uff0c\u5728\u6e29\u5982\u7389\u811a\u5c16\u7684\u4e00\u8e22\u4e4b\u4e0b\uff0c\u56db\u6563\u5d29\u88c2\uff0c\u788e\u6728\u7c89 \u98de\uff0c\u5353\u957f\u537f\u53cc\u8db3\u5de7\u5999\u5730\u65cb\u52a8\u4e03\u6b21\uff0c\u7a81\u7136\u8eab\u8eaf\u4e00\u62e7\uff0c\u53f3\u638c\u81ea\u5de6\u800c\u53f3\uff0c\u201c\u7830\u201d\u5730\u6325\u51fa\u4e00\u638c\uff0c\u53f3\u811a \u8f7b\u8f7b\u4e00\u6311\uff0c\u6311\u8d77\u4e00\u6bb5\u684c\u811a\uff0c\u5de6\u638c\u659c\u6284\uff0c\u7adf\u5c06\u8fd9\u6bb5\u684c\u811a\u63e1\u5728\u624b\u91cc\uff0c\u6b64\u523b\u4ed6\u53f3\u638c\u4e00\u56e2\uff0c\u4e94\u6307\u7b95 \u5f20\uff0c\u7a81\u7136\u4e00\u8d77\u5f39\u5411\u6e29\u5982\u7389\u5f53\u5934\u62cd\u4e0b\u7684\u4e00\u638c\uff0c\u6e29\u5982\u7389\u5389\u5578\u4e00\u58f0\uff0c\u8eab\u5f62\u4e00\u7f29\uff0c\u9000\u540e\u4e00\u6b65\uff0c\u5353\u957f\u537f \u53f3\u638c\u5df2\u81ea\u53f3\u5411\u5de6\u4e00\u56e2\uff0c\u63a5\u8fc7\u5de6\u638c\u4e0a\u7684\u684c\u811a\uff0c\u624b\u8155\u4e00\u9707\uff0c\u6296\u624b\u4e00\u5251\u523a\u53bb\u3002 \u3000\u3000\u4ed6\u8fd9\u638c\u6325\u3001\u811a\u8e22\u3001\u624b\u63a5\uff0c\u6307\u5f39\uff0c\u56db\u79cd\u53d8\u5316\uff0c\u7adf\u4e8e\u540c\u4e00\u5239\u90a3\u4e2d\u5b8c\u6210\uff0c\u7094\u5982\u7535\u5149\u706b\u77f3\uff0c\u800c\u6296 \u624b\u4e00\u523a\uff0c\u90a3\u6bb5\u957f\u4e0d\u8fc7\u4e09\u5c3a\uff0c\u7b28\u62d9\u7684\u684c\u811a\u5728\u4ed6\u624b\u4e2d\uff0c\u88ab\u6296\u8d77\u6735\u6735\u5251\u82b1\uff0c\u7adf\u65e0\u5f02\u4e8e\u4e00\u67c4\u9752\u94a2\u5251\u3002 \u3000\u3000\u5239\u90a3\u4e4b\u95f4\uff0c\u4ed6\u8eab\u6cd5\u5927\u53d8\uff0c\u5353\u7acb\u5982\u5c71\u7684\u8eab\u5f62\uff0c\u7a81\u7136\u53d8\u5f97\u98de\u626c\u8df3\u8131\uff0c\u6728\u5251\u968f\u8eab\uff0c\u8eab\u968f\u5251\u8d70\uff0c \u5f53\u771f\u662f\u9759\u5982\u6cf0\u5c71\uff0c\u52a8\u5982\u8131\u5154\uff0c\u4e4d\u770b\u5b9b\u5982\u6b66\u5f53\u7684\u4e5d\u5b98\u8fde\u73af\uff0c\u518d\u770b\u5374\u4f3c\u5df4\u5c71\u7684\u56de\u821e\u98ce\u67f3\uff0c\u4f46\u4ed4\u7ec6 \u4e00\u770b\uff0c\u5374\u53c8\u548c\u5929\u5c71\u4e00\u8109\u76f8\u4f20\u7684\u4e09\u5206\u5251\u6cd5\u6709\u4e9b\u76f8\u4f3c\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u7adf\u8ba9\u4eba\u65e0\u6cd5\u5206\u8fa8\u4ed6\u5251\u6cd5\u7684\u6765 \u5386\u3002 \u3000\u3000\u6e29\u5982\u7389\u51c4\u5389\u7684\u957f\u58f0\u4e00\u7b11\uff0c\u5de6\u638c\u6307\u56de\u5982\u94a9\uff0c\u6293\u3001\u6495\u3001\u634b\u3001\u593a\uff0c\u7a7a\u624b\u5165\u767d\u5203\uff0c\u5927\u5c0f\u64d2\u638c\u624b\uff0c \u4ece\u5353\u957f\u537f\u6f2b\u5929\u7684\u6728\u5251\u5149\u5f71\u4e2d\uff0c\u7740\u7740\u62a2\u653b\uff0c\u53ea\u8981\u5353\u957f\u537f\u5251\u6cd5\u7a0d\u6709\u6f0f\u6cc4\uff0c\u624b\u4e2d\u957f\u5251\u4fbf\u4f1a\u7acb\u65f6\u88ab \u593a\u3002 \u3000\u3000\u5979\u53f3\u638c\u5374\u662f\u70b9\u3001\u62cd\u3001\u5241\u3001\u6233\uff0c\u7adf\u5c06\u638c\u4e2d\u90a3\u957f\u4e0d\u53ca\u4e00\u5c3a\u7684\u4e94\u4e91\u70d8\u65e5\u900f\u5fc3\u9488\u7684\u9488\u7b52\uff0c\u5f53\u505a\u5185 \u5bb6\u70b9\u7a74\u7684\u5175\u5203\u201c\u70b9\u7a74\u6485\u201d\u4f7f\u7528\uff0c\u91d1\u5149\u95ea\u95ea\uff0c\u8000\u76ee\u751f\u82b1\uff0c\u62db\u62db\u5374\u4e0d\u79bb\u5353\u957f\u537f\u8eab\u4e0a\u5927\u7a74\u7684\u65b9\u5bf8\u5de6 \u53f3\u3002 \u3000\u3000\u8fd9\u4e24\u4e2a\u672c\u4ee5\u5185\u5bb6\u771f\u529b\u76f8\u640f\u7684\u6b66\u6797\u9ad8\u624b\uff0c\u6b64\u523b\u7adf\u5404\u6b32\u4ee5\u7cbe\u5965\u7684\u62db\u6570\u53d6\u80dc\uff0c\u8fd9\u4e48\u4e00\u6765\uff0c\u5353\u957f \u537f\u6570\u5341\u62db\u8fc7\u540e\uff0c\u4fbf\u53c8\u7f13\u8fc7\u4e00\u53e3\u6c14\u6765\uff0c\u8981\u77e5\u9053\u4ed6\u529f\u529b\u706b\u5019\u867d\u4e0d\u53ca\u8fd9\u4e11\u4eba\u6e29\u5982\u7389\uff0c\u4f46\u6b66\u529f\u62db\u5f0f\u5374 \u662f\u4f20\u81ea\u5929\u4e0b\u7b2c\u4e00\u5947\u4eba\uff0c\u6e29\u5982\u7389\u8fde\u65cb\u70b9\u624b\uff0c\u773c\u770b\u6709\u51e0\u62db\u5c31\u8981\u5f97\u624b\uff0c\u54ea\u77e5\u4ed6\u6728\u5251\u6325\u5904\uff0c\u5374\u90fd\u80fd\u5316 \u9669\u4e3a\u5937\u3002 \u3000\u3000\u5728\u5239\u90a3\u4e4b\u95f4\uff0c\u4e24\u4eba\u5df2\u62fc\u8fc7\u4e86\u767e\u5341\u62db\uff0c\u5353\u957f\u537f\u51b7\u7b11\u4e00\u58f0\uff0c\u5927\u559d\u9053\uff1a\u201c\u4e94\u5341\u62db\u5c31\u8981\u53eb\u6211\u4e27 \u751f\uff0c\u54fc\u54fc\uff0c\u53ea\u6015\u2014\u2014\u201d\u8bdd\u58f0\u672a\u4e86\uff0c\u7a81\u89c1\u6e29\u5982\u7389\u4e94\u6307\u5982\u94a9\uff0c\u7adf\u6293\u5411\u4ed6\u638c\u4e2d\u6728\u5251\uff0c\u4ed6\u5fc3\u5934\u4e00\u62e7\uff0c \u77e5\u9053\u5979\u8fd9\u4e00\u6293\u5fc5\u6709\u5389\u5bb3\u51fa\u624b\uff0c\u6728\u5251\u4e00\u5f15\uff0c\u6e29\u5982\u7389\u53f3\u624b\u9488\u7b52\u5df2\u75be\u7136\u70b9\u5411\u80f8\u8179\u4e4b\u95f4\u3002 \u3000\u3000\u8fd9\u4e00\u62db\u4e24\u5f0f\u5feb\u5982\u7535\u706b\u5149\u77f3\uff0c\u4ed6\u773c\u770b\u907f\u65e0\u53ef\u907f\uff0c\u53ea\u5f97\u6a2a\u5251\u4e00\u6321\uff0c\u5251\u7b52\u76f8\u4ea4\uff0c\u5353\u957f\u537f\u53ea\u89c9\u624b \u8155\u4e00\u9707\uff0c\u5bf9\u65b9\u9488\u7b52\u4e4b\u4e0a\uff0c\u5df2\u6709\u4e00\u80a1\u51cc\u5389\u4e4b\u6781\u7684\u5185\u529b\u6e90\u6e90\u4e0d\u7edd\u7684\u81ea\u4ed6\u638c\u4e2d\u6728\u5251\u903c\u4e86\u8fc7\u6765\uff0c\u4ed6\u9664 \u4e86\u4e5f\u4ee5\u5185\u529b\u62db\u67b6\uff0c\u522b\u65e0\u9009\u62e9\u4f59\u5730\uff0c\u5f53\u4e0b\u5927\u559d\u4e00\u58f0\uff0c\u53cc\u817f\u7262\u7262\u9489\u5728\u5730\u4e0a\uff0c\u6697\u8c03\u771f\u529b\uff0c\u4e0e\u6e29\u5982\u7389 \u7684\u5185\u529b\u76f8\u6297\u3002 \u3000\u3000\u660e\u73e0\u6eda\u52a8\uff0c\u6b64\u523b\u5df2\u6eda\u5230\u95e8\u8fb9\uff0c\u5353\u957f\u537f\u7259\u5173\u7d27\u54ac\uff0c\u77aa\u76ee\u5982\u73af\uff0c\u53ea\u89c9\u5bf9\u65b9\u903c\u6765\u7684\u8089\u529b\uff0c\u7adf\u662f \u4e00\u6b21\u5927\u4f3c\u4e00\u6b21\uff0c\u7b2c\u4e00\u6b21\u8fdb\u653b\u7684\u529b\u9053\u672a\u6d88\uff0c\u7b2c\u4e8c\u9053\u5185\u529b\u53c8\u903c\u4e86\u8fc7\u6765\uff0c\u7b2c\u4e8c\u9053\u653b\u529b\u72b9\u5b58\uff0c\u7b2c\u4e09\u9053 \u5185\u529b\u53c8\u81f3\uff0c\u4ed6\u7eb5\u60f3\u62bd\u5f00\u957f\u5251\uff0c\u518d\u4ee5\u62db\u5f0f\u76f8\u640f\uff0c\u5374\u53c8\u4e07\u4e07\u4e0d\u80fd\uff0c\u62ac\u76ee\u671b\u5904\uff0c\u53ea\u89c1\u6e29\u5982\u7389\u65e5\u4e2d\u5bd2 \u5149\u8d8a\u6765\u8d8a\u4eae\uff0c\u7a81\u7136\u201c\u54c7\u54c7\u201d\u602a\u7b11\u4e4b\u58f0\u53c8\u8d77\uff0c\u5979\u7adf\u602a\u7b11\u7740\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u4e0d\u662f\u806a\u660e\u4eba\u2014\u2014\u563f \u563f\uff0c\u4f60\u6b7b\u4e86\uff0c\u5c31\u8981\u6b7b\u4e86\uff0c\u8fd9\u79d8\u5bc6\u6c38\u8fdc\u6ca1\u6709\u4eba\u518d\u4f1a\u77e5\u9053\uff0c\u747e\u513f\u6c38\u8fdc\u662f\u6211\u7684\u4e86\u3002\u201d \u3000\u3000\u5979\u6b64\u523b\u5df2\u7a33\u64cd\u80dc\u5238\uff0c\u662f\u4ee5\u5728\u8fd9\u7b49\u60c5\u51b5\u4e4b\u4e0b\uff0c\u4ecd\u80fd\u5f00\u53e3\u8bf4\u8bdd\uff0c\u5353\u957f\u537f\u5fc3\u5934\u4e00\u51db\uff0c\u53ea\u89c9\u53cc\u988a \u51b0\u51c9\uff0c\u539f\u6765\u989d\u4e0a\u6c57\u73e0\u5df2\u6d41\u4e86\u4e0b\u6765\uff0c\u4ed6\u6697\u4e2d\u957f\u53f9\u4e00\u58f0\uff0c\u6b63\u5f85\u62fc\u5c3d\u6700\u540e\u4f59\u529b\uff0c\u4f7f\u5b64\u6ce8\u4e00\u63b7\u4e4b\u6597\u3002 \u3000\u3000\u54ea\u77e5\u2014\u2014 \u3000\u3000\u95e8\u5916\u591c\u8272\u4e2d\u7a81\u7136\u5e7d\u7075\u822c\u73b0\u51fa\u4e00\u6761\u4eba\u5f71\uff0c\u8eab\u62ab\u5409\u670d\uff0c\u9762\u5bb9\u82cd\u767d\uff0c\u53cc\u76ee\u83b9\u7136\u3002 \u3000\u3000\u5979\u5e7d\u5e7d\u5730\u957f\u53f9\u4e86\u4e00\u58f0\uff0c\u7a81\u7136\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u4e0d\u7528\u6740\u6b7b\u4ed6\uff0c\u8fd9\u79d8\u5bc6\u6211\u5df2\u542c\u5230\u4e86\u3002\u201d \u7b2c\u5341\u56db\u7ae0\u3000\u67d4\u80a0\u5bf8\u65ad>> \u53e4\u9f99\u300a\u6708\u5f02\u661f\u90aa\u300b \u7b2c\u5341\u56db\u7ae0\u3000\u67d4\u80a0\u5bf8\u65ad \u3000\u3000\u6e29\u5982\u7389\u3001\u5353\u957f\u537f\u5fc3\u5934\u4ff1\u90fd\u4e00\u9707\uff0c\u4e24\u4eba\u500f\u5730\u4e00\u8d77\u5206\u5f00\uff0c\u626d\u9996\u671b\u53bb\uff0c\u53ea\u89c1\u6e29\u747e\u5f53\u95e8\u800c\u7acb\uff0c\u5730 \u4e0a\u7684\u73e0\u513f\uff0c\u6620\u7740\u5979\u82cd\u767d\u7684\u9762\u5bb9\uff0c\u6e29\u5982\u7389\u6d51\u8eab\u4e00\u9635\u98a4\u6296\uff0c\u5012\u9000\u4e94\u6b65\uff0c\u501a\u5728\u5899\u4e0a\uff0c\u6709\u5982\u7a81\u7136\u89c1\u5230 \u9b3c\u94e3\u4e00\u6837\uff0c\u4f38\u51fa\u67af\u7626\u7684\u624b\u6307\uff0c\u6307\u7740\u6e29\u747e\uff0c\u98a4\u58f0\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u600e\u2026\u2026\u5730\u56de\u6765\u4e86\uff1f\u201d \u3000\u3000\u6e29\u747e\u9762\u76ee\u4e4b\u4e0a\u6728\u65e0\u8868\u60c5\uff0c\u7f13\u7f13\u4e00\u62ac\u8db3\uff0c\u8e22\u5f00\u95e8\u8fb9\u7684\u660e\u73e0\uff0c\u7f13\u7f13\u8d70\u4e86\u8fdb\u6765\uff0c\u76ee\u5149\u4e00\u8f6c\uff0c\u4ece \u5730\u4e0a\u62fe\u8d77\u90a3\u5757\u81ea\u6728\u7075\u724c\uff0c\u8f7b\u8f7b\u62e5\u5728\u6000\u91cc\uff0c\u76ee\u5149\u518d\u4e00\u8f6c\uff0c\u7b14\u76f4\u5730\u671b\u5411\u6e29\u5982\u7389\uff0c\u4e00\u5b57\u4e00\u5b57\u7684\u51b7\u51b7 \u8bf4\u9053\uff1a\u201c\u6211\u7239\u7239\u662f\u4e0d\u662f\u4f60\u6740\u6b7b\u7684\uff1f\u201d \u3000\u3000\u8fd9\u51b0\u51b7\u7684\u8bed\u58f0\uff0c\u5b9b\u5982\u4e00\u652f\u5229\u7bad\uff0c\u65e0\u60c5\u5730\u5c04\u5165\u6e29\u5982\u7389\u7684\u5fc3\u91cc\u3002 \u3000\u3000\u5979\u5168\u8eab\u4e00\u9707\uff0c\u67af\u7626\u7684\u8eab\u8eaf\u50cf\u662f\u5728\u9003\u907f\u7740\u4ec0\u4e48\uff0c\u7d27\u7d27\u8fdf\u5230\u5899\u89d2\u3002 \u3000\u3000\u6e29\u747e\u76ee\u5149\u4e00\u62ac\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u6211\u77e5\u9053\u7239\u7239\u662f\u4f60\u6740\u6b7b\u7684\uff0c\u662f\u4e0d\u662f\u2026\u2026\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u5979\u7f13\u6162\u5730\u79fb\u52a8\u7740\u811a\u6b65\uff0c\u4e00\u6b65\u4e00\u6b65\u5730\u5411\u6e29\u5982\u7389\u8d70\u4e86\u8fc7\u53bb\uff0c\u5353\u957f\u537f\u4e00\u62b9\u989d\u4e0a\u7684\u6c57\u73e0\uff0c\u4f46\u638c\u5fc3 \u4ea6\u662f\u6e7f\u6e7f\u7684\uff0c\u5df2\u51fa\u4e86\u4e00\u638c\u51b7\u6c57\u3002 \u3000\u3000\u4ed6\u7684\u5fc3\u4ea6\u5728\u614c\u4e71\u5730\u8df3\u52a8\u7740\uff0c\u4ed6\u773c\u770b\u7740\u6e29\u747e\u7684\u8eab\u5f62\uff0c\u8ddd\u79bb\u6e29\u5982\u7389\u8d8a\u6765\u8d8a\u8fd1\uff0c\u54ea\u77e5\u6e29\u5982\u7389\u7a81 \u7136\u5927\u559d\u4e86\u4e00\u58f0\uff1a\u201c\u7ad9\u7740\uff01\u201d \u3000\u3000\u6e29\u747e\u811a\u6b65\u4e00\u505c\u987f\uff0c\u6e29\u5982\u7389\u5374\u53c8\u957f\u53f9\u4e00\u58f0\uff0c\u7f13\u7f13\u5782\u4e0b\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u7239\u7239\u662f\u6211\u6740\u6b7b\u7684\u2026\u2026 \u662f\u6211\u6740\u6b7b\u7684\uff01\u201d \u3000\u3000\u6e29\u747e\u4f38\u624b\u4e00\u63a2\u67d4\u53d1\uff0c\u7a81\u7136\u7eb5\u58f0\u72c2\u7b11\u8d77\u6765\u3002 \u3000\u3000\u201c\u6211\u7239\u7239\u662f\u4f60\u6740\u6b7b\u7684\uff0c\u6211\u7239\u7239\u662f\u4f60\u6740\u6b7b\u7684\u2026\u2026\u6211\u5988\u5988\u4e5f\u662f\u4f60\u6740\u6b7b\u7684\u4e86\uff1f\u201d \u3000\u3000\u5979\u7eb5\u58f0\u72c2\u7b11\u7740\uff0c\u7b11\u58f0\u51c4\u5389\uff0c\u53ea\u542c\u5f97\u5353\u957f\u537f\u638c\u5fc3\u53d1\u51b7\uff0c\u4ed6\u4ece\u672a\u60f3\u5230\u4eba\u4eec\u7684\u7b11\u58f0\u4e4b\u4e2d\u4e5f\u4f1a\u5305 \u6db5\u7740\u8fd9\u8bb8\u591a\u60b2\u54c0\u51c4\u51c4\u7684\u610f\u5473\u3002 \u3000\u3000\u53ea\u89c1\u6e29\u747e\u53c8\u81ea\u7f13\u7f13\u62ac\u8d77\u811a\u6b65\uff1a\u201c\u6211\u5988\u5988\u4e5f\u662f\u4f60\u6740\u6b7b\u7684\u4e86\uff0c\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u5979\u72c2\u7b11\u7740\uff0c\u51b0\u51c9\u800c\u6676\u83b9\u7684\u6cea\u73e0\uff0c\u50cf\u662f\u4e00\u4e32\u65ad\u4e86\u7ebf\u7684\u73cd\u73e0\uff0c\u4e0d\u505c\u7684\u6cbf\u7740\u5979\u67d4\u6da6\u7684\u9762\u988a\u6d41\u4e86 \u4e0b\u6765\uff0c\u5979\u91cd\u590d\u7684\u95ee\u7740\uff1a\u201c\u662f\u4e0d\u662f\uff1f\u2026\u2026\u662f\u4e0d\u662f\u2026\u2026\u201d \u3000\u3000\u5979\u7f13\u7f13\u7684\u79fb\u52a8\u7740\u811a\u6b65\uff0c\u6bcf\u4e00\u4e3e\u6b65\uff0c\u90fd\u50cf\u662f\u4e00\u8bb0\u5343\u94a9\u94c1\u9524\uff0c\u5728\u6e29\u5982\u7389\u5fc3\u91cc\u5934\u649e\u51fb\u7740\u3002\u6e29\u5982 \u7389\u67af\u7626\u7684\u8eab\u8eaf\uff0c\u7d27\u7d27\u5730\u8d34\u5728\u5899\u4e0a\uff0c\u5979\u98a4\u6296\u7740\u4f38\u51fa\u624b\u6307\uff1a\u201c\u4e0d\u8981\u518d\u8d70\u8fc7\u6765\uff0c\u77e5\u9053\u5417\uff1f\u4e0d\u8981\u903c\u6211 \u6740\u6b7b\u4f60\uff0c\u4e0d\u8981\u903c\u6211\u6740\u6b7b\u4f60\u2026\u2026\u201d \u3000\u3000\u6e29\u747e\u7684\u7b11\u58f0\u66f4\u51c4\u5389\u4e86\uff1a\u201c\u6740\u6b7b\u6211\u2026\u2026\u54c8\u54c8\uff0c\u4f60\u6700\u597d\u6740\u6b7b\u6211\uff0c\u4f60\u6740\u6b7b\u4e86\u6211\u7239\u7239\uff0c\u6740\u6b7b\u4e86\u6211 \u5988\u5988\u2026\u2026\u201d \u3000\u3000\u54ea\u77e5\u2014\u2014 \u3000\u3000\u5979\u8bdd\u58f0\u5c1a\u672a\u4e86\uff0c\u6e29\u5982\u7389\u7adf\u4e5f\u7a81\u7136\u7eb5\u58f0\u72c2\u7b11\u8d77\u6765\uff1a\u201c\u6211\u6740\u4e86\u4f60\u5988\u5988\uff0c\u54c8\u54c8\u2014\u2014\u6211\u6740\u4e86\u4f60\u5988 \u5988\u2026\u2026\u201d \u3000\u3000\u7a81\u5730\u2014\u2014 \u3000\u3000\u5353\u957f\u537f\u53ea\u542c\u201c\u8f70\u201d\u7136\u4e00\u58f0\uff0c\u6728\u77f3\u5c18\u7802\uff0c\u6f2b\u5929\u98de\u8d77\u3002 \u3000\u3000\u4ed6\u4e00\u60ca\u4e4b\u4e0b\uff0c\u5b9a\u775b\u671b\u53bb\uff0c\u53ea\u542c\u6e29\u5982\u7389\u60e8\u5389\u7684\u7b11\u58f0\u8d8a\u53bb\u8d8a\u8fdc\uff0c\u8fd9\u5973\u9b54\u5934\u7adf\u4ee5\u81f3\u5f3a\u81f3\u521a\u7684\u5185 \u5bb6\u771f\u529b\uff0c\u5728\u5899\u4e0a\u7a7f\u4e86\u4e00\u4e2a\u5927\u6d1e\uff0c\u8131\u8eab\u800c\u53bb\uff0c\u8fdc\u8fdc\u4f20\u6765\u5979\u51c4\u5389\u7684\u7b11\u58f0\uff1a\u201c\u6211\u6740\u4e86\u4f60\u5988\u5988\u2026\u2026\u6211 \u6740\u4e86\u4f60\u5988\u5988\u2026\u2026\u201d \u3000\u3000\u5239\u90a3\u4e4b\u95f4\uff0c\u7b11\u58f0\u5212\u7a7a\u800c\u8fc7\uff0c\u56db\u4e0b\u53c8\u5df2\u5f52\u4e8e\u5bc2\u9759\uff0c\u53ea\u6709\u6e29\u747e\u4e0e\u5353\u957f\u537f\u7684\u547c\u5438\u4e4b\u58f0\uff0c\u5728\u8fd9\u5bc2 \u9759\u5982\u6b7b\u7684\u591c\u8272\u4e2d\u54cd\u8d77\u4e00\u4e9b\u58f0\u97f3\uff0c\u4f46\u5374\u53c8\u53ea\u662f\u90a3\u4e48\u5fae\u5f31\u3002 \u3000\u3000\u6e29\u747e\u8fd8\u81ea\u5446\u5446\u7684\u7ad9\u5728\u5730\u4e0a\uff0c\u77aa\u7740\u5931\u795e\u7684\u773c\u775b\uff0c\u832b\u7136\u671b\u7740\u6e10\u6e10\u5e73\u606f\u7684\u7802\u5c18\uff0c\u5979\u50f5\u7acb\u7740\u7684\u8eab \u8eaf\uff0c\u6e10\u6e10\u4e5f\u8d77\u4e86\u4e00\u9635\u98a4\u6296\u3002 \u3000\u3000\u7ec8\u4e8e\u2014\u2014 \u3000\u3000\u5979\u518d\u4e5f\u5fcd\u4e0d\u4f4f\u6fc0\u8361\u7684\u5fc3\u60c5\uff0c\u5931\u58f0\u75db\u54ed\u4e86\u8d77\u6765\uff0c\u5353\u957f\u537f\u53ea\u89c1\u5979\u8eab\u8eaf\u6447\u4e86\u4e24\u6447\uff0c\u7136\u540e\u4fbf\u50cf\u662f \u4e00\u7f15\u67f3\u4e1d\u822c\u865a\u5f31\u7684\u843d\u5230\u5730\u4e0a\uff0c\u4ed6\u5fc3\u5934\u4e00\u8df3\uff0c\u518d\u4e5f\u987e\u4e0d\u5f97\u522b\u7684\uff0c\u7eb5\u8eab\u63a0\u4e86\u8fc7\u53bb\uff0c\u4e00\u628a\u6402\u4f4f\u5979\u7684 \u7ea4\u8170\uff0c\u60f6\u58f0\u95ee\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4f60\u600e\u6837\u4e86\u2026\u2026\u201d \u3000\u3000\u4f46\u662f\u6e29\u747e\u53c8\u600e\u4f1a\u542c\u5f97\u5230\u4ed6\u7684\u58f0\u97f3\uff0c\u5979\u53ea\u89c9\u5fc3\u4e2d\u6709\u6cf0\u5c71\u4e00\u6837\u91cd\u7684\u60b2\u54c0\uff0c\u5317\u6d77\u4e00\u6837\u6df1\u7684\u4ec7 \u6068\uff0c\u8981\u5ba3\u6cc4\u51fa\u6765\u3002 \u3000\u3000\u4f46\u662f\u5979\u6b64\u523b\u9664\u4e86\u75db\u54ed\u4e4b\u5916\uff0c\u5979\u4ec0\u4e48\u4e5f\u4e0d\u80fd\u505a\uff0c\u5979\u518d\u4e5f\u60f3\u4e0d\u5230\u81ea\u5979\u6709\u751f\u4ee5\u6765\uff0c\u5c31\u4e00\u76f4\u7231\u7740 \u5979\u3001\u7167\u987e\u7740\u5979\u7684\u59d1\u59d1\uff0c\u7adf\u4f1a\u662f\u5979\u4e0d\u5171\u6234\u5929\u7684\u4ec7\u4eba\uff0c\u5979\u4e0d\u7ba1\u5728\u522b\u4eba\u773c\u4e2d\uff0c\u5bf9\u5979\u7684\u59d1\u59d1\u5982\u4f55\u60f3 \u6cd5\uff0c\u4f46\u662f\u90a3\u4e48\u591a\u5e74\uff0c\u59d1\u59d1\u5728\u5979\u770b\u6765\uff0c\u5374\u6c38\u8fdc\u662f\u6148\u853c\u800c\u4eb2\u5207\u7684\u3002 \u3000\u3000\u76f4\u5230\u6b64\u523b\u2014\u2014 \u3000\u3000\u76f4\u5230\u6b64\u523b\u6240\u6709\u5979\u4e00\u751f\u4e2d\u5168\u5fc3\u501a\u8d56\u7740\u7684\u4e1c\u897f\uff0c\u5168\u90e8\u50cf\u98de\u70df\u4e00\u6837\u7684\u6d88\u5931\u4e86\u3002 \u3000\u3000\u201c\u6211\u8be5\u600e\u4e48\u529e\u2026\u2026\u7239\u7239\u3001\u5988\u5988\uff0c\u4f60\u4eec\u600e\u4e48\u4e0d\u8ba9\u5973\u513f\u89c1\u4f60\u4e00\u9762\u2026\u2026\u201d \u3000\u3000\u5979\u75db\u54ed\u7740\u4f4e\u8bed\u7740\uff0c\u7239\u7239\u3001\u5988\u5988\uff0c\u5728\u5979\u8111\u6d77\u4e2d\u53ea\u662f\u4e00\u4e2a\u6a21\u7cca\u800c\u865a\u5e7b\u7684\u5f71\u5b50\uff0c\u5979\u6355\u6349\u4e0d\u5230\uff0c \u800c\u4e14\u4e5f\u770b\u4e0d\u771f\u786e\u2014\u2014\u4f46\u662f\u2014\u2014\u6e29\u5982\u7389\u7684\u5f71\u5b50\u5374\u662f\u90a3\u4e48\u9c9c\u660e\u800c\u6df1\u8fd9\u5730\u7559\u5728\u5979\u8111\u6d77\u91cc\uff0c\u5979\u65e0\u6cd5\u6446 \u8131\uff0c\u96be\u4ee5\u81ea\u9063\uff0c\u5341\u4f59\u5e74\u6765\u7684\u7231\u62a4\u4e0e\u5173\u5207\uff0c\u6b64\u523b\u7adf\u50cf\u662f\u90fd\u53d8\u6210\u4e86\u4e00\u6761\u6bd2\u86c7\uff0c\u7d27\u7d27\u7684\u54ac\u7740\u5979\u7684 \u5fc3\uff0c\u4eba\u7c7b\u7684\u60c5\u611f\uff0c\u60c5\u611f\u7684\u4eba\u7c7b\uff0c\u751f\u547d\u7684\u75db\u82e6\uff0c\u75db\u82e6\u7684\u751f\u547d\uff1a\u201c\u554a\uff0c\u4e3a\u4ec0\u4e48\u82cd\u5929\u5bf9\u6211\u8fd9\u6837\u6b8b \u5fcd\u2026\u2026\u201d \u3000\u3000\u5979\u54c0\u54c0\u5730\u54ed\u7740\uff0c\u773c\u6cea\u6cbe\u6e7f\u4e86\u5353\u957f\u537f\u7684\u80f8\u819b\uff0c\u4ed6\u4e0d\u6562\u79fb\u52a8\u4e00\u4e0b\uff0c\u4ed6\u77e5\u9053\u6b64\u523b\u87cb\u4f0f\u5728\u4ed6\u80f8\u819b \u4e0a\u7684\u5973\u5b69\u5b50\u7684\u75db\u82e6\uff0c\u4ed6\u4e5f\u9886\u53d7\u5f97\u5230\u5979\u7684\u60b2\u54c0\uff0c\u4ed6\u770b\u5230\u95e8\u5916\u5df2\u6709\u4e86\u4e00\u7ebf\u6de1\u6de1\u7684\u66d9\u5149\uff0c\u4f46\u662f\u665a\u98ce \u5f88\u51b7\uff0c\u4ed6\u4e0d\u77e5\u9053\u9ece\u660e\u524d\u4e3a\u4ec0\u4e48\u603b\u4f1a\u6709\u4e00\u6bb5\u66f4\u6df1\u7684\u9ed1\u6697\u548c\u66f4\u91cd\u7684\u5bd2\u610f\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u8ba9\u5979\u8737\u4f0f\u5728\u81ea\u5df1\u7684\u6000\u62b1\u91cc\uff0c\u9886\u5c1d\u7740\u8fd9\u6df7\u5408\u7740\u60b2\u54c0\u3001\u4ec7\u6068\u3001\u5bd2\u51b7\uff0c\u4f46\u5374\u53c8\u6709\u4e00\u4e1d\u6de1 \u6de1\u7684\u6e29\u99a8\u7684\u6ecb\u5473\u3002 \u3000\u3000\u6ca1\u6709\u4e00\u53e5\u5b89\u6170\u7684\u8bdd\uff0c\u4e5f\u6ca1\u6709\u4e00\u4e2a\u5b89\u6170\u7684\u52a8\u4f5c\uff0c\u56e0\u4e3a\u4ed6\u77e5\u9053\u8fd9\u4e00\u5207\u90fd\u662f\u591a\u4f59\u7684\uff0c\u4ed6\u53ea\u662f\u8f7b \u8f7b\u5730\u62e5\u504e\u7740\u5979\uff0c\u76f4\u5230\u5979\u54ed\u58f0\u5fae\u5f31\u4e0b\u6765\u3002 \u3000\u3000\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u73e0\u5149\u9eef\u6de1\u4e86\uff0c\u6653\u8272\u5374\u660e\u4eae\u4e86\u3002 \u3000\u3000\u5353\u957f\u537f\u611f\u89c9\u5230\u4ed6\u6000\u4e2d\u7684\u6e29\u747e\u54ed\u58f0\u5df2\u5bc2\uff0c\u9f3b\u606f\u5374\u6e10\u6e10\u6c89\u91cd\u8d77\u6765\uff0c\u4ed6\u4e0d\u77e5\u9053\u5979\u662f\u5426\u7761\u4e86\uff0c\u4f46 \u75db\u54ed\u4e4b\u540e\u7684\u5973\u5b50\uff0c\u5374\u5e38\u662f\u5bb9\u6613\u5165\u7761\u7684\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u4ecd\u672a\u79fb\u52a8\u4e00\u4e0b\u8eab\u8eaf\uff0c\u53ea\u662f\u7a0d\u4e3a\u95ed\u8d77\u773c\u775b\uff0c\u517b\u4e86\u4e00\u4f1a\u513f\u795e\u3002 \u3000\u3000\u6e05\u6668\u7684\u5927\u5730\u662f\u5bc2\u9759\u7684\uff0c\u6f6e\u6e7f\u800c\u6e05\u51b7\u7684\u5bd2\u98ce\uff0c\u867d\u7136\u6ca1\u6709\u5439\u5e72\u6811\u53f6\u4e0a\u7684\u671d\u9732\uff0c\u5374\u5439\u5e72\u4e86\u6e29\u747e \u7684\u773c\u6cea\u3002 \u3000\u3000\u4ed6\u770b\u5230\u4e86\u4ed6\u3002 \u3000\u3000\u4ed6\u611f\u89c9\u5230\u5979\u8eab\u8eaf\u7684\u52a8\u5f39\uff0c\u77e5\u9053\u5979\u9192\u4e86\uff0c\u4ed6\u5782\u4e0b\u5934\u2014\u2014\u4e8e\u662f\u4ed6\u4e5f\u770b\u5230\u4e86\u5979\u3002 \u3000\u3000\u8fd9\u4e00\u77a5\u7684\u611f\u89c9\u662f\u5343\u53e4\u4ee5\u6765\u6240\u6709\u7684\u8bcd\u4eba\u58a8\u5ba2\u90fd\u8d39\u5c3d\u5fc3\u673a\u60f3\u541f\u548f\u51fa\u6765\uff0c\u5374\u53c8\u65e0\u6cd5\u541f\u548f\u51fa\u6765 \u7684\u3002 \u3000\u3000\u56e0\u4e3a\u4e16\u95f4\u8fd8\u6ca1\u6709\u4efb\u4f55\u4e00\u79cd\u8bed\u8a00\u548c\u6587\u5b57\u80fd\u63cf\u53d9\u51fa\u8fd9\u4e00\u77a5\u7684\u5fae\u5999\u3002 \u3000\u3000\u90ce\u662f\u751f\u758f\u7684\u611f\u60c5\u7684\u6210\u719f\uff0c\u5206\u79bb\u7684\u611f\u60c5\u7684\u6295\u5408\uff0c\u8ff7\u4e71\u7684\u611f\u60c5\u7684\u4f9d\u5f52\u2014\u2014\u65e2\u50cf\u662f\u8e0f\u7834\u94c1\u978b\u7684 \u641c\u5bfb\u7740\u5728\u4e00\u77ac\u95f4\u7a81\u7136\u53d1\u73b0\u4e86\u81ea\u5df1\u6240\u8981\u5bfb\u627e\u7684\u4e1c\u897f\uff0c\u53c8\u50cf\u662f\u6d53\u96fe\u4e2d\u8ff7\u5931\u7684\u822a\u8239\u6597\u7136\u627e\u7740\u4e86\u822a\u884c \u7684\u65b9\u5411\u2014\u2014\u5979\u62ac\u8d77\u5934\uff0c\u5782\u4e0b\uff0c\u5782\u4e0b\u5934\uff0c\u62ac\u8d77\uff0c\u5fc3\u623f\u7684\u8df3\u52a8\u6df7\u5408\u4e86\u60b2\u68a6\u7684\u521d\u9192\uff0c\u5728\u8fd9\u4e00\u5239\u90a3 \u65f6\uff0c\u5979\u7684\u786e\u5df2\u5fd8\u8bb0\u4e86\u4e16\u95f4\u6240\u6709\u7684\u60b2\u54c0\uff0c\u867d\u53ea\u662f\u5239\u90a3\u4e4b\u95f4\uff0c\u4f46\u7b49\u5979\u5fc6\u8d77\u60b2\u54c0\u7684\u65f6\u5019\uff0c\u5979\u5374\u5df2\u9886 \u53d7\u8fc7\u4eba\u751f\u7684\u81f3\u5883\u3002 \u3000\u3000\u5979\u7f9e\u6da9\u7684\u5fae\u7b11\u4e00\u4e0b\uff0c\u4e0d\u5b89\u7684\u5750\u76f4\u4e86\u8170\u8eab\uff0c\u7136\u540e\u5e7d\u5e7d\u957f\u53f9\u4e00\u58f0\uff0c\u5f20\u4e86\u5f20\u5634\u5507\uff0c\u7728\u4e86\u7728\u773c \u775b\uff0c\u5374\u53c8\u4e0d\u77e5\u8be5\u8bf4\u4ec0\u4e48\u3002\u4f46\u662f\u6709\u5982\u6d77\u6f6e\u822c\u7684\u60b2\u54c0\u4e0e\u6124\u4ec7\uff0c\u5374\u53c8\u5df2\u56de\u5230\u5979\u5fc3\u91cc\u3002 \u3000\u3000\u5979\u7684\u773c\u775b\u53c8\u6e7f\u6da6\u4e86\uff0c\u957f\u957f\u7684\u776b\u6bdb\u50cf\u662f\u4e0d\u80dc\u8d1f\u62c5\u5927\u591a\u7684\u5fe7\u90c1\uff0c\u800c\u53c8\u6c89\u91cd\u5730\u5408\u4e86\u8d77\u6765\uff0c\u5979\u5408 \u7740\u773c\u6574\u4e86\u6574\u8863\u886b\uff0c\u7ad9\u4e86\u8d77\u6765\uff0c\u76ee\u5149\u4e00\u8f6c\uff0c\u671b\u5411\u571f\u5899\u7684\u7834\u6d1e\uff0c\u53c8\u81ea\u957f\u53f9\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5929\u4eae\u4e86\uff0c \u6211\u8be5\u8d70\u4e86\u201d\u201c\u5979\u7f13\u7f13\u56de\u8fc7\u5934\uff0c\u76ee\u5149\u7a81\u7136\u53d8\u5f97\u6e29\u67d4\u8bb8\u591a\uff1a\u201c\u6211\u4e0d\u8bf4\u4f60\u5927\u6982\u4e5f\u4f1a\u77e5\u9053\u6211\u8981\u5230\u54ea\u91cc \u53bb\uff0c\u6211\u2026\u2026\u6211\u8981\u53bb\u627e\u6211\u7684\u4ec7\u4eba\u2026\u2026\u4ec7\u4eba\uff0c\u4f60\u4e5f\u8be5\u8d70\u4e86\uff0c\u5929\u4eae\u4e86\uff0c\u5929\u4eae\u4e86\u2026\u2026\u201d \u3000\u3000\u5979\u68a6\u5453\u822c\u91cd\u590d\u7740\u81ea\u5df1\u7684\u8a00\u8bed\uff0c\u8f6c\u8eab\u8d70\u5230\u95e8\u53e3\uff0c\u4f3c\u4e4e\u8981\u8bc1\u5b9e\u4e00\u4e0b\u5916\u9762\u662f\u4e0d\u662f\u5929\u4eae\u4e86\u4e00\u6837\u3002 \u3000\u3000\u6668\u96fe\u4e5f\u6563\u4e86\uff0c\u4f46\u6668\u6101\u5374\u672a\u6563\uff0c\u5979\u518d\u6b21\u56de\u8fc7\u5934\uff0c\u51dd\u6ce8\u7740\u5353\u957f\u537f\u4e00\u773c\uff0c\u751f\u50cf\u662f\u5979\u5df2\u81ea\u77e5\u4ee5\u540e \u6c38\u8fdc\u4e5f\u89c1\u4e0d\u7740\u4ed6\u4f3c\u7684\uff0c\u56e0\u4e3a\u5979\u5df2\u62b1\u5b9a\u4e86\u51b3\u6b7b\u7684\u5fc3\uff0c\u53bb\u590d\u4ec7\uff0c\u6216\u53bb\u9001\u6b7b\uff01\u8fd9\u5176\u95f4\u7adf\u6ca1\u6709\u9009\u62e9\u7684 \u4f59\u5730\u3002 \u3000\u3000\u5353\u957f\u537f\u7f13\u7f13\u7ad9\u4e86\u8d77\u6765\uff0c\u4ed6\u9886\u53d7\u5f97\u5230\u5979\u8a00\u8bed\u4e0e\u76ee\u5149\u4e2d\u7684\u542b\u610f\uff0c\u8fd9\u662f\u4ed6\u5e73\u751f\u4ece\u672a\u9886\u53d7\u5230\uff0c\u751a \u81f3\u4ece\u672a\u68a6\u60f3\u5230\u7684\u611f\u89c9\u3002 \u3000\u3000\u76f4\u5230\u5979\u5df2\u7f13\u7f13\u8d70\u51fa\u95e8\u53e3\uff0c\u4ed6\u624d\u5982\u68a6\u521d\u9192\uff0c\u8131\u53e3\u547c\u9053\uff1a\u201c\u59d1\u5a18\uff01\u201d \u3000\u3000\u6e29\u747e\u811a\u6b65\u4e00\u987f\uff0c\u53e3\u8fc7\u5934\uff0c\u9ed8\u9ed8\u5730\u51dd\u6ce8\u7740\u4ed6\uff0c\u4ed6\u5b9a\u4e86\u5b9a\u795e\uff0c\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u9053\u90a3\u6e29\u5982\u7389\u5230\u54ea \u91cc\u53bb\u4e86\uff1f\u201d \u3000\u3000\u6e29\u747e\u7f13\u7f13\u6447\u4e86\u6447\u5934\uff0c\u5e7d\u5e7d\u53f9\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u77e5\u9053\uff0c\u4f46\u662f\u2026\u2026\u6211\u76f8\u4fe1\u6211\u4f1a\u627e\u5f97\u5230\u5979\u7684\uff0c\u4e00\u5b9a \u627e\u5f97\u7740\u5979\u7684\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u62a2\u6b65\u8d70\u5230\u5979\u8eab\u8fb9\uff0c\u9f13\u8d77\u52c7\u6c14\uff1a\u201c\u90a3\u4e48\u6211\u4eec\u5c31\u9f50\u53bb\u627e\u5427\uff01\u201d \u3000\u3000\u6e29\u747e\u5fae\u5fae\u4e00\u695e\uff1a\u201c\u6211\u4eec\u2026\u2026\u201d \u3000\u3000\u5353\u957f\u537f\u957f\u53f9\u4e00\u58f0\uff0c\u76ee\u5149\u6295\u5411\u82cd\u7a79\uff1a\u201c\u5bb6\u7236\u5bb6\u6bcd\u4e5f\u662f\u6b7b\u5728\u90a3\u6e29\u5982\u7389\u624b\u91cc\u7684\uff01\u201d \u3000\u3000\u6e29\u747e\u5168\u8eab\u4e00\u9707\uff0c\u5374\u542c\u5353\u957f\u537f\u53c8\u9053\uff1a\u201c\u5341\u4f59\u5e74\u524d\uff0c\u5728\u9ec4\u5c71\u59cb\u4fe1\u5cf0\u4e0b\u2014\u2014\u201d\u6e29\u747e\u201c\u5440\u201d\u5730\u4e00 \u58f0\uff0c\u8131\u53e3\u8f7b\u547c\u51fa\u6765\uff1a\u201c\u6211\u8bb0\u5f97\u4e86\u2026\u2026\u6211\u8bb0\u5f97\u4e86\u2026\u2026\u9ec4\u5c71\uff0c\u90a3\u662f\u5728\u9ec4\u5c71\u2026\u2026\u662f\u4f60\uff0c\u60f3\u4e0d\u5230\u662f \u4f60\u2026\u2026\u201d \u3000\u3000\u5979\u7f13\u7f13\u5782\u4e0b\u5934\uff0c\u4f3c\u4e4e\u5728\u53f9\u606f\u7740\u9020\u7269\u7684\u5fae\u5999\uff0c\u82e5\u6362\u4e86\u4e24\u65e5\u4ee5\u524d\uff0c\u8fd9\u4e24\u4eba\u539f\u672c\u662f\u4ec7\u654c\uff0c\u4f46\u6b64 \u523b\u2026\u2026 \u3000\u3000\u5353\u957f\u537f\u53c8\u53f9\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u6211\u8be5\u966a\u4f60\u4e00\u8d77\u53bb\u3002\u201d \u3000\u3000\u4ed6\u5782\u4e0b\u5934\uff0c\u5979\u62ac\u8d77\u5934\uff0c\u4e24\u4eba\u76ee\u5149\u76f8\u5bf9\uff0c\u5353\u957f\u537f\u5fcd\u4e0d\u4f4f\u8f7b\u8f7b\u63e1\u4f4f\u5979\u7684\u624b\uff0c\u4e24\u4eba\u5fc3\u610f\u76f8\u6d41\uff0c \u4f46\u89c9\u81ea\u5df1\u5fc3\u80f8\u4e4b\u95f4\u7a81\u7136\u751f\u51fa\u65e0\u6bd4\u7684\u52c7\u6c14\uff0c\u5353\u957f\u537f\u63a5\u7740\u53f9\u9053\uff1a\u201c\u4e3a\u4f60\u590d\u4ec7\uff0c\u4e5f\u4e3a\u6211\u590d\u4ec7\uff0c\u5509\u2014 \u2014\u53ea\u6015\u90a3\u6e29\u5982\u7389\u6b64\u523b\u5df2\u4e0d\u77e5\u8eb2\u5230\u54ea\u91cc\u53bb\u4e86\u3002\u201d \u3000\u3000\u4ed6\u8bed\u58f0\u4e00\u987f\uff0c\u6717\u58f0\u53c8\u9053\uff1a\u201c\u4f46\u6211\u4eec\u4e00\u5b9a\u627e\u5f97\u5230\u7684\uff0c\u662f\u5417\uff1f\u201d \u3000\u3000\u9ed8\u7136\u826f\u4e45\uff0c\u8fd9\u4e00\u53cc\u654c\u5ffe\u540c\u4ec7\u7684\u5c11\u5e74\u7537\u5973\uff0c\u4fbf\u9f50\u5730\u63a0\u51fa\u4e86\u8fd9\u6b8b\u8d25\u7684\u5bfa\u9662\uff0c\u63a0\u5411\u5929\u76ee\u5c71\u5dc5\uff0c \u90a3\u5c31\u662f\u6e29\u5982\u7389\u539f\u6765\u6b47\u606f\u4e4b\u5904\u3002 \u3000\u3000\u4ed6\u4eec\u867d\u7136\u6df1\u6df1\u77e5\u9053\u4ed6\u4eec\u7684\u5904\u5883\u662f\u5371\u9669\u7684\uff0c\u56e0\u4e3a\u5929\u56fd\u5c71\u5dc5\u4e0a\u9664\u4e86\u4e11\u4eba\u6e29\u5982\u7389\u4e4b\u5916\uff0c\u8fd8\u6709\u7740 \u8bb8\u591a\u4e2a\u6b66\u6797\u9ad8\u624b\uff0c\u8fd9\u4e9b\u4eba\u539f\u672c\u662f\u4e3a\u4e86\u8981\u5bf9\u4ed8\u4e00\u5fc3\u6765\u53c2\u4e0e\u5929\u76ee\u4e4b\u4f1a\u7684\u6b66\u6797\u7fa4\u8c6a\u7684\uff0c\u4f46\u6b64\u523b\u5374\u90fd \u53ef\u80fd\u53d8\u505a\u4ed6\u4eec\u590d\u4ec7\u7684\u969c\u788d\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u4eec\u5fc3\u4e2d\u5374\u5df2\u6beb\u65e0\u754f\u60e7\u4e4b\u5fc3\uff0c\u4f46\u53ea\u8981\u4ed6\u4eec\u4e24\u4eba\u80fd\u5728\u4e00\u5904\uff0c\u4fbf\u662f\u5929\u5927\u5371\u96be\u4e5f\u4e0d\u653e\u5728\u5fc3 \u4e0a\u3002 \u3000\u3000\u6b64\u523b\u671d\u9633\u5df2\u5347\uff0c\u5f69\u971e\u5c06\u6d88\u672a\u6d88\uff0c\u65ed\u65e5\u6620\u5f97\u6ee1\u5c71\u9752\u8471\u7684\u6728\u53f6\uff0c\u707f\u70c2\u4e00\u7247\u5149\u8f89\uff0c\u8f7b\u7075\u800c\u66fc\u5999 \u7684\u98de\u63a5\u5728\u6e29\u747e\u8eab\u65c1\u3002 \u3000\u3000\u53ea\u542c\u6e29\u747e\u5e7d\u5e7d\u53f9\u9053\uff1a\u201c\u4f60\u7684\u4ec7\u4eba\u9664\u4e86\u2026\u2026\u9664\u4e86\u5979\u4e4b\u5916\uff0c\u8fd8\u6709\u53e6\u4e00\u4e2a\u5c39\u51e1\uff0c\u5047\u5982\u2026\u2026\u5047 \u5982\u2026\u2026\u5509\uff0c\u6211\u4eec\u4e0a\u5c71\u627e\u4e0d\u5230\u5979\uff0c\u6211\u5c31\u966a\u4f60\u4e00\uff0c\u8d77\u53bb\u627e\u5c39\u51e1\uff0c\u4f46\u53ea\u6015\u2026\u2026\u201d \u3000\u3000\u5979\u53c8\u81ea\u4e00\u53f9\uff0c\u7ec8\u7a76\u6ca1\u6709\u8bf4\u51fa\u5931\u671b\u7684\u8bdd\uff0c\u5353\u957f\u537f\u70b9\u4e86\u70b9\u5934\uff0c\u5fc3\u4e2d\u7a81\u7136\u4e00\u52a8\uff1a\u201c\u6628\u591c\u4f60\u600e\u7684 \u90a3\u4e48\u5feb\u5c31\u56de\u6765\u4e86\uff0c\u96be\u9053\u5c39\u51e1\u5c31\u5728\u6b64\u5c71\u9644\u8fd1\u5417\uff1f\u201d \u3000\u3000\u6e29\u747e\u9053\uff1a\u201c\u6211\u6628\u591c\u6839\u672c\u6ca1\u6709\u8ddf\u53bb\uff0c\u56e0\u4e3a\u2026\u2026\u56e0\u4e3a\u6211\u5fc3\u91cc\u6709\u90a3\u4e48\u591a\u4e8b\uff0c\u6211\u53ea\u662f\u5728\u534a\u5c71\u559d\u4f4f \u90a3\u4e24\u4e2a\u5c11\u5e74\uff0c\u8ba9\u4ed6\u4eec\u81ea\u5df1\u8bf4\u51fa\u5c39\u51e1\u843d\u811a\u7684\u5730\u65b9\uff0c\u5f53\u65f6\u6211\u8fd8\u5728\u5947\u602a\uff0c\u660e\u660e\u4e00\u95ee\u5c31\u53ef\u77e5\u9053\u7684\u4e8b\uff0c \u59d1\u2014\u2014\u5979\u4e3a\u4ec0\u4e48\u8fd8\u8981\u6211\u8ddf\u53bb\uff0c\u56e0\u4e3a\u90a3\u4e24\u4e2a\u5c11\u5e74\u6839\u672c\u4e0b\u6562\u8bf4\u5047\u8bdd\u7684\uff0c\u4f46\u662f\u73b0\u5728\u6211\u5374\u77e5\u9053\u4e86\uff0c\u5979 \u4e0d\u8fc7\u53ea\u662f\u8981\u5c06\u6211\u652f\u5f00\u800c\u5df2\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u76ee\u5149\u4e00\u91cd\uff1a\u201c\u6628\u591c\u4f60\u82e5\u6ca1\u6709\u534a\u9014\u6298\u56de\u7684\u8bdd\uff0c\u53ea\u6015\u4e00\u4e00\u201d\u6e29\u747e\u5fe7\u90c1\u5730\u4e00\u7b11\uff1a\u201c\u6240\u4ee5 \u6211\u73b0\u5728\u76f8\u4fe1\u5929\u7f51\u6062\u6062\uff0c\u758f\u800c\u4e0d\u6f0f\u90a3\u53e5\u8bdd\u3002\u201d \u3000\u3000\u5929\u76ee\u5c71\u4e0a\uff0c\u6797\u6728\u82cd\u90c1\uff0c\u800c\u5165\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u8eab\u5f62\u5df2\u63a0\u8fc7\u767e\u5341\u4e08\u3002 \u3000\u3000\u6e29\u747e\u7a81\u53c8\u53f9\u9053\uff1a\u201c\u8fd9\u4e48\u4e00\u6765\uff0c\u53ea\u6015\u4f1a\u6709\u8bb8\u591a\u4e13\u7a0b\u8d76\u6765\u7684\u4eba\u8981\u5931\u671b\u4e86\uff0c\u5509\u2014\u2014\u8fd9\u603b\u7b97\u4ed6\u4eec \u5e78\u8fd0\uff0c\u8981\u4e0d\u7136\uff0c\u2014\u2014\u201d\u5353\u957f\u537f\u5251\u7709\u4e00\u8f69\uff0c\u7a81\u7136\u8131\u53e3\u9053\uff1a\u201c\u6709\u4e00\u53e5\u8bdd\uff0c\u6211\u4e0d\u77e5\u8be5\u4e0d\u8be5\u95ee\u4f60\uff1f\u201d \u3000\u3000\u6e29\u747e\u9053\uff1a\u201c\u4f60\u53ea\u7ba1\u8bf4\u597d\u4e86\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u53f9\u9053\uff1a\u201c\u51b3\u5200\u4f1a\u7684\u90a3\u4e9b\u95e8\u5f92\uff0c\u2014\u2014\u5509\uff0c\u4e0d\u95ee\u4e5f\u7f62\uff0c\u53cd\u6b63\u4e8b\u8fc7\u5883\u8fc1\u2014\u2014\u201d\u4ed6\u751f\u6015\u6e29 \u747e\u8bf4\u51fa\u4ee4\u4ed6\u4f24\u5fc3\u7684\u8bdd\u6765\uff0c\u56e0\u4e4b\u4ed6\u60f3\u6765\u60f3\u53bb\uff0c\u7eb5\u60f3\u95ee\u51fa\uff0c\u4f46\u8bdd\u5230\u53e3\u8fb9\u5374\u53c8\u4e0d\u5fcd\u8bf4\u51fa\u53e3\u6765\u4e86\u3002 \u3000\u3000\u54ea\u77e5\u6e29\u747e\u5374\u6b63\u8272\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u7528\u62c5\u5fc3\uff0c\u90a3\u4e9b\u4eba\u771f\u7684\u4e0d\u662f\u6211\u52a8\u624b\u6740\u7684\uff0c\u800c\u4e14\u4e5f\u4e0d\u662f\u6211\u90a3\u4e9b \u5a62\u5b50\u4eec\u6740\u7684\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u4e0d\u7981\u677e\u4e86\u53e3\u6c14\uff0c\u4ed6\u771f\u4e0d\u6562\u60f3\uff0c\u5047\u5982\u6e29\u747e\u8bf4\uff1a\u201c\u662f\u6211\u6740\u7684\u3002\u201d\u90a3\u4e48\u4ed6\u8be5\u600e\u4e48\u529e\uff1f \u3000\u3000\u4ed6\u5fae\u7b11\u4e00\u4e0b\uff0c\u5fcd\u4e0d\u4f4f\u53c8\u9053\uff1a\u201c\u5947\u602a\u7684\u662f\uff0c\u90a3\u4e9b\u4eba\u4e0d\u77e5\u7a76\u7adf\u662f\u8c01\u6740\u7684\uff1f\u201d \u3000\u3000\u6e29\u747e\u8f7b\u53f9\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u8fd9\u4e2a\u4eba\u4f60\u6c38\u8fdc\u4e5f\u4e0d\u4f1a\u731c\u51fa\u6765\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u53d8\u8272\u9053\uff1a\u201c\u662f\u8c01\uff1f\u201d \u3000\u3000\u6e29\u747e\u53f9\u9053\uff1a\u201c\u6211\u544a\u8bc9\u4f60\uff0c\u4f60\u4e5f\u4e0d\u4f1a\u76f8\u4fe1\uff0c\u53cd\u6b63\u4f60\u4ee5\u540e\u603b\u4f1a\u77e5\u9053\u7684\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u811a\u4e0b\u4e0d\u505c\uff0c\u5fc3\u5ff5\u6570\u8f6c\uff0c\u5374\u4ecd\u5fcd\u4e0d\u4f4f\u95f4\u9053\uff1a\u201c\u96be\u9053\u662f\u90a3\u4e07\u5999\u771f\u541b\u5c39\u51e1\uff1f\u201d \u3000\u3000\u6e29\u747e\u6447\u4e86\u6447\u5934\uff0c\u5353\u957f\u537f\u53c8\u9053\uff1a\u201c\u662f\u4ed6\u7684\u51e0\u4e2a\u5f92\u5f1f\uff1f\u201d \u3000\u3000\u6e29\u747e\u53c8\u6447\u4e86\u6447\u5934\u3002 \u3000\u3000\u5353\u957f\u537f\u5947\u9053\uff1a\u201c\u8fd9\u6211\u5012\u771f\u7684\u731c\u4e0d\u51fa\u4e86\uff0c\u53ea\u662f\u5947\u602a\u7684\u662f\uff0c\u6c5f\u6e56\u4e2d\u4e0d\u77e5\u8c01\u6709\u90a3\u4e48\u9738\u9053\u7684\u6697 \u5668\uff0c\u9664\u4e86\u8fd9\u4e9b\u4eba\u4e4b\u5916\uff0c\u6211\u5b9e\u5728\u60f3\u4e0d\u51fa\u8fd8\u6709\u8c01\u4e86\u3002\u201d \u3000\u3000\u6e29\u747e\u8f7b\u8f7b\u4e00\u7b11\uff1a\u201c\u90a3\u4e9b\u6697\u5668\u53eb\u505a\u65e0\u5f71\u795e\u9488\uff0c\u5012\u7684\u786e\u662f\u6211\u53d1\u51fa\u6765\u7684\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u5fc3\u5934\u4e00\u9707\uff0c\u500f\u7136\u987f\u4f4f\u8eab\u5f62\uff0c\u9762\u5bb9\u4ea6\u81ea\u5927\u53d8\uff0c\u98a4\u58f0\u9053\uff1a\u201c\u662f\u4f60\u2026\u2026\u4f60\u2026\u2026\u201d \u3000\u3000\u6e29\u747e\u53c8\u81ea\u8f7b\u7b11\u4e00\u4e0b\uff1a\u201c\u4e0d\u8fc7\u6211\u53d1\u51fa\u8fd9\u4e9b\u6697\u5668\u975e\u4f46\u4e0d\u662f\u4f24\u4eba\uff0c\u800c\u4e14\u8fd8\u662f\u6551\u4eba\u7684\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u7adf\u4e0d\u7981\u4e3a\u4e4b\u4e00\u6123\uff0c\u5927\u5947\u9053\uff1a\u201c\u6551\u4eba\u7684\uff1f\u6b64\u8bdd\u600e\u8bb2\uff1f\u201d \u3000\u3000\u6e29\u747e\u9053\uff1a\u201c\u8fd9\u8bdd\u8bf4\u6765\u5f88\u957f\uff0c\u6211\u6162\u6162\u518d\u544a\u8bc9\u4f60\uff0c\u603b\u4e4b\u4f60\u8981\u76f8\u4fe1\uff0c\u73b0\u5728\u6211\u2026\u2026\u6211\u518d\u4e5f\u4e0d\u4f1a\u9a97 \u4f60\u7684\u3002\u201d\u9762\u988a\u5fae\u5fae\u4e00\u7ea2\uff0c\u4f38\u51fa\u7389\u638c\uff0c\u9065\u6307\u524d\u65b9\uff0c\u9053\uff1a\u201c\u4f60\u770b\u5230\u6ca1\u6709\uff0c\u524d\u9762\u90a3\u7eff\u53f6\u724c\u574a\uff0c\u90a3\u5c31 \u662f\u672c\u6765\u51c6\u5907\u505a\u5929\u76ee\u4e4b\u4f1a\u7684\u5730\u65b9\u4e86\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u6123\u4e86\u534a\u664c\uff0c\u5fc3\u4e2d\u53cd\u590d\u60f3\u9053\uff1a\u201c\u2026\u2026\u73b0\u5728\u518d\u4e5f\u4e0d\u4f1a\u9a97\u4f60\u4e86\u2026\u2026\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\uff0c\u4e0d\u89c9\u7591\u5ff5\u987f\u6d88\uff0c\u62ac\u5934\u671b\u53bb\uff0c\u53ea\u89c1\u524d\u9762\u5c71\u836b\u9053\u4e0a\uff0c\u6797\u6728\u6e10\u758f\uff0c\u5c71\u52bf\u987f\u9634\uff0c\u4e00\u6761\u77f3 \u6881\u5c0f\u9053\uff0c\u7b14\u76f4\u901a\u5411\u5c71\u53bb\uff0c\u77f3\u6881\u5c71\u9053\u4e0a\u5374\u8d6b\u7136\u77d7\u7acb\u7740\u4e00\u4e2a\u9ad8\u7ea6\u4e94\u4e08\uff0c\u5bbd\u7ea6\u4e09\u4e08\uff0c\u867d\u662f\u6811\u679d\u642d\u6210 \u7684\uff0c\u4f46\u6c14\u52bf\u5374\u6781\u5dcd\u7136\u7684\u7eff\u53f6\u724c\u697c\u3002 \u3000\u3000\u724c\u697c\u4e24\u8fb9\uff0c\u6302\u7740\u4e24\u6761\u8840\u7ea2\u7684\u957f\u8054\uff0c\u4e0a\u9762\u5199\u7740\u6597\u5927\u7684\u5341\u516d\u4e2a\u5b7d\u5de2\u5927\u5b57\uff1a\u201c\u4ef0\u671b\u82cd\u7a79\u65e0\u7a77\uff0c \u4fef\u89c6\u6b66\u6797\u7fa4\u8c6a\uff01\u201d \u3000\u3000\u5bf9\u8054\u5e76\u4e0d\u5de5\u6574\uff0c\u4f46\u53e3\u6c14\u4e4b\u5927\uff0c\u5374\u662f\u5c11\u89c1\uff0c\u5353\u957f\u537f\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u8fd9\u60f3\u5fc5\u662f\u90a3\u6e29\u5982\u7389\u5199 \u7684\u3002\u201d \u3000\u3000\u6e29\u747e\u6447\u4e86\u6447\u5934\uff0c\u7a81\u7b11\u9053\uff1a\u201c\u5199\u5de8\u5e45\u5bf9\u8054\u662f\u8c01\uff1a\u53ea\u6015\u4f60\u4e5f\u4e07\u4e07\u731c\u4e0d\u5230\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u4e0d\u89c9\u53c8\u81ea\u5927\u5947\uff1a\u201c\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u6e29\u747e\u9053\uff1a\u201c\u5199\u8fd9\u526f\u5bf9\u8054\u7684\uff0c\u5c31\u662f\u5728\u6b66\u6797\u4e2d\u4eba\u7f18\u6781\u597d\u7684\u90a3\u4e2a\u795e\u5077\u4e54\u8fc1\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u5fc3\u5934\u4e00\u9707\uff1a\u201c\u96be\u9053\u5c31\u662f\u62ff\u7740\u4e09\u5e45\u4e66\u5377\uff0c\u5230\u5904\u626c\u8a00\u7684\u5de8\u5bcc\u795e\u5077\u4e54\u8fc1\uff0c\u8fd9\u5012\u771f\u662f\u4ee4\u4eba \u65e0\u6cd5\u610f\u6599\uff0c\u4ed6\u600e\u4e48\u4f1a\u4e0e\u6e29\u5982\u7389\u6709\u7740\u5e72\u7cfb\uff1f\u201d \u3000\u3000\u6e29\u747e\u6de1\u6de1\u4e00\u7b11\uff1a\u201c\u77e5\u4eba\u77e5\u9762\u4e0d\u77e5\u5fc3\uff0c\u4e16\u4eba\u7684\u5584\u6076\uff0c\u771f\u53eb\u4eba\u731c\u4e0d\u900f\uff0c\u6b66\u6797\u4e2d\u8c01\u90fd\u8bf4\u4e54\u8fc1\u662f \u4e2a\u597d\u4eba\uff0c\u5176\u5b9e\u2014\u2014\u54fc\uff0c\u8fd9\u4eba\u6211\u77e5\u9053\u5f97\u6700\u6e05\u695a\u3002\u201d \u3000\u3000\u539f\u6765\u5f53\u65f6\u4e11\u4eba\u6e29\u5982\u7389\u7acb\u4e0b\u51b3\u5fc3\uff0c\u8981\u5c06\u6b66\u6797\u7fa4\u8c6a\u90fd\u8bf1\u5230\u5929\u76ee\u5c71\u6765\uff0c\u5979\u60f3\u6765\u60f3\u53bb\uff0c\u4ec0\u4e48\u90fd\u4e0d \u7f3a\u5c11\uff0c\u5c31\u53ea\u5c11\u4e86\u4e00\u4e2a\u4f20\u8baf\u4e4b\u4eba\u3002 \u3000\u3000\u8981\u77e5\u9053\u6b64\u79cd\u60c5\u4e8b\uff0c\u82e5\u8981\u5728\u6c5f\u6e56\u4f20\u626c\u51fa\u6765\uff0c\u6e29\u5982\u7389\u5fc5\u662f\u4e0d\u80fd\u4eb2\u81ea\u51fa\u9762\uff0c\u56e0\u4e3a\u90a3\u4e48\u4e00\u6765\uff0c\u522b \u4eba\u4e00\u5b9a\u4f1a\u751f\u51fa\u7591\u60e7\u4e4b\u5fc3\uff0c\u800c\u8fd9\u4f20\u8baf\u4e4b\u4eba\uff0c\u4e0d\u4f46\u8981\u53e3\u624d\u4fbf\u6377\uff0c\u800c\u4e14\u8981\u5728\u6b66\u6797\u4e2d\u672c\u6709\u6781\u597d\u4eba\u7f18\uff0c \u4f7f\u5f97\u6b66\u6797\u4e2d\u4eba\u4e0d\u4f1a\u7591\u5fc3\u5979\u522b\u6709\u7528\u5fc3\u3002 \u3000\u3000\u5979\u60f3\u4e86\u8bb8\u4e45\uff0c\u4fbf\u7740\u4eba\u4e0b\u5c71\uff0c\u5230\u6b66\u6797\u4e2d\u5bfb\u4e86\u4e00\u4e2a\u7b26\u5408\u6b64\u79cd\u6761\u4ef6\u4e4b\u4eba\uff0c\u5176\u4e00\u4fbf\u662f\u4e54\u8fc1\uff0c\u53e6\u4e24 \u4eba\u5176\u4e2d\u4e4b\u4e00\u751f\u6027\u521a\u5f3a\uff0c\u672c\u6781\u4e0d\u6ee1\u6e29\u5982\u7389\u7684\u4e3a\u4eba\uff0c\u4e0a\u5f97\u5c71\u6765\uff0c\u4e0d\u5230\u4e00\u65e5\uff0c\u5c31\u88ab\u6e29\u5982\u7389\u7ed9\u5236\u6b7b\uff0c \u4e34\u6b7b\u4e4b\u9645\uff0c\u4ed6\u8fd8\u9a82\u4e0d\u7edd\u53e3\u3002 \u3000\u3000\u53e6\u4e00\u4eba\u4e5f\u4e0d\u613f\u505a\u6b64\u7b49\u5bb3\u4eba\u4e4b\u4e8b\uff0c\u53e3\u91cc\u867d\u7136\u7b54\u5e94\uff0c\u4f46\u591c\u95f4\u5374\u60f3\u4e58\u9699\u6e9c\u8d70\uff0c\u81ea\u7136\u4e5f\u88ab\u6e29\u5982\u7389 \u6740\u4e86\u706d\u53e3\uff0c\u800c\u90a3\u4e54\u8fc1\u4e0d\u4f46\u4e00\u53e3\u7b54\u5e94\uff0c\u4e14\u8fd8\u66ff\u6e29\u5982\u7389\u51fa\u4e86\u8bb8\u591a\u4e3b\u610f\uff0c\u4e8e\u662f\u4ed6\u4e34\u8d70\u4e4b\u9645\uff0c\u4e0d\u4f46\u5e26 \u4e86\u90a3\u4e09\u5e45\u4e66\u5377\uff0c\u800c\u4e14\u8fd8\u5e26\u8d70\u6e29\u5982\u7389\u7684\u4e00\u888b\u73e0\u5b9d\u3002 \u3000\u3000\u6e29\u747e\u5c06\u8fd9\u4e9b\u4e8b\u5bf9\u5353\u957f\u537f\u8bf4\u4e86\uff0c\u53ea\u542c\u5f97\u5353\u957f\u537f\u5251\u7709\u6012\u8f69\uff0c\u5207\u9f7f\u5927\u9a82\uff0c\u4ed6\u751f\u6027\u5fe0\u76f4\uff0c\u81ea\u7136\u60f3 \u4e0d\u5230\u4e16\u4e0a\u8fd8\u6709\u6b64\u7b49\u5351\u9119\u65e0\u803b\u4e4b\u4f46\u6e29\u747e\u5374\u6de1\u6de1\u7b11\u9053\uff1a\u201c\u8fd9\u79cd\u4eba\u6211\u770b\u5f97\u591a\u4e86\uff0c\u6709\u4e9b\u4eba\u5728\u6b66\u6797\u4e2d\u9887 \u6709\u4fa0\u540d\uff0c\u5176\u5b9e\u2014\u2014\u54fc\u54fc\uff0c\u7b49\u4f1a\u4f60\u5230\u4e86\u91cc\u9762\uff0c\u4f60\u5c31\u4f1a\u53d1\u73b0\u8bb8\u591a\u4f60\u6839\u672c\u4e0d\u4f1a\u60f3\u5230\u7684\u4e8b\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u957f\u53f9\u4e00\u58f0\uff0c\u968f\u7740\u5979\u63a0\u4eba\u90a3\u7eff\u53f6\u724c\u697c\uff0c\u524d\u884c\u5341\u6570\u4e08\uff0c\u5c71\u8def\u5ffd\u7136\u5206\u6210\u4e24\u6761\u5c94\u9053\uff0c\u4e00\u6761 \u9053\u53e3\u7acb\u7740\u4e00\u5757\u767d\u6768\u6728\u724c\uff0c\u4e0a\u9762\u5199\u9053\uff1a\u201c\u6613\u9053\u6613\u884c\uff0c\u8bf7\u541b\u884c\u6b64\u3002\u201d \u3000\u3000\u53e6\u4e00\u6761\u9053\u53e3\uff0c\u4e5f\u7acb\u7740\u4e00\u9762\u81ea\u6768\u6728\u724c\uff0c\u4e0a\u9762\u5199\u7740\u7684\u5374\u662f\uff1a\u201c\u82e5\u884c\u6b64\u9053\uff0c\u96be\u5982\u767b\u5929\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u5fc3\u4e2d\u4e00\u52a8\uff0c\u65b9\u81ea\u5fd6\u9053\uff1a\u201c\u8fd9\u60f3\u5fc5\u662f\u90a3\u6e29\u5982\u7389\u7528\u6765\u8003\u8f83\u522b\u4eba\u8f7b\u529f\u7684\u82b1\u6837\u3002\u201d \u3000\u3000\u5374\u89c1\u6e29\u747e\u811a\u4e0b\u4e0d\u505c\uff0c\u8eab\u5f62\u5982\u71d5\uff0c\u5df2\u81ea\u5f53\u5148\u5411\u90a3\u96be\u9053\u4e2d\u63a0\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u4e0d\u7981\u6697\u7b11\uff1a\u201c\u5979\u771f\u662f\u751f\u6027\u5014\u5f3a\u5f97\u5f88\uff0c\u6b64\u65f6\u6b64\u523b\uff0c\u5979\u5728\u6211\u9762\u524d\u7adf\u8fd8\u4e0d\u80af\u793a\u5f31\uff0c\u504f\u8981 \u8d70\u8fd9\u6761\u96be\u8d70\u7684\u8def\uff0c\u5509\u2014\u2014\u5176\u5b9e\u5979\u7559\u4e9b\u529b\u6c14\uff0c\u7528\u6765\u5bf9\u4ed8\u4ec7\u4eba\u5c82\u975e\u8981\u597d\u5f97\u591a\u3002\u201d \u3000\u3000\u4f46\u6b64\u523b\u6e29\u747e\u5df2\u63a0\u51fa\u6570\u4e08\uff0c\u6b63\u81ea\u56de\u5934\u5411\u4ed6\u62db\u624b\uff0c\u4ed6\u5fc3\u5ff5\u8f69\u5904\uff0c\u5374\u4e5f\u5df2\u968f\u540e\u63a0\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u5176\u5b9e\u4ed6\u81ea\u5df1\u751f\u6027\u4ea6\u662f\u5014\u5f3a\u65e0\u6bd4\uff0c\u82e5\u6362\u4e86\u4ed6\u81ea\u5df1\u9009\u62e9\uff0c\u4e5f\u5fc5\u4f1a\u9009\u62e9\u8fd9\u6761\u9053\u8def\u65e0\u7591\uff0c\u500f\u7136\u51e0 \u4e2a\u8d77\u843d\uff0c\u4ed6\u8eab\u5f62\u4e5f\u5df2\u63a0\u51fa\u5341\u6570\u4e08\uff0c\u53ea\u89c1\u8fd9\u6761\u9053\u4e0a\u5c71\u77f3\u55df\u5ce8\uff0c\u9053\u8def\u72ed\u7a84\uff0c\u679c\u771f\u662f\u96be\u884c\u65e0\u6bd4\uff0c\u4f46 \u662f\u4ed6\u8f7b\u529f\u5374\u6781\u4f73\u5999\uff0c\u6b64\u8def\u867d\u7136\u96be\u884c\uff0c\u4ed6\u5374\u6839\u672c\u6ca1\u6709\u653e\u5728\u5fc3\u4e0a\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u65b9\u81ea\u6697\u6652\uff1a\u201c\u8fd9\u79cd\u9053\u8def\u82e5\u4e5f\u7b97\u96be\u5982\u767b\u5929\u7684\u8bdd\uff0c\u90a3\u4e48\u4e16\u4e0a\u96be\u5982\u767b\u5929\u7684\u9053\u8def\u4e5f\u672a\u514d\u5927 \u591a\u4e86\u3002\u201d \u3000\u3000\u54ea\u77e5\u4ed6\u5fc3\u5ff5\u5c1a\u672a\u8f6c\u5b8c\uff0c\u524d\u9762\u7684\u9053\u8def\u7adf\u7136\u66f4\u52a0\u5e73\u5766\u8d77\u6765\uff0c\u4fbf\u662f\u8f7b\u529f\u6beb\u65e0\u6839\u57fa\u7684\u666e\u901a\u58ee\u6c49\uff0c \u53ea\u6015\u4e5f\u80fd\u8d70\u8fc7\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u4e0d\u7981\u53c8\u4e3a\u4e4b\u7591\u60d1\u8d77\u6765\uff0c\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u8fd9\u6761\u9053\u8def\u4e5f\u7b97\u505a\u96be\u884c\u7684\u8bdd\uff0c\u90a3\u4e48\u90a3\u8fb9\u2018\u6613 \u9053\u2019\u4e4b\u4e0a\uff0c\u5c82\u975e\u8def\u4e0a\u94fa\u7684\u90fd\u662f\u68c9\u82b1\uff1f\u201d \u3000\u3000\u6e29\u747e\u4e00\u7b11\u9053\uff1a\u201c\u4f60\u53c8\u731c\u9519\u4e86\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u4e00\u6115\uff0c\u5fc3\u5ff5\u52a8\u5904\uff0c\u7a81\u7136\u604d\u7136\u9053\uff1a\u201c\u539f\u6765\u8fd9\u53c8\u662f\u90a3\u6e29\u5982\u7389\u6545\u5f04\u7384\u865a\uff0c\u662f\u4e0d\u662f\uff1f\u6613\u9053 \u96be\u884c\uff0c\u96be\u9053\u6613\u884c\uff0c\u8fd9\u4e48\u4e00\u6765\uff0c\u6b66\u6797\u4e2d\u4eba\u5341\u4e2d\u6709\u4e5d\u90fd\u96be\u514d\u8981\u4e0a\u5979\u7684\u6076\u5f53\u3002\u201d \u3000\u3000\u8981\u77e5\u9053\u4ed6\u672c\u4e43\u806a\u660e\u7edd\u9876\u4e4b\u4eba\uff0c\u867d\u56e0\u6d89\u4e16\u4e0d\u6df1\uff0c\u518d\u52a0\u4ee5\u592d\u6027\u6b63\u76f4\uff0c\u662f\u4ee5\u5bf9\u4e8e\u4eba\u5fc3\u9669\u6076\u4e4b \u5904\uff0c\u4ed6\u5f80\u5f80\u770b\u4e0d\u751a\u6e05\uff0c\u4f46\u53ea\u8981\u522b\u4eba\u8be6\u52a0\u6307\u900f\uff0c\u4ed6\u7acb\u523b\u4fbf\u80fd\u6beb\u65e0\u56f0\u96be\u5730\u731c\u5230\u4e8b\u5b9e\u771f\u76f8\u3002 \u3000\u3000\u6e29\u747e\u679c\u7136\u9894\u9996\u9053\uff1a\u201c\u8fd9\u6b21\u4f60\u5012\u731c\u5bf9\u4e86\uff0c\u90a3\u6761\u6613\u9053\uff0c\u8868\u9762\u770b\u6765\u867d\u7136\u5e73\u5e73\u65e0\u5947\uff0c\u6781\u4e3a\u597d\u884c\uff0c \u5176\u5b9e\u5176\u4e2d\u5374\u662f\u6b65\u6b65\u5371\u673a\uff0c\u6ee1\u662f\u9677\u9631\uff0c\u83ab\u8bf4\u8f7b\u529f\u5e73\u5e38\u7684\u4eba\uff0c\u5c31\u7b97\u662f\u8f7b\u529f\u8f83\u9ad8\u7684\u6b66\u6797\u9ad8\u624b\uff0c\u82e5\u4e0d \u7559\u610f\uff0c\u4e5f\u96be\u514d\u4e2d\u4f0f\uff0c\u5176\u4e2d\u5c24\u4ee5\u90a3\u767e\u6b65\u7559\u6c99\u3001\u5341\u4e08\u6bd2\u6cb3\u4e24\u4e2a\u5730\u65b9\uff0c\u4f60\u53ea\u8981\u771f\u6c14\u7a0d\u6709\u4e0d\u7ee7\uff0c\u7acb\u65f6 \u4fbf\u662f\u706d\u9876\u4ea1\u9b42\u4e4b\u7978\u3002\u201d \u3000\u3000\u5979\u8bed\u58f0\u4e00\u987f\uff0c\u53c8\u9053\uff1a\u201c\u5230\u6b64\u95f4\u6765\u7684\u6b66\u6797\u8c6a\u58eb\uff0c\u591a\u534a\u4e3a\u4e86\u8981\u593a\u5b9d\u85cf\uff0c\u82e5\u975e\u771f\u6b63\u827a\u9ad8\u80c6\u5927\u7684 \u4eba\uff0c\u8c01\u4e5f\u4e0d\u613f\u591a\u8d39\u529b\u6c14\uff0c\u81ea\u7136\u90fd\u8981\u8d70\u90a3\u6761\u6613\u9053\uff0c\u4e8e\u662f\u4ed6\u4eec\u4e0d\u4f46\u4e0a\u5f53\uff0c\u800c\u4e14\u8fd8\u5f97\u9001\u547d\uff0c\u81f3\u4e8e\u90a3 \u4e9b\u6562\u8d70\u96be\u9053\u7684\u4eba\uff0c\u6b66\u529f\u5b9a\u5fc5\u751a\u9ad8\uff0c\u4e00\u4e9b\u666e\u901a\u9677\u9631\u672a\u5fc5\u80fd\u96be\u5f97\u5012\u4ed6\u4eec\uff0c\u6240\u4ee5\u8fd9\u6761\u96be\u9053\u4e0a\u53cd\u800c\u4ec0 \u4e48\u9677\u9631\u4e5f\u6ca1\u6709\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u6697\u53f9\u4e00\u58f0\uff0c\u5fd6\u9053\uff1a\u201c\u8fd9\u6e29\u5982\u7389\u7528\u5fc3\u5f53\u771f\u662f\u6076\u6bd2\u65e0\u6bd4\uff0c\u82e5\u975e\u6211\u5148\u6765\u4e00\u8d9f\uff0c\u63a2\u51fa\u6b64\u95f4 \u771f\u76f8\uff0c\u90a3\u771f\u4e0d\u77e5\u6709\u591a\u5c11\u6b66\u6797\u8c6a\u58eb\u4f1a\u846c\u8eab\u6b64\u5730\u3002\u201d \u3000\u3000\u5fc3\u5ff5\u4e00\u8f6c\uff0c\u53c8\u5fd6\u9053\uff1a\u201c\u2018\u8fd1\u6731\u8005\u8d64\uff0c\u8fd1\u58a8\u8005\u9ed1\u3002\u2019\u6e29\u747e\u81ea\u5e7c\u53ca\u957f\uff0c\u90fd\u53d7\u7740\u8fd9\u79cd\u9b54\u5934\u7684\u718f \u9676\uff0c\u884c\u4e8b\u81ea\u7136\u4e5f\u96be\u514d\u4f1a\u6709\u4e9b\u53e4\u602a\uff0c\u751a\u81f3\u4f1a\u6709\u4e9b\u51b7\u9177\uff0c\u5509\u2014\u2014\u4f46\u613f\u5979\u4ee5\u540e\u548c\u6211\u4e00\u8d77\u4f1a\u2014\u2014\u201d\u4e00 \u5ff5\u81f3\u6b64\uff0c\u4ed6\u5fc3\u4e2d\u4e0d\u7981\u5fae\u5fae\u4e00\u70ed\uff0c\u4e0d\u7981\u53c8\u81ea\u6697\u7b11\u81ea\u5df1\uff0c\u672a\u514d\u5c06\u4e8b\u60c5\u60f3\u5f97\u592a\u8fdc\u4e86\u4e9b\u3002 \u3000\u3000\u62ac\u5934\u671b\u5904\uff0c\u53ea\u89c1\u524d\u9762\u53c8\u5230\u4e86\u9053\u8def\u5c3d\u5934\uff0c\u5c3d\u5934\u5904\u53c8\u6709\u4e00\u5ea7\u7eff\u53f6\u724c\u697c\uff0c\u6ca1\u6709\u5bf9\u8054\uff0c\u5374\u6709\u4e00\u65b9 \u6a2a\u533e\uff0c\u4e0a\u9762\u4ea6\u5199\u7740\u4e09\u4e2a\u638c\u5de2\u5927\u5b57\uff1a\u201c\u7b2c\u4e00\u5173\u3002\u201d \u3000\u3000\u6e29\u747e\u5374\u5df2\u6084\u7136\u7acb\u5728\u724c\u697c\u4e4b\u4e0b\uff0c\u5e26\u7740\u4e00\u4e1d\u5fae\u542b\u5fe7\u90c1\u7684\u7b11\u5bb9\u671b\u7740\u4ed6\u3002 \u3000\u3000\u4ed6\u9762\u988a\u4e00\u7ea2\uff0c\u63a0\u4e86\u8fc7\u53bb\uff0c\u53e3\u4e2d\u9053\uff1a\u201c\u4f60\u5012\u5148\u5230\u4e86\u3002\u201d \u3000\u3000\u6e29\u747e\u542b\u7b11\u9053\uff1a\u201c\u6211\u89c1\u4f60\u5fc3\u91cc\u597d\u50cf\u7a81\u7136\u60f3\u8d77\u4ec0\u4e48\u5fc3\u601d\u4f3c\u7684\uff0c\u5374\u4e0d\u77e5\u4f60\u5728\u60f3\u7740\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5979\u79cb\u6ce2\u4e00\u8f6c\uff0c\u7a81\u7136\u89c1\u5230\u5353\u957f\u537f\u773c\u4e2d\u7684\u773c\u8272\uff0c\u4e24\u988a\u4ea6\u4e0d\u7981\u4e00\u7ea2\uff0c\u542b\u7b11\u9ed8\u9ed8\u7684\u5782\u4e0b\u5934\u53bb\u3002 \u3000\u3000\u8fd9\u4e00\u53cc\u5c11\u5e74\u7537\u5973\u5fc3\u4e2d\u672c\u6765\u867d\u90fd\u662f\u5fe7\u95f7\u54c0\u75db\uff0c\u4f46\u8fd9\u534a\u65e5\u4e4b\u95f4\uff0c\u5f7c\u6b64\u5374\u53c8\u90fd\u7ed9\u4e86\u5bf9\u65b9\u65e0\u6bd4\u7684 \u6170\u85c9\uff0c\u662f\u4ee5\u8fd9\u4e24\u4eba\u6b64\u523b\u9762\u4e0a\u624d\u90fd\u6709\u4e00\u4e9b\u6de1\u6de1\u7b11\u5bb9\uff0c\u4f46\u7eb5\u7136\u5982\u6b64\uff0c\u4ed6\u4eec\u7684\u7b11\u5bb9\u5374\u4e5f\u4ecd\u975e\u5f00\u6717 \u7684\u3002 \u3000\u3000\u53ea\u542c\u6e29\u747e\u5f90\u7f13\u9053\uff1a\u201c\u8fd9\u91cc\u9762\u4e00\u5171\u5206\u6210\u4e09\u5173\uff0c\u7b2c\u4e00\u5173\u91cc\u9762\u6709\u4e09\u5ea7\u64c2\u53f0\uff0c\u7b2c\u4e8c\u5173\u91cc\u9762\u662f\u7f57\u6c49 \u9999\u3001\u6885\u82b1\u6869\u4e00\u7c7b\u7684\u529f\u592b\uff0c\u7b2c\u4e09\u5173\u5374\u6b63\u662f\u91d1\u5200\u6362\u638c\u3001\u4e94\u832b\u795e\u73e0\u3001\u9694\u5c71\u6253\u725b\u4e4b\u7c7b\u5185\u5bb6\u529f\u592b\u7684\u8003\u8f83 \u4e4b\u5730\u4e86\uff0c\u8fc7\u4e86\u8fd9\u4e09\u5173\uff0c\u624d\u662f\u6211\u2014\u2014\u201d\u5979\u8bed\u58f0\u987f\u5904\u53c8\u81ea\u9762\u988a\u4e00\u7ea2\uff0c\u8f7b\u58f0\u9053\uff1a\u201c\u53ea\u662f\u8fd9\u4e9b\u4e1c\u897f\uff0c \u73b0\u5728\u6211\u90fd\u4e0d\u7ba1\u4e86\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u53f9\u9053\uff1a\u201c\u5149\u53ea\u8fd9\u4e9b\u4e1c\u897f\uff0c\u60f3\u5fc5\u5c31\u4e0d\u77e5\u82b1\u8d39\u4e86\u591a\u5c11\u4eba\u529b\u7269\u529b\uff0c\u8fd9\u6e29\u5982\u7389\u5f53\u771f\u662f\u751f\u6027 \u5947\u5f02\u5df2\u6781\uff0c\u5979\u8bbe\u4e0b\u8fd9\u4e9b\u4e1c\u897f\uff0c\u7ade\u53ea\u662f\u4e3a\u4e86\u8a00\u4eba\u800c\u5df2\uff0c\u5509\u2014\u2014\u6211\u542c\u90a3\u5c39\u51e1\u66fe\u8bf4\u8d77\u8fd9\u91cc\u6bcf\u4e00\u5904\u90fd \u5185\u4f0f\u6076\u6bd2\u9677\u9631\uff0c\u4e3b\u64c2\u7684\u4eba\u4e5f\u90fd\u662f\u4e9b\u6076\u6bd2\u7684\u9b54\u5934\uff0c\u6b64\u523b\u90a3\u4e9b\u4eba\u5374\u53c8\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u6e29\u747e\u9053\uff1a\u201c\u8bf7\u6765\u4e3b\u64c2\u7684\u4eba\uff0c\u6709\u7684\u8fd8\u672a\u6765\uff0c\u6709\u7684\u6b64\u523b\u53ea\u6015\u8fd8\u5728\u91cc\u9762\u7761\u89c9\u2014\u2014\u201d\u5979\u8bed\u58f0\u672a \u4e86\uff0c\u7eff\u53f6\u724c\u697c\u7a81\u7136\u4f20\u6765\u58f0\u5a07\u547c\uff1a\u201c\u5c0f\u59d0\u5728\u8fd9\u91cc\uff01\u201d \u3000\u3000\u5353\u957f\u537f\u3001\u6e29\u747e\u84e6\u5730\u4e00\u60ca\uff0c\u56de\u9996\u671b\u53bb\uff0c\u53ea\u89c1\u8fd9\u724c\u697c\u8fb9\uff0c\u4e00\u5ea7\u4f9d\u5c71\u642d\u5efa\u7684\u51cc\u7a7a\u7af9\u9601\u4e4b\u5185\uff0c\u500f \u7136\u63a0\u4e0b\u4e09\u6761\u4eba\u5f71\uff0c\u6b63\u662f\u90a3\u4e9b\u7a7f\u7740\u4e00\u8eab\u8f7b\u7eb1\u7f57\u886b\u7684\u5782\u9aeb\u5c11\u5973\uff0c\u60ca\u9e3f\u822c\u63a0\u5411\u6e29\u747e\uff0c\u516d\u9053\u79cb\u6ce2\u8f6c \u5904\uff0c\u7a81\u7136\u671b\u89c1\u5353\u957f\u537f\uff0c\u9762\u5bb9\u4e00\u53d8\uff0c\u8eab\u5f62\u9aa4\u987f\uff0c\u50cf\u662f\u7a81\u7136\u88ab\u9489\u7262\u5728\u5730\u4e0a\u4f3c\u7684\uff0c\u60ca\u5f97\u8bf4\u4e0d\u51fa\u8bdd \u6765\u3002 \u3000\u3000\u5979\u4eec\u518d\u4e5f\u60f3\u4e0d\u5230\u81ea\u5df1\u7684\u5c0f\u59d0\u4f1a\u548c\u8fd9\u7384\u886b\u5c11\u5e74\u5982\u6b64\u4eb2\u5462\u5730\u7ad9\u5728\u4e00\u5904\uff0c\u5353\u957f\u537f\u76ee\u5149\u671b\u5904\uff0c\u53ea \u89c1\u8fd9\u4e09\u4e2a\u5c11\u5973\u6b63\u662f\u6628\u591c\u5f80\u4e34\u5b89\u57ce\u4e2d\u9001\u5e16\u4e4b\u4eba\uff0c\u5f53\u4e0b\u5251\u7709\u4e00\u8f69\uff0c\u65b9\u5f85\u53d1\u8bdd\uff0c\u6e29\u747e\u5374\u5df2\u51b7\u51b7\u95ee \u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u8fd9\u4e09\u4e2a\u7ea2\u886b\u5c11\u5973\u76ee\u5149\u76f8\u5bf9\uff0c\u55eb\u5685\u534a\u664c\uff0c\u5176\u4e2d\u6709\u4e00\u4e2a\u5e74\u9f84\u8f83\u957f\u7684\u65b9\u81ea\u671f\u827e\u7740\u9053\uff1a\u201c\u90a3\u4f4d\u5c11 \u6797\u6d3e\u7684\u5927\u548c\u5c1a\uff0c\u4e0d\u77e5\u4e3a\u4ec0\u4e48\u4e8b\u5f97\u7f6a\u4e86\u5343\u91cc\u660e\u9640\u548c\u65e0\u5f71\u7f57\u5239\u90a3\u80a1\u4eba\uff0c\u4ed6\u4eec\u4eca\u5929\u65e9\u4e0a\u5929\u65b9\u9ece\u660e\uff0c \u5c31\u903c\u7740\u90a3\u5927\u548c\u5c1a\u548c\u4ed6\u4eec\u52a8\u624b\u2014\u2014\u201d\u6e29\u747e\u67f3\u7709\u8f7b\u76b1\uff1a\u201c\u73b0\u5728\u600e\u6837\uff1f\u201d \u3000\u3000\u8fd9\u5c11\u5973\u63a5\u9053\uff1a\u201c\u5a62\u5b50\u4eec\u51fa\u6765\u770b\u7684\u65f6\u5019\uff0c\u5927\u548c\u5c1a\u6b63\u548c\u90a3\u65e0\u5f71\u7f57\u5239\u5728\u90a3\u7b2c\u4e8c\u9635\u7f57\u6c49\u9999\u4e0a\u52a8 \u624b\uff0c\u90a3\u5927\u548c\u5c1a\u8eab\u6750\u867d\u7136\u53c8\u80d6\u53c8\u5927\uff0c\u4f46\u8f7b\u529f\u5374\u4e0d\u9519\uff0c\u4e24\u4eba\u6253\u4e86\u4e00\u4f1a\u513f\uff0c\u773c\u770b\u7740\u5927\u548c\u5c1a\u5c31\u8981\u5f97 \u80dc\uff0c\u54ea\u77e5\u90a3\u5343\u91cc\u660e\u9a7c\u5374\u7a81\u7136\u559d\u4f4f\u4e86\u4ed6\u4eec\uff0c\u8bf4\u662f\u4e0d\u5206\u80dc\u8d1f\uff0c\u4e0d\u8981\u518d\u6253\u4e86\uff0c\u5374\u6362\u4e86\u53e6\u4e00\u4e2a\u53eb\u94c1\u5251 \u7eaf\u9633\u7684\uff0c\u5c31\u662f\u90a3\u7a7f\u7740\u4e00\u8eab\u516b\u5366\u8863\u7684\u9053\u58eb\uff0c\u5728\u6885\u82b1\u6869\u4e0a\u548c\u4ed6\u4ea4\u8d77\u624b\u6765\u3002\u201d \u3000\u3000\u6e29\u747e\u51b7\u201c\u54fc\u201d\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u8f66\u8f6e\u6218\uff01\u201d \u3000\u3000\u5353\u957f\u537f\u51b7\u7b11\u9053\uff1a\u201c\u771f\u662f\u65e0\u803b\u3002\u201d \u3000\u3000\u5374\u542c\u90a3\u5c11\u5973\u53c8\u9053\uff1a\u201c\u6211\u4eec\u672c\u6765\u8fd8\u4ee5\u4e3a\u4ed6\u4eec\u662f\u5728\u95f9\u7740\u73a9\u7684\uff0c\u54ea\u77e5\u540e\u6765\u89c1\u4ed6\u4eec\u7adf\u8d8a\u6253\u8d8a\u51f6\uff0c \u771f\u50cf\u662f\u8981\u62fc\u547d\u7684\u6837\u5b50\uff0c\u5fc3\u91cc\u53c8\u6015\uff0c\u53c8\u505a\u4e0d\u5f97\u4e3b\uff0c\u5c31\u8dd1\u91cc\u53bb\u7980\u62a5\uff0c\u54ea\u77e5\u79df\u59d1\u59d1\u4e0d\u5728\uff0c\u5c0f\u59d0\u4e5f\u4e0d \u5728\uff0c\u6211\u4eec\u8fd9\u4e0b\u624d\u771f\u7684\u614c\u4e86\u624b\u811a\uff0c\u4e0d\u77e5\u9053\u8be5\u600e\u4e48\u529e\u624d\u597d\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u3001\u6e29\u747e\u5bf9\u671b\u4e86\u4e00\u773c\uff0c\u5fc3\u4e2d\u5404\u81ea\u5fd6\u9053\uff1a\u201c\u6e29\u5982\u7389\u4e0d\u5728\uff0c\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u201d \u3000\u3000\u6e29\u747e\u9762\u5bb9\u5927\u53d8\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u8bf4\u4e0b\u53bb\uff01\u201d \u3000\u3000\u54ea\u5c11\u5973\u89c1\u5230\u6e29\u747e\u9762\u4e0a\u7684\u795e\u8272\uff0c\u50cf\u662f\u5341\u5206\u5bb3\u6015\uff0c\u5979\u4eec\u4ece\u6765\u4e5f\u6ca1\u6709\u89c1\u5230\u81ea\u5df1\u7684\u5c0f\u59d0\u6709\u5982\u6b64\u795e \u8272\uff0c\u76ee\u5149\u4e00\u5782\uff0c\u65b9\u81ea\u63a5\u9053\uff1a\u201c\u6211\u4eec\u4ece\u91cc\u9762\u8dd1\u51fa\u6765\u7684\u65f6\u5019\uff0c\u4ed6\u4eec\u5df2\u6362\u5230\u7b2c\u4e09\u5173\u91cc\u52a8\u624b\u4e86\uff0c\u4e00\u4e2a \u53eb\u505a\u4ec0\u4e48\u4e94\u4e01\u795e\u5c06\u7684\u5927\u4e2a\u5b50\uff0c\u6b63\u548c\u90a3\u5927\u548c\u5c1a\u5728\u91d1\u5200\u6362\u638c\u9635\u91cc\u52a8\u7740\u624b\uff0c\u90a3\u5927\u548c\u5c1a\u5df2\u7ecf\u7d2f\u5f97\u6c14\u5598 \u54bb\u54bb\uff0c\u6ee1\u5934\u5927\u6c57\uff0c\u4f46\u62f3\u811a\u6253\u51fa\u6765\uff0c\u4ecd\u7136\u6c14\u52bf\u864e\u864e\uff0c\u5a01\u98ce\u516b\u9762\uff0c\u53ea\u662f\u90a3\u4e94\u4e01\u795e\u5c06\u6b66\u529f\u4e5f\u4e0d\u5f31\uff0c \u4e00\u65f6\u4e4b\u95f4\uff0c\u4e5f\u6ca1\u6709\u80dc\u8d1f\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u6697\u53f9\u4e00\u58f0\uff0c\u5fd6\u9053\uff1a\u201c\u770b\u6765\u5c11\u6797\u4e00\u6d3e\u6240\u79f0\u96c4\u6b66\u6797\uff0c\u786e\u975e\u5076\u7136\uff0c\u8fd9\u591a\u4e8b\u5934\u9640\u4e0d\u8fc7\u662f\u4e2a \u7b2c\u4e8c\u4ee3\u5f1f\u5b50\uff0c\u6b66\u529f\u5374\u5df2\u5982\u6b64\uff0c\u5c31\u53ea\u8bba\u8fd9\u6c14\u529b\u4e4b\u957f\uff0c\u5c31\u7edd\u975e\u5e38\u4eba\u80fd\u53ca\u4e86\u3002\u201d \u3000\u3000\u4ed6\u5374\u4e0d\u77e5\u9053\u591a\u4e8b\u5934\u9640\u4e00\u8eab\u7ae5\u5b50\u529f\u5341\u4e09\u592a\u4fdd\u6a2a\u7ec3\uff0c\u6570\u5341\u5e74\u672a\u66fe\u95f4\u65ad\u4e00\u65e5\uff0c\u6c14\u529b\u4e4b\u957f\uff0c\u6b63\u662f \u4ed6\u7684\u770b\u5bb6\u672c\u9886\u3002 \u3000\u3000\u8fd9\u5ff5\u5934\u5728\u4ed6\u5fc3\u4e2d\u4e00\u95ea\u800c\u8fc7\uff0c\u5374\u542c\u90a3\u7ea2\u88f3\u5c11\u5973\u63a5\u9053\uff1a\u201c\u6211\u4eec\u90fd\u77e5\u9053\u8fd9\u7b2c\u4e09\u9635\u91cc\u9762\u7684\u6b66\u529f\uff0c \u90fd\u662f\u51f6\u9669\u65e0\u6bd4\uff0c\u4e00\u4e2a\u4e0d\u597d\uff0c\u5c31\u7b97\u6b66\u529f\u518d\u597d\u7684\u4eba\uff0c\u4e5f\u5f97\u8840\u6e85\u5f53\u5730\uff0c\u90a3\u4e9b\u4eba\u4e0d\u662f\u7956\u59d1\u59d1\u8bf7\u6765\uff0c\u5c31 \u662f\u5c0f\u59d0\u8bf7\u6765\u7684\uff0c\u8c01\u53d7\u4e86\u4f24\u90fd\u4e0d\u597d\uff0c\u4f46\u53c8\u6ca1\u6709\u529e\u6cd5\u963b\u6b62\u4ed6\u4eec\u3002\u60f3\u6765\u60f3\u53bb\uff0c\u5a62\u5b50\u4eec\u53ea\u5f97\u5206\u5934\u51fa\u6765 \u627e\uff0c\u60f3\u4e0d\u5230\u5374\u5728\u8fd9\u91cc\u9047\u7740\u5c0f\u59d0\u3002\u201d \u3000\u3000\u76ee\u5149\u5fae\u62ac\uff0c\u5077\u5077\u779f\u4e86\u5353\u957f\u537f\u4e00\u773c\uff0c\u76ee\u5149\u4e2d\u4ecd\u6ee1\u542b\u60ca\u8be7\u4e4b\u610f\u3002 \u3000\u3000\u6e29\u747e\u5fc3\u5ff5\u4e00\u8f6c\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u59d1\u59d1\u7684\u786e\u4e0d\u5728\u7eff\u7af9\u8f69\u91cc\u5417\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5973\u8fde\u5fd9\u9894\u9996\u9053\uff1a\u201c\u6ca1\u6709\uff0c\u5a62\u5b50\u4eec\u2026\u201d \u3000\u3000\u6e29\u747e\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u4eec\u53ef\u770b\u6e05\u695a\u4e86\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5973\u9053\uff1a\u201c\u5a62\u5b50\u4eec\u4e0d\u4f46\u770b\u6e05\u695a\u4e86\uff0c\u800c\u4e14\u8fd8\u5728\u522b\u7684\u5730\u65b9\u627e\u4e86\u4e00\u5708\uff0c\u5374\u4e5f\u6ca1\u6709\u627e\u5230\u3002\u201d \u3000\u3000\u6e29\u747e\u201c\u55ef\u201d\u4e86\u4e00\u58f0\uff0c\u53c8\u9053\uff1a\u201c\u90a3\u65e0\u6839\u5927\u5e08\u6b64\u523b\u8fd8\u5728\u52a8\u624b\u5417\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5973\u8fde\u5fd9\u9053\uff1a\u201c\u5a62\u5b50\u4eec\u79bb\u5f00\u624d\u4e0d\u8fc7\u4e00\u4f1a\u513f\uff0c\u5a62\u5b50\u4eec\u79bb\u5f00\u7684\u65f6\u5019\uff0c\u4ed6\u4eec\u6253\u5f97\u6b63\u5389\u5bb3 \u54e9\u3002\u201d \u3000\u3000\u76ee\u5149\u8f7b\u62ac\uff0c\u53c8\u5fcd\u4e0d\u4f4f\u5077\u5077\u779f\u4e86\u5353\u957f\u537f\u4e00\u773c\u3002 \u3000\u3000\u5353\u957f\u537f\u4f46\u89c9\u9762\u988a\u5fae\u5fae\u4e00\u7ea2\uff0c\u5374\u542c\u6e29\u747e\u8f7b\u8f7b\u4e00\u53f9\uff0c\u8bf4\u9053\uff1a\u201c\u65e0\u6839\u5927\u5e08\u65e2\u7136\u5728\u91cc\u9762\u52a8\u624b\uff0c\u6211 \u4eec\u81ea\u7136\u8981\u53bb\u770b\u770b\u4ed6\u7684\uff0c\u662f\u5417\uff1f\u201d \u3000\u3000\u5353\u957f\u537f\u8fde\u5fd9\u9894\u9996\u9053\uff1a\u201c\u6b63\u662f\u3002\u201d \u3000\u3000\u5fc3\u4e2d\u5374\u53c8\u4e0d\u7981\u6697\u81ea\u611f\u53f9\uff1a\u201c\u8fd9\u5341\u6570\u5e74\u6765\uff0c\u6e29\u747e\u548c\u6e29\u5982\u7389\u671d\u5915\u76f8\u5904\uff0c\u4e0d\u8bf4\u522b\u7684\uff0c\u5c31\u8fde\u8bf4\u8bdd \u90fd\u548c\u6e29\u5982\u7389\u6709\u4e9b\u76f8\u4f3c\uff0c\u6700\u540e\u603b\u559c\u6b22\u52a0\u4e2a\u2018\u662f\u5417\u2019\uff0c\u5509\u2014\u2014\u5979\u5728\u5982\u6b64\u73af\u5883\u4e4b\u4e2d\u751f\u957f\uff0c\u6027\u60c5\u7eb5\u7136 \u6709\u4e9b\u53e4\u602a\uff0c\u53c8\u600e\u5730\u602a\u5f97\u4e86\u5979\u3002\u201d \u3000\u3000\u8fd9\u7b2c\u4e00\u9053\u7eff\u53f6\u724c\u697c\u4e4b\u540e\uff0c\u9664\u4e86\u90a3\u4f9d\u5c71\u51cc\u7a7a\u800c\u5efa\u7684\u7af9\u9601\u4e4b\u5916\uff0c\u9053\u8fb9\u8fd8\u6709\u51e0\u5904\u7af9\u68da\uff0c\u68da\u5185\u684c \u6905\u4e95\u7136\uff0c\u770b\u6765\u60f3\u5fc5\u662f\u4e3a\u4e86\u4efb\u4eba\u6b47\u811a\u4e4b\u7528\u3002 \u3000\u3000\u7136\u540e\u4e00\u9053\u788e\u77f3\u5c71\u9053\uff0c\u5a49\u8712\u800c\u4e0a\uff0c\u4ed6\u4eec\u8eab\u5f62\u6570\u5c55\uff0c\u53ea\u89c1\u524d\u9762\u662f\u4e00\u5904\u5c71\u62d7\uff0c\u65b9\u56de\u7855\u5927\uff0c\u5c71\u62d7 \u4e2d\u642d\u7740\u4e09\u5904\u767d\u6768\u64c2\u53f0\uff0c\u4ea6\u90fd\u662f\u4f9d\u5c71\u800c\u5efa\uff0c\u64c2\u53f0\u5bbd\u7ea6\u4e94\u4e08\uff0c\u6df1\u7ea6\u4e09\u56db\u4e08\uff0c\u60ac\u7ea2\u7ed3\u5f69\uff0c\u5b9b\u5982\u4e61\u95f4 \u916c\u795e\u5531\u620f\u65f6\u6240\u642d\u7684\u620f\u53f0\u4e00\u6837\u3002 \u3000\u3000\u5353\u957f\u537f\u76ee\u5149\u8f6c\u5904\uff0c\u5fcd\u4e0d\u4f4f\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u8fd9\u4e9b\u64c2\u53f0\u4e24\u8fb9\uff0c\u4e5f\u6302\u526f\u5bf9\u8054\u624d\u662f\u3002\u201d \u3000\u3000\u6e29\u747e\u659c\u659c\u8198\u4ed6\u4e00\u773c\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\u5bf9\u8054\uff1f\u201d \u3000\u3000\u5353\u957f\u537f\u7b11\u9053\uff1a\u201c\u6211\u5e7c\u65f6\u770b\u90a3\u4e9b\u574a\u95f4\u8bf4\u90e8\uff0c\u6446\u53f0\u65c1\u8fb9\u603b\u6302\u7740\u4e00\u526f\u5bf9\u8054\uff1a\u2018\u62f3\u6253\u5357\u5c71\u731b\u864e\uff0c \u811a\u8e22\u5317\u6d77\u86df\u9f99\u2019\uff0c\u8fd8\u6709\u4ec0\u4e48\u2018\u6c5f\u6e56\u597d\u6c49\u7b2c\u4e00\uff0c\u6b66\u6797\u8c6a\u6770\u65e0\u53cc\u2019\u3002\u8fd9\u4e09\u5ea7\u64c2\u53f0\u6ca1\u6709\u5bf9\u8054\uff0c\u5c82\u975e \u6709\u4e9b\u4e0d\u50cf\u3002\u201c\u6e29\u747e\u8f7b\u8f7b\u4e00\u7b11\uff0c\u90a3\u4e09\u4e2a\u7ea2\u88f3\u5c11\u5973\u4e5f\u5fcd\u4e0d\u4f4f\u201d\u5657\u54e7\u201c\u4e00\u7b11\uff0c\u7b11\u51fa\u58f0\u6765\u3002\u5374\u89c1\u5353\u957f \u537f\u7b11\u5bb9\u4e00\u655b\uff0c\u7a81\u7136\u957f\u53f9\u4e86\u4e00\u58f0\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u7531\u6b64\u53ef\u89c1\uff0c\u73b0\u5b9e\u751f\u6d3b\u4e0e\u4e66\u4e2d\u6545\u4e8b\uff0c\u662f\u5b58\u7740\u4e00\u6bb5 \u8ddd\u79bb\u7684\uff0c\u6545\u4e8b\u867d\u591a\u7f8e\u4e3d\uff0c\u4f46\u73b0\u5b9e\u751f\u6d3b\u4e2d\u5374\u5c3d\u591a\u60b2\u54c0\u4e4b\u4e8b\uff0c\u4f60\u8bf4\u662f\u5417\uff1f\u201d \u3000\u3000\u6e29\u747e\u7f13\u7f13\u9894\u9996\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u8fd9\u5c11\u5e74\u7537\u5973\u4e24\u4eba\u7adf\u50cf\u662f\u53c8\u7a81\u7136\u53d8\u5f97\u8427\u7d22\u8d77\u6765\u3002 \u7b2c\u5341\u4e94\u7ae0\u3000\u4e71\u77f3\u6d6e\u6c99>> \u53e4\u9f99\u300a\u6708\u5f02\u661f\u90aa\u300b \u7b2c\u5341\u4e94\u7ae0\u3000\u4e71\u77f3\u6d6e\u6c99 \u3000\u3000\u8f6c\u8fc7\u8fd9\u5904\u5c71\u62d7\uff0c\u53c8\u662f\u4e00\u5904\u8fe4\u4fea\u5c71\u9053\uff0c\u524d\u884c\u5341\u6570\u4e08\uff0c\u524d\u9762\u7a81\u7136\u4e00\u7247\u8302\u6797\u963b\u8def\uff0c\u8302\u6797\u4e0a\u53c8\u662f \u4e00\u9053\u7eff\u53f6\u724c\u697c\uff0c\u4e0a\u5199\uff1a\u7b2c\u4e8c\u5173\u3002 \u3000\u3000\u6e29\u747e\u8eab\u5982\u60ca\u9e3f\uff0c\u5f53\u5148\u4eba\u6797\uff0c\u5353\u957f\u537f\u76ee\u5149\u8f6c\u5904\uff0c\u5ffd\u7136\u770b\u5230\u6811\u6797\u4e2d\u7adf\u6709\u6570\u5904\u4f9d\u6811\u800c\u642d\u7684\u6728 \u68da\uff0c\u5236\u4f5c\u5f97\u6781\u89c1\u7cbe\u5de7\uff0c\u4e00\u5165\u6797\u4e2d\uff0c\u5b9b\u5982\u53c8\u56de\u5230\u5de2\u6c0f\u5de2\u5c45\u4e4b\u65e5\uff0c\u5353\u957f\u537f\u5fc3\u4e2d\u65b9\u81ea\u6697\u53f9\uff0c\u5374\u53c8\u89c1 \u8fd9\u4e9b\u6728\u68da\u7684\u95e8\u6237\u4e0a\uff0c\u5404\u5404\u6709\u7740\u4e00\u65b9\u6a2a\u533e\uff0c\u4e0a\u9762\u7adf\u5199\u7684\u662f\uff1a\u201c\u7597\u4f24\u5904\u201d\uff0c\u4e09\u4e2a\u7eff\u5b57\u3002 \u3000\u3000\u5353\u957f\u537f\u4e0d\u7981\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5979\u5012\u60f3\u5f97\u5468\u5230\u5f97\u5f88\u3002\u201d \u3000\u3000\u90a3\u4e09\u4e2a\u5c11\u5973\u8ddf\u5728\u4ed6\u8eab\u540e\uff0c\u53c8\u81ea\u5bf9\u671b\u4e00\u773c\uff0c\u4e0d\u77e5\u9053\u5176\u4e2d\u7a76\u7adf\u6709\u4ec0\u4e48\u79d8\u5bc6\u3002 \u3000\u3000\u8302\u6797\u6df1\u5904\uff0c\u7a81\u6709\u4e00\u7247\u5e73\u5730\uff0c\u663e\u89c1\u662f\u7531\u4eba\u5de5\u5f00\u8f9f\u800c\u6210\uff0c\u780d\u5012\u7684\u6811\u5e72\uff0c\u5df2\u88ab\u5228\u53bb\u6811\u76ae\uff0c\u6a2a\u653e \u5728\u56db\u5468\uff0c\u50cf\u662f\u4e00\u6761\u4f9b\u4eba\u6b47\u811a\u7684\u957f\u6905\u3002 \u3000\u3000\u56db\u9762\u957f\u6905\u56f4\u7ed5\u4e2d\u7684\u4e00\u9762\u5e73\u5730\u4e0a\uff0c\u5374\u53c8\u7528\u5de8\u6728\u683c\u6210\u56db\u683c\u3002 \u3000\u3000\u7b2c\u4e00\u683c\u5185\uff0c\u4e71\u77f3\u6210\u5806\uff0c\u4e4d\u770b\u50cf\u662f\u96f6\u4e71\u5f97\u5f88\uff0c\u5176\u4e2d\u5374\u53c8\u4e95\u7136\u6709\u5e8f\uff0c\u5de8\u6728\u4e0a\u63d2\u7740\u4e00\u65b9\u6728\u724c\uff0c \u5199\u7684\u662f\uff1a\u4e71\u77f3\u9635\u3002 \u3000\u3000\u7b2c\u4e8c\u683c\u5185\uff0c\u5374\u662f\u4e00\u5806\u5806\u6d6e\u6c99\uff0c\u4ea6\u662f\u770b\u6765\u96f6\u4e71\uff0c\u6697\u5408\u5947\u95e8\uff0c\u5353\u957f\u537f\u6bcb\u5eb8\u770b\u90a3\u6728\u724c\uff0c\u4fbf\u77e5\u9053 \u8fd9\u4fbf\u662f\u4e94\u53f0\u7edd\u6280\u2014\u2014\u6d6e\u6c99\u9635\u3002 \u3000\u3000\u7b2c\u4e09\u683c\u5185\uff0c\u5374\u6781\u4e3a\u6574\u9f50\u5730\u6392\u5217\u7740\u4e5d\u4e5d\u516b\u5341\u4e00\u682a\u77ed\u6728\u6869\uff0c\u8fd9\u81ea\u7136\u4fbf\u662f\u5c11\u6797\u5357\u5b97\u7684\u7edd\u9876\u6b66\u529f \u4e4b\u4e00\u6885\u82b1\u6869\u4e86\u3002 \u3000\u3000\u7b2c\u56db\u683c\u5185\uff0c\u5374\u6392\u5217\u7740\u4e00\u675f\u675f\u7684\u7f57\u6c49\u9999\uff0c\u53ea\u662f\u5176\u4e2d\u5374\u6298\u65ad\u4e86\u51e0\u675f\uff0c\u5353\u957f\u537f\u51b7\u7b11\u4e00\u58f0\uff0c\u5fd6 \u9053\uff1a\u201c\u65e0\u6839\u5927\u5e08\u65b9\u624d\u60f3\u5fc5\u5c31\u662f\u5728\u8fd9\u7f57\u6c49\u9999\u9635\u4e0a\u4e0e\u4eba\u52a8\u624b\u7684\u4e86\u3002\u201d \u3000\u3000\u5239\u90a3\u4e4b\u95f4\uff0c\u4ed6\u76ee\u5149\u5728\u8fd9\u56db\u683c\u65b9\u5730\u4e0a\u4e00\u8f6c\u65f6\uff0c\u5fc3\u4e2d\u4ea6\u4e0d\u7981\u6697\u60ca\uff1a\u201c\u96be\u602a\u90a3\u6e29\u5982\u7389\u8981\u5728\u6797\u5916 \u5efa\u4e0b\u7597\u4f24\u4e4b\u5730\uff0c\u8fd9\u5374\u53c8\u5e76\u975e\u5168\u662f\u4e3a\u4e86\u793a\u5a01\u800c\u5df2\uff0c\u6b66\u6797\u4e2d\u4eba\u8981\u5230\u56db\u9635\u4e0a\u52a8\u624b\uff0c\u80fd\u4e0d\u53d7\u4f24\u7684\uff0c\u53ea \u6015\u771f\u7684\u4e0d\u591a\u3002\u201d \u3000\u3000\u4ed6\u5fc3\u5ff5\u52a8\u5904\uff0c\u811a\u4e0b\u4e0d\u505c\uff0c\u811a\u5c16\u5728\u7b2c\u4e8c\u683c\u7b2c\u4e09\u5806\u6d6e\u6c99\u4e0a\u8f7b\u8f7b\u4e00\u70b9\uff0c\u8eab\u5f62\u7a81\u7136\u63a0\u8d77\u4e09\u4e08\uff0c\u6709 \u5982\u5de8\u9e64\u51b2\u5929\u800c\u8d77\uff0c\u7a81\u53c9\u98d8\u98d8\u800c\u4e0b\uff0c\u8f7b\u7075\u7684\u8f6c\u6298\u4e00\u4e0b\uff0c\u8eab\u5f62\u4fbf\u5df2\u843d\u5728\u90a3\u7f57\u6c49\u9999\u9635\u7684\u6700\u540e\u4e00\u675f\u9999 \u4e0a\u3002 \u3000\u3000\u817f\u4e0d\u66f2\uff0c\u80a9\u4e0d\u52a8\uff0c\u8eab\u5f62\u7a81\u53c8\u63a0\u8d77\uff0c\u5bc2\u65e0\u58f0\u606f\u7684\u63a0\u4eba\u6797\u4e2d\u3002 \u3000\u3000\u8ddf\u5728\u4ed6\u8eab\u540e\u7684\u4e09\u4e2a\u7ea2\u88f3\u5c11\u5973\uff0c\u5fcd\u4e0d\u4f4f\u6697\u4e2d\u60ca\u53f9\u4e00\u58f0\uff0c\u75f4\u75f4\u5730\u671b\u7740\u4ed6\u7684\u80cc\u5f71\uff0c\u5446\u4e86\u534a\u664c\uff0c \u65b9\u81ea\u5077\u7b11\u4e00\u4e0b\uff0c\u968f\u540e\u63a0\u53bb\u3002 \u3000\u3000\u7a7f\u6797\u800c\u8fc7\uff0c\u524d\u884c\u53c8\u5341\u4e08\uff0c\u524d\u9762\u7a81\u89c1\u5371\u5761\u8038\u7acb\uff0c\u5176\u52bf\u9661\u659c\u3002 \u3000\u3000\u5353\u957f\u537f\u4e0e\u6e29\u747e\u5e76\u80a9\u63a0\u4e86\u8fc7\u53bb\uff0c\u53ea\u89c1\u4e00\u8def\u602a\u77f3\u5d99\u5ccb\uff0c\u5fc3\u4e2d\u65b9\u81ea\u6697\u8c05\u5c71\u52bf\u4e4b\u9669\uff0c\u54ea\u77e5\u76ee\u5149\u52a8 \u5904\uff0c\u5374\u4e0d\u7981\u201c\u5440\u201d\u5730\u4e00\u58f0\uff0c\u60ca\u5524\u51fa\u58f0\u6765\u3002 \u3000\u3000\u6e29\u747e\u8f7b\u53f9\u4e00\u58f0\uff0c\u4fa7\u987e\u9053\uff1a\u201c\u8fd9\u4e5f\u662f\u90a3\u795e\u5077\u4e54\u8fc1\u7684\u4e3b\u610f\u3002\u201d \u3000\u3000\u539f\u6765\u8fd9\u4e00\u8def\u957f\u5761\u4e4b\u4e0a\uff0c\u4e24\u65c1\u7adf\u6392\u5217\u7740\u4e00\u6392\u767d\u6768\u68fa\u6728\u3002 \u3000\u3000\u4e00\u773c\u671b\u53bb\uff0c\u53ea\u89c1\u8fd9\u4e9b\u68fa\u6750\u4e00\u53ea\u53ea\u8fde\u7740\u7684\u6392\u4e86\u4e0a\u53bb\uff0c\u7adf\u770b\u4e0d\u6e05\u7a76\u7adf\u6709\u591a\u5c11\u4e2a\uff0c\u5c71\u884c\u6e10\u9ad8\uff0c \u5c71\u51e4\u6e10\u5bd2\uff0c\u7a00\u6de1\u7684\u9633\u5149\uff0c\u6620\u5728\u8fd9\u4e00\u6392\u68fa\u6750\u4e0a\uff0c\u8ba9\u4eba\u89c1\u4e86\uff0c\u5fc3\u4e2d\u5fcd\u4e0d\u4f4f\u8981\u751f\u51fa\u4e00\u822c\u5bd2\u610f\u3002 \u3000\u3000\u5353\u957f\u537f\u5251\u7709\u8f69\u5904\uff0c\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u65e0\u8a00\u5730\u63a0\u4e86\u4e0a\u53bb\uff0c\u5fc3\u4e2d\u5374\u6ee1\u6000\u6124\u4ec7\uff0c\u6b64\u523b\u90a3\u4e54\u8fc1\u82e5\u662f \u7a81\u7136\u51fa\u73b0\uff0c\u4fbf\u7acb\u65f6\u5f97\u4f24\u5728\u4ed6\u7684\u638c\u4e0b\u3002 \u3000\u3000\u5761\u957f\u7ade\u6709\u51e0\u91cc\uff0c\u4e00\u8def\u4e0a\u5c71\u98ce\u51db\u51db\u666f\u8272\u66f4\u662f\u8c05\u5fc3\u89e6\u76ee\u3002 \u3000\u3000\u76f4\u5230\u8fd9\u957f\u5761\u5c3d\u5934\uff0c\u4fbf\u53c8\u89c1\u4e00\u5904\u7eff\u53f6\u724c\u6402\uff0c\u4e0a\u9762\u5199\u7740\u7684\u81ea\u662f\uff1a\u201c\u7b2c\u4e09\u5173\u201d\u4e09\u5b57\u3002 \u3000\u3000\u724c\u697c\u5185\u5374\u662f\u4e00\u7247\u5b9b\u5982\u4e94\u4e01\u795e\u65a7\u4e00\u7247\u524a\u6210\u7684\u5c71\u5730\uff0c\u5c71\u5730\u4e0a\u642d\u7740\u56db\u9053\u770b\u53f0\uff0c\u770b\u53f0\u540e\u9762\u662f\u4ec0\u4e48 \u6837\u5b50\uff0c\u5353\u957f\u537f\u867d\u65e0\u6cd5\u770b\u5230\uff0c\u4f46\u5374\u6709\u4e00\u201c\u9635\u9635\u53f1\u559d\u4e4b\u58f0\uff0c\u4ece\u90a3\u8fb9\u9690\u9690\u4f20\u6765\uff0c\u5f53\u4e0b\u4ed6\u811a\u6b65\u52a0\u7d27\uff0c \u8eab\u5f62\u66f4\u5feb\uff0c\u500f\u7136\u4e00\u4e2a\u8d77\u843d\uff0c\u8dc3\u4e0a\u4e86\u90a3\u9ad8\u7ea6\u4e09\u4e08\u7684\u7af9\u6728\u770b\u53f0\u3002\u53ea\u89c1\u2014\u2014\u8fd9\u56db\u9053\u770b\u53f0\u4e4b\u4e2d\u7684\u4e00\u7247 \u7ec6\u7802\u5730\u4e0a\uff0c\u7adf\u904d\u7740\u6570\u767e\u67c4\u5200\u53e3\u5411\u4e0a\u7684\u89e3\u8155\u5c16\u5200\uff0c\u5200\u950b\u95ea\u95ea\uff0c\u6620\u65e5\u751f\u82b1\u3002\u8fd9\u4e00\u7247\u5c16\u5200\u4e4b\u4e0a\uff0c\u5de6 \u53f3\u4e24\u8fb9\uff0c\u8fd8\u642d\u7740\u4e24\u67b6\u94a2\u67b6\u3002\u94a2\u67b6\u4e0a\u94a2\u652f\u6392\u5217\uff0c\u4e0b\u60ac\u94c1\u7ec3\uff0c\u4e00\u9762\u94c1\u7ec3\u4e0a\u60ac\u6302\u7684\u662f\u6570\u5341\u53e3\u5947\u5f62\u77ed \u5200\uff0c\u5c71\u51e4\u867d\u5927\uff0c\u8fd9\u4e9b\u5c16\u5200\u5374\u7eb9\u4e1d\u4e0d\u52a8\uff0c\u663e\u89c1\u5f97\u4efd\u91cf\u6781\u91cd\u3002\u53e6\u4e00\u5904\u94a2\u67b6\u4e0a\uff0c\u5374\u60ac\u6302\u7740\u6570\u5341\u7c92\u76f4 \u5f84\u53ea\u6709\u4e00\u5c3a\uff0c\u4e0a\u9762\u6ee1\u5e03\u8292\u523a\u7684\u4e94\u8292\u94a2\u73e0\u3002\u6b64\u523b\u8fd9\u4e94\u8292\u795e\u73e0\u9635\uff0c\u94c1\u7ec3\u53ee\u5f53\u54cd\uff0c\u94a2\u73e0\u98de\u52a8\uff0c\u5176\u4e2d \u8fd8\u5939\u6742\u7740\u4e24\u6761\u5154\u8d77\u9e64\u843d\u7684\u6de1\u7070\u4eba\u5f71\u3002\u5c71\u9876\u9633\u5149\u867d\u7136\u8f83\u7a00\uff0c\u4f46\u7167\u6620\u5728\u8fd9\u4e00\u7247\u5200\u5c71\u4e0a\uff0c\u518d\u52a0\u4e0a\u90a3 \u98de\u52a8\u7740\u7684\u94a2\u73e0\u94c1\u7ec3\uff0c\u8ba9\u4eba\u89c1\u4e86\uff0c\u53ea\u89c9\u5149\u534e\u95ea\u52a8\uff0c\u4e0d\u53ef\u65b9\u7269\u3002\u518d\u52a0\u4e0a\u90a3\u6151\u4eba\u5fc3\u9b42\u7684\u94c1\u7ec3\u94a2\u73e0\u7684 \u53ee\u5f53\u4e4b\u58f0\uff0c\u4e24\u6761\u4eba\u5f71\u7684\u559d\u53f1\u4e4b\u58f0\u3002\u5353\u957f\u537f\u4e00\u773c\u671b\u53bb\uff0c\u5fc3\u4e2d\u4ea6\u4e0d\u7981\u4e3a\u4e4b\u4e00\u51db\u3002\u4ed6\u76ee\u5149\u518d\u4e00\u8f6c\uff0c \u5374\u89c1\u5bf9\u9762\u4e00\u5ea7\u770b\u53f0\u4e0a\uff0c\u7adf\u8fd8\u6742\u4e71\u5730\u5750\u7740\u5341\u6570\u4e2a\u6b66\u6797\u8c6a\u58eb\uff0c\u8fd9\u5176\u4e2d\u6709\u7684\u662f\u81ea\u53d1\u7693\u7136\uff0c\u6709\u7684\u662f\u6ee1 \u9762\u866c\u987b\uff0c\u6709\u7684\u662f\u957f\u888d\u9ad8\u8f94\u7684\u9053\u4eba\uff0c\u6709\u7684\u662f\u4e00\u8eab\u52b2\u88c5\u7684\u8c6a\u96c4\uff0c\u5f62\u72b6\u867d\u5404\u5f02\uff0c\u4f46\u5374\u90fd\u662f\u795e\u6001\u5955 \u5955\uff0c\u6c14\u52bf\u5a01\u731b\uff0c\u663e\u89c1\u5f97\u90fd\u662f\u6b66\u6797\u9ad8\u624b\u3002\u5353\u957f\u537f\u76ee\u5149\u52a8\u5904\uff0c\u53ea\u89c1\u8fd9\u4e9b\u4eba\u6570\u5341\u9053\u76ee\u5149\uff0c\u867d\u90fd\u662f\u660e \u5982\u5229\u7bad\u822c\u671b\u5411\u4ed6\uff0c\u4f46\u5374\u4ecd\u7aef\u5750\u5982\u6545\uff0c\u6ca1\u6709\u4e00\u4e2a\u4eba\u663e\u51fa\u60ca\u614c\u4e4b\u6001\u6765\u3002\u6b64\u523b\u6e29\u747e\u5df2\u63a0\u4e0a\u770b\u53f0\uff0c\u8fd9 \u4e9b\u4eba\u89c1\u4e86\u8fd9\u7a81\u7136\u73b0\u8eab\u7684\u5c11\u5e74\uff0c\u5fc3\u4e2d\u867d\u7136\u5947\u602a\uff0c\u4f46\u89c1\u4ed6\u65e2\u4e0e\u6e29\u747e\u4e00\u8def\uff0c\u60f3\u6765\u4ea6\u7b97\u81ea\u5df1\u4eba\uff0c\u662f\u4ee5 \u90fd\u672a\u51fa\u58f0\uff0c\u800c\u6628\u5929\u4e0e\u4ed6\u66fe\u7ecf\u89c1\u9762\u4ea4\u624b\u7684\u201d\u724c\u5251\u97ad\u5200\u201c\u4e0e\u201d\u6d77\u5357\u4e09\u5251\u201c\uff0c\u6b64\u523b\u65e9\u5df2\u81ea\u89c9\u65e0\u989c\uff0c \u6697\u4e2d\u8d70\u4e86\u3002\u6e29\u747e\u76ee\u5149\u4e00\u8f6c\uff0c\u67f3\u7709\u8f7b\u98a6\uff0c\u8eab\u5f62\u52a8\u5904\uff0c\u5237\u7684\u63a0\u4e86\u4e0b\u53bb\u3002\u5979\u8eab\u5f62\u98d8\u98d8\u843d\u4e0b\uff0c\u7adf\u843d\u5728 \u4e00\u5904\u5200\u5c16\u4e0a\uff0c\u5355\u8db3\u8f7b\u70b9\uff0c\u4e00\u8db3\u5fae\u66f2\uff0c\u8eab\u5f62\u5374\u7eb9\u4e1d\u4e0d\u52a8\uff0c\u9633\u5149\u95ea\u95ea\uff0c\u6620\u7740\u5979\u4e00\u8eab\u7d20\u670d\uff0c\u6ee1\u5934\u957f \u53d1\uff0c\u5c71\u98ce\u51db\u51db\uff0c\u5439\u52a8\u7740\u5979\u5bbd\u5927\u7684\u8863\u886b\u3002\u5353\u957f\u537f\u5fcd\u4e0d\u4f4f\u6697\u4e2d\u559d\u91c7\uff0c\u53ea\u89c1\u5bf9\u9762\u7684\u90a3\u4e9b\u6b66\u6797\u8c6a\u6770\u82f1 \u96c4\uff0c\u6b64\u523b\u5df2\u90fd\u957f\u8eab\u800c\u8d77\uff0c\u4e00\u9f50\u62f1\u624b\u9053\uff1a\u201c\u59d1\u5a18\u5012\u65e9\u5f97\u5f88\u3002\u201d \u3000\u3000\u8981\u77e5\u9053\u6e29\u747e\u5e74\u7eaa\u867d\u7136\u751a\u8f7b\uff0c\u4f46\u5374\u662f\u4e11\u4eba\u6e29\u5982\u7389\u7684\u552f\u4e00\u5f1f\u5b50\uff0c\u5728\u6b66\u6797\u4e2d\u5730\u4f4d\u5374\u4e0d\u4f4e\uff0c\u662f\u4ee5 \u8fd9\u4e9b\u6210\u540d\u5df2\u4e45\u7684\u6b66\u6797\u4eba\u7269\uff0c\u5bf9\u5979\u4ea6\u6781\u4e3a\u606d\u656c\u3002 \u3000\u3000\u5979\u5fae\u7b11\u4e00\u4e0b\uff0c\u8f7b\u8f7b\u9053\uff1a\u201c\u65e9\u3002\u201d \u3000\u3000\u65e5\u5149\u4e00\u8f6c\uff0c\u5374\u8f6c\u5411\u90a3\u4e94\u8292\u795e\u73e0\u9635\uff0c\u53ea\u89c1\u9635\u4e2d\u7684\u4eba\u5f71\u7eb5\u6a2a\u4ea4\u9519\uff0c\u5374\u6b63\u662f\u90a3\u591a\u4e8b\u5934\u9640\u65e0\u6839\u5927 \u5e08\u4e0e\u5343\u91cc\u660e\u9a7c\u3002 \u3000\u3000\u5979\u53c8\u81ea\u51b7\u51b7\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u65e0\u6839\u5927\u5e08\u600e\u4e48\u4e0e\u522b\u4eba\u52a8\u8d77\u624b\u6765\u4e86\u5979\u8bdd\u58f0\u672a\u4e86\uff0c\u770b\u53f0\u4e0a\u5374\u5df2\u63a0\u51fa \u4e00\u6761\u7626\u957f\u4eba\u5f71\uff0c\u8f7b\u8f7b\u843d\u5230\u5200\u5c71\u4e4b\u4e0a\uff0c\u8f7b\u529f\u4ea6\u81ea\u4e0d\u5f31\uff0c\u6e29\u747e\u79cb\u6ce2\u4e00\u8f6c\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u8427\u5927\u4fa0\uff0c\u4f60 \u77e5\u9053\u8fd9\u662f\u4e3a\u4e86\u4ec0\u4e48\u5417\uff1f\u201d \u3000\u3000\u201c\u65e0\u5f71\u7f57\u5239\u201d\u54c8\u54c8\u5e72\u7b11\u6570\u58f0\uff0c\u9053\uff1a\u201c\u8fd9\u53ea\u662f\u6211\u4eec\u4e45\u4ef0\u5c11\u6797\u7edd\u6280\uff0c\u662f\u4ee5\u624d\u5411\u65e0\u6839\u5927\u5e08\u8ba8\u6559 \u4e00\u4e0b\u800c\u5df2\uff0c\u522b\u7684\u6ca1\u6709\u4ec0\u4e48\u3002\u201d \u3000\u3000\u6e29\u747e\u957f\u957f\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u539f\u6765\u662f\u8fd9\u6837\u3002\u201d \u3000\u3000\u7a81\u7136\u51b7\u7b11\u4e00\u4e0b\uff1a\u201c\u4f46\u662f\u8fd9\u91d1\u5200\u6362\u638c\uff0c\u548c\u4e94\u8292\u795e\u73e0\u9635\uff0c\u53ef\u4e0d\u662f\u81ea\u5df1\u4eba\u8003\u8f83\u6b66\u529f\u7684\u5730\u65b9 \u5440\u3002\u201d \u3000\u3000\u201c\u65e0\u5f71\u7f57\u5239\u201d\u8427\u94c1\u98ce\u5fae\u5fae\uff5e\u6115\uff0c\u5374\u4ecd\u81ea\u6ee1\u9762\u5f3a\u7b11\u7684\u8bf4\u9053\uff1a\u201c\u53ea\u8981\u5927\u5bb6\u624b\u4e0b\u7559\u5fc3\u4e9b\uff0c\u4e5f\u6ca1 \u6709\u4ec0\u4e48\u3002\u201d \u3000\u3000\u8bdd\u58f0\u672a\u4e86\uff0c\u53ea\u542c\u201c\u5f53\u201d\u7684\u4e00\u58f0\u5de8\u54cd\uff0c\u539f\u6765\u591a\u4e8b\u5934\u9640\u89c1\u4e86\u6e29\u747e\u6765\u4e86\uff0c\u7cbe\u795e\u7a81\u632f\uff0c\u594b\u8d77\u4e00 \u638c\uff0c\u8361\u8d77\u4e00\u9897\u4e94\u8292\u795e\u73e0\uff0c\u5411\u725b\u4e00\u5c71\u51fb\u53bb\uff0c\u90a3\u725b\u4e00\u5c71\u672c\u662f\u4e2a\u9a7c\u5b50\uff0c\u6b64\u523b\u8eab\u5f62\u4e00\u77ee\uff0c\u4fbf\u5df2\u907f\u8fc7\uff0c \u53cd\u624b\u4e00\u6325\uff0c\u4ea6\u81ea\u6325\u53bb\u4e00\u9897\u4e94\u8292\u795e\u73e0\u3002 \u3000\u3000\u591a\u4e8b\u5934\u9640\u5927\u559d\u4e00\u58f0\uff0c\u5e26\u8d77\u53e6\u4e00\u9897\u4e94\u8292\u795e\u73e0\uff0c\u76f4\u51fb\u8fc7\u53bb\uff0c\u4e24\u73e0\u76f8\u51fb\uff0c\u4fbf\u53d1\u51fa\u201c\u5f53\u201d\u7684\u4e00\u58f0 \u5de8\u54cd\uff0c\u4f46\u8863\u8896\u4e4b\u95f4\uff0c\u5374\u5df2\u88ab\u53e6\u4e00\u9897\u795e\u73e0\u5212\u4e86\u9053\u53e3\u5b50\u3002 \u3000\u3000\u8981\u77e5\u9053\u4ed6\u8eab\u8eaf\u8981\u6bd4\u725b\u4e00\u5c71\u9ad8\u5927\u4e00\u500d\uff0c\u5728\u8fd9\u79cd\u5730\u65b9\u4ea4\u624b\uff0c\u65e0\u5f62\u4e2d\u5403\u4e86\u5927\u4e8f\uff0c\u4f55\u51b5\u4ed6\u65b9\u624d\u8fde \u63a5\u4e09\u9635\uff0c\u6b64\u523b\u6c14\u529b\u5df2\u81ea\u4e0d\u7ee7\u3002 \u3000\u3000\u4ed6\u8863\u8896\u5212\u7834\uff0c\u5fc3\u5934\u4e00\u51db\uff0c\u811a\u4e0b\u5fae\u6643\uff0c\u90a3\u5343\u91cc\u660e\u9a7c\u725b\u4e00\u5c71\u5360\u7740\u4e86\u5148\u673a\uff0c\u54ea\u80af\u8f7b\u6613\u653e\u8fc7\uff0c\u6697 \u4e2d\u51b7\u7b11\u4e00\u58f0\uff0c\u8eab\u5f62\u4e00\u7f13\uff0c\u5012\u9000\u4e09\u5c3a\uff0c\u811a\u4e0b\u65e9\u5df2\u5fd6\u597d\u5730\u52bf\uff0c\u8f7b\u8f7b\u843d\u5728\u7b2c\u4e09\u67c4\u5c16\u5200\u4e0a\uff0c\u53cc\u638c\u9f50\u9f50 \u5f53\u80f8\u63a8\u51fa\uff0c\u63a8\u8d77\u56db\u9897\u4e94\u8292\u94a2\u73e0\uff0c\u76f4\u51fb\u591a\u4e8b\u5934\u9640\u3002 \u3000\u3000\u8fd9\u56db\u9897\u94a2\u73e0\u867d\u662f\u540c\u65f6\u88ad\u51fb\u6765\uff0c\u65b9\u5411\u5374\u4e0d\u4e00\uff0c\u5728\u5239\u90a3\u4e4b\u95f4\uff0c\u591a\u4e8b\u5934\u9640\u53ea\u89c9\u8033\u7554\u53ee\u5f53\u5de8\u54cd\uff0c \u773c\u4e2d\u5149\u534e\u95ea\u8000\uff0c\u4ed6\u811a\u4e0b\u5df2\u81ea\u4e0d\u7a33\uff0c\u6c14\u529b\u4e5f\u5df2\u4e0d\u7ee7\uff0c\u54ea\u91cc\u6321\u5f97\u4f4f\u8fd9\u725b\u4e00\u5c71\u5168\u529b\u4e00\u51fb\u4e4b\u4e0b\u6240\u51fb\u51fa \u7684\u56da\u9897\u91cd\u903e\u5341\u65a4\u7684\u4e94\u8292\u795e\u73e0\u3002 \u3000\u3000\u4ed6\u4e0d\u7981\u6697\u53f9\u4e00\u58f0\uff0c\u53ea\u9053\u81ea\u5df1\u4eca\u65e5\u6050\u8981\u846c\u8eab\u5728\u8fd9\u4e94\u8292\u795e\u73e0\u9635\u4e2d\u3002 \u3000\u3000\u54ea\u77e5\u2014\u2014 \u3000\u3000\u53ea\u542c\u4e00\u58f0\u6e05\u5578\uff0c\u5212\u7a7a\u800c\u6765\uff0c\u63a5\u7740\u4e00\u9635\u53ee\u5f53\u4ea4\u51fb\u4e4b\u58f0\uff0c\u4e0d\u7edd\u4e8e\u8033\uff0c\u7136\u540e\u4fbf\u662f\u90a3\u5343\u91cc\u660e\u9a7c\u725b \u4e00\u5c71\u7684\u4e00\u58f0\u60e8\u547c\u3002 \u3000\u3000\u591a\u4e8b\u5934\u9640\u53ea\u89c9\u624b\u8155\u4e00\u7d27\uff0c\u8eab\u4e0d\u81ea\u4e3b\u5730\u9000\u4e86\u51fa\u53bb\uff0c\u4e00\u9000\u7adf\u4e00\u4e08\u8fdc\uff0c\u4ed6\u5b9a\u4e86\u5b9a\u795e\uff0c\u65b9\u81ea\u7741\u5f00 \u773c\u6765\uff0c\u53ea\u89c2\u7a79\u82cd\u5982\u6d17\uff0c\u9633\u5149\u8000\u76ee\uff0c\u4e94\u8292\u795e\u73e0\u867d\u4ecd\u5728\u98de\u821e\u4e0d\u5df2\uff0c\u4ed6\u81ea\u5df1\u5374\u5df2\u8fdc\u8fdc\u7ad9\u5728\u5200\u5c71\u65c1\u7684 \u6c99\u5730\u4e0a\u3002 \u3000\u3000\u8981\u77e5\u9053\u5353\u957f\u537f\u626c\u5a01\u5929\u76ee\u5c71\uff0c\u6280\u6151\u7fa4\u96c4\uff0c\u4e07\u5999\u771f\u541b\u4e00\u751f\u501f\u5200\u6740\u4eba\uff0c\u5230\u5934\u6765\u5374\u81ea\u98df\u5176\u679c\uff0c\u6e29 \u5982\u738b\u6325\u624b\u7b11\u5f04\u94c1\u8fbe\u4eba\u3001\u77f3\u5e73\uff0c\u542b\u7b11\u800c\u901d\uff0c\u6e29\u747e\u751f\u6b7b\u4e00\u5ff5\uff0c\u51e0\u4e4e\u4e27\u751f\u5728\u4e94\u4e91\u70d8\u65e5\u900f\u5fc3\u9488\u4e0b\u2026\u2026 \u3000\u3000\u591a\u4e8b\u5934\u9640\u5728\u8fd9\u5239\u90a3\u4e4b\u95f4\uff0c\u7531\u751f\u9669\u6b7b\uff0c\u7531\u6b7b\u8fd8\u751f\uff0c\u6b64\u523b\u5fc3\u4e2d\u4f46\u89c9\u72c2\u6cc9\u767e\u6d8c\uff0c\u6e10\u9759\u6e10\u5f31\u6e10 \u6d88\uff0c\u4ed6\u5446\u5446\u5730\u6123\u4e86\u534a\u664c\uff0c\u65b9\u81ea\u5b9a\u4e00\u5b9a\u795e\uff0c\u51dd\u76ee\u671b\u53bb\uff0c\u53ea\u89c1\u7a79\u82cd\u5982\u6d17\uff0c\u9633\u5149\u8000\u76ee\uff0c\u4e94\u8292\u795e\u73e0\u5728 \u98de\u821e\u4e0d\u5df2\uff0c\u98de\u821e\u7740\u7684\u4e94\u8292\u795e\u73e0\u4e0b\uff0c\u5374\u5012\u5367\u7740\u4e00\u6761\u4eba\u5f71\uff0c\u4e0d\u95ee\u53ef\u77e5\uff0c\u81ea\u662f\u90a3\u7acb\u5fc3\u5bb3\u4eba\uff0c\u53cd\u5bb3\u4e86 \u81ea\u5df1\u7684\u5343\u91cc\u660e\u9a7c\u725b\u4e00\u5c71\u4e86\u3002 \u3000\u3000\u539f\u6765\u65b9\u624d\u591a\u4e8b\u5934\u9640\u4e45\u6218\u529b\u75b2\uff0c\u5728\u725b\u4e00\u5c71\u5168\u529b\u4e00\u51fb\u6240\u51fb\u51fa\u7684\u4e94\u8292\u795e\u73e0\u4e4b\u4e0b\uff0c\u5df2\u662f\u751f\u6b7b\u60ac\u4e8e \u4e00\u7ebf\uff0c\u5c31\u5728\u8fd9\u95f4\u4e0d\u5bb9\u53d1\u7684\u5239\u90a3\u4e4b\u95f4\uff0c\u5353\u957f\u537f\u6e05\u5578\u4e00\u58f0\uff0c\u8eab\u5f62\u500f\u7136\u63a0\u8d77\uff0c\u6709\u5982\u7ecf\u5929\u957f\u8679\u4e00\u822c\u63a0 \u5165\u4e94\u8292\u795e\u73e0\u9635\u4e2d\uff0c\u4e00\u624b\u6293\u4f4f\u591a\u4e8b\u5934\u9640\u7684\u624b\u8155\uff0c\u6b63\u5f85\u5c06\u4e4b\u6551\u51fa\u9669\u5883\u3002 \u3000\u3000\u54ea\u77e5\u5343\u91cc\u660e\u9a7c\u6740\u673a\u5df2\u8d77\uff0c\u773c\u770b\u591a\u4e8b\u5934\u9640\u5df2\u5c06\u4e27\u547d\uff0c\u6b64\u523b\u54ea\u91cc\u5bb9\u5f97\u4ed6\u9003\u751f\uff0c\u53cc\u638c\u4e00\u9519\uff0c\u8eab \u5f62\u5fae\u95ea\uff0c\u7adf\u7136\u8ffd\u6251\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u5353\u957f\u537f\u8eab\u5f62\u5df2\u8f6c\uff0c\u6b64\u523b\u5251\u7709\u5fae\u76b1\uff0c\u53cd\u624b\u4e00\u201c\u638c\uff0c\u9f99\u5c3e\u6325\u51e4\u3002\u5343\u91cc\u660e\u9a7c\u725b\u4e00\u5c71\u53ea\u89c1\u8fd9\u7384\u886b \u5c11\u5e74\u968f\u610f\u4e00\u638c\u6325\u6765\uff0c\u4ed6\u4e0d\u7981\u6697\u4e2d\u51b7\u7b11\u4e00\u58f0\uff1a\u201c\u4f60\u8fd9\u662f\u81ea\u5bfb\u6b7b\u8def\u3002\u201d \u3000\u3000\u8170\u8eab\u4e00\u584c\uff0c\u53cc\u638c\u5f53\u80f8\uff0c\u5e73\u63a8\u800c\u51fa\uff0c\u5343\u91cc\u660e\u9a7c\u4e00\u751f\u4ee5\u529b\u89c1\u957f\uff0c\u4e00\u53cc\u94c1\u638c\u4e0a\uff0c\u7684\u786e\u6709\u7740\u8db3\u4ee5 \u5f00\u5c71\u88c2\u77f3\u7684\u771f\u529f\u592b\uff0c\u53ea\u9053\u8fd9\u7384\u886b\u5c11\u5e74\u4e0e\u81ea\u5df1\u8fd9\u53cc\u638c\u4e00\u63a5\uff0c\u6015\u4e0d\u7acb\u4f7f\u4e4b\u8155\u6298\u638c\u65ad\u3002 \u3000\u3000\u54ea\u77e5\u4ed6\u62db\u5f0f\u5c1a\u672a\u9012\u6ee1\uff0c\u4fbf\u89c9\u4e00\u822c\u5f3a\u98ce\u5f53\u80f8\u51fb\u6765\u3002 \u3000\u3000\u4ed6\u8fd9\u624d\u77e5\u9053\u4e0d\u597d\uff0c\u4f46\u6b64\u65f6\u6b64\u523b\uff0c\u54ea\u91cc\u8fd8\u6709\u4ed6\u540e\u6094\u7684\u4f59\u5730\u3002 \u3000\u3000\u4ed6\u53cc\u638c\u65b9\u81ea\u9012\u51fa\uff0c\u811a\u4e0b\u5df2\u662f\u7acb\u8db3\u4e0d\u7a33\uff0c\u6b64\u523b\u82e5\u662f\u5728\u5e73\u5730\uff0c\u4ed6\u4e5f\u8bb8\u8fd8\u80fd\u62bd\u62db\u5e94\u654c\uff0c\u9003\u5f97\u6027 \u547d\uff0c\u4f46\u6b64\u523b\u4ed6\u811a\u4e0b\u4e00\u6643\uff0c\u65b9\u81ea\u5012\u9000\u534a\u6b65\uff0c\u8eab\u540e\u5df1\u6709\u4e09\u7c92\u4e94\u8292\u795e\u73e0\u8361\u7740\u52b2\u98ce\uff0c\u5411\u4ed6\u88ad\u6765\uff0c\u98ce\u58f0 \u5f3a\u52b2\uff0c\u4ed6\u867d\u5df2\u89c9\u5bdf\uff0c\u4f46\u5374\u518d\u4e5f\u65e0\u6cd5\u95ea\u907f\u3002 \u3000\u3000\u201c\u7830\u3001\u7830\u3001\u7830\u201d\u4e09\u58f0\uff0c\u8fd9\u4e09\u7c92\u4e94\u8292\u795e\u73e0\uff0c\u7adf\u4e00\u8d77\u91cd\u91cd\u7684\u51fb\u5728\u4ed6\u7684\u8eab\u4e0a\u3002 \u3000\u3000\u4ed6\u4f46\u89c9\u5168\u8eab\u4e00\u9707\uff0c\u5fc3\u5934\u4e00\u51c9\uff0c\u5589\u5934\u4e00\u751c\u2014\u2014\u5f20\u53e3\u201c\u54c7\u201d\u7684\u55b7\u51fa\u4e00\u53e3\u9c9c\u8840\uff0c\u72c2\u543c\u4e00\u58f0\uff0c\u6251 \u5728\u5730\u4e0a\uff0c\u4ed6\u7eb5\u6709\u4e00\u8eab\u6a2a\u7ec3\uff0c\u4f46\u5728\u8fd9\u4e13\u7834\u91d1\u949f\u7f69\u3001\u94c1\u5e03\u886b\u7684\u4e94\u8292\u795e\u73e0\u7684\u91cd\u51fb\u4e0b\uff0c\u53c8\u7109\u4f1a\u518d\u6709\u6d3b \u8def\u3002 \u3000\u3000\u5353\u957f\u537f\u8fd9\u957f\u5578\u3001\u7eb5\u8eab\u3001\u6551\u4eba\u3001\u6325\u638c\uff0c\u5f53\u771f\u662f\u5feb\u5982\u95ea\u7535\uff0c\u591a\u4e8b\u5934\u9640\u56de\u76ee\u4e00\u671b\uff0c\u53ea\u89c1\u5353\u957f\u537f \u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5927\u5e08\uff0c\u6ca1\u6709\u4e8b\u5427\uff1f\u201d \u3000\u3000\u591a\u4e8b\u5934\u9640\u60f3\u8d77\u81ea\u5df1\u4ee5\u524d\u5bf9\u8fd9\u4f4d\u5c11\u5e74\u7684\u795e\u60c5\u4e3e\u6b62\uff0c\u4e0d\u89c9\u9762\u988a\u4e3a\u4e4b\u4e00\u7ea2\uff0c\u4f46\u662f\u4ed6\u6b63\u662f\u80f8\u6000\u78ca \u843d\u7684\u6c49\u5b50\uff0c\u6b64\u523b\u5fc3\u4e2d\u867d\u89c9\u6709\u4e9b\u4ed9\u6c55\u7684\u4e0d\u597d\u610f\u601d\uff0c\u4f46\u5374\u4ecd\u4e00\u63d6\u5230\u5730\uff0c\u5927\u58f0\u9053\uff1a\u201c\u5144\u5f1f\uff0c\u548c\u5c1a\u4eca \u5929\u670d\u4e86\u4f60\u4e86\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u542b\u7b11\u9053\uff1a\u201c\u5927\u5e08\u8a00\u91cd\u4e86\u3002\u201d \u3000\u3000\u8f6c\u76ee\u671b\u53bb\uff0c\u53ea\u89c1\u5bf9\u9762\u53f0\u4e0a\u7684\u6570\u5341\u9053\u76ee\u5149\uff0c\u6b64\u523b\u6b63\u90fd\u5389\u7535\u822c\u7684\u671b\u7740\u81ea\u5df1\uff0c\u90a3\u65e0\u5f71\u7f57\u5239\u8427\u94c1 \u98ce\uff0c\u5374\u5df2\u63a0\u81f3\u4e94\u8292\u795e\u73e0\u9635\u8fb9\uff0c\u5c06\u5343\u91cc\u660e\u9a7c\u725b\u4e00\u5c71\u7684\u5c38\u8eab\uff0c\u62b1\u4e86\u51fa\u6765\uff0c\u8fd9\u8427\u94c1\u51e4\u6709\u65e0\u5f71\u4e4b\u79f0\uff0c \u8f7b\u529f\u679c\u81ea\u4e0d\u5f31\uff0c\u624b\u91cc\u62b1\u7740\u90a3\u4e48\u6c89\u91cd\u7684\u8eaf\u4f53\uff0c\u5728\u8fd9\u6620\u76ee\u751f\u5149\u7684\u5c16\u5200\u4e4b\u4e0a\uff0c\u7626\u957f\u7684\u8eab\u5f62\u5374\u4ecd\u884c\u52a8 \u8f7b\u7075\uff0c\u55d6\u7684\u4e24\u4e2a\u8d77\u843d\uff0c\u63a0\u51fa\u5c16\u5200\u4e4b\u9635\uff0c\u843d\u5230\u65c1\u8fb9\u7684\u7a7a\u5730\u4e0a\uff0c\u4fef\u9996\u4e00\u671b\uff0c\u4f4e\u53f9\u9053\uff1a\u201c\u679c\u7136\u6b7b \u4e86\u3002\u201d\u5353\u957f\u537f\u5251\u7709\u5fae\u76b1\uff0c\u5fc3\u4e2d\u7a81\u7136\u89c9\u5f97\u5927\u4e3a\u6b49\u7136\uff0c\u8981\u77e5\u9053\u4ed6\u81ea\u51fa\u6c5f\u6e56\u4ee5\u6765\uff0c\u4e0e\u4eba\u52a8\u624b\uff0c\u867d\u6709 \u591a\u6b21\uff0c\u4f24\u4eba\u6027\u547d\uff0c\u5374\u4ece\u672a\u4e4b\u6709\u8fc7\uff0c\u6b64\u523b\u4f46\u89c9\u96be\u53d7\u5f02\u5e38\uff0c\u8702\u8170\u5fae\u626d\uff0c\u4e00\u63a0\u56db\u4e08\uff0c\u7adf\u63a0\u81f3\u65e0\u5f71\u7f57 \u5239\u8427\u94c1\u98ce\u8eab\u4fa7\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u4e5f\u8bb8\u6709\u6551\uff0c\u4ea6\u672a\u53ef\u77e5\u3002\u201d \u3000\u3000\u6b63\u5f85\u4fef\u4e0b\u8eab\u53bb\u67e5\u770b\u725b\u4e00\u5c71\u7684\u4f24\u52bf\u3002 \u3000\u3000\u54ea\u77e5\u8427\u94c1\u98ce\u500f\u7136\u8f6c\u8fc7\u5934\u53bb\uff0c\u4e00\u773c\u671b\u89c1\u4e86\u4ed6\uff0c\u4fbf\u7acb\u523b\u5389\u559d\u9053\uff1a\u201c\u6eda\uff01\u6eda\u5f00\uff01\u201d \u3000\u3000\u5353\u957f\u537f\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u5728\u4e0b\u4e43\u662f\u4e00\u756a\u597d\u610f\uff0c\u9601\u4e0b\u4f55\u5fc5\u5982\u6b64\uff01\u201d \u3000\u3000\u65e0\u5f71\u7f57\u5239\u8427\u94c1\u98ce\u51b7\u7b11\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u610f\u2014\u2014\u54fc\u54fc\uff0c\u6211\u4ece\u524d\u542c\u5230\u732b\u6293\u6b7b\u4e86\u8001\u9f20\uff0c\u53c8\u53bb\u5047 \u54ed\uff0c\u8fd8\u4e0d\u76f8\u4fe1\u4e16\u4e0a\u6709\u6b64\u7b49\u60c5\u4e8b\uff0c\u4eca\u65e5\u4e00\u89c1\u2014\u2014\u54fc\u54fc\uff0c\u771f\u6559\u6211\u597d\u7b11\u5f97\u5f88\uff0c\u6211\u8427\u94c1\u98ce\u53c8\u975e\u4e09\u5c81\u5b69 \u7ae5\uff0c\u4f60\u8fd9\u5047\u6148\u60b2\u9a97\u5f97\u4e86\u8c01\uff1f\u201d \u3000\u3000\u5353\u957f\u537f\u53c8\u6014\u4e86\u4e00\u6014\uff0c\u5fc3\u5ff5\u6570\u8f6c\uff0c\u5374\u53ea\u89c9\u65e0\u8a00\u53ef\u5bf9\uff0c\u4ed6\u81ea\u89c9\u81ea\u5df1\u7684\u4e00\u756a\u597d\u610f\u6b64\u523b\u7adf\u88ab\u4eba\u5982 \u6b64\u770b\u5f85\uff0c\u5fc3\u4e2d\u867d\u6709\u4e9b\u6c14\u5fff\uff0c\u4f46\u8f6c\u5ff5\u4e00\u60f3\uff0c\u4eba\u5bb6\u8bf4\u7684\u5374\u53c8\u662f\u53e5\u53e5\u5b9e\u8a00\uff0c\u82e5\u8bf4\u4e00\u4eba\u5c06\u53e6\u4e00\u4eba\u6740\u6b7b \u4e4b\u540e\uff0c\u518d\u53bb\u597d\u610f\u67e5\u770b\u90a3\u4eba\u7684\u4f24\u52bf\uff0c\u522b\u4eba\u81ea\u7136\u4e07\u4e07\u4e0d\u4f1a\u76f8\u4fe1\u3002 \u3000\u3000\u4ed6\u5446\u5446\u5730\u6014\u4e86\u534a\u664c\uff0c\u53ea\u89c1\u90a3\u5343\u91cc\u660e\u9a7c\u4ef0\u5367\u5728\u5730\u4e0a\uff0c\u524d\u80f8\u4e00\u7247\u9c9c\u8840\uff0c\u5634\u89d2\u66f4\u662f\u8840\u8ff9\u6dcb\u6f13\uff0c \u53cc\u6674\u51f8\u51fa\uff0c\u9762\u65e5\u72f0\u72de\u2014\u2014\u4ed6\u4e0d\u89c9\u957f\u53f9\u4e00\u58f0\uff0c\u95ed\u4e0a\u773c\u775b\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u5728\u4e0b\u5b9e\u5728\u4e00\u756a\u597d\u610f\uff0c\u9601\u4e0b \u5982\u4e0d\u76f8\u4fe1\u2026\u2026\u201d \u3000\u3000\u8bdd\u72b9\u672a\u4e86\uff0c\u6e29\u747e\u4e00\u63a0\u800c\u81f3\uff0c\u622a\u53e3\u8bf4\u9053\uff1a\u201c\u4ed6\u4e0d\u76f8\u4fe1\u5c31\u7b97\u4e86\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u5f20\u5f00\u773c\u6765\uff0c\u53f9\u9053\uff1a\u201c\u6211\u4e0e\u6b64\u4eba\u65e0\u51a4\u65e0\u4ec7\uff0c\u6b64\u523b\u6211\u65e0\u610f\u4f24\u4e86\u4ed6\u7684\u6027\u547d\uff0c\u5fc3\u4e2d\u5b9e\u5728\u4e0d \u5b89\u2026\u2026\u201d \u3000\u3000\u6e29\u747e\u51b7\u51b7\u9053\uff1a\u201c\u82e5\u662f\u4ed6\u4f24\u4e86\u65e0\u6839\u5927\u5e08\u7684\u6027\u547d\u5462\uff1f\u4f60\u662f\u4e3a\u4e86\u6551\u4eba\uff0c\u53c8\u6709\u8c01\u4f1a\u602a\u4f60\uff1f\u96be\u9053\u4f60 \u5e94\u8be5\u8896\u624b\u770b\u7740\u65e0\u5f88\u5927\u5e08\u88ab\u4ed6\u6740\u6b7b\u4e48\uff1f\u201d \u3000\u3000\u5353\u957f\u537f\u4fef\u9996\u6c89\u601d\u534a\u664c\uff0c\u7a81\u53c8\u957f\u53f9\u4e00\u58f0\uff0c\u65b9\u5f85\u7b54\u8bdd\uff0c\u5374\u89c1\u65e0\u5f71\u7f57\u5239\u8427\u94c1\u98ce\u7a81\u7136\u957f\u8eab\u800c\u8d77\uff0c \u76ee\u5c04\u51f6\u5149\uff0c\u5389\u58f0\u9053\uff1a\u201c\u6211\u4e0d\u7ba1\u4f60\u662f\u771f\u610f\u5047\u610f\u3001\u6076\u610f\u597d\u610f\uff0c\u8fd9\u725b\u4e00\u5c71\u603b\u662f\u88ab\u4f60\u7ed9\u6740\u6b7b\u7684\uff0c\u6b64\u540e \u725b\u4e00\u5c71\u7684\u540e\u4ee3\u3001\u5b50\u5973\u3001\u4eb2\u621a\u3001\u670b\u53cb\uff0c\u4f1a\u4e00\u4e2a\u63a5\u7740\u4e00\u4e2a\u7684\u627e\u4f60\u590d\u4ec7\uff0c\u76f4\u5230\u773c\u770b\u7740\u4f60\u4e5f\u50cf\u725b\u4e00\u5c71 \u4e00\u6837\u7684\u6b7b\u53bb\u4e3a\u6b62\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u5fc3\u4e2d\u4f46\u89c9\u60ca\u7136\u800c\u98a4\uff0c\u6ee1\u5934\u5927\u6c57\uff0c\u6d0b\u8749\u800c\u843d\uff0c\u5fd6\u9053\uff1a\u201c\u590d\u4ec7\uff0c\u590d\u4ec7\u2026\u2026\u5440\uff0c\u8fd9\u725b\u4e00 \u5c71\u7684\u5b50\u5973\u8981\u627e\u5bfb\u6211\u590d\u4ec7\uff0c\u8fd8\u4e0d\u662f\u6b63\u5982\u6211\u8981\u5bfb\u4eba\u590d\u4ec7\u4e00\u6837\uff0c\u51a4\u51a4\u76f8\u62a5\uff0c\u4ee3\u4ee3\u5bfb\u4ec7\uff0c\u4f55\u65f6\u624d \u4e86\u2026\u2026\u201d \u3000\u3000\u53ea\u542c\u6e29\u747e\u7a81\u7136\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u65e2\u4e5f\u662f\u725b\u4e00\u5c71\u7684\u670b\u53cb\uff0c\u60f3\u6765\u4f60\u4e5f\u4ee3\u725b\u4e00\u5c71\u590d\u4ec7\u4e86\uff1f\u201d \u3000\u3000\u8427\u94c1\u98ce\u76ee\u5149\u4e00\u8f6c\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u4e3a\u53cb\u62a5\u4ec7\uff0c\u81ea\u662f\u5929\u7ecf\u5730\u4e49\u4e4b\u4e8b\u2026\u2026\u201d \u3000\u3000\u6e29\u747e\u51b7\u7b11\u622a\u53e3\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u82e5\u6709\u6b64\u529b\u91cf\uff0c\u4f60\u4e00\u5b9a\u4f1a\u4ee3\u53cb\u62a5\u4ec7\uff0c\u5c06\u6740\u6b7b\u4f60\u670b\u53cb\u7684\u4eba\u6740\u6b7b\u7684 \u4e86\uff1f\u201d \u3000\u3000\u8427\u94c1\u51e4\u4e0d\u7981\u4e3a\u4e4b\u4e00\u6014\uff0c\u9053\uff1a\u201c\u8fd9\u4e2a\u81ea\u7136\uff01\u201d \u3000\u3000\u6e29\u747e\u63a5\u53e3\u9053\uff1a\u201c\u6b64\u4eba\u867d\u7136\u6740\u6b7b\u4e86\u4f60\u7684\u670b\u53cb\uff0c\u4f46\u5374\u4e0e\u4f60\u65e0\u51a4\u65e0\u4ec7\uff0c\u4f60\u4e3a\u4f55\u8981\u5c06\u4eba\u5bb6\u6740\u6b7b\uff1f \u8fd9\u5c82\u975e\u662f\u65e0\u7406\u4e4b\u6781\u3002\u201d \u3000\u3000\u8427\u94c1\u51e4\u9053\uff1a\u201c\u8fd9\u5c82\u662f\u65e0\u7406\uff0c\u6211\u4ee3\u53cb\u590d\u4ec7\uff0c\u8fd9\u6709\u7406\u6781\u4e86\u3002\u201d \u3000\u3000\u6e29\u747e\u51b7\u7b11\u63a5\u53e3\u9053\uff1a\u201c\u5bf9\u4e86\uff0c\u4f60\u8981\u4ee3\u53cb\u590d\u4ec7\uff0c\u6240\u4ee5\u80fd\u5c06\u4e00\u4e2a\u4e0e\u4f60\u7d20\u65e0\u51a4\u4ec7\u7684\u4eba\u6740\u6b7b\uff0c\u800c\u4e14 \u81ea\u79f0\u6781\u6709\u9053\u7406\uff0c\u90a3\u4e48\u725b\u4e00\u5c71\u82e5\u662f\u6740\u6b7b\u4e86\u6211\u4eec\u7684\u670b\u53cb\uff0c\u6211\u4eec\u518d\u5c06\u4ed6\u6740\u6b7b\uff0c\u5c82\u975e\u662f\u6781\u6709\u9053\u7406\u4e4b \u4e8b\uff1f\u201d\u8427\u94c1\u51e4\u53c8\u4e3a\u4e4b\u4e00\u6123\uff0c\u6e29\u747e\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u725b\u4e00\u5c71\u7acb\u5fc3\u8981\u6740\u6b7b\u6211\u4eec\u7684\u670b\u53cb\uff0c\u6211\u4eec\u662f\u4ee5 \u5148\u5c06\u4ed6\u6740\u6b7b\uff0c\u800c\u6551\u51fa\u6211\u4eec\u7684\u670b\u53cb\uff0c\u96be\u9053\u5c31\u4e0d\u662f\u6781\u6709\u9053\u7406\u7684\u4e8b\u4e48\uff1f\u201d \u3000\u3000\u5979\u7ffb\u6765\u8986\u53bb\uff0c\u53ea\u8bf4\u5f97\u8427\u94c1\u51e4\u4e24\u773c\u53d1\u76f4\uff0c\u54d1\u53e3\u65e0\u8a00\uff0c\u6e29\u747e\u51b7\u51b7\u4e00\u7b11\uff0c\u6325\u624b\u9053\uff1a\u201c\u597d\u597d\u7684\u5c06 \u4f60\u670b\u53cb\u7684\u5c38\u8eab\u5e26\u8d70\u5427\uff0c\u8fd8\u7ad9\u5728\u8fd9\u91cc\u5e72\u4ec0\u4e48\uff01\u201d \u3000\u3000\u8427\u94c1\u98ce\u5446\u4e86\u534a\u664c\uff0c\u4fef\u8eab\u6a2a\u62b1\u8d77\u725b\u4e00\u5c71\u7684\u5c38\u8eab\u7eb5\u8eab\u4e00\u63a0\uff0c\u63a5\u8fde\u4e09\u4e24\u4e2a\u8d77\u843d\uff0c\u4fbf\u81ea\u6d88\u5931\u65e0 \u5f71\u3002 \u3000\u3000\u5353\u957f\u537f\u671b\u7740\u4ed6\u7684\u80cc\u5f71\uff0c\u5251\u7709\u5374\u4ecd\u76b1\u5728\u4e00\u5904\uff0c\u4f3c\u4e4e\u82e5\u6709\u6240\u601d\u3002 \u3000\u3000\u5374\u542c\u770b\u53f0\u4e4b\u4e0a\uff0c\u7a81\u7136\u54cd\u8d77\u4e00\u9635\u6e05\u5b9b\u7684\u638c\u58f0\uff0c\u4e00\u4e2a\u5c16\u7ec6\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u59d1\u5a18\u597d\u5389\u5bb3\u7684\u53e3 \u624d\uff0c\u7ade\u5c06\u4e00\u4e2a\u7f57\u5239\u8bf4\u5f97\u62b1\u5934\u9f20\u7a9c\u800c\u8d70\uff0c\u54c8\u54c8\u2014\u2014\u5f53\u771f\u662f\u820c\u5251\u5507\u67aa\uff0c\u9510\u5982\u5229\u5203\uff0c\u6559\u6211\u5b9e\u5728\u4f69\u670d \u5f97\u5f88\u3002\u201d \u3000\u3000\u8bdd\u58f0\u65b9\u843d\uff0c\u5353\u957f\u537f\u4f46\u89c9\u773c\u524d\u4e00\u82b1\uff0c\u9762\u524d\u5df2\u591a\u4e86\u4e00\u6761\u4eba\u5f71\u3002 \u3000\u3000\u4ed6\u6697\u4e2d\u4e00\u60ca\uff0c\u6b64\u4eba\u8f7b\u529f\u53ef\u7b97\u9ad8\u624b\uff0c\u5b9a\u6674\u671b\u53bb\uff0c\u53ea\u89c9\u6b64\u4eba\u867d\u7136\u6ee1\u5934\u767d\u53d1\uff0c\u9894\u4e0b\u7684\u987b\u5b50\u5374\u522e \u5f97\u5e72\u5e72\u51c0\u51c0\uff0c\u8eab\u4e0a\u7a7f\u7684\uff0c\u66f4\u662f\u4e94\u989c\u516d\u8272\uff0c\u5341\u8272\u7f24\u5f69\uff0c\u7adf\u6bd4\u5987\u4eba\u4e4b\u8f88\u7a7f\u7684\u8fd8\u8981\u82b1\u5999\u3002 \u3000\u3000\u5353\u957f\u537f\u4e00\u773c\u671b\u53bb\uff0c\u51e0\u4e4e\u5fcd\u4e0d\u4f4f\u8981\u7b11\u51fa\u58f0\u6765\uff0c\u6e29\u747e\u89c1\u4e86\u6b64\u4eba\uff0c\u795e\u8272\u5374\u4f3c\u4e4e\u6123\u4e86\u4e00\u6123\uff0c\u53ea\u89c1 \u6b64\u4eba\u888d\u8896\u4e00\u62c2\uff0c\u542b\u7b11\u53c8\u8bf4\u9053\uff1a\u201c\u8001\u592b\u6765\u7684\u771f\u51d1\u5de7\uff0c\u867d\u672a\u89c1\u7740\u59d1\u5a18\u7684\u8eab\u624b\uff0c\u5374\u5df2\u89c1\u5230\u59d1\u5a18\u7684\u53e3 \u820c\uff0c\u5f53\u771f\u662f\u773c\u798f\u4e0d\u6d45\u5f97\u5f88\u3002\u201d \u3000\u3000\u8fd9\u8001\u8005\u4e0d\u4f46\u88c5\u675f\u602a\u5f02\uff0c\u8bf4\u8d77\u8bdd\u6765\uff0c\u7ade\u4ea6\u5c16\u7ec6\u6709\u5982\u5973\u5b50\uff0c\u6e29\u747e\u5fc3\u4e2d\u65e2\u60ca\u4e14\u6068\uff0c\u5979\u4ece\u672a\u89c1\u8fc7 \u6b64\u4eba\uff0c\u7ade\u4e0d\u77e5\u6b64\u4eba\u662f\u54ea\u91cc\u6765\u7684\u3002 \u3000\u3000\u51e0\u65f6\u6765\u7684\uff0c\u4e0d\u7981\u8f6c\u773c\u4e00\u671b\uff0c\u671b\u4e86\u90a3\u4e09\u4e2a\u65b9\u81ea\u8ddf\u6765\u7684\u7ea2\u88f3\u5c11\u5973\u4e00\u773c\uff0c\u53ea\u89c1\u5979\u4eec\u4ea6\u662f\u6ee1\u9762\u832b \u7136\u4e4b\u8272\uff0c\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u6055\u6211\u773c\u62d9\uff0c\u8001\u524d\u8f88\u2026\u2026\u201d \u3000\u3000\u5979\u8bdd\u72b9\u672a\u4e86\uff0c\u8fd9\u8001\u4eba\u5df2\u653e\u58f0\u7b11\u9053\uff1a\u201c\u59d1\u5a18\u5fc3\u91cc\u5927\u7ea6\u5728\u5947\u602a\uff0c\u8001\u592b\u662f\u54ea\u91cc\u6765\u7684\uff0c\u54c8\u54c8\u2014\u2014 \u8001\u592b\u4eca\u6668\u5077\u6109\u6478\u6478\u7684\u4e0a\u5c71\uff0c\u4e00\u76f4\u5230\u4e86\u8fd9\u91cc\uff0c\u4e3a\u7684\u5c31\u662f\u8981\u5927\u5bb6\u5403\u4e0a\u4e00\u60ca\u3002\u201d \u3000\u3000\u6e29\u747e\u51b7\u7b11\u6697\u5fd6\u9053\uff1a\u201c\u82e5\u975e\u6628\u591c\u53d1\u751f\u4e86\u90a3\u4e9b\u4e8b\uff0c\u4f60\u60f3\u4e0a\u5c71\uff0c\u5c82\u6709\u5982\u6b64\u5bb9\u6613\u3002\u201d \u3000\u3000\u770b\u53f0\u4e4b\u4e0a\uff0c\u5341\u4eba\u4e4b\u4e2d\uff0c\u5012\u6709\u4e94\u4eba\u8ba4\u5f97\u6b64\u4eba\uff0c\u6b64\u523b\u8fd9\u4e9b\u6c5f\u6e56\u67ad\u96c4\uff0c\u90fd\u4ecd\u7aef\u5750\u672a\u52a8\uff0c\u4ed6\u4eec\u5f53 \u7136\u4e0d\u77e5\u9053\u6e29\u747e\u4e0e\u4e11\u4eba\u4e4b\u95f4\u7684\u7ea0\u7eb7\uff0c\u662f\u4ee5\u65b9\u624d\u773c\u770b\u5343\u91cc\u660e\u9a7c\u88ab\u6740\u4e4b\u4e8b\uff0c\u6b64\u523b\u4f3c\u4efb\u81ea\u5b89\u7136\u7aef\u5750\uff0c \u50cf\u662f\u53c8\u7b49\u7740\u770b\u70ed\u95f9\u4e00\u6837\u7684\u3002 \u3000\u3000\u53ea\u89c1\u8fd9\u5f69\u670d\u8001\u4eba\u54c8\u54c8\u4e00\u7b11\uff0c\u53c8\u9053\uff1a\u201c\u59d1\u5a18\u867d\u4e0d\u8ba4\u5f97\u8001\u592b\uff0c\u8001\u592b\u5374\u8ba4\u5f97\u59d1\u5a18\u7684\uff0c\u8001\u592b\u5df2\u4e45 \u4ef0\u59d1\u5a18\u7684\u7f8e\u8273\uff0c\u66f4\u4e45\u4ef0\u59d1\u5a18\u7684\u8fa3\u624b\uff0c\u662f\u4ee5\u5fcd\u4e0d\u4f4f\u8981\u5230\u5929\u76ee\u5c71\u6765\u8d70\u4e0a\u4e00\u906d\u2014\u2014\u201d\u6e29\u747e\u7a81\u7136\u77aa\u76ee \u9053\uff1a\u201c\u4f60\u662f\u82b1\u90ce\u6bd5\u4e94\u7684\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u8fd9\u5f69\u773c\u8001\u4eba\u7b11\u5c06\u8d77\u6765\uff0c\u773c\u775b\u772f\u6210\u4e00\u7ebf\uff0c\u773c\u89d2\u7684\u76b1\u7eb9\u66f4\u6709\u5982\u86db\u7f51\u5bc6\u5e03\uff0c\u4f46\u4e00\u53e3\u7259\u9f7f\uff0c\u5374\u4ecd \u662f\u96ea\u767d\u5e72\u51c0\uff0c\u6709\u5982\u73e0\u7389\u3002 \u3000\u3000\u4ed6\u9732\u51fa\u7259\u9f7f\uff0c\u772f\u773c\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u59d1\u5a18\u679c\u7136\u773c\u5149\u96ea\u4eae\uff0c\u4e0d\u9519\u2014\u2014\u8001\u592b\u6bd5\u56db\uff0c\u4fbf\u662f\u6bd4\u90a3\u4e0d\u6210 \u6750\u7684\u82b1\u90ce\u6bd5\u4e94\u66f4\u4e0d\u6210\u6750\u7684\u54e5\u54e5\u3002\u201d \u3000\u3000\u6e29\u747e\u5fc3\u5934\u4e00\u9707\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u96be\u9053\u9601\u4e0b\u4fbf\u662f\u6709\u79f0\u7389\u90ce\u7684\u6bd5\u56db\u5148\u751f\u4e48\uff1f\u201d \u3000\u3000\u5f69\u670d\u8001\u4eba\u53c8\u81ea\u772f\u773c\u4e00\u7b11\uff0c\u8fde\u8fde\u9894\u9996\uff0c\u5353\u957f\u537f\u6628\u591c\u5728\u8f66\u53a2\u4e4b\u5916\u542c\u5f97\u90a3\u4e9b\u7ea2\u88f3\u5c11\u5973\u6240\u8bf4\u82b1\u90ce \u6bd5\u4e94\u88ab\u6e29\u747e\u524a\u53bb\u9f3b\u5b50\u4e4b\u4e8b\uff0c\u6b64\u65f6\u542c\u89c1\u8fd9\u8001\u4eba\u81ea\u62a5\u59d3\u540d\uff0c\u5fc3\u4e2d\u4ea6\u4e0d\u7981\u4e3a\u4e4b\u4e00\u52a8\uff0c\u6697\u81ea\u5fd6\u9053\uff1a \u201c\u6b64\u4eba\u60f3\u5fc5\u662f\u6765\u4e3a\u4ed6\u5f1f\u5f1f\u590d\u4ec7\u7684\u3002\u201d \u3000\u3000\u7acb\u5373\u76ee\u5149\u7684\u7684\uff0c\u5168\u795e\u6212\u5907\u8d77\u6765\uff0c\u90a3\u4e09\u4e2a\u7ea2\u88f3\u5c11\u5973\u89c1\u4e86\u8fd9\u8001\u4eba\u7684\u5947\u88c5\u5f02\u670d\uff0c\u518d\u542c\u89c1\u8fd9\u8001\u5f97 \u5df2\u5feb\u6210\u7cbe\u7684\u8001\u4eba\u5c45\u7136\u8fd8\u53eb\u505a\u7389\u90ce\uff0c\u5fc3\u4e2d\u5374\u4e0d\u89c9\u597d\u7b11\uff0c\u53ea\u662f\u4e0d\u6562\u7b11\u51fa\u58f0\u6765\u3002 \u3000\u3000\u53ea\u89c1\u8fd9\u7389\u90ce\u6bd5\u56db\u772f\u8d77\u773c\u775b\uff0c\u4e0a\u4e0a\u4e0b\u4e0b\u8198\u4e86\u6e29\u747e\u51e0\u773c\uff0c\u9053\uff1a\u201c\u59d1\u5a18\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u4e0d\u4f46\u53e3\u624d\u7280 \u5229\uff0c\u800c\u4e14\u76ee\u4e2d\u795e\u5149\u6ee1\u76c8\uff0c\u663e\u89c1\u5185\u529f\u5df2\u6709\u6839\u57fa\uff0c\u96be\u602a\u6211\u90a3\u4e0d\u6210\u6750\u5f1f\u5f1f\uff0c\u8981\u88ab\u59d1\u5a18\u524a\u53bb\u9f3b\u5b50\u3002\u201d \u3000\u3000\u6e29\u747e\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u90a3\u4e48\u9601\u4e0b\u6b64\u6765\uff0c\u83ab\u975e\u662f\u8981\u4e3a\u4ee4\u5f1f\u590d\u4ec7\u7684\uff0c\u90a3\u4e48\u2026\u2026\u201d \u3000\u3000\u54ea\u77e5\u5979\u8bdd\u58f0\u672a\u4e86\uff0c\u8fd9\u7389\u90ce\u6bd5\u56db\u5374\u5df2\u5927\u6447\u5176\u5934\uff0c\u622a\u53e3\u9053\uff1a\u201c\u4e0d\u5bf9\uff0c\u4e0d\u5bf9\uff0c\u4e0d\u4f46\u4e0d\u5bf9\uff0c\u800c\u4e14 \u5927\u9519\u7279\u9519\u4e86\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u3001\u6e29\u747e\u9f50\u5730\u4e00\u6123\u3002 \u3000\u3000\u53ea\u542c\u8fd9\u7389\u90ce\u53c8\u9053\uff1a\u201c\u90a3\u6bd5\u4e94\u53c8\u8001\u53c8\u7cca\u6d82\uff0c\u81ea\u5df1\u4e0d\u7167\u7167\u955c\u5b50\uff0c\u5374\u60f3\u6765\u5403\u5929\u9e45\u8089\uff0c\u59d1\u5a18\u83ab\u8bf4 \u524a\u53bb\u4ed6\u7684\u9f3b\u4e8e\uff0c\u5c31\u7b97\u518d\u524a\u53bb\u4ed6\u4e24\u53ea\u8033\u6735\uff0c\u8001\u592b\u6211\u4e0d\u4f46\u4e0d\u4f1a\u53cd\u5bf9\uff0c\u540f\u4e0d\u4f1a\u529b\u4ed6\u590d\u4ec7\uff0c\u53ea\u4f2f\u8fd8\u8981 \u9f13\u638c\u8d5e\u6210\u7684\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u3001\u6e29\u747e\u4e24\u4eba\u5fc3\u4e2d\u4e0d\u7ea6\u800c\u540c\u5730\u6697\u5fd6\uff1a\u201c\u4eba\u9053\u9f99\u751f\u4e5d\u5b50\uff0c\u5b50\u5b50\u4e0d\u540c\uff0c\u770b\u6765\u5f53\u771f\u7edd\u975e\u865a \u8bed\uff0c\u90a3\u2018\u82b1\u90ce\u2019\u6bd5\u4e94\u867d\u7136\u65e0\u803b\uff0c\u60f3\u4e0d\u5230\u4ed6\u5374\u6709\u4e2a\u6df1\u660e\u5927\u4e49\u7684\u5144\u957f\u3002\u5509\u2014\u2014\u5f53\u771f\u662f\u4eba\u4e0d\u53ef\u8c8c \u76f8\uff0c\u8fd9\u6bd5\u56db\u770b\u6765\u867d\u4e0d\u5f97\u4eba\u5fc3\uff0c\u60f3\u4e0d\u5230\u5374\u662f\u80f8\u895f\u78ca\u843d\u7684\u6c49\u5b50\u3002\u201d \u3000\u3000\u4e00\u5ff5\u81f3\u6b64\uff0c\u4e24\u4eba\u4e0d\u7981\u5bf9\u8fd9\u4f4d\u7389\u90ce\u6bd5\u56db\u5927\u8d77\u597d\u611f\uff0c\u6e29\u747e\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u8bf7\u6055\u6211\u65e0\u793c\uff0c\u65b9\u624d\u591a \u6709\u5192\u72af\u4e4b\u5904\u3002\u201d \u3000\u3000\u5979\u8bed\u58f0\u4e00\u987f\uff0c\u53c8\u9053\uff1a\u201c\u8001\u524d\u8f88\u6b64\u6765\uff0c\u53ef\u662f\u4e3a\u4e86\u5bb6\u5e08\u2026\u2026\u201d \u3000\u3000\u6b64\u65f6\u6b64\u523b\uff0c\u5979\u4ea6\u4e0d\u613f\u522b\u4eba\u77e5\u9053\u5979\u4e0e\u4e11\u4eba\u4e4b\u95f4\u7684\u60c5\u4e8b\uff0c\u662f\u4ee5\u6b64\u523b\u53e3\u53e3\u58f0\u58f0\u4ecd\u79f0\u201c\u5bb6\u5e08\u201d\u3002 \u3000\u3000\u54ea\u77e5\u5979\u8bed\u5230\u4e2d\u9014\uff0c\u90a3\u7389\u90ce\u6bd5\u56db\u53c8\u4e0d\u4f4f\u6447\u8d77\u624b\u6765\uff0c\u5979\u6123\u4e86\u4e00\u6123\uff0c\u500f\u7136\u987f\u4f4f\u8bdd\u58f0\uff0c\u53ea\u542c\u6bd5\u56db \u9053\uff1a\u201c\u4e0d\u662f\u4e0d\u662f\uff0c\u975e\u4f46\u4e0d\u662f\uff0c\u800c\u4e14\u5927\u9519\u7279\u9519\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u5fc3\u4e2d\u5927\u5947\uff0c\u5fd6\u9053\uff1a\u201c\u4ed6\u8fd9\u4e5f\u4e0d\u662f\uff0c\u90a3\u4e5f\u4e0d\u662f\uff0c\u90a3\u4e48\u4ed6\u6b64\u6765\u5374\u53c8\u4e3a\u4e86\u4ec0\u4e48\u5462\uff1f\u201d \u3000\u3000\u53ea\u89c1\u8fd9\u7389\u90ce\u772f\u773c\u4e00\u7b11\u9053\uff1a\u201c\u8001\u592b\u4e0d\u4f3c\u6bd5\u4e94\u4e0e\u4ee4\u5e08\u8fd8\u6709\u4e09\u5206\u4ea4\u60c5\uff0c\u6b64\u6765\u53c8\u600e\u4f1a\u4e3a\u4e86\u4ee4\u5e08 \u5462\uff1f\u82e5\u662f\u2026\u2026\u54c8\u54c8\uff01\u201d \u3000\u3000\u4ed6\u5927\u7b11\u4e24\u58f0\uff0c\u500f\u7136\u987f\u4f4f\u8bdd\u58f0\uff0c\u53c8\u81ea\u772f\u8d77\u773c\u775b\uff0c\u4e0a\u4e0a\u4e0b\u4e0b\u5730\u6253\u91cf\u7740\u6e29\u747e\uff0c\u6e29\u747e\u88ab\u4ed6\u77a7\u5f97\u597d \u751f\u4e0d\u8010\uff0c\u4f46\u5374\u4e0d\u4ec5\u6076\u8a00\u76f8\u52a0\uff0c\u79c0\u7709\u5fae\u8e59\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u90a3\u4e48\u8001\u524d\u8f88\u6b64\u6765\uff0c\u96be\u9053\u662f\u6e38\u5c71\u73a9 \u6c34\u7684\u4e48\uff1f\u201d \u3000\u3000\u5979\u672c\u5c31\u4e3d\u8d28\u5929\u751f\uff0c\u7b11\u5c06\u8d77\u6765\uff0c\u66f4\u6709\u5982\u767e\u5408\u521d\u653e\uff0c\u67f3\u7709\u8212\u5c55\uff0c\u661f\u7738\u5fae\u6655\uff0c\u7389\u9f7f\u5fae\u73b0\uff0c\u68a8\u7a9d \u6d45\u9732\uff0c\u5f53\u771f\u662f\u56fd\u8272\u5929\u9999\uff0c\u65e0\u4e0e\u4f26\u6bd4\uff0c\u5353\u957f\u537f\u6708\u5149\u52a8\u5904\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u4e0d\u89c9\u770b\u5f97\u5446\u4e86\u3002 \u3000\u3000\u6e29\u747e\u76ee\u5149\u867d\u672a\u671b\u5411\u5353\u957f\u537f\uff0c\u4f46\u5374\u4e5f\u77e5\u9053\u4ed6\u6b63\u5728\u770b\u5979\u3002 \u3000\u3000\u5979\u53ea\u89c9\u5fc3\u91cc\u751c\u751c\u7684\uff0c\u867d\u4e0d\u60f3\u7b11\uff0c\u5374\u5fcd\u4e0d\u4f4f\u8981\u7b11\u4e86\u51fa\u6765\uff0c\u76ee\u5149\u62ac\u5904\uff0c\u5374\u89c1\u90a3\u7389\u90ce\u6bd5\u56db\u4e5f\u6b63 \u5728\u5446\u5446\u5730\u671b\u7740\u5979\u3002 \u3000\u3000\u5979\u7b11\u5bb9\u4e00\u655b\uff0c\u53ea\u89c1\u8fd9\u7389\u90ce\u6bd5\u56db\u6447\u5934\u6643\u8111\uff0c\u55b7\u55b7\u8fde\u58f0\uff0c\u9053\uff1a\u201c\u7f8e\u3001\u7f8e\u3001\u771f\u7f8e\uff01\u201d \u3000\u3000\u8bed\u58f0\u5fae\u987f\uff0c\u7a81\u7136\u53cc\u624b\u4e00\u5206\u3001\u4e00\u626c\uff0c\u5355\u819d\u70b9\u5730\uff0c\u8dea\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u5353\u957f\u537f\u4e00\u6123\uff0c\u6e29\u747e\u66f4\u662f\u5927\u5947\uff0c\u7ea4\u8170\u5fae\u626d\uff0c\u9000\u540e\u4e09\u6b65\u8be7\u58f0\u9053\uff1a\u201c\u8001\u524d\u8f88\uff0c\u4f60\u8fd9\u662f\u5e72\u4ec0 \u4e48\uff1f\u201d \u3000\u3000\u7389\u90ce\u6bd5\u56db\u9053\uff1a\u201c\u4f60\u771f\u7684\u4e0d\u77e5\u9053\u4e48\uff1f\u201d \u3000\u3000\u6e29\u747e\u6447\u9996\u9053\uff1a\u201c\u6211\u771f\u7684\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u738b\u90ce\u6bd5\u56db\u53cc\u624b\u4e00\u5408\uff0c\u6367\u5728\u81ea\u5df1\u7684\u80f8\u524d\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u4f60\u771f\u7684\u4e0d\u77e5\u9053\u2026\u2026\u4f60\u771f\u4e0d\u77e5\u9053\u6211\u7684\u5fc3 \u4e48\uff1f\u2026\u2026\u6211\u6b63\u5728\u5411\u4f60\u6c42\u5a5a\u5440\uff1f\u6211\u8981\u4f60\u7b54\u5e94\uff0c\u7b54\u5e94\u5ac1\u7ed9\u6211\uff0c\u6211\u867d\u7136\u662f\u6bd5\u4e94\u7684\u54e5\u54e5\uff0c\u5374\u957f\u5f97\u6bd4\u4ed6 \u5e74\u8f7b\uff0c\u66f4\u6bd4\u4ed6\u82f1\u4fca\uff0c\u4f60\u867d\u7136\u62d2\u7edd\u4e86\u4ed6\uff0c\u4ed6\u6d3b\u8be5\uff0c\u6211\u60f3\u4f60\u4e00\u5b9a\u4e0d\u4f1a\u62d2\u7edd\u6211\u7684\uff0c\u662f\u5417\uff1f\u201d \u3000\u3000\u5353\u957f\u537f\u3001\u6e29\u747e\u3001\u591a\u4e8b\u5934\u9640\u3001\u7ea2\u88f3\u5c11\u5973\uff0c\u4e00\u8d77\u7741\u5706\u773c\u775b\uff0c\u671b\u5728\u8fd9\u7389\u90ce\u6bd5\u56db\u8eab\u4e0a\uff0c\u89c1\u4e4e\u4ee5\u4e3a \u6b64\u4eba\u75af\u4e86\u3002 \u3000\u3000\u4ed6\u4eec\u6709\u751f\u4ee5\u6765\uff0c\u505a\u68a6\u4e5f\u6ca1\u6709\u60f3\u5230\uff0c\u4e16\u4e0a\u7adf\u4f1a\u6709\u5982\u6b64\u65e0\u803b\u4e4b\u4eba\uff0c\u7adf\u4f1a\u505a\u51fa\u8fd9\u79cd\u65e0\u803b\u4e4b\u4e8b\u3002 \u3000\u3000\u4ed6\u4eec\u7adf\u8fde\u7b11\u90fd\u7b11\u4e0d\u51fa\u6765\u4e86\uff0c\u6c14\u4ea6\u65e0\u6cd5\u6c14\u51fa\u6765\uff0c\u53ea\u542c\u770b\u53f0\u4e4b\u4e0a\uff0c\u53cd\u5012\u7b11\u58f0\u5982\u96f7\uff0c\u90a3\u7389\u90ce\u6bd5 \u56db\u5374\u4ecd\u76f4\u633a\u633a\u7684\u8dea\u5728\u5730\u4e0a\uff0c\u626c\u81c2\u9053\uff1a\u201c\u6211\u5f53\u7740\u522b\u4eba\u8dea\u5728\u4f60\u9762\u524d\uff0c\u8fd9\u8868\u793a\u6211\u5bf9\u4f60\u662f\u591a\u4e48\u75f4\u60c5\uff0c \u4f60\u80fd\u4f24\u5bb3\u4e00\u4e2a\u5982\u6b64\u75f4\u60c5\u7684\u4eba\u7684\u5fc3\u5417\uff1f\u4e0d\u4f1a\u7684\uff0c\u4e0d\u4f1a\u7684\uff0c\u4f60\u662f\u90a3\u4e48\u2026\u2026\u201d \u3000\u3000\u5353\u957f\u537f\u518d\u4e5f\u5fcd\u4e0d\u4f4f\uff0c\u5927\u559d\u4e00\u58f0\u9053\uff1a\u201c\u4f4f\u53e3\uff01\u201d \u3000\u3000\u7389\u90ce\u6bd5\u56db\u9762\u8272\u4e00\u6c89\uff0c\u9053\uff1a\u201c\u6211\u8bf4\u6211\u7684\uff0c\u5e72\u4f60\u4f55\u4e8b\uff0c\u96be\u9053\u4f60\u5728\u5403\u918b\u5417\uff1f\u201d \u3000\u3000\u5353\u957f\u537f\u94c1\u9762\u5982\u6c34\uff0c\u751f\u51b7\u800c\u7b80\u77ed\u7684\u8bf4\u9053\uff1a\u201c\u7ad9\u8d77\u6765\u3002\u201d \u3000\u3000\u7389\u90ce\u6bd5\u56db\u5e72\u6da9\u800c\u67af\u8001\u7684\u9762\u5bb9\u50cf\u662f\u4e00\u5757\u5e72\u6a58\u76ae\uff0c\u7a81\u7136\u5728\u706b\u4e0a\u70b8\u5f00\u4e86\u82b1\uff0c\u4ed6\u626b\u5e1a\u822c\u7684\u53cc\u7709\uff0c \u91d1\u9c7c\u822c\u7684\u773c\uff0c\u5728\u8fd9\u4e00\u77ac\u4e4b\u95f4\uff0c\u90fd\u500f\u7136\u5012\u7ad6\u8d77\u6765\uff0c\u6012\u559d\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u4f60\u53ef\u77e5\u9053\u8001\u592b\u662f\u8c01\uff1f\u4f60 \u7adf\u6562\u5728\u8001\u592b\u9762\u524d\u8fd9\u822c\u653e\u8086\uff0c\u54fc\u54fc\uff0c\u5927\u7ea6\u771f\u7684\u662f\u6d3b\u5f97\u6709\u4e9b\u4e0d\u8010\u70e6\u4e86\u3002\u201d \u3000\u3000\u8fd9\u7389\u90ce\u6bd5\u56db\u65b9\u624d\u8a00\u8bed\u6e29\u67d4\uff0c\u67d4\u5982\u7ef5\u7f8a\uff0c\u6b64\u523b\u8bf4\u8d77\u8bdd\u6765\uff0c\u5374\u662f\u76ee\u77aa\u7709\u7ad6\uff0c\u731b\u5982\u6012\u72ee\uff0c\u53ea\u662f \u4ed6\u5374\u5fd8\u4e86\u81ea\u5df1\u6b64\u523b\u4ecd\u7136\u8dea\u5728\u5730\u4e0a\uff0c\u8eab\u4f53\u7684\u59ff\u52bf\uff0c\u4e0e\u9762\u76ee\u7684\u8868\u60c5\u592a\u4e0d\u76f8\u79f0\uff0c\u90a3\u4e9b\u7ea2\u88f3\u5c11\u5973\u89c1\u8fd9 \u7b49\u60c5\u51b5\uff0c\u5fcd\u4e0d\u4f4f\u53c8\u90fd\u63a9\u53e3\u6697\u7b11\u8d77\u6765\u3002 \u3000\u3000\u5353\u957f\u537f\u6012\u6c14\u66f4\u70bd\uff0c\u65b9\u5f85\u6012\u559d\uff0c\u5374\u542c\u6bd5\u56db\u51b7\u54fc\u4e00\u58f0\uff0c\u53c8\u5df2\u63a5\u53e3\u8bf4\u9053\uff1a\u201c\u6211\u8bf4\u8bdd\u7684\u5bf9\u8c61\u662f\u8fd9 \u4f4d\u59d1\u5a18\uff0c\u53ea\u8981\u8fd9\u4f4d\u59d1\u5a18\u613f\u610f\u542c\uff0c\u8c01\u90fd\u4e0d\u80fd\u53eb\u6211\u4f4f\u53e3\uff0c\u4f60\u8fd9\u5c0f\u5b50\u7b97\u662f\u4ec0\u4e48\uff0c\u54fc\u54fc\uff0c\u5f53\u771f\u662f\u72d7\u6349 \u8001\u9f20\uff0c\u591a\u7ba1\u95f2\u4e8b\uff01\u201d \u3000\u3000\u5353\u957f\u537f\u6123\u4e86\u4e00\u6123\uff0c\u4ed6\u751f\u6765\u76f4\u80a0\u76f4\u809a\uff0c\u5fc3\u4e2d\u6240\u60f3\u4e4b\u4e8b\uff0c\u534a\u70b9\u4e0d\u4f1a\u8f6c\u5f2f\uff0c\u6b64\u523b\u4e0d\u7981\u6697\u5fd6\uff1a \u201c\u662f\u4e86\uff0c\u6211\u66fe\u542c\u4eba\u8bf4\u8fc7\uff0c\u5973\u5b50\u6700\u4e0d\u559c\u522b\u4eba\u5949\u627f\uff0c\u8fd9\u59d3\u6bd5\u7684\u6ee1\u53e3\u80e1\u8a00\uff0c\u6e29\u747e\u5374\u5e76\u672a\u2014\u2014\u201d\u60f3\u5230 \u8fd9\u91cc\uff0c\u5fcd\u4e0d\u4f4f\u76ee\u5149\u659c\u8198\u6e29\u747e\u4e00\u773c\u3002 \u3000\u3000\u5374\u542c\u6e29\u747e\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u59d3\u6bd5\u7684\uff0c\u4f60\u8bf4\u4e86\u4e00\u5806\u5e9f\u8bdd\uff0c\u6211\u4e95\u6ca1\u6709\u559d\u6b62\uff0c\u4f60\u77e5\u9053\u662f\u4e3a\u4e86\u4ec0 \u4e48\uff1f\u201d\u7389\u90ce\u6bd5\u56db\u672c\u867d\u6ee1\u9762\u6012\u6c14\uff0c\u5ffd\u7136\u542c\u89c1\u6e29\u747e\u7adf\u7136\u5bf9\u81ea\u5df1\u8bf4\u8d77\u8bdd\u6765\uff0c\u800c\u4e14\u83ba\u58f0\u71d5\u8bed\uff0c\u8bed\u6c14\u4e2d \u5e76\u65e0\u6012\u6c14\uff0c\u5fc3\u4e2d\u4e0d\u7981\u4e00\u8361\uff0c\u7acb\u523b\u67d4\u58f0\u9053\uff1a\u201c\u60f3\u6765\u662f\u6211\u7684\u4e00\u7247\u771f\u5fc3\u8bda\u610f\uff0c\u6253\u52a8\u4e86\u59d1\u5a18\u7684\u82b3\u5fc3\uff0c \u662f\u4ee5\u2014\u2014\u201d\u6e29\u747e\u6447\u4e86\u6447\u5934\uff0c\u63a5\u53e3\u9053\uff1a\u201c\u4e0d\u5bf9\uff01\u201d \u3000\u3000\u7389\u90ce\u6bd5\u56db\u7b11\u5bb9\u4e00\u655b\uff0c\u4f46\u77ac\u5373\u53c8\u542b\u7b11\u9053\uff1a\u201c\u90a3\u4e48\u53ef\u662f\u59d1\u5a18\u542c\u6211\u8bf4\u7684\u5341\u5206\u597d\u542c\uff0c\u662f\u4ee5\u2014\u2014\u201d \u4ed6\u8bdd\u672a\u8bf4\u5b8c\uff0c\u6e29\u747e\u53c8\u81ea\u6447\u9996\u63a5\u53e3\u9053\uff1a\u201c\u4e5f\u4e0d\u5bf9\uff01\u201d \u3000\u3000\u5979\u8f7b\u8f7b\u4e00\u62c2\u8863\u89d2\uff0c\u5634\u89d2\u4f3c\u7b11\u975e\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u6211\u5c0f\u7684\u65f6\u5019\uff0c\u4e00\u4e2a\u51ac\u5929\u7684\u65e9\u4e0a\uff0c\u6b63\u5750\u5728\u9662\u5b50 \u91cc\u6652\u592a\u9633\uff0c\u5ffd\u7136\u6709\u4e00\u6761\u75af\u72d7\u8dea\u6765\u5bf9\u6211\u4e71\u5420\uff0c\u6211\u6c14\u4e0d\u8fc7\uff0c\u5c31\u628a\u5b83\u6253\u8dd1\u4e86\uff0c\u54ea\u77e5\u6211\u2026\u2026\u6211\u59d1\u59d1\u8d70 \u6765\u770b\u89c1\uff0c\u5374\u5c06\u6211\u9a82\u4e86\u4e00\u987f\uff0c\u8bf4\u4e00\u4e2a\u5973\u5b69\u5b50\u5e94\u8be5\u6587\u9759\u4e9b\uff0c\u600e\u4e48\u53ef\u4ee5\u548c\u75af\u72d7\u4e00\u822c\u89c1\u8bc6\uff01\u201d \u3000\u3000\u5979\u8bed\u58f0\u672c\u5c31\u5a07\u67d4\u52a8\u542c\uff0c\u9762\u4e0a\u66f4\u6c38\u8fdc\u5e26\u7740\u4e09\u5206\u7b11\u5bb9\uff0c\u6b64\u523b\u9633\u5149\u6e29\u67d4\u5730\u6620\u5728\u5979\u9762\u5bb9\u4e0a\uff0c\u66f4\u663e \u5f97\u5979\u5a07\u988a\u5982\u82b1\u3002 \u3000\u3000\u7389\u90ce\u6bd5\u56db\u76f4\u770b\u5f97\u5fc3\u75d2\u96be\u6293\uff0c\u5fcd\u4e0d\u4f4f\u9053\uff1a\u201c\u662f\u6781\uff0c\u662f\u6781\uff0c\u59d1\u5a18\u4eca\u65e5\u8fd9\u822c\u6587\u9759\uff0c\u60f3\u5fc5\u5b9a\u662f\u5e7c \u65f6\u6559\u517b\u6781\u4f73\u4e4b\u6545\u3002\u201d \u3000\u3000\u6e29\u747e\u5fae\u5fae\u4e00\u7b11\uff0c\u53c8\u9053\uff1a\u201c\u6211\u6587\u9759\u867d\u4e0d\u89c1\u5f97\uff0c\u4f46\u5374\u771f\u7684\u518d\u4e5f\u4e0d\u548c\u75af\u72d7\u4e00\u822c\u89c1\u8bc6\u4e86\uff0c\u4ee5\u540e\u518d \u6709\u75af\u72d7\u5728\u6211\u65c1\u8fb9\u72c2\u5420\uff0c\u6211\u53ea\u8981\u8d70\u5f00\u4e00\u70b9\uff0c\u8ba9\u8ba9\u5b83\u2026\u2026\u201d \u3000\u3000\u5979\u8bed\u58f0\u4e00\u987f\uff0c\u76ee\u5149\u5ffd\u7136\u6e29\u67d4\u5730\u843d\u5728\u5353\u957f\u537f\u8eab\u4e0a\uff0c\u63a5\u53e3\u9053\uff1a\u201c\u53ef\u662f\u73b0\u5728\u5982\u679c\u6709\u75af\u72d7\u5728\u6211\u65c1 \u8fb9\u72c2\u5420\uff0c\u6211\u5c31\u518d\u4e5f\u4e0d\u5fc5\u8ba9\u5b83\u4e86\uff0c\u56e0\u4e3a\u6211\u73b0\u5728\u5df2\u7ecf\u6709\u4e86\u2026\u2026\u201d \u3000\u3000\u5782\u9996\u4e00\u7b11\uff0c\u65b9\u81ea\u63a5\u9053\uff1a\u201c\u6709\u4e86\u4e00\u4e2a\u4fdd\u62a4\u6211\u7684\u4eba\u3002\u201d \u3000\u3000\u7ea4\u624b\u5fae\u62ac\uff0c\u7f13\u7f13\u6307\u5411\u6bd5\u56db\uff1a\u201c\u957f\u537f\uff0c\u4f60\u66ff\u6211\u628a\u8fd9\u6761\u75af\u72d7\u8d76\u8d70\uff0c\u597d\u4e0d\u597d\uff1f\u201d \u3000\u3000\u5353\u957f\u537f\u89c1\u5979\u7adf\u8fd8\u5728\u4e0e\u6bd5\u56db\u542b\u7b11\u800c\u8a00\uff0c\u5fc3\u4e2d\u6b63\u662f\u6012\u6124\u586b\u81ba\uff0c\u6068\u4e0d\u5f97\u7acb\u65f6\u6389\u9996\u4e0d\u987e\u800c\u53bb\uff0c\u6b64 \u523b\u95fb\u8a00\u6123\u4e86\u4e00\u6123\uff0c\u624d\u604d\u7136\u4e86\u89e3\u5979\u7684\u542b\u610f\uff0c\u5fc3\u4e2d\u4e0d\u89c9\u53c8\u7b11\u53c8\u607c\uff0c\u8fd9\u5c11\u5973\u5f53\u771f\u8c03\u76ae\u5f97\u5f88\uff0c\u6b64\u65f6\u6b64 \u523b\uff0c\u5c45\u7136\u8fd8\u6709\u5fc3\u60c5\u6765\u8bf4\u7b11\uff0c\u8f6c\u76ee\u671b\u53bb\uff0c\u53ea\u89c1\u90a3\u7389\u90ce\u6bd5\u56db\u76f4\u633a\u633a\u8dea\u5728\u5730\u4e0a\uff0c\u9762\u4e0a\u53c8\u7ea2\u53c8\u7d2b\u6709\u5982 \u732a\u809d\uff0c\u7a81\u7136\u5927\u559d\u4e00\u58f0\uff0c\u8df3\u5c06\u8d77\u6765\uff0c\u6233\u6307\u6e29\u747e\uff0c\u7834\u53e3\u5927\u9a82\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u59ae\u5b50\uff0c\u5f53\u771f\u662f\u4e0d\u8bc6\u62ac \u4e3e\uff0c\u6bd5\u56db\u592a\u7237\u597d\u610f\u62ac\u4e3e\u4f60\u2014\u2014\u201d\u8bdd\u58f0\u672a\u4e86\uff0c\u5ffd\u89c9\u4e00\u80a1\u52b2\u98ce\u5f53\u80f8\u88ad\u6765\uff0c\u5a01\u731b\u5f3a\u52b2\uff0c\u7adf\u662f\u81ea\u5df1\u751f \u5e73\u672a\u9047\u3002 \u3000\u3000\u4ed6\u5927\u8c05\u4e4b\u4e0b\uff0c\u8eab\u5f62\u4e00\u65cb\uff0c\u500f\u7136\u6ed1\u5f00\u4e94\u5c3a\uff0c\u5b9a\u775b\u671b\u53bb\uff0c\u53ea\u89c1\u5353\u957f\u537f\u9762\u5e26\u5bd2\u971c\uff0c\u6325\u638c\u51b7\u7b11\u8bf4 \u9053\uff1a\u201c\u6211\u624b\u638c\u4e09\u6325\u4e4b\u540e\uff0c\u4f60\u82e5\u8fd8\u5728\u6b64\u5730\uff0c\u5c31\u83ab\u602a\u6211\u624b\u4e0b\u65e0\u60c5\u4e86\uff01\u201d \u3000\u3000\u7389\u90ce\u6bd5\u56db\u4f3c\u4e4e\u88ab\u4ed6\u638c\u98ce\u4e4b\u5f3a\u52b2\u6240\u60ca\uff0c\u9762\u8272\u4e00\u53d8\uff0c\u5012\u9000\u4e09\u6b65\uff0c\u5353\u957f\u537f\u624b\u638c\u4e24\u6325\uff0c\u89c1\u4ed6\u5df2\u6709 \u53bb\u610f\uff0c\u5fc3\u4e2d\u4e0d\u7981\u4e00\u5bbd\uff0c\u8981\u77e5\u9053\u4ed6\u751f\u5177\u6027\u60c5\uff0c\u65b9\u624d\u4f24\u4e86\u90a3\u5343\u91cc\u660e\u9a7c\u725b\u4e00\u5c71\u7684\u6027\u547d\uff0c\u5fc3\u4e2d\u5df2\u662f\u5927 \u4e3a\u4e0d\u5fcd\uff0c\u6b64\u523b\u5bf9\u8fd9\u7389\u90ce\u6bd5\u56db\u867d\u7136\u6781\u4e3a\u607c\u6012\u4f46\u5374\u4ecd\u4e0d\u613f\u51fa\u624b\u76f8\u4f24\u3002 \u3000\u3000\u738b\u90ce\u6bd5\u56db\u5012\u9000\u4e09\u6b65\uff0c\u8eab\u5f62\u65b9\u81ea\u5411\u540e\u4e00\u8f6c\uff0c\u7a81\u53c8\u6e9c\u6e9c\u7684\u4e00\u4e2a\u8f6c\u8eab\uff0c\u5feb\u4f3c\u65cb\u98ce\uff0c\u624b\u638c\u5fae\u626c\uff0c \u52b2\u98ce\u4e09\u9053\uff0c\u5206\u5411\u5353\u957f\u537f\u524d\u80f8\u5c06\u53f0\u3001\u7384\u5173\u3001\u4e73\u6cc9\u4e09\u5904\u5927\u7a74\u88ad\u6765\uff0c\u8fd9\u4e09\u9053\u6697\u5668\u4e0d\u4f46\u4f53\u79ef\u5947\u5c0f\uff0c\u96be \u4ee5\u89c9\u5bdf\uff0c\u800c\u4e14\u53c8\u662f\u5728\u6bd5\u56db\u8f6c\u8eab\u4e4b\u95f4\u53d1\u51fa\uff0c\u5353\u957f\u537f\u4f46\u89c9\u773c\u524d\u5fae\u82b1\uff0c\u6697\u5668\u8ddd\u79bb\u81ea\u5df1\u524d\u80f8\uff0c\u5df2\u4e0d\u53ca \u4e09\u5c3a\u3002 \u3000\u3000\u6e29\u747e\u60c5\u6025\u5173\u5fc3\uff0c\u82b1\u5bb9\u60e8\u53d8\uff0c\u6a31\u549b\u4e00\u58f0\u6251\u4e0a\u524d\u53bb\uff0c\u53ea\u89c1\u5353\u957f\u537f\u867d\u7136\u80f8\u8179\u4e00\u7f29\uff0c\u811a\u4e0b\u4e0d\u52a8\uff0c \u524d\u80f8\u7adf\u7136\u7f29\u540e\u4e00\u5c3a\uff0c\u4f46\u8fd9\u4e00\u70b9\u6697\u5668\uff0c\u5374\u4ecd\u90fd\u7740\u7740\u5b9e\u5b9e\u51fb\u5728\u4ed6\u8eab\u4e0a\uff0c\u6e29\u747e\u76ee\u5149\u52a8\u5904\uff0c\u53ea\u89c9\u773c\u524d \u4e00\u9ed1\uff0c\u8111\u4e2d\u4e00\u9635\u6655\u7729\uff0c\u8e6c\u8e6c\u8e6c\u8fde\u9000\u6570\u6b65\uff0c\u9669\u4e9b\u4e00\u8de4\u8dcc\u5728\u5730\u4e0a\u3002\u7389\u90ce\u6bd5\u56db\u4e00\u58f0\u602a\u7b11\uff0c\u9053\uff1a\u201c\u8fd9 \u5c0f\u5b50\u5f20\u72c2\uff0c\u4e5f\u8981\u4f60\u89c1\u89c1\u6bd5\u56db\u592a\u7237\u7684\u4e00\u4e00\u4e00\u201d\u8bdd\u58f0\u672a\u4e86\uff0c\u5ffd\u89c1\u5353\u957f\u537f\u4f38\u624b\u4e00\u63a5\uff0c\u63a5\u5728\u638c\u4e2d\u3002 \u3000\u3000\u7389\u90ce\u6bd5\u56db\u4e00\u9635\u5927\u60ca\uff0c\u770b\u53f0\u4e4b\u4e0a\uff0c\u591a\u662f\u6b66\u6797\u9ad8\u624b\uff0c\u773c\u5149\u660e\u9510\uff0c\u662f\u4ee5\u90a3\u6697\u5668\u867d\u7ea4\u5c0f\uff0c\u8fd9\u4e9b\u4eba \u4e5f\u4ff1\u90fd\u770b\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u6b64\u523b\u5fc3\u4e2d\u4ea6\u4e0d\u7981\u5927\u611f\u60ca\u6115\uff0c\u6709\u7684\u7adf\u5fcd\u4e0d\u4f4f\u8131\u53e3\u60ca\u547c\u51fa\u58f0\u6765\u3002 \u3000\u3000\u6e29\u747e\u5b9a\u4e86\u5b9a\u795e\uff0c\u5f20\u5f00\u773c\u5e18\uff0c\u65b9\u5f85\u6328\u5230\u5353\u957f\u537f\u8eab\u4e0a\uff0c\u67e5\u770b\u4ed6\u7684\u4f24\u52bf\uff0c\u6b64\u523b\u89c1\u4ed6\u5c45\u7136\u65e0\u6059\uff0c \u5fc3\u4e2d\u60ca\u559c\u4ea4\u96c6\uff0c\u5f20\u53e3\u534a\u664c\uff0c\u7adf\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u5353\u957f\u537f\u5251\u7709\u8f69\u5904\uff0c\u51b7\u51b7\u4e00\u7b11\uff0c\u7a81\u7136\u624b\u638c\u4e00\u626c\uff0c\u638c\u4e2d\u90a3\u4e09\u652f\u6bd4\u666e\u901a\u5f62\u72b6\u5c0f\u4e86\u4e00\u500d\u7684\u4e94\u68f1\u94a2 \u9488\uff0c\u4fbf\u5df2\u539f\u5c01\u4e0d\u52a8\u5730\u88ad\u5411\u6bd5\u56db\uff0c\u98ce\u58f0\u5c16\u9510\uff0c\u7ade\u6bd4\u6bd5\u56db\u65b9\u624d\u51fb\u51fa\u4e4b\u65f6\uff0c\u529b\u9053\u8fd8\u8981\u5f3a\u52b2\u6570\u500d\u3002 \u3000\u3000\u8fd9\u4e09\u652f\u4e94\u68f1\u94a2\u9488\uff0c\u672c\u662f\u7389\u90ce\u6bd5\u56db\u626c\u540d\u6c5f\u6e56\u7684\u6697\u5668\uff0c\u5a01\u529b\u867d\u4e0d\u53ca\u4e11\u4eba\u6e29\u5982\u7389\u7684\u65e0\u5f71\u795e\u9488\u9738 \u9053\uff0c\u4f46\u5374\u4e5f\u662f\u89c1\u8840\u5c01\u5589\uff0c\u6781\u4e3a\u6b79\u6bd2\uff0c\u800c\u4e14\u950b\u5229\u65e0\u6bd4\uff0c\u518d\u52a0\u4e0a\u7389\u90ce\u6bd5\u56db\u624b\u52b2\u975e\u540c\u5c0f\u53ef\uff0c\u7eb5\u662f\u8eab \u6000\u91d1\u949f\u7f69\u3001\u94c1\u5e03\u886b\u3001\u5341\u4e09\u592a\u4fdd\u6a2a\u7ec3\u4e00\u7c7b\u529f\u592b\u4e4b\u4eba\uff0c\u82e5\u662f\u9047\u7740\u6b64\u7b49\u6697\u5668\uff0c\u4e00\u6837\u4e5f\u662f\u65e0\u6cd5\u62b5\u6321\u3002 \u662f\u4ee5\u7389\u90ce\u6bd5\u56db\u518d\u4e5f\u60f3\u4e0d\u5230\u81ea\u5df1\u53d1\u51fa\u7684\u6697\u5668\uff0c\u7ade\u4f24\u4e0d\u4e86\u8fd9\u7384\u886b\u5c11\u5e74\uff0c\u513f\u523b\u60ca\u6050\u4e4b\u4e0b\uff0c\u5374\u89c1\u8fd9\u4e09 \u652f\u94a2\u9488\u7adf\u7136\u539f\u7269\u9000\u56de\uff0c\u4ed6\u6df1\u77e5\u81ea\u5df1\u8fd9\u79cd\u6697\u5668\u7684\u5a01\u529b\uff0c\u5f53\u4e0b\u5413\u5f97\u5fc3\u80c6\u7686\u4e27\uff0c\u518d\u4e5f\u987e\u4e0d\u5f97\u989c\u9762\uff0c \u8eab\u5f62\u4e00\u7f29\uff0c\u5c31\u5730\u4e00\u6eda\uff0c\u53ea\u515a\u98ce\u58f0\u4e09\u7f15\uff0c\u81ea\u5934\u9876\u98de\u8fc7\uff0c\u5212\u7a7a\u98de\u51fa\u6570\u4e08\uff0c\u65b9\u81ea\u843d\u5230\u5730\u4e0a\uff0c\u4ed6\u7ffb\u8eab \u7ad9\u8d77\uff0c\u989d\u4e0a\u51b7\u6c57\u6d94\u6d94\u843d\u4e0b\uff0c\u65b9\u624d\u9762\u4e0a\u7684\u72c2\u50b2\u4e4b\u610f\uff0c\u6b64\u523b\u65e9\u5df2\u7ecf\u6d88\u5931\u65e0\u5f71\uff0c\u5fc3\u4e2d\u5374\u5140\u81ea\u5927\u60d1\u4e0d \u89e3\uff0c\u6697\u5fd6\u9053\uff1a\u201c\u4ee5\u6211\u7684\u624b\u52b2\u53d1\u51fa\u7684\u8fd9\u4e9b\u4e94\u68f1\u6bd2\u9488\uff0c\u7eb5\u662f\u94c1\u677f\uff0c\u4e5f\u672a\u89c1\u80fd\u4ee5\u62b5\u6321\uff0c\u8fd9\u5c11\u5e74\u662f\u51ed \u7740\u4ec0\u4e48\uff0c\u96be\u9053\u4ed6\u7684\u5185\u529f\u771f\u5df2\u7ec3\u5230\u91d1\u94a2\u4e0d\u574f\u4e4b\u8eab\u5417\uff1f\u201d \u3000\u3000\u4ed6\u81ea\u7136\u4e0d\u4f1a\u77e5\u9053\uff0c\u5353\u957f\u537f\u8eab\u4e0a\u6240\u7a7f\u7684\u8fd9\u6761\u7384\u8272\u957f\u886b\uff0c\u770b\u8d77\u6765\u867d\u7136\u6beb\u4e0d\u8d77\u773c\uff0c\u4f46\u5176\u5b9e\u5374\u975e \u51e1\u7269\uff0c\u6b63\u662f\u53f8\u7a7a\u8001\u4eba\u4ee5\u6614\u5e74\u5f97\u81ea\u9ec4\u5c71\u7684\u90a3\u602a\u86c7\u4e4b\u76ae\u6240\u88c1\u5236\uff0c\u4e11\u4eba\u6e29\u5982\u7389\u90a3\u65f6\u4e0d\u8fdc\u5343\u91cc\u8d76\u81f3\u9ec4 \u5c71\uff0c\u4e00\u534a\u4e5f\u662f\u4e3a\u7740\u6b64\u7269\u4e86\u3002 \u3000\u3000\u4e16\u4e8b\u4e4b\u5947\uff0c\u6709\u4e9b\u7684\u786e\u4e0d\u662f\u5e38\u7406\u6240\u80fd\u5fd6\u5ea6\uff0c\u8fd9\u602a\u86c7\u4e4b\u76ae\uff0c\u4e0d\u53ea\u89c1\u7389\u90ce\u6bd5\u56db\u5446\u7acb\u534a\u664c\uff0c\u9762\u4e0a \u9635\u9752\u9635\u767d\uff0c\u7ec8\u4e8e\u6697\u53f9\u4e00\u58f0\uff0c\u8eab\u5f62\u5fae\u626d\uff0c\u8f6c\u8eab\u6b32\u53bb\uff0c\u54ea\u77e5\u6e29\u747e\u7a81\u7136\u51b7\u51b7\u4e00\u7b11\uff0c\u559d\u9053\uff1a\u201c\u7ad9 \u4f4f\uff01\u201d \u3000\u3000\u6bd5\u56db\u8eab\u5f62\u5fae\u987f\uff0c\u6e29\u747e\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u4e71\u5420\u4e86\u534a\u5929\uff0c\u5c31\u8fd9\u6837\u60f3\u8d70\u4e86\u5417\uff1f\u201d \u3000\u3000\u7ea4\u8db3\u5fae\u70b9\uff0c\u66fc\u5999\u7684\u8eab\u5f62\uff0c\u7a81\u7136\u60ca\u9e3f\u822c\u63a0\u5230\u8eab\u4fa7\u3002\u201c\u4f60\u90a3\u5b9d\u8d1d\u5f1f\u5f1f\uff0c\u7559\u4e0b\u4e00\u53ea\u9f3b\u5b50\uff0c\u4f60\u597d \u6b79\u4e5f\u8be5\u7559\u4e0b\u4e00\u4e9b\u6765\u5440\uff01\u201d \u3000\u3000\u7389\u90ce\u6bd5\u56db\u5fc3\u4e2d\u53c8\u6025\u53c8\u6012\uff0c\u53ea\u89c1\u6e29\u747e\u5fae\u4e00\u62db\u624b\uff0c\u7acb\u5728\u8fdc\u5904\u7684\u4e00\u4e2a\u7ea2\u88f3\u5c11\u5973\u7acb\u523b\u5982\u98de\u63a0\u6765\uff0c \u53cc\u624b\u9012\u4e0a\u4e00\u67c4\u5f62\u4f3c\u5315\u9996\u7684\u77ed\u5251\uff0c\u5251\u957f\u4ec5\u6709\u4e00\u5c3a\u957f\uff0c\u5251\u67c4\u5236\u4f5c\u7684\u6781\u4e3a\u7cbe\u81f4\uff0c\u5251\u8eab\u5374\u6676\u83b9\u96ea\u4eae\uff0c \u5728\u65e5\u5149\u4e0b\u95ea\u95ea\u751f\u5149\uff0c\u6b63\u662f\u5f53\u65f6\u6c5f\u6e56\u5973\u5b50\u5e38\u7528\u7684\u9632\u8eab\u4e4b\u7269\u3002 \u3000\u3000\u6e29\u747e\u53e3\u89d2\u542b\u7b11\uff0c\u63a5\u8fc7\u77ed\u5251\uff0c\u4f38\u51fa\u6625\u8471\u822c\u7684\u7ea4\u7ea4\u7389\u6307\uff0c\u5728\u5251\u8eab\u4e0a\u8f7b\u8f7b\u4e00\u62b9\u3001\u4e00\u5f39\uff0c\u53ea\u542c \u201c\u545b\u201d\u7684\u4e00\u58f0\u8f7b\u541f\uff0c\u6e29\u747e\u53c8\u9053\uff1a\u201c\u662f\u9f3b\u5b50\u6709\u7528\u4e9b\u8fd8\u662f\u8033\u6735\u6709\u7528\u4e9b\uff1f\u5440\u2014\u2014\u60f3\u6765\u4e24\u6837\u90fd\u6ca1\u6709\u4ec0 \u4e48\u7528\uff0c\u4f60\u8fd8\u662f\u4e24\u6837\u90fd\u7559\u4e0b\u6765\u5427\uff01\u201d \u3000\u3000\u7389\u90ce\u6bd5\u56db\u6697\u9053\u4e00\u58f0\uff1a\u201c\u7f62\u4e86\u3002\u201d \u3000\u3000\u4ed6\u867d\u7136\u539a\u989c\u65e0\u803b\uff0c\u5374\u53c8\u600e\u80fd\u5f53\u7740\u8fd9\u4e9b\u4eba\u4e4b\u9762\uff0c\u53d7\u5230\u5982\u6b64\u6b3a\u8fb1\uff0c\u5fc3\u4e2d\u867d\u77e5\u81ea\u5df1\u4e07\u4e07\u4e0d\u662f\u90a3 \u7384\u886b\u5c11\u5e74\u7684\u654c\u624b\uff0c\u4f46\u6b64\u65f6\u6b64\u523b\uff0c\u5374\u5c11\u4e0d\u5f97\u8981\u62fc\u4e0a\u4e00\u62fc\uff0c\u8f6c\u5ff5\u4e4b\u95f4\uff0c\u6b63\u5f85\u7ffb\u8eab\u4e00\u638c\u51fb\u51fa\u3002 \u3000\u3000\u54ea\u77e5\u5c31\u5728\u4ed6\u5fc3\u5ff5\u8f6c\u5904\uff0c\u8eab\u540e\u7a81\u7136\u5fae\u98ce\u62c2\u8fc7\uff0c\u90a3\u7384\u886b\u5c11\u5e74\uff0c\u7adf\u5df2\u63a0\u5230\u4ed6\u8eab\u524d\uff0c\u4ed6\u9762\u8272\u4e00 \u53d8\uff0c\u5374\u542c\u90a3\u7384\u886b\u5c11\u5e74\u7adf\u7f13\u7f13\u9053\uff1a\u201c\u653e\u4ed6\u53bb\u5427\uff01\u201d \u3000\u3000\u6e29\u747e\u5fae\u5fae\u4e00\u6115\uff0c\u79cb\u6ce2\u6570\u8f6c\uff0c\u7a81\u7136\u201c\u5413\u54e7\u201d\u4e00\u7b11\uff0c\u653e\u4e0b\u624b\u638c\uff0c\u5a07\u7b11\u9053\uff1a\u201c\u6211\u624d\u4e0d\u4f1a\u548c\u4ed6\u4e00 \u822c\u89c1\u8bc6\u54e9\uff0c\u521a\u624d\u4e0d\u8fc7\u662f\u6545\u610f\u5413\u5413\u4ed6\u7684\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u542b\u7b11\u9053\uff1a\u201c\u90a3\u5c31\u597d\u4e86\u3002\u201d \u3000\u3000\u624b\u638c\u4e00\u6325\uff1a\u201c\u8fd8\u4e0d\u5feb\u8d70\u3002\u201d \u3000\u3000\u4ed6\u89c1\u6e29\u747e\u5982\u6b64\u7684\u67d4\u987a\uff0c\u5fc3\u4e2d\u4e0d\u89c9\u5927\u611f\u5b89\u6170\uff0c\u90a3\u4e9b\u7ea2\u886b\u5c11\u5973\u89c1\u5230\u6e29\u747e\u5e73\u65e5\u90a3\u6837\u5201\u86ee\uff0c\u4eca\u65e5 \u5bf9\u8fd9\u7384\u886b\u5c11\u5e74\u5374\u53c8\u5982\u6b64\u6e29\u9a6f\uff0c\u5f7c\u6b64\u5bf9\u671b\u4e00\u773c\uff0c\u5fc3\u4e2d\u5404\u81ea\u4e0d\u89e3\u3002 \u3000\u3000\u7389\u90ce\u6bd5\u56db\u76ee\u5149\u6028\u6bd2\u7684\u77aa\u4e86\u5353\u957f\u537f\u4e00\u773c\uff0c\u7a81\u7136\u957f\u53f9\u4e00\u58f0\u9053\uff1a\u201c\u9752\u5c71\u4e0d\u6539\uff0c\u7eff\u6c34\u957f\u6d41\u2026\u2026\u201d \u3000\u3000\u8bed\u58f0\u672a\u4e86\uff0c\u4ed6\u8eab\u5f62\u5df2\u5982\u98de\u63a0\u53bb\uff0c\u53ea\u89c1\u8fdc\u8fdc\u4ecd\u6709\u8bed\u58f0\u4f20\u6765\uff1a\u201c\u6b64\u6069\u6b64\u5fb7\uff0c\u6765\u65e5\u5fc5\u62a5\u3002\u201d \u3000\u3000\u6e29\u747e\u79cb\u6ce2\u6d41\u8f6c\uff0c\u671b\u7740\u4ed6\u7684\u80cc\u5f71\uff0c\u8f7b\u8f7b\u8bf4\u9053\uff1a\u201c\u4f60\u5bf9\u4ed6\u867d\u7136\u8fd9\u4e48\u4ec1\u6148\uff0c\u53ef\u662f\u4ed6\u5374\u672a\u5fc5\u4f1a\u611f \u6fc0\u4f60\uff0c\u8bf4\u4e0d\u5b9a\u4ee5\u540e\u8fd8\u8981\u627e\u4f60\u62a5\u4ec7\u4e5f\u8bf4\u4e0d\u5b9a\uff0c\u5509\u2014\u2014\u90a3\u4e48\u4f60\u8fd9\u53c8\u662f\u4f55\u6614\uff1f\u201d \u3000\u3000\u5353\u957f\u537f\u9762\u8272\u4e00\u6c89\uff0c\u6b63\u8272\u9053\uff1a\u201c\u505a\u4eba\u4f46\u6c42\u81ea\u5df1\u65e0\u6127\u4e8e\u5fc3\uff0c\u81f3\u4e8e\u522b\u4eba\u600e\u6837\u5bf9\u6211\u65e0\u6240\u8c13\uff0c\u54fc \u54fc\uff0c\u6211\u5c82\u662f\u65bd\u6069\u5fd8\u62a5\u4e4b\u4eba\u2014\u2014\u201d\u8bf4\u5230\u8fd9\u91cc\uff0c\u5ffd\u7136\u77a5\u89c1\u6e29\u747e\u76ee\u5149\u5728\u95ea\u52a8\uff0c\u9690\u7740\u6cea\u73e0\uff0c\u77e5\u9053\u81ea\u5e7c \u53d7\u7740\u4e11\u4eba\u6e29\u5982\u738b\u7684\u653e\u7eb5\uff0c\u80fd\u591f\u5982\u6b64\uff0c\u5df2\u662f\u5927\u4e3a\u4e0d\u6613\uff0c\u6709\u65f6\u7eb5\u7136\u884c\u4e3a\u7565\u4e3a\u504f\u6fc0\uff0c\u5374\u4e5f\u96be\u602a\u3002 \u3000\u3000\u4e00\u5ff5\u81f3\u6b64\uff0c\u4ed6\u4e0d\u7981\u67d4\u58f0\u9053\uff1a\u201c\u6709\u4e9b\u4e8b\u4f60\u81ea\u7136\u4e0d\u4f1a\u660e\u4e86\uff0c\u5509\u2014\u2014\u8981\u662f\u4f60\u4ece\u5c0f\u5c31\u8ddf\u7740\u6211\u90a3\u6069 \u5e08\u5728\u4e00\u8d77\u5c31\u4e0d\u4f1a\u2014\u2014\u201d\u8bed\u58f0\u672a\u4e86\uff0c\u5ffd\u542c\u4e00\u58f0\u60e8\u547c\uff0c\u81ea\u8fdc\u5904\u4f20\u6765\uff0c\u58f0\u97f3\u51c4\u60e8\u7edd\u4f26\uff0c\u542c\u6765\u4ee4\u4eba\u6bdb \u9aa8\u60ca\u7136\uff0c\u5353\u957f\u537f\u9762\u8272\u4e00\uff0c\u53d8\uff0c\u8131\u53e3\u9053\uff1a\u201c\u8fd9\u662f\u738b\u90ce\u6bd5\u56db\uff01\u201d \u3000\u3000\u8f6c\u9762\u671b\u5411\u6e29\u747e\uff1a\u201c\u8fd9\u53c8\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d \u3000\u3000\u6e29\u747e\u6447\u4e86\u6447\u5934\uff0c\u5fc3\u4e2d\u7a81\u7136\u4e00\u52a8\uff0c\u9762\u8272\u4e0d\u7981\u53c8\u4e3a\u4e4b\u5927\u53d8\u3002 \u3000\u3000\u90a3\u770b\u53f0\u4e4b\u4e0a\u7684\u6b66\u6797\u7fa4\u8c6a\uff0c\u6709\u4e9b\u867d\u4e0e\u7389\u90ce\u6bd5\u56db\u6709\u6545\u4ea4\uff0c\u4f46\u89c8\u5353\u957f\u537f\u6b66\u529f\u90a3\u822c\u60ca\u4eba\uff0c\u6e29\u747e\u53c8 \u662f\u4e11\u4eba\u6e29\u5982\u7389\u7684\u5f92\u5f1f\uff0c\u8fd9\u4e9b\u4eba\u867d\u7136\u4ff1\u90fd\u4e0d\u662f\u7b49\u95f2\u89d2\u8272\uff0c\u4f46\u5374\u8c01\u90fd\u4e0d\u6562\u62db\u60f9\u6e29\u52a0\u7389\uff0c\u662f\u4ee5\u6bd5\u56db \u53d7\u8fb1\uff0c\u4ed6\u4eec\u5374\u4e00\u76f4\u8896\u624b\u65c1\u89c2\uff0c\u7aef\u5750\u4e0d\u52a8\u3002 \u3000\u3000\u4f46\u6b64\u523b\u7684\u8fd9\u4e00\u58f0\u60e8\u5578\uff0c\u5374\u4f7f\u5f97\u4ed6\u4eec\u4e0d\u7981\u90fd\u957f\u8eab\u800c\u8d77\uff0c\u7fd8\u9996\u671b\u53bb\uff0c\u53ea\u89c1\u4e24\u6761\u6de1\u7ea2\u4eba\u5f71\uff0c\u81ea \u90a3\u8fb9\u5982\u98de\u63a0\u6765\uff0c\u8eab\u6cd5\u8f7b\u76c8\u7f8e\u5999\uff0c\u4e0d\u5f31\u4e8e\u6b66\u6797\u4e2d\u4e00\u6d41\u9ad8\u624b\uff0c\u77ac\u606f\u4e4b\u95f4\uff0c\u4fbf\u5df2\u63a0\u5230\u8fd1\u524d\u3002 \u3000\u3000\u5353\u957f\u537f\u62ac\u76ee\u671b\u53bb\uff0c\u53ea\u89c1\u8fd9\u4e24\u4e2a\u7ea2\u886b\u5c11\u5973\uff0c\u7adf\u662f\u5728\u90a3\u7ea2\u5dfe\u4f1a\u5e2e\u4f17\u60e8\u6b7b\u4e4b\u65f6\u4ece\u5730\u4e0a\u62fe\u8d77\u90a3\u7c92 \u7c89\u7ea2\u8272\u7684\u73e0\u5b50\u7684\u5c0f\u73b2\u3001\u5c0f\u743c\uff0c\u6b64\u523b\u5979\u4fe9\u4eba\u8eab\u5f62\u5982\u98ce\uff0c\u63a0\u5230\u8fd1\u524d\uff0c\u500f\u7136\u987f\u4f4f\u8eab\u5f62\uff0c\u5c0f\u73b2\u7389\u638c\u5e73 \u4f36\uff0c\u638c\u4e2d\u6258\u7740\u4e00\u65b9\u7d20\u7ee2\uff0c\u7ee2\u4e0a\u9c9c\u8840\u6dcb\u6f13\uff0c\u7adf\u8d6b\u7136\u653e\u7740\u4e09\u56e2\u8840\u8089\u3002 \u3000\u3000\u5353\u957f\u537f\u5fc3\u5934\u4e00\u98a4\uff0c\u4ed4\u7ec6\u671b\u6765\uff0c\u624d\u770b\u51fa\u8fd9\u4e09\u56e2\u8840\u8089\uff0c\u7adf\u662f\u4e00\u53cc\u4eba\u8033\uff0c\u4e00\u53ea\u4eba\u9f3b\uff0c\u4e0d\u7981\u8131\u53e3 \u60ca\u547c\u4e00\u58f0\uff0c\u53c8\u81ea\u53d8\u8272\u9053\uff1a\u201c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d \u3000\u3000\u5c0f\u73b2\u3001\u5c0f\u743c\u56db\u9053\u79cb\u6ce2\uff0c\u9f50\u5730\u4e00\u8f6c\uff0c\u9762\u4e0a\u5374\u6728\u7136\u6ca1\u6709\u4e1d\u6beb\u8868\u60c5\uff0c\u7f13\u7f13\u7684\u8d70\u5230\u6e29\u747e\u8eab\u524d\uff0c\u6e29 \u747e\u67f3\u7709\u5fae\u98a6\uff0c\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u8fd9\u53ef\u662f\u90a3\u7389\u90ce\u6bd5\u56db\u7684\uff1f\u201d \u3000\u3000\u5c0f\u73b2\u5fae\u5fae\u9894\u9996\uff0c\u9053\uff1a\u201c\u8fd9\u662f\u72d9\u59d1\u59d1\u53eb\u6211\u4eec\u4ea4\u7ed9\u59d1\u5a18\u7684\u2014\u2014\u201d\u5979\u8bed\u97f3\u5fae\u987f\uff0c\u53c8\u9053\uff1a\u201c\u5979\u8001 \u4eba\u5bb6\u8bf4\uff0c\u65e0\u8bba\u59d1\u5a18\u5bf9\u5979\u600e\u6837\uff0c\u8981\u662f\u6709\u4eba\u5bf9\u59d1\u5a18\u65e0\u793c\uff0c\u5979\u8001\u4eba\u5bb6\u8fd8\u662f\u4e0d\u80fd\u5750\u89c6\uff0c\u6240\u4ee5\u2014\u2014\u5979\u8001 \u4eba\u5bb6\u5c31\u4ee3\u59d1\u5a18\u628a\u8fd9\u59d3\u6bd5\u7684\u9f3b\u5b50\u548c\u8033\u6735\u5272\u4e0b\u6765\u4ea4\u7ed9\u59d1\u5a18\u3002\u201d \u3000\u3000\u53cc\u624b\u4e00\u4f38\uff0c\u7b14\u76f4\u5730\u4ea4\u5230\u6e29\u747e\u9762\u524d\u3002 \u3000\u3000\u5353\u957f\u537f\u5fc3\u4e2d\u6697\u60ca\uff1a\u201c\u8fd9\u4e11\u4eba\u6e29\u5982\u7389\u5f53\u771f\u662f\u795e\u51fa\u9b3c\u6ca1\uff0c\u6211\u534a\u70b9\u6ca1\u6709\u770b\u5230\u5979\u7684\u5f71\u5b50\uff0c\u4f46\u6b64\u95f4 \u53d1\u751f\u4e4b\u4e8b\uff0c\u5979\u5374\u90fd\u4e86\u5982\u6307\u638c\u3002\u201d \u3000\u3000\u6e29\u747e\u5446\u5446\u5730\u671b\u7740\u8fd9\u4e00\u65b9\u8840\u7ee2\uff0c\u5fc3\u4e2d\u4f46\u89c9\u767e\u515a\u4ea4\u96c6\uff0c\u601d\u6f6e\u7ffb\u6d8c\u2026\u2026 \u3000\u3000\u5c0f\u73b2\u7b49\u4e86\u534a\u664c\uff0c\u89c1\u5979\u4ecd\u4e0d\u4f38\u624b\u6765\u63a5\uff0c\u79cb\u6ce2\u4e00\u8f6c\uff0c\u7f13\u7f13\u5782\u4e0b\u8170\u6765\uff0c\u5c06\u8fd9\u4e00\u65b9\u7d20\u7ee2\uff0c\u653e\u5230\u5730 \u4e0a\uff0c\u8f7b\u53f9\u4e00\u58f0\uff0c\u63a5\u7740\u53c8\u9053\uff1a\u201c\u59d1\u5a18\u4e0d\u63a5\uff0c\u6211\u53ea\u5f97\u5c06\u5b83\u653e\u5728\u8fd9\u91cc\uff0c\u53cd\u6b63\u53ea\u8981\u59d1\u5a18\u77e5\u9053\uff0c\u7956\u59d1\u5979 \u8001\u4eba\u5bb6\u5bf9\u59d1\u5a18\u8fd8\u662f\u90a3\u4e48\u5173\u5fc3\u5c31\u597d\u4e86\u3002\u201d \u3000\u3000\u5c0f\u743c\u76ee\u5149\u4e00\u5782\uff0c\u63a5\u9053\uff1a\u201c\u7956\u59d1\u8fd8\u53eb\u6211\u4eec\u544a\u8bc9\u59d1\u5a18\uff0c\u59d1\u5a18\u82e5\u662f\u60f3\u6211\u5979\u8001\u4eba\u5bb6\u62a5\u4ec7\uff0c\u5979\u8001\u4eba \u5bb6\u4e00\u5b9a\u4f1a\u8ba9\u59d1\u5a18\u79f0\u5fc3\u5982\u610f\u7684\uff0c\u4eca\u5929\u665a\u4e0a\uff0c\u5979\u8001\u4eba\u5bb6\u5c31\u5728\u6628\u5929\u665a\u4e0a\u7684\u5385\u5802\u91cc\u7b49\u5019\u59d1\u5a18\u2014\u2014\u201d\u5979 \u773c\u7736\u4f3c\u4e4e\u5fae\u5fae\u4e00\u7ea2\uff0c\u65b9\u81ea\u63a5\u9053\uff1a\u201c\u5979\u8001\u4eba\u5bb6\u8fd8\u8bf4\uff0c\u8bf7\u8fd9\u4f4d\u5353\u76f8\u516c\u4e5f\u548c\u59d1\u5a18\u4e00\u8d77\u53bb\u3002\u201d \u3000\u3000\u5c0f\u73b2\u8f7b\u53f9\u4e00\u58f0\uff0c\u63a5\u9053\uff1a\u201c\u5230\u65f6\u5019\u6211\u4eec\u4e24\u4eba\u4e5f\u4f1a\u5728\u90a3\u91cc\u7b49\u7740\u59d1\u5a18\u7684\uff0c\u6211\u4e24\u4eba\u548c\u59d1\u5a18\u4ece\u5c0f\u5728 \u4e00\u8d77\uff0c\u627f\u8499\u59d1\u5a18\u770b\u5f97\u8d77\uff0c\u6ca1\u6709\u628a\u6211\u4eec\u770b\u6210\u4e0b\u4eba\uff0c\u6211\u4e24\u4eba\u4e5f\u4e00\u76f4\u611f\u6fc0\u5f97\u5f88\uff0c\u5e38\u5e38\u60f3\u4ee5\u540e\u4e00\u5b9a\u8981 \u62a5\u7b54\u59d1\u5a18\uff0c\u53ef\u662f\u2014\u2014\u201d\u5979\u8bed\u58f0\u5fae\u987f\uff0c\u65e5\u5149\u4e00\u5782\uff1a\u201c\u53ef\u662f\u4eca\u5929\u665a\u4e0a\uff0c\u6211\u4e24\u4eba\u518d\u89c1\u59d1\u5a18\u4e4b\u9762\u7684\u65f6 \u5019\uff0c\u5374\u5df2\u662f\u59d1\u5a18\u7684\u4ec7\u4eba\uff0c\u59d1\u5a18\u82e5\u8981\u5bf9\u7956\u59d1\u8001\u4eba\u5bb6\u600e\u6837\uff0c\u90a3\u4e48\u5c31\u8bf7\u59d1\u5a18\u4e5f\u4e00\u6837\u5730\u5bf9\u6211\u4eec\u3002\u201d \u3000\u3000\u5979\u5e7d\u5e7d\u957f\u53f9\u4e00\u58f0\uff0c\u53c8\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u4e0d\u50cf\u59d1\u5a18\u4e00\u6837\u7684\u535a\u5b66\u591a\u624d\uff0c\u6211\u4eec\u90fd\u7b28\u5f97\u5f88\uff0c\u53ef\u662f\u6211\u4eec \u5374\u4e5f\u542c\u8bf4\u8fc7\u4e00\u53e5\u6d3b\uff0c\u90a3\u5c31\u662f\uff1a\u2018\u4eba\u82e5\u4ee5\u56fd\u571f\u5f85\u6211\uff0c\u6211\u4fbf\u4ee5\u56fd\u571f\u5bf9\u4eba\u3002\u2019\u8fd9\u53e5\u8bdd\u6211\u4e0d\u77e5\u8bf4\u5f97\u5bf9 \u4e0d\u5bf9\uff0c\u4f46\u610f\u601d\u6211\u5374\u662f\u61c2\u7684\u3002\u201c\u5c0f\u743c\u76ee\u5149\u4e00\u76f4\u5782\u5411\u5730\u9762\uff0c\u6b64\u523b\u5979\u773c\u7736\u4eff\u4f5b\u66f4\u7ea2\u4e86\uff0c\u5e7d\u5e7d\u5730\u53f9 \u9053\uff1a\u201c\u6211\u4eec\u4e0d\u7ba1\u7956\u59d1\u59d1\u4e3a\u4eba\u600e\u6837\uff0c\u4f46\u5979\u8001\u4eba\u5bb6\u4e00\u76f4\u5bf9\u6211\u4eec\u5f88\u597d\uff0c\u5c31\u50cf\u5979\u8001\u4eba\u5bb6\u4e00\u76f4\u5bf9\u59d1\u5a18\u5f88 \u597d\u4e00\u6837\u3002\u201d \u3000\u3000\u8fd9\u4e24\u4eba\u4e00\u53e5\u8fde\u7740\u4e00\u53e5\uff0c\u53ea\u542c\u5f97\u6e29\u747e\u5fc3\u4e2d\u66f4\u89c9\u8f9b\u9178\u82e6\u8fa3\u3001\u4e94\u5473\u4ff1\u5168\u3002 \u3000\u3000\u5979\u5782\u9996\u65e0\u8a00\uff0c\u6123\u4e86\u534a\u664c\uff0c\u660e\u7738\u4e4b\u4e2d\u53c8\u5df2\u9690\u6cdb\u6cea\u73e0\u3002 \u3000\u3000\u5353\u957f\u537f\u76ee\u5149\u52a8\u5904\uff0c\u53cc\u7709\u5fae\u76b1\uff0c\u50cf\u662f\u60f3\u8bf4\u4ec0\u4e48\uff0c\u5374\u53c8\u7ec8\u4e8e\u5fcd\u4f4f\u3002 \u3000\u3000\u53ea\u89c1\u6e29\u747e\u5782\u9996\u826f\u4e45\uff0c\u622a\u65ad\u4e86\u5979\u7684\u8bf8\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u6211\u4eec\u77e5\u9053\u59d1\u5a18\u7684\u5fc3\u610f\uff0c\u5f53\u7136\u6211\u4eec\u4e0d\u80fd\u52c9 \u5f3a\uff0c\u53ef\u662f\u6211\u4e5f\u542c\u8bf4\uff0c\u53e4\u4eba\u6709\u5272\u888d\u65ad\u4e49\u3001\u5212\u5730\u7edd\u4ea4\u7684\u6545\u4e8b\u2014\u2014\u201d\u5979\u8bdd\u58f0\u500f\u7136\u4e2d\u6b62\uff0c\u624b\u8155\u4e00\u4f38\u4e00 \u7f29\uff0c\u4ece\u6000\u4e2d\u53d6\u51fa\u4e00\u67c4\u77ed\u5251\uff0c\u5de6\u4e8e\u7d27\u634f\u8863\u89d2\uff0c\u53f3\u624b\u4e00\u5212\uff0c\u53ea\u542c\u201c\u5636\u201d\u7684\u4e00\u58f0\uff0c\u90a3\u4ef6\u7ea2\u88f3\u8863\u8882\uff0c \u4fbf\u88ab\u5229\u5251\u4e00\u5206\u4e3a\u4e8c\u3002 \u3000\u3000\u5979\u6697\u4e2d\u4e00\u54ac\u94f6\u7259\uff0c\u63a5\u7740\u9053\uff1a\u201c\u4ece\u6b64\u59d1\u5a18\u4e0d\u8981\u518d\u8ba4\u5f97\u6211\uff0c\u6211\u4e5f\u4e0d\u518d\u8ba4\u5f97\u59d1\u5a18\u4e86\u3002\u201d \u3000\u3000\u7389\u638c\u4e00\u6325\uff0c\u77ed\u5251\u8131\u624b\u98de\u51fa\uff0c\u659c\u659c\u5730\u63d2\u5728\u5730\u4e0a\uff0c\u5916\u5730\u4e00\u58f0\uff0c\u5251\u8eab\u9f50\u6ca1\u5165\u5730\uff0c\u5979\u8868\u9762\u867d\u5f3a\uff0c \u5fc3\u4e2d\u5374\u4e0d\u7981\u5fc3\u9178\uff0c\u4e24\u6ef4\u6cea\u73e0\uff0c\u593a\u7736\u800c\u51fa\uff0c\u62ac\u5934\u671b\u5904\uff0c\u6e29\u747e\u4ea6\u5df2\u5fcd\u4e0d\u4f4f\u6d41\u4e0b\u6cea\u6765\u3002 \u3000\u3000\u4e24\u4eba\u6cea\u773c\u76f8\u5bf9\uff0c\u5353\u957f\u537f\u6697\u53f9\u4e00\u58f0\uff0c\u8f6c\u8fc7\u9762\u53bb\uff0c\u4ed6\u65e0\u6cd5\u8c05\u89e3\uff0c\u9020\u5316\u4e3a\u4f55\u5982\u6b64\u5f04\u4eba\uff0c\u8ba9\u4e16\u4eba \u6709\u5982\u6b64\u591a\u60b2\u60e8\u4e4b\u4e8b\u3002 \u3000\u3000\u770b\u53f0\u4e4b\u4e0a\u7684\u6b66\u6797\u8c6a\u58eb\uff0c\u89c1\u4e86\u8fd9\u7b49\u573a\u9762\uff0c\u4e2a\u4e2a\u5fc3\u4e2d\u4e0d\u7981\u60ca\u7591\u4ea4\u96c6\uff0c\u4f46\u5176\u4e2d\u771f\u76f8\uff0c\u5374\u65e0\u4e00\u4eba \u77e5\u9053\uff0c\u4f17\u4eba\u9762\u9762\u76f8\u89d1\uff0c\u8c01\u4e5f\u65e0\u6cd5\u4f38\u624b\u6765\u7ba1\u6b64\u4e8b\uff0c\u6709\u7684\u4eba\u53ea\u5f97\u8f6c\u8eab\u8d70\u4e86\uff0c\u6709\u7684\u4eba\u867d\u8fd8\u7559\u5728\u5f53 \u5730\uff0c\u4f46\u5374\u65e0\u4e00\u4eba\u63d2\u53e3\u591a\u4e8b\u7684\u3002 \u3000\u3000\u4e00\u76f4\u5782\u9996\u800c\u7acb\u7684\u5c0f\u743c\uff0c\u6b64\u523b\u53c8\u81ea\u957f\u53f9\u4e00\u58f0\uff0c\u7f13\u7f13\u8bf4\u9053\uff0c\u201c\u4e8b\u5df2\u81f3\u6b64\uff0c\u62a1\u4e5f\u518d\u65e0\u8bdd\u8bf4\u3001\u6211 \u60f3\u59d1\u5a18\u603b\u6bd4\u6211\u4eec\u806a\u660e\u5f97\u591a\uff0c\u4f1a\u9009\u62e9\u4e00\u6761\u8be5\u8d70\u7684\u7565\uff0c\u53ef\u662f\u2014\u2014\u201d\u5979\u8bdd\u58f0\u4e00\u987f\uff0c\u7a81\u7136\u8d70\u5411\u5353\u957f \u537f\uff0c\u8bf4\u9053\uff1a\u201c\u5353\u76f8\u516c\uff0c\u4f60\u662f\u806a\u660e\u4eba\uff0c\u6211\u60f3\u95ee\u95ee\u4f60\u4e00\u53e5\u8bdd\uff0c\u4e0d\u77e5\u4f60\u53ef\u613f\u610f\u542c\uff1f\u201d \u3000\u3000\u5353\u957f\u537f\u5fae\u5fae\u4e00\u6123\uff0c\u6c89\u541f\u9053\uff1a\u201c\u4e14\u8bf7\u8bf4\u51fa\u3002\u201d \u3000\u3000\u5c0f\u743c\u7f13\u7f13\u9053\uff1a\u201c\u751f\u80b2\u4e4b\u82e6\uff0c\u56fa\u662f\u4e3a\u4eba\u5b50\u5973\u8005\u5fc5\u62a5\u4e4b\u6069\uff0c\u4f46\u517b\u80b2\u4e4b\u6069\uff0c\u96be\u9053\u5c31\u4e0d\u662f\u5927\u6069 \u4e48\uff0c\u96be\u9053\u5c31\u53ef\u4ee5\u4e0d\u62a5\u4e48\uff1f\u201d \u3000\u3000\u5353\u957f\u537f\u53c8\u81ea\u4e00\u6014\uff0c\u4e0d\u77e5\u8be5\u5982\u4f55\u56de\u7b54\uff0c\u5374\u89c1\u8fd9\u4e24\u4e2a\u5c11\u5973\u5df2\u4e00\u8d77\u8f6c\u8fc7\u8eab\u53bb\uff0c\u5934\u4e5f\u4e0d\u56de\u5730\u8d70 \u4e86\uff0c\u672c\u6765\u7ad9\u5728\u4e00\u65c1\u7684\u7ea2\u88f3\u5c11\u5973\uff0c\u4e2a\u4e2a\u5bf9\u671b\u513f\u773c\uff0c\u4ea6\u81ea\u9ed8\u7136\u8ddf\u5728\u5979\u4eec\u8eab\u540e\uff0c\u5782\u9996\u8d70\u53bb\u3002 \u7b2c\u5341\u516d\u7ae0\u3000\u6069\u91cd\u4ec7\u6df1>> \u53e4\u9f99\u300a\u6708\u5f02\u661f\u90aa\u300b \u7b2c\u5341\u516d\u7ae0\u3000\u6069\u91cd\u4ec7\u6df1 \u3000\u3000\u6e29\u747e\u5782\u9996\u800c\u7acb\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u5fc3\u4e2d\u662f\u6068\u662f\u6028\uff0c\u662f\u6069\u662f\u4ec7\uff0c\u5979\u81ea\u5df1\u4e5f\u5206\u8fa8\u4e0d\u6e05\uff0c\u826f\u4e45\u826f\u4e45\uff0c \u5979\u65b9\u81ea\u62ac\u8d77\u5934\u6765\uff0c\u56db\u4fa7\u5374\u5df2\u522b\u65e0\u4eba\u5f71\uff0c\u770b\u53f0\u4e0a\u7684\u6b66\u6797\u7fa4\u8c6a\uff0c\u6b64\u65f6\u4e5f\u90fd\u8d70\u5f97\u5e72\u5e72\u51c0\u51c0\uff0c\u53ea\u6709\u5353 \u957f\u537f\u4ecd\u7136\u65e0\u8a00\u7684\u7ad9\u5728\u5979\u8eab\u65c1\uff0c\u5c31\u8fde\u90a3\u7d20\u6765\u591a\u4e8b\u7684\u591a\u4e8b\u5934\u9640\u65e0\u6839\u5927\u5e08\uff0c\u6b64\u523b\u90fd\u5df2\u4e0d\u77e5\u8d70\u5230\u54ea\u91cc \u53bb\u4e86\u3002 \u3000\u3000\u9633\u5149\u4ecd\u7136\u707f\u70c2\uff0c\u4ecd\u7136\u5c06\u5730\u4e0a\u7684\u5c16\u5200\uff0c\u6620\u5f97\u95ea\u95ea\u751f\u5149\uff0c\u5979\u7f13\u7f13\u5730\u4fef\u4e0b\u8eab\uff0c\u7f13\u7f13\u5730\u62d4\u8d77\u90a3\u67c4 \u63d2\u5728\u5730\u4e0a\u7684\u77ed\u5251\uff0c\u548c\u81ea\u5df1\u624b\u4e2d\u7684\u4e00\u67c4\u77ed\u5251\uff0c\u653e\u5728\u4e00\u8d77\uff0c\u4e00\u9635\u98ce\u5439\u6765\uff0c\u5979\u7adf\u4f3c\u4e4e\u89c9\u5f97\u6709\u4e9b\u51c9 \u610f\uff0c\u4e8e\u662f\u5979\u8f6c\u8eab\u9762\u5411\u5353\u957f\u537f\uff0c\u6014\u4e86\u8bb8\u4e45\uff0c\u7ec8\u4e8e\u201c\u54c7\u201d\u7684\u4e00\u58f0\uff0c\u6251\u5728\u4ed6\u6000\u91cc\uff0c\u653e\u58f0\u75db\u54ed\u8d77\u6765\u3002 \u3000\u3000\u5979\u53ea\u89c9\u5f97\u6b64\u65f6\u6240\u80fd\u4f9d\u9760\u7684\uff0c\u53ea\u6709\u8fd9\u5bbd\u9614\u800c\u575a\u5b9e\u7684\u80f8\u819b\uff0c\u5979\u611f\u89c9\u5230\u4ed6\u7684\u4e00\u53cc\u81c2\u8180\uff0c\u7d27\u7d27\u5730 \u73af\u62b1\u4f4f\u4e86\u81ea\u5df1\u7684\u80a9\u8180\u3002 \u3000\u3000\u4e00\u4e1d\u6e29\u6696\u7684\u611f\u89c9\u6084\u6084\u4ece\u5979\u5fc3\u4e2d\u5347\u8d77\uff0c\u5979\u52c9\u5f3a\u6b62\u4f4f\u54ed\u58f0\uff0c\u62bd\u6ce3\u7740\u9053\uff1a\u201c\u6211\u8be5\u600e\u4e48\u529e\u5462\uff1f\u957f \u537f\uff0c\u6211\u8be5\u600e\u4e48\u529e\u5462\uff1f\u201d \u3000\u3000\u5353\u957f\u537f\u5782\u4e0b\u76ee\u5149\uff0c\u5979\u5982\u4e91\u7684\u79c0\u53d1\u6b63\u5728\u4ed6\u5bbd\u9614\u7684\u80f8\u819b\u4e0a\u8d77\u4f0f\u7740\uff0c\u5c31\u50cf\u662f\u5e73\u9759\u7684\u6e56\u6cca\u4e2d\u6e29\u67d4 \u7684\u6ce2\u6d6a\u4f3c\u7684\u3002 \u3000\u3000\u4ed6\u62ac\u8d77\u5934\uff0c\u8f7b\u8f7b\u7684\u629a\u6478\u7740\u8fd9\u6e29\u67d4\u7684\u6ce2\u6d6a\uff0c\u5929\u5730\u95f4\u7684\u4e00\u5207\uff0c\u6b64\u523b\u90fd\u50cf\u662f\u5df2\u9759\u6b62\u4e86\u4e0b\u6765\uff0c\u4ed6 \u611f\u89c9\u5f97\u51fa\u5979\u5fc3\u8df3\u7684\u58f0\u97f3\uff0c\u4f46\u5374\u4e5f\u4f3c\u4e4e\u90a3\u4e48\u9065\u8fdc\u3002 \u3000\u3000\u5f3a\u5fcd\u7740\u7684\u62bd\u6ce3\uff0c\u53c8\u5316\u6210\u653e\u58f0\u7684\u75db\u54ed\u3002 \u3000\u3000\u90c1\u79ef\u7740\u7684\u60b2\u54c0\uff0c\u4e5f\u968f\u7740\u8fd9\u653e\u58f0\u7684\u75db\u54ed\uff0c\u800c\u5f97\u5230\u4e86\u5ba3\u6cc4\u3002 \u3000\u3000\u4f46\u662f\u5353\u957f\u537f\u7684\u5fc3\u60c5\uff0c\u5374\u66f4\u52a0\u6c89\u91cd\u4e86\u8d77\u6765\uff0c\u4ed6\u6697\u95ee\u81ea\u5df1\uff1a\u201c\u6211\u8be5\u600e\u4e48\u505a\u5462\uff1f\u751f\u80b2\u4e4b\u82e6\uff0c\u517b \u80b2\u4e4b\u96be\u2026\u2026\u5509\uff0c\u6211\u65e2\u8be5\u8ba9\u5979\u62a5\u7236\u6bcd\u4e4b\u4ec7\uff0c\u5374\u4e5f\u8be5\u8ba9\u5979\u62a5\u517b\u80b2\u4e4b\u6069\u5440\uff01\u201d \u3000\u3000\u4ed6\u65e0\u6cd5\u56de\u7b54\u81ea\u5df1\uff0c\u4ed6\u66f4\u65e0\u6cd5\u56de\u7b54\u6e29\u747e\u3002 \u3000\u3000\u7ec8\u4e8e\uff0c\u4ed6\u505a\u4e0b\u4e86\u4e2a\u51b3\u5b9a\uff0c\u4e8e\u662f\u4ed6\u8f7b\u62cd\u7740\u5979\u7684\u80a9\u8180\uff0c\u51fa\u58f0\u9053\uff1a\u201c\u6211\u4eec\u8d70\u5427\u3002\u201d \u3000\u3000\u6e29\u747e\u670d\u4ece\u5730\u62ac\u8d77\u5934\uff0c\u9ed8\u9ed8\u5730\u968f\u7740\u4ed6\uff0c\u5f80\u5916\u9762\u8d70\u53bb\uff0c\u4ed6\u4eec\u8c01\u4e5f\u4e0d\u613f\u610f\u65bd\u5c55\u8f7b\u529f\uff0c\u7f13\u6162\u5730\u7ed5 \u8fc7\u90a3\u4e00\u7247\u5200\u6d77\uff0c\u8d70\u51fa\u770b\u53f0\uff0c\u8d70\u8fc7\u90a3\u4e00\u6761\u4e24\u65c1\u653e\u6ee1\u68fa\u6728\u7684\u5c0f\u9053\uff0c\u767d\u6768\u7684\u68fa\u6728\uff0c\u5728\u9633\u5149\u4e0b\u5448\u73b0\u7740 \u4e11\u6076\u7684\u989c\u8272\uff0c\u5353\u957f\u537f\u5fc3\u4e2d\u79ef\u90c1\u96be\u6d88\uff0c\u7a81\u7136\u5927\u559d\u4e00\u58f0\uff0c\u626c\u624b\u4e00\u638c\uff0c\u5411\u9053\u65c1\u4e00\u53e3\u68fa\u6728\u5288\u53bb\uff0c\u6fc0\u70c8 \u7684\u638c\u98ce\uff0c\u9707\u5f97\u68fa\u6728\u56db\u6563\u98de\u626c\u3002 \u3000\u3000\u7a81\u5730\u2014\u2014 \u3000\u3000\u68fa\u6728\u4e4b\u4e2d\uff0c\u7adf\u6709\u4e00\u58f0\u60e8\u547c\u53d1\u51fa\uff0c\u547c\u58f0\u5c16\u9510\uff0c\u6709\u5982\u9b3c\u5578\uff01 \u3000\u3000\u5353\u957f\u537f\u84e6\u5730\u4e00\u60ca\uff0c\u53ea\u89c9\u4e00\u9635\u5bd2\u610f\uff0c\u81ea\u811a\u5e95\u76f4\u5347\u80cc\u810a\u2014\u2014\u4ed6\u5446\u82e5\u6728\u9e21\u5730\u5b9a\u6674\u671b\u53bb\uff0c\u53ea\u89c1\u968f \u7740\u56db\u6563\u7684\u68fa\u6728\uff0c\u7adf\u6709\u4e00\u6761\u4eba\u5f71\uff0c\u968f\u7740\u98de\u51fa\uff0c\u201c\u53f6\u201d\u7684\u4e00\u58f0\uff0c\u843d\u5728\u5730\u4e0a\uff0c\u8f97\u8f6c\u4e24\u4e0b\uff0c\u5bc2\u7136\u4e0d \u52a8\u3002 \u3000\u3000\u5353\u957f\u537f\u5446\u5446\u5730\u6123\u4e86\u534a\u664c\uff0c\u4e00\u4e2a\u7bad\u6b65\uff0c\u7a9c\u4e86\u8fc7\u53bb\uff0c\u5730\u4e0a\u8eba\u7740\u7684\u5c38\u8eab\uff0c\u9ed1\u886b\u9ed1\u670d\uff0c\u4ef0\u5929\u800c \u8eba\uff0c\u9762\u4e0a\u6ee1\u662f\u60ca\u6050\u4e4b\u8272\uff0c\u50cf\u662f\u5728\u60ca\u5947\u7740\u6b7b\u4ea1\u7adf\u4f1a\u6765\u5f97\u8fd9\u4e48\u7a81\u7136\u4f3c\u7684\uff0c\u4ed6\u7ade\u8fde\u4e00\u4e1d\u53cd\u6297\u7684\u4f59\u5730 \u90fd\u6ca1\u6709\u3002 \u3000\u3000\u6e29\u747e\u4ea6\u81ea\u5927\u5403\u4e00\u60ca\uff0c\u79cb\u6ce2\u6d41\u8f6c\uff0c\u56db\u4e0b\u800c\u671b\uff0c\u9633\u5149\u4e4b\u4e0b\u5927\u5730\u50cf\u662f\u53c8\u56de\u590d\u4e86\u5bc2\u9759\uff0c\u4f46\u662f\u2014\u2014 \u9053\u65c1\u7684\u68fa\u6728\uff0c\u5374\u4f3c\u4e4e\u6709\u6570\u53e3\u7f13\u7f13\u79fb\u52a8\u4e86\u8d77\u6765\uff0c\u5979\u51e0\u4e4e\u4e0d\u76f8\u4fe1\u81ea\u5df1\u7684\u773c\u775b\uff0c\u6b64\u523b\u7eb5\u7136\u662f\u767d\u5929\uff0c \u7eb5\u7136\u6709\u9633\u5149\u5982\u6b64\u5149\u4eae\uff0c\u4f46\u662f\u5979\u5374\u4e0d\u7531\u81ea\u4e3b\u7684\u6cdb\u8d77\u4e00\u9635\u96be\u4ee5\u63cf\u8ff0\u7684\u60ca\u6817\u4e4b\u610f\uff0c\u5c31\u50cf\u662f\u4e00\u4e2a\u5b64\u72ec \u7684\u4eba\u5728\u7ecf\u8fc7\u9b3c\u706b\u78f7\u78f7\uff0c\u9b3c\u8bed\u557e\u557e\u7684\u8352\u575f\u65f6\u4e00\u6837\u3002 \u3000\u3000\u6e29\u747e\u5446\u7acb\u534a\u664c\uff0c\u5fc3\u5ff5\u6570\u8f6c\uff0c\u7a81\u7136\u67f3\u7709\u4e00\u8f69\uff0c\u53cc\u624b\u9f50\u626c\u3002 \u3000\u3000\u53ea\u89c1\u94f6\u5149\u4e24\u9053\uff0c\u5389\u5982\u95ea\u7535\uff0c\u968f\u7740\u5979\u7ea4\u624b\u4e00\u62ac\u4e4b\u52bf\uff0c\u88ad\u5411\u4e24\u5177\u5e76\u7f6e\u7684\u68fa\u6728\u3002 \u3000\u3000\u201c\u535c\u201d\u7684\u4e24\u58f0\uff0c\u4e24\u67c4\u77ed\u5251\uff0c\u4e00\u8d77\u6df1\u6ca1\u5165\u68fa\u3002 \u3000\u3000\u63a5\u7740\u7adf\u7136\u53c8\u662f\u4e24\u58f0\u51c4\u5389\u7684\u60e8\u547c\uff0c\u9c9c\u7ea2\u7684\u8840\u6c34\uff0c\u6cbf\u7740\u5140\u81ea\u7559\u5728\u68fa\u5916\u7684\u5251\u67c4\uff0c\u4e00\u6ef4\u4e00\u6ef4\u7684\u6d41 \u4e86\u51fa\u6765\uff0c\u6d41\u5728\u7070\u6697\u7684\u5c71\u9053\u4e0a\uff0c\u5353\u957f\u537f\u4e00\u60ca\u56de\u8eab\uff0c\u63a0\u5230\u6e29\u747e\u8eab\u65c1\uff0c\u4e24\u4eba\u65b9\u81ea\u5306\u5306\u4ea4\u6362\u4e86\u4e00\u4e0b\u76ee \u5149\u3002 \u3000\u3000\u7a81\u7136\u2014\u2014 \u3000\u3000\u5c71\u9053\u5c3d\u5934\uff0c\u4f20\u6765\u4e09\u58f0\u6e05\u8106\u7684\u94dc\u9523\u4e4b\u58f0\u3002 \u3000\u3000\u5578\uff01\u5578\uff01\u5578\u2026\u2026 \u3000\u3000\u4f59\u97f3\u8885\u8885\u672a\u6b47\uff0c\u5c71\u9053\u4e24\u65c1\u7684\u767e\u5341\u53e3\u68fa\u6728\u7684\u81ea\u6768\u68fa\u76d6\uff0c\u7a81\u7136\u4e00\u8d77\u5411\u4e0a\u62ac\u8d77\u2014\u2014\u5353\u957f\u537f\u5728\u5927 \u60ca\u4e4b\u4e0b\uff0c\u76ee\u5149\u4e00\u626b\uff0c\u53ea\u89c1\u968f\u7740\u8fd9\u68fa\u76d6\u4e00\u626c\u4e4b\u52bf\uff0c\u6570\u767e\u9053\u4e0d\u7ecf\u7559\u610f\uff0c\u4fbf\u6781\u96be\u5206\u8fa8\u7684\u4e4c\u9ed1\u5149\u534e\uff0c \u5e26\u7740\u5c16\u9510\u98ce\u58f0\uff0c\u7535\u5c04\u800c\u81f3\uff0c\u4ed6\u5fc3\u5934\u4e00\u51c9\uff0c\u987a\u624b\u62c9\u8d77\u6e29\u747e\u7684\u624b\u8155\uff0c\u53cc\u8db3\u4e00\u987f\uff0c\u8eab\u5f62\u51b2\u5929\u800c\u8d77\uff0c \u5e94\u53d8\u4e4b\u8fc5\uff0c\u5f53\u771f\u662f\u60ca\u4e16\u9a87\u4fd7\u3002 \u3000\u3000\u53ea\u89c1\u6570\u767e\u9053\u4e4c\u9ed1\u5149\u534e\uff0c\u81ea\u811a\u5e95\u4ea4\u53c9\u800c\u8fc7\uff0c\u5374\u53c8\u6709\u6570\u767e\u9053\u4e4c\u9ed1\u5149\u534e\uff0c\u81ea\u68fa\u4e2d\u7535\u5c04\u800c\u51fa\uff0c\u4ed6 \u8eab\u5728\u7a7a\u4e2d\uff0c\u85c9\u529b\u65e0\u5904\uff0c\u8fd9\u4e00\u4e0b\u4f3c\u4e4e\u662f\u907f\u65e0\u53ef\u907f\uff0c\u53ea\u542c\u6e29\u747e\u8131\u53e3\u60ca\u547c\u9053\uff1a\u201c\u65e0\u5f71\u795e\u9488\uff01\u201d \u3000\u3000\u4ed6\u5fc3\u5934\u66f4\u662f\u4e00\u5bd2\uff0c\u60f3\u5230\u8fd9\u6697\u5668\u4e4b\u6b79\u6bd2\uff0c\u53ef\u7b97\u5929\u4e0b\u5c11\u6709\uff0c\u81ea\u5df1\u5728\u7a7a\u4e2d\u867d\u80fd\u8eab\u5f62\u53d8\u5316\uff0c\u4f46\u8fd9 \u4e9b\u6697\u5668\u5bc6\u5982\u98de\u8757\uff0c\u81ea\u5df1\u8eab\u7a7f\u86c7\u8863\uff0c\u5982\u518d\u8f6c\u6298\u63a0\u5f00\uff0c\u7eb5\u7136\u8eab\u4e0a\u4e2d\u4e0a\u51e0\u5904\uff0c\u4ea6\u81ea\u65e0\u59a8\uff0c\u4f46\u6e29\u747e\u5c82 \u975e\u51f6\u591a\u5409\u5c11\u3002 \u3000\u3000\u6b64\u523b\u4ed6\u60c5\u51b5\u4e4b\u9669\uff0c\u5f53\u771f\u662f\u751f\u6b7b\u4ff1\u5728\u4e00\u5ff5\u4e4b\u95f4\u3002 \u3000\u3000\u5353\u957f\u537f\u60c5\u6025\u4e4b\u4e0b\uff0c\u5fc3\u4e2d\u7a81\u7136\u95ea\u7535\u822c\u6cdb\u8d77\u4e00\u4e2a\u5ff5\u5934\u3002 \u3000\u3000\u4ed6\u751a\u81f3\u6765\u4e0d\u53ca\u601d\u7d22\u8fd9\u5ff5\u5934\u662f\u5426\u53ef\u884c\uff0c\u4fbf\u5df2\u5927\u559d\u4e00\u58f0\uff0c\u626c\u624b\u4e00\u638c\uff0c\u5411\u6e29\u747e\u5f53\u80f8\u51fb\u51fa\u3002 \u3000\u3000\u8fd9\u4e00\u638c\u638c\u98ce\u6fc0\u70c8\uff0c\u5a01\u52bf\u60ca\u4eba\uff0c\u4f46\u638c\u52bf\u5374\u5e76\u4e0d\u751a\u6025\uff0c\u6e29\u747e\u8eab\u5728\u7a7a\u4e2d\uff0c\u773c\u89c1\u4ed6\u8fd9\u4e00\u638c\u51fb\u6765\uff0c \u5fc3\u4e2d\u65e2\u60ca\u4e14\u602a\uff0c\u6123\u4e86\u4e00\u6123\uff0c\u4ea6\u81ea\u626c\u624b\u62cd\u51fa\u4e00\u638c\u3002 \u3000\u3000\u201c\u5413\u201d\u7684\u4e00\u58f0\uff0c\u4e24\u638c\u76f8\u63a5\uff0c\u6e29\u747e\u5ffd\u89c9\u4e00\u822c\u5185\u529b\u81ea\u638c\u5fc3\u4f20\u6765\u3002 \u3000\u3000\u5979\u672c\u6781\u7075\u6167\uff0c\u5fc3\u4e2d\u7a81\u7136\u4e00\u52a8\uff0c\u638c\u5fc3\u5f80\u5916\u4e00\u7ffb\uff0c\u5a40\u5a1c\u7684\u8eab\u8eaf\uff0c\u4fbf\u5df2\u501f\u7740\u8fd9\u4e00\u638c\u4e4b\u529b\uff0c\u6a2a\u98de \u4e09\u4e08\uff0c\u6709\u5982\u4e00\u652f\u5de7\u71d5\u822c\u98de\u51fa\u5c71\u9053\u4e4b\u5916\u3002 \u3000\u3000\u5353\u957f\u537f\u81ea\u5df1\u4e5f\u501f\u7740\u8fd9\u4e00\u638c\u4e4b\u529b\uff0c\u6a2a\u98de\u5f00\u53bb\uff0c\u773c\u770b\u90a3\u4e9b\u4e4c\u9ed1\u7684\u6697\u5668\u65e0\u5f71\u795e\u9488\uff0c\u5df2\u81ea\u4ea4\u76f8\u5954 \u5411\u81ea\u5df1\uff0c\u624d\u51cc\u7a7a\u7740\u5730\uff0c\u4e0d\u7981\u6697\u9053\u4e00\u58f0\uff1a\u201c\u4fa5\u5e78\u201d\uff0c\u4f38\u624b\u4e00\u634f\uff0c\u638c\u5fc3\u5374\u5df2\u6dcc\u6ee1\u4e00\u638c\u51b7\u6c57\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u8eab\u5f62\u5374\u4e1d\u6beb\u6ca1\u6709\u534a\u5206\u505c\u987f\uff0c\u811a\u5c16\u4e00\u70b9\uff0c\u8eab\u5f62\u4fbf\u5df2\u95ea\u7535\u822c\u5411\u65b9\u624d\u9523\u58f0\u54cd\u5904\u6251\u53bb\uff0c\u76ee \u5149\u95ea\u5904\uff0c\u8fdc\u671b\u53bb\u53ea\u89c1\u5c71\u9053\u5c3d\u5934\u5904\u7684\u4e00\u5177\u68fa\u6728\u4e4b\u4e2d\uff0c\u4ec1\u7acb\u7740\u4e00\u4e2a\u9ed1\u8863\u6c49\u5b50\uff0c\u624b\u4e2d\u4e00\u9762\u91d1\u9523\uff0c\u5728 \u65e5\u5149\u4e01\u95ea\u95ee\u751f\u5149\uff0c\u8fd9\u6c49\u5b50\u4e00\u624b\u626c\u9524\uff0c\u6b63\u5f85\u518d\u6b21\u51fb\u4e0b\uff0c\u671b\u89c1\u5353\u957f\u537f\u5982\u98de\u63a0\u6765\uff0c\u5413\u5f97\u624b\u4e2d\u4e00\u8f6f\uff0c \u201c\u5578\u201d\u7684\u4e00\u58f0\uff0c\u91d1\u9523\u843d\u5730\uff0c\u8eab\u5f62\u4e00\u62e7\uff0c\u4e00\u8dc3\u4e24\u4e08\uff0c\u4ea1\u547d\u5730\u5411\u5c71\u4e0b\u63a0\u53bb\u3002 \u3000\u3000\u5353\u957f\u537f\u5927\u559d\u4e00\u58f0\uff1a\u201c\u54ea\u91cc\u9003\uff01\u201d \u3000\u3000\u500f\u7136\u4e00\u4e2a\u8d77\u843d\uff0c\u8eab\u5f62\u659c\u98de\u6570\u4e08\uff0c\u968f\u540e\u5c31\u8ffd\u4e86\u8fc7\u53bb\uff0c\u6b64\u523b\u6e29\u747e\u4ea6\u5df1\u5982\u98de\u63a0\u6765\uff0c\u53ea\u89c1\u90a3\u9ed1\u8863 \u6c49\u5b50\u811a\u4e0b\u77eb\u5065\uff0c\u8f7b\u529f\u4e0d\u5f31\uff0c\u65bd\u5c55\u7684\u8eab\u6cd5\uff0c\u7adf\u662f\u4e0a\u4e58\u8f7b\u529f\u7edd\u6280\u516b\u6b65\u8d76\u8749\u3002 \u3000\u3000\u5353\u957f\u537f\u811a\u4e0b\u4e0d\u505c\uff0c\u53e3\u4e2d\u5927\u559d\u9053\uff1a\u201c\u83ab\u653e\u8fd9\u53ae\u9003\u8d70\uff01\u201d \u3000\u3000\u4ed6\u4e24\u4eba\u8f7b\u529f\u4e4b\u5999\uff0c\u5f53\u771f\u662f\u7edd\u4e16\u60ca\u4eba\uff0c\u90a3\u6c49\u5b50\u8eab\u6cd5\u867d\u5feb\uff0c\u5374\u518d\u4e5f\u4e0d\u662f\u4ed6\u4e24\u4eba\u7684\u654c\u624b\uff0c\u4e00\u7728 \u773c\u4e4b\u95f4\uff0c\u53ea\u89c9\u8eab\u540e\u8863\u6289\u5e26\u98ce\u4e4b\u58f0\uff0c\u8d8a\u6765\u8d8a\u8fd1\uff0c\u4ed6\u77e5\u9053\u81ea\u5df1\u4e07\u4e07\u65e0\u6cd5\u9003\u51fa\u8fd9\u4e24\u4eba\u7684\u638c\u63e1\uff0c\u7a81\u7136 \u56de\u9996\u5927\u559d\u4e00\u58f0\u9053\uff1a\u201c\u770b\u9556\uff01\u201d \u3000\u3000\u5353\u957f\u537f\u3001\u6e29\u747e\u9f50\u5730\u4e00\u60ca\uff0c\u8eab\u5f62\u5fae\u987f\uff0c\u6e29\u747e\u65e5\u5149\u52a8\u5904\uff0c\u77a5\u89c1\u8fd9\u4eba\u7684\u9762\u76ee\uff0c\u4e0d\u7981\u53d8\u8272\u8131\u53e3\u800c \u51fa\uff0c\u547c\u9053\uff1a\u201c\u4e54\u8fc1\uff01\u201d \u3000\u3000\u547c\u58f0\u672a\u4e86\uff0c\u5df2\u6709\u4e00\u9053\u5bd2\u5149\u51fb\u6765\uff0c\u5353\u957f\u537f\u5251\u7709\u5fae\u626c\uff0c\u968f\u624b\u4e00\u638c\uff0c\u5c06\u8fd9\u4e00\u9053\u9556\u5149\u8fdc\u8fdc\u5288\u843d\uff0c \u843d\u5165\u8349\u4e1b\u4e4b\u4e2d\uff0c\u5927\u559d\u95ee\u9053\uff1a\u201c\u8fd9\u53ae\u4fbf\u662f\u4e54\u8fc1\uff1f\u201d \u3000\u3000\u6e29\u747e\u9053\uff1a\u201c\u4e0d\u9519\u2014\u2014\u8ffd\uff01\u201d \u3000\u3000\u968f\u7740\u547c\u558a\u4e4b\u58f0\uff0c\u4ed6\u4e24\u4eba\u8eab\u5f62\u53c8\u5df2\u63a0\u51fa\u5341\u4e08\uff0c\u524d\u9762\u5df2\u662f\u6811\u6797\uff0c\u5353\u957f\u537f\u773c\u770b\u6b64\u4eba\u5df2\u81ea\u63a0\u4eba\u6811 \u6797\uff0c\u7a81\u7136\u957f\u5578\u4e00\u58f0\uff0c\u8eab\u5728\u7a7a\u4e2d\uff0c\u53cc\u81c2\u5fae\u5206\uff0c\u6709\u5982\u5c55\u7fc5\u795e\u9e70\uff0c\u4e00\u63a0\u4e09\u4e08\uff0c\u5934\u4e0b\u811a\u4e0b\uff0c\u626c\u624b\u4e00 \u638c\uff0c\u5411\u8fd9\u6c49\u5b50\u5f53\u5934\u5288\u4e0b\u3002 \u3000\u3000\u8fd9\u4e00\u638c\u5a01\u52bf\u4e4b\u731b\uff0c\u5f53\u771f\u662f\u65e0\u4e0e\u4f26\u6bd4\uff01\u90a3\u6c49\u5b50\u5fc3\u80c6\u7686\u4e27\uff0c\u4fef\u8eab\u4e00\u7a9c\uff0c\u8eab\u5f62\u843d\u5730\uff0c\u8fde\u6eda\u6570 \u6eda\uff0c\u6eda\u4eba\u6811\u6797\u91cc\uff0c\u5fc3\u4e2d\u65b9\u81ea\u4e00\u5b9a\uff0c\u53ea\u9053\u81ea\u5df1\u4e00\u5165\u5bc6\u6797\uff0c\u6027\u547d\u4fbf\u5df2\u53ef\u6361\u56de\u4e00\u534a\uff0c\u54ea\u77e5\u8eab\u524d\u7a81\u7136 \u4e00\u4eba\u51b7\u559d\u9053\uff1a\u201c\u8fd8\u5f80\u54ea\u91cc\u9003\uff1f\u201d \u3000\u3000\u4ed6\u5fc3\u5934\u4e00\u98a4\uff0c\u4e3e\u76ee\u671b\u8fc7\u53bb\uff0c\u65b9\u624d\u90a3\u7384\u8863\u5c11\u5e74\u5df2\u51b7\u7136\u7acb\u5728\u4ed6\u8eab\u524d\uff0c\u4ed6\u518d\u4e5f\u987e\u4e0d\u5f97\u7f9e\u8fb1\uff0c\u53cc \u65f6\u5411\u540e\u4e00\u633a\uff0c\u8eab\u5f62\u53c8\u81ea\u5411\u540e\u6eda\u51fa\uff0c\u8fd9\u6c5f\u6e56\u4e0b\u4e94\u95e8\u4e2d\u7684\u7edd\u9876\u529f\u592b\u5c31\u5730\u5341\u516b\u6eda\uff0c\u4f3c\u4e4e\u88ab\u4ed6\u8fd0\u7528\u5f97 \u51fa\u795e\u5165\u5316\uff0c\u4f46\u89c1\u4ed6\u67af\u7626\u7684\u8eab\u8eaf\uff0c\u5728\u5730\u4e0a\u6eda\u52a8\u5982\u7403\uff0c\u8fde\u6eda\u6570\u6eda\uff0c\u7a81\u7136\u53c8\u6709\u4e00\u4e2a\u51b0\u51b7\u7684\u58f0\u97f3\u81ea\u4ed6 \u8eab\u540e\u53d1\u51fa\uff1a\u201c\u54ea\u91cc\u53bb\uff01\u201d \u3000\u3000\u4ed6\u5fc3\u5934\u53ef\u81ea\u4e00\u51db\uff0c\u5077\u5077\u4e00\u671b\uff0c\u66f4\u662f\u9762\u5982\u4e0a\u8272\uff0c\u4ed6\u77e5\u9053\u8fd9\u5c11\u5973\u4fbf\u662f\u7ea2\u8863\u5a18\u5a18\u6e29\u5982\u7389\u7684\u5f1f\u5b50 \u6e29\u747e\u3002 \u3000\u3000\u524d\u65e0\u9000\u8def\uff0c\u540e\u6709\u8ffd\u5175\uff0c\u4ed6\u81ea\u77e5\u6b66\u529f\u4e07\u4e07\u4e0d\u662f\u8fd9\u4e24\u4eba\u7684\u654c\u624b\uff0c\u5374\u8fd8\u5984\u60f3\u884c\u9669\u4fa5\u5e78\uff0c\u7a81\u7136\u5389 \u53f1\u4e00\u58f0\uff0c\u53cc\u8098\u3001\u53cc\u819d\u4e00\u8d77\u7528\u529b\u3002 \u3000\u3000\u8eab\u5f62\u81ea\u5730\u4e0a\u5f39\u8d77\uff0c\u53cc\u624b\u8fde\u626c\uff0c\u5341\u6570\u9053\u4e4c\u9ed1\u5149\u534e\uff0c\u4ff1\u90e8\u95ea\u7535\u822c\u5411\u6e29\u747e\u53d1\u51fa\u2014\u2014\u6e29\u747e\u51b7\u7b11\u4e00 \u58f0\uff1a\u201c\u4f60\u8fd9\u53eb\u73ed\u95e8\u5f04\u65a7\uff01\u201d \u3000\u3000\u7ea4\u8eaf\u4e00\u626d\uff0c\u7f57\u8896\u98de\u626c\uff0c\u8fd9\u5341\u6570\u9053\u6697\u5668\u5728\u7728\u773c\u4e4b\u95f4\u4fbf\u6709\u5982\u6ce5\u725b\u5165\u6d77\uff0c\u7acb\u65f6\u65e0\u5f71\u65e0\u8e2a\u3002 \u3000\u3000\u8fd9\u6c49\u5b50\u8eab\u5f62\u4e00\u8f6c\uff0c\u53c8\u5f85\u5411\u4fa7\u9762\u5bc6\u6797\u4e2d\u6251\u53bb\uff0c\u54ea\u77e5\u8eab\u540e\u7a81\u7136\u4e00\u58f0\u51b7\u7b11\uff0c\u4ed6\u4f46\u89c9\u808b\u4e0b\u8170\u95f4\u4e00 \u9ebb\uff0c\u5468\u8eab\u518d\u5df2\u65e0\u529b\uff0c\u6251\u5730\u5750\u5728\u5730\u4e0a\u3002 \u3000\u3000\u5353\u957f\u537f\u4e00\u62db\u5f97\u624b\uff0c\u559d\u9053\uff1a\u201c\u4f60\u4e14\u770b\u4f4f\u8fd9\u53ae\uff0c\u6211\u5230\u90a3\u8fb9\u770b\u770b\u3002\u201d \u3000\u3000\u8bf4\u5230\u201c\u770b\u770b\u201d\u4e24\u5b57\uff0c\u4ed6\u8eab\u5f62\u5df2\u8fdc\u5728\u5341\u4e08\u4e4b\u5916\uff0c\u63a5\u8fde\u4e09\u4e24\u4e2a\u8d77\u843d\uff0c\u53ea\u89c1\u90a3\u7247\u5c71\u9053\u4e4b\u4e0a\u7684\u4e24 \u65c1\u68fa\u6728\u4e2d\uff0c\u5df2\u63a5\u8fde\u8dc3\u51fa\u6570\u5341\u4e2a\u9ed1\u8863\u6c49\u6765\uff0c\u4ed6\u6e05\u5578\u4e00\u58f0\uff0c\u6f5c\u9f99\u5347\u5929\uff0c\u4e00\u51b2\u4e09\u4e08\uff0c\u5927\u559d\u9053\uff1a\u201c\u5168 \u90e8\u7ad9\u4f4f\uff01\u201d \u3000\u3000\u90a3\u4e9b\u6c49\u5b50\u4e00\u60ca\u4e4b\u4e0b\uff0c\u62ac\u76ee\u671b\u53bb\uff0c\u53ea\u89c1\u4e00\u4e2a\u7384\u8863\u5c11\u5e74\u5728\u7a7a\u4e2d\u8eab\u5f62\u5982\u9f99\uff0c\u592d\u77eb\u76d8\u65cb\uff0c\u4ed6\u4eec\u867d \u7136\u90fd\u662f\u4e45\u8d70\u6c5f\u6e56\u7684\u89d2\u8272\uff0c\u4f46\u51e0\u66fe\u89c1\u8fc7\u8fd9\u7b49\u58f0\u5a01\uff0c\u53ea\u5413\u5f97\u811a\u4e0b\u53d1\u8f6f\uff0c\u679c\u7136\u6ca1\u6709\u4e00\u4e2a\u6562\u518d\u8d70\u4e00 \u6b65\u3002 \u3000\u3000\u5353\u957f\u537f\u594b\u8d77\u795e\u5a01\uff0c\u53cc\u638c\u4e00\u626c\uff0c\u51cc\u7a7a\u5288\u4e0b\uff0c\u638c\u51e4\u6fc0\u8361\uff0c\u7adf\u5c06\u5c71\u9053\u4e24\u65c1\u4e00\u5de6\u4e00\u53f3\u4e24\u53e3\u68fa\u6728\u5288 \u5f97\u6728\u7247\u56db\u4e0b\u7eb7\u98de\u3002 \u3000\u3000\u4ed6\u5927\u559d\u4e00\u58f0\uff1a\u201c\u8c01\u518d\u4e71\u8d70\u4e00\u4e0b\uff0c\u8fd9\u68fa\u6728\u4fbf\u662f\u699c\u6837\u3002\u201d \u3000\u3000\u559d\u58f0\u8fc7\u540e\uff0c\u4ed6\u8eab\u5f62\u4fbf\u81ea\u98d8\u98d8\u843d\u4e0b\uff0c\u6709\u5982\u4e00\u7247\u843d\u53f6\u66fc\u5999\u65e0\u58f0\u3002 \u3000\u3000\u90a3\u4e9b\u9ed1\u8863\u6c49\u5b50\u9762\u9762\u76f8\u89d1\uff0c\u5446\u4e86\u534a\u664c\uff0c\u679c\u7136\u4e00\u4e2a\u4e2a\u8d70\u4e86\u56de\u6765\uff0c\u5782\u5934\u4e27\u6c14\u7684\u7acb\u5728\u9053\u65c1\uff0c\u6709\u5982 \u5f85\u5bb0\u7684\u725b\u6cf5\uff0c\u5168\u8eab\u98a4\u6296\uff0c\u9762\u5982\u6b7b\u7070\u3002 \u3000\u3000\u5353\u957f\u537f\u51b7\u7b11\u4e00\u58f0\u540e\uff0c\u6e29\u747e\u5df2\u81ea\u4e00\u624b\u63d0\u7740\u90a3\u6c49\u5b50\uff0c\u63a0\u4e86\u8fc7\u6765\uff0c\u5413\u7684\u4e00\u58f0\uff0c\u5c06\u4ed6\u63b7\u5230\u5730\u4e0a\uff0c \u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u53ae\u679c\u7136\u5c31\u662f\u4e54\u8fc1\uff01\u6211\u65e9\u5df2\u77e5\u9053\u4ed6\u4e0d\u662f\u597d\u4eba\uff0c\u5374\u60f3\u4e0d\u5230\u4ed6\u7adf\u574f\u5230\u8fd9\u79cd\u5730 \u6b65\uff0c\u4ed6\u8fd9\u4e00\u624b\u60f3\u6765\u662f\u60f3\u5c06\u5230\u4f1a\u7684\u6b66\u6797\u8c6a\u58eb\uff0c\u4e00\u7f51\u6253\u5c3d\uff0c\u5509\u4e00\u8981\u662f\u5728\u9ed1\u591c\u4e4b\u4e2d\uff0c\u84e6\u7136\u9047\u7740\u8fd9\u4e48 \u4e00\u624b\uff0c\u8fd8\u771f\u7684\u662f\u53eb\u4eba\u9632\u4e0d\u80dc\u9632\u3002\u201d \u3000\u3000\u5979\u7f13\u7f13\u8d70\u5230\u68fa\u6728\u4e4b\u524d\uff0c\u79cb\u6ce2\u4e00\u8f6c\uff0c\u7a81\u7136\u4ece\u68fa\u4e2d\u53d6\u51fa\u4e00\u5305\u5e72\u7cae\uff0c\u4e00\u58f6\u98df\u6c34\u6765\uff0c\u5411\u5353\u957f\u537f\u4e00 \u626c\uff0c\u5353\u957f\u537f\u5251\u7709\u8f69\u5904\uff0c\u51b7\u54fc\u4e00\u58f0\u3002 \u3000\u3000\u6e29\u747e\u53c8\u9053\uff1a\u201c\u5947\u602a\u7684\u662f\uff0c\u8fd9\u4e9b\u6c49\u5b50\u53d1\u653e\u6697\u5668\u7684\u624b\u6cd5\uff0c\u4ff1\u90fd\u4e0d\u5f31\uff0c\u771f\u4e0d\u77e5\u9053\u8fd9\u59d3\u4e54\u7684\u662f\u4ece \u54ea\u91cc\u627e\u5f97\u6765\u7684\uff1f\u2018\u5979\u8bed\u58f0\u5fae\u987f\uff0c\u53c8\u81ea\u4ece\u5730\u4e0a\u62fe\u8d77\u4e00\u7269\uff0c\u628a\u73a9\u534a\u664c\uff0c\u9001\u5230\u5353\u957f\u537f\u624b\u4e0a\uff0c\u5353\u957f\u537f \u4fef\u9996\u671b\u5904\uff0c\u53ea\u89c1\u6b64\u7269\u4f53\u79ef\u6781\u5c0f\uff0c\u56db\u5468\u8292\u523a\u7a81\u51fa\uff0c\u679c\u7136\u4fbf\u662f\u81ea\u5df1\u5728\u4e34\u5b89\u57ce\u4e2d\u6240\u89c1\u4e4b\u7269\uff0c\u4e0d\u7981\u76b1 \u7709\u9053\uff1a\u201c\u8fd9\u96be\u9053\u53c8\u662f\u2014\u2014\u53c8\u662f\u90a3\u6e29\u5982\u7389\u6697\u4e2d\u8bbe\u4e0b\u7684\u57cb\u4f0f\u4e48\uff1f\u201d \u3000\u3000\u6e29\u747e\u8793\u9996\u8f7b\u5782\uff0c\u67f3\u7709\u6df1\u98a6\uff0c\u8f7b\u58f0\u9053\uff1a\u201c\u8fd9\u65e0\u5f71\u795e\u9488\uff0c\u7684\u786e\u662f\u5979\u4e0d\u4f20\u4e4b\u79d8\uff0c\u9664\u4e86\u6211\u548c\u5c0f \u743c\u3001\u5c0f\u73b2\u4e4b\u5916\u5c31\u4f3c\u4e4e\u6ca1\u6709\u4f20\u7ed9\u8fc7\u522b\u4eba\uff0c\u800c\u4e14\u6b64\u7269\u5236\u9020\u4e0d\u6613\u2014\u2014\u201d\u8bed\u58f0\u7a81\u987f\uff0c\u5782\u9996\u6c89\u601d\u534a\u664c\uff0c \u7a81\u7136\u63a0\u5230\u4e54\u8fc1\u8eab\u4fa7\uff0c\u7ea4\u8db3\u5fae\u62ac\uff0c\u95ee\u7535\u822c\u5728\u4e54\u8fc1\u80cc\u810a\u4e4b\u540e\u8fde\u8e22\u4e09\u811a\u3002 \u3000\u3000\u53ea\u89c1\u4e54\u8fc1\u7626\u5c0f\u7684\u8eab\u8eaf\uff0c\u968f\u7740\u5979\u8fd9\u4e00\u8e22\u4e4b\u52bf\uff0c\u5411\u5916\u6eda\u5f00\u4e09\u6b65\uff0c\u5f20\u53e3\u5410\u51fa\u4e00\u53e3\u6d53\u75f0\uff0c\u7ffb\u8eab\u5750 \u4e86\u8d77\u6765\uff0c\u673a\u8b66\u5c16\u9510\u7684\u773c\u73e0\uff0c\u6ef4\u6e9c\u6e9c\u56db\u4e0b\u4e00\u8f6c\uff0c\u5e72\u54b3\u4e00\u58f0\uff0c\u5782\u4e0b\u5934\u53bb\uff0c\u4ed6\u77e5\u9053\u81ea\u5df1\u6b64\u523b\u5df2\u5728\u4eba \u5bb6\u638c\u63e1\u4e4b\u4e2d\uff0c\u6709\u5982\u74ee\u4e2d\u4e4b\u9cd6\uff0c\u662f\u4ee5\u6839\u672c\u518d\u4e5f\u4e0d\u60f3\u9003\u8d70\u4e4b\u8ba1\uff0c\u5c45\u7136\u76d8\u819d\u5750\u5728\u5730\u4e0a\uff0c\u4e00\u8a00\u4e0d\u53d1\u7791 \u76ee\u6c89\u601d\u8d77\u6765\u3002 \u3000\u3000\u6e29\u747e\u51b7\u7b11\u4e00\u58f0\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u6211\u95ee\u4f60\u4e00\u53e5\u8bdd\uff0c\u4f60\u53ef\u8981\u597d\u751f\u7b54\u590d\u6211\uff01\u201d \u3000\u3000\u4e54\u8fc1\u4ee5\u624b\u652f\u989d\uff0c\u4e0d\u8a00\u4e0d\u52a8\uff0c\u751f\u50cf\u662f\u6839\u672c\u6ca1\u6709\u542c\u5230\u5979\u7684\u8bdd\u4f3c\u7684\u3002 \u3000\u3000\u5353\u957f\u537f\u89c1\u6b64\u4eba\u9762\u5bb9\u5e72\u67af\uff0c\u51f9\u6674\u51f8\u98a7\uff0c\u9762\u4e0a\u751f\u50cf\u5bf8\u8089\u4e0d\u751f\uff0c\u4e00\u773c\u671b\u53bb\uff0c\u4fbf\u77e5\u662f\u5c16\u523b\u4e4b\u50cf\uff0c \u5634\u5507\u66f4\u662f\u523b\u8584\u5982\u7eb8\uff0c\u60f3\u5fc5\u53c8\u662f\u80fd\u8a00\u5584\u8fa9\u4e4b\u5f92\uff0c\u5fc3\u535e\u4e0d\u89c9\u5927\u8d77\u6076\u611f\uff0c\u5251\u7709\u5fae\u76b1\uff0c\u53f1\u9053\uff1a\u201c\u6b64\u4eba \u770b\u6765\u5c16\u72e1\u7edd\u4f26\uff0c\u4f60\u8981\u95ee\u4ed6\u4ec0\u4e48\uff0c\u4ed6\u7eb5\u7136\u7b54\u590d\uff0c\u4e5f\u672a\u89c1\u53ef\u4fe1\u2014\u2014\u201d\u8bf4\u5230\u8fd9\u91cc\uff0c\u6697\u53f9\u4e00\u58f0\uff0c\u5ffd\u89c9 \u81ea\u5df1\u5bf9\u8fd9\u4e9b\u597d\u72e1\u4e4b\u5f92\uff0c\u5b9e\u5728\u662f\u675f\u624b\u65e0\u7b56\uff0c\u5374\u89c1\u6e29\u747e\u5fae\u5fae\u51b7\u7b11\uff0c\u63a5\u53e3\u6c89\u58f0\u8bf4\u9053\uff1a\u201c\u6bd4\u4ed6\u518d\u597d\u72e1 \u5341\u500d\u7684\u51f6\u5f92\uff0c\u6211\u4e5f\u770b\u5f97\u591a\u7684\uff0c\u6211\u82e5\u4e0d\u80fd\u53eb\u4ed6\u8bf4\u51fa\u5b9e\u8bdd\u6765\u4e00\u54fc\u54fc\u3002\u201d \u3000\u3000\u5979\u51b7\u54fc\u4e24\u58f0\uff0c\u53c8\u9053\uff1a\u201c\u957f\u537f\uff0c\u4f60\u53ef\u77e5\u9053\u5bf9\u4ed8\u8fd9\u79cd\u4eba\uff0c\u8be5\u7528\u4ec0\u4e48\u529e\u6cd5\uff1f\u201d \u3000\u3000\u5353\u957f\u537f\u6123\u4e86\u4e00\u6123\uff0c\u7f13\u7f13\u6447\u4e86\u6447\u5934\uff0c\u5374\u89c1\u6e29\u747e\u79cb\u6ce2\u4e00\u8f6c\uff0c\u4f3c\u4e4e\u5411\u81ea\u5df1\u4f7f\u4e86\u4e2a\u773c\u8272\uff0c\u51b7\u7b11\u53c8 \u9053\uff1a\u201c\u6211\u518d\u95ee\u4ed6\u4e00\u53e5\uff0c\u4ed6\u82e5\u4e0d\u597d\u751f\u56de\u7b54\u4e8e\u6211\uff0c\u6211\u5c31\u524a\u4e0b\u4ed6\u4e00\u652f\u624b\u6307\uff0c\u7136\u540e\u518d\u95ee\u4ed6\u4e00\u53e5\uff0c\u4ed6\u8457 \u8fd8\u4e0d\u56de\u7b54\uff0c\u6211\u5c31\u518d\u524a\u4e0b\u4ed6\u4e24\u53ea\u624b\u6307\uff0c\u4ed6\u5c31\u7b97\u771f\u7684\u662f\u94c1\u6253\u7684\u6c49\u5b50\uff0c\u7b49\u5230\u6211\u8981\u524a\u4ed6\u7684\u8033\u6735\uff0c\u5207\u4ed6 \u7684\u9f3b\u5b50\uff0c\u62d4\u4ed6\u7684\u820c\u5934\uff0c\u6316\u4ed6\u7684\u773c\u73e0\u7684\u65f6\u5019\uff0c\u6211\u5c31\u4e0d\u76f8\u4fe1\u4ed6\u8fd8\u4e0d\u8bf4\u51fa\u6765\u3002\u201d \u3000\u3000\u5979\u7f13\u7f13\u8bf4\u6765\uff0c\u8bed\u58f0\u548c\u7f13\uff0c\u4f46\u5374\u542c\u5f97\u5353\u957f\u537f\u5fc3\u5934\u4e00\u9707\uff0c\u8f6c\u76ee\u671b\u53bb\uff0c\u53ea\u89c1\u90a3\u4e54\u8fc1\u5374\u4ecd\u7791\u76ee\u800c \u5750\uff0c\u9762\u989d\u4e0a\u5df2\u5fcd\u4e0d\u4f4f\u6d41\u4e0b\u51b7\u6c57\u3002 \u3000\u3000\u6e29\u747e\u51b7\u7b11\u4e00\u58f0\uff0c\u53c8\u9053\uff1a\u201c\u957f\u537f\uff0c\u4f60\u8981\u662f\u4e0d\u4fe1\uff0c\u6211\u5c31\u8bd5\u7ed9\u4f60\u770b\u770b\u3002\u201d \u3000\u3000\u67f3\u8170\u4e00\u62e7\uff0c\u7f13\u6b65\u8d70\u5230\u4e54\u8fc1\u9762\u524d\uff0c\u8fd8\u672a\u8bf4\u8bdd\uff0c\u5374\u89c1\u4e54\u8fc1\u5df2\u81ea\u957f\u53f9\u9053\uff1a\u201c\u4f60\u8981\u95ee\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6e29\u747e\u8f7b\u8f7b\u4e00\u7b11\uff0c\u79cb\u6ce2\u8f7b\u8198\u5353\u957f\u537f\u4e00\u773c\uff0c\u9053\uff1a\u201c\u4f60\u770b\uff0c\u4ed6\u4e0d\u662f\u4e5f\u806a\u660e\u5f97\u5f88\u4e48\uff1f\u201d \u3000\u3000\u5353\u957f\u537f\u6697\u53f9\u4e00\u58f0\uff0c\u5fd6\u9053\uff1a\u201c\u6076\u4eba\u81ea\u6709\u6076\u4eba\u78e8\uff0c\u770b\u6765\u6b64\u8bdd\u771f\u7684\u4e00\u70b9\u4e5f\u4e0d\u9519\u3002\u201d \u3000\u3000\u4ed6\u5374\u4e0d\u77e5\u9053\u6e29\u747e\u867d\u662f\u8f7b\u63cf\u6de1\u5199\u7684\u51e0\u53e5\u8bdd\uff0c\u5374\u5df2\u8db3\u591f\u53eb\u4e54\u8fc1\u542c\u4e86\u80c6\u5bd2\uff0c\u8fd9\u662f\u56e0\u4e3a\u4e54\u8fc1\u6df1\u77e5 \u8fd9\u4f4d\u5973\u9b54\u5934\u7684\u5f1f\u5b50\u5f53\u771f\u662f\u8bf4\u5f97\u51fa\uff0c\u505a\u5f97\u5230\u7684\u89d2\u8272\u3002 \u3000\u3000\u53ea\u542c\u6e29\u747e\u4e00\u7b11\u9053\uff1a\u201c\u6211\u5148\u95ee\u4f60\uff0c\u4f60\u8fd9\u4e9b\u65e0\u5f71\u795e\u9488\uff0c\u5230\u5e95\u662f\u4ece\u54ea\u91cc\u6765\u7684\uff1f\u201d \u3000\u3000\u4e54\u8fc1\u53cc\u76ee\u4e00\u5f20\uff0c\u76ee\u5149\u4e00\u8f6c\uff0c\u9053\uff1a\u201c\u6211\u82e5\u5c06\u4e00\u5207\u4e8b\u90fd\u636e\u5b9e\u544a\u8bc9\u4f60\uff0c\u4f60\u8fd8\u8981\u5bf9\u6211\u600e\u6837\uff1f\u201d \u3000\u3000\u6e29\u747e\u67f3\u7709\u4e00\u8f69\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u82e5\u8001\u8001\u5b9e\u5b9e\u5730\u53e3\u7b54\u6211\u7684\u8bdd\uff0c\u6211\u5c31\u5e9f\u53bb\u4f60\u4e00\u8eab\u6b66\u529f\uff0c\u8ba9\u4f60\u6eda \u56de\u5bb6\u53bb\uff0c\u518d\u4e5f\u4e0d\u80fd\u5bb3\u4eba\u3002\u201d \u3000\u3000\u4e54\u8fc1\u9762\u8272\u4e00\u53d8\uff0c\u989d\u4e0a\u6c57\u4e0b\u5982\u96e8\uff0c\u5446\u5446\u5730\u6123\u4e86\u534a\u664c\uff0c\u9893\u7136\u5782\u4e0b\u5934\u53bb\uff0c\u5353\u957f\u537f\u53cc\u7709\u4e00\u76b1\uff0c\u5fd6 \u9053\uff1a\u201c\u5e9f\u53bb\u6b66\u529f\uff0c\u751f\u4e0d\u5982\u6b7b\uff0c\u8fd9\u4e00\u4e0b\u6211\u770b\u4ed6\u5927\u7ea6\u5b81\u53ef\u6b7b\u53bb\uff0c\u4e5f\u4e0d\u613f\u8bf4\u51fa\u4e86\u3002\u201d \u3000\u3000\u54ea\u77e5\u4ed6\u5fc3\u5ff5\u5c1a\u672a\u8f6c\u5b8c\uff0c\u4e54\u8fc1\u5374\u5df2\u60e8\u58f0\u9053\uff1a\u201c\u6211\u8bf4\u51fa\u4e4b\u540e\uff0c\u59d1\u5a18\u7eb5\u7136\u9976\u6211\u4e00\u547d\uff0c\u4f46\u53ea\u6015\u2014 \u2014\u201d\u4ed6\u76ee\u5149\u4e00\u8f6c\uff0c\u5411\u90a3\u4e9b\u9ed1\u8863\u6c49\u5b50\u659c\u779f\u4e00\u773c\uff1a\u201c\u6211\u8fd8\u6ca1\u6709\u56de\u5bb6\uff0c\u5c31\u5df2\u88ab\u4eba\u4e71\u5200\u5206\u5c38\u4e86\u3002\u201d \u3000\u3000\u6e29\u747e\u67f3\u7709\u626c\u5904\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u4f60\u8981\u600e\u5730\uff1f\u201d \u3000\u3000\u4e54\u8fc1\u76ee\u5149\u4e00\u8f6c\uff0c\u5782\u9996\u9053\uff1a\u201c\u6211\u53ea\u671b\u59d1\u5a18\u80fd\u5c06\u6211\u8f7b\u529f\u7559\u4e0b\u51e0\u5206\uff0c\u8ba9\u6211\u80fd\u6709\u6d3b\u547d\u4e4b\u8def\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u957f\u53f9\u4e00\u58f0\uff0c\u5fd6\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u4e16\u4e0a\u7adf\u6709\u4eba\u5c06\u751f\u547d\u770b\u5f97\u5982\u6b64\u73cd\u8d35\uff0c\u751a\u81f3\u6bd4\u81ea\u5df1\u7684\u540d \u8a89\u3001\u4fe1\u7528\u3001\u81ea\u7531\u7684\u603b\u548c\u8fd8\u8981\u770b\u5f97\u91cd\u4e9b\uff0c\u5509\u2014\u2014\u81ea\u53e4\u8270\u96be\u552f\u4e00\u6b7b\uff0c\u96be\u602a\u90a3\u4e9b\u629b\u5934\u9885\u3001\u6d12\u70ed\u8840\uff0c \u5c06\u81ea\u5df1\u751f\u6b7b\u751f\u547d\u7f6e\u4e4b\u5ea6\u5916\u7684\u82f1\u96c4\u8c6a\u6770\uff0c\u80fd\u591f\u7559\u4f20\u53f2\u518c\uff0c\u540d\u5782\u5343\u53e4\u3002\u201d \u3000\u3000\u4e00\u5ff5\u81f3\u6b64\uff0c\u53e3\u8f6c\u5934\u53bb\uff0c\u4e0d\u5fcd\u518d\u89c1\u6b64\u4eba\u7684\u4e11\u6001\u3002 \u3000\u3000\u53ea\u542c\u90a3\u6e29\u747e\u8f7b\u53f1\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4ee5\u4f60\u6240\u505a\u6240\u4e3a\uff0c\u8ba9\u4f60\u4e00\u6b7b\uff0c\u65e9\u5df2\u662f\u4fbf\u5b9c\u4e86\u4f60\uff0c\u4f60\u5982\u6b64\u8ba8\u4ef7 \u8fd8\u4ef7\uff0c\u5f53\u771f\u662f\u2014\u2014\u201d\u5979\u8bdd\u58f0\u672a\u4e86\uff0c\u90a3\u8fb9\u9ed1\u8863\u5927\u6c49\u7fa4\u4e2d\u5df2\u5927\u6b65\u8d70\u51fa\u4e00\u4e2a\u4eba\u6765\uff0c\u6e29\u747e\u79cb\u6ce2\u4e00\u76b1\uff0c \u8f7b\u53f1\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u96be\u9053\u4f60\u6709\u4ec0\u4e48\u8bdd\u8bf4\u4e48\uff1f\u201d \u3000\u3000\u90a3\u9ed1\u8863\u6c49\u5b50\u62a2\u524d\u4e09\u6b65\uff0c\u8eac\u8eab\u4e00\u63d6\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u5c0f\u7684\u5510\u4e49\uff0c\u4e43\u662f\u8700\u4e2d\u5510\u95e8\u5f53\u4eca\u5e84\u4e3b\u7684\u4e09\u4f20 \u5f1f\u5b50\u2014\u2014\u201d\u6e29\u747e\u53e3\u4e2d\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u5fc3\u4e2d\u5374\u604d\u7136\u800c\u609f\uff1a\u201c\u96be\u602a\u8fd9\u4e9b\u4eba\u53d1\u653e\u6697\u5668\u7684\u624b\u6cd5\uff0c\u90fd\u975e \u5eb8\u624b\uff0c\u539f\u6765\u4ed6\u4eec\u7ade\u90fd\u662f\u540d\u91cd\u6b66\u6797\u5df2\u4e45\uff0c\u5929\u4e0b\u6697\u5668\u540d\u95e8\u7684\u5510\u6c0f\u95e8\u4eba\u3002\u201d \u3000\u3000\u5374\u542c\u8fd9\u9ed1\u8863\u6c49\u5b50\u5510\u4e49\u8eac\u8eab\u53c8\u9053\uff1a\u201c\u59d1\u5a18\u8981\u95ee\u4ec0\u4e48\u8bdd\uff0c\u5c0f\u7684\u90fd\u53ef\u4ee5\u636e\u5b9e\u8bf4\u51fa\uff0c\u4f46\u671b\u59d1\u5a18\u5c06 \u8fd9\u65e0\u4fe1\u65e0\u4e49\u7684\u4e54\u8fc1\uff0c\u5e26\u56de\u8700\u4e2d\u2014\u2014\u201d\u5353\u957f\u537f\u7a81\u7136\u63a5\u53e3\u9053\uff1a\u201c\u4f60\u5148\u8bf4\u51fa\u4fbf\u662f\u3002\u201d \u3000\u3000\u4ed6\u5bf9\u4e54\u8fc1\u5fc3\u4e2d\u6076\u611f\u6781\u6df1\uff0c\u662f\u4ee5\u6b64\u523b\u65e0\u6b8a\u5df2\u7b54\u5e94\u4e86\u8fd9\u6c49\u5b50\u7684\u6761\u4ef6\u3002 \u3000\u3000\u53ea\u542c\u5510\u4e49\u8eac\u8eab\u9053\uff1a\u201c\u8fd9\u59d3\u4e54\u7684\u4e0e\u655d\u95e8\u672c\u65e0\u6df1\u4ea4\uff0c\u6570\u6708\u4e4b\u524d\uff0c\u4ed6\u5ffd\u7136\u6765\u5230\u8700\u4e2d\uff0c\u5e76\u4e14\u5e26\u6765 \u4e00\u4efd\u79d8\u56fe\uff0c\u8bf4\u662f\u5f97\u81ea\u7ea2\u8863\u5a18\u5a18\u4e4b\u5904\uff0c\u8fd9\u4efd\u79d8\u56fe\u4fbf\u662f\u65e0\u5f71\u795e\u9488\u7684\u5236\u9020\u65b9\u6cd5\uff0c\u5f53\u65f6\u655d\u638c\u95e8\u4eba\u4e0d\u5728 \u8700\u4e2d\uff0c\u662f\u7531\u5c0f\u4eba\u7684\u4e09\u5e08\u7956\u63a5\u5f85\u4e8e\u4ed6\u2014\u2014\u201d\u6e29\u747e\u63a5\u53e3\u9053\uff1a\u201c\u53ef\u5c31\u662f\u90a3\u4eba\u79f0\u4e09\u624b\u8ffd\u9b42\u7684\u5510\u591a\uff1f\u201d \u3000\u3000\u5510\u4e49\u9894\u9996\u9053\uff1a\u201c\u655d\u95e8\u4e09\u5e08\u7956\u53d4\u5728\u6c5f\u6e56\u4e2d\u672c\u5c11\u8d70\u52a8\uff0c\u662f\u4ee5\u4fbf\u88ab\u8fd9\u53ae\u82b1\u8a00\u5de7\u8bed\u6240\u60d1\uff0c\u5c06\u8fd9\u4efd \u79d8\u56fe\uff0c\u4ea4\u7ed9\u655d\u95e8\u5c5e\u4e0b\u7684\u6697\u5668\u5236\u9020\u4e4b\u4e03\u7075\u5382\uff0c\u9650\u4e8e\u4e94\u5341\u5929\uff0c\u5236\u51fa\u4e09\u5343\u679a\u65e0\u5f71\u795e\u9488\u6765\uff0c\u655d\u95e8\u81ea\u4e09 \u4ee3\u5f1f\u5b50\u4ee5\u4e0b\uff0c\u65e0\u4e0d\u65e5\u591c\u52a0\u5de5\uff0c\u56db\u5341\u4e94\u5929\u4e4b\u4e2d\uff0c\u4fbf\u5df2\u4ea4\u5377\u2026\u2026\u201d \u3000\u3000\u5353\u957f\u537f\u5fcd\u4e0d\u4f4f\u9053\uff1a\u201c\u96be\u9053\u4f60\u4eec\u6240\u7528\u7684\u6697\u5668\u90fd\u662f\u81ea\u5df1\u95e8\u5f92\u6240\u5236\u4e48\uff1f\u201d \u3000\u3000\u5510\u4e49\u6115\u4e86\u4e00\u6115\uff0c\u5fd6\u9053\uff1a\u201c\u6b64\u4eba\u6b66\u529f\u4e4b\u9ad8\uff0c\u770b\u6765\u5c24\u5728\u5e08\u7237\u4e4b\u4e0a\uff0c\u600e\u5730\u6c5f\u6e56\u9605\u5386\u5374\u5982\u6b64\u4e4b \u6d45\uff0c\u8700\u4e2d\u5510\u95e8\u7684\u6bd2\u836f\u6697\u5668\u540d\u626c\u5929\u4e0b\uff0c\u4e16\u4e16\u4ee3\u4ee3\uff0c\u4ff1\u662f\u5510\u95e8\u4e03\u7075\u5382\u6240\u521b\uff0c\u6b66\u6797\u4e2d\u5927\u534a\u77e5\u9053\uff0c\u600e \u5730\u4ed6\u5374\u4e0d\u77e5\u5462\uff1f\u201d \u3000\u3000\u5fc3\u4e2d\u867d\u5982\u6b64\u60f3\uff0c\u53e3\u4e2d\u5374\u4ecd\u606d\u8eab\u9053\uff1a\u201c\u6b63\u662f\uff0c\u6570\u767e\u5e74\u6765\uff0c\u636e\u5f1f\u5b50\u6240\u77e5\uff0c\u655d\u95e8\u4e03\u7075\u5382\u5236\u4f5c\u522b \u95e8\u522b\u6d3e\u7684\u6697\u5668\uff0c\u6b64\u6b21\u5c1a\u5c5e\u9996\u521b\u3002\u201d \u3000\u3000\u4ed6\u8bed\u58f0\u4e00\u987f\uff0c\u53c8\u9053\uff1a\u201c\u65e0\u5f71\u795e\u9488\u5982\u671f\u4ea4\u5377\u4e4b\u540e\uff0c\u655d\u6d3e\u638c\u95e8\u4eba\u4e5f\u81ea\u5929\u5c71\u8d76\u4e86\u56de\u6765\uff0c\u8fd9\u59d3\u4e54 \u7684\u5c11\u4e0d\u5f97\u53c8\u5728\u655d\u6d3e\u638c\u95e8\u4eba\u9762\u524d\u82b1\u8a00\u5de7\u8bed\u4e00\u756a\uff0c\u662f\u4ee5\u2014\u2014\u201d\u5353\u957f\u537f\u5fcd\u4e0d\u4f4f\u53c8\u81ea\u63d2\u53e3\u9053\uff1a\u201c\u8d35\u6d3e \u7684\u638c\u95e8\u4eba\u53c8\u662f\u8c01\u5462\uff1f\u201d \u3000\u3000\u5510\u4e49\u53c8\u81ea\u4e00\u6123\uff0c\u9762\u4e0a\u4f3c\u4e4e\u5fae\u5fae\u73b0\u51fa\u4e0d\u60a6\u4e4b\u8272\uff0c\u8981\u77e5\u9053\uff0c\u8700\u4e2d\u5510\u95e8\uff0c\u540d\u626c\u5929\u4e0b\uff0c\u5510\u95e8\u4e09 \u6770\uff0c\u66f4\u662f\u5929\u4e0b\u7686\u95fb\uff0c\u5510\u4e49\u89c1\u5353\u957f\u537f\u7adf\u4e0d\u77e5\u9053\uff0c\u62ac\u76ee\u671b\u4e86\u4e24\u671b\uff0c\u9762\u4e0a\u4ecd\u7136\u4e0d\u6562\u73b0\u51fa\u4e0d\u6ee1\uff0c\u8eac\u8eab \u9053\uff1a\u201c\u655d\u6d3e\u638c\u95e8\u4eba\u6c5f\u6e56\u4eba\u79f0\u2014\u2014\u201d\u6e29\u747e\u63a5\u53e3\u9053\uff1a\u201c\u4e09\u73af\u5957\u6708\u538b\u5929\u4e0b\uff0c\u6ee1\u5929\u82b1\u5bf8\u9707\u4e7e\u5764\uff0c\u6458\u661f \u5c04\u6708\u65e0\u6545\u795e\u5510\u98de\uff01\u201d \u3000\u3000\u5510\u4e49\u5fae\u5fae\u4e00\u7b11\uff0c\u5411\u6e29\u747e\u8eac\u8eab\u4e00\u793c\uff0c\u63a5\u9053\uff1a\u201c\u655d\u6d3e\u638c\u95e8\u4eba\u542c\u4e86\u8fd9\u59d3\u4e54\u7684\u8bdd\uff0c\u5728\u5bc6\u5ba4\u4e4b\u4e2d\u5750 \u5173\u4e09\u5929\uff0c\u7136\u540e\u4f20\u4ee4\u655d\u6d3e\u4e09\u4ee3\u5f1f\u5b50\u4e03\u5341\u4eba\uff0c\u4e0e\u5f1f\u5b50\u4eec\u548c\u5e08\u4f2f\u5e08\u53d4\u4eec\u4e03\u4eba\uff0c\u8ddf\u8fd9\u59d3\u4e54\u7684\u4e00\u8d77\u5230\u8fd9 \u5929\u76ee\u5c71\u7c73\uff0c\u4e3a\u7684\u53ea\u662f\u90a3\u4e09\u5e45\u753b\u5377\u4e2d\u7684\u540d\u5251\u7075\u836f\u800c\u5df2\u3002\u201d \u3000\u3000\u6e29\u747e\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8700\u4e2d\u5510\u95e8\uff0c\u5bcc\u53ef\u654c\u56fd\uff0c\u81ea\u7136\u4e0d\u4f1a\u628a\u91d1\u94f6\u73e0\u5b9d\u770b\u5728\u773c\u91cc\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u89c1\u6e29\u747e\u8a00\u8bed\u4e4b\u4e2d\uff0c\u5bf9\u8fd9\u8700\u4e2d\u5510\u95e8\u4f3c\u662f\u9887\u4e3a\u63a8\u5d07\uff0c\u5fc3\u4e2d\u4e0d\u89c9\u6709\u4e9b\u5947\u602a\u3002 \u3000\u3000\u4ed6\u5374\u4e0d\u77e5\u9053\u8700\u4e2d\u5510\u95e8\u6570\u767e\u5e74\u6765\uff0c\u5728\u6b66\u6797\u4e2d\u7684\u5730\u4f4d\u5df2\u662f\u6839\u6df1\u8482\u56fa\uff0c\u6bd4\u4e4b\u5c11\u6797\u3001\u6b66\u5f53\u7b49\u540d\u95e8 \u5927\u6d3e\uff0c\u5e76\u4e0d\u591a\u8ba9\u3002 \u3000\u3000\u800c\u4e14\u8700\u4e2d\u5510\u95e8\u95e8\u4e2d\u867d\u4e5f\u6709\u4e9b\u4e0d\u8d1e\u5f1f\u5b50\uff0c\u4e3a\u5bb3\u6c5f\u6e56\uff0c\u4f46\u5927\u4f53\u8bf4\u6765\uff0c\u5374\u8fd8\u4e0d\u6127\u4e3a\u6b66\u6797\u6b63\u5b97\uff0c \u662f\u4ee5\u6b66\u6797\u4e2d\u4eba\u5bf9\u5510\u95e8\u4e2d\u4eba\uff0c\u591a\u6709\u4e00\u4e9b\u656c\u610f\u3002 \u3000\u3000\u5374\u542c\u6e29\u747e\u8bed\u58f0\u4e00\u987f\uff0c\u7a81\u53c8\u51b7\u7b11\u9053\uff1a\u201c\u53ea\u662f\u6458\u661f\u5c04\u6708\u65e0\u654c\u624b\u5510\u5927\u4fa0\uff0c\u5728\u6c5f\u6e56\u4e2d\u4eab\u6709\u4fa0\u540d\uff0c \u800c\u4e14\u7d20\u79f0\u94c1\u9762\uff0c\u6b64\u6b21\u600e\u4e48\u542c\u8d77\u59d3\u4e54\u7684\u8bdd\u6765\uff0c\u8fd9\u5012\u6709\u4e9b\u5947\u602a\u4e86\u3002\u201d \u3000\u3000\u5510\u4e49\u9762\u988a\u5fae\u7ea2\uff0c\u5782\u9996\u8bf4\u9053\uff1a\u201c\u655d\u6d3e\u638c\u95e8\u4e2d\u4e8b\uff0c\u5c0f\u4eba\u4eec\u672c\u4e0d\u592a\u5341\u5206\u6e05\u695a\uff0c\u4f46\u5bb6\u5e08\u7956\u6b64\u6b21\u636e \u8bf4\u662f\u53e6\u6709\u6df1\u610f\u2014\u2014\u5bb6\u5e08\u7956\u6b64\u6b21\u5929\u5c71\u4e4b\u884c\uff0c\u5927\u7ea6\u662f\u6811\u4e0b\u5f3a\u654c\uff0c\u662f\u4ee5\u4fbf\u5e0c\u671b\u80fd\u5f97\u5230\u8fd9\u4e9b\u540d\u5251\u7075\u836f \u4e00\u4e00\u4e00\u201d\u4ed6\u8bed\u58f0\u7a81\u987f\u9053\uff1a\u201c\u5c0f\u4eba\u4eec\u6b64\u6b21\u5984\u6f0f\u672c\u95e8\u79d8\u5bc6\uff0c\u672c\u5df2\u62b1\u5fc5\u6b7b\u4e4b\u5fc3\uff0c\u53ea\u671b\u59d1\u5a18\u77e5\u9053\u4e86\uff0c \u4e0d\u8981\u518d\u4f20\u8a00\u51fa\u53bb\uff0c\u5c0f\u4eba\u4fbf\u5df2\u611f\u6069\u4e0d\u5c3d\u4e86\u3002\u201d \u3000\u3000\u6e29\u747e\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u5982\u6b64\u505a\u6cd5\uff0c\u4e0d\u8fc7\u5c31\u662f\u60f3\u5c06\u8fd9\u7f6a\u9b41\u7978\u9996\u4e54\u8fc1\uff0c\u5e26\u56de\u8700\u4e2d\uff0c\u8fd9\u5176\u4e2d \u5374\u53c8\u6709\u4ec0\u4e48\u539f\u56e0\u5462\uff1f\u201d \u3000\u3000\u5510\u4e49\u94a2\u7259\u4e00\u54ac\uff0c\u6068\u58f0\u9053\uff1a\u201c\u8fd9\u59d3\u4e54\u7684\u4e00\u5230\u6b64\u95f4\uff0c\u5c45\u7136\u53c8\u4ee5\u82b1\u8a00\u5de7\u8bed\u5c06\u5f1f\u5b50\u4eec\u8fd9\u4e03\u4f4d\u5e08\u53d4 \u8bf1\u60d1\uff0c\u5728\u4e34\u5b89\u57ce\u4e2d\uff0c\u5148\u8bf7\u655d\u95e8\u4e24\u4f4d\u5973\u5e08\u53d4\uff0c\u5206\u5934\u5411\u7ea2\u4e2d\u5feb\u5200\u4e24\u6d3e\u53d1\u4e0b\u67ec\u5e16\uff0c\u4f7f\u5f97\u4ed6\u4eec\u5fc3\u4e2d\u60f6 \u7136\uff0c\u731c\u7591\u4e0d\u5b89\uff0c\u53c8\u4e58\u9ed1\u591c\u4e4b\u4e2d\uff0c\u547d\u5f1f\u5b50\u4eec\u5c06\u7ea2\u4e2d\u4f1a\u4f17\u4e00\u7f51\u6253\u5c3d\uff0c\u7136\u540e\u53c8\u547d\u5f1f\u5b50\u4eec\u6f5c\u4f0f\u4e8e\u8def\u8fb9 \u5e97\u6355\u4e4b\u4e2d\uff0c\u65bd\u7528\u65e0\u5f71\u795e\u9488\uff0c\u5077\u88ad\u5feb\u5200\u4f1a\u4f17----\u201d\u5353\u957f\u537f\u201c\u5440\u201d\u7684\u4e00\u58f0\uff0c\u8131\u53e3\u9053\uff1a\u201c\u539f\u6765\u662f\u4ed6 \u5e72\u7684\u4e8b\uff01\u201d \u3000\u3000\u76ee\u5149\u659c\u779f\u6e29\u747e\u4e00\u773c\uff0c\u6e29\u747e\u53ea\u5fae\u5fae\u4e00\u7b11\uff0c\u5ffd\u53c8\u53f9\u9053\uff1a\u201c\u539f\u6765\u6b64\u4e8b\u5176\u4e2d\u7adf\u6709\u8fd9\u4e48\u591a\u7684\u66f2\u6298\uff0c \u5148\u524d\u6211\u8fd8\u4ee5\u4e3a\u2026\u2026\u201d \u3000\u3000\u7a81\u7136\u5927\u559d\u4e00\u58f0\uff1a\u201c\u54ea\u91cc\u53bb\uff01\u201d \u3000\u3000\u53ea\u89c1\u4e54\u8fc1\u8eab\u5f62\u5728\u5730\u4e0a\u8fde\u6eda\u6570\u6eda\uff0c\u4e00\u8dc3\u800c\u8d77\uff0c\u4ea1\u547d\u5954\u53bb\u3002 \u3000\u3000\u6e29\u747e\u5927\u559d\u4e00\u58f0\uff0c\u8eab\u5f62\u5df2\u63a0\u51fa\u4e09\u4e08\uff0c\u7ea4\u8db3\u5fae\u70b9\uff0c\u500f\u7136\u4e00\u4e2a\u8d77\u843d\uff0c\u7ea4\u638c\u626c\u5904\uff0c\u4e09\u70b9\u4e4c\u56e2\u8131\u624b \u800c\u51fa\uff0c\u53ea\u542c\u4e54\u8fc1\u60e8\u53eb\u4e00\u58f0\uff0c\u7830\u7136\u843d\u5728\u5730\u4e0a\uff0c\u8eab\u5f62\u53c8\u7ed5\u4e86\u51e0\u5904\uff0c\u4fbf\u5df2\u7fc1\u7136\u4e0d\u52a8\u3002 \u3000\u3000\u5353\u957f\u537f\u968f\u540e\u63a0\u6765\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u8fd9\u53ae\u662f\u4e0d\u662f\u6b7b\u4e86\uff1f\u201d \u3000\u3000\u6e29\u747e\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u8ba9\u4ed6\u8fd9\u6837\u6b7b\u6389\u4e86\uff0c\u5c82\u975e\u592a\u4fbf\u5b9c\u4e86\u4ed6\u3002\u201d \u3000\u3000\u5c06\u4e54\u8fc1\u53c8\u81ea\u63d0\u4e86\u56de\u6765\uff0c\u5f80\u5510\u4e49\u9762\u4e0a\u4e00\u629b\uff0c\u5510\u4e49\u4fef\u8eab\u671b\u5904\uff0c\u53ea\u89c1\u8fd9\u597d\u72e1\u51f6\u733e\u7684\u6c49\u5b50\u6b64\u523b\u52a8 \u4e5f\u4e0d\u52a8\u5730\u4f0f\u5728\u5730\u4e0a\uff0c\u867d\u4f3c\u5df2\u6b7b\u53bb\uff0c\u4f46\u4ed4\u7ec6\u4e00\u770b\uff0c\u4ed6\u80cc\u540e\u9879\u4e0a\u5927\u690e\u4e0b\u6570\u7b2c\u5341\u56db\u8282\u4e24\u65c1\u5404\u5f00\u4e09\u5bf8 \u5904\u7684\u5de6\u53f3\u5fd7\u5802\u5927\u7a74\u5916\uff0c\u5c1a\u9732\u534a\u679a\u65e0\u5f71\u795e\u9488\u5e76\u672a\u6df1\u5165\uff0c\u663e\u89c1\u53ea\u662f\u7a74\u9053\u88ab\u70b9\uff0c\u5e76\u672a\u81f4\u547d\u3002 \u3000\u3000\u8fd9\u79cd\u624b\u6cd5\u8ba4\u7a74\u4e4b\u51c6\u5c1a\u5728\u5176\u6b21\uff0c\u52b2\u529b\u62ff\u634f\u5f97\u6070\u5230\u597d\u5904\uff0c\u5374\u5f53\u771f\u662f\u9a87\u4eba\u542c\u95fb\uff0c\u5510\u4e49\u76ee\u5149\u671b \u5904\uff0c\u4e0d\u7981\u5012\u62bd\u4e00\u53e3\u51b7\u6c14\u3002 \u3000\u3000\u4ed6\u672c\u662f\u6697\u5668\u540d\u95e8\u4e4b\u5f92\uff0c\u4f46\u6b64\u523b\u89c1\u4e86\u8fd9\u79cd\u624b\u6cd5\uff0c\u5fc3\u4e2d\u4ecd\u4e3a\u4e4b\u9a87\u7136\uff0c\u5446\u5446\u5730\u6123\u4e86\u534a\u664c\uff0c\u5450\u5450 \u9053\uff1a\u201c\u5c0f\u4eba\u4eec\u5728\u6697\u4e2d\u5077\u88ad\u5feb\u5200\u4f1a\u4f17\u4e4b\u9645\uff0c\u6240\u53d1\u6697\u5668\uff0c\u5927\u534a\u88ab\u4eba\u51fb\u843d\uff0c\u662f\u4ee5\u7094\u5200\u4f1a\u4f17\uff0c\u624d\u80fd\u9003 \u8131\u5927\u534a\u751f\u547d\uff0c\u5176\u65f6\u5c0f\u4eba\u4eec\u5c31\u5728\u6697\u4e2d\u9a87\u5f02\uff0c\u4e0d\u77e5\u662f\u8c01\u7684\u6697\u5668\u624b\u6cd5\u7adf\u662f\u90a3\u822c\u60ca\u4eba\uff0c\u6b64\u523b\u60f3\u6765\uff0c\u60f3 \u5fc5\u5c31\u662f\u59d1\u5a18\u3002\u201d \u3000\u3000\u6e29\u747e\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u90a3\u65f6\u6211\u4e5f\u5728\u5947\u602a\uff0c\u4f0f\u5728\u6697\u4e2d\u65bd\u653e\u7684\u6697\u5668\uff0c\u600e\u5730\u90a3\u822c\u9738\u9053\uff0c\u6211\u5148\u8fd8 \u4ee5\u4e3a\u53ea\u662f\u94c1\u84ba\u85dc\u3001\u6885\u82b1\u9488\u4e00\u7c7b\u7684\u6697\u5668\uff0c\u53c8\u4ee5\u4e3a\u662f\u90a3\u4e07\u5999\u771f\u541b\u5c39\u51e1\uff0c\u6216\u662f\u82b1\u90ce\u6bd5\u4e94\u7b49\u4eba\uff0c\u8eb2\u5728 \u6697\u4e2d\u6363\u4e71\uff0c\u672c\u60f3\u67e5\u4e2a\u6e05\u695a\u2014\u2014\u201d\u5979\u5fae\u7b11\u4e00\u4e0b\uff0c\u5411\u5353\u957f\u537f\u8f7b\u779f\u4e00\u773c\uff1a\u201c\u4f46\u540e\u6765\u88ab\u4f60\u4e00\u8ffd\uff0c\u518d\u67e5 \u4e5f\u67e5\u4e0d\u51fa\u4e86\uff0c\u5374\u4e07\u4e07\u60f3\u4e0d\u5230\u6697\u4e2d\u5077\u88ad\u4e4b\u4eba\uff0c\u7adf\u662f\u5510\u95e8\u5f1f\u5b50\uff0c\u66f4\u60f3\u4e0d\u5230\u90a3\u4e9b\u6697\u5668\uff0c\u5c45\u7136\u662f\u65e0\u5f71 \u795e\u9488\u2026\u2026\u201d \u3000\u3000\u5353\u957f\u537f\u6b64\u523b\u5fc3\u4e2d\u5df2\u5c3d\u604d\u7136\uff0c\u5fd6\u9053\uff1a\u201c\u96be\u602a\u5979\u8bf4\u6697\u5668\u5979\u867d\u53d1\u8fc7\uff0c\u5374\u4ec5\u662f\u6551\u4eba\u800c\u5df2\uff0c\u5509\u2014\u2014 \u6211\u771f\u7684\u9669\u4e9b\u9519\u602a\u4e86\u5979\uff0c\u770b\u6765\u6c5f\u6e56\u8be1\u6a58\uff0c\u7684\u786e\u662f\u4ee4\u4eba\u96be\u4ee5\u731c\u6d4b\u3002\u201d \u3000\u3000\u5411\u6e29\u747e\u5fae\u5fae\u4e00\u7b11\uff0c\u8fd9\u4e00\u7b11\u4e4b\u4e2d\uff0c\u60ed\u6127\u3001\u62b1\u6b49\u4e4b\u610f\u517c\u800c\u6709\u4e4b\u3002 \u3000\u3000\u6e29\u747e\u5fcd\u4e0d\u4f4f\u5a07\u7b11\u4e00\u4e0b\uff0c\u5782\u4e0b\u5934\u53bb\uff0c\u5fc3\u4e2d\u5927\u662f\u5b89\u6170\u3002 \u3000\u3000\u5353\u957f\u537f\u7a81\u53c8\u6068\u58f0\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u8fd9\u59d3\u4e54\u7684\u5982\u6b64\u6b79\u6bd2\uff0c\u90a3\u7094\u5200\u3001\u7ea2\u5dfe\u4e24\u4f1a\u7684\u95e8\u4eba\uff0c\u4e0e\u4ed6\u7d20\u65e0 \u51a4\u4ec7\uff0c\u4ed6\u4f55\u6614\u4e0b\u6b64\u6bd2\u624b\uff01\u201d \u3000\u3000\u5510\u4e49\u6c89\u58f0\u8bf4\u9053\uff1a\u201c\u8fd9\u53ae\u5982\u6b64\u505a\u6cd5\uff0c\u4e00\u6765\uff0c\u662f\u60f3\u4ee5\u6b64\u6270\u4e71\u6b66\u6797\u4e2d\u4eba\u7684\u8033\u76ee\uff0c\u4f7f\u5f97\u5929\u4e0b\u5927 \u4e71\uff0c\u4ed6\u5374\u4e58\u4e71\u53d6\u5229\uff1b\u518d\u6765\u4ed6\u53c8\u60f3\u5ac1\u7978\u4e8e\u7ea2\u8863\u5a18\u5a18\uff0c\u8ba9\u6b66\u6797\u4e2d\u4eba\u4ee5\u4e3a\u8fd9\u4e9b\u4e8b\u90fd\u662f\u7ea2\u8863\u5a18\u5a18\u6240 \u505a\uff1a\u4e09\u6765\u4ed6\u4e0e\u5feb\u5200\u4e01\u4e03\uff0c\u4ee5\u53ca\u7ea2\u5dfe\u4e09\u6770\u90fd\u7ed3\u6709\u6881\u5b50\uff0c\u4ed6\u6b64\u4e3e\u81ea\u662f\u4e58\u673a\u590d\u4ec7\uff1b\u56db\u6765\u4ed6\u5982\u6b64\u4e00 \u505a\uff0c\u5374\u53c8\u4f7f\u5f97\u655d\u95e8\u65e0\u5f62\u4e2d\u7ed3\u4e0b\u8bb8\u591a\u4ec7\u5bb6\uff0c\u5982\u679c\u4ed6\u4e00\u8bf4\u51fa\u6765\uff0c\u52bf\u5fc5\u8981\u5f15\u8d77\u8f69\u7136\u5927\u6ce2\uff0c\u4ed6\u4fbf\u53ef\u4ee5 \u6b64\u6765\u631f\u80c1\u655d\u95e8\uff0c\u8bf4\u4e0d\u5b9a\u4ed6\u4ee5\u540e\u8fd8\u8981\u518d\u6311\u62e8\u4e0e\u5feb\u5200\u3001\u7ea2\u5dfe\u4e24\u4f1a\u6709\u4ea4\u60c5\u7684\u6b66\u6797\u8c6a\u58eb\u5230\u8700\u4e2d\u6765\u5411\u655d \u95e8\u5bfb\u4ec7\uff1b\u4e94\u6765\u4ed6\u81ea\u7136\u662f\u4ee5\u6b64\u6d88\u9664\u5f02\u5df1\uff0c\u589e\u690d\u81ea\u5df1\u52bf\u529b\uff1b\u516d\u6765\u95fb\u9053\u4ed6\u5728\u6c5f\u6e56\u4e2d\u8981\u53e6\u5916\u518d\u8d77\u95e8 \u6237\uff0c\u6c5f\u6e56\u4e2d\u51e0\u4e2a\u65b0\u8d77\u7684\u95e8\u6d3e\u88ab\u4ed6\u5b8c\u5168\u6d88\u706d\u4e4b\u540e\uff0c\u4ed6\u5982\u6709\u4ec0\u4e48\u4e3e\u52a8\uff0c\u81ea\u7136\u4e8b\u534a\u529f\u500d\u2014\u2014\u201d\u4ed6\u6ed4 \u6ed4\u4e0d\u7edd\uff0c\u4e00\u53e3\u6c14\u8bf4\u5230\u8fd9\u91cc\uff0c\u7f13\u58f0\u7a0d\u987f\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u603b\u4e4b\u6b64\u4eba\u4e4b\u5978\u72e1\uff0c\u5b9e\u5728\u662f\u7f6a\u65e0\u53ef\u6055\uff0c\u5c0f\u4eba \u867d\u65e9\u5df2\u5bf9\u8fd9\u53ae\u75db\u6068\u5165\u9aa8\uff0c\u4f46\u600e\u5948\u5c0f\u4eba\u7684\u5e08\u53d4\u5374\u5bf9\u4ed6\u5341\u5206\u4fe1\u4efb\uff0c\u662f\u4ee5\u5c0f\u4eba\uff0c\u4eba\u8f7b\u8a00\u5fae\uff0c\u81ea\u4e5f\u65e0 \u53ef\u5948\u4f55\uff0c\u6b64\u523b\u4ed6\u88ab\u4e24\u4f4d\u64d2\u4f4f\uff0c\u53c8\u60f3\u51fa\u5356\u655d\u95e8\uff0c\u4e0d\u4f46\u5c0f\u4eba\u542c\u5230\uff0c\u90a3\u8fb9\u8fd8\u6709\u6570\u5341\u4e2a\u8bc1\u4eba\uff01\u662f\u4ee5\u5c0f \u4eba\u624d\u4e0d\u987e\u81ea\u8eab\u5b89\u5371\uff0c\u5c06\u8fd9\u53ae\u8ba1\u8c0b\u63ed\u7a7f\uff0c\u64d2\u56de\u8700\u4e2d\uff0c\u4ea4\u5230\u638c\u95e8\u4eba\u9762\u524d\uff0c\u6b63\u4ee5\u5bb6\u6cd5\uff0c\u8ba9\u8fd9\u53ae\u4e5f\u77e5 \u9053\u53cd\u590d\u65e0\u4e49\u3001\u5978\u72e1\u51f6\u733e\u4e4b\u4eba\uff0c\u8be5\u6709\u4ec0\u4e48\u4e0b\u573a\u3002\u201d \u3000\u3000\u8bf4\u5230\u8fd9\u91cc\uff0c\u4ed6\u7a81\u7136\u4ef0\u5929\u957f\u53f9\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u53ea\u662f\u5c0f\u4eba\u6b64\u523b\u5374\u4e5f\u6cc4\u51fa\u672c\u95e8\u79d8\u5bc6\uff0c\u867d\u7136\u6b64\u4e3e\u662f \u4e3a\u4e86\u672c\u95e8\u7740\u60f3\uff0c\u4f46\u53ea\u6015\u2014\u2014\u5509\u3002\u201d \u3000\u3000\u53c8\u81ea\u53f9\u4e00\u58f0\uff0c\u500f\u7136\u987f\u4f4f\u8bed\u58f0\u3002 \u3000\u3000\u5353\u957f\u537f\u76b1\u7709\u9053\uff1a\u201c\u4f60\u90a3\u4e03\u4f4d\u5e08\u53d4\u5462\uff0c\u600e\u4e48\u672a\u89c1\u540c\u6765\uff1f\u201d \u3000\u3000\u5510\u4e49\u6068\u58f0\u9053\uff1a\u201c\u8fd9\u81ea\u7136\u53c8\u662f\u8fd9\u53ae\u6240\u5f04\u7684\u82b1\u6837\uff01\u4ed6\u5c06\u5c0f\u4eba\u4eec\u4e58\u9ed1\u591c\u4e4b\u4e2d\u7531\u4e00\u6761\u79d8\u9053\uff0c\u6084\u6084 \u5e26\u5230\u8fd9\u91cc\u6765\uff0c\u88c5\u5728\u6728\u68fa\u4e4b\u4e2d\uff0c\u5374\u8ba9\u5c0f\u4eba\u4eec\u7684\u4e03\u4f4d\u5e08\u53d4\uff0c\u7fcc\u65e5\u548c\u6b66\u6797\u8c6a\u58eb\u4e00\u8d77\u8d74\u7ea6\uff0c\u7b49\u5230\u7fcc\u65e5 \u665a\u95f4\uff0c\u90a3\u65f6\u8fd9\u2018\u5929\u76ee\u5927\u4f1a\u2019\u5fc5\u7136\u5df2\u544a\u7ed3\u675f\uff0c\u80dc\u8d1f\u5df2\u53ef\u5206\u51fa\uff0c\u518d\u7ecf\u8fd9\u6761\u5c71\u9053\u51fa\u53bb\u7684\uff0c\u5fc5\u5b9a\u662f\u7ecf \u8fc7\u4e00\u756a\u82e6\u5173\u4e4b\u540e\u5f97\u80dc\u7684\u9ad8\u624b\uff0c\u8fd9\u53ae\u4fbf\u53eb\u5c0f\u4eba\u4eec\u5373\u65f6\u7a81\u7136\u81ea\u68fa\u4e2d\u65bd\u653e\u6697\u5668\uff0c\u53c8\u8ba9\u5c0f\u4eba\u4eec\u7684\u4e03\u4f4d \u5e08\u53d4\u5728\u5916\u76f8\u5e94\u3002\u91cc\u5e94\u5916\u5408\uff0c\u4e00\u4e3e\u594f\u529f\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u5fc3\u5934\u4e00\u51c9\uff0c\u6697\u5fd6\uff1a\u201c\u9ed1\u665a\u4e4b\u4e2d\uff0c\u9aa4\u9047\u6b64\u53d8\uff0c\u7eb5\u7136\u8eab\u624b\u7edd\u9876\uff0c\u53ea\u6015\u4e5f\u96be\u9003\u51fa\u6bd2\u624b\uff0c \u5509\u2014\u2014\u6b64\u4eba\u600e\u5730\u5982\u6b64\u72e0\u6bd2\uff0c\u7adf\u60f3\u5c06\u5929\u4e0b\u82f1\u8c6a\u4e00\u7f51\u6253\u5c3d\uff0c\u53ea\u662f\u4ed6\u667a\u8005\u5343\u8651\uff0c\u7ec8\u6709\u4e00\u5931\uff0c\u5374\u60f3\u4e0d \u5230\u6211\u4f1a\u8bef\u6253\u8bef\u649e\u7684\u5c06\u6b64\u597d\u8c0b\u63ed\u7834\uff0c\u770b\u6765\u5929\u7f51\u867d\u758f\uff0c\u5374\u5f53\u771f\u662f\u758f\u800c\u4e0d\u6f5c\u5fc3\u54e9\u3002\u201d \u3000\u3000\u76ee\u5149\u4e00\u8f6c\uff0c\u8f6c\u5411\u6e29\u747e\uff0c\u4e24\u4eba\u5fc3\u610f\u76f8\u4eff\uff0c\u5f7c\u6b64\u5fc3\u4e2d\u4ff1\u90fd\u4e0d\u7981\u4e3a\u4e4b\u611f\u6168\u4e0d\u5df2\u3002 \u3000\u3000\u53ea\u89c1\u5510\u4e49\u8083\u7acb\u534a\u664c\uff0c\u606d\u58f0\u53c8\u9053\uff1a\u201c\u4eba\u4eec\u6240\u77e5\u4e0d\u8a00\uff0c\u6240\u8a00\u4e0d\u5c3d\uff0c\u4e24\u4f4d\u5982\u80af\u6055\u8fc7\u5c0f\u4eba\u4eec\u65b9\u624d \u4e4b\u8fc7\uff0c\u5c0f\u4eba\u7acb\u65f6\u4fbf\u8bf7\u544a\u9000\uff0c\u4e0d\u4f46\u4ece\u6b64\u8db3\u8ff9\u7edd\u4e0d\u5165\u5929\u76ee\u65b9\u5706\u767e\u91cc\u4e00\u6b65\uff0c\u4fbf\u662f\u5c0f\u4eba\u4eec\u7684\u5e08\u957f\uff0c\u4e5f \u5fc5\u5b9a\u6c38\u8fdc\u611f\u6fc0\u4e24\u4f4d\u7684\u5927\u5fb7\u3002\u201d \u3000\u3000\u4ed6\u8bed\u58f0\u5fae\u987f\uff0c\u7a81\u7136\u4e00\u633a\u80f8\u819b\uff0c\u53c8\u9053\uff1a\u201c\u82e5\u662f\u4e24\u4f4d\u4e0d\u613f\u6055\u5374\u5c0f\u4eba\u4eec\u4e4b\u7f6a\uff0c\u5c0f\u4eba\u4eec\u81ea\u77e5\u5b66\u827a \u4e0d\u7cbe\uff0c\u7edd\u4e0d\u662f\u4e24\u4f4d\u7684\u653e\u624b\uff0c\u4f46\u51ed\u4e24\u4f4d\u5904\u7f6e\uff0c\u5c0f\u4eba\u4eec\u7edd\u4e0d\u76b1\u4e00\u76b1\u7709\u5934\u3002\u201d \u3000\u3000\u8fd9\u5510\u4e49\u6b66\u529f\u867d\u4e0d\u9ad8\uff0c\u5374\u7cbe\u660e\u5e72\u7ec3\uff0c\u8a00\u8bed\u7075\u6377\uff0c\u800c\u4e14\u6c5f\u6e56\u5386\u7ec3\u751a\u4e30\uff0c\u6b64\u523b\u8bf4\u8d77\u8bdd\u6765\uff0c\u5f53\u771f \u662f\u4e0d\u5351\u4e0d\u4ea2\u3002 \u3000\u3000\u5353\u957f\u537f\u3001\u6e29\u747e\u76ee\u5149\u4e00\u8f6c\uff0c\u5bf9\u671b\u4e00\u773c\uff0c\u53e3\u4e2d\u4e0d\u8a00\uff0c\u5fc3\u4e2d\u5374\u5404\u81ea\u6697\u5730\u5bfb\u601d\uff1a\u201c\u662f\u653e\u5462\uff1f\u8fd8\u662f \u4e0d\u653e\uff1f\u201d \u3000\u3000\u5353\u957f\u537f\u6697\u53f9\u4e00\u58f0\uff0c\u5fd6\u9053\uff1a\u201c\u8fd9\u4e9b\u6c49\u5b50\u867d\u7136\u4ff1\u662f\u6ee1\u624b\u8840\u8ff9\uff0c\u4f46\u4ed6\u4eec\u5374\u662f\u5949\u547d\u800c\u884c\uff0c\u53ea\u4e0d\u8fc7 \u662f\u522b\u4eba\u7684\u5de5\u5177\u800c\u5df2\u2014\u2014\u201d\u4ed6\u751f\u6027\u5bbd\u5927\uff0c\u4e00\u5ff5\u81f3\u6b64\uff0c\u4e0d\u7981\u6c89\u58f0\u9053\uff1a\u201c\u6211\u4e0e\u4f60\u4eec\u7d20\u65e0\u4ec7\u6028\uff0c\u4f60\u4eec \u65b9\u624d\u867d\u7136\u6697\u7b97\u4e8e\u6211\uff0c\u4f46\u2026\u2026\u201d \u3000\u3000\u6e29\u747e\u5fae\u5fae\u4e00\u7b11\uff1a\u201c\u65e5\u540e\u82e5\u518d\u6709\u6076\u884c\u2014\u2014\u54fc\u54fc\uff0c\u6211\u4e0d\u8bf4\u4f60\u4eec\u4e5f\u8be5\u77e5\u9053\uff0c\u6211\u4f1a\u4e0d\u4f1a\u518d\u653e\u8fc7\u4f60 \u4eec\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u5fae\u5fae\u4e00\u7b11\uff0c\u610f\u9887\u79f0\u8bb8\uff0c\u53ea\u89c1\u5510\u4e49\u53e3\u4e2d\u8bfa\u8bfa\u8fde\u58f0\uff0c\u606d\u8eab\u884c\u4e86\u4e00\u793c\uff0c\u4fef\u8eab\u625b\u8d77\u4e54\u8fc1\uff0c \u9053\uff1a\u201c\u4e0d\u6740\u4e4b\u6069\uff0c\u6c38\u94ed\u5fc3\u4e2d\u3002\u201d \u3000\u3000\u5de6\u624b\u4e00\u6325\uff0c\u90a3\u6570\u5341\u4e2a\u9ed1\u8863\u6c49\u5b50\u4e00\u8d77\u5954\u4e86\u8fc7\u6765\uff0c\u9f50\u5730\u8eac\u8eab\u4e00\u793c\uff0c\u8fd9\u6570\u5341\u6761\u6c49\u5b50\u5728\u8fd9\u7b49\u60c5\u51b5 \u4e4b\u4e2d\uff0c\u884c\u8d70\u8fdb\u9000\uff0c\u4ecd\u7136\u4e00\u4e1d\u4e0d\u4e71\uff0c\u800c\u4e14\u7edd\u65e0\u55a7\u6742\u4e4b\u58f0\uff0c\u5353\u957f\u537f\u6697\u6697\u5fd6\u9053\uff1a\u201c\u5982\u6b64\u770b\u6765\uff0c\u8700\u4e2d \u5510\u95e8\uff0c\u7684\u786e\u975e\u662f\u6cdb\u6cdb\u4e4b\u8f88\u3002\u201d \u3000\u3000\u53ea\u89c1\u8fd9\u6570\u5341\u4e2a\u9ed1\u8863\u6c49\u5b50\uff0c\u4e00\u4e2a\u8fde\u7740\u4e00\u4e2a\uff0c\u9c7c\u8d2f\u800c\u884c\uff0c\u884c\u4e0b\u5c71\u9053\uff0c\u5510\u4e49\u7a81\u53c8\u8f6c\u8eab\u5954\u56de\uff0c\u63a0 \u81f3\u5353\u957f\u537f\u8eab\u524d\uff0c\u53c8\u81ea\u8eac\u8eab\u4e00\u793c\uff0c\u9053\uff1a\u201c\u9601\u4e0b\u4fa0\u5fc3\u4fa0\u672f\uff0c\u6b66\u529f\u9ad8\u7edd\u6b66\u6797\uff0c\u4e0d\u77e5\u53ef\u5426\u5c06\u4fa0\u540d\u89c1 \u544a\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u5fae\u5fae\u4e00\u7b11\uff0c\u4ed6\u7d20\u6027\u6de1\u6cca\uff0c\u5e76\u65e0\u5728\u6b66\u6797\u4e2d\u626c\u540d\u7acb\u4e07\u4e4b\u5fc3\uff0c\u56e0\u800c\u4fbf\u987e\u5de6\u53f3\u800c\u8a00\u4ed6\u5730\u7b11 \u9053\uff1a\u201c\u592a\u9633\u2014\u2014\u201d\u4ed6\u672c\u60f3\u8bf4\uff1a\u201c\u592a\u9633\u597d\u70c8\u3002\u201d\u54ea\u77e5\u4ed6\u65b9\u81ea\u8bf4\u4e86\u201c\u592a\u9633\u201d\u4e24\u5b57\uff0c\u6e29\u747e\u4fbf\u5df2\u63a5\u53e3 \u9053\uff1a\u201c\u4ed6\u53eb\u5353\u957f\u537f\u3002\u201d \u3000\u3000\u67f3\u7709\u5e26\u7b11\uff0c\u661f\u7738\u6d41\u76fc\uff0c\u795e\u8272\u4e4b\u4e2d\uff0c\u6ee1\u662f\u5f97\u610f\u4e4b\u60c5\uff0c\u663e\u89c1\u662f\u9887\u4ee5\u6709\u53cb\u5982\u6b64\u800c\u81ea\u50b2\u3002 \u3000\u3000\u5510\u4e49\u656c\u8bfa\u4e00\u58f0\uff0c\u606d\u58f0\u9053\uff1a\u201c\u539f\u6765\u9601\u4e0b\u4fa0\u540d\u592a\u9633\u541b\u5b50\uff0c\u5509\u2014\u2014\u9601\u4e0b\u5982\u6b64\u4e3a\u4eba\uff0c\u867d\u7136\u662f\u592a\u9633 \u547d\u540d\uff0c\u4e5f\u4e0d\u8db3\u4ee5\u5f62\u5bb9\u9601\u4e0b\u4ec1\u4e49\u4e8e\u4e07\u5353\u957f\u537f\u6123\u4e86\u4e00\u6123\uff0c\u5374\u89c1\u4ed6\u53c8\u662f\u8f6c\u8eab\u800c\u53bb\uff0c\u4e0d\u7981\u82e6\u7b11\u9053\uff1a \u201c\u592a\u9633\u541b\u5b50\u2014\u2014\u770b\u6765\u6b64\u4eba\u7adf\u6562\u7ed9\u6211\u6309\u4e0a\u4e00\u4e2a\u5982\u6b64\u53e4\u602a\u7684\u540d\u5b57\u3002\u201d \u3000\u3000\u6e29\u747e\u5a07\u7b11\u9053\uff1a\u201c\u8fd9\u4e2a\u540d\u5b57\u4e0d\u597d\u4e48\uff1f\u201d \u3000\u3000\u5353\u957f\u537f\u82e6\u7b11\u9053\uff1a\u201c\u6211\u539f\u5148\u672c\u5728\u5947\u602a\uff0c\u6b66\u6797\u8c6a\u58eb\uff0c\u5927\u534a\u6709\u4e2a\u540d\u53f7\uff0c\u5374\u4e0d\u77e5\u8fd9\u4e9b\u540d\u53f7\u662f\u54ea\u91cc \u6765\u7684\uff0c\u5982\u4eca\u60f3\u6765\uff0c\u5927\u90fd\u662f\u8fd9\u6837\u8bef\u6253\u8bef\u649e\u5f97\u5230\u7684\u5427\uff01\u201d \u3000\u3000\u6e29\u747e\u7b11\u9053\uff1a\u201c\u8fd9\u4e5f\u672a\u5fc5\u89c1\u5f97\uff0c\u6709\u4e9b\u4eba\u7684\u540d\u53f7\uff0c\u7684\u786e\u662f\u6c5f\u6e56\u4e2d\u4eba\u516c\u9001\u7684\uff0c\u6b66\u6797\u4e2d\u8fd9\u8d3a\u53f7\u5927 \u5178\uff0c\u672c\u662f\u5341\u5206\u9686\u91cd\u4e4b\u4e8b\uff0c\u8b6c\u5982\u8bf4\u90a3\u829c\u6e56\u57ce\u4e2d\u7684\u4ec1\u4e49\u5251\u5ba2\u4e91\u4e2d\u7a0b\u8d3a\u53f7\u4e4b\u65f6\uff0c\u636e\u8bf4\u6c5f\u5357\u7684\u6b66\u6797\u8c6a \u58eb\uff0c\u5728\u829c\u6e56\u57ce\u4e2d\uff0c\u66fe\u6446\u9152\u4e03\u65e5\uff0c\u4ee5\u8868\u656c\u8d3a\uff0c\u6709\u7684\u4eba\u7684\u540d\u53f7\uff0c\u5374\u662f\u88ab\u4eba\u9a82\u51fa\u6765\u7684\u2014\u2014\u201d\u5353\u957f\u537f \u5fae\u5fae\u4e00\u7b11\uff0c\u672c\u60f3\u8bf4\u9053\uff1a\u201c\u60f3\u6765\u4e11\u4eba\u4e24\u5b57\uff0c\u5c31\u662f\u88ab\u4eba\u9a82\u51fa\u6765\u7684\u4e86\u3002\u201d \u3000\u3000\u4f46\u8bdd\u5230\u53e3\u8fb9\uff0c\u53c8\u590d\u5fcd\u4f4f\uff0c\u53ea\u542c\u6e29\u747e\u9053\uff1a\u201c\u8fd8\u6709\u4e9b\u4eba\u7684\u540d\u53f7\uff0c\u5374\u662f\u81ea\u5df1\u5f80\u81ea\u5df1\u9762\u4e0a\u8d34\u91d1\uff0c \u81ea\u5df1\u7ed9\u81ea\u5df1\u53d6\u7684\u4ec0\u4e48\u5927\u738b\uff0c\u4ec0\u4e48\u4ed9\u5b50\uff0c\u4ec0\u4e48\u7687\u5e1d\uff0c\u5927\u6982\u5176\u4e2d\u5341\u4e4b\u516b\u4e5d\uff0c\u90fd\u662f\u5c5e\u4e8e\u8fd9\u4e00\u7c7b \u7684\u3002\u201d\u5353\u957f\u537f\u7b11\u9053\uff1a\u201c\u5984\u7aa5\u5e1d\u53f7\uff0c\u804a\u4ee5\u81ea\u5a31\uff0c\u8fd9\u4e9b\u4eba\u5012\u4e5f\u90fd\u5929\u771f\u5f97\u5f88\u3002\u201d \u3000\u3000\u6e29\u747e\u7b11\u9053\uff1a\u201c\u6b66\u6797\u4e4b\u4e2d\uff0c\u4e3a\u4e86\u540d\u53f7\u6240\u751f\u7684\u7ea0\u7eb7\uff0c\u81ea\u53e4\u4ee5\u6765\uff0c\u5c31\u4e0d\u77e5\u6709\u591a\u5c11\uff0c\u6614\u5e74\u6b66\u5f53\u3001 \u5c11\u6797\u4e24\u6d3e\uff0c\u672c\u6765\u4e25\u7981\u95e8\u4e0b\u5f1f\u5b50\u5728\u6b66\u6797\u4e2d\u5984\u5f97\u540d\u53f7\uff0c\u54ea\u77e5\u5f53\u65f6\u6b66\u5f53\u3001\u5c11\u6797\u4e24\u6d3e\u7684\u638c\u95e8\u4eba\uff0c\u5374\u90fd \u88ab\u6c5f\u6e56\u4eba\u8d77\u4e86\u4e2a\u540d\u53f7\uff0c\u4e8e\u662f\u4ed6\u4eec\u8fd9\u624d\u77e5\u9053\uff0c\u5728\u6c5f\u6e56\u4e2d\u80fd\u7acb\u4e0b\u4e2a\u2018\u4e07\u513f\u2019\uff0c\u867d\u7136\u4e0d\u6613\uff0c\u4f46\u4e00\u7ecf \u7acb\u4e0b\uff0c\u5374\u6839\u672c\u4e0d\u7531\u81ea\u5df1\u505a\u4e3b\uff0c\u4f60\u4e0d\u60f3\u53eb\u8fd9\u4e2a\u540d\u5b57\uff0c\u90a3\u53ef\u771f\u6bd4\u4ec0\u4e48\u90fd\u96be\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u5fae\u4e00\u76b1\u7709\uff0c\u7b11\u9053\uff1a\u201c\u5982\u679c\u6211\u4e0d\u613f\u88ab\u4eba\u53eb\u505a\u592a\u9633\u541b\u5b50\u90fd\u4e0d\u884c\u4e48\uff1f\uff01\u201d \u3000\u3000\u6e29\u747e\u7b11\u9053\uff1a\u201c\u90a3\u4e2a\u81ea\u7136\uff0c\u6570\u5341\u5e74\u524d\uff0c\u70b9\u82cd\u6709\u4f4d\u5251\u5ba2\uff0c\u88ab\u4eba\u79f0\u505a\u91d1\u9e21\u5251\u5ba2\uff0c\u8fd9\u5927\u6982\u4ed6\u672c\u662f \u6606\u660e\u4eba\uff0c\u6c5f\u6e56\u4e2d\u4eba\u66ff\u4ed6\u53d6\u7684\u8fd9\u4e2a\u540d\u5b57\uff0c\u4e5f\u4e0d\u8fc7\u662f\u7528\u7684\u91d1\u4e4c\u78a7\u5251\u4e4b\u610f\uff0c\u54ea\u77e5\u8fd9\u4f4d\u5251\u5ba2\u5374\u4e3a\u4e86\u8fd9 \u4e2a\u540d\u5b57\uff0c\u9669\u4e9b\u4e00\u547d\u545c\u547c\uff0c\u5230\u540e\u6765\u867d\u672a\u6b7b\u53bb\uff0c\u5374\u4e5f\u5f04\u5f97\u4e00\u8eab\u9ebb\u70e6\uff0c\u72fc\u72c8\u4e0d\u582a\u4e86\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u5fc3\u4e2d\u5927\u5947\uff0c\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u8fd9\u5374\u53c8\u662f\u4f55\u6545\uff1f\u201d \u3000\u3000\u6e29\u747e\u9053\uff1a\u201c\u539f\u6765\u90a3\u65f6\u6b66\u6797\u4e2d\u53eb\u505a\u8708\u86a3\u7684\u4eba\u7279\u522b\u591a\uff0c\u6709\u98de\u5929\u8708\u86a3\uff0c\u6709\u5343\u8db3\u8708\u86a3\uff0c\u6709\u94c1\u8708 \u86a3\uff0c\u6709\u8708\u86a3\u795e\u5251\uff0c\u8fd9\u8fd8\u4e0d\u7528\u8bf4\u4ed6\uff0c\u8fd8\u6709\u4e00\u4e2a\u52bf\u529b\u6781\u5927\u7684\u5e2e\u4f1a\uff0c\u5374\u4e5f\u53eb\u505a\u8708\u86a3\u5e2e\u3002\u201d \u3000\u3000\u5979\u5a07\u7b11\u4e00\u58f0\uff0c\u53c8\u9053\uff1a\u201c\u8fd9\u4e9b\u8708\u86a3\u4eec\uff0c\u90fd\u8ba4\u4e3a\u91d1\u9e21\u5251\u5ba2\u7684\u540d\u5b57\u89e6\u72af\u4e86\u4ed6\u4eec\u7684\u5927\u5fcc\uff0c\u56e0\u4e4b\u90fd \u8d76\u5230\u4e91\u5357\u53bb\uff0c\u8981\u5c06\u90a3\u91d1\u9e21\u5251\u5ba2\u7f6e\u4e4b\u6b7b\u5730\u3002\u201d \u3000\u3000\u201c\u90a3\u91d1\u9e21\u5251\u5ba2\u6b66\u529f\u867d\u9ad8\uff0c\u4f46\u53cc\u62f3\u4e0d\u654c\u56db\u624b\uff0c\u88ab\u8fd9\u4e9b\u4eba\u903c\u5f97\u51e0\u4e4e\u6ca1\u6709\u85cf\u8eab\u4e4b\u5730\uff0c\u90a3\u65f6\u70b9\u82cd \u6d3e\u7684\u4e03\u624b\u795e\u5251\u5df2\u6b7b\u53bb\u591a\u5e74\uff0c\u70b9\u82cd\u6d3e\u6b63\u662f\u6700\u8870\u5fae\u4e0d\u632f\u7684\u65f6\u5019\uff0c\u662f\u4ee5\u4ed6\u7684\u540c\u95e8\u4e5f\u4ff1\u90fd\u675f\u624b\u65e0 \u7b56\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u5e7c\u968f\u4e25\u5e08\uff0c\u53f8\u7a7a\u8001\u4eba\u867d\u4e5f\u66fe\u5bf9\u4ed6\u8bf4\u8fc7\u4e9b\u6b66\u6797\u540d\u4eba\u7684\u4e8b\u8ff9\uff0c\u4f46\u5374\u90fd\u662f\u4e00\u4e9b\u5149\u660e\u5802\u7687 \u7684\u6545\u4e8b\uff0c\u662f\u4ee5\u5353\u957f\u537f\u4e00\u751f\u4e4b\u4e2d\uff0c\u51e0\u66fe\u542c\u5230\u8fc7\u8fd9\u4e9b\u8da3\u5473\u76ce\u7136\u7684\u6b66\u6797\u638c\u6545\uff0c\u5fcd\u4e0d\u4f4f\u542b\u7b11\u63a5\u53e3\u9053\uff1a \u201c\u540e\u6765\u90a3\u91d1\u9e21\u96be\u9053\u4f1a\u88ab\u90a3\u4e9b\u8708\u86a3\u54ac\u6b7b\u4e48\uff1f\u201d \u3000\u3000\u6e29\u747e\u7b11\u9053\uff1a\u201c\u90a3\u91d1\u9e21\u5251\u5ba2\u4e1c\u85cf\u897f\u8eb2\uff0c\u5230\u540e\u6765\u5b9e\u5728\u65e0\u6cd5\uff0c\u4fbf\u626c\u6709\u6b66\u6797\uff0c\u8bf4\u81ea\u5df1\u4e0d\u8981\u518d\u53eb\u91d1 \u9e21\u8fd9\u4e2a\u540d\u53f7\u4e86\uff0c\u54ea\u77e5\u90a3\u4e9b\u8708\u86a3\u5374\u8fd8\u662f\u4e0d\u80af\u653e\u8fc7\u4ed6\uff0c\u76f4\u5230\u540e\u6765\u6b66\u5f53\u3001\u5c11\u6797\u4e24\u6d3e\u7684\u638c\u95e8\u771f\u4eba\uff0c\u4e00 \u8d77\u51fa\u6765\u4e3a\u4ed6\u5316\u89e3\uff0c\u624d\u7b97\u65e0\u4e8b\uff0c\u4f60\u770b\uff0c\u4e3a\u4e86\u4e00\u4e2a\u540d\u5b57\uff0c\u5728\u6c5f\u6e56\u4e2d\u7adf\u7136\u5f04\u51fa\u8f69\u7136\u5927\u6ce2\uff0c\u8fd9\u5c82\u975e\u5947 \u4e8b\u4e48\uff1f\u201d \u3000\u3000\u5353\u957f\u537f\u5927\u611f\u5174\u8da3\uff0c\u9053\uff1a\u201c\u8fd8\u6709\u5462\uff1f\u201d \u3000\u3000\u6e29\u747e\u5a07\u7b11\u4e00\u58f0\uff0c\u79cb\u6ce2\u4e00\u8f6c\uff0c\u53c8\u9053\uff1a\u201c\u8bf4\u5230\u91d1\u9e21\uff0c\u6211\u60f3\u8d77\u6614\u5e74\u8fd8\u6709\u4e00\u4e2a\u8ddb\u5b50\uff0c\u4e5f\u88ab\u4eba\u53eb\u505a \u91d1\u9e21\uff0c\u53ea\u662f\u8fd9\u5374\u662f\u522b\u4eba\u5728\u6697\u4e2d\u8baa\u5632\u4ed6\uff0c\u53d6\u7684\u662f\u91d1\u9e21\u72ec\u7acb\u4e4b\u610f\uff0c\u53ea\u53ef\u7b11\u8fd9\u4eba\u8fd8\u4e0d\u77e5\u9053\uff0c\u7ade\u81ea\u4ee5 \u4e3a\u5f97\u610f\uff0c\u8fd8\u521b\u91d1\u9e21\u5e2e\uff0c\u8981\u4ed6\u7684\u95e8\u4eba\u5b50\u5f1f\uff0c\u90fd\u7a7f\u7740\u4e94\u989c\u516d\u8272\u7684\u8863\u88f3\uff0c\u7f8e\u5176\u540d\u4e3a\u9e21\u5c3e\u3002\u201d \u3000\u3000\u5979\u53f9\u4e86\u53e3\u6c14\uff0c\u53c8\u9053\uff1a\u201c\u6b66\u6797\u4e2d\u6709\u5173\u540d\u5b57\u7684\u7b11\u8bdd\u867d\u591a\uff0c\u4f46\u56e0\u6b64\u751f\u51fa\u60b2\u60e8\u4e4b\u4e8b\u6765\u7684\uff0c\u4e5f\u6709\u4e0d \u5c11\uff0c\u636e\u8bf4\u6614\u5e74\u6b66\u6797\u4e2d\u6709\u4e24\u4f4d\u76d6\u4e16\u5947\u4eba\uff0c\u4e00\u4e2a\u53eb\u5357\u9f99\uff0c\u4e00\u4e2a\u53eb\u5317\u9f99\uff0c\u4e24\u4eba\u5c31\u662f\u4e3a\u4e86\u8fd9\u540d\u5b57\uff0c\u5404 \u4e0d\u76f8\u8ba9\uff0c\u7adf\u6bd4\u6597\u4e86\u6570\u5341\u5e74\uff0c\u5230\u540e\u6765\u7ade\u540c\u5f52\u4e8e\u5c3d\uff0c\u4e00\u8d77\u6b7b\u5728\u5317\u4eac\u57ce\u90ca\u7684\u4e00\u4e2a\u6811\u6797\u91cc\uff0c\u4ed6\u4eec\u6b7b\u540e \u53c8\u5404\u4f20\u4e86\u4e00\u4e2a\u5f1f\u5b50\uff0c\u90a3\u4e24\u4e2a\u5c11\u5e74\uff0c\u672c\u662f\u597d\u53cb\uff0c\u4f46\u4e3a\u4e86\u4ed6\u4eec\u4e0a\u4ee3\u7684\u6028\u4ec7\uff0c\u5374\u4e5f\u53ea\u5f97\u5316\u53cb\u4e3a\u654c\uff0c \u76f4\u5230\u6570\u5341\u5e74\u4e4b\u540e\uff0c\u624d\u5c06\u8fd9\u6bb5\u6028\u4ec7\u89e3\u5f00\uff0c\u4f46\u5374\u5df2\u4e0d\u77e5\u751f\u51fa\u591a\u5c11\u4e8b\u6545\u4e86\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u957f\u53f9\u4e00\u58f0\uff1a\u201c\u8fd9\u53c8\u4f55\u82e6\uff01\u201d \u3000\u3000\u5782\u9996\u534a\u664c\uff0c\u5ffd\u53c8\u5c55\u989c\u7b11\u95ee\uff1a\u201c\u8fd8\u6709\u6ca1\u6709\uff1f\u201d \u3000\u3000\u6e29\u747e\u201c\u6251\u54e7\u201d\u4e00\u7b11\uff0c\u5a07\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u4eba\u771f\u662f\u7684\uff0c\u4e5f\u6ca1\u6709\u770b\u89c1\u2026\u2026\u201d \u3000\u3000\u8bdd\u58f0\u672a\u4e86\uff0c\u53ea\u542c\u8fdc\u5904\u7a81\u7136\u547c\u58f0\u8fed\u8d77\uff0c\u4ed6\u4fe9\u9f50\u5730\u4e00\u60ca\uff0c\u7eb5\u8eab\u63a0\u53bb\u3002 \u3000\u3000\u53ea\u89c1\u90a3\u4e9b\u5510\u95e8\u9ed1\u8863\u6c49\u5b50\uff0c\u4ff1\u5c06\u884c\u4eba\u5bc6\u6797\uff0c\u6b64\u523b\u4ed6\u4eec\u672c\u81ea\u6392\u5217\u5f97\u5341\u5206\u6574\u9f50\u7684\u884c\u5217\uff0c\u7adf\u7a81\u7136 \u5927\u4e71\u8d77\u6765\uff0c\u547c\u53f1\u4e4b\u58f0\u4ea4\u5e94\u4e0d\u7edd\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u4e9b\u6742\u4e71\u7684\u4eba\u5f71\u4e4b\u4e2d\uff0c\u53c8\u6709\u4e24\u6761\u4eba\u5f71\uff0c\u5de6\u5954\u53f3\u7a81\uff0c\u6240\u7ecf\u4e4b\u5904\uff0c\u9ed1\u8863\u6c49\u5b50\u5e94\u58f0\u800c\u5012\uff0c \u5353\u957f\u537f\u5389\u53f1\u4e00\u58f0\uff0c\u98de\u5954\u800c\u53bb\uff0c\u53ea\u89c1\u90a3\u4e24\u6761\u4eba\u5f71\u4ea6\u81ea\u4e00\u58f0\u5927\u559d\uff0c\u4e00\u63a0\u6570\u4e08\uff0c\u5982\u98de\u63a0\u4e86\u8fc7\u6765\u3002 \u7b2c\u5341\u4e03\u7ae0\u3000\u58f0\u9707\u56db\u91ce>> \u53e4\u9f99\u300a\u6708\u5f02\u661f\u90aa\u300b \u7b2c\u5341\u4e03\u7ae0\u3000\u58f0\u9707\u56db\u91ce \u3000\u3000\u65e5\u5149\u4e4b\u4e0b\uff0c\u53ea\u770b\u89c1\u8fd9\u4e24\u6761\u4eba\u5f71\uff0c\u53d1\u9afb\u84ec\u4e71\uff0c\u8863\u886b\u4e0d\u6574\uff0c\u4f3c\u662f\u9887\u4e3a\u7126\u6025\u6f66\u5012\uff0c\u53ea\u6709\u8eab\u4e0a\u7684 \u4e00\u88ad\u674f\u9ec4\u8863\u886b\uff0c\u72b9\u5728\u65e5\u5149\u4e2d\u95f4\u70c1\u7740\u593a\u76ee\u7684\u9c9c\u8273\u4e4b\u8272\uff0c\u5374\u6b63\u662f\u90a3\u4e07\u5999\u771f\u541b\u7684\u5f1f\u5b50\u94c1\u8fbe\u4eba\u4e0e\u77f3 \u5e73\u3002 \u3000\u3000\u5353\u957f\u537f\u8eab\u5f62\u65b9\u52a8\uff0c\u4fbf\u77a5\u89c1\u8fd9\u4e24\u4eba\u7684\u8863\u51a0\u9762\u5bb9\uff0c\u811a\u6b65\u7acb\u523b\u4e3a\u4e4b\u4e00\u987f\uff0c\u53ea\u89c1\u4ed6\u4e24\u4eba\u5982\u98de\u5730\u5728 \u81ea\u5df1\u8eab\u4fa7\u63a0\u8fc7\uff0c\u671b\u4e5f\u4e0d\u671b\u81ea\u5df1\u4e00\u773c\uff0c\u7b14\u76f4\u63a0\u5230\u6e29\u747e\u8eab\u524d\uff0c\u6e29\u747e\u79cb\u6ce2\u8f6c\u5904\uff0c\u51b7\u51b7\u4e00\u7b11\uff0c\u7f13\u7f13 \u9053\uff1a\u201c\u505a\u5b8c\u4e86\u4e48\uff1f\u201d \u3000\u3000\u94c1\u8fbe\u4eba\u3001\u77f3\u5e73\u80f8\u819b\u6025\u5267\u5730\u8d77\u4f0f\u4e86\u534a\u664c\uff0c\u65b9\u81ea\u9f50\u58f0\u7b54\u9053\uff1a\u201c\u505a\u5b8c\u4e86\u3002\u201d \u3000\u3000\u6e29\u747e\u4e00\u624b\u8f7b\u629a\u4e91\u9b13\uff0c\u7a81\u5730\u76ee\u5149\u4e00\u51db\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\u505a\u5b8c\u4e86\uff1f\u201d \u3000\u3000\u94c1\u8fbe\u4eba\u3001\u77f3\u5e73\u9f50\u5730\u4e00\u6123\uff0c\u6084\u4fcf\u5bf9\u671b\u4e00\u773c\uff0c\u4e24\u4eba\u76ee\u5149\u76f8\u5bf9\uff0c\u5404\u4e2a\u5f20\u53e3\u7ed3\u820c\uff0c\u5446\u5446\u5730\u6115\u4e86\u534a \u664c\uff0c\u94c1\u8fbe\u4eba\u4e8e\u54b3\u4e00\u58f0\uff0c\u671f\u827e\u7740\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\u2026\u2026\u201d \u3000\u3000\u77f3\u5e73\u62bd\u8fdb\u4e00\u53e3\u957f\u6c14\uff0c\u5450\u5450\u5730\u63a5\u53e3\u9053\uff1a\u201c\u6211\u4eec\u5df2\u2026\u2026\u5df2\u2026\u2026\u201d \u3000\u3000\u8fd9\u4e24\u4eba\u867d\u7136\u624b\u9ed1\u5fc3\u8fa3\uff0c\u65e0\u4ec1\u65e0\u4e49\uff0c\u4f46\u6bd5\u7adf\u8fd8\u662f\u65e0\u6cd5\u5c06\u6740\u5e08\u7684\u6076\u884c\u8bf4\u51fa\u53e3\u6765\u3002 \u3000\u3000\u6e29\u747e\u51b7\u7b11\u4e00\u58f0\uff0c\u5fae\u62e7\u7ea4\u8170\uff0c\u8f6c\u8fc7\u8eab\u53bb\uff0c\u518d\u4e5f\u4e0d\u671b\u4ed6\u4e24\u4eba\u4e00\u773c\uff0c\u8f7b\u8511\u4e0d\u5c51\u4e4b\u610f\uff0c\u73b0\u4e8e\u8f9e \u8272\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u957f\u537f\uff0c\u6211\u4eec\u8d70\u5427\uff01\u201d \u3000\u3000\u94c1\u8fbe\u4eba\u3001\u77f3\u5e73\u9762\u8272\u9f50\u5730\u4e00\u53d8\uff0c\u5927\u559d\u4e00\u58f0\uff1a\u201c\u6e29\u59d1\u5a18\uff01\u201d \u3000\u3000\u4e00\u5de6\u4e00\u53f3\uff0c\u63a0\u5230\u6e29\u747e\u8eab\u524d\uff0c\u9f50\u5730\u559d\u9053\uff1a\u201c\u6e29\u59d1\u5a18\u6162\u8d70\uff01\u201d \u3000\u3000\u6e29\u747e\u9762\u5bb9\u4e00\u6574\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6211\u4e0e\u4f60\u4e24\u4eba\u7d20\u4e0d\u76f8\u8bc6\uff0c\u4f60\u4e24\u4eba\u8fd9\u822c\u7684\u7ea0\u7f20\u4e8e\u6211\uff0c\u96be\u9053\u662f\u6d3b \u5f97\u4e0d\u8010\u70e6\u4e86\uff1f\u201d \u3000\u3000\u5979\u81ea\u5e7c\u4e0e\u90a3\u540d\u6ee1\u5929\u4e0b\u7684\u5973\u9b54\u5934\u7ea2\u8863\u5a18\u5a18\u751f\u957f\uff0c\u8a00\u8bed\u4e4b\u4e2d\uff0c\u4fbf\u81ea\u4e5f\u67d3\u4e0a\u4e86\u8bb8\u591a\u6e29\u5982\u7389\u90a3\u822c \u51b7\u524a\u68ee\u5bd2\u7684\u610f\u5473\uff0c\u6b64\u523b\u4e00\u4e2a\u5b57\u4e00\u4e2a\u5b57\u8bf4\u5c06\u51fa\u6765\uff0c\u5f53\u771f\u662f\u5b57\u5b57\u6709\u5982\u5229\u7bad\uff0c\u7bad\u7bad\u5c04\u4eba\u94c1\u3001\u77f3\u4e24\u4eba \u5fc3\u4e2d\u3002 \u3000\u3000\u5353\u957f\u537f\u4e00\u6b65\u63a0\u56de\uff0c\u65e5\u5149\u52a8\u5904\uff0c\u89c1\u5230\u8fd9\u4e24\u4eba\u9762\u989d\u4e4b\u4e0a\uff0c\u51b7\u6c57\u6d94\u6d94\u843d\u4e0b\uff0c\u5fc3\u4e2d\u7a81\u89c9\u4e0d\u5fcd\uff0c\u800c \u957f\u53f9\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u4e24\u4eba\u53ef\u662f\u8981\u5bfb\u90a3\u6e29\u5982\u7389\u4e3a\u4f60\u7b49\u89e3\u53bb\u4e03\u7edd\u91cd\u624b\u4e48\uff1f\u201d \u3000\u3000\u94c1\u8fbe\u4eba\u3001\u77f3\u5e73\u76ee\u5149\u4e00\u4eae\uff0c\u8fde\u5fd9\u7b54\u9053\uff1a\u201c\u6b63\u662f\uff0c\u5982\u8499\u9601\u4e0b\u6307\u6559\uff0c\u513f\u6069\u6b64\u5fb7\uff0c\u6c38\u4e0d\u6562\u5fd8\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u7f13\u7f13\u8f6c\u8fc7\u76ee\u5149\uff0c\u4ed6\u5b9e\u5728\u4e0d\u613f\u89c1\u5230\u8fd9\u4e24\u4eba\u6b64\u523b\u8fd9\u79cd\u5351\u8d31\u4e4b\u6001\uff0c\u957f\u53f9\u4e00\u58f0\uff0c\u7f13\u7f13\u9053\uff1a \u201c\u6e29\u5982\u7389\u6b64\u523b\u5230\u54ea\u91cc\u53bb\u4e86\uff0c\u6211\u5b9e\u5728\u4e0d\u77e5\u9053\uff01\u2026\u2026\u201d \u3000\u3000\u8bed\u58f0\u672a\u4e86\uff0c\u94c1\u3001\u77f3\u4e24\u4eba\u9762\u5bb9\u53c8\u81ea\u53d8\u5f97\u4e00\u7247\u60e8\u767d\uff0c\u76ee\u5149\u4e2d\u6ee1\u9732\u54c0\u6c42\u4e5e\u601c\u4e4b\u610f\uff0c\u4f38\u51fa\u98a4\u6296\u7684 \u624b\u638c\uff0c\u4e00\u62b9\u9762\u4e0a\u7684\u6c57\u73e0\uff0c\u98a4\u58f0\u9053\uff1a\u201c\u9601\u4e0b\u867d\u4e0d\u77e5\u9053\uff0c\u96be\u9053\u6e29\u59d1\u5a18\u4e5f\u4e0d\u77e5\u9053\u4e48\uff1f\u201d \u3000\u3000\u6e29\u747e\u67f3\u7709\u4e00\u626c\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u6211\u7eb5\u7136\u77e5\u9053\uff0c\u4e5f\u4e0d\u4f1a\u544a\u8bc9\u4f60\uff0c\u50cf\u4f60\u4eec\u8fd9\u79cd\u4eba\uff0c\u4e16\u4e0a\u591a\u4e00\u4e2a\u4e0d \u5982\u5c11\u4e00\u4e2a\u7684\u597d\u3002\u201d \u3000\u3000\u7ea4\u8170\u4e00\u626d\uff0c\u518d\u6b21\u8f6c\u8fc7\u8eab\u53bb\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u957f\u537f\uff0c\u6211\u4eec\u8fd8\u4e0d\u8d70\u4e48\uff1f\u201d \u3000\u3000\u5353\u957f\u537f\u6697\u53f9\u4e00\u58f0\uff0c\u8f6c\u76ee\u671b\u53bb\uff0c\u53ea\u89c1\u94c1\u3001\u77f3\u4e24\u4eba\uff0c\u5782\u624b\u800c\u7acb\uff0c\u9762\u4e0a\u7a81\u7136\u73b0\u51fa\u4e00\u9635\u6124\u6fc0\u4e4b \u8272\uff0c\u53cc\u624b\u4e00\u9635\u7d27\u63e1\uff0c\u4f46\u77ac\u53c8\u5e73\u590d\uff0c\u4e00\u5de6\u4e00\u53f3\uff0c\u518d\u6b21\u63a0\u5230\u6e29\u747e\u9762\u524d\uff0c\u94c1\u8fbe\u4eba\u4e00\u626f\u77f3\u5e73\u7684\u8863\u895f\uff0c \u98a4\u5362\u9053\uff0c\u201c\u6e29\u59d1\u5a18\uff0c\u6211\u4e24\u4eba\u867d\u6709\u4e0d\u7aef\u4e4b\u884c\uff0c\u4f46\u5374\u662f\u5949\u4e86\u4ee4\u5e08\u4e4b\u547d\u2026\u2026\u6e29\u59d1\u5a18\uff0c\u6211\u4e24\u4eba\u4e0e\u4f60\u65e0 \u51a4\u65e0\u4ec7\uff0c\u96be\u9053\u4f60\u5c31\u5fcd\u5fc3\u4ee4\u6211\u4e24\u4eba\u5c31\u8fd9\u6837\u2026\u2026\u201d \u3000\u3000\u4ed6\u8bed\u58f0\u98a4\u6296\uff0c\u795e\u6001\u5351\u8d31\uff0c\u7eb5\u662f\u4e5e\u4e10\u6c42\u98df\uff0c\u5a74\u513f\u7d20\u4e73\uff0c\u4e5f\u6bd4\u4e0d\u4e0a\u4ed6\u6b64\u523b\u795e\u60c5\u4e4b\u4e07\u4e00\uff0c\u54ea\u91cc \u8fd8\u6709\u534a\u5206\u4ed6\u5e73\u65e5\u90a3\u822c\u5028\u9a84\u9ad8\u505a\u4e4b\u6001\uff0c\u8bf4\u5230\u540e\u6765\uff0c\u66f4\u662f\u58f0\u6cea\u9f50\u4e0b\uff0c\u51e0\u4e4e\u8dea\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u5353\u957f\u537f\u89c1\u5230\u8fd9\u822c\u60c5\u51b5\uff0c\u5fc3\u4e2d\u65e2\u89c9\u8f7b\u8511\uff0c\u53c8\u89c9\u4e0d\u5fcd\uff0c\u957f\u53f9\u4e00\u58f0\uff0c\u7f13\u7f13\u63a5\u53e3\u9053\uff1a\u201c\u751f\u547d\u5f53\u771f \u662f\u8fd9\u822c\u53ef\u8d35\u4e48\uff1f\u201d \u3000\u3000\u94c1\u8fbe\u4eba\u8bed\u58f0\u4e00\u987f\uff0c\u5446\u4e86\u4e00\u5446\uff0c\u5353\u957f\u537f\u63a5\u53e3\u53c8\u9053\uff1a\u201c\u751f\u547d\u56fa\u662f\u53ef\u8d35\uff0c\u4f46\u4f60\u4eec\u4e24\u4eba\u53ef\u77e5\u9053\uff0c \u4e16\u4e0a\u4e5f\u5e76\u975e\u5168\u65e0\u66f4\u6bd4\u751f\u547d\u53ef\u8d35\u4e4b\u7269\uff0c\u4f60\u4e24\u4eba\u6602\u85cf\u4e03\u5c3a\uff0c\u6b64\u523b\u5374\u505a\u51fa\u8fd9\u79cd\u795e\u6001\uff0c\u5fc3\u91cc\u662f\u5426\u89c9\u5f97 \u96be\u53d7\uff1f\u201d \u3000\u3000\u94c1\u8fbe\u4eba\u5446\u4e86\u534a\u664c\uff0c\u5782\u9996\u9053\uff1a\u201c\u597d\u6b7b\u4e0d\u5982\u6b79\u6d3b\uff0c\u6b64\u8bdd\u7531\u6765\u5df2\u4e45\uff0c\u6211\u4eec\u5e74\u7eaa\u8fd8\u8f7b\uff0c\u5b9e\u5728\u4e0d \u613f\u2026\u2026\u5b9e\u5728\u4e0d\u613f\u2026\u2026\u201d \u3000\u3000\u77f3\u5e73\u622a\u53e3\u9053\uff1a\u201c\u9601\u4e0b\u5e74\u7eaa\u4e0e\u6211\u7b49\u76f8\u82e5\uff0c\u6b63\u662f\u5927\u597d\u5e74\u534e\uff0c\u82e5\u662f\u9601\u4e0b\u4e5f\u4e00\u6837\u9047\u7740\u6211\u7b49\u6b64\u523b\u6240 \u9047\u4e4b\u4e8b\uff0c\u53ea\u6015\u2026\u2026\u201d \u3000\u3000\u5782\u4e0b\u5934\u53bb\uff0c\u4e0d\u4f4f\u54b3\u55fd\u3002 \u3000\u3000\u5353\u957f\u537f\u5251\u7709\u4e00\u8f69\uff0c\u6717\u58f0\u9053\uff1a\u201c\u751f\u56fa\u6211\u6240\u6b32\u4e5f\uff0c\u4e49\u4ea6\u6211\u6240\u6b32\u4e5f\uff0c\u4e24\u8005\u4e0d\u53ef\u5f97\u517c\uff0c\u820d\u751f\u800c\u53d6 \u4e49\u8033\uff01\u201d \u3000\u3000\u8bed\u58f0\u4e00\u987f\uff0c\u7a81\u7136\u60f3\u5230\u8fd9\u4e24\u4eba\u81ea\u5b69\u63d0\u4e4b\u65f6\uff0c\u4fbf\u88ab\u5c39\u51e1\u6536\u517b\uff0c\u5e73\u65e5\u8033\u5112\u76ee\u67d3\uff0c\u5c3d\u662f\u4e0d\u4ec1\u4e0d\u4e49 \u4e4b\u4e8b\uff0c\u82e5\u60f3\u8fd9\u4e24\u4eba\u4e86\u89e3\u8fd9\u79cd\u5723\u8d24\u4e4b\u8a00\uff0c\u5c82\u662f\u4e00\u65f6\u80fd\u4ee5\u505a\u5230\u4e4b\u4e8b\uff0c\u6b63\u662f\u201c\u4eba\u4e4b\u521d\uff0c\u6027\u672c\u5584\uff0c\u82df \u4e0d\u6559\uff0c\u6027\u4e43\u8fc1\u2026\u2026\u201d \u3000\u3000\u8fd9\u4e24\u4eba\u6709\u4eca\u65e5\u5351\u8d31\u4e4b\u6001\uff0c\u5b9e\u5728\u4e5f\u4e0d\u80fd\u5b8c\u5168\u602a\u5f97\u4e86\u4ed6\u4eec\u3002 \u3000\u3000\u8981\u77e5\u9053\u5353\u957f\u537f\u9762\u51b7\u5fc3\u6148\uff0c\u751f\u6027\u5bbd\u539a\uff0c\u4e00\u751f\u884c\u4e8b\uff0c\u4e3a\u5df2\u7740\u60f3\u7684\u5c11\uff0c\u4e3a\u4eba\u7740\u60f3\u7684\u591a\uff0c\u6b64\u523b\u4e00 \u5ff5\u81f3\u6b64\uff0c\u4e0d\u7981\u53f9\u9053\uff1a\u201c\u6e29\u5982\u7389\u6b64\u523b\u662f\u5728\u4f55\u5904\uff0c\u6211\u4e0e\u6e29\u59d1\u5a18\u4e0d\u77e5\u9053\uff0c\u4f46\u4eca\u591c\u5979\u5374\u5b9a\u8981\u5230\u6628\u591c\u90a3 \u5385\u5802\u4e4b\u4e2d\uff0c\u4e0e\u6211\u4e24\u4eba\u76f8\u4f1a\uff0c\u4f60\u7b49\u4e0d\u59a8\u5148\u53bb\u7b49\u5979\uff01\u201d \u3000\u3000\u6e29\u747e\u51b7\u7b11\u4e00\u58f0\uff0c\u76ee\u5149\u671b\u5411\u5929\u4e0a\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u5176\u5b9e\u4ee5\u8fd9\u4e24\u4eba\u7684\u4e3a\u4eba\uff0c\u8fd8\u4e0d\u5982\u8ba9\u4ed6\u4eec\u6b7b\u4e86\u66f4 \u597d\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u5e72\u54b3\u4e00\u58f0\uff0c\u4f3c\u662f\u60f3\u8bf4\u4ec0\u4e48\uff0c\u5374\u53c8\u5fcd\u4f4f\uff0c\u6325\u624b\u9053\uff1a\u201c\u4f60\u4e24\u4eba\u8fd8\u4e0d\u53bb\u4e48\uff1f\u201d \u3000\u3000\u76ee\u5149\u4e00\u62ac\uff0c\u5374\u89c1\u94c1\u3001\u77f3\u4e24\u4eba\u7adf\u662f\u72e0\u72e0\u5730\u671b\u7740\u6e29\u747e\uff0c\u76ee\u5149\u4e2d\u6ee1\u542b\u6028\u6bd2\u4e4b\u610f\uff0c\u826f\u4e45\u826f\u4e45\uff0c\u624d \u81ea\u8f6c\u8fc7\u8eab\u6765\uff0c\u9762\u5411\u5353\u957f\u537f\u62b1\u62f3\u4e00\u63d6\uff0c\u6c89\u58f0\u8bf4\u9053\uff1a\u201c\u9752\u5c71\u4e0d\u6539\uff0c\u7eff\u6c34\u957f\u6d41\uff0c\u518d\u89c1\u6709\u671f\u3002\u201d \u3000\u3000\u4e24\u4eba\u5237\u5730\u62e7\u8170\u63a0\u53bb\uff0c\u6e29\u747e\u671b\u7740\u4ed6\u4e24\u4eba\u7684\u8eab\u5f71\uff0c\u6068\u58f0\u8bf4\u9053\uff1a\u82e5\u4f9d\u7740\u6211\u7684\u6027\u5b50\uff0c\u771f\u4e0d\u5982\u53eb\u8fd9 \u4e24\u4eba\u6b7b\u4e86\u7684\u597d\u3002\u201c\u5353\u957f\u537f\u4e00\u6574\u9762\u5bb9\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u4eba\u4e4b\u521d\u6027\u672c\u5584\uff0c\u4e16\u4e0a\u6076\u4eba\u591a\u56e0\u73af\u5883\u4f7f\u7136\uff0c \u518d\u65e0\u4e00\u4eba\u751f\u6765\u4fbf\u60f3\u4e3a\u532a\u4e3a\u76d7\u7684\uff0c\u80fd\u4f7f\u4e00\u6076\u4eba\u6539\u8fc7\u5411\u5584\uff0c\u66f4\u80dc\u8fc7\u8bdb\u4e00\u6076\u4eba\u591a\u591a\uff0c\u747e\u513f\uff0c\u4e3a\u4eba\u7acb \u8eab\u5904\u4e16\uff0c\u603b\u8be5\u5904\u5904\u4ee5\u4ec1\u539a\u4e3a\u6000\uff0c\u8fd9\u6837\u7684\u8bdd\uff0c\u4f60\u4ee5\u540e\u4e0d\u8981\u8bf4\u4e86\u3002\u201d \u3000\u3000\u6e29\u747e\u9762\u988a\u4e00\u7ea2\uff0c\u5979\u4e00\u751f\u5a07\u7eb5\uff0c\u51e0\u4f1a\u53d7\u4eba\u8d23\u5907\uff0c\u4f46\u6b64\u523b\u542c\u4e86\u5353\u957f\u537f\u7684\u8bdd\uff0c\u5374\u8fde\u534a\u53e5\u8fa8\u9a73\u4e4b \u8a00\u4e5f\u8bf4\u4e0d\u51fa\u53e3\u3002 \u3000\u3000\u4e00\u9635\u5c71\u98ce\u5439\u8d77\u4e86\u5979\u9b13\u8fb9\u7684\u4e71\u53d1\uff0c\u5979\u7a81\u7136\u89c9\u5f97\u4e00\u652f\u5bbd\u5927\u6e29\u6696\u7684\u624b\u638c\uff0c\u5728\u8f7b\u8f7b\u6574\u7406\u7740\u5979\u88ab\u98ce \u5439\u4e71\u4e86\u7684\u53d1\u4e1d\uff0c\u4e5f\u4f3c\u4e4e\u5728\u8f7b\u8f7b\u6574\u7406\u7740\u5979\u5fc3\u4e2d\u7d0a\u4e71\u7684\u601d\u7eea\uff0c\u4e8e\u662f\u5979\u7ec8\u4e8e\u53c8\u5012\u5411\u4ed6\u5bbd\u9614\u7684\u80f8\u819b\uff0c \u53bb\u4eab\u53d7\u4eca\u591c\u66b4\u98ce\u96e8\u524d\u7247\u523b\u7684\u5b81\u9759\u3002 \u3000\u3000\u7136\u800c\u66b4\u98ce\u96e8\u524d\u7684\u4e34\u5b89\uff0c\u5374\u5e76\u6ca1\u6709\u7247\u523b\u7684\u5b81\u9759\uff0c\u968f\u7740\u65f6\u65e5\u4e4b\u65e2\u53bb\uff0c\u4e34\u5b89\u57ce\u4e2d\u7684\u6b66\u6797\u7fa4\u8c6a\uff0c \u4eba\u4eba\u5fc3\u4e2d\u90fd\u5728\u7126\u6025\u5730\u6697\u4e2d\u81ea\u5ff5\uff1a\u201c\u8ddd\u79bb\u5929\u76ee\u4e4b\u4f1a\uff0c\u53ea\u6709\u4e24\u4e09\u5929\u4e86\uff0c\u4e24\u4e09\u5929\u4e86\u2026\u2026\u201d \u3000\u3000\u8fd9\u4e24\u4e09\u5929\u7684\u65f6\u95f4\uff0c\u5728\u4eba\u4eec\u5fc3\u4e2d\u5374\u90fd\u4f3c\u6709\u4e0d\u53ef\u6bd4\u62df\u7684\u6f2b\u957f\u3002 \u3000\u3000\u4e45\u5df2\u55a7\u80dc\u4eba\u53e3\u7684\u5929\u76ee\u4e4b\u4f1a\uff0c\u5728\u4eba\u4eec\u5fc3\u4e2d\uff0c\u5c31\u4eff\u4f5b\u662f\u9b54\u672f\u5e08\u624b\u4e2d\u9ed1\u4e2d\u4e0b\u7684\u79d8\u5bc6\uff0c\u4ed6\u4eec\u90fd\u5728 \u671f\u5f85\u7740\u8fd9\u9ed1\u4e2d\u7684\u63ed\u5f00\uff0c\u8fd9\u5fc3\u5883\u7684\u786e\u662f\u4eca\u4eba\u51c6\u4ee5\u63cf\u8ff0\uff0c\u53ea\u6709\u601d\u6625\u7684\u6028\u5987\u7b49\u5019\u592b\u5a7f\u5f52\u6765\u7684\u5fc3\u60c5\uff0c \u624d\u53ef\u6bd4\u62df\u4e07\u4e00\u3002 \u3000\u3000\u4ece\u56db\u9762\u6f6e\u6c34\u822c\u6d8c\u6765\u7684\u6b66\u6797\u8c6a\u58eb\uff0c\u4e5f\u8d8a\u6765\u8d8a\u591a\uff0c\u6177\u6168\u591a\u91d1\u7684\u8c6a\u58eb\u4eec\uff0c\u9020\u6210\u4e86\u4e34\u5b89\u57ce\u7578\u5f62\u7684 \u7e41\u534e\uff0c\u57ce\u5f00\u4e0d\u591c\uff0c\u7b19\u6b4c\u5904\u5904\uff0c\u751a\u81f3\u8fde\u90bb\u53bf\u7684\u6398\u91d1\u5a18\u5b50\uff0c\u4e5f\u7a7f\u4e0a\u5979\u4eec\u73cd\u85cf\u7684\u8863\u886b\uff0c\u8d76\u96c6\u4f3c\u7684\u8d76 \u5230\u4e34\u5b89\u57ce\u6765\u3002 \u3000\u3000\u51cc\u6668\uff0c\u9752\u77f3\u677f\u7684\u5927\u8def\uff0c\u4e09\u4e94\u6210\u7fa4\u5730\uff0c\u628a\u81c2\u8d70\u8fc7\u7684\u662f\u9152\u610f\u5c1a\u672a\u5168\u6d88\u7684\u8fdf\u5f52\u4eba\uff0c\u82b1\u8857\u67f3\u5df7\u4e2d \u7684\u5987\u4eba\uff0c\u5934\u4e0a\u4e5f\u591a\u4e86\u4e9b\u91d1\u9970\uff0c\u8fce\u7740\u521d\u5347\u7684\u9633\u5149\uff0c\u4f38\u7740\u5a07\u6175\u7684\u61d2\u8170\uff0c\u5fc3\u4e2d\u5374\u65e9\u5df2\u5c06\u6628\u591c\u7684\u751c\u8a00 \u871c\u8bed\u3001\u5c71\u76df\u6d77\u8a93\u5168\u90e8\u5fd8\u53bb\u3002 \u3000\u3000\u4e00\u9635\u6c89\u91cd\u7684\u811a\u6b65\u58f0\uff0c\u4e00\u58f0\u6c89\u58f0\u7684\u54b3\u55fd\uff0c\u591a\u81c2\u795e\u5251\u4e91\u8c26\u7236\u5b50\uff0c\u7cbe\u795e\u6296\u64de\u5730\u4ece\u5f7b\u591c\u672a\u5173\u7684\u5e97 \u95e8\u4e2d\u5927\u6b65\u8d70\u4e86\u51fa\u6765\uff0c\u76ee\u5149\u56db\u4e0b\u4e00\u626b\uff0c\u6d53\u7709\u5fae\u5fae\u4e00\u76b1\uff0c\u8e0f\u7740\u9752\u77f3\u8def\u4e0a\u7684\u659c\u9633\uff0c\u8d70\u5230\u4ed6\u4eec\u60ef\u53bb\u7684 \u8336\u5c4b\uff0c\u957f\u65e5\u6f2b\u6f2b\uff0c\u5982\u4f55\u6d88\u78e8\uff0c\u786e\u662f\u96be\u4e8b\u3002 \u3000\u3000\u8fdf\u5f52\u7684\u4eba\u867d\u591a\uff0c\u65e9\u8d77\u7684\u4eba\u5374\u4e5f\u6709\u4e0d\u5c11\uff0c\u6c5f\u6e56\u4e2d\u4eba\u4eec\u7684\u4f18\u52a3\u4e0a\u4e0b\uff0c\u5728\u5176\u95f4\u4e00\u76ee\u4fbf\u53ef\u4e86\u7136\uff0c \u591a\u81c2\u795e\u5251\u4e00\u751f\u884c\u8d70\u6c5f\u6e56\uff0c\u4ff1\u662f\u5faa\u89c4\u8e48\u77e9\uff0c\u4ece\u672a\u505a\u8fc7\u8d8a\u8f68\u4e4b\u4e8b\uff0c\u6b64\u523b\u6f2b\u6b65\u800c\u884c\uff0c\u5bf9\u90a3\u80a1\u591c\u884c\u8fdf \u5f52\u4eba\u7684\u70b9\u9996\u5bd2\u55a7\uff0c\u4ff1\u90fd\u53ea\u505a\u672a\u95fb\uff0c\u53ea\u5f53\u672a\u89c1\u3002 \u3000\u3000\u4e00\u4e2a\u4e91\u9b13\u84ec\u4e71\u3001\u8102\u7c89\u5df2\u6b8b\u7684\u5987\u4eba\uff0c\u53f3\u624b\u633d\u7740\u53d1\u9afb\uff0c\u5de6\u624b\u6263\u7740\u53f3\u895f\uff0c\u62d6\u7740\u91d1\u6f06\u6728\u5c65\uff0c\u4ece\u4e00 \u6761\u659c\u5df7\u4e2d\u8e0f\u7740\u788e\u6b65\u884c\u51fa\uff0c\u5306\u5fd9\u5730\u8d70\u4eba\u4e00\u5bb6\u5e03\u5e97\uff0c\u53c8\u5306\u5fd9\u5730\u884c\u53bb\uff0c\u808b\u4e0b\u5374\u5df2\u591a\u4e86\u4e00\u65b9\u4e94\u8272\u9c9c\u8273 \u7684\u82b1\u7ee2\uff0c\u7709\u5f00\u773c\u7b11\u5730\u8dd1\u53e3\u5c0f\u5df7\uff0c\u4e8e\u662f\u5c0f\u5df7\u4e2d\u7684\u9634\u5f71\u4fbf\u53c8\u5c06\u5979\u7684\u6b22\u7b11\u4e0e\u8eab\u5f71\u4e00\u8d77\u541e\u6ca1\uff0c\u751f\u6d3b\u5728 \u9634\u5f71\u4e2d\u7684\u4eba\u4eec\uff0c\u4f3c\u4e4e\u90fd\u6709\u7740\u5c5e\u4e8e\u4ed6\u4eec\u81ea\u5df1\u7684\u6b22\u4e50\uff0c\u56e0\u4e3a\u8fd9\u4e9b\u5815\u843d\u7684\u4eba\u4eec\uff0c\u7075\u9b42\u90fd\u5df2\u88ab\u714e\u71ac\u5f97 \u5168\u7136\u9ebb\u6728\uff0c\u76f4\u5230\u4e00\u5929\uff0c\u5e74\u534e\u65e2\u53bb\uff0c\u5c31\u4e0d\u518d\u6765\uff0c\u4ed6\u4eec\u9ebb\u6728\u7684\u7075\u9b42\uff0c\u624d\u4f1a\u9192\u89c9\uff0c\u53ef\u662f\u2014\u2014\u90a3\u4e0d\u662f \u5df2\u7ecf\u592a\u8fdf\u4e86\u4e48\uff1f \u3000\u3000\u4e91\u8c26\u624b\u634b\u957f\u9aef\uff0c\u6c89\u91cd\u5730\u53f9\u606f\u4e00\u58f0\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u65e5\u540e\u56de\u5230\u829c\u6e56\uff0c\u4f60\u4e0d\u59a8\u53bb\u548c\u90a3\u4e09\u73ed\u5927\u6355\u90ed \u5f00\u6cf0\u5546\u91cf\u4e00\u4e0b\uff0c\u53eb\u4ed6\u5c06\u829c\u6e56\u57ce\u4e2d\u7684\u82b1\u6237\uff0c\u5c3d\u529b\u7ea6\u675f\u4e00\u4e0b\u3002\u201d \u3000\u3000\u4ec1\u4e49\u5251\u5ba2\u4e91\u4e2d\u7a0b\u773c\u89c2\u9f3b\u3001\u9f3b\u89c2\u5fc3\u5730\u8ddf\u5728\u4ed6\u7239\u7239\u8eab\u540e\uff0c\u606d\u8eab\u9053\uff1a\u4e00\u56de\u829c\u6e56\uff0c\u6211\u4fbf\u53bb\u529e\u6b64 \u4e8b\uff0c\u7239\u7239\u53ea\u7ba1\u653e\u5fc3\u597d\u4e86\u3002\u201c\u4e91\u8c26\u5fae\u559d\u4e00\u58f0\uff0c\u53c8\u9053\uff1a\u201c\u81ea\u53e4\u4ee5\u6765\uff0c\u6deb\u4e4b\u4e00\u5b57\uff0c\u4fbf\u4e3a\u4e07\u6076\u4e4b\u9996\uff0c \u4e0d\u77e5\u6d88\u78e8\u4e86\u591a\u5c11\u9752\u5e74\u4eba\u7684\u96c4\u5fc3\u3001\u5927\u4e08\u592b\u7684\u8c6a\u6c14\uff0c\u5f53\u771f\u53ef\u6015\u5f97\u5f88\uff0c\u53ef\u6015\u5f97\u5f88\u3002\u201d \u3000\u3000\u8bdd\u58f0\u987f\u5904\uff0c\u8f6c\u8eab\u8d70\u5165\u8336\u5c4b\uff0c\u5e97\u5c0f\u4e8c\u7684\u6bb7\u52e4\uff0c\u670b\u53cb\u4eec\u7684\u5bd2\u55a7\uff0c\u4f7f\u5f97\u8fd9\u521a\u76f4\u7684\u8001\u4eba\u4e25\u5cfb\u7684\u9762 \u5bb9\u4e0a\uff0c\u9732\u51fa\u4e86\u671d\u9633\u822c\u7684\u7b11\u5bb9\u3002 \u3000\u3000\u8336\u5c4b\u4e2d\u4e00\u7247\u7b11\u58f0\u4eba\u8bed\uff0c\u7b11\u8bed\u4eba\u58f0\u4e2d\uff0c\u7a81\u7136\u6709\u9635\u9635\u53ee\u549a\u58f0\u54cd\uff0c\u81ea\u5c4b\u540e\u4f20\u6765\uff0c\u4e91\u8c26\u6d53\u7709\u4e00 \u76b1\uff0c\u6325\u624b\u53eb\u6765\u5802\u500c\uff0c\u6c89\u58f0\u95ee\u9053\uff1a\u201c\u4f60\u8fd9\u8336\u4e2d\u5c4b\u540e\u623f\u5728\u505a\u4ec0\u4e48\uff0c\u600e\u4e48\u8fd9\u822c\u55a7\u4e71\u3002\u201d \u3000\u3000\u7761\u773c\u60fa\u677e\u7684\u5802\u500c\uff0c\u966a\u4e0a\u4e00\u8138\u804c\u4e1a\u6027\u7684\u7b11\u5bb9\uff0c\u8eac\u8eab\u8bf4\u9053\uff1a\u201c\u56de\u7980\u4f60\u8001\uff0c\u540e\u9762\u4e0d\u662f\u6211\u4eec\u4e00\u5bb6 \u8001\u677f\uff0c\u8bf7\u4f60\u8001\u539f\u8c05\u8fd9\u4e2a\uff01\u201d \u3000\u3000\u4e91\u8c26\u201c\u54e6\u201d\u4e86\u4e00\u58f0\uff0c\u5374\u53c8\u5947\u9053\uff1a\u201c\u540e\u9762\u8fd9\u5bb6\u5e97\u94fa\uff0c\u5374\u53c8\u4f5c\u4f55\u8425\u751f\uff0c\u600e\u5730\u6e05\u6668\u8fd9\u822c\u5fd9 \u788c\uff1f\u201d \u3000\u3000\u5802\u500c\u4f38\u624b\u6307\u7740\u5634\u5507\uff0c\u538b\u4e0b\u4e86\u4e00\u4e2a\u5c06\u8981\u53d1\u51fa\u7684\u5475\u6b20\uff0c\u56db\u987e\u4e00\u773c\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u56de\u7980\u4f60\u8001\uff0c\u9694 \u58c1\u8fd9\u5bb6\u5e97\u505a\u7684\u53ef\u662f\u4e27\u6c14\u751f\u610f\uff0c\u4e13\u505a\u68fa\u6750\u3002\u201d \u3000\u3000\u591a\u81c2\u795e\u5251\u6d53\u7709\u4e00\u8f69\uff0c\u5374\u542c\u8fd9\u5802\u500c\u63a5\u7740\u53c8\u9053\uff1a\u201c\u4ed6\u4eec\u8fd9\u5bb6\u5e97\u672c\u6765\u751f\u610f\u6e05\u6de1\u5f97\u5f88\uff0c\u53ef\u662f\u8fd1\u4e9b \u65e5\u5b50\u6765\u53ef\u771f\u7b97\u53d1\u4e86\u8d22\u5566\uff0c\u4e0d\u4f46\u5b58\u8d27\u5168\u90e8\u5356\u5149\uff0c\u65b0\u8d27\u66f4\u662f\u65e5\u65e5\u591c\u591c\u5730\u8d76\u7740\u505a\uff0c\u524d\u9762\u4e09\u5bb6\u90a3\u95f4\u672c \u662f\u505a\u6728\u5668\u751f\u610f\u7684\uff0c\u770b\u7740\u773c\u7ea2\uff0c\u524d\u5929\u4e5f\u6539\u884c\u505a\u8d77\u68fa\u6750\u6765\u4e86\uff0c\u6211\u53ea\u6015\u4ed6\u4eec\u505a\u7684\u5927\u591a\u4e86\u5356\u4e0d\u51fa\u53bb\uff0c \u4ed6\u4eec\u5374\u8bf4\u518d\u8fc7\u4e09\u56db\u5929\uff0c\u751f\u610f\u53ea\u4f1a\u8d8a\u6765\u8d8a\u597d\uff0c\u4f60\u8bf4\u8fd9\u4e9b\u4eba\u53ef\u6068\u4e0d\u53ef\u6068\uff0c\u53ea\u5df4\u671b\u8fdc\u5904\u5230\u8fd9\u91cc\u6765\u7684 \u4eba\uff0c\u90fd\u2026\u2026\u90fd\u2026\u2026\u90fd\u2026\u2026\u201d \u3000\u3000\u4ed6\u5520\u5520\u53e8\u53e8\u4ed6\u8bf4\u5230\u8fd9\u91cc\uff0c\u7a81\u542c\u4e91\u8c26\u51b7\u54fc\u4e00\u58f0\uff0c\u76ee\u5149\u95ea\u7535\u822c\u5411\u4ed6\u4e00\u626b\u3002 \u3000\u3000\u4ed6\u5413\u5f97\u53e3\u4e2d\u4e00\u8fde\u8bf4\u4e86\u4e09\u4e2a\u201c\u90fd\u201d\u5b57\uff0c\u4f38\u624b\u4e00\u63a9\u5634\u5507\uff0c\u53ea\u89c1\u8fd9\u8001\u4eba\u5229\u5251\u822c\u7684\u76ee\u5149\uff0c\u4ecd\u5728\u671b \u7740\u81ea\u5df1\uff0c\u76f4\u5230\u53e6\u6709\u5ba2\u4eba\u8fdb\u6765\uff0c\u4ed6\u624d\u5982\u9022\u5927\u8d66\u822c\u5927\u559d\u4e00\u58f0\uff1a\u201c\u5ba2\u6765\uff01\u201d \u3000\u3000\u4e00\u65f6\u4e4b\u95f4\uff0c\u4e91\u8c26\u53ea\u89c9\u90a3\u53ee\u54af\u4e4b\u58f0\u9707\u8033\u800c\u6765\uff0c\u8d8a\u6765\u8d8a\u54cd\uff0c\u4f3c\u4e4e\u5c06\u56db\u4e0b\u7684\u4eba\u58f0\u7b11\u8bed\uff0c\u4ff1\u90fd\u4e00 \u8d77\u6df9\u6ca1\u3002 \u3000\u3000\u76f4\u5230\u4e91\u4e2d\u7a0b\u89c1\u4ed6\u7239\u7239\u7684\u795e\u6001\uff0c\u731c\u5230\u4e86\u7239\u7239\u7684\u5fc3\u4e8b\uff0c\u5e72\u54b3\u4e00\u4e8b\uff0c\u4e71\u4ee5\u4ed6\u8bed\uff0c\u591a\u81c2\u795e\u5251\u4e91\u8c26 \u65b9\u4ece\u6c89\u601d\u4e2d\u9192\u6765\u3002 \u3000\u3000\u8336\u5c45\u517c\u552e\u65e9\u81b3\uff0c\u672c\u662f\u6c5f\u5357\u4e00\u5e26\u901a\u5e38\u98ce\u6c14\uff0c\u4f46\u4e91\u8c26\u4eca\u65e5\u5fc3\u4e8b\u91cd\u91cd\uff0c\u54ea\u6709\u5fc3\u60c5\u6765\u7528\u65e9\u70b9\uff0c\u65b9 \u81ea\u7565\u4e3a\u52a8\u4e86\u51e0\u8457\uff0c\u7a81\u5730\u4e00\u9635\u5947\u5f02\u7684\u8bed\u58f0\uff0c\u81ea\u5e97\u5916\u4f20\u5165\uff0c\u63a5\u7740\u8d70\u4eba\u4e09\u4e2a\u5947\u88c5\u5f02\u670d\u3001\u53c8\u77ee\u53c8\u80d6\u7684 \u4eba\u6765\u3002 \u3000\u3000\u53ea\u89c1\u8fd9\u4e09\u4eba\u9ad8\u77ee\u5982\u4e00\uff0c\u80a5\u7626\u76f8\u540c\uff0c\u8eab\u4e0a\u7684\u88c5\u675f\u6253\u626e\uff0c\u7ade\u4e5f\u662f\u5b8c\u5168\u4e00\u6a21\u4e00\u6837\uff0c\u4ff1\u90fd\u7a7f\u7740\u4e00 \u88ad\u5947\u8272\u6591\u70c2\u7684\u5f69\u8863\uff0c\u65e5\u5f71\u4e4b\u4e0b\uff0c\u95ea\u95ea\u751f\u5149\uff0c\u8170\u7554\u659c\u4f69\u4e00\u53e3\u957f\u5251\uff0c\u5251\u9798\u6ee1\u7f00\u73e0\u5b9d\uff0c\u886c\u7740\u4ed6\u4eec\u7684 \u5947\u88c5\u5f02\u670d\uff0c\u66f4\u89c9\u7eda\u5947\u8be1\u5f02\u3001\u65e0\u4e0e\u4f26\u6bd4\u3002 \u3000\u3000\u8fd9\u4e09\u4eba\u6602\u9996\u9614\u6b65\u7684\u884c\u4eba\u5e97\u4e2d\uff0c\u7acb\u523b\u5438\u5f15\u4e86\u5e97\u4e2d\u6240\u6709\u4eba\u4eec\u7684\u76ee\u5149\u3002 \u3000\u3000\u5e97\u4f19\u65e2\u60ca\u4e14\u602a\u53c8\u6015\uff0c\u5374\u53c8\u4e0d\u5f97\u4e0d\u4e0a\u524d\u62db\u547c\uff0c\u54ea\u77e5\u8fd9\u4e09\u4eba\u4e0d\u4f46\u88c5\u675f\u5947\u602a\uff0c\u6240\u64cd\u8a00\u8bed\uff0c\u66f4\u662f \u4ee4\u4eba\u96be\u61c2\uff0c\u51e0\u8bb8\u5468\u6298\uff0c\u5e97\u4f19\u65b9\u9001\u4e0a\u98df\u7269\uff0c\u8fd9\u4e09\u4eba\u5927\u5403\u5927\u559d\uff0c\u7b95\u8e1e\u800c\u5750\uff0c\u7adf\u5c06\u65c1\u4eba\u4ff1\u90fd\u6ca1\u6709\u653e \u5728\u773c\u4e2d\u3002 \u3000\u3000\u591a\u81c2\u795e\u5251\u58ee\u5c81\u65f6\u8d70\u5357\u95ef\u5317\uff0c\u904d\u6e38\u5929\u4e0b\uff0c\u5357\u5317\u65b9\u8a00\uff0c\u867d\u4e0d\u751a\u7cbe\uff0c\u5374\u90fd\u80fd\u901a\uff0c\u6b64\u523b\u4e0e\u4ed6\u7231\u5b50 \u5bf9\u671b\u4e00\u773c\uff0c\u5fc3\u4e2d\u5df2\u6709\u51e0\u5206\u731c\u5230\u8fd9\u4e09\u4eba\u7684\u6765\u8def\u3002 \u3000\u3000\u53ea\u89c1\u9762\u8857\u800c\u5750\u7684\u4e00\u4eba\uff0c\u4e00\u7b77\u5939\u4e0a\u4e00\u76c6\u5e72\u4e1d\uff0c\u9f50\u5730\u5377\u5230\u53e3\u4e2d\uff0c\u5480\u56bc\u51e0\u4e0b\uff0c\u7a81\u7136\u4e00\u62cd\u684c\u5b50\uff0c \u5927\u58f0\u9053\uff1a\u201c\u65f6\u54c0\u9b3c\u5f04\u4eba\uff0c\u6211\u505a\u597d\u649e\u4e0d\u649e\uff0c\u70b9\u4f1a\u649e\u505a\u6761\u8fb0\u91ce\u9753\u4ed4\uff0c\u6b66\u529f\u537f\u4e48\u4f7f\u5f97\uff0c\u6664\u7cfb\u6211\u89c1\u673a \u5f97\u5feb\u5440\uff0c\u6211\u628a\u5251\u65e9\u5c31\u6664\u77e5\u98de\u53bb\u8fb9\u5ea6\u5566\uff01\u201d \u3000\u3000\u4ed6\u8bf4\u8bdd\u7684\u8bed\u58f0\u867d\u5927\uff0c\u56db\u5ea7\u4e4b\u4eba\u9762\u9762\u76f8\u89d1\uff0c\u9664\u4e86\u591a\u81c2\u795e\u5251\u4e4b\u5916\uff0c\u5374\u518d\u65e0\u4e00\u4eba\u80fd\u591f\u542c\u61c2\u3002 \u3000\u3000\u4e91\u8c26\u6d53\u7709\u5fae\u76b1\uff0c\u4f4e\u8bed\u9053\uff1a\u201c\u6b64\u4eba\u4f3c\u662f\u6765\u81ea\u6d77\u5357\u4e00\u5e26\uff0c\u8bf4\u662f\u9047\u89c1\u4e00\u4e2a\u5c11\u5e74\uff0c\u6b66\u529f\u7edd\u9ad8\uff0c\u82e5 \u975e\u4ed6\u80fd\u968f\u673a\u5e94\u53d8\uff0c\u638c\u4e2d\u957f\u5251\u90fd\u8981\u88ab\u90a3\u5c11\u5e74\u9707\u98de\uff01\u201d \u3000\u3000\u8bed\u58f0\u5fae\u987f\uff0c\u76ee\u5149\u4e00\u8f6c\uff0c\u53c8\u81ea\u5947\u9053\uff1a\u201c\u8fd9\u4e09\u4eba\u770b\u6765\u6b66\u529f\u4e0d\u5f31\uff0c\u5374\u4e0d\u77e5\u90a3\u5c11\u5e74\u662f\u8c01\uff1f\u96be \u9053\u2026\u2026\u201d\u606c\u72b9\u672a\u4e86\uff0c\u5374\u542c\u53e6\u4e00\u4eba\u5df2\u81ea\u63a5\u9053\uff1a\u201c\u7ec6\u4f6c\uff0c\u54aa\u5435\u5f97\u683c\u4e48\u5df4\u95ed\u597d\u5417\uff1f\u4eba\u683c\u4e48\u591a\uff0c\u5435\u751f \u7ec6\u4f5c\u5305\u91ce\uff1f\u201d \u3000\u3000\u4e91\u4e2d\u7a0b\u76ee\u5149\u4e2d\u6ee1\u542b\u8be2\u95ee\u4e4b\u610f\uff0c\u5411\u4ed6\u7239\u7239\u671b\u4e86\u4e00\u773c\uff0c\u4e91\u8c26\u542b\u7b11\u4f4e\u8bed\u9053\uff1a\u201c\u4eba\u591a\u8033\u6742\uff0c\u6b64\u4eba \u53eb\u4ed6\u5144\u5f1f\u4e0d\u8981\u4e71\u5435\u3002\u201d \u3000\u3000\u53ea\u542c\u7b2c\u4e09\u4eba\u9053\uff1a\u201c\u5927\u4f6c\uff0c\u6211\u542c\u5de8\u81ea\u62a5\u59d3\u540d\uff0c\u6664\u77e5\u7cfb\u6664\u7cfb\u505a\u5353\u957f\u537f\uff0c\u778e\uff0c\u6ce5\u6761\u91ce\u5e74\u7eaa\u8f7b \u8f7b\uff0c\u53c8\u6709\u58f0\u540d\uff0c\u70b9\u89e3\u6b66\u529f\u56bc\u4e48\u7280\u5229\u5440\uff1f\u201d \u3000\u3000\u4e91\u8c26\u6d53\u7709\u4e00\u626c\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u6b64\u4e09\u4eba\u6240\u9047\u5c11\u5e74\uff0c\u679c\u7136\u4fbf\u662f\u957f\u537f\u8d24\u4f84\uff0c\u4e0d\u77e5\u4ed6\u6b64\u523b\u5728\u54ea \u91cc\u3002\u201d \u3000\u3000\u53ea\u542c\u6700\u5148\u53d1\u8bdd\u4e4b\u4eba\u7a81\u5730\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6b66\u529f\u7280\u5229\u53c8\u6709\u4ebf\u7528\uff0c\u4e00\u9635\u95f4\u5de8\u5982\u679c\u649e\u7740\u5c71\u4e0a\u7684 \u5404\u73ed\u53cb\u4ed4\uff0c\u6664\u7cfb\u4e00\u6837\u8981\u5012\u9709\uff0c\u53ea\u6015\u8fde\u5c38\u9aa8\u90fd\u672a\u6709\u4eba\u6536\u5462\uff01\u201d \u3000\u3000\u4e91\u4e2d\u7a0b\u89c1\u5230\u8fd9\u4e09\u4eba\u5947\u5f02\u7684\u5f62\u72b6\uff0c\u542c\u5230\u8fd9\u4e09\u4eba\u5947\u5f02\u7684\u8a00\u8bed\uff0c\u5fc3\u4e2d\u4e0d\u7531\u81ea\u4e3b\u5730\u5927\u751f\u597d\u5947\u4e4b \u5fc3\uff0c\u65b9\u5f85\u518d\u95ee\u4ed6\u7239\u7239\u8fd9\u4e09\u4eba\u6b64\u523b\u6240\u8bf4\u4e4b\u8bed\u662f\u4f55\u610f\u601d\uff0c\u54ea\u77e5\u4e91\u8c26\u7a81\u5730\u4f4e\u53f1\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u8d70\uff01\u201d \u5306\u5306\u629b\u4e0b\u4e00\u952d\u788e\u94f6\uff0c\u957f\u8eab\u79bb\u684c\u800c\u53bb\u3002 \u3000\u3000\u4e91\u4e2d\u7a0b\u65e2\u60ca\u53c8\u5947\uff0c\u6115\u4e86\u4e00\u6115\uff0c\u8ddf\u5728\u4e91\u8c26\u8eab\u540e\uff0c\u5954\u51fa\u5e97\u5916\u3002 \u3000\u3000\u53ea\u89c1\u4e91\u8c26\u94f6\u987b\u98d8\u52a8\uff0c\u5927\u6b65\u800c\u884c\uff0c\u4e09\u811a\u4e24\u6b65\uff0c\u8d70\u5230\u8857\u53e3\uff0c\u4e00\u811a\u8de8\u4e0a\u4e00\u8f86\u505c\u5728\u8857\u8fb9\u7684\u9a6c\u8f66\uff0c \u8fde\u53f1\u5feb\u8d70\u3002 \u3000\u3000\u9a6c\u8f66\u592b\u4ea6\u662f\u60ca\u5947\u4ea4\u96c6\uff0c\u4e91\u8c26\u53c8\u81ea\u638f\u51fa\u4e00\u952d\u94f6\u5b50\uff0c\u585e\u5728\u4ed6\u638c\u4e0a\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u5929\u76ee\u5c71\u53bb\uff01\u201d \u3000\u3000\u74a8\u8000\u7684\u767d\u94f6\uff0c\u5c01\u4f4f\u4e86\u9a6c\u8f66\u592b\u7684\u5634\uff0c\u4e5f\u538b\u4e0b\u4e86\u4ed6\u7684\u60ca\u5947\u4e4b\u5fc3\uff0c\u7b49\u5230\u4e91\u4e2d\u7a0b\u8d76\u5230\u8f66\u4e0a\uff0c\u8f66\u9a6c \u5df2\u81ea\u542f\u884c\uff0c\u7247\u523b\u4fbf\u9a76\u51fa\u57ce\u5916\u3002 \u3000\u3000\u4e91\u4e2d\u7a0b\u4fa7\u76ee\u671b\u53bb\uff0c\u53ea\u89c1\u4ed6\u7239\u7239\u9762\u8272\u51dd\u91cd\uff0c\u6d53\u7709\u6df1\u76b1\uff0c\u5fc3\u4e2d\u7eb3\u95f7\u4e86\u534a\u664c\uff0c\u7ec8\u4e8e\u5fcd\u4e0d\u4f4f\u95ee \u9053\uff1a\u201c\u65b9\u624d\u90a3\u4eba\u8bf4\u7684\u7a76\u7adf\u662f\u4ec0\u4e48\uff1f\u600e\u4f1a\u4ee4\u7239\u7239\u5982\u6b64\u60ca\u614c\uff1f\u201d \u3000\u3000\u4e91\u8c26\u957f\u53f9\u4e00\u58f0\u9053\uff1a\u201c\u4f60\u957f\u537f\u5f1f\u5b64\u8eab\u95ef\u5165\u864e\u7a74\uff0c\u53ea\u6015\u6709\u9669\uff0c\u5509\uff0c\u5353\u5927\u54e5\u5bf9\u6211\u6069\u6df1\u5982\u6d77\uff0c\u6211 \u82e5\u4e0d\u80fd\u4e3a\u4ed6\u4fdd\u5168\u540e\u4ee3\uff0c\u7109\u6709\u989c\u9762\u89c1\u6545\u4eba\u4e8e\u5730\u4e0b\u3002\u201d \u3000\u3000\u4e91\u4e2d\u7a0b\u5251\u7709\u76b1\u5904\uff0c\u4e0d\u518d\u8a00\u8bed\uff0c\u53ea\u542c\u8f66\u58f0\u8f98\u8f98\uff0c\u8e44\u58f0\u5f97\u5f97\uff0c\u8f66\u9a6c\u6512\u884c\u751a\u6025\uff0c\u4e91\u4e2d\u7a0b\u867d\u5df2\u6210 \u5bb6\u7acb\u4e1a\uff0c\u4e14\u5df2\u540d\u52a8\u6c5f\u6e56\uff0c\u4f46\u5728\u4e25\u7236\u4e4b\u524d\uff0c\u5374\u4ecd\u4e0d\u6562\u591a\u8a00\uff0c\u63a2\u9996\u81ea\u8f66\u7a97\u5916\u671b\uff0c\u7a81\u7136\u60ca\u5524\u4e00\u58f0\uff0c \u8131\u53e3\u9053\uff1a\u201c\u5149\u5929\u5316\u65e5\u4e4b\u4e0b\uff0c\u600e\u5730\u6709\u5982\u6b64\u591a\u591c\u884c\u4eba\u5728\u9053\u8def\u4e4b\u4e0a\u884c\u8d70\uff1f\u201d \u3000\u3000\u4e91\u8c26\u76ee\u5149\u52a8\u5904\uff0c\u53ea\u89c1\u6570\u5341\u4e2a\u9ed1\u8863\u52b2\u88c5\u6ee1\u8eab\u591c\u884c\u8863\u670d\u7684\u5927\u6c49\uff0c\u6cbf\u7740\u5b98\u9053\u4e4b\u65c1\uff0c\u4e00\u4e2a\u63a5\u7740\u4e00 \u4e2a\uff0c\u9ed8\u7136\u800c\u884c\uff0c\u9762\u4e0a\u65e2\u4e0d\u5feb\u4e50\uff0c\u4e5f\u4e0d\u5fe7\u90c1\uff0c\u4e0d\u7981\u5fae\u76b1\u6d53\u7709\uff0c\u8be7\u58f0\u8bf4\u9053\uff1a\u201c\u8fd9\u4e9b\u6c49\u5b50\u5b9a\u662f\u67d0\u4e00 \u5e2e\u6d3e\u95e8\u4e0b\u2026\u2026\u201d \u3000\u3000\u8f66\u884c\u751a\u6025\uff0c\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u5df2\u5c06\u5730\u4e00\u884c\u957f\u8fbe\u5341\u6570\u4e08\u7684\u884c\u5217\u8d70\u8fc7\uff0c\u7a81\u5730\u77a5\u89c1\u884c\u5217\u4e4b\u5c3e\uff0c\u4e00\u67b6\u677e \u6728\u67b6\u6210\u7684\u642d\u5e8a\u4e4b\u4e0a\uff0c\u50f5\u5367\u7740\u4e00\u4e2a\u5e72\u62d3\u7626\u5c0f\u7684\u9ed1\u8863\u4eba\uff0c\u9762\u76ee\u4f9d\u7a00\u671b\u6765\uff0c\u7adf\u4f3c\u4e54\u8fc1\uff0c\u4e0d\u7981\u5931\u58f0 \u9053\uff1a\u201c\u4e54\u8fc1\uff01\u201d \u3000\u3000\u4f38\u624b\u4e00\u63a8\u8f66\u95e8\uff0c\u5237\u5730\u63a0\u4e0b\u8f66\u53bb\uff0c\u4e91\u4e2d\u7a0b\u4f4e\u53f1\u4e00\u58f0\uff1a\u201c\u505c\u8f66\uff01\u201d \u3000\u3000\u968f\u4e4b\u63a0\u4e0b\u3002 \u3000\u3000\u4e91\u8c26\u5fae\u4e00\u8d77\u843d\uff0c\u4fbf\u5df2\u8ffd\u53ca\u62ac\u5e8a\u800c\u884c\u7684\u5927\u6c49\uff0c\u53e3\u4e2d\u5389\u53f1\u4e00\u58f0\uff0c\u4e00\u628a\u626f\u7740\u4ed6\u7684\u540e\u895f\uff0c\u90a3\u5927\u6c49 \u5927\u60ca\u4e4b\u4e0b\uff0c\u8f6c\u9996\u559d\u9053\uff1a\u201c\u670b\u53cb\uff0c\u4f60\u8fd9\u662f\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4e91\u8c26\u4ece\u6765\u8840\u6027\u8fc7\u4eba\uff0c\u4e00\u751f\u884c\u4e8b\uff0c\u4ff1\u90fd\u7a0d\u5acc\u83bd\u649e\uff0c\u4e34\u5230\u8001\u6765\u3002 \u3000\u3000\u5374\u662f\u59dc\u6842\u4e4b\u6027\uff0c\u8001\u800c\u5f25\u8fa3\uff0c\u6b64\u523b\u4e00\u773c\u77a5\u89c1\u4e54\u8fc1\u800c\u5168\u8eab\u50f5\u6728\uff0c\u9762\u5982\u91d1\u7eb8\uff0c\u4f3c\u662f\u53d7\u4e86\u6781\u91cd\u7684 \u5185\u4f24\uff0c\u5fc3\u4e2d\u4f46\u89c9\u4e00\u80a1\u6012\u6c14\u4e0a\u6d8c\uff0c\u5389\u53f1\u9053\uff1a\u201c\u8c01\u662f\u4f60\u7684\u670b\u53cb\uff01\u201d \u3000\u3000\u624b\u8155\u4e00\u6296\uff0c\u90a3\u5927\u6c49\u867d\u7136\u8eab\u5f3a\u529b\u58ee\uff0c\u5374\u600e\u7981\u5f97\u8d77\u8fd9\u822c\u6b66\u6797\u9ad8\u624b\u6e29\u6012\u4e4b\u4e0b\u7684\u8155\u529b\uff0c\u624b\u8155\u4e00 \u677e\uff0c\u60ca\u547c\u4e86\u4e00\u58f0\uff0c\u4ef0\u5929\u5012\u4e0b\u3002 \u3000\u3000\u8fd9\u4e00\u58f0\u60ca\u547c\uff0c\u7acb\u523b\u7531\u884c\u5217\u4e4b\u5c3e\uff0c\u4f20\u5230\u884c\u5217\u4e4b\u5934\uff0c\u90a3\u5927\u6c49\u867d\u5df2\u4ef0\u5929\u8dcc\u5012\uff0c\u4f46\u5374\u672a\u53d7\u4f24\uff0c\u53cc \u65f6\u4e00\u633a\uff0c\u633a\u8170\u7acb\u8d77\uff0c\u6012\u76ee\u5706\u7741\uff0c\u547c\u7136\u4e00\u638c\uff0c\u5411\u4e91\u8c26\u9762\u95e8\u51fb\u53bb\uff0c\u4f46\u62f3\u5230\u4e2d\u9014\uff0c\u8033\u7554\u53ea\u542c\u4e00\u58f0\u5389 \u53f1\uff1a\u201c\u9f20\u8f88\u4f60\u6562\uff01\u201d \u3000\u3000\u808b\u4e0b\u7a81\u5730\u4e00\u9ebb\uff0c\u5168\u8eab\u529b\u6c14\uff0c\u4ff1\u90fd\u6d88\u5931\u65e0\u5f71\uff0c\u7adf\u53c8\u6251\u5730\u8dcc\u5012\u3002 \u3000\u3000\u672c\u81ea\u6709\u5982\u957f\u86c7\u822c\u7684\u4e00\u6761\u884c\u5217\uff0c\u5217\u9996\u5df2\u5411\u540e\u56f4\u4e86\u56de\u6765\uff0c\u5239\u90a3\u4e4b\u95f4\uff0c\u4fbf\u5df2\u5c06\u4e91\u6c0f\u7236\u5b50\u56f4\u5728\u6838 \u5fc3\uff0c\u4e91\u8c26\u6c89\u58f0\u9053\uff1a\u201c\u4e2d\u7a0b\uff0c\u4f60\u4e14\u5148\u770b\u770b\u4e54\u5927\u54e5\u7684\u4f24\u52bf\u3002\u201d \u3000\u3000\u7a81\u7136\u8f6c\u8fc7\u8eab\u6765\uff0c\u5389\u53f1\uff1a\u201c\u4f60\u7b49\u662f\u4f55\u4eba\u95e8\u4e0b\uff1f\u201d \u3000\u3000\u8fd9\u4e00\u58f0\u5389\u53f1\uff0c\u76f4\u9707\u5f97\u4f17\u4eba\u8033\u9f13\u55e1\u55e1\u4f5c\u54cd\uff0c\u56f4\u5728\u56db\u5468\u7684\u6570\u5341\u4e2a\u9ed1\u8863\u5927\u6c49\uff0c\u7adf\u90fd\u88ab\u4ed6\u7684\u6c14\u5ea6 \u6240\u6151\uff0c\u518d\u65e0\u4e00\u4eba\u6562\u8e0f\u524d\u4e00\u6b65\u3002 \u3000\u3000\u591a\u81c2\u795e\u5251\u53cc\u81c2\u659c\u5206\uff0c\u53cc\u62f3\u7d27\u63e1\uff0c\u76ee\u5149\u5982\u7535\uff0c\u987b\u53d1\u7686\u5f20\uff0c\u7765\u7768\u56db\u987e\u4e00\u773c\uff0c\u5fc3\u4e2d\u8c6a\u60c5\u987f\u751f\uff0c \u4f3c\u4e4e\u53c8\u56de\u590d\u5230\u591a\u5e74\u524d\u53f1\u54a4\u6c5f\u6e56\u7684\u60c5\u51b5\uff0c\u8981\u77e5\u4e91\u8c26\u8fd1\u5e74\u867d\u5df2\u95ed\u95e8\u5bb6\u5c45\uff0c\u4f46\u6b66\u529f\u5374\u672a\u5c1d\u4e00\u65e5\u629b \u4e0b\uff0c\u6b63\u662f\u8001\u9aa5\u4f0f\u67a5\uff0c\u5176\u5fd7\u4ecd\u5728\u5343\u91cc\uff0c\u6b64\u523b\u89c1\u5230\u8fd9\u822c\u6c49\u5b50\u7684\u754f\u7f29\u4e4b\u6001\uff0c\u5fc6\u53ca\u81ea\u5df1\u5f53\u5e74\u7684\u82f1\u98ce\u8c6a \u8ff9\uff0c\u4e0d\u7981\u7eb5\u58f0\u72c2\u7b11\u8d77\u6765\u3002 \u3000\u3000\u7a81\u89c1\u9ed1\u8863\u6c49\u5b50\u4e1b\u4e2d\u633a\u80f8\u8d70\u51fa\u4e00\u6761\u5927\u6c49\uff0c\u4e91\u8c26\u7b11\u58f0\u500f\u987f\uff0c\u76ee\u5149\u4e00\u51db\uff0c\u5411\u524d\u8fde\u8e0f\u4e09\u6b65\uff0c\u5389\u58f0 \u9053\uff1a\u201c\u4f60\u7b49\u662f\u4f55\u4eba\u95e8\u4e0b\uff0c\u96be\u9053\u8fde\u8001\u592b\u90fd\u4e0d\u8ba4\u8bc6\u4e48\uff1f\u201d \u3000\u3000\u76ee\u5149\u4e00\u8f6c\uff0c\u4e0d\u7b49\u90a3\u6c49\u5b50\u63a5\u53e3\uff0c\u53c8\u9053\uff1a\u201c\u4e54\u8fc1\u8eab\u4e2d\u4f55\u4f24\uff0c\u88ab\u4f55\u8bb8\u4eba\u6240\u4f24\uff0c\u5feb\u4e9b\u636e\u5b9e\u8bf4\u6765\uff0c \u5426\u5219\u2026\u2026\u54fc\u54fc\uff01\u201d \u3000\u3000\u5426\u5219\u4e24\u5b57\u51fa\u53e3\u4e4b\u540e\uff0c\u4ed6\u53ea\u89c9\u4e0b\u9762\u4e4b\u8a00\u8bed\uff0c\u82e5\u662f\u8bf4\u5f97\u592a\u8fc7\u72e0\u8fa3\uff0c\u4fbf\u5931\u4e86\u8eab\u4efd\uff0c\u82e5\u662f\u8bf4\u5f97\u592a \u8fc7\u5e73\u5e38\uff0c\u53c8\u4e0d\u8db3\u4ee5\u4ee4\u4eba\u6151\u6015\uff0c\u5fc3\u5ff5\u6570\u8f6c\uff0c\u53ea\u5f97\u4ee5\u4e24\u58f0\u51b7\u54fc\u7ed3\u675f\u4e86\u81ea\u5df1\u7684\u8bdd\u3002 \u3000\u3000\u54ea\u77e5\u90a3\u6c49\u5b50\u8eab\u8eaf\u633a\u5f97\u7b14\u76f4\uff0c\u5fae\u5fae\u62b1\u62f3\u4e00\u793c\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u5728\u4e0b\u5510\u4e49\uff0c\u8001\u524d\u8f88\u9ad8\u59d3\u5927\u540d\uff0c \u5728\u4e0b\u4e0d\u6562\u52a8\u95ee\uff0c\u4f46\u60f3\u8bf7\u95ee\u4e00\u53e5\u8001\u524d\u8f88\u4e0e\u8fd9\u4e54\u8fc1\u7a76\u7adf\u6709\u4f55\u5173\u7cfb\uff1f\u201d \u3000\u3000\u591a\u81c2\u795e\u5251\u6d53\u7709\u4e00\u8f69\uff0c\u6c89\u58f0\u559d\u9053\uff1a\u201c\u4e54\u8fc1\u4e43\u4ee5\u7236\u6267\u8f88\u5c0a\u6211\uff0c\u8001\u592b\u4ea6\u4ee5\u5b50\u4f84\u822c\u7167\u987e\u4ed6\uff0c\u4e54\u8fc1 \u6b64\u756a\u8eab\u53d7\u91cd\u4f24\u2026\u2026\u201d \u3000\u3000\u5510\u4e49\u7a81\u7136\u60ca\u547c\u4e00\u58f0\uff0c\u63a5\u53e3\u8bf4\u9053\uff1a\u201c\u8001\u524d\u8f88\u53ef\u662f\u4eba\u79f0\u591a\u81c2\u795e\u5251\u7684\u4e91\u5927\u4fa0\uff1f\u201d \u3000\u3000\u4e91\u8c26\u53cd\u800c\u4e00\u5446\uff0c\u6c89\u541f\u534a\u664c\uff0c\u65b9\u9053\uff1a\u201c\u4f60\u600e\u4f1a\u8ba4\u5f97\u8001\u592b\uff1f\u201d \u3000\u3000\u5510\u4e49\u8083\u7136\u9053\uff1a\u201c\u829c\u6e56\u4e91\u95e8\uff0c\u7236\u5b50\u53cc\u4fa0\uff0c\u540d\u6ee1\u5929\u4e0b\uff0c\u5728\u4e0b\u867d\u7136\u611a\u6627\uff0c\u4f46\u89c1\u4e86\u8001\u524d\u8f88\u7684\u795e \u6001\uff0c\u542c\u4e86\u8001\u524d\u8f88\u7684\u8a00\u8bed\uff0c\u4ea6\u53ef\u731c\u51fa\u51e0\u5206\u3002\u201d \u3000\u3000\u4e91\u8c26\u9f3b\u4e2d\u201c\u55ef\u201d\u4e86\u4e00\u58f0\uff0c\u7a81\u53c9\u95ee\u9053\uff1a\u201c\u4f60\u662f\u4f55\u4eba\u95e8\u4e0b\uff0c\u4f60\u53eb\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5510\u4e49\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u591a\u81c2\u795e\u5251\u5f53\u771f\u8001\u4e86\uff0c\u6211\u65b9\u624d\u81ea\u62a5\u59d3\u540d\uff0c\u4ed6\u6b64\u523b\u5374\u5df2\u5fd8\u8bb0\u3002\u201d \u3000\u3000\u4f46\u53e3\u4e2d\u5374\u8083\u7136\u9053\uff1a\u201c\u5728\u4e0b\u5510\u4e49\uff0c\u4e43\u8700\u4e2d\u5510\u6c0f\u95e8\u4eba\uff01\u201d \u3000\u3000\u4e91\u8c26\u6d53\u7709\u4e00\u9635\u8038\u52a8\uff0c\u8be7\u7136\u9053\uff1a\u201c\u8700\u4e2d\u5510\u95e8\uff1f\u4f60\u4fbf\u662f\u5510\u4e09\u73af\u95e8\u4e0b\uff1f\u201d \u3000\u3000\u8bed\u58f0\u5fae\u987f\uff0c\u76b1\u7709\u53c8\u9053\uff1a\u201c\u636e\u8001\u592b\u6240\u77e5\uff0c\u4e54\u8fc1\u4e0e\u8700\u4e2d\u5510\u95e8\u6beb\u65e0\u74dc\u845b\uff0c\u600e\u4f1a\u91cd\u4f24\u5728\u4f60\u7b49\u624b \u4e0b\uff1f\u201d\u5510\u4e49\u4fef\u9996\u6c89\u541f\u534a\u664c\uff0c\u7a81\u7136\u4ef0\u9996\u9053\uff1a\u201c\u8001\u524d\u8f88\u4fa0\u4e49\u4e3a\u6000\uff0c\u6bcf\u4ee5\u541b\u5b50\u4e4b\u5fc3\u5ea6\u5c0f\u4eba\u4e4b\u8179\uff0c\u662f \u4ee5\u5bf9\u4e54\u8fc1\u4e4b\u4e3a\u4eba\u6216\u5c1a\u4e0d\u751a\u4e86\u7136\u3002\u201d \u3000\u3000\u4e91\u8c26\u51b7\u54fc\u4e00\u58f0\u9053\uff1a\u201c\u8bf4\u4e0b\u53bb\uff01\u201d \u3000\u3000\u5510\u4e49\u53c8\u81ea\u6c89\u541f\u534a\u664c\uff0c\u65b9\u9053\uff1a\u201c\u82e5\u662f\u522b\u4eba\u76f8\u95ee\u5728\u4e0b\uff0c\u5728\u4e0b\u4e5f\u8bb8\u4e0d\u4f1a\u8bf4\u51fa\u5b9e\u60c5\uff0c\u4f46\u8001\u524d\u8f88\u4fa0 \u4e49\u4e4b\u540d\uff0c\u540d\u6ee1\u5929\u4e0b\uff0c\u5728\u4e0b\u56e0\u5df2\u4ef0\u6155\u5df2\u4e45\uff0c\u662f\u4ee5\u665a\u8f88\u624d\u80af\u8bf4\u51fa\u6b64\u4e2d\u771f\u76f8\u3002\u201d \u3000\u3000\u4e91\u8c26\u8f69\u7709\u9053\uff1a\u201c\u96be\u9053\u6b64\u4e8b\u4e4b\u4e2d\uff0c\u8fd8\u6709\u4ec0\u4e48\u9690\u79d8\u4e0d\u6210\uff1f\u201d \u3000\u3000\u5510\u4e49\u606d\u58f0\u9053\uff1a\u201c\u4e54\u8fc1\u5b9e\u975e\u6211\u5f1f\u5144\u6240\u4f24\uff0c\u8001\u524d\u8f88\u5f53\u53ef\u770b\u51fa\u4ee5\u6211\u5144\u5f1f\u7684\u6b66\u529f\uff0c\u5b9e\u4e0d\u80fd\u4f24\u5f97\u4e86 \u4ed6\u3002\u201d \u3000\u3000\u4e91\u8c26\u5389\u58f0\u9053\uff1a\u201c\u4f24\u4ed6\u4e4b\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u5510\u4e49\u6df1\u6df1\u5438\u8fdb\u4e00\u53e3\u6c14\uff0c\u4e3e\u76ee\u671b\u5411\u5929\u4e0a\uff0c\u6b64\u523b\u65e5\u5df2\u4e2d\u5929\uff0c\u4e07\u9053\u91d1\u5149\uff0c\u6620\u5f97\u5927\u5730\u707f\u70c2\u8f89\u714c\uff0c \u4ed6\u53cc\u7709\u4e00\u626c\uff0c\u6717\u58f0\u9053\uff1a\u201c\u6b64\u4eba\u540d\u53eb\u592a\u9633\u541b\u5b50\u3002\u201d \u3000\u3000\u591a\u81c2\u795e\u5251\u8be7\u58f0\u95ee\u9053\uff1a\u201c\u592a\u9633\u541b\u5b50\uff1f\u201d \u3000\u3000\u4ed6\u4e00\u751f\u95ef\u8361\u6b66\u6797\uff0c\u5374\u4ece\u672a\u542c\u8fc7\u5982\u6b64\u5947\u5f02\u7684\u540d\u53f7\uff0c\u5f53\u4e0b\u65e2\u5947\u4e14\u602a\uff0c\u63a5\u53e3\u9053\uff1a\u201c\u6b66\u6797\u4e2d\u4f55\u6765 \u5982\u6b64\u4e00\u53f7\u4eba\u7269\uff1f\u201d \u3000\u3000\u5510\u4e49\u6717\u58f0\u9053\uff1a\u201c\u6b64\u4eba\u867d\u7136\u5e74\u8f7b\uff0c\u4f46\u4e0d\u4ec5\u6b66\u529f\u9ad8\u7edd\uff0c\u884c\u4e8b\u4e3a\u4eba\u66f4\u662f\u5927\u4ec1\u5927\u4e49\uff0c\u636e\u5c0f\u53ef\u6240 \u77e5\uff0c\u6b66\u6797\u4e2d\u9664\u5374\u6b64\u4eba\u4e4b\u5916\uff0c\u518d\u96be\u6709\u4eba\u80fd\u5f53\u5f97\u8d77\u8fd9\u2018\u592a\u9633\u541b\u5b50\u2019\u56db\u5b57\uff01\u201d \u3000\u3000\u4e91\u8c26\u9053\uff1a\u201c\u6b64\u4eba\u662f\u4f55\u59d3\u540d\uff1f\u201d \u3000\u3000\u5510\u4e49\u6717\u58f0\u9053\uff1a\u201c\u6b64\u4eba\u59d3\u5353\uff0c\u540d\u2026\u2026\u201d \u3000\u3000\u4e91\u8c26\u63a5\u53e3\u9053\uff1a\u201c\u5353\u957f\u537f\uff1f\u201d \u3000\u3000\u5510\u4e49\u626c\u7709\u5947\u9053\uff1a\u201c\u6b63\u662f\uff0c\u8001\u524d\u8f88\u96be\u9053\u4e5f\u8ba4\u5f97\u4ed6\u4e48\uff1f\u201d \u3000\u3000\u591a\u81c2\u795e\u5251\u4e91\u8c26\u4ef0\u9996\u4e00\u9635\u5927\u7b11\uff0c\u7b11\u58f0\u4e2d\u5145\u6ee1\u5f97\u610f\u4e4b\u60c5\uff0c\u66f4\u5145\u6ee1\u9a84\u505a\u4e4b\u610f\uff0c\u6717\u6717\u7684\u7b11\u58f0\uff0c\u7acb \u65f6\u968f\u7740\u201c\u592a\u9633\u541b\u5b50\u5353\u957f\u537f\u201d\u4e03\u5b57\uff0c\u5728\u539f\u91ce\u4e2d\u6563\u5e03\u5f00\u53bb\u3002 \u3000\u3000\u7b11\u58f0\u4e4b\u4e2d\uff0c\u4e91\u4e2d\u7a0b\u7a81\u7136\u957f\u8eab\u800c\u8d77\uff0c\u60ca\u559d\u4e00\u58f0\u9053\uff1a\u201c\u65e0\u5f71\u795e\u9488\uff01\u201d \u3000\u3000\u539f\u6765\u4ec1\u4e49\u5251\u5ba2\u4e91\u4e2d\u7a0b\u4e00\u751f\u884c\u4e8b\u6781\u662f\u8c28\u614e\u4ed4\u7ec6\uff0c\u65b9\u624d\u4ed6\u4fef\u8eab\u68c0\u89c6\u4e54\u8fc1\u7684\u4f24\u52bf\uff0c\u89c1\u5230\u7559\u5728\u4e54 \u8fc1\u7a74\u9053\u5916\u7684\u534a\u622a\u4e4c\u9488\uff0c\u5fc3\u4e2d\u5df2\u81ea\u731c\u5230\u51e0\u5206\uff0c\u4f46\u4ed6\u672a\u5c06\u4e8b\u5b9e\u5b8c\u5168\u6f84\u660e\u4ee5\u524d\uff0c\u65e2\u4e0d\u613f\u968f\u53e3\u8bf4\u51fa\uff0c \u4ea6\u4e0d\u613f\u968f\u624b\u627e\u4e0b\uff0c\u5f53\u4e0b\u4ed4\u7ec6\u68c0\u89c6\u826f\u4e45\uff0c\u5148\u95ed\u4f4f\u4e54\u8fc1\u9634\u53a5\u809d\u7ecf\uff0c\u5de6\u9633\u5c11\u8109\u9644\u8fd1\u7684\u4e03\u864e\u7a74\u9053\uff0c\u7136 \u540e\u518d\u4ee5\u4e00\u65b9\u8f6f\u7ee2\u6577\u5728\u624b\u4e0a\uff0c\u62d4\u4e0b\u4e4c\u9488\uff0c\u786e\u5b9a\u5b9e\u4e43\u65e0\u5f71\u795e\u9488\uff0c\u518d\u65e0\u534a\u5206\u7591\u4e49\u4e4b\u4f59\uff0c\u65b9\u81ea\u8131\u53e3\u60ca \u547c\u51fa\u6765\u3002 \u3000\u3000\u591a\u81c2\u795e\u5251\u4e91\u8c26\u5fc3\u5934\u4e00\u9707\uff0c\u500f\u7136\u8f6c\u8fc7\u8eab\u53bb\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u83ab\u975e\u4e54\u8fc1\u4e43\u662f\u88ab\u65e0\u5f71\u795e\u9488\u6240\u4f24\uff1f\u201d \u3000\u3000\u4e91\u4e2d\u7a0b\u9762\u5bd2\u5982\u6c34\uff0c\u8083\u7136\u9053\uff1a\u201c\u6b63\u662f\uff01\u201d \u3000\u3000\u591a\u81c2\u795e\u5251\u5927\u559d\u4e00\u58f0\uff0c\u62e7\u8170\u9519\u6b65\uff0c\u5237\u5730\u63a0\u5230\u5510\u4e49\u7684\u8eab\u524d\uff0c\u5389\u53f1\u9053\uff1a\u201c\u2018\u65e0\u77e5\u7a1a\u5b50\uff0c\u5c45\u7136\u6562 \u6b3a\u9a97\u8d77\u8001\u592b\u6765\u4e86\uff01\u201d\u5510\u4e49\u53cc\u7709\u4e00\u626c\uff0c\u633a\u80f8\u9053\uff1a\u201c\u5728\u4e0b\u6240\u8bf4\uff0c\u5b57\u5b57\u53e5\u65ec\u4ff1\u90fd\u662f\u5b9e\u8a00\uff0c\u82e5\u6709\u534a\u5206 \u6b3a\u9a97\u8001\u524d\u8f88\u4e4b\u5904\uff0c\u4efb\u51ed\u53d1\u843d\u5c31\u662f\uff01\u201d \u3000\u3000\u4e91\u8c26\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5353\u957f\u537f\u4e43\u662f\u6614\u5e74\u5927\u4fa0\u5353\u6d69\u7136\u4e4b\u5b50\uff0c\u4e0e\u8001\u592b\u4e24\u4ee3\u76f8\u4ea4\u3002\u201d \u3000\u3000\u8bf4\u5230\u5353\u6d69\u7136\u4e09\u5b57\uff0c\u4ed6\u80f8\u819b\u4e00\u633a\uff0c\u76ee\u5149\u4e00\u4eae\uff0c\u8bf4\u5230\u4e24\u4ee3\u76f8\u4ea4\u56db\u5b57\uff0c\u4ed6\u8bdd\u58f0\u66f4\u662f\u5f97\u610f\u9a84\u505a\uff0c \u610f\u6c14\u98de\u626c\uff0c\u7a0d\u987f\u65b9\u81ea\u63a5\u9053\uff1a\u201c\u5353\u957f\u537f\u7684\u4e3a\u4eba\u884c\u4e8b\uff0c\u8001\u592b\u56fa\u662f\u6e05\u6e05\u695a\u695a\uff0c\u4ed6\u7684\u6b66\u529f\u5bb6\u6559\uff0c\u8001\u592b \u66f4\u662f\u4e86\u5982\u6307\u638c\uff0c\u4f60\u7740\u60f3\u660e\u8a00\u7792\u9a97\u8001\u592b\uff0c\u5c82\u975e\u75f4\u4eba\u8bf4\u68a6\uff1f\u201d \u3000\u3000\u5510\u4e49\u6717\u58f0\u9053\uff1a\u201c\u4e54\u8fc1\u5b9e\u4e3a\u592a\u9633\u541b\u5b50\u6240\u64d2\uff0c\u4f46\u8eab\u4e2d\u7684\u6697\u5668\u5374\u662f\u5353\u5927\u4fa0\u8eab\u65c1\u7684\u4e00\u4f4d\u59d1\u5a18\u6240 \u53d1\uff0c\u5728\u4e0b\u7edd\u65e0\u76f8\u6b3a\u4e4b\u5fc3\uff0c\u8001\u524d\u8f88\u4f11\u5f97\u9519\u602a\uff01\u201d \u3000\u3000\u4e91\u8c26\u6d53\u7709\u4e00\u8f69\uff0c\u5947\u9053\uff1a\u201c\u4ed6\u8eab\u4fa7\u8fd8\u6709\u4e00\u4f4d\u59d1\u5a18\uff1f\u59d3\u751a\u540d\u8c01\uff1f\u957f\u5f97\u662f\u4ec0\u4e48\u6a21\u6837\uff1f\u201d \u3000\u3000\u5510\u4e49\u8eac\u8eab\u9053\uff1a\u201c\u90a3\u4f4d\u59d1\u5a18\u50cf\u662f\u59d3\u6e29\uff0c\u53ea\u56e0\u5979\u662f\u5353\u5927\u4fa0\u4e4b\u53cb\uff0c\u5728\u4e0b\u672a\u6562\u5e73\u89c6\uff0c\u53ea\u89c9\u5979\u8273\u5149 \u7167\u4eba\uff0c\u7f8e\u5982\u5929\u4ed9\uff0c\u6b66\u529f\u4ea6\u662f\u9ad8\u660e\u5df2\u6781\u3002\u201d \u3000\u3000\u4e91\u8c26\u5fc3\u4e2d\u4e0d\u7981\u66f4\u4e3a\u4e4b\u5927\u5947\uff0c\u4fef\u9996\u6c89\u601d\u534a\u664c\uff0c\u53c8\u81ea\u5947\u9053\uff1a\u201c\u4f60\u4e14\u5c06\u6b64\u4e8b\u7ecf\u8fc7\u8be6\u7ec6\u8bf4\u51fa\uff01\u201d \u3000\u3000\u5510\u4e49\u5e72\u54b3\u4e00\u58f0\uff0c\u4fbf\u5c06\u4e54\u8fc1\u5982\u4f55\u643a\u5236\u9020\u65e0\u5f71\u795e\u5251\u4e4b\u56fe\u6837\uff0c\u8bf4\u52a8\u5510\u6c0f\u95e8\u4eba\uff0c\u5982\u4f55\u6f5c\u81f3\u5929\u76ee\u5c71 \u4e2d\uff0c\u5982\u4f55\u9690\u4e8e\u6728\u68fa\u4ee5\u5185\uff0c\u5982\u4f55\u88ab\u5353\u957f\u537f\u53d1\u89c9\u2026\u2026\u7b49\u7b49\u60c5\u4e8b\uff0c\u4e00\u4e00\u8bf4\u5c06\u51fa\u6765\u3002 \u3000\u3000\u53ea\u542c\u5f97\u4e91\u8c26\u65f6\u800c\u626c\u7709\u77aa\u76ee\uff0c\u65f6\u800c\u62cd\u638c\u6012\u9a82\uff0c\u4ed6\u518d\u4e5f\u60f3\u4e0d\u5230\u4e54\u8fc1\u7ade\u662f\u5982\u6b64\u5351\u9119\u72e0\u8fa3\u7684\u9f20 \u8f88\u3002 \u3000\u3000\u5510\u4e49\u8bed\u58f0\u4e00\u4e86\uff0c\u4e91\u8c26\u76f4\u6c14\u5f97\u53cc\u76ee\u706b\u8d64\uff0c\u987b\u53d1\u7686\u5f20\uff0c\u5927\u6012\u53f1\u9053\uff1a\u201c\u597d\u4e2a\u4e54\u8fc1\uff0c\u771f\u6b63\u6c14\u715e\u8001 \u592b\u3002\u201d \u3000\u3000\u4e91\u4e2d\u7a0b\u5374\u76b1\u7709\u5947\u9053\uff1a\u201c\u957f\u537f\u5f1f\u600e\u4f1a\u4e0e\u90a3\u59d3\u6e29\u7684\u59d1\u5a18\u8d70\u5230\u4e00\u5904\uff1f\u201d \u3000\u3000\u8bed\u58f0\u7a0d\u987f\uff0c\u53c8\u9053\uff1a\u201c\u4ed6\u6b64\u523b\u662f\u7559\u5728\u5929\u76ee\u5c71\u4e2d\uff0c\u4e0d\u77e5\u4f55\u65f6\u4f1a\u9047\u5230\u5371\u9669\uff0c\u7239\u7239\uff0c\u6211\u4eec\u8fd8 \u662f\u2026\u2026\u201d \u3000\u3000\u4e91\u8c26\u63a5\u53e3\u9053\uff1a\u201c\u6b63\u662f\uff0c\u6b63\u662f\uff0c\u8fd8\u662f\u7094\u53bb\u63a5\u5e94\u4ed6\u3002\u201d \u3000\u3000\u76ee\u5149\u51b7\u7136\u5411\u4e54\u8fc1\u4e00\u626b\uff1a\u201c\u8fd9\u7b49\u5351\u9119\u4e4b\u5f92\uff0c\u82e5\u975e\u8001\u592b\u6b64\u523b\u6709\u4e8b\uff0c\u771f\u8981\u5148\u6253\u4ed6\u51e0\u62f3\u51fa\u51fa\u6076 \u6c14\uff01\u201d \u3000\u3000\u65e5\u65b9\u897f\u843d\uff0c\u8f66\u9a6c\u5df2\u5230\u5929\u95f0\u5c71\u53e3\uff0c\u4e91\u6c0f\u7236\u5b50\u4e3a\u5173\u5fc3\u5353\u957f\u537f\u5b89\u5371\uff0c\u5374\u5fd8\u4e86\u5929\u76ee\u5c71\u4e2d\u7684\u9669\u5883\uff0c \u5404\u81ea\u5c55\u52a8\u8eab\u5f62\uff0c\u76f4\u95ef\u4e0a\u5c71\uff0c\u4e3a\u4eba\u4e4b\u5371\uff0c\u5fd8\u5df2\u4e4b\u9669\uff0c\u8fd9\u6b63\u662f\u4fa0\u4e49\u9053\u7684\u5fc3\u6027\uff0c\u4e5f\u6b63\u662f\u5927\u4e08\u592b\u7684\u672c \u8272\u3002 \u3000\u3000\u5c71\u5f84\u66f2\u6298\uff0c\u6797\u6728\u5939\u9053\uff0c\u5374\u65e0\u4e00\u4eba\u8ff9\uff0c\u6c5f\u6e56\u4e2d\u4eba\u4ff1\u77e5\u6b64\u5c71\u4e2d\u6b64\u65f6\u5df2\u662f\u56db\u4f0f\u5371\u673a\uff0c\u4f46\u770b\u6765\u5374 \u53c8\u4ecd\u548c\u5e73\u65e5\u4e00\u6837\uff0c\u4e1d\u6beb\u6ca1\u6709\u5947\u5f02\u4e4b\u5904\uff0c\u4e91\u6c0f\u7236\u5b50\u867d\u77e5\u5353\u957f\u537f\u5b9a\u5728\u6b64\u5c71\uff0c\u4f46\u5c71\u6df1\u8def\u6b8a\uff0c\u5374\u4e0d\u77e5 \u8be5\u5982\u4f55\u5bfb\u53bb\uff1f \u3000\u3000\u65e5\u8272\u6e10\u6e10\u897f\u6c89\uff0c\u66ae\u4e91\u6e10\u751f\u6e10\u6d53\uff0c\u7eda\u70c2\u7684\u5915\u9633\u6620\u5165\u6797\u68a2\uff0c\u6620\u5728\u6d53\u6797\u95f4\u7684\u4e00\u7247\u7a7a\u5730\u4e0a\uff0c\u67d4\u8349 \u5982\u8335\uff0c\u5915\u9633\u4e0b\u671b\u53bb\u6709\u5982\u91d1\u8272\u7684\u68a6\u3002 \u3000\u3000\u6797\u68a2\u95f4\u5bc2\u9759\u65e0\u58f0\uff0c\u8349\u5730\u4e0a\u5bc2\u9759\u65e0\u4eba\uff0c\u5bc6\u6797\u540e\u7a81\u7136\u4f20\u51fa\u4e00\u58f0\u5e7d\u5e7d\u7684\u53f9\u606f\uff0c\u4e00\u4e2a\u5a07\u67d4\u751c\u7f8e\u7684 \u58f0\u97f3\u8f7b\u8f7b\u8bf4\u9053\uff1a\u201c\u5929\u5df2\u7ecf\u665a\u4e86\uff0c\u5929\u4e3a\u4ec0\u4e48\u665a\u5f97\u8fd9\u4e48\u5feb\uff01\u201d \u3000\u3000\u5e7d\u6028\u7684\u8bed\u58f0\uff0c\u4f4e\u6c89\u800c\u7f13\u6162\uff0c\u4f7f\u5f97\u8fd9\u5e73\u51e1\u7684\u8bed\u53e5\uff0c\u90fd\u5316\u505a\u4e86\u60a6\u8033\u7684\u6b4c\u66f2\u3002 \u3000\u3000\u56de\u58f0\u8885\u8885\uff0c\u53c8\u5f52\u9759\u5bc2\u826f\u4e45\uff0c\u53c8\u662f\u4e00\u58f0\u53f9\u606f\uff0c\u4e00\u4e2a\u4f4e\u6c89\u7684\u58f0\u97f3\u9053\uff1a\u201c\u5929\u771f\u7684\u665a\u4e86\uff0c\u5929\u771f\u7684 \u665a\u5f97\u5f88\u5feb\u3002\u201d \u3000\u3000\u8bed\u58f0\u843d\u5904\uff0c\u53c8\u662f\u4e00\u9635\u9759\u5bc2\u3002 \u3000\u3000\u7136\u540e\uff0c\u90a3\u5a07\u67d4\u751c\u7f8e\u7684\u58f0\u97f3\u53c8\u81ea\u5e7d\u5e7d\u4e00\u53f9\uff0c\u9053\uff1a\u201c\u4f60\u997f\u4e86\u4e48\uff1f\u4f60\u770b\uff0c\u6211\u771f\u662f\u7cca\u6d82\uff0c\u4e1c\u897f\u62ff \u6765\u4e86\uff0c\u5374\u6ca1\u6709\u5f04\u7ed9\u4f60\u5403\u3002\u201d \u3000\u3000\u968f\u7740\u8bed\u58f0\uff0c\u6d53\u6797\u4e2d\u6f2b\u6b65\u8d70\u51fa\u5ae3\u7136\u7b11\u7740\u7684\u6e29\u747e\uff0c\u5979\u4e00\u624b\u8f7b\u629a\u4e91\u9b13\uff0c\u4e00\u624b\u63d0\u7740\u4e00\u53ea\u9542\u82b1\u7af9 \u7bee\uff0c\u5979\u9762\u4e0a\u867d\u6709\u7b11\u5bb9\uff0c\u4f46\u79cb\u6ce2\u4e2d\u5374\u5145\u6ee1\u5e7d\u6028\u4e4b\u610f\u3002 \u3000\u3000\u5979\u8f7b\u8f7b\u4fef\u4e0b\u8eab\uff0c\u5c06\u624b\u4e2d\u7684\u7af9\u7bee\uff0c\u8f7b\u8f7b\u653e\u5728\u68a6\u4e00\u822c\u67d4\u8f6f\u7684\u8349\u5730\u4e0a\uff0c\u8f7b\u8f7b\u542f\u5f00\u7af9\u7bee\uff0c\u8f7b\u8f7b\u53d6 \u51fa\u4e00\uff0c\u65b9\u6d45\u7eff\u8272\u7684\u67d4\u7ee2\uff0c\u8f7b\u8f7b\u94fa\u4e0b\u3002 \u3000\u3000\u7136\u540e\uff0c\u5979\u53d1\u89c9\u8eab\u540e\u7f13\u7f13\u8d70\u6765\u4e00\u6761\u9880\u957f\u7684\u4eba\u5f71\uff0c\u5915\u9633\uff0c\u5c06\u4ed6\u7684\u4eba\u5f71\u957f\u957f\u62d6\u5728\u8349\u5730\u4e0a\uff0c\u4e5f\u957f \u957f\u5730\u5370\u5728\u5979\u8eab\u4e0a\u3002 \u3000\u3000\u5979\u4e0d\u7528\u56de\u987e\uff0c\u4e5f\u6bcb\u5eb8\u8be2\u95ee\u3002 \u3000\u3000\u5979\u53ea\u662f\u8f7b\u8f7b\u5408\u4e0a\u773c\u5e18\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u996d\u8fd8\u6ca1\u6709\u505a\u597d\uff0c\u4f60\u5c31\u8dd1\u6765\u3002\u771f\u8ba8\u538c\u6b7b\u4e86\u3002\u3001\u5ffd\u89c1\u8eab\u540e \u7684\u4eba\u5f64\u4e3e\u8d77\u4e00\u53ea\u624b\u638c\uff0c\u5411\u81ea\u5df1\u5f53\u5934\u62cd\u4e86\u4e0b\u6765\u3002\u98ce\u58f0\u864e\u864e\uff0c\u638c\u5f0f\u4e2d\u4f3c\u8574\u5185\u529f\uff0c\u6e29\u747e\u5fc3\u4e2d\u4e00\u60ca\uff0c \u5fd6\u9053\uff1a\u201c\u96be\u9053\u4ed6\u4e0d\u662f\u957f\u537f\uff1f\u201d \u3000\u3000\u5927\u559d\u4e00\u58f0\uff1a\u201c\u662f\u8c01\uff1f\u201d \u3000\u3000\u633a\u8eab\u7ad9\u8d77\uff0c\u62e7\u8170\u4e00\u638c\u5288\u53bb\uff0c\u53ea\u89c1\u8eab\u540e\u6765\u90a3\u4eba\u624b\u638c\u4e00\u62cd\uff0c\u5411\u81ea\u5df1\u638c\u4e0a\u8fce\u6765\uff0c\u4e24\u638c\u76f8\u51fb\uff0c \u201c\u556a\u201d\u5730\u4e00\u58f0\uff0c\u6e29\u747e\u53ea\u89c1\u5bf9\u65b9\u5c0f\u5c0f\u4e00\u53ea\u624b\u638c\uff0c\u5374\u4f3c\u6c6a\u6d0b\u5927\u6d77\uff0c\u5c06\u81ea\u5df1\u638c\u4e0a\u5185\u529b\u5168\u90e8\u5316\u89e3\u5f00 \u53bb\u3002 \u3000\u3000\u5239\u90a3\u4e4b\u95f4\uff0c\u5979\u5fc3\u5934\u4e00\u98a4\uff0c\u62ac\u76ee\u671b\u53bb\uff0c\u5374\u89c1\u5353\u957f\u537f\u677f\u7740\u9762\u5b54\u7ad9\u5728\u9762\u524d\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u5728\u8bf4 \u8c01\u8ba8\u538c\uff1f\u201d \u3000\u3000\u201c\u8bdd\u58f0\u672a\u4e86\uff0c\u5df2\u81ea\u5931\u58f0\u7b11\u4e86\u8d77\u6765\u3002\u7b11\u58f0\u8d8a\u6765\u8d8a\u54cd\uff0c\u6e29\u747e\u6a31\u549b\u4e00\u4e00\u58f0\uff0c\u5a07\u58f0\u9053\uff1a\u201c\u4f60\u2026\u2026 \u4f60\u4e0d\u4f46\u8ba8\u538c\uff0c\u800c\u4e14\u574f\u6b7b\u4e86\u3002\u201d \u3000\u3000\u5374\u89c1\u5353\u957f\u537f\u5df2\u7b11\u5f97\u5f2f\u4e0b\u8170\u53bb\u3002 \u3000\u3000\u6e29\u747e\u5c0f\u5634\u4e00\u5476\uff0c\u5c06\u4ed6\u8f6c\u4e86\u4e2a\u8eab\uff0c\u8fdc\u8fdc\u63a8\u4e86\u5f00\u53bb\uff0c\u5a07\u55d4\u7740\u9053\uff1a\u201c\u4f60\u8981\u662f\u4e0d\u7ad9\u8fdc\u4e00\u4e9b\uff0c\u6211\u5c31 \u4e0d\u5f04\u4e1c\u897f\u7ed9\u4f60\u5403\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u8fde\u8fde\u5e94\u9053\uff1a\u201c\u662f\uff0c\u662f\uff0c\u6211\u4e00\u5b9a\u7ad9\u5f97\u8fdc\u8fdc\u7684\u3002\u201d \u3000\u3000\u6e29\u747e\u9053\uff1a\u201c\u8fd9\u624d\u662f\u4e56\u5b69\u5b50\u3002\u201d \u3000\u3000\u5ae3\u7136\u4e00\u7b11\uff0c\u8f6c\u8eab\u8d70\u4e86\u4e24\u6b65\uff0c\u5374\u53c8\u5fcd\u4e0d\u4f4f\u5ae3\u7136\u56de\u7738\uff0c\u201c\u6251\u54e7\u201d\u7b11\u51fa\u58f0\u6765\u3002 \u3000\u3000\u5353\u957f\u537f\u5446\u5446\u5730\u671b\u7740\u5979\u7684\u80cc\u5f71\uff0c\u53ea\u89c1\u5979\u67f3\u8170\u7ea4\u7ec6\uff0c\u7c89\u9888\u5982\u4e91\uff0c\u5915\u9633\u4e0b\u7684\u7f8e\u4eba\u4eff\u4f5b\u6bd4\u5e73\u65e5\u66f4 \u8981\u7f8e\u4e0a\u597d\u51e0\u5206\uff0c\u53ea\u89c1\u5979\u624b\u5fd9\u811a\u4e71\u5730\u4ece\u7bee\u4e2d\u53d6\u51fa\u8bb8\u591a\u4e1c\u897f\uff0c\u4e00\u4e00\u653e\u5728\u90a3\u65b9\u67d4\u7ee2\u4e0a\uff0c\u53c8\u62ff\u4e86\u4e9b\u5c0f \u74f6\u5c0f\u7f50\uff0c\u4e1c\u6d12\u4e00\u70b9\u76d0\u5df4\uff0c\u897f\u6d12\u4e00\u70b9\u9171\u6cb9\u3002 \u3000\u3000\u5353\u957f\u537f\u53ea\u89c9\u4e00\u9635\u6696\u610f\uff0c\u81ea\u5fc3\u5e95\u5347\u8d77\u3001\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u505a\u597d\u4e86\u4e48\uff1f\u201d \u3000\u3000\u6e29\u747e\u56de\u7738\u7b11\u9053\uff1a\u201c\u505a\u662f\u505a\u597d\u4e86\uff0c\u6211\u504f\u8981\u4f60\u518d\u7b49\u4e00\u7b49\uff0c\u535c\u5353\u957f\u537f\u666e\u7740\u8138\u9053\uff1a\u201c\u6211\u7b49\u4e0d\u53ca \u4e86\u3002\u201d \u3000\u3000\u6e29\u747e\u54af\u54af\u7b11\u9053\uff1a\u201c\u770b\u4f60\u8fd9\u526f\u998b\u6837\u5b50\uff0c\u597d\u597d\u3001\u4eca\u5929\u5c31\u9976\u4f60\u4e00\u6b21\uff0c\u7094\u6765\u5403\u5427\uff01\u201d \u3000\u3000\u5353\u957f\u537f\u5927\u6b65\u5954\u4e86\u8fc7\u53bb\uff0c\u91cd\u91cd\u5750\u5728\u6e29\u747e\u8eab\u65c1\uff0c\u6e29\u747e\u5939\u4e86\u4e00\u5757\u767d\u9e21\uff0c\u653e\u5728\u4ed6\u53e3\u8fb9\uff0c\u4ed6\u5f20\u5f00\u5927 \u53e3\uff0c\u4e00\u53e3\u5403\u4e86\uff0c\u6e29\u747e\u4ef0\u9762\u9053\uff1a\u201c\u4f60\u8bf4\uff0c\u4f60\u8bf4\u597d\u5403\u4e0d\u597d\u5403\uff1f\u201d \u3000\u3000\u79cb\u6ce2\u5982\u6c34\uff0c\u5410\u6c14\u5982\u5170\uff0c\u5353\u957f\u537f\u7f13\u7f13\u4f38\u624b\u51fa\u638c\uff0c\u8f7b\u8f7b\u4e00\u629a\u5979\u9b13\u8fb9\u4e71\u53d1\uff0c\u6b64\u65f6\u6b64\u523b\uff0c\u4ed6\u53ea\u89c9 \u5fc3\u4e2d\u4ff1\u662f\u67d4\u60c5\u871c\u610f\uff0c\u8981\u77e5\u4ed6\u81ea\u5e7c\u5b64\u72ec\uff0c\u4fbf\u662f\u666e\u901a\u5e7c\u7ae5\u7684\u9ec4\u91d1\u7ae5\u5e74\uff0c\u4ed6\u4e5f\u672a\u66fe\u4eab\u53d7\uff0c\u800c\u6b64\u60c5\u6b64 \u666f\uff0c\u4ed6\u66f4\u662f\u5728\u68a6\u4e2d\u4e5f\u672a\u66fe\u60f3\u8d77\u3002 \u3000\u3000\u6e29\u747e\u671b\u7740\u4ed6\u51fa\u795e\u7684\u9762\u5bb9\uff0c\u53c8\u9053\uff1a\u201c\u4f60\u8bf4\uff0c\u597d\u4e0d\u597d\u5403\u561b\uff1f\u201d \u3000\u3000\u5353\u957f\u537f\u7b11\u9053\uff1a\u201c\u4f60\u518d\u5939\u4e00\u5757\u7ed9\u6211\u5403\u5403\uff0c\u8fd9\u4e48\u5c0f\u7684\u4e00\u5757\uff0c\u6211\u8fde\u5473\u9053\u90fd\u6ca1\u6709\u5403\u51fa\u54e9\u3002\u201d \u3000\u3000\u6e29\u747e\u7b11\u9a82\u9053\uff1a\u201c\u998b\u9b3c\u3002\u201d \u3000\u3000\u53c8\u5939\u4e86\u4e09\u5757\u9e21\u8089\uff0c\u4e00\u8d77\u653e\u5728\u4ed6\u5634\u91cc\u3002 \u3000\u3000\u5353\u957f\u537f\u5480\u56bc\u534a\u664c\uff0c\u7b11\u9053\uff1a\u201c\u597d\u5403\uff0c\u597d\u5403\uff0c\u2026\u2026\u53ea\u662f\uff0c\u53ea\u662f\u2026\u2026\u201d \u3000\u3000\u6e29\u747e\u9053\uff1a\u201c\u53ea\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5353\u957f\u537f\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6211\u8fd8\u4ee5\u4e3a\u4f60\u548c\u76d0\u5df4\u5e97\u7ed3\u4e86\u4eb2\u5bb6\uff0c\u4e0d\u7136\u600e\u4f1a\u6210\u5f97\u8fd9\u822c\u5413\u4eba\u3002\u201d \u3000\u3000\u6e29\u747e\u201c\u5624\u549b\u201d\u4e00\u58f0\uff0c\u5939\u8d77\u4e00\u6761\u9e21\u817f\uff0c\u4e00\u8d77\u585e\u5230\u4ed6\u7684\u53e3\u4e2d\uff0c\u5a07\u55d4\u9053\uff1a\u201c\u54b8\u6b7b\u4f60\uff0c\u54b8\u6b7b\u4f60\uff0c \u6211\u5c31\u8981\u54b8\u6b7b\u4f60\u3002\u201d \u3000\u3000\u8bdd\u672a\u8bf4\u5b8c\uff0c\u53c8\u54af\u54af\u5730\u7b11\u4e86\u8d77\u6765\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u4ff1\u662f\u906d\u9047\u51c4\u6614\uff0c\u8eab\u4e16\u5b64\u72ec\uff0c\u4f46\u6b64\u523b\u5f7c\u6b64\u76f8\u5bf9\u5927\u7b11\uff0c\u4e00\u751f\u4e2d\u7684\u5bc2\u5bde\u5b64\u82e6\uff0c\u4f3c\u4e4e\u90fd\u5df2 \u5728\u7b11\u58f0\u4e2d\u6d88\u53bb\u3002 \u3000\u3000\u7b11\u4e86\u534a\u664c\u540e\uff0c\u4e00\u58f0\u866b\u9e23\uff0c\u4e24\u4eba\u7b11\u58f0\u7a81\u5730\u4e00\u8d77\u987f\u4f4f\uff0c\u4f60\u5446\u5446\u5730\u671b\u7740\u6211\uff0c\u6211\u5446\u5446\u5730\u671b\u7740\u4f60\uff0c \u826f\u4e45\u826f\u4e45\uff0c\u6e29\u747e\u7a81\u5730\u5e7d\u5e7d\u53f9\u9053\uff1a\u201c\u5929\u8d8a\u6765\u8d8a\u9ed1\u4e86\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u832b\u7136\u4ef0\u89c6\u4e00\u773c\uff0c\u4e00\u5f26\u660e\u6708\uff0c\u5df2\u81ea\u6797\u68a2\u5347\u8d77\uff0c\u4ed6\u4e0d\u7981\u4e5f\u53f9\u9053\uff1a\u201c\u6708\u4eae\u5347\u8d77\u6765\u4e86\u3002\u201d \u3000\u3000\u6e29\u747e\u7f13\u7f13\u5782\u4e0b\u5934\u53bb\uff0c\u9053\uff1a\u201c\u4e0d\u77e5\u9053\u2026\u2026\u4e0d\u77e5\u9053\u6e29\u5982\u7389\u5979\u2026\u2026\u5979\u53ef\u662f\u5df2\u7ecf\u53bb\u4e86\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u7f13\u7f13\u9053\uff1a\u201c\u53ea\u6015\u8fd8\u6ca1\u6709\u53bb\u5427\uff0c\u73b0\u5728\u2026\u2026\u73b0\u5728\u8fd8\u4e0d\u5230\u665a\u4e0a\u561b\uff01\u201d \u3000\u3000\u6e29\u747e\u9053\uff1a\u201c\u4f46\u662f\u5979\u6bd5\u7adf\u662f\u5feb\u53bb\u4e86\uff0c\u665a\u4e0a\u2026\u2026\u665a\u4e0a\u5df2\u7ecf\u5230\u4e86\u3002\u201d \u3000\u3000\u7a81\u5730\u4e00\u5408\u773c\u7751\uff0c\u4e24\u884c\u6676\u83b9\u7684\u6cea\u73e0\uff0c\u593a\u7736\u800c\u51fa\uff0c\u987a\u816e\u6d41\u4e0b\u3002 \u3000\u3000\u4e00\u65f6\u4e4b\u95f4\uff0c\u4e24\u4eba\u9ed8\u7136\u76f8\u5bf9\uff0c\u65b9\u624d\u7684\u6b22\u7b11\uff0c\u5df2\u88ab\u5fe7\u90c1\u4ee3\u66ff\u3002 \u3000\u3000\u4ed6\u4eec\u867d\u60f3\u4ee5\u6b22\u7b11\u6765\u9ebb\u6728\u81ea\u5df1\uff0c\u4f46\u6b22\u7b11\u5374\u7ec8\u4e8e\u63a9\u4e0d\u4f4f\u6b8b\u9177\u7684\u73b0\u5b9e\uff0c\u56e0\u4e3a\u4eca\u5bb5\u4fbf\u53ef\u51b3\u5b9a\u4ed6\u4eec \u8fd9\u4e00\u751f\u7684\u547d\u8fd0\uff0c\u751a\u81f3\u8fd8\u53ef\u4ee5\u51b3\u5b9a\u4ed6\u4eec\u7684\u751f\u547d\u3002 \u3000\u3000\u9762\u5bf9\u7740\u90a3\u6b66\u529f\u9ad8\u7edd\u7684\u6df1\u4ec7\u5927\u654c\uff0c\u4ed6\u4eec\u8c01\u4e5f\u6ca1\u6709\u628a\u63e1\u53ef\u4ee5\u5236\u80dc\uff0c\u800c\u4e0d\u80fd\u5236\u80dc\u7684\u540e\u679c\u662f\u4ec0 \u4e48\uff0c\u4ed6\u4eec\u5fc3\u91cc\u5df2\u6e05\u695a\u5f97\u5f88\u3002 \u3000\u3000\u5353\u957f\u537f\u8f7b\u8f7b\u629a\u4f4f\u5979\u7684\u80a9\u5934\uff0c\u53ea\u89c1\u5979\u7f13\u7f13\u62fe\u8d77\u5934\u6765\uff0c\u4ef0\u9762\u9053\uff1a\u201c\u957f\u537f\uff0c\u4f60\u80fd\u4e0d\u80fd\u544a\u8bc9\u6211\uff0c \u4e3a\u4ec0\u4e48\u4eba\u4eec\u7684\u76f8\u4f1a\uff0c\u603b\u6bd4\u522b\u79bb\u77ed\u6682\u3002\u201d \u3000\u3000\u6797\u68a2\u6f0f\u4e0b\u7684\u6726\u80e7\u6708\u8272\uff0c\u6620\u7740\u5979\u6cea\u6c34\u6676\u83b9\u7684\u79cb\u6ce2\uff0c\u5353\u957f\u537f\u6697\u95ee\u81ea\u5df1\uff1a\u201c\u4e3a\u4ec0\u4e48\u76f8\u4f1a\u603b\u6bd4\u522b \u79bb\u77ed\u6682\u2026\u2026\u4e3a\u4ec0\u4e48\u76f8\u4f1a\u603b\u6bd4\u522b\u79bb\u77ed\u6682\u2026\u2026\u201d \u3000\u3000\u4ed6\u7ec6\u7ec6\u5480\u56bc\u7740\u8fd9\u4e24\u53e5\u8bdd\u7684\u6ecb\u5473\uff0c\u53ea\u89c9\u60b2\u4ece\u4e2d\u6765\uff0c\u4e0d\u53ef\u65ad\u7edd\u3002 \u3000\u3000\u6e29\u747e\u4f38\u624b\u4e00\u62ed\u773c\u7751\uff0c\u5f3a\u989c\u4e00\u7b11\uff0c\u8f7b\u8f7b\u9053\uff1a\u201c\u660e\u65e5\u6b64\u523b\uff0c\u6211\u4eec\u82e5\u662f\u8fd8\u80fd\u5230\u8fd9\u91cc\u6765\uff0c\u6211\u4e00\u5b9a \u5728\u767d\u9e21\u4e0a\u5c11\u653e\u4e00\u4e9b\u9171\u6cb9\u3001\u76d0\uff0c\u514d\u5f97\u4f60\u8bf4\u6211\u548c\u4ed6\u4eec\u7ed3\u4e86\u4eb2\u5bb6\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u5782\u9996\u4e0d\u8bed\u3002 \u3000\u3000\u6e29\u747e\u53c8\u9053\uff1a\u201c\u65b9\u624d\u4f60\u5728\u6211\u8eab\u540e\u5288\u6211\u4e00\u638c\uff0c\u6211\u771f\u7684\u4ee5\u4e3a\u662f\u7389\u90ce\u6bd5\u56db\uff0c\u54ea\u77e5\u4f60\u770b\u6765\u8001\u8001\u5b9e \u5b9e\uff0c\u5176\u5b9e\u5374\u672a\u89c1\u5f97\u6709\u591a\u8001\u5b9e\u54e9\uff01\u201d \u3000\u3000\u5353\u957f\u537f\u4ecd\u662f\u5782\u9996\u4e0d\u8bed\u3002 \u3000\u3000\u6e29\u747e\u9053\uff1a\u201c\u6700\u53ef\u7b11\u7684\u662f\u7389\u90ce\u6bd5\u56db\u90a3\u526f\u81ea\u6211\u9676\u9189\u7684\u6837\u5b50\uff0c\u6211\u5fc3\u91cc\u53ea\u8981\u4e00\u60f3\u8d77\u6765\uff0c\u5c31\u5fcd\u4e0d\u4f4f \u8981\u7b11\u3002\u201d \u3000\u3000\u63a9\u53e3\u7b11\u4e86\u4e24\u58f0\uff0c\u7b11\u58f0\u4e2d\u5374\u5168\u65e0\u7b11\u610f\u3002 \u3000\u3000\u5353\u957f\u537f\u4f9d\u7136\u5782\u9996\u4e0d\u8bed\u3002 \u3000\u3000\u6e29\u747e\u51fa\u795e\u5730\u5411\u4ed6\u671b\u4e86\u534a\u664c\uff0c\u7a81\u5730\u5e7d\u5e7d\u4e00\u53f9\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u4f60\u96be\u9053\u4e0d\u80fd\u9ad8\u9ad8\u5174\u5174\u5730\u548c\u6211\u8bf4 \u8bdd\u4e48\uff0c\u4f60\u96be\u9053\u4e0d\u80fd\u5c06\u5fc3\u91cc\u7684\u70e6\u607c\u5168\u90e8\u629b\u5f00\uff1f\u4f60\u96be\u9053\u2026\u2026\u201d \u3000\u3000\u8bed\u58f0\u4e00\u9635\u4fbf\u54bd\uff0c\u5fcd\u4e0d\u4f4f\u53c8\u6d41\u4e0b\u6cea\u6765\u3002 \u3000\u3000\u4e91\u6c0f\u7236\u5b50\u6ee1\u5c71\u800c\u884c\uff0c\u53ea\u89c9\u6708\u4eae\u8d8a\u5347\u8d8a\u9ad8\uff0c\u5c71\u98ce\u8d8a\u6765\u8d8a\u5bd2\uff0c\u591a\u81c2\u795e\u5251\u4e91\u8c26\u5fc3\u4e2d\u8d8a\u7126\u8e81\uff0c\u76b1 \u7709\u9053\uff1a\u201c\u4e2d\u7a0b\uff0c\u5929\u76ee\u5c71\u4e2d\u6b64\u523b\u600e\u5730\u5168\u65e0\u52a8\u9759\uff0c\u8fd9\u5012\u602a\u4e86\uff01\u201d \u3000\u3000\u8bed\u58f0\u5fae\u987f\uff0c\u53c8\u9053\uff1a\u201c\u4f60\u6211\u6700\u597d\u5206\u505a\u4e24\u8def\uff0c\u5018\u82e5\u6211\u4e0d\u5230\u957f\u537f\uff0c\u7b49\u6708\u4eae\u5347\u5230\u5c71\u5dc5\uff0c\u6211\u4eec\u4fbf\u5230 \u8fd9\u91cc\u6765\uff0c\u82e5\u662f\u9047\u7740\u4e86\u4ed6\uff0c\u4e5f\u5c06\u4ed6\u5e26\u5230\u8fd9\u91cc\u3002\u201d \u3000\u3000\u4e91\u4e2d\u7a0b\u6c89\u541f\u9053\uff1a\u201c\u4eba\u5b64\u52bf\u5355\uff0c\u8457\u662f\u9047\u7740\u654c\u4eba\u2026\u201d \u3000\u3000\u591a\u81c2\u795e\u5251\u73af\u7709\u8f69\u5904\uff0c\u63a5\u53e3\u9053\uff1a\u201c\u4f60\u5f53\u4f60\u7239\u7239\u771f\u7684\u8001\u5f97\u4e0d\u4e2d\u7528\u4e86\u4e48\uff1f\u201d \u3000\u3000\u4e91\u4e2d\u7a0b\u8083\u7136\u4e00\u5782\u9996\uff0c\u518d\u4e5f\u4e0d\u6562\u8a00\u8bed\u3002 \u3000\u3000\u4e91\u8c26\u9053\uff1a\u201c\u4f60\u8ba4\u6e05\u4e86\u8fd9\u91cc\u7684\u5730\u5f62\uff0c\u5c31\u5feb\u4e9b\u5f80\u897f\u9e3f\u7b49\uff0c\u77e5\u9053\u4e86\u4e48\uff1f\u201d \u3000\u3000\u4e00\u634b\u94f6\u987b\uff0c\u5f53\u5148\u5411\u4e1c\u9762\u63a0\u53bb\u3002 \u3000\u3000\u4e91\u4e2d\u7a0b\u6697\u4e2d\u53f9\u606f\u4e00\u58f0\uff0c\u56db\u987e\u4e00\u773c\uff0c\u7f13\u6b65\u897f\u884c\uff0c\u8d70\u4e86\u51e0\u6b65\uff0c\u53c8\u4e0d\u653e\u5fc3\uff0c\u56de\u9996\u800c\u671b\uff0c\u4f46\u7239\u7239 \u5374\u5df2\u4e0d\u77e5\u8d70\u5230\u54ea\u91cc\u53bb\u4e86\u3002 \u3000\u3000\u7a7a\u5c71\u5bc2\u5bde\uff0c\u98ce\u5439\u6797\u6728\uff0c\u7a81\u5730\u4e00\u9635\u4eba\u58f0\uff0c\u968f\u98ce\u81ea\u5c71\u5f2f\u540e\u4f20\u51fa\u3002 \u3000\u3000\u4e91\u4e2d\u7a0b\u5fc3\u5934\u5fae\u5fae\u4e00\u51db\uff0c\u500f\u7136\u56db\u987e\u4e00\u773c\uff0c\u53ea\u89c1\u4e00\u682a\u5343\u5e74\u53e4\u6811\uff0c\u51cc\u7a7a\u6a2a\u66f3\uff0c\u679d\u5e72\u82cd\u866c\uff0c\u6728\u53f6 \u6c89\u90c1\uff0c\u62d9\u58ee\u7684\u6811\u5e72\u95f4\uff0c\u5374\u6709\u51e0\u5904\u7a7a\u6d1e\u3002 \u3000\u3000\u4ed6\u4e00\u773c\u77a5\u8fc7\uff0c\u4fbf\u4e0d\u518d\u8fdf\u7591\uff0c\u5506\u5730\u4e00\u4e2a\u7bad\u6b65\uff0c\u63a0\u4e0a\u6811\u5e72\uff0c\u4f0f\u8eab\u5411\u4e00\u4e2a\u6811\u7a9f\u4e2d\u94bb\u4e86\u8fdb\u53bb\uff0c\u53c8 \u8f7b\u5feb\u5730\u62c9\u4e0b\u679d\u53f6\uff0c\u4f5c\u4e3a\u63a9\u9970\uff0c\u4ec1\u4e49\u5251\u5ba2\u540d\u6ee1\u6c5f\u6e56\uff0c\u6b66\u529f\u81ea\u4e0d\u5f31\uff0c\u4f46\u884c\u4e8b\u5f97\u8c28\u614e\u4ed4\u7ec6\uff0c\u9047\u4e8b\u7684 \u51b3\u65ad\u673a\u667a\uff0c\u5374\u662f\u4ed6\u4e4b\u80fd\u4ee5\u6210\u540d\u7684\u4e3b\u8981\u56e0\u7d20\u3002 \u3000\u3000\u5239\u90a3\u4e4b\u95f4\uff0c\u4ed6\u5df2\u9690\u8eab\u505c\u5f53\uff0c\u800c\u6b64\u523b\u5c71\u5f2f\u540e\u4ea6\u5df2\u8d70\u51fa\u4e86\u4e24\u4e2a\u5bb9\u8c8c\u9893\u8d25\u3001\u795e\u6c14\u6cae\u4e27\u7684\u9ec4\u886b\u5c11 \u5e74\u6765\uff0c\u5176\u4e2d\u4e00\u4eba\uff0c\u795e\u60c5\u5c24\u89c1\u843d\u5bde\uff0c\u76ee\u5149\u4f4e\u5782\uff0c\u4e0d\u4f4f\u957f\u53f9\uff0c\u53e6\u4e00\u4eba\u642d\u4f4f\u4ed6\u7684\u80a9\u5934\uff0c\u7f13\u7f13\u9053\uff1a \u201c\u4f60\u96be\u53d7\u4ec0\u4e48\uff1f\u4e8b\u60c5\u65e2\u5df2\u505a\u51fa\uff0c\u96be\u53d7\u4e5f\u6ca1\u6709\u7528\u4e86\uff0c\u597d\u5728\u6211\u76f8\u4fe1\u4ee5\u6e29\u5982\u7389\u7684\u4e3a\u4eba\uff0c\u65e2\u7136\u8bf4\u51fa\u4e8b \u6210\u540e\u4fbf\u5b9a\u4e3a\u6211\u4eec\u89e3\u5f00\u7a74\u9053\uff0c\u60f3\u5fc5\u4e0d\u4f1a\u98df\u8a00\u80cc\u4fe1\uff0c\u518d\u7b49\u534a\u664c\uff0c\u6211\u4eec\u5230\u90a3\u53e4\u5e99\u4e2d\u53bb\u2026\u2026\u201d \u3000\u3000\u53e6\u4e00\u4eba\u7a81\u5730\u957f\u53f9\u4e00\u58f0\uff0c\u62ac\u8d77\u5934\u6765\uff0c\u63a5\u53e3\u9053\uff1a\u201c\u5979\u7eb5\u4e3a\u6211\u4eec\u89e3\u5f00\u7a74\u9053\uff0c\u53ea\u6015\u6211\u4eec\u4e5f\u6d3b\u4e0d\u957f \u4e86\u3002\u201d \u3000\u3000\u53c8\u81ea\u5782\u9996\u63a5\u9053\uff1a\u201c\u5f11\u5e08\u4e4b\u7f6a\uff0c\u662f\u4e3a\u5929\u4e0b\u96be\u5bb9\uff0c\u65e5\u540e\u53ea\u6015\u4e0d\u77e5\u9053\u8981\u6709\u591a\u5c11\u4eba\u4f1a\u6765\u2026\u2026\u5509\uff0c \u8fbe\u4eba\uff0c\u4f60\u8bf4\u662f\u4e48\uff1f\u201d \u3000\u3000\u94c1\u8fbe\u4eba\u201c\u55e4\u201d\u7684\u4e00\u58f0\u51b7\u7b11\uff0c\u9053\uff1a\u201c\u9519\u4e86\uff01\u201d\u3002 \u3000\u3000\u77f3\u5e73\u53f9\u9053\uff1a\u201c\u4e07\u4e07\u4e0d\u4f1a\u9519\u7684\uff0c\u5f11\u5e08\u4e4b\u7f6a\u2026\u2026\u5509\uff0c\u4e07\u4e07\u4e0d\u4f1a\u9519\u7684\u3002\u201d \u3000\u3000\u94c1\u8fbe\u4eba\u51b7\u51b7\u9053\uff1a\u201c\u897f\u65bd\u4e0e\u592b\u5dee\uff0c\u662f\u5426\u6740\u592b\uff0c\u6740\u592b\u662f\u5426\u4ea6\u662f\u5927\u7f6a\uff1f\u4f46\u5929\u4e0b\u4eba\u4e0d\u8bf4\u897f\u65bd\u6deb \u6076\uff0c\u53cd\u9053\u5176\u4eba\u4e4b\u8d1e\u5584\uff0c\u8fd9\u662f\u4e3a\u7684\u4ec0\u4e48\uff0c\u4f60\u53ef\u77e5\u9053\uff1f\u201d \u3000\u3000\u77f3\u5e73\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u4f46\u2026\u2026\u201d \u3000\u3000\u94c1\u8fbe\u4eba\u968f\u8eab\u5728\u90a3\u53e4\u6811\u4e0b\u7684\u4e00\u5757\u5e73\u77f3\u4e0a\u5750\u4e86\u4e0b\u6765\uff0c\u63a5\u53e3\u9053\uff1a\u201c\u6211\u5947\u602a\u4f60\u7684\u8111\u7b4b\u600e\u7684\u6709\u65f6\u8fd9 \u822c\u5446\u677f\uff0c\u4e07\u5999\u771f\u541b\u5c39\u51e1\u7684\u6076\u540d\u5728\u5916\uff0c\u4f60\u6211\u53ea\u8981\u7a0d\u52a0\u82b1\u8a00\u5de7\u8bed\uff0c\u6b66\u6797\u4e2d\u4eba\u53ea\u9053\u4f60\u6211\u5927\u4e49\u706d\u4eb2\uff0c \u5938\u5956\u79f0\u8d5e\u8fd8\u6765\u4e0d\u53ca\uff0c\u600e\u4f1a\u5bf9\u6211\u4e8c\u4eba\u4e0d\u5229\uff1f\u201d \u3000\u3000\u77f3\u5e73\u4fef\u9996\u6c89\u541f\u534a\u664c\uff0c\u9053\uff1a\u201c\u4f46\u2026\u2026\u201d \u3000\u3000\u76ee\u5149\u4e00\u8f6c\uff0c\u671b\u5411\u94c1\u8fbe\u4eba\uff0c\u7a81\u5730\u54c8\u54c8\u5927\u7b11\u8d77\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4e0d\u9519\u2026\u2026\u201d \u3000\u3000\u4e24\u4eba\u76f8\u5bf9\u5927\u7b11\uff0c\u76f4\u542c\u5f97\u4e91\u4e2d\u7a0b\u53cc\u7709\u5251\u8f69\uff0c\u6012\u6124\u586b\u81ba\uff0c\u51e0\u4e4e\u5fcd\u4e0d\u4f4f\u8981\u4e0b\u53bb\u5c06\u8fd9\u4e24\u4e2a\u4e0d\u4ec1\u4e0d \u4e49\u7684\u6076\u5f92\u75db\u6bb4\u4e00\u987f\uff0c\u4ee5\u6d88\u80f8\u4e2d\u6076\u6c14\u3002 \u3000\u3000\u7a81\u5730\u5bf9\u9762\u5c71\u9053\u4e0a\uff0c\u5189\u5189\u6d8c\u8d77\u4e00\u6761\u4eba\u5f71\uff0c\u4e91\u4e2d\u7a0b\u76ee\u5149\u52a8\u5904\uff0c\u5fc3\u4e2d\u7acb\u65f6\u4e3a\u4e4b\u4e00\u51db\uff1a\u201c\u6e29\u5982\u7389 \u8fd9\u9b54\u5934\u7adf\u4e5f\u6765\u4e86\u3002\u201d \u3000\u3000\u53ea\u542c\u6811\u4e0b\u7684\u4e24\u4e2a\u9ec4\u886b\u5c11\u5e74\u7b11\u58f0\u72b9\u672a\u7edd\uff0c\u6e29\u5982\u7389\u67af\u7626\u9880\u957f\u7684\u8eab\u5f71\u5374\u6709\u5982\u5e7d\u7075\u822c\u8d8a\u6765\u8d8a \u8fd1\u2026\u2026 \u3000\u3000\u4e91\u4e2d\u7a0b\u53ea\u89c9\u5fc3\u5934\u72c2\u8df3\uff0c\u624b\u638c\u51b0\u51b7\uff0c\u5374\u4e0d\u77e5\u662f\u4e3a\u4e86\u81ea\u5df1\uff0c\u6291\u6216\u662f\u4e3a\u4e86\u8fd9\u4e24\u4e2a\u4e0d\u4ec1\u4e0d\u4e49\u7684\u9ec4 \u886b\u5c11\u5e74\u62c5\u5fc3\u5462\uff1f \u3000\u3000\u7b11\u58f0\u84e6\u5730\u4e00\u987f\uff0c\u98ce\u7a7f\u679d\u53f6\uff0c\u679d\u53f6\u5fae\u98a4\uff0c\u53ea\u542c\u6e29\u5982\u7389\u9634\u607b\u607b\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u8ba9\u4f60\u4eec\u529e\u7684 \u4e8b\uff0c\u53ef\u66fe\u529e\u597d\u4e86\u4e48\uff1f\u201d \u3000\u3000\u94c1\u8fbe\u4eba\u3001\u77f3\u5e73\u9f50\u5730\u5e94\u58f0\uff1a\u201c\u662f\u2026\u2026\u201d \u3000\u3000\u6e29\u5982\u7389\u51b7\u51b7\u7b11\u9053\uff1a\u201c\u5f88\u597d\uff01\u201d\u811a\u4e0b\u4e0d\u505c\uff0c\u8eab\u5f62\u4f9d\u7136\u5189\u5189\u968f\u98ce\u98d8\u52a8\uff0c\u5411\u5c71\u5f2f\u90a3\u8fb9\u98d8\u53bb\u3002 \u3000\u3000\u94c1\u8fbe\u4eba\u3001\u77f3\u5e73\u5bf9\u671b\u4e00\u773c\uff0c\u5fcd\u4e0d\u4f4f\u9f50\u559d\u4e00\u58f0\uff1a\u201c\u6e29\u8001\u524d\u8f88\uff01\u201d \u3000\u3000\u6e29\u5982\u7389\u56de\u8eab\u5389\u53f1\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u94c1\u8fbe\u4eba\u5782\u9996\u9053\uff1a\u201c\u665a\u8f88\u8eab\u4e2d\u7684\u4e03\u7edd\u91cd\u624b\uff0c\u5df2\u7ecf\u8fc7\u4e86\u5c06\u8fd1\u5341\u4e8c\u4e2a\u65f6\u8fb0\u4e86\uff01\u201d \u3000\u3000\u6e29\u52a0\u7389\u51b7\u51b7\u9053\uff1a\u201c\u8fd8\u6709\u4e09\u5341\u591a\u4e2a\u65f6\u8fb0\u597d\u6d3b\u2026\u2026\u201d \u3000\u3000\u94c1\u8fbe\u4eba\u9762\u5bb9\u84e6\u7136\u4e00\u53d8\uff0c\u98a4\u58f0\u9053\uff1a\u201c\u665a\u8f88\u4eec\u5df2\u9075\u8001\u524d\u8f88\u4e4b\u547d\uff0c\u5c06\u6bd2\u2026\u2026\u5c06\u6bd2\u2026\u2026\u4e0b\u5728\u5bb6\u5e08 \u7684\u8336\u676f\u91cc\uff0c\u800c\u4e14\u4eb2\u773c\u770b\u89c1\u4ed6\u559d\u4e86\u4e0b\u53bb\uff0c\u4f46\u671b\u8001\u524d\u8f88\u2026\u2026\u201d \u3000\u3000\u6e29\u5982\u7389\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u9075\u547d\uff1f\u54fc\uff0c\u54ea\u4e2a\u53eb\u4f60\u4e0b\u6bd2\u7684\uff1f\u201d \u3000\u3000\u77f3\u5e73\u53d8\u8272\u9053\uff1a\u201c\u8001\u524d\u8f88\u2026\u2026\u201d \u3000\u3000\u6e29\u5982\u7389\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u4e14\u5c06\u6211\u6628\u591c\u8bf4\u7684\u8bdd\u4ed4\u7ec6\u518d\u60f3\u4e00\u904d\uff0c\u6211\u53ef\u66fe\u547d\u4f60\u505a\u8fc7\u4ec0\u4e48\uff1f\u53c8\u53ef\u66fe\u7b54 \u5e94\u8fc7\u4f60\u4eec\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u77f3\u5e73\u98a4\u58f0\u9053\uff1a\u201c\u4f46\u2026\u2026\u4f46\u662f\u2026\u2026\u201d \u3000\u3000\u7f13\u7f13\u5782\u4e0b\u5934\u53bb\u3002 \u3000\u3000\u6e29\u5982\u7389\u51b7\u7b11\u9053\uff1a\u201c\u6211\u6628\u591c\u53ea\u662f\u5c06\u90a3\u8ff7\u836f\u629b\u5728\u5730\u4e0a\uff0c\u662f\u4e48\uff1f\u201d \u3000\u3000\u94c1\u8fbe\u4eba\u98a4\u58f0\u9053\uff1a\u201c\u4f46\u8001\u524d\u8f88\u53c8\u8bf4\u2026\u2026\u6e29\u5982\u7389\u76ee\u5149\u4e00\u51db\uff0c\u63a5\u53e3\u9053\uff1a\u201c\u6211\u8bf4\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u94c1\u8fbe\u4eba\u9053\uff1a\u201c\u8001\u524d\u8f88\u8bf4\uff1a\u8fd9\u5305\u836f\u65e0\u8272\u65e0\u5473\uff0c\u968f\u4fbf\u653e\u5728\u8336\u91cc\u3001\u9152\u91cc\u3001\u6c64\u91cc\u90fd\u53ef\u4ee5\uff0c\u800c \u4e14\u2026\u2026\u201d\u8bed\u58f0\u4e00\u987f\uff0c\u65e0\u6cd5\u7ee7\u7eed\u3002 \u3000\u3000\u6e29\u5982\u7389\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u8d44\u8d28\u7684\u786e\u5728\u666e\u901a\u4eba\u4e0a\uff0c\u8bb0\u5fc6\u529b\u5df2\u53ef\u79f0\u5f97\u4e0a\u662f\u4e0a\u4e0a\u4e4b\u9009\uff0c\u6211\u8fd8\u8bf4\u4e86\u4e9b \u4ec0\u4e48\uff0c\u4f60\u81ea\u4e5f\u8bb0\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u90a3\u4e48\u2026\u2026\u6211\u53ef\u66fe\u53eb\u4f60\u4e0b\u6bd2\u5728\u5c39\u51e1\u8336\u91cc\uff1f\u201d \u3000\u3000\u94c1\u8fbe\u4eba\u3001\u77f3\u5e73\u5bf9\u671b\u4e00\u773c\uff0c\u4e24\u4eba\u7a81\u7136\u4e00\u8d77\u8dea\u4e86\u4e0b\u53bb\uff0c\u94c1\u8fbe\u4eba\u9053\uff1a\u201c\u665a\u8f88\u4eec\u5e74\u5e7c\u65e0\u77e5\uff0c\u4f46\u671b \u8001\u524d\u8f88\u9ad8\u62ac\u8d35\u624b\uff0c\u6551\u665a\u8f88\u4e00\u547d\uff01\u201d \u3000\u3000\u6e29\u5982\u7389\u51b7\u51b7\u4e00\u7b11\uff0c\u505c\u7f13\u9053\uff1a\u201c\u6211\u5e76\u672a\u53eb\u4f60\u4e0b\u6bd2\u662f\u4e48\uff1f\u201d \u3000\u3000\u94c1\u8fbe\u4eba\u3001\u77f3\u5e73\u9053\uff1a\u201c\u8001\u524d\u8f88\u5e76\u672a\u53eb\u665a\u8f88\u4e0b\u6bd2\u3002\u201d \u3000\u3000\u6e29\u5982\u7389\u7f13\u7f13\u9053\uff1a\u201c\u6211\u65e2\u672a\u547d\u4f60\u7b49\u4e0b\u6bd2\uff0c\u53c8\u4f55\u66fe\u7b54\u5e94\u8fc7\u4e3a\u4f60\u7b49\u89e3\u5f00\u7a74\u9053\uff1f\u201d \u3000\u3000\u94c1\u8fbe\u4eba\u98a4\u58f0\u9053\uff1a\u201c\u8001\u524d\u8f88\u867d\u672a\u7b54\u5e94\uff0c\u4f46\u2026\u2026\u201d \u3000\u3000\u6e29\u5982\u7389\u7a81\u7136\u4ef0\u5929\u957f\u7b11\u8d77\u6765\uff0c\u7b11\u58f0\u5c16\u9510\u523a\u8033\uff0c\u7b11\u58f0\u4e2d\u5145\u6ee1\u8f7b\u8511\u4e4b\u610f\uff0c\u9690\u5728\u6811\u7a9f\u4e2d\u7684\u4e91\u4e2d\u7a0b \u4e0d\u7981\u4e3a\u4e4b\u6697\u53f9\u4e00\u58f0\uff0c\u5374\u542c\u6e29\u5982\u7389\u7b11\u58f0\u7a81\u53c8\u4e00\u987f\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u4e03\u7edd\u91cd\u624b\uff0c\u5931\u4f20\u767e\u5e74\uff0c\u5f53\u4eca\u5929 \u4e0b\uff0c\u53ea\u6709\u4e00\u4eba\u4f1a\u4f7f\uff0c\u6b64\u4eba\u81ea\u7136\u4fbf\u662f\u6211\u4e86\uff01\u4e5f\u53ea\u6709\u4e00\u4eba\u80fd\u89e3\uff0c\u6b64\u4eba\u4f60\u7b49\u53ef\u77e5\u9053\u662f\u8c01\uff1f\u201d \u3000\u3000\u94c1\u8fbe\u4eba\u3001\u77f3\u5e73\u9f50\u5730\u6115\u4e86\u4e00\u6115\uff0c\u9053\uff1a\u201c\u81ea\u7136\u662f\u8001\u524d\u8f88\u4e86\u3002\u201d \u3000\u3000\u6e29\u5982\u7389\u4ef0\u5929\u5927\u7b11\u9053\uff1a\u201c\u9519\u4e86\uff0c\u9519\u4e86\uff0c\u666e\u5929\u4e4b\u4e0b\uff0c\u552f\u4e00\u80fd\u89e3\u4e03\u7edd\u91cd\u624b\u4e4b\u4eba\uff0c\u5e76\u975e\u662f\u6211\u3002\u201d \u94c1\u8fbe\u4eba\u8131\u53e3\u60ca\u9053\uff1a\u201c\u662f\u8c01\uff1f\u201d \u3000\u3000\u6e29\u5982\u7389\u7b11\u58f0\u518d\u6b21\u4e00\u987f\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u6b64\u4eba\u4e43\u662f\u88ab\u4f60\u4eec\u6bd2\u6b7b\u7684\u5c39\u51e1\uff01\u201d \u3000\u3000\u6b64\u8bdd\u4e00\u51fa\uff0c\u5c31\u8fde\u4e91\u4e2d\u7a0b\u90fd\u4e0d\u7981\u4e3a\u4e4b\u4e00\u60ca\uff0c\u94c1\u8fbe\u4eba\u3001\u77f3\u5e73\uff0c\u66f4\u662f\u9762\u5982\u6b7b\u7070\uff0c\u5446\u4e86\u534a\u664c\uff0c\u5fc3 \u4e2d\u4ecd\u5b58\u4e00\u4e1d\u5e0c\u671b\uff0c\u54c0\u58f0\u9053\uff1a\u201c\u8001\u524d\u8f88\u2026\u2026\u665a\u8f88\u4eec\u2026\u2026\u201d \u3000\u3000\u6e29\u5982\u7389\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u4eec\u96be\u9053\u4ee5\u4e3a\u6211\u5728\u9a97\u4eba\u4e48\uff1f\u201d \u3000\u3000\u94c1\u8fbe\u4eba\u5782\u9996\u9053\uff1a\u201c\u665a\u8f88\u4e0d\u6562\uff0c\u4f46\u2026\u2026\u201d \u3000\u3000\u6e29\u52a0\u7389\u7f13\u7f13\u9053\uff1a\u201c\u6614\u5e74\u6211\u5f97\u5230\u8fd9\u4e03\u7edd\u91cd\u624b\u7684\u4e0d\u4f20\u79d8\u7b08\u65f6\uff0c\u5171\u6709\u4e24\u5377\uff0c\u4e0a\u5377\u662f\u7ec3\u529f\u5fc3\u6cd5\uff0c \u4e0b\u5377\u9664\u4e86\u89e3\u6cd5\u4e4b\u5916\uff0c\u8fd8\u6709\u4e00\u7bc7\u7ec3\u4e39\u79d8\u5f55\uff0c\u90a3\u65f6\u6211\u2026\u2026\u201d \u3000\u3000\u5979\u62ac\u5934\u671b\u5411\u5929\u4e0a\uff0c\u76ee\u5149\u4e2d\u4f3c\u4e4e\u53c8\u95ee\u8fc7\u4e00\u4e1d\u8f7b\u7ea2\u7684\u5149\u91c7\uff0c\u867d\u662f\u4e00\u95ea\u800c\u6ca1\uff0c\u4f46\u5374\u5df2\u8db3\u591f\u4ee4\u4eba \u770b\u51fa\u5979\u5f80\u4e8b\u4e2d\u7684\u9690\u79d8\u3002 \u3000\u3000\u7b49\u5230\u8fd9\u5149\u91c7\u6d88\u5931\u7684\u65f6\u5019\uff0c\u5979\u9762\u5bb9\u4fbf\u53c8\u7acb\u523b\u56de\u590d\u5230\u65b9\u624d\u7684\u51b7\u6f20\uff0c\u63a5\u53e3\u9053\uff1a\u201c\u90a3\u65f6\u6211\u4e00\u5fc3\u4ee5 \u4e3a\u4f60\u4eec\u7684\u5e08\u7236\u662f\u4e2a\u597d\u4eba\uff0c\u4e1d\u6beb\u672a\u66fe\u9632\u8303\u4e8e\u4ed6\uff0c\u54ea\u77e5\u2026\u2026\u201d \u3000\u3000\u5979\u8bed\u58f0\u518d\u6b21\u4e00\u987f\uff0c\u672c\u5df2\u51b7\u6f20\u4e4b\u9762\u5bb9\u4e0a\uff0c\u4f3c\u53c8\u52a0\u4e0a\u4e00\u5c42\u5bd2\u971c\uff1a\u201c\u54ea\u77e5\u4ed6\u867d\u6709\u4eba\u9762\uff0c\u5374\u65e0\u4eba \u5fc3\uff0c\u7adf\u4e58\u6211\u95ed\u5173\u516b\u5341\u4e00\u65e5\uff0c\u7ec3\u5230\u8fd9\u4e03\u7edd\u91cd\u624b\u4e4b\u9645\uff0c\u5c06\u6211\u6240\u85cf\u7684\u4e00\u4e9b\u73cd\u5b9d\u548c\u90a3\u79d8\u53d1\u7684\u4e0b\u5377\u4e00\u8d77 \u76d7\u53bb\u3002\u201d \u3000\u3000\u4e91\u4e2d\u7a0b\u76f4\u5230\u6b64\u523b\uff0c\u624d\u77e5\u9053\u4e11\u4eba\u6e29\u5982\u7389\u4e0e\u4e07\u5999\u771f\u541b\u4e4b\u95f4\u7adf\u6709\u5982\u6b64\u4e00\u6bb5\u5f80\u4e8b\uff0c\u4ed6\u867d\u7136\u5c4f\u606f\u9759 \u6c14\uff0c\u4e0d\u6562\u53d1\u51fa\u4efb\u4f55\u58f0\u606f\uff0c\u5374\u7981\u4e0d\u4f4f\u5fc3\u5934\u7684\u8df3\u52a8\uff0c\u4e5f\u7981\u4e0d\u4f4f\u51b7\u6c57\u7684\u6d41\u843d\uff0c\u56e0\u4e3a\u4ed6\u6df1\u77e5\u81ea\u5df1\u7684\u884c \u85cf\u82e5\u88ab\u4eba\u53d1\u73b0\uff0c\u7acb\u65f6\u4fbf\u662f\u4e0d\u4e86\u4e4b\u5c40\u3002 \u3000\u3000\u591c\u8272\u6e10\u6d53\uff0c\u4ed6\u6e10\u6e10\u770b\u4e0d\u6e05\u6e29\u5982\u7389\u7684\u9762\u5bb9\uff0c\u4f46\u5374\u53ef\u542c\u5f97\u51fa\u5979\u8bed\u58f0\u4e2d\u542b\u8574\u7684\u60c5\u611f\u2014\u2014\u7adf\u662f\u6df7 \u7740\u60b2\u6124\u3001\u5e7d\u6028\u4e0e\u54c0\u75db\u7684\u60c5\u611f\uff0c\u8fd9\u79cd\u60c5\u611f\u7adf\u4f1a\u53d1\u81ea\u4e11\u4eba\u6e29\u5982\u7389\u7684\u53e3\u4e2d\uff0c\u5b9e\u5728\u4ee4\u4e91\u4e2d\u7a0b\u65e0\u9650\u60ca \u5f02\u3002 \u3000\u3000\u94c1\u8fbe\u4eba\u3001\u77f3\u5e73\u53cc\u53cc\u4f0f\u5728\u5730\u4e0a\uff0c\u542c\u6e29\u5982\u7389\u5c06\u8bdd\u8bf4\u5b8c\uff0c\u4e24\u4eba\u9762\u9762\u76f8\u89d1\uff0c\u53ea\u542c\u6e29\u5982\u7389\u53c8\u81ea\u4e00\u58f0 \u6cc9\u6cc9\u591c\u557c\u822c\u7684\u51b7\u7b11\uff0c\u4ef0\u5929\u7b11\u9053\uff1a\u201c\u5c39\u51e1\u5440\u5c39\u51e1\uff0c\u6211\u603b\u7b97\u5bf9\u5f97\u8d77\u4f60\uff0c\u8ba9\u4f60\u5728\u9ec4\u660f\u8def\u4e0a\u4e5f\u4e0d\u4f1a\u5bc2 \u5bde\uff0c\u4f60\u8fd9\u4e24\u4e2a\u5fc3\u7231\u7684\u5f92\u5f1f\uff0c\u9a6c\u4e0a\u5c31\u8981\u53bb\u966a\u7740\u4f60\u4e86\u3002\u201d \u3000\u3000\u888d\u8896\u4e00\u62c2\uff0c\u518d\u6b21\u5189\u5189\u5411\u5c71\u540e\u98d8\u53bb\uff0c\u77f3\u5e73\u53cc\u62f3\u7d27\u63e1\uff0c\u5237\u5730\u957f\u8eab\u800c\u8d77\uff0c\u4f3c\u8981\u7b14\u76f4\u5411\u5979\u6251\u53bb\uff0c \u5374\u88ab\u94c1\u8fbe\u4eba\u4e00\u628a\u62c9\u4f4f\u8863\u895f\u3002 \u3000\u3000\u53ea\u542c\u94c1\u8fbe\u4eba\u6c89\u58f0\u9053\uff1a\u201c\u4f60\u8981\u5e72\u4ec0\u4e48\uff1f\u4f60\u6211\u5c82\u662f\u8fd9\u9b54\u5934\u7684\u654c\u624b\uff1f\u201d \u3000\u3000\u77f3\u5e73\u53cc\u76ee\u5706\u7741\uff0c\u4f4e\u53f1\u9053\uff1a\u201c\u7eb5\u975e\u5979\u4e4b\u654c\u624b\uff0c\u4e5f\u8981\u627e\u5979\u62fc\u4e0a\u4e00\u62fc\uff0c\u53cd\u6b63\u2026\u2026\u201d \u3000\u3000\u94c1\u8fbe\u4eba\u7a81\u5730\u5fae\u7b11\u2014\u2014\uff0c\u63a5\u53e3\u9053\uff1a\u201c\u4f60\u4ee5\u4e3a\u6211\u4eec\u518d\u65e0\u751f\u8def\u4e86\u4e48\uff1f\u201d \u3000\u3000\u77f3\u5e73\u6115\uff0c\u5450\u5450\u9053\uff1a\u201c\u96be\u9053\u2026\u2026\u96be\u9053\u2026\u2026\u201d \u3000\u3000\u94c1\u8fbe\u4eba\u4f38\u624b\u4e00\u62c2\u819d\u4e0a\u5c18\u571f\uff0c\u9762\u76ee\u4e0a\u6ee1\u9732\u5f97\u610f\u4e4b\u8272\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u4f60\u518d\u4ed4\u7ec6\u60f3\u4e0a\u4e00\u60f3\uff0c\u4f60\u6211 \u4e0d\u4f46\u5927\u6709\u751f\u8def\uff0c\u800c\u4e14\u8fd8\u53ef\u591a\u5f97\u8bb8\u591a\u597d\u5904\u3002\u201d \u3000\u3000\u77f3\u5e73\u53c8\u81ea\u4e00\u6115\uff0c\u4fbf\u8fde\u4e91\u4e2d\u7a0b\u4ea6\u81ea\u5927\u60d1\u4e0d\u89e3\uff0c\u53ea\u89c1\u94c1\u8fbe\u4eba\u7f13\u7f13\u4f38\u51fa\u62c7\u3001\u4e2d\u4e8c\u6307\uff0c\u4e24\u6307\u76f8 \u637b\uff0c\u556a\u5730\u53d1\u51fa\u4e00\u58f0\u6e05\u54cd\uff0c\u542b\u7b11\u9053\uff1a\u201c\u90a3\u5377\u79d8\u7b08\u7684\u4e0b\u5377\uff0c\u65e2\u7136\u8f7d\u6709\u89e3\u6cd5\uff0c\u4f60\u6211\u53ea\u8981\u5feb\u4e9b\u8d76\u56de \u53bb\uff0c\u5c06\u90a3\u5377\u79d8\u7b08\u5bfb\u51fa\uff0c\u5c82\u975e\u5bf9\u4f60\u6211\u2026\u2026\u201d \u3000\u3000\u8bed\u58f0\u672a\u4e86\uff0c\u77f3\u5e73\u5df2\u81ea\u5927\u559c\u63a5\u53e3\u9053\uff1a\u201c\u4f60\u5fc3\u667a\u4e4b\u7075\u5de7\uff0c\u7684\u786e\u975e\u6211\u80fd\u53ca\uff0c\u4f46\u662f\u90a3\u5377\u79d8\u7b08\u662f\u5728 \u4f55\u5904\uff0c\u96be\u9053\u4f60\u5df2\u80f8\u6709\u6210\u7af9\u4e48\uff1f\u201d \u3000\u3000\u94c1\u8fbe\u4eba\u4ef0\u5929\u4e00\u9635\u72c2\u7b11\uff0c\u7a81\u5730\u7b11\u58f0\u4e00\u987f\uff0c\u4e0a\u4e0b\u77a7\u4e86\u77f3\u5e73\u4e24\u773c\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u4e09\u5f1f\uff0c\u4f60\u6211\u81ea\u5e7c \u76f8\u5904\uff0c\u4ea4\u60c5\u53ef\u7b97\u4e0d\u9519\uff0c\u4f46\u6211\u8fd8\u89c9\u5f97\u4f60\u7a0d\u5acc\u72c2\u50b2\uff0c\u6709\u4e9b\u4e8b\uff0c\u4e00\u610f\u5b64\u884c\uff0c\u6839\u672c\u5c31\u672a\u5c06\u6211\u8fd9\u4e2a\u5e08\u54e5 \u770b\u5728\u773c\u91cc\u3002\u201d \u3000\u3000\u77f3\u5e73\u76ee\u5149\u4e00\u8f6c\uff0c\u966a\u7b11\u9053\uff1a\u201c\u5c0f\u5f1f\u5e74\u7eaa\u8f7b\u4e9b\uff0c\u6709\u8bb8\u591a\u4e8b\u662f\u8981\u5e08\u5144\u591a\u591a\u5305\u6db5\u4e00\u4e8c\u3002\u201d \u3000\u3000\u94c1\u8fbe\u4eba\u563f\u5730\u7b11\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u8fd9\u4e2a\u81ea\u7136\uff0c\u4f46\u2026\u2026\u4f46\u518d\u8fc7\u4e24\u5e74\uff0c\u4f60\u7684\u5e74\u7eaa\u5c31\u4e0d\u8f7b\u4e86\u3002\u201d \u3000\u3000\u77f3\u5e73\u8fde\u5fd9\u63a5\u53e3\u9053\uff1a\u201c\u65e5\u540e\u6211\u5bf9\u5e08\u5144\uff0c\u5fc5\u5b9a\u52a0\u500d\u7684\u606d\u656c\uff0c\u518d\u4e5f\u4e0d\u6562\u6709\u4e0d\u606d\u4e4b\u4e8b\u4e86\u3002\u201d \u3000\u3000\u4e91\u4e2d\u7a0b\u9690\u8eab\u6697\u5904\uff0c\u95fb\u4e4b\u4e0d\u7981\u6697\u53f9\uff0c\u8fd9\u5e08\u5144\u5f1f\u4e24\u4eba\uff0c\u4e0d\u4f46\u5bf9\u4eba\u5978\u8bc8\uff0c\u5c31\u8fde\u5bf9\u81ea\u5df1\u5144\u5f1f\uff0c\u7adf \u4e5f\u662f\u8fd9\u822c\u52fe\u5fc3\u6597\u89d2\uff0c\u4e92\u4e0d\u76f8\u8ba9\uff0c\u770b\u6765\u5929\u4e0b\u4eba\u7684\u5584\u6076\u4e4b\u5206\uff0c\u5f53\u771f\u662f\u5224\u5982\u4e91\u58e4\u7684\u4e86\u3002 \u3000\u3000\u53ea\u542c\u94c1\u8fbe\u4eba\u563b\u563b\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u6211\u4e24\u4eba\uff0c\u60c5\u5982\u5144\u5f1f\uff0c\u4e5f\u8c08\u4e0d\u5230\u4ec0\u4e48\u606d\u656c\u4e0d\u606d\u656c\u7684\uff0c\u53ea\u8981 \u4f60\u65e5\u540e\u8fd8\u6709\u513f\u5206\u8bb0\u5f97\u6211\u7684\u597d\u5904\u5c31\u662f\u4e86\u3002\u201d \u3000\u3000\u77f3\u5e73\u5782\u9996\u9053\uff1a\u201c\u81ea\u7136\u81ea\u7136\uff0c\u5e08\u5144\u7684\u5927\u6069\u5927\u5fb7\uff0c\u5c0f\u5f1f\u518d\u4e5f\u4e0d\u4f1a\u5fd8\u8bb0\u3002\u201d \u3000\u3000\u65b9\u624d\u4ed6\u8fd8\u5728\u4f60\u6211\u76f8\u79f0\uff0c\u6b64\u523b\u5374\u58f0\u58f0\u81ea\u79f0\u5c0f\u5f1f\uff0c\u94c1\u8fbe\u4eba\u7b11\u9053\uff0c\u201c\u5176\u5b9e\u5e08\u7236\u90a3\u672c\u79d8\u53d1\u7684\u85cf \u5904\uff0c\u4f60\u4e5f\u8be5\u77e5\u9053\uff0c\u53ea\u662f\u4f60\u5e73\u65e5\u4e0d\u751a\u7559\u610f\u7f62\u4e86\u3002\u201d \u3000\u3000\u7a81\u5730\u4e00\u58f0\u51b7\u7b11\uff0c\u81ea\u4e0a\u4f20\u4e0b\uff0c\u4e00\u4e2a\u68ee\u51b7\u5165\u9aa8\u7684\u58f0\u97f3\u4e00\u4e2a\u5b57\u4e00\u4e2a\u5b57\u4ed6\u8bf4\u9053\uff1a\u201c\u6211\u85cf\u5728\u54ea \u91cc\uff1f\u201d \u3000\u3000\u94c1\u8fbe\u4eba\u6d51\u8eab\u4e00\u9707\uff0c\u5982\u4e2d\u96f7\u8f70\u7535\u51fb\u3002 \u3000\u3000\u77f3\u5e73\u60f6\u7136\u56db\u987e\uff0c\u5982\u4e34\u5b89\u5371\uff0c\u7ec8\u4e8e\u4e00\u4f0f\u8170\u8eab\uff0c\u5237\u5730\u6a2a\u63a0\u4e24\u4e08\uff0c\u5982\u98de\u9003\u53bb\u3002 \u3000\u3000\u94c1\u8fbe\u4eba\u5374\u6251\u5730\u4e00\u58f0\uff0c\u8dea\u4e0b\u53bb\u3002 \u3000\u3000\u53ea\u89c1\u4e00\u6761\u9ed1\u5f71\uff0c\u968f\u7740\u4e00\u58f0\u51b7\u7b11\uff0c\u81ea\u53e4\u6811\u5bf9\u9762\u5c71\u58c1\u95f4\u5212\u7a7a\u63a0\u4e0b\uff0c\u77f3\u5e73\u65b9\u4e00\u8d77\u843d\uff0c\u8fd9\u4eba\u5f71\u4fbf \u5df2\u63a0\u5230\u4ed6\u9762\u524d\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u8fd8\u60f3\u9003\u4e48\uff1f\u201d \u3000\u3000\u77f3\u5e73\u60e8\u547c\u4e00\u58f0\uff0c\u8fde\u9000\u4e03\u6b65\uff0c\u683d\u5012\u5728\u5730\u4e0a\u3002 \u3000\u3000\u4e91\u4e2d\u7a0b\u95ea\u65e5\u671b\u53bb\uff0c\u53ea\u89c1\u4e00\u4e2a\u9ad8\u51a0\u7fbd\u58eb\uff0c\u4e30\u795e\u51b2\u5937\uff0c\u795e\u6001\u6f47\u6d12\u7684\u9880\u957f\u8001\u4eba\uff0c\u8de8\u8fc7\u77f3\u5e73\u5c38 \u4f53\uff0c\u4e00\u6b65\u4e00\u6b65\u5730\u8d70\u5230\u94c1\u8fbe\u4eba\u9762\u524d\u3002 \u3000\u3000\u94c1\u8fbe\u4eba\u4f0f\u5728\u5730\u4e0a\uff0c\u8fde\u8fde\u53e9\u9996\uff0c\u9053\uff1a\u201c\u5f1f\u5b50\u8be5\u6b7b\uff0c\u5f1f\u5b50\u8be5\u6b7b\uff01\u201d \u3000\u3000\u5c39\u51e1\u5c31\u7ad9\u5728\u90a3\u91cc\u4e00\u52a8\u4e0d\u52a8\uff0c\u4e5f\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u51b0\u51b7\u7684\u76ee\u5149\u4e2d\u7a81\u7136\u6709\u4e86\u4e00\u4e1d\u6696\u610f\uff0c\u53f9\u9053\uff1a \u201c\u4f60\u867d\u6709\u5341\u5206\u884c\u6076\u4e4b\u5fc3\uff0c\u5374\u65e0\u4e00\u5206\u884c\u6076\u4e4b\u80fd\uff0c\u4f60\u5c06\u90a3\u5305\u8ff7\u836f\u5012\u5728\u6211\u8336\u91cc\uff0c\u6211\u6697\u4e2d\u65e9\u5df2\u770b\u5f97\u6e05 \u6e05\u695a\u695a\uff0c\u53ea\u662f\u6211\u4e0d\u77e5\u4f60\u4e24\u4eba\u7a76\u7adf\u4e3a\u4f55\u5982\u6b64\uff0c\u662f\u4ee5\u6545\u4f5c\u4e0d\u77e5\uff0c\u53c8\u4e58\u4f60\u4e24\u4eba\u4e0d\u89c1\uff0c\u5c06\u8336\u6362\u4e86\u4e00 \u58f6\uff0c\u518d\u5f53\u4f60\u4e24\u4eba\u4e4b\u9762\u559d\u4e0b\u3002\u201d \u3000\u3000\u94c1\u8fbe\u4eba\u5782\u4e0b\u5934\u53bb\uff0c\u518d\u4e5f\u4e0d\u6562\u62ac\u8d77\uff0c\u5c39\u51e1\u53c8\u9053\uff1a\u201c\u4eca\u6668\u6211\u89c1\u4f60\u4e24\u4eba\u5728\u6211\u7a97\u5916\u770b\u4e86\u534a\u664c\uff0c\u5374 \u53c8\u4e0d\u6562\u5165\u5ba4\u67e5\u770b\uff0c\u5c31\u5306\u5306\u8d70\u4e86\uff0c\u6211\u5c31\u4e00\u76f4\u8ddf\u5728\u4f60\u4eec\u8eab\u540e\uff0c\u65b9\u624d\u4f60\u4e24\u4eba\u548c\u90a3\u6e29\u52a0\u7389\u7684\u8c08\u8bdd\uff0c\u6211 \u4e5f\u5728\u5c71\u58c1\u4e0a\u542c\u5f97\u6e05\u6e05\u695a\u695a\u3002\u201d \u3000\u3000\u4e91\u4e2d\u7a0b\u6697\u53f9\u4e00\u58f0\uff0c\u5fd6\u9053\uff1a\u201c\u8fd9\u5c39\u51e1\u4e4b\u80fd\uff0c\u8db3\u4ee5\u6d4e\u5176\u4e3a\u6076\uff0c\u6b64\u4eba\u4e4b\u53ef\u6015\uff0c\u5f53\u771f\u662f\u5c24\u5728\u866b\u86c7 \u731b\u517d\u4e4b\u4e0a\uff0c\u600e\u80fd\u8ba9\u4ed6\u7559\u5728\u4e16\u4e0a\uff1f\u201d \u3000\u3000\u4e00\u5ff5\u81f3\u6b64\uff0c\u4ed6\u5fc3\u4e2d\u4e0d\u7981\u5927\u751f\u4fa0\u4e49\u4e4b\u5fc3\uff0c\u65b9\u81ea\u6697\u4e2d\u5bfb\u601d\uff0c\u8be5\u5982\u4f55\u4e3a\u4e16\u4eba\u9664\u5374\u6b64\u5bb3\uff0c\u54ea\u77e5\u76ee \u5149\u52a8\u5904\uff0c\u7a81\u5730\u53c8\u89c1\u4e00\u6761\u4eba\u5f71\uff0c\u5189\u5189\u81ea\u5c71\u540e\u98d8\u51fa\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u5c39\u51e1\uff0c\u4f60\u8fd9\u6837\u505a\u4e8b\uff0c\u4e0d\u662f\u5927\u4e0d\u516c \u5e73\u4e86\u4e48\uff1f\u201d \u3000\u3000\u626c\u624b\u4e00\u6ce8\u5149\u5f71\uff0c\u7b14\u76f4\u51fb\u5411\u94c1\u8fbe\u4eba\u8eab\u4e0a\u3002 \u3000\u3000\u94c1\u8fbe\u4eba\u5374\u5df2\u4e00\u58f0\u60e8\u547c\uff0c\u5728\u5730\u4e0a\u8fde\u6eda\u6570\u6eda\uff0c\u6eda\u5230\u65e9\u5df2\u6c14\u7edd\u4e86\u7684\u77f3\u5e73\u8eab\u4fa7\uff0c\u8fd9\u5144\u5f1f\u4e24\u4eba\u7ec8\u4e8e \u6b7b\u5728\u4e00\u5904\u3002 \u3000\u3000\u5c39\u51e1\u5927\u60ca\u4e4b\u4e0b\uff0c\u970d\u7136\u8f6c\u8eab\uff0c\u53ea\u89c1\u6e29\u5982\u7389\u67af\u7626\u7684\u8eab\u5f62\uff0c\u5189\u5189\u98d8\u6765\uff0c\u51b7\u51b7\u63a5\u9053\uff1a\u201c\u8fd9\u4e24\u4eba\u6076 \u884c\u5982\u4e00\uff0c\u600e\u80fd\u8ba9\u4ed6\u4eec\u4e00\u6b7b\u4e00\u751f\uff0c\u6211\u751f\u5e73\u6700\u4e0d\u60ef\u89c1\u4e0d\u5e73\u4e4b\u4e8b\uff0c\u7d22\u6027\u8fde\u4ed6\u4e5f\u4ee3\u4f60\u4e00\u5e76\u9664\u53bb\u4e86\u7684 \u597d\u3002\u201d \u3000\u3000\u5c39\u51e1\u76ee\u5149\u4e00\u8f6c\uff0c\u9762\u8272\u8fde\u53d8\u6570\u6b21\uff0c\u7a81\u5730\u5fae\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u597d\u6781\uff0c\u597d\u6781\uff0c\u6211\u4e5f\u6b63\u6709\u6b64\u610f\uff0c\u8fd9 \u7b49\u53db\u5f92\u7559\u5728\u4e16\u4e0a\u4e5f\u662f\u65e0\u7528\uff01\u201d \u3000\u3000\u6e29\u5982\u7389\u51b7\u54fc\u4e00\u58f0\uff0c\u76ee\u5149\u77ac\u4e5f\u4e0d\u77ac\uff0c\u51dd\u6ce8\u5728\u4ed6\u8eab\u4e0a\u3002 \u3000\u3000\u53ea\u89c1\u4ed6\u9762\u4e0a\u7b11\u5bb9\u8d8a\u53d1\u5f00\u6717\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u5982\u7389\uff0c\u591a\u5e74\u4e0d\u89c1\uff0c\u60f3\u4e0d\u5230\u4f60\u548c\u4ee5\u524d\u8fd8\u662f\u4e00 \u6837\u2026\u2026\u201d\u4fef\u9996\u957f\u53f9\u4e00\u58f0\uff1a\u201c\u8fd9\u4e9b\u5e74\u6765\u2026\u2026\u5509\uff01\u6211\u5374\u8001\u5f97\u591a\u4e86\u3002\u201d \u3000\u3000\u6e29\u5982\u7389\u53c8\u81ea\u51b7\u54fc\u4e00\u58f0\uff0c\u76ee\u5149\u4f9d\u65e7\u77ac\u4e5f\u4e0d\u77ac\u5730\u671b\u5728\u4ed6\u8eab\u4e0a\u3002 \u3000\u3000\u5c39\u51e1\u7f13\u7f13\u4f38\u51fa\u624b\u638c\uff0c\u4e00\u637b\u9888\u4e0b\u957f\u987b\uff0c\u4ef0\u5929\u4e00\u53f9\uff0c\u53c8\u9053\uff1a\u201c\u5c81\u6708\u50ac\u4eba\uff0c\u5e74\u534e\u4e0d\u518d\uff0c\u6211\u6bcf\u4e00 \u5fc6\u53ca\u4f60\u6211\u6614\u5e74\u76f8\u5904\u7684\u5149\u666f\uff0c\u5c31\u4f1a\u89c9\u5f97\u6101\u6000\u4e0d\u80fd\u81ea\u9063\u2026\u2026\u5982\u7389\uff0c\u4f60\u53ef\u8bb0\u5f97\u6211\u4eec\u5728\u5c71\u5dc5\u6811\u4e0b\uff0c\u4e3e \u676f\u5bf9\u6708\uff0c\u5171\u795d\u957f\u751f\u7684\u5149\u666f\u2026\u2026\u5509\uff01\u6211\u4e0d\u6b62\u4e00\u6b21\u60f3\uff0c\u603b\u89c9\u4eba\u751f\u5982\u6b64\u77ed\u6682\uff0c\u7edd\u65e0\u767e\u5e74\u4e0d\u6563\u4e4b\u4f1a\uff0c \u5012\u4e0d\u5982\u5f7c\u6b64\u90fd\u5728\u5fc3\u4e2d\u7559\u4e0b\u4e00\u6bb5\u56de\u5fc6\u5982\u751f\u3002\u5509\uff01\u8fd9\u6b63\u662f\u76f8\u89c1\u4e0d\u5982\u4e0d\u89c1\u2026\u2026\u5509\uff01\u5982\u738b\uff0c\u4f60\u8bf4\u53ef\u662f \u4e48\uff1f\u201d \u3000\u3000\u76ee\u5149\u8f6c\u5904\uff0c\u53ea\u89c1\u90a3\u6e29\u5982\u7389\u4ecd\u5728\u51b7\u51b7\u671b\u7740\u81ea\u5df1\uff0c\u7a81\u53c8\u957f\u53f9\u4e86\u4e00\u58f0\uff0c\u4f4e\u541f\u9053\uff1a\u201c\u6b64\u60c5\u53ef\u5f85\u6210 \u8ffd\u5fc6\uff0c\u53ea\u662f\u5f53\u2026\u2026\u65f6\u2026\u2026\u5df2\u2026\u2026\u60d8\u2026\u2026\u7136\u2026\u2026\u201d \u3000\u3000\u6e29\u5982\u7389\u7a81\u5730\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u8fd9\u4e9b\u8bdd\u82e5\u6362\u4e86\u591a\u5e74\u4ee5\u524d\u8ba9\u6211\u542c\u4e86\uff0c\u53ea\u6015\u6211\u53c8\u2026\u2026\u201d \u3000\u3000\u5634\u5507\u4e00\u95ed\uff0c\u51b7\u54fc\u6570\u58f0\u3002 \u3000\u3000\u5c39\u51e1\u9053\uff1a\u201c\u5e74\u534e\u867d\u5df2\u901d\u53bb\uff0c\u6b64\u60c5\u5374\u6c38\u4e0d\u53d8\uff0c\u96be\u9053\u4eca\u65e5\u53c8\u548c\u4ee5\u524d\u6709\u4ec0\u4e48\u4e0d\u540c\u4e48\u2026\u2026\u201d \u3000\u3000\u6e29\u5982\u7389\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u4e9b\u82b1\u8a00\u5de7\u8bed\uff0c\u5bf9\u522b\u4eba\u8bf4\u522b\u4eba\u4e5f\u8bb8\u8fd8\u4f1a\u4e0a\u5f53\uff0c\u6211\u5374\u5df2\u542c\u5f97\u817b\u4e86\u3002\u201d \u3000\u3000\u5c39\u51e1\u5446\u4e86\u4e00\u5446\uff0c\u76ee\u5149\u8fde\u8f6c\u6570\u8f6c\uff0c\u7ec8\u53c8\u5f3a\u7b11\u4e00\u58f0\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u5982\u7389\uff0c\u6211\u77e5\u9053\u4f60\u5fc3\u91cc\u5fc5\u5b9a\u5bf9 \u6211\u6709\u8bb8\u591a\u7684\u8bef\u4f1a\uff0c\u4f46\u662f\u6211\u2026\u2026\u201d \u3000\u3000\u6e29\u52a0\u7389\u7a81\u5730\u5389\u53f1\u4e00\u58f0\uff1a\u201c\u4e0d\u8981\u8bf4\u4e86\u2026\u2026\u201d \u3000\u3000\u7f13\u7f13\u5782\u4e0b\u5934\uff0c\u4f3c\u4e4e\u6697\u4e2d\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u4ef0\u9996\u53c8\u9053\uff1a\u201c\u6b63\u5982\u4f60\u6240\u8bf4\uff0c\u5e74\u534e\u901d\u53bb\uff0c\u6211\u5df2\u8001\u4e86\uff0c \u8001\u4e86\u2026\u2026\u201d \u3000\u3000\u76ee\u5149\u51dd\u6ce8\uff0c\u7adf\u7a81\u7136\u4ef0\u5929\u72c2\u7b11\u8d77\u6765\uff0c\u7b11\u58f0\u5c16\u5389\uff0c\u6ee1\u542b\u60b2\u6124\u4e4b\u60c5\u3002 \u3000\u3000\u5c39\u51e1\u67d4\u58f0\u9053\uff1a\u201c\u4f60\u6ca1\u6709\u8001\uff0c\u53ea\u662f\u2026\u2026\u201d \u3000\u3000\u6e29\u5982\u7389\u72c2\u7b11\u58f0\u63a5\u53e3\u9053\uff1a\u201c\u5e74\u8001\u6210\u7cbe\uff0c\u6211\u518d\u4e5f\u4e0d\u4f1a\u4e0a\u4f60\u7684\u5f53\uff0c\u53d7\u4f60\u7684\u9a97\u4e86\uff0c\u76f4\u5230\u6b64\u523b\uff0c\u4f60 \u8fd8\u4ee5\u4e3a\u4f60\u806a\u660e\uff0c\u6bd4\u4efb\u4f55\u4eba\u90fd\u806a\u660e\uff0c\u5374\u4e0d\u77e5\u6211\u5df2\u6bd4\u4f60\u806a\u660e\u8bb8\u591a\u3002\u201d \u3000\u3000\u5c39\u51e1\u5e72\u54b3\u4e00\u58f0\u9053\uff1a\u201c\u4f60\u7684\u806a\u660e\u624d\u667a\u4e00\u76f4\u5728\u6211\u4e4b\u4e0a\u2026\u2026\u201d \u3000\u3000\u4ed6\u8fd9\u756a\u606d\u7ef4\u4e4b\u8a00\uff0c\u6e29\u5982\u7389\u5374\u4e00\u5982\u672a\u95fb\uff0c\u81ea\u7ba1\u63a5\u53e3\u9053\uff1a\u201c\u6211\u65e9\u5c31\u7b97\u5b9a\u8fd9\u4e24\u4e2a\u8822\u624d\u4e00\u5b9a\u6bd2\u4e0d \u5012\u4f60\uff0c\u4e5f\u65e9\u5df2\u7b97\u5b9a\u4f60\u4e00\u5b9a\u4f1a\u8ddf\u7740\u4ed6\u4eec\u4e0a\u5929\u76ee\u5c71\u6765\uff0c\u679c\u7136\u5374\u4e0d\u51fa\u6211\u6240\u6599\u3002\u201d \u3000\u3000\u5979\u72c2\u7b11\u6570\u58f0\uff0c\u63a5\u9053\uff1a\u201c\u4ee5\u524d\u6211\u4e8b\u4e8b\u9003\u4e0d\u51fa\u4f60\u7684\u8ba1\u7b97\u4e4b\u4e2d\uff0c\u73b0\u5728\u5374\u8f6e\u5230\u4f60\u4e86\u3002\u201d \u3000\u3000\u5c39\u51e1\u6545\u610f\u957f\u53f9\u4e00\u58f0\uff0c\u5782\u9996\u65e0\u8bed\uff0c\u76ee\u5149\u95ea\u52a8\u95f4\uff0c\u5fc3\u91cc\u5374\u53c8\u5728\u6253\u7b97\u8131\u8eab\u4e4b\u8ba1\u3002 \u3000\u3000\u6e29\u5982\u7389\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff0c\u201c\u4f60\u5fc3\u91cc\u4e0d\u5fc5\u518d\u6253\u8131\u8eab\u4e4b\u8ba1\uff0c\u8fd9\u4e9b\u5e74\u6765\u6211\u4e00\u76f4\u82e6\u7ec3\u8f7b\u529f\uff0c\u4f60\u5982\u4e0d \u4fe1\uff0c\u5c3d\u7ba1\u8bd5\u8bd5\u597d\u4e86\u3002\u201d \u3000\u3000\u5c39\u51e1\u5fc3\u5934\u4e00\u60ca\uff0c\u4f46\u5fc3\u5ff5\u8f6c\u52a8\u95f4\uff0c\u53c8\u81ea\u5fd6\u9053\uff1a\u201c\u5979\u4e00\u76f4\u82e6\u7ec3\u8f7b\u529f\uff0c\u522b\u7684\u529f\u592b\u4e00\u5b9a\u6401\u4e0b\u5f88 \u591a\uff0c\u6211\u5982\u5168\u529b\u4e0e\u5979\u4e00\u62fc\uff0c\u4e5f\u672a\u5fc5\u4e0d\u80fd\u80dc\u5979\u3002\u201d \u3000\u3000\u6e29\u5982\u7389\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u4e5f\u4e0d\u5fc5\u60f3\u4e0e\u6211\u4e00\u8f83\u8eab\u624b\uff0c\u82e5\u662f\u8bba\u6b66\u529f\uff0c\u4f60\u662f\u4e07\u4e07\u4e0d\u53ca\u6211\u7684\uff0c\u4e14\u4e0d\u8bba \u522b\u7684\uff0c\u5c31\u53ea\u90a3\u4e03\u7ecf\u79d8\u7b08\u4e0a\u5377\u6240\u8f7d\u624b\u6cd5\uff0c\u5c31\u7edd\u975e\u4f60\u80fd\u62b5\u6321\uff0c\u4e0d\u7136\u2014\u2014\u54fc\u54fc\uff0c\u4f60\u82e5\u4e0d\u4fe1\uff0c\u4e5f\u5c3d\u53ef \u8bd5\u4e0a\u4e00\u8bd5\u3002\u201d \u3000\u3000\u5c39\u51e1\u62ac\u5934\u4e00\u6115\uff0c\u7ec8\u4e8e\u957f\u53f9\u9053\uff1a\u201c\u6570\u5e74\u6765\u6211\u4e00\u76f4\u60f3\u518d\u89c1\u4f60\u4e00\u9762\uff0c\u6b64\u523b\u600e\u4f1a\u6709\u8131\u8eab\u4e4b\u610f\uff0c\u66f4 \u4e0d\u4f1a\u60f3\u548c\u4f60\u4e00\u8f83\u8eab\u624b\uff0c\u5982\u7389\uff0c\u4f60\u60f3\u5f97\u672a\u514d\u592a\u591a\u4e86\u5427\uff01\u201d \u3000\u3000\u6e29\u5982\u7389\u5927\u7b11\u9053\uff1a\u201c\u6211\u60f3\u5f97\u5927\u591a\u4e86\u4e48\uff01\u2026\u2026\u563f\u563f\uff0c\u4f60\u5fc3\u91cc\u5728\u60f3\u4ec0\u4e48\uff0c\u4f60\u81ea\u5df1\u81ea\u7136\u77e5\u9053\uff01\u201d \u3000\u3000\u5c39\u51e1\u9053\uff1a\u201c\u6211\u5fc3\u91cc\u5728\u60f3\u6b66\u6797\u4e2d\u98ce\u6ce2\u5982\u6b64\u9669\u6076\uff0c\u4f60\u6211\u5e74\u7eaa\u53c8\u90fd\u8fd9\u4e48\u5927\u4e86\uff0c\u4e0d\u5982\u65e9\u4e9b\u5bfb\u4e2a\u98ce \u666f\u5e7d\u7f8e\u4e4b\u5904\u4e00\u8d77\u5ea6\u8fc7\u4f59\u5e74\uff01\u201d \u3000\u3000\u4ed6\u4e0d\u4f46\u8a00\u8bed\u6e29\u67d4\uff0c\u800c\u4e14\u8bed\u58f0\u66f4\u6781\u662f\u52a8\u542c\uff0c\u6e29\u5982\u7389\u7f13\u7f13\u5782\u4e0b\u773c\u5e18\uff0c\u4f3c\u4e4e\u5df2\u6709\u51e0\u5206\u88ab\u4ed6\u6253 \u52a8\u3002 \u3000\u3000\u5c39\u51e1\u76ee\u5149\u4e00\u9635\u95ea\u52a8\uff0c\u5634\u89d2\u4e0d\u7981\u53c8\u6cdb\u8d77\u4e00\u4e1d\u7b11\u610f\uff0c\u67d4\u58f0\u53c8\u9053\uff1a\u201c\u5982\u7389\uff0c\u4f60\u4e14\u60f3\u60f3\uff0c\u4f60\u6211\u4e00 \u751f\u4e2d\u53f1\u54a4\u6c5f\u6e56\uff0c\u5230\u5934\u6765\u53c8\u80fd\u7559\u4e0b\u4e9b\u4ec0\u4e48\u2026\u2026\u5509\uff0c\u9664\u4e86\u4f60\u5fc3\u91cc\u8fd8\u6709\u6211\uff0c\u6211\u5fc3\u91cc\u8fd8\u6709\u4f60\u2026\u2026\u201d \u3000\u3000\u8fd9\u4e24\u53e5\u8bdd\u8bf4\u5f97\u66f4\u662f\u7f20\u7ef5\u60b1\u607b\uff0c\u8361\u6c14\u56de\u80a0\uff0c\u8bf4\u5230\u540e\u6765\uff0c\u4ed6\u4f3c\u4e4e\u60c5\u611f\u6fc0\u8361\uff0c\u4e0d\u80fd\u81ea\u5df1\uff0c\u4f38\u624b \u8f7b\u8f7b\u4e00\u62ed\u773c\u89d2\uff0c\u7f13\u7f13\u5782\u4e0b\u5934\u53bb\u3002 \u3000\u3000\u54ea\u77e5\u6e29\u5982\u7389\u7a81\u7136\u53c8\u4ef0\u5929\u72c2\u7b11\u4e86\u8d77\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u5fc3\u91cc\u6709\u6211\uff0c\u6211\u5fc3\u91cc\u6709\u4f60\u2026\u2026\u54c8\u54c8\uff0c\u54c8 \u54c8\uff0c\u4f59\u751f\uff0c\u4f59\u751f\u2026\u2026\u201d \u3000\u3000\u7b11\u58f0\u4e00\u987f\uff1a\u201c\u8001\u5b9e\u544a\u8bc9\u4f60\uff0c\u6211\u65e9\u5df2\u6ca1\u6709\u518d\u6d3b\u4e0b\u53bb\u7684\u5ff5\u5934\u4e86\uff0c\u4f60\u80af\u966a\u6211\u6b7b\u5417\uff1f\u201d \u3000\u3000\u5c39\u51e1\u5f3a\u7b11\u9053\uff1a\u201c\u5982\u7389\uff0c\u597d\u6b7b\u4e0d\u5982\u6b79\u6d3b\uff0c\u4f60\u8bf4\u8fd9\u4e9b\u8bdd\u5e72\u4ec0\u4e48\uff0c\u4f60\u6211\u8eab\u4f53\u90fd\u8fd8\u5065\u6717\uff0c\u81f3\u5c11\u8fd8 \u53ef\u518d\u6d3b\u4e0a\u5341\u5e74\u4e8c\u5341\u5e74\u7684\u3002\u201d \u3000\u3000\u6e29\u5982\u7389\u9053\uff1a\u201c\u4f60\u4e0d\u80af\u966a\u6211\u53bb\u6b7b\uff0c\u6211\u4e0d\u602a\u4f60\uff0c\u4f60\u867d\u5bf9\u6211\u4e0d\u597d\uff0c\u4f46\u662f\u6211\u4e5f\u4e0d\u4f1a\u6740\u4f60\u2026\u2026 \u6211\u2026\u2026\u6211\u53ea\u8981\u4f60\u518d\u7737\u6211\u505a\u4e00\u4ef6\u4e8b\u2026\u2026\u201d \u3000\u3000\u8bf4\u5230\u540e\u6765\uff0c\u5979\u8bed\u58f0\u4e2d\u7a81\u7136\u53c8\u6709\u51c4\u51c9\u5e7d\u6028\u4e4b\u60c5\uff0c\u4e00\u9635\u6d53\u4e91\uff0c\u62d6\u8fc7\u6708\u8272\uff0c\u591c\u8272\u5f88\u6df1\u4e86\u3002 \u3000\u3000\u4e00\u9635\u6d53\u4e91\uff0c\u63a9\u8fc7\u6708\u8272\uff0c\u6e29\u747e\u4ef0\u9762\u9053\uff1a\u201c\u591c\u5df2\u5f88\u6df1\u4e86\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u76ee\u5149\u4e00\u8f6c\uff0c\u9053\uff1a\u201c\u90a3\u53e4\u5e99\u5df2\u5728\u524d\uff0c\u4e0d\u77e5\u6e29\u5982\u7389\u662f\u5426\u5df2\u53bb\u3002\u201d \u3000\u3000\u6e29\u747e\u9053\uff1a\u201c\u5979\u8bf4\u8981\u53bb\uff0c\u60f3\u5fc5\u4e00\u5b9a\u4f1a\u53bb\u7684\u3002\u201d \u3000\u3000\u4f38\u624b\u633d\u4f4f\u5353\u957f\u537f\u7684\u81c2\u8180\uff0c\u4e24\u4eba\u4e3e\u6b65\u4e4b\u95f4\uff0c\u4fbf\u5df2\u63a0\u5165\u53e4\u5e99\uff0c\u591c\u8272\u6df1\u6c89\u4e2d\u7684\u4f5b\u6bbf\uff0c\u795e\u53f0\u4f5b \u50cf\uff0c\u4e00\u65e0\u6539\u53d8\uff0c\u5782\u76ee\u4f4e\u7709\u7684\u5927\u4f5b\uff0c\u4e5f\u4f9d\u7136\u50cf\u662f\u5728\u601c\u60dc\u7740\u4e16\u4e0a\u7684\u65e0\u9650\u6101\u82e6\uff0c\u4f46\u5353\u957f\u537f\u4e0e\u6e29\u747e\u7684 \u5fc3\u5883\uff0c\u4eca\u591c\u4e0e\u6628\u591c\u5374\u5df1\u4e0d\u77e5\u6539\u53d8\u4e86\u591a\u5c11\u3002 \u3000\u3000\u4eba\u5f71\u79fb\u52a8\uff0c\u6708\u5149\u5982\u68a6\uff0c\u4ed6\u4fe9\u5728\u90a3\u795e\u50cf\u524d\u7684\u84b2\u56e2\u4e0a\u4e95\u80a9\u5750\u4e86\u4e0b\u6765\uff0c\u5fc3\u4e2d\u6b63\u662f\u7231\u6068\u55d4\u559c\uff0c\u767e \u611f\u4ea4\u9519\uff0c\u8c01\u4e5f\u4e0d\u77e5\u8be5\u8bf4\u4ec0\u4e48\u3002 \u3000\u3000\u6bbf\u540e\u5e7d\u7136\u8f6c\u51fa\u4e00\u7247\u706f\u5149\u7684\u4e24\u6761\u4eba\u5f71\uff0c\u4e00\u822c\u7a88\u7a95\uff0c\u4e00\u822c\u9ad8\u77ee\uff0c\u5353\u957f\u537f\u3001\u6e29\u747e\u4e00\u8d77\u56de\u9996\u671b \u53bb\uff0c\u4e00\u8d77\u8131\u53e3\u9053\uff1a\u201c\u4f60\u4eec\u5df2\u6765\u4e86\u4e48\uff1f\u201d \u3000\u3000\u5c0f\u73b2\u5fae\u5fae\u4e00\u7b11\uff0c\u5c06\u5802\u4e2d\u4e24\u76cf\u94dc\u706f\u653e\u5230\u795e\u53f0\u4e0a\uff0c\u5c0f\u743c\u63a5\u53e3\u9053\uff1a\u201c\u6211\u4e24\u4eba\u65e9\u5c31\u6765\u4e86\uff0c\u7956\u59d1\u5979 \u8001\u4eba\u5bb6\u4e5f\u5c31\u8981\u6765\u4e86\u3002\u201d \u3000\u3000\u4e0e\u5c0f\u73b2\u5782\u624b\u7acb\u5728\u795e\u53f0\u8fb9\uff0c\u4e0d\u518d\u671b\u6e29\u747e\u4e00\u773c\uff0c\u4e8e\u662f\u5927\u6bbf\u4e2d\u53ea\u6709\u56db\u4eba\u5fc3\u6c14\u7684\u8df3\u52a8\u58f0\uff0c\u5212\u7834\u4e86 \u65e0\u9650\u7684\u6c89\u9ed8\u3002 \u3000\u3000\u4e00\u9635\u98ce\u5439\u5165\u6bbf\u4e2d\uff0c\u5fae\u5e26\u5bd2\u610f\u7684\u665a\u98ce\uff0c\u5439\u5165\u4e00\u7247\u843d\u53f6\uff0c\u4e5f\u5439\u4eba\u4e00\u6761\u4eba\u5f71\uff0c\u968f\u843d\u53f6\u4e00\u8d77\u5189\u5189 \u98d8\u843d\u3002\u5353\u957f\u537f\u3001\u6e29\u747e\u3001\u5c0f\u73b2\u3001\u5c0f\u743c\uff0c\u4e00\u8d77\u8f6c\u76ee\u671b\u53bb\uff0c\u4e00\u8d77\u60ca\u547c\u51fa\u58f0\uff1a\u201c\u662f\u4f60\uff01\u201d \u3000\u3000\u8fd9\u4eba\u5f71\u5fae\u5fae\u4e00\u7b11\uff0c\u5374\u662f\u5c39\u51e1\uff0c\u7b11\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u4e48\uff1f\u201d \u3000\u3000\u8d1f\u624b\u8e31\u4e86\u4e24\u6b65\uff0c\u7a81\u5730\u9762\u5bf9\u5353\u957f\u537f\u7f13\u7f13\u9053\uff1a\u201c\u606d\u559c\u4e16\u5144\uff0c\u4ee4\u5c0a\u4e0e\u4ee4\u5802\u7684\u5927\u4ec7\u4eca\u65e5\u5c31\u53ef\u62a5\u5374 \u4e86\u3002\u201d \u3000\u3000\u53c8\u8d1f\u624b\u8e31\u4e86\u4e24\u6b65\uff0c\u8d70\u5230\u58c1\u95f4\u4e0a\uff0c\u671b\u7740\u58c1\u4e0a\u5df2\u7136\u5265\u843d\u4e86\u5927\u534a\u7684\u58c1\u753b\u3002 \u3000\u3000\u4e00\u65f6\u4e4b\u95f4\uff0c\u5353\u957f\u537f\u5fc3\u4e2d\u53cd\u89c9\u7591\u4e91\u5927\u8d77\uff0c\u4f5c\u58f0\u4e0d\u5f97\uff0c\u53ea\u542c\u53c8\u662f\u4e00\u9635\u98ce\u58f0\uff0c\u6bbf\u4e2d\u53c8\u81ea\u98d8\u4e0b\u4e00 \u6761\u4eba\u5f71\uff0c\u5c0f\u73b2\u3001\u5c0f\u743c\u4e00\u8d77\u547c\u9053\uff1a\u201c\u7956\u59d1\u6765\u4e86\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u3001\u6e29\u747e\u4f46\u89c9\u5fc3\u5934\u4e00\u51db\uff0c\u70ed\u8840\u4e0a\u6d8c\uff0c\u53ea\u542c\u6e29\u5982\u7389\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u4eec\u6765\u5f97\u5012\u65e9\uff01\u201d \u3000\u3000\u5353\u957f\u537f\u3001\u6e29\u747e\u5bf9\u671b\u4e00\u773c\uff0c\u6e29\u5982\u7389\u51c4\u7136\u4e00\u7b11\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u4eec\u5fc3\u5207\uff0c\u590d\u4ec7\u8fde\u4e00\u65f6\u4e00\u523b\u90fd\u7b49 \u4e0d\u53ca\u7684\uff0c\u662f\u4e48\uff1f\u201d \u3000\u3000\u5353\u957f\u537f\u6602\u7136\u9053\uff1a\u201c\u7236\u6bcd\u4e4b\u4ec7\uff0c\u4e0d\u5171\u6234\u5929\uff0c\u665a\u8f88\u4e00\u65e5\u4e0d\u80fd\u62a5\u6b64\u6df1\u4ec7\uff0c\u5b9e\u662f\u5bdd\u98df\u96be\u5b89\u3002\u201d \u3000\u3000\u6e29\u5982\u7389\u51b7\u7b11\u4e00\u58f0\uff0c\u63a5\u53e3\u9053\uff1a\u201c\u6740\u4f60\u7236\u6bcd\u7684\u4ec7\u4eba\uff0c\u6b64\u523b\u4ff1\u90fd\u5728\u4f60\u773c\u524d\uff0c\u4f46\u4f60\u53ef\u66fe\u60f3\u5230\u8fc7\uff0c \u5c31\u51ed\u4f60\u7684\u6b66\u529f\uff0c\u4eca\u65e5\u8981\u60f3\u62a5\u4ec7\uff0c\u662f\u5426\u53ef\u80fd\uff1f\u201d \u3000\u3000\u5353\u957f\u537f\u5251\u7709\u4e00\u8f69\uff0c\u6717\u7136\u9053\uff1a\u201c\u5728\u4e0b\u4eca\u65e5\u6b64\u6765\uff0c\u65e9\u5df2\u672a\u5c06\u751f\u6b7b\u4e4b\u4e8b\u653e\u5728\u5fc3\u91cc\uff01\u201d \u3000\u3000\u6e29\u5982\u7389\u51b7\u7b11\u9053\uff1a\u201c\u6709\u5fd7\u6c14\uff0c\u6709\u5fd7\u6c14\uff0c\u4f46\u6211\u4e00\u751f\u4ece\u672a\u5360\u8fc7\u522b\u4eba\u4fbf\u5b9c\u3002\u201d \u3000\u3000\u7a81\u7136\u81ea\u6000\u4e2d\u53d6\u51fa\u4e24\u679a\u91d1\u5149\u707f\u70c2\u7684\u5706\u7b52\uff0c\u51b7\u51b7\u63a5\u53e3\u53c8\u9053\uff1a\u8fd9\u4e24\u7b52\u4e94\u4e91\u70d8\u65e5\u900f\u5fc3\u9488\uff0c\u4e00\u5b9e\u4e00 \u7a7a\uff0c\u6211\u4e14\u8ba9\u4f60\u5148\u9009\u4e00\u7b52\uff0c\u4f60\u82e5\u9009\u7684\u662f\u5b9e\uff0c\u6211\u4fbf\u6210\u5168\u4f60\u7684\u5fc3\u613f\uff0c\u5426\u5219\u2026\u2026\u54c8\u54c8\uff0c\u5c39\u51e1\uff0c\u4f60\u4e14\u5c06 \u8fd9\u4e24\u7b80\u900f\u5fc3\u9488\u53d6\u51fa\uff0c\u8ba9\u4ed6\u5148\u9009\u4e00\u7b52\uff1a\u201c\u5c39\u51e1\u5fae\u4e00\u8fdf\u7591\uff0c\u76ee\u5149\u4e2d\u7a81\u5730\u53c8\u6709\u4e00\u4e1d\u5149\u8292\u95ea\u52a8\uff0c\u7f13\u7f13 \u8d70\u5230\u6e29\u5982\u7389\u7684\u8eab\u540e\uff0c\u7f13\u7f13\u63a5\u8fc7\u5979\u638c\u4e2d\u7684\u4e24\u679a\u5706\u7b52\uff0c\u7f13\u7f13\u8f6c\u8eab\u2026\u2026\u7a81\u5730\uff0c\u4ed6\u62e7\u8170\u53cd\u8eab\uff0c\u53cc\u638c\u9f50 \u626c\uff0c\u53ea\u542c\u201d\u683c\u683c\u201c\u4e00\u4e32\u8f7b\u54cd\u2026\u2026\u8f7b\u54cd\u58f0\u4e2d\uff0c\u53c8\u5939\u6742\u7740\u5c39\u51e1\u7684\u51e0\u58f0\u72de\u7b11\uff0c\u54ea\u77e5\u2026\u2026\u4e24\u7b52\u4e94\u4e91\u70d8 \u65e5\u900f\u5fc3\u9488\u4e2d\uff0c\u5374\u65e0\u4e00\u9488\u53d1\u51fa\uff0c\u5c39\u51e1\u72de\u7b11\u4e4b\u58f0\u7a81\u987f\uff0c\u6e29\u5982\u7389\u72c2\u7b11\u4e4b\u58f0\u7acb\u8d77\uff0c\u5c39\u51e1\u8fde\u9000\u4e86\u4e09\u6b65\uff0c \u6e29\u5982\u7389\u72c2\u7b11\u9053\uff1a\u201c\u9519\u4e86\uff0c\u9519\u4e86\uff0c\u4f60\u53c8\u8d70\u9519\u4e00\u6b65\uff0c\u4f60\u53c8\u843d\u4eba\u4e86\u6211\u7684\u7b97\u8ba1\u4e2d\u3002\u201d\u5353\u957f\u537f\u3001\u6e29\u747e\u6115 \u7136\u800c\u671b\uff0c\u5c39\u51e1\u9762\u5982\u6b7b\u7070\uff0c\u6e29\u5982\u7389\u72c2\u7b11\u53c8\u9053\uff1a\u201c\u5728\u4f60\u4e00\u751f\u4e4b\u4e2d\uff0c\u4ece\u672a\u505a\u8fc7\u4e00\u4ef6\u6b63\u76f4\u4e4b\u4e8b\uff0c\u4e5f\u4ece \u672a\u505a\u8fc7\u4e00\u4ef6\u672a\u6b3a\u9a97\u522b\u4eba\u7684\u4e8b\uff0c\u6211\u867d\u65e9\u6709\u6740\u4f60\u4e4b\u5fc3\uff0c\u4f46\u4eca\u65e5\u672c\u5df2\u66ff\u4f60\u7559\u4e0b\u4e00\u6761\u751f\u8def\uff0c\u53ea\u8981\u4f60\u65b9 \u624d\u4e0d\u8981\u518d\u9a97\u6211\uff0c\u6211\u5c31\u51b3\u5b9a\u653e\u4f60\u56de\u53bb\u2026\u2026\u201d \u3000\u3000\u5979\u8fb9\u8bf4\u5c39\u51e1\u8fb9\u9000\uff0c\u5c39\u51e1\u8fb9\u9000\uff0c\u5979\u6b65\u6b65\u7d27\u903c\uff0c\u76f4\u903c\u5f97\u5c39\u51e1\u9000\u5230\u5899\u89d2\uff0c\u5979\u7a81\u53c8\u81ea\u6000\u4e2d\u53d6\u51fa\u4e24 \u679a\u91d1\u8272\u7684\u5706\u7b52\uff0c\u53e3\u4e2d\u8bf4\u9053\uff1a\u201c\u6614\u5e74\u9ec4\u5c71\u59cb\u4fe1\u5cf0\u4e0b\uff0c\u82e5\u975e\u6709\u4f60\uff0c\u627e\u4e5f\u4e0d\u4f1a\u5c06\u4eba\u5bb6\u592b\u5987\u4e00\u8d77\u7f6e\u4e8e \u6b7b\u8def\uff0c\u747e\u513f\u82e5\u975e\u4f60\u4ece\u4e2d\u6311\u62e8\uff0c\u4e5f\u4e0d\u4f1a\u2026\u2026\u201d \u3000\u3000\u8bed\u58f0\u4e00\u987f\uff0c\u7a81\u7136\u4f4e\u559d\u9053\uff1a\u201c\u5353\u957f\u537f\uff0c\u4f60\u8fc7\u6765\uff1a\u201c\u5353\u957f\u537f\u6115\u4e86\u4e00\u6115\uff0c\u4e00\u63a0\u800c\u524d\uff0c\u6e29\u5982\u7389\u5934 \u4e5f\u4e0d\u56de\u5730\u5c06\u638c\u4e2d\u7684\u4e24\u679a\u4e94\u4e91\u70d8\u65e5\u900f\u5fc3\u9488\uff0c\u4e00\u8d77\u9012\u5230\u4ed6\u8eab\u524d\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u6b64\u4eba\u4ea6\u662f\u4f60\u6740\u7236\u4ec7 \u4eba\uff0c\u4f60\u53ea\u7ba1\u5c06\u6b64\u9488\u53d6\u51fa\u4e00\u7b52\u2026\u2026\u201d \u3000\u3000\u5353\u957f\u537f\u7f13\u7f13\u63a5\u8fc7\u4e00\u7b52\uff0c\u7a81\u53c8\u629b\u56de\u6e29\u5982\u7389\u638c\u4e2d\uff0c\u6717\u58f0\u9053\uff1a\u201c\u7236\u6bcd\u4e4b\u4ec7\uff0c\u867d\u4e0d\u5171\u6234\u5929\uff0c\u4f46\u5728 \u4e0b\u5374\u4e0d\u613f\u56e0\u4eba\u6210\u4e8b\uff0c\u66f4\u4e0d\u613f\u4ef0\u4ed7\u2026\u2026\u201d \u3000\u3000\u8bed\u58f0\u672a\u4e86\uff0c\u5c39\u51e1\u7a81\u5730\u6709\u5982\u4e00\u9053\u8f7b\u70df\u822c\u8d34\u5899\u800c\u8d77\uff0c\u8db3\u8ddf\u4e00\u70b9\u58c1\u9762\uff0c\u8eab\u5f62\u500f\u7136\u6a2a\u98de\u4e09\u4e08\u3002 \u3000\u3000\u6e29\u5982\u7389\u51b7\u7b11\u4e00\u58f0\uff0c\u53f1\u9053\uff1a\u4f60\u8fd8\u60f3\u8d70\uff1f\u201c\u8f6c\u8eab\uff0c\u626c\u638c\uff0c\u4e94\u70b9\u91d1\u5149\uff0c\u66b4\u5c04\u800c\u51fa\uff0c\u4e94\u70b9\u91d1\u5149\u4ff1 \u90fd\u51fb\u5411\u5c39\u51e1\u8eab\u4e0a\u3002\u53ea\u542c\u201d\u6251\u201c\u5730\u4e00\u58f0\u5de8\u54cd\uff0c\u8f7b\u529f\u5df2\u81fb\u7edd\u9876\u7684\u4e07\u5999\u771f\u541b\u5c39\u51e1\uff0c\u7ec8\u4e8e\u4e5f\u50cf\u4efb\u4f55\u4e00 \u4e2a\u51e1\u4eba\u4e00\u6837\uff0c\u6c89\u91cd\u5730\u843d\u4e86\u4e0b\u6765\u3002\u5c18\u4e0a\u98de\u626c\uff0c\u4ed6\u8eab\u5f62\u5374\u5728\u98de\u626c\u7740\u7684\u5c18\u4e0a\u4e2d\u5bc2\u7136\u4e0d\u52a8\uff0c\u6e29\u5982\u7389\u51b7 \u524a\u7684\u7b11\u58f0\uff0c\u7a81\u7136\u4e5f\u53d8\u5f97\u5bc2\u7136\u65e0\u58f0\u3002\u5728\u8fd9\u5239\u90a3\u4e4b\u95f4\uff0c\u5979\u5168\u8eab\u4f3c\u4e5f\u5168\u90e8\u9ebb\u6728\uff0c\u76ee\u5149\u75f4\u5446\u5730\u671b\u7740\u5c39 \u51e1\u7684\u8eab\u8eaf\uff0c\u811a\u6b65\u4e5f\u75f4\u5446\u5730\u5411\u4ed6\u7f13\u7f13\u79fb\u52a8\u4e86\u4e24\u6b65\uff0c\u665a\u98ce\u5439\u52a8\u7740\u5979\u663e\u7136\u5df2\u6709\u4e24\u65e5\u672a\u66fe\u68b3\u6d17\u7684\u5760\u9a6c \u4e91\u9afb\uff0c\u5439\u5f97\u5979\u82b1\u767d\u7684\u5934\u53d1\u4e1d\u4e1d\u98d8\u52a8\uff0c\u706f\u5149\u660f\u9ec4\uff0c\u4eba\u5f71\u6726\u80e7\uff0c\u5bd2\u610f\u66f4\u91cd\u3002\u826f\u4e45\u826f\u4e45\uff0c\u5979\u65b9\u81ea\u7f13 \u7f13\u8f6c\u8fc7\u8eab\u6765\uff0c\u65e0\u6bd4\u4ed4\u7ec6\u5730\u7aef\u8be6\u4e86\u6e29\u747e\u548c\u5353\u957f\u537f\u4e24\u773c\uff0c\u7a81\u5730\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u4eec\u8981\u62a5\u4ec7\uff0c\u8fd8\u4e0d\u52a8\u624b \u4e48\uff1f\u201d \u3000\u3000\u5c06\u638c\u4e2d\u4e24\u7b52\u900f\u5fc3\u9488\uff0c\u4e00\u8d77\u629b\u5230\u5730\u4e0a\uff1a\u201c\u5047\u5982\u4f60\u4eec\u613f\u610f\uff0c\u4e0d\u59a8\u5148\u9009\u4e00\u7b52\u3002\u201d \u3000\u3000\u5bd2\u610f\u66f4\u91cd\u4e86\u3002 \u3000\u3000\u4ec1\u4e49\u5251\u5ba2\u4e91\u4e2d\u7a0b\uff0c\u56de\u5230\u4e86\u4ed6\u4e0e\u4ed6\u7239\u7239\u7ea6\u5b9a\u76f8\u4f1a\u7684\u5730\u65b9\uff0c\u56db\u4e0b\u65e0\u58f0\uff0c\u4ed6\u7239\u7239\u4ecd\u672a\u5230\u6765\uff0c\u4ed6 \u5fc3\u4e2d\u5374\u6709\u5982\u4e71\u9ebb\u4e00\u822c\u7d0a\u4e71\u3002 \u3000\u3000\u65b9\u624d\u4ed6\u4eb2\u773c\u89c1\u5230\u8bb8\u591a\u4ece\u6765\u672a\u89c1\u4e4b\u4e8b\uff0c\u4e5f\u4eb2\u8033\u542c\u5230\u8bb8\u591a\u4ed6\u4ece\u6765\u672a\u95fb\u4e4b\u4e8b\uff0c\u6700\u4ee4\u4ed6\u5927\u60d1\u4e0d\u89e3 \u7684\uff0c\u5374\u662f\u6e29\u5982\u7389\u6700\u540e\u6240\u8bf4\u7684\u51e0\u65ec\u8bdd\uff1a\u201c\u6211\u53ea\u8981\u4f60\u518d\u4e3a\u6211\u505a\u4e00\u4e8b\uff0c\u7b49\u6211\u6b7b\u540e\uff0c\u4f60\u8981\u8bbe\u6cd5\u544a\u8bc9\u747e \u513f\uff0c\u6881\u540c\u9e3f\u867d\u662f\u5979\u7236\u4eb2\uff0c\u5b5f\u5982\u5149\u5374\u4e0d\u662f\u5979\u5988\u5988\u3002\u201d \u3000\u3000\u4ed6\u4eb2\u773c\u89c1\u5230\u5c39\u51e1\u70b9\u5934\u7b54\u5e94\uff0c\u53c8\u4eb2\u8033\u542c\u5230\u6e29\u5982\u7389\u51c4\u51c9\u7684\u8bf4\u9053\uff1a\u201c\u747e\u513f\u771f\u53ef\u601c\uff0c\u5979\u518d\u4e5f\u4e0d\u4f1a \u60f3\u5230\u6740\u6b7b\u5979\u7239\u7239\u7684\u4ec7\u4eba\uff0c\u7adf\u662f\u5979\u4eb2\u751f\u7684\u5988\u5988\u2026\u6211\u600e\u80fd\u5fcd\u5fc3\u544a\u8bc9\u5979\uff0c\u6211\u600e\u80fd\u5fcd\u5fc3\u544a\u8bc9\u5979\u2026\u2026\u201d \u3000\u3000\u4e91\u4e2d\u7a0b\u6e05\u695a\u7684\u8bb0\u5f97\uff0c\u5f53\u4ed6\u542c\u5230\u8fd9\u91cc\u7684\u65f6\u5019\uff0c\u4ed6\u5fc3\u4e2d\u8d77\u4e86\u4e00\u9635\u60b2\u51c4\u7684\u611f\u89c9\uff0c\u8fd9\u5176\u4e2d\u7684\u6069\u6028 \u7ea0\u7f20\uff0c\u4ed6\u867d\u4e0d\u5c3d\u4e86\u89e3\uff0c\u5374\u5df2\u731c\u4e2d\u51e0\u5206\u3002 \u3000\u3000\u4ed6\u8fd8\u66fe\u542c\u5230\u6e29\u5982\u7389\u5bf9\u5c39\u51e1\u8bf4\uff1a\u201c\u6881\u540c\u9e3f\u5bf9\u4e0d\u8d77\u6211\uff0c\u5c31\u6b63\u5982\u4f60\u5bf9\u4e0d\u8d77\u6211\u4e00\u6837\uff0c\u4ed6\u9a97\u6211\uff0c\u8bf4 \u4ed6\u7231\u6211\uff0c\u54ea\u77e5\u5374\u4e3a\u7684\u662f\u8981\u9a97\u6211\u7684\u6b66\u529f\u4e0e\u8d22\u5bcc\uff0c\u7b49\u5230\u6211\u540e\u6765\u77e5\u9053\u4ed6\u8fd8\u6709\u59bb\u5b50\uff0c\u6211\u81ea\u7136\u9976\u4e0d\u8fc7 \u4ed6\uff0c\u81ea\u7136\u8981\u5c06\u4ed6\u592b\u59bb\u4e00\u8d77\u6740\u6b7b\uff0c\u53ef\u662f\u90a3\u65f6\u6211\u8eab\u4e0a\u5374\u5df2\u6709\u4e86\u8eab\u5b55\uff0c\u5509\uff0c\u82cd\u5929\u5440\u82cd\u5929\uff0c\u4f60\u4e3a\u4ec0\u4e48 \u603b\u662f\u8fd9\u822c\u6349\u5f04\u6211\u5462\uff1f\u201d \u3000\u3000\u76f4\u5230\u6b64\u523b\uff0c\u4e91\u4e2d\u7a0b\u8033\u7554\u4f3c\u4e4e\u8fd8\u5728\u98d8\u8361\u7740\u6e29\u5982\u7389\u8fd9\u6700\u540e\u4e00\u53e5\u8bdd\u3002 \u3000\u3000\u4ed6\u7a81\u7136\u5bf9\u8fd9\u4e16\u4e0a\u4eba\u4eba\u553e\u9a82\u7684\u5973\u9b54\u5934\u8d77\u4e86\u4e00\u9635\u96be\u8a00\u7684\u540c\u60c5\u4e4b\u5fc3\u3002 \u3000\u3000\u4ed6\u54fa\u54fa\u6697\u95ee\u81ea\u5df1\uff1a\u201c\u8fd9\u4e9b\u662f\u5979\u7684\u9519\u5417\uff1f\u2026\u2026\u5979\u4e0d\u8fc7\u53ea\u662f\u4e2a\u53ef\u601c\u800c\u53c8\u4e11\u964b\u7684\u5973\u4eba\u7f62\u4e86\u2026\u2026 \u4f46\u662f\u5979\u4e3a\u4ec0\u4e48\u8981\u90a3\u4e48\u6b8b\u9177\u2026\u2026\u6b8b\u9177\u4e0e\u53ef\u601c\u4e4b\u95f4\uff0c\u96be\u9053\u53c8\u6709\u7740\u4ec0\u4e48\u5173\u7cfb\u5417\uff1f\u201d \u3000\u3000\u4ec1\u4e49\u5251\u5ba2\u4e91\u4e2d\u7a0b\u5fc3\u4e2d\u7126\u6025\uff0c\u6765\u56de\u8e40\u8e31\uff0c\u4ed6\u77e5\u9053\u5353\u957f\u537f\u4e0e\u6e29\u747e\u6b64\u523b\u5374\u5728\u4e00\u5ea7\u540d\u53eb\u5929\u7985\u5bfa\u7684 \u5e99\u91cc\uff0c\u4ed6\u53ea\u671b\u4ed6\u7239\u7239\u65e9\u4e9b\u5230\u6765\u3002 \u3000\u3000\u4e8e\u662f\uff0c\u4ed6\u53c8\u4e0d\u7981\u4e3a\u4ed6\u7239\u7239\u60f3\u2014\u2014\u53ea\u7b49\u4ed6\u7239\u7239\u5230\u6765\u7684\u65f6\u5019\u3002 \u3000\u3000\u4ed6\u5306\u5306\u8bf4\u4e86\u4e24\u53e5\uff0c\u4fbf\u548c\u4ed6\u7239\u7239\u4e00\u8d77\u53bb\u5bfb\u90a3\u5929\u7985\u5e9f\u5bfa\uff0c\u6df1\u591c\u8352\u5c71\uff0c\u8981\u627e\u4e00\u5ea7\u53e4\u5bfa\u867d\u975e\u6613 \u4e8b\uff0c\u4f46\u5374\u6bd5\u7adf\u88ab\u4ed6\u4eec\u627e\u5230\u3002 \u3000\u3000\u4ed6\u4eec\u770b\u5230\u4e86\u660f\u9ec4\u7684\u706f\u5149\uff0c\u81ea\u53e4\u5bfa\u7684\u5927\u6bbf\u4e2d\u6620\u51fa\uff0c\u4e8e\u662f\u4ed6\u4eec\u5168\u529b\u5c55\u52a8\u8eab\u5f62\uff0c\u52a0\u901f\u63a0\u53bb\u3002 \u3000\u3000\u7a81\u7136\uff0c\u4ed6\u4eec\u542c\u5230\u4e00\u58f0\u6025\u5598\uff0c\u4e24\u58f0\u5a07\u547c\uff0c\u63a5\u7740\u4e00\u9635\u54c0\u54c0\u7684\u75db\u54ed\u2026\u2026 \u3000\u3000\u597d\u4e0d\u5bb9\u6613\u5730\u76fc\u5230\u591a\u81c2\u795e\u5251\u5728\u591c\u8272\u4e2d\u51fa\u73b0\u3001\u591a\u81c2\u795e\u5251\u4e00\u89c1\u9762\u5c31\u6025\u6025\u95ee\u9053\uff1a\u201c\u6709\u6ca1\u6709\u53d1\u73b0\u4ec0 \u4e48\uff1f\u201d \u3000\u3000\u591a\u81c2\u795e\u5251\u6d53\u7709\u4e00\u76b1\uff0c\u516b\u6b65\u8d76\u8749\uff0c\u9ad8\u5927\u7684\u8eab\u5f62\u63a5\u8fde\u513f\u4e2a\u8d77\u843d\uff0c\u500f\u7136\u63a0\u4e0a\u6bbf\uff0c\u95ea\u76ee\u5185\u671b\u3002 \u3000\u3000\u53ea\u89c1\u660f\u9ec4\u7684\u706f\u5149\u4e0b\uff0c\u5353\u957f\u537f\u3001\u6e29\u747e\u5446\u5446\u5730\u76f8\u5bf9\u800c\u7acb\uff0c\u4e24\u4e2a\u7740\u7ea2\u8863\u886b\u7684\u5c11\u5973\u4f0f\u5728\u5730\u4e0a\u54c0\u54c0 \u75db\u54ed\uff0c\u5728\u4ed6\u4eec\u4e4b\u95f4\uff0c\u5374\u89c1\u90a3\u7ea2\u8863\u5a18\u5a18\u6e29\u5982\u7389\u4e4b\u5c38\u8eab\uff0c\u4ecd\u548c\u5979\u751f\u524d\u4e00\u6837\uff0c\u51b0\u51b7\u67af\u7626\u3002 \u3000\u3000\u4ed6\u4eec\u4f3c\u4e4e\u8c01\u4e5f\u6ca1\u6709\u6ce8\u610f\u5230\u4e91\u6c0f\u7236\u5b50\u7a81\u7136\u73b0\u8eab\uff0c\u4e91\u6c0f\u7236\u5b50\u4e24\u4eba\u4e5f\u90fd\u6ca1\u6709\u53bb\u60ca\u52a8\u4ed6\u4eec\u3002 \u3000\u3000\u9759\u5bc2\u8fd9\u4e2d\uff0c\u7a81\u542c\u201c\u5578\u201d\u5730\u4e00\u58f0\uff0c\u6e29\u5982\u7389\u67af\u7626\u7684\u624b\u638c\u7f13\u7f13\u4f38\u5f00\u3001\u50f5\u6897\u4e00\u624b\u4e2d\u5374\u843d\u4e0b\u4e00\u679a\u91d1 \u8272\u5706\u7b52\uff0c\u7f13\u7f13\u6eda\u5230\u4e91\u4e2d\u7a0b\u811a\u7554\u3002 \u3000\u3000\u4ed6\u4fef\u8eab\u62fe\u4e86\u8d77\u6765\uff0c\u9762\u8272\u4e0d\u7981\u4e00\u53d8\uff0c\u56e0\u4e3a\u4ed6\u8ba4\u5f97\u8fd9\u4fbf\u662f\u6c5f\u6e56\u4e2d\u4eba\u95fb\u540d\u4e27\u80c6\u7684\u4e94\u4e91\u70d8\u65e5\u900f\u5fc3 \u9488\uff0c\u4ed6\u4ed4\u7ec6\u5730\u770b\u4e86\u534a\u664c\uff0c\u65cb\u5f00\u540e\u9762\u7684\u7b52\u76d6\uff0c\u5012\u51fa\u4e94\u679a\u91d1\u8272\u7684\u5c16\u9488\uff0c\u4e8e\u662f\u4ed6\u4e0d\u7981\u53c8\u4e3a\u4e4b\u6697\u53f9\u4e00 \u58f0\uff0c\u4ed6\u6df1\u77e5\u8fd9\u4e00\u7b52\u91d1\u9488\u6e29\u5982\u7389\u82e5\u662f\u53d1\u51fa\uff0c\u6b64\u523b\u8eba\u5728\u5730\u4e0a\u7684\u5fc5\u662f\u522b\u4eba\uff0c\u4ed6\u4e5f\u6df1\u77e5\u6e29\u5982\u7389\u4e3a\u4ec0\u4e48 \u6ca1\u53d1\u7684\u7f18\u6545\u3002 \u3000\u3000\u5353\u957f\u537f\u5446\u5446\u5730\u671b\u7740\u5730\u4e0a\u8fd9\u5177\u5c38\u4f53\uff0c\u8fd9\u5177\u5c38\u4f53\u662f\u4ed6\u548c\u6e29\u747e\u6240\u6b32\u6740\u7684\u4ec7\u4eba\uff0c\u5947\u602a\u7684\u662f\uff0c\u4ed6\u6b64 \u523b\u7adf\u4e1d\u6beb\u6ca1\u6709\u80dc\u5229\u7684\u6109\u5feb\uff0c\u66f4\u6ca1\u6709\u6740\u654c\u540e\u7684\u81ea\u50b2\uff0c\u4ed6\u7684\u5fc3\u60c5\uff0c\u751a\u81f3\u6bd4\u65b9\u624d\u8fd8\u8981\u6c89\u91cd\uff01 \u3000\u3000\u8fd9\u4e3a\u7684\u662f\u4ec0\u4e48\uff0c\u4ed6\u65e0\u6cd5\u89e3\u91ca\uff0c\u4e5f\u4e0d\u613f\u89e3\u91ca\u3002 \u3000\u3000\u6e29\u747e\u5462\uff0c\u6e29\u747e\u7684\u5fc3\u60c5\u2026\u2026 \u3000\u3000\u7a81\u7136\uff0c\u817f\u80a1\u4e4b\u95f4\u8fde\u4e2d\u4e94\u9488\u7684\u4e07\u5999\u771f\u541b\u5c39\u51e1\uff0c\u7adf\u662f\u82cf\u9192\u8fc7\u6765\uff0c\u4ed6\u8f7b\u5fae\u7684\u547b\u541f\u4e00\u4e0b\uff0c\u8f6c\u4fa7\u4e00 \u4e0b\uff0c\u6323\u624e\u7740\u62ac\u8d77\u5934\u6765\uff0c\u547b\u541f\u7740\u9053\uff1a\u201c\u4f60\u4eec\u2026\u2026\u7ec8\u4e8e\u2026\u2026\u62a5\u4e86\u4ec7\u4e86\u2026\u2026\u597d\u6781\u2026\u2026\u597d\u6781\u3002\u201d \u3000\u3000\u5353\u957f\u537f\u3001\u6e29\u747e\u4e00\u8d77\u8f6c\u56de\u76ee\u5149\u3002 \u3000\u3000\u4e00\u4e1d\u6614\u7b11\uff0c\u53c8\u81ea\u6cdb\u8d77\u5728\u5634\u89d2\uff0c\u4ed6\u7d27\u54ac\u4e00\u9635\u7259\u5173\uff0c\u53c8\u81ea\u547b\u541f\u7740\u9053\uff1a\u201c\u5947\u602a\u4e48\uff0c\u6211\u7adf\u7136\u8fd8\u6ca1 \u6709\u6b7b\u2026\u2026\u56e0\u4e3a\u2026\u2026\u56e0\u4e3a\u6211\u8fd8\u6709\u4e00\u4ef6\u79d8\u5bc6\u672a\u66fe\u8bf4\u51fa\uff0c\u4f60\u4eec\u2026\u2026\u4f60\u4eec\u2026\u2026\u53ef\u8981\u542c\u4e48\uff1f\u2026\u2026\u201d \u3000\u3000\u4e91\u4e2d\u7a0b\u5fc3\u5934\u4e00\u8df3\uff0c\u53ea\u542c\u4ed6\u53c8\u9053\uff1a\u201c\u8fd9\u79d8\u5bc6\u5173\u7cfb\u7740\u2026\u2026\u5173\u7cfb\u7740\u4f60\u4e00\u751f\u7684\u547d\u8fd0\uff0c\u4f46\u2026\u2026\u4f46\u5374 \u53ea\u6709\u6211\u4e00\u4eba\u77e5\u9053\u2026\u2026\u4f60\u4eec\u82e5\u60f3\u542c\uff0c\u5c31\u2026\u2026\u5c31\u7094\u4e9b\u8bbe\u6cd5\u66ff\u6211\u6cbb\u597d\u4f24\u2026\u2026\u201d \u3000\u3000\u5353\u957f\u537f\u3001\u6e29\u747e\u5bf9\u671b\u4e00\u773c\uff0c\u5fae\u4e00\u8fdf\u7591\uff0c\u54ea\u77e5\u4e91\u4e2d\u7a0b\u7a81\u7136\u5927\u559d\u4e00\u58f0\uff1a\u201c\u96be\u9053\u4f60\u4e34\u6b7b\u8fd8\u8981\u9a97\u4eba \u4e48\uff1f\u201d \u3000\u3000\u500f\u7136\u98de\u8d77\u4e00\u811a\uff0c\u76f4\u8e22\u5f97\u5c39\u51e1\u60e8\u547c\u4e00\u58f0\uff0c\u5410\u8840\u800c\u4ea1\uff0c\u4ed6\u5fc3\u4e2d\u7eb5\u7136\u8fd8\u6709\u8bb8\u591a\u597d\u8ba1\uff0c\u5374\u518d\u4e5f\u65e0 \u6cd5\u4f7f\u51fa\u4e86\u3002 \u3000\u3000\u4e91\u4e2d\u7a0b\u6697\u4e2d\u4e00\u53f9\uff0c\u81ea\u8bed\u7740\u9053\uff1a\u201c\u6c38\u8fdc\u4e0d\u4f1a\u6709\u4eba\u77e5\u9053\u4e86\uff0c\u6c38\u8fdc\u4e0d\u4f1a\u518d\u6709\u4eba\u4f24\u5bb3\u4ed6\u4eec\u7684\u5e78\u798f \u4e86\u3002\u201d \u3000\u3000\u591a\u81c2\u795e\u5251\u6d53\u7709\u4e00\u76b1\uff0c\u9053\uff1a\u201c\u4e2d\u7a0b\uff0c\u4f60\u5728\u8bf4\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4e91\u4e2d\u7a0b\u957f\u957f\u5410\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6211\u5728\u8bf4\u5353\u4f2f\u4f2f\u82f1\u7075\u6709\u77e5\uff0c\u4e5d\u6cc9\u4e4b\u4e0b\uff0c\u4e5f\u81ea\u7791\u76ee\u4e86\u3002\u201d \u3000\u3000\u4e91\u8c26\u5446\u4e86\u4e00\u5446\uff0c\u53cc\u76ee\u5706\u7741\uff0c\u95ea\u95ea\u7684\u76ee\u5149\u4e2d\uff0c\u7a81\u5730\u6d41\u4e0b\u4e24\u6ef4\u6cea\u6765\uff0c\u5353\u957f\u537f\u53ea\u89c9\u5fc3\u60c5\u4e00\u9635\u6fc0 \u52a8\uff0c\u773c\u7751\u4e00\u5408\u4e00\u5f20\uff0c\u5fcd\u4e0d\u4f4f\u4e24\u6ef4\u6676\u83b9\u7684\u6cea\u73e0\uff0c\u593a\u7736\u800c\u51fa\u3002\u6e29\u747e\u671b\u4e86\u671b\u72b9\u81ea\u4f0f\u5728\u5730\u4e0a\u54c0\u54ed\u7684\u5c0f \u73b2\u3001\u5c0f\u743c\uff0c\u5fc3\u4e2d\u4e00\u9635\u70ed\u8840\u4e0a\u6d8c\uff0c\u7a81\u5730\u4f0f\u5230\u5730\u4e0a\uff0c\u653e\u58f0\u75db\u54ed\u8d77\u6765\u3002 \u3000\u3000\u4e91\u4e2d\u7a0b\u9053\uff1a\u201c\u771f\u5947\u602a\uff0c\u4f60\u4eec\u600e\u4e48\u54ed\u8d77\u6765\u4e86\uff1f\u201d \u3000\u3000\u4e00\u4f38\u624b\u4e00\u62ed\u773c\u7751\uff0c\u773c\u4e2d\u5374\u4e5f\u5df2\u6ee1\u542b\u6cea\u73e0\u3002 \u3000\u3000\u7136\u800c\uff0c\u4ed6\u4eec\u7684\u6cea\u73e0\u5374\u90fd\u662f\u6676\u83b9\u800c\u53ef\u8d35\uff0c\u5c31\u6b63\u5982\u660e\u4eae\u7684\u73cd\u73e0\u4e00\u6837\uff0c\u6728\u7acb\u6d41\u6cea\u7684\u5353\u957f\u537f\uff0c\u7a81 \u7136\u89c9\u5f97\u80a9\u5934\u4e00\u9635\u6e29\u6696\uff0c\u4e00\u53ea\u7ea4\u7ea4\u7389\u624b\uff0c\u9001\u6765\u4e00\u6761\u7c89\u7ea2\u7684\u624b\u5e15\uff0c\u4ed6\u4f38\u624b\u63a5\u8fc7\uff0c\u56de\u9996\u671b\u53bb\uff0c\u5374\u6b63 \u597d\u671b\u7740\u6e29\u747e\u90a3\u4e00\u53cc\u542b\u60c5\u8109\u8109\u7684\u79cb\u6ce2\u3002 \u3000\u3000\u79cb\u6ce2\u5982\u6c34\uff0c\u706f\u5149\u5982\u68a6\uff0c\u8c01\u4e5f\u4e0d\u77e5\u66d9\u8272\u662f\u5728\u4f55\u65f6\u722c\u4e0a\u5730\u5e73\u7ebf\uff0c\u4e8e\u662f\u4e1c\u65b9\u4e00\u9053\u91d1\u9ec4\u7684\u9633\u5149\uff0c \u51b2\u7834\u6c89\u91cd\u7684\u591c\u5e55\uff0c\u6628\u591c\u78a7\u7a7a\u4e0a\u7684\u661f\u4e0e\u6708\uff0c\u4e5f\u4ff1\u5728\u8fd9\u7eda\u70c2\u7684\u9633\u5149\u4e0b\u6d88\u5931\u65e0\u8e2a\u3002"}),showOnHomepage:!0}]}}]);