"use strict";(self.webpackChunkwebsite=self.webpackChunkwebsite||[]).push([[20744],{47562:(e,n,t)=>{t.r(n),t.d(n,{default:()=>a});var s=t(74848);const a=[{name:"\u840d\u8e2a\u4fa0\u5f71\u5f55",content:(0,s.jsx)(s.Fragment,{children:"\u6e05\u5bd2\u5439\u89d2\uff0c\u96c1\u95e8\u5173\u5916\uff0c\u6714\u98ce\u6012\u5377\u9ec4\u660f\u3002 \u3000\u3000\u8fd9\u65f6\u4e43\u662f\u660e\u4ee3\u6b63\u7edf\uff08\u660e\u82f1\u5b97\u5e74\u53f7\uff09\u4e09\u5e74\uff0c\u8ddd\u79bb\u660e\u592a\u7956\u6731\u5143\u748b\u6b7b\u540e\uff0c\u8fd8\u4e0d\u5230\u56db\u5341\u5e74\u3002\u8499\u53e4 \u7684\u52bf\u529b\uff0c\u53c8\u6b7b\u7070\u590d\u71c3\uff0c\u5728\u897f\u5317\u5174\u8d77\uff0c\u5176\u4e2d\u5c24\u4ee5\u74e6\u523a\u65cf\u6700\u4e3a\u5f3a\u5927\uff0c\u9010\u5e74\u5185\u4fb5\uff0c\u81f3\u6b63\u7edf\u5e74\u95f4\uff0c\u5df2 \u5230\u4e86\u96c1\u95e8\u5173\u5916\u767e\u91cc\u4e4b\u5730\uff0c\u8fd9\u767e\u91cc\u4e4b\u5730\uff0c\u9042\u6210\u4e86\u660e\u4e0e\u74e6\u523a\u7684\u7f13\u51b2\u5730\u5e26\uff0c\u4e5f\u662f\u65e0\u4eba\u5730\u5e26\u3002\u897f\u98ce\u8083 \u6740\uff0c\u9ec4\u6c99\u4e0e\u843d\u53f6\u9f50\u98de\uff0c\u843d\u65e5\u660f\u9ec4\uff0c\u9a6c\u94c3\u4e0e\u80e1\u7b33\u5e76\u8d77\uff0c\u5728\u8fd9\u201c\u65e0\u4eba\u5730\u5e26\u201d\u4e4b\u95f4\uff0c\u8fd9\u65f6\u5019\u5374\u6709\u4e00 \u8f86\u9a74\u8f66\uff0c\u4ece\u5ce1\u8c37\u7684\u5c71\u9053\u4e0a\u75be\u9a70\u800c\u8fc7\u3002 \u3000\u3000\u9a74\u8f66\u540e\u7d27\u8ddf\u7740\u4e00\u9a91\u9a8f\u9a6c\uff0c\u9a6c\u4e0a\u7684\u9a91\u5ba2\u662f\u4e00\u4e2a\u8eab\u6750\u5065\u786c\u7684\u4e2d\u5e74\u6c49\u5b50\uff0c\u80cc\u8d1f\u7bad\u56ca\uff0c\u8170\u60ac\u957f \u5251\uff0c\u4e0d\u65f6\u5730\u56de\u5934\u987e\u76fc\u3002\u6714\u98ce\u8d8a\u5377\u8d8a\u70c8\uff0c\u98ce\u4e2d\u9690\u9690\u4f20\u6765\u4e86\u80e1\u9a6c\u5636\u9e23\u4e0e\u91d1\u6208\u4ea4\u51fb\u4e4b\u58f0\uff0c\u9661\u7136\u95f4\uff0c \u53ea\u542c\u5f97\u4e00\u58f0\u51c4\u5389\u7684\u957f\u53eb\uff0c\u9a6c\u8e44\u5386\u4e71\u4e4b\u58f0\u6e10\u8fdc\u6e10\u5bc2\uff0c\u8f66\u4e2d\u4e00\u4e2a\u767d\u53d1\u82cd\u82cd\u7684\u8001\u8005\uff0c\u5377\u8d77\u8f66\u5e18\uff0c\u98a4 \u58f0\u95ee\u9053\uff1a\u201c\u662f\u6f84\u513f\u5728\u53eb\u6211\u4e48\uff1f\u53ef\u662f\u4ed6\u9047\u96be\u4e5f\uff1f\u8c22\u4fa0\u58eb\uff0c\u4f60\u4e0d\u5fc5\u518d\u987e\u6211\u4e86\uff0c\u4f60\u53bb\u63a5\u5e94\u4ed6\u4eec\u5427\uff0c \u6211\u5230\u5f97\u8fd9\u513f\uff0c\u6b7b\u5df2\u7791\u76ee\uff01\u201d \u3000\u3000\u4e2d\u5e74\u9a91\u5ba2\u5e94\u4e86\u4e00\u58f0\uff0c\u9065\u6307\u8bf4\u9053\uff1a\u201c\u8001\u4f2f\u4e07\u5b89\uff0c\u4f60\u542c\u90a3\u9a6c\u8e44\u5386\u4e71\u4e4b\u58f0\uff0c\u6599\u662f\u80e1\u5175\u5df2\u9000\u4e86\u3002 \u5662\uff0c\u4f60\u77a7\uff0c\u8fd9\u4e0d\u662f\u4ed6\u4eec\u6765\u4e86\uff01\u201d\u4e00\u62e8\u9a6c\u5934\uff0c\u5982\u98de\u8fce\u4e0a\u3002\u8f66\u4e2d\u8001\u8005\uff0c\u957f\u53f9\u4e00\u58f0\uff0c\u6f78\u7136\u6cea\u4e0b\u3002\u8f66 \u4e2d\u8e66\u5730\u8df3\u8d77\u4e00\u4e2a\u5c0f\u5973\u5b69\uff0c\u5c0f\u8138\u513f\u51bb\u5f97\u7ea2\u51ac\u51ac\u7684\uff0c\u6709\u5982\u719f\u900f\u4e86\u7684\u82f9\u679c\uff0c\u63c9\u63c9\u773c\u775b\uff0c\u4f3c\u662f\u521a\u521a\u7761 \u9192\u7684\u6837\u5b50\uff0c\u5f00\u58f0\u95ee\u9053\uff1a\u201c\u7237\u7237\uff0c\u8fd9\u662f\u4e2d\u56fd\u7684\u5730\u65b9\u4e86\u5417\uff1f\u201d\u90a3\u8001\u8005\u52d2\u4f4f\u9a74\u8f66\uff0c\u51dd\u89c6\u8f66\u4e0b\u7684\u571f \u5730\uff0c\u58f0\u8c03\u4f4e\u6c89\u9053\uff1a\u201c\u55ef\uff0c\u662f\u4e2d\u56fd\u7684\u5730\u65b9\u4e86\u3002\u963f\u857e\uff0c\u4f60\u4e0b\u8f66\u53bb\uff0c\u66ff\u7237\u7237\u62ff\u4e00\u628a\u6ce5\u571f\u56de\u6765\uff01\u201d \u3000\u3000\u5c71\u8c37\u53e3\u5916\uff0c\u4e09\u9a91\u8d1f\u4f24\u7684\u6218\u9a6c\u80cc\u7740\u8863\u51a0\u7834\u788e\u7684\u4e58\u5ba2\uff0c\u72c2\u5636\u5954\u56de\uff0c\u9886\u5148\u7684\u662f\u4e00\u4e2a\u548c\u5c1a\u3002\u90a3\u59d3 \u8c22\u7684\u4e2d\u5e74\u6c49\u5b50\u8fce\u4e0a\u95ee\u9053\uff1a\u201c\u6f6e\u97f3\u5e08\u5144\uff0c\u4e91\u6f84\u5e08\u5f1f\u5462\uff1f\u201d\u90a3\u548c\u5c1a\u52d2\u4f4f\u9a6c\u5934\uff0c\u9eef\u7136\u8bf4\u9053\uff1a\u201c\u4ed6\u5df2 \u6b7b\u4e86\uff01\u771f\u60f3\u4e0d\u5230\u4e07\u6c34\u5343\u5c71\uff0c\u9003\u5230\u8fd9\u513f\uff0c\u96c1\u95e8\u5173\u5df2\u7ecf\u5728\u671b\uff0c\u4ed6\u5374\u8fd8\u9003\u4e0d\u51fa\u80e1\u4eba\u4e4b\u624b\u3002\u4e0d\u8fc7\uff0c\u4ed6 \u4e5f\u771f\u4e0d\u6127\u662f\u4e2a\u94c1\u94ee\u94ee\u7684\u6c49\u5b50\uff0c\u91cd\u4f24\u4e4b\u540e\uff0c\u8fd8\u529b\u6bd9\u6570\u4eba\uff0c\u4e34\u6b7b\u4e4b\u524d\uff0c\u8fd8\u6740\u4e86\u5730\u4e2a\u9886\u5175\u7684\u9791\u5b50\uff0c \u628a\u90a3\u4e9b\u8499\u53e4\u5175\u5413\u5f97\u8fde\u5fd9\u9003\u547d\uff0c\u4e0d\u6562\u518d\u8ffd\u3002\u4eba\u8c01\u65e0\u6b7b\uff0c\u50cf\u4ed6\u8fd9\u6837\uff0c\u6b7b\u4e5f\u503c\u5f97\u4e86\u3002\u4f60\u7684\u5f92\u513f\u4e5f\u4e0d \u9519\uff0c\u4ed6\u4e5f\u662f\u529b\u6740\u6570\u4eba\uff0c\u548c\u4ed6\u7684\u5e08\u53d4\u5e76\u80a9\u6218\u6b7b\u7684\u3002\u201d \u3000\u3000\u90a3\u4e2d\u5e74\u6c49\u5b50\u53cc\u76ee\u70af\u70af\uff0c\u6012\u89c6\u957f\u7a7a\uff0c\u5ffd\u800c\u4e00\u58f0\u957f\u7b11\u9053\uff1a\u201c\u96c1\u95e8\u5173\u5df2\u7ecf\u5728\u671b\uff0c\u6211\u4eec\u7ec8\u7b97\u4e0d\u8d1f \u4e91\u6f84\u5f1f\u4e4b\u6258\uff0c\u5c06\u4ed6\u7684\u7239\u7239\u9001\u56de\u6765\u4e86\uff0c\u4e91\u6f84\u5728\u4e5d\u6cc9\u4e4b\u4e0b\uff0c\u5f53\u53ef\u7791\u76ee\u3002\u53ea\u662f\u4e91\u5927\u4eba\u54c0\u75db\u4f59\u751f\uff0c\u8fd9 \u4e8b\u513f\u6682\u4e14\u7792\u7740\u4ed6\u3002\u201d\u7eb5\u9a6c\u8d76\u56de\u9a74\u8f66\uff0c\u53ea\u89c1\u8f66\u4e2d\u7684\u8001\u8005\u8de8\u5728\u8f66\u8f95\u4e4b\u4e0a\uff0c\u6367\u7740\u4e00\u64ae\u6ce5\u571f\uff0c\u795e\u60c5\u975e \u5e38\u5947\u5f02\uff0c\u90a3\u5c0f\u5973\u5b69\u7ad9\u5728\u5730\u4e0a\uff0c\u6014\u6014\u5730\u770b\u7740\u5979\u7684\u7237\u7237\u3002 \u3000\u3000\u6f6e\u97f3\u548c\u5c1a\u53eb\u9053\uff1a\u201c\u4e91\u5927\u4eba\uff0c\u6211\u4eec\u56de\u6765\u4e86\u3002\u201d\u8001\u8005\u95ee\u4ed6\u9053\uff1a\u201c\u6211\u7684\u6f84\u513f\u5462\uff1f\u201d\u6f6e\u97f3\u548c\u5c1a \u9053\uff1a\u201c\u9791\u5b50\u5175\u5df2\u88ab\u6211\u4eec\u6740\u9000\uff0c\u4ed6\u53d7\u4e86\u70b9\u8f7b\u4f24\uff0c\u548c\u5929\u534e\u5e08\u5f1f\u7684\u5f92\u513f\u6bbf\u540e\u3002\u201d\u58f0\u8c03\u5c3d\u7ba1\u5f3a\u4f5c\u5e73 \u9759\uff0c\u8fd8\u662f\u6291\u4e0d\u4f4f\u90a3\u60b2\u6124\u4e4b\u60c5\u3002\u90a3\u8001\u8005\u9762\u8272\u5927\u53d8\uff0c\u6f6e\u97f3\u548c\u5c1a\u548c\u8c22\u5929\u534e\u90a3\u6837\u8c6a\u8fc8\u7684\u4fa0\u5ba2\uff0c\u5728\u4ed6\u903c \u89c6\u4e4b\u4e0b\uff0c\u4e5f\u4e0d\u89c9\u540e\u9000\u51e0\u6b65\uff0c\u4e0d\u6562\u63a5\u89e6\u4ed6\u7684\u76ee\u5149\uff0c\u53ea\u542c\u5f97\u4ed6\u7eb5\u58f0\u7b11\u9053\uff1a\u201c\u7236\u662f\u5fe0\u81e3\u513f\u5b5d\u5b50\uff0c\u5fe0 \u81e3\u5b5d\u5b50\u96c6\u4e8e\u4e00\u95e8\uff0c\u6211\u4e91\u9756\u5c1a\u6709\u4f55\u61be\uff01\u54c8\u54c8\uff0c\u54c8\uff01\u201d\u7b11\u58f0\u51c4\u5389\u4e4b\u4e2d\u542b\u7740\u6781\u5ea6\u7684\u60b2\u6124\uff0c\u9a74\u8f66\u65c1\u7684 \u9a91\u58eb\u90fd\u4e0d\u6562\u4f5c\u58f0\u3002\u90a3\u5973\u5b69\u5b50\u4ef0\u9762\u95ee\u4ed6\u9053\uff1a\u201c\u7237\u7237\uff0c\u4f60\u7b11\u4ec0\u4e48\uff1f\u6211\u5f88\u6015\u542c\uff0c\u7237\u7237\uff0c\u4f60\u522b\u8fd9\u6837\u7b11 \u5566\u3002\u7239\u7239\u4e3a\u4ec0\u4e48\u8fd8\u4e0d\u56de\u6765\uff1f\u201d \u3000\u3000\u90a3\u8001\u8005\u7b11\u58f0\u9aa4\u6b62\uff0c\u9759\u9ed8\u4e86\u597d\u4e00\u4f1a\u5b50\uff0c\u7f13\u7f13\u95ee\u9053\uff1a\u201c\u660e\u5929\u6e05\u65e9\uff0c\u53ef\u4ee5\u8d76\u5230\u96c1\u95e8\u5173\u5417\uff1f\u201d\u8c22 \u5929\u534e\u9053\uff1a\u201c\u662f\uff0c\u4eca\u665a\u6b63\u662f\u5341\u6708\u5341\u4e94\uff0c\u665a\u4e0a\u6708\u5149\u660e\u4eae\uff0c\u660e\u65e9\u5b9a\u53ef\u8d76\u5230\u3002\u201d\u90a3\u8001\u8005\u6367\u7740\u90a3\u64ae\u6ce5 \u571f\uff0c\u5982\u6367\u73cd\u5b9d\u4f3c\u7684\uff0c\u51d1\u8fd1\u9f3b\u7aef\uff0c\u6df1\u6df1\u547c\u5438\u4e86\u597d\u51e0\u4e0b\uff0c\u6ce5\u571f\u6563\u53d1\u7740\u6b8b\u679d\u8d25\u53f6\u7684\u6c14\u606f\uff0c\u90a3\u8001\u8005\u6df1 \u6df1\u547c\u5438\uff0c\u5982\u55c5\u5f02\u9999\uff0c\u51c4\u7136\u7b11\u9053\uff1a\u201c\u4e8c\u5341\u5e74\u4e86\uff0c\u5982\u4eca\u59cb\u95fb\u5f97\u7740\u6545\u4e61\u6ce5\u571f\u7684\u6c14\u5473\u3002\u201d\u8c22\u5929\u534e\u9053\uff1a \u201c\u8001\u4f2f\u5c45\u7559\u5f02\u56fd\uff0c\u5b58\u8282\u5168\u5fe0\uff0c\u6bd4\u82cf\u6b66\u7559\u80e1\uff0c\u5c1a\u591a\u4e00\u8f7d\uff0c\u5982\u6b64\u5b64\u81e3\u5b7d\u5b50\u4e4b\u5fc3\uff0c\u4eba\u5929\u5171\u4ef0\uff01\u201d \u3000\u3000\u90a3\u8001\u8005\u7709\u5934\u4e00\u5c55\uff0c\u53cc\u624b\u4e00\u4f38\uff0c\u628a\u90a3\u5973\u5b69\u5b50\u62b1\u4e0a\u8f66\u6765\uff0c\u53c8\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u963f\u857e\uff0c\u4f60\u4eca\u5e74\u4e03\u5c81 \u4e86\uff0c\u5e94\u8be5\u5f00\u59cb\u61c2\u4e8b\u4e86\uff0c\u7237\u7237\u4eca\u665a\u7ed9\u4f60\u8bf4\u4e00\u4e2a\u6545\u4e8b\uff0c\u4f60\u8981\u7d27\u7d27\u8bb0\u5728\u5fc3\u91cc\u3002\u201d\u90a3\u5973\u5b69\u91cd\u590d\u7740\u8bf4 \u9053\uff1a\u201c\u55ef\uff0c\u8981\u7d27\u7d27\u8bb0\u5728\u5fc3\u91cc\u3002\u6211\u77e5\u9053\u4e86\uff0c\u7237\u7237\u662f\u8bf4\u81ea\u5df1\u7684\u6545\u4e8b\uff01\u201d\u90a3\u8001\u8005\u5947\u602a\u5730\u770b\u4e86\u5b59\u5973\u4e00 \u773c\uff0c\u9053\uff1a\u201c\u4f60\u771f\u662f\u7cbe\u7075\u5f97\u53ef\u4ee5\uff0c\u6bd4\u6211\u5c0f\u65f6\uff0c\u806a\u660e\u5f97\u591a\u4e86\uff01\u201d\u6b8a\u4e0d\u77e5\u8fd9\u5973\u5b69\u81ea\u51fa\u751f\u4e4b\u540e\uff0c\u4e0a\u4e00 \u4e2a\u6708\u624d\u89c1\u7740\u5979\u7684\u7237\u7237\uff0c\u5f53\u65f6\u5979\u5c31\u66fe\u95ee\u7236\u4eb2\uff0c\u4e3a\u4ec0\u4e48\u7a81\u7136\u95f4\u6765\u4e86\u4e00\u4e2a\u7237\u7237\uff0c\u5979\u7236\u4eb2\u5bf9\u5979\u8bf4\u9053\uff1a \u201c\u6211\u7ed9\u4f60\u8bf4\u8fc7\u8bb8\u591a\u6b21\u82cf\u6b66\u7267\u7f8a\u7684\u6545\u4e8b\uff0c\u7237\u7237\u7684\u6545\u4e8b\u6bd4\u82cf\u6b66\u7267\u7f8a\u7684\u6545\u4e8b\u8fd8\u8981\u52a8\u542c\uff0c\u5c06\u6765\u7237\u7237\u81ea \u5df1\u8bf4\u7ed9\u4f60\u542c\uff0c\u4f60\u8981\u7d27\u7d27\u8bb0\u5728\u5fc3\u4e2d\u3002\u201d\u6240\u4ee5\u4eca\u665a\u7237\u7237\u4e00\u8bf4\u6545\u4e8b\uff0c\u5979\u5c31\u77e5\u9053\u90a3\u662f\u7237\u7237\u81ea\u5df1\u7684\u6545 \u4e8b\u3002 \u3000\u3000\u4f17\u4eba\u73af\u7ed5\u9a74\u8f66\uff0c\u90fd\u50cf\u90a3\u5973\u5b69\u5b50\u4e00\u6837\uff0c\u51fa\u795e\u503e\u542c\uff0c\u53ea\u89c1\u90a3\u8001\u4eba\u62ff\u51fa\u4e00\u6839\u7af9\u6756\uff0c\u6756\u5934\u4e0a\u6709\u51e0 \u6839\u7a00\u758f\u7684\u65c4\u6bdb\uff0c\u90a3\u8001\u4eba\u53f9\u8a00\u9053\uff1a\u201c\u8fd9\u4f7f\u8282\u7684\u65c4\u65cc\u9970\u54c1\u90fd\u7ed9\u5317\u5730\u7684\u51b0\u96ea\u6d88\u878d\u5c3d\u4e86\u3002\u963f\u857e\uff0c\u4f60\u77e5 \u9053\u4ec0\u4e48\u53eb\u505a\u4f7f\u8282\u5417\uff1f\u6211\u8bf4\u7ed9\u4f60\u542c\u3002\u4e8c\u5341\u5e74\u524d\uff0c\u4f60\u7237\u7237\u662f\u5927\u660e\u5929\u5b50\u7684\u4f7f\u81e3\uff0c\u5949\u9063\u5230\u8499\u53e4\u7684\u74e6\u523a \u56fd\u53bb\u4e92\u901a\u53cb\u597d\uff0c\u8fd9\u6839\u7af9\u6756\u5c31\u662f\u7687\u5e1d\u6240\u8d50\u7684\uff0c\u79f0\u4e3a\u4f7f\u8282\uff0c\u8fd9\u4f7f\u8282\u4ee3\u8868\u5929\u5b50\uff0c\u6027\u547d\u53ef\u4e22\uff0c\u8282\u4e0d\u53ef \u6bc1\u3002\u90a3\u65f6\u8499\u53e4\u5206\u4e3a\u4e24\u90e8\uff0c\u4e00\u53eb\u74e6\u523a\uff0c\u4e00\u53eb\u9791\u977c\uff0c\u56fd\u529b\u8fd8\u5f88\u5fae\u5f31\u3002\u5927\u660e\u5929\u5b50\u6d3e\u4f7f\u81e3\u4eb2\u4e34\uff0c\u7167\u7406 \u5e94\u8be5\u5f88\u53d7\u4ed6\u4eec\u7684\u5c0a\u656c\uff0c\u5374\u4e0d\u6599\u5728\u5448\u9012\u56fd\u4e66\u4e4b\u65e5\uff0c\u90a3\u74e6\u523a\u738b\u8d77\u521d\u8fd8\u5f6c\u5f6c\u6709\u793c\uff0c\u540e\u6765\u6765\u4e86\u4e00\u4e2a\u8eab \u62ab\u80e1\u670d\u7684\u6c49\u4eba\uff0c\u4f69\u5251\u4e0a\u671d\uff0c\u628a\u74e6\u523a\u738b\u62c9\u8fc7\u4e00\u8fb9\uff0c\u6084\u6084\u8bf4\u8bdd\uff0c\u4e00\u8fb9\u8bf4\u4e00\u8fb9\u770b\u7740\u6211\u3002\u8fd9\u6c49\u4eba\u4e0d\u8fc7 \u4e8c\u5341\u6765\u5c81\u7684\u6837\u5b50\uff0c\u773c\u5149\u4e2d\u5374\u9732\u7740\u65e0\u9650\u6028\u6bd2\uff0c\u597d\u50cf\u6211\u548c\u4ed6\u6709\u7740\u767e\u8f7d\u6df1\u4ec7\uff01\u201d \u3000\u3000\u8c22\u5929\u534e\u5947\u9053\uff1a\u201c\u90a3\u4eba\u662f\u8ba4\u5f97\u8001\u4f2f\u7684\u5417\uff1f\u201d\u4e91\u9756\u9053\uff1a\u201c\u4e0d\uff0c\u6211\u7edd\u4e0d\u8ba4\u8bc6\u4ed6\u3002\u6211\u81ea\u95ee\u5c45\u5b98\u6e05 \u767d\uff0c\u5e73\u751f\u6ca1\u6709\u4ec7\u4eba\uff0c\u66f4\u4e0d\u4f1a\u5728\u80e1\u4eba\u4e4b\u5730\u7ed3\u6709\u4ec7\u4eba\uff0c\u4e5f\u4e0d\u77e5\u4ed6\u5bf9\u6211\u4f55\u4ee5\u5982\u6b64\u6028\u6bd2\uff01\u4e0d\u8fc7\uff0c\u6211\u5f53 \u65f6\u89c1\u4ed6\u8eab\u62ab\u80e1\u670d\uff0c\u4e5f\u786e\u5b9e\u4e0d\u5c51\u548c\u4ed6\u4ea4\u8c08\u3002\u4ed6\u548c\u74e6\u523a\u738b\u8c08\u4e86\u4e00\u9635\uff0c\u7a81\u7136\u4e0b\u4ee4\u5c06\u6211\u6263\u7559\uff0c\u8fd8\u8981\u593a \u6211\u7684\u4f7f\u8282\u3002\u6211\u5927\u6012\u6297\u8bae\uff1a\u6027\u547d\u53ef\u4ee5\u4e22\uff0c\u8fd9\u4ee3\u8868\u5927\u660e\u5929\u5b50\u7684\u4f7f\u8282\u5374\u4e0d\u53ef\u6bc1\u3002\u53ef\u6068\u4ed6\u8eab\u662f\u6c49\u4eba\uff0c \u542c\u4e86\u4e4b\u540e\uff0c\u53cd\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u2018\u5927\u660e\u5929\u5b50\uff0c\u5927\u660e\u5929\u5b50\uff01\u54c8\u54c8\uff0c\u4f60\u662f\u51c6\u5907\u505a\u5927\u660e\u5929\u5b50\u7684\u5fe0\u81e3\u6765 \u4e86\uff1f\u597d\uff01\u6211\u4e00\u5b9a\u53eb\u4f60\u79f0\u5fc3\u5982\u613f\uff0c\u505a\u7b2c\u4e8c\u4e2a\u82cf\u6b66\uff0c\u82cf\u6b66\u7267\u7f8a\uff0c\u4f60\u5c31\u53bb\u7267\u9a6c\u5427\uff01\u2019\u81ea\u6b64\u6211\u4fbf\u5728\u6781 \u5317\u82e6\u5bd2\u4e4b\u5730\uff0c\u7267\u9a6c\u4e8c\u5341\u5e74\uff01\u8d77\u521d\u6211\u8fd8\u6307\u671b\u660e\u671d\u6d3e\u5175\u6765\u6551\uff0c\u5e74\u590d\u4e00\u5e74\uff0c\u5374\u662f\u6beb\u65e0\u6d88\u606f\u3002\u540e\u6765\u542c \u8bf4\u5927\u660e\u7687\u5e1d\uff0d\uff0d\u660e\u6210\u7956\u6731\u68e3\uff0d\uff0d\u5f52\u5929\uff0c\u4ec1\u5b97\u7ee7\u7acb\uff0c\u4e0d\u5230\u4e00\u5e74\uff0c\u53c8\u544a\u592d\u6298\uff0c\u5e7c\u4e3b\u5373\u4f4d\uff0c\u56fd\u4e2d\u65e0 \u4eba\uff0c\u592a\u7956\u3001\u6210\u7956\u5f00\u7586\u8f9f\u571f\u7684\u524d\u4ee3\u96c4\u98ce\uff0c\u5df2\u6210\u9648\u8ff9\uff0c\u6211\u65ad\u4e86\u5ff5\u5934\uff0c\u81ea\u5206\u5fc5\u8001\u6b7b\u5f02\u56fd\uff0c\u96be\u56de\u6c49\u57df \u4e86\uff0c\u8c01\u77e5\u4e5f\u8fd8\u6709\u4eca\u65e5\uff01\u201d \u3000\u3000\u8c22\u5929\u534e\u4e0e\u6f6e\u97f3\u548c\u5c1a\u76f8\u5bf9\u4e00\u89c6\uff0c\u9ed8\u4e0d\u4f5c\u58f0\uff0c\u9762\u8272\u5947\u5f02\uff0c\u4f3c\u662f\u65e2\u6709\u4f69\u670d\u4e4b\u60c5\u5374\u53c8\u6709\u4e0d\u4ee5\u4e3a\u7136 \u4e4b\u610f\u3002\u4e91\u9756\u6beb\u4e0d\u5728\u610f\uff0c\u58f0\u8c03\u8d8a\u53d1\u4f4e\u6c89\uff0c\u5341\u6307\u5c48\u62d7\uff0c\u52d2\u52d2\u4f5c\u54cd\uff0c\u53c8\u9053\uff1a\u201c\u4e8c\u5341\u5e74\u6765\uff0c\u6211\u53d7\u4e86\u65e0 \u6570\u7684\u82e6\uff0c\u5728\u6c99\u6f20\u4e4b\u4e2d\uff0c\u65e0\u6c34\u53ef\u996e\uff0c\u6709\u65f6\u4fbf\u559d\u9a6c\u5c3f\u89e3\u6e34\uff0c\u5230\u4e86\u79cb\u51ac\u4e4b\u5b63\uff0c\u996e\u51b0\u56bc\u96ea\uff0c\u66f4\u662f\u5bfb\u5e38 \u4e4b\u4e8b\u4e86\uff01\u8fd9\u4e9b\u90fd\u8fd8\u4e0d\u7b97\u4ec0\u4e48\uff0c\u66f4\u53ef\u6068\u7684\u662f\uff0c\u90a3\u53ae\u8fd8\u65f6\u4e0d\u65f6\u6d3e\u4eba\u6765\u770b\u6211\uff0c\u5728\u6211\u7684\u9762\u524d\uff0c\u8fb1\u9a82\u5927 \u660e\u5929\u5b50\u3002\u4e8c\u5341\u5e74\u6765\uff0c\u6211\u65e0\u65f6\u4e0d\u51c6\u5907\u6b7b\u96be\uff0c\u53ef\u6068\u90a3\u53ae\u5374\u53c8\u5e76\u4e0d\u6740\u6211\uff0c\u53ea\u662f\u5c06\u6211\u6298\u78e8\u3002\u201d\u4e91\u857e\u542c \u5f97\u597d\u4e0d\u6124\u6012\uff0c\u95ee\u9053\uff1a\u201c\u90a3\u574f\u4eba\u53eb\u4ec0\u4e48\u540d\u5b57\uff1f\u7237\u7237\u8bf4\u7ed9\u6211\u542c\uff0c\u857e\u857e\u5927\u4e86\u66ff\u4f60\u62a5\u4ec7\u3002\u201d\u4e91\u9756\u7eed \u9053\uff1a\u201c\u4e0d\u4e45\u6211\u5c31\u77e5\u9053\uff0c\u90a3\u53ae\u59d3\u5f20\uff0c\u53cc\u540d\u5b97\u5468\uff0c\u540d\u4e3a\u2018\u5b97\u5468\u2019\uff0c\u5b9e\u5219\u4e0d\u5b97\u5468\uff0c\u8bd5\u60f3\u5468\u5ba4\u4e43\u662f\u5929 \u4e0b\u7684\u5171\u4e3b\uff0c\u65e2\u662f\u5b97\u5468\uff0c\u5374\u53c8\u8fb1\u9a82\u5927\u660e\u7684\u5929\u5b50\uff0c\u90a3\u4e0d\u662f\u81ea\u5df1\u5632\u9a82\u81ea\u5df1\u5417\uff1f\u201d\u90a3\u5973\u5b69\u5b50\u4e0d\u61c2\u5f97\u4ec0 \u4e48\u53eb\u505a\u201c\u5468\u5ba4\u201d\uff0c\u66f4\u4e0d\u61c2\u4ec0\u4e48\u53eb\u505a\u201c\u5171\u4e3b\u201d\uff0c\u6b63\u76f8\u53d1\u95ee\uff0c\u53ea\u542c\u5f97\u5979\u7684\u7237\u7237\u53c8\u9053\uff1a\u201c\u8fd9\u4e9b\u5386\u53f2 \u4e0a\u7684\u4e8b\u60c5\uff0c\u4f60\u957f\u5927\u4e86\u5ff5\u4e86\u4e66\u81ea\u7136\u660e\u767d\uff0c\u7237\u7237\u4e0d\u518d\u591a\u8bf4\u4e86\u3002\u201d\u4e91\u9756\u5176\u5b9e\u4e0d\u53ea\u662f\u8bf4\u7ed9\u5b59\u5973\u542c\uff0c\u4e5f \u662f\u8bf4\u7ed9\u90a3\u4e24\u4f4d\u4fa0\u58eb\u542c\u3002\u81f3\u6b64\u987f\u4e86\u4e00\u987f\uff0c\u7a81\u7136\u63d0\u9ad8\u58f0\u8c03\u95ee\u9053\uff1a\u201c\u4e24\u4f4d\u4fa0\u58eb\uff0c\u4f60\u8bf4\u8fd9\u53ae\u8be5\u4e0d\u8be5 \u6740\uff1f\u201d\u6f6e\u97f3\u548c\u5c1a\u7985\u6756\u987f\u5730\u4e0e\u8c22\u5929\u534e\u62a2\u7740\u8bf4\u9053\u201c\u8be5\u6740\uff01\u201d \u3000\u3000\u4e91\u9756\u5fae\u5fae\u4e00\u7b11\uff0c\u629a\u7740\u5b59\u5973\u7684\u5934\u53c8\u9053\uff1a\u201c\u90a3\u5f20\u5b97\u5468\u539f\u6765\u662f\u5978\u8d3c\u4e16\u5bb6\uff0c\u4ed6\u7684\u7236\u4eb2\u5df2\u5728\u8499\u53e4\u4e3a \u5b98\uff0c\u81f3\u4ed6\u66f4\u5f97\u91cd\u7528\uff0c\u4e8c\u5341\u591a\u5c81\uff0c\u5c31\u5f53\u4e86\u74e6\u523a\u56fd\u7684\u53f3\u4e1e\u76f8\uff0c\u4e0e\u5de6\u4e1e\u76f8\u8131\u6b22\uff0c\u540c\u5f97\u74e6\u523a\u53ef\u6c57\u8131\u8131 \u4e0d\u82b1\u7684\u91cd\u7528\uff0c\u4ed6\u8eab\u5b50\u5f88\u597d\uff0c\u60f3\u6765\u8fd8\u6709\u4e8c\u4e09\u5341\u5e74\u7684\u547d\u3002\u6211\u5728\u51b0\u5929\u96ea\u5730\u4e4b\u4e2d\u7267\u9a6c\u76ee\u76fc\u591c\u76fc\uff0c\u53ea\u76fc \u671b\u4ed6\u5409\u4e07\u4e0d\u8981\u65e9\u6b7b\uff01\u201d\u6f6e\u97f3\u548c\u5c1a\u6027\u60c5\u6897\u76f4\uff0c\u95fb\u8a00\u602a\u9053\uff1a\u201c\u8fd9\u5374\u662f\u4e3a\u4e86\u4ec0\u4e48\uff1f\u201d\u4e91\u9756\u591a\u5e74\u6124 \u6012\uff0c\u4e45\u8574\u5fc3\u4e2d\uff0c\u8bf4\u5230\u6b64\u5904\uff0c\u51b7\u51b7\u4e00\u7b11\u3002\u4e91\u857e\u6253\u4e86\u4e00\u4e2a\u5bd2\u5664\uff0c\u53ea\u89c1\u5979\u7684\u7237\u7237\u5728\u6000\u4e2d\u6478\u51fa\u4e00\u5757\u7f8a \u76ae\uff0c\u4e0a\u9762\u5199\u7740\u51e0\u884c\u7ea2\u5b57\uff0c\u9690\u9690\u95fb\u5230\u8840\u8165\u5473\u3002 \u3000\u3000\u8c22\u5929\u534e\u9a87\u7136\u8bf4\u9053\uff1a\u201c\u4e91\u8001\u4f2f\uff0c\u8fd9\u662f\u4f60\u5199\u7684\u8840\u4e66\uff1f\u201d\u4e91\u9756\u6de1\u7136\u8bf4\u9053\uff1a\u201c\u8fd9\u5df2\u7ecf\u662f\u7b2c\u4e8c\u4efd \u4e86\u3002\u6211\u8d77\u521d\u6307\u671b\u671d\u5ef7\u5174\u5e08\u95ee\u7f6a\uff0c\u5c06\u5978\u8d3c\u62ff\u7740\uff0c\u660e\u6b63\u5178\u5211\uff0c\u540e\u6765\u5b9e\u662f\u65e0\u671b\uff0c\u60f3\u81ea\u5df1\u523a\u6740\u5978\u8d3c\uff0c \u81ea\u5df1\u5374\u53c8\u662f\u624b\u65e0\u7f1a\u9e21\u4e4b\u529b\u7684\u4e66\u751f\uff0c\u60f3\u6765\u60f3\u53bb\uff0c\u53ea\u6709\u76fc\u671b\u6211\u513f\u5b59\u4eec\u4e89\u6c14\uff0c\u5f03\u6587\u4e60\u6b66\uff0c\u80fd\u66ff\u6211\u62a5 \u8fd9\u5927\u6068\u6df1\u4ec7\u3002\u679c\u7136\u5929\u4ece\u4eba\u613f\uff0c\u6211\u7267\u9a6c\u5341\u5e74\u4e4b\u4e45\uff0c\u6f84\u513f\u4e5f\u5230\u4e86\u80e1\u8fb9\uff0c\u9690\u59d3\u57cb\u540d\uff0c\u5bfb\u627e\u6211\u7684\u8e2a \u8ff9\u3002\u6211\u51fa\u4f7f\u4e4b\u524d\uff0c\u4ed6\u521a\u521a\u8003\u53d6\u79c0\u624d\uff0c\u662f\u4e2a\u6587\u8d28\u5f6c\u5f6c\u7684\u4e66\u751f\uff0c\u5728\u80e1\u8fb9\u518d\u89c1\u4e4b\u65f6\uff0c\u4ed6\u5df2\u662f\u4e2a\u96c4\u8d73 \u8d73\u7684\u6b66\u592b\u4e86\u3002\u539f\u6765\u4ed6\u77e5\u9053\u671d\u5ef7\u4e0d\u613f\u4e3a\u6211\u4e00\u4eba\uff0c\u5174\u5e08\u95ee\u7f6a\uff0c\u4e8e\u662f\u4fbf\u5f03\u6587\u4e60\u6b66\uff0c\u60f3\u6df1\u5165\u80e1\u8fb9\uff0c\u5355 \u9a91\u6551\u7236\u3002\u542c\u8bf4\u4ed6\u5728\u5929\u4e0b\u7b2c\u4e00\u5251\u5ba2\u7384\u673a\u9038\u58eb\u7684\u95e8\u4e0b\u5b66\u4e86\u4e03\u5e74\uff0c\u6b66\u529f\u867d\u672a\u6709\u5927\u6210\uff0c\u7b49\u95f2\u4e09\u4e94\u5341\u4eba \u5df2\u8fd1\u4ed6\u4e0d\u5f97\uff0c\u4ed6\u6551\u7236\u5fc3\u6025\uff0c\u4e0d\u7b49\u6ee1\u5e08\uff0c\u4fbf\u8d76\u6765\u4e86\u3002\u201d\u4e91\u857e\u542c\u5f97\u51fa\u795e\uff0c\u4e00\u53cc\u773c\u73e0\u6ef4\u6e9c\u6e9c\u5730\u8f6c\u6765 \u8f6c\u53bb\uff0c\u5fc3\u4e2d\u5145\u6ee1\u7591\u60d1\uff0c\u95ee\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u7239\u7239\u65e2\u6709\u90a3\u4e48\u5927\u7684\u672c\u9886\uff0c\u4e3a\u4ec0\u4e48\u6211\u4e00\u70b9\u4e5f\u4e0d\u77e5\u9053\uff1f\u6211 \u53ea\u89c1\u4ed6\u5929\u5929\u548c\u5988\u5988\u4e00\u540c\u53bb\u7267\u7f8a\uff0c\u6709\u4e00\u5929\uff0c\u6709\u4e00\u4e2a\u9791\u5b50\u5175\u6b3a\u8d1f\u4ed6\uff0c\u8981\u62a2\u4ed6\u7684\u7f8a\uff0c\u6253\u4ed6\u4e5f\u6ca1\u6709\u8fd8 \u624b\u3002\u201d \u3000\u3000\u4e91\u9756\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u963f\u857e\uff0c\u4f60\u8fd8\u5c0f\uff0c\u6709\u8bb8\u591a\u4e8b\u60c5\uff0c\u8bf4\u7ed9\u4f60\u542c\uff0c\u4f60\u4e5f\u4e0d\u61c2\u3002\u4e0d\u8fc7\uff0c\u5c06\u6765 \u5c31\u7b97\u6211\u6b7b\u4e86\uff0c\u4e0d\u53ca\u89c1\u4f60\u957f\u5927\uff0c\u4e24\u4f4d\u4f2f\u4f2f\u4e5f\u4f1a\u544a\u8bc9\u4f60\u7684\u3002\u201d \u3000\u3000\u8c22\u5929\u534e\u77e5\u9053\u4e91\u9756\u4eca\u665a\u503e\u8c08\u8eab\u4e16\uff0c\u5176\u5b9e\u662f\u60f3\u8bf4\u7ed9\u4ed6\u4eec\u542c\uff0c\u5176\u4e2d\u5fc5\u6709\u542b\u610f\u3002\u89c1\u4e91\u9756\u8eab\u8eaf\u98a4 \u6296\uff0c\u5fae\u5fae\u5598\u606f\uff0c\u4fbf\u6276\u7740\u4ed6\u9053\uff1a\u201c\u8001\u4f2f\uff0c\u4f60\u6b47\u6b47\u5427\uff0c\u8bf4\u8bdd\u7684\u65f6\u5019\u8fd8\u591a\u7740\u5462\uff0c\u7b49\u5230\u4e86\u96c1\u95e8\u5173\u4e4b\u540e \u518d\u8bf4\u5427\uff0c\u8001\u4f2f\u4ed6\u65e5\u6709\u4ec0\u4e48\u5429\u5490\uff0c\u665a\u8f88\u4e00\u5b9a\u4f9d\u4ece\u3002\u201d \u3000\u3000\u4e91\u9756\u54b3\u4e86\u4e00\u58f0\uff0c\u5598\u7740\u6c14\u9053\uff1a\u201c\u4e0d\uff0c\u6211\u4e00\u5b9a\u8981\u8bf4\u4e0b\u53bb\u3002\u8fd9\u4e9b\u4e8b\u60c5\u618b\u5728\u5fc3\u4e2d\u592a\u4e45\u592a\u4e45\u4e86\uff0c\u4e0d \u8bf4\u51fa\u6765\uff0c\u5c31\u4e0d\u75db\u5feb\u3002\u201d\u6b47\u4e86\u4e00\u4f1a\u513f\uff0c\u63a5\u4e0b\u53bb\u9053\uff1a\u201c\u6f84\u513f\u628a\u4e8b\u60c5\u770b\u5f97\u592a\u5bb9\u6613\uff0c\u4ee5\u4e3a\u51ed\u4ed6\u7684\u6b66\u529f \u4fbf\u53ef\u4ee5\u5c06\u6211\u6551\u51fa\u80e1\u8fb9\u3002\u8c01\u77e5\u5929\u5916\u6709\u5929\uff0c\u4eba\u4e0a\u6709\u4eba\uff0c\u8499\u53e4\u5730\u65b9\u4e5f\u6709\u8bb8\u591a\u9ad8\u624b\uff0c\u5c31\u662f\u90a3\u5f20\u5b97\u5468\u7684 \u624b\u4e0b\uff0c\u4e5f\u7740\u5b9e\u6709\u51e0\u4e2a\u672c\u9886\u975e\u51e1\u7684\u4eba\u7269\u3002\u6211\u5728\u96ea\u5730\u7267\u9a6c\uff0c\u6697\u4e2d\u5b9e\u662f\u6709\u4eba\u76d1\u89c6\u3002\u6f84\u513f\u597d\u4e0d\u5bb9\u6613\u627e \u7740\u4e86\u6211\uff0c\u8fd8\u672a\u6765\u5f97\u53ca\u5546\u8bae\u9003\u8dd1\uff0c\u5c31\u7ed9\u4eba\u53d1\u73b0\uff0c\u4e0d\u662f\u6211\u53eb\u4ed6\u5feb\u9003\uff0c\u8fde\u4ed6\u90fd\u51e0\u4e4e\u7ed9\u4eba\u64d2\u62ff\u4f4f\u3002\u540e \u6765\u4ed6\u53c8\u6697\u4e2d\u548c\u5f20\u5b97\u5468\u7684\u624b\u4e0b\u8f83\u91cf\u4e86\u51e0\u6b21\uff0c\u90fd\u8ba8\u4e0d\u4e86\u4fbf\u5b9c\uff0c\u8fd9\u624d\u628a\u5355\u9a91\u6551\u7236\u7684\u5ff5\u5934\u653e\u4e0b\u6765\u3002\u56e0 \u6b64\u4ed6\u4fbf\u9075\u7167\u6211\u7684\u53ee\u5631\uff0c\u9690\u59d3\u57cb\u540d\u5728\u8499\u53e4\u4f4f\u4e0b\u6765\uff0c\u88c5\u505a\u4e00\u70b9\u4e5f\u4e0d\u61c2\u5f97\u6b66\u529f\u7684\u6a21\u6837\uff0c\u6697\u4e2d\u5bfb\u627e\u673a \u4f1a\uff0c\u548c\u6211\u5077\u901a\u8baf\u606f\u3002\u201d \u3000\u3000\u201c\u6211\u8981\u4ed6\u5728\u8499\u53e4\u4f4f\u4e0b\u6765\uff0c\u53c8\u8981\u4ed6\u5a36\u4e86\u80e1\u5973\u4e3a\u59bb\uff0c\u4e3a\u7684\u5c31\u662f\u66ff\u6211\u4f20\u5b97\u63a5\u4ee3\uff0c\u597d\u62a5\u6b64\u5927\u6068\u6df1 \u4ec7\u3002\u6211\u60f3\u8d77\u611a\u516c\u79fb\u5c71\u7684\u6545\u4e8b\uff0c\u8fd9\u4ec7\u6211\u7684\u513f\u5b50\u82e5\u4e0d\u80fd\u62a5\uff0c\u8fd8\u6709\u6211\u7684\u5b59\u5b50\u6765\u62a5\uff0c\u6211\u7684\u5b59\u5b50\u4e0d\u80fd \u62a5\uff0c\u8fd8\u6709\u6211\u7684\u66fe\u5b59\uff0c\u53ea\u8981\u6211\u4e91\u5bb6\u8fd8\u6709\u540e\u4eba\uff0c\u8fd9\u4ec7\u5c31\u4e00\u5b9a\u80fd\u62a5\u3002\u800c\u5f20\u5bb6\u5462\uff0c\u5373\u7b97\u5f20\u5b97\u5468\u6b7b\u4e86\uff0c \u4ed6\u4e5f\u8fd8\u6709\u540e\u4eba\uff0c\u4ed6\u7684\u540e\u4eba\u4e5f\u8981\u66ff\u4ed6\u53d7\u8fd9\u62a5\u5e94\uff01\u6211\u4e03\u5e74\u524d\u542c\u8bf4\u4ed6\u751f\u4e86\u4e00\u4e2a\u7537\u5b69\uff0c\u6211\u5c31\u5199\u4e0b\u4e86\u7b2c \u4e00\u4efd\u8840\u4e66\uff0c\u8981\u6211\u7684\u7537\u5b59\u7d27\u8bb0\uff0c\u65e5\u540e\u957f\u5927\u4e86\uff0c\u53ea\u8981\u78b0\u7740\u4e86\u5f20\u5b97\u5468\u8fd9\u4e00\u8109\u6240\u4f20\u7684\u4eba\uff0c\u4e0d\u8bba\u7537\u5973\u8001 \u5e7c\uff0c\u90fd\u8981\u66ff\u6211\u628a\u4ed6\u4eec\u6740\u6389\uff01\u201d \u3000\u3000\u8c22\u5929\u534e\u53ea\u611f\u5230\u4e00\u9635\u9635\u5bd2\u610f\uff0c\u76f4\u900f\u5fc3\u5934\uff0c\u5634\u8fb1\u6380\u52a8\uff0c\u5374\u53c8\u5fcd\u7740\uff0c\u5fc3\u9053\uff1a\u201c\u6028\u6bd2\u4e4b\u751a\uff0c\u7adf\u81f3 \u5982\u6b64\uff01\u8fd9\u6837\u7684\u62a5\u590d\uff0c\u5c82\u4e0d\u6bd4\u6c5f\u6e56\u4e0a\u7684\u4ec7\u6740\u8fd8\u8981\u6b8b\u9177\uff1f\u60f3\u6765\u4ed6\u5728\u51b0\u5929\u96ea\u5730\u91cc\u7267\u9a6c\u4e8c\u5341\u5e74\uff0c\u53d7\u5c3d \u6298\u78e8\uff0c\u6240\u4ee5\u5931\u53bb\u4e86\u5e38\u6027\u3002\u4e14\u5f85\u4ed6\u56de\u5230\u4e2d\u571f\u4e4b\u540e\uff0c\u7cbe\u795e\u6062\u590d\uff0c\u518d\u6162\u6162\u529d\u89e3\u4ed6\u5427\u3002\u201d \u3000\u3000\u4e91\u9756\u6307\u7740\u8840\u4e66\uff0c\u5fae\u5fae\u5598\u6c14\uff0c\u53c8\u9053\uff1a\u201c\u6f84\u513f\u542c\u6211\u7684\u5631\u5490\u5c06\u8840\u4e66\u7f1d\u5728\u5b69\u5b50\u7684\u8863\u88f3\u91cc\uff0c\u9001\u7ed9\u4ed6 \u7684\u4e00\u4f4d\u5e08\u5144\u4e3a\u5f92\u3002\u6b64\u540e\u6211\u56e0\u4e3a\u8f6c\u79fb\u5730\u65b9\u7267\u9a6c\uff0c\u53c8\u5931\u53bb\u4e86\u8054\u7cfb\uff0c\u76f4\u5230\u4e09\u4e2a\u6708\u524d\uff0c\u4ed6\u624d\u5077\u5077\u5730\u548c \u6211\u89c1\u4e86\u4e00\u9762\uff0c\u544a\u8bc9\u6211\uff0c\u4ed6\u5df2\u7ea6\u4e86\u540c\u95e8\uff0c\u8d76\u6765\u8425\u6551\u3002\u90a3\u65f6\uff0c\u6211\u81ea\u5ff5\u5e74\u8fc8\u82cd\u82cd\uff0c\u5df2\u4e0d\u518d\u4f5c\u9003\u751f\u4e4b \u60f3\uff0c\u5bf9\u4ed6\u7684\u8bdd\uff0c\u4e5f\u4e0d\u5728\u610f\uff0c\u53ea\u95e8\u4ed6\u5728\u8fd9\u522b\u540e\u4e03\u5e74\u4e4b\u4e2d\uff0c\u6709\u6ca1\u6709\u518d\u751f\u5b69\u5b50\uff1f\u4ed6\u8bf4\u53c8\u751f\u4e86\u4e00\u4e2a\u5973 \u513f\uff0c\u8fd9\u4fbf\u662f\u4f60\u3002\u6211\u7acb\u523b\u518d\u5199\u4e0b\u4e00\u4efd\u8840\u4e66\uff0c\u662f\u5b59\u5973\u4e5f\u8981\u66ff\u6211\u62a5\u4ec7\u3002\u857e\u857e\uff0c\u4ee5\u540e\u4f60\u8981\u7d27\u7d27\u8bb0\u7740\uff1a \u82e5\u78b0\u7740\u5f20\u5b97\u5468\u4e00\u8109\u6240\u4f20\u7684\u4eba\uff0c\u4e0d\u8bba\u7537\u5973\u8001\u5e7c\uff0c\u90fd\u8981\u66ff\u6211\u628a\u4ed6\u4eec\u6740\u6389\uff0c\u5316\u9aa8\u626c\u7070\uff01\u201d \u3000\u3000\u4e91\u857e\u542c\u5f97\u5b9a\u4e86\u773c\u795e\uff0c\u82f9\u679c\u822c\u7684\u5c0f\u8138\u4e0a\u5145\u6ee1\u4e86\u5bb3\u6015\u6050\u60e7\u7684\u8868\u60c5\uff0c\u7a81\u7136\u201c\u54c7\u201d\u7684\u4e00\u58f0\u54ed\u8d77\u6765 \u9053\uff1a\u201c\u7237\u7237\uff0c\u8981\u6740\u90a3\u4e48\u591a\u4eba\u5417\uff1f\u857e\u857e\u5bb3\u6015\uff0c\u5988\u5988\u81ea\u5e7c\u6559\u6211\u4e0d\u8981\u968f\u4fbf\u6740\u751f\uff0c\u8fde\u521d\u751f\u7684\u7f8a\u7f94\u4e5f\u8981 \u4fdd\u62a4\u3002\u54ce\uff0c\u5988\u5988\u5462\uff1f\u7239\u7239\u8bf4\u5988\u5988\u5c31\u8981\u6765\u7684\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u89c1\u5988\u5988\u6765\uff0c\u8fde\u7239\u7239\u4e5f\u4e0d\u89c1\u4e86\uff1f\u201d\u5979\u54ea\u91cc \u77e5\u9053\uff0c\u5979\u7684\u7239\u7239\u4e91\u6f84\u5728\u80e1\u8fb9\u9690\u59d3\u57cb\u540d\uff0c\u8eab\u4e16\u6765\u5386\u8fde\u5979\u7684\u5988\u5988\u4e5f\u6ca1\u6709\u544a\u8bc9\uff0c\u4e00\u6708\u4e4b\u524d\uff0c\u7adf\u662f\u7792 \u7740\u59bb\u5b50\uff0c\u5f03\u5bb6\u9003\u8d70\u7684\u3002 \u3000\u3000\u4e91\u9756\u767d\u987b\u6380\u52a8\uff0c\u7a81\u7136\u6012\u58f0\u8bf4\u9053\uff1a\u201c\u857e\u857e\uff0c\u4f60\u4e0d\u542c\u6211\u7684\u8bdd\u4e86\u5417\uff1f\u6211\u544a\u8bc9\u4f60\uff0c\u4f60\u7684\u7239\u7239\uff0c\u4f60 \u7684\u7239\u7239\uff0c\u4ed6\u5df2\u7ecf\u2500\u201d\u795e\u8272\u4ff1\u5389\uff0c\u5413\u5f97\u4e91\u857e\u5664\u4e0d\u4f5c\u58f0\uff0c\u773c\u6cea\u4e5f\u6536\u4e86\uff0c\u4e91\u9756\u53f9\u4e86\u53e3\u6c14\uff0c\u8bdd\u5230\u53e3 \u8fb9\uff0c\u53c8\u54bd\u4e86\u56de\u53bb\uff0c\u4e0d\u5fcd\u628a\u5979\u7239\u7239\u7684\u6b7b\u8baf\u518d\u8bf4\u51fa\u6765\u3002 \u3000\u3000\u8c22\u5929\u534e\u6697\u6697\u53f9\u6c14\uff0c\u6447\u4e86\u6447\u5934\uff0c\u53ea\u89c1\u4e91\u857e\u4f4e\u4e0b\u4e86\u5934\uff0c\u5c0f\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u542c\u7237\u7237\u7684\u8bdd\uff01\u201d\u4e91\u9756 \u628a\u4e09\u6708\u524d\u65b0\u5199\u7684\u8840\u4e66\u585e\u5230\u5979\u7684\u6000\u91cc\uff0c\u4ef0\u5929\u7b11\u9053\uff1a\u201c\u4e0d\u60f3\u6211\u4e91\u9756\u5c1a\u6709\u9003\u51fa\u5f02\u57df\uff0c\u91cd\u5f52\u6545\u91cc\u4e4b \u65f6\u3002\u8c22\u4fa0\u58eb\uff0c\u6c42\u4f60\u77a7\u5728\u6f84\u513f\u7684\u9762\u4e0a\uff0c\u628a\u8fd9\u5973\u5a03\u5b50\u6536\u505a\u5f92\u5f1f\u5427\uff01\u201d \u3000\u3000\u8c22\u5929\u534e\u4e00\u9635\u8fdf\u7591\uff0c\u7f13\u7f13\u7b54\u9053\uff1a\u201c\u8fd9\u4e2a\u4e14\u6162\u5546\u91cf\u3002\u2500\u55ef\uff0c\u8001\u4f2f\u4e0d\u8981\u8bef\u4f1a\uff0c\u4e0d\u662f\u6211\u4e0d\u7b54\u5e94 \u60a8\uff0c\u6211\u662f\u60f3\u66ff\u5979\u627e\u4e00\u4e2a\u66f4\u52a0\u597d\u7684\u5e08\u7236\u3002\u201d \u3000\u3000\u8c22\u5929\u534e\u4e0e\u6f6e\u97f3\u548c\u5c1a\u4e43\u662f\u4e91\u6f84\u7684\u540c\u95e8\uff0c\u4ed6\u4eec\u7684\u5e08\u7236\u7384\u673a\u9038\u58eb\u53f7\u79f0\u5929\u4e0b\u7b2c\u4e00\u5251\u5ba2\uff0c\u4e0d\u6b62\u5728\u5251 \u672f\u4e0a\u6709\u6781\u7cbe\u6e5b\u7684\u9020\u8be3\uff0c\u5176\u4ed6\u7684\u6b66\u529f\uff0c\u4e5f\u5f88\u535a\u6742\u3002\u53ea\u662f\u7384\u673a\u9038\u58eb\u813e\u6c14\u53e4\u602a\uff0c\u4ed6\u5171\u6709\u4e94\u4e2a\u5f92\u5f1f\uff0c \u6bcf\u4e2a\u5f92\u5f1f\uff0c\u53ea\u4f20\u4e00\u95e8\u6b66\u529f\u3002\u4f8b\u5982\u8c22\u5929\u534e\u5c31\u53ea\u5f97\u5251\u672f\u7684\u4e00\u534a\u3002\u600e\u4e48\u53eb\u505a\u4e00\u534a\uff1f\u539f\u6765\u7384\u673a\u9038\u58eb\u6709 \u4e24\u5957\u5251\u6cd5\uff0c\u76f8\u53cd\u76f8\u6210\u3002\u4ed6\u53c8\u70bc\u6709\u96cc\u96c4\u53cc\u5251\uff0c\u96cc\u5251\u540d\u53eb\u201c\u9752\u51a5\u201d\uff0c\u96c4\u5251\u540d\u4e3a\u201c\u767d\u4e91\u201d\uff0c\u201c\u767d \u4e91\u201d\u96c4\u5251\u4f20\u7ed9\u8c22\u5929\u534e\uff0c\u201c\u9752\u51a5\u201d\u96cc\u5251\u5219\u4f20\u7ed9\u4e86\u53e6\u4e00\u4e2a\u5973\u5f1f\u5b50\uff0c\u4e24\u4eba\u5404\u5f97\u4e86\u4ed6\u7684\u4e00\u5957\u5251\u672f\u3002 \u3000\u3000\u8fd9\u4e24\u5957\u5251\u672f\u4e43\u662f\u7384\u673a\u9038\u58eb\u6bd5\u751f\u5fc3\u8840\u6240\u805a\uff0c\u82e5\u7136\u53cc\u5251\u5408\u58c1\uff0c\u5929\u4e0b\u65e0\u654c\u3002\u6240\u4ee5\u5728\u4ed6\u95e8\u4e0b\u4e94\u4eba \u4e4b\u4e2d\uff0c\u4e5f\u4ee5\u8c22\u5929\u534e\u548c\u90a3\u4e2a\u5973\u5f1f\u5b50\u6b66\u529f\u6700\u9ad8\uff0c\u96be\u5206\u8f69\u8f7e\u3002\u81f3\u4e8e\u4e91\u6f84\uff0c\u5219\u56e0\u5c1a\u672a\u6ee1\u5e08\uff0c\u6b66\u529f\u6700 \u5f31\u3002\u90a3\u6f6e\u97f3\u548c\u5c1a\u5219\u662f\u4e8c\u5f92\u5f1f\uff0c\u4f20\u4e86\u4f0f\u9b54\u6756\u6cd5\uff0c\u5916\u5bb6\u529f\u592b\uff0c\u4e5f\u5230\u4e86\u767b\u5cf0\u9020\u6781\u7684\u5730\u6b65\u3002 \u3000\u3000\u8c22\u5929\u534e\u4e0e\u6f6e\u97f3\u548c\u5c1a\u90fd\u662f\u5e94\u5e08\u5f1f\u4e91\u6f84\u7684\u9080\u8bf7\uff0c\u5404\u81ea\u5e26\u4e86\u5f92\u5f1f\u524d\u6765\uff0c\u81ea\u4e2d\u571f\u8fdc\u81f3\u80e1\u8fb9\uff0c\u52a9\u4ed6 \u6551\u7236\u7684\u3002\u6070\u503c\u74e6\u523a\u53ef\u6c57\u521a\u5f97\u4e86\u592a\u5b50\uff0c\u56fd\u4e2d\u5927\u5e86\uff0c\u76d1\u89c6\u7a0d\u677e\uff0c\u4e09\u4eba\u5408\u529b\uff0c\u6740\u4e86\u51e0\u540d\u770b\u5b88\uff0c\u7adf\u7136 \u8f7b\u8f7b\u6613\u6613\u5730\u9003\u4e86\u51fa\u6765\uff0c\u5374\u53c8\u60f3\u4e0d\u5230\u96c1\u95e8\u5173\u5df2\u7ecf\u5728\u671b\uff0c\u624d\u9047\u5230\u8ffd\u5175\u8ffd\u6740\uff0c\u4e91\u6f84\u7adf\u7136\u8840\u6e85\u56fd\u95e8\u8fb9 \u5883\u3002\u8c22\u5929\u534e\u552f\u4e00\u7684\u5f92\u5f1f\uff0c\u4e5f\u529b\u6218\u800c\u4ea1\u3002 \u3000\u3000\u4e91\u9756\u8bf4\u5b8c\u90a3\u756a\u8bdd\u4e4b\u540e\uff0c\u5f7c\u7d2f\u4e0d\u582a\uff0c\u6c89\u6c89\u7761\u53bb\u3002\u4e91\u857e\u6014\u6014\u5730\u671b\u7740\u5979\u7684\u7237\u7237\uff0c\u4e0d\u8bf4\u4e0d\u7b11\u3002\u8c22 \u5929\u534e\u53f9\u4e86\u53e3\u6c14\uff0c\u6325\u4e86\u6325\u624b\uff0c\u9a74\u8f66\u53c8\u5728\u5ce1\u8c37\u7684\u5c71\u9053\u4e0a\u5954\u9a70\u3002\u8fd9\u65f6\u660e\u6708\u5df2\u51fa\u5929\u8fb9\uff0c\u8352\u51c9\u7684\u5c71\u8c37\u6d78 \u5728\u6708\u5149\u4e4b\u4e2d\uff0c\u6709\u5982\u8499\u4e0a\u4e00\u5c42\u8584\u96fe\u8f7b\u7eb1\uff0c\u66f4\u663e\u5f97\u51b7\u6e05\u6e05\u7684\uff0c\u8be1\u79d8\u5e7d\u9759\u3002\u8c22\u5929\u534e\u8ba9\u4e91\u857e\u5403\u4e86\u51e0\u7247 \u8089\u812f\uff0c\u559d\u4e86\u4e00\u53e3\u6c34\uff0c\u62cd\u62cd\u5979\u7684\u8eab\u5b50\u540e\uff0c\u4e0d\u4e45\u4e5f\u719f\u7761\u4e86\u3002 \u3000\u3000\u5728\u9a74\u8f66\u98a0\u7c38\u4e2d\uff0c\u5ffd\u542c\u5f97\u4e91\u9756\u68a6\u4e2d\u53eb\u9053\uff1a\u201c\u51b7\uff0c\u51b7\u2500\u72fc\u554a\u72fc\u6765\u4e86\uff01\u201d\u6f6e\u97f3\u548c\u5c1a\u7b11\u9053\uff1a\u201c\u8fd9 \u8001\u5934\u513f\u8fd8\u4ee5\u4e3a\u4ecd\u65e7\u662f\u5728\u80e1\u8fb9\u7267\u9a6c\u5462\u3002\u201d\u53c8\u542c\u5f97\u4e91\u857e\u5728\u68a6\u4e2d\u53eb\u9053\uff1a\u201c\u5988\u5988\uff0c\u857e\u857e\u4e0d\u6740\u4eba\uff0c\u857e\u857e \u5bb3\u6015\u3002\u201d\u8c22\u5929\u534e\u6115\u7136\u6447\u9996\uff0c\u5ffd\u542c\u5f97\u4e00\u58f0\u54cd\u7bad\uff0c\u63a0\u8fc7\u5c71\u8c37\uff0c\u4e91\u9756\u5728\u68a6\u4e2d\u8df3\u8d77\uff0c\u53eb\u9053\uff1a\u201c\u72fc\u6765 \u4e86\uff01\u201d\u5f20\u773c\u4e00\u77a7\uff0c\u53ea\u89c1\u4e00\u9053\u84dd\u706b\uff0c\u6447\u66f3\u4e0b\u964d\uff0c\u6f6e\u97f3\u548c\u5c1a\u5df2\u4e00\u63a0\u6570\u4e08\uff0c\u4e0a\u524d\u8fce\u654c\uff0c\u8c22\u5929\u534e\u9053\uff1a \u201c\u8001\u4f2f\u52ff\u60ca\uff0c\u6765\u7684\u6ca1\u6709\u51e0\u4eba\u3002\u201d \u3000\u3000\u4e91\u9756\u8fd9\u4e00\u5413\u7761\u610f\u5168\u6d88\uff0c\u98a4\u58f0\u8bf4\u9053\uff1a\u201c\u4e0d\u597d\uff0c\u8fd9\u662f\u5f20\u5b97\u5468\u624b\u4e0b\u7684\u7b2c\u4e00\u540d\u52c7\u58eb\uff0c\u590d\u59d3\u2018\u6fb9 \u53f0\u2019\uff0c\u5b57\u53f7\u2018\u706d\u660e\u2019\uff0c\u59d3\u540d\u4f3c\u662f\u80e1\u513f\uff0c\u5176\u5b9e\u5374\u662f\u6c49\u4eba\u3002\u6f84\u513f\u66fe\u7ecf\u548c\u4ed6\u4ea4\u8fc7\u624b\uff0c\u5403\u8fc7\u4ed6\u7684\u5927 \u4e8f\uff0c\u672c\u4e8b\u59d4\u5b9e\u4e86\u5f97\u3002\u201d \u3000\u3000\u8c22\u5929\u534e\u7b11\u9053\uff1a\u201c\u6211\u7684\u5e08\u5144\u53cc\u638c\u4e00\u6756\uff0c\u5a01\u9707\u4e2d\u539f\uff0c\u8499\u53e4\u5730\u65b9\u7684\u7b2c\u4e00\u52c7\u58eb\u53c8\u7b97\u5f97\u4e86\u4ec0\u4e48\u3002\u53ea \u8981\u4ed6\u6765\u4eba\u4e0d\u591a\uff0c\u7ba1\u6559\u4ed6\u6765\u5f97\u53bb\u4e0d\u5f97\uff0c\u5f85\u6211\u4eec\u628a\u4ed6\u64d2\u4e86\uff0c\u7ed9\u8001\u4f2f\u5e26\u4e0a\u4eac\u53bb\u732e\u529f\uff0c\u770b\u8fd9\u53ae\u8fd8\u6562\u4e0d \u6562\u2018\u706d\u660e\u2019\uff01\u201d\u8c22\u5929\u534e\u884c\u4fa0\u4ed7\u4e49\uff0c\u6700\u6068\u5356\u56fd\u4e4b\u5f92\uff0c\u542c\u8bf4\u90a3\u4eba\u53f7\u4e3a\u201c\u706d\u660e\u201d\uff0c\u6012\u4e0d\u53ef\u904f\uff0c\u62d4\u51fa \u957f\u5251\uff0c\u5954\u51fa\u8c37\u53e3\uff0c\u4e0a\u524d\u52a9\u9635\u3002 \u3000\u3000\u53ea\u89c1\u4e00\u5458\u80e1\u5c06\uff0c\u8eab\u62ab\u9501\u5b50\u9ec4\u91d1\u7532\uff0c\u4e52\u4f7f\u53cc\u9f99\u62a4\u624b\u94a9\u4e0e\u6f6e\u97f3\u548c\u5c1a\u6253\u5f97\u6b63\u70c8\u3002\u6f6e\u97f3\u548c\u5c1a\u7684\u7985 \u6756\u5982\u795e\u9f99\u51fa\u6d77\uff0c\u6a2a\u626b\u76f4\u5288\uff0c\u547c\u547c\u98ce\u54cd\uff0c\u90a3\u80e1\u5c06\u7adf\u662f\u5206\u6beb\u4e0d\u8ba9\uff0c\u53cc\u94a9\u76d8\u65cb\uff0c\u7eb5\u6a2a\u6325\u821e\uff0c\u5c06\u6f6e\u97f3 \u548c\u5c1a\u7897\u53e3\u5927\u7684\u7985\u6756\u8feb\u5f97\u4e1c\u5012\u897f\u6b6a\u3002\u8c22\u5929\u534e\u5927\u5403\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u53ae\u672c\u4e8b\u679c\u7136\u4e86\u5f97\uff0c\u602a\u4e0d\u5f97\u4e91 \u6f84\u8981\u5403\u4ed6\u7684\u4e8f\uff0c\u770b\u6765\u5e08\u5144\u4e5f\u4e0d\u662f\u4ed6\u7684\u5bf9\u624b\u3002\u201d\u7acb\u5373\u957f\u5251\u51fa\u9798\uff0c\u632f\u81c2\u4e00\u63a0\uff0c\u72b9\u5982\u5de8\u9e1f\u6469\u4e91\uff0c\u63a0 \u7a7a\u800c\u964d\uff0c\u957f\u5251\u4e00\u6296\uff0c\u4e00\u62db\u201c\u62c2\u67f3\u7a7f\u82b1\u201d\uff0c\u7a7f\u5fc3\u76f4\u523a\uff0c\u8fd9\u4e00\u5251\u662f\u4e13\u7834\u94a9\u3001\u593a\u4e4b\u7c7b\u5175\u5668\u7684\u6740\u624b\u795e \u62db\uff0c\u6b63\u662f\u7384\u673a\u9038\u58eb\u82e6\u5fc3\u6240\u521b\u7684\u5389\u5bb3\u62db\u6570\u3002 \u3000\u3000\u62a4\u624b\u94a9\u4e0e\u4e07\u5b57\u593a\u4e4b\u7c7b\uff0c\u672c\u6765\u662f\u53ef\u4ee5\u514b\u5236\u5200\u5251\u7684\u5916\u95e8\u5175\u5203\uff0c\u4f46\u7384\u673a\u9038\u58eb\u6240\u521b\u8fd9\u5957\u5251\u6cd5\uff0c\u8f7b \u7075\u7fd4\u52a8\uff0c\u53d8\u5316\u4e07\u72b6\uff0c\u53ef\u4ee5\u968f\u7740\u94a9\u593a\u4e4b\u52bf\uff0c\u53cd\u5236\u654c\u4eba\u3002\u82e5\u654c\u4eba\u4ecd\u672c\u7740\u201c\u94a9\u593a\u53ef\u4ee5\u9501\u62ff\u5200\u5251\u201d\u7684 \u65b9\u6cd5\u8fdb\u62db\uff0c\u5219\u8f7b\u8005\u624b\u6307\u88ab\u524a\uff0c\u91cd\u8005\u54bd\u5589\u88ab\u7a7f\uff0c\u7aef\u7684\u5389\u5bb3\uff0c\u800c\u4eca\u8c22\u5929\u534e\u4f7f\u51fa\u6740\u624b\u795e\u62db\uff0c\u957f\u5251\u5206 \u5fc3\u4e00\u523a\uff0c\u5185\u85cf\u5de6\u53f3\u53cc\u65cb\u4e24\u4e2a\u53d8\u5316\uff0c\u4e0d\u8bba\u654c\u4eba\u662f\u6b63\u9762\u8fce\u63a5\u6216\u662f\u4e24\u7ffc\u5077\u88ad\uff0c\u90fd\u96be\u9003\u6b64\u4e00\u5251\u4e4b\u5371\u3002 \u4e0d\u6599\u90a3\u80e1\u5c06\u53cc\u94a9\u970d\u970d\uff0c\u5de6\u94a9\u5f80\u4e0b\u4e00\u6c89\uff0c\u53f3\u94a9\u5f80\u4e0a\u4e00\u5e26\uff0c\u8c22\u5929\u534e\u7684\u957f\u5251\u51e0\u4e4e\u7ed9\u4ed6\u5f15\u8fc7\u53bb\u3002\u8bf4\u65f6 \u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u4f46\u89c1\u94a9\u5149\u95ea\u95ea\uff0c\u4f38\u7f29\u4e0d\u5b9a\uff0c\u4e5f\u4e0d\u77e5\u662f\u4ece\u54ea\u91cc\u88ad\u6765\uff0c\u654c\u4eba\u7adf\u8d81\u7740\u8c22\u5929\u534e\u7a0d\u4e00\u987f\u632b \u4e4b\u65f6\uff0c\u7acb\u523b\u53cd\u5ba2\u4e3a\u4e3b\u3002 \u3000\u3000\u8c22\u5929\u534e\u6697\u5403\u4e00\u60ca\uff0c\u9aa4\u9022\u52b2\u654c\uff0c\u7cbe\u795e\u4e00\u632f\uff0c\u957f\u5251\u4e00\u6296\uff0c\u5251\u62db\u500f\u53d8\uff0c\u4e00\u4e2a\u201c\u6402\u819d\u62d7\u6b65\u201d\uff0c\u5251 \u5149\u5212\u4e86\u4e00\u9053\u957f\u5f27\uff0c\u8eab\u968f\u5251\u52bf\uff0c\u6ef4\u6e9c\u6e9c\u7684\u8f6c\u4e86\u534a\u4e2a\u5706\u5708\uff0c\u201c\u5413\u201d\u7684\u4e00\u58f0\uff0c\u624b\u5fc3\u4e00\u767b\uff0c\u5251\u5c16\u5f80\u5916 \u75be\u5410\u3002\u8fd9\u662f\u653b\u5b88\u517c\u5907\u7684\u72ec\u7279\u62db\u6570\uff0c\u90a3\u80e1\u5c06\u94a9\u5149\u95ea\u95ea\uff0c\u5374\u9012\u4e0d\u8fdb\u53bb\u62db\uff0c\u903c\u5f97\u53cc\u94a9\u5916\u5c01\uff0c\u5411\u5de6\u4fa7 \u79fb\u4e86\u4e00\u6b65\u3002\u8c22\u5929\u534e\u7acb\u523b\u504f\u950b\u76f4\u4e0a\uff0c\u5251\u8d70\u8fde\u73e0\uff0c\u90a3\u80e1\u5c06\u53eb\u58f0\uff1a\u201c\u597d\u5251\u6cd5\uff01\u201d\u8fde\u6321\u4e09\u62db\uff0c\u7a81\u7136\u53eb \u9053\uff1a\u201c\u4f4f\u624b\uff01\u201d\u8c22\u5929\u534e\u54ea\u91cc\u80af\u542c\uff0c\u5251\u5149\u970d\u970d\uff0c\u8fde\u73af\u75be\u8fdb\uff0c\u90a3\u80e1\u5c06\u52c3\u7136\u4f5c\u8272\uff0c\u6012\u9053\uff1a\u201c\u4f60\u4ee5\u4e3a \u6211\u6015\u4f60\u4e0d\u6210\uff1f\u201d\u53cc\u94a9\u4e00\u5c55\uff0c\u8fce\u3001\u9001\u3001\u526a\u3001\u624e\u3001\u541e\u3001\u5410\u3001\u62bd\u3001\u6492\uff0c\u6070\u4f3c\u9a87\u7535\u60ca\u9706\uff0c\u4e24\u9053\u94f6\u86c7\uff0c \u8d34\u7740\u8c22\u5929\u534e\u7684\u5251\u5149\u98de\u821e\uff0c\u8c22\u5929\u534e\u7684\u5251\u6cd5\u867d\u7136\u795e\u5999\uff0c\u7adf\u7136\u5948\u4f55\u4e0d\u4e86\u4ed6\u3002 \u3000\u3000\u6f6e\u97f3\u548c\u5c1a\u5927\u543c\u4e00\u58f0\uff0c\u6325\u821e\u7985\u6756\uff0c\u4e0a\u524d\u52a9\u6218\uff0c\u90a3\u80e1\u5c06\u5927\u58f0\u7b11\u9053\uff1a\u201c\u770b\u4f60\u7684\u6b66\u529f\uff0c\u5b9a\u662f\u4e2d\u571f \u7684\u6210\u540d\u5251\u5ba2\uff0c\u542c\u8bf4\u4e2d\u571f\u6b66\u6797\u7684\u6210\u540d\u4eba\u7269\uff0c\u6700\u8bb2\u7a76\u5355\u6253\u72ec\u6597\u89c4\u77e9\uff0c\u4f60\u4eec\u5374\u60f3\u4ee5\u591a\u4e3a\u80dc\u5417\uff1f\u201d\u6f6e \u97f3\u548c\u5c1a\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u53ae\u662f\u4e0d\u662f\u53eb\u6fb9\u53f0\u706d\u660e\uff1f\u201d\u90a3\u80e1\u5c06\u907f\u4e86\u8c22\u5929\u534e\u4e00\u5251\uff0c\u8fd8\u4e86\u4e24\u62db\uff0c\u4fa7\u76ee\u7b11 \u9053\uff1a\u201c\u4f60\u8fd9\u548c\u5c1a\u4e5f\u77e5\u9053\u6211\u7684\u540d\u5b57\u3002\u201d\u6f6e\u97f3\u548c\u5c1a\u559d\u9053\uff1a\u201c\u4f60\u8eab\u662f\u6c49\u4eba\uff0c\u5374\u4e3a\u80e1\u5c06\uff0c\u7f9e\u4e5f\u4e0d\u7f9e\uff1f \u5bf9\u4f60\u8fd9\u6837\u7684\u53db\u56fd\u5978\u8d3c\uff0c\u8c01\u548c\u4f60\u8bb2\u4e2d\u539f\u7684\u6b66\u6797\u89c4\u77e9\uff1f\u5403\u6d12\u5bb6\u4e00\u6756\uff01\u201d\u6fb9\u53f0\u706d\u660e\u9762\u8272\u4e00\u6c89\uff0c\u5ffd\u800c \u7eb5\u58f0\u957f\u7b11\u9053\uff1a\u201c\u5339\u9a6c\u7eb5\u6a2a\u6f20\u5317\uff0c\u6b64\u5fc3\u53ef\u5bf9\u82cd\u5929\uff01\u8c01\u662f\u53db\u56fd\u5978\u8d3c\uff1f\u6211\u53db\u8c01\u7684\u56fd\u6765\u4e86\uff1f\u6731\u5143\u748b\u5de7 \u593a\u5929\u4e0b\uff0c\u53ea\u6709\u4f60\u4eec\u8fd9\u4e9b\u4e0d\u4e89\u6c14\u7684\u4eba\uff0c\u624d\u53bb\u5bf9\u4ed6\u7684\u513f\u5b59\u4fef\u9996\u79f0\u81e3\u3002\u201d\u4fa7\u8eab\u4e00\u95ea\uff0c\u5c06\u7985\u6756\u8ba9\u8fc7\u4e00 \u8fb9\uff0c\u53cc\u94a9\u4e00\u4e2a\u76d8\u65cb\uff0c\u62a4\u7740\u8eab\u5b50\uff0c\u5728\u94a9\u5149\u5251\u5f71\u4e4b\u4e2d\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u8bf4\u4e0e\u4f60\u8fd9\u83bd\u548c\u5c1a\u542c\u4f60\u4e5f\u4e0d \u89e3\uff0c\u597d\u5427\uff0c\u4f60\u65e2\u8981\u53ae\u6597\uff0c\u6211\u5c31\u53eb\u4e24\u4e2a\u5c0f\u8f88\u63a5\u4f60\u7684\u62db\u3002\u201d\u53cc\u94a9\u4e00\u6307\uff0c\u5c06\u6f6e\u97f3\u548c\u5c1a\u7684\u7985\u6756\u8feb\u8fc7\u4e00 \u8fb9\uff0c\u4ed6\u8eab\u540e\u7684\u4e24\u5458\u5c0f\u5c06\u6325\u52a8\u5200\u67aa\uff0c\u7acb\u523b\u62a2\u4e0a\u524d\u6765\uff0c\u63a5\u7740\u4e86\u6f6e\u97f3\u548c\u5c1a\u7684\u7985\u6756\u3002\u8fd9\u4e24\u5458\u5c0f\u5c06\u6b66\u529f \u867d\u7136\u8f83\u6f6e\u97f3\u4e3a\u4f4e\u4e00\u7574\uff0c\u4f46\u4ea6\u975e\u5eb8\u624b\uff0c\u6f6e\u97f3\u548c\u5c1a\u534a\u665a\u4e4b\u95f4\uff0c\u7ecf\u4e86\u4e24\u573a\u6fc0\u6597\uff0c\u6c14\u529b\u4e0d\u652f\uff0c\u7adf\u81ea\u80dc \u4ed6\u4eec\u4e0d\u5f97\u3002 \u3000\u3000\u8c22\u5929\u534e\u542c\u90a3\u6fb9\u53f0\u706d\u660e\u4f83\u4f83\u800c\u8c08\uff0c\u5fc3\u4e2d\u4e00\u52a8\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u53ae\u5012\u4e0d\u662f\u5bfb\u5e38\u4e4b\u8f88\u3002\u4f46\u52a9\u80e1\u706d \u6c49\uff0c\u5374\u65e0\u8bba\u5982\u4f55\uff0c\u4e5f\u4e0d\u5e94\u8be5\u3002\u201d\u6012\u6c14\u4e00\u8d77\uff0c\u6325\u5251\u5f3a\u653b\uff0c\u6fb9\u53f0\u706d\u660e\u529b\u654c\u6570\u62db\uff0c\u5ffd\u800c\u95ee\u9053\uff1a\u201c\u4f60 \u83ab\u4e0d\u662f\u7384\u673a\u9038\u58eb\u7684\u95e8\u4e0b\u4e48\uff1f\u201d \u3000\u3000\u8c22\u5929\u534e\u6014\u4e86\u4e00\u6014\uff0c\u53ea\u542c\u5f97\u90a3\u6fb9\u53f0\u706d\u660e\u7b11\u58f0\u53c8\u8d77\uff1a\u201c\u4f60\u7684\u5e08\u7236\u5f53\u5e74\u8d39\u5c3d\u5fc3\u8840\u4e5f\u80dc\u4e0d\u4e86\u6211\u7684 \u5e08\u7236\uff0c\u4f60\u8981\u80dc\u6211\uff0c\u54ea\u91cc\u80fd\u591f\uff1f\u4f60\u65e2\u7136\u4e0d\u77e5\u8fdb\u9000\uff0c\u597d\u5427\uff0c\u54b1\u4eec\u4eca\u65e5\u5c31\u5404\u4e3a\u5176\u4e3b\uff0c\u518d\u6597\u4e2a\u4e09\u4e94\u767e \u62db\u5427\uff01\u201d\u8c22\u5929\u534e\u609a\u7136\u4e00\u60ca\uff0c\u731b\u7136\u60f3\u8d77\u5e08\u7236\u6240\u8bf4\u8fc7\u7684\u5f80\u4e8b\u3002\u5728\u4e8c\u5341\u5e74\u524d\uff0c\u5e08\u7236\u66fe\u4e0e\u4e00\u4e2a\u9b54\u5934\u4e92 \u4e89\u6b66\u6797\u76df\u4e3b\u4e4b\u5ea7\uff0c\u5728\u5ce8\u5d4b\u4e4b\u5dc5\uff0c\u6597\u4e86\u4e09\u65e5\u4e09\u591c\uff0c\u4e0d\u5206\u80dc\u8d1f\u3002\u8fd9\u9b54\u5934\u590d\u59d3\u4e0a\u5b98\u53cc\u540d\u5929\u91ce\uff0c\u672c\u662f \u7eff\u6797\u7684\u5927\u76d7\uff0c\u7ecf\u6b64\u4e00\u6218\u4e4b\u540e\uff0c\u5ffd\u7136\u533f\u8ff9\u6f5c\u8e2a\uff0c\u4e0d\u77e5\u8eb2\u5230\u54ea\u91cc\u53bb\u4e86\u3002\u542c\u8fd9\u6fb9\u53f0\u706d\u660e\u5982\u6b64\u8bf4\u6cd5\uff0c \u90a3\u4e0a\u5b98\u5929\u91ce\u5b9a\u7136\u662f\u8eb2\u5230\u8499\u53e4\uff0c\u800c\u6fb9\u53f0\u706d\u660e\u4e5f\u5b9a\u7136\u662f\u4ed6\u7684\u5f92\u5f1f\u65e0\u7591\u3002 \u3000\u3000\u8c22\u5929\u534e\u672c\u5f85\u505c\u5251\u559d\u95ee\uff0c\u4f46\u542c\u4ed6\u8bf4\u51fa\u201c\u5404\u4e3a\u5176\u4e3b\u201d\u7684\u8bf4\u8bdd\uff0c\u6012\u6c14\u53c8\u751f\uff0c\u628a\u5e08\u7236\u6240\u4f20\u7684\u5251\u6cd5 \u65bd\u5c55\u5f97\u98ce\u96e8\u4e0d\u900f\uff0c\u6070\u82e5\u90a3\u94f6\u5149\u531d\u5730\uff0c\u7d2b\u7535\u98de\u7a7a\uff0c\u653b\u4e2d\u5b88\uff0c\u5b88\u4e2d\u6709\u653b\u3002\u90a3\u6fb9\u53f0\u706d\u660e\u4e5f\u597d\u751f\u5389 \u5bb3\uff0c\u53cc\u94a9\u4ea4\u526a\uff0c\u7adf\u5982\u4e24\u9053\u91d1\u8679\uff0c\u5c06\u95e8\u6237\u5c01\u95ed\u5f97\u5341\u5206\u4e25\u5bc6\uff0c\u4e5f\u662f\u653b\u5b88\u517c\u5907\uff0c\u865a\u5b9e\u4e92\u53d8\uff0c\u521a\u67d4\u9f50 \u65bd\uff0c\u8f6c\u77ac\u6597\u4e86\u767e\u6570\u5341\u62db\uff0c\u7adf\u662f\u4e0d\u5206\u80dc\u8d1f\u3002\u8c22\u5929\u534e\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u53ef\u60dc\u56db\u59b9\u4e0d\u5728\u8fd9\u513f\uff0c\u82e5\u7136\u53cc\u5251 \u5408\u74a7\uff0c\u4e09\u4e2a\u6fb9\u53f0\u706d\u660e\uff0c\u4e5f\u8981\u6b7b\u5728\u5251\u4e0b\u3002\u201d \u3000\u3000\u6fb9\u53f0\u706d\u660e\u94a9\u5149\u4ea4\u70c1\uff0c\u8fde\u8fdb\u4e09\u62db\uff0c\u8c22\u5929\u534e\u4e00\u6b65\u4e0d\u8ba9\uff0c\u8fd8\u4e86\u56db\u5251\u3002\u6fb9\u53f0\u706d\u660e\u5ffd\u7136\u54c8\u54c8\u5927\u7b11\uff0c \u8df3\u51fa\u5708\u5b50\uff0c\u53eb\u9053\uff1a\u201c\u5982\u4f55\uff1f\u4f60\u6211\u7528\u4e86\u5168\u529b\uff0c\u90fd\u4e0d\u80fd\u53d6\u80dc\uff0c\u4e0d\u5982\u4f4f\u624b\u4e86\u5427\uff01\u201d\u8c22\u5929\u534e\u6012\u9053\uff1a \u201c\u6c49\u8d3c\u4e0d\u4e24\u7acb\uff0c\u4eca\u65e5\u4e4b\u4e8b\uff0c\u975e\u6b7b\u4e0d\u4f11\uff01\u201d\u6fb9\u53f0\u706d\u660e\u53cc\u94a9\u4e00\u6307\uff0c\u903c\u4f4f\u4e86\u8c22\u5929\u534e\u7684\u957f\u5251\uff0c\u9ad8\u58f0\u559d \u9053\uff1a\u201c\u72d7\u4ea4\u5415\u6d1e\u5bbe\uff0c\u4e0d\u8bc6\u597d\u4eba\u5fc3\uff0c\u6211\u662f\u6551\u4f60\u6765\u7684\u201d\u8c22\u5929\u534e\u4e0d\u6562\u653e\u677e\uff0c\u957f\u5251\u5f80\u5916\u4e00\u5c55\uff0c\u5c06\u53cc\u94a9 \u8361\u8fc7\u4e00\u8fb9\uff0c\u559d\u9053\uff1a\u201c\u6211\u4eec\u4e07\u6c34\u5343\u5c71\uff0c\u90fd\u7ecf\u8fc7\u4e86\uff0c\u800c\u4eca\u5230\u4e86\u6b64\u5730\uff0c\u8fd8\u6709\u4ec0\u4e48\u5371\u96be\uff0c\u8981\u4f60\u76f8\u6551\uff1f \u4f60\u82e5\u771f\u80af\u6539\u90aa\u5f52\u6b63\uff0c\u5f03\u6697\u6295\u660e\uff0c\u5feb\u5feb\u629b\u4e0b\u53cc\u94a9\uff0c\u968f\u6211\u8d70\u5427\uff01\u201d\u6fb9\u53f0\u706d\u660e\u51b7\u51b7\u4e00\u7b11\uff0c\u6717\u58f0\u8bf4 \u9053\uff1a\u201c\u4f60\u771f\u662f\u4e0d\u77e5\u597d\u574f\uff0c\u6211\u5949\u5f20\u4e1e\u76f8\u4e4b\u547d\uff0c\u529d\u4f60\u4eec\u56de\u53bb\u3002\u4f60\u4eec\u82e5\u6267\u610f\u8981\u56de\u8f6c\u4e2d\u539f\uff0c\u53ea\u6050\u672a\u5230 \u96c1\u95e8\u5173\uff0c\u5c31\u8981\u906d\u53d7\u975e\u5e38\u4e4b\u7978\uff01\u201d\u8c22\u5929\u534e\u6012\u4e0d\u53ef\u904f\uff0c\u957f\u5251\u75be\u8fdb\uff0c\u5927\u58f0\u65a5\u9053\uff1a\u201c\u4f60\u8fd9\u72d7\u8d3c\uff0c\u80c6\u6562 \u5c06\u6211\u620f\u800d\uff01\u201d\u6fb9\u53f0\u706d\u660e\u4e5f\u751f\u4e86\u6c14\uff0c\u56de\u9a82\u9053\uff1a\u201c\u4f60\u65e2\u8981\u81ea\u5bfb\u6b7b\u8def\uff0c\u90a3\u5c31\u4f11\u8981\u602a\u4ffa\u65e0\u60c5\u3002\u201d\u8c22\u5929 \u534e\u54ac\u7d27\u7259\u6839\uff0c\u4e00\u58f0\u4e0d\u54cd\uff0c\u5251\u5982\u98ce\u96e8\uff0c\u6fb9\u53f0\u706d\u660e\u4e5f\u4e0d\u6562\u8bf4\u8bdd\u5206\u5fc3\uff0c\u53cc\u94a9\u6325\u970d\uff0c\u89c1\u62db\u62c6\u62db\uff0c\u89c1\u5f0f \u62c6\u5f0f\uff0c\u53c8\u6218\u4e86\u767e\u6570\u5341\u62db\uff0c\u4ecd\u662f\u4e0d\u5206\u80dc\u8d1f\uff0c\u96be\u89e3\u96be\u5206\u3002 \u3000\u3000\u6597\u5f97\u6b63\u9163\uff0c\u6fb9\u53f0\u706d\u660e\u5ffd\u7136\u4e00\u58f0\u80e1\u54e8\uff0c\u5356\u4e2a\u7834\u7efd\uff0c\u8f6c\u8eab\u4fbf\u8d70\u4e86\uff0c\u90a3\u4e24\u5458\u5c0f\u5c06\uff0c\u4e5f\u8df3\u51fa\u5708 \u5b50\uff0c\u968f\u540e\u6025\u9003\u3002\u8c22\u5929\u534e\u4e0e\u6f6e\u97f3\u548c\u5c1a\u6740\u5f97\u6027\u8d77\uff0c\u54ea\u91cc\u80af\u653e\uff0c\u4ed7\u5251\u633a\u6756\uff0c\u7eb5\u6b65\u4fbf\u8ffd\uff0c\u7247\u523b\u4e4b\u95f4\u8fc7 \u4e86\u4e00\u4e2a\u5c71\u5773\u3002\u8c22\u5929\u534e\u8f83\u4e3a\u8c28\u614e\uff0c\u5ffd\u7136\u60f3\u9053\uff1a\u201c\u8fd9\u53ae\u4e1d\u6beb\u672a\u9732\u8d25\u8c61\uff0c\u4f55\u4ee5\u9003\u8dd1\uff1f\u83ab\u975e\u5176\u4e2d\u53e6\u6709 \u8be1\u8ba1\u4e48\uff1f\u4e91\u5927\u4eba\u629b\u5728\u540e\u8fb9\uff0c\u65e0\u80fd\u624b\u9632\u62a4\uff0c\u83ab\u4e0d\u8981\u7740\u4e86\u4ed6\u7684\u6697\u7b97\uff01\u201d\u6b63\u5f85\u62db\u547c\u5e08\u5144\u56de\u5934\uff0c\u5ffd\u89c1 \u90a3\u6fb9\u53f0\u706d\u660e\u731b\u7136\u7eb5\u8eab\u5411\u8c37\u4e2d\u4e00\u8df3\uff0c\u8c22\u5929\u534e\u5927\u5403\u4e00\u60ca\uff0c\u7acb\u8db3\u5904\u79bb\u8c37\u5e95\u5c11\u8bf4\u4e5f\u6709\u5341\u6570\u4e08\u9ad8\uff0c\u8c37\u5e95 \u602a\u77f3\u5d99\u5ccb\uff0c\u8fd9\u4e00\u8df3\u4e0b\uff0c\u96be\u9053\u662f\u60f3\u81ea\u5df1\u5bfb\u6b7b\u4e0d\u6210\uff0c\u8fd9\u4e00\u7740\u771f\u662f\u5927\u51fa\u610f\u5916\uff01 \u3000\u3000\u8c22\u5929\u534e\u5ff5\u5934\u672a\u8f6c\uff0c\u53ea\u89c1\u90a3\u6fb9\u53f0\u706d\u660e\u8eab\u5b50\u5728\u534a\u7a7a\u4e00\u4e2a\u5c48\u4f38\uff0c\u547c\u7684\u4e00\u58f0\uff0c\u629b\u51fa\u4e00\u6761\u957f\u7ef3\uff0c\u7ef3 \u7aef\u7cfb\u6709\u5229\u94a9\uff0c\u4e00\u4e0b\u5b50\u5c31\u642d\u4f4f\u4e86\u5bf9\u9762\u7684\u677e\u6811\uff0c\u8eab\u8eaf\u4e00\u8361\uff0c\u6253\u79cb\u5343\u822c\u8361\u4e86\u8fc7\u53bb\u3002\u8fd9\u5c71\u8c37\u5f62\u52bf\u7edd \u9669\uff0c\u4e43\u662f\u4e00\u5c71\u5206\u51fa\u4e24\u5cf0\uff0c\u4e24\u5cf0\u76f8\u8ddd\u5341\u4f59\u4e08\uff0c\u8f7b\u529f\u591a\u597d\u4e5f\u4e0d\u80fd\u98de\u8d8a\uff0c\u5374\u60f3\u4e0d\u5230\u6fb9\u53f0\u706d\u660e\u7528\u8fd9\u4e2a \u65b9\u6cd5\u8df3\u4e86\u8fc7\u53bb\uff0c\u4e00\u8df3\u8fc7\u53bb\uff0c\u518d\u8f6c\u4e00\u4e2a\u5f2f\uff0c\u4fbf\u662f\u4e91\u9756\u7684\u9a74\u8f66\u4e86\u3002 \u3000\u3000\u8c22\u5929\u534e\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u5fc3\u77e5\u82e5\u5faa\u539f\u8def\u6298\u56de\uff0c\u8d76\u5230\u4e4b\u65f6\uff0c\u4e91\u9756\u5fc5\u7136\u5df2\u906d\u6bd2\u624b\u4e86\u3002\u4f46\u5ce1\u8c37 \u4e0d\u80fd\u98de\u8d8a\uff0c\u4e0d\u5faa\u539f\u8def\u800c\u56de\uff0c\u53c8\u5f85\u5982\u4f55\uff1f\u4e8b\u5df2\u5982\u65af\uff0c\u53ea\u5f97\u6a2a\u4e86\u5fc3\u80a0\uff0c\u56de\u5934\u8ffd\u8d76\uff0c\u62fc\u7740\u53bb\u66ff\u4e91\u9756 \u590d\u4ec7\uff0c\u4e0e\u6fb9\u53f0\u706d\u660e\u518d\u62fc\u4e2a\u6b7b\u6d3b\u3002 \u3000\u3000\u8c22\u5929\u534e\u51b7\u6c57\u76f4\u5192\uff0c\u597d\u4e0d\u5bb9\u6613\u8d76\u4e86\u56de\u6765\uff0c\u53ea\u89c1\u90a3\u6fb9\u53f0\u706d\u660e\u5df2\u7ad9\u5728\u9a74\u8f66\u4e4b\u524d\uff0c\u4e91\u9756\u5219\u8de8\u5728\u8f66 \u8f95\u4e4b\u4e0a\uff0c\u4e24\u4eba\u9762\u9762\u76f8\u5bf9\u3002\u6fb9\u53f0\u706d\u660e\u53cc\u94a9\u6302\u5728\u8170\u95f4\uff0c\u624b\u4e0a\u5e76\u65e0\u5175\u5203\uff0c\u9762\u4e0a\u9732\u51fa\u7b11\u5bb9\uff0c\u4f3c\u6b63\u5728\u4f4e \u58f0\u6551\u6073\uff0c\u800c\u4e91\u9756\u5219\u58f0\u8272\u4ff1\u5389\uff0c\u8c22\u5929\u534e\u8d76\u5230\u7684\u65f6\u5019\uff0c\u6b63\u542c\u5f97\u4e91\u9756\u9a82\u9053\uff1a\u201c\u80e1\u8bf4\u516b\u9053\uff01\u6211\u4e0e\u5f20\u5b97 \u5468\u6b64\u4ec7\u4e0d\u5171\u6234\u5929\uff0c\u4f60\u8981\u6740\u4fbf\u6740\uff0c\u6211\u5c82\u80af\u4e0e\u4f60\u56de\u53bb\uff0c\u6258\u5e87\u4e8e\u4ed6\uff1f\u201d\u8c22\u5929\u534e\u4e0d\u7981\u5927\u5947\uff0c\u53ea\u89c1\u90a3\u6fb9 \u53f0\u706d\u660e\u56de\u8fc7\u5934\u6765\uff0c\u5411\u81ea\u5df1\u5fae\u5fae\u4e00\u7b11\uff0c\u9ad8\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u770b\u89c1\u4e86\uff1f\u6211\u82e5\u8981\u53d6\u4e91\u8001\u513f\u6027\u547d\uff0c\u6613\u5982\u53cd \u638c\uff0c\u8fd8\u5f85\u4f60\u8d76\u56de\u6765\u4e48\uff1f\u4e91\u8001\u513f\uff0c\u6211\u82e6\u82e6\u76f8\u529d\uff0c\u751f\u6b7b\u7978\u798f\uff0c\u7cfb\u4e8e\u4f60\u4e00\u5ff5\u4e4b\u95f4\u4e86\u3002\u201d\u4e91\u9756\u6012\u4e0d\u53ef \u904f\uff0c\u987b\u7709\u6380\u52a8\uff0c\u5374\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u8981\u6211\u56de\u53bb\u518d\u66ff\u4f60\u7684\u5f20\u5927\u4eba\u5728\u51b0\u5929\u96ea\u5730\u91cc\u7267\u9a6c\u4e8c\u5341\u5e74\u4e48\uff1f\u201d\u6fb9 \u53f0\u706d\u660e\u7eb5\u58f0\u957f\u7b11\uff0c\u5ffd\u7136\u6b63\u5bb9\u8bf4\u9053\uff1a\u201c\u5f20\u5927\u4eba\u5c31\u56e0\u4f60\u7267\u9a6c\u4e8c\u5341\u5e74\uff0c\u4e0d\u5c48\u4e0d\u6320\uff0c\u624d\u656c\u91cd\u4f60\u7684\u4e3a \u4eba\uff0c\u8981\u4f60\u56de\u53bb\u3002\u201d\u4e91\u9756\u9a82\u9053\uff1a\u201c\u5f20\u5b97\u5468\u53db\u56fd\u5978\u8d3c\uff0c\u5351\u8d31\u5c0f\u4eba\uff0c\u6211\u4e91\u67d0\u803f\u803f\u5fe0\u5fc3\uff0c\u8c01\u8981\u4ed6\u7684\u656c \u91cd\uff01\u201d\u6fb9\u53f0\u706d\u660e\u51b7\u51b7\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5f20\u5927\u4eba\u679c\u7136\u8bf4\u5f97\u4e0d\u5dee\uff0c\u4f60\u53ea\u662f\u5f92\u6709\u611a\u5fe0\uff0c\u4e0d\u8db3\u4e0e\u8c08\u5927\u4e8b\u3002 \u4ed6\u4e5f\u6599\u4f60\u4e0d\u4f1a\u56de\u6765\u7684\u4e86\uff0c\u53ef\u662f\u4ed6\u89c1\u4f60\u4e5f\u662f\u4e00\u6761\u6c49\u5b50\uff0c\u4e0d\u5fcd\u89c1\u6b7b\u4e0d\u6551\uff0c\u624d\u547d\u6211\u4e07\u91cc\u8ffd\u6765\uff0c\u53ef\u60dc \u4f60\u8f9c\u8d1f\u4e86\u4ed6\u4e00\u7247\u82e6\u5fc3\u4e86\u3002\u201d\u4e91\u9756\u624b\u6276\u8f66\u8f95\uff0c\u6c14\u6781\u6012\u6781\uff0c\u98a4\u5dcd\u5dcd\u7684\u7834\u53e3\u9a82\u9053\uff1a\u201c\u54fc\uff0c\u82e6\u5fc3\u6551 \u6211\uff1f\u6211\u4e91\u67d0\u4e8c\u5341\u5e74\u7267\u9a6c\uff0c\u6b64\u8eab\u5c1a\u5e78\u5f97\u5f52\u846c\u6545\u571f\uff0c\u6b7b\u4ea6\u7791\u76ee\u3002\u4f60\u8ffd\u5230\u6b64\u5730\uff0c\u8981\u6740\u4fbf\u6740\uff0c\u6b64\u5730\u5df2 \u662f\u4e2d\u56fd\u5730\u65b9\uff0c\u8840\u6d12\u6545\u4e61\u5c1a\u6709\u4f55\u6068\uff1f\u201d\u6fb9\u53f0\u706d\u660e\u6012\u8a00\u9053\uff1a\u201c\u8c01\u8981\u6740\u4f60\uff1f\u8981\u6740\u4f60\u7684\u4e0d\u662f\u6211\u4eec\uff01\u201d \u4e91\u9756\u54ac\u7259\u8bf4\u9053\uff1a\u201c\u4f60\u6740\u4e86\u6211\u7684\u6f84\u513f\uff0c\u8fd8\u6765\u5f53\u9762\u6c14\u6211\u4e48\uff1f\u201d\u8eab\u8eaf\u98a4\u6296\uff0c\u51e0\u4e4e\u8dcc\u5012\u3002\u6fb9\u53f0\u706d\u660e\u5c06 \u4ed6\u4e00\u628a\u6276\u4f4f\uff0c\u9053\uff1a\u201c\u4f60\u7684\u513f\u5b50\u4e0d\u662f\u6211\u4eec\u6740\u7684\u3002\u8981\u8bf4\u7ed9\u4f60\u542c\uff0c\u4f60\u4e5f\u4e0d\u660e\u767d\uff0c\u968f\u6211\u56de\u53bb\u89c1\u4e86\u5f20\u5927 \u4eba\u4f60\u5c31\u77e5\u9053\u4e86\u3002\u201d\u4e91\u9756\u5f20\u53e3\u628a\u4e00\u53e3\u553e\u6d8e\uff0c\u75be\u5410\u51fa\u53bb\uff0c\u6fb9\u53f0\u706d\u660e\u8f7b\u8f7b\u4e00\u95ea\uff0c\u907f\u8fc7\u4e00\u8fb9\uff0c\u53ea\u542c\u5f97 \u4e91\u9756\u53c8\u9a82\u9053\uff1a\u201c\u4e0d\u662f\u4f60\u4eec\u6740\u7684\uff1f\u90a3\u4e9b\u4eba\u96be\u9053\u8fd8\u662f\u660e\u5175\u4e0d\u6210\uff1f\u201d\u6fb9\u53f0\u706d\u660e\u82e6\u7b11\u9053\uff1a\u201c\u90a3\u662f\u6211\u4eec \u5de6\u4e1e\u76f8\u7684\u90e8\u4e0b\u3002\u201d\u4e91\u9756\u9a82\u9053\uff1a\u201c\u4ec0\u4e48\u5de6\u4e1e\u76f8\u53f3\u4e1e\u76f8\uff0c\u90fd\u662f\u9a9a\u72d0\u9791\u5b50\u3002\u6211\u5df2\u5728\u4f60\u624b\u4e2d\uff0c\u4f60\u5feb\u5feb \u628a\u6211\u6740\u6389\uff0c\u4f11\u8981\u591a\u8a00\u3002\u201d\u8c22\u5929\u534e\u4e5f\u89c9\u5f97\u6fb9\u53f0\u706d\u660e\u771f\u662f\u5c82\u6709\u6b64\u7406\uff0c\u4ed6\u65e2\u7136\u8eab\u4e3a\u74e6\u523a\u56fd\u7684\u5927\u5c06\uff0c \u74e6\u523a\u7684\u5b98\u5175\u5c06\u4eba\u6740\u4e86\uff0c\u4ed6\u8fd8\u8981\u5f53\u9762\u6765\u6c14\u88ab\u6740\u8005\u7684\u7236\u4eb2\uff0c\u4f55\u51b5\u8fd9\u88ab\u6740\u8005\u7684\u7236\u4eb2\uff0c\u53c8\u8eab\u7ecf\u4e86\u4e8c\u5341 \u5e74\u7684\u82e6\u96be\uff01\u60b2\u75db\u4f59\u751f\uff0c\u54ea\u80fd\u7ecf\u5f97\u8fd9\u6837\u6b8b\u9177\u7684\u620f\u5f04\uff1f \u3000\u3000\u4e24\u4eba\u8d8a\u8bf4\u8d8a\u50f5\uff0c\u4f46\u53ea\u89c1\u90a3\u6fb9\u53f0\u706d\u660e\u62b1\u62f3\u4e00\u62f1\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u4e91\u5927\u4eba\uff0c\u6211\u8a00\u5c3d\u4e8e\u6b64\uff0c\u542c\u4e0d \u542c\u4ece\uff0c\u90a3\u5c31\u5168\u5728\u4f60\u4e86\u3002\u201d\u4e91\u9756\u6c14\u6781\u5439\u987b\uff0c\u730e\u730e\u4f5c\u54cd\uff0c\u5df2\u8bf4\u4e0d\u51fa\u534a\u4e2a\u5b57\u6765\u3002\u8c22\u5929\u534e\u5927\u6012\u559d\u58f0 \u9053\uff1a\u201c\u8feb\u5bb3\u4e00\u4e2a\u624b\u65e0\u7f1a\u9e21\u4e4b\u529b\u7684\u8001\u4eba\uff0c\u7b97\u4ec0\u4e48\u884c\u5f84\uff1f\u6709\u79cd\u7684\u54b1\u4eec\u518d\u6597\u4e09\u4e94\u767e\u62db\u3002\u201d\u6fb9\u53f0\u706d\u660e \u6beb\u4e0d\u7406\u4f1a\u4ed6\uff0c\u538b\u4f4e\u58f0\u8c03\uff0c\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u90a3\u6211\u53ea\u597d\u8d70\u4e86\u3002\u5f20\u4e1e\u76f8\u8bf4\uff0c\u7d2f\u4f60\u7267\u9a6c\u4e8c\u5341 \u5e74\uff0c\u5b9e\u5728\u8fc7\u610f\u4e0d\u53bb\u3002\u4ed6\u4e5f\u6599\u4f60\u4e0d\u4f1a\u56de\u6765\uff0c\u53eb\u6211\u4ee3\u9001\u4f60\u4e09\u9053\u9526\u56ca\uff0c\u4f9d\u7740\u9526\u56ca\u5999\u8ba1\uff0c\u8fd8\u53ef\u6551\u4f60\u6027 \u547d\u3002\u5f20\u4e1e\u76f8\u8bf4\u8fd9\u4e09\u9053\u9526\u56ca\uff0c\u5c31\u7b97\u4f60\u66ff\u4ed6\u7267\u9a6c\u4e8c\u5341\u5e74\u7684\u916c\u62a5\u3002\u201d\u628a\u624b\u4e00\u64a4\uff0c\u8f6c\u8eab\u4fbf\u8d70\u3002\u8c22\u5929\u534e \u6014\u4e86\u6014\uff0c\u6fb9\u53f0\u706d\u660e\u5df2\u4ece\u4ed6\u8eab\u8fb9\u8d70\u8fc7\uff0c\u53ea\u542c\u5f97\u5495\u547c\u4e00\u58f0\uff0c\u4e91\u9756\u5012\u5728\u8f66\u4e0a\u3002\u8c22\u5929\u534e\u4e00\u4f38\u624b\u6253\u51fa\u4e94 \u679a\u5b50\u5348\u593a\u9b42\u9489\uff0c\u5206\u6253\u4e94\u5904\u7a74\u9053\uff0c\u6fb9\u53f0\u706d\u660e\u5934\u4e5f\u4e0d\u56de\uff0c\u53cc\u94a9\u4e00\u4e2a\u76d8\u65cb\uff0c\u53ea\u542c\u5f97\u53ee\u53ee\u53ee\u51e0\u58f0\u8fde \u54cd\uff0c\u6fb9\u53f0\u706d\u660e\u4e00\u58f0\u51b7\u7b11\uff0c\u4eba\u5f71\u5df2\u6ca1\u5165\u82cd\u677e\u602a\u77f3\u4e4b\u95f4\uff0c\u8f6c\u8fc7\u5c71\u5773\u53bb\u4e86\u3002 \u3000\u3000\u8c22\u5929\u534e\u8fd9\u4e00\u628a\u98de\u9489\uff0c\u672c\u5c31\u4e0d\u6307\u671b\u80fd\u5c06\u654c\u4eba\u6253\u5012\uff0c\u4e0d\u8fc7\u89c1\u4ed6\u8fd9\u6837\u8f7b\u6613\u5730\u4e00\u4e3e\u5c06\u4e94\u679a\u98de\u9489\u626b \u6570\u6253\u843d\uff0c\u4e5f\u4e0d\u89c9\u5403\u4e86\u4e00\u60ca\uff0c\u98de\u6b65\u5954\u5411\u9a74\u8f66\u3002\u53ea\u89c1\u4e91\u9756\u5618\u5618\u6c14\u5598\uff0c\u8116\u5b50\u901a\u7ea2\uff0c\u8c22\u5929\u534e\u4f38\u624b\u5728\u4ed6 \u80f8\u53e3\u4e00\u63c9\uff0c\u4e91\u9756\u201c\u54c7\u201d\u7684\u4e00\u58f0\u5410\u51fa\u4e00\u53e3\u6d53\u75f0\uff0c\u5927\u53eb\u9053\uff1a\u201c\u6c14\u6b7b\u6211\u4e5f\uff01\u201d\u98a4\u5dcd\u5dcd\u5730\u5750\u4e86\u8d77\u6765\u3002 \u8c22\u5929\u534e\u77e5\u9053\u4ed6\u662f\u6124\u706b\u4e2d\u70e7\uff0c\u75f0\u585e\u5589\u5934\uff0c\u8eab\u4e0a\u5e76\u65e0\u53d7\u5230\u5176\u4ed6\u4f24\u635f\uff0c\u8fd9\u624d\u653e\u4e0b\u4e86\u5fc3\u3002\u6b63\u5f85\u5584\u8a00\u5f00 \u89e3\uff0c\u5ffd\u542c\u5f97\u6f6e\u97f3\u548c\u5c1a\u5471\u5471\u5927\u58f0\uff0c\u6a2a\u62d6\u7985\u6756\uff0c\u4ece\u5c71\u5773\u5916\u75be\u8dd1\u56de\u6765\u3002 \u3000\u3000\u8c22\u5929\u534e\u53c8\u5403\u4e86\u4e00\u60ca\uff0c\u8fde\u5fd9\u95ee\u9053\uff1a\u201c\u5e08\u5144\uff0c\u4f60\u600e\u4e48\u5566\uff1f\u201d\u6f6e\u97f3\u548c\u5c1a\u6124\u7136\u8bf4\u9053\uff1a\u201c\u4e09\u5f1f\uff0c\u6211 \u4e22\u5c3d\u5e08\u95e8\u7684\u9762\u5b50\u5566\uff01\u6211\u4eca\u751f\u4e0d\u628a\u6fb9\u53f0\u706d\u660e\u75db\u6253\u4e09\u767e\u7985\u6756\uff0c\u96be\u6d88\u6b64\u6068\uff01\u201d\u8c22\u5929\u534e\u77e5\u9053\u5e08\u5144\u662f\u4e2a \u6025\u6027\u7684\u4eba\uff0c\u6309\u4ed6\u5750\u4e0b\uff0c\u8ba9\u4ed6\u559d\u4e86\u53e3\u6c34\uff0c\u8bf4\u9053\uff1a\u201c\u4e8c\u5e08\u5144\uff0c\u6709\u8bdd\u6162\u6162\u8bf4\uff0c\u51ed\u7740\u54b1\u4eec\u56db\u4e2a\u5144\u5f1f\uff0c \u5c31\u7b97\u662f\u4e0a\u5b98\u8001\u9b54\u5934\u4eb2\u81ea\u5230\u4e34\uff0c\u8fd9\u4ec7\u4e5f\u53ef\u4ee5\u62a5\uff0c\u4f55\u51b5\u6fb9\u53f0\u706d\u660e\u5462\uff1f\u201d\u6f6e\u97f3\u548c\u5c1a\u5495\u561f\u561f\u5730\u559d\u4e86\u4e00 \u5927\u53e3\u6c34\uff0c\u6c14\u6124\u5730\u7eed\u9053\uff1a\u201c\u6211\u53ea\u9053\u8fd9\u53ae\u8981\u5bf9\u4e91\u5927\u4eba\u6697\u65bd\u6bd2\u624b\uff0c\u5fc3\u6025\u8d76\u56de\uff0c\u53f5\u8010\u90a3\u4e24\u4e2a\u5c0f\u8d3c\uff0c\u6b7b \u7f20\u4e0d\u653e\uff0c\u82e5\u662f\u5e73\u65e5\uff0c\u8fd9\u4e24\u4e2a\u5c0f\u8d3c\u6211\u771f\u8fd8\u4e0d\u653e\u5728\u5fc3\u4e0a\u3002\u65e0\u5948\u6211\u63a5\u8fde\u4e24\u573a\u6076\u6597\uff0c\u6c14\u529b\u4e0d\u52a0\uff0c\u548c\u4ed6 \u4eec\u8fb9\u8d70\u8fb9\u6597\uff0c\u8fdb\u8fdb\u9000\u9000\uff0c\u7adf\u7136\u8d76\u4e0d\u56de\u6765\uff0c\u6597\u4e86\u4e00\u4e8c\u767e\u62db\uff0c\u6211\u4e00\u6025\u8fde\u8d70\u9669\u62db\uff0c\u521a\u521a\u62a2\u4e86\u4e0a\u98ce\uff0c \u4e0d\u6599\u6fb9\u53f0\u706d\u660e\u8fd9\u53ae\u53c8\u56de\u6765\u4e86\u3002\u6211\u4ee5\u4e3a\u4ed6\u5df2\u5c06\u4e91\u5927\u4eba\u5bb3\u4e86\uff0c\u7834\u53e3\u5927\u9a82\u3002\u90a3\u53ae\u53cc\u94a9\u4e00\u642d\uff0c\u5c06\u6211\u7684 \u7985\u6756\u62c9\u8fc7\u4e00\u8fb9\uff0c\u7a81\u7136\u52b2\u529b\u4e00\u677e\uff0c\u6697\u65bd\u8be1\u8ba1\uff0c\u5c06\u6211\u8dcc\u4e86\u4e00\u8de4\u3002\u8fd9\u8fd8\u4e0d\u7b97\uff0c\u8fd8\u6253\u4e86\u6211\u4e00\u4e2a\u8033\u5149\uff0c \u9a82\u6211\u662f\u2018\u83bd\u548c\u5c1a\u2019\uff0c\u8bf4\u6211\u2018\u80e1\u8bf4\u516b\u9053\uff0c\u4e71\u56bc\u820c\u5934\uff0c\u6253\u4e2a\u8033\u5149\uff0c\u804a\u4f5c\u8584\u60e9\u2019\u4e91\u4e91\u3002\u9a82\u5b8c\u4e4b\u540e\uff0c \u4fbf\u5e26\u4e86\u4e24\u4e2a\u5c0f\u8d3c\uff0c\u626c\u957f\u800c\u53bb\u3002\u6211\u4eec\u95ef\u8361\u6c5f\u6e56\u51e0\u5341\u5e74\uff0c\u51e0\u66fe\u53d7\u8fc7\u5982\u6b64\u6b3a\u4fae\uff0c\u4f60\u8bf4\u6c14\u4e0d\u6c14\u4eba\uff1f\u201d \u505c\u4e86\u4e00\u505c\uff0c\u76ee\u5149\u6ce8\u5730\u4e0a\uff0c\u5ffd\u7136\u53c8\u56b7\u8d77\u6765\u9053\uff1a\u201c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u4ed6\u548c\u4f60\u4ea4\u4e86\u624b\u6ca1\u6709\uff1f\u4e91\u5927\u4eba\u597d \u7aef\u7aef\u7684\u6ca1\u4e8b\uff0c\u8fd9\u5730\u4e0a\u5374\u6709\u7740\u4e09\u4e2a\u8fd9\u6837\u8da3\u81f4\u7684\u9526\u56ca\uff1f\u201d \u3000\u3000\u6f6e\u97f3\u548c\u5c1a\u4e00\u8fb9\u8bf4\u4e00\u8fb9\u628a\u4e09\u9053\u9526\u56ca\u62fe\u4e86\u8d77\u6765\uff0c\u5567\u5567\u8d5e\u8d4f\u9053\uff1a\u201c\u4e0a\u9762\u8fd8\u4e61\u6709\u9a86\u9a7c\u5462\u3002\u54a6\uff0c\u8fd9 \u4e0d\u662f\u8499\u53e4\u4eba\u7684\u523a\u7ee3\u5417\uff1f\u8fd9\u3001\u8fd9\u662f\u8c01\u7684\uff1f\u201d\u4e91\u9756\u52c3\u7136\u6012\u9053\uff1a\u201c\u81ed\u9791\u5b50\u7684\u81ed\u4e1c\u897f\uff0c\u628a\u5b83\u6495\u6210\u7c89 \u788e\uff0c\u629b\u5230\u6c61\u6ce5\u91cc\u53bb\uff01\u201d\u6f6e\u97f3\u6115\u7136\u4e00\u671b\uff0c\u7528\u529b\u4fbf\u6495\uff0c\u5ffd\u7136\u624b\u8155\u4e00\u75db\uff0c\u4e09\u9053\u9526\u56ca\uff0c\u90fd\u7ed9\u8c22\u5929\u534e\u62a2 \u53bb\u3002\u6f6e\u97f3\u548c\u5c1a\u8be7\u9053\uff1a\u201c\u5e08\u5f1f\uff0c\u4f60\u8fd9\u662f\u2026\u2026\u201d\u8c22\u5929\u534e\u9053\uff1a\u201c\u4e91\u5927\u4eba\u770b\u4e00\u770b\u4e5f\u4e0d\u788d\u4e8b\uff0c\u4f60\u4fbf\u770b\u5b83 \u8bf4\u7684\u4ec0\u4e48\u3002\u82e5\u7136\u771f\u662f\u80e1\u8bf4\u516b\u9053\uff0c\u90a3\u65f6\u518d\u6495\uff0c\u4e5f\u8fd8\u4e0d\u8fdf\uff01\u201d \u3000\u3000\u8c22\u5929\u4f1a\u5fc3\u4e2d\u5341\u5206\u7591\u60d1\uff1a\u8fd9\u6fb9\u53f0\u706d\u660e\u6b66\u529f\u9ad8\u5f3a\u4e4b\u6781\uff0c\u4ed6\u65e2\u7136\u4e0d\u6b32\u52a0\u5bb3\u4e91\u9756\uff0c\u90a3\u4e48\u6240\u4e3a\u7684\u53c8 \u662f\u4f55\u6765\uff1f\u96be\u9053\u771f\u662f\u60f3\u201c\u6551\u4eba\u201d\u4e0d\u6210\uff1f\u4f46\u4ed6\u4f55\u4ee5\u53c8\u5728\u8499\u53e4\u4e3a\u5b98\uff0c\u4e8c\u5341\u5e74\u6765\u52a9\u90a3\u5f20\u5b97\u5468\u6298\u78e8\u4e91 \u9756\uff1f\u518d\u8bf4\u96c1\u95e8\u5173\u5df2\u7ecf\u5728\u671b\uff0c\u8e0f\u5165\u4e86\u4e2d\u56fd\u5730\u65b9\uff0c\u8fd8\u6709\u8c01\u4f1a\u52a0\u5bb3\u4e91\u9756\uff1f\u8fd9\u4e0d\u662f\u9a97\u4eba\u7684\u9b3c\u8bdd\u5417\uff1f\u4f46 \u82e5\u8bf4\u4ed6\u4e07\u91cc\u8fdc\u6765\uff0c\u4e3a\u7684\u5c31\u662f\u8bf4\u8fd9\u756a\u9b3c\u8bdd\uff0c\u5374\u53c8\u662f\u7edd\u65e0\u6b64\u7406\u3002\u4f55\u51b5\u4ed6\u867d\u7136\u50b2\u5cb8\uff0c\u5374\u53c8\u4f3c\u4e4e\u624b\u4e0b \u7559\u60c5\uff0c\u8981\u4e0d\u7136\u5e08\u5144\u600e\u80fd\u9003\u5f97\u6027\u547d\uff0c\u8fd9\u771f\u662f\u767e\u601d\u4e0d\u5f97\u5176\u89e3\u4e86\uff01 \u3000\u3000\u4e0d\u8bf4\u8c22\u5929\u534e\u5fc3\u91cc\u6c89\u541f\uff0c\u4e14\u8bf4\u4e91\u9756\u63a5\u8fc7\u9526\u56ca\uff0c\u6068\u6068\u4e00\u77a5\uff0c\u53ea\u89c1\u7b2c\u4e00\u9053\u9526\u56ca\u4e0a\u5199\u7740\u201c\u5373\u5f00\u201d \u4e8c\u5b57\uff0c\u4e91\u9756\u6c14\u547c\u547c\u5730\u4e00\u628a\u6495\u5f00\uff0c\u62bd\u51fa\u91cc\u9762\u7684\u4fe1\u7b3a\uff0c\u4e0a\u9762\u5199\u9053\uff1a\u201c\u6b64\u65f6\u901f\u56de\u8499\u53e4\uff0c\u5c1a\u53ef\u65e0\u4e8b\uff0c \u6fb9\u53f0\u5c06\u519b\u7559\u9a7b\u5de6\u4e91\uff0c\u53ef\u4ee5\u63a5\u5e94\u3002\u201d\u4e91\u9756\u770b\u5b8c\u4e4b\u540e\uff0c\u968f\u624b\u4e00\u6495\uff0c\u629b\u5728\u5730\u4e0a\u3002 \u3000\u3000\u8c22\u5929\u534e\u89c1\u4ed6\u767d\u987b\u98a4\u6296\uff0c\u9762\u8272\u7126\u9ec4\uff0c\u4e0d\u6562\u52a8\u95ee\u3002\u4e91\u9756\u770b\u7740\u90a3\u6495\u788e\u7684\u7eb8\u7247\u4e00\u7247\u7247\u98d8\u843d\u6c61\u6ce5\uff0c \u6124\u7136\u8bf4\u9053\uff1a\u201c\u4ec0\u4e48\u9526\u56ca\u5999\u8ba1\uff0c\u8fd8\u4e0d\u662f\u90a3\u756a\u9b3c\u8bdd\uff01\u201d\u62ff\u8d77\u7b2c\u4e8c\u9053\u9526\u56ca\uff0c\u53ea\u89c1\u4e0a\u9762\u5199\u9053\uff1a\u201c\u79bb\u96c1 \u95e8\u5173\u4e03\u91cc\u4e4b\u5730\u5f00\u62c6\u3002\u201d\u4e91\u9756\u9053\uff1a\u201c\u504f\u4e0d\u542c\u4f60\u7684\u8bdd\u3002\u201d\u7528\u529b\u4e00\u6495\uff0c\u91cc\u9762\u53c8\u9732\u51fa\u4e00\u5f20\u4fe1\u7b3a\u5199\u9053\uff1a \u201c\u65f6\u673a\u5df2\u8feb\uff0c\u6b64\u9645\u96c1\u95e8\u5173\u5f53\u6709\u4eba\u63a5\u4f60\uff0c\u5148\u884c\u9886\u961f\u8005\u82e6\u975e\u5468\u5065\u603b\u5175\uff0c\u4f60\u5f53\u7acb\u5373\u5feb\u9a6c\u98de\u9003\uff0c\u7559\u8c22 \u5929\u534e\u4e0e\u6f6e\u97f3\u65ad\u540e\uff0c\u6216\u8bb8\u5c1a\u80fd\u4fdd\u5168\u9996\u9886\u3002\u201d\u96c1\u95e8\u5173\u53eb\u5175\u5468\u5065\u548c\u4e91\u9756\u4e43\u662f\u540c\u4e61\u597d\u53cb\uff0c\u4e00\u4eba\u4e60\u6587\uff0c \u4e00\u4eba\u4e60\u6b66\uff0c\u662f\u540c\u79d1\u4e2d\u7684\u6587\u6b66\u8fdb\u58eb\u3002\u4e91\u6f84\u6b64\u6b21\u6551\u7236\uff0c\u5f97\u4ed6\u6697\u52a9\u751a\u591a\uff0c\u5b9e\u884c\u6551\u7236\u8ba1\u5212\u4e4b\u524d\uff0c\u53c8\u5df2 \u6d3e\u4eba\u98de\u9a91\u62a5\u77e5\u5468\u603b\u5175\uff0c\u53eb\u4ed6\u8f6c\u544a\u671d\u5ef7\uff0c\u4e00\u8def\u884c\u8e2a\uff0c\u90fd\u6d3e\u6709\u4eba\u6697\u4e2d\u8054\u7cfb\u7684\u3002\u4e91\u9756\u60f3\u9053\uff1a\u201c\u5468\u5065 \u89c1\u6211\u5230\u6765\uff0c\u5c82\u6709\u4e0d\u6765\u8fce\u63a5\u4e4b\u7406\uff1f\u6211\u8282\u6bd4\u82cf\u6b66\uff0c\u5f02\u57df\u5f52\u6765\uff0c\u5927\u660e\u5929\u5b50\u5373\u7b97\u4e0d\u7acb\u50cf\u8bb0\u529f\uff0c\u4e5f\u5f53\u91cd \u7528\u3002\u80e1\u513f\u5984\u56fe\u79bb\u95f4\uff0c\u771f\u771f\u5c82\u6709\u6b64\u7406\uff01\u201d\u968f\u624b\u4e00\u6495\uff0c\u53c8\u628a\u4fe1\u7b3a\u6495\u6210\u7c89\u788e\u3002 \u3000\u3000\u8c22\u5929\u534e\u65c1\u80af\u5077\u7aa5\uff0c\u4e00\u77a5\u4e4b\u4e0b\uff0c\u89c1\u4fe1\u7b3a\u4e0a\u6709\u81ea\u5df1\u7684\u540d\u5b57\uff0c\u602a\u800c\u95ee\u9053\uff1a\u201c\u4e0a\u9762\u8bf4\u7684\u4ec0\u4e48\uff1f\u201d \u4e91\u9756\u9119\u5c51\u8bf4\u9053\uff1a\u201c\u8fd8\u4e0d\u662f\u9b3c\u8bdd\u8fde\u7bc7\u3002\u4e0d\u8fc7\u5978\u8d3c\u4e5f\u771f\u5389\u5bb3\uff0c\u4ed6\u4eec\u597d\u50cf\u5df2\u9884\u77e5\u4f60\u4eec\u4e8c\u4eba\u6df1\u5165\u80e1 \u8fb9\uff0c\u524d\u6765\u6551\u6211\u3002\u4e0d\u77e5\u4f55\u4ee5\u53c8\u65e0\u9632\uff1f\u201d\u8c22\u5929\u534e\u7709\u5934\u4e00\u76b1\uff0c\u4f4e\u9996\u6c89\u541f\uff0c\u7591\u60d1\u66f4\u751a\u3002\u4e91\u9756\u968f\u624b\u53c8\u62ff \u8d77\u7b2c\u4e09\u9053\u9526\u56ca\uff0c\u6b63\u8981\u6495\u5f00\uff0c\u5ffd\u53c8\u653e\u4e0b\u4e86\uff0c\u8c22\u5929\u534e\u4e00\u89c1\uff0c\u4e0d\u89c9\u53eb\u51fa\u58f0\u6765\u3002 \u3000\u3000\u90a3\u7b2c\u4e09\u9053\u9526\u56ca\u4e0a\u5199\u7740\uff1a\u201c\u6b64\u51fd\u4ea4\u8c22\u5929\u534e\u5f00\u62c6\u3002\u201d\u4e91\u9756\u51b7\u51b7\u5730\u770b\u4e86\u8c22\u5929\u534e\u4e00\u773c\uff0c\u5fc3\u8d77\u7591 \u4e91\u3002\u8c22\u5929\u534e\u4e45\u5386\u6c5f\u6e56\uff0c\u4eba\u751a\u7cbe\u7ec6\uff0c\u89c1\u6b64\u4ee5\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u5978\u8d3c\u8be1\u8ba1\u591a\u7aef\uff0c\u4e91\u5927\u4eba\u4f60\u62c6 \u5f00\u770b\u770b\uff0c\u4ed6\u8bf4\u4ec0\u4e48\uff1f\u201d\u4e91\u9756\u7565\u4e00\u8fdf\u7591\uff0c\u628a\u9526\u56ca\u6162\u6162\u62c6\u5f00\uff0c\u62bd\u51fa\u4fe1\u7b3a\u6765\uff0c\u7f13\u7f13\u8bfb\u9053\uff1a\u201c\u6b64\u9645\u4e91 \u5927\u4eba\u5f53\u5df2\u88ab\u6355\uff0c\u9526\u56ca\u4e4b\u5185\uff0c\u5c1a\u6709\u8721\u4e38\u4e00\u4e2a\uff0c\u4f60\u5bc6\u85cf\u6b64\u4e38\uff0c\u5207\u4e0d\u53ef\u5f00\uff0c\u6025\u901f\u5165\u4eac\uff0c\u9762\u89c1\u4e8e\u8c26\uff0c \u53c2\u52be\u738b\u632f\uff0c\u4e91\u5927\u4eba\u6027\u547d\u80fd\u5426\u4fdd\u5168\uff0c\u5168\u5728\u6b64\u4e00\u4e3e\u77e3\u3002\u201d\u4e91\u9756\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u6012\u4e0d\u53ef\u904f\uff0c\u4fe1\u624b\u4e00 \u6495\uff0c\u53c8\u628a\u4fe1\u7b3a\u6495\u6210\u7c89\u788e\uff0c\u9a82\u9053\uff1a\u201c\u5371\u8a00\u8038\u542c\uff0c\u80e1\u8bf4\u516b\u9053\uff01\u6211\u4e91\u67d0\u662f\u4e2a\u5927\u5927\u7684\u5fe0\u81e3\uff0c\u5c82\u6709\u88ab\u6355 \u4e4b\u7406\uff1f\u201d\u53c8\u628a\u9526\u56ca\u5f80\u5730\u4e0b\u4e00\u63b7\u3002\u8c22\u5929\u534e\u4e00\u7eb5\u8eab\u63a5\u8fc7\u9526\u56ca\uff0c\u679c\u7136\u5728\u5176\u4e2d\u63ac\u51fa\u4e00\u9897\u8721\u4e38\uff0c\u85cf\u5728\u8eab \u4e0a\u3002\u4e91\u9756\u9762\u8272\u4e00\u53d8\uff0c\u8c22\u5929\u534e\u9053\uff1a\u201c\u4e14\u85cf\u7740\u8fd9\u73a9\u610f\u513f\uff0c\u4e5f\u5360\u4e0d\u4e86\u4ec0\u4e48\u5730\u65b9\uff0c\u73a9\u73a9\u4e5f\u597d\u3002\u201d\u4e91\u9756 \u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u5fae\u6120\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u7ed9\u4f60\u7684\u4e1c\u897f\uff0c\u4f60\u8981\u85cf\u4fbf\u85cf\u7740\u5427\u3002\u6211\u4e91\u9756\u4e0e\u5978\u8d3c\u4e0d\u5171\u6234\u5929\uff0c \u7eb5\u7136\u771f\u662f\u788e\u5c38\u4e07\u6bb5\uff0c\u4e5f\u4e0d\u8981\u4ed6\u6765\u76f8\u6551\u3002\u201d \u3000\u3000\u9a74\u8f66\u8d81\u7740\u6708\u8272\uff0c\u5728\u591c\u95f4\u8d76\u8def\uff0c\u96c1\u95e8\u5173\u5916\uff0c\u8fb9\u5883\u5b88\u591c\u7684\u660e\u5175\u89d2\u58f0\uff0c\u5df2\u9690\u9690\u53ef\u95fb\u3002\u4e91\u9756\u7cbe\u795e \u4e00\u632f\uff0c\u867d\u5954\u6ce2\u957f\u8def\uff0c\u4e00\u665a\u672a\u7761\uff0c\u5374\u662f\u6beb\u65e0\u5026\u610f\u3002\u7fd8\u9996\u957f\u7a7a\uff0c\u7eb5\u58f0\u541f\u9053\uff1a\u201c\u559c\u6709\u4f59\u751f\u5f52\u6545\u571f\uff0c \u96c4\u5173\u5206\u9694\u522b\u534e\u5937\u3002\u6211\u4e91\u67d0\u660e\u65e5\u5f53\u53ef\u91cd\u6574\u8863\u51a0\uff0c\u624b\u6301\u4f7f\u8282\uff0c\u793c\u62dc\u660e\u541b\u4e86\u3002\u201d\u8c22\u5929\u534e\u9053\uff1a\u201c\u5927\u4eba \u5b64\u5fe0\uff0c\u767e\u4e16\u4e0d\u53ef\u4e00\u89c1\uff0c\u800c\u4eca\u5929\u5b50\uff0c\u5c01\u5b98\u53d9\u7235\uff0c\u4e5f\u4e0d\u8db3\u8a00\u916c\u3002\u201d\u4e91\u9756\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u8fd9\u662f\u81e3\u5b50\u4efd \u5185\u4e4b\u4e8b\uff0c\u5c82\u671b\u671d\u5ef7\u916c\u62a5\u3002\u201d\u505c\u4e86\u4e00\u505c\uff0c\u5ffd\u7136\u95ee\u9053\uff1a\u201c\u6211\u53bb\u56fd\u4e4b\u65f6\uff0c\u5c1a\u662f\u6c38\u4e50\u5341\u5e74\uff0c\u800c\u4eca\u5df2\u7ecf \u5386\u4e8c\u5341\u8f7d\uff0c\u6362\u4e86\u4e09\u671d\uff0c\u671d\u5ef7\u4e4b\u4e8b\uff0c\u5168\u65e0\u6240\u77e5\uff0c\u4e0d\u77e5\u5982\u4eca\u662f\u8c01\u5f53\u653f\uff1f\u201d\u8c22\u5929\u534e\u9053\uff1a\u201c\u662f\u738b\u632f\u5f53 \u6743\u3002\u201d\u4e91\u9756\u60f3\u8d77\u7b2c\u4e09\u9053\u9526\u56ca\u4e2d\u7684\u8bf4\u8bdd\uff0c\u51b2\u53e3\u8bf4\u9053\uff1a\u201c\u90a3\u4e48\u5929\u4f51\u6211\u671d\uff0c\u8fd9\u738b\u632f\u4e00\u5b9a\u662f\u4e2a\u5927\u5927\u7684 \u5fe0\u81e3\uff0c\u53ea\u6709\u90a3\u4e2a\u4e8e\u8c26\u60f3\u5fc5\u662f\u5978\u81e3\u4e86\u3002\u201d \u3000\u3000\u6f6e\u97f3\u548c\u5c1a\u6b63\u7eb5\u9a6c\u4e0a\u6765\uff0c\u508d\u7740\u9a74\u8f66\uff0c\u542c\u4e86\u4e91\u9756\u8a00\u8bed\uff0c\u5ffd\u7136\u628a\u7897\u53e3\u5927\u7684\u7985\u6756\u5f80\u5730\u4e0b\u4e00\u987f\uff0c\u5927 \u58f0\u8bf4\u9053\uff1a\u201c\u5927\u4eba\u9519\u4e86\uff0c\u8fd9\u738b\u632f\u662f\u4e2a\u5927\u5927\u7684\u5978\u81e3\uff0c\u82e5\u7136\u4ed6\u8981\u649e\u5728\u6d12\u5bb6\u624b\u4e0a\uff0c\u4e5f\u8981\u6559\u4ed6\u5403\u6211\u4e00\u987f \u7985\u6756\uff01\u201d\u4e91\u9756\u6115\u7136\u8bf4\u9053\uff1a\u201c\u4ec0\u4e48\uff0c\u4ed6\u662f\u5978\u81e3\uff1f\u4e0d\u4f1a\uff0c\u4e0d\u4f1a\u5427\uff01\u82e5\u7136\u4ed6\u662f\u5978\u81e3\uff0c\u80e1\u513f\u4f55\u4ee5\u53c8\u8981 \u5506\u4f7f\u4ec0\u4e48\u4e8e\u8c26\u51fa\u5934\uff0c\u53bb\u53c2\u52be\u4ed6\u3002\u201d\u8c22\u5929\u534e\u9053\uff1a\u201c\u5927\u4eba\u6709\u6240\u4e0d\u77e5\uff0c\u8fd9\u738b\u632f\u7684\u786e\u662f\u4e2a\u5978\u5ba6\u3002\u201d\u4e91 \u9756\u8be7\u9053\uff1a\u201c\u4ec0\u4e48\uff0c\u4ed6\u662f\u4e2a\u592a\u76d1\u5417\uff1f\u201d\u8c22\u5929\u534e\u9053\uff1a\u201c\u6b63\u662f\u3002\u542c\u8bf4\u6b64\u4eba\u539f\u5148\u5728\u6545\u4e61\u851a\u5dde\u8bfb\u8fc7\u4e66\uff0c \u4e0b\u8fc7\u8003\u573a\uff0c\u505a\u8fc7\u53bf\u5b98\uff0c\u540e\u6765\u72af\u4e86\u7f6a\uff0c\u672c\u5f53\u5145\u519b\uff0c\u9002\u9022\u7687\u5e1d\u4e0b\u8bcf\u2018\u6709\u5b50\u8005\u4ea6\u51c6\u51c0\u8eab\u5165\u5185\u2019\uff0c\u738b \u632f\u9042\u94bb\u8fdb\u4e86\u7687\u5bab\u3002\u540e\u6765\u5949\u6d3e\u4f8d\u5949\u592a\u5b50\uff0c\u4ea6\u5373\u5f53\u4eca\u7687\u4e0a\u8bfb\u4e66\uff0c\u81f3\u5148\u5e1d\u5f52\u5929\uff0c\u592a\u5b50\u5373\u4f4d\uff0c\u738b\u632f\u9042 \u5f97\u4efb\u53f8\u793c\u592a\u76d1\uff0c\u7ba1\u7406\u5185\u5916\u594f\u7ae0\uff0c\u4e8e\u662f\u9042\u52fe\u7ed3\u671d\u81e3\uff0c\u64c5\u4f5c\u5a01\u798f\uff0c\u5de7\u7acb\u540d\u76ee\uff0c\u82db\u5f81\u66b4\u655b\uff0c\u867d\u7136\u4e0d \u8fc7\u4e09\u5e74\uff0c\u767e\u59d3\u5df2\u662f\u6068\u4e4b\u5165\u9aa8\u3002\u5927\u4eba\u6b64\u6b21\u56de\u53bb\uff0c\u4e5f\u8981\u5f53\u5fc3\u3002\u201d\u4e91\u9756\u542c\u4e86\uff0c\u4e0d\u89c9\u6115\u7136\uff0c\u4ea6\u662f\u72d0\u7591 \u6ee1\u8179\u3002 \u3000\u3000\u8c22\u5929\u534e\u7eed\u8a00\u9053\uff1a\u201c\u90a3\u4e8e\u8c26\u5b98\u5c45\u5175\u90e8\u4f8d\u90ce\uff0c\u542c\u8bf4\u5012\u662f\u4e3a\u5b98\u6e05\u6b63\u3002\u201d\u4e91\u9756\u542c\u4e86\uff0c\u9ed8\u7136\u4e0d\u8bed\uff0c \u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u8fd9\u4e24\u4eba\u4e43\u662f\u6c5f\u6e56\u4e0a\u7684\u83bd\u592b\uff0c\u6240\u8a00\u4e0d\u8db3\u6df1\u4fe1\uff0c\u5f85\u6211\u56de\u671d\u4e4b\u540e\uff0c\u518d\u4eb2\u81ea\u770b\u4e2a\u660e \u767d\u3002\u201d\u53c8\u60f3\u9053\uff1a\u201c\u5175\u6cd5\u6709\u4e91\uff1a\u865a\u8005\u5b9e\u4e4b\uff0c\u5b9e\u8005\u865a\u4e4b\uff0c\u7eb5\u7136\u8fd9\u4e24\u4eba\u6240\u8bf4\u662f\u5b9e\uff0c\u4e5f\u5b9a\u662f\u5f20\u5b97\u5468\u5e03 \u4e0b\u7684\u5708\u5957\uff0c\u6545\u610f\u53eb\u6211\u76f8\u4fe1\u4ed6\u7684\u8bdd\uff0c\u5176\u4e2d\u5fc5\u5b9a\u85cf\u6709\u9634\u8c0b\u3002\u201d \u3000\u3000\u9a74\u8f66\u4e0a\u4e91\u857e\u7761\u5f97\u6b63\u9163\uff0c\u4e91\u9756\u671b\u7740\u5979\u82f9\u679c\u822c\u7684\u8138\u513f\uff0c\u5929\u771f\u65e0\u90aa\uff0c\u53ef\u7231\u4e4b\u6781\u3002\u60f3\u5230\u4ed6\u5e74\u4e91\u857e \u957f\u5927\u4e4b\u540e\uff0c\u4e5f\u8981\u8fdc\u8d74\u80e1\u8fb9\uff0c\u51b2\u971c\u5192\u96ea\uff0c\u66ff\u81ea\u5df1\u62a5\u4ec7\uff0c\u4e0d\u89c9\u53f9\u4e86\u53e3\u6c14\u3002\u4f46\u77ac\u606f\u4e4b\u95f4\uff0c\u4e8c\u5341\u5e74\u6765 \u56bc\u96ea\u996e\u51b0\uff0c\u6371\u9965\u62b5\u51b7\u79cd\u79cd\u82e6\u96be\uff0c\u53c8\u5728\u5fc3\u5934\u6cdb\u8d77\uff0c\u6068\u706b\u70e7\u5fc3\uff0c\u76d6\u8fc7\u4e86\u4e3a\u4e91\u857e\u601c\u60dc\u4e4b\u5ff5\u3002\u773c\u671b\u591c \u7a7a\uff0c\u5fc3\u6f6e\u6d6a\u6d8c\uff0c\u8fc7\u4e86\u4e9b\u65f6\uff0c\u4e0d\u89c9\u8ff7\u8ff7\u7cca\u7cca\u5730\u548c\u8863\u7761\u4e86\u3002 \u3000\u3000\u4e00\u89c9\u9192\u6765\uff0c\u5df2\u662f\u7b2c\u4e8c\u65e5\u6e05\u6668\uff0c\u96c1\u95e8\u5173\u4e0a\u7684\u65cc\u65d7\uff0c\u5df2\u7ecf\u53ef\u4ee5\u6e05\u695a\u671b\u89c1\u3002\u6f6e\u97f3\u548c\u5c1a\u9053\uff1a\u201c\u8fd9 \u662f\u4e03\u91cc\u94fa\uff0c\u79bb\u96c1\u95e8\u5173\u53ea\u6709\u4e03\u91cc\u8def\u4e86\u3002\u524d\u9762\u5c31\u662f\u96c1\u95e8\u5173\u5916\u68c0\u67e5\u884c\u65c5\u7684\u536b\u6240\u4e86\u3002\u201d\u4e91\u9756\u8df3\u4e86\u8d77 \u6765\uff0c\u63ed\u5f00\u5e18\u5e55\uff0c\u95ee\u9053\uff1a\u201c\u5468\u603b\u540e\u4fe8\u4e86\u6ca1\u6709\uff1f\u201d\u6f6e\u97f3\u548c\u5c1a\u9053\uff1a\u201c\u5929\u534e\u5e08\u5f1f\u5df2\u5165\u5185\u901a\u62a5\u53bb\u4e86\u3002\u4e0d \u66fe\u542c\u8bf4\u5468\u603b\u5175\u8981\u6765\u3002\u201d\u4e91\u9756\u6014\u4e86\u6014\uff0c\u5ffd\u800c\u5931\u7b11\uff0c\u81ea\u8a00\u81ea\u8bed\u9053\uff1a\u201c\u6211\u4e5f\u7ed9\u90a3\u4e2a\u9b3c\u9526\u56ca\u5f04\u9519\u4e86\u3002 \u5468\u603b\u5175\u600e\u4f1a\u77e5\u9053\u6211\u4eca\u65e5\u5230\u6765\uff1f\u901a\u62a5\u4e4b\u540e\uff0c\u4ed6\u81ea\u7136\u4f1a\u6765\u8fce\u6211\u3002\u201d\u4fbf\u5429\u5490\u505c\u4e0b\u9a74\u8f66\uff0c\u5728\u536b\u6240\u4e4b\u524d \u7b49\u5f85\u3002\u536b\u5352\u4eec\u5728\u57ce\u5899\u5185\u5f20\u671b\u7740\uff0c\u5e76\u65e0\u4efb\u4f55\u52a8\u9759\u3002 \u3000\u3000\u4e14\u8bf4\u8c22\u5929\u534e\u4e3a\u4eba\uff0c\u80c6\u5927\u5fc3\u7ec6\uff0c\u5148\u5165\u96c1\u95e8\u5173\u901a\u62a5\uff0c\u4fbf\u662f\u4ed6\u7684\u4e3b\u610f\u3002\u96c1\u95e8\u5173\u7684\u603b\u5175\u5468\u5065\uff0c\u8c22 \u5929\u534e\u4e5f\u66fe\u89c1\u8fc7\u51e0\u9762\uff0c\u6df1\u77e5\u8fd9\u4f4d\u8fb9\u5173\u5b88\u5c06\uff0c\u4e0d\u4f46\u662f\u4e91\u9756\u7684\u540c\u4e61\u65e7\u53cb\uff0c\u800c\u4e14\u4fa0\u9aa8\u82f1\u98ce\uff0c\u4e0e\u6c5f\u6e56\u8c6a \u6770\u80f8\u895f\u65e0\u4e8c\u3002\u4e03\u91cc\u8def\u7a0b\u8f6c\u77ac\u5373\u5230\uff0c\u96c1\u95e8\u5173\u4e0a\u4e86\u65e0\u5f02\u72b6\uff0c\u4ecd\u662f\u7531\u524d\u51e0\u6b21\u5e26\u5f15\u81ea\u5df1\u7684\u65d7\u724c\u5b98\u63a5\u5f85 \u5165\u5185\uff0c\u8c22\u5929\u534e\u5fc3\u5934\u4e00\u5bbd\uff0c\u6697\u7b11\u9053\uff1a\u201c\u6fb9\u53f0\u706d\u660e\u6545\u5e03\u7591\u9635\uff0c\u88c5\u795e\u5f04\u9b3c\uff0c\u8fde\u6211\u4e5f\u53d7\u4ed6\u8ff7\u60d1\u4e86\u3002\u53ea \u8981\u5468\u603b\u5175\u4ecd\u9547\u5b88\u6b64\u5173\uff0c\u6709\u8c01\u6562\u52a0\u5bb3\u4e91\u9756\uff1f\u201d \u3000\u3000\u5e10\u4e2d\u5750\u5b9a\uff0c\u65d7\u724c\u5b98\u732e\u4e0a\u8336\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u603b\u5175\u5927\u4eba\u5c31\u8981\u51fa\u6765\u4e86\uff0c\u8c22\u4fa0\u58eb\u4f60\u6b47\u606f\u4f1a\u513f\u3002\u201d\u8c22 \u5929\u534e\u559d\u4e86\u9999\u8336\uff0c\u5378\u4e0b\u62a4\u8eab\u888d\u7532\uff0c\u6b63\u5728\u7b49\u5f85\uff0c\u5ffd\u89c9\u5934\u660f\u773c\u82b1\uff0c\u53eb\u58f0\u201c\u4e0d\u597d\uff01\u201d\u8fde\u5fd9\u62d4\u5251\uff0c\u90a3\u65d7 \u724c\u5b98\u5df2\u62a2\u5148\u4e00\u6b65\uff0c\u5c06\u4ed6\u5b9d\u5251\u593a\u53bb\uff0c\u5e10\u5916\u547c\u547c\u4e24\u58f0\uff0c\u629b\u8fdb\u4e86\u4e24\u6761\u7eca\u9a6c\u7d22\uff0c\u5c06\u4ed6\u7eca\u5012\u3002 \u3000\u3000\u8c22\u5929\u534e\u5185\u529f\u6df1\u6e5b\uff0c\u867d\u7136\u4e2d\u4e86\u6697\u7b97\uff0c\u5c1a\u672a\u660f\u8ff7\uff0c\u6323\u624e\u6b32\u8d77\uff0c\u5374\u662f\u6d51\u8eab\u65e0\u529b\uff0c\u800c\u4e14\u660f\u660f\u601d \u7761\uff0c\u773c\u76ae\u6e10\u6e10\u7741\u4e0d\u5f00\u6765\u3002\u8c22\u5929\u534e\u9ed8\u8fd0\u7384\u529f\uff0c\u4e0e\u7761\u9b54\u76f8\u6297\uff0c\u8ff7\u8ff7\u7cca\u7cca\u4e4b\u4e2d\uff0c\u4f3c\u5df2\u88ab\u4eba\u625b\u8d77\uff0c\u4e0d \u4e45\u53c8\u542c\u5f97\u5173\u95e8\u4e0b\u9501\u4e4b\u58f0\uff0c\u4f3c\u662f\u5df2\u7ed9\u4eba\u5173\u5728\u4e00\u95f4\u9ed1\u6c89\u6c89\u7684\u5c4b\u5b50\u91cc\u4e86\u3002 \u3000\u3000\u90a3\u7897\u8336\u4e2d\u6eb6\u6709\u6781\u5389\u5bb3\u7684\u8499\u6c57\u836f\uff0c\u5bfb\u5e38\u4e4b\u4eba\uff0c\u6d45\u5c1d\u5373\u5012\uff0c\u8c22\u5929\u534e\u7ec3\u8fc7\u6613\u7b4b\u6d17\u9ad3\u7684\u529f\u592b\uff0c\u8fd0 \u6c14\u76f8\u6297\uff0c\u4f7f\u81ea\u5df1\u4fdd\u6301\u7740\u5fc3\u5934\u7684\u4e00\u7247\u6e05\u9192\u3002\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u5c11\u65f6\u5019\uff0c\u623f\u95e8\u5440\u5440\u63a8\u5f00\uff0c\u4e00\u4e2a\u4eba\u63a2\u5934 \u8fdb\u6765\uff0c\u8c22\u5929\u534e\u5b9a\u775b\u4e00\u77a7\uff0c\u6b63\u662f\u96c1\u95e8\u5173\u7684\u603b\u5175\u5468\u5065\u3002 \u3000\u3000\u8c22\u5929\u534e\u6258\u5730\u8df3\u8d77\uff0c\u4f7f\u5c3d\u6c14\u529b\uff0c\u547c\u7684\u4e00\u638c\u6a2a\u626b\uff0c\u5411\u4ed6\u8111\u95e8\u5288\u53bb\u3002\u5468\u5065\u6a2a\u80b1\u4e00\u67b6\uff0c\u53eb\u9053\uff1a \u201c\u662f\u6211\uff01\u201d\u8c22\u5929\u534e\u6c14\u529b\u672a\u590d\uff0c\u7ed9\u4ed6\u4e00\u67b6\uff0c\u8dc4\u8dc4\u8e09\u8e09\u5730\u5012\u9000\u6570\u6b65\uff0c\u4e00\u5934\u649e\u5728\u5899\u4e0a\uff0c\u6012\u53eb\u9053\uff1a \u201c\u597d\uff0c\u77e5\u4eba\u77e5\u9762\u4e0d\u77e5\u5fc3\uff0c\u603b\u5175\u5927\u4eba\uff0c\u4f60\u7528\u7684\u4e0b\u4e09\u6d41\u7684\u6697\u7b97\u624b\u6bb5\uff0c\u7528\u5f97\u771f\u5230\u5bb6\u5440\uff01\u201d\u5468\u5065\u8fc8\u524d \u4e24\u6b65\uff0c\u628a\u4ed6\u624b\u8155\u4e00\u62ff\uff0c\u4f4e\u58f0\u53eb\u9053\uff1a\u201c\u4e8b\u60c5\u5df2\u6025\uff0c\u5feb\u670d\u4e0b\u89e3\u836f\uff0c\u6211\u4e0e\u4f60\u6551\u4e91\u5927\u4eba\u53bb\u3002\u4f60\u7684\u5b9d\u5251 \u6211\u66ff\u4f60\u62ff\u56de\u6765\u4e86\uff0c\u5feb\u5440\uff01\u201d\u8c22\u5929\u534e\u60ca\u6115\u4e4b\u6781\uff0c\u53eb\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u4f60\u3001\u4f60\u662f\u4ec0\u4e48\u7528\u610f\uff1f\u201d\u9ed1\u5ba4\u4e4b \u4e2d\uff0c\u4f46\u89c1\u5468\u5065\u53cc\u7738\u70af\u70af\uff0c\u522b\u5177\u5a01\u4e25\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u5468\u5065\u662f\u4f55\u7b49\u4e4b\u4eba\uff0c\u4f60\u8fd8\u4e0d\u77e5\u9053\u5417\uff1f\u6b64\u9645 \u4e8b\u673a\u5df2\u6025\uff0c\u6709\u8bdd\u6162\u8bf4\uff0c\u4f60\u5feb\u968f\u6211\u51fa\u53bb\u3002\u201d\u8c22\u5929\u534e\u4e0d\u7531\u5f97\u5f20\u5f00\u4e86\u5634\uff0c\u541e\u4e0b\u4e86\u5468\u5065\u585e\u6765\u7684\u836f\u4e38\u3002 \u8c22\u5929\u534e\u5fc3\u5934\u672c\u5c31\u6e05\u9192\uff0c\u541e\u4e0b\u89e3\u836f\uff0c\u7761\u610f\u5168\u6d88\u4e86\uff0c\u63a5\u8fc7\u5468\u5065\u9012\u6765\u7684\u5b9d\u5251\uff0c\u8dc3\u51fa\u95e8\u5916\u3002 \u3000\u3000\u96c1\u95e8\u5173\u5916\u53f7\u89d2\u957f\u9e23\uff0c\u53ea\u89c1\u5148\u524d\u90a3\u540d\u7528\u8499\u6c57\u836f\u5077\u65bd\u6697\u7b97\u7684\u65d7\u724c\u5b98\u62e6\u4e0a\u524d\u6765\uff0c\u9ad8\u58f0\u53eb\u9053\uff1a \u201c\u5468\u5927\u4eba\uff0c\u4f60\u53ef\u5f97\u4e09\u601d\u800c\u884c\uff0c\u522b\u8981\u81ea\u8bef\u524d\u7a0b\uff01\u201d\u5468\u5065\u4e00\u58f0\u4e0d\u54cd\uff0c\u7a81\u7136\u4e00\u8dc3\u800c\u8d77\uff0c\u6325\u5200\u4e00\u65a9\uff0c \u5c06\u90a3\u65d7\u724c\u5b98\u65a9\u4e3a\u4e24\u622a\uff0c\u593a\u4e86\u4e24\u9a91\u5feb\u9a6c\uff0c\u4e0e\u8c22\u5929\u534e\u5954\u51fa\u8f95\u95e8\uff0c\u5173\u5916\u5b98\u5175\uff0c\u65e0\u4eba\u6562\u6321\u3002 \u3000\u3000\u5468\u5065\u5a01\u98ce\u51db\u51db\uff0c\u6740\u6c14\u817e\u817e\uff0c\u5728\u9a6c\u80cc\u4e0a\u626c\u97ad\u6307\u9053\uff1a\u201c\u4ed6\u4eec\u6b63\u5728\u4e03\u91cc\u94fa\u5916\u53ae\u6740\uff0c\u4f60\u6211\u6284\u5c0f\u8def \u53bb\uff01\u201d\u4e00\u62e8\u9a6c\u5934\uff0c\u4ece\u5c71\u8fb9\u5c0f\u5f84\u9a70\u53bb\uff0c\u5927\u8def\u4e0a\u8f66\u9a6c\u5954\u9a70\uff0c\u8bb8\u591a\u4eba\u9ad8\u58f0\u547c\u558a\uff0c\u53eb\u5468\u603b\u5175\u56de\u6765\u3002\u5468 \u5065\u6beb\u4e0d\u7406\u776c\u3002 \u3000\u3000\u4e14\u8bf4\u4e91\u9756\u5728\u4e03\u91cc\u94fa\u7684\u536b\u6240\u5916\u7b49\u4e86\u8bb8\u4e45\uff0c\u6b63\u81ea\u751f\u6c14\uff0c\u5ffd\u89c1\u8def\u4e0a\u5c18\u5934\u5927\u8d77\uff0c\u5341\u51e0\u9a91\u5feb\u9a6c\u98de\u5954 \u800c\u6765\uff0c\u4e0d\u4e00\u523b\u536b\u6240\u6253\u5f00\uff0c\u620d\u5b88\u536b\u6240\u7684\u5b98\u957f\u62ab\u6302\u51fa\u8fce\uff0c\u9ad8\u58f0\u8bf7\u8fdb\u3002\u4e91\u9756\u770b\u5f97\u6e05\u695a\uff0c\u90a3\u4ece\u96c1\u95e8\u5173 \u6765\u8fce\u63a5\u7684\u5341\u51e0\u9a91\u5feb\u9a6c\uff0c\u5176\u4e2d\u5e76\u65e0\u5468\u5065\u5728\u5185\uff0c\u5fc3\u4e2d\u5341\u5206\u4e0d\u5feb\uff0c\u4f46\u4ecd\u662f\u6021\u7136\u81ea\u82e5\uff0c\u624b\u6301\u4f7f\u8282\uff0c\u6b65 \u5165\u8fb9\u5173\u3002 \u3000\u3000\u536b\u6240\u5185\u8bbe\u597d\u5ea7\u4f4d\uff0c\u53ea\u89c1\u5341\u516d\u540d\u5fa1\u6797\u519b\u5206\u6210\u4e24\u961f\uff0c\u5206\u5217\u5728\u9636\u4e0b\uff0c\u5802\u4e0a\u4e24\u540d\u94a6\u5dee\uff0c\u51a0\u5e26\u51fa \u8fce\u3002\u4e91\u9756\u987f\u65f6\u6b22\u559c\u8d77\u6765\uff0c\u5fc3\u4e2d\u60f3\uff1a\u201c\u539f\u6765\u662f\u5723\u5929\u5b50\u7279\u964d\u5929\u6069\uff0c\u5ff5\u6211\u4e8c\u5341\u5e74\u5b88\u8282\uff0c\u7adf\u7136\u6d3e\u94a6\u5dee \u5230\u8fb9\u5173\u8fce\u63a5\u6765\u4e86\u3002\u201d\u6b63\u8bf4\u5f97\u53e5\u201c\u4e91\u67d0\u4f55\u529f\uff0c\u6562\u52b3\u94a6\u5dee\u8fdc\u63a5\u201d\uff0c\u5802\u4e0a\u7684\u94a6\u5dee\uff0c\u9762\u5b54\u4e00\u7aef\uff0c\u5ffd\u7136 \u95f4\u9ad8\u58f0\u559d\u53f1\u9053\uff1a\u201c\u53db\u81e3\u4e91\u9756\uff0c\u8dea\u4e0b\u63a5\u65e8\uff01\u201d \u3000\u3000\u4e91\u9756\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u624b\u6301\u4f7f\u8282\uff0c\u98a4\u58f0\u8fa9\u9053\uff1a\u201c\u4e91\u67d0\u51fa\u4f7f\u5f02\u56fd\uff0c\u4e8c\u5341\u5e74\u6765\u7267\u9a6c\u80e1\u8fb9\uff0c\u5c1a \u5b58\u6b64\u8282\uff0c\u81ea\u95ee\u65e0\u7f6a\uff0c\u4e0d\u6562\u63a5\u6b64\u8bcf\u4e66\uff01\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u5df2\u7ed9\u4e24\u540d\u5fa1\u6797\u519b\u6309\u5012\u5730\u4e0a\u3002\u53ea\u542c\u5f97\u5176\u4e2d\u4e00 \u540d\u94a6\u5dee\uff0c\u5c55\u5f00\u62db\u4e66\uff0c\u9ad8\u58f0\u8bfb\u9053\uff1a \u3000\u3000\u201c\u7f6a\u81e3\u4e91\u9756\uff0c\u5148\u5e1d\u5bc4\u4ee5\u8179\u5fc3\uff0c\u9063\u4f7f\u74e6\u523a\uff0c\u800c\u4e43\u4e0d\u611f\u6069\u56fe\u62a5\uff0c\u53cd\u817c\u989c\u4e8b\u4ec7\uff0c\u5fd8\u5176\u7236\u6bcd\u4e4b \u56fd\u3002\u4eca\u65e5\u79c1\u81ea\u5f52\u6765\uff0c\u56fe\u8c0b\u5185\u5e94\uff0c\u7f6a\u65e0\u53ef\u6055\uff0c\u672c\u5e94\u660e\u6b63\u5178\u5211\uff0c\u59d1\u5ff5\u5176\u662f\u524d\u671d\u65e7\u81e3\uff0c\u6069\u5f00\u6cd5\u5916\uff0c \u51c6\u5176\u4ef0\u836f\u81ea\u88c1\uff0c\u5168\u5c38\u6536\u6b93\u3002\u94a6\u6b64\u3002\u201d \u3000\u3000\u4e91\u9756\u9b42\u4e0d\u9644\u4f53\uff0c\u53ea\u89c1\u4e00\u540d\u5fa1\u6797\u519b\u6367\u7740\u4e00\u53ea\u94f6\u74f6\uff0c\u5185\u4e2d\u836f\u6c34\u6bb7\u7ea2\uff0c\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u7f6a\u81e3\u4e91\u9756 \u8fd8\u4e0d\u8c22\u6069\u9886\u65e8\u4e48\uff1f\u201d \u3000\u3000\u4e91\u9756\u53ea\u89c9\u8111\u95e8\u4e0a\u8f70\u7684\u4e00\u58f0\uff0c\u53c8\u60ca\u53c8\u6c14\u53c8\u6025\u53c8\u6012\uff0c\u5ffd\u7136\u4e00\u624b\u6293\u8fc7\u94f6\u74f6\uff0c\u5c16\u58f0\u53eb\u9053\uff1a\u201c\u7ed9\u8bcf \u4e66\u6211\u770b\uff0c\u6211\u4e0d\u4fe1\u8fd9\u662f\u771f\u7684\uff01\u201d\u94a6\u5dee\u51b7\u7b11\u4e00\u58f0\uff0c\u559d\u9053\uff1a\u201c\u597d\u5927\u7684\u80c6\u5b50\uff0c\u8bcf\u4e66\u662f\u4f60\u770b\u5f97\u7684\u5417\uff1f\u201d \u8bdd\u72b9\u672a\u4e86\uff0c\u53ea\u542c\u5f97\u8f70\u5929\u4ef7\u7684\u4e00\u58f0\u5de8\u54cd\uff0c\u4e24\u6247\u534a\u63a9\u7684\u5927\u95e8\u51ed\u7a7a\u98de\u4e86\u8d77\u6765\uff0c\u4e00\u4e2a\u83bd\u548c\u5c1a\u63d0\u7740\u4e00\u7897 \u53e3\u822c\u7c97\u5927\u7684\u7985\u6756\uff0c\u6cfc\u98ce\u4f3c\u7684\u6253\u5c06\u5165\u6765\uff0c\u9ad8\u58f0\u559d\u9053\uff1a\u201c\u7ba1\u5b83\u771f\u7684\u5047\u7684\uff0c\u90fd\u6253\u6b7b\u4e86\u518d\u8bf4\uff01\u201d\u5341\u516d \u540d\u5fa1\u6797\u519b\u4e0a\u524d\u62b5\u654c\uff0c\u54ea\u80fd\u62b5\u654c\u5f97\u4f4f\uff1f\u53ea\u89c1\u4ed6\u6307\u4e1c\u6253\u897f\uff0c\u6307\u5357\u6253\u5317\uff0c\u7985\u6756\u6240\u5230\u4e4b\u5904\uff0c\u6709\u5982\u5f00\u5c71 \u88c2\u77f3\uff0c\u53ea\u8981\u6328\u7740\u4e00\u70b9\uff0c\u4fbf\u4e0d\u6b7b\u5373\u4f24\u3002 \u3000\u3000\u90a3\u4e24\u4e2a\u94a6\u5dee\u5413\u5f97\u9762\u9752\u5507\u767d\uff0c\u817f\u90fd\u8f6f\u4e86\u3002\u90a3\u548c\u5c1a\u4e00\u8def\u6253\u5230\u5802\u4e0a\uff0c\u5de6\u540e\u4e00\u62bb\uff0c\u5140\u9e70\u6293\u9e21\u4f3c\u5730 \u63d0\u8d77\u4e86\u4e00\u540d\u94a6\u5dee\uff0c\u9a82\u9053\uff1a\u201c\u4e91\u5927\u4eba\u820d\u547d\u9003\u56de\uff0c\u4f60\u4eec\u8fd8\u8981\u5c06\u4ed6\u5f04\u6b7b\uff0c\u662f\u4f55\u9053\u7406\uff1f\u201d\u201c\u535c\u201d\u7684\u4e00 \u7985\u6756\uff0c\u6572\u5728\u4ed6\u7684\u5934\u4e0a\uff0c\u7529\u624b\u4e00\u6454\uff0c\u8111\u6d46\u6d82\u5730\uff0c\u6b7b\u4e8e\u9636\u4e0b\u3002\u53e6\u4e00\u540d\u94a6\u5dee\u5413\u5f97\u795e\u667a\u660f\u4e71\uff0c\u5140\u81ea\u53eb \u9053\uff1a\u201c\u53cd\u4e86\uff0c\u53cd\u4e86\uff01\u5192\u72af\u94a6\u5dee\uff0c\u8be5\u5f53\u4f55\u7f6a\uff1f\u201d\u90a3\u548c\u5c1a\u653e\u58f0\u5927\u7b11\uff0c\u53c8\u4e00\u628a\u5c06\u4ed6\u6293\u4e86\u8d77\u6765\uff0c\u9a82\u4ed6 \u9053\uff1a\u201c\u5140\u8fd9\u53ae\u9e1f\uff0c\u94a6\u5dee\u503c\u5f97\u6211\u5c11\u94b1\u4e00\u65a4\uff1f\u201d\u7985\u6756\u5f80\u5730\u4e0a\u4e00\u63d2\uff0c\u786c\u751f\u751f\u5730\u5c06\u4ed6\u6495\u6210\u4e24\u7247\u3002\u5fa1\u6797 \u519b\u7eb7\u7eb7\u9003\u51fa\uff0c\u5439\u8d77\u53f7\u89d2\uff0c\u536b\u6240\u5185\u5c38\u6a2a\u904d\u5730\uff0c\u53ea\u5269\u4e0b\u4e86\u548c\u5c1a\u548c\u4e91\u9756\u4e8c\u4eba\u3002 \u3000\u3000\u4e91\u9756\u76ee\u77aa\u53e3\u5446\uff0c\u604d\u5982\u5728\u4e00\u573a\u6076\u68a6\u4e4b\u4e2d\uff0c\u4e0d\u77e5\u76ee\u524d\u6240\u53d1\u751f\u7684\u79cd\u79cd\u4e8b\u60c5\u662f\u771f\u662f\u5047\uff0c\u5b9a\u4e86\u5b9a \u795e\uff0c\u89c1\u6f6e\u97f3\u548c\u5c1a\u671d\u4ed6\u8d70\u6765\uff0c\u731b\u7136\u53eb\u9053\uff1a\u201c\u628a\u90a3\u8bcf\u4e66\u7ed9\u6211\u3002\u201d \u3000\u3000\u6f6e\u97f3\u548c\u5c1a\u54a7\u5634\u51b7\u7b11\uff0c\u9053\uff1a\u201c\u8fd8\u6709\u4ec0\u4e48\u9e1f\u8bcf\u4e66\uff0c\u5feb\u5feb\u968f\u6211\u8d70\u5427\uff01\u201d\u4e91\u9756\u76d8\u819d\u4e00\u5750\uff0c\u4e00\u5b57\u4e00 \u53e5\uff0c\u65a9\u9489\u622a\u94c1\u5730\u8bf4\u9053\uff1a\u201c\u628a\u90a3\u8bcf\u4e66\u7ed9\u6211\uff01\u201d\u6f6e\u97f3\u548c\u5c1a\u6a2a\u4ed6\u4e00\u773c\uff0c\u5728\u51e0\u6848\u4e0a\u6293\u8d77\u8bcf\u4e66\uff0c\u6454\u7ed9 \u4ed6\uff1a\u201c\u597d\uff0c\u5feb\u770b\uff01\u5feb\u770b\uff01\u201d\u5bf9\u4ed6\u5982\u6b64\u56fa\u6267\uff0c\u4e07\u5206\u4e0d\u89e3\u3002 \u3000\u3000\u4e91\u9756\u5c55\u5f00\u8bcf\u4e66\uff0c\u4e00\u77a5\u4e4b\u4e0b\uff0c\u9762\u5982\u6b7b\u7070\uff0c\u90a3\u8bcf\u4e66\u4e0a\u7684\u7389\u73ba\uff0c\u4e0e\u8bcf\u4e66\u7684\u683c\u5f0f\u7eb8\u8d28\uff0c\u90fd\u662f\u771f \u7684\u3002\u4e91\u9756\u8fd8\u8bb0\u5f97\u4ee5\u524d\u6210\u7956\u593a\u4f4d\uff0c\u66fe\u5728\u5185\u76d1\u624b\u4e0a\u62a2\u593a\u7389\u73ba\uff0c\u90a3\u5185\u76d1\u5c06\u7389\u73ba\u6454\u4e0b\u5929\u9636\uff0c\u7f3a\u4e86\u4e00 \u89d2\uff0c\u540e\u6765\u53eb\u5de7\u5320\u91cd\u8865\uff0c\u7eb9\u7406\u4e24\u6837\uff0c\u800c\u4eca\u7ec6\u8fa8\u8fd9\u8bcf\u4e66\u4e0a\u7684\u7389\u73ba\uff0c\u6b63\u662f\u5982\u6b64\uff0c\u7edd\u5bf9\u5047\u5192\u4e0d\u6765\u3002 \u3000\u3000\u6f6e\u97f3\u548c\u5c1a\u53eb\u9053\uff1a\u201c\u770b\u591f\u4e86\u6ca1\u6709\uff1f\u201d\u4e91\u9756\u773c\u775b\u76f4\u89c6\uff0c\u542c\u800c\u4e0d\u95fb\u3002\u8fd9\u4e00\u77ac\u95f4\uff0c\u4e8c\u5341\u5e74\u6765\u5728\u80e1 \u8fb9\u6240\u53d7\u7684\u82e6\u96be\uff0c\u95ea\u7535\u822c\u5730\u5728\u8111\u6d77\u4e4b\u4e2d\u63a0\u8fc7\u3002\u7136\u800c\u8fd9\u4e00\u5207\u82e6\u96be\uff0c\u6bd4\u8d77\u800c\u4eca\u7684\u75db\u82e6\uff0c\u7b80\u76f4\u7b97\u4e0d\u4e86 \u4ec0\u4e48\u3002\u987b\u77e5\u4e91\u9756\u80fd\u591f\u652f\u6491\u4e8c\u5341\u5e74\uff0c\u5168\u5728\u5fe0\u541b\u4e00\u5ff5\uff0c\u6ee1\u4ee5\u4e3a\u9003\u56de\u4e4b\u540e\uff0c\u671d\u5ef7\u5fc5\u5b9a\u5347\u5b98\u53d9\u7235\uff0c\u8868 \u626c\u529f\u7ee9\uff0c\u54ea\u77e5\u7687\u5e1d\u7adf\u662f\u4eb2\u4e0b\u8bcf\u4e66\uff0c\u5c06\u4ed6\u5904\u6b7b\u3002\u6b63\u5982\u5bf9\u4e00\u4e2a\u4eba\u5d07\u62dc\u4fe1\u4ef0\u5230\u4e86\u6781\u70b9\uff0c\u671f\u671b\u6781\u6df1\uff0c \u5ffd\u800c\u53d1\u73b0\u90a3\u4e2a\u4eba\u5c31\u662f\u8981\u5bb3\u6b7b\u81ea\u5df1\u7684\u4eba\uff0c\u8fd9\u4e00\u79cd\u7edd\u671b\u7684\u75db\u82e6\u5fc3\u60c5\uff0c\u4e16\u754c\u4e0a\u8fd8\u6709\u4ec0\u4e48\u53ef\u8d85\u8fc7\uff1f \u3000\u3000\u6f6e\u97f3\u548c\u5c1a\u53eb\u4e86\u4e24\u58f0\uff0c\u4e0d\u89c1\u7b54\u5e94\uff0c\u5fc3\u4e2d\u5927\u5f02\u3002\u5ffd\u89c1\u4e91\u9756\u7f13\u7f13\u7ad9\u4e86\u8d77\u6765\uff0c\u5c06\u90a3\u4e00\u6839\u4f34\u968f\u4ed6\u5728 \u51b0\u5929\u96ea\u91cc\u4e8c\u5341\u5e74\u7684\u4f7f\u8282\uff0c\u7528\u529b\u4e00\u62d7\uff0c\u201c\u556a\u201d\u7684\u4e00\u58f0\uff0c\u6298\u4e3a\u4e24\u6bb5\u3002 \u3000\u3000\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u4e91\u9756\u8111\u4e2d\u7a7a\u7a7a\u6d1e\u6d1e\uff0c\u597d\u50cf\u795e\u7ecf\u5168\u90fd\u9ebb\u6728\uff0c\u4e00\u5207\u90fd\u89c9\u832b\u7136\uff0e\u751f\u7684\u610f\u4e49\u5df2\u7ecf\u6d88 \u5931\uff0c\u6574\u4e2a\u4e16\u754c\u90fd\u597d\u50cf\u8131\u79bb\u4e86\u81ea\u5df1\u5411\u6773\u4e0d\u53ef\u77e5\u7684\u8fdc\u65b9\u98de\u53bb\u3002\u4ed6\u7684\u8eab\u8eaf\u5fae\u5fae\u98a4\u6296\uff0c\u811a\u5c16\u7a81\u7136\u78b0\u7740 \u5730\u4e0b\u7684\u94f6\u74f6\uff0c\u4e91\u9756\u4e00\u5f2f\u8170\u6293\u8d77\u94f6\u74f6\uff0c\u53ea\u4e00\u53e3\u5c31\u628a\u90a3\u74f6\u4e2d\u7684\u6bd2\u836f\u559d\u4e2a\u5e72\u51c0\u3002 \u3000\u3000\u6f6e\u97f3\u53eb\u9053\uff1a\u201c\u4f60\u5e72\u4ec0\u4e48\uff1f\u201d\u98de\u6b65\u4e0a\u524d\uff0c\u53ea\u89c1\u4e91\u9756\u5012\u5728\u5730\u4e0a\uff0c\u4e03\u7a8d\u6d41\u8840\u3002\u90a3\u94f6\u74f6\u4e2d\u7684\u6bd2\u836f \u4e43\u662f\u6700\u5389\u5bb3\u7684\u201c\u9e64\u9876\u7ea2\u201d\u6bd2\u9152\uff0c\u6cbe\u4e86\u4e00\u6ef4\u4fbf\u8db3\u6bd9\u547d\uff0c\u4f55\u51b5\u559d\u4e86\u4e00\u74f6\uff01 \u3000\u3000\u6f6e\u97f3\u548c\u5c1a\u5446\u5728\u5ead\u4e2d\uff0e\u505a\u58f0\u4e0d\u5f97\uff0c\u53ea\u542c\u5f97\u5916\u9762\u4eba\u58f0\u5608\u6742\uff0c\u5200\u67aa\u58f0\u54cd\uff0c\u8fd8\u5939\u6709\u4e91\u857e\u7684\u54ed\u58f0\u3002 \u539f\u6765\u9a74\u8f66\u5c31\u505c\u5728\u536b\u6240\u95e8\u5916\uff0c\u60f3\u662f\u6765\u6349\u4eba\u7684\u5378\u6797\u519b\u5df2\u56f4\u5728\u9a74\u8f66\u4e0e\u81ea\u5df1\u7684\u4e24\u4e2a\u5f92\u5f1f\u6253\u8d77\u6765\u4e86\u3002 \u3000\u3000\u6f6e\u97f3\u548c\u5c1a\u5927\u543c\u4e00\u58f0\uff0c\u62e8\u8d77\u7985\u6756\u6253\u5c06\u51fa\u53bb\uff0c\u4f17\u519b\u58eb\u53d1\u4e00\u58f0\u558a\uff0c\u5206\u51fa\u4eba\u6765\u5835\u622a\uff0c\u6f6e\u97f3\u548c\u5c1a\u6a2a \u6756\u4e00\u9694\uff0c\u5200\u67aa\u4e71\u98de\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u62a2\u5230\u8f66\u524d\uff0c\u62b1\u8d77\u4e91\u857e\uff0c\u62cd\u62cd\u5979\u9053\uff1a\u201c\u522b\u6015\uff0c\u522b\u6015\uff01\u201d\u7ffb\u8f6c\u8eab \u6765\uff0c\u53c8\u6740\u51fa\u53bb\u3002 \u3000\u3000\u4e91\u857e\u4f0f\u5728\u4ed6\u7684\u80a9\u4e0a\uff0c\u7741\u7740\u4e24\u53ea\u5706\u6e9c\u6e9c\u7684\u773c\u775b\uff0c\u5374\u4e5f\u4e0d\u54ed\u5c0f\u53eb\u3002\u6f6e\u97f3\u548c\u5c1a\u4e0e\u4e24\u4e2a\u5f92\u5f1f\u51b2\u6740 \u51fa\u53bb\uff0c\u67aa\u4e86\u9a6c\u5339\uff0c\u4e0a\u9a6c\u98de\u9a70\u3002\u96c1\u95e8\u5173\u5916\u8ffd\u5175\u5df2\u5230\uff0c\u4e07\u7bad\u5982\u8757\uff0c\u7eb7\u7eb7\u6512\u5c04\uff0c\u6f6e\u97f3\u5e08\u5f92\u4e09\u4eba\u5404\u5404 \u821e\u52a8\u5175\u5668\uff0c\u62e8\u7bad\u62a4\u8eab\uff0c\u6162\u4e86\u4e0b\u6765\uff0c\u8ffd\u5175\u8d8a\u6765\u8d8a\u8fd1\u3002 \u3000\u3000\u6f6e\u97f3\u548c\u5c1a\u6697\u6697\u53eb\u58f0\u201c\u82e6\u4e5f\uff01\u201d\u51ed\u7740\u81ea\u5df1\u8fd9\u6839\u7985\u4ed7\uff0c\u5728\u5343\u519b\u4e07\u9a6c\u4e4b\u4e2d\uff0c\u867d\u7136\u4e5f\u80fd\u51b2\u6740\u51fa \u4f17\uff0c\u4f46\u62b1\u7740\u4e91\u857e\uff0e\u5374\u662f\u4e0d\u65e0\u987e\u5fcc\u3002\u6b63\u5403\u7d27\u95ee\uff0c\u5ffd\u5730\u55d6\u55d6\u4e24\u58f0\uff0c\u75be\u52b2\u4e4b\u6781\uff0c\u6f6e\u97f3\u548c\u5c1a\u7684\u4e24\u4e2a\u5f92 \u5f1f\uff0c\u7ffb\u4e86\u4e00\u4e2a\u7b4b\u6597\uff0c\u8dcc\u4e0b\u9a6c\u80cc\uff0c\u7ade\u7ed9\u5229\u7bad\u7a7f\u8fc7\u54bd\u5589\uff0c\u6b7b\u4e8e\u975e\u547d\u3002 \u3000\u3000\u6f6e\u97f3\u548c\u5c1a\u72c2\u543c\u4e00\u58f0\uff0c\u62a1\u52a8\u63b8\u6756\uff0c\u7a81\u7136\u62d4\u8f6c\u9a6c\u5934\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u53cd\u6b63\u662f\u6b7b\uff0c\u4e0d\u5982\u6740\u5b83\u51e0 \u4e2a\u3002\u201d\u773c\u775b\u4e00\u77a5\uff0c\u5ffd\u89c1\u4e91\u857e\u90a3\u5bf9\u5706\u6e9c\u6e9c\u7684\u773c\u73e0\uff0c\u597d\u50cf\u5b9a\u4f4d\u4e86\u4e00\u822c\uff0c\u4e5f\u4e2a\u77e5\u662f\u60e7\u6015\u8fd8\u662f\u60f6\u60d1\uff0c \u6f6e\u664b\u548c\u5c1a\u53f9\u4e86\u53e3\u6c14\uff0c\u5ffd\u5730\u53c8\u662f\u4e00\u652f\u51b7\u7bad\u98de\u6765\uff0c\u78b0\u7740\u6756\u5934\uff0c\u94ff\u7136\u58f0\u54cd\uff0c\u663e\u7136\u4e0d\u662f\u5bfb\u5e38\u5eb8\u624b\u6240 \u5c04\u3002 \u3000\u3000\u770b\u770b\u8ffd\u5175\u5df2\u5230\u80cc\u540e\uff0c\u5ffd\u5730\u5b98\u519b\u9635\u5f62\u5927\u4e71\uff0c\u7bad\u96e8\u9aa4\u505c\uff0c\u53ea\u89c1\u961f\u4e2d\u51b2\u51fa\u4e24\u4eba\uff0c\u4e00\u4e2a\u662f\u8c22\u5929 \u534e\uff0c\u53e6\u2014\u4e2a\u5374\u662f\u96c1\u95e8\u5173\u7684\u603b\u5175\u5468\u5065\uff0c\u6f6e\u97f3\u548c\u5c1a\u53c8\u559c\u53c8\u7591\uff0c\u51e0\u4e4e\u4e0d\u6562\u76f8\u4fe1\u81ea\u5df1\u7684\u773c\u775b\uff01 \u3000\u3000\u5b98\u519b\u4e2d\u4e00\u540d\u5c06\u519b\u6325\u5200\u5835\u622a\uff0c\u8c22\u5929\u534e\u624b\u8155\u4e00\u7ffb\uff0c\u4e00\u62db\u201c\u957f\u86c7\u51fa\u6d1e\u201d\uff0c\u75be\u523a\u8fc7\u53bb\uff0c\u90a3\u519b\u5b98\u4e00 \u4e2a\u201c\u956b\u91cc\u85cf\u8eab\u201d\uff0c\u5c45\u7136\u907f\u4e86\u5f00\u53bb\u3002\u8c22\u5929\u534e\u5237\u5237\u5237\u4e00\u8fde\u4e09\u5251\uff0c\u72e0\u75be\u5f02\u5e38\uff0c\u6740\u5f97\u90a3\u519b\u5b98\u624b\u5fd9\u811a \u4e71\uff0c\u5ffd\u542c\u5f97\u540c\u5065\u5927\u58f0\u559d\u9000\uff1a\u201c\u80e1\u5c06\u519b\u6211\u5f85\u4f60\u4e0d\u8584\uff0c\u4eca\u65e5\u6211\u8981\u5411\u4f60\u8ba8\u60c5\u4e86\uff01\u201d\u90a3\u519b\u5b98\u4e00\u58f0\u4e0d \u54cd\uff0c\u7a81\u7136\u6389\u8f6c\u9a6c\u5934\uff0c\u5b98\u4e3e\u4eec\u4f6f\u4f5c\u5450\u558a\u8ffd\u6740\uff0c\u5374\u65e0\u4e00\u4eba\u771f\u4e2a\u62e6\u622a\uff0c\u5468\u5065\u5411\u591a\u5e74\u6765\u540c\u7518\u5171\u82e6\u7684\u90e8 \u4e0b\u626b\u4e86\u4e00\u773c\uff0c\u5ffd\u7136\u6d12\u4e0b\u51e0\u6ef4\u6cea\u73e0\uff0c\u51b2\u51fa\u91cd\u56f4\u4e0e\u6f6e\u97f3\u548c\u5c1a\u4f1a\u5408\uff0c\u8fde\u9a91\u5317\u53bb\u3002 \u3000\u3000\u5317\u56fd\u5bd2\u51ac\uff0c\u5f64\u4e91\u5e03\u7a7a\uff0c\u4e2d\u5348\u65f6\u5206\u3001\u592a\u9633\u8fd8\u672a\u9732\u51fa\u9762\u6765\uff0c\u5929\u8272\u9634\u973e\u4e4b\u6781\u3002\u8c22\u5929\u534e\u7b49\u4e09\u9a91\u5feb \u9a6c\uff0c\u5954\u5165\u4e86\u96c1\u95e8\u5173\u5916\u7684\u65e0\u4eba\u5730\u5e26\u3002\u5468\u5065\u7b56\u9a6c\u5c71\u5934\uff0c\u832b\u7136\u56db\u987e\uff0e\u6f78\u7136\u6cea\u4e0b\u3002\u8c22\u5929\u534e\u5df2\u4ece\u5e08\u5144\u53e3 \u4e2d\uff0c\u77e5\u9053\u4e86\u4e91\u9756\u6298\u65ad\u4f7f\u8282\uff0c\u4ef0\u836f\u81ea\u88c1\u7b49\u7b49\u60c5\u4e8b\uff0c\u77e5\u4ed6\u4f24\u5fc3\u6545\u53cb\uff0c\u6cea\u6d12\u5c71\u5934\uff0c\u53c8\u60f3\u8d77\u4ed6\u4e3a\u4e86\u6551 \u53cb\uff0c\u4e0d\u60dc\u80cc\u53db\u671d\u5ef7\uff0c\u81ea\u6bc1\u524d\u7a0b\uff0c\u751a\u4e3a\u611f\u52a8\uff0c\u4fbf\u4f4e\u58f0\u529d\u9053\uff1a\u201c\u5468\u603b\u5175\uff0c\u4e8b\u5df3\u5982\u65af\uff0c\u53ea\u597d\u5f90\u56fe\u5584 \u540e\u5427\u3002\u53ea\u662f\u7d2f\u4e86\u4f60\u4e86\u3002\u201d\u5468\u5065\u51c4\u7136\u4e00\u7b11\uff0c\u901a\u9053\uff1a\u201c\u6211\u65e9\u5df2\u4e0d\u662f\u603b\u5175\u4e86\u3002\u534a\u6708\u4e4b\u524d\uff0c\u6211\u5df2\u5949\u4ee4 \u8c03\u804c\uff0c\u53ea\u662f\u65b0\u7684\u603b\u5175\u672a\u5230\uff0c\u6240\u4ee5\u6211\u6682\u65f6\u7559\u5728\u5173\u4e2d\u800c\u5df1\u3002\u521a\u624d\u90a3\u4f4d\u80e1\u5c06\u519b\u624d\u662f\u7f72\u7406\u603b\u5175\u3002\u201d \u3000\u3000\u8c22\u5929\u534e\u5fc3\u4e2d\u585e\u6ee1\u7591\u56e2\uff0c\u4e0d\u89c9\u95ee\u9053\uff1a\u201c\u5468\u603b\u5175\u5c61\u5efa\u8fb9\u529f\uff0c\u4f55\u4ee5\u7a81\u7136\u8c03\u804c\uff1f\u4e91\u5927\u4eba\u5b64\u5fe0\u82e6 \u5b88\uff0c\u53c8\u4f55\u4ee5\u7a81\u906d\u8d50\u6b7b\uff1f\u201d\u5468\u5065\u6447\u4e86\u6447\u5934\uff0c\u4ef0\u5929\u957f\u53f9\u9053\uff1a\u201c\u671d\u5ef7\u4e4b\u4e8b\uff0c\u83ab\u95ee\u83ab\u95ee\u3002\u201d\u987f\u4e86\u4e00 \u987f\uff0c\u7ec8\u4e8e\u5fcd\u4e0d\u4f4f\u53c8\u9053\uff1a\u201c\u5978\u5ba6\u5f53\u6743\uff0e\u4eb2\u500d\u662f\u4efb\u3002\u6211\u4e0d\u662f\u738b\u632f\u7684\u4eb2\u4fe1\uff0c\u4ed6\u81ea\u7136\u8981\u8bbe\u6cd5\u628a\u6211\u8c03 \u4e86\u3002\u81f3\u4e8e\u671d\u5ef7\u4e3a\u4f55\u8981\u6740\u4e91\u9756\uff0c\u8fd9\u539f\u56e0\u6211\u4e5f\u767e\u601d\u4e0d\u5f97\u5176\u89e3\u3002\u4e0d\u8fc7\u4eca\u4e0a\u5e74\u5e7c\uff0c\u5927\u6743\u64cd\u5728\u738b\u632f\u624b \u5dfe\uff0c\u8981\u6740\u4e91\u9756\uff0c\u60f3\u5fc5\u4e5f\u662f\u738b\u632f\u7684\u4e3b\u610f\u3002\u201d \u3000\u3000\u8c22\u5927\u534e\u9ed8\u7136\u4e0d\u8bed\uff0c\u60f3\u4e86\u2014\u60f3\uff0c\u5ffd\u7136\u95ee\u9053\uff1a\u201c\u90a3\u74e6\u524c\u56fd\u7684\u5f20\u5b97\u5468\u53ef\u66fe\u548c\u5468\u603b\u5175\u4ea4\u8fc7\u624b \u4e48\uff1f\u201d\u5468\u5065\u9053\uff1a\u201c\u4f60\u662f\u8bf4\u90a3\u4e2a\u5978\u8d3c\u5417\uff1f\u5341\u5e74\u4e4b\u524d\uff0c\u4ed6\u66fe\u7387\u9886\u80e1\u5175\uff0c\u5165\u5bc7\u4e24\u6b21\uff0c\u540e\u6765\u4e24\u8fb9\u8bb2 \u548c\uff0c\u4ed6\u4e5f\u4e0d\u518d\u6765\u4e86\u3002\u201d\u8c22\u5929\u534e\u7d27\u7d27\u95ee\u9053\uff1a\u201c\u4ed6\u5bf9\u4e8e\u6211\u4eec\u671d\u5ef7\u7684\u6d88\u606f\uff0c\u597d\u4f3c\u4e86\u5982\u6307\u638c\uff0c\u83ab\u975e\u4ed6 \u548c\u671d\u4e2d\u5c06\u76f8\uff0c\u4e5f\u6709\u52fe\u7ed3\uff1f\u201d\u5468\u5065\u770b\u4e86\u8c22\u5929\u534e\u4e00\u773c\uff0c\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\uff1f\u4f60\u4e0d\u8bf4\u6211\u4e5f\u5fd8\u4e86\u3002\u738b \u632f\u548c\u74e6\u524c\u7684\u5de6\u4e1e\u76f8\u8131\u6b22\uff0c\u79c1\u4ea4\u751a\u597d\uff0c\u542c\u8bf4\u548c\u5f20\u5b97\u5468\u4e5f\u6709\u5f80\u6765\u3002\u201d\u8c22\u5929\u534e\u5fc3\u7591\u66f4\u751a\uff0c\u638f\u51fa\u8721 \u4e38\uff0c\u4e00\u53e3\u54ac\u7834\uff0c\u62c9\u51fa\u5b57\u6761\uff0c\u4e0e\u5468\u952e\u540c\u770b\uff0c\u7adf\u662f\u738b\u632f\u7684\u5b57\u8ff9\uff0c\u5199\u4e0e\u8131\u6b22\u3001\u5f20\u5b97\u5468\u4e8c\u4eba\uff0c\u5546\u91cf\u4ee5 \u4e2d\u56fd\u7684\u94c1\u5668\u6362\u53d6\u8499\u53e4\u7684\u540d\u9a6c\u7684\u3002\u8c22\u5929\u534e\u53f9\u9053\uff1a\u201c\u8499\u53e4\u7f3a\u94c1\uff0c\u82e5\u65e0\u4e2d\u56fd\u826f\u94c1\uff0c\u4ed6\u4eec\u8fde\u5229\u7bad\u90fd\u4e0d \u80fd\u9020\uff0c\u8fd9\u4e0d\u662f\u516c\u7136\u8d44\u654c\u4e48\uff1f\u201d\u5468\u5065\u9053\uff1a\u201c\u6211\u8fd8\u5fd8\u4e86\u4e00\u4e8b\uff0c\u90a3\u4e24\u4e2a\u94a6\u5dee\u4e09\u5929\u4e4b\u524d\u5df2\u7ecf\u6765\u4e86\uff0c\u8499 \u5360\u8fd8\u6709\u4f7f\u8005\u4e0e\u4ed6\u4eec\u89c1\u9762\u3002\u6211\u6781\u6000\u7591\u6697\u5bb3\u4e91\u9756\u4e4b\u4e8b\uff0c\u4e5f\u662f\u8131\u6b22\u6216\u8005\u5f20\u5b8b\u5468\u7684\u4e3b\u610f\u3002\u201d\u8c22\u5929\u534e \u9053\uff1a\u201c\u90a3\u4e48\u6fb9\u53f0\u706d\u660e\u5949\u5f20\u672b\u5468\u4e4b\u547d\u9001\u6765\u8fd9\u4e2a\u8721\u4e5d\uff0c\u53c8\u662f\u4f55\u610f\uff1f\u201d\u9042\u5c06\u524d\u4e8b\u8bf4\u4e0e\u5468\u5065\u77e5\u9053\uff0c\u4e24 \u4eba\u518d\u4e09\u63a8\u6d4b\uff0c\u5747\u662f\u4e0d\u89e3\u3002\u5468\u5065\u9053\uff1a\u201c\u5f20\u5b97\u5468\u8fd9\u53ae\u8fd8\u4f1a\u6709\u4ec0\u4e48\u597d\u5fc3\uff0c\u53ea\u51ed\u4ed6\u5974\u5f79\u4e91\u9756\u4e8c\u5341\u5e74\u8fd9 \u70b9\uff0e\u6211\u5c31\u6068\u4e2a\u5f97\u628a\u4ed6\u6740\u6389\uff01\u201d \u3000\u3000\u4e91\u857e\u62a2\u8d77\u5c0f\u8138\uff0c\u9053\uff1a\u201c\u7237\u7237\u5462\uff1f\u7237\u7237\u53eb\u6211\u6740\u4eba\uff0c\u4f60\u4eec\u4e5f\u8981\u6740\u4eba\u3002\u6211\u6015\u5440\uff0c\u6211\u6015\uff01\u201d\u8c22\u5929 \u534e\u8f7b\u629a\u5979\u7684\u5934\u53d1\uff0c\u4f4e\u5362\u8bf4\u9053\uff1a\u201c\u6740\u574f\u4eba\u6ca1\u6709\u4ec0\u4e48\u53ef\u6015\u7684\u3002\u201d\u5ffd\u5730\u8df3\u4e0b\u9a6c\u6765\uff0c\u5bf9\u6f6e\u97f3\u548c\u5c1a\u8bf4 \u9053\uff1a\u201c\u4f60\u5c06\u8fd9\u4e2a\u5973\u5a03\u4ea4\u7ed9\u56db\u59b9\uff0c\u6211\u518d\u5230\u8499\u53e4\u53bb\u3002\u201d\u6f6e\u97f3\u9053\uff1a\u201c\u53bb\u505a\u4ec0\u4e48\uff1f\u201d\u8c22\u5929\u534e\u9053\uff1a\u201c\u6740 \u5f20\u5b97\u5468\uff01\u201d\u6f6e\u97f3\u4e00\u987f\u7985\u6756\uff0c\u8bf4\u9053\uff1a\u201c\u6b63\u8be5\u5982\u6b64\uff0c\u4f60\u6740\u4e86\u5f20\u5b97\u5468\uff0c\u5c31\u4e0d\u5fc5\u8fd9\u5973\u5a03\u513f\u4ed6\u65e5\u6740\u4eba \u4e86\u3002\u597d\uff0c\u54b1\u4eec\u4e00\u4e2a\u629a\u5b64\uff0c\u4e00\u4e2a\u62a5\u4ec7\uff0c\u5341\u5e74\u4e4b\u540e\uff0c\u518d\u5230\u96c1\u95e8\u5173\u76f8\u89c1\uff01\u201d\u8fd9\u4e00\u53bb\u4e5f\uff0c\u6709\u5206\u6559\uff1a \u3000\u3000\u7591\u5e55\u91cd\u91cd\u7ec8\u63ed\u7834\uff0c\u5947\u7537\u4fa0\u5973\u95f9\u6c5f\u6e56\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u770b\u4e0b\u6587\u5206\u89e3\u3002 \u3000\u3000---------------------------- \u3000\u3000brillt \u91cd\u6821\uff0c\u65e7\u96e8\u697c \u72ec\u5bb6\u66f4\u65b0  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u840d\u8e2a\u4fa0\u5f71\u5f55\u300b\u2014\u2014\u7b2c\u4e00\u56de\u3000\u5f39\u6307\u65ad\u5f26\u5f3a\u4eba\u52a8\u519b\u9977\u3000\u98de\u82b1\u6251\u8776\u7389\u5973\u663e\u795e\u901a \u6881\u7fbd\u751f\u300a\u840d\u8e2a\u4fa0\u5f71\u5f55\u300b \u7b2c\u4e00\u56de\u3000\u5f39\u6307\u65ad\u5f26\u5f3a\u4eba\u52a8\u519b\u9977\u3000\u98de\u82b1\u6251\u8776\u7389\u5973\u663e\u795e\u901a \u3000\u3000\u65f6\u5149\u6d41\u77e2\uff0c\u8f6c\u77ac\u8fc7\u4e86\u5341\u5e74\uff0c\u8fd9\u4e00\u5e74\u5df2\u662f\u660e\u6b63\u7edf\u5341\u4e09\u5e74\u4e86\u3002 \u3000\u3000\u5341\u5e74\u4eba\u4e8b\u51e0\u756a\u65b0\u3002\u96c1\u95e8\u5173\u5916\u767e\u91cc\u4e4b\u5730\u867d\u4ecd\u662f\u80e1\u9a6c\u5636\u9e23\uff0c\u5341\u5e74\u524d\u9547\u5b88\u8fb9\u5173\u7684\u603b\u5175\u5468\u5065\uff0c\u5df2 \u6e10\u6e10\u4e3a\u4eba\u5fd8\u8bb0\uff0c\u800c\u90a3\u4e2a\u5f02\u57df\u5f52\u6765\u7684\u5c48\u6b7b\u8fb9\u5173\u7684\u4f7f\u81e3\u4e91\u9756\uff0c\u66f4\u6ca1\u4eba\u77e5\u9053\u4ed6\u7684\u4e8b\u8ff9\u4e86\u3002 \u3000\u3000\u53ea\u662f\u8fd9\u51e0\u5e74\u6765\uff0c\u5728\u96c1\u95e8\u5173\u5916\uff0c\u5374\u6709\u4e00\u80a1\u7eff\u6797\uff0c\u95f9\u5f97\u8f70\u8f70\u70c8\u70c8\u3002\u8fd9\u4e00\u80a1\u7eff\u6797\uff0c\u5341\u5206\u7279\u522b\uff0c \u4ed6\u4eec\u5c31\u76d8\u8ddd\u5728\u96c1\u95e8\u5173\u5916\u90a3\u65b9\u5706\u767e\u91cc\u4e4b\u5730\u7684\u201c\u65e0\u4eba\u5730\u5e26\u201d\u4e4b\u95f4\uff0c\u4ed6\u4eec\u65e2\u6297\u80e1\u5bc7\uff0c\u53c8\u6297\u660e\u5175\uff0c\u4eba \u6570\u867d\u7136\u4e0d\u591a\uff0c\u5374\u9690\u9690\u6210\u4e86\u660e\u671d\u4e0e\u74e6\u523a\u201c\u4e24\u5927\u201d\u4e4b\u95f4\u7684\u4e00\u4e2a\u201c\u7f13\u51b2\u529b\u91cf\u201d\uff0c\u660e\u671d\u4e0e\u74e6\u523a\u90fd\u4e0d\u6562 \u8fdb\u53bb\u8ffd\u6355\u3002\u4ed6\u4eec\u7684\u4f5c\u98ce\u4e5f\u5f88\u7279\u522b\uff0c\u5e76\u4e0d\u4ee5\u6253\u5bb6\u52ab\u820d\u62a2\u63a0\u884c\u65c5\u4e3a\u751f\uff0c\u5374\u662f\u5728\u90a3\u201c\u65e0\u4eba\u5730\u5e26\u201d\u4e4b \u4e2d\uff0c\u5f00\u8352\u57a6\u6b96\u3002\u4ed6\u4eec\u6709\u65f6\u4e5f\u4e0b\u5c71\u62a2\u63a0\uff0c\u6240\u62a2\u7684\u5374\u5927\u90fd\u662f\u8d2a\u5b98\u6c61\u540f\u7684\u4e0d\u4e49\u4e4b\u8d22\u3002\u8fd9\u80a1\u7eff\u6797\uff0c\u4ee5 \u65e5\u6708\u53cc\u65d7\u4e3a\u8bb0\uff0c\u76d7\u515a\u7684\u9996\u9886\u636e\u8bf4\u662f\u4e00\u4e2a\u8c79\u5934\u864e\u76ee\u7684\u8001\u8005\uff0c\u4f46\u5916\u95f4\u5374\u65e0\u4eba\u77e5\u9053\u4ed6\u7684\u540d\u5b57\u3002\u4ed6\u548c \u5b98\u519b\u5bf9\u654c\u4e4b\u65f6\uff0c\u6bcf\u6b21\u90fd\u662f\u6234\u7740\u9762\u5177\uff0c\u56e0\u4ed6\u624b\u4f7f\u91d1\u5200\uff0c\u6240\u4ee5\u5b98\u519b\u6863\u6848\u4e4b\u4e2d\uff0c\u4fbf\u79f0\u4ed6\u4e3a\u201c\u91d1\u5200\u8001 \u8d3c\u201d\u3002\u8fd9\u201c\u91d1\u5200\u8001\u8d3c\u201d\u8fd8\u6709\u4e00\u6837\u5947\u602a\u4e4b\u5904\uff0c\u4ed6\u867d\u7136\u4e5f\u4e0e\u5b98\u519b\u4e3a\u654c\uff0c\u4f46\u5374\u4ece\u6765\u4e0d\u52ab\u990d\u95e8\u5173\u7684\u519b \u9977\uff0c\u800c\u4e14\u6bcf\u6b21\u4e0e\u5b98\u519b\u4f5c\u6218\uff0c\u7eb5\u7136\u6253\u80dc\u4e5f\u4ece\u4e0d\u8ffd\u6740\u3002 \u3000\u3000\u8fd9\u4e00\u5e74\u66ae\u6625\u65f6\u8282\uff0c\u5175\u90e8\u53c8\u6d3e\u9063\u5b98\u5175\u62bc\u89e3\u6765\u4e00\u6279\u519b\u9977\uff0c\u62bc\u89e3\u7684\u519b\u5b98\u53eb\u505a\u65b9\u5e86\uff0c\u6b66\u4e3e\u51fa\u8eab\uff0c \u5bb6\u4f20\u5f13\u9a6c\uff0c\u6b66\u6280\u5a34\u719f\uff0c\u81ea\u79f0\u201c\u795e\u7bad\u65b9\u5e86\u201d\uff0c\u751a\u4e3a\u81ea\u8d1f\u3002\u8fd9\u4e00\u6b21\u62bc\u89e3\u7684\u519b\u9977\u662f\u56db\u5341\u4e07\u4e24\u94f6\u5b50\uff0c \u519b\u9977\u6ee1\u662f\u88c5\u597d\u4e86\u94f6\u9798\u7684\u5143\u5b9d\uff0c\u6bcf\u9798\u4e94\u767e\u4e24\uff0c\u7528\u4e00\u767e\u5339\u5065\u9aa1\u9a6e\u80cc\u3002\u53e6\u6709\u5341\u5339\u5065\u9aa1\uff0c\u88c5\u7684\u662f\u96c1\u95e8 \u5173\u73b0\u4efb\u603b\u5175\u4e01\u5927\u53ef\u79c1\u8fd0\u7684\u8d27\u7269\u3002\u62bc\u89e3\u7684\u5175\u4e01\u53ea\u6709\u4e00\u767e\u4eba\uff0c\u8fd9\u4e5f\u662f\u56e0\u4e3a\u5386\u5e74\u6765\u4ece\u672a\u5931\u8fc7\u4e8b\u7684\u7f18 \u6545\u3002 \u3000\u3000\u66ae\u6625\u4e09\u6708\uff0c\u6b63\u662f\u6c5f\u5357\u8349\u957f\uff0c\u7fa4\u83ba\u4e71\u98de\u7684\u5b63\u8282\uff0c\u5728\u96c1\u95e8\u5173\u5916\u5374\u8fd8\u662f\u79ef\u96ea\u672a\u5316\uff0c\u6625\u5bd2\u6599\u5ced\uff0c \u4f46\u867d\u7136\u5982\u6b64\uff0c\u5b98\u519b\u4eec\u9014\u8dcb\u6d89\uff0c\u4e5f\u611f\u5230\u6709\u4e9b\u71e0\u70ed\u3002\u8fd9\u65f6\u5df2\u662f\u5348\u540e\u65f6\u5206\uff0c\u9633\u5149\u666e\u7167\uff0c\u65b9\u5e86\u5728\u9a6c\u80cc \u4e0a\u626c\u97ad\u6307\u9053\uff1a\u201c\u660e\u65e5\u4e2d\u5348\uff0c\u4fbf\u53ef\u4ee5\u8d76\u5230\u96c1\u95e8\u5173\u4e86\u3002\u8fd9\u6b21\u6211\u4eec\u53ea\u7387\u9886\u4e00\u767e\u7cbe\u9a91\uff0c\u89e3\u8fd0\u91cd\u9977\uff0c\u7a7f \u5c71\u8d8a\u5cad\uff0c\u5343\u91cc\u8fe2\u8fe2\uff0c\u5dee\u5e78\u65e0\u4e8b\uff0c\u771f\u771f\u662f\u53ef\u5e86\u5440\uff01\u201d\u540c\u884c\u62bc\u8fd0\u7684\u4e24\u4e2a\u526f\u5b98\u963f\u5e9a\u5949\u627f\uff0c\u62a2\u7740\u8bf4\uff1a \u201c\u65b9\u5927\u4eba\u795e\u7bad\u795e\u5a01\uff0c\u5929\u4e0b\u8c01\u4e0d\u77e5\u9053\uff1f\u8def\u4e0a\u7eb5\u6709\u4e00\u4e9b\u6bdb\u8d3c\uff0c\u542c\u5f97\u662f\u5927\u4eba\u62bc\u8fd0\uff0c\u4e5f\u4e0d\u6562\u6b63\u773c\u76f8\u89d1 \u4e86\uff01\u201d\u65b9\u5e86\u54c8\u54c8\u5927\u7b11\uff0c\u8fde\u8bf4\u9053\uff1a\u201c\u597d\u8bf4\uff0c\u597d\u8bf4\uff01\u201d\u5b98\u519b\u4eec\u542c\u4e86\uff0c\u90fd\u6697\u6697\u597d\u7b11\u3002 \u3000\u3000\u9a7f\u9053\u65c1\u8fb9\uff0c\u6b63\u6709\u4e00\u4e2a\u9152\u8086\uff0c\u90a3\u662f\u4f9b\u884c\u65c5\u5ba2\u5546\uff0c\u6b47\u606f\u559d\u9152\u7684\u5730\u65b9\u3002\u65b9\u5e86\u4e00\u9ad8\u5174\u4fbf\u9053\uff1a\u201c\u8fd9 \u6b21\u5e73\u5b89\u65e0\u4e8b\uff0c\u4e5f\u4e0d\u5168\u662f\u6211\u4e00\u4eba\u4e4b\u529b\uff0c\u5927\u5bb6\u90fd\u6709\u529f\u52b3\u3002\u96c1\u95e8\u5173\u5df2\u8fd1\uff0c\u4e0d\u5fc5\u6025\u6025\u8d76\u8def\u4e86\uff0c\u5927\u5bb6\u5c31 \u5728\u8def\u8fb9\u6b47\u6b47\u5427\u3002\u6211\u8bf7\u4e24\u4f4d\u526f\u5b98\u559d\u4e00\u676f\u9152\u3002\u201d\u8df3\u4e0b\u9a6c\u80cc\uff0c\u8fdb\u5165\u9152\u8086\u4e2d\uff0c\u4e24\u4e2a\u526f\u5b98\u4ea6\u6b65\u4ea6\u8d8b\u3002\u65b9 \u5e86\u559d\u4e86\u51e0\u676f\u9152\u540e\uff0c\u610f\u6001\u66f4\u8c6a\uff0c\u6ed4\u6ed4\u4e0d\u7edd\u5730\u5938\u8bf4\u4ed6\u7684\u6b66\u529f\uff0c\u8bf4\u4ed6\u4ee5\u524d\u5728\u4e1c\u5e73\u5e9c\u5f53\u6355\u5934\u7684\u65f6\u5019\uff0c \u600e\u6837\u4ed7\u7740\u4e00\u628a\u795e\u5f13\uff0c\u5c31\u6536\u670d\u4e86\u7fa4\u76d7\u3002 \u3000\u3000\u65b9\u5e86\u6ed4\u6ed4\u4e0d\u7edd\u5730\u81ea\u5938\u6b66\u827a\uff0c\u4e24\u4f4d\u526f\u5b98\uff0c\u5c82\u6709\u4e0d\u8d81\u52bf\u5949\u627f\u4e4b\u7406\uff0c\u6709\u4e00\u4e2a\u9053\uff1a\u201c\u53ef\u60dc\u5927\u4eba\u804c \u5b88\u5728\u8eab\uff0c\u8981\u4e0d\u7136\u4eca\u5e74\u7684\u5f00\u79d1\u6bd4\u6b66\u8ba9\u65b9\u5927\u4eba\u53bb\uff0c\u4e00\u5b9a\u53ef\u4ee5\u628a\u6b66\u72b6\u5143\u62a2\u5230\u624b\u4e2d\u3002\u201d\u53c8\u4e00\u4e2a\u9053\uff1a \u201c\u4eca\u65e5\u5929\u6717\u6c14\u6e05\uff0c\u5351\u804c\u80c6\u6562\u8bf7\u5927\u4eba\u6f14\u6f14\u795e\u7bad\u4e4b\u6280\uff0c\u53eb\u6211\u4eec\u5f00\u5f00\u773c\u754c\u5427\u3002\u201d\u65b9\u5e86\u559d\u4e86\u4e00\u5927\u676f \u9152\uff0c\u54c8\u54c8\u5927\u7b11\uff0c\u53d6\u4e0b\u80cc\u4e0a\u7684\u94c1\u80ce\u5f13\uff0c\u8a00\u9053\uff1a\u201c\u90fd\u968f\u6211\u6765\uff01\u201d\u8d70\u51fa\u9152\u8086\uff0c\u62d4\u51fa\u4e24\u679d\u7fbd\u7bad\uff0c\u9053\uff1a \u201c\u770b\u6e05\u695a\u4e86\uff01\u201d\u55d6\u7684\u4e00\u7bad\u5c04\u4e0a\u5929\u7a7a\uff0c\u5c31\u5728\u8fd9\u4e00\u679d\u7bad\u6389\u5934\u4e0b\u843d\u4e4b\u9645\uff0c\u7b2c\u4e8c\u679d\u7bad\u53c8\u55d6\u5730\u4e00\u58f0\u5c04\u4e86 \u4e0a\u53bb\uff0c\u4e24\u679d\u7fbd\u7bad\u7adf\u7136\u5728\u534a\u7a7a\u4e2d\u649e\u4e2a\u6b63\u7740\uff0c\u4e24\u8fb9\u98de\u5f00\uff0c\u4e00\u9f50\u843d\u5730\u3002\u4e24\u4e2a\u526f\u5b98\u56fa\u7136\u662f\u5927\u58f0\u6b22\u547c\uff0c \u4f17\u5b98\u5175\u770b\u4e86\u4e5f\u90fd\u6697\u6697\u8bf4\u9053\uff1a\u201c\u679c\u7136\u6709\u4e24\u4e0b\u5b50\uff0c\u5e76\u4e0d\u662f\u80e1\u4e71\u5439\u725b\u3002\u201d \u3000\u3000\u6b22\u547c\u58f0\u4e2d\uff0c\u53ea\u542c\u5f97\u8e44\u58f0\u5f97\u5f97\uff0c\u9a7f\u9053\u4e0a\u4e00\u9a91\u9a6c\u9a70\u6765\uff0c\u9a6c\u4e0a\u4eba\u4e5f\u9ad8\u58f0\u8d5e\u9053\uff1a\u201c\u597d\u7bad\uff0c\u597d \u7bad\uff01\u201d\u65b9\u5e86\u4e00\u770b\uff0c\u5374\u662f\u4e00\u4e2a\u79c0\u624d\u6a21\u6837\u7684\u4eba\uff0c\u5934\u6234\u9752\u5dfe\uff0c\u76f8\u8c8c\u65af\u6587\uff0c\u80cc\u4e0a\u5374\u4e5f\u80cc\u7740\u4e00\u628a\u9ed1\u5f13\uff0c \u53ea\u662f\u90a3\u5339\u9a6c\u65e2\u5f88\u7626\u5c0f\uff0c\u90a3\u628a\u5f13\u4e5f\u6bd4\u5bfb\u5e38\u7684\u94c1\u80ce\u5f13\u5c0f\u5f97\u591a\uff0c\u4e0e\u65b9\u5e86\u90a3\u628a\u5927\u5f13\uff0c\u5dee\u5f97\u66f4\u8fdc\u3002\u65b9\u5e86 \u5fc3\u4e2d\u6697\u7b11\uff1a\u8fd9\u4e66\u751f\u5927\u7ea6\u662f\u6015\u9053\u8def\u4e0d\u9756\uff0c\u80cc\u628a\u5f13\u58ee\u58ee\u80c6\u5b50\u3002\u5176\u5b9e\u8fd9\u6837\u4e0d\u663e\u773c\u7684\u5f13\u7bad\uff0c\u4f60\u4e0d\u80cc\u4e5f \u8fd8\u7f62\u4e86\u3002\u82e5\u7136\u771f\u6709\u5f3a\u76d7\u884c\u52ab\uff0c\u4e00\u770b\u5c31\u77e5\u4f60\u662f\u4e2a\u5b71\u5f31\u4e66\u751f\u3002 \u3000\u3000\u90a3\u79c0\u624d\u6a21\u6837\u7684\u4eba\uff0c\u5c06\u9a6c\u7cfb\u5728\u8def\u8fb9\u6811\u4e0a\uff0c\u4e5f\u8e0f\u5165\u9152\u8086\u3002\u65b9\u5e86\u6599\u4ed6\u4e5f\u662f\u4e2a\u6709\u529f\u540d\u7684\u4eba\uff0c\u4fbf\u4e3e \u624b\u4e3a\u793c\uff0c\u95ee\u9053\uff1a\u201c\u5144\u53f0\u8d35\u59d3\uff0c\u4f55\u4ee5\u5355\u9a91\u884c\u8d70\uff0c\u4e0d\u6015\u76d7\u8d3c\u4e48\uff1f\u201d\u90a3\u79c0\u624d\u9053\uff1a\u201c\u5c0f\u5f1f\u59d3\u5b5f\uff0c\u5355\u540d \u4e00\u4e2a\u7391\u5b57\uff0c\u5bb6\u4e61\u6559\u9986\u7cca\u53e3\uff0c\u662f\u4ee5\u8fdc\u6765\u5173\u5916\uff0c\u5e0c\u671b\u655d\u4eb2\u7167\u987e\uff0c\u5728\u5e55\u4e2d\u5bfb\u4e2a\u5c0f\u5c0f\u7684\u5dee\u4e8b\u3002\u201d\u65b9\u5e86 \u5fc3\u9053\uff1a\u201c\u539f\u6765\u662f\u4e2a\u6765\u627e\u5dee\u4e8b\u6253\u79cb\u98ce\u7684\u7a77\u79c0\u624d\u3002\u201d\u4fbf\u9053\uff1a\u201c\u8fd9\u597d\u6781\u4e86\uff0c\u8d35\u4eb2\u4e01\u603b\u5175\u6b63\u662f\u6211\u4eec\u5175 \u90e8\u5c1a\u4e66\u7684\u513f\u5973\u4eb2\u5bb6\uff0c\u8fd9\u6b21\u6211\u62bc\u8fd0\u519b\u9977\uff0c\u4e5f\u66ff\u4e01\u603b\u5175\u634e\u5e26\u4e86\u4e00\u4e9b\u4e1c\u897f\u53bb\u3002\u201d\u90a3\u81ea\u79f0\u5b5f\u7391\u7684\u79c0\u624d \u9053\uff1a\u201c\u6211\u8fd9\u56de\u53ef\u771f\u662f\u8def\u9047\u8d35\u4eba\u4e86\u3002\u6211\u542c\u8bf4\u8fd9\u4e00\u5e26\u6709\u5f3a\u4eba\u4e3a\u60a3\uff0c\u6b63\u81ea\u5bb3\u6015\uff0c\u6211\u3001\u6211\u2026\u2026\u201d\u65b9\u5e86 \u65e9\u77e5\u5176\u610f\uff0c\u4e5f\u662f\u6709\u4e86\u51e0\u5206\u9152\u610f\uff0c\u4fbf\u62cd\u62cd\u80f8\u53e3\uff0c\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u5144\u53f0\u78b0\u7740\u4e86\u6211\uff0c\u4f55\u7528\u60e7\u6015\u3002\u6211\u4ed7 \u7740\u8fd9\u628a\u795e\u5f13\uff0c\u4e00\u8def\u8fdc\u6765\uff0c\u6bdb\u8d3c\u90fd\u671b\u98ce\u800c\u907f\uff0c\u5144\u53f0\u65e2\u7136\u662f\u5230\u96c1\u95e8\u5173\u6295\u4eb2\uff0c\u5927\u5bb6\u90fd\u662f\u4e00\u4f19\uff0c\u4f60\u968f \u6211\u540c\u884c\u597d\u4e86\uff01\u201d\u90a3\u79c0\u624d\u542c\u4e86\uff0c\u9762\u9732\u559c\u8272\uff0c\u518d\u4e09\u9053\u8c22\uff0c\u5f20\u7740\u773c\u775b\uff0c\u4e0d\u505c\u5730\u770b\u4ed6\u90a3\u628a\u94c1\u80ce\u5f13\u3002\u65b9 \u5e86\u53c8\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u8fd9\u628a\u5f13\u662f\u7279\u522b\u6253\u9020\uff0c\u52a0\u5927\u7684\u94c1\u5f13\uff0c\u4e24\u81c2\u975e\u6709\u4e94\u767e\u65a4\u529b\u6c14\uff0c\u4f11\u60f3\u5f00\u5f97\uff01\u201d\u5b5f \u7391\u8fde\u58f0\u9053\uff1a\u201c\u4f69\u670d\uff01\u4f69\u670d\uff01\u201d \u3000\u3000\u65b9\u5e86\u5174\u8d77\uff0c\u53c8\u62c9\u5b5f\u7391\u518d\u559d\u4e86\u51e0\u5927\u676f\u9152\uff0c\u51fa\u4e86\u9152\u8086\uff0c\u62d4\u961f\u8d77\u884c\uff0c\u5bd2\u98ce\u4e00\u5439\uff0c\u9152\u610f\u66f4\u751a\u3002\u8d70 \u4e86\u4e00\u7a0b\uff0c\u9a7f\u9053\u508d\u5c71\u800c\u884c\uff0c\u5230\u4e86\u7d20\u79f0\u9669\u5cfb\u7684\u897f\u7559\u5c71\u53e3\uff0c\u5c71\u4e0a\u733f\u557c\u96c1\u98de\uff0c\u89c1\u5927\u961f\u4eba\u6765\uff0c\u9e1f\u98de\u733f \u8d70\u3002\u5b5f\u7391\u8bf4\u9053\uff1a\u201c\u8fd9\u91cc\u5730\u5f62\u9669\u5cfb\uff0c\u53ea\u6015\u5f3a\u4eba\u51fa\u6ca1\u3002\u201d\u65b9\u5e86\u5927\u7b11\u9053\uff1a\u201c\u82e5\u6709\u5f3a\u4eba\u51fa\u6765\uff0c\u90a3\u4fbf\u662f \u4ed6\u4eec\u81ea\u5bfb\u6b7b\u8def\u4e86\uff01\u201d\u5b5f\u7391\u7a81\u7136\u628a\u80cc\u4e0a\u7684\u90a3\u628a\u5f13\u53d6\u5728\u624b\u4e2d\uff0c\u9762\u6709\u5f02\u8272\u3002 \u3000\u3000\u65b9\u5e86\u7b11\u9053\uff1a\u201c\u5144\u53f0\u60e7\u6015\u4e48\uff1f\u201d\u5b5f\u7391\u7b11\u9053\uff1a\u201c\u6211\u771f\u662f\u6709\u4e9b\u80c6\u5bd2\uff0c\u4e0d\u77e5\u4e0d\u89c9\u53d6\u4e86\u5f13\u7bad\uff0c\u51c6\u5907 \u9632\u8eab\u3002\u8fd9\u65e0\u804a\u4e4b\u4e3e\uff0c\u6559\u5927\u4eba\u89c1\u7b11\u4e86\u3002\u201d\u65b9\u5e86\u679c\u7136\u54c8\u54c8\u5927\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u5fd8\u8bb0\u662f\u548c\u6211\u4eec\u540c\u884c \u4e86\u3002\u54c8\u54c8\uff0c\u82e5\u7136\u771f\u6709\u5f3a\u4eba\uff0c\u4f60\u8fd9\u628a\u5f13\u53c8\u6d4e\u5f97\u751a\u4e8b\uff1f\u201d\u8d81\u7740\u9152\u610f\uff0c\u4f38\u624b\u8bf4\u9053\uff1a\u201c\u628a\u4f60\u8fd9\u5c0f\u73a9\u610f \u513f\u4e0e\u6211\u77a7\u77a7\uff01\u201d\u5b5f\u7391\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6559\u5927\u4eba\u89c1\u7b11\u3002\u201d\u5374\u4e5f\u5e76\u4e0d\u63a8\u8f9e\uff0c\u5c06\u90a3\u628a\u5f13\u9012\u4e86\u7ed9\u4ed6\u3002 \u3000\u3000\u65b9\u5e86\u63a5\u8fc7\u90a3\u628a\u6f06\u5f97\u9ed1\u9edd\u9edd\u7684\u5f13\uff0c\u53ea\u89c9\u751a\u4e3a\u6c89\u91cd\uff0c\u4e0d\u7531\u5f97\u5403\u4e86\u4e00\u60ca\uff0c\u5583\u5583\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u4ec0 \u4e48\u505a\u7684\uff1f\u201d\u7528\u529b\u4e00\u62c9\uff0c\u7adf\u7136\u62c9\u5b83\u4e0d\u52a8\u3002\u987b\u77e5\u65b9\u5e86\u62c9\u60ef\u5f3a\u5f13\uff0c\u4e24\u81c2\u5b9e\u6709\u4e94\u767e\u65a4\u529b\u91cf\uff0c\u8fd9\u4e00\u62c9\u4e0d \u52a8\u4e0d\u7531\u5f97\u6ee1\u9762\u901a\u7ea2\uff0c\u53c8\u60ca\u53c8\u6127\uff0c\u9152\u610f\u4e5f\u9192\u4e86\u51e0\u5206\uff0c\u8bb7\u8bb7\u5730\u8bf4\u9053\uff1a\u201c\u4f60\u3001\u4f60\uff0d\uff0d\u201d\u5b5f\u7391\u987a\u624b\u53d6 \u56de\u9ed1\u5f13\u4e00\u7b11\u8bf4\u9053\uff1a\u201c\u5927\u4eba\u60f3\u662f\u591a\u559d\u4e86\u9152\uff0c\u6240\u4ee5\u6c14\u529b\u7528\u4e0d\u51fa\u6765\u3002\u5c0f\u5f1f\u6597\u80c6\uff0c\u4e5f\u8bf7\u5927\u4eba\u8d50\u5b9d\u5f13\u4e00 \u89c2\u5982\u4f55\uff1f\u201d\u65b9\u5e86\u60ca\u7591\u4e4b\u6781\uff0c\u628a\u90a3\u628a\u7279\u5236\u52a0\u5927\u7684\u4e94\u77f3\u94c1\u5f13\u9012\u4e86\u8fc7\u53bb\u3002\u53ea\u89c1\u90a3\u79c0\u624d\u5de6\u624b\u5982\u6258\u6cf0 \u5c71\uff0c\u53f3\u624b\u5982\u62b1\u5a74\u513f\uff0c\u53ea\u4e00\u62c9\u5c31\u628a\u90a3\u94c1\u80ce\u5f13\u62c9\u5f97\u5f13\u5982\u6ee1\u6708\uff0c\u53e3\u4e2d\u8d5e\u9053\uff1a\u201c\u679c\u7136\u597d\u5f13\uff01\u201d\u624b\u8155\u4e00 \u6c89\uff0c\u53ea\u542c\u5f97\u25a1\u556a\u4e00\u58f0\uff0c\u5f13\u5f26\u65ad\u4e3a\u4e24\u6bb5\u3002 \u3000\u3000\u65b9\u5e86\u8fd9\u65f6\u9152\u610f\u5168\u6d88\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u4f60\u662f\u4f55\u4eba\uff1f\u201d\u90a3\u4e66\u751f\u63b7\u5f13\u4e8e\u5730\uff0c\u4ef0\u5929\u5927\u7b11\uff0c\u7a81\u7136\u4e00\u653e \u25a1\u7ef3\uff0c\u90a3\u5339\u7626\u9a6c\u7adf\u7136\u8dd1\u5f97\u5feb\u75be\u4e4b\u6781\uff0c\u7edd\u5c18\u800c\u53bb\u3002\u65b9\u5e86\u5927\u53eb\u201c\u653e\u7bad\uff01\u201d\u54ea\u6765\u5f97\u53ca\u3002\u9661\u7136\u95f4\u53ea\u542c \u5f97\u5431\u5431\u8fde\u58f0\u54cd\u8d77\u547c\u54e8\uff0c\u5c71\u5761\u4e71\u8349\u4e4b\u4e2d\uff0c\u5230\u5904\u7a9c\u51fa\u5f3a\u4eba\u3002\u90a3\u5b5f\u7391\u62d4\u8f6c\u9a6c\u5934\uff0c\u5728\u9a6c\u80cc\u4e0a\u5927\u7b11\u9053\uff1a \u201c\u795e\u5f13\u5999\u6280\uff0c\u4e0d\u8fc7\u5982\u6b64\uff01\u54b1\u4eec\u4fbf\u662f\u8981\u52ab\u4f60\u94f6\u4e24\u7684\u5f3a\u4eba\uff0c\u4f60\u8fd8\u8981\u4e0e\u6211\u8f83\u91cf\u8f83\u91cf\u4e48\uff1f\u201d \u3000\u3000\u65b9\u5e86\u867d\u5df2\u62fe\u53d6\u94c1\u5f13\uff0c\u4f46\u5f13\u5f26\u5df2\u65ad\uff0c\u65e0\u53ef\u62b5\u654c\uff0c\u5140\u81ea\u9ad8\u58f0\u5406\u559d\uff0c\u538b\u7740\u9635\u811a\uff0c\u72b9\u56fe\u987d\u6297\u3002\u53ea \u542c\u5f97\u72c2\u7b11\u58f0\u4e2d\uff0c\u5f13\u5f26\u4e00\u54cd\uff0c\u90a3\u5b5f\u7391\u53eb\u9053\uff1a\u201c\u53eb\u4f60\u4eec\u77e5\u9053\u5389\u5bb3\uff01\u201d\u5f13\u5982\u6ee1\u6708\uff0c\u7bad\u4f3c\u6d41\u661f\uff0c\u547c\u5578 \u58f0\u4e2d\uff0c\u524d\u884c\u7684\u4e00\u540d\u526f\u5c06\u60e8\u53eb\u4e00\u58f0\uff0c\u88ab\u5229\u7bad\u7a7f\u8fc7\u54bd\u5589\uff0c\u5012\u6bd9\u9a6c\u4e0b\u3002\u5b5f\u7391\u53c8\u662f\u4e00\u58f0\u957f\u5578\uff0c\u5f13\u5f26\u518d \u54cd\uff0c\u7b2c\u4e8c\u540d\u526f\u5c06\uff0c\u53c8\u88ab\u5229\u7bad\u4ece\u524d\u5fc3\u7a7f\u8fc7\u540e\u5fc3\uff0c\u4f17\u5b98\u5175\u5413\u5f97\u9b42\u4e0d\u9644\u4f53\uff0c\u53d1\u4e00\u58f0\u558a\uff0c\u62d4\u9a6c\u4fbf\u9003\u3002 \u53ea\u542c\u5f97\u5b5f\u7391\u53c8\u53eb\u9053\uff1a\u201c\u53eb\u4f60\u4e5f\u5403\u4e00\u7bad\uff01\u201d\u65b9\u5e86\u624b\u63d0\u65ad\u5f13\uff0c\u7528\u529b\u4e00\u62e8\u5f00\uff0c\u53ea\u542c\u5f97\u201c\u5580\u5693\u201d\u4e00 \u58f0\uff0c\u5229\u7bad\u4e0e\u94c1\u5f13\u76f8\u89e6\uff0c\u8ff8\u51fa\u706b\u82b1\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u5f13\u5f26\u54cd\u5904\uff0c\u7b2c\u4e8c\u679d\u7bad\uff0c\u53c8\u60ca\u25a1\u95ea\u7535\u822c\u5288 \u9762\u5c04\u5230\u3002\u65b9\u5e86\u4e00\u4e2a\u7b4b\u6597\uff0c\u4ece\u9a6c\u80cc\u4e0a\u843d\u4e0b\uff0c\u90a3\u679d\u7bad\u4ece\u4ed6\u5934\u9876\u4e09\u5bf8\u4e4b\u5904\u98de\u8fc7\u800c\u53bb\uff0c\u5934\u53d1\u4e00\u9635\u6c81 \u51c9\uff0c\u65b9\u5e86\u53eb\u9053\uff1a\u201c\u6b64\u756a\u6027\u547d\u4f11\u4e5f\uff01\u201d \u3000\u3000\u7b2c\u4e09\u679d\u7bad\u5374\u4e0d\u89c1\u5c04\u6765\uff0c\u4f46\u542c\u5f97\u5b5f\u7391\u5927\u7b11\u9053\uff1a\u201c\u4f60\u80fd\u8eb2\u8fc7\u4e24\u7bad\uff0c\u4e5f\u7b97\u597d\u6c49\uff0c\u9976\u4f60\u4e00\u547d\uff01\u201d \u547c\u54e8\u58f0\u4e2d\uff0c\u524d\u8fb9\u5c71\u5761\u6eda\u4e0b\u4e71\u77f3\uff0c\u5c06\u9053\u8def\u963b\u585e\uff0c\u53c8\u7a9c\u51fa\u4e00\u4f19\u5f3a\u4eba\u3002\u65b9\u5e86\u548c\u8863\u4e00\u6eda\uff0c\u62fc\u547d\u6eda\u4e0b\u5c71 \u5761\u53bb\uff0c\u53ea\u542c\u5f97\u5229\u7bad\u55d6\u55d6\u4e4b\u58f0\uff0c\u4f46\u5374\u6ca1\u6709\u4e00\u679d\u7bad\u5c04\u5230\u4ed6\u7684\u8eab\u4e0a\u3002 \u3000\u3000\u65b9\u5e86\u6eda\u4e0b\u5c71\u8c37\uff0c\u4f0f\u5728\u5c71\u6da7\u8fb9\u82a6\u8349\u4e4b\u4e2d\uff0c\u4e0a\u9762\u9a6c\u5636\u4eba\u53eb\uff0c\u95f9\u4e86\u534a\u4e2a\u65f6\u8fb0\uff0c\u8fd9\u624d\u542c\u5f97\u5386\u4e71\u8e44 \u58f0\uff0c\u79bb\u5f00\u4e86\u9a7f\u9053\u800c\u53bb\u3002 \u3000\u3000\u65b9\u5e86\u63a2\u51fa\u5934\u6765\uff0c\u53ea\u89c1\u65b0\u6708\u5728\u5929\uff0c\u56db\u65e0\u4eba\u8ff9\uff0c\u866b\u9e23\u5527\u5527\uff0c\u591c\u5bd2\u6c81\u4eba\u3002\u65b9\u5e86\u624b\u8db3\u5e76\u7528\uff0c\u722c\u5230 \u4e0a\u9762\uff0c\u5728\u7709\u6708\u5bd2\u661f\u4e4b\u4e0b\uff0c\u4f46\u89c1\u4e24\u540d\u526f\u5b98\u7684\u5c38\u4f53\u6a2a\u5728\u9053\u8def\u4e0a\uff0c\u5176\u4ed6\u7684\u4eba\u9a6c\u90fd\u4e0d\u89c1\u4e86\u3002\u65b9\u5e86\u60ca\u6050 \u4e4b\u6781\uff0c\u60f3\u9053\uff1a\u201c\u6211\u5e26\u7684\u5175\u60f3\u5fc5\u90fd\u88ab\u4ed6\u4eec\u4fd8\u864f\u53bb\u4e86\uff01\u201d\u6781\u76ee\u8fdc\u773a\uff0c\u5f3a\u4eba\u5f71\u5b50\u5df2\u6773\uff0c\u4ec0\u4e48\u4e5f\u77a7\u4e0d \u51fa\u6765\u3002 \u3000\u3000\u65b9\u5e86\u60ca\u9b42\u7a0d\u5b9a\uff0c\u60b2\u75db\u7ee7\u4e4b\u800c\u6765\uff0c\u5931\u4e86\u56db\u5341\u4e07\u4e24\u519b\u9977\uff0c\u8fd9\u4e8b\u975e\u540c\u5c0f\u53ef\uff0c\u8d77\u7801\u4e5f\u662f\u4e2a\u51cc\u8fdf\u7684 \u7f6a\u540d\u3002\u65b9\u5e86\u6478\u6478\u5934\u76ae\uff0c\u6b32\u54ed\u5374\u65e0\u6cea\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u4e0d\u5982\u90a3\u5f3a\u76d7\u628a\u6211\u5c04\u6b7b\u8fd8\u597d\uff01\u201d\u5446\u5750\u8def\u4e0a\uff0c \u770b\u6708\u4eae\u6162\u6162\u5347\u5230\u5929\u4e2d\uff0c\u60f3\u6765\u60f3\u53bb\uff0c\u5b9e\u662f\u96be\u9003\u4e00\u6b7b\uff0c\u53f9\u4e86\u53e3\u6c14\uff0c\u6478\u5230\u4e00\u6761\u7eca\u9a6c\u7684\u7c97\u7ef3\uff0c\u5728\u9888\u4e0a \u6253\u4e86\u4e2a\u7ed3\uff0c\u60ac\u5728\u6811\u6860\uff0c\u4f01\u56fe\u81ea\u5c3d\u3002 \u3000\u3000\u8eab\u5b50\u60ac\u7a7a\uff0c\u7ede\u7d22\u6e10\u7d27\uff0c\u65b9\u5e86\u53ea\u89c9\u80f8\u4e2d\u6c14\u4fc3\uff0c\u547c\u5438\u7a92\u606f\uff0c\u5934\u75db\u6b32\u88c2\uff0c\u96be\u53d7\u4e4b\u6781\uff0c\u5fc3\u4e2d\u60f3 \u9053\uff1a\u201c\u65e9\u77e5\u81ea\u7f22\u5982\u6b64\u8f9b\u82e6\uff0c\u4e0d\u5982\u6295\u6c34\u8fd8\u597d\u3002\u201d\u5176\u5b9e\u5317\u5730\u6625\u5bd2\uff0c\u6295\u6c34\u81ea\u6740\u4e5f\u662f\u4e00\u6837\u7684\u4e0d\u597d\u53d7\u3002 \u65b9\u5e86\u672c\u662f\u8feb\u4e8e\u81ea\u5c3d\uff0c\u5fc3\u4e2d\u5b9e\u4e0d\u60f3\u6b7b\u3002\u7ede\u7d22\u66f4\u7d27\uff0c\u8840\u6d41\u6025\u4fc3\uff0c\u66f4\u662f\u8f9b\u82e6\uff0c\u8fd9\u65f6\u60f3\u53eb\u53c8\u53eb\u4e0d\u51fa \u58f0\uff0c\u773c\u524d\u4e00\u56e2\u9ed1\u5f71\u6e10\u6e10\u6269\u5927\uff0c\u770b\u770b\u5c31\u8981\u6c14\u7edd\u8eab\u4ea1\u3002 \u3000\u3000\u5ffd\u7136\u8eab\u4e0a\u4e00\u8f7b\uff0c\u4f3c\u662f\u6709\u4eba\u62b1\u7740\u81ea\u5df1\uff0c\u6162\u6162\u653e\u4e0b\u5730\u6765\u3002\u65b9\u5e86\u8f7b\u8f7b\u547c\u5438\uff0c\u8fc7\u4e86\u4e00\u9635\uff0c\u7741\u5f00\u773c \u775b\uff0c\u53ea\u89c1\u4e00\u4e2a\u5c11\u5e74\uff0c\u7a7f\u7740\u7c97\u5e03\u8863\u88f3\uff0c\u7ad9\u5728\u8eab\u8fb9\uff0c\u5411\u7740\u81ea\u5df1\u5fae\u5fae\u7b11\u7740\u3002 \u3000\u3000\u65b9\u5e86\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u6551\u6211\uff1f\u201d\u5c11\u5e74\u7b11\u9053\uff1a\u201c\u5c82\u6709\u89c1\u6b7b\u4e0d\u6551\u4e4b\u7406\uff1f\u201d\u65b9\u5e86\u5f97\u4e86 \u6027\u547d\uff0c\u9661\u7136\u53c8\u60f3\u8d77\u4e86\u51cc\u8fdf\u4e4b\u7f6a\uff0c\u6b7b\u5ff5\u53c8\u840c\uff0c\u6323\u8131\u4e86\u5c11\u5e74\u7684\u548c\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u53cd\u6b63\u662f\u6b7b\uff0c\u4f60\u6551\u4e5f \u6551\u4e0d\u4e86\u6211\u3002\u201d\u5c11\u5e74\u9053\uff1a\u201c\u4f60\u4f55\u4e8b\u81ea\u6740\uff1f\u8bf4\u8bf4\u6211\u542c\u3002\u201d\u53cc\u624b\u4e00\u7d27\uff0c\u65b9\u5e86\u7adf\u81ea\u52a8\u5f39\u4e0d\u5f97\u3002\u65b9\u5e86\u6025 \u5f97\u8df3\u811a\u9053\uff1a\u201c\u4f60\u522b\u4e0e\u6211\u6b6a\u7f20\u4e86\uff0c\u8bf4\u4e0e\u4f60\u542c\u4e5f\u6ca1\u6709\u7528\u3002\u201d\u5c11\u5e74\u7a81\u7136\u677e\u624b\u7b11\u9053\uff1a\u201c\u770b\u4f60\u7684\u6837\u5b50\uff0c \u4f3c\u662f\u4e00\u4f4d\u671d\u5ef7\u7684\u519b\u5b98\u3002\u5475\uff0c\u6211\u77e5\u9053\u4e86\uff0c\u4f60\u4e00\u5b9a\u662f\u62bc\u8fd0\u519b\u9977\uff0c\u7ed9\u8d3c\u4eba\u52ab\u4e86\uff0c\u6240\u4ee5\u5bfb\u6b7b\u89c5\u6d3b\uff01\u201d \u65b9\u5e86\u8df3\u8d77\u6765\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\uff1f\u201d\u5c11\u5e74\u9053\uff1a\u201c\u4f60\u4eec\u62bc\u89e3\u519b\u9977\u7684\u6bcf\u5e74\u90fd\u8981\u7ecf\u8fc7\u8fd9\u91cc\u4e24\u6b21\uff0c\u6bcf\u6b21 \u5230\u6765\uff0c\u90fd\u95f9\u5f97\u9e21\u98de\u72d7\u8d70\uff0c\u8c01\u4e0d\u77e5\u9053\uff01\u201d\u65b9\u5e86\u82e6\u7b11\u9053\uff1a\u201c\u4f60\u65e2\u7136\u77e5\u9053\uff0c\u5c31\u4e0d\u8be5\u518d\u62e6\u963b\u6211\u3002\u201d\u5c11 \u5e74\u4e0d\u7406\u4ed6\u8bf4\uff0c\u81ea\u987e\u81ea\u7684\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u867d\u7136\u95f9\u5f97\u9e21\u98de\u72d7\u8d70\uff0c\u5230\u5e95\u662f\u8fd0\u519b\u9977\u7ed9\u8fb9\u5173\u7684\u5b88\u5175\uff0c\u82e5\u6ca1 \u6709\u5175\u5b88\uff0c\u9791\u5b50\u5175\u8bf4\u4e0d\u5b9a\u5c31\u662f\u4fb5\u8fdb\u6765\uff0c\u6240\u4ee5\u8fd8\u662f\u4e0d\u8981\u5bfb\u6b7b\u7684\u597d\uff01\u201d\u65b9\u5e86\u5fc3\u4e2d\u5927\u5947\uff0c\u53cd\u624b\u4e00\u6293\uff0c \u5374\u6251\u4e86\u4e2a\u7a7a\uff0c\u5c11\u5e74\u9053\uff1a\u201c\u4f60\u505a\u4ec0\u4e48\uff1f\u201d\u65b9\u5e86\u559d\u9053\uff1a\u201c\u4f60\u662f\u4f55\u4eba\uff1f\u4f60\u81ea\u79c1\u77e5\u9053\u519b\u9977\u88ab\u52ab\uff1f\u201d\u5c11 \u5e74\u9053\uff1a\u201c\u6211\u662f\u8fd9\u91cc\u79cd\u5730\u7684\u5c71\u6c11\uff0c\u6628\u665a\u4e00\u5927\u961f\u5f3a\u4eba\uff0c\u62bc\u7740\u8bb8\u591a\u9aa1\u5b50\uff0c\u8fd8\u7f1a\u4e86\u4e00\u5927\u4e32\u7684\u5b98\u5175\uff0c\u7ecf \u8fc7\u6211\u5bb6\u95e8\u524d\uff0c\u5411\u5c71\u4e2d\u8d70\u53bb\uff0c\u6211\u53c8\u4e0d\u662f\u50bb\u5b50\uff0c\u89c1\u8fd9\u60c5\u5f62\uff0c\u8fd8\u731c\u4e0d\u4e2d\u5417\uff1f\u201d\u65b9\u5e86\u9053\uff1a\u201c\u4f60\u77e5\u9053\u5f3a \u4eba\u7684\u5de2\u7a74\u5728\u54ea\u91cc\uff1f\u201d\u5c11\u5e74\u9053\uff1a\u201c\u6211\u53c8\u4e0d\u662f\u76d7\u515a\uff0c\u6211\u600e\u4e48\u77e5\u9053\uff1f\u201d\u65b9\u5e86\u6014\u4e86\u4e00\u6014\uff0c\u60f3\u9053\uff1a\u201c\u5c31 \u7b97\u6211\u77e5\u9053\u5f3a\u76d7\u5de2\u7a74\uff0c\u4e5f\u6ca1\u6709\u7528\u3002\u201d\u53c8\u56b7\u7740\u5bfb\u6b7b\u89c5\u6d3b\uff0c\u5c11\u5e74\u77a7\u4e86\u65b9\u5e86\u4e00\u773c\uff0c\u5ffd\u7136\u8bf4\u9053\uff1a\u201c\u94f6\u5b50 \u82e5\u80fd\u5bfb\u56de\uff0c\u4f60\u5c31\u4e0d\u5bfb\u6b7b\u4e86\uff0c\u662f\u4e0d\u662f\uff1f\u5bfb\u94f6\u80dc\u4e8e\u5bfb\u6b7b\uff0c\u4f60\u4e0d\u5982\u5bfb\u94f6\u5b50\u53bb\u5427\uff01\u201d \u3000\u3000\u65b9\u5e86\u609a\u7136\u4e00\u9707\uff0c\u84e6\u7136\u9192\u8d77\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u6211\u80fd\u5f00\u4e94\u77f3\u5f3a\u5f13\u6c14\u529b\u8fdc\u80dc\u5e38\u4eba\uff0c\u521a\u624d\u7ed9\u4ed6\u8f7b\u8f7b \u4e00\u62ff\uff0c\u7adf\u81ea\u52a8\u5f39\u4e0d\u5f97\uff0c\u8fd9\u5c11\u5e74\u5b9a\u662f\u975e\u5e38\u4e4b\u4eba\uff01\u201d\u65b9\u5e86\u7ecf\u8fc7\u6628\u65e5\u4e4b\u5f79\uff0c\u9a84\u77dc\u4e4b\u6c14\u5927\u51cf\uff0c\u77e5\u9053\u5929 \u5916\u6709\u5929\uff0c\u4e0d\u5230\u81ea\u5df1\u901e\u5f3a\u597d\u80dc\uff0c\u8fd9\u65f6\u798f\u81f3\u5fc3\u7075\uff0c\u7eb3\u5934\u4fbf\u62dc\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u65b9\u5e86\u81ea\u53f9\u6280\u4e0d\u5982\u4eba\uff0c\u5b9e \u662f\u6597\u90a3\u5f3a\u4eba\u4e0d\u8fc7\uff0c\u6073\u6c42\u4fa0\u58eb\u63f4\u624b\uff0c\u6551\u6211\u4e00\u547d\u3002\u201d\u90a3\u5c11\u5e74\u5927\u7b11\u9053\uff1a\u201c\u6211\u54ea\u91cc\u662f\u4ec0\u4e48\u4fa0\u58eb\uff0c\u6211\u662f \u4e00\u4e2a\u666e\u901a\u7684\u5c71\u6c11\u3002\u4f60\u8fd9\u8bdd\u82e5\u6559\u6211\u7684\u4e61\u91cc\u542c\u4e86\uff0c\u6015\u4e0d\u7b11\u6389\u4ed6\u4eec\u7684\u5927\u7259\u624d\u662f\u5462\uff01\u201d\u65b9\u5e86\u597d\u751f\u5931 \u671b\uff0c\u6b63\u5f85\u518d\u6c42\uff0c\u53ea\u542c\u5f97\u90a3\u5c11\u5e74\u53c8\u8bf4\u9053\uff1a\u201c\u77a7\u4f60\u8fd9\u6837\u53ef\u601c\uff0c\u7f62\uff0c\u7f62\uff0c\u6211\u4e14\u6307\u70b9\u4f60\u4e00\u6761\u660e\u8def\u3002\u201d \u65b9\u5e86\u5927\u559c\u8bf4\u9053\uff1a\u201c\u8bf7\u5144\u53f0\u6307\u6559\u3002\u201d\u5c11\u5e74\u9053\uff1a\u201c\u6211\u867d\u7136\u4e0d\u80fd\u6551\u4f60\uff0c\u4f46\u79bb\u6b64\u4e0d\u8fdc\uff0c\u4fbf\u6709\u4e00\u4f4d\u5947 \u4eba\uff0c\u4f60\u82e5\u6c42\u5f97\u6b64\u4eba\u7b54\u5e94\uff0c\u5931\u53bb\u7684\u519b\u9977\u5b9a\u53ef\u5f97\u56de\u3002\u201d\u65b9\u5e86\u9053\uff1a\u201c\u8fd9\u4f4d\u5947\u4eba\u59d3\u751a\u540d\u8c01\uff1f\u4f4f\u5728\u4f55 \u5904\uff1f\u6c42\u5144\u53f0\u6307\u70b9\u624d\u662f\u3002\u201d\u90a3\u5c11\u5e74\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u5947\u4eba\u813e\u6c14\u53e4\u602a\uff0c\u4f60\u82e5\u6253\u542c\u4ed6\u7684\u540d\u5b57\uff0c\u6027\u547d\u4e0d \u4fdd\u3002\u201d\u65b9\u5e86\u5413\u4e86\u4e00\u8df3\uff0c\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u6211\u4e0d\u6253\u542c\u4fbf\u662f\u3002\u70e6\u5144\u5f3a\u5f15\u89c1\u3002\u201d\u5c11\u5e74\u7eed\u9053\uff1a\u201c\u4f60\u5f53 \u662f\u8fd9\u6837\u6613\u6c42\u7684\u5417\uff1f\u201d\u65b9\u5e86\u9053\uff1a\u201c\u90a3\u4e48\u8981\u5982\u4f55\u6c42\u6cd5\uff1f\u201d \u3000\u3000\u5c11\u5e74\u5fae\u5fae\u4e00\u7b11\uff0c\u7a81\u7136\u5728\u5730\u4e0a\u62ff\u8d77\u65b9\u5e86\u9002\u624d\u81ea\u7f22\u7684\u7c97\u7ef3\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u987b\u5f97\u518d\u5bfb\u6b7b\u4e00\u6b21\uff01\u201d \u65b9\u5e86\u5403\u4e86\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u201d\u5c11\u5e74\u8bf4\u9053\uff1a\u201c\u4f60\u660e\u65e5\u7edd\u65e9\uff0c\u4fbf\u4ece\u6b64\u5730\u52a8\u8eab\uff0c\u8d70\u5165\u5c71\u8c37\uff0c\u5f80\u897f \u65b9\u8d70\u7ea6\u4e03\u516b\u91cc\uff0c\u4fbf\u53ef\u89c1\u5230\u4e00\u5e26\u6843\u6797\uff0c\u8fd8\u6709\u8bb8\u591a\u82b1\u6811\uff0c\u90a3\u4e2a\u5730\u65b9\u53eb\u2018\u8774\u8776\u8c37\u2019\u3002\u6843\u6797\u540e\u9762\u6709\u4e00 \u95f4\u5c0f\u623f\u5b50\uff0c\u5947\u4ece\u4fbf\u4f4f\u5728\u91cc\u9762\u3002\u4f60\u4e0d\u53ef\u5f84\u53bb\u6c42\u6073\uff0c\u6843\u6797\u524d\u9762\u7ea6\u767e\u6b65\u4e4b\u5904\uff0c\u6709\u4e00\u4e2a\u5927\u5ca9\u77f3\uff0c\u77f3\u8272 \u6bb7\u7ea2\u975e\u5e38\u597d\u8ba4\u3002\u4f60\u8981\u5728\u65e5\u5934\u672a\u51fa\u4e4b\u524d\uff0c\u5230\u90a3\u77f3\u5ca9\u4e2d\u95f4\u7684\u88c2\u7f1d\u4e4b\u5904\u8eb2\u85cf\u3002\u82e5\u89c1\u6709\u4eba\uff0c\u4e0d\u53ef\u51fa \u6765\uff0c\u7b49\u5230\u9633\u5149\u521a\u521a\u5c04\u8fdb\u5ca9\u77f3\u7f1d\u9699\u4e4b\u65f6\uff0c\u4f60\u624d\u53ef\u51fa\u6765\uff0c\u968f\u4fbf\u62e3\u4e00\u68f5\u6843\u6811\uff0c\u50cf\u521a\u624d\u4e00\u6837\u4e0a\u540a\uff0c\u90a3 \u4f4d\u5947\u4eba\u4fbf\u4f1a\u6765\u6551\u4f60\u4e86\u3002\u4e0a\u540a\u4e4b\u65f6\uff0c\u4f60\u5343\u4e07\u4e0d\u80fd\u4f5c\u5047\uff0c\u4e00\u5b9a\u8981\u6253\u6b7b\u7ed3\uff0c\u603b\u4e4b\u8981\u548c\u521a\u624d\u7684\u4e00\u6a21\u4e00 \u6837\uff0c\u7d27\u8bb0\u7d27\u8bb0\uff01\u5230\u90a3\u4f4d\u5947\u4eba\u95ee\u4f60\u4e4b\u65f6\uff0c\u4f60\u5343\u4e07\u4e0d\u80fd\u8bf4\u662f\u6709\u4eba\u6307\u70b9\u7684\u3002\u201d \u3000\u3000\u65b9\u5e86\u542c\u4e86\uff0c\u72d0\u7591\u6ee1\u8179\uff0c\u90a3\u5c11\u5e74\u7b11\u9053\uff1a\u201c\u4f60\u80fd\u4e0d\u80fd\u6361\u56de\u6027\u547d\u5c31\u5168\u8981\u770b\u4f60\u7684\u9020\u5316\u4e86\u3002\u4f60\u597d\u597d \u7761\u4e00\u523b\u5427\uff0c\u6211\u8981\u8d70\u4e86\u3002\u201d\u65b9\u5e86\u53eb\u9053\uff1a\u201c\u5144\u53f0\u6162\u8d70\uff01\u201d\u54ea\u91cc\u62c9\u5f97\u4f4f\u4ed6\uff0c\u7728\u773c\u4e4b\u95f4\uff0c\u90a3\u5c11\u5e74\u5df2\u8d70 \u5f97\u65e0\u5f71\u65e0\u8e2a\u3002 \u3000\u3000\u65b9\u5e86\u60f3\u9053\uff1a\u201c\u6211\u53cd\u6b63\u662f\u6b7b\uff0c\u8fd9\u5c11\u5e74\u8bf4\u8bdd\u867d\u7136\u602a\u8bde\uff0c\u4e5f\u4e0d\u59a8\u4e00\u8bd5\u3002\u201d\u5fc3\u4e2d\u6709\u4e8b\uff0c\u4e0d\u6562\u7761 \u89c9\uff0c\u6253\u4e86\u4e2a\u76f9\uff0c\u770b\u770b\u6708\u4eae\u843d\u5c71\uff0c\u4fbf\u8d77\u8eab\u8d76\u8def\u3002\u6478\u8fdb\u5c71\u8c37\uff0c\u897f\u884c\u6570\u91cc\uff0c\u6b8b\u661f\u660e\u706d\uff0c\u66d9\u8272\u9690\u73b0\uff0c \u65b9\u5e86\u518d\u884c\u4e00\u4e8c\u91cc\u8def\uff0c\u5929\u8fb9\u5df2\u73b0\u51fa\u4e73\u767d\u8272\uff0c\u5ffd\u95fb\u6251\u9f3b\u6e05\u9999\uff0c\u7cbe\u795e\u4e3a\u4e4b\u4e00\u723d\uff0c\u524d\u9762\u679c\u7136\u6709\u4e00\u5e26\u6843 \u6797\uff0c\u8fd8\u6742\u7740\u8bb8\u591a\u4e0d\u77e5\u540d\u7684\u82b1\u6811\uff0c\u7ea2\u7684\u767d\u7684\uff0c\u707f\u5982\u4e91\u971e\uff0c\u851a\u6210\u82b1\u6d77\u3002\u6843\u6797\u524d\u9762\u679c\u7136\u6709\u4e00\u5757\u5927\u5ca9 \u77f3\uff0c\u77f3\u8272\u6bb7\u7ea2\u5982\u8840\uff0c\u7ea6\u6709\u4e09\u4e2a\u4eba\u9ad8\uff0c\u5ca9\u77f3\u4e2d\u95f4\u6709\u4e00\u6761\u5927\u88c2\u7f1d\uff0c\u521a\u521a\u53ef\u4ee5\u5bb9\u8eab\uff0c\u65b9\u5e86\u8eb2\u8fdb\u91cc \u9762\uff0c\u5fc3\u4e2d\u60f4\u60f4\uff0c\u5f20\u5927\u773c\u775b\uff0c\u4ece\u77f3\u9699\u7f1d\u4e2d\u5077\u7aa5\u51fa\u6765\uff0c\u7b49\u5f85\u5947\u8ff9\u3002 \u3000\u3000\u7b49\u4e86\u4e00\u4f1a\uff0c\u4e0d\u89c1\u52a8\u9759\u3002\u518d\u7b49\u4e00\u4f1a\uff0c\u773c\u775b\u4e00\u4eae\uff0c\u4ece\u88c2\u7f1d\u4e0a\u7aef\u7aa5\u51fa\uff0c\u5df2\u53ef\u89c1\u7740\u4e00\u7ebf\u5929\u5149\uff0c\u4e0d \u4e00\u523b\uff0c\u4e91\u4e2d\u767d\u5149\u95ea\u53d1\uff0c\u4e1c\u65b9\u5929\u8272\u51fa\u6726\u80e7\u9010\u6e10\u53d8\u7ea2\uff0c\u4e00\u8f6e\u8840\u7ea2\u7684\u65ed\u65e5\u7a81\u7136\u4ece\u96fe\u4e2d\u9732\u4e86\u51fa\u6765\uff0c\u5f69 \u971e\u6ee1\u5929\uff0c\u4e0e\u5149\u76f8\u6620\uff0c\u66f4\u663e\u5f97\u7f8e\u8273\u65e0\u4fe6\uff01\u4e0d\u77e5\u4ece\u54ea\u91cc\u98de\u6765\u4e86\u8bb8\u591a\u5f69\u8272\u7684\u8774\u8776\uff0c\u7fa4\u96c6\u5728\u82b1\u6811\u4e4b \u4e0a\uff0c\u5ffd\u800c\u53c8\u7ed5\u6811\u7a7f\u82b1\uff0c\u65b9\u5e86\u867d\u662f\u4e00\u4ecb\u6b66\u592b\uff0c\u4e5f\u89c9\u5f97\u795e\u6021\u76ee\u593a\u3002 \u3000\u3000\u518d\u8fc7\u4e9b\u65f6\uff0c\u9633\u5149\u5df2\u5c04\u5165\u6843\u6797\uff0c\u65b9\u5e86\u773c\u775b\u53c8\u662f\u4e00\u4eae\uff0c\u5ffd\u89c1\u7e41\u82b1\u5982\u6d77\u4e4b\u4e2d\uff0c\u7a81\u7136\u591a\u4e86\u4e00\u4e2a\u5c11 \u5973\uff0c\u767d\u8272\u8863\u88d9\uff0c\u8863\u8882\u98d8\u98d8\uff0c\u96c5\u4e3d\u5982\u4ed9\uff0c\u4e5f\u4e0d\u77e5\u662f\u4ece\u54ea\u91cc\u6765\u7684\uff01\u90a3\u5c11\u5973\u5411\u7740\u9633\u5149\uff0c\u5f2f\u8170\u4f38\u624b\uff0c \u505a\u4e86\u51e0\u4e2a\u52a8\u4f5c\uff0c\u7a81\u7136\u7ed5\u6811\u800c\u8dd1\uff0c\u8d8a\u8dd1\u8d8a\u75be\uff0c\u628a\u65b9\u5e86\u770b\u5f97\u76f4\u662f\u773c\u82b1\u7f2d\u4e71\uff0c\u867d\u7136\u8eab\u5b50\u5c40\u4fc3\u5728\u77f3\u9699 \u4e4b\u4e2d\uff0c\u4e5f\u597d\u4f3c\u8981\u8ddf\u7740\u5979\u65cb\u8f6c\u4f3c\u7684\u3002\u65b9\u5e86\u6b63\u81ea\u611f\u5230\u6655\u7729\uff0c\u90a3\u5c11\u5973\u5ffd\u7136\u505c\u4e0b\u6b65\u6765\uff0c\u7f13\u7f13\u884c\u4e86\u4e00 \u531d\uff0c\u7a81\u7136\u8eab\u5f62\u4e00\u8d77\uff0c\u8df3\u4e0a\u4e00\u68f5\u6811\u68a2\uff0c\u53c8\u4ece\u8fd9\u4e00\u68f5\u8df3\u5230\u53e6\u4e00\u68f5\uff0c\u771f\u662f\u8eab\u5982\u98de\u9e1f\uff0c\u6377\u4f3c\u7075\u733f\u3002\u90a3 \u5c11\u5973\u5728\u6811\u4e0a\u5954\u817e\u8df3\u8dc3\uff0c\u6ee1\u6811\u6843\u82b1\uff0c\u7adf\u65e0\u4e00\u6735\u843d\u4e0b\uff01\u65b9\u5e86\u770b\u5f97\u77eb\u820c\u96be\u4e0b\uff0c\u5fc3\u9053\uff1a\u201c\u96be\u9053\u90a3\u5c11\u5e74 \u6240\u8bf4\u7684\u5947\u4eba\uff0c\u7adf\u7136\u5c31\u662f\u8fd9\u4e2a\u5c11\u5973\uff1f\u201d \u3000\u3000\u518d\u770b\u65f6\uff0c\u90a3\u5c11\u5973\u53c8\u4ece\u6811\u4e0a\u8df3\u4e0b\uff0c\u957f\u8896\u6325\u821e\uff0c\u7fe9\u7fe9\u5982\u4ed9\uff0c\u8fc7\u4e86\u6b64\u65f6\uff0c\u53ea\u89c1\u6811\u679d\u7c0c\u7c0c\u6296\u52a8\uff0c \u4f3c\u7ed9\u6625\u98ce\u5439\u62c2\u4e00\u822c\uff0c\u6811\u4e0a\u6843\u82b1\uff0c\u7eb7\u7eb7\u843d\u4e0b\u3002\u5c11\u5973\u4e00\u58f0\u957f\u7b11\uff0c\u53cc\u8896\u4e00\u5377\uff0c\u628a\u843d\u4e0b\u7684\u82b1\u6735\uff0c\u53c8\u5377 \u5165\u8896\u4e2d\u3002\u60a0\u60a0\u95f2\u95f2\u5730\u501a\u7740\u6843\u6811\uff0c\u7f8e\u76ee\u542b\u7b11\uff0c\u987e\u76fc\u751f\u59ff\uff01 \u3000\u3000\u65b9\u5e86\u770b\u5f97\u5446\u4e86\uff0c\u5fc3\u9053\uff1a\u201c\u5929\u4e0b\u95f4\u7adf\u6709\u8fd9\u6837\u7f8e\u8273\u7684\u5c11\u5973\uff0c\u6843\u82b1\u90fd\u7ed9\u5979\u6bd4\u4e0b\u53bb\u4e86\u3002\u201d\u8fc7\u4e86\u4e00 \u4f1a\uff0c\u90a3\u4e00\u5927\u7fa4\u8774\u8776\uff0c\u9002\u624d\u88ab\u5c11\u5973\u5728\u679d\u5934\u60ca\u8d70\u7684\uff0c\u53c8\u98de\u4e86\u56de\u6765\uff0c\u6e38\u620f\u82b1\u95f4\u3002\u5c11\u5973\u7a81\u7136\u53cc\u8896\u4e00 \u626c\uff0c\u65e0\u6570\u6843\u82b1\uff0c\u7eb7\u7eb7\u81ea\u8863\u8896\u4e4b\u4e2d\u98de\u51fa\uff0c\u8774\u8776\u5431\u5431\u602a\u53eb\uff0c\u843d\u4e86\u4e00\u5730\u3002\u65b9\u5e86\u8fd9\u4e00\u60ca\u66f4\u662f\u975e\u540c\u5c0f \u53ef\uff1a\u7528\u6843\u82b1\u6765\u505a\u6697\u54ed\uff0c\u8fd9\u771f\u662f\u65f7\u53e4\u672a\u95fb\u4e4b\u4e8b\uff01\u53c8\u4e3a\u90a3\u7fa4\u7f8e\u4e3d\u7684\u5f69\u8776\u53ef\u60dc\uff0c\u5fc3\u9053\uff1a\u201c\u82b1\u95f4\u6251\u8776 \u4e43\u662f\u97f5\u4e8b\u800c\u628a\u8774\u8776\u5f04\u6b7b\uff0c\u8fd9\u5374\u672a\u514d\u592a\u715e\u98ce\u666f\u4e86\uff01\u201d \u3000\u3000\u8f6c\u77ac\u4e4b\u95f4\uff0c\u90a3\u4e9b\u843d\u5730\u7684\u8774\u8776\u53c8\u5c55\u7fc5\u98de\u8d77\uff0c\u53ea\u542c\u5f97\u90a3\u5c11\u5973\u7b11\u9053\uff1a\u201c\u8776\u513f\u5475\uff0c\u7d2f\u4f60\u4eec\u53d7\u60ca \u4e86\uff0c\u6211\u4e5f\u4e0d\u518d\u6253\u6405\u4f60\u4eec\u5566\uff01\u201d\u7f13\u7f13\u6b65\u5165\u82b1\u6811\u4e1b\u4e2d\uff0c\u8fdb\u5165\u4e86\u6843\u6797\u540e\u7684\u5c0f\u5c4b\u3002 \u3000\u3000\u65b9\u5e86\u8212\u4e86\u53e3\u6c14\uff0c\u5ffd\u89c9\u9633\u5149\u8000\u773c\uff0c\u5df2\u4ece\u77f3\u9699\u4e2d\u900f\u5c04\u8fdb\u6765\u3002\u65b9\u5e86\u4e0d\u89c9\u5927\u5947\uff0c\u60f3\u9053\uff1a\u201c\u90a3\u5c11\u5e74 \u7adf\u7136\u7b97\u5f97\u5982\u6b64\u51c6\u786e\uff0c\u8fd9\u5c11\u5973\u521a\u521a\u6b65\u5165\u5c0f\u5c4b\uff0c\u5c31\u662f\u9633\u5149\u900f\u8fdb\u77f3\u9699\u4e4b\u65f6\uff01\u201d \u3000\u3000\u8fd9\u65f6\u65b9\u5e86\u7684\u6c42\u751f\u4e4b\u5ff5\u4e0e\u597d\u5947\u4e4b\u5fc3\u6df7\u6742\u4e00\u8d77\uff0c\u6025\u5fd9\u8d70\u51fa\u77f3\u9699\u95f4\uff0c\u62ff\u8d77\u7c97\u7ef3\uff0c\u5728\u5589\u5934\u6253\u4e86\u4e00 \u4e2a\u6b7b\u7ed3\uff0c\u5c06\u81ea\u5df1\u60ac\u5728\u6811\u4e0a\u3002\u7ede\u7d22\u6e10\u6e10\u6536\u7d27\uff0c\u547c\u5438\u7a92\u606f\uff0c\u96be\u53d7\u975e\u5e38\uff0c\u65b9\u5e86\u4e24\u773c\u53d1\u76f4\uff0c\u5374\u4e0d\u89c1\u90a3 \u5c11\u5973\u51fa\u6765\u76f8\u6551\u3002\u65b9\u5e86\u60f3\u558a\u53c8\u558a\u4e0d\u51fa\u58f0\uff0c\u7ede\u7d22\u66f4\u7d27\uff0c\u53ea\u89c9\u773c\u524d\u91d1\u661f\u4e71\u5192\uff0c\u5730\u8f6c\u5929\u65cb\uff0c\u6843\u6797\u4e4b\u4e2d \u4ecd\u662f\u6e3a\u65e0\u4eba\u5f71\u3002\u65b9\u5e86\u5927\u6094\uff0c\u5fc3\u60f3\uff1a\u201c\u83ab\u975e\u662f\u90a3\u5c11\u5e74\u6545\u610f\u620f\u5f04\u4e8e\u6211\uff0c\u53eb\u6211\u518d\u53d7\u4e00\u6b21\u7f22\u7ef3\u4e4b \u82e6\uff01\u201d\u8f9b\u82e6\u4e4b\u6781\uff0c\u53cc\u811a\u4e71\u8e22\uff0c\u8e22\u5f97\u6811\u4e0a\u7684\u82b1\u6735\uff0c\u7247\u7247\u843d\u4e0b\u3002 \u3000\u3000\u8d8a\u662f\u6323\u624e\uff0c\u7ede\u7d22\u8d8a\u7d27\uff0c\u65b9\u5e86\u773c\u775b\u53d1\u9ed1\uff0c\u795e\u667a\u4e5f\u6e10\u8ff7\u7cca\u3002\u5c31\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u5ffd\u89c9\u6709\u4eba\u5728\u81ea\u5df1 \u8eab\u4e0a\u8f7b\u8f7b\u4e00\u62c2\uff0c\u597d\u50cf\u6709\u4e00\u628a\u5229\u526a\u7ed9\u81ea\u5df1\u526a\u65ad\u4e86\u7ede\u7d22\uff0c\u547c\u5438\u7acb\u523b\u7545\u901a\uff0c\u65b9\u5e86\u5f20\u5f00\u4e86\u53e3\uff0c\u5374\u8bf4\u4e0d \u51fa\u8bdd\u3002\u539f\u6765\u662f\u7ed9\u7ef3\u7d22\u7ede\u5f97\u592a\u7d27\u4e86\u3002 \u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\uff0c\u65b9\u5e86\u6c14\u529b\u6e10\u6e10\u6062\u590d\uff0c\u5f20\u5f00\u773c\u775b\uff0c\u53ea\u89c1\u9762\u524d\u7ad9\u7740\u7684\u6b63\u662f\u9002\u624d\u6797\u4e2d\u7684\u5c11\u5973\u3002\u65b9\u5e86 \u4f4e\u58f0\u9053\u8c22\uff0c\u90a3\u5c11\u5973\u7684\u76ee\u5149\u6709\u5982\u5bd2\u51b0\u5229\u526a\uff0c\u76ef\u7740\u4ed6\u9053\uff1a\u201c\u5140\uff0c\u4f60\u8fd9\u5b98\u513f\uff0c\u56e0\u4f55\u5bfb\u6b7b\uff1f\u201d\u65b9\u5e86\u62dc \u5012\u5730\u4e0a\uff0c\u8bc9\u8bf4\u5931\u53bb\u4e86\u56db\u5341\u4e07\u4e24\u519b\u9977\uff0c\u82e5\u6309\u519b\u6cd5\u5904\u7f6e\uff0c\u5c31\u8981\u53d7\u51cc\u8fdf\u5904\u6b7b\u3002\u5c11\u5973\u8e59\u4e86\u7709\u5934\uff0c\u5ffd\u7136 \u6325\u8896\u8bf4\u9053\uff1a\u201c\u8fd9\u4e8b\u60c5\u6211\u4e0d\u80fd\u7ba1\uff01\u201d\u65b9\u5e86\u5927\u6025\uff0c\u5f80\u524d\u626f\u5979\u88d9\u89d2\uff0c\u54ea\u626f\u5f97\u7740\uff1f\u65b9\u5e86\u54d1\u58f0\u54ed\u9053\uff1a \u201c\u6211\u4e0a\u6709\u8001\u6bcd\u3001\u4e0b\u6709\u5b64\u513f\u3002\u4f60\u82e5\u4e0d\u7406\uff0c\u8fd9\u4e16\u4e0a\u5c31\u6dfb\u4e86\u4e09\u4e2a\u51a4\u9b3c\u4e86\uff01\u201d\u90a3\u5c11\u5973\u7f13\u7f13\u56de\u5934\uff0c\u9053\uff1a \u201c\u662f\u771f\u7684\u5417\uff1f\u201d\u65b9\u5e86\u9053\uff1a\u201c\u82e5\u6709\u534a\u53e5\u865a\u8a00\uff0c\u6559\u6211\u518d\u53d7\u4e00\u6b21\u7ede\u7d22\u4e4b\u82e6\uff01\u201d\u5c11\u5973\u9762\u8272\u4e00\u5c55\uff0c\u5583\u5583 \u81ea\u8bed\u9053\uff1a\u201c\u53cd\u6b63\u6211\u90fd\u8981\u627e\u4ed6\u4eec\uff0c\u4e5f\u597d\uff0c\u5c31\u66ff\u4f60\u7ba1\u4e00\u6b21\u95f2\u4e8b\u3002\u201d\u65b9\u5e86\u5927\u559c\u62dc\u8c22\uff0c\u5c11\u5973\u55d4\u9053\uff1a \u201c\u6211\u53c8\u4e0d\u662f\u6b7b\u4eba\uff0c\u4f60\u62dc\u6211\u505a\u751a\uff1f\u55ef\uff0c\u518d\u53d7\u4e00\u6b21\u7ede\u7d22\u4e4b\u82e6\uff1f\u5454\uff0c\u662f\u8c01\u4eba\u6307\u70b9\u4f60\u6765\u6c42\u6211\u7684\uff1f\u201d\u65b9 \u5e86\u9053\uff1a\u201c\u6ca1\u6709\u5440\uff0c\u6ca1\u6709\uff01\u201d\u5c11\u5973\u9053\uff1a\u201c\u4f60\u81ea\u7f22\u4e86\u51e0\u6b21\u4e86\uff1f\u201d\u65b9\u5e86\u9053\uff1a\u201c\u5c31\u8fd9\u4e00\u6b21\u5440\u3002\u201d\u5c11\u5973 \u6c89\u541f\u4e00\u4f1a\uff0c\u5ffd\u7136\u7b11\u9053\uff1a\u201c\u5176\u5b9e\u4f60\u81ea\u7f22\u51e0\u6b21\uff0c\u6211\u4e5f\u7ba1\u4e0d\u7740\u4f60\u3002\u6211\u65e2\u7136\u8bf4\u4e86\u6551\u4f60\uff0c\u5c31\u662f\u6709\u4eba\u6307 \u70b9\uff0c\u6211\u4e5f\u5f97\u6551\u4f60\u5230\u5e95\uff01\u81ea\u7f22\u5f88\u4e0d\u597d\u73a9\uff0c\u4e0b\u6b21\u4e0d\u8981\u518d\u8bd5\u4e86\u3002\u201d\u5ae3\u7136\u4e00\u7b11\uff0c\u5934\u4e0a\u4e24\u4e2a\u4e2b\u89d2\u5fae\u5fae\u6446 \u52a8\u3002\u65b9\u5e86\u77a7\u8fd9\u5c11\u5973\uff0c\u4e0d\u8fc7\u5341\u516d\u4e03\u5c81\u7684\u6837\u5b50\uff0c\u5fae\u7b11\u8bf4\u8bdd\u4e4b\u65f6\uff0c\u9732\u51fa\u4e00\u8138\u7a1a\u6c14\uff0c\u4e0d\u89c9\u53c8\u662f\u6697\u6697\u62c5 \u5fe7\uff0c\u53ea\u6050\u8fd9\u5b64\u8eab\u5c11\u5973\u6597\u4e0d\u8fc7\u90a3\u7fa4\u5f3a\u76d7\u3002 \u3000\u3000\u5c11\u5973\u9053\uff1a\u201c\u597d\uff0c\u4f60\u968f\u6211\u6765\uff01\u201d\u65b9\u5e86\u8ddf\u5979\u8d70\u8fdb\u6797\u4e2d\u5c0f\u5c4b\uff0c\u5c11\u5973\u9053\uff1a\u201c\u4f60\u4e00\u5b9a\u997f\u4e86\uff0c\u5148\u70e4\u70b9 \u864e\u8089\u5403\u5427\uff01\u201d\u65b9\u5e86\u4e00\u77a5\uff0c\u53ea\u89c1\u5c4b\u89d2\u4e00\u53ea\u540a\u775b\u767d\u989d\u5927\u866b\uff0c\u8eba\u5728\u5730\u4e0a\u3002\u65b9\u5e86\u5403\u4e86\u4e00\u60ca\uff0c\u5c11\u5973\u7b11 \u9053\uff1a\u201c\u8fd9\u662f\u6b7b\u8001\u864e\uff0c\u4f60\u6015\u4ec0\u4e48\uff1f\u4f60\u4f1a\u5265\u864e\u76ae\u5417\uff1f\u201d\u65b9\u5e86\u9053\uff1a\u201c\u89c1\u730e\u6237\u5265\u8fc7\u3002\u201d\u5c11\u5973\u9053\uff1a \u201c\u597d\uff0c\u90a3\u4f60\u66ff\u6211\u5f04\u3002\u770b\u4f60\u9002\u624d\u8e22\u90a3\u6843\u6811\u4e4b\u529b\uff0c\u8fd9\u4e09\u767e\u591a\u65a4\u7684\u8001\u864e\uff0c\u4f60\u8fd8\u7ffb\u5f04\u5f97\u52a8\u3002\u201d\u65b9\u5e86\u53c8 \u662f\u4e00\u60ca\uff0c\u5c11\u5973\u6253\u864e\uff0c\u5df2\u662f\u5947\u95fb\uff0c\u800c\u53ea\u4e00\u77a7\u5c31\u77a7\u51fa\u81ea\u5df1\u6c14\u529b\u5927\u5c0f\uff0c\u66f4\u662f\u7cbe\u6653\u6b66\u529f\u7684\u5927\u884c\u5bb6\u4e86\u3002 \u3000\u3000\u5403\u8fc7\u70e4\u8001\u864e\u8089\uff0c\u5df2\u662f\u4e2d\u5348\u65f6\u5206\uff0c\u5c11\u5973\u4ece\u5899\u4e0a\u53d6\u4e0b\u4e00\u67c4\u5b9d\u5251\u9053\uff1a\u201c\u4f60\u968f\u6211\u6765\uff0c\u54b1\u4eec\u53bb\u627e\u5f3a \u4eba\uff0c\u8ba8\u56de\u90a3\u56db\u5341\u4e07\u4e24\u94f6\u5b50\u3002\u201d\u4ece\u5c71\u8c37\u4e2d\u722c\u4e0a\uff0c\u8fdb\u5165\u6df1\u5c71\u5bc6\u6797\u4e4b\u95f4\uff0c\u8d70\u4e86\u4e00\u4e2a\u65f6\u8fb0\uff0c\u53ea\u89c1\u4e24\u5cf0 \u5939\u5cd9\uff0c\u5ced\u58c1\u9661\u7acb\uff0c\u5ced\u58c1\u4e4b\u4e0b\uff0c\u6709\u4e00\u4e2a\u5ca9\u6d1e\uff0c\u5ca9\u6d1e\u524d\u9762\u5374\u662f\u4e00\u7247\u5e73\u5730\uff0c\u5c11\u5973\u9053\uff1a\u201c\u8fd9\u91cc\u60f3\u5fc5\u5c31 \u662f\u4ed6\u4eec\u85cf\u91d1\u4e4b\u6240\u3002\u201d\u8fc8\u6b65\u76f4\u8fdb\uff0c\u5ffd\u7136\u542c\u5f97\u4e00\u58f0\u558a\u9053\uff1a\u201c\u6321\u9a7e\uff01\u201d\u5728\u8349\u4e1b\u4e2d\u7a81\u7136\u8df3\u8d77\u4e24\u6761\u6c49 \u5b50\uff0c\u4e24\u6761\u68cd\u68d2\uff0c\u5288\u5934\u6253\u4e0b\uff0c\u6765\u52bf\u8fc5\u75be\u4e4b\u6781\uff01 \u3000\u3000\u5c11\u5973\u8eab\u5f62\u4e00\u8f6c\uff0c\u4e24\u6761\u68cd\u68d2\u5168\u6251\u4e86\u7a7a\uff0c\u53ea\u89c1\u5979\u957f\u8896\u4e00\u7529\uff0c\u90a3\u4e24\u6761\u6c49\u5b50\uff0c\u6251\u52bf\u592a\u731b\uff0c\u6536\u4e0d\u4f4f \u811a\u6b65\uff0c\u53c8\u7ed9\u5979\u8f7b\u8f7b\u4e00\u5e26\uff0c\u7adf\u7136\u53cc\u53cc\u6454\u5012\u5730\u4e0a\uff0c\u56db\u811a\u671d\u5929\u3002\u5c11\u5973\u51b7\u7b11\u4e00\u58f0\uff0c\u5934\u4e5f\u4e0d\u56de\uff0c\u4e0d\u505c\u6b65 \u5730\u5411\u524d\u8dd1\u53bb\u3002 \u3000\u3000\u5ca9\u6d1e\u4e4b\u524d\uff0c\u4e71\u77f3\u5982\u72ee\u5982\u864e\uff0c\u5982\u9a6c\u5982\u725b\uff0c\u5947\u5f62\u602a\u72b6\uff0c\u4e0d\u8ba1\u5176\u6570\uff0c\u56f4\u7740\u4e00\u5757\u5e73\u5730\uff0c\u5c11\u5973\u811a\u4e0d \u505c\u6b65\uff0c\u95ef\u5165\u77f3\u9635\u4e4b\u4e2d\uff0c\u731b\u7136\u542c\u5f97\u53c8\u662f\u4e00\u58f0\uff1a\u201c\u6321\u9a7e\uff01\u201d\u5728\u4e71\u77f3\u4e1b\u4e2d\u5200\u67aa\u9f50\u51fa\uff0c\u5200\u523a\u9165\u80f8\uff0c\u67aa \u6311\u819d\u76d6\uff0c\u5c11\u5973\u51cc\u7a7a\u4e00\u8dc3\uff0c\u8863\u8896\u5f80\u4e0b\u4e00\u62c2\uff0c\u51b7\u7b11\u4e00\u58f0\u9053\uff1a\u201c\u4e5f\u6321\u4e0d\u4f4f\uff01\u201d\u90a3\u8df3\u8d77\u6765\u821e\u5200\u5f04\u67aa\u7684 \u4e24\u6761\u6c49\u5b50\uff0c\u867d\u662f\u5200\u67aa\u6420\u7a7a\uff0c\u5374\u7acb\u523b\u6536\u52bf\u6251\u8ffd\uff0c\u5e76\u4e0d\u50cf\u524d\u5148\u90a3\u4e24\u4eba\u4e00\u6837\u6454\u5012\u3002\u65b9\u5e86\u5fc3\u60ca\u80c6\u6218\uff0c \u4e0d\u6562\u8d70\u8fdb\uff0c\u53ea\u89c1\u90a3\u5c11\u5973\u62db\u62db\u624b\u9053\uff1a\u201c\u6765\u5440\uff01\u4f60\u662f\u5931\u94f6\u5b50\u7684\u6b63\u4e3b\uff0c\u4f60\u4e0d\u6765\u4ed6\u4eec\u8fd8\u7ed9\u8c01\u4eba\uff1f\u201d \u3000\u3000\u65b9\u5e86\u9f13\u8d77\u52c7\u6c14\uff0c\u8d70\u5165\u77f3\u9635\uff0c\u53ea\u89c1\u90a3\u5c11\u5973\u5df2\u548c\u56db\u6761\u6c49\u5b50\u6253\u5728\u4e00\u8d77\uff0c\u56db\u6761\u6c49\u5b50\uff0c\u5404\u5360\u56db\u65b9\uff0c \u5c06\u5c11\u5973\u56f4\u5728\u5f53\u4e2d\uff0c\u4e24\u6761\u68cd\u68d2\uff0c\u4e00\u5200\u4e00\u67aa\uff0c\u72e0\u72b0\u653b\u51fb\u3002\u5c11\u5973\u8170\u60ac\u5b9d\u5251\uff0c\u5374\u5e76\u4e0d\u62d4\u51fa\u5e94\u6218\uff0c\u53ea\u89c1 \u5979\u5728\u5200\u67aa\u68cd\u68d2\u4e4b\u4e2d\uff0c\u98d8\u6765\u6643\u53bb\uff0c\u6070\u5982\u8774\u8776\u7a7f\u82b1\uff0c\u873b\u8713\u620f\u6c34\uff0c\u8863\u8882\u98ce\u98d8\uff0c\u597d\u770b\u4e4b\u6781\uff01\u65b9\u5e86\u9887\u6653 \u6b66\u529f\uff0c\u4f46\u770b\u4e86\u4e00\u9635\uff0c\u5df2\u89c9\u8111\u888b\u6655\u7729\u4e0d\u5df2\uff0c\u6025\u5fd9\u5c06\u76ee\u5149\u79fb\u5f00\uff0c\u6b47\u4e86\u4e00\u4f1a\uff0c\u624d\u6562\u518d\u770b\u3002 \u3000\u3000\u90a3\u5c11\u5973\u8eab\u6cd5\u8f7b\u7075\u4e4b\u6781\uff0c\u5200\u67aa\u68cd\u68d2\uff0c\u6709\u5982\u66b4\u98ce\u9aa4\u96e8\uff0c\u5374\u8fde\u5979\u7684\u88d9\u89d2\u90fd\u6cbe\u4e0d\u7740\uff01\u6218\u4e86\u4e00\u9635\uff0c \u90a3\u5c11\u5973\u4e00\u58f0\u53f1\u25a1\uff0c\u5ffd\u5730\u4e00\u638c\u5411\u5de6\u524d\u65b9\u7684\u90a3\u4e2a\u4f7f\u68cd\u68d2\u7684\u58ee\u6c49\u62cd\u53bb\u3002\u53f3\u65b9\u4f7f\u5200\u7684\u6c49\u5b50\uff0c\u5355\u5200\u5377\u5730 \u65a9\u6765\uff0c\u4fa7\u9762\u4f7f\u67aa\u7684\u6c49\u5b50\uff0c\u4e5f\u4e00\u67aa\u6311\u5230\uff0c\u90a3\u4f7f\u68cd\u68d2\u7684\u58ee\u6c49\uff0c\u53ea\u89c9\u5fae\u98ce\u98d2\u7136\uff0c\u654c\u4eba\u624b\u638c\u5df2\u62cd\u5230\u9876 \u95e8\uff0c\u5927\u9a87\u4e4b\u4e0b\uff0c\u5c31\u5730\u4e00\u6eda\uff0c\u5c31\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u5200\u67aa\u9f50\u5230\uff0c\u5c11\u5973\u638c\u5fc3\u5f80\u5916\u4e00\u767b\uff0c\u7adf\u5728\u95f4\u4e0d\u5bb9\u53d1\u4e4b \u9645\uff0c\u81ea\u5200\u67aa\u5939\u51fb\u7f1d\u4e2d\u98de\u8d77\u3002\u90a3\u4f7f\u68cd\u68d2\u7684\u6c49\u5b50\uff0c\u867d\u7136\u8eb2\u95ea\u5f97\u5feb\uff0c\u80a9\u5934\u8fd8\u662f\u7ed9\u638c\u950b\u626b\u4e86\u4e00\u4e0b\uff0c\u6eda \u51fa\u4e86\u6570\u4e08\u4e4b\u9065\uff0c\u624d\u6536\u5f97\u4f4f\u52bf\uff0c\u53c8\u60ca\u53c8\u6012\uff0c\u4e00\u8dc3\u800c\u8d77\uff0c\u5374\u5e78\u6ca1\u6709\u53d7\u4f24\u3002 \u3000\u3000\u8fd9\u4e00\u6765\uff0c\u56db\u6761\u6c49\u5b50\uff0c\u9f50\u90fd\u6c14\u9981\uff0c\u5c11\u5973\u6307\u4e1c\u6253\u897f\u6253\u5357\u6253\u5317\uff0c\u6709\u5982\u884c\u4e91\u6d41\u6c34\uff0c\u66f4\u662f\u6325\u6d12\u81ea \u5982\u3002\u65b9\u5e86\u76ee\u7729\u795e\u6447\uff0c\u6025\u53c8\u628a\u76ee\u5149\u79fb\u5f80\u522b\u5904\uff0c\u5076\u7136\u4e00\u77a5\uff0c\u5ffd\u89c1\u5ca9\u6d1e\u4e4b\u524d\uff0c\u7ad9\u6709\u4e00\u4eba\uff0c\u5f20\u5f13\u6b32 \u5c04\uff0c\u6b64\u4eba\u975e\u4ed6\uff0c\u6b63\u662f\u6628\u65e5\u5192\u5145\u79c0\u624d\uff0c\u5c06\u65b9\u5e86\u94c1\u5f13\u95f0\u65ad\u7684\u5b5f\u7391\u3002\u65b9\u5e86\u5927\u5403\u4e00\u60ca\uff0c\u6025\u5fd9\u53eb\u9053\uff1a \u201c\u6709\u4eba\u6697\u7b97\uff0c\u5c0f\u5fc3\u5440\uff01\u201d\u5f13\u5f26\u4e00\u54cd\uff0c\u5b5f\u7391\u5df2\u55d6\u7684\u53d1\u51fa\u4e00\u7bad\uff01 \u3000\u3000\u767d\u8863\u5c11\u5973\uff0c\u7adf\u4f3c\u6beb\u4e0d\u5728\u610f\uff0c\u628a\u624b\u4e00\u6284\uff0c\u5c31\u5c06\u5c04\u6765\u7684\u5229\u7bad\u6284\u5728\u624b\u4e2d\u3002\u5f13\u5f26\u75be\u54cd\uff0c\u5b5f\u7391\u7684\u7b2c \u4e8c\u7bad\u53c8\u95ea\u7535\u822c\u5c04\u51fa\uff0c\u65b9\u5e86\u662f\u5c04\u7bad\u597d\u624b\uff0c\u770b\u5230\u8fd9\u6837\u5389\u5bb3\u7684\u8fde\u73e0\u7bad\u6cd5\uff0c\u4e5f\u4e0d\u89c9\u9b44\u6563\u9b42\u98de\u3002\u90a3\u5c11\u5973 \u5728\u5200\u67aa\u68cd\u68d2\u56f4\u653b\u4e4b\u4e0b\uff0c\u4e07\u96be\u9003\u907f\uff0c\u4f46\u89c1\u5979\u53cc\u6307\u4e00\u5f39\uff0c\u5c06\u63a5\u5230\u7684\u7bad\u535c\u7684\u5f39\u51fa\uff0c\u4e24\u679d\u7bad\u5728\u534a\u7a7a\u4e2d \u649e\u4e2a\u6b63\u7740\uff0c\u5de6\u53f3\u5206\u98de\uff0c\u4e00\u9f50\u843d\u4e0b\u3002\u8fd9\u5c11\u5973\u7684\u6307\u529b\u7adf\u7136\u654c\u5f97\u4f4f\u5b5f\u7391\u7684\u5f13\u5f26\u4e4b\u529b\uff0c\u5b9e\u662f\u9a87\u4eba\u3002\u5b5f \u7391\u53eb\u58f0\uff1a\u201c\u597d\uff01\u201d\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u7b2c\u4e09\u679d\u7bad\u53c8\u7834\u7a7a\u5c04\u51fa\uff0c\u4e00\u7bad\u5954\u5589\uff0c\u5c04\u4e2a\u6b63\u7740\uff01\u65b9\u5e86\u9a87\u53eb \u4e00\u58f0\uff0c\u5ffd\u89c1\u90a3\u5c11\u5973\u5f20\u53e3\u4e00\u5410\uff0c\u5c06\u90a3\u679d\u7bad\u5410\u4e86\u51fa\u53bb\u3002\u539f\u6765\u5979\u7528\u7684\u7adf\u662f\u63a5\u7bad\u6cd5\u4e2d\u6700\u96be\u7ec3\u3001\u6700\u5192\u9669 \u7684\u201c\u556e\u7c07\u6cd5\u201d\uff01 \u3000\u3000\u767d\u8863\u5c11\u5973\u7ed9\u5b5f\u7391\u8fde\u5c04\u4e09\u7bad\uff0c\u9762\u6709\u6012\u5bb9\uff0c\u5ffd\u7136\u53eb\u9053\uff1a\u201c\u6765\u800c\u4e0d\u5f80\u975e\u793c\u4e5f\uff01\u201d\u7389\u624b\u4e00\u626c\uff0c\u4f46 \u89c1\u4e94\u516d\u6735\u6885\u82b1\u5f62\u7684\u6697\u5668\uff0c\u6563\u5e03\u7a7a\u4e2d\uff0c\u56db\u9762\u98de\u4e0b\u3002\u6b63\u662f\uff1a \u3000\u3000\u98de\u82b1\u8fce\u5927\u654c\uff0c\u51fa\u624b\u89c1\u795e\u5947\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u840d\u8e2a\u4fa0\u5f71\u5f55\u300b\u2014\u2014\u7b2c\u4e8c\u56de\u3000\u7978\u798f\u96be\u77e5\u5355\u8eab\u5165\u864e\u7a74\u3000\u53cb\u4ec7\u83ab\u6d4b\u5b9d\u5251\u5bf9\u91d1\u5200 \u6881\u7fbd\u751f\u300a\u840d\u8e2a\u4fa0\u5f71\u5f55\u300b \u7b2c\u4e8c\u56de\u3000\u7978\u798f\u96be\u77e5\u5355\u8eab\u5165\u864e\u7a74\u3000\u53cb\u4ec7\u83ab\u6d4b\u5b9d\u5251\u5bf9\u91d1\u5200 \u3000\u3000\u65b9\u5e86\u8fd8\u672a\u770b\u5f97\u6e05\u695a\uff0c\u4f46\u542c\u5f97\u54ce\u54df\u8fde\u58f0\uff0c\u9664\u4e86\u5b5f\u7391\u4e4b\u5916\uff0c\u56f4\u653b\u767d\u8863\u5c11\u5973\u7684\u90a3\u56db\u6761\u6c49\u5b50\uff0c\u90fd \u5df2\u5012\u5728\u5730\u4e0a\u3002\u5b5f\u7391\u95ea\u5f00\u4e86\u4e24\u679a\u6885\u82b1\u6697\u5668\uff0c\u5927\u58f0\u8d5e\u9053\uff1a\u201c\u6563\u82b1\u5973\u4fa0\uff01\u540d\u4e0d\u865a\u4f20\uff01\u201d\u4e00\u8a00\u752b\u6bd5\uff0c \u90a3\u56db\u6761\u6c49\u5b50\uff0c\u4e5f\u90fd\u8df3\u4e86\u8d77\u6765\uff0c\u5404\u4eba\u624b\u4e0a\u62c8\u7740\u4e00\u679a\u6697\u5668\uff0c\u540c\u58f0\u8bf4\uff1a\u201c\u591a\u8c22\u5973\u4fa0\u624b\u4e0b\u7559\u60c5\uff0c\u54b1\u4eec \u670d\u4e86\uff01\u201d\u539f\u6765\u90a3\u56db\u4eba\u90fd\u88ab\u5c11\u5973\u7528\u90a3\u201c\u5929\u5973\u6563\u82b1\u201d\u7684\u624b\u6cd5\uff0c\u6253\u4e2d\u7a74\u9053\uff0c\u6697\u5668\u6765\u52bf\u6781\u6025\uff0c\u89e6\u4f53\u5374 \u8f7b\uff0c\u6253\u4e2d\u7a74\u9053\uff0c\u4e5f\u53ea\u662f\u4e00\u9635\u9178\u9ebb\uff0c\u5e76\u65e0\u788d\u5904\uff0c\u8fd9\u660e\u660e\u662f\u767d\u8863\u5c11\u5973\u6545\u610f\u76f8\u8ba9\u3002 \u3000\u3000\u767d\u8863\u5c11\u5973\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u539f\u6765\u4f60\u4eec\u53bb\u63a2\u542c\u4e86\u6211\u7684\u6765\u5386\uff0c\u90a3\u4e48\u8fd9\u4f4d\u670b\u53cb\u7684\u94f6\u5b50\uff0c\u53ef\u4ee5\u5f52 \u8fd8\u4e86\u5427\uff1f\u201d\u5b5f\u7391\u4e00\u6307\u8fd9\u5ca9\u6d1e\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u6765\u5f97\u4e0d\u5de7\uff0c\u94f6\u5b50\u4eca\u65e9\u5df2\u642c\u8d70\u4e86\u3002\u201d\u5c11\u5973\u9762\u8272\u4e00\u6c89\uff0c \u6b63\u5f85\u53d1\u8bdd\uff0c\u5b5f\u7391\u53c8\u9053\uff1a\u201c\u8981\u52b3\u4f60\u591a\u8d70\u4e00\u8d9f\u4e86\uff0c\u6211\u4eec\u5df2\u5907\u4e0b\u5feb\u9a6c\u3002\u65b9\u5927\u4eba\uff0c\u4f60\u6628\u665a\u53d7\u60ca\u4e86\u3002\u201d \u65b9\u5e86\u6ee1\u9762\u901a\u7ea2\u3002\u5c11\u5973\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u6211\u5c31\u53bb\u62dc\u89c1\u4f60\u5bb6\u5be8\u4e3b\u3002\u597d\uff0c\u54b1\u4eec\u8d70\u5427\uff01\u201d \u3000\u3000\u5b5f\u7391\u64ae\u5507\u4e00\u5578\uff0c\u5c71\u5ca9\u540e\u6709\u4eba\u7275\u51fa\u51e0\u5339\u9a6c\u6765\uff0c\u767d\u8863\u5c11\u5973\u8df3\u4e0a\u9a6c\u80cc\uff0c\u4e00\u8a00\u4e0d\u53d1\uff0c\u968f\u7740\u4ed6\u4eec\u4fbf \u8dd1\u3002\u5c71\u9053\u5d0e\u5c96\uff0c\u5c71\u5761\u503e\u9661\uff0c\u9a91\u5728\u9a6c\u80cc\u4e4b\u4e0a\uff0c\u5c31\u5982\u817e\u4e91\u9a7e\u96fe\u4e00\u822c\uff0c\u65b9\u5e86\u867d\u662f\u5f13\u9a6c\u4e16\u5bb6\uff0c\u4e5f\u89c9\u60ca \u5fc3\u52a8\u9b44\uff0c\u90a3\u51e0\u5339\u9a6c\u90fd\u662f\u4e45\u7ecf\u8bad\u7ec3\u7684\u6218\u9a6c\uff0c\u968f\u7740\u5b5f\u7391\u90a3\u5339\u9886\u5934\u7684\u5750\u9a91\uff0c\u767b\u5c71\u8df3\u6da7\uff0c\u7adf\u7136\u5982\u8d70\u5e73 \u5730\u3002 \u3000\u3000\u8dd1\u4e86\u4e2a\u591a\u65f6\u523b\uff0c\u7ea2\u65e5\u5df2\u5230\u4e2d\u5929\uff0c\u5b5f\u7391\u5728\u9a6c\u80cc\u4e0a\u626c\u97ad\u6307\u9053\uff1a\u201c\u4e0b\u9762\u4fbf\u662f\u96c1\u95e8\u5173\u4e86\uff0c\u4e01\u5927\u603b \u5175\u660e\u5929\u4fbf\u7b49\u7740\u8981\u53d1\u519b\u9977\uff0c\u8fd9\u4f1a\u513f\u6b63\u4e0d\u77e5\u591a\u5fc3\u7126\u4e86\uff01\u201d\u65b9\u5e86\u95fb\u8a00\u4e00\u60ca\uff0c\u95ee\u9053\uff1a\u201c\u6211\u4eec\u5df2\u8fc7\u4e86\u96c1 \u95e8\u5173\u5417\uff1f\u4f60\u3001\u4f60\u4eec\u662f\u4e0d\u662f\u65e5\u6708\u65d7\u91d1\u5200\u5be8\u4e3b\u7684\u624b\u4e0b\uff1f\u201d\u5b5f\u7391\u9053\u8a00\uff1a\u201c\u6709\u4f60\u7684\u94f6\u5b50\u4fbf\u662f\uff0c\u4f55\u5fc5\u591a \u95ee\uff01\u201d\u65b9\u5e86\u5fc3\u5982\u540a\u6876\uff0c\u4e03\u4e0a\u516b\u843d\uff0c\u60f3\u9053\uff1a\u201c\u8fd9\u91d1\u5200\u8001\u8d3c\uff0c\u4ece\u6765\u4e0d\u52ab\u519b\u9977\uff0c\u4e0d\u77e5\u4f55\u4ee5\u4eca\u756a\u7834 \u4f8b\uff1f\u4e45\u95fb\u91d1\u5200\u8001\u8d3c\u662f\u4e2a\u5929\u4e0d\u6015\u5730\u4e0d\u6015\u7684\u5927\u5f3a\u4eba\uff0c\u8499\u53e4\u9791\u5b50\u548c\u5927\u660e\u5b98\u5175\u90fd\u4e0d\u6562\u634b\u4ed6\u864e\u987b\uff0c\u82e5\u662f \u4ed6\u7acb\u5fc3\u8981\u8fd9\u519b\u9977\uff0c\u8d77\u5c3d\u5341\u4e07\u5b98\u519b\uff0c\u4e5f\u672a\u5fc5\u8ba8\u5f97\u56de\u6765\uff0c\u6b64\u4e00\u53bb\u4e5f\uff0c\u53ea\u6050\u51f6\u591a\u5409\u5c11\u4e86\u3002\u201d \u3000\u3000\u9a6c\u884c\u4e00\u523b\uff0c\u9762\u524d\u5ffd\u89c1\u4e00\u7247\u5f00\u9614\uff0c\u5c71\u5c97\u56f4\u62b1\u4e4b\u4e2d\uff0c\u7adf\u662f\u6c83\u91ce\u5e73\u7574\uff0c\u6709\u4eba\u5728\u7530\u4e2d\u8015\u4f5c\uff0c\u521d\u521d \u770b\u5230\uff0c\u8fd8\u7591\u662f\u4e16\u5916\u6843\u6e90\uff0c\u54ea\u60f3\u5f97\u5230\u8fd9\u7adf\u662f\u5a01\u9707\u80e1\u6c49\u7684\u5f3a\u4eba\u5de2\u7a74\uff1f\u9a6c\u961f\u5728\u78e8\u76d8\u4f3c\u7684\u5c71\u9053\u8fc2\u56de\u524d \u8fdb\u7740\uff0c\u5c71\u9053\u4e24\u65c1\uff0c\u4e0d\u65f6\u95ea\u51fa\u4eba\u5f71\uff0c\u6253\u7740\u65d7\u53f7\uff0c\u6ca1\u591a\u4e45\uff0c\u5c31\u5230\u4e86\u5c71\u5be8\u524d\u9762\u3002 \u3000\u3000\u5c71\u4e0a\u7889\u5821\u8fde\u4e91\uff0c\u4f9d\u7740\u5c71\u5f62\uff0c\u4e92\u4e3a\u5c4f\u969c\uff0c\u7aef\u7684\u6c14\u8c61\u4e07\u5343\u3002\u65b9\u5e86\u5fe7\u5fc3\u5fe1\u5fe1\uff0c\u8ddf\u5728\u5b5f\u7391\u4e0e\u5c11\u5973 \u4e4b\u540e\uff0c\u4e0b\u9a6c\u8fdb\u5c71\u3002\u6709\u4eba\u5f15\u5230\u5927\u5be8\u9762\u524d\uff0c\u53ea\u542c\u5f97\u949f\u58f0\u5f53\u5f53\u5de8\u54cd\uff0c\u63a5\u7740\u9f13\u89d2\u9f50\u9e23\uff0c\u5be8\u95e8\u5f00\u5904\uff0c\u4e24 \u961f\u5f3a\u4eba\u5217\u9635\u76f8\u8fce\uff0c\u5200\u67aa\u5982\u96ea\uff0c\u7532\u80c4\u9c9c\u660e\uff0c\u767d\u8863\u5c11\u5973\u9762\u6709\u7b11\u5bb9\uff0c\u82e5\u65e0\u5176\u4e8b\u5730\u4ece\u5200\u67aa\u5251\u621f\u4e1b\u4e2d\u7a7f \u8fc7\uff0c\u65b9\u5e86\u89c1\u8fd9\u9635\u4ed7\uff0c\u5413\u5f97\u77ed\u4e86\u534a\u622a\u5b50\uff0c\u786c\u7740\u5934\u76ae\uff0c\u4ea6\u6b65\u4ea6\u8d8b\u5730\u968f\u7740\u767d\u8863\u5c11\u5973\u8d70\u4e0a\u4e2d\u5802\u3002 \u3000\u3000\u5927\u5802\u4e0a\u6446\u597d\u864e\u76ae\u4ea4\u6905\uff0c\u5374\u662f\u65e0\u4eba\u76f8\u5019\uff0c\u767d\u8863\u5c11\u5973\u9762\u8272\u5fae\u6120\u95ee\u9053\uff1a\u201c\u4f60\u4eec\u7684\u8001\u5be8\u4e3b\u5462\uff1f\u201d \u5b5f\u7391\u5fae\u5fae\u4e00\u7b11\uff0c\u53ea\u89c1\u4e24\u4e2a\u7c97\u8c6a\u5927\u6c49\uff0c\u63ed\u5f00\u864e\u5e10\uff0c\u76f4\u95ef\u5165\u6765\u3002 \u3000\u3000\u524d\u9762\u90a3\u6761\u5927\u6c49\u6367\u7740\u4e00\u4e2a\u5927\u9152\u7f38\uff0c\u91d1\u8272\u707f\u7136\uff0c\u60f3\u662f\u9ec4\u94dc\u505a\u6210\u7684\uff0c\u77a7\u90a3\u6837\u5b50\uff0c\u6015\u4e0d\u6709\u4e94\u4e03\u5341 \u65a4\uff1f\u540e\u9762\u90a3\u6761\u6c49\u5b50\uff0c\u5374\u6367\u7740\u4e00\u5927\u76d8\u70e4\u719f\u7684\u725b\u8089\uff0c\u70ed\u6c14\u817e\u817e\uff0c\u6bcf\u5757\u725b\u8089\u4e0a\u90fd\u63d2\u7740\u4e00\u67c4\u660e\u6643\u6643\u7684 \u5229\u5203\u3002\u4e24\u4e2a\u6c49\u5b50\u5531\u4e86\u4e00\u4e2a\u80a5\u558f\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u8d35\u5ba2\u8fdc\u6765\uff0c\u65e0\u7269\u62db\u5f85\uff0c\u8bf7\u559d\u4e00\u676f\u6c34\u9152\u5427\u3002\u201d\u4e00 \u8a00\u672a\u4e86\uff0c\u524d\u9762\u90a3\u6761\u6c49\u5b50\u53cc\u81c2\u4e00\u632f\uff0c\u4e00\u5927\u7f38\u9152\u5288\u9762\u63b7\u4e86\u8fc7\u6765\u3002\u767d\u8863\u5c11\u5973\u9762\u4e0d\u6539\u5bb9\uff0c\u53e3\u4e2d\u8c22\u9053\uff1a \u201c\u4f55\u5fc5\u5ba2\u6c14\uff1f\u201d\u624b\u81c2\u4e00\u5f2f\uff0c\u5728\u90a3\u9152\u7f38\u65c1\u8fb9\u4e00\u5e26\uff0c\u90a3\u9152\u7f38\u7adf\u8d34\u7740\u5979\u7684\u638c\u5fc3\u6ef4\u6e9c\u6e9c\u5730\u8f6c\u4e2a\u4e0d\u505c\uff0c \u4e5f\u4e0d\u843d\u4e0b\uff0c\u7adf\u5982\u5c0f\u5b69\u5b50\u73a9\u7684\u9640\u87ba\u4e00\u822c\u4f3c\u7684\u3002\u8fd9\u4e00\u7f38\u9152\u88ab\u90a3\u6c49\u5b50\u4f7f\u529b\u4e00\u63b7\uff0c\u5a01\u52bf\u4f55\u7b49\u60ca\u4eba\uff0c\u6ca1 \u6709\u4e09\u4e94\u767e\u65a4\u529b\u6c14\uff0c\u4e5f\u4f11\u60f3\u63a5\u5f97\u5b83\u4f4f\uff0c\u5374\u4e0d\u6599\u88ab\u8fd9\u5c11\u5973\u8f7b\u8f7b\u4e00\u5e26\uff0c\u628a\u90a3\u80a1\u5288\u9762\u63b7\u6765\u7684\u52b2\u529b\uff0c\u5316 \u89e3\u4e8e\u65e0\u5f62\u3002\u5c11\u5973\u5fae\u5fae\u4e00\u7b11\uff0c\u4fef\u9996\u7f38\u8fb9\uff0c\u559d\u4e86\u4e00\u5927\u53e3\u9152\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u9152\uff0c\u597d\u9152\uff01\u201d\u90a3\u4e24\u4e2a\u6c49\u5b50 \u6014\u4e86\u6014\uff0c\u540e\u9762\u7684\u90a3\u4e2a\u6c49\u5b50\u62a2\u4e0a\u4e24\u6b65\uff0c\u559d\u9053\uff1a\u201c\u8fd9\u4e2a\u7ed9\u4f60\u9001\u9152\uff01\u201d\u89c1\u624b\u8d77\u5904\uff0c\u4e24\u67c4\u63d2\u7740\u725b\u8089\u7684 \u5315\u9996\u98de\u4e86\u8fc7\u6765\uff0c\u767d\u8863\u5c11\u5973\u53c8\u662f\u5fae\u5fae\u4e00\u7b11\uff0c\u6a31\u6843\u5c0f\u5634\u4e00\u5f20\uff0c\u201c\u5580\u5693\u201d\u4e00\u58f0\uff0c\u628a\u4e24\u67c4\u5315\u9996\uff0c\u54ac\u5728 \u53e3\u4e2d\uff0c\u5f20\u53e3\u4e00\u5410\uff0c\u4e24\u4e19\u5315\u9996\u4e00\u9f50\u98de\u51fa\uff0c\u7aef\u7aef\u6b63\u6b63\u5730\u5e76\u63d2\u5728\u5927\u6881\u4e4b\u4e0a\uff0c\u4e24\u6761\u5927\u6c49\u76f8\u987e\u5931\u8272\u3002\u53ea \u89c1\u90a3\u5c11\u5973\u7709\u6bdb\u4e00\u626c\uff0c\u559d\u9053\uff1a\u201c\u8fd8\u656c\u4f60\u4eec\u4e00\u676f\u9152\uff01\u201d\u638c\u5fc3\u5f80\u5916\u4e00\u767b\uff0c\u547c\u7684\u4e00\u58f0\uff0c\u628a\u5927\u9152\u7f38\u53cd\u63a8 \u51fa\u53bb\uff0c\u90a3\u4e24\u6761\u6c49\u5b50\u5c82\u6562\u76f8\u63a5\uff0c\u773c\u770b\u9152\u7f38\u5288\u9762\u63b7\u6765\uff0c\u907f\u5df2\u4e0d\u53ca\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c\u53ea\u89c1\u4e00\u4e2a\u5c11\u5e74\u6c49\u5b50\u4ece\u540e\u5802\u98de\u6b65\u5954\u51fa\u4e00\u638c\u62cd\u51fa\uff0c\u628a\u90a3\u5927\u9152\u7f38\u62cd\u5f97\u98de \u8fc7\u4e00\u8fb9\uff0c\u5316\u4e86\u6765\u52bf\uff0c\u5de6\u8db3\u4e00\u5e26\uff0c\u7f38\u9152\u7f13\u7f13\u843d\u5728\u5730\u4e0a\uff0c\u4e00\u5927\u7f38\u9152\uff0c\u6ca1\u6709\u6ea2\u51fa\u534a\u70b9\u3002\u8fd9\u5c11\u5e74\u663e\u4e86 \u8fd9\u624b\u529f\u592b\u4e4b\u540e\uff0c\u56de\u5934\u65a5\u9053\uff1a\u201c\u4f60\u4eec\u8fd9\u4e24\u4e2a\u8822\u7269\uff0c\u656c\u5ba2\u4e5f\u4e0d\u61c2\u5f97\uff0c\u8fd8\u5728\u8fd9\u91cc\u4e22\u4eba\u73b0\u773c\u4e48\uff1f\u201d\u5411 \u5c11\u5973\u62b1\u62f3\u4e00\u62f1\uff0c\u9053\uff1a\u201c\u5f85\u6162\u5973\u4fa0\uff0c\u6055\u7f6a\uff0c\u6055\u7f6a\uff01\u201d\u65b9\u5e86\u4e00\u770b\uff0c\u5413\u5f97\u51e0\u4e4e\u53eb\u51fa\u58f0\u6765\uff0c\u8fd9\u5c11\u5e74\u4e0d \u662f\u522b\u4eba\uff0c\u6b63\u662f\u6628\u665a\u6551\u4e86\u4ed6\u7684\u6027\u547d\uff0c\u53c8\u6307\u70b9\u4ed6\u53bb\u627e\u767d\u8863\u5c11\u5973\u7684\u90a3\u4e2a\u5c11\u5e74\u3002\u53ea\u662f\u6628\u665a\u4ed6\u4e43\u662f\u5c71\u91ce \u6a35\u592b\u6253\u626e\uff0c\u800c\u4eca\u5374\u662f\u8f7b\u88d8\u7f13\u5e26\uff0c\u4fe8\u82e5\u6d4a\u4e16\u4e2d\u7684\u7fe9\u7fe9\u516c\u5b50\uff0c\u6c14\u5ea6\u81ea\u662f\u4e0d\u51e1\u3002 \u3000\u3000\u767d\u8863\u5c11\u5973\u8fd8\u4e86\u4e00\u63d6\uff0c\u9053\uff1a\u201c\u516c\u5b50\u597d\u4fca\u7684\u529f\u592b\uff01\u201d\u542c\u5f97\u90a3\u4e2a\u6c49\u5b50\u51fa\u95e8\u4e4b\u65f6\uff0c\u5782\u624b\u53eb\u4ed6\u505a \u201c\u5c11\u5be8\u4e3b\u201d\uff0c\u53c8\u7b11\u9053\uff1a\u201c\u8fd9\u56de\u53ef\u627e\u7740\u6b63\u4e3b\u4e86\uff0c\u8fd9\u4f4d\u670b\u53cb\u7684\u56db\u5341\u4e07\u4e24\u94f6\u5b50\uff0c\u8bf7\u5c11\u5be8\u4e3b\u8d4f\u9762\u8d50 \u8fd8\u3002\u201d\u90a3\u5c11\u5e74\u9053\uff1a\u201c\u4e9b\u987b\u94f6\u5b50\uff0c\u4f55\u8db3\u6302\u9f7f\uff0c\u59d1\u5a18\uff0c\u4f60\u4e14\u8bf7\u5750\u3002\u201d\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u6765\u4eba\u54ea\uff01\u201d\u773c \u5149\u4e00\u8f6c\uff0c\u5411\u65b9\u5e86\u6253\u4e86\u4e00\u4e2a\u62db\u547c\uff0c\u773c\u8272\u4e4b\u95f4\uff0c\u542b\u7740\u8be1\u79d8\u7684\u795e\u60c5\uff0c\u4f3c\u4e4e\u662f\u5728\u8bf4\u9053\uff1a\u201c\u6211\u7684\u6307\u70b9\u4e0d \u9519\u5427\uff01\u201d \u3000\u3000\u65b9\u5e86\u5446\u5728\u4e00\u8fb9\uff0c\u6ee1\u8179\u7591\u4e91\uff0c\u5b9e\u662f\u767e\u601d\u4e0d\u5f97\u5176\u89e3\u3002\u8fd9\u5c11\u5e74\u65e2\u7136\u662f\u8fd9\u91cc\u7684\u5c11\u5be8\u4e3b\uff0c\u4f55\u4ee5\u52ab\u4e86 \u94f6\u4e24\uff0c\u5374\u53c8\u6253\u6551\u81ea\u5df1\uff1f\u8fd8\u628a\u90a3\u767d\u8863\u5c11\u5973\u4e5f\u5f15\u5230\u8fd9\u513f\uff1f\u83ab\u975e\u8fd9\u662f\u9677\u654c\u4e4b\u8ba1\uff1f\u8eab\u5728\u9f99\u6f6d\u864e\u7a74\u4e4b \u4e2d\uff0c\u5e10\u5916\u5f3a\u4eba\u73af\u4f3a\uff0c\u5409\u8282\u96be\u6d4b\uff0c\u7978\u798f\u672a\u77e5\uff0c\u60ca\u7591\u4ea4\u5e76\uff0c\u542c\u90a3\u5e10\u5916\u5200\u73af\u6296\u7d22\u4e4b\u58f0\uff0c\u4e0d\u7981\u6bdb\u9aa8\u609a \u7136\u3002 \u3000\u3000\u8fc7\u4e86\u7247\u523b\uff0c\u53ea\u89c1\u4e00\u961f\u5f3a\u76d7\uff0c\u628a\u52ab\u53bb\u7684\u94f6\u9798\u90fd\u642c\u4e86\u5165\u6765\uff0c\u5806\u6ee1\u9636\u4e0b\u3002\u767d\u8863\u5c11\u5973\u9053\uff1a\u201c\u5c11\u5be8 \u4e3b\u679c\u662f\u5feb\u4eba\uff0c\u6211\u591a\u8c22\u4e86\uff01\u201d\u90a3\u5c11\u5e74\u5ffd\u7136\u4e00\u58f0\u957f\u7b11\uff0c\u5f20\u624b\u8bf4\u9053\uff1a\u201c\u4e14\u6162\uff01\u201d \u3000\u3000\u767d\u8863\u5c11\u5973\u4e00\u6115\uff0c\u53ea\u89c1\u4e00\u540d\u76d7\u515a\uff0c\u5728\u94f6\u9798\u5806\u4e0a\uff0c\u63d2\u4e0a\u4e00\u9762\u65d7\u5e1c\uff0c\u4e00\u9762\u753b\u7740\u5706\u5706\u7684\u7ea2\u65e5\uff0c\u53e6 \u4e00\u9762\u5374\u753b\u7740\u4e00\u94a9\u65b0\u6708\uff0c\u8fd9\u65e5\u6708\u53cc\u65d7\uff0c\u6b63\u662f\u5c71\u5be8\u7684\u65d7\u53f7\u3002\u90a3\u5c11\u5e74\u5fae\u5fae\u4e00\u7b11\uff0c\u5728\u684c\u4e0a\u63d0\u8d77\u4e00\u4e2a\u94f6 \u8d28\u7684\u5c0f\u9152\u58f6\uff0c\u659f\u4e86\u4e24\u676f\u9152\uff0c\u81ea\u5df1\u5148\u559d\u4e86\u4e00\u676f\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u56db\u5341\u4e07\u4e24\u94f6\u5b50\u867d\u662f\u65e0\u8db3\u6302\u9f7f\uff0c\u4f46\u8fd9 \u9762\u76ee\u6708\u65d7\u5374\u662f\u4ef7\u503c\u8fde\u57ce\uff01\u201d\u767d\u8863\u5c11\u5973\u773c\u6ce2\u6d41\u8f6c\uff0c\u53ea\u89c1\u6ee1\u5802\u76d7\u515a\uff0c\u795e\u60c5\u8083\u7136\uff0c\u90fd\u6ce8\u671b\u7740\u81ea\u5df1\uff0c \u751a\u662f\u4e0d\u89e3\uff0c\u4e0d\u7531\u5f97\u5e03\u9732\u51fa\u7591\u60d1\u7684\u795e\u8272\uff0c\u8be7\u7136\u95ee\u9053\uff1a\u201c\u4f60\u8bf4\u7684\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d\u90a3\u5c11\u5e74\u5e76\u4e0d\u7b54 \u8bdd\uff0c\u53ea\u662f\u5fae\u7b11\uff0c\u767d\u8863\u5c11\u5973\u60f3\u4e86\u4e00\u60f3\u9053\uff1a\u201c\u54e6\uff0c\u8fd9\u4e24\u9762\u65d7\u662f\u4f60\u4eec\u7684\u65d7\u53f7\uff0c\u90a3\u786e\u4e4e\u662f\u4e07\u91d1\u4e0d\u6362\u7684 \u4e1c\u897f\u4e86\u3002\u4f46\u8fd9\u548c\u6211\u4eec\u7684\u4e8b\u53c8\u6709\u4ec0\u4e48\u5173\u7cfb\uff1f\u201d\u90a3\u5c11\u5e74\u4ecd\u7136\u5fae\u7b11\u4e0d\u7b54\uff0c\u9636\u4e0b\u7684\u76d7\u515a\u5374\u4e2a\u4e2a\u73b0\u51fa\u6012 \u5bb9\u3002 \u3000\u3000\u65b9\u5e86\u5728\u65c1\u8fb9\u770b\u5f97\u6697\u6697\u53eb\u82e6\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u8fd9\u5973\u5b50\u6b66\u529f\u867d\u7136\u9ad8\u5f3a\uff0c\u5374\u539f\u6765\u662f\u4e00\u4e2a\u521d\u51fa\u9053\u7684 \u5c0f\u96cf\u513f\uff0c\u7adf\u7136\u8fde\u8fd9\u70b9\u9ed1\u9053\u4e0a\u7684\u89c4\u77e9\u90fd\u4e0d\u61c2\u5f97\uff01\u76d7\u515a\u5728\u94f6\u9798\u4e0a\u63d2\u4e86\u65d7\u53f7\uff0c\u8fd9\u610f\u601d\u5c31\u662f\u8bf4\uff0c\u4f60\u82e5 \u6709\u672c\u4e8b\u628a\u8fd9\u4e24\u679d\u65d7\u62d4\u4e0b\uff0c\u94f6\u5b50\u4fbf\u53ef\u62ff\u53bb\uff0c\u8981\u4e0d\u7136\uff0c\u4f60\u5c31\u5f97\u4e56\u4e56\u9000\u51fa\u3002\u8fd9\u5206\u660e\u662f\u9080\u6597\u7684\u610f\u601d\uff01 \u8fd9\u56de\u771f\u4e2a\u662f\u51f6\u591a\u5409\u5c11\u4e86\uff01\u201d \u3000\u3000\u767d\u8863\u5c11\u5973\u95ee\u4e86\u4e24\u6b21\uff0c\u672a\u89c1\u56de\u7b54\uff0c\u5fae\u5e26\u7a1a\u6c14\u7684\u8138\u4e0a\u6655\u8d77\u4e00\u5c42\u7ea2\u6f6e\uff0c\u4f3c\u4e4e\u5df2\u6709\u70b9\u6120\u6012\u4e86\uff0c\u4f46 \u89c1\u5979\u67f3\u7709\u4e00\u7ad6\uff0c\u7ad9\u4e86\u8d77\u6765\uff0c\u5bf9\u65b9\u5e86\u62db\u624b\u9053\uff1a\u201c\u94f6\u5b50\u5df2\u5728\u8fd9\u513f\uff0c\u4f60\u8fd8\u4e0d\u53bb\u70b9\u70b9\uff1f\u65d7\u5b50\u662f\u4ed6\u4eec \u7684\uff0c\u4f60\u7559\u4e0b\u6765\u597d\u4e86\u3002\u201d\u8eab\u5b50\u4e00\u632a\uff0c\u521a\u521a\u8de8\u51fa\u534a\u6b65\uff0c\u5ffd\u542c\u5f97\u90a3\u5c11\u5e74\u54c8\u54c8\u4e00\u7b11\uff0c\u63d0\u7740\u9152\u58f6\uff0c\u8eab\u5f62 \u75be\u8d77\uff0c\u6070\u6070\u6321\u5728\u5979\u9762\u524d\uff0c\u6717\u7136\u8bf4\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4f60\u8fd8\u662f\u5750\u4e0b\u6765\u559d\u9152\u5427\uff01\u201d\u767d\u8863\u5c11\u5973\u6012\u9053\uff1a\u201c\u6211 \u4e0d\u559d\u9152\u8c01\u6562\u5f3a\u6211\u559d\u9152\uff1f\u201d\u811a\u6b65\u5411\u524d\u8fc8\u51fa\u3002\u90a3\u5c11\u5e74\u9152\u58f6\u5411\u524d\u4e00\u63a8\uff0c\u5de6\u624b\u4e3e\u8d77\u676f\u5b50\u4e00\u5149\uff0c\u9053\uff1a \u201c\u8fd9\u70b9\u9762\u5b50\u90fd\u4e0d\u7ed9\u5417\uff1f\u201d\u9152\u58f6\u5288\u80f8\uff0c\u9152\u676f\u7167\u9762\uff0c\u7adf\u7136\u662f\u4e24\u8bb0\u6781\u5389\u5bb3\u7684\u62db\uff0c\u4f46\u89c1\u90a3\u5c11\u5973\u8eab\u5f62\u4e00 \u8f6c\uff0c\u5c11\u5e74\u6251\u4e86\u4e2a\u7a7a\uff0c\u9152\u676f\u843d\u624b\u98de\u51fa\uff0c\u25a1\u25a1\u4e00\u58f0\uff0c\u788e\u6210\u51e0\u7247\u3002\u539f\u6765\u662f\u7ed9\u5c11\u5973\u7528\u8fc5\u96f7\u4e0d\u53ca\u63a9\u8033\u7684 \u624b\u6cd5\uff0c\u649e\u4e86\u4e00\u4e0b\u3002\u90a3\u5c11\u5e74\u4e5f\u771f\u4e86\u5f97\uff0c\u9152\u58f6\u4e00\u6643\uff0c\u8f6c\u8eab\u4e00\u63a8\uff0c\u53c8\u6321\u4f4f\u4e86\u5c11\u5973\u7684\u53bb\u8def\uff0c\u9152\u58f6\u7684\u5c16 \u5634\uff0c\u6307\u7740\u5c11\u5973\u63ff\u4e0b\u7684\u4e73\u7a81\u7a74\u3002\u767d\u8863\u5c11\u5973\u731b\u7136\u4e00\u77ee\u8eab\u8eaf\uff0c\u53cc\u6307\u4e00\u5f39\uff0c\u638c\u5fc3\u4e00\u5e26\uff0c\u4f46\u89c1\u58f6\u76d6\u98de \u5f00\uff0c\u4e00\u58f6\u9152\u90fd\u6cfc\u4e86\u51fa\u6765\u6e85\u4e86\u6ee1\u5730\uff0c\u9152\u9999\u6251\u9f3b\uff0c\u6ee1\u5802\u5931\u8272\uff01\u4f46\u90a3\u9152\u58f6\u5374\u8fd8\u7d27\u63e1\u5728\u5c11\u5e74\u624b\u4e2d\u3002 \u3000\u3000\u4e24\u4eba\u4ea4\u6362\u4e86\u8fd9\u4e24\u62db\uff0c\u663e\u7136\u662f\u767d\u8863\u5c11\u5973\u6280\u80dc\u4e00\u7b79\uff0c\u4f46\u8fd0\u8db3\u5185\u529b\uff0c\u5374\u4e5f\u6ca1\u80fd\u5c06\u9152\u58f6\u51fb\u98de\uff0c\u5c11 \u5e74\u6b66\u529f\uff0c\u663e\u7136\u4ea6\u975e\u5f31\u8005\u3002\u4ed6\u7adf\u5c06\u9152\u58f6\u5f53\u6210\u5175\u5668\uff0c\u811a\u8ddf\u4e00\u65cb\uff0c\u53c8\u8f6c\u5230\u4e86\u5c11\u5973\u7684\u9762\u524d\uff0c\u8bf4\u9053\uff1a \u201c\u8fd9\u676f\u9152\u65e0\u6ca6\u5982\u4f55\u8bf7\u4f60\u8d4f\u9762\u3002\u201d\u7528\u7684\u7adf\u662f\u6d41\u661f\u9524\u4e2d\u201c\u6d41\u661f\u8d76\u6708\u201d\u7684\u62db\u6570\u3002\u767d\u8863\u5c11\u5973\u659c\u95ea\u4e24 \u6b65\uff0c\u67f3\u7709\u76f4\u7ad6\uff0c\u674f\u8138\u542b\u55d4\uff0c\u970d\u7684\u4e00\u58f0\uff0c\u62d4\u51fa\u5b9d\u5251\uff0c\u4f46\u89c1\u4e00\u7f15\u5bd2\u5149\uff0c\u8131\u5323\u5c04\u51fa\uff0c\u5c11\u5e74\u4e5f\u9000\u4e86\u4e24 \u6b65\uff0c\u9152\u58f6\u63a9\u80f8\uff0c\u5c01\u7d27\u95e8\u6237\u3002\u767d\u8863\u5c11\u5973\u5251\u5c16\u4e00\u6307\uff0c\u559d\u9053\uff1a\u201c\u4f60\u597d\u65e0\u793c\uff0c\u54b1\u4eec\u6bd4\u5212\u6bd4\u5212\uff01\u201d\u6ee1\u5802 \u76d7\u515a\u500f\u5730\u4e00\u4e0b\u9000\u5230\u56db\u8fb9\uff0c\u770b\u662f\u817e\u51fa\u5730\u65b9\u8ba9\u4ed6\u4eec\u4e8c\u4eba\u52a8\u624b\uff0c\u5b9e\u5219\u5e03\u6210\u4e86\u5408\u56f4\u4e4b\u9635\uff0c\u53ea\u8981\u5c11\u5e74\u4e00 \u4e2a\u4e0d\u654c\uff0c\u7acb\u523b\u5c31\u8981\u7fa4\u8d77\u56f4\u653b\uff01 \u3000\u3000\u65b9\u5e86\u5413\u5f97\u5fc3\u60ca\u80c6\u6218\uff0c\u9762\u5982\u6b7b\u7070\uff0c\u5fc3\u60f3\u8fd9\u5c11\u5973\u7eb5\u6709\u5929\u5927\u7684\u795e\u901a\uff0c\u4ea6\u96be\u95ef\u51fa\u9f99\u6f6d\u864e\u7a74\uff0c\u5f85\u4f1a \u76d7\u515a\u56f4\u653b\uff0c\u53ea\u6050\u4e24\u4eba\u90fd\u8981\u88ab\u65a9\u6210\u8089\u7cdc\uff01\u6b63\u5728\u63d0\u5fc3\u540a\u80c6\uff0c\u5ffd\u89c9\u5927\u5802\u4e0a\u7684\u6c14\u6c1b\u5f02\u4e4e\u5bfb\u5e38\uff0c\u5bc2\u9759\u5f97 \u4ee4\u4eba\u9a87\u6015\uff0c\u653e\u773c\u770b\u65f6\uff0c\u53ea\u89c1\u90a3\u5c11\u5e74\u5c01\u7d27\u95e8\u6237\uff0c\u5e76\u4e0d\u8fdb\u62db\uff0c\u5802\u4e0a\u7fa4\u76d7\uff0c\u56f4\u5217\u56db\u5468\uff0c\u4e2a\u4e2a\u5782\u624b\u800c \u7acb\u3002\u864e\u5e10\u5916\u8fdc\u8fdc\u4f20\u6765\u53f7\u89d2\u4e4b\u58f0\uff0c\u5ffd\u542c\u5f97\u6709\u4eba\u62a5\u9053\uff1a\u201c\u5927\u738b\u9a7e\u5230\uff01\u201d \u3000\u3000\u90a3\u5c11\u5e74\u500f\u5730\u8df3\u5f00\uff0c\u53ea\u89c1\u5916\u9762\u8d70\u8fdb\u4e86\u4e00\u4f19\u4eba\uff0c\u4e3a\u9996\u7684\u957f\u987b\u98d8\u62c2\uff0c\u6c14\u5ea6\u5a01\u4e25\uff0c\u770b\u6765\u5e74\u8fc7\u516d \u65ec\uff0c\u5374\u662f\u7cbe\u795e\u77cd\u94c4\u3002\u767d\u8863\u5c11\u5973\u770b\u4e86\u4e00\u773c\uff0c\u65bd\u793c\u95ee\u9053\uff1a\u201c\u6765\u7684\u53ef\u662f\u8001\u5be8\u4e3b\u4e48\uff1f\u201d\u957f\u987b\u8001\u4eba\u5fae\u5fae \u4e00\u7b11\u9053\uff1a\u201c\u542c\u8bf4\u59d1\u5a18\u4eca\u65e5\u4e0a\u5c71\uff0c\u8001\u592b\u5931\u8fce\u4e86\u3002\u201d\u8fb9\u8bf4\u8fb9\u6253\u91cf\u90a3\u4e2a\u5c11\u5973\uff0c\u795e\u8272\u751a\u662f\u7279\u522b\u3002 \u3000\u3000\u767d\u8863\u5c11\u5973\u7ed9\u4ed6\u770b\u5f97\u4e0d\u597d\u610f\u601d\uff0c\u6309\u5251\u8bf4\u9053\uff1a\u201c\u4e45\u4ef0\u5be8\u4e3b\u5a01\u540d\uff0c\u4ec1\u4fa0\u65e0\u53cc\uff0c\u4eca\u65e5\u6709\u7f18\u62dc\u89c1\uff0c \u517c\u5411\u5be8\u4e3b\u6c42\u60c5\u3002\u201d\u957f\u987b\u8001\u4eba\u968f\u53e3\u5e94\u9053\uff1a\u201c\u597d\u8bf4\uff0c\u597d\u8bf4\u3002\u201d\u7a81\u7136\u95ee\u9053\uff1a\u201c\u59d1\u5a18\u4eca\u5e74\u5e9a\uff1f\u53ef\u662f\u5c5e \u7f8a\u7684\u4e48\uff1f\u201d\u767d\u8863\u5c11\u5973\u4e0d\u63d0\u9632\u4ed6\u6709\u6b64\u4e00\u95ee\uff0c\u4e0d\u89c9\u5f97\u4e00\u6014\uff0c\u5fae\u6120\u8bf4\u9053\uff1a\u201c\u8001\u5be8\u4e3b\u83ab\u975e\u8bf4\u6211\u5e74\u8f7b\u8bc6 \u6d45\uff0c\u4e0d\u914d\u4e0a\u5c71\uff0c\u5411\u4f60\u6c42\u60c5\u4e48\uff1f\u201d\u957f\u987b\u8001\u4eba\u6253\u4e86\u4e00\u4e2a\u54c8\u54c8\uff0c\u9053\uff1a\u201c\u59d1\u5a18\u8a00\u91cd\u4e86\u3002\u201d\u767d\u8863\u5c11\u5973\u7d27 \u903c\u9053\uff1a\u201c\u8fd9\u9636\u4e0b\u7684\u56db\u5341\u4e07\u4e24\u94f6\u5b50\uff0c\u4e43\u662f\u96c1\u95e8\u5173\u7684\u519b\u9977\uff0c\u5be8\u4e3b\u4f60\u8fd9\u4e00\u4f38\u624b\uff0c\u4e0d\u4f46\u5bb3\u4e86\u8fd9\u4f4d\u516c\u7237 \u7684\u6027\u547d\uff0c\u96c1\u95e8\u5173\u7684\u6570\u4e07\u5b98\u5175\uff0c\u4e5f\u8981\u559d\u897f\u5317\u98ce\u5566\uff01\u201d\u957f\u987b\u8001\u4eba\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u4e2a\u6211\u5c82\u6709\u4e0d \u77e5\uff1f\u201d\u767d\u8863\u5c11\u5973\u9053\uff1a\u201c\u8001\u5be8\u4e3b\u65e2\u7136\u77e5\u9053\u7684\u5176\u4e2d\u5229\u5bb3\uff0c\u90a3\u5c31\u5e94\u8be5\u628a\u94f6\u5b50\u53d1\u56de\u3002\u201d \u3000\u3000\u957f\u987b\u8001\u4eba\u634b\u634b\u80e1\u5b50\uff0c\u7b11\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4f60\u5374\u4e5f\u6709\u6240\u4e0d\u77e5\u3002\u201d\u767d\u8863\u5c11\u5973\u9053\uff1a\u201c\u8bf7\u5be8\u4e3b\u8d50 \u6559\u3002\u201d\u957f\u987b\u8001\u4eba\u6307\u4e86\u6307\u90a3\u65e5\u6708\u53cc\u65d7\uff0c\u8bf4\u9053\uff1a\u201c\u7eff\u6797\u91cc\u7684\u89c4\u77e9\uff0c\u65e2\u52ab\u4e86\u6765\uff0c\u90a3\u5c31\u4e0d\u80fd\u53ea\u51ed\u4e00\u53e5 \u8bf4\u8bdd\u9000\u4e86\u56de\u53bb\u3002\u94f6\u5b50\u4e8b\u5c0f\uff0c\u8fd9\u65d7\u5b50\u7684\u5a01\u540d\u53ef\u5f97\u4fdd\u5168\u3002\u59d1\u5a18\uff0c\u4f60\u65e2\u7136\u66ff\u8fd9\u4f4d\u516c\u7237\u6c42\u60c5\uff0c\u4e5f\u603b\u5f97 \u6296\u9732\u4e24\u624b\u7ed9\u5f1f\u5144\u4eec\u770b\u770b\u3002\u8981\u4e0d\u7136\u6211\u9000\u4e86\u94f6\u4e24\uff0c\u4ed6\u4eec\u4e5f\u4e0d\u670d\u6c14\u3002\u201d\u767d\u8863\u5c11\u5973\u6012\u4e0a\u7709\u68a2\uff0c\u51b7\u7b11\u8bf4 \u9053\uff1a\u201c\u6211\u53ea\u9053\u95fb\u540d\u4e0d\u5982\u89c1\u9762\uff0c\u8c01\u77e5\u9053\u89c1\u9762\u4e0d\u4f3c\u95fb\u540d\u3002\u597d\uff0c\u597d\uff01\u90a3\u5c31\u8bf7\u5be8\u4e3b\u4f60\u5212\u51fa\u9053\u513f\uff01\u201d\u957f \u987b\u8001\u4eba\u53c8\u662f\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5c0f\u59d1\u5a18\uff0c\u5929\u5730\u4e4b\u95f4\uff0c\u89c1\u9762\u4e0d\u4f3c\u95fb\u540d\u7684\u591a\u7740\u5462\uff01\u5c82\u72ec\u8001\u673d\u4e3a\u7136\u3002 \u4f60\u602a\u6211\u4e0d\u80af\u723d\u723d\u5feb\u5feb\u9000\u56de\u94f6\u5b50\u4e48\uff1f\u201d\u767d\u8863\u5c11\u5973\u76ee\u5149\u659c\u89c6\uff0c\u4e0d\u63a5\u8bdd\u5cf0\uff0c\u5c31\u50cf\u95f9\u813e\u6c14\u7684\u5b69\u5b50\u4e00 \u6837\uff0c\u5e72\u8106\u7ed9\u4ed6\u4e2a\u9ed8\u8ba4\u3002\u957f\u987b\u8001\u4eba\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u6211\u5c31\u7ed9\u4f60\u4e2a\u75db\u5feb\u7684\u529e\u6cd5\u3002\u4f60\u65e2\u5e26\u5251\u4e0a\u5c71\uff0c\u5b9a \u7136\u5728\u5251\u672f\u4e0a\u6709\u6df1\u6e5b\u7684\u9020\u8be3\u3002\u597d\u5427\uff0c\u6211\u5c31\u7528\u8fd9\u53e3\u91d1\u5200\uff0c\u9886\u6559\u4f60\u51e0\u8def\u5251\u6cd5\u3002\u5b66\u65e0\u524d\u540e\uff0c\u8fbe\u8005\u4e3a \u5e08\u3002\u4f60\u53ef\u4e0d\u8981\u56e0\u6211\u5e74\u7eaa\u8001\u8fc8\uff0c\u5c31\u6545\u610f\u5251\u4e0b\u7559\u60c5\u3002\u4f60\u82e5\u8d62\u4e86\uff0c\u8fd9\u56db\u5341\u4e07\u4e24\u94f6\u9977\uff0c\u6211\u4eb2\u81ea\u7ed9\u4ed6\u9001 \u56de\uff0c\u4e00\u4e2a\u5b50\u513f\u4e5f\u4e0d\u7f3a\u5c11\uff01\u201d\u8fb9\u8bf4\u8fb9\u659f\u8d77\u9152\u6765\uff0c\u8bdd\u8bf4\u5b8c\u540e\uff0c\u9152\u5df2\u559d\u4e86\u4e24\u676f\uff0c\u84e6\u7136\u62ff\u8d77\u4e24\u4e2a\u7a7a \u676f\uff0c\u5411\u6881\u4e0a\u4e00\u6454\uff0c\u5389\u58f0\u8bf4\u9053\uff1a\u201c\u597d\u597d\u7684\u5927\u6881\uff0c\u8c01\u4eba\u5728\u8fd9\u91cc\u63d2\u4e86\u4e24\u67c4\u5315\u9996\uff1f\u201d\u9152\u676f\u98de\u5904\uff0c\u25a1\u25a1 \u58f0\u54cd\u8d77\uff0c\u788e\u7247\u7eb7\u98de\uff0c\u4e24\u67c4\u5315\u9996\u5374\u4e5f\u968f\u7740\u788e\u7247\u8dcc\u4e86\u4e0b\u6765\uff0c\u9152\u676f\u662f\u4e00\u89e6\u5373\u788e\u7684\u4e1c\u897f\uff0c\u78b0\u7740\u5927\u6881\uff0c \u7adf\u80fd\u5c06\u5315\u9996\u9707\u843d\uff0c\u8fd9\u8001\u5934\u513f\u5185\u529f\u4e4b\u6df1\u539a\uff0c\u5b9e\u662f\u8db3\u4ee5\u9a87\u4eba\uff01 \u3000\u3000\u767d\u8863\u5c11\u5973\u4e0d\u89c9\u4e00\u6014\uff0c\u5979\u8d77\u521d\u672c\u60f3\u7a7a\u624b\u5bf9\u654c\uff0c\u800c\u4eca\u89c1\u4ed6\u9732\u4e86\u8fd9\u624b\uff0c\u4e0d\u7531\u5f97\u4e0d\u628a\u8f7b\u654c\u4e4b\u5fc3\u6536 \u655b\uff0c\u5f53\u4e0b\u62d4\u51fa\u5251\u6765\uff0c\u8df3\u51fa\u5ead\u5fc3\uff0c\u5728\u4e0b\u9996\u7ad9\u5b9a\uff0c\u5fae\u4e00\u62f1\u624b\uff0c\u8bf4\u9053\uff1a\u201c\u8bf7\u5be8\u4e3b\u8d50\u62db\u3002\u201d\u957f\u987b\u8001\u4eba \u77a5\u4e86\u4e00\u773c\uff0c\u8d5e\u58f0\uff1a\u201c\u597d\u5251\uff01\u201d\u628a\u624b\u4e00\u62ac\uff0c\u53ea\u89c1\u4e24\u540d\u55bd\u5175\u62ac\u7740\u4e00\u67c4\u91d1\u5149\u95ea\u95ea\u7684\u5927\u5200\uff0c\u957f\u987b\u8001\u4eba \u63a5\u8fc7\u5927\u5200\uff0c\u53cc\u6307\u4e00\u5f39\uff0c\u7eb5\u58f0\u7b11\u9053\uff1a\u201c\u91d1\u5200\u5475\uff0c\u4eca\u56de\u4f60\u53ef\u78b0\u5230\u5bf9\u624b\u4e86\u3002\u201d \u3000\u3000\u4e24\u4eba\u5404\u81ea\u7acb\u597d\u95e8\u6237\uff0c\u767d\u8863\u5c11\u5973\u77e5\u4ed6\u81ea\u5c45\u524d\u8f88\uff0c\u51b3\u4e0d\u80af\u62a2\u5148\u53d1\u62db\uff0c\u5f53\u4e0b\u624b\u629a\u5251\u67c4\uff0c\u5251\u5c16\u5411 \u4e0b\u4e00\u70b9\uff0c\u8fd9\u662f\u540e\u8f88\u5bf9\u524d\u8f88;\u52a8\u624b\u65f6\u8868\u793a\u8c26\u8ba9\u7684\u8d77\u624b\u62db\u5f0f\u3002\u957f\u987b\u8001\u4eba\u5411\u540e\u4e00\u4e2a\u9000\u6b65\uff0c\u53ea\u542c\u5f97\u5237 \u7684\u4e00\u58f0\uff0c\u767d\u8863\u5c11\u5973\u4e00\u62db\u201c\u5f69\u8776\u7a7f\u82b1\u201d\uff0c\u5251\u52bf\u8f7b\u7075\u4e4b\u6781\uff0c\u957f\u987b\u8001\u4eba\u559d\u58f0\u201c\u597d\u201d\uff0c\u4e00\u4e2a\u201c\u51e4\u51f0\u593a \u7a9d\u201d\uff0c\u8eab\u5f62\u53cd\u4e86\u8fc7\u6765\uff0c\u4e00\u4e0b\u5b50\u5c31\u62a2\u7740\u4e86\u5c11\u5973\u5148\u524d\u7684\u4f4d\u7f6e\u3002\u767d\u8863\u5c11\u5973\u5403\u4e86\u4e00\u60ca\uff0c\u60f3\u4e0d\u5230\u8fd9\u4f4d\u91d1 \u5200\u5be8\u4e3b\u5e74\u7eaa\u867d\u8001\uff0c\u8eab\u6cd5\u8fc5\u6377\uff0c\u53ef\u662f\u4e0d\u900a\u5e74\u8f7b\uff0c\u8fd9\u4e00\u4e2a\u98de\u8eab\u593a\u4f4d\uff0c\u81ea\u5df1\u7684\u5de6\u53f3\u4e2d\u4e09\u8def\uff0c\u90fd\u5df2\u7ed9 \u4ed6\u7684\u5200\u52bf\u5236\u4f4f\u4e86\u3002 \u3000\u3000\u76d7\u515a\u4eec\u8f70\u7136\u559d\u91c7\uff0c\u53ef\u662f\u53ea\u77ac\u606f\u4e4b\u95f4\uff0c\u53c8\u662f\u5168\u573a\u58f0\u5bc2\u3002\u53ea\u89c1\u90a3\u767d\u8863\u5c11\u5973\u51cc\u7a7a\u98de\u4e0b\uff0c\u633d\u4e86\u4e00 \u4e2a\u5251\u82b1\uff0c\u5251\u5149\u56db\u5c04\uff0c\u5c31\u5982\u540c\u5343\u4e07\u70b9\u5bd2\u661f\uff0c\u5f53\u5934\u6d12\u4e0b\u3002\u5251\u5149\u5200\u5f71\u4e4b\u4e2d\uff0c\u53ea\u542c\u5f97\u4e00\u9635\u65ad\u91d1\u621b\u7389\u4e4b \u58f0\u9707\u5f97\u55e1\u55e1\u8033\u54cd\uff0c\u4f17\u4eba\u653e\u773c\u770b\u65f6\uff0c\u53ea\u89c1\u767d\u8863\u5c11\u5973\u5df2\u5728\u4e00\u4e08\u5f00\u5916\uff0c\u957f\u987b\u8001\u4eba\u6a2a\u5200\u5f53\u80f8\uff0c\u53eb\u9053\uff1a \u201c\u5251\u597d\uff0c\u5251\u6cd5\u66f4\u597d\uff01\u8fd9\u4e00\u62db\u5f7c\u6b64\u90fd\u4e0d\u8f93\u4e8f\uff0c\u518d\u6765\uff0c\u518d\u6765\uff01\u201d \u3000\u3000\u65b9\u5e86\u6b66\u529f\u5e73\u5eb8\uff0c\u8fd8\u770b\u4e0d\u51fa\u6240\u4ee5\u7136\u6765\uff0c\u76d7\u515a\u4e2d\u7684\u9ad8\u624b\uff0c\u5374\u662f\u4e2a\u4e2a\u5fc3\u60ca\u3002\u767d\u8863\u5c11\u5973\u521a\u624d\u90a3 \u62db\uff0c\u5728\u53d7\u654c\u63a7\u5236\u4e4b\u4e0b\uff0c\u7a81\u7136\u98de\u8eab\u800c\u51fa\uff0c\u5b9e\u662f\u5251\u5b66\u4e4b\u4e2d\u6700\u7ed9\u7ec3\u7684\u62db\u6570\uff0c\u773c\u5229\u7684\u4e14\u77a5\u89c1\u8001\u5be8\u4e3b\u7684 \u91d1\u5200\u5df2\u7f3a\u4e86\u4e00\u53e3\uff0c\u66f4\u662f\u62c5\u5fc3\u3002 \u3000\u3000\u767d\u8863\u5c11\u5973\u5fae\u5fae\u5598\u6c14\uff0c\u5979\u867d\u7136\u5c06\u654c\u4eba\u7684\u91d1\u5200\u524a\u4e86\u4e00\u4e2a\u7f3a\u53e3\uff0c\u53ef\u662f\u81ea\u5df1\u7ed9\u4ed6\u7684\u91d1\u5200\u4e00\u8feb\uff0c\u5012 \u9000\u4e00\u4e08\uff0c\u8fd8\u51e0\u4e4e\u6536\u52bf\u4e0d\u4f4f\uff0c\u8bba\u5230\u529f\u529b\u7684\u6df1\u539a\uff0c\u81ea\u5df1\u5b9e\u4e0d\u5982\u4ed6\u3002 \u3000\u3000\u4e24\u4eba\u6362\u4e86\u4e00\u62db\uff0c\u5404\u6709\u6212\u60e7\uff0c\u518d\u6597\u4e4b\u65f6\uff0c\u5f62\u52bf\u53c8\u662f\u4e0d\u540c\u3002\u53ea\u89c1\u767d\u8863\u5c11\u5973\u5de6\u7a7f\u53f3\u63d2\uff0c\u6709\u5982\u8774 \u8776\u7a7f\u82b1\uff0c\u5251\u5149\u95ea\u70c1\u4e0d\u5b9a\uff0c\u8eab\u5f62\u8d8a\u8f6c\u8d8a\u75be\uff0c\u8f6c\u5f97\u65c1\u89c2\u7684\u4eba\u90fd\u89c9\u5934\u6655\u773c\u82b1\uff0c\u91d1\u5200\u5be8\u4e3b\u5374\u5140\u7acb\u5982 \u5c71\uff0c\u4e0d\u4e3a\u6240\u52a8\u3002\u731b\u542c\u5f97\u767d\u8863\u5c11\u5973\u4e00\u58f0\u6e05\u53f1\uff0c\u5251\u5149\u66b4\u957f\uff0c\u653b\u52bf\u7a81\u53d1\uff0c\u6709\u5982\u957f\u6c5f\u5927\u6cb3\uff0c\u6eda\u6eda\u800c \u4e0a\uff0c\u4f46\u89c1\u5251\u82b1\u9519\u843d\uff0c\u5251\u6c14\u7eb5\u6a2a\uff0c\u51fa\u624b\u4e4b\u5feb\uff0c\u65e0\u4ee5\u5f62\u5bb9\uff01\u91d1\u5200\u5be8\u4e3b\u5374\u7f13\u7f13\u6325\u52a8\u91d1\u5200\uff0c\u811a\u8ddf\u6709\u5982 \u9489\u7262\u5728\u5730\u4e0a\u4e00\u822c\uff0c\u4efb\u5979\u5251\u52bf\u96e8\u9aa4\u98ce\u72c2\uff0c\u7adf\u4e0d\u79fb\u52a8\u534a\u6b65\uff0c\u5200\u52bf\u867d\u7f13\uff0c\u90a3\u864e\u864e\u7684\u5200\u98ce\u5374\u9707\u8033\u9a87 \u5fc3\uff0c\u767d\u8863\u5c11\u5973\u4e00\u53e3\u6c14\u653b\u4e86\u4e94\u4e03\u5341\u62db\uff0c\u5140\u662f\u653b\u4e0d\u8fdb\u53bb\u3002\u76d7\u515a\u4eec\u90fd\u5618\u4e86\u53e3\u6c14\uff0c\u5fc3\u5ff5\u8001\u5be8\u4e3b\u5f53\u80fd\u6218 \u80dc\u3002\u65b9\u5e86\u867d\u7136\u770b\u4e0d\u61c2\u4e24\u4eba\u62db\u6570\uff0c\u89c1\u76d7\u515a\u4eec\u7684\u9762\u8272\u7531\u7d27\u5f20\u800c\u8f6c\u4e3a\u8f7b\u677e\uff0c\u5fc3\u4e2d\u5df2\u77e5\u4e0d\u5999\uff0c\u4e0d\u7531\u5f97 \u7259\u5173\u6253\u6218\uff0c\u5982\u5750\u51b0\u5c71\u3002 \u3000\u3000\u9163\u6597\u4e4b\u4e2d\uff0c\u731b\u542c\u5f97\u957f\u987b\u8001\u4eba\u559d\u58f0\u201c\u53bb\uff01\u201d\u91d1\u5149\u4e00\u95ea\uff0c\u767d\u5149\u75be\u9000\uff0c\u90a3\u5c11\u5973\u8eab\u5f62\u53c8\u5df2\u5728\u4e00\u4e08 \u5f00\u5916\uff0c\u76d7\u515a\u4eec\u8f70\u5929\u4ef7\u7684\u53c8\u559d\u8d77\u5f69\u6765\uff01 \u3000\u3000\u767d\u8863\u5c11\u5973\u7eb5\u51fa\u6570\u6b65\uff0c\u63c9\u8eab\u53c8\u4e0a\uff0c\u957f\u987b\u8001\u4eba\u8fd9\u4e00\u5200\u731b\u52bf\u6c89\uff0c\u5374\u4e5f\u6ca1\u5c06\u767d\u8863\u5c11\u5973\u7684\u5b9d\u5251\u5288 \u843d\uff0c\u5fc3\u4e2d\u4ea6\u81ea\u60ca\u5f02\u3002\u767d\u8863\u5c11\u5973\u63c9\u8eab\u518d\u4e0a\uff0c\u5251\u6cd5\u53c8\u53d8\u3002\u53ea\u89c1\u5979\u9752\u950b\u659c\u524a\uff0c\u4fe8\u5982\u72c2\u98ce\u626b\u53f6\uff0c\u5251\u5c16 \u76f4\u523a\u6709\u5982\u66b4\u96e8\u6467\u82b1\uff0c\u5251\u5149\u7f2d\u7ed5\u4e4b\u4e2d\uff0c\u4f46\u89c1\u56db\u9762\u516b\u65b9\u90fd\u662f\u767d\u8863\u5c11\u5973\u7684\u5f71\u5b50\uff0c\u5251\u5149\u5ffd\u4e1c\u5ffd\u897f\uff0c\u5ffd \u805a\u5ffd\u6563\uff0c\u7fe9\u82e5\u60ca\u9e3f\uff0c\u5b9b\u5982\u6e38\u9f99\uff0c\u4e0d\u4f46\u628a\u65c1\u89c2\u7684\u4eba\u770b\u5f97\u773c\u82b1\u7f2d\u4e71\uff0c\u91d1\u5200\u5be8\u4e3b\u4e5f\u5403\u4e86\u4e00\u60ca\u3002\u8fd9\u767d \u8863\u5c11\u5973\u5251\u6cd5\u5947\u7edd\uff0c\u770b\u5979\u5982\u5c01\u4f3c\u95ed\uff0c\u5374\u53c8\u5982\u8fdb\u4f3c\u653b\uff0c\u5b9e\u662f\u6349\u6478\u4e0d\u5230\u3002\u91d1\u5200\u5be8\u4e3b\u53ea\u5f97\u5c01\u95ed\u95e8\u6237\uff0c \u518d\u548c\u5979\u6e38\u6597\uff0c\u767d\u8863\u5c11\u5973\u4e00\u53e3\u6c14\u53c8\u8fdb\u4e86\u4e09\u4e94\u5341\u62db\uff0c\u865a\u865a\u5b9e\u5b9e\uff0c\u53d8\u5316\u5c42\u51fa\u4e0d\u7a77\uff0c\u91d1\u5200\u5be8\u4e3b\u867d\u7136\u4ecd \u662f\u672a\u66fe\u79fb\u52a8\u534a\u6b65\uff0c\u9762\u8272\u51dd\u91cd\uff0c\u663e\u662f\u6bd4\u5148\u524d\u5403\u529b\u5f97\u591a\u3002\u9163\u6597\u4e2d\u91d1\u5200\u5be8\u4e3b\u4e00\u5200\u659c\u5288\uff0c\u5ffd\u88ab\u5bf9\u65b9\u5251 \u5c16\u4e00\u6302\uff0c\u628a\u91d1\u5200\u8f7b\u8f7b\u5730\u9ecf\u51fa\u5916\u95e8\u3002\u8fd9\u4e00\u5200\u7528\u4e86\u516b\u6210\u529b\u91cf\uff0c\u5ffd\u5982\u6251\u4e86\u4e2a\u7a7a\uff0c\u88ab\u5bf9\u65b9\u8f7b\u8f7b\u5730\u5c06\u52b2 \u529b\u5378\u4e86\uff0c\u91d1\u5200\u5be8\u4e3b\u4e0d\u7531\u5f97\u8eab\u5b50\u524d\u503e\uff0c\u6251\u524d\u4e24\u6b65\uff0c\u867d\u7136\u7acb\u5373\u51dd\u8eab\u7ad9\u5b9a\uff0c\u575a\u5b88\u4e4b\u52bf\u5df2\u662f\u88ab\u5979\u7275 \u52a8\uff0c\u95e8\u6237\u518d\u4e5f\u5c01\u95ed\u4e0d\u4f4f\u3002 \u3000\u3000\u767d\u8863\u5c11\u5973\u5251\u52bf\u9aa4\u7f13\uff0c\u5251\u5c16\u642d\u7740\u5200\u950b\uff0c\u8f6c\u6765\u8f6c\u53bb\uff0c\u957f\u987b\u8001\u4eba\u91d1\u5200\u4e09\u7ede\uff0c\u628a\u767d\u8863\u5c11\u5973\u903c\u5f97\u6b65 \u6b65\u540e\u9000\uff0c\u4f46\u5200\u5251\u7ea0\u7f20\u4e4b\u52bf\u5374\u672a\u89e3\u5f00\uff0c\u4e24\u4eba\u653b\u8fc7\u5747\u6162\uff0c\u4e00\u8fdb\u4e00\u9000\uff0c\u53c8\u6218\u4e86\u4e00\u4e2a\u65f6\u8fb0\u3002\u65b9\u5e86\u89c1\u767d \u8863\u5c11\u5973\u4e0d\u4f4f\u540e\u9000\uff0c\u5bb3\u6015\u4e4b\u6781\uff0c\u4f46\u542c\u90a3\u6ee1\u5802\u5bc2\u9759\uff0c\u5468\u56f4\u76d7\u515a\uff0c\u4e2a\u4e2a\u5c4f\u606f\u4ee5\u89c2\uff0c\u65e0\u4e00\u4eba\u6562\u53d1\u58f0\u8c08 \u8bba\uff0c\u4e0e\u5148\u524d\u53fd\u53fd\u55b3\u55b3\uff0c\u53e3\u8bb2\u6307\u5212\u7684\u60c5\u52bf\u5927\u4e0d\u76f8\u540c\uff0c\u770b\u6765\u53c8\u4e0d\u4f3c\u91d1\u5200\u5be8\u4e3b\u5360\u5f97\u4e0a\u98ce\u3002 \u3000\u3000\u76d7\u515a\u7fa4\u8c6a\u89c1\u767d\u8863\u5c11\u5973\u5251\u6cd5\u5947\u5999\uff0c\u65e2\u6709\u6b66\u5f53\u6d3e\u8fbe\u6469\u5251\u6cd5\u7684\u62db\u6570\uff0c\u53c8\u6709\u592a\u6781\u5251\u7684\u62db\u6570\uff0c\u98d8\u5ffd \u4e4b\u5904\u4f3c\u8e51\u4e91\u5251\u7684\u8def\u6570\uff0c\u51dd\u91cd\u4e4b\u5904\u53c8\u4f3c\u4e09\u9633\u5251\u7684\u8def\u6570\uff0c\u5947\u62db\u5999\u7740\u5c42\u51fa\u4e0d\u7a77\uff0c\u90fd\u662f\u53c8\u60ca\u5947\u53c8\u62c5 \u5fc3\u3002\u4f46\u91d1\u5200\u5be8\u4e3b\u6325\u5200\u529b\u65ab\uff0c\u4e5f\u672a\u9732\u8d25\u8c61\u3002\u91d1\u5200\u5be8\u4e3b\u5c0f\u5fc3\u7ffc\u7ffc\u6b65\u6b65\u8fdb\u903c\uff0c\u767d\u8863\u5c11\u5973\u8eab\u5b50\u5ffd\u7136\u5411 \u540e\u4e00\u5316\uff0c\u5b9d\u5251\u4e00\u64a4\uff0c\u76d7\u515a\u9ad8\u624b\u53eb\u9053\uff1a\u201c\u8b07\u4e3b\u5c0f\u5fc3\uff01\u201d\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u90a3\u767d\u8863\u5c11\u5973\u8eab\u5f62\u75be \u8d77\uff0c\u5251\u5149\u5982\u8679\uff0c\u53c8\u662f\u51cc\u7a7a\u5f80\u4e0b\u523a\uff01\u91d1\u5200\u5be8\u4e3b\u5ffd\u5730\u54c8\u54c8\u5927\u7b11\uff0c\u559d\u9053\uff1a\u201c\u6492\u624b\uff01\u201d\u8eab\u8eaf\u4e00\u77ee\uff0c\u5f85 \u90a3\u767d\u8863\u5c11\u5973\u521a\u521a\u4e0b\u523a\u4e4b\u65f6\uff0c\u7a81\u5730\u4e00\u5200\u5411\u5979\u62e6\u8170\u5288\u53bb\uff0c\u8fd9\u4e00\u62db\u5947\u5999\u4e4b\u6781\uff0c\u9664\u4e86\u6454\u5251\u649e\u5f00\u5200\u950b\uff0c \u7136\u540e\u624d\u80fd\u7acb\u5373\u95ea\u907f\u4e4b\u5916\uff0c\u5b9e\u65e0\u5176\u4ed6\u62db\u6570\u53ef\u4ee5\u62b5\u6321\u3002\u91d1\u5200\u5be8\u4e3b\u706b\u5019\u8001\u5230\uff0c\u7ecf\u9a8c\u751a\u4e30\uff0c\u8fd9\u4e00\u5200\u6b63 \u662f\u4ed6\u6218\u4e86\u534a\u5929\u4e4b\u540e\uff0c\u6240\u60f3\u51fa\u6765\u7684\u552f\u4e00\u7834\u654c\u62db\u6570\u3002 \u3000\u3000\u76d7\u515a\u9ad8\u624b\u77a9\u76ee\u60ca\u5fc3\uff0c\u770b\u89c1\u5be8\u4e3b\u4f7f\u51fa\u8fd9\u4e00\u795e\u62db\uff0c\u7981\u4e0d\u4f4f\u8f70\u5929\u4ef7\u7684\u53c8\u559d\u8d77\u597d\u6765\uff0c\u5374\u4e0d\u6599\u559d\u91c7 \u4e4b\u58f0\u672a\u505c\uff0c\u5f62\u52bf\u5ffd\u53c8\u5927\u53d8\uff0c\u4e5f\u4e0d\u77e5\u90a3\u767d\u8863\u5c11\u5973\u7528\u7684\u662f\u4ec0\u4e48\u624b\u6cd5\uff0c\u53ea\u542c\u5f97\u5979\u4e5f\u559d\u4e00\u58f0\u201c\u6492 \u624b\u201d\uff0c\u8001\u5be8\u4e3b\u7684\u91d1\u5200\uff0c\u7adf\u7136\u8131\u624b\u98de\u51fa\uff0c\u547c\u7684\u4e00\u58f0\u63d2\u5728\u6a2a\u6881\u4e4b\u4e0a\u3002\u539f\u6765\u767d\u8863\u5c11\u5973\u6218\u4e0d\u4e0b\uff0c\u4e5f\u77e5 \u9053\u4e0d\u80fd\u529b\u654c\uff0c\u56e0\u6b64\u5c06\u8ba1\u5c31\u8ba1\uff0c\u5c55\u51fa\u4e86\u5e08\u95e8\u662f\u6700\u5192\u9669\u7684\u6551\u547d\u795e\u62db\uff0c\u5728\u91d1\u5200\u5288\u6765\u4e4b\u65f6\uff0c\u811a\u5c16\u8f7b\u8f7b \u4e00\u70b9\u5200\u5934\u8f6c\u950b\u4fbf\u6233\u654c\u4eba\u624b\u8155\uff0c\u8fd9\u4e00\u7740\u7edd\u9669\u795e\u62db\uff0c\u7acb\u523b\u53d8\u5ba2\u4e3a\u4e3b\u3002 \u3000\u3000\u91d1\u5200\u5be8\u4e3b\u4e07\u4e07\u6599\u4e0d\u5230\u5979\u6709\u6b64\u4e00\u62db\uff0c\u8fd9\u65f6\u9664\u4e86\u6454\u5200\u4e4b\u5916\uff0c\u66f4\u65e0\u4ed6\u6cd5\u3002\u767d\u8863\u5c11\u5973\u5a07\u58f0\u4e00\u7b11\uff0c \u7ad9\u5728\u5730\u4e0a\uff0c\u8f6c\u8fc7\u8eab\u6765\uff0c\u6b63\u60f3\u8bf4\u58f0\u201c\u8001\u5be8\u4e3b\uff0c\u627f\u4f60\u8ba9\u5566\uff01\u201d\u5ffd\u89c1\u91d1\u5200\u5be8\u4e3b\u60e8\u7136\u4e00\u7b11\uff0c\u773c\u4e2d\u9690\u6709 \u6cea\u73e0\uff0c\u767d\u8863\u5c11\u5973\u4e0d\u89c9\u4e00\u6014\uff0c\u5fc3\u9053\uff1a\u201c\u600e\u4e48\u8fd9\u6837\u4e00\u4e2a\u5a01\u9707\u80e1\u6c49\u7684\u8001\u82f1\u96c4\uff0c\u8f93\u4e86\u62db\u4e5f\u4f1a\u54ed\u5462\uff1f\u201d \u5fc3\u4e2d\u6b49\u759a\uff0c\u6307\u4ed6\u8f93\u62db\u7684\u8bdd\u7adf\u8bf4\u4e0d\u51fa\u53e3\u6765\u3002\u53ea\u89c1\u91d1\u5200\u5be8\u4e3b\u7684\u76ee\u5149\u6ce8\u5b9a\u81ea\u5df1\uff0c\u4f3c\u54ed\u4f3c\u7b11\uff0c\u624b\u6307\u6162 \u6162\u63ed\u5f00\u957f\u888d\u4e00\u89d2\uff0c\u62bd\u51fa\u4e00\u6839\u7af9\u6756\uff0c\u7af9\u6756\u751a\u77ed\uff0c\u4e0b\u7aef\u4e14\u6709\u88c2\u75d5\uff0c\u751a\u4e0d\u5e73\u4e0d\u6574\uff0c\u4f3c\u662f\u672c\u6765\u751a\u957f\uff0c \u540e\u6765\u7ed9\u4eba\u62d7\u65ad\u4f3c\u7684\u3002\u7af9\u6756\u4e0a\u5934\u8fd8\u6709\u51e0\u6839\u7a00\u758f\u7684\u65c4\u6bdb\uff0c\u767d\u8863\u5c11\u5973\u4e00\u89c1\u6b64\u6756\uff0c\u9762\u8272\u5927\u53d8\uff0c\u5ffd\u7136\u54c7 \u7684\u4e00\u58f0\u54ed\u4e86\u51fa\u6765\uff0c\u8dea\u5728\u5730\u4e0a\u3002 \u3000\u3000\u8fd9\u4e00\u4e0b\u66f4\u662f\u4ee4\u4eba\u9707\u60ca\uff0c\u51fa\u4eba\u610f\u8868\u3002\u91d1\u5200\u5be8\u4e3b\u5de6\u624b\u6301\u6756\uff0c\u53f3\u624b\u5c06\u90a3\u767d\u8863\u5c11\u5973\u7f13\u7f13\u62c9\u4e86\u8d77 \u6765\uff0c\u5ffd\u800c\u53c8\u7eb5\u58f0\u7b11\u9053\uff1a\u201c\u4e91\u9756\u6709\u6b64\u5b59\u5973\uff0c\u4e5d\u6cc9\u4e4b\u4e0b\u5f53\u53ef\u7791\u76ee\u4e86\uff01\u201d\u5c11\u5973\u545c\u545c\u54bd\u54bd\uff0c\u6cea\u5c1a\u672a \u6536\uff0c\u89c1\u4e86\u6b64\u6756\uff0c\u60f3\u8d77\u5341\u5e74\u524d\u4e8b\u3002\u90a3\u65f6\u5979\u8fd8\u662f\u53ea\u6709\u4e03\u5c81\u7684\u5c0f\u5b69\u5b50\uff0c\u5979\u7237\u7237\u4e91\u9756\u548c\u5979\u4ece\u8499\u53e4\u9003 \u56de\uff0c\u5728\u9a74\u8f66\u4e4b\u4e0a\uff0c\u66fe\u7ecf\u7ed9\u5979\u770b\u8fc7\u8fd9\u6839\u201c\u4f7f\u8282\u201d\uff0c\u7ed9\u5979\u8bf4\u8fc7\u7267\u9a6c\u80e1\u8fb9\u7684\u6545\u4e8b\u3002\u800c\u4eca\u89c1\u4e86\u6b64\u6756\uff0c \u604d\u5982\u91cd\u89c1\u7237\u7237\uff0c\u600e\u4e0d\u4ee4\u5979\u4f24\u5fc3\u75db\u54ed\u3002 \u3000\u3000\u91d1\u5200\u5be8\u4e3b\u4ee5\u8896\u63e9\u6cea\uff0c\u5ffd\u800c\u8bf4\u9053\uff1a\u201c\u4f60\u800c\u4eca\u4e0d\u662f\u5c0f\u5b69\u5b50\u4e86\uff0c\u4f60\u4eca\u65e5\u662f\u4e0a\u5c71\u8ba8\u9556\u7684\u5973\u82f1\u96c4\uff0c \u53ef\u4e0d\u80fd\u54ed\u5475\uff01\u5feb\u5feb\u62b9\u5e72\u773c\u6cea\uff0c\u54b1\u4eec\u7684\u4e8b\u8fd8\u672a\u4e86\u5462\uff01\u201d\u767d\u8863\u5c11\u5973\u4e00\u4e2a\u8f6c\u8eab\uff0c\u7a81\u7136\u8f7b\u98d8\u98d8\u5730\u98de\u8eab \u8dc3\u8d77\uff0c\u4e00\u624b\u94a9\u4f4f\u6a2a\u6881\uff0c\u628a\u91d1\u5200\u62d4\u4e86\u4e0b\u6765\uff0c\u8d70\u5230\u5be8\u4e3b\u9762\u524d\uff0c\u6251\u901a\u8dea\u4e0b\uff0c\u4e3e\u5200\u8fc7\u5934\uff0c\u9053\uff1a\u201c\u4f46\u51ed \u53d4\u7956\u5927\u4eba\u5904\u7f6e\uff01\u201d\u6b64\u8a00\u4e00\u51fa\uff0c\u628a\u65b9\u5e86\u5413\u5f97\u9b44\u6563\u9b42\u6d88\uff0c\u5fc3\u9053\uff1a\u201c\u7cdf\u4e86\uff01\u7cdf\u4e86\uff01\u6211\u628a\u8fd9\u5973\u5b69\u5b50\u501a \u4f5c\u9760\u5c71\uff0c\u5374\u539f\u6765\u4ed6\u4eec\u7adf\u662f\u4e00\u5bb6\uff01\u201d \u3000\u3000\u957f\u987b\u8001\u4eba\u63a5\u8fc7\u91d1\u5200\uff0c\u9053\uff1a\u201c\u4f60\u8d77\u6765\uff0c\u5c06\u8fd9\u534a\u622a\u7af9\u6756\u85cf\u8d77\u6765\u5427\u3002\u8fd9\u7af9\u6756\u867d\u7136\u4ee4\u4eba\u75db\u6068\uff0c\u5230 \u5e95\u662f\u4f60\u7237\u7237\u7684\u9057\u7269\u3002\u201d\u767d\u8863\u5c11\u5973\u63a5\u8fc7\u7af9\u6756\uff0c\u6536\u4e86\u6cea\u73e0\uff0c\u53ea\u89c1\u91d1\u5200\u5be8\u4e3b\u62db\u624b\u8bf4\u9053\uff1a\u201c\u65b9\u5e86\uff0c\u4f60 \u8fc7\u6765\u5440\uff01\u201d \u3000\u3000\u65b9\u5e86\u8eab\u8eaf\u98a4\u6296\uff0c\u811a\u90fd\u8f6f\u4e86\uff0c\u91d1\u5200\u5be8\u4e3b\u4e00\u7b11\uff0c\u53eb\u4e24\u4e2a\u4eba\u6276\u4ed6\u8fc7\u6765\uff0c\u9053\uff1a\u201c\u56db\u5341\u4e07\u4e24\u519b\u9977\u90fd \u5728\u8fd9\u513f\uff0c\u4f60\u62bc\u56de\u53bb\u5427\u3002\u201d\u65b9\u5e86\u559c\u51fa\u671b\u5916\uff0c\u53e9\u5934\u9053\u8c22\uff0c\u5ffd\u60f3\u8d77\u5b64\u8eab\u4e00\u4eba\uff0c\u5982\u4f55\u62bc\u8fd0\uff1f\u91d1\u5200\u5be8\u4e3b \u4f3c\u4e4e\u77e5\u9053\u4ed6\u7684\u5fc3\u613f\uff0c\u5411\u65c1\u8fb9\u4e00\u4e2a\u5934\u76ee\u8bf4\u4e86\u51e0\u53e5\uff0c\u6253\u5f00\u5be8\u95e8\uff0c\u8fc7\u4e86\u4e00\u9635\uff0c\u53ea\u89c1\u4e00\u961f\u5175\u4e01\uff0c\u5e26\u7740 \u4e00\u961f\u9aa1\u7fa4\uff0c\u6392\u5728\u5be8\u5916\uff0c\u91d1\u5200\u5be8\u4e3b\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u4eba\u94f6\u90fd\u53d1\u56de\u7ed9\u4f60\uff0c\u4f60\u53ef\u8981\u70b9\u70b9\u6570\u76ee\u4e48\uff1f\u201d\u65b9\u5e86 \u5927\u559c\u4e4b\u4f59\uff0c\u5ffd\u7136\u60f3\u8d77\u4e00\u4e8b\uff0c\u5927\u7740\u80c6\u5b50\u8bf4\u9053\uff1a\u201c\u56db\u5341\u4e07\u4e24\u519b\u9977\u90fd\u5728\u8fd9\u513f\u4e86\uff0c\u53ef\u662f\u8fd8\u6709\u5341\u5339\u5065 \u9aa1\uff0c\u88c5\u8f7d\u7684\u662f\u4e01\u603b\u5175\u8fd0\u7684\u8d27\u7269\uff0c\u6562\u60c5\u5be8\u4e3b\u4e5f\u4e00\u5e76\u53d1\u8fd8\u3002\u201d \u3000\u3000\u91d1\u5200\u5be8\u4e3b\u54c8\u54c8\u5927\u7b11\uff0c\u9053\uff1a\u201c\u4e01\u603b\u5175\u79c1\u8fd0\u7684\u8d27\u7269\u4e48\uff1f\u90a3\u4e9b\u6b63\u5408\u6211\u5c71\u5be8\u4e4b\u7528\uff0c\u6263\u4e0b\u6765\u4e86\uff01\u201d \u65b9\u5e86\u53c8\u662f\u4e00\u60ca\uff0c\u519b\u9977\u867d\u662f\u5f97\u56de\uff0c\u5931\u4e86\u603b\u5175\u7684\u5de8\u8d27\uff0c\u4e5f\u662f\u6b7b\u7f6a\u96be\u9976\uff0c\u53e9\u5934\u8bb7\u8bb7\u8bf4\u9053\uff1a\u201c\u6c42\u5be8\u4e3b \u5f00\u6069\uff0c\u5f00\u6069\uff0c\u518d\u9ad8\u62ac\u8d35\u624b\uff0c\u6551\u6211\u4e00\u547d\uff01\u201d\u91d1\u5200\u5be8\u4e3b\u5927\u7b11\u9053\uff1a\u201c\u4e01\u603b\u5175\u90fd\u820d\u5f97\u7ed9\u6211\uff0c\u4f60\u53cd\u800c\u4e0d \u820d\u5f97\u4e48\uff1f\u201d\u5ffd\u5728\u6000\u4e2d\u6478\u51fa\u4e00\u4e2a\u4fe1\u5c01\u62bd\u51fa\u4e00\u5f20\u5927\u7ea2\u62dc\u8d34\u3002 \u3000\u3000\u65b9\u5e86\u653e\u773c\u4e00\u77a7\uff0c\u53ea\u89c1\u62dc\u5e16\u4e0a\u9762\u5199\u7684\u662f\uff1a\u201c\u656c\u732e\u8584\u793c\u5341\u9a6e\u3002\u5468\u8001\u5927\u4eba\u54c2\u5450\u3002\u804c\u4e01\u5927\u53ef \u5177\u3002\u201d\u65b9\u5e86\u5403\u4e86\u4e00\u60ca\uff0c\u96c1\u95e8\u5173\u7684\u603b\u5175\u4e43\u662f\u671d\u5ef7\u9547\u5b88\u8fb9\u5173\u7684\u5927\u5c06\uff0c\u7adf\u4f1a\u5411\u5f3a\u76d7\u5934\u5b50\u732e\u793c\u79f0\u804c\uff0c \u6b64\u4e8b\u771f\u662f\u4e07\u4e0d\u53ef\u89e3\u3002\u4ed6\u54ea\u91cc\u6599\u60f3\u5f97\u5230\uff0c\u8fd9\u4f4d\u91d1\u5200\u5be8\u4e3b\uff0c\u6b63\u662f\u5341\u5e74\u524d\u7684\u96c1\u95e8\u5173\u603b\u5175\u5468\u5065\uff0c\u5728\u4ed6 \u5f53\u603b\u5175\u4e4b\u65f6\uff0c\u73b0\u4efb\u7684\u603b\u5175\u4e01\u5927\u53ef\u4e0d\u8fc7\u662f\u4ed6\u624b\u4e0b\u7684\u4e00\u4e2a\u526f\u5c06\u3002 \u3000\u3000\u5468\u5065\u634b\u987b\u7b11\u9053\uff1a\u201c\u4f60\u6562\u60c5\u662f\u8fd8\u4e0d\u76f8\u4fe1\uff1f\u597d\uff0c\u6211\u518d\u53eb\u4e00\u4e2a\u4eba\u51fa\u6765\u3002\u201d\u4f20\u4ee4\u4e0b\u53bb\uff0c\u4e0d\u4e00\u4f1a\u4fbf \u5e26\u4e0a\u4e00\u4e2a\u519b\u5b98\uff0c\u6b63\u662f\u96c1\u95e8\u5173\u63a5\u6536\u519b\u9977\u5e76\u4e13\u7ba1\u7cae\u8349\u7684\u519b\u5b98\u3002\u5468\u5065\u7b11\u9053\uff1a\u201c\u8fd9\u56db\u5341\u4e07\u4e24\u519b\u9977\u65e9\u7ecf \u4ed6\u70b9\u8fc7\u65e0\u8bef\uff0c\u4f60\u53ef\u4ee5\u653e\u5fc3\u4e86\u3002\u201d\u65b9\u5e86\u4e0e\u90a3\u519b\u5b98\u672c\u662f\u719f\u8bc6\uff0c\u60f3\u4e0d\u5230\u5374\u5728\u6b64\u76f8\u89c1\uff0c\u5728\u6b64\u4ea4\u5272\uff0c\u5012 \u662f\u56e0\u7978\u5f97\u798f\uff0c\u7701\u4e86\u4ed6\u4e0d\u5c11\u9ebb\u70e6\u3002 \u3000\u3000\u5468\u5065\u8d77\u7acb\u9001\u5ba2\uff0c\u90a3\u519b\u5b98\u548c\u65b9\u5e86\u90fd\u518d\u4e09\u9053\u8c22\uff0c\u5468\u5065\u5bf9\u90a3\u519b\u5b98\u8bf4\u9053\uff1a\u201c\u70e6\u4f60\u4e0a\u590d\u4f60\u5bb6\u603b\u5175\uff0c \u5916\u654c\u5f53\u524d\uff0c\u54b1\u4eec\u8fd8\u662f\u5408\u529b\u5bf9\u4ed8\u7684\u597d\u3002\u6628\u65e5\u4e4b\u7ea6\uff0c\u4e0d\u8981\u5fd8\u4e86\u3002\u201d\u90a3\u519b\u5b98\u8fde\u9053\uff1a\u201c\u662f\uff0c\u662f\uff01\u201d\u5468 \u5065\u6325\u624b\u8bf4\u9053\uff1a\u201c\u5b5f\u7391\uff0c\u4f60\u66ff\u6211\u9001\u4ed6\u4eec\u4e0b\u5c71\u3002\u90a3\u65e5\u6708\u53cc\u65d7\uff0c\u5c31\u8ba9\u4ed6\u4eec\u63d2\u5230\u96c1\u95e8\u5173\u5427\u3002\u201d\u65b9\u5e86\u77e5 \u9053\u6709\u8fd9\u65e5\u6708\u53cc\u65d7\uff0c\u7b49\u4e8e\u91d1\u5200\u5be8\u4e3b\u4eb2\u8eab\u62a4\u9001\uff0c\u6b64\u53bb\u5b9a\u53ef\u65e0\u4e8b\u3002\u53c8\u518d\u8f6c\u8eab\u9053\u8c22\uff0c\u5b5f\u7391\u4e00\u7b11\u800c\u8d77\uff0c \u548c\u65b9\u5e86\u5e76\u80a9\u8d70\u51fa\uff0c\u5bf9\u4ed6\u7b11\u9053\uff1a\u201c\u65b9\u5927\u4eba\uff0c\u4f60\u56de\u53bb\u540e\u53ef\u5f97\u597d\u597d\u518d\u70bc\u5f13\u9a6c\u5475\uff01\u201d\u65b9\u5e86\u60f3\u8d77\u524d\u65e5\u5927 \u5439\u725b\u76ae\u88ab\u4ed6\u6298\u5f13\u52ab\u9977\u8fd9\u4e8b\uff0c\u4e0d\u89c9\u9762\u7ea2\u8fc7\u8033\u3002 \u3000\u3000\u5468\u5065\u5f85\u90a3\u4e9b\u4eba\u53bb\u540e\uff0c\u56de\u8fc7\u5934\u6765\uff0c\u5bf9\u767d\u8863\u5c11\u5973\u7b11\u9053\uff1a\u201c\u4e91\u857e\u4f60\u6765\u5f97\u6b63\u597d\uff01\u201d\u4e91\u857e\u6ee1\u8179\u7591 \u56e2\uff0c\u5341\u5e74\u4e4b\u524d\uff0c\u5979\u4e0e\u5468\u5065\u66fe\u5728\u96c1\u95e8\u5173\u524d\u89c1\u8fc7\u4e00\u9762\uff0c\u90a3\u6b21\u89c1\u9762\uff0c\u4e43\u662f\u5728\u519b\u9a6c\u25a1\u6740\u5f53\u4e2d\uff0c\u4e91\u857e\u4e14 \u53c8\u5e74\u5c0f\uff0c\u9762\u8c8c\u90fd\u672a\u770b\u6e05\uff0c\u60f3\u4e0d\u5230\u4ed6\u5c45\u7136\u8fd8\u8ba4\u8bc6\u81ea\u5df1\u3002\u5468\u5065\u4f3c\u4e4e\u77e5\u9053\u5979\u7684\u5fc3\u601d\uff0c\u7b11\u9053\uff1a\u201c\u4eca\u65e5 \u82e5\u4e0d\u662f\u628a\u4f60\u5f15\u4e0a\u5c71\u6765\uff0c\u903c\u4f60\u732e\u51fa\u7384\u673a\u9038\u58eb\u7684\u72ec\u95e8\u5251\u6cd5\uff0c\u6211\u8fd8\u771f\u4e0d\u6562\u8ba4\u5462\uff01\u201d\u4e91\u857e\u8fd9\u624d\u604d\u7136\u5927 \u609f\u3002\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u4ed6\u4e3a\u4e86\u5f15\u6211\u4e0a\u5c71\uff0c\u7adf\u548c\u96c1\u95e8\u5173\u603b\u5175\u5f00\u4e86\u8fd9\u4e48\u5927\u7684\u73a9\u7b11\uff0c\u8fd9\u4f4d\u53d4\u7956\u7684\u884c\u4e8b\uff0c \u4e5f\u672a\u514d\u592a\u8fc7\u51fa\u4e4e\u4eba\u60c5\u4e4b\u5e38\u4e86\u3002\u201d\u5979\u521d\u51fa\u6c5f\u6e56\uff0c\u5929\u771f\u672a\u706d\uff0c\u53e3\u867d\u4e0d\u8bed\uff0c\u9762\u4e0a\u5374\u73b0\u51fa\u4e0d\u6ee1\u7684\u795e \u60c5\u3002 \u3000\u3000\u5468\u5065\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u597d\u4f84\u5b59\u5973\uff0c\u4f60\u53ef\u77e5\u9053\u6211\u4e3a\u4ec0\u4e48\u8981\u52ab\u519b\u9977\u5417\uff1f\u201d\u4e91\u857e\u9053\uff1a\u201c\u4f60\u4e0d\u662f \u8bf4\u8981\u5f15\u6211\u4e0a\u5c71\u5417\uff1f\u5176\u5b9e\u4f60\u4e0d\u5f15\u6211\u6211\u4e5f\u8981\u6765\u7684\u3002\u201d\u5468\u5065\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u4e91\u857e\u9053\uff1a\u201c\u5341\u5e74\u4e4b\u524d\uff0c \u6f6e\u97f3\u5927\u5e08\u5c06\u6211\u4ece\u96c1\u95e8\u5173\u6551\u51fa\uff0c\u5e26\u6211\u5230\u5ddd\u5317\u5c0f\u5bd2\u5c71\uff0c\u4ea4\u7ed9\u6211\u5e08\u5085\u629a\u517b\u3002\u201d\u5468\u5065\u63d2\u53e3\u9053\uff1a\u201c\u4f60\u7684 \u5e08\u5085\u662f\u4e0d\u662f\u5916\u53f7\u53eb\u98de\u5929\u9f99\u5973\u7684\u53f6\u76c8\u76c8\uff1f\u201d\u4e91\u857e\u70b9\u4e86\u70b9\u5934\uff0c\u5f80\u4e0b\u8bf4\u9053\uff1a\u201c\u6211\u5b66\u4e86\u5341\u5e74\uff0c\u5e08\u5085\u5c31 \u53eb\u6211\u4e0b\u5c71\u3002\u5979\u628a\u7237\u7237\u7684\u8840\u4e66\u4ea4\u7ed9\u4e86\u6211\uff0c\u5979\u8bf4\u6211\u7237\u7237\u6700\u6068\u7684\u4eba\u867d\u7136\u662f\u4ee4\u4ed6\u7267\u9a6c\u4e8c\u5341\u5e74\u7684\u5f20\u5b97 \u5468\uff0c\u4f46\u5bb3\u6b7b\u4ed6\u7684\u5374\u662f\u671d\u5ef7\u7684\u738b\u632f\u3002\u4e0d\u8fc7\u771f\u5b9e\u60c5\u5f62\uff0c\u5e08\u5085\u4e5f\u4e0d\u6e05\u695a\u3002\u5979\u8bf4\u4f60\u662f\u6211\u7237\u7237\u6700\u597d\u7684\u670b \u53cb\uff0c\u5f53\u5e74\u5c31\u662f\u4e3a\u4e86\u6211\u7237\u7237\u60e8\u6b7b\uff0c\u53cd\u51fa\u8fb9\u5173\u7684\u3002\u5979\u542c\u8bf4\u4f60\u843d\u8349\u4e3a\u5bc7\uff0c\u4e0d\u77e5\u662f\u771f\u662f\u5047\uff0c\u56e0\u6b64\u53eb\u6211 \u4e0b\u5c71\u4e4b\u540e\uff0c\u7b2c\u4e00\u4e2a\u5c31\u5e94\u627e\u4f60\u3002\u201d\u5468\u5065\u542c\u4e86\u6447\u4e86\u6447\u5934\uff0c\u53d1\u51fa\u82e6\u7b11\u3002 \u3000\u3000\u4e91\u857e\u8be7\u7136\u505c\u8bed\uff0c\u53ea\u542c\u5f97\u5468\u5065\u8bf4\u9053\uff1a\u201c\u4f60\u7237\u7237\u6b7b\u4e86\u5341\u5e74\uff0c\u6b64\u4e8b\u8fd8\u6210\u60ac\u6848\u3002\u201d\u5f53\u4e0b\u5c06\u5f53\u5e74\u7684 \u4e8b\u8be6\u7ec6\u8bf4\u4e86\uff0c\u9053\uff1a\u201c\u5f20\u5b97\u5468\u548c\u738b\u632f\u4e5f\u6709\u52fe\u7ed3\uff0c\u4e0d\u8fc7\u5c31\u5f53\u5e74\u4e4b\u4e8b\u770b\u6765\uff0c\u4f60\u7684\u7237\u7237\u5b9e\u5728\u6b7b\u5f97\u7cca\u91cc \u7cca\u6d82\uff0c\u4e24\u4eba\u5230\u5e95\u54ea\u4e2a\u662f\u771f\u6b63\u51f6\u624b\uff0c\u6211\u4e5f\u83ab\u540d\u5176\u5999\u3002\u201d\u4e91\u857e\u8bf4\u9053\uff1a\u201c\u6211\u628a\u8fd9\u4e24\u4eba\u90fd\u5f53\u505a\u4ec7\u4eba\uff0c \u5728\u8fd9\u4e24\u4eba\u4e4b\u4e2d\uff0c\u5f20\u5b97\u5468\u66f4\u662f\u7b2c\u4e00\u4e2a\u4ec7\u4eba\u3002\u201d\u5468\u5065\u70b9\u4e86\u70b9\u5934\uff0c\u9053\uff1a\u201c\u8fd9\u4ec7\u53ef\u4e0d\u6613\u62a5\u554a\uff01\u201d\u4e91\u857e \u9053\uff1a\u201c\u6211\u8eab\u8d1f\u4e24\u4ee3\u8840\u4ec7\uff0c\u53ea\u6709\u5c3d\u529b\u800c\u4e3a\uff0c\u6b7b\u800c\u540e\u5df2\u3002\u201d\u5468\u5065\u5fae\u5fae\u53f9\u606f\uff0c\u4e91\u857e\u5f80\u4e0b\u7eed\u9053\uff1a\u201c\u6211 \u5230\u4e86\u96c1\u95e8\u5173\u524d\uff0c\u542c\u5f97\u91d1\u5200\u5be8\u4e3b\u65e5\u6708\u53cc\u65d7\u7684\u5a01\u540d\uff0c\u5c31\u731c\u60f3\u5230\u662f\u53d4\u7956\u5728\u6b64\u5f00\u5c71\u7acb\u5be8\u3002\u4e0d\u8fc7\u8fd8\u62ff\u4e0d \u51c6\uff0c\u6240\u4ee5\u5728\u8774\u8776\u8c37\u4e2d\u4f4f\u4e0b\uff0c\u60f3\u63a2\u542c\u6e05\u695a\u4e4b\u540e\u624d\u6765\u62dc\u8c12\u3002\u201d\u5468\u5065\u7b11\u9053\uff1a\u201c\u8fd9\u4e2a\u6211\u65e9\u77e5\u9053\u3002\u4f60\u53ef \u77e5\u9053\uff0c\u4f60\u4e0b\u5c71\u4e4b\u540e\uff0c\u66fe\u7528\u6885\u82b1\u6697\u5668\u6253\u8d25\u4e86\u51e0\u8def\u5f3a\u4eba\uff0c\u56e0\u6b64\u5728\u6c5f\u6e56\u4e0a\u5f97\u4e86\u6563\u82b1\u5973\u4fa0\u7684\u79f0\u53f7\uff1f\u201d \u4e91\u857e\u9053\uff1a\u201c\u8fd9\u540d\u5b57\u5012\u4e5f\u597d\u542c\uff0c\u4e0d\u8fc7\u6211\u5374\u4e0d\u77e5\u3002\u201d\u5468\u5065\u9053\uff1a\u201c\u4f60\u5728\u8774\u8776\u8c37\u4e2d\u5c45\u4f4f\uff0c\u6211\u624b\u4e0b\u65e9\u5df2 \u6ce8\u610f\u5230\u4e86\u3002\u4e0d\u8fc7\uff0c\u8fde\u6211\u5728\u5185\uff0c\u90fd\u672a\u731c\u5230\u662f\u4f60\u3002\u56e0\u6b64\u6211\u624d\u8bbe\u8ba1\u5c06\u4f60\u5f15\u4e0a\u5c71\u6765\uff0c\u8bd5\u8bd5\u4f60\u7684\u6b66\u827a\uff0c \u770b\u770b\u4f60\u662f\u4f55\u4eba\u3002\u201d\u4e91\u857e\u9053\uff1a\u201c\u53ef\u662f\u4f60\u8fd9\u4e00\u5f15\uff0c\u6211\u53cd\u800c\u4ee5\u4e3a\u6211\u5148\u524d\u7684\u731c\u60f3\u5168\u90fd\u9519\u4e86\u3002\u6211\u4ee5\u4e3a\u82e5 \u662f\u53d4\u7956\uff0c\u90a3\u5c31\u4e07\u4e07\u4e0d\u4f1a\u52ab\u96c1\u95e8\u5173\u519b\u9977\uff0c\u6240\u4ee5\u6211\u624d\u6562\u548c\u53d4\u7956\u76f8\u6597\u3002\u201d\u5468\u5065\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211 \u4ece\u6765\u4e0d\u52ab\u96c1\u95e8\u5173\u519b\u9977\uff0c\u8fd9\u6b21\u52ab\u4e86\uff0c\u867d\u8bf4\u4e3a\u7684\u662f\u4f60\uff0c\u53ef\u4e5f\u4e0d\u5168\u662f\u4e3a\u4f60\uff0c\u8fd9\u91cc\u9762\u7684\u5173\u7cfb\u53ef\u5927\u7740 \u5462\uff01\u201d\u4e91\u857e\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\u5173\u7cfb\uff1f\u201d\u5468\u5065\u9053\uff1a\u201c\u5c0f\u5219\u5173\u7cfb\u96c1\u95e8\u5173\u4e0e\u6211\u8fd9\u5c71\u5be8\u7684\u6bc1\u706d\uff0c\u5927\u5219\u5173\u7cfb \u5927\u660e\u4e5d\u4e07\u91cc\u6cb3\u5c71\u7684\u53d8\u8272\uff01\u201d\u4e91\u857e\u5403\u4e86\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u201d\u5468\u5065\u62ac\u5934\u4e00\u770b\u5929\u8272\uff0c\u77bf\u7136\u8bf4\u9053\uff1a \u201c\u65f6\u5019\u5df2\u4e0d\u65e9\u4e86\uff0c\u4f60\u5feb\u53bb\u7761\u4e00\u89c9\u5427\uff0c\u517b\u597d\u7cbe\u795e\uff0c\u4eca\u665a\u6211\u8fd8\u8981\u4f60\u5e2e\u6211\u53bb\u5e72\u4e00\u4ef6\u5927\u4e8b\u3002\u201d\u628a\u624b\u4e00 \u6325\uff0c\u5927\u5be8\u4e0a\u7acb\u523b\u9e23\u949f\u51fb\u9f13\uff0c\u5148\u524d\u4e0e\u4e91\u857e\u76f8\u6597\u7684\u90a3\u4e2a\u5c11\u5e74\u548c\u53e6\u4e00\u4e2a\u5934\u76ee\u8d70\u4e0a\u524d\u7980\u9053\uff1a\u201c\u8bf7\u5be8\u4e3b \u9063\u5c06\u53d1\u5175\u3002\u201d\u5468\u5065\u70b9\u4e86\u70b9\u5934\uff0c\u6307\u90a3\u5c11\u5e74\u8bf4\u9053\uff1a\u201c\u4ed6\u53eb\u5468\u5c71\u6c11\uff0c\u662f\u4f60\u7684\u53d4\u53d4\uff0c\u6bd4\u4f60\u5374\u5927\u4e0d\u4e86\u51e0 \u5e74\u3002\u201d\u4e91\u857e\u65bd\u4e86\u4e2a\u793c\uff0c\u9053\u58f0\uff1a\u201c\u5f97\u7f6a\u3002\u201d\u5468\u5c71\u6c11\u7b11\u9053\uff1a\u201c\u5dfe\u5e3c\u51fa\u82f1\u96c4\uff0c\u82f1\u96c4\u5728\u5e74\u5c11\uff0c\u4f60\u8fd9\u4e2a \u4f84\u5973\u53ef\u6bd4\u6211\u8fd9\u4e2a\u53d4\u53d4\u5f3a\u591a\u4e86\u3002\u201d\u53eb\u4eba\u5c06\u4e91\u857e\u5e26\u5230\u5e10\u540e\u6b47\u606f\u3002\u4e91\u857e\u542c\u90a3\u53f7\u89d2\u9f50\u9e23\uff0c\u6ee1\u5c71\u4eba\u9a6c\u5954 \u8dd1\u4e4b\u58f0\uff0c\u54ea\u91cc\u7761\u5f97\u7740\u3002 \u3000\u3000\u665a\u996d\u8fc7\u540e\uff0c\u5c71\u5be8\u91cc\u7a7a\u65f7\u65f7\u7684\uff0c\u53ea\u5269\u4e0b\u5be5\u5be5\u51e0\u4e2a\u770b\u5b88\uff0c\u4e91\u857e\u95ee\u9053\uff1a\u201c\u53ef\u662f\u548c\u5b98\u519b\u4f5c\u6218 \u4e48\uff1f\u201d\u5468\u5065\u9053\uff1a\u201c\u4e0d\u662f\u3002\u201d\u4e91\u857e\u9053\uff1a\u201c\u53ef\u662f\u548c\u9791\u5b50\u4f5c\u6218\u4e48\uff1f\u201d\u5468\u5065\u9053\uff1a\u201c\u4e5f\u8fd8\u672a\u53ef\u77e5\u3002\u201d\u4e91 \u857e\u6ee1\u8179\u7591\u56e2\uff0c\u9053\uff1a\u201c\u90a3\u4e48\u53d4\u7956\u8c03\u5175\u9063\u5c06\uff0c\u5374\u662f\u4e3a\u4f55\uff1f\u201d\u5468\u5065\u7b11\u8a00\u9053\uff1a\u201c\u4f60\u5148\u522b\u95ee\uff0c\u4e14\u548c\u6211\u53bb \u4e00\u4e2a\u5730\u65b9\u3002\u201d\u4e0e\u4e91\u857e\u6362\u4e86\u591c\u884c\u8863\u670d\uff0c\u8d70\u51fa\u5c71\u5be8\uff0c\u53ea\u89c1\u6ee1\u5929\u661f\u6597\uff0c\u591c\u5df2\u4e09\u66f4\u3002 \u3000\u3000\u5468\u5065\u5e26\u4e91\u857e\u722c\u4e0a\u4e1c\u9762\u5c71\u5cf0\uff0c\u4e00\u5904\u5904\u4e1b\u83bd\u5bc6\u83c1\uff0c\u8346\u68d8\u6ee1\u9053\uff0c\u8d8a\u5165\u8d8a\u6df1\uff0c\u8d8a\u884c\u8d8a\u9669\uff0c\u4e91\u857e\u6ee1 \u8179\u7591\u56e2\uff0c\u5fc3\u60f3\u53d4\u7956\u4e43\u4e00\u5be8\u4e4b\u4e3b\uff0c\u65e2\u662f\u8c03\u5175\u5916\u51fa\uff0c\u4f55\u4ee5\u81ea\u5df1\u4e0d\u9547\u5b81\u5c71\u5934\uff0c\u5374\u5b64\u8eab\u591c\u884c\uff0c\u5b9e\u662f\u767e \u601d\u83ab\u89e3\u3002\u9759\u591c\u4e4b\u4e2d\uff0c\u5ffd\u542c\u5f97\u6c34\u58f0\u6f7a\u6f7a\uff0c\u8fdc\u5904\u5f02\u58f0\u9aa4\u8d77\uff0c\u4f3c\u662f\u6709\u4eba\u957f\u5578\uff0c\u53c8\u4f3c\u662f\u80e1\u7b33\u6025\u4fc3\u4e4b \u58f0\uff0c\u5468\u5065\u4f38\u624b\u4e00\u62c9\uff0c\u4e0e\u4e91\u857e\u9690\u58f0\u5728\u5ca9\u77f3\u4e4b\u540e\u3002 \u3000\u3000\u6de1\u6708\u758f\u661f\u4e4b\u4e0b\uff0c\u53ea\u89c1\u5468\u5065\u9762\u8272\u51dd\u91cd\u5f02\u5e38\uff0c\u4f0f\u5730\u542c\u58f0\uff0c\u5ffd\u7136\u201c\u566b\u201d\u4e86\u4e00\u58f0\uff0c\u81ea\u8a00\u81ea\u8bed\u8bf4 \u9053\uff1a\u201c\u96be\u9053\u662f\u6211\u6599\u60f3\u9519\u4e86\uff1f\u201d\u4e91\u857e\u575a\u8033\u4e00\u542c\uff0c\u5f02\u58f0\u5df2\u5bc2\uff0c\u602a\u800c\u95ee\u9053\uff1a\u201c\u53d4\u7956\u542c\u5230\u4ec0\u4e48\uff1f\u201d\u5468 \u5065\u5f80\u4e0b\u4e00\u6307\uff0c\u9053\uff1a\u201c\u4f60\u770b\u3002\u201d\u5ced\u58c1\u4e4b\u4e0b\uff0c\u662f\u7fa4\u5c71\u73af\u62b1\u7684\u5c71\u8c37\uff0c\u8c37\u4e2d\u5f00\u9614\uff0c\u7530\u4ea9\u7eb5\u6a2a\uff0c\u501a\u5c71\u4e4b \u5904\uff0c\u5efa\u6709\u4eba\u5de5\u6e56\u575d\uff0c\u77f3\u575d\u7ea6\u6709\u4e24\u5c42\u697c\u9ad8\uff0c\u6e56\u8fb9\u4e0d\u5927\uff0c\u5360\u5730\u4ea6\u6709\u767e\u6570\u5341\u4ea9\uff0c\u767d\u832b\u832b\u4e00\u7247\uff0c\u9ed1\u591c \u751f\u5149\u3002\u5468\u5065\u9053\uff1a\u201c\u8fd9\u91cc\u5c71\u5730\u5168\u9760\u6e56\u6c34\u704c\u6e89\uff0c\u6211\u4eec\u4ee5\u519c\u4e3a\u751f\uff0c\u6240\u4ee5\u8fd9\u4e2a\u6e56\u5b9e\u662f\u6211\u4eec\u5c71\u5be8\u7684\u547d \u8109\u3002\u201d\u5468\u5065\u5341\u5e74\u751f\u805a\uff0c\u628a\u8352\u5c71\u53d8\u4e3a\u826f\u7530\uff0c\u8c08\u8d77\u8fd9\u4e2a\u6e56\u6765\uff0c\u5341\u5206\u5f97\u610f\uff0c\u7ee7\u800c\u53f9\u9053\uff1a\u201c\u53ef\u662f\u9791\u5b50 \u548c\u5b98\u5175\u504f\u4e0d\u8ba9\u6211\u4eec\u5728\u6b64\u5b89\u5c45\uff0c\u524d\u65e5\u6211\u63a5\u5230\u63a2\u5b50\u5bc6\u62a5\uff0c\u8bf4\u662f\u9791\u5b50\u8981\u6d3e\u9ad8\u624b\u5077\u5165\uff0c\u6bc1\u6b64\u6e56\u575d\u3002\u201d \u4e91\u857e\u9053\uff1a\u201c\u6b64\u6e56\u575d\u4f3c\u975e\u51e0\u4eba\u4e4b\u529b\u53ef\u6bc1\u3002\u201d\u5468\u5065\u9053\uff1a\u201c\u4f60\u6709\u6240\u4e0d\u77e5\uff0c\u73b0\u5728\u5df2\u662f\u5f00\u6625\u65f6\u5206\uff0c\u6bcf\u5e74 \u6625\u5b63\uff0c\u8fd9\u91cc\u90fd\u6709\u5c71\u6d2a\u4e3a\u60a3\uff0c\u6211\u4eec\u5728\u4e0a\u6d41\u4e4b\u5730\uff0c\u8fd8\u5efa\u6709\u51e0\u5904\u62e6\u6d2a\u5824\u9632\uff0c\u53ea\u8981\u5c06\u5824\u9632\u5f04\u7a7f\u4e00\u4e2a\u5927 \u6d1e\uff0c\u5c71\u6d2a\u4e00\u6765\uff0c\u6e56\u6c34\u7acb\u523b\u6cdb\u6ee5\uff0c\u90a3\u65f6\u5c71\u8c37\u5c06\u6210\u6cfd\u56fd\uff0c\u5c71\u4e2d\u7684\u6570\u5343\u4ea9\u826f\u7530\uff0c\u90fd\u5c06\u4e3a\u6c34\u6240\u6df9 \u4e86\u3002\u201d\u4e91\u857e\u5207\u9f7f\u9053\uff1a\u201c\u771f\u662f\u53ef\u6068\uff0c\u4ed6\u4eec\u82e5\u6765\uff0c\u6211\u5c31\u7ed9\u4ed6\u4eec\u4e00\u5251\u3002\u201d\u5468\u5065\u9053\uff1a\u201c\u4ed6\u4eec\u6076\u6bd2\u4e4b \u5904\uff0c\u8fd8\u4e0d\u6b62\u6b64\u5462\u3002\u201d\u6b63\u8bf4\u8bdd\u95f4\uff0c\u5ffd\u542c\u5f97\u5f02\u58f0\u53c8\u8d77\uff0c\u5468\u5065\u4e00\u542c\uff0c\u9053\uff1a\u201c\u5947\u602a\uff01\u201d\u4e91\u857e\u95ee\u9053\uff1a \u201c\u4ec0\u4e48\u5947\u602a\uff1f\u201d\u5468\u5065\u8a00\u9053\uff1a\u201c\u542c\u8fd9\u58f0\u97f3\uff0c\u4f3c\u662f\u5341\u591a\u9a91\u9a6c\uff0c\u8ffd\u9010\u4e00\u4e2a\u9003\u72af\u3002\u521a\u624d\u8ffd\u5411\u897f\u65b9\uff0c\u73b0 \u5728\u5374\u6b63\u5bf9\u7740\u6211\u4eec\u8fd9\u8fb9\u6765\u4e86\u3002\u54a6\uff0c\u8fd9\u4e9b\u4eba\u5e76\u4e0d\u719f\u6089\u9053\u8def\uff0c\u4ed6\u4eec\u5728\u90a3\u91cc\u7ed5\u7740\u5708\u5708\uff0c\u8d70\u4e4b\u5b57\u8def\u3002\u58f0 \u97f3\u53c8\u5c0f\u4e86\uff0c\u4f60\u542c\u5f97\u51fa\u4e48\uff1f\u201d\u4e91\u857e\u6447\u4e86\u6447\u5934\uff0c\u5468\u5065\u7b11\u9053\uff1a\u201c\u4f60\u4eca\u540e\u95ef\u8361\u6c5f\u6e56\uff0c\u8fd9\u4f0f\u5730\u542c\u58f0\u7684\u672c \u4e8b\uff0c\u53ef\u5f97\u7ec3\u7ec3\u554a\u3002\u201d\u5f80\u4e0b\u8bf4\u9053\uff1a\u201c\u6211\u5df2\u7b97\u5b9a\u4ed6\u4eec\u4eca\u591c\u5fc5\u5b9a\u6765\u7834\u574f\uff0c\u4f46\u542c\u8fd9\u58f0\u97f3\uff0c\u7adf\u662f\u8ffd\u9010\u9003 \u72af\uff0c\u83ab\u975e\u4ed6\u4eec\u4e4b\u4e2d\u4ea6\u6709\u53d8\u4e48\uff1f\u201d\u4e91\u857e\u6b63\u60f3\u95ee\u5468\u5065\u4f55\u4ee5\u4f1a\u7b97\u5b9a\u4ed6\u4eec\u4eca\u591c\u5fc5\u6765\uff0c\u5ffd\u89c1\u5468\u5065\u6253\u4e86\u4e00 \u4e2a\u624b\u52bf\uff0c\u793a\u610f\u5664\u58f0\uff0c\u5411\u5916\u4e00\u6307\uff0c\u53ea\u89c1\u4e03\u516b\u4e08\u5916\u7684\u4e00\u4e2a\u5c71\u5cf0\uff0c\u5ffd\u7136\u73b0\u51fa\u4e24\u6761\u4eba\u5f71\uff0c\u4ee5\u5468\u5065\u4f0f\u5730 \u542c\u58f0\u7684\u672c\u9886\uff0c\u4e5f\u8981\u5230\u4e86\u4e34\u8fd1\u624d\u80fd\u53d1\u73b0\uff0c\u8fd9\u4e24\u4eba\u6b66\u529f\u4e4b\u9ad8\uff0c\u4e5f\u5c31\u53ef\u4ee5\u60f3\u89c1\u4e86\u3002 \u3000\u3000\u6708\u5149\u4e2d\u53ea\u89c1\u4e24\u4e2a\u80e1\u4eba\u5e76\u7acb\u5c71\u5934\uff0c\u4e00\u4eba\u626c\u97ad\u6307\u9053\uff1a\u201c\u660e\u65e5\u5348\u95f4\uff0c\u8fd9\u65b9\u5706\u767e\u4f59\u91cc\u7684\u5c71\u5be8\uff0c\u4fbf \u8981\u5937\u4e3a\u5e73\u5730\u3002\u54c8\u54c8\uff0c\u8fd9\u56de\u771f\u662f\u5929\u4f51\u6211\u56fd\uff0c\u96c1\u95e8\u5173\u7684\u603b\u5175\u7adf\u4f1a\u5148\u6765\u6c42\u52a9\u3002\u6211\u4eec\u706d\u4e86\u91d1\u5200\u8001\u8d3c\u4e4b \u540e\u518d\u53d6\u96c1\u95e8\u5173\u90a3\u5c31\u6613\u5982\u53cd\u638c\uff0c\u96c1\u95e8\u5173\u4e00\u4e0b\uff0c\u5230\u4eac\u5e08\u4e4b\u8def\uff0c\u5df2\u65e0\u9669\u963b\uff0c\u5927\u660e\u4e5d\u4e07\u91cc\u6cb3\u5c71\uff0c\u90fd\u5c06 \u662f\u6211\u4eec\u7684\u4e86\uff01\u6fb9\u53f0\u5c06\u519b\uff0c\u8fd9\u56de\u4f60\u7684\u529f\u52b3\u53ef\u4e0d\u5c0f\u554a\uff01\u201d\u7eb5\u58f0\u5927\u7b11\uff0c\u58f0\u9707\u5c71\u8c37\u3002\u4e91\u857e\u5403\u4e86\u4e00\u60ca\uff0c \u53ea\u542c\u5f97\u53e6\u4e00\u4eba\u9053\uff1a\u201c\u738b\u7237\u795e\u673a\u5999\u7b97\uff0c\u81ea\u662f\u65e0\u4eba\u53ef\u53ca\uff0c\u4f46\u4ea6\u4e0d\u80fd\u4e0d\u5c0f\u5fc3\u5728\u610f\uff0c\u660e\u65e5\u82e5\u96c1\u95e8\u5173\u7684 \u5b98\u519b\u63a5\u5e94\u4e0d\u4e0a\uff0c\u54b1\u4eec\u7684\u56db\u8def\u5206\u5175\uff0c\u53ef\u4e0d\u90fd\u9677\u4e8e\u9669\u5883\u4e48\uff1f\u82e5\u5c06\u56db\u8def\u7f29\u4e3a\u4e24\u8def\uff0c\u4f3c\u8f83\u7a33\u91cd\u5f97 \u591a\u3002\u201d\u5148\u5934\u90a3\u4eba\u53c8\u5927\u7b11\u9053\uff1a\u201c\u660e\u671d\u5929\u5b50\u6781\u6b32\u527f\u706d\u91d1\u5200\u8001\u8d3c\uff0c\u96c1\u95e8\u5173\u7684\u603b\u5175\u529b\u6709\u4e0d\u53ca\uff0c\u65e0\u6cd5\u53ef \u60f3\uff0c\u8fd9\u624d\u7ea6\u6211\u4eec\u5408\u56f4\uff0c\u6211\u624d\u4e0d\u6015\u4ed6\u4eec\u5931\u7ea6\uff0c\u8fd9\u662f\u5343\u8f7d\u4e00\u65f6\u4e4b\u673a\uff0c\u5927\u5c06\u7528\u5175\uff0c\u5b89\u80fd\u754f\u9996\u754f \u5c3e\uff1f\u201d\u8bf4\u7f62\u53c8\u7eb5\u58f0\u5927\u7b11\u3002 \u3000\u3000\u4e91\u857e\u5fc3\u4e2d\u4e00\u52a8\uff0c\u60f3\u9053\uff1a\u201c\u8fd9\u6fb9\u53f0\u5c06\u519b\u83ab\u975e\u5c31\u662f\u4e8c\u5e08\u4f2f\u5e38\u8bf4\u7684\u90a3\u4e2a\u6fb9\u53f0\u706d\u660e\uff1f\u82e5\u7136\u662f\u4ed6\uff0c \u90a3\u4ed6\u4e5f\u662f\u6211\u7684\u6740\u7236\u4ec7\u4eba\uff0c\u4eca\u665a\u53ef\u4e0d\u80fd\u653e\u8fc7\u4ed6\u4e86\u3002\u201d\u53ea\u542c\u5f97\u88ab\u5524\u505a\u201c\u6fb9\u53f0\u5c06\u519b\u201d\u7684\u4eba\u53c8\u9053\uff1a \u201c\u738b\u7237\u8fd8\u662f\u5c0f\u5fc3\u7684\u597d\uff0c\u6b64\u5730\u6b63\u5728\u4ed6\u4eec\u56db\u9762\u5c71\u5be8\u5305\u56f4\u4e4b\u4e2d\u3002\u201d\u90a3\u80e1\u4eba\u53c8\u5927\u7b11\u9053\uff1a\u201c\u6211\u6b63\u6015\u4ed6\u4eec \u4e0d\u51fa\u6765\uff0c\u6211\u4eec\u51c6\u5907\u6bc1\u5824\u653e\u6c34\uff0c\u5c31\u662f\u8981\u653b\u4ed6\u4eec\u4e4b\u6240\u5fc5\u6551\uff0c\u4ed6\u4eec\u82e5\u6765\u5305\u56f4\uff0c\u90a3\u4e48\u6211\u4eec\u5be5\u5be5\u5341\u6570\u4eba \u4e4b\u529b\uff0c\u5c31\u53ef\u4ee5\u5438\u4f4f\u4ed6\u4eec\u7684\u4e3b\u529b\uff0c\u5916\u9762\u653b\u5c71\u7684\u56db\u8def\u5927\u519b\uff0c\u5c31\u5c06\u5982\u5165\u65e0\u4eba\u4e4b\u5883\u4e86\u3002\u4ee5\u6211\u4eec\u4e24\u4eba\u7684 \u6b66\u827a\uff0c\u54ea\u4f1a\u88ab\u4ed6\u4eec\u6349\u4f4f\uff0c\u6700\u591a\u4e0d\u8fc7\u727a\u7272\u6bc1\u5824\u653e\u6c34\u7684\u5341\u591a\u4e2a\u5c0f\u5175\u3002\u201d\u4e91\u857e\u542c\u4e86\uff0c\u5fc3\u4e2d\u6697\u9a82\u597d\u72e0 \u7684\u6bd2\u8ba1\uff0c\u5bf9\u5468\u5065\u4eca\u665a\u7684\u884c\u4e8b\u4e5f\u5c31\u604d\u7136\u5927\u609f\uff0c\u60f3\u9053\uff1a\u201c\u539f\u6765\u53d4\u7956\u4eca\u65e5\u8c03\u5175\u9063\u5c06\uff0c\u662f\u53bb\u5bf9\u4ed8\u90a3\u56db \u8def\u5077\u88ad\u7684\u80e1\u5175\uff0c\u800c\u7ea6\u6211\u5230\u6b64\uff0c\u5374\u662f\u4e3a\u9632\u5907\u4ed6\u4eec\u6bc1\u5824\u653e\u6c34\uff0c\u53d4\u7956\u771f\u4e0d\u6127\u662f\u5927\u5c06\u4e4b\u624d\uff0c\u6211\u521a\u624d\u8fd8 \u9053\u4ed6\u5b64\u8eab\u72af\u9669\uff0c\u539f\u6765\u5374\u662f\u5fc5\u987b\u8fd9\u6837\u5bf9\u4ed8\u3002\u201d \u3000\u3000\u4e91\u857e\u6293\u7d27\u5251\u67c4\uff0c\u5374\u89c1\u5468\u5065\u9762\u8272\u7d27\u5f20\uff0c\u6447\u9996\u793a\u610f\uff0c\u53eb\u81ea\u5df1\u4e0d\u8981\u8f7b\u4e3e\u5984\u52a8\u3002\u53ea\u542c\u5f97\u90a3\u6fb9\u53f0\u5c06 \u519b\u201c\u54a6\u201d\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u600e\u4e48\u4ed6\u4eec\u8fd8\u4e0d\u6765\u5462\uff1f\u201d\u90a3\u738b\u7237\u5728\u5c71\u5934\u4e0a\u5f80\u6765\u8e31\u6b65\uff0c\u4f3c\u4e5f\u9887\u4e3a\u7126 \u6025\u3002\u6fb9\u53f0\u5c06\u519b\u5ffd\u9053\uff1a\u201c\u54a6\uff0c\u4ed6\u4eec\u5728\u8ffd\u9010\u4ec0\u4e48\u4eba\uff1f\u201d\u53ea\u542c\u5f97\u9a6c\u8e44\u4e4b\u58f0\u81ea\u8fdc\u800c\u8fd1\uff0c\u5ffd\u89c1\u4e00\u9a91\u9a6c\u5728 \u5ce1\u8c37\u4e4b\u4e2d\u51b2\u51fa\uff0c\u80cc\u540e\u5341\u4f59\u9a91\u9a6c\u8854\u5c3e\u75be\u8ffd\uff0c\u9a6c\u5339\u8dc3\u5165\u7530\u4ea9\u4e4b\u4e2d\uff0c\u90a3\u738b\u7237\u9a82\u58f0\uff1a\u201c\u8113\u5305\uff01\u201d\u62c9\u5f00 \u94c1\u5f13\u3002\u6fb9\u53f0\u706d\u660e\u53eb\u9053\uff1a\u201c\u738b\u7237\u4e0d\u8981\u6740\u4ed6\uff01\u201d\u8bdd\u521a\u51fa\u53e3\uff0c\u90a3\u738b\u7237\u5df2\u55d6\u7684\u4e00\u7bad\u5c04\u51fa\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u5468\u5065\u4e00\u62cd\u4e91\u857e\uff0c\u8bf4\u9053\uff1a\u201c\u6740\u90a3\u756a\u738b\uff01\u201d\u4e24\u4eba\u4e00\u8dc3\u800c\u51fa\uff0c\u4e91\u857e\u8eab\u8f7b\u4f3c\u71d5\uff0c \u4e00\u4e2a\u8d77\u4f0f\uff0c\u5df2\u63a0\u4e0a\u5c71\u5934\uff0c\u4eba\u672a\u843d\u5730\uff0c\u6697\u5668\u5148\u53d1\uff0c\u516d\u679a\u201c\u6885\u82b1\u8774\u8776\u9556\u201d\u5206\u6253\u6fb9\u53f0\u706d\u660e\u4e0e\u90a3\u756a\u738b \u7684\u4e0a\u4e2d\u4e0b\u4e09\u8def\u3002\u5979\u6068\u6fb9\u53f0\u706d\u660e\u662f\u5979\u7684\u6740\u7236\u4ec7\u4eba\uff0c\u51fa\u624b\u6781\u5feb\uff0c\u7adf\u7136\u4e0d\u542c\u5468\u5065\u7684\u5429\u5490\uff0c\u5c06\u6697\u5668\u5206 \u88ad\u4e24\u4e2a\u5927\u654c\u3002\u53ea\u542c\u5f97\u6fb9\u53f0\u706d\u660e\u54c8\u54c8\u5927\u7b11\uff0c\u53cc\u94a9\u4e00\u7acb\uff0c\u4e09\u679a\u6885\u82b1\u8774\u8776\u9556\u90fd\u7ed9\u6fc0\u5f97\u53cd\u5c04\u56de\u6765\uff0c\u800c \u90a3\u4e2a\u738b\u7237\u5374\u201c\u54ce\u54df\u201d\u4e00\u58f0\uff0c\u629b\u5f13\u4e8e\u5730\uff0c\u51b2\u524d\u4e24\u6b65\uff0c\u811a\u6b65\u8dc4\u8e09\uff0c\u4f3c\u6b32\u8dcc\u5012\uff0c\u5ffd\u53c8\u7ad9\u5b9a\uff0c\u7834\u53e3\u9a82 \u9053\uff1a\u201c\u597d\u4e2a\u5c0f\u8d3c\uff0c\u6562\u65bd\u6697\u7b97\uff01\u201d\u62bd\u51fa\u8170\u5200\uff0c\u4f3c\u6b32\u4e0a\u524d\uff0c\u8eab\u8eaf\u4e00\u5f2f\uff0c\u5374\u53c8\u7ad9\u7740\u3002\u539f\u6765\u4e91\u857e\u6240\u7528 \u7684\u72ec\u95e8\u6697\u5668\u201c\u6885\u82b1\u8774\u8776\u9556\u201d\uff0c\u4e43\u662f\u98de\u5929\u9f99\u5973\u53f6\u76c8\u76c8\u6240\u4f20\u7684\u7edd\u6280\uff0c\u80fd\u6253\u4eba\u8eab\u4e09\u5341\u516d\u9053\u5927\u7a74\uff0c\u7aef \u7684\u5389\u5bb3\u975e\u5e38\u3002\u90a3\u756a\u738b\u6b66\u529f\u672c\u6781\u9ad8\u5f3a\uff0c\u5374\u56e0\u4e00\u6765\u6b63\u5728\u653e\u7bad\u5c04\u4eba\uff0c\u4e8c\u6765\u4e0d\u9632\u4e91\u857e\u6765\u5f97\u5982\u6b64\u4e4b\u5feb\uff0c \u4e09\u679a\u98de\u9556\u62e8\u5f00\u4e00\u679a\uff0c\u907f\u5f00\u4e00\u679a\uff0c\u5374\u7ed9\u7b2c\u4e09\u679a\u6253\u4e2d\u817f\u5f2f\u7684\u5173\u8282\u8f6f\u9ebb\u7a74\uff0c\u867d\u7136\u4ed7\u7740\u7cbe\u7eaf\u7684\u5185\u529f\uff0c \u4e0d\u81f3\u8dcc\u7ffb\uff0c\u5374\u662f\u4e3e\u6b65\u8270\u96be\uff0c\u4e24\u817f\u9ebb\u8f6f\u3002\u8fd9\u4e5f\u662f\u4ed6\u547d\u4e0d\u8be5\u6b7b\uff0c\u82e5\u7136\u4e91\u857e\u516d\u679a\u98de\u9556\u5168\u90fd\u5c04\u4ed6\uff0c\u90a3 \u4ed6\u5c31\u4e07\u4e07\u9003\u907f\u4e0d\u4e86\u3002 \u3000\u3000\u4e91\u857e\u516d\u9556\u9f50\u53d1\uff0c\u4e24\u4e2a\u654c\u4eba\u90fd\u672a\u8dcc\u5012\uff0c\u4e0d\u7981\u5927\u5403\u4e00\u60ca\u3002\u53ea\u89c1\u90a3\u6fb9\u53f0\u706d\u660e\u4e00\u58f0\u602a\u5578\uff0c\u500f\u5730\u5230 \u4e86\u9762\u524d\uff0c\u8eab\u5f62\u4e4b\u6109\uff0c\u8fdc\u5728\u81ea\u5df1\u4e4b\u4e0a\u3002\u4e91\u857e\u54ac\u7d27\u7259\u5173\uff0c\u7693\u8155\u4e00\u7ffb\uff0c\u5237\u7684\u4e00\u5251\u523a\u51fa\u3002\u6b63\u662f\uff1a \u3000\u3000\u5434\u94a9\u5212\u5904\u5c71\u6cb3\u788e\uff0c\u5251\u5e95\u98ce\u4e91\u53d8\u5e7b\u591a\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u840d\u8e2a\u4fa0\u5f71\u5f55\u300b\u2014\u2014\u7b2c\u4e09\u56de\u3000\u964c\u8def\u9047\u5f3a\u5f92\u5077\u65bd\u5999\u624b\u3000\u98ce\u5c18\u9022\u5f02\u58eb\u6697\u620f\u4f73\u4eba \u6881\u7fbd\u751f\u300a\u840d\u8e2a\u4fa0\u5f71\u5f55\u300b \u7b2c\u4e09\u56de\u3000\u964c\u8def\u9047\u5f3a\u5f92\u5077\u65bd\u5999\u624b\u3000\u98ce\u5c18\u9022\u5f02\u58eb\u6697\u620f\u4f73\u4eba \u3000\u3000\u6fb9\u53f0\u706d\u660e\u53cc\u94a9\u4e00\u7acb\uff0c\u89c1\u662f\u4e00\u4e2a\u5c11\u5973\uff0c\u559d\u9053\uff1a\u201c\u5524\u4f60\u5bb6\u5927\u4eba\u51fa\u6765\uff0c\u6211\u53cc\u94a9\u4e0d\u6740\u65e0\u540d\u5c0f \u8f88\u3002\u201d\u4e91\u857e\u8fd0\u5251\u5982\u98ce\uff0c\u5237\u5237\u4e24\u5251\uff0c\u76f4\u523a\u5230\u4ed6\u7684\u9762\u524d\uff0c\u6fb9\u53f0\u706d\u660e\u53cc\u94a9\u4e00\u62e6\uff0c\u8fd0\u8db3\u5185\u529b\uff0c\u628a\u4e91\u857e \u7684\u5b9d\u5251\u53cd\u5f39\u51fa\u6765\uff0c\u559d\u9053\uff1a\u201c\u91ce\u4e2b\u5934\u4f60\u627e\u6b7b\u4e48\uff1f\u201d\u4e91\u857e\u6beb\u4e0d\u9000\u7f29\uff0c\u4e00\u62db\u201c\u767d\u8679\u8d2f\u65e5\u201d\uff0c\u53c8\u653b\u8fc7 \u53bb\uff0c\u6fb9\u53f0\u706d\u660e\u53cc\u94a9\u4e00\u65cb\uff0c\u500f\u5982\u53cc\u9f99\u51fa\u6d77\u628a\u4e91\u857e\u7684\u5b9d\u5251\u5377\u5728\u5f53\u4e2d\uff0c\u4e91\u857e\u624b\u5fc3\u4e00\u7ffb\uff0c\u90a3\u67c4\u5251\u7a81\u7136 \u53cd\u5f39\u51fa\u6765\u5237\u7684\u4e00\u4e0b\uff0c\u53c8\u4ece\u53cc\u94a9\u4ea4\u9501\u4e4b\u4e2d\u9012\u51fa\u62db\u53bb\u3002\u6fb9\u53f0\u706d\u660e\u201c\u566b\u201d\u4e86\u4e00\u58f0\uff0c\u597d\u751f\u8be7\u5f02\uff0c\u5de6\u94a9 \u4e00\u6307\uff0c\u53f3\u94a9\u4e00\u62c9\uff0c\u5c06\u4e91\u857e\u5b9d\u5251\u5e26\u51fa\u95e8\u5916\uff0c\u903c\u5f97\u5979\u811a\u6b65\u4e0d\u7a33\uff0c\u8fde\u9000\u4e09\u6b65\u3002\u4e91\u857e\u4e0d\u5f85\u5bf9\u65b9\u6740\u5230\uff0c \u98de\u8eab\u53c8\u8d77\uff0c\u5251\u5149\u5288\u9762\u653b\u6765\uff0c\u6fb9\u53f0\u706d\u660e\u7709\u5934\u4e00\u76b1\uff0c\u9053\uff1a\u201c\u8c01\u6559\u4f60\u8fd9\u6837\u6253\u6cd5\uff1f\u4f60\u8fd9\u662f\u4e0d\u987e\u6027\u547d\u7684 \u25a1\u62fc\uff0c\u54ea\u80fd\u5bf9\u4ed8\u5f3a\u654c\uff1f\u201d\u4e91\u857e\u9053\uff1a\u201c\u6211\u5c31\u662f\u8981\u548c\u4f60\u62fc\u547d\uff01\u201d\u6fb9\u53f0\u706d\u660e\u5fc3\u60f3\u5f85\u6211\u628a\u5979\u7684\u5b9d\u5251\u9501 \u62ff\u51fa\u53bb\uff0c\u770b\u5979\u901e\u4e0d\u901e\u5f3a\uff0c\u518d\u95ee\u5979\u4e3a\u4f55\u8981\u4e0e\u6211\u62fc\u547d\uff01\u53cc\u94a9\u4e00\u4e2a\u56de\u65cb\uff0c\u5de6\u53f3\u5708\u8f6c\u518d\u628a\u4e91\u857e\u7684\u5b9d\u5251 \u5377\u5728\u5f53\u4e2d\u3002\u54ea\u77e5\u4e91\u857e\u7cbe\u7075\u4e4b\u6781\uff0c\u5403\u4e86\u6b21\u4e8f\uff0c\u8fd9\u56de\u53ef\u4e0d\u4e0a\u5f53\uff0c\u5979\u8c8c\u4f3c\u9c81\u83bd\uff0c\u5b9e\u5374\u7cbe\u7ec6\uff0c\u624b\u8155\u4e00 \u6c89\uff0c\u5378\u5f00\u6765\u52bf\uff0c\u9661\u7136\u53cd\u524a\u4e0a\u53bb\uff0c\u201c\u5f53\u25a1\u201d\u4e00\u58f0\uff0c\u6fb9\u53f0\u706d\u660e\u5de6\u624b\u94a9\u7684\u6708\u7259\uff0c\u7adf\u7ed9\u524a\u53bb\u4e00\u9f7f\u3002\u6fb9 \u53f0\u706d\u660e\u53eb\u9053\uff1a\u201c\u597d\u5251\u6cd5\uff01\u201d\u53cc\u94a9\u501f\u52bf\u4e00\u62e8\uff0c\u4e91\u857e\u53ea\u89c9\u4e00\u80a1\u5927\u529b\u8feb\u6765\uff0c\u864e\u53e3\u53d1\u9ebb\uff0c\u53ea\u89c1\u94a9\u5149\u95ea \u95ea\uff0c\u6307\u5230\u80f8\u524d\uff0c\u4e91\u857e\u8f6c\u5251\u62b5\u6321\uff0c\u5df2\u6765\u4e0d\u53ca\uff0c\u5ffd\u542c\u5f97\u6fb9\u53f0\u706d\u660e\u559d\u9053\uff1a\u201c\u4f60\u662f\u7384\u673a\u9038\u58eb\u7684\u4ec0\u4e48 \u4eba\uff1f\u201d \u3000\u3000\u4e91\u857e\u8d81\u4ed6\u8fd9\u4e00\u559d\u95ee\uff0c\u957f\u5251\u4e00\u6296\uff0c\u53cd\u5377\u56de\u6765\uff0c\u89e3\u5f00\u4e86\u654c\u4eba\u653b\u52bf\uff0c\u6012\u9053\uff1a\u201c\u51ed\u4f60\u4e5f\u914d\u63d0\u6211\u5e08 \u7956\u540d\u53f7\uff1f\u201d\u6fb9\u53f0\u706d\u660e\u54c8\u54c8\u5927\u7b11\uff0c\u53cc\u94a9\u970d\u970d\uff0c\u628a\u4e91\u857e\u903c\u5f97\u8ddf\u7740\u4ed6\u53cc\u94a9\u65cb\u8f6c\uff0c\u9012\u4e0d\u8fdb\u62db\u3002\u4e91\u857e\u8d8a \u8d25\u8d8a\u72e0\uff0c\u88ab\u6fb9\u53f0\u706d\u660e\u683c\u9000\u4e09\u6b65\uff0c\u53cd\u6251\u4e0a\u56db\u6b65\u3002\u6fb9\u53f0\u706d\u660e\u9053\uff1a\u201c\u4f60\u5e08\u7236\u4e5f\u4e0d\u662f\u6211\u7684\u5bf9\u624b\uff0c\u4f60\u77e5 \u9053\u4e48\uff1f\u201d\u5176\u5b9e\u8fd9\u662f\u6fb9\u53f0\u706d\u660e\u5938\u5927\u4e4b\u8bcd\uff0c\u4ed6\u548c\u8c22\u5929\u534e\u3001\u98de\u5929\u9f99\u5973\u4e8c\u4eba\u529f\u529b\u6089\u654c\uff0c\u90a3\u662f\u771f\u7684\u3002\u4e91 \u857e\u4e0d\u7406\u4e0d\u776c\uff0c\u5251\u8d70\u8fde\u73af\uff0c\u8fde\u8fdb\u9669\u62db\uff0c\u6fb9\u53f0\u706d\u660e\u88ab\u5979\u7f20\u5f97\u6027\u8d77\uff0c\u53cc\u94a9\u4e00\u5c55\uff0c\u94f6\u5149\u66b4\u957f\uff0c\u6070\u5982\u4e24 \u9053\u94f6\u86c7\uff0c\u5c06\u4e91\u857e\u7d27\u7d27\u88f9\u7740\uff0c\u8d70\u4e86\u5341\u4f59\u4e8c\u5341\u62db\uff0c\u4e91\u857e\u6c14\u529b\u4e0d\u652f\uff0c\u62db\u67b6\u4e5f\u67b6\u4e0d\u4f4f\uff0c\u6fb9\u53f0\u706d\u660e\u9aa4\u4e0b \u6740\u624b\uff0c\u5de6\u94a9\u4e00\u5c01\uff0c\u53f3\u94a9\u5411\u5979\u5929\u7075\u76d6\u5288\u4e0b\uff0c\u4e91\u857e\u53eb\u9053\uff1a\u201c\u7239\u7239\u554a\uff0c\u5973\u513f\u4e0d\u80fd\u66ff\u4f60\u62a5\u4ec7\u4e86\uff01\u201d\u594b \u529b\u4e00\u6321\uff0c\u660e\u77e5\u654c\u4eba\u8fd9\u4e00\u62db\u529b\u631f\u5343\u94a7\uff0c\u6321\u4e5f\u6321\u4ed6\u4e0d\u4f4f\uff0c\u4e0d\u6599\u94a9\u5251\u76f8\u4ea4\uff0c\u8fd9\u4e00\u62db\u529b\u9053\u5374\u4e0d\u8fdc\u5982\u60f3 \u50cf\u4e2d\u7684\u6c89\u91cd\u3002\u53ea\u542c\u5f97\u6fb9\u53f0\u706d\u660e\u559d\u9053\uff1a\u201c\u5420\uff0c\u4f60\u8fd9\u5c0f\u4e2b\u5934\u53ef\u662f\u4e91\u9756\u7684\u5b59\u5973\u513f\u4e48\uff1f\u201d\u4e91\u857e\u53cd\u624b\u4e00 \u5251\uff0c\u9a82\u9053\uff1a\u201c\u53db\u56fd\u5978\u8d3c\uff0c\u4f60\u8fd8\u6709\u8138\u63d0\u6211\u7684\u7237\u7237\uff01\u201d\u6fb9\u53f0\u706d\u660e\u52c3\u7136\u5927\u6012\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u6211\u6fb9\u53f0\u706d \u660e\u53cd\u6b63\u662f\u88ab\u4f60\u4eec\u8fd9\u73ed\u7537\u5973\u82f1\u96c4\u3001\u5fe0\u81e3\u4e49\u58eb\u9a82\u5b9a\u7684\u4e86\uff0c\u5c31\u518d\u628a\u4f60\u8fd9\u4f4d\u5fe0\u81e3\u4e4b\u540e\u6740\u6389\u4e5f\u7b97\u4e0d\u4e86\u4ec0 \u4e48\uff01\u201d\u53cc\u94a9\u4e00\u65cb\uff0c\u5357\u6a2a\u5317\u8f6c\uff0c\u8ba4\u771f\u25a1\u6740\u8d77\u6765\u4e86\u3002\u4e91\u857e\u5251\u6cd5\u867d\u7cbe\uff0c\u54ea\u6321\u5f97\u4f4f\uff1f\u773c\u770b\u5c31\u8981\u4e27\u5728\u654c \u4eba\u53cc\u94a9\u4e4b\u4e0b\u3002 \u3000\u3000\u9163\u6597\u4e2d\uff0c\u53ea\u542c\u5f97\u5c71\u8c37\u4e0b\u7530\u4ea9\u4e4b\u95f4\u80e1\u5175\u88ab\u6740\u5f97\u9b3c\u54ed\u795e\u53f7\uff0c\u60f3\u662f\u5468\u5065\u5927\u5c55\u795e\u5a01\uff0c\u5df2\u83b7\u5168\u80dc\u3002 \u4e91\u857e\u5fc3\u4e2d\u4e00\u5bbd\uff0c\u5ffd\u542c\u5f97\u90a3\u756a\u738b\u53eb\u9053\uff1a\u201c\u6fb9\u53f0\u5c06\u519b\uff0c\u4e0d\u8981\u604b\u6218\uff0c\u91d1\u5200\u8001\u8d3c\u6765\u4e86\uff01\u201d \u3000\u3000\u547c\u559d\u58f0\u4e2d\uff0c\u5468\u5065\u63d0\u5200\u7eb5\u4e0a\uff0c\u91d1\u5200\u4e00\u6446\uff0c\u51fa\u624b\u201c\u4e09\u7f8a\u5f00\u6cf0\u201d\u8fde\u73af\u4e09\u62db\uff0c\u5f53\u7684\u4e00\u58f0\uff0c\u628a\u53cc\u94a9 \u9694\u5f00\uff0c\u53f3\u8db3\u8d34\u5730\u4e00\u626b\uff0c\u5927\u58f0\u559d\u9a82\u9053\uff1a\u201c\u4eca\u65e5\u6211\u4e0d\u628a\u4f60\u8fd9\u5978\u8d3c\u788e\u5c38\u4e07\u6bb5\uff0c\u4e5f\u5bf9\u4e0d\u4f4f\u6211\u7684\u91d1 \u5200\uff01\u201d\u6fb9\u53f0\u706d\u660e\u4e00\u8fdb\u4e00\u95ea\uff0c\u672c\u662f\u8d70\u52bf\uff0c\u95fb\u8a00\u51b7\u7b11\uff0c\u53cc\u94a9\u53c8\u523a\u8fc7\u6765\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u6211\u5012\u8981 \u770b\u770b\u4f60\u7684\u91d1\u5200\u6709\u4f55\u672c\u9886\uff1f\u201d\u906e\u3001\u62e6\u3001\u52fe\u3001\u526a\uff0c\u6321\u4e86\u51e0\u62db\uff0c\u7eb5\u58f0\u5927\u7b11\u9053\uff1a\u201c\u4ec0\u4e48\u91d1\u5200\u94f6\u5200\uff0c\u5728 \u6211\u770b\u6765\uff0c\u4e5f\u4e0d\u8fc7\u7834\u94dc\u70c2\u94c1\u3002\u201d\u94a9\u5149\u4e00\u95ea\uff0c\u94ff\u9535\u4e00\u58f0\uff0c\u5728\u91d1\u5200\u80cc\u4e0a\u5212\u4e86\u4e00\u9053\u53e3\u5b50\uff0c\u5468\u5065\u5927\u6012\u8df3 \u8d77\uff0c\u731b\u5288\u4e09\u5200\uff0c\u4e91\u857e\u504f\u950b\u6025\u4e0a\uff0c\u4e5f\u75be\u523a\u4e24\u5251\u3002\u597d\u4e2a\u6fb9\u53f0\u706d\u660e\uff0c\u7adf\u7136\u5de6\u94a9\u62e6\u5200\uff0c\u53f3\u94a9\u654c\u5251\uff0c\u4e0d \u614c\u4e0d\u5fd9\uff0c\u4e00\u4e00\u62c6\u5f00\u3002\u4efb\u662f\u5468\u5065\u529b\u5927\u5200\u6c89\uff0c\u4e91\u857e\u8eab\u8f7b\u5251\u75be\uff0c\u5200\u5251\u8054\u653b\uff0c\u4e5f\u81ea\u653b\u4e0d\u8fdb\u53bb\u3002\u4e09\u4e2a\u4eba \u90fd\u6740\u5f97\u6027\u8d77\uff0c\u8dd1\u9a6c\u706f\u4f3c\u7684\u56e2\u56e2\u75be\u8f6c\uff0c\u6fb9\u53f0\u706d\u660e\u90a3\u5bf9\u53cc\u9f99\u62a4\u624b\u94a9\u5728\u5200\u5149\u5251\u5f71\u4e4b\u4e2d\u6325\u821e\u81ea\u5982\uff0c\u5140 \u662f\u653b\u591a\u5b88\u5c11\u3002 \u3000\u3000\u5468\u5065\u4e0e\u4e91\u857e\u53cc\u6218\u4e0d\u4e0b\uff0c\u597d\u4e0d\u5403\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u4e45\u95fb\u6b64\u4eba\u4e43\u74e6\u523a\u7b2c\u4e00\u52c7\u5c06\uff0c\u679c\u7136\u540d\u4e0d\u865a\u4f20\u3002 \u5982\u6b64\u4eba\u624d\uff0c\u7adf\u4e3a\u80e1\u864f\u6240\u7528\uff0c\u53ef\u60dc\u53ef\u60dc\u3002\u201d\u53ea\u542c\u5f97\u90a3\u756a\u738b\u53c8\u6c11\u9053\uff1a\u201c\u6fb9\u53f0\u5c06\u519b\uff0c\u65f6\u5019\u5df2\u5230\uff0c\u4e0d \u5fc5\u604b\u6218\u4e86\uff01\u201d\u5468\u5065\u731b\u7136\u9192\u8d77\uff0c\u5fc3\u9053\uff1a\u201c\u64d2\u8d3c\u64d2\u738b\uff0c\u6211\u548c\u4ed6\u82e6\u6597\u4f5c\u751a\uff1f\u201d\u594b\u529b\u4e00\u5200\uff0c\u5c06\u6fb9\u53f0\u706d \u660e\u51b2\u9000\u4e09\u6b65\uff0c\u53eb\u9053\uff1a\u201c\u4e91\u857e\u4f60\u5c0f\u5fc3\u5e94\u4ed8\u51e0\u62db\u3002\u201d\u6258\u5730\u8df3\u51fa\uff0c\u4e00\u5200\u671d\u90a3\u756a\u738b\u5288\u4e0b\u3002\u4e91\u857e\u673a\u7075\u4e4b \u6781\uff0c\u7acb\u5373\u8865\u8fdb\u7a7a\u6863\uff0c\u4f38\u5251\u75be\u523a\uff0c\u4f7f\u7684\u90fd\u662f\u7cbe\u5999\u6740\u624b\uff0c\u6fb9\u53f0\u706d\u660e\u6b66\u529f\u867d\u7136\u8fdc\u80dc\u4e8e\u5979\uff0c\u6025\u5207\u4e4b \u95f4\uff0c\u5374\u7adf\u88ab\u7f20\u7740\u3002 \u3000\u3000\u90a3\u756a\u738b\u89c1\u5468\u5065\u4e00\u5200\u5288\u6765\uff0c\u4e3e\u8d77\u8170\u5200\u4e00\u65ab\uff0c\u5f53\u7684\u4e00\u58f0\uff0c\u4e24\u53e3\u5200\u4e00\u9f50\u9707\u5f00\uff0c\u5468\u5065\u5403\u4e86\u4e00\u60ca\uff0c \u5fc3\u9053\uff1a\u8fd9\u756a\u738b\u597d\u5927\u7684\u529b\u6c14\uff01\u8d1f\u4f24\u4e4b\u540e\uff0c\u5c45\u7136\u8fd8\u80fd\u654c\u6211\u3002\u90a3\u756a\u738b\u864e\u53e3\u6d41\u8840\uff0c\u53c8\u4e0d\u80fd\u7eb5\u8dc3\uff0c\u5403\u60ca \u66f4\u751a\u3002\u5468\u5065\u8fde\u5288\u4e09\u5200\uff0c\u4e00\u5200\u731b\u8fc7\u4e00\u5200\uff0c\u5288\u5230\u7b2c\u4e09\u5200\u65f6\uff0c\u90a3\u756a\u738b\u518d\u4e5f\u62b5\u6321\u4e0d\u4f4f\uff0c\u8170\u5200\u7ed9\u8fb1\u5f97\u8131 \u624b\u98de\u53bb\uff0c\u5468\u5065\u6402\u5934\u4e00\u5200\uff0c\u731b\u529b\u65ab\u4e0b\u90a3\u756a\u738b\u5927\u53eb\u4e00\u58f0\uff1a\u201c\u6211\u547d\u4f11\u77e3\uff01\u201d\u987e\u4e0d\u5f97\u817f\u5f2f\u9aa8\u8282\u75bc\u75db\uff0c \u6251\u5730\u4fbf\u6eda\u3002\u5468\u5065\u4e00\u5200\u5288\u7a7a\uff0c\u6325\u5200\u518d\u65ab\uff0c\u731b\u89c9\u80cc\u540e\u91d1\u5200\u5288\u98ce\u4e4b\u58f0\uff0c\u53cd\u624b\u4e00\u683c\uff0c\u53ee\u5f53\u4e00\u58f0\uff0c\u9707\u5f97 \u8eab\u5f62\u4e0d\u7a33\u3002\u53ea\u89c1\u6fb9\u53f0\u706d\u660e\u5df2\u8d8a\u8fc7\u524d\u5934\u53cc\u94a9\u4e00\u63d2\uff0c\u7a7a\u4e86\u53cc\u624b\uff0c\u4e00\u628a\u6293\u8d77\u90a3\u4e2a\u756a\u738b\uff0c\u817e\u8eab\u4fbf\u8dd1\u3002 \u5468\u5065\u54ea\u91cc\u80af\u653e\uff0c\u4e00\u4e2a\u864e\u8df3\uff0c\u626c\u5200\u518d\u65ab\uff0c\u6fb9\u53f0\u706d\u660e\u4e00\u624b\u62b1\u7740\u756a\u738b\uff0c\u970d\u5730\u4e00\u4e2a\u201c\u51e4\u70b9\u5934\u201d\uff0c\u8eab\u8eaf \u4e00\u77ee\uff0c\u6a2a\u638c\u4fbf\u626b\uff0c\u8fd9\u4e00\u62db\u4f7f\u7528\u5f97\u51f6\u9669\u7edd\u4f26\uff0c\u5468\u5065\u62db\u6570\u7528\u8001\uff0c\u56de\u5200\u4e0d\u53ca\uff0c\u5371\u6025\u4e4b\u4e2d\uff0c\u4e5f\u4f7f\u51fa\u6551 \u547d\u9669\u62db\uff0c\u4e00\u4e2a\u5f2f\u5200\u5185\u5411\uff0c\u5200\u67c4\u5f80\u5916\u4e00\u649e\u3002\u53ea\u542c\u5f97\u25a1\u556a\u4e00\u58f0\uff0c\u4e53\u7684\u4e00\u54cd\uff0c\u5468\u5065\u624b\u8155\u7ed9\u638c\u950b\u626b \u4e2d\uff0c\u91d1\u5200\u6389\u5730\uff0c\u6fb9\u53f0\u706d\u660e\u80f8\u53e3\u4e5f\u649e\u4e86\u4e00\u4e0b\uff0c\u75db\u5f97\u773c\u775b\u53d1\u9ed1\uff0c\u5374\u662f\u54fc\u4e5f\u4e0d\u54fc\uff0c\u80cc\u8d77\u756a\u738b\u75be\u8dd1\u3002 \u3000\u3000\u4e91\u857e\u7ed9\u4ed6\u5728\u5341\u62db\u4e4b\u5185\u6740\u9000\uff0c\u773c\u770b\u7740\u53d4\u7956\u529f\u8d25\u5782\u6210\uff0c\u53c8\u7f9e\u53c8\u6012\uff0c\u98de\u8eab\u8d76\u53bb\uff0c\u626c\u624b\u53c8\u662f\u4e09\u679a \u6885\u82b1\u8774\u8776\u9556\u3002\u6fb9\u53f0\u706d\u660e\u5934\u4e5f\u4e0d\u56de\u53cd\u624b\u4e00\u6284\uff0c\u5c06\u6697\u5668\u5168\u6284\u5230\u624b\u4e2d\uff0c\u53cd\u63b7\u8fc7\u6765\uff0c\u529b\u9053\u53f0\u52b2\uff0c\u631f\u98ce \u547c\u5578\uff0c\u4e91\u857e\u81ea\u5df1\u4e5f\u4e0d\u6562\u63a5\uff0c\u903c\u5f97\u95ea\u8fc7\u4e00\u8fb9\u3002\u53ea\u89c1\u90a3\u4e09\u679a\u8774\u8776\u9556\u4e00\u9f50\u5c04\u5230\u4e00\u5757\u5927\u77f3\u4e4b\u4e0a\uff0c\u6e85\u8d77 \u65e0\u6570\u706b\u661f\uff0c\u5374\u5e76\u4e0d\u6389\u4e0b\uff0c\u5168\u90fd\u5728\u77f3\u4e0a\u3002\u4e91\u857e\u5927\u5403\u4e00\u60ca\uff0c\u6fb9\u53f0\u706d\u660e\u75be\u8d70\u5982\u98ce\uff0c\u5df2\u8d8a\u8fc7\u4e00\u4e2a\u5c71 \u5773\u3002 \u3000\u3000\u4e91\u857e\u5c1a\u6b32\u8ffd\u8d76\uff0c\u5ffd\u547c\u63d0\u4e1c\u8fb9\u5c71\u8c37\uff0c\u4e00\u58f0\u70ae\u54cd\uff0c\u5730\u52a8\u5c71\u6447\uff0c\u5468\u5065\u53eb\u9053\uff1a\u201c\u963f\u857e\uff0c\u7a77\u5bc7\u83ab \u8ffd\uff0c\u4e0d\u8981\u8d76\u4e86\u3002\u201d\u7247\u523b\u4e4b\u95f4\uff0c\u53ea\u542c\u5f97\u4e1c\u8fb9\u3001\u5357\u8fb9\u3001\u897f\u8fb9\u3001\u5317\u8fb9\u70ae\u58f0\u63a5\u8fde\u800c\u8d77\uff0c\u970e\u65f6\u95f4\u6740\u58f0\u9707 \u5929\uff0c\u5468\u5065\u6361\u8d77\u91d1\u5200\uff0c\u6a2a\u5200\u5927\u7b11\u9053\uff1a\u201c\u4efb\u4ed6\u9791\u5b50\u4f7f\u5c3d\u5fc3\u673a\uff0c\u4e5f\u7ec8\u662f\u6211\u74ee\u4e2d\u4e4b\u9cd6\u3002\u201d\u4e91\u857e\u6b63\u5f85\u53d1 \u95ee\uff0c\u5468\u5065\u5ffd\u75be\u8dd1\u4e0b\u5c71\u62db\u624b\u8bf4\u9053\uff1a\u201c\u5feb\u6765\u52a9\u6211\u6551\u4eba\u3002\u201d\u4e91\u857e\u83ab\u540d\u5176\u5999\uff0c\u968f\u7740\u4e0b\u5c71\u3002\u53ea\u89c1\u5c38\u6a2a\u904d \u5730\uff0c\u8840\u67d3\u5c71\u8c37\uff0c\u90fd\u662f\u5468\u5065\u91d1\u5200\u5288\u6740\u7684\u80e1\u5175\uff0c\u4e91\u857e\u76ee\u4e0d\u5fcd\u7779\uff0c\u63a9\u9762\u4e0d\u6562\u6b63\u89c6\u3002\u5468\u5065\u5524\u9053\uff1a\u201c\u963f \u857e\uff0c\u4f60\u8eab\u4e0a\u5e26\u6709\u89e3\u6bd2\u7684\u91d1\u521b\u836f\u5417\uff1f\u201d\u56de\u5934\u4e00\u77a5\uff0c\u7b11\u9053\uff1a\u201c\u963f\u857e\uff0c\u4f60\u600e\u4e48\u5566\uff1f\u8fd9\u4e5f\u5bb3\u6015\uff1f\u4f60\u5c06 \u6765\u600e\u4e48\u62a5\u4ec7\u554a\uff01\u201d\u4e91\u857e\u9053\uff1a\u201c\u548c\u8d3c\u4eba\u25a1\u6740\u5012\u6ca1\u4ec0\u4e48\uff0c\u770b\u7740\u8fd9\u4e9b\u80a2\u4f53\u4e0d\u5168\u7684\u6b7b\u4eba\uff0c\u53ef\u4e0d\u5fcd \u5fc3\u3002\u201d\u5468\u5065\u5927\u7b11\u9053\uff1a\u201c\u4f60\u5012\u771f\u662f\u4fa0\u9aa8\u67d4\u80a0\u7684\u5973\u82f1\u96c4\uff0c\u6218\u573a\u4e4b\u4e0a\uff0c\u6bd4\u8fd9\u66f4\u60e8\u7684\u8fd8\u6709\u5462\uff01\u6765\u5427\uff0c \u6765\u5427\uff0c\u770b\u60ef\u4e86\u4f60\u5c31\u4e0d\u6076\u5fc3\u4e86\u3002\u201d\u4e91\u857e\u8d70\u4e86\u8fc7\u53bb\uff0c\u89c1\u5468\u5065\u62b1\u7740\u4e00\u4e2a\u6c49\u4eba\u6253\u626e\u7684\u6b66\u58eb\uff0c\u6b66\u58eb\u80cc\u4e0a \u63d2\u7740\u4e00\u679d\u957f\u7bad\uff0c\u770b\u6837\u5b50\u6ca1\u5165\u4e00\u534a\u4ee5\u4e0a\u3002\u4e91\u857e\u9053\uff1a\u201c\u8fd8\u80fd\u6551\u4e48\uff1f\u201d\u5468\u5065\u9053\uff1a\u201c\u5fc3\u5934\u8fd8\u6709\u4e00\u4e1d\u6c14 \u606f\uff0c\u597d\u574f\u8bd5\u4ed6\u4e00\u8bd5\u5427\u3002\u201d\u4e91\u857e\u9053\uff1a\u201c\u91d1\u521b\u89e3\u6bd2\u4e4b\u836f\uff0c\u6211\u8eab\u4e0a\u6709\u7684\u662f\uff0c\u5c31\u4e0d\u77e5\u5408\u4e0d\u5408\u7528\uff1f\u201d\u5468 \u5065\u63a5\u8fc7\u836f\u6563\uff0c\u5c06\u957f\u7bad\u8f7b\u8f7b\u62d4\u51fa\uff0c\u53ea\u89c1\u7600\u9ed1\u8840\u5757\u968f\u7bad\u800c\u51fa\uff0c\u5468\u5065\u9053\uff1a\u201c\u8fd9\u7bad\u597d\u6bd2\uff01\u201d\u5c06\u836f\u6563\u6577 \u4e0a\uff0c\u53c8\u66ff\u4f24\u8005\u63a8\u8840\u8fc7\u5bab\uff0c\u8fc7\u4e86\u4e9b\u65f6\uff0c\u53ea\u89c1\u4f24\u8005\u53cc\u76ee\u5fae\u5fae\u5f20\u5f00\uff0c\u4f46\u6c14\u82e5\u6e38\u4e1d\uff0c\u4ecd\u662f\u8bf4\u4e0d\u51fa\u8bdd\u3002 \u5468\u5065\u6447\u4e86\u6447\u5934\uff0c\u4e91\u857e\u95ee\u9053\uff1a\u201c\u600e\u4e48\u5566\uff1f\u201d\u5468\u5065\u8a00\u9053\uff1a\u201c\u8fd9\u662f\u8499\u53e4\u89c1\u8840\u5c01\u5589\u7684\u6bd2\u7bad\uff0c\u6ca1\u6709\u4ed6\u4eec \u7684\u89e3\u836f\uff0c\u6551\u6cbb\u4e0d\u4e86\u3002\u4f46\u8fd9\u4eba\u5185\u529f\u5df2\u6709\u51e0\u6210\u706b\u5019\uff0c\u6240\u4ee5\u80fd\u652f\u6491\u81f3\u4eca\u3002\u4f60\u7684\u89e3\u836f\u4e0e\u6211\u7684\u63a8\u62ff\uff0c\u5927 \u7ea6\u53ef\u52a9\u4ed6\u82cf\u9192\u4e00\u65f6\uff0c\u4f46\u4e5f\u8fc7\u4e0d\u4e86\u660e\u65e5\u3002\u201d\u4e91\u857e\u95fb\u8a00\u60e8\u7136\u9053\uff1a\u201c\u6a2a\u76f4\u662f\u6b7b\uff0c\u90a3\u5c31\u4e0d\u5982\u4e0d\u8981\u6551\u4ed6 \u597d\uff0c\u7701\u5f97\u4ed6\u591a\u53d7\u75db\u695a\u3002\u201d\u5468\u5065\u9053\uff1a\u201c\u6b64\u4eba\u9003\u51fa\u80e1\u8fb9\uff0c\u88ab\u9791\u5b50\u7a77\u8ffd\uff0c\u5fc5\u7136\u6709\u6781\u5927\u7684\u79d8\u5bc6\uff0c\u82e5\u4e0d \u8ba9\u4ed6\u4e34\u7ec8\u8bf4\u51fa\uff0c\u4ed6\u6b7b\u4e0d\u7791\u76ee\u3002\u201d\u6478\u51fa\u4e00\u679d\u9ad8\u4e3d\u4eba\u53c2\uff0c\u7528\u5200\u5207\u4e0b\u534a\u622a\uff0c\u653e\u5165\u6b64\u4eba\u53e3\u4e2d\uff0c\u7136\u540e\u8f7b \u8f7b\u5c06\u4ed6\u653e\u5012\u5730\u4e0a\uff0c\u9ad8\u4e3d\u53c2\u53ef\u4f5c\u8865\u6c14\u540a\u547d\u4e4b\u7528\uff0c\u770b\u6765\u5468\u5065\u662f\u60f3\u501f\u836f\u7269\u4e4b\u529b\uff0c\u8ba9\u4ed6\u53ef\u4ee5\u6709\u56de\u5149\u53cd \u7167\u7684\u673a\u4f1a\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u53ea\u542c\u5f97\u56db\u9762\u5c71\u8c37\uff0c\u6740\u58f0\u9707\u5929\uff0c\u6218\u9a6c\u5636\u9e23\uff0c\u70ae\u58f0\u9686\u9686\u7fa4\u5c71\u56de\u54cd\uff0c\u9707\u8033\u6b32\u804b\u3002\u5468\u5065\u5f39 \u5200\u7b11\u9053\uff1a\u201c\u4e0d\u5230\u5929\u660e\uff0c\u9791\u5b50\u5c31\u8981\u5168\u519b\u8986\u6ca1\u3002\u4e91\u857e\u73b0\u5728\u4f60\u53ef\u77e5\u9053\u6211\u52ab\u96c1\u95e8\u5173\u519b\u9977\u7684\u7528\u610f\u4e86 \u5427\uff1f\u201d\u4e91\u857e\u5fc3\u601d\u7075\u6bcf\uff0c\u60f3\u4e86\u4e00\u60f3\uff0c\u629a\u638c\u7b11\u9053\uff1a\u201c\u53d4\u7956\u7aef\u7684\u597d\u8ba1\uff01\u4f60\u52ab\u4e86\u519b\u9977\uff0c\u96c1\u95e8\u5173\u7684\u603b\u5175 \u81ea\u7136\u8981\u552f\u4f60\u4e4b\u547d\u662f\u542c\u4e86\u3002\u9791\u5b50\u7ea6\u4ed6\u4e00\u540c\u51fa\u5175\uff0c\u4f60\u8981\u4ed6\u6309\u5175\u4e0d\u52a8\uff0c\u8fd9\u6837\u4f60\u5728\u660e\u5904\uff0c\u654c\u5728\u6697\u5904\uff0c \u884c\u519b\u90e8\u7f72\u53c8\u5168\u88ab\u6253\u4e71\uff0c\u8fd9\u4e2a\u4ed7\u81ea\u7136\u662f\u4f60\u6253\u8d62\u5566\uff01\u201d\u5468\u5065\u751a\u4e3a\u5f97\u610f\uff0c\u7b11\u8a00\u9053\uff1a\u201c\u4e01\u5927\u53ef\u5176\u5b9e\u4e5f \u8fd8\u4e0d\u7b97\u5f88\u574f\uff0c\u53ea\u662f\u529f\u540d\u5fc3\u91cd\uff0c\u671d\u5ef7\u8981\u4ed6\u56f4\u527f\u5c71\u5be8\uff0c\u4ed6\u81ea\u5df1\u5175\u529b\u4e0d\u591f\uff0c\u6240\u4ee5\u548c\u9791\u5b50\u52fe\u642d\u4e0a\u4e86\u3002 \u6211\u52ab\u4e86\u4ed6\u7684\u519b\u9977\uff0c\u66fe\u5355\u8eab\u8dd1\u53bb\u4f1a\u4ed6\uff0c\u95ee\u4ed6\u613f\u88ab\u997f\u5175\u4e71\u5200\u65a9\u6b7b\uff0c\u8fd8\u662f\u613f\u4e0e\u9791\u5b50\u4e3a\u654c\u3002\u4ed6\u6743\u8861\u8f7b \u91cd\uff0c\u53ea\u597d\u4e56\u4e56\u542c\u6211\u7684\u8bdd\u3002\u201d\u8bf4\u5230\u6b64\u5904\uff0c\u5ffd\u7136\u5fcd\u4e0d\u4f4f\u53d1\u7b11\u3002 \u3000\u3000\u4e91\u857e\u9053\uff1a\u201c\u53d4\u7956\u4f60\u7b11\u4ec0\u4e48\uff1f\u201d\u5468\u5065\u9053\uff1a\u201c\u90a3\u4e01\u5927\u53ef\u5e73\u65e5\u6587\u4e66\u5f80\u6765\uff0c\u5524\u6211\u505a\u2018\u91d1\u5200\u8001 \u8d3c\u2019\uff0c\u89c1\u4e86\u6211\u9762\uff0c\u5374\u53e3\u53e3\u58f0\u58f0\u53eb\u8001\u4e0a\u53f8\u5462\uff01\u201d\u4e91\u857e\u4e5f\u5fcd\u4e0d\u4f4f\u7b11\uff0c\u95ee\u9053\uff1a\u201c\u4ed6\u5728\u6b64\u4e4b\u524d\uff0c\u53ef\u77e5 \u9053\u2018\u91d1\u5200\u8001\u8d3c\u2019\u5c31\u662f\u4ed6\u7684\u8001\u4e0a\u53f8\u4e48\uff1f\u201d\u5468\u5065\u9053\uff1a\u201c\u4ed6\u662f\u6211\u4e00\u624b\u63d0\u62d4\u8d77\u6765\u7684\u4eba\uff0c\u89c1\u8fc7\u6211\u7684\u91d1\u5200 \u672c\u9886\uff0c\u731c\u4e5f\u5e94\u8be5\u731c\u5230\u662f\u6211\uff0c\u4e0d\u8fc7\u4ed6\u5e73\u65e5\u6545\u4f5c\u4e0d\u77e5\u7f62\u4e86\u3002\u6211\u4ee5\u5f80\u4e0e\u5b98\u519b\u5bf9\u654c\uff0c\u603b\u662f\u6234\u7740\u9762\u5177\uff0c \u4e3a\u7684\u5c31\u662f\u4e0d\u60f3\u5b98\u519b\u77e5\u9053\u662f\u6211\u3002\u201d\u4e91\u857e\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d\u5468\u5065\u9053\uff1a\u201c\u82e5\u7136\u5c0f\u5175\u4eec\u4e5f\u90fd\u77e5\u9053\u6211\u662f \u4ed6\u4eec\u7684\u8001\u603b\u5175\uff0c\u90a3\u4e48\u51c6\u6709\u4e00\u534a\u4ee5\u4e0a\u8981\u6295\u8fc7\u6765\u3002\u96c1\u95e8\u5173\u662f\u8fb9\u7586\u91cd\u9547\uff0c\u603b\u5f97\u6709\u5b98\u519b\u9632\u5b88\u54ea\u3002\u6240\u4ee5 \u6211\u8fd9\u91cc\u53ea\u6536\u7eb3\u7a77\u6c49\uff0c\u4e0d\u6536\u5bb9\u5b98\u519b\u3002\u201d \u3000\u3000\u4e91\u857e\u5e74\u7eaa\u5c1a\u5c0f\uff0c\u5e73\u65f6\u54ea\u4f1a\u60f3\u5230\u8fd9\u4e9b\u95ee\u9898\uff0c\u542c\u4e86\u6b64\u8bdd\uff0c\u53ea\u89c9\u53d4\u7956\u542b\u610f\u6781\u6df1\uff0c\u4e0d\u89c9\u6014\u6014\u601d \u7d22\u3002\u5ffd\u542c\u5f97\u5468\u5065\u8bf4\u9053\uff1a\u201c\u597d\u5566\uff0c\u9192\u8fc7\u6765\u5566\u3002\u201d\u53ea\u89c1\u90a3\u4eba\u4e00\u4e2a\u8f6c\u8eab\uff0c\u54d1\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u662f\u8c01\uff1f \u5feb\u5feb\u6276\u6211\u53bb\u89c1\u91d1\u5200\u5be8\u4e3b\u3002\u201d\u5468\u5065\u9053\uff1a\u201c\u6211\u5c31\u662f\u91d1\u5200\u5be8\u4e3b\u3002\u201d\u90a3\u4eba\u8a00\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u9053\u4e91\u9756\u7684\u5b59 \u5973\uff0c\u4e91\u857e\u7684\u4e0b\u843d\u4e48\uff1f\u201d\u4e91\u857e\u5403\u4e86\u4e00\u60ca\u63a5\u53e3\u8bf4\u9053\uff1a\u201c\u6211\u5c31\u662f\u4e91\u857e\uff01\u201d\u90a3\u4eba\u500f\u5730\u5f20\u5927\u53cc\u773c\uff0c\u9053\uff1a \u201c\u4f60\u5c31\u662f\u4e91\u857e\uff0c\u597d\u6781\uff0c\u597d\u6781\uff01\u90a3\u4e48\u6211\u6b7b\u53ef\u7791\u76ee\u4e86\u3002\u4f60\u54e5\u54e5\u5c1a\u5728\u4eba\u95f4\uff0c\u73b0\u5728\u4e0a\u4eac\u5e08\u8003\u8bd5\u53bb\u4e86\uff0c \u4f60\u5feb\u5feb\u524d\u53bb\u627e\u4ed6\u3002\u201d\u4e91\u857e\u5403\u4e86\u4e00\u60ca\uff0c\u5979\u662f\u6709\u4e00\u4e2a\u54e5\u54e5\uff0c\u540d\u53eb\u4e91\u91cd\uff0c\u4e94\u5c81\u4e4b\u65f6\uff0c\u5979\u7684\u7236\u4eb2\u4e91\u6f84 \u5c31\u5c06\u4ed6\u9001\u4e0e\u4e00\u4f4d\u5e08\u554a\u4e3a\u5f92\u3002\u8fd9\u4e8b\u8fd8\u662f\u540e\u6765\u542c\u5e08\u7236\u8bf4\u8d77\u7684\u3002\u539f\u6765\u5979\u5e08\u7956\u7384\u673a\u9038\u58eb\u95e8\u4e0b\uff0c\u5171\u6709\u4e94 \u4eba\uff0c\u9664\u4e86\u81ea\u5df1\u7684\u7236\u4eb2\u4e91\u6f84\uff0c\u672a\u6ee1\u5e08\u4fbf\u5230\u80e1\u8fb9\u5355\u8eab\u6551\u7236\u4e4b\u5916\uff0c\u5176\u4ed6\u56db\u4eba\u5404\u5f97\u5e08\u7956\u4e00\u5957\u6b66\u827a\u3002\u6f6e \u97f3\u548c\u5c1a\u6392\u884c\u7b2c\u4e8c\uff0c\u4f20\u4e86\u4f0f\u9b54\u6756\u6cd5\u548c\u5916\u5bb6\u786c\u529f\uff1b\u8c22\u5929\u534e\u6392\u884c\u7b2c\u4e09\uff0c\u98de\u5929\u9f99\u5973\u53f6\u76c8\u76c8\u6392\u884c\u7b2c\u56db\uff0c \u5404\u5f97\u4e00\u95e8\u5251\u672f\u3002\u5927\u5f92\u5f1f\u53eb\u505a\u8463\u5cb3\uff0c\u4f20\u7684\u5374\u662f\u91d1\u521a\u624b\u7684\u5927\u529b\u9e70\u6253\u6210\u4e00\u7247\u722a\u529f\uff0c\u4e91\u91cd\u4fbf\u662f\u9001\u7ed9\u4ed6 \u505a\u5f92\u5f1f\u3002\u8463\u5cb3\u5230\u4e86\u8499\u53e4\u4e4b\u540e\uff0c\u53c8\u8fdc\u6e38\u85cf\u8fb9\uff0c\u5341\u591a\u5e74\u6765\uff0c\u4e0d\u95fb\u97f3\u8baf\uff0c\u4e91\u91cd\u662f\u751f\u662f\u6b7b\uff0c\u81ea\u4ea6\u65e0\u4eba \u53ef\u77e5\u3002\u800c\u4eca\u4e91\u857e\u7a81\u7136\u542c\u5230\u8fd9\u4e2a\u672a\u89c1\u8fc7\u9762\u7684\u54e5\u54e5\u7684\u6d88\u606f\uff0c\u4e0d\u7981\u60ca\u559c\u4ea4\u96c6\uff0c\u6025\u5fd9\u95ee\u9053\uff1a\u201c\u4f60\u662f \u8c01\uff1f\u201d\u90a3\u4eba\u8a00\u9053\uff1a\u201c\u6211\u662f\u4f60\u54e5\u54e5\u7684\u5e08\u5144\u3002\u201d\u4e91\u857e\u9053\uff1a\u201c\u55ef\uff0c\u90a3\u4e48\u4f60\u4e5f\u662f\u6211\u7684\u5e08\u5144\u4e86\u3002\u201d\u6b63\u60f3 \u95ee\u4ed6\u6d88\u606f\uff0c\u90a3\u4eba\u53cc\u773c\u53d1\u767d\uff0c\u5636\u58f0\u8bf4\u9053\uff1a\u201c\u8fd8\u6709\u66f4\u7d27\u8981\u7684\u4e8b\uff0c\u9791\u5b50\u8981\u56f4\u653b\u4f60\u7684\u5c71\u5be8\uff0c\u65ad\u4f60\u7684 \u6c34\u3002\u201d\u5468\u5065\u9053\uff1a\u201c\u8fd9\u6211\u5df2\u77e5\u9053\uff0c\u4f60\u542c\u89c1\u70ae\u58f0\u4e48\uff1f\u6211\u4eec\u5df2\u7ecf\u6253\u80dc\u4e86\u3002\u201d\u90a3\u4eba\u9762\u73b0\u7b11\u5bb9\uff0c\u65ad\u65ad\u7eed \u7eed\u8bf4\u9053\uff1a\u201c\u4ed6\u4eec\u8fd8\u8981\u51fa\u5175\u653b\u6253\u660e\u671d\u3002\u4f60\u8981\u8bbe\u6cd5\u53bb\u544a\u8bc9\u7687\u4e0a\u3002\u6211\u3001\u6211\u3001\u6211\u8eab\u4e0a\u6709\u4e00\u5c01\u4fe1\uff0c\u662f\u7ed9 \u4f60\u7684\u3002\u597d\u5566\uff0c\u6211\u89c1\u4e86\u4f60\u4eec\u53ef\u4ee5\u53bb\u4e86\u3002\u201d\u58f0\u97f3\u8d8a\u8bf4\u8d8a\u4f4e\uff0c\u8bf4\u5b8c\u4e4b\u540e\uff0c\u5fc3\u4e0a\u5df2\u65e0\u7275\u6302\uff0c\u9762\u5e26\u7b11 \u5bb9\uff0c\u542b\u7b11\u800c\u6b81\u3002\u5468\u5065\u53f9\u4e86\u53e3\u6c14\uff0c\u62bd\u51fa\u4fe1\u7b3a\uff0c\u64e6\u71c3\u706b\u77f3\uff0c\u77a7\u4e86\u4e00\u773c\uff0c\u9053\uff1a\u201c\u662f\u4f60\u5927\u5e08\u4f2f\u5199 \u7684\u3002\u201d\u5b57\u8ff9\u6f66\u8349\uff0c\u60f3\u89c1\u5199\u5f97\u5f88\u662f\u5306\u5fd9\u3002\u5468\u5065\u5c55\u4fe1\u8bfb\u9053\uff1a\u201c\u5cb3\u5c71\u91ce\u5339\u592b\uff0c\u5bc4\u8eab\u6f20\u5916\uff0c\u7caa\u571f\u738b \u4faf\uff0c\u6597\u9152\u81ea\u9189\u3002\u5e73\u751f\u65e0\u6240\u6068\uff0c\u6240\u6068\u8005\u552f\u5c1a\u672a\u8bc6\u8346\u8033\u3002\u201d\u5468\u5065\u5fc3\u9053\u201c\u8fd9\u4e2a\u8463\u5cb3\uff0c\u5374\u4e5f\u9887\u6709\u610f \u601d\u3002\u201d\u518d\u7eed\u4e0b\u53bb\u9053\uff1a\u201c\u5148\u751f\u4e0e\u6211\u867d\u7d20\u6627\u5e73\u751f\uff0c\u7136\u6211\u4e8e\u5929\u534e\u8d24\u5f1f\u53e3\u4e2d\uff0c\u4ea6\u77e5\u5148\u751f\u4fa0\u6c14\u8c6a\u98ce\uff0c\u6c5f \u6e56\u5171\u4ef0\u3002\u5148\u751f\u867d\u5360\u5c71\u81ea\u7acb\uff0c\u62d2\u6c49\u6297\u80e1\uff0c\u671d\u5ef7\u867d\u523b\u8584\u5be1\u6069\uff0c\u7136\u6211\u77e5\u5148\u751f\u5fc5\u4e0d\u613f\u89c1\u80e1\u4eba\u5357\u4e0b\u800c\u7267 \u9a6c\uff0c\u4e2d\u539f\u53d8\u6c49\u800c\u6613\u5937\u90fd\u4e5f\u3002\u201d\u5468\u5065\u53f9\u606f\u9053\uff1a\u201c\u60a0\u60a0\u82cd\u5929\uff0c\u8fd9\u4eba\u5012\u662f\u6211\u7684\u77e5\u5df1\uff01\u201d \u3000\u3000\u5468\u5065\u518d\u7eed\u4e0b\u53bb\u9053\uff1a\u201c\u74e6\u523a\u81ea\u8131\u6b22\u6b7b\u540e\uff0c\u5176\u5b50\u4e5f\u5148\u7ee7\u4f4d\uff0c\u521d\u4e3a\u4e1e\u76f8\uff0c\u5176\u540e\u81ea\u5c01\u56fd\u5e08\uff0c\u603b\u63fd \u519b\u653f\u5927\u6743\uff0c\u6574\u519b\u7ecf\u6b66\uff0c\u6b32\u56fe\u95ee\u9f0e\u4e2d\u539f\uff0c\u8fd1\u590d\u6a84\u53ec\u6c11\u592b\uff0c\u7b79\u96c6\u7cae\u8349\uff0c\u8d77\u5175\u4e4b\u671f\uff0c\u5f53\u4e0d\u5728\u8fdc\u3002\u5916 \u654c\u5f53\u524d\u6b32\u53e9\u5173\uff0c\u671d\u4e2d\u5927\u8001\u72b9\u9189\u68a6\uff0c\u7fd8\u9996\u71d5\u4e91\uff0c\u80fd\u4e0d\u6982\u53f9\uff01\u201d\u5468\u5065\u8bfb\u5230\u6b64\u5904\uff0c\u53f9\u606f\u8bf4\u9053\uff1a\u201c\u671d \u4e2d\u5927\u8001\u72b9\u9189\u68a6\u3002\u82e5\u53ea\u662f\u5982\u9189\u5982\u68a6\uff0c\u90a3\u8fd8\u7b97\u662f\u597d\u7684\u4e86\u3002\u201d\u518d\u8bfb\u4e0b\u53bb\u9053\uff1a\u201c\u5c0f\u5f92\u4e91\u91cd\u5fc3\u5207\u7236\u4ec7\uff0c \u9057\u4e66\u5f52\u56fd\uff0c\u5f7c\u5e74\u8f7b\u8bc6\u6d45\uff0c\u5c82\u77e5\u6743\u81e3\u5f53\u9053\uff0c\u674e\u5e7f\u65e0\u529f\u3002\u613f\u5148\u751f\u5ff5\u5728\u6545\u4eba\uff0c\u8bad\u5f7c\u987d\u52a3\u3002\u95fb\u4e91\u6f84\u5c1a \u6709\u4e00\u5973\u540d\u5524\u4e91\u857e\uff0c\u82e5\u5148\u751f\u77e5\u5176\u4e0b\u843d\uff0c\u8bf7\u4ee5\u5176\u5144\u6d88\u606f\u76f8\u544a\u3002\u518d\u8005\u5929\u534e\u5e08\u5f1f\uff0c\u81ea\u5341\u5e74\u524d\u5728\u80e1\u8fb9\u4e00 \u9762\u4e4b\u540e\u5373\u65ad\u7edd\u97f3\u8baf\u3002\u9053\u8def\u4f20\u8a00\uff0c\u6709\u4e91\u5176\u5df2\u906d\u5f20\u8d3c\u6bd2\u624b\uff0c\u6709\u4e91\u5176\u5df2\u88ab\u7981\u80e1\u5bab\uff0c\u60f3\u5cb3\u5b64\u638c\u96be\u9e23\uff0c \u65e0\u4ece\u63f4\u6551\u3002\u8bf7\u8f6c\u544a\u6f6e\u97f3\u7ea6\u540c\u76c8\u59b9\u901f\u81f3\u80e1\u8fb9\uff0c\u8bf8\u4e8b\u62dc\u6258\uff0c\u4e0d\u6562\u8a00\u8c22\u3002\u201d \u3000\u3000\u5468\u5065\u8bfb\u5b8c\u4e4b\u540e\uff0c\u63a9\u4fe1\u592a\u606f\u3002\u4e91\u857e\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u90a3\u4e48\u6211\u5148\u4e0a\u4eac\u53bb\u627e\u54e5\u54e5\u3002\u201d\u5468\u5065\u77a7\u5979 \u4e00\u773c\uff0c\u82e5\u6709\u6240\u601d\uff0c\u4e45\u4e45\u624d\u59cb\u9053\uff1a\u201c\u4e5f\u597d\u3002\u201d\u4e91\u857e\u671b\u4ed6\u9762\u8272\uff0c\u9887\u89c9\u5947\u5f02\u3002\u5468\u5065\u9053\uff1a\u201c\u6211\u95fb\u8bf4\u5f53 \u4eca\u5929\u5b50\uff0c\u4e0b\u8bcf\u6c42\u5947\u624d\u5f02\u80fd\u4e4b\u58eb\uff0c\u4eca\u79cb\u6b66\u8bd5\uff0c\u7279\u52a0\u6069\u699c\uff0c\u51c6\u6ca1\u6709\u529f\u540d\u7684\u4eba\uff0c\u901a\u8fc7\u521d\u8bd5\u590d\u8bd5\u4e4b \u540e\uff0c\u4e5f\u540c\u5230\u6821\u573a\uff0c\u8003\u6b66\u72b6\u5143\u3002\u4f60\u7684\u54e5\u54e5\uff0c\u5927\u7ea6\u662f\u60f3\u4ece\u6b64\u6c42\u5f97\u51fa\u8eab\uff0c\u501f\u671d\u5ef7\u5175\u529b\uff0c\u62a5\u4f60\u7237\u7237\u7684 \u5927\u4ec7\u3002\u671d\u5ef7\u7279\u52a0\u6069\u699c\uff0c\u5728\u8fb9\u7586\u544a\u6025\uff0c\u9700\u7834\u683c\u7528\u4eba\u4e4b\u9645\uff0c\u7528\u610f\u867d\u662f\u751a\u597d\u4f46\u6050\u6743\u81e3\u628a\u6301\uff0c\u4ea6\u662f\u6709 \u540d\u65e0\u5b9e\u3002\u201d\u8bf4\u5230\u6b64\u5904\uff0c\u62ac\u5934\u4ef0\u671b\u5bd2\u661f\uff0c\u5ffd\u7136\u95ee\u9053\uff1a\u201c\u963f\u857e\uff0c\u4f60\u53ef\u8bfb\u8fc7\u674e\u9675\u7b54\u82cf\u6b66\u4e66\u4e48\uff1f\u201d\u4e91 \u857e\u56e0\u5979\u7684\u7237\u7237\u751f\u524d\u81ea\u6bd4\u82cf\u6b66\uff0c\u56e0\u6b64\u81ea\u8bc6\u8bfb\u4e66\u4e4b\u540e\uff0c\u4fbf\u8981\u5e08\u5085\u4f20\u6559\u5979\u8bfb\u8fd9\u7bc7\u6587\u7ae0\uff0c\u5f53\u4e0b\u70b9\u4e86\u70b9 \u5934\u3002\u5468\u5065\u9053\uff1a\u201c\u674e\u9675\u5f53\u5e74\u5b64\u519b\u6297\u80e1\uff0c\u4ee5\u4e94\u5343\u4e4b\u4f17\uff0c\u5bf9\u5341\u4e07\u4e4b\u519b\uff0c\u7b56\u75b2\u4e4f\u4e4b\u5175\uff0c\u5bf9\u65b0\u7f81\u4e4b\u9a6c\uff0c \u7136\u72b9\u65a9\u5c06\u6434\u65d7\uff0c\u8ffd\u5954\u9010\u5317\u3002\u5176\u540e\u4ee5\u4f17\u5be1\u4e0d\u654c\uff0c\u4e3a\u654c\u6240\u4fd8\uff0c\u5c1a\u601d\u6709\u6240\u4f5c\u4e3a\uff0c\u52ab\u6301\u654c\u5e05\u3002\u4f46\u6c49\u5ba4 \u4e0d\u8c05\uff0c\u7adf\u628a\u4ed6\u7684\u5168\u5bb6\u6740\u4e86\u3002\u6240\u4ee5\u674e\u9675\u624d\u65ad\u4e86\u5f52\u6c49\u4e4b\u5fc3\u3002\u4ed6\u5728\u7ed9\u82cf\u6b66\u7684\u4fe1\u4e2d\u8bf4\u9053\uff1a\u2018\u4e0a\u5ff5\u8001 \u6bcd\uff0c\u4e34\u5e74\u88ab\u622e\uff0c\u59bb\u5b50\u65e0\u8f9c\uff0c\u5e76\u4e3a\u9cb8\u9cb5\uff0c\u8eab\u8d1f\u56fd\u6069\uff0c\u4e3a\u4e16\u6240\u60b2\uff0c\u5b50\u5f52\u53d7\u8363\uff0c\u6211\u7559\u53d7\u8fb1\uff0c\u547d\u4e5f\u5982 \u4f55\uff01\u2019\u8fd9\u51e0\u53e5\u8bdd\u8bf4\u5f97\u60b2\u75db\u6781\u4e86\u3002\u674e\u9675\u884c\u867d\u53ef\u8bae\uff0c\u60c5\u5b9e\u53ef\u60b2\uff01\u201d\u8bf4\u7f62\u4ef0\u5929\u957f\u53f9\u3002\u4e91\u857e\u9053\uff1a\u201c\u53d4 \u7956\uff0c\u4f60\u59cb\u7ec8\u529b\u6297\u80e1\u5175\uff0c\u674e\u9675\u54ea\u80fd\u6bd4\u4f60\uff1f\u201d\u5468\u5065\u9053\uff1a\u201c\u4f60\u4e03\u5c81\u4e4b\u65f6\uff0c\u542c\u4f60\u7237\u7237\u7684\u6545\u610f\uff0c\u73b0\u5728\u6211 \u4e5f\u628a\u6211\u7684\u6545\u4e8b\u8bf4\u4f60\u542c\u542c\u3002\u6211\u6614\u5e74\u9547\u5b88\u8fb9\u5173\uff0c\u5927\u5c0f\u6570\u5341\u4ed7\uff0c\u6bcf\u4ed7\u5fc5\u80dc\uff0c\u8c01\u6599\u7687\u4e0a\u542c\u4fe1\u8c17\u8a00\uff0c\u4e00 \u7eb8\u6587\u4e66\u5c31\u628a\u6211\u514d\u4e86\u3002\u8fd9\u4e5f\u7b97\u4e0d\u4e86\u4ec0\u4e48\uff0c\u4f60\u7684\u7237\u7237\uff0c\u8282\u6bd4\u82cf\u6b66\uff0c\u906d\u9047\u66f4\u60e8\uff0c\u7adf\u88ab\u7687\u4e0a\u8d50\u6b7b\uff0c\u8fd9 \u8fd8\u6709\u5929\u7406\u4e48\uff1f\u56e0\u6b64\uff0c\u6211\u5f53\u5e74\u4e00\u6124\uff0c\u53cd\u51fa\u8fb9\u5173\u3002\u5f53\u65f6\u5c1a\u672a\u6709\u5360\u5c71\u81ea\u7acb\u4e4b\u5fc3\u3002\u540e\u6765\u660e\u671d\u7684\u5929\u5b50\u4e5f \u50cf\u6c49\u671d\u4e4b\u5bf9\u674e\u9675\u4e00\u6837\uff0c\u628a\u6211\u6ee1\u95e8\u6284\u65a9\uff0c\u5e78\u9760\u4e00\u4e2a\u5fe0\u5b9e\u8001\u4ec6\uff0c\u624d\u6551\u51fa\u6211\u7684\u5c0f\u513f\u5b50\uff0c\u4ed6\u5c31\u662f\u524d\u65e5 \u5f15\u4f60\u4e0a\u5c71\u7684\u4eba\u3002\u201d\u4e91\u857e\u6cea\u4ea4\u53cc\u776b\uff0c\u671b\u7740\u5468\u5065\u94c5\u4e00\u822c\u6c89\u91cd\u7684\u9762\u8272\uff0c\u8bf4\u4e0d\u51fa\u8bdd\u3002\u53ea\u89c1\u5468\u5065\u626c\u5200\u4e00 \u6307\uff0c\u6307\u7740\u90a3\u5c71\u5934\u4e0a\u88ab\u5bd2\u98ce\u5439\u5f97\u730e\u730e\u4f5c\u54cd\u7684\u53cc\u65d7\u8bf4\u9053\uff1a\u201c\u53ef\u662f\u6211\u7684\u65d7\u53f7\u8fd8\u662f\u65e5\u6708\u65d7\uff01\u201d \u3000\u3000\u4e91\u857e\u770b\u90a3\u53cc\u65d7\uff0c\u8fce\u98ce\u62db\u5c55\uff0c\u4e00\u8fb9\u7ea2\u65e5\uff0c\u4e00\u8fb9\u7709\u6708\uff0c\u5408\u8d77\u6765\u6b63\u662f\u4e00\u4e2a\u201c\u660e\u201d\u5b57\uff0c\u5fc3\u4e2d\u53f9 \u9053\uff1a\u201c\u539f\u6765\u53d4\u7956\u843d\u8349\u4e3a\u5bc7\uff0c\u4e5f\u8fd8\u5fd8\u4e0d\u4e86\u660e\u671d\u3002\u201d\u5468\u5065\u9053\uff1a\u201c\u4f60\u82e5\u627e\u7740\u54e5\u54e5\uff0c\u53eb\u4ed6\u4e0d\u8981\u8003\u4ec0\u4e48 \u52b3\u5b50\u7684\u6b66\u72b6\u5143\u7684\u3002\u8fd8\u662f\u56de\u5230\u6211\u8fd9\u513f\u6765\u5427\u3002\u671d\u5ef7\u523b\u8584\u5be1\u6069\uff0c\u770b\u5230\u4f60\u7237\u7237\u7684\u4f8b\u5b50\uff0c\u96be\u9053\u8fd8\u4e0d\u5fc3\u5bd2 \u5417\uff1f\u201d\u4e91\u857e\u9053\uff1a\u201c\u53d4\u7956\u8bf4\u7684\u662f\u3002\u201d\u5468\u5065\u6298\u8d77\u4fe1\u7b3a\u653e\u5165\u6000\u4e2d\uff0c\u53c8\u9053\uff1a\u201c\u4f60\u7684\u4e09\u5e08\u4f2f\u8c22\u5929\u534e\u82f1\u98ce \u4fa0\u9aa8\uff0c\u4ea6\u662f\u6211\u6240\u94a6\u4f69\u4e4b\u4eba\uff0c\u60f3\u8d77\u5341\u5e74\u4e4b\u524d\uff0c\u4ed6\u548c\u6f6e\u97f3\u5927\u5e08\u76f8\u7ea6\uff0c\u4e00\u4e2a\u629a\u5b64\uff0c\u4e00\u4e2a\u62a5\u4ec7\u3002\u5982\u4eca \u6f6e\u97f3\u5927\u53d4\u5df2\u6258\u4ed6\u7684\u5e08\u59b9\u5c06\u4f60\u629a\u517b\u6210\u4eba\uff0c\u5929\u534e\u62a5\u4ec7\u4e4b\u4e8b\uff0c\u5374\u8fd8\u662f\u6e3a\u832b\u4e4b\u6781\uff0c\u597d\u4e0d\u4ee4\u4eba\u4f24\u611f\u3002\u201d \u4e91\u857e\u9053\uff1a\u201c\u6211\u53bb\u901a\u77e5\u5bb6\u5e08\uff0c\u53eb\u5979\u548c\u4e8c\u5e08\u4f2f\u4e00\u540c\u8d76\u5230\u80e1\u8fb9\uff0c\u627e\u5bfb\u4e09\u5e08\u4f2f\u4fbf\u662f\u3002\u201d\u5468\u5065\u9053\uff1a\u201c\u4f60 \u53ea\u6709\u4e00\u4e2a\u4eba\uff0c\u600e\u80fd\u4e24\u8fb9\u517c\u987e\uff1f\u8fd9\u6837\u5427\uff0c\u4f60\u8fd8\u662f\u4e13\u5fc3\u53bb\u627e\u4f60\u7684\u54e5\u54e5\uff0c\u6211\u66ff\u4f60\u53bb\u901a\u77e5\u5e08\u7236\u3002\u201d\u4e91 \u857e\u9053\uff1a\u201c\u90a3\u6562\u60c5\u597d\uff0c\u90a3\u4e48\uff0c\u6211\u660e\u5929\u5c31\u52a8\u8eab\u4e86\u3002\u201d\u5468\u5065\u7b11\u4e86\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u518d\u803d\u6401\u51e0\u5929\u3002\u8bba\u6b66 \u529f\u6211\u4e0d\u5982\u4f60\uff0c\u53ef\u662f\u6709\u4e9b\u4e1c\u897f\u4f60\u53ef\u5f97\u5411\u6211\u5b66\u5b66\u554a\u3002\u201d \u3000\u3000\u4e1c\u65b9\u53d1\u767d\uff0c\u70ae\u58f0\u6e10\u5bc2\uff0c\u5468\u5065\u4e0e\u4e91\u857e\u56de\u8f6c\u5927\u5be8\uff0c\u4e2d\u5348\u65f6\u5206\uff0c\u56db\u8def\u4f0f\u5175\u90fd\u544a\u6377\u56de\u5c71\uff0c\u679c\u7136\u662f \u5927\u83b7\u5168\u80dc\uff0c\u628a\u8499\u53e4\u5175\u6740\u5f97\u6e83\u4e0d\u6210\u519b\uff0c\u4fd8\u83b7\u4eba\u9a6c\u65e0\u6570\u3002\u5468\u5065\u4e0b\u4ee4\u7292\u8d4f\uff0c\u5fd9\u4e86\u534a\u5929\uff0c\u5904\u7406\u5b8c\u6bd5\uff0c \u8fd9\u624d\u7b11\u5bf9\u4e91\u857e\u8bf4\u9053\uff1a\u201c\u4f60\u867d\u7136\u6b66\u827a\u9ad8\u5f3a\uff0c\u5bf9\u6c5f\u6e56\u4e0a\u7684\u8def\u9053\u8fd8\u4e0d\u719f\u6089\uff0c\u6211\u53eb\u5c71\u6c11\u6559\u4f60\u3002\u201d\u81ea\u6b64 \u4e00\u8fb9\u4e09\u65e5\uff0c\u5468\u5c71\u6c11\u5c06\u6c5f\u6e56\u4e0a\u7684\u5404\u79cd\u5207\u53e3\u3001\u5e2e\u6d3e\u3001\u7981\u5fcc\uff0c\u4ee5\u53ca\u5404\u8def\u6210\u540d\u82f1\u96c4\uff0c\u5176\u4e2d\u95e8\u6237\u6e0a\u6e90\uff0c \u7ea0\u7eb7\u6069\u6028\u7b49\u7b49\uff0c\u90fd\u8be6\u7ec6\u8bf4\u7ed9\u4e91\u857e\u77e5\u9053\u3002\u4e91\u857e\u4eba\u751a\u806a\u660e\uff0c\u8bb0\u6027\u6781\u597d\uff0c\u5b66\u4e86\u4e09\u65e5\uff0c\u5bf9\u6c5f\u6e56\u4e4b\u4e8b\uff0c \u4e86\u5982\u6307\u638c\u3002\u5468\u5065\u8fd8\u6015\u7ecf\u9a8c\u4e0d\u591f\uff0c\u719f\u4eba\u65e0\u591a\uff0c\u53c8\u5c06\u4e00\u5bf9\u65e5\u6708\u65d7\u9001\u4e86\u7ed9\u5979\u8bf4\u9053\uff1a\u201c\u5317\u4e94\u7701\u6c34\u9646\u4e24 \u8def\u82f1\u96c4\u89c1\u6b64\u65d7\u53f7\uff0c\u90fd\u8981\u76f8\u8ba9\u51e0\u5206\uff0c\u4f60\u82e5\u9047\u5230\u5371\u9669\uff0c\u53ef\u5c06\u6b64\u65d7\u53d6\u51fa\uff0c\u4e0d\u8fc7\uff0c\u4e5f\u4e0d\u8981\u968f\u4fbf\u7528 \u5b83\u3002\u201d\u4e91\u857e\u5fc3\u9053\uff1a\u201c\u6211\u95ef\u8361\u6c5f\u6e56\u6b63\u8981\u5386\u7ec3\u5386\u7ec3\uff0c\u8981\u65d7\u53f7\u4fdd\u62a4\uff0c\u90a3\u8fd8\u6709\u4ec0\u4e48\u610f\u601d\uff1f\u201d\u4e0d\u8fc7\u788d\u4e8e \u53d4\u7956\u597d\u610f\uff0c\u8fd8\u662f\u63a5\u4e86\u3002 \u3000\u3000\u5468\u5065\u53c8\u53d6\u51fa\u51e0\u5957\u7537\u5b50\u8863\u88f3\u4ee5\u53ca\u91d1\u94f6\u73e0\u5b9d\uff0c\u7b11\u9053\uff1a\u201c\u5355\u8eab\u5c11\u5973\uff0c\u72ec\u4e0a\u4eac\u5e08\uff0c\u60f9\u4eba\u6ce8\u76ee\uff0c\u4f60 \u6362\u4e86\u8863\u638c\uff0c\u6613\u9497\u800c\u5f01\u5427\u3002\u8fd9\u70b9\u73e0\u5b9d\uff0c\u7559\u7ed9\u4f60\u5728\u8def\u4e0a\u4f7f\u7528\u3002\u201d\u4e91\u857e\u4e00\u60f3\u4e0d\u9519\uff0c\u4fbf\u6362\u4e86\u8863\u88f3\uff0c\u63a5 \u4e86\u73e0\u5b9d\uff0c\u62dc\u8f9e\u4e0b\u5c71\u3002 \u3000\u3000\u5468\u5065\u9053\uff1a\u201c\u5c71\u6c11\uff0c\u4f60\u9001\u5979\u4e00\u7a0b\u3002\u201d\u51fa\u4e86\u5c71\u5be8\uff0c\u6362\u4e0a\u5feb\u9a6c\uff0c\u4e2d\u5348\u65f6\u5206\uff0c\u5df2\u8d8a\u8fc7\u96c1\u95e8\u5173\uff0c\u8e0f \u4e0a\u524d\u53bb\u4eac\u5e08\u7684\u5927\u8def\u3002\u4e91\u857e\u8a00\u9053\uff1a\u201c\u53d4\u53d4\u4f60\u56de\u53bb\u5427\u3002\u201d\u5468\u5c71\u6c11\u6df1\u6df1\u5730\u770b\u5979\u4e00\u773c\uff0c\u5fae\u559f\u8bf4\u9053\uff1a \u201c\u4f60\u53ef\u5f97\u56de\u6765\u554a\uff01\u201d\u4ecd\u7136\u4e0e\u4e91\u857e\u5e76\u9a6c\u800c\u884c\uff0c\u4f9d\u4f9d\u4e0d\u820d\u3002\u4e91\u857e\u7b11\u9053\uff1a\u201c\u53d4\u53d4\uff0c\u591a\u8c22\u4f60\u4e86\u3002\u4f60\u56de \u53bb\u5427\u3002\u201d\u5468\u5c71\u6c11\u9762\u4e0a\u5ffd\u7136\u73b0\u51fa\u4e00\u5c42\u7ea2\u6655\uff0c\u7b11\u9053\uff1a\u201c\u5176\u5b9e\u6211\u4e5f\u6bd4\u4f60\u5927\u4e0d\u4e86\u51e0\u5e74\uff0c\u54b1\u4eec\u4e0a\u8f88\u867d\u662f \u6df1\u4ea4\uff0c\u5374\u975e\u5144\u5f1f\u3002\u82e5\u8bba\u8d77\u5e74\u9f84\uff0c\u54b1\u4eec\u8fd8\u662f\u5144\u59b9\u76f8\u79f0\uff0c\u66f4\u4e3a\u9002\u5408\u3002\u201d\u4e91\u857e\u597d\u751f\u5947\u602a\uff0c\u5ffd\u60f3\u8d77\u8fd9 \u51e0\u65e5\u6765\uff0c\u5468\u5c71\u6c11\u5bf9\u5979\u5341\u5206\u5173\u5207\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u8fd9\u4e2a\u53d4\u53d4\u4e3a\u4eba\u751a\u597d\uff0c\u53ea\u662f\u8bf4\u8bdd\u6709\u70b9\u4e0d\u5bf9\u52b2 \u513f\u3002\u201d\u4e91\u857e\u5e74\u7eaa\u8fd8\u8f7b\uff0c\u54ea\u60f3\u5f97\u5230\u4ed6\u7684\u7528\u610f\uff0c\u4e00\u7b11\u8bf4\u9053\uff1a\u201c\u4f60\u5acc\u6211\u53eb\u4f60\u53d4\u53d4\u53eb\u8001\u4f60\u4e48\uff1f\u597d\u5427\uff0c \u4ed6\u65e5\u6211\u56de\u6765\u65f6\uff0c\u7980\u8fc7\u53d4\u7956\uff0c\u6539\u6389\u79f0\u547c\u4fbf\u662f\u3002\u201d \u3000\u3000\u5468\u5c71\u6c11\u9762\u7ea2\u8fc7\u8033\uff0c\u4e91\u857e\u4e00\u7b11\u7b56\u9a6c\uff0c\u75be\u9a70\u4e0a\u9053\uff0c\u56de\u9996\u770b\u65f6\uff0c\u53ea\u89c1\u5468\u5c71\u6c11\u8fd8\u5728\u75f4\u75f4\u9065\u671b\u3002 \u3000\u3000\u4e00\u8def\u65e0\u8bdd\uff0c\u7b2c\u4e09\u65e5\u6765\u5230\u9633\u66f2\uff0c\u8fd9\u662f\u6c7e\u9152\u96c6\u6563\u4e4b\u5730\u3002\u5165\u5230\u57ce\u6765\uff0c\u53ea\u89c1\u5904\u5904\u9152\u65d7\u62db\u5c55\uff0c\u4e91\u857e \u8179\u4e2d\u9965\u6e34\uff0c\u5fc3\u9053\uff1a\u201c\u4e45\u95fb\u5c71\u897f\u6c7e\u9152\u7684\u7f8e\u540d\uff0c\u4eca\u65e5\u4e14\u653e\u6000\u4e00\u559d\u3002\u201d\u884c\u5230\u4e00\u5904\u9152\u5bb6\uff0c\u89c1\u95e8\u5916\u624e\u7740 \u4e00\u5339\u767d\u9a6c\uff0c\u56db\u8e44\u5982\u96ea\uff0c\u5341\u5206\u795e\u9a8f\u3002\u4e91\u857e\u884c\u8fd1\u53bb\u770b\uff0c\u5ffd\u89c1\u5899\u89d2\u6709\u6c5f\u6e56\u4eba\u7269\u7684\u8bb0\u53f7\uff0c\u4e91\u857e\u597d\u5947\u5fc3 \u8d77\uff0c\u6b65\u4e0a\u9152\u697c\uff0c\u53ea\u89c1\u4e00\u4e2a\u4e66\u751f\uff0c\u72ec\u636e\u5357\u9762\u4e34\u7a97\u7684\u5ea7\u5934\uff0c\u628a\u9152\u4ee3\u914c\u3002\u4e1c\u9762\u5ea7\u5934\u5374\u662f\u4e24\u4e2a\u7c97\u8c6a\u7537 \u5b50\uff0c\u4e00\u80a5\u4e00\u7626\uff0c\u62ab\u895f\u8fce\u98ce\uff0c\u7b95\u8e1e\u731c\u679a\uff0c\u95f9\u9152\u8f70\u996e\u3002\u4e91\u857e\u65c1\u89c2\u8005\u6e05\uff0c\u53ea\u89c1\u8fd9\u4e24\u4eba\u8c8c\u4f5c\u95f9\u9152\uff0c\u5374 \u65f6\u4e0d\u65f6\u7528\u773c\u89d2\u77a5\u4e66\u751f\u3002 \u3000\u3000\u4e66\u751f\u670d\u9970\u534e\u8d35\uff0c\u4f3c\u4e4e\u662f\u5bcc\u5bb6\u516c\u5b50\uff0c\u4ed6\u72ec\u81ea\u996e\u9152\uff0c\u4e00\u676f\u53c8\u590d\u4e00\u676f\uff0c\u8eab\u5b50\u6447\u6447\u6643\u6643\uff0c\u9887\u4f3c\u6709 \u4e86\u9152\u610f\uff0c\u5ffd\u800c\u9ad8\u58f0\u541f\u9053\uff1a\u201c\u5929\u751f\u6211\u6750\u5fc5\u6709\u7528\uff0c\u5343\u91d1\u6563\u5c3d\u8fd8\u590d\u6765\u3002\u70f9\u7f8a\u5bb0\u725b\u4e14\u4e3a\u4e50\uff0c\u4f1a\u987b\u4e00\u996e \u4e09\u767e\u676f\u3002\u201d\u6447\u5934\u6446\u8111\uff0c\u9178\u6001\u53ef\u63ac\uff0c\u5495\u561f\u561f\u53c8\u5c3d\u4e00\u676f\u3002\u4e91\u857e\u5fc3\u9053\uff1a\u201c\u8fd9\u9178\u79c0\u624d\u771f\u662f\u4e0d\u77e5\u4e16\u9014\u8270 \u9669\uff0c\u5f3a\u76d7\u7aa5\u4f3a\u5728\u65c1\uff0c\u5374\u8fd8\u5728\u653e\u6000\u559d\u9152\u3002\u201d \u3000\u3000\u4e1c\u9762\u5ea7\u5934\u7684\u7626\u6c49\u5b50\u53eb\u9053\uff1a\u201c\u4e00\u996e\u4e09\u767e\u676f\uff0c\u597d\u5440\uff01\u5144\u5f1f\uff0c\u522b\u4eba\u4e00\u996e\u4e09\u767e\u676f\uff0c\u8fd9\u4e09\u676f\u9152\u4f60\u8fd8 \u4e0d\u559d\uff1f\u201d\u4ed6\u7684\u540c\u4f34\u8df3\u4e86\u8d77\u6765\uff0c\u53eb\u9053\uff1a\u201c\u80e1\u8bf4\uff0c\u4f60\u559d\u4e00\u676f\u8981\u6211\u559d\u4e09\u676f\uff01\u201d\u7626\u6c49\u5b50\u9053\uff1a\u201c\u4f60\u4e2a\u5b50 \u6bd4\u6211\u5927\u4e09\u500d\uff0c\u6211\u559d\u4e00\u676f\uff0c\u4f60\u975e\u559d\u4e09\u676f\u4e0d\u884c\u3002\u201d\u80a5\u6c49\u6012\u9053\uff1a\u201c\u653e\u5c41\u653e\u5c41\uff0c\u6211\u504f\u4e0d\u559d\uff01\u201d\u7626\u6c49\u559d \u9053\uff1a\u201c\u4f60\u559d\u4e0d\u559d\uff1f\u201d\u63d0\u8d77\u90a3\u9152\u58f6\u4fbf\u704c\uff0c\u80a5\u6c49\u5927\u6012\uff0c\u7528\u529b\u4e00\u63a8\uff0c\u7ed9\u6c7e\u9152\u6dcb\u4e86\u4e00\u8eab\uff0c\u4e24\u4eba\u6253\u5c06\u8d77 \u6765\uff0c\u8dcc\u8dcc\u649e\u649e\uff0c\u4e00\u4e0b\u5b50\u649e\u5230\u90a3\u4e66\u751f\u7684\u8eab\u4e0a\uff0c\u4e66\u751f\u6012\u559d\u9053\uff1a\u201c\u5c82\u6709\u6b64\u7406\uff01\u201d\u5ffd\u542c\u5f97\u201c\u5f53\u201d\u7684\u4e00 \u58f0\uff0c\u4e66\u751f\u7684\u4e00\u4e2a\u7ee3\u8377\u5305\u6389\u5728\u5730\u4e0a\uff0c\u51e0\u4e2a\u5c0f\u91d1\u952d\u548c\u4e00\u4e32\u73cd\u73e0\u6eda\u4e86\u51fa\u6765\uff0c\u91d1\u952d\u4e5f\u8fd8\u7f62\u4e86\uff0c\u90a3\u73cd\u73e0 \u5149\u5f69\u593a\u76ee\uff0c\u867d\u5728\u767d\u65e5\u6674\u5929\uff0c\u4e5f\u63a9\u4e0d\u7740\u90a3\u5b9d\u6c14\u73e0\u5149\u3002\u4e66\u751f\u4e00\u811a\u8e0f\u7740\u8377\u5305\u5f2f\u8170\u62fe\u90a3\u73cd\u73e0\u91d1\u952d\uff0c\u5927 \u53eb\u9053\uff1a\u201c\u4f60\u4eec\u60f3\u62a2\u4e1c\u897f\u5417\uff1f\u201d\u90a3\u4e24\u4e2a\u6c49\u5b50\u500f\u7136\u505c\u624b\uff0c\u559d\u9053\uff1a\u201c\u8c01\u62a2\u4f60\u7684\u4e1c\u897f\uff1f\u4f60\u7adf\u6562\u8d56\u4eba\uff0c \u770b\u8001\u5b50\u6253\u4f60\uff01\u201d\u65c1\u89c2\u7684\u9152\u5ba2\uff0c\u505a\u597d\u505a\u574f\uff0c\u4e0a\u524d\u529d\u89e3\u3002\u4e91\u857e\u5fc3\u4e2d\u6697\u7b11\u9053\uff1a\u201c\u8fd9\u4e24\u4e2a\u6c49\u5b50\u5206\u660e\u662f \u5f3a\u76d7\u7684\u7ebf\u4eba\uff0c\u501f\u95f9\u9152\u4e3a\u540d\uff0c\u6545\u610f\u649e\u8dcc\u8377\u5305\u67e5\u5bdf\u4e66\u751f\u7684\u865a\u5b9e\u3002\u53ea\u662f\u6709\u6211\u5728\u6b64\uff0c\u53ef\u53eb\u4f60\u4eec\u4e0d\u80fd\u5982 \u613f\u3002\u201d \u3000\u3000\u4e91\u857e\u4e5f\u8d70\u8fc7\u53bb\uff0c\u53cc\u638c\u4e00\u63a8\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u95f9\u9152\u600e\u4e48\u95f9\u5230\u522b\u4eba\u7684\u5ea7\u4f4d\uff1f\u201d\u987a\u624b\u4e00\u6478\uff0c\u628a\u4e24\u4e2a \u6c49\u5b50\u7684\u94f6\u4e24\u90fd\u6478\u4e86\u8fc7\u6765\uff0c\u4e91\u857e\u8eab\u624b\u8f7b\u7075\uff0c\u5728\u55a7\u95f9\u4e4b\u4e2d\u5077\u7a83\u94f6\u4e24\uff0c\u7adf\u65e0\u4e00\u4eba\u77e5\u6653\u3002\u90a3\u4e24\u4e2a\u6c49\u5b50 \u7ed9\u5979\u4e00\u63a8\uff0c\u80f8\u53e3\u53d1\u75db\uff0c\u5403\u4e86\u4e00\u60ca\uff0c\u4e0d\u6562\u518d\u95f9\uff0c\u5600\u5600\u5495\u5495\u5730\u8a00\u9053\uff1a\u201c\u8c01\u53eb\u4ed6\u8d56\u6211\u5077\u4e1c\u897f\uff1f\u201d\u65c1 \u8fb9\u7684\u4eba\u529d\u9053\uff1a\u201c\u597d\u4e86\uff0c\u597d\u4e86\u3002\u4f60\u4eec\u5148\u649e\u4eba\u5bb6\u603b\u662f\u4e0d\u5bf9\uff0c\u56de\u53bb\u597d\u597d\u559d\u9152\u5427\u3002\u201d\u90a3\u4e66\u751f\u4e3e\u8d77\u9152\u676f \u9053\uff1a\u201c\u8001\u5f1f\u53f0\uff0c\u4f60\u4e5f\u559d\u4e00\u676f\u3002\u201d\u9152\u6c14\u55b7\u4eba\uff0c\u4e91\u857e\u9053\uff1a\u201c\u591a\u8c22\u4e86\u3002\u201d\u56de\u5230\u81ea\u5df1\u5ea7\u4f4d\uff0c\u770b\u90a3\u4e24\u4e2a \u6c49\u5b50\u5982\u4f55\u3002 \u3000\u3000\u90a3\u4e24\u4e2a\u6c49\u5b50\u76ef\u4e86\u4e91\u857e\u4e00\u773c\uff0c\u53eb\u9053\uff1a\u201c\u638c\u67dc\u7684\uff0c\u7ed3\u5e10\uff01\u201d\u7626\u7684\u5148\u638f\u94f6\u5b50\uff0c\u4e00\u638f\u6ca1\u6709\uff0c\u9762\u8272 \u53d1\u9752\uff1b\u80a5\u7684\u4e00\u770b\u4e0d\u5999\uff0c\u4f38\u624b\u6478\u81ea\u5df1\u7684\u8377\u5305\uff0c\u94f6\u5b50\u4e5f\u4e0d\u89c1\u4e86\u3002\u4e24\u4eba\u9762\u9762\u76f8\u89d1\uff0c\u505a\u58f0\u4e0d\u5f97\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u786e\u662f\u76d7\u515a\uff0c\u5077\u9e21\u4e0d\u7740\uff0c\u53cd\u8680\u628a\u7c73\uff0c\u660e\u77e5\u662f\u4e91\u857e\u6240\u4e3a\u5374\u6050\u56e0\u5c0f\u5931\u5927\uff0c\u4e0d\u6562\u5f20\u626c\u3002\u638c \u67dc\u7684\u8d70\u6765\u9053\uff1a\u201c\u627f\u60e0\u4e00\u4e24\u4e09\u94b1\u94f6\u5b50\u3002\u201d\u4e24\u4eba\u9762\u8272\u5c34\u5c2c\uff0c\u624b\u653e\u5728\u6000\u4e2d\u62ff\u4e0d\u51fa\u6765\uff0c\u638c\u67dc\u7684\u9053\uff1a \u201c\u4e24\u4f4d\u5927\u7237\u8d4f\u9762\uff0c\u627f\u60e0\u4e00\u4e24\u4e09\u94b1\u3002\u201d\u7626\u6c49\u5b50\u55eb\u5685\u8bf4\u9053\uff1a\u201c\u6302\u5e10\u6210\u4e0d\u6210\uff1f\u201d\u638c\u67dc\u7684\u9762\u8272\u4e00\u53d8\uff0c \u51b7\u7b11\u9053\uff1a\u201c\u6765\u5f80\u7684\u5ba2\u4eba\u90fd\u8981\u6302\u5e10\uff0c\u6211\u4eec\u559d\u897f\u5317\u98ce\u4e0d\u6210\uff1f\u201d\u9152\u4fdd\u4e5f\u5e2e\u7740\u5406\u559d\u9053\uff1a\u201c\u4f60\u4eec\u4e8c\u4eba\u662f \u4e0d\u662f\u5b58\u5fc3\u5728\u8fd9\u91cc\u95f9\u4e8b\uff1f\u95f9\u9152\u3001\u6253\u67b6\u3001\u649e\u4eba\uff0c\u73b0\u5728\u53c8\u8981\u767d\u98df\u767d\u559d\uff1f\u4e0d\u7ed9\u4e5f\u6210\uff0c\u628a\u8863\u670d\u8131\u4e0b \u6765\u3002\u201d\u770b\u70ed\u95f9\u7684\u9152\u5ba2\u54c4\u5802\u5927\u7b11\uff0c\u90fd\u8bf4\u8fd9\u4e24\u4e2a\u6c49\u5b50\u4e0d\u5bf9\uff0c\u8fd9\u4e24\u4e2a\u6c49\u5b50\u65e0\u5948\uff0c\u53ea\u5f97\u8131\u4e0b\u8863\u670d\u3002\u9152 \u4fdd\u9053\uff1a\u201c\u8fd9\u4e24\u4ef6\u5927\u8902\u4e0d\u591f\u3002\u201d\u4f38\u624b\u628a\u4e24\u9876\u5e3d\u5b50\u4e5f\u6458\u4e0b\u6765\uff0c\u9053\uff1a\u201c\u7b97\u54b1\u4eec\u5012\u9709\u4e86\uff0c\u5feb\u6eda\uff0c\u5feb \u6eda\uff01\u201d\u4e24\u4e2a\u6c49\u5b50\u5149\u7740\u5934\uff0c\u4e0a\u8eab\u53ea\u62ab\u4e00\u4ef6\u6c57\u8863\uff0c\u5728\u5bd2\u98ce\u4e2d\u62b1\u5934\u9f20\u7a9c\u800c\u53bb\u3002 \u3000\u3000\u4e91\u857e\u597d\u4e0d\u75db\u5feb\uff0c\u72ec\u81ea\u53c8\u559d\u4e86\u4e24\u676f\uff0c\u89c1\u90a3\u4e66\u751f\u4ecd\u5728\u559d\u9152\uff0c\u731b\u7136\u60f3\u8d77\u8fd9\u4e24\u4e2a\u6c49\u5b50\u4e0d\u8fc7\u662f\u76d7\u515a \u4e2d\u7684\u4f4e\u4e0b\u4e4b\u4eba\uff0c\u4ed6\u4eec\u5403\u4e86\u8fd9\u4e2a\u54d1\u4e8f\uff0c\u5fc5\u7136\u56de\u53bb\u544a\u8bc9\u76d7\u9996\uff0c\u6211\u662f\u4e0d\u6015\uff0c\u8fd9\u4e66\u751f\u7684\u73e0\u5b9d\u5374\u53ef\u4e0d \u4fdd\u3002\u4e8e\u662f\u4e5f\u7ad9\u4e86\u8d77\u6765\uff0c\u53eb\u9053\uff1a\u201c\u638c\u67dc\u7684\uff0c\u7ed3\u5e10\uff01\u201d\u6253\u5b9a\u4e3b\u610f\uff0c\u60f3\u53bb\u8ddf\u8e2a\u8fd9\u4e24\u4e2a\u76d7\u5f92\u3002 \u3000\u3000\u638c\u67dc\u7684\u89c1\u4e91\u857e\u8863\u7740\u751a\u597d\uff0c\u50cf\u4e2a\u516c\u5b50\u54e5\u513f\uff0c\u6ee1\u9762\u5806\u6b22\uff0c\u8d70\u6765\u8bf4\u9053\uff1a\u201c\u627f\u60e0\u4e00\u4e24\u4e8c\u94b1\u3002\u201d\u4e91 \u857e\u4f38\u624b\u4e00\u6478\uff0c\u5979\u628a\u5468\u5065\u9001\u7ed9\u5979\u7684\u91d1\u94f6\u73e0\u5b9d\u5305\u5728\u4e00\u6761\u624b\u5dfe\u4e4b\u5185\uff0c\u4e00\u6478\u7adf\u4e0d\u89c1\u4e86\u4e0d\u7531\u5f97\u5927\u5403\u4e00 \u60ca\uff0c\u518d\u6478\u5de6\u8fb9\u7684\u8863\u888b\uff0c\u521a\u624d\u5077\u6765\u7684\u51e0\u4e24\u94f6\u5b50\u4e5f\u4e0d\u89c1\u4e86\u3002\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u867d\u7136\u662f\u6625\u5bd2\u51db\u51bd\uff0c \u989d\u4e0a\u7684\u6c57\u73e0\u4e5f\u6025\u51fa\u6765\u7684\u3002\u638c\u67dc\u7684\u597d\u4e0d\u6000\u7591\uff0c\u770b\u4e91\u857e\u8863\u670d\u4e3d\u90fd\uff0c\u53c8\u4e0d\u50cf\u662f\u6ca1\u94b1\u7684\u6837\u5b50\uff0c\u7591\u60d1 \u9053\uff1a\u201c\u4f60\u8001\u53ef\u662f\u6ca1\u6709\u6563\u94f6\uff1f\u5143\u5b9d\u91d1\u952d\u90fd\u6210\uff0c\u5c0f\u5e97\u66ff\u4f60\u627e\u6362\uff0c\u4e0d\u4f1a\u9a97\u4f60\u7684\u6210\u8272\u3002\u201d\u4e91\u857e\u66f4\u662f\u7740 \u6025\uff0c\u751f\u6015\u4e5f\u88ab\u8131\u4e0b\u8863\u670d\uff0c\u90a3\u5c31\u8981\u5f53\u5802\u51fa\u4e11\u4e86\uff01 \u3000\u3000\u638c\u67dc\u7684\u89c1\u5979\u5de6\u6478\u53f3\u6478\uff0c\u9762\u8272\u6e10\u6e10\u4e0d\u5bf9\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u5927\u7237\uff0c\u4f60\u600e\u4e48\u5566\uff1f\u201d\u90a3\u4e66\u751f\u5ffd\u7136\u6447\u6447 \u6446\u6446\u8d70\u4e86\u51fa\u6765\uff0c\u541f\u9053\uff1a\u201c\u56db\u6d77\u4e4b\u5185\u7686\u670b\u53cb\uff0c\u5343\u91d1\u6563\u5c3d\u8fd8\u590d\u6765\u3002\u8fd9\u4f4d\u5c0f\u54e5\u7684\u5e10\u6211\u4f1a\u4e86\u3002\u201d\u6478\u51fa \u4e00\u952d\u94f6\u5b50\uff0c\u8db3\u6709\u5341\u4e24\uff0c\u629b\u7ed9\u638c\u67dc\u9053\uff1a\u201c\u591a\u4e0b\u7684\u7ed9\u4f60\uff01\u201d\u638c\u67dc\u7684\u559c\u51fa\u671b\u5916\uff0c\u8fde\u8fde\u591a\u8c22\u3002 \u3000\u3000\u4e91\u857e\u9762\u7ea2\u8fc7\u8033\uff0c\u4f4e\u58f0\u9053\u8c22\uff0c\u4e66\u751f\u9053\uff1a\u201c\u8c22\u4ec0\u4e48\uff1f\u6211\u6559\u4f60\u4e00\u4e2a\u79d8\u5ed6\uff0c\u4f60\u4e0b\u4e00\u6b21\u559d\u9152\u65f6\u591a\u7a7f \u4e24\u4ef6\u8863\u88f3\uff0c\u7ed3\u5e10\u65f6\u5c31\u4e0d\u6015\u4e86\u3002\u201d\u9152\u6c14\u6251\u4eba\uff0c\u6447\u6447\u6643\u6643\uff0c\u4e0d\u7406\u4e91\u857e\uff0c\u4e0b\u697c\u81ea\u53bb\u3002\u4e91\u857e\u597d\u751f\u7740 \u607c\uff0c\u5fc3\u9053\uff1a\u201c\u597d\u4e2a\u4e0d\u77e5\u793c\u8c8c\u7684\u72c2\u751f\uff0c\u521a\u624d\u82e5\u4e0d\u662f\u6211\u53bb\u6551\u4f60\uff0c\u53ea\u6015\u4f60\u7684\u4e1c\u897f\u65e9\u5df2\u88ab\u4eba\u62a2\u53bb \u4e86\u3002\u201d \u3000\u3000\u4e91\u857e\u56db\u9762\u4e00\u671b\uff0c\u6ee1\u5802\u9152\u5ba2\u4e4b\u4e2d\uff0c\u770b\u4e0d\u51fa\u8c01\u662f\u53ef\u7591\u4e4b\u4eba\uff0c\u5fc3\u4e2d\u7eb3\u95f7\uff0c\u60f3\u4e0d\u5230\u5728\u8fd9\u91cc\u4f1a\u78b0\u89c1 \u5982\u65af\u5999\u624b\uff0c\u76d7\u5f92\u4e4b\u4e8b\u65e0\u5fc3\u518d\u7406\uff0c\u51fa\u4e86\u9152\u697c\uff0c\u8de8\u4e0a\u9a6c\u80cc\uff0c\u7ee7\u7eed\u8d76\u8def\u3002\u8d70\u51fa\u57ce\u5916\uff0c\u5ffd\u89c1\u4e66\u751f\u90a3\u5339 \u767d\u9a6c\uff0c\u4e5f\u5728\u524d\u9762\u3002\u4e91\u857e\u5fc3\u4e2d\u4e00\u52a8\uff0c\u9053\uff1a\u201c\u83ab\u975e\u662f\u8fd9\u4e66\u751f\u4e0d\u6210\uff0c\u53ef\u53c8\u4e0d\u50cf\u5440\uff01\u201d\u628a\u9a6c\u4e00\u50ac\uff0c\u8d76 \u4e0a\u524d\u53bb\uff0c\u5237\u7684\u4e00\u97ad\uff0c\u4f6f\u4f5c\u8d76\u9a6c\uff0c\u97ad\u68a2\u5374\u6253\u5230\u4e66\u751f\u80c1\u4e0b\u7a74\u9053\u8981\u5bb3\u4e4b\u5904\u3002 \u3000\u3000\u4e91\u857e\u8fd9\u4e00\u97ad\u5b9e\u662f\u8bd5\u90a3\u4e66\u751f\u6b66\u529f\u6df1\u6d45\uff0c\u5979\u97ad\u68a2\u6240\u6307\uff0c\u6070\u662f\u8981\u5bb3\u6240\u5728\uff0c\u82e5\u7136\u4e66\u751f\u4e43\u662f\u4f1a\u5bb6\uff0c \u5fc5\u5b9a\u4e00\u4e0b\u95ea\u5f00\uff1b\u82e5\u7136\u662f\u6b66\u529f\u66f4\u9ad8\u7684\uff0c\u90a3\u5c31\u53ef\u80fd\u51fa\u624b\u76f8\u683c\u3002\u5c82\u6599\u4e00\u97ad\u6253\u53bb\uff0c\u90a3\u4e66\u751f\u53eb\u4e86\u4e00\u58f0\uff0c \u7adf\u7136\u95ea\u907f\u4e0d\u5f00\uff0c\u97ad\u68a2\u6302\u4e0a\u8863\u88f3\uff0c\u597d\u5728\u4e91\u857e\u6697\u4e2d\u6536\u52b2\uff0c\u97ad\u52bf\u867d\u731b\uff0c\u6cbe\u8863\u4e4b\u65f6\u5374\u5df2\u65e0\u529b\u3002\u9976\u662f\u5982 \u6b64\uff0c\u90a3\u4e66\u751f\u4e5f\u6643\u4e86\u51e0\u6643\uff0c\u5728\u9a6c\u80cc\u4e0a\u8e0f\u8db3\u4e0d\u7a33\uff0c\u51e0\u4e4e\u8dcc\u4e0b\u3002\u4e91\u857e\u597d\u751f\u8fc7\u7adf\u4e0d\u53bb\uff0c\u9053\uff1a\u201c\u5931\u624b\u6253 \u4e86\u4f60\u4e86\uff0c\u6211\u8fd9\u91cc\u7ed9\u4f60\u8d54\u7f6a\uff01\u201d\u4e66\u751f\u62ac\u773c\u4e00\u671b\uff0c\u9a87\u53eb\u9053\uff1a\u201c\u5403\u767d\u98df\u7684\u53c8\u6765\u4e86\uff01\u4f60\u4e0d\u8981\u4ee5\u4e3a\u6211\u6709 \u51e0\u4e2a\u94b1\u5c31\u6765\u7f20\u6211\uff0c\u6211\u7684\u94b1\u662f\u4ea4\u597d\u670b\u53cb\u7684\uff0c\u50cf\u4f60\u8fd9\u6837\u559d\u4e86\u4eba\u5bb6\u7684\u53c8\u6253\u4eba\u5bb6\uff0c\u6211\u53ef\u4e0d\u6562\u9886\u6559 \u5440\uff01\u201d\u4e91\u857e\u53c8\u597d\u6c14\u53c8\u597d\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u7684\u9152\u8fd8\u672a\u9192\u5417\uff1f\u201d\u90a3\u4e66\u751f\u541f\u9053\uff1a\u201c\u62bd\u5200\u65ad\u6c34\u6c34\u66f4\u6d41\uff0c\u4e3e \u676f\u6d88\u6101\u6101\u66f4\u6101\u3002\u4eba\u751f\u5728\u4e16\u4e0d\u79f0\u610f\uff0c\u660e\u671d\u6563\u53d1\u5f04\u6241\u3002\u5440\uff0c\u5440\uff01\u6211\u4e0d\u548c\u4f60\u559d\u9152\uff0c\u4e0d\u548c\u4f60\u559d\u9152\uff01\u201d \u9189\u6001\u53ef\u63ac\u3002\u4e91\u857e\u7ed9\u4ed6\u5f04\u5f97\u4e0d\u77e5\u5e94\u4ed8\uff0c\u6b63\u60f3\u6276\u4ed6\uff0c\u5ffd\u89c1\u4ed6\u53cc\u817f\u4e00\u5939\uff0c\u90a3\u5339\u767d\u9a6c\u98de\u4e00\u822c\u5730\u5954\u8dd1\u3002 \u4e91\u857e\u7684\u9a6c\u662f\u5c71\u5be8\u4e2d\u6311\u9009\u51fa\u6765\u7684\u8499\u53e4\u6218\u9a6c\uff0c\u7adf\u7136\u8ffd\u4ed6\u4e0d\u4e0a\u3002\u4e91\u857e\u5fc3\u9053\uff1a\u201c\u6b64\u4eba\u4e0d\u901a\u6b66\u827a\uff0c\u8fd9\u5339 \u9a6c\u53ef\u662f\u975e\u51e1\u4f73\u54c1\u554a\uff01\u201d\u5931\u4e86\u94f6\u4e24\uff0c\u95f7\u95f7\u4e0d\u4e50\uff0c\u50ac\u9a6c\u7eed\u884c\u3002 \u3000\u3000\u8d70\u4e86\u534a\u65e5\uff0c\u62ac\u5934\u4e00\u671b\uff0c\u53ea\u89c1\u5915\u9633\u843d\u5c71\uff0c\u708a\u70df\u56db\u8d77\uff0c\u60f3\u6295\u519c\u5bb6\u4f4f\u5bbf\uff0c\u888b\u4e2d\u5374\u53c8\u65e0\u94b1\uff0c\u5ffd\u542c \u5f97\u9a6c\u5636\u4e4b\u58f0\uff0c\u53ea\u89c1\u524d\u9762\u662f\u4e00\u5ea7\u4e1b\u6797\uff0c\u6797\u4e2d\u6709\u4e00\u5bfa\u89c2\uff0c\u5bfa\u89c2\u5916\u6709\u4e00\u5339\u767d\u9a6c\u6b63\u5728\u4f4e\u5934\u5403\u8349\u3002\u4e91\u857e \u8a00\u9053\uff1a\u201c\u54a6\uff0c\u539f\u6765\u4ed6\u4e5f\u5728\u8fd9\u91cc\u3002\u5bfa\u89c2\u4e2d\u7684\u548c\u5c1a\u597d\u76f8\u4e0e\uff0c\u6211\u4e0d\u5982\u5728\u8fd9\u91cc\u4f4f\u5bbf\u4e00\u5bb5\u3002\u201d\u5728\u5bfa\u89c2\u5916 \u624e\u597d\u9a6c\u5339\uff0c\u63a8\u95e8\u5165\u53bb\uff0c\u53ea\u89c1\u90a3\u4e66\u751f\u5728\u5eca\u4e0b\u751f\u4e86\u5806\u706b\uff0c\u6b63\u5728\u90a3\u91cc\u7168\u828b\u5934\uff0c\u4e00\u89c1\u4e91\u857e\u5165\u6765\uff0c\u53c8\u541f \u9053\uff1a\u201c\u4eba\u751f\u65e0\u5904\u4e0d\u9022\u541b\u3002\u5440\uff0c\u5440\uff01\u53c8\u78b0\u7740\u4f60\u4e86\u3002\u201d\u4e91\u857e\u77a7\u4ed6\u4e00\u773c\uff0c\u9053\uff1a\u201c\u4f60\u7684\u9152\u9192\u4e86\uff1f\u201d\u90a3 \u4e66\u751f\u9053\uff1a\u201c\u6211\u51e0\u65f6\u559d\u9189\uff1f\u6211\u8ba4\u5f97\u51fa\u4f60\u662f\u98df\u767d\u98df\u7684\u4eba\u3002\u201d\u4e91\u857e\u751f\u6c14\u9053\uff1a\u201c\u4f60\u77e5\u9053\u4ec0\u4e48\uff1f\u6709\u5f3a\u4eba \u5728\u52ab\u4f60\u7684\u73e0\u5b9d\uff01\u201d\u90a3\u4e66\u751f\u8df3\u8d77\u6765\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u5f3a\u4eba\uff1f\u8fd9\u4e2a\u5bfa\u89c2\u91cc\u548c\u5c1a\u4e5f\u6ca1\u6709\u4e00\u4e2a\uff0c\u5f3a\u4eba\u6765 \u4e86\uff0c\u8fde\u58ee\u80c6\u7684\u90fd\u6ca1\u6709\u3002\u597d\uff0c\u6211\u4e0d\u4f4f\u5728\u8fd9\u91cc\u4e86\u3002\u201d\u4e91\u857e\u53c8\u597d\u6c14\u53c8\u597d\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u53bb\u54ea\u91cc\uff1f\u4f60 \u4e00\u5230\u5916\u9762\u5f3a\u76d7\u52ab\u4f60\uff0c\u66f4\u662f\u65e0\u4eba\u6253\u6551\u3002\u6709\u6211\u5728\u8fd9\u91cc\uff0c\u767e\u5341\u4e2a\u5f3a\u76d7\u4e5f\u8fd8\u4e0d\u5728\u5fc3\u4e0a\u3002\u201d\u4e66\u751f\u5f20\u5927\u773c \u775b\uff0c\u5ffd\u7136\u201c\u5657\u55e4\u201d\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u6709\u8fd9\u6837\u5927\u7684\u672c\u4e8b\uff0c\u4e3a\u4f55\u8fd8\u8981\u767d\u5403\u4eba\u5bb6\u7684\uff1f\u201d\u4e91\u857e\u9053\uff1a\u201c\u6211 \u7684\u94f6\u5b50\u7ed9\u5c0f\u5077\u5077\u53bb\u4e86\u3002\u201d\u90a3\u4e66\u751f\u7b11\u5f97\u4e0a\u6c14\u4e0d\u63a5\u4e0b\u6c14\uff0c\u6307\u7740\u4e91\u857e\u9053\uff1a\u201c\u767e\u5341\u4e2a\u5f3a\u76d7\u4e0d\u653e\u5728\u5fc3 \u4e0a\uff0c\u94f6\u5b50\u5374\u7ed9\u5c0f\u5077\u5077\u53bb\u3002\u54c8\u54c8\uff0c\u4f60\u8bf4\u8c0e\u7684\u672c\u4e8b\u53ef\u6ca1\u6709\u4f60\u9a97\u98df\u7684\u672c\u4e8b\u597d\uff01\u201d\u672c\u4f3c\u6b32\u8d70\uff0c\u53cd\u53c8\u5750 \u4e86\u4e0b\u6765\uff0c\u9053\uff1a\u201c\u518d\u4e0d\u542c\u4f60\u7684\u8c0e\u8bdd\uff0c\u6e05\u5e73\u4e16\u754c\uff0c\u54ea\u6709\u8fd9\u4e48\u591a\u5f3a\u76d7\u5c0f\u5077\uff1f\u201d\u61d2\u6d0b\u6d0b\u7684\u53c8\u7168\u828b\u5934\u3002 \u3000\u3000\u4e91\u857e\u8d4c\u6c14\u9053\uff1a\u201c\u4f60\u4e0d\u4fe1\u5c31\u4e0d\u4fe1\uff0c\u4e0d\u8981\u4f60\u4fe1\uff01\u201d\u7168\u7126\u7684\u828b\u5934\u9999\u6c14\u4e00\u9635\u9635\u76f4\u6251\u9f3b\u89c2\uff0c\u4e91\u857e\u8dd1 \u9a6c\u534a\u65e5\u809a\u5b50\u9965\u997f\uff0c\u541e\u4e86\u541e\u53e3\u6c34\uff0c\u5374\u4e0d\u597d\u610f\u601d\u95ee\u90a3\u4e66\u751f\u8981\u3002\u8fd9\u5bfa\u89c2\u662f\u4e2a\u8352\u5239\uff0c\u679c\u662f\u6ca1\u6709\u548c\u5c1a\uff0c \u54ea\u80fd\u627e\u5230\u5145\u9965\u4e4b\u7269\u3002 \u3000\u3000\u90a3\u4e66\u751f\u54ac\u4e86\u4e00\u53e3\u828b\u5934\uff0c\u6447\u5934\u6446\u8111\uff0c\u81ea\u8a00\u81ea\u8bed\u5730\u8bf4\u9053\uff1a\u201c\u9ec4\u9152\u53ef\u9189\uff0c\u6c7e\u9152\u4ea6\u9189\uff1b\u9c7c\u8089\u56fa \u4f73\uff0c\u828b\u5934\u4ea6\u5999\u3002\u597d\u9999\u5440\uff0c\u597d\u9999\uff01\u201d\u4e91\u857e\u6012\u770b\u4ed6\u4e00\u773c\uff0c\u522b\u8fc7\u5934\u53bb\u3002\u90a3\u4e66\u751f\u53eb\u9053\uff1a\u201c\u5582\uff0c\u5403\u767d\u98df \u7684\uff0c\u7ed9\u4f60\u4e00\u4e2a\u828b\u5934\u3002\u201d\u6251\u7684\uff0c\u5c06\u4e00\u4e2a\u70e4\u719f\u7684\u5c71\u828b\u629b\u4e86\u8fc7\u6765\uff0c\u4e91\u857e\u6012\u9053\uff1a\u201c\u8c01\u5403\u4f60\u7684\uff01\u201d\u541e\u4e86 \u541e\u53e3\u6c34\uff0c\u76d8\u819d\u5750\u5728\u5730\u4e0a\uff0c\u773c\u89c2\u9f3b\uff0c\u9f3b\u89c2\u5fc3\uff0c\u9759\u9759\u5730\u505a\u8d77\u5410\u7eb3\u529f\u592b\uff0c\u597d\u4e0d\u5bb9\u6613\u628a\u9965\u706b\u538b\u4e0b\u3002\u4e91 \u857e\u7684\u5185\u529f\u4e43\u662f\u7384\u95e8\u6b63\u5b97\uff0c\u505a\u4e86\u529f\u8bfe\uff0c\u53ea\u89c9\u901a\u4f53\u8212\u6cf0\u3002\u7741\u5f00\u773c\u775b\uff0c\u53ea\u89c1\u90a3\u4e66\u751f\u547c\u547c\u719f\u7761\uff0c\u70e4\u719f \u7684\u828b\u5934\uff0c\u6eda\u4e86\u6ee1\u5730\u3002\u4e91\u857e\u4f38\u4f38\u820c\u5934\uff0c\u60f3\u4f38\u51fa\u624b\u53bb\uff0c\u5ffd\u89c1\u90a3\u4e66\u751f\u8f6c\u4e86\u4e2a\u8eab\uff0c\u5374\u53c8\u7761\u53bb\u3002\u4e91\u857e\u8d4c \u6c14\u60f3\u9053\uff1a\u201c\u6211\u5c31\u997f\u5b83\u4e00\u665a\uff0c\u4e5f\u7b97\u4e0d\u4e86\u4ec0\u4e48\uff01\u201d\u90a3\u4e66\u751f\u9f3e\u58f0\u5982\u96f7\uff0c\u4e91\u857e\u60f3\u7761\u4e5f\u7761\u4e0d\u7740\uff0c\u5ffd\u7136\u60f3 \u9053\uff1a\u201c\u8fd9\u4e66\u751f\u8863\u670d\u534e\u8d35\uff0c\u8eab\u6000\u91cd\u5b9d\uff0c\u4f55\u4ee5\u51fa\u95e8\u4e0d\u5e26\u4fdd\u9556\uff1f\u53c8\u6562\u5728\u8352\u5c71\u53e4\u5bfa\u4f4f\u5bbf\uff0c\u5403\u8fd9\u4e0d\u503c\u94b1 \u7684\u70e4\u828b\u5934\uff1f\u96be\u9053\u4ed6\u662f\u88c5\u4f5c\u4e0d\u61c2\u6b66\u827a\u7684\u4e48\uff1f\u53ef\u662f\u53c8\u4e0d\u50cf\u662f\u88c5\u7684\u5440\uff01\u201d\u6084\u6084\u7ad9\u8d77\uff0c\u60f3\u641c\u4ed6\u8eab\u4e86\uff0c \u90a3\u4e66\u751f\u53c8\u8f6c\u4e86\u4e2a\u8eab\uff0c\u4e91\u857e\u60f3\u9053\uff1a\u201c\u4ed6\u82e5\u60ca\u9192\u5c82\u4e0d\u4ee5\u4e3a\u6211\u5077\u4ed6\u4e1c\u897f\uff1f\u201d\u597d\u751f\u8e0c\u8e87\uff0c\u4e0a\u524d\u4e09\u6b65\uff0c \u9000\u540e\u4e24\u6b65\u3002\u5ffd\u542c\u5f97\u5916\u9762\u6709\u602a\u5578\u4e4b\u58f0\uff0c\u4e91\u857e\u770b\u4e86\u4e66\u751f\u4e00\u773c\uff0c\u89c1\u4ed6\u719f\u7761\u5982\u730e\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u672c\u6765\u4e0d \u8be5\u7406\u4f60\uff0c\u77a7\u4f60\u53c8\u89c9\u53ef\u601c\uff0c\u597d\uff0c\u7b97\u4f60\u597d\u9020\u5316\uff0c\u59d1\u5a18\u66ff\u4f60\u53bb\u6321\u5f3a\u4eba\u3002\u201d\u8d70\u51fa\u5bfa\u95e8\uff0c\u4e00\u7eb5\u8eab\u85cf\u5728\u6811 \u4e0a\u3002 \u3000\u3000\u6de1\u6708\u5bd2\u661f\u4e4b\u4e0b\uff0c\u53ea\u89c1\u4e24\u4e2a\u8499\u9762\u5f3a\u4eba\u76f4\u8d70\u8fc7\u6765\uff0c\u4e00\u4e2a\u8bf4\u9053\uff1a\u201c\u4f60\u770b\u8fd9\u5339\u767d\u9a6c\uff0c\u60f3\u5fc5\u662f\u5728\u6b64 \u4e86\u3002\u201d\u4e00\u4e2a\u9053\uff1a\u201c\u4ed6\u82e5\u4e0d\u80af\u4f9d\u4ece\u53c8\u600e\u4e48\u529e\uff1f\u201d\u4e00\u4e2a\u9053\uff1a\u201c\u8bf4\u4e0d\u5b9a\u53ea\u597d\u53d6\u4ed6\u9996\u7ea7\u4e86\u3002\u201d\u5148\u5934\u90a3 \u4e00\u4e2a\u9053\uff1a\u201c\u8fd9\u600e\u4e48\u4f7f\u5f97\uff1f\u7ed9\u4ed6\u6302\u70b9\u5f69\u90a3\u8fd8\u53ef\u4ee5\u3002\u201d\u4e91\u857e\u542c\u5f97\u6012\u4ece\u5fc3\u8d77\uff0c\u5fc3\u9053\uff1a\u201c\u597d\u72e0\u7684\u5f3a \u76d7\uff0c\u52ab\u8d22\u8fd8\u60f3\u5bb3\u547d\uff01\u201d\u5ffd\u542c\u5f97\u5176\u4e2d\u4e00\u4eba\u53eb\uff1a\u201c\u6811\u4e0a\u6709\u4eba\uff01\u201d\u4e91\u857e\u4e24\u679a\u8774\u8776\u9556\u5df2\u4ece\u6811\u4e0a\u5c04\u4e0b\uff0c \u4e24\u4e2a\u8499\u9762\u4eba\u8eab\u624b\u77eb\u5065\u4e4b\u6781\uff0c\u4e00\u95ea\u95ea\u5f00\u3002\u4e91\u857e\u633d\u4e86\u4e00\u4e2a\u5251\u82b1\uff0c\u4e00\u62db\u201c\u9e4f\u640f\u4e5d\u9704\u201d\uff0c\u51cc\u7a7a\u51fb\u4e0b\uff0c \u5206\u523a\u4e24\u4eba\uff0c\u4e24\u4e2a\u8499\u9762\u4eba\u4e00\u4e2a\u624b\u4f7f\u94c1\u62d0\uff0c\u4e00\u4e2a\u624b\u4f7f\u53cc\u94a9\uff0c\u7167\u7740\u957f\u5251\u4fbf\u7838\uff0c\u5251\u950b\u8fc7\u5904\uff0c\u706b\u82b1\u98de \u6e85\uff0c\u94c1\u62d0\u7ed9\u622a\u4e86\u4e00\u4e2a\u5207\u53e3\uff0c\u53cc\u94a9\u5374\u628a\u5b9d\u5251\u5e26\u8fc7\u4e00\u8fb9\u3002\u4e91\u857e\u5fc3\u9053\uff1a\u201c\u8fd9\u4e24\u4e2a\u5f3a\u76d7\u624b\u5e95\u5012\u786c\uff01\u201d \u90a3\u4e24\u4e2a\u8499\u9762\u4eba\u66f4\u662f\u5403\u60ca\uff0c\u6b32\u5f85\u559d\u95ee\uff0c\u4e91\u857e\u7684\u5b9d\u5251\u5df2\u5982\u75be\u98ce\u66b4\u96e8\u4e00\u822c\u6740\u6765\u3002\u4e91\u857e\u8fd9\u67c4\u5b9d\u5251\u4e43\u662f \u7384\u673a\u9038\u58eb\u6240\u70bc\u7684\u96cc\u96c4\u53cc\u5251\u4e4b\u4e00\uff0c\u540d\u4e3a\u201c\u9752\u51a5\u201d\uff0c\u5bfb\u5e38\u5175\u5203\uff0c\u4e00\u622a\u5373\u65ad\uff0c\u4f7f\u94c1\u62d0\u7684\u5175\u5668\u867d\u7136\u6c89 \u91cd\uff0c\u5374\u4e5f\u4e0d\u6562\u548c\u5b83\u76f8\u78b0\uff0c\u5012\u662f\u90a3\u4f7f\u53cc\u94a9\u7684\u8eab\u624b\u975e\u51e1\uff0c\u906e\u62e6\u52fe\u6321\u4ea6\u5b88\u4ea6\u653b\uff0c\u4e91\u857e\u7684\u5b9d\u5251\u7adf\u7136\u78b0 \u4e0d\u7740\u4ed6\u7684\u5175\u5668\u3002 \u3000\u3000\u4e91\u857e\u4f7f\u51fa\u98de\u82b1\u6251\u8776\u7684\u8eab\u6cd5\uff0c\u5728\u53cc\u94a9\u4e00\u62d0\u7684\u4ea4\u51fb\u7f1d\u4e2d\uff0c\u76d8\u65cb\u75be\u8fdb\uff0c\u5251\u5149\u6709\u5982\u4e00\u56e2\u7535\u5149\uff0c\u6eda \u6765\u6eda\u53bb\uff0c\u4f7f\u5230\u75be\u5904\uff0c\u771f\u4f3c\u6c34\u94f6\u6cfb\u5730\uff0c\u82b1\u96e8\u7f24\u7eb7\uff0c\u90a3\u4e24\u4e0a\u4eba\u88ab\u5979\u6740\u5f97\u6b65\u6b65\u540e\u9000\u3002\u53ef\u662f\u94c1\u62d0\u529b \u6c89\uff0c\u53cc\u94a9\u7075\u6d3b\uff0c\u9996\u5c3e\u76f8\u5e94\uff0c\u4e91\u857e\u5374\u4e5f\u65e0\u6cd5\u5948\u4f55\u3002\u6fc0\u6597\u9163\u65f6\uff0c\u4e91\u857e\u7a81\u7136\u54ac\u7d27\u7259\u6839\uff0c\u4e00\u5251\u659c\u524a\uff0c \u5411\u90a3\u4f7f\u53cc\u94a9\u7684\u8499\u9762\u5f3a\u76d7\u75db\u4e0b\u6740\u624b\u3002\u8fd9\u4e00\u5251\u53c8\u72e0\u53c8\u75be\uff0c\u65e0\u8bba\u524d\u6251\u540e\u95ea\uff0c\u90fd\u96be\u8eb2\u5f00\uff0c\u6b63\u662f\u98de\u5929\u9f99 \u5973\u6240\u4f20\u7684\u593a\u547d\u795e\u62db\u3002\u4e91\u857e\u672c\u6765\u8fd8\u4e0d\u60f3\u53d6\u90a3\u4e24\u4e2a\u8499\u9762\u5f3a\u4eba\u7684\u6027\u547d\uff0c\u53ef\u662f\u82e5\u975e\u523a\u6740\u4e00\u4eba\uff0c\u5374\u662f\u65e0 \u6cd5\u5f97\u80dc\uff0c\u6240\u4ee5\u903c\u5f97\u51fa\u6b64\u7edd\u62db\u3002 \u3000\u3000\u5c82\u6599\u4e00\u5251\u524a\u53bb\uff0c\u90a3\u4f7f\u53cc\u94a9\u7684\u5f3a\u76d7\u5de6\u94a9\u5f80\u4e0b\u4e00\u6c89\uff0c\u53f3\u94a9\u5f80\u4e0a\u4e00\u5e26\uff0c\u4e91\u857e\u7684\u201c\u9752\u51a5\u201d\u5251\u51e0\u4e4e \u7ed9\u4ed6\u5f15\u5f97\u8131\u624b\u98de\u53bb\u3002\u4e91\u857e\u5927\u5403\u4e00\u60ca\uff0c\u8fd9\u4e00\u62db\u7adf\u662f\u6fb9\u53f0\u706d\u660e\u7684\u5bb6\u6570\uff0c\u6025\u5fd9\u4e00\u4e2a\u8f6c\u8eab\uff0c\u5251\u950b\u4e00\u8f6c \u8feb\u5f00\u4f7f\u94c1\u62d0\u7684\u5f3a\u76d7\uff0c\u8eab\u5f62\u5012\u7eb5\uff0c\u53c8\u95ea\u5f00\u53cc\u94a9\u7684\u5077\u88ad\uff0c\u626c\u5251\u559d\u9053\uff1a\u201c\u5140\u4f60\u8fd9\u25a1\u53ef\u662f\u6fb9\u53f0\u706d\u660e\u7684 \u5f1f\u5b50\u4e48\uff1f\u201d\u90a3\u4f7f\u53c8\u94a9\u7684\u731b\u8df3\u8d77\u6765\uff0c\u6c89\u58f0\u559d\u9053\uff1a\u201c\u4f60\u65e2\u8bc6\u7834\u6211\u7684\u6765\u5386\uff0c\u660e\u5e74\u4eca\u65e5\u4fbf\u662f\u4f60\u7684\u5468\u5e74 \u5fcc\u65e5\u4e86\uff01\u201d\u53cc\u94a9\u970d\u970d\uff0c\u52c7\u731b\u65e0\u6bd4\uff0c\u7adf\u7136\u5168\u662f\u62fc\u547d\u7684\u62db\u6570\u3002\u4e91\u857e\u4e5f\u7ea2\u4e86\u773c\u775b\uff0c\u9a82\u9053\uff1a\u201c\u5927\u80c6\u80e1 \u513f\uff0c\u5c45\u7136\u6562\u5077\u5165\u8fb9\u5173\uff0c\u4f60\u5f53\u4e2d\u56fd\u65e0\u4eba\u4e48\uff1f\u201d\u4e00\u53e3\u5251\u6307\u4e1c\u6253\u897f\u6307\u5357\u6253\u5317\uff0c\u4e5f\u662f\u7edd\u4e0d\u7559\u60c5\uff0c\u62db\u62db \u72e0\u75be\u3002\u82e5\u8bba\u672c\u8eab\u6b66\u827a\uff0c\u4e91\u857e\u8981\u7ecf\u6fb9\u53f0\u706d\u660e\u7684\u5f92\u5f1f\u7a0d\u80dc\u4e00\u7b79\uff0c\u4f46\u4e00\u6765\u654c\u65b9\u6709\u4f7f\u94c1\u62d0\u7684\u76f8\u5e2e\uff0c\u4e8c \u6765\u4e91\u857e\u997f\u4e86\u534a\u5929\u534a\u591c\uff0c\u6c14\u529b\u4e0d\u52a0\uff0c\u6597\u4e86\u4e00\u767e\u4f59\u62db\uff0c\u9999\u6c57\u6dcb\u6f13\uff0c\u6e10\u6e10\u53ea\u6709\u62db\u67b6\u4e4b\u529b\u3002\u53cc\u94a9\u4e00 \u62d0\uff0c\u8d8a\u653b\u8d8a\u7d27\uff0c\u4e91\u857e\u88ab\u56f0\u5728\u6838\u5fc3\uff0c\u5371\u6025\u975e\u5e38\u3002\u4f7f\u94c1\u62d0\u7684\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u7684\u5251\u5012\u5f88\u4e0d\u9519\uff0c\u7b49\u4e00\u4f1a \u4f60\u8ba9\u6211\u8981\u8fd9\u53e3\u5251\u6210\u4e0d\u6210\uff1f\u201d\u4f7f\u53cc\u94a9\u7684\u5e94\u9053\uff1a\u201c\u597d\uff0c\u8ba9\u4f60\uff0c\u8ba9\u4f60\u3002\u4f46\u7b49\u4f1a\u6349\u4eba\u4e4b\u65f6\uff0c\u4f60\u53ef\u8981\u542c \u6211\u7684\u8bdd\u3002\u201d\u4e24\u4eba\u4e00\u95ee\u4e00\u7b54\uff0c\u4f3c\u4e4e\u4e91\u857e\u4e4b\u6b7b\uff0c\u5df2\u662f\u6beb\u65e0\u7591\u95ee\u3002\u4e91\u857e\u5927\u6012\uff0c\u4e00\u62db\u201c\u98de\u7011\u6d41\u6cc9\u201d\u5411 \u90a3\u4f7f\u94c1\u62d0\u7684\u8fce\u9762\u4fbf\u523a\uff0c\u90a3\u8499\u9762\u8d3c\u5355\u62d0\u5f80\u4e0a\u4e00\u8fce\u62d0\u65b9\u64a9\u8d77\uff0c\u5ffd\u7136\u54ce\u54df\u4e00\u58f0\uff0c\u624b\u5782\u4e0b\u6765\u3002\u4e91\u857e\u8fd9 \u4e00\u5251\u4f55\u7b49\u5feb\u75be\uff0c\u4e00\u5251\u7a7f\u5589\uff0c\u5c06\u4ed6\u523a\u6bd9\uff0c\u4f7f\u53cc\u94a9\u7684\u5413\u5f97\u5446\u4e86\uff0c\u4e91\u857e\u53cd\u624b\u4e00\u5251\uff0c\u5580\u5693\u4e00\u58f0\uff0c\u5c06\u4ed6 \u5de6\u624b\u7684\u62a4\u624b\u94a9\u622a\u6210\u4e24\u6bb5\u3002\u4f7f\u53cc\u94a9\u7684\u98de\u8eab\u75be\u8dd1\uff0c\u4e91\u857e\u4e00\u626c\u624b\uff0c\u4e09\u679a\u201c\u6885\u82b1\u8774\u8776\u9556\u201d\u5954\u4ed6\u540e\u5fc3\uff0c \u770b\u6765\u5b9a\u53ef\u6253\u4e2d\uff0c\u5ffd\u542c\u5f97\u53ee\u53ee\u8fde\u54cd\uff0c\u8774\u8776\u9556\u7adf\u7136\u4e0d\u77e5\u88ab\u4ec0\u4e48\u4e1c\u897f\u78b0\u7740\u6253\u4e86\u4e0b\u6765\uff0c\u8f6c\u77ac\u4e4b\u95f4\uff0c\u654c \u4eba\u5df2\u8dd1\u5f97\u65e0\u5f71\u65e0\u8e2a\u3002 \u3000\u3000\u4e91\u857e\u4e00\u7247\u832b\u7136\uff0c\u5341\u5206\u4e0d\u89e3\uff01\u81ea\u5df1\u521a\u624d\u90a3\u4e00\u5251\u867d\u51f6\u72e0\uff0c\u4f46\u6599\u60f3\u90a3\u4f7f\u94c1\u62d0\u7684\u654c\u4eba\u8fd8\u80fd\u62b5\u6321\uff0c \u5374\u4e0d\u6599\u5728\u6700\u7d27\u6025\u4e4b\u65f6\uff0c\u5bf9\u65b9\u7684\u94c1\u62d0\u7adf\u7136\u4f1a\u5782\u4e0b\u6765\uff0c\u7adf\u4f3c\u795e\u5dee\u9b3c\u4f7f\u4e00\u822c\uff0c\u4e27\u547d\u5728\u81ea\u5df1\u4e09\u5c3a\u9752\u950b \u4e4b\u4e0b\u3002\u4e91\u857e\u8d8a\u60f3\u8d8a\u5947\uff0c\u5fc3\u9053\uff1a\u201c\u83ab\u975e\u662f\u6709\u4eba\u6697\u52a9\u4e0d\u6210\uff1f\u4f46\u81ea\u5df1\u90a3\u4e09\u679a\u8774\u8776\u9556\u4f55\u4ee5\u4e5f\u7a81\u7136\u843d \u5730\uff0c\u96be\u9053\u662f\u6697\u4e2d\u51fa\u624b\u7684\u9ad8\u4eba\uff0c\u65e2\u52a9\u81ea\u5df1\uff0c\u53c8\u52a9\u654c\u4eba\uff1f\u60f3\u8d77\u6765\u53c8\u5b9e\u662f\u65e0\u6b64\u9053\u7406\u3002\u201d \u3000\u3000\u4e91\u857e\u4fef\u9996\u770b\u90a3\u6b7b\u5728\u5730\u4e0a\u7684\u5f3a\u76d7\uff0c\u4e00\u5251\u5c06\u4ed6\u7684\u9762\u5177\u64a9\u5f00\uff0c\u679c\u7136\u662f\u4e00\u4e2a\u80e1\u4eba\u3002\u4e91\u857e\u60ca\u7591\u4e0d \u5b9a\uff0c\u8fd9\u663e\u7136\u4e0d\u662f\u666e\u901a\u60f3\u52ab\u8d22\u7269\u7684\u5f3a\u4eba\u4e86\u3002\u4e91\u857e\u5927\u7740\u80c6\u5b50\uff0c\u641c\u4ed6\u7684\u8eab\uff0c\u9664\u4e86\u51e0\u4e24\u788e\u94f6\u548c\u4e00\u5305\u5e72 \u7cae\u4e4b\u5916\uff0c\u522b\u65e0\u6240\u6709\u3002\u4e91\u857e\u7b11\u9053\uff1a\u201c\u8fd9\u6b63\u5408\u6211\u7528\u3002\u201d\u56bc\u4e0b\u5e72\u7cae\uff0c\u5c06\u94f6\u5b50\u7eb3\u5165\u6000\u4e2d\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u6797\u4e2d\u5f02\u58f0\u53c8\u8d77\uff0c\u53ea\u89c1\u53c8\u662f\u4e24\u4e2a\u8499\u9762\u5f3a\u4eba\u98de\u5954\u800c\u6765\uff0c\u626c\u58f0\u559d\u9053\uff1a\u201c\u5408\u5b50\u4e0a\u7684\u670b\u53cb\uff0c \u4e00\u7897\u6c34\u7aef\u6765\u5927\u5bb6\u559d\u3002\u201d\u610f\u601d\u662f\u8bf4\u5f7c\u6b64\u90fd\u662f\u540c\u9053\uff0c\u4f60\u52ab\u5230\u7684\u8d22\u7269\u53ef\u4e0d\u80fd\u72ec\u541e\uff0c\u62ff\u51fa\u6765\u5927\u5bb6\u5206 \u5427\u3002\u4e91\u857e\u5927\u6012\uff0c\u559d\u9053\uff1a\u201c\u597d\u5440\uff0c\u4f60\u4eec\u8fd8\u6709\u591a\u5c11\u4eba\u6765\uff0c\u90fd\u5403\uff01\u201d\u672c\u60f3\u8bf4\uff1a\u201c\u90fd\u5403\u59d1\u5a18\u4e00\u5251\u201d\uff0c \u731b\u9192\u8d77\u81ea\u5df1\u5df2\u662f\u6613\u9497\u800c\u5f01\uff0c\u201c\u59d1\u5a18\u201d\u4e8c\u5b57\uff0c\u8bf4\u5230\u53e3\u8fb9\u53c8\u541e\u4e86\u56de\u53bb\u3002\u90a3\u4e24\u4e2a\u5f3a\u76d7\u5927\u7b11\u9053\uff1a\u201c\u54c8 \u54c8\uff0c\u8fd9\u624d\u662f\u597d\u670b\u53cb\uff0c\u5927\u5bb6\u90fd\u6709\u5f97\u5403\u3002\u201d\u8d70\u8fc7\u6765\u4f38\u624b\u5c31\u8981\u3002 \u3000\u3000\u4e91\u857e\u51b7\u7b11\u4e00\u58f0\uff0c\u53cd\u624b\u5c31\u662f\u4e00\u5251\u3002\u90a3\u4e24\u4e2a\u5f3a\u76d7\uff0c\u4e00\u4e2a\u624b\u4f7f\u5355\u5200\uff0c\u4e00\u4e2a\u5374\u7a7a\u7740\u53cc\u624b\uff0c\u4e91\u857e\u4e00 \u5251\u523a\u53bb\uff0c\u53ea\u89c9\u5fae\u98ce\u98d2\u7136\uff0c\u7a7a\u624b\u7684\u8d3c\u4eba\u8eab\u5b50\u4e00\u7ffb\uff0c\u7adf\u7136\u76f4\u62a2\u8fc7\u6765\uff0c\u5de6\u638c\u4e00\u62c2\uff0c\u4f3c\u5207\u4f3c\u622a\uff0c\u4f7f\u7684 \u5c45\u7136\u662f\u5927\u64d2\u62ff\u624b\u7684\u62db\u6570\u3002\u4e91\u857e\u5403\u4e86\u4e00\u60ca\uff0c\u4e0d\u6562\u5927\u610f\uff0c\u5251\u5c16\u4e00\u70b9\uff0c\u659c\u950b\u75be\u626b\uff0c\u4f7f\u5355\u5200\u7684\u53eb\u9053\uff1a \u201c\u70b9\u5b50\u597d\u786c\uff01\u201d\u4e00\u5200\u5288\u6765\uff0c\u52bf\u5b50\u4e5f\u9887\u51f6\u731b\uff0c\u4e91\u857e\u4f7f\u51fa\u7a7f\u82b1\u7ed5\u6811\u7684\u6b65\u6cd5\uff0c\u4e00\u5251\u6420\u7a7a\uff0c\u8eab\u5f62\u75be \u95ea\uff0c\u65e2\u907f\u5f00\u4e86\u5de6\u8fb9\u654c\u4eba\u7684\u64d2\u62ff\u624b\uff0c\u53c8\u907f\u5f00\u4e86\u53f3\u8fb9\u654c\u4eba\u7684\u5355\u5200\u3002 \u3000\u3000\u8fd9\u4e24\u4e2a\u5f3a\u4eba\u867d\u975e\u5eb8\u624b\uff0c\u4f46\u4e91\u857e\u5251\u6cd5\u7cbe\u5999\u4e4b\u6781\uff0c\u8eab\u5f62\u65e2\u5feb\uff0c\u5251\u5149\u53c8\u662f\u98d8\u77a5\u4e0d\u5b9a\uff0c\u4e24\u4e2a\u5f3a\u4eba \u90fd\u4f3c\u89c9\u5f97\u5bf9\u65b9\u4e13\u95e8\u653b\u51fb\u81ea\u5df1\u3002\u6597\u4e86\u4e09\u4e94\u5341\u62db\uff0c\u5f92\u624b\u7684\u8d3c\u4eba\u53eb\u9053\uff1a\u201c\u597d\uff0c\u8ba9\u4f60\u72ec\u541e\u597d\u5566\uff0c\u7559\u4e0b \u4e07\u513f\uff08\u540d\u53f7\uff09\u6765\uff0c\u54b1\u4eec\u4ea4\u4e2a\u670b\u53cb\uff01\u201d\u4e91\u857e\u6012\u9053\uff1a\u201c\u52ab\u593a\u8d22\u7269\u4e4b\u7f6a\u53ef\u6055\uff0c\u901a\u756a\u5356\u56fd\u4e4b\u7f6a\u96be\u9976\u3002 \u8c01\u548c\u4f60\u4ea4\u670b\u53cb\uff01\u201d\u500f\u5730\u4e00\u62db\u201c\u5206\u82b1\u62c2\u67f3\u201d\uff0c\u5251\u52bf\u5411\u5de6\uff0c\u53c8\u4f3c\u5411\u53f3\uff0c\u4e00\u62db\u5206\u523a\u4e8c\u4eba\uff0c\u4f7f\u5355\u5200\u7684 \u201c\u54ce\u54df\u201d\u4e00\u58f0\uff0c\u624b\u8155\u5148\u4e2d\u4e86\u4e00\u5251\uff0c\u5355\u5200\u8131\u624b\u98de\u51fa\u3002\u7a7a\u624b\u7684\u8d3c\u4eba\u8f83\u4e3a\u6e9c\u6ed1\uff0c\u8eab\u5b50\u4e00\u7f29\uff0c\u907f\u4e86\u5f00 \u53bb\u3002\u4e91\u857e\u4f7f\u7684\u662f\u8fde\u73af\u62db\u6570\uff0c\u4e00\u5251\u523a\u51fa\u8ddf\u7740\u7eed\u4e0a\uff0c\u52bf\u5982\u62bd\u4e1d\uff0c\u7ef5\u7ef5\u4e0d\u65ad\u3002\u4e91\u857e\u53ea\u4ee5\u4e3a\u8fd9\u4e24\u4eba\u548c \u5148\u524d\u90a3\u4e24\u4e2a\u756a\u8d3c\u540c\u662f\u4e00\u4f19\uff0c\u6240\u4ee5\u4e0b\u624b\u7edd\u4e0d\u7559\u60c5\uff0c\u8fd9\u4e00\u5251\u75be\u5982\u9a87\u7535\uff0c\u5251\u5c16\u5df2\u89e6\u53ca\u654c\u4eba\u540e\u5fc3\uff0c\u5ffd \u7136\u201c\u55e4\u201d\u7684\u4e00\u54cd\uff0c\u624b\u8155\u4e0a\u4f3c\u7ed9\u5927\u8682\u8681\u54ac\u4e86\u4e00\u53e3\uff0c\u7a81\u7136\u5931\u4e86\u51c6\u5934\uff0c\u5251\u5c16\u6ed1\u8fc7\u4e00\u8fb9\uff0c\u4e24\u4e2a\u8499\u9762\u8d3c \u4eba\u62fc\u547d\u5954\u9003\uff0c\u8dd1\u5165\u4e86\u4e1b\u6797\u8349\u83bd\u4e4b\u95f4\u3002 \u3000\u3000\u4e91\u857e\u6012\u9053\uff1a\u201c\u65bd\u6697\u7b97\u7684\u5c0f\u8d3c\u6eda\u51fa\u6765\uff01\u201d\u56db\u5468\u9759\u6084\u6084\u7684\u7a7a\u65e0\u4e00\u4eba\uff0c\u4e91\u857e\u7b49\u4e86\u4e00\u9635\uff0c\u4e0d\u89c1\u6709 \u4eba\u63a5\u58f0\uff0c\u770b\u81ea\u5df1\u7684\u624b\u8155\uff0c\u7ea2\u80bf\u8d77\u9ec4\u8c46\u822c\u5927\u7684\u4e00\u7c92\u5c0f\u5757\uff0c\u60f3\u6765\u662f\u4e2d\u4e86\u6781\u5fae\u7ec6\u7684\u6697\u5668\uff0c\u60f3\u5728\u5730\u4e0a \u5bfb\u627e\uff0c\u4e5f\u627e\u4e0d\u51fa\u6765\u3002\u4e91\u857e\u8fd9\u4e24\u4ed7\u867d\u662f\u5927\u83b7\u5168\u80dc\uff0c\u53ef\u662f\u6697\u4e2d\u53d7\u4eba\u620f\u5f04\uff0c\u5fc3\u4e2d\u5b9e\u662f\u4e0d\u7518\uff0c\u6ca1\u7cbe\u6253 \u91c7\u5730\u56de\u5230\u5bfa\u5185\uff0c\u4f46\u89c1\u90a3\u4e2a\u4e66\u751f\u4ecd\u662f\u719f\u7761\u5982\u6ce5\uff0c\u9f3e\u58f0\u4e0d\u65ad\u3002 \u3000\u3000\u4e91\u857e\u53eb\u9053\uff1a\u201c\u5582\uff0c\u4f60\u8fd9\u6b7b\u4eba\uff0c\u4f60\u5012\u7761\u5f97\u5feb\u6d3b\uff01\u201d\u90a3\u4e66\u751f\u7ffb\u4e86\u4e2a\u8eab\uff0c\u54bf\u54bf\u5514\u5514\u7684\u547b\u4e86\u4e24 \u58f0\uff0c\u4e91\u857e\u53eb\u9053\uff1a\u201c\u5f3a\u76d7\u6765\u4e86\uff01\u201d\u90a3\u4e66\u751f\u7761\u773c\u60fa\u677e\uff0c\u61d2\u6d0b\u6d0b\u5730\u5750\u8d77\u6765\uff0c\u541f\u9053\uff1a\u201c\u5927\u68a6\u8c01\u5148\u89c9\uff0c \u5e73\u751f\u6211\u81ea\u77e5\u3002\u201d\u4e91\u857e\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u77e5\u4ec0\u4e48\uff1f\u5f3a\u76d7\u6765\u8fc7\u5566\uff01\u201d\u4e66\u751f\u63c9\u63c9\u7761\u773c\uff0c\u9053\uff1a\u201c\u534a\u591c\u4e09 \u66f4\uff0c\u6270\u4eba\u6e05\u68a6\uff01\u4f60\u8fd9\u5c0f\u54e5\u513f\u600e\u4e48\u4e13\u548c\u6211\u6363\u4e71\uff1f\u201d\u4e00\u70b9\u4e5f\u4e0d\u4fe1\u4e91\u857e\u7684\u8bdd\uff0c\u975e\u4f46\u4e0d\u591a\u8c22\uff0c\u53cd\u800c\u602a \u8d23\u3002\u4e91\u857e\u6c14\u9053\uff1a\u201c\u4f60\u4e0d\u4fe1\u4f60\u5c31\u5230\u5916\u9762\u53bb\u770b\uff0c\u5f3a\u76d7\u5df2\u6765\u8fc7\u5566\uff01\u201d\u4e66\u751f\u4f38\u4e86\u4f38\u61d2\u8170\uff0c\u5ffd\u800c\u7b11\u9053\uff1a \u201c\u65e2\u7136\u6765\u8fc7\u4e86\uff0c\u90a3\u4e0d\u662f\u6ca1\u4e8b\u4e86\uff0c\u4f60\u8fd8\u53eb\u9192\u6211\u505a\u4ec0\u4e48\uff1f\u201d\u4e91\u857e\u53c8\u6c14\u53c8\u607c\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u662f\u6211\u628a \u4ed6\u4eec\u90fd\u6740\u9000\u7684\u3002\u201d\u90a3\u4e66\u751f\u9053\uff1a\u201c\u771f\u7684\u5417\uff1f\u597d\u6781\uff0c\u597d\u6781\uff01\u4f60\u5403\u4e00\u4e2a\u828b\u5934\u3002\u8fd9\u56de\u4f60\u4e0d\u662f\u65e0\u529f\u53d7 \u7984\uff0c\u6211\u4e0d\u8bf4\u4f60\u767d\u5403\u4e86\uff01\u201d\u201c\u535c\u201d\u7684\u628a\u4e00\u4e2a\u828b\u5934\u629b\u6765\uff0c\u4e91\u857e\u5927\u6012\uff0c\u4e00\u638c\u5c06\u828b\u5934\u62cd\u98de\uff0c\u9053\uff1a\u201c\u8c01 \u548c\u4f60\u5f00\u73a9\u7b11\u5462\uff0c\u5582\uff0c\u6211\u95ee\u4f60\uff0c\u4f60\u59d3\u751a\u540d\u8c01\uff0c\u4ece\u54ea\u91cc\u6765\u7684\uff1f\u201d\u90a3\u4e66\u751f\u4e00\u77aa\u773c\u775b\uff0c\u5ffd\u7136\u5b66\u8db3\u4e91\u857e \u7684\u795e\u6c14\uff0c\u621f\u6307\u559d\u9053\uff1a\u201c\u5582\uff0c\u6211\u6765\u95ee\u4f60\uff0c\u4f60\u59d3\u751a\u540d\u8c01\uff0c\u4ece\u54ea\u91cc\u6765\u7684\uff1f\u201d\u4e91\u857e\u6012\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u201d \u4e66\u751f\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u80fd\u5ba1\u95ee\u6211\uff0c\u96be\u9053\u6211\u5c31\u4e0d\u80fd\u5ba1\u95ee\u4f60\uff1f\u4f60\u662f\u5b98\u513f\uff0c\u751f\u6765\u5ba1\u95ee\u522b\u4eba\u7684\u4e0d\u6210\uff1f\u201d \u3000\u3000\u4e91\u857e\u7a92\u4e86\u4e00\u7a92\uff0c\u8fd9\u4e66\u751f\u5f3a\u8bcd\u593a\u7406\uff0c\u53ef\u4e5f\u771f\u7684\u7ed9\u4ed6\u95ee\u4f4f\uff0c\u5fc3\u4e2d\u60f3\uff1a\u201c\u6211\u7684\u6765\u5386\uff0c\u5982\u4f55\u80fd\u8bf4 \u4f60\u77e5\uff1f\u201d\u89c1\u90a3\u4e66\u751f\u659c\u7740\u773c\u775b\uff0c\u770b\u7740\u81ea\u5df1\uff0c\u4e00\u526f\u795e\u6c14\uff0c\u4ee4\u4eba\u54ed\u7b11\u4e0d\u5f97\u3002\u4e91\u857e\u8f6c\u5ff5\u4e00\u60f3\uff1a\u201c\u6211\u7684 \u6765\u5386\uff0c\u4e0d\u80fd\u8bf4\u7ed9\u4ed6\u77e5\uff0c\u4e5f\u8bb8\u4ed6\u7684\u6765\u5386\uff0c\u4e00\u6837\u4e0d\u80fd\u8bf4\u7ed9\u6211\u77e5\u3002\u5df1\u6240\u4e0d\u6b32\uff0c\u4f55\u5fc5\u5f3a\u65bd\u4e8e\u4eba\uff1f\u90a3\u4e24 \u4e2a\u80e1\u4eba\uff0c\u4e07\u91cc\u8ffd\u8e2a\uff0c\u83ab\u975e\u4ed6\u4e5f\u50cf\u6211\u7237\u7237\u4e00\u6837\uff0c\u662f\u4ece\u8499\u53e4\u90a3\u8fb9\uff0c\u95f4\u5173\u9003\u51fa\u6765\u7684\u6c49\u4eba\uff1f\u201d\u8fd9\u6837\u4e00 \u60f3\uff0c\u4e0d\u89c9\u5bf9\u4e66\u751f\u6709\u4e86\u656c\u610f\uff0c\u4f46\u7785\u4ed6\u90a3\u526f\u61d2\u6d0b\u6d0b\u4f3c\u7b11\u975e\u7b11\u659c\u773c\u770b\u4eba\u7684\u795e\u6c14\uff0c\u53c8\u89c9\u8ba8\u538c\u3002\u60f3\u4e86\u4e00 \u60f3\uff0c\u4ece\u6000\u4e2d\u53d6\u51fa\u5468\u5065\u9001\u7ed9\u7684\u90a3\u5bf9\u65e5\u6708\u53cc\u65d7\uff0c\u629b\u8fc7\u53bb\u9053\uff1a\u201c\u8fd9\u4e2a\u7ed9\u4f60\uff0c\u6211\u4e0d\u548c\u4f60\u540c\u8d70\u5566\u3002\u201d\u4e66 \u751f\u77a5\u4e86\u4e00\u773c\uff0c\u9053\uff1a\u201c\u6211\u53c8\u4e0d\u662f\u620f\u5b50\uff0c\u8981\u4f60\u8fd9\u4e24\u9762\u65d7\u505a\u4ec0\u4e48\uff1f\u201d\u4e91\u857e\u8a00\u9053\uff1a\u201c\u4f60\u5b64\u8eab\u4e00\u8def\uff0c\u5371 \u9669\u5f97\u5f88\uff0c\u6709\u4e86\u8fd9\u4e24\u9762\u65d7\u5b50\uff0c\u5f3a\u76d7\u5c31\u4e0d\u6562\u6253\u52ab\u4f60\u4e86\u3002\u201d\u4e66\u751f\u9053\uff1a\u201c\u4ec0\u4e48\uff0c\u8fd9\u65d7\u5b50\u662f\u5723\u65e8\u5417\uff1f\u201d \u4e91\u857e\u7b11\u8a00\u9053\uff1a\u201c\u53ea\u6015\u6bd4\u5723\u65e8\u8fd8\u6709\u529b\u91cf\u5462\uff01\u8fd9\u662f\u91d1\u5200\u5be8\u4e3b\u7684\u65e5\u6708\u53cc\u65d7\uff0c\u4f60\u4ece\u5317\u8fb9\u6765\uff0c\u96be\u9053\u6ca1\u542c \u8bf4\u8fc7\u5417\uff1f\u91d1\u5200\u5be8\u4e3b\u7b49\u4e8e\u662f\u5317\u8fb9\u5f3a\u76d7\u76df\u4e3b\uff0c\u7eff\u6797\u8c6a\u6770\uff0c\u8c01\u90fd\u656c\u4ed6\u51e0\u5206\u3002\u201d\u4e91\u857e\u9001\u4ed6\u65e5\u6708\u53cc\u65d7\uff0c \u5b9e\u662f\u4e00\u756a\u597d\u610f\uff0c\u4e0d\u6599\u90a3\u4e66\u751f\u9762\u8272\u4e00\u53d8\uff0c\u62ff\u8d77\u65e5\u6708\u53cc\u65d7\uff0c\u5ffd\u7136\u51b7\u7b11\u9053\uff1a\u201c\u5927\u4e08\u592b\u7acb\u8eab\u5904\u4e16\uff0c\u5c82 \u80fd\u6258\u5e87\u532a\u4eba\uff1f\u4f60\u8bfb\u8fc7\u5b54\u5b5f\u4e4b\u4e66\u5417\uff1f\u201d\u53cc\u624b\u4e00\u6495\uff0c\u7adf\u628a\u5a01\u9707\u80e1\u6c49\u7684\u65e5\u6708\u53cc\u65d7\u6495\u6210\u56db\u7247\uff01 \u3000\u3000\u4e91\u857e\u9762\u8272\u53d1\u9752\uff0c\u8fd9\u4e00\u6c14\u53ef\u662f\u975e\u540c\u5c0f\u53ef\uff0c\u5927\u6012\u559d\u9053\uff1a\u201c\u91d1\u5200\u5be8\u4e3b\u5a01\u9707\u80e1\u6c49\uff0c\u662f\u4e2a\u9876\u5929\u7acb\u5730 \u7684\u82f1\u96c4\uff0c\u5c82\u5bb9\u4f60\u8fd9\u9178\u4e01\u4fae\u8fb1\uff01\u201d\u4e3e\u8d77\u624b\u638c\uff0c\u5288\u9762\u6253\u4ed6\u8033\u5149\uff0c\u5ffd\u89c1\u4ed6\u7f8a\u8102\u767d\u7389\u822c\u7684\u8138\u86cb\uff0c\u5439\u5f39 \u5f97\u7834\uff0c\u60f3\u9053\uff1a\u201c\u8fd9\u4e00\u638c\u6253\u53bb\uff0c\u5c82\u4e0d\u5728\u4ed6\u8138\u4e0a\u7559\u4e0b\u4e94\u4e2a\u6307\u5370\uff0c\u90a3\u591a\u96be\u770b\uff01\u201d\u624b\u638c\u62cd\u5230\u4e86\u4e2d\u9014\uff0c \u53c8\u6536\u4e86\u56de\u6765\uff0c\u6012\u9053\uff1a\u201c\u6211\u4e0d\u4e0e\u4f60\u8fd9\u8150\u5112\u9178\u4e01\u4e00\u822c\u89c1\u8bc6\uff0c\u7f62\u7f62\uff0c\u9976\u4f60\u4e00\u6b21\u3002\u4ee5\u540e\u4f60\u88ab\u5f3a\u4eba\u52ab \u6740\uff0c\u4e5f\u662f\u4f60\u81ea\u5df1\u8ba8\u6b7b\uff0c\u6211\u4e0d\u518d\u7ba1\u4f60\u5566\uff01\u201d\u500f\u5730\u8f6c\u8eab\uff0c\u65cb\u98ce\u822c\u51b2\u51fa\u95e8\u5916\u53bb\uff0c\u5979\u4e00\u756a\u597d\u610f\uff0c\u5f04\u6210 \u8fd9\u6837\uff0c\u5fc3\u4e2d\u6781\u4e0d\u8212\u670d\uff0c\u518d\u4e5f\u4e0d\u613f\u591a\u77a7\u90a3\u4e66\u751f\u4e00\u773c\u3002\u90a3\u4e66\u751f\u53cc\u76ee\u95ea\u5149\uff0c\u770b\u4e91\u857e\u51b2\u51fa\u95e8\u53bb\uff0c\u7f13\u7f13 \u7ad9\u4e86\u8d77\u6765\u5fc3\u60f3\u51fa\u58f0\u547c\u5524\uff0c\u5ffd\u53c8\u51b7\u7b11\u4e00\u58f0\uff0c\u5fcd\u7740\u4e0d\u53eb\u3002 \u3000\u3000\u4e91\u857e\u7b56\u9a6c\u51fa\u6797\uff0c\u5728\u4e1b\u6797\u4e2d\u5ffd\u542c\u5f97\u201c\u545c\u201d\u7684\u4e00\u58f0\u63a0\u8fc7\u5934\u9876\uff0c\u4e91\u857e\u52d2\u7740\u9a6c\u25a1\uff0c\u53eb\u9053\uff1a\u201c\u65bd\u6697 \u7b97\u7684\u5c0f\u8d3c\uff0c\u6709\u79cd\u7684\u6eda\u51fa\u6765\uff01\u201d\u5ffd\u7136\u5934\u4e0a\u556a\u7684\u4e00\u54cd\uff0c\u4e91\u857e\u4e00\u62c9\u9a6c\u5934\uff0c\u907f\u4e86\u5f00\u53bb\uff0c\u53ea\u89c1\u4e00\u679d\u6811\u679d \u8dcc\u4e0b\u5730\u6765\uff0c\u6811\u679d\u4e0a\u7f1a\u7740\u4e00\u4e2a\u5c0f\u5c0f\u7684\u7ee3\u82b1\u5dfe\u624e\u6210\u7684\u5305\u88f9\u3002\u4e91\u857e\u5403\u4e86\u4e00\u60ca\uff0c\u8fd9\u6b63\u662f\u81ea\u5df1\u7684\u4e1c\u897f\uff0c \u6025\u5fd9\u89e3\u5f00\u6765\u770b\uff0c\u53ea\u89c1\u5468\u5065\u9001\u7ed9\u5979\u7684\u91d1\u94f6\u73e0\u5b9d\uff0c\u5168\u5728\u5176\u4e2d\uff0c\u8fde\u81ea\u5df1\u5077\u6765\u7684\u90a3\u51e0\u4e24\u94f6\u5b50\u4e5f\u5728\u5176 \u5185\u3002\u4e91\u857e\u6025\u5728\u9a6c\u80cc\u4e0a\u817e\u8eab\u98de\u8d77\uff0c\u63a0\u4e0a\u6811\u68a2\uff0c\u7eb5\u76ee\u56db\u671b\uff0c\u4f46\u89c1\u6b8b\u661f\u660e\u706d\u98ce\u5439\u8349\u52a8\uff0c\u56db\u91ce\u65e0\u4eba\u3002 \u3000\u3000\u4e91\u857e\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u7f62\u7f62\uff0c\u771f\u662f\u5929\u5916\u6709\u5929\uff0c\u60f3\u4e0d\u5230\u5728\u8fd9\u5c0f\u5730\u65b9\uff0c\u4e5f\u78b0\u5230\u5982\u65af\u9ad8\u624b\u3002\u201d\u7eb5 \u9a6c\u51fa\u6797\uff0c\u6797\u5b50\u5916\u8fb9\uff0c\u5df2\u662f\u66d9\u5149\u6b32\u73b0\u3002 \u3000\u3000\u4e91\u857e\u8d81\u7740\u6e05\u6668\uff0c\u8de8\u9a6c\u4e0a\u8def\uff0c\u7eed\u5411\u897f\u884c\u3002\u4f46\u89c1\u4e00\u8def\u4e0a\u4eba\u9a6c\u4e0d\u7edd\uff0c\u4e2a\u4e2a\u90fd\u662f\u96c4\u8d73\u8d73\u7684\u6b66\u592b\uff0c \u4e00\u770b\u5c31\u77e5\u662f\u4e09\u5c71\u4e94\u5cb3\u7684\u597d\u6c49\u3002 \u3000\u3000\u4e91\u857e\u60f3\u8d77\u5468\u5c71\u6c11\u7ed9\u5979\u8bb2\u89e3\u7684\u201c\u6c5f\u6e56\u5e38\u8bc6\u201d\uff0c\u5fc3\u9053\uff1a\u201c\u4f3c\u6b64\u60c5\u666f\uff0c\u82e5\u975e\u4ec0\u4e48\u5e2e\u4f1a\u5927\u5178\uff0c\u5c31 \u662f\u6b66\u6797\u4f1a\u76df\u4e86\u3002\u201d\u90a3\u4e9b\u4eba\u7b56\u9a6c\u8d76\u8fc7\u4e91\u857e\uff0c\u4e5f\u4e0d\u7406\u5979\u3002\u4e91\u857e\u8d70\u4e86\u4e00\u7a0b\uff0c\u8179\u4e2d\u9965\u6e34\uff0c\u8d70\u8fdb\u8def\u8fb9\u4e00 \u4e2a\u517c\u5356\u7ca5\u996d\u7684\u8336\u4ead\uff0c\u80e1\u4e71\u5403\u4e86\u4e2a\u9971\uff0c\u89c1\u90a3\u8336\u4ead\u6b63\u70e7\u7740\u4e24\u5927\u7f38\u8336\uff0c\u9042\u548c\u90a3\u8336\u4ead\u4e3b\u4eba\u642d\u8baa\u9053\uff1a \u201c\u4eca\u513f\u597d\u751f\u610f\u554a\uff0c\u4e00\u8def\u4e0a\u8d76\u8def\u7684\u4eba\u53ef\u771f\u4e0d\u5c11\u3002\u201d\u90a3\u8336\u4ead\u4e3b\u4eba\u7b11\u9053\uff1a\u201c\u5ba2\u5b98\uff0c\u4f60\u662f\u4e0d\u662f\u5230\u9ed1\u77f3 \u5e84\u53bb\u7684\u5427\uff1f\u201d\u4e91\u857e\u9053\uff1a\u201c\u4ec0\u4e48\u9ed1\u77f3\u5e84\uff1f\u201d\u90a3\u8336\u4ead\u4e3b\u4eba\u9053\uff1a\u201c\u5ba2\u5b98\u60f3\u5fc5\u662f\u4ece\u5916\u8def\u6765\u7684\u4e86\uff0c\u9ed1\u77f3 \u5e84\u7684\u77f3\u5927\u7237\u4eca\u5929\u505a\u5927\u5bff\uff0c\u8bb8\u591a\u670b\u53cb\u90fd\u8d76\u6765\u7ed9\u4ed6\u62dc\u5bff\u3002\u201d\u4e91\u857e\u5fc3\u4e2d\u4e00\u52a8\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u8bf4\u7684\u662f\u8f70 \u5929\u96f7\u77f3\u82f1\u77f3\u8001\u82f1\u96c4\u4e48\uff1f\u201d\u8336\u4ead\u4e3b\u4eba\u8083\u7136\u8d77\u656c\uff0c\u9053\uff1a\u201c\u539f\u6765\u4f60\u4e5f\u662f\u77f3\u5927\u7237\u7684\u670b\u53cb\u3002\u201d\u4e91\u857e\u9053\uff1a \u201c\u77f3\u8001\u82f1\u96c4\u8c01\u4eba\u4e0d\u77e5\uff0c\u6211\u867d\u662f\u5916\u7701\u4eba\uff0c\u4e5f\u542c\u8fc7\u4ed6\u7684\u540d\u5b57\u3002\u201d\u8336\u4ead\u4e3b\u4eba\u9053\uff1a\u201c\u662f\u5440\uff0c\u77f3\u5927\u7237\u4ea4 \u6e38\u5e7f\u9614\uff0c\u5404\u8def\u4eba\u7269\uff0c\u4e0d\u8bba\u8bc6\u4e0e\u4e0d\u8bc6\uff0c\u6295\u5230\u4ed6\u7684\u5e84\u4e2d\uff0c\u65e0\u4e0d\u62db\u5f85\u3002\u201d\u4e91\u857e\u542c\u5468\u5c71\u6c11\u8bf4\u8fc7\uff0c\u90a3\u77f3 \u82f1\u4ee5\u8e51\u4e91\u5251\u4e0e\u98de\u8757\u77f3\u5a01\u9707\u6b66\u6797\uff0c\u90a3\u624b\u8e51\u4e91\u5251\u56fa\u662f\u6b66\u6797\u4e00\u7edd\uff0c\u90a3\u624b\u98de\u8757\u77f3\u6697\u5668\u4e5f\u6781\u8db3\u60ca\u4eba\uff0c\u4e2d \u4eba\u6709\u5982\u70ae\u5f39\uff0c\u6240\u4ee5\u5916\u53f7\u53eb\u505a\u8f70\u5929\u96f7\u3002\u8fd9\u77f3\u82f1\u4e0d\u4f46\u6b66\u827a\u9ad8\u5f3a\uff0c\u800c\u4e14\u8c6a\u4fa0\u4ed7\u4e49\uff0c\u53ea\u662f\u813e\u6c14\u6709\u70b9\u53e4 \u602a\u3002\u4e91\u60f3\u9053\uff1a\u201c\u539f\u6765\u6b64\u4eba\u5c31\u4f4f\u5728\u66f2\u9633\u57ce\u5916\uff0c\u6211\u4e0d\u5982\u4e5f\u53bb\u62dc\u5bff\u3002\u4e09\u5c71\u4e94\u5cb3\u7684\u82f1\u96c4\u65e2\u7136\u5927\u6279\u6765 \u5230\uff0c\u90a3\u620f\u5f04\u6211\u7684\u9ad8\u624b\u53ef\u80fd\u4e5f\u5728\u5176\u4e2d\uff0c\u6211\u5c82\u53ef\u9519\u8fc7\u673a\u4f1a\u3002\u201d\u4e3b\u610f\u6253\u5b9a\uff0c\u5411\u8336\u4ead\u4e3b\u4eba\u8ba8\u4e86\u7eb8\u7b14\uff0c \u5199\u4e86\u4e00\u5f20\u8d3a\u8d34\uff0c\u7b11\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u9053\u4ed6\u8001\u4eba\u5bb6\u4eca\u65e5\u505a\u5bff\uff0c\u771f\u662f\u78b0\u5de7\u78b0\u4e0a\u4e86\u3002\u201d\u95ee\u660e\u4e86\u53bb\u9ed1\u77f3\u5e84 \u7684\u8def\uff0c\u7ed3\u4e86\u8336\u94b1\uff0c\u8de8\u4e0a\u9a6c\u80cc\uff0c\u5f84\u5230\u9ed1\u77f3\u5e84\u53bb\u3002 \u3000\u3000\u9ed1\u77f3\u5e84\u8d3a\u5ba2\u5982\u4e91\uff0c\u6536\u8d3a\u793c\u7684\u770b\u4e86\u8d3a\u8d34\uff0c\u95ee\u4e5f\u4e0d\u95ee\uff0c\u5c31\u8ba9\u77e5\u5ba2\u7684\u5e26\u5165\u5bb4\u5ba2\u7684\u5927\u82b1\u56ed\uff0c\u4e91\u857e \u6765\u5f97\u6b63\u662f\u65f6\u5019\uff0c\u56ed\u4e2d\u7b75\u5f00\u767e\u5e2d\uff0c\u6070\u662f\u5165\u5e2d\u4e4b\u65f6\u3002\u4e91\u857e\u88ab\u62db\u547c\u5750\u5728\u4e00\u4e2a\u89d2\u843d\uff0c\u540c\u5e2d\u7684\u90fd\u4e0d\u76f8 \u8bc6\u3002\u542c\u5f97\u4ed6\u4eec\u53fd\u53fd\u55b3\u55b3\u7684\u8c08\u8bba\uff0c\u6709\u4e00\u4e2a\u8bf4\uff1a\u201c\u77f3\u8001\u82f1\u96c4\u4eca\u513f\u4e0d\u4f46\u505a\u5927\u5bff\uff0c\u542c\u8bf4\u8fd8\u8981\u9009\u5973\u5a7f \u5462\u3002\u201d\u53e6\u4e00\u4e2a\u9053\uff1a\u201c\u8001\u5934\u513f\u53ef\u5934\u75db\u5566\uff0c\u6c99\u5be8\u4e3b\uff0c\u97e9\u5c9b\u4e3b\uff0c\u6797\u5e84\u4e3b\uff0c\u4e09\u5bb6\u4e00\u540c\u6765\u6c42\u5a5a\uff0c\u8fd9\u53ef\u600e \u4e48\u5bf9\u4ed8\u5f97\u4e86\uff1f\u201d\u53e6\u4e00\u4e2a\u9053\uff1a\u201c\u8f70\u5929\u96f7\u81ea\u6709\u6cd5\u513f\uff0c\u4f55\u5fc5\u4f60\u6765\u66ff\u4ed6\u62c5\u5fe7\u3002\u201d\u4f38\u624b\u4e00\u6307\uff0c\u9053\uff1a\u201c\u4f60 \u770b\uff01\u201d\u4e91\u857e\u8ddf\u7740\u770b\u53bb\uff0c\u53ea\u89c1\u56ed\u4e2d\u642d\u8d77\u4e00\u4e2a\u5927\u64c2\u53f0\uff0c\u9ad8\u53ef\u4e8c\u4e08\u6709\u4f59\u3002\u90a3\u4eba\u7b11\u9053\uff1a\u201c\u542c\u8bf4\u8f70\u5929\u96f7 \u5012\u662f\u8c6a\u723d\u4e4b\u6781\uff0c\u5e72\u8106\u6765\u4e2a\u6bd4\u6b66\u62db\u4eb2\uff0c\u8c01\u6253\u5f97\u8d62\u4ed6\u7684\u5973\u513f\u8c01\u5c31\u662f\u4ed6\u7684\u5973\u5a7f\uff0c\u81f3\u4eb2\u597d\u53cb\uff0c\u6beb\u4e0d\u4f8b \u5916\uff0c\u4e09\u5bb6\u90fd\u6ca1\u8bdd\u8bf4\u3002\u201d\u5176\u4ed6\u7684\u4eba\u7b11\u9053\uff1a\u201c\u8fd9\u53ef\u6709\u70ed\u95f9\u770b\u4e86\u3002\u201d\u4e91\u857e\u5fc3\u4e2d\u6697\u7b11\uff1a\u201c\u5929\u4e0b\u95f4\u7adf\u6709 \u8fd9\u6837\u9009\u5973\u5a7f\u7684\u529e\u6cd5\uff0c\u4e07\u4e00\u9009\u4e86\u4e2a\u5927\u9ebb\u5b50\uff0c\u5c82\u4e0d\u59d4\u5c48\u4e86\u5973\u513f\uff01\u201d \u3000\u3000\u5915\u9633\u6162\u6162\u897f\u79fb\uff0c\u5ffd\u542c\u5f97\u4e00\u7247\u606d\u8d3a\u4e4b\u58f0\uff0c\u6ee1\u573a\u8d77\u7acb\uff0c\u4e91\u857e\u8e2e\u9ad8\u811a\u770b\uff0c\u53ea\u89c1\u4e00\u4e2a\u7ea2\u9762\u8001\u4eba\uff0c \u643a\u7740\u4e00\u4e2a\u5973\u5b50\u8d70\u4e86\u51fa\u6765\uff0c\u6392\u5f00\u8d3a\u5ba2\uff0c\u8df3\u4e0a\u64c2\u53f0\u3002\u90a3\u5973\u5b50\u751f\u5f97\u751a\u4e3a\u79c0\u4e3d\uff0c\u8138\u4f3c\u8299\u84c9\uff0c\u7709\u957f\u5165 \u9b13\uff0c\u4e91\u857e\u6324\u4e0a\u524d\u770b\uff0c\u53ea\u89c1\u5979\u843d\u843d\u5927\u65b9\uff0c\u7709\u5b87\u4e4b\u95f4\uff0c\u9690\u6709\u82f1\u6c14\uff0c\u5bf9\u7740\u4e00\u7fa4\u5bbe\u5ba2\uff0c\u5c45\u7136\u5e76\u4e0d\u7f9e \u60e7\u3002\u6b63\u662f\uff1a \u3000\u3000\u7b75\u524d\u817e\u5251\u6c14\uff0c\u4fa0\u5973\u4f1a\u5947\u7537\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u840d\u8e2a\u4fa0\u5f71\u5f55\u300b\u2014\u2014\u7b2c\u56db\u56de\u3000\u94f8\u9519\u672c\u65e0\u5fc3\u64c2\u53f0\u4e89\u80dc\u3000\u8ffd\u8e2a\u7591\u6709\u610f\u9526\u5e10\u9003\u4eba \u6881\u7fbd\u751f\u300a\u840d\u8e2a\u4fa0\u5f71\u5f55\u300b \u7b2c\u56db\u56de\u3000\u94f8\u9519\u672c\u65e0\u5fc3\u64c2\u53f0\u4e89\u80dc\u3000\u8ffd\u8e2a\u7591\u6709\u610f\u9526\u5e10\u9003\u4eba \u3000\u3000\u4e91\u857e\u542c\u5f97\u65c1\u4eba\u8c08\u8bba\uff0c\u77e5\u9053\u8fd9\u7ea2\u9762\u8001\u4eba\u6b63\u662f\u9ed1\u77f3\u5e84\u7684\u5e84\u4e3b\u8f70\u5929\u96f7\u77f3\u82f1\uff0c\u90a3\u5973\u7684\u4fbf\u662f\u4ed6\u7684\u5973 \u513f\u77f3\u7fe0\u51e4\u4e86\u3002\u4e91\u857e\u6697\u6697\u559d\u5f69\uff0c\u6697\u81ea\u7b11\u9053\uff1a\u201c\u8fd9\u8001\u5934\u513f\u7ea2\u8138\u5c16\u5634\uff0c\u679c\u7136\u50cf\u753b\u4e0a\u7684\u96f7\u516c\uff0c\u751f\u4e0b\u7684 \u5973\u513f\u5374\u8fd9\u6837\u4fca\u79c0\u3002\u201d \u3000\u3000\u53ea\u89c1\u77f3\u82f1\u62b1\u62f3\u5411\u53f0\u4e0b\u4e00\u62f1\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u5c0f\u8001\u513f\u7684\u8d31\u65e5\u751f\u8fb0\uff0c\u627f\u5404\u4f4d\u5927\u54e5\u8d4f\u9762\uff0c\u4e0d\u60dc\u5c48 \u9a7e\u5230\u8fd9\u5c0f\u5e84\u5b50\u6765\uff0c\u4ffa\u5148\u656c\u5927\u5bb6\u4e09\u676f\uff01\u201d\u53f0\u4e0b\u8d3a\u5ba2\u8f70\u7136\u9053\u597d\uff0c\u5404\u81ea\u628a\u9152\u90fd\u5e72\u4e86\u3002\u77f3\u82f1\u62c8\u987b\u7b11 \u9053\uff1a\u201c\u9ed1\u77f3\u5e84\u7a77\u4e61\u50fb\u58e4\uff0c\u65e0\u4ee5\u5a31\u5bbe\uff0c\u53eb\u5404\u4f4d\u89c1\u7b11\u4e86\u3002\u4ffa\u8fd9\u5973\u513f\u8fd8\u7c97\u4f1a\u62f3\u811a\uff0c\u5c31\u53eb\u5979\u7ec3\u51e0\u8def\u7b28 \u62f3\uff0c\u7ed9\u5404\u4f4d\u53d4\u4f2f\u966a\u9152\u5982\u4f55\uff1f\u201d\u4f17\u4eba\u66f4\u662f\u5927\u58f0\u53eb\u597d\u3002\u77f3\u82f1\u53c8\u7b11\u9053\uff1a\u201c\u53ea\u662f\u4e00\u4eba\u7ec3\u62f3\uff0c\u4ea6\u65e0\u8da3 \u5473\uff0c\u6562\u70e6\u6c99\u5be8\u4e3b\u3001\u97e9\u5c9b\u4e3b\u548c\u6797\u5e84\u4e3b\u7684\u4e09\u4f4d\u4ee4\u90ce\uff0c\u7ed9\u5979\u8d50\u6559\u51e0\u62db\u3002\u770b\u8c01\u7ec3\u7684\u6700\u597d\uff0c\u4ffa\u4e5f\u6709\u70b9\u5c0f \u5c0f\u7684\u5f69\u7269\uff0c\u4e09\u4f4d\u4e16\u5144\u610f\u4e0b\u5982\u4f55\uff1f\u201d\u4ed6\u867d\u6ca1\u6709\u660e\u8a00\u6bd4\u6b66\u62db\u4eb2\uff0c\u5e2d\u4e0a\u7fa4\u8c6a\u5374\u77e5\u9053\u4ed6\u7684\u7528\u610f\uff0c\u97e9\u5c9b \u4e3b\u548c\u6797\u5e84\u4e3b\u5148\u81ea\u53eb\u9053\uff1a\u201c\u597d\u6781\uff0c\u597d\u6781\uff01\u201d\u5e26\u4e86\u513f\u5b50\u5728\u4eba\u4e1b\u4e2d\u4fbf\u98de\u4e0a\u53f0\u6765\uff0c\u77eb\u5065\u4e4b\u6781\u3002\u90a3\u6c99\u5be8 \u4e3b\u7565\u4e00\u8fdf\u7591\uff0c\u4e5f\u5e26\u4e86\u513f\u5b50\u7eb5\u4e0a\u53f0\u6765\u3002\u90a3\u64c2\u53f0\u9ad8\u8fbe\u4e8c\u4e08\u6709\u591a\uff0c\u6c99\u5be8\u4e3b\u4e00\u8dc3\u5373\u4e0a\uff0c\u4ed6\u7684\u513f\u5b50\u811a\u5c16 \u5728\u53f0\u8fb9\u4e00\u52fe\uff0c\u5374\u9669\u9669\u8dcc\u4e86\u4e0b\u6765\u3002\u53f0\u4e0b\u7fa4\u4f17\uff0c\u5927\u4e3a\u60ca\u8be7\u3002\u8fd9\u6c99\u5be8\u4e3b\uff0c\u5728\u9ed1\u9053\u4e0a\u662f\u9876\u513f\u5c16\u513f\u7684\u4eba \u7269\uff0c\u6b66\u529f\u7cbe\u7eaf\u4eba\u6240\u5171\u77e5\uff0c\u4ed6\u7684\u513f\u5b50\u5bb6\u5b66\u6e0a\u6e90\uff0c\u5c3d\u5f97\u4ed6\u7684\u6240\u4f20\uff0c\u5fc3\u72e0\u624b\u8fa3\uff0c\u53c8\u517c\u4eba\u5728\u58ee\u5e74\uff0c\u5728 \u9ed1\u9053\u4e0a\u7684\u5a01\u540d\uff0c\u5df2\u8d76\u4e0a\u4e86\u4ed6\u7684\u7236\u4eb2\u3002\u77e5\u9053\u5e95\u7ec6\u7684\u4eba\uff0c\u90fd\u6599\u4ed6\u4eca\u65e5\u5fc5\u64cd\u80dc\u7b97\uff0c\u8c01\u77e5\u4ed6\u4e00\u4e0a\u64c2 \u53f0\uff0c\u5c31\u5148\u7ed9\u97e9\u5c9b\u4e3b\u548c\u6797\u5e84\u4e3b\u7684\u513f\u5b50\u6bd4\u4e86\u4e0b\u53bb\uff0c\u800c\u8fd9\u4e00\u7eb5\u4e00\u8dc3\uff0c\u4e5f\u5927\u4e0d\u5982\u4ed6\u5e73\u65e5\u7684\u529f\u592b\uff0c\u8fd9\u53ef \u771f\u771f\u51fa\u4eba\u610f\u5916\u3002 \u3000\u3000\u6c99\u5be8\u4e3b\u7709\u5934\u4e00\u76b1\uff0c\u8bb7\u8bb7\u6b32\u8a00\uff0c\u97e9\u5c9b\u4e3b\u7684\u513f\u5b50\u97e9\u5927\u6d77\u5df2\u5148\u8dc3\u5230\u53f0\u5fc3\uff0c\u4e00\u63d6\u8bf4\u9053\uff1a\u201c\u77f3\u8001\u4f2f \u723d\u5feb\u4e4b\u6781\uff0c\u6211\u4e5f\u4e0d\u5ba2\u6c14\u4e86\uff0c\u5c31\u8ba9\u6211\u5148\u8bf7\u6559\u4e16\u59b9\u51e0\u62db\u5427\uff0c\u4e16\u59b9\u53ef\u8981\u624b\u4e0b\u7559\u60c5\u554a\uff01\u201d\u77f3\u82f1\u7b11\u7740 \u9053\uff1a\u201c\u597d\u8bf4\uff0c\u597d\u8bf4\uff01\u6211\u5c31\u559c\u6b22\u723d\u5feb\u7684\u4eba\u3002\u5927\u5bb6\u90fd\u4e0d\u5fc5\u5ba2\u5957\u4e86\uff0c\u6709\u591a\u5c11\u672c\u4e8b\u5c3d\u7ba1\u62ff\u51fa\u6765\uff0c\u6253\u4f24 \u4e86\u6211\u6709\u836f\u533b\u3002\u201d\u97e9\u5927\u6d77\u5e94\u4e86\u4e00\u58f0\uff0c\u53cc\u638c\u4e00\u63d6\uff0c\u5288\u9762\u5c31\u662f\u4e00\u62db\u201c\u7ae5\u5b50\u62dc\u89c2\u97f3\u201d\uff0c\u53cc\u638c\u9f50\u51fa\uff0c\u65e2 \u662f\u656c\u793c\u7684\u5bb6\u6570\uff0c\u53c8\u662f\u96c4\u52b2\u7684\u62db\u6570\uff0c\u77f3\u82f1\u9053\u4e86\u58f0\u201c\u597d\uff01\u201d\u6c99\u5be8\u4e3b\u7236\u5b50\u76f8\u5bf9\u82e6\u7b11\uff0c\u628a\u60f3\u8bf4\u7684\u8bdd\u541e \u4e86\u56de\u53bb\u3002 \u3000\u3000\u77f3\u7fe0\u51e4\u8eab\u5b50\u6ef4\u6e9c\u6e9c\u4e00\u8f6c\uff0c\u500f\u7136\u8f6c\u5230\u97e9\u5927\u6d77\u7684\u80cc\u540e\uff0c\u97e9\u5927\u6d77\u8fde\u53d1\u6570\u62db\uff0c\u5de6\u53f3\u640f\u51fb\uff0c\u5374\u8fde\u5979 \u7684\u88d9\u89d2\u90fd\u635e\u4e0d\u7740\u3002\u4e91\u857e\u5fc3\u60f3\u9053\uff1a\u201c\u539f\u6765\u5979\u7ec3\u7684\u548c\u6211\u540c\u4e00\u5bb6\u6570\uff0c\u90fd\u662f\u4ece\u516b\u5366\u6e38\u8eab\u638c\u5316\u51fa\u6765 \u7684\u3002\u201d\u4e91\u857e\u5728\u6843\u6797\u4e2d\u6240\u7ec3\u7684\u201c\u7a7f\u82b1\u7ed5\u6811\u201d\u8eab\u6cd5\u4e43\u662f\u516b\u5366\u6e38\u8eab\u638c\u7684\u6700\u4e0a\u4e58\u529f\u592b\uff0c\u867d\u662f\u5728\u516b\u5366\u6e38 \u8eab\u638c\u4e2d\u53d8\u5316\u51fa\u54d1\uff0c\u5b9e\u5df2\u5728\u6b63\u5b97\u7684\u516b\u5366\u6e38\u8eab\u638c\u4e4b\u4e0a\uff0c\u6240\u4ee5\u8fd9\u65f6\u770b\u77f3\u7fe0\u51e4\u5728\u53f0\u4e0a\u7ed5\u6765\u620f\u53bb\u4e00\u62db\u4e00 \u5f0f\u90fd\u770b\u5f97\u5341\u5206\u6e05\u695a\u3002\u53f0\u4e0a\u7684\u97e9\u5927\u6d77\u5374\u5df2\u773c\u82b1\u7f2d\u4e71\uff0c\u4f46\u89c9\u56db\u9762\u516b\u65b9\u90fd\u662f\u77f3\u7fe0\u51e4\u4fcf\u751f\u751f\u7684\u5f71\u5b50\u3002 \u4e91\u857e\u770b\u4e86\u4e00\u9635\uff0c\u5fc3\u4e2d\u6697\u7b11\uff0c\u53ea\u89c1\u97e9\u5927\u6d77\u8ddf\u7740\u77f3\u7fe0\u51e4\u56e2\u56e2\u4e71\u8f6c\uff0c\u8d8a\u6253\u8d8a\u7cdf\uff0c\u5374\u5c3d\u81ea\u652f\u6491\uff0c\u4e0d\u80af \u505c\u624b\u3002\u97e9\u5c9b\u4e3b\u76b1\u7709\u559d\u9053\uff1a\u201c\u7b28\u5c0f\u5b50\uff0c\u4f60\u4e0d\u662f\u77f3\u59d1\u5a18\u7684\u5bf9\u624b\uff0c\u8fd8\u4e0d\u5feb\u9000\u4e0b\u6765\u4e48\uff1f\u201d \u3000\u3000\u97e9\u5c9b\u4e3b\u8fd9\u4e48\u4e00\u56b7\uff0c\u77f3\u7fe0\u51e4\u7684\u8eab\u5f62\u7565\u7565\u8fdf\u7f13\u4e0b\u6765\uff0c\u97e9\u5927\u6d77\u7a81\u7136\u8dc3\u8d77\uff0c\u6251\u817e\u817e\u4e09\u62f3\u8fde\u53d1\u3002\u4e91 \u857e\u6697\u7b11\u9053\uff1a\u201c\u771f\u662f\u4e2a\u4e0d\u77e5\u8fdb\u9000\u7684\u9c81\u83bd\u7b28\u866b\uff0c\u522b\u4eba\u8ba9\u4ed6\u4ed6\u8fd8\u4e0d\u77e5\u9053\u3002\u201d\u53ea\u89c1\u77f3\u7fe0\u51e4\u5fae\u5fae\u4e00\u95ea\uff0c \u5de6\u8098\u4e00\u649e\uff0c\u97e9\u5927\u6d77\u6c34\u725b\u822c\u7684\u8eab\u8eaf\uff0c\u6251\u901a\u8dcc\u5012\u3002\u77f3\u82f1\u8d76\u5fd9\u6276\u8d77\u9053\uff1a\u201c\u51e4\u513f\uff0c\u4f60\u8fd8\u4e0d\u4e0a\u6765\u8d54\u7f6a \u4e48\uff1f\u201d\u97e9\u5927\u6d77\u9053\uff1a\u201c\u6ca1\u4f24\u7740\uff0c\u77f3\u59d1\u5a18\u4f60\u771f\u597d\u529f\u592b\uff0c\u6211\u3001\u6211\u2026\u2026\u201d\u4ed6\u662f\u4e2a\u6123\u5c0f\u5b50\uff0c\u201c\u6211\u53ef\u4e0d\u6562 \u5a36\u4f60\u505a\u8001\u5a46\u5566\uff01\u201d\u51e0\u4e4e\u8bf4\u4e86\u51fa\u6765\u3002\u4ed6\u7684\u7236\u4eb2\u53cc\u773c\u4e00\u77aa\uff0c\u628a\u4ed6\u5413\u5f97\u4e0d\u6562\u4f5c\u58f0\u3002 \u3000\u3000\u6797\u5e84\u4e3b\u7684\u513f\u5b50\u6797\u9053\u5b89\u8f7b\u6447\u6298\u6247\uff0c\u7f13\u7f13\u8d70\u51fa\uff0c\u9634\u58f0\u602a\u6c14\u9053\uff1a\u201c\u6211\u4e5f\u9886\u6559\u51e0\u62db\uff0c\u4e16\u59b9\u4f60\u53ef\u5f97 \u8ba9\u7740\u70b9\u554a\uff01\u201d\u4ed6\u751f\u5f97\u6e29\u6587\u5c14\u96c5\uff0c\u8bf4\u8bdd\u4e5f\u4f3c\u5973\u5b50\uff0c\u70b9\u7a74\u7684\u529f\u592b\u5374\u662f\u53c8\u51c6\u53c8\u72e0\u3002\u53ea\u89c1\u4ed6\u6298\u6247\u4e00 \u5408\uff0c\u6247\u5934\u4e00\u6307\uff0c\u4fbf\u5f84\u5954\u77f3\u7fe0\u51e4\u80c1\u4e0b\u7684\u8f6f\u9ebb\u7a74\uff0c\u77f3\u7fe0\u51e4\u53c8\u4f7f\u51fa\u516b\u5366\u6e38\u8eab\u638c\u7684\u8eab\u6cd5\uff0c\u7ed5\u7740\u4ed6\u8f6c\uff0c \u6797\u9053\u5b89\u5b88\u7740\u95e8\u6237\uff0c\u5e76\u4e0d\u968f\u5979\u79fb\u52a8\uff0c\u51b7\u4e0d\u9632\u5c31\u662f\u4e00\u62db\uff0c\u6247\u5934\u6240\u6307\uff0c\u5168\u662f\u4eba\u8eab\u4e0a\u7684\u9ebb\u7a74\u548c\u6655\u7a74\u3002 \u4e00\u53cc\u8272\u8ff7\u8ff7\u7684\u773c\u775b\u76ef\u77f3\u7fe0\u51e4\u7684\u8eab\u5f62\u3002 \u3000\u3000\u77f3\u7fe0\u51e4\u5fc3\u5934\u70e6\u8e81\uff0c\u6697\u4e2d\u60f3\u9053\uff1a\u201c\u770b\u8fd9\u5bb6\u4f19\u7684\u6a21\u6837\uff0c\u4e0d\u662f\u4e2a\u6b63\u7ecf\u7684\u4eba\u513f\uff0c\u8fd9\u53cc\u773c\u775b\u5c31\u53eb\u4eba \u8ba8\u538c\u3002\u53ef\u4e0d\u8981\u7ed9\u4ed6\u5f97\u4e86\u624b\u53bb\u3002\u201d\u77f3\u7fe0\u51e4\u5b9e\u662f\u4e0d\u613f\u5ac1\u4ed6\uff0c\u638c\u6cd5\u8d8a\u6765\u8d8a\u7d27\uff0c\u53ef\u662f\u6797\u9053\u5b89\u7684\u6b66\u529f\u59d4 \u5b9e\u4e0d\u5f31\uff0c\u70b9\u7a74\u7684\u529f\u592b\u4e5f\u987b\u5c0f\u5fc3\u9632\u5907\uff0c\u6253\u4e86\u4e94\u4e03\u5341\u62db\uff0c\u77f3\u7fe0\u51e4\u6beb\u65e0\u529e\u6cd5\u3002\u6797\u9053\u5b89\u5341\u62ff\u4e5d\u7a33\uff0c\u5fc3 \u9053\uff1a\u201c\u770b\u4f60\u8fd9\u5973\u6d41\u4e4b\u8f88\u6709\u591a\u5c11\u6c14\u529b\u548c\u6211\u5bf9\u8017\uff1f\u201d\u6298\u6247\u4e00\u7f29\uff0c\u53ea\u5f85\u5979\u75b2\u5377\u65e0\u795e\uff0c\u4fbf\u8981\u5c06\u5979\u70b9 \u5012\u3002 \u3000\u3000\u9163\u6597\u4e2d\u77f3\u7fe0\u51e4\u6b3a\u8eab\u76f4\u8fdb\uff0c\u5ffd\u7136\u6a31\u5507\u4e00\u542f\uff0c\u5411\u4ed6\u5fae\u5fae\u4e00\u7b11\uff0c\u9f7f\u5982\u7f16\u8d1d\uff0c\u68a8\u7a9d\u9690\u73b0\uff0c\u6797\u9053\u5b89 \u5fc3\u795e\u4e00\u8361\uff0c\u60f3\u9053\uff1a\u201c\u6211\u8fd9\u6837\u7684\u4eba\u54c1\u6b66\u529f\uff0c\u81ea\u7136\u662f\u6559\u5979\u5fc3\u6298\u7684\u4e86\u3002\u201d\u6ee1\u5fc3\u4ee5\u4e3a\u5979\u4e00\u7b11\u4e4b\u540e\uff0c\u4fbf \u8981\u8ba4\u8f93\uff0c\u6298\u6247\u4e00\u5c01\uff0c\u4e5f\u62a5\u4e86\u4e00\u7b11\uff0c\u4e0d\u6599\u77f3\u7fe0\u51e4\u7a81\u7136\u7b11\u9053\uff1a\u201c\u5f97\u7f6a\u4e86\uff01\u201d\u62e2\u6307\u4e00\u62c2\uff0c\u5728\u4ed6\u592a\u9633 \u7a74\u4e0a\u8f7b\u8f7b\u4e00\u6309\uff0c\u6797\u9053\u5b89\u5927\u53eb\u4e00\u58f0\uff0c\u773c\u524d\u91d1\u661f\u4e71\u5192\uff0c\u7adf\u7136\u6655\u5012\u53f0\u4e0a\u3002 \u3000\u3000\u6797\u5e84\u4e3b\u773c\u770b\u7740\u513f\u5b50\u529f\u8d25\u5782\u6210\uff0c\u597d\u751f\u607c\u6012\uff0c\u5374\u662f\u4e0d\u6562\u53d1\u4f5c\u51fa\u6765\u3002\u77f3\u82f1\u5728\u6797\u9053\u5b89\u8111\u540e\u4e00\u634f\uff0c \u9053\uff1a\u201c\u6ca1\u4e8b\uff0c\u6ca1\u4e8b\uff01\u51e4\u513f\uff0c\u4f60\u600e\u4e48\u51fa\u624b\u4e0d\u77e5\u8f7b\u91cd\uff0c\u4e13\u6253\u4eba\u5bb6\u7684\u8981\u5bb3\uff01\u201d\u6797\u9053\u5b89\u9192\u4e86\u8fc7\u6765\uff0c\u51b7 \u51b7\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u77f3\u59d1\u5a18\uff0c\u9886\u6559\u5566\uff01\u201d\u548c\u7236\u4eb2\u5e76\u80a9\u7eb5\u8d77\uff0c\u4e00\u8dc3\u8df3\u4e0b\u64c2\u53f0\u3002 \u3000\u3000\u77f3\u82f1\u6447\u4e86\u6447\u5934\uff0c\u53c8\u62c8\u987b\u7b11\u9053\uff1a\u201c\u5c0f\u5973\u4fa5\u5e78\u8fde\u80dc\u4e24\u573a\uff0c\u8fd9\u56de\u53ef\u8981\u8bf7\u65e0\u5fcc\u4e16\u5144\u6559\u8bad\u6559\u8bad\u5979 \u4e86\uff0c\u53ef\u522b\u8ba9\u5979\u592a\u5f97\u610f\u554a\uff01\u201d\u65e0\u5fcc\u4e43\u662f\u6c99\u5be8\u4e3b\u513f\u5b50\u7684\u540d\u5b57\uff0c\u5728\u4e09\u4eba\u4e4b\u4e2d\uff0c\u77f3\u82f1\u5bf9\u4ed6\u6700\u4e3a\u8d4f\u8bc6\uff0c \u5c31\u662f\u5acc\u4ed6\u624b\u5e95\u592a\u8fc7\u72e0\u8fa3\uff0c\u5728\u7eff\u6797\u4e4b\u4e2d\uff0c\u6709\u5a01\u540d\u800c\u65e0\u5a01\u671b\u3002\u4f46\u77f3\u82f1\u5fc3\u60f3\u4e16\u4e0a\u96be\u6c42\u5341\u5168\u5341\u7f8e\u4e4b \u4eba\uff0c\u6709\u8fd9\u6837\u4e00\u4e2a\u5973\u5a7f\uff0c\u4e5f\u7b97\u662f\u4e0d\u9519\u4e86\u3002 \u3000\u3000\u77f3\u82f1\u6df1\u77e5\u6c99\u65e0\u5fcc\u6b66\u529f\u5728\u81ea\u5df1\u5973\u513f\u4e4b\u4e0a\uff0c\u4ee5\u4e3a\u4ed6\u5fc5\u6b23\u7136\u52a8\u624b\u7684\uff0c\u4e0d\u6599\u4ed6\u7709\u5934\u4e00\u76b1\uff0c\u5ffd\u7136\u82e6 \u7b11\u8bf4\u9053\uff1a\u201c\u4e0d\u5fc5\u6bd4\u4e86\uff0c\u82e5\u7136\u4eca\u65e5\u8981\u6bd4\uff0c\u90a3\u5c0f\u4f84\u5012\u5c31\u5e72\u8106\u8ba4\u8f93\u4e86\uff01\u201d \u3000\u3000\u6b64\u8a00\u4e00\u51fa\uff0c\u5ea7\u4e0a\u7fa4\u8c6a\uff0c\u65e0\u4e0d\u6115\u7136\u3002\u77f3\u82f1\u602b\u7136\u4e0d\u60a6\uff0c\u8bf4\u9053\uff1a\u201c\u6c99\u8d24\u4f84\u6b64\u8bdd\u600e\u8bf4\uff0c\u83ab\u975e\u5c0f\u5973 \u4e0d\u582a\u627f\u6559\u4e48\uff1f\u201d\u6c99\u65e0\u5fcc\u53c8\u662f\u4e00\u58f0\u82e6\u7b11\uff0c\u7f13\u7f13\u5c06\u8863\u8896\u5377\u8d77\uff0c\u53ea\u89c1\u53f3\u81c2\u4e0a\u4e00\u9053\u4f24\u75d5\uff0c\u76f4\u5230\u624b\u8155\uff0c \u4f24\u75d5\u6df1\u5904\uff0c\u9aa8\u5934\u90fd\u9732\u4e86\u51fa\u6765\u3002\u77f3\u82f1\u5403\u4e86\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u8d24\u4f84\u662f\u600e\u4e48\u6302\u5f69\u7684\uff1f\u201d\u6c99\u65e0\u5fcc\u5411\u53f0\u4e0b\u626b \u4e86\u4e00\u773c\uff0c\u9053\uff1a\u201c\u6628\u65e5\u5728\u9634\u6c9f\u91cc\u7ffb\u4e86\u8239\u5566\uff0c\u54fc\uff0c\u54fc\uff0c\u7740\u4e86\u4e00\u4e2a\u5c0f\u8d3c\u7684\u9053\u513f\u3002\u201d\u4ed6\u7684\u7236\u4eb2\u6c99\u5be8\u4e3b \u6c99\u6d9b\u63a5\u53e3\u8bf4\u9053\uff1a\u201c\u6628\u65e5\u6211\u53eb\u80e1\u8001\u4e8c\u548c\u4ed6\u53bb\u8ffd\u8d76\u4e00\u4e2a\u4ece\u5317\u8fb9\u6765\u7684\u7f8a\u726f\uff08\u76d7\u515a\u672f\u8bed\uff0c\u5373\u6253\u52ab\u7684\u5bf9 \u8c61\uff09\uff0c\u5374\u4e0d\u6599\u4ed6\u6697\u4e2d\u8bf7\u4e86\u4e00\u4e2a\u4fdd\u9556\uff0c\u5341\u5206\u624e\u624b\uff0c\u65e0\u5fcc\u7ed9\u4ed6\u4f24\u4e86\u3002\u201d\u77f3\u82f1\u66f4\u662f\u5403\u60ca\uff0c\u90a3\u80e1\u8001\u4e8c \u4e43\u662f\u6c99\u6d9b\u7684\u526f\u5be8\u4e3b\uff0c\u6b66\u529f\u5c1a\u5728\u6c99\u65e0\u5fcc\u4e4b\u4e0a\uff0c\u4ee5\u4e8c\u4eba\u4e4b\u529b\uff0c\u7adf\u7136\u7ed9\u4e00\u4e2a\u4fdd\u9556\u7684\u6740\u8d25\uff0c\u5b9e\u662f\u96be\u4ee5 \u601d\u8bae\u3002\u6c99\u6d9b\u5ffd\u5730\u51b7\u68ee\u68ee\u8bf4\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4f60\u770b\u8be5\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u77f3\u82f1\u6014\u4e86\u4e00\u6014\uff0c\u5ffd\u5730\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u90a3\u4fdd\u9556\u7684\u5012\u4e5f\u662f\u4e2a\u80fd\u4eba\u3002\u53ea\u4e0d\u77e5\u4ed6\u662f\u4f55\u6765 \u5386\uff1f\u73b0\u5728\u4f55\u65b9\uff1f\u6211\u4ea6\u60f3\u4f1a\u4e00\u4f1a\u4ed6\u4e0e\u4f60\u4eec\u4e24\u5bb6\u548c\u89e3\u548c\u89e3\u3002\u201d\u6c99\u65e0\u5fcc\u9762\u8272\u4e00\u53d8\uff0c\u9053\uff1a\u201c\u5c0f\u4f84\u51fa\u9053 \u4ee5\u6765\uff0c\u4ece\u672a\u5982\u6b64\u53d7\u8fb1\uff0c\u6b64\u4e8b\u548c\u89e3\u4e0d\u4e86\u3002\u201d\u5ffd\u7684\u5411\u53f0\u4e0b\u4e00\u6307\uff0c\u9053\uff1a\u201c\u8fd9\u25a1\u5403\u4e86\u72fc\u5fc3\u8c79\u80c6\uff0c\u80c6\u5b50 \u53ef\u5927\u7740\u54e9\uff0c\u4ed6\u5c31\u5728\u8fd9\u513f\u3002\u201d\u6c99\u6d9b\u5927\u53eb\u4e00\u58f0\uff0c\u559d\u9053\uff1a\u201c\u6211\u6c99\u5bb6\u7236\u5b50\u8fd8\u8981\u4f1a\u4f1a\u4f60\u8fd9\u4f4d\u80fd\u4eba\uff0c\u4f60\u5f80 \u54ea\u91cc\u8d70\uff01\u201d \u3000\u3000\u64c2\u53f0\u4e0a\u4e24\u6761\u4eba\u5f71\u500f\u5730\u6251\u4e0b\uff0c\u8d3a\u5bff\u7684\u5ba2\u4eba\u4e00\u9635\u5927\u4e71\uff0c\u5429\u5429\u53eb\u9053\uff1a\u201c\u70b9\u5b50\u5728\u54ea\u91cc\uff1f\u201d\u8d3a\u5ba2\u4e2d \u51e0\u4e4e\u6709\u4e00\u534a\u662f\u6c99\u5be8\u4e3b\u7684\u670b\u53cb\uff0c\u89c1\u6b64\u60c5\u5f62\uff0c\u6025\u6765\u76f8\u52a9\u3002\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u53ea\u89c1\u6c99\u6d9b\u4e00\u4e2a\u7bad\u6b65\uff0c \u5954\u5230\u4e91\u857e\u9762\u524d\uff0c\u4e94\u6307\u5982\u94a9\uff0c\u6251\u5730\u5f53\u5934\u4fbf\u6293\u3002\u4e91\u857e\u8eab\u6cd5\u4f55\u7b49\u5feb\u6377\uff0c\u4e00\u95ea\u95ea\u5f00\uff0c\u6c99\u65e0\u5fcc\u4e5f\u8ddf\u8e2a\u8ffd \u5230\uff0c\u5de6\u624b\u4e00\u62ac\uff0c\u4e00\u67c4\u5315\u9996\u76f4\u63d2\u8fc7\u6765\u4e86\u3002\u4e91\u857e\u811a\u8ddf\u4e00\u65cb\uff0c\u53cd\u624b\u4e00\u62c2\uff0c\u7b11\u9053\uff1a\u201c\u54c8\uff0c\u539f\u6765\u4f60\u5c31\u662f \u6628\u665a\u7684\u8499\u9762\u5c0f\u8d3c\uff01\u201d\u53ea\u542c\u5f97\u5f53\u25a1\u4e00\u58f0\uff0c\u6c99\u65e0\u5fcc\u7684\u5315\u9996\u5df2\u7ed9\u62c2\u843d\u3002 \u3000\u3000\u4e91\u857e\u4e00\u4e2a\u8f6c\u8eab\uff0c\u8098\u649e\u811a\u8e22\uff0c\u6253\u7ffb\u4e24\u4e2a\u5954\u6765\u52a9\u62f3\u7684\u4eba\uff0c\u4e00\u8dc3\u8df3\u8fc7\u4e00\u5f20\u516b\u4ed9\u684c\u5b50\uff0c\u6c99\u6d9b\u62d4\u51fa \u8170\u5200\uff0c\u8ffd\u8fc7\u53bb\u4fbf\u780d\uff0c\u4e91\u857e\u53eb\u9053\uff1a\u201c\u4e0d\u8981\u8138\uff0c\u8981\u501a\u591a\u4e3a\u80dc\u4e48\uff1f\u201d\u5c06\u684c\u5b50\u4e00\u6380\uff0c\u7897\u789f\u7eb7\u98de\uff0c\u4e52\u4e52 \u4e53\u4e53\uff0c\u4e00\u9635\u4e71\u54cd\uff0c\u6c99\u6d9b\u95ea\u8eab\u4e0d\u8fed\uff0c\u7ed9\u9152\u996d\u83dc\u6d12\u6e85\u4e86\u4e00\u8eab\uff0c\u8eab\u4e0a\u6c64\u6c34\u6dcb\u6f13\uff0c\u8840\u8109\u507e\u5f20\uff0c\u55d6\u55d6\u4e24 \u5200\uff0c\u5200\u6cd5\u654f\u6377\u4e4b\u6781\uff0c\u4e91\u857e\u6025\u5fd9\u62d4\u51fa\u5b9d\u5251\uff0c\u8fce\u9762\u4e00\u67b6\uff0c\u6c99\u6d9b\u4e00\u4e2a\u77ee\u8eab\u65a9\u9a6c\u5200\u52bf\uff0c\u5411\u4e0b\u622a\u65a9\u4e91\u857e \u7684\u53cc\u8db3\u3002\u4e91\u857e\u6012\u9053\uff1a\u201c\u597d\u72e0\u7684\u5f3a\u76d7\uff01\u201d\u8eab\u5f62\u4e00\u8d77\u4e00\u4e2a\u201c\u71d5\u5b50\u659c\u98de\u201d\u4e4b\u52bf\uff0c\u5728\u5200\u5149\u95ea\u95ea\u4e4b\u4e2d\u63a0 \u8eab\u98de\u8fc7\uff0c\u9752\u950b\u4e00\u6307\uff0c\u5f53\u80f8\u4fbf\u6233\uff0c\u5251\u52bf\u6bd4\u5200\u52bf\u66f4\u72e0\u66f4\u75be\uff0c\u6c99\u6d9b\u5413\u5f97\u6025\u5fd9\u4f4e\u5934\uff0c\u731b\u542c\u5f97\u53c8\u662f\u5f53\u25a1 \u4e00\u54cd\uff0c\u8170\u5200\u7adf\u88ab\u4e91\u857e\u7684\u5b9d\u5251\u524a\u4e3a\u4e24\u6bb5\u3002 \u3000\u3000\u8fd9\u8fd8\u662f\u4e91\u857e\u4e0d\u60f3\u4f24\u4eba\uff0c\u6240\u4ee5\u4ec5\u4ec5\u5c06\u4ed6\u7684\u5175\u5668\u524a\u65ad\u3002\u6c99\u6d9b\u5374\u4e0d\u627f\u60c5\uff0c\u817e\u7a7a\u6251\u8d77\uff0c\u4f38\u624b\u53c8 \u6293\uff0c\u4e91\u857e\u5251\u950b\u4e00\u8f6c\uff0c\u4e00\u62db\u201c\u6597\u8f6c\u661f\u6a2a\u201d\u6a2a\u524a\u8fc7\u53bb\uff0c\u970e\u65f6\u95f4\u6362\u4e86\u6570\u62db\uff0c\u8feb\u5207\u4e4b\u95f4\uff0c\u4e91\u857e\u7adf\u672a\u80fd \u5c06\u4ed6\u903c\u9000\u3002\u53c8\u6709\u51e0\u4eba\u4e0a\u524d\u52a9\u62f3\uff0c\u4e91\u857e\u5251\u6cd5\u65bd\u5c55\u4e0d\u5f00\uff0c\u6c99\u6d9b\u5927\u559d\u4e00\u58f0\u624b\u638c\u4e00\u7ffb\uff0c\u5f53\u5934\u5288\u4e0b\uff01 \u3000\u3000\u4e91\u857e\u773c\u775b\u4e00\u77a5\uff0c\u53ea\u89c1\u6c99\u6d9b\u7684\u624b\u638c\uff0c\u638c\u5fc3\u6bb7\u7ea2\u5982\u8840\uff0c\u77e5\u4ed6\u7ec3\u6709\u6bd2\u7802\u638c\u7684\u529f\u592b\uff0c\u8fd9\u4e00\u638c\u4e07\u4e07 \u4e0d\u80fd\u7ed9\u4ed6\u6253\u4e2d\uff0c\u6025\u5fd9\u95f4\u4f38\u624b\u4e00\u62c9\u786c\u5c06\u4e00\u4e2a\u52a9\u62f3\u7684\u62c9\u4e86\u8fc7\u6765\uff0c\u5411\u524d\u4e00\u6321\uff0c\u6c99\u6d9b\u614c\u5fd9\u7f29\u624b\uff0c\u4e91\u857e \u6251\u7684\u53c8\u4ece\u7f3a\u53e3\u8df3\u51fa\uff0c\u8dc3\u8fc7\u4e00\u5f20\u684c\u5b50\uff0c\u62ff\u8d77\u7897\u789f\uff0c\u8fce\u5934\u4e71\u6254\uff0c\u5c06\u52a9\u62f3\u7684\u6253\u5f97\u9762\u9752\u5507\u80bf\uff0c\u6c64\u6c34\u6dcb \u6f13\u3002\u6b63\u81ea\u95f9\u5f97\u4e0d\u53ef\u5f00\u4ea4\uff0c\u53ea\u542c\u5f97\u77e5\u5ba2\u7684\u7eb7\u7eb7\u53eb\u9053\uff1a\u201c\u4e0d\u6210\u8bdd\uff0c\u4e0d\u6210\u8bdd\u5566\uff01\u201d \u3000\u3000\u6c99\u65e0\u5fcc\u62ff\u8d77\u4e00\u5f20\u6905\u5b50\uff0c\u53c8\u62a2\u4e0a\u524d\u6765\uff0c\u72e0\u72e0\u7838\u4e0b\uff0c\u4e91\u857e\u970d\u5730\u4e00\u4e2a\u201c\u51e4\u70b9\u5934\u201d\uff0c\u4e00\u5251\u5288\u53bb\uff0c \u5c06\u6905\u5b50\u4e5f\u5288\u6210\u4e24\u8fb9\u3002\u6c99\u6d9b\u53cc\u624b\u4e00\u9519\uff0c\u547c\u547c\u5288\u6765\uff0c\u4e91\u857e\u66f4\u4e0d\u6362\u62db\uff0c\u5251\u67c4\u4e00\u6296\uff0c\u8d81\u52bf\u523a\u51fa\uff0c\u5ffd\u5730 \u4eba\u5f71\u6251\u9762\u800c\u6765\u5f53\u4e2d\u4e00\u7acb\uff0c\u53cc\u638c\u659c\u5206\uff0c\u4e91\u857e\u3001\u6c99\u6d9b\u5404\u81ea\u5012\u8dc3\u4e09\u6b65\uff0c\u53ea\u542c\u5f97\u77f3\u82f1\u5927\u53eb\u9053\uff1a\u201c\u6c99\u5927 \u54e5\u7ed9\u5c0f\u5f1f\u4e00\u70b9\u8584\u9762\uff0c\u8fd9\u4f4d\u5c0f\u54e5\u4e5f\u8bf7\u4f4f\u624b\u3002\u201d \u3000\u3000\u6c99\u6d9b\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4f60\u66ff\u6211\u4f5c\u4e3b\u3002\u54b1\u4eec\u7236\u5b50\u7684\u9762\u5b50\u4e5f\u5168\u9760\u4f60\u4e00\u53e5\u8bdd\u5566\u3002\u201d\u77f3\u82f1\u770b\u4e86\u4e91\u857e\u4e00 \u773c\uff0c\u5fc3\u9053\uff1a\u201c\u5929\u4e0b\u95f4\u7adf\u6709\u5982\u6b64\u7f8e\u8c8c\u7684\u7537\u5b50\uff0c\u82e5\u975e\u4eb2\u773c\u89c1\u4ed6\u672c\u9886\uff0c\u53ef\u771f\u4e0d\u6562\u76f8\u4fe1\u4ed6\u80fd\u628a\u6c99\u5bb6\u7236 \u5b50\u6253\u5f97\u4e00\u8d25\u6d82\u5730\u3002\u201d\u5fc3\u4e0b\u597d\u751f\u8e0c\u8e87\u3002\u4e91\u857e\u9053\uff1a\u201c\u77f3\u5e84\u4e3b\uff0c\u6211\u5f97\u7f6a\u4f60\u7684\u8d35\u5ba2\u5566\uff0c\u4eca\u65e5\u6211\u767b\u95e8\u62dc \u5bff\uff0c\u53ef\u4e0d\u6562\u548c\u4f60\u52a8\u624b\uff0c\u8981\u6740\u8981\u5250\uff0c\u968f\u4f60\u5904\u7f6e\u3002\u201d\u6309\u6c5f\u6e56\u4e0a\u7684\u89c4\u77e9\uff0c\u4e91\u857e\u5230\u6b64\u62dc\u5bff\uff0c\u4e5f\u4fbf\u662f\u77f3 \u82f1\u7684\u5ba2\u4eba\uff0c\u6709\u5929\u5927\u7684\u4e8b\u60c5\uff0c\u77f3\u82f1\u4e5f\u8be5\u62c5\u5f85\u3002\u6c99\u6d9b\u542c\u4e86\uff0c\u6697\u6697\u9a82\u58f0\u597d\u4e2a\u4f36\u4fd0\u7684\u5c0f\u8d3c\u3002\u53cc\u773c\u4e00 \u7ffb\uff0c\u5ffd\u5730\u95ee\u9053\uff1a\u201c\u77f3\u5927\u54e5\uff0c\u6562\u95ee\u8fd9\u4f4d\u5c0f\u54e5\u9ad8\u59d3\u5927\u540d\uff0c\u5e08\u7236\u662f\u54ea\u4e00\u4f4d\uff1f\u201d\u77f3\u82f1\u4e00\u6115\uff0c\u9053\uff1a\u201c\u6211 \u600e\u4e48\u77e5\u9053\u5462\uff1f\u201d\u6c99\u6d9b\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u539f\u6765\u77f3\u5927\u54e5\u5e76\u4e0d\u4e0e\u4ed6\u8ba4\u8bc6\u3002\u5728\u5ea7\u7684\u5404\u4f4d\u5927\u54e5\uff0c\u53ef\u6709\u8c01 \u8ba4\u8bc6\u4ed6\u5417\uff1f\u201d\u8fd9\u65f6\u6ee1\u56ed\u8d3a\u5ba2\u90fd\u56f4\u4f4f\u4e91\u857e\uff0c\u6ca1\u4e00\u4eba\u4e0e\u4ed6\u76f8\u8bc6\u3002\u6c99\u6d9b\u51b7\u7b11\u9053\uff1a\u201c\u5927\u54e5\u53ef\u6e05\u695a\u4e86\uff0c \u8fd9\u5c0f\u5b50\u662f\u5192\u5145\u8d3a\u5ba2\uff0c\u540d\u4e3a\u62dc\u5bff\uff0c\u5b9e\u662f\u907f\u96be\u3002\u8ba9\u4ed6\u767d\u98df\u4e8b\u5c0f\uff0c\u8bf4\u51fa\u53bb\u53ef\u4e0d\u635f\u4e86\u54b1\u4eec\u5c71\u897f\u9ed1\u9053\u4e0a \u7684\u989c\u9762\u4e48\uff1f\u201d \u3000\u3000\u77f3\u82f1\u597d\u751f\u4e0d\u60a6\uff0c\u9053\uff1a\u201c\u4f9d\u5927\u54e5\u4e4b\u610f\u5982\u4f55\uff1f\u201d\u6c99\u6d9b\u9053\uff1a\u201c\u628a\u4ed6\u6240\u4fdd\u7684\u90a3\u4e2a\u4e3b\u513f\u7684\u7167\u591c\u72ee\u5b50 \u9a6c\u4e0e\u73e0\u5b9d\u4ea4\u51fa\u6765\uff0c\u518d\u8ba9\u65e0\u5fcc\u7167\u6837\u5728\u4ed6\u624b\u81c2\u4e0a\u62c9\u4e0a\u4e00\u5200\uff0c\u90a3\u5c31\u4e07\u4e8b\u4f5c\u4e86\u3002\u201d\u4e91\u857e\u542c\u4ed6\u8bf4\u51fa\u201c\u7167 \u591c\u72ee\u5b50\u9a6c\u201d\u7684\u540d\u53f7\uff0c\u5fc3\u9053\uff1a\u201c\u4e45\u95fb\u7167\u591c\u72ee\u5b50\u9a6c\u662f\u8499\u53e4\u6700\u7f55\u89c1\u7684\u540d\u9a6c\uff0c\u4ee5\u524d\u4e43\u662f\u8d21\u7269\uff0c\u7eb5\u51fa\u5343 \u4e24\u9ec4\u91d1\uff0c\u4e5f\u96be\u6c42\u5f97\u3002\u60f3\u4e0d\u5230\u90a3\u4e66\u751f\u7684\u767d\u9a6c\uff0c\u7adf\u7136\u5c31\u662f\u7167\u591c\u72ee\u5b50\u3002\u201d\u8111\u6d77\u4e2d\u4e0d\u6cdb\u51fa\u90a3\u4e66\u751f\u4f3c\u7b11 \u975e\u7b11\u4e00\u526f\u61d2\u6d0b\u6d0b\u7684\u795e\u6c14\u6765\uff0c\u60f3\u8d77\u65e5\u524d\u79cd\u79cd\u4e4b\u4e8b\uff0c\u5bf9\u90a3\u4e66\u751f\u7684\u8eab\u4efd\u66f4\u662f\u6000\u7591\u3002 \u3000\u3000\u77f3\u82f1\u89c1\u4e91\u857e\u4e00\u526f\u51fa\u795e\u7684\u6837\u5b50\uff0c\u53ea\u9053\u4ed6\u5413\u5f97\u5446\u4e86\uff0c\u671d\u4ed6\u80a9\u8180\u8f7b\u8f7b\u4e00\u62cd\uff0c\u9053\uff1a\u201c\u8fd9\u4f4d\u5c0f\u54e5\uff0c \u4f60\u53c8\u6709\u4f55\u8bdd\u8bf4\uff1f\u201d\u4e91\u857e\u9053\uff1a\u201c\u4ed6\u52ab\u4eba\uff0c\u6211\u6551\u4eba\uff0c\u8fd9\u6709\u4ec0\u4e48\u597d\u8bf4\u7684\uff1f\u4ed6\u4eec\u82e5\u4e0d\u670d\u6c14\uff0c\u5c31\u8bf7\u4e0a\u6765 \u597d\u4e86\uff0c\u53ea\u8981\u4ed6\u4eec\u7236\u5b50\u80dc\u4e86\uff0c\u83ab\u8bf4\u53ea\u662f\u5728\u81c2\u4e0a\u62c9\u4e86\u4e00\u5200\uff0c\u5c31\u662f\u4e09\u5200\u516d\u6d1e\uff0c\u6211\u4e5f\u9003\u8dd1\u4e0d\u4e86\u3002\u201d\u77f3 \u82f1\u9762\u8272\u4e00\u6c89\uff0c\u5fc3\u9053\uff1a\u201c\u539f\u6765\u8fd9\u5c0f\u5b50\u8fd8\u662f\u521d\u51fa\u9053\u7684\u96cf\u513f\uff0c\u5c82\u4e0d\u77e5\u5230\u4e86\u8fd9\u513f\uff0c\u6211\u5c31\u662f\u4e8b\u4e3b\uff0c\u6211\u65e2 \u8bf4\u660e\u8981\u628a\u4e8b\u60c5\u6401\u5230\u80a9\u4e0a\uff0c\u4f60\u5411\u4ed6\u4eec\u6311\u6218\uff0c\u53ef\u4e0d\u5c31\u662f\u5411\u6211\u6311\u6218\u4e48\uff1f\u201d\u679c\u7136\u6c99\u6d9b\u542c\u4e86\uff0c\u54c8\u54c8\u5927 \u7b11\u3002 \u3000\u3000\u4e91\u857e\u773c\u775b\u4e00\u77aa\uff0c\u9053\uff1a\u201c\u4f60\u72c2\u4ec0\u4e48\uff1f\u4f60\u7236\u5b50\u5c3d\u7ba1\u4e0a\u6765\uff0c\u770b\u4ffa\u53ef\u66fe\u6015\u4f60\uff1f\u201d\u4e91\u857e\u8bb0\u4f4f\u5468\u5c71\u6c11 \u6240\u6559\u8fc7\u5979\u7684\u6c5f\u6e56\u89c4\u77e9\uff0c\u82e5\u9047\u4e0a\u5bf9\u65b9\u4eba\u591a\uff0c\u800c\u53c8\u662f\u6210\u540d\u4eba\u7269\u7684\u8bdd\uff0c\u90a3\u5c31\u5f97\u628a\u8bdd\u62ff\u4f4f\uff0c\u9080\u4ed6\u4eec\u5355 \u6253\u72ec\u6597\u3002\u4e91\u857e\u5fc3\u60f3\uff0c\u6c99\u5bb6\u7236\u5b50\u4e8c\u4eba\u4e5f\u4e0d\u662f\u5979\u7684\u5bf9\u624b\uff0c\u6240\u4ee5\u4e50\u5f97\u4e00\u9080\u5c31\u9080\u6597\u4ed6\u4eec\u7236\u5b50\u4e8c\u4eba\u3002\u5c82 \u77e5\u5468\u5c71\u6c11\u6240\u6559\u7684\u201c\u6c5f\u6e56\u5e38\u8bc6\u201d\uff0c\u53ea\u662f\u4e00\u822c\u60c5\u51b5\uff0c\u5e76\u4e0d\u9002\u5408\u4eca\u65e5\u4e4b\u7528\u3002\u53ea\u89c1\u6c99\u6d9b\u54c8\u54c8\u5927\u7b11\u4e4b \u540e\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u77f3\u5927\u54e5\uff0c\u4f60\u542c\u6e05\u695a\u4e86\uff1f\u8fd9\u5c0f\u5b50\u7684\u773c\u5185\u5c82\u6b62\u6ca1\u6709\u4ffa\u6c99\u5bb6\u7236\u5b50\uff0c\u4e5f\u6ca1\u6709\u4f60\u5927\u54e5 \u5566\uff01\u201d \u3000\u3000\u77f3\u82f1\u9762\u8272\u53c8\u662f\u4e00\u6c89\uff0c\u9053\uff1a\u201c\u4ffa\u81ea\u6709\u5429\u5490\u3002\u5582\uff0c\u8fd9\u4f4d\u5c0f\u54e5\uff0c\u4f60\u613f\u6bd4\u5251\u8fd8\u662f\u6bd4\u62f3\uff1f\u201d\u4e91\u857e \u9053\uff1a\u201c\u4ec0\u4e48\uff0c\u548c\u4f60\u6bd4\u5417\uff1f\u5e84\u4e3b\uff0c\u4f60\u7684\u8e51\u4e91\u5251\u5929\u4e0b\u95fb\u540d\uff0c\u5c0f\u8f88\u7109\u80fd\u4e0e\u4f60\u52a8\u624b\uff1f\u6211\u53ea\u662f\u8981\u548c\u4ed6\u4eec \u6bd4\u5212\u6bd4\u5212\uff01\u201d\u77f3\u82f1\u9661\u7136\u4e00\u559d\uff0c\u9053\uff1a\u201c\u4f4f\u53e3\uff01\u8c01\u8981\u5728\u6211\u8fd9\u513f\u52a8\u62f3\u5200\uff0c\u5c31\u5f97\u671d\u7740\u6211\u6765\uff01\u201d\u53cc\u773c\u4e00 \u626b\uff0c\u6b64\u8bdd\u660e\u91cc\u662f\u8bf4\u4e91\u857e\uff0c\u6697\u4e2d\u5374\u4e5f\u8bf4\u7740\u6c99\u5bb6\u7236\u5b50\u3002 \u3000\u3000\u4e91\u857e\u4e00\u6014\uff0c\u4e00\u65f6\u95f4\u4e0d\u77e5\u5982\u4f55\u5e94\u4ed8\u3002\u53ea\u542c\u77f3\u82f1\u53c8\u9053\uff1a\u201c\u4f60\u65e2\u7136\u6015\u6211\u7684\u8e51\u4e91\u5251\u6cd5\uff0c\u90a3\u4e48\u5c31\u6bd4 \u62f3\u6cd5\u597d\u4e86\u3002\u201d\u4e91\u857e\u9053\uff1a\u201c\u665a\u8f88\u4e0d\u6562\u3002\u201d\u77f3\u82f1\u9762\u8272\u4e00\u7aef\uff0c\u9053\uff1a\u201c\u4e0d\u6bd4\u4e0d\u6210\uff01\u4e0d\u8fc7\u5ff5\u4f60\u4e43\u662f\u5c0f \u8f88\uff0c\u8001\u592b\u4e5f\u4e0d\u5c51\u4e0e\u4f60\u52a8\u624b\u3002\u7fe0\u513f\uff0c\u4f60\u4e0e\u6211\u63a5\u4ed6\u51e0\u62db\uff01\u5c0f\u5b50\uff0c\u5feb\u5feb\u4e0a\u64c2\u53f0\u53bb\uff01\u201d \u3000\u3000\u77f3\u82f1\u8fd9\u4e00\u756a\u8bdd\uff0c\u5927\u51fa\u4f17\u4eba\u610f\u5916\u3002\u6c99\u5bb6\u7236\u5b50\uff0c\u66f4\u662f\u607c\u6012\uff0c\u9762\u8272\u9752\u91cc\u6cdb\u7ea2\u3002\u8981\u77e5\u77f3\u82f1\u4eca\u65e5\u8ba9 \u5973\u513f\u6446\u4e0b\u64c2\u53f0\uff0c\u867d\u672a\u8bf4\u660e\u7528\u610f\uff0c\u4f17\u4eba\u5374\u65e0\u4e0d\u77e5\u9053\u4ed6\u4e43\u662f\u501f\u6b64\u9009\u62e9\u4f73\u5a7f\u3002\u77f3\u82f1\u77a5\u4e86\u6c99\u5bb6\u7236\u5b50\u4fe9 \u4e00\u773c\uff0c\u5e76\u4e0d\u7406\u776c\u4ed6\u4eec\uff0c\u4ecd\u662f\u4e0d\u4f4f\u5730\u50ac\u4fc3\u4e91\u857e\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u4f60\u65e2\u6709\u80c6\u6562\u6df7\u8fdb\u9ed1\u77f3\u5e84\u6765\uff0c\u5c31\u8be5\u6709 \u80c6\u4e0a\u64c2\u53f0\u53bb\u663e\u663e\u8eab\u624b\uff0c\u5484\uff01\u4f60\u4e0d\u4e0a\u53bb\uff0c\u96be\u9053\u8981\u8001\u592b\u628a\u4f60\u629b\u4e0a\u53bb\u4e48\uff1f\u201d\u58f0\u8272\u4ff1\u5389\uff0c\u5484\u5484\u903c\u4eba\uff0c \u5468\u56f4\u8d3a\u5ba2\uff0c\u5374\u90fd\u6697\u6697\u5077\u7b11\uff0c\u8fd9\u6837\u505a\u4f5c\uff0c\u5206\u660e\u662f\u770b\u4e2d\u4e91\u857e\u4e86\u3002 \u3000\u3000\u4e91\u857e\u62ac\u5934\u4e00\u671b\uff0c\u53ea\u89c1\u7fe0\u51e4\u674f\u8138\u6cdb\u7ea2\uff0c\u773c\u5149\u51fa\u6b63\u5c04\u4e0b\u53f0\u6765\uff0c\u548c\u5979\u63a5\u4e2a\u6b63\u7740\u3002\u4e91\u857e\u5fc3\u5ff5\u4e00 \u52a8\uff0c\u5ffd\u7136\u4e00\u6574\u8863\u5e26\uff0c\u6168\u7136\u5730\u8bf4\u9053\uff1a\u201c\u606d\u656c\u4e0d\u5982\u4ece\u547d\uff0c\u90a3\u4e48\u6211\u5c31\u4e0a\u53bb\u63a5\u5c0f\u59d0\u51e0\u62db\u3002\u201d\u4f17\u4eba\u65e9\u5df2 \u8ba9\u5f00\u6761\u8def\uff0c\u4e91\u857e\u4ece\u5bb9\u8d70\u51fa\uff0c\u4e00\u8dc3\u4e0a\u53f0\u3002 \u3000\u3000\u77f3\u82f1\u5429\u5490\u4e86\u7ba1\u5bb6\u51e0\u53e5\uff0c\u508d\u7740\u6c99\u6d9b\u5750\u4e0b\uff0c\u62c8\u987b\u7b11\u9053\uff1a\u201c\u6c99\u5927\u54e5\uff0c\u54b1\u4eec\u591a\u5e74\u4ea4\u60c5\uff0c\u6211\u4e5f\u4e0d\u80fd \u53eb\u4f60\u5403\u4e8f\u3002\u201d\u6c99\u6d9b\u6c14\u5f97\u8bf4\u4e0d\u51fa\u8bdd\u6765\uff0c\u5374\u53c8\u4e0d\u80fd\u53d1\u4f5c\u3002\u77f3\u82f1\u5fae\u5fae\u4e00\u7b11\uff0c\u53c8\u9053\uff1a\u201c\u4e0d\u8fc7\u540e\u8f88\u4e2d\u7684 \u80fd\u4eba\uff0c\u54b1\u4eec\u4e5f\u8be5\u683d\u57f9\u683d\u57f9\uff0c\u82e5\u7136\u5b9a\u8981\u7f6e\u4e4b\u6b7b\u5730\uff0c\u90a3\u5c31\u663e\u5f97\u54b1\u4eec\u6c14\u91cf\u7a84\u4e86\u3002\u201d\u77f3\u82f1\u662f\u5c71\u897f\u3001\u9655 \u897f\u4e8c\u7701\u7684\u6b66\u6797\u9886\u8896\uff0c\u6c99\u6d9b\u53ea\u5f97\u5fcd\u7740\u6c14\u9053\uff1a\u201c\u5927\u54e5\u8bf4\u7684\u662f\uff01\u5c0f\u5f1f\u627f\u6559\uff0c\u544a\u8f9e\u4e86\uff01\u201d\u77f3\u82f1\u5c06\u4ed6\u4e00 \u6309\uff0c\u9053\uff1a\u201c\u770b\u4e86\u8fd9\u573a\uff0c\u4e5f\u8fd8\u672a\u8fdf\u3002\u4f60\u770b\uff0c\u4ed6\u4eec\u6253\u5f97\u591a\u70ed\u95f9\u5440\uff01\u201d \u3000\u3000\u53ea\u89c1\u64c2\u53f0\u4e0a\u4e24\u6761\u4eba\u5f71\uff0c\u6b64\u6765\u5f7c\u5f80\uff0c\u7a7f\u6765\u63d2\u53bb\uff0c\u7729\u76ee\u6b32\u82b1\u3002\u5927\u5bb6\u90fd\u662f\u5dee\u4e0d\u591a\u7684\u8eab\u6cd5\uff0c\u6ef4\u6e9c \u6e9c\u7684\u7ed5\u7740\u53f0\u75be\u8f6c\uff0c\u4e91\u857e\u4e00\u8eab\u767d\u8272\u8863\u88f3\uff0c\u77f3\u7fe0\u51e4\u5219\u662f\u7eff\u8884\u7ea2\u88d9\uff0c\u8863\u8882\u98d8\u626c\uff0c\u8d8a\u8f6c\u8d8a\u75be\uff0c\u6709\u5982\u4e00 \u7247\u767d\u4e91\u6367\u51fa\u4e00\u56e2\u7ea2\u971e\u5728\u78a7\u7eff\u7684\u6d77\u4e0a\u7ffb\u817e\uff0c\u4ee4\u4eba\u773c\u82b1\u7f2d\u4e71\u3002 \u3000\u3000\u82e5\u4f9d\u4e91\u857e\u7684\u672c\u9886\uff0c\u672c\u6765\u53ef\u4ee5\u5728\u4e09\u4e94\u5341\u62db\u4e4b\u5185\uff0c\u5c06\u77f3\u7fe0\u51e4\u6253\u5012\uff0c\u4f46\u4e91\u857e\u6709\u5fc3\u8981\u770b\u77f3\u7fe0\u51e4\u7684 \u201c\u4e91\u857e\u8fd9\u6837\u7684\u4eba\u54c1\u6b66\u529f\uff0c\u65e9\u5df2\u503e\u5012\uff0c\u53ea\u662f\u25a1\u6597\u4e4b\u4e0b\u89c1\u4e91\u857e\u51fa\u624b\uff0c\u5206\u660e\u662f\u6545\u610f\u7559\u60c5\uff0c\u72b6\u540c\u513f \u620f\uff0c\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u6211\u82e5\u4e0d\u9732\u51fa\u4e24\u624b\u529f\u592b\uff0c\u5c06\u6765\u6210\u4eb2\u4e4b\u540e\uff0c\u5c82\u4e0d\u6559\u4ed6\u8f7b\u89c6\u201d\u77f3\u7fe0\u51e4\u662f\u4e2a\u597d\u80dc\u7684 \u59d1\u5a18\uff0c\u8bef\u4f1a\u4e91\u857e\u6709\u610f\u76f8\u8ba9\u4e43\u662f\u8f7b\u89c6\uff0c\u638c\u6cd5\u4e00\u53d8\uff0c\u7adf\u5982\u75be\u98ce\u8fc5\u96e8\uff0c\u67d4\u4e2d\u5e26\u521a\uff0c\u638c\u5288\u6307\u6233\uff0c\u5176\u4e2d \u7adf\u6742\u7740\u8e51\u4e91\u5251\u7684\u8def\u6570\u3002\u4e91\u857e\u5fc3\u4e2d\u4e00\u6123\uff0c\u6296\u64de\u7cbe\u795e\uff0c\u4e00\u53e3\u6c14\u63a5\u4e86\u5979\u5341\u6765\u62db\uff0c\u4e5f\u65bd\u5c55\u4e86\u5e08\u95e8\u7edd \u6280\uff0c\u4ee5\u201c\u767e\u53d8\u7384\u673a\u201d\u5251\u6cd5\u5316\u5230\u638c\u4e0a\u6765\uff0c\u865a\u5b9e\u76f8\u751f\u53d8\u5316\u83ab\u6d4b\uff0c\u771f\u662f\u77bb\u4e4b\u5728\u524d\uff0c\u5ffd\u7109\u5728\u540e\uff0c\u987f\u65f6 \u5316\u5ba2\u4e3a\u4e3b\uff0c\u7740\u7740\u62a2\u653b\u3002\u77f3\u7fe0\u51e4\u89c1\u5979\u5982\u6b64\uff0c\u5fc3\u4e2d\u5012\u53cd\u6b22\u559c\uff0c\u6697\u9053\uff1a\u201c\u5230\u5e95\u903c\u5f97\u4f60\u4f7f\u51fa\u771f\u5b9e\u7684\u672c \u9886\u4e86\u3002\u201d\u8d8a\u53d1\u5356\u5f04\uff0c\u9163\u6597\u4e2d\u7a81\u51fa\u9669\u62db\uff0c\u8eab\u5b50\u5411\u524d\u4e00\u503e\uff0c\u7adf\u7136\u6b3a\u8fdb\u4e91\u857e\u6000\u4e2d\uff0c\u4e09\u6307\u4e00\u4f38\u6765\u6263\u4e91 \u857e\u7684\u8109\u95e8\uff0c\u4e91\u857e\u6b66\u529f\u867d\u6bd4\u5979\u9ad8\uff0c\u8fd9\u4e00\u62db\u5374\u4e5f\u771f\u96be\u5316\u89e3\uff0c\u767e\u5fd9\u4e2d\u4e0d\u5047\u601d\u7d22\uff0c\u624b\u8155\u4e00\u62ac\uff0c\u5c06\u5979\u624b \u81c2\u6258\u9ad8\uff0c\u5de6\u81c2\u4e00\u63fd\uff0c\u5c06\u5979\u7ed3\u7ed3\u5b9e\u5b9e\u62b1\u7740\uff0c\u624b\u6307\u5728\u5979\u80c1\u4e0b\u4e00\u634f\uff0c\u77f3\u7fe0\u51e4\u8eab\u5b50\u9165\u9ebb\uff0c\u4e0d\u7531\u81ea\u4e3b\u5730 \u5012\u5165\u4e91\u857e\u6000\u4e2d\u3002\u4e91\u857e\u201c\u54ce\u5440\u201d\u4e00\u58f0\u542c\u5f97\u53f0\u4e0b\u54c4\u7b11\u8fd9\u58f0\uff0c\u731b\u7136\u9192\u8d77\u81ea\u5df1\u73b0\u5728\u7684\u8eab\u4efd\u4e43\u662f\u7537\u513f\uff0c \u4e0d\u89c9\u6ee1\u8138\u901a\u7ea2\uff0c\u6025\u5fd9\u5728\u5979\u80c1\u4e0b\u4e00\u6309\uff0c\u89e3\u5f00\u5df2\u88ab\u5c01\u95ed\u4e86\u7684\u9ebb\u7a74\uff0c\u5c06\u5979\u8f7b\u8f7b\u4e00\u63a8\uff0c\u968f\u5373\u8dc3\u540e\u4e09 \u6b65\uff0c\u62b1\u62f3\u4e00\u63d6\uff0c\u8bf4\u9053\uff1a\u201c\u59d1\u5a18\u5305\u6db5\uff0c\u5c0f\u751f\u5f97\u7f6a\u4e86\uff01\u201d \u3000\u3000\u64c2\u53f0\u4e0b\u77f3\u82f1\u62c8\u987b\u5fae\u7b11\uff0c\u6c99\u6d9b\u9762\u8272\u94c1\u9752\uff0c\u9053\uff1a\u201c\u606d\u559c\u5927\u54e5\u9009\u5f97\u4f73\u5a7f\uff0c\u5c0f\u5f1f\u544a\u8f9e\u4e86\u3002\u201d\u77f3\u82f1 \u628a\u624b\u4e00\u62db\uff0c\u53eb\u7ba1\u5bb6\u8fc7\u6765\u9053\uff1a\u201c\u6c99\u8d24\u5f1f\uff0c\u505a\u5927\u54e5\u7684\u66ff\u4f60\u8d54\u7f6a\uff0c\u8fd9\u91cc\u6709\u4e00\u5305\u73e0\u5b9d\uff0c\u804a\u4f5c\u8d54\u507f\u4e4b \u8d44\u3002\u90a3\u7167\u591c\u72ee\u5b50\u9a6c\u975e\u51e1\u9a6c\u53ef\u6bd4\uff0c\u53ea\u597d\u8bf7\u8d24\u5f1f\u5230\u6211\u7684\u9a6c\u53a9\u4e2d\u6311\u9009\u5341\u5339\u6700\u597d\u7684\u9a6c\uff0c\u4ee5\u4e3a\u62b5\u507f\uff0c\u8bf7 \u8d24\u5f1f\u624b\u4e0b\u7559\u60c5\uff0c\u653e\u8fc7\u4ed6\u6240\u4fdd\u7684\u8fd9\u8d9f\u9556\u5427\u3002\u201d\u77f3\u82f1\u5148\u524d\u542c\u5f97\u6c99\u6d9b\u6240\u8bf4\uff0c\u8fd8\u4ee5\u4e3a\u4e91\u857e\u771f\u662f\u4e2a\u4fdd\u9556 \u7684\u4eba\u3002 \u3000\u3000\u6c99\u6d9b\u51b7\u51b7\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8c22\u5927\u54e5\u539a\u8d50\uff0c\u5c0f\u5f1f\u8fd8\u8584\u6709\u8d44\u8d22\uff0c\u4e0d\u6562\u8d2a\u5f97\u3002\u53ea\u662f\u9ed1\u9053\u4e0a\u7684\u89c4\u77e9\uff0c \u8fd9\u8d9f\u9556\u5c0f\u5f1f\u65e2\u7136\u4e00\u5ea6\u5931\u624b\uff0c\u90a3\u5c31\u4e0d\u80fd\u5c31\u6b64\u7f62\u4f11\uff0c\u8fd9\u4e2a\u8981\u8bf7\u5927\u54e5\u89c1\u8c05\u3002\u201d\u4e00\u63d6\u5230\u5730\uff0c\u643a\u4e86\u6c99\u65e0 \u5c82\u6392\u4f17\u800c\u53bb\u3002\u77f3\u82f1\u597d\u751f\u4e0d\u60a6\uff0c\u53eb\u7ba1\u5bb6\u9001\u5ba2\uff0c\u81ea\u5df1\u4e5f\u8dc3\u4e0a\u4e86\u64c2\u53f0\u3002 \u3000\u3000\u64c2\u53f0\u4e0a\u77f3\u7fe0\u51e4\u6ee1\u9762\u901a\u7ea2\uff0c\u89c1\u7236\u4eb2\u4e0a\u53f0\uff0c\u4f4e\u4e0b\u5934\u6765\uff0c\u624b\u6307\u8f7b\u637b\u8863\u5e26\uff0c\u4e91\u857e\u9762\u8272\u4ea6\u751a\u5c34\u5c2c\u3002 \u77f3\u82f1\u54c8\u54c8\u5927\u7b11\uff0c\u9053\uff1a\u201c\u957f\u6c5f\u540e\u6d6a\u63a8\u524d\u6d6a\uff0c\u4e16\u4e0a\u65b0\u4eba\u6362\u65e7\u4eba\u3002\u5e74\u5c11\u82f1\u96c4\uff0c\u96be\u5f97\u96be\u5f97\u3002\u201d\u77f3\u82f1\u9002 \u624d\u5728\u53f0\u4e0b\uff0c\u5df2\u5411\u7ba1\u5bb6\u67e5\u5230\u4e91\u857e\u7684\u62dc\u8d34\uff0c\u77e5\u9053\u4e86\u5979\u7684\u540d\u5b57\uff0c\u53c8\u7b11\u8a00\u9053\uff1a\u201c\u4e91\u76f8\u516c\uff0c\u4f60\u8fd9\u6837\u7684\u8eab \u624b\uff0c\u4f55\u5fc5\u8981\u505a\u4fdd\u9556\uff1f\u201d\u4e91\u857e\u7b54\u9053\uff1a\u201c\u6211\u5e76\u6ca1\u6709\u505a\u4fdd\u9556\u5440\uff01\u524d\u65e5\u5728\u8def\u4e0a\u5076\u7136\u7ed3\u8bc6\u4e00\u4f4d\u670b\u53cb\uff0c\u66ff \u4ed6\u62b5\u5fa1\u52ab\u8d3c\uff0c\u65e0\u610f\u4e4b\u4e2d\uff0c\u4e0e\u6c99\u5be8\u4e3b\u7236\u5b50\u7ed3\u4e0b\u6881\u5b50\u3002\u201d\u77f3\u82f1\u5fc3\u4e2d\u4e00\u5bbd\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\u3002\u4f60\u5bb6\u4e2d \u5c1a\u6709\u4f55\u4eba\uff1f\u8ba2\u4eb2\u6ca1\u6709\uff1f\u201d\u4e91\u857e\u8fdf\u7591\u534a\u664c\uff0c\u9053\uff1a\u201c\u53ea\u6709\u4e00\u4f4d\u54e5\u54e5\uff0c\u5c1a\u672a\u8ba2\u4eb2\u3002\u201d\u77f3\u82f1\u54c8\u54c8\u5927 \u7b11\uff0c\u9053\uff1a\u201c\u5c11\u5e74\u4eba\u63d0\u8d77\u8ba2\u4eb2\uff0c\u5c31\u5bb3\u81ca\u4e86\u3002\u201d\u4e91\u857e\u66f4\u662f\u5c34\u5c2c\uff0c\u53ea\u542c\u5f97\u77f3\u82f1\u53c8\u9053\uff1a\u201c\u8fd9\u64c2\u53f0\u4f60\u6253 \u80dc\u4e86\uff0c\u6211\u8981\u7ed9\u4f60\u4e00\u70b9\u5f69\u7269\u3002\u201d\u62ff\u51fa\u4e00\u679a\u7eff\u7389\u6212\u6307\uff0c\u4e0a\u9762\u9576\u7740\u4e24\u7c92\u201c\u732b\u513f\u773c\u201d\u5b9d\u77f3\uff0c\u95ea\u95ea\u653e \u5149\u3002\u77f3\u82f1\u9053\uff1a\u201c\u8fd9\u662f\u7fe0\u513f\u7684\u6bcd\u4eb2\u4e34\u7ec8\u4e4b\u65f6\u4ea4\u4e0e\u5979\u7684\uff0c\u73b0\u5728\u8f6c\u9001\u4f60\u4e86\u3002\u201d\u4e91\u857e\u9053\uff1a\u201c\u65e2\u662f\u77f3\u5c0f \u59d0\u4e4b\u7269\uff0c\u665a\u8f88\u4e0d\u6562\u63a5\u53d7\u3002\u201d\u77f3\u82f1\u53c8\u662f\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u8fd9\u662f\u7ed9\u4f60\u4eec\u8ba2\u5a5a\u7684\u793c\u7269\uff0c\u4e3a\u4f55\u4e0d\u80fd\u63a5 \u53d7\uff1f\u201d\u4e91\u857e\u7b54\u9053\uff1a\u201c\u665a\u8f88\u4e0d\u6562\u9ad8\u6500\u3002\u201d\u77f3\u82f1\u9762\u8272\u4e00\u6c89\uff0c\u4f4e\u58f0\u95ee\u9053\uff1a\u201c\u4f60\u5acc\u5f03\u6211\u7684\u5973\u513f\u4e48\uff1f\u201d \u4e91\u857e\u9053\uff1a\u201c\u5c82\u6562\u5acc\u5f03\u5c0f\u59d0\uff0c\u53ea\u662f\u6b64\u4e8b\u4e07\u96be\u4ece\u547d\u3002\u201d\u77f3\u82f1\u6012\u9053\uff1a\u201c\u8fd9\u5374\u662f\u4e3a\u4f55\uff1f\u201d\u4e91\u857e\u773c\u775b\u4e00 \u77a5\uff0c\u53ea\u89c1\u77f3\u7fe0\u51e4\u8f7b\u62c8\u88d9\u89d2\uff0c\u6da8\u7ea2\u4e86\u9762\uff0c\u4e24\u53ea\u53c8\u5706\u53c8\u5927\u7684\u773c\u775b\uff0c\u6ce8\u7740\u81ea\u5df1\uff0c\u773c\u4e2d\u6cdb\u7740\u6cea\u5149\uff0c\u5fc3 \u5ff5\u4e00\u52a8\uff0c\u6697\u4e2d\u60f3\u9053\uff1a\u201c\u4e5f\u597d\uff0c\u4e14\u5f85\u6211\u6765\u4e2a\u79fb\u82b1\u63a5\u6728\u4e4b\u8ba1\u3002\u201d\u4fbf\u5047\u610f\u63a8\u8f9e\u9053\uff1a\u201c\u5c1a\u672a\u7980\u8fc7\u5c0a \u957f\uff0c\u5982\u4f55\u597d\u79c1\u4e0b\u8ba2\u4eb2\uff1f\u201d\u77f3\u82f1\u9053\uff1a\u201c\u4f60\u7684\u5144\u957f\u73b0\u5728\u4f55\u65b9\uff1f\u201d\u4e91\u857e\u9053\uff1a\u201c\u6211\u5144\u5f1f\u81ea\u5e7c\u5931\u6563\u4e0d\u77e5 \u4ed6\u7684\u4e0b\u843d\u3002\u201d\u77f3\u82f1\u7709\u5934\u4e00\u76b1\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u8981\u7980\u544a\u4f55\u4eba\uff1f\u201d\u4e91\u857e\u9053\uff1a\u201c\u6211\u7236\u6bcd\u53cc\u4ea1\uff0c\u6709\u4e00\u4f4d\u4e16 \u4ea4\u53d4\u7956\uff0c\u5f85\u6211\u6709\u5982\u5b59\u513f\uff0c\u5a5a\u4e8b\u987b\u8981\u7980\u544a\u4e8e\u4ed6\u3002\u201d\u77f3\u82f1\u9053\uff1a\u201c\u4f60\u7684\u4e16\u4ea4\u53d4\u7956\u59d3\u751a\u540d\u8c01\uff0c\u662f\u4f55\u7b49 \u4eba\u7269\uff1f\u201d\u4e91\u857e\u9053\uff1a\u201c\u6211\u4e16\u53d4\u7956\u7684\u540d\u5b57\u5728\u8fd9\u91cc\u4e0d\u597d\u8bf4\u5f97\uff0c\u4ed6\u662f\u6b66\u6797\u4e2d\u6709\u6570\u7684\u4eba\u7269\u3002\u201d\u77f3\u82f1\u5927\u7b11 \u9053\uff1a\u201c\u6b66\u6797\u4e2d\u6709\u6570\u7684\u4eba\u7269\uff0c\u63d0\u8d77\u6211\u8f70\u5929\u96f7\u77f3\u82f1\u7684\u540d\u5b57\uff0c\u5927\u7ea6\u4e5f\u603b\u5f97\u5356\u70b9\u4ea4\u60c5\uff0c\u8fd9\u5a5a\u4e8b\u4f60\u662f\u65e0 \u987b\u987e\u8651\u7684\u4e86\u3002\u201d\u4e91\u857e\u7eb3\u5934\u4fbf\u62dc\uff0c\u53eb\u4e86\u58f0\uff1a\u201c\u5cb3\u7236\u5927\u4eba\uff01\u201d\u5728\u6000\u4e2d\u53d6\u51fa\u4e00\u679d\u73ca\u745a\uff0c\u9053\uff1a\u201c\u5ba2\u4e2d \u6ca1\u5e26\u4ec0\u4e48\u4e1c\u897f\uff0c\u8fd9\u679d\u73ca\u745a\u6743\u5f53\u8058\u793c\u3002\u201d\u77f3\u82f1\u54c8\u54c8\u5927\u7b11\uff0c\u628a\u73ca\u745a\u4ea4\u7ed9\u5973\u513f\uff0c\u62c9\u8d77\u4e91\u857e\u5728\u53f0\u4e2d\u5fc3 \u4e00\u7ad9\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u6b64\u540e\u8fd9\u4f4d\u4e91\u76f8\u516c\u4fbf\u662f\u6211\u534a\u4e2a\u513f\u5b50\uff0c\u4ed6\u65e5\u5728\u6c5f\u6e56\u4e0a\u8d70\u52a8\uff0c\u8bf7\u5404\u4f4d\u591a\u591a\u7167 \u987e\u3002\u201d\u53f0\u4e0b\u8d3a\u5ba2\u7eb7\u7eb7\u8d3a\u559c\uff0c\u77f3\u82f1\u53c8\u8bf4\u9053\uff1a\u201c\u62e3\u65e5\u4e0d\u5982\u649e\u65e5\uff0c\u6211\u5e74\u8001\u6500\u692d\u9997\u6d77\u25a1\u94f1\u25a1\u91a3\u552c\u25a1\u4e00 \u8dc3\u800c\u8d77\u3002 \u3000\u3000\u77f3\u7fe0\u51e4\u5f00\u4e86\u623f\u95e8\uff0c\u5429\u5490\u4e2b\u9b1f\u9053\uff1a\u201c\u628a\u88ab\u8925\u5168\u90fd\u6362\u8fc7\u3002\u201d\u4e2b\u9b1f\u89c1\u9526\u8925\u4e0a\u6ee1\u662f\u978b\u5370\u6ce5\u6c61\uff0c\u63a9 \u53e3\u6697\u7b11\u3002\u77f3\u7fe0\u51e4\u4e00\u624b\u63d0\u706f\uff0c\u4e00\u624b\u643a\u7740\u4e91\u857e\uff0c\u8f6c\u8fc7\u51e0\u5904\u56de\u5eca\uff0c\u8d70\u4e0a\u4e00\u5ea7\u5927\u697c\u3002 \u3000\u3000\u697c\u9ad8\u4e94\u5c42\uff0c\u77f3\u7fe0\u51e4\u63a8\u7740\u4e91\u857e\u8d70\u4e0a\u5c42\uff0c\u53ea\u89c1\u697c\u4e2d\u6446\u7740\u4e00\u5f20\u5706\u684c\uff0c\u684c\u4e0a\u6446\u4e86\u65e0\u6570\u73cd\u5b9d\uff0c\u77f3\u82f1 \u5750\u5728\u5f53\u4e2d\uff0c\u5de6\u53f3\u5750\u7740\u56db\u4eba\u3002\u77f3\u82f1\u89c1\u5979\u8fdb\u6765\uff0c\u4e00\u7b11\u8bf4\u9053\uff1a\u201c\u4eca\u56de\u8981\u591a\u7559\u4e00\u4ef6\u5566\uff0c\u7fe0\u513f\u857e\u513f\uff0c\u4f60 \u4eec\u90fd\u62e3\u4e00\u4ef6\uff0c\u4f59\u4e0b\u6765\u7684\u624d\u7ed9\u597d\u670b\u53cb\u4eec\u3002\u201d \u3000\u3000\u4e91\u857e\u83ab\u540d\u5176\u5999\uff0c\u7fe0\u51e4\u9053\uff1a\u201c\u8fd9\u662f\u6211\u4eec\u7684\u8001\u89c4\u77e9\uff0c\u4f60\u542c\u7239\u7684\u8bdd\uff0c\u5148\u62e3\u4e00\u4ef6\u3002\u201d \u3000\u3000\u4e91\u857e\u62ff\u4e86\u4e00\u4e2a\u78a7\u7389\u72ee\u5b50\uff0c\u77f3\u7fe0\u51e4\u4e5f\u968f\u624b\u62ff\u4e86\u4e00\u679d\u7389\u7c2a\u3002\u4e91\u857e\u4e3e\u76ee\u56db\u987e\uff0c\u8fd9\u623f\u95f4\u5012\u5f88\u6734 \u7d20\uff0c\u623f\u4e2d\u9664\u4e86\u4e00\u4e2a\u94c1\u7bb1\u4e4b\u5916\uff0c\u7adf\u662f\u65e2\u65e0\u5bb6\u5177\uff0c\u53c8\u65e0\u6446\u8bbe\uff0c\u53ea\u662f\u5899\u4e0a\u6302\u7740\u4e00\u5e45\u5de8\u5927\u7684\u5de5\u7b14\u753b\uff0c \u753b\u4e2d\u4e00\u5ea7\u5927\u57ce\uff0c\u5c71\u73af\u6c34\u7ed5\uff0c\u8fd8\u70b9\u7f00\u6709\u4ead\u53f0\u697c\u9601\u3001\u56ed\u6797\u4eba\u7269\uff0c\u770b\u6765\u662f\u6c5f\u5357\u7684\u4e00\u5904\u540d\u57ce\u3002\u77f3\u82f1\u7b11 \u9053\uff1a\u201c\u4f60\u6b22\u559c\u8fd9\u5e45\u753b\u4e48\uff1f\u660e\u65e5\u6211\u518d\u548c\u4f60\u8bf4\u8fd9\u5e45\u753b\u7684\u6545\u4e8b\u3002\u597d\uff0c\u4f60\u4eec\u53ef\u4ee5\u56de\u53bb\u4e86\u3002\u201d \u3000\u3000\u4e91\u857e\u4e0e\u7fe0\u51e4\u8d70\u51fa\u623f\u95e8\uff0c\u53ea\u542c\u5f97\u623f\u4e2d\u5ba2\u4eba\u8bf4\u9053\uff1a\u201c\u771f\u53ef\u60dc\uff0c\u8fd9\u662f\u6700\u540e\u4e00\u6b21\u7684\u4ea4\u6613\u4e86\u3002\u201d\u77f3 \u82f1\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4e16\u95f4\u54ea\u6709\u767e\u5e74\u4e0d\u8c22\u4e4b\u82b1\uff0c\u6211\u5e74\u5df2\u8001\u8fc8\uff0c\u8fd9\u4e70\u5356\u4e0d\u80fd\u5e72\u4e86\u3002\u597d\uff0c\u54b1\u4eec\u8fd8\u662f\u7167\u8001 \u89c4\u77e9\uff0c\u4f60\u4eec\u4f30\u4ef7\u5427\u3002\u201d\u4e91\u857e\u597d\u751f\u5947\u602a\uff0c\u60f3\u518d\u542c\u4e0b\u53bb\u5374\u7ed9\u7fe0\u51e4\u62c9\u4e86\u4e0b\u697c\u3002 \u3000\u3000\u56de\u5230\u65b0\u623f\uff0c\u5e8a\u4e0a\u88ab\u5168\u5df2\u6362\u8fc7\uff0c\u7329\u7329\u6be1\u5b50\u914d\u4e0a\u6e58\u7ee3\u7684\u5927\u7ea2\u88ab\u9762\uff0c\u8d8a\u53d1\u663e\u5f97\u7f8e\u8273\u534e\u4e3d\uff0c\u8fdc\u8fdc \u542c\u5f97\u66f4\u9f13\u4e4b\u58f0\uff0c\u7fe0\u51e4\u9053\uff1a\u201c\u55ef\uff0c\u5df2\u4e09\u66f4\u5566\u3002\u201d\u4e91\u857e\u9053\uff1a\u201c\u6211\u73b0\u5728\u5012\u4e0d\u60f3\u7761\u4e86\uff0c\u4f60\u7ed9\u6211\u8bf4\u8bf4\uff0c \u4f60\u7239\u9002\u624d\u662f\u600e\u4e48\u4e00\u56de\u4e8b\uff1f\u201d \u3000\u3000\u7fe0\u51e4\u9053\uff1a\u201c\u6211\u7239\u662f\u4e00\u4e2a\u72ec\u811a\u5927\u76d7\uff0c\u6bcf\u5e74\u51fa\u53bb\u4f5c\u6848\u4e00\u6b21\u3002\u4e61\u4eba\u90fd\u4e0d\u77e5\u9053\u3002\u4ed6\u6bcf\u6b21\u4f5c\u6848\u56de \u6765\uff0c\u603b\u8981\u8ba9\u6211\u5148\u62e3\u4e00\u4ef6\u73e0\u5b9d\uff0c\u5176\u4f59\u7684\u624d\u62ff\u53bb\u53d1\u5356\u3002\u201d\u4e91\u857e\u9053\uff1a\u201c\u5077\u6765\u7684\u4e1c\u897f\u600e\u597d\u62ff\u53bb\u53d1 \u5356\uff1f\u201d\u7fe0\u51e4\u9053\uff1a\u201c\u81ea\u7136\u6709\u505a\u8fd9\u8def\u751f\u610f\u7684\u4eba\uff0c\u521a\u624d\u90a3\u56db\u4e2a\u6c49\u5b50\u5c31\u662f\u4e13\u6536\u4e70\u7239\u7239\u73e0\u5b9d\u7684\u4eba\uff0c\u542c\u8bf4 \u4ed6\u4eec\u795e\u901a\u5e7f\u5927\uff0c\u5728\u5317\u65b9\u52ab\u6765\u7684\u62ff\u5230\u5357\u65b9\u53bb\u5356\uff0c\u5357\u65b9\u52ab\u6765\u7684\u5c31\u62ff\u5230\u5317\u65b9\u53bb\u5356\uff0c\u4ece\u6765\u6ca1\u5931\u8fc7\u624b\u3002 \u6211\u7239\u7239\u5356\u5f97\u7684\u94b1\uff0c\u4e00\u5c0f\u90e8\u5206\u7f6e\u4e86\u4ea7\u4e1a\uff0c\u5176\u4f59\u7684\u5168\u62ff\u6765\u6551\u6d4e\u6c5f\u6e56\u4e0a\u7684\u7a77\u670b\u53cb\u4e86\u3002\u201d\u4e91\u857e\u9053\uff1a \u201c\u539f\u6765\u5982\u6b64\uff0c\u602a\u4e0d\u5f97\u4f60\u7239\u7239\u6709\u8d5b\u5b5f\u5c1d\u4e4b\u79f0\u3002\u201d \u3000\u3000\u7fe0\u51e4\u5fae\u5fae\u4e00\u7b11\uff0c\u542c\u5f97\u66f4\u9f13\u53c8\u201c\u549a\u201d\u7684\u4e00\u4e0b\uff0c\u7f8e\u76ee\u6d41\u76fc\uff0c\u7768\u7740\u4e91\u857e\u7b11\u9053\uff1a\u201c\u4f60\u8981\u548c\u6211\u8c08\u4e2a \u901a\u5bb5\u4e48\uff1f\u201d\u4e91\u857e\u9053\uff1a\u201c\u6211\u518d\u95ee\u4f60\u4ef6\u4e8b\uff0c\u90a3\u5e45\u753b\u53c8\u6709\u4ec0\u4e48\u6545\u4e8b\u5462\uff1f\u201d\u7fe0\u51e4\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u77e5\u9053\uff0c \u7239\u4ece\u672a\u548c\u6211\u8bf4\u8fc7\u3002\u201d\u6c89\u541f\u534a\u664c\uff0c\u9053\uff1a\u201c\u6211\u4e5f\u5947\u602a\uff0c\u7239\u4ec0\u4e48\u4e8b\u90fd\u548c\u6211\u8bf4\uff0c\u5c31\u662f\u4ece\u672a\u63d0\u8fc7\u90a3\u5e45 \u753b\u3002\u201d \u3000\u3000\u5916\u9762\u66f4\u9f13\u53c8\u201c\u549a\u201d\u7684\u4e00\u4e0b\uff0c\u7fe0\u51e4\u7b11\u9053\uff1a\u201c\u4f60\u8fd8\u6709\u4ec0\u4e48\u8981\u95ee\u5417\uff1f\u201d\u4e91\u857e\u641c\u7d22\u67af\u80a0\uff0c\u60f3\u4e0d\u51fa \u4ec0\u4e48\u53ef\u62d6\u5ef6\u4e4b\u8ba1\uff0c\u52bf\u4e5f\u4e0d\u80fd\u548c\u5979\u8c08\u4e2a\u901a\u5bb5\uff0c\u5fc3\u4e2d\u5927\u6025\u3002\u7fe0\u51e4\u4f4e\u58f0\u95ee\u9053\uff1a\u201c\u4e91\u76f8\u516c\uff0c\u4f60\u771f\u7684\u4e0d \u5acc\u5f03\u6211\u4e48\uff1f\u201d\u4e91\u857e\u9053\uff1a\u201c\u4f60\u6c38\u8fdc\u662f\u6211\u7684\u597d\u59d0\u59d0\uff0c\u6211\u600e\u4e48\u4f1a\u5acc\u5f03\u4f60\u5462\uff1f\u201d\u7fe0\u51e4\u67d4\u58f0\u8bf4\u9053\uff1a \u201c\u597d\uff0c\u90a3\u4e48\u54b1\u4eec\u660e\u513f\u518d\u8c08\u5427\uff0c\u4f60\u4e5f\u8be5\u7761\u5566\u3002\u201d \u3000\u3000\u4e91\u857e\u624b\u6478\u8863\u895f\u7ebd\u6263\u53e3\u4e2d\u8bf4\u9053\uff1a\u201c\u662f\u5566\u662f\u5566\u3002\u662f\u8be5\u7761\u5566\u3002\u201d\u624b\u5374\u505c\u5728\u7ebd\u6263\u65c1\u8fb9\uff0c\u5e76\u4e0d\u53bb \u89e3\u3002\u6b63\u81ea\u65e0\u8ba1\u53ef\u65bd\uff0c\u5ffd\u542c\u5f97\u5916\u9762\u66f4\u9523\u6025\u54cd\uff0c\u4eba\u58f0\u55a7\u56a3\uff0c\u6709\u4eba\u5927\u53eb\u9053\uff1a\u201c\u6349\u8d3c\uff0c\u6349\u8d3c\uff01\u201d \u3000\u3000\u8f70\u5929\u96f7\u77f3\u82f1\u7684\u5bb6\u4e2d\uff0c\u5c45\u7136\u6709\u8d3c\u5149\u987e\uff0c\u8fd9\u53ef\u662f\u5929\u5927\u7684\u7b11\u8bdd\uff01\u7559\u5bbf\u7684\u8d3a\u5ba2\uff0c\u90fd\u662f\u4e09\u5c71\u4e94\u5cb3\u7684 \u80fd\u4eba\uff0c\u95fb\u58f0\u7eb7\u7eb7\u8df3\u8d77\u56db\u5904\u641c\u7d22\u3002 \u3000\u3000\u4e91\u857e\u4e00\u7b11\u9053\uff1a\u201c\u7761\u4e0d\u6210\u5566\uff0c\u8fd9\u8d3c\u4eba\u4e00\u5b9a\u662f\u89ca\u89ce\u4f60\u7239\u7239\u7684\u73e0\u5b9d\u6765\u7684\u3002\u201d\u4e0e\u7fe0\u51e4\u53cc\u53cc\u8dc3\u51fa\uff0c \u5f84\u5954\u85cf\u5b9d\u697c\u6765\u3002 \u3000\u3000\u4e91\u857e\u8f7b\u529f\u8d85\u5999\uff0c\u8fdc\u5728\u4f17\u4eba\u4e4b\u4e0a\uff0c\u7728\u773c\u4e4b\u95f4\uff0c\u4e0d\u4f46\u8d8a\u8fc7\u4e86\u5bb6\u4e01\u4e0e\u8d3a\u5ba2\u7684\u524d\u9762\uff0c\u800c\u4e14\u628a\u77f3\u7fe0 \u51e4\u4e5f\u7529\u5728\u540e\u8fb9\uff0c\u77f3\u7fe0\u51e4\u53c8\u662f\u559c\u53c8\u662f\u607c\uff0c\u559c\u8005\u662f\u201c\u4ed6\u201d\u4e3a\u4e86\u77f3\u5bb6\u4e4b\u4e8b\uff0c\u5982\u6b64\u7740\u6025\uff1b\u607c\u8005\u662f\u5927\u58f0 \u547c\u53eb\uff0c\u201c\u4ed6\u201d\u5374\u4e0d\u80af\u4e00\u505c\u3002 \u3000\u3000\u77f3\u5bb6\u5e84\u56ed\u5e7f\u9614\uff0c\u90a3\u85cf\u5b9d\u697c\u5728\u540e\u9662\u4e1c\u89d2\uff0c\u4e91\u857e\u4e00\u6e9c\u70df\u5730\u8dd1\u5230\u697c\u4e0b\uff0c\u56de\u5934\u4e00\u671b\uff0c\u53ea\u89c1\u77f3\u7fe0\u51e4 \u7684\u8eab\u5f62\uff0c\u8fd8\u5728\u5916\u9762\u5927\u9662\u7684\u5c4b\u9876\u3002\u4e91\u857e\u62d4\u5251\u51fa\u9798\uff0c\u98de\u8eab\u4e00\u63a0\uff0c\u811a\u52fe\u6a90\u89d2\uff0c\u5355\u624b\u4e00\u6309\uff0c\u4ece\u7b2c\u4e00\u5c42 \u7684\u6a90\u89d2\uff0c\u98de\u4e0a\u4e86\u7b2c\u4e8c\u5c42\u697c\uff0c\u4fa7\u8033\u4e00\u542c\uff0c\u5ffd\u95fb\u5f97\u602a\u58f0\u557e\u557e\uff0c\u6709\u5982\u9b3c\u53eb\uff0c\u9759\u591c\u4e4b\u4e2d\uff0c\u4ee4\u4eba\u80c6\u5bd2\u3002 \u3000\u3000\u4e91\u857e\u9a82\u9053\uff1a\u201c\u5c0f\u8d3c\u88c5\u795e\u5f04\u9b3c\uff0c\u60f3\u5413\u4eba\u4e48\uff1f\u201d\u542c\u5f97\u5f02\u58f0\u6765\u81ea\u697c\u5185\uff0c\u64e6\u71c3\u968f\u8eab\u6240\u5e26\u7684\u706b\u77f3\uff0c \u71c3\u8d77\u706b\u6298\uff0c\u4fbf\u94bb\u4e86\u8fdb\u53bb\uff0c\u5f80\u4e0a\u4e00\u95ef\uff0c\u5728\u4e09\u697c\u7684\u697c\u68af\u4e4b\u4e0b\uff0c\u731b\u4e00\u62ac\u5934\uff0c\u5ffd\u89c1\u56db\u6761\u5927\u6c49\uff0c\u90fd\u662f\u7528 \u7740\u201c\u91d1\u9e21\u72ec\u7acb\u201d\u4e4b\u52bf\uff0c\u6328\u6b21\u7acb\u5728\u68af\u7ea7\u4e4b\u4e0a\uff0c\u4e00\u8db3\u4e3e\u8d77\uff0c\u4f3c\u4e4e\u6b63\u6b32\u5954\u8dd1\u4e0b\u6765\uff0c\u5374\u88ab\u4eba\u7528\u201c\u5b9a\u8eab \u6cd5\u201d\u5b9a\u4f4f\u4f3c\u7684\uff0c\u77aa\u7740\u53cc\u773c\uff0c\u5589\u5934\u683c\u683c\u4f5c\u54cd\uff0c\u201c\u5475\u5475\u201d\u4f5c\u58f0\u3002\u5c24\u5176\u53ef\u6015\u7684\u662f\uff0c\u4e00\u4e2a\u4e2a\u7684\u8138\u90e8\u808c \u8089\uff0c\u90fd\u56e0\u75c9\u631b\u800c\u626d\u66f2\u53d8\u5f62\uff0c\u5c31\u50cf\u521a\u4ece\u5730\u72f1\u4e2d\u95ef\u51fa\u6765\u7684\u6076\u9b3c\uff01\u5927\u7740\u80c6\u5b50\uff0c\u4e3e\u8d77\u706b\u6298\uff0c\u5f80\u524d\u4e00 \u7167\uff0c\u56db\u4eba\u9762\u90e8\u867d\u7136\u53d8\u5f62\uff0c\u7ec6\u770b\u4e4b\u4e0b\uff0c\u4ecd\u5206\u8fa8\u5f97\u51fa\u4e43\u662f\u9002\u624d\u5411\u77f3\u82f1\u8d2d\u4e70\u8d43\u7269\u7684\u56db\u4e2a\u73e0\u5b9d\u5ba2\u5546\u3002 \u8fd9\u56db\u4e2a\u5ba2\u5546\u80fd\u505a\u8fd9\u79cd\u751f\u610f\u6b66\u529f\u5f53\u975e\u6cdb\u6cdb\uff0c\u800c\u7adf\u5728\u5954\u4e0b\u697c\u68af\u7684\u970e\u90a3\u4e4b\u95f4\uff0c\u88ab\u4eba\u70b9\u4e86\u7a74\u9053\uff0c\u697c\u68af \u72ed\u7a84\uff0c\u800c\u4e14\u53c8\u662f\u4ee5\u4e00\u88ad\u56db\uff0c\u8fd9\u4eba\u6b66\u529f\u4e4b\u5f3a\uff0c\u51fa\u624b\u4e4b\u5feb\uff0c\u53ef\u60f3\u800c\u77e5\u3002 \u3000\u3000\u4e91\u857e\u5fc3\u9053\uff1a\u201c\u8fd9\u79cd\u5389\u5bb3\u7684\u70b9\u7a74\uff0c\u771f\u662f\u89c1\u6240\u672a\u89c1\uff0c\u4e0d\u77e5\u6211\u7528\u672c\u95e8\u7684\u89e3\u7a74\u4e4b\u6cd5\uff0c\u80fd\u5426\u6709 \u6548\uff1f\u201d\u5bdf\u770b\u56db\u4eba\u5f62\u72b6\uff0c\u5927\u7ea6\u662f\u88ab\u4eba\u70b9\u4e86\u810a\u690e\u4e4b\u4e0b\u7684\u9ebb\u7a74\u4e0e\u54d1\u7a74\uff0c\u8bd5\u7740\u7528\u672c\u4eec\u89e3\u9ebb\u7a74\u4e4b\u6cd5\u65bd \u6551\uff0c\u679c\u7136\u5e94\u624b\u89c1\u6548\uff0c\u53ea\u89c1\u56db\u4eba\u5927\u53eb\u4e00\u58f0\uff0c\u7a81\u7136\u6251\u5012\uff0c\u4e91\u857e\u6025\u6025\u8dc3\u5f00\uff0c\u4f46\u542c\u5f97\u91d1\u7389\u76f8\u649e\u4e4b\u58f0\uff0c \u56db\u4eba\u6000\u4e2d\u7684\u73e0\u5b9d\uff0c\u6eda\u6eda\u6ee1\u5730\u3002 \u3000\u3000\u4e91\u857e\u53c8\u662f\u4e00\u6014\uff0c\u8fd9\u56db\u4eba\u6240\u6709\u7684\u73e0\u5b9d\uff0c\u4ef7\u503c\u4f55\u6b62\u5341\u4e07\uff0c\u90a3\u4e48\u5077\u88ad\u4ed6\u4eec\u7684\u8d3c\u4eba\uff0c\u663e\u7136\u4e0d\u662f\u4e3a \u4e86\u8d22\u7269\u800c\u6765\u4e86\u3002\u4e91\u857e\u559d\u9053\uff1a\u201c\u8d3c\u4eba\u53bb\u4e86\u6ca1\u6709\uff1f\u201d\u56db\u4eba\u4e00\u624b\u6309\u7740\u80f8\u53e3\uff0c\u4e00\u624b\u5411\u4e0a\u4e00\u6307\uff0c\u6c14\u5598\u5401 \u5401\u7adf\u662f\u8bf4\u4e0d\u51fa\u8bdd\u3002\u539f\u6765\u56db\u4eba\u672c\u88ab\u70b9\u4e86\u54d1\u7a74\uff0c\u6043\u7740\u5185\u529f\u90fd\u6709\u706b\u5019\uff0c\u5f3a\u81ea\u8fd0\u6c14\u51b2\u5173\uff0c\u6240\u4ee5\u5589\u5934\u53d1 \u51fa\u602a\u58f0\uff0c\u7a74\u9053\u4e00\u89e3\uff0c\u52b2\u6c14\u5916\u5192\uff0c\u5589\u5499\u8fa3\u75db\uff0c\u8eab\u75b2\u9aa8\u8f6f\uff0c\u7adf\u5982\u5927\u75c5\u4e86\u4e00\u573a\u3002 \u3000\u3000\u4e91\u857e\u6253\u9192\u7cbe\u795e\uff0c\u58ee\u8d77\u80c6\u5b50\uff0c\u94bb\u51fa\u7a97\u5916\uff0c\u4e00\u7eb5\u8eab\u53c8\u4e0a\u4e86\u56db\u697c\u7684\u98de\u6a90\u3002\u5ffd\u542c\u5f97\u9876\u697c\u4e0a\u77f3\u82f1\u7684 \u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u7236\u5b50\u4e24\u4ee3\u5df2\u7b49\u4e86\u516d\u5341\u5e74\u4e86\uff0c\u4f60\u4e0d\u80af\u9732\u51fa\u771f\u5bb9\u4e0e\u6211\u76f8\u89c1\u4e48\uff1f\u201d\u4e91\u857e\u6025\u6025\u98de\u8eab \u76f4\u4e0a\u3002 \u3000\u3000\u9876\u697c\u4e0a\u70db\u5f71\u6447\u7ea2\uff0c\u4e91\u857e\u52fe\u7740\u6a90\u89d2\uff0c\u4e00\u773c\u77a5\u53bb\uff0c\u53ea\u89c1\u4e00\u4e2a\u4eba\u5f71\u80cc\u7740\u81ea\u5df1\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u62ff \u6765\uff01\u201d\u8fd9\u58f0\u97f3\u7adf\u5728\u4ec0\u4e48\u5730\u65b9\u542c\u8fc7\u4f3c\u7684\uff01\u53ea\u89c1\u77f3\u82f1\u5c06\u5899\u4e0a\u6240\u6302\u7684\u90a3\u5e45\u753b\u53d6\u4e0b\uff0c\u5377\u6210\u4e00\u5377\uff0c\u90a3\u5f71 \u5b50\u7a81\u7136\u4f38\u51fa\u53cc\u624b\uff0c\u4e00\u624b\u53d6\u753b\uff0c\u4e00\u624b\u7adf\u4f3c\u5411\u77f3\u82f1\u5f53\u5934\u62cd\u4e0b\u3002\u4e91\u857e\u5927\u53eb\u4e00\u58f0\uff0c\u957f\u8eab\u98de\u8d77\u3002\u731b\u542c\u5f97 \u547c\u7684\u4e00\u58f0\uff0c\u6697\u5668\u631f\u98ce\uff0c\u8fce\u9762\u5954\u5230\uff0c\u4e91\u857e\u626c\u5251\u4e00\u6321\uff0c\u53ea\u89c9\u4e00\u80a1\u5927\u529b\uff0c\u6709\u5982\u5954\u96f7\u538b\u9876\uff0c\u706b\u82b1\u56db\u6e85 \u4e4b\u4e2d\uff0c\u6697\u5668\u56fa\u7136\u662f\u88ab\u9707\u5f97\u7c89\u788e\uff0c\u4e91\u857e\u4e5f\u7ed9\u9707\u5f97\u7ad9\u4e0d\u7740\u811a\uff0c\u7a81\u7136\u4e00\u8db3\u8e0f\u7a7a\uff0c\u4ece\u9876\u697c\u6a90\u89d2\u5012\u8dc3\u4e0b \u53bb\uff01\u5e78\u5f97\u4e91\u857e\u6b66\u529f\u4e0d\u5f31\uff0c\u4f38\u8db3\u4e00\u52fe\uff0c\u53c8\u52fe\u7740\u4e86\u5c4b\u6a90\u3002 \u3000\u3000\u9ed1\u591c\u4e4b\u4e2d\uff0c\u547c\u547c\u98ce\u54cd\uff0c\u7b2c\u4e8c\u9053\u6697\u5668\u53c8\u5954\u4e86\u4e0b\u6765\uff0c\u53d1\u6697\u5668\u4e4b\u4eba\uff0c\u7528\u7684\u7adf\u662f\u8fde\u73e0\u624b\u6cd5\uff0c\u4e91\u857e \u6697\u7528\u201c\u5343\u65a4\u5760\u201d\u7684\u91cd\u8eab\u6cd5\uff0c\u52fe\u5b9e\u5c4b\u6a90\uff0c\u9752\u51a5\u5251\u626c\u7a7a\u4e00\u51fb\uff0c\u706b\u82b1\u98de\u6e85\u4e4b\u4e2d\uff0c\u6697\u5668\u88c2\u6210\u65e0\u6570\u788e \u7247\u3002\u8fd9\u6697\u5668\u539f\u6765\u662f\u4e00\u5757\u77f3\u5934\u3002\u4e91\u857e\u51fb\u788e\u6697\u5668\uff0c\u5411\u4e0a\u671b\u53bb\uff0c\u5ffd\u89c1\u77f3\u82f1\u63a2\u51fa\u5934\u6765\uff0c\u5927\u58f0\u559d\u9053\uff1a \u201c\u662f\u8c01\uff1f\u201d\u5ffd\u800c\u58f0\u8c03\u4e00\u53d8\uff0c\u60ca\u9053\uff1a\u201c\u857e\u513f\uff0c\u662f\u4f60\u4e48\uff1f\u4e0d\u5e72\u4f60\u4e8b\uff0c\u5feb\u5feb\u8eb2\u5f00\uff01\u201d \u3000\u3000\u4e91\u857e\u8fd9\u4e00\u60ca\u66f4\u662f\u975e\u540c\u5c0f\u53ef\uff0c\u770b\u90a3\u8d3c\u4eba\u5206\u660e\u662f\u8981\u52ab\u77f3\u82f1\u7684\u5b9d\u7269\uff0c\u4f55\u4ee5\u77f3\u82f1\u53cd\u800c\u52a9\u4ed6\uff1f\u7adf\u53d1 \u51fa\u98de\u8757\u77f3\u963b\u4eba\u63f4\u6551\uff1f\u8fd9\u65f6\u85cf\u5b9d\u697c\u4e0b\uff0c\u4eba\u5f71\u5e62\u5e62\uff0c\u5df2\u6709\u8d3a\u5bff\u7684\u5ba2\u4eba\u8d76\u4e86\u524d\u6765\uff0c\u4e91\u857e\u8fd8\u672a\u53ca\u8eb2 \u5f00\uff0c\u5ffd\u89c1\u77f3\u82f1\u8dc3\u4e86\u51fa\u6765\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u8d3c\u4eba\u5df2\u7ed9\u6211\u6253\u8dd1\u4e86\u6ca1\u4e8b\u4e86\uff0c\u5927\u5bb6\u90fd\u56de\u53bb\u5427\uff01\u201d\u4e91\u857e\u773c \u5229\uff0c\u5ffd\u89c1\u90a3\u6761\u4eba\u5f71\uff0c\u4ece\u80cc\u9762\u7684\u7a97\u5b50\u7a7f\u7a97\u98de\u51fa\uff0c\u8f7b\u7075\u8fc5\u75be\u4e4b\u6781\uff0c\u4e91\u857e\u4e0d\u5047\u601d\u7d22\uff0c\u98de\u8eab\u4e00\u8f6c\uff0c\u63a0 \u5230\u5c4b\u6a90\u7684\u53e6\u4e00\u8fb9\uff0c\u90a3\u4eba\u5f71\u5df2\u7eb5\u5230\u8fb9\u62a4\u9662\u7684\u5899\u4e0a\u3002\u4e91\u857e\u65bd\u5c55\u4e0a\u4e58\u8f7b\u529f\uff0c\u98de\u8eab\u6251\u53bb\uff0c\u4f46\u89c1\u90a3\u4eba\u4ece \u5899\u5934\u98de\u8d77\uff0c\u5728\u534a\u7a7a\u4e4b\u4e2d\uff0c\u7a81\u7136\u626d\u8f6c\u5934\u6765\u4f38\u624b\u5411\u4e91\u857e\u4e00\u62db\uff0c\u90a3\u4eba\u9762\u4e0a\u8499\u7740\u9ed1\u5dfe\uff0c\u53ea\u9732\u51fa\u4e00\u53cc\u773c \u775b\uff0c\u4e91\u857e\u770b\u4e0d\u6e05\u695a\uff0c\u4ecd\u7136\u98de\u8eab\u8ffd\u8d76\u3002 \u3000\u3000\u5899\u5916\u662f\u4e00\u7247\u6811\u6797\uff0c\u6811\u6797\u4e2d\u5ffd\u542c\u5f97\u4e00\u58f0\u9a6c\u5636\uff0c\u6708\u5149\u4e4b\u4e0b\uff0c\u53ea\u89c1\u4e00\u5339\u767d\u9a6c\u4ece\u6797\u4e2d\u8dd1\u51fa\uff0c\u4e91\u857e \u4e00\u89c1\uff0c\u53c8\u662f\u5927\u5403\u4e00\u60ca\uff0c\u8fd9\u767d\u9a6c\u795e\u9a8f\u975e\u51e1\uff0c\u6b63\u662f\u524d\u65e5\u76f8\u9047\u7684\u90a3\u4e2a\u4e66\u751f\u7684\u5750\u9a91\uff01\u4e91\u857e\u5413\u5f97\u5446\u4e86\uff0c \u6b64\u4e8b\u771f\u662f\u4e07\u5206\u96be\u89e3\uff1a\u524d\u65e5\u76f8\u8bd5\uff0c\u90a3\u4e66\u751f\u5206\u660e\u4e0d\u4f1a\u6b66\u529f\uff0c\u4f55\u4ee5\u7adf\u4f1a\u5230\u6b64\u76d7\u5b9d\uff1f\u90a3\u8499\u9762\u4e4b\u4eba\u5230\u5e95 \u662f\u4e0d\u662f\u4ed6\uff1f\u800c\u4e14\u5230\u5e95\u662f\u4e0d\u662f\u76d7\u5b9d\uff0c\u4ea6\u5c5e\u96be\u77e5\u3002\u82e5\u8bf4\u662f\u201c\u76d7\u5b9d\u201d\uff0c\u4f55\u4ee5\u90a3\u56db\u4e2a\u5ba2\u5546\u7684\u73e0\u5b9d\uff0c\u4ed6 \u5168\u4e0d\u53d6\uff0c\u53ea\u53d6\u4e86\u4e00\u5f20\u753b\u53bb\uff0c\u96be\u9053\u90a3\u5f20\u753b\u6bd4\u4ef7\u503c\u8fde\u57ce\u7684\u73e0\u5b9d\u66f4\u503c\u94b1\uff1f\u5c1a\u6709\u4e00\u70b9\u66f4\u53ef\u7591\u7684\u662f\uff0c\u90a3 \u4e66\u751f\u770b\u6765\u53ea\u662f\u4e8c\u5341\u591a\u5c81\u7684\u5c11\u5e74\u4eba\uff0c\u4f55\u4ee5\u9002\u624d\u77f3\u82f1\u53c8\u8bf4\u7b49\u4e86\u4ed6\u516d\u5341\u5e74\uff1f \u3000\u3000\u79cd\u79cd\u7591\u56e2\uff0c\u6a2a\u585e\u80f8\u81c6\uff0c\u4e91\u857e\u6b63\u5728\u63a8\u6572\uff0c\u5ffd\u542c\u5f97\u540e\u9762\u4eba\u58f0\u5608\u6742\uff0c\u77f3\u82f1\u5927\u58f0\u53eb\u9053\uff1a\u201c\u7a77\u5bc7\u83ab \u8ffd\uff0c\u857e\u513f\u56de\u6765\uff01\u201d\u4e91\u857e\u66f4\u662f\u7591\u60d1\u4e07\u5206\uff0c\u770b\u77f3\u82f1\u4eca\u665a\u6240\u4f5c\u4e4b\u4e8b\uff0c\u7adf\u662f\u5904\u5904\u62a4\u7740\u90a3\u4e2a\u8d3c\u4eba\u3002\u4e91\u857e \u5e74\u5c11\u597d\u5947\uff0c\u975e\u4f46\u4e0d\u542c\u77f3\u82f1\u4e4b\u8bdd\uff0c\u53cd\u800c\u8eab\u5f62\u6025\u8d77\uff0c\u98de\u51fa\u5899\u5916\uff0c\u5ffd\u53c8\u542c\u5f97\u6797\u5b50\u91cc\u4e00\u58f0\u9a6c\u5636\uff0c\u4e91\u857e \u4e3e\u9996\u4e00\u770b\uff0c\u66f4\u662f\u60ca\u5f02\uff01 \u3000\u3000\u4ece\u6797\u4e2d\u8dd1\u51fa\u7684\u90a3\u5339\u7ea2\u9b03\u9a6c\uff0c\u6b63\u662f\u4e91\u857e\u7684\u5750\u9a91\uff0c\u4e91\u857e\u8bb0\u5f97\u8fd9\u5339\u9a6c\u4e43\u662f\u624e\u5728\u9ed1\u77f3\u5e84\u524d\uff0c\u4e0d\u77e5 \u600e\u7684\u7adf\u4f1a\u5230\u4e86\u6797\u5b50\u91cc\u9762\uff1f\u90a3\u8499\u9762\u602a\u5ba2\u8fd9\u65f6\u5df2\u8de8\u4e0a\u9a6c\u80cc\uff0c\u5374\u5e76\u4e0d\u50ac\u9a6c\u524d\u884c\uff0c\u56de\u8fc7\u5934\u6765\uff0c\u53c8\u5411\u4e91 \u857e\u62db\u624b\uff0c\u8fd9\u56de\u4e91\u857e\u770b\u5f97\u8f83\u4e3a\u6e05\u695a\uff0c\u867d\u7136\u8fd8\u672a\u6562\u65ad\u5b9a\uff0c\u4f46\u90a3\u4eba\u7684\u8eab\u6750\u5374\u5341\u5206\u4f3c\u90a3\u4e66\u751f\u3002\u8fd9\u4e00\u4e0b \u60f9\u5f97\u4e91\u857e\u5fc3\u4e2d\u706b\u8d77\uff0c\u9a82\u9053\uff1a\u201c\u5140\u4f60\u8fd9\u25a1\uff0c\u7adf\u6562\u4e24\u6b21\u4e09\u756a\uff0c\u524d\u6765\u620f\u6211\uff01\u201d\u98de\u8eab\u4e0a\u9a6c\uff0c\u53cc\u817f\u4e00 \u5939\uff0c\u50ac\u9a6c\u4fbf\u8ffd\u3002\u90a3\u5339\u767d\u9a6c\u56db\u8e44\u4e00\u8d77\uff0c\u8fc5\u903e\u8ffd\u98ce\uff0c\u7728\u773c\u4e4b\u95f4\u51b2\u51fa\u6797\u5b50\u3002\u4e91\u857e\u542c\u540e\u9762\u9a6c\u8e44\u4e4b\u58f0\uff0c \u77e5\u662f\u77f3\u82f1\u7387\u9886\u5e84\u4e01\u7b56\u9a6c\u8ffd\u8d76\uff0c\u66f4\u662f\u653e\u9a6c\u98de\u9a70\u3002\u90a3\u5339\u201c\u7167\u591c\u72ee\u5b50\u9a6c\u201d\u56fa\u7136\u662f\u4e16\u4e0a\u7f55\u89c1\u7684\u767d\u9a6c\uff0c \u5373\u4e91\u857e\u8fd9\u5339\u5750\u9a91\uff0c\u4e5f\u662f\u5343\u4e2d\u9009\u4e00\u7684\u8499\u53e4\u6218\u9a6c\uff0c\u9ed1\u77f3\u5e84\u7684\u9a6c\u5339\u54ea\u91cc\u8ffd\u8d76\u5f97\u4e0a\uff1f\u4e0d\u6d88\u7247\u523b\uff0c\u4e24\u5339 \u9a6c\u90fd\u9a70\u4e0a\u4e86\u4ece\u9633\u66f2\u897f\u53bb\u4eac\u90fd\u7684\u5927\u9053\u3002 \u3000\u3000\u8499\u9762\u4eba\u7684\u767d\u9a6c\u4e00\u76f4\u5728\u4e91\u857e\u534a\u91cc\u4e4b\u5916\uff0c\u770b\u770b\u4e91\u857e\u8ffd\u8d76\u4e0d\u4e0a\uff0c\u53c8\u653e\u6162\u4e0b\u6765\uff0c\u4e91\u857e\u53c8\u662f\u6c14\u607c\uff0c \u53c8\u662f\u597d\u5947\uff0c\u6025\u6b32\u63ed\u7834\u5fc3\u4e2d\u4e4b\u8ff7\uff0c\u4e5f\u4e0d\u987e\u524d\u9762\u6709\u4f55\u5371\u9669\uff0c\u4e00\u80a1\u52b2\u5730\u5f80\u524d\u76f4\u8ffd\uff01 \u3000\u3000\u8ffd\u98ce\u8e0f\u6708\uff0c\u9a8f\u9a6c\u98de\u9a70\uff0c\u4e00\u524d\u4e00\u540e\uff0c\u8ffd\u9010\u4e86\u767e\u6570\u5341\u91cc\uff0c\u6b8b\u6708\u897f\u4e0b\uff0c\u6653\u98ce\u4e91\u5f00\uff0c\u4e0d\u77e5\u4e0d\u89c9\u5df2 \u662f\u6e05\u6668\u65f6\u5206\uff0c\u4e5f\u4e0d\u77e5\u8ffd\u5230\u4e86\u4ec0\u4e48\u5730\u65b9\uff0c\u4f46\u89c1\u524d\u9762\u53c8\u662f\u4e00\u7247\u4e1b\u6797\uff0c\u8499\u9762\u4eba\u56de\u5934\u53eb\u4e00\u58f0\u9053\uff1a\u201c\u5931 \u966a\u4e86\uff01\u201d\u767d\u9a6c\u56db\u8e44\u7ffb\u98de\uff0c\u6ca1\u5165\u6797\u4e2d\u3002 \u3000\u3000\u4e91\u857e\u6012\u9053\uff1a\u201c\u4f60\u8dd1\u5230\u5929\u8fb9\uff0c\u6211\u4e5f\u8981\u8ffd\u4f60\uff01\u201d\u62cd\u9a6c\u98de\u8d76\uff0c\u521a\u5230\u6797\u8fb9\uff0c\u5ffd\u542c\u5f97\u767d\u9a6c\u5636\u9e23\uff0c\u6797 \u5b50\u4e2d\u6709\u4eba\u602a\u5578\uff01\u4e91\u857e\u4e00\u52d2\u9a6c\u25a1\uff0c\u53ea\u89c1\u90a3\u5339\u767d\u9a6c\u95ea\u7535\u822c\u98de\u5954\u51fa\u6765\uff0c\u9a6c\u80cc\u4e0a\u7684\u4eba\u5df2\u4e0d\u89c1\u4e86\u3002\u4e91\u857e \u5403\u4e86\u4e00\u60ca\uff1a\u90a3\u8499\u9762\u4eba\u7684\u6b66\u529f\u975e\u540c\u5c0f\u53ef\uff0c\u96be\u9053\u7adf\u7136\u7ed9\u6697\u7b97\uff0c\u53ea\u9003\u51fa\u8fd9\u5339\u6765\u6765\uff1f \u3000\u3000\u6797\u5b50\u91cc\u602a\u5578\u4e4b\u540e\uff0c\u53c8\u4f20\u6765\u4e86\u547c\u559d\u4e4b\u58f0\uff0c\u4e91\u857e\u7565\u4e00\u601d\u7d22\uff0c\u7ffb\u8eab\u4e0b\u9a6c\uff0c\u65bd\u5c55\u4e0a\u4e58\u8f7b\u529f\uff0c\u8df3\u5230 \u4e00\u68f5\u6811\u4e0a\uff0c\u53ea\u89c1\u6797\u5b50\u4e2d\u8ffd\u51fa\u6570\u4eba\u53eb\u9053\uff1a\u201c\u53ef\u60dc\uff0c\u53ef\u60dc\uff01\u7ed9\u90a3\u767d\u9a6c\u8dd1\u4e86\uff01\u54a6\uff0c\u8fd8\u6709\u4e00\u5339\u7ea2\u9a6c\uff0c \u5440\u53ef\u60dc\u4e5f\u8dd1\u4e86\uff01\u201d\u4e91\u857e\u7684\u9a6c\u662f\u4e45\u7ecf\u8bad\u7ec3\u7684\u6218\u9a6c\uff0c\u61c2\u5f97\u81ea\u884c\u8eb2\u907f\uff0c\u4f46\u53ea\u8981\u4e3b\u4eba\u53eb\u5524\uff0c\u53c8\u4f1a\u56de \u6765\u3002\u4e91\u857e\u4e0d\u7528\u62c5\u5fc3\uff0c\u5728\u6811\u679d\u4e0a\u5c55\u5f00\u8f7b\u7075\u7684\u8eab\u6cd5\uff0c\u4ece\u8fd9\u4e00\u67e5\u8df3\u5230\u53e6\u4e00\u68f5\u6811\uff0c\u7247\u523b\u4e4b\u95f4\u5df2\u5230\u8302\u6797 \u6df1\u5904\u3002 \u3000\u3000\u6797\u4e2d\u4eba\u8bed\u5608\u6742\uff0c\u4e91\u857e\u9690\u4e86\u8eab\u5f62\uff0c\u5077\u5077\u7aa5\u4e0b\uff0c\u89c1\u524d\u65e5\u6240\u9047\u7684\u90a3\u4e2a\u4e66\u751f\u7b95\u8e1e\u5728\u4e00\u5757\u5ca9\u77f3\u4e4b \u4e0a\uff0c\u4ed6\u7684\u8499\u5dfe\u5df2\u89e3\u5f00\u4e86\u3002\u5728\u4ed6\u5468\u56f4\u9ad8\u9ad8\u77ee\u77ee\uff0c\u56f4\u7740\u4e86\u4e03\u516b\u4e2a\u4eba\uff0c\u6c99\u6d9b\u7236\u5b50\u4e5f\u5728\u5176\u5185\uff0c\u53e6\u5916\u8fd8 \u6709\u4e00\u4e2a\u62ab\u53d1\u5934\u9640\uff0c\u4e00\u4e2a\u9752\u8863\u9053\u58eb\uff0c\u76f8\u8c8c\u5947\u7279\uff0c\u6700\u4e3a\u60f9\u4eba\u6ce8\u76ee\u3002 \u3000\u3000\u53ea\u542c\u5f97\u6c99\u6d9b\u51b7\u51b7\u7b11\u9053\uff1a\u201c\u9976\u4f60\u8fd9\u25a1\u6e9c\u6ed1\uff0c\u4e5f\u7ec8\u96be\u9003\u6211\u7684\u638c\u5fc3\uff0c\u4f60\u60f3\u8981\u547d\u4e48\uff1f\u201d\u90a3\u4e66\u751f\u6447 \u5934\u6446\u8111\u9053\uff1a\u201c\u592b\u877c\u8681\u5c1a\u4e14\u8d2a\u751f\uff0c\u51b5\u5c5e\u4eba\u4e4e\uff1f\u201d\u6c99\u6d9b\u9053\uff1a\u201c\u4f60\u65e2\u7136\u8981\u547d\uff0c\u5feb\u5feb\u628a\u4f60\u7684\u7167\u591c\u72ee\u5b50 \u9a6c\u5524\u56de\u6765\uff01\u4f60\u7684\u73e0\u5b9d\u6211\u4eec\u53ef\u4ee5\u4e0d\u8981\uff0c\u8fd9\u5339\u9a6c\u5374\u662f\u975e\u8981\u4e0d\u53ef\uff01\u201d\u90a3\u4e66\u751f\u53c8\u6447\u6447\u5934\u9053\uff1a\u201c\u5b9d\u9a6c\u795e \u9a79\uff0c\u5c82\u80fd\u8f7b\u6613\u6613\u624b\uff01\u201d\u6c99\u6d9b\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u4f60\u7684\u4fdd\u9556\u5df2\u5728\u9ed1\u77f3\u5e84\u4f5c\u5a07\u5ba2\u4e86\uff0c\u8c01\u6765\u66ff\u4f60\u4fdd\u9a7e\uff1f\u201d \u90a3\u4e66\u751f\u5ffd\u7136\u628a\u624b\u4e00\u6307\u9053\uff1a\u201c\u575a\u5b50\u4f55\u77e5\uff0c\u6211\u4e4b\u4fdd\u9556\u6765\u77e3\uff01\u201d\u5ffd\u7136\u58f0\u8c03\u4e00\u8f6c\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u4fdd\u9556 \u7684\u4f60\u8fd8\u4e0d\u5feb\u5feb\u4e0b\u6765\u6551\u9a7e\u4e48\uff1f\u201d\u6b63\u662f\uff1a \u3000\u3000\u6ce2\u8c32\u4e91\u8be1\u96be\u9884\u6d4b\uff0c\u67f3\u6697\u82b1\u660e\u53c8\u4e00\u6751\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u840d\u8e2a\u4fa0\u5f71\u5f55\u300b\u2014\u2014\u7b2c\u4e94\u56de\u3000\u540d\u58eb\u620f\u4eba\u95f4\u4ea6\u72c2\u4ea6\u4fa0\u3000\u5947\u884c\u8fc8\u6d41\u4fd7\u80fd\u54ed\u80fd\u6b4c \u6881\u7fbd\u751f\u300a\u840d\u8e2a\u4fa0\u5f71\u5f55\u300b \u7b2c\u4e94\u56de\u3000\u540d\u58eb\u620f\u4eba\u95f4\u4ea6\u72c2\u4ea6\u4fa0\u3000\u5947\u884c\u8fc8\u6d41\u4fd7\u80fd\u54ed\u80fd\u6b4c \u3000\u3000\u90a3\u4e66\u751f\u628a\u624b\u4e00\u6307\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u4fdd\u9556\u7684\u4f60\u8fd8\u4e0d\u5feb\u5feb\u4e0b\u6765\u6551\u9a7e\u4e48\uff1f\u201d\u4e91\u857e\u51b7\u4e0d\u9632\u7ed9\u4ed6\u4e00\u53e3 \u559d\u7834\u884c\u85cf\uff0c\u5fc3\u4e2d\u867d\u662f\u6c14\u607c\uff0c\u5374\u4e5f\u4e0d\u5f97\u4e0d\u98d8\u7136\u843d\u5730\u3002\u90a3\u62ab\u53d1\u5934\u9640\u9762\u8272\u4e00\u53d8\u4e00\u626c\u624b\u5c31\u662f\u4e09\u679d\u5229 \u9556\uff0c\u8054\u7fe9\u98de\u81f3\uff0c\u4e91\u857e\u8eab\u5b50\u60ac\u7a7a\uff0c\u5c1a\u672a\u62d4\u5251\uff0c\u62b5\u6321\u4e0d\u5f97\uff0c\u8eb2\u95ea\u4ea6\u96be\uff0c\u5ffd\u542c\u5f97\u53ee\u53ee\u53ee\u4e09\u58f0\u54cd\uff0c\u90a3 \u5934\u9640\u6240\u53d1\u7684\u4e09\u679d\u5229\u9556\u5168\u90fd\u843d\u5728\u5730\u4e0a\u3002\u5934\u9640\u5927\u5403\u4e00\u60ca\uff0c\u4f38\u624b\u53c8\u53d6\u6697\u5668\uff0c\u6c99\u6d9b\u6c89\u58f0\u8bf4\u9053\uff1a\u201c\u4e14 \u6162\uff0c\u8c05\u8fd9\u5c0f\u5b50\u63d2\u7ffc\u96be\u98de\uff01\u201d\u628a\u624b\u4e00\u6325\uff0c\u4e03\u516b\u4e2a\u4eba\u56db\u8fb9\u7ad9\u5b9a\uff0c\u5c06\u4e91\u857e\u56f4\u5728\u6838\u5fc3\u3002 \u3000\u3000\u6c99\u65e0\u5fcc\u4e00\u89c1\u4e91\u857e\uff0c\u53c8\u5992\u53c8\u6068\uff0c\u773c\u90fd\u7ea2\u4e86\uff0c\u78d4\u78d4\u602a\u7b11\uff0c\u626c\u58f0\u559d\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u4f60\u4e0d\u5728\u9ed1\u77f3 \u5e84\u4f5c\u5a07\u5ba2\uff0c\u5230\u8fd9\u91cc\u505a\u4ec0\u4e48\uff1f\u8f70\u5929\u96f7\u7684\u624b\u81c2\u518d\u957f\uff0c\u4e5f\u4e0d\u80fd\u4f38\u5230\u8fd9\u513f\u5e87\u62a4\u4f60\u4e86\uff01\u201d\u626c\u5200\u6b32\u4e0a\uff0c\u6c99 \u6d9b\u4e00\u628a\u62c9\u4f4f\u95ee\u4e91\u857e\u9053\uff1a\u201c\u662f\u77f3\u82f1\u53eb\u4f60\u6765\u7684\u4e48\uff1f\u201d\u6c99\u6d9b\u5fcc\u60ee\u77f3\u82f1\uff0c\u672a\u95ee\u6e05\u695a\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u5c1a\u672a \u6562\u9020\u6b21\u3002\u90a3\u4e66\u751f\u7b95\u8e1e\u5ca9\u77f3\u4e4b\u4e0a\uff0c\u54c8\u54c8\u5927\u7b11\uff0c\u63a5\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u8bf4\u7684\u8bdd\uff0c\u4f60\u4eec\u542c\u4e0d\u89c1\u4e48\uff1f\u662f\u6211\u53eb \u4ed6\u6765\u7684\uff01\u4ed6\u662f\u6211\u7684\u4fdd\u9556\uff0c\u4f60\u4eec\u8981\u8c0b\u6211\u7684\u8d22\uff0c\u5bb3\u6211\u7684\u547d\uff0c\u4ed6\u600e\u80fd\u591f\u4e0d\u6765\uff1f\u4fdd\u9556\u7684\uff0c\u4f60\u5403\u6211\u7684\uff0c \u559d\u6211\u7684\uff0c\u6211\u800c\u4eca\u9047\u96be\uff0c\u4f60\u600e\u4e48\u8fd8\u4e0d\u52a8\u624b\u5440\uff1f\u201d \u3000\u3000\u6c99\u6d9b\u559d\u9053\uff1a\u201c\u679c\u771f\u4e0e\u8f70\u5929\u96f7\u65e0\u5173\u4e48\uff1f\u201d\u4e91\u857e\u751a\u662f\u6c14\u607c\uff0c\u53ef\u662f\u5728\u6b64\u60c5\u5f62\u4e4b\u4e0b\uff0c\u52bf\u53c8\u4e0d\u80fd\u4e0d \u4e3a\u4e66\u751f\u52a8\u624b\uff0c\u9752\u51a5\u5b9d\u5251\uff0c\u62d4\u5728\u624b\u4e2d\uff0c\u6012\u58f0\u559d\u9053\uff1a\u201c\u4ec0\u4e48\u8f70\u5929\u96f7\uff0c\u8f70\u5730\u96f7\uff1f\u4ffa\u5c31\u662f\u51ed\u8fd9\u53e3\u624b\u4e2d \u5229\u5251\uff0c\u72ec\u6765\u72ec\u5f80\uff0c\u4ece\u4e0d\u85cf\u5978\u5f04\u9b3c\uff0c\u7f29\u5728\u4e00\u8fb9\uff0c\u53eb\u522b\u4eba\u51fa\u5934\uff01\u201d\u8fd9\u8bdd\u660e\u662f\u9a82\u8d3c\uff0c\u6697\u4e2d\u5b9e\u662f\u9a82\u90a3 \u4e66\u751f\u3002\u90a3\u4e66\u751f\u53c8\u662f\u54c8\u54c8\u5927\u7b11\uff0c\u9053\uff1a\u201c\u597d\u5440\uff0c\u597d\u5440\uff01\u8fd9\u4e2a\u4fdd\u9556\u8bf7\u5f97\u4e0d\u9519\uff0c\u679c\u7136\u662f\u4e2a\u6709\u79cd\u7684\uff01\u201d \u6c99\u6d9b\u4e00\u58f0\u602a\u7b11\uff0c\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u65e2\u7136\u4e0e\u8f70\u5929\u96f7\u65e0\u5173\uff0c\u90a3\u5c31\u662f\u4f60\u7684\u6b7b\u671f\u5230\u4e86\uff01\u201d\u53cc\u638c\u4e00\u9519\uff0c\u8fde \u73af\u62cd\u51fa\uff0c\u90a3\u62ab\u53d1\u5934\u9640\u548c\u9752\u8863\u9053\u58eb\u4e5f\u63c9\u8eab\u75be\u4e0a\uff0c\u7fa4\u8d77\u56f4\u653b\u3002 \u3000\u3000\u4e91\u857e\u4e00\u4e2a\u76d8\u9f99\u7ed5\u6b65\uff0c\u9752\u51a5\u5251\u626c\u7a7a\u4e00\u95ea\u4fbf\u7167\u6c99\u6d9b\u80a9\u540e\u7684\u201c\u51e4\u5e9c\u7a74\u201d\u75be\u523a\uff0c\u5ffd\u542c\u5f97\u201c\u5f53\u201d\u7684 \u4e00\u58f0\uff0c\u90a3\u5934\u9640\u6212\u5200\u4e00\u7acb\uff0c\u5c06\u4e91\u857e\u9707\u5f97\u864e\u53e3\u53d1\u9ebb\uff0c\u731b\u5730\u91cc\u9752\u5149\u4e00\u95ea\uff0c\u90a3\u9752\u8863\u9053\u58eb\u7684\u957f\u5251\u53c8\u582a\u582a \u523a\u5230\uff0c\u4e91\u857e\u6025\u5c55\u201c\u7a7f\u82b1\u7ed5\u6811\u201d\u7684\u8eab\u6cd5\uff0c\u659c\u91cc\u4e00\u95ea\uff0c\u672a\u53ca\u56de\u7738\uff0c\u53ea\u542c\u5f97\u5237\u7684\u4e00\u58f0\uff0c\u8863\u8896\u5df2\u7ed9\u5251 \u5c16\u6495\u53bb\u4e00\u5757\uff01\u90a3\u5934\u9640\u4e0e\u4e91\u857e\u5200\u5251\u76f8\u4ea4\uff0c\u867d\u628a\u4e91\u857e\u9707\u9000\uff0c\u6212\u5200\u5374\u4e5f\u7f3a\u4e86\u4e00\u53e3\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u8fd9 \u5c0f\u5b50\u4f7f\u7684\u4e43\u662f\u5b9d\u5251\uff01\u201d\u9752\u8863\u9053\u58eb\u7b11\u9053\uff1a\u201c\u597d\u6781\uff0c\u597d\u6781\uff01\u540d\u9a6c\u5b9d\u5251\u90fd\u5df2\u6709\u4e86\uff01\u201d\u56de\u5251\u4e00\u524a\uff0c\u4e91 \u857e\u53cd\u5251\u76f8\u8fce\uff0c\u4e0d\u6599\u90a3\u9053\u58eb\u500f\u7136\u4e00\u7f29\uff0c\u5251\u5230\u4e2d\u9014\uff0c\u7a81\u7136\u53d8\u52bf\u4e0b\u523a\uff0c\u559d\u9053\uff1a\u201c\u7740\uff01\u201d\u9053\u58eb\u53d8\u62db\u5df2 \u5feb\uff0c\u4e91\u857e\u53d8\u62db\u66f4\u5feb\uff0c\u4e00\u62db\u201c\u98a0\u5012\u9634\u9633\u201d\uff0c\u4e0a\u4e0b\u6613\u4f4d\uff0c\u75be\u523a\u9053\u58eb\u5c0f\u8179\uff0c\u968f\u7740\u5251\u52bf\uff0c\u5251\u8bc0\u4e00\u6307\uff0c \u4e5f\u559d\u58f0\uff1a\u201c\u7740\uff01\u201d\u4e91\u857e\u7684\u5e08\u7956\u7384\u673a\u9038\u58eb\u5f53\u5e74\u521b\u4e86\u4e24\u5957\u5251\u6cd5\uff0c\u4e00\u5957\u540d\u4e3a\u201c\u767e\u53d8\u9634\u9633\u7384\u673a\u5251\u201d\uff0c \u4e00\u5957\u540d\u4e3a\u201c\u4e07\u6c49\u671d\u6d77\u5143\u5143\u5251\u201d\u3002\u201c\u767e\u53d8\u9634\u9633\u201d\u5251\u6cd5\uff0c\u987e\u540d\u601d\u4e49\uff0c\u4e43\u662f\u4ee5\u5947\u8be1\u89c1\u957f\uff0c\u8fd9\u4e00\u62db \u201c\u98a0\u5012\u9634\u9633\u201d\uff0c\u5c24\u662f\u5176\u4e2d\u5999\u7740\uff0c\u672c\u4ee5\u4e3a\u9053\u58eb\u975e\u4e2d\u5251\u4e0d\u53ef\uff0c\u4e0d\u6599\u4e00\u5251\u523a\u51fa\uff0c\u53ea\u542c\u5f97\u201c\u5237\u201d\u7684\u4e00 \u58f0\u6420\u4e86\u4e2a\u7a7a\uff0c\u5934\u9640\u7684\u6212\u5200\u5df2\u659c\u523a\u5288\u5230\uff01 \u3000\u3000\u9976\u662f\u90a3\u9053\u58eb\u8eb2\u95ea\u5f97\u5feb\uff0c\u675f\u9053\u888d\u7684\u4e1d\u5e26\u5df2\u7ed9\u4e91\u857e\u5229\u5251\u5272\u65ad\uff0c\u5413\u51fa\u4e00\u8eab\u51b7\u6c57\u3002\u4e91\u857e\u8fd9\u4e00\u62db\u7edd \u5999\u5251\u6cd5\uff0c\u523a\u4e0d\u7740\u90a3\u9053\u58eb\uff0c\u4e5f\u662f\u5403\u4e86\u4e00\u60ca\uff0c\u817e\u632a\u95ea\u5c55\u4e4b\u4e0b\uff0c\u67b6\u5f00\u4e86\u5934\u9640\u7684\u6212\u5200\uff0c\u8eb2\u5f00\u4e86\u6c99\u6d9b\u7684 \u4e00\u6293\uff0c\u9752\u8863\u9053\u58eb\u53c8\u63d0\u5251\u51b2\u4e0a\u3002\u6c99\u65e0\u5fcc\u53eb\u9053\uff1a\u201c\u6349\u4e0d\u4e86\u6d3b\u7684\uff0c\u6b7b\u7684\u4e5f\u884c\uff01\u5e76\u80a9\u5b50\u4e0a\u5475\uff0c\u4e71\u5200\u65ab \u8fd9\u5c0f\u5b50\uff01\u201d\u7387\u9886\u76d7\u515a\uff0c\u5c06\u4e91\u857e\u56f4\u5f97\u4ecb\u98ce\u96e8\u4e0d\u900f\u3002 \u3000\u3000\u6c99\u5bb6\u7236\u5b50\u5df2\u975e\u5eb8\u624b\uff0c\u90a3\u62ab\u53d1\u5934\u9640\u548c\u9752\u8863\u9053\u58eb\uff0c\u6b66\u827a\u66f4\u662f\u9ad8\u5f3a\uff0c\u4e24\u53e3\u6212\u5200\uff0c\u4e00\u53e3\u957f\u5251\uff0c\u4e92 \u4e3a\u547c\u5e94\uff0c\u53eb\u4e91\u857e\u65e0\u6cd5\u65bd\u5c55\u5b9d\u5251\u4e4b\u957f\u3002\u4e91\u857e\u88ab\u56f0\u5728\u6838\u5fc3\uff0c\u5708\u5b50\u8d8a\u7f29\u8d8a\u5c0f\uff0c\u6c99\u65e0\u5fcc\u6068\u4ed6\u62a2\u53bb\u77f3\u5bb6 \u5c0f\u59d0\uff0c\u5728\u6212\u5200\u4e0e\u957f\u5251\u63a9\u62a4\u4e4b\u4e0b\uff0c\u5f53\u5934\u6025\u653b\u3002\u6fc0\u6218\u4e4b\u4e2d\uff0c\u5934\u9640\u3001\u9053\u58eb\u3001\u6c99\u6d9b\u7684\u5200\u3001\u5251\u3001\u638c\u540c\u65f6 \u88ad\u5230\uff0c\u4e91\u857e\u4e00\u62db\u201c\u529b\u5212\u9e3f\u6c9f\u201d\uff0c\u594b\u529b\u62db\u67b6\uff0c\u6c99\u65e0\u5fcc\u89d1\u7740\u7834\u7efd\uff0c\u9b3c\u5934\u5200\u6402\u5934\u76f4\u5288\uff0c\u53e6\u4e00\u540d\u76d7\u515a \u7684\u52fe\u9570\u67aa\u4e5f\u659c\u523a\u52fe\u5230\uff0c\u4e91\u857e\u4e0d\u662f\u4e09\u5934\u516d\u81c2\uff0c\u654c\u90a3\u5934\u9640\u3001\u9053\u58eb\u3001\u6c99\u6d9b\u7684\u4e00\u5200\u53cc\u638c\u4e00\u5251\u5df2\u662f\u5403\u529b \u4e07\u5206\uff0c\u6c99\u65e0\u5fcc\u7684\u9b3c\u5934\u5200\u548c\u76d7\u515a\u7684\u52fe\u9570\u67aa\u53c8\u540c\u65f6\u88ad\u6765\uff0c\u90a3\u662f\u4e07\u4e07\u8eb2\u95ea\u4e0d\u4e86\u3002 \u3000\u3000\u6c99\u65e0\u5fcc\u54ac\u7259\u5207\u9f7f\uff0c\u8fd9\u4e00\u5200\u51fa\u624b\u6781\u91cd\uff0c\u9661\u7136\u95f4\uff0c\u624b\u8155\u5173\u8282\u4e4b\u5904\uff0c\u5ffd\u4f3c\u7ed9\u4eba\u7528\u5229\u9488\u523a\u4e86\u4e00 \u4e0b\uff0c\u4e0d\u7531\u5f97\u5927\u53eb\u4e00\u58f0\uff0c\u9b3c\u5934\u5200\u8131\u624b\u98de\u53bb\uff0c\u5bd2\u5149\u4e00\u95ea\uff0c\u51b7\u6c14\u6c81\u808c\uff0c\u7adf\u4ece\u4e91\u857e\u7684\u9888\u4fa7\u98de\u8fc7\u3002\u4e91\u857e \u5403\u4e86\u4e00\u60ca\uff0c\u53ea\u89c1\u90a3\u4f7f\u52fe\u9570\u67aa\u7684\u4e5f\u5927\u53eb\u4e00\u58f0\uff0c\u52fe\u9570\u67aa\u5012\u52fe\u56de\u6765\uff0c\u4f24\u4e86\u81ea\u5df1\uff0c\u7adf\u7136\u4e00\u8de4\u8dc3\u5012\u5730 \u4e0a\uff0c\u722c\u4e0d\u8d77\u6765\u3002\u539f\u6765\u4ed6\u4e5f\u4f3c\u7ed9\u4eba\u7528\u5229\u9488\u523a\u4e86\u4e00\u4e0b\uff0c\u63e1\u7740\u67aa\u628a\u7684\u624b\u56e0\u75db\u4e00\u7f29\u4e00\u5f2f\uff0c\u90a3\u52fe\u9570\u67aa\u4e00 \u5f2f\u5373\u62d0\uff0c\u56e0\u800c\u975e\u4f46\u4f24\u4e0d\u4e86\u4e91\u857e\uff0c\u53cd\u628a\u81ea\u5df1\u80f8\u80c1\u6495\u5f00\u4e86\u4e00\u5927\u7247\u76ae\u8089\u3002 \u3000\u3000\u4e91\u857e\u4f55\u7b49\u673a\u7075\uff0c\u8d81\u7740\u654c\u4eba\u60ca\u614c\u4e4b\u9645\uff0c\u500f\u5730\u4ece\u6c99\u65e0\u5fcc\u539f\u6765\u5360\u7740\u7684\u7a7a\u6863\u8df3\u51fa\uff0c\u53ea\u542c\u5f97\u90a3\u4e66\u751f \u7b11\u9053\uff1a\u201c\u5999\u6781\uff0c\u5999\u6781\uff01\u4fdd\u9556\u7684\uff0c\u4f60\u8fd9\u624b\u6697\u5668\u6253\u5f97\u771f\u4e0d\u574f\u5440\uff01\u201d\u4e91\u857e\u7ed9\u4e66\u751f\u4e00\u8bed\u70b9\u9192\uff0c\u5fc3\u5ff5\u4e00 \u52a8\u60f3\u9053\uff1a\u201c\u654c\u4f17\u6211\u5be1\uff0c\u662f\u975e\u7528\u6697\u5668\u4e0d\u884c\uff01\u201d\u8d81\u7740\u8fd9\u4e2a\u7a7a\u9699\uff0c\u817e\u51fa\u5de6\u624b\uff0c\u638f\u4e86\u4e00\u628a\u6885\u82b1\u8774\u8776\u9556 \u626c\u7a7a\u4e00\u6d12\uff0c\u904d\u88ad\u654c\u4f17\uff0c\u4e91\u857e\u51fa\u9053\u672a\u4e45\uff0c\u5373\u5f97\u4e86\u201c\u6563\u82b1\u5973\u4fa0\u201d\u7684\u7f8e\u540d\uff0c\u8fd9\u8774\u8776\u9556\u7684\u529f\u592b\u81ea\u662f\u5341 \u5206\u4e86\u5f97\u3002\u53ea\u542c\u5f97\u53ee\u53ee\u8fde\u54cd\uff0c\u4e00\u7247\u53eb\u58f0\uff0c\u9664\u4e86\u5934\u9640\u3001\u9053\u58eb\u548c\u6c99\u6d9b\u80fd\u683c\u5f00\u6697\u5668\u4e4b\u5916\uff0c\u5176\u4f59\u7684\u76d7\u515a \u5168\u90fd\u7ed9\u6253\u5012\u4e86\u3002 \u3000\u3000\u90a3\u62ab\u5934\u53d1\u9640\u548c\u9752\u8863\u9053\u58eb\u4e43\u662f\u6c99\u6d9b\u9080\u8bf7\u6765\u7684\u9ed1\u9053\u9ad8\u624b\uff0c\u89c1\u72b6\u60ca\u7591\u4e0d\u5b9a\uff0c\u4e0d\u77e5\u5148\u524d\u90a3\u6697\u5668\u662f \u4e0d\u662f\u4e91\u857e\u653e\u7684\uff1f\u82e5\u662f\u4e91\u857e\u653e\u7684\uff0c\u5219\u201c\u4ed6\u201d\u5728\u56f4\u653b\u4e4b\u4e0b\uff0c\u8fd8\u80fd\u795e\u4e0d\u77e5\u9b3c\u4e0d\u89c9\u5730\u5077\u653e\u6697\u5668\uff0c\u8fd9\u79cd \u672c\u9886\u5b9e\u662f\u9a87\u4eba\uff1b\u82e5\u7136\u4e0d\u662f\u4e91\u857e\u653e\u7684\uff0c\u5219\u90a3\u6697\u4e2d\u76f8\u52a9\u7684\u9ad8\u624b\u66f4\u662f\u52b2\u654c\u3002\u5982\u6b64\u4e00\u60f3\uff0c\u4e09\u4e2a\u56f4\u653b\u4e91 \u857e\u7684\u5f3a\u654c\u90fd\u4e0d\u89c9\u80c6\u5bd2\u3002\u62ab\u53d1\u5934\u9640\u53eb\u9053\uff1a\u201c\u677e\u77f3\u9053\u5144\uff0c\u4f60\u628a\u4ed6\u9489\u7262\uff0c\u6c99\u5be8\u4e3b\uff0c\u4f60\u62a2\u4ed6\u7684\u5b9d\u5251\uff0c \u6211\u53bb\u770b\u770b\uff01\u201d\u731b\u7136\u95f4\u201c\u25a1\u201d\u7684\u4e00\u58f0\u7ec6\u54cd\uff0c\u5934\u9640\u7684\u624b\u8155\u53c8\u4f3c\u7ed9\u5229\u9488\u523a\u4e86\u4e00\u4e0b\u3002\u4e09\u4eba\u4e4b\u4e2d\uff0c\u9752\u8863 \u9053\u58eb\u6b66\u529f\u6700\u9ad8\uff0c\u7559\u5fc3\u4e4b\u4e0b\uff0c\u5df2\u77a5\u89c1\u90a3\u4e2a\u7b95\u8e1e\u5728\u5ca9\u77f3\u4e0a\u7684\u4e66\u751f\u8eab\u5f62\u5fae\u52a8\uff0c\u6025\u5fd9\u53eb\u9053\uff1a\u201c\u5e08\u5144\uff0c \u662f\u90a3\u7f8a\u726f\u6363\u7684\u9b3c\uff01\u201d\u957f\u5251\u4e00\u5c55\uff0c\u75be\u5982\u9e70\u96bc\u7a7f\u6797\uff0c\u4ece\u4e91\u857e\u8eab\u8fb9\u98de\u7a9c\u800c\u51fa\u4e00\u5251\u5411\u90a3\u4e66\u751f\u6420\u53bb\uff01 \u3000\u3000\u4e66\u751f\u5c16\u58f0\u53eb\u9053\uff1a\u201c\u6551\u547d\u5440\uff0c\u6551\u547d\u5440\uff01\u201d\u8eab\u8eaf\u98a4\u6296\uff0c\u72b9\u5982\u96e8\u6253\u82b1\u679d\u3002\u8fd9\u9752\u8863\u9053\u58eb\u540d\u53eb\u677e\u77f3 \u9053\u4eba\uff0c\u4e43\u662f\u5f53\u4eca\u6b66\u5f53\u95e8\u4e0b\u7684\u7b2c\u4e8c\u4ee3\u5f1f\u5b50\uff0c\u6b66\u5f53\u6d3e\u7684\u4e03\u5341\u4e8c\u624b\u8fde\u73af\u593a\u547d\u5251\u6cd5\u5929\u4e0b\u95fb\u540d\uff0c\u8fd9\u4e00\u5251 \u53bb\u52bf\u4f55\u7b49\u5feb\u6377\uff0c\u5237\u7684\u4e00\u58f0\uff0c\u5374\u4ece\u4ed6\u80c1\u4e0b\u7a7f\u8fc7\uff0c\u8fde\u8863\u5e26\u4e5f\u6ca1\u6cbe\u7740\u3002\u677e\u77f3\u9053\u4eba\u7684\u5251\u6cd5\u662f\u4e00\u62db\u63a5\u7740 \u4e00\u62db\u3001\u7ef5\u7ef5\u4e0d\u65ad\u7684\u8fde\u73af\u5251\u6cd5\uff0c\u7728\u773c\u4e4b\u95f4\uff0c\u8fde\u8fdb\u56db\u62db\uff0c\u4e66\u751f\u4e71\u56b7\u4e71\u8df3\uff0c\u770b\u4f3c\u624b\u5fd9\u811a\u4e71\uff0c\u5374\u662f\u6bcf \u4e00\u62db\u90fd\u8eb2\u95ea\u5f97\u6070\u5230\u597d\u5904\uff0c\u4efb\u4ed6\u5251\u5149\u970d\u970d\uff0c\u5251\u5f71\u7eb5\u6a2a\uff0c\u5374\u662f\u6beb\u53d1\u65e0\u4f24\u72b6\u540c\u620f\u800d\uff01 \u3000\u3000\u4e91\u857e\u81ea\u677e\u77f3\u9053\u4eba\u8df3\u51fa\u5708\u5b50\u4e4b\u540e\uff0c\u867d\u7136\u538b\u529b\u51cf\u8f7b\uff0c\u4f46\u90a3\u5934\u9640\u529b\u5927\u5200\u6c89\uff0c\u6c99\u6d9b\u7684\u6bd2\u7802\u638c\u4ea6\u987b \u9632\u5907\uff0c\u594b\u529b\u6218\u6765\u4e0d\u8fc7\u6253\u6210\u5e73\u624b\u3002\u542c\u5f97\u4e66\u751f\u8fde\u53eb\u6551\u547d\uff0c\u5165\u8033\u60ca\u5fc3\uff0c\u5fc3\u60f3\uff1a\u201c\u96be\u9053\u6211\u770b\u9519\u4e86\u4eba\uff0c \u8fd9\u4e66\u751f\u771f\u7684\u4e0d\u4f1a\u6b66\u827a\uff1f\u201d\u6fc0\u6218\u4e4b\u4e2d\uff0c\u5206\u4e86\u5fc3\u795e\uff0c\u659c\u773c\u4e00\u77a5\uff0c\u9669\u9669\u88ab\u5934\u9640\u4e00\u5200\u5288\u4e2d\uff0c\u6c14\u5f97\u4e91\u857e \u5fc3\u4e2d\u706b\u8d77\uff1a\u201c\u8fd9\u4e66\u751f\u771f\u771f\u53ef\u6076\uff0c\u6211\u4e3a\u4ed6\u4e0e\u5f3a\u654c\u6027\u547d\u25a1\u62fc\uff0c\u4ed6\u5374\u620f\u5f04\u4e8e\u6211\uff01\u8fd9\u6b21\u4e8b\u60c5\u8fc7\u540e\uff0c\u518d \u4e5f\u4e0d\u7406\u776c\u4ed6\u4e86\uff01\u201d \u3000\u3000\u4e91\u857e\u7ed9\u4e66\u751f\u620f\u5f04\u5f97\u5fc3\u4e2d\u706b\u8d77\uff0c\u5374\u4e0d\u77e5\u677e\u77f3\u9053\u4eba\u66f4\u662f\u7ed9\u4ed6\u620f\u5f04\u5f97\u4e03\u7a8d\u751f\u70df\uff01\u677e\u77f3\u9053\u4eba\u4e00\u5251 \u7d27\u4f3c\u4e00\u5251\uff0c\u603b\u662f\u523a\u90a3\u4e66\u751f\u4e0d\u7740\uff0c\u90a3\u4e66\u751f\u8fde\u53eb\u4e86\u51e0\u58f0\u201c\u6551\u547d\uff01\u201d\u5ffd\u7136\u7eb5\u58f0\u7b11\u9053\uff1a\u201c\u54c8\uff0c\u539f\u6765\u4f60 \u662f\u540c\u6211\u73a9\u7684\uff0c\u597d\u73a9\u5440\uff01\u4e00\u3001\u4e8c\u3001\u4e09\u3001\u56db\u2026\u2026\u516b\u3001\u4e5d\u2026\u2026\u5341\u4e8c\u3001\u5341\u4e09\u2026\u2026\u5341\u4e5d\u3001\u4e8c\u5341\u2026\u2026\u201d\u9053 \u4eba\u523a\u4e00\u5251\uff0c\u4ed6\u5c31\u6570\u4e00\u4e0b\uff0c\u7247\u523b\u4e4b\u95f4\u5df2\u6570\u5230\u4e8c\u5341\u3002\u6c99\u65e0\u5fcc\u4e2d\u4e86\u4e00\u9488\uff0c\u53d7\u4f24\u4e0d\u91cd\uff0c\u8fd9\u65f6\u5df2\u4ece\u5730\u4e0a \u722c\u4e86\u8d77\u6765\uff0c\u6361\u8d77\u4e86\u9b3c\u5934\u5200\uff0c\u5077\u5077\u8d70\u8fd1\u3002\u90a3\u4e66\u751f\u4e00\u9762\u6570\u4e00\u9762\u95ea\uff0c\u76ee\u4e0d\u65c1\u89c2\uff0c\u6c99\u65e0\u5fcc\u4ece\u77f3\u5934\u540e\u9762 \u51b7\u4e0d\u9632\u5730\u8df3\u4e86\u51fa\u6765\uff0c\u4e00\u5200\u65ab\u53bb\uff0c\u4e66\u751f\u5ffd\u800c\u53cd\u624b\u4e00\u638c\uff0c\u4e0d\u6b6a\u4e0d\u659c\uff0c\u6070\u6070\u6253\u4e2d\u4e86\u6c99\u65e0\u5fcc\u7684\u9f3b\u6881\uff0c \u987f\u65f6\u5192\u51fa\u9c9c\u8840\u3002\u4e66\u751f\u7eb5\u58f0\u9a82\u9053\uff1a\u201c\u4f60\u8fd9\u8822\u6750\uff0c\u6211\u6551\u4e86\u4f60\u7684\u6027\u547d\uff0c\u4f60\u5374\u60f3\u8981\u6211\u7684\u6027\u547d\uff0c\u4e0d\u6253\u4f60 \u4e00\u638c\u4f60\u4e5f\u4e0d\u9192\uff0c\u4f60\u6709\u5bb6\u6559\u6ca1\u6709\uff1f\u6c99\u8001\u8d3c\u662f\u6559\u4f60\u6069\u5c06\u4ec7\u62a5\u7684\u4e48\uff1f\u201d \u3000\u3000\u6b64\u8a00\u4e00\u51fa\uff0c\u6c99\u6d9b\u3001\u6c99\u65e0\u5fcc\u548c\u4e91\u857e\u4e09\u4eba\u90fd\u604d\u7136\u5927\u609f\u3002\u90a3\u4e00\u665a\u6c99\u65e0\u5fcc\u4e0e\u526f\u5be8\u4e3b\u5230\u53e4\u5bfa\u5077\u88ad\uff0c \u672c\u6765\u8981\u4e27\u547d\u5728\u4e91\u857e\u7684\u9752\u51a5\u5251\u4e0b\uff0c\u6697\u4e2d\u6709\u4eba\u76f8\u52a9\uff0c\u7528\u6697\u5668\u5c06\u4e91\u857e\u523a\u4e86\u4e00\u4e0b\uff0c\u53eb\u4e91\u857e\u7684\u5251\u52bf\u5931\u4e86 \u51c6\u5934\uff0c\u6c99\u65e0\u5fcc\u624d\u80fd\u9003\u8d70\u3002\u4e8b\u540e\u6c99\u65e0\u5fcc\u66fe\u5bf9\u7236\u4eb2\u8a00\u53ca\uff0c\u4e8c\u4eba\u80e1\u4e71\u731c\u6d4b\uff0c\u5374\u600e\u4e48\u4e5f\u731c\u4e0d\u5230\u7adf\u7136\u662f \u8fd9\u4e2a\u4e66\u751f\uff01 \u3000\u3000\u6c99\u6d9b\u4e0d\u89c9\u4e00\u5446\uff0c\u4e91\u857e\u6b63\u81ea\u4ee5\u653b\u4e3a\u5b88\uff0c\u5251\u52bf\u8fc5\u75be\u5f02\u5e38\uff0c\u5237\u7684\u4e00\u5251\uff0c\u5c06\u6c99\u6d9b\u7684\u62a4\u5934\u76d4\u5288\u88c2\u4e24 \u8fb9\uff0c\u6c99\u6d9b\u5927\u6012\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u6211\u513f\u8981\u52ab\u4ed6\u7684\u73e0\u7389\u5b9d\u9a6c\uff0c\u4ed6\u5374\u4f1a\u6697\u4e2d\u76f8\u52a9\uff1f\u4e16\u95f4\u4e0a\u65e0\u6b64\u9053 \u7406\uff01\u201d\u5341\u6307\u5c48\u4f38\uff0c\u5411\u4e91\u857e\u9762\u95e8\u53c8\u6293\u3002\u90a3\u5934\u9640\u4e5f\u7ed9\u4e91\u857e\u5251\u950b\u634e\u5e26\u4e00\u4e0b\uff0c\u9669\u9669\u53d7\u4f24\uff0c\u8fd9\u4e24\u4eba\u90fd\u662f \u9ed1\u9053\u4e0a\u7684\u9ad8\u624b\uff0c\u9a84\u6a2a\u5df2\u60ef\uff0c\u51e0\u66fe\u53d7\u8fc7\u5982\u6b64\u6298\u8fb1\uff1f\u4e24\u4eba\u6025\u6012\u4e4b\u4e0b\u7adf\u7136\u4e0d\u7406\u4e66\u751f\u8bf4\u8bdd\uff0c\u6b3a\u4e91\u857e\u5e74 \u8f7b\u529b\u5f31\uff0c\u72e0\u72e0\u6025\u653b\uff0c\u610f\u56fe\u6253\u5012\u4e91\u857e\u4e4b\u540e\uff0c\u518d\u8054\u624b\u5bf9\u90a3\u4e66\u751f\u3002\u4e91\u857e\u7ed9\u4ed6\u4eec\u4e00\u8f6e\u6025\u653b\uff0c\u524d\u906e\u540e \u6321\uff0c\u51e0\u4e4e\u900f\u4e0d\u8fc7\u6c14\u6765\u3002\u6fc0\u6218\u4e4b\u4e2d\uff0c\u518d\u4e5f\u65e0\u6687\u77a7\u90a3\u4e66\u751f\u3002 \u3000\u3000\u8033\u4e2d\u53ea\u542c\u5f97\u90a3\u4e66\u751f\u8fde\u58f0\u6570\u9053\uff1a\u201c\u4e09\u5341\u4e94\u3001\u4e09\u5341\u516d\u2026\u2026\u4e09\u5341\u4e5d\u3001\u56db\u5341\u2026\u2026\u56db\u5341\u4e09\u3001\u56db\u5341 \u56db\u2026\u2026\u56db\u5341\u516b\u3001\u56db\u5341\u4e5d\u3001\u4e94\u5341\uff01\u597d\u5440\uff0c\u6b66\u5f53\u6d3e\u7684\u597d\u5251\u6cd5\u9886\u6559\u4e86\uff0c\u9886\u6559\u4e86\uff01\u6211\u6ca1\u5de5\u592b\u966a\u4f60\u73a9 \u5566\uff01\u201d\u58f0\u97f3\u4e00\u65ad\uff0c\u5ffd\u542c\u5f97\u677e\u77f3\u9053\u4eba\u6012\u53eb\u4e00\u58f0\uff0c\u539f\u6765\u5c31\u5728\u4e00\u7728\u773c\u4e4b\u95f4\uff0c\u677e\u77f3\u9053\u4eba\u7684\u957f\u5251\u7ed9\u90a3\u4e66 \u751f\u5288\u624b\u593a\u53bb\uff01 \u3000\u3000\u4e91\u857e\u6b63\u5728\u5403\u7d27\uff0c\u521a\u907f\u8fc7\u4e86\u6c99\u6d9b\u7684\u5f53\u80f8\u4e00\u638c\uff0c\u90a3\u5934\u9640\u7684\u6212\u5200\u53c8\u5288\u9762\u65ab\u6765\uff0c\u4e91\u857e\u4e00\u62db\u201c\u5012\u5377 \u73e0\u5e18\u201d\u53cd\u524a\u4e0a\u53bb\uff0c\u90a3\u5934\u9640\u5200\u950b\u659c\u95ea\uff0c\u624b\u8155\u4e00\u7ffb\uff0c\u5200\u80cc\u53cd\u78d5\uff0c\u8fd9\u4e00\u62db\u7528\u5f97\u751a\u4e3a\u602a\u5f02\uff0c\u4e91\u857e\u5c1a\u672a \u53ca\u53d8\u62db\u62b5\u5fa1\uff0c\u5ffd\u89c1\u9752\u5149\u4e00\u95ea\uff0c\u201c\u5580\u5693\u201d\u4e00\u58f0\uff0c\u706b\u82b1\u98de\u6e85\uff0c\u53ea\u542c\u5f97\u4e66\u751f\u53eb\u9053\uff1a\u201c\u4f60\u8fd9\u79c3\u9a74\u4e3a\u53ef \u6076\uff0c\u7ed9\u4f60\u7559\u4e0b\u4e00\u70b9\u8bb0\u53f7\uff01\u201d\u5934\u9640\u60e8\u53eb\u4e00\u58f0\uff0c\u548c\u6c99\u6d9b\u98de\u8eab\u4fbf\u8dd1\u3002\u539f\u6765\u5c31\u5728\u90a3\u4e00\u77ac\u95f4\uff0c\u4e66\u751f\u4ee5\u8fc5 \u96f7\u4e0d\u53ca\u63a9\u8033\u7684\u624b\u6cd5\uff0c\u7a81\u7136\u98de\u63a0\u8fc7\u6765\uff0c\u5c06\u593a\u81ea\u677e\u77f3\u9053\u4eba\u7684\u957f\u5251\uff0c\u5411\u6212\u5200\u4e00\u524a\u3002\u677e\u77f3\u9053\u4eba\u7684\u957f\u5251 \u5251\u8eab\u8f83\u6212\u5200\u4e3a\u8584\uff0c\u6309\u8bf4\u5200\u5251\u76f8\u4ea4\uff0c\u957f\u5251\u8fd8\u8981\u5403\u4e8f\uff0c\u800c\u4e66\u751f\u8f7b\u8f7b\u4e00\u524a\uff0c\u7adf\u628a\u5934\u9640\u7684\u6212\u5200\u524a\u65ad\uff0c \u82e5\u7136\u8fd9\u628a\u957f\u5251\u662f\u50cf\u201c\u9752\u51a5\u201d\u5251\u90a3\u822c\u7684\u5b9d\u5251\uff0c\u90a3\u662f\u4e0d\u8db3\u4e3a\u5947\uff0c\u4f46\u677e\u77f3\u9053\u4eba\u7684\u5251\u5374\u4e0d\u8fc7\u662f\u666e\u901a\u7684 \u957f\u5251\uff01\u8fd9\u4e66\u751f\u5185\u5bb6\u52b2\u529b\u4e4b\u795e\u5947\u5965\u5999\uff0c\u5b9e\u662f\u8db3\u4ee5\u9a87\u4eba\uff0c\u5373\u7b97\u4e66\u751f\u4e0d\u968f\u624b\u518d\u524a\u53bb\u5934\u9640\u7684\u4e00\u53ea\u8033 \u6735\uff0c\u90a3\u5934\u9640\u4e5f\u8981\u548c\u6c99\u6d9b\u820d\u547d\u5954\u9003\u4e86\uff01 \u3000\u3000\u4e66\u751f\u54c8\u54c8\u4e00\u7b11\uff0c\u5c06\u957f\u5251\u5411\u677e\u77f3\u9053\u4eba\u4e00\u63b7\uff0c\u9053\uff1a\u201c\u8c0b\u8d22\u5bb3\u547d\u4e43\u662f\u4e0d\u4ec1\uff0c\u4e0d\u81ea\u91cf\u529b\u4e43\u662f\u4e0d \u667a\uff0c\u4e0d\u4ec1\u4e0d\u667a\uff0c\u5c82\u5b9c\u60f9\u662f\u751f\u975e\uff1f\u8fd8\u4f60\u7684\u5251\uff0c\u56de\u53bb\u518d\u7ec3\u5341\u5e74\u3002\u201d\u6b66\u5f53\u6d3e\u7684\u5251\u6cd5\u4e43\u662f\u5251\u5b66\u6b63\u5b97\uff0c \u95e8\u4e0b\u5f1f\u5b50\u4e2d\u9887\u591a\u9a84\u72c2\u81ea\u5927\u7684\uff0c\u800c\u5c24\u4ee5\u677e\u77f3\u9053\u4eba\u7231\u7ba1\u95f2\u4e8b\u3002\u6240\u4ee5\u4ed6\u867d\u7136\u4e0d\u662f\u9ed1\u9053\u4e0a\u7684\u597d\u6c49\uff0c\u6c99 \u6d9b\u9080\u4ed6\u540c\u6765\u52ab\u5b9d\uff0c\u5374\u662f\u4e00\u9080\u4fbf\u5230\uff0c\u4e0d\u6599\u8fde\u523a\u4e94\u516d\u5341\u5251\uff0c\u8fde\u4e66\u751f\u7684\u886b\u89d2\u90fd\u672a\u6cbe\u7740\uff0c\u8fd9\u65f6\u88ab\u4e66\u751f \u595a\u843d\uff0c\u54ea\u91cc\u8fd8\u6562\u901e\u5f3a\uff0c\u63a5\u8fc7\u957f\u5251\uff0c\u795e\u6cae\u6c14\u4e27\uff0c\u6c89\u58f0\u95ee\u9053\uff1a\u201c\u8bf7\u4f60\u7559\u4e0b\u4e07\u513f\u3002\u201d\u4e66\u751f\u7b11\u9053\uff1a \u201c\u4f60\u60f3\u627e\u6211\u62a5\u4ec7\u4e48\uff1f\u201d\u677e\u77f3\u9053\u4eba\u9053\uff1a\u201c\u4e0d\u6562\u3002\u201d\u4e66\u751f\u9053\uff1a\u201c\u65e2\u7136\u4e0d\u6562\uff0c\u4f55\u5fc5\u591a\u95ee\uff0c\u4f60\u4e0d\u6562\u4e0e \u6211\u4e3a\u654c\uff0c\u6211\u4e0d\u6b32\u4e0e\u4f60\u4e3a\u53cb\uff0c\u975e\u53cb\u975e\u654c\uff0c\u901a\u59d3\u540d\u4f5c\u751a\uff1f\u201d\u4e66\u751f\u8fd9\u4e00\u756a\u6b6a\u7406\uff0c\u628a\u677e\u77f3\u9053\u4eba\u9a73\u5f97\u65e0 \u8bdd\u53ef\u8bf4\uff0c\u957f\u53f9\u4e00\u58f0\uff0c\u6124\u7136\u5c06\u957f\u5251\u62d7\u4e3a\u4e24\u6bb5\uff0c\u53cd\u8eab\u51fa\u6797\uff0c\u53d1\u8a93\u4ece\u6b64\u7ec8\u751f\u4e0d\u518d\u4f7f\u5251\u3002 \u3000\u3000\u4e66\u751f\u54c8\u54c8\u5927\u7b11\uff0c\u9053\uff1a\u201c\u597d\uff0c\u90fd\u7ed9\u6211\u6eda\uff01\u201d\u7ed5\u573a\u4e00\u531d\uff0c\u811a\u5c16\u4e71\u8e22\uff0c\u88ab\u4e91\u857e\u7528\u6697\u5668\u6253\u5012\u5730\u4e0a \u7684\u90a3\u4e9b\u76d7\u515a\uff0c\u672c\u6765\u90fd\u88ab\u5c01\u4e86\u7a74\u9053\u52a8\u5f39\u4e0d\u5f97\uff0c\u4e66\u751f\u6bcf\u4eba\u8e22\u4e86\u4e00\u811a\uff0c\u7acb\u523b\u4fbf\u628a\u7a74\u9053\u89e3\u5f00\uff0c\u4e91\u857e\u7684 \u8774\u8776\u9556\u6253\u7a74\u672c\u662f\u72ec\u95e8\u624b\u6cd5\uff0c\u88ab\u4e66\u751f\u4e00\u4e3e\u624b\u4e00\u6295\u8db3\uff0c\u4fbf\u7834\u4e86\u53bb\uff0c\u751a\u662f\u9a87\u5f02\u3002\u53ea\u89c1\u90a3\u4e66\u751f\u4e00\u9762\u89e3 \u7a74\uff0c\u4e00\u9762\u7b11\u9053\uff1a\u201c\u6628\u665a\u4f60\u7834\u4e86\u6211\u7684\u72ec\u95e8\u70b9\u7a74\uff0c\u800c\u4eca\u6211\u4e5f\u7834\u4e86\u4f60\u7684\uff0c\u5f7c\u6b64\u5f7c\u6b64\uff0c\u8c01\u4e5f\u4e0d\u8981\u602a \u8c01\uff01\u201d\u4e91\u857e\u770b\u4ed6\u89e3\u7a74\u7684\u8eab\u624b\uff0c\u4e0e\u81ea\u5df1\u6240\u4f20\u7684\u5374\u53c8\u4e0d\u540c\uff0c\u53c8\u4e0d\u4f3c\u662f\u540c\u4e00\u6e0a\u6e90\uff0c\u5fc3\u4e2d\u66f4\u662f\u83ab\u540d\u5176 \u5999\u3002 \u3000\u3000\u7247\u523b\u4e4b\u95f4\uff0c\u76d7\u515a\u7684\u7a74\u9053\u5168\u90fd\u7ed9\u4e66\u751f\u89e3\u5f00\u4e86\uff0c\u6c99\u65e0\u5fcc\u5148\u524d\u5403\u4e66\u751f\u6253\u4e86\u4e00\u638c\uff0c\u5446\u5728\u573a\u4e2d\uff0c\u5c1a \u672a\u9003\u8dd1\uff0c\u89c1\u4e66\u751f\u6551\u8d77\u540c\u4f34\uff0c\u5ffd\u7136\u884c\u8fd1\u524d\u6765\uff0c\u5411\u4e66\u751f\u5f53\u5934\u4e00\u63d6\uff0c\u9053\uff1a\u201c\u4f60\u6551\u6211\u4e00\u6b21\u6027\u547d\uff0c\u6253\u6211 \u4e00\u638c\u3002\u4ed6\u65e5\u6211\u4ea6\u8981\u9976\u4f60\u4e00\u6b21\u4e0d\u6b7b\uff0c\u8fd8\u4f60\u4e00\u638c\u3002\u201d \u3000\u3000\u4e66\u751f\u7b11\u9053\uff1a\u201c\u6211\u6551\u4f60\u4e00\u547d\uff0c\u4e43\u662f\u770b\u5728\u6c99\u8001\u8d3c\u9762\u4e0a\uff0c\u4e0d\u5fc5\u4f60\u8fd9\u5c0f\u8d3c\u627f\u60c5\uff0c\u9976\u6211\u4e00\u6b21\u4e0d\u6b7b\uff0c \u90a3\u53ef\u4e0d\u5fc5\uff0c\u8fd8\u6211\u4e00\u638c\u6211\u5012\u7b49\u4f60\u3002\u53ea\u662f\u4f60\u6bd4\u677e\u77f3\u9053\u4eba\u66f4\u4e0d\u5982\uff0c\u4f60\u8981\u56de\u53bb\u518d\u7ec3\u4e8c\u5341\u5e74\uff0c\u5feb\u6eda\uff01\u201d \u6c99\u65e0\u5fcc\u5fc3\u80f8\u6700\u4e3a\u72ed\u7a84\uff0c\u5411\u4e66\u751f\u4e0e\u4e91\u857e\u72e0\u72e0\u76ef\u4e86\u4e00\u773c\uff0c\u5e26\u9886\u4f17\u76d7\uff0c\u8d70\u51fa\u6811\u6797\u3002 \u3000\u3000\u4e66\u751f\u6447\u4e86\u6447\u5934\uff0c\u5ffd\u800c\u4ef0\u5929\u53f9\u9053\uff1a\u201c\u4e00\u63b7\u4e7e\u5764\u4f5c\u7b49\u95f2\uff0c\u795e\u5dde\u8c01\u662f\u771f\u8c6a\u6770\uff1f\u6c99\u5bb6\u7236\u5b50\u5728\u9ed1\u9053 \u4e0a\u4e5f\u6709\u70b9\u865a\u540d\uff0c\u8c01\u77e5\u5374\u662f\u5982\u6b64\u4e0d\u6210\u6c14\u5019\uff01\u201d\u610f\u5174\u8427\u7d22\uff0c\u4e00\u6d3e\u5931\u671b\u7684\u795e\u60c5\u3002\u6797\u5916\u9a6c\u5636\uff0c\u76d7\u515a\u5df2 \u7ecf\u8fdc\u53bb\u3002 \u3000\u3000\u4e91\u857e\u672c\u6765\u8981\u8d70\uff0c\u542c\u4ed6\u5982\u6b64\u53f9\u606f\uff0c\u77a5\u4e86\u4e66\u751f\u4e00\u773c\uff0c\u5fcd\u4e0d\u4f4f\u5730\u5927\u58f0\u95ee\u9053\uff1a\u201c\u96c1\u95e8\u5173\u5916\u7684\u91d1\u5200 \u5be8\u4e3b\u5982\u4f55\uff1f\u96be\u9053\u4e5f\u4e0d\u7b97\u5f97\u771f\u8c6a\u6770\u4e48\uff1f\u201d\u4e66\u751f\u9762\u8272\u7565\u53d8\uff0c\u5374\u5fae\u5fae\u4e00\u7b11\uff0c\u63a9\u9970\u795e\u60c5\uff0c\u53c8\u6447\u4e86\u6447 \u5934\uff0c\u9053\uff1a\u201c\u91d1\u5200\u5be8\u4e3b\u4e0e\u6c99\u5bb6\u7236\u5b50\u81ea\u7136\u662f\u4e0d\u53ef\u540c\u65e5\u800c\u8bed\uff0c\u53ea\u662f\u8981\u8bf4\u4ed6\u5c31\u662f\u771f\u8c6a\u6770\u561b\uff0c\u4e5f\u8fd8\u672a\u89c1 \u5f97\uff01\u201d\u4e91\u857e\u6c14\u9053\uff1a\u201c\u597d\uff0c\u666e\u5929\u4e4b\u4e0b\uff0c\u53ea\u6709\u4f60\u624d\u662f\u8c6a\u6770\uff01\u201d\u4e00\u6012\u51b2\u51fa\u6811\u6797\uff0c\u5ffd\u89c1\u773c\u524d\u4eba\u5f71\u4e00 \u6643\uff0c\u53ea\u542c\u5f97\u4e66\u751f\u7b11\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u6162\u8d70\uff0c\u6211\u8bf4\u4f60\u624d\u662f\u8c6a\u6770\u3002\u201d\u4e91\u857e\u5de6\u53f3\u817e\u632a\uff0c\u8fde\u4f7f\u4e86\u51e0\u79cd\u8eab \u6cd5\uff0c\u90fd\u88ab\u4e66\u751f\u62e6\u4f4f\u53bb\u8def\u3002\u4e91\u857e\u6012\u9053\uff1a\u201c\u4f60\u62e6\u6211\u4f5c\u4ec0\u4e48\uff1f\u201d\u4e0d\u7406\u4e66\u751f\u62e6\u963b\uff0c\u817e\u8eab\u51b2\u53bb\uff0c\u4e66\u751f\u4f38 \u51fa\u4e00\u638c\uff0c\u5411\u5979\u80f8\u524d\u4e00\u6309\uff0c\u610f\u6b32\u6d88\u89e3\u5979\u7684\u53bb\u52bf\uff0c\u5c06\u5979\u62e6\u4f4f\uff0c\u4e91\u857e\u77aa\u773c\u559d\u9053\uff1a\u201c\u4f60\u3001\u4f60\u3001\u4f60\u6562\u6b3a \u8d1f\u2026\u2026\u201d\u201c\u59d1\u5a18\u201d\u4e8c\u5b57\u51b2\u5230\u53e3\u8fb9\u5ffd\u53c8\u54bd\u4f4f\uff0c\u9752\u51a5\u5251\u731b\u5f97\u5411\u524d\u4e00\u6325\uff0c\u4e66\u751f\u6599\u4e0d\u5230\u5979\u5982\u6b64\u52a8\u6012\uff0c \u6307\u672a\u6cbe\u88f3\uff0c\u6115\u7136\u6025\u9000\uff0c\u5ffd\u542c\u5f97\u4e91\u857e\u53eb\u4e86\u4e00\u58f0\uff0c\u5411\u524d\u503e\u5012\u3002\u539f\u6765\u662f\u5979\u7528\u529b\u8fc7\u731b\u5c0f\u81c2\u8131\u81fc\u3002\u4e66\u751f \u9053\uff1a\u201c\u6211\u66ff\u4f60\u63a5\u81fc\u3002\u201d\u4e91\u857e\u6012\u9053\uff1a\u201c\u4e0d\u8981\u4f60\u6765\u5f04\u3002\u201d\u5de6\u53f3\u4e24\u624b\u4e92\u63e1\uff0c\u7528\u529b\u4e00\u6309\uff0c\u80cc\u8fc7\u8eab\u53bb\uff0c \u5377\u8d77\u8863\u8896\uff0c\u64e6\u4e86\u91d1\u521b\u836f\uff0c\u7ad9\u4e86\u8d77\u6765\uff0c\u53c8\u60f3\u5954\u8dd1\uff0c\u5ffd\u89c9\u8eab\u4f53\u865a\u8f6f\u3002\u539f\u6765\u662f\u6fc0\u6218\u534a\u65e5\uff0c\u6c14\u529b\u5df2\u5c06 \u7528\u5c3d\u4e86\u3002\u4e66\u751f\u8d70\u8fd1\u524d\u6765\uff0c\u4e00\u63d6\u5230\u5730\uff0c\u9053\uff1a\u201c\u6211\u8fd9\u53a2\u66ff\u4f60\u8d54\u7f6a\u4e86\uff01\u5c0f\u5144\u5f1f\uff0c\u4f60\u5fc3\u5730\u7eaf\u826f\uff0c\u80fd\u6025 \u4eba\u4e4b\u96be\uff0c\u786e\u662f\u4fa0\u9aa8\u67d4\u80a0\uff0c\u6211\u4e00\u8def\u884c\u6765\uff0c\u6240\u89c1\u7684\u4eba\u7269\uff0c\u53ea\u6709\u4f60\u8fd8\u591f\u5f97\u4e0a\u505a\u4e2a\u670b\u53cb\u3002\u6211\u751f\u6027\u72c2 \u653e\uff0c\u6709\u5f00\u7f6a\u4e4b\u5904\uff0c\u8bf7\u4f60\u4e0d\u8981\u653e\u5728\u5fc3\u4e0a\u3002\u201d\u4e00\u5bf9\u660e\u5982\u79cb\u6708\u7684\u773c\u775b\uff0c\u6ce8\u5728\u4e91\u857e\u8eab\u4e0a\uff0c\u4e91\u857e\u9762\u4e0a\u4e00 \u7ea2\uff0c\u53ea\u89c9\u8fd9\u4e66\u751f\u522b\u6709\u4e00\u79cd\u4e30\u4eea\uff0c\u4ee4\u4eba\u5fc3\u6298\uff0c\u4f4e\u5934\u95ee\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u4e3a\u4ec0\u4e48\u8981\u9a82\u91d1\u5200\u5be8\u4e3b\u5462\uff1f\u201d \u4e66\u751f\u7b11\u9053\uff1a\u201c\u4f60\u4f69\u670d\u7684\u4eba\uff0c\u672a\u5fc5\u5c31\u662f\u6211\u4f69\u670d\u7684\uff0c\u4f55\u5fc5\u8981\u5f3a\u4eba\u540c\u4f60\u4e00\u6837\u3002\u800c\u4e14\u6211\u4e5f\u6ca1\u6709\u9a82\u4ed6\uff0c \u4ed6\u4e3a\u4eba\u4e5f\u81ea\u6709\u4ee4\u4eba\u656c\u91cd\u4e4b\u5904\u3002\u53ea\u662f\u2026\u2026\u8bf4\u6765\u8bdd\u957f\uff0c\u4e0d\u8bf4\u4e5f\u7f62\u4e86\u3002\u201d\u4e91\u857e\u5fc3\u4e2d\u4e00\u52a8\uff0c\u9053\uff1a\u201c\u4f60 \u662f\u4ece\u96c1\u95e8\u5173\u5916\u6765\u7684\u5417\uff1f\u201d\u4e66\u751f\u4ef0\u5929\u4e00\u7b11\uff0c\u541f\u9053\uff1a\u201c\u6d6e\u840d\u98d8\u6cca\u672c\u65e0\u6839\uff0c\u843d\u62d3\u6c5f\u6e56\u7fa4\u541b\u95ee\uff01\u201d\u7b11 \u5f97\u751a\u662f\u51c4\u51c9\u3002\u4e91\u857e\u5fc3\u60f3\u9053\uff1a\u201c\u8fd9\u4eba\u60f3\u5fc5\u4e5f\u6709\u4e00\u6bb5\u4f24\u5fc3\u8eab\u4e16\uff0c\u4e0e\u6211\u4e00\u6837\u3002\u6211\u7684\u4f24\u5fc3\u8eab\u4e16\u4e5f\u4e0d\u6b32 \u4eba\u77e5\uff0c\u90a3\u53c8\u4f55\u5fc5\u53bb\u76d8\u95ee\u4ed6\uff1f\u201d\u5982\u6b64\u4e00\u60f3\uff0c\u540c\u60c5\u4e4b\u5fc3\uff0c\u6cb9\u7136\u800c\u751f\uff0c\u9053\uff1a\u201c\u597d\uff0c\u90a3\u6211\u4e0d\u518d\u607c\u4f60 \u4e86\uff0c\u54b1\u4eec\u5c31\u6b64\u5206\u624b\u5427\uff01\u201d\u4e66\u751f\u5ffd\u53c8\u7b11\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u4f60\u4eca\u65e5\u505a\u6211\u7684\u4fdd\u9556\uff0c\u6211\u8be5\u8bf7\u4f60\u559d\u4e00\u676f \u9152\u3002\u8fd9\u56de\u4f60\u662f\u6709\u529f\u53d7\u7984\uff0c\u6211\u4e0d\u8bf4\u4f60\u767d\u98df\u4e86\u3002\u201d\u4e91\u857e\u5df2\u542c\u60ef\u4e86\u4ed6\u5f00\u73a9\u7b11\u7684\u58f0\u8c03\uff0c\u4e0d\u751f\u6c14\u4e86\uff0c\u60f3 \u4e86\u4e00\u60f3\uff0c\u773c\u73e0\u4e00\u8f6c\uff0c\u95ee\u9053\uff1a\u201c\u8352\u6797\u4e4b\u4e2d\uff0c\u54ea\u91cc\u6709\u9152\uff1f\u201d \u3000\u3000\u4e66\u751f\u64ae\u5507\u4e00\u5578\uff0c\u53ea\u542c\u5f97\u6797\u5916\u9a6c\u58f0\u957f\u5636\uff0c\u9065\u76f8\u547c\u5e94\uff0c\u7247\u523b\u4e4b\u540e\uff0c\u4e24\u5339\u9a6c\u5954\u5165\u6797\u4e2d\uff0c\u524d\u9762\u7684 \u90a3\u5339\u662f\u4e66\u751f\u7684\u767d\u9a6c\uff0c\u540e\u9762\u7684\u90a3\u5339\u662f\u4e91\u857e\u7684\u7ea2\u9a6c\u3002\u4e66\u751f\u7b11\u9053\uff1a\u201c\u5b83\u4eec\u5012\u5148\u4ea4\u4e0a\u670b\u53cb\u4e86\u3002\u201d\u5728\u9a6c \u80cc\u4e0a\u53d6\u4e0b\u4e00\u4e2a\u76ae\u888b\uff0c\u4ece\u76ae\u888b\u91cc\u53d6\u51fa\u4e00\u4e2a\u7ea2\u6f06\u846b\u82a6\uff0c\u9012\u7ed9\u4e91\u857e\u9053\uff1a\u201c\u4f60\u6253\u5f97\u7d2f\u4e86\uff0c\u5148\u559d\u4e00 \u53e3\u3002\u201d\u4e91\u857e\u559d\u4e86\u4e00\u53e3\uff0c\u7709\u5934\u4e00\u76b1\uff0c\u8131\u53e3\u8bf4\u9053\uff1a\u201c\u554a\uff0c\u539f\u6765\u4f60\u679c\u7136\u662f\u4ece\u8499\u53e4\u6765\u7684\uff01\u201d\u90a3\u9152\u662f\u4e00 \u79cd\u8499\u53e4\u72ec\u6709\u7684\u9a6c\u5976\u9152\uff0c\u7565\u5e26\u9178\u5473\uff0c\u9152\u6027\u751a\u70c8\u3002\u4e91\u857e\u5c0f\u65f6\u5e38\u966a\u7236\u4eb2\u559d\u9152\uff0c\u4e91\u857e\u7231\u5403\u751c\u9152\uff0c\u4e0d\u559c \u70c8\u9152\uff0c\u66f4\u6015\u90a3\u79cd\u53c8\u9178\u53c8\u9a9a\u7684\u5473\u9053\uff0c\u6240\u4ee5\u5165\u53e3\u96be\u5fd8\u3002 \u3000\u3000\u4e66\u751f\u53cc\u7738\u70af\u70af\uff0c\u9053\uff1a\u201c\u4f60\u4e5f\u662f\u4ece\u8499\u53e4\u6765\u7684\uff1f\u770b\u4f60\u6e29\u6587\u4fca\u79c0\u5012\u50cf\u662f\u6765\u81ea\u5c71\u6e29\u6c34\u8f6f\u7684\u6c5f \u5357\u3002\u201d\u4e91\u857e\u7ed9\u4ed6\u4e00\u8d5e\uff0c\u4e5f\u62a5\u4ee5\u5fae\u5fae\u4e00\u7b11\u3002\u4e66\u751f\u53cc\u6307\u76f8\u64e6\uff0c\u201c\u55d2\u201d\u7684\u4e00\u58f0\uff0c\u7b11\u9053\uff1a\u201c\u840d\u8e2a\u5bc4 \u8ff9\uff0c\u4f55\u5fc5\u8ffd\u95ee\u6765\u6e90\uff0c\u6d41\u6c34\u884c\u4e91\uff0c\u672c\u5e94\u5404\u9002\u5176\u9002\u3002\u4f60\u4e0d\u5fc5\u95ee\u6211\uff0c\u6211\u4e5f\u4e0d\u5fc5\u95ee\u4f60\uff0c\u8fd9\u56de\u662f\u6211\u95ee\u9519 \u4e86\u3002\u201d\u4e91\u857e\u597d\u5947\u5fc3\u8d77\uff0c\u6309\u637a\u4e0d\u4f4f\uff0c\u8131\u53e3\u53c8\u95ee\uff1a\u201c\u90a3\u5929\u665a\u4e0a\uff0c\u90a3\u4e24\u4e2a\u80e1\u4eba\u662f\u8ffd\u4f60\u56de\u53bb\u7684\u4e48\uff1f\u201d \u4e66\u751f\u5927\u53e3\u559d\u9152\uff0c\u5fae\u7b11\u4e0d\u7b54\uff0c\u4e91\u857e\u81ea\u8a00\u81ea\u8bed\u9053\uff1a\u201c\u74e6\u523a\u4e0e\u4e2d\u56fd\u5373\u5c06\u4ea4\u5175\uff0c\u4f60\u662f\u6c49\u4eba\u4e2d\u7684\u8c6a\u6770\uff0c \u6240\u4ee5\u8981\u9003\u51fa\u80e1\u8fb9\u4e86\uff1f\u201d\u4e66\u751f\u82e6\u7b11\u4e00\u58f0\uff0c\u795e\u60c5\u751a\u662f\u5947\u5f02\uff0c\u4ecd\u662f\u5927\u53e3\u559d\u9152\uff0c\u4efb\u7531\u4e91\u857e\u731c\u5ea6\u3002\u4e91\u857e \u62ac\u5934\u671b\u4ed6\uff0c\u773c\u5149\u4e2d\u5145\u6ee1\u7591\u95ee\uff0c\u53c8\uff1a\u201c\u90a3\u4e24\u4e2a\u80e1\u4eba\u65e2\u90fd\u662f\u8ffd\u6355\u4f60\u7684\uff0c\u4e3a\u4f55\u4f60\u52a9\u6211\u6740\u4e86\u4e00\u4eba\uff0c\u5374 \u53c8\u6551\u4e86\u53e6\u4e00\u4eba\uff1f\u201d\u4e66\u751f\u53c8\u559d\u4e86\u53e3\u9152\uff0c\u5ffd\u7136\u7b11\u8a00\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u4f60\u771f\u597d\u95ee\uff01\u4f60\u53ef\u77e5\u9053\u6211\u6551\u7684\u662f \u4ec0\u4e48\u4eba\uff1f\u201d\u4e91\u857e\u8131\u53e3\u8bf4\u9053\uff1a\u201c\u662f\u6fb9\u53f0\u706d\u660e\u7684\u5f92\u5f1f\u3002\u201d\u4e66\u751f\u770b\u4e86\u4e91\u857e\u4e00\u773c\uff0c\u89c1\u5979\u51b2\u53e3\u7b54\u51fa\uff0c\u751a \u662f\u5947\u5f02\uff0c\u6de1\u6de1\u4e00\u7b11\uff0c\u7f13\u7f13\u8bf4\uff1a\u201c\u90a3\u6b7b\u7684\u662f\u8131\u6b22\u5e10\u4e0b\u7684\u6b66\u58eb\u3002\u201d\u53ea\u8bf4\u4e86\u6b64\u53e5\uff0c\u4fbf\u95ed\u53e3\u4e0d\u8a00\u3002\u4e91 \u857e\u66f4\u89c9\u7591\u60d1\uff0c\u60f3\uff1a\u201c\u6fb9\u53f0\u706d\u660e\u662f\u5f20\u5b97\u5468\u624b\u4e0b\u6700\u5f97\u529b\u7684\u6b66\u58eb\uff0c\u90a3\u6b7b\u7684\u662f\u8131\u6b22\u7684\u6b66\u58eb\u5f20\u5b97\u5468\u548c\u8131 \u6b22\u662f\u74e6\u523a\u56fd\u7684\u5de6\u53f3\u4e1e\u76f8\uff0c\u90a3\u53c8\u6709\u4ec0\u4e48\u4e0d\u540c\uff1f\u4e3a\u4f55\u8981\u6740\u8131\u6b22\u7684\u6b66\u58eb\uff0c\u5374\u653e\u8d70\u5f20\u5b97\u5468\u7684\u4eba\uff1f\u201d\u8fd8 \u5f85\u518d\u95ee\uff0c\u89c1\u4e66\u751f\u53ea\u987e\u559d\u9152\uff0c\u77e5\u9053\u95ee\u4e5f\u65e0\u7528\u3002\u90a3\u4e66\u751f\u559d\u4e86\u51e0\u53e3\uff0c\u6447\u4e86\u4e00\u6447\u846b\u82a6\uff0c\u5931\u58f0\u8bf4\u9053\uff1a \u201c\u53ea\u5269\u4e0b\u4e00\u5c0f\u534a\u4e86\u3002\u201d\u60cb\u60dc\u4e4b\u60c5\uff0c\u73b0\u4e8e\u8f9e\u8272\u3002\u4e91\u857e\u9053\uff1a\u201c\u8fd9\u9152\u6709\u4ec0\u4e48\u597d\uff1f\u4e2d\u56fd\u5904\u5904\u90fd\u6709\u4f73 \u917f\uff0c\u8fd8\u4e0d\u591f\u4f60\u559d\u7684\u5417\uff1f\u201d\u4e66\u751f\u6005\u7136\u8bf4\u9053\uff1a\u201c\u4eba\u79bb\u4e61\u8d31\uff0c\u7269\u79bb\u4e61\u8d35\u3002\u6211\u5c31\u662f\u5b9d\u8d1d\u8fd9\u79cd\u9152\u3002\u201d\u6367 \u8d77\u846b\u82a6\uff0c\u653e\u5728\u9f3b\u5598\uff0c\u95fb\u90a3\u9152\u5473\u3002\u4e91\u857e\u89c1\u4ed6\u795e\u8272\uff0c\u5ffd\u7136\u60f3\u8d77\u5e7c\u5e74\u4e8b\u60c5\u3002\u4e03\u5c81\u90a3\u65f6\uff0c\u5979\u548c\u7237\u7237\u521d \u56de\u4e2d\u56fd\uff0c\u5728\u96c1\u95e8\u5173\u5916\uff0c\u7237\u7237\u62fe\u8d77\u4e00\u5757\u6ce5\u571f\uff0c\u604b\u604b\u4e0d\u820d\u5730\u95fb\u55c5\uff0c\u4fe8\u7136\u5c31\u662f\u8fd9\u526f\u795e\u60c5\uff0c\u4e0d\u89c9\u53c8\u8131 \u53e3\u95ee\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u6c49\u4eba\u5417\uff1f\u201d \u3000\u3000\u4e66\u751f\u8be7\u7136\u8bf4\u9053\uff1a\u201c\u4f60\u770b\u6211\u4e0d\u50cf\u6c49\u4eba\u5417\uff1f\u201d\u4e66\u751f\u5251\u7709\u6717\u76ee\uff0c\u4fca\u7f8e\u5f02\u5e38\uff0c\u83ab\u8bf4\u5728\u8499\u53e4\u627e\u4e0d\u5230 \u8fd9\u6837\u7684\u4eba\u7269\uff0c\u5373\u5728\u6c5f\u5357\u58eb\u5b50\u4e4b\u4e2d\u4e5f\u4e0d\u53ef\u591a\u89c1\u3002\u4e91\u857e\u77a7\u4ed6\u4e00\u773c\uff0c\u9762\u4e0a\u53c8\u662f\u4e00\u7ea2\uff0c\u9053\uff1a\u201c\u4f60\u5c31\u662f \u6b7b\u4e86\u53d8\u7070\uff0c\u4e5f\u8fd8\u662f\u6c49\u4eba\u3002\u201d\u8bdd\u8bf4\u4e4b\u540e\uff0c\u5ffd\u611f\u5931\u8a00\uff0c\u90a3\u4e66\u751f\u773c\u775b\u4e00\u4eae\uff0c\u653e\u58f0\u8bf4\u9053\uff1a\u201c\u5bf9\u6781\uff0c\u5bf9 \u6781\uff01\u6211\u6b7b\u4e86\u53d8\u7070\u4e5f\u8fd8\u662f\u4e2d\u56fd\u4e4b\u4eba\uff01\u54b1\u4eec\u559d\u9152\uff01\u201d\u62d4\u5f00\u585e\u5b50\uff0c\u53c8\u628a\u90a3\u8499\u53e4\u9152\u503e\u5165\u53e3\u4e2d\u3002 \u3000\u3000\u4e91\u857e\u7b11\u9053\uff1a\u201c\u4f60\u9cb8\u541e\u725b\u996e\uff0c\u51e0\u53e3\u559d\u5b8c\uff0c\u5c82\u4e0d\u66f4\u4e3a\u53ef\u60dc\uff1f\u201d\u4e66\u751f\u9189\u773c\u6d41\u76fc\uff0c\u9152\u610f\u98de\u4e0a\u7709 \u68a2\uff0c\u5927\u7b11\u8bf4\u9053\uff1a\u201c\u4eca\u65e5\u662f\u6211\u6700\u5f97\u610f\u4e4b\u65e5\uff0c\u7406\u5f53\u5f00\u6000\u75db\u996e\u3002\u201d\u4e91\u857e\u9053\uff1a\u201c\u4f55\u4e8b\u5f97\u610f\uff1f\u201d\u4e66\u751f\u8a00 \u9053\uff1a\u201c\u4e00\u8005\u662f\u4ea4\u4e86\u4f60\u8fd9\u4e2a\u670b\u53cb\uff0c\u4e8c\u8005\u662f\u6211\u5f97\u4e86\u7a00\u4e16\u4e4b\u73cd\u3002\u6765\uff0c\u6765\uff01\u5c0f\u5144\u5f1f\uff0c\u6211\u8bf7\u4f60\u996e\u9152\u8d4f \u753b\uff01\u201d\u5728\u76ae\u888b\u91cc\u53d6\u51fa\u90a3\u5377\u753b\u6765\uff0c\u8fce\u98ce\u4e00\u6643\uff0c\u6302\u5728\u679d\u6748\u4e4b\u4e0a\uff0c\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u770b\u5440\uff0c\u8fd9\u5c82\u4e0d\u662f \u7a00\u4e16\u4e4b\u73cd\u5417\uff1f\u201d \u3000\u3000\u4e91\u857e\u4e66\u9999\u95e8\u7b2c\uff0c\u7956\u7236\u662f\u5f53\u671d\u4e00\u54c1\uff0c\u94a6\u547d\u4f7f\u81e3\uff0c\u7236\u4eb2\u5148\u6587\u540e\u6b66\uff0c\u4e5f\u662f\u4e2a\u9971\u8bfb\u8bd7\u4e66\u7684\u79c0\u624d\uff0c \u4e91\u857e\u5e7c\u53d7\u718f\u9676\u4e5f\u7565\u89e3\u8bcd\u7ae0\u5b57\u753b\u3002\u8fd9\u5e45\u753b\u6b63\u662f\u77f3\u82f1\u85cf\u5b9d\u697c\u4e2d\u6240\u6302\u7684\u90a3\u5e45\u5de8\u753b\uff0c\u6628\u665a\u77a7\u4e0d\u6e05\u695a\uff0c \u800c\u4eca\u4e34\u8fd1\u4e00\u770b\uff0c\u53ea\u89c1\u753b\u4e2d\u57ce\u5eca\u5c71\u6c34\u6811\u6728\u4eba\u7269\uff0c\u65e0\u4e00\u7b14\u4e0d\u662f\u5de5\u7b14\u753b\u63cf\uff0c\u90a3\u81ea\u7136\u662f\u4e0a\u4e0a\u7684\u753b\u5e08\u6240 \u7ed8\uff0c\u4f46\u5374\u4f3c\u662f\u53ea\u6c42\u4f20\u771f\u4e0d\u89c1\u795e\u97f5\uff0c\u4e0e\u53e4\u6765\u7684\u5c71\u6c34\u540d\u5bb6\u76f8\u6bd4\uff0c\u90a3\u662f\u8fdc\u8fdc\u4e0d\u5982\uff0c\u5fc3\u4e2d\u7b11\u9053\uff1a\u201c\u8fd9 \u4e66\u751f\u6f47\u6d12\u8131\u4fd7\uff0c\u8d4f\u753b\u7684\u773c\u529b\u5374\u662f\u4e0d\u89c1\u9ad8\u660e\u3002\u201d\u4e66\u751f\u628a\u90a3\u4e00\u846b\u82a6\u70c8\u9152\u5168\u90fd\u559d\u5b8c\uff0c\u5927\u7b11\u8bf4\u9053\uff1a \u201c\u4f60\u77a7\u4e0d\u51fa\u5176\u4e2d\u5999\u5904\u4e48\uff1f\u201d \u3000\u3000\u53ea\u89c1\u90a3\u4e66\u751f\u8d70\u8fd1\u6469\u6332\uff0c\u770b\u4e86\u53c8\u770b\uff0c\u5ffd\u800c\u9ad8\u58f0\u6b4c\u9053\uff1a\u201c\u8c01\u628a\u82cf\u676d\u66f2\u5b50\u8bb4\uff1f\u8377\u82b1\u5341\u91cc\u6842\u4e09 \u79cb\u3002\u90a3\u77e5\u5349\u6728\u65e0\u60c5\u7269\uff0c\u7275\u52a8\u957f\u6c5f\u4e07\u53e4\u6101\uff01\u5440\uff0c\u7275\uff0d\uff0d\u52a8\uff0d\uff0d\u957f\uff0d\uff0d\u6c5f\uff0d\uff0d\u4e07\uff0d\uff0d\u53e4\uff0d\u6101\uff01\u201d \u5531\u5230\u6700\u540e\u4e00\u53e5\uff0c\u53cd\u590d\u541f\u548f\uff0c\u6447\u66f3\u751f\u59ff\uff0c\u771f\u5982\u4e0d\u80dc\u90a3\u4e07\u53e4\u4e4b\u6101\u3002\u4e91\u857e\u5fc3\u9053\uff1a\u201c\u53e4\u4eba\u4e91\u72c2\u6b4c\u5f53 \u54ed\uff0c\u542c\u4ed6\u8fd9\u6b4c\u58f0\uff0c\u771f\u6bd4\u54ed\u8fd8\u96be\u53d7\uff01\u201d\u60f3\u4e0d\u5230\u90a3\u4e66\u751f\u4e00\u6b4c\u65e2\u7ec8\uff0c\u5f53\u771f\u54ed\u4e86\u8d77\u6765\uff0c\u54ed\u58f0\u9707\u6797\uff0c\u54ed \u5f97\u6811\u53f6\u6447\u843d\uff0c\u6797\u9e1f\u60ca\u98de\u3002\u4e91\u857e\u624b\u8db3\u65e0\u63aa\uff0c\u4e0d\u77e5\u5176\u60b2\u4ece\u4f55\u6765\uff0c\u4f55\u6545\u75db\u54ed\u5982\u65af\uff1f \u3000\u3000\u4e66\u751f\u54ed\u4e2a\u4e0d\u505c\uff0c\u4e91\u857e\u7ed9\u4ed6\u54ed\u5f97\u5fc3\u70e6\u610f\u4e71\uff0c\u5bf9\u65b9\u662f\u4e2a\u964c\u751f\u7537\u5b50\uff0c\u60f3\u4e0a\u53bb\u529d\u89e3\uff0c\u53c8\u89c9\u4e0d\u597d\u610f \u601d\uff1b\u82e5\u79bb\u5f00\u4ed6\uff0c\u53c8\u4f3c\u4e0d\u8fd1\u4eba\u60c5\u3002\u4e66\u751f\u8d8a\u54ed\u8d8a\u54c0\uff0c\u4e91\u857e\u4e5f\u89c9\u5fc3\u9178\uff0c\u5fcd\u4e0d\u4f4f\u966a\u4ed6\u54ed\u4e86\u3002\u4e66\u751f\u77a5\u5979 \u4e00\u773c\uff0c\u5ffd\u800c\u4ee5\u8896\u62ed\u6cea\uff0c\u54ed\u58f0\u987f\u6b62\u3002\u731b\u5730\u53c8\u62ac\u8d77\u5934\u6765\uff0c\u4ef0\u5929\u72c2\u7b11\u3002\u4e91\u857e\u201c\u5478\u201d\u4e86\u4e00\u58f0\uff0c\u9053\uff1a \u201c\u4f60\u559d\u9189\u4e86\u4e48\uff1f\u54ed\u54ed\u7b11\u7b11\uff0c\u95f9\u4e9b\u4ec0\u4e48\u554a\uff1f\u201d\u4e66\u751f\u5411\u5979\u4e00\u6307\uff0c\u9053\uff1a\u201c\u4f60\u4e5f\u9189\u4e86\uff0c\u5f7c\u6b64\u5f7c\u6b64\u3002\u201d \u4e91\u857e\u4f4e\u5934\u4e00\u770b\uff0c\u539f\u6765\u81ea\u5df1\u7684\u8863\u895f\u4e5f\u7ed9\u6cea\u73e0\u6ef4\u6e7f\u4e86\u3002\u65e0\u7aef\u7aef\u966a\u4ed6\u54ed\u4e86\u4e00\u573a\uff0c\u771f\u662f\u597d\u6ca1\u6765\u7531\uff0c\u4e0d \u89c9\u4e5f\u7b11\u4e86\u8d77\u6765\u3002 \u3000\u3000\u4e66\u751f\u7eb5\u58f0\u5927\u7b11\uff0c\u541f\u9053\uff1a\u201c\u4ea6\u72c2\u4ea6\u4fa0\u771f\u540d\u58eb\uff0c\u80fd\u54ed\u80fd\u6b4c\u8fc8\u6d41\u4fd7\u3002\u5f53\u54ed\u4fbf\u54ed\uff0c\u5f53\u7b11\u4fbf\u7b11\uff0c\u4f55 \u5fc5\u77eb\u60c5\u9970\u4fd7\u3002\u4f60\u6211\u4ff1\u662f\u6027\u60c5\u4e2d\u4eba\u54ed\u54ed\u7b11\u7b11\uff0c\u6709\u4f55\u8db3\u602a\uff1f\u201d\u53cc\u624b\u628a\u753b\u7f13\u7f13\u5377\u8d77\uff0c\u53c8\u541f\u9053\uff1a\u201c\u957f \u6c5f\u4e07\u53e4\u5411\u4e1c\u6d41\uff0c\u7acb\u9a6c\u80e1\u5c71\u5fd7\u672a\u916c\uff0c\u516d\u5341\u5e74\u6765\u4e00\u56de\u987e\uff0c\u6c5f\u5357\u6f20\u5317\u51e0\u4eba\u6101\uff1f\u201d\u4e91\u857e\u5fc3\u4e2d\u4e00\u52a8\uff0c\u60f3 \u9053\uff1a\u201c\u6628\u665a\u8fd9\u4e66\u751f\u5230\u9ed1\u77f3\u5e84\u53d6\u753b\uff0c\u77f3\u82f1\u8bf4\u7b49\u4e86\u4ed6\u516d\u5341\u5e74\uff0c\u800c\u4eca\u8fd9\u4e66\u751f\u53c8\u8bf4\u51fa\u2018\u516d\u5341\u5e74\u6765\u4e00\u56de \u987e\u2019\u7684\u8bdd\uff0c\u6570\u76ee\u4e0d\u8c0b\u800c\u5408\uff0c\u8fd9\u91cc\u9762\u85cf\u7684\u662f\u4ec0\u4e48\u54d1\u8c1c\uff1f\u83ab\u8bf4\u8fd9\u4e66\u751f\u4ec5\u662f\u4e8c\u5341\u4f59\u5c81\u7684\u5c11\u5e74\uff0c\u90a3\u77f3 \u82f1\u4e5f\u4e0d\u8fc7\u521a\u8fc7\u516d\u5341\u5c81\u751f\u65e5\uff0c\u8fd9\u516d\u5341\u5e74\u4e4b\u8bdd\uff0c\u5982\u4f55\u89e3\u91ca\uff1f\u201d\u767e\u601d\u4e0d\u5f97\u5176\u89e3\uff0c\u53ea\u542c\u5f97\u4e66\u751f\u53c8\u7f13\u7f13 \u8bf4\u9053\uff1a\u201c\u4eca\u65e5\u7b11\u5f97\u75db\u5feb\uff0c\u54ed\u4e5f\u75db\u5feb\uff0c\u53ef\u60dc\u9152\u5df2\u6ca1\u6709\u4e86\u3002\u201d\u201c\u535c\u201d\u7684\u4e00\u58f0\uff0c\u628a\u846b\u82a6\u63b7\u5230\u5730\u4e0a\uff0c \u788e\u4e3a\u56db\u7247\u3002 \u3000\u3000\u4e66\u751f\u884c\u5f84\u867d\u602a\u5f02\u4e91\u857e\u5374\u89c9\u5f97\u4ed6\u522b\u6709\u4e00\u79cd\u5f3a\u70c8\u7684\u611f\u4eba\u4e4b\u5904\u3002\u62ac\u5934\u4e00\u770b\uff0c\u7ea2\u65e5\u5df2\u8fc7\u4e2d\u5929\uff0c\u4e91 \u857e\u9053\uff1a\u201c\u54b1\u4eec\u8be5\u5206\u624b\u5566\u3002\u201d\u8bf4\u51fa\u4e4b\u540e\uff0c\u81ea\u5df1\u542c\u7740\uff0c\u4e5f\u89c9\u5f97\u6709\u70b9\u60cb\u60dc\u7684\u5473\u9053\u3002\u4e00\u9053\uff1a\u201c\u4f60\u53bb \u54ea\uff1f\u4f60\u8fd8\u8981\u56de\u9ed1\u77f3\u5e84\u5417\uff1f\u201d\u4e91\u857e\u9053\uff1a\u201c\u4e0d\u8981\u4f60\u7ba1\u3002\u201d\u4e66\u751f\u7b11\u7740\u9053\uff1a\u201c\u4f60\u6628\u665a\u7684\u884c\u4e8b\uff0c\u6211\u90fd\u77a7 \u89c1\u5566\uff01\u201d\u4e91\u857e\u60f3\u8d77\u6d1e\u623f\u60c5\u4e8b\uff0c\u9762\u7ea2\u8fc7\u8033\u3002\u4e66\u751f\u9053\uff1a\u201c\u90a3\u77f3\u5bb6\u5c0f\u59d0\uff0c\u7f8e\u8c8c\u975e\u5e38\uff0c\u53c8\u901a\u6b66\u827a\uff0c\u5c0f \u5144\u5f1f\uff0c\u4f60\u4e3a\u4f55\u4e09\u63a8\u56db\u6258\uff0c\u4e0d\u613f\u4e0e\u5979\u6210\u4eb2\uff1f\u201d\u4e91\u857e\u561f\u5634\u8bf4\u9053\uff1a\u201c\u6211\u613f\u4e0e\u4e0d\u613f\uff0c\u4e0e\u4f60\u4f55\u5e72\uff1f\u201d\u4e66 \u751f\u7b11\u9053\uff1a\u201c\u82e5\u4e0d\u662f\u6211\u6628\u665a\u90a3\u4e48\u4e00\u95f9\uff0c\u4f60\u4e5f\u9003\u4e0d\u51fa\u9ed1\u77f3\u5e84\uff0c\u8fd8\u4e0d\u591a\u8c22\u6211\u5440\uff01\u201d\u4e91\u857e\u7ed9\u4ed6\u9017\u5f97\u62bf \u5634\u4e00\u7b11\u3002\u4e66\u751f\u9053\uff1a\u201c\u6211\u8f88\u8c6a\u6770\uff0c\u539f\u4e0d\u5b9c\u5760\u5165\u6e29\u67d4\u9677\u9631\u4e4b\u4e2d\uff0c\u4f60\u7684\u5b9a\u529b\uff0c\u6211\u5f88\u4f69\u670d\u3002\u201d\u4e91\u857e\u9762 \u4e0a\u53c8\u662f\u4e00\u7ea2\uff0c\u8bda\u6050\u4e0e\u4e66\u751f\u518d\u8c08\u4e0b\u53bb\uff0c\u9732\u51fa\u672c\u6765\u9762\u76ee\uff0c\u4e0d\u518d\u6253\u8bdd\uff0c\u4fbf\u500f\u5730\u98de\u8eab\u4e0a\u9a6c\u3002\u54ea\u77e5\u521a\u51fa \u6797\u5b50\uff0c\u4f46\u542c\u5f97\u80cc\u540e\u9a6c\u94c3\u53ee\u5f53\uff0c\u4e66\u751f\u7684\u767d\u9a6c\u5df2\u662f\u8d76\u4e0a\uff0c\u626c\u58f0\u8bf4\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\u6211\u6709\u8bdd\u8bf4\u3002\u201d \u3000\u3000\u4e91\u857e\u52d2\u9a6c\u56de\u5934\u9053\uff1a\u201c\u8bf7\u8bf4\u3002\u201d\u4e66\u751f\u50ac\u9a6c\u4e0a\u524d\uff0c\u4e0e\u4e91\u857e\u5e76\u8f94\u800c\u884c\uff0c\u4e00\u7b11\u8bf4\u9053\uff1a\u201c\u5c71\u897f\u5883 \u5185\uff0c\u90fd\u662f\u77f3\u82f1\u4e0e\u6c99\u6d9b\u7684\u52bf\u529b\uff0c\u4f60\u5b64\u8eab\u72ec\u884c\uff0c\u4e0d\u662f\u88ab\u77f3\u82f1\u8ffd\u56de\u9ed1\u77f3\u5e84\u53bb\u505a\u5973\u5a7f\uff0c\u5c31\u662f\u88ab\u6c99\u5bb6\u7236 \u5b50\u6349\u53bb\u6298\u78e8\uff0c\u4e0d\u5982\u4e0e\u6211\u540c\u884c\uff0c\u7531\u6211\u505a\u4f60\u7684\u4fdd\u9556\u3002\u201d\u4e91\u857e\u4e00\u60f3\uff0c\u4e5f\u662f\u9053\u7406\u3002\u5c1a\u672a\u56de\u7b54\uff0c\u4e66\u751f\u53c8 \u7d27\u95ee\u9053\uff1a\u201c\u4f60\u4e0a\u54ea\u513f\uff1f\u201d\u4e91\u857e\u7b54\u9053\uff1a\u201c\u6211\u4e0a\u5317\u4eac\u3002\u201d\u4e66\u751f\u9053\uff1a\u201c\u90a3\u5de7\u6781\u4e86\uff0c\u6211\u4e5f\u662f\u4e0a\u5317\u4eac\u3002 \u54b1\u4eec\u5144\u5f1f\u79f0\u547c\u4e86\u5427\u3002\u201d\u4e91\u857e\u7b11\u9053\uff1a\u201c\u6211\u8fd8\u672a\u77e5\u9053\u4f60\u7684\u59d3\u540d\uff0c\u600e\u6837\u79f0\u547c\u4f60\uff1f\u96be\u9053\u6574\u5929\u5c31\u53eb\u4f60\u505a \u54e5\u54e5\u5417\uff1f\u201d\u4e66\u751f\u9053\uff1a\u201c\u6211\u59d3\u5f20\uff0c\u53cc\u540d\u4e39\u67ab\u3002\u4e39\u5fc3\u7684\u4e39\uff0c\u67ab\u6811\u7684\u67ab\u3002\u201d\u4e91\u857e\u7b11\u9053\uff1a\u201c\u597d\u96c5\u81f4\u7684 \u540d\u5b57\uff0c\u53ea\u662f\u8499\u53e4\u5730\u65b9\uff0c\u53ef\u6ca1\u6709\u67ab\u6811\u554a\uff0c\u4f60\u8fd9\u540d\u5b57\u662f\u600e\u4e48\u53d6\u7684\uff1f\u201d\u4e66\u751f\u95ee\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u4f60\u7684\u59d3 \u540d\u5462\uff1f\u201d\u4e91\u857e\u9053\uff1a\u201c\u6211\u59d3\u4e91\uff0c\u5355\u540d\u2018\u857e\u2019\u5b57\uff0c\u84d3\u857e\u7684\u2018\u857e\u2019\u3002\u201d\u4e66\u751f\u4e5f\u7b11\u9053\uff1a\u201c\u597d\u4e00\u4e2a\u6f02\u4eae \u7684\u540d\u5b57\uff0c\u53ea\u662f\u5e26\u4e00\u70b9\u5973\u513f\u6c14\u5473\uff0c\u51b0\u96ea\u80e1\u8fb9\uff0c\u4e5f\u96be\u770b\u5230\u82b1\u6735\u84d3\u857e\u554a\uff0c\u4f60\u8fd9\u540d\u5b57\u662f\u600e\u4e48\u53d6\u7684\uff1f\u201d \u4e91\u857e\u9762\u8272\u4e00\u53d8\uff0c\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\u6211\u662f\u5728\u51b0\u96ea\u80e1\u8fb9\u957f\u5927\u7684\uff1f\u201d\u4e66\u751f\u7b11\u9053\uff1a\u201c\u6211\u7684\u9152\u4f60\u4e00\u5165\u53e3 \u4fbf\u77e5\u6765\u5386\uff0c\u8fd9\u5c82\u4e0d\u662f\u4e5f\u660e\u660e\u544a\u8bc9\u4e86\u6211\u4f60\u7684\u6765\u5386\u5417\uff1f\u201d\u4e91\u857e\u4e00\u60f3\uff0c\u4e0d\u89c9\u54d1\u7136\u5931\u7b11\u3002\u4f46\u7ec6\u5473\u4e66\u751f \u8bdd\u610f\uff0c\u4f3c\u4e4e\u4ed6\u6240\u77e5\u5c1a\u4e0d\u6b62\u6b64\uff0c\u4e0d\u89c9\u53c8\u662f\u60f4\u60f4\u4e0d\u5b89\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u8c08\u7b11\u98ce\u751f\uff0c\u5929\u6587\u5730\u7406\u8bcd\u7ae0\u6b66\u4e8b\uff0c\u7adf\u4f3c\u65e0\u4e00\u4e0d\u77e5\uff0c\u4e91\u857e\u542c\u5f97\u6d25\u6d25\u6709\u5473\uff0c\u6e10\u6e10\u5fd8\u4e86\u6212 \u60e7\u4e4b\u5fc3\u3002\u4e00\u8def\u884c\u6765\uff0c\u4e0d\u89c9\u53c8\u662f\u5929\u66ae\uff0c\u5f20\u4e39\u67ab\u626c\u97ad\u4e00\u6307\uff0c\u9053\uff1a\u201c\u524d\u9762\u6709\u4e00\u4e2a\u5c0f\u9547\uff0c\u54b1\u4eec\u662f\u8be5\u6295 \u5bbf\u4e86\u3002\u201d\u4e24\u4eba\u9a6c\u9a70\u8fc5\u75be\uff0c\u7247\u523b\u4e4b\u540e\uff0c\u4fbf\u5230\u9547\u4e0a\u627e\u4e86\u4e00\u95f4\u5ba2\u5e97\u3002\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u7ed9\u6211\u4eec\u4e00\u95f4\u9760\u5357 \u7684\u5927\u623f\u3002\u201d\u4e91\u857e\u6025\u63a5\u53e3\u9053\uff1a\u201c\u6211\u4eec\u8981\u4e24\u95f4\u9760\u5357\u7684\u623f\u5b50\u3002\u201d\u638c\u67dc\u7684\u6414\u5934\u8bf4\u9053\uff1a\u201c\u7a76\u7adf\u662f\u8981\u4e00\u95f4 \u8fd8\u662f\u4e24\u95f4\uff1f\u201d\u4e91\u857e\u6025\u9053\uff1a\u201c\u4e24\u95f4\uff0c\u4e24\u95f4\uff01\u201d\u638c\u67dc\u7684\u671b\u671b\u4e66\u751f\uff0c\u5f20\u4e39\u67ab\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u597d\uff0c \u5c31\u8981\u4e24\u95f4\u3002\u201d\u638c\u67dc\u7684\u9053\uff1a\u201c\u5c31\u662f\u4f60\u4eec\u4e24\u4e2a\u4eba\u5417\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u662f\u5440\uff0c\u5c31\u662f\u6211\u4eec\u4e24\u4e2a\u4eba\u3002\u201d \u3000\u3000\u638c\u67dc\u7684\u751a\u4e3a\u8be7\u5f02\uff0c\u4f46\u591a\u79df\u51fa\u4e00\u95f4\u623f\u5b50\uff0c\u5bf9\u4ed6\u81ea\u662f\u6709\u5229\uff0c\u4fbf\u4e0d\u518d\u95ee\uff0c\u6b23\u7136\u5f15\u5f20\u3001\u4e91\u4e8c\u4eba\u770b \u4e86\u623f\u5b50\uff0c\u81ea\u53bb\u5907\u529e\u9152\u83dc\u3002\u5f20\u4e39\u67ab\u5165\u623f\u4e4b\u540e\uff0c\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u4e0d\u662f\u6211\u541d\u556c\u51e0\u4e2a\u94f6\u5b50\uff0c\u4f60\u6211 \u4e8c\u4eba\uff0c\u62b5\u8db3\u6e05\u8c08\uff0c\u5c82\u4e0d\u751a\u597d\uff1f\u4f55\u5fc5\u8981\u4e24\u95f4\u623f\u5b50\uff1f\u201d\u4e91\u857e\u9053\uff1a\u201c\u8d24\u5144\u6709\u6240\u4e0d\u77e5\uff0c\u6211\u5e73\u751f\u6700\u6015\u4e0e \u4eba\u540c\u5bbf\u3002\u201d\u5f20\u4e39\u67ab\u4e00\u7b11\u8bf4\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u4f60\u5728\u9ed1\u77f3\u5e84\u4e0d\u80af\u4e0e\u77f3\u5c0f\u59d0\u6d1e\u623f\u3002\u201d\u4e91\u857e\u9762\u4e0a\u4e00\u7ea2\uff0c\u6025 \u5fd9\u4e71\u4ee5\u4ed6\u8bed\uff0c\u4e66\u751f\u4e5f\u4e0d\u518d\u95ee\uff0c\u4e8c\u4eba\u5403\u8fc7\u665a\u996d\uff0c\u5404\u81ea\u5165\u623f\u5b89\u6b47\u3002 \u3000\u3000\u4e91\u857e\u5fc3\u751a\u4e0d\u5b89\uff0c\u95e9\u4e86\u95e8\u540e\uff0c\u7d27\u7d27\u5173\u4e0a\u7a97\u5b50\uff0c\u548c\u8863\u800c\u5367\u3002\u7ec6\u60f3\u4e66\u751f\u7684\u4e00\u8a00\u4e00\u7b11\uff0c\u4e0d\u6562\u9616 \u773c\uff0c\u542c\u5f97\u5916\u9762\u6253\u4e86\u4e09\u66f4\uff0c\u5ba2\u5e97\u4e2d\u9759\u6084\u6084\u5730\u65e0\u4e00\u70b9\u58f0\u606f\uff0c\u7d27\u5f20\u7684\u5fc3\u60c5\u6e10\u6e10\u677e\u9a70\uff0c\u6697\u81ea\u7b11\u9053\uff1a \u201c\u8fd9\u4e66\u751f\u867d\u7136\u72c2\u653e\uff0c\u770b\u6765\u4e0d\u662f\u8f7b\u8584\u4e4b\u5f92\u3002\u201d\u4e91\u857e\u4e24\u665a\u6ca1\u6709\u597d\u7761\uff0c\u4e00\u653e\u4e86\u5fc3\uff0c\u4e0d\u89c9\u547c\u547c\u7761\u53bb\u3002 \u4e5f\u4e0d\u77e5\u7761\u4e86\u591a\u4e45\uff0c\u6726\u80e7\u4e2d\u5ffd\u4f3c\u89c1\u90a3\u4e66\u751f\u8d70\u8fd1\u81ea\u5df1\u5e8a\u8fb9\uff0c\u4fef\u8eab\u5fae\u7b11\uff0c\u4e91\u857e\u4e00\u5251\u6420\u53bb\uff0c\u90a3\u4e66\u751f\u7a81 \u7136\u5927\u53eb\u4e00\u58f0\uff0c\u970e\u65f6\u4e4b\u95f4\uff0c\u6ee1\u8eab\u90fd\u662f\u9c9c\u8840\u3002\u4e91\u857e\u60ca\u6781\u800c\u547c\uff0c\u53ea\u542c\u5f97\u7a97\u5916\u7830\u7684\u4e00\u58f0\uff0c\u5f20\u4e39\u67ab\u53eb \u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u5feb\u6765\uff01\u201d\u4e91\u857e\u63c9\u63c9\u773c\u775b\uff0c\u542c\u5f20\u4e39\u67ab\u7684\u53eb\u58f0\uff0c\u5145\u6ee1\u60ca\u610f\uff0c\u51e0\u7591\u975e\u68a6\uff0c\u7d27\u63a5\u7740\u5f20\u4e39 \u67ab\u7684\u53eb\u58f0\uff0c\u53c8\u542c\u5f97\u9a6c\u5339\u5636\u9e23\u4e4b\u58f0\uff0c\u53eb\u5f97\u751a\u662f\u51c4\u5389\uff01 \u3000\u3000\u4e91\u857e\u4e00\u8dc3\u800c\u8d77\uff0c\u597d\u5728\u662f\u8863\u548c\u800c\u5367\uff0c\u65e0\u987b\u803d\u6401\uff0c\u4fbf\u6253\u5f00\u623f\u95e8\u8d70\u51fa\uff0c\u5f20\u4e39\u67ab\u5728\u5c4b\u9876\u62db\u624b\u9053\uff1a \u201c\u54b1\u4eec\u7684\u5b9d\u9a6c\u5df2\u88ab\u4eba\u5077\u53bb\uff0c\u5feb\u8ffd\u5feb\u8ffd\uff01\u201d\u987b\u77e5\u5f20\u4e39\u67ab\u7684\u7167\u591c\u72ee\u5b50\u9a6c\u4e0e\u4e91\u857e\u7684\u7ea2\u9b03\u6218\u9a6c\uff0c\u90fd\u662f \u4e45\u7ecf\u6218\u9635\u7684\u540d\u9a79\uff0c\u5bfb\u5e38\u7684\u4eba\uff0c\u54ea\u91cc\u8fd1\u5f97\u5b83\u4eec\uff1f\u5c24\u5176\u662f\u5f20\u4e39\u67ab\u90a3\u5339\u9a6c\uff0c\u6027\u70c8\u529b\u5927\uff0c\u9664\u4e86\u4e3b\u4eba\uff0c \u8c01\u4e5f\u4f7f\u5524\u4e0d\u5f97\uff0c\u6240\u4ee5\u5f20\u4e39\u67ab\u6562\u628a\u5947\u73cd\u5f02\u5b9d\uff0c\u90fd\u653e\u5728\u9a6c\u4e0a\uff0c\u4e00\u65e0\u987e\u8651\u3002\u5374\u60f3\u4e0d\u5230\u8fd9\u6837\u7684\u4e24\u5339\u5b9d \u9a6c\uff0c\u5c45\u7136\u4e5f\u4f1a\u7ed9\u4eba\u5077\u53bb\uff0c\u90a3\u5077\u9a6c\u4e4b\u4eba\uff0c\u82e5\u975e\u5201\u94bb\u5230\u6781\u7684\u795e\u5077\u5999\u624b\uff0c\u5c31\u662f\u6b66\u827a\u8d85\u51e1\u5165\u5723\u4e4b\u4eba\u3002 \u9976\u662f\u5f20\u4e39\u67ab\u827a\u9ad8\u80c6\u5927\uff0c\u4e5f\u4e0d\u89c9\u663e\u51fa\u4e86\u614c\u5f20\u7684\u795e\u8272\u3002 \u3000\u3000\u4e91\u857e\u4e00\u8dc3\u4e0a\u5c4b\uff0c\u9053\uff1a\u201c\u8ffd\u5f97\u4e0a\u4e48\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u54b1\u4eec\u7684\u9a6c\u5fc5\u4e0d\u80af\u4efb\u8d3c\u4eba\u9a71\u4f7f\uff0c\u8ffd\u5f97 \u4e0a\uff01\u201d\u968f\u624b\u6478\u4e86\u4e00\u952d\u94f6\u5b50\uff0c\u5411\u5c4b\u4e0b\u4e00\u4e22\uff0c\u5e97\u4e3b\u4eba\u8fd9\u65f6\u624d\u8df3\u8d77\u54d7\u53eb\uff0c\u5f20\u4e39\u67ab\u53eb\u9053\uff1a\u201c\u623f\u996d\u94b1\u5728 \u5730\u4e0a\u5462\u3002\u201d\u4e00\u53e5\u8bdd\u5c1a\u672a\u8bf4\u5b8c\uff0c\u8eab\u5f62\u5df2\u5728\u5341\u6570\u4e08\u5916\uff01 \u3000\u3000\u4e91\u857e\u7d27\u7d27\u8ddf\u5728\u4ed6\u7684\u540e\u9762\uff0c\u524d\u9762\u4e00\u8def\u9a6c\u5636\uff0c\u4e24\u4eba\u5faa\u58f0\u8ffd\u8d76\uff0c\u4e0d\u77e5\u4e0d\u89c9\u8ffd\u5230\u90ca\u5916\uff0c\u5728\u6de1\u6708\u661f \u5149\u4e4b\u4e0b\uff0c\u4f46\u89c1\u7ea2\u9a6c\u5728\u524d\uff0c\u767d\u9a6c\u5728\u540e\uff0c\u8df3\u8dc3\u5636\u53eb\uff0c\u4f3c\u662f\u4e0d\u80af\u884c\u8d70\uff0c\u7528\u529b\u6323\u624e\u3002\u4e24\u4e2a\u9a6c\u8d3c\uff0c\u90fd\u662f \u4e00\u8272\u9752\u8272\u8863\u88f3\uff0c\u8499\u8fc7\u5934\u9762\uff0c\u624b\u62ff\u7740\u4e00\u628a\u9999\u706b\uff0c\u70b9\u70b9\u706b\u661f\uff0c\u5728\u9ed1\u591c\u4e2d\u5341\u5206\u523a\u76ee\u3002\u9999\u706b\u4e0d\u4f4f\u5730\u637a \u5728\u9a6c\u7684\u8eab\u4e0a\uff0c\u9a6c\u513f\u8d1f\u75db\uff0c\u6b32\u60f3\u6323\u624e\u53c8\u88ab\u9a6c\u8d3c\u53cc\u817f\u5939\u4f4f\uff0c\u53d1\u4e0d\u51fa\u51f6\u6027\uff0c\u65e0\u53ef\u5948\u4f55\uff0c\u88ab\u9999\u706b\u70e7\u4e00 \u4e0b\uff0c\u5c31\u8dd1\u4e00\u9635\uff0c\u6240\u4ee5\u867d\u7136\u8fdc\u8fdc\u4e0d\u53ca\u5e73\u65f6\u7684\u795e\u901f\uff0c\u5f20\u4e39\u67ab\u548c\u4e91\u857e\u65bd\u5c55\u4e86\u7edd\u9876\u8f7b\u529f\uff0c\u4e5f\u8fd8\u662f\u8ffd\u5b83 \u4e0d\u4e0a\u3002\u542c\u5f97\u4e24\u5339\u5b9d\u9a6c\u58f0\u58f0\u60e8\u5636\uff0c\u4e66\u751f\u548c\u4e91\u857e\u90fd\u662f\u5fc3\u75db\u6b32\u88c2\uff01 \u3000\u3000\u90a3\u7167\u591c\u72ee\u5b50\u9a6c\u542c\u5f97\u4e3b\u4eba\u7684\u58f0\u97f3\uff0c\u6323\u624e\u66f4\u70c8\uff0c\u9a6c\u8d3c\u7528\u9999\u706b\u53c8\u70e7\uff0c\u5f20\u4e39\u67ab\u5927\u543c\u4e00\u58f0\uff0c\u4e00\u63a0\u6570 \u4e08\uff0c\u53f3\u624b\u4e00\u626c\uff0c\u53ea\u89c1\u6570\u5341\u7f15\u94f6\u5149\u98de\u5c04\u800c\u53bb\uff0c\u90a3\u4e24\u4e2a\u9a6c\u8d3c\u597d\u50cf\u8111\u540e\u957f\u6709\u773c\u775b\uff0c\u4e00\u4e2a\u7b4b\u6597\u52fe\u7740\u9a6c \u978d\u8eb2\u5230\u9a6c\u8179\u4e0b\u9762\u3002\u5f20\u4e39\u67ab\u75db\u60dc\u540d\u9a79\uff0c\u53ea\u662f\u5c04\u4eba\uff0c\u4e0d\u6562\u5c04\u9a6c\uff0c\u6570\u5341\u53e3\u98de\u9488\uff0c\u65e0\u4e00\u6253\u4e2d\u3002\u4e24\u5339\u9a8f \u9a6c\u8d1f\u75db\u72c2\u5636\uff0c\u5954\u4e0a\u5c71\u5c97\uff0c\u5f20\u4e39\u67ab\u4e0e\u4e91\u857e\u7d27\u8ffd\u4e0d\u820d\uff0c\u5ffd\u542c\u5f97\u4e24\u4e2a\u9a6c\u8d3c\u54c8\u54c8\u4e00\u7b11\uff0c\u58f0\u751a\u5a07\u5a9a\uff0c\u7adf \u4f3c\u662f\u4e24\u4e2a\u5973\u4eba\u3002\u4e91\u857e\u4e00\u6014\u3002\u53ea\u89c1\u5c71\u5c97\u4e0a\u78a7\u7eff\u8272\u7684\u78f7\u706b\u5728\u4e71\u8349\u4e1b\u4e2d\u6d41\u52a8\u660e\u706d\uff0c\u5c71\u5c97\u4e0a\u8352\u51a2\u5792 \u5792\uff0c\u9634\u51b7\u4e4b\u6c14\u88ad\u4eba\uff0c\u4e91\u857e\u81f3\u6b64\uff0c\u4e0d\u89c9\u6bdb\u9aa8\u609a\u7136\uff0c\u5f20\u4e39\u67ab\u5ffd\u800c\u7eb5\u58f0\u7b11\u9053\uff1a\u201c\u5c82\u6709\u4f73\u4eba\u7518\u4f5c\u8d3c\uff0c \u6df1\u5bb5\u5374\u4e0e\u9b3c\u4e3a\u90bb\uff1f\u628a\u6211\u7684\u9a6c\u8fd8\u6765\uff0c\u6211\u4e0d\u4e0e\u5973\u6d41\u4e4b\u8f88\u52a8\u624b\u3002\u201d\u4e0e\u4e91\u857e\u8dc3\u4e0a\u5c71\u5c97\uff0c\u5ffd\u542c\u5f97\u6709\u4eba\u5a07 \u58f0\u8bf4\u9053\uff1a\u201c\u8fd9\u5077\u5b9d\u8d3c\u80c6\u5b50\u5012\u5927\uff01\u201d\u4e91\u857e\u5b9a\u4e00\u770b\uff0c\u9661\u89c1\u5230\u90a3\u4e24\u5339\u9a6c\u524d\u9762\u4e24\u8e44\u9ad8\u9ad8\u4e3e\u8d77\uff0c\u6709\u5982\u4eba \u7acb\uff0c\u4e00\u5148\u4e00\u540e\uff0c\u7acb\u5728\u5c71\u5761\u4e4b\u4e0a\uff0c\u65e2\u4e0d\u5636\u53eb\uff0c\u4ea6\u4e0d\u79fb\u52a8\uff0c\u5728\u6708\u5149\u4e4b\u4e0b\u663e\u5f97\u602a\u5f02\u975e\u5e38\u3002\u4e91\u857e\u4e0d\u7981 \u60ca\u53eb\u4e00\u58f0\uff0c\u53ea\u542c\u5f97\u5f20\u4e39\u67ab\u51b7\u7b11\u9053\uff1a\u201c\u539f\u6765\u662f\u4f60\u4eec\u6363\u9b3c\uff01\u201d\u4e91\u857e\u5b9a\u4e86\u5fc3\u795e\uff0c\u518d\u7ec6\u770b\u65f6\uff0c\u5728\u5c71\u5c97 \u4e4b\u4e0a\uff0c\u8fd8\u6328\u6b21\u7acb\u7740\u56db\u6761\u6c49\u5b50\uff0c\u5404\u4e3e\u4e00\u8db3\uff0c\u4f5c\u6b65\u4e0b\u697c\u68af\u4e4b\u72b6\uff0c\u795e\u60c5\u6728\u7136\uff0c\u6709\u5982\u96d5\u5851\u3002\u8fd9\u56db\u6761\u6c49 \u5b50\u6b63\u662f\u4e0e\u77f3\u82f1\u4ea4\u6613\u7684\u90a3\u56db\u4e2a\u73e0\u5b9d\u5546\u4eba\uff0c\u4ed6\u4eec\u6240\u4f5c\u7684\u5f62\u72b6\uff0c\u4e5f\u6b63\u662f\u90a3\u665a\u88ab\u5f20\u4e39\u67ab\u70b9\u7a74\u4e4b\u540e\u7684\u5f62 \u72b6\u3002 \u3000\u3000\u4e91\u857e\u677e\u4e86\u53e3\u6c14\u3002\u6c5f\u6e56\u4e4b\u4e0a\u6709\u79cd\u9a6c\u8d3c\uff0c\u80fd\u5728\u91ce\u9a6c\u72c2\u5954\u4e4b\u9645\uff0c\u7a81\u7136\u5c06\u5b83\u67d0\u4e00\u8981\u5bb3\u4e4b\u5904\u7684\u8840\u6d41 \u5c01\u4f4f\uff0c\u5c31\u5982\u88ab\u70b9\u4e86\u7a74\u9053\u4e00\u822c\uff0c\u540c\u6837\u4e0d\u80fd\u52a8\u5f39\u3002\u8fd9\u56db\u4e2a\u73e0\u5b9d\u5546\u4eba\u5927\u7ea6\u662f\u56e0\u6628\u665a\u5403\u4e86\u82e6\u5934\uff0c\u6240\u4ee5 \u4eca\u665a\u5c06\u8fd9\u4e24\u5339\u9a6c\u62ff\u6765\u62a5\u590d\u3002\u8fd9\u5f62\u72b6\u867d\u7136\u6050\u6016\uff0c\u4f46\u4e91\u857e\u5df2\u77e5\u4ed6\u4eec\u4e0d\u662f\u9b3c\u9b45\uff0c\u53cd\u4e0d\u4f3c\u4ee5\u524d\u7684\u60ca \u6050\uff0c\u51b2\u7740\u90a3\u56db\u4e2a\u6c49\u5b50\u53eb\u9053\uff1a\u201c\u6628\u665a\u6211\u66ff\u4f60\u4eec\u89e3\u4e86\u7a74\u9053\uff0c\u4e3a\u4f55\u4f60\u4eec\u5374\u96be\u4e3a\u6211\u7684\u5750\u9a91\uff1f\u201d\u90a3\u56db\u4e2a \u73e0\u5b9d\u5546\u4eba\u4ecd\u662f\u6728\u7136\u4e0d\u8bed\uff0c\u5ffd\u542c\u5f97\u5c71\u5c97\u4e4b\u4e0a\uff0c\u6709\u58f0\u8bf4\u9053\uff1a\u201c\u5ba2\u4eba\u90fd\u6765\u4e86\u5417\uff1f\u5e26\u4ed6\u8fdb\u5893\uff01\u201d\u58f0\u97f3 \u7adf\u4f3c\u662f\u4ece\u5730\u5e95\u4e2d\u53d1\u51fa\uff0c\u9634\u6c89\u6c89\u7684\uff0c\u597d\u50cf\u5f88\u8fdc\uff0c\u5374\u53c8\u4f3c\u5f88\u8fd1\u3002\u4e91\u857e\u5403\u4e86\u4e00\u60ca\uff0c\u8fd9\u79cd\u201c\u4f20\u97f3\u5165 \u5bc6\u201d\u7684\u529f\u592b\u975e\u5185\u529f\u7cbe\u7eaf\uff0c\u5b9e\u96be\u529e\u5230\u3002\u770b\u6765\u4eca\u665a\u7684\u654c\u4eba\u867d\u4e0d\u662f\u9b3c\u9b45\uff0c\u4f46\u5374\u8981\u6bd4\u9b3c\u9b45\u8fd8\u66f4\u53ef\u6015\uff01 \u3000\u3000\u90a3\u4e2a\u58f0\u97f3\u4f20\u51fa\u4e4b\u540e\uff0c\u4e71\u77f3\u5806\u4e2d\u7a81\u7136\u73b0\u51fa\u4e24\u4eba\uff0c\u4e00\u8272\u9752\u8863\uff0c\u4e24\u53cc\u78a7\u8272\u7684\u773c\u73e0\u9732\u5728\u9762\u7f69\u5916 \u9762\uff0c\u987e\u76fc\u4e4b\u95f4\uff0c\u53d1\u51fa\u8367\u8367\u84dd\u5149\uff0c\u663e\u7136\u4e0d\u4f3c\u6c49\u65cf\u5987\u5973\u3002\u8fd9\u4e24\u4e2a\u5987\u5973\u5c48\u4e86\u534a\u819d\u65bd\u793c\u8bf4\u9053\uff1a\u201c\u8bf7 \u5440\uff01\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u5148\u628a\u6211\u4eec\u7684\u9a6c\u6551\u4e86\u518d\u8bf4\u3002\u201d\u90a3\u4e24\u4e0a\u5987\u5973\u9053\uff1a\u201c\u6211\u4eec\u7684\u4e3b\u4eba\u81ea\u6709\u5429\u5490\uff0c\u4f60 \u4eec\u4e0d\u8981\u89c1\u602a\uff0c\u82e5\u975e\u5982\u6b64\uff0c\u4e5f\u4e0d\u80fd\u5f15\u4f60\u4eec\u5230\u6765\u3002\u201d\u4e91\u857e\u89c1\u5979\u4eec\u8bf4\u8bdd\u5c1a\u9887\u548c\u6c14\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u4eec\u7684 \u4e3b\u4eba\u662f\u4ec0\u4e48\u4eba\uff1f\u201d\u884c\u5148\u7684\u5987\u4eba\u626d\u5934\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u662f\u554a\uff0c\u6211\u5012\u5fd8\u8bb0\u4f60\u4eec\u4e2d\u56fd\u7eff\u6797\u9053\u4e0a\u7684\u89c4\u77e9 \u4e86\uff0c\u4e8c\u5ac2\uff0c\u9012\u62dc\u8d34\u7ed9\u4ed6\u4eec\uff01\u201d\u540e\u9762\u90a3\u4e2a\u5987\u4eba\u4e00\u8f6c\u8eab\u9012\u4e0a\u4e24\u7247\u9ab7\u9ac5\u5934\u9aa8\uff0c\u5f20\u4e39\u67ab\u4e00\u89c1\uff0c\u9762\u8272\u5927 \u53d8\uff01 \u3000\u3000\u4e91\u857e\u6545\u4f5c\u9547\u5b9a\uff0c\u9053\uff1a\u201c\u8fd9\u62dc\u8d34\u5012\u5f88\u7279\u522b\u3002\u201d\u4e24\u4e2a\u5987\u4eba\u5fae\u5fae\u4e00\u7b11\uff0c\u5728\u524d\u5f15\u8def\u3002\u5f20\u4e39\u67ab\u6025\u5fd9 \u5728\u4e91\u857e\u8033\u8fb9\u8bf4\u9053\uff1a\u201c\u4f60\u5feb\u9003\u8d70\uff0c\u5979\u4eec\u7684\u4e3b\u4eba\u662f\u9ed1\u767d\u6469\u8bc3\uff01\u201d\u4e91\u857e\u5fc3\u4e2d\u5ff5\u9053\uff1a\u201c\u9ed1\u767d\u6469\u8bc3\uff01\u201d \u731b\u7136\u7701\u8d77\uff0c\u8fd9\u4e43\u662f\u5468\u5c71\u6c11\u8bf4\u8fc7\u7684\u5f53\u4eca\u6c5f\u6e56\u4e0a\u6700\u53ef\u6015\u7684\u4e24\u4e2a\u602a\u4eba\u3002\u4ed6\u4eec\u7684\u7236\u4eb2\u4e43\u662f\u5370\u5ea6\u5546\u4eba\uff0c \u8fdb\u5165\u897f\u85cf\u7ecf\u5546\uff0c\u843d\u85c9\u897f\u85cf\uff0c\u53d6\u85cf\u5973\u4e3a\u59bb\uff0c\u751f\u4e0b\u4e00\u5bf9\u5b6a\u751f\u5144\u5f1f\uff0c\u7adf\u662f\u4e00\u9ed1\u4e00\u767d\uff0c\u5341\u5206\u5947\u602a\u3002\u68b5 \u6587\u79f0\u6076\u9b54\u4e3a\u201c\u6469\u8bc3\u201d\uff0c\u6240\u4ee5\u4ed6\u4eec\u540c\u65cf\u4e4b\u4eba\u4fbf\u79f0\u54e5\u54e5\u4e3a\u201c\u9ed1\u6469\u8bc3\u201d\uff0c\u5f1f\u5f1f\u4e3a\u201c\u767d\u6469\u8bc3\u201d\u3002\u9ed1\u767d \u6469\u8bc3\u7684\u7236\u4eb2\u672c\u662f\u5370\u5ea6\u7684\u6b66\u5b66\u540d\u5bb6\uff0c\u4ed6\u4eec\u4e8c\u4eba\u65e2\u5b66\u4e86\u5370\u5ea6\u7684\u6b66\u529f\uff0c\u53c8\u5b66\u4e86\u897f\u85cf\u3001\u8499\u53e4\u5404\u79cd\u6b66 \u6280\uff0c\u6240\u4ee5\u6b66\u529f\u751a\u4e3a\u602a\u5f02\u3002\u4e24\u4eba\u957f\u5230\u5341\u591a\u5c81\u540e\uff0c\u79bb\u5f00\u897f\u85cf\uff0c\u904d\u6e38\u4e2d\u571f\uff0c\u95fb\u8bf4\u540e\u6765\u90fd\u5a36\u4e86\u5b9a\u5c45\u5e7f \u5dde\u7684\u6ce2\u65af\u5bcc\u8d3e\u4e4b\u5973\u4e3a\u59bb\uff0c\u56e0\u800c\u4ed6\u4eec\u4e00\u5bb6\u4fbf\u901a\u6653\u51e0\u79cd\u8bed\u8a00\uff1a\u5370\u5ea6\u8bed\uff0c\u6c49\u8bed\uff0c\u6ce2\u65af\u8bed\uff0c\u8499\u85cf\u8bed\uff0c \u90fd\u8bb2\u5f97\u751a\u4e3a\u6d41\u5229\u3002\u8fd9\u4e00\u5bb6\u4eba\u51fa\u6ca1\u65e0\u5e38\uff0c\u5728\u8bb8\u591a\u5730\u65b9\u90fd\u6709\u4f4f\u5b85\uff0c\u8eab\u4e0a\u5e38\u5e26\u6709\u5947\u73cd\u5f02\u5b9d\uff0c\u82e5\u6709\u4e0d \u77e5\u4ed6\u4eec\u5e95\u7ec6\u7684\u7eff\u6797\u5927\u76d7\u6216\u5b98\u5e9c\u4e2d\u4eba\u60f3\u593a\u53d6\u4ed6\u4eec\u7684\u73e0\u5b9d\uff0c\u5fc5\u7136\u88ab\u4ed6\u4eec\u6298\u78e8\u4e2a\u591f\uff0c\u7136\u540e\u5904\u6b7b\u3002\u56e0 \u6b64\u9ed1\u9053\u3001\u767d\u9053\u90fd\u628a\u4ed6\u4eec\u4e00\u5bb6\u770b\u4f5c\u715e\u661f\u3002\u81f3\u4e8e\u4ed6\u4eec\u4e3a\u4ec0\u4e48\u5e38\u5e38\u5e26\u6709\u73e0\u5b9d\u5728\u8eab\uff0c\u5219\u4eba\u8a00\u4eba\u6b8a\uff0c\u6709 \u4eba\u8bf4\u662f\u5077\u7684\uff0c\u6709\u4eba\u8bf4\u4ed6\u4eec\u662f\u6b63\u5f53\u7684\u73e0\u5b9d\u5546\u4eba\uff0c\u5230\u5e95\u5982\u4f55\uff0c\u6ca1\u6709\u4eba\u6562\u53bb\u63a2\u95ee\u3002 \u3000\u3000\u5176\u5b9e\u4ed6\u4eec\u4e00\u5bb6\u65e2\u975e\u5927\u8d3c\uff0c\u4ea6\u975e\u6b63\u5f53\u5546\u4eba\uff0c\u539f\u6765\u4ed6\u4eec\u662f\u4e13\u505a\u89c1\u4e0d\u5f97\u5149\u7684\u73e0\u5b9d\u4e70\u5356\u7684\u3002\u4ea6\u5373 \u662f\u4e13\u95e8\u6536\u4e70\u72ec\u811a\u5927\u76d7\uff08\u6ca1\u6709\u540c\u4f34\u7684\u5355\u8eab\u52ab\u8d3c\uff0c\u79f0\u4e3a\u72ec\u811a\u76d7\uff09\u7684\u8d43\u7269\uff0c\u7136\u540e\u5356\u5230\u6ce2\u65af\u6216\u5370\u5ea6\u3002 \u51e1\u662f\u72ec\u811a\u5927\u76d7\uff0c\u6b66\u529f\u4e00\u5b9a\u8d85\u5353\u5f02\u5e38\uff0c\u4f5c\u6848\u5341\u4e5d\u4e0d\u4f1a\u5931\u624b\uff0c\u5077\u4e1c\u897f\u4e0d\u96be\uff0c\u4e3a\u96be\u7684\u5374\u662f\u5c06\u73e0\u5b9d\u51fa \u624b\uff0c\u6709\u9ed1\u767d\u6469\u8bc3\u8fd9\u6837\u7684\u4eba\u6536\u4e70\uff0c\u4ed6\u4eec\u81ea\u662f\u6c42\u4e4b\u4e0d\u5f97\uff0c\u800c\u4e14\u9ed1\u767d\u6469\u8bc3\u5c06\u73e0\u5b9d\u5356\u51fa\u6d77\u5916\uff0c\u66f4\u4e0d\u4f1a \u6709\u7834\u6848\u7684\u5371\u9669\u3002\u6240\u4ee5\u6c5f\u6e56\u4e0a\u51e0\u4e2a\u6700\u5389\u5bb3\u7684\u72ec\u811a\u5927\u76d7\uff0c\u90fd\u4e0e\u9ed1\u767d\u6469\u8bc3\u6697\u4e2d\u5f80\u6765\uff0c\u8f70\u5929\u96f7\u77f3\u82f1\u4fbf \u662f\u5176\u4e2d\u4e4b\u4e00\uff0c\u4e5f\u53ea\u6709\u9ed1\u767d\u6469\u8bc3\u624d\u6562\u548c\u4ed6\u4eec\u505a\u8fd9\u79cd\u4e70\u5356\u3002\u4e91\u857e\u90a3\u665a\u6240\u89c1\u7684\u90a3\u56db\u4e2a\u73e0\u5b9d\u5546\u4eba\uff0c\u4fbf \u662f\u9ed1\u767d\u6469\u8bc3\u7684\u201c\u4e70\u624b\u201d\uff0c\u6b64\u4e2d\u5185\u5e55\uff0c\u975e\u4f46\u4e91\u857e\u4e0d\u77e5\uff0c\u8fde\u5f20\u4e39\u67ab\u4e5f\u4e0d\u77e5\u9053\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u4e00\u89c1\u9ab7\u9ac5\u9aa8\u5934\uff0c\u77e5\u662f\u9ed1\u767d\u6469\u8bc3\u7684\u6807\u5fd7\uff0c\u6084\u6084\u53eb\u4e91\u857e\u9003\u8d70\uff0c\u4e0d\u6599\u4e91\u857e\u53cd\u800c\u5fae\u5fae\u4e00 \u7b11\uff0c\u9053\uff1a\u201c\u4f60\u65e5\u95f4\u4e0d\u662f\u53eb\u6211\u505a\u4fdd\u9556\u7684\u5417\uff1f\u73b0\u5728\u6211\u662f\u975e\u8ddf\u5b9a\u4f60\u4e0d\u53ef\u4e86\uff01\u201d\u5f20\u4e39\u67ab\u4ee5\u4e3a\u5979\u4e0d\u77e5\u9ed1 \u767d\u6469\u8bc3\u7684\u6b66\u529f\u548c\u6765\u5386\uff0c\u60f3\u5411\u5979\u89e3\u8bf4\uff0c\u5374\u975e\u4e09\u8a00\u4e24\u8bed\u8bf4\u5f97\u6e05\u695a\uff0c\u90a3\u4e24\u4e2a\u6ce2\u65af\u5987\u5973\u53c8\u4e0d\u65f6\u56de\u5934\u63a2 \u671b\u3002\u5f20\u4e39\u67ab\u5fc3\u4e2d\u53eb\u82e6\uff1a\u5440\uff0c\u4f60\u8fd8\u4e0d\u77e5\u9053\u8fd9\u4e24\u4e2a\u9b54\u5934\u7684\u5389\u5bb3\uff01 \u3000\u3000\u5176\u5b9e\u4e91\u857e\u4e0d\u662f\u4e0d\u77e5\uff0c\u800c\u662f\u4e0d\u613f\u5728\u5371\u96be\u4e4b\u4e2d\u820d\u4ed6\u800c\u53bb\u3002\u4e24\u4e2a\u6ce2\u65af\u5987\u4eba\u5728\u524d\u5f15\u8def\uff0c\u4ece\u4e71\u77f3\u8352 \u51a2\u4e4b\u4e2d\u7a7f\u8fc7\uff0c\u6ca1\u591a\u4e45\uff0c\u5230\u4e86\u4e00\u5ea7\u5de8\u5927\u65e0\u6bd4\u7684\u53e4\u5893\u9762\u524d\uff0c\u5893\u4e2d\u6709\u58f0\u8bf4\u9053\uff1a\u201c\u6765\u7684\u5ba2\u4eba\u662f\u4e24\u4e2a\u5c0f \u5a03\u5a03\u5417\uff1f\u201d\u6ce2\u65af\u5987\u4eba\u7b11\u9053\uff1a\u201c\u6b63\u662f\uff0c\u8fd9\u4e24\u4e2a\u5c0f\u5a03\u5a03\u53ef\u80c6\u5927\u54e9\uff01\u201d\u5893\u4e2d\u7684\u58f0\u97f3\u9053\uff1a\u201c\u597d\uff0c\u585e\u4ed6 \u4eec\u8fdb\u6765\uff01\u201d \u3000\u3000\u6ce2\u65af\u5987\u4eba\u7684\u624b\u5728\u5893\u95e8\u4e00\u6309\uff0c\u5893\u95e8\u8f67\u8f67\u4f5c\u54cd\uff0c\u5f20\u4e39\u67ab\u5ffd\u7136\u8fd0\u638c\u4e00\u62cd\uff0c\u201c\u8f70\u201d\u7684\u4e00\u58f0\uff0c\u5893\u95e8 \u584c\u5012\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4e0d\u5fc5\u4f60\u8bf7\uff0c\u6211\u81ea\u5df1\u5df2\u6765\u4e86\u3002\u201d \u3000\u3000\u53e4\u5893\u91cc\u6709\u5385\u5802\u623f\u95e8\uff0c\u9648\u8bbe\u534e\u4e3d\uff0c\u6709\u5982\u5730\u4e0b\u5bab\u6bbf\uff0c\u5385\u4e0a\u63d2\u7740\u5341\u4e8c\u679d\u7c97\u5982\u4eba\u81c2\u7684\u6731\u6cb9\u70db\uff0c\u71c3 \u70e7\u5f97\u5341\u5206\u660e\u4eae\uff0c\u5927\u7ea6\u8fd9\u5730\u4e0b\u5bab\u6bbf\u8fd8\u6709\u548c\u5916\u9762\u901a\u6c14\u7684\u5efa\u7b51\uff0c\u4eba\u5728\u5176\u4e2d\u5e76\u4e0d\u96be\u53d7\u3002 \u3000\u3000\u4e91\u857e\u653e\u773c\u4e00\u770b\uff0c\u53ea\u89c1\u5927\u5385\u4e0a\u6446\u7740\u4e00\u5f20\u5927\u7406\u77f3\u684c\uff0c\u5f53\u4e2d\u5750\u7740\u4e24\u4e2a\u9b08\u53d1\u52fe\u9f3b\u7684\u602a\u4eba\uff0c\u4e00\u9ed1\u4e00 \u767d\uff0c\u76f8\u6620\u6210\u8da3\u3002\u4e24\u65c1\u5404\u5750\u4e24\u4e2a\u6c49\u4eba\uff0c\u6b63\u5c31\u662f\u90a3\u56db\u4e2a\u73e0\u5b9d\u5546\u3002\u4e91\u857e\u5fc3\u9053\uff1a\u201c\u539f\u6765\u8fd9\u53e4\u5893\u8fd8\u53e6\u6709 \u5165\u53e3\u901a\u9053\u3002\u201d \u3000\u3000\u9ed1\u767d\u6469\u8bc3\u95ee\u9053\uff1a\u201c\u5077\u5b9d\u7684\u662f\u8fd9\u4e24\u4e2a\u4eba\u5417\uff1f\u201d\u73e0\u5b9d\u5546\u4eba\u9053\uff1a\u201c\u662f\u5e74\u957f\u7684\u8fd9\u4e2a\uff0c\u5e74\u5e7c\u7684\u8fd9\u4e2a \u662f\u77f3\u82f1\u7684\u5973\u5a7f\uff0c\u4ed6\u6ca1\u6709\u52a8\u624b\uff0c\u8fd8\u66ff\u6211\u4eec\u89e3\u4e86\u7a74\u9053\u3002\u201d\u9ed1\u6469\u8bc3\u70b9\u4e86\u70b9\u5934\uff0c\u6307\u7740\u4e91\u857e\u9053\uff1a\u201c\u4f60\u7ad9 \u8fc7\u4e00\u8fb9\uff01\u201d\u4e91\u857e\u6297\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u548c\u4ed6\u662f\u4e00\u9053\u6765\u7684\uff0c\u4e3a\u4f55\u8981\u7ad9\u8fc7\u4e00\u8fb9\uff1f\u201d\u767d\u6469\u8bc3\u76b1\u4e86\u76b1\u7709\uff0c \u9053\uff1a\u201c\u5c0f\u5a03\u5a03\u4e0d\u77e5\u597d\u574f\u3002\u201d\u7709\u6bdb\u4e00\u52a8\u4fbf\u4e0d\u518d\u8bf4\u3002 \u3000\u3000\u9ed1\u6469\u8bc3\u53c8\u6307\u7740\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u4f60\u8fd9\u5927\u5a03\u5a03\u597d\u5927\u80c6\uff0c\u5c45\u7136\u6562\u5230\u9ed1\u77f3\u5e84\u53bb\u76d7\u5b9d\u4f24\u4eba\uff0c\u8fd8\u6253\u70c2\u4e86 \u6211\u7684\u5927\u95e8\uff0c\u4f60\u53ef\u4ee5\u4e3a\u6211\u4eec\u662f\u597d\u60f9\u7684\u5417\uff1f\u201d\u5f20\u4e39\u67ab\u5927\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u5230\u4e2d\u56fd\u591a\u4e45\u4e86\uff1f\u201d\u9ed1\u767d\u6469\u8bc3 \u6012\u9053\uff1a\u201c\u4f60\u8fd9\u8bdd\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u4f60\u4eec\u53ef\u542c\u8fc7\u2018\u51a4\u6709\u5934\uff0c\u503a\u6709\u4e3b\u2019\u8fd9\u4e24\u53e5\u4e2d\u56fd\u4fd7 \u8bdd\u5417\uff1f\u83ab\u8bf4\u6211\u4e0d\u662f\u76d7\u5b9d\uff0c\u5373\u7b97\u6211\u5230\u9ed1\u77f3\u5e84\u76d7\u5b9d\uff0c\u53c8\u4e0e\u4f60\u4eec\u4f55\u5e72\uff1f\u77f3\u82f1\u4e0d\u7ba1\u8981\u4f60\u4eec\u6765\u7ba1\uff1f\u201d\u9ed1 \u767d\u6469\u8bc3\u53d8\u4e86\u9762\u8272\uff0c\u53ea\u542c\u5f97\u5f20\u4e39\u67ab\u53c8\u9053\uff1a\u201c\u4f60\u4eec\u5077\u6211\u7684\u9a6c\uff0c\u53c8\u600e\u602a\u5f97\u6211\u6253\u70c2\u4f60\u7684\u5927\u95e8\uff1f\u518d\u8bf4\u8fd9 \u5730\u65b9\u4e5f\u4e0d\u662f\u4f60\u7684\uff0c\u8fd9\u5730\u65b9\u662f\u6b7b\u4eba\u4f4f\u7684\u5440\uff01\u201d\u9ed1\u6469\u8bc3\u9053\uff1a\u201c\u597d\u5440\uff0c\u4f60\u5634\u597d\u5201\uff0c\u5012\u7ba1\u8d77\u6211\u4eec\u6765 \u4e86\u3002\u201d\u5f20\u4e39\u67ab\u7b11\u9053\uff1a\uff1a\u201c\u5c31\u53ea\u8bb8\u4f60\u7ba1\u4eba\u5bb6\u4e48\uff1f\u6211\u770b\uff0c\u4f60\u4eec\u5173\u4e0a\u5893\u95e8\uff0c\u5e72\u8106\u4e0d\u8981\u5230\u5916\u9762\u53bb\u4e86\u6700 \u597d\uff01\u201d\u767d\u6469\u8bc3\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u8fd9\u4e2a\u5893\u60f3\u5fc5\u662f\u54ea\u4e2a\u738b\u516c\u7684\uff1f\u201d\u767d\u6469\u8bc3\u9053\uff1a\u201c\u662f\u4ee5 \u524d\u664b\u738b\u7684\uff0c\u600e\u4e48\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u4fd7\u8bed\u8bf4\uff0c\u5173\u4e0a\u5927\u95e8\u505a\u7687\u5e1d\uff0c\u4f60\u4eec\u5173\u4e0a\u4e86\u8fd9\u6247\u5927\u95e8\uff0c\u4e0d\u662f\u4e5f \u53ef\u4ee5\u79f0\u5b64\u9053\u5be1\u4e86\u5417\uff1f\u5c31\u662f\u505a\u4e0d\u6210\u7687\u5e1d\uff0c\u6700\u5c11\u4e5f\u53ef\u4ee5\u5192\u5145\u664b\u738b\u5566\u3002\u4e0d\u8fc7\uff0c\u505a\u7687\u5e1d\u5176\u5b9e\u4e5f\u6ca1\u6709\u4ec0 \u4e48\u610f\u601d\u3002\u201d \u3000\u3000\u9ed1\u767d\u6469\u8bc3\u8fde\u63a5\u53d7\u4ed6\u6316\u82e6\uff0c\u4e0d\u7981\u5927\u6012\uff0c\u4e5f\u4e0d\u89c1\u4ed6\u4eec\u600e\u6837\u4f5c\u52bf\u9661\u7136\u4ece\u5ea7\u4e2d\u98de\u8eab\u76f4\u8d77\uff0c\u4e24\u4eba\u56db \u624b\uff0c\u9f50\u5411\u5f20\u4e39\u67ab\u8111\u95e8\u6293\u4e0b\u3002\u4e91\u857e\u53eb\u4e86\u4e00\u58f0\uff0c\u5ffd\u89c1\u4e00\u9053\u767d\u5149\uff0c\u4fe8\u5982\u5339\u7ec3\uff0c\u500f\u7136\u6a2a\u5728\u5385\u95f4\u3002\u539f\u6765 \u5f20\u4e39\u67ab\u7684\u4f69\u5251\u4e5f\u662f\u5b9d\u5251\uff0c\u7565\u4e00\u6325\u52a8\uff0c\u6709\u5982\u767d\u8679\u3002 \u3000\u3000\u9ed1\u767d\u6469\u8bc3\u53eb\u9053\uff1a\u201c\u597d\u5b9d\u8d1d\uff01\u201d\u53ea\u89c1\u5251\u5149\u4eba\u5f71\u4e4b\u4e2d\uff0c\u58f0\u5982\u88c2\u5e1b\uff0c\u5f20\u4e39\u67ab\u5927\u7b11\u9053\uff1a\u201c\u54c8\uff0c \u54c8\uff01\u5999\u6781\uff0c\u5999\u6781\uff01\u9ed1\u767d\u6469\u8bc3\u5408\u529b\u6765\u5bf9\u4ed8\u4e00\u4e2a\u5927\u5a03\u5a03\uff01\u201d\u6b64\u8a00\u4e00\u51fa\uff0c\u53ea\u89c1\u9ed1\u767d\u6469\u8bc3\u9661\u7136\u4e00\u4e2a\u7b4b \u6597\u53c8\u7ffb\u56de\u5230\u539f\u6765\u7684\u5ea7\u4f4d\u4e4b\u4e0a\uff0c\u751a\u662f\u5c34\u5c2c\u3002\u539f\u6765\u4ed6\u4eec\u5e76\u672a\u5c06\u5f20\u4e39\u67ab\u5f53\u6210\u5bf9\u624b\uff0c\u521a\u624d\u4e00\u6012\u4e4b\u4e0b\uff0c \u5404\u5404\u98de\u8d77\u52a8\u624b\uff0c\u5e76\u672a\u60f3\u5230\u6b66\u6797\u4e2d\u5e73\u8f88\u5bf9\u654c\u7684\u89c4\u77e9\uff0c\u4ed6\u4eec\u90fd\u4ee5\u4e3a\u4e00\u4e0b\u5b50\u4fbf\u53ef\u5c06\u8fd9\u201c\u5927\u5a03\u5a03\u201d\u4e86 \u7ed3\uff0c\u54ea\u77e5\u4e8b\u60c5\u5927\u51fa\u610f\u5916\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u62d4\u5251\u5feb\u6781\uff0c\u4ed6\u4eec\u98de\u8eab\u4e0b\u6251\uff0c\u9661\u89c1\u5251\u5149\uff0c\u907f\u5df2\u4e0d\u53ca\uff0c\u7ed3\u679c\u5f20\u4e39\u67ab\u7684\u957f\u886b\u867d\u88ab\u4ed6\u4eec\u6495 \u6210\u6570\u7247\uff0c\u4ed6\u4eec\u5934\u9876\u7684\u4e1d\u51a0\u4e5f\u88ab\u524a\u53bb\uff0c\u8fde\u5934\u53d1\u4e5f\u88ab\u524a\u53bb\u4e00\u7247\uff0c\u8fd8\u843d\u4e86\u4e2a\u4ee5\u5927\u6b3a\u5c0f\uff0c\u4ee5\u4f17\u6b3a\u5be1\u7684 \u7f6a\u540d\u3002 \u3000\u3000\u9ed1\u6469\u8bc3\u770b\u4e86\u5f20\u4e39\u67ab\u4e00\u773c\uff0c\u9053\uff1a\u201c\u597d\u5251\u6cd5\uff0c\u54b1\u4eec\u5012\u8981\u597d\u597d\u6bd4\u5212\u6bd4\u5212\u3002\u201d\u53e3\u543b\u4e00\u6539\uff0c\u5df2\u4e0d\u5c06 \u4ed6\u5f53\u505a\u201c\u5a03\u5a03\u201d\u770b\u5f85\uff0c\u800c\u662f\u5c06\u4ed6\u5f53\u6210\u5e73\u7b49\u7684\u5bf9\u624b\u4e86\u3002\u5f20\u4e39\u67ab\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u662f\u4f60\u4eec\u4e24\u4e2a\u4e00 \u9f50\u4e0a\u5462\uff0c\u8fd8\u662f\u4e00\u5bf9\u4e00\u7684\u5355\u6253\u72ec\u6597\uff1f\u80dc\u4e86\u5982\u4f55\uff1f\u8d25\u4e86\u5982\u4f55\uff1f\u5148\u5f97\u5212\u51fa\u4e2a\u9053\u513f\u6765\uff01\u201d\u9ed1\u6469\u8bc3\u6012 \u9053\uff1a\u201c\u4f60\u4eec\u4e8c\u4eba\uff0c\u6211\u4eec\u4e5f\u662f\u4e8c\u4eba\uff0c\u8c01\u4e5f\u4e0d\u5360\u4fbf\u5b9c\u3002\u201d\u4ee5\u9ed1\u767d\u6469\u8bc3\u8fd9\u6837\u5927\u7684\u5a01\u540d\uff0c\u613f\u4e0e\u4e8c\u4eba\u4e00 \u5bf9\u4e00\u7684\u4ea4\u624b\uff0c\u53ef\u89c1\u4ed6\u4eec\u5bf9\u5f20\u3001\u4e91\u4e8c\u4eba\u5df2\u662f\u5fcc\u60ee\u3002\u5f20\u4e39\u67ab\u62a2\u7740\u8bf4\uff1a\u201c\u6b64\u4e8b\u4e0e\u6211\u8fd9\u4f4d\u5144\u5f1f\u65e0\u5173\uff0c \u53ea\u662f\u6211\u4e00\u4eba\u4e0e\u4f60\u4eec\u6bd4\u5212\u3002\u201d\u9ed1\u6469\u8bc3\u9053\uff1a\u201c\u90a3\u4e48\u6211\u4fbf\u4e00\u4eba\u4e0e\u4f60\u8fc7\u62db\u3002\u201d\u9ed1\u6469\u8bc3\u4e00\u5f00\u53e3\uff0c\u4e91\u857e\u4e5f \u62a2\u7740\u9053\uff1a\u201c\u6211\u4eec\u4e8c\u4eba\u540c\u6765\uff0c\u81ea\u7136\u662f\u8981\u4e00\u540c\u4e0e\u4f60\u4eec\u6bd4\u5212\u3002\u201d\u767d\u6469\u8bc3\u8bf4\u9053\uff1a\u201c\u597d\u6781\uff0c\u597d\u6781\uff0c\u4f60\u4eec \u82e5\u4e00\u9f50\u52a8\u624b\uff0c\u90a3\u4e48\u6211\u4e5f\u966a\u4f60\u4eec\u8fc7\u62db\u3002\u201d\u5f20\u4e39\u67ab\u6025\u6781\uff0c\u9053\uff1a\u201c\u4e0d\uff0c\u4e0d\uff0c\u662f\u6211\u4e00\u4eba\u4e0e\u4f60\u4eec\u6bd4 \u5212\uff01\u201d\u9ed1\u6469\u8bc3\u53eb\u9053\uff1a\u201c\u600e\u4e48\u7f57\u91cc\u7f57\u5506\u8bf4\u4e2a\u4e0d\u6e05\uff1f\u6211\u548c\u4f60\u6bd4\u5212\uff0c\u4f60\u7684\u5144\u5f1f\u82e5\u4e0d\u51fa\u624b\uff0c\u6211\u7684\u5144\u5f1f \u4e5f\u4e0d\u51fa\u624b\uff0c\u8fd9\u4e0d\u7b80\u5355\u4e4b\u6781\u5417\uff1f\u201d\u4e91\u857e\u5c1a\u5f85\u8bf4\u8bdd\uff0c\u5f20\u4e39\u67ab\u6025\u9053\uff1a\u201c\u597d\u5144\u5f1f\uff0c\u8ba9\u6211\u5148\u8bd5\u8bd5\uff0c\u82e5\u8981 \u4e0d\u884c\uff0c\u4f60\u518d\u51fa\u624b\u4e5f\u8fd8\u4e0d\u8fdf\u3002\u201d\u9ed1\u6469\u8bc3\u4e00\u4f38\u624b\uff0c\u4ece\u5899\u89d2\u7684\u7389\u68fa\u91cc\u53d6\u51fa\u4e00\u6839\u7389\u6756\uff0c\u78a7\u8367\u8367\u653e\u51fa\u7eff \u5149\uff0c\u53cd\u8eab\u8dc3\u51fa\u573a\u4e2d\uff0c\u53eb\u9053\uff1a\u201c\u6765\u5440\uff0c\u6765\u5440\uff01\u6211\u82e5\u80dc\u4e86\uff0c\u4f60\u7684\u9a6c\u5339\u73e0\u5b9d\uff0c\u4e00\u5207\u4e1c\u897f\u5168\u5f52\u6211 \u6709\u3002\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u4f60\u82e5\u8d25\u4e86\u5462\uff1f\u201d\u9ed1\u6469\u8bc3\u6c14\u9053\uff1a\u201c\u6211\u82e5\u8d25\u4e86\uff0c\u8fd9\u4e2a\u5730\u65b9\u5c31\u8ba9\u4f60\u4f5c\u4e3b\u4eba \u5566\u3002\u201d\u987b\u77e5\u8fd9\u4e2a\u53e4\u5893\uff0c\u4e43\u662f\u9ed1\u767d\u6469\u8bc3\u7684\u85cf\u5b9d\u6d1e\u7a9f\u4e4b\u4e00\uff0c\u5176\u4e2d\u73cd\u5b9d\uff0c\u4ef7\u503c\u8fde\u57ce\uff0c\u9ed1\u6469\u8bc3\u4ee5\u6b64\u8d4c \u8d5b\uff0c\u5b9e\u662f\u516c\u5e73\u4e4b\u6781\u3002\u5f20\u4e39\u67ab\u5374\u5927\u7b11\u9053\uff1a\u201c\u8c01\u8981\u505a\u8fd9\u4e2a\u9b3c\u7a9f\u7684\u4e3b\u4eba\uff1f\u201d\u9ed1\u6469\u8bc3\u9053\uff1a\u201c\u90a3\u4f60\u610f\u6b32 \u5982\u4f55\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u628a\u6211\u7684\u9a6c\u5339\u533b\u597d\u3002\u201d\u9ed1\u6469\u8bc3\u4e5f\u5927\u7b11\u8bf4\u9053\uff1a\u201c\u8fd9\u4e2a\u5bb9\u6613\u5230\u6781\u3002\u4f46\u6211\u505a\u60ef \u4e70\u5356\uff0c\u8a00\u51fa\u5fc5\u884c\u3002\u54b1\u4eec\u516c\u5e73\u8d4c\u535a\uff0c\u6211\u4e5f\u4e0d\u60f3\u5360\u4f60\u4fbf\u5b9c\u3002\u4f60\u7684\u5b9d\u7269\u4e0e\u6211\u7684\u5b9d\u7269\u4ef7\u503c\u96be\u5206\u9ad8\u4e0b\uff0c \u8981\u4e0e\u4e0d\u8981\uff0c\u968f\u4f60\u7684\u4fbf\u3002\u8fdb\u62db\u5427\uff01\u201d \u3000\u3000\u5f20\u4e39\u67ab\u7684\u957f\u8863\u9002\u624d\u88ab\u9ed1\u6469\u8bc3\u88c2\u6210\u7247\u7247\uff0c\u6302\u5728\u8eab\u4e0a\uff0c\u788d\u624b\u788d\u811a\uff0c\u4e14\u751a\u96be\u770b\u3002\u5f20\u4e39\u67ab\u6574\u4e86\u6574 \u8863\uff0c\u81ea\u987e\u81ea\u7684\u7b11\u9053\uff1a\u201c\u6211\u5012\u6210\u4e86\u4e2a\u53eb\u5316\u5b50\u4e86\u3002\u201d\u5237\u7684\u4e00\u58f0\uff0c\u5c06\u957f\u8863\u6574\u4ef6\u6495\u4e0b\uff0c\u9732\u51fa\u7d27\u8eab\u8863 \u8902\uff0c\u4e0a\u8eab\u662f\u4ef6\u91d1\u4e1d\u82cf\u7ee3\u7684\u80cc\u5fc3\uff0c\u7ee3\u6709\u4e24\u6761\u91d1\u9f99\u5728\u6d77\u4e0a\u817e\u6ce2\u4e89\u6597\uff0c\u5728\u70db\u5149\u6620\u7167\u4e4b\u4e0b\uff0c\u66f4\u663e\u5f97\u534e \u4e3d\u65e0\u4f26\u3002\u4e91\u857e\u770b\u51fa\u4e86\u795e\uff0c\u5fc3\u4e2d\u5947\u9053\uff1a\u201c\u54a6\uff0c\u8499\u53e4\u5730\u65b9\u4e5f\u6709\u8fd9\u6837\u597d\u7684\u82cf\u7ee3\uff01\u201d \u3000\u3000\u5f20\u4e39\u67ab\u6574\u597d\u8863\u886b\uff0c\u629a\u5251\u4e00\u63d6\uff0c\u9053\uff1a\u201c\u4f60\u5148\u8bf7\uff01\u201d\u9ed1\u6469\u8bc3\u5fae\u5fae\u4e00\u7b11\uff0c\u5bf9\u4ed6\u7684\u793c\u8c8c\u4f3c\u662f\u751a\u4e3a \u6ee1\u610f\u3002\u8eab\u5f62\u5fae\u52a8\uff0c\u7b11\u5bb9\u672a\u655b\uff0c\u4fbf\u547c\u7684\u4e00\u6756\u5411\u4ed6\u8fce\u9762\u626b\u6765\uff0c\u5f20\u4e39\u67ab\u53cd\u624b\u4e00\u5251\uff0c\u4f46\u89c1\u767d\u5149\u7eff\u5149\u4e92 \u76f8\u7ea0\u7ed3\uff0c\u53d1\u51fa\u4e00\u7247\u6781\u5176\u6e05\u4eae\u7684\u91d1\u7389\u4e4b\u58f0\u3002\u6b63\u662f\uff1a \u3000\u3000\u6756\u5f71\u5251\u5149\u637a\u773c\u4e71\uff0c\u6df1\u5bb5\u53e4\u5893\u6597\u795e\u9b54\u3002 \u3000\u3000\u6b32\u77e5\u4e8c\u4eba\u80dc\u8d25\u5982\u4f55\uff1f\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u840d\u8e2a\u4fa0\u5f71\u5f55\u300b\u2014\u2014\u7b2c\u516d\u56de\u3000\u8054\u5251\u60e9\u51f6\u5947\u62db\u542f\u7591\u7aa6\u3000\u62bd\u4e1d\u5265\u8327\u5bc6\u5ba4\u9732\u7aef\u502a \u6881\u7fbd\u751f\u300a\u840d\u8e2a\u4fa0\u5f71\u5f55\u300b \u7b2c\u516d\u56de\u3000\u8054\u5251\u60e9\u51f6\u5947\u62db\u542f\u7591\u7aa6\u3000\u62bd\u4e1d\u5265\u8327\u5bc6\u5ba4\u9732\u7aef\u502a \u3000\u3000\u9ed1\u6469\u8bc3\u6325\u52a8\u7389\u6756\uff0c\u7eff\u5149\u95ea\u95ea\uff0c\u4e0e\u5f20\u4e39\u67ab\u7684\u5b9d\u5251\u76f8\u78b0\uff0c\u53d1\u51fa\u4e00\u7247\u6781\u5176\u6e05\u4eae\u7684\u91d1\u7389\u4e4b\u58f0\uff0c\u767d \u5149\u7eff\u5149\uff0c\u4e92\u76f8\u7ea0\u7ed3\uff0c\u4e91\u857e\u770b\u5f97\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u539f\u6765\u8fd9\u602a\u7269\u7684\u7389\u6756\u4e5f\u662f\u4e00\u4ef6\u5b9d\u7269\uff01\u201d\u4e8c\u4eba \u4f3c\u662f\u5404\u4ee5\u4e0a\u4e58\u5185\u529f\u76f8\u6301\uff0c\u5f20\u4e39\u67ab\u7684\u5b9d\u5251\u9644\u5728\u7389\u6756\u4e4b\u4e0a\u79fb\u52a8\u4e0d\u5f97\uff0c\u800c\u9ed1\u6469\u8bc3\u7684\u7389\u6756\u4e5f\u4f3c\u88ab\u5251\u5149 \u88f9\u4f4f\uff0c\u62bd\u4e0d\u51fa\u6765\u3002\u53ea\u89c1\u4e24\u4eba\u72b9\u5982\u9489\u7262\u5728\u5730\u4e0a\u4e00\u822c\uff0c\u82e6\u82e6\u76f8\u6301\uff0c\u8fc7\u4e86\u4e00\u76cf\u8336\u65f6\u523b\uff0c\u4e24\u4eba\u989d\u4e0a\u90fd \u6ef4\u4e0b\u6c57\u73e0\u3002\u4e91\u857e\u6b63\u81ea\u60f3\u9053\uff1a\u201c\u8fd9\u6837\u4e0b\u53bb\uff0c\u5c82\u4e0d\u4e24\u8d25\u4ff1\u4f24\uff1f\u201d\u5ffd\u542c\u5f97\u547c\u7684\u4e00\u58f0\uff0c\u9ed1\u6469\u8bc3\u8eab\u5f62\u98de \u8d77\uff0c\u5b9d\u6756\u4ecd\u672a\u62bd\u5f00\uff0c\u8fde\u4eba\u5e26\u6756\uff0c\u5c31\u5982\u540a\u5728\u5f20\u4e39\u67ab\u7684\u5b9d\u5251\u4e4b\u4e0a\u4f3c\u7684\uff0c\u547c\u547c\u75be\u8f6c\u3002\u4e91\u857e\u5fc3\u4e2d\u7eb3 \u95f7\uff1a\u8fd9\u662f\u54ea\u95e8\u5b50\u7684\u6b66\u529f\uff1f\u5ffd\u542c\u5f97\u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c\u5f20\u4e39\u67ab\u5927\u53eb\u58f0\u9053\uff1a\u201c\u4e56\u4e56\uff01\u4e0d\u5f97\u4e86\uff01\u201d\u4e91\u857e \u5927\u5403\u4e00\u60ca\uff0c\u6b63\u8981\u62d4\u5251\uff0c\u4f46\u89c1\u4e8c\u4eba\u5df2\u500f\u5730\u5206\u5f00\uff0c\u4e1c\u897f\u76f8\u5411\uff0c\u53c8\u542c\u5f97\u5f20\u4e39\u67ab\u5927\u7b11\u9053\uff1a\u201c\u6ca1\u4e8b\uff0c\u6ca1 \u4e8b\uff01\u539f\u6765\u4f60\u4e0d\u8fc7\u662f\u5934\u8001\u9a74\uff0c\u8f6c\u78e8\u8f6c\u4e86\u534a\u5929\uff0c\u4e5f\u8f6c\u4e0d\u51fa\u4e2a\u9053\u7406\u6765\uff01\u54c8\uff0c\u54c8\uff01\u5f92\u6709\u865a\u540d\u9a87\u4e16\u4fd7\uff0c \u5374\u65e0\u672c\u4e8b\u9000\u5a03\u5a03\uff01\u54c8\uff0c\u54c8\uff0c\u54c8\uff01\u201d\u7b11\u58f0\u672a\u6bd5\u53ea\u89c1\u90a3\u9ed1\u6469\u8bc3\u987b\u7709\u6012\u5f20\uff0c\u5927\u53eb\u9053\uff1a\u201c\u5a03\u5a03\uff0c\u4e0d\u77e5 \u6b7b\u6d3b\uff01\u201d\u8eab\u5f62\u66b4\u8d77\uff0c\u7eff\u5149\u4e00\u957f\uff0c\u75be\u5982\u96f7\u9706\uff0c\u5411\u5f20\u4e39\u67ab\u7684\u989d\u89d2\u5929\u5ead\u731b\u5730\u6233\u4e0b\uff0c\u6765\u52bf\u65e2\u75be\uff0c\u624b\u6cd5 \u53c8\u602a\u5f02\u4e4b\u6781\u3002\u4e91\u857e\u542c\u5b8c\u5f20\u4e39\u67ab\u90a3\u4e24\u53e5\u6b6a\u8bd7\uff0c\u6b63\u81ea\u60f3\u7b11\uff0c\u5634\u5df4\u521a\u521a\u5f20\u5f00\uff0c\u8fd9\u4e00\u4e0b\u5b50\uff0c\u7b11\u58f0\u4f3c\u7a81 \u7136\u88ab\u4eba\u5c01\u4f4f\uff0c\u5374\u201c\u554a\u201d\u7684\u4e00\u58f0\u53eb\u4e86\u51fa\u6765\uff01 \u3000\u3000\u5ffd\u542c\u5f97\u5f20\u4e39\u67ab\u53c8\u662f\u5927\u7b11\u4e00\u58f0\u53eb\u9053\uff1a\u201c\u5a03\u5a03\u6253\u8001\u9a74\u5934\u4e86\uff01\u201d\u811a\u6b65\u4e0d\u52a8\uff0c\u5c0f\u8179\u5185\u9677\uff0c\u8eab\u8eaf\u9661 \u7684\u540e\u79fb\uff0c\u9752\u950b\u4e09\u5c3a\uff0c\u75be\u8d77\u800c\u8fce\uff0c\u8fd9\u4e00\u62db\u62ff\u634f\u65f6\u5019\uff0c\u6070\u5230\u597d\u5904\uff0c\u773c\u770b\u9ed1\u6469\u8bc3\u7684\u4e00\u6761\u957f\u81c2\uff0c\u5c31\u8981 \u88ab\u5f20\u4e39\u67ab\u7684\u5b9d\u5251\u786c\u751f\u751f\u5730\u5207\u4e0b\u3002\u539f\u6765\u4e8c\u4eba\u5404\u4ee5\u4e0a\u4e58\u7684\u5185\u529f\u76f8\u62fc\uff0c\u4e89\u6301\u4e0d\u4e0b\uff0c\u5f20\u4e39\u67ab\u4e0d\u6562\u53d8 \u62db\uff0c\u800c\u9ed1\u6469\u8bc3\u5374\u4ee5\u897f\u57df\u7684\u201c\u78e8\u76d8\u529f\u201d\u89e3\u8131\u51fa\u6765\u3002\u5f20\u4e39\u67ab\u867d\u6ca1\u53d7\u4f24\uff0c\u5374\u662f\u5403\u60ca\u975e\u5c0f\uff0c\u5fc3\u4e2d\u60f3 \u9053\uff1a\u201c\u6211\u65e0\u6cd5\u89e3\u5f00\u8fd9\u76f8\u6301\u4e4b\u5c40\uff0c\u4ed6\u5374\u8131\u8eab\u51fa\u6765\uff0c\u5b9e\u662f\u4e0d\u5bb9\u8f7b\u89c6\u3002\u201d\u65e0\u8ba1\u7834\u654c\uff0c\u6240\u4ee5\u6545\u610f\u51fa\u8a00 \u76f8\u6fc0\u3002\u5f20\u4e39\u67ab\u521d\u5165\u5893\u95e8\u4e4b\u65f6\uff0c\u9ed1\u6469\u8bc3\u770b\u4e0d\u8d77\u4ed6\uff0c\u79f0\u4ed6\u4e3a\u201c\u5927\u5a03\u5a03\u201d\uff0c\u5176\u540e\u89c1\u4ed6\u663e\u51fa\u672c\u9886\uff0c\u624d \u6539\u5bb9\u76f8\u5411\u3002\u800c\u4eca\u5f20\u4e39\u67ab\u6545\u610f\u81ea\u79f0\u201c\u5a03\u5a03\u201d\u51fa\u8a00\u85d0\u89c6\uff0c\u5b9e\u662f\u6709\u5fc3\u6fc0\u6012\u4ed6\u3002 \u3000\u3000\u9ed1\u6469\u8bc3\u679c\u7136\u4e2d\u8ba1\uff0c\u66b4\u6012\u98de\u8d77\uff0c\u75be\u4f7f\u6bd2\u62db\u3002\u54ea\u77e5\u9ad8\u624b\u8f83\u6280\uff0c\u6700\u5fcc\u52a8\u6c14\uff0c\u8fd9\u4e00\u4e0b\u6b63\u9677\u5165\u4e86\u5f20 \u4e39\u67ab\u4ee5\u9759\u5236\u52a8\u7684\u5708\u5957\uff0c\u4f46\u89c1\u5f20\u4e39\u67ab\u4e00\u5251\u659c\u524a\uff0c\u5251\u5149\u900f\u8fc7\u7eff\u5149\uff0c\u5df2\u524a\u5230\u9ed1\u6469\u8bc3\u7684\u81c2\u4e0a\uff0c\u4efb\u4ed6\u6b66 \u529f\u7edd\u9876\uff0c\u4e5f\u96be\u9003\u8fd9\u65ad\u81c2\u4e4b\u707e\uff01 \u3000\u3000\u54ea\u77e5\u9ed1\u6469\u8bc3\u7684\u6b66\u529f\uff0c\u5f02\u4e8e\u4e2d\u571f\uff0c\u4ed6\u7ec3\u6709\u5370\u5ea6\u7684\u745c\u4f3d\u4e4b\u672f\uff0c\u5168\u8eab\u67d4\u82e5\u65e0\u9aa8\uff0c\u5404\u90e8\u808c\u8089\uff0c\u90fd \u53ef\u968f\u610f\u626d\u66f2\u5c48\u4f38\u3002\u5f20\u4e39\u67ab\u6b63\u559c\u5f97\u624b\uff0c\u5ffd\u89c9\u5251\u5c16\u4e00\u6ed1\uff0c\u9ed1\u6469\u8bc3\u7684\u81c2\u818a\u7adf\u626b\u8fc7\u80cc\u540e\uff0c\u968f\u5373\u4e00\u4e2a\u7b4b \u6597\u5012\u7ad6\u5730\u4e0a\uff0c\u53cc\u773c\u5706\u7741\uff0c\u6709\u5982\u94dc\u94c3\uff0c\u66b4\u6012\u53eb\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u4ffa\u4e0e\u4f60\u62fc\u4e86\uff01\u201d\u500f\u5730\u8df3\u4e86\u8d77\u6765\uff0c \u4ee5\u8db3\u4f5c\u624b\uff0c\u62a1\u8d77\u7389\u6756\uff0c\u6311\u5411\u5f20\u4e39\u67ab\u7684\u4e39\u7530\u8981\u7a74\uff01\u6756\u6cd5\u4e4b\u602a\uff0c\u4e16\u7f55\u5176\u4f26\uff01 \u3000\u3000\u5f20\u4e39\u67ab\u8fd0\u5251\u5982\u98ce\uff0c\u7728\u773c\u4e4b\u95f4\uff0c\u8fd8\u51fb\u6570\u62db\uff0c\u4f46\u89c1\u90a3\u9ed1\u6469\u8bc3\u65f6\u800c\u98de\u8eab\u8dc3\u8d77\uff0c\u65f6\u800c\u5012\u7ad6\u5730\u4e0a\uff0c \u624b\u8db3\u5e76\u7528\uff0c\u628a\u5b9d\u6756\u62a1\u5f97\u547c\u547c\u98ce\u54cd\u62db\u6570\u602a\u7edd\uff0c\u653b\u52bf\u731b\u6781\u3002\u4e91\u857e\u5012\u5438\u4e00\u53e3\u51c9\u6c14\uff0c\u5b9a\u775b\u770b\u65f6\uff0c\u53ea\u89c1 \u5f20\u4e39\u67ab\u53e3\u89d2\u655b\u4e86\u7b11\u5bb9\uff0c\u5728\u7eff\u5149\u7b3c\u7f69\u4e4b\u4e0b\uff0c\u7adf\u662f\u51dd\u8eab\u4e0d\u52a8\uff0c\u957f\u5251\u6325\u821e\uff0c\u6709\u5982\u767d\u8679\u8d2f\u65e5\uff0c\u5728\u7eff\u8272 \u5149\u5708\u4e4b\u4e0b\uff0c\u4e1c\u4e00\u6307\uff0c\u897f\u4e00\u5212\uff0c\u51fa\u624b\u5e76\u4e0d\u89c1\u5feb\u4f46\u6bcf\u4e00\u62db\u90fd\u662f\u5999\u5230\u6beb\u98a0\uff0c\u6070\u6070\u5c06\u9ed1\u6469\u8bc3\u7684\u653b\u52bf\u5316 \u5f00\u3002\u770b\u4ed6\u5251\u950b\u660e\u662f\u4e1c\u6307\uff0c\u5374\u5ffd\u5730\u504f\u5411\u897f\u8fb9\uff0c\u660e\u662f\u5411\u53f3\u524a\u53bb\uff0c\u5374\u4e0d\u77e5\u600e\u7684\uff0c\u51fa\u624b\u4e4b\u540e\uff0c\u5374\u662f\u5411 \u5de6\u6233\u6765\uff0c\u800c\u6bcf\u4e00\u62db\u90fd\u662f\u653b\u654c\u4e4b\u6240\u5fc5\u6551\u5b88\u654c\u4e4b\u6240\u5fc5\u653b\uff0c\u9ed1\u6469\u8bc3\u7684\u653b\u52bf\u5982\u98ce\u72c2\u96e8\u9aa4\uff0c\u5374\u662f\u65e0\u6cd5\u4f7f \u4ed6\u79fb\u52a8\u534a\u6b65\u3002\u9ed1\u6469\u8bc3\u7684\u6756\u6cd5\u4e43\u662f\u897f\u571f\u79d8\u4f20\uff0c\u4e2d\u571f\u7f55\u89c1\u7684\u6b66\u6797\u7edd\u5b66\uff1a\u5929\u6469\u6756\u6cd5\u3002\u6597\u4e86\u4e00\u767e\u6765 \u62db\uff0c\u7adf\u5bfb\u4e0d\u5230\u654c\u4eba\u534a\u70b9\u7834\u7efd\uff0c\u4e5f\u4e0d\u89c9\u5012\u5438\u4e86\u4e00\u53e3\u51c9\u6c14\u3002\u767d\u6469\u8bc3\u5728\u65c1\u864e\u89c6\u7708\u7708\uff0c\u4f46\u4ee5\u6709\u8a00\u5728 \u5148\uff0c\u4e0d\u4fbf\u51fa\u624b\u76f8\u52a9\u3002 \u3000\u3000\u4e24\u4eba\u5404\u4ee5\u602a\u5f02\u62db\u6570\u640f\u51fb\uff0c\u76f8\u6301\u4e0d\u4e0b\uff0c\u4f46\u542c\u5f97\u5893\u95e8\u4e4b\u5916\uff0c\u6668\u9e21\u52a8\u91ce\uff0c\u98de\u9e1f\u9e23\u6797\uff0c\u4e0d\u77e5\u4e0d\u89c9 \u5df2\u662f\u6e05\u6668\u65f6\u5206\u3002\u9ed1\u6469\u8bc3\u4e45\u6218\u4e0d\u4e0b\u7126\u8e81\u5f02\u5e38\uff0c\u640f\u51fb\u66f4\u70c8\uff0c\u5f20\u4e39\u67ab\u4ecd\u662f\u4e0d\u4e3a\u6240\u52a8\uff0c\u811a\u8ddf\u72b9\u5982\u9489\u7262 \u5728\u5730\u4e0a\u4e00\u822c\uff0c\u5251\u52bf\u4e0d\u75be\u4e0d\u5f90\uff0c\u7adf\u4f3c\u624b\u6325\u4e94\u5f26\uff0c\u76ee\u9001\u98de\u9e3f\uff0c\u51dd\u91cd\u4e4b\u6781\u800c\u53c8\u6f47\u6d12\u4e4b\u6781\uff01 \u3000\u3000\u4e91\u857e\u770b\u5f97\u773c\u82b1\u7f2d\u4e71\uff0c\u5fc3\u4e2d\u6697\u6697\u79f0\u5947\uff0c\u987b\u77e5\u4e91\u857e\u81ea\u5c0f\u4fbf\u8ddf\u98de\u5929\u9f99\u5973\u53f6\u76c8\u76c8\u5b66\u5251\uff0c\u5e74\u7eaa\u867d\u7136 \u53ea\u6709\u5341\u4e03\u5c81\uff0c\u5374\u5df2\u5b66\u4e86\u5341\u5e74\u3002\u53f6\u76c8\u76c8\u7684\u5251\u672f\uff0c\u5728\u6b66\u6797\u4e4b\u4e2d\uff0c\u6570\u4e00\u6570\u4e8c\uff0c\u5bf9\u5404\u5bb6\u5404\u6d3e\u7684\u5251\u672f\u65e0 \u4e0d\u901a\u6653\uff0c\u56e0\u6b64\u4e91\u857e\u867d\u662f\u5e74\u7eaa\uff0c\u5bf9\u4e8e\u5251\u672f\u4e00\u9053\uff0c\u5374\u79f0\u5f97\u4e0a\u662f\u4e2a\u201c\u5927\u884c\u5bb6\u201d\uff0c\u53ea\u8981\u522b\u4eba\u4e00\u4f38\u624b\uff0c \u4e00\u51fa\u62db\uff0c\u5c31\u80fd\u77e5\u9053\u4ed6\u7684\u5b97\u6d3e\u6765\u5386\u3002\u504f\u504f\u4eca\u665a\u770b\u4e86\u534a\u591c\uff0c\u5374\u4e00\u70b9\u4e5f\u770b\u4e0d\u51fa\u5f20\u4e39\u67ab\u7684\u5251\u672f\u6e0a\u6e90\uff0c \u4f46\u89c9\u4ed6\u7684\u5251\u672f\u4e5f\u597d\u4f3c\u81ea\u5df1\u6240\u5b66\u7684\u4e00\u6837\uff0c\u5305\u542b\u6709\u5404\u5bb6\u5404\u6d3e\u7684\u6210\u4efd\uff0c\u4f46\u51fa\u624b\u62db\u6570\uff0c\u5374\u53c8\u4e0e\u81ea\u5df1\u6240 \u5b66\u7684\u5927\u4e0d\u76f8\u540c\uff0c\u4e0d\u7531\u5f97\u7eb3\u7f55\u4e4b\u6781\uff01 \u3000\u3000\u518d\u770b\u4e9b\u65f6\uff0c\u5ffd\u53c8\u89c9\u5f20\u4e39\u67ab\u6b64\u5957\u5251\u6cd5\u4f3c\u66fe\u76f8\u8bc6\uff0c\u5374\u53c8\u504f\u504f\u8bf4\u4e0d\u51fa\u540d\u6765\u3002\u4e91\u857e\u7ec6\u7ec6\u601d\u91cf\uff0c\u8fd9 \u5957\u5251\u6cd5\u81ea\u5df1\u53c8\u660e\u660e\u6ca1\u6709\u89c1\u8fc7\uff0c\u800c\u4e14\u4e5f\u4ece\u672a\u542c\u5e08\u7236\u8bf4\u8fc7\u6709\u8fd9\u79cd\u602a\u5f02\u7684\u5251\u6cd5\uff0c\u81ea\u5df1\u600e\u7684\u5374\u4f1a\u6709\u5982 \u6b64\u5fae\u5999\u7684\u3001\u4f3c\u66fe\u76f8\u8bc6\u7684\u611f\u89c9\uff1f\u771f\u662f\u8d8a\u60f3\u8d8a\u5947\uff0c\u83ab\u660e\u6240\u4ee5\u3002\u4f46\u89c9\u4ed6\u6bcf\u4e00\u62db\u867d\u7136\u90fd\u662f\u51fa\u4e4e\u81ea\u5df1\u610f \u6599\u4e4b\u5916\uff0c\u4f46\u5230\u4ed6\u51fa\u624b\u4e4b\u540e\uff0c\u5374\u53c8\u89c9\u5f97\u6bcf\u4e00\u62db\u90fd\u201c\u6df1\u5408\u543e\u5fc3\u201d\uff0c\u597d\u4f3c\u81ea\u5df1\u60f3\u8bf4\u4e00\u53e5\u8bdd\uff0c\u8fd8\u672a\u60f3 \u5230\u5982\u4f55\u8868\u8fbe\uff0c\u5374\u5ffd\u7136\u7ed9\u522b\u4eba\u5148\u884c\u8bf4\u4e86\uff0c\u800c\u53c8\u8bf4\u5f97\u975e\u5e38\u4e4b\u5999\uff0c\u4ee4\u81ea\u5df1\u53c8\u662f\u4f69\u670d\uff0c\u53c8\u662f\u75db\u5feb\uff0c\u65e2 \u51fa\u610f\u5916\uff0c\u53c8\u5728\u610f\u4e2d\u3002 \u3000\u3000\u4e91\u857e\u5168\u795e\u8d2f\u6ce8\uff0c\u5ffd\u5730\u5fc3\u5934\u597d\u50cf\u6709\u4e00\u9053\u7535\u5149\u95ea\u8fc7\uff0c\u84e6\u7136\u611f\u5230\u5f20\u4e39\u67ab\u8fd9\u5957\u5251\u6cd5\u867d\u662f\u4e0e\u81ea\u5df1\u6240 \u5b66\u7684\u5927\u4e0d\u76f8\u540c\uff0c\u4f46\u5374\u53c8\u4f3c\u662f\u4e0e\u81ea\u5df1\u6240\u5b66\u7684\u76f8\u514b\u76f8\u751f\uff0c\u53ef\u4ee5\u4e92\u76f8\u914d\u5408\uff0c\u5c31\u5982\u4e00\u5bf9\u5b6a\u751f\u5144\u5f1f\uff0c\u5fc3 \u7075\u4ea4\u611f\uff0c\u547c\u5438\u76f8\u901a\uff01 \u3000\u3000\u8fd9\u65f6\u4e91\u857e\u4f46\u89c9\u5f97\u5fc3\u795e\u604d\u60da\uff0c\u6d6e\u60f3\u8054\u7fe9\uff0c\u573a\u4e2d\u7684\u9ed1\u6469\u8bc3\u4e0e\u5f20\u4e39\u67ab\u867d\u7136\u8fd8\u5728\u6fc0\u6218\uff0c\u5979\u5374\u597d\u50cf \u89c6\u800c\u4e0d\u89c1\uff0c\u542c\u800c\u4e0d\u95fb\uff0c\u7a81\u7136\u60f3\u8d77\u4e0b\u5c71\u524d\u5915\uff0c\u5e08\u7236\u5bf9\u5979\u6240\u8bf4\u7684\u8bdd\u6765\u3002 \u3000\u3000\u90a3\u662f\u4e00\u4e2a\u9664\u5915\u4e4b\u591c\uff0c\u5ddd\u5317\u5c0f\u5bd2\u5c71\u7684\u5c71\u5cf0\u4e4b\u4e0a\u6709\u4e00\u95f4\u77f3\u5c4b\uff0c\u77f3\u5c4b\u5185\u70b9\u7740\u5341\u4e8c\u679d\u7c97\u5982\u4eba\u81c2\u7684 \u725b\u6cb9\u5de8\u70db\uff0c\u70db\u7684\u5f0f\u6837\u548c\u679d\u6570\uff0c\u90fd\u5982\u4eca\u665a\u6240\u89c1\u7684\u4e00\u6837\u3002\u6d4a\u5149\u8f6c\u7ed5\u4e4b\u4e2d\uff0c\u5750\u7740\u4e00\u4e2a\u4e2d\u5e74\u5973\u5b50\u548c\u4e00 \u4e2a\u8273\u82e5\u9c9c\u82b1\u7684\u5c11\u5973\uff0c\u8fd9\u5c31\u662f\u98de\u5929\u9f99\u5973\u53f6\u76c8\u76c8\u548c\u5979\u552f\u4e00\u7684\u7231\u5f92\u4e91\u857e\u4e86\u3002\u5c4b\u5185\u6446\u6709\u9152\u98df\u4f46\u5374\u4e0d\u662f \u9664\u5915\u6b22\u5bb4\uff0c\u800c\u662f\u5e08\u5f92\u76f8\u522b\u7684\u79bb\u7b75\uff0c\u539f\u6765\u53f6\u76c8\u76c8\u66ff\u5979\u7684\u5f92\u5f1f\u996f\u884c\uff0c\u4e91\u857e\u6b66\u827a\u5df2\u6210\uff0c\u9075\u5949\u5e08\u7236\u4e4b \u547d\uff0c\u660e\u5929\u4fbf\u8981\u4e0b\u5c71\u4e86\u3002 \u3000\u3000\u4e91\u857e\u65e9\u5df2\u4ece\u5e08\u7236\u53e3\u4e2d\u77e5\u9053\u81ea\u5df1\u4e00\u5bb6\u7684\u8840\u6d77\u6df1\u4ec7\uff0c\u65e0\u65f6\u65e0\u523b\u4e0d\u60f3\u4e0b\u5c71\u5219\u65e5\u62a5\u4ec7\uff0c\u53ef\u662f\u4eca\u665a \u5e08\u7236\u66ff\u5979\u996f\u884c\uff0c\u5374\u9887\u51fa\u5979\u610f\u6599\u4e4b\u5916\u3002\u4e3a\u4ec0\u4e48\u65e9\u4e0d\u53eb\u8d70\uff0c\u8fdf\u4e0d\u53eb\u8d70\uff0c\u5374\u504f\u504f\u5728\u9664\u5915\u4e4b\u591c\u66ff\u5979\u996f \u884c\u5462\uff1f\u4e91\u857e\u4e00\u8fb9\u542c\u5e08\u7236\u7684\u5631\u5490\uff0c\u4e00\u8fb9\u5fc3\u4e2d\u6697\u81ea\u601d\u7591\uff0c\u9762\u4e0a\u9732\u51fa\u7591\u60d1\u7684\u989c\u8272\u3002\u53f6\u76c8\u76c8\u4e5f\u4f3c\u89c9\u5bdf \u5230\u4e86\uff0c\u4e00\u53e3\u4e00\u53e3\u7684\u559d\u9152\uff0c\u8fde\u5c3d\u4e86\u4e09\u5927\u676f\uff0c\u5ffd\u5730\u559f\u7136\u53f9\u9053\uff1a\u201c\u4e00\u5e74\u5c06\u5c3d\u591c\uff0c\u4e07\u91cc\u672a\u5f52\u4eba\u3002\u5341\u4e8c \u5e74\u524d\u6211\u9001\u8d70\u4e86\u4e00\u4e2a\u4eba\uff0c\u4e0d\uff0c\u662f\u8d76\u8d70\u4e86\u4e00\u4e2a\u4eba\uff0c\u4eca\u665a\u6211\u53c8\u8981\u9001\u4f60\u79bb\u5f00\u4e86\u3002\u201d \u3000\u3000\u4e91\u857e\u542c\u5f97\u6ca1\u5934\u6ca1\u8111\uff0c\u4e0d\u6562\u7f6e\u7b54\u3002\u98de\u5929\u9f99\u5973\u53f9\u606f\u4e4b\u540e\uff0c\u5b9a\u795e\u671b\u7740\u4e91\u857e\uff0c\u5ffd\u9053\uff1a\u201c\u4f60\u4eca\u540e\u5982 \u5230\u8499\u53e4\uff0c\u89c1\u7740\u4e00\u4e2a\u4eba\uff0c\u4f60\u5c31\u8bf4\u6211\u53eb\u4ed6\u56de\u6765\u3002\u201d\u4e91\u857e\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\u5440\uff1f\u201d\u98de\u5929\u9f99\u5973\u542c\u5979\u4e00\u95ee\uff0c \u54d1\u7136\u5931\u7b11\uff0c\u5ffd\u800c\u9762\u4e0a\u73b0\u51fa\u7ea2\u6655\uff0c\u53c8\u559d\u4e86\u4e00\u676f\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u7684\u4e09\u5e08\u4f2f\u8c22\u5929\u534e\u3002\u201d\u4e91\u857e\u5947 \u9053\uff1a\u201c\u4e09\u5e08\u4ef6\u8c22\u5929\u534e\uff1f\u4ed6\u4e0d\u662f\u5230\u4e86\u8499\u53e4\uff0c\u8981\u66ff\u6211\u7684\u7237\u7237\u62a5\u4ec7\uff0c\u53bb\u523a\u6740\u5f20\u5b97\u5468\u7684\u5417\uff1f\u201d\u53f6\u76c8\u76c8 \u8bf4\u9053\uff1a\u201c\u662f\u5440\u3002\u4ed6\u53bb\u8499\u53e4\u662f\u5341\u5e74\u524d\u4e4b\u4e8b\uff0c\u53ef\u662f\u4ed6\u79bb\u5f00\u6211\uff0c\u5374\u662f\u5341\u4e8c\u5e74\u524d\u7684\u4eca\u665a\u3002\u4ed6\u7684\u6b66\u529f\u9ad8 \u5f3a\uff0c\u4eba\u53c8\u6c89\u6bc5\u673a\u667a\uff0c\u4ed6\u8bf4\u66ff\u4f60\u7237\u7237\u62a5\u4ec7\u90a3\u5c31\u4e00\u5b9a\u62a5\u5f97\u4e86\u3002\u800c\u4e14\u4e00\u5b9a\u7528\u4e0d\u4e86\u5341\u5e74\u3002\u201d\u4e91\u857e\u9053\uff1a \u201c\u90a3\u4e48\u4ed6\u4e3a\u4ec0\u4e48\u5341\u5e74\u6765\u4e00\u76f4\u6ca1\u6709\u4fe1\u606f\uff1f\u201d\u53f6\u76c8\u76c8\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u6211\u731c\u4ed6\u662f\u4e0d\u613f\u56de\u6765\u4e86\u3002\u201d\u4e91\u857e \u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d\u53f6\u76c8\u76c8\u5ffd\u800c\u8f6c\u8fc7\u8bdd\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u5929\u4e0b\u5404\u5bb6\u5404\u6d3e\u7684\u5251\u6cd5\u6211\u90fd\u901a\u6653\uff0c\u5c31\u662f\u6709\u4e00 \u5bb6\u7684\u5251\u6cd5\u6ca1\u6709\u89c1\u8fc7\uff0c\u4f60\u8bf4\u5947\u4e0d\u5947\u602a\uff1f\u201d\u4e91\u857e\u5fc3\u9053\uff1a\u201c\u5929\u4e0b\u4e4b\u5927\u6d3e\u522b\u4e4b\u591a\uff0c\u6709\u4e00\u5bb6\u7684\u5251\u6cd5\u672a\u66fe \u89c1\u8fc7\uff0c\u4e5f\u6ca1\u4ec0\u4e48\u5947\u602a\u3002\u201d\u4e0d\u60f3\u5979\u7684\u5e08\u7236\uff0c\u7d27\u63a5\u7740\u8bf4\u51fa\u4e00\u53e5\u8bdd\uff0c\u679c\u7136\u4ee4\u4e91\u857e\u5927\u4e3a\u60ca\u5947\uff0c\u5979\u5e08\u7236 \u9053\uff1a\u201c\u90a3\u5c31\u662f\u6211\u4eec\u81ea\u5df1\u672c\u95e8\u7684\u5251\u6cd5\uff01\u201d \u3000\u3000\u53e4\u5893\u91cc\u7684\u5927\u5385\u4e0a\u70db\u5f71\u6447\u7ea2\uff0c\u4e91\u857e\u51dd\u795e\u601d\u7d22\u5f80\u4e8b\uff0c\u5728\u70db\u5149\u6643\u8361\u4e4b\u4e2d\uff0c\u4f3c\u4e4e\u73b0\u51fa\u5e08\u7236\u5f53\u65f6\u61ca \u6094\u7684\u9762\u5b54\u3002\u5979\u7ee7\u7eed\u60f3\u4e0b\u53bb\uff1a\u201c\u90a3\u65f6\u6211\u4e5f\u5f88\u4e3a\u5947\u602a\uff0c\u4fbf\u95ee\u5e08\u7236\u3002\u5e08\u7236\u9053\uff1a\u2018\u4f60\u4e0d\u77e5\u9053\uff0c\u4f60\u73b0\u5728 \u6240\u5b66\u7684\u867d\u7136\u4ea6\u53ef\u4ee5\u81ea\u6210\u4e00\u5bb6\uff0c\u4f46\u5b9e\u5728\u8bf4\u6765\uff0c\u5374\u53ea\u662f\u672c\u95e8\u4e2d\u7684\u534a\u5957\u5251\u6cd5\u800c\u5df2\u3002\u2019\u6211\u518d\u95ee\u4e0b\u53bb\u624d \u77e5\u9053\u539f\u6765\u5e08\u7956\u7384\u673a\u9038\u58eb\u813e\u6c14\u751a\u602a\uff0c\u4ed6\u6240\u77e5\u6781\u535a\uff0c\u800c\u6700\u5f97\u610f\u7684\u5374\u662f\u4ed6\u522b\u51fa\u5fc3\u88c1\u72ec\u521b\u7684\u4e24\u5957\u5251 \u6cd5\uff0c\u4e00\u5957\u540d\u4e3a\u2018\u4e07\u6d41\u671d\u6d77\u5143\u5143\u5251\u6cd5\u2019\uff0c\u53e6\u4e00\u5957\u540d\u4e3a\u2018\u767e\u53d8\u9634\u9633\u7384\u673a\u5251\u6cd5\u2019\uff0c\u5e08\u7236\u548c\u4e09\u5e08\u4f2f\u5404 \u5f97\u4e00\u5957\uff0c\u5b9e\u662f\u534a\u5957\u3002\u5e08\u7956\u8bf4\uff1a\u2018\u4ed6\u94bb\u7814\u51fa\u8fd9\u4e24\u5957\u5251\u6cd5\u4e43\u662f\u5343\u53e4\u6b66\u5b66\u4e4b\u79d8\uff0c\u4e07\u4e0d\u53ef\u540c\u6388\u4e8e\u4e00 \u4eba\u3002\u82e5\u4ee5\u4eba\u7269\u6bd4\u62df\u5251\u672f\uff0c\u5219\u5143\u5143\u5251\u6cd5\u6709\u5982\u5367\u9f99\uff0c\u7384\u673a\u5251\u6cd5\u6709\u5982\u51e4\u96cf\uff0c\u5367\u9f99\u51e4\u96cf\uff0c\u4e0d\u53ef\u540c\u5f52\u4e8e \u4e00\u4e3b\uff0c\u5f52\u5fc5\u6709\u7978\u3002\u2019\u6240\u4ee5\u4e25\u7981\u4ed6\u4eec\u4e8c\u4eba\uff0c\u4e0d\u8bb8\u79c1\u81ea\u6388\u53d7\uff01\u201d\u4e91\u857e\u6b63\u5728\u51fa\u795e\u601d\u60f3\uff0c\u5ffd\u542c\u5f97\u5f20\u4e39 \u67ab\u54c8\u54c8\u5927\u7b11\uff0c\u9ed1\u6469\u8bc3\u4e00\u58f0\u5927\u53eb\uff01 \u3000\u3000\u4e91\u857e\u601d\u8def\u88ab\u6253\u65ad\uff0c\u62ac\u5934\u4e00\u770b\uff0c\u539f\u6765\u662f\u5f20\u4e39\u67ab\u4e0e\u9ed1\u6469\u8bc3\u4ea4\u6362\u4e86\u4e00\u62db\u9669\u62db\uff0c\u9ed1\u6469\u8bc3\u6a2a\u6756\u75be \u626b\uff0c\u4e0d\u6599\u4e00\u51fb\u4e0d\u4e2d\uff0c\u53cd\u800c\u9669\u88ab\u5f20\u4e39\u67ab\u523a\u4e2d\u808b\u80c1\u3002\u4e8c\u4eba\u6362\u4e86\u4e00\u62db\u4e4b\u540e\uff0c\u90fd\u4e0d\u6562\u5192\u9669\u8e81\u8fdb\uff0c\u53c8\u5728 \u90a3\u91cc\u50f5\u6301\u8d77\u6765\u3002 \u3000\u3000\u5251\u98ce\u864e\u864e\uff0c\u70db\u5149\u6447\u6643\uff0c\u4e91\u857e\u5fc3\u5ff5\u4e00\u52a8\uff0c\u84e6\u7136\u60f3\u9053\uff1a\u201c\u83ab\u975e\u5f20\u4e39\u67ab\u8fd9\u5957\u5251\u6cd5\uff0c\u5c31\u662f\u6211\u5e08\u7236 \u4ece\u672a\u89c1\u8fc7\u7684\u90a3\u5957\u672c\u95e8\u5251\u6cd5\uff1f\u96be\u9053\u4ed6\u662f\u4e09\u5e08\u4f2f\u5728\u8499\u53e4\u6240\u6536\u7684\u5f92\u5f1f\u4e48\uff1f\u4f46\u770b\u4ed6\u5251\u6cd5\u7684\u7cbe\u5999\u548c\u529f\u529b \u7684\u6df1\u539a\uff0c\u7eb5\u662f\u6709\u540d\u5e08\u4f20\u6388\uff0c\u4ea6\u975e\u6709\u5341\u5e74\u4ee5\u4e0a\u7684\u78e8\u7ec3\u4e0d\u884c\uff0c\u4e09\u5e08\u4f2f\u4e00\u5fd7\u66ff\u6211\u7237\u7237\u590d\u4ec7\uff0c\u65ad\u65e0\u4e00 \u5230\u8499\u53e4\u5c31\u7acb\u523b\u6536\u5f92\uff0c\u4e13\u5fc3\u6388\u4e1a\u7684\u9053\u7406\u3002\u201d\u5979\u56de\u60f3\u5927\u5e08\u4f2f\u8463\u5cb3\u7ed9\u91d1\u5200\u5be8\u4e3b\u5468\u5065\u7684\u4fe1\uff0c\u201c\u800c\u4e14\uff0c \u542c\u8bf4\u4e09\u5e08\u4f2f\u5df2\u88ab\u654c\u4eba\u6349\u83b7\uff0c\u5e7d\u7981\u80e1\u5bab\uff0c\u90a3\u66f4\u65ad\u65ad\u4e0d\u4f1a\u5728\u8499\u53e4\u7687\u5bab\u6536\u4e0b\u5f92\u5f1f\uff0c\u5c31\u7b97\u9000\u4e00\u4e07\u6b65\u6765 \u8bf4\uff0c\u6536\u4e0b\u5f92\u5f1f\uff0c\u4e5f\u65ad\u65ad\u4e0d\u4f1a\u662f\u4e2a\u6c49\u4eba\u5440\u3002\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\u5462\uff1f\u201d\u4e91\u857e\u767e\u601d\u4e0d\u5f97\u5176\u89e3\u3002\u5979\u53c8\u60f3 \u9053\uff1a\u201c\u6211\u5e08\u7236\u6781\u8d5e\u4e09\u5e08\u4f2f\u7684\u672c\u9886\uff0c\u8bf4\u4ed6\u8a00\u51fa\u5fc5\u884c\uff0c\u65e2\u80af\u5e94\u627f\u66ff\u6211\u7237\u7237\u62a5\u4ec7\uff0c\u8fd9\u4ec7\u5c31\u4e00\u5b9a\u80fd\u62a5 \u5f97\u4e86\uff0c\u800c\u4e14\u7528\u4e0d\u4e86\u5341\u5e74\u3002\u5979\u53c8\u54ea\u91cc\u6599\u60f3\u5f97\u5230\uff0c\u5f20\u5b97\u5468\u8fd9\u25a1\u73b0\u5728\u4ecd\u5728\u8499\u53e4\u53d1\u53f7\u65bd\u4ee4\uff0c\u800c\u4e09\u5e08\u4f2f \u53cd\u800c\u662f\u5b58\u4ea1\u83ab\u6d4b\uff01\u5440\uff0c\u5e08\u7236\uff0c\u4f60\u597d\u53ef\u601c\u5440\uff01\u201d\u8111\u6d77\u4e2d\u4e0d\u89c9\u53c8\u6d6e\u73b0\u51fa\u5e08\u7236\u90a3\u665a\u66ff\u5979\u996f\u884c\u7684\u795e \u60c5\u3002\u5e08\u7236\u9152\u91cf\u7d20\u8c6a\uff0c\u90a3\u665a\u5927\u676f\u5927\u676f\u5730\u559d\u9152\uff0c\u559d\u5230\u540e\u6765\uff0c\u4e5f\u4e0d\u89c9\u9189\u4e86\u3002\u5ffd\u7136\u628a\u8863\u8896\u9ad8\u5377\uff0c\u53ea\u89c1 \u81c2\u4e0a\u5251\u75d5\u4ea4\u9519\uff0c\u7adf\u5728\u81c2\u4e0a\u523b\u51fa\u4e00\u6735\u7ea2\u82b1\u3002\u5e08\u7236\u54fd\u54bd\u8bf4\u9053\uff1a\u201c\u857e\u513f\uff0c\u4e00\u4e2a\u4eba\u5343\u4e07\u4e0d\u53ef\u4efb\u6027\uff0c\u4efb \u6027\u800c\u884c\uff0c\u505a\u9519\u4e86\u4e8b\uff0c\u90a3\u5c31\u540e\u6094\u8fdf\u4e86\u3002\u5341\u4e8c\u5e74\u524d\uff0c\u6211\u8d76\u8d70\u4e86\u4f60\u7684\u8c22\u5e08\u4f2f\uff0c\u4ee5\u540e\u6bcf\u5e74\u9664\u5915\uff0c\u6211\u5c31 \u5fc3\u75db\u5982\u5272\uff0c\u5fcd\u53d7\u4e0d\u4f4f\uff0c\u4fbf\u62d4\u51fa\u9752\u51a5\u5b9d\u5251\uff0c\u5728\u81c2\u4e0a\u90a3\u4e48\u4e00\u5212\uff0c\u54c8\uff0c\u54c8\uff0c\u8fd9\u5012\u662f\u4e2a\u7075\u65b9\uff0c\u81c2\u4e0a\u75db \u6781\uff0c\u5fc3\u4e0a\u7684\u75db\u695a\u5c31\u51cf\u8f7b\u4e86\u3002\u6211\u4e00\u5212\u5c31\u662f\u4e00\u74e3\u82b1\u74e3\uff0c\u4f60\u770b\u5440\uff0c\u8fd9\u6735\u6d78\u900f\u6211\u9c9c\u8840\u7684\u5927\u7ea2\u82b1\uff0c\u7f8e\u4e0d \u7f8e\u5440\uff1f\u201d\u4e91\u857e\u7ec6\u5fc3\u4e00\u6570\uff0c\u6b63\u662f\u5341\u4e8c\u74e3\u82b1\u74e3\uff0c\u4e0d\u89c9\u6253\u4e86\u4e00\u4e2a\u5bd2\u98a4\u3002\u53ea\u542c\u5f97\u5979\u5e08\u7236\u53c8\u8bf4\u9053\uff1a\u201c\u4f60 \u5728\u6211\u95e8\u4e0b\u5341\u5e74\uff0c\u8fd9\u4e2a\u6545\u4e8b\u4f60\u53ef\u8fd8\u6ca1\u542c\u6211\u8bf4\u8fc7\u3002\u4f60\u77e5\u9053\u5341\u4e09\u5e74\u524d\uff0c\u6211\u5c31\u50cf\u4f60\u4e00\u6837\uff0c\u662f\u4e2a\u5e74\u8f7b\u597d \u4e8b\u7684\u5c11\u5973\uff0c\u800c\u4e14\u6211\u6bd4\u4f60\u597d\u80dc\u4efb\u6027\u5f97\u591a\uff0c\u5bf9\u81ea\u5df1\u4e0d\u77e5\u9053\u7684\u4e8b\u60c5\uff0c\u603b\u662f\u60f3\u5c3d\u529e\u6cd5\u77e5\u9053\u3002\u4f60\u5e08\u7956\u4e25 \u7981\u6211\u4eec\u79c1\u76f8\u6388\u53d7\uff0c\u8fde\u7ec3\u5251\u65f6\u90fd\u8981\u9694\u5f00\uff0c\u5e08\u7956\u7684\u7981\u4ee4\u8d8a\u4e25\u6211\u5c31\u8d8a\u53d1\u597d\u5947\uff0c\u5929\u534e\u4e0e\u6211\u60c5\u5982\u5144\u59b9\uff0c \u504f\u504f\u5728\u8fd9\u5173\u8282\u4e0a\u5934\u4e0d\u80af\u653e\u677e\uff0c\u4e00\u70b9\u4e5f\u4e0d\u80af\u900f\u9732\u3002\u4f60\u5e08\u7956\u95e8\u4e0b\uff0c\u5171\u6709\u4e94\u4eba\uff0c\u9664\u4e86\u4f60\u7684\u7236\u4eb2\u4e91\u6f84 \u672a\u6ee1\u5e08\u4fbf\u5230\u8499\u53e4\u4e4b\u5916\uff0c\u6211\u4eec\u56db\u4eba\u5404\u5f97\u4e00\u5957\u6b66\u827a\uff0c\u51fa\u5e08\u4e4b\u540e\u5404\u6210\u4e00\u5bb6\uff0c\u5929\u534e\u4e0e\u6211\u6765\u5f80\u6700\u5bc6\uff0c\u6211 \u597d\u51e0\u6b21\u8feb\u4ed6\uff0c\u4ed6\u90fd\u4e0d\u80af\u628a\u6240\u5b66\u7684\u5251\u6cd5\u663e\u9732\uff0c\u5176\u5b9e\u6211\u4e5f\u4e0d\u662f\u6709\u5fc3\u8981\u5b66\u4ed6\u7684\u5251\u6cd5\uff0c\u53ea\u662f\u60f3\u5f00\u5f00\u773c \u754c\u7f62\u4e86\u3002\u4ed6\u5e73\u65e5\u5bf9\u6211\u5343\u4f9d\u767e\u987a\uff0c\u5c31\u662f\u4e00\u8c08\u5230\u5404\u4eba\u6240\u5b66\uff0c\u4fbf\u95ed\u53e3\u4e0d\u8a00\u3002\u6709\u4e00\u5e74\u9664\u5915\u4e4b\u591c\uff0c\u4ed6\u5230 \u5c0f\u5bd2\u5c71\u770b\u6211\uff0c\u6211\u53c8\u8feb\u4ed6\u663e\u9732\u5251\u6cd5\uff0c\u4ed6\u50cf\u4ee5\u5f80\u4e00\u6837\uff0c\u5fae\u7b11\u4e0d\u8bed\u3002\u6211\u751f\u6c14\u4e86\uff0c\u9a82\u9053\uff1a\u2018\u539f\u6765\u4f60\u5e73 \u65e5\u8bf4\u600e\u6837\u600e\u6837\u559c\u6b22\u6211\uff0c\u90fd\u662f\u5047\u7684\u3002\u2019\u4ed6\u9762\u8272\u4e00\u4e0b\u5b50\u82cd\u767d\uff0c\u5634\u5507\u52a8\u4e86\u51e0\u4e0b\uff0c\u5374\u4ecd\u662f\u6b32\u8bf4\u8fd8\u4f11\u3002 \u6211\u62d4\u51fa\u9752\u51a5\u5b9d\u5251\uff0c\u7acb\u523b\u5411\u4ed6\u80f8\u53e3\u523a\u53bb\u3002\u201d \u3000\u3000\u201c\u6211\u672c\u610f\u662f\u60f3\u8feb\u4ed6\u62d4\u5251\u62b5\u6321\uff0c\u4ee5\u4fbf\u7aa5\u5bdf\u4ed6\u6240\u5b66\u5f97\u7684\u672c\u95e8\u5251\u6cd5\uff0c\u54ea\u77e5\u4ed6\u7adf\u6beb\u4e0d\u62b5\u6321\uff0c\u6211\u4e00 \u5251\u523a\u53bb\uff0c\u6536\u62db\u5df2\u6765\u4e0d\u53ca\uff0c\u5251\u950b\u4e00\u659c\uff0c\u5728\u4ed6\u81c2\u4e0a\u62c9\u5f00\u4e86\u957f\u957f\u7684\u4e00\u9053\u4f24\u53e3\uff0c\u9c9c\u8840\u4e00\u70b9\u4e00\u70b9\u5730\u6ef4\u5728 \u767d\u7691\u7691\u7684\u96ea\u5730\u4e0a\uff0c\u6709\u5982\u5728\u6d01\u767d\u65e0\u7455\u7684\u5b9d\u77f3\u4e0a\u5d4c\u4e0a\u76f8\u601d\u7ea2\u8c46\u3002\u6211\u6599\u4e0d\u5230\u4ed6\u4f1a\u5982\u6b64\uff0c\u63d0\u5251\u5446\u7acb\uff0c \u4e00\u65f6\u95f4\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002\u4ed6\u7a81\u7136\u63a9\u9762\u53eb\u4e86\u4e00\u58f0\uff0c\u4e5f\u4e0d\u5305\u88f9\u4f24\u53e3\uff0c\u5c31\u65cb\u98ce\u4e00\u822c\u5730\u8dd1\u4e86\u3002\u8fc7\u4e86\u51e0\u5929\uff0c \u4f60\u5e08\u7956\u4eb2\u81ea\u5230\u5c0f\u5bd2\u5c71\u4e0a\uff0c\u5927\u53d1\u96f7\u9706\uff0c\u51e0\u4e4e\u8981\u5c06\u6211\u6bd9\u4e86\uff0c\u5e78\u597d\u540c\u6765\u7684\u5927\u5e08\u5144\u66ff\u6211\u6c42\u60c5\uff0c\u7ed3\u679c\u547d \u662f\u9976\u4e86\uff0c\u4f46\u5374\u7f5a\u6211\u5728\u5c0f\u5bd2\u5c71\u9762\u58c1\u601d\u8fc7\u4e00\u5341\u4e94\u5e74\u3002\u5728\u8fd9\u5341\u4e94\u5e74\u95f4\uff0c\u4e0d\u8bb8\u5077\u4e0b\u5c71\u4e00\u6b65\uff0c\u800c\u4e14\u8981\u6211 \u5728\u8fd9\u5341\u4e94\u5e74\u95f4\u505a\u597d\u4e24\u4ef6\u4e8b\u60c5\uff1a\u4e00\u4ef6\u662f\u8981\u7ec3\u6210\u4e24\u79cd\u6700\u96be\u7ec3\u7684\u6b66\u827a\uff1b\u4e00\u4ef6\u662f\u8981\u6211\u8c03\u6559\u51fa\u4e00\u4e2a\u7cbe\u901a \u2018\u767e\u53d8\u7384\u673a\u5251\u6cd5\u2019\u7684\u5f92\u5f1f\uff0c\u8fd9\u5f92\u5f1f\u7531\u5e08\u7956\u996c\u4ee4\u672c\u95e8\u4e2d\u4eba\u4ee3\u4e3a\u5bfb\u89c5\uff0c\u6559\u597d\u4e4b\u540e\uff0c\u5c31\u628a\u9752\u51a5\u5b9d\u5251 \u4f20\u7ed9\u5979\u3002\u73b0\u5728\u65f6\u95f4\u8fc7\u4e86\u5341\u4e8c\u5e74\uff0c\u90a3\u4e24\u6837\u6b66\u827a\u6211\u8fd8\u6ca1\u6709\u7ec3\u6210\uff0c\u7cbe\u901a\u7384\u673a\u5251\u6cd5\u7684\u5f92\u5f1f\u5374\u5148\u8c03\u6559\u51fa \u6765\u4e86\u3002\u201d\u4e91\u857e\u542c\u4e86\uff0c\u624d\u77e5\u9053\u98de\u5929\u9f99\u5973\u53f6\u76c8\u76c8\u6536\u81ea\u5df1\u4e3a\u5f92\uff0c\u539f\u6765\u8fd8\u6709\u8fd9\u4e00\u6bb5\u7f18\u6545\u3002\u53ea\u542c\u5f97\u5e08\u7236 \u53c8\u9053\uff1a\u201c\u5927\u5e08\u5144\u8463\u5cb3\u548c\u6211\u4ea6\u751a\u8981\u597d\uff0c\u5728\u90a3\u4ef6\u4e8b\u53d1\u751f\u4e4b\u524d\u4e09\u5e74\uff0c\u4ed6\u5949\u5e08\u7956\u4e4b\u547d\uff0c\u5230\u8499\u85cf\u8fb9\u5883\u53bb \u529e\u4e00\u4ef6\u4e8b\u60c5\uff0c\u90a3\u65f6\u521a\u81ea\u897f\u85cf\u56de\u6765\u3002\u8fc7\u4e0d\u591a\u4e45\u7b2c\u4e8c\u6b21\u518d\u53bb\uff0c\u4e34\u53bb\u4e4b\u524d\uff0c\u66fe\u7279\u522b\u8dd1\u6765\u89c1\u6211\uff0c\u53eb\u6211 \u8010\u5fc3\u5728\u5c0f\u5bd2\u5c71\u4e0a\u4fee\u7ec3\u6b66\u529f\uff0c\u8bf4\u4e5f\u8bb8\u56e0\u6b64\u53cd\u800c\u56e0\u7978\u5f97\u798f\u3002\u53c8\u95ee\u6211\u9053\uff1a\u2018\u4f60\u77e5\u9053\u5e08\u7236\u4e3a\u4f55\u5982\u6b64\u4e25 \u7981\u4f60\u4eec\u79c1\u76f8\u6388\u53d7\uff0c\u5bf9\u8fd9\u6b21\u4e8b\u60c5\u53c8\u4e3a\u4f55\u5982\u6b64\u6124\u6012\u4e48\uff1f\u2019\u6211\u9053\uff1a\u2018\u5e08\u7236\u884c\u4e8b\uff0c\u6bcf\u51fa\u5e38\u4eba\u610f\u5916\uff0c\u6211 \u600e\u80fd\u77e5\u9053\u4ed6\u7684\u7528\u610f\uff1f\u4e0d\u8fc7\u6211\u6709\u4e00\u6b21\u542c\u4ed6\u8bf4\uff0c\u4ed6\u628a\u8fd9\u4e24\u5957\u5251\u6cd5\u6bd4\u4e3a\u5367\u9f99\u96cf\u51e4\u4e0d\u80fd\u540c\u5f52\u4e00\u4e3b\uff0c\u5f52 \u5219\u6709\u7978\u3002\u8fd9\u4e2a\u597d\u50cf\u7985\u673a\u5999\u7406\u7684\u8bf4\u8bdd\uff0c\u6211\u542c\u4e86\u4e5f\u4e0d\u5f88\u61c2\u3002\u2019\u5927\u5e08\u5144\u7b11\u4e86\u4e00\u7b11\uff0c\u9053\uff1a\u2018\u4f60\u53ef\u77e5\u9053 \u5728\u4e8c\u5341\u591a\u5e74\u524d\uff0c\u5e08\u7236\u66fe\u4e0e\u4e00\u4e2a\u9b54\u5934\u4e92\u4e89\u6b66\u6797\u76df\u4e3b\u4e4b\u5ea7\uff0c\u5728\u5ce8\u5d4b\u4e4b\u5dc5\uff0c\u6597\u4e86\u4e09\u65e5\u4e09\u591c\uff0c\u4e0d\u5206\u80dc \u8d1f\u7684\u4e8b\u4e48\uff1a\u2019\u6211\u8bf4\uff1a\u2018\u77e5\u9053\u3002\u2019\u4ed6\u8bf4\uff1a\u2018\u8fd9\u9b54\u5934\u590d\u59d3\u4e0a\u5b98\uff0c\u53cc\u540d\u5929\u91ce\uff0c\u672c\u662f\u7eff\u6797\u7684\u5927\u76d7\uff0c\u7ecf \u6b64\u4e00\u6218\u4e4b\u540e\uff0c\u5ffd\u7136\u533f\u8ff9\u6f5c\u8e2a\uff0c\u4e0d\u77e5\u8eb2\u5230\u54ea\u91cc\u53bb\u4e86\u3002\u4e8c\u5341\u591a\u5e74\u6765\uff0c\u5e08\u7236\u603b\u4e0d\u653e\u5fc3\uff0c\u6211\u5230\u8499\u85cf\u8fb9 \u5883\uff0c\u5c31\u662f\u5949\u5e08\u7236\u4e4b\u547d\uff0c\u53bb\u63a2\u542c\u90a3\u4eba\u7684\u6d88\u606f\u7684\u3002\u2019\u6211\u95ee\u9053\uff1a\u2018\u90a3\u9b54\u5934\u65e2\u7136\u5982\u6b64\u5389\u5bb3\uff0c\u4f60\u53bb\u63a2\u542c \u6d88\u606f\uff0c\u82e5\u7ed9\u4ed6\u77e5\u9053\uff0c\u5982\u4f55\u662f\u597d\uff1f\u2019\u5927\u5e08\u5144\u7b11\u9053\uff1a\u2018\u90a3\u9b54\u5934\u4e0e\u6211\u4eec\u5e08\u7236\u540c\u4e00\u73ed\u8f88\uff0c\u4eba\u53c8\u6781\u4e3a\u81ea \u8d1f\uff0c\u7eb5\u8bb8\u77e5\u9053\uff0c\u4e5f\u4e0d\u4f1a\u4e0e\u6211\u4eec\u5c0f\u8f88\u4e3a\u96be\u3002\u2019\u6211\u542c\u4ed6\u5982\u6b64\u8bf4\u6cd5\uff0c\u8fd9\u624d\u653e\u5fc3\uff0c\u4f46\u4ecd\u7136\u4e0d\u77e5\u9053\u8fd9\u4e8b \u4e0e\u5e08\u7236\u4e0d\u8bb8\u6211\u4eec\u79c1\u6388\u5251\u6cd5\u53c8\u6709\u4f55\u76f8\u5173\uff1f\u4fbf\u628a\u8fd9\u7591\u95ee\u95ee\u5927\u5e08\u5144\uff0c\u5927\u5e08\u5144\u7b11\u4e86\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u2018\u6211 \u731c\u5e08\u7236\u7684\u7528\u610f\u662f\u8981\u4f60\u4e0e\u5929\u534e\u5e08\u5f1f\u53bb\u5bf9\u4ed8\u8fd9\u4e2a\u5927\u9b54\u5934\uff0c\u8ba9\u8fd9\u4e2a\u5927\u9b54\u5934\u5728\u4f60\u4eec\u624b\u4e0b\u5403\u4e2a\u5927\u5927\u7684\u8d25 \u4ed7\uff0c\u597d\u53eb\u5929\u4e0b\u82f1\u96c4\u77e5\u9053\uff0c\u4e0d\u5fc5\u4ed6\u4eb2\u81ea\u52a8\u624b\uff0c\u53ea\u662f\u4ed6\u7684\u5f92\u5f1f\u5c31\u6709\u90a3\u4e48\u5927\u7684\u80fd\u4e3a\u3002\u2019\u6211\u5413\u4e86\u4e00 \u8df3\uff0c\u9053\uff1a\u2018\u6211\u4eec\u7684\u6b66\u529f\u4e0e\u5e08\u7236\u76f8\u6bd4\u72b9\u5982\u8424\u706b\u4e4b\u5149\u6bd4\u65e5\u6708\u4e4b\u8f89\uff0c\u7b80\u76f4\u4e0d\u80fd\u6bd4\u62df\u3002\u90a3\u5927\u9b54\u5934\uff0c\u5e08 \u7236\u72b9\u81ea\u4e0d\u80fd\u80dc\u4ed6\uff0c\u53eb\u6211\u4eec\u53bb\uff0c\u90a3\u4e0d\u662f\u9001\u6b7b\u5417\uff1f\u5e08\u5144\uff0c\u4f60\u662f\u4e0d\u662f\u548c\u6211\u8bf4\u7b11\u8bdd\uff1f\u2019\u5e08\u5144\u5927\u7b11\u8bf4 \u9053\uff1a\u2018\u5e08\u7236\u82e5\u65e0\u5341\u6210\u628a\u63e1\uff0c\u5c82\u6709\u8ba9\u4f60\u4eec\u9001\u6b7b\u7684\u9053\u7406\uff0c\u5176\u4e2d\u522b\u6709\u5965\u5999\u4f60\u51b0\u96ea\u806a\u660e\uff0c\u4e5f\u731c\u4e0d\u51fa\u6765 \u4e48\uff1f\u2019\u201d \u3000\u3000\u201c\u6211\u767e\u601d\u4e0d\u89e3\uff0c\u4fbf\u8bf4\u786e\u5b9e\u662f\u731c\u4e0d\u900f\u3002\u5927\u5e08\u5144\u9053\uff1a\u2018\u5143\u5143\u5251\u6cd5\uff0c\u4e0e\u7384\u673a\u5251\u6cd5\uff0c\u4e43\u5e08\u7236\u7a77\u534a \u751f\u4e4b\u529b\uff0c\u63a2\u767e\u5bb6\u5251\u672f\u4e4b\u79d8\uff0c\u6709\u9b3c\u795e\u83ab\u6d4b\u4e4b\u673a\uff0c\u82e6\u5fc3\u6240\u521b\u3002\u4e24\u5957\u5251\u6cd5\uff0c\u53ea\u5f97\u5176\u4e00\u5373\u53ef\u79f0\u96c4\u6c5f \u6e56\uff0c\u82e5\u7136\u53cc\u5251\u5408\u74a7\uff0c\u5219\u5929\u4e0b\u65e0\u654c\uff01\u66f4\u5999\u7684\u662f\uff0c\u8fd9\u5957\u5251\u6cd5\uff0c\u672c\u6765\u5c31\u662f\u76f8\u53cd\u76f8\u6210\uff0c\u4e0d\u5fc5\u9884\u5148\u4e0e\u5bf9 \u65b9\u7ec3\u4e60\u914d\u5408\uff0c\u4e00\u4f7f\u5f00\u6765\uff0c\u4fbf\u81ea\u7136\u80fd\u5929\u8863\u65e0\u7f1d\uff0c\u4e92\u4e3a\u547c\u5e94\u3002\u6240\u4ee5\u6211\u731c\u5e08\u7236\u4e0d\u8bb8\u4f60\u4eec\u77e5\u9053\u53e6\u4e00\u5957 \u5251\u6cd5\uff0c\u5176\u4e2d\u60f3\u662f\u6709\u4e24\u4e2a\u9053\u7406\uff1a\u4e00\u8005\u662f\u6015\u4f60\u4eec\u77e5\u9053\u4e86\u53e6\u4e00\u5957\u4e4b\u540e\uff0c\u5c31\u96be\u514d\u5206\u5fc3\uff0c\u5077\u5077\u53bb\u5b66\uff0c\u987b \u77e5\u4e00\u4eba\u7cbe\u529b\u6709\u9650\uff0c\u8fd9\u4e24\u5957\u5251\u6cd5\u90fd\u662f\u590d\u6742\u65e0\u6bd4\uff0c\u53ea\u5b66\u4e00\u5957\uff0c\u4e5f\u8981\u4e13\u5fc3\u77e2\u5fd7\uff0c\u7528\u4e0a\u5341\u5e74\u4ee5\u4e0a\u7684\u529f \u592b\uff0c\u82e5\u517c\u5b66\u4e24\u5957\uff0c\u53ea\u6050\u6015\u96be\u4ee5\u767b\u5cf0\u9020\u6781\u3002\u800c\u4e14\u8fd9\u4e24\u5957\u5251\u6cd5\uff0c\u672c\u6765\u662f\u8981\u4e24\u4eba\u4f7f\u7528\u624d\u80fd\u53d1\u6325\u5b83\u7684 \u7edd\u5999\u4e4b\u5904\u7684\uff0c\u6240\u4ee5\u5b9e\u5728\u4e5f\u4e0d\u5fc5\u517c\u5b66\u3002\u4e8c\u8005\u662f\u90a3\u4e0a\u5b98\u5929\u91ce\uff0c\u672c\u9886\u786e\u662f\u8d85\u51e1\u5165\u5723\uff0c\u5e08\u7236\u867d\u7136\u60f3\u51fa \u514b\u5236\u4ed6\u7684\u5251\u6cd5\uff0c\u4f46\u4ea6\u6015\u4ed6\u9884\u5148\u77e5\u9053\u3002\u2019\u6211\u4e00\u542c\u5927\u5e08\u5144\u5982\u6b64\u8bf4\u6cd5\uff0c\u7acb\u523b\u9886\u609f\u5e08\u7236\u5927\u7ea6\u662f\u6015\u6211\u4eec \u5c11\u5e74\u597d\u4e8b\uff0c\u82e5\u7136\u77e5\u9053\u53cc\u5251\u5408\u74a7\u5c31\u53ef\u65e0\u654c\u4e8e\u5929\u4e0b\u4e4b\u540e\uff0c\u6709\u6043\u65e0\u6050\uff0c\u53ef\u80fd\u62db\u60f9\u5f3a\u654c\uff0c\u6cc4\u6f0f\u51fa\u53bb\uff0c \u90a3\u65f6\u5c31\u4f1a\u88ab\u4e0a\u5b98\u5929\u91ce\u63a2\u77e5\uff0c\u9884\u4e3a\u9632\u8303\u4e86\u3002\u5927\u5e08\u5144\u8bf4\u5b8c\u8fd9\u756a\u8bdd\u540e\uff0c\u7b2c\u4e8c\u65e5\u4fbf\u8fdc\u8d74\u8499\u85cf\u8fb9\u5883\u3002\u8fc7 \u4e86\u4e24\u5e74\uff0c\u5929\u534e\u4e5f\u53bb\u8499\u53e4\uff0c\u6211\u867d\u7136\u77e5\u9053\u8fd9\u53cc\u5251\u5408\u74a7\u7684\u79d8\u5bc6\uff0c\u4f46\u5374\u4ece\u6765\u6ca1\u6709\u8bd5\u8fc7\uff0c\u5929\u534e\u6240\u5b66\u7684\u5143 \u5143\u5251\u6cd5\uff0c\u6211\u4e5f\u662f\u4ece\u672a\u77e5\u8fc7\u4e00\u62db\u534a\u5f0f\u3002\u201d \u3000\u3000\u98de\u5929\u9f99\u5973\u53f6\u76c8\u76c8\u6240\u8bf4\u7684\u6545\u4e8b\uff0c\u95ea\u7535\u822c\u7684\u5728\u4e91\u857e\u8111\u6d77\u4e4b\u4e2d\u95ea\u8fc7\uff0c\u65e0\u6570\u7591\u56e2\uff0c\u6a2a\u6897\u80f8\u81c6\uff0c\u84e6 \u7136\u60f3\u9053\uff1a\u201c\u82e5\u7136\u8fd9\u5c11\u5e74\u4f7f\u7684\u771f\u662f\u5143\u5143\u5251\u6cd5\uff0c\u90a3\u4e48\u6211\u4e00\u51fa\u624b\uff0c\u5c82\u975e\u53ef\u4ee5\u7acb\u523b\u5236\u80dc\u514b\u654c\uff1f\u201d\u731b\u542c \u5f97\u9ed1\u6469\u8bc3\u53c8\u662f\u4e00\u58f0\u5927\u53eb\uff0c\u5f20\u4e39\u67ab\u957f\u5578\u4e00\u58f0\uff0c\u62ac\u5934\u770b\u65f6\uff0c\u53ea\u89c1\u573a\u4e2d\u5f62\u52bf\u53c8\u53d8\u3002\u90a3\u9ed1\u6469\u8bc3\u5df2\u4e0d\u4f3c \u5148\u524d\u7684\u72c2\u66b4\u86ee\u653b\uff0c\u4f46\u89c1\u4ed6\u5982\u540c\u633d\u7740\u5343\u65a4\u91cd\u7269\u4e00\u6837\uff0c\u7eff\u7389\u6756\u4e1c\u6307\u897f\u5212\uff0c\u663e\u5f97\u5f88\u662f\u5403\u529b\uff0c\u5f20\u4e39\u67ab \u6a2a\u5251\u5f53\u80f8\uff0c\u9762\u8272\u51dd\u91cd\uff0c\u597d\u50cf\u5168\u526f\u7cbe\u795e\u90fd\u96c6\u4e2d\u5728\u5bf9\u65b9\u7684\u7389\u6756\u5c16\u7aef\uff0c\u6bcf\u9694\u4e00\u9635\uff0c\u624d\u7a81\u7136\u653b\u51fa\u4e00 \u5251\u3002\u4e24\u4eba\u51fa\u62db\u90fd\u751a\u7f13\u6162\uff0c\u770b\u6765\u4f3c\u662f\u5728\u96e8\u9aa4\u98ce\u72c2\u4e4b\u540e\u91cd\u5f52\u5e73\u9759\uff0c\u5176\u5b9e\u5374\u662f\u53c8\u5404\u4ee5\u4e0a\u4e58\u5185\u529f\u25a1 \u62fc\uff0c\u6bcf\u4e00\u62db\u4e00\u5f0f\uff0c\u90fd\u8574\u85cf\u7740\u65e0\u9650\u6740\u673a\u3002\u5f20\u4e39\u67ab\u7684\u5251\u6cd5\u867d\u5999\uff0c\u4f46\u5251\u5149\u7f2d\u7ed5\uff0c\u5374\u65e0\u6cd5\u900f\u8fc7\u7eff\u7389\u5bd2 \u5149\uff0c\u4e91\u857e\u4e00\u770b\u4e4b\u4e0b\uff0c\u4fbf\u77e5\u4ed6\u7684\u5185\u5bb6\u771f\u529b\uff0c\u786e\u662f\u6bd4\u5bf9\u65b9\u5c1a\u900a\u4e00\u7b79\uff0c\u4ec5\u80fd\u4ed7\u5251\u81ea\u4fdd\u3002 \u3000\u3000\u8fd9\u65f6\u6625\u65e5\u7684\u671d\u9633\u5df2\u7ecf\u5347\u8d77\uff0c\u90a3\u5893\u95e8\u88ab\u5f20\u4e39\u67ab\u6253\u5f00\u4e4b\u540e\u5c1a\u672a\u5173\u4e0a\uff0c\u65e5\u5149\u900f\u5c04\u8fdb\u6765\uff0c\u8000\u773c\u751f \u7f2c\u3002\u5f20\u4e39\u67ab\u9762\u5411\u9633\u5149\u66f4\u662f\u4e0d\u5229\uff0c\u4f46\u89c1\u90a3\u9ed1\u6469\u8bc3\u8d8a\u8feb\u8d8a\u7d27\uff0c\u62a1\u5706\u7389\u6756\uff0c\u6bcf\u62db\u53d1\u51fa\uff0c\u9690\u9690\u5939\u6709\u98ce \u96f7\u4e4b\u58f0\u3002\u5f20\u4e39\u67ab\u7684\u5251\u5149\u5706\u5377\u8d8a\u7f29\u8d8a\u5c0f\uff0c\u6162\u6162\u5730\u53ea\u5728\u5934\u9876\u4e4b\u4e0a\u76d8\u65cb\u7740\uff0c\u9ed1\u6469\u8bc3\u731b\u5730\u5927\u559d\u4e00\u58f0\uff0c \u6756\u5939\u98ce\u96f7\uff0c\u5411\u7740\u5f20\u4e39\u67ab\u7684\u5934\u76d6\u731b\u7838\u4e0b\u53bb\u3002 \u3000\u3000\u4e91\u857e\u53eb\u58f0\uff1a\u201c\u4e0d\u597d\uff01\u201d\u4e0d\u5047\u601d\u7d22\uff0c\u4e09\u679d\u6885\u82b1\u8774\u8776\u9556\u8131\u624b\u98de\u51fa\u3002\u5f20\u4e39\u67ab\u5927\u53eb\u9053\uff1a\u201c\u8d24\u5f1f\u5feb \u8d70\uff01\u201d\u4f46\u89c1\u98de\u9556\u5982\u7535\uff0c\u843d\u5904\u65e0\u58f0\u6709\u5982\u6ce5\u725b\u5165\u6d77\uff0c\u5168\u65e0\u8e2a\u8ff9\uff0c\u7adf\u662f\u88ab\u90a3\u5251\u6756\u4ea4\u8361\u7684\u52b2\u98ce\u9707\u5f97\u7c89 \u788e\u4e86\u3002\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u4e45\u5df2\u84c4\u52b2\u5f85\u53d1\u7684\u767d\u6469\u8bc3\u4e00\u58f0\u72c2\u7b11\uff0c\u8eab\u5f62\u98de\u8d77\uff0c\u957f\u81c2\u75be\u4f38\uff0c\u547c\u7684\u4e00\u58f0 \u5411\u4e91\u857e\u5f53\u5934\u6293\u4e0b\u3002 \u3000\u3000\u4e91\u857e\u53cd\u624b\u4e00\u5251\uff0c\u9661\u89c9\u8170\u80c1\u4e00\u9ebb\uff0c\u6025\u6025\u98de\u8eab\u63a0\u51fa\u4e08\u8bb8\uff0c\u5438\u4e86\u53e3\u6c14\uff0c\u6a2a\u5251\u56de\u7768\uff0c\u53ea\u89c1\u90a3\u767d\u6469 \u8bc3\u624b\u4e0a\u5df2\u591a\u4e86\u4e00\u6839\u767d\u7389\u6756\uff0c\u51fa\u624b\u6a2a\u626b\uff0c\u72e0\u72e0\u6253\u6765\u3002\u539f\u6765\u4e24\u4eba\u9002\u624d\u6362\u4e86\u4e00\u62db\uff0c\u767d\u6469\u8bc3\u4e0d\u77e5\u4e91\u857e \u6240\u4f7f\u7684\u4ea6\u662f\u5b9d\u5251\uff0c\u88ab\u9752\u51a5\u5251\u7684\u950b\u8292\u524a\u53bb\u80a9\u5934\u4e00\u7247\u76ae\u8089\uff0c\u800c\u4e91\u857e\u8f7b\u529f\u867d\u5999\uff0c\u4ea6\u88ab\u4ed6\u7684\u638c\u7f18\u626b\u4e2d \u4e86\u80cc\u540e\u7684\u201c\u810a\u5fc3\u7a74\u201d\uff0c\u5e78\u5f97\u4e24\u4eba\u90fd\u5df2\u907f\u8fc7\u5bf9\u65b9\u7684\u52b2\u529b\uff0c\u6240\u53d7\u7684\u5251\u4f24\u3001\u638c\u4f24\u90fd\u662f\u5f3a\u5f29\u4e4b\u672b\u7684\u4f59 \u52bf\uff0c\u8981\u4e0d\u7136\u90fd\u8981\u547d\u4e27\u5f53\u573a\u3002 \u3000\u3000\u767d\u6469\u8bc3\u4e0d\u6562\u6258\u5927\uff0c\u62bd\u51fa\u5b9d\u6756\u5bf9\u4ed8\u4e91\u857e\u7684\u5b9d\u5251\u3002\u767d\u6469\u8bc3\u7684\u767d\u7389\u6756\u4e0e\u9ed1\u6469\u8bc3\u7684\u7eff\u7389\u6756\u90fd\u662f\u5929 \u7afa\u7279\u4ea7\u7684\u5b9d\u7389\u6240\u5236\uff0c\u575a\u903e\u7cbe\u521a\u3002\u767d\u6469\u8bc3\u7684\u529f\u529b\u8fdc\u80dc\u4e8e\u4e91\u857e\uff0c\u8fd9\u4e00\u6756\u626b\u6765\uff0c\u6709\u5982\u96f7\u9706\u75be\u53d1\uff0c\u4e91 \u857e\u4e0d\u6562\u786c\u63a5\uff0c\u4e00\u62db\u201c\u7389\u5973\u6295\u68ad\u201d\uff0c\u907f\u8fc7\u6756\u5cf0\uff0c\u659c\u8eab\u8fdb\u5251\u3002\u767d\u6469\u8bc3\u597d\u4e0d\u5389\u5bb3\uff0c\u7389\u6756\u4e00\u62a1\uff0c\u547c\u7684 \u4e00\u58f0\uff0c\u5c31\u628a\u4e91\u857e\u8fde\u4eba\u5e26\u5251\u5708\u5728\u6756\u5f71\u4e4b\u5185\u3002\u767d\u7389\u6756\u957f\u53ef\u4e03\u5c3a\uff0c\u821e\u52a8\u8d77\u6765\uff0c\u4e00\u4e08\u65b9\u5706\u4e4b\u5185\uff0c\u5168\u907f \u4e0d\u5f00\u4ed6\u52b2\u529b\u7684\u653b\u51fb\uff0c\u4e91\u857e\u65bd\u5c55\u4e00\u8eab\u8f7b\u7075\u5c0f\u5de7\u7684\u529f\u592b\uff0c\u5728\u5251\u98ce\u6756\u5f71\u4e4b\u4e2d\uff0c\u7a9c\u6765\u7a9c\u53bb\uff0c\u773c\u89c1\u6027\u547d \u5df2\u5728\u547c\u5438\u4fc4\u9877\u4e4b\u95f4\u3002 \u3000\u3000\u4e91\u857e\u7a81\u7136\u51fa\u624b\uff0c\u5927\u51fa\u5f20\u4e39\u67ab\u610f\u6599\u4e4b\u5916\u3002\u539f\u6765\u4ed6\u7684\u529f\u529b\u867d\u7136\u6bd4\u9ed1\u6469\u8bc3\u7565\u900a\u4e00\u7b79\uff0c\u4ed7\u7740\u7cbe\u5999 \u7684\u5251\u6cd5\uff0c\u5c1a\u80fd\u81ea\u4fdd\uff0c\u4ed6\u9002\u624d\u7f29\u5c0f\u5708\u5b50\uff0c\u6b63\u662f\u8fd0\u7528\u5b9d\u5251\u4e4b\u529b\uff0c\u914d\u4ee5\u4e0a\u4e58\u7684\u5185\u529f\uff0c\u53d6\u5f97\u5185\u7ebf\u62b5\u5fa1 \u7684\u4f18\u52bf\uff0c\u9ed1\u6469\u8bc3\u7684\u5929\u6469\u6756\u6cd5\u867d\u7136\u5389\u5bb3\uff0c\u5374\u662f\u65e0\u5948\u4ed6\u4f55\u3002\u4e24\u4eba\u25a1\u62fc\u534a\u591c\uff0c\u773c\u89c1\u5c06\u4ee5\u5e73\u624b\u4e4b\u5c40\u544a \u7ec8\uff0c\u4ee5\u9ed1\u767d\u6469\u8bc3\u90a3\u6837\u5927\u7684\u540d\u5934\uff0c\u80fd\u6218\u6210\u5e73\u624b\uff0c\u4ed6\u4eec\u5df2\u8981\u8ba4\u683d\uff0c\u4e0d\u6599\u4e91\u857e\u7a81\u7136\u63d2\u8fdb\uff0c\u5f15\u4e86\u767d\u6469 \u8bc3\u52a0\u5165\u6218\u56e2\uff0c\u771f\u662f\u5982\u5e73\u5730\u6ce2\u6f9c\uff0c\u7a81\u751f\u53d8\u5316\u3002\u5f20\u4e39\u67ab\u5fc3\u4e2d\u6697\u6697\u53eb\u82e6\uff1a\u81ea\u5df1\u4ee5\u4e00\u5bf9\u4e00\uff0c\u5c1a\u81ea\u5904\u5728 \u4e0b\u98ce\uff0c\u4e91\u857e\u6b66\u529f\uff0c\u900a\u4e8e\u81ea\u5df1\uff0c\u66f4\u662f\u8fdc\u975e\u90a3\u767d\u6469\u8bc3\u7684\u5bf9\u624b\u3002\u773c\u89c1\u4e91\u857e\u5371\u6025\uff0c\u5fc3\u4e2d\u5927\u6025\uff0c\u5237\u5237\u4e24 \u5251\uff0c\u53cd\u5b88\u4e3a\u653b\uff0c\u5f3a\u81ea\u659c\u51b2\u51fa\u53bb\uff0c\u867d\u7136\u660e\u77e5\u4e8c\u4eba\u8054\u624b\uff0c\u4ea6\u975e\u9ed1\u767d\u6469\u8bc3\u4e4b\u654c\uff0c\u4f46\u4e8b\u5df2\u81f3\u6b64\uff0c\u4e0d\u5f97 \u4e0d\u7136\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u4e91\u857e\u4e3a\u6211\u8e48\u9669\uff0c\u6211\u53c8\u7109\u80fd\u5f03\u2018\u4ed6\u2019\u800c\u72ec\u81ea\u9003\u751f\u3002\u201d \u3000\u3000\u5f20\u4e39\u67ab\u5251\u4e0e\u8eab\u5408\uff0c\u75be\u8d70\u5982\u98ce\uff0c\u98de\u8eab\u76f8\u6551\u3002\u9ed1\u6469\u8bc3\u54c8\u54c8\u5927\u7b11\u53eb\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4e2a\u5a03\u5a03\u8fd8\u60f3\u9003 \u4e48\uff1f\u201d\u4ed6\u6b63\u56e0\u82e6\u6218\u4e0d\u4e0b\uff0c\u5fc3\u4e2d\u7126\u8e81\uff0c\u5ffd\u89c1\u4e91\u857e\u51fa\u624b\uff0c\u770b\u4e86\u4e00\u62db\uff0c\u77e5\u4e91\u857e\u5251\u6cd5\u867d\u5999\uff0c\u529f\u529b\u5c1a \u5f31\uff0c\u4ee5\u81ea\u5df1\u5144\u5f1f\u4e4b\u529b\uff0c\u4ee5\u4e8c\u654c\u4e8c\uff0c\u90a3\u662f\u7a33\u64cd\u80dc\u5238\uff0c\u5f53\u4e0b\u7389\u6756\u524d\u6307\uff0c\u7d27\u8e51\u654c\u4eba\u4e4b\u540e\uff0c\u6756\u7aef\u76f4\u6307 \u5f20\u4e39\u67ab\u7684\u80cc\u5fc3\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u4e91\u857e\u4e00\u58f0\u6b22\u547c\uff0c\u53cc\u5251\u4e00\u5408\uff0c\u5251\u5149\u66b4\u957f\uff0c\u5237\u5237\u4e24\u58f0\uff0c\u767d\u6469\u8bc3\u7684\u5de6\u53f3\u811a\u8e1d\uff0c\u4e00\u8fb9\u4e2d\u4e86 \u4e00\u5251\uff0c\u9ed1\u6469\u8bc3\u7684\u7eff\u7389\u6756\u63d2\u6765\uff0c\u88ab\u53cc\u5251\u4e00\u5708\uff0c\u53cd\u8361\u51fa\u53bb\u3002\u9ed1\u6469\u8bc3\u5927\u5403\u4e00\u60ca\uff0c\u53eb\u9053\uff1a\u201c\u8d70\u79bb\u65b9\uff0c \u8e0f\u5dfd\u4f4d\uff0c\u56f0\u4f4f\u4ed6\u4eec\uff01\u201d\u9ed1\u767d\u6469\u8bc3\u7684\u5929\u6469\u6756\u6cd5\u4e5f\u662f\u53ef\u4ee5\u4e92\u76f8\u914d\u5408\u7684\u6756\u6cd5\uff0c\u4e24\u4eba\u9996\u5c3e\u76f8\u5e94\uff0c\u8e0f\u7740 \u516b\u5366\u65b9\u4f4d\uff0c\u5c31\u5982\u5e03\u4e0b\u4e86\u516b\u9635\u5408\u56f4\u4e4b\u56fe\uff0c\u4efb\u662f\u591a\u5f3a\u7684\u654c\u4eba\u4e5f\u51b2\u4e0d\u51fa\u53bb\u3002\u9ed1\u767d\u6469\u8bc3\u4e43\u662f\u5b6a\u751f\u5144 \u5f1f\uff0c\u5fc3\u610f\u76f8\u901a\uff0c\u6218\u7565\u4e00\u5b9a\uff0c\u767d\u6469\u8bc3\u5fcd\u7740\u75bc\u75db\uff0c\u6325\u6756\u75be\u7ed5\u659c\u5708\uff0c\u4e0e\u9ed1\u6469\u8bc3\u5de6\u53f3\u5408\u56f4\uff0c\u5411\u5f20\u3001\u4e91 \u4e8c\u4eba\u72e0\u72e0\u653b\u51fb\uff0c\u8fde\u4e0b\u6740\u624b\uff01\u53ea\u628a\u90a3\u5728\u65c1\u89c2\u6218\u7684\u56db\u4e2a\u73e0\u5b9d\u5546\u4eba\u770b\u5f97\u773c\u82b1\u7f2d\u4e71\u3002 \u3000\u3000\u4e91\u857e\u4e00\u5251\u523a\u51fa\uff0c\u9ed1\u6469\u8bc3\u7684\u7eff\u7389\u6756\u6a2a\u91cc\u4e00\u6311\uff0c\u6b63\u4f7f\u5230\u201c\u5929\u6469\u732e\u9152\u201d\u4e00\u62db\uff0c\u6756\u7aef\u6311\u5411\u654c\u4eba\u4e0b \u989a\uff0c\u6756\u8eab\u6a2a\u51fb\u654c\u8155\uff0c\u6756\u67c4\u53c8\u6309\u5230\u654c\u4eba\u7684\u4e39\u7530\u8981\u7a74\uff0c\u4e00\u62db\u4e09\u5f0f\uff0c\u7aef\u7684\u5389\u5bb3\u975e\u5e38\u3002\u4e91\u857e\u7684\u201c\u767e\u53d8 \u7384\u673a\u5251\u6cd5\u201d\u4ee5\u5947\u8be1\u5584\u53d8\u89c1\u957f\uff0c\u8eab\u5f62\u6643\u5904\uff0c\u4e00\u62db\u201c\u5012\u8f6c\u9634\u9633\u201d\u5251\u950b\u81ea\u4e0b\u800c\u4e0a\uff0c\u53cd\u524a\u8fc7\u53bb\uff0c\u907f\u5f00 \u4e86\u7389\u6756\u7684\u4e00\u6311\uff0c\u53c8\u4ee5\u653b\u52bf\u8feb\u5f97\u9ed1\u6469\u8bc3\u632a\u504f\u4e86\u6756\u8eab\uff0c\u6309\u8bf4\u4e5f\u53ef\u4ee5\u89e3\u5f00\u6756\u67c4\u6309\u7a74\u7684\u62db\u6570\u3002\u4f46\u9ed1\u6469 \u8bc3\u5230\u5e95\u662f\u4e45\u7ecf\u6218\u9635\uff0c\u529f\u529b\u53c8\u6df1\uff0c\u89c1\u4e91\u857e\u5251\u6cd5\u7cbe\u5999\uff0c\u6599\u77e5\u524d\u9762\u4e24\u5f0f\uff0c\u5b9a\u7136\u65e0\u6548\uff0c\u7a81\u7136\u52a0\u7d27\u6700\u540e \u4e00\u51fb\uff0c\u6a2a\u8f6c\u7389\u6756\uff0c\u6756\u67c4\u91cd\u91cd\u4e00\u6309\uff0c\u4e91\u857e\u53ea\u89c9\u4e00\u80a1\u52b2\u529b\u8feb\u6765\uff0c\u773c\u89c1\u90a3\u6756\u67c4\u5df2\u6309\u5230\u81ea\u5df1\u4e39\u7530\u4e0a\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c\u706b\u82b1\u98de\u6e85\uff0c\u5f20\u4e39\u67ab\u4e00\u5251\u9694\u5f00\u767d\u6469\u8bc3\u7684\u7389\u6756\uff0c\u4f59\u52bf\u672a\u8870\uff0c\u5251\u950b\u987a\u624b \u62b9\u53bb\uff0c\u6070\u6070\u63a0\u8fc7\u9ed1\u6469\u8bc3\u9888\u9879\u3002\u9ed1\u6469\u8bc3\u5ffd\u89c9\u5251\u6c14\u68ee\u68ee\uff0c\u6c81\u5165\u808c\u9aa8\uff0c\u4e0d\u77e5\u662f\u865a\u662f\u5b9e\uff0c\u6025\u6025\u7684\u53cd\u6756 \u4e00\u51fb\uff0c\u653e\u5f00\u4e86\u4e91\u857e\u3002\u9ed1\u767d\u6469\u8bc3\u6309\u7740\u516b\u5c01\u65b9\u4f4d\u51fa\u51fb\uff0c\u9ed1\u6469\u8bc3\u53cd\u6756\u4e00\u51fb\uff0c\u8eab\u5f62\u8f6c\u5012\u201c\u4e7e\u201d\u4f4d\uff0c\u767d \u6469\u8bc3\u659c\u8d70\u201c\u5151\u201d\u65b9\uff0c\u767d\u7389\u6756\u4ea6\u5df2\u5288\u51fa\uff0c\u53cc\u6756\u5408\u63a0\uff0c\u8f6c\u6210\u4e00\u4e2a\u5927\u5f27\uff0c\u5f20\u4e39\u67ab\u672a\u53ca\u6362\u62db\uff0c\u53eb\u58f0\uff1a \u201c\u4e0d\u597d\uff01\u201d\u4e91\u857e\u5ffd\u7136\u968f\u624b\u4e00\u5251\uff0c\u63d2\u8fdb\u5f53\u4e2d\uff0c\u8fd9\u4e00\u5251\u63d2\u5f97\u6070\u5230\u597d\u5904\uff0c\u4f46\u89c1\u53cc\u5251\u659c\u5206\uff0c\u9ed1\u767d\u6469\u8bc3 \u90fd\u8eb2\u95ea\u4e0d\u8fed\u3002\u8fd9\u51e0\u62db\u6025\u5982\u7535\u5149\u77f3\u706b\uff0c\u5927\u5bb6\u90fd\u662f\u4e0d\u5047\u601d\u7d22\uff0c\u5374\u4e0d\u6599\u914d\u5408\u5f97\u5999\u5230\u6beb\u5dc5\uff0c\u4e91\u857e\u7709\u5f00 \u773c\u7b11\uff0c\u5927\u559c\u53eb\u9053\uff1a\u201c\u53cc\u5251\u5408\u74a7\uff0c\u679c\u7136\u65e0\u654c\uff01\u201d\u968f\u624b\u53d1\u51fa\u4e00\u62db\uff0c\u4f46\u89c1\u5f20\u4e39\u67ab\u7684\u5b9d\u5251\u4ea6\u4ece\u76f8\u53cd\u7684 \u65b9\u5411\u524a\u51fa\uff0c\u53cc\u5251\u592d\u5a07\u5982\u9f99\uff0c\u53c8\u628a\u9ed1\u767d\u6469\u8bc3\u903c\u5f97\u8fde\u8fde\u540e\u9000\uff01 \u3000\u3000\u5f20\u4e39\u67ab\u5927\u662f\u60ca\u5947\uff0c\u7591\u5fc3\u9661\u8d77\uff0c\u77a5\u4e86\u4e91\u857e\u4e00\u773c\uff0c\u4e91\u857e\u7b11\u9053\uff1a\u201c\u4f60\u77a7\uff0c\u6211\u8fd9\u4e2a\u4fdd\u9556\u8fd8\u4e0d\u9519 \u5427\uff1f\u5f97\u7406\u4e0d\u9976\u4eba\uff0c\u5e76\u80a9\u5b50\u4e0a\u5475\uff01\u201d\u5979\u5f97\u610f\u5fd8\u5f62\uff0c\u628a\u4ece\u5468\u5c71\u6c11\u5904\u5b66\u5f97\u7684\u6c5f\u6e56\u5207\u53e3\uff0c\u4e71\u642c\u51fa\u6765\u3002 \u5f20\u4e39\u67ab\u53c8\u662f\u60ca\u5947\uff0c\u53c8\u662f\u597d\u7b11\uff0c\u6325\u5251\u4e0e\u5979\u5e76\u80a9\u75be\u8fdb\uff0c\u9ed1\u767d\u6469\u8bc3\u62fc\u5c3d\u5168\u529b\uff0c\u6325\u6756\u529b\u6297\uff0c\u5140\u662f\u62b5\u6321 \u4e0d\u4f4f\u3002\u5f20\u4e39\u67ab\u5927\u7b11\u9053\uff1a\u201c\u5999\u6781\uff0c\u5999\u6781\u4e86\uff01\u6211\u4eec\u4e8c\u4eba\u4e00\u914d\u8d77\u6765\uff0c\u771f\u662f\u73e0\u8054\u74a7\u5408\uff01\u201d\u4ed6\u968f\u53e3\u6389 \u6587\uff0c\u4e91\u857e\u542c\u5728\u5fc3\u91cc\uff0c\u4e0d\u89c9\u9762\u4e0a\u4e00\u7ea2\uff0c\u4f46\u89c1\u5f20\u4e39\u67ab\u5728\u5927\u7b11\u58f0\u4e2d\uff0c\u8fd0\u5251\u5982\u98ce\u72e0\u72e0\u653b\u51fb\uff0c\u76ee\u5149\u53ea\u6ce8 \u5b9a\u9ed1\u767d\u6469\u8bc3\uff0c\u53c8\u4e0d\u4f3c\u662f\u6709\u5fc3\u5411\u81ea\u5df1\u8c03\u7b11\u3002 \u3000\u3000\u53cc\u5251\u5408\u74a7\u5a01\u529b\u4f55\u6b62\u589e\u52a0\u4e00\u500d\uff0c\u9ed1\u767d\u6469\u8bc3\u7684\u6b65\u6cd5\u7adf\u88ab\u6253\u4e71\uff0c\u8d70\u4e0d\u6210\u4e94\u95e8\u516b\u5366\u7684\u65b9\u4f4d\uff0c\u5f20\u3001 \u4e91\u4e8c\u4eba\u6216\u8005\u5e76\u80a9\u51fa\u5251\uff0c\u6216\u8005\u524d\u540e\u8054\u62db\uff0c\u6216\u8005\u5de6\u53f3\u5206\u51fb\uff0c\u6216\u8005\u4e0a\u4e0b\u5939\u653b\uff0c\u4e00\u624b\u63a5\u7740\u4e00\u624b\uff0c\u4e00\u5f0f \u8054\u7740\u4e00\u5f0f\uff0c\u53cc\u5251\u63a8\u52a8\uff0c\u6709\u5982\u9f99\u95e8\u6d6a\u6d8c\uff0c\u5927\u6d77\u6f6e\u751f\uff0c\u9ed1\u767d\u6469\u8bc3\u867d\u662f\u89c1\u591a\u8bc6\u5e7f\uff0c\u6280\u901a\u4e2d\u897f\uff0c\u4e5f\u4e0d \u7981\u88ab\u8fd9\u79cd\u6349\u6478\u4e0d\u900f\u7684\u602a\u5f02\u5251\u6cd5\uff0c\u5413\u5f97\u77a0\u76ee\u7ed3\u820c\uff01\u53ea\u662f\u518d\u8d70\u4e86\u5341\u4f59\u4e8c\u5341\u62db\uff0c\u767d\u6469\u8bc3\u53c8\u4e2d\u4e86\u4e00 \u5251\uff0c\u9ed1\u6469\u8bc3\u4e5f\u88ab\u524a\u53bb\u675f\u53d1\u7684\u91d1\u73af\u3002\u9ed1\u6469\u8bc3\u957f\u53f9\u4e00\u58f0\uff0c\u53eb\u9053\uff1a\u201c\u516b\u5341\u5c81\u8001\u5a18\u5012\u7ef7\u5b69\u513f\uff0c\u7f62\u4e86\uff0c \u7f62\u4e86\uff01\u201d\u7a81\u7136\u626f\u767d\u6469\u8bc3\u8df3\u51fa\u5708\u5b50\uff0c\u6a2a\u6756\u53eb\u9053\uff1a\u201c\u4f60\u4eec\u8d62\u4e86\uff0c\u6b64\u5730\u7531\u4f60\u4eec\u4f5c\u4e3b\u4e86\uff01\u201d\u957f\u5578\u4e00 \u58f0\uff0c\u4ed6\u4eec\u7684\u59bb\u5b50\uff0c\u90a3\u4e24\u4e2a\u6ce2\u65af\u5987\u4eba\uff0c\u548c\u4ed6\u4eec\u7684\u4e70\u624b\uff0c\u90a3\u56db\u4e2a\u73e0\u5b9d\u5546\u4eba\uff0c\u90fd\u662f\u9762\u5982\u6b7b\u7070\uff0c\u4e00\u8a00 \u4e0d\u53d1\uff0c\u9ed8\u9ed8\u5730\u968f\u7740\u9ed1\u767d\u6469\u8bc3\u8d70\u51fa\u5893\u95e8\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u7b11\u9053\uff1a\u201c\u8fd9\u4e24\u5144\u5f1f\u679c\u662f\u602a\u4eba\uff0c\u4f46\u4e5f\u7b97\u4e0d\u5f97\u662f\u82f1\u96c4\u4eba\u7269\u3002\u5582\uff0c\u5c0f\u5144\u5f1f\uff0d\uff0d\u201d\u6b63\u6b32\u8be2 \u95ee\u4e91\u857e\uff0c\u5ffd\u542c\u5f97\u95e8\u5916\u9a6c\u5636\uff0c\u90a3\u5339\u96ea\u767d\u7684\u7167\u591c\u72ee\u5b50\u9a6c\u548c\u4e91\u857e\u7684\u7ea2\u9b03\u6218\u9a6c\u76f8\u7ee7\u8dd1\u5165\u3002\u539f\u6765\u9ed1\u767d\u6469 \u8bc3\u8df5\u7ea6\uff0c\u5c06\u4e24\u5339\u5b9d\u9a6c\u533b\u597d\u653e\u56de\uff0c\u767d\u9a6c\u5148\u5230\uff0c\u8df3\u8dc3\u5636\u53eb\uff0c\u6328\u7740\u4e3b\u4eba\u6469\u64e6\uff0c\u4f3c\u662f\u65e0\u9650\u6b22\u6b23\uff0c\u4e91\u857e \u4e5f\u4e0a\u524d\u63fd\u7740\u7ea2\u9a6c\u9a6c\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u9a6c\u513f\u5475\uff0c\u4f60\u7ed9\u90a3\u602a\u7269\u6574\u60e8\u4e86\u3002\u5582\uff0c\u5927\u54e5\uff0d\uff0d\u201d\u6b63\u60f3\u8be2\u95ee\u5f20\u4e39 \u67ab\u7684\u5251\u6cd5\u6765\u5386\uff0c\u5ffd\u89c9\u80f8\u53e3\u4e00\u95f7\uff0c\u8bf4\u8bdd\u7a81\u88ab\u6897\u4f4f\uff0c\u5f20\u4e39\u67ab\u5411\u4e91\u857e\u9762\u4e0a\u4e00\u77a7\uff0c\u7a81\u7136\u60ca\u53eb\u9053\uff1a\u201c\u5c0f \u5144\u5f1f\uff0c\u4f60\u662f\u4e0d\u662f\u88ab\u767d\u6469\u8bc3\u6253\u4e86\u4e00\u638c\uff0c\u55ef\uff0c\u4e0d\u8981\u8bf4\u8bdd\u2026\u2026\u201d\u4e91\u857e\u70b9\u4e86\u70b9\u5934\uff0c\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u8d76\u5feb \u8fd0\u6c14\u62a4\u7740\u4e39\u7530\uff0c\u6211\u66ff\u4f60\u6cbb\uff0c\u4f60\u53d7\u4e86\u4f24\u4e86\u3002\u201d\u4f38\u624b\u4e0a\u524d\uff0c\u4e91\u857e\u7a81\u7136\u4e00\u4e2a\u8f6c\u8eab\uff0c\u6447\u4e86\u6447\u5934\uff0c\u8dcc\u5750 \u5730\u4e0a\uff0c\u54c7\u7684\u5410\u51fa\u4e00\u53e3\u8840\u75f0\uff0c\u9053\uff1a\u201c\u4f60\u4e0d\u8981\u6765\uff0c\u6211\u81ea\u5df1\u6cbb\u3002\u201d \u3000\u3000\u5f20\u4e39\u67ab\u6014\u4e86\u4e00\u6014\uff0c\u5ffd\u7136\u7b11\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u8fd9\u4e2a\u65f6\u5019\u4f60\u8fd8\u907f\u5fcc\u4e48\uff1f\u6211\u65e9\u770b\u51fa\u6765\u4e86\u3002\u201d\u4e91\u857e \u9762\u7ea2\u8fc7\u8033\uff0c\u628a\u5934\u5dfe\u4e00\u63ed\uff0c\u9732\u51fa\u9752\u4e1d\uff0c\u542b\u7f9e\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u8be5\u7792\u9a97\u5927\u54e5\uff0c\u6211\u5b9e\u662f\u4e00\u4e2a\u5973\u5b50\u3002\u201d\u5f20 \u4e39\u67ab\u9053\uff1a\u201c\u610f\u6c14\u76f8\u6295\u7ed3\u4e3a\u77e5\u5df1\uff0c\u53c8\u4f55\u5fc5\u95ee\u662f\u7537\u662f\u5973\uff0c\u662f\u5973\u662f\u7537\u3002\u55ef\uff0c\u5c0f\u5144\u5f1f\uff0c\u96be\u9053\u4f60\u4e5f\u6709\u4e16 \u4fd7\u4e4b\u89c1\u4e48\uff1f\u201d\u4e91\u857e\u89c1\u4ed6\u6c14\u6717\u795e\u6e05\uff0c\u6f47\u6d12\u8131\u4fd7\uff0c\u4e5f\u4e0d\u89c9\u6cef\u706d\u4e86\u7537\u5973\u4e4b\u9632\uff0c\u5fae\u5fae\u4e00\u7b11\u6b63\u60f3\u8bf4\u9053\uff1a \u201c\u53ef\u662f\u54b1\u4eec\u5f7c\u6b64\u7684\u6765\u5386\uff0c\u90fd\u8fd8\u662f\u4e92\u4e0d\u77e5\u9053\u5462\uff01\u201d\u4f46\u89c1\u5f20\u4e39\u67ab\u5634\u89d2\u542b\u7b11\uff0c\u6447\u624b\u8bf4\u9053\uff1a\u201c\u5c0f\u5144 \u5f1f\uff0c\u6211\u77e5\u9053\u4f60\u80f8\u4e2d\u6709\u65e0\u6570\u7591\u56e2\uff0c\u6211\u4e5f\u662f\u6709\u8bb8\u591a\u7591\u95ee\uff0c\u4f46\u4f60\u5982\u4eca\u4f24\u91cd\uff0c\u5b9e\u4e0d\u5b9c\u591a\u8bf4\u8bdd\uff0c\u591a\u5219\u4e94 \u65e5\uff0c\u5c11\u5219\u4e09\u65e5\uff0c\u5f85\u4f60\u4f24\u597d\u4e4b\u540e\uff0c\u54b1\u4eec\u518d\u8bf4\u4e2a\u75db\u5feb\u5982\u4f55\uff1f\u201d\u4e91\u857e\u9894\u9996\u4e0d\u8a00\u8bed\uff0c\u53ea\u89c1\u5f20\u4e39\u67ab\u53c8\u662f \u5fae\u5fae\u4e00\u7b11\uff0c\u9762\u5bf9\u7740\u4e91\u857e\u8bf4\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u4f60\u7684\u4f24\u52bf\u5982\u4f55\uff0c\u5e94\u8be5\u5982\u4f55\u6cbb\u6cd5\uff0c\u6211\u90fd\u5b9e\u5728\u5bf9\u4f60\u8bf4\u4e86 \u5427\u3002\u201d\u4e91\u857e\u9762\u9732\u7b11\u5bb9\uff0c\u53c8\u70b9\u4e86\u70b9\u5934\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u4e2a\u5927\u54e5\u4eba\u5012\u723d\u5feb\u5f97\u5f88\uff0c\u751a\u5408\u6211\u7684\u5fc3\u601d\uff0c\u53ea\u662f \u4ed6\u4e3a\u4ec0\u4e48\u8981\u90a3\u6837\u7b11\u5462\uff1f\u201d\u53ea\u542c\u5f97\u5f20\u4e39\u67ab\u7eed\u8bf4\u9053\uff1a\u201c\u6211\u770b\u4f60\u8fd9\u4f24\u52bf\uff0c\u662f\u88ab\u767d\u6469\u8bc3\u7684\u638c\u529b\u9707\u52a8\u4e86 \u80cc\u540e\u7684\u810a\u5fc3\u7a74\u809d\u810f\u79fb\u4f4d\uff0c\u4f60\u6240\u7ec3\u7684\u5185\u5bb6\u52b2\u529b\u90c1\u79ef\u4e0d\u80fd\u53d1\u6563\uff0c\u6240\u4ee5\u5fc3\u5934\u71e5\u70ed\uff0c\u9762\u7ea2\u76ee\u8d64\uff0c\u82e5\u4e0d \u53ca\u65e9\u533b\u6cbb\uff0c\u5143\u6c14\u5fc5\u7136\u5927\u635f\uff0c\u4e0d\u6b7b\u4e5f\u8981\u53d8\u6210\u6b8b\u5e9f\u4e86\u3002\u597d\u5728\u4f60\u7684\u5185\u529f\u5df2\u6709\u6839\u5e95\uff0c\u6211\u518d\u4ee5\u672c\u8eab\u529f\u529b \u52a9\u4f60\u628a\u4e09\u9634\uff08\u592a\u9634\u3001\u5c11\u9634\u3001\u53a5\u9634\uff09\u4e09\u9633\uff08\u9633\u660e\u3001\u592a\u9633\u3001\u5c11\u9633\uff09\u7684\u7ecf\u8109\u8d2f\u901a\uff0c\u4e94\u810f\u516d\u8151\u4e4b\u6c14\u4fbf \u81ea\u7136\u80fd\u5faa\u73af\u4e0d\u606f\uff0c\u7cbe\u795e\u6d3b\u6cfc\u4e86\u3002\u201d\u4e2d\u56fd\u53e4\u533b\u5b66\u7684\u201c\u7075\u67a2\u201d\u7ecf\u8109\u7bc7\u8f7d\u6709\u5341\u4e8c\u7ecf\u5341\u4e94\u7edc\u7684\u5b66\u8bf4\uff0c \u770b\u4f3c\u5965\u5999\u65e0\u7a3d\uff0c\u5176\u5b9e\u751a\u6709\u9053\u7406\uff0c\u6240\u8c13\u7ecf\u7edc\u5373\u662f\u4eba\u4f53\u6c14\u8840\u8fd0\u884c\u7ecf\u8fc7\u7684\u8054\u7edc\u7684\u9053\u8def\uff0c\u6c14\u8840\u7545\u901a\uff0c \u81ea\u7136\u767e\u75c5\u4e0d\u751f\u3002\uff08\u7fbd\u751f\u6309\uff1a\u5357\u4eac\u4e2d\u533b\u5b66\u9662\u7740\u6709\u300a\u4e2d\u533b\u5b66\u6982\u8bba\u300b\u4e00\u539a\u672c\uff0c\u5185\u6709\u4e24\u7ae0\u4e13\u8bba\u300a\u5341\u4e8c \u7ecf\u8109\u7684\u5faa\u884c\u300b\u4e0e\u300a\u5947\u7ecf\u516b\u8109\u300b\u7684\uff0c\u751a\u4e3a\u8be6\u5c3d\uff0c\u6709\u5174\u8da3\u8005\uff0c\u53ef\u4ee5\u53c2\u770b\u3002\uff09\u53e4\u4ee3\u51e1\u4e60\u6b66\u4e4b\u4eba\uff0c\u591a \u5c11\u61c2\u70b9\u4e2d\u533b\u7684\u9053\u7406\uff0c\u4e91\u857e\u542c\u4ed6\u6ed4\u6ed4\u4e0d\u7edd\u5730\u8c08\u8bba\u533b\u7406\uff0c\u5fc3\u4e2d\u6697\u6697\u7b11\u9053\uff1a\u201c\u8fd9\u4e2a\u5927\u54e5\u771f\u6709\u610f\u601d\uff0c \u524d\u4e24\u65e5\u770b\u4ed6\u54ed\u7b11\u65e0\u7aef\uff0c\u53ea\u9053\u4ed6\u662f\u4e00\u4e2a\u6e38\u620f\u4eba\u95f4\u7684\u72c2\u58eb\uff0c\u5982\u4eca\u770b\u4ed6\u6b63\u895f\u5371\u5750\uff0c\u8c08\u8bba\u533b\u9053\u5374\u53c8\u4f3c \u4e2a\u535a\u5b66\u7684\u5112\u533b\u4e86\u3002\u201d\u5f20\u4e39\u67ab\u8bf4\u4e86\u533b\u7406\uff0c\u505c\u4e86\u4e00\u505c\uff0c\u5ffd\u5730\u7b11\u9053\uff1a\u201c\u53ef\u662f\u6211\u5374\u8981\u6c42\u4f60\u4e00\u4e8b\uff01\u201d \u3000\u3000\u4e91\u857e\u4f4e\u58f0\u9053\uff1a\u201c\u5927\u54e5\u8bf7\u8bf4\u201d\u5f20\u4e39\u67ab\u4e00\u7b11\u8bf4\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u6211\u7ed9\u4f60\u533b\u6cbb\u4e4b\u65f6\uff0c\u4f60\u8981\u5fd8\u8bb0\u6211 \u662f\u4e2a\u7537\u5b50\uff0c\u6211\u4e5f\u5fd8\u8bb0\u4f60\u662f\u4e2a\u5973\u5b50\u4f60\u505a\u5f97\u5230\u4e48\uff1f\u201d\u4e91\u857e\u9732\u51fa\u672c\u76f8\u4e4b\u540e\uff0c\u5f20\u4e39\u67ab\u4ecd\u53e3\u53e3\u58f0\u58f0\u79f0\u5979 \u4e3a\u201c\u5144\u5f1f\u201d\uff0c\u8bf4\u5f97\u751a\u662f\u81ea\u7136\uff0c\u5fc3\u4e2d\u5b9e\u5df2\u6cef\u706d\u7537\u5973\u4e4b\u89c1\u3002\u4e91\u857e\u672c\u662f\u4e00\u7247\u65e0\u90aa\uff0c\u89c1\u4ed6\u5982\u6b64\uff0c\u66f4\u662f \u91ca\u7136\u65e0\u6742\u5ff5\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u4ed6\u66ff\u6211\u6253\u901a\u4e09\u9634\u4e09\u9633\u7684\u7ecf\u8109\uff0c\u90a3\u81ea\u7136\u4e0d\u514d\u624b\u8db3\u76f8\u63a5\u4e86\uff0c\u6211\u4e0e\u4ed6\u65e2 \u7ed3\u62dc\u2018\u5144\u5f1f\u2019\uff0c\u60c5\u5982\u624b\u8db3\uff0c\u8fd9\u4e5f\u503c\u5f97\u63d0\u51fa\u6765\u8bf4\u5417\uff1f\u201d\u5fae\u5fae\u4e00\u7b11\uff0c\u62ac\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u5f20\u4e39\u67ab\u773c\u5982 \u79cb\u6c34\u6a2a\u6ce2\uff0c\u4f3c\u7b11\u975e\u7b11\uff0c\u53c8\u4e0d\u89c9\u5fc3\u4e2d\u4e00\u8361\uff0c\u8138\u4e0a\u5fae\u5fae\u73b0\u51fa\u7ea2\u6655\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u56db\u5468\u4e00\u987e\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u5893\u4e2d\u4e16\u754c\uff0c\u5012\u50cf\u4e16\u5916\u6843\u6e90\uff0c\u6b63\u5408\u7597\u4f24\u9759\u517b\u3002\u53ea\u662f\u8fd9\u4e24\u5339\u9a6c \u513f\uff0c\u4e0d\u5b9c\u5728\u6b64\u3002\u201d\u957f\u5578\u4e00\u58f0\uff0c\u624b\u638c\u4e00\u62cd\uff0c\u90a3\u201c\u7167\u591c\u72ee\u5b50\u9a6c\u201d\u4f3c\u719f\u6089\u4e3b\u4eba\u5fc3\u610f\uff0c\u7acb\u5373\u8dd1\u4e86\u51fa \u53bb\u3002\u4e91\u857e\u90a3\u5339\u7ea2\u9b03\u6218\u9a6c\u8fd9\u4e24\u65e5\u6765\u4e0e\u7167\u591c\u72ee\u5b50\u751a\u662f\u25a1\u719f\uff0c\u4e5f\u8ddf\u7740\u8dd1\u51fa\u53bb\u4e86\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u628a\u5893\u95e8\u5173\u4e0a\uff0c\u5c01\u4e86\u5893\u9053\uff0c\u7ec6\u7ec6\u5bdf\u770b\uff0c\u8fd9\u5893\u662f\u501a\u5c71\u5efa\u7b51\uff0c\u5893\u4e2d\u6709\u5385\u6709\u623f\uff0c\u4e43\u662f\u53e4\u4ee3 \u664b\u738b\u4e4b\u5893\u3002\u5f20\u4e39\u67ab\u56db\u58c1\u6478\u7d22\uff0c\u6572\u6572\u6253\u6253\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u91cc\u9762\u8fd8\u6709\u5bc6\u5ba4\u3002\u201d\u5728\u5730\u4e0a\u53d6\u8d77\u4e00\u6839\u77f3 \u6761\uff0c\u62b5\u7740\u5899\u89d2\u4e00\u5904\u51f9\u5165\u4e4b\u5904\u5de6\u53f3\u65cb\u8f6c\uff0c\u8fc7\u4e86\u4e00\u4f1a\u77f3\u58c1\u5ffd\u7136\u5206\u5f00\uff0c\u73b0\u51fa\u4e00\u9053\u6697\u95e8\uff0c\u539f\u6765\u8fd9\u79cd\u5e1d \u738b\u516c\u4faf\u7684\u201c\u5730\u4e0b\u5bab\u6bbf\u201d\uff0c\u90fd\u662f\u8fd9\u79cd\u5efa\u7b51\u3002\u77f3\u95e8\u5185\u4fa7\u4e0e\u95e8\u5916\u76f8\u5bf9\u79f0\u7684\u5730\u65b9\uff0c\u6709\u51f8\u8d77\u90e8\u5206\uff0c\u7528\u4ee5 \u627f\u6258\u4e00\u6839\u7279\u522b\u5236\u9020\u7684\u77f3\u6761\uff0c\u540d\u53eb\u201c\u81ea\u6765\u77f3\u201d\uff0c\u7528\u4f5c\u9876\u95e8\u4e4b\u7528\u3002\u81ea\u6765\u77f3\u4e24\u7aef\u7565\u5bbd\uff0c\u523b\u6709\u83b2\u74e3\uff0c \u4e2d\u95f4\u7565\u7a84\uff0c\u5728\u77f3\u95e8\u5173\u95ed\u4e4b\u65f6\uff0c\u81ea\u6765\u77f3\u4e0a\u7aef\u9876\u7740\u95e8\u5185\u51f8\u8d77\u90e8\u5206\uff0c\u4e0b\u7aef\u5d4c\u5165\u95e8\u5916\u5730\u9762\u4e0a\u4e00\u4e2a\u51f9\u69fd \u5185\uff0c\u82e5\u662f\u4e0d\u660e\u5176\u4e2d\u9053\u7406\uff0c\u4efb\u51ed\u5916\u9762\u7684\u4eba\u5982\u4f55\u7528\u529b\u63a8\u90a3\u77f3\u95e8\u4e5f\u63a8\u4e0d\u5f00\u3002 \u3000\u3000\u6697\u95e8\u5f00\u542f\uff0c\u5f20\u4e39\u67ab\u6276\u4e91\u857e\u5165\u5185\uff0c\u5ffd\u89c1\u91cc\u9762\u5b9d\u5149\u95ea\u8000\uff0c\u6709\u7389\u51e0\u77f3\u6848\uff0c\u5806\u6ee1\u53e4\u73a9\u91d1\u5b9d\u3002\u5f20\u4e39 \u67ab\u4e00\u76b1\u7709\u5934\uff0c\u968f\u624b\u4e00\u626b\uff0c\u5c06\u91d1\u5b9d\u53e4\u73a9\u5168\u90e8\u62e8\u843d\u5730\u4e0a\uff0c\u8e22\u5230\u5899\u89d2\uff0c\u9053\uff1a\u201c\u522b\u8ba9\u8fd9\u4e9b\u52b3\u4ec0\u5b50\u963b\u788d \u5730\u65b9\u3002\u201d\u6276\u4e91\u857e\u5728\u7389\u51e0\u4e0a\u5750\u4e0b\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u53e4\u7389\u6e29\u51c9\uff0c\u5012\u662f\u5927\u53ef\u52a9\u4f60\u5438\u53bb\u8eab\u4e0a\u7684\u70ed\u6bd2\u3002\u201d\u8f7b \u8f7b\u62c9\u8d77\u4e91\u857e\u53f3\u624b\uff0c\u81ea\u98df\u6307\u5c16\u7aef\uff0c\u6cbf\u98df\u6307\u7684\u62c7\u6307\u4fa7\u4e0a\u7f18\uff0c\u901a\u8fc7\u7b2c\u4e00\u3001\u7b2c\u4e8c\u638c\u9aa8\u4e4b\u95f4\uff0c\u4e0a\u5165\u8155\u4e0a \u62c7\u6307\u540e\u4e24\u7b4b\u4e4b\u95f4\u7684\u51f9\u9677\u5904\uff0c\u8f7b\u8f7b\u63a8\u62ff\uff0c\u8fd9\u662f\u9633\u660e\u7ecf\u8109\u5faa\u884c\u90e8\u4f4d\uff0c\u8d70\u80a9\u5cf0\u524d\u7f18\uff0c\u4e0e\u8bf8\u9633\u7ecf\u76f8\u4f1a \u4e8e\u67f1\u9aa8\u7684\u5927\u690e\u4e4b\u4e0a\uff0c\u518d\u5411\u4e0b\u5165\u7f3a\u76c6\uff0c\u8054\u7edc\u80ba\u810f\u3002\u63a8\u62ff\u4e86\u4e00\u9635\uff0c\u4e91\u857e\u53ea\u89c9\u5fae\u5fae\u6709\u4e00\u80a1\u70ed\u6c14\u76f4\u900f \u5fc3\u5934\uff0c\u518d\u8fc7\u4e00\u9635\uff0c\u8bf4\u4e5f\u5947\u602a\uff0c\u5fc3\u5934\u71e5\u6e10\u51cf\uff0c\u904d\u4f53\u751f\u51c9\u3002\u5f20\u4e39\u67ab\u653e\u5f00\u4e86\u624b\uff0c\u9053\uff1a\u201c\u4f60\u7684\u9633\u660e\u7ecf \u8109\u5df2\u662f\u8d2f\u901a\uff0c\u4f60\u81ea\u5df1\u8fd0\u6c14\u884c\u8840\uff0c\u56fa\u672c\u57f9\u539f\u5427\uff0c\u660e\u65e5\u6211\u518d\u66ff\u4f60\u6253\u901a\u592a\u9633\u7ecf\u8109\u3002\u201d \u3000\u3000\u5bc6\u5ba4\u91cc\u6709\u7f8e\u9152\u5185\u812f\uff0c\u60f3\u662f\u90a3\u9ed1\u767d\u6469\u8bc3\u6240\u7559\uff0c\u5f20\u4e39\u67ab\u996e\u9152\u56bc\u8089\uff0c\u5ffd\u800c\u6717\u58f0\u541f\u9053\uff1a\u201c\u5c11\u5987\u57ce \u5357\u6b32\u65ad\u80a0\uff0c\u5f81\u4eba\u84df\u5317\u7a7a\u56de\u9996\uff0c\u8fb9\u98ce\u98d8\u98d8\u90a3\u53ef\u5ea6\uff0c\u7edd\u57df\u82cd\u832b\u66f4\u4f55\u6709\uff1f\u6740\u6c14\u4e09\u65f6\u4f5c\u9635\u4e91\uff0c\u5bd2\u58f0\u4e00 \u591c\u4f20\u5201\u6597\u3002\u5440\u5440\uff0c\u5e1d\u738b\u877c\u8681\u540c\u5c18\u571f\uff0c\u4e16\u4e0a\u4f55\u4eba\u80fd\u4e0d\u673d\uff01\u201d\u6b4c\u58f0\u5982\u7b11\u5982\u54ed\uff0c\u4f3c\u662f\u538c\u6068\u90a3\u7ec8\u53e4\u4e0d \u606f\u7684\u5e72\u6208\uff0c\u6545\u501f\u6b4c\u8bcd\u53d1\u51fa\u65e0\u7a77\u7684\u611f\u6168\u3002 \u3000\u3000\u4e91\u857e\u6b63\u5728\u7528\u529f\uff0c\u542c\u90a3\u6b4c\u58f0\u9661\u5730\u5fc3\u5934\u4e00\u9707\uff0c\u4e0d\u89c9\u51b2\u53e3\u8bf4\u9053\uff1a\u201c\u6218\u4e89\u81ea\u662f\u60b2\u60e8\u4e4b\u4e8b\uff0c\u4f46\u82e5\u88ab \u8499\u53e4\u4eba\u6253\u4e86\u8fdb\u6765\uff0c\u90a3\u4e48\u4e0d\u8bba\u7537\u5973\u8001\u5e7c\uff0c\u5374\u90fd\u8be5\u6267\u5e72\u6208\u4ee5\u536b\u793e\u7a37\u3002\u4e3a\u56fd\u5bb6\u7acb\u5927\u529f\u4e4b\u4eba\uff0c\u4ea6\u53ef\u7b97 \u662f\u4e0d\u673d\u4e4b\u4eba\u4e86\u3002\u201d\u5f20\u4e39\u67ab\u8eab\u5b50\u5fae\u5fae\u53d1\u6296\uff0c\u4e00\u676f\u9152\u6cfc\u5728\u5730\u4e0a\uff0c\u56de\u8fc7\u5934\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u8d76\u5feb\u7528 \u529f\uff0c\u4e0d\u8981\u8bf4\u8bdd\u3002\u6211\u4e00\u65f6\u5fd8\u5f62\uff0c\u75db\u996e\u72c2\u6b4c\uff0c\u60ca\u52a8\u4f60\u4e86\u3002\u201d\u4e91\u857e\u5410\u4e86\u53e3\u6c14\uff0c\u5c0f\u5634\u513f\u4e00\u6485\uff0c\u6267\u62d7\u5730 \u95ee\u9053\uff1a\u201c\u5927\u6b4c\uff0c\u4f60\u8bf4\uff0c\u6211\u7684\u8bdd\u5230\u5e95\u662f\u5bf9\u4e0e\u4e0d\u5bf9\uff1f\u201d\u5f20\u4e39\u67ab\u559d\u4e86\u53e3\u9152\uff0c\u9053\uff1a\u201c\u5bf9\u6781\uff0c\u5bf9\u6781\uff01\u5176 \u5b9e\u60f3\u6253\u4ed7\u7684\u4eba\u90fd\u4e0d\u662f\u8001\u767e\u59d3\uff0c\u82e5\u7136\u8c6a\u6770\u4e4b\u58eb\u90fd\u4e0d\u60f3\u79f0\u738b\u79f0\u5e1d\u4e89\u593a\u6c5f\u5c71\uff0c\u5c82\u4e0d\u751a\u597d\uff1f\u55ef\uff0c\u5c0f\u5144 \u5f1f\uff0c\u54b1\u4eec\u522b\u518d\u8c08\u8bba\u4e86\uff0c\u4f60\u5feb\u5feb\u4e13\u5fc3\u7528\u529f\u5427\u3002\u201d\u4e91\u857e\u601d\u6f6e\u4e00\u8d77\u65e0\u6cd5\u5e73\u4f0f\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u8fd9\u5927\u54e5 \u4e3a\u4eba\u751a\u597d\uff0c\u4f55\u4ee5\u4e00\u8c08\u5230\u8499\u53e4\u4e0e\u4e2d\u56fd\u4e4b\u95f4\u7684\u6218\u4e8b\uff0c\u5c31\u4f3c\u751a\u4e3a\u75db\u82e6\uff0c\u8fd9\u662f\u4f55\u56e0\uff1f\u8fd9\u662f\u4f55 \u56e0\uff1f\u2026\u2026\u201d\u7591\u95ee\u4e1b\u751f\u4e0d\u80fd\u5e73\u606f\u3002\u5f20\u4e39\u67ab\u7f13\u7f13\u8d70\u5230\u5979\u7684\u9762\u524d\uff0c\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u6211\u672c\u6b32\u5f85\u4f60\u4f24\u597d \u4e4b\u540e\uff0c\u4e0e\u4f60\u8bf4\u4e2a\u75db\u5feb\uff0c\u4f46\u770b\u4f60\u7684\u6837\u5b50\uff0c\u4f3c\u4e4e\u4e0d\u8bf4\u4e2a\u660e\u767d\uff0c\u5c31\u4e0d\u80fd\u9759\u4e0b\u5fc3\u601d\u7528\u529f\u3002\u201d\u4e91\u857e\u4f4e\u58f0 \u9053\uff1a\u201c\u662f\u5440\u3002\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u4f46\u4f60\u7684\u4f24\u52bf\uff0c\u5b9e\u5728\u4e0d\u5b9c\u5206\u795e\u8bf4\u8bdd\u3002\u6211\u4eec\u4e4b\u95f4\u6240\u8981\u8bf4\u7684\uff0c\u53c8\u4e0d\u662f \u4e00\u65f6\u534a\u523b\u53ef\u4ee5\u8bf4\u5f97\u660e\u767d\uff0c\u8fd9\u6837\u5427\uff0c\u4f60\u73b0\u5728\u9759\u5fc3\u7528\u529f\uff0c\u5230\u5403\u665a\u996d\u4e4b\u65f6\uff0c\u6211\u7ed9\u4f60\u8bf4\u4e00\u4e2a\u6545\u4e8b\uff0c\u4f60 \u6bcf\u65e5\u90fd\u8981\u5403\u4e00\u6b21\u665a\u996d\uff0c\u7167\u6211\u4f30\u5ea6\uff0c\u4f60\u4e09\u65e5\u4e4b\u540e\u53ef\u597d\uff0c\u90a3\u4e48\u6211\u5c31\u6bcf\u65e5\u7ed9\u4f60\u8bf4\u4e00\u4e2a\u6545\u4e8b\u3002\u5230\u4e86\u7b2c \u56db\u65e5\uff0c\u4f60\u5168\u597d\u4e86\uff0c\u54b1\u4eec\u518d\u5f7c\u6b64\u5c06\u8eab\u4e16\u6765\u5386\u503e\u5410\u51fa\u6765\u3002\u5c0f\u5144\u5f1f\uff0c\u4f60\u82e5\u7136\u662f\u4e0d\u542c\u8bdd\uff0c\u6211\u5c31\u8fde\u6545\u4e8b \u4e5f\u4e0d\u8bf4\u4e0e\u4f60\u542c\uff0c\u54ea\uff0c\u4f60\u73b0\u5728\u4e0d\u8bb8\u95ee\u4e86\uff0c\u5feb\u5feb\u7528\u529f\u3002\u201d \u3000\u3000\u5f20\u4e39\u67ab\u7684\u773c\u5149\u4f3c\u4e4e\u542b\u6709\u4e00\u79cd\u5f3a\u5236\u7684\u529b\u91cf\uff0c\u4e91\u857e\u53ea\u89c9\u6709\u8fd9\u6837\u4e00\u79cd\u611f\u89c9\uff1a\u81ea\u5df1\u8fd8\u662f\u5c0f\u5b69\u5b50\u7684 \u65f6\u5019\uff0c\u6bcd\u4eb2\u6bcf\u665a\u5728\u5979\u5e8a\u8fb9\u5531\u8499\u53e4\u7684\u50ac\u7720\u5c0f\u66f2\uff0c\u90a3\u5145\u6ee1\u67d4\u60c5\u7684\u773c\u5149\uff0c\u4ee4\u4eba\u6c38\u4e0d\u80fd\u5fd8\u3002\u5f20\u4e39\u67ab\u8fd9 \u65f6\u7684\u773c\u5149\u5c31\u53eb\u5979\u60f3\u8d77\u6bcd\u4eb2\u3002\u53ef\u662f\u4e24\u4eba\u7684\u773c\u5149\u6709\u76f8\u540c\u5374\u53c8\u6709\u4e0d\u540c\u3002\u5979\u53c8\u60f3\u8d77\u7237\u7237\u6bcf\u6b21\u6559\u8bad\u5979\u65f6 \u90a3\u79cd\u4e25\u5389\u7684\u773c\u5149\uff0c\u5f20\u4e39\u67ab\u7684\u773c\u5149\u53c8\u53eb\u5979\u60f3\u8d77\u7237\u7237\u3002\u8fd9\u65e2\u662f\u6148\u7231\u7684\u53c8\u662f\u4e25\u5389\u7684\u773c\u5149\uff0c\u6709\u4e00\u79cd\u4ee4 \u4eba\u4e0d\u53ef\u62b5\u6297\u7684\u529b\u91cf\uff0c\u4e91\u857e\u4e0d\u77e5\u4e0d\u89c9\u5982\u53d7\u50ac\u7720\uff0c\u5fc3\u60c5\u6162\u6162\u5730\u5e73\u9759\u4e0b\u53bb\u4e86\uff0c\u4e0d\u4e45\u5c31\u4e13\u5fc3\u4e00\u81f4\u5730\u7528 \u8d77\u529f\u6765\u3002 \u3000\u3000\u8fd9\u53e4\u5893\u662f\u501a\u5c71\u5d26\u5efa\uff0c\u5893\u4e2d\u5bc6\u5ba4\u7684\u4e00\u8fb9\uff0c\u5c31\u662f\u77f3\u5c71\u7684\u5ced\u58c1\uff0c\u5149\u6ed1\u5982\u955c\uff0c\u5c4b\u9876\u4e0a\u7aef\u6709\u6709\u4e24\u4e2a \u77f3\u7f45\uff0c\u6070\u6070\u53ef\u4f5c\u900f\u6c14\u901a\u98ce\u4e4b\u7528\uff0c\u5bf9\u7740\u5893\u95e8\u7684\u77f3\u58c1\u5d4c\u6709\u4e00\u9762\u5c0f\u94dc\u955c\uff0c\u8fd9\u5bc6\u5ba4\u6784\u9020\u5404\u751a\u662f\u7279\u522b\uff0c \u5ba4\u5185\u7684\u4eba\u53ef\u4ee5\u900f\u8fc7\u94dc\u955c\uff0c\u770b\u5230\u5916\u9762\uff0c\u5916\u9762\u7684\u4eba\u5374\u770b\u4e0d\u8fdb\u6765\u3002\u8fd9\u65f6\u9633\u5149\u4ece\u77f3\u7f45\u900f\u8fdb\u5ba4\u5185\uff0c\u770b\u5730 \u4e0a\u7684\u65e5\u5f71\uff0c\u4f3c\u4e4e\u5df2\u8fc7\u5348\u65f6\uff0c\u5916\u9762\u5ffd\u7136\u4f20\u6765\u4e00\u9635\u58f0\u54cd\uff0c\u4f3c\u4e4e\u6709\u4eba\u6316\u95e8\uff0c\u5916\u9762\u7684\u5893\u95e8\uff0c\u5728\u6628\u665a\u6ce2 \u65af\u5987\u4eba\u5e26\u5f20\u3001\u4e91\u4e8c\u4eba\u8fdb\u6765\u4e4b\u65f6\uff0c\u5df2\u88ab\u635f\u574f\u4e86\u4e0b\u9762\u7684\u7a81\u8d77\u7684\u83b2\u74e3\uff0c\u6ca1\u6709\u201c\u81ea\u6765\u77f3\u201d\u9876\u4f4f\uff0c\u5916\u9762 \u7684\u4eba\u6316\u677e\u4e86\u6ce5\u571f\u4e4b\u540e\u4e00\u63a8\u5c31\u63a8\u5f00\u4e86\u3002\u90a3\u94dc\u955c\u7684\u8272\u6cfd\u548c\u5899\u58c1\u7684\u8272\u6cfd\u4e00\u6837\uff0c\u4e91\u857e\u4ed4\u7ec6\u8fa8\u8ba4\uff0c\u90a3\u5f71 \u5728\u94dc\u955c\u4e0a\u7684\u6a21\u7cca\u4eba\u5f71\u7adf\u7136\u4f3c\u662f\u4e00\u4e2a\u719f\u6089\u7684\u5c11\u5973\u3002\u4e91\u857e\u5fc3\u4e2d\u4e00\u52a8\uff0c\u6025\u7528\u8863\u8896\u63e9\u62b9\u94dc\u5883\uff0c\u4e00\u77a7\u6e05 \u695a\uff0c\u9669\u9669\u53eb\u51fa\u58f0\u6765\uff0c\u8fd9\u4e2a\u5c11\u5973\u4e0d\u662f\u522b\u4eba\u6b63\u662f\u8f70\u5929\u96f7\u77f3\u82f1\u7684\u5973\u513f\u77f3\u7fe0\u51e4\u3002 \u3000\u3000\u53ea\u89c1\u77f3\u7fe0\u51e4\u6478\u6478\u7d22\u7d22\u8d70\u4e86\u8fdb\u6765\uff0c\u8fb9\u8d70\u8fb9\u53eb\u9053\uff1a\u201c\u4e91\u76f8\u516c\uff0c\u4e91\u76f8\u516c\uff01\u201d\u4e91\u857e\u5fc3\u4e2d\u6697\u7b11\uff1a \u201c\u6211\u4eec\u8fd8\u53ea\u662f\u534a\u591c\u2018\u592b\u59bb\u2019\uff0c\u5979\u5bf9\u6211\u5012\u601d\u5ff5\u5f97\u7d27\u3002\u201d\u5893\u4e2d\u5149\u7ebf\u6697\u6de1\uff0c\u77f3\u7fe0\u51e4\u8d70\u8fd1\u901a\u9053\uff0c\u8d70\u4e0a \u5927\u5385\u201c\u5693\u201d\u7684\u4e00\u58f0\uff0c\u71c3\u8d77\u706b\u77f3\uff0c\u89c1\u6bbf\u4e0a\u63d2\u6709\u5341\u4e8c\u679d\u725b\u6cb9\u5de8\u70db\uff0c\u6b63\u5408\u5fc3\u610f\uff0c\u4e00\u4e00\u70b9\u71c3\uff0c\u628a\u5927\u5385 \u7167\u8000\u5f97\u660e\u5982\u767d\u663c\u3002\u5bc6\u5ba4\u5185\u6697\u5d4c\u7684\u94dc\u955c\u7167\u51fa\u77f3\u7fe0\u51e4\u7684\u9762\u5bb9\uff0c\u4ee4\u4e91\u857e\u5403\u4e86\u4e00\u60ca\uff1a\u6570\u65e5\u4e0d\u89c1\uff0c\u5979\u7adf \u7136\u6194\u60b4\u5982\u65af\uff01 \u3000\u3000\u94dc\u5883\u5185\u6620\u51fa\u77f3\u7fe0\u51e4\u5f80\u6765\u5bdf\u770b\uff0c\u5ffd\u7136\u8e72\u5728\u5730\u4e0a\uff0c\u201c\u54c7\u201d\u7684\u4e00\u58f0\u54ed\u4e86\u51fa\u6765\u3002\u539f\u6765\u5979\u5728\u5730\u4e0a\u53d1 \u73b0\u4e86\u4e00\u6ee9\u9c9c\u8840\uff0c\u90a3\u672c\u662f\u767d\u6469\u8bc3\u4e2d\u5251\u6240\u6d41\u7684\u8840\u5979\u5374\u4ee5\u4e3a\u662f\u4e91\u857e\u7684\u3002\u9ed1\u767d\u6469\u8bc3\u662f\u5979\u7236\u4eb2\u7684\u8001\u4e3b \u987e\uff0c\u5979\u81ea\u662f\u6df1\u77e5\u8fd9\u4e2a\u6469\u5934\u7684\u5389\u5bb3\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u4e91\u76f8\u516c\u88ab\u9ed1\u767d\u6469\u8bc3\u6240\u4f24\uff0c\u53ea\u6015\u4e0d\u6b7b\u4e5f\u6210\u6b8b \u5e9f\u3002\u201d\u6545\u6b64\u54c0\u54c0\u75db\u54ed\u3002 \u3000\u3000\u4e91\u857e\u89c1\u5979\u54ed\u5f97\u4f24\u5fc3\uff0c\u5341\u5206\u4e0d\u5fcd\uff0c\u8df3\u4e86\u8d77\u6765\uff0c\u60f3\u5f00\u95e8\u51fa\u53bb\uff0c\u5f20\u4e39\u67ab\u4e00\u628a\u5c06\u5979\u6309\u4f4f\u9053\uff1a\u201c\u4e0d \u7ba1\u5916\u9762\u5982\u4f55\uff0c\u4f60\u90fd\u4e0d\u8981\u51fa\u58f0\uff0c\u201d\u62b5\u7740\u5979\u7684\u638c\u5fc3\uff0c\u53c8\u52a9\u5979\u52a8\u6c14\u884c\u8840\u3002 \u3000\u3000\u53ea\u89c1\u77f3\u7fe0\u51e4\u54ed\u4e86\u4e00\u9635\uff0c\u4ece\u6000\u91cc\u638f\u51fa\u4e00\u679d\u73ca\u745a\uff0c\u653e\u5728\u6848\u4e0a\uff0c\u90a3\u6b63\u662f\u4e91\u857e\u9001\u7ed9\u5979\u7684\u8058\u7269\uff0c\u5979 \u6469\u6332\u518d\u56db\uff0c\u54ed\u4e86\u4e00\u9635\uff0c\u53c8\u54c0\u54c0\u53eb\u9053\uff1a\u201c\u5f1f\u5f1f\uff0c\u5f1f\u5f1f\uff0c\u6211\u597d\u82e6\u547d\u5475\uff01\u201d\u4e91\u857e\u5fc3\u4e2d\u8fde\u58f0\u53eb\u9053\uff1a \u201c\u59d0\u59d0\uff0c\u6211\u8fd8\u672a\u6b7b\uff0c\u6211\u8fd8\u672a\u6b7b\u5462\uff01\u201d\u53ef\u662f\u77f3\u7fe0\u51e4\u54ea\u80fd\u542c\u89c1\uff0c\u5979\u53c8\u54ed\u53c8\u53eb\uff0c\u5ffd\u5730\u62d4\u51fa\u4f69\u5200\uff0c\u626c \u7a7a\u865a\u65ab\u4e00\u5200\uff0c\u53eb\u9053\uff1a\u201c\u857e\u5f1f\uff0c\u4e0d\u7ba1\u90a3\u4e24\u4e2a\u9b54\u5934\u5982\u4f55\u5389\u5bb3\uff0c\u6211\u4e00\u5b9a\u8981\u7239\u7239\u66ff\u4f60\u62a5\u4ec7\uff01\u201d\u53cd\u8eab\u8d70 \u51fa\uff0c\u8d70\u4e86\u51e0\u6b65\uff0c\u5ffd\u7136\u53c8\u8e72\u4e86\u4e0b\u6765\uff0c\u5728\u5730\u4e0a\u62fe\u8d77\u4e24\u7247\u91d1\u73af\uff0c\u90a3\u662f\u9ed1\u6469\u8bc3\u5934\u4e0a\u7684\u675f\u53d1\u91d1\u73af\uff0c\u65e9\u4e0a \u6fc0\u6218\u4e4b\u65f6\uff0c\u88ab\u5f20\u4e39\u67ab\u524a\u65ad\u4e86\u7684\u3002\u77f3\u7fe0\u51e4\u5583\u5583\u8bf4\u9053\uff1a\u201c\u54a6\uff0c\u96be\u9053\u90a3\u4e24\u9b54\u5934\u6ca1\u6709\u9a97\u6211\uff1f\u201d\u5c06\u4e24\u7247 \u91d1\u73af\u7ffb\u6765\u8986\u53bb\u5730\u770b\uff0c\u6014\u6014\u51fa\u795e\u3002 \u3000\u3000\u539f\u6765\u90a3\u665a\u4e91\u857e\u8d70\u540e\uff0c\u77f3\u7fe0\u51e4\u4e58\u5feb\u9a6c\u8ffd\u8d76\uff0c\u5728\u8def\u4e0a\u78b0\u89c1\u9ed1\u767d\u6469\u8bc3\uff0c\u5411\u4ed6\u4eec\u6253\u542c\u6709\u6ca1\u6709\u89c1\u8fc7 \u50cf\u4e91\u857e\u8fd9\u6837\u770b\u9752\u4fca\u4fcf\u7684\u5c0f\u4f19\u5b50\uff0c\u9ed1\u767d\u6469\u8bc3\u95ee\u4e86\u4e91\u857e\u7684\u5f62\u72b6\uff0c\u51b7\u7b11\u4e00\u58f0\uff0c\u95ee\u9053\uff1a\u201c\u4ed6\u662f\u4f60\u7684\u4ec0 \u4e48\u4eba\uff1f\u201d\u77f3\u7fe0\u51e4\u4f9d\u5b9e\u8bf4\u4e86\uff0c\u9ed1\u6469\u8bc3\u201c\u54fc\u201d\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u597d\u4f84\u5973\uff0c\u4f60\u914d\u7684\u597d\u592b\u5a7f\uff0c\u529f\u592b\u771f\u4e0d\u9519 \u5440\uff01\u201d\u77f3\u7fe0\u51e4\u60ca\u9053\uff1a\u201c\u4f60\u8001\u5982\u4f55\u77e5\u9053\uff1f\u201d\u9ed1\u6469\u8bc3\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4ed6\u66ff\u4f60\u8d62\u4e86\u4e00\u5927\u7b14\u73e0\u5b9d\uff0c\u6211\u5728 \u6b64\u5730\u6240\u6709\u7684\u90fd\u8f93\u7ed9\u4ed6\u4e86\u3002\u8f70\u5929\u96f7\u6709\u8fd9\u6837\u7684\u597d\u5973\u5a7f\uff0c\u81ea\u4e50\u5f97\u91d1\u76d8\u6d17\u624b\u4e0d\u5fc5\u5e72\u5566\u3002\u201d\u77f3\u7fe0\u51e4\u4e00 \u60ca\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\uff0c\u4ed6\u5c45\u7136\u6562\u548c\u4f60\u8001\u52a8\u624b\u4e86\uff1f\u201d\u9ed1\u6469\u8bc3\u6012\u76ee\u76f8\u89c6\uff0c\u4ee5\u4e3a\u77f3\u7fe0\u51e4\u662f\u5b58\u5fc3\u6c14\u4ed6\uff0c\u4e0d \u7406\u4e0d\u7b54\uff0c\u4e0e\u767d\u6469\u8bc3\u4e00\u6012\u800c\u53bb\u3002 \u3000\u3000\u77f3\u7fe0\u51e4\u77e5\u9053\u9ed1\u767d\u6469\u8bc3\u79d8\u5bc6\u7684\u85cf\u8eab\u5893\u7a9f\uff0c\u614c\u5fd9\u8d76\u5230\uff0c\u5979\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\u4e91\u857e\u5c45\u7136\u4f1a\u6253\u8d25\u9ed1\u767d \u6469\u8bc3\uff0c\u6b64\u9645\u53d1\u73b0\u4e86\u9ed1\u6469\u8bc3\u88ab\u524a\u65ad\u7684\u91d1\u73af\uff0c\u5140\u662f\u5c06\u4fe1\u5c06\u7591\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u4ee5\u9ed1\u767d\u6469\u8bc3\u90a3\u6837\u5927\u7684 \u672c\u9886\uff0c\u7edd\u65e0\u8f93\u7ed9\u4e91\u857e\u7684\u9053\u7406\u3002\u4f46\u4ee5\u9ed1\u767d\u6469\u8bc3\u90a3\u6837\u5927\u7684\u540d\u5934\uff0c\u4ea6\u4f3c\u4e4e\u4e0d\u4f1a\u8bf4\u8c0e\uff0c\u8fd9\u662f\u600e\u4e48\u56de \u4e8b\uff0c\u96be\u9053\u662f\u53e6\u6709\u522b\u4eba\u4f24\u4e86\u857e\u5f1f\u4e48\uff1f\u201d\u5979\u8fd8\u4ee5\u4e3a\u5730\u4e0a\u6240\u6d41\u7684\u662f\u4e91\u857e\u7684\u9c9c\u8840\u3002\u6b63\u5728\u601d\u7591\u4e0d\u5b9a\uff0c\u5ffd \u542c\u5f97\u5916\u9762\u4e00\u58f0\u9a6c\u5636\uff0c\u53ea\u89c1\u4e00\u4e2a\u5c11\u5e74\u7275\u7740\u4e00\u5339\u7ea2\u9a6c\uff0c\u8d70\u5165\u5893\u9053\uff0c\u8fd9\u5339\u9a6c\u6b63\u662f\u4e91\u857e\u7684\u7ea2\u9b03\u6218\u9a6c\u3002 \u4e91\u857e\u4e00\u89c1\uff0c\u53c8\u51e0\u4e4e\u56b7\u51fa\u58f0\u6765\uff01 \u3000\u3000\u8fd9\u5c11\u5e74\u4e0d\u662f\u522b\u4eba\uff0c\u6b63\u662f\u91d1\u5200\u5be8\u4e3b\u5468\u5065\u7684\u513f\u5b50\u5468\u5c71\u6c11\uff0c\u4ed6\u5949\u4e86\u7236\u4eb2\u4e4b\u547d\uff0c\u5165\u5173\u6765\u529e\u4e00\u4ef6\u4e8b \u60c5\uff0c\u5e76\u63a2\u542c\u4e91\u857e\u7684\u8e2a\u8ff9\u3002\u7ecf\u8fc7\u6b64\u5730\uff0c\u89c1\u4e86\u4e91\u857e\u7684\u7ea2\u9a6c\uff0c\u90a3\u7ea2\u9b03\u6218\u9a6c\uff0c\u672c\u662f\u5468\u5c71\u6c11\u7684\u5750\u9a91\uff0c\u56e0 \u6b64\u628a\u4ed6\u5e26\u5165\u5893\u7a74\u3002 \u3000\u3000\u90a3\u7ea2\u9a6c\u6b22\u8dc3\u5636\u9e23\uff0c\u4f3c\u662f\u5411\u65e7\u4e3b\u4eba\u793a\u610f\uff0c\u4e91\u857e\u5c31\u5728\u91cc\u9762\uff0c\u5468\u5c71\u6c11\u6b63\u5728\u6697\u6697\u79f0\u5947\uff0c\u9661\u7136\u60f3\u8d77 \u9ed1\u767d\u6469\u8bc3\u7231\u4f4f\u53e4\u5893\u7684\u602a\u50fb\u884c\u5f84\uff0c\u4e0d\u89c9\u5413\u51fa\u4e00\u8eab\u51b7\u6c57\u3002\u8fdb\u4e86\u5893\u95e8\uff0c\u89c1\u5927\u5385\u4e0a\u706f\u706b\u8f89\u714c\uff0c\u6773\u65e0\u4e00 \u4eba\u66f4\u662f\u5403\u60ca\uff0c\u6b63\u60f3\u51fa\u58f0\u547c\u5524\uff0c\u5ffd\u89c1\u4e00\u4e2a\u62ab\u5934\u6563\u53d1\u7684\u5973\u5b50\uff0c\u5728\u5899\u89d2\u6697\u5904\u7a81\u7136\u8dc3\u51fa\uff0c\u4e00\u5200\u5c31\u5288\u8fc7 \u6765\u3002\u539f\u6765\u77f3\u7fe0\u51e4\u54ed\u4e86\u534a\u5929\uff0c\u5df2\u662f\u795e\u5fd7\u660f\u4e71\uff0c\u89c1\u4e86\u4e91\u857e\u7684\u7ea2\u9b03\u6218\u9a6c\uff0c\u7adf\u8ba4\u5b9a\u5468\u5c71\u6c11\u5c31\u662f\u6697\u7b97\u4e91 \u857e\u4e4b\u4eba\u3002 \u3000\u3000\u77f3\u7fe0\u51e4\u8fd9\u4e00\u5200\u6765\u52bf\u751a\u731b\uff0c\u5468\u5c71\u6c11\u5413\u4e86\u4e00\u8df3\uff0c\u6025\u6025\u95ea\u5f00\uff0c\u77f3\u7fe0\u51e4\u7b2c\u4e8c\u5200\u53c8\u659c\u91cc\u5288\u5230\uff0c\u5468\u5c71 \u6c11\u62d4\u51fa\u8170\u5200\uff0c\u5c06\u5979\u9694\u5f00\uff0c\u53ea\u89c1\u77f3\u7fe0\u51e4\u72b6\u82e5\u75af\u72c2\uff0c\u7b2c\u4e09\u5200\u3001\u7b2c\u56db\u5200\u8fde\u73af\u5288\u81f3\uff0c\u5468\u5c71\u6c11\u53eb\u9053\uff1a \u201c\u5582\u6211\u4e0e\u4f60\u65e0\u51a4\u65e0\u4ec7\uff0c\u4f55\u6545\u65bd\u884c\u6697\u88ad\uff1f\u201d \u3000\u3000\u77f3\u7fe0\u51e4\u8fde\u5288\u56db\u5200\uff0c\u731b\u7136\u60f3\u9053\uff1a\u201c\u8fd9\u25a1\u672c\u4e8b\u4e0e\u6211\u76f8\u82e5\uff0c\u600e\u80fd\u662f\u4e91\u857e\u5bf9\u624b\uff1f\u201d\u518d\u5288\u4e24\u5200\uff0c\u626c \u58f0\u95ee\u9053\uff1a\u201c\u5140\u4f60\u8fd9\u25a1\uff0c\u5feb\u8bf4\u5b9e\u8bdd\u8fd9\u7ea2\u9b03\u6218\u9a6c\uff0c\u4f60\u662f\u4ece\u4f55\u5904\u5f97\u6765\uff1f\u201d \u3000\u3000\u5468\u5c71\u6c11\u54c8\u54c8\u4e00\u7b11\uff0c\u970d\u5730\u8df3\u5f00\uff0c\u624b\u629a\u7ea2\u9a6c\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u7ea2\u9b03\u6218\u9a6c\uff0c\u672c\u6765\u5c31\u662f\u6211\u7684\u5750\u9a91\uff0c\u4f60 \u95ee\u5b83\u4f5c\u751a\uff1f\u201d\u90a3\u7ea2\u9a6c\u6328\u7740\u5468\u5c71\u6c11\u25a1\u64e6\uff0c\u72b6\u6781\u4eb2\u70ed\uff0c\u4f3c\u662f\u8bc1\u5b9e\u5468\u5c71\u6c11\u6240\u8bf4\u975e\u5047\u3002 \u3000\u3000\u77f3\u7fe0\u51e4\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u94a2\u5200\u4e00\u6643\uff0c\u5288\u5230\u4e2d\u9014\uff0c\u89c1\u6b64\u60c5\u72b6\u5ffd\u53c8\u505c\u4f4f\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u8fd9\u7ea2\u9b03 \u6218\u9a6c\uff0c\u6027\u70c8\u975e\u5e38\uff0c\u600e\u80af\u5982\u6b64\u542c\u4ed6\u8bf4\u8bdd\uff1f\u201d \u3000\u3000\u53ea\u89c1\u5468\u5c71\u6c11\u76ee\u5149\u56db\u5c04\uff0c\u5ffd\u7136\u505c\u5728\u5f53\u4e2d\u77f3\u6848\u4e4b\u4e0a\uff0c\u4e00\u773c\u77a5\u89c1\u90a3\u679d\u73ca\u745a\uff0c\u9762\u8272\u7acb\u53d8\uff0c\u500f\u5730\u8df3 \u53bb\uff0c\u4f38\u624b\u4fbf\u62ff\uff0c\u77f3\u7fe0\u51e4\u94a2\u5200\u4e00\u6643\u9694\u5728\u5f53\u4e2d\uff0c\u6012\u58f0\u65a5\u9053\uff1a\u201c\u4f60\u505a\u4ec0\u4e48\uff1f\u201d\u5468\u5c71\u6c11\u9053\uff1a\u201c\u54a6\uff0c\u4f60 \u505a\u4ec0\u4e48\uff1f\u201d\u77f3\u7fe0\u51e4\u51b7\u7b11\u9053\uff1a\u201c\u83ab\u975e\u8fd9\u73ca\u745a\u4e5f\u662f\u4f60\u7684\u4e48\uff1f\u201d\u5468\u5c71\u6c11\u53c8\u662f\u54c8\u54c8\u4e00\u7b11\u6602\u5934\u8bf4\u9053\uff1a \u201c\u5b9e\u4e0d\u76f8\u7792\uff0c\u8fd9\u73ca\u745a\u6b63\u662f\u5728\u4e0b\u7684\uff01\u201d\u58f0\u8c03\u4e00\u53d8\uff0c\u5389\u58f0\u95ee\u9053\uff1a\u201c\u5140\u4f60\u8fd9\u5a46\u5a18\uff0c\u5feb\u8bf4\u5b9e\u8bdd\uff0c\u4f60\u8fd9 \u73ca\u745a\u662f\u5077\u6765\u7684\u8fd8\u662f\u52ab\u6765\u7684\uff1f\u201d\u987b\u77e5\u8fd9\u679d\u73ca\u745a\u5b9e\u662f\u5468\u5065\u9001\u4e0e\u4e91\u857e\uff0c\u4e91\u857e\u518d\u9001\u4e0e\u7fe0\u51e4\u7684\uff0c\u5468\u5c71\u6c11 \u89c1\u4e86\u73ca\u745a\uff0c\u4e0d\u7531\u5f97\u5fc3\u751f\u7591\u8651\u3002 \u3000\u3000\u77f3\u7fe0\u51e4\u5927\u6012\u8df3\u8d77\uff0c\u970d\u7684\u4e00\u5200\u53c8\u5288\u8fc7\u53bb\uff0c\u5468\u5c71\u6c11\u8fd8\u4e86\u4e00\u5200\uff0c\u7edd\u4e0d\u5ba2\u6c14\uff0c\u52b2\u529b\u5947\u5927\uff0c\u77f3\u7fe0\u51e4 \u7684\u5200\u51e0\u7ed9\u9707\u98de\uff0c\u6025\u7528\u8e51\u4e91\u6b65\u6cd5\u8eab\u5f62\u4e00\u8f6c\uff0c\u7ed5\u5230\u5468\u5c71\u6c11\u80cc\u540e\uff0c\u5468\u5c71\u6c11\u53cd\u624b\u4e00\u5200\uff0c\u6ca1\u6709\u626b\u4e2d\uff0c\u4e24 \u4eba\u767b\u65f6\u53c8\u6253\u8d77\u6765\u3002 \u3000\u3000\u4e91\u857e\u5728\u5bc6\u5ba4\u4e2d\u89c1\u4e24\u4eba\u6253\u6597\u751a\u70c8\uff0c\u6781\u4e3a\u7740\u6025\uff0c\u7adf\u4e0d\u80fd\u5b89\u5fc3\u8fd0\u6c14\u5410\u7eb3\uff0c\u5f20\u4e39\u67ab\u53cc\u638c\u62b5\u7740\u4e91\u857e \u638c\u5fc3\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u522b\u6025\uff0c\u4ed6\u4eec\u4e8c\u4eba\u8c01\u4e5f\u80dc\u4e0d\u4e86\u8c01\u3002\u90a3\u7537\u5b50\u662f\u4f60\u719f\u8bc6\u7684\u4e48\uff1f\u201d\u4e91\u857e\u70b9\u4e86\u70b9 \u5934\uff0c\u5ffd\u60f3\u8d77\u5f20\u4e39\u67ab\u6495\u6bc1\u65e5\u6708\u53cc\u65d7\u4e4b\u4e8b\uff0c\u77aa\u4ed6\u4e00\u773c\uff0c\u5f04\u5f97\u5f20\u4e39\u67ab\u83ab\u540d\u5176\u5999\u3002 \u3000\u3000\u5468\u5c71\u6c11\u4e0e\u77f3\u7fe0\u51e4\u6597\u4e86\u4e09\u4e94\u5341\u62db\uff0c\u4e00\u4e2a\u80dc\u5728\u5200\u6c89\u529b\u52b2\uff0c\u4e00\u4e2a\u80dc\u5728\u8eab\u7075\u6b65\u6377\uff0c\u679c\u662f\u4e0d\u5206\u80dc \u8d1f\uff0c\u77f3\u7fe0\u51e4\u65ab\u4e86\u4e00\u5200\uff0c\u5ffd\u7136\u626c\u58f0\u559d\u9053\uff1a\u201c\u4f60\u8bf4\u73ca\u745a\u662f\u4f60\u7684\uff0c\u4f60\u6709\u4ec0\u4e48\u8bb0\u53f7\uff1f\u201d \u3000\u3000\u5468\u5c71\u6c11\u54c8\u54c8\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u8c05\u4f60\u8fd9\u52ab\u8d3c\u4e5f\u4e0d\u77e5\u9053\uff0c\u4f60\u770b\u90a3\u73ca\u745a\u7684\u7b2c\u4e09\u53f6\u53f6\u5e95\uff0c\u662f\u4e0d\u662f\u523b \u6709\u4e00\u4e2a\u5468\u5b57\uff1f\u201d\u77f3\u7fe0\u51e4\u65e5\u6765\u7779\u7269\u601d\u4eba\uff0c\u628a\u73a9\u90a3\u679d\u73ca\u745a\u4f55\u6b62\u6570\u5341\u767e\u904d\uff0c\u90a3\u201c\u5468\u201d\u5b57\u5979\u65e9\u5df2\u53d1 \u73b0\uff0c\u5fc3\u4e2d\u4e00\u76f4\u6000\u7591\uff0c\u4f55\u4ee5\u4e91\u857e\u9001\u7ed9\u5979\u7684\u8058\u793c\uff0c\u5374\u523b\u4e0a\u522b\u4eba\u7684\u59d3\u6c0f\uff0c\u89c1\u5468\u5c71\u6c11\u5982\u6b64\u4e00\u8bf4\uff0c\u5ffd\u5730 \u604d\u7136\u5927\u609f\uff0c\u62bd\u5200\u8df3\u51fa\u5708\u5b50\u95ee\u9053\uff1a\u201c\u5582\u4f60\u662f\u4e0d\u662f\u4e91\u857e\u7684\u4e49\u5144\uff1f\u201d\u5468\u5c71\u6c11\u4e0d\u89c9\u4e00\u6014\uff0c\u4e5f\u62bd\u5200\u8dc3\u8fc7 \u4e00\u8fb9\uff0c\u9053\uff1a\u201c\u4f60\u65e2\u77e5\u6211\u662f\u4e91\u857e\u7684\u4e49\u5144\uff0c\u4f55\u4ee5\u4e0d\u77e5\u8fd9\u73ca\u745a\u4e43\u662f\u6211\u9001\u4e0e\u5979\u7684\uff1f\u201d \u3000\u3000\u77f3\u7fe0\u51e4\u60f3\u8d77\u90a3\u665a\u6d1e\u623f\u60c5\u4e8b\uff0c\u4e91\u857e\u8001\u662f\u628a\u201c\u4ed6\u201d\u7684\u201c\u4e49\u5144\u201d\u8bf4\u4e2a\u4e0d\u4f11\uff0c\u4e0d\u89c9\u76ef\u4e86\u5c71\u6c11\u4e00 \u773c\uff0c\u53ea\u89c9\u5c71\u6c11\u867d\u4e0d\u53ca\u4e91\u857e\u6e05\u79c0\uff0c\u521a\u5065\u5a01\u6b66\uff0c\u5374\u66f4\u6709\u7537\u5b50\u6c14\u6168\u3002\u8fd9\u65f6\u4ed6\u4e5f\u6b63\u773c\u5149\u5149\u5730\u76ef\u7740\u81ea \u5df1\uff0c\u4e0d\u89c9\u8138\u4e0a\u4e00\u7ea2\uff0c\u201c\u5478\u201d\u4e86\u4e00\u58f0\uff0c\u5979\u60f3\u5230\u90a3\u665a\u60c5\u4e8b\uff0c\u5fc3\u4e2d\u5b9e\u662f\u607c\u6012\u4e91\u857e\u3002\u5468\u5c71\u6c11\u9053\uff1a\u201c\u51ed \u4f60\u8fd9\u4e2a\u5973\u8d3c\uff0c\u5c31\u60f3\u5f3a\u5360\u6211\u7684\u4e1c\u897f\u4e48\uff1f\u201d\u77f3\u7fe0\u51e4\u5927\u6012\u8bf4\u9053\uff1a\u201c\u4ec0\u4e48\u4f60\u7684\u4e1c\u897f\uff1f\u8fd9\u73ca\u745a\u662f\u4e91\u857e\u9001 \u7ed9\u6211\u7684\u8058\u793c\uff0c\u4e0d\u770b\u4f60\u662f\u4e91\u857e\u4e49\u5144\u7684\u9762\u4e0a\uff0c\u6211\u5c31\u4e00\u5200\u628a\u4f60\u5288\u4e86\uff01\u201d \u3000\u3000\u5468\u5c71\u6c11\u987f\u65f6\u6115\u5728\u5f53\u573a\uff0c\u7247\u523b\u8bf4\u9053\uff1a\u201c\u4ec0\u4e48\u8058\u793c\uff1f\u4e91\u857e\u662f\u4f60\u4f55\u4eba\uff1f\u201d\u77f3\u7fe0\u51e4\u9053\uff1a\u201c\u4ed6\u662f\u6211 \u7684\u4e08\u592b\uff0c\u6211\u4e5f\u4e0d\u6015\u8bf4\u4e0e\u4f60\u542c\u3002\u201d\u5468\u5c71\u6c11\u7a81\u7136\u54c8\u54c8\u5927\u7b11\uff0c\u5ffd\u800c\u60f3\u9053\uff1a\u201c\u4e91\u857e\u4e54\u88c5\u6253\u626e\u5355\u8eab\u4e0a \u4eac\uff0c\u8eab\u4e16\u4e4b\u79d8\uff0c\u5b9e\u662f\u4e0d\u80fd\u7ed9\u4eba\u77e5\u9053\uff0c\u6240\u4ee5\u8fde\u8fd9\u4e2a\u5973\u5b50\u4e5f\u7ed9\u5979\u7792\u8fc7\uff0c\u6211\u4e0d\u5e94\u63ed\u7a7f\u5979\u7684\u9762\u76ee\u3002\u201d \u7b11\u58f0\u500f\u5730\u505c\u4f4f\uff0c\u95ee\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4f60\u59d3\u751a\u540d\u8c01\uff1f\u662f\u51e0\u65f6\u4e0e\u4e91\u857e\u6210\u7684\u4eb2\uff1f\u201d \u3000\u3000\u77f3\u7fe0\u51e4\u8fd9\u4e00\u6c14\u975e\u540c\u5c0f\u53ef\uff0c\u624b\u6309\u5200\u67c4\uff0c\u7741\u76ee\u8bf4\u9053\uff1a\u201c\u8f70\u5929\u96f7\u77f3\u82f1\u662f\u6211\u7684\u7236\u4eb2\uff0c\u4e09\u65e5\u4e4b\u524d\u6211 \u4eec\u6210\u4eb2\uff0c\u600e\u4e48\u6837\uff1f\u77f3\u82f1\u7684\u5973\u513f\u914d\u4e0d\u4e0a\u4f60\u7684\u4e49\u5f1f\u4e48\uff1f\u201d \u3000\u3000\u5468\u5c71\u6c11\u9887\u51fa\u610f\u5916\uff0c\u624b\u629a\u5200\u67c4\uff0c\u65bd\u4e86\u4e00\u793c\uff0c\u9053\uff1a\u201c\u5f1f\u5ac2\u4f11\u6012\u6211\u5b9e\u662f\u65e0\u8f7b\u89c6\u4e4b\u610f\u3002\u77f3\u8001\u82f1\u96c4 \u53ef\u597d\uff1f\u201d\u77f3\u7fe0\u51e4\u6c14\u547c\u547c\u5730\u7b54\u9053\uff1a\u201c\u597d\uff01\u201d\u5468\u5c71\u6c11\u9053\uff1a\u201c\u4f60\u4eec\u6210\u4eb2\u4e09\u65e5\uff0c\u4ed6\u90fd\u5728\u9ed1\u77f3\u5e84\u4e48\uff1f\u201d \u5468\u5c71\u6c11\u4e0d\u597d\u610f\u601d\u95ee\u53ca\u6d1e\u623f\u60c5\u72b6\uff0c\u6545\u6b64\u65c1\u6572\u4fa7\u51fb\uff0c\u77f3\u7fe0\u51e4\u9053\uff1a\u201c\u4ed6\u5f53\u665a\u8ffd\u4e00\u767d\u9a6c\u8d3c\u4eba\uff0c\u81f3\u4eca\u4e0d \u77e5\u6d88\u606f\u3002\u201d \u3000\u3000\u5468\u5c71\u6c11\u5927\u5403\u4e00\u60ca\uff0c\u4ed6\u6b63\u662f\u4e3a\u90a3\u201c\u767d\u9a6c\u8d3c\u4eba\u201d\u800c\u6765\uff0c\u4fbf\u9053\uff1a\u201c\u662f\u4e0d\u662f\u4e00\u4e2a\u4e66\u751f\u6a21\u6837\u7684\u767d\u9a6c \u5c11\u5e74\uff1f\u201d\u77f3\u7fe0\u51e4\u9053\uff1a\u201c\u6211\u672a\u89c1\u8fc7\u4ed6\u7684\u9762\u8c8c\u3002\u201d\u5468\u5c71\u6c11\u9053\uff1a\u201c\u4ed6\u7684\u767d\u9a6c\u795e\u9a8f\u975e\u5e38\uff0c\u662f\u4e5f\u4e0d \u662f\uff1f\u201d\u77f3\u7fe0\u51e4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u4eec\u9ed1\u77f3\u5e84\u6700\u597d\u7684\u9a6c\u90fd\u8ffd\u5b83\u4e0d\u4e0a\u3002\u201d\u5468\u5c71\u6c11\u9053\uff1a\u201c\u4f60\u5feb\u9886\u6211\u53bb\u89c1 \u77f3\u8001\u82f1\u96c4\uff0c\u4f20\u7eff\u6797\u7bad\u6349\u6355\u8fd9\u25a1\u3002\u54ce\u54df\uff0c\u4e91\u857e\u53ea\u6050\u88ab\u8fd9\u5978\u8d3c\u5bb3\u4e86\uff01\u201d \u3000\u3000\u5bc6\u5ba4\u5185\u5916\uff0c\u4e91\u857e\u4e0e\u77f3\u7fe0\u51e4\u540c\u5403\u4e00\u60ca\uff0c\u53ea\u542c\u5f97\u77f3\u7fe0\u51e4\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\u5978\u8d3c\uff1f\u6211\u53ea\u4ee5\u4e3a\u4ed6\u662f\u4e00 \u4e2a\u9ed1\u5403\u9ed1\u7684\u52ab\u5b9d\u8d3c\u4eba\uff0c\u4f46\u6211\u7239\u7239\u5374\u8bf4\u4ed6\u4e0d\u662f\uff0c\u6211\u95ee\u8fc7\u7239\u7239\u4ed6\u662f\u8c01\uff0c\u7239\u7239\u53c8\u4e0d\u80af\u8bf4\uff0c\u8a00\u8c08\u4e4b \u95f4\uff0c\u7239\u7239\u53cd\u800c\u597d\u50cf\u5bf9\u4ed6\u751a\u4e3a\u5c0a\u656c\uff0c\u8fd9\u7a76\u7adf\u662f\u600e\u4e48\u4e00\u56de\u4e8b\uff1f\u201d \u3000\u3000\u5468\u5c71\u6c11\u51b7\u51b7\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4ed6\u5417\uff0d\uff0d\u201d\u5893\u95e8\u5916\u5f71\u5b50\u4e00\u6643\uff0c\u5ffd\u7136\u53c8\u8d70\u8fdb\u4e00\u4eba\uff0c\u987f\u65f6\u628a\u5468\u5c71\u6c11 \u7684\u8bf4\u8bdd\u6253\u65ad\u3002\u4e91\u857e\u4e00\u89c1\uff0c\u53c8\u5403\u4e00\u60ca\uff0c\u8fd9\u4eba\u4e43\u662f\u90a3\u665a\u5728\u53e4\u5bfa\u5916\u4e0e\u5979\u52a8\u8fc7\u624b\u7684\u80e1\u8d3c\uff0c\u6fb9\u53f0\u706d\u660e\u7684 \u5f92\u5f1f\uff01\u53ea\u89c1\u5468\u5c71\u6c11\u4e00\u8dc3\u800c\u8d77\uff0c\u6325\u5200\u4fbf\u65a9\uff0c\u5927\u58f0\u9a82\u9053\uff1a\u201c\u5927\u80c6\u80e1\u513f\u5077\u5165\u4e2d\u56fd\uff0c\u610f\u6b32\u4f55\u4e3a\uff01\u201d\u539f \u6765\u6fb9\u53f0\u706d\u660e\u4e0e\u4ed6\u7684\u5f92\u5f1f\u90fd\u66fe\u9886\u5175\u6253\u8fc7\u5468\u5065\uff0c\u5468\u5c71\u6c11\u66fe\u4e0e\u4ed6\u4ea4\u8fc7\u624b\u3002 \u3000\u3000\u6fb9\u53f0\u706d\u660e\u7684\u5f92\u5f1f\u540d\u53eb\u54c8\u8fbe\u83b1\uff0c\u4e00\u8fdb\u5893\u95e8\u4fbf\u5927\u58f0\u53eb\u9053\uff1a\u201c\u5f20\u76f8\u516c\uff01\u201d\u84e6\u89c1\u5468\u5c71\u6c11\u4e00\u5200\u5288 \u5230\uff0c\u6025\u62d4\u53cc\u94a9\u62b5\u6321\uff0c\u53ee\u5f53\u4e00\u58f0\uff0c\u628a\u5468\u5c71\u6c11\u7684\u91d1\u5200\u683c\u8fc7\u4e00\u8fb9\uff0c\u559d\u9053\uff1a\u201c\u662f\u4f60\u628a\u5f20\u76f8\u516c\u5bb3\u4e86 \u4e48\uff1f\u201d\u5468\u5c71\u6c11\u9053\uff1a\u201c\u8fde\u4f60\u4e5f\u8981\u788e\u5c38\u4e07\u6bb5\uff01\u201d\u6325\u5200\u529b\u65ab\uff0c\u54c8\u8fbe\u83b1\u53cc\u94a9\u4e00\u7acb\u7eb5\u6a2a\u6325\u821e\uff0c\u62db\u6570\u53d8\u5316 \u65e0\u7a77\uff0c\u5c06\u5468\u5c71\u6c11\u6740\u5f97\u53ea\u6709\u62db\u67b6\u4e4b\u529f\uff0c\u6beb\u65e0\u8fd8\u5200\u4e4b\u529b\u3002 \u3000\u3000\u77f3\u7fe0\u51e4\u773c\u770b\u5468\u5c71\u6c11\u5c31\u8981\u843d\u8d25\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u4e2a\u5927\u4f2f\u867d\u65e0\u793c\uff0c\u6211\u5374\u5b9a\u8981\u52a9\u4ed6\u3002\u201d\u62bd\u51fa\u4f69\u5200\uff0c \u4e0a\u524d\u5939\u653b\u3002\u77f3\u7fe0\u51e4\u8eab\u6cd5\u8f7b\u76c8\uff0c\u5728\u54c8\u8fbe\u83b1\u4e4b\u4e0a\uff0c\u6c14\u529b\u867d\u7136\u4e0d\u80dc\uff0c\u4f46\u6709\u5468\u5c71\u6c11\u6321\u4f4f\uff0c\u4e24\u4eba\u957f\u77ed\u4e92 \u8865\u4e24\u67c4\u5355\u5200\u592d\u77eb\u5982\u9f99\uff0c\u7acb\u523b\u5c06\u54c8\u8fbe\u83b1\u7684\u51f6\u7130\u538b\u4f4f\uff0c\u7740\u7740\u53cd\u51fb\u3002 \u3000\u3000\u54c8\u8fbe\u83b1\u53d1\u4e00\u58f0\u5578\uff0c\u53cc\u94a9\u659c\u98de\uff0c\u5c06\u4e24\u53e3\u5355\u5200\u8feb\u5f00\uff0c\u660e\u662f\u8fdb\u653b\u5b9e\u662f\u8d25\u8d70\uff0c\u53ea\u89c1\u4ed6\u594b\u529b\u4e00\u51fb\u7acb \u523b\u62bd\u8eab\u6025\u8d70\uff0c\u5468\u5c71\u6c11\u54ea\u91cc\u80af\u820d\uff0c\u4e0e\u77f3\u7fe0\u51e4\u6025\u6025\u8ddf\u8e2a\u8ffd\u51fb\uff0c\u7247\u523b\u4e4b\u540e\uff0c\u4e09\u4eba\u7684\u58f0\u97f3\u90fd\u53bb\u5f97\u8fdc \u4e86\u3002 \u3000\u3000\u5bc6\u5ba4\u4e4b\u4e2d\uff0c\u4e91\u857e\u601d\u7591\u4e0d\u5b9a\uff0c\u62ac\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u5f20\u4e39\u67ab\u542b\u7b11\u671b\u7740\u81ea\u5df1\uff0c\u4f3c\u4e4e\u662f\u5728\u8bf4\u9053\uff1a\u201c\u4f60 \u77a7\u6211\u662f\u4e2a\u5978\u8d3c\u4e48\uff1f\u201d\u4e91\u857e\u5bf9\u5468\u5065\u7236\u5b50\u672c\u662f\u5341\u5206\u76f8\u4fe1\uff0c\u82e5\u975e\u8fd9\u51e0\u65e5\u4e0e\u5f20\u4e39\u67ab\u540c\u884c\uff0c\u542c\u5230\u5468\u5c71\u6c11 \u90a3\u4e00\u58f0\u201c\u5978\u8d3c\u201d\uff0c\u53ea\u6015\u5c31\u8981\u62d4\u5251\u523a\u4ed6\u3002\u8fd9\u65f6\u5fc3\u4e2d\u597d\u751f\u77db\u76fe\uff0c\u5468\u5c71\u6c11\u65ad\u65ad\u4e0d\u4f1a\u80e1\u4e71\u8bec\u4eba\uff0c\u800c\u5f20 \u4e39\u67ab\u53c8\u7edd\u5bf9\u4e0d\u4f3c\u4e00\u4e2a\u201c\u5978\u8d3c\u201d\uff0c\u540c\u884c\u51e0\u65e5\uff0c\u5979\u5bf9\u5f20\u4e39\u67ab\u5df2\u662f\u7531\u618e\u538c\u800c\u53d8\u4e3a\u559c\u6b22\uff0c\u751a\u81f3\u4e8e\u53ef\u4ee5 \u8bf4\u662f\u6709\u51e0\u5206\u5d07\u62dc\u4ed6\u4e86\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u4ed6\u4ece\u8499\u53e4\u56de\u6765\uff0c\u53ea\u6015\u662f\u50cf\u6211\u7237\u7237\u90a3\u6837\u9003\u8d70\u51fa\u7684\u6c49\u65cf\u5fd7 \u58eb\uff0c\u6240\u4ee5\u8499\u53e4\u8981\u6355\u4ed6\u56de\u53bb\uff0c\u800c\u5468\u5c71\u6c11\u4e5f\u8bef\u4f1a\u4ed6\u662f\u4e2a\u5978\u7ec6\u4e86\u3002\u201d\u81ea\u731c\u81ea\u60f3\uff0c\u5fc3\u4e2d\u91ca\u7136\uff0c\u5ffd\u7136\u5fae \u5fae\u4e00\u7b11\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u5927\u54e5\uff0c\u6211\u76f8\u4fe1\u4f60\uff01\u201c \u3000\u3000\u5f20\u4e39\u67ab\u8138\u8272\u8212\u5c55\uff0c\u73b0\u51fa\u65e0\u9650\u6b23\u60a6\u4e4b\u60c5\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201d\u8d24\u5f1f\uff0c\u4f60\u662f\u6211\u751f\u5e73\u7b2c\u4e00\u77e5\u5df1\u3002\u597d\u597d \u7528\u529f\u5427\uff0c\u4eca\u665a\u6211\u7ed9\u4f60\u8bf4\u7b2c\u4e00\u4e2a\u6545\u4e8b\u3002\u201c\u5f00\u4e86\u5bc6\u5ba4\uff0c\u8d70\u51fa\u5916\u9762\u5c06\u5893\u95e8\u91cd\u53c8\u5173\u4e0a\uff0c\u53c8\u642c\u8fc7\u4e24\u6839\u77f3 \u6761\u9876\u4f4f\uff0c\u975e\u6709\u5343\u65a4\u6c14\u529b\uff0c\u518d\u4e5f\u96be\u5f00\u3002 \u3000\u3000\u4e91\u857e\u4e13\u5fc3\u7528\u529f\uff0c\u5bfc\u6c14\u8fd0\u884c\uff0c\u751a\u89c9\u8212\u670d\uff0c\u8fc7\u4e86\u8bb8\u4e45\uff0c\u5c4b\u9876\u77f3\u9699\uff0c\u5df2\u65e0\u9633\u5149\u5c04\u8fdb\uff0c\u77e5\u662f\u9ec4 \u660f\uff0c\u9ed1\u767d\u6469\u8bc3\u5728\u5bc6\u5ba4\u4e4b\u4e2d\u7559\u6709\u98df\u7cae\uff0c\u5f20\u4e39\u67ab\u751f\u706b\u716e\u4e86\u4e00\u9505\u7a00\u7ca5\uff0c\u628a\u8089\u812f\u3001\u51bb\u9e21\u4e4b\u7c7b\u716e\u70ed\uff0c\u670d \u4f8d\u4e91\u857e\u98df\u7ca5\uff0c\u4e91\u857e\u751a\u662f\u611f\u6fc0\uff0c\u53ea\u89c1\u5f20\u4e39\u67ab\u6e29\u67d4\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u597d\u4e9b\u4e86\uff0c\u4f46\u8fd8\u4e0d\u5b9c\u591a\u8bf4\u8bdd\uff0c\u4f60 \u53ea\u542c\u6211\uff0c\u4e0d\u8981\u591a\u95ee\uff0c\u6211\u73b0\u5728\u5c31\u7ed9\u4f60\u8bf4\u7b2c\u4e00\u4e2a\u6545\u4e8b\u3002\u4e09\u4e2a\u6545\u4e8b\u8bf4\u5b8c\u4e4b\u540e\uff0c\u7136\u540e\u6211\u518d\u8be6\u7ec6\u5c06\u6211\u7684 \u6765\u5386\u8bf4\u4e0e\u4f60\u77e5\u3002\u201d\u6b63\u662f\uff1a \u3000\u3000\u8eab\u4e16\u79bb\u5947\u96be\u4ee5\u8bf4\uff0c\u82b1\u660e\u67f3\u6697\u8d39\u7591\u731c\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u840d\u8e2a\u4fa0\u5f71\u5f55\u300b\u2014\u2014\u7b2c\u4e03\u56de\u3000\u4e00\u7247\u8840\u4e66\u6df1\u4ec7\u8c01\u53ef\u89e3\u3000\u5341\u5206\u5fc3\u4e8b\u65e0\u8bed\u72ec\u601d\u91cf \u6881\u7fbd\u751f\u300a\u840d\u8e2a\u4fa0\u5f71\u5f55\u300b \u7b2c\u4e03\u56de\u3000\u4e00\u7247\u8840\u4e66\u6df1\u4ec7\u8c01\u53ef\u89e3\u3000\u5341\u5206\u5fc3\u4e8b\u65e0\u8bed\u72ec\u601d\u91cf \u3000\u3000\u4e91\u857e\u62ac\u5934\u4e00\u671b\uff0c\u53ea\u542c\u5f97\u5f20\u4e39\u67ab\u8bf4\u9053\uff1a\u201c\u4ece\u524d\u6709\u4e24\u4e2a\u82e6\u4eba\uff0c\u672c\u6765\u90fd\u662f\u66ff\u5730\u4e3b\u79cd\u7530\u7684\uff0c\u540e\u56e0 \u5929\u707e\u4eba\u7978\uff0c\u65e0\u4ee5\u4e3a\u751f\uff0c\u4e00\u4e2a\u505a\u4e86\u53eb\u5316\u5b50\uff0c\u4e00\u4e2a\u505a\u4e86\u8fd0\u79c1\u76d0\u7684\u2018\u76d0\u8885\u2019\uff0c\u53eb\u5316\u5b50\u548c\u79c1\u76d0\u8d29\u5b50\u610f \u6c14\u76f8\u6295\uff0c\u7ed3\u4e3a\u5144\u5f1f\u3002\u90a3\u65f6\u4e2d\u56fd\u88ab\u5f02\u65cf\u7edf\u6cbb\uff0c\u8349\u6cfd\u82f1\u96c4\uff0c\u90fd\u60f3\u8d77\u6765\u53cd\u6297\uff0c\u8fd9\u4e24\u5144\u5f1f\u90fd\u662f\u80f8\u6000\u5927 \u5fd7\uff0c\u597d\u50cf\u53e4\u65f6\u7684\u9648\u80dc\u3001\u5434\u5e7f\u56fe\u8c0b\u53cd\u79e6\u4e00\u6837\uff0c\u51fb\u638c\u7acb\u8a93\uff1a\u82df\u5f97\u5bcc\u8d35\uff0c\u4e92\u4e0d\u76f8\u5fd8\uff01\u53e6\u5916\u8fd8\u6709\u4e00\u4e2a \u548c\u5c1a\u5e74\u7eaa\u6bd4\u8fd9\u4e24\u4eba\u5927\u5f97\u591a\uff0c\u66fe\u6559\u8fc7\u8fd9\u4e24\u5144\u5f1f\u6b66\u827a\uff0c\u4e24\u5144\u5f1f\u5c0a\u79f0\u4ed6\u505a\u5e08\u7236\u3002\u5386\u671d\u5386\u4ee3\u98df\u76d0\u90fd\u662f \u7531\u5b98\u5bb6\u4e13\u5356\u7684\uff0c\u8d29\u79c1\u76d0\u7684\u4eba\uff0c\u4e00\u88ab\u6349\u5230\uff0c\u5c31\u8981\u88ab\u5b98\u5bb6\u5904\u6b7b\u3002\u79c1\u76d0\u8d29\u5b50\u662f\u4e49\u5144\uff0c\u53eb\u5316\u5b50\u662f\u4e49 \u5f1f\u3002\u53eb\u5316\u5b50\u4e0d\u6562\u5192\u9669\uff0c\u5165\u4e86\u4e00\u95f4\u5bfa\u9662\u505a\u5c0f\u548c\u5c1a\uff0c\u540e\u6765\u90a3\u95f4\u5bfa\u9662\u4e5f\u56e0\u707e\u8352\u65e0\u4eba\u65bd\u820d\uff0c\u5bfa\u4e2d\u548c\u5c1a \u5341\u6b7b\u4e03\u516b\uff0c\u79c1\u76d0\u8d29\u5b50\u7528\u6027\u547d\u535a\u5f97\u4e00\u70b9\u94b1\u8d22\u90fd\u5468\u6d4e\u4e86\u4ed6\u7684\u4e49\u5f1f\u3002\u540e\u6765\u90a3\u5bfa\u9662\u9063\u6563\uff0c\u53eb\u5316\u5b50\u505a\u4e86 \u6e38\u65b9\u50e7\u4eba\uff0c\u4ecd\u7136\u5230\u5904\u4e5e\u98df\u3002\u201d \u3000\u3000\u201c\u540e\u6765\u90a3\u4e24\u5144\u5f1f\u7684\u5e08\u7236\u4e3e\u4e49\u65d7\uff0c\u53eb\u5316\u5b50\u4e49\u5f1f\u968f\u4ed6\u8d77\u5175\uff0c\u5728\u4e00\u6b21\u5927\u6218\u4e4b\u540e\uff0c\u90a3\u8001\u548c\u5c1a\u4e0d\u77e5 \u4e0b\u843d\uff0c\u6709\u4eba\u8bf4\u4ed6\u6218\u6b7b\uff0c\u6709\u4eba\u8bf4\u4ed6\u5931\u8e2a\u540e\u4ecd\u7136\u5f53\u4e86\u548c\u5c1a\uff0c\u5230\u5e95\u5982\u4f55\uff0c\u65e0\u4eba\u77e5\u9053\u3002\u201d \u3000\u3000\u201c\u90a3\u79c1\u76d0\u8d29\u5b50\u8fd9\u65f6\u8d29\u76d0\u8fdc\u8d70\u6c5f\u5317\uff0c\u81ea\u5df1\u7ea0\u96c6\u6570\u767e\u76d0\u4e01\uff0c\u4e5f\u8d77\u5175\u79f0\u738b\u3002\u8fc7\u4e86\u597d\u51e0\u5e74\uff0c\u90a3\u79c1 \u76d0\u8d29\u5b50\u52bf\u529b\u6e10\u5927\uff0c\u5728\u82cf\u5dde\u79f0\u5e1d\uff0c\u957f\u6c5f\u51e0\u7701\uff0c\u90fd\u662f\u4ed6\u7684\u3002\u56db\u5904\u89c5\u90a3\u4e49\u5f1f\uff0c\u5374\u89c5\u4e0d\u89c1\u3002\u8fd9\u65f6\u5929\u4e0b \u7fa4\u96c4\u7eb7\u8d77\uff0c\u5176\u4e2d\u6709\u4e00\u8def\u4ee5\u7ea2\u5dfe\u4e3a\u53f7\uff0c\u52bf\u529b\u6700\u5927\uff0c\u90a3\u7ea2\u5dfe\u519b\u7684\u9886\u8896\u524d\u4e24\u5e74\u6b7b\u4e86\uff0c\u7531\u4e00\u4e2a\u5c11\u5e74\u82f1 \u96c4\u7ee7\u4efb\u9886\u8896\uff0c\u653b\u57ce\u63a0\u5730\uff0c\u52bf\u529b\u4f38\u5c55\u5230\u957f\u6c5f\u4ee5\u5357\u3002\u79c1\u76d0\u8d29\u5b50\u4e00\u6253\u542c\uff0c\u8fd9\u5c11\u5e74\u9886\u8896\u539f\u6765\u662f\u505a\u548c\u5c1a \u7684\uff0c\u518d\u4ed4\u7ec6\u6253\u542c\uff0c\u7adf\u7136\u5c31\u662f\u81ea\u5df1\u4ee5\u524d\u90a3\u4e2a\u53eb\u5316\u5b50\u4e49\u5f1f\u3002\u8fd8\u6709\u4eba\u8bf4\uff0c\u8fd9\u53eb\u5316\u5b50\u968f\u8001\u548c\u5c1a\u5174\u5175\uff0c \u8001\u548c\u5c1a\u6218\u8d25\u4e4b\u540e\u4ed6\u6697\u4e2d\u5c06\u8001\u548c\u5c1a\u5356\u7ed9\u5b98\u5bb6\uff0c\u81ea\u5df1\u5374\u88c5\u4f5c\u597d\u4eba\uff0c\u7edf\u7387\u4e86\u8001\u548c\u5c1a\u7684\u90e8\u5c5e\uff0c\u6539\u6295\u7ea2 \u5dfe\u519b\uff0c\u6240\u4ee5\u4e00\u5165\u7ea2\u5dfe\u519b\u5c31\u505a\u4e86\u5934\u76ee\uff0c\u5f97\u5230\u7ea2\u5dfe\u519b\u4e3b\u5e05\u7684\u770b\u91cd\uff0c\u4e00\u8def\u5347\u8fc1\uff0c\u56e0\u6b64\u5176\u540e\u624d\u80fd\u66ff\u4ee3 \u4ed6\u7684\u4f4d\u7f6e\u3002\u79f0\u4e86\u7687\u5e1d\u7684\u4e49\u5144\u4e0d\u76f8\u4fe1\u8fd9\u4e2a\u4f20\u8bf4\uff0c\u4e0d\u8fc7\u6d3e\u4eba\u8054\u7edc\u7684\u7ed3\u679c\uff0c\u5374\u8bc1\u5b9e\u4e86\u8fd9\u4e2a\u7ea2\u5dfe\u519b\u7684 \u65b0\u4e3b\u5e05\u679c\u7136\u662f\u81ea\u5df1\u7684\u4e49\u5f1f\u3002\u201d \u3000\u3000\u201c\u8fd9\u65f6\u4e49\u5144\u4e49\u5f1f\u7684\u52bf\u529b\u5df2\u5728\u957f\u6c5f\u63a5\u89e6\uff0c\u4e49\u5144\u6d3e\u4f7f\u8005\u8fc7\u6c5f\uff0c\u81f4\u4e66\u4e49\u5f1f\uff0c\u8bf4\uff1a\u4f60\u6211\u4e8c\u4eba\u8c01\u505a \u7687\u5e1d\u90fd\u662f\u4e00\u6837\uff0c\u8bf7\u4f60\u8fc7\u6c5f\u76f8\u89c1\uff0c\u5148\u53d9\u5144\u5f1f\u4e4b\u60c5\uff0c\u540e\u5b9a\u8054\u76df\u4e4b\u8ba1\uff0c\u5171\u540c\u5bf9\u6297\u5f02\u65cf\u3002\u4e0d\u6599\u90a3\u4e49\u5f1f \u5374\u5c06\u4e66\u4fe1\u6495\u6bc1\uff0c\u4e0d\u5141\u8fc7\u6c5f\uff0c\u8fd8\u5272\u4e86\u4f7f\u8005\u7684\u8033\u6735\uff0c\u9063\u4ed6\u56de\u6765\u62a5\u9053\uff1a\u5929\u65e0\u4e8c\u65e5\uff0c\u6c11\u65e0\u4e8c\u4e3b\uff0c\u4f60\u6211 \u90fd\u662f\u5f53\u4e16\u82f1\u96c4\uff0c\u4e0d\u662f\u4f60\u6b7b\uff0c\u4fbf\u662f\u6211\u4ea1\uff01\u201d \u3000\u3000\u201c\u4e49\u5144\u63a5\u4e66\u5927\u6012\uff0c\u4e24\u5144\u5f1f\u7adf\u7136\u81ea\u76f8\u6b8b\u6740\uff0c\u6df7\u6218\u51e0\u5e74\uff0c\u4e92\u6709\u80dc\u8d25\uff0c\u6700\u540e\u4e00\u6b21\u5728\u957f\u6c5f\u51b3\u6218\uff0c \u4e49\u5f1f\u5927\u80dc\uff0c\u5c06\u4e49\u5144\u6349\u4f4f\uff0c\u8981\u4e49\u5144\u4fef\u9996\u79f0\u81e3\uff0c\u4e49\u5144\u4e0d\u80af\uff0c\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u2018\u5c0f\u53eb\u5316\uff0c\u4f60\u4e0b\u5f97\u624b\u4fbf \u6740\u4e86\u6211\u5427\u3002\u2019\u4e49\u5f1f\u4e00\u58f0\u4e0d\u53d1\uff0c\u7acb\u523b\u53eb\u4eba\u7528\u4e71\u68cd\u628a\u4e49\u5144\u6253\u6b7b\uff0c\u6c89\u5c38\u957f\u6c5f\uff01\u706d\u4e86\u4e49\u5144\u4e4b\u540e\uff0c\u7acb\u523b \u81ea\u79f0\u7687\u5e1d\u3002\u800c\u4e14\u4e0d\u8fc7\u51e0\u5e74\uff0c\u8fd8\u628a\u5f02\u65cf\u9010\u51fa\u4e2d\u56fd\uff0c\u524a\u5e73\u7fa4\u96c4\u7edf\u4e00\u5929\u4e0b\uff0c\u771f\u4e2a\u6210\u4e86\u4e00\u4ee3\u5f00\u56fd\u7684\u541b \u7687\u3002\u5c0f\u5144\u5f1f\uff0c\u4f60\u8bf4\u8fd9\u7687\u5e1d\u574f\u4e0d\u574f\uff1f\u201d \u3000\u3000\u4e91\u857e\u9053\uff1a\u201c\u8fd9\u4e49\u5f1f\u4e0d\u987e\u624b\u8db3\u4e4b\u60c5\uff0c\u5f53\u7136\u5f88\u574f\u3002\u4e0d\u8fc7\u4ed6\u80fd\u9a71\u9664\u5f02\u65cf\uff0c\u8fd8\u6211\u6cb3\u5c71\uff0c\u5374\u4e5f\u7b97\u5f97 \u662f\u4e2a\u82f1\u96c4\u8c6a\u6770\u3002\u201d\u5f20\u4e39\u67ab\u9762\u8272\u5fae\u53d8\uff0c\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u4f60\u4e5f\u5982\u6b64\u8bf4\u5417\uff1f\u90a3\u5c0f\u53eb\u5316\u505a\u4e86\u7687\u5e1d \u4e4b\u540e\uff0c\u5927\u6740\u529f\u81e3\uff0c\u5bf9\u4e49\u5144\u7684\u540e\u4eba\u66f4\u662f\u4e0d\u80af\u653e\u8fc7\uff0c\u4fa6\u9a91\u56db\u51fa\uff0c\u5fc5\u8981\u6740\u5c3d\u65b9\u4f11\uff0c\u6240\u4ee5\u90a3\u4e49\u5144\u7684\u540e \u4eba\u548c\u4e00\u4e9b\u5fe0\u81e3\u540e\u4ee3\uff0c\u90fd\u8fdc\u8fdc\u9003\u8d70\uff0c\u6d41\u6563\u56db\u65b9\u3002\u5440\uff0c\u4f60\u5403\u5b8c\u7ca5\u5566\uff0c\u597d\u5f97\u5f88\uff0c\u8fd9\u6545\u4e8b\u4e5f\u6070\u5de7\u5b8c \u4e86\u3002\u201d \u3000\u3000\u4e91\u857e\u5ffd\u7136\u62ac\u5934\u8bf4\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4f60\u8bf4\u7684\u8fd9\u4e2a\u6545\u4e8b\u6211\u731c\u5230\u4e86\uff0c\u4f60\u8bf4\u7684\u662f\u6211\u671d\u5f00\u56fd\u4e4b\u4e8b\uff0c\u90a3\u53eb \u5316\u5b50\u4e49\u5f1f\u5c31\u662f\u660e\u592a\u7956\u6731\u5143\u748b\uff0c\u90a3\u79c1\u76d0\u8d29\u5b50\u4e49\u5144\u5c31\u662f\u81ea\u79f0\u5927\u5468\u7687\u5e1d\u7684\u5f20\u58eb\u8bda\uff01\u4e0d\u8fc7\u6211\u53ef\u672a\u542c\u8bf4 \u4ed6\u4eec\u4e8c\u4eba\u7ed3\u62dc\u8fc7\u5144\u5f1f\u3002\u53f2\u4e66\u4e0a\u90fd\u4e0d\u662f\u8fd9\u6837\u5199\u7684\u3002\u4e66\u4e0a\u8fd8\u8bf4\u5f20\u58eb\u8bda\u672c\u6765\u662f\u4e2a\u65e0\u8d56\u5c0f\u4eba\uff0c\u592a\u7956\u6740 \u4ed6\uff0c\u662f\u4e3a\u6c11\u8ba8\u8d3c\u3002\u201d\u5f20\u4e39\u67ab\u51b7\u7b11\u4e00\u58f0\u9053\uff1a\u201c\u6210\u8005\u4e3a\u738b\uff0c\u8d25\u8005\u4e3a\u5bc7\uff0c\u5343\u53e4\u7686\u7136\u3002\u4e0d\u8981\u8bf4\u4ed6\u4eec\u7ed3 \u62dc\u4e4b\u4e8b\u53f2\u4e66\u4e0a\u4e0d\u6562\u5199\uff0c\u90a3\u6731\u5143\u748b\u662f\u5c0f\u53eb\u5316\uff0c\u6e38\u65b9\u50e7\u7684\u51fa\u8eab\uff0c\u5b98\u4fee\u7684\u53f2\u4e66\u4e0a\u4e5f\u4e0d\u662f\u8fde\u63d0\u90fd\u4e0d\u6562 \u63d0\u4e48\uff01\u5176\u5b9e\u505a\u53eb\u5316\u5b50\uff0c\u505a\u7a77\u548c\u5c1a\uff0c\u4e5f\u4e0d\u89c1\u6709\u4ec0\u4e48\u8fb1\u6ca1\u5148\u4eba\u4e4b\u5904\u3002\u54fc\uff0c\u54fc\uff01\u201d\u660e\u592a\u7956\u6731\u5143\u748b\u505a \u8fc7\u4e5e\u4e10\u53c8\u5728\u7687\u89c9\u5bfa\u505a\u8fc7\u548c\u5c1a\u4e4b\u4e8b\uff0c\u5929\u4e0b\u65e0\u4eba\u4e0d\u77e5\uff0c\u5230\u4ed6\u79f0\u5e1d\u4e4b\u540e\uff0c\u5374\u5f15\u4e3a\u5fcc\u8bb3\u3002\u6709\u4e00\u4e2a\u5e9c\u5b66 \u4e0a\u8d3a\u8868\uff0c\u7528\u201c\u777f\u667a\u751f\u77e5\u201d\u56db\u5b57\u88ab\u6740\uff0c\u7f6a\u540d\u5c31\u662f\u56e0\u201c\u751f\u201d\u5b57\u4e0e\u201c\u50e7\u201d\u5b57\u540c\u97f3\uff0c\u6731\u5143\u748b\u7591\u5fc3\u90a3\u5e9c \u5b66\u662f\u501f\u6765\u9a82\u4ed6\u505a\u8fc7\u548c\u5c1a\u3002\u53c8\u6709\u4e00\u4e2a\u6559\u8c15\u4e0a\u8d3a\u8868\u7528\u201c\u53d6\u6cd5\u8c61\u9b4f\u201d\u4e00\u8bed\uff0c\u6731\u5143\u748b\u8bf4\u662f\u201c\u53d6\u6cd5\u201d\u4e0e \u201c\u5243\u53d1\u201d\u540c\u97f3\uff0c\u4e5f\u662f\u9a82\u4ed6\u66fe\u505a\u8fc7\u548c\u5c1a\uff0c\u4e5f\u628a\u90a3\u62cd\u9a6c\u5e87\u62cd\u5230\u9a6c\u811a\u4e0a\u7684\u6559\u8c15\u6740\u4e86\u3002\u6b64\u7b49\u201c\u7b11\u8bdd\u201d \u6697\u4e2d\u6d41\u4f20\uff0c\u5b98\u573a\u7684\u4eba\u8c01\u90fd\u77e5\u9053\u3002\u4e91\u857e\u4e5f\u542c\u7237\u7237\u8bf4\u8fc7\uff0c\u542c\u5f20\u4e39\u67ab\u8bf4\u4e86\u8fd9\u4e2a\u6545\u4e8b\uff0c\u53c8\u60f3\u8d77\u81ea\u5df1\u7237 \u7237\u7684\u60e8\u906d\u6740\u5bb3\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u53cd\u6b63\u505a\u7687\u5e1d\u7684\u90fd\u4e0d\u662f\u597d\u4eba\uff0c\u4e0d\u7ba1\u6731\u5143\u748b\u548c\u5f20\u58eb\u8bda\u90fd\u662f\u4e00\u6837\u3002\u4f46 \u5927\u54e5\u8bf4\u8fd9\u6545\u4e8b\u6709\u4ec0\u4e48\u610f\u601d\uff1f\u4e3a\u4ec0\u4e48\u4ed6\u90a3\u6837\u6068\u5f00\u56fd\u7684\u592a\u7956\u7687\u5e1d\uff1f\u201d\u5f20\u4e39\u67ab\u4e0d\u8bb8\u5979\u591a\u8bf4\u8bdd\uff0c\u53c8\u66ff \u5979\u8f7b\u8f7b\u63a8\u62ff\uff0c\u4e91\u857e\u505a\u4e86\u534a\u5929\u529f\u592b\uff0c\u5143\u6c14\u5c1a\u672a\u6062\u590d\uff0c\u4e5f\u5c31\u4e0d\u8d39\u795e\u7ec6\u60f3\uff0c\u8fc7\u4e86\u7247\u523b\uff0c\u6c89\u6c89\u7761\u53bb\u3002 \u3000\u3000\u7b2c\u4e8c\u65e5\u4e00\u65e9\u9192\u6765\uff0c\u53ea\u89c1\u5f20\u4e39\u67ab\u5750\u5728\u8eab\u8fb9\uff0c\u8863\u4e0d\u89e3\u5e26\uff0c\u53cc\u773c\u5fae\u80bf\uff0c\u4f3c\u662f\u6628\u665a\u66fe\u7ecf\u54ed\u8fc7\uff0c\u4e91 \u857e\u5fc3\u751a\u611f\u6fc0\uff0c\u53c8\u751a\u53ef\u601c\uff0c\u5fc3\u9053\uff1a\u201c\u5f85\u4ed6\u503e\u8bc9\u8eab\u4e16\u4e4b\u540e\uff0c\u6211\u5b9a\u8981\u597d\u597d\u7ed9\u4ed6\u5b89\u6170\u3002\u201d \u3000\u3000\u5f20\u4e39\u67ab\u89c1\u5979\u9192\u6765\uff0c\u542b\u7b11\u95ee\u9053\uff1a\u201c\u597d\u4e00\u70b9\u5417\uff1f\u201d\u4e91\u857e\u7b54\u9053\uff1a\u201c\u597d\u8bb8\u591a\u4e86\u3002\u5927\u54e5\u4f60\u6628\u665a\u6ca1\u597d \u7761\u5475\uff01\u201d\u5f20\u4e39\u67ab\u7b11\u9053\uff1a\u201c\u6211\u6570\u65e5\u4e0d\u7761\u6216\u4e00\u7761\u6570\u65e5\u90fd\u662f\u5e38\u4e8b\uff0c\u4f60\u4e0d\u5fc5\u7ba1\u6211\uff0c\u4f38\u51fa\u4f60\u7684\u811a\u6765\u3002\u201d \u4e91\u857e\u4f38\u51fa\u5de6\u811a\uff0c\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u4e0d\uff0c\u662f\u53f3\u811a\u3002\u201d\u8131\u4e86\u5979\u7684\u978b\u5b50\uff0c\u624b\u6307\u6309\u7740\u5979\u7684\u53f3\u8db3\u7684\u5927\u8dbe\u8dbe\u5c16 \u7aef\uff0c\u6cbf\u5927\u8dbe\u5185\u4fa7\uff0c\u8fc7\u5927\u8d74\u672c\u8282\u540e\u7684\u534a\u5706\u9aa8\uff0c\u8f7b\u8f7b\u63a8\u62ff\uff0c\u8fd9\u662f\u8db3\u90e8\u592a\u9633\u7ecf\u8109\u7684\u5faa\u884c\u90e8\u4f4d\uff0c\u4e0a\u884c \u8db3\u5185\u8e1d\u524d\u65b9\uff0c\u518d\u4e0a\u817f\u809a\uff0c\u6cbf\u80eb\u9aa8\u5185\u4fa7\u540e\u65b9\uff0c\u76f4\u62b5\u8179\u5185\uff0c\u5165\u5c5e\u813e\u810f\u3002\u4e91\u857e\u8db3\u8dbe\u88ab\u4ed6\u8f7b\u8f7b\u63a8\u62ff\uff0c \u6709\u4e00\u79cd\u5fae\u5fae\u75d5\u75d2\u7684\u611f\u89c9\uff0c\u8fde\u8fde\u566b\u6c14\uff0c\u8fc7\u4e86\u4e00\u9635\uff0c\u53ea\u89c9\u904d\u4f53\u8f7b\u677e\uff0c\u5fc3\u5883\u7a7a\u660e\u3002\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u884c \u4e86\uff0c\u660e\u65e5\u6211\u66ff\u4f60\u6253\u901a\u4e09\u9633\u7ecf\u8109\uff0c\u4f60\u7684\u4f24\u5c31\u5168\u597d\u4e86\uff0c\u4f60\u4eca\u65e5\u5c31\u597d\u597d\u7528\u529f\u5427\u3002\u201d\u79bb\u5f00\u4e91\u857e\u8dcc\u5750\u5730 \u4e0a\uff0c\u53c8\u4ece\u6000\u4e2d\u53d6\u51fa\u90a3\u5e45\u753b\u6765\u3002 \u3000\u3000\u53ea\u89c1\u4ed6\u62ff\u7740\u70db\u53f0\uff0c\u51dd\u795e\u7ec6\u770b\u753b\u9762\uff0c\u770b\u4e86\u8bb8\u4e45\u8bb8\u4e45\uff0c\u4f3c\u4e4e\u662f\u8981\u5728\u753b\u4e2d\u5bfb\u89c5\u4ec0\u4e48\u3002\u4e91\u857e\u505a\u4e86 \u534a\u65e5\u529f\u8bfe\uff0c\u4ed6\u4e5f\u770b\u4e86\u534a\u65e5\uff0c\u5ffd\u542c\u5f97\u5916\u9762\u53c8\u6709\u811a\u6b65\u4e4b\u58f0\uff0c\u5f20\u4e39\u67ab\u53f9\u4e86\u53e3\u6c14\uff0c\u8fd9\u624d\u628a\u753b\u5377\u8d77\uff0c \u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u6709\u4eba\u504f\u504f\u7231\u5165\u8fd9\u4e2a\u9b3c\u57df\uff1f\u201d\u6447\u9996\u793a\u610f\uff0c\u53eb\u4e91\u857e\u4e0d\u7ba1\u770b\u5230\u4ec0\u4e48\u90fd\u4e0d\u8981\u51fa\u58f0\u3002 \u3000\u3000\u5893\u95e8\u5916\u4f3c\u4e4e\u4e0d\u6b62\u4e00\u4eba\uff0c\u5728\u8fd9\u91cc\u5408\u529b\u6316\u571f\uff0c\u8fc7\u4e86\u4e00\u9635\uff0c\u53ea\u542c\u5f97\u201c\u8f70\u201d\u7684\u4e00\u58f0\uff0c\u77f3\u95e8\u5df2\u88ab\u63a8 \u5f00\uff0c\u867d\u8bf4\u6ce5\u571f\u5df2\u88ab\u6316\u677e\uff0c\u95e8\u5916\u4e4b\u4eba\uff0c\u6c14\u529b\u786e\u662f\u4e0d\u5c0f\u3002 \u3000\u3000\u95e8\u5916\u5171\u662f\u4e94\u4eba\uff0c\u624b\u6301\u706b\u628a\uff0c\u9c7c\u8d2f\u8d70\u5165\uff0c\u4e91\u857e\u4e00\u770b\uff0c\u53ea\u89c1\u90a3\u56db\u4e2a\u73e0\u5b9d\u5546\u4eba\uff0c\u4e24\u4e2a\u5728\u524d\uff0c\u4e24 \u4e2a\u5728\u540e\uff0c\u9ed1\u77f3\u5e84\u7684\u5e84\u4e3b\uff0c\u8f70\u5929\u96f7\u77f3\u82f1\u5219\u5939\u5728\u4e2d\u95f4\u3002\u4e91\u857e\u597d\u4e0d\u60ca\u614c\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u56db\u4e2a\u73e0\u5b9d\u5546 \u4eba\uff0c\u5b9a\u77e5\u5bc6\u5ba4\u6240\u5728\uff0c\u82e5\u77f3\u82f1\u53eb\u6211\u56de\u53bb\uff0c\u8fd9\u8be5\u600e\u529e\uff1f\u201d \u3000\u3000\u53ea\u542c\u5f97\u8d70\u5728\u524d\u9762\u7684\u73e0\u5b9d\u5546\u4eba\u9053\uff1a\u201c\u4ed6\u4eec\u4e8c\u4eba\u5b9a\u7136\u8fd8\u5728\u6b64\uff0c\u77f3\u8001\u5e84\u4e3b\uff0c\u4f60\u66ff\u6211\u4eec\u4f5c\u4e3b\u3002\u201d \u539f\u6765\u9ed1\u767d\u6469\u8bc3\uff0c\u4e00\u6012\u8d70\u56de\u897f\u85cf\uff0c\u5374\u9063\u8fd9\u56db\u4e2a\u4e70\u624b\uff0c\u5230\u5357\u65b9\u53bb\u7ed3\u675f\u751f\u610f\uff0c\u4ed6\u4eec\u8f93\u4e86\u53e4\u5893\u4e2d\u6240\u6709 \u7684\u5b9d\u85cf\uff0c\u5df2\u65e0\u672c\u94b1\u518d\u505a\u8fd9\u79cd\u9ed1\u9053\u504f\u95e8\u7684\u73e0\u5b9d\u751f\u610f\u4e86\u3002\u8fd9\u56db\u4e2a\u73e0\u5b9d\u5546\u4eba\u5fc3\u6709\u4e0d\u7518\uff0c\u6070\u5de7\u5728\u8def\u4e0a \u78b0\u5230\u8ffd\u8d76\u5973\u513f\u7684\u77f3\u82f1\uff0c\u4fbf\u592e\u6c42\u77f3\u82f1\u66ff\u4ed6\u4eec\u51fa\u5934\uff0c\u4ed6\u4eec\u72b9\u81ea\u4ee5\u4e3a\u5f20\u4e39\u67ab\u90a3\u665a\u5230\u77f3\u82f1\u5bb6\u4e2d\u76d7\u53d6\u5b9d \u7269\uff0c\u77f3\u82f1\u7684\u672c\u9886\u867d\u7136\u4e0d\u80fd\u8d85\u8fc7\u9ed1\u767d\u6469\u8bc3\uff0c\u4f46\u5c71\u897f\u3001\u9655\u897f\u7684\u7eff\u6797\u597d\u6c49\u5168\u90fd\u542c\u4ed6\u53f7\u4ee4\uff0c\u53ea\u8981\u6fc0\u6012 \u4e86\u77f3\u82f1\uff0c\u4f20\u4e0b\u7eff\u6797\u4ee4\u7bad\uff0c\u90a3\u4e48\u5f20\u4e39\u67ab\u672c\u4e8b\u518d\u5927\uff0c\u4e5f\u63d2\u7ffc\u96be\u98de\u3002 \u3000\u3000\u5c82\u77e5\u77f3\u82f1\u6b63\u60f3\u89c1\u5f20\u4e39\u67ab\u4e00\u9762\uff0c\u66f4\u4f55\u51b5\u4e91\u857e\u7684\u4e0b\u843d\uff0c\u4e5f\u987b\u89c1\u4e86\u5f20\u4e39\u67ab\u624d\u80fd\u5f97\u77e5\uff0c\u4fbf\u5047\u610f\u7b54 \u5141\uff0c\u53eb\u56db\u4e2a\u73e0\u5b9d\u5546\u4eba\u9886\u4ed6\u5230\u6b64\u3002 \u3000\u3000\u90a3\u56db\u4e2a\u73e0\u5b9d\u4eba\u7ed5\u7740\u5927\u5385\u884c\u4e86\u4e00\u5468\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u80c6\u6562\u5728\u592a\u5c81\u5934\u4e0a\u52a8\u571f\u7684\u597d\u5c0f\u5b50\uff0c\u6eda\u51fa \u6765\uff01\u201d\u77f3\u82f1\u6025\u5fd9\u6b62\u4f4f\uff0c\u5411\u7a7a\u4e2d\u4f5c\u4e86\u4e00\u63d6\uff0c\u9053\uff1a\u201c\u5f20\u516c\u5b50\uff0c\u8bf7\u51fa\u6765\uff0c\u8001\u592b\u6e34\u5ff5\u4e00\u89c1\uff0c\u6709\u8001\u592b\u5728 \u6b64\uff0c\u66ff\u4f60\u4eec\u89e3\u4e86\u4e24\u5bb6\u7684\u51a4\u4ec7\u5427\uff01\u201d\u56db\u4e2a\u73e0\u5b9d\u5546\u4eba\u89c1\u4ed6\u5982\u6b64\u606d\u656c\uff0c\u5927\u4e3a\u9519\u6115\uff0c\u4e3a\u9996\u7684\u6084\u6084\u7684\u5728 \u77f3\u82f1\u8033\u8fb9\u8bf4\u9053\uff1a\u201c\u77f3\u8001\u5e84\u4e3b\uff0c\u4e0d\u5fc5\u62c5\u5fc3\uff0c\u82e5\u7136\u4ed6\u4eec\u4e8c\u4eba\u90fd\u65e0\u4f24\u635f\uff0c\u53cc\u5251\u5408\u58c1\uff0c\u90a3\u6211\u4eec\u4e94\u4eba\u81ea \u7136\u4e0d\u662f\u4ed6\u4eec\u5bf9\u624b\uff0c\u53ea\u662f\u4ee4\u5a7f\u5df2\u88ab\u767d\u6469\u8bc3\u6240\u4f24\uff0c\u4ed6\u4e00\u4eba\u4e0d\u662f\u6211\u4eec\u5bf9\u624b\u3002\u54ce\uff0c\u77f3\u8001\u82f1\u96c4\uff0c\u4ee4\u5a7f\u7684 \u4f24\uff0c\u6211\u4eec\u5305\u80fd\u6cbb\u597d\uff0c\u53ea\u8981\u90a3\u767d\u9a6c\u5c0f\u5b50\u5c06\u73e0\u5b9d\u4ea4\u56de\u3002\u201d\u8fd9\u56db\u4e2a\u73e0\u5b9d\u5546\u4eba\u5148\u524d\u6015\u77f3\u82f1\u89c1\u602a\uff0c\u4e0d\u6562 \u5c06\u4e91\u857e\u53d7\u4f24\u7684\u4e8b\u8bf4\u77e5\uff0c\u6b64\u9645\u89c1\u77f3\u82f1\u90a3\u526f\u795e\u6c14\uff0c\u53c8\u4ee5\u4e3a\u4ed6\u662f\u5bb3\u6015\u654c\u624b\u592a\u5f3a\u4e0d\u6562\u4e0e\u5f20\u4e39\u67ab\u653e\u5bf9\uff0c \u6240\u4ee5\u903c\u5f97\u5c06\u771f\u76f8\u8bf4\u51fa\u3002 \u3000\u3000\u77f3\u82f1\u542c\u8bf4\u4e91\u857e\u53d7\u4f24\uff0c\u5fc3\u4e2d\u5927\u6025\uff0c\u53eb\u9053\uff1a\u201c\u5f20\u516c\u5b50\uff0c\u8bf7\u51fa\u6765\u5427\uff0c\u5c0f\u5a7f\u65e5\u524d\u65e0\u77e5\u5192\u72af\uff0c\u8bf7\u4f60 \u4e0d\u8981\u89c1\u602a\u3002\u201d\u5bc6\u5ba4\u4e2d\u5f20\u4e39\u67ab\u4ecd\u4e0d\u4f5c\u58f0\uff0c\u56db\u4e2a\u5546\u4eba\u9053\uff1a\u201c\u597d\uff0c\u4f60\u4e0d\u51fa\u6765\uff0c\u54b1\u4eec\u5c31\u8fdb\u53bb\u628a\u4f60\u63ea\u51fa \u6765\u4e86\uff01\u201d\u5728\u5730\u4e0a\u53d6\u4e86\u77f3\u6761\uff0c\u62b5\u7740\u5bc6\u5ba4\u5916\u5899\u51f9\u5904\uff0c\u7528\u529b\u8f6c\u52a8\uff0c\u5f20\u4e39\u67ab\u4e0d\u5f85\u95e8\u5f00\uff0c\u5429\u5490\u4e86\u4e91\u857e\u4e24 \u53e5\uff0c\u500f\u5730\u53d6\u5f00\u4e86\u201c\u81ea\u6765\u77f3\u201d\uff0c\u628a\u95e8\u4e00\u5f00\uff0c\u98de\u8eab\u8df3\u51fa\uff0c\u968f\u624b\u53c8\u628a\u5bc6\u5ba4\u4e4b\u95e8\u63a9\u4e0a\u3002 \u3000\u3000\u90a3\u56db\u4e2a\u73e0\u5b9d\u5546\u4eba\u6b63\u5728\u7528\u529b\u65cb\u8f6c\u77f3\u6761\uff0c\u9aa4\u7136\u5931\u4e86\u91cd\u5fc3\uff0c\u9f50\u90fd\u8dcc\u5012\uff0c\u7ad9\u8d77\u6765\u65f6\uff0c\u53ea\u89c1\u5f20\u4e39\u67ab \u8f7b\u6447\u63cf\u91d1\u6247\u5b50\uff0c\u8eab\u4e0a\u62ab\u7684\uff0c\u5c31\u662f\u90a3\u665a\u548c\u9ed1\u6469\u8bc3\u6253\u6597\u65f6\u7a7f\u7684\u90a3\u4ef6\u7ee3\u6709\u53cc\u9f99\u5728\u6d77\u4e0a\u817e\u6ce2\u4e89\u6597\u7684\u7d27 \u8eab\u9a6c\u8902\u3002\u56db\u4e2a\u73e0\u5b9d\u5546\u4eba\u614c\u5fd9\u8df3\u5230\u56db\u8fb9\u7ad9\u5b9a\uff0c\u91c7\u53d6\u4e86\u5408\u56f4\u4e4b\u52bf\uff0c\u53ea\u5f85\u4ed6\u548c\u77f3\u82f1\u4e00\u4e2a\u52a8\u624b\uff0c\u5c31\u7acb \u523b\u5c06\u4ed6\u56f4\u5728\u5793\u5fc3\u3002 \u3000\u3000\u70db\u5149\u7167\u8000\u4e0b\uff0c\u53ea\u89c1\u5f20\u4e39\u67ab\u795e\u6001\u6f47\u6d12\u81ea\u5982\uff0c\u6247\u5b50\u4e00\u6643\uff0c\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u77f3\u5e84\u4e3b\uff0c\u6570\u5341\u5e74\u6069 \u60c5\uff0c\u6211\u66ff\u5148\u4eba\u62dc\u8c22\u4e86\u3002\u201d\u77f3\u82f1\u770b\u5f97\u771f\u5207\uff0c\u5ffd\u7136\u54ed\u51fa\u58f0\u6765\uff0c\u6251\u5730\u8dea\u5012\uff0c\u5728\u5730\u4e0a\u78d5\u4e86\u56db\u4e2a\u54cd\u5934\uff0c \u9053\uff1a\u201c\u5c11\uff0c\u5c11\uff0d\uff0d\u201d\u5f20\u4e39\u67ab\u6447\u4e86\u6447\u624b\uff0c\u4f3c\u662f\u793a\u610f\u53eb\u4ed6\u4e0d\u8981\u8bf4\u51fa\u81ea\u5df1\u7684\u8eab\u4efd\u3002\u5f85\u4ed6\u78d5\u4e86\u56db\u4e2a\u54cd \u5934\uff0c\u7acb\u523b\u5c06\u4ed6\u6276\u8d77\uff0c\u8eac\u8eab\u8fd8\u4e86\u4e00\u793c\uff0c\u6001\u5ea6\u867d\u7136\u606d\u656c\uff0c\u4f46\u4e0d\u8dea\u4e0b\u8fd8\u793c\uff0c\u663e\u7136\u662f\u4e0a\u53f8\u5bf9\u4e0b\u5c5e\u7684\u793c \u4eea\u3002 \u3000\u3000\u8f70\u5929\u96f7\u77f3\u82f1\u8fd9\u4e00\u756a\u4e3e\u52a8\uff0c\u5bc6\u5ba4\u5185\u5916\uff0c\u90fd\u662f\u5403\u60ca\u975e\u5c0f\u3002\u5ba4\u5185\u7684\u4e91\u857e\uff0c\u4e00\u60ca\u4e4b\u540e\uff0c\u5374\u662f\u82b3\u5fc3 \u5927\u6170\uff0c\u5fc3\u9053\uff1a\u201c\u5927\u54e5\u679c\u7136\u4e0d\u662f\u574f\u4eba\uff0c\u770b\u77f3\u8001\u82f1\u96c4\u5bf9\u4ed6\u5982\u6b64\u5c0a\u656c\uff01\u53ea\u662f\u5927\u54e5\u672a\u514d\u592a\u65e0\u793c\u4e86\uff0c\u5e74 \u7eaa\u9752\u9752\uff0c\u5c82\u5e94\u53d7\u77f3\u8001\u82f1\u96c4\u8dea\u62dc\uff1f\u201d \u3000\u3000\u90a3\u56db\u4e2a\u73e0\u5b9d\u5546\u4eba\u5374\u662f\u8d8a\u6765\u8d8a\u60ca\uff0c\u60f3\u4e0d\u5230\u6240\u501a\u7684\u9760\u5c71\u7adf\u4e0e\u654c\u4eba\u4e00\u8def\uff0c\u4e00\u4e2a\u5f20\u4e39\u67ab\u5df2\u591f\u4ed6\u4eec \u597d\u53d7\uff0c\u66f4\u4f55\u51b5\u8fd8\u6709\u77f3\u82f1\u5e2e\u4ed6\u3002 \u3000\u3000\u53ea\u89c1\u5f20\u4e39\u67ab\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u77f3\u5e84\u4e3b\u5728\u6b64\uff0c\u4f60\u4eec\u95ee\u95ee\u4ed6\u6211\u662f\u4e0d\u662f\u8d2a\u8d22\u76d7\u5b9d\u4e4b\u4eba\uff1f\u201d\u56db \u4e2a\u73e0\u5b9d\u5546\u614c\u5fd9\u6253\u63d6\uff0c\u8fde\u58f0\u8bf4\u9053\uff1a\u201c\u4e0d\u6562\uff0c\u4e0d\u6562\uff01\u201d\u5f20\u4e39\u67ab\u53c8\u662f\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u7b49\u7740\uff0c \u9ed1\u767d\u6469\u8bc3\u90a3\u70b9\u70b9\u5bb6\u5f53\uff0c\u4ffa\u8fd8\u4e0d\u66fe\u653e\u5728\u5fc3\u4e0a\u3002\u201d\u8f7b\u8f7b\u62c9\u5f00\u5bc6\u5ba4\u77f3\u95e8\u4ec5\u5bb9\u8eab\u5b50\u901a\u8fc7\uff0c\u8d70\u4e86\u8fdb\u53bb\uff0c \u5bc6\u5ba4\u751a\u5927\uff0c\u4e91\u857e\u5750\u5728\u5899\u89d2\uff0c\u5916\u9762\u4eba\u77a7\u4e0d\u89c1\u5979\u3002 \u3000\u3000\u73e0\u5b9d\u5546\u4eba\u4e0e\u77f3\u82f1\u90fd\u4e0d\u6562\u4f38\u9888\u5f20\u671b\uff0c\u53ea\u89c1\u5f20\u4e39\u67ab\u624b\u6301\u626b\u5e1a\uff0c\u5c06\u5806\u5728\u5899\u89d2\u7684\u4e00\u5927\u5806\u53e4\u73a9\u73e0\u5b9d \u72b9\u5982\u626b\u5783\u573e\u4e00\u822c\u5730\u90fd\u626b\u4e86\u51fa\u6765\uff0c\u6602\u5934\u5927\u7b11\u9053\uff1a\u201c\u4e16\u4eba\u504f\u7231\u5b9d\uff0c\u6211\u610f\u72ec\u601c\u624d\u3002\u6765\uff0c\u6765\uff0c\u4f60\u4eec\u70b9 \u70b9\u770b\u53ef\u6709\u7f3a\u5c11\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u56db\u4e2a\u73e0\u5b9d\u5546\u4eba\u559c\u51fa\u671b\u5916\uff0c\u628a\u53e4\u73a9\u73e0\u5b9d\u4e00\u4e00\u62fe\u8d77\u653e\u80cc\u56ca\uff0c\u5f20\u4e39\u67ab\u559d\u9053\uff1a\u201c\u6eda\u5427\uff0c\u544a\u4e0e\u9ed1\u767d \u6469\u8bc3\u77e5\u9053\u53eb\u4ed6\u4eec\u597d\u597d\u5730\u505a\u751f\u610f\uff0c\u53ef\u4e0d\u8bb8\u6043\u5f3a\u4e70\u5356\u3002\u201d\u56db\u4e2a\u73e0\u5b9d\u5546\u4eba\u8fde\u9053\uff1a\u201c\u662f\uff0c\u662f\uff01\u201d\u53c8\u8ba8 \u597d\u9053\uff1a\u201c\u4ee4\u53cb\u4f24\u52bf\u5982\u4f55\uff1f\u6211\u4eec\u80fd\u6cbb\u3002\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u5c31\u53ea\u4f60\u4eec\u80fd\u6cbb\u4e48\uff1f\u6211\u65e9\u5df2\u5c06\u4ed6\u6cbb\u597d\u4e86\uff0c \u4e0d\u5fc5\u591a\u8bdd\uff0c\u5feb\u6eda\uff01\u201d\u56db\u4e2a\u73e0\u5b9d\u5546\u4eba\u53c8\u8fde\u9053\uff1a\u201c\u662f\uff0c\u662f\uff01\u201d\u4e00\u8def\u97a0\u8eac\uff0c\u8d70\u51fa\u95e8\u5916\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u5927\u7b11\u9053\uff1a\u201c\u628a\u8fd9\u4e9b\u963f\u5835\u7269\u626b\u9664\u5e72\u51c0\uff0c\u5fc3\u4e2d\u597d\u4e0d\u75db\u5feb\u4e5f\uff01\u4e0d\u4e49\u4e4b\u8d22\uff0c\u4ea6\u4e0d\u6015\u7528\uff0c\u4e0d \u8fc7\uff0c\u8981\u7528\u5f97\u5176\u5f53\uff0c\u77f3\u8001\u82f1\u96c4\uff0c\u4f60\u8bf4\u53ef\u662f\uff1f\u201d\u77f3\u82f1\u8eac\u8eab\u9053\uff1a\u201c\u5c11\u4e3b\u6559\u8bad\u7684\u662f\u3002\u201d\u5f20\u4e39\u67ab\u9053\uff1a \u201c\u597d\u5566\u3002\u4f60\u89c1\u7740\u4e86\u6211\uff0c\u4e5f\u53ef\u4ee5\u8d70\u5566\u3002\u201d\u77f3\u82f1\u9053\uff1a\u201c\u6c42\u5c11\u4e3b\u5c06\u5c0f\u5a7f\u653e\u56de\u3002\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u4f60\u5973 \u513f\u7684\u597d\u59fb\u7f18\u5305\u5728\u4e91\u857e\u548c\u6211\u7684\u8eab\u4e0a\uff0c\u4f60\u4e0d\u5fc5\u62c5\u5fc3\uff0c\u4e00\u5b9a\u7ed9\u4f60\u4e2a\u597d\u5973\u5a7f\u4fbf\u662f\uff0c\u6211\u4e0d\u60f3\u4f60\u591a\u5728\u6b64\u5730 \u803d\u7559\uff0c\u4f60\u5feb\u8d70\u5427\uff01\u201d\u8bf4\u5230\u201c\u8d70\u201d\u5b57\uff0c\u72b9\u5982\u4e0b\u547d\u4ee4\u4e00\u822c\u3002 \u3000\u3000\u77f3\u82f1\u53c8\u8eac\u8eab\u9053\uff1a\u201c\u90a3\u4e48\u5c0f\u4eba\u8d70\u4e86\uff0c\u5c11\u4e3b\u4f60\u8fd8\u6709\u4f55\u5429\u5490\uff1f\u201d\u4e91\u857e\u542c\u5f97\u751a\u4e3a\u60ca\u5f02\uff0c\u5fc3\u9053\uff1a \u201c\u77f3\u82f1\u597d\u574f\u4e5f\u662f\u664b\u3001\u9655\u4e8c\u7701\u7684\u6b66\u6797\u76df\u4e3b\uff0c\u6b66\u529f\u4e0d\u5728\u5f20\u4e39\u67ab\u4e4b\u4e0b\uff0c\u4f55\u6545\u5bf9\u4ed6\u606d\u656c\u82e5\u662f\uff0c\u5bb3\u6015\u5982 \u65af\uff1f\u4ed6\u53e3\u53e3\u58f0\u58f0\u79f0\u547c\u5c11\u4e3b\uff0c\u96be\u9053\u4ed6\u66fe\u662f\u5927\u54e5\u5bb6\u4e2d\u7684\u4e0b\u4eba\u4e48\uff1f\u201d\u53ea\u542c\u5f97\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u6ca1\u4ec0\u4e48 \u5566\uff01\u201d\u77f3\u82f1\u9053\uff1a\u201c\u5c11\u4e3b\u82e5\u6709\u6240\u9700\uff0c\u5c0f\u4eba\u4f20\u4e0b\u7eff\u6797\u7bad\uff0c\u4e24\u7701\u9ed1\u9053\u4e0a\u7684\u670b\u53cb\uff0c\u597d\u574f\u4e5f\u8981\u7ed9\u70b9\u9762 \u5b50\u3002\u201d\u5f20\u4e39\u67ab\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e16\u4e8b\u6bcf\u591a\u51fa\u4eba\u610f\u5916\uff0c\u53ea\u6050\u6709\u4e8b\u4e4b\u65f6\uff0c\u8c01\u4e5f\u5e2e\u4e0d\u4e86\u6211\uff01\u201d\u77f3\u82f1 \u9762\u8272\u4e00\u53d8\uff0c\u751a\u662f\u5c34\u5c2c\u9053\uff1a\u201c\u5c0f\u4eba\u867d\u662f\u65e0\u80fd\uff0c\u5c11\u4e3b\u5429\u5490\u4e0b\u6765\uff0c\u6211\u8d74\u6c64\u8e48\u706b\uff0c\u4ea6\u5728\u6240\u4e0d\u8f9e\u3002\u201d\u5f20 \u4e39\u67ab\u6325\u4e86\u6325\u624b\u9893\u7136\u8bf4\u9053\uff1a\u201c\u4f60\u7684\u597d\u610f\u6211\u5fc3\u9886\u4e86\uff0c\u4f60\u8d70\u5427\uff01\u201d\u77f3\u82f1\u65bd\u4e86\u4e00\u793c\uff0c\u53cd\u8eab\u8d70\u51fa\u3002 \u3000\u3000\u4e91\u857e\u5fc3\u4e2d\u52a8\u8361\u4e0d\u5b89\uff0c\u5f85\u5f20\u4e39\u67ab\u8d70\u8fdb\u5bc6\u5ba4\uff0c\u5288\u5934\u95ee\u9053\uff1a\u201c\u5927\u54e5\uff0c\u77f3\u82f1\u95ee\u4f60\u6709\u4f55\u5429\u5490\u4e4b\u65f6\uff0c \u4f60\u4f55\u4e0d\u4e58\u673a\u6c42\u4ed6\u4e00\u4e8b\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u4f55\u4e8b\uff1f\u201d\u4e91\u857e\u8bb7\u8bb7\u8bf4\u9053\uff1a\u201c\u6628\u65e5\u4e0e\u77f3\u7fe0\u51e4\u540c\u6765\u7684\u90a3\u4e2a \u5c11\u5e74\uff0c\u4e0d\u662f\u8bf4\u8d77\u4ec0\u4e48\u7eff\u6797\u7bad\u5417\uff1f\u201d\u5f20\u4e39\u67ab\u5927\u7b11\u9053\uff1a\u201c\u4f60\u662f\u8bf4\u96c1\u95e8\u5173\u5916\u7684\u90a3\u4f4d\u5468\u5c11\u5be8\u4e3b\u4e48\uff1f\u4ed6 \u4eec\u7236\u5b50\u4e5f\u8fd8\u7b97\u5f97\u662f\u4e2a\u4eba\u7269\u3002\u4ed6\u8981\u4f1a\u5408\u77f3\u82f1\u4f20\u4e0b\u7eff\u6797\u4ee4\u7bad\uff0c\u4e0d\u5229\u4e8e\u6211\uff0c\u6b64\u4e8b\u4ea6\u65e9\u5df2\u5728\u6211\u610f\u4e2d\u3002 \u6211\u751f\u6765\u4e0d\u60ef\u6c42\u4eba\uff0c\u800c\u4e14\u501f\u52bf\u529b\u538b\u670d\u4e0b\u6765\uff0c\u6211\u9762\u4e0a\u4ea6\u65e0\u5149\u5f69\u3002\u518d\u8bf4\u5b9e\u8bdd\uff0c\u6211\u82e5\u6015\u4ed6\u4eec\u4f20\u4ec0\u4e48\u7eff \u6797\u7bad\uff0c\u9002\u624d\u6211\u4e00\u51fa\u53bb\uff0c\u5c31\u53ef\u4ee5\u7ed3\u679c\u4f60\u7684\u4e49\u5144\uff0c\u6211\u504f\u8981\u8ba9\u4ed6\u4eec\u8bd5\u4e00\u8bd5\u3002\u55ef\uff0c\u77f3\u7fe0\u51e4\u914d\u7ed9\u5468\u5c71\u6c11 \u5012\u662f\u5f88\u597d\uff0c\u602a\u4e0d\u5f97\u4f60\u6d1e\u623f\u4e4b\u591c\uff0c\u8001\u662f\u63d0\u4f60\u8fd9\u4f4d\u4e49\u5144\u3002\u201d\u8bf4\u5f97\u5341\u5206\u81ea\u8d1f\u5374\u53c8\u662f\u5341\u5206\u65f7\u8fbe\u3002\u4e91\u857e \u60f3\u9053\uff1a\u201c\u539f\u6765\u4ed6\u65e9\u5df2\u77e5\u9053\u4e86\u5468\u5c71\u6c11\u7684\u8eab\u4efd\uff0c\u5468\u5c71\u6c11\u9a82\u4ed6\u4e4b\u65f6\uff0c\u4e5f\u4e8f\u4ed6\u5fcd\u5f97\u4f4f\u3002\u201d\u5fc3\u4e2d\u6697\u6697\u62c5 \u5fe7\uff0c\u5374\u53c8\u4e0d\u77e5\u9053\u4ed6\u4e0e\u5468\u5065\u4e4b\u95f4\uff0c\u6709\u8fc7\u4ec0\u4e48\u8bef\u4f1a\u3002\u5f20\u4e39\u67ab\u5411\u5979\u77a7\u4e86\u4e00\u773c\uff0c\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u4f60\u6c14\u8272 \u66f4\u597d\u4e86\uff0c\u8fd8\u662f\u4e13\u5fc3\u7528\u529f\u3002\u5f85\u665a\u996d\u4e4b\u65f6\uff0c\u6211\u518d\u7ed9\u4f60\u8bf4\u7b2c\u4e8c\u4e2a\u6545\u4e8b\u3002\u201d \u3000\u3000\u4e91\u857e\u5185\u529f\u751a\u6709\u6839\u5e95\uff0c\u5230\u4e86\u665a\u996d\u4e4b\u65f6\uff0c\u75c5\u52bf\u5df2\u53bb\u4e86\u4e03\u516b\uff0c\u53ef\u4ee5\u8fdb\u5e72\u996d\u4e86\u3002\u5f20\u4e39\u67ab\u4e00\u8fb9\u670d\u4f8d \u5979\u98df\u996d\uff0c\u4e00\u8fb9\u8bf4\u9053\uff1a\u201c\u5f88\u4e45\u5f88\u4e45\u4ee5\u524d\uff0c\u6709\u4e00\u4e2a\u56fd\u5bb6\uff0c\u56fd\u4e2d\u6709\u4e00\u4e2a\u5927\u5fe0\u81e3\uff0c\u59d3\u751a\u540d\u8c01\u4e0d\u5fc5\u63d0 \u5566\uff0c\u53cd\u6b63\u4efb\u4f55\u671d\u4ee3\uff0c\u90fd\u6709\u8fd9\u6837\u7684\u5fe0\u81e3\uff0c\u4e5f\u8bb8\u59d3\u5f20\uff0c\u4e5f\u8bb8\u59d3\u674e\uff0c\u4e5f\u8bb8\u59d3\u738b\uff0c\u4e5f\u8bb8\u59d3\u4e91\u2026\u2026\u201d \u3000\u3000\u201c\u53e6\u5916\u6709\u4e00\u4e2a\u56fd\u5bb6\u4e0e\u8fd9\u4e2a\u56fd\u5bb6\u76f8\u90bb\uff0c\u4e24\u4e2a\u56fd\u5bb6\u65f6\u5e38\u6253\u4ed7\uff0c\u6709\u65f6\uff0c\u662f\u90a3\u4e00\u4e2a\u56fd\u5bb6\u4fb5\u5165\u4e86\u8fd9 \u4e00\u4e2a\u56fd\u5bb6\uff0c\u6709\u65f6\u53c8\u662f\u8fd9\u4e00\u4e2a\u56fd\u5bb6\u4fb5\u5165\u4e86\u90a3\u4e00\u4e2a\u56fd\u5bb6\uff0c\u4f46\u4e0d\u8bba\u54ea\u4e00\u4e2a\u56fd\u5bb6\u5f97\u80dc\uff0c\u53d7\u82e6\u7684\u90fd\u662f\u8001 \u767e\u59d3\u3002\u201d \u3000\u3000\u201c\u6545\u4e8b\u53d1\u751f\u7684\u65f6\u5019\uff0c\u662f\u5927\u5fe0\u81e3\u90a3\u4e2a\u56fd\u5bb6\u5f97\u52bf\uff0c\u8981\u90a3\u4e2a\u76f8\u90bb\u7684\u56fd\u5bb6\u5e74\u5e74\u8fdb\u8d21\uff0c\u5c81\u5c81\u6765\u671d\u3002 \u90a3\u4e00\u4e2a\u56fd\u5bb6\u4e0d\u670d\uff0c\u4fbf\u793c\u8d24\u4e0b\u58eb\uff0c\u62db\u63fd\u4eba\u624d\uff0c\u6e10\u6e10\u56fd\u52bf\u4e5f\u5f3a\u8d77\u6765\u4e86\u3002\u5927\u5fe0\u81e3\u90a3\u4e2a\u56fd\u5bb6\u4e00\u770b\u4e0d \u5bf9\uff0c\u5c31\u6d3e\u9063\u5927\u5fe0\u81e3\u505a\u4f7f\u81e3\uff0c\u51fa\u4f7f\u90a3\u4e2a\u56fd\u5bb6\uff0c\u4e00\u9762\u65bd\u884c\u7b3c\u7edc\u7684\u624b\u6bb5\uff0c\u4e00\u9762\u6697\u4e2d\u6253\u542c\u865a\u5b9e\u3002\u4e0d\u6599 \u8fd9\u5927\u5fe0\u81e3\u4e00\u53bb\u5c31\u53bb\u4e86\u4e8c\u5341\u5e74\u3002\u5582\uff0c\u5c0f\u5144\u5f1f\uff0c\u4f60\u600e\u4e48\u5566\uff1f\u4f60\u9053\u4ed6\u600e\u4e48\u4e00\u53bb\u5c31\u53bb\u4e86\u4e8c\u5341\u5e74\uff1f\u539f\u6765 \u662f\u2026\u2026\u5582\uff0c\u857e\u5f1f\uff0c\u857e\u5f1f\uff01\u201d\u5f20\u4e39\u67ab\u4e00\u8def\u8bf4\uff0c\u4e00\u8def\u89c1\u4e91\u857e\u7684\u9762\u8272\u6e10\u6e10\u4e0d\u5bf9\uff0c\u8bf4\u5230\u201c\u4e8c\u5341\u5e74\u201d\u4e4b \u65f6\uff0c\u53ea\u89c1\u4e91\u857e\u9762\u8272\u60e8\u767d\uff0c\u6447\u6447\u6b32\u5012\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u60ca\u5f02\u4e4b\u6781\uff0c\u6025\u5fd9\u4f38\u624b\u6276\u5979\uff0c\u53ea\u542c\u5f97\u4e91\u857e\u63a5\u7740\u4ed6\u7684\u6545\u4e8b\u9053\uff1a\u201c\u4f60\u9053\u4ed6\u600e\u4e48\u4e00\u53bb\u5c31\u53bb \u4e86\u4e8c\u5341\u5e74\uff1f\u539f\u6765\u662f\u7ed9\u4eba\u6263\u7559\u4e86\u5728\u51b0\u5929\u96ea\u5730\u91cc\u7267\u9a6c\uff01\u5927\u54e5\uff0c\u4f60\u4e0d\u8981\u8bf4\u5566\uff0c\u8fd9\u4e2a\u6545\u4e8b\u6211\u4e0d\u8981 \u542c\uff01\u201d \u3000\u3000\u5f20\u4e39\u67ab\u7684\u9762\u8272\u4e5f\u4e00\u4e0b\u5b50\u53d8\u5f97\u82cd\u767d\uff0c\u53cc\u7709\u6df1\u9501\uff0c\u4f3c\u662f\u4e45\u5df2\u7591\u8651\u7684\u4e8b\u60c5\u5ffd\u7136\u5f97\u5230\u4e86\u8bc1\u5b9e\uff0c\u4ed6 \u4f3c\u7a81\u7136\u4ece\u4e00\u4e2a\u6076\u68a6\u4e2d\u60ca\u9192\u8fc7\u6765\uff0c\u6df1\u6c89\u5730\u770b\u4e86\u4e91\u857e\u4e00\u773c\uff0c\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u539f\u6765\u8fd9\u4e2a\u6545\u4e8b\u4f60\u65e9\u77e5 \u9053\u5566\uff01\u90a3\u4e48\u6211\u660e\u665a\u518d\u8bf4\u7b2c\u4e09\u4e2a\u6545\u4e8b\uff0c\u4f60\u5c31\u4ec0\u4e48\u90fd\u660e\u767d\u5566\u3002\u5c0f\u5144\u5f1f\u4f60\u5b9a\u4e00\u5b9a\u795e\uff0c\u73b0\u5728\u4ec0\u4e48\u4e5f\u4e0d \u8981\u95ee\uff0c\u4ec0\u4e48\u4e5f\u4e0d\u8981\u8bf4\uff0c\u4f60\u8fd8\u6709\u4e09\u9634\u8109\u7edc\u987b\u8981\u6253\u901a\uff0c\u4e0d\u53ef\u52a8\u5ff5\u52b3\u795e\uff0c\u529f\u4e8f\u4e00\u7bd1\uff0c\u5c0f\u5144\u5f1f\uff0c\u6211\u52a9 \u4f60\u7528\u529f\u3002\u201d\u53cc\u638c\u62b5\u4f4f\u4e91\u857e\u7684\u638c\u5fc3\uff0c\u53ea\u89c9\u5979\u7684\u638c\u5fc3\u706b\u70ed\uff0c\u76ee\u5149\u5982\u9189\uff0c\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u4f60 \u5fc3\u91cc\u70e6\u95f7\uff0c\u90a3\u5c31\u6682\u65f6\u4e0d\u8981\u505a\u5410\u7eb3\u529f\u592b\u5566\u3002\u201d\u79fb\u5f00\u624b\u638c\uff0c\u5728\u5ba4\u4e2d\u8d70\u6765\u8d70\u53bb\uff0c\u4e0d\u4f4f\u5728\u7ed5\u7740\u5708\u5b50\uff0c \u987b\u77e5\u4e91\u857e\u7684\u8fd0\u6c14\u7597\u4f24\u6b63\u5230\u4e86\u6700\u7d27\u8981\u7684\u5173\u5934\uff0c\u82e5\u7136\u65e0\u6cd5\u4f7f\u5979\u5fc3\u60c5\u5e73\u9759\uff0c\u90a3\u4e48\u75c5\u52bf\u53c8\u8981\u4e25\u91cd\u8d77 \u6765\u3002 \u3000\u3000\u4e91\u857e\u89c1\u4ed6\u7ed5\u5ba4\u5f77\u5fa8\uff0c\u5fc3\u77e5\u4ed6\u6b63\u4e3a\u81ea\u5df1\u5fe7\u8651\uff0c\u60f3\u95ee\u4ed6\u7684\u8bb8\u591a\u7591\u95ee\uff0c\u90fd\u538b\u4e0b\u6765\u4e0d\u95ee\uff0c\u4e3e\u624b\u8f7b \u63a0\u4e91\u9b13\uff0c\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4f60\u65e9\u4e9b\u7761\u5427\uff0c\u6211\u8010\u5fc3\u7b49\u4f60\u660e\u5929\u7ed9\u6211\u8bf4\u6545\u4e8b\u3002\u201d\u5fc3\u60c5\u663e\u5df2\u5e73\u9759\u8bb8 \u591a\u4e86\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u5fae\u5fae\u4e00\u7b11\uff0c\u5728\u7389\u51e0\u4e0a\u6361\u8d77\u4e00\u628a\u80e1\u7434\uff0c\u6821\u597d\u5f26\u7d22\uff0c\u8fb9\u5f39\u8fb9\u5531\u9053\uff1a \u3000\u3000\u201c\u4e1c\u5357\u5f62\u80dc\uff0c\u6c5f\u5434\u90fd\u4f1a\uff0c\u94b1\u5858\u81ea\u53e4\u7e41\u534e\u3002\u70df\u67f3\u753b\u6865\uff0c\u98ce\u5e18\u7fe0\u5e55\uff0c\u53c2\u5dee\u5341\u4e07\u4eba\u5bb6\u3002\u4e91\u6811\u7ed5 \u5824\u6c99\uff0c\u6012\u6d9b\u5377\u971c\u96ea\uff0c\u5929\u5811\u65e0\u6daf\u3002\u5e02\u5217\u73e0\u7391\uff0c\u6237\u76c8\u7f57\u7eee\uff0c\u7ade\u8c6a\u5962\u3002\u91cd\u6e56\u53e0\u5d82\u6e05\u5609\u3002\u6709\u4e09\u79cb\u6842 \u5b50\uff0c\u5341\u91cc\u8377\u82b1\u3002\u7f8c\u7ba1\u5f04\u6674\uff0c\u83f1\u6b4c\u6cdb\u591c\uff0c\u5b09\u5b09\u9493\u53df\u83b2\u5a03\u3002\u5343\u9a91\u62e5\u9ad8\u7259\u4e58\u9189\u542c\u8427\u9f13\uff0c\u5464\u8d4f\u70df\u971e\uff0c \u5f02\u65e5\u56fe\u5c06\u597d\u666f\uff0c\u5f52\u53bb\u51e4\u6c60\u5938\u3002\u201d \u3000\u3000\u8fd9\u662f\u5b8b\u4ee3\u5927\u8bcd\u4eba\u67f3\u6c38\u548f\u53f9\u676d\u5dde\u98ce\u8c8c\u7684\u540d\u8bcd\uff0c\u5f39\u594f\u8d77\u6765\uff0c\u5982\u89c1\u8377\u8273\u6842\u9999\uff0c\u5986\u70b9\u6e56\u5c71\u6e05\u4e3d\uff1b \u5982\u542c\u83ba\u58f0\u71d5\u8bed\uff0c\u5531\u51fa\u6625\u65e5\u98ce\u5149\u3002\u4e00\u6d3e\u6b22\u4e50\u7684\u60c5\u8c03\uff0c\u4f3c\u6625\u98ce\u5439\u62c2\uff0c\u626b\u53bb\u4e86\u5fc3\u4e0a\u7684\u9634\u973e\uff0c\u4e91\u857e\u6e10 \u6e10\u5fd8\u8bb0\u5fe7\u6101\u3002\u53ea\u89c1\u5f20\u4e39\u67ab\u653e\u4e0b\u80e1\u7434\uff0c\u8d70\u8fd1\u524d\u6765\uff0c\u629a\u7740\u5979\u7684\u5934\u53d1\uff0c\u8f7b\u8f7b\u8bf4\u9053\uff1a\u201c\u7761\u5427\uff0c\u7761 \u5427\uff01\u201d\u4e91\u857e\u5982\u53d7\u50ac\u7720\uff0c\u679c\u7136\u4e0d\u4e45\u5c31\u6c89\u6c89\u7761\u53bb\u3002 \u3000\u3000\u7b2c\u4e8c\u65e5\u4e00\u65e9\u9192\u6765\uff0c\u56e0\u7761\u7720\u5f97\u597d\uff0c\u7cbe\u795e\u751a\u89c1\u9971\u6ee1\uff0c\u5f20\u4e39\u67ab\u7b11\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u4f60\u4eca\u65e5\u518d\u9759\u5750 \u4e00\u65e5\uff0c\u90a3\u5c31\u5b8c\u5168\u597d\u4e86\uff0c\u529f\u529b\u4e0d\u4f46\u4e0d\u4f1a\u51cf\u9000\uff0c\u800c\u4e14\u8fd8\u8981\u80dc\u4e8e\u4ece\u524d\u3002\u201d\u6bcf\u9694\u4e00\u4e2a\u65f6\u8fb0\uff0c\u52a9\u5979\u884c\u529f \u4e00\u6b21\uff0c\u8fc7\u4e86\u6b63\u5348\uff0c\u5df2\u63a5\u8fde\u628a\u5979\u7684\u201c\u592a\u9633\u201d\u201c\u5c11\u9634\u201d\u201c\u53a5\u9634\u201d\u4e09\u9634\u7ecf\u8109\u6253\u901a\u3002\u4e91\u857e\u9762\u8272\u6e10\u8f6c\u7ea2 \u6da6\uff0c\u5f20\u4e39\u67ab\u559c\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u4f60\u7684\u8fdb\u5883\u771f\u5feb\uff0c\u518d\u8fc7\u4e24\u4e2a\u65f6\u8fb0\uff0c\u5c31\u5b8c\u5168\u597d\u4e86\u3002\u201d \u3000\u3000\u4e91\u857e\u9759\u5750\u7528\u529f\uff0c\u5f20\u4e39\u67ab\u53c8\u72ec\u81ea\u5750\u5728\u4e00\u65c1\u770b\u753b\uff0c\u8fc7\u4e86\u534a\u8fc7\u65f6\u8fb0\uff0c\u5ffd\u542c\u5f97\u95e8\u5916\u53c8\u6709\u4eba\u58f0\uff0c\u5f20 \u4e39\u67ab\u76b1\u7709\u8bf4\u9053\uff1a\u201c\u600e\u4e48\u53c8\u6709\u4eba\u6765\u9a9a\u6270\uff01\u201d\u8bdd\u58f0\u672a\u4e86\uff0c\u53ea\u542c\u5f97\u90a3\u5339\u7167\u591c\u72ee\u5b50\u9a6c\u4e00\u58f0\u957f\u5636\uff0c\u63a5\u7740 \u662f\u201c\u8f70\u201d\u7684\u4e00\u58f0\u5de8\u54cd\uff0c\u77f3\u95e8\u98de\u5f00\uff0c\u5c18\u6c99\u6eda\u6eda\u4e4b\u4e2d\uff0c\u4e00\u5339\u767d\u9a6c\u9a6e\u7740\u4e00\u4e2a\u9ed1\u8863\u9a91\u58eb\u98de\u5954\u5165\u6765\uff0c\u58f0 \u70ed\u6781\u662f\u9a87\u4eba\uff01 \u3000\u3000\u5893\u95e8\u5916\u7684\u6ce5\u571f\u6628\u65e5\u867d\u662f\u5df2\u88ab\u6316\u677e\uff0c\u4f46\u4ee5\u4e00\u4eba\u4e4b\u529b\uff0c\u5373\u80fd\u7834\u95e8\u800c\u5165\uff0c\u8fd9\u4eba\u7684\u6b66\u529f\uff0c\u4ea6\u5df2\u5b9e \u662f\u8db3\u4ee5\u9a87\u4eba\u3002\u66f4\u4ee4\u4eba\u60ca\u5947\u7684\u662f\uff1a\u90a3\u5339\u7167\u591c\u72ee\u5b50\u9a6c\u4f55\u7b49\u795e\u9a8f\uff0c\u9664\u4e86\u4e3b\u4eba\u4e4b\u5916\uff0c\u8c01\u90fd\u662f\u4e0d\u80af\u542c \u4ece\uff0c\u7adf\u53c8\u5c45\u7136\u7ed9\u90a3\u4eba\u5236\u670d\u3002\u5bc6\u5ba4\u4e4b\u4e2d\uff0c\u5f20\u3001\u4e91\u4e8c\u4eba\u5168\u90fd\u53d8\u4e86\u9762\u8272\u3002\u53ea\u89c1\u90a3\u767d\u9a6c\u4e00\u58f0\u957f\u5578\uff0c\u5954 \u8fc7\u901a\u9053\uff0c\u8dc3\u4e0a\u5927\u5385\uff0c\u9ed1\u8863\u9a91\u58eb\u8df3\u4e0b\u9a6c\u6765\u6765\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u4e39\u67ab\uff0c\u4e39\u67ab\uff01\u201d\u955c\u4e2d\u73b0\u5f71\uff0c\u8fd9\u9ed1\u8863 \u9a91\u58eb\u7adf\u7136\u4e0d\u662f\u522b\u4eba\uff0c\u800c\u662f\u74e6\u523a\u56fd\u7684\u7b2c\u4e00\u52c7\u5c06\uff0d\uff0d\u6fb9\u53f0\u706d\u660e\u3002\u4e91\u857e\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u4e00\u58f0\u5c16 \u53eb\uff0c\u4f7f\u6b32\u8dc3\u8d77\uff0c\u5ffd\u89c9\u8170\u80c1\u4e00\u9ebb\u52a8\u5f39\u4e0d\u5f97\uff0c\u539f\u6765\u5df2\u88ab\u5f20\u4e39\u67ab\u5728\u8033\u8fb9\u8bf4\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u4e0d\u53ef\u5984\u52a8 \u597d\u597d\u7528\u529f\u3002\u6211\u53bb\u53bb\u5c31\u6765\uff0c\u4f60\u7b49\u7740\u6211\u66ff\u4f60\u8bf4\u7b2c\u4e09\u4e2a\u6545\u4e8b\u3002\u201d \u3000\u3000\u5916\u9762\u6fb9\u53f0\u706d\u660e\u53c8\u53eb\u9053\uff1a\u201c\u4e39\u67ab\uff0c\u4f60\u548c\u8c01\u5728\u91cc\u9762\uff1f\u201d\u70b9\u8d77\u725b\u6cb9\u5de8\u70db\uff0c\u4e91\u857e\u867d\u7136\u53e3\u4e0d\u80fd\u8a00\uff0c \u773c\u775b\u5374\u8fd8\u80fd\u6e05\u6e05\u695a\u695a\u5730\u77a7\u89c1\uff0c\u90a3\u5339\u767d\u9a6c\u6b63\u6328\u5728\u6fb9\u53f0\u706d\u660e\u7684\u8eab\u8fb9\uff0c\u4f3c\u662f\u548c\u4ed6\u751a\u662f\u25a1\u719f\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u5f00\u4e86\u5ba4\u95e8\uff0c\u4e00\u8dc3\u800c\u51fa\uff0c\u201c\u5618\u201d\u4e86\u4e00\u58f0\uff0c\u53ea\u542c\u5f97\u6fb9\u53f0\u706d\u660e\u8bf4\u9053\uff1a\u201c\u4e39\u67ab\uff0c\u76f8\u7237\uff0d \uff0d\u201d\u5f20\u4e39\u67ab\u53c8\u201c\u5618\u201d\u4e86\u4e00\u58f0\uff0c\u6fb9\u53f0\u706d\u660e\u6539\u53e3\u8bf4\u9053\uff1a\u201c\u4f60\u7239\u53eb\u4f60\u56de\u53bb\uff01\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u6fb9\u53f0\u5c06 \u519b\uff0c\u70e6\u4f60\u56de\u590d\u4ed6\u8001\u4eba\u5bb6\uff0c\u6211\u65e2\u79bb\u8499\u53e4\uff0c\u6b64\u751f\u6c38\u662f\u4e2d\u56fd\u4e4b\u4eba\uff0c\u4e0d\u56de\u53bb\u4e86\uff01\u201d\u6fb9\u53f0\u706d\u660e\u9053\uff1a\u201c\u4f60 \u4e0d\u4e3a\u4f60\u7239\u7740\u60f3\uff0c\u4e5f\u8981\u4e3a\u4f60\u81ea\u5df1\u7740\u60f3\u3002\u4f60\u5355\u9a91\u5165\u5173\uff0c\u4e2d\u539f\u8c6a\u6770\uff0c\u8c01\u80fd\u77e5\u4f60\u4e4b\u5fc3\uff0c\u8c01\u80fd\u8c05\u4f60\uff1f\u201d \u5f20\u4e39\u67ab\u6c89\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u7eb5\u788e\u5c38\u4e07\u6bb5\uff0c\u4e5f\u7ec8\u662f\u846c\u8eab\u6545\u571f\uff0c\u80dc\u4e8e\u57cb\u9aa8\u5f02\u57df\u9057\u81ed\u4ed6\u90a6\u3002\u70e6\u4f60\u4e0a\u590d\u4ed6 \u8001\u4eba\u5bb6\uff0c\u53eb\u4ed6\u597d\u81ea\u73cd\u91cd\u3002\u201d \u3000\u3000\u4e91\u857e\u60ca\u7591\u4e0d\u5b9a\uff0c\u731b\u5730\u60f3\u9053\uff1a\u201c\u4ed6\u82e5\u662f\u8499\u53e4\u5730\u65b9\u7684\u6c49\u65cf\u5fd7\u58eb\u6fb9\u53f0\u706d\u660e\u5c82\u4f1a\u5bf9\u4ed6\u5982\u6b64\u4eb2\u70ed\uff1f \u76f8\u7237\uff0c\u76f8\u7237\uff1f\u96be\u9053\u4ed6\u662f\uff0d\uff0d\u201d\u5ffd\u542c\u5f97\u6fb9\u53f0\u706d\u660e\u66b4\u559d\u4e00\u58f0\uff0c\u4e91\u857e\u601d\u8def\u987f\u88ab\u6253\u65ad\uff0c\u53ea\u89c1\u6fb9\u53f0\u706d\u660e \u5288\u9762\u5c31\u662f\u4e00\u62f3\uff0c\u559d\u9053\uff1a\u201c\u4f60\u5f53\u771f\u4e0d\u613f\u610f\u968f\u6211\u56de\u53bb\u4e48\uff1f\u201d\u5f20\u4e39\u67ab\u8fde\u8ba9\u4e24\u62f3\uff0c\u51c4\u7136\u8bf4\u9053\uff1a\u201c\u6fb9\u53f0 \u5c06\u519b\uff0c\u4f60\u4f55\u5fc5\u82e6\u82e6\u903c\u6211\uff01\u201d\u6fb9\u53f0\u706d\u660e\u51fa\u624b\u53c8\u662f\u4e00\u62f3\uff0c\u6a2a\u51fb\u524d\u5fc3\uff0c\u5f20\u4e39\u67ab\u62ac\u81c2\u4e00\u9694\uff0c\u6fb9\u53f0\u706d\u660e \u51fa\u624b\u5982\u98ce\uff0c\u5316\u62f3\u4e3a\u638c\uff0c\u5411\u4ed6\u9888\u8116\u4e00\u62b9\uff0c\u7adf\u662f\u8fde\u4e0b\u6740\u624b\uff01 \u3000\u3000\u4e91\u857e\u6b64\u9645\uff0c\u5fc3\u4e71\u5982\u9ebb\uff0c\u53c8\u60ca\u53c8\u559c\u53c8\u7591\uff0c\u60ca\u8005\u662f\u6fb9\u53f0\u706d\u660e\u731b\u5982\u6012\u72ee\uff0c\u6bd4\u90a3\u9ed1\u767d\u6469\u8bc3\u66f4\u4e3a\u5389 \u5bb3\uff1b\u559c\u8005\u662f\u5f20\u4e39\u67ab\u51fa\u624b\u76f8\u6297\uff0c\u663e\u89c1\u4e0d\u662f\u6fb9\u53f0\u706d\u660e\u4e00\u8def\u4e4b\u4eba\uff1b\u7591\u8005\u662f\u90a3\u201c\u76f8\u7237\u201d\u4e8c\u5b57\u597d\u50cf\u4e00\u628a \u5c16\u5200\uff0c\u63d2\u5165\u5979\u5fc3\u7a9d\uff0c\u4ee4\u5979\u5bf9\u5f20\u4e39\u67ab\u7684\u8eab\u4efd\uff0c\u66f4\u66fe\u7591\u8651\u3002 \u3000\u3000\u53ea\u89c1\u5f20\u4e39\u67ab\u594b\u529b\u62b5\u6321\uff0c\u4eba\u5f71\u7eb5\u6a2a\uff0c\u62f3\u98ce\u864e\u864e\u9707\u52a8\u5899\u58c1\uff0c\u6fb9\u53f0\u706d\u660e\u6377\u6b65\u4f3c\u733f\u7334\uff0c\u51fa\u62f3\u5982\u731b \u864e\uff0c\u529b\u96c4\u52bf\u52b2\uff0c\u53d8\u5316\u65e0\u65b9\uff0c\u628a\u5f20\u51e1\u67ab\u903c\u5f97\u6b65\u6b65\u540e\u9000\u3002\u4e91\u857e\u6068\u4e0d\u5f97\u8dc3\u8d77\u8eab\u6765\uff0c\u52a9\u4ed6\u4e00\u81c2\uff0c\u4e5f\u4e0d \u7ba1\u6709\u5426\u6548\u679c\u6025\u5fd9\u52a8\u6c14\u51b2\u5173\uff0c\u5e0c\u671b\u80fd\u591f\u81ea\u89e3\u7a74\u9053\u3002\u6b63\u5728\u7126\u6025\u5f02\u5e38\uff0c\u9a87\u76ee\u60ca\u5fc3\u4e4b\u9645\uff0c\u5ffd\u89c1\u6fb9\u53f0\u706d \u660e\u4f38\u81c2\u4e00\u6293\uff0c\u559d\u58f0\uff1a\u201c\u53bb\uff01\u201d\u628a\u5f20\u4e39\u67ab\u4e00\u628a\u6293\u8d77\uff0c\u817e\u7a7a\u6454\u51fa\uff0c\u5982\u629b\u7ee3\u7403\uff01 \u3000\u3000\u5bc6\u5ba4\u4e2d\u4e91\u857e\u5413\u5f97\u95ed\u4e86\u773c\u775b\uff0c\u5ffd\u542c\u5f97\u201c\u54a6\u201d\u7684\u4e00\u58f0\uff0c\u5f20\u5f00\u773c\u65f6\uff0c\u53ea\u89c1\u5f20\u4e39\u67ab\u597d\u7aef\u7aef\u7684\u7ad9\u5728 \u5730\u4e0a\uff0c\u7adf\u4f3c\u6beb\u65e0\u4f24\u635f\u3002\u539f\u6765\u6fb9\u53f0\u706d\u660e\u90a3\u4e00\u6454\uff0c\u770b\u4f3c\u51f6\u731b\uff0c\u5b9e\u662f\u6697\u4f7f\u5de7\u52b2\uff0c\u628a\u5f20\u4e39\u67ab\u6454\u5230\u534a \u7a7a\uff0c\u7ffb\u4e86\u4e00\u4e2a\u7b4b\u6597\uff0c\u6070\u6070\u5934\u4e0a\u811a\u4e0b\uff0c\u5e73\u5e73\u5b89\u5b89\u5730\u843d\u5728\u5730\u4e0a\uff0c\u8fd9\u4e00\u7740\u4e0d\u4f46\u4e91\u857e\u731c\u60f3\u4e0d\u5230\uff0c\u4e5f\u5927 \u51fa\u5f20\u4e39\u67ab\u7684\u610f\u6599\u4e4b\u5916\u3002 \u3000\u3000\u53ea\u89c1\u6fb9\u53f0\u706d\u660e\u8fc8\u524d\u4e24\u6b65\uff0c\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u4e39\u67ab\uff0c\u4e0d\u6789\u4f60\u5e08\u7236\u82e6\u5fc3\u6559\u5bfc\uff0c\u4f60\u7684\u6b66\u529f\u679c\u7136\u6709\u72ec \u5230\u4e4b\u5904\uff0c\u5c45\u7136\u80fd\u63a5\u6211\u4e94\u5341\u591a\u62db\uff0c\u53ef\u4ee5\u72ec\u95ef\u6c5f\u6e56\u4e86\u3002\u4f60\u597d\u81ea\u4e3a\u4e4b\uff0c\u81ea\u5df1\u5c0f\u5fc3\u5427\u3002\u5728\u4f60\u7239\u9762\u524d\uff0c \u6709\u6211\u66ff\u4f60\u8bf4\u8bdd\uff0c\u4f60\u4e0d\u5fc5\u6302\u5fc3\u3002\u201d\u5f20\u4e39\u67ab\u8fd9\u624d\u77e5\u9053\u6fb9\u53f0\u706d\u660e\u5b9e\u662f\u5bf9\u4ed6\u4e00\u756a\u597d\u610f\uff0c\u521a\u624d\u6240\u4e3a\uff0c\u4e0d \u8fc7\u4e43\u662f\u8bd5\u62db\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u4e00\u63d6\u5230\u5730\uff0c\u9053\uff1a\u201c\u6fb9\u53f0\u5c06\u519b\uff0c\u4e00\u5207\u62dc\u6258\u4f60\u4e86\u3002\u201d\u6fb9\u53f0\u706d\u660e\u5ffd\u800c\u95ee\u9053\uff1a\u201c\u5ba4\u4e2d\u8fd8\u6709 \u4f55\u4eba\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u662f\u4e00\u4f4d\u670b\u53cb\uff0c\u4ed6\u4e0d\u613f\u4e0e\u4f60\u76f8\u89c1\uff0c\u6c42\u4f60\u770b\u5728\u6211\u7684\u9762\u4e0a\uff0c\u4e0d\u8981\u60ca\u52a8\u4e8e \u4ed6\u3002\u201d\u6fb9\u53f0\u706d\u660e\u9053\uff1a\u201c\u65e2\u4e0d\u6b32\u89c1\uff0c\u4e0d\u5fc5\u52c9\u5f3a\uff0c\u592a\u5e08\u4e4b\u610f\uff0c\u5341\u6708\uff0d\uff0d\u201d\u5f20\u4e39\u67ab\u53c8\u201c\u5618\u201d\u4e86\u4e00 \u58f0\uff0c\u6fb9\u53f0\u706d\u660e\u987f\u65f6\u7f29\u53e3\uff0c\u7b11\u9053\uff1a\u201c\u54b1\u4eec\u4e5f\u4e0d\u77e5\u65e5\u540e\u80fd\u5426\u76f8\u4f1a\uff0c\u4f60\u4e0e\u6211\u51fa\u53bb\u8c08\u4e00\u4f1a\u513f\u3002\u201d\u4e0d\u7531 \u5206\u8bf4\uff0c\u5c06\u5f20\u4e39\u67ab\u62b1\u4e0a\u9a6c\u80cc\uff0c\u75be\u9a70\u51fa\u95e8\u3002 \u3000\u3000\u4e91\u857e\u5618\u4e86\u53e3\u6c14\uff0c\u987f\u53c8\u89c9\u5f97\u5982\u6709\u5343\u65a4\u5927\u77f3\uff0c\u538b\u5728\u5fc3\u5934\uff0c\u6025\u5fd9\u51dd\u795e\u9759\u601d\uff0c\u518d\u884c\u8fd0\u6c14\u51b2\u5f00\u3002\u9ad8 \u624b\u70b9\u7a74\uff0c\u5404\u6709\u5404\u7684\u72ec\u95e8\u624b\u6cd5\uff0c\u672c\u4e0d\u6613\u81ea\u884c\u89e3\u5f00\uff0c\u4e91\u857e\u8bd5\u7528\u672c\u95e8\u5fc3\u6cd5\uff0c\u8fd0\u6c14\u4e09\u8f6c\uff0c\u7adf\u7136\u594f\u6548\uff0c \u4e5f\u662f\u9887\u51fa\u610f\u5916\u3002 \u3000\u3000\u4e91\u857e\u6025\u4e0d\u53ca\u5f85\uff0c\u4e00\u8dc3\u800c\u8d77\uff0c\u5fc3\u9053\uff1a\u201c\u5f85\u6211\u81ea\u884c\u63ed\u7834\u4f60\u7684\u8eab\u4e16\u4e4b\u8c1c\u3002\u201d\u6e38\u76ee\u56db\u987e\uff0c\u89c1\u5f20\u4e39 \u67ab\u90a3\u628a\u5b9d\u5251\u5c1a\u7559\u5728\u5ba4\u4e2d\uff0c\u62ff\u8d77\u4e00\u770b\uff0c\u53ea\u89c1\u5251\u67c4\u523b\u6709\u201c\u767d\u4e91\u201d\u4e8c\u5b57\u3002\u9752\u51a5\u3001\u767d\u4e91\u4e43\u662f\u7384\u673a\u9038\u58eb \u6240\u70bc\u7684\u5251\uff0c\u4e00\u4f20\u8c22\u5929\u534e\uff0c\u4e00\u4f20\u53f6\u76c8\u76c8\uff0c\u4e91\u857e\u4e00\u89c1\uff0c\u5fc3\u5934\u53c8\u662f\u201c\u535c\u901a\u201d\u4e00\u8df3\uff0c\u60f3\u9053\uff1a\u201c\u8fd9\u628a\u5251 \u4ed6\u4ece\u4f55\u5904\u5f97\u6765\uff01\u96be\u9053\u4ed6\u771f\u662f\u4e09\u5e08\u4f2f\u7684\u5f92\u5f1f\uff1f\u201d\u518d\u7ec6\u770b\u65f6\uff0c\u53ea\u89c1\u5251\u4e0a\u8fd8\u6709\u4e00\u4e2a\u5251\u5760\uff0c\u662f\u4e00\u5757\u548c \u9617\u7f8e\u7389\uff0c\u523b\u6210\u9f99\u5f62\uff0c\u540a\u5728\u5251\u4e0a\uff0c\u7528\u4e3a\u88c5\u9970\u7684\u3002\u4e91\u857e\u53cd\u590d\u7ec6\u770b\uff0c\u53ea\u89c1\u90a3\u5251\u5760\u4e4b\u4e0a\uff0c\u523b\u6709\u201c\u53f3\u4e1e \u76f8\u5e9c\u201d\u56db\u5b57\uff0c\u65c1\u8fb9\u8fd8\u6709\u4e00\u884c\u5c0f\u5b57\uff0c\u6ce8\u660e\u8fd9\u5757\u5b9d\u7389\u7684\u6765\u5386\uff0c\u90a3\u884c\u5b57\u662f\uff1a\u67ab\u513f\u51fa\u4e16\uff0c\u56fd\u4e3b\u6240\u8d50\u3002 \u3000\u3000\u4e91\u857e\u624b\u98a4\u811a\u8f6f\uff0c\u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c\u767d\u4e91\u5b9d\u5251\u8dcc\u843d\u5730\u4e0a\uff0c\u8fd9\u4e00\u4e0b\u4ec0\u4e48\u90fd\u660e\u767d\u4e86\uff0c\u4e00\u8def\u540c\u884c\uff0c \u5bc6\u5ba4\u76f8\u4f34\u7684\u5f20\u4e39\u67ab\uff0c\u7adf\u7136\u662f\u5927\u5978\u8d3c\u5f20\u5b97\u5468\u7684\u513f\u5b50\uff0c\u662f\u4e91\u5bb6\u7684\u5927\u4ec7\u4eba\u5f20\u5b97\u5468\u7684\u513f\u5b50\uff01 \u3000\u3000\u4e91\u857e\u53ea\u89c9\u4e00\u7247\u832b\u7136\uff0c\u8fd9\u970e\u90a3\u95f4\u597d\u50cf\u6574\u4e2a\u4e16\u754c\u90fd\u4e0d\u590d\u5b58\u5728\uff0c\u8111\u6d77\u4e2d\u7a7a\u8361\u8361\u7684\u4e00\u65e0\u6240\u6709\uff0c\u65e0 \u610f\u4e4b\u95f4\u624b\u89e6\u524d\u80f8\uff0c\u89e6\u7740\u4e00\u5c0f\u7247\u786c\u7269\uff0c\u90a3\u6b63\u662f\u4e91\u857e\u7684\u7237\u7237\u6240\u7559\u4e0b\u7684\u7f8a\u76ae\u8840\u4e66\uff0c\u5341\u5e74\u6765\u4e91\u857e\u65e0\u65f6 \u65e0\u523b\u4e0d\u5e26\u5728\u8eab\u4e0a\u3002\u8840\u4e66\u4e0a\u5199\u660e\uff1a\u51e1\u662f\u4e91\u5bb6\u540e\u4ee3\uff0c\u78b0\u7740\u4e86\u5f20\u5b97\u5468\u8fd9\u4e00\u8109\u6240\u4f20\u7684\u4eba\uff0c\u4e0d\u8bba\u7537\u5973\u8001 \u5e7c\uff0c\u90fd\u8981\u628a\u4ed6\u4eec\u6740\u6389\uff01\u867d\u662f\u9694\u4e86\u5341\u5e74\uff0c\u867d\u662f\u9694\u7740\u8863\u88f3\uff0c\u4e91\u857e\u8fd8\u597d\u4f3c\u95fb\u5230\u90a3\u7f8a\u76ae\u4e0a\u7684\u8840\u8165\u5473 \u9053\uff01 \u3000\u3000\u4e91\u857e\u53ea\u611f\u5230\u4e86\u9635\u5bd2\u610f\uff0c\u76f4\u900f\u5fc3\u5934\uff0c\u8fd9\u592a\u53ef\u6015\u4e86\u3002\u90a3\u8840\u4e66\u597d\u4f3c\u4e00\u7247\u5bd2\u51b0\uff0c\u5305\u56f4\u7740\u5979\u7684\u8eab \u4f53\uff0c\u5979\u7684\u5fc3\u7075\uff0c\u53c8\u4f3c\u662f\u4e00\u9053\u65e0\u53ef\u6297\u62d2\u7684\u547d\u4ee4\uff0c\u8981\u5979\u4eb2\u81ea\u52a8\u624b\u53bb\u6740\u5f20\u4e39\u67ab\uff01 \u3000\u3000\u95e8\u5916\u9a6c\u58f0\u5636\u9e23\uff0c\u5f20\u4e39\u67ab\u53c8\u56de\u6765\u4e86\u3002\u4e91\u857e\u5b9a\u4e86\u5b9a\u795e\uff0c\u54ac\u5b9e\u7259\u5173\uff0c\u5782\u9996\u4f4e\u5750\uff0c\u770b\u6765\u4f3c\u662f\u6b63\u5728 \u7528\u529f\uff0c\u5b9e\u662f\u4e0d\u6b32\u5f20\u4e39\u67ab\u77a7\u89c1\u5979\u60e8\u767d\u7684\u9762\u8272\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u8f7b\u8f7b\u5730\u63a8\u5f00\u5ba4\u5185\uff0c\u8d70\u4e86\u8fdb\u6765\uff0c\u7b11\u9053\uff1a\u201c\u7b2c\u4e09\u4e2a\u6545\u4e8b\u6211\u53ef\u8981\u63d0\u524d\u8bf4\u4e86\u3002\u5c0f\u5144\u5f1f\uff0c\u4f60 \u600e\u4e48\u5566\uff1f\u201d\u8d70\u5230\u94dc\u955c\u4e4b\u524d\uff0c\u6574\u7406\u51cc\u4e71\u7684\u5934\u53d1\u3002\u5ffd\u800c\u955c\u4e2d\u73b0\u5f71\uff0c\u53ea\u89c1\u4e91\u857e\u5706\u7741\u53cc\u773c\uff0c\u4e00\u5251\u5411\u4ed6 \u523a\u6765\uff01 \u3000\u3000\u5f53\u90ce\u4e00\u58f0\uff0c\u4e91\u857e\u624b\u6307\u98a4\u6296\uff0c\u5251\u950b\u7a0d\u504f\uff0c\u4e00\u5251\u4ece\u4ed6\u9888\u9879\u65c1\u8fb9\u659c\u659c\u523a\u51fa\uff0c\u5c06\u94dc\u955c\u523a\u788e\uff0c\u5f20\u4e39 \u67ab\u500f\u5730\u56de\u8fc7\u5934\u6765\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u5c0f\u5144\u5f1f\uff0c\u4f60\u542c\u6211\u8bf4\u2026\u2026\u201d\u4e91\u857e\u95ed\u4e86\u773c\u775b\uff0c\u5237\uff0c\u5237\uff0c\u5237\uff0c\u4e00\u53e3 \u6c14\u8fde\u523a\u4e09\u5251\uff01 \u3000\u3000\u5f20\u4e39\u67ab\u817e\u8eab\u8df3\u8fc7\u7389\u51e0\uff0c\u53ea\u542c\u5f97\u4e91\u857e\u54ed\u9053\uff1a\u201c\u6211\u5168\u90fd\u660e\u767d\u5566\u7b2c\u4e09\u4e2a\u6545\u4e8b\u4f60\u4e0d\u5fc5\u8bf4\u4e86\uff01\u201d\u98de \u8eab\u63a0\u8d77\uff0c\u5237\u7684\u53c8\u662f\u4e00\u5251\uff0c\u5f20\u4e39\u67ab\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f60\u662f\u4e91\u9756\u7684\u5b59\u5973\u513f\uff1f\u201d\u4e91\u857e\u53eb\u9053\uff1a\u201c\u4f60\u662f \u6211\u5bb6\u4ec7\u4eba\u7684\u513f\u5b50\uff01\u201d\u5251\u5c16\u523a\u5230\u524d\u5fc3\uff0c\u5f20\u4e39\u67ab\u8eab\u5b50\u4e00\u633a\u53eb\u9053\uff1a\u201c\u597d\u5c0f\u5144\u5f1f\uff0c\u4f60\u523a\u5427\uff01\u6211\u4e0d\u6c42\u4f60 \u9976\u6055\uff01\u201d \u3000\u3000\u201c\u55e4\u201d\u7684\u4e00\u58f0\uff0c\u5251\u950b\u4e00\u659c\uff0c\u63a0\u8fc7\u53f3\u65b9\uff0c\u5f20\u4e39\u67ab\u7684\u53f3\u81c2\u62c9\u4e86\u4e00\u9053\u4f24\u53e3\uff0c\u53ea\u542c\u5f97\u5f20\u4e39\u67ab\u9053\uff1a \u201c\u5c0f\u5144\u5f1f\uff0c\u4f60\u6740\u4e86\u6211\u540e\uff0c\u4e0d\u80fd\u52a8\u6c14\uff0c\u4f60\u8fd8\u8981\u9759\u5750\u4e00\u4e2a\u65f6\u8fb0\uff0c\u7389\u51e0\u4e0a\u6709\u4e00\u4e2a\u5c0f\u94f6\u74f6\uff0c\u74f6\u4e2d\u6709\u7559 \u7ed9\u4f60\u7684\u836f\uff0c\u53ef\u4ee5\u52a9\u4f60\u589e\u957f\u5143\u6c14\uff01\u597d\uff0c\u5c0f\u5144\u5f1f\uff0c\u6211\u4e0d\u6c42\u4f60\u9976\u6055\uff0c\u4f60\u523a\u8fc7\u6765\u5427\uff01\u201d \u3000\u3000\u4e91\u857e\u773c\u6cea\u593a\u7736\u800c\u51fa\uff0c\u624b\u98a4\u5fc3\u75db\uff0c\u9752\u51a5\u5b9d\u5251\u51e0\u4e4e\u8dcc\u843d\u5730\u4e0a\uff0c\u5ffd\u53c8\u89c9\u5f97\u80f8\u524d\u90a3\u5757\u7f8a\u76ae\u8840\u4e66\uff0c \u4f3c\u4e00\u5ea7\u5927\u5c71\uff0c\u91cd\u91cd\u538b\u5728\u5979\u7684\u5fc3\u4e0a\u5f3a\u8feb\u7740\u5979\uff0c\u8981\u5979\u590d\u4ec7\uff01 \u3000\u3000\u4e91\u857e\u5251\u950b\u4e00\u98a4\uff0c\u53eb\u9053\uff1a\u201c\u62fe\u8d77\u5251\u6765\uff0c\u6211\u4e0d\u6740\u624b\u65e0\u5bf8\u94c1\u4e4b\u4eba\u7684\uff01\u201d\u5979\u660e\u77e5\u5f20\u4e39\u67ab\u6b66\u529f\u6bd4\u5979 \u9ad8\u5f3a\uff0c\u82e5\u7136\u5bf9\u624b\u6bd4\u5251\uff0c\u90a3\u6b7b\u4ea1\u7684\u5c31\u4e00\u5b9a\u4e0d\u662f\u5f20\u4e39\u67ab\u800c\u662f\u81ea\u5df1\u3002\u53ef\u662f\u4e0d\u77e5\u600e\u7684\uff0c\u5979\u5374\u5b9a\u8981\u5f20\u4e39 \u67ab\u6bd4\u5251\uff0c\u597d\u4f3c\u82e5\u7136\u6fc0\u6218\u4e4b\u540e\uff0c\u81ea\u5df1\u6b7b\u5728\u5f20\u4e39\u67ab\u5251\u4e0b\uff0c\u4e5f\u7b97\u5f97\u662f\u5bf9\u5f97\u8d77\u7237\u7237\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u51dd\u7acb\u4e0d\u52a8\uff0c\u8138\u4e0a\u4e00\u7247\u4f3c\u54ed\u4f3c\u7b11\u7684\u795e\u60c5\uff0c\u4ee4\u4e91\u857e\u4e0d\u6562\u4ef0\u89c6\u3002\u4e91\u857e\u4e00\u54ac\u7259\u5173\uff0c\u5728\u5730\u4e0a \u62fe\u8d77\u767d\u4e91\u5251\uff0c\u629b\u63b7\u8fc7\u53bb\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u6211\u4e24\u5bb6\uff0c\u6df1\u4ec7\u4e0d\u5171\u6234\u5929\uff0c\u4e0d\u662f\u4f60\u6b7b\uff0c\u4fbf\u662f\u6211\u4ea1\uff0c\u5feb\u5feb\u62d4 \u5251\u5427\uff01\u201d \u3000\u3000\u5f20\u4e39\u67ab\u63a5\u8fc7\u5b9d\u5251\uff0c\u51c4\u7136\u8bf4\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u6211\u4eca\u751f\u8a93\u4e0d\u4e0e\u4f60\u52a8\u624b\uff0c\u4f60\u8981\u6740\u4fbf\u6740\uff0c\u4f60\u82e5\u4e0d\u52a8 \u624b\uff0c\u6211\u4fbf\u8d70\u4e86\uff01\u201d\u4e91\u857e\u865a\u6643\u4e00\u5251\u5251\u5149\u95ea\u8fc7\u5f20\u4e39\u67ab\u9762\u95e8\uff0c\u4ecd\u7136\u659c\u63a0\u51fa\u53bb\uff0c\u5f20\u4e39\u67ab\u957f\u53f9\u4e00\u58f0\uff0c\u8df3 \u51fa\u5bc6\u5ba4\u8de8\u4e0a\u767d\u9a6c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u4f60\u5584\u81ea\u73cd\u91cd\uff0c\u6211\u53bb\u4e86\uff01\u201d\u95e8\u5916\u9a6c\u5636\uff0c\u7247\u523b\u4e4b\u540e\uff0c\u5df2\u5728 \u6570\u91cc\u4e4b\u5916\u3002\u4e91\u857e\u5446\u82e5\u6728\u9e21\uff0c\u957f\u5251\u5760\u5730\uff0c\u773c\u524d\u4e00\u7247\u660f\u6697\u3002\u6b63\u662f\uff1a \u3000\u3000\u662f\u7231\u662f\u4ec7\u96be\u81ea\u89e3\uff0c\u5374\u6559\u7389\u5973\u72ec\u5fc3\u4f24\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u840d\u8e2a\u4fa0\u5f71\u5f55\u300b\u2014\u2014\u7b2c\u516b\u56de\u3000\u7231\u6068\u96be\u660e\u60ca\u4f20\u7eff\u6797\u7bad\u3000\u6069\u4ec7\u83ab\u8fa8\u6101\u5c55\u7d2b\u7f57\u8863 \u6881\u7fbd\u751f\u300a\u840d\u8e2a\u4fa0\u5f71\u5f55\u300b \u7b2c\u516b\u56de\u3000\u7231\u6068\u96be\u660e\u60ca\u4f20\u7eff\u6797\u7bad\u3000\u6069\u4ec7\u83ab\u8fa8\u6101\u5c55\u7d2b\u7f57\u8863 \u3000\u3000\u95e8\u5916\u9a6c\u5636\uff0c\u6e10\u8fdc\u6e10\u5bc2\uff0c\u5f20\u4e39\u67ab\u4e0d\u89c1\u4e86\uff0c\u4f46\u613f\u5f20\u4e39\u67ab\u4ece\u6b64\u6c38\u8fdc\u4e0d\u89c1\u4e86\uff0c\u4f46\u613f\u4eba\u4e16\u95f4\u4ece\u6765\u6ca1 \u6709\u8fc7\u8fd9\u4e48\u4e00\u4e2a\u5f20\u4e39\u67ab\uff01\u591a\u4e48\u53e4\u602a\u7684\u5ff5\u5934\uff0c\u6709\u8840\u6709\u8089\u7684\u5f20\u4e39\u67ab\uff0c\u5728\u5bc6\u5ba4\u4e2d\u548c\u81ea\u5df1\u4f5c\u4f34\u4e09\u65e5\u7684\u5f20 \u4e39\u7eb2\u600e\u4e48\u4f1a\u4ece\u6765\u6ca1\u6709\u5462\uff1f\u662f\u7684\uff0c\u5f20\u4e39\u67ab\u8d70\u8fdc\u4e86\uff0c\u5f20\u4e39\u67ab\u4e0d\u89c1\u4e86\uff0c\u53ef\u662f\u4ed6\u771f\u7684\u4e0d\u89c1\u4e86\u4e48\uff1f\u4e0d\uff0c \u4e0d\uff01\u4f60\u770b\u554a\uff0c\u4ed6\u53c8\u6765\u4e86\uff0c\u6765\u4e86\uff0c\u6765\u4e86\uff01\u4ed6\u7684\u5f71\u5b50\u8f7b\u8f7b\u5730\uff0c\u6162\u6162\u5730\uff0c\u6f5c\u5165\u4e86\u4e91\u857e\u7684\u5fc3\u5934\uff0c\u8fd9\u4e00 \u77ac\u95f4\u7f8a\u76ae\u8840\u4e66\u7684\u9634\u5f71\u4e5f\u7ed9\u4ed6\u7684\u5f71\u5b50\u906e\u6ca1\u4e86\u3002 \u3000\u3000\u4e91\u857e\u4e00\u7247\u8ff7\u832b\uff0c\u662f\u6068\uff1f\u662f\u7231\uff1f\u662f\u559c\uff1f\u662f\u54c0\uff1f\u90fd\u65e0\u4ece\u5206\u8fa8\uff0c\u6069\u4ec7\u4ea4\u7ec7\uff0c\u7231\u6068\u96be\u660e\uff0c\u526a\u4e0d \u65ad\uff0c\u7406\u8fd8\u4e71\u3002\u970e\u90a3\u4e4b\u95f4\uff0c\u4e00\u5207\u601d\u6f6e\u7a81\u7136\u9000\u706d\uff0c\u4e91\u857e\u8111\u4e2d\u7a7a\u8361\u8361\u7684\uff0c\u4f3c\u4e4e\u4ec0\u4e48\u4e5f\u4e0d\u66fe\u60f3\uff0c\u4ec0\u4e48 \u4e5f\u4e0d\u5b58\u5728\uff0c\u8ff7\u832b\u4e2d\u5ffd\u53c8\u4f3c\u89c1\u5f20\u4e39\u67ab\u5189\u5189\u800c\u6765\uff0c\u5728\u5979\u8033\u8fb9\u4f4e\u8bed\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u5c0f\u5144\u5f1f\u2026\u2026\u201d\u5440\uff01 \u90a3\u50cf\u7237\u7237\u4e00\u6837\u4e25\u5389\uff0c\u53c8\u50cf\u5988\u5988\u4e00\u6837\u6148\u7231\u7684\u773c\u5149\uff01\u4e16\u754c\u4e0a\u6709\u4ec0\u4e48\u4eba\u7528\u8fd9\u6e29\u67d4\u7684\u58f0\u97f3\u53eb\u5524\u8fc7\u81ea \u5df1\uff1f\u6709\u4ec0\u4e48\u4eba\u7528\u8fd9\u6837\u7684\u773c\u5149\u6ce8\u89c6\u8fc7\u81ea\u5df1\uff1f\u9664\u4e86\u8fd9\u4e2a\u81ea\u5df1\u4f46\u613f\u4ed6\u6c38\u4e0d\u5b58\u5728\u7684\u5f20\u4e39\u67ab\uff01 \u3000\u3000\u4e91\u857e\u7684\u773c\u5149\u7f13\u7f13\u79fb\u52a8\uff0c\u77a5\u89c1\u4e86\u7389\u51e0\u4e0a\u5f20\u4e39\u67ab\u7559\u4e0b\u7684\u94f6\u74f6\uff0c\u74f6\u4e2d\u662f\u5f20\u4e39\u67ab\u7559\u7ed9\u5979\u7684\u7075\u836f\uff0c \u201c\u8fd9\u662f\u4ec7\u4eba\u7684\u4e1c\u897f\uff0c\u4e0d\uff0c\u4e0d\uff0c\u6211\u4e0d\u80fd\u5403\u3002\u2026\u2026\u8fd9\u662f\u5f20\u4e39\u67ab\u6700\u540e\u7684\u4e00\u756a\u597d\u610f\uff0c\u4e0d\uff0c\u4e0d\uff0c\u6211\u4e0d\u5e94 \u62d2\u7edd\u4e8e\u4ed6\u2026\u2026\u201d\u4e24\u79cd\u5ff5\u5934\u5728\u4e91\u857e\u5fc3\u4e2d\u4ea4\u6218\uff0c\u8ff7\u832b\u4e2d\u5ffd\u53c8\u4f3c\u89c1\u5f20\u4e39\u67ab\u542b\u60c5\u8109\u8109\u5730\u51dd\u89c6\u7740\u81ea\u5df1\uff0c \u5728\u8033\u8fb9\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u4f60\u7684\u4f24\u867d\u5df2\u6cbb\u6108\uff0c\u5143\u6c14\u5c1a\u672a\u6062\u590d\uff0c\u5403\u5427\uff0c\u5403\u5427\u2026\u2026\u201d\u90a3\u4e0d\u53ef\u6297 \u62d2\u7684\u773c\u5149\uff0c\u90a3\u4e0d\u53ef\u6297\u62d2\u7684\u58f0\u97f3\uff0c\u4e91\u857e\u4e0d\u77e5\u4e0d\u89c9\u5730\u62ff\u8d77\u4e86\u94f6\u74f6\uff0c\u5c06\u4e09\u7c92\u7ea2\u8272\u7684\u836f\u4e38\u503e\u5012\u624b\u5fc3\uff0c \u7eb3\u5165\u53e3\u4e2d\u3002 \u3000\u3000\u4e5f\u4e0d\u77e5\u5728\u5730\u4e0a\u5750\u4e86\u591a\u4e45\uff0c\u53ea\u89c1\u655e\u5f00\u7684\u5893\u95e8\u5916\u65e5\u5f71\u897f\u79fb\uff0c\u60f3\u5df2\u662f\u9ec4\u660f\u65f6\u5206\uff0c\u5ffd\u542c\u5f97\u5916\u9762\u4e00 \u58f0\u9a6c\u5636\uff0c\u4e91\u857e\u5fc3\u5934\u4e00\u9707\uff0c\u8df3\u4e86\u8d77\u6765\uff0c\u60f3\u9053\uff1a\u201c\u96be\u9053\u662f\u4ed6\u53c8\u56de\u6765\u4e86\uff1f\u201d \u3000\u3000\u53ea\u542c\u5f97\u4e00\u58f0\u6b22\u547c\uff0c\u4f46\u89c1\u5468\u5c71\u6c11\u75be\u5954\u800c\u6765\uff0c\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u4e91\u59b9\u59b9\uff0c\u4f60\u679c\u7136\u8fd8\u5728\u8fd9\u91cc\uff01\u54ce \u54df\uff0c\u4f60\u4e2d\u4e86\u90a3\u25a1\u7684\u6bd2\u624b\u5417\uff1f\u201d\u4e91\u857e\u6de1\u6de1\u4e00\u7b11\uff0c\u6447\u4e86\u6447\u5934\u3002\u5468\u5c71\u6c11\u6328\u5728\u5979\u8eab\u8fb9\u5750\u4e0b\uff0c\u671d\u5979\u7684\u9762 \u4e0a\u770b\u4e86\u53c8\u770b\uff0c\u6194\u60b4\u7684\u989c\u5bb9\uff0c\u5931\u9b42\u843d\u9b44\u7684\u6a21\u6837\uff0c\u4ee4\u4ed6\u65e0\u9650\u62c5\u5fc3\u3002 \u3000\u3000\u4e91\u857e\u5b9a\u4e86\u5b9a\u795e\uff0c\u53ea\u542c\u5f97\u5468\u5c71\u6c11\u9053\uff1a\u201c\u539f\u6765\u4f60\u548c\u4ed6\u8eb2\u5728\u8fd9\u4e2a\u5893\u4e2d\uff0c\u4f60\u6ca1\u6709\u5403\u4ed6\u7684\u4e8f\u5427\uff1f\u4f60 \u77e5\u9053\u4ed6\u662f\u8c01\uff1f\u4ed6\u662f\u5927\u5978\u8d3c\u5f20\u5b97\u5468\u7684\u513f\u5b50\uff0c\u662f\u4f60\u7237\u7237\u7684\u5927\u4ec7\u4eba\uff01\u201d\u5468\u5c71\u6c11\u6b64\u8a00\u4e00\u51fa\uff0c\u4ee5\u4e3a\u4e91\u857e \u5fc5\u7136\u5413\u5f97\u8df3\u8d77\uff0c\u5c82\u6599\u4e91\u857e\u53ea\u662f\u4f4e\u4f4e\u5730\u5e94\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u55ef\uff0c\u6211\u77e5\u9053\u4e86\u3002\u201d\u8fd9\u4e00\u4e0b\uff0c\u53cd\u800c\u628a \u5468\u5c71\u6c11\u5413\u5f97\u8df3\u4e86\u8d77\u6765\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u4f60\u77e5\u9053\u4e86\uff1f\u4f60\u4ec0\u4e48\u65f6\u5019\u77e5\u9053\u7684\uff1f\u201d\u4e91\u857e\u8eab\u5b50\u4e0d \u52a8\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u521a\u521a\u77e5\u9053\u7684\uff0c\u6fb9\u53f0\u706d\u660e\u65b9\u624d\u6765\u8fc7\u2026\u2026\u201d\u5468\u5c71\u6c11\u5618\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u539f\u6765\u5982 \u6b64\uff0c\u6211\u9053\u4f60\u82e5\u65e9\u77e5\u4ed6\u662f\u4ec7\u4eba\uff0c\u600e\u4f1a\u4e0e\u4ed6\u4f5c\u4f34\uff1f\u4f60\u548c\u4ed6\u52a8\u4e86\u624b\u4e86\uff1f\u53ef\u771f\u7684\u6ca1\u53d7\u4f24\u4e48\uff1f\u201d \u3000\u3000\u4e91\u857e\u9053\uff1a\u201c\u6211\u53d7\u4e86\u767d\u6469\u8bc3\u7684\u6bd2\u624b\u6240\u4f24\uff0c\u662f\u4ed6\u7ed9\u6211\u6cbb\u7684\u3002\u201d\u5468\u5c71\u6c11\u9053\uff1a\u201c\u4ed6\uff1f\u4ed6\u662f\u8c01\uff1f\u201d \u4e91\u857e\u9053\uff1a\u201c\u6211\u7237\u7237\u7684\u5927\u4ec7\u4eba\uff01\u201d\u5468\u5c71\u6c11\u4e00\u6014\uff0c\u9053\uff1a\u201c\u4ed6\u4e0d\u77e5\u9053\u4f60\u662f\u4e91\u9756\u7684\u5b59\u5973\u513f\uff1f\u201d\u4e91\u857e \u9053\uff1a\u201c\u6211\u7528\u5251\u523a\u4ed6\uff0c\u4ed6\u77e5\u9053\u4e86\uff01\u201d\u5468\u5c71\u6c11\u53c8\u662f\u4e00\u6014\uff0c\u5ffd\u4f3c\u987f\u7136\u9192\u609f\u9053\uff1a\u201c\u54e6\uff0c\u6211\u77e5\u9053\u4e86\u3002\u8fd9 \u5978\u8d3c\u521d\u65f6\u4e0d\u77e5\u4f60\u662f\u4ed6\u4ec7\u4eba\uff0c\u8fd9\u624d\u5c06\u4f60\u7b3c\u7edc\uff0c\u60f3\u628a\u4f60\u6536\u4e3a\u5df1\u7528\u3002\u540e\u6765\u4f60\u62d4\u5251\u523a\u4ed6\uff0c\u4ed6\u4e0d\u662f\u4f60\u7684 \u5bf9\u624b\uff0c\u6240\u4ee5\u9003\u4e86\u3002\u53ef\u60dc\u4f60\u53d7\u4f24\u521a\u597d\uff0c\u6c14\u529b\u5927\u7ea6\u8fd8\u672a\u6062\u590d\uff0c\u8981\u4e0d\u7136\u5b9a\u53ef\u4e00\u5251\u5c06\u4ed6\u523a\u6b7b\uff0c\u6211\u4e5f\u4e0d \u7528\u8d39\u8fd9\u4e48\u5927\u7684\u52b2\u4e86\u3002\u201d \u3000\u3000\u4e91\u857e\u4f4e\u9996\u4e0d\u8bed\u4efb\u7531\u5468\u5c71\u6c11\u731c\u5ea6\u3002\u53ea\u542c\u5f97\u5468\u5c71\u6c11\u5f97\u610f\u7b11\u9053\uff1a\u201c\u65e9\u77e5\u4ed6\u6b66\u529f\u5982\u6b64\u7a00\u677e\u5e73\u5e38\uff0c \u6211\u4e5f\u4e0d\u7528\u8d39\u8fd9\u4e48\u5927\u7684\u52b2\uff0c\u6c42\u90a3\u8f70\u5929\u96f7\u77f3\u82f1\u5171\u540c\u4f20\u4e0b\u7eff\u6797\u7bad\u4e86\uff01\u201d\u4e91\u857e\u5403\u4e86\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u4ec0 \u4e48\uff0c\u7eff\u6797\u7bad\uff1f\u201d \u3000\u3000\u5468\u5c71\u6c11\u7b11\u9053\uff1a\u201c\u4f60\u6c5f\u6e56\u9605\u5386\u5c1a\u6d45\uff0c\u8fd8\u4e0d\u77e5\u9053\u4ec0\u4e48\u662f\u7eff\u6797\u7bad\u5417\uff1f\u7eff\u6797\u7bad\u662f\u7eff\u6797\u9886\u8896\u4f20\u4e0b\u7684 \u4ee4\u7bad\uff0c\u7eff\u6797\u82f1\u96c4\uff0c\u89c1\u4e86\u4ee4\u7bad\uff0c\u8d74\u6c64\u8e48\u706b\uff0c\u4ea6\u4e0d\u6562\u8f9e\u3002\u4e91\u59b9\u59b9\uff0c\u771f\u662f\u795e\u5dee\u9b3c\u4f7f\uff0c\u5f20\u5b97\u5468\u7684\u513f\u5b50 \u5c45\u7136\u6562\u4e00\u4e2a\u4eba\u95ef\u8fdb\u5173\u6765\uff0c\u4f60\u7684\u5927\u4ec7\u662f\u5b9a\u80fd\u62a5\u4e86\uff01\u201d \u3000\u3000\u7f8a\u76ae\u8840\u4e66\u7684\u9634\u5f71\u53c8\u5728\u5fc3\u5934\u6269\u5927\u8d77\u6765\uff0c\u4e91\u857e\u5bf9\u8fd9\u6d88\u606f\u4e5f\u4e0d\u77e5\u9053\u662f\u559c\u662f\u60b2\uff0c\u7237\u7237\u7684\u9057\u5631\u90a3\u662f \u4e07\u4e07\u4e0d\u80fd\u8fdd\u80cc\uff0c\u5f20\u5bb6\u7684\u4eba\u4e00\u4e2a\u4e5f\u4e0d\u80fd\u9976\uff0c\u90a3\u4e48\u5c31\u8ba9\u4ed6\u7ed9\u522b\u4eba\u6740\u4e86\uff0c\u514d\u5f97\u81ea\u5df1\u52a8\u624b\u3002\u53ef\u662f\u4e00\u60f3 \u5230\u5f20\u4e39\u67ab\u8981\u88ab\u7eff\u6797\u7fa4\u96c4\u4e71\u5200\u65a9\u6b7b\uff0c\u90a3\u666f\u8c61\u5374\u662f\u60f3\u4e5f\u4e0d\u6562\u4e00\u60f3\u3002\u53ea\u542c\u5468\u5c71\u6c11\u53c8\u5728\u65c1\u8fb9\u8bf4\u9053\uff1a \u201c\u4e91\u59b9\u59b9\uff0c\u81ea\u4f60\u79bb\u5c71\u4e4b\u540e\uff0c\u6211\u5341\u5206\u6302\u5ff5\u3002\u201d\u58f0\u97f3\u5f88\u662f\u6e29\u67d4\uff0c\u4e91\u857e\u62ac\u8d77\u4e86\u5934\uff0c\u6709\u6c14\u6ca1\u529b\u5730\u9053\uff1a \u201c\u55ef\uff0c\u591a\u8c22\u4f60\u7684\u8bb0\u6302\u3002\u201d\u5468\u5c71\u6c11\u89c1\u5979\u8fd9\u526f\u6ca1\u7cbe\u6253\u5f69\u7684\u6837\u5b50\u751a\u662f\u5931\u671b\uff0c\u4ecd\u5f80\u4e0b\u8bf4\u9053\uff1a\u201c\u6211\u603b\u60f3 \u518d\u89c1\u7740\u4f60\uff0c\u53ef\u662f\u5c71\u5be8\u4e8b\u5fd9\uff0c\u54ea\u91cc\u80fd\u591f\uff1f\u4e0a\u6708\u6211\u4eec\u5728\u8fb9\u5883\u7684\u63a2\u5b50\uff0c\u63a2\u51fa\u5f20\u5b97\u5468\u7684\u513f\u5b50\u4e00\u4e2a\u4eba\u95ef \u8fdb\u5173\u6765\uff0c\u626e\u6210\u4e00\u4e2a\u79c0\u624d\u6a21\u6837\uff0c\u9a91\u7740\u4e00\u5339\u767d\u9a6c\uff0c\u6781\u662f\u795e\u9a8f\u3002\u6211\u7239\u548c\u5c71\u5be8\u4e2d\u4eba\u5546\u91cf\uff0c\u5927\u5bb6\u90fd\u8bf4\uff0c \u5f20\u5b97\u5468\u7684\u513f\u5b50\u95ef\u8fdb\u5173\u6765\u8fd8\u80fd\u5b89\u4ec0\u4e48\u597d\u5fc3\uff0c\u4e00\u5b9a\u662f\u6253\u56fe\u8c0b\u4e2d\u56fd\u7684\u574f\u4e3b\u610f\u4e86\u3002\u6211\u7239\u5c31\u53eb\u6211\u8ffd\u8e2a\uff0c \u4f1a\u540c\u5404\u5730\u7684\u7eff\u6797\u9886\u8896\uff0c\u5171\u4f20\u7eff\u6797\u7bad\u5b9a\u8981\u5c06\u4ed6\u64d2\u83b7\u3002\u6b64\u5730\u662f\u5c71\u897f\u5883\u5185\uff0c\u664b\u3001\u9655\u4e24\u7701\u7684\u6b66\u6797\u76df \u4e3b\uff0c\u4e43\u662f\u77f3\u82f1\uff0c\u504f\u504f\u6211\u53bb\u5bfb\u4ed6\u4e4b\u65f6\uff0c\u4ed6\u5df2\u4e0d\u5728\u9ed1\u77f3\u5e84\u4e2d\u3002\u540e\u6765\u89c1\u4e86\u77f3\u82f1\u7684\u5973\u513f\uff0c\u624d\u77e5\u9053\u539f\u6765 \u4f60\u7adf\u7136\u505a\u4e86\u77f3\u82f1\u7684\u5973\u5a7f\u3002\u77f3\u5c0f\u59d0\u53ef\u8fd8\u662f\u771f\u7684\u559c\u6b22\u4f60\uff01\u201d \u3000\u3000\u4e91\u857e\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u770b\u77f3\u5c0f\u59d0\u5979\u5982\u4f55\uff1f\u201d\u5468\u5c71\u6c11\u9053\uff1a\u201c\u6b66\u827a\u4e5f\u8fd8\u8fc7\u5f97\u53bb\u3002\u201d\u4e91\u857e \u9053\uff1a\u201c\u5176\u4ed6\u5462\uff1f\u201d\u5468\u5c71\u6c11\u9053\uff1a\u201c\u6211\u4e0e\u5979\u76f8\u8bc6\u8fd8\u4e0d\u5230\u534a\u5929\uff0c\u600e\u77e5\u4ec0\u4e48\u2018\u5176\u4ed6\u2019\uff1f\u201d\u4e91\u857e\u53c8\u662f\u5fae \u5fae\u4e00\u7b11\u3002\u672c\u60f3\u518d\u8bf4\uff0c\u53ef\u662f\u5fc3\u4e2d\u60ac\u6302\u201c\u7eff\u6797\u7bad\u201d\u4e4b\u4e8b\uff0c\u7eb3\u95f7\u77f3\u82f1\u5bf9\u5f20\u4e39\u67ab\u90a3\u4e48\u5c0a\u656c\uff0c\u53c8\u4f55\u4ee5\u4f1a \u4e0e\u5468\u5c71\u6c11\u5171\u4f20\u4e0b\u7eff\u6797\u7bad\uff1f\u6b64\u4e00\u7591\u95ee\uff0c\u6025\u6b32\u5206\u6653\uff0c\u4fbf\u4e0d\u518d\u6253\u8d2b\uff0c\u8ba9\u4ed6\u8bf4\u4e0b\u53bb\u3002 \u3000\u3000\u5468\u5c71\u6c11\u5f80\u4e0b\u8bf4\u9053\uff1a\u201c\u90a3\u65e5\u6211\u4e0e\u77f3\u59d1\u5a18\u8ffd\u8d76\u6fb9\u53f0\u706d\u660e\u7684\u5f92\u5f1f\u4ed6\u7684\u9a6c\u662f\u5927\u5b9b\u826f\u9a6c\uff0c\u8ffd\u51fa\u4e86\u4e09 \u4e94\u5341\u91cc\uff0c\u6211\u4eec\u7684\u9a6c\u90fd\u7d2f\u4e86\uff0c\u4ed6\u7684\u9a6c\u8fd8\u662f\u5954\u8d70\u5982\u98ce\uff0c\u8ffd\u4e0d\u4e0a\u5566\uff01\u201d\u4e91\u857e\u63d2\u53e3\u9053\uff1a\u201c\u77f3\u59d1\u5a18 \u5462\uff1f\u201d\u5468\u5c71\u6c11\u4e00\u7b11\u8bf4\u9053\uff1a\u201c\u4f60\u8fd9\u4f4d\u592b\u4eba\u5bf9\u6211\u4f3c\u662f\u751a\u6709\u6210\u89c1\uff0c\u4e00\u8def\u548c\u6211\u62ac\u6760\uff0c\u542c\u5979\u8a00\u4e0b\u4e4b\u610f\uff0c \u4f3c\u4e4e\u751a\u4e0d\u6ee1\u610f\u6211\u662f\u4f60\u7684\u4e49\u5144\uff0c\u5012\u628a\u6211\u5f04\u5f97\u83ab\u540d\u5176\u5999\uff0c\u6211\u662f\u4f60\u7684\u4e49\u5144\uff0c\u53c8\u5e72\u5979\u4ec0\u4e48\u6765\u4e86\uff1f\u201d\u4e91 \u857e\u5fc3\u4e2d\u597d\u7b11\uff0c\u60f3\u4e0d\u5230\u90a3\u665a\u201c\u6d1e\u623f\u4e4b\u591c\u201d\uff0c\u4e0e\u77f3\u7fe0\u51e4\u5c61\u5c61\u63d0\u53ca\u4e49\u5144\uff0c\u53cd\u800c\u5f04\u5de7\u6210\u62d9\u3002 \u3000\u3000\u5468\u5c71\u6c11\u505a\u4e86\u4e2a\u53d7\u59d4\u5c48\u7684\u8868\u60c5\uff0c\u8038\u80a9\u8bf4\u9053\uff1a\u201c\u8ffd\u4e0d\u4e0a\u654c\u4eba\uff0c\u5979\u548c\u6211\u5435\u4e86\u4e00\u67b6\uff0c\u8bf4\u8981\u72ec\u81ea\u56de \u5bb6\uff0c\u4e5f\u4e0d\u613f\u5e26\u6211\u53bb\u89c1\u5979\u7684\u7236\u4eb2\uff0c\u8fd8\u5435\u7740\u8981\u6211\u628a\u90a3\u679d\u73ca\u745a\u8fd8\u5979\uff0c\u5979\u50cf\u90a3\u73ca\u745a\u662f\u5979\u547d\u6839\u5b50\u4f3c \u7684\u3002\u201d\u4e91\u857e\u4e0d\u89c9\u53c8\u662f\u62bf\u5634\u4e00\u7b11\u3002\u5468\u5c71\u6c11\u9053\uff1a\u201c\u6211\u77e5\u9053\u90a3\u73ca\u745a\u662f\u4f60\u7ed9\u5979\u7684\u8058\u793c\uff0c\u5979\u5bf9\u4f60\u771f\u60c5\u4e00 \u7247\uff0c\u602a\u4e0d\u5f97\u5b9d\u8d1d\u5982\u65af\uff01\u201d\u4e91\u857e\u7b11\u7740\u9053\uff1a\u201c\u8fd9\u56de\u662f\u4f60\u7ed9\u5979\u7684\u8058\u793c\uff0c\u4e0d\u662f\u6211\u7ed9\u7684\u4e86\u3002\u201d\u5468\u5c71\u6c11\u9762 \u4e0a\u4e00\u7ea2\uff0c\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u9b3c\u5934\uff0c\u4e71\u56bc\u820c\u5934\uff0c\u770b\u6211\u6495\u4f60\u7684\u5634\u3002\u201d\u4e91\u857e\u4e00\u7b11\u907f\u5f00\uff0c\u9053\uff1a\u201c\u8bf4\u6b63\u7ecf \u7684\uff0c\u77f3\u59d1\u5a18\u65e2\u4e0d\u613f\u5e26\u4f60\u53bb\u89c1\u5979\u7684\u7236\u4eb2\uff0c\u4f60\u7684\u7eff\u6797\u7bad\u53c8\u4ece\u54ea\u91cc\u5f97\u6765\uff1f\u201d \u3000\u3000\u5468\u5c71\u6c11\u9053\uff1a\u201c\u65e0\u5de7\u4e0d\u5de7\uff0c\u77f3\u59d1\u5a18\u53bb\u540e\u4e0d\u4e45\uff0c\u6211\u7b56\u9a6c\u897f\u884c\uff0c\u4e0d\u4e45\u5c31\u9047\u89c1\u4e86\u8f70\u5929\u96f7\u77f3\u82f1\uff0c\u4ed6 \u8fd8\u4e0d\u77e5\u9053\u4ed6\u5973\u513f\u66fe\u548c\u6211\u4e00\u9053\u5462\u3002\u60f3\u6765\u662f\u4ed6\u7236\u5973\u5404\u8d70\u4e00\u9014\uff0c\u6240\u4ee5\u6ca1\u6709\u89c1\u9762\u3002\u201d\u4e91\u857e\u9053\uff1a\u201c\u77f3\u82f1 \u662f\u4e0d\u662f\u548c\u56db\u4e2a\u73e0\u5b9d\u5546\u4eba\u4e00\u9053\uff1f\u201d\u5468\u5c71\u6c11\u9053\uff1a\u201c\u662f\u5440\uff0c\u4ed6\u884c\u8272\u5306\u5306\u597d\u50cf\u6709\u4ec0\u4e48\u6025\u4e8b\uff0c\u65e0\u6687\u4e0e\u6211 \u591a\u8bf4\u3002\u6211\u95ee\u4ed6\u8981\u7eff\u6797\u7bad\uff0c\u6b63\u60f3\u4e00\u4e00\u8be6\u544a\u4e8e\u4ed6\uff0c\u4ed6\u5374\u6447\u624b\u8bf4\u9053\uff1a\u2018\u91d1\u5200\u5be8\u4e3b\u7684\u4fa0\u4e49\u5a01\u540d\uff0c\u5929\u4e0b \u8c01\u4eba\u4e0d\u77e5\uff01\u65e2\u662f\u4f60\u4eec\u8981\u8ffd\u6355\u7684\uff0c\u90a3\u5c31\u5fc5\u5b9a\u662f\u4e07\u6076\u4e0d\u8d66\u4e4b\u4eba\uff0c\u4e0d\u5fc5\u8bf4\u4e86\uff0c\u7eff\u6797\u7bad\u62ff\u53bb\u4fbf\u662f\uff01\u6211 \u6709\u6025\u4e8b\uff0c\u6055\u4e0d\u966a\u4e86\u3002\u5c11\u5be8\u4e3b\uff0c\u4f60\u4e8b\u60c5\u4e86\u7ed3\u4e4b\u540e\uff0c\u90a3\u65f6\u8bf7\u518d\u5230\u9ed1\u77f3\u5e84\u4e00\u53d9\uff0c\u8be6\u7ec6\u8c08\u8c08\u3002\u2019\u4ed6\u95ee \u4e5f\u4e0d\u95ee\u4fbf\u628a\u7eff\u6797\u7bad\u4ea4\u7ed9\u4e86\u6211\uff0c\u7acb\u523b\u4e0e\u90a3\u56db\u4e2a\u73e0\u5b9d\u5546\u4eba\u8d70\u4e86\u3002\u201d\u4e91\u857e\u5fc3\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\uff0c\u82e5\u7136 \u77f3\u82f1\u591a\u95ee\u4e00\u58f0\uff0c\u77e5\u9053\u6240\u8981\u8ffd\u6355\u7684\u662f\u8c01\uff0c\u90a3\u5c31\u7edd\u4e0d\u81f3\u4e8e\u6709\u6b64\u8bef\u4f1a\u3002\u201d \u3000\u3000\u5468\u5c71\u6c11\u7eed\u9053\uff1a\u201c\u6211\u548c\u77f3\u82f1\u5728\u5b5f\u826f\u5c97\u9644\u8fd1\u4f1a\u9762\uff0c\u90a3\u9644\u8fd1\u4fbf\u662f\u84dd\u5929\u77f3\u5be8\u4e3b\u7684\u5730\u5934\uff0c\u6211\u5c06\u7eff\u6797 \u7bad\u4ea4\u7ed9\u4e86\u4ed6\uff0c\u53eb\u4ed6\u4e09\u65e5\u4e4b\u5185\uff0c\u904d\u4f20\u7eff\u6797\u540c\u9053\u3002\u6211\u5728\u4ed6\u5be8\u4e2d\u4f4f\u4e86\u4e00\u5929\u542c\u5019\u6d88\u606f\uff0c\u4e8b\u60c5\u987a\u5229\u5f97 \u5f88\uff0c\u6709\u77f3\u82f1\u548c\u6211\u7239\u7239\u8054\u540d\uff0c\u597d\u51e0\u4e2a\u4ece\u6765\u4e0d\u80af\u542c\u4eba\u8c03\u9063\uff0c\u96c4\u9738\u4e00\u65b9\u7684\u7eff\u6797\u5927\u8c6a\uff0c\u90fd\u613f\u610f\u62d4\u5200\u76f8 \u52a9\u4e86\u3002\u4e91\u59b9\u59b9\uff0c\u8fd9\u6b21\u4f60\u5bb6\u7684\u5927\u4ec7\u4e00\u5b9a\u80fd\u62a5\u4e86\uff01\u54ce\uff0c\u600e\u4e48\uff1f\u4f60\u600e\u4e48\u8fd8\u4e0d\u6b22\u559c\u5462\uff1f\u201d\u4e91\u857e\u9762\u8272\u82cd \u767d\uff0c\u542c\u4ed6\u4e00\u95ee\uff0c\u5f3a\u7b11\u8bf4\u9053\uff1a\u201c\u55ef\uff0c\u6211\u6709\u70b9\u4e0d\u5927\u8212\u670d\uff0c\u73b0\u5728\u597d\u4e86\u3002\u6211\u3001\u6211\u5f88\u9ad8\u5174\uff01\u201d \u3000\u3000\u5468\u5c71\u6c11\u9053\uff1a\u201c\u7eff\u6797\u7bad\u6709\u7eff\u6797\u540c\u9053\u4e00\u624b\u4f20\u7ed9\u4e00\u624b\uff0c\u4e0d\u5fc5\u6211\u518d\u591a\u7ba1\u3002\u6211\u60f3\u8d77\u90a3\u65e5\u5728\u6b64\u9047\u89c1\u4f60 \u7684\u7ea2\u9b03\u6218\u9a6c\uff0c\u4fbf\u518d\u56de\u6765\u627e\u4f60\uff0c\u5929\u53ef\u601c\u89c1\uff0c\u679c\u7136\u89c1\u7740\u4f60\u4e86\u3002\u201d\u4e91\u857e\u4e0d\u8a00\u4e0d\u8bed\uff0c\u5468\u5c71\u6c11\u6b63\u60f3\u518d\u5410 \u8877\u66f2\uff0c\u5ffd\u800c\u597d\u4f3c\u542c\u89c1\u4ec0\u4e48\u4f3c\u7684\uff0c\u6025\u6025\u4f0f\u5728\u5730\u4e0a\u3002 \u3000\u3000\u4e91\u857e\u95ee\u9053\uff1a\u201c\u662f\u4e0d\u662f\u53c8\u6709\u4ec0\u4e48\u4eba\u6765\u4e86\uff1f\u600e\u4e48\u6211\u542c\u4e0d\u89c1\uff1f\u201d\u5468\u5c71\u6c11\u7ad9\u8d77\u6765\u9053\uff1a\u201c\u6765\u4eba\u8fd8\u5728 \u4e03\u516b\u91cc\u5916\u3002\u201d\u4ece\u5bb9\u5730\u628a\u5916\u9762\u77f3\u95e8\u63a9\u4e0a\u3002\u8fd9\u201c\u4f0f\u5730\u542c\u58f0\u201d\u7684\u672c\u9886\uff0c\u662f\u7eff\u6797\u9ad8\u624b\u7684\u7edd\u6280\uff0c\u4ea6\u662f\u7ecf \u9a8c\u7d2f\u79ef\u6240\u6210\uff0c\u4e91\u857e\u867d\u7136\u5b66\u8fc7\uff0c\u706b\u5019\u5374\u8fd8\u5dee\u5f97\u592a\u8fdc\u3002 \u3000\u3000\u5468\u5c71\u6c11\u770b\u4e86\u4e91\u857e\u4e00\u773c\uff0c\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u4f60\u8be5\u6362\u8863\u670d\u4e86\u5427\uff1f\u201d\u4e91\u857e\u81ea\u90a3\u65e5\u5411\u5f20\u4e39\u67ab\u9732\u51fa\u672c\u76f8 \u4e4b\u540e\uff0c\u4fbf\u6362\u4e86\u5973\u513f\u670d\u9970\uff0c\u8fd9\u65f6\u88ab\u5468\u5c71\u6c11\u63d0\u9192\uff0c\u4e0d\u89c9\u7c89\u9762\u98de\u971e\uff0c\u4f4e\u5934\u8d70\u8fdb\u5bc6\u5ba4\uff0c\u628a\u95e8\u5173\u4e0a\u3002\u5468 \u5c71\u6c11\u4e00\u4eba\u7559\u5728\u95e8\u5916\uff0c\u5fc3\u4e2d\u751a\u662f\u72d0\u7591\uff1a\u770b\u4e91\u857e\u8fd9\u4e2a\u6837\u5b50\uff0c\u83ab\u975e\u5728\u5979\u672a\u8bc6\u7834\u4ec7\u4eba\u9762\u76ee\u4e4b\u524d\uff0c\u7adf\u5df2 \u5230\u4e86\u548c\u4ed6\u719f\u843d\u65e0\u62d8\u7684\u5730\u6b65\uff1f \u3000\u3000\u4e91\u857e\u5728\u5bc6\u5ba4\u91cc\u6253\u5f00\u884c\u56ca\uff0c\u8111\u6d77\u4e2d\u4e0d\u89c9\u53c8\u6cdb\u51fa\u5f20\u4e39\u67ab\u4f3c\u7b11\u975e\u7b11\u7684\u6837\u5b50\uff0c\u201c\u5c0f\u5144\u5f1f\uff0c\u5c0f\u5144 \u5f1f\u2026\u2026\u201d\u90a3\u4ee4\u4eba\u5fc3\u9b42\u52a8\u8361\u7684\u58f0\u97f3\uff0c\u53c8\u4f3c\u5728\u8033\u8fb9\u54cd\u4e86\u8d77\u6765\u3002\u4e91\u857e\u968f\u624b\u53d6\u51fa\u51e0\u4ef6\u5973\u88c5\u8863\u88f3\uff0c\u72e0\u72e0 \u5730\u4e00\u4ef6\u4e00\u4ef6\u6495\u6210\u4e24\u534a\u3002\u5979\u6068\u4ec0\u4e48\uff1f\u6068\u8fd9\u4e9b\u8863\u88f3\u5417\uff1f\u4e0d\uff0c\u5979\u81ea\u5df1\u4e5f\u4e0d\u77e5\u9053\u6068\u7684\u662f\u4ec0\u4e48\uff0c\u53ea\u662f\u5fc3 \u4e2d\u7684\u6291\u90c1\u5374\u597d\u4f3c\u968f\u7740\u8fd9\u88c2\u5e1b\u4e4b\u58f0\u800c\u6d88\u6563\u7a7a\u6e9f\uff0c\u53c8\u597d\u4f3c\u6495\u6bc1\u4e86\u8fd9\u4e9b\u8863\u88f3\uff0c\u5c31\u7b49\u5982\u6495\u6bc1\u4e86\u81ea\u5df1\u7684 \u8bb0\u5fc6\u3002\u5979\u771f\u613f\u610f\u81ea\u5df1\u771f\u662f\u4e00\u4e2a\u7537\u513f\uff0c\u5982\u679c\u662f\u4e00\u4e2a\u7537\u513f\u7684\u8bdd\uff0c\u4e5f\u8bb8\u4f1a\u5c11\u4e86\u8bb8\u591a\u82e6\u607c\u3002 \u3000\u3000\u4e91\u857e\u4e00\u4ef6\u4e00\u4ef6\u5730\u6495\u4e0b\u53bb\uff0c\u7a81\u7136\u505c\u4e0b\u624b\u6765\u3002\u5979\u624b\u4e0a\u63d0\u8d77\u7684\u662f\u4e00\u4ef6\u7d2b\u8272\u7684\u7f57\u8863\uff0c\u8bb0\u5f97\u9732\u4e86\u5973 \u513f\u672c\u76f8\u4e4b\u540e\uff0c\u7b2c\u4e00\u665a\u6362\u7684\u5c31\u662f\u8fd9\u4ef6\u8863\u88f3\uff0c\u8bb0\u5f97\u90a3\u65f6\u5f20\u4e39\u67ab\u9732\u51fa\u5f02\u6837\u7684\u76ee\u5149\uff0c\u5567\u5567\u5730\u79f0\u8d5e\u81ea\u5df1 \u7684\u7f8e\u4e3d\u3002\u4e91\u857e\u53f9\u4e86\u53e3\u6c14\uff0c\u628a\u7f57\u8863\u4e00\u5c55\uff0c\u77a7\u4e86\u53c8\u77a7\uff0c\u8fd9\u662f\u5f20\u4e39\u67ab\u8d5e\u8d4f\u8fc7\u7684\u8863\u88f3\u554a\uff01\u5979\u8f7b\u8f7b\u5730\u629a \u6478\u90a3\u67d4\u8f6f\u7684\u4e1d\u7ef8\uff0c\u53c8\u8f7b\u8f7b\u5730\u628a\u8863\u88f3\u6298\u4e86\u8d77\u6765\uff0c\u5c0f\u5fc3\u7ffc\u7ffc\u5730\u628a\u5b83\u653e\u597d\uff0c\u4e0d\u518d\u6495\u4e0b\u53bb\u4e86\u3002 \u3000\u3000\u5bc6\u5ba4\u5916\u4f20\u6765\u4e86\u5468\u5c71\u6c11\u8e31\u6765\u8e31\u53bb\u7684\u811a\u6b65\u58f0\uff0c\u4e91\u857e\u731b\u7136\u9192\u9053\uff1a\u201c\u6211\u5728\u8fd9\u91cc\u53d1\u50bb\uff0c\u5468\u5927\u54e5\u53ef\u7b49 \u5f97\u4e0d\u8010\u70e6\u4e86\uff01\u201d\u968f\u624b\u7ffb\u51fa\u4e00\u4ef6\u7537\u88c5\u8863\u88f3\uff0c\u5306\u5306\u6362\u4e0a\uff0c\u8d70\u51fa\u95e8\u6765\uff0c\u53ea\u89c1\u5468\u5c71\u6c11\u501a\u5728\u5916\u8fb9\u7684\u77f3\u95e8 \u8bf4\u9053\uff1a\u201c\u4f60\u542c\u90a3\u9a6c\u8e44\u4e4b\u58f0\uff0c\u6765\u4eba\u5df2\u5728\u4e00\u91cc\u4e4b\u5185\u3002\u5230\u8fd9\u8352\u90ca\u5893\u5730\u6765\u7684\uff0c\u5fc5\u5b9a\u4e0d\u662f\u5bfb\u5e38\u4e4b\u4eba\uff0c\u4f60 \u7cbe\u795e\u5982\u4f55\uff0c\u80fd\u7528\u5251\u5417\uff1f\u201d \u3000\u3000\u4e91\u857e\u9053\uff1a\u201c\u8fd8\u53ef\u5bf9\u4ed8\u3002\u5468\u5927\u54e5\uff0c\u4f60\u518d\u7ed9\u6211\u8bf4\u8bf4\u7eff\u6797\u7bad\u4e4b\u4e8b\u5427\u3002\u201d\u5468\u5c71\u6c11\u60f3\u4e0d\u5230\u5979\u5728\u8fd9\u4e2a \u65f6\u5019\u8fd8\u4f1a\u548c\u4ed6\u95f2\u804a\uff0c\u8be7\u9053\uff1a\u201c\u7eff\u6797\u7bad\u8fd9\u65f6\u60f3\u5df2\u4f20\u5404\u5730\uff0c\u8fd8\u6709\u4ec0\u4e48\u53ef\u8bf4\u7684\u5462\uff1f\u201d\u4e91\u857e\u9053\uff1a\u201c\u8fd9 \u5c71\u897f\u4e00\u7701\uff0c\u6709\u54ea\u4e9b\u5389\u5bb3\u7684\u7eff\u6797\u82f1\u96c4\uff1f\u201d\u5468\u5c71\u6c11\u7b11\u9053\uff1a\u201c\u54e6\uff0c\u4f60\u662f\u62c5\u5fc3\u62a5\u4e0d\u4e86\u4ec7\u5417\uff1f\u5c71\u897f\u7701\u7684 \u7eff\u6797\u9ad8\u624b\u53ef\u591a\u7740\u5462\uff01\u554a\uff0c\u6211\u8fd8\u5fd8\u4e86\u544a\u8bc9\u4f60\u4e00\u4e8b\uff0c\u4f60\u7684\u4e8c\u5e08\u4f2f\u6f6e\u97f3\u5927\u5e08\u65b0\u8fd1\u4ece\u8499\u53e4\u5f52\u6765\u6b63\u5728\u6b64 \u5730\uff0c\u53ea\u6015\u4ed6\u4e5f\u77e5\u9053\u6211\u4eec\u4f20\u7eff\u6797\u7bad\u4e4b\u4e8b\u4e86\u3002\u201d\u4e91\u857e\u5947\u9053\uff1a\u201c\u662f\u5417\uff1f\u4ed6\u51e0\u65f6\u5230\u4e86\u8499\u53e4\uff1f\u4f60\u78b0\u89c1\u4ed6 \u5417\uff1f\u201d\u5468\u5c71\u6c11\u9053\uff1a\u201c\u6211\u6ca1\u78b0\u89c1\uff0c\u542c\u4eba\u8bf4\u7684\u3002\u55ef\uff0c\u4e0d\u8981\u54cd\uff0c\u4f60\u542c\uff0c\u6709\u4eba\u5728\u5916\u9762\u53eb\u4f60\uff01\u201d\u8bdd\u58f0\u4e00 \u505c\uff0c\u679c\u7136\u542c\u5f97\u6709\u4eba\u5728\u5916\u9762\u53eb\u9053\uff1a\u201c\u4e91\u857e\uff0c\u4e91\u857e\uff01\u201d\u8fd9\u6b63\u662f\u77f3\u7fe0\u51e4\u7684\u58f0\u97f3\uff0c\u4e91\u857e\u6014\u4e86\u4e00\u6014\uff0c\u6b63 \u60f3\u8bf4\u9053\uff1a\u201c\u4e0d\u8981\u5f00\u95e8\uff01\u201d\u5468\u5c71\u6c11\u5374\u5df2\u628a\u5979\u653e\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u77f3\u7fe0\u51e4\u65cb\u98ce\u4e00\u822c\u5730\u98de\u8dd1\u8fdb\u6765\uff0c\u4e00\u89c1\u4e91\u857e\uff0c\u559c\u51fa\u671b\u5916\uff0c\u6b22\u58f0\u53eb\u9053\uff1a\u201c\u4e91\u76f8\u516c\uff0c\u4f60\u679c\u7136\u8fd8\u5728 \u6b64\u5730\uff01\u201d\u8bf4\u7740\uff0c\u8bf4\u7740\uff0c\u4e0d\u89c9\u6ef4\u4e0b\u6cea\u6765\uff0c\u53c8\u54ed\u53c8\u7b11\u3002\u5468\u5c71\u6c11\u9053\uff1a\u201c\u4e91\u76f8\u516c\u4f24\u52bf\u98ce\u597d\uff0c\u4f60\u4e0d\u8981\u5608 \u5435\u4ed6\u4e86\uff01\u201d\u77f3\u7fe0\u51e4\u8fd9\u624d\u770b\u5230\u5468\u5c71\u6c11\u4e5f\u5728\u65c1\u8fb9\uff0c\u67f3\u7709\u4e00\u7ad6\uff0c\u6012\u9053\uff1a\u201c\u6211\u4eec\u592b\u59bb\u4e4b\u4e8b\uff0c\u4f60\u7ba1\u5f97 \u7740\uff01\u201d\u4e0a\u524d\u9760\u8fd1\u4e91\u857e\u4f4e\u58f0\u95ee\u9053\uff1a\u201c\u4e91\u76f8\u516c\uff0c\u4f60\u7740\u4e86\u9ed1\u767d\u6469\u8bc3\u7684\u6bd2\u624b\u4e48\uff1f\u201d\u4e91\u857e\u9053\uff1a\u201c\u4f60\u4e0d\u7528 \u62c5\u5fc3\uff0c\u73b0\u5728\u5df2\u7ecf\u5168\u597d\u4e86\u3002\u201d\u8f7b\u8f7b\u62c9\u8d77\u77f3\u7fe0\u51e4\u7684\u624b\uff0c\u9053\uff1a\u201c\u5468\u5927\u54e5\u8bf4\u5f97\u4e0d\u9519\uff0c\u6211\u662f\u60f3\u6b47\u4e00\u4f1a \u513f\uff0c\u4f60\u770b\uff0c\u5929\u8272\u5df2\u7ecf\u665a\u4e86\u3002\u201d\u77f3\u7fe0\u51e4\u9762\u8272\u6da8\u7ea2\uff0c\u5fc3\u4e2d\u6012\u9053\uff1a\u201c\u4f60\u5c31\u5e2e\u7740\u4f60\u4e49\u5144\uff0c\u5168\u4e0d\u628a\u6211\u653e \u5728\u5fc3\u4e0a\u3002\u201d\u53ef\u662f\u4e91\u857e\u65e2\u7136\u5982\u6b64\u8bf4\u6cd5\uff0c\u5979\u4e5f\u4e0d\u597d\u53d1\u4f5c\u51fa\u6765\u3002 \u3000\u3000\u5468\u5c71\u6c11\u5728\u65c1\u8fb9\u5657\u55e4\u4e00\u7b11\uff0c\u77f3\u7fe0\u51e4\u6a2a\u4ed6\u4e00\u773c\uff0c\u9053\uff1a\u201c\u4f60\u7b11\u4ec0\u4e48\uff1f\u201d\u4e91\u857e\u63d2\u53e3\u9053\uff1a\u201c\u6211\u809a\u5b50 \u997f\u5566\uff0c\u77f3\u59d1\u5a18\u9ebb\u70e6\u4f60\u7ed9\u6211\u5f04\u996d\uff0c\u8fd9\u91cc\u6709\u7c73\uff0c\u8fd8\u6709\u8089\u812f\u548c\u814a\u7f8a\u817f\u3002\u6211\u6682\u65f6\u6b47\u4e00\u6b47\uff0c\u996d\u719f\u4e86\u4f60\u518d \u53eb\u6211\u3002\u201d\u81ea\u987e\u81ea\u5730\u8d70\u8fdb\u5bc6\u5ba4\uff0c\u5468\u5c71\u6c11\u4e5f\u60f3\u8ddf\u7740\u8fdb\u53bb\uff0c\u521a\u521a\u8d70\u4e86\u4e24\u6b65\uff0c\u77f3\u7fe0\u51e4\u5ffd\u7136\u6012\u58f0\u53eb\u9053\uff1a \u201c\u5582\uff0c\u4f60\u6765\u5e2e\u6211\u5012\u6c34\u6d17\u7c73\uff01\u201d\u5468\u5c71\u6c11\u597d\u4e0d\u5c34\u5c2c\uff0c\u53ea\u597d\u9000\u51fa\uff0c\u4e91\u857e\u5411\u4ed6\u5fae\u5fae\u4e00\u7b11\uff0c\u597d\u50cf\u5c0f\u5b69\u5b50 \u505a\u4e86\u4e00\u4ef6\u6076\u4f5c\u5267\uff0c\u751a\u4e3a\u5f97\u610f\u3002 \u3000\u3000\u5468\u5c71\u6c11\u95f7\u58f0\u4e0d\u54cd\u5730\u5e2e\u77f3\u7fe0\u51e4\u6d17\u7c73\u3001\u751f\u706b\u3001\u5f04\u996d\uff0c\u77f3\u7fe0\u51e4\u4e5f\u95f7\u58f0\u4e0d\u54cd\uff0c\u6beb\u4e0d\u7406\u776c\u4e8e\u4ed6\uff0c\u663e \u7136\u8fd8\u5728\u751f\u6c14\u3002\u4e91\u857e\u5728\u5bc6\u5ba4\u91cc\u72ec\u81ea\u601d\u91cf\uff0c\u5728\u60f3\u600e\u6837\u5c06\u4ed6\u4eec\u64ae\u5408\uff0c\u542c\u5916\u9762\u4e24\u4eba\u6beb\u4e0d\u4ea4\u8c08\u4e0d\u89c9\u6697 \u7b11\uff1a\u4e0d\u662f\u51a4\u5bb6\u4e0d\u805a\u5934\uff0c\u7fe0\u51e4\u5982\u6b64\u6068\u4ed6\uff0c\u60f3\u5fc5\u662f\u4ee5\u4e3a\u6211\u504f\u5411\u4e49\u5144\uff0c\u6545\u6b64\uff0c\u5bf9\u4ed6\u5fc3\u6709\u82a5\u8482\uff0c\u82e5\u7136 \u5979\u77e5\u9053\u6211\u548c\u4ed6\u540c\u662f\u4e00\u6837\u7684\u5973\u513f\u8eab\u4efd\uff0c\u5c82\u4e0d\u8981\u54d1\u7136\u5931\u7b11\uff1f\u5634\u91cc\u5480\u56bc\u7740\u201c\u4e0d\u662f\u51a4\u5bb6\u4e0d\u805a\u5934\u201d\u8fd9\u53e5 \u8bf4\u8bdd\uff0c\u5ffd\u7136\u60f3\u8d77\u81ea\u5df1\u4e0e\u5f20\u4e39\u67ab\u521d\u89c1\u4e4b\u65f6\uff0c\u4e5f\u662f\u5bf9\u4ed6\u618e\u538c\uff0c\u53c8\u4e0d\u89c9\u8f7b\u8f7b\u53f9\u4e86\u53e3\u6c14\u3002 \u3000\u3000\u4e91\u857e\u80e1\u601d\u4e71\u60f3\uff0c\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u5c11\u65f6\u5019\uff0c\u542c\u5f97\u77f3\u7fe0\u51e4\u6572\u95e8\u53eb\u9053\uff1a\u201c\u4e91\u76f8\u516c\uff0c\u996d\u719f\u5566\uff01\u201d\u4e91 \u857e\u8fd9\u624d\u5982\u68a6\u521d\u9192\uff0c\u5f00\u95e8\u51fa\u6765\uff0c\u4e00\u773c\u77a5\u89c1\u77f3\u7fe0\u51e4\u548c\u5468\u5c71\u6c11\u4e92\u4e0d\u7406\u776c\u7684\u5c34\u5c2c\u6a21\u6837\uff0c\u4e0d\u89c9\u53c8\u5931\u58f0\u7b11 \u4e86\u51fa\u6765\u3002 \u3000\u3000\u77f3\u7fe0\u51e4\u548c\u5468\u5c71\u6c11\u90fd\u62a2\u7740\u66ff\u4e91\u857e\u76db\u996d\uff0c\u77f3\u7fe0\u51e4\u53c8\u6a2a\u4e86\u5468\u5c71\u6c11\u4e00\u773c\uff0c\u4e91\u857e\u5fae\u5fae\u4e00\u7b11\uff0c\u63a5\u8fc7\u4e86 \u77f3\u7fe0\u51e4\u9012\u6765\u7684\u996d\u7897\uff0c\u5468\u5c71\u6c11\u60f3\u8d77\u81ea\u5df1\u592a\u8fc7\u7740\u8ff9\uff0c\u5fc3\u6015\u4e91\u857e\u89c1\u7b11\uff0c\u9762\u4e0a\u53c8\u662f\u4e00\u7ea2\u3002\u4e91\u857e\u9053\uff1a \u201c\u7fe0\u51e4\uff0c\u6211\u8fd9\u4f4d\u5468\u5927\u54e5\u4e43\u662f\u65e5\u6708\u53cc\u65d7\u91d1\u5200\u5c11\u5be8\u4e3b\uff0c\u89c1\u591a\u8bc6\u5e7f\uff0c\u53c8\u662f\u6781\u597d\u7684\u597d\u4eba\uff0c\u4f60\u8be5\u591a\u5411\u4ed6 \u8bf7\u6559\u3002\u201d\u77f3\u7fe0\u51e4\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6211\u77e5\u9053\u3002\u4f60\u7684\u4e49\u5144\u81ea\u7136\u662f\u4e2a\u4e86\u4e0d\u5f97\u7684\u82f1\u96c4\u8c6a\u6770\uff0c\u8981\u4e0d \u7136\u4f60\u600e\u4f1a\u90a3\u6837\u542c\u4ed6\u7684\u8bf4\u8bdd\uff01\u201d \u3000\u3000\u5468\u5c71\u6c11\u5c34\u5c2c\u82e6\u7b11\uff0c\u4e91\u857e\u89e3\u5f00\u50f5\u5c40\uff0c\u7b11\u95ee\u77f3\u7fe0\u51e4\u9053\uff1a\u201c\u5468\u5927\u54e5\u8bf4\uff0c\u4f60\u90a3\u5929\u8d76\u7740\u56de\u5bb6\uff0c\u600e\u4e48 \u53c8\u51fa\u6765\u4e86\uff1f\u201d\u77f3\u7fe0\u51e4\u9053\uff1a\u201c\u6211\u56de\u5230\u5bb6\u540e\uff0c\u4e0d\u591a\u4e00\u4f1a\uff0c\u7239\u7239\u4e5f\u56de\u6765\u4e86\u3002\u4ed6\u9762\u8272\u975e\u5e38\u6c89\u91cd\uff0c\u597d\u4f3c \u6709\u4ec0\u4e48\u6781\u5927\u7684\u5fc3\u4e8b\u4e00\u822c\u3002\u6211\u95ee\u4ed6\u89c1\u7740\u4f60\u6ca1\u6709\uff0c\u4ed6\u8bf4\u6ca1\u6709\u89c1\u7740\uff0c\u4f46\u5df2\u786e\u5b9e\u77e5\u9053\u4f60\u8fd8\u5728\u9ed1\u767d\u6469\u8bc3 \u7684\u53e4\u5893\u4e4b\u4e2d\uff0c\u4e0d\u8fc7\u6709\u4eba\u4e0d\u8bb8\u4ed6\u89c1\u5230\u4f60\u3002\u6211\u542c\u4e86\u975e\u5e38\u5947\u602a\u3002\u201d \u3000\u3000\u5468\u5c71\u6c11\u4e5f\u89c9\u5341\u5206\u5947\u602a\uff0c\u5fcd\u4e0d\u4f4f\u63d2\u53e3\u8bf4\u9053\uff1a\u201c\u4f60\u7239\u7239\u6b66\u529f\u8d85\u5353\uff0c\u5a01\u9707\u7eff\u6797\uff0c\u8c01\u6562\u62e6\u963b\uff1f\u201d \u77f3\u7fe0\u51e4\u542c\u4ed6\u79f0\u8d5e\u81ea\u5df1\u7236\u4eb2\uff0c\u5bf9\u4ed6\u6076\u611f\u7a0d\u51cf\uff0c\u5374\u4ecd\u662f\u504f\u7740\u5934\u5bf9\u4e91\u857e\u9053\uff1a\u201c\u6211\u518d\u4e09\u95ee\u7239\u7239\uff0c\u90a3\u662f \u8c01\u4eba\uff0c\u7239\u7239\u603b\u4e0d\u80af\u8bf4\uff0c\u53ea\u8bf4\u4ed6\u5929\u4e0d\u6015\uff0c\u5730\u4e0d\u6015\uff0c\u53ea\u662f\u90a3\u4eba\u7684\u8bf4\u8bdd\u4e0d\u80fd\u4e0d\u542c\u3002\u53c8\u8bf4\u90a3\u4eba\u8bf4\u8fc7\u6211 \u7684\u5a5a\u4e8b\u5305\u5728\u4ed6\u548c\u4e91\u76f8\u516c\u7684\u8eab\u4e0a\uff0c\u6240\u4ee5\u53eb\u6211\u4e0d\u8981\u5fc3\u70e6\u3002\u201d\u8bf4\u81f3\u6b64\u5904\uff0c\u77f3\u7fe0\u51e4\u4e24\u988a\u98de\u7ea2\uff0c\u4f4e\u5934\u5f04 \u8863\uff0c\u4e0d\u6562\u548c\u4e91\u857e\u7684\u76ee\u5149\u76f8\u63a5\u3002\u4e91\u857e\u5fc3\u4e2d\u6697\u7b11\uff0c\u53c8\u662f\u6b22\u559c\u53c8\u662f\u60b2\u54c0\u3002\u6697\u7b11\u77f3\u7fe0\u51e4\u7684\u90a3\u7247\u5973\u513f\u7f9e \u6001\uff1b\u6b22\u559c\u77f3\u82f1\u5bf9\u5f20\u4e39\u67ab\u7684\u5c0a\u5d07\uff1b\u60b2\u54c0\u7684\u5374\u662f\u81ea\u5df1\u7684\u906d\u9047\u3002\u5979\u5df2\u77e5\u9053\u77f3\u82f1\u6240\u8bf4\u7684\u90a3\u4eba\u4e43\u662f\u5f20\u4e39 \u67ab\uff0c\u4f46\u5374\u4e0d\u613f\u660e\u8bf4\u51fa\u6765\u3002 \u3000\u3000\u77f3\u7fe0\u51e4\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u8fd9\u5341\u591a\u5929\u6765\uff0c\u6211\u7239\u7239\u884c\u4e8b\u5341\u5206\u53e4\u602a\uff0c\u5e73\u65e5\u4ed6\u6709\u4ec0\u4e48\u4e8b\u90fd\u548c\u6211\u8bf4\uff0c\u8fd9 \u5341\u591a\u5929\u6765\uff0c\u5374\u4e8b\u4e8b\u90fd\u7792\u7740\u6211\uff0c\u90a3\u767d\u9a6c\u5c0f\u8d3c\u7684\u6765\u5386\uff0c\u90a3\u5f20\u56fe\u753b\u7684\u6765\u5386\uff0c\u4ee5\u53ca\u62e6\u963b\u4ed6\u7684\u4eba\u662f\u8c01\uff0c \u8fd9\u79cd\u79cd\u602a\u4e8b\uff0c\u90fd\u4e0d\u80af\u5411\u6211\u900f\u9732\u534a\u70b9\u3002\u6211\u751f\u6c14\u4ed6\u4e5f\u4e0d\u7406\uff0c\u5374\u8981\u6211\u7acb\u523b\u66ff\u4ed6\u9001\u4fe1\u3002\u201d\u4e91\u857e\u5947\u9053\uff1a \u201c\u9001\u4fe1\uff0c\u9001\u4e0e\u8c01\u4eba\uff1f\u201d\u77f3\u7fe0\u51e4\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u9001\u7ed9\u4e00\u4e2a\u6c5f\u6e56\u4e0a\u5927\u540d\u9f0e\u9f0e\u7684\u5947\u4eba\uff0c\u8fd9\u65f6\u4e0d\u5148 \u8bf4\u4e0e\u4f60\u77e5\uff0c\u4f60\u82e5\u613f\u610f\u89c1\u90a3\u5947\u4eba\uff0c\u660e\u65e5\u4e0e\u6211\u540c\u53bb\u3002\u201d\u5468\u5c71\u6c11\u9053\uff1a\u201c\u5c71\u897f\u7701\u5185\u6709\u4ec0\u4e48\u5927\u540d\u9f0e\u9f0e\u7684 \u5947\u4eba\uff1f\u662f\u84dd\u5927\u4fa0\u5417\uff1f\u662f\u90dd\u5e84\u4e3b\u5417\uff1f\u662f\u2026\u2026\u201d\u77f3\u7fe0\u51e4\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u522b\u80e1\u731c\u5566\uff0c\u4f60\u867d\u7136 \u662f\u5927\u540d\u9f0e\u9f0e\u7684\u91d1\u5200\u5c11\u5be8\u4e3b\uff0c\u4e5f\u4e0d\u89c1\u5f97\u80fd\u8bc6\u904d\u6c5f\u6e56\u4e0a\u7684\u5947\u4eba\u3002\u201d\u5468\u5c71\u6c11\u78b0\u4e86\u4e00\u4e2a\u9489\u5b50\uff0c\u95f7\u58f0\u4e0d \u54cd\uff0c\u4e91\u857e\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u522b\u5c3d\u62ac\u6760\u5566\u3002\u8fd9\u4e48\u8bf4\uff0c\u660e\u5929\u6211\u4e0e\u5468\u5927\u54e5\u90fd\u8ddf\u4f60\u53bb\u3002\u65f6\u5019\u4e0d\u65e9\uff0c\u6211\u8981\u7761 \u5566\u3002\u201d\u63a8\u5f00\u5c0f\u95e8\uff0c\u8d70\u8fdb\u5bc6\u5ba4\u3002 \u3000\u3000\u77f3\u7fe0\u51e4\u7565\u4e00\u8fdf\u7591\uff0c\u4e5f\u8ddf\u7740\u8d70\u4e86\u8fdb\u53bb\uff0c\u4e91\u857e\u67d4\u58f0\u8bf4\u9053\uff1a\u201c\u51e4\u59d0\u59d0\uff0c\u90a3\u8fb9\u8fd8\u6709\u4e00\u95f4\u623f\u5b50\u3002\u201d \u77f3\u7fe0\u51e4\u53c8\u7f9e\u53c8\u6c14\uff0c\u7ad9\u5b9a\u811a\u6b65\uff0c\u6b63\u60f3\u8bf4\u8bdd\uff0c\u53ea\u542c\u5f97\u5468\u5c71\u6c11\u53c8\u53eb\u9053\uff1a\u201c\u5440\uff01\u8fd9\u53e4\u5893\u91cc\u9762\u771f\u662f\u522b\u6709 \u5929\u5730\uff0c\u6709\u5982\u5730\u4e0b\u5bab\u6bbf\u4e00\u822c\uff0c\u9664\u4e86\u8fd9\u4e2a\u5927\u5385\uff0c\u8fd8\u6709\u597d\u51e0\u95f4\u623f\u5b50\uff0c\u771f\u662f\u592a\u597d\u5566\u3002\u4f60\u4eec\u4e00\u4eba\u7761\u4e00\u95f4 \u623f\u5b50\uff0c\u6211\u7761\u5728\u5927\u5385\u66ff\u4f60\u4eec\u5b88\u591c\u3002\u8d24\u5f1f\uff0c\u4f60\u4f24\u52bf\u521d\u6108\u8fd8\u8981\u9759\u517b\uff0c\u65e9\u4e9b\u7761\u5427\uff0c\u4e0d\u8981\u52b3\u795e\u591a\u8bf4\u8bdd \u4e86\u3002\u201d\u77f3\u7fe0\u51e4\u9762\u7ea2\u76f4\u900f\u8033\u6839\uff0c\u970d\u5730\u8df3\u4e86\u51fa\u6765\uff0c\u53ea\u89c1\u5468\u5c71\u6c11\u4f3c\u7b11\u975e\u7b11\u7684\u773c\u671b\u7740\u5979\uff0c\u4e0d\u518d\u8a00\u8bed\u3002 \u77f3\u7fe0\u51e4\u6068\u4e0d\u5f97\u4e00\u5200\u628a\u4ed6\u5288\u4e3a\u4e24\u6bb5\uff0c\u6c14\u547c\u547c\u5730\u63a8\u5f00\u5de6\u8fb9\u5c0f\u623f\u7684\u623f\u95e8\uff0c\u597d\u534a\u591c\u8fd8\u7761\u4e0d\u7740\u3002 \u3000\u3000\u7b2c\u4e8c\u65e5\u4e00\u65e9\uff0c\u4e09\u4eba\u8d77\u6765\uff0c\u4e91\u857e\u548c\u5468\u3001\u77f3\u4e8c\u4eba\u70b9\u5934\u8bf4\u8bdd\uff0c\u4ed6\u4eec\u4e8c\u4eba\u5374\u662f\u4e92\u4e0d\u7406\u776c\u3002\u4e09\u4eba\u5f04 \u4e86\u65e9\u996d\uff0c\u5403\u8fc7\u4e4b\u540e\u6b63\u60f3\u51fa\u95e8\uff0c\u53ea\u542c\u5f97\u8fdc\u5904\u4e00\u58f0\u9a6c\u5636\uff0c\u5468\u5c71\u6c11\u8df3\u8d77\u6765\u9053\uff1a\u201c\u8fd9\u9a6c\u6765\u5f97\u597d\u5feb\uff01\u201d \u8bdd\u72b9\u672a\u4e86\u9a6c\u8e44\u4e4b\u58f0\u5df2\u662f\u8d8a\u6765\u8d8a\u8fd1\uff0c\u53c8\u662f\u4e24\u58f0\u957f\u5636\uff0c\u77f3\u7fe0\u51e4\u201c\u54a6\u201d\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u50cf\u662f\u90a3 \u5339\u767d\u9a6c\u7684\u53eb\u58f0\uff01\u201d\u4e91\u857e\u9762\u8272\u82cd\u767d\uff0c\u6447\u6447\u6b32\u5012\uff0c\u5468\u5c71\u6c11\u62d4\u5200\u53eb\u9053\uff1a\u201c\u597d\uff0c\u4ed6\u5012\u5148\u5bfb\u6211\u4eec\u6765\u4e86\uff0c \u5408\u529b\u6597\u4ed6\uff01\u201d\u4e91\u857e\u4f38\u624b\u62d4\u5251\uff0c\u624b\u6307\u98a4\u6296\uff0c\u5b9d\u5251\u8fd8\u672a\u51fa\u9798\u53ea\u542c\u5f97\u201c\u8f70\u9686\u201d\u5de8\u54cd\u77f3\u95e8\u5df2\u7ed9\u6765\u4eba\u649e \u5f00\uff0c\u6c99\u77f3\u98de\u626c\uff0c\u4e00\u5339\u767d\u9a6c\u98de\u5954\u800c\u5165\uff01 \u3000\u3000\u53ea\u542c\u5f97\u5468\u5c71\u6c11\u53eb\u4e86\u4e00\u58f0\uff0c\u62a2\u7740\u4e0a\u524d\u65bd\u793c\uff0c\u4e91\u857e\u5b9a\u775b\u4e00\u671b\uff0c\u90a3\u9a6c\u4e0a\u7684\u9a91\u5ba2\u5374\u4e0d\u662f\u81ea\u5df1\u610f\u6599 \u4e4b\u4e2d\u7684\u5f20\u4e39\u67ab\uff0c\u800c\u662f\u51fa\u4e4e\u81ea\u5df1\u610f\u6599\u4e4b\u5916\u7684\u6f6e\u97f3\u548c\u5c1a\uff0c\u4e00\u79cd\u7a81\u5982\u5176\u6765\u7684\u6b22\u559c\u4e0e\u5931\u671b\u4ea4\u7ec7\u5fc3\u5934\uff0c \u4ee4\u5f97\u4e91\u857e\u6014\u6014\u5730\u7ad9\u5728\u6f6e\u97f3\u9762\u524d\uff0c\u970e\u90a3\u4e4b\u95f4\uff0c\u8bf4\u4e0d\u51fa\u8bdd\u3002\u6f6e\u97f3\u548c\u5c1a\u89c1\u4e86\u5973\u626e\u7537\u88c5\u7684\u4e91\u857e\uff0c\u4e5f\u662f \u4e00\u6014\uff0c\u201c\u54a6\u201d\u7684\u4e00\u58f0\uff0c\u6b63\u60f3\u95ee\u8bdd\uff0c\u5468\u5c71\u6c11\u6025\u5fd9\u4e00\u626f\u6f6e\u97f3\u548c\u5c1a\u7684\u50e7\u888d\uff0c\u5c06\u4ed6\u62c9\u8fc7\u4e00\u8fb9\uff0c\u4f4e\u58f0\u8bf4 \u4e86\u51e0\u53e5\uff0c\u6f6e\u97f3\u548c\u5c1a\u731b\u7136\u54c8\u54c8\u5927\u7b11\uff0c\u5411\u4e91\u857e\u62db\u624b\u8bf4\u9053\uff1a\u201c\u857e\u513f\uff0c\u4f60\u8fc7\u6765\uff0c\u5f85\u6211\u4ed4\u7ec6\u770b\u770b\uff0c\u51e0\u5e74 \u4e0d\u89c1\u4f60\u5df2\u7ecf\u957f\u5927\u6210\u4eba\u5566\uff01\u201d\u4e91\u857e\u53eb\u4e86\u4e00\u58f0\u201c\u5e08\u4f2f\u201d\uff0c\u4e0a\u524d\u65bd\u793c\uff0c\u77f3\u7fe0\u51e4\u4e5f\u968f\u5728\u4e91\u857e\u540e\u9762\u4e0a\u524d \u8c12\u89c1\uff0c\u6f6e\u97f3\u548c\u5c1a\u53cc\u773c\u4e00\u7ffb\uff0c\u5411\u77f3\u7fe0\u51e4\u626b\u4e86\u4e00\u773c\uff0c\u5ffd\u800c\u7eb5\u58f0\u7b11\u9053\uff1a\u201c\u597d\u4fca\u7684\u5a18\u513f\uff01\u857e\u513f\uff0c\u4f60\u53ef \u4e0d\u80fd\u4e8f\u5f85\u4e8e\u5979\u3002\u201d\u77f3\u7fe0\u51e4\u88e3\u887d\u95ee\u597d\uff0c\u6f6e\u97f3\u5ffd\u53c8\u7b11\u9053\uff1a\u201c\u4eba\u957f\u5f97\u602a\u4fca\uff0c\u4e0d\u77e5\u4f60\u53ef\u4f1a\u5f04\u996d\u83dc\uff1f\u201d \u77f3\u7fe0\u51e4\u4e00\u6115\uff0c\u5468\u5c71\u6c11\u63a5\u53e3\u8bf4\u9053\uff1a\u201c\u5f1f\u5ac2\u806a\u660e\u6781\u5566\uff0c\u5c82\u6b62\u4f1a\u5f04\u996d\uff0c\u8fd8\u70e7\u5f97\u4e00\u624b\u597d\u5c0f\u83dc\u3002\u201d\u6f6e\u97f3 \u548c\u5c1a\u7b11\u9053\uff1a\u201c\u597d\u6781\uff0c\u597d\u6781\uff01\u6211\u4e24\u65e5\u4e4b\u95f4\uff0c\u8d70\u4e86\u4e03\u516b\u767e\u91cc\uff0c\u809a\u5b50\u997f\u6781\u5566\uff0c\u5feb\u7ed9\u6211\u53bb\u70e7\u83dc\u5f04 \u996d\uff01\u201d\u77f3\u7fe0\u51e4\u6115\u7136\u60f3\u9053\uff1a\u201c\u4f60\u809a\u5b50\u997f\u4e5f\u4e0d\u8be5\u5982\u6b64\u65e0\u793c\uff0c\u6211\u7239\u7239\u90fd\u4ece\u6ca1\u7528\u8fc7\u8fd9\u79cd\u53e3\u6c14\u5411\u6211\u5429 \u5490\u3002\u201d\u6f6e\u97f3\u548c\u5c1a\u628a\u9a6c\u7cfb\u597d\uff0c\u5927\u9a6c\u91d1\u5200\u7684\u5750\u4e86\u4e0b\u6765\uff0c\u53c8\u50ac\u4fc3\u9053\uff1a\u201c\u5c71\u6c11\u8d24\u4f84\uff0c\u4f60\u4e5f\u53bb\u5e2e\u5e2e\u6211\u7684 \u4f84\u5987\u5f04\u996d\uff0c\u653e\u4e09\u65a4\u7c73\u83dc\u4e0d\u8981\u592a\u591a\uff0c\u6709\u516d\u4e03\u6837\u4fbf\u6210\uff01\u201d\u6f6e\u97f3\u548c\u5c1a\u6beb\u4e0d\u5ba2\u6c14\u7684\u5dee\u9063\uff0c\u628a\u77f3\u7fe0\u51e4\u5f04 \u5f97\u54ed\u7b11\u4e0d\u5f97\uff0c\u5fc3\u9053\uff1a\u201c\u600e\u4e48\u4e91\u857e\u7684\u4e49\u5144\u3001\u5e08\u4f2f\uff0c\u5168\u90fd\u662f\u8fd9\u6837\u4e0d\u8fd1\u4eba\u60c5\u7684\u602a\u7269\uff01\u201d\u788d\u7740\u4e91\u857e\u60c5 \u9762\uff0c\u53ea\u597d\u6485\u7740\u5634\u513f\u5230\u91cc\u9762\u5f04\u996d\u3002 \u3000\u3000\u5468\u5c71\u6c11\u4ea6\u6b65\u4ea6\u8d8b\u5730\u4e5f\u8ddf\u4e86\u8fdb\u6765\uff0c\u77f3\u7fe0\u51e4\u6c14\u607c\u4e4b\u6781\uff0c\u52c3\u7136\u53d1\u4f5c\uff0c\u6012\u58f0\u8bf4\u9053\uff1a\u201c\u4e0d\u8981\u4f60\u6765\u5e2e \u6211\u3002\u201d\u5468\u5c71\u6c11\u7b11\u9053\uff1a\u201c\u5618\uff0c\u5c0f\u58f0\u70b9\u3002\u4f60\u4e0d\u77e5\u9053\u4e91\u857e\u7684\u5e08\u4f2f\u662f\u4e2a\u51fa\u540d\u7684\u83bd\u548c\u5c1a\u5417\uff1f\u4f60\u82e5\u548c\u6211\u5728 \u8fd9\u91cc\u5435\u67b6\uff0c\u53eb\u4ed6\u77e5\u9053\uff0c\u4e00\u5b9a\u4f1a\u5728\u4e91\u857e\u9762\u524d\u8bf4\u4f60\u3002\u201d\u77f3\u7fe0\u51e4\u679c\u7136\u4e0d\u6562\u5927\u58f0\uff0c\u677f\u7740\u8138\u513f\uff0c\u7785\u4e86\u5468 \u5c71\u6c11\u4e00\u773c\u3002\u5468\u5c71\u6c11\u53c8\u7b11\u9053\uff1a\u201c\u518d\u8bf4\u90a3\u548c\u5c1a\u80c3\u53e3\u771f\u5927\uff0c\u4e03\u6837\u83dc\u8fd8\u8bf4\u4e0d\u591a\uff0c\u4f60\u4e00\u4e2a\u4eba\u5f04\u5f97\u4e86 \u5417\uff1f\u201d\u77f3\u7fe0\u51e4\u4e00\u60f3\u679c\u662f\u9053\u7406\uff0c\u53ea\u662f\u6c14\u6068\u4e0d\u8fc7\uff0c\u5f20\u5934\u51fa\u53bb\uff0c\u5bf9\u7740\u6f6e\u97f3\u548c\u5c1a\u7684\u80cc\u5f71\u72e0\u72e0\u5730\u5550\u4e86\u4e00 \u53e3\u3002\u5468\u5c71\u6c11\u53c8\u5618\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u4ed6\u4eec\u5e08\u4f84\u5728\u90a3\u91cc\u8bf4\u8bdd\uff0c\u4f60\u4e0d\u8981\u6253\u6270\u4ed6\u4eec\u3002\u8fd9\u4e2a\u83bd\u548c\u5c1a\u813e\u6c14\u5f53\u771f \u4e0d\u597d\uff0c\u4f60\u53ef\u8981\u5c0f\u5fc3\u3002\u201d\u77f3\u7fe0\u51e4\u6c14\u5f97\u51e0\u4e4e\u8981\u54ed\u51fa\u58f0\u6765\uff0c\u6012\u9053\uff1a\u201c\u597d\u5440\uff0c\u4f60\u4eec\u5e08\u4f84\u5144\u5f1f\uff0c\u5c31\u6211\u4e00 \u4e2a\u662f\u2018\u5916\u4eba\u2019\uff0c\u6211\u53bb\u95ee\u4e91\u857e\u53bb\uff01\u201d\u5916\u9762\u6f6e\u97f3\u548c\u5c1a\u731b\u7136\u54b3\u4e86\u4e00\u58f0\uff0c\u77f3\u7fe0\u51e4\u8bf4\u8bf4\u800c\u5df2\uff0c\u53ef\u8fd8\u4e0d\u6562 \u771f\u7684\u53d1\u4f5c\uff0c\u53ea\u597d\u4e0e\u5468\u5c71\u6c11\u4e00\u9053\u70e7\u83dc\u5f04\u996d\u3002 \u3000\u3000\u5468\u5c71\u6c11\u5fc3\u4e2d\u6697\u7b11\uff0c\u4ed6\u662f\u6545\u610f\u505a\u597d\u505a\u574f\uff0c\u597d\u8ba9\u6f6e\u97f3\u548c\u5c1a\u4e0e\u4e91\u857e\u4e00\u9053\u653e\u5fc3\u8bf4\u8bdd\u3002\u6b8a\u4e0d\u77e5\u4e91\u857e \u5374\u4e5f\u662f\u522b\u6709\u5fc3\u601d\uff0c\u597d\u8ba9\u5468\u5c71\u6c11\u591a\u548c\u77f3\u7fe0\u51e4\u4e00\u8d77\u3002\u5468\u3001\u77f3\u4e8c\u4eba\u8fdb\u5165\u91cc\u9762\u5f04\u996d\u4e4b\u9645\uff0c\u5979\u4fbf\u5c06\u5728\u9ed1 \u77f3\u5e84\u5165\u8d58\u4e4b\u4e8b\uff0c\u7ec6\u8bf4\u4e0e\u5e08\u4f2f\u77e5\u9053\uff0c\u628a\u6f6e\u97f3\u548c\u5c1a\u5f04\u5f97\u7b11\u4e2a\u4e0d\u505c\u3002\u7b11\u5b8c\u4e4b\u540e\uff0c\u5ffd\u7136\u6b63\u8272\u8bf4\u9053\uff1a \u201c\u4f60\u5012\u5f00\u5fc3\uff0c\u6211\u53ef\u4e3a\u4f60\u5728\u8499\u53e4\u6c14\u5f97\u6b7b\u53bb\u6d3b\u6765\uff01\u201d \u3000\u3000\u4e91\u857e\u5403\u4e86\u4e00\u60ca\uff0c\u53ea\u542c\u5f97\u6f6e\u97f3\u548c\u5c1a\u95ee\u9053\uff1a\u201c\u857e\u513f\uff0c\u4f60\u8fd8\u8bb0\u5f97\u4f60\u662f\u54ea\u4e00\u5e74\u548c\u7237\u7237\u56de\u5230\u4e2d\u56fd\u7684 \u5417\uff1f\u201d\u4e91\u857e\u9053\uff1a\u201c\u8bb0\u5f97\uff0c\u90a3\u662f\u6b63\u7edf\u4e09\u5e74\u3002\u201d\u6f6e\u97f3\u9053\uff1a\u201c\u4eca\u5e74\u5462\uff1f\u201d\u4e91\u857e\u9053\uff1a\u201c\u4eca\u5e74\u662f\u6b63\u7edf\u5341 \u4e09\u5e74\u3002\u201d\u6f6e\u97f3\u548c\u5c1a\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u597d\u5feb\u554a\uff0c\u7728\u4e00\u7728\u773c\u4fbf\u662f\u6574\u6574\u5341\u5e74\u3002\u5341\u5e74\u4e4b\u524d\uff0c\u6211\u548c\u4f60\u7684\u4e09 \u5e08\u4f2f\u8c22\u5929\u534e\u5728\u96c1\u95e8\u5173\u5916\u51fb\u638c\u7acb\u8a93\uff0c\u4e00\u4e2a\u629a\u5b64\uff0c\u4e00\u4e2a\u62a5\u4ec7\u3002\u6211\u8d1f\u8d23\u5c06\u4f60\u5e26\u56de\u5c0f\u5bd2\u5c71\u4ea4\u7ed9\u56db\u59b9\u629a \u517b\uff0c\u4ed6\u8d1f\u8d23\u8fdc\u8d74\u8499\u53e4\uff0c\u5c06\u5978\u8d3c\u5f20\u5b97\u5468\u523a\u6740\uff0c\u4e3a\u4f60\u590d\u4ec7\u3002\u8fd9\u4e8b\u60c5\u4f60\u5e08\u7236\u60f3\u5fc5\u65e9\u5df2\u5bf9\u4f60\u8bf4\u4e86\uff1f\u201d \u3000\u3000\u4e91\u857e\u76ee\u6709\u6cea\u5149\uff0c\u7b54\u9053\uff1a\u201c\u65e9\u5df2\u8bf4\u4e86\uff0c\u591a\u8c22\u5e08\u4f2f\u4eec\u4e3a\u6211\u64cd\u5fc3\u4e86\u3002\u201d\u6f6e\u97f3\u548c\u5c1a\u53c8\u53f9\u53e3\u6c14\u9053\uff1a \u201c\u4f60\u591a\u8c22\u5f97\u592a\u65e9\u4e86\u3002\u201d\u987f\u4e86\u4e00\u987f\u5f80\u4e0b\u8bf4\u9053\uff1a\u201c\u6211\u4e0e\u5929\u534e\u5e08\u5f1f\u4ee5\u5341\u5e74\u4e3a\u671f\uff0c\u7ea6\u5b9a\u4eca\u5e74\u5728\u96c1\u95e8\u5173 \u5916\u4e00\u4e2a\u5730\u65b9\u76f8\u89c1\u3002\u4e0d\u6599\u5230\u671f\u4ed6\u5374\u4e0d\u6765\uff0c\u9053\u8def\u4f20\u8a00\u8bf4\u4ed6\u751f\u6b7b\u83ab\u535c\uff0c\u8fd8\u6709\u4eba\u8bf4\uff0c\u4ed6\u5df2\u88ab\u5f20\u5b97\u5468\u64d2 \u4e86\uff0c\u4e8e\u662f\u6211\u9042\u5339\u9a6c\u5355\u9a91\u8fdc\u8d74\u80e1\u8fb9\uff0c\u6df1\u5165\u74e6\u523a\u3002\u5929\u534e\u5f1f\u5982\u6709\u4e0d\u6d4b\uff0c\u8fd9\u62a5\u4ec7\u7684\u4e8b\u513f\u53ea\u597d\u7531\u6211\u62c5 \u627f\u3002\u201d \u3000\u3000\u4e91\u857e\u63d2\u53e3\u8bf4\u9053\uff1a\u201c\u6211\u5e08\u7236\u8bf4\u8c22\u5e08\u4f2f\u6b66\u529f\u5353\u7edd\uff0c\u667a\u52c7\u53cc\u5168\uff0c\u60f3\u6765\u8be5\u4e0d\u81f3\u4e8e\u906d\u4eba\u6bd2\u624b\uff1f\u201d\u6f6e \u97f3\u548c\u5c1a\u51b7\u51b7\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u8c22\u5929\u534e\u786e\u662f\u6b66\u529f\u5353\u7edd\uff0c\u8981\u4e0d\u7136\u6211\u5df2\u66ff\u4f60\u62a5\u4e86\u4ec7\u4e86\u3002\u201d\u4e91\u857e\u6115\u7136 \u9053\uff1a\u201c\u4e8c\u5e08\u4f2f\u6b64\u8bdd\uff0c\u4ee4\u4eba\u96be\u89e3\u3002\u201d\u6f6e\u97f3\u548c\u5c1a\u62cd\u7684\u4e00\u638c\uff0c\u5c06\u7389\u51e0\u780d\u6389\u4e00\u89d2\uff0c\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u4e5f \u662f\u5341\u5206\u4e0d\u89e3\u5440\uff01\u201d\u53c8\u662f\u4e00\u58f0\u957f\u53f9\uff0c\u5f80\u4e0b\u8bf4\u9053\uff1a\u201c\u6211\u6f5c\u5165\u74e6\u523a\uff0c\u6697\u4e2d\u6253\u542c\u591a\u65f6\uff0c\u603b\u6253\u542c\u4e0d\u51fa\u5929 \u534e\u5e08\u5f1f\u7684\u4e0b\u843d\uff0c\u60f3\u8981\u590d\u4ec7\uff0c\u90a3\u5f20\u5b97\u5468\u6709\u6fb9\u53f0\u706d\u660e\u4fdd\u62a4\u95e8\u7981\u53c8\u6781\u68ee\u4e25\uff0c\u7109\u80fd\u8f7b\u6613\u4e0b\u624b\uff1f\u6211\u5728\u74e6 \u523a\u5ea6\u65e5\u5982\u5e74\uff0c\u5fc3\u7126\u6781\u4e86\u3002\u4e0d\u610f\uff0c\u5230\u4e86\u4e0a\u4e00\u4e2a\u6708\uff0c\u5374\u5ffd\u542c\u5230\u4e00\u4e2a\u6d88\u606f\uff0c\u8bf4\u662f\u6fb9\u53f0\u706d\u660e\u5df2\u4e0d\u5728\u5f20 \u5b97\u5468\u7684\u5de6\u53f3\uff0c\u5927\u7ea6\u662f\u7ed9\u90a3\u5978\u8d3c\u5dee\u9063\u5230\u4ec0\u4e48\u5730\u65b9\u529e\u4e8b\u53bb\u4e86\u3002\u6211\u6253\u542c\u5c5e\u5b9e\uff0c\u4e8e\u662f\u9009\u62e9\u4e86\u4e00\u665a\u6708\u9ed1 \u98ce\u9ad8\u7684\u665a\u4e0a\uff0c\u5355\u8eab\u95ef\u5165\u5f20\u8d3c\u7684\u4e1e\u76f8\u5e9c\u3002\u201d \u3000\u3000\u201c\u90a3\u5f20\u8d3c\u7684\u4e1e\u76f8\u5e9c\u597d\u5927\uff0c\u4ed6\u4e5f\u771f\u4f1a\u4eab\u53d7\uff0c\u7adf\u5728\u6f20\u5317\u82e6\u5bd2\u4e4b\u5730\uff0c\u5efa\u8d77\u50cf\u6c5f\u5357\u4e00\u5e26\u7684\u56ed\u6797\uff0c \u76f8\u5e9c\u4e2d\u7684\u623f\u5c4b\uff0c\u4e5f\u90fd\u662f\u82cf\u676d\u4e24\u5730\u7684\u697c\u53f0\u4ead\u9601\u683c\u5f0f\u3002\u6211\u6478\u4e86\u534a\u591c\uff0c\u6349\u5230\u4e86\u4e00\u4e2a\u5c0f\u25a1\uff0c\u624d\u6253\u63a2\u51fa \u5f20\u8d3c\u4f4f\u5728\u82b1\u56ed\u4e1c\u89d2\u7684\u4e00\u5ea7\u697c\u4e2d\u3002\u201d \u3000\u3000\u201c\u8fd9\u65f6\u5df2\u662f\u4e94\u66f4\u65f6\u5206\uff0c\u53ef\u602a\u5f97\u5f88\uff0c\u5f20\u8d3c\u7adf\u7136\u8fd8\u672a\u7761\u89c9\uff0c\u72ec\u81ea\u5750\u5728\u623f\u4e2d\u5199\u5b57\uff0c\u4f4e\u9996\u6325\u6beb\uff0c \u4e1d\u6beb\u6ca1\u6709\u6ce8\u610f\u5230\u7a97\u5916\u6709\u4eba\u8981\u53d6\u4ed6\u7684\u6027\u547d\u3002\u6211\u638c\u5fc3\u65e9\u5df2\u6263\u4e86\u4e09\u679a\u91d1\u94b1\u9556\uff0c\u4e00\u770b\u673a\u4e0d\u53ef\u5931\uff0c\u7acb\u523b \u7528\u8fde\u73e0\u624b\u6cd5\uff0c\u53d6\u4ed6\u2018\u5c06\u53f0\u2019\u3001\u2018\u7487\u7391\u2019\u3001\u2018\u91d1\u6cc9\u2019\u4e09\u9053\u5927\u7a74\u3002\u6211\u7684\u94b1\u9556\u5728\u4e09\u4e08\u4e4b\u5185\uff0c\u767e\u53d1\u767e \u4e2d\uff0c\u83ab\u8bf4\u4ed6\u5728\u51dd\u795e\u5199\u5b57\uff0c\u5373\u7b97\u6b66\u827a\u9ad8\u5f3a\u4e4b\u8f88\uff0c\u6709\u6240\u9632\u5907\uff0c\u4e5f\u96be\u4ee5\u4e00\u4e00\u8eb2\u5f00\u3002\u201d \u3000\u3000\u201c\u4e0d\u6599\u94b1\u9556\u4e00\u53d1\uff0c\u53ea\u542c\u5f97\u53ee\uff0c\u53ee\uff0c\u53ee\uff0c\u8fde\u58f0\u75be\u54cd\uff0c\u4e09\u679a\u94b1\u9556\u90fd\u5728\u4ed6\u7684\u773c\u524d\u843d\u4e0b\u3002\u90a3\u623f\u4e2d \u6709\u590d\u58c1\u6697\u95e8\uff0c\u5f20\u8d3c\u8eab\u4e00\u9760\u5899\uff0c\u7acb\u523b\u8eb2\u4e86\u8fdb\u53bb\uff0c\u6211\u8df3\u8fdb\u53bb\u4e00\u6293\uff0c\u53ea\u6293\u7d27\u4ed6\u7684\u4e00\u5e45\u8863\u89d2\uff0c\u5c31\u5728\u5176 \u65f6\u6709\u4eba\u7a81\u7136\u8df3\u51fa\u4e00\u638c\u5c06\u6211\u63a8\u5f97\u4ec6\u5012\u684c\u4e0a\uff0c\u857e\u513f\u4f60\u731c\u90a3\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u4e91\u857e\u51b2\u53e3\u8bf4\u9053\uff1a\u201c\u83ab\u975e\u662f\u6fb9\u53f0\u706d\u660e\u6ca1\u6709\u5916\u51fa\u6545\u4f5c\u5708\u5957\uff1f\u201d\u8bf4\u4e86\u4e4b\u540e\uff0c\u731b\u7136\u60f3\u8d77\u4e0a\u6708\u6708 \u521d\uff0c\u81ea\u5df1\u5728\u96c1\u95e8\u5173\u5916\uff0c\u8fd8\u66fe\u548c\u91d1\u5200\u5468\u5065\u5408\u6218\u8fc7\u6fb9\u53f0\u706d\u660e\uff0c\u751a\u662f\u6000\u7591\uff0c\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u53ef\u662f\u6fb9\u53f0 \u706d\u660e\u600e\u80fd\u6709\u5206\u8eab\u4e4b\u672f\uff1f\u4f46\u82e5\u975e\u6fb9\u53f0\u706d\u660e\u53c8\u6709\u8c01\u6709\u90a3\u4e48\u9ad8\u7684\u6b66\u827a\uff1f\u201d \u3000\u3000\u6f6e\u97f3\u548c\u5c1a\u51b7\u51b7\u4e00\u7b11\uff0c\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u82e5\u662f\u6fb9\u53f0\u706d\u660e\uff0c\u90a3\u5012\u6beb\u4e0d\u8db3\u602a\uff0c\u8fd9\u4eba\u5374\u662f\u4e0e\u6211\u60c5\u5982\u624b \u8db3\u7684\u540c\u95e8\u5144\u5f1f\u8c22\u5929\u534e\uff01\u201d\u4e91\u857e\u60ca\u9053\uff1a\u201c\u662f\u4e09\u5e08\u4f2f\uff1f\u201d\u6f6e\u97f3\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u662f\u8c22\u5929\u534e\uff01\u8fd9\u624d\u628a\u6211 \u6c14\u5f97\u6b7b\u53bb\u6d3b\u6765\u3002\u6211\u559d\u95ee\u4ed6\u9053\uff1a\u2018\u5341\u5e74\u4e4b\u7ea6\uff0c\u4f60\u5fd8\u8bb0\u4e86\u5417\uff1f\u4f60\u662f\u590d\u4ec7\u8fd8\u662f\u4e8b\u4ec7\uff1f\u2019\u4ed6\u77aa\u6211\u4e00 \u773c\uff0c\u5237\u5237\u5237\uff0c\u4e00\u8fde\u4e09\u5251\uff0c\u5c06\u6211\u903c\u51fa\u5c4b\u5916\uff0c\u7d27\u7d27\u8ddf\u8e2a\u8ffd\u51fa\u3002\u5728\u540c\u95e8\u4e4b\u4e2d\uff0c\u4ed6\u7684\u6b66\u529f\u6700\u5f3a\uff0c\u6211\u660e \u77e5\u4e0d\u662f\u4ed6\u7684\u5bf9\u624b\uff0c\u53ef\u662f\u8fd9\u65f6\u6068\u6781\u6c14\u6781\uff0c\u53cd\u8f6c\u8eab\u6765\uff0c\u4fbf\u8981\u548c\u4ed6\u62fc\u547d\uff01\u201d \u3000\u3000\u201c\u53ef\u602a\u4ed6\u5728\u5c4b\u5185\u90a3\u6837\u72e0\u5fc3\uff0c\u5728\u5c4b\u5916\u5374\u5e76\u4e0d\u52a8\u624b\uff0c\u907f\u6211\u6570\u62db\u5374\u5ffd\u5730\u4f4e\u58f0\u8bf4\u9053\uff1a\u2018\u4f60\u77e5\u9053\u5f20 \u5b97\u5468\u662f\u4ec0\u4e48\u4eba\uff1f\u2019\u6211\u6012\u6781\u9a82\u9053\uff1a\u2018\u51ed\u4f60\u5982\u4f55\u8bf4\u6cd5\uff0c\u603b\u4e0d\u80fd\u628a\u5f20\u8d3c\u8bf4\u6210\u597d\u4eba\uff01\u2019\u5288\u9762\u53c8\u662f\u4e00 \u5200\uff0c\u8f7b\u8eab\u591c\u884c\uff0c\u4e0d\u4fbf\u643a\u5e26\u7985\u6756\uff0c\u6211\u5e26\u7684\u4e43\u662f\u77ed\u5200\uff0c\u4f7f\u6765\u751a\u4e0d\u8d81\u624b\uff0c\u54ea\u80fd\u65ab\u5f97\u4ed6\u7740\uff1f\u53ea\u65ab\u4e86\u4e24 \u5200\uff0c\u731b\u542c\u5f97\u4ed6\u4f4e\u8bf4\u4e86\u58f0\uff1a\u2018\u597d\u7cca\u6d82\u7684\u5e08\u5144\uff01\u2019\u5ffd\u5730\u6b3a\u8eab\u76f4\u8fdb\uff0c\u4e00\u4f38\u624b\u5c31\u70b9\u4e86\u6211\u7684\u8f6f\u9ebb\u7a74\uff0c\u5c06 \u6211\u80cc\u4e86\u8d77\u6765\u3002\u8fd9\u65f6\u76f8\u5e9c\u5185\u5df2\u662f\u4eba\u58f0\u9f0e\u6cb8\uff0c\u5b88\u591c\u7684\u6b66\u58eb\u90fd\u5df2\u60ca\u8d77\uff0c\u4ed6\u80cc\u7740\u6211\u7a9c\u9ad8\u7eb5\u4f4e\uff0c\u8f6c\u5f2f\u7ed5 \u89d2\uff0c\u8f6c\u77ac\u4e4b\u95f4\uff0c\u4fbf\u5230\u4e86\u56ed\u4e2d\u4e00\u4e2a\u9759\u50fb\u7684\u89d2\u843d\uff0c\u90a3\u91cc\u6709\u4e00\u4e2a\u7cbe\u81f4\u7684\u9a6c\u53a9\uff0c\u4ed6\u4ece\u9a6c\u53a9\u4e2d\u7275\u51fa\u4e00\u5339 \u767d\u9a6c\uff0c\u89e3\u5f00\u6211\u7684\u7a74\u9053\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u2018\u591a\u5e74\u5144\u5f1f\u96be\u9053\u4f60\u8fd8\u4e0d\u77e5\u6211\u7684\u4e3a\u4eba\uff1f\u5feb\u8d70\uff0c\u5feb\u8d70\uff01\u2019\u6211\u4e0d \u80af\u4e0a\u9a6c\uff0c\u5bf9\u4ed6\u8bf4\u9053\uff1a\u2018\u4f60\u82e5\u4e0d\u4e0e\u6211\u8bf4\u4e2a\u660e\u767d\uff0c\u6211\u51b3\u4e0d\u8d70\uff01\u2019\u4ed6\u9762\u8272\u4e00\u53d8\uff0c\u5ffd\u7136\u5389\u58f0\u8bf4\u9053\uff1a \u2018\u4f60\u82e5\u4e0d\u8d70\uff0c\u4f11\u602a\u6211\u624b\u4e0b\u65e0\u60c5\uff0c\u4e0d\u4f46\u8981\u8d70\u51fa\u76f8\u5e9c\uff0c\u6211\u9650\u4f60\u4e09\u65e5\u4e4b\u5185\uff0c\u79bb\u5f00\u8499\u53e4\uff0c\u5426\u5219\u53d6\u4f60\u6027 \u547d\uff01\u2019\u6211\u5927\u6012\u6325\u5200\u518d\u65a9\uff0c\u5200\u5374\u7ed9\u4ed6\u62a2\u53bb\u6298\u65ad\uff0c\u4e00\u4e0b\u5b50\u5c06\u6211\u629b\u4e0a\u9a6c\u80cc\uff0c\u559d\u9053\uff1a\u2018\u4f60\u771f\u7684\u4e0d\u60f3\u8981 \u547d\u4e86\u4e48\uff1f\u2019\u6211\u7edd\u6599\u60f3\u4e0d\u5230\u4ed6\u5982\u6b64\u53cd\u9762\u65e0\u60c5\uff0c\u81ea\u601d\uff1a\u4ed6\u65e2\u5982\u6b64\u5f03\u4fe1\u80cc\u4e49\uff0c\u6211\u767d\u9001\u4e86\u6027\u547d\uff0c\u6709\u8c01 \u77e5\u9053\u4ed6\u662f\u672c\u95e8\u53db\u5f92\uff1f\u4e0d\u5982\u6743\u4e14\u907f\u5f00\uff0c\u4ee5\u540e\u518d\u627e\u4ed6\u7b97\u5e10\u3002\u90a3\u5339\u767d\u9a6c\u795e\u9a8f\u975e\u51e1\uff0c\u4e0d\u542c\u4eba\u9a91\uff0c\u5e78\u800c \u6211\u8fd8\u6709\u70b9\u529f\u592b\uff0c\u5f3a\u529b\u5c06\u5b83\u5236\u670d\uff0c\u9a91\u9a6c\u51b2\u51fa\u76f8\u5e9c\uff0c\u80cc\u540e\u6570\u5341\u767e\u9a91\uff0c\u7eb7\u7eb7\u8ffd\u6765\uff0c\u58f0\u52bf\u6c79\u6c79\uff0c\u53ea\u542c \u5f97\u90a3\u4e9b\u4eba\u90fd\u5728\u559d\u9a82\uff1a\u2018\u597d\u5927\u80c6\u7684\u8d3c\u4eba\uff0c\u5c45\u7136\u6562\u5077\u4e86\u4e1e\u76f8\u7684\u5b9d\u9a6c\uff01\u2019\u54c8\uff0c\u539f\u6765\u8fd9\u767d\u9a6c\u7adf\u7136\u662f\u5f20 \u8d3c\u7684\u5750\u9a91\uff0c\u602a\u4e0d\u5f97\u5982\u6b64\u795e\u9a8f\uff0c\u5b83\u88ab\u6211\u5236\u670d\u4e4b\u540e\uff0c\u653e\u5f00\u56db\u8e44\u75be\u8dd1\uff0c\u771f\u5982\u8ffd\u4e91\u9010\u7535\u4e00\u822c\u4e0d\u6d88\u591a \u4e45\uff0c\u4fbf\u628a\u90a3\u4e9b\u4eba\u90fd\u6487\u5728\u540e\u9762\uff0c\u518d\u4e5f\u8ffd\u8d76\u4e0d\u4e0a\u3002\u90a3\u4e00\u665a\u6211\u867d\u7136\u88ab\u6c14\u5f97\u6b7b\u53bb\u6d3b\u6765\uff0c\u5374\u4e5f\u610f\u5916\u5730\u5f97 \u4e86\u4e00\u5339\u5b9d\u9a6c\u201d\u90a3\u5339\u767d\u9a6c\u5c31\u7cfb\u5728\u5385\u4e2d\uff0c\u4f3c\u4e4e\u77e5\u9053\u6f6e\u97f3\u548c\u5c1a\u8bf4\u5b83\uff0c\u53c8\u5636\u4e86\u4e00\u58f0\u3002\u4e91\u857e\u7ec6\u770b\uff0c\u8fd9\u5339 \u767d\u9a6c\u548c\u5f20\u4e39\u67ab\u90a3\u5339\u201c\u7167\u591c\u72ee\u5b50\u9a6c\u201d\u751a\u662f\u76f8\u50cf\uff0c\u53ea\u662f\u9888\u4e0a\u591a\u4e86\u4e00\u64ae\u9ec4\u8272\u7684\u9b03\u6bdb\uff0c\u60f3\u6765\u90fd\u662f\u540c\u4e00 \u9a6c\u79cd\u3002 \u3000\u3000\u6f6e\u97f3\u548c\u5c1a\u9053\uff1a\u201c\u857e\u513f\uff0c\u4f60\u5728\u51fa\u795e\u60f3\u4e9b\u4ec0\u4e48\uff1f\u201d\u4e91\u857e\u8bf4\u9053\uff1a\u201c\u4e09\u5e08\u4f2f\u82e5\u662f\u7518\u5fc3\u4e8b\u4ec7\uff0c\u53c8\u7109 \u80af\u5c06\u5f20\u5b97\u5468\u7684\u5b9d\u9a6c\u4e5f\u9001\u7ed9\u4f60\uff1f\u201d\u6f6e\u97f3\u9053\uff1a\u201c\u6240\u4ee5\u6211\u662f\u5341\u5206\u4e0d\u89e3\u5440\uff01\u82e5\u975e\u8fd9\u5339\u5b9d\u9a6c\uff0c\u6211\u4e5f\u9003\u4e0d \u51fa\u8499\u53e4\u3002\u201d\u4e91\u857e\u6447\u5934\u9053\uff1a\u201c\u6b64\u4e8b\u5b9e\u662f\u8d39\u4eba\u731c\u7591\uff01\u90a3\u5f20\u5b97\u5468\u662f\u4ec0\u4e48\u4eba\uff1f\u96be\u9053\uff0d\uff0d\u201d\u6f6e\u97f3\u201c\u556a\u201d \u7684\u4e00\u638c\uff0c\u53c8\u5c06\u7389\u51e0\u6253\u6389\u4e00\u89d2\u6012\u9053\uff1a\u201c\u90a3\u5f20\u5b97\u5468\u662f\u5978\u8d3c\u4e16\u5bb6\uff0c\u5386\u4ee3\u5728\u74e6\u523a\u4e3a\u5b98\uff0c\u52a9\u74e6\u523a\u6574\u519b\u7ecf \u6b66\uff0c\u56fe\u8c0b\u541e\u5e76\u4e2d\u534e\uff0c\u8fd9\u6837\u4e00\u4e2a\u5929\u4e0b\u7686\u77e5\u7684\u5927\u5978\u8d3c\uff0c\u4f60\u8bf4\u4ed6\u8fd8\u80fd\u662f\u597d\u4eba\u5417\uff1f\u201d\u4e91\u857e\u60f3\u8d77\u7237\u7237\u88ab \u6298\u78e8\uff0c\u5728\u51b0\u5929\u96ea\u91cc\u7267\u9a6c\u4e8c\u5341\u5e74\u4e4b\u4e8b\uff0c\u5fc3\u75db\u5982\u5272\uff0c\u98a4\u58f0\u8bf4\u9053\uff1a\u201c\u4ed6\u662f\u4e07\u6076\u4e0d\u8d66\u7684\u5978\u4eba\uff0c\u662f\u6211\u5bb6 \u7684\u5927\u4ec7\u4eba\uff01\u4f46\uff0c\u4f60\u770b\u4ed6\u662f\u4e0d\u662f\u53e6\u6709\u6765\u5386\uff1f\u201d\u6f6e\u97f3\u773c\u73e0\u4e00\u8f6c\uff0c\u5ffd\u7136\u4f3c\u60f3\u8d77\u4ec0\u4e48\u4e8b\u60c5\u4f3c\u7684\uff0c\u4ece\u888b \u4e2d\u638f\u51fa\u4e00\u4e2a\u7eb8\u56e2\uff0c\u5c55\u5f00\u8bf4\u9053\uff1a\u201c\u90a3\u665a\u6211\u884c\u523a\u5f20\u8d3c\uff0c\u4e00\u51fb\u4e0d\u4e2d\uff0c\u88ab\u5929\u534e\u4e00\u638c\u5c06\u6211\u63a8\u5f00\uff0c\u6070\u5de7\u4ec6 \u5012\u5728\u5f20\u8d3c\u7684\u4e66\u6848\u4e0a\uff0c\u6211\u968f\u624b\u4e00\u6293\uff0c\u62fe\u8d77\u4e86\u8fd9\u4e2a\u7eb8\u56e2\uff0c\u5c31\u662f\u90a3\u665a\u5f20\u8d3c\u6240\u5199\u7684\u3002\u6211\u60f3\u90a3\u5978\u8d3c\u6df1\u591c \u4e0d\u7720\uff0c\u6240\u5199\u7684\u53ef\u80fd\u662f\u4ec0\u4e48\u673a\u5bc6\u6587\u4e66\uff0c\u5c31\u628a\u5b83\u5e26\u56de\u6765\u4e86\u3002\u53ef\u6068\u4ed6\u5199\u5f97\u90a3\u4e48\u6f66\u8349\uff0c\u6211\u6597\u5927\u7684\u5b57\u867d \u8fd8\u8ba4\u5f97\u51e0\u4e2a\u5c31\u8ba4\u4e0d\u51fa\u8fd9\u9f9f\u513f\u5b50\u5199\u7684\u662f\u4ec0\u4e48\u4e1c\u897f\u3002\u4f60\u7ed9\u6211\u770b\u770b\uff0c\u6bcf\u4e00\u884c\u90fd\u662f\u4e03\u4e2a\u5b57\uff0c\u4e0d\u591a\u4e0d \u5c11\uff0c\u4e00\u5171\u53ea\u6709\u4e8c\u5341\u516b\u4e2a\u5b57\uff0c\u83ab\u975e\u4e0d\u662f\u4ec0\u4e48\u6587\u4e66\u662f\u4ec0\u4e48\u8bd7\u5440\u8bcd\u5440\u4e4b\u7c7b\u7684\u73a9\u610f\u5417\uff1f\u201d\u4e91\u857e\u5fcd\u4fca\u4e0d \u7981\uff0c\u5657\u55e4\u4e00\u7b11\uff0c\u5c06\u90a3\u5f20\u7eb8\u63a5\u4e86\u8fc7\u6765\uff0c\u7ec6\u7ec6\u4e00\u770b\uff0c\u6c89\u541f\u4e0d\u8bed\u3002\u6f6e\u97f3\u95ee\u9053\uff1a\u201c\u8fd9\u9f9f\u513f\u5b50\u5199\u7684\u662f\u4ec0 \u4e48\uff1f\u201d\u4e91\u857e\u9053\uff1a\u201c\u662f\u4e00\u9996\u8bd7\u3002\u201d\u5ff5\u9053\uff1a\u201c\u8c01\u628a\u82cf\u676d\u66f2\u5b50\u8bb4\uff1f\u8377\u82b1\u5341\u91cc\u6842\u4e09\u79cb\u3002\u54ea\u77e5\u5349\u6728\u65e0\u60c5 \u7269\uff0c\u7275\u52a8\u957f\u6c5f\u4e07\u53e4\u6101\uff01\u201d\u4e5f\u6b63\u662f\u5f20\u4e39\u67ab\u5c55\u56fe\u611f\u6168\uff0c\u66fe\u7ecf\u5bf9\u4e91\u857e\u541f\u8fc7\u7684\u90a3\u9996\u8bd7\u3002 \u3000\u3000\u6f6e\u97f3\u7709\u5934\u4e00\u76b1\uff0c\u9053\uff1a\u201c\u90a3\u5978\u8d3c\u6df1\u591c\u4e0d\u7720\uff0c\u5199\u7684\u5c31\u662f\u8fd9\u4e48\u6837\u7684\u4e00\u9996\u8bd7\u5417\uff1f\u4ec0\u4e48\u6101\u4e0d\u6101\u7684\uff0c \u957f\u6c5f\u600e\u4e48\u4f1a\u6101\u5462\uff1f\u54fc\uff0c\u4e0d\u901a\uff0c\u4e0d\u901a\uff01\u201d\u4e91\u857e\u5fcd\u4e0d\u7740\u53c8\u662f\u5657\u55e4\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u662f\u5b8b\u671d\u4e00\u4e2a\u540d\u8bd7 \u4eba\u7684\u8bd7\uff0c\u957f\u6c5f\u81ea\u53e4\u4ee5\u6765\u662f\u5357\u5317\u4ea4\u6218\u7684\u6218\u573a\uff0c\u6211\u770b\u8fd9\u9996\u8bd7\u611f\u6168\u5f88\u6df1\u5462\u3002\u201d\u6f6e\u97f3\u5c34\u5c2c\u7b11\u9053\uff1a\u201c\u90a3 \u4e48\u5c31\u7b97\u662f\u6211\u8fd9\u8001\u7c97\u4e0d\u901a\uff0c\u4f60\u7ed9\u6211\u8bf4\u4ed6\u5199\u8fd9\u9996\u8bd7\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d\u4e91\u857e\u6c89\u541f\u534a\u664c\uff0c\u5ffd\u9053\uff1a\u201c\u8fd9\u672c \u662f\u5b8b\u671d\u8c22\u5904\u539a\u5199\u7684\u4e00\u9996\u8bd7\uff0c\u4f46\u5934\u4e00\u53e5\u548c\u5c3e\u4e00\u53e5\u90fd\u7ed9\u5f20\u5b97\u5468\u6539\u4e86\u4e00\u4e2a\u5b57\u3002\u539f\u8bd7\u5934\u4e00\u53e5\u662f\uff1a\u2018\u8c01 \u628a\u676d\u5dde\u66f2\u5b50\u8bb4\uff1f\u2019\u7ed9\u4ed6\u6539\u6210\u2018\u82cf\u676d\u2019\u4e86\uff0c\u672b\u4e00\u53e5\u662f\u5c06\u2018\u5730\u57df\u4e4b\u6101\u2019\u6539\u4e3a\u2018\u65f6\u95f4\u4e4b\u6101\u2019\uff0c\u90a3\u662f \u4f24\u5fc3\u4eba\u522b\u6709\u6000\u62b1\uff0c\u4e0d\u5fc5\u53bb\u7406\u4f1a\u5b83\u3002\u672b\u4e00\u53e5\u672c\u662f\u2018\u4e07\u91cc\u6101\u2019\u7ed9\u4ed6\u6539\u6210\u4e86\u2018\u4e07\u53e4\u6101\u2019\uff0c\u5934\u4e00\u53e5\u672c \u6765\u53ea\u662f\u8bf4\u676d\u5dde\u7684\uff0c\u4ed6\u5374\u786c\u6dfb\u4e0a\u4e00\u4e2a\u82cf\u5dde\u8fd9\u53ef\u662f\u4e3a\u4ec0\u4e48\u5462\uff1f\u55ef\uff0c\u5b97\u5468\uff0c\u5b97\u5468\uff0c\u5b97\u5468\u2026\u2026\u201d\u6f6e\u97f3 \u5947\u602a\u9053\uff1a\u201c\u4f60\u5c3d\u5ff5\u8fd9\u6c49\u5978\u7684\u540d\u5b57\u505a\u4ec0\u4e48\uff1f\u201d\u4e91\u857e\u5ffd\u9053\uff1a\u201c\u4f60\u8bf4\u90a3\u5f20\u5b97\u5468\u7684\u76f8\u5e9c\uff0c\u5efa\u7b51\u6709\u50cf\u6c5f \u5357\u4e00\u5e26\u7684\u56ed\u6797\uff0c\u6211\u6ca1\u6709\u5230\u8fc7\u82cf\u5dde\uff0c\u4f46\u4ea6\u77e5\u82cf\u5dde\u7684\u56ed\u6797\u6700\u662f\u6709\u540d\uff0c\u4e0d\u77e5\u90a3\u5f20\u8d3c\u6240\u7ecf\u8425\u5efa\u7b51\u7684\uff0c \u662f\u4e0d\u662f\u4e0e\u82cf\u5dde\u7684\u56ed\u6797\u4e00\u4e2a\u6a21\u6837\uff1f\u201d\u6f6e\u97f3\u9053\uff1a\u201c\u6b63\u662f\u4e00\u6837\uff0c\u770b\u6765\u5f20\u8d3c\u7279\u522b\u559c\u7231\u82cf\u5dde\u3002\u201d\u4e91\u857e\u60f3 \u5f97\u51fa\u4e86\u795e\uff0c\u53c8\u4f4e\u5934\u5ff5\u9053\uff1a\u201c\u5b97\u5468\uff0c\u5b97\u5468\uff0c\u5b97\u5468\u2026\u2026\u201d \u3000\u3000\u6f6e\u97f3\u548c\u5c1a\u60ca\u9053\uff1a\u201c\u857e\u513f\uff0c\u4f60\u4e2d\u4e86\u90aa\u4e48\uff1f\u201d\u8fd9\u970e\u90a3\u95f4\uff0c\u5f20\u4e39\u67ab\u7ed9\u5979\u8bf4\u8fc7\u7684\u4e00\u4e2a\u6545\u4e8b\uff0c\u4ece\u5fc3 \u5934\u95ea\u8fc7\uff0c\u4e91\u857e\u7a81\u7136\u62ac\u8d77\u4e86\u5934\u9053\uff1a\u201c\u6211\u660e\u767d\u4e86\uff0c\u5f20\u5b97\u5468\u4e43\u662f\u5f20\u58eb\u8bda\u7684\u540e\u4ee3\uff01\u201d\u8fd9\u65f6\u8ddd\u6731\u5143\u748b\u5f00 \u56fd\u4e0d\u8fc7\u4e03\u516b\u5341\u5e74\uff0c\u5f20\u58eb\u8bda\u7684\u4e8b\u8ff9\u8fd8\u6d41\u4f20\u6c11\u95f4\uff0c\u6f6e\u97f3\u6014\u4e86\u4e00\u6014\u9053\uff1a\u201c\u5f20\u58eb\u8bda\uff1f\u5c31\u662f\u4e0e\u592a\u7956\u4e89\u593a \u6c5f\u5c71\u7684\u90a3\u4e2a\u5f20\u58eb\u8bda\u5417\uff1f\u201d\u4e91\u857e\u9053\uff1a\u201c\u5f20\u58eb\u8bda\u5728\u82cf\u5dde\u79f0\u5e1d\uff0c\u56fd\u53f7\u2018\u5927\u5468\u2019\uff0c\u5f20\u5b97\u5468\u7684\u540d\u5b57\uff0c\u4e0d \u662f\u660e\u660e\u8bf4\u51fa\u4ed6\u6240\u2018\u5b97\u2019\u7684\u4ecd\u662f\u4ed6\u7956\u5148\u6240\u5efa\u7684\u2018\u5927\u5468\u2019\uff0c\u800c\u4e0d\u662f\u6731\u5143\u748b\u6240\u5efa\u7684\u5927\u660e\u5417\uff1f\u201d\u6f6e\u97f3 \u548c\u5c1a\u5947\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u4e2b\u5934\uff0c\u600e\u4e48\u8f6c\u5f2f\u62b9\u89d2\u60f3\u5230\u8fd9\u4e48\u591a\u4e1c\u897f\uff1f\u597d\u50cf\u731c\u54d1\u8c1c\u4e00\u822c\u3002\u201d\u4e91\u857e\u4f4e\u9996\u6c89 \u601d\uff0c\u5bf9\u4ed6\u7684\u8bdd\uff0c\u5982\u542c\u800c\u4e0d\u95fb\u3002 \u3000\u3000\u6f6e\u97f3\u548c\u5c1a\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u7ba1\u4ed6\u662f\u4e0d\u662f\u5f20\u58eb\u8bda\u7684\u540e\u4ee3\uff0c\u4ed6\u52a9\u74e6\u523a\u5165\u4fb5\uff0c\u603b\u4e0d\u662f\u597d\u4e1c\u897f\uff01\u201d\u4e91 \u857e\u82e6\u607c\u4e07\u5206\uff0c\u9053\uff1a\u201c\u4e8c\u5e08\u4f2f\u8bf4\u7684\u662f\uff01\u201d\u5fc3\u4e2d\u518d\u7ffb\u8d77\u4e0e\u5f20\u4e39\u67ab\u4e00\u8def\u540c\u884c\u7684\u79cd\u79cd\u4e8b\u60c5\uff0c\u60f3\u9053\uff1a \u201c\u5f20\u4e39\u67ab\u575a\u51b3\u9003\u51fa\u8499\u53e4\uff0c\u60f3\u6765\u4e0d\u662f\u4ed6\u7236\u4eb2\u90a3\u4e00\u8def\u4eba\u3002\u4f46\u8c22\u5929\u534e\u5e08\u4f2f\u4fa0\u4e49\u540d\u4f20\u5929\u4e0b\uff0c\u82e5\u5f20\u5b97\u5468 \u679c\u662f\u4e07\u6076\u4e0d\u8d66\u7684\u5978\u8d3c\uff0c\u4ed6\u4e3a\u4f55\u4e0d\u5c06\u4ed6\u523a\u6740\uff0c\u53cd\u800c\u62a4\u4ed6\uff1f\u201d\u8fd9\u79cd\u79cd\u7591\u56e2\uff0c\u771f\u662f\u767e\u601d\u83ab\u89e3\u3002\u4f46\u4e0d \u7ba1\u5f20\u5b97\u5468\u3001\u5f20\u4e39\u67ab\u662f\u597d\u662f\u574f\uff0c\u4ed6\u4eec\u603b\u662f\u4e91\u5bb6\u7684\u5927\u4ec7\u4eba\uff0c\u662f\u4e91\u857e\u7237\u7237\u7559\u4e0b\u8840\u4e66\uff0c\u6307\u540d\u8981\u65a9\u5c3d\u6740 \u7edd\u7684\u4eba\uff01 \u3000\u3000\u6f6e\u97f3\u548c\u5c1a\u53f9\u4e86\u53e3\u6c14\uff0c\u53c8\u9053\uff1a\u201c\u6211\u7edd\u60f3\u4e0d\u5230\u5929\u534e\u5e08\u5f1f\u9b3c\u8ff7\u5fc3\u7a8d\uff0c\u5c45\u7136\u4f1a\u52a9\u8fd9\u5978\u8d3c\u3002\u6211\u5982\u4eca \u4e0e\u4ed6\u5144\u5f1f\u4e4b\u60c5\u5df2\u65ad\uff0c\u6b64\u6b21\u56de\u6765\uff0c\u5c31\u662f\u51c6\u5907\u53bb\u6073\u6c42\u5e08\u7956\uff0c\u8bf7\u4ed6\u63d0\u65e9\u4e09\u5e74\uff0c\u51c6\u4f60\u7684\u5e08\u7236\u4e0b\u5c71\u3002\u4f60 \u5e08\u7236\u7684\u6b66\u529f\u4e0e\u5929\u534e\u5728\u4f2f\u4ef2\u4e4b\u95f4\uff0c\u6211\u4e0e\u5979\u8054\u624b\uff0c\u90a3\u5c31\u5b9a\u80fd\u5c06\u4ed6\u6740\u6389\u5566\uff01\u201d\u4e91\u857e\u731b\u53c8\u60f3\u8d77\u81ea\u5df1\u4e0b \u5c71\u524d\u5915\uff0c\u5e08\u7236\u9762\u58c1\u5341\u5e74\uff0c\u8fd8\u5ff5\u5ff5\u4e0d\u5fd8\u5929\u534e\u5e08\u4f2f\uff0c\u53ef\u77e5\u4ed6\u4eec\u76f8\u7231\u4e4b\u6df1\uff0c\u82e5\u7136\u5e08\u7236\u77e5\u9053\u6b64\u4e8b\uff0c\u4e0d \u77e5\u9053\u591a\u4f24\u5fc3\u5462\uff01 \u3000\u3000\u6f6e\u97f3\u548c\u5c1a\u53c8\u7b11\u9053\uff1a\u201c\u4ed6\u9001\u6211\u8fd9\u5339\u9a6c\u6b63\u7528\u5f97\u7740\uff0c\u9a91\u5b83\u5230\u5c0f\u5bd2\u5c71\u53bb\uff0c\u7528\u4e0d\u4e86\u4e00\u4e2a\u6708\u5934\u3002\u8fd9\u771f \u662f\u4e00\u5339\u5b9d\u9a6c\u5440\uff0c\u54c8\uff0c\u54c8\uff01\u201d \u3000\u3000\u4e24\u4eba\u8c08\u4e86\u534a\u5929\uff0c\u77f3\u7fe0\u51e4\u4e0e\u5468\u5c71\u6c11\u5df2\u5728\u91cc\u9762\u5f04\u597d\u996d\u83dc\uff0c\u7aef\u4e86\u51fa\u6765\u3002\u5468\u5c71\u6c11\u5c06\u996d\u83dc\u653e\u597d\uff0c\u4e5f \u8dd1\u53bb\u7aef\u8be6\u90a3\u5339\u767d\u9a6c\uff0c\u5567\u5567\u7684\u8d5e\u8d4f\u4e0d\u4f11\uff0c\u6f6e\u97f3\u548c\u5c1a\u5927\u7897\u9152\u5927\u5757\u8089\u7684\u5012\u5165\u53e3\u4e2d\uff0c\u98ce\u5377\u6b8b\u4e91\uff0c\u4e0d\u6d88 \u7247\u523b\uff0c\u8fde\u90a3\u4e09\u65a4\u7c73\u996d\u4e5f\u5403\u4e2a\u7cbe\u5149\uff0c\u6413\u6413\u809a\u76ae\u7b11\u9053\uff1a\u201c\u597d\u4f84\u5ab3\u5987\uff0c\u4f60\u7684\u624b\u827a\u4e0d\u9519\u5440\uff01\u996d\u70e7\u5f97 \u9999\uff0c\u83dc\u4e5f\u505a\u5f97\u7f8e\uff01\u201d\u77f3\u7fe0\u51e4\u6c14\u5c1a\u672a\u6d88\u6de1\u6de1\u4e00\u7b11\uff0c\u6487\u8fc7\u5934\u770b\u90a3\u5b9d\u9a6c\u3002\u6f6e\u97f3\u548c\u5c1a\u53c8\u7b11\u9053\uff1a\u201c\u8fd9\u662f \u4e00\u5339\u5b9d\u9a6c\uff0c\u4f46\u8fd8\u6709\u6bd4\u5b83\u66f4\u597d\u7684\u5b9d\u9a6c\uff0c\u6211\u548c\u5c1a\u8fd9\u56de\u695e\u8ba4\u683d\u4e86\uff01\u201d\u5468\u5c71\u6c11\u5584\u4e8e\u76f8\u9a6c\uff0c\u5947\u9053\uff1a\u201c\u4ec0 \u4e48\uff0c\u8fd8\u6709\u6bd4\u5b83\u66f4\u597d\u7684\u9a6c\uff1f\u201d\u6f6e\u97f3\u8a00\u9053\uff1a\u201c\u662f\u5440\uff01\u4e16\u4e0a\u5c45\u7136\u8fd8\u6709\u6bd4\u5b83\u66f4\u597d\u7684\u9a6c\uff01\u5c71\u6c11\u8d24\u4f84\uff0c\u4f60 \u7528\u91d1\u5200\u5be8\u4e3b\u7684\u540d\u4e49\uff0c\u4e0e\u77f3\u82f1\u8054\u540d\u4f20\u4e0b\u4e86\u7eff\u6797\u7bad\uff0c\u6b64\u4e8b\u6211\u524d\u5929\u65b9\u77e5\u3002\u5c71\u897f\u7701\u9ed1\u9053\u4e0a\u7684\u6210\u540d\u4eba \u7269\uff0c\u6211\u90fd\u8ba4\u5f97\uff0c\u6211\u548c\u5c1a\u7d20\u6765\u597d\u4e8b\uff0c\u4fbf\u9a91\u7740\u767d\u9a6c\u6253\u542c\uff0c\u539f\u6765\u4f60\u4eec\u6240\u8981\u8ffd\u6355\u7684\u4e5f\u662f\u4e00\u4e2a\u9a91\u767d\u9a6c\u7684 \u4e66\u751f\uff0c\u8fd9\u4eba\u53ef\u771f\u662f\u80c6\u5927\u5305\u5929\uff0c\u73b0\u5728\u5df2\u5e72\u4e0b\u9707\u52a8\u7eff\u6797\u4e4b\u4e8b\uff01\u201d \u3000\u3000\u4e91\u857e\u3001\u5468\u5c71\u6c11\u3001\u77f3\u7fe0\u51e4\u8038\u7136\u52a8\u5bb9\uff0c\u9f50\u58f0\u95ee\u9053\uff1a\u201c\u4ed6\u5e72\u4e86\u4ec0\u4e48\u4e8b\uff1f\u201d\u795e\u8272\u5404\u5404\u4e0d\u540c\u3002\u6f6e\u97f3 \u548c\u5c1a\u4e2d\u6307\u3001\u98df\u6307\u76f8\u642d\uff0c\u201c\u5693\u201d\u7684\u4e00\u58f0\uff0c\u8d5e\u53f9\u9053\uff1a\u201c\u5468\u8d24\u4f84\uff0c\u4f60\u4eec\u6240\u8981\u5bf9\u4ed8\u7684\u767d\u9a6c\u4e66\u751f\u662f\u4f55\u7b49 \u6837\u4eba\uff0c\u6211\u5148\u4e0d\u95ee\uff0c\u770b\u4ed6\u7684\u884c\u5f84\uff0c\u53ef\u771f\u662f\u82f1\u96c4\u672c\u8272\uff01\u4e00\u822c\u7684\u4eba\u88ab\u7eff\u6797\u5927\u8c6a\u4f20\u4e0b\u7eff\u6797\u7bad\u8ffd\u6355\uff0c\u8eb2 \u907f\u90fd\u6765\u4e0d\u53ca\uff0c\u4ed6\u5374\u5148\u627e\u4e0a\u95e8\u53bb\uff01\u201d\u5468\u5c71\u6c11\u8be7\u9053\uff1a\u201c\u627e\u4e0a\u95e8\u53bb\uff1f\u4ed6\u627e\u4e86\u8c01\u4e86\uff1f\u201d\u6f6e\u97f3\u548c\u5c1a\u9053\uff1a \u201c\u53ea\u6015\u63a5\u5230\u4f60\u7eff\u6797\u7bad\u7684\u4eba\uff0c\u4ed6\u90fd\u53bb\u627e\u5566\uff01\u6211\u524d\u65e5\u5230\u84dd\u5927\u4fa0\u5904\u6253\u542c\uff0c\u4ed6\u521a\u63a5\u5230\u90a3\u767d\u9a6c\u4e66\u751f\u7684\u7559 \u5200\u5bc4\u7b80\uff0c\u7ea6\u4ed6\u4e03\u65e5\u4e4b\u540e\u5230\u2018\u9707\u4e09\u754c\u2019\u6bd5\u9053\u51e1\u5bb6\u91cc\u76f8\u4f1a\u3002\u201d\u5468\u5c71\u6c11\u3001\u77f3\u7fe0\u51e4\u60ca\u8d77\u53eb\u9053\uff1a\u201c\u9707\u4e09 \u754c\u6bd5\u9053\u51e1\uff1f\u201d\u4e91\u857e\u867d\u7136\u4e0d\u77e5\u9053\u201c\u9707\u4e09\u754c\u201d\u6bd5\u9053\u51e1\u662f\u4f55\u7b49\u6837\u4eba\uff0c\u4f46\u770b\u4ed6\u4eec\u60ca\u5f02\u7684\u795e\u60c5\uff0c\u81ea\u5fc5\u662f \u975e\u5e38\u7684\u4eba\u7269\u3002 \u3000\u3000\u6f6e\u97f3\u548c\u5c1a\u9053\uff1a\u201c\u6b63\u662f\u9707\u4e09\u754c\u6bd5\u9053\u51e1\u3002\u4f60\u8bf4\u4ed6\u53ef\u4e0d\u662f\u5403\u4e86\u72fc\u5b50\u5fc3\u8c79\u5b50\u80c6\u5417\uff1f\u6211\u8f9e\u522b\u4e86\u84dd\u5927 \u4fa0\uff0c\u4e0b\u5348\u5230\u9f99\u5be8\u4e3b\u90a3\u91cc\uff0c\u4ed6\u4e5f\u521a\u63a5\u5230\u90a3\u767d\u9a6c\u4e66\u751f\u7684\u7559\u5200\u5bc4\u7b80\uff0c\u4e5f\u662f\u7ea6\u4ed6\u4e03\u65e5\u4e4b\u540e\u5230\u2018\u9707\u4e09 \u754c\u2019\u6bd5\u9053\u51e1\u5bb6\u91cc\u76f8\u4f1a\u3002\u84dd\u5927\u4fa0\u4e0e\u9f99\u5be8\u4e3b\u90fd\u662f\u6b66\u6797\u4e2d\u54cd\u5f53\u5f53\u7684\u811a\u8272\uff0c\u6b66\u827a\u5c82\u662f\u5bfb\u5e38\uff0c\u7adf\u7136\u88ab\u4ed6 \u5077\u8fdb\u5bb6\u4e2d\uff0c\u7559\u5200\u5bc4\u7b80\uff0c\u4f20\u58f0\u793a\u8b66\u4e4b\u540e\u8fd9\u624d\u53d1\u73b0\uff0c\u8fd9\u767d\u9a6c\u4e66\u751f\u7684\u672c\u4e8b\uff0c\u5b9e\u662f\u8db3\u4ee5\u9a87\u4eba\u3002\u201d\u4e91\u857e \u521d\u9047\u5f20\u4e39\u67ab\u65f6\uff0c\u88ab\u4ed6\u51e0\u6b21\u620f\u5f04\uff0c\u89c1\u8bc6\u8fc7\u5f20\u4e39\u67ab\u7684\u8f7b\u529f\u672c\u9886\uff0c\u5012\u4e5f\u4e0d\u89c9\u5947\u5f02\uff0c\u5468\u5c71\u6c11\u3001\u77f3\u7fe0\u51e4 \u5df2\u662f\u77eb\u820c\u96be\u4e0b\u3002 \u3000\u3000\u6f6e\u97f3\u548c\u5c1a\u7eed\u9053\uff1a\u201c\u6211\u597d\u5947\u5fc3\u8d77\uff0c\u4ed7\u7740\u9a6c\u5feb\uff0c\u4fbf\u53bb\u8ffd\u8e2a\u8fd9\u4e2a\u767d\u9a6c\u4e66\u751f\uff0c\u5728\u5d1e\u53bf\u4ee5\u5317\u7684\u91ce\uff0c \u53d1\u73b0\u4e86\u4ed6\u7684\u8e2a\u8ff9\uff0c\u6211\u98de\u9a91\u6025\u8ffd\uff0c\u53ea\u542c\u5f97\u4ed6\u4e00\u8def\u7b11\u58f0\u4e0d\u7edd\uff0c\u9065\u9065\u558a\u9053\uff1a\u2018\u4f60\u4e5f\u63a5\u5230\u4e86\u8f70\u5929\u96f7\u7684 \u7eff\u6797\u7bad\u5417\uff1f\u6055\u6211\u4e0d\u77e5\u4f60\u5b89\u7a91\u4f55\u5904\uff0c\u7acb\u5be8\u4f55\u65b9\uff0c\u672a\u66fe\u62dc\u8bbf\uff0c\u4e03\u65e5\u4e4b\u540e\uff0c\u4f60\u4e5f\u5230\u9707\u4e09\u754c\u6bd5\u9053\u51e1\u5bb6 \u91cc\u53bb\u5427\uff01\u2019\u539f\u6765\u4ed6\u628a\u6211\u4e5f\u5f53\u6210\u662f\u8ffd\u6355\u4ed6\u7684\u4eba\u5566\u3002\u6211\u7684\u9a6c\u5feb\uff0c\u4ed6\u7684\u9a6c\u66f4\u5feb\uff0c\u4e0d\u5230\u4e00\u987f\u996d\u7684\u5de5 \u592b\uff0c\u53ea\u89c1\u65f7\u91ce\u5e73\u7574\u4e4b\u4e0a\uff0c\u53ea\u6709\u4e00\u4e2a\u767d\u70b9\u6eda\u52a8\uff0c\u8ffd\u4e0d\u4e0a\u5566\uff01\u665a\u4e0a\u6211\u8d76\u5230\u4ee3\u53bf\u4e4b\u897f\u90dd\u5e84\u4e3b\u90a3\u91cc\uff0c \u624d\u77e5\u4ed6\u5728\u9ec4\u660f\u65f6\u5019\uff0c\u4e5f\u63a5\u5230\u90a3\u767d\u9a6c\u4e66\u751f\u7684\u7559\u5200\u5bc4\u7b80\uff0c\u770b\u6765\u4ed6\u90a3\u5339\u767d\u9a6c\u6bd4\u6211\u8fd9\u5339\u767d\u9a6c\u8981\u5feb\u534a\u65e5 \u811a\u7a0b\uff01\u201d \u3000\u3000\u5468\u5c71\u6c11\u9053\uff1a\u201c\u9707\u4e09\u754c\u6bd5\u9053\u51e1\u5728\u9ed1\u767d\u4e24\u9053\u4e4b\u5916\uff0c\u884c\u8e2a\u8be1\u79d8\u975e\u5e38\uff0c\u8fd9\u767d\u9a6c\u8d3c\u4eba\u65b0\u4ece\u8499\u53e4\u800c \u6765\uff0c\u600e\u77e5\u4ed6\u7684\u4f4f\u5740\uff1f\u201d\u6b64\u8a00\u4e00\u51fa\uff0c\u6f6e\u97f3\u548c\u5c1a\u4e0e\u77f3\u7fe0\u51e4\u90fd\u540c\u611f\u60ca\u5947\uff0c\u9762\u6709\u5f02\u8272\uff0c\u6f6e\u97f3\u548c\u5c1a\u662f\u542c \u5230\u4e86\u201c\u8499\u53e4\u201d\u4e8c\u5b57\u800c\u60ca\u5947\uff1b\u77f3\u7fe0\u51e4\u5219\u597d\u4f3c\u8be7\u5f02\u5468\u5c71\u6c11\u4e5f\u5c45\u7136\u77e5\u9053\u9707\u4e09\u754c\u6bd5\u9053\u51e1\u7684\u8eab\u4efd\u3002 \u3000\u3000\u6f6e\u97f3\u548c\u5c1a\u9053\uff1a\u201c\u6bd5\u9053\u51e1\u5728\u6cb3\u5317\u3001\u5c71\u897f\u4e8c\u7701\u4ea4\u754c\u4e4b\u5904\uff0c\u5728\u4e00\u4e2a\u540d\u53eb\u2018\u83b7\u9e7f\u2019\u7684\u5c0f\u6751\u5e84\u5c45 \u4f4f\uff0c\u6211\u4e5f\u662f\u524d\u65e5\u521a\u4ece\u84dd\u5927\u4fa0\u5904\u5f97\u77e5\u7684\u3002\u4ed6\u4ece\u8499\u53e4\u8fdc\u6765\uff0c\u5374\u600e\u7684\u5bf9\u4e2d\u539f\u7684\u6210\u540d\u4eba\u7269\uff0c\u90fd\u77e5\u5f97\u6e05 \u6e05\u695a\u695a\uff1f\u6b64\u4e8b\u5b9e\u662f\u53ef\u7591\uff0c\u5514\uff0c\u83ab\u975e\u2026\u2026\u201d\u6b32\u8bf4\u53c8\u6b62\u3002\u4e91\u857e\u62a2\u7740\u95ee\uff1a\u201c\u4f60\u4eec\u5c3d\u8bf4\u9707\u4e09\u754c\u6bd5\u9053 \u51e1\uff0c\u8fd9\u9707\u4e09\u754c\u7a76\u7adf\u662f\u4f55\u7b49\u6837\u4eba\uff1f\u201d\u6b64\u4e00\u95ee\u4e5f\uff0c\u6709\u5206\u6559\uff1a \u3000\u3000\u5f15\u6765\u4f0f\u864e\u5c60\u9f99\u624b\uff0c\u9053\u7834\u5b64\u81e3\u5b7d\u5b50\u5fc3\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u840d\u8e2a\u4fa0\u5f71\u5f55\u300b\u2014\u2014\u7b2c\u4e5d\u56de\u3000\u6eda\u6eda\u5927\u6c5f\u6d41\u82f1\u96c4\u8840\u6d12\u3000\u60a0\u60a0\u957f\u591c\u68a6\u513f\u5973\u60c5\u75f4 \u6881\u7fbd\u751f\u300a\u840d\u8e2a\u4fa0\u5f71\u5f55\u300b \u7b2c\u4e5d\u56de\u3000\u6eda\u6eda\u5927\u6c5f\u6d41\u82f1\u96c4\u8840\u6d12\u3000\u60a0\u60a0\u957f\u591c\u68a6\u513f\u5973\u60c5\u75f4 \u3000\u3000\u6f6e\u97f3\u548c\u5c1a\u9053\uff1a\u201c\u4f60\u4e0d\u95ee\uff0c\u6211\u4e5f\u60f3\u8bf4\u3002\u8fd9\u9707\u4e09\u754c\u6bd5\u9053\u51e1\u4e00\u5bb6\u5b50\u4e43\u662f\u6b66\u6797\u4e2d\u884c\u4e8b\u6700\u602a\u7684\u4e00 \u5bb6\u3002\u4ed6\u5bb6\u7236\u4f20\u5b50\u5b50\u4f20\u5b59\uff0c\u90fd\u5b88\u7740\u4e00\u6761\u602a\u5f02\u900f\u9876\u7684\u5bb6\u89c4\uff1a\u51e1\u662f\u7537\u5b50\uff0c\u5230\u5341\u516d\u5c81\u6210\u4eba\u4e4b\u65f6\uff0c\u90fd\u8981 \u524a\u53d1\u4e3a\u50e7\uff0c\u505a\u6e38\u65b9\u548c\u5c1a\uff0c\u505a\u4e86\u5341\u5e74\u4e4b\u540e\uff0c\u624d\u51c6\u957f\u53d1\u8fd8\u4fd7\uff0c\u53ef\u662f\u8fd8\u4e0d\u80fd\u6210\u5bb6\u7acb\u5ba4\uff0c\u53c8\u8981\u505a\u5341\u5e74 \u53eb\u5316\uff0c\u505a\u6ee1\u5341\u5e74\u53eb\u5316\u4e4b\u540e\uff0c\u624d\u8bb8\u7ed3\u5a5a\u751f\u5b50\u3002\u6240\u4ee5\u6bd5\u5bb6\u7684\u7537\u5b50\uff0c\u82e5\u8981\u7ed3\u5a5a\uff0c\u6700\u5c11\u5f97\u5728\u4e09\u5341\u516d\u5c81 \u4e4b\u540e\u3002\u6bd5\u5bb6\u4eba\u4e01\u5355\u8584\uff0c\u6570\u4ee3\u5355\u4f20\uff0c\u6216\u8bb8\u4e0e\u7ed3\u5a5a\u4e4b\u8fdf\uff0c\u4e5f\u4e0d\u65e0\u5173\u7cfb\u3002\u6bd5\u9053\u51e1\u6b66\u529f\u9ad8\u5f3a\uff0c\u795e\u51fa\u9b3c \u6ca1\uff0c\u5341\u5e74\u4e3a\u50e7\uff0c\u5341\u5e74\u4e3a\u4e10\uff0c\u540e\u6765\u53c8\u8fd8\u4fd7\u9690\u5c45\uff0c\u5728\u50e7\u3001\u4e10\u3001\u4fd7\u4eba\u4e4b\u4e2d\uff0c\u90fd\u6709\u8fc7\u8bb8\u591a\u5947\u884c\u5f02\u8ff9\uff0c \u56e0\u6b64\u5f97\u4e86\u2018\u9707\u4e09\u754c\u2019\u8fd9\u4e2a\u7f8e\u540d\u3002\u5468\u8d24\u4f84\uff0c\u8fd9\u6bd5\u9053\u51e1\u4e43\u662f\u8df3\u51fa\u4e86\u50e7\u4e10\u4fd7\u4e09\u754c\u4e4b\u5916\uff0c\u53c8\u4e0d\u5728\u9ed1\u767d \u9053\u4e4b\u4e2d\u7684\u4e00\u4e2a\u602a\u4eba\uff0c\u96be\u9053\u4ed6\u4e5f\u4f1a\u63a5\u4f60\u4eec\u7684\u7eff\u6797\u7bad\uff0c\u4f38\u624b\u7ba1\u8fd9\u79cd\u95f2\u4e8b\u5417\uff1f\u201d \u3000\u3000\u5468\u5c71\u6c11\u9053\uff1a\u201c\u6211\u600e\u6562\u5c06\u7eff\u6797\u7bad\u4f20\u4e0e\u4ed6\u3002\u82e5\u5f97\u6bd5\u524d\u8f88\u51fa\u624b\u76f8\u52a9\uff0c\u6b63\u662f\u6211\u6240\u6b32\u4e5f\uff0c\u4e0d\u6562\u8bf7 \u8033\u3002\u201d\u77f3\u7fe0\u51e4\u95ee\u9053\uff1a\u201c\u4f60\u8bf7\u6211\u7239\u7239\u8054\u540d\u4f20\u4e0b\u4e86\u7eff\u6797\u7bad\uff0c\u5230\u5e95\u4e3a\u4e86\u4f55\u4e8b\uff1f\u90a3\u767d\u9a6c\u5c0f\u8d3c\u7a76\u662f\u4f55 \u4eba\uff1f\u201d\u5468\u5c71\u6c11\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u4e3a\u4e86\u66ff\u4f60\u7684\u4e08\u592b\u62a5\u4ec7\uff01\u90a3\u767d\u9a6c\u5c0f\u8d3c\u662f\u5927\u5978\u8d3c\u5f20\u5b97\u5468\u7684\u72ec\u751f\u513f \u5b50\uff0c\u4e5f\u662f\u6211\u4e91\u857e\u5f1f\u7684\u5927\u4ec7\u4eba\uff01\u201d\u987f\u4e86\u4e00\u987f\u6c89\u541f\u534a\u664c\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u770b\u6bd5\u8001\u524d\u8f88\u591a\u534a\u4f1a\u51fa\u624b\u76f8 \u52a9\u3002\u53ef\u60dc\u6211\u4e0d\u77e5\u9053\u4ed6\u4fbf\u4f4f\u5728\u83b7\u9e7f\uff0c\u5426\u5219\u6211\u5f53\u8bf7\u77f3\u8001\u524d\u8f88\u4e0e\u6211\u7239\u8054\u540d\u5199\u4fe1\u4e0e\u4ed6\u7684\u3002\u201d\u77f3\u7fe0\u51e4\u5ffd \u9053\uff1a\u201c\u4e91\u76f8\u516c\uff0c\u90a3\u767d\u9a6c\u5c0f\u8d3c\u679c\u771f\u662f\u4f60\u7684\u5927\u4ec7\u4eba\u5417\uff1f\u201d\u4e91\u857e\u9762\u8272\u82cd\u767d\uff0c\u9053\uff1a\u201c\u55ef\uff0c\u7684\uff0c\uff0d\uff0d\u662f \u7684\u3002\u4ed6\u662f\u6211\u5bb6\u7684\u5927\u4ec7\u4eba\uff01\u201d\u77f3\u7fe0\u51e4\u67f3\u7709\u4e00\u5c55\uff0c\u7b11\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u8be5\u8c22\u6211\u624d\u6210\u3002\u201d\u638f\u51fa\u4e86\u4e00\u5c01\u706b \u6f06\u5bc6\u5c01\u7684\u4fe1\uff0c\u9053\uff1a\u201c\u6211\u7239\u65e9\u5df2\u60f3\u5230\u4ed6\u4e86\u3002\u4f60\u4eec\u4e0d\u6562\u8bf7\u4ed6\uff0c\u6211\u66ff\u4f60\u4eec\u53bb\u8bf7\u3002\u201d\u5468\u5c71\u6c11\u4e00\u773c\u77a5 \u53bb\uff0c\u53ea\u89c1\u4fe1\u5c01\u4e0a\u7aef\u7aef\u6b63\u6b63\u5199\u7740\uff1a\u201c\u9707\u4e09\u754c\u6bd5\u9053\u51e1\u5144\u53f0\u4eb2\u542f\u3002\u201d\u62cd\u638c\u7b11\u9053\uff1a\u201c\u77f3\u8001\u524d\u8f88\u679c\u662f\u987e \u8651\u5468\u8be6\uff0c\u65e9\u5c31\u60f3\u5230\u8fd9\u4e00\u7740\u68cb\u3002\u8fd9\u5c0f\u8d3c\u4eca\u6b21\u771f\u662f\u81ea\u6295\u7f57\u7f51\uff0c\u8d24\u5f1f\uff0c\u4f60\u53ef\u4ee5\u4eb2\u624b\u62a5\u4ec7\u4e86\uff01\u201d \u3000\u3000\u77f3\u7fe0\u51e4\u5f97\u610f\u6d0b\u6d0b\u8bf4\u9053\uff1a\u201c\u6211\u4e00\u56de\u5230\u5bb6\u4e2d\uff0c\u4ed6\u4fbf\u5199\u4e86\u8fd9\u5c01\u4fe1\u8981\u6211\u7acb\u523b\u9001\u53bb\u3002\u6211\u5947\u602a\u4ed6\u4e3a\u4ec0 \u4e48\u8fd9\u6837\u6025\u6cd5\uff0c\u539f\u6765\u662f\u8981\u66ff\u4f60\u62a5\u4ec7\u554a\u3002\u597d\u7239\u7239\uff0c\u4ed6\u628a\u6211\u8499\u5728\u9f13\u91cc\uff0c\u4e0d\u80af\u5c06\u90a3\u5c0f\u8d3c\u6765\u5386\u8bf4\u4e0e\u6211 \u77e5\uff0c\u539f\u6765\u90a3\u5c0f\u8d3c\uff0c\u7adf\u662f\u4f60\u7684\u5927\u4ec7\u4eba\uff01\u7b49\u4f1a\u513f\u54b1\u4eec\u4e00\u540c\u8d76\u53bb\uff0c\u4e5f\u6559\u4f60\u8ba4\u8bc6\u8ba4\u8bc6\u90a3\u5927\u540d\u9f0e\u9f0e\u7684\u9707 \u4e09\u754c\u6bd5\u9053\u51e1\uff01\u201d\u4e91\u857e\u5fc3\u5934\u4e00\u9707\u95ee\u9053\uff1a\u201c\u4f60\u770b\u8fc7\u8fd9\u5c01\u4fe1\u5417\uff1f\u201d\u77f3\u7fe0\u51e4\u9053\uff1a\u201c\u4f60\u6ca1\u542c\u6211\u8bf4\uff0c\u6211\u7239 \u5c06\u6211\u8499\u5728\u9f13\u91cc\u5417\uff1f\u82e5\u6211\u65e9\u770b\u4e86\u8fd9\u5c01\u4fe1\uff0c\u8fd8\u4e0d\u660e\u767d\uff1f\u73b0\u5728\uff0c\u8fd9\u5c01\u4fe1\u4e0d\u7528\u770b\u4e5f\u731c\u5f97\u51fa\u4ed6\u5199\u4ec0\u4e48\uff0c \u5f53\u7136\u662f\u8bf7\u9707\u4e09\u754c\u62d4\u5200\u52a9\u4f60\u4e86\u3002\u201d\u4e91\u857e\u6ee1\u8179\u7591\u56e2\uff1a\u77f3\u82f1\u5e76\u4e0d\u77e5\u9053\u5f20\u4e39\u67ab\u662f\u5979\u4ec7\u4eba\uff0c\u81ea\u5df1\u53c8\u4eb2\u89c1 \u8fc7\u4ed6\u5bf9\u5f20\u4e39\u67ab\u662f\u90a3\u4e48\u4e00\u526f\u5982\u4ec6\u4eba\u5bf9\u4e3b\u4eba\u7684\u795e\u6c14\uff0c\u4ed6\u5c82\u4f1a\u5199\u4fe1\u53eb\u6bd5\u9053\u51e1\u53bb\u6740\u5f20\u4e39\u67ab\uff1f\u8fd9\u5c01\u4fe1\u8bf4 \u7684\u662f\u4ec0\u4e48\uff1f\u5b9e\u5728\u96be\u4ee5\u6599\u6d4b\uff01\u77f3\u7fe0\u51e4\u8be7\u9053\uff1a\u201c\u4e91\u76f8\u516c\uff0c\u4f60\u5728\u60f3\u4ec0\u4e48\uff1f\u6211\u7239\u4e3a\u4f60\u4f20\u4e0b\u4e86\u7eff\u6797\u7bad\uff0c \u53c8\u8bf7\u4eba\u66ff\u4f60\u62a5\u4ec7\uff0c\u4f60\u8fd8\u4e0d\u9ad8\u5174\u5417\uff1f\u201d \u3000\u3000\u4e91\u857e\u5f3a\u989c\u7b11\u9053\uff1a\u201c\u6211\u9ad8\u5174\u6781\u5566\uff01\u77f3\u59d1\u5a18\uff0c\u4f60\u7239\u548c\u90a3\u9707\u4e09\u754c\u6bd5\u9053\u51e1\u662f\u81f3\u4ea4\u5417\uff1f\u201d\u77f3\u7fe0\u51e4 \u9053\uff1a\u201c\u4e0d\uff0c\u4ed6\u662f\u6211\u7239\u7684\u5bf9\u5934\uff01\u4ed6\u53ef\u5f3a\u6a2a\u9738\u9053\u5f97\u5f88\u5462\uff0c\u6211\u8fd8\u6ca1\u89c1\u8fc7\u8c01\u6562\u50cf\u4ed6\u90a3\u6837\u6b3a\u8d1f\u6211\u7684\u7239 \u7239\uff01\u201d\u6b64\u8a00\u5927\u51fa\u4f17\u4eba\u610f\u6599\u4e4b\u5916\uff0c\u6f6e\u97f3\u548c\u5c1a\u53eb\u9053\uff1a\u201c\u8c01\u8bf4\u6bd5\u9053\u51e1\u5f3a\u6a2a\u9738\u9053\uff1f\u201d\u4e91\u857e\u9053\uff1a\u201c\u5456\uff0c \u4ed6\u600e\u4e48\u6b3a\u8d1f\u4f60\u7684\u7239\u7239\uff1f\u201d\u5468\u5c71\u6c11\u53eb\u9053\uff1a\u201c\u5373\u662f\u5982\u6b64\uff0c\u4f60\u7239\u600e\u4e48\u8fd8\u7ed9\u4ed6\u5199\u8fd9\u5c01\u4fe1\uff1f\u201d \u3000\u3000\u4e09\u4eba\u7eb7\u7eb7\u8d28\u95ee\uff0c\u77f3\u7fe0\u51e4\u8f97\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4ed6\u6b3a\u8d1f\u6211\u7239\uff0c\u53ef\u662f\u6211\u7239\u5c31\u9876\u4f69\u670d\u4ed6\uff01\u4f60\u95ee\u4ed6\u600e \u6837\u6b3a\u8d1f\u6211\u7239\u5417\uff1f\u6211\u8bf4\u8d77\u6765\u8fd9\u5df2\u662f\u5341\u6570\u5e74\u524d\u4e4b\u4e8b\u4e86\uff01\u201d \u3000\u3000\u201c\u90a3\u65f6\u6211\u8fd8\u662f\u4e00\u4e2a\u4e03\u516b\u5c81\u7684\u5c0f\u5b69\u5b50\uff0c\u867d\u7136\u5e74\u5e7c\u65e0\u77e5\uff0c\u5f53\u65e5\u7684\u60c5\u666f\u53ef\u8fd8\u8bb0\u5f97\u6e05\u6e05\u695a\u695a\u3002\u6709 \u4e00\u65e5\uff0c\u6211\u5bb6\u95e8\u5916\u6765\u4e86\u4e00\u4e2a\u6076\u4e10\uff0c\u5bb6\u4eba\u7ed9\u7c73\u4ed6\u4e0d\u8981\uff0c\u7ed9\u94b1\u4ed6\u4e5f\u4e0d\u8981\uff0c\u53e3\u53e3\u58f0\u58f0\u8981\u6211\u7239\u7ed9\u4e00\u4ef6\u5b9d \u7269\u4e0e\u4ed6\u3002\u8c01\u4e0d\u77e5\u9053\u6211\u7239\u662f\u505a\u9ed1\u9053\u4e0a\u7684\u73e0\u5b9d\u4e70\u5356\u7684\uff0c\u5bb6\u4eba\u4ee5\u4e3a\u4ed6\u662f\u6765\u8bb9\u8bc8\u52d2\u7d22\uff0c\u6709\u4eba\u4fbf\u52a8\u624b\u6253 \u4ed6\uff0c\u4ed6\u52a8\u4e5f\u4e0d\u52a8\uff0c\u6253\u4ed6\u7684\u4eba\u4fbf\u7ed9\u5f39\u5230\u6570\u4e08\u5f00\u5916\uff0c\u540e\u6765\u6211\u624d\u77e5\u9053\u8fd9\u662f\u2018\u6cbe\u8863\u5341\u516b\u8dcc\u2019\u7684\u4e0a\u4e58\u5185 \u529f\u3002 \u3000\u3000\u201c\u90a3\u65e5\u6211\u7239\u6b63\u6559\u6211\u8bfb\u4e66\u5199\u5b57\uff0c\u5bb6\u4eba\u8fdb\u6765\u7980\u62a5\uff0c\u8bf4\u6709\u8fd9\u4e48\u4e00\u4e2a\u6765\u5386\u4e0d\u660e\uff0c\u53e3\u6c14\u5947\u5927\u7684\u6076 \u4e10\u3002\u6211\u7239\u7684\u9762\u8272\u4e00\u4e0b\u5b50\u53d8\u5f97\u60e8\u767d\uff0c\u6325\u624b\u8bf4\u9053\uff1a\u2018\u597d\uff0c\u8bf7\u4ed6\u8fdb\u6765\u3002\u4ed6\u8fdb\u6765\u540e\u8c01\u4e5f\u4e0d\u8bb8\u5230\u5185\u95f4\u534a \u6b65\uff0c\u5c31\u662f\u6211\u7ed9\u4ed6\u6253\u6b7b\u4e86\uff0c\u4f60\u4eec\u4e5f\u4e0d\u51c6\u8fdb\u6765\uff01\u2019\u53c8\u53eb\u6211\u8eb2\u5230\u5367\u623f\u53bb\u4e0d\u8981\u51fa\u6765\u3002\u6211\u542c\u7239\u90a3\u4e48\u8bf4\uff0c \u5bb3\u6015\u6781\u4e86\uff0c\u53ef\u662f\u6211\u8fd8\u662f\u4e0d\u542c\u4ed6\u7684\u8bdd\uff0c\u5f85\u90a3\u6076\u4e10\u8fdb\u6765\u4e4b\u540e\uff0c\u6211\u5c31\u8eb2\u5728\u5916\u9762\u7684\u5c4b\u89d2\u5077\u770b\u3002\u201d \u3000\u3000\u201c\u90a3\u6076\u4e10\u76f8\u8c8c\u5947\u7279\uff0c\u4e71\u53d1\u5982\u84ec\uff0c\u9762\u5982\u9ed1\u9505\uff0c\u62ff\u7740\u4e00\u6839\u53eb\u5316\u68d2\uff0c\u5c31\u5982\u51f6\u795e\u6076\u715e\u4e00\u822c\uff0c\u8fdb\u6765 \u4e4b\u540e\uff0c\u5750\u5728\u6211\u7239\u5bf9\u9762\uff0c\u4e00\u53cc\u602a\u773c\u95ea\u95ea\u53d1\u5149\uff0c\u7785\u7740\u6211\u7239\uff0c\u597d\u4e45\uff0c\u597d\u4e45\uff0c\u4e24\u4eba\u90fd\u4e0d\u8bf4\u8bdd\u3002\u201d \u3000\u3000\u201c\u6211\u7239\u53f9\u4e86\u53e3\u6c14\uff0c\u8d70\u5165\u5185\u5ba4\uff0c\u53d6\u4e86\u8bb8\u591a\u73cd\u5b9d\u51fa\u6765\uff0c\u5806\u5728\u4ed6\u7684\u9762\u524d\uff0c\u8bf4\u9053\uff1a\u2018\u6bd5\u7237\uff0c\u6211\u7684 \u5bb6\u5f53\u90fd\u5728\u8fd9\u513f\u4e86\u3002\u2019\u90a3\u6076\u4e10\u4e00\u58f0\u51b7\u7b11\uff0c\u5c06\u73cd\u5b9d\u90fd\u6253\u5728\u5730\u4e0a\u9053\uff1a\u2018\u8f70\u5929\u96f7\uff0c\u4f60\u548c\u6211\u88c5\u75af\u4f5c\u50bb \u4e48\uff1f\u6211\u5bb6\u5c61\u4ee3\u5bfb\u8bbf\uff0c\u5df2\u627e\u4e86\u51e0\u5341\u5e74\u4e86\uff0c\u800c\u4eca\u6211\u67e5\u5f97\u786e\u786e\u5b9e\u5b9e\uff0c\u90a3\u4e1c\u897f\u5c31\u5728\u4f60\u8fd9\u91cc\uff0c\u4f60\u8fd8\u4e0d\u7ed9 \u6211\u62ff\u51fa\u6765\u4e48\uff1f\u2019\u6211\u7239\u9053\uff1a\u2018\u4e1c\u897f\u4e5f\u4e0d\u662f\u4f60\u7684\uff0c\u51ed\u4ec0\u4e48\u8981\u7ed9\u4f60\uff1f\u2019\u90a3\u6076\u4e10\u51b7\u7b11\u9053\uff1a\u2018\u96be\u9053\u662f\u4f60 \u7684\u4e0d\u6210\u5417\uff1f\u4f60\u77e5\u5426\u5b83\u7684\u6765\u5386\uff0c\u600e\u6562\u8bf4\u6211\u4e0d\u662f\u5b83\u7684\u4e3b\u4eba\uff1f\u2019\u6211\u4ece\u672a\u89c1\u8fc7\u6709\u4eba\u6562\u7528\u8fd9\u6837\u7684\u53e3\u543b\u5bf9 \u6211\u7239\u5927\u58f0\u8bf4\u8bdd\uff0c\u6211\u7239\u5012\u50cf\u6073\u6c42\u4f3c\u7684\uff0c\u5bf9\u4ed6\u8bf4\u9053\uff1a\u2018\u8fd9\u4ef6\u5b9d\u7269\uff0c\u5c31\u7b97\u4f60\u6cbe\u4e0a\u70b9\u8fb9\uff0c\u4e5f\u4e0d\u80fd\u8bf4\u5168 \u662f\u4f60\u7684\u3002\u6211\u53d7\u4eba\u6240\u6258\uff0c\u5bb6\u5f53\u53ef\u4ee5\u4e0d\u8981\uff0c\u8fd9\u4e1c\u897f\u53ef\u8bf7\u6bd5\u7237\u653e\u5f00\u624b\u5427\uff01\u2019\u90a3\u6076\u4e10\u52c3\u7136\u53d1\u4f5c\uff0c\u7ad9\u4e86 \u8d77\u6765\uff0c\u5927\u58f0\u8bf4\u9053\uff1a\u2018\u5bb6\u5f53\uff0c\u5bb6\u5f53\uff1f\u8fd9\u4e1c\u897f\u4f60\u662f\u7ed9\u8fd8\u662f\u4e0d\u7ed9\uff1f\u2019\u6211\u7239\u9053\uff1a\u2018\u4e0d\u7ed9\uff01\u2019\u90a3\u6076\u4e10\u51b7 \u51b7\u4e00\u7b11\uff0c\u5c06\u53eb\u5316\u68d2\u6ef4\u6e9c\u6e9c\u821e\u4e86\u4e00\u4e2a\u5706\u5708\uff0c\u9053\uff1a\u2018\u597d\u5440\uff01\u4f60\u65e2\u7136\u4e0d\u7ed9\uff0c\u90a3\u6211\u53ef\u8981\u9886\u6559\u9886\u6559\u4f60\u72ec \u6b65\u5929\u4e0b\u7684\u8e51\u4e91\u5251\u6cd5\u4e86\uff01\u2019\u201d \u3000\u3000\u201c\u6211\u7239\u9053\uff1a\u2018\u65e2\u7136\u5982\u6b64\uff0c\u90a3\u5c31\u6055\u6211\u653e\u8086\u5566\uff01\u2019\u62d4\u51fa\u5251\u6765\uff0c\u8ddf\u4ed6\u72e0\u6253\uff0c\u90a3\u65f6\u6211\u8fd8\u672a\u5b66\u5251 \u6cd5\uff0c\u53ea\u89c1\u6211\u7239\u4f3c\u75af\u864e\u4e00\u822c\uff0c\u5251\u5149\u970d\u970d\uff0c\u4fe8\u7136\u662f\u4e00\u526f\u62fc\u547d\u7684\u795e\u6c14\u3002\u90a3\u6076\u4e10\u7684\u4e00\u6761\u53eb\u5316\u68d2\uff0c\u88ab\u88f9 \u5728\u5251\u5149\u4e4b\u4e2d\uff0c\u5374\u662f\u4f38\u7f29\u81ea\u5982\u6709\u5982\u4e00\u6761\u602a\u87d2\uff0c\u628a\u6211\u770b\u5f97\u773c\u82b1\u7f2d\u4e71\uff01\u201d \u3000\u3000\u201c\u4ed6\u4eec\u72e0\u6253\u72e0\u62fc\uff0c\u8fc7\u4e86\u4e00\u987f\u996d\u7684\u5de5\u592b\uff0c\u8fd8\u662f\u672a\u5206\u9ad8\u4e0b\u3002\u5ffd\u542c\u5f97\u90a3\u6076\u4e10\u4e00\u58f0\u559d\u9053\uff1a\u2018\u4f60\u7ed9 \u4e0d\u7ed9\uff1f\u2019\u2018\u25a1\u2019\u7684\u4e00\u68d2\u6253\u4e2d\u6211\u7239\u80a9\u5934\uff0c\u6211\u7239\u53eb\u9053\uff1a\u2018\u4e0d\u7ed9\uff01\u2019\u51fa\u5176\u4e0d\u610f\u2018\u5237\u2019\u7684\u633a\u8170\u8fd8\u4e86\u4e00 \u5251\u4e5f\u5728\u4ed6\u80a9\u5934\u5212\u4e86\u4e00\u9053\u4f24\u53e3\u3002\u90a3\u6076\u4e10\u53eb\u9053\uff1a\u2018\u597d\u6c49\u5b50\uff01\u2019\u6325\u68d2\u53c8\u6253\uff0c\u8fc7\u4e86\u4e00\u9635\uff0c\u53ea\u542c\u5f97\u53c8\u662f \u2018\u25a1\u2019\u7684\u4e00\u58f0\uff0c\u90a3\u6076\u4e10\u4e00\u68d2\u6325\u53bb\uff0c\u5c06\u6211\u7239\u6454\u4e86\u4e00\u4e2a\u7b4b\u6597\uff0c\u6211\u7239\u54fc\u4e5f\u4e0d\u54fc\uff0c\u722c\u8eab\u6765\uff0c\u53c8\u8ddf\u4ed6 \u6597\uff0c\u4e0d\u591a\u4e45\uff0c\u4e5f\u5c06\u90a3\u6076\u4e10\u523a\u4e86\u4e00\u5251\uff0c\u90a3\u6076\u4e10\u4e0e\u6211\u7239\u4e00\u6837\uff0c\u4ea6\u662f\u54fc\u4e5f\u4e0d\u54fc\uff0c\u72e0\u6253\u72e0\u6597\uff0c\u6597\u5230\u540e \u6765\uff0c\u5730\u4e0a\u90fd\u662f\u9c9c\u8840\uff0c\u6211\u7239\u5148\u540e\u6454\u4e86\u597d\u51e0\u4e2a\u7b4b\u6597\uff0c\u989d\u89d2\u4e5f\u7ed9\u53eb\u5316\u68d2\u6253\u5f97\u76ae\u5f00\u8089\u88c2\u3002\u867d\u662f\u5982\u6b64\uff0c \u90a3\u6076\u4e10\u53ef\u4e5f\u5360\u4e0d\u4e86\u4fbf\u5b9c\uff0c\u4e0d\u4f46\u4e71\u8349\u4e00\u822c\u7684\u5934\u53d1\u90fd\u7ed9\u5251\u5149\u524a\u77ed\uff0c\u8eab\u4e0a\u4e5f\u53d7\u4e86\u597d\u51e0\u5904\u5251\u4f24\uff0c\u6597\u5230 \u540e\u6765\uff0c\u4e24\u4eba\u90fd\u5df2\u7b4b\u75b2\u529b\u7aed\uff0c\u90a3\u6076\u4e10\u53c8\u6253\u4e86\u6211\u7239\u4e00\u68d2\uff0c\u6211\u7239\u4e5f\u523a\u4e86\u4ed6\u4e00\u5251\uff0c\u4e24\u4eba\u90fd\u5012\u5728\u5730\u4e0a\uff0c \u722c\u4e0d\u8d77\u6765\u3002\u6211\u5bb3\u6015\u6781\u4e86\uff0c\u5148\u5934\u4e0d\u6562\u51fa\u58f0\u558a\u53eb\uff0c\u73b0\u5728\u53ef\u54c7\u7684\u4e00\u58f0\u54ed\u4e86\u51fa\u6765\uff0c\u6211\u7239\u5728\u5730\u4e0a\u6eda\u4e86\u51e0 \u4e0b\uff0c\u6323\u624e\u53eb\u9053\uff1a\u2018\u597d\uff0c\u6bd5\u7237\uff0c\u4f60\u62ff\u53bb\u5427\uff01\u6211\u8ba4\u8f93\u4e86\uff01\u2019\u58f0\u97f3\u98a4\u6296\uff0c\u975e\u5e38\u53ef\u6015\u3002\u90a3\u6076\u4e10\u9053\uff1a \u2018\u4e0d\u4f60\u6ca1\u6709\u8f93\u3002\u4f60\u5fe0\u4e8e\u6240\u6258\uff0c\u786e\u662f\u6211\u751f\u5e73\u6240\u89c1\u7684\u4e00\u6761\u786c\u6c49\uff0c\u90a3\u5b9d\u7269\u4f60\u5c31\u6682\u65f6\u7559\u7740\u5427\u3002\u6211\u4e0d\u548c \u4f60\u786c\u8981\u3002\u4f60\u4eca\u540e\u82e5\u6709\u4ec0\u4e48\u4e3a\u96be\u4e4b\u4e8b\uff0c\u503c\u5f97\u5c06\u90a3\u5b9d\u7269\u4ea4\u6362\u7684\uff0c\u53ea\u8981\u4f60\u4e00\u5f00\u53e3\uff0c\u6211\u65e0\u6709\u4e0d\u5c3d\u529b\u800c \u4e3a\u3002\u2019\u722c\u4e86\u8d77\u6765\uff0c\u5305\u624e\u597d\u4f24\u53e3\uff0c\u7528\u53eb\u5316\u68d2\u5f53\u4f5c\u62d0\u6756\uff0c\u8dc4\u8dc4\u8e09\u8e09\u5730\u8d70\u51fa\u95e8\u53e3\u3002\u6211\u7239\u53ef\u722c\u4e0d\u8d77 \u6765\uff0c\u6211\u51fa\u53bb\u53eb\uff0c\u5bb6\u4eba\u624d\u6562\u8fdb\u6765\uff0c\u5c06\u6211\u62ac\u5230\u5e8a\u4e0a\uff0c\u517b\u4e86\u534a\u4e2a\u591a\u6708\uff0c\u4f24\u624d\u517b\u597d\u3002\u521a\u80fd\u8d70\u52a8\uff0c\u4ed6\u5c31 \u6276\u7740\u5899\u58c1\u5230\u85cf\u5b9d\u697c\u53bb\uff0c\u5728\u90a3\u5e45\u753b\u524d\u72ec\u81ea\u6d41\u6cea\uff0c\u6211\u6574\u65e5\u4e0d\u79bb\u4ed6\u7684\u5de6\u53f3\uff0c\u90a3\u65e5\u6211\u4e5f\u5077\u5077\u8ddf\u53bb\uff0c\u90fd \u77a7\u89c1\u5566\u3002\u90a3\u65f6\uff0c\u6211\u5e74\u7eaa\u5c0f\uff0c\u4e0d\u6562\u95ee\u4ed6\uff0c\u957f\u5927\u4e4b\u540e\uff0c\u95ee\u4ed6\u4ed6\u4e5f\u4e0d\u8bf4\u3002\u201d\u4e91\u857e\u5fc3\u4e2d\u4e00\u52a8\u95ee\u9053\uff1a \u201c\u662f\u54ea\u5e45\u753b\uff1f\u201d\u77f3\u7fe0\u51e4\u9053\uff1a\u201c\u5c31\u662f\u6211\u4eec\u6210\u4eb2\u4e4b\u65e5\u4f60\u5728\u697c\u4e0a\u6240\u89c1\u7684\u90a3\u5e45\u5de8\u753b\u3002\u201d\u4e91\u857e\u201c\u5514\u201d\u4e86 \u4e00\u58f0\uff0c\u4e0d\u518d\u8a00\u8bed\u3002 \u3000\u3000\u77f3\u7fe0\u51e4\u7eed\u9053\uff1a\u201c\u6211\u7239\u540e\u6765\u5e38\u5bf9\u6211\u8bf4\u90a3\u6076\u4e10\u5176\u5b9e\u4e0d\u662f\u6076\u4eba\uff0c\u800c\u662f\u4e00\u4e2a\u5947\u4fa0\uff0c\u8a00\u4e0b\u4e4b\u610f\u5bf9\u4ed6 \u7adf\u4f3c\u5341\u5206\u4f69\u670d\u3002\u6211\u5c31\u4e0d\u80af\u76f8\u4fe1\uff0c\u90a3\u65e5\u5c31\u5982\u6b64\u6b3a\u8d1f\u6211\u7684\u7239\u7239\uff0c\u5f3a\u6a2a\u9738\u9053\u4e4b\u6781\uff0c\u600e\u4e48\u8fd8\u4e0d\u662f\u6076 \u4eba\uff1f\u6211\u7239\u505a\u9ed1\u9053\u4e0a\u7684\u73e0\u5b9d\u4e70\u5356\uff0c\u98ce\u9669\u6781\u5927\uff0c\u6709\u597d\u51e0\u6b21\u78b0\u5230\u8eab\u5bb6\u6027\u547d\u7684\u5371\u96be\uff0c\u5176\u65f6\u603b\u5bf9\u6211\u8bf4\u8d77 \u90a3\u4e2a\u5f53\u5e74\u7684\u6076\u4e10\uff0c\u4eca\u65e5\u7684\u2018\u9707\u4e09\u754c\u2019\u6bd5\u9053\u51e1\uff0c\u8bf4\u662f\u6b64\u4e8b\u82e5\u6709\u6bd5\u7237\u76f8\u52a9\uff0c\u4fbf\u53ef\u5316\u9669\u4e3a\u5937\uff0c\u8bf4\u662f \u5982\u6b64\uff0c\u6211\u7239\u53ef\u4ece\u672a\u66fe\u5411\u4ed6\u6c42\u52a9\u3002\u4e91\u76f8\u516c\uff0c\u4eca\u65e5\u6211\u7239\u4e3a\u4f60\uff0c\u5c45\u7136\u80af\u5199\u4fe1\u7ed9\u4ed6\uff0c\u53ef\u77e5\u4ed6\u7231\u4f60\u903e\u4e8e \u81ea\u5df1\uff0c\u6bd4\u5bf9\u6211\u8fd8\u8981\u6df1\u539a\u5f97\u591a\u3002\u6211\u800c\u4eca\u4e5f\u4e0d\u7ba1\u4ed6\u662f\u597d\u4eba\u8fd8\u662f\u6076\u4eba\uff0c\u662f\u5947\u4fa0\u8fd8\u662f\u602a\u7269\uff0c\u603b\u4e4b\u53ea\u8981 \u4ed6\u80af\u62d4\u5200\u76f8\u52a9\u4e3a\u4f60\u62a5\u4ec7\uff0c\u6211\u4fbf\u6ee1\u5fc3\u9ad8\u5174\uff0c\u518d\u4e5f\u4e0d\u5ff5\u4ed6\u7684\u65e7\u6076\u3002\u201d \u3000\u3000\u4e91\u857e\u51fa\u795e\u601d\u7d22\uff0c\u5bf9\u77f3\u7fe0\u51e4\u7684\u8bdd\u7adf\u4f3c\u4e0d\u95fb\u3002\u6f6e\u97f3\u548c\u5c1a\u63a5\u53e3\u8bf4\u9053\uff1a\u201c\u9707\u4e09\u754c\u6bd5\u9053\u51e1\u6b64\u4eba\uff0c\u4f60 \u8bf4\u4ed6\u51f6\u6076\u786e\u662f\u6076\u5230\u4e86\u6781\u70b9\uff0c\u4f60\u8bf4\u4ed6\u826f\u5584\u5374\u4e5f\u826f\u5584\u5230\u6781\u70b9\u3002\u4e8c\u5341\u591a\u5e74\u4e4b\u524d\uff0c\u6211\u548c\u4ed6\u89c1\u8fc7\u4e00\u9762\uff0c \u90a3\u65f6\u4ed6\u4e0e\u6211\u4e00\u6837\u662f\u4e2a\u548c\u5c1a\uff0c\u8fd8\u672a\u66fe\u84c4\u53d1\u8fd8\u4fd7\u4e5f\u672a\u66fe\u505a\u53eb\u5316\u5b50\u3002\u201d \u3000\u3000\u201c\u90a3\u65f6\u6211\u6280\u4e1a\u521d\u6210\uff0c\u6d6a\u8361\u6c5f\u6e56\uff0c\u662f\u4e2a\u5403\u56db\u65b9\u7684\u6e38\u65b9\u50e7\u4eba\u3002\u4e00\u65e5\u5230\u4e86\u5b89\u5fbd\u51e4\u9633\uff0c\u90a3\u662f\u592a\u7956 \u7687\u5e1d\u6731\u5143\u748b\u7684\u6545\u4e61\uff0c\u6709\u9996\u6b4c\u8c23\u5531\u9053\uff1a\u2018\u8bf4\u51e4\u9633\uff0c\u9053\u51e4\u9633\uff0c\u51e4\u9633\u672c\u662f\u4e2a\u597d\u5730\u65b9\uff0c\u81ea\u4ece\u51fa\u4e86\u4e2a\u6731 \u7687\u5e1d\uff0c\u5341\u5e74\u5012\u6709\u4e5d\u5e74\u8352\u3002\u5927\u6237\u4eba\u5bb6\u5356\u7cae\u98df\uff0c\u5c0f\u6237\u4eba\u5bb6\u5356\u513f\u90ce\uff0c\u5974\u5bb6\u6ca1\u6709\u513f\u90ce\u5356\uff0c\u80cc\u8d77\u82b1\u9f13\u8d70 \u56db\u65b9\u3002\u2019\u53ef\u77e5\u51e4\u9633\u867d\u662f\u2018\u5e1d\u4e61\u2019\uff0c\u5374\u975e\u4f46\u6ca1\u6709\u6cbe\u7740\u7687\u5e1d\u7684\u5149\uff0c\u53cd\u800c\u7ed9\u7687\u5e1d\u5b9a\u4e0b\u6765\u7684\u82db\u6350\u6742 \u7a0e\uff0c\u5f04\u5f97\u6c11\u4e0d\u804a\u751f\uff0c\u4e00\u9047\u8352\u5e74\uff0c\u767e\u59d3\u5c31\u8981\u56db\u5904\u9003\u8352\u3002\u90a3\u5e74\u4e5f\u662f\u8352\u5e74\uff0c\u51e4\u9633\u5341\u5ba4\u4e5d\u7a7a\uff0c\u707e\u60c5\u5341 \u5206\u4e25\u91cd\u3002\u4f46\u5374\u6709\u4e00\u5904\u5730\u65b9\u5bcc\u4e3d\u5802\u7687\uff0c\u72c2\u5962\u6781\u4f88\uff0c\u4f60\u9053\u90a3\u662f\u4e2a\u4ec0\u4e48\u5730\u65b9\uff0c\u90a3\u662f\u4e00\u95f4\u5bfa\u9662\uff01\u201d\u4e91 \u857e\u5947\u9053\uff1a\u201c\u5bfa\u9662\uff1f\u5bfa\u9662\u4e0d\u662f\u548c\u5c1a\u4f4f\u7684\u5730\u65b9\u5417\uff1f\u201d\u6f6e\u97f3\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5bfa\u9662\u662f\u548c\u5c1a\u4f4f\u7684\u5730\u65b9\uff0c\u53ef \u662f\u90a3\u95f4\u5bfa\u9662\u7684\u548c\u5c1a\uff0c\u5374\u4e0d\u4e0e\u6d12\u5bb6\u4e00\u6837\uff0c\u4ed6\u4eec\u662f\u6709\u94b1\u6709\u52bf\u7684\u5927\u548c\u5c1a\uff01\u5728\u8fd9\u91cc\u8bf4\u8bdd\u4e0d\u5fc5\u5fcc\u8bb3\uff0c\u6211 \u671d\u7684\u592a\u7956\u6731\u5143\u748b\u5c11\u65f6\u66fe\u524a\u53d1\u4e3a\u50e7\uff0c\u4ed6\u5c31\u662f\u5728\u90a3\u95f4\u5bfa\u9662\u51fa\u5bb6\u7684\u3002\u90a3\u672c\u662f\u4e00\u95f4\u5c0f\u5bfa\u9662\uff0c\u6731\u5143\u748b\u505a \u4e86\u7687\u5e1d\u540e\uff0c\u90a3\u5bfa\u9662\u53ef\u5c31\u5927\u5174\u571f\u6797\uff0c\u6210\u4e86\u540d\u95fb\u5929\u4e0b\u7684\u5927\u5bfa\u9662\u5566\u3002\u56e0\u4e3a\u7687\u5e1d\u66fe\u5728\u90a3\u91cc\u51fa\u5bb6\u6240\u4ee5\u53eb \u505a\u7687\u89c9\u5bfa\u3002\u201d \u3000\u3000\u201c\u7687\u89c9\u5bfa\u7684\u50e7\u4eba\u6a2a\u884c\u9738\u9053\uff0c\u8fd9\u4e14\u4e0d\u5fc5\u8bf4\u4e86\uff0c\u4ed6\u4eec\u65e2\u4e0d\u6301\u6212\u5f8b\uff0c\u4e5f\u4e0d\u5b88\u6e05\u89c4\uff0c\u8d81\u7740\u8352\u5e74\uff0c \u7adf\u7136\u5927\u6279\u4e70\u5165\u9003\u8352\u4eba\u5bb6\u7684\u5973\u513f\uff0c\u517b\u5728\u5bfa\u9662\u4e4b\u4e2d\u6deb\u4e50\u3002\u6211\u5728\u51e4\u9633\u4e00\u8def\u542c\u5f97\u90a3\u4e9b\u707e\u6c11\u8c08\u8d77\u5356\u5973\u513f \u7ed9\u5bfa\u9662\u4e4b\u4e8b\uff0c\u8fd9\u4e2a\u8bf4\u5f97\u4e86\u4e94\u767e\u94b1\uff0c\u90a3\u4e2a\u8bf4\u5f97\u4e86\u4e09\u767e\u94b1\uff0c\u8fd9\u4e9b\u94b1\u8fd8\u4e0d\u591f\u4e70\u5341\u5929\u7684\u53e3\u7cae\u3002\u8fd8\u6709\u4e9b \u662f\u8feb\u4e8e\u65e0\u6cd5\u517b\u6d3b\u5973\u513f\uff0c\u4e0d\u7ed9\u94b1\u4e5f\u8981\u6c42\u5bfa\u9662\u8981\u7684\u3002\u6211\u542c\u4e86\u5fc3\u5934\u706b\u8d77\uff0c\u5929\u4e0b\u7adf\u7136\u6709\u8fd9\u6837\u7684\u5bfa\u9662\uff0c \u8fd9\u6837\u7684\u548c\u5c1a\uff0c\u8fde\u6211\u8fd9\u4e2a\u72d7\u8089\u548c\u5c1a\u7684\u9762\u5b50\u90fd\u7ed9\u4ed6\u4eec\u4e22\u6e05\u5149\u5566\uff01\u201d \u3000\u3000\u201c\u90a3\u65f6\u6211\u4e0d\u5230\u4e09\u5341\u5c81\uff0c\u706b\u6c14\u6bd4\u73b0\u5728\u5927\uff0c\u4e5f\u4e0d\u7ba1\u5b83\u662f\u4ec0\u4e48\u7687\u89c9\u5bfa\uff0c\u62fd\u8d77\u7985\u6756\u4fbf\u8dd1\u53bb\u627e\u90a3\u4f4f \u6301\u548c\u5c1a\u5927\u9a82\u4e00\u901a\u3002\u54ea\u77e5\u90a3\u4e9b\u548c\u5c1a\u4e2a\u4e2a\u90fd\u4f1a\u6b66\u529f\uff0c\u4f4f\u6301\u5c24\u5176\u662f\u4e2a\u9ad8\u624b\uff0c\u5168\u9662\u548c\u5c1a\u90fd\u8dd1\u4e86\u51fa\u6765\uff0c \u8981\u5c06\u6211\u751f\u64d2\u6d3b\u6349\uff0c\u51cc\u8fb1\u5904\u6b7b\u3002\u6211\u548c\u4ed6\u4eec\u6597\u4e86\u534a\u5929\u6253\u6b7b\u4e86\u597d\u51e0\u4e2a\uff0c\u53ef\u662f\u5be1\u4e0d\u654c\u4f17\uff0c\u6597\u5f97\u529b\u7aed\u7b4b \u75b2\uff0c\u773c\u770b\u5c31\u8981\u906d\u4ed6\u4eec\u7684\u6bd2\u624b\u3002\u201d \u3000\u3000\u201c\u6b63\u5728\u5403\u7d27\uff0c\u5916\u9762\u5ffd\u7136\u53c8\u6765\u4e86\u4e2a\u6e38\u65b9\u548c\u5c1a\uff0c\u624b\u6572\u6728\u9c7c\uff0c\u53e3\u5ba3\u4f5b\u53f7\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u2018\u6e05\u5e73\u4e16 \u754c\uff0c\u6717\u6717\u4e7e\u5764\uff0c\u4f60\u4eec\u8fd9\u73ed\u4f5b\u95e8\u8d25\u7c7b\uff0c\u6562\u5728\u8fd9\u91cc\u5bb3\u4eba\u4e48\uff1f\u2019\u4e00\u9762\u5ff5\u7740\u963f\u5f25\u9640\u4f5b\uff0c\u4e00\u9762\u52a8\u624b\u6740 \u4eba\uff0c\u6740\u5f97\u6b7b\u4f24\u904d\u5730\uff0c\u6211\u770b\u7740\u4e5f\u5fc3\u8f6f\u4e86\uff0c\u4fbf\u9053\uff1a\u2018\u5e08\u5144\uff0c\u4f60\u9976\u4e86\u4ed6\u4eec\u5427\uff01\u2019\u90a3\u548c\u5c1a\u9053\uff1a\u2018\u522b\u95f4 \u5bfa\u9662\u7684\u548c\u5c1a\u53ef\u9976\uff0c\u8fd9\u95f4\u5bfa\u9662\u7684\u548c\u5c1a\u6211\u6068\u4e4b\u5165\u9aa8\uff01\u4f60\u53d1\u6148\u60b2\u5c31\u8ba9\u6211\u4e00\u4e2a\u4eba\u52a8\u624b\u3002\u2019\u4ed6\u4e00\u5200\u4e00 \u4e2a\uff0c\u7adf\u7136\u6765\u4e86\u4e2a\u65a9\u5c3d\u6740\u7edd\u3002\u7687\u89c9\u5bfa\u91cc\u6302\u6709\u4e00\u5f20\u6bd4\u4eba\u8fd8\u9ad8\u7684\u660e\u592a\u7956\u6731\u5143\u748b\u7684\u753b\u50cf\uff0c\u53ef\u7b11\u5f97\u5f88\uff0c \u5bfa\u9662\u91cc\u6302\u7687\u5e1d\u7684\u50cf\uff0c\u90a3\u50cf\u4e2d\u7684\u7687\u5e1d\uff0c\u5374\u53c8\u4e0d\u6562\u753b\u6210\u662f\u524a\u4e86\u53d1\u7684\u548c\u5c1a\u3002\u90a3\u6e38\u65b9\u548c\u5c1a\u5728\u6731\u5143\u748b\u7684 \u753b\u50cf\u4e4b\u524d\u5927\u7b11\u4e09\u901a\uff0c\u5478\u7684\u4e00\u53e3\u6d53\u75f0\u5c31\u5410\u5728\u50cf\u4e0a\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e43\u662f\u5927\u9006\u4e0d\u9053\u7684\u60ca\u4eba\u4e3e\u52a8\uff0c\u6d12\u5bb6\u867d\u7136\u4e5f\u6068\u6b3a\u538b\u826f\u5584\u7684\u5b98\u5e9c\u6076\u9738\uff0c\u89c1\u4ed6\u5bf9\u7687\u5e1d\u7684\u753b\u50cf \u5982\u6b64\u4fae\u8fb1\u5fc3\u4e2d\u4e5f\u4e0d\u7981\u5927\u4e3a\u9707\u60ca\u3002\u8fd9\u548c\u5c1a\u9053\uff1a\u2018\u4f60\u4e0d\u5fc5\u5bb3\u6015\uff0c\u6731\u5143\u748b\u672a\u505a\u7687\u5e1d\u4e4b\u524d\uff0c\u4e5f\u4e0d\u8fc7\u548c \u54b1\u4eec\u4e00\u6837\uff0c\u4ed6\u6015\u4eba\u63d0\u8d77\u4ed6\u505a\u8fc7\u548c\u5c1a\uff0c\u6211\u8fd8\u6068\u4ed6\u73b7\u6c61\u4e86\u548c\u5c1a\u8fd9\u4e2a\u79f0\u53f7\u3002\u4f60\u6562\u6740\u8fd9\u4e9b\u6deb\u50e7\uff0c\u4e3a\u4ec0 \u4e48\u5c31\u4e0d\u6562\u6068\u8fd9\u4e2a\u7eb5\u5bb9\u6deb\u50e7\uff0c\u66fe\u4e3a\u548c\u5c1a\u7684\u7687\u5e1d\uff1f\u2019\u4ed6\u8bf4\u5f97\u706b\u8d77\uff0c\u7adf\u5c06\u90a3\u753b\u50cf\u4e00\u628a\u6495\u4e86\u4e0b\u6765\uff0c\u626f \u5f97\u7c89\u788e\u3002\u6211\u88ab\u4ed6\u5f53\u5934\u4e00\u559d\u5982\u95fb\u4f5b\u6cd5\uff0c\u4e0d\u518d\u60ca\u6050\uff0c\u5408\u4ec0\u5927\u7b11\u9053\uff1a\u2018\u75db\u5feb\uff01\u75db\u5feb\uff01\u2019\u201d \u3000\u3000\u201c\u90a3\u548c\u5c1a\u9053\uff1a\u2018\u6740\u4eba\u75db\u5feb\uff0c\u6551\u4eba\u53ef\u6781\u9ebb\u70e6\u3002\u505a\u4eba\u4e5f\u4e0d\u53ef\u53ea\u56fe\u75db\u5feb\u800c\u754f\u60e7\u9ebb\u70e6\u3002\u2019\u7687\u89c9\u5bfa \u4e2d\u85cf\u6709\u5973\u5b50\u751a\u591a\uff0c\u5979\u4eec\u7684\u7236\u6bcd\u5df2\u56db\u6563\u9003\u8352\uff0c\u52a0\u4ee5\u8def\u9014\u4e0d\u9756\u653e\u5979\u4eec\u51fa\u53bb\u4e5f\u65e0\u4ece\u5bfb\u89c5\u3002\u90a3\u548c\u5c1a \u9053\uff1a\u2018\u6551\u4eba\u987b\u6551\u4e2a\u5f7b\u5e95\uff0c\u4f60\u6211\u7406\u8be5\u62a4\u9001\u5979\u4eec\uff0c\u66ff\u5979\u4eec\u627e\u5230\u5bb6\u4eba\u3002\u2019\u4ed6\u8bf4\u5f97\u5bf9\u6781\uff0c\u6740\u4eba\u6613\uff0c\u6551 \u4eba\u96be\uff0c\u6211\u4eec\u8db3\u8db3\u82b1\u4e86\u4e24\u4e2a\u6708\u7684\u5de5\u592b\uff0c\u624d\u5c06\u90a3\u4e9b\u5973\u5b50\u4e00\u4e00\u9001\u56de\u5979\u4eec\u7684\u7236\u6bcd\u5144\u5f1f\u624b\u4e0a\u3002\u81f3\u4e8e\u7687\u89c9 \u5bfa\u4e2d\u7684\u8d22\u7269\uff0c\u81ea\u7136\u4e5f\u90fd\u5206\u7ed9\u4e86\u707e\u6c11\u3002\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u4e43\u662f\u6211\u4e0b\u5c71\u4e4b\u540e\u6240\u79ef\u7684\u7b2c\u4e00\u4ef6\u529f\u5fb7\uff0c\u6b64\u751f\u600e \u4e5f\u4e0d\u4f1a\u5fd8\u8bb0\u3002\u201d \u3000\u3000\u201c\u6211\u4e0e\u90a3\u548c\u5c1a\u76f8\u805a\u4e24\u4e2a\u591a\u6708\uff0c\u610f\u6c14\u76f8\u6295\uff0c\u5f7c\u6b64\u5370\u8bc1\u6b66\u529f\uff0c\u4e5f\u4e0d\u76f8\u4e0a\u4e0b\uff0c\u9042\u7ed3\u4e3a\u77e5\u4ea4\u3002\u8fd9 \u4e2a\u548c\u5c1a\u4fbf\u662f\u4eca\u65e5\u7684\u2018\u9707\u4e09\u754c\u2019\u6bd5\u9053\u51e1\uff0c\u6211\u53ef\u771f\u60f3\u5ff5\u4ed6\uff0c\u53ef\u60dc\u81ea\u90a3\u6b21\u522b\u540e\uff0c\u4fbf\u4e00\u76f4\u6ca1\u6709\u89c1 \u8fc7\u3002\u201d \u3000\u3000\u4e91\u857e\u542c\u5f97\u51fa\u795e\uff0c\u6f6e\u97f3\u548c\u5c1a\u7684\u6545\u4e8b\u56fa\u7136\u52a8\u542c\uff0c\u6545\u4e8b\u4e2d\u7684\u6bd5\u9053\u51e1\u66f4\u60f9\u5979\u601d\u7591\uff0c\u542c\u6f6e\u97f3\u548c\u5c1a\u8bf4 \u6765\uff0c\u5b9b\u5982\u89c1\u5230\u6bd5\u9053\u51e1\u553e\u5410\u6731\u5143\u748b\u753b\u50cf\u65f6\u7684\u90a3\u526f\u795e\u6c14\u3002\u4ed6\u4e3a\u4ec0\u4e48\u90a3\u6837\u618e\u6068\u660e\u671d\u5f00\u56fd\u7684\u7687\u5e1d\uff1f\u5b9e \u662f\u8d39\u4eba\u7591\u731c\u3002\u4e91\u857e\u84e6\u7136\u60f3\u8d77\u4e86\u5f20\u4e39\u67ab\uff0c\u60f3\u8d77\u4e86\u5f20\u4e39\u67ab\u63d0\u5230\u6731\u5143\u748b\u65f6\u7684\u90a3\u5e45\u618e\u6068\u795e\u6c14\uff0c\u987f\u89c9\u4e00 \u7247\u60d8\u7136\uff0c\u601d\u6f6e\u66f4\u4e71\u3002 \u3000\u3000\u53ea\u542c\u5f97\u5468\u5c71\u6c11\u7b11\u9053\uff1a\u201c\u6f6e\u97f3\u5927\u5e08\uff0c\u8fd9\u56de\u4f60\u53ef\u4ee5\u89c1\u7740\u4ed6\u4e86\u3002\u4e00\u4e2a\u6bd5\u9053\u51e1\u5df2\u8db3\u591f\u90a3\u5c0f\u8d3c\u5e94 \u4ed8\uff0c\u518d\u52a0\u4e0a\u4f60\u8001\uff0c\u4efb\u4ed6\u4e09\u5934\u516d\u81c2\uff0c\u63d2\u7ffc\u96be\u98de\u3002\u54c8\u54c8\uff0c\u8d24\u5f1f\uff0c\u4f60\u7684\u5927\u4ec7\u5b9a\u80fd\u62a5\u590d\uff0c\u4f60\u7239\u5728\u4e5d\u6cc9 \u4e4b\u4e0b\u4e5f\u53ef\u7791\u76ee\u4e86\u3002\u201d\u4e91\u857e\u53cc\u76ee\u53d1\u76f4\u51dd\u89c6\u8fdc\u65b9\uff0c\u7adf\u7136\u4e0d\u7b54\u5c71\u6c11\u7684\u8bdd\uff0c\u8fde\u6f6e\u97f3\u4e0e\u77f3\u7fe0\u51e4\u4e5f\u89c9\u751a\u4e3a \u5947\u602a\u3002 \u3000\u3000\u65e5\u5f71\u8fd1\u5348\uff0c\u6f6e\u97f3\u548c\u5c1a\u4e00\u8dc3\u800c\u8d77\uff0c\u8bf4\u9053\uff1a\u201c\u8ddd\u90a3\u767d\u9a6c\u4e66\u751f\u4e4b\u7ea6\uff0c\u53ea\u6709\u56db\u65e5\uff0c\u54b1\u4eec\u8be5\u8d76\u53bb \u4e86\u3002\u201d\u56db\u4eba\u9c7c\u8d2f\u8d70\u51fa\u5893\u7a74\uff0c\u4e91\u857e\u4ef0\u671b\u4e07\u91cc\u6674\u7a7a\uff0c\u5b9b\u5982\u505a\u4e86\u4e00\u573a\u6076\u68a6\u3002 \u3000\u3000\u6f6e\u97f3\u548c\u5c1a\u7684\u767d\u9a6c\u6700\u5feb\uff0c\u4e91\u857e\u7684\u7ea2\u9b03\u6218\u9a6c\u6b21\u4e4b\uff0c\u6f6e\u97f3\u653e\u677e\u9a6c\u25a1\uff0c\u4e0e\u4e91\u857e\u5e76\u9a6c\u800c\u884c\uff0c\u6545\u610f\u628a \u5468\u5c71\u6c11\u4e0e\u77f3\u7fe0\u51e4\u7559\u5728\u540e\u9762\uff0c\u77f3\u7fe0\u51e4\u81ea\u662f\u6781\u4e3a\u4e0d\u60a6\uff0c\u53ef\u4ea6\u65e0\u53ef\u5948\u4f55\u3002 \u3000\u3000\u508d\u665a\u65f6\u5206\uff0c\u5230\u4e86\u5ffb\u53bf\u4e1c\u7684\u4e00\u4e2a\u5c0f\u9547\uff0c\u78b0\u5230\u4e86\u4e24\u62e8\u4eba\u9a6c\uff0c\u4e00\u62e8\u662f\u592a\u8c37\u7684\u706b\u795e\u5f39\u90dd\u5e84\u4e3b\uff0c\u4e00 \u62e8\u662f\u996e\u9a6c\u5be8\u7684\u84dd\u5be8\u4e3b\uff0c\u6f6e\u97f3\u548c\u5c1a\u4e0e\u5468\u5c71\u6c11\u90fd\u548c\u4ed6\u4eec\u719f\u8bc6\uff0c\u5f7c\u6b64\u62db\u547c\uff0c\u90fd\u662f\u540c\u5230\u9707\u4e09\u754c\u6bd5\u9053\u51e1 \u5bb6\u8d74\u4f1a\u53bb\u7684\u3002\u6f6e\u97f3\u548c\u5c1a\u4e00\u884c\u56db\u4eba\u548c\u4ed6\u4eec\u540c\u5305\u4e0b\u4e00\u5bb6\u6700\u5927\u7684\u5ba2\u5e97\u3002\u6f6e\u97f3\u548c\u5c1a\u8981\u4e86\u4e09\u95f4\u623f\u5b50\uff0c\u4ed6 \u81ea\u5df1\u4e0e\u5468\u5c71\u6c11\u540c\u4f4f\u4e00\u95f4\uff0c\u5374\u53eb\u77f3\u7fe0\u51e4\u4e0e\u4e91\u857e\u5404\u4f4f\u4e00\u95f4\uff0c\u5728\u4f17\u76ee\u777d\u777d\u4e4b\u4e0b\u77f3\u7fe0\u51e4\u54ea\u6562\u9053\u534a\u4e2a\u4e0d \u5b57\u3002 \u3000\u3000\u8fd9\u4e00\u665a\u4e91\u857e\u7ffb\u6765\u8986\u53bb\u7761\u4e0d\u7740\u89c9\uff0c\u5ffd\u542c\u5f97\u95e8\u5916\u6709\u4eba\u8f7b\u8f7b\u5f39\u4e86\u51e0\u4e0b\uff0c\u4e91\u857e\u95ee\u9053\uff1a\u201c\u8c01\u5440\uff1f\u201d \u95e8\u5916\u77f3\u7fe0\u51e4\u7684\u58f0\u97f3\u4f4e\u4f4e\u5730\u7b54\u9053\uff1a\u201c\u662f\u6211\u3002\u201d\u4e91\u857e\u6015\u5979\u95f9\u51fa\u7b11\u8bdd\uff0c\u53ea\u5f97\u6234\u597d\u5934\u5dfe\uff0c\u62ab\u4e0a\u5916\u8863\uff0c \u628a\u95e8\u5f00\u4e86\uff0c\u4f46\u89c1\u77f3\u7fe0\u51e4\u6cea\u75d5\u6ee1\u9762\uff0c\u548c\u8eab\u6251\u5165\u6000\u4e2d\u3002\u4e91\u857e\u8f7b\u8f7b\u5c06\u5979\u6276\u8d77\uff0c\u5750\u5728\u5e8a\u4e0a\uff0c\u95ee\u9053\uff1a \u201c\u4f60\u600e\u4e48\u5566\uff1f\u201d\u77f3\u7fe0\u51e4\u79cb\u6ce2\u4e00\u77a5\uff0c\u5982\u6028\u5982\u6012\uff0c\u8bf4\u9053\uff1a\u201c\u4e91\u76f8\u516c\uff0c\u6211\u53ef\u4e0d\u662f\u4f4e\u4e09\u4e0b\u56db\u4e4b\u4eba\uff0c\u6211 \u53ef\u53d7\u4e0d\u4e86\u8fd9\u53e3\u95f7\u6c14\u3002\u201d\u4e91\u857e\u9053\uff1a\u201c\u8c01\u7ed9\u6c14\u4f60\u53d7\u5566\uff1f\u201d\u77f3\u7fe0\u51e4\u9053\uff1a\u201c\u4f60\u7684\u5e08\u4f2f\u4e0e\u4f60\u7684\u4e49\u5144\uff0c\u600e \u4e48\u603b\u50cf\u6709\u610f\u79bb\u95f4\u54b1\u4eec\u4f3c\u7684\uff0c\u4ed6\u4eec\u7b80\u76f4\u4e0d\u628a\u6211\u5f53\u505a\u4f60\u7684\u59bb\u5b50\u770b\u5f85\u3002\u662f\u4e0d\u662f\u4ed6\u4eec\u5acc\u6211\u914d\u4e0d\u4e0a\u4f60\uff0c \u8981\u66ff\u4f60\u53e6\u9009\u4f73\u4eba\uff1f\u201d\u4e91\u857e\u5fcd\u4e0d\u4f4f\u5657\u55e4\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u60f3\u5230\u54ea\u91cc\u53bb\u5566\uff1f\u4ed6\u4eec\u5b9e\u662f\u4e00\u7247\u597d\u5fc3\u3002\u201d \u77f3\u7fe0\u51e4\u6012\u4e0d\u53ef\u904f\uff0c\u9053\uff1a\u201c\u597d\u5440\uff0c\u4ed6\u4eec\u8981\u66ff\u4f60\u53e6\u9009\u4f73\u4eba\u4e5f\u662f\u4e00\u7247\u597d\u5fc3\uff1f\u6211\u6709\u4ec0\u4e48\u5931\u5fb7\u4e4b\u4e8b\uff0c\u4f60 \u5c31\u5b58\u5fc3\u8981\u628a\u6211\u4f11\u4e86\uff1f\u201d\u6f78\u7136\u6cea\u4e0b\uff0c\u4e91\u857e\u624b\u8db3\u65e0\u63aa\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\u8bdd\u4ec0\u4e48\u8bdd\uff1f\u4f60\u8d8a\u626f\u8d8a\u8fdc\u5566\uff01\u6211 \u51e0\u65f6\u8bf4\u8981\u628a\u4f60\u4f11\u4e86\uff1f\u201d\u77f3\u7fe0\u51e4\u9053\uff1a\u201c\u90a3\u4f60\u3001\u4f60\uff0d\uff0d\u201d\u4e00\u8fde\u51e0\u4e2a\u201c\u4f60\u201d\u5b57\uff0c\u542b\u7f9e\u8bf4\u4e0d\u4e0b\u53bb\uff0c\u4e91 \u857e\u5fc3\u9053\uff1a\u201c\u5f04\u5047\u6210\u771f\uff0c\u8fd9\u56de\u600e\u751f\u662f\u597d\uff1f\u201d\u6b63\u8bf4\u5f97\u53e5\u201c\u4f60\u542c\u6211\u8bf4\uff0c\u6211\u90a3\u4e49\u5144\uff0d\uff0d\u201d\u77f3\u7fe0\u51e4 \u201c\u5478\u201d\u7684\u4e00\u58f0\uff0c\u622a\u7740\u8bf4\u9053\uff1a\u201c\u4f60\u90a3\u4e49\u5144\uff0c\u518d\u63d0\u4f60\u90a3\u4e49\u5144\uff0c\u6211\u5c31\u9a6c\u4e0a\u56de\u53bb\u627e\u7239\u7239\u6765\u8bc4\u7406\u3002\u4f60\u662f \u5a36\u6211\u8fd8\u662f\u5a36\u4f60\u4e49\u5144\uff1f\u54fc\uff0c\u54fc\uff0c\u6211\u6700\u6068\u4f60\u90a3\u4e49\u5144\uff01\u201d\u4e91\u857e\u5c34\u5c2c\u4e4b\u6781\uff0c\u628a\u5fc3\u4e00\u6a2a\uff0c\u5c31\u60f3\u5c06\u771f\u76f8\u8bf4 \u4e0e\u5979\u77e5\uff0c\u5ffd\u542c\u5f97\u95e8\u5916\u4e00\u58f0\u54b3\u55fd\uff0c\u5468\u5c71\u6c11\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u4f60\u4e0e\u8c01\u8bf4\u8bdd\u5440\uff1f\u201d\u4e91\u857e\u5982\u83b7\u6551 \u661f\uff0c\u4e00\u628a\u5c06\u77f3\u7fe0\u51e4\u63a8\u5f00\u9053\uff1a\u201c\u5468\u5927\u54e5\u6765\u4e86\u4f60\u5feb\u51fa\u53bb\u5427\uff0c\u62b9\u5e72\u773c\u6cea\u522b\u53eb\u4ed6\u77a7\u7740\u4e0d\u96c5\u3002\u201d\u77f3\u7fe0\u51e4 \u8fd9\u4e00\u6c14\u975e\u540c\u5c0f\u53ef\uff0c\u53cd\u8eab\u5954\u51fa\u95e8\u5916\uff0c\u5374\u53c8\u4e0d\u6599\u6070\u6070\u4e0e\u5468\u5c71\u6c11\u649e\u4e2a\u6ee1\u6000\uff0c\u5979\u6068\u5f97\u4e00\u624b\u5c06\u5468\u5c71\u6c11\u63a8 \u5f97\u51e0\u4e4e\u8dcc\u5012\uff0c\u81ea\u56de\u623f\u4e2d\uff0c\u8499\u88ab\u8fc7\u5934\uff0c\u5728\u88ab\u4e2d\u5077\u54ed\u3002 \u3000\u3000\u4e91\u857e\u89c1\u5468\u5c71\u6c11\u6df1\u591c\u5230\u6765\uff0c\u751a\u662f\u60ca\u8bb6\u3002\u53ea\u542c\u5f97\u5468\u5c71\u6c11\u8bf4\u9053\uff1a\u201c\u8d24\u59b9\uff0c\u4f60\u6211\u4eb2\u5982\u5bb6\u4eba\uff0c\u6709\u8bdd \u4e0d\u59a8\u5bf9\u6211\u5c3d\u8bf4\u3002\u4f60\u53ef\u662f\u6709\u4ec0\u4e48\u96be\u89e3\u7684\u5fc3\u4e8b\u4e48\uff1f\u201d\u4e91\u857e\u5fc3\u5934\u4e00\u9707\uff0c\u5f3a\u7b11\u9053\uff1a\u201c\u6709\u5440\uff0c\u4f60\u4e0d\u770b\u5230 \u77f3\u59d1\u5a18\u5bf9\u6211\u7ea0\u7f20\u4e48\uff1f\u8fd9\u5c31\u662f\u96be\u89e3\u7684\u5fc3\u4e8b\u3002\u8fd9\u5fc3\u4e8b\u6211\u89e3\u4e0d\u5f00\uff0c\u53ea\u6709\u9760\u5927\u54e5\u4f60\u66ff\u6211\u89e3\u5566\u3002\u201d\u5468\u5c71 \u6c11\u9762\u8272\u4e00\u53d8\uff0c\u53ea\u542c\u5f97\u4e91\u857e\u53c8\u8bf4\u9053\uff1a\u201c\u77f3\u7fe0\u51e4\u5b9e\u662f\u4e00\u4e2a\u597d\u5973\u5b50\uff0c\u4e0e\u4f60\u95e8\u6237\u76f8\u5f53\u3002\u5927\u54e5\uff0c\u4f60\u4e0e\u5979 \u4e00\u8def\u540c\u884c\uff0c\u96be\u9053\u5bf9\u5979\u6ca1\u534a\u70b9\u610f\u601d\u5417\uff1f\u201d\u5468\u5c71\u6c11\u9762\u8272\u4e00\u4e0b\u5b50\u53d8\u5f97\u96be\u770b\u4e4b\u6781\uff0c\u5fc3\u4e2d\u5982\u6253\u7ffb\u4e86\u4e00\u4e2a \u918b\u74f6\uff0c\u6599\u60f3\u4e91\u857e\u5b9a\u662f\u770b\u4e0a\u4ed6\u4eba\uff0c\u6545\u6b64\u8981\u5c06\u77f3\u7fe0\u51e4\u8ba9\u4e0e\u4ed6\u627f\u53d7\u3002\u4e91\u857e\u5fc3\u5730\u7eaf\u771f\u54ea\u6599\u5f97\u5230\u4ed6\u5982\u6b64 \u60f3\u6cd5\uff0c\u89c1\u4ed6\u9762\u8272\u9661\u53d8\uff0c\u4e0d\u89c9\u6014\u7740\u3002\u53ea\u542c\u5f97\u5468\u5c71\u6c11\u8bf4\u9053\uff1a\u201c\u4e91\u59b9\uff0c\u4f60\u522b\u7792\u7740\u6211\u5566\uff0c\u4f60\u662f\u53e6\u6709\u5fc3 \u4e8b\u3002\u201d\u4e91\u857e\u55d4\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u201d\u5468\u5c71\u6c11\u77a7\u5979\u4e00\u773c\uff0c\u5ffd\u9053\uff1a\u201c\u90a3\u5f20\u5b97\u5468\u7684\u513f\u5b50\u4e0e\u4f60\u4e00\u8def\u540c\u884c\u5bf9\u4f60 \u53ef\u597d\uff1f\u201d\u4e91\u857e\u8eab\u8eaf\u6296\u98a4\uff0c\u9053\uff1a\u201c\u5f88\u597d\uff01\u201d\u5468\u5c71\u6c11\u9053\uff1a\u201c\u53ef\u662f\u4ed6\u662f\u4f60\u5bb6\u7684\u5927\u4ec7\u4eba\uff01\u201d\u4e91\u857e\u9053\uff1a \u201c\u8fd9\u4e8b\u60c5\u4e0d\u7528\u4f60\u6765\u63d0\u9192\u6211\uff0c\u6211\u7237\u7237\u7684\u8840\u4e66\u8bf4\u5f97\u660e\u767d\u3002\u201d\u5468\u5c71\u6c11\u9053\uff1a\u201c\u8bf4\u4e9b\u4ec0\u4e48\uff1f\u201d\u4e91\u857e\u9053\uff1a \u201c\u8981\u6211\u5c06\u5f20\u5bb6\u7684\u4eba\uff0c\u4e0d\u8bba\u7537\u5973\u8001\u5e7c\uff0c\u5168\u90fd\u6740\u7edd\uff01\u201d \u3000\u3000\u5468\u5c71\u6c11\u903c\u95ee\u9053\uff1a\u201c\u53ef\u662f\u4ed6\u5bf9\u4f60\u597d\uff01\u201d\u4e91\u857e\u9053\uff1a\u201c\u597d\u4e0e\u4e0d\u597d\u662f\u4e00\u6837\uff0c\u6211\u3001\u6211\u3001\u6211\u600e\u80fd\u8fdd\u80cc \u7237\u7237\u4e34\u6b7b\u7684\u9057\u8a00\uff01\u201d\u54fd\u54bd\u7740\u8bf4\u4e0d\u4e0b\u53bb\uff0c\u8fd9\u970e\u90a3\u95f4\u771f\u60c5\u6d41\u9732\uff0c\u5468\u5c71\u6c11\u5fc3\u51c9\u4e86\u534a\u622a\uff0c\u53ef\u662f\u542c\u5979\u575a \u51b3\u8981\u5b88\u7237\u7237\u7684\u9057\u8a00\uff0c\u5374\u4e5f\u653e\u4e0b\u4e86\u53e6\u5916\u4e00\u534a\u6015\u5979\u4ee5\u654c\u4e3a\u53cb\u7684\u987e\u8651\u3002\u89c1\u4e91\u857e\u8eab\u8eaf\u98a4\u6296\uff0c\u76ee\u8574\u6cea \u5149\uff0c\u5fcd\u4e0d\u4f4f\u53c8\u7231\u53c8\u601c\uff0c\u53c8\u662f\u4f24\u5fc3\uff0c\u4f38\u624b\u53bb\u6276\uff0c\u731b\u7136\u95f4\u624b\u81c2\u4e00\u9ebb\uff0c\u6709\u5982\u7ed9\u5927\u8682\u8681\u53ee\u4e86\u4e00\u53e3\uff0c\u53ea \u542c\u5f97\u5916\u9762\u6f6e\u97f3\u548c\u5c1a\u5927\u58f0\u53eb\u9053\uff1a\u201c\u597d\u8d3c\u4eba\uff0c\u597d\u80c6\u91cf\uff0c\u6d12\u5bb6\u5728\u6b64\uff0c\u4f60\u4e5f\u5c45\u7136\u6562\u627e\u4e0a\u95e8\u5566\uff01\u201d\u5468\u5c71 \u6c11\u5fc3\u5934\u4e00\u9707\uff0c\u62d4\u51fa\u8170\u5200\uff0c\u8df3\u51fa\u5c4b\u9876\u3002\u53ea\u89c1\u5728\u768e\u768e\u6708\u5149\u4e4b\u4e0b\uff0c\u4e00\u4e2a\u9762\u5982\u51a0\u7389\u7684\u4e66\u751f\uff0c\u4f3c\u7b11\u975e \u7b11\uff0c\u8fce\u98ce\u800c\u7acb\uff0c\u53ef\u4e0d\u6b63\u662f\u81ea\u5df1\u4f20\u4e0b\u7eff\u6797\u7bad\u6240\u8981\u8ffd\u6355\u4e4b\u4eba\uff01\u90a3\u84dd\u5be8\u4e3b\u548c\u90dd\u5e84\u4e3b\u90fd\u5df2\u73b0\u51fa\u8eab\u5f62\uff0c \u4f0f\u5728\u6a90\u89d2\u3002\u6f6e\u97f3\u548c\u5c1a\u53c8\u53eb\u9053\uff1a\u201c\u6211\u4e0d\u4e0e\u5c0f\u8f88\u52a8\u624b\uff0c\u6211\u66ff\u4f60\u4eec\u53bb\u5236\u670d\u4ed6\u90a3\u767d\u9a6c\uff0c\u4f60\u4eec\u5c0f\u5fc3\u4e0d\u8981 \u8ba9\u4ed6\u9003\u4e86\uff01\u201d\u5468\u5c71\u6c11\u53eb\u9053\uff1a\u201c\u857e\u5f1f\uff0c\u5feb\u6765\uff01\u201d\u90dd\u5e84\u4e3b\u90dd\u5b9d\u693f\u53f7\u79f0\u706b\u795e\u5f39\uff0c\u4e00\u626c\u624b\u5c31\u662f\u4e09\u7c92\u706b \u73e0\uff0c\u8fce\u9762\u5c04\u81f3\uff0c\u90a3\u4e66\u751f\u8eab\u5f62\u98d8\u98d8\uff0c\u5168\u90fd\u907f\u8fc7\uff1b\u84dd\u5be8\u4e3b\u84dd\u5929\u77f3\u62bd\u51fa\u5224\u5b98\u7b14\uff0c\u53cc\u7b14\u4e00\u70b9\uff0c\u5de6\u53f3\u659c \u98de\uff0c\u8df3\u4e0a\u524d\u53bb\u52a8\u624b\uff0c\u90a3\u4e66\u751f\u4ecd\u4e0d\u62d4\u5251\uff0c\u5de6\u624b\u5212\u4e86\u534a\u4e2a\u5b64\u5f62\u53f3\u638c\u4e00\u626c\uff0c\u4e00\u62db\u201c\u957f\u6cb3\u843d\u65e5\u201d\uff0c\u8fde \u5b88\u5e26\u653b\uff0c\u5c06\u84dd\u5929\u77f3\u8feb\u5f00\u4e24\u6b65\uff1b\u5468\u5c71\u6c11\u4e00\u5200\u75be\u65ab\uff0c\u90a3\u4e66\u751f\u8eab\u5f62\u597d\u5feb\uff0c\u811a\u8ddf\u534a\u65cb\uff0c\u62e2\u6307\u4e00\u62c2\uff0c\u5468 \u5c71\u6c11\u731d\u4e0d\u53ca\u9632\uff0c\u624b\u8155\u88ab\u4ed6\u62c2\u4e86\u4e00\u4e0b\uff0c\u767b\u65f6\u7ea2\u80bf\u3002\u5c4b\u5185\u4e91\u857e\u65e9\u5df2\u8d76\u5230\uff0c\u9752\u51a5\u5b9d\u5251\u626c\u7a7a\u4e00\u95ea\uff0c\u4f5c \u52bf\u6b32\u523a\uff0c\u6708\u5149\u4e4b\u4e0b\uff0c\u53ea\u89c1\u5f20\u4e39\u67ab\u76ee\u4e2d\u4f3c\u95ea\u6cea\u5149\uff0c\u4e91\u857e\u54ac\u5b9e\u7259\u6839\uff0c\u5237\u7684\u4e00\u5251\u523a\u51fa\uff0c\u53ea\u542c\u5f97\u5f20\u4e39 \u67ab\u53eb\u9053\uff1a\u201c\u6211\u90fd\u542c\u5230\u4e86\uff0c\u4f60\u539f\u6765\u8fd9\u6837\u6068\u6211\u5417\uff1f\u201d\u8eab\u5f62\u4e00\u6643\u907f\u5f00\uff0c\u5e76\u4e0d\u8fd8\u51fb\u3002\u5468\u5c71\u6c11\u53eb\u9053\uff1a \u201c\u523a\u4ed6\u5927\u7a74\uff0c\u4e0d\u8981\u7559\u60c5\u3002\u201d\u90dd\u5b9d\u693f\u53c8\u53d1\u706b\u5f39\u4e09\u4e0b\u5939\u653b\uff0c\u5f20\u4e39\u67ab\u957f\u541f\u9053\uff1a\u201c\u5fae\u8eaf\u539f\u53ef\u5f52\u5c18\u571f\uff0c \u5176\u5948\u6069\u4ec7\u672a\u4e86\u4f55\uff01\u201d\u7331\u8eab\u75be\u8fdb\uff0c\u95ea\u8fc7\u4e86\u4e91\u857e\u4e00\u5251\uff0c\u7167\u7740\u84dd\u5929\u77f3\u9762\u95e8\u547c\u7684\u4e00\u638c\uff0c\u84dd\u5929\u77f3\u6025\u6025\u95ea \u5f00\uff0c\u5f20\u4e39\u67ab\u4e00\u8dc3\u8df3\u4e0b\uff0c\u5468\u5c71\u6c11\u53eb\u9053\uff1a\u201c\u5feb\u8ffd\uff01\u201d\u4e91\u857e\u5982\u9189\u5982\u68a6\uff0c\u8eab\u4e0d\u7531\u4e3b\uff0c\u968f\u7740\u4f17\u4eba\u8ffd\u4e0b\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u64ae\u5507\u4e00\u5578\uff0c\u4f3c\u662f\u62db\u547c\u90a3\u5339\u201c\u7167\u591c\u72ee\u5b50\u9a6c\u201d\uff0c\u4f46\u542c\u5f97\u91cc\u8bb8\u4e4b\u5916\uff0c\u9a6c\u58f0\u957f\u5636\uff0c\u6f6e\u97f3\u548c \u5c1a\u8de8\u4e0a\u767d\u9a6c\uff0c\u62e6\u622a\u5f20\u4e39\u67ab\u90a3\u5339\u767d\u9a6c\uff0c\u4e24\u5339\u767d\u9a6c\uff0c\u7adf\u4f3c\u5341\u5206\u719f\u8bc6\uff0c\u6b64\u5636\u5f7c\u5e94\uff0c\u201c\u7167\u591c\u72ee\u5b50\u9a6c\u201d \u7adf\u662f\u4e0d\u80af\u8fc7\u6765\u3002\u5f20\u4e39\u67ab\u53c8\u662f\u4e00\u58f0\u957f\u5578\uff0c\u90a3\u5339\u201c\u7167\u591c\u72ee\u5b50\u9a6c\u201d\u6602\u9996\u4eba\u7acb\uff0c\u6f6e\u97f3\u548c\u5c1a\u7167\u7740\u9a6c\u9888\u4e00 \u638c\uff0c\u90a3\u9a6c\u7ed9\u4ed6\u626b\u4e2d\uff0c\u56db\u8e44\u5c48\u5730\u3002\u5f20\u4e39\u67ab\u5fc3\u75db\u5982\u5272\uff0c\u9a82\u9053\uff1a\u201c\u8d3c\u548c\u5c1a\uff0c\u7adf\u4f24\u6211\u5b9d\u9a6c\uff01\u201d\u53cc\u638c\u8fde \u73af\u75be\u626b\uff0c\u53ef\u662f\u84dd\u3001\u90dd\u3001\u5468\u3001\u4e91\u56db\u4eba\u5df2\u5c06\u4ed6\u56f4\u5728\u6838\u5fc3\uff0c\u4ed6\u6025\u5207\u4e4b\u95f4\uff0c\u53c8\u4e0d\u80fd\u62d4\u5251\uff0c\u7adf\u662f\u51b2\u4e0d\u51fa \u53bb\u3002 \u3000\u3000\u6f6e\u97f3\u548c\u5c1a\u7b11\u9053\uff1a\u201c\u4f60\u6ca1\u6709\u5b9d\u9a6c\uff0c\u770b\u4f60\u5982\u4f55\u9003\u5f97\u51fa\u53bb\uff1f\u201d\u8bdd\u58f0\u672a\u4e86\uff0c\u4ed6\u5750\u4e0b\u90a3\u5339\u767d\u9a6c\u731b\u7136 \u6012\u5636\uff0c\u524d\u8e44\u4e00\u8d77\uff0c\u6f6e\u97f3\u548c\u5c1a\u51e0\u4e4e\u7ed9\u5b83\u63bc\u4e0b\u9a6c\u6765\u3002\u8fd9\u5339\u9a6c\u88ab\u6f6e\u97f3\u548c\u5c1a\u6536\u4f0f\u591a\u65f6\uff0c\u672c\u5df2\u542c\u4ed6\u4f7f \u5524\uff0c\u751a\u4e3a\u9a6f\u670d\uff0c\u8fd9\u65f6\u9aa4\u7136\u72c2\u6012\uff0c\u5927\u51fa\u6f6e\u97f3\u548c\u5c1a\u610f\u6599\u4e4b\u5916\uff01 \u3000\u3000\u6f6e\u97f3\u548c\u5c1a\u54ea\u91cc\u77e5\u9053\uff0c\u5f20\u4e39\u67ab\u90a3\u5339\u201c\u7167\u591c\u72ee\u5b50\u9a6c\u201d\uff0c\u6b63\u662f\u4ed6\u6240\u9a91\u7684\u8fd9\u5339\u767d\u9a6c\u6240\u751f\u3002\u5f20\u5b97\u5468 \u75bc\u7231\u513f\u5b50\uff0c\u6240\u4ee5\u8ba9\u4ed6\u9a91\u5e74\u8f7b\u529b\u58ee\u7684\u201c\u7167\u591c\u72ee\u5b50\u9a6c\u201d\uff0c\u6f6e\u97f3\u548c\u5c1a\u6253\u4f24\u4e86\u201c\u7167\u591c\u72ee\u5b50\u9a6c\u201d\uff0c\u4ed6\u7684 \u90a3\u5339\u5750\u9a91\u72c2\u5954\u53d1\u4f5c\uff0c\u6602\u9996\u8df3\u8dc3\uff0c\u629b\u4e0d\u843d\u6f6e\u97f3\u548c\u5c1a\uff0c\u5c31\u7d22\u6027\u53d1\u529b\u5411\u7740\u524d\u98de\u5954\u3002\u6f6e\u97f3\u548c\u5c1a\u867d\u662f\u6b66 \u529f\u9ad8\u5f3a\uff0c\u529b\u80fd\u4f0f\u9a6c\uff0c\u53ef\u662f\u4ed6\u65e2\u4e0d\u539f\u6253\u4f24\u81ea\u5df1\u7684\u5750\u9a91\uff0c\u88ab\u5b83\u9a6e\u7740\u53d1\u529b\u72c2\u5954\uff0c\u6025\u5207\u5236\u5b83\u4e0d\u4f4f\uff0c\u6643 \u773c\u4e4b\u524d\uff0c\u7adf\u7ed9\u5b83\u9a6e\u4e86\u5954\u51fa\u6570\u91cc\u4e4b\u5916\uff01 \u3000\u3000\u90a3\u5339\u201c\u7167\u591c\u72ee\u5b50\u9a6c\u201d\u795e\u9a8f\u975e\u5e38\uff0c\u75db\u8fc7\u4e4b\u540e\uff0c\u4e00\u58f0\u957f\u5636\uff0c\u731b\u7136\u8dc3\u8d77\uff0c\u98de\u51b2\u8fc7\u6765\u3002\u5f20\u4e39\u67ab\u5927 \u7b11\u9053\uff1a\u201c\u597d\uff0c\u597d\uff01\u201d\u84dd\u5929\u77f3\u53cc\u7b14\u6025\u843d\uff0c\u90dd\u5b9d\u68d2\u91d1\u97ad\u5012\u5377\uff0c\u5468\u5c71\u6c11\u4e00\u5200\u659c\u5954\uff0c\u4e09\u4eba\u62b1\u7740\u540c\u4e00\u5fc3 \u601d\u90fd\u662f\u610f\u56fe\u622a\u7740\u5f20\u4e39\u67ab\uff0c\u4e0d\u8ba9\u4ed6\u53bb\u62a2\u9a6c\u3002\u5f20\u4e39\u67ab\u8eab\u5f62\u4e00\u6643\uff0c\u5411\u4e91\u857e\u6240\u5b88\u7684\u65b9\u4f4d\u4e00\u51b2\uff0c\u4e91\u857e\u54ac \u7259\u4e00\u5251\uff0c\u5251\u950b\u5374\u53c8\u662f\u659c\u659c\u5730\u4ece\u5f20\u4e39\u67ab\u9762\u95e8\u63a0\u8fc7\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u90a3\u5339\u201c\u7167\u591c\u72ee\u5b50\u9a6c\u201d\u5df2\u731b \u51b2\u8fc7\u6765\uff0c\u5468\u5c71\u6c11\u8feb\u5f97\u659c\u9000\u907f\u5f00\uff0c\u5f20\u4e39\u67ab\u4e00\u8dc3\u4e0a\u9a6c\uff0c\u90dd\u5b9d\u693f\u731b\u53d1\u6697\u5668\u201c\u706b\u7075\u73e0\u201d\uff0c\u6697\u5668\u53bb\u52bf\u867d \u75be\uff0c\u90a3\u5339\u5b9d\u9a6c\u66f4\u5feb\uff0c\u7adf\u90fd\u843d\u5728\u9a6c\u7684\u540e\u9762\u3002\u53ea\u542c\u5f97\u90a3\u767d\u9a6c\u4e66\u751f\u9065\u9065\u53eb\u9053\uff1a\u201c\u6055\u4e0d\u5949\u966a\uff0c\u4e09\u65e5\u4e4b \u540e\u518d\u89c1\u5427\uff01\u201d\u7b11\u58f0\u8e44\u58f0\uff0c\u98d8\u6563\u7a7a\u9645\uff0c\u7728\u773c\u4e4b\u95f4\u4eba\u9a6c\u4ff1\u6773\u3002 \u3000\u3000\u4e91\u857e\u5446\u82e5\u6728\u9e21\uff0c\u84dd\u5929\u77f3\u3001\u90dd\u5b9d\u693f\u3001\u5468\u5c71\u6c11\u4e09\u4eba\u4e5f\u90fd\u5782\u5934\u4e27\u6c14\u3002\u8fc7\u4e86\u597d\u4e45\uff0c\u6f6e\u97f3\u548c\u5c1a\u624d\u5236 \u4f0f\u4e86\u81ea\u5df1\u90a3\u5339\u5750\u9a91\uff0c\u7f13\u7f13\u800c\u56de\uff0c\u89c1\u4f17\u4eba\u60c5\u72b6\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u54b1\u4eec\u4eca\u665a\u90fd\u683d\u4e86\u3002\u8bf4\u4e0d\u5f97\u4e09\u65e5\u4e4b \u540e\uff0c\u6211\u4e5f\u8981\u51fa\u624b\u4e86\u3002\u201d \u3000\u3000\u7b2c\u4e8c\u65e5\u7edd\u65e9\uff0c\u7fa4\u96c4\u7ed3\u4f34\u897f\u884c\uff0c\u77f3\u7fe0\u51e4\u7ecf\u6628\u665a\u4e00\u95f9\uff0c\u65e2\u662f\u751f\u6c14\uff0c\u53c8\u662f\u4f24\u5fc3\uff0c\u7adf\u4e0d\u518d\u548c\u4e91\u857e \u8bf4\u8bdd\u3002\u5468\u5c71\u6c11\u4e00\u8def\u601d\u91cf\uff0c\u7ecf\u8fc7\u6628\u665a\u7684\u9635\u4ed7\uff0c\u4ed6\u5df2\u6df1\u77e5\u5f20\u4e39\u67ab\u7684\u6b66\u529f\u5b9e\u5728\u4e91\u857e\u4e4b\u4e0a\uff0c\u5f20\u4e39\u67ab\u60c5 \u77e5\u5979\u662f\u4ec7\u654c\uff0c\u4e5f\u4e0d\u5fcd\u4f24\u5979\uff0c\u8db3\u89c1\u4e24\u4eba\u5df2\u662f\u4e92\u6709\u60c5\u610f\u3002\u4ed6\u4e00\u8def\u601d\u91cf\u95f7\u95f7\u4e0d\u4e50\uff0c\u4e5f\u4e0d\u518d\u53bb\u62db\u60f9\u4e91 \u857e\u3002\u4e91\u857e\u5012\u4e50\u5f97\u8033\u6839\u6e05\u51c0\uff0c\u53ea\u662f\u5fc3\u4e2d\u7684\u82e6\u95f7\uff0c\u5374\u662f\u4e0e\u65e5\u4ff1\u589e\u3002 \u3000\u3000\u4e09\u65e5\u4e4b\u540e\uff0c\u5230\u4e86\u83b7\u9e7f\uff0c\u6bd5\u9053\u51e1\u6240\u5c45\u7684\u5c71\u6751\uff0c\u5c71\u73af\u6c34\u7ed5\uff0c\u5f62\u52bf\u751a\u4e3a\u9669\u5cfb\u3002\u6f6e\u97f3\u548c\u5c1a\u4e00\u9a6c\u9886 \u5148\uff0c\u901a\u540d\u5165\u89c1\uff0c\u53ea\u89c1\u6bd5\u5bb6\u4e4b\u4e2d\u5df2\u662f\u7fa4\u96c4\u6bd5\u96c6\uff0c\u90fd\u9732\u51fa\u4e86\u7126\u6025\u7684\u795e\u60c5\u3002\u6f6e\u97f3\u4e0e\u6bd5\u9053\u51e1\u4e8c\u5341\u4f59\u5e74 \u4e0d\u89c1\uff0c\u81ea\u662f\u72c2\u559c\u4e0d\u7981\uff0c\u5404\u9053\u601d\u5ff5\u3002\u5bbe\u4e3b\u5750\u5b9a\uff0c\u63a5\u5230\u7eff\u6797\u7bad\u3001\u88ab\u5f20\u4e39\u67ab\u7ea6\u6765\u7684\u7eff\u6797\u7fa4\u8c6a\u90fd\u8feb\u4e0d \u53ca\u5f85\uff0c\u7eb7\u7eb7\u5411\u5468\u5c71\u6c11\u63a2\u95ee\uff0c\u6240\u8981\u5bf9\u4ed8\u7684\u767d\u9a6c\u4e66\u751f\u7a76\u7adf\u662f\u4f55\u7b49\u6765\u5386\u3002 \u3000\u3000\u6bd5\u9053\u51e1\u9053\uff1a\u201c\u4ee4\u5c0a\u91d1\u5200\u5be8\u4e3b\u4e0e\u6211\u867d\u672a\u66fe\u4f1a\u9762\uff0c\u5374\u4e45\u5df2\u809d\u80c6\u76f8\u7167\uff0c\u4ed6\u6240\u8981\u8ffd\u6355\u7684\u5b9a\u662f\u4e07\u6076 \u4e0d\u8d66\u4e4b\u5f92\uff0c\u53ea\u770b\u90a3\u8d3c\u4eba\u4eca\u65e5\u7684\u5e03\u7f6e\uff0c\u5df2\u662f\u5c45\u5fc3\u9669\u6076\u4e4b\u6781\uff0c\u4f60\u4e0d\u5fc5\u7ec6\u8bf4\uff0c\u6211\u4e5f\u8981\u4e0e\u4ed6\u52a8\u624b\u3002\u201d \u4e00\u773c\u77a5\u53bb\uff0c\u53ea\u6709\u77f3\u7fe0\u51e4\u662f\u4e2a\u5973\u5b50\uff0c\u6bd5\u9053\u51e1\u62c8\u987b\u7b11\u9053\uff1a\u201c\u6055\u6211\u773c\u62d9\u4e0d\u77e5\u7eff\u6797\u9053\u4e2d\u51fa\u4e86\u4e00\u4f4d\u5973\u4e2d \u8c6a\u6770\u3002\u201d\u5468\u5c71\u6c11\u4ee3\u7b54\u9053\uff1a\u201c\u8fd9\u4f4d\u59d1\u5a18\u6b63\u662f\u8f70\u5929\u96f7\u7684\u638c\u73e0\u3002\u201d\u77f3\u7fe0\u51e4\u4e0a\u524d\u65bd\u4e86\u534a\u793c\u6717\u58f0\u8bf4\u9053\uff1a \u201c\u5bb6\u7236\u6709\u4fe1\u95ee\u5019\u3002\u201d\u6bd5\u9053\u51e1\u5927\u559c\u7b11\u9053\uff1a\u201c\u8f70\u5929\u96f7\u6709\u4e8b\u5429\u5490\uff0c\u6211\u8d74\u6c64\u8e48\u706b\u4ea6\u5728\u6240\u4e0d\u8f9e\uff0c\u8fd9\u5c01\u4fe1 \u6211\u5df2\u7b49\u4e86\u5341\u591a\u5e74\u4e86\uff01\u201d\u62c6\u4fe1\u4e00\u770b\uff0c\u9762\u8272\u5ffd\u7136\u4e00\u53d8\u3002 \u3000\u3000\u4e91\u857e\u5fc3\u4e2d\u4e03\u4e0a\u516b\u843d\uff0c\u4e0d\u77e5\u4fe1\u4e2d\u8bf4\u7684\u7a76\u7adf\u662f\u4ec0\u4e48\uff0c\u53ea\u89c1\u9707\u4e09\u754c\u6bd5\u9053\u51e1\u770b\u4e86\u53c8\u770b\uff0c\u628a\u4fe1\u6162\u6162 \u6298\u8d77\uff0c\u653e\u5165\u6000\u4e2d\u3002\u5468\u5c71\u6c11\u6b63\u60f3\u8bf4\u90a3\u767d\u9a6c\u4e66\u751f\u7684\u6765\u5386\uff0c\u6bd5\u9053\u51e1\u773c\u5149\u4e00\u77a5\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u5fc5 \u5148\u8bf4\uff0c\u6211\u6709\u5206\u6570\u3002\u201d\u773c\u5149\u77a5\u5230\u4e91\u857e\uff0c\u5468\u5c71\u6c11\u9053\uff1a\u201c\u8fd9\u4f4d\u82f1\u96c4\u662f\u6f6e\u97f3\u5927\u5e08\u7684\u5e08\u4f84\uff0c\u4ea6\u662f\u77f3\u8001\u82f1 \u96c4\u7684\u5973\u5a7f\u3002\u201d\u6bd5\u9053\u51e1\u9053\uff1a\u201c\u8f70\u5929\u96f7\u7684\u5973\u5a7f\u90fd\u6765\u4e86\u53ef\u60dc\u4ed6\u6ca1\u6709\u6765\uff01\u8fd9\u6bb5\u516c\u6848\u53ea\u6050\u8fd8\u662f\u65e0\u6cd5\u4e86 \u65ad\u3002\u201d\u53cc\u773c\u4e00\u7ffb\uff0c\u6602\u9996\u671d\u5929\uff0c\u9ed1\u6e17\u6e17\u7684\u9762\u4e0a\u900f\u51fa\u7ea2\u5149\uff0c\u5ea7\u4e0a\u7fa4\u96c4\u5c4f\u795e\u9759\u6c14\uff0c\u53ea\u542c\u5f97\u4ed6\u5e72\u7b11\u4e00 \u58f0\uff0c\u5411\u4e91\u857e\u3001\u7fe0\u51e4\u62db\u624b\u8bf4\u9053\uff1a\u201c\u90fd\u968f\u6211\u6765\uff01\u201d\u53c8\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u82e5\u7136\u90a3\u767d\u9a6c\u4e66\u751f\u7a81\u7136\u6765\u88ad\uff0c\u6f6e \u97f3\u5e08\u5144\uff0c\u4f60\u6682\u4ee3\u6211\u5e94\u4ed8\u3002\u201d\u4ed6\u867d\u662f\u8fd8\u4fd7\u5df2\u4e45\u5bf9\u6f6e\u97f3\u548c\u5c1a\u4ecd\u7528\u6614\u65e5\u79f0\u547c\u3002 \u3000\u3000\u4e91\u857e\u3001\u7fe0\u51e4\u8ddf\u4ed6\u7a7f\u5eca\u7ed5\u5c4b\uff0c\u8d70\u4e0a\u4e00\u5ea7\u5c0f\u697c\uff0c\u5c0f\u697c\u6302\u6709\u4e00\u5e45\u753b\uff0c\u57ce\u5eca\u5c71\u6c34\u82b1\u6811\u6276\u758f\uff0c\u4e0e\u77f3 \u82f1\u5ba4\u4e2d\u90a3\u5e45\u5b9b\u5982\u51fa\u81ea\u4e00\u4eba\u624b\u7b14\uff0c\u53ea\u662f\u6bd4\u77f3\u82f1\u90a3\u5e45\u5374\u5c0f\u5f97\u591a\u3002\u5c1a\u672a\u5750\u5b9a\uff0c\u4e00\u4e2a\u5c0f\u5b69\u8dd1\u4e86\u8fdb\u6765\uff0c \u6307\u7740\u90a3\u5e45\u753b\u9053\uff1a\u201c\u7239\u7239\u7ed9\u6211\uff0c\u7ed9\u6211\u73a9\uff01\u201d\u5c0f\u5b69\u5e74\u7ea6\u4e03\u516b\u5c81\uff0c\u751f\u5f97\u7c89\u96d5\u7389\u7422\uff0c\u751a\u662f\u53ef\u7231\u3002\u6bd5\u9053 \u51e1\u6380\u987b\u4e00\u7b11\uff0c\u5c06\u90a3\u5e45\u753b\u53d6\u4e86\u4e0b\u6765\uff0c\u63b7\u7ed9\u5b69\u5b50\u9053\uff1a\u201c\u62ff\u53bb\uff01\u4eca\u65e5\u53ef\u89c1\u771f\u753b\uff0c\u8fd9\u5e45\u8d5d\u54c1\uff0c\u6211\u4e5f\u4e0d \u5fc5\u5b9d\u8d1d\u5b83\u4e86\u3002\u201d\u5b69\u5b50\u53d6\u4e86\u90a3\u753b\uff0c\u53c8\u7b11\u53c8\u8df3\uff0c\u51fa\u53bb\u81ea\u73a9\uff0c\u60f3\u662f\u4ed6\u5df2\u5411\u7236\u4eb2\u6c42\u8fc7\u591a\u6b21\uff0c\u4eca\u65e5\u65b9\u624d \u5230\u624b\u3002 \u3000\u3000\u6bd5\u9053\u51e1\u76ee\u9001\u5b69\u5b50\u4e0b\u697c\uff0c\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u77f3\u59d1\u5a18\uff0c\u90a3\u5e74\u6211\u5230\u4f60\u5bb6\u4e4b\u65f6\uff0c\u4f60\u4e5f\u548c\u4ed6\u4e00\u822c\u5927 \u5c0f\u3002\u4f60\u8fd8\u8bb0\u5f97\u5417\uff1f\u201d\u7fe0\u51e4\u9053\uff1a\u201c\u6211\u7239\u5367\u5e8a\u4e24\u6708\uff0c\u6b64\u4e8b\u6211\u600e\u80fd\u5fd8\u4e86\uff1f\u201d \u3000\u3000\u6bd5\u9053\u51e1\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6211\u5f53\u65e5\u751a\u662f\u51f6\u6076\uff0c\u4f60\u76f4\u81f3\u4eca\u65e5\uff0c\u8fd8\u8bb0\u6068\u6211\u4e48\uff1f\u4f60\u7239\u7239\u53ef\u5bf9\u4f60\u8bf4 \u4e86\u6ca1\u6709\uff1f\u201d\u77f3\u7fe0\u51e4\u9053\uff1a\u201c\u6211\u7239\u5012\u4e00\u70b9\u4e5f\u4e0d\u6068\u4f60\u3002\u4eca\u65e5\u82e5\u5f97\u4f60\u51fa\u624b\u76f8\u52a9\u62a5\u4ec7\u6211\u4e5f\u8981\u5411\u4f60\u9053 \u8c22\u3002\u201d\u6bd5\u9053\u51e1\u8be7\u9053\uff1a\u201c\u62a5\u4ec7\uff0c\u62a5\u4ec0\u4e48\u4ec7\uff1f\u201d\u77f3\u7fe0\u51e4\u5947\u9053\uff1a\u201c\u7239\u7239\u4fe1\u4e2d\u8fd8\u672a\u8bf4\u5f97\u6e05\u695a\u5417\uff1f\u90a3\u767d \u9a6c\u4e66\u751f\u4e43\u662f\u4e91\u76f8\u516c\u7684\u5927\u4ec7\u4eba\uff01\u201d\u6bd5\u9053\u51e1\u770b\u4e86\u5979\u4e00\u773c\uff0c\u95ee\u9053\uff1a\u201c\u662f\u4e48\uff1f\u201d\u4e91\u857e\u9762\u8272\u82cd\u767d\uff0c\u9053\uff1a \u201c\u77f3\u59d1\u5a18\u8bf4\u5f97\u4e0d\u9519\u3002\u53ea\u662f\u590d\u4ec7\u4e4b\u4e8b\uff0c\u6211\u53ef\u4e0d\u613f\u5047\u624b\u4ed6\u4eba\uff01\u201d\u6bd5\u9053\u51e1\u9053\uff1a\u201c\u597d\u5fd7\u6c14\uff01\u6211\u53ef\u60f3\u4e0d \u5230\u5176\u4e2d\u8fd8\u6709\u8bb8\u591a\u60c5\u4e8b\uff0c\u5012\u6559\u6211\u4e3a\u96be\u4e86\u3002\u201d\u77f3\u7fe0\u51e4\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u6ca1\u6709\u60f3\u5230\uff01\u6211\u7239\u4fe1\u4e2d\u5199\u7684\u7a76\u7adf \u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6bd5\u9053\u51e1\u6de1\u6de1\u4e00\u7b11\uff0c\u534a\u8fb9\u8138\u671d\u7740\u7fe0\u51e4\uff0c\u6c89\u58f0\u8bf4\u9053\uff1a\u201c\u4eca\u65e5\u6211\u7ea6\u4f60\u5230\u6765\uff0c\u4e43\u662f\u8981\u7ed9\u4f60\u8bf4\u4e00\u4e2a \u6545\u4e8b\uff0c\u8fd9\u4e2a\u6545\u4e8b\uff0c\u4f60\u7239\u4e5f\u672a\u77e5\u5f97\u5468\u5168\u3002\u201d \u3000\u3000\u201c\u5f88\u4e45\u5f88\u4e45\u4ee5\u524d\uff0c\u6709\u4e00\u4e2a\u8001\u548c\u5c1a\uff0c\u7cbe\u901a\u6b66\u529f\uff0c\u5999\u63a7\u4e16\u6cd5\u3002\u5176\u65f6\u6b63\u662f\u5f02\u65cf\u5165\u4e3b\u4e2d\u534e\uff0c\u5929\u4e0b \u6df7\u4e71\uff0c\u6709\u4e24\u4e2a\u7ed3\u62dc\u5144\u5f1f\uff0c\u5927\u54e5\u662f\u79c1\u76d0\u8d29\u5b50\uff0c\u5f1f\u5f1f\u662f\u5c0f\u53eb\u5316\u5b50\u3002\u4e24\u4eba\u90fd\u80f8\u6709\u5927\u5fd7\uff0c\u8981\u4e3e\u4e49\u5175\u9a71 \u9010\u80e1\u4eba\u3002\u90a3\u8001\u548c\u5c1a\u5374\u6bd4\u4ed6\u4eec\u90fd\u62a2\u5148\u4e00\u6b65\uff0c\u5728\u6dee\u897f\u5148\u7ad6\u8d77\u4e86\u4e49\u65d7\u3002\u2026\u2026\u201d \u3000\u3000\u4e91\u857e\u5ffd\u62a2\u7740\u9053\uff1a\u201c\u90a3\u8001\u548c\u5c1a\u6709\u4e24\u4e2a\u5f92\u5f1f\uff0c\u5c31\u662f\u8fd9\u4e2a\u79c1\u76d0\u8d29\u5b50\u4e49\u5144\u548c\u53eb\u5316\u5b50\u4e49\u5f1f\u3002\u201d\u6bd5\u9053 \u51e1\u76ee\u5149\u4e00\u95ea\uff0c\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u4f60\u4e5f\u8fd8\u77e5\u5f97\u4e0d\u5168\uff0c\u90a3\u8001\u548c\u5c1a\u4e0d\u662f\u6709\u4e24\u4e2a\u5f92\u5f1f\uff0c\u800c\u662f\u6709\u4e09\u4e2a\u5f92\u5f1f\u3002 \u8fd9\u6b8b\u7f3a\u4e0d\u5168\u7684\u6545\u4e8b\uff0c\u662f\u8c01\u8bf4\u4f60\u542c\u7684\uff1f\u201d \u3000\u3000\u4e91\u857e\u9053\uff1a\u201c\u5b9e\u4e0d\u76f8\u7792\uff0c\u4fbf\u662f\u4eca\u65e5\u4f60\u4eec\u6240\u8981\u5bf9\u4ed8\u4e4b\u4eba\u3002\u4ed6\u672c\u8981\u4e0e\u6211\u8bf4\u4e09\u4e2a\u6545\u4e8b\uff0c\u7b2c\u4e00\u4e2a\u6545 \u4e8b\u7684\u5f00\u5934\u4e00\u6bb5\u4e0e\u4f60\u9002\u624d\u6240\u8bf4\u7684\u65e0\u5f02\uff0c\u7b2c\u4e8c\u4e2a\u6545\u4e8b\u6211\u5df2\u81ea\u77e5\uff0c\u7b2c\u4e09\u4e2a\u6545\u4e8b\u4ed6\u5c1a\u672a\u8bf4\u3002\u201d\u77f3\u7fe0\u51e4 \u597d\u751f\u60ca\u5f02\uff0c\u770b\u90a3\u6bd5\u9053\u51e1\u503e\u795e\u5728\u542c\uff0c\u9762\u4e0d\u6539\u5bb9\uff0c\u5374\u4f3c\u65e9\u5728\u610f\u6599\u4e4b\u4e2d\u7684\u3002\u53ea\u542c\u5f97\u6bd5\u9053\u51e1\u63a5\u53e3\u8bf4 \u9053\uff1a\u201c\u90a3\u5c31\u662f\u4e86\u3002\u4ed6\u6bd4\u6211\u77e5\u5f97\u66f4\u591a\uff0c\u6211\u4eca\u65e5\u6240\u8bf4\uff0c\u4e5f\u8bb8\u8fd8\u53ea\u662f\u4ed6\u7b2c\u4e09\u4e2a\u6545\u4e8b\u7684\u4e00\u534a\u3002\u201d\u77f3\u7fe0 \u51e4\u9762\u8272\u6c89\u6697\uff0c\u7785\u7740\u4e91\u857e\uff0c\u4f3c\u662f\u57cb\u6028\u201c\u4ed6\u201d\u4e00\u76f4\u5c06\u81ea\u5df1\u8499\u5728\u9f13\u4e2d\u3002 \u3000\u3000\u6bd5\u9053\u51e1\u9053\uff1a\u201c\u6b64\u4e8b\u4ed6\u65e2\u8bf4\u4e86\u4e00\u4e9b\uff0c\u6211\u4e5f\u5c31\u4e0d\u5fc5\u85cf\u8d77\u59d3\u540d\u3002\u90a3\u79c1\u76d0\u8d29\u5b50\u662f\u5f20\u58eb\u8bda\uff0c\u90a3\u5c0f\u53eb \u5316\u662f\u6731\u5143\u748b\uff0c\u90a3\u8001\u548c\u5c1a\u4fbf\u662f\u4ed6\u4eec\u7684\u5e08\u7236\u53eb\u5f6d\u83b9\u7389\u3002\u201d \u3000\u3000\u201c\u5f6d\u83b9\u7389\u8fd8\u6709\u4e00\u4e2a\u5f92\u5f1f\u53eb\u6bd5\u51cc\u865a\uff0c\u6b64\u4eba\u719f\u8bfb\u5175\u4e66\uff0c\u591a\u8c0b\u8db3\u667a\uff0c\u66fe\u8ddf\u5f6d\u83b9\u7389\u8d70\u904d\u5929\u4e0b\uff0c\u626e \u8fc7\u5404\u79cd\u8eab\u4efd\u7684\u4eba\uff0c\u4e5f\u66fe\u505a\u8fc7\u548c\u5c1a\u505a\u8fc7\u53eb\u5316\u3002\u201d \u3000\u3000\u201c\u6731\u5143\u748b\u5728\u672a\u6295\u5165\u7ea2\u5dfe\u519b\u4e4b\u524d\uff0c\u66fe\u5728\u4ed6\u5e08\u7236\u7684\u4e49\u519b\u4e4b\u4e2d\uff0c\u505a\u8fc7\u4e00\u4e2a\u5c0f\u9996\u9886\u3002\u6b64\u4e8b\u60f3\u90a3\u4eba \u5df2\u5bf9\u4f60\u8bf4\u4e86\u3002\u5176\u65f6\u5143\u519b\u5982\u767e\u8db3\u4e4b\u866b\uff0c\u6b7b\u800c\u4e0d\u50f5\uff0c\u5728\u4e00\u65f6\u5e76\u8d77\u7684\u7fa4\u96c4\u4e4b\u4e2d\uff0c\u5f6d\u83b9\u7389\u5175\u529b\u4e0d\u5927\uff0c \u7ed9\u5143\u519b\u8d25\u8fc7\u51e0\u6b21\uff0c\u5f62\u52bf\u751a\u9669\u3002\u6731\u5143\u748b\u91ce\u5fc3\u6781\u5927\uff0c\u5728\u4e00\u6b21\u5175\u8d25\u52bf\u5371\u4e4b\u65f6\uff0c\u5c06\u5e08\u7236\u5356\u4e0e\u5143\u519b\uff0c\u81ea \u5df1\u5374\u4e00\u628a\u6cea\u4e00\u628a\u9f3b\u6d95\u7684\u5192\u5145\u597d\u4eba\uff0c\u6536\u62fe\u6b8b\u5c40\uff0c\u5c06\u5e08\u7236\u7684\u90e8\u5c5e\u5e26\u5230\u5f53\u65f6\u58f0\u52bf\u6700\u5927\u7684\u7ea2\u5dfe\u519b\u4e2d\uff0c \u60f3\u7528\u7ea2\u5dfe\u519b\u4f5c\u4e3a\u672c\u94b1\uff0c\u4e89\u593a\u5929\u4e0b\u3002\u201d \u3000\u3000\u201c\u6731\u5143\u748b\u4ee5\u4e3a\u5e08\u7236\u5fc5\u6b7b\uff0c\u5176\u5b9e\u672a\u6b7b\uff0c\u5728\u5143\u519b\u5c06\u4ed6\u89e3\u4e0a\u5317\u4eac\u7684\u9014\u4e2d\uff0c\u6bd5\u51cc\u865a\u4e07\u91cc\u8ffd\u968f\uff0c\u591a \u65b9\u8bbe\u8ba1\uff0c\u7ec8\u4e8e\u628a\u4ed6\u6551\u4e86\u3002\u5176\u4e2d\u7ecf\u8fc7\u66f2\u6298\u590d\u6742\uff0c\u5728\u6b64\u6211\u4e5f\u4e0d\u5fc5\u7ec6\u8bf4\u3002\u201d \u3000\u3000\u201c\u5176\u65f6\u4e2d\u539f\u5df2\u6210\u6df7\u4e71\u4e4b\u5c40\uff0c\u5f6d\u83b9\u7389\u5e08\u5f92\u4e8c\u4eba\u56de\u4e0d\u4e86\u6c5f\u5357\uff0c\u4e43\u53e6\u7ec4\u4e49\u5175\uff0c\u56fe\u8c0b\u590d\u8d77\u3002\u4f46\u5317 \u65b9\u5c1a\u662f\u5143\u519b\u7684\u6839\u636e\u4e4b\u5730\uff0c\u5f6d\u3001\u6bd5\u4e8c\u4eba\u6b63\u56fe\u8d77\u4e49\uff0c\u4fbf\u7ed9\u5143\u519b\u5927\u4e3e\u8fdb\u653b\uff0c\u5728\u4e00\u6b21\u6218\u5f79\u4e4b\u4e2d\uff0c\u5f6d\u83b9 \u7389\u53d7\u4e86\u91cd\u4f24\uff0c\u4e34\u6b7b\u4e4b\u65f6\u5bf9\u6bd5\u51cc\u865a\u9053\uff1a\u2018\u4eba\u751f\u96be\u514d\u4e00\u6b7b\uff0c\u6211\u800c\u4eca\u6b7b\u5728\u6c99\u573a\uff0c\u80dc\u4e8e\u6b7b\u5728\u7f27\u7ec1\u4e4b\u4e2d \u591a\u77e3\u3002\u53ea\u662f\u8fd8\u6709\u4e00\u4ef6\u672a\u4e86\u4e4b\u4e8b\uff0c\u5f97\u4f60\u66ff\u6211\u53bb\u529e\u3002\u2019\u201d \u3000\u3000\u201c\u2018\u770b\u4eca\u65e5\u4e4b\u52bf\uff0c\u6c49\u65cf\u91cd\u5149\uff0c\u5df2\u662f\u5fc5\u7136\u4e4b\u5c40\u3002\u5929\u4e0b\u7fa4\u96c4\uff0c\u80fd\u767b\u5927\u5b9d\u8005\uff0c\u636e\u6211\u770b\u6765\uff0c\u5fc5\u662f \u4f60\u7684\u4e24\u4e2a\u5e08\u5144\uff0c\u975e\u6731\u5373\u5f20\u3002\u4ed6\u4eba\u65ad\u96be\u95ee\u9f0e\u3002\u2019\u201d \u3000\u3000\u201c\u2018\u6731\u5143\u748b\u96c4\u624d\u5927\u7565\uff0c\u5374\u662f\u523b\u8584\u5be1\u6069\uff0c\u5012\u4e0d\u662f\u6211\u6068\u4ed6\u51fa\u5356\u8fc7\u6211\uff0c\u6211\u5b9e\u662f\u4e0d\u6b32\u4ed6\u4e3a\u7687\u5e1d\uff0c \u91cd\u82e6\u9ece\u6c11\u3002\u6211\u81ea\u5c0f\u6d41\u6d6a\u6c5f\u6e56\uff0c\u5468\u6e38\u5929\u4e0b\uff0c\u5bf9\u5404\u5904\u5c71\u5ddd\u9669\u8981\uff0c\u7528\u5175\u653b\u5b88\u4e4b\u5730\uff0c\u4e86\u5982\u6307\u638c\uff0c\u66fe\u753b \u6709\u4e00\u4efd\u519b\u7528\u7684\u5929\u4e0b\u8be6\u56fe\uff0c\u8c01\u5f97\u6b64\u56fe\uff0c\u4fbf\u53ef\u56fe\u738b\u9738\u4e4b\u4e1a\u3002\u4f60\u66ff\u6211\u5c06\u8fd9\u4efd\u5730\u56fe\uff0c\u4ea4\u4e0e\u5f20\u58eb\u8bda \u5427\u3002\u2019\u201d \u3000\u3000\u201c\u6bd5\u51cc\u865a\u53d7\u4e86\u91cd\u6258\uff0c\u5192\u7edd\u9669\u4e07\u96be\uff0c\u95f4\u5173\u5357\u4e0b\u3002\u53ef\u60dc\u4ed6\u6765\u5f97\u8fdf\u4e86\uff0c\u6765\u5230\u6c5f\u5357\u4e4b\u65f6\uff0c\u6731\u3001\u5f20 \u4e89\u96c4\u4e4b\u5c40\u5df2\u53d8\uff0c\u5f20\u58eb\u8bda\u88ab\u56f0\u5728\u82cf\u5dde\u4e00\u9685\u773c\u89c1\u5373\u5c06\u88ab\u706d\u3002\u5f20\u58eb\u8bda\u4e0d\u613f\u88ab\u56f0\u800c\u6b7b\uff0c\u4e43\u4f5c\u4e7e\u5764\u4e00 \u63b7\uff0c\u7ea6\u4e86\u6731\u5143\u748b\u5728\u957f\u6c5f\u4e2d\u4f5c\u6700\u540e\u7684\u751f\u6b7b\u51b3\u6218\u3002\u201d \u3000\u3000\u201c\u6bd5\u51cc\u865a\u529d\u4ed6\u4fdd\u5168\u5b9e\u529b\uff0c\u51b2\u51fa\u9003\u4ea1\uff0c\u5f20\u58eb\u8bda\u5927\u7b11\u9053\uff1a\u2018\u6211\u600e\u4e48\u80fd\u5931\u4fe1\u7ed9\u5c0f\u53eb\u5316\uff01\u2019\u5f53\u665a \u53eb\u4e86\u4e00\u540d\u753b\u5320\uff0c\u753b\u4e0b\u4e86\u82cf\u5dde\u7684\u98ce\u666f\u56fe\u3002\u5f20\u58eb\u8bda\u9177\u7231\u56f4\u68cb\uff0c\u5f53\u665a\u8fd8\u795e\u8272\u5982\u5e38\u4e0e\u6bd5\u51cc\u865a\u996e\u9152\u4e0b \u68cb\uff0c\u4e0b\u5230\u5929\u660e\uff0c\u753b\u4ea6\u7ed8\u5c31\uff0c\u8fd9\u56fe\u753b\u5f97\u5341\u5206\u8be6\u7ec6\uff0c\u5c71\u4e18\u57ce\u5854\uff0c\u5c3d\u90fd\u753b\u5728\u91cc\u9762\u3002\u5f20\u58eb\u8bda\u5c06\u591a\u5e74\u79ef \u805a\u7684\u73cd\u5b9d\u4e0e\u53ca\u4ed6\u5e08\u7236\u5f6d\u548c\u5c1a\u6240\u7ed8\u7684\u90a3\u4efd\u8be6\u7ec6\u5730\u56fe\uff0c\u90fd\u85cf\u5728\u4e00\u4e2a\u9690\u50fb\u7684\u5730\u65b9\uff0c\u5728\u753b\u4e0a\u505a\u4e0b\u4e86\u8bb0 \u53f7\uff0c\u53eb\u4e00\u4e2a\u4eb2\u4fe1\u5e26\u8fd9\u5e45\u753b\u4e0e\u4ed6\u7684\u513f\u5b50\u8fde\u591c\u9003\u4ea1\u3002\u6bd5\u51cc\u865a\u5927\u4e3a\u611f\u52a8\uff0c\u4e0d\u613f\u79bb\u5f00\u5371\u57ce\uff0c\u6700\u540e\u5728\u957f \u6c5f\u4e00\u6218\uff0c\u7adf\u5148\u5f20\u58eb\u8bda\u6218\u6b7b\u3002\u4ed6\u6709\u4e00\u4e2a\u5c0f\u513f\u5b50\u968f\u7740\u4e71\u519b\u9003\u51fa\uff0c\u5e78\u5f97\u4fdd\u5168\u3002\u201d \u3000\u3000\u201c\u5f20\u58eb\u8bda\u6240\u85cf\u7684\u73cd\u5b9d\u4e5f\u8fd8\u7f62\u4e86\uff0c\u90a3\u5e45\u519b\u7528\u5730\u56fe\u53ef\u662f\u65e0\u4ef7\u4e4b\u5b9d\uff0c\u82e5\u7136\u6709\u4eba\u5f97\u4e86\uff0c\u5927\u53ef\u4e0e\u6731 \u7684\u5b50\u5b59\u518d\u4e89\u5929\u4e0b\uff0c\u518d\u5ed6\u96cc\u96c4\u3002\u201d \u3000\u3000\u77f3\u7fe0\u51e4\u542c\u5f97\u60ca\u5fc3\u52a8\u9b44\uff0c\u95ee\u9053\uff1a\u201c\u90a3\u5e45\u753b\u5462\uff1f\u201d\u8bdd\u58f0\u672a\u6bd5\uff0c\u5ffd\u542c\u5f97\u201c\u55e4\u201d\u7684\u4e00\u58f0\u4e00\u679d\u84dd\u8272 \u706b\u7bad\u51b2\u5929\u76f4\u4e0a\uff0c\u6709\u4eba\u53eb\u9053\uff1a\u201c\u90a3\u767d\u9a6c\u4e66\u751f\u6765\u4e86\uff01\u201d \u3000\u3000\u6bd5\u9053\u51e1\u4ece\u5bb9\u4e0d\u8feb\uff0c\u7f13\u7f13\u7ad9\u7acb\uff0c\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u8fd9\u5e45\u753b\u5c31\u5728\u77f3\u59d1\u5a18\u4f60\u7684\u5bb6\u4e2d\uff0c\u73b0\u5728\u6216\u8bb8\u5df2\u5230 \u4e86\u8fd9\u767d\u9a6c\u4e66\u751f\u7684\u624b\u91cc\uff01\u201d\u77f3\u7fe0\u51e4\u5f20\u76ee\u7ed3\u820c\uff0c\u53ea\u542c\u5f97\u6bd5\u9053\u51e1\u53c8\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u4f60\u7239\u7684\u4fe1\u5c31\u662f\u8981\u6211 \u89c1\u89c1\u8fd9\u4f4d\u767d\u9a6c\u4e66\u751f\uff0c\u5373\u975e\u6709\u4e8b\u6c42\u52a9\uff0c\u66f4\u975e\u8bf7\u6211\u62a5\u4ec7\u3002\u4e00\u5207\u4e8b\u60c5\uff0c\u90fd\u4efb\u4ece\u6211\u7684\u4e3b\u610f\u5904\u7f6e\u3002\u53ea\u662f \u6211\u8fd8\u6709\u6570\u4e8b\u672a\u660e\uff0c\u53ef\u60dc\u4f60\u7684\u7239\u7239\u53c8\u4e0d\u80af\u524d\u6765\u89c1\u6211\u3002\u4eca\u65e5\u4e4b\u4e8b\uff0c\u5012\u6559\u6211\u96be\u4e8e\u5904\u7f6e\u4e86\uff01\u201d \u3000\u3000\u4e91\u857e\u6014\u6014\u51fa\u795e\uff0c\u4f46\u542c\u5f97\u5f20\u4e39\u67ab\u7684\u7b11\u58f0\u5df2\u8fdc\u8fdc\u4f20\u81f3\u3002\u6bd5\u9053\u51e1\u9053\uff1a\u201c\u8fd9\u4f4d\u767d\u9a6c\u4e66\u751f\u5012\u662f\u53ef \u4eba\uff0c\u503c\u5f97\u53bb\u89c1\u89c1\u4ed6\uff01\u201d\u5de6\u624b\u643a\u4e86\u4e91\u857e\uff0c\u53f3\u624b\u643a\u4e86\u7fe0\u51e4\uff0c\u7f13\u7f13\u4e0b\u697c\u3002 \u3000\u3000\u4e91\u857e\u5fc3\u6025\u5982\u711a\uff0c\u51fa\u5230\u5916\u9762\uff0c\u9ad8\u547c\u9163\u6597\u4e4b\u58f0\u5df2\u662f\u60ca\u5fc3\u52a8\u9b44\u3002\u628a\u773c\u770b\u65f6\uff0c\u4f46\u89c1\u6f6e\u97f3\u548c\u5c1a\u5df2\u4e0e \u5f20\u4e39\u67ab\u6597\u5728\u4e00\u8d77\u3002 \u3000\u3000\u6f6e\u97f3\u548c\u5c1a\u7684\u5916\u5bb6\u529f\u592b\uff0c\u767b\u5cf0\u9020\u6781\uff0c\u65e9\u5df2\u540d\u6ee1\u6c5f\u6e56\uff0c\u7eff\u6797\u7fa4\u8c6a\uff0c\u73af\u7acb\u5982\u5835\uff0c\u770b\u8fd9\u4e24\u4eba\u5728\u5708 \u4e2d\u6076\u6597\uff0c\u6f6e\u97f3\u548c\u5c1a\u7897\u53e3\u822c\u5927\u7684\u7985\u6756\u4f7f\u5f97\u547c\u547c\u98ce\u54cd\uff0c\u90a3\u4e66\u751f\u8eab\u5f62\u98d8\u5ffd\uff0c\u5251\u52bf\u5982\u8679\uff0c\u5251\u6756\u4ea4\u950b\uff0c \u4e00\u65f6\u95f4\u5206\u4e0d\u51fa\u8c01\u5f3a\u8c01\u5f31\u3002 \u3000\u3000\u4e24\u4eba\u6597\u4e86\u534a\u4e2a\u65f6\u8fb0\uff0c\u6f6e\u97f3\u548c\u5c1a\u4e00\u58f0\u5927\u559d\uff0c\u7985\u6756\u62a1\u5706\uff0c\u547c\u547c\u731b\u626b\uff0c\u6709\u5982\u86df\u9f99\u51fa\u6d1e\uff0c\u5012\u6d77\u7ffb \u6c5f\uff0c\u5f20\u4e39\u67ab\u5251\u52bf\u4e00\u6536\uff0c\u8e0f\u7740\u4e94\u884c\u516b\u5366\u65b9\u4f4d\uff0c\u6b65\u6b65\u540e\u9000\u3002\u6bd5\u9053\u51e1\u5fae\u7b11\u9053\uff1a\u201c\u6f6e\u97f3\u5e08\u5144\u7684\u4f0f\u9b54\u6756 \u6cd5\u5927\u6709\u957f\u8fdb\u3002\u8fd9\u767d\u9a6c\u4e66\u751f\u7684\u5251\u6cd5\uff0c\u6211\u53ef\u662f\u4ece\u672a\u89c1\u8fc7\u3002\u201d\u8bf4\u8bdd\u4e4b\u95f4\u4e8c\u4eba\u53c8\u6597\u4e86\u5341\u4f59\u4e8c\u5341\u62db\uff0c\u6f6e \u97f3\u548c\u5c1a\u6b65\u6b65\u8fdb\u903c\uff0c\u5ffd\u542c\u5f97\u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c\u706b\u661f\u98de\u6e85\uff0c\u6f6e\u97f3\u548c\u5c1a\u7684\u7985\u6756\u5df2\u7ed9\u5251\u524a\u4e86\u4e00\u4e2a\u5207\u53e3\uff0c \u7eff\u6797\u7fa4\u8c6a\uff0c\u60ca\u8d77\u53eb\u9053\uff1a\u201c\u597d\u5b9d\u5251\uff01\u201d \u3000\u3000\u6f6e\u97f3\u548c\u5c1a\u970d\u5730\u4e00\u8df3\uff0c\u968f\u624b\u4e00\u6296\uff0c\u90a3\u6839\u7897\u53e3\u5927\u7684\u7985\u6756\u76f4\u5f39\u8d77\u6765\uff0c\u8fd9\u662f\u4f0f\u9b54\u6756\u6cd5\u7684\u6740\u624b\u795e \u62db\uff0c\u52a0\u4e0a\u6f6e\u97f3\u548c\u5c1a\u51e0\u5341\u5e74\u7684\u529f\u529b\uff0c\u731d\u7136\u4f7f\u51fa\uff0c\u5982\u6233\u5982\u626b\uff0c\u970e\u5ffd\u4e4b\u95f4\u628a\u5f20\u4e39\u67ab\u4e0a\u4e0b\u5de6\u53f3\u51e0\u8def\uff0c \u5168\u90fd\u5c01\u4f4f\uff0c\u4e91\u857e\u89e6\u76ee\u60ca\u5fc3\uff0c\u9a87\u7136\u800c\u547c\uff0c\u5ffd\u542c\u5f97\u6f6e\u97f3\u548c\u5c1a\u4e00\u58f0\u5927\u7b11\uff0c\u5f20\u4e39\u67ab\u7684\u5251\u98de\u4e0a\u534a\u5929\u3002 \u3000\u3000\u7eff\u6797\u7fa4\u8c6a\uff0c\u6b22\u58f0\u96f7\u52a8\uff0c\u5ffd\u89c1\u6f6e\u97f3\u548c\u5c1a\u7985\u6756\u4e00\u6536\uff0c\u6258\u5730\u8df3\u51fa\u5708\u5b50\uff0c\u5f20\u4e39\u67ab\u8eab\u5f62\u63a0\u8d77\uff0c\u7fe9\u5982 \u98de\u9e1f\uff0c\u5c06\u5b9d\u5251\u4e00\u628a\u63a5\u7740\u3002\u6f6e\u97f3\u548c\u5c1a\u53eb\u9053\uff1a\u201c\u4f60\u5e08\u7236\u867d\u5c5e\u53ef\u6068\uff0c\u4f60\u5374\u662f\u6211\u672c\u95e8\u5c0f\u8f88\uff0c\u6211\u5fcc\u80fd\u4ee5 \u5927\u538b\u5c0f\uff0c\u7531\u4f60\u53bb\u5427\uff01\u201d\u7eff\u6797\u7fa4\u96c4\u5927\u4e3a\u60ca\u8be7\uff0c\u7eb7\u7eb7\u8bae\u8bba\u3002\u6bd5\u9053\u51e1\u5fae\u7b11\u9053\uff1a\u201c\u4e8b\u60c5\u8d8a\u6765\u8d8a\u5999\uff0c\u8fd9 \u767d\u9a6c\u4e66\u751f\u600e\u4e48\u53c8\u6210\u4e86\u6f6e\u97f3\u5e08\u5144\u7684\u540c\u95e8\u665a\u8f88\u4e86\uff1f\u7985\u6756\u88ab\u524a\uff0c\u5b9d\u5251\u8131\u624b\uff0c\u4ed6\u4eec\u5e08\u4f2f\u5e08\u4f84\uff0c\u5012\u6253\u4e86 \u4e00\u4e2a\u5e73\u624b\uff0c\u6709\u8da3\uff0c\u6709\u8da3\uff01\u201d \u3000\u3000\u5f20\u4e39\u67ab\u624b\u629a\u5251\u67c4\uff0c\u6f47\u6d12\u81ea\u5982\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u665a\u8f88\u5f20\u4e39\u67ab\u524d\u6765\u8d74\u7ea6\uff0c\u656c\u8bf7\u6bd5\u8001\u82f1\u96c4\u4e00 \u89c1\u3002\u201d\u90dd\u5e84\u4e3b\u4e0e\u4ee3\u53bf\u7684\u72ec\u884c\u5927\u76d7\u909d\u4e2d\u6700\u4e3a\u6027\u5b50\u66b4\u8e81\uff0c\u6bd5\u9053\u51e1\u5c1a\u672a\u51fa\u58f0\uff0c\u4ed6\u4eec\u5df2\u8d8a\u4f17\u800c\u51fa\uff0c\u4e00 \u4e2a\u624b\u4f7f\u957f\u97ad\uff0c\u4e00\u4e2a\u624b\u821e\u94c1\u724c\uff0c\u957f\u97ad\u5377\u5730\uff0c\u94c1\u724c\u538b\u9876\uff0c\u4e24\u822c\u5175\u5668\uff0c\u98ce\u96e8\u88ad\u6765\u3002\u5f20\u4e39\u67ab\u6a2a\u5251\u5f53 \u80f8\uff0c\u8eab\u5b50\u6ef4\u6e9c\u6e9c\u4e00\u8f6c\uff0c\u5e76\u4e0d\u51fa\u62db\u53cd\u51fb\uff0c\u90dd\u3001\u909d\u4e8c\u4eba\u6b63\u5f85\u6362\u62db\uff0c\u4f46\u89c1\u5f20\u4e39\u67ab\u8eab\u5f62\u4e00\u95ea\uff0c\u5df2\u4ece\u5175 \u5668\u5939\u51fb\u7684\u7f1d\u9699\u4e2d\u94bb\u4e86\u51fa\u53bb\u3002\u53ea\u542c\u5f97\u6bd5\u9053\u51e1\u53eb\u9053\uff1a\u201c\u90fd\u4e0d\u8981\u52a8\u624b\uff0c\u5f20\u5144\u8bf7\u968f\u6211\u6765\uff01\u201d\u58f0\u5982\u6d2a \u949f\uff0c\u9707\u6151\u5168\u573a\u3002\u7eff\u6797\u7fa4\u96c4\u5fc3\u4e2d\u90fd\u9053\uff1a\u201c\u5b9a\u662f\u9707\u4e09\u754c\u8981\u4eb2\u81ea\u4e0e\u4ed6\u8f83\u91cf\u4e86\uff01\u201d \u3000\u3000\u4f46\u89c1\u6bd5\u9053\u51e1\u7f13\u6b65\u524d\u5bfc\uff0c\u5c06\u5f20\u4e39\u67ab\u5e26\u5230\u540e\u9762\u82b1\u56ed\uff0c\u5047\u5c71\u6e56\u77f3\u56f4\u7ed5\u4e4b\u4e2d\uff0c\u6709\u4ead\u7ffc\u7136\uff0c\u4ead\u4e2d\u77f3 \u6848\u4e4b\u4e0a\uff0c\u6446\u7740\u4e00\u76d8\u56f4\u68cb\uff0c\u68cb\u5b50\u758f\u758f\u843d\u843d\uff0c\u60f3\u6765\u662f\u8fd8\u672a\u4e0b\u5b8c\u7684\u4e00\u5c40\u6b8b\u68cb\u3002 \u3000\u3000\u6bd5\u9053\u51e1\u53eb\u5bb6\u4eba\u659f\u4e86\u4e24\u58f6\u9152\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u540d\u5c06\u559c\u68cb\uff0c\u9ad8\u4eba\u8d4f\u753b\uff0c\u53e4\u4eca\u540c\u597d\uff0c\u5144\u53f0\u4e5f\u6709\u5174\u81f4 \u4e0b\u4e00\u76d8\u4e48\uff1f\u53ef\u60dc\u8001\u673d\u8fd9\u91cc\uff0c\u65e0\u753b\u53ef\u8d4f\uff01\u201d \u3000\u3000\u5f20\u4e39\u67ab\u5fae\u5fae\u4e00\u7b11\uff0c\u4e00\u63d6\u8bf4\u9053\uff1a\u201c\u665a\u751f\u4e0d\u624d\uff0c\u95fb\u5f26\u6b4c\u800c\u77e5\u96c5\u610f\u3002\u665a\u751f\u968f\u8eab\u643a\u6709\u5377\u753b\uff0c\u867d\u975e \u540d\u5bb6\u624b\u7b14\uff0c\u6216\u8bb8\u4ea6\u53ef\u4e00\u89c2\u3002\u201d\u5c06\u53d6\u81ea\u77f3\u82f1\u5bb6\u4e2d\u7684\u90a3\u5e45\u5de8\u753b\u9ad8\u6302\u4ead\u4e2d\uff0c\u6bd5\u9053\u51e1\u77a5\u4e86\u4e00\u773c\uff0c\u5ffd\u5730 \u957f\u53f9\u4e00\u58f0\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u6c5f\u5c71\u65e0\u6059\u6211\u91cd\u6765\u3002\u5f53\u5e74\u5199\u8fd9\u5e45\u753b\u65f6\uff0c\u60f3\u4ea6\u6709\u4eba\u4e0b\u68cb\u996e\u9152\uff0c\u5f20\u5144\uff0c\u4f60 \u5bb6\u5b66\u6e0a\u6e90\uff0c\u8bf7\u6301\u767d\u5b50\u3002\u201d \u3000\u3000\u4e24\u4eba\u8fd9\u4e00\u756a\u4e3e\u52a8\uff0c\u5927\u51fa\u4f17\u4eba\u610f\u6599\u4e4b\u5916\u3002\u4f20\u7eff\u6797\u7bad\u662f\u4f55\u7b49\u7d27\u5f20\u90d1\u91cd\u4e4b\u4e8b\uff0c\u4ed6\u4eec\u5374\u5728\u8fd9\u91cc\u8d4f \u753b\u4e0b\u68cb\u3002\u6f6e\u97f3\u548c\u5c1a\u4e5f\u8be7\u9053\uff1a\u201c\u8fd9\u5e08\u4f84\u6211\u4ea6\u4ece\u672a\u89c1\u8fc7\uff0c\u9707\u4e09\u754c\u600e\u4e48\u77e5\u9053\u4ed6\u5bb6\u5b66\u6e0a\u6e90\u64c5\u4e8e\u4e0b \u68cb\uff1f\u201d\u4e91\u857e\u5728\u4ed6\u8eab\u8fb9\uff0c\u5ffd\u5730\u56de\u5934\u8bf4\u9053\uff1a\u201c\u4ed6\u81ea\u7136\u77e5\u9053\u3002\u8fd9\u5e45\u753b\u753b\u7684\u53ef\u662f\u82cf\u5dde\u98ce\u666f\u4e48\uff1f\u201d\u6f6e\u97f3 \u8be7\u9053\uff1a\u201c\u4f60\u672a\u5230\u8fc7\u82cf\u5dde\uff0c\u4f60\u53c8\u5982\u4f55\u77e5\u9053\u5462\uff1f\u201d\u77f3\u7fe0\u51e4\u5728\u65c1\u4e5f\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4ed6\u81ea\u7136\u77e5\u9053\u3002\u201d \u3000\u3000\u4ead\u4e2d\u4e24\u4eba\u4e00\u9762\u996e\u9152\uff0c\u4e00\u9762\u4e0b\u68cb\uff0c\u7fa4\u8c6a\u8fdc\u8fdc\u89c2\u770b\u7eb3\u95f7\u5f02\u5e38\u3002\u6bd5\u9053\u51e1\u6301\u9ed1\u5b50\u5148\u4e0b\uff0c\u8d77\u624b\u5e03\u51fa \u201c\u71d5\u53cc\u98de\u201d\u7684\u5c40\u52bf\uff0c\u5f20\u4e39\u67ab\u7b2c\u4e00\u6b65\u68cb\uff0c\u5374\u4e22\u5728\u68cb\u76d8\u5f53\u4e2d\uff0c\u76f4\u5360\u201c\u5929\u5143\u201d\u4e4b\u4f4d\u3002\u56f4\u68cb\u672f\u8bed\u6709 \u4e91\uff1a\u201c\u91d1\u8fb9\u94f6\u89d2\u77f3\u809a\u5b50\u201d\uff0c\u610f\u601d\u662f\u4fdd\u6301\u8fb9\u89d2\u4e43\u662f\u4e0a\u4e58\uff0c\u62a2\u5f53\u4e2d\u8179\u5730\u5374\u662f\u6613\u53d7\u5165\u4fb5\uff0c\u4e2d\u770b\u4e0d\u4e2d \u5403\u7684\u3002\u6bd5\u9053\u51e1\u8d77\u624b\u6240\u5e03\u7684\u201c\u71d5\u53cc\u98de\u201d\u4e4b\u5c40\uff0c\u4fbf\u662f\u4fdd\u6301\u8fb9\u89d2\u7684\u6218\u7565\u3002\u4e0d\u6599\u5f20\u4e39\u67ab\u610f\u4e0d\u4e0e\u4ed6\u62a2\u593a \u8fb9\u89d2\uff0c\u5f84\u5360\u5f53\u4e2d\u3002\u6bd5\u9053\u51e1\u8d5e\u9053\uff1a\u201c\u5144\u53f0\u8c6a\u6c14\uff0c\u679c\u662f\u51cc\u9a7e\u524d\u4eba\uff0c\u7adf\u4e0d\u5c51\u4e0e\u6211\u4e89\u4e00\u9685\u4e4b\u5730\u4e48\uff1f\u201d \u51dd\u601d\u826f\u4e45\uff0c\u59cb\u4e0b\u4e00\u7c92\uff0c\u5f20\u4e39\u67ab\u5374\u662f\u4fe1\u624b\u4fbf\u4e0b\uff0c\u6beb\u4e0d\u601d\u7d22\uff0c\u4e0b\u4e86\u534a\u4e2a\u65f6\u8fb0\uff0c\u68cb\u76d8\u4e2d\u68cb\u5b50\u8fd8\u662f\u758f \u758f\u843d\u843d\uff0c\u6bd5\u9053\u51e1\u6c57\u6d94\u6d94\u4e0b\uff0c\u5ffd\u7136\u7ad9\u8d77\u8eab\u6765\uff0c\u5c06\u76d8\u4e2d\u68cb\u5b50\u4e00\u626b\uff0c\u60e8\u7136\u9053\uff1a\u201c\u8fd9\u5c40\u68cb\u6211\u4e0d\u80fd\u518d\u4e0e \u4f60\u4e89\u4e86\uff01\u201d \u3000\u3000\u5f20\u4e39\u67ab\u4e00\u7b11\u8d77\u7acb\uff0c\u9053\u58f0\uff1a\u201c\u627f\u8ba9\uff01\u201d\u5c06\u753b\u5377\u4e0b\u3002\u7eff\u6797\u7fa4\u8c6a\u8038\u52a8\uff0c\u6bd5\u9053\u51e1\u77a5\u4e86\u4e00\u773c\uff0c\u5ffd \u9053\uff1a\u201c\u5f20\u5144\uff0c\u975e\u662f\u8001\u673d\u4e0d\u77e5\u8fdb\u9000\uff0c\u4f60\u65e2\u7ea6\u4e86\u8fd9\u4e48\u591a\u597d\u670b\u53cb\u6765\uff0c\u8001\u673d\u4e5f\u4e0d\u80fd\u4e0d\u968f\u4fd7\u4f8b\uff0c\u8981\u8bf7\u6559 \u5144\u53f0\u51e0\u8def\u5251\u6cd5\u3002\u201d\u5f20\u4e39\u67ab\u76ee\u5149\u95ea\u95ea\uff0c\u6bd5\u9053\u51e1\u6b64\u8bed\u9887\u4f3c\u51fa\u4ed6\u7684\u610f\u6599\uff0c\u4f46\u4ecd\u662f\u795e\u8272\u81ea\u82e5\uff0c\u4e00\u63d6\u8bf4 \u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u8bf7\u6bd5\u8001\u524d\u8f88\u624b\u4e0b\u7559\u60c5\u3002\u201d \u3000\u3000\u6bd5\u9053\u51e1\u4ece\u5899\u89d2\u53d6\u4e86\u4e00\u6839\u6728\u68d2\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u53eb\u5316\u68d2\u8fd8\u7528\u5f97\u7740\u554a\uff01\u201d\u6bd5\u9053\u51e1\u7684\u68d2\u4e43\u964d\u9f99\u6728\u6240 \u9020\uff0c\u575a\u903e\u91d1\u94c1\u3002\u5f20\u4e39\u67ab\u5728\u4e0b\u9996\u7acb\u4e86\u4e2a\u95e8\u6237\uff0c\u6bd5\u9053\u51e1\u77e5\u4ed6\u4e0d\u80af\u5148\u624b\u51fa\u62db\uff0c\u68cd\u5c16\u4e00\u6307\uff0c\u9053\u58f0\uff1a \u201c\u7559\u795e\u63a5\u62db\u201d\uff0c\u624b\u8d77\u4e00\u68d2\u62e6\u8170\u626b\u53bb\uff0c\u5f20\u4e39\u67ab\u9053\u4e2a\u201c\u597d\u201d\u5b57\uff0c\u970d\u5730\u6643\u8eab\u4e00\u8df3\uff0c\u964d\u9f99\u68d2\u5728\u4ed6\u811a\u4e0b \u4e00\u63a0\u800c\u8fc7\uff0c\u4ed6\u8eab\u5f62\u672a\u843d\uff0c\u5251\u5149\u5df2\u8d77\uff0c\u4e00\u62db\u201c\u767d\u8679\u8d2f\u65e5\u201d\uff0c\u4fbf\u5411\u6bd5\u9053\u51e1\u201c\u534e\u76d6\u7a74\u201d\u523a\u5230\uff0c\u6bd5\u9053 \u51e1\u4e5f\u53eb\u58f0\u201c\u597d\uff01\u201d\u964d\u9f99\u68d2\u5f80\u4e0b\u4e00\u6c89\uff0c\u4e00\u62db\u201c\u5e73\u6c99\u843d\u96c1\u201d\uff0c\u659c\u62cd\u8109\u95e8\uff0c\u6b63\u51fb\u53cc\u80eb\uff0c\u4e00\u62db\u4e09\u5f0f\uff0c \u7528\u5f97\u5341\u5206\u8001\u8fa3\uff0c\u5f20\u4e39\u67ab\u731b\u7f29\u8eab\u5f62\uff0c\u8eab\u968f\u5251\u8d70\uff0c\u7a81\u51fa\u4e00\u62db\u201c\u65e5\u6708\u7ecf\u5929\u201d\uff0c\u5251\u5149\u5982\u8679\uff0c\u6a2a\u63a0\u800c \u8fc7\uff0c\u5c06\u6bd5\u9053\u51e1\u7684\u653b\u52bf\u5168\u90e8\u7834\u89e3\u3002\u6bd5\u9053\u51e1\u8d5e\u9053\uff1a\u201c\u5f20\u5144\u5251\u6cd5\u679c\u7136\u7edd\u5929\u4e0b\uff01\u201d\u84e6\u5730\u5c06\u964d\u9f99\u6728\u68d2\u4e00 \u4e2a\u987a\u52bf\u53cd\u62bd\uff0c\u75be\u5982\u9a87\u7535\uff0c\u770b\u4f3c\u5f20\u4e39\u67ab\u907f\u65e0\u53ef\u907f\uff0c\u4ed6\u5374\u5ffd\u5730\u53cd\u8eab\u4e00\u5251\uff0c\u8eab\u6cd5\u4e4b\u5feb\u4e0e\u5251\u62db\u4e4b\u5999\uff0c \u90fd\u914d\u5408\u5f97\u6070\u5230\u597d\u5904\uff0c\u6070\u6070\u4ece\u6728\u68d2\u659c\u8fb9\u957f\u8eab\u800c\u51fa\uff0c\u5b9d\u5251\u4e00\u62ac\uff0c\u78b0\u4e2a\u6b63\u7740\uff0c\u706b\u82b1\u98de\u6e85\uff0c\u94ff\u9535\u6709 \u58f0\u3002\u6bd5\u9053\u51e1\u4f3c\u5413\u4e86\u4e00\u8df3\uff0c\u62bd\u68d2\u770b\u65f6\uff0c\u5f20\u4e39\u67ab\u5df2\u5237\u7684\u4e00\u5251\u4ece\u4ed6\u9888\u4fa7\u7a7f\u8fc7\uff0c\u6bd5\u9053\u51e1\u504f\u8eab\u7acb\u68d2\uff0c\u547c \u7684\u53c8\u65cb\u8fc7\u6765\uff0c\u7eff\u6797\u7fa4\u8c6a\u5fc3\u4e2d\u90fd\u53eb\u597d\u9669\u3002\u6f6e\u97f3\u548c\u5c1a\u5374\u5728\u8be7\u5f02\uff0c\u8fd9\u4e00\u5251\u5251\u5c16\u53ea\u8981\u7565\u7565\u4e00\u504f\uff0c\u5c31\u53ef \u523a\u4e2d\uff0c\u96be\u9053\u662f\u5f20\u4e39\u67ab\u7684\u52b2\u529b\u8fd8\u4e0d\u80fd\u63a7\u5236\u81ea\u5982\uff1f \u3000\u3000\u6bd5\u9053\u51e1\u5374\u77e5\u9053\u4ed6\u6709\u610f\u8ba9\u4e86\u4e00\u62db\uff0c\u4e00\u770b\u964d\u9f99\u68d2\uff0c\u5e76\u65e0\u7f3a\u53e3\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u7684\u5b9d\u5251\u4e0e\u6211\u7684 \u53eb\u5316\u68d2\u4e24\u65e0\u4f24\u635f\uff0c\u4e0d\u5fc5\u987e\u5fcc\u3002\u201d\u6728\u68d2\u4e00\u5c55\uff0c\u76d8\u3001\u6253\u3001\u6311\u3001\u6251\u3001\u5708\u3001\u6296\u3001\u6572\u3001\u649e\uff0c\u65bd\u5c55\u68cd\u68d2\u795e \u6253\u516b\u6cd5\uff0c\u821e\u5f04\u5f97\u51fa\u795e\u5165\u5316\uff0c\u5f20\u4e39\u67ab\u6253\u70b9\u7cbe\u795e\uff0c\u7ec6\u5fc3\u5e94\u4ed8\uff0c\u53ea\u89c9\u4ed6\u7684\u68cd\u68d2\u5e26\u7740\u4e00\u79cd\u65e0\u5f62\u7684\u52b2 \u529b\uff0c\u6709\u5982\u5929\u98ce\u6d77\u96e8\uff0c\u8feb\u4eba\u800c\u6765\u3002\u539f\u6765\u82e5\u8bba\u8eab\u6cd5\u8f7b\u7075\uff0c\u4e43\u662f\u5f20\u4e39\u67ab\u7a0d\u80dc\uff0c\u82e5\u8bba\u5185\u529b\u7684\u6c89\u52b2\uff0c\u5374 \u662f\u6bd5\u9053\u51e1\u9ad8\u5f3a\u3002\u6597\u4e86\u4e09\u4e94\u5341\u62db\uff0c\u5f20\u4e39\u67ab\u4f7f\u4e86\u4e00\u62db\u201c\u9f99\u95e8\u9f13\u6d6a\u201d\uff0c\u5251\u52bf\u6392\u7a7a\u800c\u81f3\uff0c\u5f3a\u52b2\u4e4b\u6781\uff0c \u773c\u770b\u5251\u950b\u5df2\u662f\u89e6\u53ca\u964d\u9f99\u5b9d\u68d2\uff0c\u5ffd\u5730\u88ab\u6bd5\u9053\u51e1\u6a2a\u68d2\u4e00\u5e26\uff0c\u8eab\u4e0d\u7531\u5df1\uff0c\u8eac\u8170\u6b32\u5012\uff0c\u6251\u5411\u659c\u65b9\u3002\u53ea \u542c\u5f97\u547c\u7684\u4e00\u58f0\uff0c\u6bd5\u9053\u51e1\u4e00\u68d2\u4ece\u4ed6\u810a\u9aa8\u4e0a\u626b\u8fc7\uff0c\u5f20\u4e39\u67ab\u53cd\u8eab\u4e00\u8dc3\uff0c\u8df3\u8fc7\u4e00\u8fb9\u3002\u7eff\u6797\u7fa4\u8c6a\u5fc3\u4e2d\u90fd \u9053\uff1a\u201c\u53ef\u60dc\u53ef\u60dc\uff01\u201d\u6f6e\u97f3\u548c\u5c1a\u5374\u5728\u8be7\u5f02\uff0c\u8fd9\u4e00\u68d2\u53ea\u8981\u7565\u7565\u4e00\u6c89\uff0c\u4fbf\u53ef\u5c06\u5f20\u4e39\u67ab\u810a\u9aa8\u6572\u788e\uff0c\u96be \u9053\u6bd5\u9053\u51e1\u90a3\u6837\u7684\u529f\u529b\uff0c\u52b2\u529b\u5c1a\u8fd8\u4e0d\u80fd\u63a7\u5236\u81ea\u5982\uff1f \u3000\u3000\u5f20\u4e39\u67ab\u5374\u660e\u767d\u662f\u6bd5\u9053\u51e1\u8fd8\u8ba9\u4e86\u4e00\u62db\uff0c\u6301\u5251\u8e0c\u8e87\uff0c\u6b63\u6b32\u8bbe\u6cd5\u63a2\u95ee\u6bd5\u9053\u51e1\u771f\u610f\u6240\u5728\u3002\u5ffd\u542c\u5f97 \u6bd5\u9053\u51e1\u54c8\u54c8\u5927\u7b11\uff0c\u6301\u68d2\u903c\u6765\u3002\u6b63\u662f\uff1a \u3000\u3000\u5251\u5149\u6620\u51fa\u5f53\u5e74\u6068\uff0c\u72b9\u672a\u6572\u6b8b\u4e00\u5c40\u68cb\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u840d\u8e2a\u4fa0\u5f71\u5f55\u300b\u2014\u2014\u7b2c\u5341\u56de\u3000\u4e00\u5c40\u68cb\u6b8b\u82f1\u96c4\u60ca\u9738\u6c14\u3000\u6df1\u5bb5\u68a6\u65ad\u7389\u5973\u52a8\u82b3\u5fc3 \u6881\u7fbd\u751f\u300a\u840d\u8e2a\u4fa0\u5f71\u5f55\u300b \u7b2c\u5341\u56de\u3000\u4e00\u5c40\u68cb\u6b8b\u82f1\u96c4\u60ca\u9738\u6c14\u3000\u6df1\u5bb5\u68a6\u65ad\u7389\u5973\u52a8\u82b3\u5fc3 \u3000\u3000\u5f20\u4e39\u67ab\u6a2a\u5251\u5f53\u80f8\uff0c\u53ea\u542c\u5f97\u6bd5\u9053\u51e1\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5144\u53f0\u5251\u6cd5\u5999\u7edd\uff0c\u8001\u673d\u53ef\u4ee5\u653e\u5fc3\u4e86\uff01\u201d\u7a81\u7136 \u4f38\u68d2\u4e00\u642d\u5251\u8eab\uff0c\u5f20\u4e39\u67ab\u53ea\u89c9\u4e00\u80a1\u9ecf\u529b\uff0c\u5f80\u5916\u626f\u53bb\uff0c\u5b9d\u5251\u53ea\u597d\u987a\u52bf\u4e00\u5c55\uff0c\u5251\u68d2\u76f8\u4ea4\uff0c\u5e76\u7ad6\u7a7a \u4e2d\uff0c\u5f62\u4f3c\u4e00\u4e2a\u201c\u4eba\u201d\u5b57\uff0c\u8fd9\u662f\u6b66\u6797\u4e2d\u5316\u654c\u4e3a\u53cb\u7684\u8868\u793a\uff0c\u7fa4\u8c6a\u76f8\u987e\u8be7\u7136\u3002\u6bd5\u9053\u51e1\u773c\u5149\u4e00\u626b\uff0c\u6717 \u58f0\u8bf4\u9053\uff1a\u201c\u5f20\u5144\u662f\u6211\u4e16\u4ea4\uff0c\u5929\u5927\u7684\u4e8b\u60c5\uff0c\u8bf7\u51b2\u7740\u5c0f\u8001\u513f\u7684\u8584\u9762\uff0c\u63ed\u8fc7\u53bb\u5427\uff01\u201d\u54c8\u54c8\u5927\u7b11\u63b7\u68d2 \u4e8e\u5730\uff0c\u643a\u7740\u5f20\u4e39\u67ab\u7684\u624b\uff0c\u4eb2\u81ea\u9001\u51fa\u95e8\u5916\u3002 \u3000\u3000\u5468\u5c71\u6c11\u53cc\u773c\u5706\u7741\uff0c\u7eff\u6797\u7fa4\u96c4\u4e5f\u90fd\u8038\u7136\u52a8\u5bb9\uff0c\u4f46\u89c1\u6bd5\u9053\u51e1\u795e\u8272\u51db\u7136\uff0c\u4e0e\u5f20\u4e39\u67ab\u643a\u624b\u5e76\u80a9\uff0c \u5bf9\u65c1\u4eba\u795e\u8272\uff0c\u6beb\u4e0d\u7406\u4f1a\uff0c\u8fd9\u662f\u6c5f\u6e56\u4e0a\u6700\u9686\u91cd\u7684\u62a4\u9001\u65b9\u5f0f\uff0c\u65c1\u4eba\u867d\u6709\u4e0d\u6ee1\uff0c\u788d\u7740\u6bd5\u9053\u51e1\u7684\u9762 \u5b50\uff0c\u6b64\u9645\u4e5f\u4e0d\u6562\u516c\u7136\u53d1\u8bdd\u3002 \u3000\u3000\u95e8\u5916\u767d\u9a6c\u6b22\u8dc3\u5636\u9e23\uff0c\u5f20\u4e39\u67ab\u624b\u629a\u5251\u67c4\uff0c\u4fef\u8170\u4e00\u8eac\uff0c\u9053\u58f0\uff1a\u201c\u591a\u8c22\u8001\u4f2f\u3002\u201d\u98de\u8eab\u4e0a\u9a6c\uff0c\u6717 \u58f0\u541f\u9053\uff1a\u201c\u4e2d\u5dde\u98ce\u96e8\u6211\u5f52\u6765\uff0c\u4f46\u613f\u6c5f\u5c71\u51fa\u9738\u624d\uff0c\u5018\u5f97\u6d9b\u5e73\u6ce2\u9759\u65e5\uff0c\u4e0e\u541b\u540c\u4e0a\u96c6\u8d24\u53f0\u3002\u201d\u773c\u5149 \u4e00\u4e0e\u4e91\u857e\u76f8\u63a5\uff0c\u7acb\u523b\u7eb5\u9a6c\u5954\u9a70\uff0c\u8bd7\u58f0\u6447\u66f3\u4e4b\u4e2d\uff0c\u767d\u9a6c\u5df2\u95ea\u7535\u822c\u5954\u51fa\u6570\u91cc\u4e4b\u5916\u3002 \u3000\u3000\u6bd5\u9053\u51e1\u53cc\u76ee\u95ea\u5149\uff0c\u5446\u7136\u8fdc\u671b\uff0c\u5ffd\u800c\u7fd8\u8d77\u62c7\u6307\uff0c\u5927\u58f0\u8d5e\u9053\uff1a\u201c\u597d\u6c14\u6982\uff0c\u679c\u7136\u80dc\u4f3c\u524d\u4eba\uff0c\u4e0d \u6789\u77f3\u82f1\u66ff\u4ed6\u5b88\u4e86\u51e0\u5341\u5e74\u3002\u201d\u84dd\u5be8\u4e3b\u84dd\u5929\u77f3\u8d8a\u4f17\u800c\u51fa\uff0c\u95ee\u9053\uff1a\u201c\u8fd9\u767d\u9a6c\u5c11\u5e74\u7aef\u7684\u662f\u4f55\u6765\u5386\uff1f\u8f70 \u5929\u96f7\u4e0e\u91d1\u5200\u5be8\u4e3b\u8054\u540d\u53d1\u51fa\u7684\u7eff\u6797\u7bad\uff0c\u96be\u9053\u662f\u65e0\u7684\u653e\u77e2\u4e48\uff1f\u201d \u3000\u3000\u6bd5\u9053\u51e1\u79fb\u773c\u671b\u7740\u7fe0\u51e4\uff0c\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u77f3\u59d1\u5a18\uff0c\u4f60\u73b0\u4eca\u8be5\u660e\u767d\u4e86\u5427\uff1f\u6211\u7684\u5e08\u7956\u5f6d\u548c\u5c1a\u4f20\u4e0b \u4e09\u4e2a\u5f92\u5f1f\uff0c\u4e8c\u5f1f\u5b50\u6731\u5143\u748b\u8d35\u4e3a\u5927\u660e\u7684\u5f00\u56fd\u7687\u5e1d\uff0c\u5927\u5f1f\u5b50\u5f20\u58eb\u8bda\u6218\u6b7b\u957f\u6c5f\uff0c\u8fd9\u767d\u9a6c\u5c11\u5e74\u4fbf\u662f\u4ed6 \u7684\u540e\u4ee3\u5b50\u5b59\uff0c\u4e09\u5144\u5f1f\u4e2d\u6700\u4e0d\u6d4e\u7684\u662f\u6211\u8fd9\u652f\uff0c\u4e16\u4e16\u4ee3\u4ee3\u8fd8\u662f\u5f53\u5e74\u672c\u8272\u3002\u201d \u3000\u3000\u7fa4\u8c6a\u672a\u542c\u8fc7\u6bd5\u9053\u51e1\u7684\u6545\u4e8b\uff0c\u7eb7\u7eb7\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u4ec0\u4e48\uff1f\u201d\u201c\u90a3\u767d\u9a6c\u5c11\u5e74\u7adf\u662f\u5f20\u58eb\u8bda\u7684\u540e \u4eba\uff1f\u201d\u201c\u8f70\u5929\u96f7\u77f3\u82f1\u548c\u4ed6\u53c8\u662f\u4ec0\u4e48\u5173\u7cfb\uff1f\u201d\u77f3\u7fe0\u51e4\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u55ef\uff0c\u6211\u660e\u767d\u4e86\uff0c\u6211\u5bb6\u7956\u5148 \u6562\u60c5\u5c31\u662f\u5f20\u58eb\u8bda\u5f53\u5e74\u6258\u4ed6\u4fdd\u5b88\u90a3\u5e45\u5de8\u753b\u7684\u4eb2\u4fe1\u3002\u53ef\u662f\u4ed6\u3001\u4ed6\u662f\u6211\u4e91\u76f8\u516c\u7684\u5927\u4ec7\u4eba\u5440\uff01\u201d \u3000\u3000\u6bd5\u9053\u51e1\u76b1\u7709\u8bf4\u9053\uff1a\u201c\u6240\u4ee5\u6211\u8bf4\u5c1a\u6709\u6570\u4e8b\u672a\u660e\uff0c\u6b64\u4e8b\u5c31\u662f\u5176\u4e2d\u4e4b\u4e00\u3002\u4f60\u7239\u7239\u7684\u4fe1\u4e2d\u4e5f\u672a\u6709 \u63d0\u53ca\u3002\u4e91\u76f8\u516c\uff0c\u4ed6\u662f\u600e\u4e48\u548c\u4f60\u7ed3\u4ec7\u7684\uff1f\u201d \u3000\u3000\u4e91\u857e\u9762\u8272\u60e8\u767d\uff0c\u76ee\u4e2d\u8574\u6cea\uff0c\u4e45\u4e45\u8bf4\u4e0d\u51fa\u8bdd\uff0c\u7eff\u6797\u7fa4\u8c6a\u7591\u95ee\u60ca\u8be7\u4e4b\u58f0\u4e0d\u7edd\u4e8e\u8033\u3002\u6bd5\u9053\u51e1 \u9053\uff1a\u201c\u90fd\u5230\u91cc\u9762\u8bf4\u5427\u3002\u201d\u56de\u5230\u5ba2\u5385\u5750\u5b9a\uff0c\u6bd5\u9053\u51e1\u5c06\u4ee5\u524d\u8bf4\u8fc7\u7684\u6545\u4e8b\uff0c\u7ea6\u7565\u518d\u8bf4\u4e00\u904d\uff0c\u53f9\u53e3\u6c14 \u9053\uff1a\u201c\u5f53\u5e74\u4e09\u5144\u5f1f\u5e76\u4e3e\u4e49\u65d7\uff0c\u540e\u6765\u662f\u4e00\u4eba\u72ec\u5360\u5929\u4e0b\uff0c\u8001\u5b9e\u8bf4\uff0c\u6211\u5fc3\u4e2d\u4ea6\u662f\u4e0d\u670d\u3002\u6211\u5bb6\u6570\u4ee3\u4f20 \u4e0b\u7684\u5bb6\u89c4\uff0c\u6bcf\u4e2a\u7537\u4e01\uff0c\u90fd\u8981\u505a\u5341\u5e74\u548c\u5c1a\uff0c\u5341\u5e74\u4e5e\u4e10\uff0c\u8fd9\u4e00\u6765\u56fa\u662f\u7eaa\u5ff5\u524d\u4eba\uff0c\u4e8c\u6765\u4e5f\u662f\u501f\u6b64\u4e91 \u6e38\u5929\u4e0b\u8bbf\u5bfb\u90a3\u5e45\u4e0e\u56fd\u8fd0\u6781\u6709\u5173\u7cfb\u7684\u753b\u5377\uff0c\u597d\u518d\u4e0e\u6731\u5143\u748b\u7684\u5b50\u5b59\u4e00\u8f83\u96cc\u96c4\u3002\u53ef\u662f\u5982\u4eca\u4e0d\u5fc5\u6211\u518d \u8d39\u5fc3\u4e86\uff0c\u6211\u7684\u513f\u5b50\u4e5f\u4e0d\u5fc5\u518d\u505a\u548c\u5c1a\uff0c\u518d\u505a\u53eb\u5316\u5566\uff01\u201d \u3000\u3000\u84dd\u5be8\u4e3b\u95ee\u9053\uff1a\u201c\u6bd5\u8001\u82f1\u96c4\u6b64\u8bdd\u662f\u4f55\u610f\u601d\uff1f\u201d\u6bd5\u9053\u51e1\u60e8\u7b11\u8a00\u9053\uff1a\u201c\u4ee5\u524d\u866c\u9aef\u5ba2\u6709\u5fd7\u4e8e\u5929 \u4e0b\uff0c\u4e0e\u674e\u4e16\u6c11\u4e0b\u4e00\u5c40\u68cb\uff0c\u68cb\u672a\u4e0b\u5b8c\u5c31\u62b9\u4e71\u68cb\u5b50\uff0c\u8bf4\u8fd9\u5929\u4e0b\u4e0d\u80fd\u518d\u4e89\u4e86\u3002\u6211\u867d\u65e0\u866c\u9aef\u5ba2\u7684\u9738 \u6c14\uff0c\u53ef\u662f\u4ee5\u524d\u4e5f\u8fd8\u4e0d\u81ea\u91cf\uff0c\u8fd8\u60f3\u5728\u5bfb\u5f97\u753b\u5377\u4e4b\u540e\uff0c\u518d\u9010\u9e7f\u4e2d\u539f\u3002\u53ef\u662f\u5982\u4eca\u4e5f\u5fc3\u7518\u60c5\u613f\u8f93\u7ed9\u5f20 \u4e39\u67ab\u5566\uff0c\u8fd9\u5e45\u753b\u627e\u5230\u5b83\u7684\u771f\u4e3b\u4eba\u4e86\u3002\u4f60\u4eec\u90fd\u542c\u89c1\u5f20\u4e39\u67ab\u4e34\u53bb\u7684\u541f\u8bd7\uff0c\u90a3\u662f\u4f55\u7b49\u6c14\u9b44\uff0c\u4e0d\u95ee\u53ef \u77e5\uff0c\u4ed6\u5b9a\u662f\u6309\u56fe\u7d22\u9aa5\uff0c\u8981\u53d1\u6398\u4ed6\u7956\u5148\u5f53\u5e74\u7684\u5b9d\u85cf\uff0c\u4e0e\u90a3\u5e45\u65e0\u4ef7\u4e4b\u5b9d\u7684\u5730\u56fe\uff0c\u518d\u4e3e\u4e49\u65d7\uff0c\u91cd\u56fe \u5e1d\u4e1a\uff0c\u53c8\u4e00\u6b21\u4e0e\u6731\u5bb6\u4e89\u593a\u6c5f\u5c71\u4e86\uff01\u201d \u3000\u3000\u5468\u5c71\u6c11\u4e0d\u80fd\u518d\u5fcd\uff0c\u4e00\u8dc3\u800c\u8d77\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u53ea\u6050\u4ed6\u8981\u628a\u6c5f\u5c71\u5949\u9001\u5916\u4eba\uff01\u201d\u6bd5\u9053\u51e1\u77a0\u76ee \u9053\uff1a\u201c\u4f60\u8bf4\u4ec0\u4e48\uff1f\u201d\u5468\u5c71\u6c11\u8a00\u9053\uff1a\u201c\u6bd5\u8001\u524d\u8f88\u4f60\u8fd8\u4e0d\u77e5\u9053\u4e48\uff1f\u8fd9\u767d\u9a6c\u5c11\u5e74\u7684\u7236\u4eb2\u5f20\u5b97\u5468\u5728\u74e6 \u523a\u5b98\u62dc\u53f3\u4e1e\u76f8\uff0c\u74e6\u523a\u5165\u4fb5\u5df2\u8feb\u5728\u7709\u776b\uff0c\u4ed6\u5355\u9a91\u5165\u5173\uff0c\u4e0d\u662f\u5978\u7ec6\uff0c\u8fd8\u80fd\u662f\u4ec0\u4e48\uff1f\u53ea\u6050\u6bd4\u5978\u7ec6\u66f4 \u4e3a\u5371\u9669\u3002\u8bd5\u60f3\u4ed6\u82e5\u53d6\u5f97\u90a3\u5e45\u519b\u7528\u5730\u56fe\u56fd\u4e2d\u9669\u8981\u4e4b\u5730\uff0c\u4e86\u5982\u6307\u638c\uff0c\u732e\u51fa\u74e6\u523a\uff0c\u6309\u56fe\u8fdb\u5175\uff0c\u4e2d\u56fd \u600e\u80fd\u62b5\u654c\uff1f\u201d\u6bd5\u9053\u51e1\u795e\u8272\u5927\u53d8\u9053\uff1a\u201c\u4f60\u8bdd\u53ef\u662f\u771f\uff1f\u201d\u5468\u5c71\u6c11\u9053\uff1a\u201c\u534a\u70b9\u4e0d\u5047\uff01\u6211\u7236\u5b50\u4e3e\u8d77\u65e5 \u6708\u53cc\u65d7\uff0c\u62d2\u6c49\u6297\u80e1\uff0c\u5929\u4e0b\u5171\u77e5\u3002\u8fd9\u7b49\u5927\u4e8b\uff0c\u5c82\u5bb9\u8bf4\u8c0e\uff01\u5c31\u662f\u8fd9\u4f4d\u4e91\u76f8\u516c\u7684\u8840\u6d77\u6df1\u4ec7\uff0c\u4e5f\u56e0\u5f20 \u5b97\u5468\u8fd9\u4e2a\u5927\u5978\u8d3c\u800c\u8d77\uff01\u857e\u5f1f\uff0c\u4f60\u8bf4\u4e0e\u8bf8\u4f4d\u82f1\u96c4\u542c\u542c\u3002\u201d\u4e91\u857e\u6cea\u54bd\u5fc3\u9178\uff0c\u88ab\u5468\u5c71\u6c11\u4e00\u903c\uff0c \u201c\u54c7\u201d\u7684\u4e00\u58f0\u54ed\u4e86\u51fa\u6765\uff0c\u8bdd\u8bed\u8bf4\u4e0d\u51fa\u53e3\u3002\u5468\u5c71\u6c11\u6025\u9053\uff1a\u201c\u857e\u5f1f\u4f60\u522b\u4f24\u5fc3\u3002\u6bd5\u8001\u524d\u8f88\u4e0e\u5217\u4f4d\u82f1 \u96c4\u5b9a\u80fd\u66ff\u4f60\u4f5c\u4e3b\u7684\uff0c\u6211\u4ee3\u4f60\u8bf4\u4e86\u5427\u3002\u201d\u5c06\u4e91\u9756\u7267\u9a6c\u80e1\u8fb9\u5f52\u9014\u9047\u5bb3\u7b49\u60c5\u4e8b\u8bf4\u4e86\uff0c\u6bd5\u9053\u51e1\u9893\u7136\u5012 \u5728\u6905\u4e0a\uff0c\u534a\u664c\u8bf4\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u6211\u5bb6\u6570\u4ee3\u8bbf\u5bfb\u5f20\u58eb\u8bda\u540e\u4ee3\uff0c\u90fd\u662f\u4e00\u70b9\u7ebf\u7d22\u90fd\u627e\u4e0d\u5230\uff0c\u539f\u6765\u662f\u8fdc \u8d74\u6f20\u5916\u53bb\u4e86\u3002\u201d\u84e6\u5730\u8d77\u7acb\uff0c\u957f\u987b\u98a4\u6296\uff0c\u6124\u7136\u8bf4\u9053\uff1a\u201c\u5f20\u58eb\u8bda\u7adf\u7136\u6709\u8fd9\u7b49\u4e0d\u8096\u7684\u5b50\u5b59\uff1f\u770b\u5f20\u4e39 \u67ab\u7684\u6c14\u6982\u8c6a\u60c5\uff0c\u4ed6\u3001\u4ed6\u600e\u80fd\u662f\u4e2a\u5978\u8d3c\uff1f\u201d\u5468\u5c71\u6c11\u8bf4\u9053\uff1a\u201c\u6709\u5176\u7236\u5fc5\u6709\u5176\u5b50\uff0c\u53ea\u51ed\u5916\u8868\u600e\u80fd\u65ad \u5b9a\u4ed6\u7684\u4e3a\u4eba\uff1f\u201d\u6bd5\u9053\u51e1\u7ea2\u9762\u53d8\u7d2b\uff0c\u53cc\u775b\u70af\u70af\uff0c\u597d\u50cf\u8981\u55b7\u51fa\u706b\u6765\uff0c\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u90a3 \u662f\u6211\u7684\u9519\u4e86\u4e0d\u662f\uff1f\u201d\u5468\u5c71\u6c11\u4e00\u5664\uff0c\u6f6e\u97f3\u548c\u5c1a\u63a5\u53e3\u9053\uff1a\u201c\u8001\u5927\u54e5\uff0c\u6211\u8bf4\u662f\u4f60\u9519\u4e86\uff0c\u90a3\u5f20\u5b97\u5468\u786e \u5b9e\u662f\u4e2a\u5927\u5978\u8d3c\uff0c\u6211\u4e5f\u66fe\u6df1\u5165\u74e6\u523a\uff0c\u8eab\u53d7\u5176\u5bb3\uff01\u201d\u6bd5\u9053\u51e1\u88ab\u4ed6\u76f4\u8bf4\uff0c\u987f\u65f6\u50cf\u4e00\u4e2a\u6cc4\u4e86\u6c14\u7684\u76ae \u7403\uff0c\u5782\u4e0b\u5934\u6765\uff0c\u5583\u5583\u8bf4\u9053\uff1a\u201c\u662f\u6211\u9519\u4e86\uff1f\u771f\u7684\u6211\u9519\u4e86\uff1f\u201d \u3000\u3000\u5468\u5c71\u6c11\u89c1\u4ed6\u6c14\u7130\u7a0d\u51cf\uff0c\u53c8\u9f13\u52c7\u6c14\u8bf4\u9053\uff1a\u201c\u6bd5\u8001\u524d\u8f88\uff0c\u8fd9\u6b21\u53ea\u6050\u662f\u4f60\u4e00\u65f6\u4e0d\u5bdf\uff0c\u88ab\u90a3\u5978\u8d3c \u6240\u5229\u7528\u4e86\uff0c\u60f3\u90a3\u5f20\u4e39\u67ab\u7ea6\u4e86\u5217\u4f4d\u82f1\u96c4\u5230\u4f60\u5bb6\u6765\uff0c\u5fc5\u662f\u7b97\u5b9a\u53ef\u4ee5\u62ff\u4f60\u4f5c\u4e3a\u6321\u7bad\u724c\u8ba9\u4f60\u66ff\u4ed6\u5316 \u89e3\uff0c\u4f7f\u5f97\u7eff\u6797\u82f1\u96c4\u6b64\u540e\u4e0d\u518d\u4e0e\u4ed6\u4e3a\u96be\u3002\u201d\u6bd5\u9053\u51e1\u54fc\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u82e5\u4ed6\u771f\u662f\u5978\u8d3c\uff0c\u6211\u5b9a\u8981\u4eb2\u624b \u5c06\u4ed6\u6bd9\u4e86\u3002\u201d\u76ee\u5149\u95ea\u95ea\uff0c\u9762\u4e0a\u5145\u6ee1\u7591\u60d1\u7684\u795e\u60c5\uff0c\u5468\u5c71\u6c11\u542c\u4ed6\u8bdd\u8bed\uff0c\u4f3c\u662f\u4ecd\u672a\u6df1\u4fe1\uff0c\u6b63\u60f3\u518d \u8bf4\uff0c\u5ffd\u89c1\u6bd5\u9053\u51e1\u8d70\u51fa\u95e8\u5916\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u4eba\u6765\uff01\u201d\u5429\u5490\u4e00\u4e2a\u5bb6\u4eba\uff1a\u201c\u4f60\u5feb\u53bb\u6253\u63a2\uff0c\u6211\u6d3e\u53bb\u7684 \u4eba\u56de\u6765\u4e86\u6ca1\u6709\uff1f\u201d\u53cd\u8eab\u8f6c\u5165\u5ba2\u5385\uff0c\u5ffd\u5730\u8bf4\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u53ea\u6050\u76ee\u4e0b\u5c31\u6709\u4e00\u573a\u5927\u7978\uff01\u201d \u3000\u3000\u7eff\u6797\u7fa4\u96c4\u4e89\u76f8\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\u5927\u7978\uff1f\u201d\u201c\u6709\u6211\u7b49\u4f17\u4eba\u5728\u6b64\uff0c\u4ec0\u4e48\u4e8b\u4e0d\u80fd\u62c5\u5f53\uff1f\u201d\u6bd5\u9053\u51e1 \u9053\uff1a\u201c\u5217\u4f4d\u6709\u6240\u4e0d\u77e5\uff0c\u6211\u5bb6\u4e43\u662f\u5927\u660e\u5929\u5b50\u7684\u4e16\u4ec7\uff0c\u6731\u5143\u748b\u5728\u751f\u4e4b\u65f6\u5c31\u66fe\u9881\u4e0b\u5bc6\u4ee4\uff0c\u8981\u5c06\u5f20\u5bb6 \u4e0e\u6211\u6bd5\u5bb6\u7684\u540e\u4eba\u65a9\u8349\u9664\u6839\u3002\u6211\u5bb6\u4e16\u4ee3\u4e3a\u50e7\u4e3a\u4e10\uff0c\u9664\u4e86\u4e0a\u9762\u8bf4\u4e24\u4e2a\u539f\u56e0\u4e4b\u5916\uff0c\u8fd8\u6709\u4e00\u4e2a\u539f\u56e0\u5c31 \u662f\u501f\u6b64\u907f\u7978\u3002\u7956\u5b97\u4fdd\u4f51\uff0c\u6570\u4ee3\u4ee5\u6765\uff0c\u8fd8\u672a\u7ed9\u671d\u5ef7\u53d1\u73b0\u8e2a\u8ff9\u3002\u201d \u3000\u3000\u201c\u4e5f\u8bb8\u662f\u6211\u95ef\u8361\u6c5f\u6e56\uff0c\u865a\u540d\u62db\u7978\uff0c\u6570\u5e74\u4e4b\u524d\uff0c\u5df2\u53d1\u73b0\u6709\u9e70\u72ac\u5bf9\u6211\u6ce8\u610f\uff0c\u4e8e\u662f\u6211\u9042\u907f\u5c45\u6b64 \u4e00\u8352\u6751\uff0c\u6f5c\u8e2a\u533f\u8ff9\u3002\u4e0d\u6599\u5341\u6570\u65e5\u524d\uff0c\u6751\u4e2d\u53c8\u53d1\u73b0\u6709\u964c\u751f\u4eba\u6765\u8fc7\uff0c\u542c\u6751\u4e2d\u4eba\u8bf4\uff0c\u90a3\u4e9b\u964c\u751f\u4eba\u8fd8 \u66fe\u95ee\u8fc7\u6211\u7684\u6765\u5386\uff0c\u8fd9\u4e9b\u4eba\u60f3\u6765\u5b9a\u662f\u671d\u5ef7\u7684\u9e70\u72ac\u65e0\u7591\u3002\u5b9e\u4e0d\u76f8\u7792\uff0c\u6211\u672c\u5b9a\u5728\u6570\u65e5\u4e4b\u524d\u5c31\u4e3e\u5bb6\u642c \u8fc1\uff0c\u53ea\u56e0\u90a3\u5f20\u4e39\u67ab\u6307\u5b9a\u4eca\u65e5\u8981\u5728\u6211\u5bb6\u4e0e\u8bf8\u4f4d\u76f8\u4f1a\uff0c\u6545\u6b64\u803d\u6401\u4e0b\u6765\u3002\u82e5\u7136\u7ed9\u4eac\u5e08\u7684\u6731\u7687\u5e1d\u77e5\u9053 \u7eff\u6797\u7fa4\u96c4\u5728\u6211\u5bb6\u805a\u4f1a\uff0c\u6d3e\u9063\u9ad8\u624b\uff0c\u524d\u6765\u56f4\u6355\uff0c\u5c82\u4e0d\u8981\u7ed9\u4ed6\u4e00\u7f51\u6253\u5c3d\u5417\uff1f\u201d \u3000\u3000\u542c\u4e86\u6b64\u756a\u8bdd\u540e\uff0c\u7eff\u6797\u7fa4\u96c4\uff0c\u7591\u5fc3\u66f4\u8d77\uff0c\u5728\u5ba2\u5385\u4e2d\u7ed9\u5f20\u4e39\u67ab\u6253\u8d25\u8fc7\u7684\u201c\u706b\u795e\u5f39\u201d\u90dd\u5b9d\u693f\u9996 \u5148\u8bf4\u9053\uff1a\u201c\u4e8b\u60c5\u6709\u8fd9\u6837\u5de7\u6cd5\uff1f\u6211\u770b\u8fd9\u662f\u90a3\u767d\u9a6c\u5c0f\u8d3c\u6709\u5fc3\u5e03\u4e0b\u7684\u9677\u9631\uff01\u201d\u6bd5\u9053\u51e1\u6c89\u541f\u4e0d\u8bed\uff0c\u84dd \u5be8\u4e3b\u4ea6\u9053\uff1a\u201c\u6b64\u4e8b\u5b9e\u662f\u53eb\u4eba\u7591\u5fc3\uff01\u201d\u6bd5\u9053\u51e1\u9053\uff1a\u201c\u5f20\u58eb\u8bda\u7684\u5b50\u5b59\u600e\u4f1a\u4e0e\u671d\u5ef7\u7ad9\u5728\u4e00\u8d77\uff1f\u201d\u5468 \u5c71\u6c11\u9053\uff1a\u201c\u5f20\u5b97\u5468\u7236\u5b50\u65e2\u80fd\u4f5c\u74e6\u523a\u7684\u5978\u7ec6\uff0c\u4e5f\u5c31\u80fd\u4f5c\u671d\u5ef7\u7684\u5978\u7ec6\u3002\u5982\u6b64\u4e4b\u4eba\uff0c\u4ec0\u4e48\u4e8b\u60c5\u505a\u4e0d \u51fa\u6765\u5462\uff1f\u201d\u6f6e\u97f3\u548c\u5c1a\u4ea6\u9053\uff1a\u201c\u662f\u5440\uff0c\u5f20\u5b97\u5468\u4e0e\u5978\u5ba6\u738b\u632f\u66fe\u6709\u6536\u4fe1\u5f80\u6765\uff0c\u6b64\u4e8b\u6211\u4ea6\u77e5\u9053\u3002\u201d\u6bd5 \u9053\u51e1\u62c8\u987b\u6c89\u541f\uff0c\u534a\u664c\u8bf4\u9053\uff1a\u201c\u6211\u672c\u5bf9\u4ed6\u65e0\u751a\u7591\u5fc3\uff0c\u542c\u5f97\u5468\u8d24\u4f84\u8bf4\u7834\u4ed6\u7684\u6765\u5386\u540e\uff0c\u5374\u6559\u6211\u96be\u5224 \u65ad\u4e86\u3002\u54b3\uff0c\u4e24\u4ef6\u4e8b\u60c5\u8054\u5728\u4e00\u8d77\uff0c\u786e\u662f\u4ee4\u4eba\u601d\u7591\uff0c\u83ab\u975e\u4ed6\u771f\u662f\u7528\u7684\u7f13\u5175\u4e4b\u8ba1\uff0c\u963b\u6b62\u6211\u5bb6\u642c\u8fc1\uff0c \u597d\u4ee4\u671d\u5ef7\u9e70\u72ac\u6709\u65f6\u95f4\u5230\u6b64\u6355\u4eba\u4e48\uff1f\u5440\uff0c\u77e5\u4eba\u77e5\u9762\u4e0d\u77e5\u5fc3\uff0c\u96be\u9053\u8fd9\u6b21\u6211\u771f\u7684\u770b\u9519\u4e86\u4eba\uff1f\u8d70\u4e86\u773c \u4e86\uff1f\u201d\u6bd5\u9053\u51e1\u4e3a\u4eba\u7cbe\u660e\u679c\u65ad\uff0c\u6b64\u6b21\u5374\u662f\u4ed6\u5e73\u751f\u7b2c\u4e00\u6b21\u96be\u4e8e\u51b3\u65ad\u4e8b\u60c5\u3002 \u3000\u3000\u5468\u5c71\u6c11\u6012\u6c14\u51b2\u51b2\uff0c\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u6b64\u4e8b\u4f55\u5fc5\u731c\u7591\uff0c\u5b9a\u662f\u90a3\u5f20\u4e39\u67ab\u6240\u5e03\u7684\u9677\u9631\u3002\u54b1\u4eec\u4e14\u5546\u91cf \u5bf9\u4ed8\u4e4b\u7b56\u5427\uff01\u201d\u7eff\u6797\u7fa4\u8c6a\u53c8\u7eb7\u7eb7\u8bae\u8bba\uff0c\u6709\u7684\u8bf4\u8981\u7b49\u5f85\u5b98\u519b\u524d\u6765\uff0c\u548c\u4ed6\u25a1\u6740\u4e00\u756a\uff0c\u6709\u7684\u8bf4\u4e0d\u5982 \u5148\u907f\u5f00\u7684\u597d\uff0c\u907f\u5f00\u4e4b\u540e\uff0c\u518d\u5e7f\u4f20\u7eff\u6797\u7bad\uff0c\u53eb\u5357\u5317\u7684\u9ed1\u9053\u82f1\u96c4\u90fd\u5171\u540c\u53bb\u5bf9\u4ed8\u90a3\u4e2a\u5f20\u4e39\u67ab\uff0c\u4e00\u5b9a \u8981\u4ee4\u4ed6\u5904\u5904\u8346\u68d8\uff0c\u5bf8\u6b65\u96be\u884c\u3002 \u3000\u3000\u6bd5\u9053\u51e1\u5750\u7acb\u4e0d\u5b89\uff0c\u542c\u7eff\u6797\u7fa4\u8c6a\u7eb7\u7eb7\u8bae\u8bba\uff0c\u51e0\u4e4e\u5168\u90fd\u662f\u5bf9\u5f20\u4e39\u67ab\u4e0d\u5229\u7684\uff0c\u53ea\u6709\u4e91\u857e\u4e00\u4eba\u72ec \u5750\u4e00\u9685\uff0c\u76ee\u8574\u6cea\u5149\uff0c\u5374\u4e0d\u53d1\u8bdd\u3002\u6bd5\u9053\u51e1\u7591\u5fc3\u5927\u8d77\uff0c\u60f3\u9053\uff1a\u201c\u6b64\u4eba\u4e0e\u5f20\u4e39\u67ab\u4ec7\u6068\u6700\u6df1\uff0c\u4f55\u4ee5\u4ed6 \u4e0d\u8bf4\u8bdd\uff0c\u83ab\u975e\u5176\u4e2d\u53e6\u6709\u522b\u60c5\uff1f\u201d\u60f3\u8fc7\u53bb\u4e0e\u4e91\u857e\u5355\u72ec\u8c08\u8bdd\uff0c\u5c4b\u4e2d\u4eba\u58f0\u5982\u6cb8\uff0c\u5608\u5608\u6742\u6742\uff0c\u8c01\u4eba\u7684 \u8bdd\u90fd\u542c\u4e0d\u6e05\u3002\u6bd5\u9053\u51e1\u76b1\u4e86\u76b1\u7709\uff0c\u84e6\u542c\u5f97\u8fdc\u5904\u4e00\u58f0\u9a6c\u5636\uff0c\u6709\u4eba\u53eb\u9053\uff1a\u201c\u90a3\u767d\u9a6c\u5c0f\u8d3c\u53c8\u56de\u6765 \u4e86\uff01\u201d\u7247\u523b\u4e4b\u540e\uff0c\u9a6c\u94c3\u53ee\u5f53\uff0c\u8d8a\u6765\u8d8a\u8fd1\uff0c\u6bd5\u9053\u51e1\u6025\u6025\u5954\u51fa\u95e8\u5916\uff0c\u53ea\u89c1\u4e00\u9a91\u98de\u6765\uff0c\u679c\u7136\u662f\u5f20\u4e39 \u67ab\u90a3\u5339\u767d\u9a6c\uff01 \u3000\u3000\u53ea\u89c1\u5f20\u4e39\u67ab\u795e\u8272\u4ed3\u60f6\uff0c\u6ee1\u5934\u5927\u6c57\uff0c\u4e00\u8dc3\u4e0b\u9a6c\uff0c\u62a2\u7740\u8bf4\u9053\uff1a\u201c\u4e16\u4f2f\u5feb\u8d70\uff01\u201d\u6bd5\u9053\u51e1\u53cc\u773c\u4e00 \u7ffb\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u597d\u5440\uff0c\u4f60\u8fd8\u6709\u4ec0\u4e48\u82b1\u62db\uff1f\u201d\u5f20\u4e39\u67ab\u6014\u4e86\u4e00\u6014\uff0c\u9762\u8272\u500f\u53d8\uff0c\u4ef0\u5929\u72c2\u7b11\u9053\uff1a \u201c\u60a0\u60a0\u82cd\u5929\uff0c\u77e5\u6211\u8c01\u4eba\uff1f\u6bd5\u7237\uff0c\u6b64\u523b\u6211\u4e5f\u4e0d\u613f\u591a\u8d39\u5507\u820c\u8981\u4f60\u4fe1\u6211\u3002\u6211\u53ea\u6c42\u4f60\u5feb\u8d70\uff0c\u5b98\u519b\u79bb\u6b64 \u5df2\u4e0d\u5230\u5341\u91cc\u4e86\uff01\u201d\u6bd5\u9053\u51e1\u6599\u4e0d\u5230\u5b98\u519b\u6765\u5f97\u5982\u6b64\u4e4b\u5feb\uff0c\u6012\u9053\uff1a\u201c\u597d\u5440\u6211\u5c31\u62fc\u7740\u8840\u6e85\u9ec4\u6c99\u597d\u6210 \u5168\u2026\u2026\u201d\u6bd5\u9053\u51e1\u6c14\u6124\u4e4b\u6781\uff0c\u60f3\u8bf4\u7684\u672c\u662f\u201c\u597d\u6210\u5168\u4f60\u5947\u529f\u4e00\u4ef6\u3002\u201d\u773c\u89d2\u77a5\u4e86\u5f20\u4e39\u67ab\u4e00\u4e0b\uff0c\u5ffd\u89c1 \u4ed6\u8863\u88f3\u67d3\u8840\uff0c\u6ee1\u9762\u7126\u6025\u7684\u795e\u8272\uff0c\u5374\u4e0d\u4f3c\u5047\u5192\u5f97\u6765\uff0c\u8fd9\u8bdd\u8bf4\u4e86\u4e00\u534a\uff0c\u53c8\u54bd\u56de\u53bb\u3002\u53ea\u542c\u5f97\u5f20\u4e39\u67ab \u53c8\u9053\uff1a\u201c\u6211\u5728\u6751\u5916\u5341\u4f59\u91cc\u5730\uff0c\u78b0\u89c1\u5b98\u519b\uff0c\u6211\u4ed7\u7740\u5feb\u9a6c\uff0c\u65ab\u4e86\u4e24\u4eba\uff0c\u62a2\u56de\u6765\u7ed9\u4f60\u62a5\u4fe1\u3002\u201d \u3000\u3000\u5ffd\u5730\u91cc\u201c\u84ec\u201d\u7684\u4e00\u58f0\uff0c\u201c\u706b\u795e\u5f39\u201d\u90dd\u5b9d\u693f\u4eba\u672a\u8dc3\u51fa\uff0c\u6697\u5668\u5148\u53d1\uff0c\u4e00\u652f\u86c7\u7130\u7bad\u631f\u7740\u4e00\u6e9c\u84dd \u706b\uff0c\u5411\u5f20\u4e39\u67ab\u5288\u9762\u5c04\u6765\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u95e8\u5185\u7fa4\u96c4\uff0c\u4e00\u6d8c\u800c\u51fa\uff0c\u996e\u9a6c\u5ddd\u7684\u84dd\u5be8\u4e3b\u9996\u5148\u53d1\u8bdd \u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u4f60\u5f53\u6211\u4eec\u662f\u4e09\u5c3a\u5b69\u7ae5\uff0c\u4efb\u7531\u4f60\u620f\u800d\u4e48\uff1f\u201d\u4e0d\u7531\u5f97\u5f20\u4e39\u67ab\u5206\u8fa8\uff0c\u5df2\u6709\u56db\u4e94\u4e2a\u4eba \u4e0a\u524d\u52a8\u624b\uff0c\u7eff\u6797\u7fa4\u8c6a\u7eb7\u7eb7\u559d\u9a82\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u82b1\u8a00\u5de7\u8bed\u9a97\u5f97\u8c01\u6765\uff1f\u201d\u201c\u5148\u628a\u4ed6\u5bb0\u4e86\u518d\u6740\u5b98 \u5175\uff01\u201d\u201c\u60f3\u4e00\u7f51\u6253\u5c3d\uff0c\u53ef\u6ca1\u90a3\u4e48\u5bb9\u6613\uff01\u201d\u867d\u4f17\u53e3\u5f02\u8bcd\uff0c\u5374\u90fd\u662f\u8ba4\u5b9a\u5f20\u4e39\u67ab\u4e0e\u5b98\u519b\u4e00\u8def\uff0c\u4e0a\u524d \u52a8\u624b\u7684\u8d8a\u6765\u8d8a\u591a\u7adf\u628a\u5f20\u4e39\u67ab\u56f4\u5728\u6838\u5fc3\uff0c\u5251\u6c14\u5200\u5149\uff0c\u4e0d\u5206\u7682\u767d\uff0c\u7eb7\u7eb7\u5411\u5f20\u4e39\u67ab\u8eab\u4e0a\u62db\u547c\uff01 \u3000\u3000\u53ea\u542c\u5f97\u53ee\u5f53\u6570\u58f0\u8fd1\u8eab\u7684\u51e0\u53e3\u5175\u5203\u5df2\u7ed9\u5f20\u4e39\u67ab\u7684\u5b9d\u5251\u524a\u65ad\uff0c\u5468\u5c71\u6c11\u4e00\u63a8\u4e91\u857e\u53eb\u9053\uff1a\u201c\u5feb\u5feb \u4e0a\u524d\uff0c\u7528\u4f60\u7684\u5b9d\u5251\u5bf9\u4ed8\u4ed6\uff01\u201d\u4e91\u857e\u8eab\u4e0d\u7531\u5df1\uff0c\u62d4\u51fa\u5b9d\u5251\uff0c\u95ef\u5165\u4eba\u4e1b\u3002\u53ea\u89c1\u5f20\u4e39\u67ab\u767d\u8863\u98d8\u98d8\uff0c \u5728\u5200\u67aa\u5251\u621f\u4e1b\u4e2d\uff0c\u4e1c\u7a9c\u897f\u95ea\uff0c\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u4f60\u4eec\u770b\u6211\u90a3\u5339\u5b9d\u9a6c\uff0c\u82e5\u7136\u6211\u662f\u5b98\u519b\u5185\u5e94\u80af\u8ba9\u5b83\u5982 \u6b64\u53d7\u4f24\u4e48\uff1f\u201d\u90a3\u5339\u201c\u7167\u591c\u72ee\u5b50\u9a6c\u201d\u81c2\u4e0a\u4e2d\u4e86\u4e24\u7bad\uff0c\u8fd8\u63d2\u5728\u90a3\u91cc\uff0c\u60f3\u662f\u88ab\u5b98\u519b\u8ffd\u8d76\u65f6\u653e\u7bad\u6240 \u5c04\uff0c\u6b66\u6797\u4e4b\u58eb\u6700\u7231\u5b9d\u5251\u540d\u9a6c\uff0c\u66f4\u4f55\u51b5\u8fd9\u5339\u5e76\u4e16\u65e0\u4e8c\u7684\u201c\u7167\u591c\u72ee\u5b50\u9a6c\u201d\u5462\uff1f\u5c06\u5fc3\u6bd4\u5fc3\uff0c\u5f20\u4e39\u67ab \u81ea\u5f53\u662f\u7231\u5982\u6027\u547d\uff0c\u800c\u4eca\u4e3a\u4e86\u8d76\u7740\u56de\u6765\u62a5\u4fe1\uff0c\u7adf\u65e0\u6687\u66ff\u5b9d\u9a6c\u62d4\u7bad\u7597\u4f24\uff0c\u56f4\u653b\u7684\u7fa4\u96c4\u6709\u4e00\u534a\u5df2\u653e \u677e\u4e86\u624b\u3002 \u3000\u3000\u201c\u706b\u795e\u5f39\u201d\u90dd\u5b9d\u693f\u53eb\u9053\uff1a\u201c\u7109\u77e5\u8fd9\u662f\u4e0d\u662f\u82e6\u8089\u4e4b\u8ba1\uff1f\u201d\u4ecd\u7136\u6325\u97ad\u731b\u8fdb\uff0c\u53ea\u542c\u5f97\u201c\u5580\u5693\u201d \u4e00\u58f0\uff0c\u97ad\u68a2\u53c8\u88ab\u5b9d\u5251\u524a\u53bb\u4e00\u6bb5\uff0c\u5468\u5c71\u6c11\u53eb\u9053\uff1a\u201c\u5feb\u4e0a\uff01\u201d\u4e91\u857e\u4e00\u5251\u5954\u524d\u8fce\u9762\u4e00\u62db\u201c\u7389\u5973\u6295 \u68ad\u201d\u5f20\u4e39\u67ab\u9762\u8272\u82cd\u767d\uff0c\u5e76\u4e0d\u8fd8\u62db\uff0c\u8eab\u5f62\u4e00\u4e2a\u76d8\u65cb\uff0c\u95ea\u4e86\u5f00\u53bb\u3002\u90dd\u5b9d\u693f\u89c1\u4ed6\u5982\u6b64\uff0c\u8d8a\u53d1\u8ba4\u5b9a\u4ed6 \u662f\u80c6\u602f\u60c5\u865a\uff0c\u6325\u821e\u94a2\u97ad\uff0c\u4e0a\u6253\u201c\u96ea\u82b1\u76d6\u9876\u201d\uff0c\u4e0b\u6253\u201c\u67af\u6811\u76d8\u6839\u201d\uff0c\u53ea\u542c\u5f97\u53c8\u662f\u201c\u5580\u5693\u201d\u4e00 \u58f0\uff0c\u5f20\u4e39\u67ab\u5b9d\u5251\u7565\u6325\uff0c\u7adf\u628a\u94a2\u97ad\u4ece\u4e2d\u622a\u65ad\uff0c\u5269\u4e0b\u534a\u622a\uff0c\u821e\u5f04\u4e0d\u5f97\u3002\u4e91\u857e\u5982\u9189\u5982\u72c2\uff0c\u624b\u6307\u6296 \u7d22\uff0c\u9752\u51a5\u5251\u626c\u7a7a\u4e00\u95ea\uff0c\u6b32\u523a\u4e0d\u523a\u3002\u53ea\u542c\u5f97\u5f20\u4e39\u67ab\u5927\u53eb\u9053\uff1a\u201c\u706b\u5df2\u71c3\u7709\u4e86\uff0c\u4f60\u4eec\u8fd8\u4e0d\u5feb\u5feb\u9003 \u8dd1\uff0c\u4e0e\u6211\u7ea0\u7f20\u4f5c\u751a\uff1f\u201d\u84dd\u5be8\u4e3b\u559d\u9053\uff1a\u201c\u5478\uff0c\u4f60\u60f3\u62ff\u5b98\u519b\u5413\u552c\u8001\u5b50\uff1f\u54b1\u4eec\u90fd\u662f\u5728\u5b98\u519b\u7684\u5200\u67aa\u4e0b \u957f\u5927\u7684\uff01\u201d\u628a\u624b\u4e00\u6325\uff0c\u53c8\u7387\u7fa4\u96c4\u56f4\u4e0a\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u957f\u5251\u4e00\u5c55\uff0c\u5212\u4e86\u4e00\u9053\u5706\u5b64\uff0c\u6321\u7740\u7fa4\u96c4\u5175\u5203\uff0c\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u8fd9\u662f\u4ece\u4eac\u57ce\u6765\u7684\u9526\u8863 \u536b\uff0c\u4f60\u5f53\u662f\u666e\u901a\u7684\u5b98\u519b\u4e48\uff1f\u770b\u76f8\u5b50\u53ea\u6015\u662f\u4eac\u5e08\u7684\u4e09\u5927\u9ad8\u624b\uff0c\u5168\u90fd\u6765\u4e86\uff01\u201d\u9526\u8863\u536b\u6307\u6325\u5f20\u98ce \u5e9c\uff0c\u5fa1\u524d\u4f8d\u536b\u6a0a\u5fe0\uff0c\u5185\u5ef7\u536b\u58eb\u8d2f\u4ef2\uff0c\u8fd9\u4e09\u4eba\u4ee5\u524d\u90fd\u662f\u6b66\u6797\u4eba\u7269\uff0c\u8eab\u624b\u975e\u51e1\u5929\u4e0b\u95fb\u540d\uff0c\u5408\u79f0\u4eac \u5e08\u4e09\u5927\u9ad8\u624b\u3002\u7fa4\u96c4\u542c\u4e86\u4e0d\u89c9\u90fd\u662f\u4e00\u6014\uff0c\u8fd9\u65f6\u90a3\u5339\u767d\u9a6c\u6b63\u5728\u8d1f\u75db\u957f\u5636\uff0c\u88ab\u6f6e\u97f3\u548c\u5c1a\u7684\u7985\u6756\u9694 \u4f4f\uff0c\u51b2\u4e0d\u8fc7\u6765\u3002\u6bd5\u9053\u51e1\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u8fd9\u767d\u9a6c\u795e\u9a8f\u975e\u5e38\uff0c\u5feb\u903e\u8ffd\u98ce\uff0c\u7adf\u7136\u4e5f\u4e2d\u654c\u4eba\u4e24\u7bad\uff0c\u80fd\u5c04 \u4f24\u8fd9\u5339\u767d\u9a6c\u7684\u4eba\uff0c\u5373\u975e\u4e09\u5927\u9ad8\u624b\uff0c\u4e5f\u662f\u975e\u5e38\u4eba\u7269\uff0c\u8fd9\u4e66\u751f\u6240\u8a00\uff0c\u5b81\u53ef\u4fe1\u5176\u6709\uff0c\u4e0d\u53ef\u4fe1\u5176 \u65e0\u3002\u201d\u53ea\u542c\u5f97\u5f20\u4e39\u67ab\u53eb\u9053\uff1a\u201c\u9526\u8863\u536b\u7684\u540e\u9762\u8fd8\u6709\u5927\u961f\u7684\u5fa1\u6797\u519b\uff0c\u82e5\u8bf4\u53ea\u662f\u6355\u6349\u6bd5\u7237\u4e00\u4eba\uff0c\u4f55 \u987b\u7528\u8fd9\u8bb8\u591a\u4eba\u9a6c\uff1f\u82e5\u7136\u5fa1\u6797\u519b\u5206\u5175\u53bb\u88ad\u5404\u4f4d\u7684\u5c71\u5be8\u5404\u4f4d\u4e0d\u5728\uff0c\u5982\u4f55\u5e94\u4ed8\uff1f\u201d\u6b64\u8a00\u4e00\u51fa\uff0c\u7eff\u6797 \u7fa4\u96c4\u66f4\u662f\u8038\u7136\u52a8\u5bb9\uff0c\u6709\u4e00\u5c0f\u534a\u5df2\u4e89\u6025\u4e0a\u9a6c\uff0c\u5306\u5306\u5411\u6bd5\u9053\u51e1\u62f1\u624b\u544a\u8f9e\uff0c\u9a70\u5f52\u5c71\u5be8\u3002 \u3000\u3000\u5468\u5c71\u6c11\u5927\u6012\u53eb\u9053\uff1a\u201c\u597d\u4e2a\u5978\u8d3c\uff0c\u5371\u8a00\u606b\u5413\uff0c\u4f60\u53c8\u4e0d\u662f\u5fa1\u6797\u519b\u7684\u6307\u6325\uff0c\u5982\u4f55\u77e5\u9053\u4ed6\u4eec\u7528\u5175 \u4e4b\u8ba1\uff1f\u9664\u975e\u4f60\u5c31\u662f\u4e0e\u4ed6\u4eec\u5408\u8c0b\u4e4b\u4eba\uff01\u201d\u5f20\u4e39\u67ab\u4ef0\u5929\u54c8\u54c8\u4e00\u7b11\uff0c\u968f\u624b\u4e00\u62db\u201c\u516b\u65b9\u98ce\u96e8\u201d\uff0c\u957f\u5251 \u4e00\u6325\uff0c\u8361\u5f00\u4e86\u84dd\u5929\u77f3\u3001\u90dd\u5b9d\u693f\u4e0e\u5468\u5c71\u6c11\u7b49\u4eba\u7684\u5175\u5668\uff0c\u5927\u7b11\u8bf4\u9053\uff1a\u201c\u6789\u4f60\u7239\u7239\u66fe\u662f\u8fb9\u5173\u540d\u5c06\uff0c \u4f60\u5373\u672a\u8bfb\u8fc7\u5175\u4e66\uff0c\u4e5f\u5f53\u77e5\u9053\u4e00\u70b9\u5175\u6cd5\uff0c\u4e3a\u5c06\u4e4b\u9053\uff0c\u5f53\u77e5\u6599\u654c\u5bdf\u52bf\uff0c\u56fa\u5df1\u9632\u4eba\uff0c\u6700\u4e0d\u6d4e\u4e5f\u5f53\u77e5 \u9053\u6743\u8861\u8f7b\u91cd\u3002\u5373\u7b97\u6211\u662f\u4f60\u6240\u8bf4\u7684\u2018\u5978\u8d3c\u2019\uff0c\u8bd5\u95ee\u5927\u654c\u5f53\u524d\uff0c\u4f60\u4eec\u4e3a\u6211\u4e00\u4eba\u800c\u5192\u57fa\u4e1a\u6bc1\u706d\u4e4b \u9669\uff0c\u8fd9\u4e0d\u662f\u611a\u7b28\u4e4b\u6781\u5417\uff1f\u201d\u4e0d\u5f85\u5f20\u4e39\u67ab\u8bf4\u5b8c\uff0c\u56f4\u653b\u7684\u7fa4\u8c6a\u53c8\u6709\u4e00\u534a\u6563\u53bb\uff0c\u5468\u5c71\u6c11\u6da8\u7ea2\u4e86\u9762\u6012 \u9053\uff1a\u201c\u6211\u7684\u5c71\u5be8\u4e0d\u5728\u6b64\u5904\uff0c\u4e5f\u4e0d\u6015\u5b98\u519b\u56f4\u88ad\uff0c\u6211\u8fd8\u8981\u518d\u9886\u6559\u9886\u6559\u4f60\u7684\u5251\u6cd5\uff0c\u857e\u5f1f\u4e0a\u554a\uff01\u201d\u4e91 \u857e\u4e00\u5251\u683c\u5f00\u5f20\u4e39\u67ab\u7684\u5b9d\u5251\uff0c\u5468\u5c71\u6c11\u8fc8\u6b65\u5c31\u662f\u4e00\u5200\uff0c\u5f20\u4e39\u67ab\u5fae\u5fae\u4e00\u7b11\uff0c\u5de6\u624b\u634f\u7740\u5251\u8bc0\uff0c\u5e76\u672a\u5f20 \u5f00\uff0c\u968f\u624b\u4e00\u62c2\uff0c\u53ea\u542c\u5f97\u5f53\u25a1\u4e00\u58f0\uff0c\u5468\u5c71\u6c11\u5200\u5df2\u5815\u5730\u3002 \u3000\u3000\u6bd5\u9053\u51e1\u770b\u5728\u773c\u91cc\uff0c\u542c\u5728\u5fc3\u4e2d\uff0c\u6697\u6697\u70b9\u5934\uff0c\u5fc3\u4e2d\u8bf4\u9053\uff1a\u201c\u82e5\u7136\u5f20\u4e39\u67ab\u771f\u4e2a\u52a8\u624b\uff0c\u5468\u5c71\u6c11\u4e0d \u6b7b\u4e5f\u5f97\u91cd\u4f24\u3002\u56f4\u653b\u4ed6\u7684\u5404\u8def\u5be8\u4e3b\u5175\u5203\u5341\u4e5d\u90fd\u8981\u88ab\u4ed6\u524a\u65ad\u3002\u201d\u90a3\u5339\u767d\u9a6c\u88ab\u6f6e\u97f3\u548c\u5c1a\u6240\u963b\uff0c\u53eb\u58f0 \u4e0d\u7edd\uff0c\u6bd5\u9053\u51e1\u7eb5\u6b65\u5954\u524d\uff0c\u53e3\u4e2d\u4f5c\u9a6c\u53eb\u4e4b\u58f0\uff0c\u5de6\u624b\u4e00\u62db\uff0c\u7a81\u7136\u4e00\u4e2a\u98de\u8eab\u8df3\u8fd1\u90a3\u5339\u767d\u9a6c\uff0c\u767d\u9a6c\u7adf \u4f3c\u751a\u6709\u7075\u6027\uff0c\u77e5\u9053\u6765\u4eba\u5e76\u65e0\u6076\u610f\uff0c\u56db\u8e44\u8e0f\u5730\uff0c\u4e0d\u518d\u8df3\u8dc3\u3002\u6bd5\u9053\u51e1\u8f7b\u629a\u9a6c\u9888\uff0c\u53f3\u624b\u4e00\u4f38\uff0c\u5feb\u5982 \u95ea\u7535\u822c\u628a\u4e24\u679d\u7bad\u62d4\u4e0b\uff0c\u8fc5\u5373\u628a\u85cf\u5728\u638c\u5fc3\u7684\u91d1\u521b\u836f\u66ff\u5b83\u6577\u4e0a\u3002\u6bd5\u9053\u51e1\u8001\u4e8e\u6c5f\u6e56\uff0c\u6c5f\u6e56\u5ba2\u5e94\u901a\u6653 \u7684\u5404\u79cd\u6742\u5b66\uff0c\u4ed6\u65e0\u4e0d\u5185\u884c\uff0c\u9a6f\u9a6c\u533b\u9a6c\u66f4\u662f\u64c5\u957f\uff0c\u4ee4\u6f6e\u97f3\u548c\u5c1a\u770b\u5f97\u76ee\u77aa\u53e3\u5446\u3002 \u3000\u3000\u5468\u5c71\u6c11\u62fe\u8d77\u5355\u5200\uff0c\u4ecd\u4e0e\u84dd\u5929\u77f3\u7b49\u4eba\u604b\u6218\u4e0d\u901a\uff0c\u4e91\u857e\u9762\u76ee\u6beb\u65e0\u8868\u60c5\uff0c\u5de6\u4e00\u5251\u53f3\u4e00\u5251\u7684\u8ddf\u7740 \u5468\u5c71\u6c11\u5411\u5f20\u4e39\u67ab\u4e71\u523a\uff0c\u5ffd\u542c\u5f97\u6bd5\u9053\u51e1\u6717\u58f0\u8bf4\uff1a\u201c\u5f20\u5144\uff0c\u4f60\u7684\u5b9d\u9a6c\u6765\u4e86\uff0c\u4f60\u5feb\u5feb\u8d70\u5427\uff01\u201d \u3000\u3000\u5468\u5c71\u6c11\u5403\u4e86\u4e00\u60ca\uff0c\u628a\u773c\u770b\u65f6\uff0c\u53ea\u89c1\u6bd5\u9053\u51e1\u6b63\u628a\u6f6e\u97f3\u548c\u5c1a\u62c9\u5f00\uff0c\u8ba9\u90a3\u5339\u767d\u9a6c\u51b2\u4e86\u8fc7\u6765\u3002\u90dd \u5b9d\u693f\u6025\u9053\uff1a\u201c\u7eb5\u864e\u5bb9\u6613\u6349\u864e\u96be\uff0c\u6bd5\u8001\u82f1\u96c4\uff0c\u8bf7\u4f60\u4e09\u601d\u3002\u201d\u53ea\u542c\u5f97\u6bd5\u9053\u51e1\u53c8\u9053\uff1a\u201c\u5f20\u5144\uff0c\u4f60\u4eca \u65e5\u7684\u597d\u610f\u6211\u5fc3\u9886\u4e86\uff0c\u4f60\u7684\u9a6c\u6240\u4f24\u975e\u91cd\uff0c\u5feb\u5feb\u8dd1\u5427\uff01\u201d\u84dd\u5929\u77f3\u6115\u7136\u505c\u624b\uff0c\u4e91\u857e\u95ea\u8fc7\u4e00\u8fb9\uff0c\u5373\u5468 \u5c71\u6c11\u4e5f\u9000\u540e\u4e86\u51e0\u6b65\u3002 \u3000\u3000\u4f46\u89c1\u5f20\u4e39\u67ab\u5fae\u5fae\u4e00\u7b11\uff0c\u541f\u9053\uff1a\u201c\u6570\u4ee3\u4ea4\u60c5\u5df2\u53ef\u8d35\uff0c\u76f8\u77e5\u4e00\u9762\u66f4\u96be\u80fd\uff01\u6bd5\u8001\u4f2f\uff0c\u4f60\u4e0d\u5fc5\u7ba1 \u6211\uff0c\u5feb\u5feb\u9003\u8dd1\u3002\u201d\u6bd5\u9053\u51e1\u8bf4\u9053\uff1a\u201c\u6211\u4e3e\u5bb6\u5927\u5c0f\uff0c\u8fd8\u6709\u4e9b\u7269\u4e8b\u9700\u8981\u6536\u62fe\uff0c\u4f60\u5148\u8dd1\u5427\uff01\u55ef\u84dd\u5be8 \u4e3b\uff0c\u90dd\u5e84\u4e3b\uff0c\u5468\u8d24\u4f84\uff0c\u4f60\u4eec\u4e5f\u5feb\u8dd1\u5427\uff01\u5f20\u4e39\u67ab\u4eca\u65e5\u4e4b\u4e8b\uff0c\u4f60\u4eec\u4e0d\u5fc5\u7ba1\u4e86\uff01\u201d \u3000\u3000\u84dd\u5929\u77f3\u4e00\u8a00\u4e0d\u53d1\uff0c\u4e0a\u9a6c\u4fbf\u8d70\u3002\u90dd\u5b9d\u693f\u5446\u7acb\u5f53\u573a\u4e00\u7247\u832b\u7136\u3002\u5468\u5c71\u6c11\u6301\u5200\u8e0c\u8e87\uff0c\u6b63\u60f3\u8bf4\u8bdd\uff0c \u5ffd\u542c\u5f97\u4e07\u6012\u5954\u817e\u4e4b\u58f0\uff0c\u5df2\u5982\u6f6e\u6c34\u822c\u500f\u7136\u6d8c\u81f3\uff0c\u968f\u5373\u542c\u5f97\u54cd\u7bad\u58f0\u3001\u5450\u558a\u58f0\u9707\u8361\u5c71\u8c37\uff0c\u6bd5\u9053\u51e1\u9762 \u8272\u4e00\u53d8\uff0c\u5411\u7ba1\u5bb6\u7684\u5429\u5490\u4e86\u51e0\u53e5\uff0c\u51c4\u7136\u8bf4\u9053\uff1a\u201c\u53eb\u4f60\u4eec\u8dd1\u4f60\u4eec\u65e9\u4e0d\u8dd1\uff0c\u73b0\u5728\u8dd1\u53ef\u96be\u4e86\uff01\u201d \u3000\u3000\u5c0f\u6751\u5728\u7fa4\u5c71\u5305\u56f4\u4e4b\u4e2d\uff0c\u53ea\u89c1\u5c71\u9053\u4e0a\u4e09\u6761\u4eba\u5f71\u75be\u51b2\u800c\u4e0b\uff0c\u968f\u540e\u662f\u51e0\u5341\u9a91\u9a6c\u5339\u81ea\u8c37\u53e3\u9c7c\u8d2f\u800c \u5165\uff0c\u81ea\u5c71\u4e0a\u5954\u4e0b\u7684\u4e09\u4eba\u7adf\u8d76\u5728\u9a6c\u961f\u524d\u5934\uff0c\u5148\u5230\u6751\u5e84\uff0c\u542c\u90a3\u5c71\u8c37\u5916\u7684\u9a6c\u8e44\u58f0\uff0c\u60f3\u5fc5\u8fd8\u6709\u6570\u4ee5\u5343 \u8ba1\u7684\u5fa1\u6797\u519b\u56f4\u5728\u5916\u9762\u3002 \u3000\u3000\u6bd5\u9053\u51e1\u6253\u4e86\u4e2a\u54c8\u54c8\uff0c\u8fce\u4e0a\u524d\u9053\uff1a\u201c\u6bd5\u67d0\u51e0\u6839\u8001\u9aa8\u5934\u4f55\u582a\u4e00\u51fb\uff1f\u7d2f\u5f97\u4e09\u4f4d\u5927\u4eba\u8385\u4e34\u5c71\u6751\uff0c \u771f\u662f\u5e78\u4f55\u5982\u4e4b\uff01\u201d\u4e3a\u9996\u4e09\u4eba\uff0c\u5f53\u4e2d\u7684\u90a3\u4e2a\u519b\u5b98\u5251\u7709\u864e\u76ee\u4e0d\u6012\u81ea\u5a01\uff0c\u6b63\u662f\u9526\u8863\u536b\u7684\u6307\u6325\u5f20\u98ce \u5e9c\uff0c\u5bb6\u4f20\u201c\u4e94\u864e\u65ad\u95e8\u5200\u6cd5\u201d\u5929\u4e0b\u65e0\u53cc\uff0c\u5de6\u8fb9\u7684\u90a3\u4eba\u9762\u5982\u9505\u5e95\uff0c\u77ed\u987b\u82e5\u621f\uff0c\u4e43\u662f\u5fa1\u524d\u4f8d\u536b\u6a0a \u5fe0\uff0c\u53f3\u8fb9\u7684\u90a3\u4e2a\u9762\u8272\u7126\u9ec4\uff0c\u53cc\u773c\u51f8\u51fa\uff0c\u5374\u662f\u5927\u5185\u7684\u9ad8\u624b\u8d2f\u4ef2\u3002\u6a0a\u5fe0\u4e0e\u6bd5\u9053\u51e1\u5341\u4f59\u5e74\u524d\u5728\u6c5f\u6e56 \u4e0a\u66fe\u6709\u4e00\u9762\u4e4b\u7f18\uff0c\u9996\u5148\u8bf4\u9053\uff1a\u201c\u6bd5\u5927\u7237\uff0c\u54b1\u4eec\u90fd\u662f\u5949\u4e0a\u547d\u5dee\u9063\u800c\u6765\uff0c\u4f60\u53ef\u4f11\u602a\uff0c\u5c31\u70e6\u4f60\u8d70\u4e00 \u8d9f\uff0c\u54b1\u4eec\u7edd\u4e0d\u4f1a\u5c06\u4f60\u96be\u4e3a\u3002\u201d\u6bd5\u9053\u51e1\u51b7\u51b7\u4e00\u7b11\uff0c\u6b63\u60f3\u51fa\u8bed\u56de\u656c\uff0c\u5ffd\u542c\u5f97\u5f20\u98ce\u5e9c\u7eb5\u58f0\u5927\u7b11\u62a2\u5148 \u8bf4\u9053\uff1a\u201c\u6a0a\u8d24\u5f1f\uff0c\u4f60\u8fd9\u53ef\u4e0d\u662f\u5e9f\u8bdd\u5417\uff1f\u60f3\u90a3\u9f0e\u9f0e\u5927\u540d\u7684\u9707\u4e09\u754c\u662f\u4f55\u4eba\u7269\uff0c\u7109\u80fd\u675f\u624b\u5c31\u64d2\uff1f\u54b1 \u4eec\u8fd8\u662f\u723d\u723d\u5feb\u5feb\u5730\u76f4\u8bf4\u4e86\u5427\u3002\u6bd5\u5927\u7237\uff0c\u4eca\u65e5\u4e4b\u4e8b\uff0c\u975e\u903c\u5f97\u52a8\u624b\u4e0d\u6210\uff0c\u5c31\u8bf7\u4eae\u51fa\u5175\u5668\uff0c\u8d50\u6559\u51e0 \u62db\uff0c\u4f60\u82e5\u95ef\u5f97\u8fc7\u6211\u7684\u5b9d\u5200\uff0c\u90a3\u4e48\u5929\u5927\u7684\u4e8b\u60c5\uff0c\u6211\u4e00\u80a9\u6311\u8d77\uff0c\u653e\u4f60\u9003\u8dd1\u4fbf\u662f\u3002\u81f3\u4e8e\u5728\u573a\u7684\u7eff\u6797 \u9053\u4e0a\u82f1\u96c4\uff0c\u6b63\u662f\u76f8\u8bf7\u4e0d\u5982\u5076\u9047\uff0c\u8bf4\u4e0d\u5f97\u4e5f\u8bf7\u4e00\u5e76\u52a8\u624b\u5566\u3002\u81f3\u4e8e\u4e0d\u662f\u7eff\u6797\u9053\u4e0a\u7684\u670b\u53cb\uff0c\u90a3\u54b1\u4eec \u51b3\u4e0d\u6ee5\u6355\u65e0\u8f9c\uff0c\u8981\u8d70\u8bf7\u4fbf\u3002\u201d\u6a2a\u773c\u4e00\u626b\uff0c\u5ffd\u5730\u626c\u5200\u4e00\u6307\uff0c\u8bf4\u9053\uff1a\u201c\u54a6\uff0c\u8fd9\u4f4d\u79c0\u624d\u5927\u7237\uff0c\u5374\u662f \u54ea\u6761\u7ebf\u4e0a\u7684\u597d\u6c49\uff1f\u201d\u5f20\u4e39\u67ab\u7b11\u9053\uff1a\u201c\u4f60\u662f\u6355\u4eba\u7684\u6307\u6325\uff0c\u6211\u662f\u6349\u9b3c\u7684\u8fdb\u58eb\uff01\u201d\u5f20\u98ce\u5e9c\u5927\u7b11\u9053\uff1a \u201c\u90a3\u4e48\u8bf4\u6765\uff0c\u54b1\u4eec\u53ef\u4e5f\u8981\u8f83\u91cf\u8f83\u91cf\u5566\uff01\u201d\u8d2f\u4ef2\u9002\u624d\u5728\u9014\u4e2d\u4e43\u662f\u5148\u884c\uff0c\u767d\u9a6c\u5c31\u662f\u4ed6\u5c04\u4f24\u7684\uff0c\u77a5 \u4e86\u5f20\u4e39\u67ab\u4e00\u773c\uff0c\u53eb\u9053\uff1a\u201c\u54c8\uff0c\u539f\u6765\u4f60\u4e5f\u5728\u6b64\uff0c\u5999\u6781\u5999\u6781\uff0c\u8fd9\u5339\u767d\u9a6c\u53ef\u5f97\u7ed9\u4ffa\u7559\u4e0b\u6765\u5566\uff01\u201d\u5f20 \u5f13\u642d\u7bad\uff0c\u5f2f\u5f13\u6b32\u5c04\u3002 \u3000\u3000\u6a0a\u5fe0\u7231\u9a6c\u5982\u547d\uff0c\u53eb\u9053\uff1a\u201c\u8d2f\u8d24\u5f1f\uff0c\u4f11\u518d\u5c04\u5b83\u751f\u64d2\u4e3a\u5999\u3002\u201d\u7387\u9886\u58eb\u5352\uff0c\u4e0a\u524d\u6355\u9a6c\uff0c\u5ffd\u5730\u54ce \u54df\u8fde\u58f0\uff0c\u51e0\u540d\u9526\u8863\u536b\u58eb\u624b\u81c2\u5173\u8282\u4e4b\u5904\uff0c\u5982\u88ab\u5229\u9488\u6240\u523a\uff0c\u75db\u5f97\u6cea\u6c34\u76f4\u6d41\u3002\u8d2f\u4ef2\u53eb\u9053\uff1a\u201c\u539f\u6765\u4f60 \u8fd8\u4f1a\u53d1\u6885\u82b1\u9488\uff0c\u6765\u800c\u4e0d\u5f80\u975e\u793c\u4e5f\uff0c\u770b\u7bad\uff01\u201d\u5f2f\u5f13\u4e00\u5c04\uff0c\u7bad\u4f3c\u6d41\u661f\u55d6\u7684\u4e00\u58f0\uff0c\u52b2\u75be\u975e\u5e38\uff0c\u5f20\u4e39 \u67ab\u4e0d\u6562\u624b\u63a5\uff0c\u8eab\u5f62\u4e00\u95ea\u907f\u8fc7\uff0c\u90a3\u679d\u7bad\u4f59\u52bf\u672a\u8870\uff0c\u5c04\u5230\u6f6e\u97f3\u548c\u5c1a\u9762\u524d\uff0c\u6f6e\u97f3\u7985\u6756\u4e00\u6446\uff0c\u94ff\u9535\u6709 \u58f0\uff0c\u706b\u82b1\u98de\u8d77\uff0c\u90a3\u679d\u7bad\u659c\u98de\u6570\u4e08\uff0c\u8fd9\u624d\u6389\u4e0b\u3002\u6f6e\u97f3\u5927\u6012\u6325\u624b\u53eb\u9053\uff1a\u201c\u5468\u8d24\u4f84\uff0c\u54b1\u4eec\u51b2\u51fa \u53bb\uff01\u201d\u7985\u6756\u6a2a\u6311\u76f4\u626b\u95ef\u5165\u9526\u8863\u536b\u58eb\u4e1b\u4e2d\u3002\u6a0a\u5fe0\u624b\u6325\u53cc\u9524\uff0c\u8fce\u5934\u78d5\u4e0b\uff0c\u53ea\u542c\u5f97\u201c\u5f53\u201d\u7684\u4e00\u58f0\u5de8 \u54cd\uff0c\u6f6e\u97f3\u7684\u7985\u6756\u7ed9\u78d5\u5f97\u6b6a\u8fc7\u4e00\u8fb9\uff0c\u6a0a\u5fe0\u7684\u864e\u53e3\u4e5f\u9707\u5f97\u75bc\u75db\u6b32\u88c2\uff0c\u53cc\u9524\u51e0\u4e4e\u638c\u63e1\u4e0d\u4f4f\uff0c\u6a0a\u5fe0\u5728 \u5bab\u4e2d\u6709\u5927\u529b\u58eb\u4e4b\u79f0\uff0c\u4e0e\u6f6e\u97f3\u6362\u4e86\u4e00\u62db\uff0c\u6b63\u662f\u529f\u529b\u6089\u654c\uff0c\u68cb\u9022\u5bf9\u624b\uff0c\u767b\u65f6\u6076\u6218\u8d77\u6765\u3002 \u3000\u3000\u6bd5\u9053\u51e1\u4ef0\u5929\u6253\u4e86\u4e2a\u54c8\u54c8\uff0c\u53d6\u51fa\u964d\u9f99\u68d2\u53eb\u9053\uff1a\u201c\u5f20\u5927\u4eba\u627f\u4f60\u77a7\u5f97\u8d77\u6211\uff0c\u54b1\u4eec\u4e5f\u8f83\u91cf\u8f83 \u91cf\uff01\u201d\u5f20\u98ce\u5e9c\u626c\u5200\u7b11\u9053\uff1a\u201c\u597d\u6781\uff0c\u597d\u6781\uff01\u54b1\u4eec\u5c31\u4f9d\u6c5f\u6e56\u89c4\u77e9\uff0c\u5355\u6253\u72ec\u6597\u4e00\u573a\uff0c\u4f60\u82e5\u95ef\u5f97\u8fc7\u6211 \u7684\u5b9d\u5200\u6211\u6709\u8bdd\u5728\u524d\uff0c\u65e0\u4eba\u5c06\u4f60\u62e6\u963b\u3002\u201d\u8bf4\u8bdd\u53e3\u6c14\uff0c\u81ea\u8d1f\u975e\u51e1\uff0c\u6bd5\u9053\u51e1\u5927\u6012\uff0c\u4fe1\u624b\u4e00\u68d2\uff0c\u75be\u82e5 \u5954\u96f7\uff0c\u5f20\u98ce\u5e9c\u659c\u8eab\u7ed5\u6b65\uff0c\u53cd\u624b\u4e00\u5200\uff0c\u52b2\u98ce\u75be\u8feb\uff0c\u5200\u68d2\u76f8\u4ea4\uff0c\u5404\u9000\u4e09\u6b65\uff0c\u6bd5\u9053\u51e1\u53eb\u9053\uff1a\u201c\u597d\uff01 \u4e0d\u6127\u662f\u4eac\u5e08\u7b2c\u4e00\u9ad8\u624b\uff01\u201d\u624b\u8155\u4e00\u7ffb\uff0c\u964d\u9f99\u68d2\u5237\u7684\u53c8\u6253\u8fc7\u53bb\uff0c\u5f20\u98ce\u5e9c\u5200\u5c16\u8fce\u7740\u6728\u68d2\u8f7b\u8f7b\u4e00\u70b9\uff0c \u501f\u7740\u6728\u68d2\u4e4b\u529b\uff0c\u8eab\u5f62\u9aa4\u7136\u98de\u8d77\uff0c\u5200\u5149\u4e00\u95ea\u4ece\u7a7a\u5288\u4e0b\uff0c\u8fd9\u4e00\u62db\u5389\u5bb3\u975e\u5e38\u3002\u6bd5\u9053\u51e1\u4e34\u5371\u4e0d\u4e71\uff0c\u7a81 \u7136\u4f7f\u51fa\u201c\u94c1\u677f\u6865\u201d\u7684\u7edd\u9876\u529f\u592b\uff0c\u5de6\u53f3\u6491\u5730\uff0c\u53f3\u8db3\u817e\u7a7a\uff0c\u5934\u5411\u540e\u4ef0\uff0c\u7f05\u5200\u5237\u7684\u4e00\u4e0b\u4ece\u4ed6\u5934\u9876\u63a0 \u8fc7\uff0c\u6bd5\u9053\u51e1\u53f3\u8db3\u4e00\u6311\uff0c\u4e00\u4e2a\u201c\u9ca4\u9c7c\u6253\u633a\u201d\uff0c\u7ffb\u8eab\u5c31\u662f\u4e00\u811a\uff0c\u5f20\u98ce\u5e9c\u7684\u5200\u9669\u9669\u7ed9\u4ed6\u8e22\u98de\uff0c\u8d5e\u4e86 \u4e00\u58f0\uff1a\u201c\u9707\u4e09\u754c\u679c\u662f\u540d\u4e0d\u865a\u4f20\uff01\u201d\u62db\u5f0f\u4e00\u6362\uff0c\u4e00\u4e2a\u201c\u8fde\u73af\u4e09\u5200\u201d\uff0c\u75be\u5982\u98ce\u96e8\u7adf\u628a\u6bd5\u9053\u51e1\u903c\u5f97 \u8fde\u8fde\u540e\u9000\uff01 \u3000\u3000\u90a3\u8fb9\u53a2\u8d2f\u4ef2\u4e5f\u4e0e\u5f20\u4e39\u67ab\u52a8\u4e86\u5175\u5203\u3002\u8d2f\u4ef2\u4f7f\u7684\u662f\u4e09\u8282\u8f6f\u97ad\uff0c\u62db\u6570\u7cbe\u5947\uff0c\u4ed6\u8fd8\u4e0d\u77e5\u5f20\u4e39\u67ab\u662f \u4f55\u7b49\u6837\u4eba\uff0c\u610f\u5b58\u8f7b\u654c\uff0c\u624b\u8d77\u4e00\u97ad\u201c\u4e4c\u9f99\u7ed5\u67f1\u201d\uff0c\u811a\u8e0f\u4e2d\u5bab\uff0c\u6beb\u65e0\u987e\u5fcc\u5730\u5411\u5f20\u4e39\u67ab\u624b\u8155\u4fbf\u7ed5\uff0c \u610f\u6b32\u5c06\u4ed6\u5b9d\u5251\u593a\u51fa\u624b\u53bb\u3002\u5f20\u4e39\u67ab\u201c\u563f\u563f\u201d\u51b7\u7b11\uff0c\u624b\u8155\u4e00\u6c89\uff0c\u5251\u950b\u5237\u7684\u53cd\u5f39\u800c\u8d77\uff0c\u5251\u5149\u4e00\u7ed5\uff0c \u7acb\u628a\u8f6f\u97ad\u524a\u53bb\u4e00\u622a\uff0c\u8eab\u5f62\u5fae\u52a8\uff0c\u66f4\u4e0d\u6362\u62db\uff0c\u7b2c\u4e8c\u5251\u3001\u7b2c\u4e09\u5251\u5df2\u8fde\u7ef5\u53d1\u51fa\u3002\u8d2f\u4ef2\u5413\u4e86\u4e00\u8df3\uff0c\u4f46 \u4ed6\u4e5f\u662f\u4e00\u540d\u9ad8\u624b\uff0c\u5728\u7edd\u9669\u4e4b\u9645\uff0c\u7a81\u7136\u4f7f\u9669\u62db\uff0c\u4e0d\u9000\u53cd\u8fdb\uff0c\u5893\u7136\u4f7f\u51fa\u64d2\u62ff\u624b\u6cd5\u53cd\u81c2\u4e00\u6293\uff0c\u5f20\u4e39 \u67ab\u56de\u5251\u4e00\u524a\uff0c\u4ed6\u7684\u8f6f\u97ad\u5df2\u64a4\u4e86\u56de\u6765\uff0c\u62e6\u8170\u4fbf\u626b\uff0c\u5f20\u4e39\u67ab\u5bf8\u6b65\u4e0d\u8ba9\uff0c\u8eab\u5982\u5782\u67f3\uff0c\u5de6\u53f3\u6447\u6446\uff0c\u624b \u5e95\u6beb\u4e0d\u653e\u677e\uff0c\u5237\u5237\u5237\u53c8\u662f\u4e00\u8fde\u4e09\u5251\uff0c\u8d2f\u4ef2\u8f6f\u97ad\u867d\u957f\uff0c\u5374\u662f\u6cbe\u4e0d\u7740\u4ed6\u7684\u8863\u88f3\uff0c\u53cd\u7ed9\u903c\u5f97\u9000\u4e86\u51e0 \u6b65\u3002\u5f20\u4e39\u67ab\u7740\u7740\u62a2\u5148\uff0c\u6325\u5251\u5f3a\u653b\uff0c\u4f46\u8feb\u5207\u4e4b\u95f4\uff0c\u5374\u4e5f\u4e0d\u80fd\u7a81\u56f4\u800c\u51fa\u3002\u8fd9\u65f6\u4e24\u8fb9\u5df2\u6210\u4e86\u6df7\u6218\u4e4b \u5c40\uff0c\u9526\u8863\u536b\u5df2\u51b2\u5165\u6bd5\u5bb6\uff0c\u5413\u5f97\u9e21\u98de\u72d7\u8d70\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u628a\u773c\u770b\u65f6\uff0c\u53ea\u89c1\u6f6e\u97f3\u548c\u5c1a\u4e0e\u5fa1\u524d\u4f8d\u536b\u6a0a\u5fe0\u6070\u6070\u6218\u6210\u5e73\u624b\uff0c\u6bd5\u9053\u51e1\u5374\u662f\u8fde\u8d70\u4e0b\u98ce\u3002 \u5f20\u98ce\u5e9c\u90a3\u53e3\u5200\u4e43\u662f\u767e\u70bc\u7f05\u5200\uff0c\u5728\u5175\u5203\u4e0a\u5148\u4e0d\u5403\u4e8f\uff0c\u529f\u529b\u4e0a\u4e24\u4eba\u90fd\u5dee\u4e0d\u591a\uff0c\u4f46\u5f20\u98ce\u5e9c\u5360\u4e86\u5e74\u58ee \u529b\u5f3a\u7684\u4fbf\u5b9c\uff0c\u4e00\u53e3\u5200\u6709\u5982\u795e\u9f99\u63a2\u722a\uff0c\u98de\u9e70\u5c55\u7fc5\uff0c\u7740\u7740\u90fd\u662f\u8fdb\u624b\u7684\u62db\u6570\uff0c\u6bd5\u9053\u51e1\u903c\u5f97\u8f6c\u653b\u4e3a \u5b88\uff0c\u4f7f\u51fa\u6f5c\u9f99\u62a4\u5b9d\u76d8\u65cb\u5341\u516b\u6253\u7684\u68d2\u6cd5\u5c06\u95e8\u6237\u5b88\u5f97\u5341\u5206\u4e25\u5bc6\u3002\u867d\u7136\u5982\u6b64\u4f46\u4e45\u6218\u4e0b\u53bb\uff0c\u5374\u662f\u5b9a\u5fc5 \u5403\u4e8f\u3002 \u3000\u3000\u5f20\u98ce\u5e9c\u3001\u8d2f\u4ef2\u3001\u6a0a\u5fe0\u4e09\u4eba\u90fd\u662f\u5355\u6253\u72ec\u6597\uff0c\u5176\u4f59\u7684\u4eba\u5219\u5df2\u6210\u6df7\u6218\u4e4b\u5c40\u3002\u5f20\u4e39\u67ab\u518d\u628a\u773c\u770b \u65f6\uff0c\u53ea\u89c1\u4e91\u857e\u4ed7\u7740\u5b9d\u5251\u4e4b\u5a01\uff0c\u524a\u65ad\u4e86\u8bb8\u591a\u9526\u8863\u536b\u7684\u5175\u5203\uff0c\u63a9\u62a4\u5468\u5c71\u6c11\u4e0e\u90dd\u5b9d\u693f\u7b49\u4eba\uff0c\u4e14\u6218\u4e14 \u8d70\uff0c\u6e10\u6e10\u51b2\u5230\u4e86\u6f6e\u97f3\u548c\u5c1a\u7684\u8ddf\u524d\u3002 \u3000\u3000\u6a0a\u5fe0\u4e0e\u6f6e\u97f3\u6b63\u5728\u9ad8\u547c\u9163\u6597\uff0c\u5ffd\u89c1\u9752\u5149\u4e00\u95ea\uff0c\u4e91\u857e\u7684\u5b9d\u5251\u65cb\u98ce\u822c\u5730\u5954\u524d\u5fc3\u6025\u523a\u800c\u6765\uff0c\u6a0a\u5fe0 \u53cc\u9524\u4e00\u5206\uff0c\u5de6\u9524\u62a4\u8eab\u53f3\u9524\u8fce\u654c\u3002\u4e91\u857e\u7684\u5251\u6cd5\u4ee5\u5947\u8be1\u5584\u53d8\u89c1\u957f\uff0c\u6a0a\u5fe0\u4e00\u9524\u51fb\u53bb\uff0c\u6ee1\u62df\u5c06\u654c\u4eba\u7684 \u5b9d\u5251\u51fb\u98de\uff0c\u4e0d\u6599\u9661\u89c1\u9752\u5149\u75be\u95ea\uff0c\u4f3c\u5de6\u5ffd\u53f3\uff0c\u6025\u6025\u53d8\u62db\u8fce\u654c\uff0c\u5de6\u9524\u5374\u7ed9\u6f6e\u97f3\u7684\u7985\u6756\u5c01\u4f4f\uff0c\u6253\u4e0d \u51fa\u6765\u3002\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u53ea\u542c\u5f97\u5237\u7684\u4e00\u58f0\uff0c\u6a0a\u5fe0\u80a9\u5934\u5df2\u7740\u4e86\u4e00\u5251\uff0c\u6a0a\u5fe0\u5927\u543c\u4e00\u58f0\u5de6\u9524\u7529\u624b\u98de \u51fa\uff0c\u4e91\u857e\u987f\u89c9\u52b2\u98ce\u8d2f\u80f8\uff0c\u6025\u95ea\u5f00\u65f6\uff0c\u4f46\u89c1\u90a3\u9524\u76f4\u98de\u51fa\u6570\u4e08\u4e4b\u5916\uff0c\u8f70\u7684\u4e00\u58f0\u5de8\u54cd\uff0c\u649e\u5728\u5c71\u5ca9\u77f3 \u4e4b\u4e0a\uff0c\u6253\u5f97\u77f3\u7247\u7eb7\u98de\uff0c\u800c\u6a0a\u5fe0\u4e5f\u8d81\u7740\u4e00\u63b7\u4e4b\u5a01\uff0c\u7eb5\u8eab\u8df3\u51fa\u5708\u5b50\u3002 \u3000\u3000\u4e91\u857e\u867d\u628a\u6a0a\u5fe0\u6253\u9000\uff0c\u4f46\u7ed9\u9524\u98de\u4e00\u903c\uff0c\u6709\u5982\u7a81\u7136\u95f4\u7ed9\u5343\u65a4\u91cd\u7269\u4e00\u538b\uff0c\u6c14\u4e5f\u51e0\u4e4e\u900f\u4e0d\u8fc7\u6765\uff0c \u77e5\u9053\u5389\u5bb3\uff0c\u4e0d\u6562\u518d\u903c\uff0c\u4e0e\u6f6e\u97f3\u548c\u5c1a\u6025\u6025\u95ef\u51fa\u3002\u90a3\u5339\u7ea2\u9b03\u6218\u9a6c\u98de\u5954\u8fc7\u6765\uff0c\u4e91\u857e\u4e00\u8dc3\u4e0a\u9a6c\uff0c\u4ed7\u5251 \u5411\u524d\u5f00\u8def\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u89c1\u4e91\u857e\u5373\u5c06\u8131\u9669\uff0c\u5fc3\u4e2d\u5927\u559c\uff0c\u7cbe\u795e\u500d\u957f\uff0c\u5237\u5237\u4e24\u5251\u53c8\u628a\u8d2f\u4ef2\u903c\u9000\u51e0\u6b65\uff0c\u5927\u58f0\u53eb \u9053\uff1a\u201c\u6bd5\u4e16\u4f2f\uff0c\u626f\u547c\uff01\u201d\u6bd5\u9053\u51e1\u95f7\u58f0\u4e0d\u54cd\uff0c\u6325\u68d2\u529b\u6218\uff0c\u5bf9\u5f20\u4e39\u67ab\u7684\u8bf4\u8bdd\uff0c\u5982\u542c\u800c\u4e0d\u95fb\u3002\u5f20\u4e39 \u67ab\u7709\u5934\u4e00\u76b1\uff0c\u518d\u628a\u773c\u770b\u65f6\uff0c\u53ea\u89c1\u4e91\u857e\u4e00\u9a6c\u5f53\u524d\uff0c\u5de6\u6709\u6f6e\u97f3\u548c\u5c1a\uff0c\u53f3\u6709\u77f3\u7fe0\u51e4\u3001\u5468\u5c71\u6c11\u3001\u90dd\u5b9d \u693f\u7b49\u7eff\u6797\u597d\u6c49\u8ddf\u5728\u540e\u9762\uff0c\u770b\u770b\u5c31\u8981\u95ef\u51fa\u91cd\u56f4\uff0c\u5f20\u4e39\u67ab\u5fc3\u9053\uff1a\u201c\u6b64\u65f6\u4e0d\u8d70\uff0c\u66f4\u5f85\u4f55\u65f6\uff1f\u201d\u53c8\u5927 \u58f0\u9053\uff1a\u201c\u7559\u5f97\u9752\u5c71\u5728\uff0c\u4e0d\u6015\u6ca1\u67f4\u70e7\u3002\u6bd5\u8001\u82f1\u96c4\uff0c\u5e76\u80a9\u5b50\u95ef\u554a\uff01\u201d\u6bd5\u9053\u51e1\u4ecd\u662f\u95f7\u58f0\u4e0d\u54cd\uff0c\u5982\u542c \u800c\u4e0d\u95fb\u3002\u4e00\u6839\u68d2\u76d8\u65cb\u98de\u821e\uff0c\u604b\u6218\u4e0d\u4f11\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u731b\u7136\u9192\u8d77\uff0c\u6bd5\u9053\u51e1\u548c\u5f20\u98ce\u5e9c\u6bd4\u6597\u4e4b\u65f6\uff0c\u66fe\u88ab\u5f20\u98ce\u5e9c\u51fa\u8a00\u6240\u6fc0\uff0c\u82e5\u7136\u4e0d\u80fd\u4ece\u5f20\u98ce\u5e9c \u5b9d\u5200\u4e4b\u4e0b\u95ef\u51fa\uff0c\u6362\u8a00\u4e4b\u5373\u662f\u82e5\u4e0d\u80fd\u5c06\u5f20\u98ce\u5e9c\u6253\u9000\uff0c\u5219\u4ed6\u65ad\u4e0d\u80af\u9003\u8dd1\u3002\u6240\u4ee5\u73b0\u5728\u867d\u5904\u4e0b\u98ce\uff0c\u5374 \u4ecd\u662f\u4f9d\u7740\u6c5f\u6e56\u4e0a\u5355\u6253\u72ec\u6597\u7684\u89c4\u77e9\uff1a\u65e2\u4e0d\u80af\u8ba4\u8f93\uff0c\u90a3\u5c31\u4e0d\u6b7b\u4e0d\u4f11\uff01 \u3000\u3000\u5f20\u4e39\u67ab\u5fc3\u4e2d\u70e6\u8e81\u60f3\u9053\uff1a\u201c\u8fd9\u4e2a\u5173\u5934\u8fd8\u4e89\u8fd9\u53e3\u95f2\u6c14\u4f5c\u751a\uff1f\u201d\u4f46\u4ed6\u77e5\u9053\u6bd5\u9053\u51e1\u813e\u6c14\uff0c\u7eb5\u8bb8\u81ea \u5df1\u4e0a\u524d\u52a9\u4ed6\u6253\u9000\u654c\u4eba\uff0c\u4ed6\u4e5f\u672a\u5fc5\u80af\u8d70\uff0c\u6b63\u81ea\u8e0c\u8e87\u65e0\u8ba1\uff0c\u5ffd\u542c\u5f97\u4e00\u4e2a\u5b69\u5b50\u53eb\u9053\uff1a\u201c\u653e\u6211\u4e0b\u6765\uff0c \u6211\u4e5f\u8981\u6253\u5f3a\u76d7\uff01\u201d\u539f\u6765\u662f\u6bd5\u5bb6\u5bb6\u4e01\u6b63\u5728\u4e0e\u5b98\u519b\u6df7\u6218\uff0c\u6bd5\u9053\u51e1\u7684\u72ec\u751f\u513f\u5b50\u80cc\u5728\u7ba1\u5bb6\u7684\u80cc\u4e0a\uff0c\u6323 \u624e\u7740\u56b7\u8981\u4e0b\u6765\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u5fc3\u5ff5\u4e00\u52a8\uff0c\u55d6\u7684\u98de\u8eab\u800c\u8d77\uff0c\u5982\u7bad\u79bb\u5f26\u4e09\u8d77\u4e09\u4f0f\uff0c\u95ef\u5165\u9526\u8863\u536b\u58eb\u4e1b\u4e2d\uff0c\u957f\u5251\u6325\u821e\uff0c \u4e91\u6d8c\u98ce\u7ffb\uff0c\u5982\u6c64\u6cfc\u96ea\uff0c\u4e00\u822c\u536b\u58eb\u5982\u4f55\u62e6\u6321\u5f97\u4f4f\uff1f\u53ea\u89c1\u4ed6\u6740\u5165\u5793\u5fc3\uff0c\u9661\u5730\u4f38\u81c2\u4e00\u6293\uff0c\u5c06\u6bd5\u9053\u51e1 \u7684\u513f\u5b50\u593a\u4e86\u8fc7\u6765\uff0c\u7ba1\u5bb6\u7684\u554a\u5440\u4e00\u58f0\uff0c\u5f20\u4e39\u67ab\u53eb\u9053\uff1a\u201c\u4f60\u4eec\u5feb\u5f80\u5916\u95ef\uff01\u201d\u624b\u8d77\u5251\u843d\uff0c\u65ab\u7ffb\u51e0 \u4eba\uff0c\u8fc5\u5373\u53c8\u6740\u51fa\u53bb\uff0c\u5893\u5730\u64ae\u5507\u4e00\u5578\uff0c\u90a3\u5339\u201c\u7167\u591c\u72ee\u5b50\u9a6c\u201d\u88ab\u5b98\u519b\u56f4\u6355\uff0c\u6b63\u5728\u5de6\u51b2\u53f3\u7a81\uff0c\u542c\u5f97 \u4e3b\u4eba\u5578\u58f0\uff0c\u53d1\u529b\u4e00\u51b2\uff0c\u96ea\u8e44\u98de\u5904\uff0c\u8e0f\u5012\u4e24\u4eba\uff0c\u5f20\u4e39\u67ab\u7a81\u7136\u628a\u90a3\u5b69\u5b50\u5f80\u9a6c\u80cc\u4e0a\u4e00\u629b\uff0c\u53eb\u9053\uff1a \u201c\u5750\u7a33\u4e86\uff01\u201d\u90a3\u5b69\u5b50\u867d\u7136\u53ea\u6709\u4e03\u516b\u5c81\uff0c\u80c6\u5b50\u751a\u5927\uff0c\u6293\u4f4f\u9a6c\u25a1\uff0c\u8ba9\u90a3\u767d\u9a6c\u9a6e\u7740\u4fbf\u8dd1\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u8eab\u5f62\u5feb\u6781\uff0c\u8f6c\u8eab\u4e00\u63a0\uff0c\u98de\u4e00\u822c\u7684\u63a0\u5230\u6bd5\u9053\u51e1\u524d\u9762\u3002\u8fd9\u65f6\u6b63\u6709\u51e0\u540d\u9526\u8863\u536b\u58eb\u633a\u67aa\u6420 \u90a3\u767d\u9a6c\uff0c\u767d\u9a6c\u5636\u9e23\uff0c\u5b69\u5b50\u54c7\u54c7\u5927\u53eb\u3002\u5f20\u4e39\u67ab\u5927\u53eb\u9053\uff1a\u201c\u6bd5\u8001\u4f2f\uff0c\u4f60\u8fd8\u4e0d\u53bb\u7167\u987e\u5b69\u5b50\uff01\u201d\u5251\u5c16 \u4e00\u5410\uff0c\u62db\u8d70\u8f7b\u7075\uff0c\u5f53\u7684\u4e00\u58f0\uff0c\u642d\u4e0a\u4e86\u5f20\u98ce\u5e9c\u7684\u7f05\u5200\u3002\u6bd5\u9053\u51e1\u957f\u53f9\u4e00\u58f0\uff0c\u865a\u65ab\u4e24\u5200\uff0c\u5954\u51fa\u5793 \u5fc3\u3002\u5f20\u4e39\u67ab\u53c8\u662f\u4e00\u58f0\u957f\u5578\uff0c\u90a3\u5339\u767d\u5200\u53bb\u52bf\u987f\u7f13\uff0c\u6bd5\u9053\u51e1\u4e00\u624b\u4e09\u6697\u5668\uff0c\u6253\u4f24\u4e86\u90a3\u51e0\u4e2a\u633a\u67aa\u6420\u9a6c \u7684\u5175\u58eb\uff0c\u7eb5\u4e0a\u9a6c\u80cc\uff0c\u62b1\u7d27\u5b69\u5b50\uff0c\u767d\u9a6c\u4e00\u58f0\u957f\u5636\uff0c\u626c\u8e44\u75be\u8d70\uff0c\u970e\u5ffd\u4e4b\u95f4\uff0c\u5df2\u662f\u7a81\u56f4\u800c\u51fa\u3002 \u3000\u3000\u5f20\u98ce\u5e9c\u52c3\u7136\u5927\u6012\uff0c\u65ad\u95e8\u5200\u4e00\u4e2a\u65cb\u98ce\u6025\u8f6c\uff0c\u5f20\u4e39\u67ab\u53ea\u89c9\u4e00\u80a1\u6f5c\u529b\u626f\u7740\u5251\u5c16\uff0c\u5b9d\u5251\u51e0\u4e4e\u8131\u624b \u98de\u51fa\uff0c\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u6b64\u4eba\u679c\u662f\u540d\u4e0d\u865a\u4f20\uff0c\u529f\u529b\u975e\u51e1\uff0c\u4e0d\u6127\u79f0\u4e3a\u4eac\u5e08\u7b2c\u4e00\u9ad8\u624b\u3002\u201d\u957f\u5251\u5f80\u524d\u4e00 \u63a2\u4e5f\u6697\u8fd0\u5185\u529b\uff0c\u89e3\u4e86\u90a3\u7ede\u5200\u4e4b\u52bf\uff0c\u5251\u950b\u4e00\u8f6c\uff0c\u5f53\u7684\u4e00\u58f0\uff0c\u5c06\u7f05\u5200\u524a\u4e86\u4e00\u4e2a\u7f3a\u53e3\uff0c\u5f20\u98ce\u5e9c\u5403\u4e86 \u4e00\u60ca\u5ffd\u5730\u7b11\u9053\uff1a\u201c\u4e0d\u6015\u4f60\u5b9d\u5251\uff01\u201d\u5200\u950b\u4e00\u504f\uff0c\u500f\u5730\u53c8\u642d\u4e0a\u4e86\u5251\u8eab\uff0c\u7528\u529b\u4e00\u65cb\uff0c\u5f20\u4e39\u67ab\u5251\u88ab \u201c\u9ecf\u201d\u4e0a\uff0c\u5c55\u4e0d\u51fa\u5b9d\u5251\u7684\u5a01\u529b\uff0c\u5374\u662f\u7eb5\u58f0\u7b11\u9053\uff1a\u201c\u597d\u5440\uff0c\u54b1\u4eec\u8f83\u91cf\u8f83\u91cf\uff01\u201d\u624b\u8155\u4e00\u6296\uff0c\u5251\u950b \u4e00\u7ffb\uff0c\u53c8\u8131\u4e86\u51fa\u6765\u3002\u5ffd\u542c\u5f97\u5f13\u5f26\u75be\u54cd\uff0c\u767d\u9a6c\u72c2\u5636\uff0c\u8d2f\u4ef2\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u5927\u54e5\u5feb\u8ffd\uff0c\u6bd5\u8001\u8d3c\u8dd1 \u4e86\uff01\u201d\u5f20\u98ce\u5e9c\u5893\u7136\u9192\u8d77\uff0c\u8fd9\u662f\u5f20\u4e39\u67ab\u201c\u56f4\u9b4f\u6551\u8d75\u201d\u4e4b\u7b56\uff0c\u5200\u950b\u5ffd\u8f6c\uff0c\u7eb5\u8eab\u5954\u51fa\uff0c\u5f20\u4e39\u67ab\u633a\u5251 \u6025\u523a\uff0c\u5f20\u98ce\u5e9c\u7a81\u5730\u53cd\u624b\u4e00\u638c\uff0c\u638c\u631f\u52b2\u98ce\uff0c\u8fce\u80f8\u5288\u81f3\u3002\u5f20\u4e39\u67ab\u903c\u5f97\u95ea\u8eab\uff0c\u80f8\u53e3\u7ed9\u638c\u98ce\u6240\u9707\uff0c\u7adf \u662f\u8fa3\u8fa3\u4f5c\u75db\uff0c\u5403\u4e86\u4e00\u60ca\uff0c\u6025\u5fd9\u8fd0\u6c14\u62a4\u8eab\u3002\u53ea\u89c1\u5f20\u98ce\u5e9c\u5df2\u62a2\u4e86\u4e00\u9a91\u5feb\u9a6c\uff0c\u75be\u8ffd\u90a3\u5339\u201c\u7167\u591c\u72ee\u5b50 \u9a6c\u201d\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u5fc3\u4e2d\u7b11\u9053\uff1a\u201c\u6211\u7684\u5b9d\u9a6c\u867d\u7136\u8fde\u4e2d\u4e09\u7bad\uff0c\u8c05\u4f60\u4e5f\u8ffd\u5b83\u4e0d\u4e0a\u3002\u201d\u53ea\u662f\u6bd5\u9053\u51e1\u867d\u7136\u8131\u51fa \u91cd\u56f4\uff0c\u4ed6\u5374\u53c8\u88ab\u56f4\u56f0\uff0c\u90a3\u6a0a\u5fe0\u5df2\u821e\u9524\u6025\u4e0a\uff0c\u4e0e\u4ed6\u4ea4\u624b\u3002\u6a0a\u5fe0\u53cc\u9524\u91cd\u516b\u5341\u65a4\uff0c\u5b9d\u5251\u524a\u5b83\u4e0d\u5f97\uff0c \u66f4\u52a0\u4e0a\u9526\u8863\u536b\u7684\u56f4\u653b\uff0c\u7adf\u662f\u8131\u4e0d\u4e86\u8eab\u3002\u8981\u77e5\u6a0a\u5fe0\u65e2\u80fd\u4e0e\u6f6e\u97f3\u6218\u4e2a\u5e73\u6c34\uff0c\u4e0e\u5f20\u4e39\u67ab\u4ea6\u662f\u4f2f\u4ef2\u4e4b \u95f4\uff0c\u5f20\u4e39\u67ab\u60f3\u9a6c\u4e0a\u7a81\u56f4\u54ea\u91cc\u80fd\u591f\uff01 \u3000\u3000\u4e91\u857e\u7b49\u4eba\uff0c\u672c\u5df2\u8131\u51fa\u91cd\u56f4\uff0c\u5ffd\u547c\u5f97\u540e\u9762\u53eb\u58f0\uff0c\u4e91\u857e\u56de\u7738\u4e00\u77a5\uff0c\u89c1\u5f20\u4e39\u67ab\u9690\u5165\u82e6\u6218\u4e4b\u4e2d\uff0c \u82b3\u5fc3\u4e00\u60ca\uff0c\u8d2f\u4ef2\u9aa4\u7684\u4e00\u7bad\u5c04\u6765\uff0c\u4e91\u857e\u6b63\u5728\u5931\u795e\uff0c\u5b9d\u5251\u62e8\u7bad\u7a0d\u8fdf\uff0c\u7adf\u7ed9\u4ed6\u4e00\u7bad\u5c04\u4e2d\u9a6c\u9888\u3002 \u3000\u3000\u4e91\u857e\u731d\u7136\u5012\u5730\u4e0a\uff0c\u672a\u53ca\u8d77\u7acb\uff0c\u8eab\u540e\u7684\u9526\u8863\u536b\u58eb\u53d1\u4e00\u58f0\u558a\uff0c\u5df2\u662f\u4e00\u62e5\u800c\u524d\uff0c\u5200\u67aa\u5e76\u6233\u3002\u4e91 \u857e\u5355\u638c\u6309\u5730\uff0c\u9661\u5730\u6253\u4e86\u4e00\u5708\u5708\uff0c\u5251\u5149\u63a0\u5904\uff0c\u6709\u5982\u5e73\u5730\u4e0a\u6d8c\u8d77\u4e00\u5708\u94f6\u8679\uff0c\u53ea\u542c\u5f97\u4e00\u7247\u65ad\u91d1\u621b\u7389 \u4e4b\u58f0\uff0c\u6233\u5230\u80f8\u524d\u7684\u51e0\u67c4\u5200\u67aa\u4e00\u9f50\u6298\u65ad\u3002\u4e91\u857e\u4e00\u8dc3\u800c\u8d77\uff0c\u8d2f\u4ef2\u624b\u63d0\u4e09\u8282\u8f6f\u97ad\uff0c\u5982\u98de\u8d76\u5230\uff0c\u624b\u8d77 \u4e00\u97ad\uff0c\u62e6\u8170\u7f20\u8155\u3002\u8d2f\u4ef2\u9886\u6559\u8fc7\u5b9d\u5251\u7684\u5389\u5bb3\uff0c\u5229\u7528\u8f6f\u97ad\u7684\u4f38\u7f29\u81ea\u5982\uff0c\u8fd9\u4e00\u97ad\u626b\u5f97\u6070\u5230\u597d\u5904\u3002\u4e91 \u857e\u6a2a\u5251\u524a\u5b83\u4e0d\u7740\uff0c\u8f6f\u97ad\u5df2\u5982\u4e00\u6761\u6bd2\u87d2\u4f3c\u7684\u5954\u5230\u524d\u5fc3\u3002\u597d\u4e2a\u4e91\u857e\uff0c\u80a9\u5934\u5fae\u52a8\uff0c\u8eab\u5f62\u7565\u77ed\uff0c\u7fe9\u5982 \u98de\u96c1\uff0c\u4ece\u97ad\u68a2\u4e0b\u4e00\u63a0\u800c\u51fa\uff0c\u5237\u7684\u4e00\u5251\u4ecd\u662f\u5f3a\u653b\uff0c\u8d2f\u4ef2\u659c\u7a9c\u4e09\u6b65\uff0c\u624b\u8155\u4e00\u987f\uff0c\u97ad\u68a2\u4e00\u5e26\uff0c\u547c\u7684 \u53c8\u5708\u56de\u6765\u3002\u4e24\u4eba\u6362\u4e86\u6570\u62db\uff0c\u672a\u5206\u9ad8\u4e0b\uff0c\u97ad\u58f0\u5251\u5f71\uff0c\u6253\u5f97\u4e2a\u96be\u89e3\u96be\u5206\u3002\u6309\u8bf4\u4e24\u4eba\u672c\u662f\u65d7\u9f13\u76f8 \u5f53\uff0c\u53ef\u662f\u4e91\u857e\u6c14\u529b\u8f83\u5f31\uff0c\u4e8c\u4e09\u5341\u62db\u4e00\u8fc7\uff0c\u6c14\u5598\u6c57\u6d41\uff0c\u6e10\u611f\u4e0d\u652f\u3002\u8d2f\u4ef2\u54c8\u54c8\u5927\u7b11\uff0c\u653b\u52bf\u9aa4\u76db\uff0c \u5341\u4f59\u540d\u9526\u8863\u536b\u58eb\u4e2d\u7684\u9ad8\u624b\u6563\u5e03\u56db\u5468\uff0c\u5e03\u6210\u5706\u9635\uff0c\u9632\u5907\u4e91\u857e\u9003\u8d70\u3002 \u3000\u3000\u53e6\u4e00\u8fb9\u5f20\u4e39\u67ab\u9677\u5165\u91cd\u56f4\uff0c\u5b9d\u5251\u88ab\u6a0a\u5fe0\u53cc\u9524\u903c\u8feb\uff0c\u8ba8\u4e0d\u4e86\u4fbf\u5b9c\uff0c\u53c8\u8981\u5e94\u4ed8\u5176\u4ed6\u4eba\u7684\u5175\u5203\uff0c \u4e5f\u662f\u9669\u8c61\u73af\u751f\u3002\u9163\u6218\u4e2d\u5ffd\u89c1\u4e91\u857e\u5815\u9a6c\uff0c\u5fc3\u4e2d\u5927\u6025\uff0c\u5893\u7136\u4e00\u4e2a\u8f6c\u8eab\uff0c\u53cd\u624b\u4e00\u5251\uff0c\u654c\u7740\u6a0a\u5fe0\u7684\u53cc \u9524\u5de6\u624b\u4e00\u6293\uff0c\u5c06\u4e00\u540d\u536b\u58eb\u7684\u8863\u9886\u6293\u7740\uff0c\u4e00\u628a\u4e3e\u5c06\u8d77\u6765\u3002\u8fd9\u4e00\u62db\u7528\u5f97\u5b9e\u662f\u9669\u6781\uff0c\u82e5\u7136\u5dee\u4e86\u6beb \u5398\uff0c\u8eab\u4e0a\u6015\u65e9\u88ab\u56f4\u653b\u7684\u536b\u58eb\u6420\u5341\u4e2a\u516b\u4e2a\u900f\u660e\u7a9f\u7abf\uff01\u5f20\u4e39\u67ab\u62ff\u634f\u65f6\u5019\uff0c\u5999\u5230\u6beb\u5dc5\uff0c\u4e00\u51fb\u6210\u529f\uff0c \u80c6\u6c14\u987f\u58ee\u3002\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u6a0a\u5fe0\u6b63\u8d81\u7740\u4ed6\u8f6c\u8eab\u4e4b\u9645\u53cc\u9524\u6a2a\u51fb\u8fc7\u6765\uff0c\u5374\u4e0d\u6599\u4ed6\u5df2\u6293\u8d77\u90a3\u540d\u536b \u58eb\uff0c\u5927\u559d\u4e00\u58f0\uff0c\u56de\u8eab\u4fbf\u6321\uff0c\u6a0a\u5fe0\u53cc\u9524\u6025\u7f29\uff0c\u5f20\u4e39\u67ab\u53f3\u624b\u6325\u5251\uff0c\u5de6\u624b\u5c31\u5c06\u6293\u7740\u7684\u4eba\u8d28\u4f5c\u4e3a\u5175 \u5668\uff0c\u4e00\u9635\u65cb\u98ce\u6025\u821e\uff0c\u6321\u8005\u8f9f\u6613\uff0c\u970e\u5ffd\u4e4b\u95f4\uff0c\u51b2\u51fa\u91cd\u56f4\u3002\u6a0a\u5fe0\u7d27\u8ffd\u4e0d\u820d\uff0c\u5f20\u4e39\u67ab\u4e00\u58f0\u5927\u7b11\uff0c\u559d \u9053\uff1a\u201c\u63a5\u7740\uff01\u201d\u5c06\u90a3\u540d\u4eba\u8d28\u53cd\u81c2\u63b7\u51fa\u3002\u6a0a\u5fe0\u8fd8\u771f\u4e0d\u80fd\u4e0d\u542c\u4ed6\u7684\u547d\u4ee4\uff0c\u903c\u5f97\u629b\u4e86\u53cc\u9524\uff0c\u63a5\u8fc7\u4f19 \u4f34\uff0c\u53ea\u89c1\u5f20\u4e39\u67ab\u5728\u5927\u7b11\u58f0\u4e2d\uff0c\u53c8\u5df2\u95ef\u5165\u4e86\u5835\u622a\u4e91\u857e\u7684\u5706\u9635\u3002 \u3000\u3000\u4e91\u857e\u6b63\u5728\u5403\u7d27\uff0c\u9661\u89c1\u5f20\u4e39\u67ab\u4e00\u5251\u98de\u6765\uff0c\u5893\u7136\u4e00\u9635\u5fc3\u8df3\uff0c\u7f8a\u76ae\u8840\u4e66\u7684\u9634\u5f71\u5728\u5979\u773c\u524d\u4e00\u6643\uff0c \u8fd9\u53ef\u618e\u53ef\u6068\u53ef\u559c\u53ef\u7231\u7684\u201c\u4ec7\u4eba\u201d\u53c8\u6765\u63f4\u6551\u81ea\u5df1\u4e86\uff0c\u8be5\u628a\u4ed6\u5f53\u4f5c\u670b\u53cb\u8fd8\u662f\u8be5\u628a\u4ed6\u5f53\u4f5c\u654c\u4eba\uff1f\u8be5 \u63a5\u53d7\u4ed6\u7684\u6551\u52a9\u8fd8\u662f\u201c\u5b81\u6b7b\u4e0d\u5c48\u201d\uff1f\u82b3\u5fc3\u5fd0\u5fd1\uff0c\u6b63\u81ea\u6253\u4e0d\u5b9a\u4e3b\u610f\uff0c\u8ff7\u832b\u4e2d\u8d2f\u4ef2\u4e00\u97ad\u626b\u4e0b\uff0c\u4e91\u857e \u60ca\u8d77\u4e4b\u65f6\uff0c\u97ad\u5f71\u5df2\u5230\u5934\u4e0a\u3002 \u3000\u3000\u4f46\u89c1\u5251\u5149\u4e00\u95ea\uff0c\u8033\u8fb9\u6709\u4eba\u53eb\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u5feb\u5feb\u51fa\u62db\uff01\u201d\u4e91\u857e\u968f\u624b\u4e00\u5251\uff0c\u53ea\u542c\u5f97\u201c\u5580 \u5693\u201d\u4e24\u58f0\uff0c\u8d2f\u4ef2\u90a3\u4e09\u8282\u8f6f\u97ad\u65ad\u4e3a\u56db\u622a\uff01\u8d2f\u4ef2\u9002\u624d\u4e0e\u5f20\u4e39\u67ab\u6597\u8fc7\u4e00\u767e\u4f59\u62db\uff0c\u867d\u7136\u5904\u5728\u4e0b\u98ce\uff0c\u53ef \u8fd8\u672a\u66fe\u843d\u8d25\uff0c\u6ee1\u5fc3\u4ee5\u4e3a\u5408\u4f17\u536b\u58eb\u4e4b\u529b\uff0c\u5bf9\u4ed8\u4e24\u4eba\uff0c\u4ea6\u662f\u7ef0\u6709\u4f59\u88d5\uff0c\u54ea\u6599\u53cc\u5251\u5408\u74a7\uff0c\u5a01\u529b\u66b4 \u589e\uff0c\u53ea\u662f\u4e00\u62db\u5c31\u97ad\u6298\u4eba\u4f24\uff0c\u614c\u5fd9\u6025\u8d70\u3002\u5f20\u4e39\u67ab\u62d6\u7740\u4e91\u857e\uff0c\u53cc\u5251\u5de6\u53f3\u5e76\u5c55\uff0c\u968f\u610f\u6240\u65bd\uff0c\u65e0\u4e0d\u5999 \u7edd\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u5341\u4f59\u540d\u536b\u58eb\u90fd\u4e2d\u5251\u53d7\u4f24\uff0c\u5012\u5730\u4e0d\u8d77\uff01 \u3000\u3000\u5f20\u4e39\u67ab\u62d6\u7740\u4e91\u857e\uff0c\u4e14\u6218\u4e14\u8d70\uff0c\u6a0a\u5fe0\u624b\u821e\u53cc\u9524\uff0c\u8fce\u9762\u800c\u6765\uff0c\u8d2f\u4ef2\u53eb\u9053\uff1a\u201c\u4e8c\u54e5\uff0c\u5c0f\u5fc3\uff01\u201d \u5f20\u4e39\u67ab\u3001\u4e91\u857e\u53cc\u5251\u9f50\u51fa\uff0c\u500f\u5730\u5408\u6210\u4e00\u4e2a\u5149\u73af\uff0c\u6a0a\u5fe0\u5927\u5403\u4e00\u60ca\uff0c\u65e0\u53ef\u62b5\u654c\uff0c\u6025\u5c06\u53cc\u9524\u4e00\u629b\uff0c\u6eda \u5730\u4e00\u4e2a\u5927\u7ffb\uff0c\u4fa7\u8eab\u6eda\u51fa\u4e00\u4e08\u5f00\u5916\uff0c\u53ea\u89c9\u5934\u9876\u4e00\u7247\u6c81\u51c9\u3002\u9976\u662f\u4ed6\u6eda\u5f97\u5982\u6b64\u4e4b\u5feb\uff0c\u62a4\u5934\u76d4\u4ea6\u88ab\u524a \u6389\uff0c\u8fde\u5934\u53d1\u4e5f\u88ab\u524a\u4e86\u597d\u5927\u4e00\u7247\u3002 \u3000\u3000\u6a0a\u5fe0\u51e0\u66fe\u5403\u8fc7\u5982\u6b64\u5927\u4e8f\uff0c\u7ffb\u8eab\u8dc3\u8d77\uff0c\u52c3\u7136\u5927\u6012\u6325\u624b\u559d\u9053\uff1a\u201c\u7528\u9a6c\u961f\u51b2\uff01\u201d\u6570\u5341\u540d\u9526\u8863\u536b \u58eb\u8de8\u4e0a\u6218\u5200\uff0c\u5206\u6210\u56db\u961f\uff0c\u7eb5\u6a2a\u9a70\u9a8b\uff0c\u9f50\u5411\u5f20\u3001\u4e91\u4e8c\u4eba\u51b2\u6765\u3002\u4ed6\u4e8c\u4eba\u6b66\u827a\u7eb5\u7b97\u518d\u9ad8\uff0c\u4e5f\u96be\u62b5\u654c \u8fd9\u6837\u72c2\u98ce\u66b4\u96e8\u822c\u5954\u6765\u7684\u9a6c\u961f\uff01 \u3000\u3000\u5f20\u4e39\u67ab\u53eb\u9053\uff1a\u201c\u5feb\u5feb\u4e0a\u5c71\uff01\u201d\u4e0e\u4e91\u857e\u65bd\u5c55\u7edd\u9876\u8f7b\u529f\uff0c\u5411\u540e\u5c71\u98de\u5954\u3002\u6bd5\u5bb6\u95e8\u524d\u8ddd\u5c71\u811a\u7ea6\u6709 \u4e00\u91cc\u4e4b\u5730\uff0c\u4e24\u4eba\u5c06\u5230\u5c71\u811a\uff0c\u5df2\u88ab\u5feb\u9a6c\u8ffd\u53ca\u3002\u5f20\u4e39\u67ab\u7a81\u7136\u6293\u8d77\u4e91\u857e\uff0c\u5f80\u5c71\u4e0a\u4e00\u629b\uff0c\u524d\u5934\u90a3\u5339\u5feb \u9a6c\u4eba\u7acb\u6251\u6765\uff0c\u5f20\u4e39\u67ab\u8db3\u5c16\u70b9\u5730\uff0c\u8eab\u8eaf\u7b14\u76f4\u8e7f\u8d77\uff0c\u90a3\u9a6c\u6251\u4e86\u4e2a\u7a7a\u3002\u5c31\u5728\u8fd9\u4e00\u77ac\u4e4b\u95f4\uff0c\u5f20\u4e39\u67ab\u5df2 \u98de\u4e0a\u9a6c\u80cc\uff0c\u5c06\u9a6c\u4e0a\u90a3\u540d\u536b\u58eb\u6a2a\u629b\u51fa\u6570\u4e08\u4e4b\u5916\u3002\u8fd9\u8fd8\u662f\u5f20\u4e39\u67ab\u4e00\u5ff5\u6148\u60b2\uff0c\u8981\u4e0d\u7136\u82e5\u5c06\u4ed6\u63b7\u4e8e\u5730 \u4e0a\uff0c\u6015\u4e0d\u88ab\u9a6c\u961f\u8df5\u6210\u8089\u997c\uff1f\u90a3\u5339\u9a6c\u53bb\u52bf\u6781\u75be\uff0c\u7247\u523b\u5df2\u51b2\u5230\u5c71\u8fb9\uff0c\u5f20\u4e39\u67ab\u5728\u9a6c\u80cc\u4e0a\u4e00\u4e2a\u98de\u8eab\uff0c \u6293\u7740\u5c71\u8fb9\u4e00\u68f5\u5927\u6811\u7684\u6811\u679d\uff0c\u6253\u79cb\u5343\u4f3c\u7684\u5f80\u524d\u4e00\u8361\uff0c\u843d\u4e0b\u4e4b\u65f6\uff0c\u5df2\u5728\u5c71\u5761\uff0c\u53ea\u89c1\u4e91\u857e\u6b63\u5728\u534a\u5c71 \u5f20\u671b\u3002 \u3000\u3000\u5176\u65f6\u5df2\u662f\u66ae\u972d\u542b\u5c71\uff0c\u5929\u8272\u5fae\u6697\uff0c\u5c71\u4e0a\u602a\u77f3\u5d99\u5ccb\uff0c\u9a6c\u961f\u4e0d\u6562\u51b2\u4e0a\uff0c\u53ea\u56f4\u5728\u5c71\u4e0b\u5450\u558a\uff0c\u6a0a\u5fe0 \u4f20\u4e0b\u53f7\u4ee4\uff0c\u5c06\u8c37\u53e3\u5916\u7684\u5fa1\u6797\u519b\u8c03\u4e86\u4e00\u90e8\u5206\u8fdb\u6765\uff0c\u5f3a\u5f13\u52b2\u5f29\uff0c\u5b88\u4f4f\u5c71\u811a\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u770b\u4f60\u80fd \u5728\u5c71\u4e0a\u56f0\u5f97\u591a\u4e45\uff1f\u201d\u5f20\u3001\u4e91\u4e8c\u4eba\u5c71\u4e0a\u8fbd\u671b\uff0c\u4f46\u89c1\u5c71\u4e0b\u56db\u5904\u65cc\u65d7\u62db\u5c55\uff0c\u8fd9\u5ea7\u5c0f\u5c71\u5df2\u5168\u7ed9\u5fa1\u6797\u519b \u5305\u56f4\u4f4f\u4e86\u3002 \u3000\u3000\u5f20\u3001\u4e91\u4e8c\u4eba\u6076\u6597\u4e86\u5927\u534a\u65e5\uff0c\u8fd9\u65f6\u53ea\u89c9\u53c8\u9965\u53c8\u7d2f\uff0c\u6625\u65e5\u9634\u6674\u65e0\u5b9a\uff0c\u65e5\u95f4\u9633\u5149\u666e\u7167\uff0c\u9ec4\u660f\u4e4b \u540e\u5374\u5ffd\u7136\u4e0b\u8d77\u96e8\u6765\u3002\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u54b1\u4eec\u627e\u4e2a\u5730\u65b9\u907f\u96e8\u53bb\uff0c\u6211\u8eab\u4e0a\u8fd8\u5e26\u6709\u5e72\u7cae\u3002\u201d\u4e91 \u857e\u9ed8\u58f0\u4e0d\u8bed\uff0c\u5934\u626d\u8fc7\u4e00\u8fb9\u3002\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u90a3\u8fb9\u6709\u4e2a\u5c71\u6d1e\u3002\u201d\u4e00\u628a\u62d6\u7740\u4e91\u857e\u4fbf\u8dd1\uff0c\u808c\u80a4\u76f8\u63a5\uff0c \u53ea\u89c9\u4e91\u857e\u624b\u5fc3\u51b0\u51b7\uff0c\u6599\u77e5\u5979\u5fc3\u4e2d\u5fc5\u662f\u60f6\u6050\u4e0d\u5b89\u3002 \u3000\u3000\u90a3\u201c\u5c71\u6d1e\u201d\u5176\u5b9e\u53ea\u662f\u4e24\u5757\u5927\u5ca9\u77f3\u5939\u5cd9\u800c\u6210\u7684\u7f1d\u9699\uff0c\u5ca9\u77f3\u4e0a\u6709\u866c\u677e\u76d8\u7ed3\uff0c\u96e8\u70b9\u5374\u4e5f\u98d8\u4e0d\u8fdb \u6765\u3002\u77f3\u7f1d\u4e2d\u6070\u6070\u53ef\u5bb9\u4e24\u4eba\uff0c\u5f20\u4e39\u67ab\u5c06\u4e91\u857e\u62d6\u5165\u5c71\u6d1e\uff0c\u4e24\u4eba\u9762\u9762\u76f8\u5bf9\uff0c\u5fc3\u8df3\u4e4b\u58f0\uff0c\u5404\u81ea\u53ef\u95fb\u3002 \u5f20\u4e39\u67ab\u8f7b\u8f7b\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u54b1\u4eec\u4e24\u5bb6\u7684\u51a4\u4ec7\u771f\u662f\u65e0\u6cd5\u53ef\u89e3\u5417\uff1f\u201d\u66ae\u8272\u9eef\u6de1\uff0c\u66f4\u517c \u662f\u4e0b\u96e8\u7684\u9634\u5929\uff0c\u5f20\u4e39\u67ab\u5fae\u4fa7\u8eab\u8eaf\uff0c\u770b\u4e0d\u89c1\u4e91\u857e\u9762\u4e0a\u7684\u8868\u60c5\uff0c\u4f46\u95fb\u8863\u88f3\u6089\u7d22\uff0c\u5251\u73af\u6296\u52a8\u4e4b\u58f0\uff0c \u77e5\u5979\u6b63\u5728\u624b\u6478\u5251\u67c4\u3002\u5f20\u4e39\u67ab\u53c8\u53f9\u6c14\u9053\uff1a\u201c\u4e0d\u662f\u51a4\u5bb6\u4e0d\u805a\u5934\uff0c\u5c0f\u5144\u5f1f\u4f60\u628a\u6211\u6740\u4e86\u5427\uff0c\u6b7b\u5728\u4f60\u7684 \u624b\u4e0a\uff0c\u6211\u6b7b\u800c\u65e0\u6028\uff01\u201d \u3000\u3000\u84e6\u5730\u4e00\u58f0\u96f7\u54cd\uff0c\u7535\u5149\u4e00\u95ea\uff0c\u7167\u89c1\u4e91\u857e\u60e8\u767d\u7684\u9762\u8272\uff0c\u4e5f\u7167\u89c1\u5979\u773c\u89d2\u7684\u4e24\u9897\u6cea\u73e0\u3002\u4e91\u857e\u501a\u7740 \u5ca9\u77f3\uff0c\u624b\u62c8\u8863\u5e26\uff0c\u5b9d\u5251\u60ac\u5728\u8170\u95f4\u9732\u51fa\u4e86\u77ed\u77ed\u7684\u534a\u622a\uff0c\u60f3\u662f\u5979\u8f7b\u8f7b\u62bd\u52a8\uff0c\u5374\u53c8\u7acb\u5373\u628a\u624b\u79fb\u5f00\u3002 \u7535\u5149\u4e00\u95ea\u5373\u706d\uff0c\u77f3\u6d1e\u8fc5\u53c8\u5f52\u4e8e\u9ed1\u6697\u3002 \u3000\u3000\u9ed1\u6697\u4e2d\u4f46\u95fb\u4e91\u857e\u5598\u606f\u4e4b\u58f0\uff0c\u826f\u4e45\u826f\u4e45\uff0c\u4ecd\u4e0d\u89c1\u5979\u8bf4\u8bdd\u3002\u5f20\u4e39\u67ab\u53d6\u51fa\u5e72\u7cae\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u5f1f \u5144\uff0c\u4f60\u5403\u70b9\u4e1c\u897f\u3002\u201d\u4e91\u857e\u8eab\u501a\u77f3\u58c1\uff0c\u52a8\u4e5f\u4e0d\u52a8\u3002\u5f20\u4e39\u67ab\u751a\u662f\u60b2\u75db\uff0c\u5374\u6545\u610f\u626e\u4e86\u4e2a\u9b3c\u8138\uff0c\u563b\u563b \u7b11\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u8fd9\u6b21\u6211\u4e0d\u8bf4\u4f60\u98df\u767d\u98df\u5566\uff0c\u5403\u4e00\u70b9\u5427\uff01\u201d\u5f20\u4e39\u67ab\u6545\u610f\u63d0\u8d77\u521d\u89c1\u4e4b\u65f6\u7684\u7b11\u8bdd\uff0c \u5b9e\u662f\u60f3\u9017\u5979\u8bf4\u7b11\u3002\u5ffd\u5730\u201c\u556a\u201d\u7684\u4e00\u58f0\uff0c\u4e91\u857e\u5c06\u4ed6\u9012\u8fc7\u6765\u7684\u5e72\u7cae\u62cd\u843d\u5730\u4e0a\uff0c\u5f20\u4e39\u67ab\u82e6\u7b11\u4e00\u58f0\uff0c \u5c06\u5e72\u7cae\u6361\u8d77\uff0c\u968f\u624b\u6401\u5728\u4e00\u74e3\u51f8\u51fa\u7684\u77f3\u74e3\u4e0a\u3002 \u3000\u3000\u4e91\u857e\u4ea6\u662f\u6ee1\u8179\u8f9b\u9178\uff0c\u6b32\u54ed\u65e0\u6cea\uff0c\u9ed1\u6697\u4e2d\u53ea\u542c\u5f97\u5f20\u4e39\u67ab\u53f9\u4e86\u53e3\u6c14\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u62a5\u4ec7\uff0c\u62a5 \u4ec7\uff0c\u51a4\u51a4\u76f8\u62a5\uff0c\u7a76\u7adf\u4f55\u65f6\u4e86\uff1f\u6211\u7684\u7956\u5148\u4e0e\u6731\u5143\u748b\u4e89\u593a\u6c5f\u5c71\uff0c\u4ea6\u662f\u7559\u4e0b\u9057\u4e66\uff0c\u8981\u540e\u4ee3\u5b50\u5b59\u66ff\u4ed6 \u62a5\u4ec7\uff0c\u6211\u5bb6\u7684\u62a5\u4ec7\uff0c\u53ef\u4e0d\u53ea\u662f\u8981\u540e\u4eba\u51ed\u8840\u6c14\u4e4b\u52c7\u53bb\u523a\u6740\u654c\u4eba\uff0c\u800c\u662f\u8981\u91cd\u593a\u5927\u660e\u5929\u5b50\u7684\u6c5f \u5c71\uff01\u201d \u3000\u3000\u4e91\u857e\u6253\u4e86\u4e2a\u5bd2\u98a4\u5fc3\u9053\uff1a\u201c\u8fd9\u6837\u7684\u62a5\u4ec7\u53ef\u771f\u662f\u53e4\u5f80\u4eca\u6765\u6700\u60e8\u9177\u7684\u62a5\u4ec7\uff0c\u82e5\u7136\u5f20\u5bb6\u62a5\u5f97\u6b64 \u4ec7\uff0c\u5c82\u975e\u8981\u6740\u4eba\u76c8\u57ce\u6d41\u8840\u904d\u5730\uff1f\u201d\u53c8\u60f3\u9053\uff1a\u201c\u82e5\u7136\u5f20\u4e39\u67ab\u662f\u4e3a\u4e86\u62a5\u4ec7\uff0c\u800c\u52fe\u7ed3\u74e6\u523a\u80e1\u5175\u5165 \u5bc7\uff0c\u62a2\u593a\u6c5f\u5c71\uff0c\u90a3\u4ed6\u53ef\u5c31\u662f\u4e07\u53e4\u7684\u7f6a\u4eba\uff0c\u6211\u4ea6\u5bb9\u4ed6\u4e0d\u5f97\uff01\u201d\u601d\u6f6e\u8d77\u4f0f\u4e0d\u5b9a\uff0c\u624b\u6307\u53c8\u6293\u7d27\u4e86\u9752 \u51a5\u5b9d\u5251\u7684\u5251\u67c4\u3002 \u3000\u3000\u53ea\u542c\u5f97\u5f20\u4e39\u67ab\u7eed\u9053\uff1a\u201c\u6211\u7684\u7956\u7236\u9003\u5230\u74e6\u523a\uff0c\u90a3\u65f6\u8499\u53e4\u52bf\u529b\u8870\u5fae\uff0c\u5185\u90e8\u5206\u88c2\uff0c\u660e\u5175\u65f6\u65f6\u95ef \u8fdb\u8499\u53e4\u8349\u539f\u52ab\u63a0\uff0c\u660e\u671d\u53c8\u8981\u4ed6\u4eec\u5e74\u5e74\u8fdb\u8d21\u5c81\u5c81\u6765\u671d\uff0c\u4ed6\u4eec\u4ea6\u662f\u6124\u6068\u5f97\u5f88\uff0c\u6240\u4ee5\u4ed6\u4eec\u4e5f\u8981\u62a5 \u4ec7\u3002\u54b3\uff0c\u4eba\u4e0e\u4eba\uff0c\u56fd\u4e0e\u56fd\uff0c\u90fd\u6709\u90a3\u4e48\u591a\u7684\u51a4\u4ec7\uff0c\u6211\u771f\u4e0d\u77e5\u9053\u4e3a\u4ec0\u4e48\u4ed6\u4eec\u4e0d\u80fd\u5e73\u7b49\u76f8\u5f85\uff0c\u548c\u5e73 \u76f8\u5904\uff1f\u201d \u3000\u3000\u4e91\u857e\u5fc3\u4e2d\u4e00\u52a8\uff0c\u5f20\u4e39\u67ab\u7eed\u9053\uff1a\u201c\u5148\u7956\u548c\u74e6\u523a\u5148\u738b\u90fd\u60f3\u62a5\u4ec7\u5411\u5927\u660e\u62a5\u4ec7\uff0c\u8fd9\u4e48\u6837\u4ed6\u5c31\u5728\u74e6 \u523a\u4e3a\u5b98\u5566\u3002\u74e6\u523a\u4e00\u5929\u5929\u5f3a\u76db\u8d77\u6765\u5148\u7956\u7684\u5b98\u4e5f\u8d8a\u505a\u8d8a\u5927\uff0c\u5230\u4e86\u6211\u7684\u7236\u4eb2\uff0c\u4e0d\u4f46\u627f\u88ad\u4e86\u5148\u7956\u7684\u5b98 \u4f4d\u540e\u6765\u66f4\u5347\u4efb\u4e86\u53f3\u4e1e\u76f8\u3002\u201d \u3000\u3000\u201c\u6211\u7236\u4eb2\u8bb0\u7740\u5148\u4ee3\u4e4b\u4ec7\uff0c\u5bf9\u6731\u5143\u748b\u7684\u5b50\u5b59\u4ee5\u53ca\u5fe0\u4e8e\u660e\u671d\u7684\u4eba\u90fd\u6068\u4e4b\u5165\u9aa8\u3002\u4e09\u5341\u5e74\u524d\u4f60\u7684 \u7237\u7237\u51fa\u4f7f\u74e6\u523a\uff0c\u53e3\u53e3\u58f0\u58f0\u4ee5\u660e\u671d\u7684\u5927\u5fe0\u81e3\u81ea\u5c45\uff0c\u6211\u7239\u4e00\u6c14\u4e4b\u4e0b\uff0c\u5c31\u8feb\u4ed6\u5230\u51b0\u5929\u96ea\u5730\u91cc\u53bb\u7267\u9a6c \u4e8c\u5341\u5e74\uff01\u201d \u3000\u3000\u4e91\u857e\u7259\u9f7f\u54ac\u5f97\u683c\u683c\u4f5c\u54cd\uff0c\u5ffd\u5730\u8f6c\u5ff5\u4e00\u60f3\uff1a\u201c\u6211\u7237\u7237\u4e3a\u4e86\u8eab\u53d7\u7267\u9a6c\u4e8c\u5341\u5e74\u4e4b\u82e6\uff0c\u5c31\u8981\u6740\u5c3d \u5f20\u5bb6\u6240\u6709\u7684\u4eba\uff0c\u90a3\u4e48\u660e\u671d\u62a2\u53bb\u4e86\u4ed6\u5148\u4eba\u7684\u6c5f\u5c71\uff0c\u4e5f\u5c31\u96be\u602a\u4ed6\u4eec\u5982\u6b64\u6124\u6068\uff0c\u7d2f\u53ca\u6211\u7684\u7237\u7237\u3002\u53ef \u662f\u8fd9\u79cd\u79cd\u662f\u975e\u6069\u6028\uff0c\u6211\u4eec\u540e\u8f88\u53ef\u7ba1\u4e0d\u7740\uff0c\u7237\u7237\u8981\u6211\u62a5\u7684\u4ec7\u6211\u53c8\u600e\u80fd\u7f6e\u4e4b\u4e0d\u7406\uff1f\u201d \u3000\u3000\u4e91\u857e\u6293\u7d27\u5251\u67c4\uff0c\u5fc3\u4e71\u5982\u9ebb\uff0c\u53ea\u542c\u5f97\u5f20\u4e39\u67ab\u53c8\u9053\uff1a\u201c\u4f60\u7237\u7237\u5728\u51b0\u5929\u96ea\u91cc\u7267\u9a6c\u4e8c\u5341\u5e74\uff0c\u59cb\u7ec8 \u4e0d\u5c48\u3002\u540e\u6765\u6211\u7684\u7236\u4eb2\u4e5f\u6709\u70b9\u4f69\u670d\u4ed6\u5566\uff0c\u6211\u7236\u4eb2\u4e5f\u66fe\u5bf9\u6211\u8bf4\u8d77\u4f60\u7237\u7237\u7684\u6545\u4e8b\uff0c\u8bf4\u662f\u5f53\u5e74\u4f60\u7237\u7237 \u79c1\u9003\u56de\u56fd\u4e4b\u65f6\u4ed6\u5b9e\u662f\u4e8b\u524d\u77e5\u9053\uff0c\u6545\u610f\u4e0d\u6d3e\u5175\u963b\u62e6\u8ba9\u4ed6\u4eec\u9003\u8dd1\u7684\u3002\u6211\u7239\u8fd8\u8bf4\uff0c\u5f53\u65f6\u4ed6\u66fe\u9063\u6fb9\u53f0 \u5c06\u519b\u9001\u7ed9\u4f60\u7237\u7237\u4e09\u9053\u9526\u56ca\uff0c\u53ef\u4ee5\u6551\u4ed6\u6027\u547d\uff0c\u53ef\u60dc\u4f60\u7237\u7237\u4e0d\u4fe1\uff0c\u8f9c\u8d1f\u4e86\u4ed6\u4e00\u7247\u82e6\u5fc3\u3002\u201d\u4e91\u857e\u5c06 \u4fe1\u5c06\u7591\uff0c\u4ecd\u7136\u4e0d\u4f5c\u4e00\u8bed\uff0c\u624b\u6307\u4ecd\u7136\u6293\u7d27\u5251\u67c4\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u6211\u7236\u4eb2\u5bf9\u4f60\u7237\u7237\u786e\u662f\u592a\u8fc7\uff0c\u540e\u4e3a\u7684\u597d\u610f\u4e5f\u5c31\u96be\u602a\u4f60\u7237\u7237\u4e0d\u80af\u76f8 \u4fe1\uff0c\u5148\u4eba\u6b20\u503a\u540e\u4eba\u8fd8\uff0c\u5440\uff0c\u6211\u4e5f\u96be\u602a\u4f60\u8fd9\u6837\u6068\u6211\uff01\u201d \u3000\u3000\u201c\u74e6\u523a\u4e00\u5929\u5929\u5f3a\u5927\uff0c\u660e\u671d\u4e0d\u6562\u6b3a\u8d1f\u5b83\uff0c\u53cd\u4e86\u8fc7\u6765\uff0c\u53cd\u800c\u88ab\u5b83\u6b3a\u8d1f\u4e86\u3002\u5341\u5e74\u4e4b\u524d\uff0c\u6211\u7684\u5e08 \u7236\u5230\u74e6\u523a\u6765\uff0c\u542c\u8bf4\u4ed6\u672c\u6765\u662f\u8981\u66ff\u4f60\u7237\u7237\u62a5\u4ec7\uff0c\u540e\u6765\u5374\u505a\u8d77\u6211\u7684\u5e08\u7236\u5566\u3002\u4ed6\u6559\u6211\u8bb0\u5f97\u81ea\u5df1\u662f\u4e2d \u56fd\u4eba\uff0c\u5343\u4e07\u4e0d\u80fd\u4e0e\u4e2d\u56fd\u4e3a\u654c\uff01\u5e08\u7236\u6765\u540e\uff0c\u6211\u7239\u7239\u7684\u6027\u60c5\u4e5f\u597d\u50cf\u6709\u4e9b\u6539\u53d8\u4e86\uff0c\u6211\u5e38\u5e38\u89c1\u4ed6\u6df1\u591c \u6376\u80f8\u4e2d\u5bb5\u7ed5\u5ba4\uff0c\u81ea\u8a00\u81ea\u8bed\u5730\u8bf4\u9053\uff1a\u2018\u62a5\u4ec7\uff0c\u62a5\u4ec7\uff0c\u8be5\u4e0d\u8be5\u8fd9\u6837\u62a5\u4ec7\uff1f\u2019\u795e\u60c5\u5f88\u662f\u53ef\u6015\u3002\u6211\u6709 \u4e00\u4e24\u6b21\u4e0a\u53bb\u529d\u4ed6\uff0c\u4ed6\u5374\u53c8\u77aa\u7740\u773c\u775b\u8bf4\uff1a\u2018\u5b69\u5b50\u554a\uff0c\u4f60\u53ef\u5f97\u8bb0\u5f97\u5148\u4eba\u7684\u5982\u5c71\u4ec7\u6068\uff01\u2019\u201d \u3000\u3000\u201c\u6211\u6b64\u6b21\u5b9e\u662f\u7792\u7740\u7236\u4eb2\uff0c\u79c1\u9003\u56de\u6765\u7684\uff0c\u4e8b\u60c5\u53ea\u6709\u6211\u5e08\u7236\u4e00\u4eba\u77e5\u9053\u3002\u4e2d\u539f\u6b66\u6797\u7684\u79cd\u79cd\u60c5 \u5f62\uff0c\u4e5f\u662f\u6211\u5e08\u7236\u5bf9\u6211\u8bf4\u7684\u3002\u6211\u662f\u4e2d\u56fd\u4eba\uff0c\u6211\u7edd\u4e0d\u4f1a\u52a9\u74e6\u523a\u5165\u4fb5\uff0c\u53ef\u662f\u6211\u4e5f\u8981\u62a5\u4ec7\u2026\u2026\u201d\u4e91\u857e \u51b2\u53e3\u8bf4\u9053\uff1a\u201c\u600e\u6837\u62a5\u4ec7\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u6211\u5165\u5173\u4e4b\u540e\uff0c\u7ec6\u5bdf\u60c5\u5f62\uff0c\u671d\u671d\u5176\u5b9e\u5df2\u662f\u8150\u8d25\u5230\u6781\uff0c \u8981\u62a5\u4ec7\u6211\u770b\u4e5f\u4e0d\u5f88\u96be\uff0c\u6211\u82e5\u627e\u5230\u5730\u56fe\u5b9d\u85cf\uff0c\u91cd\u91d1\u7ed3\u58eb\uff0c\u63ed\u7aff\u4e3a\u65d7\uff0c\u5927\u660e\u5929\u4e0b\u4e0d\u96be\u593a\u53d6\uff01\u201d\u4e91 \u857e\u5403\u4e86\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u4f60\u60f3\u79f0\u738b\u79f0\u5e1d\uff1f\u201d\u5f20\u4e39\u67ab\u7b11\u9053\uff1a\u201c\u7687\u5e1d\u4e5f\u662f\u5e38\u4eba\u505a\uff0c\u4e00\u5bb6\u4e00\u59d3\u7684\u6c5f\u5c71\u5c82 \u80fd\u7ef4\u6301\u767e\u4e16\uff1f\u4e0d\u8fc7\u6211\u62a2\u5927\u660e\u7684\u6c5f\u5c71\uff0c\u4e5f\u4e0d\u53ea\u662f\u5c31\u4e3a\u4e86\u505a\u7687\u5e1d\u2026\u2026\u201d\u4e91\u857e\u9053\uff1a\u201c\u5c31\u4e3a\u4e86\u62a5\u4ec7 \u5417\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u4e5f\u4e0d\u53ea\u662f\u5c31\u4e3a\u62a5\u4ec7\uff0c\u82e5\u7136\u5929\u4e0b\u4e07\u90a6\uff0c\u6c38\u4e0d\u518d\u52a8\u5e72\u6208\uff0c\u90a3\u53ef\u591a\u597d\uff01\u201d\u987f\u4e86 \u4e00\u987f\uff0c\u5ffd\u7136\u4e00\u9635\u72c2\u7b11\u541f\u9053\uff1a\u201c\u4eba\u5bff\u6709\u51e0\u4f55\uff1f\u6cb3\u6e05\u5b89\u53ef\u4fdf\uff1f\u7109\u5f97\u5723\u4eba\u51fa\uff0c\u5927\u540c\u4f20\u4e07\u4e16\uff01\u54c8\u54c8\uff0c \u82e5\u80fd\u916c\u7d20\u613f\uff0c\u4f55\u5fc5\u4e3a\u5929\u5b50\uff1f\u201d\u4e91\u857e\u5728\u9ed1\u6697\u4e2d\u867d\u662f\u770b\u4e0d\u6e05\u4ed6\u7684\u9762\u5bb9\uff0c\u4e5f\u53ef\u60f3\u89c1\u4ed6\u7684\u72c2\u6001\uff0c\u5fcd\u4e0d \u4f4f\u63a5\u53e3\u8bf4\u9053\uff1a\u201c\u505a\u4e0d\u505a\u7687\u5e1d\uff0c\u90a3\u5012\u6ca1\u6709\u4ec0\u4e48\u5e0c\u7f55\u3002\u53ea\u662f\u4f60\u82e5\u60f3\u62a2\u5927\u660e\u4e5d\u4e07\u91cc\u7684\u6c5f\u5c71\uff0c\u4e0d\u7ba1\u4f60 \u613f\u4e0d\u613f\u610f\uff0c\u53ea\u6050\u4e5f\u8981\u5f04\u81f3\u6740\u4eba\u76c8\u57ce\uff0c\u6d41\u8840\u904d\u91ce\uff0c\u4f55\u51b5\u73b0\u5728\u8499\u53e4\u53c8\u8981\u5165\u4fb5\u3002\u4f60\u82e5\u4e0e\u5927\u660e\u5929\u5b50\u4e3a \u4ec7\uff0c\u5c82\u975e\u53cd\u52a9\u4e86\u74e6\u523a\u4e00\u81c2\uff1f\u201d\u5f20\u4e39\u67ab\u6014\u4e86\u4e00\u6014\uff0c\u5ffd\u5730\u67d4\u58f0\u8bf4\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u4f60\u7684\u8bdd\u4e5f\u6709\u9053 \u7406\u3002\u5c0f\u5144\u5f1f\uff0c\u5927\u54e5\u542c\u4f60\u7684\u8bdd\uff0c\u4f60\u8bf4\u4e0d\u8ba9\u6211\u505a\u7687\u5e1d\u6211\u5c31\u4e0d\u505a\u7687\u5e1d\u3002\u5c0f\u5144\u5f1f\uff0c\u4f60\u8bf4\u5427\uff0c\u6211\u5c31\u542c\u4f60 \u7684\u8bdd\u3002\u201d\u58f0\u8c03\u6e29\u67d4\uff0c\u8a00\u8bed\u751c\u871c\uff0c\u4e91\u857e\u9762\u4e0a\u4e00\u70ed\uff0c\u8eab\u5b50\u5f80\u91cc\u4e00\u7f29\uff0c\u624b\u638c\u5f80\u5916\u6012\u9053\uff1a\u201c\u8c01\u8981\u4f60\u542c \u6211\u7684\u8bdd\uff01\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u600e\u4e48\u5566\uff1f\u53c8\u751f\u6c14\u4e86\uff1f\u201d\u4e91\u857e\u518d\u4e5f\u4e0d\u8bf4\u4e00\u53e5\u8bdd\uff0c\u5f20\u4e39\u67ab\u53f9\u4e86\u53e3\u6c14\uff0c\u624b \u89e6\u5ca9\u77f3\uff0c\u6401\u5728\u77f3\u74e3\u4e0a\u7684\u5e72\u7cae\u5df2\u5168\u88ab\u4e91\u857e\u5403\u5149\u4e86\u3002\u539f\u6765\u9002\u624d\u4e91\u857e\u542c\u5f20\u4e39\u67ab\u8bf4\u8bdd\uff0c\u542c\u5f97\u51fa\u4e86\u795e\u4e0d \u77e5\u4e0d\u89c9\u5730\u62ff\u8d77\u5e72\u7cae\u6765\u5403\uff0c\u5230\u7701\u8d77\u201c\u4e0d\u8be5\u201d\u5403\u65f6\uff0c\u5df2\u662f\u5403\u5230\u6700\u540e\u7684\u4e00\u5757\u4e86\u3002\u5f20\u4e39\u67ab\u6697\u6697\u5077\u7b11\uff0c \u9ed1\u6697\u4e2d\u4f46\u89c1\u4e91\u857e\u4e00\u53cc\u773c\u775b\u6709\u5982\u9ed1\u591c\u660e\u661f\uff0c\u95ea\u95ea\u53d1\u4eae\u3002\u5f20\u4e39\u67ab\u67d4\u58f0\u8bf4\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u4f60\u8be5\u7761 \u5566\uff01\u201d\u7ed9\u5979\u4f4e\u5531\u50ac\u7720\u5c0f\u66f2\uff0c\u4e91\u857e\u672c\u89c9\u75b2\u5026\uff0c\u5403\u9971\u4e4b\u540e\uff0c\u542c\u4ed6\u67d4\u58f0\u50ac\u7720\uff0c\u7761\u610f\u987f\u6d53\uff0c\u773c\u76ae\u6162\u6162 \u5730\u9616\u4e86\u4e0b\u6765\u3002\u5f20\u4e39\u67ab\u63d0\u5251\u5750\u5728\u6d1e\u53e3\u66ff\u5979\u5b88\u536b\uff0c\u5176\u65f6\u9aa4\u96e8\u5df2\u8fc7\uff0c\u4f46\u9ed1\u591c\u4e4b\u4e2d\uff0c\u5b98\u519b\u4e5f\u4e0d\u6562\u95ef\u4e0a \u5c71\u6765\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u4ea6\u662f\u75b2\u5026\u4e4b\u6781\uff0c\u4f46\u4e3a\u4e86\u536b\u62a4\u4e91\u857e\uff0c\u6491\u7740\u773c\u76ae\u5374\u662f\u4e0d\u6562\u7761\u89c9\uff0c\u5ffd\u7136\u542c\u5f97\u4e91\u857e\u53eb\u9053\uff1a \u201c\u5927\u54e5\uff0c\u5927\u54e5\u2026\u2026\u7237\u7237\u2026\u2026\u7237\u7237\u2026\u2026\u201d\u5f20\u4e39\u67ab\u5e94\u4e86\u4e00\u58f0\uff0c\u56de\u5934\u4e00\u671b\uff0c\u4e91\u857e\u53c8\u4e0d\u53eb\u4e86\uff0c\u542c\u5979\u9f3b \u606f\u5747\u5300\uff0c\u539f\u6765\u662f\u8bf4\u68a6\u8bdd\u3002\u5f20\u4e39\u67ab\u8131\u4e0b\u5916\u8863\uff0c\u8f7b\u8f7b\u5730\u62ab\u5728\u5979\u7684\u8eab\u4e0a\u4ecd\u7136\u5750\u5728\u6d1e\u53e3\u63d0\u5251\u5b88\u536b\u3002 \u3000\u3000\u4e91\u857e\u6b63\u5728\u68a6\u4e2d\uff0c\u68a6\u4e2d\u89c1\u5f20\u4e39\u67ab\u4ef0\u5929\u957f\u7b11\uff0c\u5ffd\u7136\u53c8\u624b\u629a\u753b\u90d1\u75db\u54ed\u9ad8\u6b4c\uff0c\u4e91\u857e\u89c9\u4ed6\u751a\u662f\u53ef \u601c\uff0c\u4e0a\u524d\u6273\u4ed6\u80a9\u8180\uff0c\u5ffd\u5730\u7237\u7237\u6301\u7740\u90a3\u6839\u9970\u6709\u65c4\u6bdb\u7684\u7af9\u6756\uff0c\u98a4\u5dcd\u5dcd\u5730\u8d70\u6765\uff0c\u63d2\u5165\u4e24\u4eba\u4e2d\u95f4\uff0c\u4e3e \u8d77\u7af9\u6756\u4fbf\u6253\uff0c\u4e91\u857e\u9053\uff1a\u201c\u5927\u54e5\u6551\u6211\uff01\u201d\u7237\u7237\u624b\u91cc\u7684\u201c\u4f7f\u8282\u201d\u5ffd\u7136\u53c8\u53d8\u4e86\u7f8a\u76ae\u8840\u4e66\uff0c\u7237\u7237\u6301\u90a3 \u5757\u7f8a\u76ae\u5f80\u5979\u5934\u9876\u4e00\u7f69\uff0c\u9a82\u9053\uff1a\u201c\u8c01\u662f\u4f60\u7684\u5927\u54e5\uff0c\u4f60\u5feb\u5feb\u628a\u4ed6\u6740\u6389\uff01\u201d\u8840\u8165\u5473\u9635\u9635\u6251\u6765\uff0c\u4e91\u857e \u975e\u5e38\u96be\u53d7\uff0c\u558a\u53c8\u558a\u4e0d\u51fa\u6765\uff0c\u4e00\u60ca\u800c\u9192\u3002 \u3000\u3000\u4f46\u89c1\u6d1e\u53e3\u66d9\u5149\u900f\u5165\uff0c\u4e91\u857e\u5b9a\u4e86\u5b9a\u795e\uff0c\u53d1\u89c9\u81ea\u5df1\u8eab\u4e0a\u62ab\u7740\u5f20\u4e39\u67ab\u7684\u5916\u8863\uff0c\u9762\u4e0a\u53d1\u70e7\uff0c\u5fc3\u5934 \u53d1\u9178\uff0c\u53d6\u4e0b\u5916\u8863\uff0c\u8f7b\u8f7b\u8d70\u51fa\uff0c\u53ea\u89c1\u5f20\u4e39\u67ab\u5750\u5728\u77f3\u4e0a\uff0c\u5251\u5c16\u62b5\u5730\uff0c\u5934\u5411\u4e0b\u5782\u3002\u539f\u6765\u5f20\u4e39\u67ab\u4e00\u591c \u672a\u7761\uff0c\u5b9e\u5728\u71ac\u4e0d\u4f4f\u4e86\uff0c\u6240\u4ee5\u4e34\u5230\u5929\u4eae\u4e4b\u9645\uff0c\u6253\u4e86\u4e2a\u76f9\u3002 \u3000\u3000\u7f8a\u76ae\u8840\u4e66\u7684\u9634\u5f71\u53c8\u5728\u5fc3\u5934\u6269\u5927\u8d77\u6765\uff0c\u4e91\u857e\u624b\u629a\u5251\u67c4\u5fc3\u9053\uff1a\u201c\u82e5\u7136\u6b64\u9645\u523a\u4ed6\u4e00\u5251\uff0c\u5012\u662f\u7edd \u597d\u65f6\u673a\u3002\u554a\uff0c\u554a\uff01\u6211\u600e\u80fd\u5982\u6b64\u60f3\u6cd5\uff0c\u7237\u7237\u554a\uff0c\u7237\u7237\u554a\uff01\u4e0d\u8981\u903c\u6211\uff0c\u4e0d\u8981\u903c\u6211\u554a\uff01\u201d\u6726\u80e7\u4e2d\u4f3c \u89c1\u7237\u7237\u6301\u7740\u4f7f\u8282\u8d70\u6765\uff0c\u5c31\u50cf\u68a6\u4e2d\u90a3\u6837\u60c5\u666f\uff0c\u7528\u4e25\u5389\u7684\u76ee\u5149\u77aa\u7740\u81ea\u5df1\uff0c\u96be\u9053\u662f\u8fd8\u5728\u68a6\u4e2d\uff1f\u4e91\u857e \u54ac\u54ac\u6307\u5934\uff0c\u611f\u89c9\u75db\u82e6\uff0c\u8fd9\u4e0d\u662f\u68a6\uff0c\u53ef\u662f\u5979\u53c8\u591a\u613f\u6c38\u5728\u68a6\u4e2d\uff0c\u6c38\u4e0d\u9192\u6765\u3002\u68a6\u4e2d\u867d\u662f\u96be\u53d7\uff0c\u4e5f\u6bd4 \u4e0d\u4e0a\u9192\u6765\u9762\u5bf9\u201c\u4ec7\u4eba\u201d\u4e4b\u65f6\u7684\u96be\u53d7\u554a\uff01\u201c\u6211\u653e\u5f03\u4e86\u8fd9\u4e2a\u7edd\u597d\u65f6\u673a\uff0c\u4e0d\u6740\u5f20\u5bb6\u7684\u4eba\uff0c\u7237\u7237\u5728\u4e5d \u6cc9\u4e4b\u4e0b\u4f1a\u602a\u6211\u4e48\uff1f\u201d\u4e91\u857e\u624b\u629a\u5251\u67c4\uff0c\u8fc8\u524d\u4e24\u6b65\uff0c\u5ffd\u7136\u53c8\u628a\u624b\u6307\u9001\u5165\u53e3\u4e2d\u4e00\u54ac\uff0c\u5267\u75db\u4e2d\u987f\u65f6\u6e05 \u9192\uff0c\u7237\u7237\u7684\u5f71\u5b50\u6d88\u5931\u4e86\uff0c\u5979\u628a\u5251\u4e00\u4e0b\u6309\u5165\u9798\u4e2d\uff0c\u5c06\u957f\u8863\u8f7b\u8f7b\u5730\u66ff\u5f20\u4e39\u67ab\u62ab\u4e0a\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u52a8\u4e86\u4e00\u4e0b\uff0c\u84e6\u7136\u4f38\u4e86\u4e2a\u61d2\u8170\uff0c\u7b11\u7740\u7ad9\u8d77\u6765\u9053\uff1a\u201c\u55ef\u5c0f\u5144\u5f1f\uff0c\u4f60\u8fd9\u6837\u65e9\u5c31\u9192\u6765\u4e86\uff01 \u4e3a\u4ec0\u4e48\u4e0d\u591a\u7761\u4e00\u4f1a\u513f\uff1f\u201d\u4e91\u857e\u54ac\u7740\u5634\u5507\uff0c\u9762\u8272\u82cd\u767d\uff0c\u5f20\u4e39\u67ab\u51dd\u671b\u7740\u5979\uff0c\u76ee\u5149\u5145\u6ee1\u67d4\u60c5\uff0c\u53c8\u5e26 \u7740\u65e0\u9650\u601c\u60dc\uff0c\u4e91\u857e\u6fc0\u52a8\u5f97\u51e0\u4e4e\u54ed\u4e86\u51fa\u6765\uff0c\u8f6c\u8eab\u4e0d\u6562\u518d\u770b\u5f20\u4e39\u67ab\u3002\u5f20\u4e39\u67ab\u53f9\u4e86\u53e3\u6c14\uff0c\u5f80\u5c71\u4e0b\u770b \u65f6\uff0c\u53ea\u89c1\u6570\u5341\u5916\u9526\u8863\u536b\u58eb\u6742\u7740\u5fa1\u6797\u519b\uff0c\u4e09\u4e94\u6210\u7fa4\u6b63\u8d81\u7740\u6e05\u6668\u6c14\u723d\uff0c\u4e0a\u5c71\u641c\u7d22\u3002 \u3000\u3000\u51e0\u5341\u540d\u536b\u58eb\u5bb9\u6613\u5bf9\u4ed8\uff0c\u53ef\u662f\u5c71\u4e0b\u65cc\u65d7\u62db\u5c55\u600e\u80fd\u51b2\u51fa\u91cd\u56f4\uff1f\u5f20\u4e39\u67ab\u8e0c\u8e87\u65e0\u8ba1\uff0c\u53ea\u89c1\u654c\u4eba\u5206 \u5934\u4e0a\u5c71\uff0c\u5df2\u5230\u5c71\u8170\uff0c\u5f20\u4e39\u67ab\u4e00\u628a\u62d6\u7740\u4e91\u857e\uff0c\u8eb2\u5230\u4e00\u5757\u5927\u77f3\u4e4b\u540e\u3002 \u3000\u3000\u5b98\u519b\u8d8a\u6765\u8d8a\u8fd1\uff0c\u5ffd\u542c\u5f97\u5f20\u98ce\u5e9c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u51fa\u6765\uff0c\u51fa\u6765\uff0c\u6211\u5df2\u77a7\u89c1\u4f60\u4eec\u4e86\uff01\u51fa\u6765\u6211\u6709\u8bdd \u8bf4\u3002\u201d\u5f20\u4e39\u67ab\u6253\u4e86\u4e2a\u7a81\uff0c\u8fd9\u5f20\u98ce\u5e9c\u662f\u4eac\u5e08\u7b2c\u4e00\u9ad8\u624b\uff0c\u60f3\u4e0d\u5230\u4ed6\u8fd9\u6837\u5feb\u53c8\u56de\u6765\u4e86\uff0c\u4ed6\u4eb2\u81ea\u7387\u4eba \u5305\u56f4\uff0c\u60f3\u51b2\u51fa\u53bb\u66f4\u662f\u65e0\u671b\uff01 \u3000\u3000\u5f20\u98ce\u5e9c\u7f05\u5200\u4e00\u6307\uff0c\u53c8\u5927\u58f0\u53eb\u9053\uff1a\u201c\u8eb2\u8eb2\u85cf\u85cf\uff0c\u7b97\u5f97\u4ec0\u4e48\u597d\u6c49\uff1f\u201d\u8bdd\u58f0\u672a\u4e86\uff0c\u53ea\u89c1\u5c71\u5934\u4eba \u5f71\u4e00\u6643\uff0c\u5f20\u4e39\u67ab\u8863\u8882\u98d8\u98d8\uff0c\u81ea\u5ca9\u77f3\u4e4b\u540e\u4e00\u8dc3\u800c\u51fa\uff0c\u62d4\u5251\u5927\u7b11\u9053\uff1a\u201c\u5f20\u5927\u4eba\u6b66\u529f\u76d6\u4e16\uff0c\u7387\u9886\u5343 \u519b\u4e07\u9a6c\uff0c\u5c45\u7136\u653b\u4e0a\u6b64\u5c71\uff0c\u786e\u5b9e\u7b97\u5f97\u597d\u6c49\uff01\u201d \u3000\u3000\u5f20\u98ce\u5e9c\u9762\u4e0a\u4e00\u7ea2\uff0c\u9053\uff1a\u201c\u4f60\u4e0d\u5fc5\u6fc0\u6211\uff0c\u8fd9\u5c71\u4e0b\u867d\u6709\u4f17\u591a\u519b\u9a6c\uff0c\u4f60\u4eec\u4e5f\u5c3d\u7ba1\u51b2\u7740\u6211\u5f20\u67d0\u4e00 \u4eba\uff01\u201d\u5f20\u4e39\u67ab\u5b9d\u5251\u4e00\u6643\uff0c\u7b11\u9053\uff1a\u201c\u5999\u6781\uff0c\u5999\u6781\uff0c\u90a3\u4e48\u8bf7\u5212\u4e0b\u9053\u513f\uff01\u201d\u5f20\u98ce\u5e9c\u779f\u4e86\u4ed6\u4eec\u4e00\u773c\uff0c \u5ffd\u9053\uff1a\u201c\u770b\u4f60\u4eec\u4e8c\u4eba\u5e76\u975e\u9ed1\u9053\u4e0a\u7684\u4eba\u7269\uff0c\u548c\u90a3\u9707\u4e09\u754c\u5374\u662f\u4ec0\u4e48\u4ea4\u60c5\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u8fd9\u4e2a\u4f60 \u4e0d\u5fc5\u7ba1\uff0c\u95f2\u8bdd\u4f11\u63d0\uff0c\u54b1\u4eec\u4e14\u6597\u4e2a\u4e09\u4e94\u767e\u62db\uff0c\u4f60\u82e5\u4e0d\u80fd\u80dc\u6211\uff0c\u53c8\u5f85\u5982\u4f55\uff1f\u201d\u5f20\u4e39\u67ab\u81ea\u5fd6\uff1a\u82e5\u8bba \u529f\u529b\u7684\u6df1\u539a\uff0c\u81ea\u5df1\u5b9e\u4e0d\u5982\u4ed6\uff1b\u82e5\u8bba\u5251\u672f\u7684\u7cbe\u5999\uff0c\u5219\u81ea\u5df1\u5374\u8981\u7a0d\u9ad8\u534a\u7740\uff0c\u5728\u4e09\u4e94\u767e\u62db\u4e4b\u5185\uff0c\u53ea \u6015\u8c01\u4e5f\u80dc\u4e0d\u4e86\u8c01\u3002\u4ed6\u77e5\u9053\u5f20\u98ce\u5e9c\u4e43\u662f\u4eac\u5e08\u7b2c\u4e00\u9ad8\u624b\uff0c\u4e3a\u4eba\u81ea\u8d1f\u4e4b\u6781\uff0c\u6240\u4ee5\u7528\u8bdd\u5c06\u4ed6\u903c\u4f4f\u3002 \u3000\u3000\u5f20\u98ce\u5e9c\u53c8\u77a7\u4e86\u4e8c\u4eba\u4e00\u773c\uff0c\u7b11\u9053\uff1a\u201c\u4e0d\u5fc5\u5355\u6253\u72ec\u6597\uff0c\u4f60\u4eec\u4e8c\u4eba\u4e00\u9f50\u4e0a\u6765\uff01\u201d\u5f20\u4e39\u67ab\u51b7\u51b7\u8bf4 \u9053\uff1a\u201c\u90a3\u4e48\u4eac\u5e08\u4e09\u5927\u9ad8\u624b\uff0c\u4eca\u540e\u5c31\u53ea\u5269\u4e0b\u4e24\u4eba\u5566\uff01\u201d\u610f\u601d\u662f\u8bf4\uff0c\u82e5\u7136\u4ed6\u6562\u4ee5\u4e00\u654c\u4e8c\uff0c\u90a3\u5c31\u5fc5 \u6b7b\u65e0\u7591\u3002\u5f20\u98ce\u5e9c\u7b11\u9053\uff1a\u201c\u90a3\u5374\u4e5f\u4e0d\u89c1\u5f97\uff01\u4f60\u4eec\u4e8c\u4eba\u6b66\u529f\u6211\u90fd\u89c1\u8fc7\u7684\uff0c\u82e5\u8bf4\u5355\u6253\u72ec\u6597\uff0c\u4f60\u5927\u7ea6 \u53ef\u63a5\u6211\u4e09\u4e94\u767e\u62db\uff0c\u4f60\u5212\u8fd9\u4e2a\u9053\u513f\uff0c\u6211\u53ef\u4e0d\u4e0a\u4f60\u5f53\u3002\u201d\u5f20\u4e39\u67ab\u4e00\u6014\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u4eba\u679c\u662f\u5389\u5bb3\uff0c \u77e5\u5df1\u77e5\u5f7c\uff0c\u548c\u6211\u6240\u89c1\u7adf\u662f\u5b8c\u5168\u76f8\u540c\u3002\u201d\u4fbf\u9053\uff1a\u201c\u90a3\u4fbf\u4e0d\u4ee5\u4e09\u4e94\u767e\u62db\u4e3a\u9650\uff0c\u54b1\u4eec\u4e00\u5bf9\u4e00\u7684\u25a1 \u62fc\uff0c\u968f\u4f60\u5212\u51fa\u9053\u6765\u3002\u201d\u53ea\u542c\u5f97\u5f20\u98ce\u5e9c\u7eed\u9053\uff1a\u201c\u81f3\u4e8e\u4f60\u8fd9\u4f4d\u4f19\u4f34\u7684\u6b66\u529f\uff0c\u5927\u7ea6\u53ea\u53ef\u63a5\u6211\u767e\u62db\u3002 \u8fd9\u6837\u5427\uff0c\u4f60\u4eec\u4e8c\u4eba\u4e00\u9f50\u4e0a\u6765\uff0c\u5728\u4e94\u5341\u62db\u4e4b\u5185\uff0c\u4f60\u4eec\u82e5\u80fd\u53d6\u80dc\uff0c\u90a3\u4e48\u6211\u4fbf\u4fdd\u4e3e\u4f60\u4eec\u505a\u4eca\u79d1\u7684\u6b66 \u8fdb\u58eb\uff0c\u4e0d\u5fc5\u518d\u8003\u8bd5\u5566\u3002\u201d\u5f20\u4e39\u67ab\u5927\u7b11\u9053\uff1a\u201c\u6211\u4eec\u4e8c\u4eba\u8981\u80dc\u4f60\u6613\u5982\u53cd\u638c\uff0c\u4f55\u987b\u4e94\u5341\u62db\uff0c\u5728\u4e94\u62db \u4e4b\u5185\uff0c\u6211\u4eec\u82e5\u7136\u4e0d\u80fd\u53d6\u80dc\uff0c\u4efb\u7531\u4f60\u7684\u5904\u7f6e\u3002\u82e5\u7136\u5728\u4e94\u62db\u4e4b\u5185\uff0c\u6211\u4eec\u80dc\u4e86\uff0c\u6211\u4eec\u4e5f\u4e0d\u5e0c\u7f55\u4ec0\u4e48 \u8fdb\u58eb\u72b6\u5143\uff0c\u54b1\u4eec\u7eff\u6c34\u9752\u5c71\uff0c\u540e\u4f1a\u6709\u671f\uff01\u201d\u6b64\u8bdd\u610f\u601d\uff0c\u5373\u662f\u8bf4\u5728\u4e94\u62db\u4e4b\u5185\uff0c\u5047\u82e5\u4ed6\u4eec\u4e8c\u4eba\u80dc \u4e86\uff0c\u5f20\u98ce\u5e9c\u53ef\u5f97\u4efb\u7531\u4ed6\u4eec\u9003\u8d70\u3002 \u3000\u3000\u4f60\u9053\u5f20\u98ce\u5e9c\u4f55\u4ee5\u5b9a\u8981\u575a\u6301\u4e0e\u4ed6\u4eec\u4e8c\u4eba\u76f8\u6597\uff1f\u539f\u6765\u5f20\u98ce\u5e9c\u6628\u65e5\u8ffd\u4e0d\u4e0a\u6bd5\u9053\u51e1\uff0c\u56de\u6765\u4e4b\u540e\uff0c \u89c1\u6a0a\u5fe0\u3001\u8d2f\u4ef2\u4e8c\u4eba\u90fd\u53d7\u4e86\u4f24\uff0c\u60ca\u95ee\u5176\u6545\uff0c\u6a0a\u3001\u8d2f\u4e8c\u4eba\u8bf4\u53ca\u5f20\u4e39\u67ab\u4e0e\u4e91\u857e\u8054\u5251\u4e4b\u5a01\uff0c\u8a00\u4e0b\u5c1a\u6709 \u4f59\u6016\u3002\u5f20\u4e39\u67ab\u542c\u4e86\uff0c\u751a\u662f\u60ca\u5947\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u4ed6\u4eec\u4e8c\u4eba\uff0c\u4ee5\u90a3\u767d\u9a6c\u4e66\u751f\u6b66\u529f\u6700\u9ad8\uff0c\u4f46\u4ea6\u4e0d\u8fc7 \u6bd4\u6a0a\u5fe0\u3001\u8d2f\u4ef2\u7565\u80dc\u4e00\u7b79\uff0c\u8054\u8d77\u624b\u6765\u5728\u4e94\u4e03\u5341\u62db\u4e4b\u5185\uff0c\u6253\u8d25\u6a0a\u5fe0\u3001\u8d2f\u4ef2\uff0c\u4e5f\u8fd8\u4e0d\u7b97\u7a00\u5947\uff0c\u5c82\u6709 \u5728\u4e00\u4e24\u62db\u5185\u5c31\u80fd\u5927\u80dc\u7684\u9053\u7406\uff1f\u201d\u5f20\u98ce\u5e9c\u4e43\u662f\u6b66\u672f\u540d\u5bb6\uff0c\u5e73\u751f\u6f5c\u5fc3\u6b66\u5b66\uff0c\u95fb\u8bf4\u6709\u4ec0\u4e48\u7279\u5f02\u6b66 \u529f\uff0c\u4fbf\u60f3\u89c1\u8bc6\uff0c\u4e3a\u4eba\u62b1\u8d1f\u5374\u662f\u4e0e\u666e\u901a\u7684\u536b\u58eb\u4e0d\u540c\u3002 \u3000\u3000\u5f20\u98ce\u5e9c\u81ea\u601d\uff0c\u81ea\u5df1\u65ad\u65e0\u5728\u4e94\u5341\u62db\u4e4b\u5185\u843d\u8d25\u4e4b\u7406\uff0c\u4e00\u542c\u5f20\u4e39\u67ab\u8bf4\u53ea\u987b\u4e94\u62db\uff0c\u4e0d\u7981\u72c2\u7b11\uff0c\u7f05 \u5200\u626c\u7a7a\u4e00\u5288\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u597d\u5427\u90a3\u7b2c\u4e00\u62db\u6765\u4e86\uff0c\u63a5\u5200\uff01\u201d\u5200\u5149\u98d8\u5ffd\u4f3c\u5de6\u4f3c\u53f3\uff0c\u4e00\u51fa\u624b\u4fbf\u4ee5 \u201c\u6d41\u661f\u95ea\u7535\u201d\u7684\u62db\u6570\uff0c\u5206\u88ad\u4e8c\u4eba\u3002 \u3000\u3000\u4e91\u857e\u72ec\u501a\u5ca9\u8fb9\uff0c\u5982\u9189\u5982\u75f4\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u4f46\u89c1\u5f20\u98ce\u5e9c\u5200\u5149\u95ea\u95ea\uff0c\u63a0\u5230\u9762\u95e8\u3002\u5f20\u4e39\u67ab \u5927\u6025\uff0c\u53eb\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u5feb\u5feb\u51fa\u62db\uff01\u201d\u5251\u968f\u58f0\u5230\uff0c\u624b\u8d77\u4e00\u5251\uff0c\u201c\u62e6\u6c5f\u622a\u6597\u201d\uff0c\u62a2\u5230\u4e91\u857e\u524d \u9762\uff0c\u62db\u67b6\u5f20\u98ce\u5e9c\u7684\u7f05\u5200\u3002\u5f20\u98ce\u5e9c\u90a3\u62db\u6d41\u661f\u5200\u6cd5\uff0c\u672c\u662f\u5206\u88ad\u4e8c\u4eba\uff0c\u5200\u5251\u76f8\u4ea4\uff0c\u94ff\u9535\u4e00\u58f0\uff0c\u5200\u950b \u5f80\u524d\u4e00\u8361\uff0c\u4f59\u52bf\u672a\u8870\uff0c\u4ecd\u7167\u7740\u4e91\u857e\u5288\u53bb\uff0c\u4e91\u857e\u8fd9\u65f6\u624d\u51fa\u62db\u76f8\u6297\uff0c\u5251\u950b\u4e00\u5708\u4e00\u6296\uff0c\u5c06\u5f20\u98ce\u5e9c\u7684 \u7f05\u5200\u5c01\u51fa\u5916\u95e8\u3002\u8eab\u5b50\u4e5f\u4e0d\u7531\u81ea\u4e3b\u5012\u9000\u51e0\u6b65\uff0c\u6447\u6447\u6643\u6643\u3002\u8fd9\u8fd8\u662f\u56e0\u4e3a\u6709\u5f20\u4e39\u67ab\u66ff\u5979\u5148\u6321\u4e86\u4e00 \u4e0b\uff0c\u8981\u4e0d\u7136\u4e91\u857e\u7684\u5251\u65e9\u5df2\u7ed9\u4ed6\u9707\u98de\u3002 \u3000\u3000\u5f20\u98ce\u5e9c\u54c8\u54c8\u5927\u7b11\uff0c\u9053\uff1a\u201c\u539f\u6765\u8054\u5251\u4e4b\u5a01\uff0c\u4e5f\u4e0d\u8fc7\u5982\u6b64\uff01\u5c0f\u5fc3\uff0c\u63a5\u5200\uff01\u6211\u7b2c\u4e8c\u62db\u662f\u2018\u516b\u65b9 \u98ce\u96e8\u2019\uff0c\u4f60\u4eec\u53cc\u5251\u5fc5\u987b\u540c\u51fa\u624d\u884c\uff0c\u4f11\u8bf4\u6211\u4e0d\u544a\u8bc9\u4f60\uff01\u201d\u4e91\u857e\u6ca1\u7cbe\u6253\u91c7\uff0c\u5e73\u65e5\u79cb\u6c34\u822c\u7684\u773c\u76ae\u4e5f \u50cf\u5931\u53bb\u4e86\u5149\u8f89\uff0c\u5f20\u4e39\u67ab\u5927\u6025\uff0c\u6084\u58f0\u8bf4\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u4f60\u867d\u6068\u6211\uff0c\u4e5f\u8981\u5148\u6253\u9000\u6b64\u4eba\uff0c\u7559\u5f97\u6027 \u547d\uff0c\u4f60\u624d\u80fd\u5411\u6211\u62a5\u4ec7\u5440\uff01\u50bb\u5144\u5f1f\uff01\u201d\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u5f20\u98ce\u5e9c\u7f05\u5200\u626c\u7a7a\u4e00\u95ea\uff0c\u4f46\u89c1\u94f6\u5149\u5982 \u96e8\uff0c\u5343\u70b9\u4e07\u70b9\uff0c\u904d\u6d12\u4e0b\u6765\uff0c\u8fd9\u4e00\u62db\u662f\u201c\u4e94\u864e\u65ad\u95e8\u5200\u201d\u7684\u7cbe\u534e\u6240\u5728\uff0c\u6bd4\u521a\u624d\u66f4\u4e3a\u5389\u5bb3\uff01\u4e91\u857e\u5fc3 \u4e2d\u611f\u52a8\uff0c\u53cc\u775b\u8574\u6cea\uff0c\u9752\u51a5\u5b9d\u5251\u5f80\u524d\u4e00\u6307\uff0c\u77ac\u606f\u4e4b\u95f4\uff0c\u628a\u788e\u96e8\u822c\u7684\u5200\u5149\u8feb\u5f97\u96e8\u6536\u5149\u6563\uff0c\u5f20\u98ce\u5e9c \u64a4\u62db\u53eb\u9053\uff1a\u201c\u597d\uff0c\u679c\u7136\u662f\u6709\u70b9\u9053\u7406\uff01\u518d\u63a5\u4e00\u62db\uff01\u201d\u9a84\u6c14\u53d7\u632b\uff0c\u8fd9\u7b2c\u4e09\u62db\u4ed6\u53ef\u4e0d\u6562\u9884\u5148\u8bf4\u51fa \u4e86\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u9762\u9732\u7b11\u5bb9\uff0c\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u51fa\u624b\u8981\u66f4\u5feb\u4e00\u4e9b\uff01\u201d\u5f20\u98ce\u5e9c\u8fc8\u524d\u4e00\u6b65\uff0c\u7f05\u5200\u4e00\u63a8\uff0c\u5de6 \u53f3\u659c\u6487\uff0c\u8fd9\u4e00\u62db\u540d\u4e3a\u2018\u5206\u82b1\u6307\u67f3\u2019\uff0c\u67d4\u4e2d\u5e26\u521a\uff0c\u5374\u662f\u534a\u5b88\u534a\u653b\u4e4b\u7740\u3002\u5f20\u4e39\u67ab\u4e00\u58f0\u957f\u7b11\uff0c\u5251\u8bc0 \u4e00\u9886\uff0c\u51fa\u624b\u5982\u7535\uff0c\u4f46\u662f\u4e91\u857e\u968f\u624b\u4e00\u6325\uff0c\u9752\u51a5\u5251\u4e5f\u6025\u968f\u800c\u51fa\uff0c\u5f20\u98ce\u5e9c\u62db\u6570\u8fd8\u672a\u4f7f\u5f00\uff0c\u5df2\u7ed9\u53cc\u5251 \u5c01\u4f4f\uff0c\u4e0d\u7531\u5f97\u5927\u5403\u4e00\u60ca\uff0c\u5f3a\u529b\u4e00\u4e2a\u201c\u5927\u5f2f\u8170\uff0c\u659c\u63d2\u67f3\u201d\uff0c\u628a\u653b\u52bf\u5168\u6539\u4e3a\u5b88\u52bf\uff0c\u786c\u751f\u751f\u7684\u5c06\u7f05 \u5200\u64a4\u4e86\u56de\u6765\uff0c\u5f20\u3001\u4e91\u4e8c\u4eba\u90fd\u89c9\u5251\u5c16\u5982\u7ed9\u4e00\u80a1\u52b2\u529b\u9ecf\u4f4f\uff0c\u867d\u7136\u662f\u77ac\u606f\u4e4b\u95f4\u5373\u5c06\u4ed6\u8fd9\u79cd\u5185\u529f\u67d4\u52b2 \u5316\u89e3\uff0c\u4f46\u5f20\u98ce\u5e9c\u4ea6\u5df2\u8131\u4e86\u9669\u5883\uff0c\u8dc4\u8dc4\u8e09\u8e09\u5730\u659c\u7a9c\u51fa\u4e00\u4e08\u5f00\u5916\uff0c\u5401\u5401\u5598\u6c14\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u6697\u8d5e\u4e00\u58f0\uff0c\u6b64\u4eba\u679c\u4e0d\u6127\u662f\u4eac\u5e08\u7b2c\u4e00\u9ad8\u624b\uff0c\u4f46\u89c1\u5f20\u98ce\u5e9c\u811a\u6b65\u4e0d\u4e01\u4e0d\u516b\uff0c\u6a2a\u5200\u5f53\u80f8\uff0c \u5b88\u7740\u95e8\u6237\uff0c\u53cc\u773c\u7741\u5706\uff0c\u663e\u89c1\u5fc3\u4e2d\u751a\u662f\u60ca\u5f02\u3002\u5f20\u4e39\u67ab\u7709\u5934\u4e00\u76b1\uff0c\u5fc3\u9053\u201c\u6b64\u4eba\u786e\u662f\u6c5f\u6e56\u8001\u624b\uff0c\u4ed6 \u5168\u91c7\u5b88\u52bf\uff0c\u6211\u4eec\u53ea\u5269\u4e00\u62db\uff0c\u8fd9\u4e00\u62db\u672a\u5fc5\u80fd\u5c06\u4ed6\u6253\u8d25\uff01\u201d\u5f20\u98ce\u5e9c\u7528\u4e0a\u4e58\u5200\u6cd5\uff0c\u62a4\u7740\u5168\u8eab\uff0c\u5fc3\u4e2d \u7a0d\u5b9a\uff0c\u53c8\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u6211\u5df2\u5360\u5148\u8d70\u4e86\u4e09\u62db\uff0c\u8fd8\u6709\u4e00\u62db\uff0c\u8be5\u8ba9\u4f60\u4eec\u5148\u8d70\u4e86\uff01\u597d\uff0c\u6765\u5440\uff01\u201d\u5f20\u4e39 \u67ab\u77a5\u4e86\u4e91\u857e\u4e00\u773c\uff0c\u53ea\u89c1\u5979\u76ee\u5149\u95ea\u95ea\uff0c\u53c8\u6062\u590d\u4e86\u5e73\u65e5\u7684\u5149\u8f89\uff0c\u6b63\u5728\u5168\u795e\u8d2f\u6ce8\uff0c\u51dd\u89c6\u654c\u4eba\uff0c\u5f20\u4e39 \u67ab\u53d1\u4e00\u58f0\u5578\uff0c\u4e24\u4eba\u540c\u65f6\u98de\u8d77\uff0c\u53cc\u5251\u9f50\u4f38\uff0c\u4e24\u9053\u94f6\u5149\uff0c\u51cc\u7a7a\u4e0b\u523a\uff0c\u5f20\u98ce\u5e9c\u8eab\u8eaf\u4e00\u77ee\uff0c\u6a2a\u5200\u5f80\u4e0a \u6321\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u4f46\u89c1\u53cc\u5251\u6025\u843d\uff0c\u94f6\u8679\u4ea4\u526a\uff0c\u500f\u5730\u4f38\u5c55\u5f00\u6765\u3002 \u3000\u3000\u5f20\u98ce\u5e9c\u4e00\u4e2a\u7ffb\u8eab\uff0c\u5200\u5149\u4e00\u8f6c\uff0c\u500f\u5730\u817e\u8eab\u98de\u8d77\uff0c\u5f20\u4e39\u67ab\u7edd\u6599\u4e0d\u5230\u4ed6\u5728\u53cc\u5251\u73af\u653b\u4e4b\u4e0b\uff0c\u5c45\u7136 \u6562\u51fa\u6b64\u9669\u62db\uff0c\u6697\u53eb\u4e00\u58f0\uff1a\u201c\u4e0d\u597d\uff01\u201d\u53ea\u6050\u4e00\u51fb\u4e0d\u4e2d\uff0c\u53c8\u8981\u7ed9\u4ed6\u5154\u8131\uff0c\u90a3\u5c31\u6ee1\u4e86\u56db\u62db\uff0c\u81ea\u5df1\u53ea \u597d\u8ba4\u8f93\u4e86\u3002\u5f20\u4e39\u67ab\u51fa\u5251\u7a0d\u524d\uff0c\u62db\u6570\u5df2\u7ecf\u4f7f\u5c3d\uff0c\u6b63\u5728\u5fc3\u6025\uff0c\u5ffd\u89c1\u4e91\u857e\u51fa\u5251\u7a0d\u540e\uff0c\u5251\u52bf\u672a\u5c3d\uff0c\u5251 \u5c16\u521a\u521a\u78b0\u5230\u5f20\u98ce\u5e9c\u7684\u811a\u8ddf\uff0c\u5c31\u5728\u8fd9\u7a0d\u7eb5\u5373\u901d\u4e4b\u9645\uff0c\u5c06\u4ed6\u51fb\u5012\uff01 \u3000\u3000\u5f20\u4e39\u67ab\u53c8\u60ca\u53c8\u559c\uff0c\u5fc3\u4e2d\u6697\u6697\u5947\u602a\uff0c\u6309\u8bf4\u5f20\u98ce\u5e9c\u7684\u529f\u592b\uff0c\u90a3\u4e00\u8dc3\u7eb5\uff0c\u53ea\u8981\u53bb\u52bf\u7a0d\u5feb\uff0c\u4e91\u857e \u7684\u5251\u5c16\u5c31\u843d\u4e86\u7a7a\uff0c\u4e0d\u77e5\u4f55\u4ee5\u4ed6\u597d\u8c61\u8fd8\u672a\u5c3d\u5c55\u6240\u80fd\u3002 \u3000\u3000\u53ea\u89c1\u5f20\u98ce\u5e9c\u4e00\u4e2a\u201c\u9ca4\u9c7c\u6253\u633a\u201d\uff0c\u4ece\u5730\u4e0b\u4e00\u8dc3\u800c\u8d77\uff0c\u82e6\u7b11\u4e00\u58f0\uff0c\u6325\u624b\u8bf4\u9053\uff1a\u201c\u53cc\u5251\u5408\u58c1\uff0c \u679c\u662f\u795e\u5947\uff01\u4f60\u4eec\u8d70\u5427\u3002\u201d\u8d2f\u4ef2\u5728\u65c1\u8bf4\u9053\uff1a\u201c\u5927\u54e5\uff0c\u5982\u6b64\u8f7b\u6613\uff0c\u4fbf\u653e\u4ed6\u4eec\u8d70\u4e86\uff1f\u201d\u5f20\u98ce\u5e9c\u9053\uff1a \u201c\u541b\u5b50\u4e00\u8a00\uff0c\u5feb\u9a6c\u4e00\u97ad\uff0c\u653e\u4ed6\u4eec\u8d70\uff01\u201d\u8d2f\u4ef2\u55eb\u55eb\u5685\u5685\uff0c\u5c1a\u6b32\u8fdb\u8a00\uff0c\u5f20\u98ce\u5e9c\u9053\uff1a\u201c\u4ed6\u4eec\u53c8\u4e0d\u662f \u9ed1\u9053\u4e0a\u7684\u4eba\u7269\uff0c\u653e\u4e86\u4ed6\u4eec\uff0c\u4e5f\u6ca1\u4ec0\u4e48\u7f6a\u8d23\uff0c\u4f55\u5fc5\u8d2a\u9886\u4e00\u529f\uff01\u201d\u8d2f\u4ef2\u9762\u4e0a\u4e00\u7ea2\uff0c\u9053\uff1a\u201c\u5927\u54e5\u65e2 \u7136\u4e00\u529b\u62c5\u627f\uff0c\u54b1\u4eec\u6ca1\u6709\u8bdd\u8bf4\u3002\u201d\u5f20\u98ce\u5e9c\u4f20\u4e0b\u5c06\u4ee4\uff0c\u8ba9\u5f20\u3001\u4e91\u4e8c\u4eba\u5b89\u7136\u4e0b\u5c71\uff0c\u4e0d\u51c6\u62e6\u622a\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u65bd\u4e86\u4e00\u793c\uff0c\u5f20\u98ce\u5e9c\u9053\uff1a\u201c\u54b1\u4eec\u4e24\u6b21\u4ea4\u624b\uff0c\u5c1a\u672a\u77e5\u9053\u4f60\u7684\u59d3\u540d\uff0c\u4f60\u5230\u5e95\u662f\u4ece\u54ea\u513f\u6765 \u7684\uff1f\u201d\u5f20\u4e39\u67ab\u61d2\u6d0b\u6d0b\u6253\u4e86\u4e2a\u54c8\u6b20\uff0c\u9053\uff1a\u201c\u4f60\u8001\u5b50\u59d3\u5f20\uff0c\u54b1\u8001\u5b50\u4e5f\u59d3\u5f20\u3002\u6b64\u5f20\u867d\u4e0d\u540c\u5f7c\u5f20\uff0c\u4e94 \u767e\u5e74\u524d\u662f\u4e00\u5bb6\u3002\u6211\u5c0a\u4f60\u4e00\u58f0\u5927\u54e5\uff0c\u4e3a\u5f1f\u75b2\u5026\u5f97\u7d27\uff0c\u8fd9\u91cc\u4eba\u591a\u5608\u6742\uff0c\u4e0d\u597d\u7761\u89c9\uff0c\u6055\u4e0d\u5949\u966a \u5566\uff01\u201d\u5f20\u4e39\u67ab\u4ea6\u5e84\u4ea6\u8c10\uff0c\u8d2f\u4ef2\u6c14\u5f97\u9762\u76ae\u53d8\u8272\uff0c\u5f20\u98ce\u5e9c\u5374\u662f\u4e0d\u4ee5\u4e3a\u610f\uff0c\u5927\u7b11\u9053\uff1a\u201c\u4ea6\u72c2\u4ea6\u4fa0\uff0c \u6709\u8fd9\u6837\u4e00\u4e2a\u540c\u5b97\u5144\u5f1f\u5012\u4e5f\u4e0d\u9519\uff0c\u597d\uff0c\u4f60\u8d70\u5427\uff01\u201d\u5f20\u4e39\u67ab\u6717\u541f\u9053\uff1a\u201c\u5c1a\u6709\u6c5f\u6e56\u672c\u8272\u5728\uff0c\u5c06\u519b\u4ea6 \u662f\u53ef\u4eba\u513f\u3002\u7eff\u6c34\u9752\u5c71\uff0c\u540e\u4f1a\u6709\u671f\uff0c\u6211\u53bb\u4e86\uff01\u201d\u643a\u4e86\u4e91\u857e\uff0c\u5f84\u81ea\u4e0b\u5c71\uff0c\u626c\u957f\u800c\u53bb\u3002 \u3000\u3000\u4e00\u8def\u4e0a\u4e91\u857e\u9ed8\u4e0d\u4f5c\u58f0\uff0c\u8d70\u51fa\u4e94\u3001\u4e03\u91cc\u5730\uff0c\u5df2\u628a\u5b98\u519b\u8fdc\u8fdc\u7529\u5728\u540e\u9762\uff0c\u9762\u524d\u662f\u4e00\u6761\u4e09\u53c9\u8def\uff0c \u5f20\u4e39\u67ab\u53c8\u6253\u4e86\u4e2a\u54c8\u6b20\uff0c\u642d\u8baa\u8bf4\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u54b1\u4eec\u8be5\u627e\u4e2a\u5730\u65b9\u6b47\u606f\u5566\uff01\u6b63\u4e2d\u8fd9\u6761\u8def\u901a\u5f80\u6b63 \u5b9a\uff0c\u5de6\u8fb9\u8fd9\u6761\u8def\u901a\u5f80\u683e\u57ce\uff0c\u54b1\u4eec\u8fd8\u662f\u5f80\u6b63\u5b9a\u53bb\u5427\u3002\u201d\u4e91\u857e\u8863\u8896\u4e00\u62c2\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u8d70\u4f60 \u7684\uff0c\u6211\u8d70\u6211\u7684\uff01\u201d\u5f20\u4e39\u67ab\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u4f60\u5c31\u8fd9\u6837\u6068\u6211\u5417\uff1f\u201d\u4e91\u857e\u907f\u5f00\u4ed6\u7684\u76ee\u5149\uff0c\u8138\u76ae\u7d27 \u7ef7\uff0c\u9053\uff1a\u201c\u591a\u8c22\u4f60\u51e0\u6b21\u6551\u547d\u4e4b\u6069\uff0c\u4fbf\u54b1\u4eec\u4e24\u5bb6\u4e4b\u4ec7\uff0c\u65e0\u6cd5\u53ef\u89e3\u3002\u54b3\uff0c\u8c01\u53eb\u6211\u7684\u7237\u7237\u65e9\u6b7b\uff0c\u60f3 \u529d\u4ed6\u56de\u5fc3\u8f6c\u610f\uff0c\u5df2\u662f\u4e0d\u80fd\u3002\u7956\u5148\u7559\u4e0b\u7684\u9057\u547d\uff0c\u5b50\u5b59\u600e\u80fd\u8fdd\u80cc\uff1f\u54b3\uff0c\u8fd9\u662f\u547d\u4e2d\u6ce8\u5b9a\u2026\u2026\u201d\u5f20\u4e39 \u67ab\u9053\uff1a\u201c\u6211\u4e0d\u4fe1\u547d\u3002\u201d\u4e91\u857e\u9053\uff1a\u201c\u4e0d\u4fe1\u53c8\u5f85\u5982\u4f55\uff1f\u2026\u2026\u597d\uff0c\u4f60\u8d70\u5427\uff0c\u4f60\u82e5\u8d70\u4e1c\uff0c\u6211\u5c31\u8d70 \u897f\uff01\u201d\u5f20\u4e39\u67ab\u9eef\u7136\u8bf4\u9053\uff1a\u201c\u4f60\u65e2\u5b9a\u8981\u62a5\u4ec7\uff0c\u4f55\u4e0d\u75db\u5feb\u4e0b\u624b\uff1f\u201d\u4e91\u857e\u773c\u5708\u4e00\u7ea2\uff0c\u8e0f\u4e0a\u6b63\u4e2d\u90a3\u6761 \u8def\uff0c\u5934\u4e5f\u4e0d\u56de\uff0c\u75be\u5f80\u524d\u8dd1\u3002\u6b63\u662f\uff1a \u3000\u3000\u7559\u6709\u8840\u4e66\u9634\u5f71\u5728\uff0c\u6069\u4ec7\u96be\u89e3\u6700\u4f24\u5fc3\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u840d\u8e2a\u4fa0\u5f71\u5f55\u300b\u2014\u2014\u7b2c\u5341\u4e00\u56de\u3000\u534a\u591c\u88ad\u756a\u738b\u5947\u60c5\u53e0\u89c1\u3000\u4e2d\u9014\u6765\u602a\u5ba2\u5f02\u4e8b\u96be\u731c \u6881\u7fbd\u751f\u300a\u840d\u8e2a\u4fa0\u5f71\u5f55\u300b \u7b2c\u5341\u4e00\u56de\u3000\u534a\u591c\u88ad\u756a\u738b\u5947\u60c5\u53e0\u89c1\u3000\u4e2d\u9014\u6765\u602a\u5ba2\u5f02\u4e8b\u96be\u731c \u3000\u3000\u4e91\u857e\u5f80\u524d\u75be\u8dd1\uff0c\u53ea\u542c\u5f97\u540e\u9762\u4e00\u58f0\u957f\u53f9\uff0c\u5f20\u4e39\u67ab\u7684\u58f0\u97f3\u9053\uff1a\u201c\u89c1\u4e86\u4f60\u60f9\u4f60\u4f24\u5fc3\uff0c\u4e0d\u89c1\u4f60\u6211 \u53c8\u4f24\u5fc3\u3002\u5440\u4f60\u4f24\u5fc3\u4e0d\u5982\u6211\u4f24\u5fc3\u3002\u5c0f\u5144\u5f1f\uff0c\u4f60\u597d\u597d\u4fdd\u91cd\uff0c\u53bb\u5427\uff0c\u53bb\u5427\uff01\u201d\u4e91\u857e\u5fc3\u4e2d\u4e00\u9178\uff0c\u5f3a\u5fcd \u7740\u6cea\uff0c\u4e5f\u4e0d\u56de\u5934\u3002\u53ea\u542c\u5f97\u540e\u9762\u8bd7\u58f0\u65ad\u7eed\uff0c\u968f\u98ce\u98d8\u5165\u8033\u4e2d\uff0c\u542c\u6e05\u695a\u4e86\uff0c\u5374\u662f\u201c\u76f8\u89c1\u4e89\u5982\u4e0d\u89c1\uff0c \u6709\u60c5\u603b\u4f3c\u65e0\u60c5\u201d\u4e24\u53e5\u3002\u4e91\u857e\u5341\u4e03\u5c81\u6709\u591a\uff0c\u4ece\u672a\u60f3\u8fc7\u7537\u5973\u4e4b\u60c5\uff0c\u542c\u4e86\u8bd7\u58f0\uff0c\u9762\u4e0a\u4e00\u7ea2\uff0c\u7ec6\u7ec6\u5480 \u56bc\u8fd9\u4e24\u53e5\u8bdd\uff0c\u5fc3\u9053\uff1a\u201c\u96be\u9053\u6211\u771f\u662f\u9677\u5165\u60c5\u7f51\u4e2d\u4e86\uff1f\u201d\u9661\u89c9\u795e\u601d\u98d8\u5ffd\u4e00\u7247\u8ff7\u60d8\uff0c\u4ece\u9762\u4e0a\u7ea2\u5230\u8033 \u6839\u3002\u811a\u6b65\u5374\u662f\u4e0d\u6562\u505c\u7559\uff0c\u8f6c\u773c\u4e4b\u95f4\uff0c\u53c8\u8dd1\u51fa\u6570\u5341\u4e08\uff0c\u518d\u56de\u5934\u65f6\uff0c\u5f20\u4e39\u67ab\u7684\u5f71\u5b50\u53c8\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u5230\u4e86\u6b63\u5b9a\uff0c\u5915\u9633\u5c1a\u672a\u843d\u5c71\uff0c\u4e91\u857e\u6295\u4e86\u4e00\u5bb6\u6700\u5927\u7684\u5ba2\u5e97\uff0c\u8981\u4e86\u623f\u95f4\uff0c\u5173\u4e0a\u623f\u95e8\uff0c\u547c\u547c\u4fbf \u7761\u3002\u4e5f\u4e0d\u77e5\u7761\u4e86\u591a\u5c11\u65f6\u5019\uff0c\u5ffd\u542c\u5f97\u9523\u58f0\u94db\u94db\uff0c\u6709\u4eba\u5927\u58f0\u547c\u559d\uff0c\u5e97\u4e3b\u4eba\u4e00\u95f4\u623f\u4e00\u95f4\u623f\u7684\u62cd\u95e8\u53eb \u9053\uff1a\u201c\u5c0f\u5e97\u5df2\u88ab\u5b98\u519b\u5f81\u7528\uff0c\u5ba2\u5b98\u8bf7\u642c\u5230\u522b\u5bb6\u53bb\u5427\uff0c\u623f\u94b1\u67dc\u4e0a\u9000\u8fd8\uff0c\u4e8b\u975e\u5f97\u5df2\uff0c\u5ba2\u5b98\u5305\u6db5\u5219 \u4e2a\u3002\u201d\u5b98\u5e9c\u5f81\u7528\uff0c\u4f4f\u5ba2\u867d\u662f\u4e07\u5206\u4e0d\u6ee1\uff0c\u53ef\u4ea6\u4e0d\u5f97\u4e0d\u642c\u3002 \u3000\u3000\u6700\u540e\u624d\u6765\u6572\u4e91\u857e\u7684\u623f\u95f4\uff0c\u4e91\u857e\u65e9\u5df2\u6574\u597d\u884c\u88c5\uff0c\u5f00\u4e86\u623f\u95e8\uff0c\u5bf9\u5e97\u5c0f\u4e8c\u9053\uff1a\u201c\u4f60\u4e0d\u5fc5\u8bf4\u5566\uff0c \u6211\u8d70\u4fbf\u662f\u3002\u201d\u5e97\u5c0f\u4e8c\u9053\uff1a\u201c\u5b9e\u662f\u5bf9\u4f60\u8001\u4e0d\u4f4f\u3002\u201d\u773c\u5149\u5ffd\u4e0a\u5ffd\u4e0b\uff0c\u6253\u91cf\u4e91\u857e\uff0c\u4e91\u857e\u597d\u751f\u5947\u602a \u9053\uff1a\u201c\u4f60\u770b\u4ec0\u4e48\uff1f\u201d\u5e97\u5c0f\u4e8c\u5173\u4e0a\u623f\u95e8\uff0c\u5c0f\u58f0\u8bf4\u9053\uff1a\u201c\u5ba2\u5b98\u53ef\u77e5\u9053\u5b98\u5bb6\u4e3a\u4f55\u5f81\u7528\u5c0f\u5e97\u5417\uff1f\u201d\u4e91 \u857e\u9053\uff1a\u201c\u4eba\u58f0\u5608\u6742\uff0c\u6211\u542c\u4e0d\u6e05\u695a\u3002\u201d\u5e97\u5c0f\u4e8c\u9053\uff1a\u201c\u542c\u8bf4\u662f\u62db\u5f85\u8499\u53e4\u4f7f\u81e3\uff0c\u5723\u4e0a\u6d3e\u6709\u5fa1\u6797\u519b\u7edf \u9886\u4eb2\u81ea\u62a4\u9001\u5462\u3002\u4eca\u65e5\u664c\u5348\u65f6\u5206\uff0c\u6b63\u5b9a\u7684\u5ba2\u5e97\u5c31\u63a5\u5230\u8859\u95e8\u901a\u544a\uff0c\u8bf4\u662f\u82e5\u6709\u53ef\u7591\u7684\u964c\u751f\u4eba\u6295\u5bbf\uff0c \u4e00\u5b9a\u8981\u62a5\u7ed9\u516c\u5dee\u77e5\u9053\u3002\u6240\u4ee5\u6211\u6015\u5ba2\u5b98\u5230\u522b\u95f4\u6295\u5bbf\uff0c\u4f1a\u6709\u9ebb\u70e6\u3002\u201d\u4e91\u857e\u7b11\u9053\uff1a\u201c\u90a3\u4e48\u4f55\u4ee5\u4f60\u4eec \u53c8\u6562\u6536\u7559\u5462\uff1f\u6211\u4e0d\u53ef\u7591\u4e48\uff1f\u201d\u5e97\u5c0f\u4e8c\u5ffd\u9053\uff1a\u201c\u5ba2\u5b98\u7684\u771f\u59d3\uff0c\u662f\u4e0d\u662f\u4e00\u4e2a\u2018\u4e91\u2019\u5b57\uff1f\u201d\u4e91\u857e\u6295 \u5bbf\u4e4b\u65f6\uff0c\u7528\u7684\u4e43\u662f\u5047\u540d\u5047\u59d3\uff0c\u95fb\u8a00\u4e0d\u89c9\u4e00\u60ca\uff0c\u624b\u8155\u4e00\u7ffb\uff0c\u6263\u7740\u5e97\u5c0f\u4e8c\u8109\u95e8\uff0c\u4f4e\u58f0\u559d\u9053\uff1a\u201c\u4f60 \u662f\u8c01\uff1f\u201d\u5e97\u5c0f\u4e8c\u9053\uff1a\u201c\u5ba2\u5b98\u522b\u60ca\uff0c\u90fd\u662f\u81ea\u5df1\u4eba\u3002\u4f60\u82e5\u4e0d\u4fe1\uff0c\u6709\u4f4d\u5ba2\u4eba\u7559\u4e0b\u4e00\u6837\u4e1c\u897f\u7ed9\u4f60\uff0c\u4f60 \u4e00\u770b\u5c31\u77e5\u9053\u4e86\u3002\u201d\u4e91\u857e\u5fc3\u60f3\uff1a\u201c\u82e5\u7136\u81ea\u5df1\u884c\u85cf\u7834\u9732\uff0c\u8fdf\u65e9\u96be\u514d\u52a8\u6b66\uff0c\u4e0d\u653e\u4ed6\u8d70\uff0c\u4ea6\u662f\u4e8e\u4e8b\u65e0 \u8865\u3002\u201d\u4fbf\u677e\u5f00\u4e86\u624b\uff0c\u8ba9\u5e97\u5c0f\u4e8c\u51fa\u95e8\u53bb\u3002 \u3000\u3000\u8fc7\u4e86\u7247\u523b\u5e97\u5c0f\u4e8c\u548c\u638c\u67dc\u4e00\u540c\u8d70\u8fdb\uff0c\u638c\u67dc\u7684\u53d6\u51fa\u4e00\u4e2a\u5c0f\u5305\uff0c\u7528\u4e1d\u5dfe\u5305\u4f4f\uff0c\u9012\u8fc7\u53bb\u9053\uff1a\u201c\u4e91 \u76f8\u516c\uff0c\u8fd9\u5c31\u662f\u90a3\u4f4d\u5ba2\u5b98\u7559\u4e0b\u6765\u7ed9\u4f60\u7684\u4fe1\u7269\u3002\u201d\u4e91\u857e\u8f7b\u8f7b\u89e3\u5f00\uff0c\u53ea\u89c1\u88f9\u7740\u7684\u4e43\u662f\u4e00\u679d\u78a7\u7eff\u73ca \u745a\uff0c\u5171\u5206\u4e5d\u74e3\uff0c\u7eff\u8272\u6676\u83b9\uff0c\u4e91\u857e\u4e00\u89c1\uff0c\u4e0d\u89c9\u5446\u4e86\u3002\u8fd9\u679d\u73ca\u745a\u6b63\u662f\u81ea\u5df1\u9001\u4e0e\u77f3\u7fe0\u51e4\u4f5c\u4e3a\u8058\u793c\u7684 \u90a3\u679d\u73ca\u745a\uff0c\u4e0d\u89c9\u5931\u58f0\u95ee\u9053\uff1a\u201c\u5979\u4e5f\u6765\u4e86\uff0c\u5979\u5728\u6b64\u4e48\uff1f\u201d\u638c\u67dc\u7684\u9053\uff1a\u201c\u77f3\u59d1\u5a18\u6628\u65e5\u66fe\u5230\u6b64\u5904\uff0c \u8be6\u7ec6\u8bf4\u4e86\u4e91\u76f8\u516c\u7684\u9762\u8c8c\uff0c\u53eb\u6211\u4eec\u7559\u795e\uff0c\u4e91\u76f8\u516c\u679c\u7136\u6295\u5bbf\u5c0f\u5e97\uff0c\u8fd9\u53ef\u771f\u662f\u5de7\u554a\uff01\u201d \u3000\u3000\u4e91\u857e\u505a\u58f0\u4e0d\u5f97\uff0c\u60f3\u8d77\u77f3\u7fe0\u51e4\u4e00\u7247\u75f4\u60c5\uff0c\u7adf\u662f\u6446\u8131\u4e0d\u4e86\uff0c\u4e0d\u7531\u5f97\u6697\u6697\u53eb\u82e6\u3002\u638c\u67dc\u7684\u9053\uff1a \u201c\u5b9e\u4e0d\u76f8\u7792\uff0c\u5c0f\u5e97\u4e43\u662f\u6d77\u9633\u5e2e\u7684\u4ea7\u4e1a\uff0c\u6697\u4e2d\u62db\u5f85\u6c5f\u6e56\u4e0a\u5404\u53f7\u4eba\u7269\uff0c\u8f70\u5929\u96f7\u77f3\u8001\u524d\u8f88\u4e0e\u6211\u4eec\u90fd \u662f\u8001\u76f8\u8bc6\u3002\u77f3\u59d1\u5a18\u6628\u665a\u5306\u5306\u7ecf\u8fc7\uff0c\u7559\u4e0b\u6b64\u679d\u73ca\u745a\uff0c\u8bf7\u4f60\u660e\u65e5\u7edd\u65e9\uff0c\u4e00\u5b9a\u8981\u5230\u9752\u9f99\u5ce1\u5019\u5979\uff01\u5230 \u65f6\u81ea\u7136\u6709\u4eba\u5e26\u4f60\u524d\u5f80\u3002\u201d\u4e91\u857e\u53ea\u5f97\u70b9\u4e86\u70b9\u5934\uff0c\u95ee\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u6211\u4eca\u665a\u5bbf\u5728\u4f55\u5904\uff1f\u201d\u638c\u67dc\u7684 \u9053\uff1a\u201c\u6211\u5f53\u4f60\u662f\u81ea\u5df1\u4eba\uff0c\u53ea\u662f\u59d4\u5c48\u76f8\u516c\u5c06\u5ba2\u623f\u8ba9\u51fa\u6765\u4f4f\u5230\u5e10\u623f\u91cc\u53bb\u3002\u201d\u4e91\u857e\u559c\u9053\uff1a\u201c\u597d\u6781\uff0c \u597d\u6781\uff01\u6211\u4e5f\u8981\u770b\u770b\u8499\u53e4\u4f7f\u81e3\u7684\u5a01\u98ce\u3002\u201d \u3000\u3000\u4e91\u857e\u5403\u8fc7\u665a\u996d\uff0c\u53c8\u5047\u5bd0\u4e00\u56de\uff0c\u517b\u8db3\u7cbe\u795e\uff0c\u53ea\u542c\u5f97\u95e8\u5916\u8e44\u58f0\u5f97\u5f97\uff0c\u4eba\u9a6c\u58f0\u55a7\uff0c\u5ba2\u5e97\u4e2d\u4eba\uff0c \u90fd\u8dd1\u51fa\u53bb\u8fce\u63a5\uff0c\u4e91\u857e\u4e0d\u6562\u9732\u9762\uff0c\u4ece\u95e8\u7f1d\u91cc\u5f20\u671b\uff0c\u53ea\u89c1\u56db\u4e2a\u519b\u5b98\u966a\u7740\u4e03\u516b\u4e2a\u8499\u53e4\u4eba\u8d70\u8fdb\u5ba2\u5e97\u3002 \u8d70\u5728\u4e2d\u95f4\uff0c\u88ab\u4f17\u4eba\u7fa4\u661f\u6367\u6708\u822c\u5730\u62e5\u7740\u90a3\u4e2a\u8499\u53e4\u4eba\u7279\u522b\u4ee4\u4eba\u6ce8\u76ee\uff0c\u4e91\u857e\u4e00\u770b\uff0c\u8ba4\u5f97\u6b64\u4eba\u6b63\u662f\u4ee5 \u524d\u5077\u88ad\u5468\u5065\u5c71\u5be8\uff0c\u66fe\u548c\u81ea\u5df1\u4ea4\u8fc7\u624b\u7684\u90a3\u4e2a\u756a\u738b\u3002 \u3000\u3000\u8fd9\u95f4\u5ba2\u5e97\u662f\u57ce\u4e2d\u6700\u5927\u7684\u5ba2\u5e97\uff0c\u623f\u95f4\u751a\u591a\uff0c\u56db\u4e2a\u5fa1\u6797\u519b\u5b98\u9010\u4e2a\u623f\u95f4\u7ec6\u7ec6\u5bdf\u770b\uff0c\u53c8\u95ee\u638c\u67dc\u7684 \u9053\uff1a\u201c\u6ca1\u6709\u95f2\u4eba\u4e86\u4e48\uff1f\u201d\u638c\u67dc\u7684\u9053\uff1a\u201c\u957f\u5b98\u660e\u5bdf\uff0c\u5c0f\u5e97\u5e78\u8499\u5f81\u7528\uff0c\u600e\u6562\u6536\u7559\u95f2\u4eba\uff1f\u201d\u519b\u5b98\u5c1a \u6b32\u8fdb\u5185\u95f4\u7ec6\u67e5\uff0c\u90a3\u8499\u53e4\u756a\u738b\u5927\u58f0\u7b11\u9053\uff1a\u201c\u7edf\u9886\u4e0d\u7528\u5982\u6b64\u5c0f\u5fc3\u4e86\uff0c\u4e2d\u56fd\u867d\u5927\uff0c\u80fd\u4e0e\u6211\u4eec\u62b5\u654c\u7684 \u4eba\u7269\u53ea\u6015\u8fd8\u672a\u66fe\u6709\uff01\u82e5\u7136\u6709\u4eba\u6697\u7b97\u90a3\u5c31\u662f\u4ed6\u81ea\u5bfb\u6b7b\u8def\uff0c\u4e5f\u4e0d\u5fc5\u52b3\u52a8\u8bf8\u4f4d\u76f8\u52a9\uff0c\u53ea\u987b\u8d1f\u8d23\u63a9\u57cb \u6b7b\u5c38\u4fbf\u884c\u4e86\u3002\u201d\u56db\u4e2a\u5fa1\u6797\u519b\u5b98\u4e00\u9f50\u54c8\u8170\u8bf4\u9053\uff1a\u201c\u662f\uff0c\u662f\uff01\u8d35\u56fd\u6b66\u58eb\u5929\u4e0b\u65e0\u654c\uff0c\u662f\u5351\u804c\u8fc7\u4e8e\u5c0f \u5fc3\u4e86\u3002\u201d\u4e91\u857e\u5728\u91cc\u9762\u597d\u4e0d\u751f\u6c14\uff0c\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u7b49\u4e00\u4f1a\u513f\uff0c\u6211\u5012\u8981\u4f60\u4eec\u77e5\u9053\u5389\u5bb3\uff01\u201d \u3000\u3000\u4e00\u7fa4\u4eba\u7b49\uff0c\u5404\u81ea\u5b89\u6b47\uff0c\u53ea\u6709\u4e24\u540d\u8499\u53e4\u6b66\u58eb\u4e0e\u4e24\u540d\u519b\u5b98\u8f6e\u73ed\u5b88\u591c\u3002\u4e91\u857e\u6362\u4e00\u591c\u884c\u8863\u670d\uff0c\u542c \u5f97\u6572\u4e86\u4e09\u66f4\uff0c\u6084\u6084\u5730\u7a7f\u7a97\u800c\u51fa\uff0c\u4f0f\u5728\u6a90\u89d2\uff0c\u5c06\u6885\u82b1\u8774\u8776\u9556\u6263\u5728\u638c\u5fc3\uff0c\u53ea\u7b49\u90a3\u4e24\u540d\u8499\u53e4\u6b66\u58eb\u80cc \u5411\u81ea\u5df1\u4e4b\u65f6\uff0c\u5c31\u53d1\u9556\u5c06\u4ed6\u4eec\u5c04\u6b7b\u3002 \u3000\u3000\u5ffd\u89c1\u5c4b\u9876\u4e0a\u767d\u5f71\u4e00\u95ea\uff0c\u4e91\u857e\u5403\u4e86\u4e00\u60ca\uff0c\u626d\u5934\u770b\u65f6\uff0c\u5fae\u98ce\u98d2\u7136\uff0c\u4eba\u5f71\u5df2\u63a0\u8eab\u800c\u8fc7\u3002\u90a3\u4eba\u8499 \u7740\u9ed1\u8272\u9762\u5dfe\uff0c\u7a7f\u7684\u5374\u662f\u767d\u957f\u8863\uff0c\u5728\u9ed1\u591c\u4e4b\u4e2d\uff0c\u7279\u522b\u523a\u76ee\u3002\u4e91\u857e\u60f3\u8d77\u5f53\u65e5\u5f20\u4e39\u67ab\u591c\u5165\u9ed1\u77f3\u5e84\u4e5f \u662f\u8fd9\u642c\u6253\u626e\uff0c\u5fc3\u5934\u9e7f\u8df3\uff0c\u6025\u5fd9\u6253\u4e86\u4e2a\u624b\u52bf\uff0c\u90a3\u8499\u9762\u4eba\u8f6c\u8fc7\u8eab\u6765\uff0c\u53cc\u624b\u4e00\u6325\uff0c\u6307\u6307\u5916\u9762\uff0c\u793a\u610f \u53eb\u5979\u5feb\u8d70\uff01 \u3000\u3000\u4e91\u857e\u672a\u53ca\u7ec6\u770b\uff0c\u90a3\u4eba\u5df2\u500f\u5730\u8df3\u4e0b\uff0c\u53ea\u542c\u5f97\u4e24\u58f0\u60e8\u53eb\uff0c\u90a3\u8499\u9762\u4eba\u51fa\u624b\u5982\u7535\uff0c\u970e\u5ffd\u4e4b\u95f4\uff0c\u5df2 \u628a\u4e24\u540d\u8499\u53e4\u6b66\u58eb\u4e00\u9f50\u6253\u6b7b\u3002\u4e91\u857e\u6697\u4e2d\u8d5e\u9053\uff1a\u201c\u597d\u4e2a\u5927\u529b\u9e70\u722a\u7684\u91d1\u521a\u624b\u6cd5\uff01\u6211\u53ef\u6ca1\u66fe\u89c1\u5f20\u4e39\u67ab \u7528\u8fc7\u8fd9\u79cd\u624b\u6cd5\u5440\uff1f\u5230\u5e95\u662f\u4ed6\uff0c\u8fd8\u662f\u4e0d\u662f\u4ed6\uff1f\u201d \u3000\u3000\u6b63\u5728\u4e91\u857e\u731c\u5ea6\u4e4b\u65f6\uff0c\u5728\u5185\u95f4\u5b88\u591c\u7684\u4e24\u540d\u5fa1\u6797\u519b\u5b98\u5df2\u662f\u95fb\u58f0\u8df3\u51fa\uff0c\u8fd9\u8499\u9762\u4eba\u4e00\u58f0\u4e0d\u54cd\uff0c\u8eab \u5f62\u4e00\u8d77\uff0c\u53cc\u81c2\u659c\u4f38\uff0c\u5411\u4e24\u540d\u519b\u5b98\u8170\u80c1\u7684\u8f6f\u9ebb\u7a74\u75be\u70b9\u3002 \u3000\u3000\u5de6\u9996\u90a3\u540d\u519b\u5b98\u5e94\u58f0\u5012\u5730\uff0c\u53f3\u9996\u7684\u90a3\u540d\u519b\u5b98\u6b66\u529f\u4e0d\u5f31\uff0c\u4e00\u62db\u201c\u624b\u6325\u7435\u7436\u201d\uff0c\u8fde\u6d88\u5e26\u6253\u7adf\u81ea \u907f\u4e86\u5f00\u53bb\u3002\u90a3\u8499\u9762\u4eba\u4f4e\u58f0\u559d\u9053\uff1a\u201c\u708e\u9ec4\u5b50\u5b59\uff0c\u4f55\u82e6\u4e3a\u80e1\u513f\u5356\u547d\uff01\u201d\u58f0\u97f3\u751a\u4f4e\uff0c\u4e91\u857e\u5728\u5916\u95f4\u542c \u4e0d\u6e05\u695a\uff0c\u53ea\u662f\u5947\u602a\u6b64\u4eba\u4f55\u4ee5\u9aa4\u7136\u6539\u7528\u70b9\u7a74\u624b\u6cd5\uff0c\u4e0d\u7528\u4ed6\u90a3\u624b\u5927\u529b\u91d1\u521a\u624b\u7684\u6740\u624b\u795e\u62db\uff1f \u3000\u3000\u53ea\u89c1\u8499\u9762\u4eba\u624b\u6cd5\u4e00\u53d8\uff0c\u90a3\u540d\u519b\u5b98\u51db\u7136\u6025\u9000\uff0c\u8499\u9762\u4eba\u5411\u4e2d\u95f4\u623f\u6025\u95ef\uff0c\u6b63\u662f\u90a3\u8499\u53e4\u756a\u738b\u6240\u4f4f \u7684\u623f\uff0c\u672a\u5230\u95e8\u524d\u623f\u95e8\u5ffd\u7136\u5927\u5f00\u3002\u53ea\u542c\u5f97\u91cc\u9762\u54c8\u54c8\u5927\u7b11\uff0c\u4eba\u5f71\u4e00\u6643\uff0c\u4e00\u80a1\u52b2\u98ce\u5df2\u75be\u6251\u51fa\u6765\uff0c\u8499 \u9762\u4eba\u8eab\u4e0d\u7531\u5df1\u75be\u9000\u4e09\u6b65\uff0c\u4e91\u857e\u5b9a\u6674\u4e00\u77a7\uff0c\u7adf\u662f\u6fb9\u53f0\u706d\u660e\uff01\u4ed6\u65e9\u5df2\u5165\u5173\uff0c\u4e0d\u77e5\u4f55\u4ee5\u73b0\u5728\u53c8\u548c\u8499 \u53e4\u4f7f\u81e3\u4e00\u9053\u3002\u90a3\u8499\u9762\u4eba\u4e00\u9000\u590d\u8fdb\uff0c\u53ea\u89c1\u6fb9\u53f0\u706d\u660e\u4e00\u4e2a\u65cb\u8eab\uff0c\u53cd\u624b\u4e00\u9001\uff0c\u90a3\u8499\u9762\u4eba\u53c8\u7ed9\u6454\u5012\uff0c \u4f46\u4ecd\u662f\u4e00\u8dc3\u5373\u8d77\u3002\u4e91\u857e\u4e0d\u7981\u51fa\u58f0\u53eb\u9053\uff1a\u201c\u5feb\u8d70\uff01\u201d\u4e09\u679a\u8774\u8776\u9556\u5411\u6fb9\u53f0\u706d\u660e\u4e0a\u4e2d\u4e0b\u4e09\u8def\u4e00\u9f50\u6253 \u53bb\uff0c\u6fb9\u53f0\u706d\u660e\u53cc\u8896\u4e00\u6325\uff0c\u8774\u8776\u9556\u534a\u9014\u843d\u5730\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u90a3\u8499\u9762\u4eba\u53c8\u6251\u4e0a\u6765\uff0c\u6fb9\u53f0\u706d\u660e \u53cc\u638c\u9f50\u51fa\uff0c\u201c\u25a1\u556a\u201d\u4e24\u58f0\uff0c\u56db\u638c\u76f8\u62b5\uff0c\u90a3\u8499\u9762\u4eba\u8dc4\u556e\u8e09\u8e09\u7ed9\u9707\u5f97\u9000\u540e\u6570\u6b65\uff0c\u5374\u5e76\u672a\u8dcc\u5012\u3002\u6fb9 \u53f0\u706d\u660e\u8d5e\u9053\uff1a\u201c\u80fd\u63a5\u6211\u4e00\u638c\uff0c\u4e5f\u7b97\u5f97\u662f\u4e00\u6761\u597d\u6c49\uff01\u201d \u3000\u3000\u4e09\u5ea6\u4ea4\u950b\uff0c\u90a3\u8499\u9762\u4eba\u90fd\u5403\u4e86\u4e8f\uff0c\u4f3c\u5df2\u77e5\u9053\u4e0d\u654c\uff0c\u8f6c\u8eab\u8df3\u4e0a\u5899\u5934\uff0c\u6b63\u5728\u8eab\u5f62\u7eb5\u8d77\u4e4b\u65f6\uff0c\u5148 \u524d\u90a3\u540d\u519b\u5b98\uff0c\u6b63\u5728\u8fd1\u5904\uff0c\u5ffd\u5730\u53d6\u51fa\u4e00\u6761\u8f6f\u97ad\uff0c\u5411\u4e0a\u4e00\u5377\u3002\u4e91\u857e\u5927\u6012\uff0c\u8774\u8776\u9556\u53c8\u8131\u624b\u98de\u51fa\uff0c\u8fd9 \u540d\u519b\u5b98\u53ef\u6ca1\u6709\u6fb9\u53f0\u706d\u660e\u90a3\u6837\u672c\u4e8b\uff0c\u7ed9\u8774\u8776\u9556\u6253\u4e2d\u624b\u8155\u8109\u95e8\uff0c\u767b\u65f6\u6655\u5012\u8f6f\u97ad\u843d\u5730\uff0c\u8499\u9762\u4eba\u5df2\u98de \u8eab\u8df3\u4e0a\u5899\u5934\u4f4e\u4f4e\u8bf4\u58f0\uff1a\u201c\u591a\u8c22\uff01\u201d\u75be\u9a70\u800c\u53bb\u3002\u4e91\u857e\u4e00\u6014\uff0c\u8fd9\u58f0\u97f3\uff0c\u8fd9\u80cc\u5f71\u90fd\u597d\u4f3c\u4ec0\u4e48\u65f6\u5019\u89c1 \u8fc7\u4e00\u822c\uff0c\u53ef\u53c8\u4e0d\u50cf\u662f\u5f20\u4e39\u67ab\u7684\uff01 \u3000\u3000\u4e91\u857e\u8fd9\u4e00\u51fa\u795e\u5bfb\u601d\uff0c\u90a3\u51e0\u540d\u968f\u6765\u7684\u8499\u53e4\u6b66\u58eb\u548c\u5fa1\u6797\u519b\u5b98\u5df2\u662f\u4e00\u9f50\u60ca\u8d77\u6251\u51fa\uff0c\u4e91\u857e\u773c\u775b\u4e00 \u77a5\uff0c\u53ea\u89c1\u6fb9\u53f0\u706d\u660e\u5411\u7740\u81ea\u5df1\u85cf\u8eab\u4e4b\u5904\u53d1\u7b11\uff01\u4e91\u857e\u5403\u4e86\u4e00\u60ca\uff0c\u9669\u9669\u8dcc\u5012\uff0c\u53ea\u542c\u5f97\u90a3\u4e9b\u8499\u53e4\u6b66\u58eb \u7eb7\u7eb7\u95ee\u9053\uff1a\u201c\u8d3c\u4eba\u5462\uff1f\u201d\u6fb9\u53f0\u706d\u660e\u7a81\u7136\u4e00\u4e2a\u65cb\u8eab\uff0c\u5411\u4e91\u857e\u76f8\u53cd\u7684\u65b9\u5411\u53d1\u4e86\u4e00\u652f\u54cd\u7bad\uff0c\u8bf4\u9053\uff1a \u201c\u8d3c\u4eba\u515a\u7fbd\u751a\u591a\uff0c\u7559\u4e0b\u4e24\u4eba\u62a4\u536b\u738b\u7237\uff0c\u5176\u4f59\u7684\u968f\u6211\u53bb\u8ffd\uff01\u201d \u3000\u3000\u8fd9\u4e00\u4e0b\u5927\u51fa\u4e91\u857e\u610f\u6599\u4e4b\u5916\uff0c\u6fb9\u53f0\u706d\u660e\u5206\u660e\u662f\u5df2\u53d1\u73b0\u81ea\u5df1\uff0c\u4f55\u4ee5\u53c8\u5c06\u540c\u4f34\u5f15\u5f00\uff1f\u771f\u662f\u767e\u601d \u4e0d\u89e3\u3002\u8fd9\u65f6\u5e97\u5185\u4e71\u6210\u4e00\u7247\uff0c\u4e91\u857e\u6084\u6084\u6e9c\u4e86\u4e0b\u6765\uff0c\u53ea\u89c1\u90a3\u5e97\u5c0f\u4e8c\u7ad9\u5728\u6697\u89d2\uff0c\u5411\u5979\u62db\u624b\u3002\u4e91\u857e\u8d70 \u4e86\u8fc7\u53bb\uff0c\u90a3\u5e97\u5c0f\u4e8c\u9053\uff1a\u201c\u5feb\u968f\u6211\u6765\uff0c\u8d81\u4e71\u9003\u8dd1\u3002\u201d\u4e91\u857e\u968f\u4ed6\u6e9c\u51fa\u540e\u9762\u6697\u95e8\uff0c\u5374\u559c\u65e0\u4eba\u77e5\u6653\u3002 \u3000\u3000\u5c0f\u57ce\u57ce\u95e8\u6ca1\u6709\u5173\u95f8\uff0c\u90a3\u5e97\u5c0f\u4e8c\u4e00\u76f4\u5c06\u5979\u5e26\u5230\u57ce\u5916\u4e00\u4e2a\u571f\u5c97\u9053\uff1a\u201c\u4e94\u66f4\u65f6\u5206\uff0c\u6709\u4eba\u6765 \u63a5\u3002\u201d\u4e91\u857e\u677e\u4e86\u53e3\u6c14\uff0c\u9053\u58f0\uff1a\u201c\u597d\u9669\u554a\uff01\u201d\u6708\u8272\u661f\u5149\u4e4b\u4e0b\uff0c\u53ea\u89c1\u90a3\u5e97\u5c0f\u4e8c\u9732\u51fa\u8be1\u79d8\u7684\u7b11\u5bb9\uff0c \u8bf4\u9053\uff1a\u201c\u77f3\u59d1\u5a18\u4ea4\u5f85\u53eb\u4e91\u76f8\u516c\u5e26\u90a3\u679d\u73ca\u745a\u89c1\u5979\uff0c\u76f8\u516c\u53ef\u85cf\u597d\u6ca1\u6709\uff1f\u201d \u3000\u3000\u4e91\u857e\u597d\u4e0d\u5fc3\u70e6\uff0c\u60f3\u9053\uff1a\u201c\u6b63\u662f\u4e00\u6ce2\u672a\u5e73\uff0c\u4e00\u6ce2\u53c8\u8d77\u3002\u201d\u542d\u58f0\u95f7\u6c14\u7b54\u9053\uff1a\u201c\u77e5\u9053\u4e86\uff01\u201d\u5e97 \u5c0f\u4e8c\u89c1\u5979\u8272\u53d8\uff0c\u4e0d\u6562\u53d6\u7b11\u3002\u7ea6\u6478\u8fc7\u4e86\u534a\u4e2a\u66f4\u6b21\uff0c\u53ea\u89c1\u4e24\u9a91\u9a6c\u75be\u5954\u800c\u6765\uff0c\u4e00\u9a91\u6709\u4eba\uff0c\u4e00\u9a91\u5374\u662f \u7a7a\u9a91\uff0c\u8fd1\u524d\u4e00\u770b\uff0c\u539f\u6765\u662f\u706b\u795e\u5f39\u90dd\u5b9d\u693f\u3002 \u3000\u3000\u706b\u795e\u5f39\u90dd\u5b9d\u693f\u5bf9\u5f20\u4e39\u67ab\u654c\u610f\u6700\u6d53\uff0c\u4e91\u857e\u5bf9\u4ed6\u5b9e\u662f\u65e0\u597d\u611f\uff0c\u4f46\u6b64\u9645\u52ab\u540e\u91cd\u9022\uff0c\u5374\u4e5f\u611f\u5230\u559c \u60a6\u3002\u90dd\u5b9d\u693f\u62b1\u62f3\u65bd\u793c\u95ee\u9053\uff1a\u201c\u4f60\u4e5f\u9003\u51fa\u6765\u4e86\uff1f\u90a3\u767d\u9a6c\u5c0f\u8d3c\u5462\uff1f\u90a3\u65e5\u5b98\u519b\u662f\u4e0d\u662f\u4ed6\u5e26\u9886\u6765 \u7684\uff1f\u201d\u4e91\u857e\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u662f\u4ed6\u820d\u547d\u6551\u4e86\u6bd5\u8001\u82f1\u96c4\uff0c\u6bd5\u524d\u8f88\u6ca1\u5bf9\u4f60\u8bf4\u8d77\u4e48\uff1f\u201d\u90dd\u5b9d\u693f\u4e00\u6115\uff0c \u9053\uff1a\u201c\u771f\u6709\u6b64\u4e8b\uff1f\u6211\u8fd8\u672a\u89c1\u7740\u6bd5\u8001\u82f1\u96c4\uff0c\u77f3\u59d1\u5a18\u53eb\u6211\u9886\u4f60\u53bb\u540e\uff0c\u518d\u9a6c\u4e0a\u53bb\u627e\u4ed6\u3002\u201d\u4e91\u857e\u9053\uff1a \u201c\u6bd5\u8001\u82f1\u96c4\u73b0\u5728\u4f55\u65b9\uff1f\u201d\u90dd\u5b9d\u693f\u9053\uff1a\u201c\u542c\u77f3\u59d1\u5a18\u8bf4\uff0c\u6bd5\u8001\u82f1\u96c4\u8131\u9669\u4e4b\u540e\uff0c\u5168\u5bb6\u5728\u996e\u9a6c\u5ddd\u84dd\u5929 \u77f3\u7684\u8001\u5bb6\u5b89\u6b47\uff0c\u79bb\u6b64\u4e0d\u8fc7\u5341\u6765\u91cc\u8def\u3002\u55ef\uff0c\u4e1c\u65b9\u5c06\u767d\uff0c\u54b1\u4eec\u8be5\u8d76\u8def\u5566\uff01\u201d \u3000\u3000\u90dd\u5b9d\u693f\u8bf7\u4e91\u857e\u4e0a\u9a6c\uff0c\u81ea\u5df1\u5728\u524d\u5f15\u8def\uff0c\u9a6c\u884c\u751a\u5feb\uff0c\u9ece\u660e\u65f6\u5206\u5230\u4e86\u4e00\u5904\u5c71\u8c37\u3002\u90dd\u5b9d\u693f\u9053\uff1a \u201c\u8fd9\u5c31\u662f\u9752\u9f99\u5ce1\u4e86\u3002\u201d\u957f\u5578\u4e09\u58f0\uff0c\u53ea\u542c\u5f97\u91cc\u9762\u4e5f\u6709\u4eba\u53d1\u58f0\u76f8\u5e94\uff0c\u90dd\u5b9d\u693f\u9053\uff1a\u201c\u77f3\u59d1\u5a18\u5df2\u5148\u6765 \u4e86\uff0c\u4f60\u8fdb\u53bb\u5427\uff0c\u6211\u8fd8\u8981\u53bb\u89c1\u9707\u4e09\u754c\u6bd5\u8001\u82f1\u96c4\u3002\u201d \u3000\u3000\u4e91\u857e\u5f03\u9a6c\u5165\u5c71\uff0c\u4e0d\u4e00\u523b\uff0c\u5c71\u5773\u5904\u8f6c\u51fa\u4e00\u4eba\uff0c\u6b63\u662f\u77f3\u7fe0\u51e4\u3002\u53ea\u89c1\u5979\u6cea\u6c34\u6ee1\u9762\uff0c\u75be\u5954\u4e0a\u6765\uff0c \u4e00\u628a\u62b1\u7740\u4e91\u857e\u9053\uff1a\u201c\u54b1\u4eec\u53c8\u89c1\u7740\u4e86\uff01\u201d\u4e91\u857e\u6276\u5979\u8f7b\u8f7b\u5750\u4e0b\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u7edd\u65e9\u7ea6\u6211\u4f1a\u9762\uff0c\u60f3\u6765 \u4e0d\u53ea\u662f\u4e3a\u4e86\u8c08\u60c5\u3002\u201d\u77f3\u7fe0\u51e4\u8584\u6012\u542b\u55d4\uff0c\u6a2a\u5979\u4e00\u773c\uff0c\u62b9\u62b9\u773c\u6cea\uff0c\u8bf4\u9053\uff1a\u201c\u8001\u5929\u4fdd\u4f51\uff0c\u54b1\u4eec\u5e78\u800c \u91cd\u89c1\uff0c\u53ef\u662f\u5468\u5927\u54e5\uff0c\u5468\u5927\u54e5\u2026\u2026\u201d\u4e91\u857e\u60ca\u9053\uff1a\u201c\u5468\u5927\u54e5\u600e\u4e48\u5566\uff1f\u201d\u77f3\u7fe0\u51e4\u5ffd\u9053\uff1a\u201c\u6211\u9519\u602a\u4f60 \u7684\u4e49\u5144\u4e86\uff0c\u5468\u5927\u54e5\u5b9e\u662f\u597d\u4eba\uff01\u201d\u4e91\u857e\u6025\u9053\uff1a\u201c\u5feb\u8bf4\uff0c\u5468\u5927\u54e5\u4ed6\u600e\u4e48\u5566\uff1f\u201d\u77f3\u7fe0\u51e4\u9053\uff1a\u201c\u90a3\u65e5 \u4f60\u5815\u9a6c\u53d7\u56f4\uff0c\u54b1\u4eec\u60f3\u56de\u6765\u62a2\u6551\uff0c\u5df2\u88ab\u9694\u65ad\u3002\u540e\u6765\u90a3\u5f20\u98ce\u5e9c\u8ffd\u6bd5\u9053\u51e1\u4e0d\u4e0a\uff0c\u5374\u622a\u7740\u6211\u4e0e\u5468\u5927\u54e5 \u4e8c\u4eba\u3002\u6211\u4eec\u4e8c\u4eba\u4e0d\u662f\u4ed6\u7684\u5bf9\u624b\uff0c\u5341\u4f59\u62db\u540e\uff0c\u6211\u88ab\u4ed6\u5200\u80cc\u4e00\u62cd\uff0c\u6253\u843d\u9a6c\u80cc\uff0c\u773c\u770b\u5c31\u8981\u88ab\u4ed6\u6240 \u64d2\uff0c\u5e78\u5f97\u5468\u5927\u54e5\u820d\u8eab\u76f8\u6551\uff0c\u4e00\u8dc3\u4e0b\u5730\uff0c\u7adf\u5192\u7740\u88ab\u9a6c\u8e44\u8df5\u8e0f\u4e4b\u5371\uff0c\u62d6\u7740\u5f20\u98ce\u5e9c\u7684\u540e\u817f\u72e0\u547d\u4fbf \u54ac\uff0c\u5f20\u98ce\u5e9c\u4e00\u5200\u5c06\u4ed6\u62cd\u6655\uff0c\u6293\u4e0a\u9a6c\u80cc\uff0c\u5927\u7ea6\u662f\u8d76\u7740\u56de\u53bb\u6cbb\u4f24\uff0c\u4fbf\u4e0d\u987e\u5f97\u518d\u8ffd\u6211\u4e86\u3002\u201d \u3000\u3000\u4e91\u857e\u4e0e\u5468\u5c71\u6c11\u4e4b\u95f4\uff0c\u867d\u66fe\u95f9\u8fc7\u4e0d\u6109\u5feb\u7684\u4e8b\u60c5\uff0c\u5374\u662f\u60c5\u5982\u9aa8\u8089\uff0c\u95fb\u8a00\u6025\uff0c\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u53ef \u5f97\u60f3\u6cd5\u6551\u4ed6\u624d\u662f\u3002\u201d\u77f3\u7fe0\u51e4\u9053\uff1a\u201c\u6211\u7ea6\u4f60\u5230\u6b64\uff0c\u5c31\u662f\u60f3\u6cd5\u6551\u4ed6\u5440\uff01\u4f60\u542c\u6211\u8bf4\uff0c\u8fd8\u6709\u4e00\u6869\u5947\u602a \u4e4b\u4e8b\u3002\u6211\u8131\u9669\u4e4b\u540e\uff0c\u524d\u65e5\u5728\u5609\u53bf\u4f4f\u5bbf\uff0c\u534a\u591c\u65f6\u5206\uff0c\u5ffd\u88ab\u4e00\u4e2a\u8499\u9762\u4eba\u60ca\u8d77\uff0c\u5c06\u6211\u5f15\u51fa\u90ca\u5916\uff0c\u770b \u4ed6\u8eab\u624b\u6b66\u529f\uff0c\u5728\u6211\u4e4b\u4e0a\uff0c\u5374\u53c8\u5e76\u4e0d\u5bf9\u6211\u4f24\u5bb3\u3002\u5f15\u5230\u90ca\u5916\uff0c\u4fbf\u81ea\u53bb\u4e86\u3002\u6211\u6ee1\u8179\u72d0\u7591\uff0c\u7b2c\u4e8c\u65e5\u624d \u77e5\u9053\u90a3\u665a\u5609\u57ce\u4e2d\uff0c\u5b98\u5dee\u6355\u5feb\u4e00\u9f50\u51fa\u52a8\uff0c\u534a\u591c\u641c\u67e5\u5ba2\u5e97\uff0c\u76d8\u95ee\u884c\u4eba\uff0c\u542c\u8bf4\u662f\u8981\u8fce\u63a5\u4ec0\u4e48\u8d35\u4eba\uff0c \u6240\u4ee5\u9884\u5148\u9632\u8303\u3002\u90a3\u4eba\u5f15\u6211\u8d70\u51fa\u5ba2\u5e97\uff0c\u60f3\u662f\u4e8b\u5148\u5f97\u77e5\u6d88\u606f\uff0c\u51fa\u4e8e\u4e00\u7247\u597d\u5fc3\u3002\u201d\u4e91\u857e\u5927\u662f\u5947\u602a\uff0c \u5583\u5583\u81ea\u8bed\u9053\uff1a\u201c\u8499\u9762\u4eba\uff0c\u8499\u9762\u4eba\uff1f\u4ed6\u7684\u8eab\u6bb5\u50cf\u4e0d\u50cf\u4ee5\u524d\u5077\u5165\u4f60\u5bb6\u4e2d\u7684\u90a3\u4e2a\u3001\u90a3\u4e2a\u767d\u9a6c\u4e66 \u751f\uff1f\u201d \u3000\u3000\u77f3\u7fe0\u51e4\u9053\uff1a\u201c\u9ed1\u591c\u4e4b\u4e2d\uff0c\u6211\u6ca1\u770b\u6e05\u3002\u518d\u8bf4\u6211\u4e5f\u4ece\u672a\u8054\u60f3\u5230\u90a3\u767d\u9a6c\u4e66\u751f\uff0c\u662f\u4ee5\u65e0\u4ece\u6bd4 \u8f83\u3002\u201d\u4e91\u857e\u4e0d\u89c9\u9762\u6cdb\u6843\u7ea2\uff0c\u9053\uff1a\u201c\u6211\u77e5\u9053\u5609\u53bf\u6240\u8981\u8fce\u63a5\u7684\u662f\u4ec0\u4e48\u8d35\u4eba\uff0c\u5c31\u662f\u90a3\u73ed\u8499\u53e4\u4eba\u3002\u53ea \u56e0\u5609\u53bf\u662f\u4e2a\u5927\u57ce\uff0c\u6240\u4ee5\u8981\u9884\u5148\u4e00\u65e5\u76d8\u67e5\u5ba2\u5e97\u3002\u201d\u77f3\u7fe0\u51e4\u5947\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\uff1f\u201d\u4e91\u857e\u9053\uff1a \u201c\u6628\u665a\u6211\u4e5f\u89c1\u7740\u90a3\u8499\u9762\u4eba\u4e86\u3002\u6b64\u4e8b\u4ee5\u540e\u518d\u8c08\uff0c\u4f60\u5148\u8bf4\u4f60\u7684\u3002\u201d\u77f3\u7fe0\u51e4\u9053\uff1a\u201c\u6628\u665a\u6211\u78b0\u5230\u4e86\u7239 \u7239\u7684\u670b\u53cb\uff0c\u5f97\u77e5\u9707\u4e09\u754c\u6bd5\u9053\u51e1\u4ea6\u5df2\u8131\u9669\uff0c\u6211\u4fbf\u53bb\u627e\u4ed6\uff0c\u8c01\u77e5\u4ed6\u4e5f\u89c1\u7740\u4e86\u90a3\u4e2a\u8499\u9762\u4eba\uff0c\u800c\u4e14\u8499 \u9762\u4eba\u8fd8\u7ed9\u4ed6\u7559\u4e0b\u4e86\u4e00\u5c01\u4fe1\u3002\u6bd5\u9053\u51e1\u8bf4\uff1a\u2018\u8fd9\u4eba\u771f\u50cf\u7b2c\u4e8c\u4e2a\u5f20\u4e39\u67ab\uff0c\u5374\u4e0d\u77e5\u662f\u4e0d\u662f\u4ed6\uff1f\u2019\u6bd5\u9053 \u51e1\u521a\u5230\u84dd\u5bb6\uff0c\u8499\u9762\u4eba\u4fbf\u73b0\u8ff9\u7559\u4e66\uff0c\u6bd5\u9053\u51e1\u56e0\u4e3a\u521a\u521a\u8131\u9669\uff0c\u56e0\u6b64\u4e5f\u5c31\u65e0\u5fc3\u8ffd\u4ed6\u4e86\u3002\u201d\u4e91\u857e\u9053\uff1a \u201c\u4fe1\u4e2d\u8bf4\u7684\u662f\u4ec0\u4e48\uff1f\u201d\u77f3\u7fe0\u51e4\u9053\uff1a\u201c\u90a3\u8499\u9762\u4eba\u7684\u4fe1\u4e2d\u8bf4\u9053\uff1a\u2018\u6211\u77e5\u9053\u74e6\u523a\u4f7f\u81e3\u524d\u5f80\u5317\u4eac\uff0c\u4e3a \u9996\u7684\u662f\u4e2a\u4eb2\u738b\uff0c\u5927\u7ea6\u662f\u5411\u660e\u671d\u63d0\u51fa\u4ec0\u4e48\u6761\u4ef6\u53bb\u7684\uff0c\u5927\u660e\u5e1d\u56fd\u4e0e\u74e6\u523a\u90a6\u4ea4\u867d\u662f\u6fd2\u4e8e\u7834\u88c2\uff0c\u5927\u660e \u5929\u5b50\u53ef\u8fd8\u60f3\u6781\u529b\u5f25\u7f1d\u3002\u662f\u4ee5\u5bf9\u74e6\u523a\u8fd9\u73ed\u4f7f\u8005\u6781\u662f\u5949\u8fce\uff0c\u4fdd\u62a4\u552f\u6050\u4e0d\u5468\u3002\u2019\u4ed6\u4fe1\u4e2d\u53c8\u8bf4\uff0c\u5df2\u77e5 \u9053\u5468\u5927\u54e5\u843d\u5728\u5b98\u519b\u624b\u91cc\uff0c\u662f\u4ee5\u5efa\u8bae\u6211\u4eec\u5192\u9669\u53bb\u622a\u8fd9\u6279\u8499\u53e4\u4eba\uff0c\u82e5\u80fd\u64d2\u5230\u756a\u738b\uff0c\u90a3\u5c31\u66f4\u662f\u4e00\u4e3e \u4e24\u5f97\u3002\u4e00\u8005\u53ef\u4ee5\u62ff\u6765\u4ea4\u6362\u5468\u5927\u54e5\uff0c\u4e8c\u8005\u662f\u514d\u5f97\u671d\u5ef7\u5411\u74e6\u523a\u4f4e\u9996\u6c42\u548c\u3002\u4fe1\u4e2d\u8fd8\u8bf4\uff0c\u9752\u9f99\u5ce1\u5730\u5f62 \u6700\u9669\uff0c\u53ef\u4ee5\u5728\u6b64\u5730\u4f0f\u51fb\uff0c\u5230\u65f6\u4ed6\u6216\u8005\u4e5f\u53ef\u76f8\u52a9\u4e00\u81c2\u4e4b\u529b\u3002\u201d\u4e91\u857e\u9053\uff1a\u201c\u6bd5\u8001\u82f1\u96c4\u610f\u601d\u5982 \u4f55\uff1f\u201d\u77f3\u7fe0\u51e4\u9053\uff1a\u201c\u6bd5\u9053\u51e1\u77e5\u9053\u5468\u5927\u54e5\u88ab\u64d2\uff0c\u4ea6\u662f\u7126\u6025\u975e\u5e38\uff0c\u4f46\u82e5\u8981\u518d\u4f20\u7eff\u6797\u7bad\uff0c\u5e7f\u7ea6\u5404\u8def \u82f1\u96c4\uff0c\u5374\u662f\u8fdc\u6c34\u4e0d\u6551\u8fd1\u706b\u3002\u6bd5\u9053\u51e1\u60f3\u4e0d\u51fa\u522b\u7684\u6cd5\u5b50\uff0c\u56e0\u6b64\u4e5f\u613f\u7167\u90a3\u8499\u9762\u4eba\u6240\u8bf4\u5192\u9669\u4e00\u8bd5\u3002\u4ed6 \u53eb\u6211\u4eec\u8f6e\u6d41\u5728\u6b64\u4e86\u671b\uff0c\u4ee5\u9632\u610f\u5916\u3002\u7b49\u4e0b\u4ed6\u4eb2\u81ea\u7387\u4eba\u524d\u6765\u3002\u201d \u3000\u3000\u4e91\u857e\u6c89\u541f\u4e0d\u8bed\uff0c\u60f3\u90a3\u6fb9\u53f0\u706d\u660e\u52c7\u731b\u65e0\u6bd4\uff0c\u52ab\u4eba\u4e4b\u8ba1\u53ea\u6050\u96be\u884c\u3002\u5ffd\u542c\u5f97\u77f3\u7fe0\u51e4\u9053\uff1a\u201c\u90a3\u5e97 \u5c0f\u4e8c\u53ef\u5c06\u73ca\u745a\u4ea4\u4e0e\u4f60\u4e86\uff1f\u201d\u4e91\u857e\u9053\uff1a\u201c\u4ea4\u4e86\u3002\u201d\u77f3\u7fe0\u51e4\u9053\uff1a\u201c\u8d81\u7740\u65f6\u5019\u672a\u81f3\uff0c\u6211\u53ef\u8981\u95ee\u4f60\u4e00 \u4ef6\u4e8b\u3002\u201d\u4e91\u857e\u9053\uff1a\u201c\u4f55\u4e8b\uff1f\u201d\u77f3\u7fe0\u51e4\u9053\uff1a\u201c\u4e00\u8def\u524d\u6765\uff0c\u4f60\u5bf9\u6211\u5982\u4f55\uff0c\u4f60\u81ea\u5df1\u5fc3\u91cc\u77e5\u9053\u3002\u54b1\u4eec \u867d\u662f\u6302\u540d\u592b\u59bb\uff0c\u5176\u5b9e\u4f60\u4f55\u66fe\u5c06\u6211\u4f5c\u59bb\u5b50\u770b\u5f85\uff1f\u201d\u4e91\u857e\u6025\u9053\uff1a\u201c\u8fd9\u4e2a\u65f6\u5019\u8bf4\u8fd9\u4e2a\u5e72\u561b\uff1f\u201d\u7fe0\u51e4 \u9053\uff1a\u201c\u6211\u95f7\u4e86\u591a\u65e5\u5566\uff0c\u6211\u662f\u6025\u6027\u513f\uff0c\u6b64\u4e8b\u4e0d\u80fd\u4e0d\u95ee\u6e05\u695a\u3002\u201d\u4e91\u857e\u62ff\u5979\u65e0\u6cd5\uff0c\u89c1\u671d\u9633\u5df2\u51fa\uff0c\u6599 \u90a3\u6279\u8499\u53e4\u4f7f\u8005\u5373\u5c06\u6765\u5230\uff0c\u66f4\u662f\u65e0\u5fc3\u4e0e\u5979\u7ea0\u7f20\uff0c\u773c\u73e0\u4e00\u8f6c\uff0c\u5ffd\u5730\u7b11\u9053\uff1a\u201c\u51e4\u59d0\u59d0\u6211\u660e\u767d\u4f60\u7684\u610f \u601d\u5566\u3002\u4f60\u53eb\u5e97\u5c0f\u4e8c\u5c06\u73ca\u745a\u7559\u4ea4\u4e0e\u6211\uff0c\u4e43\u662f\u2026\u2026\u201d\u6545\u4f5c\u731c\u5ea6\u4e4b\u72b6\uff0c\u77f3\u7fe0\u51e4\u63a5\u53e3\u8bf4\u9053\uff1a\u201c\u4e43\u662f\u60f3 \u95ee\u660e\u4f60\u7684\u5fc3\u610f\u3002\u4f60\u82e5\u4e0d\u559c\u6b22\u6211\uff0c\u8fd9\u73ca\u745a\u4f60\u6536\u4e0b\u6765\u518d\u9001\u7ed9\u522b\u4eba\u3002\u4f60\u82e5\u2026\u2026\u201d\u4e91\u857e\u4e5f\u622a\u4f4f\u5979\u7684\u8bdd \u9053\uff1a\u201c\u51e4\u59d0\u59d0\uff0c\u8fd9\u679d\u73ca\u745a\u662f\u6211\u7ed9\u4f60\u7684\u8058\u793c\uff0c\u5c82\u80fd\u518d\u9001\u522b\u4eba\u3002\u6211\u73b0\u5728\u518d\u4eb2\u624b\u4ea4\u4e0e\u4f60\u5566\uff01\u201d\u77f3\u7fe0 \u51e4\u82b3\u5fc3\u5927\u6170\uff0c\u63a5\u8fc7\u73ca\u745a\uff0c\u53ea\u542c\u5f97\u4e91\u857e\u597d\u50cf\u6f2b\u4e0d\u7ecf\u610f\u5730\u8bf4\u9053\uff1a\u201c\u55ef\uff0c\u5468\u5927\u54e5\u5b9e\u662f\u597d\u4eba\uff0c\u6211\u7684\u8bdd \u53ef\u6ca1\u6709\u534a\u70b9\u9a97\u4f60\u3002\u201d\u77f3\u7fe0\u51e4\u4e00\u6014\uff0c\u4f4e\u5934\u770b\u89c1\u90a3\u679d\u73ca\u745a\u7b2c\u4e09\u74e3\u82b1\u53f6\u4e0a\u6240\u523b\u7684\u201c\u5468\u201d\u5b57\uff0c\u9762\u8272\u4e00 \u53d8\uff0c\u6b63\u6b32\u8bf4\u8bdd\uff0c\u53ea\u542c\u5f97\u5ce1\u8c37\u5916\u9a6c\u58f0\u5636\u9e23\uff0c\u4e00\u884c\u4eba\u8d70\u4e86\u5165\u6765\u3002 \u3000\u3000\u4e91\u857e\u4e0e\u77f3\u7fe0\u51e4\u9690\u8eab\u77f3\u7b0b\u4e4b\u540e\uff0c\u53ea\u89c1\u4e00\u5c0f\u961f\u5b98\u5175\u5728\u524d\u5f00\u8def\uff0c\u90a3\u8499\u53e4\u756a\u738b\u4e0e\u6fb9\u53f0\u706d\u660e\u5e76\u9a6c\u800c \u884c\uff0c\u8d70\u8fdb\u5c71\u8c37\u3002\u77f3\u7fe0\u51e4\u6084\u58f0\u8bf4\uff1a\u201c\u7cdf\u7cd5\uff0c\u4ed6\u4eec\u8fd9\u6837\u65e9\u4fbf\u6765\u4e86\u3002\u6bd5\u9053\u51e1\u53ef\u8fd8\u6ca1\u6765\u5462\u3002\u201d\u90a3\u756a\u738b \u63fd\u8f94\u626c\u97ad\u987e\u76fc\u81ea\u96c4\uff0c\u5ffd\u542c\u5f97\u6709\u4eba\u5531\u7740\u8499\u53e4\u6c11\u6b4c\u8fce\u9762\u800c\u6765\u3002\u6b4c\u9053\uff1a \u3000\u3000\u6211\u662f\u8349\u539f\u7684\u5140\u9e70\uff0c \u3000\u3000\u6211\u7684\u7fc5\u8180\u6247\u98ce\u4e91\uff0c \u3000\u3000\u671d\u98de\u65a1\u96be\u6cb3\uff0c \u3000\u3000\u591c\u5bbf\u5580\u6797\u57ce\uff0c \u3000\u3000\u98de\u4e86\u4e09\u4e2a\u6708\uff0c \u3000\u3000\u98de\u4e0d\u51fa\u5927\u6c57\u7684\u624b\u5fc3\uff01 \u3000\u3000\u8fd9\u9996\u6c11\u6b4c\uff0c\u4e43\u662f\u8499\u53e4\u4eba\u6b4c\u9882\u4ed6\u4eec\u7684\u82f1\u96c4\u6210\u5409\u601d\u6c57\u7684\uff0c\u756a\u738b\u542c\u4e86\uff0c\u5927\u4e3a\u9ad8\u5174\uff0c\u60f3\u4e0d\u5230\u6b64\u5730 \u9047\u5230\u672c\u56fd\u4e4b\u4eba\u3002\u800c\u53c8\u542c\u5230\u8fd9\u9996\u8499\u53e4\u4eba\u6700\u5f15\u4ee5\u4e3a\u8363\u7684\u6b4c\u8bcd\uff0c\u4fbf\u505c\u4e0b\u9a6c\u6765\uff0c\u5bf9\u6fb9\u53f0\u706d\u660e\u7b11\u9053\uff1a \u201c\u91cd\u632f\u5927\u6c57\u7684\u5a01\u98ce\uff0c\u53ef\u5f97\u8981\u770b\u6211\u4eec\u4e86\u3002\u201d\u53eb\u4eba\u8bf7\u90a3\u201c\u8499\u53e4\u4eba\u201d\u524d\u6765\u76f8\u89c1\u3002\u53ea\u542c\u90a3\u4eba\u53c8\u5531\u9053\uff1a \u3000\u3000\u5927\u6c57\u53ea\u624b\u8986\u5927\u5730\uff0c \u3000\u3000\u4ed6\u7684\u751f\u524d\u4eab\u8363\u540d\uff0c \u3000\u3000\u800c\u4eca\u6b7b\u4e86\u5f52\u9ec4\u571f\uff0c \u3000\u3000\u5360\u5730\u4e0d\u8fc7\u662f\u4e00\u575f\u3002 \u3000\u3000\u8fd9\u51e0\u53e5\u6b4c\u8bcd\u867d\u7528\u8499\u53e4\u8bdd\u5531\u51fa\uff0c\u5374\u662f\u4ed6\u81ea\u5df1\u7f16\u7684\uff0c\u756a\u738b\u542c\u4e86\u9762\u8272\u4e00\u53d8\uff0c\u5f85\u4ed6\u8fd1\u524d\uff0c\u7acb\u5373\u95ee \u9053\uff1a\u201c\u4f60\u662f\u8499\u53e4\u6765\u7684\u5417\uff1f\u8fd9\u652f\u6b4c\u540e\u534a\u622a\u6211\u6ca1\u542c\u8fc7\uff0c\u4f60\u662f\u4ece\u54ea\u513f\u542c\u6765\u7684\uff1f\u201d\u90a3\u4eba\u5934\u6234\u8499\u53e4\u6be1 \u5e3d\uff0c\u6cbf\u5e3d\u4e24\u5757\u7f8a\u76ae\u5782\u4e86\u4e0b\u6765\uff0c\u63a9\u4e86\u4e24\u8fb9\u9762\u5b54\uff0c\u53ea\u9732\u51fa\u53e3\u9f3b\u548c\u4e00\u53cc\u70af\u70af\u6709\u795e\u7684\u5927\u773c\u775b\uff0c\u8fd9\u662f\u8499 \u53e4\u7267\u4eba\u7684\u666e\u901a\u670d\u9970\uff0c\u53ef\u662f\u5728\u8fd9\u6625\u98ce\u9a80\u8361\u7684\u4e2d\u539f\u4e4b\u5730\uff0c\u5374\u663e\u5f97\u4e0d\u4f26\u4e0d\u7c7b\uff0c\u5341\u5206\u602a\u5f02\u3002\u90a3\u4eba\u54c8\u8170 \u8bf4\u9053\uff1a\u201c\u662f\u6211\u7279\u5730\u7f16\u6765\u5531\u7ed9\u4f60\u542c\u7684\u3002\u201d\u624b\u8155\u4e00\u7ffb\uff0c\u767b\u65f6\u6293\u7740\u4e86\u90a3\u756a\u738b\u7684\u5bf8\u5173\u5c3a\u8109\u95e8\u4e09\u5bf8\u4e4b \u5904\u3002 \u3000\u3000\u6fb9\u53f0\u706d\u660e\u65e9\u6709\u9632\u5907\uff0c\u53ea\u89c1\u4ed6\u624b\u8098\u4e00\u649e\uff0c\u90a3\u4eba\u62d6\u7740\u756a\u738b\u6eda\u5730\u4fbf\u4fbf\uff0c\u624b\u6307\u6263\u5b9e\uff0c\u4ecd\u662f\u4e0d\u80af\u653e \u677e\u3002\u6fb9\u53f0\u706d\u660e\u51fa\u624b\u5982\u98ce\uff0c\u98de\u8d77\u4e00\u811a\uff0c\u8e22\u4ed6\u8170\u80c1\uff0c\u53f3\u624b\u5f80\u4e0b\u4e00\u6293\uff0c\u90a3\u4eba\u5c31\u5730\u4e00\u6eda\uff0c\u907f\u5f00\u4e86\u6fb9\u53f0 \u706d\u660e\u7684\u98de\u811a\uff0c\u6fb9\u53f0\u706d\u660e\u7684\u957f\u81c2\u5df2\u6293\u5230\u4ed6\u7684\u5934\u9888\uff0c\u90a3\u756a\u738b\u6b66\u529f\u4e0d\u5f31\u8d81\u8fd9\u65f6\u673a\uff0c\u5de6\u624b\u53cd\u51fb\uff0c\u819d\u76d6 \u53c8\u9876\u4ed6\u5c0f\u8179\u3002\u90a3\u4eba\u80cc\u8179\u53d7\u654c\uff0c\u8feb\u5f97\u53cc\u624b\u4e00\u677e\uff0c\u8dc3\u4e86\u8d77\u6765\uff0c\u63a5\u4e86\u6fb9\u53f0\u706d\u660e\u4e00\u638c\uff0c\u8eab\u8eaf\u867d\u7ed9\u9707\u5f97 \u6447\u6447\u6643\u6643\uff0c\u53ef\u662f\u624b\u5e95\u5374\u6beb\u4e0d\u653e\u677e\uff0c\u547c\u547c\u547c\u63a5\u8fde\u62cd\u51fa\u4e09\u638c\uff0c\u7adf\u7136\u6562\u4e0e\u6fb9\u53f0\u706d\u660e\u25a1\u62fc\u3002\u65bd\u5c55\u7684\u62db \u6570\u7adf\u662f\u5916\u5bb6\u62f3\u767b\u5cf0\u9020\u6781\u7684\u4e00\u79cd\u529f\u592b\uff1a\u5927\u529b\u91d1\u521a\u91cd\u624b\u6cd5\uff01 \u3000\u3000\u4e91\u857e\u60ca\u5947\u4e0d\u5df2\uff0c\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u90a3\u4e2a\u8499\u9762\u4eba\uff01\u201d\u9762\u76ee\u867d\u770b\u4e0d\u6e05\uff0c\u5374\u4f3c\u662f\u89c1\u8fc7\u7684\u719f\u4eba\uff0c\u53ef\u53c8 \u60f3\u4e0d\u8d77\u6765\uff0c\u53ea\u89c1\u6fb9\u53f0\u706d\u660e\u6377\u6b65\u4f3c\u733f\u7334\uff0c\u51fa\u62f3\u5982\u864e\u8c79\uff0c\u5c06\u4ed6\u903c\u5f97\u6b65\u6b65\u540e\u9000\u3002\u4f46\u4ed6\u638c\u98ce\u864e\u864e\uff0c\u6216 \u6309\u6216\u5288\u6216\u6233\uff0c\u6bcf\u4e00\u62db\u4e5f\u90fd\u72e0\u8fa3\u975e\u5e38\uff0c\u867d\u7136\u7ed9\u903c\u5f97\u6b65\u6b65\u540e\u9000\uff0c\u5374\u662f\u6b65\u4f10\u4e0d\u4e71\u3002\u4e91\u857e\u5fc3\u60f3\uff1a\u201c\u6b64 \u4eba\u770b\u6765\u4e0d\u50cf\u662f\u5f20\u4e39\u67ab\uff0c\u53ef\u662f\u80fd\u4e0e\u6fb9\u53f0\u706d\u660e\u7528\u771f\u529b\u25a1\u62fc\u4e86\u8fd9\u4e48\u591a\u62db\uff0c\u6b66\u529f\u4ea6\u4e0d\u5728\u5f20\u4e39\u67ab\u4e4b \u4e0b\u3002\u201d\u53c8\u60f3\u9053\uff1a\u201c\u6fb9\u53f0\u706d\u660e\u6628\u665a\u653e\u4ed6\u9003\u8d70\uff0c\u4f55\u4ee5\u5982\u4eca\u53c8\u6b7b\u529b\u4fdd\u62a4\u90a3\u756a\u738b\u5462\uff1f\u201d\u5b9e\u662f\u4e0d\u89e3\u3002 \u3000\u3000\u6fb9\u53f0\u706d\u660e\u62a2\u4e86\u4e0a\u98ce\uff0c\u6b65\u6b65\u8fdb\u903c\uff0c\u8499\u53e4\u6b66\u58eb\u7d20\u6765\u77e5\u9053\u6fb9\u53f0\u706d\u660e\u7684\u5389\u5bb3\uff0c\u4ece\u4e0d\u8981\u4eba\u76f8\u5e2e\uff0c\u6709 \u4e24\u4e2a\u5fa1\u6797\u519b\u5b98\u5374\u60f3\u8ba8\u597d\uff0c\u5de6\u53f3\u5206\u4e0a\uff0c\u65bd\u884c\u5077\u88ad\uff0c\u6fb9\u53f0\u706d\u660e\u7a81\u7136\u505c\u624b\uff0c\u5927\u53eb\uff1a\u201c\u6eda\u5f00\uff01\u201d\u8bf4\u65f6 \u8fdf\u90a3\u65f6\u5feb\uff0c\u90a3\u4eba\u8d81\u7740\u6fb9\u53f0\u706d\u660e\u505c\u624b\u7684\u970e\u90a3\uff0c\u5df2\u7a81\u7528\u5927\u529b\u91d1\u521a\u624b\u6cd5\u5c06\u4e24\u540d\u519b\u5b98\u90fd\u7529\u4e0b\u5c71\u8c37\uff0c\u4e00 \u7ffb\u8eab\u53c8\u4e0e\u6fb9\u53f0\u706d\u660e\u76f8\u6597\uff01 \u3000\u3000\u6597\u4e86\u6570\u62db\uff0c\u6fb9\u53f0\u706d\u660e\u5927\u559d\u4e00\u58f0\uff0c\u6251\u817e\u4e00\u62f3\uff0c\u5728\u4ed6\u80a9\u5934\u91cd\u91cd\u51fb\u4e86\u4e00\u4e0b\uff0c\u90a3\u4eba\u5012\u8dc3\u4e00\u4e08\u5f00\u5916 \u6447\u6447\u6b32\u5012\u3002\u6fb9\u53f0\u706d\u660e\u4f4f\u624b\u5927\u7b11\uff0c\u8499\u53e4\u6b66\u58eb\u56f4\u4e0a\u524d\u6765\uff0c\u5c06\u4ed6\u64d2\u6349\u3002\u5ffd\u542c\u5f97\u4e00\u58f0\u547c\u558a\uff0c\u8fce\u9762\u6740\u51fa \u6570\u4eba\uff0c\u524d\u961f\u5b98\u519b\u767b\u65f6\u5927\u4e71\uff0c\u6b63\u662f\u6bd5\u9053\u51e1\u4e0e\u84dd\u5929\u77f3\u3001\u90dd\u5b9d\u693f\u7b49\u4eba\u6740\u4e86\u524d\u6765\uff0c\u5b98\u519b\u62b5\u6321\u4e0d\u4f4f\uff01 \u3000\u3000\u6fb9\u53f0\u706d\u660e\u4e00\u8dc3\u4e0a\u524d\uff0c\u5de6\u62f3\u53f3\u62f3\uff0c\u4e00\u62db\u201c\u6a2a\u4e91\u65ad\u5cf0\u201d\uff0c\u62f3\u638c\u517c\u65bd\uff0c\u62f3\u51fb\u524d\u5fc3\uff0c\u638c\u5288\u9888\u9879\uff0c \u5411\u6bd5\u9053\u51e1\u4fbf\u4e0b\u6740\u624b\u3002\u6bd5\u9053\u51e1\u964d\u9f99\u68d2\u6ef4\u6e9c\u6e9c\u4e00\u8f6c\uff0c\u68cd\u5c3e\u70b9\u4ed6\u7ae0\u95e8\u8981\u7a74\uff0c\u68cd\u5c16\u6233\u4ed6\u9762\u4e0a\u53cc\u775b\uff0c\u6bd5 \u9053\u51e1\u6709\u201c\u9707\u4e09\u754c\u201d\u4e4b\u540d\uff0c\u5728\u964d\u9f99\u68d2\u4e0a\u4e0b\u8fc7\u6570\u5341\u5e74\u529f\u592b\u4f55\u7b49\u5389\u5bb3\u3002\u6fb9\u53f0\u706d\u660e\u53eb\u58f0\uff1a\u201c\u597d\uff01\u201d\u9661 \u7136\u4e00\u7f29\uff0c\u53d8\u638c\u4fbf\u62ff\uff0c\u6bd5\u9053\u51e1\u6251\u4e86\u4e2a\u7a7a\uff0c\u51e0\u4e4e\u7ed9\u4ed6\u6293\u7740\u3002\u5e78\u800c\u529f\u592b\u8001\u5230\uff0c\u811a\u4e0b\u4f7f\u51fa\u201c\u5343\u65a4\u5760\u201d \u7684\u529f\u592b\uff0c\u7acb\u523b\u9489\u7262\u5730\u9762\uff0c\u6a2a\u68d2\u4e00\u626b\uff0c\u5c06\u6fb9\u53f0\u706d\u660e\u7684\u62db\u6570\u5316\u89e3\u5f00\u53bb\uff0c\u5fc3\u4e2d\u4e5f\u662f\u5927\u611f\u60ca\u5947\u3002 \u3000\u3000\u90a3\u4eba\u8d81\u7740\u6df7\u4e71\u4e4b\u9645\uff0c\u51fb\u5012\u6570\u4eba\uff0c\u6740\u51fa\u91cd\u56f4\u3002\u4e91\u857e\u4e00\u76b1\u7709\u5934\u751a\u89c9\u4e0d\u89e3\uff1a\u6b64\u4eba\u6709\u80c6\u6c14\u5b64\u8eab\u88ad \u51fb\u756a\u738b\uff0c\u4f55\u4ee5\u6b64\u9645\u6709\u4eba\u76f8\u5e2e\uff0c\u5374\u53c8\u72ec\u81ea\u9003\u8d70\uff1f\u90a3\u4eba\u75be\u8d70\u5982\u98ce\uff0c\u6070\u662f\u5bf9\u7740\u4e91\u857e\u85cf\u8eab\u7684\u65b9\u5411\u5954 \u6765\uff0c\u4e91\u857e\u84e6\u7136\u8dc3\u51fa\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u201d\u90a3\u4eba\u7adf\u7136\u5288\u9762\u4e00\u638c\u3002\u4e91\u857e\u95ea\u5f00\uff0c\u62d4\u51fa\u5b9d\u5251\uff0c\u53eb\u9053\uff1a \u201c\u4e0d\u52a9\u670b\u53cb\uff0c\u4e43\u662f\u4e0d\u4e49\uff0c\u54b1\u4eec\u518d\u6740\u5165\u53bb\u5427\uff01\u201d\u90a3\u4eba\u89c1\u4e86\u4e91\u857e\u62d4\u51fa\u9752\u51a5\u5b9d\u5251\uff0c\u53cc\u76ee\u95ea\u95ea\u53d1\u5149\uff0c \u5ffd\u7136\u4e5f\u62d4\u51fa\u4e00\u53e3\u5200\u6765\uff0c\u5411\u4e91\u857e\u5288\u9762\u4e00\u5200\uff0c\u8fd9\u4e00\u4e0b\u5927\u51fa\u4e91\u857e\u610f\u5916\uff0c\u5b9d\u5251\u5411\u4e0a\u4e00\u64a9\uff0c\u90a3\u4eba\u53ea\u53d1\u4e00 \u62db\uff0c\u7acb\u523b\u98de\u8eab\u4fbf\u8d70\u3002\u77f3\u7fe0\u51e4\u6251\u51fa\u6765\u9053\uff1a\u201c\u771f\u662f\u602a\u4eba\uff01\u201d\u4e91\u857e\u4e00\u77a5\u6218\u573a\u5f62\u52bf\uff0c\u9053\uff1a\u201c\u4e14\u83ab\u7ba1 \u4ed6\uff0c\u54b1\u4eec\u53bb\u52a9\u6bd5\u8001\u82f1\u96c4\u3002\u201d \u3000\u3000\u6fb9\u53f0\u706d\u660e\u7a7a\u624b\u4e0e\u6bd5\u9053\u51e1\u6597\u4e86\u5341\u6570\u62db\uff0c\u5404\u81ea\u8ba8\u4e0d\u4e86\u4fbf\u5b9c\u3002\u6fb9\u53f0\u706d\u660e\u53eb\u9053\uff1a\u201c\u597d\uff0c\u4f60\u662f\u6211\u6b64 \u6b21\u5165\u5173\u4e4b\u540e\u6240\u89c1\u7684\u7b2c\u4e00\u6761\u597d\u6c49\uff0c\u6211\u4e5f\u8981\u52a8\u5175\u5203\u5566\uff01\u201d\u865a\u6643\u4e00\u638c\uff0c\u62d4\u51fa\u53cc\u94a9\uff0c\u5f53\u80f8\u4e00\u7acb\uff0c\u53ea\u542c \u5f97\u94ff\u9535\u4e00\u58f0\uff0c\u6bd5\u9053\u51e1\u7684\u964d\u9f99\u68d2\u5df2\u7ed9\u53cc\u94a9\u5f39\u5f00\uff0c\u6fb9\u53f0\u706d\u660e\u53cc\u94a9\u4e00\u4e2a\u56de\u65cb\uff0c\u5de6\u94a9\u53f3\u6307\uff0c\u53f3\u94a9\u5de6 \u6307\uff0c\u628a\u964d\u9f99\u68d2\u903c\u5f97\u56e2\u56e2\u4e71\u8f6c\uff0c\u5140\u662f\u62b5\u5fa1\u4e0d\u4f4f\u3002\u4e91\u857e\u53eb\u58f0\u4e0d\u597d\uff0c\u62d4\u5251\u95ef\u5165\uff0c\u867d\u7136\u524a\u4e86\u51e0\u53e3\u5175 \u5203\uff0c\u53ef\u662f\u5374\u662f\u7ed9\u4e24\u540d\u8499\u53e4\u6b66\u58eb\u7f20\u4f4f\u3002\u90a3\u4e24\u540d\u6b66\u58eb\u4e00\u4f7f\u94c1\u6420\u4e00\u4f7f\u94fe\u5b50\u9524\uff0c\u90fd\u662f\u96be\u4e8e\u524a\u65ad\u7684\u91cd\u5175 \u5668\uff0c\u6025\u5207\u4e4b\u95f4\uff0c\u95ef\u4e0d\u8fc7\u53bb\u3002\u84dd\u5929\u77f3\u3001\u90dd\u5b9d\u693f\u3001\u77f3\u7fe0\u51e4\u7b49\u4e5f\u90fd\u5206\u522b\u88ab\u4eba\u56f4\u4f4f\uff0c\u4f1a\u5408\u4e0d\u5230\u4e00\u5904\u3002 \u3000\u3000\u6bd5\u9053\u51e1\u5c55\u51fa\u5168\u8eab\u672c\u9886\uff0c\u4ecd\u662f\u65e0\u6cd5\u8131\u8eab\uff0c\u6fb9\u53f0\u706d\u660e\u53cc\u94a9\u98de\u821e\u4fe8\u5982\u86df\u9f99\u51fa\u6d77\uff0c\u9e4f\u9e1f\u8ffd\u4e91\uff0c\u597d \u51e0\u6b21\u964d\u9f99\u68d2\u51e0\u4e4e\u7ed9\u4ed6\u593a\u51fa\u624b\u53bb\u3002\u6bd5\u9053\u51e1\u5012\u5438\u4e00\u53e3\u51c9\u6c14\uff0c\u5fc3\u9053\uff1a\u201c\u4e0d\u610f\u6211\u9003\u51fa\u6731\u660e\u9b54\u722a\uff0c\u5374\u4f1a \u6b7b\u5728\u80e1\u513f\u4e4b\u624b\u3002\u201d \u3000\u3000\u6b63\u5728\u5403\u7d27\uff0c\u5ffd\u89c1\u5b98\u519b\u7eb7\u7eb7\u60ca\u53eb\u9003\u907f\uff0c\u8f70\u9686\u4e4b\u58f0\u9707\u64bc\u5c71\u8c37\uff0c\u4e91\u857e\u62ac\u5934\u4e00\u770b\uff0c\u5374\u539f\u6765\u5c31\u662f\u90a3 \u4f5c\u8499\u53e4\u7267\u4eba\u6253\u626e\u7684\u602a\u5ba2\uff0c\u4e0a\u4e86\u5c71\u9876\uff0c\u628a\u4e00\u5757\u5757\u78e8\u76d8\u5927\u7684\u5927\u77f3\u63a8\u4e0b\u5c71\u6765\uff01\u9752\u9f99\u5ce1\u5728\u4e24\u5c71\u5939\u5cd9\u4e4b \u4e2d\u5c71\u9ad8\u5ce1\u7a84\uff0c\u5927\u77f3\u6eda\u4e0b\uff0c\u58f0\u52bf\u9a87\u4eba\uff0c\u82e5\u7ed9\u78b0\u7740\uff0c\u96be\u582a\u8bbe\u60f3\u3002\u5b98\u519b\u767b\u65f6\u5927\u4e71\uff0c\u56db\u5904\u7a9c\u8d70\uff0c\u8499\u53e4 \u6b66\u58eb\uff0c\u4e5f\u5413\u5f97\u614c\u4e86\u3002\u4e91\u857e\u7cbe\u795e\u5927\u632f\u53cd\u624b\u4e00\u5251\uff0c\u5c06\u90a3\u540d\u4f7f\u94c1\u6420\u7684\u6b66\u58eb\u523a\u4f24\uff0c\u6e38\u8d70\u5954\u524d\uff0c\u5411\u6fb9\u53f0 \u706d\u660e\u8fde\u653b\u6570\u5251\uff0c\u6fb9\u53f0\u706d\u660e\u5f20\u76ee\u559d\u9053\uff1a\u201c\u53c8\u662f\u4f60\u8fd9\u4e2a\u5a03\u5a03\uff01\u201d\u5de6\u94a9\u4e00\u5c01\uff0c\u5c06\u9752\u51a5\u5251\u9ecf\u51fa\u5916\u95e8\u3002 \u6bd5\u9053\u51e1\u53eb\u9053\uff1a\u201c\u4eca\u65e5\u96be\u5360\u4fbf\u5b9c\uff0c\u54b1\u4eec\u64a4\u8d70\uff01\u201d\u964d\u9f99\u4e00\u62db\u201c\u529b\u654c\u5343\u94a9\u201d\uff0c\u6321\u4e86\u6fb9\u53f0\u706d\u660e\u4e00\u62db\uff0c \u4e0e\u4e91\u857e\u8f6c\u8eab\u4fbf\u8d70\u3002\u6fb9\u53f0\u706d\u660e\u8ffd\u4e0a\u4e24\u6b65\uff0c\u5ffd\u7136\u4e00\u5757\u5927\u77f3\u6eda\u5230\u8ddf\u524d\uff0c\u6fb9\u53f0\u706d\u660e\u6536\u4e86\u53cc\u94a9\uff0c\u8eab\u8eaf\u534a \u8e72\uff0c\u53cc\u81c2\u4e00\u63a5\uff0c\u594b\u8d77\u795e\uff0c\u5c06\u90a3\u5757\u5927\u77f3\u63b7\u5230\u534a\u5c71\uff0c\u6070\u6070\u4e0e\u53e6\u4e00\u5757\u6eda\u4e0b\u6765\u7684\u5927\u77f3\u78b0\u4e2a\u6b63\u7740\u3002\u8f70\u9686 \u4e00\u58f0\uff0c\u6c99\u77f3\u7eb7\u98de\uff0c\u5b98\u519b\u56fa\u7136\u514d\u4e86\u4f24\u5bb3\uff0c\u6bd5\u9053\u51e1\u7b49\u4eba\u4e5f\u8d81\u7740\u6c99\u77f3\u5f25\u7a7a\u4e4b\u9645\u6025\u5954\u4e0a\u5c71\u3002 \u3000\u3000\u6fb9\u53f0\u706d\u660e\u5c1a\u6b32\u518d\u8ffd\uff0c\u90a3\u756a\u738b\u5fc3\u60ca\u80c6\u6218\u6025\u5fd9\u6b62\u4f4f\u4ed6\u9053\uff1a\u201c\u6fb9\u53f0\u5c06\u519b\uff0c\u7a77\u5bc7\u83ab\u8ffd\uff01\u201d\u5b9e\u662f\u6015 \u53e6\u6709\u57cb\u4f0f\uff0c\u6240\u4ee5\u8981\u7559\u4ed6\u5728\u8eab\u8fb9\u58ee\u80c6\u3002 \u3000\u3000\u6bd5\u9053\u51e1\u7b49\u4eba\u5954\u4e0a\u5c71\u5934\uff0c\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u597d\u6c49\u7559\u6b65\uff01\u201d\u90a3\u4f5c\u8499\u53e4\u7267\u4eba\u6253\u626e\u7684\u602a\u5ba2\uff0c\u5f85\u4ed6\u4eec\u4e0a \u5230\u534a\u5c71\uff0c\u5ffd\u7136\u4e00\u58f0\u957f\u5578\uff0c\u4ece\u80cc\u9762\u4e0b\u5c71\uff0c\u5f85\u6bd5\u9053\u51e1\u7b49\u4e0a\u5230\u5c71\u9876\u4e4b\u65f6\uff0c\u4ed6\u5df2\u7ecf\u9003\u9038\u65e0\u8e2a\u4e86\u3002 \u3000\u3000\u6bd5\u9053\u51e1\u9053\u58f0\uff1a\u201c\u771f\u602a\uff01\u201d\u7ffb\u4e0b\u9ad8\u5c71\uff0c\u6b63\u5348\u65f6\u5206\u56de\u5230\u84dd\u5bb6\uff0c\u5927\u5bb6\u7eb7\u7eb7\u8bae\u8bba\u90a3\u4e2a\u602a\u5ba2\uff0c\u90fd\u731c \u4e0d\u900f\u4ed6\u7684\u6765\u5386\uff0c\u53ea\u6709\u4e00\u70b9\uff0c\u5927\u5bb6\u5f02\u53e3\u540c\u58f0\u80af\u5b9a\u7684\u662f\uff1a\u8fd9\u602a\u5ba2\u4e00\u5b9a\u5c31\u662f\u90a3\u8499\u9762\u4eba\u3002 \u3000\u3000\u6bd5\u9053\u51e1\u9053\uff1a\u201c\u4e0d\u4f46\u6b64\u4eba\u602a\u5f02\uff0c\u90a3\u80e1\u513f\u4e5f\u602a\u3002\u6211\u4eec\u9003\u51fa\u4e4b\u65f6\u90dd\u8001\u5f1f\u8d70\u5728\u6700\u5148\uff0c\u82e5\u7136\u4ed6\u90a3\u5757 \u5927\u77f3\u4e0d\u63b7\u4e0a\u5c71\uff0c\u843d\u540e\u5341\u4e08\u516b\u4e08\uff0c\u90dd\u8001\u5f1f\u5b9e\u662f\u5371\u9669\u975e\u5e38\u3002\u201d\u90dd\u5b9d\u693f\u9053\uff1a\u201c\u4e5f\u8bb8\u662f\u4ed6\u4e3a\u4e86\u907f\u514d\u5b98 \u519b\u53d7\u4f24\uff0c\u6240\u4ee5\u5982\u6b64\u3002\u201d\u4e91\u857e\u7b11\u9053\uff1a\u201c\u90a3\u4eba\u4e0d\u662f\u2018\u80e1\u513f\u2019\uff0c\u4ed6\u53eb\u6fb9\u53f0\u706d\u660e\uff0c\u5b9e\u662f\u5728\u8499\u53e4\u957f\u5927\u7684 \u6c49\u4eba\u3002\u201d\u6bd5\u9053\u51e1\u76b1\u7709\u8bf4\u9053\uff1a\u201c\u6211\u867d\u6068\u6781\u6731\u5143\u748b\u7684\u5b50\u5b59\uff0c\u4f46\u76f8\u52a9\u80e1\u4eba\uff0c\u800c\u4e14\u5c45\u7136\u4ee5\u2018\u706d\u660e\u2019\u4e3a \u53f7\uff0c\u66f4\u662f\u53ef\u6068\u3002\u201d\u4e91\u857e\u53c8\u8bf4\u51fa\u6628\u665a\u6fb9\u53f0\u706d\u660e\u6545\u610f\u8ba9\u5979\u9003\u8d70\u4e4b\u4e8b\uff0c\u4f17\u4eba\u53c8\u662f\u8bae\u8bba\u7eb7\u7eb7\u3002 \u3000\u3000\u6bd5\u9053\u51e1\u9053\uff1a\u201c\u90a3\u602a\u5ba2\u7684\u6765\u5386\uff0c\u54b1\u4eec\u4ee5\u540e\u518d\u67e5\uff0c\u6fb9\u53f0\u706d\u660e\u662f\u4f55\u7528\u5fc3\u54b1\u4eec\u4e5f\u5148\u522b\u7ba1\uff0c\u5f53\u4eca\u7684 \u6025\u52a1\u662f\uff1a\u5982\u4f55\u6551\u51fa\u5c71\u6c11\u8d24\u4f84\u3002\u201d\u4f17\u4eba\u90fd\u60f3\u4e0d\u51fa\u597d\u7684\u6cd5\u5b50\u3002\u4e91\u857e\u9053\uff1a\u201c\u65e2\u7136\u65e0\u8ba1\u53ef\u65bd\uff0c\u90a3\u5c31\u53ea \u6709\u786c\u5e72\uff1a\u534a\u8def\u622a\u52ab\u56da\u8f66\u3002\u201d\u90dd\u5b9d\u693f\u9053\uff1a\u201c\u5b98\u519b\u52bf\u5927\uff0c\u53c8\u662f\u4eac\u5e08\u4e09\u5927\u9ad8\u624b\u62bc\u89e3\uff0c\u53ea\u6015\u52ab\u4eba\u4e0d \u6210\uff0c\u53cd\u906d\u6298\u635f\u3002\u201d\u6bd5\u9053\u51e1\u9053\uff1a\u201c\u4e14\u6253\u542c\u518d\u8bf4\u3002\u201d \u3000\u3000\u508d\u665a\u65f6\u5206\uff0c\u63a2\u5b50\u56de\u6765\u62a5\u9053\uff0c\u5f20\u98ce\u5e9c\u7559\u4e0b\u8d2f\u4ef2\u9886\u5927\u90e8\u5206\u7684\u5fa1\u6797\u519b\u548c\u9526\u8863\u536b\u534f\u52a9\u5730\u65b9\u201c\u626b \u8361\u201d\u5404\u4e2a\u5c71\u5be8\uff0c\u4ed6\u548c\u6a0a\u5fe0\u53ea\u9886\u7740\u4e94\u4e03\u5341\u540d\u5fa1\u6797\u519b\uff0c\u5c06\u64d2\u83b7\u7684\u4fd8\u864f\u62bc\u89e3\u4e0a\u4eac\uff0c\u660e\u65e5\u53ef\u80fd\u7ecf\u8fc7\u6b64 \u5730\u3002\u6bd5\u9053\u51e1\u559c\u9053\uff1a\u201c\u597d\uff0c\u54b1\u4eec\u660e\u65e5\u4fbf\u53bb\u4e0e\u4ed6\u786c\u5e72\u4e00\u573a\u3002\u201d\u6b63\u662f\uff1a \u3000\u3000\u9f99\u4e89\u864e\u6597\u6325\u6208\u5904\uff0c\u53c8\u89c1\u79bb\u5947\u53e4\u602a\u60c5\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u840d\u8e2a\u4fa0\u5f71\u5f55\u300b\u2014\u2014\u7b2c\u5341\u4e8c\u56de\u3000\u5ce1\u8c37\u52ab\u56da\u8f66\u53d8\u751f\u4e0d\u6d4b\u3000\u8352\u90ca\u9a70\u9a8f\u9a6c\u7978\u5f2d\u65e0\u5f62 \u6881\u7fbd\u751f\u300a\u840d\u8e2a\u4fa0\u5f71\u5f55\u300b \u7b2c\u5341\u4e8c\u56de\u3000\u5ce1\u8c37\u52ab\u56da\u8f66\u53d8\u751f\u4e0d\u6d4b\u3000\u8352\u90ca\u9a70\u9a8f\u9a6c\u7978\u5f2d\u65e0\u5f62 \u3000\u3000\u4e91\u857e\u8fd9\u665a\u7ffb\u6765\u8986\u53bb\u4e0d\u80fd\u5165\u5bd0\uff0c\u60f3\u8d77\u5468\u5c71\u6c11\u843d\u5165\u654c\u4eba\u4e4b\u624b\uff0c\u751a\u662f\u62c5\u5fe7\uff0c\u5fc3\u9053\uff1a\u201c\u6211\u660e\u65e5\u4fbf \u662f\u62fc\u4e86\u6027\u547d\uff0c\u4e5f\u8981\u6551\u4ed6\u3002\u201d\u8111\u6d77\u4e2d\u5ffd\u7136\u73b0\u51fa\u5468\u5c71\u6c11\u8981\u5979\u6539\u53e3\u4ee5\u5144\u5f1f\u76f8\u79f0\u65f6\u7684\u25a1\u8146\u795e\u60c5\uff0c\u60f3\u8d77 \u4ed6\u4e00\u8def\u4e0a\u9690\u9690\u900f\u9732\u7684\u60c5\u610f\uff0c\u53c8\u4e0d\u89c9\u751a\u662f\u60f6\u6050\u4e0d\u5b89\uff0c\u60f3\u9053\uff1a\u201c\u8981\u6211\u820d\u547d\u6551\u4ed6\uff0c\u90a3\u8fd8\u5bb9\u6613\uff1b\u8981\u6211 \u63a5\u53d7\u4ed6\u7684\u60c5\u610f\uff0c\u5374\u662f\u4e07\u4e07\u4e0d\u80fd\uff01\u201d\u9694\u623f\u900f\u8fc7\u77f3\u7fe0\u51e4\u54b3\u545b\u7684\u58f0\u97f3\uff0c\u60f3\u5979\u4ea6\u662f\u5fc3\u4e8b\u91cd\u91cd\uff0c\u672a\u66fe\u5165 \u7761\u3002\u4e91\u857e\u60f3\u8d77\u77f3\u7fe0\u51e4\u7684\u4e00\u7247\u75f4\u60c5\uff0c\u53c8\u4e0d\u89c9\u54d1\u7136\u5931\u7b11\uff0c\u8111\u6d77\u4e2d\u5468\u5c71\u6c11\u4e0e\u77f3\u7fe0\u51e4\u7684\u5f71\u5b50\u62fc\u5728\u4e00 \u8d77\uff0c\u6697\u81ea\u7b11\u9053\uff1a\u201c\u597d\uff0c\u5c31\u662f\u8fd9\u6837\uff0c\u628a\u4ed6\u4eec\u62c9\u5728\u4e00\u8d77\uff0c\u4ec0\u4e48\u9ebb\u70e6\u90fd\u6ca1\u6709\u5566\uff01\u201d\u53ef\u662f\uff0c\u771f\u7684\u5c31\u4ec0 \u4e48\u9ebb\u70e6\u4e5f\u6ca1\u6709\u4e86\u5417\uff1f\u5468\u5c71\u6c11\u4e0e\u77f3\u7fe0\u51e4\u7684\u5f71\u5b50\u521a\u521a\u6d88\u5931\uff0c\u5f20\u4e39\u67ab\u7684\u5f71\u5b50\u5374\u53c8\u6084\u6084\u5730\u722c\u4e0a\u5fc3\u5934\uff0c \u8fd9\u4e0d\u6b62\u662f\u66f4\u5927\u7684\u201c\u9ebb\u70e6\u201d\uff0c\u8fd9\u8fd8\u662f\u96be\u89e3\u7684\u201c\u51a4\u5b7d\u201d\uff0c\u4e91\u857e\u7a81\u89c9\u4e00\u7247\u832b\u7136\u4e0d\u80fd\u518d\u60f3\uff0c\u4e5f\u4e0d\u6562\u518d \u5f80\u4e0b\u60f3\u4e86\u3002 \u3000\u3000\u7b2c\u4e8c\u65e5\u4e00\u65e9\u8d77\u8eab\uff0c\u6bd5\u9053\u51e1\u5df2\u662f\u5e03\u7f6e\u505c\u5f53\u3002\u4e91\u857e\u51fa\u5230\u5385\u4e2d\uff0c\u53ea\u89c1\u9662\u5b50\u91cc\u4e00\u7247\u9ed1\u538b\u538b\u7684\u4eba \u7fa4\uff0c\u6bd5\u9053\u51e1\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u5df2\u6253\u542c\u6e05\u695a\uff0c\u5f20\u98ce\u5e9c\u4e0e\u6a0a\u5fe0\u53ea\u7387\u9886\u7740\u4e94\u5341\u540d\u5fa1\u6797\u519b\uff0c\u62bc\u89e3\u7740\u516d\u8f86\u56da \u8f66\uff0c\u5176\u4e2d\u6709\u4e00\u8f86\u7279\u5927\u7684\u56da\u8f66\uff0c\u8f66\u5b50\u884c\u65f6\uff0c\u5f20\u98ce\u5e9c\u7684\u5750\u9a91\u4e0d\u79bb\u5de6\u53f3\uff0c\u770b\u5f97\u5f88\u7d27\uff0c\u8f66\u4e2d\u7684\u56da\u72af\u60f3 \u5fc5\u5c31\u662f\u5c71\u6c11\u8d24\u4f84\u3002\u54b1\u4eec\u867d\u6765\u4e0d\u53ca\u4f20\u4e0b\u7eff\u6797\u7bad\uff0c\u84dd\u5144\u5f1f\u7684\u5e84\u4e01\u548c\u9644\u8fd1\u7684\u5144\u5f1f\u51d1\u5408\u8d77\u6765\u4e5f\u6709\u56db\u5341 \u591a\u4eba\uff0c\u5c3d\u53ef\u591f\u7528\u3002\u5f20\u98ce\u5e9c\u867d\u7136\u5389\u5bb3\uff0c\u7531\u6211\u548c\u4e91\u76f8\u516c\u53bb\u5bf9\u4ed8\u4ed6\uff0c\u5927\u7ea6\u4e5f\u8fd8\u5bf9\u4ed8\u5f97\u4e86\u3002\u9752\u9f99\u5ce1\u5f62 \u52bf\u7edd\u9669\uff0c\u6628\u65e5\u8499\u9762\u602a\u5ba2\u5c71\u9876\u6eda\u77f3\u90a3\u624b\u6cd5\u513f\uff0c\u54b1\u4eec\u4e5f\u53ef\u91c7\u7528\u3002\u201d\u84dd\u5929\u77f3\u9053\uff1a\u201c\u81ea\u5c71\u9876\u6eda\u4e0b\u5927 \u77f3\uff0c\u4e0d\u6015\u7838\u574f\u4e86\u56da\u8f66\u4e48\uff1f\u201d\u6bd5\u9053\u51e1\u9053\uff1a\u201c\u4e0d\u5fc5\u6eda\u4e0b\u5927\u77f3\uff0c\u7528\u9e45\u5375\u5927\u7684\u77f3\u5934\u98de\u77f3\u4e71\u6253\u90a3\u961f\u5b98 \u519b\uff0c\u53ea\u8981\u5bf9\u4ed6\u4eec\u7684\u961f\u5f62\u6270\u4e71\uff0c\u53eb\u4ed6\u4eec\u8981\u5206\u795e\u5e94\u4ed8\u90a3\u5c31\u884c\u5566\u3002\u90dd\u5e84\u4e3b\uff0c\u77f3\u59d1\u5a18\uff0c\u4f60\u4eec\u9886\u5341\u591a\u540d \u5144\u5f1f\u722c\u4e0a\u5c71\u9876\uff0c\u5c31\u8fd9\u6837\u529e\u5427\u3002\u5b98\u519b\u4e2d\u5348\u65f6\u5206\u5927\u7ea6\u53ef\u5230\u9752\u9f99\u5ce1\uff0c\u54b1\u4eec\u73b0\u5728\u8be5\u52a8\u8eab\u5566\uff01\u201d \u3000\u3000\u4f17\u4eba\u51fa\u4e86\u5927\u9662\uff0c\u7eb7\u7eb7\u4e0a\u9a6c\u3002\u4e91\u857e\u508d\u7740\u6bd5\u9053\u51e1\u5e76\u8f94\u5954\u9a70\uff0c\u5ffd\u7136\u95ee\u9053\uff1a\u201c\u6bd5\u8001\u524d\u8f88\uff0c\u4f60\u600e\u4e48 \u4e0d\u9a91\u90a3\u5339\u767d\u9a6c\uff1f\u201d\u6bd5\u9053\u51e1\u7b11\u9053\uff1a\u201c\u5f52\u4e86\u5b83\u7684\u4e3b\u4eba\u5566\u3002\u201d\u4e91\u857e\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u5f20\u4e39\u67ab\u51e0\u65f6\u53c8\u89c1\u4e86 \u4f60\u4e86\uff1f\u201d\u6bd5\u9053\u51e1\u9053\uff1a\u201c\u8fd9\u7167\u591c\u72ee\u5b50\u9a6c\u771f\u662f\u5929\u4e0b\u7f55\u89c1\u7684\u540d\u9a79\uff0c\u6781\u6709\u7075\u6027\uff0c\u90a3\u65e5\u5b83\u542c\u4e3b\u4eba\u5429\u5490\uff0c \u9a6e\u6211\u8131\u9669\uff0c\u8131\u9669\u4e4b\u540e\uff0c\u5b83\u5c31\u8fde\u58f0\u5636\u9e23\uff0c\u518d\u4e5f\u4e0d\u670d\u6211\u9a91\u5566\u3002\u6211\u77e5\u9053\u5b83\u662f\u60f3\u5ff5\u4e3b\u4eba\uff0c\u5c31\u5c06\u5b83\u653e \u4e86\u3002\u201d\u4e91\u857e\u9053\uff1a\u201c\u4f60\u600e\u77e5\u5b83\u4e00\u5b9a\u80fd\u627e\u5230\u4e3b\u4eba\uff0c\u82e5\u7ed9\u574f\u4eba\u622a\u4e86\u5c82\u4e0d\u53ef\u60dc\u4e86\uff1f\u201d\u6bd5\u9053\u51e1\u4e00\u7b11\u8bf4 \u9053\uff1a\u201c\u4e00\u822c\u597d\u7684\u6218\u9a6c\uff0c\u4e5f\u77e5\u9053\u5bfb\u89c5\u4e3b\u4eba\uff0c\u4f55\u51b5\u662f\u8fd9\u5339\u5929\u4e0b\u7f55\u89c1\u7684\u7167\u591c\u72ee\u5b50\uff1f\u518d\u8bf4\uff0c\u6ca1\u6709\u64d2\u9f99 \u4f0f\u864e\u7684\u672c\u4e8b\u8c01\u53c8\u622a\u5f97\u5b83\u4f4f\uff1f\u201d\u4e91\u857e\u672c\u4e5f\u77e5\u9053\u90a3\u5339\u767d\u9a6c\u7684\u7075\u5f02\uff0c\u53ef\u662f\u56e0\u4e3a\u5fc3\u4e2d\u60ac\u6302\u5f20\u4e39\u67ab\uff0c\u4e0d \u514d\u591a\u6240\u987e\u8651\u3002\u6bd5\u9053\u51e1\u8bf4\u4e86\u8bdd\u540e\uff0c\u5ffd\u53c8\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e91\u76f8\u516c\uff0c\u82e5\u4e0d\u662f\u77f3\u59d1\u5a18\u8bf4\u8fc7\uff0c\u6211\u771f\u770b \u4e0d\u51fa\u4f60\u548c\u5f20\u4e39\u67ab\u7adf\u662f\u4e0d\u5171\u6234\u5929\u7684\u5927\u4ec7\u4eba\uff01\u201d \u3000\u3000\u4e91\u857e\u9762\u4e0a\u4e00\u7ea2\uff0c\u62cd\u9a6c\u52a0\u97ad\uff0c\u907f\u800c\u4e0d\u7b54\u3002\u6bd5\u9053\u51e1\u597d\u751f\u5947\u602a\uff0c\u6599\u77e5\u5176\u4e2d\u5fc5\u6709\u522b\u60c5\uff0c\u5374\u4e5f\u4e0d\u518d \u53d1\u95ee\u3002 \u3000\u3000\u4e0d\u4e00\u523b\u8fdb\u5165\u5ce1\u8c37\uff0c\u6bd5\u9053\u51e1\u6309\u7167\u539f\u5b9a\u4e4b\u8ba1\uff0c\u6307\u6325\u4f17\u4eba\u57cb\u4f0f\u3002\u773c\u770b\u65e5\u5934\u6e10\u6e10\u897f\u79fb\uff0c\u5ffd\u542c\u5f97\u524d \u9762\u628a\u98ce\u7684\u4eba\u4f20\u4e0b\u8bdd\u9053\uff1a\u201c\u6765\u4e86\uff0c\u6765\u4e86\uff01\u201d\u4f17\u4eba\u634f\u7d27\u5175\u5668\uff0c\u53ea\u89c1\u4e00\u961f\u5b98\u519b\uff0c\u62bc\u7740\u516d\u8f86\u56da\u8f66\uff0c\u7f13 \u7f13\u8d70\u5165\u5ce1\u8c37\uff0c\u6bd5\u9053\u51e1\u5bf9\u4e91\u857e\u9053\uff1a\u201c\u5c31\u662f\u4e2d\u95f4\u90a3\u8f86\u3002\u201d\u5ffd\u89c1\u5f20\u98ce\u5e9c\u5728\u9a6c\u4e0a\u626c\u97ad\u5927\u7b11\uff0c\u53eb\u9053\uff1a \u201c\u8981\u52ab\u56da\u8f66\u7684\u8fd9\u53ef\u662f\u65f6\u5019\u4e86\uff01\u201d \u3000\u3000\u6bd5\u9053\u51e1\u3001\u4e91\u857e\u540c\u5403\u4e86\u4e00\u60ca\uff0c\u8fd9\u5f20\u98ce\u5e9c\u7adf\u4f3c\u65e9\u6709\u9632\u5907\uff01\u7bad\u5728\u5f26\u4e0a\uff0c\u4e0d\u5f97\u4e0d\u53d1\uff0c\u970e\u65f6\u95f4\uff0c\u4f0f \u5175\u5c3d\u51fa\uff0c\u53ea\u89c1\u5f20\u98ce\u5e9c\u5c06\u5fa1\u6797\u519b\u6446\u4e86\u4e00\u4e2a\u5706\u9635\uff0c\u62a4\u7740\u6b63\u4e2d\u7684\u90a3\u8f86\u56da\u8f66\u3002\u6bd5\u9053\u51e1\u4e00\u9a6c\u5f53\u5148\uff0c\u7387\u961f \u6025\u51b2\uff0c\u90a3\u4e94\u5341\u540d\u5fa1\u6797\u519b\u90fd\u662f\u767e\u4e2d\u9009\u4e00\u7684\u7cbe\u9510\uff0c\u5706\u9635\u53d8\u5316\u65e0\u65b9\uff0c\u9996\u5c3e\u76f8\u5e94\u3002\u84dd\u5bb6\u7684\u5e84\u4e01\u867d\u7136\u9a81 \u52c7\uff0c\u5374\u662f\u51b2\u4e0d\u8fc7\u53bb\u3002 \u3000\u3000\u4f46\u542c\u5f97\u5f20\u98ce\u5e9c\u54c8\u54c8\u5927\u7b11\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u9707\u4e09\u754c\u6bd5\u8001\u5934\u513f\uff0c\u524d\u65e5\u7ed9\u4f60\u9976\u5e78\u9003\u8131\uff0c\u600e\u53c8\u81ea\u6295 \u7f57\u7f51\u6765\u4e86\uff1f\u201d\u6bd5\u9053\u51e1\u54fc\u4e86\u4e00\u58f0\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u770b\u662f\u8c01\u81ea\u6295\u7f57\u7f51\uff1f\u201d\u84e6\u5730\u4e00\u58f0\u957f\u5578\uff0c\u987f\u65f6\u5c71\u9e23 \u8c37\u5e94\uff0c\u6797\u9e1f\u60ca\u98de\uff01 \u3000\u3000\u8fd9\u662f\u53eb\u5c71\u9876\u8bf8\u4eba\u52a8\u624b\u7684\u4fe1\u53f7\uff0c\u5c71\u9876\u4e0a\u90dd\u5b9d\u693f\u53d1\u4e00\u58f0\u558a\uff0c\u73b0\u51fa\u8eab\u6765\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u5ffd \u542c\u5f97\u631f\u98ce\u547c\u5578\u7684\u6697\u5668\u7834\u7a7a\u4e4b\u58f0\uff0c\u4e09\u67c4\u98de\u9525\u8fde\u7fe9\u98de\u81f3\uff0c\u90dd\u5b9d\u693f\u53eb\u58f0\uff1a\u201c\u4e0d\u597d\uff01\u201d\u903c\u5f97\u5c06\u77f3\u5934\u5411 \u4e0a\u63b7\u51fa\uff0c\u6253\u843d\u98de\u9525\u3002\u4f46\u89c1\u5bf9\u9762\u5c71\u5cf0\u51fa\u73b0\u4e86\u4e00\u961f\u5b98\u519b\uff0c\u5c06\u77f3\u5934\u7eb7\u7eb7\u629b\u63b7\u8fc7\u6765\uff0c\u5176\u4e2d\u8fd8\u5939\u6709\u98de \u9556\u3001\u98de\u9525\u3001\u5f39\u4e38\u4e4b\u7c7b\u7684\u6697\u5668\uff0c\u4e3a\u9996\u7684\u4e43\u662f\u4e0e\u5f20\u98ce\u5e9c\u5e76\u79f0\u4eac\u5e08\u4e09\u5927\u9ad8\u624b\u4e4b\u4e00\u7684\u5fa1\u524d\u4f8d\u536b\u6a0a\u5fe0\u3002 \u4ed6\u6240\u53d1\u7684\u98de\u9525\u6700\u4e3a\u5f3a\u52b2\uff0c\u706b\u795e\u5f39\u90dd\u5b9d\u693f\u867d\u662f\u6697\u5668\u540d\u5bb6\uff0c\u4e5f\u4e0d\u5f97\u4e0d\u5c0f\u5fc3\u5e94\u4ed8\uff0c\u5176\u4ed6\u8bf8\u4eba\u66f4\u662f\u7ed9 \u95f9\u5f97\u624b\u5fd9\u811a\u4e71\uff0c\u53cc\u65b9\u63b7\u77f3\u4f5c\u6218\uff0c\u54ea\u8fd8\u817e\u5f97\u51fa\u624b\u6765\u6253\u4e0b\u9762\u7684\u5b98\u519b\uff1f \u3000\u3000\u5f20\u98ce\u5e9c\u5f97\u610f\u4e4b\u6781\uff0c\u53c8\u662f\u54c8\u54c8\u5927\u7b11\uff0c\u626c\u5200\u8bf4\u9053\uff1a\u201c\u4e3a\u5c06\u4e4b\u9053\u5c82\u80fd\u4e0d\u5ba1\u5bdf\u5730\u5f62\uff0c\u9632\u60a3\u672a\u7136\u3002 \u9707\u4e09\u754c\u4f60\u6b66\u529f\u867d\u5f3a\uff0c\u5374\u662f\u5c11\u8bfb\u5175\u4e66\uff01\u201d\u6bd5\u9053\u51e1\u5927\u6012\uff0c\u964d\u9f99\u68d2\u6ef4\u6e9c\u6e9c\u4e00\u8f6c\uff0c\u903c\u9000\u8bf8\u822c\u5175\u5668\uff0c\u731b \u7136\u4f38\u624b\u4e00\u6293\uff0c\u65bd\u5c55\u5927\u64d2\u62ff\u624b\u6cd5\uff0c\u5c06\u4e00\u540d\u5b98\u519b\u6454\u7a3b\u8349\u4eba\u822c\u7684\u76f4\u7529\u51fa\u53bb\u3002\u4e91\u857e\u5237\u5237\u4e24\u5251\uff0c\u5c06\u5fa1\u6797 \u519b\u7684\u94c1\u7532\u5212\u7834\uff0c\u5b9d\u5251\u5a01\u529b\u60ca\u4eba\uff0c\u5fa1\u6797\u519b\u867d\u7136\u8eab\u62ab\u94e0\u7532\uff0c\u4e5f\u7ed9\u903c\u5f97\u4e24\u8fb9\u95ea\u5f00\u3002\u6bd5\u9053\u51e1\u4e0e\u4e91\u857e\u4e00 \u7528\u638c\u529b\uff0c\u4e00\u4ed7\u5b9d\u5251\uff0c\u7adf\u7136\u95ef\u8fdb\u91cd\u56f4\u3002 \u3000\u3000\u5f20\u98ce\u5e9c\u628a\u624b\u4e00\u6325\uff0c\u5706\u9635\u4e00\u53d8\uff0c\u7d22\u6027\u5c06\u4e8c\u4eba\u653e\u5165\uff0c\u5374\u628a\u5176\u4ed6\u4eba\u7fa4\u622a\u5728\u9635\u5916\uff0c\u5f20\u98ce\u5e9c\u80cc\u9760\u56da \u8f66\uff0c\u7f05\u5200\u4e00\u6307\uff0c\u7b11\u9053\uff1a\u201c\u9707\u4e09\u754c\u54b1\u4eec\u518d\u6597\u4e09\u767e\u56de\u5408\uff01\u201d\u659c\u773c\u4e00\u77a5\u4e91\u857e\uff0c\u53c8\u7b11\u9053\uff1a\u201c\u597d\u6781\u597d \u6781\uff0c\u4f60\u4e5f\u6765\u4e86\uff01\u597d\u5427\u4f60\u4eec\u4e24\u4eba\u5c31\u4e00\u9f50\u4e0a\u5427\uff0c\u6211\u53ef\u4e0d\u8981\u522b\u4eba\u76f8\u5e2e\u3002\u201d\u6bd5\u9053\u51e1\u9762\u4e0a\u4e00\u70ed\uff0c\u6325\u68d2\u8bf4 \u9053\uff1a\u201c\u4eca\u65e5\u4e4b\u4e8b\u54b1\u4eec\u90fd\u662f\u4e3a\u4e86\u670b\u53cb\uff0c\u62fc\u7740\u4e24\u80c1\u63d2\u5200\uff0c\u7ba1\u4f60\u4eba\u591a\u4eba\u5c11\uff0c\u6211\u90fd\u548c\u4f60\u62fc\u5566\uff01\u201d\u4e00\u62db \u201c\u98ce\u864e\u4e91\u9f99\u201d\uff0c\u68d2\u631f\u52b2\u98ce\uff0c\u5f53\u5934\u5288\u4e0b\u3002 \u3000\u3000\u5f20\u98ce\u5e9c\u51dd\u8eab\u4e0d\u52a8\uff0c\u4e00\u4e2a\u201c\u591c\u6218\u516b\u65b9\u201d\u62db\u5f0f\uff0c\u7f05\u5200\u75be\u53d1\uff0c\u67b6\u5f00\u964d\u9f99\u68d2\u903c\u9000\u9752\u51a5\u5251\uff0c\u5237\u5237\u5237 \u8fd8\u4e86\u4e09\u5200\u3002\u6bd5\u9053\u51e1\u6697\u53eb\u4e00\u58f0\u201c\u60ed\u6127\u201d\uff0c\u6362\u4e86\u4e00\u4e2a\u62db\u5f0f\uff0c\u7528\u7f20\u8eab\u5341\u516b\u6253\u7684\u68cd\u6cd5\uff0c\u76d8\u65cb\u6eda\u8fdb\uff0c\u4e91 \u857e\u5251\u8d70\u8f7b\u7075\uff0c\u4e5f\u7740\u7740\u62a2\u653b\u3002\u82e5\u7136\u4ee5\u4e00\u654c\u4e00\uff0c\u5f20\u98ce\u5e9c\u80dc\u5728\u6c14\u529b\uff0c\u8981\u6bd4\u6bd5\u9053\u51e1\u7a0d\u9ad8\u4e00\u7b79\uff0c\u800c\u4eca\u52a0 \u4e0a\u4e91\u857e\uff0c\u6597\u5230\u4e09\u5341\u62db\u5f00\u5916\uff0c\u5f20\u98ce\u5e9c\u903c\u5f97\u659c\u95ea\u6570\u6b65\uff0c\u4e91\u857e\u8eab\u6cd5\u5feb\u6781\uff0c\u8d81\u6b64\u7a7a\u6863\uff0c\u4e00\u63a0\u75be\u8fc7\uff0c\u98de \u8eab\u8dc3\u4e0a\u56da\u8f66\u3002 \u3000\u3000\u4e91\u857e\u4e00\u9897\u5fc3\u5267\u70c8\u8df3\u52a8\uff0c\u60f3\u4e0d\u5230\u7adf\u7136\u8fd9\u6837\u5bb9\u6613\u4fbf\u544a\u5f97\u624b\uff0c\u60f3\u90a3\u5f20\u98ce\u5e9c\u5e76\u975e\u5eb8\u624d\uff0c\u4f55\u4ee5\u7adf\u4f1a \u72ec\u81ea\u62b5\u654c\uff0c\u4e0d\u8981\u5b98\u519b\u9632\u62a4\uff1f\u5373\u662f\u81ea\u8d1f\uff0c\u4ea6\u4e0d\u5e94\u8f7b\u654c\u5982\u65af\u3002\u4e0d\u8fc7\u5979\u867d\u6709\u6240\u7591\u5fc3\uff0c\u4f46\u6b64\u65f6\u6b64\u9645\uff0c \u5df2\u4e0d\u5bb9\u7ec6\u5fc3\u63a8\u60f3\uff0c\u4e00\u8dc3\u4e0a\u8f66\uff0c\u7acb\u5373\u63ed\u5f00\u5e10\u5e18\uff0c\u53ea\u89c1\u6709\u4e00\u4eba\u8737\u7f29\u5185\u91cc\uff0c\u8f66\u5185\u5149\u7ebf\u5fae\u5f31\uff0c\u770b\u4e0d\u6e05 \u695a\uff0c\u4e91\u857e\u60ca\u559c\u4ea4\u96c6\uff0c\u98a4\u58f0\u53eb\u4e86\u53e5\uff1a\uff1a\u201c\u5468\u5927\u54e5\uff01\u201d\u5251\u4ea4\u5de6\u624b\uff0c\u53f3\u624b\u5f80\u91cc\u4e00\u63a2\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u201c\u563f\u563f\u201d\u4e24\u58f0\u51b7\u7b11\uff0c\u8f66\u5185\u90a3\u4eba\u7a81\u7136\u5750\u8d77\uff0c\u624b\u8155\u4e00\u7ffb\u5df2\u628a\u4e91\u857e\u8109\u95e8\u6263\u4f4f\uff0c\u4e91\u857e\u8fd9\u4e00 \u60ca\u975e\u540c\u5c0f\u53ef\u3002\u90a3\u4eba\u559d\u9053\uff1a\u201c\u8fdb\u6765\u5427\uff01\u201d\u7528\u529b\u4e00\u626f\uff0c\u4e91\u857e\u8eab\u4e0d\u7531\u5df1\uff0c\u8dcc\u8fdb\u8f66\u5185\uff0c\u6251\u5012\u4e4b\u65f6\uff0c\u5b9d \u5251\u4e00\u62c9\uff0c\u5c06\u8f66\u5e10\u5272\u65ad\uff0c\u9633\u5149\u900f\u5165\uff0c\u5ffd\u53c8\u542c\u5f97\u90a3\u4eba\u53eb\u9053\uff1a\u201c\u54a6\uff0c\u539f\u6765\u662f\u4f60\uff01\u201d\u4f3c\u662f\u9887\u4e3a\u60ca\u8be7\uff0c \u4e91\u857e\u5fc3\u7075\u624b\u654f\uff0c\u5e94\u53d8\u5feb\u6377\uff0c\u5251\u67c4\u53cd\u624b\u4e00\u70b9\uff0c\u90a3\u4eba\u677e\u624b\u907f\u5f00\uff0c\u4e0e\u4e91\u857e\u53cc\u53cc\u8dc3\u51fa\u8f66\u5916\u3002 \u3000\u3000\u9633\u5149\u4e4b\u4e0b\uff0c\u53ea\u89c1\u90a3\u4eba\u6234\u7740\u906e\u98ce\u76ae\u5e3d\uff0c\u53cc\u773c\u5916\u9732\u70af\u70af\u6709\u795e\uff0c\u7adf\u7136\u5c31\u662f\u6628\u65e5\u5047\u626e\u8499\u53e4\u7267\u4eba\uff0c \u88ad\u51fb\u756a\u738b\u7684\u90a3\u4e2a\u602a\u5ba2\uff01\u4e24\u4eba\u5bf9\u9762\u7ad9\u7acb\uff0c\u76f8\u8ddd\u4e0d\u8fc7\u54ab\u5c3a\uff0c\u4e91\u857e\u770b\u5f97\u771f\u5207\uff0c\u90a3\u773c\u5149\u795e\u6001\uff0c\u8eab\u6750\u80a5 \u7626\u548c\u524d\u665a\u90a3\u8499\u9762\u4eba\u53c8\u6b63\u662f\u4e00\u4eba\u3002 \u3000\u3000\u4e91\u857e\u559c\u51fa\u671b\u5916\uff0c\u6025\u5fd9\u95ee\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u9053\u5468\u5927\u54e5\u5728\u54ea\u4e00\u8f86\u56da\u8f66\uff1f\u201d\u5728\u4e91\u857e\u5fc3\u4e2d\uff0c\u4ee5\u4e3a\u6b64\u4eba \u65e2\u66fe\u732e\u8ba1\u53eb\u6bd5\u9053\u51e1\u622a\u52ab\u756a\u738b\uff0c\u53c8\u66fe\u5f97\u4ed6\u6697\u4e2d\u76f8\u52a9\uff0c\u5fc5\u662f\u81ea\u5df1\u4eba\u65e0\u7591\u3002\u54ea\u6599\u6b64\u4eba\u5ffd\u7136\u53c8\u662f\u4e00\u58f0 \u51b7\u7b11\uff0c\u9053\uff1a\u201c\u8c01\u77e5\u9053\u4f60\u7684\u5468\u5927\u54e5\uff01\u201d\u5de6\u624b\u5212\u4e86\u534a\u4e2a\u5706\u5f27\uff0c\u731d\u7136\u7528\u5927\u529b\u91d1\u521a\u624b\u6cd5\u786c\u62a2\u4e91\u857e\u624b\u4e2d \u7684\u5b9d\u5251\u3002 \u3000\u3000\u8fd9\u4e00\u7a81\u53d8\uff0c\u66f4\u662f\u51fa\u4e8e\u4e91\u857e\u610f\u5916\uff0c\u731b\u4e0d\u53ca\u9632\uff0c\u90a3\u4eba\u624b\u6307\u5df2\u582a\u582a\u89e6\u53ca\uff0c\u76f8\u8ddd\u66f4\u8fd1\uff0c\u5ffd\u89c1\u4ed6\u53cc \u7738\u70af\u70af\uff0c\u624b\u6307\u4e00\u5212\uff0c\u62db\u6570\u5c06\u53d1\u4e0d\u53d1\u3002\u4e91\u857e\u75be\u7684\u4e00\u5251\uff0c\u90a3\u4eba\u4f3c\u662f\u731b\u7136\u5403\u4e86\u4e00\u60ca\uff0c\u624b\u6307\u4e00\u5f39\uff0c\u53ea \u542c\u5f97\u94ff\u9535\u4e00\u58f0\uff0c\u5f39\u7740\u5251\u80cc\uff0c\u4e91\u857e\u864e\u53e3\u53d1\u75bc\uff0c\u51e0\u4e4e\u628a\u63e1\u4e0d\u4f4f\uff0c\u5fc3\u4e2d\u6697\u60ca\uff1a\u6b64\u4eba\u7684\u91d1\u521a\u5927\u529b\u624b \u6cd5\uff0c\u679c\u662f\u4e0d\u540c\u51e1\u54cd\uff01 \u3000\u3000\u53ea\u542c\u5f97\u5f20\u98ce\u5e9c\u53c8\u662f\u54c8\u54c8\u5927\u7b11\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u6bd5\u8001\u5934\u513f\uff0c\u4f60\u770b\u53ef\u662f\u8c01\u81ea\u6295\u7f57\u7f51\uff01\u201d\u63a5\u7740\u4e00 \u58f0\u53f1\u25a1\uff0c\u4e00\u58f0\u6012\u9a82\uff0c\u5200\u68d2\u76f8\u4ea4\uff0c\u58f0\u9707\u8033\u819c\uff0c\u60f3\u662f\u6bd5\u9053\u51e1\u6012\u4e0d\u53ef\u904f\uff0c\u4f7f\u51fa\u6c14\u529b\uff0c\u4e0b\u4e86\u91cd\u624b\u3002 \u3000\u3000\u4e91\u857e\u7b2c\u4e8c\u5251\u7b2c\u4e09\u5251\u53c8\u5df2\u8fde\u7ef5\u53d1\u51fa\uff0c\u90a3\u4eba\u53cc\u638c\u7ffb\u98de\uff0c\u968f\u7740\u5251\u5c16\u821e\u52a8\uff0c\u638c\u98ce\u6325\u5904\uff0c\u6bcf\u5c06\u5251\u523a \u65b9\u5411\u903c\u6b6a\u3002\u4e91\u857e\u5251\u6cd5\u6025\u53d8\uff0c\u9752\u51a5\u5251\u4e00\u5708\u4e00\u8f6c\uff0c\u53ea\u542c\u5f97\u55e1\u7136\u4e00\u58f0\uff0c\u4e45\u4e45\u4e0d\u7edd\uff01 \u3000\u3000\u4e91\u857e\u7684\u201c\u767e\u53d8\u7384\u673a\u5251\u6cd5\u201d\uff0c\u5947\u8be1\u5feb\u6377\uff0c\u5929\u4e0b\u65e0\u53cc\uff0c\u6b64\u9645\u88ab\u8feb\u4f7f\u51fa\u7edd\u62db\uff0c\u4e0a\u516b\u5251\uff0c\u4e0b\u516b \u5251\uff0c\u5de6\u516b\u5251\uff0c\u53f3\u516b\u5251\uff0c\u6bcf\u6b21\u8fde\u523a\u516b\u5251\uff0c\u90fd\u662f\u4e00\u6c14\u5475\u6210\uff0c\u4e0a\u4e0b\u5de6\u53f3\uff0c\u970e\u65f6\u4e4b\u95f4\uff0c\u523a\u4e86\u4e09\u5341\u4e8c \u5251\u3002\u90a3\u4eba\u638c\u529b\u867d\u7136\u9052\u52b2\u5374\u8ddf\u4e0d\u4e0a\u5251\u62db\u7684\u5feb\u6377\uff0c\u597d\u51e0\u6b21\u9669\u9669\u88ab\u5979\u523a\u4e2d\u3002\u4f46\u4e0d\u77e5\u600e\u7684\uff0c\u4e91\u857e\u603b\u89c9 \u8fd9\u4eba\u4f3c\u66fe\u76f8\u8bc6\uff0c\u867d\u7136\u4e0d\u77e5\u662f\u5728\u4ec0\u4e48\u65f6\u5019\u4ec0\u4e48\u5730\u65b9\u89c1\u8fc7\uff0c\u5fc3\u4e2d\u5374\u6709\u4e00\u4e2a\u4eb2\u8fd1\u7684\u611f\u89c9\uff0c\u597d\u51e0\u6b21\u5e94 \u8be5\u53ef\u4ee5\u523a\u4e2d\uff0c\u90fd\u662f\u4e0d\u671f\u7136\u800c\u7136\u7684\u5251\u5c16\u4e00\u6ed1\uff0c\u8d34\u8863\u800c\u8fc7\uff0c\u8fde\u81ea\u5df1\u4e5f\u89c9\u5f97\u4e07\u5206\u5947\u602a\u3002 \u3000\u3000\u4e0a\u4e0b\u5de6\u53f3\u8ffd\u98ce\u516b\u5251\u81ea\u6210\u4e00\u4e2a\u6bb5\u843d\uff0c\u4e09\u5341\u4e8c\u5251\u523a\u5b8c\uff0c\u52bf\u9053\u7a0d\u7f13\uff0c\u90a3\u4eba\u663e\u662f\u77e5\u9053\u8089\u638c\u4e0d\u80fd\u5e94 \u4ed8\uff0c\u55d6\u7684\u62d4\u51fa\u8170\u5200\uff0c\u5de6\u5200\u53f3\u638c\uff0c\u7acb\u5373\u62a2\u653b\u3002\u53ea\u89c1\u4ed6\u5200\u5149\u95ea\u95ea\uff0c\u7528\u7684\u5168\u662f\u5feb\u624b\uff0c\u51fa\u638c\u5374\u662f\u8212\u7f13 \u81ea\u5982\uff0c\u8d8a\u6765\u8d8a\u6162\uff0c\u4e00\u5feb\u4e00\u6162\uff0c\u5404\u6709\u5999\u5904\u3002\u7528\u5feb\u5200\u65a9\u4e71\u9ebb\u4e4b\u52bf\uff0c\u628a\u4e91\u857e\u7684\u653b\u52bf\u6253\u4e71\uff0c\u53c8\u7528\u638c\u529b \u9707\u6b6a\u4e91\u857e\u7684\u5251\u70b9\uff0c\u53eb\u5979\u5b9d\u5251\u4e4b\u5a01\uff0c\u65e0\u6cd5\u65bd\u5c55\uff0c\u8fd9\u6837\u4e00\u6765\uff0c\u7acb\u5373\u53cd\u5ba2\u4e3a\u4e3b\uff0c\u8f6c\u5b88\u4e3a\u653b\u3002\u4e91\u857e\u5251 \u6cd5\u867d\u7136\u7cbe\u5999\uff0c\u5374\u4e5f\u53ea\u6709\u62db\u67b6\u4e4b\u529f\uff0c\u4ec5\u80fd\u81ea\u4fdd\u3002\u90a3\u4eba\u7684\u5200\u6cd5\u867d\u7136\u51cc\u5389\u4e5f\u8fd8\u7f62\u4e86\uff0c\u90a3\u638c\u529b\u5374\u662f\u8d8a \u6765\u8d8a\u52b2\uff0c\u628a\u5708\u5b50\u6e10\u6e10\u6269\u5927\uff0c\u76f4\u628a\u4e91\u857e\u903c\u51fa\u516b\u4e08\u5f00\u5916\uff0c\u8fd1\u8eab\u4e0d\u5f97\u3002\u4f46\u8bf4\u4e5f\u5947\u602a\uff0c\u6709\u597d\u51e0\u6b21\u4e91\u857e \u906d\u9047\u9669\u62db\uff0c\u90a3\u4eba\u7684\u5200\u98ce\u638c\u52bf\uff0c\u4e5f\u662f\u63a0\u9762\u800c\u8fc7\uff0c\u6cbe\u8863\u5373\u9000\uff0c\u4e5f\u4e0d\u77e5\u4ed6\u662f\u6709\u610f\u65e0\u610f\uff0c\u5c31\u6070\u50cf\u4e91\u857e \u9002\u624d\u5bf9\u4ed6\u4e00\u6837\u3002 \u3000\u3000\u4e91\u857e\u5251\u6cd5\u52a0\u7d27\uff0c\u5168\u795e\u5e94\u4ed8\uff0c\u53ea\u89c1\u90a3\u4eba\u76ee\u5149\u95ea\u52a8\uff0c\u867d\u662f\u5728\u6025\u653b\u4e4b\u4e2d\uff0c\u5374\u662f\u4e0d\u505c\u5730\u6253\u91cf\u81ea \u5df1\u3002\u4e91\u857e\u5fc3\u4e2d\u4e00\u52a8\uff0c\u5237\u7684\u4e00\u5251\uff0c\u62e6\u5200\u62d2\u638c\uff0c\u559d\u95ee\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u201d\u90a3\u4eba\u8fd8\u4e86\u4e00\u62db\uff0c\u4e5f\u559d\u9053\uff1a \u201c\u4f60\u662f\u8c01\uff1f\u201d\u4e91\u857e\u4e00\u6014\uff0c\u9053\uff1a\u201c\u4f60\u5148\u8bf4\uff01\u201d\u90a3\u4eba\u9762\u6709\u5f02\u8272\u4e5f\u9053\uff1a\u201c\u4f60\u5148\u8bf4\uff01\u201d\u4e91\u857e\u5fc3\u9053\uff1a \u201c\u6211\u7684\u6765\u5386\u5982\u4f55\u80fd\u8bf4\u4e0e\u4f60\u77e5\uff1f\u201d\u4f46\u5374\u53c8\u6025\u4e8e\u77e5\u9053\u6b64\u4eba\u7684\u6765\u5386\uff0c\u7565\u4e00\u8fdf\u7591\uff0c\u53c8\u6321\u4e86\u4e09\u62db\uff0c\u575a\u6301 \u8bf4\u9053\uff1a\u201c\u4f60\u5148\u8bf4\uff01\u201d\u8bf4\u8bdd\u795e\u60c5\uff0c\u6d3b\u50cf\u4e00\u4e2a\u8d1f\u6c14\u56fa\u6267\u7684\u5b69\u5b50\u3002\u90a3\u4eba\u773c\u73e0\u4e00\u8f6c\u795e\u8272\u66f4\u662f\u8be7\u5f02\uff0c\u4f3c \u4e4e\u662f\u78b0\u7740\u4e00\u4e2a\u7ae5\u5e74\u65f6\u5019\u7684\u670b\u53cb\uff0c\u56de\u5fc6\u5979\u5f53\u5e74\u7684\u795e\u60c5\uff0c\u62ff\u6765\u4e0e\u73b0\u5728\u5370\u8bc1\u4e00\u6837\uff0c\u5de6\u5200\u53f3\u638c\uff0c\u90fd\u8fdf \u7f13\u4e0b\u6765\uff0c\u76ee\u5149\u4e0d\u4f4f\u5730\u5728\u4e91\u857e\u9762\u4e0a\u626b\u6765\u626b\u53bb\u3002\u4e91\u857e\u903c\u4e0a\u4e00\u6b65\uff0c\u90a3\u4eba\u5ffd\u53c8\u55d6\u55d6\u4e24\u5200\uff0c\u5c06\u4e91\u857e\u9694 \u5f00\uff0c\u575a\u6301\u8bf4\u9053\uff1a\u201c\u4f60\u5148\u8bf4\uff01\u201d\u6b63\u5728\u7ea0\u7f20\u4e0d\u6e05\uff0c\u5ffd\u542c\u5f97\u6bd5\u9053\u51e1\u5927\u53eb\u4e00\u58f0\uff1a\u201c\u4eca\u65e5\u98ce\u7d27\uff0c\u5e76\u80a9\u5b50 \u626f\u547c\uff01\u201d\u4e91\u857e\u659c\u773c\u4e00\u77a5\uff0c\u53ea\u89c1\u6bd5\u9053\u51e1\u5df2\u662f\u5168\u7136\u9677\u5728\u4e0b\u98ce\uff0c\u88ab\u5f20\u98ce\u5e9c\u5200\u5149\u7f69\u7740\uff0c\u5f62\u52bf\u751a\u662f\u5371 \u9669\u3002\u5916\u9762\u7f13\u5175\uff0c\u53c8\u7ed9\u5b98\u519b\u7684\u5706\u9635\u6321\u7740\uff0c\u95ef\u4e0d\u8fdb\u6765\u3002 \u3000\u3000\u4e91\u857e\u5927\u6025\uff0c\u5251\u8d70\u8fde\u73af\uff0c\u75be\u62a2\u6570\u62db\uff0c\u90a3\u4eba\u638c\u529b\u52a0\u7d27\uff0c\u5c31\u5982\u4e00\u9053\u5899\u58c1\uff0c\u62e6\u5728\u4e2d\u95f4\uff0c\u6025\u5207\u95f4\u5982 \u4f55\u95ef\u5f97\u8fc7\u53bb\u3002\u90a3\u4eba\u53c8\u53eb\u9053\uff1a\u201c\u4f60\u5230\u5e95\u8bf4\u4e0d\u8bf4\uff1f\u201d\u4e91\u857e\u5fc3\u4e2d\u751f\u6c14\uff0c\u95f7\u58f0\u4e0d\u54cd\uff0c\u6325\u5251\u4e0e\u4ed6\u62a2\u653b\uff0c \u970e\u65f6\u4e4b\u95f4\uff0c\u53c8\u6597\u4e86\u4e09\u4e94\u5341\u62db\u3002\u4e91\u857e\u529f\u529b\u672c\u6765\u7a0d\u900a\uff0c\u53ea\u4ed7\u7740\u5251\u6cd5\u7cbe\u5999\uff0c\u6240\u4ee5\u624d\u80fd\u5904\u5728\u4e0b\u98ce\uff0c\u52c9 \u5f3a\u6253\u6210\u5e73\u624b\u3002\u6b64\u9645\u56e0\u62c5\u5fc3\u6bd5\u9053\u51e1\u800c\u4e0d\u514d\u5206\u795e\u66f4\u662f\u611f\u89c9\u4e0d\u652f\uff0c\u4e0d\u4f46\u62a2\u653b\u4e0d\u6210\uff0c\u53cd\u7ed9\u903c\u5f97\u8fde\u8fde\u540e \u9000\uff01 \u3000\u3000\u6b63\u5728\u5403\u7d27\uff0c\u5ffd\u89c1\u8c37\u53e3\u90a3\u8fb9\u5c18\u6c99\u5927\u8d77\uff0c\u5f20\u98ce\u5e9c\u559d\u9053\uff1a\u201c\u8c01\u6562\u95ef\u9053\uff1f\u201d\u731b\u7136\u95f4\u53ea\u542c\u5f97\u602a\u7b11\u4e4b \u58f0\u9707\u64bc\u5c71\u8c37\u516b\u9a91\u5065\u9a6c\u8fce\u9762\u5954\u6765\uff0c\u4e3a\u9996\u4e24\u4eba\uff0c\u670d\u9970\u602a\u5f02\uff0c\u4e00\u9ed1\u4e00\u767d\uff0c\u76f8\u6620\u6210\u8da3\uff0c\u4e91\u857e\u4e0d\u89c9\u60ca\u53eb \u4e00\u58f0\uff0c\u8fd9\u4e24\u4eba\u53ef\u4e0d\u6b63\u662f\u767d\u6469\u8bc3\u4e0e\u9ed1\u6469\u8bc3\uff01\u4e2d\u95f4\u56db\u4eba\u5c31\u662f\u66fe\u5230\u9ed1\u77f3\u5e84\u7684\u90a3\u56db\u4e2a\u73e0\u5b9d\u4e70\u624b\uff0c\u540e\u9762 \u4e24\u4e2a\u7f20\u7740\u5934\u5dfe\u7684\u5987\u4eba\uff0c\u5374\u662f\u9ed1\u767d\u6469\u8bc3\u7684\u6ce2\u65af\u59bb\u5b50\uff0c\u8fd9\u516b\u4eba\u7b56\u9a6c\u9a70\u9a8b\uff0c\u5168\u4e0d\u628a\u25a1\u6740\u53cc\u65b9\u653e\u5728\u5fc3 \u4e0a\u3002 \u3000\u3000\u9ed1\u6469\u8bc3\u5feb\u9a6c\u5148\u5230\uff0c\u5f20\u98ce\u5e9c\u52c3\u7136\u5927\u6012\u559d\u9053\uff1a\u201c\u6eda\u4e0b\u9a6c\u6765\uff01\u201d\u51cc\u7a7a\u4e00\u8dc3\uff0c\u6402\u5934\u5c31\u662f\u4e00\u5200\u3002\u9ed1 \u6469\u8bc3\u4e00\u58f0\u602a\u7b11\uff0c\u7eff\u7389\u6756\u5f80\u4e0a\u4e00\u6233\u76f4\u523a\u4e39\u7530\u6c14\u7a74\u3002\u5f20\u98ce\u5e9c\u5927\u5403\u4e00\u60ca\uff0c\u60f3\u4e0d\u5230\u8fd9\u4e2a\u602a\u4eba\u7adf\u5177\u5982\u65af \u8eab\u624b\uff0c\u8eab\u5b50\u51ed\u7a7a\u626d\u8f6c\uff0c\u811a\u5c16\u4e00\u52fe\u9a6c\u956b\uff0c\u8eab\u843d\u9a6c\u80cc\uff0c\u5de6\u53f3\u8fde\u4e24\u5200\uff0c\u5feb\u6377\u65e0\u4f26\u3002\u9ed1\u6469\u8bc3\u4e5f\u4e0d\u7981\u5927 \u5403\u4e00\u60ca\uff0c\u60f3\u4e0d\u5230\u8fd9\u4e2a\u519b\u5b98\u7adf\u7136\u5982\u6b64\u5389\u5bb3\uff0c\u7eff\u7389\u6756\u4e00\u6a2a\uff0c\u5411\u5f20\u98ce\u5e9c\u80f8\u524d\u731b\u63a8\uff0c\u5f20\u98ce\u5e9c\u6a2a\u5200\u67b6 \u4f4f\uff0c\u53ea\u5f97\u534a\u8fb9\u5c41\u80a1\u5750\u5728\u9a6c\u4e0a\uff0c\u5f62\u52bf\u8fdc\u4e0d\u5982\u9ed1\u6469\u8bc3\u6709\u5229\uff0c\u6c42\u80dc\u5fc3\u5207\uff0c\u7a81\u628a\u53f3\u624b\u4e00\u677e\uff0c\u5f85\u5f97\u9ed1\u6469 \u8bc3\u8eab\u5b50\u524d\u503e\uff0c\u5de6\u638c\u84e6\u5730\u5f80\u524d\u4e00\u63a2\uff0c\u4f7f\u51fa\u64d2\u62ff\u624b\u7edd\u62db\uff0c\u53ea\u4e00\u6293\u5c31\u6293\u7740\u4e86\u9ed1\u6469\u8bc3\u7684\u5c0f\u81c2\u3002 \u3000\u3000\u5f20\u98ce\u5e9c\u5927\u559c\uff0c\u6b63\u5f85\u7528\u529f\uff0c\u9aa4\u7136\u95f4\u5ffd\u89c9\u6240\u6293\u4e4b\u5904\u5168\u4e0d\u53d7\u529b\uff0c\u9ed1\u6469\u8bc3\u7684\u624b\u81c2\u6ed1\u4f3c\u6e38\u9c7c\uff0c\u7a81\u7136 \u626d\u66f2\uff0c\u5f2f\u4e86\u8fc7\u6765\uff0c\u556a\u7684\u4e00\u638c\u6253\u5230\u5f20\u98ce\u5e9c\u9762\u95e8\u3002\u5f20\u98ce\u5e9c\u54ea\u6599\u5f97\u5230\u9ed1\u6469\u8bc3\u4f7f\u7684\u662f\u5370\u5ea6\u745c\u4f3d\u529f\u592b\uff0c \u808c\u8089\u53ef\u4ee5\u968f\u610f\u626d\u66f2\u53d8\u5f62\uff0c\u9aa4\u4e0d\u53ca\u9632\uff0c\u638c\u98ce\u5df2\u7136\u6251\u9762\uff0c\u5f20\u98ce\u5e9c\u4e00\u58f0\u5927\u53eb\uff0c\u8db3\u25a1\u9a6c\u956b\uff0c\u8eab\u5982\u98de\u7bad \u79bb\u5f26\uff0c\u5e73\u7a7a\u5c04\u51fa\u6570\u4e08\u4e4b\u5916\uff0c\u5b89\u7136\u843d\u5730\u3002\u9ed1\u6469\u8bc3\u672c\u662f\u5341\u62ff\u4e5d\u7a33\uff0c\u4e00\u638c\u6253\u7a7a\uff0c\u4e5f\u4e0d\u89c9\u9a87\u7136\uff01 \u3000\u3000\u8fd9\u51e0\u62db\u6025\u5982\u7535\u5149\u77f3\u706b\uff0c\u6bd5\u9053\u51e1\u5c1a\u672a\u60f3\u5230\u6765\u4eba\u6765\u5386\uff0c\u9ed1\u6469\u8bc3\u53c8\u5df2\u98de\u9a6c\u51b2\u6765\uff0c\u6bd5\u9053\u51e1\u53eb\u9053\uff1a \u201c\u54ea\u4e00\u8def\u7684\u670b\u53cb\uff1f\u6bd5\u9053\u51e1\u8fd9\u53a2\u6709\u793c\u3002\u201d\u6bd5\u9053\u51e1\u6709\u201c\u9707\u4e09\u754c\u201d\u4e4b\u540d\uff0c\u6ee1\u4ee5\u4e3a\u8bf4\u51fa\u540d\u5934\uff0c\u6c5f\u6e56\u4e0a \u7684\u670b\u53cb\u65e0\u6709\u4e0d\u77e5\uff0c\u54ea\u6599\u9ed1\u6469\u8bc3\u53c8\u662f\u4e00\u58f0\u602a\u7b11\uff0c\u559d\u9053\uff1a\u201c\u4ec0\u4e48\u9ed1\u9053\u767d\u9053\uff1f\u7ed9\u8001\u5b50\u8ba9\u8def\uff0c\u6eda \u5f00\uff01\u201d\u5feb\u9a6c\u6a2a\u51b2\u76f4\u95ef\uff0c\u6bd5\u9053\u51e1\u903c\u5f97\u4f38\u68d2\u4e00\u62e6\uff0c\u90a3\u9a6c\u524d\u8e44\u98de\u8d77\uff0c\u9ed1\u6469\u8bc3\u4e00\u6756\u4e0b\u6233\uff0c\u68d2\u6756\u76f8\u4ea4\uff0c \u6bd5\u9053\u51e1\u7684\u964d\u9f99\u68d2\u7ed9\u9707\u5f97\u6b6a\u8fc7\u4e00\u8fb9\uff0c\u9ed1\u6469\u8bc3\u7684\u7eff\u7389\u6756\u7ed9\u4ed6\u4e00\u8361\u4e00\u5e26\uff0c\u4e5f\u51e0\u4e4e\u8dcc\u4e0b\u9a6c\u6765\u3002\u9ed1\u6469\u8bc3 \u53eb\u9053\uff1a\u201c\u597d\uff0c\u4f60\u4e5f\u662f\u4e00\u6761\u597d\u6c49\uff01\u95f2\u5f00\u4fbf\u7f62\u5566\uff01\u201d\u4ece\u53eb\u201c\u6eda\u5f00\u201d\u800c\u5230\u8bf7\u4ed6\u201c\u95ea\u5f00\u201d\uff0c\u5df2\u662f\u5341\u5206 \u5ba2\u6c14\u3002\u6bd5\u9053\u51e1\u9aa4\u9047\u5f3a\u654c\uff0c\u5374\u662f\u6536\u68d2\u4e0d\u4f4f\uff0c\u7b2c\u4e8c\u68d2\u53c8\u5df2\u662f\u4e00\u62db\u201c\u6a2a\u6c5f\u622a\u6597\u201d\u6253\u5411\u9a6c\u8eab\uff0c\u9ed1\u6469\u8bc3 \u5927\u6012\uff0c\u7eff\u7389\u6756\u5f80\u4e0b\u4e00\u6309\uff0c\u5c06\u6bd5\u9053\u51e1\u7684\u964d\u9f99\u68d2\u6309\u4f4f\u7a81\u7136\u4e00\u677e\uff0c\u6bd5\u9053\u51e1\u51e0\u4e4e\u4ec6\u5012\uff0c\u4e3a\u9a6c\u6240\u8df5\uff0c\u6025 \u6025\u98de\u8eab\u7a9c\u5f00\uff0c\u53ea\u89c1\u90a3\u5339\u9a6c\u56db\u8e44\u98de\u8d77\uff0c\u5df2\u4ece\u81ea\u5df1\u5934\u4e0a\u4e00\u8dc3\u800c\u8fc7\u3002 \u3000\u3000\u9ed1\u6469\u8bc3\u4e0e\u5f20\u98ce\u5e9c\u3001\u6bd5\u9053\u51e1\u7ea0\u7f20\u4e4b\u65f6\uff0c\u767d\u6469\u8bc3\u7684\u5feb\u9a6c\u4ea6\u5230\uff0c\u76f4\u5411\u4e91\u857e\u4e0e\u90a3\u602a\u5ba2\u4ea4\u624b\u4e4b\u5904\u51b2 \u6765\u3002\u4e91\u857e\u5fc3\u4e2d\u4e00\u6014\uff1a\u9ed1\u767d\u6469\u8bc3\u66fe\u5728\u53e4\u5893\u4e4b\u4e2d\u7ed9\u81ea\u5df1\u4e0e\u5f20\u4e39\u67ab\u8054\u5251\u6253\u8d25\uff0c\u82e5\u4ed6\u8bb0\u7740\u524d\u4ec7\uff0c\u8fd9\u53ef \u600e\u751f\u5f97\u4e86\uff1f \u3000\u3000\u767d\u6469\u8bc3\u4e00\u773c\u77a5\u89c1\u4e91\u857e\uff0c\u5ffd\u5730\u4e00\u58f0\u602a\u7b11\uff0c\u9a6c\u5934\u4e00\u62d4\uff0c\u6539\u5411\u4e0e\u4e91\u857e\u4ea4\u624b\u7684\u90a3\u4e2a\u5c11\u5e74\u4e00\u51b2\u3002\u90a3 \u4eba\u5927\u6012\uff0c\u6a2a\u638c\u4e00\u62e8\uff0c\u547c\u7684\u4e00\u58f0\u51fb\u4e2d\u9a6c\u817f\uff0c\u90a3\u9a6c\u524d\u8e44\u5c48\u5730\uff0c\u90a3\u4eba\u5288\u9762\u5c31\u662f\u4e00\u5200\uff0c\u767d\u6469\u8bc3\u5c06\u767d\u7389 \u6756\u4e00\u64a9\uff0c\u767d\u7389\u6756\u4e43\u662f\u5b9d\u6756\uff0c\u575a\u903e\u7cbe\u94a2\uff0c\u90a3\u4eba\u5374\u4e0d\u77e5\u9053\u3002\u53ea\u542c\u5f97\u94ff\u9535\u4e00\u58f0\uff0c\u5200\u950b\u53cd\u5377\uff0c\u90a3\u4eba\u624b \u8155\u4e00\u7ffb\uff0c\u53cd\u624b\u4e00\u5200\u80cc\u62cd\u53bb\uff0c\u767d\u6469\u8bc3\u7389\u6756\u4e00\u5708\uff0c\u53ea\u542c\u5f97\u53c8\u662f\u5f53\u7684\u4e00\u58f0\u90a3\u53e3\u5200\u5411\u5929\u98de\u53bb\u3002\u767d\u6469\u8bc3 \u9053\uff1a\u201c\u4f60\u80fd\u6321\u6211\u4e00\u6756\uff0c\u9976\u4f60\u4e0d\u6b7b\uff0c\u95ea\u5f00\uff01\u201d\u7389\u6756\u4e00\u6307\uff0c\u5bf9\u4e91\u857e\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u8fd9\u4eba\u5bf9\u624b\uff0c\u8fd8\u4e0d \u5feb\u9003\uff01\u201d\u53cc\u817f\u4e00\u5939\uff0c\u90a3\u5339\u9a6c\u8df3\u4e86\u8d77\u6765\u75be\u5954\u800c\u53bb\uff01 \u3000\u3000\u539f\u6765\u9ed1\u767d\u6469\u8bc3\u88ab\u5f20\u3001\u4e91\u4e8c\u4eba\u8054\u5251\u6253\u8d25\u4e4b\u540e\uff0c\u8d4c\u8d5b\u8f93\u4e86\uff0c\u5893\u4e2d\u73e0\u5b9d\u5df2\u975e\u81ea\u5df1\u6240\u6709\u7070\u5fc3\u4e27 \u6c14\uff0c\u9063\u56db\u4e2a\u4e70\u624b\u5230\u5357\u65b9\u4e86\u7ed3\u5e10\u52a1\uff0c\u672c\u62df\u56de\u8f6c\u897f\u57df\uff0c\u4ece\u6b64\u4e0d\u505a\u73e0\u5b9d\u4e70\u5356\u3002\u54ea\u77e5\u5f20\u4e39\u67ab\u540e\u6765\u6177\u6168 \u5730\u628a\u73e0\u5b9d\u5168\u6570\u53d1\u56de\uff0c\u4e24\u5144\u5f1f\u5341\u5206\u611f\u6fc0\uff0c\u6709\u4e86\u8d44\u672c\uff0c\u4fbf\u518d\u505a\u4e86\u4e24\u5b97\u5927\u4e70\u5356\uff0c\u8fd9\u6b21\u7531\u5357\u800c\u5317\uff0c\u516b \u5339\u9a6c\u9a6e\u4e86\u8bb8\u591a\u73e0\u5b9d\uff0c\u51c6\u5907\u8d8a\u559c\u9a6c\u62c9\u96c5\u5c71\u5077\u5356\u7ed9\u5370\u5ea6\u738b\u516c\uff0c\u5374\u60f3\u4e0d\u5230\u5728\u6b64\u5730\u9047\u5230\u4e24\u65b9\u6df7\u6218\u3002 \u3000\u3000\u9ed1\u767d\u6469\u8bc3\u81ea\u6210\u4e00\u8def\uff0c\u9ed1\u9053\u767d\u9053\u5168\u4e0d\u4e70\u5e10\uff0c\u66f4\u517c\u9a6e\u7740\u73e0\u5b9d\uff0c\u6050\u88ab\u5b98\u519b\u622a\u4f4f\uff0c\u6545\u6b64\u66f4\u662f\u6a2a\u51b2 \u76f4\u95ef\uff0c\u89c1\u8def\u5373\u8d70\uff0c\u53ea\u56e0\u5fc3\u611f\u5f20\u4e39\u67ab\u8fd8\u5b9d\u4e4b\u6069\uff0c\u8fd9\u624d\u52a9\u4e86\u4e91\u857e\u4e00\u624b\u3002 \u3000\u3000\u4e0d\u4f46\u9ed1\u767d\u6469\u8bc3\u6b66\u827a\u9ad8\u5f3a\uff0c\u4ed6\u4eec\u7684\u6ce2\u65af\u59bb\u5b50\u4e0e\u8ddf\u4ece\u4ed6\u4eec\u7684\u56db\u4e2a\u4e70\u624b\u4e5f\u5168\u975e\u5eb8\u624b\u3002\u516b\u5339\u9a6c\u5728 \u5ce1\u8c37\u4e2d\u4e71\u51b2\u4e71\u95ef\uff0c\u4e24\u65b9\u4eba\u9a6c\u90fd\u88ab\u903c\u5f97\u7eb7\u7eb7\u8eb2\u95ea\u9003\u907f\uff0c\u6bd5\u9053\u51e1\u89c1\u673a\u4e0d\u53ef\u5931\uff0c\u4e00\u58f0\u547c\u5578\uff0c\u5e26\u9886\u4f17 \u4eba\u722c\u4e0a\u5c71\u5cf0\u3002\u9ed1\u767d\u6469\u8bc3\u4e00\u9635\u602a\u7b11\uff0c\u5b98\u519b\u867d\u8ba9\u5f00\u4e86\u8def\uff0c\u4ed6\u4eec\u5374\u4e0d\u6025\u7740\u5954\u9a70\u51fa\u53bb\uff0c\u53c8\u5728\u5ce1\u8c37\u4e2d\u4e71 \u6405\u4e86\u597d\u4e00\u4f1a\u5b50\uff0c\u62e6\u7740\u5b98\u519b\u7b49\uff0c\u4e91\u857e\u7b49\u4eba\u722c\u4e0a\u534a\u5c71\uff0c\u8fd9\u624d\u547c\u5578\u800c\u53bb\u3002 \u3000\u3000\u5f20\u98ce\u5e9c\u5927\u6012\uff0c\u8981\u91cd\u6574\u5706\u9635\uff0c\u8ffd\u51fb\u654c\u4eba\uff0c\u5df2\u662f\u4e0d\u53ca\u3002\u53ea\u542c\u5f97\u9ed1\u767d\u6469\u8bc3\u5411\u5c71\u4e0a\u9065\u547c\u9053\uff1a\u201c\u5c0f \u5a03\u5a03\uff0c\u4f60\u90a3\u4e2a\u670b\u53cb\u5927\u5a03\u5a03\u5728\u524d\u5934\u7b49\u7740\u4f60\u5462\u3002\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u548c\u4ed6\u4e00\u9053\uff1f\u201d\u4e91\u857e\u77e5\u9053\u9ed1\u767d\u6469\u8bc3\u53e3\u4e2d \u6240\u8bf4\u7684\u201c\u5927\u5a03\u5a03\u201d\u6307\u7684\u4e43\u662f\u5f20\u4e39\u67ab\uff0c\u5fc3\u4e2d\u4e00\u8df3\u51e0\u4e4e\u8981\u53d1\u58f0\u76f8\u95ee\u3002\u6bd5\u9053\u51e1\u95ee\u9053\uff1a\u201c\u8fd9\u4e24\u4eba\u662f \u8c01\uff1f\u201d\u4e91\u857e\u9053\uff1a\u201c\u897f\u57df\u9ed1\u767d\u6469\u8bc3\u3002\u201d\u6bd5\u9053\u51e1\u60ca\u9053\uff1a\u201c\u539f\u6765\u662f\u8fd9\u4e24\u4e2a\u9b54\u5934\uff0c\u4e45\u5df2\u95fb\u540d\uff0c\u4eca\u59cb\u89c1 \u9762\u3002\u60f3\u4e0d\u5230\u54b1\u4eec\u5374\u9760\u8fd9\u4e24\u4e2a\u9b54\u5934\u8131\u4e86\u4e00\u573a\u707e\u96be\uff0c\u53ea\u662f\u5c71\u6c11\u8d24\u4f84\u672a\u80fd\u6551\u5f97\uff0c\u5982\u4f55\u662f\u597d\uff1f\u201d \u3000\u3000\u5c71\u4e0a\u90dd\u5b9d\u693f\u7b49\u4eba\u5c1a\u5728\u4e0e\u5b98\u519b\u63b7\u77f3\u4f5c\u6218\uff0c\u6bd5\u9053\u51e1\u4f1a\u5408\u8bf8\u4eba\uff0c\u7ffb\u4e0b\u5c71\u80cc\uff0c\u56de\u5230\u84dd\u5bb6\uff0c\u53c8\u5df2\u662f \u9ec4\u660f\u65f6\u5206\u3002\u8fd9\u6b21\u6551\u4eba\u4e0d\u6210\uff0c\u53cd\u906d\u8d25\u7ee9\uff0c\u4f17\u4eba\u4ff1\u95f7\u95f7\u4e0d\u4e50\u3002\u8c08\u8d77\u524d\u65e5\u626e\u4f5c\u8499\u53e4\u7267\u4eba\uff0c\u4eca\u65e5\u8eb2\u5728 \u519b\u4e2d\u8bbe\u4f0f\u7684\u90a3\u4e2a\u602a\u5c11\u5e74\uff0c\u66f4\u662f\u8bae\u8bba\u7eb7\u7eb7\uff0c\u731c\u4e0d\u900f\u4ed6\u7684\u6765\u5386\u3002 \u3000\u3000\u6bd5\u9053\u51e1\u4e00\u770b\u5929\u8272\uff0c\u9053\uff1a\u201c\u5f20\u98ce\u5e9c\u7b49\u4eba\u4eca\u665a\u5fc5\u5728\u57ce\u4e2d\u4f4f\u5bbf\uff0c\u54b1\u4eec\u6700\u5c11\u8be5\u63a2\u51fa\u5468\u575a\u4f84\u751f\u6b7b\u5982 \u4f55\uff0c\u518d\u4f5c\u6253\u7b97\u3002\u770b\u90a3\u5f20\u98ce\u5e9c\u8be1\u8ba1\u591a\u7aef\uff0c\u7528\u7684\u53ea\u6050\u662f\u91d1\u8749\u8131\u58f3\u4e4b\u8ba1\uff0c\u5468\u8d24\u4f84\u662f\u5426\u5728\u516d\u8f86\u56da\u8f66\u4e4b \u4e2d\u54b1\u4eec\u4e5f\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u4f17\u4eba\u60f3\u53ca\u90a3\u5f20\u98ce\u5e9c\u5982\u6b64\u5389\u5bb3\uff0c\u90fd\u4e0d\u89c9\u9ed8\u7136\u3002\u6bd5\u9053\u51e1\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u8fd9\u7fa4\u4eba\u4e2d\uff0c\u4e91\u76f8\u516c \u8981\u6570\u4f60\u7684\u8f7b\u529f\u6700\u597d\uff0c\u57ce\u4e2d\u6700\u5927\u90a3\u95f4\u5ba2\u5e97\u4e43\u662f\u81ea\u5df1\u4eba\u5f00\u7684\u3002\u201d\u4e91\u857e\u751a\u662f\u673a\u7075\uff0c\u4e00\u70b9\u5373\u900f\u9053\uff1a \u201c\u662f\u554a\uff0c\u767d\u65e5\u91cc\u660e\u5200\u660e\u67aa\u622a\u52ab\u4e0d\u6210\uff0c\u54b1\u4eec\u665a\u4e0a\u53bb\u7ed9\u4ed6\u4eec\u6363\u4e2a\u5c0f\u4e71\uff0c\u6700\u5c11\u4e5f\u80fd\u63a2\u4e2a\u865a\u5b9e\u3002\u60f3\u90a3 \u5f20\u98ce\u5e9c\u6b66\u827a\u867d\u9ad8\u8f7b\u529f\u5374\u662f\u672a\u81fb\u4f73\u5999\u3002\u82e5\u6709\u4e0d\u6d4b\uff0c\u6211\u5c31\u7ed9\u4ed6\u4e00\u4e2a\u6e9c\u4e4b\u5927\u5409\uff0c\u4ed6\u672a\u5fc5\u8ffd\u5f97\u4e0a \u6211\u3002\u201d\u5f53\u4e0b\u8bae\u5b9a\uff0c\u4e91\u857e\u53bb\u63a2\u865a\u5b9e\uff0c\u6bd5\u9053\u51e1\u5728\u5ba2\u5e97\u5916\u9762\u7b56\u5e94\u3002 \u3000\u3000\u665a\u4e0a\u4e8c\u66f4\u65f6\u5206\u4e24\u4e2a\u4eba\u6084\u6084\u6e9c\u5165\u57ce\u4e2d\uff0c\u57ce\u4e2d\u65e9\u5df2\u6709\u4eba\u63a5\u5e94\uff0c\u5f20\u98ce\u5e9c\u8fd9\u73ed\u4eba\u679c\u7136\u5728\u90a3\u5bb6\u5ba2\u5e97 \u4f4f\u5bbf\u3002\u4e91\u857e\u9760\u7740\u5e97\u5c0f\u4e8c\u7684\u5e26\u5f15\uff0c\u4ece\u5ba2\u5e97\u540e\u95e8\u6e9c\u5165\uff0c\u95ee\u660e\u4e86\u5f20\u98ce\u5e9c\u6240\u4f4f\u7684\u623f\u95f4\uff0c\u6b47\u4e86\u4e00\u4f1a\uff0c\u517b \u597d\u7cbe\u795e\uff0c\u542c\u5f97\u6572\u8fc7\u4e09\u66f4\uff0c\u6362\u4e86\u591c\u884c\u8863\u670d\uff0c\u6b63\u60f3\u767b\u4e0a\u5c4b\u9876\uff0c\u5ffd\u542c\u5f97\u5ba2\u5e97\u5916\u9a6c\u8e44\u4e4b\u58f0\u751a\u6025\uff0c\u500f\u5ffd \u5230\u4e86\u95e8\u524d\uff0c\u5ba2\u5e97\u5185\u5df2\u6709\u5fa1\u6797\u519b\u7684\u519b\u5b98\u51fa\u53bb\u8fce\u63a5\u3002 \u3000\u3000\u5e97\u5c0f\u4e8c\u9053\uff1a\u201c\u4e91\u76f8\u516c\u4f60\u4e14\u5f85\u4e00\u4f1a\u513f\u3002\u201d\u63d0\u4e86\u6c34\u6876\u9972\u6599\u51fa\u5916\u7ea6\u6478\u8fc7\u4e86\u4e00\u76cf\u8336\u7684\u65f6\u5019\u5916\u9762\u95f9 \u58f0\u5df2\u6b62\u3002\u5e97\u5c0f\u4e8c\u56de\u6765\u62a5\u9053\uff1a\u201c\u770b\u60c5\u5f62\u8fd9\u662f\u516b\u767e\u91cc\u52a0\u7d27\u7684\u98de\u9a91\u4f20\u62a5\uff0c\u53ea\u4e0d\u77e5\u662f\u4ec0\u4e48\u6587\u4e66\uff0c\u5982\u6b64 \u7740\u7d27\uff01\u201d\u53e4\u4ee3\u4f20\u9012\u6587\u4e66\uff0c\u6700\u6025\u7684\u53eb\u505a\u201c\u516b\u767e\u91cc\u5feb\u9a6c\u52a0\u7d27\u201d\uff0c\u6bcf\u9a7f\u7ad9\u90fd\u5907\u6709\u4e13\u95e8\u9012\u9001\u8fd9\u79cd\u6587\u4e66 \u7684\u5feb\u9a6c\uff0c\u4e0a\u4e00\u7ad9\u9001\u6587\u4e66\u7684\u5feb\u9a6c\u5230\u65f6\u7acb\u523b\u6362\u9a91\uff0c\u4e00\u7ad9\u7ad9\u7684\u9012\u9001\u4e0b\u53bb\uff0c\u4e00\u65e5\u4e4b\u95f4\uff0c\u603b\u8981\u6362\u5341\u5339\u516b \u5339\u5feb\u9a6c\u3002\u6240\u4ee5\u5c3d\u7ba1\u90a3\u4e9b\u9a6c\u4e0d\u662f\u5343\u91cc\u9a6c\uff0c\u5728\u5341\u4e8c\u65f6\u8fb0\u4e4b\u5185\uff0c\u8dd1\u4e03\u516b\u767e\u91cc\u5374\u4e5f\u5e76\u975e\u96be\u4e8b\u3002 \u3000\u3000\u4e91\u857e\u4e00\u6014\uff0c\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\uff1f\u201d\u5e97\u5c0f\u4e8c\u9053\uff1a\u201c\u90a3\u4f4d\u9001\u6587\u4e66\u7684\u516c\u5dee\u521a\u4e0b\u5750\u9a91\uff0c\u9a6c\u5339\u5c31\u7d2f \u5f97\u5012\u5730\uff0c\u8981\u7528\u4e24\u4e2a\u4eba\u7684\u529b\uff0c\u624d\u628a\u9a6c\u5934\u62b1\u8d77\u6765\u559d\u6c34\u3002\u201d\u4e91\u857e\u7565\u4e00\u6c89\u541f\uff0c\u9053\uff1a\u201c\u90a3\u4e5f\u6b63\u597d\uff0c\u6211\u5c31 \u987a\u4fbf\u63a2\u63a2\u8fd9\u662f\u4ec0\u4e48\u7d27\u8981\u7684\u6587\u4e66\u3002\u201d \u3000\u3000\u5f20\u98ce\u5e9c\u4f4f\u5728\u9760\u5357\u7684\u4e00\u4e2a\u5927\u623f\uff0c\u4e91\u857e\u7528\u4e2a\u201c\u73cd\u73e0\u5012\u5377\u5e18\u201d\u7684\u59ff\u52bf\uff0c\u52fe\u7740\u5c4b\u6a90\uff0c\u5411\u4e0b\u7aa5\u671b\uff0c \u53ea\u89c1\u623f\u4e2d\u679c\u7136\u5750\u7740\u4e00\u4e2a\u516c\u5dee\uff0c\u5f20\u98ce\u5e9c\u624b\u4e2d\u6301\u7740\u4e00\u5377\u6587\u4e66\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u4eca\u6b21\u4fd8\u83b7\u7684\u8d3c\u4eba\uff0c\u6211 \u8fd8\u6ca1\u6709\u4e00\u4e2a\u4e2a\u5ba1\u95ee\uff0c\u4e5f\u4e0d\u77e5\u5176\u4e2d\u6709\u65e0\u6b64\u4eba\u3002\u82e5\u7136\u662f\u6709\u7684\u8bdd\uff0c\u6211\u81ea\u7136\u7167\u5eb7\u603b\u7ba1\u7684\u610f\u601d\u3002\u55ef\uff0c\u4f60 \u4eca\u65e5\u8f9b\u82e6\u4e86\uff0c\u5feb\u53bb\u6b47\u606f\uff0c\u660e\u65e5\u56de\u4eac\u53bb\u5427\u3002\u8fd9\u6587\u4e66\u526f\u672c\u6211\u53e6\u5916\u6d3e\u4eba\u9001\u7ed9\u8d2f\u4ef2\u3002\u201d \u3000\u3000\u516c\u5dee\u9053\u58f0\uff1a\u201c\u8c22\u5927\u4eba\u6069\u5178\u3002\u201d\u544a\u8f9e\u4e4b\u540e\uff0c\u53ea\u89c1\u5f20\u98ce\u5e9c\u5f80\u6765\u8e31\u6b65\uff0c\u7709\u5934\u6253\u7ed3\uff0c\u663e\u7136\u662f\u6709\u4ec0 \u4e48\u91cd\u5927\u7684\u5fc3\u4e8b\uff0c\u84e6\u7136\u53eb\u9053\uff1a\u201c\u6765\u4eba\u5566\uff01\u201d\u628a\u95e8\u5916\u5b88\u591c\u7684\u4e00\u4e2a\u519b\u58eb\u53eb\u4e86\u8fdb\u6765\uff0c\u4f4e\u4f4e\u5429\u5490\u51e0\u53e5\uff0c \u9063\u4ed6\u51fa\u53bb\uff0c\u4e00\u4e2a\u4eba\u5728\u623f\u4e2d\u6414\u5934\u6293\u816e\uff0c\u5ffd\u5730\u628a\u6587\u4e66\u6253\u4e86\u5f00\u6765\uff0c\u4e91\u857e\u51dd\u795e\u4e0b\u671b\uff0c\u4e00\u5f20\u753b\u50cf\u9996\u5148\u6620 \u5165\u773c\u5e18\u3002 \u3000\u3000\u4e91\u857e\u4e00\u773c\u63a0\u8fc7\uff0c\u9669\u9669\u53eb\u51fa\u58f0\u6765\uff0c\u753b\u4e2d\u4eba\u50cf\u975e\u4ed6\uff0c\u6b63\u662f\u81ea\u5df1\u8981\u6765\u56fe\u6551\u7684\u5468\u5c71\u6c11\u3002\u53ea\u542c\u5f97\u5f20 \u98ce\u5e9c\u5583\u5583\u81ea\u8bed\u9053\uff1a\u201c\u5148\u628a\u4ed6\u7684\u7435\u7436\u9aa8\u7a7f\u4e86\uff0c\u518d\u628a\u4ed6\u7684\u773c\u73e0\u5b50\u6316\u4e86\uff0c\u5374\u8fd8\u8981\u7559\u7740\u4ed6\u4e0e\u91d1\u5200\u5be8\u4e3b \u8ba8\u4ef7\u8fd8\u4ef7\uff0c\u54c8\uff0c\u8fd9\u4e00\u62db\u53ef\u771f\u9634\u635f\u5230\u6781\u5566\uff01\u201d \u3000\u3000\u4e91\u857e\u542c\u5f97\u5927\u5403\u4e00\u60ca\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u82e5\u7136\u4ed6\u4eec\u5982\u6b64\u6298\u78e8\u5c71\u6c11\u5927\u54e5\uff0c\u90a3\u4e48\u6211\u4eca\u591c\u53ef\u8981\u8c41\u51fa\u6027 \u547d\uff0c\u4e0e\u4ed6\u540c\u5f52\u4e8e\u5c3d\u4e86\u3002\u201d\u638c\u5fc3\u6263\u4e86\u6885\u82b1\u8774\u8776\u9556\uff0c\u8eab\u4e0a\u76f4\u5192\u51b7\u6c57\u3002 \u3000\u3000\u53ea\u542c\u5f97\u811a\u6b65\u58f0\u6e10\u6e10\u6765\u8fd1\uff0c\u4e91\u857e\u5fc3\u9053\uff1a\u201c\u5b9a\u662f\u4ed6\u4eec\u62bc\u89e3\u5c71\u6c11\u5927\u54e5\u6765\u4e86\u3002\u201d\u4e0d\u6599\u8fdb\u6765\u7684\u5374\u53ea \u662f\u4e00\u4eba\uff0c\u4e91\u857e\u5b9a\u775b\u4e00\u770b\uff0c\u53c8\u9669\u9669\u53eb\u51fa\u58f0\u6765\u3002 \u3000\u3000\u6765\u7684\u662f\u4e00\u4f4d\u5c11\u5e74\u519b\u5b98\uff0c\u5c31\u6b63\u662f\u65e5\u95f4\u66fe\u4e0e\u4e91\u857e\u4ea4\u624b\u3001\u524d\u665a\u5077\u88ad\u756a\u738b\u7684\u90a3\u4e2a\u602a\u5ba2\u3002\u53ea\u542c\u5f97\u5f20 \u98ce\u5e9c\u9053\uff1a\u201c\u5343\u91cc\u5144\uff0c\u8fd9\u4e8b\u53ef\u597d\u751f\u96be\u51b3\u554a\uff01\u201d \u3000\u3000\u90a3\u5c11\u5e74\u519b\u5b98\u95ee\u9053\uff1a\u201c\u5f20\u5927\u4eba\u4f55\u4e8b\u96be\u51b3\uff1f\u201d\u5f20\u98ce\u5e9c\u4e0d\u5148\u7b54\u8bdd\u5374\u5ffd\u5730\u8fc8\u524d\u4e24\u6b65\uff0c\u4e0e\u90a3\u5c11\u5e74\u519b \u5b98\u6b63\u9762\u76f8\u5bf9\uff0c\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u4f60\u662f\u5341\u4e03\u65e5\u79bb\u5f00\u4eac\u90fd\u7684\uff0c\u600e\u4e48\u524d\u665a\u624d\u6765\u89c1\u6211\uff1f\u201d\u90a3\u5c11\u5e74\u519b\u5b98\u5fae\u73b0 \u7a98\u6001\uff0c\u76ee\u5149\u79fb\u5f00\uff0c\u5f3a\u7b11\u7b54\u9053\uff1a\u201c\u6211\u4e2d\u9014\u9047\u96e8\uff0c\u9a6c\u53c8\u4e0d\u884c\uff0c\u662f\u4ee5\u8fdf\u4e86\u3002\u201d\u5f20\u98ce\u5e9c\u54c8\u54c8\u4e00\u7b11\uff0c \u9053\uff1a\u201c\u662f\u4e48\uff1f\u201d\u90a3\u5c11\u5e74\u519b\u5b98\u9762\u8272\u9661\u53d8\u9000\u540e\u4e00\u6b65\uff0c\u624b\u6309\u51e0\u684c\uff0c\u9053\uff1a\u201c\u5f20\u5927\u4eba\u7591\u5fc3\u6211\u4e86\uff1f\u201d\u5f20\u98ce \u5e9c\u53c8\u6253\u4e86\u4e2a\u54c8\u54c8\uff0c\u9053\uff1a\u201c\u5c82\u6562\uff0c\u5c82\u6562\uff01\u201d\u5ffd\u5730\u6c89\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u8865\u9526\u8863\u536b\u4e3a\u65f6\u867d\u7136\u672a\u6ee1\u4e00\u6708\uff0c \u54b1\u4eec\u53ef\u662f\u809d\u80c6\u76f8\u7167\uff0c\u662f\u4e48\uff1f\u201d\u90a3\u5c11\u5e74\u519b\u5b98\u4ee5\u8896\u8bd5\u6c57\uff0c\u9053\uff1a\u201c\u5f20\u5927\u4eba\u5fe0\u809d\u4e49\u80c6\uff0c\u6211\u662f\u65e0\u9650\u4f69 \u670d\u3002\u201d\u5f20\u98ce\u5e9c\u53c8\u8feb\u524d\u4e00\u6b65\u9053\uff1a\u201c\u4e0d\u6562\u89c1\u7591\uff0c\u8fd8\u8bf7\u5b9e\u544a\u3002\u524d\u65e5\u5728\u9752\u9f99\u5ce1\u4e2d\u5077\u88ad\u8499\u53e4\u4f7f\u81e3\uff0c\u4f60\u662f \u4e0d\u662f\u4e5f\u6709\u4e00\u4efd\uff1f\u201d\u90a3\u5c11\u5e74\u519b\u5b98\u633a\u7acb\u9053\uff1a\u201c\u5927\u4eba\u660e\u5bdf\uff0c\u4e0d\u6b62\u6709\u6211\u4e00\u4efd\uff0c\u6211\u5b9e\u662f\u4e3b\u8c0b\u4e4b\u4eba\uff01\u201d\u5f20 \u98ce\u5e9c\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u9053\u4ed6\u4eec\u662f\u671d\u5ef7\u7684\u8d35\u5ba2\uff0c\u82e5\u6709\u5dee\u9519\u53ef\u80fd\u5f15\u8d77\u4e24\u56fd\u5e72\u6208\u4e48\uff1f\u201d\u90a3\u5c11\u5e74\u519b\u5b98\u6bc5\u7136 \u7b54\u9053\uff1a\u201c\u5f20\u5927\u4eba\uff0c\u4f60\u53ef\u77e5\u9053\u4ed6\u4eec\u6b64\u6765\uff0c\u662f\u8981\u6211\u4eec\u5927\u660e\u671d\u5ef7\u5272\u5730\u8d54\u6b3e\u7684\u4e48\uff1f\u4e0e\u5176\u5c48\u8fb1\u6c42\u548c\uff0c\u4f55 \u5982\u8a93\u6b7b\u4e00\u6218\uff1f\u201d\u5f20\u98ce\u5e9c\u9053\uff1a\u201c\u4e0d\u7ba1\u5982\u4f55\uff0c\u4f60\u4ee5\u671d\u5ef7\u519b\u5b98\u7684\u8eab\u4efd\uff0c\u88ad\u51fb\u5916\u56fd\u4f7f\u81e3\u8fd9\u7f6a\u540d\u53ef\u4e0d\u5c0f \u5475\uff01\u201d\u90a3\u5c11\u5e74\u519b\u5b98\u9053\uff1a\u201c\u5927\u4e0d\u4e86\u4e5f\u4e0d\u8fc7\u662f\u51cc\u8fdf\u788e\u5250\uff0c\u5f20\u5927\u4eba\uff0c\u4f60\u5c31\u56e0\u6b64\u4e8b\u96be\u51b3\u4e48\uff1f\u4e00\u4eba\u505a\u4e8b \u4e00\u4eba\u5f53\uff0c\u6211\u7edd\u4e0d\u8fde\u7d2f\u4e8e\u4f60\u3002\u5f20\u5927\u4eba\uff0c\u6211\u800c\u4eca\u675f\u624b\u53d7\u7f1a\uff0c\u4f60\u53ef\u4ee5\u653e\u5fc3\u4e86\u5427\uff01\u201d \u3000\u3000\u5f20\u98ce\u5e9c\u5ffd\u5730\u53c8\u662f\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u5343\u91cc\u5144\uff0c\u4f55\u5fc5\u6124\u6124\u5982\u65af\uff1f\u6211\u6240\u8bf4\u7684\u96be\u51b3\u4e4b\u4e8b\uff0c\u4e0e\u4f60\u4e1d\u6beb \u65e0\u6d89\u3002\u201d\u6b64\u8a00\u4e00\u51fa\uff0c\u90a3\u5c11\u5e74\u519b\u5b98\u4f3c\u662f\u6781\u611f\u610f\u5916\uff0c\u8bb7\u8bb7\u8bf4\u9053\uff1a\u201c\u90a3\u3001\u90a3\u3001\u90a3\u53c8\u662f\u4e3a\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5f20\u5185\u5e9c\u5f90\u5f90\u5c55\u5f00\u6587\u4e66\uff0c\u6307\u7740\u90a3\u753b\u50cf\u8bf4\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u9053\u6b64\u4eba\u662f\u8c01\uff1f\u201d\u90a3\u5c11\u5e74\u519b\u5b98\u9762\u8272\u53c8\u662f \u4e00\u53d8\uff0c\u5374\u9053\uff1a\u201c\u8fd9\u4e0d\u662f\u5927\u4eba\u6b64\u6b21\u622a\u83b7\u7684\u5f3a\u76d7\u4e4b\u4e00\u5417\uff1f\u201d\u5f20\u98ce\u5e9c\u9053\uff1a\u201c\u6211\u662f\u60f3\u95ee\u4f60\u77e5\u4e0d\u77e5\u9053\u4ed6 \u7684\u8eab\u4efd\uff1f\u201d\u90a3\u5c11\u5e74\u519b\u5b98\u7565\u4e00\u8fdf\u7591\uff0c\u5ffd\u5730\u4e00\u53e3\u6c14\u7b54\u9053\uff1a\u201c\u4ed6\u662f\u96c1\u95e8\u5173\u5916\u91d1\u5200\u5be8\u4e3b\u5468\u5065\u7684\u552f\u4e00\u7231 \u5b50\uff01\u542c\u8bf4\u5341\u5e74\u4e4b\u524d\uff0c\u5468\u5065\u53db\u51fa\u8fb9\u5173\u88ab\u6ee1\u95e8\u6284\u65a9\uff0c\u5c31\u53ea\u9003\u51fa\u8fd9\u4e2a\u513f\u5b50\u3002\u201d\u5f20\u98ce\u5e9c\u7768\u4ed6\u4e00\u773c\u9053\uff1a \u201c\u4f60\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u77e5\u9053\u7684\u4e8b\u60c5\u53ef\u771f\u4e0d\u5c11\u5475\uff01\u201d \u3000\u3000\u90a3\u5c11\u5e74\u519b\u5b98\u864e\u76ee\u8574\u6cea\uff0c\u9053\uff1a\u201c\u5f20\u5927\u4eba\u2026\u2026\u201d\u5f20\u98ce\u5e9c\u622a\u7740\u8bf4\u9053\uff1a\u201c\u4ece\u4eca\u4e4b\u540e\uff0c\u4f60\u6211\u5144\u5f1f\u76f8 \u4ea4\uff0c\u8bf7\u76f4\u53eb\u6211\u7684\u540d\u53f7\u597d\u4e86\u3002\u201d\u90a3\u5c11\u5e74\u519b\u5b98\u9053\uff1a\u201c\u5f20\u5927\u54e5\uff0c\u5b9e\u4e0d\u76f8\u7792\uff0c\u91d1\u5200\u5468\u5065\u5b9e\u662f\u6211\u5bb6\u7684\u5927 \u6069\u4eba\uff0c\u81f3\u4e8e\u4f55\u4e8b\u4f55\u6069\uff0c\u6055\u6211\u73b0\u5728\u4e0d\u80fd\u5949\u544a\u3002\u201d \u3000\u3000\u5f20\u98ce\u5e9c\u9053\uff1a\u201c\u6211\u4e5f\u770b\u51fa\u4f60\u8eab\u4e16\u6709\u96be\u8a00\u4e4b\u9690\uff0c\u8fd9\u4e2a\u4e0d\u8c08\u3002\u5468\u5065\u7684\u513f\u5b50\u88ab\u6211\u4eec\u64d2\u4e86\uff0c\u4f60\u8bf4\u600e \u751f\u53d1\u843d\uff1f\u201d\u90a3\u5c11\u5e74\u519b\u5b98\u9053\uff1a\u201c\u5179\u4e8b\u4f53\u5927\uff0c\u5c0f\u5f1f\u4e0d\u6562\u7f6e\u5599\u3002\u5440\uff0c\u91d1\u5200\u5be8\u4e3b\u867d\u7136\u662f\u53db\u4e86\u671d\u5ef7\uff0c\u53ef \u662f\u4ed6\u5728\u96c1\u95e8\u5173\u5916\u5c61\u6b21\u6253\u8d25\u80e1\u5175\uff0c\u5012\u4e5f\u662f\u6709\u529f\u4e8e\u56fd\u5440\uff01\u4ed6\u5c31\u53ea\u5269\u4e0b\u8fd9\u4e2a\u513f\u5b50\u4e86\uff0c\u82e5\u7136\u62bc\u89e3\u81f3 \u4eac\uff0c\u5ba1\u95ee\u51fa\u6765\uff0c\u53ea\u6015\u4e5f\u662f\u96be\u9003\u4e00\u6b7b\uff0c\u90a3\u53ef\u771f\u662f\u60e8\u54ea\uff01\u201d\u4ed6\u867d\u53e3\u8bf4\u201c\u4e0d\u6562\u7f6e\u5599\u201d\uff0c\u5176\u5b9e\u5374\u662f\u975e \u5e38\u660e\u663e\u5730\u8bf4\u51fa\u4e86\u81ea\u5df1\u7684\u610f\u601d\uff0c\u60f3\u7528\u8bf4\u8bdd\u6253\u52a8\u5f20\u98ce\u5e9c\u4e4b\u5fc3\uff0c\u5c06\u5468\u5c71\u6c11\u901f\u901f\u91ca\u653e\u3002 \u3000\u3000\u5f20\u98ce\u5e9c\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e0d\u5fc5\u62bc\u89e3\u81f3\u4eac\uff0c\u4e5f\u4e0d\u5fc5\u6709\u52b3\u671d\u5ef7\u5ba1\u95ee\uff0c\u5eb7\u603b\u7ba1\u65e9\u5c31\u77e5\u9053\u4ed6\u7684\u8eab \u4efd\uff0c\u4f46\u5374\u4e5f\u672a\u5fc5\u81f3\u6b7b\u3002\u201d\u90a3\u5c11\u5e74\u519b\u5b98\u9053\uff1a\u201c\u9002\u624d\u9001\u6765\u7684\u516b\u767e\u91cc\u52a0\u7d27\u6587\u4e66\uff0c\u8bf4\u7684\u5c31\u662f\u6b64\u4e8b \u4e48\uff1f\u201d\u5f20\u98ce\u5e9c\u9053\uff1a\u201c\u662f\u5440\uff01\u6211\u6240\u8bf4\u7684\u96be\u51b3\u4e4b\u4e8b\uff0c\u5c31\u5728\u6b64\u4e86\u3002\u5eb7\u603b\u7ba1\u8033\u76ee\u771f\u7075\uff0c\u5df2\u77e5\u5468\u5065\u7684\u513f \u5b50\u5077\u5165\u5185\u5730\uff0c\u4e5f\u77e5\u9053\u6211\u4eec\u6b64\u6b21\u64d2\u83b7\u4e86\u4e0d\u5c11\u7eff\u6797\u4e2d\u6709\u5934\u9762\u7684\u4eba\uff0c\u5c31\u662f\u8fd8\u4e0d\u77e5\u9053\u5468\u5065\u7684\u513f\u5b50\u662f\u5426 \u4e5f\u5728\u4fd8\u864f\u4e4b\u5217\u3002\u6240\u4ee5\u98de\u9a91\u4f20\u62a5\uff0c\u8981\u6211\u4eec\u7559\u610f\u6b64\u4eba\u3002\u82e5\u662f\u5df2\u7ecf\u64d2\u4e86\uff0c\u5c31\u628a\u4ed6\u7684\u7435\u7436\u9aa8\u51ff\u7a7f\uff0c\u628a \u4ed6\u7684\u773c\u73e0\u5b50\u6316\u6389\uff0c\u53eb\u4ed6\u5931\u4e86\u6b66\u529f\uff0c\u522b\u4eba\u4e5f\u5c31\u4e0d\u6613\u5c06\u4ed6\u6551\u8d70\u3002\u7136\u540e\u5eb7\u603b\u7ba1\u8fd8\u8981\u628a\u8fd9\u4e2a\u6b8b\u5e9f\u4e4b\u4eba \u4f5c\u4e3a\u5947\u8d27\uff0c\u8981\u631f\u91d1\u5200\u5be8\u4e3b\uff0c\u53eb\u4ed6\u4e0d\u6562\u62b5\u6297\u5b98\u519b\u3002\u201d\u90a3\u5c11\u5e74\u519b\u5b98\u5931\u58f0\u8bf4\u9053\uff1a\u201c\u8fd9\u4e00\u62db\u53ef\u771f\u6bd2 \u5440\uff01\u201d\u5f20\u98ce\u5e9c\u9053\uff1a\u201c\u4f60\u6211\u5403\u7687\u6069\u53d7\u7687\u7984\uff0c\u666e\u901a\u7684\u5f3a\u76d7\uff0c\u54b1\u4eec\u624b\u5230\u64d2\u6765\uff0c\u9886\u529f\u53d7\u8d4f\uff0c\u90a3\u662f\u5fc3\u5b89 \u7406\u5f97\u3002\u53ef\u662f\u5468\u5065\u7236\u5b50\u53ef\u4e0d\u662f\u666e\u901a\u7684\u5f3a\u76d7\uff0c\u8981\u4e0d\u662f\u4ed6\u4eec\uff0c\u74e6\u523a\u7684\u5927\u519b\u53ea\u6015\u65e9\u5df2\u957f\u9a71\u4fb5\u5165\u4e86\u3002\u201d \u90a3\u5c11\u5e74\u519b\u5b98\u53cc\u76ee\u653e\u5149\uff0c\u559c\u9053\uff1a\u201c\u5f20\u5927\u4eba\uff0c\u4e0d\uff0c\u5f20\u5927\u54e5\uff0c\u90a3\u4f60\u5c31\u5c06\u4ed6\u653e\u4e86\u5427\uff01\u6211\u82e5\u65e9\u77e5\u9053\u4f60\u6709 \u8fd9\u5fc3\u601d\u2026\u2026\u201d\u5f20\u98ce\u5e9c\u7b11\u7740\u622a\u4ed6\u7684\u8bdd\uff1a\u201c\u5c31\u4e0d\u5fc5\u8d39\u8fd9\u4e48\u5927\u529b\u6c14\u53bb\u88ad\u51fb\u756a\u738b\u4e86\uff0c\u662f\u4e0d\u662f\uff1f\u5343\u91cc \u5144\uff0c\u6211\u65e9\u731c\u5230\u4f60\u88ad\u51fb\u756a\u738b\uff0c\u4e43\u662f\u4e00\u77f3\u4e24\u9e1f\u4e4b\u8ba1\u3002\u4f60\u4e0d\u6b32\u4e0e\u6211\u516c\u7136\u4f5c\u5bf9\uff0c\u5728\u6211\u5e10\u4e0b\uff0c\u5077\u653e\u6b64 \u4eba\uff0c\u6240\u4ee5\u60f3\u5047\u624b\u6bd5\u9053\u51e1\u90a3\u4e00\u5e2e\u4eba\u5c06\u756a\u738b\u64d2\u4e86\uff0c\u7528\u6765\u4ea4\u6362\uff0c\u53ef\u662f\u8fd9\u6837\uff1f\u201d\u90a3\u5c11\u5e74\u519b\u5b98\u9053\uff1a\u201c\u5927 \u54e5\uff0c\u4f60\u8bf4\u5f97\u4e00\u70b9\u4e0d\u9519\uff01\u201d \u3000\u3000\u5f20\u98ce\u5e9c\u7b11\u5bb9\u5ffd\u655b\uff0c\u9053\uff1a\u201c\u653e\u4e86\u6b64\u4eba\uff0c\u8bf4\u5f97\u5012\u5f88\u5bb9\u6613\uff0c\u4f60\u96be\u9053\u4e0d\u77e5\u9053\u5eb7\u603b\u7ba1\u7684\u5389\u5bb3\u5417\uff1f\u6211 \u8fd9\u9526\u8863\u536b\u6307\u6325\u56fa\u7136\u505a\u4e0d\u6210\uff0c\u4f60\u60f3\u4e2d\u4eca\u79d1\u7684\u6b66\u72b6\u5143\uff0c\u90a3\u4e5f\u4f11\u60f3\u4e86\u3002\u201d\u5c11\u5e74\u519b\u5b98\u9ed8\u7136\u4e0d\u8bed\uff0c\u826f\u4e45 \u826f\u4e45\uff0c\u6124\u7136\u8bf4\u9053\uff1a\u201c\u6211\u8fd9\u6b66\u72b6\u5143\u4e0d\u8003\u4e5f\u7f62\uff0c\u53ea\u662f\u7d2f\u4e86\u5f20\u5927\u4eba\u7684\u529f\u540d\uff01\u201d\u5f20\u98ce\u5e9c\u9053\uff1a\u201c\u4f55\u51b5\u4e0d \u6b62\u662f\u6389\u4e86\u529f\u540d\uff0c\u53ea\u6050\u751f\u547d\u4e5f\u672a\u5fc5\u80fd\u4fdd\u3002\u201d\u90a3\u5c11\u5e74\u519b\u5b98\u663e\u5f97\u5931\u671b\u4e4b\u6781\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u5f20\u5927\u4eba\u8fd8 \u6709\u4ec0\u4e48\u5429\u5490\uff1f\u201d\u5f20\u98ce\u5e9c\u9053\uff1a\u201c\u4f60\u5230\u5916\u8fb9\u5de1\u591c\uff0c\u9664\u4e86\u6a0a\u5fe0\u4e00\u4eba\u4e4b\u5916\uff0c\u5176\u4ed6\u7684\u4eba\u90fd\u4e0d\u51c6\u51fa\u5165\u3002\u4f60 \u53ef\u4e0d\u8bb8\u8f7b\u4e3e\u5984\u52a8\u3002\u201d\u90a3\u5c11\u5e74\u519b\u5b98\u9053\uff1a\u201c\u5728\u4f60\u5927\u54e5\uff0c\u4e0d\uff0c\u5728\u4f60\u5927\u4eba\u7684\u624b\u4e0b\uff0c\u6211\u5c31\u662f\u6562\u2018\u8f7b\u4e3e\u5984 \u52a8\u2019\uff0c\u4e5f\u9003\u4e0d\u8131\u4f60\u7684\u7f05\u5200\uff0c\u5927\u4eba\uff0c\u4f60\u653e\u5fc3\u597d\u5566\uff01\u201d\u5f20\u98ce\u5e9c\u6325\u624b\u4e00\u7b11\uff1a\u201c\u4e0d\u5fc5\u518d\u8bf4\u6c14\u8bdd\uff0c\u4f60\u53bb \u5427\uff01\u201d\u4e91\u857e\u5728\u6a90\u89d2\u5077\u77a7\uff0c\u89c1\u90a3\u5c11\u5e74\u519b\u5b98\u60bb\u60bb\u800c\u53bb\uff0c\u5fc3\u4e2d\u4e5f\u662f\u597d\u751f\u5931\u671b\u3002 \u3000\u3000\u5f20\u98ce\u5e9c\u53c8\u628a\u4eb2\u5175\u5524\u5165\uff0c\u4f4e\u58f0\u5429\u5490\u4e86\u51e0\u53e5\uff0c\u9063\u4ed6\u51fa\u53bb\uff0c\u4e0d\u4e45\u53c8\u5e26\u4e86\u4e00\u4e2a\u4eba\u5165\u6765\u3002 \u3000\u3000\u8fd9\u4eba\u4e43\u662f\u6a0a\u5fe0\uff0c\u5f20\u98ce\u5e9c\u628a\u6587\u4e66\u7ed9\u4ed6\u770b\u4e86\uff0c\u53ea\u89c1\u4ed6\u53cc\u773c\u4e00\u7ffb\u6d53\u7709\u5012\u7ad6\uff0c\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u5927\u54e5 \u53ef\u8fd8\u8bb0\u5f97\u54b1\u4eec\u6614\u65e5\u7684\u8a93\u8a00\u4e48\uff1f\u201d\u5f20\u98ce\u5e9c\u9053\uff1a\u201c\u5e74\u6df1\u65e5\u4e45\uff0c\u8bb0\u4e0d\u8d77\u4e86\uff01\u201d\u6a0a\u5fe0\u6012\u6c14\u4e0a\u51b2\uff0c\u62cd\u6848 \u8bf4\u9053\uff1a\u201c\u771f\u7684\u5c31\u5fd8\u8bb0\u4e86\uff1f\u201d\u5f20\u98ce\u5e9c\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u4f60\u8bf4\u8bf4\u770b\u3002\u201d\u6a0a\u5fe0\u9053\uff1a\u201c\u62fc\u5c06\u70ed\u8840\uff0c\u4fdd\u536b\u90a6 \u5bb6\u3002\u54b1\u4eec\u662f\u4e0d\u613f\u53d7\u5916\u654c\u6b3a\u51cc\uff0c\u8fd9\u624d\u6295\u519b\u53bb\u7684\u3002\u4e3a\u7684\u53ef\u4e0d\u662f\u5c01\u59bb\u836b\u5b50\uff0c\u5229\u7984\u529f\u540d\uff01\u201d\u987f\u4e86\u4e00 \u987f\uff0c\u53c8\u9053\uff1a\u201c\u6211\u672c\u610f\u662f\u5230\u8fb9\u5173\u4e0a\u53bb\uff0c\u4e00\u5200\u4e00\u67aa\uff0c\u8ddf\u80e1\u5175\u62fc\u4e2a\u75db\u5feb\uff0c\u504f\u504f\u7687\u4e0a\u5374\u8981\u7559\u6211\u505a\u5185\u5ef7 \u536b\u58eb\uff0c\u8fd9\u51e0\u5e74\u53ef\u95f7\u6b7b\u6211\u5566\u3002\u201d\u6b47\u4e86\u4e00\u6b47\u53c8\u9053\uff1a\u201c\u54b1\u4eec\u4e0d\u80fd\u5230\u8fb9\u5173\u53bb\u4eb2\u81ea\u6267\u5e72\u6208\u4ee5\u536b\u793e\u7a37\uff0c\u53cd \u800c\u628a\u529b\u6297\u80e1\u5175\u7684\u91d1\u5200\u5be8\u4e3b\u7684\u513f\u5b50\u5bb3\u4e86\uff0c\u8fd9\u8fd8\u6210\u4ec0\u4e48\u8bdd\uff1f\u201d\u5f20\u98ce\u5e9c\u53c8\u9053\uff1a\u201c\u54b1\u4eec\u8fd8\u6709\u4ec0\u4e48\u8a93 \u8a00\uff1f\u201d\u6a0a\u5fe0\u9053\uff1a\u201c\u6709\u798f\u540c\u4eab\uff0c\u6709\u96be\u6709\u5f53\uff01\u201d\u5f20\u98ce\u5e9c\u9053\uff1a\u201c\u597d\uff0c\u90a3\u76ee\u4e0b\u5c31\u6709\u6869\u5927\u7978\u8981\u4f60\u540c\u5f53\uff01 \u9644\u8033\u8fc7\u6765\u3002\u201d\u5728\u4ed6\u8033\u8fb9\u8bf4\u4e86\u51e0\u53e5\uff0c\u6a0a\u5fe0\u7a81\u7136\u4e00\u63d6\u5230\u5730\uff0c\u9053\uff1a\u201c\u5927\u54e5\u6055\u6211\u9002\u624d\u9c81\u83bd\uff0c\u4f60\u4ea4\u4ee3\u7684 \u4e8b\u4e07\u9519\u4e0d\u4e86\uff01\u201d\u8f6c\u8eab\u8d70\u51fa\uff0c\u5f20\u98ce\u5e9c\u559f\u7136\u53f9\u9053\uff1a\u201c\u53ea\u6015\u4f60\u7684\u4e8c\u54e5\u4e0d\u662f\u540c\u6837\u5fc3\u80a0\u3002\u201d\u6a0a\u5fe0\u9053\uff1a \u201c\u54ea\u7ba1\u5f97\u8bb8\u591a\u3002\u201d\u5934\u4e5f\u4e0d\u56de\uff0c\u5927\u6b65\u8d70\u51fa\u3002 \u3000\u3000\u4e91\u857e\u5fc3\u9053\uff1a\u201c\u539f\u6765\u8fd9\u4e24\u4eba\u5012\u4e5f\u662f\u70ed\u8840\u6c49\u5b50\u3002\u201d\u6b63\u60f3\u8ddf\u8e2a\u6a0a\u5fe0\u770b\u4ed6\u5e72\u7684\u4ec0\u4e48\uff0c\u5ffd\u89c1\u5f20\u98ce\u5e9c \u671d\u81ea\u5df1\u8fd9\u65b9\u5411\u4e00\u7b11\uff0c\u62db\u624b\u8bf4\u9053\uff1a\u201c\u8bf7\u4e0b\u6765\u5427\uff01\u4f60\u5012\u6302\u6a90\u4e0a\u8fd9\u4e48\u4e9b\u65f6\u5019\uff0c\u8fd8\u4e0d\u7d2f\u4e48\uff1f\u201d\u4e91\u857e\u5fae \u5fae\u4e00\u7b11\uff0c\u98d8\u8eab\u843d\u5730\uff0c\u62f1\u624b\u8bf4\u9053\uff1a\u201c\u5f20\u5927\u4eba\uff0c\u54b1\u4eec\u662f\u670b\u53cb\u5566\u3002\u201d\u5f20\u98ce\u5e9c\u9053\uff1a\u201c\u4f60\u662f\u4e3a\u4e86\u6551\u5468\u5c71 \u6c11\u800c\u6765\u7684\uff0c\u662f\u4e48\uff1f\u201d\u4e91\u857e\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u4eec\u7684\u8bdd\u6211\u90fd\u542c\u89c1\u5566\uff0c\u5c31\u70e6\u4f60\u628a\u4ed6\u4ea4\u4e0e\u6211\u5427\u3002\u201d\u5f20\u98ce \u5e9c\u4e00\u7b11\u8bf4\u9053\uff1a\u201c\u4ea4\u4f60\u5e26\u4ed6\u56de\u53bb\uff1f\u8fd9\u5c82\u4e0d\u8981\u60ca\u52a8\u4f17\u4eba\uff1f\u4e8b\u60c5\u8d25\u9732\uff0c\u4f60\u5c31\u4e0d\u4e3a\u6211\u8bbe\u60f3\u4e48\uff1f\u201d\u4e91\u857e \u4e00\u6014\uff0c\u60f3\u8d77\u73b0\u4e0b\u5f62\u52bf\u5df2\u53d8\uff0c\u5df2\u7ecf\u4e0d\u5fc5\u786c\u6765\uff0c\u81ea\u5df1\u8003\u8651\uff0c\u679c\u6b20\u5468\u8be6\uff0c\u4e0d\u89c9\u9762\u6709\u5c34\u5c2c\u4e4b\u8272\u3002\u5f20\u98ce \u5e9c\u53c8\u662f\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6a0a\u5fe0\u6b64\u65f6\u5df2\u628a\u4f60\u7684\u5468\u5927\u54e5\u5077\u5077\u5e26\u51fa\u53bb\u5566\uff0c\u6211\u53eb\u4ed6\u4eec\u5728\u5317\u95e8\u4e4b\u5916\u7b49 \u4f60\u3002\u201d\u4e91\u857e\u5927\u559c\uff0c\u4fbf\u5f85\u98de\u8eab\u4e0a\u5c4b\u3002\u5f20\u98ce\u5e9c\u5ffd\u9053\uff1a\u201c\u4e14\u6162\uff01\u201d\u4e91\u857e\u8f6c\u8eab\u8bf4\u9053\uff1a\u201c\u8fd8\u6709\u4f55\u4e8b\uff1f\u201d \u5f20\u98ce\u5e9c\u9053\uff1a\u201c\u4f60\u90a3\u4f4d\u9a91\u767d\u9a6c\u7684\u670b\u53cb\u5462\uff1f\u201d\u4e91\u857e\u9762\u70ed\u5fc3\u8df3\uff0c\u98a4\u58f0\u8bf4\u9053\uff1a\u201c\u4ed6\u6709\u4ed6\u8d70\uff0c\u6211\u6709\u6211 \u8d70\uff0c\u600e\u77e5\u4ed6\u5230\u4e86\u4f55\u65b9\uff1f\u201d\u5f20\u98ce\u5e9c\u597d\u8be7\u5f02\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u4e8c\u4eba\u53cc\u5251\u5408\u74a7\uff0c\u5999\u7edd\u5929\u4e0b\uff0c\u5c82\u53ef\u5206\u5f00\uff1f \u4f60\u90a3\u4f4d\u670b\u53cb\u5668\u5b87\u975e\u51e1\uff0c\u4ee4\u4eba\u4e00\u89c1\u503e\u5fc3\u3002\u4f60\u82e5\u518d\u89c1\u4ed6\u65f6\uff0c\u8bf7\u4ee3\u6211\u5411\u4ed6\u81f4\u610f\u3002\u201d\u4e91\u857e\u9053\uff1a\u201c\u6211\u4e5f \u672a\u5fc5\u80fd\u89c1\u7740\u4ed6\uff0c\u6211\u8bb0\u4e0b\u4f60\u7684\u8bdd\u4fbf\u662f\uff0c\u544a\u8f9e\u4e86\u3002\u201d\u5f20\u98ce\u5e9c\u53c8\u9053\uff1a\u201c\u4e14\u6162\uff01\u201d \u3000\u3000\u4e91\u857e\u751a\u89c9\u70e6\u8e81\uff0c\u56de\u5934\u9053\uff1a\u201c\u8fd8\u6709\u4f55\u4e8b\uff1f\u201d\u5f20\u98ce\u5e9c\u9053\uff1a\u201c\u90a3\u9707\u4e09\u754c\u6bd5\u9053\u51e1\u73b0\u5728\u4f55\u65b9\uff1f\u201d\u4e91 \u857e\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u83ab\u975e\u6bd5\u8001\u524d\u8f88\u7684\u884c\u85cf\u4ea6\u5df2\u88ab\u4ed6\u7aa5\u7834\uff1f\u201d\u4e45\u4e45\u4e0d\u7b54\u3002\u5f20\u98ce\u5e9c\u4e00\u7b11\u9053\uff1a \u201c\u4f60\u4e0d\u80af\u8bf4\uff0c\u4e5f\u5c31\u7b97\u5566\u3002\u70e6\u4f60\u8f6c\u544a\u4e8e\u4ed6\uff0c\u4ed6\u53ef\u4e0d\u6bd4\u91d1\u5200\u5be8\u4e3b\uff0c\u6211\u5949\u7687\u547d\u6355\u4ed6\uff0c\u4e07\u4e07\u4e0d\u80fd\u5f87\u79c1 \u91ca\u653e\uff0c\u770b\u5728\u4ed6\u4e5f\u7b97\u5f97\u662f\u4e00\u6761\u597d\u6c49\uff0c\u8bf7\u4ed6\u8fdc\u8fdc\u907f\u5f00\uff0c\u514d\u5f97\u5927\u5bb6\u78b0\u9762\uff01\u597d\u4e86\uff0c\u4e3a\u670b\u53cb\u53ea\u80fd\u505a\u5230\u5982 \u6b64\u5730\u6b65\u4f60\u8d70\u5427\uff01\u201d \u3000\u3000\u4e91\u857e\u98de\u8eab\u4e0a\u5c4b\uff0c\u60f3\u90a3\u5f20\u98ce\u5e9c\u884c\u5f84\uff0c\u751a\u662f\u51fa\u4e4e\u81ea\u5df1\u610f\u5916\u3002\u60f3\u8d77\u8fd9\u6837\u4e00\u4f4d\u672c\u6765\u5177\u6709\u4fa0\u4e49\u5fc3\u80a0 \u7684\u70ed\u8840\u7537\u5b50\uff0c\u5374\u4e3a\u7687\u5e1d\u4e00\u5bb6\u4e00\u59d3\u5356\u547d\uff0c\u53c8\u4e0d\u89c9\u66ff\u4ed6\u5341\u5206\u4e0d\u503c\u3002\u9661\u7136\u53c8\u60f3\u8d77\u81ea\u5df1\u7684\u7237\u7237\uff0c\u4e3a\u4e86 \u4fdd\u5168\u5927\u660e\u4f7f\u8282\uff0c\u6371\u4e86\u591a\u5c11\u5e74\u82e6\u96be\uff0c\u5374\u7ec8\u4e8e\u8840\u6e85\u56fd\u95e8\uff0c\u4e0d\u89c9\u5583\u5583\u81ea\u8bed\u9053\uff1a\u201c\u611a\u5fe0\u4e8c\u5b57\uff0c\u4e0d\u77e5\u5bb3 \u4e86\u591a\u5c11\u82f1\u96c4\u8c6a\u6770\uff01\u201d\u4e91\u857e\u5e74\u7eaa\u8f7b\u8f7b\u672c\u4e0d\u4f1a\u60f3\u5230\u8fd9\u4e9b\u5343\u53e4\u4ee5\u6765\u4ee4\u4eba\u56f0\u60d1\u7684\u95ee\u9898\uff0d\uff0d\u5fe0\u4e8e\u541b\u4e0e\u5fe0 \u4e8e\u56fd\u7684\u533a\u522b\uff0c\u5728\u5c01\u5efa\u793e\u4f1a\u4e4b\u4e2d\uff0c\u82e5\u975e\u6709\u5927\u667a\u6167\u4e4b\u4eba\uff0c\u5b9e\u662f\u4e0d\u6613\u5206\u8fa8\u6e05\u695a\u3002\u53ea\u56e0\u5979\u4e0e\u5f20\u4e39\u67ab\u591a \u65f6\u76f8\u5904\uff0c\u4e0d\u77e5\u4e0d\u89c9\u4e4b\u95f4\uff0c\u63a5\u53d7\u4e86\u4ed6\u7684\u89c2\u5ff5\u4e0e\u718f\u9676\uff0c\u6545\u6b64\u6562\u4e8e\u8511\u89c6\u4ed6\u7237\u7237\u90a3\u4ee3\u5949\u4e3a\u91d1\u79d1\u7389\u5f8b\u7684 \u5fe0\u541b\u601d\u60f3\u3002 \u3000\u3000\u4e91\u857e\u5fc3\u5185\u601d\u6f6e\u8d77\u4f0f\uff0c\u811a\u6b65\u5374\u662f\u4e1d\u6beb\u4e0d\u7f13\uff0c\u970e\u65f6\u95f4\uff0c\u51fa\u4e86\u5ba2\u5e97\uff0c\u98de\u8eab\u63a0\u4e0a\u5bf9\u9762\u6c11\u623f\uff0c\u4f46\u89c1 \u6597\u8f6c\u661f\u6a2a\uff0c\u5df2\u662f\u7f69\u66f4\u65f6\u5206\uff0c\u6bd5\u9053\u51e1\u672c\u662f\u5728\u5ba2\u5e97\u5916\u9762\u66ff\u5979\u628a\u98ce\uff0c\u8fd9\u65f6\u4e91\u857e\u7eb5\u76ee\u56db\u987e\uff0c\u5374\u662f\u6773\u65e0 \u4eba\u5f71\u3002\u4e91\u857e\u8f7b\u8f7b\u51fb\u4e86\u4e09\u4e0b\u624b\u638c\uff0c\u6bd5\u9053\u51e1\u4f0f\u5730\u542c\u58f0\u7684\u672c\u9886\u5341\u5206\u9ad8\u660e\u82e5\u7136\u4ed6\u5728\u9644\u8fd1\uff0c\u8fd9\u4e09\u4e0b\u638c \u58f0\uff0c\u5b9a\u80fd\u542c\u89c1\uff0c\u8fc7\u4e86\u4e00\u9635\uff0c\u65e2\u4e0d\u95fb\u638c\u58f0\u56de\u5e94\uff0c\u4ea6\u4e0d\u89c1\u4eba\u5f71\u51fa\u73b0\u3002\u4e91\u857e\u4e0d\u89c9\u5012\u5438\u4e00\u53e3\u51c9\u6c14\u5fc3\u91cc \u7740\u614c\u3002\u6bd5\u9053\u51e1\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u4ed6\u662f\u6c5f\u6e56\u4e0a\u7684\u5927\u884c\u5bb6\u3001\u8001\u524d\u8f88\uff0c\u65ad\u65e0\u53d7\u4eba\u6697\u7b97\u4e4b\u7406\uff0c\u5373\u8bf4\u662f\u4ed6\u89c1 \u4e86\u5468\u5c71\u6c11\uff0c\u4e5f\u5e94\u8be5\u7b49\u81ea\u5df1\u51fa\u6765\uff0c\u4e00\u9f50\u56de\u53bb\uff0c\u4e8e\u7406\u4e8e\u60c5\uff0c\u65ad\u4e0d\u4f1a\u4e0d\u89c1\u4e91\u857e\uff0c\u4fbf\u6084\u6084\u6e9c\u8d70\u3002\u90a3 \u4e48\uff0c\u6bd5\u9053\u51e1\u5230\u5e95\u5230\u54ea\u91cc\u53bb\u4e86\uff1f \u3000\u3000\u4e91\u857e\u56db\u4e0b\u4e00\u671b\uff0c\u5438\u4e00\u53e3\u6c14\uff0c\u65bd\u5c55\u7edd\u9876\u8f7b\u529f\uff0c\u5728\u5468\u56f4\u91cc\u8bb8\u4e4b\u5730\u515c\u4e86\u4e24\u4e2a\u5708\u5b50\uff0c\u7ec6\u5fc3\u641c\u7d22\uff0c \u4ecd\u662f\u4e0d\u89c1\u4eba\u5f71\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u96be\u9053\u662f\u5f20\u98ce\u5e9c\u53d1\u73b0\u4e86\u4ed6\u7684\u8e2a\u8ff9\uff0c\u9884\u5148\u5e03\u4e0b\u57cb\u4f0f\uff0c\u5c06\u4ed6\u64d2\u4e86\uff1f\u4e0d \u4f1a\u5440\uff0c\u4e0d\u4f1a\uff01\u90a3\u5f20\u98ce\u5e9c\u4e00\u76f4\u5c31\u5728\u91cc\u9762\uff0c\u9664\u4e86\u5f20\u98ce\u5e9c\u4e4b\u5916\uff0c\u5fa1\u6797\u519b\u7684\u519b\u5b98\u6ca1\u4e00\u4e2a\u662f\u6bd5\u9053\u51e1\u7684\u5bf9 \u624b\uff0c\u5373\u7b97\u662f\u5f20\u98ce\u5e9c\uff0c\u4e5f\u975e\u6597\u4e2a\u4e09\u4e94\u767e\u62db\uff0c\u4e0d\u6613\u5206\u51fa\u80dc\u8d1f\u3002\u90a3\u53c8\u600e\u4f1a\u6beb\u65e0\u58f0\u54cd\uff0c\u4fbf\u88ab\u6349\u53bb\u4e4b \u7406\uff1f\u82e5\u8bf4\u4e0d\u662f\u5fa1\u6797\u519b\u7684\u519b\u5b98\uff0c\u53e6\u6709\u9ad8\u624b\uff0c\u5c06\u4ed6\u6697\u7b97\uff0c\u90a3\u4e48\u80fd\u4e0d\u52a8\u58f0\u606f\u800c\u80fd\u5c06\u6bd5\u9053\u51e1\u52ab\u53bb\u7684 \u4eba\uff0c\u6b66\u529f\u5b9e\u662f\u4e0d\u53ef\u601d\u8bae\u3002\u5f53\u4eca\u4e4b\u4e16\uff0c\u4e5f\u672a\u5fc5\u6709\u8fd9\u6837\u7684\u4eba\u3002\u201d\u4e91\u857e\u8d8a\u60f3\u8d8a\u614c\uff0c\u7d22\u6027\u76f4\u5f80\u5317\u95e8\u5954 \u53bb\uff0c\u4e0d\u987b\u4e00\u76cf\u8336\u7684\u65f6\u523b\uff0c\u5df2\u5230\u4e86\u57ce\u5916\u90ca\u533a\uff0c\u8fd9\u662f\u5f20\u98ce\u5e9c\u6240\u8bf4\uff0c\u6a0a\u5fe0\u4e0e\u5468\u5c71\u6c11\u7b49\u5979\u4e4b\u5904\u3002\u4e91\u857e \u51fb\u638c\u76f8\u547c\uff0c\u767b\u9ad8\u7eb5\u76ee\uff0c\u4f46\u53ea\u89c1\u661f\u6cb3\u803f\u803f\uff0c\u660e\u6708\u5728\u5929\uff0c\u5bd2\u86f0\u54c0\u9e23\uff0c\u591c\u51c9\u5982\u6c34\u3002\u4f11\u8bf4\u4e0d\u89c1\u6a0a\u5fe0\u4e0e \u5468\u5c71\u6c11\u4e8c\u4eba\uff0c\u6574\u4e2a\u90ca\u91ce\u90fd\u50cf\u7761\u53bb\u4e00\u822c\uff0c\u5bc2\u9759\u5f97\u4ee4\u4eba\u5bb3\u6015\u3002 \u3000\u3000\u4e91\u857e\u53c8\u60ca\u53c8\u6012\uff0c\u5fc3\u9053\uff1a\u201c\u83ab\u975e\u8fd9\u662f\u5f20\u98ce\u5e9c\u5f04\u7684\u7384\u865a\uff0c\u6211\u600e\u80fd\u542c\u4ed6\u4e00\u9762\u4e4b\u8a00\uff1f\u6562\u60c5\u4ed6\u6839\u672c \u5c31\u6ca1\u6709\u91ca\u653e\u5c71\u6c11\u5927\u54e5\uff1f\u4f46\u4ed6\u5374\u53c8\u4f55\u5fc5\u6765\u9a97\u6211\u6765\u6b64\uff1f\u201d\u4e91\u857e\u6ee1\u8179\u7591\u56e2\uff0c\u767e\u601d\u4e0d\u89e3\uff0c\u6298\u56de\u8eab\u53c8\u5411 \u57ce\u4e2d\u5954\u53bb\u3002 \u3000\u3000\u5230\u4e86\u5ba2\u5e97\u4e4b\u5916\uff0c\u5ffd\u89c1\u5916\u9762\u5927\u95e8\u865a\u63a9\uff0c\u66f4\u662f\u60ca\u8be7\uff0c\u7d22\u6027\u63a8\u95e8\u8fdb\u53bb\uff0c\u95e8\u5185\u9662\u5b50\uff0c\u672c\u6765\u7cfb\u6709\u5341 \u4f59\u5339\u9a6c\uff0c\u8fd9\u65f6\u53ea\u89c1\u6bcf\u5339\u9a6c\u90fd\u72b6\u5982\u4eba\u7acb\uff0c\u524d\u9762\u4e24\u8e44\u9ad8\u9ad8\u4e3e\u8d77\uff0c\u8e22\u5b83\u4e0d\u52a8\uff0c\u4ea6\u4e0d\u5636\u9e23\uff0c\u5728\u6708\u5149\u4e4b \u4e0b\u66f4\u663e\u5f97\u602a\u5f02\u65e0\u4f26\uff0c\u4ee4\u4eba\u6bdb\u9aa8\u609a\u7136\u3002 \u3000\u3000\u4e91\u857e\u5b9a\u4e00\u5b9a\u795e\uff0c\u60f3\u8d77\u8fd9\u662f\u9ed1\u767d\u6469\u8bc3\u5236\u670d\u9a6c\u5339\u7684\u624b\u6cd5\uff0c\u66f4\u662f\u5927\u611f\u60ca\u5947\uff1a\u8fd9\u4e24\u4e2a\u6469\u5934\uff0c\u9ed1\u767d \u4e24\u9053\u5168\u4e0d\u4e70\u5e10\uff0c\u4eba\u4e0d\u72af\u4ed6\u4ea6\u4e0d\u72af\u522b\u4eba\uff0c\u5728\u9752\u9f99\u5ce1\u4e2d\uff0c\u4ed6\u4eec\u867d\u66fe\u6697\u52a9\u81ea\u5df1\u4e00\u81c2\u4e4b\u529b\uff0c\u5374\u4e5f\u53ea\u662f \u72c2\u51b2\u75be\u95ef\u800c\u8fc7\uff0c\u672a\u4e0e\u5b98\u519b\u4f5c\u6218\uff0c\u7f18\u4f55\u5374\u8981\u6df1\u591c\u5230\u6b64\uff0c\u4f5c\u5f04\u5b98\u519b\uff1f \u3000\u3000\u4e91\u857e\u6599\u77e5\u82e5\u662f\u9ed1\u767d\u6469\u8bc3\u5230\u6b64\uff0c\u5fc5\u7136\u5c1a\u6709\u4e0b\u6587\uff0c\u98de\u8eab\u4e0a\u5c4b\uff0c\u51dd\u795e\u7ec6\u542c\u3002\u8fd9\u5ba2\u5e97\u91cc\u8fde\u4f4f\u5bbf\u7684 \u5b98\u519b\u5728\u5185\uff0c\u603b\u6709\u516d\u4e03\u5341\u4eba\uff0c\u5374\u7adf\u81ea\u542c\u4e0d\u51fa\u534a\u70b9\u58f0\u606f\uff0c\u8fde\u9f3e\u58f0\u4e5f\u65e0\uff0c\u51b7\u68ee\u68ee\u6e05\u5bc2\u5bc2\u5730\uff0c\u7b80\u76f4\u6709 \u5982\u4e00\u5ea7\u53e4\u575f\u3002\u4e91\u857e\u98de\u8eab\u843d\u4e0b\u5185\u9662\uff0c\u60f3\u627e\u5ba2\u5e97\u4e2d\u7684\u4f19\u8ba1\uff0c\u53ea\u89c1\u623f\u95e8\u5927\u5f00\uff0c\u90a3\u66fe\u7ecf\u7ed9\u81ea\u5df1\u5e26\u8fc7\u8def \u7684\u5e97\u5c0f\u4e8c\uff0c\u719f\u7761\u5982\u6b7b\uff0c\u63a8\u4ed6\u634f\u4ed6\uff0c\u6beb\u65e0\u77e5\u89c9\uff1b\u63a2\u4ed6\u9f3b\u7aef\uff0c\u5374\u662f\u6709\u6c14\uff1b\u8bd5\u884c\u63a8\u62ff\u53c8\u4e0d\u4f3c\u88ab\u4eba\u70b9 \u7a74\u3002\u518d\u770b\u53e6\u5916\u51e0\u95f4\u5ba2\u5e97\u4f19\u8ba1\u81ea\u5df1\u4f4f\u7684\u623f\u95f4\uff0c\u4e5f\u5c3d\u90fd\u5982\u6b64\uff0c\u8fde\u90a3\u4e2a\u6b66\u529f\u9887\u6709\u6839\u5e95\u7684\u638c\u67dc\uff0c\u4e5f\u662f \u762b\u5728\u5e8a\u4e0a\u7f29\u4f5c\u4e00\u56e2\uff0c\u72b9\u5982\u6b7b\u53bb\u4e00\u822c\u3002\u4e91\u857e\u5fc3\u60f3\uff1a\u201c\u95fb\u9053\u6c5f\u6e56\u4e0a\u6709\u4e00\u79cd\u91c7\u82b1\u8d3c\u5e38\u7528\u7684\u8ff7\u9999\uff0c\u55c5 \u4e86\u8ff7\u9999\u53ef\u4ee5\u4ee4\u4eba\u719f\u7761\u5982\u6b7b\uff0c\u83ab\u975e\u662f\u4e2d\u4e86\u8ff7\u9999\uff1f\u201d\u76db\u4e86\u4e00\u7897\u51b7\u6c34\uff0c\u55b7\u90a3\u638c\u67dc\uff0c\u53ea\u89c1\u4ed6\u624b\u81c2\u5fae\u5fae \u62bd\u52a8\u4e86\u4e00\u4e0b\uff0c\u4ecd\u662f\u4e0d\u9192\uff0c\u53c8\u4e0d\u4f3c\u662f\u4e2d\u4e86\u8ff7\u9999\u3002 \u3000\u3000\u4e91\u857e\u7eb5\u518d\u80c6\u5927\uff0c\u8fd9\u65f6\u4e5f\u5fc3\u614c\u4e86\uff0c\u8dd1\u51fa\u5916\u9762\u3002\u4f46\u89c1\u6bcf\u95f4\u623f\u90fd\u662f\u623f\u95e8\u5927\u5f00\uff0c\u4f4f\u623f\u95f4\u7684\u519b\u5b98\u4e0e \u5728\u5927\u5385\u4e0a\u6253\u5730\u94fa\u7684\u5b98\u519b\uff0c\u4e00\u4e2a\u4e2a\u90fd\u662f\u6c89\u6c89\u719f\u7761\u3002\u6709\u7684\u624b\u811a\u4f38\u5f00\uff0c\u5f62\u5982\u4e00\u4e2a\u201c\u5927\u201d\uff1b\u6709\u7684\u534a\u9760 \u7740\u5899\uff0c\u53cc\u76ee\u7d27\u95ed\uff0c\u5934\u5782\u81f3\u80a9\uff0c\u4f3c\u662f\u6b63\u6b20\u8eab\u6b32\u8d77\uff0c\u5374\u7a81\u7136\u4e2d\u4e86\u201c\u5996\u6cd5\u201d\uff0c\u5c31\u6b64\u7761\u53bb\uff1b\u6709\u7684\u5634\u5df4 \u5f20\u5f00\uff0c\u9762\u4e0a\u8868\u60c5\u5343\u5947\u767e\u602a\uff0c\u597d\u4f3c\u521a\u521a\u5f20\u53e3\u5927\u54b1\uff0c\u5c31\u7a81\u7136\u7ed9\u4eba\u5236\u4f4f\u3002\u4e91\u857e\u5413\u5f97\u51b7\u6c57\u76f4\u5192\uff0c\u5927\u53eb \u4e00\u58f0\u56db\u9762\u5899\u58c1\u6321\u7740\u58f0\u97f3\uff0c\u56de\u58f0\u55e1\u55e1\u4f5c\u54cd\uff0c\u4e91\u857e\u5982\u7f6e\u8eab\u575f\u5730\u4e4b\u4e2d\uff0c\u9664\u4e86\u81ea\u5df1\uff0c\u5c31\u518d\u4e5f\u6ca1\u6709\u4e00\u4e2a \u751f\u4eba\u3002 \u3000\u3000\u4e91\u857e\u5b9a\u4e86\u5b9a\u795e\uff0c\u60f3\u90a3\u5f20\u98ce\u5e9c\u6b66\u529f\u6781\u9ad8\uff0c\u90a3\u5c11\u5e74\u519b\u5b98\u4ea6\u662f\u4e00\u628a\u597d\u624b\uff0c\u7eb5\u7136\u662f\u9ed1\u767d\u6469\u8bc3\u5230 \u6b64\uff0c\u4e5f\u672a\u5fc5\u80fd\u5360\u4e0a\u98ce\uff0c\u600e\u4f1a\u4e00\u4e0b\u5c31\u7ed9\u4ed6\u4eec\u5f04\u6210\u8fd9\u4e2a\u5149\u666f\uff1f\u4e91\u857e\u518d\u5954\u5230\u540e\u9662\uff0c\u770b\u90a3\u516d\u8f86\u56da\u8f66\uff0c \u53ea\u89c1\u8f66\u95e8\u94c1\u69db\uff0c\u5168\u7ed9\u5229\u5668\u5207\u65ad\uff0c\u8f66\u4e2d\u66f4\u65e0\u534a\u4e2a\u56da\u4eba\uff0c\u9ed1\u767d\u6469\u8bc3\u4f7f\u7684\u53c8\u4e0d\u662f\u5b9d\u5200\u5b9d\u5251\uff0c\u90a3\u4e48\u8fd9 \u5374\u53c8\u662f\u4f55\u4eba\u6240\u505a\u7684\u624b\u811a\uff1f \u3000\u3000\u96be\u9053\u5f20\u98ce\u5e9c\u4e5f\u66fe\u7ed9\u6765\u4eba\u6697\u7b97\u4e0d\u6210\uff1f\u4e91\u84c4\u8d8a\u60f3\u8d8a\u7591\uff0c\u53c8\u5411\u5f20\u98ce\u5e9c\u6240\u4f4f\u7684\u90a3\u95f4\u623f\u95f4\u5954\u53bb\u3002\u6ee1 \u5ba2\u5e97\u623f\u95e8\u5927\u5f00\uff0c\u53ea\u6709\u5f20\u98ce\u5e9c\u8fd9\u95f4\u623f\u95e8\u7d27\u95ed\uff0c\u4e91\u857e\u4e00\u811a\u8e22\u5f00\u623f\u95e8\uff0c\u5f20\u98ce\u5e9c\u4e5f\u4e0d\u89c1\uff01 \u3000\u3000\u518d\u4e00\u770b\uff0c\u53ea\u89c1\u5899\u4e0a\u7528\u9ed1\u70ad\u753b\u7740\u4e24\u7247\u9ab7\u9ac5\u5934\u9aa8\uff0c\u90a3\u662f\u9ed1\u767d\u6469\u8bc3\u7684\u6807\u8bb0\uff0c\u96be\u9053\u5f20\u98ce\u5e9c\u7ed9\u8fd9\u4e24 \u4e2a\u9b54\u5934\u5bb3\u4e86\uff1f\u5730\u4e0b\u5374\u53c8\u65e0\u534a\u70b9\u8840\u8ff9\u3002\u518d\u8005\u4ee5\u5f20\u98ce\u5e9c\u6b66\u529f\u4e4b\u9ad8\uff0c\u7eb5\u7136\u4e0d\u654c\u9ed1\u767d\u6469\u8bc3\u8054\u624b\u4e4b\u529b\uff0c \u6599\u60f3\u4e5f\u8be5\u6709\u4e00\u573a\u6fc0\u6218\uff0c\u623f\u4e2d\u5668\u7269\u5374\u662f\u4e00\u4e00\u5b8c\u6574\uff0c\u53f0\u4e0d\u7ffb\uff0c\u6905\u4e0d\u5012\uff0c\u5b8c\u5168\u4e0d\u50cf\u52a8\u8fc7\u624b\u7684\u6837\u4e86\u3002 \u6b64\u4e8b\u771f\u662f\u65b9\u5206\u4e0d\u53ef\u6069\u8bae\uff01 \u3000\u3000\u4e91\u857e\u518d\u7ec6\u770b\u65f6\uff0c\u53ea\u89c1\u4e0e\u753b\u6709\u9ab7\u9ac5\u5934\u9aa8\u7684\u5bf9\u9762\u5892\u58c1\u4e0a\uff0c\u8fd8\u9762\u6709\u4e24\u6837\u4e1c\u897f\uff0c\u4e00\u884c\u5927\u5b57\u3002 \u3000\u3000\u6b63\u4e2d\u753b\u7684\u662f\u4e00\u4e2a\u957f\u81c2\u733f\u7334\uff0c\u9762\u76ee\u72f0\u72de\u4f5c\u652b\u4eba\u4e4b\u72b6\uff0c\u5de6\u9762\u753b\u7684\u662f\u4e00\u67c4\u957f\u5251\uff0e\u5251\u5c16\u523a\u82e5\u4e00\u6735 \u7ea2\u82b1\uff0c\u8fd8\u6709\u4e24\u6735\u767d\u82b1\u8054\u7ed3\u4e24\u65c1\uff0c\u663e\u5f97\u5341\u5206\u8be1\u5f02\u3002\u90a3\u884c\u5927\u5b57\u662f\uff1a\u94c1\u81c2\u91d1\u733f\u4e09\u82b1\u5251\uff0c\u8981\u524a\u6469\u8bc3\u9ed1 \u767d\u5934\uff01\u65bd\u6697\u7b97\u4e0d\u662f\u82f1\u96c4\uff0c\u6709\u80c6\u8005\u8bf7\u5230\u9752\u9f99\u5ce1\u4e00\u6218\uff01 \u3000\u3000\u4e91\u857e\u5ff5\u9053\uff1b\u201c\u94c1\u81c2\u91d1\u733f\u4e09\u82b1\u5251\u201d\uff0c\u60f3\u8d77\u5e08\u5085\u7ed9\u5979\u8bb2\u8fc7\u7684\u5f53\u4ee3\u6b66\u6797\u4eba\u7269\uff0c\u70b9\u82cd\u6d3e\u9886\u8896\u51cc\u9704 \u5b50\u7684\u4e24\u4e2a\u5e08\u5f1f\uff0c\u4e00\u4e2a\u53f7\u79f0\u94c1\u81c2\u91d1\u733f\u9f99\u9547\u65b9\uff0c\u4e00\u4e2a\u53f7\u79f0\u4e09\u82b1\u5251\u7384\u7075\u5b50\uff0c\u5404\u6709\u7279\u5f02\u7684\u6b66\u529f\uff0c\u8f88\u5206 \u6781\u9ad8\uff0c\u4e3a\u4eba\u4eba\u5728\u90aa\u6b63\u4e4b\u95f4\uff0c\u4f46\u8fd1\u5341\u4f59\u5e74\uff0c\u5df2\u5728\u70b9\u77f3\u82cd\u4e0a\u6f5c\u4fee\u4e0a\u4e58\u5185\u529f\uff0c\u7edd\u8ff9\u6c5f\u6e56\u3002\u518d\u8bf4\u4ed6\u4eec \u4e0e\u9ed1\u767d\u6469\u8bc3\u98ce\u9a6c\u725b\u4e0d\u76f8\u6d89\uff0c\u56e0\u4f55\u5374\u4f1a\u5728\u5f20\u98ce\u5e9c\u7684\u623f\u4e2d\uff0c\u7559\u5b57\u6311\u6218\uff1f\u770b\u8fd9\u60c5\u5f62\uff0c\u4f3c\u662f\u9ed1\u767d\u6469\u8bc3 \u5148\u5230\uff0c\u94c1\u81c2\u91d1\u733f\u4e0e\u4e09\u82b1\u5251\u540e\u6765\u3002 \u3000\u3000\u4e00\u8fde\u4e32\u7684\u602a\u4e8b\u5f04\u5f97\u4e91\u857e\u4e0d\u77e5\u6240\u63aa\uff0c\u63a8\u5f00\u623f\u95e8\uff0c\u8d70\u5230\u5916\u9762\uff0c\u518d\u5230\u5904\u5de1\u89c6\uff0c\u884c\u5230\u540e\u9662\u4fa7\u95e8\uff0c \u5ffd\u53c8\u53d1\u73b0\u4e00\u6869\u602a\u4e8b\u3002 \u3000\u3000\u53ea\u89c1\u90a3\u5c11\u5e74\u519b\u5b98\u6a2a\u5200\u5f53\u80f8\uff0c\u524d\u8db3\u63d0\u8d77\uff0c\u4f3c\u662f\u6b63\u5728\u98de\u5954\uff0c\u5374\u5ffd\u7136\u7ed9\u4eba\u7528\u201c\u5b9a\u8eab\u6cd5\u201d\u5b9a\u4f4f\uff0c \u77aa\u7740\u53cc\u773c\uff0c\u5589\u5934\u683c\u683c\u4f5c\u54cd\uff0c\u751a\u662f\u9a87\u4eba\u3002\u8fd9\u5f62\u72b6\u5c31\u6b63\u5982\u4e91\u857e\u5728\u77f3\u82f1\u5bb6\u4e2d\u6240\u89c1\u8fc7\u7684\u90a3\u56db\u4e2a\u73e0\u5b9d\u4e70 \u624b\uff0c\u88ab\u5f20\u4e39\u67ab\u5c01\u95ed\u4e86\u7a74\u9053\u7684\u60c5\u5f62\uff0c\u4e00\u6a21\u2014\u6837\uff01 \u3000\u3000\u201c\u4ed6\u4e5f\u6765\u4e86\uff1f\u201d\u4e91\u857e\u5fc3\u5934\u4e71\u8df3\uff0c\u767b\u65f6\u5446\u4f4f\uff0c\u90a3\u5c11\u5e74\u519b\u5b98\u867d\u7136\u4e0d\u80fd\u8f6c\u52a8\uff0c\u76ee\u5149\u5374\u662f\u5b9a\u5b9a\u5730 \u77aa\u7740\u4e91\u8386\u3002\u4e91\u857e\u60f3\u8d77\u5f20\u4e39\u67ab\u70b9\u7a74\u4e4b\u6cd5\uff0c\u767d\u5df1\u80fd\u89e3\uff0c\u5927\u7740\u80c6\u5b50\uff0c\u5728\u4ed6\u810a\u690e\u4e0b\u7684\u201c\u5929\u7487\u201d\u3001\u201c\u5730 \u7391\u201d\u4e24\u5904\u7a74\u9053\uff0c\u5404\u6233\u4e00\u4e0b\uff0c\u90a3\u5c11\u5e74\u519b\u5b98\u4e00\u58f0\u5927\u547c\uff0c\u624b\u8db3\u8f6c\u52a8\uff0c\u7a81\u7136\u4e00\u5200\u5411\u4e91\u857e\u5288\u6765\uff01 \u3000\u3000\u4e91\u857e\u5927\u5403\u4e00\u60ca\uff0c\u9669\u4e9b\u7ed9\u4ed6\u5288\u5c11\uff0c\u98de\u8eab\u4e00\u95ea\uff0c\u62d4\u5251\u62a4\u8eab\uff0c\u53ea\u542c\u5f97\u90a3\u5c11\u5e74\u519b\u5b98\u559d\u8feb\uff1a\u201c\u5140\u4f60 \u8fd9\u53ae\uff0c\u539f\u6765\u4e0e\u5978\u8d3c\u4e43\u662f\u4e00\u4f19\uff01\u201d\u4e91\u857e\u6321\u4e86\u4e00\u5251\uff0c\u6012\u8feb\uff1a\u201c\u4f60\u4f55\u6545\u6069\u5c06\u4ec7\u62a5\uff1f\u201d\u90a3\u5c11\u5e74\u519b\u5b98 \u9053\uff1a\u201c\u90a3\u5978\u8d3c\u7684\u9634\u6bd2\u624b\u6cd5\uff0c\u504f\u504f\u4f60\u80fd\u89e3\uff0c\u4f60\u548c\u4ed6\u82e5\u4e0d\u662f\u4e00\u5e08\u6240\u6388\uff0c\u4e5f\u5b9a\u662f\u81f3\u4ea4\u53cb\u597d\uff0c\u4ed6\u624d\u4f1a \u5c06\u89e3\u7a74\u4e4b\u6cd5\u6559\u4f60\uff0c\u4f60\u8fd8\u80fd\u72e1\u8fa9\u4e48\uff1f\u201d\u4e91\u857e\u5fc3\u4e2d\u751f\u6c14\uff0c\u5237\u5237\u5237\u8fd8\u4e86\u4e09\u5251\uff0c\u9053\uff1a\u201c\u4f60\u597d\u65e0\u793c\uff0c\u82e5 \u7136\u6211\u6709\u6076\u610f\uff0c\u4f55\u5fc5\u6551\u4f60\uff1f\u201d\u90a3\u5c11\u5e74\u519b\u5b98\u9053\uff1a\u201c\u90a3\u4f60\u4e0e\u4ed6\u662f\u4f55\u5173\u7cfb\uff0c\u5feb\u5feb\u9053\u6765\uff01\u201d\u4e91\u857e\u6012\u9053\uff1a \u201c\u4f60\u662f\u6211\u7684\u4f55\u4eba\uff0c\u6211\u8981\u542c\u4f60\u7684\u8bdd\uff1f\u201d\u90a3\u5c11\u5e74\u519b\u5b98\u5288\u4e86\u4e24\u5200\uff0c\u6536\u62db\u8bf4\u9053\uff1a\u201c\u4f60\u77e5\u9053\u6697\u7b97\u6211\u7684\u4e43 \u662f\u8c01\u4eba\uff1f\u4ed6\u662f\u74e6\u523a\u53f3\u4e1e\u76f8\u5f20\u5b97\u5468\u7684\u513f\u5b50\u5440\uff01\u770b\u4f60\u884c\u5f84\uff0c\u4e5f\u662f\u4e00\u540d\u4fa0\u5ba2\uff0c\u4f60\u5982\u4eca\u77e5\u9053\u4e86\u4ed6\u7684\u6765 \u5386\uff0c\u5c31\u8be5\u52a9\u6211\u62a5\u4ec7\u3002\u201d\u4e91\u857e\u5fc3\u9053\uff1a\u201c\u6211\u65e9\u5df2\u77e5\u9053\u4e86\u4ed6\u7684\u6765\u5386\uff0c\u4f55\u5f85\u4f60\u8bf4\uff01\u201d\u5374\u597d\u5947\u95ee\u9053\uff1a \u201c\u4f60\u4e0e\u4ed6\u7a76\u6709\u4f55\u4ec7\uff1f\u201d\u90a3\u5c11\u5e74\u519b\u5b98\u9053\uff1a\u201c\u8bf4\u6765\u8bdd\u957f\uff0c\u6211\u4e0d\u6b62\u4e0e\u4ed6\u6709\u4ec7\uff0c\u4ed6\u7684\u4e00\u5bb6\u5927\u5c0f\u6211\u90fd\u8981 \u6740\u4e2a\u5e72\u51c0\uff01\u518d\u8bf4\u4ed6\u65e2\u662f\u5927\u5978\u8d3c\u5f20\u5b97\u5468\u7684\u513f\u5b50\uff0c\u5077\u5165\u4e2d\u56fd\uff0c\u8fd8\u80fd\u6000\u6709\u4ec0\u4e48\u597d\u610f\u4e48\uff1f\u4f60\u65e2\u662f\u6c5f\u6e56 \u4fa0\u58eb\uff0c\u4f60\u4e5f\u8be5\u4e0e\u4ed6\u6709\u4ec7\uff01\u201d\u4e91\u857e\u6253\u4e86\u4e00\u4e2a\u5bd2\u5664\uff0c\u5728\u4ed6\u8bdd\u4e2d\uff0c\u9690\u9690\u95fb\u5230\u7f8a\u76ae\u8840\u4e66\u90a3\u79cd\u8840\u8165\u5473 \u9053\uff0c\u8d8a\u770b\u8fd9\u5c11\u5e74\u519b\u5b98\u8d8a\u89c9\u9762\u719f\uff0c\u4e0d\u89c9\u4e00\u9635\u9635\u51b7\u610f\u76f4\u900f\u5fc3\u5934\uff0c\u8eab\u8eaf\u98a4\u6296\uff0c\u7259\u5173\u6253\u6218\u3002\u90a3\u5c11\u5e74\u519b \u5b98\u51dd\u795e\u671b\u5979\uff0c\u9053\uff1a\u201c\u4f60\u600e\u4e48\u5566\uff1f\u201d \u3000\u3000\u4e91\u857e\u5f3a\u538b\u5236\u5b9a\u795e\u7b54\u9053\uff1a\u201c\u6ca1\u4ec0\u4e48\u3002\u201d\u90a3\u5c11\u5e74\u519b\u5b98\u9053\uff1a\u201c\u597d\u5566\uff0c\u54b1\u4eec\u6253\u67b6\u4e5f\u6253\u5f97\u4e4f\u5566\uff0c\u6211 \u4e0e\u4f60\u548c\u89e3\u4e86\u5427\u3002\u4f60\u544a\u8bc9\u6211\u4f60\u7684\u6765\u5386\uff0c\u6211\u4e5f\u544a\u8bc9\u4f60\u6211\u7684\u6765\u5386\u3002\u201d\u4e91\u857e\u9053\uff1a\u201c\u6211\u4e0d\u5fc5\u4f60\u544a\u8bc9\uff0c\u6211 \u77e5\u9053\u4f60\u662f\u4ece\u8499\u53e4\u6765\u7684\u3002\u201d\u90a3\u5c11\u5e74\u519b\u5b98\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\uff1f\u201d\u4e91\u857e\u9053\uff1a\u201c\u4f60\u524d\u65e5\u5077\u88ad\u756a\u738b\uff0c\u626e \u90a3\u8499\u53e4\u7267\u4eba\u795e\u60c5\u8bed\u6c14\u90fd\u50cf\u6781\u4e86\u3002\u201d\u90a3\u5c11\u5e74\u519b\u5b98\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u662f\u4e48\uff1f\u6211\u7956\u5148\u4e24\u4ee3\uff0c\u672c\u6765\u5c31 \u662f\u8499\u53e4\u7267\u4eba\u3002\u201d\u549a\u7684\u4e00\u58f0\uff0c\u4e91\u857e\u8dcc\u5012\u5730\u4e0a\u3002\u5979\u7684\u7237\u7237\u5728\u8499\u53e4\u7267\u9a6c\u4e8c\u5341\u5e74\uff0c\u5979\u7684\u7236\u4eb2\u4e3a\u4e86\u8425\u6551 \u7237\u7237\uff0c\u5728\u8499\u53e4\u9690\u59d3\u57cb\u540d\uff0c\u8fc7\u7684\u4e5f\u662f\u7267\u7f8a\u7684\u751f\u6d3b\uff0c\u4e0d\u9519\uff0c\u4ed6\u4eec\u90fd\u66fe\u5728\u8499\u53e4\u505a\u8fc7\u7267\u4eba\uff0c\u4e0d\u8fc7\u4e0d\u662f \u81ea\u613f\u7684\u7f62\u4e86\u3002 \u3000\u3000\u8fd9\u970e\u90a3\u95f4\uff0c\u597d\u50cf\u6709\u9053\u7535\u6d41\u901a\u8fc7\u5168\u8eab\uff0c\u4e91\u857e\u6218\u6817\u4e4b\u4e2d\u795e\u7ecf\u5168\u90fd\u9ebb\u6728\u4e86\u3002\u201c\u4ed6\u662f\u6211\u7684\u54e5\u54e5\uff0c \u4e0d\u9519\uff0c\u4ed6\u51c6\u662f\u6211\u7684\u54e5\u54e5\u3002\u5475\uff0c\u4ed6\u771f\u662f\u6211\u7684\u54e5\u54e5\u4e48\uff1f\u201d\u4e91\u857e\u5165\u4eac\uff0c\u4e3a\u7684\u5c31\u662f\u63a2\u542c\u54e5\u54e5\u7684\u6d88\u606f\uff0c \u53ef\u662f\u5982\u4eca\u9047\u7740\u4e86\uff0c\u5979\u5fc3\u5e95\u4e0b\u5374\u53c8\u5e0c\u671b\u8fd9\u4eba\u4e0d\u662f\u5979\u7684\u54e5\u54e5\u3002\u4ed6\u8bf4\u8d77\u5f20\u5b97\u5468\u7236\u5b50\u4e4b\u65f6\uff0c\u662f\u591a\u4e48\u5730 \u6068\u5475\uff0c\u82e5\u7136\u4ed6\u771f\u662f\u81ea\u5df1\u7684\u54e5\u54e5\uff0c\u77e5\u9053\u81ea\u5df1\u4e0e\u5f20\u4e39\u67ab\u7684\u4ea4\u60c5\uff0c\u90a3\u53c8\u5c06\u53d1\u751f\u4f55\u7b49\u6837\u7684\u4e8b\u60c5\uff1f\u4e91\u857e \u4e0d\u613f\u62a5\u4ec7\u4e48\uff1f\u4e0d\u662f\uff0c\u7f8a\u76ae\u8840\u4e66\u7684\u9634\u5f71\u59cb\u7ec8\u5728\u5979\u5fc3\u4e0a\u6ca1\u6709\u6d88\u9664\uff0c\u5979\u559c\u6b22\u5f20\u4e39\u67ab\uff0c\u5979\u4e5f\u6068\u5f20\u4e39 \u67ab\uff0c\u53ef\u662f\u5979\u53c8\u4e0d\u559c\u6b22\u522b\u4eba\u4e5f\u6068\u5f20\u4e39\u67ab\uff0c\u5c31\u662f\u8fd9\u4e48\u53e4\u602a\u7684\u77db\u76fe\u7684\u5fc3\u60c5\u3002 \u3000\u3000\u4e91\u857e\u5495\u549a\u4e00\u58f0\u5012\u5728\u5730\u4e0a\u3002\u90a3\u5c11\u5e74\u519b\u5b98\u559d\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u201d\u9519\u7efc\u590d\u6742\u7684\u601d\u60f3\uff0c\u6ce2\u6d6a\u822c\u7684\u5728 \u5979\u5fc3\u5934\u7ffb\u8fc7\uff0c\u201c\u6682\u65f6\u4e0d\u8981\u8ba4\u4ed6\uff01\u5047\u5982\u4ed6\u4e0d\u662f\u54e5\u54e5\uff0c\u5c82\u975e\u6cc4\u9732\u4e86\u81ea\u5df1\u7684\u8eab\u4efd\u3002\u4f55\u51b5\u4ed6\u53c8\u662f\u4e00\u4e2a \u519b\u5b98\u3002\u201d\u4e91\u857e\u50cf\u5728\u6c34\u4e2d\u6c89\u6eba\u7684\u4eba\uff0c\u6293\u7740\u4e86\u4e00\u6839\u82a6\u8349\uff0c\u6293\u7740\u4e86\u8fd9\u4e2a\u53ef\u4ee5\u6682\u65f6\u4e0d\u8ba4\u54e5\u54e5\u7684\u201c\u7406 \u7531\u201d\uff0c\u4e00\u8dc3\u800c\u8d77\uff0c\u9053\uff1a\u201c\u6211\u662f\u6765\u6551\u5468\u5c71\u6c11\u7684\u4eba\u3002\u201d \u3000\u3000\u90a3\u5c11\u5e74\u519b\u5b98\u597d\u751f\u8be7\u5f02\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u662f\u6765\u6551\u5468\u5c71\u6c11\u7684\u4eba\uff0c\u4e09\u66f4\u65f6\u5206\uff0c\u4f60\u7b2c\u4e00\u6b21\u6765\u65f6\uff0c\u4f0f \u5728\u5f20\u5927\u4eba\u7684\u5c4b\u9876\u6211\u5df2\u7ecf\u77a7\u89c1\u5566\uff0c\u4e0d\u8fc7\u6211\u4e0d\u559d\u7834\u7f62\u4e86\u3002\u6211\u95ee\u7684\u4e0d\u662f\u8fd9\u4e2a\uff0d\uff0d\u201d\u4e91\u857e\u9053\uff1a\u201c\u4f60\u95ee \u522b\u7684\u6211\u5c31\u4e0d\u8bf4\uff0c\u4f60\u4e0d\u77e5\u9053\u4e8b\u60c5\u6709\u7f13\u6025\u8f7b\u91cd\u5417\uff1f\u4f60\u77a7\uff0c\u4f60\u8fd9\u91cc\u95f9\u6210\u8fd9\u4e2a\u6837\u5b50\uff0c\u4e8f\u4f60\u8fd8\u6709\u95f2\u60c5\u4e0e \u6211\u95ee\u957f\u95ee\u77ed\u3002\u6211\u95ee\u4f60\uff0c\u6211\u7684\u5468\u5927\u54e5\u5462\uff1f\u8c01\u5230\u8fc7\u8fd9\u91cc\u4e86\uff1f\u4f60\u548c\u5f20\u98ce\u5e9c\u7684\u8bdd\u6211\u4e5f\u90fd\u542c\u89c1\u5566\uff0c\u6211\u77e5 \u9053\u4f60\u4e5f\u662f\u60f3\u6551\u5c71\u6c11\u5927\u54e5\u7684\u3002\u201d \u3000\u3000\u90a3\u5c11\u5e74\u519b\u5b98\u4f3c\u662f\u970d\u7136\u9192\u8d77\uff0c\u9053\uff1a\u201c\u662f\u5475\uff0c\u54b1\u4eec\u5148\u8fdb\u91cc\u9762\u77a7\u77a7\u53bb\uff0c\u5f20\u5927\u4eba\u4e0d\u77e5\u9053\u4e3a\u4ec0\u4e48\u4e0d \u89c1\u51fa\u6765\uff1f\u201d\u987f\u4e86\u4e00\u987f\u5ffd\u9053\uff1a\u201c\u5176\u5b9e\u6211\u4e0e\u4f60\u8bf4\u7684\u4e5f\u4e0d\u662f\u95f2\u8bdd\uff0c\u4f60\u771f\u50cf\u4e00\u4e2a\u6211\u6240\u8981\u627e\u5bfb\u7684\u4eba\uff0c\u53ef \u60dc\u4f60\u662f\u7537\u7684\u3002\u5440\uff0c\u8fd9\u8bdd\u8bf4\u6765\u53ef\u957f\uff0c\u975e\u5f97\u4e00\u5929\u4e00\u665a\u8bf4\u4e0d\u660e\u767d\uff0c\u54b1\u4eec\u4ee5\u540e\u518d\u597d\u597d\u7684\u8bf4\u3002\u201d \u3000\u3000\u4e91\u857e\u5df2\u79fb\u52a8\u811a\u6b65\u8d70\u5728\u524d\u9762\uff0c\u4e0d\u8ba9\u4ed6\u77a7\u89c1\u81ea\u5df1\u9762\u4e0a\u7684\u795e\u60c5\uff0c\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u91cc\u9762\u95f9\u6210\u4ec0\u4e48\u6837 \u5b50\u4f60\u8fd8\u4e0d\u77e5\u9053\u5417\uff1f\u4f60\u7684\u5175\u58eb\u5168\u7ed9\u4eba\u5f04\u5f97\u50cf\u6b7b\u4eba\u5566\u3002\u4f60\u7684\u5f20\u5927\u4eba\u4e5f\u4e0d\u89c1\u4e86\u3002\u201d \u3000\u3000\u90a3\u5c11\u5e74\u519b\u5b98\u201c\u554a\u5440\u201d\u4e00\u58f0\u4fbf\u5f80\u91cc\u8dd1\uff0c\u89c1\u4e86\u91cc\u9762\u7684\u666f\u8c61\uff0c\u4e5f\u4e0d\u7981\u6bdb\u9aa8\u609a\u7136\uff0c\u8fdb\u4e86\u5f20\u98ce\u5e9c\u7684 \u623f\u95f4\uff0c\u770b\u4e86\u4e24\u9762\u5899\u4e0a\u6240\u7559\u4e0b\u7684\u9ab7\u9ac5\u3001\u733f\u7334\u3001\u5b9d\u5251\u7b49\u6807\u8bb0\uff0c\u9a87\u7136\u8bf4\u9053\uff1a\u201c\u679c\u7136\u662f\u4ed6\u4eec\u6765\u4e86\uff01\u201d \u3000\u3000\u4e91\u857e\u9053\uff1a\u201c\u4ed6\u4eec\uff0c\u4ed6\u4eec\u662f\u8c01\uff1f\u201d\u90a3\u5c11\u5e74\u519b\u5b98\u9053\uff1a\u201c\u9ed1\u767d\u6469\u8bc3\u548c\u5927\u5185\u603b\u7ba1\u5eb7\u8d85\u6d77\u7684\u4e24\u4e2a\u5e08 \u53d4\u3002\u201d\u4e91\u857e\u9053\uff1a\u201c\u5475\uff0c\u539f\u6765\u94c1\u81c2\u91d1\u733f\u9f99\u9547\u65b9\u4e0e\u4e09\u82b1\u5251\u7384\u7075\u5b50\u4e43\u662f\u5927\u5185\u603b\u7ba1\u7684\u5e08\u53d4\uff0c\u90a3\u4e48\u606d\u559c \u4f60\u4eec\uff0c\u4f60\u4eec\u53c8\u6dfb\u591a\u4e24\u4e2a\u9ad8\u624b\u4e86\u3002\u201d\u90a3\u5c11\u5e74\u519b\u5b98\u751a\u662f\u4e0d\u4e50\u9053\uff1a\u201c\u4f60\u53ef\u4e0d\u77e5\u5176\u4e2d\u5229\u5bb3\uff0c\u82e5\u7136\u94c1\u81c2 \u91d1\u733f\u4e0e\u4e09\u82b1\u5251\u77e5\u9053\u662f\u6211\u4eec\u91ca\u653e\u4e86\u5468\u5c71\u6c11\uff0c\u5f20\u5927\u4eba\u6027\u547d\u96be\u4fdd\u3002\u201d\u4e91\u857e\u9053\uff1a\u201c\u5468\u5c71\u6c11\u771f\u7684\u662f\u5df2\u91ca \u653e\u4e86\u5417\uff1f\u201d\u90a3\u5c11\u5e74\u519b\u5b98\u9053\uff1a\u201c\u6211\u8d77\u5148\u8ba4\u4e3a\u5f20\u5927\u4eba\u4e0d\u80af\u91ca\u653e\uff0c\u8c01\u77e5\u4ed6\u6697\u4e2d\u5df2\u6709\u5b89\u6392\u3002\u4ed6\u662f\u53eb\u6a0a \u5fe0\u6084\u6084\u5e26\u4eba\u51fa\u53bb\u7684\u3002\u201d\u4e91\u857e\u9053\uff1a\u201c\u53ef\u662f\u5468\u5c71\u6c11\u4e0e\u6a0a\u5fe0\u73b0\u4e0b\u4e5f\u4e0d\u77e5\u751f\u6b7b\u5982\u4f55\u3002\u201d\u5c06\u81ea\u5df1\u6240\u9047\u7684 \u5947\u4e8b\u8bf4\u4e86\u3002\u90a3\u5c11\u5e74\u519b\u5b98\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u8fd9\u79cd\u610f\u5916\uff0c\u8c01\u4e5f\u6599\u60f3\u4e0d\u5230\u3002\u201d\u4e91\u857e\u6b63\u60f3\u53d1\u95ee\uff0c\u90a3\u5c11\u5e74 \u519b\u5b98\u63a5\u4e0b\u53bb\u9053\uff1a\u201c\u6a0a\u5fe0\u4e0e\u5468\u5c71\u6c11\u5077\u5077\u4ece\u540e\u95e8\u6e9c\u8d70\uff0c\u6211\u5728\u90a3\u91cc\u628a\u98ce\u5de1\u591c\uff0c\u5ffd\u7136\u591c\u98ce\u4e4b\u4e2d\u5439\u8fdb\u6765 \u4e00\u80a1\u5f02\u9999\u6211\u6025\u5fd9\u6b62\u7740\u547c\u5438\uff0c\u5df2\u5438\u8fdb\u4e00\u4e01\u70b9\u513f\uff0c\u90a3\u5f02\u9999\u597d\u751f\u5389\u5bb3\uff0c\u53ea\u662f\u5438\u8fdb\u5c11\u5c11\uff0c\u5c31\u7acb\u523b\u5168\u8eab \u9165\u8f6f\u3002\u84e6\u7136\u95f4\u4e00\u6761\u9ed1\u5f71\u98de\u4e0b\u5899\u5934\uff0c\u6b63\u662f\u5f20\u4e39\u67ab\u8fd9\u4e2a\u5978\u8d3c\uff0c\u6211\u5728\u8499\u53e4\u8ba4\u5f97\u4ed6\u3002\u4ed6\u4e00\u51fa\u624b\u4fbf\u7528\u4ed6 \u90a3\u90aa\u6076\u7684\u70b9\u7a74\u529f\u592b\uff0c\u6211\u5c4f\u4f4f\u6c14\u4e0d\u6562\u547c\u5438\uff0c\u4e5f\u4e0d\u80fd\u53eb\u558a\uff0c\u4ea4\u624b\u4e94\u516d\u62db\uff0c\u5438\u8fdb\u53bb\u7684\u8ff7\u9999\uff0c\u836f\u6027\u53d1 \u4f5c\uff0c\u518d\u4e5f\u652f\u6301\u4e0d\u4f4f\uff0c\u4ee5\u81f3\u7ed9\u4ed6\u70b9\u4e86\u7a74\u9053\u3002\u201d\u4e91\u857e\u5fc3\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\u3002\u602a\u4e0d\u5f97\u4ed6\u8fd9\u6837\u5feb\u4fbf\u7740\u4e86 \u5f20\u4e39\u67ab\u7684\u9053\u513f\u5462\u3002\u53ef\u662f\u5f20\u4e39\u67ab\u4e3a\u4ec0\u4e48\u53c8\u8981\u4f5c\u5f04\u4ed6\u5462\uff1f\u201d\u90a3\u5c11\u5e74\u519b\u5b98\u63a5\u4e0b\u53bb\u8bf4\u9053\uff1a\u201c\u6211\u7ed9\u4ed6\u70b9 \u4e86\u7a74\u9053\uff0c\u91cc\u9762\u95f9\u5f97\u5982\u4f55\uff0c\u5df2\u662f\u5168\u65e0\u77e5\u6653\u3002\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u5c11\u65f6\u5019\uff0c\u5916\u9762\u5ffd\u7136\u53c8\u98de\u8fdb\u4e24\u4e2a\u4eba\u6765\uff0c \u4e00\u4e2a\u662f\u718a\u8170\u733f\u9762\u7684\u8001\u8005\uff0c\u4e00\u4e2a\u662f\u8170\u60ac\u957f\u5251\u7684\u9053\u4eba\uff0c\u4e24\u4eba\u8bd5\u7740\u7ed9\u6211\u89e3\u7a74\uff0c\u5374\u65e0\u6cd5\u89e3\u5f00\uff0c\u90a3\u4eba\u9a82 \u58f0\u2018\u8113\u5305\u2019\u5c31\u8fdb\u53bb\u4e86\u3002\u5176\u5b9e\u4ed6\u4eec\u6789\u4e3a\u70b9\u82cd\u6d3e\u7684\u957f\u8001\uff0c\u89e3\u4e0d\u5f00\u522b\u6d3e\u7684\u70b9\u7a74\uff0c\u53c8\u4f55\u5c1d\u4e0d\u662f\u8113\u5305\uff1f \u4e24\u4eba\u8fdb\u53bb\u4e4b\u540e\u4e0d\u4e00\u4f1a\u5c31\u8054\u8882\u800c\u51fa\uff0c\u6068\u6068\u7136\u5927\u9a82\u9ed1\u767d\u6469\u8bc3\uff0c\u98de\u4e00\u822c\u5730\u53c8\u8d8a\u5899\u8d70\u4e86\u3002\u55ef\uff0c\u4ed6\u4eec\u82e5 \u9047\u7740\u8fd9\u4e24\u4e2a\u9b54\u5934\uff0c\u53ef\u6709\u4e00\u573a\u597d\u6253\u3002\u201d\u4e91\u857e\u9053\uff1a\u201c\u54b1\u4eec\u4e14\u5f80\u9752\u9f99\u5ce1\u7684\u65b9\u5411\u53bb\u5bfb\u4ed6\u4eec\u201d\u90a3\u5c11\u5e74\u519b \u5b98\u9053\u4e86\u58f0\u597d\uff0c\u8d70\u51fa\u524d\u9662\uff0c\u89c1\u90a3\u4e9b\u9a6c\u5339\u7684\u602a\u72b6\uff0c\u53c8\u597d\u6c14\u53c8\u597d\u7b11\uff0c\u9a82\u9053\uff1a\u201c\u8fd9\u4e24\u4e2a\u9b54\u5934\u8fde\u9a6c\u8d3c\u7684 \u9634\u6bd2\u624b\u6cd5\u4e5f\u4f7f\u51fa\u6765\u5566\uff0c\u4e8f\u6211\u5728\u8499\u53e4\u591a\u5e74\uff0c\u5bf9\u4e8e\u6cbb\u9a6c\u7684\u529f\u592b\u8fd8\u61c2\u4e00\u624b\u3002\u201d\u8fb9\u8bf4\u8fb9\u66ff\u9a6c\u63a8\u62ff\u62cd \u6309\uff0c\u8212\u6563\u8840\u8109\uff0c\u4e0d\u4e45\u5c31\u5c06\u4e24\u5339\u6218\u9a6c\u6cbb\u597d\uff0c\u4e0e\u4e91\u857e\u9a70\u51fa\u57ce\u5916\u3002 \u3000\u3000\u8fd9\u65f6\u56db\u91ce\u9e21\u9e23\uff0c\u5929\u5c06\u8fd1\u6653\uff0c\u5230\u9752\u9f99\u5ce1\u7684\u8def\u4e0a\uff0c\u53ea\u89c1\u51e0\u6761\u9a6c\u8e44\u75d5\u8ff9\uff0c\u4ea4\u9519\u7eb5\u6a2a\u3002\u4e24\u4eba\u98de\u9a6c \u9a70\u9a71\uff0c\u8dd1\u4e86\u4e00\u9635\uff0c\u9752\u9f99\u5ce1\u5df2\u9690\u9690\u5728\u671b\uff0c\u5230\u4e86\u4e00\u6761\u5c94\u8def\uff0c\u5ffd\u542c\u5f97\u5de6\u8fb9\u9053\u4e0a\uff0c\u8fdc\u8fdc\u4f20\u6765\u5175\u5203\u4ea4\u51fb \u4e4b\u58f0\uff0c\u800c\u53f3\u8fb9\u9053\u4e0a\uff0c\u8fdc\u8fdc\u53c8\u89c1\u4e00\u4eba\u4e00\u9a91\uff0c\u6b63\u5728\u75be\u8dd1\u3002\u90a3\u5c11\u5e74\u519b\u5b98\u9053\uff1a\u201c\u6211\u5f80\u5de6\u8fb9\uff0c\u4f60\u5f80\u53f3 \u8fb9\uff0c\u5206\u5934\u63a2\u9053\u3002\u201d\u4e91\u857e\u7eb5\u9a6c\u4e0a\u524d\uff0c\u8dd1\u4e86\u4e00\u7a0b\uff0c\u4e0e\u524d\u9762\u90a3\u9a91\u6e10\u6e10\u63a5\u8fd1\uff0c\u4e91\u857e\u5439\u4e86\u4e00\u58f0\u80e1\u54e8\uff0c\u90a3 \u9a91\u9a6c\u7a81\u7136\u52d2\u4f4f\uff0c\u62e8\u8f6c\u9a6c\u5934\uff0c\u75be\u5954\u800c\u6765\uff0c\u9a6c\u4e0a\u7684\u9a91\u5ba2\u6b63\u662f\u5fa1\u6797\u519b\u7684\u6307\u6325\u6709\u4eac\u5e08\u7b2c\u4e00\u9ad8\u624b\u4e4b\u79f0\u7684 \u5f20\u98ce\u5e9c\u3002 \u3000\u3000\u4e91\u857e\u4e3e\u624b\u62db\u547c\uff0c\u5f20\u98ce\u5e9c\u52d2\u4f4f\u9a6c\u5934\uff0c\u75be\u5fd9\u95ee\u9053\uff1a\u201c\u4f60\u90a3\u4f4d\u670b\u53cb\u5462\uff1f\u201d\u4e91\u857e\u84e6\u5730\u4e00\u6014\uff0c\u8bf4 \u9053\uff1a\u201c\u4f60\u89c1\u7740\u4ed6\u4e86\u4e48\uff1f\u6211\u521a\u521a\u4ece\u4f60\u90a3\u91cc\u6765\u3002\u201d\u5f20\u98ce\u5e9c\u6c89\u541f\u534a\u664c\uff0c\u9053\uff1a\u201c\u90a3\u4e48\u6b64\u4e8b\u5c31\u771f\u5947\u602a \u4e86\uff0c\u4ed6\u4e3a\u4ec0\u4e48\u5f15\u6211\u51fa\u6765\uff0c\u5728\u8fd9\u8352\u91ce\u4e0a\u6349\u8ff7\u85cf\u3001\u515c\u5708\u5b50\uff1f\u201d\u4e91\u857e\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u662f\u4ed6\u5f15\u4f60\u51fa\u6765 \u7684\uff1f\u90a3\u9ed1\u767d\u6469\u8bc3\u5462\uff1f\u201d\u5f20\u98ce\u5e9c\u9053\uff1a\u201c\u4f60\u662f\u8bf4\u6628\u65e5\u5728\u5ce1\u8c37\u4e4b\u4e2d\u6240\u9047\u7684\u90a3\u4e24\u4e2a\u602a\u7269\uff1f\u6211\u6ca1\u6709\u89c1\u7740 \u4ed6\u4eec\u3002\u6211\u9001\u4f60\u8d70\u540e\uff0c\u6b63\u5728\u623f\u4e2d\u9759\u5750\uff0c\u601d\u8003\u5982\u4f55\u5e94\u4ed8\u8fd9\u4e8b\u7684\u540e\u679c\uff0c\u5ffd\u542c\u5f97\u6709\u4eba\u8f7b\u8f7b\u5728\u7a97\u5916\u6572\u4e86 \u4e09\u4e0b\uff0c\u8bf4\u9053\uff1a\u2018\u5b97\u5144\uff0c\u6211\u6765\u5566\uff01\u2019\u6b64\u4eba\u8f7b\u8eab\u529f\u592b\uff0c\u771f\u662f\u8d85\u51e1\u5165\u5723\uff0c\u8fde\u6211\u4e5f\u542c\u4e0d\u51fa\u6765\u3002\u6211\u4e00\u8dc3 \u800c\u51fa\uff0c\u53ea\u89c1\u4ed6\u5df2\u5728\u5c4b\u9876\u5fae\u7b11\u62db\u624b\u3002\u4ec0\u4e48\uff1f\u4f60\u8fd8\u95ee\u4ed6\u662f\u8c01\uff1f\u81ea\u7136\u5c31\u662f\u4f60\u90a3\u4f4d\u9a91\u767d\u9a6c\u7684\u670b\u53cb\u5566\u3002 \u4ed6\u53eb\u4ec0\u4e48\uff1f\u55ef\uff0c\u5f20\u4e39\u67ab\u3002\u6b64\u4eba\u884c\u4e8b\u771f\u662f\u795e\u5947\u83ab\u6d4b\uff0c\u6211\u5b9e\u662f\u60f3\u4e0e\u4ed6\u4ea4\u7eb3\uff0c\u7acb\u523b\u8ffd\u4e0a\u524d\u53bb\u3002\u90a3\u4eba \u6643\u4e00\u6643\u8eab\uff0c\u4fbf\u98de\u8fc7\u4e24\u95f4\u5c4b\u9876\uff0c\u8eab\u6cd5\u4e4b\u5feb\uff0c\u65e0\u4ee5\u5f62\u5bb9\u3002\u6211\u731c\u60f3\u4ed6\u662f\u4e0d\u4fbf\u4e0e\u6211\u5728\u5ba2\u5e97\u4e4b\u4e2d\u8c08\u8bdd\uff0c \u6240\u4ee5\u5f15\u6211\u51fa\u53bb\u3002\u6211\u8ffd\u8fc7\u4e86\u4e24\u6761\u8857\u53e3\uff0c\u53ea\u89c1\u4e24\u5339\u9a6c\u5728\u8f6c\u89d2\u4e4b\u5904\u7b49\u7740\u3002\u5f20\u4e39\u67ab\u9053\u58f0\uff1a\u2018\u4e0a\u9a6c\u2019\uff0c \u98de\u8eab\u5148\u9a91\u4e86\u90a3\u5339\u767d\u9a6c\uff0c\u6211\u4e5f\u8df3\u4e0a\u4e86\u53e6\u4e00\u5339\u9a6c\uff0c\u98de\u9a70\u51fa\u57ce\u3002\u6211\u4ee5\u4e3a\u4ed6\u5b9a\u7136\u505c\u9a6c\u4e0e\u6211\u8bf4\u8bdd\uff0c\u8c01\u77e5 \u4ed6\u4ecd\u662f\u5411\u524d\u98de\u8dd1\uff0c\u6211\u5524\u4ed6\u4ed6\u4e5f\u4e0d\u542c\uff0c\u8ffd\u4ed6\u53c8\u8ffd\u4e0d\u4e0a\u3002\u5f85\u4e0d\u8ffd\u65f6\uff0c\u4ed6\u53c8\u653e\u6162\u9a6c\u8e44\uff0c\u5728\u8fd9\u8352\u91ce\u4e0a \u5f15\u6211\u8f6c\u6765\u8f6c\u53bb\uff0c\u771f\u662f\u83ab\u540d\u5176\u5999\u3002\u201d\u4e91\u857e\u9053\uff1a\u201c\u73b0\u5728\u5462\uff1f\u201d\u5f20\u98ce\u5e9c\u9053\uff1a\u201c\u4ed6\u5df2\u7ecf\u8fc7\u4e86\u90a3\u8fb9\u5c71\u5773 \u4e86\u3002\u6211\u542c\u5f97\u4f60\u5728\u540e\u9762\u547c\u5524\uff0c\u5c31\u4e0d\u8ffd\u4ed6\u5566\u3002\u55ef\uff0c\u4f60\u521a\u4ece\u6211\u90a3\u91cc\u6765\uff1f\u53ef\u6709\u4eba\u77e5\u89c9\u4e48\uff1f\u201d\u4e91\u857e\u7b11 \u9053\uff1a\u201c\u8fd8\u8bf4\u4ec0\u4e48\u77e5\u89c9\uff1f\u4f60\u7684\u4eba\u5168\u7ed9\u9ed1\u767d\u6469\u8bc3\u5f04\u6b7b\u4e86\uff01\u201d\u5f20\u98ce\u5e9c\u8df3\u8d77\u6765\u9053\uff1a\u201c\u9ed1\u767d\u6469\u8bc3\u6709\u8fd9\u6837 \u5927\u7684\u80c6\u5b50\uff1f\u201d\u4e91\u857e\u9053\uff1a\u201c\u4e0d\u662f\u771f\u7684\u5f04\u6b7b\uff0c\u4f46\u5374\u4e0e\u6b7b\u4e5f\u76f8\u5dee\u4e0d\u591a\u3002\u201d\u5c06\u6240\u9047\u7684\u5f02\u72b6\u4e00\u4e00\u7ec6\u8bf4\u3002 \u5f20\u98ce\u5e9c\u542c\u5f97\u5ba2\u5e97\u4e2d\u4eba\u90fd\u6c89\u7761\u4e0d\u9192\uff0c\u7528\u51b7\u6c34\u55b7\u9762\u4e5f\u6ca1\u6548\u679c\uff0c\u6c89\u541f\u8bf4\u9053\uff1a\u201c\u5514\uff0c\u8fd9\u679c\u7136\u662f\u9ed1\u767d\u6469 \u8bc3\u7684\u6240\u4e3a\u4e86\u3002\u897f\u57df\u6709\u4e00\u79cd\u5f02\u9999\uff0c\u4e43\u662f\u6700\u5389\u5bb3\u7684\u8ff7\u836f\uff0c\u540d\u4e3a\u2018\u9e21\u9e23\u4e94\u9f13\u8fd4\u9b42\u9999\u2019\uff0c\u975e\u5f85\u5929\u4eae\uff0c \u65e0\u836f\u53ef\u89e3\u3002\u82e5\u5230\u5929\u4eae\uff0c\u81ea\u4f1a\u9192\u8f6c\u3002\u867d\u7136\u90aa\u6c14\u5f97\u7d27\uff0c\u5374\u662f\u5bf9\u4eba\u65e0\u5bb3\u3002\u770b\u8fd9\u60c5\u5f62\uff0c\u5f20\u4e39\u67ab\u662f\u4e0e\u9ed1 \u767d\u6469\u8bc3\u8054\u624b\u6765\u7684\uff0c\u7531\u5f20\u4e39\u67ab\u5f15\u6211\u8d70\u5f00\uff0c\u518d\u7531\u9ed1\u767d\u6469\u8bc3\u65bd\u653e\u8ff7\u9999\u3002\u54a6\uff0c\u6211\u81ea\u95ee\u4e0e\u9ed1\u767d\u6469\u8bc3\u65e0\u51a4 \u65e0\u4ec7\uff0c\u4e0e\u5f20\u4e39\u67ab\u4e5f\u6709\u4e00\u6bb5\u5c0f\u5c0f\u7684\u4ea4\u60c5\uff0c\u4e3a\u4f55\u4ed6\u4eec\u5374\u4e0e\u6211\u5f00\u5982\u6b64\u8fd9\u822c\u7684\u4e00\u4e2a\u5927\u73a9\u7b11\u3002\u201d \u3000\u3000\u4e91\u857e\u9053\uff1a\u201c\u6211\u4ea6\u662f\u5341\u5206\u4e0d\u89e3\u5440\uff01\u201d\u518d\u628a\u5728\u5ba2\u5e97\u4e2d\u6240\u89c1\u7684\u5947\u602a\u60c5\u5f62\uff0c\u7ec6\u8bf4\u4e0b\u53bb\u3002\u5f20\u98ce\u5e9c\u542c \u5230\u94c1\u81c2\u91d1\u733f\u4e0e\u4e09\u82b1\u5251\u8054\u8882\u800c\u6765\uff0c\u4e0d\u89c9\u9762\u8272\u5927\u53d8\u3002\u4e91\u857e\u9053\uff1a\u201c\u4ed6\u4eec\u4e0d\u662f\u4f60\u4eec\u7684\u81ea\u5df1\u4eba\u5417\uff1f\u4f60\u5bb3 \u6015\u600e\u5730\uff1f\u201d\u5f20\u98ce\u5e9c\u6447\u4e86\u6447\u5934\uff0c\u60e8\u7b11\u8bf4\u9053\uff1a\u201c\u4f60\u4e14\u522b\u95ee\uff0c\u5148\u8bf4\u4e0b\u53bb\u5427\u3002\u201d\u4e91\u857e\u4e00\u53e3\u6c14\u5c06\u6240\u906d\u9047 \u7684\u602a\u4e8b\u8bf4\u5b8c\uff0c\u5f20\u98ce\u5e9c\u542c\u5f97\u90a3\u5c11\u5e74\u519b\u5b98\u4e5f\u7740\u4e86\u9053\u513f\uff0c\u4e0d\u89c9\u82e6\u7b11\u3002\u4e91\u857e\u9053\uff1a\u201c\u90a3\u5c11\u5e74\u519b\u5b98\u4e0d\u77e5\u4f55 \u4ee5\u5982\u6b64\u6068\u4ed6\uff1f\u201d\u4e91\u857e\u81ea\u662f\u9690\u7740\u5f20\u4e39\u67ab\u7684\u8eab\u4efd\u4e0d\u8bf4\u3002\u5f20\u98ce\u5e9c\u6c89\u541f\u534a\u664c\u9053\uff1a\u201c\u770b\u90a3\u5f20\u4e39\u67ab\u5668\u5b87\u8f69 \u6602\uff0c\u5f53\u4e0d\u4f1a\u662f\u4e2a\u574f\u4eba\u3002\u4e91\u7edf\u9886\u4f55\u4ee5\u6068\u4ed6\uff0c\u8fd9\u4e8b\u6211\u5012\u8981\u95ee\u4e2a\u660e\u767d\u3002\u201d\u4e91\u857e\u542c\u5f97\u4e00\u4e2a\u201c\u4e91\u201d\u5b57\uff0c \u4e0d\u89c9\u9762\u8272\u60e8\u767d\uff0c\u6447\u6447\u6b32\u5760\u3002\u5f20\u98ce\u5e9c\u6025\u5fd9\u4f38\u624b\u76f8\u6276\u9053\uff1a\u201c\u4f60\u600e\u4e48\u5566\uff1f\u201d\u4e91\u857e\u62e8\u9a6c\u907f\u5f00\uff0c\u5b9a\u4e86\u5fc3 \u795e\uff0c\u9053\uff1a\u201c\u6ca1\u4ec0\u4e48\u3002\u90a3\u519b\u5b98\u53eb\u4ec0\u4e48\u540d\u5b57\u554a\uff1f\u201d\u5f20\u98ce\u5e9c\u9053\uff1a\u201c\u59d3\u4e91\u540d\u5524\u5343\u91cc\uff0c\u4f60\u95ee\u4ed6\u4f5c\u751a\uff1f\u201d \u5343\u91cc\u4e8c\u5b57\u5408\u6210\u4e00\u4e2a\u201c\u91cd\u201d\u5b57\uff0c\u4e91\u91cd\u6b63\u662f\u5e7c\u5e74\u5c31\u4e0e\u4e91\u857e\u5206\u624b\u7684\u54e5\u54e5\u3002\u4e91\u857e\u6b64\u65f6\u66f4\u65e0\u7591\u60d1\uff0c\u5fc3\u4e2d \u53c8\u662f\u6b22\u559c\u53c8\u662f\u60ca\u60f6\u3002\u6b22\u559c\u8005\u4e43\u662f\u5144\u59b9\u6bd5\u7adf\u91cd\u9022\uff0c\u60ca\u60f6\u8005\u4e43\u662f\u4ed6\u4e0e\u5f20\u4e39\u67ab\u52bf\u6210\u6c34\u706b\u3002\u53ea\u542c\u5f97\u5f20 \u98ce\u5e9c\u53c8\u9053\uff1a\u201c\u4f60\u4eec\u53ef\u662f\u76f8\u8bc6\u7684\u4e48\uff1f\u201d\u4e91\u857e\u9053\uff1a\u201c\u4ed6\u50cf\u6211\u5e7c\u5e74\u7684\u4e00\u4f4d\u670b\u53cb\u3002\u55ef\uff0c\u4ed6\u662f\u4ec0\u4e48\u65f6\u5019 \u56de\u6765\u7684\uff1f\u201d\u5f20\u98ce\u5e9c\u9053\uff1a\u201c\u56de\u6765\uff1f\u54a6\uff0c\u4f60\u4e5f\u77e5\u9053\u4ed6\u662f\u4ece\u8499\u53e4\u56de\u6765\u7684\u4e48\uff1f\u4ed6\u5230\u5fa1\u6797\u519b\u4e2d\u672a\u6ee1\u4e00 \u6708\uff0c\u6211\u662f\u9526\u8863\u536b\u6307\u6325\u517c\u5fa1\u6797\u519b\u90fd\u7edf\uff0c\u6b63\u597d\u662f\u4ed6\u4e0a\u53f8\u76f8\u5904\u65f6\u65e5\u867d\u6d45\uff0c\u5374\u662f\u610f\u6c14\u76f8\u6295\u3002\u636e\u4ed6\u8bf4\uff0c \u4ed6\u7684\u7956\u5148\u4e24\u4ee3\uff0c\u90fd\u662f\u7559\u5728\u74e6\u523a\u56fd\u7684\u6c49\u4eba\uff0c\u9971\u53d7\u6b3a\u51cc\uff0c\u6240\u4ee5\u9003\u56de\u3002\u4ed6\u7acb\u5fd7\u8981\u505a\u4e00\u4e2a\u5c06\u519b\uff0c\u597d\u4ed6 \u65e5\u9886\u5175\u53bb\u706d\u74e6\u523a\u3002\u6240\u4ee5\u5148\u5728\u5fa1\u6797\u519b\u6df7\u4e2a\u51fa\u8eab\uff0c\u51c6\u5907\u8003\u4eca\u5e74\u7279\u5f00\u7684\u6b66\u79d1\uff0c\u82e5\u7136\u4e2d\u4e86\u6b66\u79d1\u72b6\u5143\uff0c \u90a3\u5c31\u53ef\u9042\u4ed6\u7684\u5e73\u751f\u4e4b\u613f\u4e86\u3002\u201d\u4e91\u857e\u4e0d\u89c9\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u4ed6\u60f3\u505a\u5b98\u62a5\u4ec7\uff0c\u53ea\u6050\u672a\u5fc5\u80fd\u9042\u5fc3\u613f\u3002\u5f20 \u5927\u4eba\uff0c\u4f60\u4f11\u603f\u6211\u76f4\u8bf4\uff0c\u771f\u6b63\u62b5\u5fa1\u80e1\u864f\u7684\u53ef\u4e0d\u662f\u5927\u660e\u671d\u5ef7\u3002\u201d\u5f20\u98ce\u5e9c\u9ed8\u7136\u4e0d\u8bed\uff0c\u534a\u664c\u8bf4\u9053\uff1a \u201c\u4f60\u6240\u89c1\u4e5f\u672a\u5fc5\u5c3d\u7136\uff0c\u6211\u671d\u4e2d\u5c3d\u6709\u8d64\u80c6\u5fe0\u5fc3\u8a93\u5fa1\u5916\u4fae\u7684\u5927\u81e3\uff0c\u9601\u8001\u4e8e\u8c26\uff0c\u5c31\u662f\u4e07\u4eba\u666f\u4ef0\u7684\u6b63 \u76f4\u81e3\u5b50\u3002\u201d\u4e91\u857e\u4e0d\u719f\u6089\u671d\u5ef7\u4e4b\u4e8b\uff0c\u5f53\u4e0b\u4ea6\u4e0d\u4e0e\u4ed6\u5206\u8fa8\u3002 \u3000\u3000\u5f20\u98ce\u5e9c\u89c1\u4e91\u857e\u751a\u662f\u5173\u5fc3\u90a3\u4e2a\u5c11\u5e74\u519b\u5b98\uff0c\u597d\u751f\u5947\u602a\uff0c\u6b63\u60f3\u518d\u95ee\uff0c\u5ffd\u542c\u5f97\u4e00\u58f0\u9a6c\u5636\uff0c\u5f20\u4e39\u67ab \u90a3\u9a91\u767d\u9a6c\u53c8\u5954\u4e86\u56de\u6765\u3002\u5f20\u98ce\u5e9c\u53eb\u9053\uff1a\u201c\u5582\uff0c\u4f60\u5f04\u7684\u7a76\u7adf\u662f\u4ec0\u4e48\u7384\u865a\uff1f\u4f60\u7684\u597d\u53cb\u5728\u6b64\uff0c\u4e0d\u8981\u518d \u6349\u8ff7\u85cf\u4e86\u5427\uff01\u201d\u5f20\u4e39\u67ab\u767d\u9a6c\u5982\u98de\uff0c\u970e\u5ffd\u5373\u5230\uff0c\u5148\u5411\u5f20\u98ce\u5e9c\u9053\u58f0\uff1a\u201c\u5f97\u7f6a\uff01\u201d\u518d\u5411\u4e91\u857e\u8bf4\u9053\uff1a \u201c\u4f60\u597d\uff01\u201d\u4e91\u857e\u6276\u7740\u9a6c\u978d\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4e0d\u52b3\u7275\u6302\u3002\u201d \u3000\u3000\u5f20\u98ce\u5e9c\u89c1\u4e8c\u4eba\u795e\u60c5\uff0c\u5e76\u4e0d\u8c61\u662f\u597d\u53cb\uff0c\u5947\u5f02\u83ab\u540d\u3002\u53ef\u662f\u6025\u4e8e\u77e5\u9053\u4ed6\u7684\u7528\u610f\uff0c\u4e0d\u6687\u591a\u7ba1\u95f2 \u4e8b\uff0c\u4fbf\u7387\u76f4\u95ee\u9053\uff1a\u201c\u5f20\u5144\uff0c\u4f60\u6211\u4e5f\u7b97\u5f97\u4e0a\u6709\u6bb5\u4ea4\u60c5\uff0c\u4f55\u4ee5\u4f60\u4e0e\u9ed1\u767d\u6469\u8bc3\u5230\u6211\u4f4f\u6240\u6363\u4e71\uff1f\u201d\u5f20 \u4e39\u67ab\u4ef0\u5929\u5927\u7b11\uff0c\u541f\u9053\uff1a\u201c\u4e00\u7247\u82e6\u5fc3\u541b\u4e0d\u8bc6\uff0c\u4eba\u524d\u6789\u81ea\u8bf4\u6069\u4ec7\u3002\u6211\u95ee\u4f60\uff0c\u4f60\u53ef\u77e5\u9053\u4ec0\u4e48\u4eba\u6765\u67e5 \u63a2\u4f60\u4e48\uff1f\u201d\u5f20\u98ce\u5e9c\u8138\u8272\u4e00\u53d8\uff0c\u9053\uff1a\u201c\u4f60\u4e5f\u77e5\u9053\u4e86\u4e48\uff1f\u94c1\u81c2\u91d1\u733f\u9f99\u9547\u65b9\u548c\u4e09\u82b1\u5251\u7384\u7075\u5b50\u4e5f\u6765 \u4e86\u3002\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u53ef\u4e0d\u6b63\u662f\uff0c\u4ed6\u4eec\u56e0\u4f55\u800c\u6765\uff0c\u96be\u9053\u4f60\u8fd8\u4e0d\u660e\u767d\u4e48\uff1f\u201d \u3000\u3000\u94c1\u81c2\u91d1\u733f\u4e0e\u4e09\u82b1\u5251\u4e43\u662f\u5f53\u4eca\u5927\u5185\u603b\u7ba1\u5eb7\u8d85\u6d77\u7684\u5e08\u53d4\uff0c\u8fd9\u5eb7\u8d85\u6d77\u4e43\u70b9\u82cd\u6d3e\u9886\u8896\u51cc\u9704\u5b50\u7684\u9996 \u5f92\uff0c\u4e24\u81c2\u6709\u5343\u65a4\u795e\u529b\uff0c\u5916\u5bb6\u529f\u592b\u767b\u5cf0\u9020\u6781\uff0c\u53ea\u56e0\u4ed6\u957f\u5904\u5bab\u5185\uff0c\u4fdd\u536b\u7687\u5e1d\uff0c\u6240\u4ee5\u5728\u6c5f\u6e56\u4e4b\u4e0a\uff0c \u58f0\u540d\u53cd\u800c\u4e0d\u663e\u3002\u4ed6\u4e0d\u5fff\u5f20\u98ce\u5e9c\u6709\u4eac\u5e08\u7b2c\u4e00\u9ad8\u624b\u4e4b\u79f0\uff0c\u66fe\u4e09\u6b21\u7ea6\u4ed6\u6bd4\u8bd5\uff0c\u6bcf\u6b21\u90fd\u8f93\u4e86\u4e00\u62db\uff0c\u53e3 \u4e2d\u867d\u8bf4\u4f69\u62a5\uff0c\u5fc3\u4e2d\u5374\u662f\u4e0d\u5fff\uff0c\u6240\u4ee5\u6697\u5730\u91cc\u5e38\u6392\u6324\u4ed6\uff0c\u5f20\u98ce\u5e9c\u4ea6\u662f\u660e\u767d\u3002\u5eb7\u8d85\u6d77\u7684\u804c\u4f4d\u6bd4\u5f20\u98ce \u5e9c\u9ad8\uff0c\u5f20\u98ce\u5e9c\u5bf9\u4ed6\u751a\u6709\u987e\u5fcc\u3002\u5f20\u4e39\u67ab\u4e00\u756a\u8bf4\u8bdd\uff0c\u8bf4\u5f97\u5f20\u98ce\u5e9c\u9762\u8272\u5927\u53d8\uff0c\u5583\u5583\u8bf4\u9053\uff1a\u201c\u83ab\u975e\u5eb7 \u8d85\u6d77\u5c06\u4ed6\u7684\u4e24\u4e2a\u5e08\u53d4\u8bf7\u6765\uff0c\u6697\u4e2d\u60f3\u52a0\u5bb3\u4e8e\u6211\uff1f\u201d\u5f20\u4e39\u67ab\u7b11\u9053\uff1a\u201c\u4f55\u987b\u6697\u4e2d\u52a0\u5bb3\uff0c\u73b0\u4e0b\u4f60\u5c31\u6709 \u75db\u811a\u634f\u5728\u4ed6\u7684\u624b\u91cc\u3002\u201d\u5f20\u98ce\u5e9c\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u94c1\u81c2\u91d1\u733f\u4e0e\u4e09\u82b1\u5251\u672c\u6765\u4e0d\u662f\u4e3a\u4f60 \u51fa\u4eac\uff0c\u53ef\u662f\u5374\u521a\u597d\u649e\u4e0a\u4f60\u7684\u4e8b\u60c5\u3002\u4f60\u6b32\u77e5\u4e2a\u4e2d\u539f\u59d4\u4e48\uff1f\u201d\u5f20\u98ce\u5e9c\u9053\uff1a\u201c\u8bf7\u9053\u5176\u8be6\u3002\u201d\u5f20\u4e39\u67ab \u9053\uff1a\u201c\u9ed1\u767d\u6469\u8bc3\u4e70\u4e86\u4e00\u5b97\u8d3c\u8d43\uff0c\u4e43\u662f\u4eac\u4e2d\u67d0\u4eb2\u738b\u7684\u4f20\u5bb6\u4e4b\u5b9d\uff1a\u4e00\u5bf9\u78a7\u7389\u72ee\u5b50\uff0c\u5355\u90a3\u9576\u5d4c\u72ee\u5b50 \u773c\u775b\u7684\u90a3\u4e24\u5bf9\u660e\u73e0\uff0c\u5c31\u4ef7\u503c\u8fde\u57ce\uff0c\u8fd9\u4e8b\u60c5\u95f9\u5f97\u5927\u4e86\uff0c\u5eb7\u8d85\u6d77\u81ea\u77e5\u4e0d\u662f\u9ed1\u767d\u6469\u8bc3\u7684\u5bf9\u624b\uff0c\u6240\u4ee5 \u8bf7\u4e24\u4e2a\u5e08\u53d4\u51fa\u5c71\u76f8\u52a9\u67e5\u7f09\u3002\u4ed6\u4eec\u6599\u5b9a\u9ed1\u767d\u6469\u8bc3\u5fc5\u662f\u9003\u56de\u897f\u57df\uff0c\u662f\u6545\u4e00\u8def\u5317\u6765\u3002\u5374\u521a\u597d\u4f60\u4e5f\u5728 \u8fd9\u4e00\u5e26\uff0c\u6240\u4ee5\u987a\u4fbf\u5c31\u5c06\u4f60\u76d1\u89c6\u4e0a\u5566\u3002\u65e0\u5de7\u4e0d\u5de7\uff0c\u4f60\u6349\u4e86\u91d1\u5200\u5be8\u4e3b\u7684\u513f\u5b50\uff0c\u4f60\u8fd8\u672a\u77e5\u9053\u4ed6\u7684\u8eab \u5206\uff0c\u5eb7\u603b\u7ba1\u5df2\u662f\u5f97\u4eba\u544a\u77e5\uff0c\u5468\u5c71\u6c11\u7684\u8eab\u4ef7\u53ef\u66f4\u5728\u90a3\u5bf9\u7389\u72ee\u5b50\u4e4b\u4e0a\uff0c\u80fd\u64d2\u81f3\u4eac\uff0c\u4fbf\u662f\u5927\u529f\u4e00 \u4ef6\u3002\u5eb7\u603b\u7ba1\u7acb\u523b\u5c06\u8ffd\u8d43\u4e4b\u4e8b\u629b\u8fc7\u4e00\u8fb9\uff0c\u4e00\u9762\u98de\u4e66\u4f20\u62a5\uff0c\u4e00\u9762\u8bf7\u4ed6\u7684\u4e24\u4e2a\u5e08\u53d4\u8fde\u591c\u8d76\u5230\u4f60\u90a3\u91cc \u63d0\u4eba\u3002\u5468\u5c71\u6c11\u524d\u811a\u51fa\u95e8\uff0c\u4ed6\u4eec\u540e\u811a\u8d76\u81f3\u3002\u201d\u5f20\u98ce\u5e9c\u60ca\u547c\u9053\uff1a\u201c\u82e5\u7136\u4ed6\u4eec\u77e5\u9053\u6211\u5c06\u5468\u5c71\u6c11\u91ca \u653e\uff0c\u8fd9\u4e8b\u53ef\u662f\u706d\u65cf\u4e4b\u7978\u3002\u201d\u5f20\u4e39\u67ab\u7b11\u9053\uff1a\u201c\u4ed6\u4eec\u5df2\u88ab\u6211\u7528\u8ba1\u5f15\u5f00\uff0c\u8fd9\u4e8b\u4ed6\u4eec\u6c38\u4e0d\u77e5\u9053\u3002\u201d\u5f20 \u98ce\u5e9c\u9053\uff1a\u201c\u5475\uff0c\u4f60\u539f\u6765\u662f\u7528\u9ed1\u767d\u6469\u8bc3\u4e3a\u9975\uff0c\u5f15\u5f00\u4ed6\u4eec\u3002\u4f60\u7adf\u7136\u80fd\u6307\u4f7f\u8fd9\u4e24\u4e2a\u9b54\u5934\uff0c\u4f69\u670d\uff0c\u4f69 \u670d\uff01\u53ef\u662f\u4f60\u4eec\u5728\u5ba2\u5e97\u4e4b\u4e2d\u7684\u90a3\u573a\u6363\u4e71\uff0c\u5374\u53c8\u662f\u4e3a\u4f55\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u4ed6\u4eec\u867d\u4e0d\u77e5\u9053\u5468\u5c71\u6c11\u662f \u4f60\u91ca\u653e\uff0c\u4f46\u5931\u4e86\u91cd\u72af\uff0c\u8fd9\u7f6a\u540d\u53ef\u4e5f\u4e0d\u5c0f\u54c7\uff01\u5f20\u5927\u4eba\u5b97\u5144\uff0c\u4f60\u719f\u8bfb\u5175\u4e66\uff0c\u5f53\u77e5\u9ec4\u76d6\u7684\u82e6\u8089\u4e4b \u8ba1\u3002\u201d\u5f20\u98ce\u5e9c\u604d\u7136\u5927\u609f\uff0c\u5728\u9a6c\u4e0a\u62b1\u62f3\u65bd\u793c\u9053\uff1a\u201c\u591a\u8c22\u5927\u6069\uff0c\u6ca1\u9f7f\u4e0d\u5fd8\uff01\u201d\u4e91\u857e\u5c1a\u672a\u660e\u767d\uff0c\u7981 \u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u4f60\u4eec\u5f04\u7684\u7a76\u7adf\u662f\u751a\u7384\u865a\uff1f\u201d\u5f20\u98ce\u5e9c\u9053\uff1a\u201c\u4ed6\u4eec\u6253\u5f00\u56da\u8f66\uff0c\u653e\u8d70\u56da\u72af\uff0c\u6211\u81ea\u7136\u96be \u9003\u7f6a\u8d23\uff0c\u53ef\u662f\u6765\u7684\u82e5\u662f\u6781\u5389\u5bb3\u7684\u654c\u4eba\uff0c\u6211\u4eec\u4eba\u4eba\u53d7\u5236\uff0c\u90a3\u5c31\u8bf4\u6211\u5df2\u5c3d\u529b\u800c\u4e3a\uff0c\u53ea\u56e0\u529b\u6240\u4e0d \u654c\uff0c\u5e76\u65e0\u4f6f\u8d25\u79c1\u653e\u7684\u5acc\u7591\uff0c\u90a3\u7f6a\u540d\u5c31\u51cf\u8f7b\u4e86\u3002\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u4e0d\u4f46\u5982\u6b64\uff0c\u4ee5\u4f60\u7684\u58f0\u540d\uff0c\u672c\u6765 \u6218\u8d25\u5df2\u662f\u6709\u7f6a\uff0c\u4f46\u82e5\u6765\u88ad\u7684\u654c\u4eba\uff0c\u628a\u672c\u4e8b\u6bd4\u4f60\u66f4\u9ad8\u7684\u4eba\u90fd\u6253\u8d25\u4e86\uff0c\u90a3\u4e48\u5eb7\u603b\u7ba1\u4e5f\u5c31\u4e0d\u597d\u610f\u601d \u964d\u7f6a\u4f60\u5566\u3002\u201d\u5f20\u98ce\u5e9c\u9053\uff1a\u201c\u90a3\u5c31\u662f\u8bf4\u4f60\u4eec\u51c6\u5907\u7ed9\u94c1\u81c2\u91d1\u733f\u4e0e\u4e09\u82b1\u5251\u4e00\u70b9\u5389\u5bb3\u5c1d\u5c1d\u4e86\uff0c\u4f60\u4eec\u8c01 \u80fd\u6253\u8d25\u4ed6\u4eec\u4e48\uff1f\u201d\u5f20\u4e39\u67ab\u7b11\u9053\uff1a\u201c\u4f60\u4e14\u7ec6\u542c\uff01\u201d \u3000\u3000\u53ea\u542c\u5f97\u5c71\u5773\u90a3\u8fb9\u4e00\u9635\u9635\u9ad8\u547c\u9163\u6597\u4e4b\u58f0\uff0c\u4f3c\u662f\u6b63\u5411\u8fd9\u8fb9\u8ffd\u6740\u8fc7\u6765\uff0c\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u8fd8\u6709\u4e09\u91cc \u8def\u7a0b\uff0c\u5f20\u5927\u4eba\uff0c\u6211\u8fd8\u8981\u9001\u4f60\u4e00\u70b9\u8584\u793c\u3002\u201d\u5f20\u4e39\u67ab\u624b\u4e2d\u63d0\u7740\u4e00\u4e2a\u7ea2\u5e03\u5305\u88f9\uff0c\u5706\u9f13\u9f13\u7684\u597d\u8c61\u5185\u4e2d \u85cf\u7740\u4e00\u4e2a\u897f\u74dc\u3002\u5f20\u98ce\u5e9c\u63a5\u4e86\u8fc7\u6765\uff0c\u6253\u5f00\u4e00\u770b\uff0c\u5185\u4e2d\u85cf\u7684\u7adf\u662f\u4e00\u4e2a\u4eba\u5934\uff0c\u5f20\u98ce\u5e9c\u9762\u8272\u5927\u53d8\uff0c\u624b \u8d77\u4e00\u5200\uff0c\u5411\u5f20\u4e39\u67ab\u8fce\u9762\u5288\u53bb\uff0c\u5634\u4e2d\u9a82\u9053\uff1a\u201c\u4f60\u4e3a\u4f55\u6740\u4e86\u6211\u7684\u4e8c\u5f1f\uff0c\u8fd9\u96be\u9053\u4e5f\u662f\u82e6\u8089\u4e4b\u8ba1 \u5417\uff1f\u201d\u4e91\u857e\u5728\u65c1\uff0c\u4e5f\u770b\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u8fd9\u6b63\u662f\u4e0e\u5f20\u98ce\u5e9c\u3001\u6a0a\u5fe0\u5408\u79f0\u4eac\u5e08\u4e09\u5927\u9ad8\u624b\uff0c\u5185\u5ef7\u536b\u58eb\u8d2f \u4ef2\u7684\u5934\u9885\u3002 \u3000\u3000\u5f20\u98ce\u5e9c\u8fd9\u4e00\u5200\u4e43\u662f\u5728\u6025\u6012\u653b\u5fc3\u4e4b\u4e0b\u5288\u51fa\uff0c\u5a01\u52bf\u731b\u6377\u65e0\u4f26\u3002\u53ea\u89c1\u5f20\u4e39\u67ab\u5927\u53eb\u4e00\u58f0\uff1a\u201c\u54c7\u54c7 \u4e0d\u5f97\u4e86\uff01\u201d\u6574\u4e2a\u8eab\u8eaf\uff0c\u98de\u4e86\u8d77\u6765\uff01\u6b63\u662f\uff1a \u3000\u3000\u53c8\u89c1\u5f20\u90ce\u65bd\u5999\u8ba1\uff0c\u4e00\u573a\u5927\u7978\u5f2d\u65e0\u5f62\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u4e14\u770b\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000---------------------------- \u3000\u3000brillt \u91cd\u6821\uff0c\u65e7\u96e8\u697c \u72ec\u5bb6\u66f4\u65b0  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u840d\u8e2a\u4fa0\u5f71\u5f55\u300b\u2014\u2014\u7b2c\u5341\u4e09\u56de\u3000\u6234\u6708\u62ab\u661f\u82e6\u5fc3\u6551\u826f\u53cb\u3000\u79fb\u82b1\u63a5\u6728\u73cd\u91cd\u6258\u73ca\u745a \u6881\u7fbd\u751f\u300a\u840d\u8e2a\u4fa0\u5f71\u5f55\u300b \u7b2c\u5341\u4e09\u56de\u3000\u6234\u6708\u62ab\u661f\u82e6\u5fc3\u6551\u826f\u53cb\u3000\u79fb\u82b1\u63a5\u6728\u73cd\u91cd\u6258\u73ca\u745a \u3000\u3000\u5f20\u98ce\u5e9c\u8fd9\u5200\u867d\u662f\u6bd5\u751f\u529f\u529b\u4e4b\u6240\u805a\uff0c\u4f46\u5f20\u4e39\u67ab\u65e9\u6709\u9632\u5907\uff0c\u968f\u7740\u5200\u98ce\uff0c\u76f4\u6643\u51fa\u53bb\uff0c\u624b\u821e\u8db3 \u8e48\uff0c\u6545\u4f5c\u60ca\u614c\u5931\u63aa\u4e4b\u72b6\u3002\u5f20\u98ce\u5e9c\u8d8a\u53d1\u5927\u6012\uff0c\u9a82\u9053\uff1a\u201c\u4f60\u6545\u610f\u6765\u5c06\u6211\u620f\u5f04\uff0c\u662f\u4f55\u5c45\u5fc3\uff1f\u201d\u5f20\u4e39 \u67ab\u54c8\u54c8\u4e00\u7b11\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u8c22\u6211\u4e5f\u8fd8\u7f62\u4e86\uff0c\u600e\u4e48\u98a0\u5012\u9a82\u6211\uff1f\u4f60\u770b\u8fd9\u662f\u4ec0\u4e48\uff1f\u201d\u968f\u624b\u4e00\u629b\uff0c\u629b\u8fc7 \u4e00\u5c01\u6731\u6f06\u5c01\u53e3\u7684\u6587\u4e66\u3002\u6587\u4e66\u5206\u91cf\u751a\u8f7b\u7adf\u7ed9\u4ed6\u5728\u6570\u4e08\u4e4b\u5916\uff0c\u50cf\u53d1\u6697\u5668\u4e00\u6837\u7684\u629b\u63b7\u8fc7\u6765\uff0c\u5185\u5bb6\u52b2 \u529b\u4e4b\u6df1\u867d\u662f\u5f20\u98ce\u5e9c\u90a3\u6837\u7684\u9ad8\u624b\uff0c\u4e5f\u4e0d\u89c9\u5403\u4e86\u4e00\u60ca\u3002 \u3000\u3000\u62c6\u5f00\u4e00\u770b\uff0c\u8fd9\u6587\u4e66\u7adf\u662f\u8d2f\u4ef2\u79d8\u5bc6\u9001\u5448\u5eb7\u603b\u7ba1\u7684\uff0c\u5185\u4e2d\u5c06\u51fa\u5dee\u4ee5\u6765\uff0c\u5f20\u98ce\u5e9c\u7684\u4e00\u8a00\u4e00\u884c\u90fd \u5199\u5728\u91cc\u9762\uff0c\u5f20\u98ce\u5e9c\u5728\u4e94\u62db\u4e4b\u5185\u8d25\u4e0e\u5f20\u3001\u4e91\u4e8c\u4eba\uff0c\u53c8\u4e0d\u51c6\u65c1\u4eba\u5e2e\u624b\u7b49\u4e8b\uff0c\u90fd\u6709\u8bb0\u5f55\u3002\u5468\u5c71\u6c11\u5982 \u4f55\u88ab\u64d2\uff0c\u5982\u4f55\u88ab\u4ed6\u6df7\u5728\u56da\u72af\u4e4b\u4e2d\u5e26\u8d70\u7b49\u4e8b\uff0c\u66f4\u662f\u5199\u5f97\u8be6\u8be6\u7ec6\u7ec6\u3002\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u8d2f\u4ef2\u65e9\u5df2\u8ba4\u51fa \u5468\u5c71\u6c11\uff0c\u4e0d\u8fc7\u4ed6\u4e0d\u8bf4\u4e0e\u4f60\u77e5\u3002\u4ed6\u5f53\u65e5\u4e0d\u53ca\u5199\u4fe1\uff0c\u5c31\u5bc6\u9063\u5fc3\u8179\uff0c\u98de\u670d\u4e0a\u4eac\uff0c\u4e0d\u8fc7\u5bf9\u4f60\u5c1a\u65e0\u5927 \u788d\uff0c\u82e5\u8fd9\u5c01\u4fe1\u7ed9\u5eb7\u603b\u7ba1\u89c1\u4e86\uff0c\u53ef\u662f\u6709\u6240\u4e0d\u4fbf\uff01\u201d \u3000\u3000\u5f20\u98ce\u5e9c\u63b7\u5200\u957f\u53f9\u9053\uff1a\u201c\u4e8c\u5f1f\u672c\u662f\u8d2a\u5fc3\u5229\u7984\uff0c\u5374\u4e0d\u6599\u4ed6\u5351\u52a3\u5982\u65af\uff01\u201d\u5144\u5f1f\u60c5\u6df1\uff0c\u773c\u6cea\u6ef4 \u4e0b\u3002\u4e91\u857e\u5fcd\u4e0d\u4f4f\u9053\uff1a\u201c\u8fd9\u6837\u7684\u4eba\uff0c\u4f60\u8fd8\u54ed\u4ed6\u4f5c\u751a\uff1f\u201d\u5f20\u98ce\u5e9c\u9053\uff1a\u201c\u5230\u5e95\u662f\u5144\u5f1f\u4e00\u573a\u3002\u6211\u4e0d\u602a \u4f60\u6740\u4ed6\uff0c\u4f60\u8d70\u5427\uff01\u201d\u5c71\u5773\u90a3\u8fb9\u8ffd\u6740\u4e4b\u58f0\u8d8a\u6765\u8d8a\u8fd1\uff0c\u5f20\u98ce\u5e9c\u5c06\u5934\u9885\u5305\u597d\uff0c\u6302\u5728\u9a6c\u978d\uff0c\u80cc\u5411\u5f20\u3001 \u4e91\u4e8c\u4eba\u3002\u5f20\u4e39\u67ab\u7a81\u7136\u62bd\u51fa\u5b9d\u5251\uff0c\u5237\u7684\u4e00\u5251\u523a\u53bb\uff0c\u4e91\u857e\u60ca\u547c\u9053\uff1a\u201c\u4f60\u5e72\u4ec0\u4e48\uff1f\u201d\u4f46\u89c1\u5f20\u98ce\u5e9c\u75db \u5f97\u54c7\u7136\u5927\u53eb\uff0c\u56de\u8fc7\u5934\u6765\uff0c\u773c\u4e2d\u795e\u8272\uff0c\u60ca\u9a87\u4e4b\u6781\uff01 \u3000\u3000\u8fd9\u4e00\u5251\u53ea\u524a\u53bb\u4e86\u5f20\u98ce\u5e9c\u5de6\u81c2\u4e00\u7247\u76ae\u8089\uff0c\u5e76\u65e0\u5927\u788d\u3002\u5f20\u98ce\u5e9c\u53c8\u60ca\u53c8\u6012\u521a\u8bf4\u5f97\u4e00\u4e2a\u201c\u597d\u201d \u5b57\uff0c\u53ea\u542c\u5f97\u5f20\u4e39\u67ab\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u5feb\u62fe\u8d77\u7f05\u5200\uff0c\u4e0e\u6211\u4ea4\u624b\u3002\u201d\u5f20\u98ce\u5e9c\u604d\u7136\u5927\u609f\uff0c\u7acb\u5373\u62fe\u8d77\u7f05 \u5200\uff0c\u4e0e\u5f20\u4e39\u67ab\u6253\u4f5c\u4e00\u56e2\uff0c\u5de6\u81c2\u9c9c\u8840\uff0c\u4e00\u70b9\u4e00\u70b9\u5730\u6ef4\u5728\u5730\u4e0a\uff0c\u4e5f\u987e\u4e0d\u5f97\u6b62\u75db\u5305\u624e\u3002 \u3000\u3000\u4e91\u857e\u4e0d\u89c9\u5931\u7b11\uff0c\u5fc3\u9053\uff1a\u201c\u5f20\u4e39\u67ab\u771f\u662f\u7cbe\u7075\u53e4\u602a\uff0c\u8fd9\u82e6\u8089\u4e4b\u8ba1\uff0c\u5374\u4e5f\u628a\u6211\u5413\u4e86\u4e00\u8df3\u3002\u201d\u8bd5 \u60f3\u5f20\u98ce\u5e9c\u82e5\u4e0d\u88ab\u201c\u654c\u4eba\u201d\u523a\u4f24\uff0c\u5c45\u6240\u88ab\u88ad\uff0c\u5931\u6389\u91cd\u72af\u7b49\u4e8b\uff0c\u90a3\u5c31\u4e0d\u597d\u4ea4\u4ee3\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u8fb9\u6253\u8fb9\u4f4e\u58f0\u7b11\u9053\uff1a\u201c\u4f60\u9002\u624d\u780d\u6211\u4e00\u5200\uff0c\u6ca1\u6709\u780d\u7740\uff0c\u6211\u523a\u4f60\u4e00\u5251\uff0c\u5374\u628a\u4f60\u523a\u4f24\uff0c\u4f60 \u670d\u4e86\u6211\u5427\u3002\u201d\u5f20\u98ce\u5e9c\u88ab\u4ed6\u5f04\u5f97\u54ed\u7b11\u4e0d\u5f97\uff0c\u5200\u6cd5\u6563\u6f2b\uff0c\u4e0d\u6599\u5f20\u4e39\u67ab\u771f\u771f\u5047\u5047\uff0c\u5251\u6cd5\u4e00\u7d27\uff0c\u7adf\u5982 \u72c2\u98ce\u66b4\u96e8\u822c\u7684\u6740\u6765\uff0c\u5f20\u98ce\u5e9c\u5de6\u81c2\u53d7\u4f24\uff0c\u9669\u9669\u88ab\u4ed6\u523a\u4e2d\u8981\u5bb3\uff0c\u8feb\u5f97\u8ba4\u771f\u62b5\u654c\u3002 \u3000\u3000\u53ea\u89c1\u5c71\u5773\u8f6c\u89d2\u4e4b\u5904\uff0c\u4e00\u4f19\u4eba\u6253\u5f97\u7ffb\u7ffb\u6eda\u6eda\uff0c\u76f4\u903c\u8fc7\u6765\uff0c\u524d\u9762\u7684\u662f\u9ed1\u767d\u6469\u8bc3\uff0c\u540e\u9762\u7684\u4e00\u4e2a \u8001\u6c49\u4e00\u4e2a\u9053\u4eba\uff0c\u5374\u6b63\u662f\u5eb7\u603b\u7ba1\u90a3\u4e24\u4e2a\u5e08\u53d4\u3002\u9ed1\u767d\u6469\u8bc3\u8fb9\u8d70\u8fb9\u6218\uff0c\u867d\u8d25\u4e0d\u4e71\u3002 \u3000\u3000\u4e09\u82b1\u5251\u7384\u7075\u5b50\u5ffd\u89c1\u5f20\u98ce\u5e9c\u88ab\u4e00\u4e2a\u767d\u8863\u5c11\u5e74\u6740\u5f97\u624b\u5fd9\u811a\u4e71\uff0c\u8d1f\u4f24\u529b\u6218\uff0c\u4e0d\u89c9\u60ca\u7591\u4ea4\u5e76\uff0c\u5fc3 \u9053\uff1a\u201c\u8fd9\u5c11\u5e74\u662f\u4f55\u65b9\u795e\u5723\uff0c\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u5c45\u7136\u80fd\u5c06\u5f20\u98ce\u5e9c\u6253\u5f97\u5982\u6b64\u72fc\u72c8\uff0c\u96be\u9053\u662f\u5eb7\u8d85\u6d77\u8a00\u8fc7\u5176 \u5b9e\u6545\u610f\u5c06\u5f20\u98ce\u5e9c\u7684\u672c\u4e8b\u5938\u5f20\u4e86\u4e48\uff1f\u201d\u7acb\u5373\u865a\u6643\u4e00\u5251\uff0c\u820d\u4e86\u9ed1\u767d\u6469\u8bc3\uff0c\u98de\u8eab\u62a2\u5230\u524d\u9762\u53eb\u9053\uff1a \u201c\u5f20\u5927\u4eba\uff0c\u4f60\u4e14\u9000\u4e0b\uff0c\u5f85\u6211\u53d6\u4ed6\uff01\u201d \u3000\u3000\u7384\u7075\u5b50\u662f\u70b9\u82cd\u6d3e\u6709\u6570\u4eba\u7269\uff0c\u51fa\u624b\u679c\u662f\u4e0d\u540c\u51e1\u54cd\u3002\u53ea\u89c1\u4ed6\u957f\u5251\u4e00\u633d\u4e00\u9001\uff0c\u767b\u65f6\u98de\u8d77\u4e00\u6735\u5251 \u82b1\uff0c\u624b\u6cd5\u4e0d\u53d8\uff0c\u5251\u5c16\u53c8\u5df2\u5de6\u53f3\u865a\u523a\u4e24\u5251\uff0c\u53c8\u98de\u8d77\u4e24\u6735\u5251\u82b1\u3002\u4ed6\u6bcf\u51fa\u624b\uff0c\u90fd\u662f\u4e00\u62db\u4e09\u5f0f\uff0c\u4e24\u865a \u4e00\u5b9e\uff0c\u98de\u8d77\u7684\u5251\u82b1\u4e5f\u662f\u4e00\u5927\u4e24\u5c0f\uff0c\u6240\u4ee5\u6709\u201c\u4e09\u82b1\u5251\u201d\u4e4b\u79f0\uff0c\u7b49\u95f2\u4eba\u7269\uff0c\u6321\u4e0d\u4e86\u4ed6\u4e09\u62db\u4e24\u5f0f\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u53eb\u9053\uff1a\u201c\u554a\u5440\uff0c\u4e0d\u597d\u4e86\uff01\u201d\u7384\u7075\u5b50\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u77e5\u9053\u4e0d\u597d\u4e86\u4e48\uff1f\u201d\u632f\u5251\u4e00\u6325\uff0c\u4f46 \u89c1\u4e09\u6735\u5251\u82b1\uff0c\u9f50\u98de\u8fc7\u53bb\uff0c\u5f20\u4e39\u67ab\u811a\u8ddf\u4e00\u65cb\uff0c\u56e2\u56e2\u8f6c\u8f6c\uff0c\u7adf\u7136\u968f\u7740\u4ed6\u865a\u523a\u7684\u4e24\u5251\uff0c\u76f4\u8f6c\u8fc7\u53bb\uff0c \u867d\u662f\u4e09\u82b1\u76d6\u9876\uff0c\u5374\u662f\u6beb\u53d1\u65e0\u4f24\u3002\u7384\u7075\u5b50\u5403\u4e86\u4e00\u60ca\uff1a\u8fd9\u4efd\u8f7b\u529f\uff0c\u53ef\u662f\u4eba\u95f4\u7f55\u89c1\u3002\u4e0d\u6562\u8f7b\u89c6\uff0c\u4e0a \u4e0b\u524d\u540e\u5de6\u53f3\uff0c\u75be\u523a\u516d\u5251\uff0c\u6bcf\u5251\u53c8\u5206\u4e3a\u4e09\u5f0f\uff0c\u865a\u865a\u5b9e\u5b9e\uff0c\u53d8\u5316\u65e0\u7a77\uff0c\u5251\u82b1\u9519\u843d\uff0c\u6709\u5982\u5929\u4e0a\u7e41 \u661f\uff0c\u4efb\u662f\u7edd\u9876\u8f7b\u529f\uff0c\u4e5f\u96be\u8eb2\u95f2\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u5f20\u4e39\u67ab\u54c8\u54c8\u5927\u7b11\uff0c\u9661\u89c1\u4e00\u9053\u767d\u5149\uff0c\u6709\u5982\u795e\u9f99\u592d\u77eb\uff0c\u4ece\u6ee1\u7a7a\u98de\u964d\u7684\u5251\u82b1\u4e4b\u4e2d\u76f4\u7a7f\u51fa \u53bb\u3002\u5f20\u4e39\u67ab\u62d4\u5251\u51fa\u9798\u5feb\u6377\u5f02\u5e38\uff0c\u5f92\u89c1\u7384\u7075\u5b50\u770b\u51fa\u662f\u5b9d\u5251\u4e4b\u65f6\uff0c\u5f20\u4e39\u67ab\u7684\u5251\u950b\u5df2\u524a\u5230\u4ed6\u7684\u624b \u8155\u3002\u7384\u7075\u5b50\u82e5\u662f\u53cd\u5251\u62b5\u5fa1\uff0c\u5175\u5203\u5fc5\u7136\u88ab\u4ed6\u524a\u65ad\uff0c\u4e91\u857e\u770b\u5f97\u8840\u8109\u507e\u5f20\u5fcd\u4e0d\u4f4f\u53eb\u9053\uff1a\u201c\u597d\u554a\uff01\u201d \u3000\u3000\u5ffd\u89c1\u7384\u7075\u5b50\u624b\u8155\u4e00\u7ffb\uff0c\u767d\u5149\u5ffd\u5730\u505c\u4f4f\u3002\u539f\u6765\u662f\u7384\u7075\u5b50\u7684\u957f\u5251\u642d\u4e0a\u4e86\u5f20\u4e39\u67ab\u7684\u5251\u8eab\uff0c\u53cc\u5251 \u76f8\u4ea4\uff0c\u5f7c\u6b64\u9ecf\u4f4f\u3002\u5f20\u4e39\u67ab\u4e5f\u4e0d\u7981\u5927\u5403\u4e00\u60ca\uff0c\u8fd9\u7384\u7075\u5b50\u53d8\u62db\u7684\u5feb\u6377\u4e0e\u529f\u529b\u4e4b\u6df1\u539a\uff0c\u679c\u7136\u8fd8\u5728\u5f20 \u98ce\u5e9c\u4e4b\u4e0a\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u518d\u8d70\u9669\u62db\uff0c\u624b\u52b2\u4e00\u677e\uff0c\u8ba9\u7384\u7075\u5b50\u7684\u52b2\u529b\u903c\u6765\uff0c\u5b9d\u5251\u9661\u7136\u79fb\u5f00\uff0c\u5f2f\u8170\u4e00\u5251\uff0c\u523a\u7384\u7075 \u5b50\u4e0b\u76d8\u80be\u6c34\u547d\u95e8\u8981\u7a74\u3002\u7384\u7075\u5b50\u957f\u5251\u547c\u7684\u4e00\u58f0\uff0c\u4ece\u4ed6\u5934\u9876\u524a\u8fc7\uff0c\u62db\u6570\u672a\u66fe\u4f7f\u8001\uff0c\u5ffd\u5730\u5411\u540e\u4e00 \u4ef0\uff0c\u9976\u4ed6\u907f\u5f97\u5982\u6b64\u5feb\u6377\uff0c\u888d\u89d2\u4e5f\u88ab\u524a\u53bb\u4e86\u4e00\u622a\u3002\u8fd9\u4e24\u62db\u53cc\u65b9\u90fd\u4f7f\u5f97\u9669\u6781\uff0c\u5f20\u4e39\u67ab\u82e5\u4e0d\u662f\u5192\u9669 \u7a81\u653b\uff0c\u5934\u9885\u4e00\u5b9a\u88ab\u4ed6\u957f\u5251\u7a7f\u8fc7\uff01 \u3000\u3000\u7384\u7075\u5b50\u8fde\u4f7f\u6570\u62db\uff0c\u5360\u4e0d\u4e86\u4fbf\u5b9c\uff0c\u52c3\u7136\u5927\u6012\uff0c\u957f\u5251\u4e00\u4e2a\u76d8\u65cb\u65bd\u5c55\u6740\u624b\u795e\u62db\uff0c\u4f46\u89c1\u5251\u5f71\u7eb5 \u6a2a\uff0c\u5251\u82b1\u4e71\u821e\uff0c\u865a\u865a\u5b9e\u5b9e\uff0c\u53eb\u4eba\u76ee\u7729\u795e\u8ff7\u3002\u5f20\u4e39\u67ab\u5fc3\u9053\uff1a\u201c\u5728\u767e\u62db\u4e4b\u5185\uff0c\u6211\u53ef\u4ee5\u4e0e\u4ed6\u6253\u6210\u5e73 \u624b\uff0c\u82e5\u6218\u5230\u767e\u62db\u4e4b\u5916\uff0c\u6211\u7684\u6b66\u529f\u53ef\u5c31\u8981\u6cc4\u5e95\u5566\uff01\u201d\u5c06\u5b9d\u5251\u821e\u8d77\u4e00\u56e2\u767d\u5149\uff0c\u62a4\u7740\u5168\u8eab\uff0c\u9ad8\u58f0\u53eb \u9053\uff1a\u201c\u5355\u6253\u72ec\u6597\uff0c\u4f55\u65f6\u65b9\u80fd\u4e86\u7ed3\uff1f\u5582\u4f60\u8fd8\u6709\u4e00\u4e2a\u4f19\u4f34\uff0c\u53eb\u4ed6\u4e00\u9f50\u6765\u5427\uff01\u5582\uff0c\u9ed1\u767d\u6469\u8bc3\uff0c\u653e\u5f00 \u8fd9\u4e2a\u7cdf\u8001\u5934\u513f\uff0c\u4f60\u4eec\u8d70\u5427\uff01\u201d \u3000\u3000\u7384\u7075\u5b50\u7684\u5e08\u5144\u94c1\u81c2\u91d1\u733f\u9f99\u9547\u65b9\uff0c\u4ee5\u4e00\u654c\u4e8c\uff0c\u6b63\u88ab\u9ed1\u767d\u6469\u8bc3\u6740\u5f97\u547c\u547c\u5598\u6c14\u51b7\u6c57\u76f4\u6d41\uff0c\u5ffd\u611f \u538b\u529b\u4e00\u677e\uff0c\u9ed1\u767d\u6469\u8bc3\u540c\u58f0\u7b11\u9053\uff1a\u201c\u7b97\u4f60\u547d\u5927\uff0c\u6211\u7684\u5c0f\u670b\u53cb\u4fdd\u4f60\u4e0d\u6b7b\u3002\u653e\u4f60\u8d70\u5566\uff01\u201d\u9f99\u9547\u65b9\u5927 \u6012\u5c1a\u5f85\u8fdb\u62db\uff0c\u9ed1\u6469\u8bc3\u4e00\u6756\u98de\u6765\uff0c\u9f99\u9547\u65b9\u659c\u95ea\u4e24\u6b65\uff0c\u62db\u6570\u521a\u521a\u9012\u51fa\u54ea\u77e5\u9ed1\u767d\u6469\u8bc3\u8fd9\u5bf9\u5b6a\u751f\u5144 \u5f1f\uff0c\u5fc3\u610f\u76f8\u901a\uff0c\u4ed6\u4eec\u5e73\u65e5\u53c8\u914d\u5408\u6709\u7d20\u505c\u62db\u8fdb\u62db\uff0c\u90fd\u4f3c\u9884\u5148\u7ea6\u5b9a\u4e00\u822c\uff0c\u9f99\u9547\u65b9\u5411\u5de6\u4e00\u95ea\uff0c\u767d\u6469 \u8bc3\u521a\u597d\u62a2\u5148\u4e00\u6b65\uff0c\u8e0f\u4e0a\u90a3\u4e2a\u65b9\u4f4d\uff0c\u767d\u7389\u6756\u5728\u9f99\u9547\u65b9\u80cc\u4e0a\u4e00\u6572\uff0c\u5927\u7b11\u8bf4\u9053\uff1a\u201c\u6253\u4f60\u8fd9\u4e0d\u77e5\u8fdb\u9000 \u7684\u8001\u7334\u513f\uff01\u201d\u5927\u7b11\u58f0\u4e2d\uff0c\u4e24\u5144\u5f1f\u626c\u957f\u800c\u53bb\u3002\u53ea\u6c14\u5f97\u94c1\u81c2\u91d1\u733f\u51e0\u4e4e\u6655\u5012\u5730\u4e0a\u3002 \u3000\u3000\u767d\u6469\u8bc3\u8fd9\u6756\u6c89\u91cd\u5f02\u5e38\uff0c\u9976\u662f\u94c1\u81c2\u91d1\u733f\u5185\u529f\u7cbe\u7eaf\uff0c\u8fd0\u6c14\u4e09\u8f6c\u4ecd\u662f\u89c9\u5f97\u808b\u9aa8\u9690\u9690\u4f5c\u75db\u3002\u5f20\u4e39 \u67ab\u7b11\u9053\uff1a\u201c\u8001\u7334\u513f\uff0c\u88ab\u6253\u65ad\u810a\u9aa8\u4e86\u4e48\uff1f\u201d\u94c1\u81c2\u91d1\u733f\u662f\u6210\u540d\u4e86\u51e0\u5341\u5e74\u7684\u4eba\u7269\uff0c\u51e0\u66fe\u53d7\u8fc7\u4eca\u65e5\u4e4b \u6c14\u5462\uff1f\u5927\u543c\u4e00\u58f0\uff1a\u201c\u5c0f\u8d3c\u6b3a\u6211\u592a\u751a\uff01\u201d\u602a\u5175\u5668\u5f80\u5730\u4e0b\u4e00\u6491\uff0c\u8eab\u5f62\u6251\u817e\u98de\u8d77\uff0c\u7adf\u5728\u6a2a\u7a7a\u4ea4\u51fb\u7684 \u5251\u6c14\u4e4b\u4e2d\uff0c\u7a81\u7136\u4e0b\u88ad\u3002 \u3000\u3000\u94c1\u81c2\u91d1\u733f\u7684\u5175\u5668\u5f62\u4f3c\u9f99\u5934\u62d0\u6756\uff0c\u53ef\u53c8\u6bd4\u666e\u901a\u7684\u9f99\u5934\u62d0\u6756\u591a\u4e86\u4e24\u6837\u4e1c\u897f\uff0c\u4e00\u6837\u662f\u5728\u62d0\u6756\u7684 \u5c16\u7aef\uff0c\u4f38\u51fa\u4e00\u4e2a\u5f62\u5982\u624b\u638c\u7684\u4e1c\u897f\u4e94\u679d\u660e\u6643\u6643\u7684\u5229\u94a9\uff0c\u6709\u5982\u624b\u6307\uff0c\u53ef\u4ee5\u52fe\u523a\u6495\u62c9\uff1b\u62d0\u6756\u4e0a\u53c8\u957f \u6ee1\u5c16\u523a\uff0c\u6574\u679d\u62d0\u6756\u9664\u4e86\u624b\u63e1\u7684\u9f99\u5934\u628a\u624b\u90e8\u5206\uff0c\u5176\u4f59\u90fd\u4e0d\u53ef\u63a5\u89e6\uff0c\u821e\u52a8\u8d77\u6765\uff0c\u786e\u662f\u6709\u5982\u6bdb\u8338\u8338 \u7684\u733f\u81c2\uff0c\u4f5c\u652b\u4eba\u4e4b\u52bf\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u72ec\u6218\u4e09\u82b1\u5251\u7384\u7075\u5b50\u5df2\u611f\u5403\u529b\uff0c\u733f\u81c2\u91d1\u733f\u7a81\u7136\u6765\u88ad\uff0c\u6709\u5982\u7a7a\u4e2d\u4f38\u4e0b\u602a\u624b\uff0c\u5929\u7075\u76d6\u51e0 \u4e4e\u7ed9\u62d0\u6756\u5c16\u7aef\u7684\u94c1\u638c\u6293\u7740\u3002\u5f20\u4e39\u67ab\u5403\u4e86\u4e00\u60ca\uff0c\u5251\u8bc0\u4e00\u6307\uff0c\u5251\u5149\u98d8\u5ffd\uff0c\u4e00\u62db\u201c\u5206\u82b1\u62c2\u67f3\u201d\uff0c\u4f3c \u4e1c\u4f3c\u897f\uff0c\u5206\u88ad\u4e8c\u4eba\uff0c\u94c1\u81c2\u91d1\u733f\u4e00\u58f0\u4f4e\u5578\uff0c\u500f\u5ffd\u8fde\u8fdb\u4e09\u62db\u3002\u733f\u81c2\u822c\u7684\u602a\u5175\u5668\u7adf\u968f\u7740\u5251\u5149\u98de\u821e\uff0c \u6251\u51fb\u64d2\u62ff\uff0c\u5f20\u4e39\u67ab\u4e5f\u4e0d\u89c9\u6697\u6697\u9053\u597d\u5fc3\u9053\uff1a\u201c\u8fd9\u94c1\u81c2\u91d1\u733f\u679c\u7136\u540d\u4e0d\u865a\u4f20\uff0c\u5728\u82e6\u6218\u9ed1\u767d\u6469\u8bc3\uff0c\u6371 \u4e86\u4e00\u6756\u4e4b\u540e\uff0c\u5c45\u7136\u8fd8\u662f\u8fd9\u822c\u4e86\u5f97\uff01\u201d\u7384\u7075\u5b50\u7684\u4e09\u82b1\u5251\u4e5f\u9aa4\u7136\u52a0\u7d27\uff0c\u5251\u5251\u76f4\u53d6\u8981\u5bb3\uff0c\u5f20\u4e39\u67ab\u5e94 \u4ed8\u4e3a\u96be\uff0c\u5374\u662f\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u5999\u6781\uff0c\u5999\u6781\uff01\u4e24\u4e2a\u8001\u8d3c\u4e00\u9f50\u6253\u53d1\uff0c\u7701\u4e86\u591a\u5c11\u529f\u592b\uff01\u5c0f\u5144\u5f1f\u4e0a \u554a\uff01\u201d\u4e91\u857e\u6728\u7136\u4e0d\u52a8\uff0c\u5ffd\u89c1\u5f20\u4e39\u67ab\u4e00\u4e2a\u8e09\u8dc4\u9669\u9669\u88ab\u7384\u7075\u5b50\u7684\u957f\u5251\u9489\u4f4f\uff0c\u521a\u4e00\u95ea\u8eab\uff0c\u53c8\u51e0\u4e4e\u7ed9 \u94c1\u81c2\u91d1\u733f\u7684\u602a\u5175\u5203\u52fe\u7740\u54bd\u5589\uff0c\u771f\u662f\u9669\u8c61\u73af\u751f\uff0c\u4ee4\u4eba\u60ca\u5fc3\u52a8\u9b44\u3002\u5f20\u98ce\u5e9c\u9000\u4e0b\u4e00\u8fb9\uff0c\u770b\u5f97\u5341\u5206\u5fc3 \u6025\uff0c\u89c1\u4e91\u857e\u8fdf\u8fdf\u4e0d\u4e0a\uff0c\u51e0\u4e4e\u8981\u66ff\u5f20\u4e39\u98ce\u50ac\u51fa\u58f0\u6765\u3002 \u3000\u3000\u5ffd\u89c1\u9752\u5149\u4e00\u95ea\uff0c\u4e91\u857e\u6325\u5251\u75be\u4e0a\uff0c\u5f20\u4e39\u67ab\u4e00\u58f0\u5439\u547c\uff0c\u767d\u5149\u66b4\u957f\uff0c\u4f3c\u5343\u91cc\u6d2a\u6ce2\uff0c\u6e83\u56f4\u800c\u51fa\uff0c \u9752\u5149\u767d\u5149\uff0c\u4e00\u5408\u4e4b\u540e\uff0c\u5ffd\u5982\u4e00\u9053\u5149\u73af\uff0c\u56db\u8fb9\u6269\u5c55\uff0c\u53cc\u5251\u5408\u74a7\uff0c\u5a01\u52bf\u66b4\u589e\u3002\u94c1\u81c2\u91d1\u733f\u4e0e\u4e09\u82b1\u5251 \u53ea\u89c9\u654c\u4eba\u7684\u5251\u52bf\uff0c\u6709\u5982\u6392\u5c71\u5012\u6d77\u822c\u5730\u76f4\u538b\u8fc7\u6765\uff0c\u5413\u5f97\u8fde\u8fde\u540e\u9000\u3002\u7384\u7075\u5b50\u5c1a\u5f85\u89c5\u9699\u8fdb\u51fb\uff0c\u4f46\u53cc \u5251\u5408\u74a7\uff0c\u9996\u5c3e\u76f8\u8fde\uff0c\u5929\u8863\u65e0\u7f1d\uff0c\u653b\u5b88\u4ff1\u5999\u3002\u7384\u7075\u5b50\u4e0d\u8fd8\u51fb\u4e5f\u8fd8\u7f62\u4e86\uff0c\u4e00\u5251\u63d2\u8fdb\uff0c\u53cc\u5251\u5ffd\u5730\u4e00 \u5408\u4e00\u7ede\u53ea\u542c\u5f97\u4e00\u7247\u65ad\u91d1\u621b\u7389\u4e4b\u58f0\uff0c\u7384\u7075\u5b50\u7684\u957f\u5251\u7ed9\u4ea4\u53c9\u622a\u4e3a\u56db\u7247\uff0c\u4e0d\u662f\u7f29\u624b\u5f97\u5feb\uff0c\u624b\u6307\u4e5f\u51e0 \u4e4e\u5168\u88ab\u524a\u6389\u3002\u94c1\u81c2\u91d1\u733f\u5927\u5403\u4e00\u60ca\uff0c\u602a\u5175\u5203\u6025\u5f80\u5916\u5c01\uff0c\u53ea\u542c\u5f97\u5580\u5693\u4e00\u58f0\uff0c\u53cc\u5251\u9f50\u4e0b\uff0c\u62d0\u6756\u5c16\u7aef \u7684\u94c1\u638c\u4ea6\u88ab\u524a\u4e86\uff0c\u94c1\u81c2\u91d1\u733f\u8fd9\u62db\u7528\u5f97\u592a\u6025\uff0c\u94c1\u638c\u88ab\u524a\uff0c\u9661\u7136\u4e00\u9707\uff0c\u8eab\u8eaf\u9669\u9669\u6251\u5012\u3002\u5f20\u4e39\u67ab\u54c8 \u54c8\u5927\u7b11\uff0c\u9053\uff1a\u201c\u771f\u662f\u4e2a\u4e0d\u77e5\u8fdb\u9000\u7684\u8001\u7334\u513f\uff01\u201d\u98de\u8d77\u4e00\u811a\uff0c\u6b63\u6b63\u8e22\u5728\u654c\u4eba\u7684\u819d\u76d6\u9aa8\u4e0a\uff0c\u94c1\u81c2\u91d1 \u733f\u5b9a\u4e0d\u7740\u8eab\u5f62\uff0c\u4e00\u4e2a\u7ffb\u8eab\uff0c\u8dcc\u51fa\u4e94\u516d\u6b65\u5916\uff0c\u201c\u5495\u549a\u201d\u4e00\u58f0\uff0c\u53cc\u811a\u671d\u5929\uff0c\u5927\u817f\u7adf\u7ed9\u81ea\u5df1\u7684\u602a\u5175 \u5668\u78b0\u7740\u88ab\u62d0\u6756\u4e0a\u7684\u5c16\u523a\u6233\u4f24\u5341\u51e0\u5904\u4f24\u53e3\u3002 \u3000\u3000\u94c1\u81c2\u91d1\u733f\u4e0e\u4e09\u82b1\u5251\u5728\u6c5f\u6e56\u4e0a\u662f\u4f55\u7b49\u5a01\u540d\uff0c\u4e0d\u6599\u4e0d\u8fc7\u5341\u62db\u5de6\u53f3\uff0c\u5c31\u88ab\u4e24\u4e2a\u5c11\u5e74\u6740\u5f97\u5927\u8d25\uff0c \u5175\u5203\u88ab\u524a\u4eba\u4ea6\u53d7\u4f24\uff0c\u72fc\u72c8\u5341\u5206\uff0c\u989c\u9762\u65e0\u5149\u3002\u4e0d\u5f85\u5f20\u3001\u4e91\u8ffd\u6765\uff0c\u7acb\u523b\u7ffb\u8eab\u4fbf\u8d70\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u4ef0\u5929\u5927\u7b11\uff0c\u6325\u624b\u53eb\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u5feb\u5feb\u8ffd\u554a\uff0c\u6349\u8fd9\u4e24\u4e2a\u8001\u7334\u513f\uff01\u201d\u94c1\u81c2\u91d1\u733f\u4e0e\u4e09 \u82b1\u5251\u5413\u5f97\u9b42\u4e0d\u9644\u4f53\uff0c\u8dd1\u5f97\u66f4\u75be\uff0c\u5176\u5b9e\u5f20\u4e39\u67ab\u4e0d\u8fc7\u662f\u5413\u5413\u4ed6\u4eec\uff0c\u82e5\u7136\u771f\u4e2a\u8ffd\u8d76\uff0c\u4ed6\u4eec\u5c31\u662f\u6ca1\u6709 \u53d7\u4f24\uff0c\u4e5f\u5b9a\u5fc5\u88ab\u5f20\u4e39\u67ab\u8d76\u4e0a\u3002 \u3000\u3000\u5f20\u98ce\u5e9c\u6545\u610f\u5927\u547c\u5c0f\u53eb\uff0c\u4f5c\u6325\u5200\u529b\u6218\uff0c\u62b5\u5fa1\u5f3a\u654c\u4e4b\u72b6\uff0c\u5f85\u94c1\u81c2\u91d1\u733f\u4e0e\u4e09\u82b1\u5251\u53bb\u5f97\u8fdc\u4e86\uff0c\u8fd9 \u624d\u5657\u55e4\u4e00\u7b11\u5411\u5f20\u4e39\u67ab\u8c22\u9053\uff1a\u201c\u6211\u4eca\u65e5\u53d7\u4f60\u4e00\u5251\uff0c\u751a\u662f\u503c\u5f97\u3002\u4ed6\u65e5\u81f3\u4eac\uff0c\u8fd8\u8bf7\u5230\u820d\u4e0b\u76f8\u4f1a\u3002\u201d \u5c06\u4eac\u4e2d\u7684\u4f4f\u5740\u8bf4\u4e86\uff0c\u53c8\u9053\uff1a\u201c\u5f20\u5144\uff0c\u4e91\u5144\uff0c\u4f60\u4eec\u53cc\u5251\u5408\u74a7\uff0c\u5929\u4e0b\u65e0\u654c\uff0c\u53ef\u5408\u800c\u4e0d\u53ef\u5206\uff0c\u670b\u53cb \u4e4b\u95f4\uff0c\u7eb5\u6709\u4ec0\u4e48\u610f\u6c14\uff0c\u4e5f\u8be5\u6d88\u9664\u624d\u662f\u3002\u201d\u5f20\u98ce\u5e9c\u54ea\u77e5\u4e8c\u4eba\u4e4b\u95f4\u7684\u6069\u6069\u6028\u6028\uff0c\u53ea\u9053\u662f\u4ed6\u4eec\u95f9\u4e86 \u522b\u626d\u6240\u4ee5\u7279\u52a0\u529d\u89e3\u3002\u4ed6\u867d\u8bf4\u7684\u4e8c\u4eba\uff0c\u5374\u662f\u5355\u72ec\u9762\u5411\u4e91\u857e\uff0c\u4e91\u857e\u9762\u4e0a\u4e00\u7ea2\uff0c\u4f4e\u9996\u4e0d\u8bed\u3002\u5f20\u98ce\u5e9c \u5fc3\u4e2d\u5947\u9053\uff1a\u201c\u8fd9\u4f4d\u4e91\u76f8\u516c\u4ea6\u662f\u4fa0\u4e49\u4e4b\u58eb\uff0c\u4f55\u4ee5\u672a\u8bed\u5148\u81ea\u542b\u7f9e\uff0c\u5012\u50cf\u4e00\u4e2a\u672a\u51fa\u8fc7\u95e8\u7684\u95fa\u5973\uff1f\u201d \u6b63\u60f3\u5a49\u8a00\u518d\u529d\uff0c\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u4f60\u77a7\uff0c\u4ed6\u4eec\u6765\u4e86\uff01\u201d \u3000\u3000\u53ea\u89c1\u4e91\u91cd\u4e0e\u6a0a\u5fe0\u4ece\u5c71\u5773\u8f6c\u4e86\u51fa\u6765\u3002\u539f\u6765\u6a0a\u5fe0\u6628\u665a\u521a\u521a\u5c06\u5468\u5c71\u6c11\u5e26\u51fa\u540e\u95e8\uff0c\u5c31\u51b7\u4e0d\u9632\u88ab\u5f20 \u4e39\u67ab\u4e0e\u9ed1\u767d\u6469\u8bc3\u5236\u670d\uff0c\u5176\u540e\u5f20\u4e39\u67ab\u5f15\u5f00\u5f20\u98ce\u5e9c\uff0c\u9ed1\u767d\u6469\u8bc3\u7528\u8ff7\u9999\u8ff7\u5012\u4e86\u5fa1\u6797\u519b\uff0c\u5728\u9644\u8fd1\u57cb \u4f0f\uff0c\u6070\u6070\u8d76\u4e0a\u65f6\u5019\uff0c\u5f85\u94c1\u81c2\u91d1\u733f\u4e0e\u4e09\u82b1\u5251\u5728\u5ba2\u5e97\u4e2d\u8d70\u51fa\u4e4b\u65f6\uff0c\u4fbf\u5f15\u4ed6\u4eec\u8d76\u5230\u9752\u9f99\u5ce1\u9644\u8fd1\u25a1 \u6740\u3002\u6a0a\u5fe0\u4e5f\u88ab\u4ed6\u4eec\u64d2\u5230\u9752\u9f99\u5ce1\uff0c\u7f1a\u5728\u4e00\u68f5\u5927\u6811\u4e4b\u4e0a\u3002\u9ed1\u767d\u6469\u8bc3\u5728\u9752\u9f99\u5ce1\u8c37\u53e3\u4e0e\u5f3a\u654c\u25a1\u6740\u534a \u591c\uff0c\u4e0d\u5206\u80dc\u8d1f\uff08\u8fd9\u4e5f\u662f\u94c1\u81c2\u91d1\u733f\u4e0e\u4e09\u82b1\u5251\u4e3a\u4f55\u5728\u5341\u62db\u4e4b\u5185\u5c31\u8d25\u7ed9\u5f20\u3001\u4e91\u4e8c\u4eba\u7684\u9053\u7406\uff0c\u4e0d\u7136\u6309 \u4ed6\u4eec\u7684\u529f\u529b\u603b\u53ef\u4ee5\u62b5\u6321\u5230\u4e8c\u5341\u62db\u4ee5\u4e0a\u7684\uff09\u3002\u4e91\u91cd\u4e0e\u4e91\u857e\u5728\u4e09\u5c94\u9053\u53e3\uff0c\u542c\u5230\u5de6\u9762\u9053\u4e0a\u7684\u25a1\u6740 \u58f0\uff0c\u4fbf\u662f\u4ed6\u4eec\u6240\u53d1\u3002\u5f85\u4e91\u91cd\u8d76\u5230\u4e4b\u65f6\uff0c\u5df2\u662f\u5929\u5149\u5927\u767d\uff0c\u53ea\u89c1\u6a0a\u5fe0\u88ab\u7f1a\u5728\u6811\u9876\uff0c\u98d8\u98d8\u8361\u8361\uff0c\u94c1 \u81c2\u91d1\u733f\u3001\u4e09\u82b1\u5251\u4e0e\u9ed1\u767d\u6469\u8bc3\u9ad8\u547c\u9163\u6597\uff0c\u63d2\u4e0d\u8fdb\u624b\u53bb\u3002\u4e91\u91cd\u722c\u4e0a\u6811\u9876\uff0c\u5c06\u6a0a\u5fe0\u89e3\u4e0b\uff0c\u6a0a\u5fe0\u88ab\u7f1a \u5f97\u4e45\u4e86\uff0c\u624b\u811a\u90fd\u5df2\u9ebb\u6728\uff0c\u4e91\u91cd\u66ff\u4ed6\u63a8\u8840\u8fc7\u5bab\uff0c\u624b\u672f\u5c1a\u672a\u505a\u5b8c\uff0c\u94c1\u81c2\u91d1\u733f\u4e0e\u4e09\u82b1\u5251\u53c8\u5df2\u88ab\u9ed1\u767d \u6469\u8bc3\u5f15\u5f00\u3002 \u3000\u3000\u5f85\u6a0a\u5fe0\u5b8c\u5168\u6062\u590d\u4e4b\u540e\uff0c\u518d\u8d76\u6765\u65f6\uff0c\u94c1\u81c2\u91d1\u733f\u4e0e\u4e09\u82b1\u5251\u5df2\u88ab\u5f20\u3001\u4e91\u8054\u5251\u6253\u5f97\u5927\u8d25\u5954\u9003\u3002 \u3000\u3000\u4e91\u91cd\u89c1\u4e86\u5f20\u4e39\u67ab\uff0c\u84e6\u5730\u4e00\u58f0\u6012\u543c\uff0c\u6325\u5200\u75be\u4e0a\uff0c\u773c\u4e2d\u5c31\u50cf\u8981\u55b7\u51fa\u706b\u7130\u4e00\u822c\u3002\u5f20\u98ce\u5e9c\u5fc3\u4e2d\u5947 \u9053\uff1a\u201c\u4f55\u4ee5\u4e91\u7edf\u9886\u5982\u6b64\u6068\u4ed6\uff1f\u201d\u6a0a\u5fe0\u4e5f\u6325\u52a8\u53cc\u9524\u52a9\u6218\uff0c\u5f20\u4e39\u67ab\u8eab\u5f62\u98d8\u98d8\uff0c\u529b\u6218\u4e8c\u4eba\u3002\u4e91\u857e\u5fc3 \u4e2d\u75db\u82e6\u4e4b\u6781\uff0c\u72ec\u501a\u5d16\u8fb9\uff0c\u773c\u775b\u53d1\u76f4\uff0c\u663e\u5f97\u5341\u5206\u60f6\u60d1\uff0c\u4e00\u7247\u832b\u7136\u3002 \u3000\u3000\u5f20\u98ce\u5e9c\u559d\u9053\uff1a\u201c\u4f4f\u624b\uff01\u201d\u6a0a\u5fe0\u5148\u6536\u4e86\u53cc\u9524\uff0c\u4e91\u91cd\u5de6\u5200\u53f3\u638c\u5374\u4ecd\u662f\u8fde\u8fde\u8fdb\u62db\uff0c\u53eb\u9053\uff1a\u201c\u5927 \u54e5\uff01\u6b64\u4eba\u662f\u5978\u8d3c\u5f20\u5b97\u5468\u4e4b\u5b50\uff0c\u4e0d\u80fd\u653e\u8fc7\u4ed6\u3002\u201d\u5f20\u98ce\u5e9c\u5413\u4e86\u4e00\u8df3\uff0c\u6a0a\u5fe0\u53c8\u4e3e\u8d77\u53cc\u9524\uff0c\u5f20\u98ce\u5e9c \u9053\uff1a\u201c\u4e09\u5f1f\u4f11\u5f97\u5984\u52a8\uff0c\u6628\u665a\u8fde\u63a5\u7684\u610f\u5916\u4e4b\u4e8b\uff0c\u5b9e\u662f\u4ed6\u6551\u4e86\u6211\u4eec\u3002\u5f85\u6211\u95ee\u660e\u3002\u201d\u626c\u5200\u559d\u9053\uff1a \u201c\u5f20\u4e39\u67ab\uff0c\u4e91\u7edf\u9886\u6240\u8a00\u662f\u865a\u662f\u5b9e\uff1f\u201d\u5f20\u4e39\u67ab\u4ef0\u5929\u72c2\u7b11\uff0c\u541f\u9053\uff1a\u201c\u582a\u7b11\u4e16\u4eba\u591a\u767d\u773c\uff0c\u83b2\u82b1\u539f\u81ea \u51fa\u6c61\u6ce5\uff01\u4f60\u770b\u6211\u7684\u884c\u4e8b\uff0c\u8fd8\u4e0d\u77e5\u6211\u7684\u4e3a\u4eba\u5417\uff1f\u4f55\u5fc5\u8981\u558b\u558b\u4e0d\u4f11\uff0c\u67e5\u95ee\u6211\u7684\u5bb6\u8c31\uff1f\u201d \u3000\u3000\u5f20\u98ce\u5e9c\u4e00\u6115\uff0c\u5fc3\u9053\uff1a\u201c\u662f\u554a\uff01\u4ed6\u5373\u662f\u5f20\u5b97\u5468\u4e4b\u5b50\uff0c\u53c8\u6709\u4f55\u5e72\uff1f\u201d\u5927\u58f0\u559d\u9053\uff1a\u201c\u4e91\u7edf\u9886\u4f4f \u624b\uff01\u6b64\u4eba\u5bf9\u6211\u4eec\u5b9e\u662f\u4e00\u756a\u597d\u610f\uff0c\u4e0d\u53ef\u4ee5\u6028\u62a5\u5fb7\uff01\u201d\u4e91\u91cd\u547c\u547c\u4e24\u638c\uff0c\u53eb\u9053\uff1a\u201c\u5927\u54e5\u4f60\u6709\u6240\u4e0d \u77e5\uff0c\u6b64\u4eba\u4e43\u662f\u6211\u5bb6\u7684\u5927\u4ec7\u4eba\uff01\u6709\u4ec7\u4e0d\u62a5\uff0c\u5c82\u662f\u4e08\u592b\uff1f\u201d\u5f20\u98ce\u5e9c\u52c3\u7136\u53d1\u4f5c\uff0c\u6012\u9053\uff1a\u201c\u597d\u4e5f\uff0c\u4f60 \u62a5\u4f60\u7684\u4ec7\uff0c\u6211\u4e0d\u7ba1\u4f60\uff01\u201d\u4e91\u91cd\u65bd\u5c55\u5927\u529b\u91d1\u521a\u624b\u6cd5\uff0c\u72e0\u72e0\u6251\u51fb\uff0c\u5ffd\u542c\u5f97\u201c\u5f53\u25a1\u201d\u4e00\u58f0\uff0c\u5de6\u624b\u5355 \u5200\u5df2\u88ab\u5f20\u4e39\u67ab\u7684\u5b9d\u5251\u524a\u65ad\u3002\u4e91\u857e\u4e00\u58f0\u60ca\u547c\uff0c\u98de\u8eab\u4e00\u63a0\uff0c\u9752\u51a5\u5251\u5f53\u4e2d\u4e00\u683c\uff0c\u5c06\u5f20\u4e39\u67ab\u7684\u5b9d\u5251\u683c \u5f00\uff0c\u5f20\u4e39\u67ab\u672c\u5c31\u65e0\u610f\u523a\u4f24\u4e91\u91cd\uff0c\u8d81\u52bf\u6536\u62db\uff0c\u8df3\u51fa\u5708\u5b50\u3002\u5f20\u98ce\u5e9c\u89c1\u4e91\u857e\u8dc3\u51fa\uff0c\u8d77\u5148\u4ee5\u4e3a\u4ed6\u4eec\u662f \u8054\u5251\u5bf9\u4ed8\u4e91\u91cd\uff0c\u4e0d\u7531\u5f97\u5927\u5403\u4e00\u60ca\uff0c\u6025\u4e5f\u8fde\u5fd9\u8dc3\u51fa\uff0c\u9661\u89c1\u4e91\u857e\u6a2a\u5251\u683c\u5f00\uff0c\u5148\u662f\u4e00\u6014\u968f\u5373\u7b11\u9053\uff1a \u201c\u597d\u597d\uff0c\u51a4\u5bb6\u5b9c\u89e3\u4e0d\u5b9c\u7ed3\uff0c\u4f60\u683c\u5f97\u597d\uff01\u201d\u4e00\u628a\u62d6\u4e86\u4e91\u91cd\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u5df2\u89c1\u8fc7\u771f\u7ae0\uff0c\u8fd8\u4e0d\u8d70 \u4e48\uff1f\u201d\u4e91\u91cd\u72e0\u72e0\u5730\u76ef\u4e86\u5f20\u4e39\u67ab\u4e00\u773c\uff0c\u5fc3\u4e2d\u6697\u6068\u81ea\u5df1\u5b66\u827a\u4e0d\u7cbe\uff0c\u5341\u5e74\u82e6\u529f\uff0c\u7adf\u6253\u4e0d\u8fc7\u4ec7\u4eba\u7684\u513f \u5b50\uff0c\u88ab\u5f20\u98ce\u5e9c\u62d6\u5f00\uff0c\u4e5f\u53ea\u597d\u968f\u4ed6\u800c\u53bb\u3002 \u3000\u3000\u4e91\u857e\u4e00\u5251\u683c\u5f00\uff0c\u5ffd\u5730\u201c\u54c7\u201d\u7684\u4e00\u58f0\u54ed\u4e86\u51fa\u6765\uff0c\u8dcc\u5012\u5730\u4e0a\uff0c\u4e91\u91cd\u5df2\u8f6c\u51fa\u5c71\u5773\uff0c\u56de\u5934\u671b\u5979\u4e00 \u773c\uff0c\u5fc3\u4e2d\u751a\u662f\u7591\u60d1\u3002\u5f20\u98ce\u5e9c\u6015\u4ed6\u518d\u56de\u53bb\u7ea0\u7f20\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u7ba1\u522b\u4eba\u7684\u95f2\u4e8b\u505a\u4ec0\u4e48\uff1f\u201d\u62d6\u7740\u4e91 \u91cd\uff0c\u8d70\u51fa\u5c71\u8c37\u3002 \u3000\u3000\u4e91\u857e\u62ac\u8d77\u5934\u65f6\uff0c\u5df2\u770b\u4e0d\u89c1\u4e91\u91cd\u7684\u80cc\u5f71\uff0c\u4e0d\u7531\u5f97\u54c0\u54c0\u75db\u54ed\uff0c\u4f4e\u5524\u201c\u54e5\u54e5\uff01\u201d\u5ffd\u89c9\u5f20\u4e39\u67ab\u8f7b \u629a\u5979\u7684\u79c0\u53d1\uff0c\u5728\u8033\u8fb9\u67d4\u58f0\u8bf4\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u54ed\u5427\uff0c\u54ed\u5427\uff01\u54ed\u4e2a\u75db\u5feb\uff0c\u4f60\u5c31\u8212\u670d\u5566\uff01\u201d\u4ed6\u8fd9\u4e48 \u4e00\u8bf4\uff0c\u4e91\u857e\u53cd\u800c\u4e0d\u54ed\u4e86\uff0c\u7ffb\u8eab\u5750\u8d77\uff0c\u63a8\u5f00\u5f20\u4e39\u67ab\u7684\u624b\u8bf4\u9053\uff1a\u201c\u6211\u54ed\u6211\u7684\uff0c\u8c01\u8981\u4f60\u7ba1\uff01\u201d \u3000\u3000\u5f20\u4e39\u67ab\u7b11\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u4f60\u8fd9\u662f\u4f55\u82e6\u6765\uff1f\u4e16\u95f4\u591a\u5c11\u4e8b\u4ee4\u4eba\u4f24\u5fc3\uff0c\u4f60\u54ea\u6709\u8fd9\u8bb8\u591a\u773c\u6cea\uff1f\u201d \u4e91\u857e\u88ab\u4ed6\u52fe\u8d77\u5fc3\u4e8b\uff0c\u6cea\u53c8\u6ef4\u4e0b\u3002\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u5176\u5b9e\u4eba\u751f\u6700\u591a\u4e5f\u4e0d\u8fc7\u767e\u5e74\uff0c\u591a\u5c11\u5927\u4e8b\u60c5\u8fd8\u505a\u4e0d \u5b8c\u5462\uff0c\u4e2a\u4eba\u6069\u6028\u53c8\u4f55\u5fc5\u5982\u6b64\u770b\u91cd\uff1f\u201d \u3000\u3000\u4e91\u857e\u4e00\u8dc3\u800c\u8d77\uff0c\u6012\u9053\uff1a\u201c\u4f60\u5012\u8bf4\u5f97\u98ce\u51c9\uff01\u201d\u5f20\u4e39\u67ab\u89c1\u5979\u5df2\u80af\u5f00\u53e3\u8bf4\u8bdd\uff0c\u5fc3\u4e2d\u5927\u6170\uff0c\u53c8 \u9053\uff1a\u201c\u6211\u7239\u53eb\u4f60\u7237\u7237\u7267\u9a6c\u4e8c\u5341\u5e74\uff0c\u8fd9\u786e\u5b9e\u662f\u5bf9\u4f60\u4eec\u4e0d\u8d77\uff0c\u53ef\u4e5f\u65e0\u6cd5\u633d\u56de\u3002\u4f60\u7237\u7237\u4e4b\u6b7b\uff0c\u5374\u4e0e \u6211\u65e0\u6d89\uff0c\u6211\u518d\u4e09\u8bf4\u53ca\uff0c\u4f60\u90fd\u4e0d\u4fe1\u6211\u4e48\uff1f\u201d\u4e91\u857e\u60f3\u8d77\u8fd9\u7f8a\u76ae\u8840\u4e66\uff0c\u4e43\u662f\u7237\u7237\u5728\u7267\u9a6c\u4e4b\u65f6\u4fbf\u5df2\u5199 \u4e86\uff0c\u53ef\u89c1\u7237\u7237\u7eb5\u662f\u4e0d\u88ab\u5978\u4eba\u5bb3\u6b7b\uff0c\u4e5f\u8981\u81ea\u5df1\u62a5\u4ec7\uff0c\u66f4\u662f\u4f24\u5fc3\u6cea\u4e0b\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f60\u54e5\u54e5\u7684\u5927\u529b\u91d1\u521a\u624b\u6cd5\uff0c\u529f\u529b\u975e\u51e1\uff0c\u6211\u542c\u5e08\u7236\u8bf4\u8fc7\uff0c\u5f53\u4eca\u5929\u4e0b \u64c5\u957f\u5927\u529b\u91d1\u521a\u624b\u7684\uff0c\u53ea\u662f\u6709\u9650\u51e0\u4eba\uff0c\u5c24\u4ee5\u8463\u5e08\u4f2f\u6700\u9ad8\uff0c\u770b\u6765\u4f60\u54e5\u54e5\u4e43\u662f\u8463\u5e08\u4f2f\u7684\u9ad8\u8db3\u3002\u201d\u8bf4 \u5b8c\u4e4b\u540e\uff0c\u53c8\u957f\u957f\u53f9\u4e86\u53e3\u6c14\u3002\u4e91\u857e\u5fcd\u4e0d\u4f4f\u8bf4\u9053\uff1a\u201c\u6211\u54e5\u54e5\u7684\u827a\u529f\u6b63\u662f\u8463\u5e08\u4f2f\u6240\u6388\uff0c\u8fd9\u4e5f\u60f9\u4e86\u4f60 \u4eec\uff1f\u4f60\u5509\u58f0\u53f9\u6c14\uff0c\u5374\u662f\u4e3a\u4f55\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u60f3\u6211\u4eec\u4e09\u4eba\uff0c\u90fd\u662f\u540c\u95e8\u624b\u8db3\uff0c\u539f\u5e94\u4eb2\u82e5\u4e00\u5bb6\u3002 \u800c\u4eca\u5374\u88ab\u6b7b\u53bb\u4e86\u7684\u4eba\uff0c\u9694\u5f00\u4e86\u6211\u4eec\u6d3b\u7740\u7684\u4eba\uff0c\u4ee4\u6211\u4eec\u5f7c\u6b64\u76f8\u4ec7\uff0c\u5927\u5bb6\u90fd\u4e0d\u5feb\u6d3b\uff0c\u8fd9\u5c82\u4e0d\u53ef \u54c0\uff01\u201d\u4e91\u857e\u5982\u53d7\u4e00\u68d2\uff0c\u6025\u6025\u907f\u5f00\u5f20\u4e39\u67ab\u6295\u63b7\u8fc7\u6765\u7684\u76ee\u5149\uff0c\u5fc3\u4e2d\u601d\u6f6e\u8d77\u4f0f\uff0c\u9ed8\u7136\u4e0d\u8bed\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u53c8\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u4f60\u65e2\u4e0d\u80af\u76f8\u8c05\uff0c\u90a3\u4e48\u54b1\u4eec\u8fd8\u662f\u5206\u624b\u4e86\u5427\uff0c\u514d\u5f97\u5f7c\u6b64\u4f24\u5fc3\u3002\u201d\u4e91 \u857e\u5ffd\u9053\uff1a\u201c\u4e14\u6162\u3002\u201d\u5f20\u4e39\u67ab\u56de\u5934\u8bf4\u9053\uff1a\u201c\u55ef\uff0c\u4f60\u672c\u662f\u51b0\u96ea\u806a\u660e\uff0c\u800c\u4eca\u53ef\u60f3\u5f97\u901a\u900f\u4e86\uff1f\u201d\u4e91\u857e \u53c8\u907f\u5f00\u5f20\u4e39\u67ab\u7684\u76ee\u5149\uff0c\u9053\uff1a\u201c\u4f60\u6211\u4e4b\u95f4\uff0c\u5df2\u662f\u65e0\u8bdd\u53ef\u8bf4\u3002\u5468\u5927\u54e5\u5462\uff0c\u4f60\u5c06\u4ed6\u52ab\u5230\u54ea\u91cc\u53bb\u4e86\uff1f \u6bd5\u8001\u524d\u8f88\u5462\uff0c\u4f60\u53ef\u89c1\u7740\u4ed6\u4e48\uff1f\u201d\u5f20\u4e39\u67ab\u5fc3\u4e2d\u6697\u7b11\uff0c\u8bf4\u662f\u201c\u65e0\u8bdd\u53ef\u8bf4\u201d\uff0c\u504f\u8fd8\u6709\u90a3\u4e48\u591a\u8bdd\uff0c\u7b11 \u9053\uff1a\u201c\u5c71\u6c11\u5927\u54e5\u5bf9\u6211\u654c\u610f\u751a\u6df1\uff0c\u6211\u5df2\u5c06\u4ed6\u51fb\u5012\u4e86\u3002\u201d\u4e91\u857e\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u201d\u5f20\u4e39\u67ab\u7b11\u9053\uff1a\u201c\u4ed6 \u88ab\u6a0a\u5fe0\u5e26\u51fa\u540e\u95e8\u4e4b\u65f6\uff0c\u94c1\u81c2\u91d1\u733f\u4e0e\u4e09\u82b1\u5251\u5df2\u5c06\u6765\u5230\uff0c\u6211\u6015\u4ed6\u4eec\u649e\u7740\uff0c\u4e8b\u60c5\u5c31\u8981\u5f04\u7cdf\u3002\u662f\u4ee5\u529d \u6bd5\u8001\u524d\u8f88\u4e0e\u4ed6\u901f\u901f\u4e58\u6211\u7684\u767d\u9a6c\u79bb\u5f00\uff0c\u4ed6\u4e0d\u80af\u542c\uff0c\u6211\u53ea\u6709\u5c06\u4ed6\u7684\u7a74\u9053\u5c01\u95ed\uff0c\u7531\u9ed1\u767d\u6469\u8bc3\u5148\u53bb\u963b \u94c1\u81c2\u91d1\u733f\u4e0e\u4e09\u82b1\u5251\u4e00\u7a0b\uff0c\u4e09\u4eba\u540c\u4e58\u767d\u9a6c\u4e0d\u987b\u4e00\u523b\uff0c\u4fbf\u5c06\u4ed6\u9001\u5230\u84dd\u5bb6\u3002\u6211\u7684\u70b9\u7a74\u624b\u6cd5\uff0c\u6709\u8f7b\u6709 \u91cd\uff0c\u8f7b\u8005\u8fc7\u4e86\u4e00\u4e2a\u65f6\u8fb0\u53ef\u4ee5\u81ea\u89e3\uff0c\u800c\u4eca\u4ed6\u5927\u7ea6\u5df2\u5728\u84dd\u5bb6\u559d\u538b\u60ca\u9152\u5566\u3002\u201d\u4e91\u857e\u53c8\u662f\u4f69\u670d\uff0c\u53c8\u662f \u60ca\u5947\uff0c\u5374\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u4f60\u4e00\u665a\u4e4b\u95f4\uff0c\u7adf\u505a\u4e86\u90a3\u4e48\u591a\u4e8b\u3002\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u6211\u7684\u767d\u9a6c\u65e5\u884c\u5343\u91cc\uff0c \u8fd9\u7b97\u5f97\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u8bdd\u8bf4\u5b8c\u4e86\uff0c\u4e91\u857e\u53c8\u662f\u9eef\u7136\u4e0d\u8bed\uff0c\u518d\u5ea6\u907f\u5f00\u5f20\u4e39\u67ab\u6295\u8fc7\u6765\u7684\u76ee\u5149\u3002\u8fd9\u65f6\u65ed\u65e5\u4e1c\u5347\uff0c\u5df2\u5728\u9752 \u9f99\u5ce1\u4e0a\u7a7a\uff0c\u5e03\u6210\u4e86\u7f24\u7eb7\u593a\u76ee\u7684\u7ef5\u5e55\uff0c\u6625\u8272\u5c06\u6b8b\uff0c\u6742\u82b1\u751f\u6811\uff0c\u68a8\u82b1\u5982\u96ea\uff0c\u6653\u65e5\u91d1\u5149\uff0c\u6620\u51fa\u5c71\u5bb9 \u82b1\u8272\uff0c\u7f8e\u4e3d\u6e05\u5e7d\u3002\u5f20\u4e39\u67ab\u5ffd\u7136\u6478\u51fa\u4e86\u4e00\u5c01\u4fe1\uff0c\u9053\uff1a\u201c\u70e6\u4f60\u4ea4\u7ed9\u7fe0\u51e4\u59d1\u5a18\u3002\u201d\u4e91\u857e\u5e76\u4e0d\u56de\u5934\uff0c \u53cd\u624b\u63a5\u4fe1\uff0c\u5979\u660e\u77e5\u4e0e\u5f20\u4e39\u67ab\u4e0d\u514d\u4e00\u522b\uff0c\u662f\u4ee5\u5f3a\u81ea\u538b\u5236\uff0c\u514d\u5f97\u591a\u77a7\u4e00\u773c\uff0c\u591a\u589e\u4e00\u5206\u4f24\u5fc3\u3002\u5f20\u4e39 \u67ab\u53f9\u4e86\u53e3\u6c14\uff0c\u9a91\u4e0a\u767d\u9a6c\uff0c\u7f13\u7f13\u8d70\u51fa\u5c71\u8c37\uff0c\u9a6c\u8e44\u8e0f\u7740\u96f6\u843d\u7684\u82b1\u74e3\uff0c\u653e\u58f0\u6b4c\u9053\uff1a\u201c\u6768\u67f3\u4e1d\u4e1d\u5f04\u8f7b \u67d4\uff0c\u70df\u7f15\u7ec7\u6210\u6101\u3002\u6d77\u68e0\u672a\u96e8\uff0c\u68a8\u82b1\u5148\u96ea\uff0c\u4e00\u534a\u6625\u4f11\u3002\u800c\u4eca\u5f80\u4e8b\u96be\u91cd\u7701\uff0c\u5f52\u68a6\u7ed5\u79e6\u697c\u3002\u76f8\u601d\u53ea \u5728\uff0c\u4e01\u9999\u679d\u4e0a\uff0c\u8c46\u853b\u68a2\u5934\u3002\u201d\u8fd9\u662f\u5b8b\u4eba\u738b\u6ec2\u6000\u5ff5\u6539\u5ac1\u4e86\u7684\u59bb\u5b50\u7684\u4e00\u9996\u5c0f\u8bcd\uff0c\u800c\u4eca\u7531\u5f20\u4e39\u67ab\u5531 \u51fa\uff0c\u5374\u522b\u6709\u4f24\u5fc3\u4e4b\u5904\u3002\u4e91\u857e\u542c\u5f97\u5982\u9189\u5982\u75f4\uff0c\u5fc3\u9053\uff1a\u201c\u6211\u867d\u7136\u6068\u4f60\uff0c\u4f46\u6211\u8fd9\u4e00\u4e16\u7edd\u4e0d\u53e6\u5ac1\u4ed6 \u4eba\u3002\u54ce\u5440\uff0c\u8001\u5929\u7237\u5bf9\u6211\u4f55\u5176\u6b8b\u9177\uff01\u201d \u3000\u3000\u6b4c\u58f0\u56de\u65cb\uff0c\u82b1\u74e3\u96f6\u843d\uff0c\u5f20\u4e39\u67ab\u7684\u5f71\u5b50\u53c8\u4e0d\u89c1\u4e86\u3002\u4e91\u857e\u51dd\u7740\u6cea\u73e0\uff0c\u6c90\u7740\u9633\u5149\uff0c\u8ddf\u7740\u4e5f\u8d70\u51fa \u4e86\u5c71\u8c37\u3002 \u3000\u3000\u6b63\u5348\u65f6\u5206\uff0c\u4e91\u857e\u56de\u5230\u996e\u9a6c\u5ddd\u5be8\u4e3b\u84dd\u5929\u77f3\u7684\u8001\u5bb6\uff0c\u5468\u5c71\u6c11\u679c\u7136\u559d\u8fc7\u4e86\u4f17\u4eba\u7ed9\u4ed6\u6446\u7684\u538b\u60ca \u9152\uff0c\u6b63\u5728\u4e0e\u7fa4\u8c6a\u8c08\u8bba\u3002\u6bd5\u9053\u51e1\u4e00\u89c1\u4e91\u857e\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6628\u665a\u6211\u4e22\u4e0b\u4f60\u4e00\u4eba\u5148\u8d70\uff0c\u672c\u662f\u6302\u5fc3\uff0c \u53ef\u662f\u4e00\u60f3\u5230\u6709\u5f20\u4e39\u67ab\u6697\u4e2d\u7167\u5e94\uff0c\u6211\u5c31\u65e0\u987e\u8651\u5566\u3002\u201d\u8a00\u4e0b\u4e4b\u610f\uff0c\u5bf9\u5f20\u4e39\u67ab\u7adf\u662f\u5341\u5206\u4f69\u670d\u3002\u84dd\u5929 \u77f3\u4e5f\u9053\uff1a\u201c\u54b1\u4eec\u8d39\u5c3d\u5fc3\u601d\u6551\u4e0d\u4e86\u4eba\uff0c\u5f20\u4e39\u67ab\u4e00\u6765\uff0c\u4e8b\u60c5\u4fbf\u8f7b\u8f7b\u6613\u6613\u5730\u529e\u59a5\u4e86\u3002\u6b64\u4eba\u884c\u4e8b\uff0c\u771f \u662f\u795e\u5947\u83ab\u6d4b\u3002\u201d\u5bf9\u5f20\u4e39\u67ab\u654c\u610f\u751a\u6df1\u7684\u90dd\u5b9d\u693f\u4e5f\u9053\uff1a\u201c\u770b\u6765\u6b64\u4eba\u4e5f\u662f\u4e2a\u70ed\u8840\u6c49\u5b50\uff0c\u54b1\u4eec\u4ee5\u524d\u53ef \u9519\u602a\u4ed6\u4e86\u3002\u201d\u6b63\u662f\u53e3\u6709\u6240\u9053\uff0c\u7686\u662f\u9053\u53ca\u5f20\u4e39\u67ab\u3002\u5468\u5c71\u6c11\u770b\u4e86\u4e00\u773c\u4e91\u857e\u9053\uff1a\u201c\u53ef\u60dc\u4ed6\u662f\u4e91\u76f8\u516c \u7684\u4ec7\u4eba\uff0c\u8981\u4e0d\u54b1\u4eec\u771f\u8be5\u597d\u597d\u4e0e\u4ed6\u7ed3\u7eb3\u3002\u201d\u4e91\u857e\u9762\u6655\u7ea2\u6f6e\uff0c\u9ed8\u7136\u4e0d\u8bed\u3002\u77f3\u7fe0\u51e4\u9053\uff1a\u201c\u4e91\u76f8\u516c\uff0c \u6551\u51fa\u5c71\u6c11\u5927\u54e5\uff0c\u4f60\u4e5f\u6709\u529f\uff0c\u4f60\u600e\u4e48\u4e0d\u8bf4\u8bdd\u5440\uff1f\u201d \u3000\u3000\u4e91\u857e\u9053\uff1a\u201c\u6211\u6709\u4ec0\u4e48\u529f\u52b3\uff0c\u6211\u4e0d\u8fc7\u662f\u68cb\u76d8\u4e0a\u4efb\u7531\u6446\u5e03\u7684\u4e00\u53ea\u5c0f\u5352\u7f62\u4e86\u3002\u201d\u77f3\u7fe0\u51e4\u597d\u751f\u4e0d \u60a6\uff0c\u9053\uff1a\u201c\u8c01\u4eba\u80fd\u6446\u5e03\u4f60\uff1f\u201d\u4e91\u857e\u5176\u5b9e\u662f\u5fc3\u6709\u6240\u601d\uff0c\u51b2\u53e3\u800c\u51fa\uff0c\u88ab\u5979\u4e00\u95ee\uff0c\u4e0d\u89c9\u54d1\u7136\u5931\u7b11\uff0c \u5374\u53c8\u9eef\u7136\u8bf4\u9053\uff1a\u201c\u6211\u662f\u8bf4\u6211\u662f\u7531\u547d\u8fd0\u6240\u6446\u5e03\uff0c\u4e0d\u80fd\u81ea\u4e3b\u3002\u201d\u4f17\u4eba\u76f8\u987e\u6115\u7136\uff0c\u4e0d\u77e5\u5979\u4f55\u4ee5\u6ca1\u5934 \u6ca1\u5c3e\uff0c\u7a81\u7136\u8bf4\u51fa\u8fd9\u6837\u7684\u8bdd\u3002\u5468\u5c71\u6c11\u5ffd\u9053\uff1a\u201c\u771f\u662f\u7684\uff0c\u4f60\u4e0e\u5f20\u4e39\u67ab\u7ed3\u4e0b\u5bbf\u4e16\u4e4b\u4ec7\uff0c\u5c82\u4e0d\u6b63\u662f\u547d \u8fd0\u7684\u6446\u5e03\uff1f\u201d\u8981\u77e5\u5468\u5c71\u6c11\u867d\u662f\u5bf9\u5f20\u4e39\u67ab\u6e10\u6709\u597d\u611f\uff0c\u4f46\u4e00\u60f3\u8d77\u4e91\u857e\u5bf9\u5f20\u4e39\u67ab\u6240\u85cf\u7684\u6df1\u6c89\u60c5\u611f\uff0c \u4fbf\u4e0d\u89c9\u9eef\u7136\u81ea\u4f24\u3002 \u3000\u3000\u77f3\u7fe0\u51e4\u9053\uff1a\u201c\u4f60\u4eec\u600e\u4e48\u50cf\u548c\u5c1a\u8c08\u7985\u4f3c\u7684\u8bf4\u4e2a\u4e0d\u4f11\u3002\u4e91\u76f8\u516c\u4f60\u662f\u4e0d\u662f\u8fd8\u8fdb\u4eac\uff1f\u201d\u6b63\u60f3\u8bf4\u8981 \u8ddf\u201c\u4ed6\u201d\u540c\u53bb\uff0c\u4e91\u857e\u5ffd\u9053\uff1a\u201c\u55ef\u6211\u51e0\u4e4e\u5fd8\u8bb0\u4e86\uff0c\u6709\u4e00\u5c01\u4fe1\u8981\u4ea4\u7ed9\u4f60\u3002\u201d\u77f3\u7fe0\u51e4\u9053\uff1a\u201c\u5f20\u4e39\u67ab \u4f55\u4ee5\u6709\u4fe1\u7ed9\u6211\uff1f\u8fd9\u5012\u5947\u4e86\u3002\u201d\u53c8\u9053\uff1a\u201c\u4f60\u4e0e\u4ed6\u65e2\u662f\u6709\u4ec7\uff0c\u5374\u53c8\u5982\u540c\u597d\u53cb\u4e00\u822c\uff0c\u8fd9\u4e5f\u771f\u5947\uff01\u201d \u8fb9\u8bf4\u8fb9\u62c6\u5f00\u4fe1\uff0c\u53eb\u9053\uff1a\u201c\u539f\u6765\u662f\u6211\u7239\u7239\u7684\u4fe1\u3002\u54a6\uff0c\u6709\u4ec0\u4e48\u6025\u4e8b\u8981\u6211\u56de\u53bb\uff1f\u4e91\u76f8\u516c\uff0c\u8fd9\u4fe1\u5c01\u91cc \u8fd8\u5957\u6709\u53e6\u5916\u4e00\u5c01\u4fe1\u662f\u4ea4\u4e0e\u4f60\u7684\uff0c\u4e0d\uff0c\u662f\u6258\u4f60\u8f6c\u4ea4\u7ed9\u9601\u8001\u4e8e\u8c26\u7684\uff0c\u5440\u8fd9\u53ef\u4e0d\u662f\u4ed6\u7684\u5b57\u8ff9\u5440\uff01\u201d \u518d\u770b\u4e0b\u53bb\u9053\uff1a\u201c\u539f\u6765\u4ea4\u7ed9\u4f60\u90a3\u5c01\u4fe1\u53c8\u662f\u53e6\u4e00\u4e2a\u4eba\u5199\u7684\uff0c\u600e\u4e48\u8981\u8fd9\u6837\u8f97\u8f6c\u76f8\u6258\u5462\uff1f\u201d\u4e91\u857e\u63a5\u8fc7 \u90a3\u5c01\u4fe1\u4e00\u770b\uff0c\u4fe1\u5c01\u4e0a\u90a3\u51e0\u4e2a\u5b57\u5199\u5f97\u9f99\u98de\u51e4\u821e\uff0c\u6258\u4e91\u857e\u8f6c\u5448\u9601\u8001\u4e8e\u8c26\u3002\u4e91\u857e\u7684\u5fc3\u535c\u535c\u5730\u8df3\uff0c\u8fd9 \u5b57\u8ff9\u7adf\u7136\u662f\u5f20\u4e39\u67ab\u7684\uff01\u662f\u5f20\u4e39\u67ab\u6015\u81ea\u5df1\u4e0d\u80af\u63a5\u53d7\u8fd9\u4efd\u4eba\u60c5\uff0c\u8fd8\u662f\u5176\u4e2d\u53e6\u6709\u6df1\u610f\uff1f \u3000\u3000\u77f3\u7fe0\u51e4\u770b\u5b8c\u4e86\u4fe1\u597d\u751f\u5931\u671b\uff0c\u8bf4\u9053\uff1a\u201c\u7238\u7238\u6709\u4e8b\u8981\u6211\u56de\u53bb\uff0c\u4f60\u53c8\u8981\u8fdb\u4eac\uff0c\u54b1\u4eec\u4e0d\u77e5\u4f55\u65f6\u518d \u89c1\uff1f\u201d\u4e91\u857e\u6b63\u559c\u6446\u8131\u4e86\u77f3\u7fe0\u51e4\u7684\u7ea0\u7f20\uff0c\u7b11\u9053\uff1a\u201c\u6709\u7f18\u81ea\u80fd\u76f8\u89c1\u3002\u201d\u4f17\u4eba\u90fd\u5f53\u4f5c\u662f\u8fd9\u5bf9\u5c0f\u592b\u59bb \u6253\u60c5\u9a82\u4fcf\uff0c\u4e0d\u89c9\u54c4\u7136\u5927\u7b11\uff0c\u628a\u77f3\u7fe0\u51e4\u5f04\u5f97\u7c89\u9762\u901a\u7ea2\u3002 \u3000\u3000\u7b2c\u4e8c\u65e5\uff0c\u7fa4\u96c4\u5404\u81ea\u5206\u6563\u4e1c\u897f\uff0c\u6bd5\u9053\u51e1\u5230\u534e\u5c71\u907f\u7978\uff0c\u5468\u5c71\u6c11\u4e5f\u4e0d\u6562\u5728\u5173\u5185\u4e45\u7559\uff0c\u51c6\u5907\u4ecd\u56de \u5c71\u5be8\u3002\u4e91\u857e\u5355\u8eab\u5339\u9a6c\u72ec\u81ea\u5165\u4eac\uff0c\u77f3\u7fe0\u51e4\u4e0e\u5468\u5c71\u6c11\u9001\u5979\u4e00\u7a0b\uff0c\u4f9d\u4f9d\u4e0d\u820d\u3002\u5c06\u5206\u624b\u65f6\uff0c\u4e91\u857e\u5ffd \u9053\uff1a\u201c\u51e4\u59d0\uff0c\u4f60\u5148\u56de\u53bb\uff0c\u6211\u4e0e\u5468\u5927\u54e5\u6709\u51e0\u53e5\u8bdd\u8bf4\u3002\u201d\u77f3\u7fe0\u51e4\u773c\u5708\u4e00\u7ea2\uff0c\u82e5\u662f\u5f80\u65e5\uff0c\u5b9a\u7136\u751f \u6c14\uff0c\u53c8\u8981\u9a82\u4e91\u857e\u5fc3\u4e2d\u53ea\u6709\u4e49\u5144\uff0c\u6ca1\u6709\u5979\u4e86\u3002\u53ea\u56e0\u5468\u5c71\u6c11\u66fe\u820d\u547d\u6551\u8fc7\u5979\uff0c\u813e\u6c14\u53d1\u4f5c\u4e0d\u51fa\uff0c\u53ea\u597d \u54bd\u4e0b\u95f7\u6c14\u72ec\u81ea\u56de\u53bb\u3002 \u3000\u3000\u5468\u5c71\u6c11\u9053\uff1a\u201c\u6211\u4ee5\u524d\u628a\u5f20\u4e39\u67ab\u5f53\u4f5c\u5978\u8d3c\uff0c\u5982\u4eca\u770b\u6765\uff0c\u4ed6\u5012\u662f\u4e2a\u6d4a\u4e16\u7684\u5947\u7537\u5b50\u3002\u4f60\u5230\u4eac\u4e2d \u63a2\u4e2a\u660e\u767d\u3002\u82e5\u7136\u4f60\u7684\u7237\u7237\u4e0d\u662f\u4ed6\u5bb6\u5bb3\u7684\uff0c\u7267\u9a6c\u4e8c\u5341\u5e74\u4e4b\u4ec7\uff0c\u4f3c\u4e5f\u4e0d\u5fc5\u6740\u4ed6\u4e00\u5bb6\u62a5\u590d\u3002\u201d\u5468\u5c71 \u6c11\u6628\u665a\u60f3\u4e86\u4e00\u591c\uff0c\u60f3\u8d77\u5404\u6709\u7f18\u5206\u5404\u4eba\u60c5\u6709\u6240\u949f\uff0c\u4e0d\u89c9\u5fc3\u7070\u610f\u51b7\uff0c\u4ed6\u672c\u662f\u4fa0\u4e49\u4e4b\u4eba\uff0c\u4f24\u5fc3\u4e4b \u540e\uff0c\u80f8\u895f\u53cd\u89c9\u6bd4\u4ee5\u524d\u5f00\u9614\uff0c\u662f\u4ee5\u8bf4\u51fa\u4e86\u8fd9\u756a\u8bdd\u3002\u4e91\u857e\u542c\u4e86\u5927\u4e3a\u611f\u52a8\uff0c\u8bf4\u9053\uff1a\u201c\u6b64\u4e8b\u540e\u8c08\u3002\u6211 \u6709\u4e00\u4ef6\u4e1c\u897f\u8981\u9001\u7ed9\u4f60\uff0c\u4e0d\uff0c\u8fd9\u672c\u6765\u5c31\u662f\u4f60\u7684\u4e1c\u897f\u3002\u201d\u8bf4\u7f62\u53d6\u51fa\u4e00\u679d\u73ca\u745a\uff0c\u9012\u8fc7\u53bb\u9053\uff1a\u201c\u73b0\u5728 \u8fd9\u73ca\u745a\u4e5f\u8be5\u7269\u5f52\u539f\u4e3b\u5566\uff01\u201d\u5468\u5c71\u6c11\u89c1\u4e86\u9762\u8272\u4e00\u53d8\uff0c\u9053\uff1a\u201c\u4f60\u3001\u4f60\u8fd9\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d\u6b63\u662f\uff1a \u3000\u3000\u63a5\u6728\u79fb\u82b1\u65bd\u5999\u624b\uff0c\u59fb\u7f18\u6709\u5b9a\u83ab\u5f3a\u6c42\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u840d\u8e2a\u4fa0\u5f71\u5f55\u300b\u2014\u2014\u7b2c\u5341\u56db\u56de\u3000\u7f57\u6c49\u7ef5\u62f3\u5c06\u519b\u906d\u9669\u7740\u3000\u91d1\u521a\u5927\u529b\u602a\u5ba2\u901e\u5947\u80fd \u6881\u7fbd\u751f\u300a\u840d\u8e2a\u4fa0\u5f71\u5f55\u300b \u7b2c\u5341\u56db\u56de\u3000\u7f57\u6c49\u7ef5\u62f3\u5c06\u519b\u906d\u9669\u7740\u3000\u91d1\u521a\u5927\u529b\u602a\u5ba2\u901e\u5947\u80fd \u3000\u3000\u8fd9\u73ca\u745a\u4e43\u662f\u4e91\u857e\u9001\u4e0e\u77f3\u7fe0\u51e4\u7684\u8058\u793c\uff0c\u5468\u5c71\u6c11\u5982\u4f55\u6562\u63a5\uff1f\u4e91\u857e\u683c\u683c\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u672c\u6765 \u662f\u4f60\u5bb6\u7684\u4e1c\u897f\u561b\uff0c\u6211\u4e0d\u8fc7\u501f\u6765\u4e00\u7528\u7f62\u4e86\uff0c\u73b0\u5728\u7269\u5f52\u539f\u4e3b\uff0c\u5c82\u4e0d\u5e94\u5f53\uff1f\u201d\u5468\u5c71\u6c11\u5fae\u6120\u8bf4\u9053\uff1a \u201c\u4e91\u59b9\uff0c\u54b1\u4eec\u5206\u624b\u5728\u5373\uff0c\u4f60\u4f55\u82e6\u4e0e\u611a\u5144\u5f00\u8fd9\u4e2a\u73a9\u7b11\uff1f\u201d\u4e91\u857e\u9762\u8272\u4e00\u7aef\uff0c\u5ffd\u7136\u5e84\u5bb9\u8bf4\u9053\uff1a\u201c\u5927 \u54e5\uff0c\u6211\u6709\u4e00\u4e8b\u6c42\uff0c\u4f60\u80af\u662f\u4e0d\u80af\uff1f\u201d\u5468\u5c71\u6c11\u9053\uff1a\u201c\u4f60\u6211\u60c5\u903e\u5144\u59b9\uff0c\u82e5\u611a\u5144\u529b\u6240\u80fd\u53ca\uff0c\u8d74\u6c64\u8e48 \u706b\uff0c\u4ea6\u6240\u4e0d\u8f9e\u3002\u201d\u4e91\u857e\u7b11\u9053\uff1a\u201c\u6b64\u4e8b\u4e0d\u8d39\u5439\u7070\u4e4b\u529b\u3002\u201d \u3000\u3000\u5468\u5c71\u6c11\u4e0d\u662f\u7b28\u4eba\uff0c\u89c1\u6b64\u795e\u60c5\uff0c\u5df2\u7136\u9192\u609f\uff0c\u5fc3\u4e2d\u53c8\u662f\u607c\u6012\uff0c\u60f3\u9053\uff1a\u201c\u4f60\u53e6\u6709\u610f\u4e2d\u4e4b\u4eba\uff0c\u8fd9 \u4e5f\u7f62\u4e86\uff0c\u5374\u4f55\u5fc5\u884c\u8fd9\u79fb\u82b1\u63a5\u6728\u4e4b\u8ba1\uff1f\u4f60\u5c82\u4e0d\u77e5\uff0c\u66fe\u7ecf\u6ca7\u6d77\u96be\u4e3a\u6c34\uff0c\u9664\u5374\u5deb\u5c71\u4e0d\u662f\u4e91\u5417\uff1f\u201d\u6b63 \u60f3\u53d1\u8bdd\uff0c\u53ea\u542c\u5f97\u4e91\u857e\u8bf4\u9053\uff1a\u201c\u90a3\u77f3\u59d1\u5a18\u5bf9\u6211\u4e00\u7247\u75f4\u60c5\u5b9e\u662f\u53ef\u601c\u3002\u6211\u5c82\u80fd\u957f\u6b64\u76f8\u7792\uff0c\u8bef\u4e86\u5979\u7684 \u9752\u6625\u5e74\u5c11\uff1f\u201d\u5468\u5c71\u6c11\u6012\u9053\uff1a\u201c\u6b64\u4e8b\u4e0e\u6211\u4f55\u5e72\uff1f\u201d\u4e91\u857e\u773c\u5708\u4e00\u7ea2\uff0c\u9053\uff1a\u201c\u6211\u65e0\u7236\u65e0\u6bcd\uff0c\u6709\u4e86\u4e3a \u96be\u4e4b\u4e8b\uff0c\u4e0d\u6c42\u4f60\u8fd8\u6c42\u8c01\u5462\uff1f\u6211\u8fd9\u4ef6\u9ebb\u70e6\u4e8b\u53ea\u6709\u4f60\u53ef\u4ee5\u4ee3\u4e3a\u89e3\u51b3\u3002\u53d4\u7956\u548c\u8f70\u5929\u96f7\u77f3\u82f1\u53c8\u662f\u76f8 \u8bc6\uff0c\u6700\u9002\u5f53\u4e0d\u8fc7\u5566\uff01\u201d\u5468\u5c71\u6c11\u9053\uff1a\u201c\u4ec0\u4e48\uff0c\u4f60\u8fd9\u4e0d\u662f\u5f3a\u4eba\u6240\u96be\u5417\uff1f\u201d\u4e91\u857e\u9053\uff1a\u201c\u4f60\u77e5\u9053\u6211\u6c42 \u4f60\u4ec0\u4e48\uff1f\u6211\u53c8\u4e0d\u662f\u8981\u4f60\u9a6c\u4e0a\u6210\u4eb2\uff0c\u4f60\u6025\u4ec0\u4e48\uff1f\u6211\u53ea\u6c42\u4f60\u6536\u56de\u8fd9\u679d\u73ca\u745a\uff0c\u5230\u6709\u4e86\u9002\u5f53\u7684\u65f6\u673a\uff0c \u4ee3\u6211\u5411\u77f3\u59d1\u5a18\u8a00\u660e\u771f\u76f8\uff0c\u8fd9\u4e5f\u4e0d\u80af\u4e48\uff1f\u201d\u5468\u5c71\u6c11\u89c1\u5979\u8bf4\u5f97\u53ef\u601c\uff0c\u800c\u6240\u6c42\u7684\u4e8b\u60c5\u53c8\u5e76\u4e0d\u6096\u4e4e\u5e38 \u60c5\uff0c\u65e0\u53ef\u63a8\u6258\uff0c\u53ea\u597d\u6536\u4e86\u3002\u4e91\u857e\u6101\u7709\u4e00\u5c55\uff0c\u542b\u7b11\u9053\u8c22\uff0c\u8de8\u9a6c\u4fbf\u884c\u3002\u5468\u5c71\u6c11\u6014\u6014\u5730\u76ee\u9001\u5979\u7684\u80cc \u5f71\uff0c\u601d\u6f6e\u8d77\u4f0f\uff0c\u5fc3\u5934\u6709\u4e00\u80a1\u8bf4\u4e0d\u51fa\u7684\u5473\u513f\uff0c\u60d8\u60d8\u7136\u4e5f\u4e0d\u77e5\u662f\u9178\u662f\u82e6\uff0c\u662f\u7231\u662f\u60b2\uff01 \u3000\u3000\u4e91\u857e\u4e00\u8def\u65e0\u4e8b\uff0c\u6570\u65e5\u4e4b\u540e\u5230\u4e86\u4eac\u5e08\u3002\u5317\u4eac\u81ea\u91d1\u4ee3\u4e2d\u53f6\uff08\u516c\u5143\u4e00\u4e00\u4e94\u4e09\u5e74\uff09\u5efa\u4e3a\u4e2d\u90fd\uff0c\u5df2 \u5177\u4eac\u57ce\u89c4\u6a21\uff0c\u5230\u660e\u6210\u7956\u81ea\u5357\u4eac\u8fc1\u90fd\u81f3\u6b64\uff0c\u6089\u610f\u7ecf\u8425\uff0c\u5efa\u6210\u4e86\u4e16\u4e0a\u65e0\u53cc\u7684\u540d\u90fd\u3002\u4e91\u857e\u8fdb\u5f97\u57ce \u6765\uff0c\u4f46\u89c1\u7d2b\u7981\u57ce\u5185\u6bbf\u5b87\u8fde\u4e91\uff0c\u9cde\u6b21\u6809\u6bd4\uff0c\u5e02\u5185\u8857\u9053\u5bbd\u5e7f\uff0c\u767e\u8086\u6742\u9648\u8bf4\u4e0d\u5c3d\u4e00\u6d3e\u7e41\u534e\u6c14\u8c61\u3002\u4e91 \u857e\u5148\u89c5\u4e86\u4e00\u95f4\u5ba2\u5e97\u4f4f\u4e0b\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u6211\u5728\u4eac\u57ce\u6ca1\u6709\u4e00\u4e2a\u719f\u4eba\uff0c\u90a3\u4e8e\u8c26\u662f\u4e00\u54c1\u5927\u81e3\uff0c\u600e\u77e5\u4ed6 \u80af\u4e0d\u80af\u89c1\u6211\uff1f\u800c\u4e14\u6211\u4e5f\u4e0d\u77e5\u4ed6\u7684\u4f4f\u6240\u3002\u201d\u53c8\u60f3\u9053\uff1a\u201c\u6211\u65e2\u77e5\u90a3\u5c11\u5e74\u519b\u5b98\u4fbf\u662f\u6211\u7684\u54e5\u54e5\uff0c\u800c\u4ed6 \u523b\u4e0b\u53c8\u5728\u4eac\u90fd\uff0c\u6211\u5e94\u5148\u627e\u5230\u54e5\u54e5\u624d\u662f\u6b63\u7406\u3002\u201d\u84e6\u7136\u95f4\u5979\u8111\u6d77\u4e2d\u53c8\u73b0\u51fa\u54e5\u54e5\u90a3\u526f\u5bf9\u5f20\u4e39\u67ab\u4ec7\u6068 \u7684\u773c\u5149\uff0c\u4e0d\u89c9\u53f9\u4e86\u53e3\u6c14\u5fc3\u9053\uff1a\u201c\u5f53\u65e5\u5306\u5306\u5fd9\u5fd9\uff0c\u65e0\u6cd5\u5bf9\u54e5\u54e5\u8bf4\u5f97\u660e\u767d\u3002\u8fd9\u4e16\u4e0a\u5230\u5e95\u53ea\u6709\u4ed6\u662f \u6211\u7684\u4eb2\u4eba\uff0c\u6211\u4fbf\u62fc\u7740\u53d7\u4ed6\u8d23\u9a82\uff0c\u90fd\u628a\u5fc3\u4e8b\u8bf4\u4e0e\u4ed6\u542c\u597d\u5566\uff01\u53ef\u662f\u82e5\u54e5\u54e5\u8981\u6211\u4e00\u540c\u62a5\u4ec7\uff0c\u90a3\u53c8\u5982 \u4f55\uff1f\u5f20\u4e39\u67ab\u51e0\u6b21\u6551\u4e86\u6211\u7684\u6027\u547d\uff0c\u6211\u53c8\u5c82\u80fd\u4f24\u5bb3\u4e8e\u4ed6\uff1f\u5440\uff0c\u4e5f\u53ea\u6709\u89c1\u4e00\u6b65\u884c\u4e00\u6b65\u5566\uff01\u201d\u5979\u77e5\u9053 \u4e86\u54e5\u54e5\u7684\u4e0b\u843d\u7684\u559c\u60a6\uff0c\u4e0e\u5bf9\u201c\u590d\u4ec7\u201d\u7684\u62c5\u5fe7\u6df7\u5728\u4e00\u5904\uff0c\u60b2\u559c\u4ea4\u7ec7\uff0c\u6709\u5982\u6625\u8695\u4f5c\u8327\uff0c\u65e0\u6cd5\u81ea \u89e3\u3002\u53ef\u662f\u54e5\u54e5\u603b\u662f\u8981\u8ba4\u7684\u554a\uff01\u5230\u54ea\u91cc\u53bb\u627e\u54e5\u54e5\u5462\uff1f\u8fd9\u5012\u4e0d\u662f\u96be\u4e8b\uff0c\u5979\u81ea\u7136\u800c\u7136\u5730\u60f3\u8d77\u4e86\u5f20\u98ce \u5e9c\u6765\u3002 \u3000\u3000\u5f20\u98ce\u5e9c\u4ee5\u524d\u66fe\u5bf9\u5979\u8bf4\u8fc7\uff0c\u8bf4\u82e5\u7136\u5979\u4e0e\u5f20\u4e39\u67ab\u6709\u673a\u4f1a\u5230\u5317\u4eac\u7684\u8bdd\uff0c\u5b9a\u8981\u8bf7\u4ed6\u4eec\u5230\u4ed6\u5bb6\u4e2d\u4f5c \u5ba2\uff0c\u66fe\u7559\u6709\u5730\u5740\u7ed9\u5979\u3002\u4e91\u857e\u5728\u5ba2\u5e97\u4e2d\u4f4f\u4e86\u4e09\u65e5\uff0c\u6e10\u6e10\u6478\u719f\u4e86\u5317\u4eac\u7684\u9053\u8def\uff0c\u7b2c\u56db\u65e5\u4fbf\u6309\u5740\u6765\u5230 \u4e86\u5f20\u5bb6\u3002 \u3000\u3000\u5f20\u5bb6\u867d\u8fd8\u7b97\u4e0d\u4e0a\u662f\u5bcc\u8d35\u4eba\u5bb6\uff0c\u4f4f\u5b85\u4ea6\u9887\u5bbd\u5e7f\uff0c\u4ece\u5916\u9762\u770b\u53bb\u53ea\u89c1\u4e00\u9053\u56f4\u5899\uff0c\u5899\u5185\u6811\u6728\u6276 \u758f\uff0c\u91cc\u9762\u53ea\u6709\u56db\u4e94\u95f4\u5e73\u623f\uff0c\u4e91\u857e\u4e0d\u89c9\u7eb3\u7f55\uff1a\u600e\u4e48\u7559\u4e86\u8fd9\u4e48\u591a\u7a7a\u5730\uff1f\u7ee7\u800c\u4e00\u60f3\u5fc3\u9053\uff1a\u201c\u662f\u4e86\uff0c \u90a3\u5f20\u98ce\u5e9c\u4e43\u662f\u9526\u8863\u536b\u7684\u6307\u6325\uff0c\u5bb6\u4e2d\u81ea\u7136\u5c11\u4e0d\u4e86\u5bbd\u5e7f\u7684\u7ec3\u6b66\u573a\u6240\u3002\u201d \u3000\u3000\u4e91\u857e\u6263\u95e8\u6c42\u89c1\uff0c\u90a3\u7ba1\u95e8\u7684\u5c06\u4e91\u857e\u4ed4\u7ec6\u6253\u91cf\uff0c\u597d\u4e00\u4f1a\u5b50\uff0c\u6162\u541e\u541e\u5730\u9053\uff1a\u201c\u5c0f\u54e5\uff0c\u5bf9\u4e0d\u4f4f \u4e86\uff0c\u6211\u5bb6\u5927\u4eba\u4eca\u65e5\u4e0d\u89c1\u5916\u5ba2\u3002\u201d\u4e91\u857e\u6c14\u9053\uff1a\u201c\u4f60\u600e\u77e5\u4ed6\u4e0d\u80af\u89c1\u6211\uff1f\u201d\u90a3\u7ba1\u95e8\u7684\u9053\uff1a\u201c\u5f20\u5927\u4eba \u65e9\u6709\u5429\u5490\uff0c\u8fd9\u51e0\u65e5\u9664\u4e86\u5fa1\u6797\u519b\u548c\u9526\u8863\u536b\u7684\u540c\u50da\u4e4b\u5916\uff0c\u4f59\u4eba\u4e00\u6982\u4e0d\u89c1\u7684\u3002\u201d\u4e91\u857e\u9053\uff1a\u201c\u6211\u662f\u4f60 \u5bb6\u5927\u4eba\u9080\u8bf7\u6765\u7684\uff0c\u600e\u4e48\u4e0d\u89c1\uff1f\u201d\u90a3\u7ba1\u5bb6\u7684\u53c8\u6253\u91cf\u4e86\u4e91\u857e\u4e00\u773c\uff0c\u6447\u6447\u5934\u9053\uff1a\u201c\u6211\u4e0d\u76f8\u4fe1\uff01\u201d\u795e \u6c14\u4e4b\u4e2d\u663e\u6709\u8f7b\u89c6\u4e4b\u5fc3\uff0c\u597d\u50cf\u662f\u8bf4\uff1a\u201c\u4f60\u8fd9\u4e2a\u5c0f\u54e5\u513f\u6709\u4ec0\u4e48\u6765\u5934\uff0c\u6211\u5bb6\u5927\u4eba\u4f1a\u9080\u8bf7\u4f60\uff1f\u201d\u4e91\u857e \u4e00\u6c14\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u7ed9\u6211\u901a\u62a5\uff0c\u6211\u5c31\u81ea\u5df1\u8fdb\u53bb\u4e86\uff01\u201d\u624b\u63e1\u94c1\u679d\u680f\u6805\uff0c\u7528\u529b\u4e00\u6447\uff0c\u6307\u5934\u7c97\u7684\u94c1\u679d \u7adf\u7136\u5411\u5185\u5f2f\u66f2\u3002\u8fd9\u4e00\u624b\u5927\u51fa\u90a3\u7ba1\u5bb6\u7684\u610f\u6599\u4e4b\u5916\uff0c\u6539\u5bb9\u8bf4\u9053\uff1a\u201c\u5c0f\u54e5\u513f\u4e0d\u5fc5\u52a8\u86ee\uff0c\u6211\u7ed9\u4f60\u901a\u62a5 \u4fbf\u662f\uff0c\u89c1\u4e0e\u4e0d\u89c1\uff0c\u90a3\u53ef\u5f97\u770b\u5f20\u5927\u4eba\u4e86\u3002\u201d \u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\uff0c\u90a3\u7ba1\u95e8\u7684\u72ec\u81ea\u51fa\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u4e91\u76f8\u516c\uff0c\u6211\u5bb6\u5927\u4eba\u8bf7\u4f60\u8fdb\u53bb\u3002\u4f60\u4ece\u53f3\u8fb9\u7684\u77f3\u8def \u76f4\u8d70\uff0c\u518d\u5411\u5de6\u62d0\u4e00\u4e2a\u5f2f\uff0c\u6709\u4e00\u9053\u865a\u63a9\u7740\u7684\u77f3\u95e8\uff0c\u4f60\u63a8\u95e8\u8fdb\u53bb\uff0c\u6211\u5bb6\u5927\u4eba\u5728\u573a\u5b50\u91cc\u8fb9\u3002\u6211\u8fd8\u8981 \u5728\u6b64\u770b\u95e8\uff0c\u6055\u4e0d\u5e26\u5f15\u4f60\u4e86\u3002\u201d\u8fb9\u8bf4\u8fb9\u6253\u5f00\u680f\u6805\uff0c\u8ba9\u4e91\u857e\u8fdb\u5185\u3002\u4e91\u857e\u4f59\u6012\u672a\u606f\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u5f20 \u98ce\u5e9c\u597d\u5927\u7684\u67b6\u5b50\uff0c\u5728\u9752\u9f99\u5ce1\u4e4b\u65f6\uff0c\u8bf4\u5f97\u4f3c\u4e4e\u751a\u591f\u670b\u53cb\uff0c\u4eca\u65e5\u6211\u767b\u95e8\u6c42\u89c1\uff0c\u4ed6\u7adf\u7136\u4e0d\u6765\u63a5\u6211\u3002 \u54fc\uff0c\u5230\u5e95\u662f\u4e00\u4e2a\u5b98\u513f\u3002\u201d \u3000\u3000\u4e91\u857e\u6c14\u6124\u6124\u5730\u8d70\u5230\u4e86\u573a\u5b50\u5916\u8fb9\uff0c\u5fc3\u4e2d\u6b63\u5728\u601d\u91cf\u5982\u4f55\u5bf9\u5f20\u98ce\u5e9c\u8bf4\u8bdd\uff0c\u5ffd\u542c\u5f97\u5185\u9762\u4e00\u9635\u523a\u8033 \u7684\u7b11\u58f0\uff1a\u201c\u563b\u563b\uff0c\u54c8\u54c8\uff0c\u54fc\uff0c\u5c0f\u5fc3\u4e86\uff01\u201d\u8fd9\u7b11\u58f0\u7adf\u7136\u662f\u6fb9\u53f0\u706d\u660e\u7684\u7b11\u58f0\u3002\u4e91\u857e\u5403\u4e86\u4e00\u60ca\uff0c\u63a8 \u5f00\u77f3\u95e8\uff0c\u53ea\u89c1\u573a\u5b50\u5468\u56f4\u6324\u6ee1\u4e86\u5fa1\u6797\u519b\u7684\u519b\u5b98\u548c\u9526\u8863\u536b\u7684\u6b66\u58eb\uff0c\u5f20\u98ce\u5e9c\u7ad9\u5728\u524d\u5217\uff0c\u89c1\u4e91\u857e\u8fdb \u6765\uff0c\u9065\u9065\u70b9\u9996\u793a\u610f\uff0c\u573a\u5b50\u91cc\u6fb9\u53f0\u706d\u660e\u6b63\u4e0e\u4e00\u4e2a\u6b66\u58eb\u6bd4\u8bd5\uff0c\u53cc\u638c\u76f8\u62b5\uff0c\u5ffd\u7136\u5927\u7b11\u4e24\u58f0\uff0c\u5de6\u811a\u95ea \u7535\u4e00\u52fe\u90a3\u540d\u6b66\u58eb\u6251\u901a\u5012\u5730\u3002 \u3000\u3000\u6fb9\u53f0\u706d\u660e\u7b11\u9053\uff1a\u201c\u518d\u6765\uff0c\u518d\u6765\uff01\u201d\u53c8\u4e00\u540d\u6b66\u58eb\u8df3\u4e0a\u524d\u6765\uff1a\u201c\u6211\u4e5f\u9886\u6559\u9886\u6559\u6fb9\u53f0\u5c06\u519b\u7684\u7edd \u6280\uff01\u201d\u6fb9\u53f0\u706d\u660e\u7b11\u9053\uff1a\u201c\u597d\u6781\uff0c\u597d\u6781\uff01\u201d\u90a3\u6b66\u58eb\u4e00\u633a\u8170\u5750\u9a6c\uff0c\u201c\u84ec\u201d\u7684\u4e00\u62f3\u76f4\u6363\u51fa\u53bb\uff0c\u4f7f\u7684 \u662f\u5341\u516b\u8def\u957f\u62f3\u7684\u529f\u592b\uff0c\u770b\u4ed6\u62f3\u52bf\u5982\u98ce\uff0c\u9887\u89c1\u529f\u529b\uff0c\u53cc\u8db3\u9489\u7262\u5730\u9762\u72b9\u5982\u6253\u6869\u4e00\u822c\uff0c\u4e0b\u76d8\u529f\u592b\u66f4 \u89c1\u6c89\u7a33\u3002\u6fb9\u53f0\u706d\u660e\u63a8\u4e86\u4ed6\u4e24\u62f3\uff0c\u53ea\u63a8\u5f97\u4ed6\u4e0a\u8eab\u6447\u6643\uff0c\u7adf\u672a\u8dcc\u5012\u3002 \u3000\u3000\u4e91\u857e\u5927\u4e3a\u5947\u602a\uff0c\u6fb9\u53f0\u706d\u660e\u4e43\u662f\u62a4\u9001\u74e6\u523a\u7684\u756a\u738b\uff0c\u600e\u4e48\u5374\u5728\u5f20\u98ce\u5e9c\u7684\u5bb6\u4e2d\u4e0e\u4e2d\u56fd\u6b66\u58eb\u6bd4\u8d77 \u6b66\u6765\uff1f\u5f20\u98ce\u5e9c\u805a\u7cbe\u4f1a\u795e\u5730\u89c2\u770b\uff0c\u4e91\u857e\u4e0d\u4fbf\u627e\u4ed6\u8c08\u8bdd\uff0c\u53ea\u5f97\u6742\u5728\u4eba\u5806\u4e4b\u4e2d\uff0c\u542c\u4f17\u6b66\u58eb\u53fd\u53fd\u55b3\u55b3 \u7684\u8c08\u8bba\u3002 \u3000\u3000\u4e91\u857e\u542c\u4f17\u4eba\u8c08\u8bba\uff0c\u624d\u77e5\u9053\u662f\u600e\u4e48\u4e00\u56de\u4e8b\uff1a\u539f\u6765\u6fb9\u53f0\u706d\u660e\u5230\u4eac\u591a\u65e5\uff0c\u4e0e\u4f17\u6b66\u58eb\u9887\u6709\u5f80\u6765\uff0c \u81ea\u7136\u514d\u4e0d\u4e86\u8c08\u8bba\u6b66\u529f\u5404\u5938\u6280\u827a\u3002\u6fb9\u53f0\u706d\u660e\u4e45\u6709\u74e6\u523a\u7b2c\u4e00\u6b66\u58eb\u4e4b\u79f0\uff0c\u6709\u4e9b\u4eba\u4fbf\u60f3\u89c1\u8bc6\u89c1\u8bc6\u4ed6\u7684 \u6b66\u529f\uff0c\u6fb9\u53f0\u706d\u660e\u4eba\u9887\u723d\u5feb\uff0c\u517c\u4e4b\u4ed6\u4e5f\u60f3\u89c1\u8bc6\u89c1\u8bc6\u4e2d\u539f\u6b66\u58eb\u6b66\u529f\uff0c\u4fbf\u8bf7\u5f20\u98ce\u5e9c\u4ee3\u9080\u4eac\u4e2d\u597d\u624b\uff0c \u5f7c\u6b64\u201c\u5370\u8bc1\u201d\uff08\u5373\u6bd4\u8bd5\u4e4b\u610f\uff09\u3002\u672c\u6765\u6b66\u6797\u4e4b\u58eb\uff0c\u5f7c\u6b64\u5370\u8bc1\u6b66\u529f\uff0c\u4e8b\u60c5\u6781\u662f\u5bfb\u5e38\uff0c\u53ef\u662f\u56e0\u4e3a\u6fb9 \u53f0\u706d\u660e\u4e43\u662f\u74e6\u523a\u56fd\u7684\u7b2c\u4e00\u52c7\u58eb\uff0c\u8fd9\u4fbf\u6697\u542b\u4e86\u201c\u4e24\u56fd\u4e4b\u4e89\u201d\u7684\u6210\u4efd\u5728\u5185\uff0c\u6b66\u58eb\u4e4b\u4e2d\u6709\u7231\u56fd\u5fc3 \u7684\uff0c\u65e0\u4e0d\u4e89\u7740\u51fa\u6765\uff0c\u4ee5\u51fb\u5012\u6fb9\u53f0\u706d\u660e\u4e3a\u8363\uff0c\u56e0\u6b64\u6c14\u6c1b\u5f04\u5f97\u751a\u4e3a\u7d27\u5f20\uff0c\u5b9e\u975e\u6fb9\u53f0\u706d\u660e\u59cb\u6599\u6240 \u53ca\u3002 \u3000\u3000\u6bd4\u8bd5\u5df2\u8fdb\u884c\u4e86\u4e09\u65e5\uff0c\u6fb9\u53f0\u706d\u660e\u8fde\u8d25\u4eac\u4e2d\u516b\u540d\u9ad8\u624b\uff0c\u7adf\u662f\u6240\u5411\u65e0\u654c\u3002\u4eca\u65e5\u4e43\u662f\u6700\u540e\u4e00\u65e5\uff0c \u82e5\u7136\u4ecd\u662f\u65e0\u4eba\u80fd\u591f\u62b5\u654c\uff0c\u4e2d\u56fd\u6b66\u58eb\u7684\u9762\u5b50\uff0c\u53ef\u5c31\u8981\u4e22\u5149\u4e86\uff0c\u6240\u4ee5\u5927\u5bb6\u5fc3\u60c5\uff0c\u66f4\u662f\u7d27\u5f20\u6c89\u91cd\u3002 \u3000\u3000\u573a\u4e2d\u4e0e\u6fb9\u53f0\u706d\u660e\u6bd4\u8bd5\u7684\u8fd9\u4f4d\u6b66\u58eb\uff0c\u4e43\u662f\u5fa1\u6797\u519b\u7684\u526f\u7edf\u9886\uff0c\u540d\u53eb\u6768\u5a01\uff0c\u6709\u4e00\u8eab\u6a2a\u7ec3\u7684\u94c1\u5e03 \u886b\u529f\u592b\uff0c\u81ea\u4fe1\u53ef\u4ee5\u6371\u5f97\u4f4f\u6fb9\u53f0\u706d\u660e\u7684\u638c\u529b\uff0c\u8fd9\u65f6\u5df2\u62c6\u4e86\u5341\u4f59\u4e8c\u5341\u62db\u3002\u6768\u5a01\u7528\u7684\u662f\u5341\u516b\u8def\u957f\u62f3 \u7684\u529f\u592b\uff0c\u786c\u62f3\u786c\u9a6c\uff0c\u62f3\u62f3\u631f\u98ce\uff0c\u5a01\u52bf\u4ea6\u9887\u60ca\u4eba\uff0c\u6fb9\u53f0\u706d\u660e\u7528\u7684\u662f\u4e00\u5957\u5e73\u5e73\u5e38\u5e38\u7684\u201c\u94c1\u7435\u7436\u201d \u638c\u6cd5\uff0c\u8f7b\u63cf\u6de1\u5199\u5730\u5c06\u6768\u5a01\u7684\u91cd\u62f3\u4e00\u4e00\u67b6\u5f00\uff0c\u6597\u5230\u4e86\u7ea6\u4e09\u5341\u6765\u62db\uff0c\u53ea\u89c1\u6768\u5a01\u6c57\u5982\u96e8\u4e0b\uff0c\u62f3\u6cd5\u6e10 \u4e71\u3002\u6fb9\u53f0\u706d\u660e\u4e00\u7b11\u9053\uff1a\u201c\u6768\u7edf\u9886\uff0c\u4f60\u4e5f\u6b47\u6b47\u5427\uff01\u201d\u8eab\u8eaf\u970d\u5730\u4e00\u7ffb\uff0c\u62cd\u62cd\u62cd\u8fde\u73af\u4e09\u62f3\uff0c\u628a\u6768\u5a01 \u53cc\u62f3\u5206\u5f00\uff0c\u500f\u5730\u6b3a\u8eab\u4e00\u649e\uff0c\u5c06\u6768\u5a01\u649e\u5f97\u8dcc\u5012\u5c18\u57c3\u3002\u6fb9\u53f0\u706d\u660e\u9053\u58f0\uff1a\u201c\u5f97\u7f6a\u201d\uff0c\u5c06\u6768\u5a01\u6276\u4e86\u8d77 \u6765\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u662f\u7b2c\u5341\u573a\u4e86\uff0c\u8fd8\u6709\u54ea\u4f4d\u8d50\u6559\u4e48\uff1f\u201d \u3000\u3000\u5f20\u98ce\u5e9c\u518d\u4e5f\u5fcd\u53d7\u4e0d\u4f4f\uff0c\u8dc3\u51fa\u573a\u5fc3\uff0c\u62b1\u62f3\u8bf4\u9053\uff1a\u201c\u6211\u6765\u9886\u6559\u9886\u6559\u6fb9\u53f0\u5c06\u519b\u7684\u9ad8\u62db\uff01\u201d\u6fb9\u53f0 \u706d\u660e\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4e45\u95fb\u5f20\u5927\u4eba\u662f\u4eac\u4e2d\u7b2c\u4e00\u9ad8\u624b\uff0c\u8fd9\u56de\u5e78\u9022\u5bf9\u624b\uff0c\u771f\u662f\u5927\u5feb\u751f\u5e73\u3002\u201d\u8a00\u8bed\u4e4b \u4e2d\uff0c\u867d\u662f\u5bf9\u5f20\u98ce\u5e9c\u63a8\u5d07\uff0c\u5176\u5b9e\u751a\u4e3a\u81ea\u8d1f\uff0c\u8fd9\u4e00\u6218\u4e43\u662f\u4e24\u4e2a\u201c\u7b2c\u4e00\u201d\u4e4b\u4e89\uff0c\u82e5\u7136\u5f20\u98ce\u5e9c\u8f93\u4e86\uff0c \u5176\u4ed6\u7684\u4eba\u4e5f\u4e0d\u7528\u518d\u6bd4\u8bd5\u4e86\u3002 \u3000\u3000\u5f20\u98ce\u5e9c\u9053\u58f0\u201c\u9886\u6559\u201d\uff0c\u4e0e\u6fb9\u53f0\u706d\u660e\u5bf9\u9762\u7acb\u5b9a\uff0c\u5de6\u62f3\u53f3\u638c\uff0c\u62f3\u62b5\u638c\u5fc3\uff0c\u5411\u524d\u4e00\u62f1\uff0c\u8fd9\u4e43\u662f \u540d\u5bb6\u6bd4\u6b66\u7684\u89c1\u9762\u793c\u4eea\uff0c\u5176\u5b9e\u5185\u4e2d\u5374\u662f\u6697\u85cf\u52b2\u529b\uff0c\u4ee5\u9038\u4ee3\u52b3\u3002\u6fb9\u53f0\u706d\u660e\u81ea\u662f\u8bc6\u8d27\u4e4b\u4eba\uff0c\u5fae\u5fae\u4e00 \u7b11\u53cc\u638c\u4e00\u5408\uff0c\u8fd8\u4e86\u4e00\u793c\uff0c\u624b\u672a\u5206\u5f00\uff0c\u5c31\u662f\u4e00\u62db\u201c\u767d\u733f\u63a2\u8def\u201d\uff0c\u7167\u7740\u5f20\u98ce\u5e9c\u7684\u5929\u7075\u76d6\u5288\u4e0b\u3002\u5f20 \u98ce\u5e9c\u62f3\u638c\u4e00\u5206\uff0c\u659c\u8eab\u4e0a\u6b65\uff0c\u53f3\u638c\u6a2a\u6321\uff0c\u5de6\u638c\u4e00\u6325\uff0c\u970e\u65f6\u4e4b\u95f4\uff0c\u8fd8\u4e86\u4e24\u5982\uff0c\u6fb9\u53f0\u706d\u660e\u865a\u865a\u5b9e \u5b9e\uff0c\u90a3\u4e00\u638c\u5c06\u5288\u672a\u5288\uff0c\u84e6\u7136\u624b\u6307\u4e00\u5212\uff0c\u52bf\u6377\u5982\u7535\uff0c\u4e00\u4e2a\u53d8\u62db\uff0c\u53cc\u6307\u5f84\u70b9\u5f20\u98ce\u5e9c\u7684\u8170\u80c1\u8f6f\u9aa8\u3002 \u8fd9\u4e00\u4e0b\u82e5\u7136\u7ed9\u4ed6\u70b9\u4e2d\uff0c\u5f20\u98ce\u5e9c\u7acb\u523b\u8981\u762b\u75ea\u5012\u5730\u3002\u4f46\u5f20\u98ce\u5e9c\u4e5f\u662f\u4e45\u7ecf\u5927\u654c\u4e4b\u4eba\uff0c\u4e00\u89c1\u4e0d\u5999\uff0c\u7acb \u523b\u8d81\u52bf\u524d\u6251\uff0c\u7adf\u4e0d\u6362\u62db\uff0c\u638c\u529b\u76f4\u8feb\u6fb9\u53f0\u706d\u660e\u524d\u5fc3\uff0c\u8fd9\u4e43\u662f\u62fc\u4e2a\u4e24\u8d25\u4ff1\u4f24\u7684\u9669\u7740\uff0c\u6fb9\u53f0\u706d\u660e\u82e5 \u7136\u7ed9\u4ed6\u6253\u4e2d\uff0c\u6700\u5c11\u4e5f\u8981\u5455\u8840\u5f53\u573a\uff01 \u3000\u3000\u6fb9\u53f0\u706d\u660e\u53eb\u9053\uff1a\u201c\u8fd9\u4e00\u62db\u5012\u6253\u91d1\u949f\uff0c\u679c\u662f\u9ad8\u624b\uff01\u201d\u8bdd\u58f0\u672a\u4e86\uff0c\u53ea\u89c1\u4ed6\u8eab\u5f62\u98d8\u52a8\uff0c\u4e0d\u77e5\u600e \u7684\uff0c\u4e00\u4e0b\u5b50\u5c31\u53cd\u8e0f\u4e2d\u5bab\uff0c\u76f4\u62a2\u8fc7\u6765\uff0c\u53cd\u624b\u4e00\u638c\uff0c\u731b\u5207\u5f20\u98ce\u5e9c\u7684\u624b\u8155\uff0c\u4f17\u6b66\u58eb\u4e0d\u89c9\u54d7\u7136\u60ca\u547c\u3002 \u53ea\u542c\u5f97\u62cd\u62cd\u4e24\u58f0\uff0c\u4e24\u4eba\u53cc\u638c\u4e00\u4ea4\uff0c\u5404\u81ea\u659c\u8dc3\u4e09\u6b65\u3002\u7167\u4e00\u822c\u4ea4\u624b\u60c5\u5f62\uff0c\u4e00\u5408\u4e00\u5206\u4e4b\u540e\uff0c\u53cc\u65b9\u591a \u534a\u4f1a\u5404\u7acb\u95e8\u6237\uff0c\u84c4\u52b2\u5f85\u654c\uff0c\u4f17\u4eba\u65b9\u59cb\u677e\u4e86\u4e00\u53e3\u6c14\u3002\u6b63\u5f85\u770b\u4ed6\u4eec\u540e\u7740\u5982\u4f55\u653b\u5b88\uff0c\u5374\u4e0d\u6599\u6fb9\u53f0\u706d \u660e\u8eab\u5b50\u4e00\u503e\uff0c\u5e9e\u5927\u7684\u8eab\u8eaf\u7adf\u4f3c\u4e00\u6839\u6728\u5934\u822c\u5730\u5012\u538b\u4e0b\u6765\uff0c\u53cc\u638c\u547c\u547c\u9f50\u53d1\uff0c\u811a\u8ddf\u5c1a\u672a\u7acb\u7a33\uff0c\u5c45\u7136 \u5c31\u52bf\u62a2\u653b\uff0c\u8eab\u6cd5\u62db\u6570\u4e4b\u602a\uff0c\u5b9e\u662f\u6b66\u6797\u7f55\u89c1\uff01 \u3000\u3000\u8fd9\u4e24\u62f3\u907f\u65e0\u53ef\u907f\u3002\u4f46\u89c1\u5f20\u98ce\u5e9c\u5c0f\u81c2\u5212\u4e86\u534a\u4e2a\u5706\u5f27\uff0c\u53cc\u638c\u7f13\u7f13\u5f80\u5916\u63a8\u51fa\uff0c\u6fb9\u53f0\u706d\u660e\u7684\u6765\u52bf \u6781\u731b\uff0c\u5f20\u98ce\u5e9c\u51fa\u638c\u8212\u7f13\uff0c\u770b\u6765\u5b9e\u4f3c\u65e0\u53ef\u62b5\u5fa1\uff0c\u8fde\u4e91\u857e\u4e5f\u4e0d\u89c9\u89e6\u76ee\u60ca\u5fc3\u3002\u5ffd\u542c\u5f97\u6fb9\u53f0\u706d\u660e\u53eb \u9053\uff1a\u201c\u597d\u4e00\u4e2a\u7ef5\u638c\u529f\u592b\uff01\u201d\u8eab\u8eaf\u4f3c\u5f39\u7c27\u822c\u5ffd\u7136\u5f39\u8d77\uff0c\u633a\u7136\u76f4\u7acb\uff0c\u54c8\u54c8\u4e00\u7b11\uff0c\u53cc\u638c\u4e00\u5206\uff0c\u5c06\u5f20 \u98ce\u5e9c\u7684\u62db\u6570\u5316\u5f00\uff0c\u7728\u773c\u4e4b\u95f4\uff0c\u53c8\u8fdb\u4e86\u4e09\u62db\uff01 \u3000\u3000\u539f\u6765\u5f20\u98ce\u5e9c\u4ea6\u81ea\u77e5\u529f\u529b\u4e0d\u53ca\u6fb9\u53f0\u706d\u660e\uff0c\u4f46\u597d\u5728\u4ed6\u5b66\u7684\u4e43\u662f\u5185\u5bb6\u6b63\u5b97\u7684\u529f\u592b\uff0c\u5728\u201c\u7ef5\u638c\u201d \u4e0a\u6709\u975e\u5e38\u9020\u8be3\uff0c\u7ef5\u638c\u8bb2\u7a76\u7684\u662f\u4ee5\u67d4\u514b\u521a\uff0c\u7ec3\u5230\u6700\u795e\u5999\u7684\u5883\u754c\uff0c\u53ef\u4ee5\u8f7b\u8f7b\u4e00\u638c\uff0c\u51fb\u77f3\u5982\u7c89\u3002\u5f20 \u98ce\u5e9c\u867d\u7136\u8fd8\u672a\u5230\u8fd9\u4e2a\u5883\u754c\uff0c\u53ef\u662f\u5185\u52b2\u6697\u85cf\uff0c\u5c31\u52bf\u53cd\u51fb\uff0c\u6fb9\u53f0\u706d\u660e\u7684\u91cd\u624b\u6cd5\uff0c\u4e5f\u7adf\u7136\u7ed9\u4ed6\u4e3e\u91cd \u82e5\u8f7b\u7684\u5316\u89e3\u5f00\u4e86\u3002 \u3000\u3000\u6b66\u58eb\u4e2d\u7684\u9ad8\u624b\u4e0d\u89c9\u6b22\u7136\u559c\u8dc3\uff0c\u4f46\u4e91\u857e\u5374\u662f\u6697\u6697\u62c5\u5fc3\u3002\u53ea\u89c1\u4e09\u62db\u8fc7\u540e\uff0c\u5f20\u98ce\u5e9c\u795e\u60c5\u8d2f\u6ce8\uff0c \u770b\u5f97\u51fa\u6781\u662f\u7d27\u5f20\uff0c\u800c\u6fb9\u53f0\u706d\u660e\u5219\u4ecd\u662f\u795e\u8272\u81ea\u5982\uff0c\u4e5f\u4e0d\u89c1\u4ed6\u600e\u6837\u7528\u529b\uff0c\u5374\u662f\u6bcf\u4e00\u638c\u90fd\u631f\u7740\u98ce \u58f0\uff0c\u65e2\u4f3c\u8f7b\u63cf\u6de1\u5199\uff0c\u53c8\u662f\u72e0\u8fa3\u731b\u6251\u3002\u539f\u6765\u82e5\u7ec3\u5230\u6700\u9ad8\u7684\u5883\u754c\uff0c\u90a3\u81ea\u7136\u53ef\u4ee5\u201c\u4ee5\u67d4\u514b\u521a\u201d\uff0c\u4f46 \u82e5\u53cc\u65b9\u529f\u529b\u6709\u6240\u8ddd\u79bb\uff0c\u90a3\u67d4\u52b2\u9632\u8eab\u7684\u529f\u592b\uff0c\u5374\u4e5f\u672a\u5fc5\u6321\u5f97\u4e86\u91d1\u521a\u731b\u6251\uff01 \u3000\u3000\u4e24\u4eba\u4e00\u67d4\u4e00\u521a\uff0c\u8fdb\u9000\u653b\u5b88\uff0c\u6253\u4e86\u4e00\u76cf\u8336\u7684\u65f6\u5019\uff0c\u4ecd\u662f\u672a\u5206\u80dc\u8d25\uff0c\u4f46\u5f20\u98ce\u5e9c\u5df2\u6e10\u6e10\u989d\u5934\u89c1 \u6c57\uff0c\u4f17\u6b66\u58eb\u8fd8\u672a\u89c9\u5f97\u4ec0\u4e48\uff0c\u4e91\u857e\u5374\u5df2\u77e5\u9053\u4e0d\u5999\u3002\u5979\u867d\u7136\u672a\u770b\u51fa\u5f20\u98ce\u5e9c\u6709\u4f55\u8d25\u8c61\uff0c\u4f46\u5fc3\u4e2d\u6697 \u60f3\uff1a\u201c\u5f20\u98ce\u5e9c\u7684\u6b66\u529f\u4e0e\u5f20\u4e39\u67ab\u5728\u4f2f\u4ef2\u4e4b\u95f4\uff0c\u5728\u53e4\u5893\u4e4b\u4e2d\uff0c\u6fb9\u53f0\u706d\u660e\u4e0e\u5f20\u4e39\u67ab\u8bd5\u62db\uff0c\u5f20\u4e39\u67ab\u53ea \u80fd\u6321\u5f97\u5230\u4e94\u5341\u591a\u62db\uff0c\u5f20\u98ce\u5e9c\u529f\u529b\u867d\u6bd4\u5f20\u4e39\u67ab\u7a0d\u9ad8\uff0c\u770b\u6765\u4e5f\u7edd\u4e0d\u80fd\u6321\u5230\u4e03\u5341\u62db\u3002\u800c\u4eca\u4ed6\u4eec\u5df2\u25a1 \u62fc\u4e86\u5c06\u8fd1\u4e94\u5341\u62db\uff0c\u53ea\u6015\u5f20\u98ce\u5e9c\u5c31\u8981\u96be\u9003\u4e00\u8d25\u3002\u201d \u3000\u3000\u5f20\u98ce\u5e9c\u4e5f\u81ea\u77e5\u4e0d\u5999\uff0c\u518d\u6321\u4e86\u4e03\u516b\u62db\u66f4\u89c9\u547c\u5438\u903c\u4fc3\uff0c\u81ea\u601d\uff1a\u201c\u82e5\u7136\u8d25\u4e86\u58f0\u540d\u8fd8\u4e0d\u6253\u7d27\uff0c\u4e2d \u539f\u6b66\u58eb\u7684\u9762\u5b50\u5c82\u4e0d\u7ed9\u6211\u4e22\u5149\uff1f\u201d\u5fc3\u4e2d\u4e00\u6025\uff0c\u7adf\u7136\u5192\u5947\u9669\uff0c\u62fc\u5168\u529b\uff0c\u628a\u5185\u5bb6\u52b2\u529b\u90fd\u8fd0\u5230\u638c\u5fc3\uff0c \u6fb9\u53f0\u706d\u660e\u547c\u7684\u4e00\u638c\u6a2a\u626b\u8fc7\u6765\uff0c\u53c8\u662f\u4e00\u4e0b\u5343\u65a4\u91cd\u624b\u6cd5\uff0c\u5f20\u98ce\u5e9c\u7a81\u7136\u638c\u5fc3\u4e00\u7f29\uff0c\u5927\u559d\u4e00\u58f0\uff0c\u638c\u529b \u5c3d\u5410\u3002\u9ad8\u624b\u8f83\u6280\uff0c\u6700\u6015\u4e00\u638c\u6251\u7a7a\uff0c\u7ed9\u4eba\u53cd\u51fb\uff0c\u82e5\u7136\u662f\u522b\u4eba\u9047\u6b64\uff0c\u201c\u521a\u6781\u6613\u6298\u201d\uff0c\u4e0d\u5f85\u5bf9\u65b9\u53cd \u6251\u51fb\u4e2d\uff0c\u5c31\u8981\u624b\u8155\u8131\u81fc\u3002 \u3000\u3000\u4f46\u6fb9\u53f0\u706d\u660e\u662f\u4f55\u7b49\u6837\u4eba\uff0c\u7109\u80fd\u5982\u6b64\u8f7b\u6613\u53d7\u7b97\uff1f\u4ed6\u4e00\u638c\u867d\u7136\u6251\u7a7a\uff0c\u638c\u529b\u5374\u5982\u6392\u5c71\u5012\u6d77\u822c\u76f4 \u5954\u8fc7\u53bb\uff0c\u65b9\u5706\u4e00\u4e08\u4e4b\u5185\uff0c\u5168\u5728\u4ed6\u638c\u529b\u7b3c\u7f69\u4e4b\u4e0b\u3002\u5f20\u98ce\u5e9c\u6599\u4e0d\u5230\u4ed6\u7684\u529f\u529b\u5982\u6b64\u6df1\u6e5b\uff0c\u8fd9\u4e00\u6765\u5f04 \u5de7\u53cd\u62d9\uff0c\u81ea\u5df1\u7684\u6740\u624b\u795e\u62db\uff0c\u53cd\u53d8\u6210\u4e86\u5b64\u6ce8\u4e00\u63b7\u7684\u786c\u6253\u786c\u63a5\uff0c\u53ea\u89c9\u80f8\u53e3\u5982\u53d7\u5343\u94a7\u4e4b\u529b\uff0c\u547c\u5438\u53d7 \u963b\uff0c\u5168\u8eab\u53d1\u70ed\uff01\u5e78\u800c\u4ed6\u521a\u624d\u638c\u5fc3\u4e00\u7f29\u4e00\u767b\uff0c\u5185\u52b2\u5148\u655b\u540e\u53d1\uff0c\u5df2\u628a\u6fb9\u53f0\u706d\u660e\u7684\u638c\u529b\u5378\u4e86\u4e00\u534a\uff0c \u8981\u4e0d\u7136\u66f4\u662f\u96be\u4e8e\u62b5\u6321\u3002 \u3000\u3000\u8fd9\u65f6\u53cc\u65b9\u5404\u4ee5\u771f\u529b\u76f8\u63a5\uff0c\u53d8\u6210\u4e86\u9a91\u864e\u96be\u4e0b\u4e4b\u52bf\uff0c\u6fb9\u53f0\u706d\u660e\u4e5f\u6697\u6697\u5403\u4e86\u4e00\u60ca\u3002\u539f\u6765\u5f20\u98ce\u5e9c \u867d\u7136\u529f\u529b\u8f83\u4f4e\uff0c\u4f46\u4ed6\u7684\u7ef5\u638c\u529f\u592b\u5374\u662f\u5185\u5bb6\u7684\u4e0a\u4e58\u529f\u592b\uff0c\u521a\u67d4\u517c\u6d4e\uff0c\u4e5f\u662f\u6b66\u6797\u4e00\u7edd\uff0c\u6fb9\u53f0\u706d\u660e \u7684\u638c\u529b\u548c\u4ed6\u4e00\u63a5\uff0c\u7adf\u88ab\u80f6\u7740\uff0c\u6446\u8131\u4e0d\u5f97\u3002\u6fb9\u53f0\u706d\u660e\u6697\u6697\u53eb\u58f0\u201c\u82e6\u4e5f\u201d\uff0c\u81ea\u5df1\u867d\u65e0\u4f24\u4eba\u4e4b\u5fc3\uff0c \u4f46\u5904\u6b64\u5f62\u52bf\u4e4b\u4e0b\uff0c\u638c\u529b\u6536\u4e0d\u56de\u6765\uff0c\u800c\u4e14\u5f20\u98ce\u5e9c\u7684\u7ef5\u638c\u529f\u592b\u4e5f\u975e\u540c\u5c0f\u53ef\uff0c\u9ad8\u624b\u8f83\u6280\uff0c\u5230\u4e86\u201c\u6b7b \u62fc\u201d\u4e4b\u65f6\uff0c\u53c8\u4e0d\u80fd\u76f8\u8ba9\uff0c\u8feb\u5f97\u5168\u529b\u65bd\u4e3a\uff0c\u4e0d\u8ba9\u5bf9\u65b9\u7684\u638c\u529b\u53d1\u5230\u81ea\u5df1\u7684\u8eab\u4e0a\u3002 \u3000\u3000\u4e8c\u4eba\u8fd9\u4e00\u25a1\u62fc\uff0c\u65c1\u89c2\u9ad8\u624b\u65e0\u4e0d\u89e6\u76ee\u60ca\u5fc3\uff0c\u4f46\u89c1\u4e8c\u4eba\u5404\u81ea\u6c89\u8170\u5750\u9a6c\uff0c\u638c\u950b\u76f8\u63a5\uff0c\u56db\u76ee\u77aa \u89c6\uff0c\u72b6\u5982\u6597\u9e21\u3002\u7247\u523b\u4e4b\u540e\uff0c\u5f20\u98ce\u5e9c\u53d1\u51fa\u5fae\u5fae\u7684\u5598\u606f\u4e4b\u58f0\uff0c\u989d\u4e0a\u6c81\u51fa\u6c57\u73e0\uff0c\u624b\u638c\u4e0d\u4f4f\u5730\u5de6\u53f3\u6446 \u52a8\uff0c\u4f3c\u662f\u5728\u6d88\u89e3\u654c\u4eba\u51f6\u731b\u7684\u653b\u52bf\uff0c\u770b\u795e\u60c5\uff0c\u663e\u5f97\u5341\u5206\u5403\u529b\u3002\u5230\u4e86\u6b64\u9645\uff0c\u65c1\u4eba\u7eb5\u60f3\u4e0a\u524d\u62c9\u5f00\uff0c \u4e5f\u65e0\u4eba\u6709\u6b64\u529f\u529b\u3002 \u3000\u3000\u4e91\u857e\u770b\u5f97\u5446\u4e86\uff0c\u6697\u60f3\uff1a\u201c\u4f3c\u6b64\u5f62\u52bf\uff0c\u82e5\u4efb\u7531\u4ed6\u4eec\u25a1\u62fc\u4e0b\u53bb\u5f20\u98ce\u5e9c\u4e0d\u6b7b\u4e5f\u5f97\u91cd\u4f24\uff0c\u81ea\u5df1\u53c8 \u65e0\u6cd5\u76f8\u52a9\u3002\u201d\u60f3\u8d77\u5f20\u98ce\u5e9c\u867d\u662f\u671d\u5ef7\u519b\u5b98\uff0c\u5374\u8fd8\u7b97\u5f97\u4e0a\u662f\u4e2a\u70ed\u8840\u7537\u5b50\uff0c\u4e0d\u7531\u5f97\u66ff\u4ed6\u5927\u4e3a\u7740\u6025\u3002 \u518d\u8fc7\u7247\u523b\uff0c\u5f20\u98ce\u5e9c\u5598\u606f\u4e4b\u58f0\u66f4\u7c97\uff0c\u7a0d\u89e3\u6b66\u827a\u4e4b\u4eba\uff0c\u90fd\u5df2\u770b\u51fa\u4ed6\u5230\u4e86\u7edd\u9669\u4e4b\u5883\uff0c\u518d\u8fc7\u987b\u81fe\uff0c\u4fbf \u8981\u751f\u6b7b\u7acb\u5224\u3002\u767b\u65f6\u5168\u573a\u9759\u5bc2\uff0c\u8fde\u4e00\u6839\u7ee3\u82b1\u9488\u8dcc\u5728\u5730\u4e0b\uff0c\u4e5f\u542c\u5f97\u89c1\u54cd\u3002 \u3000\u3000\u5ffd\u542c\u6709\u4eba\u8f7b\u8f7b\u54b3\u4e86\u4e00\u58f0\uff0c\u573a\u4e2d\u5fc3\u4e0d\u77e5\u600e\u5730\u7a81\u7136\u591a\u4e86\u4e00\u4eba\uff0c\u8138\u8272\u7126\u9ec4\uff0c\u4e09\u7efa\u957f\u987b\uff0c\u7ea6\u6478\u6709 \u4e94\u5341\u4e0a\u4e0b\u5e74\u7eaa\uff0c\u8eab\u7a7f\u76f4\u88f0\u5927\u8902\uff0c\u62ff\u7740\u4e00\u628a\u7834\u84b2\u6247\uff0c\u4fe8\u5982\u521a\u521a\u4ece\u7530\u95f4\u8015\u4f5c\u56de\u6765\u7684\u4e61\u4e0b\u8001\u6c49\u3002\u4f17 \u4eba\u5168\u795e\u8d2f\u6ce8\uff0c\u7adf\u4e0d\u77e5\u4ed6\u662f\u5982\u4f55\u8fdb\u6765\uff0c\u90fd\u4e0d\u7981\u5927\u4e3a\u60ca\u8be7\u3002\u53ea\u89c1\u4ed6\u4e00\u6643\u773c\u95f4\uff0c\u5c31\u5230\u4e86\u4e24\u4eba\u8ddf\u524d\uff0c \u8f7b\u58f0\u7b11\u9053\uff1a\u201c\u4e24\u4f4d\u5927\u7237\u7d2f\u5566\uff0c\u6b47\u4e00\u6b47\u5427\uff01\u201d\u58f0\u97f3\u8bed\u8c03\u867d\u6709\u4e0d\u540c\uff0c\u6240\u8bf4\u7684\u8bdd\uff0c\u5374\u548c\u6fb9\u53f0\u706d\u660e\u521a \u624d\u8c03\u4f83\u90a3\u4e2a\u88ab\u6253\u7684\u6b66\u58eb\u4e00\u6837\u3002\u6fb9\u53f0\u706d\u660e\u5fc3\u4e2d\u4e00\u9707\uff0c\u53ea\u89c1\u90a3\u4e2a\u602a\u8001\u5934\u5b50\u95ea\u7535\u822c\u5730\u5c06\u7834\u84b2\u6247\u5728\u4e24 \u4eba\u5f53\u4e2d\u4e00\u9694\uff0c\u5636\u5636\u5636\u4e00\u9635\u9635\u8fde\u5bc6\u54cd\uff0c\u90a3\u7834\u84b2\u6247\u767b\u65f6\u88c2\u6210\u65e0\u6570\u788e\u7247\uff0c\u4e00\u4e1d\u4e1d\u5012\u5782\u4e0b\u6765\u3002\u5f20\u98ce\u5e9c \u5927\u53eb\u4e00\u58f0\uff0c\u5012\u8dc3\u51fa\u4e00\u4e08\u5f00\u5916\uff0c\u6fb9\u53f0\u706d\u660e\u4e5f\u6447\u6447\u6643\u6643\uff0c\u500f\u5730\u53cc\u638c\u4e00\u6536\uff0c\u9762\u4e0a\u73b0\u51fa\u65e0\u9650\u60ca\u5947\u4e4b \u8272\u3002 \u3000\u3000\u8981\u77e5\u602a\u8001\u5934\u513f\u8fd9\u4e00\u624b\u5b9e\u662f\u975e\u540c\u5c0f\u53ef\uff0c\u7adf\u7136\u501f\u7740\u7834\u84b2\u6247\u4e00\u9694\u4e4b\u529b\uff0c\u5c06\u4e24\u4eba\u7684\u5185\u5bb6\u771f\u529b\u5168\u90fd \u5378\u5728\u6247\u4e0a\uff0c\u800c\u81ea\u5df1\u5374\u6beb\u53d1\u65e0\u4f24\u3002\u8fd9\u79cd\u5378\u529b\u5316\u52b2\u7684\u529f\u592b\uff0c\u975e\u552f\u65bd\u7528\u8005\u672c\u4eba\u8981\u6709\u6df1\u6e5b\u7684\u6b66\u529f\uff0c\u800c \u4e14\u8981\u7528\u5f97\u6070\u5230\u597d\u5904\uff0c\u521a\u597d\u8d81\u7740\u4e24\u4eba\u6362\u6c14\u4e4b\u9645\uff0c\u8fd9\u624d\u80fd\u4e00\u4e3e\u89c1\u6548\uff0c\u8981\u4e0d\u7136\u81ea\u5df1\u672c\u8eab\u5c31\u6709\u751f\u547d\u4e4b \u9669\uff01 \u3000\u3000\u4f17\u4eba\u6b63\u5728\u60ca\u5947\uff0c\u53ea\u542c\u5f97\u6fb9\u53f0\u706d\u660e\u54c8\u54c8\u5927\u7b11\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u4eca\u65e5\u59cb\u5f97\u5e78\u4f1a\u9ad8\u4eba\uff0c\u6211\u6fb9\u53f0\u706d\u660e \u5012\u8981\u8bf7\u6559\u4e86\uff01\u201d\u90a3\u8c8c\u4f3c\u4e61\u4e0b\u8001\u5934\u7684\u602a\u5ba2\u63d0\u7740\u90a3\u628a\u7834\u70c2\u4e0d\u582a\u7684\u84b2\u6247\uff0c\u98a4\u5dcd\u5dcd\u7684\u60f6\u6050\u8bf4\u9053\uff1a\u201c\u6fb9 \u53f0\u5c06\u519b\u4f11\u5f97\u8bf4\u7b11\uff0c\u6211\u8fd9\u4e2a\u4e61\u4e0b\u8001\u6c49\u61c2\u5f97\u4ec0\u4e48\u628a\u5f0f\u554a\uff01\u201d\u6fb9\u53f0\u706d\u660e\u9762\u8272\u4e00\u6c89\uff0c\u8bf4\u9053\uff1a\u201c\u8001\u5148\u751f \u771f\u4e0d\u80af\u8d50\u6559\u4e48\uff1f\u201d\u5bf9\u9762\u4e09\u5c3a\uff0c\u62e2\u6307\u4e00\u5212\uff0c\u53ea\u542c\u5f97\u58f0\u5982\u88c2\u5e1b\uff0c\u628a\u90a3\u6247\u5341\u6570\u6761\u6247\u9aa8\u90fd\u9f50\u6839\u65ad\u4e86\uff0c \u5c31\u5982\u4e00\u4e0b\u5b50\u7ed9\u5229\u5203\u524a\u65ad\u4e00\u822c\uff01\u4f17\u4eba\u770b\u5f97\u5927\u60ca\u5931\u8272\uff0c\u5fc3\u4e2d\u53c8\u662f\u7eb3\u7f55\u975e\u5e38\uff0c\u60ca\u8005\u4e43\u662f\u6fb9\u53f0\u706d\u660e\u8fd9 \u624b\u94c1\u6307\u94dc\u7436\u7684\u529f\u592b\uff0c\u5df2\u5230\u4e86\u767b\u5cf0\u9020\u6781\u7684\u5883\u754c\uff1b\u7eb3\u7f55\u8005\u4e43\u662f\u770b\u5230\u90a3\u602a\u5ba2\u9002\u624d\u4e00\u4e3e\u800c\u5206\u5f00\u4e8c\u4eba\uff0c \u4e3e\u91cd\u82e5\u8f7b\uff0c\u770b\u6765\u6beb\u4e0d\u8d39\u529b\uff0c\u800c\u4eca\u4f55\u4ee5\u53c8\u5168\u4e0d\u62b5\u5fa1\uff0c\u7adf\u4efb\u7531\u6fb9\u53f0\u706d\u660e\u8fd8\u4ee5\u989c\u8272\u3002 \u3000\u3000\u5176\u5b9e\u4f17\u4eba\u6709\u6240\u4e0d\u77e5\uff0c\u90a3\u602a\u5ba2\u9002\u624d\u90a3\u6a2a\u7a7a\u4e00\u9694\uff0c\u5b9e\u662f\u534a\u51ed\u5de7\u52b2\uff0c\u534a\u51ed\u529f\u529b\uff0c\u5c06\u6fb9\u53f0\u706d\u660e\u4e0e \u5f20\u98ce\u5e9c\u4e24\u4eba\u7684\u5185\u5bb6\u771f\u529b\u90fd\u5378\u5230\u6247\u4e0a\uff0c\u8ba9\u4ed6\u4eec\u76f8\u6fc0\u76f8\u649e\uff0c\u4e92\u76f8\u62b5\u6d88\uff0c\u662f\u4ee5\u624d\u5f97\u6beb\u53d1\u65e0\u4f24\uff0c\u53ea\u6bc1 \u4e86\u4e00\u628a\u84b2\u6247\u3002\u800c\u4eca\u6fb9\u53f0\u706d\u660e\u7a81\u7136\u51fa\u624b\uff0c\u5b9e\u4e43\u51fa\u4e4e\u4ed6\u610f\u6599\u4e4b\u5916\uff0c\u4ed3\u731d\u4e4b\u95f4\uff0c\u53ea\u80fd\u8fd0\u6c14\u62a4\u8eab\uff0c\u4e0d \u53ca\u517c\u987e\u90a3\u628a\u6247\u5b50\u4e86\u3002\u8fd9\u79cd\u4e0a\u4e58\u6b66\u529f\u7684\u5965\u5999\u4e4b\u5904\uff0c\u53ea\u6709\u5f20\u98ce\u5e9c\u4e00\u4eba\u80fd\u591f\u7406\u89e3\uff0c\u5fc3\u4e2d\u611f\u6168\u4e07\u5206\uff0c \u6697\u81ea\u60f3\u9053\uff1a\u201c\u5f53\u771f\u662f\u5929\u5916\u6709\u5929\uff0c\u4eba\u5916\u6709\u4eba\uff0c\u6211\u7d20\u6765\u4ee5\u6b66\u529f\u81ea\u8d1f\uff0c\u800c\u4eca\u770b\u6765\uff0c\u4e0d\u4f46\u6fb9\u53f0\u706d\u660e\u8fdc \u80dc\u4e8e\u6211\uff0c\u5373\u8fd9\u8c8c\u4e0d\u60ca\u4eba\u7684\u8001\u6c49\u4e5f\u80dc\u6211\u591a\u591a\u3002\u770b\u8fd9\u4e24\u4eba\u5404\u5177\u795e\u901a\uff0c\u9e7f\u6b7b\u8c01\u624b\uff0c\u6b8a\u672a\u53ef\u6599\u3002\u201d\u5fc3 \u4e2d\u4e0d\u7981\u5fd0\u5fd1\u4e0d\u5b89\u3002\u8981\u77e5\u6fb9\u53f0\u706d\u660e\u4e43\u662f\u74e6\u523a\u4f7f\u8005\uff0c\u5f20\u98ce\u5e9c\u7b49\u4eba\u4e0e\u4ed6\u6bd4\u8bd5\u539f\u610f\u4e0d\u8fc7\u662f\u60f3\u632b\u6298\u4ed6\u7684 \u5a01\u98ce\uff0c\u53eb\u4ed6\u77e5\u9053\u4e2d\u56fd\u6709\u4eba\uff0c\u4e07\u4e0d\u6562\u7f6e\u4ed6\u4e8e\u6b7b\u3002\u4f46\u8fd9\u602a\u5ba2\u4e0d\u77e5\u662f\u4f55\u7b49\u6765\u5386\uff0c\u4ed6\u4e0e\u6fb9\u53f0\u706d\u660e\u90fd\u662f \u4e00\u7b49\u4e00\u7684\u9ad8\u624b\uff0c\u53cc\u65b9\u6b66\u529f\uff0c\u6df1\u4e0d\u53ef\u6d4b\uff0c\u4e00\u4ea4\u4e0a\u624b\uff0c\u53ea\u6015\u5fc5\u6709\u6b7b\u4f24\uff0c\u8fd9\u602a\u5ba2\u53c8\u4e0d\u662f\u671d\u5ef7\u4e2d\u4eba\uff0c \u52a8\u8d77\u624b\u6765\uff0c\u5f53\u65e0\u6240\u987e\u5fcc\uff0c\u800c\u4e14\u5373\u7b97\u6709\u6240\u987e\u5fcc\uff0c\u5230\u4e86\u7d27\u8981\u5173\u5934\uff0c\u6027\u547d\u76f8\u640f\u4e4b\u9645\uff0c\u5c31\u50cf\u81ea\u5df1\u521a\u624d \u4e0e\u6fb9\u53f0\u706d\u660e\u4e00\u6837\uff0c\u8c01\u4e5f\u4e0d\u80fd\u76f8\u8ba9\u4e86\u3002\u5f20\u98ce\u5e9c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u82e5\u7136\u6fb9\u53f0\u706d\u660e\u4e27\u547d\uff0c\u8fd9\u7978\u4e8b\u96be\u4ee5\u6536 \u62fe\uff0c\u4f46\u82e5\u8fd9\u8001\u5934\u4e27\u547d\uff0c\u4ed6\u66fe\u7ecf\u6551\u6211\uff0c\u6211\u53c8\u7109\u80fd\u5750\u89c6\uff1f\u5440\uff0c\u6211\u521a\u624d\u4e0e\u6fb9\u53f0\u706d\u660e\u4ea4\u624b\uff0c\u6709\u4ed6\u80fd\u591f \u5206\u5f00\uff0c\u82e5\u7136\u4ed6\u4eec\u4e8c\u4eba\u4ea4\u624b\uff0c\u53c8\u6709\u8c01\u80fd\u591f\u5206\u5f00\uff1f\u201d \u3000\u3000\u4f17\u6b66\u58eb\u4e0e\u5f20\u98ce\u5e9c\u540c\u4e00\u5fc3\u601d\uff0c\u597d\u5947\u4e4b\u5fc3\uff0c\u4ee4\u4ed6\u4eec\u5e0c\u671b\u8fd9\u4e8c\u4eba\u4ea4\u624b\u4e00\u8bd5\uff0c\u4f46\u4e00\u60f3\u5230\u5176\u4e2d\u5229 \u5bb3\uff0c\u53c8\u5e0c\u671b\u8fd9\u573a\u6bd4\u8bd5\u6bd4\u4e0d\u6210\u529f\uff0c\u573a\u4e2d\u6570\u5341\u5bf9\u773c\u775b\uff0c\u90fd\u770b\u7740\u90a3\u602a\u8001\u5934\u513f\u3002\u5f20\u98ce\u5e9c\u5fc3\u4e2d\u4e0d\u4f4f\u9053\uff1a \u201c\u5feb\u522b\u6bd4\u5427\uff0c\u5feb\u522b\u6bd4\u5427\u3002\u201d \u3000\u3000\u90a3\u602a\u8001\u5934\u513f\u5c06\u84b2\u6247\u4e00\u626c\uff0c\u5ffd\u9053\uff1a\u201c\u4f60\u5c06\u6211\u7684\u6247\u5b50\u6bc1\u4e86\uff0c\u6211\u4e0d\u8981\u5566\uff0c\u9001\u7ed9\u4f60\u5427\uff01\u201d\u90a3\u201c\u84b2 \u6247\u201d\u5176\u5b9e\u53ea\u5269\u4e0b\u4e86\u4e00\u6839\u6247\u67c4\uff0c\u53ea\u89c1\u4ed6\u53cc\u6307\u4e00\u5f39\uff0c\u6247\u67c4\u75be\u5982\u6d41\u77e2\uff0c\u5f84\u5c04\u90a3\u6fb9\u53f0\u706d\u660e\u989d\u89d2\u7684\u201c\u5929 \u7075\u7a74\u201d\uff0c\u8fd9\u4e00\u4e0b\uff0c\u6fb9\u53f0\u706d\u660e\u4e5f\u662f\u731d\u4e0d\u53ca\u9632\uff0c\u76f8\u8ddd\u592a\u8fd1\u95ea\u5df2\u4e0d\u53ca\uff0c\u542c\u90a3\u523a\u8033\u7684\u88c2\u5e1b\u4e4b\u58f0\u4e0d\u4e9a\u4e8e \u4e00\u652f\u5229\u7bad\u3002\u6fb9\u53f0\u706d\u660e\u5927\u53eb\u9053\uff1a\u201c\u597d\u4e00\u4e2a\u5f39\u6307\u795e\u901a\u7684\u529f\u592b\uff01\u201d \u3000\u3000\u4f17\u6b66\u58eb\u9f50\u90fd\u5931\u58f0\u60ca\u53eb\uff0c\u53ea\u89c1\u6fb9\u53f0\u706d\u660e\u5728\u95f4\u4e0d\u5bb9\u53d1\u4e4b\u9645\uff0c\u53cc\u624b\u7f29\u5165\u8896\u4e2d\uff0c\u957f\u8896\u4e00\u6325\uff0c \u201c\u6ce2\u201d\u7684\u4e00\u58f0\uff0c\u8863\u8896\u7a7f\u4e86\u4e00\u4e2a\u5927\u6d1e\uff0c\u90a3\u6839\u6247\u67c4\u75be\u5982\u6d41\u77e2\u7a7f\u8fc7\u573a\u5fc3\uff0c\u201c\u5693\u201d\u7684\u4e00\u58f0\u9489\u5728\u4e00\u68f5\u67f3 \u6811\u4e0a\u3002\u6fb9\u53f0\u706d\u660e\u53eb\u9053\uff1a\u201c\u6307\u4e0a\u529f\u592b\uff0c\u5f7c\u6b64\u90fd\u89c1\u8bc6\u8fc7\u4e86\uff0c\u6211\u518d\u9886\u6559\u4f60\u638c\u4e0a\u7684\u529f\u592b\u3002\u201d\u4e00\u8dc3\u800c \u8d77\uff0c\u8eab\u672a\u843d\u5730\uff0c\u5df2\u662f\u8fde\u73af\u4e24\u62f3\u76f8\u7ee7\u62cd\u51fa\u3002\u90a3\u602a\u8001\u5934\u513f\u53cc\u638c\u5f80\u5916\u4e00\u63a8\uff0c\u53eb\u9053\uff1a\u201c\u554a\u5440\uff0c\u4f60\u600e\u4e48 \u771f\u7684\u8981\u6253\u6211\u8fd9\u4e2a\u4e61\u4e0b\u8001\u6c49\uff1f\u201d\u6fb9\u53f0\u706d\u660e\u5728\u534a\u7a7a\u4e2d\u4e00\u4e2a\u8f6c\u8eab\uff0c\u201c\u54fc\u201d\u7684\u4e00\u58f0\u811a\u4e00\u6cbe\u5730\uff0c\u7acb\u523b\u53c8 \u662f\u4e00\u62f3\uff0c\u90a3\u602a\u8001\u5934\u513f\u53cc\u624b\u5408\u6210\u534a\u73af\uff0c\u5982\u62b1\u5a74\u513f\uff0c\u5f80\u5916\u4e00\u9001\uff0c\u53eb\u9053\uff1a\u201c\u6253\u6298\u6211\u8fd9\u8001\u9aa8\u5934\u5566\uff01\u201d \u53cc\u65b9\u62f3\u638c\u5176\u5b9e\u8fd8\u672a\u76f8\u4ea4\uff0c\u4f46\u90a3\u4e24\u4eba\u7684\u8863\u88f3\u3001\u5934\u53d1\u5df2\u5168\u90fd\u7ed9\u90a3\u62f3\u638c\u4e4b\u98ce\uff0c\u5439\u5f97\u98d8\u98d8\u6447\u52a8\uff01 \u3000\u3000\u5f20\u98ce\u5e9c\u9a87\u7136\u5931\u8272\uff0c\u60f3\u4e0d\u5230\u8fd9\u4e24\u4eba\u4e0d\u51fa\u624b\u5219\u5df2\uff0c\u4e00\u51fa\u624b\u7adf\u7136\u5c31\u662f\u4ee5\u771f\u529b\u76f8\u62fc\uff01\u4f46\u89c1\u90a3\u6fb9\u53f0 \u706d\u660e\u8fc5\u5982\u6012\u72ee\uff0c\u98de\u8eab\u529b\u6251\uff0c\u4e00\u638c\u63a5\u7740\u4e00\u638c\uff0c\u8fde\u73af\u731b\u51fb\uff1b\u90a3\u602a\u8001\u5934\u513f\u8eab\u5982\u6c34\u86c7\uff0c\u56db\u5468\u6e38\u8d70\uff0c\u7a81 \u7136\u4e00\u4e2a\u7ffb\u8eab\uff0c\u95ea\u7535\u822c\u4e00\u638c\u62cd\u51fa\u3002\u6fb9\u53f0\u706d\u660e\u5927\u53eb\u4e00\u58f0\uff0c\u53cc\u62f3\u9f50\u51fa\uff0c\u62f3\u638c\u4e00\u4ea4\uff0c\u5e9e\u5927\u7684\u8eab\u8eaf\u9707\u5f97 \u98de\u4e86\u8d77\u6765\u3002\u90a3\u602a\u8001\u5934\u513f\u4e5f\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u5012\u8dc3\u4e09\u6b65\uff0c\u6447\u6447\u6643\u6643\u3002\u6fb9\u53f0\u706d\u660e\u9762\u8272\u5927\u53d8\uff0c\u53eb\u9053\uff1a \u201c\u5927\u529b\u91d1\u521a\u624b\u7684\u529f\u592b\u7b97\u4f60\u5929\u4e0b\u65e0\u53cc\uff01\u8001\u82f1\u96c4\uff0c\u6211\u4ea4\u4f60\u8fd9\u4e2a\u670b\u53cb\uff0c\u4f60\u53ef\u80af\u5c06\u59d3\u540d\u6765\u5386\u8d50\u544a \u4e48\uff1f\u201d\u90a3\u602a\u8001\u5934\u513f\u53c8\u662f\u201c\u54fc\u201d\u7684\u4e00\u58f0\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4e61\u4e0b\u4eba\u4e0d\u6562\u9ad8\u6500\uff01\u201d\u5de6\u638c\u4e00\u6325\uff0c\u53f3\u811a\u98de \u8d77\u8e22\u4ed6\u817f\u5f2f\u7684\u201c\u767d\u6d77\u7a74\u201d\uff0c\u6fb9\u53f0\u706d\u660e\u5927\u6012\u559d\u9053\uff1a\u201c\u4f60\u5f53\u6211\u771f\u6015\u4f60\u4e0d\u6210\uff01\u201d\u5de6\u62f3\u4e00\u4f38\uff0c\u53f3\u638c\u4e00 \u62ff\uff0c\u90a3\u602a\u8001\u5934\u513f\u500f\u5730\u53d8\u62db\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u5929\u91ce\u8001\u602a\u7684\u4e24\u5b97\u770b\u5bb6\u672c\u9886\u90fd\u6296\u51fa\u6765\u4e86\uff0c\u597d\u4e00\u4e2a\u94c1\u7435\u7436 \u624b\u4e0e\u7f57\u6c49\u62f3\u7684\u529f\u592b\u5440\uff01\u201d\u6fb9\u53f0\u706d\u660e\u7684\u5e08\u7236\u53eb\u4e0a\u5b98\u5929\u91ce\uff0c\u4ee5\u94c1\u7435\u7436\u624b\u3001\u7f57\u6c49\u62f3\u3001\u5434\u94a9\u5251\u3001\u4e00\u6307 \u7985\u3001\u98de\u8757\u9488\u4e94\u6837\u529f\u592b\u5e76\u79f0\u6b66\u6797\u4e94\u7edd\uff0c\u56db\u5341\u5e74\u524d\u5373\u5df2\u4e0e\u4e91\u857e\u7684\u5e08\u7956\u7384\u673a\u9038\u58eb\u9f50\u540d\u5f53\u4e16\uff0c\u6b66\u6797\u540e \u5b66\u63d0\u53ca\u4ed6\u7684\u540d\u5b57\u4e5f\u8bda\u60f6\u8bda\u6050\u3002\u6fb9\u53f0\u706d\u660e\u89c1\u8fd9\u602a\u8001\u5934\u513f\u5c45\u7136\u6562\u5bf9\u81ea\u5df1\u7684\u5e08\u7236\u4e0d\u656c\u8d8a\u53d1\u5927\u6012\uff0c\u62f3 \u5982\u94c1\u9524\uff0c\u638c\u5982\u5229\u5203\uff0c\u653b\u52bf\u8d8a\u53d1\u51cc\u5389\uff01 \u3000\u3000\u90a3\u602a\u8001\u5934\u513f\u8c8c\u867d\u72c2\u50b2\uff0c\u5fc3\u4e2d\u53ef\u5b9e\u662f\u4e0d\u6562\u8f7b\u89c6\uff0c\u4e00\u638c\u62a4\u8eab\uff0c\u4e00\u638c\u8fce\u654c\uff0c\u7528\u5927\u529b\u91d1\u521a\u624b\u5c06\u7f57 \u6c49\u62f3\u4e0e\u94c1\u7435\u7436\u624b\u8feb\u4f4f\uff0c\u4e24\u4eba\u8d8a\u6253\u8d8a\u5feb\uff0c\u77f3\u8d70\u6c99\u98de\uff0c\u5708\u5b50\u8d8a\u5c55\u8d8a\u5927\uff0c\u56f4\u89c2\u8bf8\u4eba\uff0c\u8eab\u4e0d\u7531\u5df1\u5730\u90fd \u7ed9\u638c\u52bf\u62f3\u98ce\u903c\u5f97\u8fde\u8fde\u540e\u9000\uff0c\u7ad9\u5230\u79bb\u573a\u8fb9\u6570\u5c3a\u4e4b\u5730\u3002\u7f57\u6c49\u62f3\u672c\u6765\u662f\u5f88\u5e73\u5e38\u7684\u4e00\u79cd\u5c11\u6797\u62f3\u6cd5\uff0c\u94c1 \u7435\u7436\u624b\u4e5f\u5e76\u4e0d\u96be\u5b66\uff0c\u53ef\u662f\u5230\u4e86\u6fb9\u53f0\u706d\u660e\u624b\u91cc\uff0c\u5a01\u52bf\u5374\u715e\u662f\u60ca\u4eba\uff0c\u62f3\u638c\u517c\u65bd\uff0c\u653b\u5b88\u5e76\u7528\uff0c\u4e24\u79cd \u666e\u901a\u7684\u6b66\u529f\u914d\u5408\u8d77\u6765\uff0c\u5faa\u73af\u53cd\u8986\uff0c\u53d8\u5316\u65e0\u7a77\uff0c\u7adf\u662f\u6781\u5bfb\u5e38\u5904\u624d\u663e\u51fa\u6781\u6df1\u5965\u7684\u529f\u592b\u3002\u90a3\u602a\u8001\u5934 \u513f\u4e0d\u8bba\u662f\u62f3\u6765\u4e5f\u597d\uff0c\u638c\u6765\u4e5f\u597d\uff0c\u62f3\u638c\u9f50\u6765\u4e5f\u597d\uff0c\u90fd\u662f\u4ee5\u53f3\u638c\u6a2a\u76f4\u8fce\u51fb\uff0c\u51fa\u638c\u4e4b\u52bf\uff0c\u4e5f\u53d8\u5316\u65e0 \u7aef\uff0c\u6216\u4fa7\u9762\u4e00\u5288\uff0c\u6216\u6b63\u4e2d\u4e00\u5207\uff0c\u6216\u4ee5\u91cd\u624b\u6cd5\u6fc0\u5f97\u547c\u547c\u98ce\u54cd\uff0c\u6216\u8f7b\u98d8\u98d8\u5730\u62cd\u51fa\uff0c\u58f0\u606f\u6beb\u65e0\u3002\u4f46 \u6bcf\u4e00\u638c\u90fd\u662f\u6700\u5389\u5bb3\u7684\u91d1\u521a\u624b\u529f\u592b\uff0c\u4e0d\u8bba\u8f7b\u53d1\u91cd\u53d1\uff0c\u90fd\u6709\u5343\u94a7\u4e4b\u529b\uff01\u4ee5\u6fb9\u53f0\u706d\u660e\u90a3\u6837\u5f3a\u52b2\u7684\u653b \u52bf\uff0c\u4e5f\u5982\u6d2a\u6c34\u9047\u7740\u957f\u5824\uff0c\u767e\u822c\u51b2\u51fb\uff0c\u90fd\u51b2\u4e0d\u7834\u3002\u4f46\u602a\u8001\u5934\u513f\u7684\u5927\u529b\u91d1\u521a\u624b\u5374\u4e5f\u7834\u4e0d\u4e86\u6fb9\u53f0\u706d \u660e\u7684\u94c1\u7435\u7436\u624b\u4e0e\u7f57\u6c49\u62f3\u3002 \u3000\u3000\u6fb9\u53f0\u706d\u660e\u9002\u624d\u4e0e\u5f20\u98ce\u5e9c\u4e4b\u6218\u5df2\u4ee4\u89c2\u6218\u7684\u6b66\u58eb\u770b\u5f97\u77a0\u76ee\u7ed3\u820c\u4e86\uff0c\u4f46\u82e5\u4e0e\u602a\u8001\u5934\u513f\u8fd9\u4e00\u6218\u76f8 \u6bd4\uff0c\u5219\u521a\u624d\u4e4b\u6218\uff0c\u7b80\u76f4\u6709\u5982\u513f\u620f\u4e0d\u53ef\u76f8\u63d0\u5e76\u8bba\u3002\u4e0e\u5f20\u98ce\u5e9c\u4e4b\u6218\u4e0d\u8fc7\u662f\u60f3\u632b\u6298\u5bf9\u65b9\uff0c\u800c\u4e14\u5f3a\u5f31 \u5206\u660e\uff0c\u867d\u201c\u9669\u201d\u4e0d\u201c\u70c8\u201d\uff1b\u800c\u8fd9\u4e00\u6218\u5219\u53cc\u65b9\u76f4\u4f3c\u6027\u547d\u76f8\u640f\uff0c\u6240\u7528\u7684\u5168\u90fd\u662f\u6700\u4e0a\u4e58\u7684\u6b66\u529f\uff0c\u25a1 \u62fc\u4e86\u6570\u767e\u62db\u8fd8\u770b\u4e0d\u51fa\u8c01\u5f3a\u8c01\u5f31\u3002\u6709\u65f6\u660e\u660e\u770b\u6fb9\u53f0\u706d\u660e\u4e00\u62f3\u5df2\u6253\u5230\u602a\u8001\u5934\u513f\u8eab\u4e0a\uff0c\u5374\u5ffd\u5730\u7ed9\u4ed6 \u8f7b\u8f7b\u4e00\u638c\u62e8\u5f00\uff1b\u6709\u65f6\u660e\u660e\u770b\u5230\u662f\u602a\u8001\u5934\u513f\u5360\u4e86\u4e0a\u98ce\uff0c\u91d1\u521a\u624b\u5df2\u5c01\u95ed\u4e86\u56db\u65b9\u9000\u8def\uff0c\u4f46\u4e0d\u77e5\u600e\u7684 \u5374\u53c8\u5ffd\u5730\u7ed9\u6fb9\u53f0\u706d\u660e\u9003\u8131\uff0c\u800c\u4e14\u7a81\u65bd\u53cd\u51fb\u3002\u4f17\u6b66\u58eb\u770b\u5f97\u76ee\u7729\u795e\u8ff7\uff0c\u770b\u5230\u7d27\u5f20\u7cbe\u5f69\u4e4b\u5904\uff0c\u7b80\u76f4 \u4ee4\u4eba\u4e0d\u6562\u900f\u6c14\uff01 \u3000\u3000\u4e91\u857e\u5fc3\u4e2d\u5567\u5567\u79f0\u5947\uff0c\u6697\u601d\uff1a\u201c\u770b\u8fd9\u602a\u8001\u5934\u7684\u91d1\u521a\u624b\u529f\u592b\u679c\u7136\u662f\u795e\u5999\u5f97\u4e0d\u53ef\u601d\u8bae\uff0c\u7d20\u95fb\u6211 \u5927\u5e08\u4f2f\u7684\u91d1\u521a\u624b\u5929\u4e0b\u65e0\u654c\uff0c\u83ab\u975e\u4ed6\u5c31\u662f\u6211\u7684\u5927\u5e08\u4f2f\u4e48\uff1f\u201d\u7384\u673a\u9038\u58eb\u95e8\u4e0b\u4e94\u4eba\uff0c\u9664\u4e91\u857e\u7684\u7236\u4eb2 \u65e9\u6b7b\u4e4b\u5916\uff0c\u5176\u4ed6\u56db\u4eba\u5404\u5f97\u4e00\u95e8\u7edd\u827a\uff0c\u8bba\u6b66\u529f\u5251\u6cd5\u662f\u4e09\u5f1f\u5b50\u8c22\u5929\u534e\u6700\u5f3a\uff0c\u4f46\u8bba\u5230\u706b\u5019\u529f\u529b\u4e4b \u6df1\uff0c\u5374\u8981\u6570\u5927\u5f1f\u5b50\u8463\u5cb3\u7684\u91d1\u521a\u624b\u529f\u592b\u767b\u5cf0\u9020\u6781\u3002\u4e91\u857e\u53c8\u60f3\uff1a\u201c\u6211\u542c\u5e08\u7236\u8bf4\u8fc7\uff0c\u5927\u5e08\u4f2f\u548c\u4e09\u5e08 \u4f2f\u90fd\u662f\u6587\u6b66\u5168\u624d\uff0c\u4e00\u8868\u4eea\u5bb9\uff0c\u82e5\u7136\u662f\u4ed6\uff0c\u600e\u7684\u4f1a\u662f\u8fd9\u526f\u4e61\u4e0b\u8001\u5934\u7684\u6a21\u6837\uff1f\u800c\u4e14\u4ed6\u5341\u4f59\u5e74\u6765\u4e91 \u6e38\u8499\u85cf\uff0c\u53c8\u600e\u4e48\u4f1a\u7a81\u7136\u51fa\u73b0\u4eac\u90fd\uff1f\u201d \u3000\u3000\u4e91\u857e\u6b63\u5728\u5fd6\u5ea6\u601d\u91cf\uff0c\u5ffd\u89c1\u573a\u4e2d\u5f62\u52bf\u53c8\u662f\u4e00\u53d8\uff0c\u6fb9\u53f0\u706d\u660e\u4e0e\u90a3\u602a\u8001\u5934\u513f\u500f\u5730\u5206\u5f00\uff0c\u9002\u624d\u662f \u8fd0\u638c\u5982\u98ce\uff0c\u51fa\u62f3\u5982\u7535\uff0c\u5708\u5b50\u8d8a\u5c55\u8d8a\u5927\uff0c\u800c\u4eca\u5374\u662f\u6162\u817e\u817e\u5730\u4f60\u4e00\u62f3\u6211\u4e00\u811a\uff0c\u5708\u5b50\u53cd\u800c\u8d8a\u7f29\u8d8a \u5c0f\uff0c\u6709\u65f6\u751a\u81f3\u76f8\u5bf9\u51dd\u89c6\uff0c\u90fd\u4e0d\u52a8\u624b\uff0c\u7a81\u7136\u5927\u559d\u4e00\u58f0\uff0c\u5f7c\u6b64\u540c\u65f6\u8dc3\u8d77\u6362\u4e86\u4e00\u62db\uff0c\u53c8\u500f\u5730\u5206\u5f00\u3002 \u8868\u9762\u770b\u6765\uff0c\u5f62\u52bf\u6ca1\u6709\u521a\u624d\u731b\u70c8\uff0c\u5b9e\u5219\u662f\u5404\u4ee5\u5e73\u751f\u7edd\u5b66\u76f8\u62fc\uff0c\u6bcf\u4e00\u62db\u6bcf\u4e00\u5f0f\u90fd\u542b\u7740\u6740\u673a\uff01\u5f20\u98ce \u5e9c\u7b49\u8bc6\u8d27\u7684\u9ad8\u624b\u770b\u5f97\u76ee\u4e0d\u8f6c\u775b\uff0c\u6709\u65f6\u770b\u5230\u602a\u8001\u5934\u513f\u4e00\u638c\u5288\u4e0b\uff0c\u6fb9\u53f0\u706d\u660e\u4f3c\u5df2\u65e0\u53ef\u9003\u907f\uff0c\u4f46\u5374 \u5ffd\u5730\u4e00\u4e0b\u5b50\u8f7b\u63cf\u6de1\u5199\u5730\u5316\u5f00\uff0c\u5728\u4ed6\u672a\u51fa\u62db\u4e4b\u524d\uff0c\u4f17\u4eba\u90fd\u60f3\u4e0d\u51fa\u5982\u4f55\u62db\u67b6\uff0c\u5f85\u51fa\u62db\u4e4b\u540e\uff0c\u53c8\u90fd \u5fc3\u4e2d\u540c\u58f0\u8d5e\u53f9\uff1a\u201c\u554a\uff0c\u8fd9\u4e00\u8bb0\u5bfb\u5e38\u7684\u62db\u6570\uff0c\u6211\u4eec\u5374\u90fd\u6ca1\u6709\u60f3\u5230\uff01\u201d\u5176\u5b9e\u6700\u5bfb\u5e38\u53c8\u6b63\u662f\u6700\u4e0d\u5bfb \u5e38\uff0c\u4f17\u4eba\u56e0\u89c1\u53cc\u65b9\u7684\u6740\u624b\u5389\u5bb3\uff0c\u5728\u540e\u4e00\u62db\u672a\u5e94\u4e4b\u524d\uff0c\u5c3d\u4ece\u590d\u6742\u7e41\u96be\u7684\u5316\u89e3\u62db\u6570\u4e0a\u60f3\uff0c\u5374\u4e0d\u77e5 \u53cc\u65b9\u90fd\u662f\u9876\u513f\u5c16\u513f\u7684\u89d2\u8272\uff0c\u6700\u590d\u6742\u7684\u62db\u6570\u4e5f\u7792\u4e0d\u8fc7\u5bf9\u65b9\uff0c\u53cd\u4e0d\u5982\u672c\u7740\u6b63\u5b97\u7684\u62f3\u7406\uff0c\u968f\u673a\u5e94 \u53d8\uff0c\u5927\u5bb6\u90fd\u60f3\u5148\u4fdd\u6301\u7740\u4e0d\u8d25\uff0c\u7136\u540e\u53cd\u653b\u3002\u53ef\u662f\u8fd9\u6837\u4e00\u6765\uff0c\u7aef\u7684\u662f\u5404\u4ee5\u771f\u624d\u5b9e\u5b66\u76f8\u62fc\uff0c\u6700\u4e3a\u635f \u8017\u5185\u529b\uff0c\u6218\u4e0d\u591a\u65f6\uff0c\u53ea\u89c1\u4e24\u4eba\u5934\u4e0a\u90fd\u5982\u9876\u7740\u4e00\u4e2a\u5927\u84b8\u7b3c\u4f3c\u7684\u5934\u9876\u70ed\u817e\u817e\u5192\u6c14\u3002\u5f20\u98ce\u5e9c\u5927\u60ca\u5931 \u8272\uff1a\u8fd9\u6837\u4e0b\u53bb\u4e00\u5b9a\u4e24\u8d25\u4ff1\u4f24\uff0c\u4f46\u5374\u53c8\u65e0\u4ece\u89e3\u62c6\uff01 \u3000\u3000\u6fb9\u53f0\u706d\u660e\u4e00\u751f\u6765\u672a\u9047\u8fc7\u5982\u6b64\u5f3a\u52b2\u7684\u5bf9\u624b\uff0c\u5fc3\u4e2d\u4e5f\u4e0d\u7981\u6697\u6697\u53d1\u614c\u3002\u4ed6\u7684\u6027\u5b50\u8f83\u4e3a\u6025\u8e81\uff0c\u867d \u7136\u660e\u77e5\u6b64\u9645\u53d8\u62db\uff0c\u6781\u4e3a\u5192\u9669\uff0c\u4f46\u53c8\u4e0d\u613f\u4f3c\u6b64\u50f5\u6301\u4e0b\u53bb\uff0c\u5404\u53d7\u5185\u4f24\uff0c\u4e8e\u662f\u5f53\u90a3\u602a\u8001\u5934\u513f\u4ee5\u5927\u529b \u91d1\u521a\u624b\u8fd0\u52b2\u731b\u903c\u4e4b\u9645\uff0c\u9661\u7136\u5927\u559d\u4e00\u58f0\uff0c\u62db\u6570\u5927\u53d8\uff0c\u5de6\u62f3\u53f3\u638c\uff0c\u53c8\u5982\u66b4\u98ce\u8fc5\u96f7\u822c\u5730\u75be\u5377\u8fc7\u53bb\uff0c \u6bd4\u8d77\u521a\u624d\u66f4\u662f\u60ca\u4eba\uff01 \u3000\u3000\u90a3\u602a\u8001\u5934\u513f\u201c\u554a\u5440\u201d\u4e00\u58f0\uff0c\u8fde\u8fde\u540e\u9000\uff0c\u4f46\u89c1\u4ed6\u811a\u8e0f\u4e5d\u5bab\u516b\u5366\u65b9\u4f4d\uff0c\u867d\u9000\u4e0d\u4e71\uff0c\u4ecd\u662f\u4e00\u638c \u62a4\u80f8\uff0c\u4e00\u638c\u8fce\u654c\uff0c\u770b\u662f\u53ea\u5b88\u4e0d\u653b\u4f46\u5374\u6f5c\u5177\u6781\u5927\u7684\u53cd\u51fb\u4e4b\u529b\u3002\u6fb9\u53f0\u706d\u660e\u72e0\u653b\u4e0d\u4e0b\uff0c\u8fd8\u5c61\u88ab\u91d1\u521a \u638c\u529b\u903c\u9000\u56de\u6765\uff0c\u4e0d\u7531\u5f97\u5fc3\u5934\u4e00\u9707\uff0c\u60f3\u9053\uff1a\u201c\u6211\u7eb5\u6a2a\u4e8c\u5341\u4f59\u5e74\uff0c\u9664\u4e86\u4e00\u4e2a\u8c22\u5929\u534e\u582a\u79f0\u654c\u624b\u4e4b \u5916\uff0c\u5c31\u662f\u8fd9\u4e2a\u8001\u5934\u513f\u4e86\uff0c\u8c22\u5929\u534e\u7684\u5251\u6cd5\u81ea\u662f\u5929\u4e0b\u65e0\u53cc\uff0c\u4f46\u529f\u529b\u6df1\u6e5b\uff0c\u5374\u8fd8\u4f3c\u662f\u8fd9\u8001\u5934\u513f\u7a0d \u80dc\u3002\u54b3\uff0c\u96be\u9053\u4ed6\u4e5f\u4e0e\u8c22\u5929\u534e\u4e00\u6837\uff0c\u662f\u6211\u5e08\u7236\u5927\u5bf9\u5934\u7684\u95e8\u4e0b\u5f1f\u5b50\u4e48\uff1f\u201d\u4e09\u5341\u4f59\u5e74\u524d\uff0c\u6fb9\u53f0\u706d\u660e \u7684\u5e08\u7236\u4e0a\u5b98\u5929\u91ce\u66fe\u4e0e\u7384\u673a\u9038\u58eb\u4e92\u4e89\u6b66\u6797\u76df\u4e3b\u4e4b\u5ea7\uff0c\u5728\u5ce8\u5d4b\u4e4b\u5dc5\uff0c\u6597\u4e86\u4e09\u65e5\u4e09\u591c\uff0c\u4e0d\u5206\u80dc\u8d1f\u3002 \u4e0a\u5b98\u5929\u91ce\u8fd9\u624d\u9041\u8ff9\u8499\u53e4\uff0c\u5728\u585e\u5916\u6536\u5f92\uff0c\u53e6\u7acb\u5b97\u6d3e\u7684\u3002 \u3000\u3000\u6fb9\u53f0\u706d\u660e\u5fc3\u6709\u6240\u7591\uff0c\u4f46\u6b64\u65f6\u6b64\u9645\uff0c\u6b63\u662f\u751f\u6b7b\u640f\u6597\u7684\u7d27\u5f20\u5173\u5934\uff0c\u54ea\u5bb9\u53d1\u95ee\u3002\u90a3\u602a\u8001\u5934\u513f\u5e74 \u7eaa\u867d\u6bd4\u6fb9\u53f0\u706d\u660e\u5927\u4e86\u5341\u5e74\uff0c\u5374\u662f\u5185\u52b2\u60a0\u957f\uff0c\u6c14\u529b\u6beb\u4e0d\u8f93\u8680\u3002\u53ea\u89c1\u4ed6\u5b88\u4e2d\u5e26\u653b\uff0c\u5355\u638c\u7ffb\u98de\uff0c\u6216 \u62cd\u6216\u6293\uff0c\u6325\u6d12\u81ea\u5982\uff0c\u628a\u5927\u529b\u91d1\u521a\u624b\u7684\u529f\u592b\u53d1\u6325\u5f97\u6dcb\u6f13\u5c3d\u81f4\u53cd\u4e92\u7528\u3002\u6fb9\u53f0\u706d\u660e\u63a5\u7b2c\u4e00\u62db\u65f6\uff0c\u89c9 \u5f97\u6709\u4e00\u80a1\u5927\u529b\u8feb\u6765\uff0c\u6b63\u5728\u7528\u529b\u76f8\u641e\uff0c\u9661\u7136\u5bf9\u65b9\u4e00\u677e\uff0c\u52b2\u529b\u7adf\u4f3c\u5728\u4e00\u970e\u65f6\u95f4\u6d88\u5931\u5f97\u65e0\u5f71\u65e0\u8e2a\uff0c \u4e00\u4e2a\u6251\u7a7a\uff0c\u90a3\u602a\u8001\u5934\u513f\u7b2c\u4e09\u8bb0\u602a\u62db\u7a81\u53d1\uff0c\u4ee5\u62a4\u8eab\u7684\u5de6\u638c\uff0c\u53cd\u624b\u4e00\u638c\uff0c\u8fd9\u4e00\u638c\u6709\u6467\u5c71\u88c2\u77f3\u4e4b\u529f \u5b9e\u662f\u65e0\u4ee5\u62b5\u6321\uff01 \u3000\u3000\u602a\u8001\u5934\u513f\u63a5\u8fde\u4e09\u638c\uff0c\u7adf\u628a\u6fb9\u53f0\u706d\u660e\u653b\u5b88\u4ff1\u5907\u3001\u4e25\u5bc6\u5f02\u5e38\u7684\u62f3\u6cd5\u7834\u5f00\u3002\u4e91\u857e\u770b\u5f97\u5446\u4e86\uff0c\u5fc3 \u9053\uff1a\u201c\u9664\u4e86\u6211\u7684\u5927\u5e08\u4f2f\u8fd8\u6709\u8c01\u4eba\u6709\u6b64\u529f\u529b\uff1f\u201d\u4e0d\u7981\u9ad8\u53eb\u4e00\u58f0\uff1a\u201c\u597d\u554a\uff01\u201d\u5ffd\u89c1\u6fb9\u53f0\u706d\u660e\u80a9\u5934 \u4e00\u6c89\uff0c\u201c\u84ec\u201d\u7684\u4e00\u58f0\uff0c\u5982\u51fb\u8d25\u6728\uff0c\u7adf\u4e2d\u4e86\u90a3\u602a\u8001\u5934\u513f\u4e00\u638c\u3002\u5f20\u98ce\u5e9c\u5927\u53eb\u4e00\u58f0\uff1a\u201c\u4e0d\u597d\uff01\u201d\u4e0e \u6570\u540d\u9ad8\u624b\uff0c\u540c\u65f6\u8dc3\u51fa\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u6fb9\u53f0\u706d\u660e\u80a9\u5934\u4e0b\u6c89\uff0c\u602a\u8001\u5934\u513f\u7684\u624b\u638c\u7adf\u4f3c\u7ed9\u4ed6\u7275\u5f15 \u4e0b\u53bb\u672a\u53ca\u62bd\u8d77\uff0c\u6fb9\u53f0\u706d\u660e\u5df2\u7a81\u5730\u6a2a\u8170\u4e00\u51fb\uff01 \u3000\u3000\u90a3\u8001\u5934\u513f\u201c\u54fc\u201d\u201c\u54c8\u201d\u4e24\u58f0\uff0c\u8eab\u5f62\u500f\u7136\u98de\u8d77\uff0c\u7adf\u4ece\u4f17\u6b66\u58eb\u5934\u9876\u63a0\u8fc7\uff0c\u8f6c\u773c\u4e4b\u95f4\uff0c\u5c31\u4ece\u5899 \u5934\u98de\u51fa\uff0c\u62e6\u4e5f\u4e0d\u53ca\u3002\u4e91\u857e\u53ea\u89c9\u4ed6\u7684\u773c\u5149\u66fe\u5411\u81ea\u5df1\u5c04\u4e86\u4e00\u4e0b\uff0c\u4e0d\u7531\u5f97\u5fc3\u5934\u6251\u901a\u4e00\u8df3\u3002 \u3000\u3000\u5f20\u98ce\u5e9c\u9002\u624d\u62fc\u547d\u4e0e\u6fb9\u53f0\u706d\u660e\u76f8\u6297\uff0c\u6c14\u529b\u5140\u672a\u6062\u590d\uff0c\u8dc3\u51fa\u573a\u65f6\uff0c\u7a0d\u4e3a\u843d\u540e\uff0c\u4e24\u540d\u6b66\u58eb\uff0c\u62a2 \u5728\u524d\u5934\uff0c\u6b63\u60f3\u5c06\u6fb9\u53f0\u706d\u660e\u6276\u8d77\uff0c\u6fb9\u53f0\u706d\u660e\u76d8\u819d\u5750\u5728\u5730\u4e0a\uff0c\u52a8\u4e5f\u4e0d\u52a8\uff0c\u89c1\u4e24\u4eba\u62a2\u6765\uff0c\u5ffd\u7136\u80a9\u5934 \u4e00\u6446\u5de6\u53f3\u4e24\u638c\u659c\u63a8\u3002\u53ea\u542c\u5f97\u201c\u54ce\u54df\u201d\u4e24\u58f0\uff0c\u4e24\u540d\u6b66\u58eb\u90fd\u7ed9\u638c\u529b\u9707\u5f97\u8dc4\u8dc4\u8e09\u8e09\u5730\u5012\u9000\u6570\u6b65\u808b\u80c1 \u4f5c\u75db\uff0c\u4e0d\u7981\u540c\u58f0\u53eb\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5f20\u98ce\u5e9c\u731b\u7136\u9192\u609f\uff0c\u6025\u62a2\u4e0a\u524d\uff0c\u5c06\u540e\u9762\u7684\u6b66\u58eb\u62e6\u4f4f\uff0c\u8bf4\u9053\uff1a\u201c\u6fb9\u53f0\u5c06\u519b\u6b63\u4ee5\u6700\u4e0a\u4e58\u7684\u5185\u529f \u8fd0\u6c14\u62a4\u8eab\uff0c\u5927\u5bb6\u4e0d\u8981\u6270\u4ed6\uff01\u201d\u6fb9\u53f0\u706d\u660e\u8138\u4e0a\u542b\u7b11\uff0c\u5411\u5f20\u98ce\u5e9c\u5fae\u5fae\u70b9\u4e86\u70b9\u5934\uff0c\u4f3c\u662f\u5bf9\u4ed6\u8d5e\u8d4f\u3002 \u3000\u3000\u539f\u6765\u602a\u8001\u5934\u513f\u6700\u540e\u90a3\u638c\uff0c\u4ee5\u5927\u529b\u91d1\u521a\u624b\u6cd5\u5168\u529b\u5288\u4e0b\uff0c\u6fb9\u53f0\u706d\u660e\u672c\u6765\u4e0d\u6b7b\u4e5f\u5f97\u4f24\u6b8b\u3002\u5e78\u4ed6 \u4e5f\u662f\u4e2a\u529f\u529b\u6781\u9ad8\u60ef\u7ecf\u98ce\u6d6a\u7684\u4eba\uff0c\u5728\u7edd\u9669\u4e4b\u9645\uff0c\u80a9\u5934\u4e00\u6c89\uff0c\u786c\u63a5\u4e86\u91d1\u521a\u624b\u3002\u8fd9\u4e00\u6c89\u5c06\u91d1\u521a\u638c\u529b \u5378\u4e86\u4e00\u534a\uff0c\u4ed6\u8eab\u4e0a\u7a7f\u6709\u62a4\u8eab\u91d1\u7532\uff0c\u91d1\u7532\u4e5f\u7ed9\u9707\u88c2\uff0c\u4f46\u4e94\u810f\u516d\u8151\u5374\u5e78\u800c\u5f97\u514d\u9707\u4f24\u3002\u90a3\u602a\u8001\u5934\u513f \u5927\u7ea6\u4e5f\u662f\u6599\u4e0d\u5230\u4ed6\u5982\u6b64\u5e94\u7740\uff0c\u91d1\u521a\u624b\u7ed9\u4ed6\u80a9\u5934\u4e00\u6c89\u4e4b\u529b\u6240\u5f15\uff0c\u6765\u4e0d\u53ca\u64a4\u638c\u62a4\u8eab\uff0c\u7adf\u4e5f\u7ed9\u4ed6\u4e00 \u8bb0\u94c1\u7435\u7436\u62e6\u8170\u6a2a\u626b\u3002\u5e78\u800c\u6fb9\u53f0\u706d\u660e\u6b63\u5728\u8fd0\u52b2\u62a4\u8eab\uff0c\u529b\u5206\u5219\u8584\uff0c\u8fd9\u53cd\u51fb\u4e4b\u529b\uff0c\u4e0d\u53ca\u5e73\u5e38\u638c\u529b\u4e4b \u4e8c\u4e09\uff0c\u8981\u4e0d\u7136\u8fd9\u602a\u8001\u5934\u513f\u6050\u6015\u4e0d\u6b7b\u4e5f\u5f97\u91cd\u4f24\u3002\u9976\u662f\u5982\u6b64\uff0c\u4ed6\u98de\u51fa\u5f20\u5bb6\u4e4b\u540e\uff0c\u4e5f\u5410\u4e86\u4e00\u53e3\u9c9c \u8840\uff0c\u56de\u5230\u5bd3\u6240\uff0c\u4e5f\u8981\u9759\u5750\u534a\u65e5\uff0c\u624d\u80fd\u8fd0\u529f\u6062\u590d\u3002 \u3000\u3000\u6fb9\u53f0\u706d\u660e\u867d\u7136\u5f97\u514d\u5185\u4f24\uff0c\u5143\u6c14\u5374\u5df2\u5927\u8017\uff0c\u5916\u4f24\u66f4\u662f\u4e0d\u8f7b\uff0c\u5f53\u4e0b\u4e0d\u6562\u8bf4\u8bdd\uff0c\u76d8\u819d\u9759\u5750\uff0c\u884c \u6c14\u6d3b\u8840\u3002\u5f20\u98ce\u5e9c\u77a7\u4ed6\u4e00\u773c\uff0c\u5bf9\u4f17\u6b66\u58eb\u9053\uff1a\u201c\u6bd4\u6b66\u4e4b\u4e8b\u5df2\u4e86\uff0c\u8bf8\u4f4d\u8bf7\u56de\u5e9c\u5427\u3002\u201d\u4f17\u6b66\u58eb\u53ea\u6050\u6fb9 \u53f0\u706d\u660e\u6709\u6240\u4e0d\u6d4b\uff0c\u7275\u8fde\u5230\u81ea\u5df1\u8eab\u4e0a\uff0c\u4e50\u5f97\u8ba9\u5f20\u98ce\u5e9c\u4e00\u4eba\u6599\u7406\uff0c\u4e8e\u662f\u4e00\u4e2a\u4e2a\u5730\u9646\u7eed\u9000\u51fa\uff0c\u53ea\u6709 \u4e09\u6570\u540d\u6b66\u58eb\u9762\u6709\u5f02\u5bb9\uff0c\u5140\u81ea\u4e0d\u8d70\u3002\u4e91\u857e\u7b49\u5f97\u4e0d\u8010\u70e6\uff0c\u6b63\u6b32\u4e0a\u524d\u76f8\u89c1\uff0c\u5ffd\u89c1\u7559\u4e0b\u6765\u7684\u4e24\u540d\u6b66 \u58eb\uff0c\u540c\u58f0\u5bf9\u5f20\u98ce\u5e9c\u9053\uff1a\u201c\u65f6\u5019\u5c1a\u65e9\uff0c\u6fb9\u53f0\u5c06\u519b\u4ea6\u672a\u590d\u5143\uff0c\u4ffa\u5144\u5f1f\u4e14\u5f85\u7559\u6b64\u65f6\u2026\u2026\u201d\u5f20\u98ce\u5e9c\u622a \u7740\u9053\uff1a\u201c\u4e0d\u6562\u6709\u52b3\u4e24\u4f4d\u3002\u201d\u90a3\u4e24\u4eba\u7eed\u5f80\u4e0b\u9053\uff1a\u201c\u4ffa\u4e24\u5144\u5f1f\u4e00\u8005\u662f\u60f3\u5728\u6b64\u966a\u4f34\u6fb9\u53f0\u5c06\u519b\uff0c\u4e8c\u8005 \u662f\u60f3\u8d81\u6b64\u65f6\u673a\uff0c\u7ee7\u7eed\u4eca\u65e5\u7684\u76db\u4f1a\uff0c\u9886\u6559\u9886\u6559\u5f20\u5927\u4eba\u7684\u5200\u6cd5\uff0c\u5f7c\u6b64\u5370\u8bc1\u4e00\u4e0b\u6b66\u529f\uff0c\u8c05\u5f20\u5927\u4eba\u4e0d \u81f3\u4e8e\u4e0d\u5c51\u8d50\u6559\u5427\u3002\u201d \u3000\u3000\u5f20\u98ce\u5e9c\u4e00\u77a7\uff0c\u5fc3\u4e2d\u6697\u81ea\u5600\u5495\u3002\u539f\u6765\u8fd9\u4e24\u4eba\u4e43\u662f\u53f8\u793c\u592a\u76d1\u738b\u632f\u7684\u5fc3\u8179\u6b66\u58eb\uff0c\u738b\u632f\u5728\u5f53\u4eca\u7687 \u4e0a\u8fd8\u662f\u592a\u5b50\u4e4b\u65f6\uff0c\u66fe\u6559\u8fc7\u592a\u5b50\u8bfb\u4e66\uff0c\u800c\u4eca\u4ee5\u53f8\u793c\u592a\u76d1\u7684\u8eab\u4efd\u638c\u63e1\u5927\u6743\uff0c\u9677\u5bb3\u5fe0\u826f\uff0c\u52bf\u529b\u6781 \u5927\u3002\u8fd9\u4e24\u540d\u6b66\u58eb\u4e43\u662f\u540c\u80de\u5144\u5f1f\uff0c\u540d\u5524\u8def\u660e\u3001\u8def\u4eae\uff0c\u5bb6\u4f20\u516d\u5341\u4e09\u8def\u6df7\u5143\u724c\u6cd5\uff0c\u8fd9\u79cd\u724c\u6cd5\u672c\u662f\u4e00 \u624b\u6301\u76fe\uff0c\u4e00\u624b\u6301\u5251\uff0c\u53ef\u4ee5\u51b2\u950b\u9677\u9635\uff0c\u4ea6\u53ef\u4ee5\u77ed\u5175\u76f8\u63a5\u3002\u8fd9\u4e24\u5144\u5f1f\uff0c\u5374\u4e00\u4eba\u7ec3\u5251\uff0c\u4e00\u4eba\u7ec3\u76fe\uff0c \u4e24\u4eba\u5408\u4f7f\u6df7\u5143\u724c\u6cd5\uff0c\u6bd4\u4e00\u4eba\u66f4\u5389\u5bb3\u3002\u5f20\u98ce\u5e9c\u4eca\u6b21\u672c\u6765\u6ca1\u6709\u9080\u7ea6\u4ed6\u4eec\uff0c\u4ed6\u4eec\u5374\u64c5\u81ea\u6df7\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u5f20\u98ce\u5e9c\u4e00\u542c\uff0c\u4fbf\u77e5\u8def\u5bb6\u5144\u5f1f\u6765\u610f\u4e0d\u5584\uff0c\u8981\u77e5\u5f20\u98ce\u5e9c\u6b63\u5728\u6076\u6218\u6fb9\u53f0\u706d\u660e\u4e4b\u540e\uff0c\u6c14\u529b\u81ea\u7136\u6253 \u4e86\u6298\u6263\u3002\u53ef\u662f\u5f53\u7740\u6fb9\u53f0\u706d\u660e\u7684\u9762\u5f20\u98ce\u5e9c\u53c8\u4e0d\u613f\u5c06\u8fd9\u4e2a\u539f\u56e0\u8bf4\u51fa\uff0c\u62d2\u7edd\u8def\u5bb6\u5144\u5f1f\u7684\u6311\u6218\uff0c\u5f53\u4e0b \u6168\u7136\u8bf4\u9053\uff1a\u201c\u65e2\u7136\u4e24\u4f4d\u6709\u6b64\u96c5\u5174\uff0c\u5f20\u67d0\u53ea\u597d\u5949\u966a\uff0c\u54b1\u4eec\u5f7c\u6b64\u5370\u8bc1\u6b66\u529f\uff0c\u70b9\u5230\u4e3a\u6b62\uff0c\u80dc\u8d25\u4e0d \u8bba\u3002\u201d\u8def\u5bb6\u5144\u5f1f\u7b11\u9053\uff1a\u201c\u8fd9\u4e2a\u81ea\u7136\uff0c\u662f\u80dc\u662f\u8d25\uff0c\u90fd\u4e50\u5f97\u4e00\u4e2a\u54c8\u54c8\u3002\u201d\u4e24\u4eba\u5de6\u53f3\u4e00\u5206\uff0c\u5404\u81ea\u62bd \u51fa\u76fe\u724c\u5229\u5251\u3002 \u3000\u3000\u4e91\u857e\u597d\u4e0d\u70e6\u8e81\uff0c\u5fc3\u9053\uff1a\u201c\u597d\u7aef\u7aef\u7684\u53c8\u6bd4\u4ec0\u4e48\u6b66\uff1f\u201d\u53ef\u662f\u81ea\u5df1\u4e43\u662f\u5916\u4eba\uff0c\u4e0d\u4fbf\u529d\u963b\uff0c\u53ea\u597d \u5728\u65c1\u89c2\u770b\u3002\u53ea\u89c1\u5f20\u98ce\u5e9c\u62bd\u51fa\u7f05\u5200\u9053\u58f0\uff1a\u201c\u8fdb\u62db\u5427\uff01\u201d\u8def\u660e\u9053\uff1a\u201c\u5f20\u5927\u4eba\u5148\u8bf7\uff01\u201d\u7f05\u5200\u626c\u7a7a\u4e00 \u95ea\u7528\u201c\u4e94\u864e\u65ad\u95e8\u5200\u201d\u4e2d\u7684\u201c\u622a\u201d\u5b57\u8bc0\uff0c\u6a2a\u5200\u622a\u65a9\u8def\u660e\u7684\u624b\u8155\u3002\u53ea\u542c\u5f97\u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c\u8def\u4eae\u7684 \u76fe\u724c\u500f\u7136\u4f38\u51fa\uff0c\u8fce\u7740\u5200\u950b\u4fbf\u7838\uff0c\u5f20\u98ce\u5e9c\u65e9\u77e5\u4ed6\u6709\u6b64\u4e00\u62db\uff0c\u5200\u78b0\u94c1\u724c\uff0c\u987a\u52bf\u5f39\u8d77\uff0c\u9752\u5149\u95ea\u5904\uff0c \u4e00\u62db\u201c\u7ea2\u971e\u593a\u76ee\u201d\uff0c\u5200\u950b\u76f4\u53d6\u8def\u4eae\u7684\u54bd\u5589\u3002\u8def\u660e\u5229\u5251\u4e00\u6325\uff0c\u62a2\u653b\u786c\u524a\u5f20\u98ce\u5e9c\u7684\u81c2\u818a\uff0c\u5f20\u98ce\u5e9c \u56de\u5200\u4e00\u9694\uff0c\u5c06\u4ed6\u7684\u653b\u52bf\u4e00\u4e3e\u5316\u5f00\u3002 \u3000\u3000\u8def\u660e\u4e00\u770b\uff0c\u76fe\u724c\u4e0e\u5200\u950b\u76f8\u63a5\u4e4b\u5904\uff0c\u7adf\u7ed9\u6233\u4e86\u4e00\u4e2a\u5c0f\u6307\u5934\u822c\u7c97\u5927\u7684\u51f9\u9677\uff0c\u4e0d\u7981\u9a87\u7136\uff0c\u5fc3 \u9053\uff1a\u201c\u6211\u53ea\u9053\u4ed6\u5df2\u75b2\u7d2f\u4e0d\u582a\uff0c\u5374\u8fd8\u6709\u5982\u6b64\u6c14\u529b\u3002\u201d\u4e0d\u6562\u6020\u6162\uff0c\u5c06\u76fe\u724c\u821e\u5f97\u547c\u547c\u98ce\u54cd\uff0c\u63a9\u62a4\u5144 \u5f1f\u8fdb\u653b\u3002\u8fd9\u8def\u5bb6\u516d\u5341\u4e09\u8def\u6df7\u5143\u724c\u6cd5\uff0c\u5389\u5bb3\u4e4b\u5904\u5168\u5728\u8fd9\u9762\u76fe\u724c\uff0c\u7838\u3001\u538b\u3001\u6309\u3001\u5288\uff0c\u5584\u5b88\u80fd\u653b\uff0c \u786e\u6709\u51e0\u8def\u72ec\u95e8\u624b\u6cd5\u3002\u81f3\u4e8e\u90a3\u53e3\u5251\u4e0d\u8fc7\u5168\u5728\u76fe\u724c\u63a9\u62a4\u4e4b\u4e0b\uff0c\u65bd\u884c\u653b\u88ad\u3002\u4e0d\u8fc7\u56e0\u5b83\u6709\u76fe\u724c\u63a9\u62a4\uff0c \u53ef\u4ee5\u5168\u91c7\u653b\u52bf\uff0c\u5a01\u529b\u65e0\u5f62\u4e2d\u5c31\u589e\u52a0\u4e86\u4e00\u500d\u3002 \u3000\u3000\u82e5\u5728\u5e73\u65f6\uff0c\u8fd9\u4e24\u5144\u5f1f\u81ea\u7136\u4e0d\u662f\u5f20\u98ce\u5e9c\u7684\u5bf9\u624b\uff0c\u53ef\u662f\u5982\u4eca\u5f20\u98ce\u5e9c\u6c14\u529b\u5c1a\u672a\u6062\u590d\uff0c\u6b66\u529f\u6253\u4e86 \u6298\u6263\uff0c\u4ed6\u53c8\u60f3\u4ee5\u5feb\u5200\u65a9\u4e71\u9ebb\u7684\u624b\u6cd5\u901f\u6218\u901f\u51b3\uff0c\u4e0d\u5230\u4e00\u76cf\u8336\u7684\u65f6\u523b\uff0c\u5df2\u62a2\u653b\u7684\u4e09\u4e94\u5341\u62db\uff0c\u54ea\u77e5 \u8def\u5bb6\u5144\u5f1f\u914d\u5408\u5f97\u5341\u5206\u4e4b\u597d\uff0c\u5e26\u653b\u5e26\u5b88\u7adf\u4ee4\u5f20\u98ce\u5e9c\u4e0d\u80fd\u5404\u4e2a\u51fb\u7834\u3002\u4e09\u4e94\u5341\u62db\u4e00\u8fc7\uff0c\u5f20\u98ce\u5e9c\u6c14\u529b \u4e0d\u52a0\uff0c\u8def\u4eae\u76fe\u724c\u4e00\u633a\uff0c\u4e00\u4e2a\u201c\u8fc5\u96f7\u8d2f\u9876\u201d\uff0c\u5411\u5f20\u98ce\u5e9c\u5f53\u5934\u6253\u4e0b\u3002\u5f20\u98ce\u5e9c\u77e5\u4ed6\u724c\u6c89\u529b\u731b\uff0c\u8fd9\u4e00 \u4e0b\u5b50\u5c11\u8bf4\u4e5f\u6709\u4e03\u516b\u767e\u65a4\u529b\u91cf\uff0c\u82e5\u7136\u81ea\u5df1\u6c14\u529b\u5145\u6c9b\u7684\u8bdd\uff0c\u8fd9\u4e03\u516b\u767e\u65a4\u4e4b\u529b\uff0c\u81ea\u7136\u7b97\u4e0d\u4e86\u4ec0\u4e48\uff0c \u53ef\u662f\u5728\u6c14\u8870\u529b\u7aed\u4e4b\u65f6\uff0c\u5374\u4e0d\u6562\u786c\u67b6\u786c\u63a5\u4e86\u3002\u54ea\u77e5\u5f20\u98ce\u5e9c\u8fd9\u4e48\u4e00\u95ea\uff0c\u8def\u4eae\u7684\u94c1\u724c\u5982\u5f71\u968f\u5f62\uff0c\u8ffd \u7740\u7f05\u5200\u786c\u78b0\u786c\u538b\uff0c\u7acb\u523b\u628a\u5f20\u98ce\u5e9c\u8feb\u5f97\u5904\u5728\u4e0b\u98ce\uff0c\u8def\u660e\u7684\u5229\u5251\uff0c\u653b\u52bf\u9aa4\u76db\uff0c\u5982\u6bd2\u86c7\u5410\u820c\u822c\u968f\u7740 \u94c1\u724c\u8fdb\u9000\u4e00\u4f38\u4e00\u7f29\uff0c\u5251\u5251\u4e0d\u79bb\u5f20\u98ce\u5e9c\u7684\u8981\u5bb3\u3002 \u3000\u3000\u4e91\u857e\u5c1a\u672a\u6653\u77e5\u5185\u4e2d\u542b\u6709\u5371\u673a\uff0c\u770b\u5f97\u5341\u5206\u7eb3\u7f55\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u770b\u6765\u53ef\u5e76\u4e0d \u50cf\u53ea\u662f\u5370\u8bc1\u6b66\u529f\u554a\uff01\u201d\u5ffd\u89c1\u8def\u4eae\u970d\u5730\u584c\u8170\u864e\u4f0f\uff0c\u4e00\u4e2a\u65cb\u8f6c\uff0c\u76fe\u724c\u7fd8\u8d77\uff0c\u4e00\u62db\u201c\u6a2a\u626b\u5343\u519b\u201d\uff0c \u62e6\u8170\u4fbf\u5288\uff0c\u5f20\u98ce\u5e9c\u6025\u5fd9\u4e00\u4e2a\u201c\u9f99\u5f62\u98de\u6b65\u201d\uff0c\u4ece\u94c1\u724c\u4e4b\u4e0b\u63a0\u51fa\uff0c\u4e00\u7529\u8155\uff0c\u8fd8\u4e86\u4e00\u62db\u201c\u87b3\u25a1\u5c55 \u81c2\u201d\uff0c\u5200\u950b\u4e0b\u65a9\u654c\u4eba\u53cc\u8db3\uff0c\u54ea\u77e5\u771f\u4e2a\u662f\u201c\u87b3\u8782\u6355\u8749\uff0c\u9ec4\u96c0\u5728\u540e\u201d\uff0c\u62db\u6570\u521a\u521a\u4f7f\u51fa\uff0c\u8def\u660e\u5374\u7a81 \u7136\u4ece\u4fa7\u9762\u4e00\u5251\u523a\u6765\uff01 \u3000\u3000\u4e91\u857e\u60ca\u53eb\u4e00\u58f0\uff0c\u624b\u6307\u6025\u5f39\uff0c\u5c06\u4e00\u679a\u201c\u6885\u82b1\u8774\u8776\u9556\u201d\u98de\u51fa\uff0c\u8def\u660e\u8fd9\u4e00\u5251\u523a\u51fa\uff0c\u6ee1\u62df\u5728\u5f20\u98ce \u5e9c\u7684\u8eab\u4e0a\u6420\u4e2a\u900f\u660e\u7684\u7a9f\u7abf\uff0c\u4e0d\u6599\u201c\u94ee\u201d\u7684\u4e00\u58f0\uff0c\u5251\u5c16\u7a81\u7ed9\u6885\u82b1\u8774\u8776\u9556\u6253\u4e2d\uff0c\u6b6a\u8fc7\u4e00\u8fb9\uff0c\u672a\u770b \u6e05\u6697\u5668\u6765\u8def\uff0c\u6025\u5fd9\u6309\u5251\u4e00\u95ea\uff0c\u6b63\u5f85\u559d\u95ee\uff0c\u4e91\u857e\u4e5f\u6b63\u60f3\u8dc3\u51fa\uff0c\u5ffd\u89c1\u90a3\u6fb9\u53f0\u706d\u660e\u7a81\u7136\u98de\u8eab\u8dc3\u8d77\uff0c \u53eb\u9053\uff1a\u201c\u6211\u8fd8\u8981\u518d\u6253\u4e00\u573a\uff0c\u4f60\u4eec\u4e24\u4f4d\u65e2\u7136\u8981\u7559\u6b64\u4f34\u6211\uff0c\u4e3a\u4e86\u916c\u8c22\u76db\u60c5\uff0c\u6211\u5c31\u820d\u547d\u966a\u966a\u541b\u5b50 \u5427\uff01\u5f20\u5927\u4eba\uff0c\u8bf7\u4f60\u9000\u4e0b\uff01\u201d\u8bdd\u672a\u8bf4\u5b8c\uff0c\u4eba\u5df2\u98de\u5230\uff0c\u4ed6\u8fd0\u6c14\u4e5d\u8f6c\uff0c\u6c14\u529b\u5df2\u5145\u6c9b\u5982\u5e38\u3002\u53ea\u89c1\u4ed6\u5de6 \u624b\u4e00\u62ff\uff0c\u53f3\u638c\u4e00\u5288\uff0c\u547c\u7684\u4e00\u638c\uff0c\u7adf\u628a\u8def\u4eae\u7684\u94c1\u724c\u9707\u5f97\u98de\u4e0a\u534a\u7a7a\uff0c\u8def\u660e\u7684\u90a3\u53e3\u5229\u5251\u4e5f\u7ed9\u4ed6\u5288\u624b \u593a\u8fc7\uff0c\u62d7\u6298\u4e24\u6bb5\uff0c\u8def\u5bb6\u5144\u5f1f\u60ca\u5f97\u5446\u4e86\u3002\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u6fb9\u53f0\u706d\u660e\u4e00\u624b\u4e00\u4e2a\uff0c\u500f\u5730\u5c06\u8def\u660e\u3001 \u8def\u4eae\u4e3e\u4e86\u8d77\u6765\uff0c\u559d\u58f0\uff1a\u201c\u53bb\uff01\u201d\u4e00\u4e2a\u65cb\u98ce\u6025\u821e\uff0c\u5c06\u4e8c\u4eba\u63b7\u51fa\u6570\u4e08\u5f00\u5916\uff0c\u75db\u5f97\u4ed6\u4eec\u72c2\u55e5\u60e8\u53eb\uff0c \u773c\u524d\u91d1\u661f\u4e71\u821e\uff0c\u6655\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u6fb9\u53f0\u706d\u660e\u4ef0\u5929\u72c2\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6709\u751f\u4ee5\u6765\uff0c\u4eca\u65e5\u6253\u5f97\u6700\u75db\u5feb\u4e86\uff01\u201d\u5411\u5f20\u98ce\u5e9c\u70b9\u5934\u4e00\u793c\uff0c\u53c8 \u5411\u4e91\u857e\u6253\u4e86\u4e2a\u62db\u547c\uff0c\u9053\uff1a\u201c\u6211\u8fd8\u8981\u6253\u90a3\u8001\u5934\u513f\u53bb\uff0c\u5c11\u966a\u4e86\uff01\u201d\u8fc8\u5f00\u5927\u6b65\uff0c\u8d70\u51fa\u5f20\u5bb6\u7684\u7ec3\u6b66 \u573a\u3002 \u3000\u3000\u5f20\u98ce\u5e9c\u614c\u5fd9\u4e0a\u524d\u5bdf\u770b\u8def\u5bb6\u5144\u5f1f\u7684\u4f24\u52bf\uff0c\u53ea\u89c1\u8def\u660e\u7ed9\u6454\u65ad\u4e86\u4e24\u6839\u7b4b\u9aa8\uff0c\u8def\u4eae\u8dcc\u65ad\u4e86\u4e24\u53ea\u95e8 \u7259\uff0c\u6fb9\u53f0\u706d\u660e\u8fd9\u4e00\u6454\u7528\u7684\u4e43\u662f\u5de7\u52b2\uff0c\u53ea\u4ee4\u4ed6\u4eec\u53d7\u4e86\u5916\u4f24\uff0c\u5e76\u4e0d\u59a8\u53ca\u6027\u547d\u3002\u5f20\u98ce\u5e9c\u7ed9\u4ed6\u4eec\u6577\u4e0a \u91d1\u521b\u6b62\u80fd\u4e4b\u836f\uff0c\u4e24\u4eba\u5527\u5527\u54fc\u54fc\uff0c\u4e00\u8ddb\u4e00\u62d0\u7684\u81ea\u884c\u56de\u53bb\u3002 \u3000\u3000\u5f20\u98ce\u5e9c\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u5440\uff0c\u771f\u662f\u6599\u60f3\u4e0d\u5230\uff01\u201d\u4e91\u857e\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\u6599\u60f3\u4e0d\u5230\uff1f\u201d\u5f20\u98ce\u5e9c \u9053\uff1a\u201c\u6211\u4e00\u5411\u4e0d\u53d7\u738b\u632f\u7684\u7b3c\u7edc\uff0c\u8fd9\u4e24\u4eba\u4e43\u662f\u738b\u632f\u7684\u5fc3\u8179\u6b66\u58eb\uff0c\u770b\u6765\u521a\u624d\u4e4b\u4e8b\u4e43\u662f\u738b\u632f\u7684\u6307 \u4f7f\uff0c\u6709\u610f\u52a0\u5bb3\u4e8e\u6211\u4e86\u3002\u201d\u4e91\u857e\u60f3\u4e0d\u5230\u4eac\u5e08\u7684\u6b66\u58eb\u4e5f\u662f\u5404\u6709\u6d3e\u7cfb\uff0c\u4e92\u76f8\u5fcc\u523b\uff0c\u4f46\u5979\u53e6\u6709\u5fc3\u4e8b\uff0c \u4e0d\u613f\u591a\u95ee\u3002\u53ea\u542c\u5f97\u5f20\u98ce\u5e9c\u95ee\u9053\uff1a\u201c\u55ef\uff0c\u4f60\u90a3\u4f4d\u670b\u53cb\u5f20\u4e39\u67ab\u5f20\u76f8\u516c\u5462\uff1f\u201d\u4e91\u857e\u9762\u4e0a\u4e00\u7ea2\uff0c\u9053\uff1a \u201c\u5728\u9752\u9f99\u5ce1\u4e4b\u540e\uff0c\u6211\u4eec\u5c31\u5206\u624b\u4e86\u3002\u201d\u5f20\u98ce\u5e9c\u9053\uff1a\u201c\u53ef\u60dc\u53ef\u60dc\uff01\u8981\u4e0d\u7136\uff0c\u4f60\u4eec\u4e8c\u4eba\u5728\u6b64\uff0c\u53cc\u5251 \u5408\u74a7\uff0c\u5b9a\u53ef\u5c06\u6fb9\u53f0\u706d\u660e\u6253\u8d25\u3002\u8fd9\u4e09\u65e5\u6765\u4ed6\u8fde\u80dc\u5341\u573a\uff0c\u5e78\u6709\u90a3\u602a\u8001\u5934\u513f\u632b\u6298\u4e86\u4ed6\u4e00\u4e0b\u9510\u6c14\uff0c\u4f46 \u5404\u81ea\u53d7\u4f24\uff0c\u4e5f\u4e0d\u8fc7\u662f\u6253\u6210\u5e73\u624b\u3002\u5440\uff0c\u8fd9\u6b21\u53ef\u771f\u662f\u4e22\u4e86\u6211\u4eec\u4eac\u5e08\u6b66\u58eb\u7684\u9762\u5b50\u4e86\u3002\u201d\u4e91\u857e\u89c1\u4ed6\u751a \u662f\u96be\u8fc7\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u4e5f\u5e76\u6ca1\u6709\u8d25\u7ed9\u6fb9\u53f0\u706d\u660e\u5440\uff01\u201d\u5f20\u98ce\u5e9c\u9053\uff1a\u201c\u5e78\u662f\u90a3\u602a\u8001\u5934\u513f\u6765\u5f97\u53ca\u65f6\uff0c \u8981\u4e0d\u7136\u4e0d\u8bf4\u843d\u8d25\uff0c\u8fde\u6027\u547d\u6050\u6015\u4e5f\u4e22\u4e86\uff01\u8fd9\u602a\u8001\u5934\u513f\u4e5f\u4e0d\u77e5\u662f\u600e\u6837\u8fdb\u6765\u7684\uff1f\u8fd9\u4e48\u591a\u6b66\u58eb\uff0c\u7adf\u6ca1 \u6709\u4e00\u4eba\u53d1\u73b0\uff0c\u7ed9\u4ed6\u6324\u8fdb\u4e86\u573a\u4e2d\u3002\u201d\u987f\u4e86\u4e00\u987f\uff0c\u53c8\u9053\uff1a\u201c\u8fd9\u6fb9\u53f0\u706d\u660e\u4e5f\u602a\uff0c\u521a\u624d\u82e5\u4e0d\u662f\u4ed6\u90a3\u4e48 \u4e00\u63d2\u624b\uff0c\u6050\u6015\u6211\u4e5f\u96be\u9003\u6697\u7b97\u3002\u55ef\uff0c\u8bf4\u8d77\u6765\u6211\u8fd8\u8981\u591a\u8c22\u4f60\u90a3\u679a\u6885\u82b1\u8774\u8776\u9556\u5462\uff01\u201d \u3000\u3000\u4e91\u857e\u8feb\u4e0d\u53ca\u5f85\uff0c\u65e0\u5fc3\u591a\u8bf4\u95f2\u8bdd\uff0c\u5f20\u98ce\u5e9c\u8bdd\u58f0\u4e00\u6b47\uff0c\u5979\u7acb\u5373\u95ee\u9053\uff1a\u201c\u5f20\u5927\u4eba\uff0c\u6211\u4eca\u6b21\u5165 \u4eac\uff0c\u5b9e\u662f\u6709\u4e00\u4e8b\u8981\u6c42\u4f60\u76f8\u52a9\u3002\u201d\u5f20\u98ce\u5e9c\u9053\uff1a\u201c\u8bf7\u8bf4\u3002\u201d\u4e91\u857e\u9053\uff1a\u201c\u4f60\u90e8\u4e0b\u90a3\u4f4d\u59d3\u4e91\u7684\u5c11\u5e74\u519b \u5b98\uff0c\u6c42\u4f60\u8bf7\u4ed6\u6765\u4e0e\u6211\u76f8\u89c1\u53ef\u597d\uff1f\u201d\u5f20\u98ce\u5e9c\u7728\u7728\u773c\u775b\uff0c\u751a\u662f\u5947\u602a\u9053\uff1a\u201c\u4f60\u5165\u4eac\u5c31\u662f\u4e3a\u4e86\u6b64\u4e8b \u4e48\uff1f\u201d \u3000\u3000\u4e91\u857e\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5c31\u662f\u4e3a\u4e86\u6b64\u4e8b\u3002\u201d\u5f20\u98ce\u5e9c\u9053\uff1a\u201c\u4f60\u4e0e\u4e91\u7edf\u9886\u6709\u4f55\u4eb2\u6545\uff0c\u600e\u4e48\u6211\u4ece\u672a\u542c \u4ed6\u63d0\u8fc7\u3002\u201d\u4e91\u857e\u9053\uff1a\u201c\u5f7c\u6b64\u540c\u59d3\u662f\u4ee5\u6e34\u6b32\u4e00\u8bc6\u3002\u201d\u5f20\u98ce\u5e9c\u5fc3\u9053\uff1a\u201c\u5929\u4e0b\u540c\u59d3\u8005\u751a\u591a\uff0c\u8fd9\u7406\u7531 \u53ef\u8bf4\u4e0d\u901a\u3002\u201d\u4e91\u857e\u53c8\u9053\uff1a\u201c\u82e5\u5f20\u5927\u4eba\u6709\u4e8b\uff0c\u8bf7\u5c06\u4e91\u7edf\u9886\u7684\u5730\u5740\u544a\u77e5\uff0c\u6211\u81ea\u5df1\u53bb\u627e\u4ed6\u4e5f\u662f\u4e00 \u6837\u3002\u201d\u5f20\u98ce\u5e9c\u5ffd\u7136\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4e8b\u60c5\u4e14\u6162\u6162\u5546\u91cf\uff0c\u8bf7\u8fdb\u5185\u8fb9\u53bb\u8bf4\u3002\u201d\u4e91\u857e\u5fc3\u9053\uff1a \u201c\u8fd9\u4e8b\u60c5\u6709\u751a\u5546\u91cf\uff0c\u544a\u8bc9\u6211\u4e0d\u5c31\u5b8c\u4e86\u3002\u201d\u4f46\u81ea\u5df1\u4e43\u662f\u5ba2\u4eba\uff0c\u4e0d\u4fbf\u591a\u95ee\u3002 \u3000\u3000\u5f20\u98ce\u5e9c\u5e26\u4e91\u857e\u8d70\u51fa\u7ec3\u6b66\u573a\u6240\uff0c\u8ba9\u4e91\u857e\u8fdb\u5ba2\u5385\u5750\u5b9a\uff0c\u53eb\u5bb6\u4eba\u6ce1\u4e86\u58f6\u597d\u8336\uff0c\u9053\u58f0\uff1a\u201c\u5f97\u7f6a\uff0c \u6211\u8fdb\u53bb\u6362\u6362\u8863\u670d\u3002\u201d\u7ecf\u8fc7\u4e0e\u6fb9\u53f0\u706d\u660e\u90a3\u573a\u6076\u6597\uff0c\u5f20\u98ce\u5e9c\u8eab\u7a7f\u7684\u9752\u8272\u7bad\u8863\u7adf\u7ed9\u6fb9\u53f0\u706d\u660e\u7528\u201c\u94c1 \u6307\u94dc\u7435\u201d\u7684\u529f\u592b\u6495\u88c2\u4e86\u597d\u51e0\u5904\uff0c\u800c\u4e14\u8863\u4e0a\u6cbe\u6ee1\u5c18\u6c99\uff0c\u8fde\u5934\u53d1\u4e5f\u662f\u4e00\u7247\u9ec4\u8272\u3002\u4e91\u857e\u5fc3\u4e2d\u6709\u4e8b\uff0c \u672a\u8bf4\u4e4b\u524d\uff0c\u8fd8\u4e0d\u89c9\u5f97\uff0c\u65e2\u8bf4\u4e4b\u540e\uff0c\u4ed4\u7ec6\u4e00\u77a7\uff0c\u89c1\u5f20\u98ce\u5e9c\u5c31\u50cf\u7ecf\u8fc7\u6c99\u6f20\u3001\u957f\u9014\u8dcb\u6d89\u7684\u65c5\u4eba\u4e00 \u6837\uff0c\u8863\u88f3\u7834\u788e\uff0c\u6ee1\u9762\u98ce\u5c18\u4e4b\u8272\uff0c\u679c\u7136\u5341\u5206\u96be\u770b\uff0c\u4e0d\u7981\u7b11\u9053\uff1a\u201c\u90a3\u6fb9\u53f0\u706d\u660e\u771f\u662f\u5389\u5bb3\uff0c\u597d\u5728\u662f \u4f60\uff0c\u8fd8\u7ecf\u53d7\u5f97\u4f4f\u3002\u201d \u3000\u3000\u5f20\u98ce\u5e9c\u8fdb\u53bb\u6362\u8863\uff0c\u4e91\u857e\u7b49\u5f97\u597d\u4e0d\u5fc3\u6025\uff0c\u597d\u4e0d\u5bb9\u6613\uff0c\u624d\u7b49\u5230\u5f20\u98ce\u5e9c\u51fa\u6765\u6025\u5fd9\u95ee\u9053\uff1a\u201c\u5f20\u5927 \u4eba\uff0c\u90a3\u4e91\u7edf\u9886\u7a76\u7adf\u4f4f\u5728\u4f55\u5904\uff1f\u201d\u5f20\u98ce\u5e9c\u6162\u6761\u65af\u7406\u5730\u6574\u6574\u8863\u670d\uff0c\u5750\u4e86\u4e0b\u6765\uff0c\u555c\u4e86\u53e3\u8336\uff0c\u8fd9\u624d\u542b \u7b11\u8bf4\u9053\uff1a\u201c\u4e91\u7edf\u9886\u53ef\u96be\u89c1\u5230\u5566\uff01\u201d\u4e91\u857e\u5413\u4e86\u4e00\u8df3\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u4ed6\u9047\u4e86\u4ec0\u4e48\u610f\u5916\u4e48\uff1f\u201d\u4e00\u79cd \u5bf9\u4eb2\u4eba\u5173\u5207\u7684\u611f\u60c5\uff0c\u81ea\u7136\u6d41\u9732\uff0c\u5f20\u98ce\u5e9c\u77a7\u5728\u773c\u91cc\uff0c\u53c8\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u662f\u6709\u610f\u5916\uff0c\u4e0d\u8fc7\u8fd9\u2018\u610f \u5916\u2019\u4e43\u662f\u597d\u4e8b\uff0c\u4ed6\u7ed9\u7687\u4e0a\u770b\u4e2d\uff0c\u5df2\u8c03\u5230\u5185\u5ef7\u5f53\u4f8d\u536b\u53bb\u4e86\uff0c\u8f7b\u6613\u4e0d\u80fd\u51fa\u5bab\uff0c\u6240\u4ee5\u8bf4\u96be\u4e8e\u76f8 \u89c1\u3002\u201d\u4e91\u857e\u5927\u6025\uff0c\u9053\uff1a\u201c\u4f60\u4e5f\u4e0d\u80fd\u5524\u4ed6\u51fa\u6765\u5417\uff1f\u201d\u5f20\u98ce\u5e9c\u9053\uff1a\u201c\u73b0\u5728\u4ed6\u5df2\u4e0d\u5f52\u6211\u6240\u7edf\u5c5e\uff0c\u81ea \u7136\u4e0d\u80fd\u3002\u201d\u4e91\u857e\u9053\uff1a\u201c\u8fd9\u5374\u5982\u4f55\u662f\u597d\uff1f\u201d\u5f20\u98ce\u5e9c\u9053\uff1a\u201c\u4f60\u82e5\u60f3\u89c1\u4ed6\uff0c\u534a\u6708\u4e4b\u540e\u6216\u8005\u53ef\u6709\u673a \u4f1a\u3002\u201d\u4e91\u857e\u9053\uff1a\u201c\u613f\u95fb\u5176\u6545\u3002\u201d\u5f20\u98ce\u5e9c\u9053\uff1a\u201c\u534a\u6708\u4e4b\u540e\uff0c\u4eca\u5e74\u6b66\u4e3e\u7279\u79d1\u5f00\u8bd5\uff0c\u5343\u91cc\u5144\u5df2\u62a5\u4e86 \u540d\uff0c\u60f3\u4ed6\u6b66\u827a\u8d85\u7fa4\u5a34\u719f\u5175\u6cd5\uff0c\u5f53\u6709\u6b66\u72b6\u5143\u4e4b\u671b\u3002\u82e5\u4ed6\u4e2d\u4e86\u6b66\u72b6\u5143\uff0c\u7687\u4e0a\u81ea\u7136\u8d4f\u4ee5\u519b\u804c\uff0c\u8d50\u90b8 \u53e6\u5c45\uff0c\u4e0d\u5fc5\u518d\u5728\u5bab\u5185\u5f53\u4f8d\u536b\u4e86\u3002\u201d \u3000\u3000\u4e91\u857e\u597d\u751f\u5931\u671b\uff0c\u5f53\u4e0b\u4fbf\u60f3\u544a\u8f9e\u3002\u5f20\u98ce\u5e9c\u5374\u7559\u7740\u5979\u8c08\u8bdd\uff0c\u8ffd\u5fc6\u5f53\u65e5\u5728\u9752\u9f99\u5ce1\u4e4b\u4e8b\uff0c\u53c8\u5938\u5956 \u4e86\u4e00\u987f\u5f20\u4e39\u67ab\uff0c\u8bf4\u662f\u5168\u51ed\u4ed6\u7684\u667a\u8ba1\uff0c\u91d1\u5200\u5468\u5065\u7684\u513f\u5b50\u548c\u81ea\u5df1\u624d\u5f97\u4ee5\u4e24\u4fdd\u5168\u3002\u4e91\u857e\u6bcf\u542c\u4ed6\u63d0\u8d77 \u5f20\u4e39\u67ab\u5fc3\u4e2d\u5c31\u662f\u201c\u535c\u201d\u7684\u4e00\u8df3\uff0c\u5f20\u98ce\u5e9c\u90fd\u77a7\u5728\u773c\u5185\uff0c\u5fc3\u4e2d\u6781\u662f\u7eb3\u7f55\uff0c\u5ffd\u95ee\u9053\uff1a\u201c\u5f20\u4e39\u67ab\u679c\u662f \u5f20\u5b97\u5468\u7684\u513f\u5b50\u4e48\uff1f\u201d\u4e91\u857e\u9053\uff1a\u201c\u662f\u7684\u3002\u201d\u5f20\u98ce\u5e9c\u9053\uff1a\u201c\u90a3\u5c31\u771f\u662f\u51fa\u4e8e\u6c61\u6ce5\u800c\u4e0d\u67d3\u4e86\u3002\u770b\u4ed6\u6240 \u4f5c\u6240\u4e3a\uff0c\u5b9e\u662f\u4e00\u4e2a\u7231\u56fd\u7684\u7537\u513f\uff0c\u53ef\u7b11\u5343\u91cc\u5144\u6837\u6837\u90fd\u597d\uff0c\u5c31\u662f\u5bf9\u5f20\u4e39\u67ab\u5374\u56fa\u6267\u6210\u89c1\uff0c\u5207\u9f7f\u6068 \u4ed6\u3002\u201d\u4e91\u857e\u5fc3\u4e2d\u4e00\u75db\uff0c\u8bf4\u4e0d\u51fa\u8bdd\u3002\u5f20\u98ce\u5e9c\u5ffd\u53c8\u95ee\u9053\uff1a\u201c\u4f60\u4e5f\u662f\u4ece\u8499\u53e4\u6765\u7684\u5417\uff1f\u201d\u4e91\u857e\u9053\uff1a \u201c\u6211\u5c0f\u65f6\u5019\u5728\u8499\u53e4\u4f4f\u8fc7\u3002\u201d\u5f20\u98ce\u5e9c\u9053\uff1a\u201c\u90a3\u4e48\u4e0e\u5343\u91cc\u5144\u7684\u8eab\u4e16\u53ef\u5dee\u4e0d\u591a\uff0c\u4f60\u53ef\u77e5\u8fd9\u6b21\u6765\u7684\u756a \u738b\u4e0e\u6fb9\u53f0\u706d\u660e\u662f\u4ec0\u4e48\u6837\u7684\u4eba\u4e48\uff1f\u201d\u4e91\u857e\u9053\uff1a\u201c\u6211\u672a\u6ee1\u4e03\u5c81\uff0c\u5c31\u79bb\u5f00\u8499\u53e4\uff0c\u8499\u53e4\u7684\u4e8b\u60c5\uff0c\u77e5\u5f97 \u751a\u5c11\uff0c\u5927\u4eba\u4e3a\u4f55\u7279\u522b\u95ee\u8fd9\u4e8c\u4eba\uff1f\u201d \u3000\u3000\u5f20\u98ce\u5e9c\u9053\uff1a\u201c\u671d\u5ef7\u8fd1\u65e5\u6709\u4e00\u4ef6\u8bae\u8bba\u672a\u5b9a\u4e4b\u4e8b\uff0c\u751a\u662f\u4ee4\u4eba\u5947\u602a\u3002\u201d\u4e91\u857e\u60f3\u8d77\u81ea\u5df1\u4e43\u662f\u5e73\u6c11 \u4e0d\u4fbf\u6253\u542c\u671d\u5ef7\u4e4b\u4e8b\uff0c\u5e76\u4e0d\u8ffd\u95ee\u3002\u5f20\u98ce\u5e9c\u5374\u89c6\u5979\u5982\u540c\u77e5\u5df1\uff0c\u5e76\u4e0d\u987e\u8651\uff0c\u5f80\u4e0b\u8bf4\u9053\uff1a\u201c\u8fd9\u756a\u738b\u540d \u53eb\u963f\u523a\uff0c\u5728\u74e6\u523a\u56fd\u53d7\u5c01\u4e3a\u2018\u77e5\u9662\u2019\uff0c\u5373\u662f\u2018\u6267\u653f\u2019\u4e4b\u610f\uff0c\u6743\u52bf\u5728\u8bf8\u738b\u4e4b\u4e0a\uff0c\u800c\u5728\u592a\u5e08\u4e5f\u5148\u4e4b \u4e0b\u3002\u8fd9\u6b21\u6765\u671d\uff0c\u4e0e\u6211\u56fd\u8c08\u548c\uff0c\u63d0\u51fa\u4e86\u4e09\u4e2a\u6761\u4ef6\uff1a\u4e00\u662f\u5272\u96c1\u95e8\u5173\u5916\u767e\u91cc\u4e4b\u5730\uff0c\u4e24\u56fd\u4ee5\u96c1\u95e8\u5173\u4e3a \u754c\u3002\u4e8c\u662f\u4ee5\u4e2d\u56fd\u7684\u94c1\u5668\u4ea4\u6362\u8499\u53e4\u7684\u826f\u9a6c\u3002\u4e09\u662f\u8bf7\u4ee5\u516c\u4e3b\u4e0b\u5ac1\u74e6\u523a\u738b\u8131\u8131\u4e0d\u82b1\u7684\u513f\u5b50\u3002\u9601\u8001\u4e8e \u8c26\u529b\u4e89\u4e0d\u80fd\u63a5\u53d7\u6b64\u4e09\u6761\u548c\u7ea6\uff0c\u8bf4\u662f\u4e2d\u56fd\u4e4b\u5730\uff0c\u5bf8\u571f\u4e0d\u80fd\u5272\u8ba9\uff0c\u94c1\u5668\u8ba9\u4e0e\u74e6\u523a\uff0c\u4ed6\u7684\u5175\u5907\u66f4 \u5f3a\uff0c\u66f4\u662f\u517b\u864e\u8d3b\u60a3\uff0c\u4e07\u4e0d\u80fd\u5141\u3002\u81f3\u4e8e\u4ee5\u516c\u4e3b\u548c\u4eb2\u867d\u662f\u7687\u5ba4\u5185\u90e8\u7684\u4e8b\u60c5\uff0c\u4f46\u6709\u4f24\u2018\u5929\u671d\u2019\u4f53 \u9762\uff0c\u4ea6\u662f\u4e0d\u5141\u4e3a\u5b9c\u3002\u201d\u4e91\u857e\u9053\uff1a\u201c\u4e8e\u8c26\u662f\u4e2a\u6b63\u76f4\u7684\u5927\u81e3\uff0c\u516c\u5fe0\u4e3a\u56fd\uff0c\u6709\u4f55\u5947\u602a\uff1f\u201d\u5f20\u98ce\u5e9c \u9053\uff1a\u201c\u4e8e\u8c26\u529b\u4e3b\u62d2\u548c\uff0c\u90a3\u81ea\u7136\u6beb\u4e0d\u5947\u602a\u3002\u5947\u7684\u662f\u5978\u5ba6\u738b\u632f\u4e5f\u4e0d\u4e3b\u548c\u3002\u738b\u632f\u6697\u4e2d\u4e0e\u74e6\u523a\u52fe\u901a\uff0c \u6211\u7b49\u4ea6\u6709\u6240\u95fb\u3002\u96c1\u95e8\u5173\u5916\u767e\u91cc\u4e4b\u5730\u4e43\u662f\u91d1\u5200\u5468\u5065\u7684\u52bf\u548c\u6240\u5728\uff0c\u671d\u5ef7\u7ba1\u8f96\u4e0d\u5230\uff0c\u738b\u632f\u6068\u6781\u5468 \u5065\uff0c\u5341\u5e74\u6765\u5c61\u6709\u5bc6\u4ee4\u4ea4\u4e0e\u96c1\u95e8\u5173\u7684\u5b88\u5c06\uff0c\u51c6\u4ed6\u4e0e\u74e6\u523a\u8054\u5175\uff0c\u6251\u706d\u5468\u5065\u3002\u6211\u4eec\u90fd\u4ee5\u4e3a\u4ed6\u8fd9\u6b21\u4e50 \u5f97\u505a\u4e2a\u2018\u987a\u6c34\u4eba\u60c5\u2019\uff0c\u5c06\u96c1\u95e8\u5173\u5916\u4e4b\u5730\u5272\u4e0e\u74e6\u523a\u4e86\uff0c\u8c01\u77e5\u4ed6\u4e5f\u4e0d\u5141\u3002\u518d\u8bf4\u5230\u4ee5\u4e2d\u56fd\u94c1\u5668\u4ea4\u6362 \u8499\u53e4\u540d\u9a6c\u4e4b\u4e8b\uff0c\u5341\u4f59\u5e74\u6765\uff0c\u738b\u632f\u5c31\u5728\u6697\u4e2d\u505a\u8fd9\u4e70\u5356\u3002\u201d\u4e91\u857e\u9053\uff1a\u201c\u4e5f\u8bb8\u662f\u4ed6\u5185\u759a\u795e\u660e\u4e0d\u6562\u516c \u7136\u8d44\u654c\u3002\u201d\u5f20\u98ce\u5e9c\u7b11\u9053\uff1a\u201c\u738b\u632f\u6b64\u4eba\u631f\u5929\u5b50\u4ee5\u4ee4\u767e\u5b98\uff0c\u53c8\u5728\u671d\u4e2d\u904d\u690d\u515a\u7fbd\uff0c\u4ed6\u6709\u4ec0\u4e48\u4e8b\u60c5\u4e0d \u6562\u505a\uff0c\u8fde\u7687\u5e1d\u4e5f\u5f97\u770b\u4ed6\u989c\u8272\u3002\u518d\u8bf4\u5f53\u4eca\u7687\u4e0a\uff0c\u751a\u662f\u6015\u4e8b\uff0c\u82e5\u7136\u738b\u632f\u4e5f\u4e3b\u548c\u7684\u8bdd\uff0c\u8fd9\u548c\u7ea6\u65e9\u5df2 \u7b7e\u4e86\u3002\u201d\u4e91\u857e\u9053\uff1a\u201c\u671d\u5ef7\u4e4b\u4e8b\u975e\u6211\u6240\u77e5\uff0c\u6211\u4e5f\u60f3\u4e0d\u51fa\u5176\u4e2d\u9053\u7406\u3002\u201d\u5f20\u98ce\u5e9c\u9053\uff1a\u201c\u8fd8\u6709\u66f4\u5947\u602a \u7684\u5462\u3002\u738b\u632f\u975e\u4f46\u4e5f\u4e0d\u4e3b\u548c\uff0c\u800c\u4e14\u7adf\u4e3b\u5f20\u5c06\u8fd9\u6b21\u8499\u53e4\u7684\u6765\u4f7f\u6263\u4e0b\uff0c\u5012\u662f\u4e8e\u8c26\u4e0d\u80af\u8d5e\u6210\u3002\u738b\u632f\u7d20 \u6765\u6697\u52a9\u74e6\u523a\uff0c\u8fd9\u6b21\u7adf\u4f1a\u6709\u6b64\u4e3b\u5f20\uff0c\u671d\u5ef7\u767e\u5b98\uff0c\u65e0\u4e00\u4eba\u4e0d\u89c9\u5947\u602a\u3002\u201d\u4e91\u857e\u60f3\u8d77\u81ea\u5df1\u7237\u7237\u51fa\u4f7f\u74e6 \u523a\uff0c\u88ab\u6263\u7559\u4e0b\u6765\uff0c\u5728\u51b0\u5929\u96ea\u5730\u7267\u9a6c\u4e8c\u5341\u5e74\u4e4b\u4e8b\uff0c\u4e0d\u7981\u6124\u7136\u8bf4\u9053\uff1a\u201c\u4e24\u56fd\u76f8\u4e89\uff0c\u4e0d\u65a9\u6765\u4f7f\uff0c\u672c \u6765\u5c31\u4e0d\u8be5\u6263\u7559\u3002\u201d\u5f20\u98ce\u5e9c\u9053\uff1a\u201c\u8fd9\u4e8b\u7406\u6211\u4e5f\u660e\u767d\uff0c\u4e0d\u8fc7\u6263\u7559\u4f7f\u8005\u4e4b\u8bf4\uff0c\u51fa\u4e8e\u738b\u632f\u53e3\u4e2d\uff0c\u603b\u662f \u4ee4\u4eba\u5927\u60d1\u4e0d\u89e3\u3002\u201d \u3000\u3000\u5750\u8c08\u591a\u65f6\u5929\u8272\u5df2\u66ae\uff0c\u5f20\u98ce\u5e9c\u547d\u5bb6\u4eba\u5907\u996d\uff0c\u5e76\u5bf9\u4e91\u857e\u8bf4\u9053\uff1a\u201c\u4e91\u76f8\u516c\u5728\u4ec0\u4e48\u5730\u65b9\u4f4f\uff0c\u4e0d\u5acc \u8717\u5c45\u7684\u8bdd\uff0c\u8bf7\u642c\u5230\u820d\u4e0b\u5982\u4f55\uff1f\u201d\u4e91\u857e\u60f3\u8d77\u81ea\u5df1\u4e43\u662f\u5973\u5b50\uff0c\u8bf8\u591a\u4e0d\u4fbf\uff0c\u6025\u5fd9\u63a8\u8f9e\u3002\u5f20\u98ce\u5e9c\u5fc3 \u9053\uff1a\u201c\u6b64\u4eba\u600e\u7684\u6beb\u4e0d\u723d\u5feb\uff0c\u5012\u50cf\u4e00\u4e2a\u672a\u51fa\u5ac1\u7684\u95fa\u4e2d\u5c11\u5973\uff0c\u8fdc\u4e0d\u53ca\u5f20\u4e39\u67ab\u7684\u8c6a\u653e\u5feb\u4eba\u3002\u201d\u665a\u996d \u4e4b\u65f6\uff0c\u4e91\u857e\u95ee\u8d77\u4e8e\u8c26\u7684\u5730\u5740\uff0c\u5f20\u98ce\u5e9c\u7b11\u9053\uff1a\u201c\u4f60\u60f3\u89c1\u4e8e\u5927\u4eba\u4e48\uff1f\u4ed6\u8fd9\u51e0\u65e5\u5fd9\u4e8e\u56fd\u4e8b\uff0c\u5c31\u662f\u4ed6 \u80af\u89c1\u4f60\u6050\u6015\u95e8\u623f\u4e5f\u4e0d\u80af\u653e\u4f60\u8fdb\u53bb\u3002\u201d\u4f46\u5230\u5e95\u8fd8\u662f\u628a\u4e8e\u8c26\u7684\u5730\u5740\u8bf4\u4e86\u3002\u665a\u996d\u8fc7\u540e\uff0c\u4e91\u857e\u575a\u51b3\u544a \u8f9e\uff0c\u5f20\u98ce\u5e9c\u633d\u7559\u4e0d\u4f4f\uff0c\u9001\u5979\u51fa\u95e8\uff0c\u53c8\u63d0\u8d77\u5f20\u4e39\u67ab\uff0c\u7b11\u9053\uff1a\u201c\u82e5\u7136\u4f60\u90a3\u4f4d\u670b\u53cb\u4e5f\u5230\u4eac\u90fd\uff0c\u7b49\u5343 \u91cc\u5144\u4e2d\u4e86\u6b66\u72b6\u5143\uff0c\u6211\u4e00\u5b9a\u8981\u505a\u4e2a\u9c81\u4ef2\u8fde\uff0c\u66ff\u4ed6\u6446\u9152\u4e0e\u5343\u91cc\u5144\u8c08\u548c\u3002\u4f60\u81ea\u7136\u4e5f\u8981\u6765\u4f5c\u4e2a\u966a \u5ba2\u3002\u201d \u3000\u3000\u4e91\u857e\u5c34\u5c2c\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5f20\u5927\u4eba\u53e4\u9053\u70ed\u80a0\uff0c\u6211\u5148\u591a\u8c22\u4f60\u8fd9\u5e2d\u9152\u3002\u201d\u8f9e\u522b\u4e86\u5f20\u98ce\u5e9c\uff0c\u72ec\u81ea\u56de \u5230\u5ba2\u5e97\u3002 \u3000\u3000\u8fd9\u4e00\u591c\uff0c\u4e91\u857e\u8f97\u8f6c\u53cd\u4fa7\uff0c\u4e0d\u80fd\u5165\u7761\uff0c\u4e00\u4f1a\u513f\u60f3\u8d77\u4e86\u54e5\u54e5\uff0c\u4e00\u4f1a\u513f\u53c8\u60f3\u8d77\u4e86\u5f20\u4e39\u67ab\u3002\u60f3\u8d77 \u81ea\u5df1\u53ea\u6709\u8fd9\u4e48\u4e00\u4e2a\u54e5\u54e5\uff0c\u800c\u4eca\u8fdc\u9053\u6765\u4eac\uff0c\u504f\u504f\u4ed6\u53c8\u8c03\u5230\u5bab\u5185\u53bb\u5f53\u4f8d\u536b\uff0c\u867d\u8bf4\u7b49\u4ed6\u4e2d\u4e86\u6b66\u72b6 \u5143\uff0c\u53ef\u4ee5\u76f8\u89c1\uff0c\u4f46\u4e8b\u60c5\u5230\u5e95\u6d89\u832b\uff0c\u4ed6\u4e2d\u4e0d\u4e86\u53c8\u600e\u4e48\u76f8\uff1f\u4e2d\u4e86\u4e4b\u540e\uff0c\u53e6\u751f\u5176\u4ed6\u679d\u8282\u53c8\u600e\u4e48\u6837\uff1f \u4e0d\u7981\u6697\u81ea\u53f9\u9053\uff1a\u201c\u6211\u600e\u751f\u5982\u6b64\u547d\u82e6\uff0c\u8fde\u8fd9\u4e16\u4e0a\u552f\u4e00\u7684\u4eb2\u4eba\u4e5f\u89c1\u4e0d\u7740\u3002\u201d\u5fc3\u4e2d\u60f3\u8d77\u4e86\u201c\u552f\u4e00\u7684 \u4eb2\u4eba\u201d\u8fd9\u51e0\u4e2a\u5b57\uff0c\u4e0d\u77e5\u600e\u7684\uff0c\u5ffd\u7136\u53c8\u60f3\u8d77\u5f20\u4e39\u67ab\u3002\u5f20\u4e39\u67ab\u867d\u7136\u4e0d\u662f\u5979\u7684\u4eb2\u4eba\uff0c\u4f46\u4e91\u857e\u6bcf\u6b21\u60f3 \u8d77\u4ed6\u7684\u540d\u5b57\uff0c\u4e0d\u77e5\u600e\u7684\u5374\u603b\u6709\u4e00\u79cd\u4eb2\u5207\u4e4b\u611f\uff0c\u8033\u4e2d\u53c8\u60f3\u8d77\u5f20\u98ce\u5e9c\u7684\u8bdd\uff0c\u4e0d\u7981\u82e6\u7b11\u53f9\u9053\uff1a\u201c\u4f60 \u54ea\u91cc\u77e5\u9053\u6211\u5bb6\u4e0e\u4ed6\u4ec7\u6df1\u5982\u6d77\uff0c\u60f3\u529d\u6211\u5144\u957f\u4e0e\u4ed6\u548c\u89e3\uff0c\u8fd9\u82e6\u5fc3\u53ea\u6050\u662f\u767d\u8d39\u4e86\u3002\u201d \u3000\u3000\u60f3\u8d77\u4e86\u5f20\u4e39\u67ab\uff0c\u53c8\u8054\u60f3\u5230\u4e8e\u8c26\uff0c\u4e91\u857e\u6478\u51fa\u5f20\u4e39\u67ab\u6258\u5979\u8f6c\u4ea4\u4e8e\u8c26\u7684\u4fe1\uff0c\u5bf9\u7740\u4fe1\u5c01\u4e0a\u90a3\u51e0\u4e2a \u9f99\u98de\u51e4\u821e\u7684\u5b57\uff0c\u5982\u89c1\u5176\u4eba\u3002\u4e91\u857e\u5fc3\u9053\uff1a\u201c\u5f20\u4e39\u67ab\u521d\u6b21\u5165\u5173\uff0c\u600e\u4f1a\u8ba4\u8bc6\u4e8e\u8c26\uff1f\u5374\u5199\u4fe1\u4ecb\u7ecd\u6211\u53bb \u89c1\u4ed6\uff1f\u201d\u4f46\u60f3\u8d77\u5f20\u4e39\u67ab\u4e3a\u4eba\u867d\u7136\u72c2\u653e\uff0c\u505a\u4e8b\u5374\u751a\u7f1c\u5bc6\uff0c\u4ece\u6765\u4e0d\u51fa\u5dee\u9519\uff0c\u4e5f\u4ece\u6765\u4e0d\u8bf4\u8c0e\u8bdd\uff0c\u4ed6 \u65e2\u7136\u80fd\u5199\u8fd9\u5c01\u4fe1\uff0c\u5176\u4e2d\u5fc5\u6709\u9053\u7406\u3002\u53c8\u60f3\u9053\uff1a\u201c\u53cd\u6b63\u6211\u4e5f\u6ca1\u6709\u522b\u7684\u95e8\u8def\u53bb\u89c1\u4e8e\u8c26\uff0c\u4e0d\u5982\u5c31\u62ff\u8fd9 \u5c01\u4fe1\u53bb\u8bd5\u8bd5\u3002\u55ef\uff0c\u95e8\u623f\u82e5\u4e0d\u653e\u6211\u8fdb\u53bb\u53c8\u600e\u4e48\u6837\uff1f\u96be\u9053\u4e5f\u50cf\u5728\u5f20\u5bb6\u4e00\u6837\uff0c\u786c\u95ef\u8fdb\u53bb\u4e48\uff1f\u4e8e\u8c26\u662f \u4e00\u54c1\u5927\u81e3\uff0c\u6d77\u5185\u94a6\u4ef0\u7684\u9601\u8001\uff0c\u8fd9\u53ef\u4e0d\u80fd\u80e1\u6765\u5440\u3002\u5440\uff0c\u6709\u4e86\uff0c\u53cd\u6b63\u6211\u6709\u4e00\u8eab\u8f7b\u8eab\u7684\u672c\u4e8b\uff0c\u5c31\u665a \u4e0a\u6084\u6084\u53bb\u89c1\u4ed6\u5427\u3002\u201d \u3000\u3000\u7b2c\u4e8c\u65e5\u4e91\u857e\u517b\u597d\u7cbe\u795e\uff0c\u665a\u4e0a\u4e09\u66f4\u65f6\u5206\uff0c\u6362\u4e0a\u591c\u884c\u8863\u670d\uff0c\u6084\u6084\u6e9c\u51fa\u5ba2\u5e97\uff0c\u6309\u5740\u5bfb\u5230\u4e8e\u5bb6\u3002 \u5728\u4e91\u857e\u60f3\u8c61\u4e4b\u4e2d\uff0c\u4e8e\u8c26\u4e43\u662f\u4e00\u54c1\u5927\u81e3\uff0c\u4f4f\u5b85\u5fc5\u662f\u5d07\u697c\u9ad8\u9601\uff0c\u5802\u7687\u5bcc\u4e3d\uff0c\u54ea\u77e5\u7adf\u662f\u4e00\u4e2a\u5e73\u5e38\u7684 \u56db\u5408\u9662\u5b50\uff0c\u53ea\u662f\u540e\u9762\u6709\u4e00\u4e2a\u5c0f\u5c0f\u7684\u82b1\u56ed\uff0c\u8981\u4e0d\u7136\u5c31\u4e0e\u4e00\u822c\u5c0f\u5eb7\u4e4b\u5bb6\u7684\u4f4f\u5b85\u6beb\u65e0\u4e24\u6837\u3002 \u3000\u3000\u4e91\u857e\u5fc3\u4e2d\u53f9\u9053\uff1a\u201c\u5230\u5e95\u662f\u4e00\u4ee3\u540d\u81e3\uff0c\u53ea\u770b\u4f4f\u5904\uff0c\u5c31\u53ef\u60f3\u89c1\u4ed6\u7684\u4e3a\u4eba\u4e86\u3002\u201d\u5f53\u4e0b\u8f7b\u8f7b\u4e00 \u8dc3\uff0c\u98de\u4e0a\u74e6\u9762\uff0c\u51e0\u95f4\u5e73\u623f\uff0c\u4e00\u76ee\u4e86\u7136\u3002\u53ea\u89c1\u9760\u7740\u82b1\u56ed\u7684\u90a3\u95f4\u623f\u5b50\uff0c\u4e09\u9762\u90fd\u7cca\u7740\u7eb1\u7a97\uff0c\u7a97\u68c2\u7eb5 \u6a2a\u4ea4\u9519\uff0c\u5206\u6210\u5927\u5c0f\u683c\u5f0f\u7684\u82b1\u7eb9\uff0c\u6bcf\u4e00\u683c\u90fd\u6709\u4e00\u65b9\u5c0f\u73bb\u7483\u9576\u5d4c\u7740\uff0c\u663e\u5f97\u751a\u4e3a\u96c5\u81f4\uff0c\u73bb\u7483\u5185\u706f\u5149 \u6d41\u6620\u751f\u8f89\uff0c\u6848\u5934\u6240\u4f9b\u517b\u7684\u6885\u82b1\uff0c\u758f\u5f71\u6a2a\u659c\uff0c\u4e5f\u8d34\u5728\u73bb\u7483\u7a97\u4e0a\u3002\u4e91\u857e\u5fc3\u9053\uff1a\u201c\u96c5\u4e3d\u7edd\u4fd7\uff0c\u771f\u4e0d \u50cf\u662f\u5bcc\u8d35\u4eba\u5bb6\uff0c\u8fd9\u95f4\u623f\u5b50\u4e00\u5b9a\u662f\u4e8e\u8c26\u7684\u4e66\u623f\u4e86\u3002\u623f\u4e2d\u8fd8\u6709\u706f\u706b\uff0c\u60f3\u4ed6\u672a\u66fe\u7761\u89c9\u3002\u201d\u653e\u8f7b\u811a \u6b65\uff0c\u8d70\u8fd1\u4e66\u623f\uff0c\u5ffd\u542c\u5f97\u623f\u4e2d\u6709\u8c08\u8bdd\u4e4b\u58f0\u3002\u4e91\u857e\u4e00\u542c\u4e4b\u4e0b\uff0c\u5fc3\u5934\u6709\u5982\u9e7f\u649e\uff0c\u8fd9\u7adf\u662f\u5f20\u4e39\u67ab\u7684\u58f0 \u97f3\u3002\u8fd9\u8be5\u4e0d\u662f\u68a6\u5883\u5427\uff1f\u4ed6\u600e\u4e48\u7a81\u7136\u53c8\u6765\u5230\u8fd9\u513f\uff1f\u4e91\u857e\u6628\u665a\u8fd8\u68a6\u89c1\u4ed6\uff0c\u800c\u4eca\u542c\u5230\u4ed6\u7684\u58f0\u97f3\u4e86\uff0c \u5374\u53c8\u4e0d\u60f3\u89c1\u4ed6\u3002\u53ef\u662f\u771f\u7684\u4e0d\u60f3\u89c1\u4ed6\u5417\uff1f\u4e0d\uff0c\u5979\u53c8\u662f\u591a\u4e48\u6e34\u60f3\u89c1\u4ed6\u4e00\u9762\u554a\uff0c\u53ea\u662f\u8fd9\u4e48\u5077\u5077\u77a7\u4ed6 \u4e00\u773c\u4e5f\u597d\u3002 \u3000\u3000\u4e91\u857e\u8f7b\u8f7b\u8d70\u8fd1\uff0c\u5077\u5077\u4e00\u77a7\uff0c\u7eb1\u7a97\u4e0a\u6620\u51fa\u4e24\u4e2a\u4eba\u5f71\uff0c\u5176\u4e2d\u4e4b\u4e00\u679c\u7136\u662f\u5f20\u4e39\u67ab\uff01\u6b63\u662f\uff1a \u3000\u3000\u78a7\u7eb1\u7a97\u4e0a\u706f\u513f\u6620\uff0c\u72b9\u6050\u76f8\u9022\u662f\u68a6\u4e2d\u3002"}),showOnHomepage:!0},{name:"\u7b2c\u5341\u4e94\u56de\u3000\u5978\u5ba6\u5f04\u6743\u6c89\u51a4\u8c01\u4e0e\u96ea\u3000\u64c2\u53f0\u4e89\u80dc\u4fa0\u58eb\u6697\u98de\u9488",content:(0,s.jsx)(s.Fragment,{children:"\u4e91\u857e\u77a7\u89c1\u78a7\u7eb1\u7a97\u4e0a\uff0c\u73b0\u51fa\u5f20\u4e39\u67ab\u7684\u4eba\u5f71\uff0c\u4e0d\u89c9\u5446\u4e86\u3002\u8fc7\u4e86\u597d\u4e00\u4f1a\u5b50\uff0c\u624d\u4ece\u8ff7\u60d8\u4e2d\u6e05\u9192\u8fc7 \u6765\uff0c\u6025\u5fd9\u8fce\u7740\u900f\u6709\u82b1\u9999\u7684\u665a\u98ce\uff0c\u5438\u4e86\u4e00\u53e3\u6c14\uff0c\u5f3a\u6444\u7cbe\u795e\uff0c\u4f0f\u5728\u7a97\u5916\u9759\u542c\u3002 \u3000\u3000\u53ea\u542c\u5f97\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u8131\u8131\u4e0d\u82b1\u867d\u7136\u662f\u74e6\u523a\u56fd\u541b\uff0c\u519b\u6743\u5374\u64cd\u5728\u4e5f\u5148\u7684\u624b\u4e0a\uff0c\u53e6\u5916\u963f\u523a\u77e5\u9662 \u4e5f\u6709\u4e00\u90e8\u5206\u5175\u529b\u3002\u6240\u4ee5\u74e6\u523a\u5176\u5b9e\u662f\u4e09\u5bb6\u5206\u7acb\u7684\u5c40\u9762\u3002\u738b\u632f\u8fd9\u6b21\u4e3b\u5f20\u6263\u7559\u963f\u523a\uff0c\u6211\u770b\u662f\u51fa\u4e8e\u4e5f \u5148\u7684\u6388\u610f\u3002\u201d\u4e8e\u8c26\u9053\uff1a\u201c\u8fd9\u5374\u662f\u4e3a\u4f55\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u501f\u5200\u6740\u4eba\u6d88\u9664\u52b2\u654c\u3002\u6211\u77e5\u9053\u4e5f\u5148\u6b64 \u4eba\uff0c\u91ce\u5fc3\u6781\u5927\uff0c\u4ee5\u6210\u5409\u601d\u6c57\u7684\u7ee7\u627f\u8005\u81ea\u5c45\uff0c\u4ed6\u8fdf\u65e9\u5fc5\u7136\u7be1\u4f4d\uff0c\u963f\u523a\u4e0e\u74e6\u523a\u56fd\u541b\u8131\u8131\u4e0d\u82b1\u6bd4\u8f83 \u63a5\u8fd1\uff0c\u4ed6\u5148\u9664\u4e86\u963f\u523a\uff0c\u5c06\u6765\u7be1\u4f4d\u5bb9\u6613\u5f97\u591a\u3002\u201d\u4e8e\u8c26\u53f9\u9053\uff1a\u201c\u542c\u541b\u4e4b\u8bdd\u987f\u5f00\u8305\u585e\u3002\u53ef\u53f9\u6211\u671d\u5bf9 \u4e8e\u654c\u60c5\uff0c\u6beb\u4e0d\u77e5\u6653\u3002\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u82e5\u7136\u74e6\u523a\u53d1\u751f\u5185\u8ba7\u4e4b\u4e8b\uff0c\u8fd9\u5c31\u662f\u660e\u671d\u4e4b\u798f\u4e86\u3002\u201d\u4e00\u58f0\u82e6 \u7b11\uff0c\u4ef0\u5934\u671b\u5411\u7a97\u5916\uff0c\u4e91\u857e\u6025\u5fd9\u7f29\u8eab\u85cf\u5728\u82b1\u4e2d\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u5f20\u4e39\u67ab\u4e0e\u660e\u671d\u5929\u5b50\u4e43\u662f\u4e16\u4ec7\uff0c\u4ed6 \u5374\u80af\u4e3a\u660e\u671d\u8bbe\u60f3\u3002\u201d\u53ea\u542c\u5f97\u5f20\u4e39\u67ab\u53c8\u9053\uff1a\u201c\u6fb9\u53f0\u706d\u660e\u5176\u5b9e\u4e43\u662f\u5728\u74e6\u523a\u571f\u751f\u7684\u6c49\u5b50\uff0c\u4ed6\u4e0e\u963f\u523a \u77e5\u9662\u4ea6\u76f8\u5904\u751a\u597d\uff0c\u6211\u6628\u65e5\u5df2\u4e0e\u4ed6\u76f8\u89c1\uff0c\u6c42\u4ed6\u4ee5\u5927\u4e49\u529d\u670d\u6211\u7684\u7236\u4eb2\uff0c\u63a8\u6ce2\u52a9\u6f9c\uff0c\u4ece\u4e2d\u70b9\u706b\uff0c\u4fc3 \u6210\u74e6\u523a\u5185\u8ba7\u3002\u201d\u4e8e\u8c26\u9053\uff1a\u201c\u4ee4\u5c0a\u80af\u4e48\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u5b9e\u4e0d\u76f8\u7792\uff0c\u4ed6\u786e\u6709\u62a2\u593a\u5927\u660e\u6c5f\u5c71\u4e4b \u5fd7\uff0c\u4f46\u4ed6\u4e5f\u672a\u66fe\u5fd8\u8bb0\u81ea\u5df1\u4e43\u662f\u6c49\u4eba\u3002\u6240\u4ee5\u6b64\u4e8b\u662f\u6210\u662f\u8d25\uff0c\u96be\u4ee5\u9006\u6599\u3002\u201d\u4e8e\u8c26\u5ffd\u9053\uff1a\u201c\u4e16\u5144\u4f55 \u4ee5\u4e0d\u4eb2\u81ea\u56de\u53bb\u529d\u8bf4\u4ee4\u5c0a\u3002\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u6211\u6b64\u6b21\u5165\u5173\uff0c\u8fd8\u6709\u4e00\u4ef6\u6781\u7d27\u8981\u4e4b\u4e8b\uff0c\u8981\u53d6\u5f97\u4e00\u4ef6\u5173 \u4e4e\u56fd\u8fd0\u7684\u5b9d\u7269\uff0c\u662f\u4ee5\u4e0d\u80fd\u5373\u523b\u56de\u53bb\u3002\u201d\u4e8e\u8c26\u53c8\u9053\uff1a\u201c\u671f\u671b\u74e6\u523a\u5185\u8ba7\uff0c\u7a76\u7adf\u662f\u4e2a\u672a\u53ef\u77e5\u4e4b\u6570\uff0c \u74e6\u523a\u5165\u4fb5\u5374\u5df2\u8feb\u5728\u7709\u776b\uff0c\u8fd9\u5374\u5982\u4f55\u662f\u597d\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u4e2d\u56fd\u4e4b\u5927\u6570\u5341\u4f4d\u4e8e\u74e6\u523a\uff0c\u82e5\u80fd\u4e07\u4f17 \u4e00\u5fc3\uff0c\u4f55\u6089\u5f3a\u654c\uff1f\u201d\u4e8e\u8c26\u9053\uff1a\u201c\u6015\u7684\u5c31\u662f\u4e0d\u80fd\u4e07\u4f17\u4e00\u5fc3\uff01\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u9aa0\u9a91\u5c06\u519b\u90ed\u767b\uff0c\u5175 \u90e8\u4e3b\u4e8b\u6768\u6d2a\uff0c\u5fa1\u6797\u519b\u5927\u7edf\u9886\u5f20\u98ce\u5e9c\u7b49\u90fd\u662f\u4e00\u5fc3\u4e3a\u56fd\u7684\u53ef\u7528\u4e4b\u4eba\uff0c\u5927\u4eba\u53ef\u4ee5\u65e9\u4e3a\u5e03\u7f6e\u3002\u738b\u632f\u6c14 \u7130\u867d\u9ad8\uff0c\u6743\u52bf\u867d\u5927\uff0c\u4f46\u5fe0\u5978\u4e4b\u8fa8\u5230\u5e95\u6df1\u5165\u4eba\u5fc3\uff0c\u5230\u4e86\u56fd\u8fd0\u5b58\u4ea1\u4e4b\u9645\uff0c\u5927\u4eba\u632f\u81c2\u4e00\u547c\uff0c\u81ea\u5fc5\u56db \u65b9\u54cd\u5e94\uff0c\u738b\u632f\u4e00\u5978\u5ba6\u8033\uff0c\u7109\u80fd\u87b3\u81c2\u6321\u8f66\uff0c\u6bc1\u706d\u56fd\u8109\uff1f\u201d\u4e8e\u8c26\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u6210\u8d25\u96be\u77e5\uff0c\u6211\u53ea\u6c42 \u5c3d\u4e00\u5df1\u4e4b\u529b\u7f62\u4e86\u3002\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u90aa\u4e0d\u80dc\u6b63\uff0c\u65e0\u53ef\u7591\u60d1\uff01\u201d\u4e8e\u8c26\u9053\uff1a\u201c\u4e16\u5144\u89c1\u4e8b\u751a\u660e\uff0c\u6df1\u8c0b \u8fdc\u8651\uff0c\u5b9e\u662f\u5f53\u4e16\u5947\u624d\uff0c\u4f55\u4ee5\u4e0d\u80af\u4e3a\u671d\u5ef7\u6240\u7528\uff1f\u201d\u5f20\u4e39\u67ab\u4e00\u7b11\u8bf4\u9053\uff1a\u201c\u4eba\u5404\u6709\u5fd7\uff0c\u518d\u8bf4\u7537\u513f\u62a5 \u56fd\uff0c\u53c8\u4f55\u5fc5\u7acb\u4e8e\u671d\u5ef7\uff1f\u201d\u4e8e\u8c26\u4e0d\u89c9\u9ed8\u7136\u3002\u5f20\u4e39\u67ab\u81ea\u77e5\u8bf4\u5f97\u8fc7\u5206\uff0c\u53c8\u4e00\u7b11\u8bf4\u9053\uff1a\u201c\u4f3c\u5927\u4eba\u662f\u671d \u5ef7\u67f1\u77f3\uff0c\u90a3\u81ea\u7136\u53c8\u5f53\u522b\u8bba\u3002\u201d \u3000\u3000\u4e91\u857e\u5728\u5916\u9762\u542c\u5f97\u5f20\u4e39\u67ab\u4e0e\u4e8e\u8c26\u4f83\u4f83\u800c\u8c08\uff0c\u5256\u6790\u654c\u60c5\uff0c\u7b56\u5212\u56fd\u4e8b\uff0c\u4e00\u7247\u62a5\u56fd\u7684\u4e39\u5fc3\uff0c\u63ed\u7136 \u5982\u89c1\u3002\u4e0d\u89c9\u53c8\u662f\u60ca\u5947\u53c8\u662f\u6b22\u559c\u3002\u60ca\u5947\u8005\u4e43\u662f\u5f20\u4e39\u67ab\u7684\u884c\u4e8b\uff0c\u4eba\u6240\u83ab\u6d4b\uff1b\u6b22\u559c\u8005\u4e43\u81ea\u5df1\u679c\u7136\u4e0d \u66fe\u770b\u9519\u4e86\u4eba\uff0c\u5f20\u4e39\u67ab\u679c\u7136\u662f\u4e2a\u4e00\u8154\u70ed\u8840\u7684\u5947\u7537\u5b50\u3002\u987f\u65f6\u95f4\u5ffd\u89c9\u5f97\u4e24\u5bb6\u7684\u79ef\u6028\uff0c\u201c\u7978\u5ef6\u540e \u4ee3\u201d\uff0c\u5b9e\u7b49\u4e8e\u9e21\u866b\u4e4b\u4e89\uff0c\u751a\u662f\u65e0\u8c13\u3002 \u3000\u3000\u53ea\u542c\u5f97\u5f20\u4e39\u67ab\u53c8\u9053\uff1a\u201c\u6211\u6b64\u6b21\u5165\u4eac\uff0c\u5192\u9669\u8c12\u89c1\uff0c\u627f\u5927\u4eba\u6df1\u4fe1\u4e0d\u7591\uff0c\u5f02\u65e5\u82e5\u6709\u6240\u9700\uff0c\u7c89\u8eab \u788e\u9aa8\uff0c\u65e0\u4ee5\u4e3a\u62a5\u3002\u201d\u4e8e\u8c26\u8a00\u9053\uff1a\u201c\u4e3a\u4e86\u83bd\u83bd\u795e\u5dde\uff0c\u4e16\u5144\u62a5\u56fd\u5373\u662f\u62a5\u6211\u3002\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u7537\u513f \u5f53\u62a5\u56fd\uff0c\u4f55\u5fc5\u518d\u53ee\u549b\u3002\u591c\u5df2\u6df1\uff0c\u5927\u4eba\u4e5f\u8be5\u5b89\u6b47\u4e86\uff0c\u665a\u751f\u544a\u8f9e\u3002\u201d \u3000\u3000\u4e8e\u8c26\u6c89\u541f\u6709\u9877\u5ffd\u9053\uff1a\u201c\u4f60\u6211\u4f55\u65e5\u518d\u89c1\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u5f53\u89c1\u4e4b\u65f6\u6211\u81ea\u4f1a\u524d\u6765\u76f8\u89c1\u3002\u201d\u4e8e \u8be6\u9053\uff1a\u201c\u53e4\u4eba\u8bed\u4e91\uff1a\u767d\u5934\u5982\u65b0\uff0c\u503e\u76d6\u5982\u6545\u3002\uff08\u7fbd\u751f\u6ce8\uff1a\u8fd9\u4e24\u53e5\u8bdd\u7684\u610f\u601d\u662f\uff1a\u6709\u4e9b\u4eba\u505a\u4e86\u4e00\u8f88 \u5b50\u7684\u670b\u53cb\uff0c\u5927\u5bb6\u5934\u53d1\u90fd\u767d\u4e86\uff0c\u5374\u8fd8\u4f3c\u521d\u76f8\u8bc6\u7684\u4e00\u6837\uff0c\u5f7c\u6b64\u5e76\u4e0d\u4e86\u89e3\u3002\u6709\u4e9b\u4eba\u53ea\u5728\u8def\u4e0a\u76f8\u89c1\u4e00 \u9762\uff0c\u505c\u8f66\u4e0b\u6765\uff0c\u63ed\u5f00\u8f66\u76d6\u4ea4\u8c08\uff0c\u5374\u4f3c\u591a\u5e74\u7684\u8001\u670b\u53cb\u4e00\u822c\u3002\u6240\u4ee5\u53cb\u8c0a\u7684\u6df1\u6d45\uff0c\u5e76\u4e0d\u5728\u4e8e\u65f6\u95f4\u7684 \u4e45\u6682\u800c\u5728\u4e8e\u4e86\u89e3\u4e0e\u4e0d\u4e86\u89e3\u3002\uff09\u6b64\u8bdd\u771f\u662f\u4e0d\u5047\u3002\u6211\u5230\u4e86\u665a\u5e74\uff0c\u8fd8\u80fd\u7ed3\u8bc6\u4e16\u5144\u8fd9\u6837\u4e00\u4e2a\u5fd8\u5e74\u77e5 \u5df1\uff0c\u5b9e\u662f\u5927\u5feb\u5e73\u751f\u3002\u4e16\u5144\u7434\u68cb\u8bd7\u753b\uff0c\u65e0\u4e00\u4e0d\u4f73\uff0c\u6211\u524d\u65e5\u5f97\u4e86\u4e00\u5e45\u8d75\u4f51\u7684\u300a\u6881\u7236\u541f\u56fe\u300b\uff0c\u70e6\u4e16 \u5144\u66ff\u6211\u5199\u4e00\u9996\u8bd7\uff0c\u4ee5\u4e3a\u4ed6\u65e5\u4e4b\u601d\uff0c\u4e16\u5144\u53ef\u80af\u6168\u5141\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u957f\u8005\u6709\u547d\uff0c\u5c82\u6562\u63a8\u8f9e\uff1f\u5c31 \u7528\u90d1\u601d\u8096\u7684\u8bd7\u53e5\u597d\u4e86\u3002\u201d\u4e91\u857e\u5728\u5916\u9762\u542c\u5f97\u72fc\u6beb\u626b\u7eb8\u5982\u6625\u8695\u98df\u53f6\u4e4b\u58f0\uff0c\u60f3\u89c1\u4ed6\u8fd0\u7b14\u5982\u98de\u7684\u8c6a \u6982\u3002\u4e0d\u4e00\u523b\uff0c\u53ea\u542c\u5f97\u4e8e\u8c26\u541f\u9053\uff1a \u3000\u3000\u6101\u91cc\u9ad8\u6b4c\u6881\u7236\u541f\uff0c\u72b9\u5982\u91d1\u7389\u621b\u5546\u95f4\u3002 \u3000\u3000\u5341\u5e74\u52fe\u8df5\u4ea1\u5434\u8ba1\uff0c\u4e03\u65e5\u5305\u80e5\u54ed\u695a\u5fc3\u3002 \u3000\u3000\u79cb\u9001\u65b0\u9e3f\u54c0\u7834\u56fd\uff0c\u4e66\u884c\u9965\u864e\u556e\u7a7a\u6797\u3002 \u3000\u3000\u80f8\u4e2d\u6709\u8a93\u6df1\u5982\u6d77\uff0c\u80af\u4f7f\u795e\u5dde\u7adf\u9646\u6c89\u3002 \u3000\u3000\u4e8e\u8c26\u8bfb\u5b8c\u4e4b\u540e\uff0c\u51fb\u8282\u8d5e\u9053\uff1a\u201c\u5bc4\u6258\u9065\u6df1\uff0c\u7684\u662f\u597d\u8bd7\u3002\u4e0d\u77e5\u6b64\u8bd7\u4e5f\u662f\u4e16\u5144\u5fc3\u80f8\u7684\u6292\u5199 \u4e48\uff1f\u201d\u5f20\u4e39\u67ab\u5ffd\u5730\u4e00\u9635\u72c2\u7b11\uff0c\u91cd\u590d\u541f\u9053\uff1a\u201c\u80f8\u4e2d\u6709\u8a93\u6df1\u5982\u6d77\uff0c\u80af\u4f7f\u795e\u5dde\u7adf\u9646\u6c89\uff1f\u665a\u751f\u65e0\u9152\u4ea6 \u9189\uff0c\u8bf7\u5927\u4eba\u6055\u6211\u72c2\u6001\u6bd5\u9732\u3002\u540e\u4f1a\u6709\u671f\uff0c\u8bf7\u5927\u4eba\u4e0d\u5fc5\u9001\u4e86\u3002\u201d\u63a5\u7740\u4fbf\u542c\u5f97\u4e8e\u8c26\u5f00\u95e8\uff0c\u5f20\u4e39\u67ab\u811a \u6b65\u8d70\u51fa\u4e4b\u58f0\u3002 \u3000\u3000\u8fd9\u970e\u90a3\u95f4\uff0c\u4e91\u857e\u60c5\u601d\u7eb7\u4e71\uff0c\u89c1\u5462\u8fd8\u662f\u4e0d\u89c1\uff0c\u4e00\u65f6\u95f4\u5b9e\u662f\u96be\u4ee5\u51b3\u5b9a\u3002\u53ea\u542c\u5f97\u5f20\u4e39\u67ab\u5df2\u8d70\u51fa \u4e66\u623f\uff0c\u6b63\u5728\u8bf7\u4e8e\u8c26\u7559\u6b65\uff0c\u4e91\u857e\u7a81\u7136\u60f3\u8d77\u5f20\u4e39\u67ab\u7684\u8bdd\uff1a\u201c\u5f53\u7b11\u4fbf\u7b11\uff0c\u5f53\u54ed\u4fbf\u54ed\uff0c\u4f55\u5fc5\u5f3a\u4ef0\uff1f\u201d \u60f3\u9053\uff1a\u201c\u90a3\u4e48\u6211\u4ea6\u5e94\u5f53\u89c1\u4fbf\u89c1\uff0c\u4f55\u5fc5\u987e\u8651\u4eba\u8a00\uff1f\u201d\u6c14\u8840\u4e0a\u6d8c\uff0c\u5fc3\u5934\u5982\u711a\uff0c\u6b63\u5f85\u4e00\u8dc3\u800c\u51fa\uff0c\u5ffd \u89c9\u80cc\u540e\u5fae\u98ce\u98d2\u7136\uff0c\u8170\u95f4\u4f3c\u7ed9\u4eba\u78b0\u4e86\u4e00\u4e0b\uff0c\u4e91\u857e\u628a\u624b\u4e00\u6478\uff0c\u90a3\u628a\u5e08\u7236\u6240\u8d50\u7684\u9752\u51a5\u5b9d\u5251\u7adf\u5df2\u7ed9\u4eba \u62d4\u53bb\u53ea\u5269\u4e0b\u4e86\u4e00\u4e2a\u5251\u9798\u3002\u4e91\u857e\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u4e0d\u6562\u53eb\u558a\uff0c\u53cd\u8eab\u4e00\u8dc3\uff0c\u53cc\u638c\u5de6\u53f3\u4e00\u626b\uff0c\u5ffd\u7136 \u624b\u81c2\u4e00\u9178\uff0c\u773c\u524d\u4eba\u5f71\u4e00\u6643\uff0c\u4e91\u857e\u7a7a\u6709\u4e00\u8eab\u6b66\u827a\uff0c\u7adf\u7136\u51b7\u4e0d\u9632\u7ed9\u4eba\u70b9\u4e86\u9ebb\u7a74\uff0c\u631f\u8d77\u4fbf\u8dd1\uff0c\u558a\u4e5f \u558a\u4e0d\u51fa\u6765\uff0c\u8033\u8fb9\u4f3c\u4f9d\u7a00\u542c\u5f97\u5f20\u4e39\u67ab\u53eb\u9053\uff1a\u201c\u653e\u4ed6\u4e0b\u6765\uff0c\u653e\u4ed6\u4e0b\u6765\u3002\u5c0f\u5144\u5f1f\uff0c\u5c0f\u5144\u5f1f\uff0c\u679c\u771f\u662f \u4f60\u4e48\uff1f\u201d\u5f20\u4e39\u67ab\u4f3c\u662f\u4ece\u540e\u9762\u6025\u901f\u8ffd\u6765\uff0c\u53ef\u662f\u90a3\u4eba\u811a\u6b65\u5feb\u5230\u65e0\u6cd5\u5f62\u5bb9\uff0c\u4e91\u857e\u7ed9\u4ed6\u631f\u7740\uff0c\u5c31\u5982\u817e \u4e91\u9a7e\u96fe\u4e00\u822c\u3002\u5f20\u4e39\u67ab\u7684\u8f7b\u529f\u5df2\u662f\u6c5f\u6e56\u7f55\u89c1\u7684\u4e0a\u4e0a\u529f\u592b\uff0c\u800c\u90a3\u4eba\u7adf\u6bd4\u5f20\u4e39\u67ab\u8fd8\u5feb\uff0c\u7247\u523b\u4e4b\u95f4\uff0c \u5df2\u628a\u5f20\u4e39\u67ab\u7529\u5728\u80cc\u540e\u3002 \u3000\u3000\u4e91\u857e\u53c8\u60ca\u53c8\u607c\uff0c\u5374\u662f\u6323\u624e\u4e0d\u5f97\uff0c\u5ffd\u89c9\u90a3\u4eba\u5728\u81ea\u5df1\u80cc\u4e0a\u62cd\u4e86\u4e00\u4e0b\uff0c\u968f\u5373\u628a\u81ea\u5df1\u8f7b\u8f7b\u653e\u5728\u5730 \u4e0a\u3002\u4e91\u857e\u987f\u89c9\u6c14\u8840\u6d41\u901a\uff0c\u56db\u80a2\u6d3b\u52a8\uff0c\u6b63\u60f3\u53d1\u4f5c\uff0c\u62ac\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u628a\u81ea\u5df1\u631f\u6765\u7684\u4eba\uff0c\u7adf\u662f\u6628\u65e5 \u6240\u89c1\u7528\u5927\u529b\u91d1\u521a\u624b\u5c06\u6fb9\u53f0\u706d\u660e\u6253\u4f24\u7684\u90a3\u4e2a\u602a\u8001\u5934\u513f\uff01 \u3000\u3000\u4e91\u857e\u9a82\u58f0\u5df2\u5230\u53e3\u8fb9\u53c8\u541e\u4e86\u56de\u53bb\uff0c\u90a3\u602a\u8001\u5934\u513f\u5c06\u9752\u51a5\u5b9d\u5251\u634f\u5728\u624b\u4e2d\u53cd\u590d\u628a\u73a9\uff0c\u4e00\u53cc\u70af\u70af\u6709 \u795e\u7684\u773c\u775b\uff0c\u76ef\u7740\u4e91\u857e\uff0c\u84e6\u5730\u53d1\u58f0\u95ee\u9053\uff1a\u201c\u4f60\u7684\u5e08\u7236\u662f\u4e0d\u662f\u5ddd\u5317\u5c0f\u5bd2\u5c71\u7684\u98de\u5929\u9f99\u5973\u53f6\u76c8\u76c8\uff1f\u201d \u4e91\u857e\u9053\uff1a\u201c\u6b63\u662f\u3002\u201d\u90a3\u602a\u8001\u5934\u513f\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u5df2\u6709\u5341\u4f59\u5e74\u6ca1\u89c1\u5979\u4e86\uff0c\u89c1\u5251\u5982\u89c1\u4eba\uff0c \u5979\u65e2\u80af\u5c06\u9752\u51a5\u5b9d\u5251\u4ed8\u6258\u4e0e\u4f60\uff0c\u76f8\u6765\u4f60\u5e08\u7956\u8981\u5979\u505a\u7684\u4e24\u4e8b\u60c5\u90fd\u505a\u597d\u4e86\u3002\u201d\u5341\u4e8c\u5e74\u524d\uff0c\u98de\u5929\u9f99\u5973 \u72af\u4e86\u4e0e\u8c22\u5929\u534e\u79c1\u76f8\u6388\u53d7\u5251\u6cd5\u4e4b\u7f6a\uff0c\u88ab\u7384\u673a\u9038\u58eb\u7f5a\u5979\u5728\u5c0f\u5bd2\u5c71\u9762\u58c1\u5341\u4e94\u5e74\uff0c\u5e76\u9650\u5979\u5728\u5341\u4e94\u5e74\u95f4 \u505a\u597d\u4e24\u4ef6\u4e8b\u60c5\uff1a\u4e00\u4ef6\u662f\u8981\u7ec3\u6210\u4e24\u79cd\u6700\u96be\u7ec3\u7684\u6b66\u827a\uff1b\u4e00\u4ef6\u662f\u8981\u8c03\u6559\u51fa\u4e00\u4e2a\u7cbe\u901a\u201c\u767e\u53d8\u7384\u673a\u5251 \u6cd5\u201d\u7684\u5f92\u5f1f\uff0c\u6b64\u4e8b\u4e91\u857e\u66fe\u542c\u5e08\u7236\u8bf4\u8fc7\u3002\u6b64\u65f6\u542c\u8fd9\u602a\u8001\u5934\u513f\u63d0\u8d77\uff0c\u5bf9\u4ed6\u7684\u8eab\u4efd\u518d\u65e0\u7591\u60d1\uff0c\u6025\u5fd9 \u53e9\u5934\u8bf7\u5b89\uff0c\u95ee\u9053\uff1a\u201c\u60a8\u8001\u53ef\u662f\u91d1\u521a\u624b\u8463\u5927\u5e08\u4f2f\u4e48\uff1f\u201d \u3000\u3000\u90a3\u602a\u8001\u5934\u513f\u6b63\u662f\u5927\u529b\u91d1\u521a\u624b\u8463\u5cb3\uff0c\u95fb\u8a00\u54c8\u54c8\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8fd9\u5973\u5a03\u513f\u4e5f\u806a\u660e\u5f97\u7d27\uff0c\u6628 \u65e5\u6211\u5728\u5f20\u98ce\u5e9c\u5bb6\u4e2d\u89c1\u4f60\u80cc\u7740\u8fd9\u628a\u5b9d\u5251\uff0c\u5df2\u5728\u7559\u795e\uff0c\u53ea\u56e0\u89c1\u4f60\u5973\u626e\u7537\u88c5\uff0c\u4e0d\u6562\u76f8\u8ba4\u3002\u679c\u7136\u4f60\u662f \u6211\u7684\u5e08\u4f84\u3002\u4f60\u53ef\u77e5\u9053\u6211\u4e3a\u4f55\u4e0d\u8bb8\u4f60\u52a8\u624b\u4e48\uff1f\u201d\u4e91\u857e\u832b\u7136\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u201d\u5fc3\u60f3\uff1a\u201c\u6211\u53ef\u5e76\u6ca1\u6709 \u60f3\u4e0e\u8c01\u52a8\u624b\u5440\u3002\u201d\u8463\u5cb3\u9053\uff1a\u201c\u4f60\u521a\u624d\u4e0d\u662f\u60f3\u8df3\u51fa\u53bb\u523a\u6740\u90a3\u4e2a\u5f20\u4e39\u67ab\u4e48\uff1f\u4f60\u82e5\u6740\u4ed6\uff0c\u4f60\u5c31\u9519 \u4e86\u3002\u201d\u4e91\u857e\u7ed9\u4ed6\u8bef\u4f1a\uff0c\u54ed\u7b11\u4e0d\u5f97\uff0c\u5374\u5c06\u9519\u5c31\u9519\u95ee\u9053\uff1a\u201c\u600e\u4e48\u9519\u4e86\uff1f\u201d\u8463\u5cb3\u9053\uff1a\u201c\u90a3\u5f20\u4e39\u67ab\u867d \u662f\u5f20\u5b97\u5468\u4e4b\u5b50\uff0c\u4f46\u542c\u5176\u8a00\u800c\u89c2\u5176\u884c\uff0c\u5374\u662f\u8d64\u5fc3\u4e3a\u56fd\u4e4b\u4eba\u3002\u6211\u6628\u65e5\u4e0e\u6fb9\u53f0\u706d\u660e\u6076\u6597\u4e4b\u540e\uff0c\u665a\u4e0a \u66fe\u5230\u8499\u53e4\u756a\u738b\u6240\u4f4f\u7684\u793c\u5ba2\u6808\u53bb\u63a2\u542c\uff0c\u6b63\u542c\u5f97\u5f20\u4e39\u67ab\u4e0e\u6fb9\u53f0\u706d\u660e\u8bf4\u8bdd\u3002\u539f\u6765\u4ed6\u4eec\u4e8c\u4eba\u6b63\u5728\u5546\u91cf \u4e00\u4ef6\u673a\u5bc6\u5927\u4e8b\uff0c\u8fd9\u4e8b\u4f60\u4e0d\u5fc5\u77e5\u9053\uff0c\u603b\u4e4b\u662f\u5bf9\u4e2d\u56fd\u6709\u5229\u7684\u4fbf\u662f\u4e86\u3002\u56e0\u6b64\u6211\u672c\u6765\u60f3\u518d\u6253\u6fb9\u53f0\u706d\u660e \u4e00\u638c\u7684\uff0c\u4e5f\u9976\u4e86\u4ed6\u4e86\u3002\u201d\u4e91\u857e\u5fc3\u4e2d\u6697\u7b11\u9053\uff1a\u201c\u6b64\u4e8b\u6211\u65e9\u5df2\u77e5\u4e86\u3002\u201d\u8463\u5cb3\u7eed\u9053\uff1a\u201c\u8bd5\u60f3\u4f60\u82e5\u6740 \u4ed6\uff0c\u5c82\u4e0d\u662f\u94f8\u6210\u5927\u9519\u3002\u518d\u8bf4\u4f60\u7684\u6b66\u529f\u4e5f\u4e0d\u662f\u4ed6\u7684\u5bf9\u624b\uff0c\u5514\uff0c\u4f60\u8fd8\u6ca1\u6709\u89c1\u4ed6\u9732\u8fc7\u672c\u9886\u5427\uff1f\u201d\u4e91 \u857e\u9053\uff1a\u201c\u66fe\u89c1\u8fc7\u4e00\u9cde\u534a\u722a\u3002\u201d\u8463\u5cb3\u76b1\u7709\u8bf4\u9053\uff1a\u201c\u5514\uff0c\u90a3\u5c31\u66f4\u4e0d\u8be5\u4e86\u3002\u6b66\u6797\u4fa0\u58eb\u4e0d\u8be5\u5f92\u901e\u8840\u6c14 \u4e4b\u52c7\uff0c\u5e94\u8be5\u91cf\u529b\u800c\u4e3a\u3002\u4f60\u53eb\u4ec0\u4e48\u540d\u5b57\uff1f\u201d\u4e91\u857e\u8bf4\u9053\uff1a\u201c\u6211\u53eb\u4e91\u857e\u3002\u201d\u8463\u5cb3\u201c\u554a\u5440\u201d\u4e00\u58f0\uff0c \u9053\uff1a\u201c\u8e0f\u7834\u94c1\u978b\u65e0\u89c5\u5904\uff0c\u5f97\u6765\u5168\u4e0d\u8d39\u529f\u592b\uff0c\u539f\u6765\u4f60\u5c31\u662f\u4e91\u91cd\u7684\u59b9\u59b9\uff0c\u8fd9\u771f\u662f\u592a\u5999\u4e86\uff01\u5514\uff0c\u602a \u4e0d\u5f97\u4f60\u660e\u77e5\u4e0d\u654c\u4e5f\u8981\u523a\u6740\u5f20\u4e39\u67ab\u4e86\u3002\u201d \u3000\u3000\u4e91\u857e\u54ed\u7b11\u4e0d\u5f97\uff0c\u8463\u5cb3\u53c8\u9053\uff1a\u201c\u6628\u665a\u6211\u542c\u5f97\u5f20\u4e39\u67ab\u8bf4\u4eca\u665a\u8981\u6765\u4f1a\u4e8e\u8c26\uff0c\u6545\u6b64\u6211\u4e5f\u8ddf\u6765\uff0c\u4f46 \u8def\u4e0a\u53e6\u6709\u70b9\u5c0f\u4e8b\u963b\u6401\u4e86\u4e00\u4e0b\uff0c\u5230\u4e86\u4e8e\u5bb6\uff0c\u4ed6\u6b63\u8d70\u51fa\uff0c\u4e0d\u77e5\u4ed6\u4eec\u8bf4\u4e86\u4e9b\u4ec0\u4e48\uff1f\u4f60\u542c\u5230\u5417\uff1f\u201d\u4e91 \u857e\u65e0\u5fc3\u7ec6\u8bf4\u9053\uff1a\u201c\u6211\u4e5f\u542c\u4e0d\u6e05\u695a\uff0c\u53ea\u542c\u5f97\u4ec0\u4e48\u74e6\u523a\u554a\uff0c\u4e2d\u56fd\u554a\uff0c\u8981\u5f04\u5f97\u74e6\u523a\u5185\u8ba7\u554a\u7b49\u7b49\uff0c\u7f57 \u91cc\u7f57\u5506\uff0c\u8bb0\u4e0d\u5f97\u90a3\u4e48\u591a\u4e86\u3002\u201d\u8463\u5cb3\u9053\uff1a\u201c\u5514\uff0c\u90a3\u5c31\u662f\u4e86\u3002\u542c\u8bf4\u4e91\u91cd\u4e5f\u5728\u6b64\u5730\uff0c\u4f60\u4eec\u5144\u59b9\u89c1\u8fc7 \u9762\u4e86\u4e48\uff1f\u201d \u3000\u3000\u4e91\u857e\u9eef\u7136\u8bf4\u9053\uff1a\u201c\u54e5\u54e5\u5df2\u88ab\u8c03\u8fdb\u5bab\u4e2d\u5f53\u4f8d\u536b\u4e86\u3002\u201d\u8463\u5cb3\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u8fd9\u5b69\u5b50\u5fd7\u5411\u4e0d \u9519\uff0c\u4f46\u4ed6\u4ee5\u4e3a\u5148\u8981\u5728\u671d\u5ef7\u56fe\u4e2a\u51fa\u8eab\u7136\u540e\u624d\u80fd\u4e3a\u7956\u7236\u62a5\u4ec7\u4e3a\u56fd\u5bb6\u96ea\u803b\uff0c\u8fd9\u60f3\u6cd5\u5374\u9519\u4e86\u3002\u201d\u4e91\u857e \u9053\uff1a\u201c\u6743\u81e3\u5f53\u9053\uff0c\u674e\u5e7f\u65e0\u529f\uff0c\u5927\u5e08\u4ef6\u8bf4\u7684\u662f\u3002\u201d\u8fd9\u4e24\u53e5\u662f\u8463\u5cb3\u5199\u7ed9\u91d1\u5200\u5468\u5065\u4fe1\u4e2d\u7684\u8bdd\u3002\u8463\u5cb3 \u9053\uff1a\u201c\u55ef\uff0c\u90a3\u5c01\u4fe1\u4f60\u4e5f\u770b\u8fc7\u4e86\u3002\u53ef\u60dc\u91cd\u513f\u5c31\u4e0d\u660e\u767d\u8fd9\u4e2a\u9053\u7406\u3002\u8fd9\u4e48\u8bf4\u6765\uff0c\u6211\u4eec\u662f\u96be\u4ee5\u89c1\u5230\u4ed6 \u4e86\uff1f\u201d\u4e91\u857e\u9053\uff1a\u201c\u534a\u6708\u4e4b\u540e\u6216\u6709\u673a\u4f1a\u3002\u201d\u5c06\u5f20\u98ce\u5e9c\u7684\u63a8\u6d4b\u544a\u8bc9\u8463\u5cb3\u3002\u8463\u5cb3\u9053\uff1a\u201c\u6211\u6b64\u6b21\u7a81\u7136 \u56de\u6765\uff0c\u4e43\u662f\u4e3a\u4e86\u4e00\u4ef6\u7d27\u6025\u4e4b\u4e8b\uff0c\u8981\u89c1\u4f60\u4f60\u7684\u5e08\u7956\uff0c\u6240\u4ee5\u8fde\u6155\u540d\u5df2\u4e45\u7684\u91d1\u5200\u5468\u5065\u4e5f\u65e0\u6687\u62dc\u8bbf\u3002 \u8fd9\u6b21\u7ecf\u8fc7\u4eac\u5e08\uff0c\u987a\u4fbf\u63a2\u542c\u4e00\u4e0b\u91cd\u513f\u7684\u6d88\u606f\uff0c\u4e5f\u4e0d\u80fd\u4e45\u7559\u7684\u3002\u4f60\u89c1\u5230\u54e5\u54e5\u65f6\uff0c\u53ef\u5c06\u6211\u7684\u8bdd\u8f6c\u544a \u4e8e\u4ed6\u3002\u201d\u4e91\u857e\u70b9\u5934\u7b54\u5e94\u3002\u8463\u5cb3\u53c8\u9053\uff1a\u201c\u4f60\u4eec\u8981\u62a5\u5f20\u5bb6\u7684\u4e16\u4ec7\uff0c\u6309\u6b66\u6797\u60ef\u4f8b\uff0c\u6b64\u4e8b\u6211\u4e0d\u80fd\u7ba1\u3002 \u4f46\u5f20\u4e39\u67ab\u4e43\u662f\u6211\u8f88\u4e2d\u4eba\uff0c\u800c\u4e14\u4e0a\u4ee3\u4e4b\u4ec7\u4ea6\u4e0e\u4ed6\u65e0\u5173\uff0c\u82e5\u80fd\u5316\u89e3\u5c31\u5316\u89e3\u5427\u3002\u4e0d\u8fc7\u4f60\u54e5\u54e5\u4e43\u662f\u957f \u5b50\uff0c\u62a5\u4ec7\u4e4b\u4e8b\uff0c\u4f60\u8be5\u542c\u4ed6\u7684\u610f\u601d\u3002\u6211\u7684\u8bdd\u8bf4\u4f60\u53ea\u987b\u544a\u8bc9\u4ed6\uff0c\u8ba9\u4ed6\u8003\u8651\u3002\u201d\u6b66\u6797\u4e2d\u7684\u60ef\u4f8b\uff0c\u51e1 \u6d89\u53ca\u7236\u6bcd\u7956\u5148\u4e4b\u4ec7\u7684\uff0c\u5373\u5e08\u7236\u5c0a\u957f\u4ea6\u53ea\u80fd\u529d\u89e3\uff0c\u4e0d\u80fd\u7528\u547d\u4ee4\u53bb\u963b\u6b62\u4e0d\u62a5\uff0c\u662f\u4ee5\u8463\u5cb3\u6709\u8fd9\u756a \u8bdd\u3002 \u3000\u3000\u8463\u5cb3\u53c8\u9053\uff1a\u201c\u81f3\u4e8e\u90a3\u5f20\u5b97\u5468\u662f\u597d\u662f\u574f\uff0c\u6211\u5c1a\u672a\u77e5\u3002\u5929\u534e\u4e09\u5f1f\u56f0\u5728\u80e1\u5bab\uff0c\u4ed6\u7684\u786e\u5b9e\u6d88\u606f\uff0c \u4ea6\u4e0d\u77e5\u9053\u3002\u6211\u8fd9\u6b21\u53bb\u89c1\u4f60\u7684\u5e08\u7956\u60f3\u8bf7\u4ed6\u63d0\u524d\u653e\u4f60\u5e08\u7236\u4e0b\u5c71\u3002\u201d\u4e91\u857e\u9053\uff1a\u201c\u4e8c\u5e08\u4ef6\u6b64\u65f6\u6015\u5df2\u5230 \u5c0f\u5bd2\u5c71\u4e86\u3002\u201d\u5c06\u6f6e\u97f3\u548c\u5c1a\u7684\u8baf\u606f\u7ea6\u7565\u8bf4\u4e86\u4e00\u4e0b\u3002\u8463\u5cb3\u7b11\u9053\uff1a\u201c\u597d\u597d\uff01\u6211\u4eec\u56db\u4e2a\u540c\u95e8\uff0c\u770b\u6765\u53c8 \u8981\u5728\u80e1\u8fb9\u5e72\u51fa\u4e00\u756a\u60ca\u5929\u52a8\u5730\u7684\u4e8b\u4e1a\u4e86\u3002\u53ea\u6015\u5c06\u6765\u4f60\u7684\u5e08\u7956\u4ea6\u8981\u88ab\u7275\u52a8\u4e0b\u5c71\u3002\u201d\u7384\u673a\u9038\u58eb\u95ed\u95e8 \u5c01\u5251\u5df2\u4e09\u5341\u4f59\u5e74\uff0c\u4e91\u857e\u8fd8\u6ca1\u6709\u89c1\u8fc7\u4ed6\uff0c\u5fc3\u9053\uff1a\u201c\u82e5\u8981\u7275\u52a8\u4ed6\u8001\u4eba\u5bb6\u4e0b\u5c71\uff0c\u8fd9\u4e00\u5b9a\u662f\u6781\u4e3a\u96be\u6781 \u68d8\u624b\u4e4b\u4e8b\u3002\u201d\u957f\u8f88\u4e4b\u4e8b\uff0c\u4e0d\u6562\u591a\u95ee\u3002\u8463\u5cb3\u4e00\u770b\u5929\u8272\uff0c\u9053\uff1a\u201c\u5df2\u5feb\u56db\u66f4\u5566\uff0c\u660e\u65e9\u6211\u4fbf\u8981\u79bb\u4eac\uff0c \u4f60\u4f4f\u5728\u54ea\u513f\uff0c\u6211\u4e0d\u9001\u4f60\u56de\u53bb\u5566\u3002\u201d\u4e91\u857e\u9053\uff1a\u201c\u6211\u4f4f\u5728\u5ba2\u5e97\uff0c\u5927\u5e08\u4f2f\u4f60\u8bf7\u4fbf\uff0c\u6211\u4e5f\u4e0d\u9001\u4f60 \u5566\uff01\u201d\u4ed6\u4eec\u8fd9\u65f6\u8eab\u5728\u90ca\u5916\uff0c\u7acb\u8db3\u4e4b\u5904\uff0c\u65c1\u8fb9\u6709\u4e2a\u6c34\u6f6d\uff0c\u6708\u5149\u7167\u4e0b\u6765\uff0c\u6c34\u5149\u95ea\u8000\uff0c\u6f6d\u4e2d\u7167\u51fa\u4e8c \u4eba\u7684\u5f71\u5b50\u3002\u8463\u5cb3\u5ffd\u7136\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u5728\u51b0\u5929\u96ea\u5730\u6d88\u78e8\u4e86\u5341\u4f59\u8f7d\u5149\u9634\uff0c\u8fde\u5934\u53d1\u4e5f\u6591\u767d\u5566\uff01 \u54b3\uff0c\u65f6\u95f4\u8fc7\u5f97\u771f\u5feb\uff0c\u60f3\u5f53\u5e74\u4e0e\u4f60\u5e08\u7236\u5206\u624b\u4e4b\u65f6\u4f60\u5e08\u7236\u8fd8\u50cf\u4f60\u5982\u4eca\u4e00\u6837\u3002\u201d\u4e91\u857e\u5fc3\u4e2d\u4e00\u52a8\uff0c\u60f3 \u8d77\u5e08\u7236\u4e0e\u4e09\u5e08\u4f2f\u7684\u60c5\u5b7d\u7275\u8fde\uff0c\u5bf9\u5927\u5e08\u4f2f\u7684\u8bdd\uff0c\u4f3c\u89e3\u4e0d\u89e3\u3002\u62ac\u5934\u770b\u65f6\uff0c\u5927\u5e08\u4f2f\u5df2\u53bb\u5f97\u8fdc\u4e86\u3002 \u3000\u3000\u4e91\u857e\u4e00\u4e2a\u8f6c\u8eab\uff0c\u4e0d\u56de\u5ba2\u5e97\uff0c\u53c8\u5411\u4e8e\u8c26\u5bb6\u4e2d\u5954\u53bb\uff0c\u5230\u8fbe\u4e4b\u65f6\u542c\u5f97\u521a\u521a\u6572\u4e86\u56db\u66f4\uff0c\u53ea\u89c1\u4e8e\u8c26 \u7684\u4e66\u623f\uff0c\u706f\u706b\u72b9\u81ea\u660e\u4eae\u3002\u4e91\u857e\u5947\u9053\uff1a\u201c\u54a6\uff0c\u4ed6\u8fd8\u6ca1\u6709\u7761\u89c9\uff01\u201d\u6084\u6084\u8d70\u5230\u623f\u524d\uff0c\u8f7b\u8f7b\u6572\u4e86\u51e0 \u4e0b\uff0c\u4e8e\u8c26\u628a\u623f\u95e8\u6253\u5f00\uff0c\u542b\u7b11\u8bf4\u9053\uff1a\u201c\u4e91\u59d1\u5a18\uff0c\u4f60\u8bf7\u8fdb\u6765\uff0c\u6211\u7b49\u4f60\u5df2\u7ecf\u7b49\u5f97\u4e45\u4e86\uff01\u201d\u4e91\u857e\u5973\u626e \u7537\u88c5\uff0c\u4e00\u8def\u4e0a\u65e0\u4eba\u8bc6\u7834\uff0c\u89c1\u4e8e\u8c26\u4e00\u89c1\u9762\u4fbf\u79f0\u5979\u201c\u59d1\u5a18\u201d\uff0c\u4e0d\u7981\u6014\u7740\u3002\u4e8e\u8c26\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u5f20\u4e39 \u67ab\u65e9\u5df2\u628a\u4f60\u7684\u4e8b\u60c5\u3001\u4f60\u7684\u76f8\u8c8c\u90fd\u544a\u8bc9\u6211\u5566\uff0c\u4f60\u5230\u73b0\u5728\u624d\u6765\u89c1\u6211\u4e48\uff1f\u201d \u3000\u3000\u4e91\u857e\u770b\u4ed6\u4eb2\u5207\u7684\u7b11\u5bb9\uff0c\u5c31\u5982\u540c\u81ea\u5df1\u7684\u4eb2\u4eba\u957f\u8f88\u4e00\u6837\uff0c\u4e0d\u7981\u6cea\u5982\u96e8\u4e0b\uff0c\u62dc\u5012\u5730\u4e0a\u3002\u4e8e\u8c26\u4fef \u8eab\u5c06\u5979\u6276\u8d77\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u70b9\u7ff0\u6797\u90a3\u5e74\uff0c\u662f\u4f60\u7237\u7237\u505a\u7684\u4e3b\u8003\uff0c\u4e0d\u5acc\u6709\u50ed\u7684\u8bdd\uff0c\u6211\u53ef\u8981\u53eb\u4f60\u4e00\u58f0 \u4f84\u5973\u3002\u201d\u4e91\u857e\u542c\u4ed6\u63d0\u8d77\u7237\u7237\uff0c\u66f4\u662f\u4f24\u5fc3\uff0c\u62bd\u564e\u8bf4\u9053\uff1a\u201c\u6211\u7237\u7237\u662f\u600e\u6837\u6b7b\u7684\uff1f\u5f53\u771f\u662f\u7687\u4e0a\u5fa1\u65e8 \u8d50\u6b7b\u7684\u4e48\uff1f\u4f2f\u4f2f\u4f60\u53ef\u77e5\u9053\u5185\u60c5\uff1f\u201d \u3000\u3000\u4e8e\u8c26\u53eb\u4e91\u857e\u5750\u4e0b\uff0c\u7ed9\u5979\u5012\u4e86\u4e00\u676f\u70ed\u8336\u9053\uff1a\u201c\u4f60\u4e14\u63e9\u5e72\u773c\u6cea\u542c\u6211\u7ec6\u8bf4\u3002\u201d\u4e91\u857e\u8bd5\u6cea\u8046\u542c\u3002 \u4e8e\u8c26\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u7237\u7237\u9047\u96be\u90a3\u5e74\uff0c\u6211\u5df2\u505a\u5230\u5175\u90e8\u4f8d\u90ce\uff0c\u542c\u5f97\u96c1\u95e8\u5173\u5916\u4f20\u6765\u4f60\u7237\u7237\u7684 \u5669\u8017\uff0c\u6587\u6b66\u767e\u5b98\uff0c\u65e0\u4e0d\u60ca\u5947\u60b2\u6124\uff0c\u5927\u5bb6\u90fd\u8bf4\u4f60\u7237\u7237\u7f81\u7559\u5f02\u56fd\uff0c\u5728\u51b0\u5929\u96ea\u5730\u91cc\u7267\u9a6c\u4e8c\u5341\u5e74\uff0c\u59cb \u7ec8\u575a\u8d1e\u4e0d\u5c48\uff0c\u771f\u662f\u8282\u6bd4\u82cf\u6b66\uff0c\u8bf7\u7687\u4e0a\u662d\u96ea\uff0c\u66f4\u6b63\u7f6a\u540d\uff0c\u53e6\u52a0\u5c01\u8d60\u3002\u7687\u4e0a\u770b\u4e86\u594f\u672c\u7adf\u7136\u8bf4\u9053\uff1a \u2018\u4e91\u9756\u6b7b\u4e86\u5417\uff0c\u6715\u4e5f\u4e0d\u77e5\u9053\u5440\uff0c\u5f85\u6715\u56de\u53bb\u95ee\u95ee\uff0c\u4f60\u7684\u594f\u672c\uff0c\u4e14\u5148\u6401\u4e0b\u5427\u3002\u2019\u8bf4\u7f62\u5c31\u4e0b\u4ee4\u9000 \u671d\uff0c\u5927\u81e3\u5218\u65b0\u5fcd\u8010\u4e0d\u4f4f\uff0c\u633a\u8eab\u800c\u51fa\uff0c\u8ffd\u5165\u5fa1\u4e66\u623f\u95ee\u9053\uff1a\u2018\u90a3\u4e48\u8d50\u6b7b\u4e91\u9756\u7684\u8bcf\u4e66\uff0c\u4e0d\u662f\u5723\u4e0a\u5199 \u7684\u5417\uff1f\u2019\u7687\u4e0a\u652f\u652f\u543e\u543e\uff0c\u53f8\u793c\u592a\u76d1\u738b\u632f\u95fb\u626d\u8d76\u6765\uff0c\u8bf4\u9053\uff1a\u2018\u7687\u4e0a\uff0c\u4f60\u81ea\u5df1\u5199\u7684\u8bcf\u4e66\u4e5f\u5fd8\u8bb0\u4e86 \u5417\uff1f\u2019\u7687\u4e0a\u5fd9\u9053\uff1a\u2018\u554a\uff0c\u662f\u3001\u662f\u3001\u662f\u6715\u5199\u7684\u8bcf\u4e66\u3002\u4ed6\u662f\u4ec0\u4e48\u7f6a\u8d50\u6b7b\u7684\uff0c\u8ba9\u6715\u60f3\u60f3\u3002\u2019\u738b\u632f\u5728 \u65c1\u8fb9\u8bf4\u9053\uff1a\u2018\u4ed6\u8eab\u4e3a\u4f7f\u81e3\uff0c\u25a1\u989c\u4e8b\u4ec7\uff0c\u662f\u4ee5\u8d50\u6b7b\u3002\u2019\u7687\u4e0a\u9053\uff1a\u2018\u5bf9\uff0c\u5bf9\uff01\uff01\u662f\u4e3a\u4e86\u8fd9\u4e2a\u7f6a\u540d \u8d50\u6b7b\u7684\uff01\u2019\u5218\u5f97\u65b0\u5927\u9a82\u738b\u632f\u9053\uff1a\u2018\u660e\u660e\u662f\u4f60\u8fd9\u25a1\u5047\u4f20\u5723\u65e8\uff0c\u5bb3\u6b7b\u5fe0\u826f\uff0c\u5374\u5c06\u6076\u540d\u63a8\u7ed9\u7687\u4e0a\uff0c \u53eb\u7687\u4e0a\u5931\u5c3d\u4eba\u5fc3\uff01\u2019\u738b\u632f\u8001\u7f9e\u6210\u6012\uff0c\u7acb\u523b\u53d1\u4f5c\uff0c\u5c06\u5218\u5f97\u65b0\u6355\u4e0b\u5929\u7262\uff0c\u634f\u4e86\u4e00\u4e2a\u7f6a\u540d\uff0c\u8981\u628a\u4ed6 \u5904\u6b7b\u3002\u6ee1\u671d\u6587\u6b66\u4e0d\u670d\uff0c\u4ea4\u7ae0\u5f39\u52be\uff0c\u540e\u6765\u5218\u5f97\u65b0\u624d\u5f97\u514d\u4e00\u6b7b\uff0c\u524a\u804c\u4e3a\u6c11\u3002\u90a3\u4e2a\u66ff\u4f60\u7237\u7237\u4f38\u51a4\u7684 \u5fa1\u53f2\uff0c\u4e5f\u88ab\u6d41\u653e\u6d77\u5357\uff0c\u4e0d\u4e45\u5c31\u7ed9\u738b\u632f\u5bb3\u6b7b\u4e86\u3002\u5176\u4ed6\u51fa\u5934\u5f39\u52be\u7684\u4eba\uff0c\u5404\u5404\u53d7\u8d2c\uff0c\u6211\u90a3\u65f6\u4e5f\u7ed9\u8d2c \u5230\u6c5f\u897f\u53bb\u505a\u5de1\u6309\u3002\u201d \u3000\u3000\u4e91\u857e\u60b2\u6124\u4e4b\u6781\uff0c\u9053\uff1a\u201c\u597d\u53ef\u6068\u7684\u5978\u9609\uff0c\u539f\u6765\u6211\u7684\u7237\u7237\u662f\u4ed6\u5bb3\u6b7b\u7684\uff01\u4ed6\u4e3a\u4ec0\u4e48\u8981\u5bb3\u6b7b\u6211\u7684 \u7237\u7237\uff1f\u201d\u4e8e\u8c26\u9053\uff1a\u201c\u540e\u6765\u6211\u4eec\u6253\u542c\u51fa\u6765\uff0c\u539f\u6765\u738b\u632f\u8fd9\u25a1\uff0c\u65e9\u5df2\u548c\u4e5f\u5148\u7236\u5b50\u6709\u6240\u52fe\u7ed3\uff0c\u5c06\u4e2d\u56fd \u7684\u94c1\u5668\u6362\u8499\u53e4\u7684\u9a6c\u5339\uff0c\u6697\u4e2d\u5927\u505a\u4e70\u5356\uff0c\u8d5a\u5176\u5927\u94b1\uff0c\u542c\u8bf4\u8fd9\u4e9b\u4e70\u5356\u5728\u8499\u53e4\u90fd\u662f\u516c\u5f00\u4ea4\u6613\u7684\u3002\u4f60 \u7237\u7237\u662f\u524d\u671d\u5927\u81e3\uff0c\u6781\u6709\u58f0\u671b\uff0c\u66f4\u517c\u5b88\u8282\u4e8c\u5341\u5e74\uff0c\u5fe0\u8d1e\u4e0d\u4e0b\u82cf\u6b66\u7267\u7f8a\uff0c\u82e5\u7136\u56de\u6765\uff0c\u5fc5\u7136\u8981\u6574\u987f \u671d\u7eb2\u8083\u6e05\u5978\u515a\u3002\u6211\u731c\u60f3\u738b\u632f\u4e00\u6765\u662f\u6015\u4f60\u7237\u7237\u5728\u8499\u53e4\u5df2\u77e5\u9053\u4ed6\u52fe\u901a\u5916\u56fd\u7684\u60c5\u4e8b\uff0c\u4e8c\u6765\u662f\u6015\u4f60\u7237 \u7237\u56de\u671d\u4e4b\u540e\uff0c\u5bf9\u4ed6\u4e0d\u5229\uff0c\u662f\u4ee5\u5047\u4f20\u5723\u65e8\uff0c\u5148\u4e0b\u6bd2\u624b\uff01\u4ed6\u662f\u53f8\u793c\u592a\u76d1\uff0c\u7687\u4e0a\u7684\u5370\u73ba\u4e5f\u5728\u4ed6\u624b \u4e0a\uff0c\u5185\u5916\u7ae0\u594f\uff0c\u9664\u4e86\u662f\u5927\u81e3\u4eb2\u81ea\u62b1\u672c\u4e0a\u671d\u6240\u594f\u7684\u5916\uff0c\u90fd\u8981\u7ecf\u8fc7\u4ed6\u7684\u624b\uff0c\u4ed6\u8981\u5047\u4f20\u5723\u65e8\uff0c\u90a3\u662f \u6613\u4e8e\u53cd\u638c\u3002\u201d \u3000\u3000\u4e91\u857e\u542c\u4e86\u4e4b\u540e\uff0c\u5728\u60b2\u6124\u4e4b\u4e2d\uff0c\u4e0d\u7531\u5f97\u60f3\u8d77\u4e86\u5f53\u5e74\u5f20\u5b97\u5468\u53eb\u6fb9\u53f0\u706d\u660e\u9001\u7ed9\u4ed6\u7237\u7237\u7684\u4e09\u4e2a\u9526 \u56ca\u3002 \u3000\u3000\u8981\u77e5\u8fd9\u4e09\u9053\u9526\u56ca\uff0c\u6765\u5f97\u5341\u5206\u5947\u602a\uff0c\u6240\u4ee5\u4e91\u857e\u5f53\u5e74\u867d\u7136\u5e74\u5e7c\u65e0\u77e5\uff0c\u4f46\u957f\u6210\u4e4b\u540e\uff0c\u6f6e\u97f3\u548c \u5c1a\u3001\u91d1\u5200\u5468\u5065\u4ee5\u53ca\u540e\u6765\u7684\u5f20\u4e39\u67ab\u90fd\u66fe\u5bf9\u5979\u63d0\u8fc7\u3002\u7b2c\u4e00\u9053\u9526\u56ca\u4e2d\u4fbf\u85cf\u6709\u4e00\u9897\u8721\u4e38\uff0c\u5185\u4e2d\u6709\u4e00\u5f20 \u5b57\u6761\u662f\u738b\u632f\u5199\u4e0e\u8131\u6b22\uff08\u4e5f\u5148\u4e4b\u7236\uff09\u3001\u5f20\u5b97\u5468\u4e8c\u4eba\u7684\u4fe1\uff0c\u5546\u91cf\u4ee5\u94c1\u5668\u4ea4\u6362\u9a6c\u5339\u7684\u4e70\u5356\u7684\u3002\u8fd9\u4e00 \u9053\u9526\u56ca\u63a8\u65ad\u4e91\u9756\u88ab\u6355\uff0c\u53eb\u8c22\u5929\u534e\u5165\u4eac\u5c06\u8721\u4e38\u4ea4\u4e0e\u4e8e\u8c26\uff0c\u53c2\u52be\u738b\u632f\u3002\u8fd9\u7b2c\u4e09\u9053\u9526\u56ca\u7684\u63a8\u65ad\u867d\u7136 \u843d\u7a7a\uff0c\uff08\u4e91\u9756\u4e0d\u6b62\u88ab\u6355\uff0c\u800c\u4e14\u662f\u88ab\u7acb\u523b\u5bb3\u6b7b\uff09\uff0c\u4f46\u603b\u7b97\u662f\u5f20\u5b97\u5468\u7684\u4e00\u756a\u597d\u610f\u3002\u4e91\u857e\u60f3\u9053\uff1a \u201c\u82e5\u7136\u8fd9\u9897\u8721\u4e38\u5f53\u5e74\u4ea4\u4e0e\u4e8e\u8c26\uff0c\u738b\u632f\u7684\u7fbd\u7ffc\u53ca\u52bf\u529b\u90fd\u5c1a\u672a\u5982\u4eca\u4e4b\u76db\uff0c\u6709\u4e86\u771f\u51ed\u5b9e\u636e\uff0c\u628a\u4ed6\u6273 \u5012\uff0c\u4e5f\u8bf4\u4e0d\u5b9a\u3002\u201d \u3000\u3000\u4e8e\u8c26\u8bdd\u8bf4\u5b8c\u4e86\uff0c\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u4e91\u5927\u4eba\u6c89\u51a4\u672a\u96ea\uff0c\u4f46\u6709\u4f60\u8fd9\u6837\u4e00\u4e2a\u597d\u5b59\u5973\u513f\uff0c\u4e5d\u6cc9\u4e4b\u4e0b\uff0c \u4e5f\u53ef\u7791\u76ee\u4e86\u3002\u201d\u4e91\u857e\u60f3\u8d77\u7237\u7237\u7684\u60e8\u6b7b\uff0c\u6124\u706b\u53c8\u751f\uff0c\u51fb\u638c\u8a93\u9053\uff1a\u201c\u6211\u4e0d\u628a\u8fd9\u5978\u8d3c\u788e\u5c38\u4e07\u6bb5\uff0c\u8a93 \u4e0d\u4e3a\u4eba\u3002\u201d\u4e8e\u8c26\u6447\u6447\u5934\u9053\uff1a\u201c\u4e91\u59d1\u5a18\uff0c\u8fd9\u4e2a\u65f6\u5019\uff0c\u6211\u5374\u4e0d\u8d5e\u6210\u4f60\u53bb\u62a5\u4ec7\u3002\u201d \u3000\u3000\u4e91\u857e\u6124\u9053\uff1a\u201c\u8001\u4f2f\u7528\u610f\uff1f\u201d\u4e8e\u8c26\u9053\uff1a\u201c\u738b\u632f\u6b64\u65f6\u6743\u503e\u671d\u91ce\u90b8\u4e2d\u7532\u58eb\u5982\u4e91\uff0c\u8fd9\u4e5f\u7f62\u4e86\u3002\u519b \u4e2d\u5c06\u9886\uff0c\u4e5f\u6709\u8bb8\u591a\u662f\u4ed6\u7684\u5e72\u513f\uff0c\u73b0\u5728\u54b1\u4eec\u6b63\u5728\u5168\u529b\u5bf9\u4ed8\u74e6\u523a\u5165\u4fb5\uff0c\u82e5\u64cd\u4e4b\u8fc7\u6025\uff0c\u53ea\u6015\u53cd\u4f1a\u8bef \u4e86\u5927\u4e8b\u3002\u4fd7\u8bed\u6709\u4e91\uff1a\u5343\u592b\u6240\u6307\uff0c\u65e0\u75be\u800c\u6b7b\u3002\u7f6a\u6076\u6ee1\u76c8\uff0c\u53c8\u54ea\u80fd\u6709\u597d\u4e0b\u573a\u3002\u5c06\u6765\u4ed6\u5978\u8c0b\u66f4\u9732\u4e4b \u65f6\uff0c\u5c31\u662f\u4f60\u4e0d\u53bb\u4eb2\u81ea\u62a5\u4ec7\uff0c\u8fd9\u81ea\u4f1a\u6709\u4eba\u5c06\u4ed6\u9664\u6389\u3002\u518d\u8bf4\u4f60\u867d\u7cbe\u901a\u6b66\u827a\uff0c\u5374\u662f\u5b64\u638c\u96be\u9e23\uff0c\u6700\u5c11 \u4e5f\u5f97\u89c1\u4e86\u4f60\u7684\u54e5\u54e5\u518d\u8bf4\u3002\u201d \u3000\u3000\u4e91\u857e\u4e00\u60f3\u8fd9\u8bdd\u4e5f\u662f\u6b63\u7406\uff0c\u5f53\u4e0b\u9ed8\u7136\u4e0d\u8bed\uff0c\u6cea\u6e7f\u8863\u886b\u3002\u4e8e\u8c26\u7f13\u7f13\u8d77\u7acb\uff0c\u5c06\u73bb\u7483\u7a97\u683c\u63a8\u5f00\uff0c \u610f\u5473\u6df1\u957f\u5730\u9053\uff1a\u201c\u55ef\uff0c\u5929\u5c31\u8981\u4eae\u4e86\u3002\u857e\u4f84\uff0c\u4f60\u4f4f\u5728\u54ea\u513f\uff1f\u201d\u4e91\u857e\u9053\uff1a\u201c\u6211\u4f4f\u5728\u5ba2\u5e97\u3002\u201d\u4e8e\u8c26 \u8a00\u9053\uff1a\u201c\u5ba2\u5e97\u4eba\u6742\uff0c\u4f60\u5355\u8eab\u4e00\u4eba\u53c8\u662f\u5973\u626e\u7537\u88c5\uff0c\u60f3\u5fc5\u8bf8\u591a\u4e0d\u4fbf\uff0c\u4e0d\u5982\u642c\u5230\u6211\u8fd9\u513f\u5427\u3002\u6211\u8fd9\u513f \u6d88\u606f\u4e5f\u7075\u901a\u4e00\u4e9b\u3002\u201d\u4e91\u857e\u9053\uff1a\u201c\u65e2\u7136\u8001\u4f2f\u5429\u5490\uff0c\u4f84\u5973\u513f\u4e5f\u4e0d\u5ba2\u6c14\u4e86\uff0c\u5f85\u6211\u56de\u53bb\u6536\u62fe\u7acb\u523b\u642c \u6765\u3002\u201d\u9694\u623f\u6709\u4e00\u4e2a\u6e05\u8106\u7684\u5973\u5b69\u5b50\u7684\u58f0\u97f3\u53eb\u9053\uff1a\u201c\u7239\uff0c\u4f60\u53c8\u4e00\u665a\u6ca1\u7761\u89c9\u5417\uff1f\u201d \u3000\u3000\u4e8e\u8c26\u7b11\u4e0a\u7709\u68a2\uff0c\u9053\uff1a\u201c\u5c31\u7761\u5566\u3002\u201d\u5bf9\u4e91\u857e\u9053\uff1a\u201c\u6211\u7684\u5973\u513f\u50ac\u6211\u7761\u5566\uff0c\u4f60\u5feb\u642c\u884c\u674e\u6765\u5427\u3002 \u6211\u5e38\u5e38\u56e0\u4e3a\u4e8b\u5fd9\u71ac\u4e2a\u901a\u5bb5\u7684\uff0c\u8fd9\u4e5f\u6ca1\u6709\u4ec0\u4e48\uff0c\u5c31\u662f\u51b7\u6de1\u4e86\u8fd9\u4e2a\u5b69\u5b50\u3002\u201d\u4e91\u857e\u89c1\u4ed6\u4eec\u7236\u5973\u7684\u4eb2 \u7231\u60c5\u72b6\uff0c\u4e0d\u7981\u53c8\u60f3\u8d77\u4e86\u81ea\u5df1\u7684\u7237\u7237\u4e0e\u7238\u7238\u3002\u4e8e\u8c26\u7684\u5e74\u5c81\u548c\u5341\u5e74\u524d\u7684\u7237\u7237\u5dee\u4e0d\u591a\uff0c\u53ef\u7237\u7237\u5bf9\u81ea \u5df1\u5374\u6ca1\u6709\u4e8e\u8c26\u90a3\u6837\u6148\u7965\u3002 \u3000\u3000\u4e91\u857e\u56de\u53bb\u7ed3\u4e86\u5e97\u5e10\uff0c\u642c\u5230\u4e8e\u5bb6\uff0c\u4e8e\u8c26\u7684\u5973\u513f\u53eb\u505a\u4e8e\u627f\u73e0\uff0c\u4eca\u5e74\u4e0d\u8fc7\u4e5d\u5c81\uff0c\u806a\u660e\u4f36\u4fd0\uff0c\u6d3b \u6cfc\u975e\u5e38\uff0c\u4e91\u857e\u6539\u56de\u5973\u88c5\uff0c\u627f\u73e0\u76f4\u8ffd\u7740\u5979\u53eb\u59d0\u59d0\u3002\u4e91\u857e\u548c\u5979\u751a\u4e3a\u76f8\u5f97\uff0c\u81ea\u6b64\u5c31\u5728\u4e8e\u5bb6\u4f4f\u4e0b\u6765\u3002 \u4e91\u857e\u4f4f\u5230\u4e8e\u5bb6\uff0c\u5fc3\u4e2d\u8fd8\u9690\u85cf\u6709\u4e00\u4e2a\u5e0c\u671b\uff0c\u5e0c\u671b\u5f20\u4e39\u67ab\u4f1a\u518d\u6765\u4f1a\u89c1\u4e8e\u8c26\uff0c\u53ef\u662f\u4e00\u8fde\u4f4f\u4e86\u534a\u4e2a \u6708\uff0c\u5f20\u4e39\u67ab\u5374\u6ca1\u6709\u6765\u8fc7\u3002\u81f3\u4e8e\u90a3\u756a\u738b\u548c\u6fb9\u53f0\u706d\u660e\uff0c\u4e5f\u65e9\u5728\u4e91\u857e\u642c\u5230\u4e8e\u5bb6\u4e4b\u540e\u7684\u7b2c\u516d\u5929\uff0c\u5c31\u56e0 \u548c\u8c08\u5931\u8d25\u800c\u5f52\u56fd\u53bb\u4e86\u3002 \u3000\u3000\u4f4f\u5230\u534a\u6708\u4e4b\u671f\uff0c\u4e91\u857e\u60f3\u8d77\u4e86\u5f20\u98ce\u5e9c\u6240\u8bf4\u7684\u4eca\u5e74\u6b66\u4e3e\u7279\u79d1\uff0c\u4e0d\u4f4f\u5730\u95ee\u4e8e\u8c26\u6d88\u606f\uff0c\u4e8e\u8c26\u603b\u7b11 \u7740\u9053\uff1a\u201c\u4e56\u4f84\u5973\uff0c\u522b\u5fc3\u6025\uff0c\u4f60\u54e5\u54e5\u82e5\u7136\u51fa\u8003\uff0c\u6211\u603b\u53eb\u4f60\u89c1\u7740\u4ed6\u4fbf\u662f\u3002\u201d\u4e91\u857e\u95ee\u9053\uff1a\u201c\u5df2\u7ecf\u5f00 \u8003\u4e86\u5417\uff1f\u201d\u4e8e\u8c26\u9053\uff1a\u201c\u73b0\u5728\u8fd8\u662f\u521d\u8bd5\uff0c\u4eba\u591a\u7740\u5462\uff0c\u5f85\u6211\u5230\u5175\u90e8\u67e5\u67e5\uff0c\u770b\u4f60\u7684\u54e5\u54e5\u6210\u7ee9\u600e \u6837\u3002\u201d\u53c8\u8fc7\u4e86\u4e94\u5929\uff0c\u4e00\u65e5\u65e9\u6668\uff0c\u4e8e\u8c26\u7a81\u7136\u628a\u4e91\u857e\u53eb\u5230\u8ddf\u524d\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u60f3\u89c1\u54e5\u54e5\u5417\uff1f\u201d\u4e91\u857e \u8df3\u8d77\u9053\uff1a\u201c\u4f2f\u4f2f\u4f60\u4eca\u513f\u5c31\u5e26\u6211\u53bb\u89c1\u4ed6\u5417\uff1f\u201d\u4e8e\u8c26\u9053\uff1a\u201c\u662f\u5440\uff01\u53ef\u4f60\u8981\u59d4\u5c48\u4e00\u4e0b\u3002\u4f60\u626e\u4f5c\u6211\u7684 \u968f\u4ece\uff0c\u6211\u5e26\u4f60\u5230\u6821\u573a\u770b\u6bd4\u6b66\u53bb\u3002\u201d \u3000\u3000\u4e91\u857e\u8fd9\u4e00\u559c\u975e\u540c\u5c0f\u53ef\uff0c\u6025\u5fd9\u53c8\u6362\u4e86\u7537\u88c5\u626e\u6210\u4e8e\u8c26\u7684\u7ae5\u4ec6\u3002\u539f\u6765\u4eca\u65e5\u4e43\u662f\u6700\u540e\u7684\u4e00\u9053\u8003 \u8bd5\uff0c\u8ba9\u901a\u8fc7\u590d\u8bd5\u7684\u6bd4\u6b66\u5b9a\u6b66\u72b6\u5143\u3002\u672c\u6765\u6b66\u8bd5\u6ca1\u6709\u8981\u4e3e\u5b50\u4e92\u76f8\u6bd4\u6b66\u7684\uff0c\u4f46\u56e0\u4e3a\u4eca\u79d1\u662f\u7279\u79d1\uff0c\u4e3a \u7684\u662f\u62db\u63fd\u5929\u4e0b\u5947\u624d\u5f02\u80fd\u3001\u6b66\u827a\u9ad8\u5f3a\u4e4b\u58eb\uff0c\u56e0\u6b64\u5728\u901a\u8fc7\u4e86\u7b2c\u4e00\u573a\u7684\u8003\u5f13\u9a6c\uff0c\u7b2c\u4e8c\u573a\u7684\u8003\u5175\u6cd5\u4e4b \u540e\uff0c\u8fd8\u8981\u6765\u4e00\u573a\u6bd4\u6b66\u3002\u8fd9\u662f\u5927\u5185\u603b\u7ba1\u5eb7\u8d85\u6d77\u7684\u4e3b\u5f20\uff0c\u8bf4\u65e2\u662f\u7279\u5f00\u7684\u6b66\u79d1\uff0c\u5c31\u5e94\u4ee5\u6b66\u827a\u4e3a\u4e3b\uff0c \u6b66\u827a\u6709\u591a\u79cd\u591a\u6837\uff0c\u4e0d\u6b62\u9650\u4e8e\u5f2f\u5f13\u9a70\u9a6c\uff0c\u76d8\u5200\u5f04\u67aa\uff0c\u82e5\u4e0d\u6bd4\u6b66\uff0c\u7109\u80fd\u8bc6\u522b\u771f\u624d\uff1f\u7687\u5e1d\u7948\u9547\u5728\u5bab \u4e2d\u6b63\u81ea\u95f7\u5f97\u614c\uff0c\u4e00\u542c\u6709\u70ed\u95f9\u53ef\u770b\uff0c\u8fd9\u53ef\u4e50\u4e86\uff0c\u7acb\u523b\u51c6\u4e86\u5eb7\u8d85\u6d77\u6240\u594f\uff0c\u7d22\u6027\u547d\u4eba\u5728\u6821\u573a\u91cc\u642d\u8d77 \u64c2\u53f0\uff0c\u53c8\u5728\u56db\u8fb9\u642d\u8d77\u770b\u53f0\uff0c\u9664\u4e86\u81ea\u5df1\u4eb2\u4e34\u4e4b\u5916\uff0c\u8fd8\u53eb\u5404\u90e8\u5c1a\u4e66\u548c\u5927\u81e3\u4eec\u4e5f\u966a\u7740\u53bb\u770b\u3002\u5eb7\u8d85\u6d77 \u8fd9\u4e2a\u4e3b\u5f20\u5176\u5b9e\u4e5f\u85cf\u6709\u79c1\u5fc3\u3002\u539f\u6765\u4ed6\u6709\u4e24\u4e2a\u5e08\u5144\u5f1f\u4e5f\u53c2\u52a0\u4eca\u79d1\u6b66\u8bd5\uff0c\u4ed6\u7684\u4e24\u4e2a\u5e08\u5144\u5f1f\u6b66\u529f\u751a \u9ad8\uff0c\u4f46\u5bf9\u4e8e\u5175\u6cd5\u7b56\u8bba\uff0c\u5374\u662f\u5e73\u5e73\uff0c\u662f\u4ee5\u5eb7\u8d85\u6d77\u60f3\u53eb\u4ed6\u4eec\u5728\u6bd4\u6b66\u8fd9\u4e00\u573a\u5927\u663e\u5a01\u98ce\u3002 \u3000\u3000\u6821\u573a\u5468\u56f4\u6709\u5fa1\u6797\u519b\u628a\u5b88\uff0c\u573a\u4e2d\u642d\u8d77\u4e94\u4e2a\u770b\u53f0\u3002\u4e8e\u8c26\u5e26\u4e86\u4e91\u857e\u548c\u5175\u90e8\u3001\u6237\u90e8\u5404\u5927\u81e3\u5728\u4e1c\u8fb9 \u7684\u770b\u53f0\uff0c\u7687\u5e1d\u548c\u5404\u4eb2\u738b\u3001\u592a\u76d1\u5360\u7740\u6b63\u9762\u7684\u90a3\u4e2a\u770b\u53f0\u3002\u4e8e\u8c26\u6084\u6084\u8bf4\u9053\uff1a\u201c\u4f60\u77a7\uff0c\u90a3\u4e2a\u7a7f\u7740\u9f99 \u888d\uff0c\u80cc\u540e\u5217\u6709\u4e00\u6392\u6b66\u58eb\u7684\u4eba\uff0c\u4fbf\u662f\u5f53\u4eca\u7687\u4e0a\u4e86\u3002\u7687\u4e0a\u5de6\u8fb9\u7ad9\u7740\u90a3\u4eba\u4fbf\u662f\u592a\u76d1\u738b\u632f\u3002\u201d\u4e91\u857e\u72e0 \u72e0\u5730\u76ef\u4e86\u738b\u632f\u4e00\u773c\uff0c\u628a\u4ed6\u7684\u76f8\u8c8c\u7262\u7262\u8bb0\u7740\u3002 \u3000\u3000\u53c2\u52a0\u6bd4\u6b66\u7684\u4e3e\u5b50\u5728\u64c2\u53f0\u4e0b\u9762\u7684\u51c9\u68da\u4f11\u606f\uff0c\u672a\u4e0a\u64c2\u53f0\u4e4b\u524d\uff0c\u770b\u53f0\u4e0a\u53ef\u770b\u4e0d\u5230\u3002\u4e8e\u8c26\u5bf9\u4e91\u857e \u9053\uff1a\u201c\u4eca\u5e74\u7684\u7279\u79d1\uff0c\u867d\u8bf4\u662f\u4efb\u4f55\u4eba\u90fd\u53ef\u53c2\u4e0e\uff0c\u4f46\u9664\u4e86\u73b0\u6709\u519b\u804c\u4e4b\u5916\uff0c\u5176\u4ed6\u7684\u4eba\u8fd8\u9700\u8981\u6709\u4e00\u4e2a \u4e09\u54c1\u4ee5\u4e0a\u7684\u6b66\u5b98\u505a\u4fdd\u4eba\uff0c\u6240\u4ee5\u7687\u4e0a\u6562\u653e\u5fc3\u6765\u770b\u3002\u201d\u4e91\u857e\u5fc3\u60f3\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\u3002\u90a3\u6c5f\u6e56\u4e0a\u771f\u6b63 \u6709\u6781\u5927\u80fd\u4e3a\u4e4b\u4eba\u65ad\u4e4e\u4e0d\u4f1a\u6765\u4e86\u3002\u201d \u3000\u3000\u53ea\u542c\u5f97\u201c\u549a\uff0c\u549a\uff0c\u549a\u201d\u4e09\u58f0\u9f13\u54cd\uff0c\u6bd4\u6b66\u5f00\u59cb\u3002\u4e91\u857e\u7d27\u5f20\u4e4b\u6781\uff0c\u805a\u7cbe\u4f1a\u795e\u5730\u770b\u90a3\u8df3\u4e0a\u64c2\u53f0 \u6bd4\u6b66\u4e4b\u4eba\uff0c\u5374\u662f\u4e24\u4e2a\u964c\u751f\u7684\u7c97\u9c81\u7537\u5b50\uff0c\u4e24\u4eba\u6f14\u51fa\u5355\u5200\u5bf9\u82b1\u67aa\uff0c\u4e0d\u4e00\u523b\u4f7f\u5355\u5200\u7684\u8d62\u4e86\uff0c\u63a5\u8fde\u53c8 \u6bd4\u8bd5\u4e86\u4e09\u5bf9\uff0c\u4e91\u857e\u7684\u54e5\u54e5\u90fd\u6ca1\u6709\u51fa\u73b0\u3002\u8d25\u8005\u6dd8\u6c70\u80dc\u8005\u7ee7\u7eed\u4e3b\u64c2\uff0c\u8fde\u80dc\u4e24\u573a\u4e4b\u540e\uff0c\u53ef\u4ee5\u4f11\u606f\uff0c \u8ba9\u5176\u4ed6\u5404\u5bf9\u5148\u6bd4\uff0c\u5f85\u5bf9\u5b8c\u4e4b\u540e\uff0c\u518d\u6765\u4e00\u4e2a\u590d\u8d5b\u3002\u4e91\u857e\u4e5f\u65e0\u5fc3\u8bb0\u4ed6\u4eec\u7684\u540d\u5b57\uff0c\u7b2c\u56db\u5bf9\u6bd4\u5b8c\u4e4b \u540e\uff0c\u7ad9\u5728\u53f0\u4e0a\u8000\u6b66\u626c\u5a01\u7684\u5f97\u80dc\u8005\uff0c\u662f\u4e00\u4e2a\u8eab\u9ad8\u4e03\u5c3a\uff0c\u9b41\u68a7\u5947\u4f1f\u7684\u4eba\uff0c\u624b\u4f7f\u4e24\u67c4\u94c1\u9524\uff0c\u751a\u662f\u795e \u6c14\u3002 \u3000\u3000\u5175\u90e8\u5c1a\u4e8e\u4e0e\u4e8e\u8c26\u4e0e\u4e00\u770b\u53f0\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u6211\u4eec\u5175\u90e8\u65b0\u63d0\u62d4\u7684\u5c06\u519b\u80e1\u5927\u5e86\uff0c\u4e24\u81c2\u6709\u5343\u65a4 \u4e4b\u529b\u3002\u8fd9\u6b21\u7279\u79d1\uff0c\u5e94\u8bd5\u8005\u751a\u591a\uff0c\u901a\u8fc7\u524d\u4e24\u573a\u8003\u8bd5\u7684\u4e5f\u6709\u4e5d\u5341\u516d\u4eba\uff0c\u672c\u6765\u90fd\u5e94\u8be5\u53c2\u52a0\u64c2\u53f0\u6bd4 \u6b66\uff0c\u7687\u4e0a\u8bf4\u8981\u770b\u5c31\u770b\u6700\u7cbe\u5f69\u7684\uff0c\u53c8\u60f3\u5728\u4e00\u5929\u4e4b\u5185\u770b\u5b8c\uff0c\u6240\u4ee5\u6628\u5929\u5148\u5728\u5175\u90e8\u4e3e\u884c\u4e86\u4e00\u573a\u6dd8\u6c70 \u8bd5\uff0c\u4ece\u4e5d\u5341\u516d\u4eba\u4e2d\u6311\u51fa\u4e8c\u5341\u56db\u4eba\u3002\u80e1\u5c06\u519b\u5728\u6dd8\u6c70\u8bd5\u4e2d\u7684\u6210\u7ee9\u597d\u6781\u4e86\u3002\u201d \u3000\u3000\u4e8e\u8c26\u5fae\u5fae\u4e00\u7b11\uff0c\u4ed6\u77e5\u9053\u8fd9\u4e2a\u80e1\u5927\u5e86\u4e43\u662f\u5175\u90e8\u5c1a\u4e66\u7684\u4eb2\u5a01\uff0c\u5175\u90e8\u5c1a\u4e66\u81ea\u7136\u671b\u4ed6\u5f97\u80dc\u3002\u64c2\u53f0 \u524d\u7684\u65d7\u724c\u5b98\u53eb\u9053\uff1a\u201c\u7b2c\u4e5d\u53f7\u4e3e\u5b50\u6797\u9053\u5b89\u4e0a\u53f0\uff01\u4fdd\u4eba\u793c\u90e8\u4e3b\u4e8b\u674e\u987a\u3002\u201d\u8fd9\u6837\u4e00\u53eb\uff0c\u4f17\u4eba\u5c31\u77e5\u9053 \u8fd9\u53f7\u4e3e\u5b50\u5e76\u975e\u73b0\u804c\u519b\u5b98\u3002\u4e91\u857e\u4e0d\u89c9\u4e00\u6014\uff0c\u53ea\u89c1\u4e00\u4e2a\u4e3e\u5b50\u624b\u6447\u6298\u6247\u8df3\u4e0a\u53f0\u6765\uff0c\u4ed6\u867d\u7136\u7a7f\u5b50\u6b66\u4e3e \u89c4\u5b9a\u7684\u670d\u9970\uff0c\u620e\u88c5\u62ab\u6302\uff0c\u4f46\u76f8\u8c8c\u65af\u6587\uff0c\u6709\u5982\u5973\u5b50\u4e00\u822c\uff0c\u624b\u6447\u6298\u6247\uff0c\u914d\u7740\u90a3\u8eab\u620e\u88c5\uff0c\u66f4\u663e\u5f97\u4e0d \u4f26\u4e0d\u7c7b\u3002\u8fd9\u4eba\u6b63\u662f\u8f70\u5929\u96f7\u77f3\u82f1\u4e00\u4e2a\u597d\u53cb\u6797\u5e84\u4e3b\u7684\u513f\u5b50\uff0c\u6570\u6708\u4e4b\u524d\u66fe\u5411\u77f3\u7fe0\u51e4\u6c42\u5a5a\uff0c\u7ed9\u77f3\u7fe0\u51e4 \u7528\u8ba1\u6253\u8d25\u7684\u90a3\u4e2a\u6797\u9053\u5b89\u3002 \u3000\u3000\u6797\u9053\u5b89\u62b1\u6247\u4e00\u63d6\uff0c\u9634\u58f0\u602a\u6c14\u5730\u9053\uff1a\u201c\u80e1\u5c06\u519b\u624b\u4e0b\u7559\u60c5\u3002\u201d\u80e1\u5927\u5e86\u6697\u53eb\u4e00\u58f0\uff1a\u201c\u5012\u9709\uff0c\u54ea \u91cc\u8dd1\u6765\u7684\u8fd9\u6837\u4e00\u4e2a\u4e0d\u9634\u4e0d\u9633\u7684\u602a\u7269\uff01\u201d\u9524\u5934\u4e00\u6446\uff0c\u559d\u9053\uff1a\u201c\u4ec0\u4e48\u7559\u60c5\uff1f\u8fd9\u91cc\u662f\u671d\u5ef7\u62a1\u5143\u4e4b \u6240\uff0c\u4f60\u5f53\u662f\u73a9\u800d\u4e48\uff1f\u8fd8\u4e0d\u5feb\u4eae\u51fa\u5175\u5668\uff1f\u201d\u6797\u9053\u5b89\u5a07\u58f0\u8bf4\u9053\uff1a\u201c\u665a\u751f\u7684\u5175\u5668\uff0c\u5c31\u662f\u8fd9\u628a\u6247 \u5b50\uff01\u201d\u80e1\u5927\u5e86\u5927\u6012\uff0c\u547c\u7684\u4e00\u9524\u5288\u4e0b\uff0c\u4ed6\u54ea\u77e5\u6797\u9053\u5b89\u7684\u70b9\u7a74\u529f\u592b\u53c8\u51c6\u53c8\u72e0\uff0c\u53ea\u89c1\u6298\u6247\u4e00\u5408\uff0c\u6247 \u5934\u4e00\u6307\uff0c\u5f84\u5954\u80e1\u5927\u5e86\u80c1\u4e0b\u7684\u8f6f\u9ebb\u7a74\u3002\u80e1\u5927\u5e86\u8eab\u8eaf\u9ad8\u5927\uff0c\u8f6c\u52a8\u4e0d\u4fbf\uff0c\u4e24\u67c4\u5927\u94c1\u9524\u867d\u4f7f\u5f97\u547c\u547c\u98ce \u54cd\u5374\u62e6\u4e0d\u4f4f\u6797\u9053\u5b89\uff0c\u6570\u62db\u4e00\u8fc7\uff0c\u53ea\u542c\u5f97\u201c\u5495\u549a\u201d\u4e00\u58f0\uff0c\u80e1\u5927\u5e86\u6c34\u725b\u822c\u7684\u8eab\u8eaf\u5012\u5728\u53f0\u4e0a\u3002\u6797\u9053 \u5b89\u4e00\u811a\u5c06\u4ed6\u626b\u4e0b\u64c2\u53f0\uff0c\u7b11\u9053\uff1a\u201c\u665a\u751f\u627f\u8ba9\u4e86\uff01\u201d \u3000\u3000\u7687\u5e1d\u7948\u9547\u770b\u5f97\u597d\u4e0d\u5f00\u5fc3\uff0c\u7b11\u9053\uff1a\u201c\u5999\u554a\uff01\u201d\u738b\u632f\u9053\uff1a\u201c\u4e0b\u4e00\u573a\u66f4\u5999\u5462\uff0c\u7687\u4e0a\u5feb\u770b\uff01\u201d\u53ea \u542c\u5f97\u65d7\u724c\u5b98\u53eb\u9053\uff1a\u201c\u7b2c\u5341\u53f7\uff01\u201d\u8df3\u4e0a\u6765\u7684\u9ad8\u4e3e\u4e00\u9762\u94c1\u76fe\uff0c\u5374\u662f\u738b\u632f\u7684\u5fc3\u8179\u6b66\u58eb\u8def\u5bb6\u5144\u5f1f\u4e2d\u7684 \u5f1f\u5f1f\u8def\u4eae\uff0c\u4ed6\u4eec\u4e24\u5144\u5f1f\u53c2\u52a0\u6bd4\u8bd5\uff0c\u54e5\u54e5\u8def\u660e\u5728\u6628\u65e5\u7684\u521d\u6b21\u6dd8\u6c70\u8d5b\u4e2d\u5c31\u7ed9\u4e00\u4e2a\u4e0d\u77e5\u6765\u5386\u7684\u5c11\u5e74 \u6253\u8d25\uff0c\u53ea\u6709\u4ed6\u53c2\u52a0\u590d\u8bd5\u3002 \u3000\u3000\u8def\u5bb6\u7684\u6df7\u5143\u724c\u6cd5\uff0c\u867d\u7136\u8981\u5251\u76fe\u5408\u4f7f\u624d\u89c1\u7cbe\u5999\uff0c\u4f46\u53ea\u6709\u4e00\u9762\u94c1\u76fe\uff0c\u4e5f\u591f\u6797\u9053\u5b89\u5e94\u4ed8\u4e86\u3002\u8def \u4eae\u628a\u94c1\u724c\u5c55\u5f00\uff0c\u5c31\u5982\u5728\u8eab\u524d\u6446\u4e86\u4e00\u9762\u5c4f\u98ce\uff0c\u6797\u9053\u5b89\u54ea\u91cc\u653b\u5f97\u8fdb\u53bb\u3002\u4e24\u4eba\u6597\u4e86\u4e09\u4e94\u5341\u62db\uff0c\u8def\u4eae \u6545\u610f\u5356\u4e86\u4e00\u4e2a\u7834\u7efd\uff0c\u94c1\u76fe\u4e00\u652b\uff0c\u8ba9\u5f00\u4e00\u7ebf\u7684\u7a7a\u9699\uff0c\u6797\u9053\u5b89\u7684\u70b9\u7a74\u6cd5\u89c1\u9699\u5373\u5165\u5df2\u6210\u81ea\u7136\uff0c\u6247\u67c4 \u5012\u8f6c\u7acb\u523b\u70b9\u4ed6\u80f8\u9645\u7684\u201c\u7487\u7391\u7a74\u201d\u3002\u54ea\u6599\u94c1\u76fe\u7a81\u7136\u4e00\u5408\uff0c\u201c\u25a1\u201d\u7684\u4e00\u58f0\uff0c\u628a\u6797\u9053\u5b89\u7684\u63cf\u91d1\u94c1\u6247 \u5f53\u4e2d\u9707\u65ad\uff0c\u6797\u9053\u5b89\u6298\u4e86\u6247\u5b50\uff0c\u5982\u4e5e\u4e10\u4e22\u4e86\u5316\u5b50\u68d2\uff0c\u6ca1\u5f97\u821e\u5f04\uff0c\u6025\u6025\u8df3\u4e0b\u64c2\u53f0\u3002 \u3000\u3000\u738b\u632f\u7709\u5f00\u773c\u7b11\uff0c\u7687\u5e1d\u5949\u627f\u9053\uff1a\u201c\u516c\u516c\u7684\u6b66\u58eb\u679c\u7136\u672c\u4e8b\uff01\u201d\u53ea\u542c\u5f97\u65d7\u724c\u5b98\u53c8\u53eb\u9053\uff1a\u201c\u7b2c\u5341 \u4e00\u53f7\u4e3e\u5b50\u6c99\u65e0\u5fcc\u4e0a\u53f0\uff0c\u4fdd\u4eba\u5fa1\u6797\u519b\u526f\u7edf\u9886\u6768\u5a01\uff01\u201d\u4e91\u857e\u53c8\u662f\u4e00\u6014\uff0c\u60f3\u4e0d\u5230\u8fd9\u4e2a\u5fc3\u72e0\u624b\u8fa3\u7684\u7eff \u6797\u5927\u76d7\uff0c\u66fe\u5411\u77f3\u7fe0\u51e4\u6c42\u5a5a\u4e0d\u9042\u7684\u6c99\u65e0\u5fcc\uff0c\u5c45\u7136\u642d\u4e0a\u5fa1\u6797\u519b\u7684\u7ebf\uff0c\u4e5f\u6765\u53c2\u52a0\u6bd4\u6b66\u3002 \u3000\u3000\u6c99\u65e0\u5fcc\u4e00\u8df3\u4e0a\u53f0\uff0c\u6beb\u4e0d\u5ba2\u6c14\uff0c\u53cc\u638c\u4e00\u9519\uff0c\u4fbf\u9053\uff1a\u201c\u4ffa\u5c31\u7528\u8fd9\u5bf9\u8089\u638c\u63a5\u4f60\u8fd9\u9762\u94c1\u724c\uff01\u201d\u8def \u4eae\u5927\u6012\uff0c\u94c1\u724c\u4e00\u633a\uff0c\u7acb\u523b\u5f53\u5934\u538b\u4e0b\uff0c\u559d\u9053\uff1a\u201c\u597d\uff0c\u4f60\u5c31\u63a5\u5427\uff01\u201d\u724c\u631f\u52b2\u98ce\uff0c\u5c11\u8bf4\u4e5f\u6709\u4e03\u516b\u767e \u65a4\u6c14\u529b\u3002\u6c99\u65e0\u5fcc\u4e00\u8df3\u8df3\u5f00\uff0c\u5288\u9762\u8fd8\u4e86\u4e00\u638c\uff0c\u8def\u4eae\u4e00\u770b\uff0c\u6c99\u65e0\u5fcc\u638c\u5fc3\u6f06\u9ed1\u90a3\u662f\u6bd2\u7802\u638c\u7684\u529f\u592b\uff0c \u4e0d\u7981\u5927\u60ca\uff0c\u6025\u5fd9\u628a\u94c1\u76fe\u6536\u56de\u62a4\u8eab\u3002\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u53ea\u89c1\u6c99\u65e0\u5fcc\u51fa\u624b\u5982\u7535\uff0c\u201c\u556a\u201d\u7684\u4e00\u638c\uff0c \u5728\u4ed6\u80a9\u5934\u4e00\u6309\uff0c\u8def\u4eae\u5927\u53eb\u4e00\u58f0\u767b\u65f6\u6eda\u4e0b\u64c2\u53f0\u3002\u672c\u6765\u8def\u4eae\u6b66\u529f\u4e0d\u5f31\uff0c\u82e5\u4ee5\u94c1\u76fe\u62a4\u8eab\uff0c\u6c99\u65e0\u5fcc\u7684 \u6bd2\u7802\u638c\u867d\u7136\u5389\u5bb3\uff0c\u4e5f\u6253\u4e0d\u8fdb\u53bb\uff0c\u6c99\u65e0\u5fcc\u5de5\u4e8e\u5fc3\u8ba1\uff0c\u4e00\u8df3\u4e0a\u53f0\uff0c\u5c31\u6fc0\u4ed6\u51fa\u624b\uff0c\u4e58\u5176\u4e0d\u5907\uff0c\u4e00\u638c \u594f\u529f\u3002 \u3000\u3000\u8def\u4eae\u672a\u5230\u4e09\u62db\uff0c\u5c31\u88ab\u6253\u4e0b\uff0c\u738b\u632f\u6c14\u5f97\u9762\u8272\u94c1\u9752\u3002\u536b\u58eb\u603b\u7ba1\u5eb7\u8d85\u6d77\u7b11\u9053\uff1a\u201c\u516c\u516c\u4e0d\u5fc5\u751f \u6c14\uff0c\u4e0b\u4e00\u573a\u5c31\u8981\u53eb\u8fd9\u5c0f\u5b50\u53d7\u4e0d\u4e86\uff0c\u515c\u7740\u8d70\uff01\u201d\u53ea\u542c\u5f97\u65d7\u724c\u5b98\u53eb\u9053\uff1a\u201c\u7b2c\u5341\u4e8c\u53f7\u9646\u5c55\u9e4f\u4e0a\u53f0\uff0c \u4fdd\u4eba\u5927\u5185\u603b\u7ba1\u5eb7\u8d85\u6d77\uff01\u201d \u3000\u3000\u53ea\u89c1\u4e00\u4e2a\u4e94\u77ed\u8eab\u6750\u7684\u7cbe\u608d\u6c49\u5b50\u8df3\u4e0a\u64c2\u53f0\uff0c\u4ed6\u8170\u7f20\u91d1\u4e1d\u8f6f\u97ad\u5374\u4e0d\u89e3\u4e0b\uff0c\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u4f60\u7684 \u6bd2\u7802\u638c\u679c\u7136\u5389\u5bb3\uff0c\u6211\u5c31\u8ba9\u4f60\u5148\u6253\u4e09\u638c\uff01\u6211\u82e5\u95ea\u907f\uff0c\u5c31\u7b97\u6211\u8f93\uff01\u201d\u6c99\u65e0\u5fcc\u4e00\u6014\uff0c\u53ea\u542c\u5f97\u9646\u5c55\u9e4f \u8fde\u8fde\u50ac\u9053\uff1a\u201c\u6253\u5440\uff0c\u600e\u4e48\u4e0d\u6253\uff1f\u8fd9\u662f\u6bd4\u6b66\u529f\u7684\u64c2\u53f0\uff0c\u4f60\u82e5\u4e0d\u6253\uff0c\u5c31\u7ed9\u6211\u6eda\u4e0b\u53f0\u53bb\uff01\u201d\u6c99\u65e0\u5fcc \u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u6211\u8fd9\u6bd2\u7802\u638c\u5389\u5bb3\u975e\u5e38\u96be\u9053\u4ed6\u7ec3\u5f97\u5468\u8eab\u6bd2\u6c14\u4e0d\u4fb5\u4e48\uff0c\u6211\u53ef\u4e0d\u66fe\u542c\u8bf4\u8fc7\u6709\u8fd9\u79cd\u672c \u9886\u3002\u201d\u4ed6\u5fc3\u4e2d\u6c14\u6781\uff0c\u5374\u662f\u4e0d\u52a8\u58f0\u8272\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6211\u8fd9\u624b\u638c\u7684\u6bd2\uff0c\u9646\u7237\u4f60\u5f97\u5f53\u5fc3\uff01\u201d\u8bdd\u58f0\u672a \u4e86\uff0c\u500f\u5730\u4e00\u638c\u62cd\u5411\u9762\u95e8\uff0c\u4ed6\u60f3\uff1a\u201c\u6253\u5728\u8eab\u4e0a\u6709\u8863\u7269\u9694\u7740\uff0c\u53ea\u6015\u4ed6\u53e6\u6709\u5316\u89e3\u4e4b\u6cd5\uff0c\u6253\u4f60\u9762\u95e8\uff0c \u96be\u9053\u4f60\u7684\u9762\u76ae\u4e5f\u7ec3\u6709\u529f\u592b\uff1f\u201d\u54ea\u77e5\u4e00\u638c\u62cd\u51fa\uff0c\u9646\u5c55\u9e4f\u80a9\u5934\u4e00\u8038\uff0c\u671d\u4ed6\u7684\u624b\u8098\u4e00\u649e\uff0c\u6c99\u65e0\u5fcc\u75db \u5165\u5fc3\u80ba\uff0c\u624b\u81c2\u4e5f\u540a\u4e86\u4e0b\u6765\uff0c\u4f46\u4ed6\u597d\u4e0d\u72e0\u6bd2\u62fc\u7740\u53e3\u6c14\uff0c\u8d81\u52bf\u5411\u9646\u5c55\u9e4f\u80c1\u4e0b\u6b7b\u7a74\u4e00\u6293\uff0c\u82e5\u7ed9\u4ed6\u6293 \u7740\uff0c\u91d1\u521a\u7f57\u6c49\u4e5f\u53d7\u4e0d\u4e86\u3002\u4e91\u857e\u8fd9\u65f6\u4e5f\u770b\u5f97\u51fa\u795e\u4e86\uff0c\u5fc3\u4e2d\u6b63\u60f3\u8fd9\u4e00\u6293\u82e5\u4e0d\u8bb8\u8fd8\u624b\u53ef\u600e\u751f\u5316\u89e3\uff1f \u5ffd\u542c\u5f97\u6c99\u65e0\u5fcc\u60e8\u53eb\u4e00\u58f0\uff0c\u9646\u5c55\u9e4f\u8eab\u5f62\u672a\u52a8\uff0c\u6c99\u65e0\u5fcc\u5df2\u6367\u7740\u65ad\u81c2\uff0c\u6eda\u4e0b\u64c2\u53f0\uff01\u4e91\u857e\u5927\u60ca\u5931\u8272\uff0c \u8fd9\u6b63\u662f\u6c5f\u6e56\u4e0a\u7f55\u89c1\u7f55\u95fb\u7684\u201c\u6cbe\u8863\u5341\u516b\u8dcc\u201d\u7684\u4e0a\u4e58\u5185\u529f\uff01\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u6709\u8fd9\u6837\u7684\u9ad8\u624b\u53c2\u52a0\u4eca\u79d1 \u6b66\u8bd5\uff0c\u53ea\u6015\u6211\u7684\u54e5\u54e5\u672a\u5fc5\u62a2\u5f97\u4e86\u6b66\u72b6\u5143\uff01\u201d \u3000\u3000\u8fd9\u9646\u5c55\u9e4f\u6b63\u662f\u5eb7\u8d85\u6d77\u7684\u5e08\u5f1f\uff0c\u6b66\u529f\u4e0e\u5eb7\u8d85\u6d77\u4e0d\u76f8\u4e0a\u4e0b\uff0c\u8fd9\u65f6\u6b63\u5728\u6d0b\u6d0b\u5f97\u610f\u65f6\uff0c\u5ffd\u542c\u5f97\u65d7 \u724c\u5b98\u53c8\u53eb\u9053\uff1a\u201c\u7b2c\u5341\u56db\u53f7\u4e3e\u5b50\u4e0a\u53f0\uff01\u201d\u4e91\u857e\u4e00\u770b\uff0c\u53c8\u559c\u53c8\u60ca\uff0c\u6b64\u4eba\u975e\u4ed6\uff0c\u6b63\u662f\u5979\u7684\u54e5\u54e5\u4e91 \u91cd\uff01 \u3000\u3000\u9646\u5c55\u9e4f\u4e3e\u624b\u7b11\u9053\uff1a\u201c\u4e91\u7edf\u9886\u4e5f\u6765\u4e86\uff0c\u8bf7\u4eae\u5175\u5668\uff01\u201d\u4e91\u91cd\u5165\u5fa1\u6797\u519b\u6ca1\u6709\u591a\u5c11\u65f6\u5019\uff0c\u4f46\u6b66\u529f \u51fa\u8272\u5f53\u884c\uff0c\u5df2\u9690\u9690\u6709\u4e0e\u4eac\u5e08\u4e09\u5927\u9ad8\u624b\u5e76\u9a7e\u9f50\u9a71\u4e4b\u52bf\u3002\u9646\u5c55\u9e4f\u4e0d\u6562\u8f7b\u654c\uff0c\u89e3\u4e0b\u91d1\u4e1d\u8f6f\u97ad\uff0c\u62a2\u5728 \u4e0a\u9996\uff0c\u7acb\u4e86\u4e00\u4e2a\u95e8\u6237\u3002\u4ed6\u8fd9\u91d1\u4e1d\u97ad\u4e43\u7528\u91d1\u4e1d\u864e\u7b4b\u4e0e\u5343\u5e74\u5c71\u85e4\u7b49\u7269\u7f20\u6210\uff0c\u53ef\u4ee5\u514b\u5236\u5200\u5251\uff0c\u7aef\u7684 \u5341\u5206\u5389\u5bb3\uff0c\u4e91\u91cd\u4f7f\u7684\u662f\u4e00\u53e3\u7ea2\u6bdb\u5b9d\u5200\u5728\u5175\u5668\u4e0a\u5148\u5403\u4e86\u4e8f\u3002\u53ea\u89c1\u9646\u5c55\u9e4f\u6253\u4e86\u4e00\u4e2a\u62db\u547c\uff0c\u62c9\u5f00\u67b6 \u5f0f\uff0c\u53cd\u624b\u4e00\u97ad\uff0c\u5c31\u5411\u4e91\u91cd\u62e6\u8170\u75be\u626b\uff01 \u3000\u3000\u8fd9\u4e00\u97ad\u52bf\u6377\u5982\u7535\uff0c\u4f46\u4ed6\u5feb\u4e91\u91cd\u4e5f\u5feb\u3002\u53ea\u89c1\u4e91\u91cd\u8eab\u5f62\u4e00\u6643\uff0c\u65cb\u98ce\u822c\u968f\u7740\u97ad\u68a2\u76f4\u8f6c\u51fa\u53bb\uff0c\u91d1 \u4e1d\u8f6f\u97ad\u53cd\u5377\u5230\u4ed6\u7684\u8eab\u4e0a\uff0c\u5374\u662f\u5dee\u4e86\u51e0\u5bf8\uff0c\u8fde\u4ed6\u7684\u8863\u88f3\u4e5f\u6ca1\u6cbe\u7740\u3002\u4e91\u91cd\u53cd\u624b\u5c31\u662f\u4e00\u5200\uff0c\u9646\u5c55\u9e4f \u597d\u751f\u4e86\u5f97\uff0c\u4e00\u4e2a\u201c\u5f2f\u8170\u63d2\u67f3\u201d\uff0c\u5237\uff01\u5237\uff01\u5237\uff01\u8fde\u73af\u4e09\u97ad\uff0c\u547c\u547c\u98ce\u54cd\uff0c\u5377\u8d77\u4e86\u4e00\u56e2\u97ad\u5f71\uff0c\u7adf\u5982 \u72c2\u98ce\u731b\u626b\uff0c\u597d\u4e0d\u60ca\u4eba\u3002\u4e91\u91cd\u7eb5\u8dc3\u5982\u98de\uff0c\u5728\u97ad\u5f71\u7b3c\u7f69\u4e0b\u62a2\u7740\u8fdb\u62db\uff0c\u9646\u5c55\u9e4f\u89c1\u201c\u56de\u98ce\u626b\u67f3\u201d\u7684\u8fde \u73af\u4e09\u97ad\u4e5f\u6253\u4ed6\u4e0d\u7740\uff0c\u624b\u8155\u4e00\u6c89\uff0c\u53c8\u4f7f\u51fa\u6740\u624b\u7edd\u6280\u3002\u53ea\u89c1\u90a3\u8f6f\u97ad\u4e00\u62d0\u547c\u7684\u4e00\u58f0\uff0c\u5ffd\u7136\u5708\u5b50\u8f6c \u6765\uff0c\u5411\u4e91\u91cd\u7684\u624b\u8155\u75be\u7f20\uff0c\u82e5\u7ed9\u4ed6\u7f20\u4e0a\uff0c\u8fd9\u53e3\u5200\u7acb\u523b\u4fbf\u8981\u8131\u624b\u3002\u4e91\u91cd\u201c\u5413\u201d\u7684\u4e00\u58f0\uff0c\u5de6\u624b\u4e00 \u63a8\uff0c\u90a3\u97ad\u68a2\u5ffd\u7136\u6296\u5f97\u7b14\u76f4\uff0c\u8361\u4e86\u5f00\u53bb\uff0c\u638c\u98ce\u98d2\u7136\uff0c\u5370\u5411\u654c\u624b\u80f8\u819b\uff0c\u8fd9\u662f\u5927\u529b\u91d1\u521a\u624b\u7684\u4e0a\u4e58\u529f \u592b\u3002\u9646\u5c55\u9e4f\u53eb\u58f0\u201c\u597d\u554a\u201d\uff0c\u53ea\u89c1\u4ed6\u811a\u6b65\u4e0d\u52a8\u4e0a\u8eab\u9661\u7136\u5411\u540e\u79fb\u4e86\u534a\u5c3a\uff0c\u5de6\u624b\u4e94\u6307\u9a88\u6307\u4e00\u5212\uff0c\u4e24 \u638c\u76f8\u63a5\u672a\u63a5\u4e4b\u9645\uff0c\u5ffd\u5730\u53cc\u65b9\u5df2\u53d8\u62db\uff0c\u97ad\u98de\u5200\u821e\uff0c\u53c8\u5df2\u79fb\u5bab\u6362\u4f4d\uff0c\u7f20\u4f5c\u4e00\u56e2\uff0c\u628a\u4eba\u770b\u5f97\u773c\u82b1\u7f2d \u4e71\uff01 \u3000\u3000\u539f\u6765\u9646\u5c55\u9e4f\u201c\u6cbe\u8863\u5341\u516b\u8dcc\u201d\u7684\u529f\u592b\u4e5f\u662f\u6781\u4e3a\u5389\u5bb3\uff0c\u867d\u7136\u5236\u670d\u4e0d\u4e86\u4e91\u91cd\u7684\u5927\u529b\u91d1\u521a\u624b\uff0c\u5374 \u4e5f\u654c\u5f97\u4ed6\u4f4f\uff0c\u4e91\u91cd\u7684\u91d1\u521a\u624b\u731b\u51fb\u4e09\u638c\uff0c\u90fd\u7ed9\u4ed6\u5378\u4e86\u731b\u52bf\uff0c\u4e5f\u662f\u5403\u60ca\u975e\u5c0f\u3002\u8fd9\u65f6\u53cc\u65b9\u90fd\u5c55\u51fa\u4e86 \u5e73\u751f\u7edd\u5b66\uff0c\u6597\u5175\u5668\uff0c\u6597\u5185\u529f\uff0c\u6597\u638c\u6cd5\uff0c\u51e0\u79cd\u529f\u592b\u6df7\u5408\u8fd0\u7528\uff0c\u53ea\u8981\u54ea\u65b9\u7a0d\u5f31\uff0c\u5c31\u7acb\u523b\u8981\u9707\u4e0b\u64c2 \u53f0\uff0c\u6027\u547d\u96be\u4fdd\u3002\u7687\u5e1d\u770b\u5f97\u8fde\u8fde\u53eb\u597d\u4e91\u857e\u5374\u662f\u6697\u6697\u5fc3\u60ca\uff01 \u3000\u3000\u53ea\u89c1\u4e24\u4eba\u5200\u6765\u97ad\u5f80\uff0c\u6740\u5f97\u5929\u660f\u5730\u6697\uff0c\u5140\u662f\u4e0d\u5206\u80dc\u8d1f\uff0c\u53cc\u65b9\u811a\u6b65\uff0c\u90fd\u6e10\u89c1\u8fdf\u7f13\u3002\u4e91\u857e\u6697 \u60f3\uff1a\u201c\u8fd9\u4e00\u573a\u5c31\u7b97\u54e5\u54e5\u8d62\u4e86\uff0c\u4e5f\u5fc5\u7136\u7d2f\u5f97\u7b4b\u75b2\u529b\u7aed\uff0c\u6bd4\u6b66\u89c4\u77e9\uff0c\u8981\u8fde\u80dc\u4e24\u573a\uff0c\u624d\u80fd\u4f11\u606f\u3002\u8981 \u662f\u4e0b\u4e00\u573a\u53c8\u6709\u4e00\u4e2a\u50cf\u9646\u5c55\u9e4f\u90a3\u6837\u7684\u786c\u624b\uff0c\u8fd9\u6b66\u72b6\u5143\u5c31\u51c6\u5f97\u4e22\u4e86\uff0c\u4f55\u51b5\u8fd9\u4e00\u573a\u4e5f\u672a\u5fc5\u80fd\u8d62\uff01\u201d \u3000\u3000\u4e24\u4eba\u6597\u4e86\u4e00\u767e\u6765\u62db\uff0c\u529f\u529b\u6089\u654c\uff0c\u53cc\u65b9\u90fd\u751a\u7126\u6025\u3002\u4e91\u91cd\u5fd7\u5728\u5fc5\u5f97\uff0c\u8fde\u4f7f\u9669\u62db\uff0c\u91d1\u521a\u624b\u4e00\u8f7b \u4e00\u91cd\uff0c\u5ffd\u5feb\u5ffd\u6162\uff0c\u5bfb\u6687\u62b5\u9699\uff0c\u52a1\u6c42\u5236\u80dc\u3002\u9646\u5c55\u9e4f\u4eba\u8f83\u8001\u7ec3\uff0c\u4e0d\u4e3a\u6240\u52a8\uff0c\u51dd\u795e\u5bf9\u4ed8\u3002\u5ffd\u89c1\u4e91\u91cd \u4e00\u4e2a\u8dc4\u8e09\uff0c\u4fef\u8eab\u8dcc\u8fdb\u91d1\u4e1d\u8f6f\u97ad\u821e\u6210\u7684\u5708\u5b50\u91cc\u9762\uff0c\u53f3\u5200\u5de6\u638c\uff0c\u5411\u9646\u5c55\u9e4f\u4e0a\u4e09\u8def\u6025\u88ad\uff0c\u8fd9\u4e00\u62db\u7528 \u5f97\u9669\u6781\uff0c\u82e5\u7136\u4e00\u51fb\u4e0d\u4e2d\uff0c\u5df1\u8eab\u4e0d\u6b7b\u4e5f\u4f24\u3002\u9646\u5c55\u9e4f\u9053\u58f0\uff1a\u201c\u6765\u5f97\u597d\uff01\u201d\u541e\u80f8\u5438\u8179\uff0c\u8f6f\u97ad\u500f\u5730\u5f80 \u5185\u4e00\u5708\uff0c\u65e2\u907f\u638c\u529b\uff0c\u53c8\u65bd\u53cd\u51fb\uff0c\u8fd9\u62db\u6570\u4e5f\u662f\u7528\u5f97\u72e0\u6bd2\u4e4b\u6781\uff0c\u4e91\u857e\u51e0\u4e4e\u558a\u51fa\u58f0\u6765\u3002\u5ffd\u542c\u5f97\u9646\u5c55 \u9e4f\u201c\u54ce\u54df\u201d\u4e00\u58f0\uff0c\u4e91\u857e\u672a\u53ca\u770b\u6e05\uff0c\u53ea\u89c1\u4ed6\u5df2\u64a4\u97ad\u8dcc\u5012\uff0c\u6eda\u4e0b\u64c2\u53f0\uff01\u539f\u6765\u4ed6\u521a\u521a\u51fa\u62db\u53cd\u51fb\uff0c\u53cd \u8155\u5ffd\u5982\u7ed9\u5229\u9488\u4e00\u523a\uff0c\u9ad8\u624b\u8f83\u6280\uff0c\u54ea\u5bb9\u9047\u7740\u610f\u5916\uff0c\u5e78\u4ed6\u95ea\u6eda\u5f97\u5feb\uff0c\u8fd9\u624d\u4e0d\u81f3\u4e8e\u6bd9\u5728\u5927\u529b\u91d1\u521a\u624b \u4e4b\u4e0b\u3002\u4ed6\u5fc3\u4e2d\u6697\u9a82\uff1a\u201c\u54fc\uff0c\u8fd9\u5c0f\u5b50\u5c45\u7136\u638c\u5fc3\u8fd8\u6263\u6709\u6697\u5668\uff0c\u53d7\u8fd9\u6697\u7b97\uff0c\u771f\u4e2a\u4e0d\u503c\uff01\u201d\u53ef\u662f\u6bd4\u6b66 \u5e76\u4e0d\u7981\u6697\u5668\uff0c\u4ed6\u4e5f\u505a\u58f0\u4e0d\u5f97\u3002\u5176\u5b9e\u4ed6\u5374\u4e0d\u77e5\uff0c\u8fd9\u98de\u9488\u6697\u5668\u5374\u5e76\u4e0d\u662f\u4e91\u91cd\u53d1\u7684\uff01 \u3000\u3000\u770b\u53f0\u4e0a\u7684\u4e91\u857e\uff0c\u64c2\u53f0\u4e0a\u7684\u4e91\u91cd\uff0c\u90fd\u662f\u5927\u60d1\u4e0d\u89e3\uff01\u53ea\u542c\u5f97\u65d7\u724c\u5b98\u53c8\u53eb\u9053\uff1a\u201c\u7b2c\u5341\u4e94\u53f7\u4e3e\u5b50 \u5f20\u4e39\u67ab\u4e0a\u53f0\uff0c\u4fdd\u4eba\u9526\u8863\u536b\u6307\u6325\u517c\u5fa1\u6797\u519b\u603b\u6559\u5934\u5f20\u98ce\u5e9c\uff01\u201d\u4e91\u857e\u4e00\u542c\uff0c\u7075\u9b42\u513f\u98de\u4e0a\u534a\u5929\uff0c\u767b\u65f6 \u5446\u4e86\uff01\u5f20\u4e39\u67ab\u7adf\u7136\u4e5f\u4f1a\u53c2\u52a0\u6bd4\u6b66\uff0c\u4e0e\u81ea\u5df1\u7684\u54e5\u54e5\u4e89\u593a\u72b6\u5143\uff0c\u6b64\u4e8b\u53ef\u771f\u662f\u7edd\u5bf9\u6599\u60f3\u4e0d\u5230\uff01\u6b63 \u662f\uff1a \u3000\u3000\u53c8\u89c1\u5f20\u90ce\u65bd\u5999\u7b97\uff0c\u795e\u9488\u5b9d\u5251\u89e3\u6df1\u4ec7\u3002 \u3000\u3000\u6b32\u77e5\u5f20\u4e39\u67ab\u4e0e\u4e91\u91cd\u8c01\u4eba\u593a\u5f97\u6b66\u72b6\u5143\uff0c\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u840d\u8e2a\u4fa0\u5f71\u5f55\u300b\u2014\u2014\u7b2c\u5341\u516d\u56de\u3000\u559d\u96c9\u547c\u5362\u540d\u56ed\u4f5c\u8c6a\u8d4c\u3000\u626c\u58f0\u63b7\u9ab0\u4fa0\u5ba2\u5106\u51f6\u987d \u6881\u7fbd\u751f\u300a\u840d\u8e2a\u4fa0\u5f71\u5f55\u300b \u7b2c\u5341\u516d\u56de\u3000\u559d\u96c9\u547c\u5362\u540d\u56ed\u4f5c\u8c6a\u8d4c\u3000\u626c\u58f0\u63b7\u9ab0\u4fa0\u5ba2\u5106\u51f6\u987d \u3000\u3000\u4e91\u857e\u5446\u5446\u5730\u671b\u5411\u64c2\u53f0\uff0c\u53ea\u89c1\u5f20\u4e39\u67ab\u767d\u8863\u98d8\u98d8\uff0c\u811a\u767b\u7c89\u5e95\u978b\u5934\u6234\u767d\u65b9\u5dfe\uff0c\u886c\u7740\u7c89\u96d5\u7389\u7422\u7684 \u9762\u5e9e\uff0c\u7b11\u541f\u541f\u5730\u7eb5\u8eab\u4e0a\u64c2\u53f0\uff0c\u59ff\u6001\u7f8e\u5999\u4e4b\u6781\uff0c\u771f\u6709\u5982\u7389\u6811\u4e34\u98ce\uff0c\u68a8\u82b1\u98d8\u96ea\uff0c\u7aef\u7684\u662f\u4eba\u7269\u4fca \u79c0\uff0c\u6f47\u6d12\u51fa\u5c18\u3002\u8fd9\u4e00\u767b\u53f0\uff0c\u6ee1\u573a\u6b66\u58eb\u90fd\u7ed9\u4ed6\u6bd4\u4e86\u4e0b\u53bb\uff0c\u5c1a\u672a\u51fa\u624b\uff0c\u5df2\u8d62\u5f97\u4e00\u7247\u5f69\u58f0\u3002\u7687\u5e1d\u5750 \u5728\u6b63\u9762\u770b\u53f0\uff0c\u5fc3\u4e2d\u4e5f\u6697\u6697\u8d5e\u9053\uff1a\u597d\u4e2a\u98ce\u6d41\u4eba\u7269\uff01\u7b11\u5bf9\u603b\u7ba1\u5eb7\u8d85\u6d77\u9053\uff1a\u201c\u8fd9\u4eba\u5012\u5e94\u8be5\u53bb\u8003\u6587\u72b6 \u5143\uff01\u201d\u5eb7\u8d85\u6d77\u542b\u7cca\u5e94\u4e86\u4e00\u58f0\uff0c\u76ee\u4e0d\u8f6c\u6674\u5730\u76ef\u7740\u5f20\u4e39\u67ab\uff0c\u9762\u4e0a\u663e\u51fa\u51dd\u60d1\u7684\u795e\u8272\u3002\u53ea\u89c1\u5f20\u4e39\u67ab\u5411 \u6b63\u9762\u770b\u53f0\u779f\u4e86\u4e00\u773c\uff0c\u773c\u5149\u6709\u5982\u5bd2\u51b0\u5229\u526a\u500f\u5730\u4ece\u7687\u5e1d\u7948\u9547\u9762\u4e0a\u4e00\u63a0\u800c\u8fc7\uff0c\u7687\u5e1d\u4e0d\u89c9\u6253\u4e86\u4e00\u4e2a\u5bd2 \u5664\u5fc3\u9053\uff1a\u201c\u8fd9\u4eba\u770b\u6765\u5112\u96c5\u98ce\u6d41\uff0c\u773c\u5149\u5374\u5145\u6ee1\u6740\u6c14\uff01\u201d\u4ed6\u54ea\u91cc\u77e5\u9053\uff0c\u5f20\u4e39\u67ab\u7684\u7956\u5148\uff0c\u5c31\u662f\u548c\u4ed6 \u6731\u5bb6\u4e89\u593a\u6c5f\u5c71\u7684\u5927\u4ec7\u4eba\uff01 \u3000\u3000\u5f20\u4e39\u67ab\u8fd9\u4e00\u767b\u53f0\u6bd4\u6b66\uff0c\u4e0d\u4f46\u662f\u5927\u51fa\u4e91\u857e\u610f\u6599\u4e4b\u5916\uff0c\u4e8e\u8c26\u548c\u4e91\u91cd\u4e5f\u662f\u4e07\u4e07\u6599\u60f3\u4e0d\u5230\uff01\u4e8e\u8c26 \u60f3\u9053\uff1a\u201c\u5f20\u4e39\u67ab\u4e43\u5f53\u4e16\u5947\u624d\uff0c\u6211\u5c61\u6b21\u529d\u8bf4\u4ed6\u4e3a\u671d\u5ef7\u6548\u529b\uff0c\u613f\u4ee5\u8eab\u5bb6\u6027\u547d\u4fdd\u8350\u4ed6\u4ed6\u90fd\u4e0d\u5141\uff0c\u600e \u4e48\u4ed6\u5374\u4f1a\u6765\u8003\u8fd9\u52b3\u4ec0\u5b50\u7684\u6b66\u72b6\u5143\uff1f\u201d\u4e91\u91cd\u66f4\u662f\u5403\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u25a1\u660e\u660e\u662f\u74e6\u523a\u7684\u5978\u8d3c\uff0c\u4e3a\u4f55 \u4ed6\u4e5f\u6765\u4e0e\u6211\u4e89\u593a\u6b66\u72b6\u5143\uff1f\u201d\u6b32\u5f85\u559d\u7834\u4ed6\u7684\u8eab\u4efd\uff0c\u5374\u53c8\u788d\u4e8e\u4ed6\u4e43\u662f\u81ea\u5df1\u9876\u5934\u4e0a\u53f8\u5f20\u98ce\u5e9c\u4fdd\u8350 \u7684\u3002\u56e0\u6b64\u4e91\u91cd\u867d\u7136\u6df1\u5fc3\u6124\u6068\uff0c\u5374\u662f\u505a\u58f0\u4e0d\u5f97\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u65cb\u8f6c\u8eab\u8eaf\uff0c\u9762\u5bf9\u4e91\u91cd\uff0c\u7b11\u541f\u541f\u5730\u624b\u629a\u5251\u67c4\uff0c\u4e00\u63d6\u8bf4\u9053\uff1a\u201c\u4e91\u5144\u624b\u4e0b\u7559\u60c5\uff01\u201d\u4e91\u91cd \u5fc3\u5934\u6012\u8d77\uff0c\u773c\u4e2d\u76f4\u6b32\u55b7\u51fa\u706b\u6765\u3002\u53ef\u662f\u8eab\u5728\u64c2\u53f0\u4e4b\u4e0a\uff0c\u5728\u4f17\u76ee\u777d\u777d\u4e4b\u4e0b\uff0c\u5374\u53c8\u4e0d\u80fd\u5931\u793c\uff0c\u53ea\u597d \u53cc\u76ee\u5706\u7741\uff0c\u4e5f\u629a\u5200\u8fd8\u4e86\u4e00\u63d6\uff0c\u4f4e\u58f0\u559d\u9053\uff1a\u201c\u4eca\u65e5\u4e0d\u662f\u4f60\u6b7b\uff0c\u4fbf\u662f\u6211\u4ea1\uff01\u201d\u5f20\u4e39\u67ab\u4e00\u7b11\u9053\uff1a \u201c\u8fd9\u53c8\u4f55\u5fc5\uff01\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u53ea\u89c1\u4e91\u91cd\u4e00\u4e2a\u201c\u8de8\u864e\u767b\u5c71\u201d\u5f0f\uff0c\u547c\u7684\u4e00\u58f0\uff0c\u5927\u529b\u91d1\u521a\u624b\u731b\u7136\u53d1 \u51fa\u3002\u4ed6\u4e0e\u5f20\u4e39\u67ab\u884c\u8fc7\u4e86\u6b66\u58eb\u7684\u89c1\u9762\u793c\u4eea\uff0c\u518d\u4e5f\u4e0d\u5ba2\u6c14\u4e86\u3002 \u3000\u3000\u4e91\u857e\u6025\u5f97\u76f4\u5c1a\u51b7\u6c57\uff0c\u4f46\u89c1\u64c2\u53f0\u4e4b\u4e0a\uff0c\u5f20\u4e39\u67ab\u53f3\u624b\u4e00\u52fe\uff0c\u6c89\u638c\u4e00\u5f15\uff0c\u521a\u559d\u5f97\u4e00\u4e2a\u201c\u597d\u201d \u5b57\uff0c\u4e91\u91cd\u5b9d\u5200\u4e00\u8d77\uff0c\u9752\u5149\u75be\u95ea\uff0c\u5200\u968f\u638c\u53d1\uff0c\u53c8\u5df2\u4eba\u659c\u523a\u5288\u6765\uff01\u8fd9\u4e00\u638c\u4e91\u91cd\u7528\u7684\u662f\u5343\u65a4\u5927\u529b\u7684 \u91cd\u624b\u6cd5\uff0c\u88ab\u5f20\u4e39\u67ab\u8f7b\u63cf\u6de1\u5199\u5730\u5378\u52b2\u5316\u5f00\uff0c\u5fc3\u4e2d\u5b9e\u662f\u5403\u60ca\u975e\u5c0f\u3002\u6240\u4ee5\u90a3\u4e00\u5200\u5288\u4e0b\uff0c\u66f4\u662f\u7edd\u4e0d\u7559 \u60c5\u3002\u800c\u5f20\u4e39\u67ab\u6697\u8fd0\u5185\u5bb6\u771f\u529b\uff0c\u4ee5\u53f3\u624b\u7684\u529b\u9053\u624d\u521a\u80fd\u62b5\u6d88\u4e91\u91cd\u5de6\u624b\u7684\u52b2\u529b\uff0c\u5fc3\u4e2d\u4e5f\u662f\u6697\u81ea\u8d5e \u9053\uff1a\u201c\u5927\u5e08\u4f2f\u7684\u5927\u529b\u91d1\u521a\u624b\u6cd5\uff0c\u679c\u7136\u540d\u4e0d\u865a\u4f20\uff01\u201d\u4e0d\u6562\u6020\u6162\uff0c\u4e00\u4e2a\u53cd\u8eab\u62d4\u5251\uff0c\u5c31\u5728\u4e91\u91cd\u7684\u5b9d \u5200\u582a\u582a\u5288\u5230\u4e4b\u9645\u5237\u7684\u8fd8\u4e86\u4e00\u62db\u3002\u8fd9\u4e00\u62db\u6321\u5f97\u6070\u5230\u597d\u5904\uff0c\u4e91\u91cd\u4e5f\u4e0d\u89c9\u9053\u51fa\u4e00\u4e2a\u201c\u597d\u201d\u5b57\uff0c\u5200\u950b \u4e00\u8f6c\uff0c\u6025\u6025\u53d8\u62db\u6a2a\u626b\u3002 \u3000\u3000\u4e91\u91cd\u5fc3\u77e5\u5f20\u4e39\u67ab\u7684\u5b9d\u5251\u4e43\u662f\u795e\u7269\u5229\u5668\uff0c\u8fdc\u975e\u81ea\u5df1\u7684\u7ea2\u6bdb\u5b9d\u5200\u53ef\u6bd4\uff0c\u6df1\u6050\u88ab\u4ed6\u5b9d\u5251\u524a\u65ad\uff0c \u6240\u4ee5\u7528\u7684\u5168\u662f\u6a2a\u622a\u624b\u6cd5\uff0c\u5200\u5149\u95ea\u95ea\uff0c\u4e0d\u79bb\u5f20\u4e39\u67ab\u7684\u5173\u8282\u8981\u5bb3\u3002\u8fd9\u662f\u4ece\u8fd1\u8eab\u7f20\u6597\u7684\u6454\u89d2\u4e4b\u6280\u53d8 \u5316\u51fa\u6765\uff0c\u5b8c\u5168\u662f\u62fc\u4e2a\u4e24\u8d25\u4ff1\u4f24\u7684\u6218\u6cd5\uff0c\u6bcf\u4e00\u62db\u5f0f\uff0c\u90fd\u7528\u5f97\u9669\u6076\u975e\u5e38\uff01 \u3000\u3000\u5f20\u4e39\u67ab\u4e00\u58f0\u957f\u7b11\uff0c\u957f\u5251\u4e00\u5708\uff0c\u8eab\u5f62\u4e00\u8f6c\uff0c\u53ea\u89c1\u5251\u5149\u75be\u8d77\uff0c\u500f\u65f6\u51b7\u7535\u7cbe\u8292\uff0c\u7f24\u7eb7\u98de\u821e\uff0c\u5251 \u98ce\u98d2\u7136\uff0c\u64c2\u53f0\u4e4b\u4e0a\uff0c\u90fd\u662f\u5f20\u4e39\u67ab\u7684\u5f71\u5b50\uff0c\u5c31\u5982\u6709\u6570\u5341\u4eba\u6301\u5251\uff0c\u4ece\u56db\u9762\u516b\u65b9\u75be\u653b\u800c\u6765\u3002\u4e91\u91cd\u5140 \u7acb\u53f0\u5fc3\uff0c\u4e0d\u6562\u79fb\u52a8\u534a\u6b65\uff0c\u4f46\u89c1\u4eba\u5f71\u95ea\u65f6\uff0c\u4fbf\u662f\u4e00\u5200\uff0c\u6bcf\u4e00\u62db\u90fd\u662f\u5feb\u5982\u95ea\u7535\u3002\u4e91\u91cd\u7684\u6a2a\u622a\u65ad\u95e8 \u5200\u6cd5\u867d\u7136\u72e0\u8fa3\uff0c\u4f46\u5f20\u4e39\u67ab\u8eab\u6cd5\u5feb\u5230\u6781\u70b9\uff0c\u6709\u5982\u6674\u8713\u70b9\u6c34\uff0c\u4e00\u63a0\u5373\u8fc7\uff0c\u53cc\u65b9\u6597\u4e86\u4e94\u4e03\u5341\u62db\uff0c\u5140 \u662f\u6beb\u53d1\u65e0\u4f24\u3002\u7687\u5e1d\u770b\u5f97\u7709\u98de\u8272\u821e\uff0c\u5927\u53eb\uff1a\u201c\u597d\u554a\uff0c\u597d\u554a\uff01\u201d\u4e91\u857e\u5374\u662f\u5fc3\u6025\u5982\u711a\uff0c\u65e2\u6015\u5f20\u4e39\u67ab \u4f24\u4e86\u4e91\u91cd\uff0c\u4e5f\u6015\u4e91\u91cd\u4f24\u4e86\u5f20\u4e39\u67ab\u3002 \u3000\u3000\u5728\u65c1\u4eba\u770b\u6765\uff0c\u8fd9\u4e24\u4eba\u4e00\u4e2a\u5251\u6cd5\u7cbe\u5999\uff0c\u4e00\u4e2a\u5200\u6cd5\u72e0\u8fa3\uff0c\u6070\u662f\u529f\u529b\u6089\u654c\uff0c\u96be\u5206\u8f69\u8f7e\uff0c\u4f46\u5728\u4e91 \u857e\u770b\u6765\uff0c\u5176\u4e2d\u5374\u6709\u9ad8\u4e0b\u3002\u4e91\u857e\u66fe\u4e0e\u5f20\u4e39\u67ab\u6570\u5ea6\u8054\u5251\u5bf9\u654c\uff0c\u8bc6\u5f97\u5f20\u4e39\u67ab\u5251\u6cd5\u7684\u7cbe\u5fae\u5965\u5999\u6240\u5728\uff0c \u4ed6\u6218\u4e86\u8fd9\u4e48\u4e9b\u65f6\u5019\uff0c\u5374\u8fd8\u6ca1\u6709\u4e00\u62db\u65bd\u5c55\u6740\u624b\uff0c\u786e\u4f3c\u6709\u610f\u7559\u60c5\u3002\u800c\u4e91\u91cd\u5df2\u662f\u51fa\u5c3d\u5168\u529b\u3002\u9ad8\u624b\u6bd4 \u6b66\uff0c\u80dc\u8d25\u751f\u6b7b\uff0c\u76f8\u5dee\u53ea\u5728\u6beb\u53d1\u4e4b\u95f4\u56e0\u6b64\u53cc\u65b9\u9669\u62db\u8fed\u89c1\uff0c\u800c\u5f20\u4e39\u67ab\u9047\u9669\u7684\u6b21\u6570\u66f4\u6bd4\u4e91\u91cd\u4e3a\u591a\u3002 \u4e8e\u8c26\u4e5f\u770b\u5f97\u5fc3\u60ca\u80c6\u6218\uff0c\u4f3c\u662f\u81ea\u8a00\u81ea\u8bed\uff0c\u53c8\u4f3c\u662f\u5bf9\u4e91\u857e\u8bf4\u9053\uff1a\u201c\u4e24\u864e\u76f8\u6597\uff0c\u5fc5\u6709\u4e00\u4f24\uff0c\u8fd9\u771f\u662f \u4f55\u82e6\u6765\uff1f\u4f55\u82e6\u6765\uff1f\u201d\u4f46\u8fd9\u662f\u62a1\u5143\u5927\u5178\u8c01\u4e5f\u4e0d\u80fd\u5236\u6b62\u3002 \u3000\u3000\u4e91\u91cd\u51fa\u5c3d\u5168\u529b\uff0c\u8fd8\u53ea\u662f\u582a\u582a\u6253\u4e2a\u5e73\u624b\uff0c\u5fc3\u5934\u7126\u8e81\u4e4b\u6781\u3002\u66f4\u517c\u4ed6\u9002\u624d\u4e0e\u9646\u5c55\u9e4f\u82e6\u62fc\u4e86\u4e00 \u573a\uff0c\u8017\u4e86\u4e0d\u5c11\u6c14\u529b\uff0c\u800c\u4eca\u4e0e\u5f20\u4e39\u67ab\u53c8\u662f\u4e00\u573a\u6076\u6218\uff0c\u62fc\u4e86\u516d\u4e03\u5341\u62db\uff0c\u6e10\u611f\u6c14\u529b\u4e0d\u652f\u3002\u5f20\u4e39\u67ab\u4ecd \u662f\u6325\u6d12\u81ea\u5982\uff0c\u4f46\u4ed6\u6bcf\u4e00\u62db\u90fd\u4f7f\u5f97\u6070\u5230\u597d\u5904\uff0c\u5ffd\u75be\u5ffd\u5f90\uff0c\u7edd\u4e0d\u8ba9\u4e91\u91cd\u9732\u51fa\u8d25\u8c61\uff0c\u4ecd\u662f\u7ef4\u6301\u7740\u5e73 \u624b\u7684\u5c40\u9762\u3002\u8fd9\u65f6\u8fde\u4e91\u91cd\u4e5f\u89c9\u51fa\u4ed6\u662f\u6709\u610f\u76f8\u8ba9\u4e86\uff0c\u8d8a\u53d1\u706b\u8d77\uff0c\u731b\u8fd0\u91d1\u521a\u5927\u529b\u624b\u6cd5\uff0c\u53f3\u624b\u4e00\u5200\uff0c \u5de6\u624b\u4e00\u638c\uff0c\u547c\u547c\u547c\uff0c\u8fde\u5288\u4e09\u638c\uff0c\u65bd\u5c55\u5e08\u95e8\u7edd\u6280\uff0c\u91d1\u5200\u5939\u638c\uff0c\u628a\u5f20\u4e39\u67ab\u903c\u5230\u79bb\u8eab\u6570\u5c3a\u4e4b\u5916\uff0c\u9aa4 \u7136\u4e00\u4e2a\u7ffb\u8eab\uff0c\u62d6\u5200\u4fbf\u8d70\u3002\u5f20\u4e39\u67ab\u5fc3\u4e2d\u6697\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u62d6\u5200\u8bc8\u8d25\u4e4b\u8ba1\u9a97\u5f97\u8c01\u6765\uff1f\u201d\u5c06\u8ba1\u5c31\u8ba1\u633a \u5251\u76f4\u903c\uff0c\u54ea\u77e5\u4e91\u91cd\u53c8\u662f\u4e00\u4e2a\u201c\u9e5e\u5b50\u7ffb\u8eab\u201d\uff0c\u5de6\u624b\u4e00\u626c\uff0c\u53ea\u542c\u5f97\u94ee\u94ee\u6570\u58f0\u516d\u4e03\u7c92\u94c1\u83b2\u5b50\u7834\u7a7a\u98de \u51fa\uff0c\u4e92\u76f8\u6fc0\u8361\uff0c\u6216\u8d70\u76f4\u7ebf\uff0c\u6216\u6210\u5f27\u5f62\uff0c\u659c\u65b9\u62d0\u89d2\u98de\u6765\uff0c\u5168\u662f\u5954\u5411\u5f20\u4e39\u67ab\u7684\u8981\u5bb3\u7a74\u9053\u3002\u8fd9\u79cd\u6253 \u6697\u5668\u7684\u624b\u6cd5\u4e43\u662f\u7384\u673a\u9038\u58eb\u7684\u72ec\u95e8\u7edd\u6280\uff0c\u6697\u5668\u7adf\u7136\u53ef\u4ee5\u62d0\u5f2f\u6253\u7a74\uff0c\u76f4\u628a\u573a\u4e2d\u6240\u6709\u9ad8\u624b\u90fd\u770b\u5f97\u76ee \u77aa\u53e3\u5446\uff01 \u3000\u3000\u5ffd\u542c\u5f97\u53ee\u53ee\u53ee\u51e0\u58f0\u8fde\u54cd\uff0c\u58f0\u97f3\u5fae\u7ec6\uff0c\u5728\u94c1\u83b2\u5b50\u6fc0\u8361\u7684\u58f0\u97f3\u906e\u76d6\u4e4b\u4e0b\uff0c\u770b\u53f0\u4e0a\u7684\u4eba\u51e0\u4e4e\u5206 \u8fa8\u4e0d\u51fa\uff0c\u4f46\u4e91\u91cd\u5374\u662f\u5165\u8033\u523a\u5fc3\uff0c\u53ea\u89c1\u6240\u53d1\u51fa\u7684\u94c1\u83b2\u5b50\u5168\u90fd\u88ab\u6253\u843d\u53f0\u4e0b\u3002\u4e91\u91cd\u662f\u540d\u5e08\u9ad8\u8db3\uff0c\u81ea \u7136\u77e5\u9053\u8fd9\u4e43\u662f\u88ab\u5f20\u4e39\u67ab\u6240\u53d1\u7684\u6697\u5668\u51fb\u843d\uff0c\u4f46\u542c\u58f0\u8fa8\u5668\uff0c\u4e0d\u8fc7\u662f\u6885\u82b1\u9488\u4e4b\u7c7b\u7684\u6781\u5fae\u7ec6\u7684\u6697\u5668\uff0c \u800c\u7adf\u7136\u80fd\u628a\u4ed6\u7528\u91cd\u624b\u6cd5\u53d1\u51fa\uff0c\u800c\u4e14\u4f53\u79ef\u6bd4\u6885\u82b1\u9488\u5927\u6570\u5341\u500d\u7684\u94c1\u83b2\u5b50\u6253\u843d\uff0c\u8fd9\u4efd\u529f\u529b\uff0c\u771f\u662f\u975e \u540c\u5c0f\u53ef\uff01\u4e0d\u7279\u6b64\u4e5f\uff0c\u5f20\u4e39\u67ab\u8fd9\u4e00\u51fa\u624b\uff0c\u7acb\u523b\u4ee4\u4e91\u91cd\u60f3\u8d77\u521a\u624d\u7684\u4e00\u6869\u602a\u4e8b\uff01 \u3000\u3000\u4e91\u91cd\u60f3\u8d77\u4e0a\u4e00\u573a\u4e0e\u9646\u5c55\u9e4f\u82e6\u6597\u4e4b\u65f6\uff0c\u6700\u540e\u90a3\u4e00\u51fb\uff0c\u672c\u6765\u53cc\u65b9\u90fd\u5f97\u4e24\u8d25\u4ff1\u4f24\uff0c\u4f46\u5728\u6700\u6700\u5371 \u9669\u7684\u5173\u5934\uff0c\u9646\u5c55\u9e4f\u5ffd\u7136\u83ab\u540d\u5176\u5999\u5730\u8dcc\u5012\uff0c\u5f53\u65f6\u4e91\u91cd\u4e5f\u662f\u5927\u60d1\u4e0d\u89e3\uff0c\u800c\u4eca\u770b\u4e86\u5f20\u4e39\u67ab\u6240\u53d1\u7684\u6697 \u5668\u4e0d\u89c9\u604d\u7136\u5927\u609f\uff1a\u539f\u6765\u521a\u624d\u6697\u7b97\u9646\u5c55\u9e4f\u7684\u7adf\u7136\u662f\u5f20\u4e39\u67ab\uff01\u60f3\u4e0d\u5230\u8fd9\u4e2a\u201c\u4ec7\u6df1\u5982\u6d77\u201d\u7684\u654c\u4eba\uff0c \u7adf\u7136\u6697\u52a9\u81ea\u5df1\uff01 \u3000\u3000\u8fd9\u970e\u90a3\u95f4\uff0c\u4e91\u91cd\u53c8\u662f\u7f9e\u6127\u53c8\u662f\u611f\u6fc0\uff0c\u4f46\u4e5f\u8fd8\u6709\u51e0\u5206\u6124\u6068\u3002\u6b63\u5728\u4e0d\u77e5\u6240\u63aa\uff0c\u5ffd\u542c\u5f97\u5f20\u4e39\u67ab \u7b11\u9053\uff1a\u201c\u770b\u5251\uff01\u201d\u773c\u524d\u767d\u5149\u4e00\u95ea\u5f20\u4e39\u67ab\u53c8\u662f\u5237\u7684\u4e00\u5251\u523a\u6765\uff0c\u4e91\u91cd\u672c\u80fd\u5730\u8fd8\u4e86\u4e00\u5200\uff0c\u6b63\u5728\u601d \u91cf\uff0c\u8fd9\u4e2a\u6b66\u72b6\u5143\u8be5\u4e0d\u8be5\u62f1\u624b\u76f8\u8ba9\uff0c\u5ffd\u7136\u53d1\u89c9\u5f20\u4e39\u67ab\u7684\u5251\u5149\u5df2\u628a\u81ea\u5df1\u524d\u540e\u5de6\u53f3\u7684\u9000\u8def\u5168\u90fd\u5c01 \u7740\uff0c\u770b\u4ed6\u5251\u52bf\u5982\u8679\uff0c\u4e0b\u4e00\u624b\u4fbf\u662f\u6740\u624b\uff0c\u4e91\u91cd\u5927\u5403\u4e00\u60ca\u3002\u4e60\u6b66\u4e4b\u4eba\uff0c\u9047\u9669\u5fc5\u6551\u5df2\u6210\u4e60\u60ef\uff0c\u8fd9\u65f6 \u8be5\u4e0d\u8be5\u7167\u6c5f\u6e56\u89c4\u77e9\uff0d\uff0d\u5fc3\u77e5\u4e0d\u654c\uff0c\u4fbf\u8be5\u76f8\u8ba9\uff0c\u5df2\u662f\u65e0\u6687\u8003\u8651\uff0c\u6025\u6025\u5de6\u638c\u6a2a\u622a\uff0c\u53f3\u5200\u4e00\u7a7f\uff0c\u6b63 \u60f3\u7528\u201c\u5d29\u53bb\u88c2\u77f3\u201d\u7684\u62db\u6570\u786c\u63a5\u786c\u89e3\uff0c\u5ffd\u542c\u5f97\u5f20\u4e39\u67ab\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u8fd9\u62db\u4e0d\u884c\uff0c\u5feb\u7528\u4e09\u7f8a\u5f00 \u6cf0\uff01\u201d\u4e91\u91cd\u4e0d\u7531\u81ea\u4e3b\u5730\u55d6\u55d6\u55d6\u8fde\u5288\u4e09\u5200\uff0c\u679c\u7136\u4f7f\u51fa\u4e00\u62db\u4e09\u5f0f\u7684\u201c\u4e09\u7f8a\u5f00\u6cf0\u201d\u62db\u6570\u3002\u5f20\u4e39\u67ab\u4f7f \u7684\u662f\u201c\u516b\u65b9\u98ce\u96e8\u201d\u7684\u5c01\u95ed\u5251\u672f\uff0c\u8fd9\u65f6\u5251\u5c16\u521a\u521a\u753b\u4e86\u534a\u9053\u5706\u5f27\uff0c\u62db\u6570\u5c1a\u672a\u7528\u5c3d\uff0c\u5ffd\u88ab\u201c\u4e09\u7f8a\u5f00 \u6cf0\u201d\u7684\u62db\u6570\u4e00\u51b2\uff0c\u987f\u65f6\u53cd\u5ba2\u4e3a\u4e3b\uff0c\u95e8\u6237\u5927\u5f00\uff0c\u5c16\u53eb\u4e00\u58f0\uff0c\u4e91\u91cd\u62db\u6570\u4f7f\u5f00\uff0c\u6536\u624b\u4e0d\u4f4f\uff0c\u53c8\u662f\u5de6 \u53f3\u4e2d\u8fde\u5288\u4e09\u5200\u3002\u53ea\u89c1\u5f20\u4e39\u67ab\u8fde\u8fde\u540e\u9000\uff0c\u5230\u4e91\u91cd\u7b2c\u4e09\u5200\u75be\u5288\u6765\u65f6\uff0c\u4f3c\u662f\u65e0\u53ef\u62b5\u654c\uff0c\u5ffd\u7136\u4e00\u4e2a \u201c\u7ec6\u80f8\u5de7\u7ffb\u4e91\u201d\uff0c\u7ffb\u8eab\u4e00\u4e2a\u5012\u7eb5\uff0c\u8eab\u5f62\u5982\u7bad\uff0c\u5411\u540e\u75be\u98de\uff0c\u7adf\u7136\u4f3c\u65ad\u4e86\u7ebf\u7684\u98ce\u7b5d\u4e00\u822c\uff0c\u8f7b\u98d8\u98d8 \u5730\u98d8\u843d\u5730\u4e0b\u3002\u80dc\u8d25\u5df2\u5224\uff0c\u5f20\u4e39\u67ab\u8f93\u4e86\uff01\u6ee1\u573a\u9ad8\u624b\uff0c\u90fd\u4e0d\u7981\u8f70\u7136\u559d\u5f69\uff0c\u79f0\u8d5e\u4e91\u91cd\u90a3\u4e00\u624b\u53cd\u8d25\u4e3a \u80dc\u7684\u201c\u4e09\u7f8a\u5f00\u6cf0\u201d\u62db\u6570\uff0c\u771f\u662f\u5999\u5230\u6beb\u5dc5\uff0c\u9664\u4e86\u4e91\u857e\uff0c\u8c01\u4e5f\u770b\u4e0d\u51fa\u662f\u5f20\u4e39\u67ab\u6545\u610f\u76f8\u8ba9\uff01 \u3000\u3000\u539f\u6765\u5f20\u4e39\u67ab\u4e4b\u6240\u4ee5\u53c2\u56de\u6bd4\u6b66\uff0c\u76ee\u7684\u5c31\u5728\u4e8e\u6697\u52a9\u4e91\u91cd\u593a\u53d6\u6b66\u72b6\u5143\u3002\u5f20\u4e39\u67ab\u77e5\u9053\u5927\u5185\u603b\u7ba1\u5eb7 \u8d85\u6d77\u7684\u4e24\u4e2a\u5e08\u5144\u5f1f\u4e5f\u53c2\u56de\u6bd4\u6b66\uff0c\u8fd9\u4e24\u4eba\u6b66\u529f\u4e0e\u4e91\u91cd\u4e0d\u76f8\u4e0a\u4e0b\uff0c\u5c1a\u6709\u6570\u540d\u9ad8\u624b\uff0c\u6b66\u529f\u4ea6\u4e0d\u8fc7\u6bd4 \u4e91\u91cd\u7565\u900a\u4e00\u7b79\u3002\u7167\u8003\u8bd5\u7684\u89c4\u77e9\uff0c\u6700\u5c11\u8981\u8fde\u6253\u4e24\u573a\u624d\u80fd\u4f11\u606f\uff0c\u5219\u4e91\u91cd\u5b9e\u662f\u6beb\u65e0\u628a\u63e1\uff0c\u56e0\u6b64\u5f20\u4e39 \u67ab\u624d\u5192\u8fd9\u7edd\u5927\u7684\u5371\u9669\uff0c\u53eb\u5f20\u98ce\u5e9c\u4f5c\u4fdd\uff0c\u4e5f\u6765\u53c2\u52a0\u8003\u8bd5\u3002\u5728\u524d\u65e5\u7684\u6dd8\u6c70\u8d5b\u4e2d\uff0c\u4ed6\u4e0d\u4e0e\u4e91\u91cd\u540c \u7ec4\uff0c\u800c\u4e0e\u5eb7\u8d85\u6d77\u7684\u53e6\u4e00\u4e2a\u5e08\u5144\uff0c\u53ca\u540d\u6b66\u5e08\u91d1\u94a9\u5434\u950b\u3001\u536b\u58eb\u8def\u660e\u7b49\u9ad8\u624b\u540c\u7ec4\uff0c\u5f20\u4e39\u67ab\u5c06\u8fd9\u4e09\u4eba \u5168\u90fd\u6dd8\u6c70\uff0c\u7ed9\u4e91\u91cd\u51cf\u5c11\u4e86\u52b2\u654c\uff0c\u4e34\u573a\u4e4b\u65f6\uff0c\u53c8\u6697\u52a9\u4ed6\u6253\u8d25\u4e86\u9646\u5c55\u9e4f\uff0c\u6700\u540e\u81ea\u5df1\u63a5\u7740\u4e0a\u573a\uff0c\u53c8 \u6307\u70b9\u4e86\u4ed6\u4e00\u62db\uff0c\u6545\u610f\u8ba9\u4ed6\u53cd\u8d25\u4e3a\u80dc\uff0c\u8fd9\u624d\u6210\u5168\u4e86\u4e91\u91cd\u7684\u529f\u540d\u3002\u5f20\u4e39\u67ab\u7684\u82e6\u5fc3\uff0c\u8fde\u4e8e\u8c26\u548c\u5f20\u98ce \u5e9c\u90fd\u4e0d\u660e\u767d\u3002\u4e91\u91cd\u8fd9\u6837\u5f97\u80dc\uff0c\u5b9e\u662f\u68a6\u60f3\u4e0d\u5230\uff0c\u8fd9\u65f6\u6ee1\u573a\u7684\u559d\u5f69\u4e4b\u58f0\u5c1a\u672a\u505c\u606f\uff0c\u4e91\u91cd\u5446\u5446\u7ad9\u5728 \u53f0\u4e0a\uff0c\u7adf\u4f3c\u75f4\u4e86\uff0c\u8111\u4e2d\u601d\u6f6e\u8d77\u4f0f\uff0c\u7adf\u5fd8\u4e86\u8be5\u8d70\u4e0b\u53f0\u6765\uff0c\u8bf7\u6c42\u4f11\u606f\u3002\u5ffd\u542c\u5f97\u6b63\u53f0\u770b\u53f0\u4e0a\u4e00\u58f0\u5927 \u559d\uff1a\u201c\u5feb\u5feb\u6349\u8fd9\u53db\u8d3c\uff01\u201d \u3000\u3000\u4e91\u857e\u3001\u4e91\u91cd\u542c\u5f97\u8fd9\u4e00\u58f0\u66b4\u559d\uff0c\u90fd\u60ca\u5f97\u4ece\u6c89\u601d\u4e2d\u9192\u4e86\u8fc7\u6765\uff0c\u53ea\u89c1\u4f34\u7740\u7687\u5e1d\u5728\u6b63\u9762\u770b\u53f0\u4e0a\u7684 \u90a3\u4e2a\u5927\u5185\u603b\u7ba1\u5eb7\u8d85\u6d77\u633a\u7acb\u53f0\u524d\uff0c\u6307\u7740\u6821\u573a\u4e2d\u5f20\u4e39\u67ab\u7684\u80cc\u5f71\uff0c\u559d\u4ee4\u6b66\u58eb\u4eec\u5feb\u5feb\u6355\u6349\u3002\u539f\u6765\u5eb7\u8d85 \u6d77\u7684\u90a3\u4e24\u4e2a\u5e08\u53d4\uff0c\u201c\u94c1\u81c2\u91d1\u733f\u201d\u9f99\u9547\u65b9\u4e0e\u201c\u4e09\u82b1\u5251\u201d\u7384\u7075\u5b50\uff0c\u5728\u9752\u9f99\u5ce1\u88ab\u5f20\u4e39\u67ab\u4e0e\u4e91\u857e\u8054\u5251 \u6740\u8d25\u4e4b\u540e\uff0c\u9003\u56de\u4eac\u5e08\uff0c\u66fe\u5bf9\u5eb7\u8d85\u6d77\u8bf4\u8d77\u4e24\u4eba\u7684\u5f62\u8c8c\uff0c\u5c24\u5176\u5bf9\u5f20\u4e39\u67ab\u5370\u8c61\u6df1\u523b\u66f4\u662f\u8bf4\u5f97\u8be6\u7ec6\u3002 \u201c\u94c1\u81c2\u91d1\u733f\u201d\u4e0e\u201c\u4e09\u82b1\u5251\u201d\u4eca\u65e5\u867d\u4e0d\u5728\u573a\u4e2d\uff0c\u5eb7\u8d85\u6d77\u89c1\u4e86\u5f20\u4e39\u67ab\u7684\u5f62\u8c8c\u5df2\u662f\u5fc3\u91cc\u6000\u7591\uff0c\u6697\u4e2d \u7559\u610f\uff0c\u8fd9\u65f6\u6253\u5b9a\u4e86\u201c\u5b81\u53ef\u6349\u9519\uff0c\u4e0d\u53ef\u653e\u9519\u201d\u7684\u4e3b\u610f\uff0c\u6043\u7740\u5927\u5185\u603b\u7ba1\u7684\u8eab\u4efd\uff0c\u7adf\u7136\u5c31\u5f53\u7740\u7687\u5e1d \u9762\u524d\uff0c\u4e0b\u4e86\u902e\u6355\u5f20\u4e39\u67ab\u7684\u547d\u4ee4\u3002 \u3000\u3000\u6ee1\u573a\u7684\u559d\u5f69\u4e4b\u58f0\u7ed9\u5eb7\u8d85\u6d77\u8fd9\u4e00\u58f0\u66b4\u559d\u767b\u65f6\u9547\u538b\u4e0b\u53bb\uff0c\u62a4\u573a\u7684\u5378\u6797\u519b\u4e0e\u6b66\u58eb\u4eec\u5c1a\u672a\u5f04\u6e05\u8fd9 \u662f\u600e\u4e48\u4e00\u56de\u4e8b\uff0c\u4f46\u542c\u5f97\u51e0\u58f0\u72c2\u7b11\u4e00\u58f0\u5c16\u53eb\uff0c\u5f20\u4e39\u67ab\u500f\u5730\u51b2\u5230\u4e86\u573a\u8fb9\u8dd1\u9053\uff0c\u800c\u770b\u53f0\u4e0a\u7684\u5eb7\u8d85\u6d77 \u5374\u4e00\u4e2a\u5012\u683d\u8471\u8dcc\u843d\u53f0\u4e0b\u3002\u539f\u6765\u4ed6\u4e5f\u51b7\u4e0d\u53ca\u9632\uff0c\u7ed9\u5f20\u4e39\u67ab\u7684\u98de\u9488\u6697\u5668\u5c04\u4e2d\u4e86\u7a74\u9053\uff01 \u3000\u3000\u6b66\u58eb\u4eec\u5927\u9a87\u75be\u547c\uff0c\u7eb7\u7eb7\u8ffd\u4e0a\u8dd1\u9053\uff0c\u53ea\u542c\u5f97\u5f20\u4e39\u67ab\u53c8\u662f\u4e00\u58f0\u957f\u5578\uff0c\u90a3\u5339\u201c\u7167\u591c\u72ee\u5b50\u9a6c\u201d\u7535 \u4e00\u822c\u5954\u4e0a\u8dd1\u9053\uff0c\u5f20\u4e39\u67ab\u54c8\u54c8\u5927\u7b11\u8de8\u4e0a\u9a6c\u80cc\uff0c\u5b9d\u5251\u75be\u6325\uff0c\u5c06\u80cc\u540e\u5c04\u6765\u7684\u7bad\u5168\u90fd\u62d4\u843d\uff0c\u90a3\u5339\u5b9d\u9a6c \u72c2\u51b2\u6012\u5636\uff0c\u98ce\u9a70\u7535\u63a3\u822c\u5954\u51fa\u6821\u573a\uff0c\u8c01\u4e5f\u62e6\u963b\u4e0d\u4f4f\uff01 \u3000\u3000\u738b\u632f\u624b\u98a4\u811a\u9707\uff0c\u8fde\u58f0\u8bf4\u9053\uff1a\u201c\u8fd9\u3001\u8fd9\u8fd8\u5f97\u4e86\uff01\u5feb\u53eb\u4fdd\u4eba\u5f20\u98ce\u5e9c\u4e0a\u6765\uff01\u201d\u5ffd\u542c\u5f97\u7687\u5e1d\u8bf4 \u9053\uff1a\u201c\u4e14\u6162\uff0c\u5148\u95ee\u95ee\u5eb7\u8d85\u6d77\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d\u5eb7\u8d85\u6d77\u6b66\u529f\u4ea6\u7b97\u9ad8\u5f3a\uff0c\u8fd9\u65f6\u5df2\u8fd0\u6c14\u89e3\u4e86\u7a74\u9053\uff0c \u4f46\u5173\u8282\u7684\u8f6f\u7b4b\u88ab\u5229\u9488\u6240\u4f24\uff0c\u5c1a\u8981\u5f85\u7528\u78c1\u77f3\u5438\u51fa\u624d\u80fd\u75ca\u6108\uff0c\u4e00\u8ddb\u4e00\u62d0\u5730\u8d70\u4e0a\u53f0\u6765\u3002\u7687\u5e1d\u9053\uff1a \u201c\u4f60\u600e\u4e48\u5566\uff1f\u201d\u8981\u77e5\u5eb7\u8d85\u6d77\u4e43\u662f\u5927\u5185\u603b\u7ba1\uff0c\u5e73\u65e5\u603b\u60f3\u4e0e\u5f20\u98ce\u5e9c\u4e89\u593a\u4eac\u5e08\u7b2c\u4e00\u9ad8\u624b\u79f0\u53f7\uff0c\u7231\u9762 \u5b50\u5f97\u7d27\u3002\u800c\u4eca\u5f20\u4e39\u67ab\u88ab\u5f20\u98ce\u5e9c\u7684\u4e00\u4e2a\u624b\u4e0b\u6253\u4e0b\u64c2\u53f0\uff0c\u800c\u4ed6\u5374\u88ab\u5f20\u4e39\u67ab\u7684\u6697\u5668\u6240\u4f24\uff0c\u8fd9\u79cd\u5931\u9762 \u5b50\u4e4b\u4e8b\u5982\u4f55\u6562\u5bf9\u7687\u5e1d\u76f4\u8bf4\uff0c\u53ea\u5f97\u8bb7\u8bb7\u800c\u8a00\u9053\uff1a\u201c\u5974\u624d\u6025\u4e8e\u6349\u62ff\u53db\u8d3c\uff0c\u4e0d\u5c0f\u5fc3\u6454\u4e86\u4e00\u8de4\u3002\u201d\u7687 \u5e1d\u4e00\u7b11\u9053\uff1a\u201c\u90a3\u4e2a\u5f20\u4e39\u67ab\u662f\u53db\u8d3c\u5417\uff1f\u201d\u5eb7\u8d85\u6d77\u9053\uff1a\u201c\u662f\u5440\uff0c\u4ed6\u66fe\u7ecf\u4f24\u4e86\u5fa1\u6797\u519b\u7684\u5927\u7edf\u9886\u5f20\u98ce \u5e9c\uff0c\u52ab\u53bb\u4e86\u5f20\u98ce\u5e9c\u624b\u4e2d\u7684\u91cd\u8981\u56da\u72af\uff0c\u5c31\u662f\u90a3\u4e2a\u53db\u5c06\u5468\u5065\u7684\u513f\u5b50\uff0c\u5f20\u98ce\u5e9c\u4e0d\u662f\u7980\u594f\u8fc7\u7687\u4e0a\u5417\uff1f \u90a3\u52ab\u8d3c\u5c31\u662f\u8fd9\u4e2a\u5f20\u4e39\u67ab\u5440\uff01\u201d\u5eb7\u8d85\u6d77\u672a\u66fe\u597d\u597d\u601d\u91cf\uff0c\u53c8\u8981\u63a9\u9970\u81ea\u5df1\u5e08\u53d4\u88ab\u5f20\u4e39\u67ab\u6253\u8d25\u7684\u4e8b \u5b9e\uff0c\u5c06\u8fc7\u9519\u90fd\u63a8\u5230\u5f20\u98ce\u5e9c\u5934\u4e0a\u3002\u7687\u5e1d\u542c\u4e86\uff0c\u4e0d\u89c9\u54c8\u54c8\u5927\u7b11\uff0c\u9053\uff1a\u201c\u7231\u537f\uff0c\u4f60\u60f3\u5fc5\u770b\u9519\u4eba\u4e86\uff1f \u82e5\u7136\u5f20\u4e39\u67ab\u66fe\u4f24\u8fc7\u5f20\u98ce\u5e9c\uff0c\u5f20\u98ce\u5e9c\u5982\u4f55\u80af\u7ed9\u4ed6\u505a\u4fdd\u4eba\uff1f\u6211\u770b\u5f20\u4e39\u67ab\u6b64\u4eba\u867d\u7136\u7ed9\u4e91\u7edf\u9886\u6253\u8d25\uff0c \u6b66\u529f\u4ea6\u662f\u4e0d\u5f31\uff0c\u800c\u4e14\u76f8\u8c8c\u5802\u5802\uff0c\u53ef\u4ee5\u91cd\u7528\uff0c\u53ef\u60dc\u7ed9\u4f60\u5413\u8dd1\u4e86\u3002\u4f60\u597d\u597d\u5bfb\u4ed6\u56de\u6765\u5427\uff0c\u4e0d\u51c6\u5413\u552c \u4ed6\uff01\u201d\u8fd9\u4f4d\u7687\u5e1d\u5e73\u65e5\u867d\u662f\u53d7\u738b\u632f\u6240\u631f\u5236\u5374\u4e5f\u4e0d\u7b97\u660f\u5eb8\uff0c\u800c\u4e14\u8fd8\u6b22\u559c\u5356\u5f04\u70b9\u5c0f\u806a\u660e\uff0c\u8fd9\u65f6\u81ea\u89c9 \u770b\u6cd5\u6bd4\u5eb7\u8d85\u6d77\u9ad8\u660e\uff0c\u628a\u5eb7\u8d85\u6d77\u53d6\u7b11\u4e86\u4e00\u987f\uff0c\u5f97\u610f\u6d0b\u6d0b\u89c9\u5f97\u5eb7\u8d85\u6d77\u65e0\u4e8b\u81ea\u6270\uff0c\u5b9e\u662f\u611a\u8822\u3002\u5f20\u98ce \u5e9c\u634f\u4e86\u4e00\u628a\u51b7\u6c57\uff0c\u5e78\u559c\u7687\u5e1d\u5e76\u4e0d\u8ffd\u7a76\u3002 \u3000\u3000\u9a9a\u52a8\u8fc7\u540e\uff0c\u6bd4\u6b66\u7ee7\u7eed\u8fdb\u884c\uff0c\u4e91\u91cd\u8fde\u80dc\u4e24\u573a\uff0c\u53d6\u5f97\u4e86\u51b3\u8d5b\u7684\u8d44\u683c\u6682\u544a\u4f11\u606f\u3002\u6b64\u6b21\u53c2\u52a0\u6b66\u8bd5 \u7279\u79d1\u7684\u4e3e\u5b50\u867d\u591a\uff0c\u4f46\u7ecf\u8fc7\u521d\u8bd5\u3001\u590d\u8bd5\u4e0e\u6dd8\u6c70\u8d5b\u4e4b\u540e\uff0c\u53ea\u6709\u4e8c\u5341\u56db\u4eba\u6709\u8d44\u683c\u53c2\u52a0\u64c2\u53f0\u6bd4\u6b66\uff0c\u4e89 \u593a\u72b6\u5143\uff0c\u81f3\u5f20\u4e39\u67ab\u6b62\u662f\u7b2c\u5341\u4e94\u573a\uff0c\u5c1a\u5269\u4e0b\u4e5d\u573a\uff0c\u5f3a\u5b58\u5f31\u4ea1\uff0c\u4f18\u80dc\u52a3\u8d25\uff0c\u5f88\u5feb\u5c31\u6bd4\u51fa\u4e2a\u7ed3\u679c\u3002 \u4e5d\u573a\u6bd4\u8d5b\u5b8c\u4e86\uff0c\u53ea\u6709\u4e00\u4eba\u80fd\u8fde\u80dc\u4e24\u573a\uff0c\u4e0e\u4e91\u91cd\u51b3\u8d5b\uff0c\u8fd9\u4eba\u53eb\u505a\u6a0a\u4fca\uff0c\u4e43\u662f\u4eac\u5e08\u4e09\u5927\u9ad8\u624b\u4e4b \u4e00\uff0c\u5fa1\u524d\u4f8d\u536b\u6a0a\u5fe0\u7684\u80de\u5f1f\uff0c\u6b66\u529f\u51fa\u81ea\u4e43\u5144\u4f20\u6388\uff0c\u4e0e\u4e91\u91cd\u76f8\u5dee\u751a\u8fdc\uff0c\u51b3\u8d5b\u65f6\u4e0d\u5230\u5341\u62db\uff0c\u5c31\u88ab\u4e91 \u91cd\u7684\u91d1\u521a\u5927\u529b\u624b\u9707\u4e0b\u64c2\u53f0\u3002\u5728\u6ee1\u573a\u6b22\u547c\u58f0\u4e2d\uff0c\u7687\u5e1d\u4eb2\u81ea\u7ed9\u4e91\u91cd\u62ab\u7ea2\u6302\u5f69\uff0c\u5ba3\u5e03\u4eca\u79d1\u6b66\u8bd5\u529f\u5fb7 \u5706\u6ee1\uff0c\u4e91\u91cd\u593a\u5f97\u4e86\u6b66\u72b6\u5143\u3002 \u3000\u3000\u4e91\u857e\u81ea\u662f\u6ee1\u5fc3\u6b22\u559c\uff0c\u56de\u5230\u4e8e\u8c26\u5e9c\u4e0a\uff0c\u53ea\u7b49\u4e91\u91cd\u83b7\u5f97\u65b0\u7684\u5b98\u804c\uff0c\u642c\u51fa\u7687\u5bab\u4e4b\u540e\uff0c\u5c31\u51c6\u5907\u53eb \u5f20\u98ce\u5e9c\u966a\u5979\u53bb\u8ba4\u8ba4\u54e5\u54e5\u3002\u54ea\u77e5\u4e00\u8fde\u7b49\u4e86\u51e0\u5929\u5374\u6beb\u65e0\u6d88\u606f\u3002\u4e0d\u6b62\u4e91\u857e\u7126\u6025\uff0c\u5373\u4e8e\u8c26\u4e5f\u7eb3\u7f55\u5f02 \u5e38\u3002\u6309\u8bf4\u4e91\u91cd\u5df2\u4e2d\u4e86\u72b6\u5143\uff0c\u6700\u5c11\u4e5f\u4f1a\u88ab\u5c01\u4f5c\u4ec0\u4e48\u5c06\u519b\u4e4b\u804c\uff0c\u53e6\u8d50\u5b98\u90b8\uff0c\u4e0d\u5fc5\u518d\u5728\u5185\u5ef7\u5f53\u5b88\u591c \u7684\u536b\u58eb\u4e86\uff0c\u4f46\u5374\u8fdf\u8fdf\u4e0d\u89c1\u7687\u5e1d\u7684\u660e\u4ee4\u5ba3\u5e03\uff0c\u8fd9\u53ef\u662f\u5386\u671d\u5c11\u89c1\u4e4b\u4e8b\u3002\u4e8e\u8c26\u867d\u662f\u5927\u81e3\uff0c\u53ef\u662f\u5bf9\u4e8e \u5c01\u5b98\u8d60\u5178\u4e4b\u7c7b\u7684\u671d\u5ef7\u201c\u6069\u5178\u201d\uff0c\u5374\u4e5f\u4e0d\u4fbf\u53bb\u95ee\u7687\u5e1d\u3002 \u3000\u3000\u4e91\u91cd\u593a\u5f97\u4e86\u6b66\u72b6\u5143\u4e4b\u540e\uff0c\u5982\u9189\u5982\u68a6\uff0c\u542c\u7740\u4f17\u4eba\u9053\u8d3a\uff0c\u81ea\u5df1\u5374\u600e\u6837\u4e5f\u7b11\u4e0d\u51fa\u6765\u3002\u4ed6\u672a\u53d7\u65b0 \u804c\u4e4b\u524d\uff0c\u8fd8\u662f\u5bab\u4e2d\u7684\u8f6e\u503c\u536b\u58eb\uff0c\u5728\u5185\u5ef7\u4e0e\u5916\u5ef7\u5206\u754c\u4e4b\u5904\uff0c\u6709\u4e00\u6392\u623f\u95f4\uff0c\u662f\u5185\u5ef7\u536b\u58eb\u4eec\u7684\u4f4f \u6240\uff0c\u4ed6\u56de\u5230\u81ea\u5df1\u7684\u623f\u95f4\uff0c\u95ed\u4e0a\u623f\u95e8\uff0c\u540c\u4f34\u4eec\u7eb7\u6765\u9053\u8d3a\uff0c\u4ed6\u90fd\u4e0d\u4e88\u63a5\u89c1\u3002\u6709\u7684\u4ee5\u4e3a\u4ed6\u4e2d\u4e86\u6b66\u72b6 \u5143\u5c31\u6446\u67b6\u5b50\uff0c\u6709\u7684\u5219\u4ee5\u4e3a\u4ed6\u6bd4\u6b66\u4e4b\u540e\u8eab\u4f53\u75b2\u5026\uff0c\u9700\u8981\u4f11\u606f\uff0c\u5e94\u8be5\u539f\u8c05\u3002\u8c01\u4e5f\u6599\u60f3\u4e0d\u5230\uff0c\u4ed6\u4e2d \u4e86\u6b66\u72b6\u5143\u4e4b\u540e\uff0c\u5fc3\u60c5\u5374\u662f\u843d\u5bde\u4e4b\u6781\uff0c\u751a\u662f\u4e0d\u5b89\u3002\u8fd9\u65f6\u6b63\u4e00\u4e2a\u4eba\u95ed\u4e0a\u623f\u95e8\uff0c\u51a5\u601d\u9ed8\u60f3\u3002 \u3000\u3000\u522b\u4eba\u4e0d\u77e5\uff0c\u4e91\u91cd\u81ea\u5df1\u5fc3\u4e2d\u5374\u662f\u660e\u767d\uff0c\u8fd9\u6b66\u72b6\u5143\u53ef\u5e76\u4e0d\u662f\u51ed\u81ea\u5df1\u7684\u672c\u9886\u593a\u6765\uff0c\u800c\u662f\u5f20\u4e39\u67ab \u6709\u610f\u76f8\u8ba9\u7684\uff01\u8981\u81ea\u5df1\u7684\u201c\u4ec7\u4eba\u201d\u76f8\u8ba9\u8fd9\u5c82\u4e0d\u662f\u751f\u5e73\u7684\u5947\u803b\u5927\u8fb1\uff01\u4f46\u72b6\u5143\u5df2\u7ecf\u5230\u624b\uff0c\u96be\u9053\u8fd8\u53bb \u5bf9\u7687\u5e1d\u8bf4\u660e\u771f\u76f8\uff1f\u4e91\u91cd\u601d\u6f6e\u8d77\u4f0f\uff0c\u8d8a\u60f3\u8d8a\u95f7\uff0c\u5ffd\u542c\u5f97\u5927\u5c0f\u592a\u76d1\u6572\u95e8\u53eb\u9053\uff1a\u201c\u7687\u4e0a\u53ec\u89c1\u3002\u201d \u3000\u3000\u4e91\u91cd\u53c8\u60ca\u53c8\u559c\uff0c\u5306\u5306\u6574\u597d\u670d\u9970\uff0c\u968f\u592a\u76d1\u8d70\u8fc7\u5eca\u66f2\u69ad\uff0c\u5230\u6587\u534e\u6bbf\u7684\u5fa1\u4e66\u623f\uff0c\u53ea\u89c1\u4e66\u623f\u5185\u706f \u706b\u71a0\u8000\uff0c\u7687\u4e0a\u4e00\u4eba\u72ec\u5750\u770b\u4e66\uff0c\u89c1\u4e91\u91cd\u5230\u6765\uff0c\u6325\u624b\u4ee4\u592a\u76d1\u9000\u4e0b\uff0c\u5173\u4e0a\u623f\u95e8\u7b11\u9053\uff1a\u201c\u537f\u5bb6\u6b66\u827a\u9ad8 \u5f3a\u5927\u9b41\u5929\u4e0b\uff0c\u53ef\u559c\u53ef\u8d3a\uff01\u201d\u4e91\u91cd\u6ee1\u9762\u901a\u7ea2\uff0c\u8bb7\u8bb7\u8bf4\u9053\uff1a\u201c\u627f\u7687\u4e0a\u8c2c\u8d4f\uff0c\u5fae\u81e3\u7c89\u9aa8\u788e\u8eab\uff0c\u65e0\u4ee5 \u4e3a\u62a5\u3002\u201d\u7687\u5e1d\u770b\u4e86\u4e91\u91cd\u4e00\u773c\u95ee\u9053\uff1a\u201c\u537f\u5bb6\u662f\u54ea\u91cc\u4eba\u6c0f\uff1f\u201d\u4e91\u91cd\u7565\u4e00\u8fdf\u7591\uff0c\u7b54\u9053\uff1a\u201c\u81e3\u7956\u5c45\u6cb3 \u5357\u5f00\u5c01\u3002\u201d\u7687\u5e1d\u773c\u73e0\u4e00\u8f6c\uff0c\u53c8\u76ef\u4e86\u4e91\u91cd\u4e00\u773c\uff0c\u5ffd\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u4f60\u4e0e\u524d\u671d\u7684\u5927\u81e3\u4e91\u9756\u4e43\u662f \u540c\u4e61\u540c\u59d3\u4e86\u3002\u4f60\u548c\u4e91\u9756\u662f\u600e\u4e48\u4e2a\u79f0\u547c\u5462\uff1f\u201d\u4e91\u91cd\u5fc3\u4e2d\u4e00\u75db\uff0c\u8dea\u594f\u9053\uff1a\u201c\u524d\u671d\u4e91\u94a6\u4f7f\u662f\u6211\u7684\u7237 \u7237\u3002\u201d \u3000\u3000\u4e91\u91cd\u8eab\u662f\u7f6a\u81e3\u4e4b\u540e\uff0c\u8eab\u4efd\u9690\u7792\u591a\u65f6\uff0c\u4ece\u4e0d\u6562\u5bf9\u4eba\u63d0\u8d77\uff0c\u8fd9\u65f6\u7687\u4e0a\u95ee\u8d77\uff0c\u4e0d\u6562\u4e0d\u8bf4\u3002\u53ea\u89c1 \u7687\u5e1d\u9762\u8272\u4e00\u53d8\uff0c\u9053\uff1a\u201c\u4e91\u72b6\u5143\uff0c\u4f60\u5fc3\u4e2d\u5bf9\u6715\u53ef\u6709\u6000\u6068\u4e48\uff1f\u201d\u4e91\u91cd\u5fc3\u75db\u5982\u5272\uff0c\u9053\uff1a\u201c\u5fae\u81e3\u7956\u7236 \u5b64\u5fe0\u4e3a\u56fd\uff0c\u6c42\u7687\u4e0a\u6d17\u6da4\u7f6a\u540d\u3002\u201d\u773c\u6cea\u4e0d\u89c9\u593a\u7736\u800c\u51fa\uff0c\u7687\u5e1d\u672c\u65e0\u773c\u6cea\u5374\u4e5f\u5047\u4f5c\u4ee5\u8896\u62ed\u6cea\uff0c\u8bf4 \u9053\uff1a\u201c\u4f60\u7684\u7237\u7237\u4e00\u7247\u5fe0\u5fc3\u6715\u4ea6\u77e5\u9053\uff0c\u8d50\u4ed6\u81ea\u5c3d\uff0c\u672c\u4e0d\u662f\u6211\u7684\u4e3b\u610f\u3002\u201d\u4e91\u91cd\u4e00\u6014\u4e0d\u7981\u62ac\u5934\u770b\u770b \u7687\u5e1d\u3002\u7687\u5e1d\u7eed\u9053\uff1a\u201c\u4e0d\u8fc7\u8981\u66ff\u4f60\u7237\u7237\u6d17\u96ea\u7f6a\u540d\u5374\u8fd8\u8981\u5f85\u8bf8\u5f02\u65e5\u3002\u201d \u3000\u3000\u539f\u6765\u8fd9\u4f4d\u7687\u5e1d\u5e76\u975e\u611a\u8822\uff0c\u53ea\u662f\u4ed6\u81ea\u5c0f\u4fbf\u53d7\u738b\u632f\u631f\u6301\uff0c\u4e0d\u80fd\u81ea\u4e3b\uff0c\u4ed6\u4e5f\u5e38\u60f3\u6536\u56de\u6743\u67c4\uff0c\u514d \u5f97\u592a\u963f\u5012\u6301\uff0c\u53d8\u751f\u8098\u814b\uff0c\u53ea\u662f\u738b\u632f\u7fbd\u7ffc\u5df2\u6210\uff0c\u52a8\u4e4b\u4e0d\u5f97\uff0c\u56e0\u6b64\u6253\u7b97\u57f9\u690d\u5fc3\u8179\u52bf\u529b\uff0c\u6e10\u6e10\u524a\u5f31 \u738b\u632f\u7684\u6743\u67c4\u3002\u4e91\u91cd\u4e00\u7247\u5fe0\u5fc3\u53c8\u4e0e\u738b\u632f\u6709\u4ec7\uff0c\u6b63\u662f\u4ed6\u7406\u60f3\u7684\u4eba\u9009\u3002\u4e91\u91cd\u542c\u5f97\u7687\u5e1d\u8bf4\u660e\uff0c\u5bb3\u6b7b\u4ed6 \u7237\u7237\u4e43\u662f\u738b\u632f\u7684\u4e3b\u610f\u4e4b\u540e\uff0c\u679c\u7136\u75db\u54ed\u6d41\u6d95\uff0c\u77e2\u5fd7\u4e3a\u7687\u4e0a\u6548\u547d\uff0c\u6e05\u9664\u5978\u515a\u3002\u7687\u5e1d\u5f85\u4ed6\u62ed\u5e72\u773c \u6cea\uff0c\u8fd9\u624d\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u537f\u5bb6\u4e0d\u5fc5\u5fc3\u6025\uff0c\u73b0\u5728\u8fd8\u672a\u53ef\u6253\u8349\u60ca\u86c7\u3002\u201d \u3000\u3000\u4e91\u91cd\u594f\u9053\uff1a\u201c\u6c42\u7687\u4e0a\u8d50\u6211\u6548\u547d\u8fb9\u5173\uff0c\u7edf\u7387\u5e08\u65c5\uff0c\u5c06\u6765\u6218\u4e8b\u4e00\u8d77\uff0c\u52e4\u738b\u4e4b\u5e08\u56db\u96c6\uff0c\u6211\u6709\u4e86 \u5175\u6743\uff0c\u6253\u9000\u74e6\u523a\u540e\uff0c\u4fbf\u53ef\u56de\u5e08\u8083\u6e05\u541b\u4fa7\u4e86\u3002\u201d\u7687\u5e1d\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u4e5f\u6682\u7f13\uff01\u201d\u4e91\u91cd\u597d\u5931 \u671b\u53ea\u89c1\u7687\u5e1d\u53c8\u76ef\u4e86\u81ea\u5df1\u4e00\u773c\uff0c\u7b11\u9053\uff1a\u201c\u90a3\u4e2a\u4e0e\u4f60\u6bd4\u8bd5\u7684\u4e3e\u5b50\uff0c\u662f\u53eb\u505a\u5f20\u4e39\u67ab\u4e0d\u662f\uff1f\u4ed6\u7684\u6b66\u827a \u4e5f\u5f88\u4e0d\u9519\u5440\uff01\u201d\u4e91\u91cd\u9762\u70ed\u5fc3\u8df3\uff0c\u54ac\u4e00\u54ac\u7259\u594f\u9053\uff1a\u201c\u7687\u4e0a\u660e\u5bdf\uff0c\u90a3\u5f20\u4e39\u67ab\u7684\u827a\u5b9e\u5728\u5fae\u81e3\u4e4b\u4e0a\uff0c \u8fd9\u6b66\u72b6\u5143\u4e43\u662f\u4ed6\u6709\u610f\u8ba9\u4e0e\u6211\u7684\uff01\u201d\u5728\u6b64\u4e4b\u524d\uff0c\u4e91\u91cd\u5fc3\u4e2d\u60a3\u5f97\u60a3\u5931\uff0c\u751a\u662f\u4e0d\u5b89\uff0c\u5982\u4eca\u8bf4\u51fa\u5b9e \u8bdd\uff0c\u5fc3\u60c5\u53cd\u800c\u5e73\u5766\u3002\u7687\u5e1d\u9762\u6709\u8bb6\u8272\uff0c\u5ffd\u7136\u7b11\u9053\uff1a\u201c\u4f60\u5012\u8001\u5b9e\uff0c\u5176\u5b9e\u4f60\u4e0d\u8bf4\u6715\u4e5f\u770b\u5f97\u51fa\u6765\u3002\u201d \u4e91\u91cd\u4e0d\u89c9\u53c8\u662f\u4e00\u6014\uff0c\u5fc3\u9053\uff1a\u201c\u7687\u4e0a\u517b\u5c0a\u5904\u4f18\uff0c\u6599\u4ed6\u4e0d\u61c2\u6b66\u827a\uff0c\u5f20\u4e39\u67ab\u8ba9\u6211\u90a3\u62db\uff0c\u6ee1\u573a\u9ad8\u624b\uff0c \u65e0\u4e00\u77e5\u6653\uff0c\u4ed6\u600e\u4e48\u770b\u5f97\u51fa\u6765\uff1f\u201d\u5fc3\u4e2d\u7591\u60d1\u4e4b\u6781\uff0c\u767e\u601d\u4e0d\u5f97\u5176\u89e3\u3002 \u3000\u3000\u7687\u5e1d\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u9053\u5f20\u4e39\u67ab\u662f\u4ec0\u4e48\u4eba\u5417\uff1f\u201d\u4e91\u91cd\u9053\uff1a\u201c\u5fae\u81e3\u6b63\u6b32\u594f\u77e5\u7687\u4e0a\uff0c\u8fd9\u5f20\u4e39\u67ab\u4e43 \u662f\u74e6\u523a\u56fd\u53f3\u4e1e\u76f8\u5f20\u5b97\u5468\u7684\u513f\u5b50\uff0c\u8fd9\u756a\u5077\u5165\u8fb9\u5173\uff0c\u53ea\u6015\u4e0d\u6000\u597d\u610f\u3002\u201d\u7687\u5e1d\u5fae\u5fae\u4e00\u6115\uff0c\u9053\uff1a\u201c\u539f \u6765\u4ed6\u8fd8\u662f\u5f20\u5b97\u5468\u7684\u513f\u5b50\uff01\u201d\u4e91\u91cd\u5fd9\u9053\uff1a\u201c\u5f20\u98ce\u5e9c\u53ea\u6015\u8fd8\u4e0d\u77e5\u9053\u4ed6\u7684\u6765\u5386\uff0c\u89c1\u4ed6\u6b66\u827a\u9ad8\u5f3a\uff0c\u6240 \u4ee5\u4fdd\u8350\u3002\u5f20\u7edf\u9886\u5fe0\u5fc3\u4e00\u7247\uff0c\u6c42\u7687\u4e0a\u4e0d\u8981\u89c1\u7591\u3002\u201d\u4e91\u91cd\u4ee5\u4e8b\u5904\u4e24\u96be\uff0c\u4e0d\u5f97\u4e0d\u8bf4\uff0c\u8bf4\u4e86\u4e4b\u540e\uff0c\u8d76 \u5fd9\u66ff\u5f20\u98ce\u5e9c\u63a9\u9970\u3002\u7687\u5e1d\u9053\uff1a\u201c\u4e0d\u77e5\u4e0d\u7f6a\uff0c\u8bf4\u5230\u7591\u5fc3\u5417\uff0c\u5514\uff0c\u6715\u5012\u4e0d\u7591\u5f20\u98ce\u5e9c\u2026\u2026\u201d\u4e91\u91cd\u9762\u8272 \u5927\u53d8\uff0c\u594f\u9053\uff1a\u201c\u5f20\u4e39\u67ab\u5c06\u6b66\u72b6\u5143\u62f1\u624b\u8ba9\u6211\uff0c\u96be\u602a\u7687\u4e0a\u7591\u5fc3\uff0c\u5176\u5b9e\u4ed6\u5374\u662f\u6211\u5bb6\u7684\u4e16\u4ec7\uff01\u201d\u8bf4\u660e \u539f\u59d4\uff0c\u53c8\u5c06\u7237\u7237\u7684\u8840\u4e66\u7ed9\u7687\u5e1d\u770b\uff0c\u7687\u5e1d\u8fd9\u624d\u7b11\u9053\uff1a\u201c\u6211\u4e5f\u5e76\u4e0d\u7591\u5fc3\u4e8e\u4f60\u3002\u5f20\u4e39\u67ab\u6b64\u4e3e\uff0c\u4e0d\u8fc7 \u662f\u6709\u610f\u793a\u6069\uff0c\u4ee4\u4f60\u5fd8\u6389\u56fd\u6068\u5bb6\u4ec7\u7f62\u4e86\u3002\u4f60\u5f53\u7136\u4e0d\u4f1a\u4e2d\u4ed6\u5708\u5957\u3002\u201d\u7687\u5e1d\u8f7b\u63cf\u6de1\u5199\u7684\u98ce\u53e5\u8bf4\u8bdd\uff0c \u628a\u4e91\u91cd\u54c4\u5f97\u670d\u670d\u8d34\u8d34\uff0c\u672c\u6765\u5bf9\u5f20\u4e39\u67ab\u7684\u51e0\u5206\u611f\u6fc0\uff0c\u8fd9\u65f6\u4e5f\u5316\u4f5c\u4e91\u70df\u3002\u53ea\u542c\u5f97\u7687\u5e1d\u53c8\u9053\uff1a\u201c\u4f60 \u6765\uff0c\u6211\u7ed9\u4f60\u770b\u4e00\u5f20\u753b\u50cf\uff01\u201d \u3000\u3000\u7687\u5e1d\u62c9\u5f00\u4e66\u6a71\uff0c\u53d6\u51fa\u4e00\u5f20\u753b\u50cf\uff0c\u753b\u4e2d\u4eba\u5934\u6234\u7687\u51a0\uff0c\u8eab\u7a7f\u9f99\u888d\uff0c\u76f8\u8c8c\u5a01\u6b66\u3002\u53ea\u542c\u5f97\u7687\u5e1d\u58f0 \u97f3\u5fae\u5fae\u98a4\u6296\uff0c\u9053\uff1a\u201c\u4f60\u770b\u5f20\u4e39\u67ab\u53ef\u6709\u70b9\u50cf\u6b64\u4eba\u4e48\uff1f\u201d\u4e91\u91cd\u5927\u4e3a\u60ca\u6115\uff0c\u4ed4\u7ec6\u770b\u65f6\uff0c\u53ea\u89c1\u8f6e\u5ed3\u679c \u7136\u6709\u4e9b\u76f8\u4f3c\uff0c\u53ea\u662f\u753b\u4e2d\u4eba\u6bd4\u8f83\u7c97\u8c6a\uff0c\u800c\u5f20\u4e39\u67ab\u5219\u6781\u4e3a\u6f47\u6d12\uff0c\u795e\u60c5\u6c14\u5ea6\u5927\u662f\u4e0d\u540c\u3002\u4e91\u91cd\u5fc3\u9053\uff1a \u201c\u96be\u9053\u5f20\u4e39\u67ab\u7adf\u662f\u7687\u5ba4\u4e4b\u4eba\u5417\uff1f\u201d\u7687\u5e1d\u53c8\u95ee\uff1a\u201c\u662f\u4e0d\u662f\u6709\u70b9\u76f8\u50cf\uff1f\u201d\u4e91\u91cd\u55eb\u5685\u8bf4\u9053\uff1a\u201c\u662f\u3001 \u662f\u6709\u70b9\u76f8\u50cf\u3002\u201d\u53ea\u89c1\u7687\u5e1d\u9762\u8272\u5927\u53d8\uff0c\u6307\u7740\u90a3\u753b\u50cf\u9053\uff1a\u201c\u4f60\u6b7b\u4e0d\u7791\u76ee\u8fd8\u8981\u53eb\u5b50\u5b59\u6765\u62a2\u593a\u6715\u7684\u6c5f \u5c71\u4e48\uff1f\u201d\u4e91\u91cd\u60ca\u9a87\u83ab\u540d\uff0c\u9053\uff1a\u201c\u4ed6\u3001\u4ed6\u662f\u4f55\u4eba\uff1f\u201d\u7687\u5e1d\u51b7\u7b11\u9053\uff1a\u201c\u753b\u4e2d\u8d3c\u738b\u662f\u4f2a\u5927\u5468\u7687\u5e1d\u5f20 \u58eb\u8bda\uff0c\u5f20\u5b97\u5468\u3001\u5f20\u4e39\u67ab\u90fd\u662f\u4ed6\u7684\u5b50\u5b59\u3002\u54fc\uff0c\u53d6\u540d\u5b97\u5468\uff0c\u5c82\u4e0d\u662f\u60f3\u501f\u5916\u5bc7\u4e4b\u529b\uff0c\u590d\u4ed6\u5927\u5468\u7684\u6b63 \u7edf\uff0c\u706d\u6211\u5927\u660e\u6c5f\u5c71\uff1f\u201d\u5f20\u4e39\u67ab\u662f\u5f20\u58eb\u8bda\u7684\u540e\u4ee3\u5b50\u5b59\uff0c\u4e91\u91cd\u8fd8\u662f\u7b2c\u4e00\u6b21\u77e5\u9053\uff0c\u6b64\u4e8b\u592a\u51fa\u610f\u5916\uff0c \u4e00\u65f6\u95f4\u8bf4\u4e0d\u51fa\u8bdd\u6765\uff0c\u5fc3\u4e2d\u53ea\u662f\u60f3\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u4ed6\u4eec\u7236\u5b50\u5982\u6b64\u618e\u6068\u5927\u660e\u671d\u5ef7\uff0c\u53ef\u662f\u7687\u5e1d\u53c8\u600e\u4e48 \u80fd\u591f\u77e5\u9053\uff1f\u4ed6\u65e2\u7136\u77e5\u9053\uff0c\u4e3a\u4ec0\u4e48\u53c8\u4e0d\u5728\u6821\u573a\u6bd4\u6b66\u4e4b\u65f6\u5c06\u5f20\u4e39\u67ab\u62ff\u4e0b\uff1f\u201d \u3000\u3000\u53ea\u542c\u5f97\u7687\u5e1d\u53c8\u9053\uff1a\u201c\u5f53\u5e74\u5f20\u58eb\u8bda\u4e0e\u6211\u5927\u660e\u592a\u7956\u4e89\u593a\u6c5f\u5c71\uff0c\u5728\u957f\u6c5f\u51b3\u6218\uff0c\u5175\u8d25\u8eab\u4ea1\u3002\u636e\u95fb \u4ed6\u5728\u4e34\u6b7b\u4e4b\u524d\uff0c\u5c06\u91d1\u94f6\u73e0\u5b9d\u90fd\u57cb\u5728\u82cf\u5dde\u4e00\u4e2a\u5730\u65b9\uff0c\u91d1\u94f6\u73e0\u5b9d\u4e5f\u72b9\u7f62\u4e5f\uff0c\u8fd8\u6709\u4e00\u5f20\u519b\u7528\u5730\u56fe\uff0c \u8be6\u6ce8\u5929\u4e0b\u5c71\u5ddd\u9669\u8981\u7684\u5f62\u52bf\uff0c\u7559\u5728\u4eba\u95f4\uff0c\u9057\u60a3\u65e0\u7a77\u3002\u662f\u4ee5\u592a\u7956\u7559\u4e0b\u9057\u547d\uff0c\u52a1\u5fc5\u8981\u5c06\u5f20\u5bb6\u540e\u4ee3\u65a9 \u8349\u9664\u6839\uff0c\u5e76\u8981\u5bfb\u83b7\u5f20\u58eb\u8bda\u7684\u5b9d\u85cf\u5730\u56fe\uff0c\u5927\u660e\u6c5f\u5c71\u624d\u80fd\u5b89\u7a33\u3002\u5f20\u4e39\u67ab\u73b0\u5728\u5df2\u95ef\u51fa\u6821\u573a\uff0c\u79bb\u5f00\u4eac \u57ce\uff0c\u6715\u6599\u4ed6\u5fc5\u662f\u524d\u5f80\u82cf\u5dde\u90a3\u89c5\u5730\u56fe\u5b9d\u85cf\u53bb\u4e86\u3002\u6715\u8d50\u4e00\u5339\u5fa1\u9a6c\u7ed9\u4f60\uff0c\u4f60\u7acb\u5373\u8ffd\u5f80\u82cf\u5dde\uff0c\u8ddf\u8e2a\u5f20 \u4e39\u67ab\uff0c\u5728\u4ed6\u672a\u5f97\u5b9d\u85cf\u4e0e\u5730\u56fe\u4e4b\u524d\uff0c\u4e0d\u53ef\u4e0b\u624b\uff0c\u5f85\u4ed6\u53d6\u5f97\u4e4b\u540e\u5c31\u7acb\u523b\u5c06\u4ed6\u6740\u6389\uff0c\u5c06\u9996\u7ea7\u62ff\u56de\u89c1 \u6211\u3002\u201d \u3000\u3000\u4e91\u91cd\u6253\u4e86\u4e00\u4e2a\u5bd2\u5664\uff0c\u4e0d\u6562\u56de\u8bdd\u3002\u53ea\u542c\u5f97\u7687\u5e1d\u53c8\u662f\u5fae\u5fae\u4e00\u7b11\u8bf4\u9053\uff1a\u201c\u6715\u53e6\u5916\u8fd8\u6d3e\u4e03\u540d\u5927\u5185 \u9ad8\u624b\u52a9\u4f60\uff0c\u81f3\u82cf\u5dde\u4f1a\u5408\uff0c\u4f60\u653e\u5fc3\u5427\u3002\u201d\u4e91\u91cd\u4e00\u60f3\uff0c\u5f20\u4e39\u67ab\u6b66\u827a\u867d\u7136\u80dc\u8fc7\u81ea\u5df1\u4e00\u7b79\uff0c\u4f46\u6709\u4e03\u540d \u9ad8\u624b\u76f8\u52a9\uff0c\u6599\u80fd\u5c06\u4ed6\u5236\u670d\uff0c\u4e8e\u662f\u6b23\u7136\u9886\u547d\u3002 \u3000\u3000\u4f60\u9053\u7687\u5e1d\u4f55\u4ee5\u77e5\u9053\u5f20\u4e39\u67ab\u7684\u8eab\u4efd\u6765\u5386\uff1f\u539f\u6765\u5f20\u4e39\u67ab\u5728\u53c2\u52a0\u6821\u573a\u6bd4\u6b66\u4e4b\u524d\uff0c\u65e9\u5df2\u6df1\u601d\u719f \u8651\uff0c\u51c6\u5907\u4e07\u4e00\u7ed9\u4eba\u53d1\u73b0\u4e4b\u540e\u5982\u4f55\u5e94\u4ed8\u3002\u679c\u7136\u5f53\u4ed6\u4e0e\u4e91\u91cd\u6bd4\u8bd5\u4e4b\u540e\uff0c\u4fbf\u7ed9\u5eb7\u8d85\u6d77\u559d\u4ee4\u6355\u62ff\uff0c\u4ed6 \u4e00\u9762\u7528\u98de\u9488\u6697\u5668\u4f24\u4e86\u5eb7\u8d85\u6d77\uff0c\u4e00\u9762\u5c06\u65e9\u5df2\u5199\u597d\u7684\u4e00\u5c01\u4fe1\uff0c\u5377\u6210\u4e00\u4e2a\u7eb8\u56e2\uff0c\u629b\u5165\u7687\u5e1d\u7684\u9f99\u888d\u4e4b \u4e2d\uff0c\u4ed6\u53d1\u6697\u5668\u7684\u624b\u6cd5\u8d85\u5999\u7edd\u4f26\uff0c\u975e\u552f\u65c1\u4eba\u4e0d\u77e5\uff0c\u8fde\u7687\u5e1d\u81ea\u5df1\u4e5f\u4e0d\u77e5\u9053\u3002\u76f4\u81f3\u56de\u5bab\u4f11\u606f\uff0c\u8131\u4e0b \u9f99\u888d\uff0c\u624d\u53d1\u73b0\u8fd9\u4e00\u5c01\u4fe1\uff0c\u4fe1\u4e2d\u9996\u5148\u8bf4\u74e6\u523a\u5165\u4fb5\u5728\u5373\uff0c\u53eb\u7687\u5e1d\u5584\u8fa8\u5fe0\u5978\u62b5\u5fa1\u5916\u7978\uff0c\u5e76\u5217\u4e3e\u738b\u632f \u4e0e\u74e6\u523a\u79c1\u901a\u7684\u8bc1\u636e\uff0c\u53eb\u7687\u5e1d\u53ca\u65e9\u9632\u5907\u3002\u5176\u6b21\u76f4\u8bf4\u81ea\u5df1\u672c\u4e0e\u7687\u5bb6\u6709\u4e16\u5bbf\u51a4\u4ec7\uff0c\u4f46\u82e5\u7687\u5e1d\u80af\u5168\u5fc3 \u6297\u654c\uff0c\u5219\u8fd9\u51a4\u4ec7\u4e5f\u53ef\u5316\u89e3\u3002\u518d\u529d\u7687\u5e1d\u4e0d\u53ef\u6b8b\u5bb3\u5fe0\u826f\uff0c\u5426\u5219\u81ea\u5df1\u53d6\u4ed6\u9996\u7ea7\u6613\u5982\u53cd\u638c\u3002 \u3000\u3000\u8fd9\u5c01\u4fe1\u5199\u5f97\u60c5\u6587\u5e76\u8302\uff0c\u8f6f\u786c\u517c\u65bd\uff0c\u672c\u6765\u662f\u5f20\u4e39\u67ab\u4e00\u7247\u4e3a\u56fd\u5bb6\u6253\u7b97\u7684\u5fe0\u5fc3\uff0c\u5c82\u6599\u7687\u5e1d\u770b \u4e86\uff0c\u5148\u662f\u4e00\u60ca\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u4e16\u4e0a\u7adf\u6709\u8fd9\u6837\u7684\u5f02\u4eba\uff0c\u82e5\u4e0d\u9664\u6389\u6715\u7684\u6027\u547d\u5c82\u4e0d\u662f\u5728\u4ed6\u638c\u63e1\u4e4b \u4e2d\uff1f\u201d\u7ee7\u800c\u8054\u60f3\u8d77\u592a\u7956\u7684\u9057\u8bcf\uff0c\u731c\u5ea6\u6b64\u4eba\u5341\u4e5d\u662f\u5f20\u58eb\u8bda\u7684\u540e\u4ee3\uff0c\u6240\u4ee5\u624d\u4f1a\u6709\u201c\u4e16\u5bbf\u51a4\u4ec7\u201d\u4e4b \u8bed\uff0c\u6697\u81ea\u62ff\u51fa\u5bab\u4e2d\u6240\u85cf\u5f20\u58eb\u8bda\u753b\u50cf\u6bd4\u5bf9\u679c\u7136\u6709\u4e9b\u76f8\u50cf\uff0c\u8d8a\u53d1\u9a87\u6015\uff0c\u5bf9\u5f20\u4e39\u67ab\u7684\u597d\u610f\uff0c\u5168\u4e0d\u7406 \u4f1a\u3002\u56e0\u6b64\u624d\u6709\u9057\u4ee4\u4e91\u91cd\u4e0e\u5176\u4ed6\u4e03\u540d\u9ad8\u624b\u524d\u5f80\u82cf\u5dde\u4e4b\u4e3e\u3002\u5f20\u4e39\u67ab\u5199\u8fd9\u5c01\u4fe1\u867d\u7136\u6709\u5982\u5bf9\u725b\u5f39\u7434\uff0c \u4f46\u5374\u4e5f\u6709\u4e00\u70b9\u6210\u529f\u4e4b\u5904\uff0c\u90a3\u5c31\u662f\u5728\u7687\u5e1d\u672a\u80fd\u6355\u6740\u5f20\u4e39\u67ab\u4e4b\u524d\uff0c\u4e3a\u4e86\u6015\u4ed6\u6697\u6740\u624b\u6bb5\u7684\u5389\u5bb3\uff0c\u8fd9 \u5c31\u7edd\u5bf9\u4e0d\u6562\u964d\u7f6a\u4fdd\u8350\u8fc7\u5f20\u4e39\u67ab\u7684\u5f20\u98ce\u5e9c\u3002 \u3000\u3000\u7687\u5e1d\u7684\u9a87\u6015\u7591\u8651\uff0c\u4e91\u857e\u7684\u7126\u6025\u4e0d\u5b89\uff0c\u90fd\u6682\u4e14\u6309\u4e0b\u4e0d\u8868\u3002\u4e14\u8bf4\u4e91\u91cd\u9886\u4e86\u7687\u547d\uff0c\u7b2c\u4e8c\u65e5\u4e00\u65e9 \u4fbf\u79d8\u5bc6\u51fa\u4eac\uff0c\u7687\u5e1d\u6240\u8d50\u7684\u5fa1\u9a6c\u867d\u4e0d\u53ca\u5f20\u4e39\u67ab\u90a3\u5339\u201c\u7167\u591c\u72ee\u5b50\u201d\u7684\u795e\u9a8f\uff0c\u4f46\u4e5f\u76f8\u5dee\u4e0d\u8fdc\uff0c\u516d\u4e03 \u65e5\u95f4\uff0c\u4fbf\u8de8\u8fc7\u4e86\u6cb3\u5317\u3001\u5c71\u4e1c\u4e24\u7701\uff0c\u8fdb\u5165\u6c5f\u82cf\u3002\u8fd9\u4e00\u65e5\u5230\u4e86\u5434\u53bf\uff0c\u5434\u53bf\u4e0e\u82cf\u5dde\u76f8\u90bb\uff0c\u4e0d\u8fc7\u534a\u65e5 \u8def\u7a0b\u3002\u4e91\u91cd\u7f13\u4e86\u53e3\u6c14\uff0c\u7b56\u9a6c\u6162\u884c\u3002\u6c5f\u5357\u5c71\u6c34\u79c0\u4e3d\uff0c\u5929\u4e0b\u95fb\u540d\uff0c\u4e91\u91cd\u8fd9\u65f6\u4e0d\u5fc5\u6025\u4e8e\u8d76\u8def\uff0c\u5fc3\u5883 \u7a0d\u7a0d\u5bbd\u8212\uff0c\u653e\u76ee\u6d4f\u89c8\uff0c\u4f46\u89c1\u7530\u4ea9\u7eb5\u6a2a\uff0c\u6e2f\u6c4a\u4ea4\u9519\uff0c\u6ce2\u5149\u4e91\u5f71\uff0c\u6d45\u5c71\u5982\u9edb\uff0c\u5904\u5904\u663e\u51fa\u6c5f\u5357\u6c34\u4e61 \u7684\u60c5\u8c03\u3002\u4e91\u91cd\u4e45\u5904\u6f20\u5317\uff0c\u51e0\u66fe\u89c1\u8fc7\u5982\u6b64\u5e7d\u7f8e\u7684\u98ce\u666f\uff0c\u5fc3\u65f7\u795e\u6021\uff0c\u5ffd\u89c9\u5728\u5c18\u4e16\u4e0a\u9010\u5229\u4e89\u540d\u5b9e\u662f \u65e0\u8c13\u3002\u8d70\u4e86\u4e00\u6bb5\u8def\u7a0b\uff0c\u773c\u524d\u4e00\u4eae\uff0c\u524d\u9762\u4e00\u4e2a\u5c0f\u6e56\u5728\u8def\u8fb9\u5e73\u9759\u7684\u8eba\u7740\uff0c\u851a\u84dd\u7684\u5929\u8272\uff0c\u6620\u4ee5\u6de1\u78a7 \u7684\u6e56\u5149\uff0c\u771f\u662f\u4e00\u5e45\u7edd\u5999\u7684\u56fe\u753b\u3002\u6e56\u8fb9\u6709\u4e00\u5ea7\u53e4\u5893\uff0c\u4e91\u91cd\u6295\u773c\u4e00\u77a5\uff0c\u5ffd\u89c1\u7891\u77f3\u4e0a\u5199\u7684\u662f\u51e0\u4e2a\u7bc6 \u5b57\uff0c\u4e43\u662f\u201c\u6fb9\u53f0\u706d\u660e\u4e4b\u5893\u201d\uff0c\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u6fb9\u53f0\u706d\u660e\u4e43\u662f\u74e6\u523a\u7684\u5927\u5c06\uff0c\u4e0a\u4e2a\u6708\u8fd8\u5728\u5317 \u4eac\uff0c\u600e\u4e48\u8fd9\u91cc\u6709\u4ed6\u7684\u5893\uff1f\u800c\u4e14\u8fd9\u5893\u5f62\u5f0f\u5947\u53e4\uff0c\u663e\u7136\u4e0d\u662f\u65b0\u8fd1\u6240\u9020\u3002\u201d\u6b63\u7591\u60d1\u95f4\uff0c\u5ffd\u89c1\u4e00\u4e2a\u7267 \u7ae5\uff0c\u725b\u89d2\u6302\u4e66\uff0c\u81ea\u6e56\u8fb9\u7f13\u7f13\u884c\u6765\u3002\u4e91\u91cd\u95ee\u9053\uff1a\u201c\u5c0f\u54e5\uff0c\u8fd9\u91cc\u662f\u4ec0\u4e48\u5730\u65b9\uff1f\u8fd9\u662f\u4f55\u4eba\u575f\u5893\uff1f\u201d \u90a3\u7267\u7ae5\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u4f4d\u5ba2\u4eba\u60f3\u662f\u8fdc\u5730\u6765\u7684\uff0c\u8fd9\u4e2a\u6751\u53eb\u505a\u6fb9\u53f0\u6751\uff0c\u8fd9\u4e2a\u6e56\u5c31\u53eb\u505a\u6fb9\u53f0\u6e56\uff0c\u8fd9\u4e2a \u5893\u5c31\u662f\u6211\u4eec\u59cb\u7956\u7684\u575f\u5893\u3002\u201d\u4e91\u91cd\u5947\u9053\uff1a\u201c\u4ec0\u4e48\uff0c\u662f\u4f60\u4eec\u59cb\u7956\u7684\u575f\u5893\uff1f\u201d\u90a3\u7267\u7ae5\u7b11\u9053\uff1a\u201c\u770b\u4f60 \u4e0d\u50cf\u662f\u6ca1\u8bfb\u8fc7\u4e66\u7684\u4eba\uff0c\u96be\u9053\u8fde\u6fb9\u53f0\u706d\u660e\u662f\u4ec0\u4e48\u4eba\u4e5f\u4e0d\u77e5\u5417\uff1f\u201d\u4e91\u91cd\u4e00\u6014\uff0c\u53ea\u542c\u5f97\u90a3\u7267\u7ae5\u95ee \u9053\uff1a\u201c\u4ee5\u8c8c\u53d6\u4eba\uff0c\u5931\u4e4b\u5b50\u7fbd\uff0c\u8fd9\u53e5\u6210\u8bed\u4f60\u61c2\u5f97\u4e48\uff1f\u201d\u4e91\u91cd\u5fc3\u4e2d\u5fae\u6120\uff0c\u9053\uff1a\u201c\u5c0f\u54e5\u4f60\u5012\u8003\u8d77\u6211 \u6765\u4e86\u3002\u8fd9\u53e5\u8bdd\u662f\u5b54\u5b50\u8bf4\u7684\uff0c\u5b50\u7fbd\u662f\u5b54\u5b50\u7684\u5b66\u751f\uff0c\u54c1\u5b66\u517c\u4f18\uff0c\u4f46\u76f8\u8c8c\u4e11\u964b\uff0c\u6240\u4ee5\u5b54\u5b50\u8bf4\uff1a\u4ee5\u8c8c \u53d6\u4eba\uff0c\u5931\u4e4b\u5b50\u7fbd\u3002\u5c31\u662f\u53eb\u4eba\u4e0d\u8981\u53ea\u770b\u5916\u8868\u7684\u610f\u601d\u3002\u201d\u90a3\u7267\u7ae5\u7b11\u9053\uff1a\u201c\u53ef\u4e0d\u662f\u6765\u3002\u6211\u4eec\u7684\u59cb\u7956 \u6fb9\u53f0\u706d\u660e\uff0c\u5c31\u662f\u5b54\u95e8\u4e03\u5341\u4e8c\u7b2c\u5b50\u4e4b\u4e00\uff0c\u4ed6\u522b\u53f7\u5b50\u7fbd\uff0c\u53ea\u8981\u8bfb\u8fc7\u56db\u4e66\u7684\u4eba\u90fd\u4f1a\u77e5\u9053\u3002\u8fd9\u4e2a\u6e56\u672c \u6765\u662f\u4ed6\u7684\u4f4f\u5b85\uff0c\u636e\u8bf4\u540e\u6765\u6ca7\u6851\u53d8\u5316\uff0c\u4e0b\u9677\u4e3a\u6e56\uff0c\u6240\u4ee5\u53eb\u505a\u6fb9\u53f0\u6e56\u3002\u6211\u4eec\u7684\u53bf\u5fd7\u91cc\u90fd\u8f7d\u6709 \u7684\u3002\u201d\u90a3\u7267\u7ae5\u4f83\u4f83\u800c\u8c08\uff0c\u65c1\u8bc1\u535a\u5f15\uff0c\u987f\u65f6\u4ee4\u4e91\u91cd\u5446\u4e86\u3002 \u3000\u3000\u4e91\u91cd\u7684\u5e08\u7236\u8463\u5cb3\u6587\u6b66\u5168\u624d\uff0c\u4e91\u91cd\u5c0f\u65f6\u4e5f\u66fe\u8ddf\u4ed6\u5e08\u7236\u8bfb\u8fc7\u7ecf\u53f2\uff0c\u6b64\u65f6\u60f3\u8d77\u5b54\u95e8\u4e03\u5341\u4e8c\u5f1f\u5b50 \u4e4b\u4e2d\uff0c\u679c\u7136\u6709\u4e00\u4e2a\u53eb\u505a\u201c\u6fb9\u53f0\u706d\u660e\u201d\u3002\u8fd8\u8bb0\u5f97\u81ea\u5df1\u5728\u7b2c\u4e00\u6b21\u542c\u5f97\u74e6\u523a\u6709\u4e2a\u5927\u5c06\u53eb\u505a\u6fb9\u53f0\u706d\u660e \u65f6\u5fc3\u4e2d\u8fd8\u6697\u6697\u597d\u7b11\uff1a\u8fd9\u6837\u4e00\u4e2a\u6b66\u592b\u5374\u53d6\u4e86\u4e00\u4e2a\u53e4\u4ee3\u540d\u5112\u7684\u540d\u5b57\u3002\u81ea\u5df1\u8fd8\u4e00\u76f4\u4ee5\u4e3a\u201c\u6fb9\u53f0\u201d\u4e43 \u662f\u80e1\u59d3\uff0c\u8c01\u77e5\u5374\u662f\u6c5f\u5357\u6587\u7269\u4e4b\u90a6\u7684\u59d3\u6c0f\uff0c\u800c\u4e14\u8fd8\u6709\u5893\u7559\u6709\u6c5f\u82cf\u5434\u53bf\uff0c\u4f9b\u4eba\u77bb\u4ef0\u3002\u4e0d\u8fc7\u8fd9\u4e2a\u5893 \u5927\u7ea6\u662f\u4ed6\u540e\u4ee3\u5b50\u5b59\u6240\u91cd\u5efa\uff0c\u770b\u5893\u7891\u7684\u7bc6\u5b57\u548c\u8425\u9020\u7684\u5f62\u5f0f\uff0c\u6700\u5c11\u4e5f\u662f\u79e6\u3001\u6c49\u4ee5\u540e\u7684\u5efa\u7b51\uff0c\u7edd\u4e0d \u662f\u6625\u79cb\u65f6\u4ee3\u7684\u9057\u51a2\u4e86\u3002 \u3000\u3000\u90a3\u7267\u7ae5\u4e00\u7b11\u8bf4\u9053\uff1a\u201c\u4ee5\u8c8c\u53d6\u4eba\uff0c\u5931\u4e4b\u5b50\u7fbd\u3002\u5723\u4eba\u7684\u8bdd\uff0c\u679c\u7136\u8bf4\u5f97\u4e0d\u9519\uff01\u201d\u77ed\u7b1b\u6a2a\u5439\uff0c\u9a91 \u725b\u7f13\u7f13\u800c\u53bb\u3002\u4e91\u91cd\u5fc3\u4e2d\u4e00\u6014\uff0c\u5480\u56bc\u201c\u4ee5\u8c8c\u53d6\u4eba\uff0c\u5931\u4e4b\u5b50\u7fbd\u201d\u8fd9\u4e24\u53e5\u540d\u8a00\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u539f\u6765 \u90a3\u6fb9\u53f0\u706d\u660e\u679c\u662f\u6c49\u4eba\uff0c\u96be\u9053\u8fd9\u540d\u5b57\u662f\u4ed6\u6709\u610f\u53d6\u5f97\u4e0e\u5148\u8d24\u4e00\u6837\u5417\uff1f\u6fb9\u53f0\u706d\u660e\u76f8\u8c8c\u5947\u4e11\uff0c\u8fd9\u70b9\u5012 \u53ef\u4ee5\u4e0e\u53e4\u4ee3\u7684\u90a3\u4e2a\u6fb9\u53f0\u706d\u660e\u76f8\u63d0\u5e76\u8bba\uff0c\u4f46\u4ed6\u6295\u9760\u756a\u90a6\uff0c\u53c8\u5c82\u80fd\u4e0e\u5148\u8d24\u76f8\u6bd4\uff1f\u5514\uff0c\u4ee5\u8c8c\u53d6\u4eba\uff0c \u5931\u4e4b\u5b50\u7fbd\u3002\u83ab\u975e\u4ed6\u53d6\u8fd9\u4e2a\u540d\u5b57\uff0c\u5176\u4e2d\u4e5f\u542b\u6709\u6df1\u610f\uff1f\u53eb\u6211\u4eec\u4e0d\u8981\u53ea\u4ece\u5916\u8868\u7684\u76f8\u8c8c\u884c\u5f84\u53bb\u770b\u4ed6\uff1f \u96be\u9053\u8fd9\u2018\u706d\u660e\u2019\u4e8c\u5b57\u542b\u610f\u4e0d\u662f\u8981\u2018\u706d\u6389\u660e\u671d\u2019\uff1f\u54fc\uff0c\u96be\u9053\u90a3\u4e2a\u4e00\u4ecb\u6b66\u592b\u7684\u6fb9\u53f0\u706d\u660e\u4e5f\u6709\u4ec0\u4e48 \u5d07\u9ad8\u7684\u80f8\u895f\u62a5\u8d1f\uff1f\u201d \u3000\u3000\u4e91\u91cd\u7ed5\u8fc7\u6fb9\u53f0\u6e56\uff0c\u8fdb\u5165\u6fb9\u53f0\u6751\uff0c\u5fc3\u4e2d\u4e0d\u4f4f\u5730\u60f3\u6fb9\u53f0\u706d\u660e\u7684\u4e8b\uff0c\u60f3\u8d77\u81ea\u5df1\u524d\u6b21\u5728\u6b63\u5b9a\u591c\u88ad \u756a\u738b\uff0c\u6fb9\u53f0\u706d\u660e\u6b66\u529f\u8fdc\u80dc\u4e8e\u5df1\uff0c\u663e\u7136\u672a\u4e0b\u6740\u624b\u3002\u53c8\u60f3\u8d77\u4ed6\u5728\u5f20\u98ce\u5e9c\u5bb6\u4e2d\u6bd4\u6b66\uff0c\u66fe\u7ecf\u66ff\u5f20\u98ce\u5e9c \u6253\u9000\u6697\u7b97\u4ed6\u7684\u5bf9\u5934\u4e4b\u4e8b\uff0c\u5fc3\u4e2d\u66f4\u89c9\u7591\u60d1\uff0c\u5ffd\u800c\u7b11\u9053\uff1a\u201c\u6b64\u4e00\u6fb9\u53f0\u5f7c\u4e00\u6fb9\u53f0\uff0c\u6b64\u6fb9\u53f0\u4e0d\u662f\u5f7c\u6fb9 \u53f0\uff0c\u4f55\u5fc5\u60f3\u5b83\u3002\u201d\u8fd9\u65f6\u5df2\u662f\u4e2d\u5348\u65f6\u5206\uff0c\u70c8\u65e5\u5f53\u5934\uff0c\u53e3\u4e2d\u7126\u6e34\u3002\u6c5f\u5357\u82cf\u676d\u4e00\u5e26\uff0c\u8336\u4ead\u9152\u8086\uff0c\u5904 \u5904\u7686\u662f\uff0c\u8fd9\u6761\u8def\u4ece\u6751\u4e2d\u7a7f\u8fc7\uff0c\u4e24\u65c1\u7530\u4ea9\uff0c\u7adf\u65e0\u4e00\u4eba\u8015\u4f5c\uff0c\u8def\u8fb9\u7684\u8336\u4ead\u9152\u8086\u4e5f\u6ca1\u4e00\u95f4\u5f00\u95e8\u3002\u4e91 \u91cd\u89c1\u6b64\u666f\u8c61\uff0c\u5341\u5206\u5947\u602a\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u6fb9\u53f0\u6751\u96be\u9053\u6ca1\u6709\u4eba\u7684\u5417\uff1f\u201d \u3000\u3000\u4e91\u91cd\u518d\u7b56\u9a6c\u884c\u4e86\u4e00\u7a0b\uff0c\u53e3\u4e2d\u7126\u6e34\u66f4\u751a\uff0c\u5ffd\u89c1\u8def\u8fb9\u6709\u4e00\u8336\u4ead\u6709\u4e00\u4e2a\u8001\u59aa\u5728\u90a3\u91cc\u5356\u8336\u3002\u4e91\u91cd \u7b11\u9053\uff1a\u201c\u884c\u4e86\u8fd9\u8bb8\u591a\u8def\uff0c\u624d\u89c5\u5f97\u559d\u8336\u4e4b\u5904\u3002\u597d\u5728\u4e0d\u662f\u5904\u5904\u5982\u6b64\uff0c\u8981\u4e0d\u7136\u6211\u5012\u4ee5\u4e3a\u662f\u5728\u5927\u6f20\u65c5 \u884c\u4e86\u3002\u201d\u8fdb\u5165\u8336\u4ead\uff0c\u7cfb\u597d\u9a6c\u5339\u3002\u90a3\u8001\u59aa\u9053\uff1a\u201c\u5ba2\u4eba\u6765\u4e86\uff0c\u660e\u513f\u5012\u8336\u3002\u201d\u53ea\u89c1\u4e00\u4e2a\u5e74\u7ea6\u5341\u56db\u4e94 \u5c81\u7684\u5c11\u5973\uff0c\u63d0\u7740\u8336\u5177\u51fa\u6765\uff0c\u7ed9\u4ed6\u5012\u4e86\u4e00\u676f\u78a7\u7eff\u7684\u9999\u8336\u3002\u90a3\u5c11\u5973\u867d\u662f\u8346\u9497\u88d9\u5e03\uff0c\u9762\u76ee\u5374\u81ea\u6709\u4e00 \u80a1\u6e05\u79c0\u4e4b\u6c14\uff0c\u90a3\u8001\u59aa\u9053\uff1a\u201c\u6211\u4eec\u8fd9\u4e00\u6751\u90fd\u662f\u590d\u59d3\u6fb9\u53f0\uff0c\u4f60\u5c31\u53eb\u6211\u6fb9\u53f0\u5927\u5a18\u597d\u4e86\u3002\u201d\u6b63\u4e0e\u90a3\u8001 \u59aa\u642d\u8baa\u804a\u5929\uff0c\u5ffd\u89c1\u4e00\u9a91\u5feb\u9a6c\u7ecf\u8fc7\u8336\u4ead\u9a6c\u4e0a\u9a91\u58eb\u76f8\u8c8c\u7c97\u8c6a\uff0c\u5e76\u4e0d\u4e0b\u9a6c\uff0c\u5c31\u653e\u5f00\u5589\u5499\u95ee\u9053\uff1a \u201c\u5582\uff0c\u6211\u95ee\u4f60\u8fd9\u8001\u5a46\u5a46\uff0c\u6628\u65e5\u662f\u4e0d\u662f\u6709\u4e2a\u767d\u9a6c\u4e66\u751f\uff0c\u7ecf\u8fc7\u8fd9\u91cc\uff1f\u201d\u201c\u767d\u9a6c\u4e66\u751f\uff1f\u201d\u4e91\u91cd\u4e0d\u7531 \u5f97\u84e6\u7136\u4e00\u60ca\uff0c\u8fd9\u4eba\u6240\u63a2\u95ee\u7684\u201c\u767d\u9a6c\u4e66\u751f\u201d\uff0c\u5c82\u4e0d\u662f\u5f20\u4e39\u67ab\u5417\uff1f \u3000\u3000\u90a3\u8001\u5a46\u5a46\u77aa\u4e86\u4e00\u773c\uff0c\u9053\uff1a\u201c\u6ca1\u542c\u89c1\uff01\u201d\u90a3\u9a91\u58eb\u8df3\u4e0b\u9a6c\u6765\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u6211\u95ee\u4f60\u6709\u6ca1\u6709\u89c1 \u8fc7\u4e00\u4e2a\u767d\u9a6c\u4e66\u751f\uff1f\u201d\u58f0\u9707\u5c4b\u74e6\uff0c\u90a3\u8001\u5a46\u5a46\u5f20\u76ee\u7ed3\u820c\uff0c\u4ecd\u4e0d\u4f5c\u58f0\u3002\u9a91\u58eb\u5927\u6012\u9053\uff1a\u201c\u5c31\u662f\u804b\u5b50\u4e5f \u8be5\u542c\u89c1\u3002\u201d\u8d70\u5165\u8336\u4ead\uff0c\u5c31\u8981\u63ea\u90a3\u5a46\u5a46\u3002\u4e91\u91cd\u5fc3\u77e5\u6709\u5f02\uff0c\u8f7b\u8f7b\u4f38\u81c2\u4e00\u683c\uff0c\u4ed6\u7ec3\u7684\u662f\u91d1\u521a\u5927\u529b\u624b \u529f\u592b\uff0c\u8fd9\u4e00\u683c\u6697\u85cf\u52b2\u529b\uff0c\u90a3\u9a91\u58eb\u51e0\u4e4e\u7ed9\u4ed6\u6454\u5012\uff0c\u5927\u5403\u4e00\u60ca\uff0c\u60c5\u77e5\u9047\u5230\u9ad8\u4eba\u4e0d\u6562\u53d1\u4f5c\u3002\u4e91\u91cd\u7b11 \u9053\uff1a\u201c\u6709\u8bdd\u597d\u8bf4\uff0c\u4f55\u5fc5\u751f\u6c14\uff1f\u8fd9\u4f4d\u8001\u5a46\u5a46\u8033\u6735\u662f\u6709\u70b9\u4e0d\u5927\u65b9\u4fbf\u3002\u201d\u5176\u5b9e\u8fd9\u8001\u5a46\u5a46\u9002\u624d\u8fd8\u4e0e\u4e91 \u91cd\u8c08\u8bdd\uff0c\u4e91\u91cd\u6b64\u8a00\u4e43\u6545\u610f\u66ff\u5979\u63a9\u9970\u3002\u90a3\u8001\u5a46\u5a46\u5374\u4e00\u7b11\u9053\uff1a\u201c\u6211\u8fd9\u8033\u6735\u5f88\u602a\uff0c\u592a\u5927\u58f0\u542c\u4e0d\u89c1\uff0c \u592a\u5c0f\u58f0\u4e5f\u542c\u4e0d\u89c1\u3002\u8981\u4e0d\u5927\u4e0d\u5c0f\uff0c\u6070\u5230\u597d\u5904\u624d\u542c\u5f97\u89c1\u3002\u4f60\u521a\u624d\u95ee\u4ec0\u4e48\uff1f\u518d\u8bf4\u4e00\u904d\u3002\u201d\u90a3\u9a91\u58eb\u6309 \u4e0b\u6012\u706b\uff0c\u67d4\u58f0\u8bf4\u9053\uff1a\u201c\u8bf7\u95ee\u6709\u4e00\u4f4d\u767d\u9a6c\u4e66\u751f\u53ef\u66fe\u4ece\u8fd9\u91cc\u7ecf\u8fc7\uff1f\u201d\u90a3\u8001\u5a46\u5a46\u9053\uff1a\u201c\u554a\uff0c\u767d\u9a6c\u4e66 \u751f\uff1f\u5440\uff0c\u662f\uff0c\u662f\u6709\u4e00\u4f4d\u767d\u9a6c\u4e66\u751f\uff0c\u4ed6\u6628\u5929\u8fd9\u4e2a\u65f6\u5206\u4ece\u8fd9\u91cc\u7ecf\u8fc7\uff0c\u5429\u5490\u4e0b\u6765\uff0c\u8bf4\u51e1\u6709\u4eba\u95ee\u53ca\u4ed6 \u7684\uff0c\u90fd\u8bf7\u5728\u660e\u65e5\u4e2d\u5348\u5230\u82cf\u5dde\u5feb\u6d3b\u6797\u76f8\u4f1a\uff0c\u4ed6\u8bf7\u559d\u9152\u3002\u201d\u90a3\u9a91\u58eb\u542c\u4e86\u6b64\u8a00\uff0c\u7acb\u523b\u4e0a\u9a6c\u4fbf\u8d70\u3002\u90a3 \u8001\u5a46\u5a46\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u660e\u513f\uff0c\u8bb0\u4e0b\u6765\u4e86\uff01\u201d\u90a3\u5c11\u5973\u5750\u5728\u4e00\u89d2\u7ee3\u82b1\u7b11\u9053\uff1a\u201c\u662f\u8bb0\u4e0b\u6765\u4e86\u3002\u201d \u628a\u9526\u7f0e\u4e00\u626c\uff0c\u4e0a\u9762\u7ee3\u6709\u4e03\u6735\u7ea2\u82b1\uff0c\u6709\u5927\u6709\u5c0f\u9053\uff1a\u201c\u8fd9\u662f\u7b2c\u4e03\u4e2a\uff01\u201d \u3000\u3000\u4e91\u91cd\u597d\u751f\u7eb3\u95f7\uff0c\u4ed6\u60c5\u77e5\u8fd9\u4e24\u6bcd\u5973\u4e0d\u662f\u5e38\u4eba\uff0c\u4f46\u81ea\u6043\u6b66\u529f\uff0c\u4e5f\u4e0d\u907f\u6c5f\u6e56\u5fcc\u8bb3\uff0c\u7981\u4e0d\u4f4f\u95ee \u9053\uff1a\u201c\u4ec0\u4e48\u767d\u9a6c\u4e66\u751f\uff1f\u90a3\u5feb\u6d3b\u6797\u53c8\u662f\u4ec0\u4e48\u5730\u65b9\uff1f\u201d\u90a3\u8001\u5a46\u5a46\u76ef\u4e86\u4e91\u91cd\u4e00\u773c\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u4f4d \u5ba2\u5b98\u4e3a\u4eba\u5f88\u597d\uff0c\u6211\u8bf4\u4e0e\u4f60\u542c\u3002\u5feb\u6d3b\u6797\u662f\u82cf\u5dde\u4e00\u4e2a\u9500\u91d1\u573a\u6240\uff0c\u542c\u8bf4\u4ee5\u524d\u5f20\u58eb\u8bda\u5728\u82cf\u5dde\u79f0\u5e1d\u65f6\uff0c \u66fe\u628a\u90a3\u5730\u65b9\u5efa\u4f5c\u884c\u5bab\u3002\u540e\u6765\u5f20\u58eb\u8bda\u6218\u6b7b\uff0c\u5feb\u6d3b\u6797\u88ab\u5b98\u5bb6\u5f53\u4f5c\u9006\u4ea7\u5904\u7f6e\uff0c\u4ea7\u7ed9\u5546\u4e70\u3002\u73b0\u5728\u5feb\u6d3b \u6797\u7684\u4e3b\u4eba\u53eb\u505a\u4e5d\u5934\u72ee\u5b50\u6bb7\u5929\u9274\uff0c\u4ed6\u628a\u90a3\u5927\u597d\u56ed\u6797\uff0c\u53d8\u6210\u79e6\u697c\u8d4c\u9986\uff0c\u5f04\u4e86\u4e0d\u5c11\u9020\u5b7d\u94b1\uff0c\u5e7f\u4e70\u7530 \u5730\uff0c\u4e70\u5230\u6211\u4eec\u5434\u53bf\u6765\u3002\u6fb9\u53f0\u6751\u7684\u7530\u5730\uff0c\u5341\u4e4b\u4e03\u516b\u90fd\u662f\u4ed6\u7684\u3002\u201d\u4e91\u91cd\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u8fd9\u4e5d\u5934 \u72ee\u5b50\u4e5f\u7b97\u5f97\u662f\u4e2a\u5927\u6076\u9738\u4e86\uff0c\u4f46\u8fd9\u4e0e\u767d\u9a6c\u4e66\u751f\u53c8\u6709\u4f55\u5e72\uff1f\u201d\u90a3\u8001\u5a46\u5a46\u9053\uff1a\u201c\u6211\u4eec\u8fd9\u4e2a\u8336\u4ead\u7684\u5730 \u76ae\u4e5f\u662f\u4ed6\u7684\uff0c\u4ed6\u6bcf\u4e2a\u6708\u8981\u6765\u6536\u4e09\u4e24\u516d\u94b1\u94f6\u5b50\uff0c\u6211\u4eec\u6b20\u4e86\u4e09\u6708\u79df\u94b1\uff0c\u4ed6\u6628\u65e5\u5c31\u6d3e\u4e86\u4e24\u4e2a\u6b66\u5e08 \u6765\uff0c\u8bf4\u8981\u62c9\u660e\u513f\u4f5c\u4ed6\u7684\u4e2b\u5934\uff0c\u62b5\u507f\u79df\u94b1\uff0c\u6070\u6070\u90a3\u4e2a\u767d\u9a6c\u4e66\u751f\u7ecf\u8fc7\uff0c\u66ff\u6211\u4eec\u8fd8\u4e86\u94f6\u5b50\uff0c\u53c8\u5c06\u90a3 \u4e24\u4e2a\u6b66\u5e08\u6253\u5f97\u4e2a\u72d7\u5403\u5c4e\u3002\u201d\u90a3\u5c11\u5973\u63d2\u53e3\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4e66\u751f\u53ef\u6ca1\u6709\u6253\u4eba\uff0c\u662f\u90a3\u4e24\u4e2a\u6b66\u5e08\u6253 \u4ed6\u3002\u54c8\u771f\u5999\u6781\u4e86\uff0c\u90a3\u4e24\u4e2a\u6b66\u5e08\u62f3\u5934\u521a\u78b0\u7740\u4ed6\u7684\u8eab\u4f53\uff0c\u5c31\u54ce\u54df\u54df\u76f4\u53eb\u8d77\u6765\uff0c\u4e5f\u4e0d\u89c1\u90a3\u4e66\u751f\u8fd8 \u624b\uff0c\u90a3\u4e24\u4e2a\u6b66\u5e08\u5c31\u8dcc\u5012\u5730\u4e0b\u4e71\u6eda\uff0c\u722c\u8d77\u6765\u65f6\uff0c\u6211\u77a7\u89c1\u4ed6\u4eec\u7684\u62f3\u5934\u90fd\u80bf\u5f97\u50cf\u6d77\u8155\u822c\u5927\u3002\u5ba2\u5b98\uff0c \u4f60\u89c1\u591a\u8bc6\u5e7f\uff0c\u8fd9\u53ef\u662f\u4ec0\u4e48\u90aa\u6cd5\uff1f\u201d\u4e91\u91cd\u5fc3\u77e5\u8fd9\u662f\u79cd\u7c7b\u4f3c\u201c\u6cbe\u8863\u5341\u516b\u8dcc\u201d\u7684\u4e0a\u4e58\u5185\u529f\uff0c\u5634\u4e2d\u5374 \u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u77e5\u9053\u3002\u201d\u90a3\u8001\u5a46\u5a46\u9053\uff1a\u201c\u90a3\u4e24\u4e2a\u6b66\u5e08\u672c\u9886\u4e3a\u6d4e\uff0c\u53e3\u5374\u5f88\u786c\uff0c\u5bf9\u90a3\u767d\u9a6c\u4e66\u751f\u9053\uff1a \u2018\u6709\u79cd\u7684\u4f60\u5230\u5feb\u6d3b\u6797\u89c1\u6211\u4eec\u7684\u4e5d\u5934\u72ee\u5b50\u3002\u2019\u90a3\u767d\u9a6c\u4e66\u751f\u4ef0\u5929\u5927\u7b11\u9053\uff1a\u2018\u8fc7\u4e24\u5929\u6211\u5c31\u53bb\u770b\u4ed6\u3002 \u770b\u770b\u4e5d\u5934\u72ee\u5b50\u662f\u600e\u4e48\u51f6\u6cd5\uff1f\u2019\u201d \u3000\u3000\u4e91\u91cd\u5fc3\u4e2d\u751a\u662f\u5947\u602a\uff0c\u60f3\u9053\uff1a\u201c\u5f20\u4e39\u67ab\u5230\u82cf\u5dde\u6765\u660e\u660e\u662f\u8981\u627e\u4ed6\u7956\u5148\u7684\u85cf\u5b9d\u4e0e\u5730\u56fe\uff0c\u5374\u600e\u7684 \u6ca1\u6765\u7531\u591a\u7ba1\u95f2\u4e8b\uff0c\u4e0e\u4e00\u4e2a\u6076\u9738\u4f5c\u5bf9\uff0c\u4e0d\u6015\u9732\u51fa\u8eab\u4efd\u4e48\uff1f\u82e5\u8bf4\u884c\u4fa0\u4ed7\u4e49\uff0c\u90a3\u4e48\u5c06\u90a3\u4e24\u4e2a\u6b66\u5e08\u6298 \u8fb1\u4e86\u4e00\u987f\uff0c\u66ff\u8fd9\u4e24\u6bcd\u5973\u8fd8\u4e86\u94f6\u5b50\u5c31\u7b97\u4e86\uff0c\u5929\u4e0b\u6076\u9738\u6253\u4e4b\u4e0d\u5c3d\uff0c\u4f55\u51b5\u4ed6\u53c8\u6709\u5927\u4e8b\u5728\u8eab\uff0c\u5c82\u53ef\u610f \u6c14\u7528\u4e8b\uff0c\u8f7b\u91cd\u5012\u7f6e\uff1f\u201d\u4f46\u4e00\u60f3\u5230\u6240\u89c1\u6240\u95fb\uff0c\u5f20\u4e39\u67ab\u7684\u6bcf\u4ef6\u884c\u4e8b\uff0c\u90fd\u662f\u8ba1\u5212\u7f1c\u5bc6\uff0c\u542b\u6709\u6df1\u610f\uff0c \u5fc3\u4e2d\u53c8\u662f\u6349\u6478\u4e0d\u5b9a\u3002 \u3000\u3000\u90a3\u8001\u5a46\u5a46\u7eed\u9053\uff1a\u201c\u90a3\u4f4d\u767d\u9a6c\u76f8\u516c\u628a\u4e24\u4e2a\u6b66\u5e08\u8d76\u8dd1\u4e4b\u540e\uff0c\u53c8\u5bf9\u6211\u9053\uff1a\u4f60\u53eb\u6751\u4e2d\u7684\u7537\u5b50\u540e\u65e5 \u90fd\u5230\u5feb\u6d3b\u6797\u77a7\u70ed\u95f9\u53bb\uff0c\u6211\u6709\u94f6\u5b50\u5206\u7ed9\u4ed6\u4eec\u3002\u5ba2\u5b98\uff0c\u4f60\u5f53\u7136\u4e0d\u7a00\u7f55\u4ed6\u5206\u94f6\u5b50\uff0c\u53ef\u4e5f\u60f3\u77a7\u77a7\u70ed\u95f9 \u53bb\u4e48\uff1f\u201d\u4e91\u91cd\u9053\uff1a\u201c\u6211\u4e45\u6155\u82cf\u5dde\u56ed\u6797\u4e4b\u540d\uff0c\u4f55\u51b5\u53c8\u6709\u70ed\u95f9\u53ef\u770b\uff0c\u90a3\u662f\u5b9a\u7136\u8981\u53bb\u7684\u4e86\u3002\u201d\u4ed8\u4e86 \u8336\u94b1\uff0c\u7acb\u523b\u544a\u8f9e\uff0c\u5077\u773c\u4e00\u77a5\uff0c\u53ea\u89c1\u90a3\u5c11\u5973\u7684\u9526\u7f0e\u4e0a\u5df2\u7ee3\u4e86\u7b2c\u516b\u6735\u7ea2\u82b1\u3002 \u3000\u3000\u4e91\u91cd\u9a6c\u884c\u5feb\u901f\uff0c\u65e5\u5934\u672a\u843d\uff0c\u5df2\u5230\u82cf\u5dde\u3002\u53ea\u89c1\u8857\u9053\u5168\u662f\u4e94\u8272\u6591\u6593\u7684\u5927\u5c0f\u77f3\u5375\u94fa\u6210\u7684\u77f3\u5b50 \u8def\uff0c\u522b\u5177\u4e00\u79cd\u6e05\u65b0\u7684\u98ce\u683c\uff0c\u623f\u5c4b\u5efa\u7b51\u7cbe\u96c5\u4e4b\u5904\uff0c\u66f4\u975e\u522b\u7684\u57ce\u5e02\u53ef\u6bd4\u3002\u4f46\u89c1\u5904\u5904\u7eff\u836b\u63a9\u6620\uff0c\u68a7 \u6850\u6768\u67f3\u9ad8\u51fa\u56f4\u5899\uff0c\u53ea\u89c9\u8fd9\u4e2a\u57ce\u5e02\u4e4b\u4e2d\u5230\u5904\u90fd\u662f\u56ed\u6797\uff0c\u4e0e\u4e91\u91cd\u6240\u719f\u6089\u7684\u5927\u6f20\u98ce\u5149\uff0c\u6070\u6070\u662f\u4e2a\u6781 \u5f3a\u70c8\u7684\u5bf9\u6bd4\uff0c\u5fc3\u4e2d\u4e0d\u7981\u53f9\u9053\uff1a\u201c\u4e0a\u6709\u5929\u5802\uff0c\u4e0b\u6709\u82cf\u676d\uff0c\u6b64\u8bdd\u5f53\u771f\u662f\u8bf4\u5f97\u4e0d\u5dee\uff01\u201d \u3000\u3000\u4e91\u91cd\u62ff\u4e86\u7687\u5e1d\u7684\u5bc6\u65e8\u5230\u629a\u8859\u63a2\u95ee\uff0c\u90a3\u4e03\u540d\u9ad8\u624b\uff0c\u8fd8\u6ca1\u4e00\u4e2a\u5230\u6765\u3002\u4e91\u91cd\u4ee5\u7687\u547d\u5728\u8eab\uff0c\u867d\u7136 \u540c\u4f34\u672a\u6765\uff0c\u4f46\u5373\u77e5\u5f20\u4e39\u67ab\u8e2a\u8ff9\uff0c\u5f53\u7136\u8981\u53bb\u8ffd\u67e5\uff0c\u5bbf\u4e86\u4e00\u5bb5\uff0c\u7b2c\u4e8c\u65e5\u4fbf\u626e\u6210\u4e00\u4e2a\u666e\u901a\u7684\u8336\u5ba2\uff0c \u5230\u5feb\u6d3b\u6797\u53bb\u3002 \u3000\u3000\u90a3\u5feb\u6d3b\u6797\u5728\u82cf\u5dde\u5317\u90ca\uff0c\u4e43\u662f\u4e00\u4e2a\u9762\u79ef\u5f88\u5927\u7684\u56ed\u6797\uff0c\u8fdb\u5f97\u56ed\u95e8\uff0c\u4fbf\u662f\u4e00\u6761\u7ef5\u5ef6\u66f2\u6298\u7684\u957f \u5eca\uff0c\u4e24\u9762\u58c1\u4e0a\uff0c\u6709\u5386\u4ee3\u7684\u4e66\u5bb6\u6cd5\u8d34\u65e0\u6570\uff0c\u4e00\u5757\u5757\u7684\u5d4c\u5728\u58c1\u4e0a\uff0c\u53ea\u662f\u56ed\u6797\u4e3b\u4eba\u4e0d\u77e5\u4fdd\u62a4\uff0c\u5df2\u73b0 \u51fa\u5265\u843d\u6a21\u7cca\u7684\u75d5\u8ff9\u3002\u4e91\u91cd\u867d\u7136\u5bf9\u4e66\u753b\u4e43\u662f\u5916\u884c\uff0c\u4e5f\u4e0d\u7981\u5fc3\u4e2d\u6168\u53f9\u3002\u51fa\u4e86\u957f\u5eca\uff0c\u4e24\u8fb9\u6797\u6728\u63a9 \u6620\uff0c\u82b1\u6728\u7af9\u77f3\u6784\u6210\u5047\u5c71\u3001\u8377\u6c60\u3001\u5e7d\u8c37\u3001\u655e\u8f69\uff0c\u7f8e\u5999\u7cbe\u96c5\uff0c\u6709\u5982\u753b\u56fe\u3002\u53ea\u662f\u6797\u4e2d\u904d\u8bbe\u8d4c\u644a\uff0c\u517c \u4e4b\u8336\u5ba2\u4f17\u591a\uff0c\u547c\u5362\u559d\u96c9\uff0c\u5608\u5608\u6742\u6742\uff0c\u4e0e\u5f53\u524d\u98ce\u7269\u5927\u4e0d\u8c03\u548c\uff0c\u771f\u6709\u5982\u4f5b\u5934\u7740\u70d8\uff0c\u7cdf\u8e4b\u5c3d\u56ed\u6797\u5999 \u666f\uff01 \u3000\u3000\u4e91\u91cd\u6697\u91cc\u7559\u5fc3\uff0c\u5bdf\u89c9\u56ed\u4e2d\u904d\u5e03\u6253\u624b\uff0c\u60f3\u662f\u90a3\u4e5d\u5934\u72ee\u5b50\uff0c\u4e3a\u4e86\u8fce\u6218\u767d\u9a6c\u4e66\u751f\uff0c\u6697\u4e2d\u5df2\u4f5c\u4e86 \u5e03\u7f6e\u3002\u4e91\u91cd\u5750\u4e86\u4e00\u4f1a\uff0c\u7ea2\u65e5\u5df2\u8fc7\u4e2d\u5929\uff0c\u4ecd\u672a\u89c1\u5f20\u4e39\u67ab\u51fa\u73b0\uff0c\u5fc3\u9053\uff1a\u201c\u96be\u9053\u4ed6\u4e34\u65f6\u53d8\u8902\uff0c\u4e0d\u6765 \u4e86\u4e48\uff1f\u201d\u6b63\u81ea\u731c\u6d4b\uff0c\u5ffd\u542c\u5f97\u4eba\u58f0\u55a7\u54d7\uff0c\u4e00\u4f19\u4eba\u62e5\u8fdb\u56ed\u6765\u3002\u4e3a\u9996\u7684\u662f\u4e2a\u5e74\u7ea6\u4e94\u5341\u7684\u866c\u9aef\u6c49\u5b50\uff0c \u5927\u58f0\u53eb\u9053\uff1a\u201c\u4e5d\u5934\u72ee\u5b50\uff0c\u4eca\u65e5\u6211\u6765\u4e0e\u4f60\u8d4c\u51e0\u624b\u6d88\u9063\uff01\u201d \u3000\u3000\u56ed\u4e2d\u767b\u65f6\u9759\u4e86\u4e0b\u6765\uff0c\u5404\u5904\u8d4c\u644a\u4e5f\u90fd\u505c\u4e86\u3002\u4e91\u91cd\u542c\u5f97\u6709\u4eba\u6084\u6084\u8bf4\u9053\uff1a\u201c\u6d77\u9f99\u5e2e\u7684\u9f99\u5e2e\u4e3b\u6765 \u8d4c\u8fd9\u5206\u660e\u662f\u610f\u62c6\u4e5d\u5934\u72ee\u5b50\u7684\u53f0\uff0c\u4eca\u56de\u53ef\u6709\u70ed\u95f9\u770b\u4e86\u3002\u201d\u4e91\u91cd\u5374\u662f\u5927\u51fa\u610f\u5916\uff0c\u4ed6\u4e00\u5fc3\u7b49\u5019\u5f20\u4e39 \u67ab\u8c01\u77e5\u5374\u6765\u4e86\u8fd9\u4e2a\u4ec0\u4e48\u6d77\u9f99\u5e2e\u7684\u5e2e\u4e3b\uff0c\u542c\u95f2\u4eba\u95f2\u8bdd\uff0c\u8fd9\u4e2a\u6d77\u9f99\u5e2e\u5e2e\u4e3b\uff0c\u4f3c\u4e4e\u4e5f\u662f\u82cf\u5dde\u4e00\u9738\u3002 \u3000\u3000\u524d\u9762\u7684\u4eba\u4e24\u8fb9\u5206\u5f00\uff0c\u53ea\u89c1\u4e00\u4e2a\u6d53\u7709\u5927\u773c\u7684\u7c97\u8c6a\u6c49\u5b50\uff0c\u5374\u7a7f\u7740\u957f\u888d\u9a6c\u8902\uff0c\u6545\u4f5c\u65af\u6587\uff0c\u6253\u626e \u5f97\u4e0d\u4f26\u4e0d\u7c7b\uff0c\u5e26\u7740\u4e03\u516b\u4e2a\u6b66\u5e08\uff0c\u8d8a\u4f17\u800c\u51fa\uff0c\u62b1\u62f3\u8bf4\u9053\uff1a\u201c\u9f99\u5e2e\u4e3b\uff0c\u4eca\u65e5\u4ec0\u4e48\u597d\u98ce\u5439\u4f60\u5230\u6b64\uff1f \u8bf7\u5750\uff0c\u8bf7\u5750\uff0c\u559d\u676f\u597d\u8336\u3002\u5582\uff0c\u5b69\u513f\u4eec\u5429\u5490\u91cc\u9762\u7684\u5f04\u4e9b\u7cbe\u81f4\u7684\u70b9\u5fc3\u6765\u3002\u201d\u90a3\u9f99\u5e2e\u4e3b\u677f\u7740\u9762\u5b54\uff0c \u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4e5d\u5934\u72ee\u5b50\uff0c\u6211\u4eca\u65e5\u763e\u8d77\uff0c\u7279\u5730\u8981\u6765\u548c\u4f60\u8d4c\u4e00\u573a\uff0c\u559d\u8336\u4e0d\u5fd9\uff0c\u5148\u8d4c\u51e0\u624b\u518d\u8bf4\u3002\u201d \u90a3\u4e5d\u5934\u72ee\u5b50\u6bb7\u5929\u9274\u4f3c\u4e4e\u5bf9\u4ed6\u9887\u4e3a\u5fcc\u60ee\uff0c\u7b11\u8138\u8bf4\u9053\uff1a\u201c\u54b1\u54e5\u513f\u4fe9\u4f55\u5fc5\u4f24\u8fd9\u548c\u6c14\uff0c\u4f60\u6709\u4ec0\u4e48\u5429 \u5490\uff0c\u5c0f\u5f1f\u529e\u5f97\u5230\u7684\uff0c\u5c3d\u7ba1\u5429\u5490\u4e0b\u6765\u4fbf\u662f\u4e86\u3002\u201d\u9f99\u5e2e\u4e3b\u500f\u5730\u4e00\u58f0\u51b7\u7b11\uff0c\u9053\uff1a\u201c\u8001\u6bb7\uff0c\u5f00\u996d\u9986\u7684 \u8fd8\u6015\u809a\u76ae\u5927\u7684\u98df\u5ba2\uff1f\u4f60\u65e2\u5f00\u8d4c\u573a\uff0c\u5c82\u80fd\u62d2\u7edd\u6211\u6765\u8d4c\u94b1\uff1f\u4f60\u6015\u6211\u6ca1\u94b1\u4e48\uff1f\u4f60\u95ee\u6211\u6709\u4ec0\u4e48\u5429\u5490\uff0c \u6211\u5c31\u662f\u8981\u548c\u4f60\u8d4c\u94b1\uff0c\u8fd9\u4f60\u603b\u529e\u5f97\u5230\u5427\u3002\u201d\u6bb7\u5929\u9274\u9762\u8272\u5927\u53d8\uff0c\u9053\uff1a\u201c\u4eba\u4eba\u6709\u9762\uff0c\u6811\u6811\u6709\u76ae\uff0c\u4f60 \u65e2\u5728\u4f17\u4eba\u9762\u524d\u6324\u5151\u6211\uff0c\u90a3\u4e48\u6211\u53ea\u6709\u820d\u547d\u966a\u541b\u5b50\u4e86\uff0c\u597d\u5427\uff0c\u4f60\u8981\u8d4c\u4ec0\u4e48\uff1f\u201d\u9f99\u5e2e\u4e3b\u9053\uff1a\u201c\u8d4c\u63b7 \u9ab0\u5b50\u6700\u723d\u5feb\uff0c\u5c31\u63b7\u9ab0\u5b50\u3002\u5582\uff0c\u8001\u90ed\uff0c\u4f60\u624b\u6c14\u597d\uff0c\u4f60\u66ff\u6211\u63b7\uff01\u8001\u6bb7\uff0c\u4f60\u81ea\u5df1\u63b7\u8fd8\u662f\u53eb\u4f60\u7684\u5927\u5e08 \u7236\u66ff\u4f60\u63b7\uff1f\u201d \u3000\u3000\u53ea\u89c1\u9f99\u5e2e\u4e3b\u4fa7\u9762\u8f6c\u51fa\u4e00\u4e2a\u8c8c\u4e0d\u60ca\u4eba\u7684\u67af\u7626\u8001\u5934\uff0c\u626f\u4e0b\u5934\u6234\u7684\u74dc\u76ae\u5c0f\u5e3d\uff0c\u9053\uff1a\u201c\u4ffa\u90ed\u6d2a\u62dc \u89c1\u5927\u54e5\u3002\u201d\u5e3d\u5b50\u4e0d\u8131\u72b9\u53ef\uff0c\u4e00\u8131\u4e0b\u6765\uff0c\u5168\u573a\u6ce8\u76ee\uff0c\u539f\u6765\u4ed6\u8c8c\u4e0d\u60ca\u4eba\uff0c\u5934\u53d1\u5374\u662f\u60ca\u4eba\u4e4b\u6781\uff0c\u6ee1 \u5934\u90fd\u662f\u7ea2\u53d1\uff0c\u72b9\u5982\u4e00\u5806\u4e71\u8349\uff0c\u53c8\u5982\u4e00\u56e2\u706b\u4e91\uff0c\u76d8\u5728\u5934\u4e0a\u3002\u4e91\u91cd\u89c1\u4e86\uff0c\u4e5f\u4e0d\u7531\u5f97\u5927\u5403\u4e00\u60ca\uff0c\u5fc3 \u4e2d\u5947\u9053\uff1a\u201c\u54c8\uff0c\u539f\u6765\u662f\u7ea2\u53d1\u5996\u9f99\u90ed\u6d2a\uff0c\u600e\u4e48\u4ed6\u4e5f\u6765\u4e86\uff1f\u201d\u8fd9\u90ed\u6d2a\u4e43\u662f\u5978\u5ba6\u738b\u632f\u7684\u5fc3\u8179\u6b66\u58eb\uff0c \u957f\u5e74\u533f\u5728\u53f8\u793c\u592a\u76d1\u5e9c\u4e2d\uff0c\u4e13\u53f8\u4fdd\u62a4\u738b\u632f\u4e4b\u8d23\uff0c\u5f88\u5c11\u5916\u51fa\uff0c\u975e\u4f46\u6c5f\u6e56\u4e0a\u5c11\u4eba\u77e5\u9053\uff0c\u5373\u4eac\u4e2d\u89c1\u8fc7 \u4ed6\u9762\u7684\u4e5f\u4e0d\u591a\u3002\u56e0\u4ed6\u53d1\u8272\u5947\u7279\uff0c\u5f20\u98ce\u5e9c\u66fe\u5bf9\u4e91\u91cd\u63d0\u8fc7\uff0c\u6240\u4ee5\u4e91\u91cd\u867d\u7136\u4e5f\u672a\u89c1\u8fc7\u4ed6\uff0c\u53ea\u770b\u4ed6\u7684 \u7ea2\u53d1\uff0c\u5c31\u77e5\u9053\u4ed6\u662f\u738b\u632f\u5e9c\u4e2d\u7684\u795e\u79d8\u4eba\u7269\uff0d\uff0d\u7ea2\u53d1\u5996\u9f99\u90ed\u6d2a\u3002 \u3000\u3000\u4e91\u91cd\u60f3\u9053\uff1a\u201c\u738b\u632f\u5bcc\u7532\u5929\u4e0b\uff0c\u4f55\u4ee5\u6d3e\u4eba\u6765\u4e0e\u4e00\u4e2a\u571f\u9738\u4e89\u593a\u56ed\u6797\uff1f\u4ee5\u90ed\u6d2a\u7684\u8eab\u4efd\uff0c\u4e5f\u4e0d\u8be5 \u505a\u4e00\u4e2a\u5730\u65b9\u5e2e\u4f1a\u5e2e\u4e3b\u7684\u526f\u624b\uff0c\u6b64\u4e8b\u771f\u662f\u4e07\u4e0d\u53ef\u89e3\u3002\u201d\u542c\u542c\u5f97\u90a3\u4e5d\u5934\u72ee\u5b50\u6bb7\u5929\u9274\u9053\uff1a\u201c\u8fd9\u4f4d\u90ed \u5e08\u7236\u66ff\u4f60\u8d4c\u5417\uff1f\u597d\uff0c\u6211\u4e0d\u7528\u522b\u4eba\u66ff\u4ee3\uff0c\u6211\u81ea\u5df1\u4e0b\u573a\u3002\u201d \u3000\u3000\u9f99\u5e2e\u4e3b\u76ae\u7b11\u8089\u4e0d\u7b11\u5730\u6253\u4e86\u4e2a\u54c8\u54c8\uff0c\u9053\uff1a\u201c\u597d\u6781\u5566\uff0c\u8fd9\u91cc\u662f\u5341\u4e07\u4e24\u94f6\u7968\uff0c\u90fd\u662f\u5927\u94b1\u5e84\u7684\uff0c \u4f60\u770b\u6e05\u695a\u4e86\u3002\u8fd9\u4e00\u53e3\u9ab0\u5b50\uff0c\u5c31\u8d4c\u5341\u4e07\u4e24\u94f6\u5b50\uff01\u201d\u4e5d\u5934\u72ee\u5b50\u6bb7\u5929\u9274\u9053\uff1a\u201c\u6211\u624b\u5934\u4e0a\u53ef\u6ca1\u6709\u8fd9\u8bb8 \u591a\u73b0\u94b1\u3002\u201d\u9f99\u5e2e\u4e3b\u53c8\u4ef0\u5929\u6253\u4e86\u4e2a\u54c8\u54c8\uff0c\u9053\uff1a\u201c\u4f60\u7684\u5bb6\u5e95\u6211\u8fd8\u4e0d\u77e5\u9053\u5417\uff1f\u4f60\u7684\u7530\u5730\u5e97\u94fa\u503c\u94f6\u56db \u5341\u4e07\u4e24\uff0c\u8fd9\u5feb\u6d3b\u6797\u4e5f\u7b97\u5b83\u503c\u56db\u5341\u4e07\u5427\uff0c\u4f60\u7684\u8d4c\u672c\u4e00\u5171\u662f\u516b\u5341\u4e07\u4e24\uff0c\u4f60\u653e\u5fc3\u8d4c\u5427\u3002\u201d\u6bb7\u5929\u9274\u5fc3 \u4e2d\u6c14\u6781\uff0c\u4e5f\u6253\u4e86\u4e2a\u54c8\u54c8\uff0c\u9053\uff1a\u201c\u539f\u6765\u4f60\u662f\u60f3\u8981\u6211\u7684\u5feb\u6d3b\u6797\u3002\u201d\u9f99\u5e2e\u4e3b\u9053\uff1a\u201c\u4f60\u8fd8\u672a\u8d4c\u5c31\u6015\u8f93 \u4e86\u4e48\uff1f\u201d\u6bb7\u5929\u9274\u9053\uff1a\u201c\u53ea\u6015\u672a\u5fc5\u80fd\u5982\u4f60\u613f\u3002\u597d\uff0c\u8fd9\u9ab0\u5b50\u4f60\u5148\u770b\u8fc7\u3002\u201d\u90ed\u6d2a\u628a\u90a3\u526f\u9ab0\u5b50\u62ff\u8d77\u4e00 \u6382\uff0c\u9f99\u5e2e\u4e3b\u9053\uff1a\u201c\u90ed\u5927\u54e5\uff0c\u6599\u4ed6\u4e0d\u6562\u578b\u5047\uff01\u201d\u90ed\u6d2a\u53c8\u5c06\u9ab0\u5b50\u9012\u8fc7\u53bb\uff0c\u9053\uff1a\u201c\u4e5d\u5934\u72ee\u5b50\uff0c\u4f60\u662f \u8fd9\u91cc\u7684\u5e84\u5bb6\uff0c\u4f60\u5148\u63b7\uff01\u201d \u3000\u3000\u6bb7\u5929\u9274\u53cc\u624b\u4e00\u6413\u4e00\u63b7\uff0c\u559d\u58f0\uff1a\u201c\u6740\uff01\u201d\u516d\u7c92\u9ab0\u5b50\u5728\u6d77\u7897\u4e2d\u6eda\u52a8\u6fc0\u8361\uff0c\u53ea\u542c\u5f97\u5531\u644a\u7684\u53eb \u9053\uff1a\u201c\u4e8c\u516d\u4e00\u56db\uff0c\u5341\u516d\u70b9\uff0c\u5927\uff01\u201d\u987b\u77e5\u63b7\u9ab0\u5b50\u5341\u516b\u70b9\u4e43\u662f\u6700\u5927\uff0c\u5341\u516d\u70b9\u5df2\u751a\u4e3a\u96be\u5f97\u3002\u6bb7\u5929\u9274 \u62b9\u62b9\u51b7\u6c57\uff0c\u9053\uff1a\u201c\u597d\uff0c\u59d3\u90ed\u7684\uff0c\u4f60\u8d76\u5427\uff01\u201d\u90a3\u7ea2\u53d1\u8001\u4eba\u5fae\u5fae\u4e00\u7b11\uff0c\u4e0d\u614c\u4e0d\u5fd9\u5c06\u9ab0\u5b50\u63a5\u5230\u624b \u4e2d\uff0c\u6307\u5934\u5fae\u5fae\u98a4\u52a8\uff0c\u731b\u5730\u5411\u7897\u4e2d\u4e00\u63b7\uff0c\u53ea\u542c\u5f97\u5531\u644a\u7684\u53eb\u9053\uff1a\u201c\u4e8c\u516d\u4e00\u4e94\uff0c\u5341\u4e03\u70b9\uff0c\u5927\uff01\u201d\u6bb7 \u5929\u9274\u9762\u8272\u53d1\u9752\uff0c\u53eb\u9053\uff1a\u201c\u6709\u9b3c\uff01\u518d\u63b7\uff01\u201d\u90a3\u7ea2\u53d1\u8001\u4eba\u9053\uff1a\u201c\u597d\uff0c\u518d\u63b7\uff0c\u8fd9\u4e00\u53e3\u662f\u8d4c\u4e8c\u5341\u4e07 \u4e86\uff01\u201d\u6bb7\u5929\u9274\u624b\u5fc3\u91cc\u6dcc\u6c57\uff0c\u98a4\u58f0\u53eb\u9053\uff1a\u201c\u5168\u8272\uff01\u201d\u4e00\u63b7\u4e0b\u53bb\uff0c\u53ea\u542c\u5f97\u5531\u644a\u7684\u53eb\u9053\uff1a\u201c\u4e8c\u516d\u4e00 \u4e94\uff0c\u5de7\u6781\u4e86\uff0c\u53c8\u662f\u5341\u4e03\u70b9\uff01\u201d\u63b7\u5230\u5341\u4e03\u70b9\u51e0\u4e4e\u53ef\u4ee5\u8bf4\u662f\u7a33\u64cd\u80dc\u5238\uff0c\u6bb7\u5929\u9274\u5fae\u73b0\u7b11\u5bb9\u3002\u53ea\u89c1\u90a3 \u7ea2\u53d1\u8001\u4eba\u4e0d\u58f0\u4e0d\u54cd\uff0c\u968f\u624b\u4e00\u63b7\uff0c\u56f4\u89c2\u7684\u4eba\u5168\u90fd\u53d8\u8272\uff0c\u5531\u644a\u7684\u53eb\u9053\uff1a\u201c\u516d\u7ea2\u56db\uff0c\u5168\u8272\uff01\u201d\u5168\u8272 \u6700\u5927\u3002\u7ea2\u8272\u8001\u4eba\u7b11\u9053\uff1a\u201c\u4f60\u53eb\u4e0d\u6765\uff0c\u6211\u4e0d\u53eb\u5b83\u53cd\u800c\u6765\uff0c\u597d\uff0c\u8fd9\u4e00\u53e3\u5c31\u8d4c\u56db\u5341\u4e07\u4e86\uff01\u201d\u6bb7\u5929\u9274 \u9762\u8272\u66f4\u662f\u96be\u770b\uff0c\u5934\u7b4b\u7ea2\u80c0\u9053\uff1a\u201c\u8fd9\u53e3\u4f60\u5148\u63b7\uff01\u201d\u90a3\u7ea2\u53d1\u8001\u4eba\u9053\uff1a\u201c\u597d\uff0c\u6211\u4fbf\u5148\u63b7\uff01\u201d\u53cc\u624b\u5408 \u62b1\uff0c\u5c06\u9ab0\u5b50\u5728\u638c\u5fc3\u4e00\u6447\uff0c\u63b7\u5165\u7897\u4e2d\uff0c\u987f\u65f6\u9e26\u96c0\u65e0\u58f0\uff0c\u6bb7\u5929\u9274\u9762\u8272\u5982\u571f\uff0c\u8fc7\u4e86\u4e00\u8fb9\u53ea\u542c\u5f97\u5531\u644a \u7684\u98a4\u58f0\u5531\u9053\uff1a\u201c\u516d\u4e2a\u516d\uff0c\u5341\u516b\u70b9\u517c\u5168\u8272\uff0c\u901a\u6740\uff01\u201d\u6309\u7167\u63b7\u9ab0\u5b50\u7684\u89c4\u77e9\uff0c\u63b7\u5230\u5341\u516b\u70b9\u6216\u5168\u8272\u90a3 \u662f\u4e0d\u80fd\u518d\u8d76\u7684\u4e86\u3002 \u3000\u3000\u9759\u4e86\u4e00\u9635\uff0c\u5168\u573a\u54d7\u7136\uff0c\u4eba\u4eba\u5fc3\u4e2d\u5947\u602a\u4e4b\u6781\uff0c\u4f55\u4ee5\u90a3\u7ea2\u53d1\u8001\u4eba\u624b\u98ce\u5982\u6b64\u4e4b\u201c\u987a\u201d\uff01\u4e91\u91cd\u8fdc \u89c2\u624b\u52bf\uff0c\u770b\u51fa\u4e86\u5176\u4e2d\u7834\u7efd\u3002\u539f\u6765\u6697\u5668\u529f\u592b\u6781\u597d\u7684\u4eba\uff0c\u624b\u529b\u53ef\u4ee5\u64cd\u7eb5\u81ea\u5982\uff0c\u80fd\u628a\u4efb\u4f55\u4e1c\u897f\u63b7\u5230 \u4efb\u4f55\u65b9\u4f4d\uff0c\u90a3\u4e48\u624b\u624b\u63b7\u51fa\u5168\u8272\u6216\u5341\u516b\u70b9\u90fd\u4e0d\u7a00\u5947\uff0c\u53ea\u662f\u8fd9\u79cd\u4e0a\u4e58\u529f\u592b\uff0c\u4e0d\u4f46\u65c1\u89c2\u7684\u4eba\u4e0d\u61c2\uff0c \u5373\u4e5d\u5934\u72ee\u5b50\u6bb7\u5929\u9274\u4e5f\u662f\u83ab\u540d\u5176\u5999\uff01\u5927\u5bb6\u90fd\u662f\u6c5f\u6e56\u4e0a\u53eb\u5f97\u54cd\u5b57\u53f7\u7684\u4eba\uff0c\u8f93\u4e86\u4fbf\u5f97\u8ba4\u8f93\uff0c\u4f55\u51b5\u90a3 \u9ab0\u5b50\u53c8\u662f\u81ea\u5df1\u7684\uff0c\u66f4\u4e0d\u80fd\u8bf4\u4eba\u505a\u5f04\u624b\u811a\u3002\u56e0\u6b64\u6bb7\u5929\u9274\u867d\u7136\u5fc3\u75db\u5982\u5272\u4e5f\u53ea\u5f97\u82e6\u7b11\u8bf4\u9053\uff1a\u201c\u59d3\u9f99 \u7684\uff0c\u8fd9\u5feb\u6d3b\u6797\u662f\u4f60\u7684\u4e86\uff01\u201d\u9f99\u5e2e\u4e3b\u8a00\u9053\uff1a\u201c\u4f60\u5168\u90e8\u8d4c\u672c\u516b\u5341\u4e07\uff0c\u8f93\u4e86\u4e03\u5341\u4e07\uff0c\u8fd8\u53ef\u4ee5\u62ff\u56de\u5341 \u4e07\uff0c\u4f60\u613f\u8981\u7530\u5730\u8fd8\u662f\u613f\u8981\u73b0\u94b1\uff0c\u59d3\u6bb7\u7684\uff0c\u6709\u5341\u4e07\u8eab\u5bb6\uff0c\u4e5f\u7b97\u5f97\u662f\u4e2a\u5bcc\u8c6a\u4e86\uff0c\u6211\u4ece\u4e0d\u8d76\u5c3d\u6740 \u7edd\uff0c\u8fd9\u56de\u7b97\u5bf9\u5f97\u8d77\u4f60\uff01\u201d\u90a3\u7ea2\u53d1\u8001\u4eba\u9053\uff1a\u201c\u95f2\u8bdd\u5c11\u8bf4\uff0c\u9650\u4f60\u4eec\u65e5\u843d\u4e4b\u524d\uff0c\u5168\u642c\u51fa\u5feb\u6d3b\u6797 \u53bb\uff01\u201d \u3000\u3000\u5ffd\u542c\u5f97\u4e00\u58f0\u6e05\u7b11\u6709\u4eba\u53eb\u9053\uff1a\u201c\u4e14\u6162\uff0c\u6211\u4e5f\u8981\u6765\u8d4c\u4e00\u8d4c\uff01\u201d\u4e91\u91cd\u773c\u775b\u4e00\u4eae\uff0c\u53ea\u89c1\u5f20\u4e39\u67ab\u767d \u8863\u98d8\u98d8\uff0c\u4ece\u4eba\u4e1b\u4e2d\u7f13\u7f13\u8d70\u51fa\uff0c\u81ea\u5df1\u521a\u624d\u5168\u795e\u6ce8\u610f\u8d4c\u573a\uff0c\u7adf\u4e0d\u77e5\u4ed6\u662f\u4ec0\u4e48\u65f6\u5019\u6765\u7684\uff01 \u3000\u3000\u4e5d\u5934\u72ee\u5b50\u6bb7\u5929\u9274\u77aa\u5927\u773c\u775b\uff0c\u4ed6\u4ece\u624b\u4e0b\u4eba\u6240\u63cf\u7ed8\u7684\u5f62\u8c8c\uff0c\u5df2\u77e5\u6b64\u4eba\u4fbf\u662f\u6298\u8fb1\u4ed6\u4e24\u4e2a\u6b66\u5e08\u7684 \u767d\u9a6c\u4e66\u751f\uff0c\u4f46\u8fd9\u65f6\u4ed6\u8d4c\u8f93\u4e03\u5341\u4e07\u4e24\u94f6\u5b50\uff0c\u65ad\u9001\u4e86\u5feb\u6d3b\u6797\uff0c\u54ea\u8fd8\u6709\u5fc3\u60c5\u548c\u5f20\u4e39\u67ab\u95f9\u4e8b\uff0c\u53ea\u662f\u5446 \u7acb\u4e00\u8fb9\uff0c\u62b1\u7740\u201c\u9694\u6c5f\u89c2\u706b\u201d\u7684\u6001\u5ea6\uff0c\u770b\u5f20\u4e39\u67ab\u4e0e\u90a3\u7ea2\u53d1\u8001\u4eba\u53c8\u662f\u5982\u4f55\u8d4c\u6cd5\uff1f \u3000\u3000\u90a3\u6d77\u9f99\u5e2e\u7684\u9f99\u5e2e\u4e3b\u548c\u7ea2\u53d1\u8001\u4eba\u90ed\u6d2a\u89c1\u4e86\u5f20\u4e39\u67ab\u5168\u90fd\u53d8\u4e86\u9762\u8272\uff0c\u5f20\u4e39\u67ab\u7b11\u9053\uff1a\u201c\u54c8\u54c8\uff0c\u4f60 \u4eec\u4e0d\u6562\u548c\u6211\u8d4c\u5417\uff1f\u201d \u3000\u3000\u539f\u6765\u5f20\u4e39\u67ab\u8863\u670d\u534e\u4e3d\uff0c\u4e00\u6d3e\u516c\u5b50\u7684\u6d3e\u5934\uff0c\u4e00\u5230\u82cf\u5dde\uff0c\u4fbf\u5f15\u8d77\u4e86\u6d77\u9f99\u5e2e\u6ce8\u610f\uff0c\u6d77\u9f99\u5e2e\u7684\u51e0 \u4e2a\u597d\u624b\u66fe\u8ddf\u8e2a\u4ed6\u5230\u5ba2\u5e97\u3002\u5f20\u4e39\u67ab\u65e9\u5df2\u53d1\u73b0\uff0c\u5374\u6545\u4f5c\u4e0d\u77e5\uff0c\u6545\u610f\u5c06\u968f\u8eab\u73e0\u5b9d\u642c\u51fa\u6765\u628a\u73a9\uff0c\u90a3\u51e0 \u4e2a\u597d\u624b\u4e5f\u662f\u8001\u6c5f\u6e56\u4e86\uff0c\u89c1\u4ed6\u5982\u6b64\uff0c\u53cd\u800c\u4e0d\u6562\u884c\u52ab\uff0c\u56de\u53bb\u62a5\u7ed9\u5e2e\u4e3b\u77e5\u9053\u3002\u9f99\u5e2e\u4e3b\u672c\u5f85\u63a5\u6536\u4e86\u5feb \u6d3b\u6797\u4e4b\u540e\u518d\u67e5\u6e05\u5f20\u4e39\u67ab\u7684\u5e95\u7ec6\uff0c\u7136\u540e\u51b3\u5b9a\u52a8\u624b\u4e0e\u5426\uff0c\u6599\u4e0d\u5230\u4ed6\u4e0d\u8bf7\u81ea\u6765\uff0c\u800c\u4e14\u8fd8\u8981\u548c\u81ea\u5df1\u8c6a \u8d4c\u3002 \u3000\u3000\u7ea2\u53d1\u8001\u4eba\u77a5\u4e86\u5f20\u4e39\u67ab\u4e00\u773c\uff0c\u9053\uff1a\u201c\u4f60\u8d4c\u591a\u5c11\uff1f\u201d\u5f20\u4e39\u67ab\u7b11\u9053\uff1a\u201c\u4f60\u6709\u591a\u5c11\u8d4c\u672c\uff1f\u201d\u9f99\u5e2e \u4e3b\u51b7\u7b11\u9053\uff1a\u201c\u6bb7\u6797\u4e3b\u7684\u4ea7\u4e1a\u90fd\u662f\u6211\u7684\u8d4c\u672c\u3002\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u5514\uff0c\u90a3\u4e48\u8fde\u4f60\u8fd9\u5341\u4e07\u4e24\u94f6\u7968\u4e5f\u4e0d \u8fc7\u662f\u4e5d\u5341\u4e07\u4e24\u3002\u597d\uff0c\u6211\u5c31\u548c\u4f60\u8d4c\u4e24\u624b\u6d88\u9063\u6d88\u9063\uff01\u201d\u7ea2\u53d1\u8001\u4eba\u9053\uff1a\u201c\u4f60\u8d4c\u591a\u5c11\uff1f\u201d\u5f20\u4e39\u67ab\u5fae\u5fae \u4e00\u7b11\uff0c\u81ea\u6000\u4e2d\u53d6\u51fa\u4e00\u4e32\u73cd\u73e0\uff0c\u4e2a\u4e2a\u53c8\u5706\u53c8\u5927\uff0c\u767d\u8272\u6676\u83b9\uff0c\u4e00\u770b\u5c31\u77e5\u662f\u65e0\u4ef7\u4e4b\u5b9d\uff0c\u90a3\u4e32\u73cd\u73e0\u8fd8 \u7cfb\u7740\u4e00\u5757\u5b9d\u77f3\uff0c\u53d1\u51fa\u95ea\u95ea\u7eff\u5149\uff0c\u8000\u4eba\u773c\u76ee\u3002\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u6211\u8fd9\u53e3\u9ab0\u5b50\u5c31\u8d4c\u8fd9\u4e32\u73cd\u73e0\u548c\u8fd9\u5757\u5b9d \u77f3\uff0c\u4f60\u4eec\u4f30\u4ef7\u53bb\uff01\u201d\u9f99\u5e2e\u4e3b\u63a5\u8fc7\u73cd\u73e0\u4e32\uff0c\u7ffb\u6765\u8986\u53bb\u5730\u4ed4\u7ec6\u770b\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u6211\u4eec\u8d4c\u94b1\u516c\u516c\u9053 \u9053\uff0c\u4f60\u8fd9\u4e32\u73cd\u73e0\u5171\u4e00\u767e\u9897\uff0c\u6bcf\u4e00\u9897\u90fd\u662f\u4e00\u6837\u5927\u5c0f\uff0c\u6beb\u65e0\u6742\u8d28\u7684\u53c8\u5706\u53c8\u5927\u7684\u5408\u6d66\u73cd\u73e0\uff0c\u786e\u662f\u96be \u5f97\u3002\u672c\u6765\u6bcf\u9897\u503c\u4e00\u5343\u4e94\u767e\u4e24\uff0c\u96be\u5f97\u6709\u4e00\u767e\u9897\u8fd9\u6837\u7684\u73cd\u73e0\uff0c\u4ef7\u94b1\u5e94\u8be5\u9ad8\u4e00\u70b9\uff0c\u5c31\u6298\u4e8c\u5341\u4e07\u4e24\u94f6 \u5b50\u5427\uff01\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u5514\uff0c\u4f60\u8fd8\u7b97\u8bc6\u8d27\u3002\u90a3\u8fd9\u5757\u5b9d\u77f3\u5462\uff1f\u201d\u9f99\u5e2e\u4e3b\u9053\uff1a\u201c\u8fd9\u5757\u7eff\u5b9d\u77f3\u66f4\u662f\u96be \u5f97\u4e4b\u7269\uff0c\u6211\u4e5f\u65e0\u6cd5\u4f30\u4ef7\uff0c\u6298\u5341\u4e07\u4e24\u4f60\u770b\u5982\u4f55\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u6298\u5341\u4e07\u662f\u7a0d\u4e3a\u5c11\u4e86\u4e00\u70b9\uff0c\u4f46\u53cd \u6b63\u662f\u62ff\u6765\u8d4c\u7684\uff0c\u6211\u4e5f\u61d2\u5f97\u548c\u4f60\u8ba1\u8f83\u3002\u597d\uff0c\u4e24\u6ce8\u5408\u5171\u4e09\u5341\u4e07\u4e24\uff0c\u6211\u8fd9\u53e3\u9ab0\u5c31\u8d4c\u4e09\u5341\u4e07\u4e24\u3002\u6362\u8fc7 \u4e00\u526f\u9ab0\u5b50\u6765\uff01\u201d \u3000\u3000\u7ba1\u644a\u7684\u4e0b\u624b\u8fde\u5fd9\u6362\u8fc7\u4e00\u526f\u9ab0\u5b50\u3002\u5f20\u4e39\u67ab\u6382\u4e86\u4e00\u4e0b\u9053\uff1a\u201c\u6211\u82e5\u5148\u63b7\uff0c\u8981\u662f\u6765\u4e86\u5168\u8272\u6216\u5341\u516b \u70b9\uff0c\u4f60\u5c31\u6ca1\u6709\u673a\u4f1a\u518d\u640f\u4e86\u3002\u6211\u4e0d\u5360\u8fd9\u4e2a\u4fbf\u5b9c\uff0c\u514d\u5f97\u4f60\u8f93\u4e86\u4e0d\u670d\u6c14\uff0c\u4f60\u5148\u63b7\u5427\uff01\u201d \u3000\u3000\u4e91\u91cd\u6697\u6697\u7eb3\u7f55\uff0c\u60f3\u9053\uff1a\u201c\u5f20\u4e39\u67ab\u7684\u6697\u5668\u529f\u592b\u4e16\u95f4\u5c11\u89c1\uff0c\u8981\u662f\u4ed6\u5148\u63b7\uff0c\u90a3\u662f\u7a33\u64cd\u80dc\u7b97\u3002\u73b0 \u5728\u8ba9\u8fd9\u7ea2\u53d1\u5996\u9f99\u5148\u63b7\uff0c\u90a3\u5c31\u662f\u5fc5\u8d25\u65e0\u7591\u7684\u4e86\uff01\u201d \u3000\u3000\u7ea2\u53d1\u8001\u4eba\u63a5\u8fc7\u9ab0\u5b50\uff0c\u6382\u4e86\u4e00\u6382\uff0c\u611f\u89c9\u4f3c\u4e4e\u7a0d\u5fae\u8f7b\u4e86\u4e00\u70b9\uff0c\u4e5f\u4e0d\u5728\u610f\uff0c\u53cc\u624b\u4e00\u6413\uff0c\u63b7\u5165\u7897 \u4e2d\u3002\u53ea\u89c1\u7897\u4e2d\u5148\u73b0\u51fa\u4e09\u6599\u516d\u70b9\u7684\u9ab0\u5b50\uff0c\u5176\u4ed6\u4e09\u6599\u5c1a\u5728\u6eda\u52a8\uff0c\u7ea2\u53d1\u8001\u4eba\u76ee\u4e0d\u8f6c\u775b\u5730\u6ce8\u89c6\uff0c\u7247\u523b \u4e4b\u95f4\uff0c\u53c8\u6709\u4e24\u6599\u9ab0\u5b50\u73b0\u51fa\u516d\u70b9\uff0c\u7ea2\u53d1\u8001\u4eba\u9762\u73b0\u7b11\u5bb9\uff0c\u63a5\u7740\u90a3\u6700\u540e\u4e00\u6599\u9ab0\u53c8\u73b0\u51fa\u516d\u70b9\uff0c\u5374\u5ffd\u7136 \u8f6c\u52a8\u4e00\u4e0b\uff0c\u5b9a\u5728\u7897\u4e2d\uff0c\u73b0\u51fa\u4e94\u70b9\u3002\u5531\u644a\u7684\u5531\u9053\uff1a\u201c\u4e8c\u516d\u4e00\u4e94\u5341\u4e03\u70b9\uff0c\u5927\uff01\u201d\u7ea2\u53d1\u8001\u4eba\u672c\u60f3\u63b7 \u516d\u4e2a\u516d\u70b9\uff0c\u73b0\u5728\u867d\u672a\u5982\u5fc3\u6240\u613f\uff0c\u5341\u4e03\u4ea6\u5df2\u5341\u5206\u96be\u5f97\uff0c\u4fbf\u7b11\u9053\uff1a\u201c\u5341\u4e03\u70b9\u4fbf\u5341\u4e03\u70b9\uff0c\u4f60\u8d76 \u5427\uff01\u201d \u3000\u3000\u5f20\u4e39\u67ab\u5c06\u9ab0\u5b50\u4e00\u629b\uff0c\u53c8\u63a5\u5728\u624b\u4e2d\uff0c\u9053\uff1a\u201c\u5341\u4e03\u70b9\u8fd9\u53ef\u96be\u8d76\u5f97\u5f88\u554a\uff01\u201d\u4e24\u773c\u671b\u5929\uff0c\u77a7\u4e5f\u4e0d \u77a7\u4fbf\u4e00\u628a\u63b7\u51fa\uff0c\u987f\u65f6\u9e26\u96c0\u65e0\u58f0\uff0c\u7ea2\u53d1\u8001\u4eba\u7741\u5927\u4e86\u773c\uff01 \u3000\u3000\u53ea\u542c\u5f97\u5531\u644a\u7684\u5531\u9053\uff1a\u201c\u53cc\u56db\u4e24\u4e94\u53c8\u53cc\u516d\uff0c\u56db\u4e94\u516d\u5168\u6740\uff01\u201d\u5f20\u4e39\u67ab\u968f\u624b\u63b7\u51fa\u56db\u4e94\u516d\u5168\u80dc\u7684 \u9ab0\u5b50\uff0c\u4e91\u91cd\u5e76\u4e0d\u611f\u5230\u610f\u5916\uff0c\u5176\u4ed6\u7684\u4eba\u90fd\u5927\u89c9\u7a00\u5947\uff1a\u7ea2\u53d1\u8001\u4eba\u7684\u624b\u98ce\u4e4b\u4f73\u5df2\u662f\u5947\u8ff9\uff0c\u800c\u5f20\u4e39\u67ab \u7684\u201c\u8fd0\u9053\u201d\u8fd8\u8981\u6bd4\u4ed6\u66f4\u597d\uff01\u90a3\u7ea2\u53d1\u8001\u4eba\u4e5f\u662f\u6697\u6697\u7eb3\u7f55\uff0c\u4ed6\u7ec3\u6709\u6bd2\u9f99\u638c\u7684\u529f\u592b\u53ca\u64c5\u6253\u5947\u95e8\u6697\u5668 \u201c\u6bd2\u9f99\u9489\u201d\uff0c\u52b2\u529b\u5927\u5c0f\uff0c\u53ef\u4ee5\u968f\u5fc3\u6240\u6b32\uff0c\u6240\u4ee5\u4eba\u79f0\u7ea2\u53d1\u5996\u9f99\u3002\u4ed6\u63b7\u9ab0\u5b50\u7684\u624b\u6cd5\u66f4\u662f\u7ec3\u8fc7\u5343\u767e \u904d\uff0c\u8981\u591a\u5c11\u70b9\u5c31\u591a\u5c11\u70b9\uff0c\u4ece\u65e0\u4e00\u5931\uff0c\u4e0d\u6599\u4eca\u65e5\u5374\u8d25\u5728\u5f20\u4e39\u67ab\u624b\u4e0b\u3002 \u3000\u3000\u7ea2\u53d1\u8001\u4eba\u4e0d\u77e5\uff0c\u539f\u6765\u5f20\u4e39\u67ab\u5df2\u5728\u9ab0\u5b50\u4e0a\u505a\u4e86\u624b\u811a\uff0c\u4ed6\u5728\u4e00\u6382\u4e00\u6413\u4e4b\u95f4\uff0c\u5df2\u6697\u8fd0\u5185\u5bb6\u771f \u529b\uff0c\u5c06\u9ab0\u5b50\u7684\u9aa8\u8d28\u9707\u5f97\u677e\u8f6f\uff0c\u8fd9\u79cd\u4e0a\u4e58\u7684\u5185\u529f\uff0c\u987b\u8fd0\u7528\u5f97\u6070\u5230\u597d\u5904\uff0c\u52b2\u529b\u7a0d\u5927\u4f1a\u628a\u9ab0\u5b50\u9707 \u88c2\uff0c\u52b2\u529b\u7a0d\u8f7b\u53c8\u4e0d\u89c1\u6548\uff0c\u6240\u4ee5\u8fde\u7ea2\u53d1\u8001\u4eba\u4e5f\u7740\u4e86\u9053\u513f\u3002\u4ed6\u4e0d\u77e5\u9ab0\u5b50\u5df2\u7ecf\u53d8\u8d28\uff0c\u4ecd\u662f\u7528\u521a\u624d\u63b7 \u201c\u5168\u8272\u201d\u7684\u4e00\u6837\u529b\u9053\uff0c\u6240\u4ee5\u60f3\u63b7\u5341\u516b\u70b9\u5374\u53ea\u63b7\u4e86\u4e2a\u5341\u4e03\u70b9\u6765\uff01 \u3000\u3000\u5f20\u4e39\u67ab\u80dc\u4e86\u4e00\u573a\uff0c\u82e5\u65e0\u5176\u4e8b\uff0c\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u8fde\u672c\u5e26\u5229\u4e00\u5171\u662f\u516d\u5341\u4e07\u4e24\u4e86\uff0c\u8fd9\u4e00\u6ce8\u5c31\u8d4c\u516d \u5341\u4e07\u4e24\uff01\u201d\u7ea2\u53d1\u8001\u4eba\u7a0d\u4e00\u601d\u91cf\uff0c\u9053\uff1a\u201c\u597d\uff0c\u518d\u966a\u4f60\u8d4c\u4e00\u53e3\uff0c\u8fd9\u6b21\u8ba9\u4f60\u5148\u63b7\uff01\u201d\u6b64\u8a00\u4e00\u51fa\uff0c\u4e91 \u91cd\u53c8\u662f\u6697\u6697\u7eb3\u7f55\uff0c\u5fc3\u9053\uff1a\u201c\u7ecf\u8fc7\u4e86\u9002\u624d\u8fd9\u4ed7\uff0c\u7ea2\u8001\u5996\u9f99\u96be\u9053\u8fd8\u4e0d\u77e5\u9053\u5f20\u4e39\u67ab\u4e5f\u662f\u6253\u6697\u5668\u7684\u597d \u624b\uff1f\u4e3a\u4f55\u8fd8\u6562\u8ba9\u4ed6\u5148\u63b7\uff1f\u201d\u53ea\u542c\u5f97\u5f20\u4e39\u67ab\u7b11\u9053\uff1a\u201c\u8ba9\u6211\u5148\u63b7\uff0c\u597d\uff0c\u90a3\u4f60\u53ef\u522b\u540e\u6094\u3002\u201d\u62ff\u8d77\u9ab0 \u5b50\uff0c\u77a7\u4e5f\u4e0d\u77a7\uff0c\u53c8\u662f\u4e00\u628a\u63b7\u4e86\u4e0b\u53bb\uff0c\u7897\u4e2d\u516d\u7c92\u9ab0\u5b50\u6b63\u5728\u6eda\u52a8\uff0c\u90a3\u7ea2\u53d1\u8001\u4eba\u9661\u7136\u4e00\u58f0\u731b\u559d\uff1a \u201c\u6740\uff01\u201d\u516d\u7c92\u9ab0\u5b50\u5b9a\u4e86\u4e0b\u6765\u3002\u5531\u644a\u7684\u5531\u9053\uff1a\u201c\u53cc\u4e8c\u4e00\u4e00\uff0c\u4e94\u70b9\uff0c\u5c0f\uff01\u201d\u7ea2\u53d1\u8001\u4eba\u7b11\u9053\uff1a \u201c\u54c8\uff0c\u539f\u6765\u662f\u4e2a\u81ed\u4e94\uff01\u201d\u63b7\u9ab0\u5b50\u6700\u5927\u662f\u5341\u516b\u70b9\uff0c\u6700\u5c0f\u662f\u56db\u70b9\uff08\u4e00\u3001\u4e8c\u3001\u4e09\u901a\u8d54\uff0c\u4e0d\u7b97\u5728 \u5185\uff09\uff0c\u63b7\u51fa\u4e2a\u4e94\u70b9\uff0c\u90a3\u51e0\u4e4e\u662f\u5fc5\u8d25\u4e4b\u5c40\u4e86\u3002\u4e91\u91cd\u542c\u4ed6\u8fd9\u4e00\u58f0\u5927\u559d\uff0c\u5df2\u77e5\u4ed6\u662f\u7528\u201c\u4f20\u58f0\u9707\u7269\u201d \u7684\u529f\u592b\uff0c\u628a\u5f20\u4e39\u67ab\u9ab0\u5b50\u7684\u70b9\u6570\u53d8\u4e86\u3002\u8d4c\u63b7\u9ab0\u5b50\u54b1\u5362\u559d\u96c9\u4e43\u662f\u4e60\u60ef\uff0c\u8c01\u4e5f\u4e0d\u80fd\u5e72\u6d89\u3002\u4e91\u91cd\u5fc3 \u9053\uff1a\u201c\u5440\uff0c\u5f20\u4e39\u67ab\u8fd9\u4e2a\u54d1\u4e8f\u662f\u5403\u5b9a\u7684\u4e86\u3002\u201d \u3000\u3000\u7ea2\u53d1\u8001\u4eba\u5f97\u610f\u6d0b\u6d0b\uff0c\u6293\u8d77\u9ab0\u5b50\uff0c\u54d7\u5566\u4e00\u58f0\u5f80\u7897\u4e2d\u63b7\u53bb\u3002\u53ea\u542c\u5f97\u5f20\u4e39\u67ab\u54c8\u54c8\u5927\u7b11\uff0c\u5531\u644a\u7684 \u5531\u9053\uff1a\u201c\u53cc\u4e00\u4e00\u4e8c\uff0c\u56db\u70b9\uff01\u201d\u91cd\u8986\u4e24\u6b21\uff0c\u58f0\u97f3\u98a4\u6296\uff0c\u663e\u5f97\u975e\u5e38\u60ca\u8bb6\u3002\u5f20\u4e39\u67ab\u7b11\u9053\uff1a\u201c\u54c8\uff0c\u539f \u6765\u662f\u4e2a\u81ed\u56db\uff01\u201d\u7ea2\u53d1\u8001\u4eba\u9762\u8272\u5982\u8721\uff0c\u4ed6\u63b7\u9ab0\u5b50\u8f93\u4e86\uff0c\u4e5f\u5373\u662f\u5728\u6697\u5668\u624b\u6cd5\u4e0e\u5185\u529f\u7684\u8f83\u91cf\u4e0a\u90fd\u8f93 \u4e86\uff01 \u3000\u3000\u5f20\u4e39\u67ab\u624b\u6307\u4e00\u642d\uff0c\u201c\u556a\u201d\u7684\u6253\u4e86\u4e00\u54cd\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u4e24\u53e3\u9ab0\u5b50\u5171\u8f93\u4e5d\u5341\u4e07\uff0c\u6070\u597d\u628a\u8d4c\u672c\u8f93 \u6e05\uff0c\u94f6\u7968\uff0c\u4ea7\u4e1a\uff0c\u8fde\u8fd9\u5feb\u6d3b\u6797\u90fd\u662f\u6211\u8001\u5f20\u7684\u4e86\uff01\u201d \u3000\u3000\u4e5d\u5934\u72ee\u5b50\u6bb7\u5929\u9274\u7a81\u7136\u4e00\u8dc3\u800c\u8d77\uff0c\u547c\u5730\u4e00\u6293\u5411\u5f20\u4e39\u67ab\u80a9\u5934\u6293\u53bb\uff0c\u559d\u9053\uff1a\u201c\u54fc\uff0c\u4f60\u8fd9\u9a97\u5b50\uff0c \u4f60\u6562\u62a2\u6211\u7684\u5feb\u6d3b\u6797\uff1f\u201d\u559d\u58f0\u672a\u4e86\u5ffd\u5730\u54ce\u54df\u4e00\u58f0\u5012\u5728\u5730\u4e0a\u3002\u5f20\u4e39\u67ab\u7b11\u9053\uff1a\u201c\u5440\uff0c\u72ee\u5b50\u722a\u65ad \u4e86\uff01\u201d\u4f17\u4eba\u770b\u65f6\uff0c\u53ea\u89c1\u6bb7\u5929\u9274\u7684\u4e94\u53ea\u624b\u6307\u90fd\u5df2\u5c48\u6298\u8131\u8282\uff0c\u8840\u8089\u6dcb\u6f13\uff0c\u75db\u5f97\u6655\u4e86\u8fc7\u53bb\uff01 \u3000\u3000\u6bb7\u5929\u9274\u7684\u6253\u624b\u8702\u6d8c\u800c\u4e0a\uff0c\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u5478\uff0c\u4e0d\u8981\u8138\uff0c\u613f\u8d4c\u670d\u8f93\uff0c\u4f55\u51b5\u6211\u8fd9\u5feb\u6d3b\u6797\u53c8\u4e0d\u662f \u4ece\u4f60\u59d3\u6bb7\u624b\u4e0a\u8d62\u7684\uff01\u201d\u8863\u8882\u98d8\u98d8\u5de6\u4e00\u62f3\u53f3\u4e00\u811a\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u628a\u90a3\u4e9b\u6253\u624b\u5168\u90fd\u6253\u8dcc\u3002\u7ea2\u53d1\u8001\u4eba \u4f38\u624b\u4e00\u9694\uff0c\u53eb\u9053\uff1a\u201c\u4e5d\u5934\u72ee\u5b50\uff0c\u4e0d\u8981\u4e22\u4e86\u5403\u6c5f\u6e56\u996d\u7684\u9762\u5b50\uff01\u201d\u660e\u662f\u5e2e\u5f20\u4e39\u67ab\u65a5\u8d23\u6bb7\u5929\u9274\uff0c\u5b9e \u662f\u6697\u4e0b\u6bd2\u624b\uff0c\u54ea\u77e5\u5f20\u4e39\u67ab\u673a\u7075\u4e4b\u6781\uff0c\u77e5\u4ed6\u624b\u638c\u6709\u6bd2\uff0c\u8863\u8896\u4e00\u6251\u7adf\u4ed6\u7684\u638c\u529b\u5378\u4e86\u5f00\u53bb\uff0c\u4f6f\u4f5c\u4e0d \u77e5\uff0c\u6545\u610f\u7b11\u9053\uff1a\u201c\u8fd9\u624d\u662f\u53e5\u4eba\u8bdd\uff01\u201d\u5438\u4e86\u4e00\u53e3\u51b7\u8336\u5411\u6bb7\u5929\u9274\u5934\u9762\u55b7\u53bb\uff0c\u6bb7\u5929\u9274\u60a0\u60a0\u9192\u8f6c\uff0c\u9f99 \u5e2e\u4e3b\u9053\uff1a\u201c\u4e5d\u5934\u72ee\u5b50\uff0c\u8fd9\u6b21\u6211\u4eec\u90fd\u8ba4\u683d\u4e86\uff0c\u4f60\u5230\u6211\u6d77\u9f99\u5e2e\u53bb\u505a\u4e2a\u9999\u4e3b\u5427\uff0c\u8fd9\u5feb\u6d3b\u6797\u770b\u4ed6\u4fdd\u5f97 \u591a\u4e45\u3002\u201d\u9f99\u5e2e\u4e3b\u4e5f\u662f\u6b66\u6797\u597d\u624b\uff0c\u770b\u51fa\u8fde\u7ea2\u53d1\u8001\u4eba\u4e5f\u975e\u5f20\u4e39\u67ab\u4e4b\u654c\uff0c\u53ea\u597d\u4f5c\u51fa\u6c5f\u6e56\u6c14\u6982\uff0c\u613f\u8d54 \u670d\u8f93\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u4e5d\u5934\u72ee\u5b50\uff0c\u628a\u4f60\u7684\u7530\u5730\u94fa\u5951\u4e0e\u5bb6\u4e2d\u73b0\u94b1\u90fd\u642c\u51fa\u6765\uff01\u201d\u6bb7\u5929\u9274\u7528\u836f\u88f9\u597d\u624b \u6307\uff0c\u5782\u5934\u4e27\u6c14\u7684\u9053\uff1a\u201c\u90fd\u4f9d\u4f60\uff01\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u4f60\u53ef\u8981\u505a\u5f97\u6f02\u4eae\u4e00\u70b9\uff0c\u4f60\u6709\u591a\u5c11\u7530\u5730\u4ea7\u4e1a\u73b0 \u94b1\uff0c\u6211\u90fd\u77e5\u9053\uff0c\u82e5\u7136\u5f04\u9b3c\uff0c\u4f60\u5c31\u662f\u6709\u5341\u4e2a\u5934\uff0c\u6211\u90fd\u65ab\u4e86\u3002\u5582\uff0c\u4f60\u4eec\u968f\u4ed6\u53bb\u642c\u4e1c\u897f\uff01\u201d\u53ea\u89c1\u4e00 \u5927\u7fa4\u4eba\u6b22\u58f0\u96f7\u52a8\uff0c\u90fd\u62e5\u4e86\u4e0a\u6765\uff0c\u539f\u6765\u8fd9\u7fa4\u4eba\u6709\u4e9b\u662f\u6fb9\u53f0\u6751\u7684\u6751\u6c11\uff0c\u6709\u4e9b\u662f\u82cf\u5dde\u7684\u8d2b\u6c11\uff0c\u90fd\u662f \u5f20\u4e39\u67ab\u53eb\u6765\u7684\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u628a\u4e5d\u5934\u72ee\u5b50\u7684\u7530\u5730\u94fa\u5951\u4e00\u628a\u706b\u70e7\u4e2a\u5e72\u51c0\uff0c\u5c06\u73b0\u91d1\u767d\u94f6\u5168\u90fd\u5206\u6563\uff0c\u95f9\u4e86\u4e00\u4e2a\u4e0b\u5348\uff0c \u624d\u5904\u7f6e\u505c\u5f53\uff1b\u4e5d\u5934\u72ee\u5b50\u3001\u9f99\u5e2e\u4e3b\u548c\u7ea2\u53d1\u5996\u9f99\u90ed\u6d2a\u7b49\u4e00\u5e72\u4eba\u9762\u4e0a\u65e0\u5149\uff0c\u65e9\u5df2\u6084\u6084\u6e9c\u8d70\u3002\u5f20\u4e39\u67ab \u5c06\u4e5d\u5934\u72ee\u5b50\u7684\u8d22\u4ea7\u6563\u5c3d\uff0c\u54c8\u54c8\u5927\u7b11\uff0c\u5ffd\u7136\u4fef\u8eab\u5728\u83b2\u5858\u91cc\u6458\u4e86\u4e00\u6735\u8377\u82b1\uff0c\u541f\u9053\uff1a\u201c\u8fd8\u6211\u540d\u56ed\u771f \u9762\u76ee\uff0c\u83b2\u82b1\u4eca\u65e5\u51fa\u6de4\u6ce5\uff01\u201d\u773c\u4e2d\u7c0c\u7c0c\u6389\u4e0b\u6cea\u6765\u3002\u4e91\u91cd\u5fc3\u9053\uff1a\u201c\u4ed6\u5fc5\u7136\u662f\u770b\u5230\u7956\u4e1a\u5982\u6b64\u88ab\u4eba\u7cdf \u584c\uff0c\u6240\u4ee5\u5fc3\u4e2d\u751f\u611f\u3002\u201d\u8fd9\u65f6\u4eba\u7fa4\u6e10\u6e10\u6563\u53bb\uff0c\u4e91\u91cd\u6015\u5f20\u4e39\u67ab\u53d1\u73b0\uff0c\u4e5f\u6084\u6084\u5730\u6e9c\u8d70\u4e86\uff01 \u3000\u3000\u4e91\u91cd\u56de\u5230\u629a\u8859\uff0c\u7687\u5e1d\u6240\u6d3e\u7684\u4e03\u540d\u9ad8\u624b\u5df2\u6765\u4e86\u4e24\u4eba\uff0c\u5374\u662f\u5927\u5185\u603b\u7ba1\u5eb7\u8d85\u6d77\u7684\u4e24\u4e2a\u5e08\u53d4\u94c1\u81c2 \u91d1\u733f\u9f99\u9547\u65b9\u4e0e\u4e09\u82b1\u5251\u7384\u7075\u5b50\uff0c\u4e91\u91cd\u5728\u593a\u72b6\u5143\u4e4b\u65f6\u66fe\u6253\u8d25\u8fd9\u4e24\u4eba\u7684\u5e08\u4f84\u9646\u5c55\u9e4f\uff0c\u7b97\u5f97\u662f\u6709\u70b9\u5c0f \u5c0f\u7684\u201c\u6881\u5b50\u201d\uff08\u6028\u4ec7\uff09\uff0c\u4f46\u800c\u4eca\u90fd\u5949\u4e86\u7687\u547d\uff0c\u8fd9\u70b9\u4ec7\u6028\u5927\u5bb6\u4e5f\u4e0d\u4fbf\u518d\u63d0\u3002\u4e91\u91cd\u5c06\u5feb\u6d3b\u6797\u6240\u89c1 \u4e4b\u4e8b\u5bf9\u94c1\u81c2\u91d1\u733f\u4e0e\u4e09\u82b1\u5251\u8bf4\u4e86\uff0c\u8fd9\u4e24\u4eba\u90fd\u662f\u6c5f\u6e56\u8001\u624b\uff0c\u542c\u4e86\u4e91\u91cd\u4e4b\u8a00\uff0c\u76f8\u5bf9\u671b\u4e86\u4e00\u773c\uff0c\u4e24\u4eba \u90fd\u76b1\u8d77\u7709\u5934\uff0c\u8fc7\u4e86\u4e00\u9635\uff0c\u94c1\u81c2\u91d1\u733f\u9f99\u9547\u65b9\u8bf4\u9053\uff1a\u201c\u6b64\u4e8b\u8e4a\u8df7\uff0c\u7ea2\u53d1\u5996\u9f99\u662f\u738b\u632f\u6700\u5f97\u529b\u7684\u4eba\uff0c \u4ed6\u4e3a\u4f55\u8981\u5e2e\u6d77\u9f99\u5e2e\u62a2\u5feb\u6d3b\u6797\uff1f\u5f20\u4e39\u67ab\u6325\u91d1\u5982\u571f\uff0c\u884c\u8e2a\u65e0\u5b9a\uff0c\u4ed6\u5374\u53c8\u4e3a\u4f55\u504f\u504f\u8981\u8fd9\u5feb\u6d3b\u6797\uff1f\u542c \u4f60\u6240\u8bf4\uff0c\u8fd9\u5feb\u6d3b\u6797\u662f\u5f20\u58eb\u8bda\u4ee5\u524d\u7684\u907f\u7f72\u884c\u5bab\uff0c\u8bf4\u4e0d\u5b9a\u5f20\u58eb\u8bda\u7684\u85cf\u5b9d\u4e0e\u5730\u56fe\u90fd\u57cb\u5728\u5feb\u6d3b\u6797\u4e4b \u5185\u3002\u201d \u3000\u3000\u4e91\u91cd\u4e5f\u89c9\u6709\u7406\uff0c\u4e8e\u662f\u4e09\u4eba\u5403\u8fc7\u665a\u996d\uff0c\u6b47\u4e86\u4e00\u4f1a\uff0c\u542c\u5f97\u8c2f\u697c\u6253\u4e86\u4e09\u66f4\uff0c\u4fbf\u90fd\u6362\u4e86\u591c\u884c\u8863\u5954 \u8d74\u5feb\u6d3b\u6797\u3002\u5feb\u6d3b\u6797\u539f\u6765\u7684\u90a3\u4e00\u73ed\u725b\u9b3c\u86c7\u795e\u5df2\u5168\u88ab\u5f20\u4e39\u67ab\u8d76\u8dd1\uff0c\u8fd9\u65f6\u504c\u5927\u7684\u4e00\u4e2a\u56ed\u6797\u51b7\u51b7\u6e05 \u6e05\uff0c\u4e00\u671b\u4e0b\u53bb\uff0c\u5047\u5c71\u6e56\u77f3\uff0c\u5343\u5947\u767e\u602a\uff0c\u66f4\u663e\u795e\u79d8\u5e7d\u7f8e\u3002 \u3000\u3000\u8fd9\u4e09\u4eba\u90fd\u662f\u8f7b\u529f\u7edd\u9876\uff0c\u7ffb\u8fc7\u56f4\u5899\uff0c\u6084\u6084\u98de\u5165\uff0c\u6b63\u5f85\u5206\u5934\u641c\u7d22\uff0c\u5ffd\u542c\u4e1c\u9762\u4f20\u6765\u58f0\u54cd\uff0c\u4e09\u4eba \u86c7\u884c\u5154\u4f0f\uff0c\u8eba\u5728\u5047\u5c71\u77f3\u540e\u3002\u53ea\u542c\u5f97\u4e00\u4e2a\u8bf4\u9053\uff1a\u201c\u5f20\u4e39\u67ab\u90a3\u5c0f\u5b50\u8c05\u662f\u6015\u4e86\u54b1\u4eec\uff0c\u6240\u4ee5\u95fb\u98ce\u9009 \u907f\uff01\u201d\u53c8\u4e00\u4eba\u8bf4\u9053\uff1a\u201c\u83ab\u975e\u4ed6\u5df2\u7ecf\u5f97\u624b\u4e86\uff1f\u201d\u53c8\u4e00\u4eba\u9053\uff1a\u201c\u738b\u516c\u516c\u679c\u7136\u6599\u5f97\u4e0d\u5dee\uff0c\u597d\u5728\u6211\u4eec \u6765\u5f97\u4e0d\u8fdf\u3002\u201d\u8bf4\u8bdd\u8fd9\u4eba\uff0c\u6b63\u662f\u7ea2\u53d1\u5996\u9f99\u90ed\u6d2a\u3002\u4e91\u91cd\u6697\u6697\u5403\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u539f\u6765\u8fd9\u73ed\u4eba\u679c\u7136\u662f\u738b \u632f\u6d3e\u6765\u7684\u3002\u5f20\u4e39\u67ab\u5230\u82cf\u5dde\u5bfb\u5b9d\u7684\u98ce\u58f0\u600e\u4e48\u4f1a\u4f20\u4e86\u51fa\u53bb\uff1f\u201d\u7ee7\u800c\u4e00\u60f3\uff0c\u738b\u632f\u5728\u5bab\u4e2d\u8033\u76ee\u6781\u591a\uff0c \u8033\u76ee\u6781\u7075\uff0c\u7687\u5e1d\u770b\u7834\u5f20\u4e39\u67ab\u7684\u884c\u85cf\uff0c\u6d3e\u9063\u81ea\u5df1\u5230\u82cf\u5dde\u4e4b\u4e8b\uff0c\u60f3\u5fc5\u4e5f\u5df2\u88ab\u4ed6\u63a2\u542c\u51fa\u6765\u4e86\u3002 \u3000\u3000\u53ea\u542c\u5f97\u90ed\u6d2a\u53c8\u9053\uff1a\u201c\u6309\u56fe\u4e2d\u6240\u793a\uff0c\u8fd9\u662f\u8fd9\u91cc\u4e86\u3002\u4f60\u770b\u8fd9\u91cc\u6709\u6316\u6398\u7684\u75d5\u8ff9\uff0c\u4f46\u5c71\u77f3\u5374\u672a\u5f04 \u5f00\uff0c\u60f3\u662f\u90a3\u5c0f\u5b50\u5b64\u8eab\u4e00\u4eba\uff0c\u672a\u53ca\u6398\u5b9d\uff0c\u542c\u5f97\u6211\u4eec\u5927\u961f\u5230\u6765\uff0c\u4fbf\u5148\u9003\u4e86\u3002\u201d\u63a5\u7740\u53ea\u542c\u5f97\u4e00\u9635\u9504 \u5934\u6398\u77f3\uff0c\u94c1\u679d\u6316\u77f3\u4e4b\u58f0\u3002\u4e91\u91cd\u80a9\u5934\u4e00\u8038\uff0c\u5374\u5ffd\u88ab\u4e09\u82b1\u5251\u8f7b\u8f7b\u4e00\u6309\uff0c\u5728\u4ed6\u8033\u8fb9\u8bf4\u9053\uff1a\u201c\u522b\u5fd9\uff0c \u5f85\u4ed6\u4eec\u6398\u51fa\u4e4b\u540e\uff0c\u54b1\u4eec\u518d\u6765\u4e2a\u9ed1\u5403\u9ed1\u3002\u201d \u3000\u3000\u4e91\u91cd\u4ece\u77f3\u9699\u7f1d\u4e2d\u77a7\u51fa\uff0c\u53ea\u89c1\u4e00\u5757\u5f62\u5982\u731b\u864e\u7684\u5927\u6e56\u77f3\u4e4b\u524d\uff0c\u56f4\u7740\u5341\u6765\u4e2a\u4eba\uff0c\u6b63\u5728\u6316\u6398\uff0c\u8fc7 \u4e86\u4e00\u9635\uff0c\u4e00\u4eba\u53eb\u9053\uff1a\u201c\u5f97\u4e86\uff0c\u5f97\u4e86\uff0c\u4f60\u770b\u8fd9\u4e2a\u6d1e\u7a74\uff0c\u54c8\uff0c\u8fd8\u6709\u4e00\u5757\u767d\u7389\u7891\u5c01\u7740\uff01\u201d\u4e00\u4eba\u4e3e\u8d77 \u94c1\u9539\uff0c\u731b\u5730\u4e00\u6316\uff0c\u5ffd\u5730\u84ec\u7684\u4e00\u58f0\uff0c\u6e85\u51fa\u65e0\u6570\u706b\u661f\uff0c\u90ed\u6d2a\u5927\u53eb\u9053\uff1a\u201c\u5feb\u95ea\u5f00\uff01\u201d\u6d1e\u4e2d\u500f\u5730\u5c04\u51fa \u65e0\u6570\u5229\u7bad\uff0c\u7acb\u523b\u6709\u516d\u3001\u4e03\u4eba\u4e2d\u7bad\u5012\u5730\uff0c\u9762\u4e0a\u7600\u9ed1\uff0c\u7ea2\u53d1\u5996\u9f99\u90ed\u6d2a\u9053\uff1a\u201c\u597d\u5389\u5bb3\u7684\u6bd2\u7bad\uff01\u201d\u7b49 \u4e86\u4e00\u9635\uff0c\u6bd2\u7bad\u5c04\u5b8c\uff0c\u90ed\u6d2a\u8fd8\u4e0d\u653e\u5fc3\uff0c\u53d6\u8fc7\u4e00\u9762\u76fe\u724c\uff0c\u4e00\u9762\u6325\u821e\uff0c\u4e00\u9762\u5bdf\u770b\uff0c\u5ffd\u7136\u5927\u58f0\u53eb\u9053\uff1a \u201c\u54fc\uff0c\u54b1\u4eec\u90fd\u7740\u4e86\u8fd9\u5c0f\u5b50\u7684\u9053\u513f\u4e86\uff01\u201d\u9000\u540e\u6570\u6b65\uff0c\u53cc\u624b\u5404\u6267\u4e00\u628a\u94c1\u9504\uff0c\u594b\u529b\u4e00\u63b7\uff0c\u628a\u90a3\u767d\u7389 \u7891\u649e\u5f00\uff0c\u6d1e\u4e2d\u4e00\u65e0\u6240\u6709\uff0c\u8fd9\u5341\u591a\u4e2a\u4eba\u7eb7\u7eb7\u5492\u9a82\uff0c\u80cc\u4e86\u53d7\u4f24\u7684\u540c\u4f34\uff0c\u970e\u5ffd\u4e4b\u95f4\u8d70\u5f97\u5e72\u5e72\u51c0\u51c0\u3002 \u3000\u3000\u94c1\u81c2\u91d1\u733f\u9053\uff1a\u201c\u54b1\u4eec\u77a7\u53bb\u3002\u201d\u4e91\u91cd\u5c0f\u5fc3\u7ffc\u7ffc\uff0c\u4e0a\u524d\u4e00\u770b\uff0c\u53ea\u89c1\u90a3\u5757\u65ad\u7891\u4e0a\u523b\u7740\u56db\u884c\u5927\u5b57 \u201c\u4eba\u4e3a\u8d22\u6b7b\uff0c\u9e1f\u4e3a\u98df\u4ea1\uff0c\u8bf8\u541b\u5230\u6b64\uff0c\u6bd2\u7bad\u5949\u5c1d\u3002\u5927\u5468\u7687\u5e1d\u5f20\u58eb\u8bda\u7acb\u7891\u3002\u201d\u4e91\u91cd\u609a\u7136\u4e00\u60ca\uff1a \u201c\u539f\u6765\u5f20\u58eb\u8bda\u6599\u5f97\u6709\u4eba\u6398\u4ed6\u9057\u5b9d\uff0c\u7adf\u7136\u9884\u4e0b\u5e03\u4e0b\u6bd2\u7bad\uff0c\u8fd9\u624b\u6bb5\u4e5f\u5fd2\u6bd2\u8fa3\u3002\u201d\u4f46\u90a3\u77f3\u6d1e\u751a\u6d45\uff0c \u4f20\u8bf4\u4e4b\u4e2d\u5f20\u58eb\u8bda\u7684\u85cf\u5b9d\u5982\u5c71\uff0c\u8fd9\u77f3\u6d1e\u600e\u5bb9\u5f97\u4e86\uff1f\u4e0d\u7981\u9762\u9762\u76f8\u89d1\u3002\u4e09\u82b1\u5251\u9053\uff1a\u201c\u6211\u770b\u5f20\u4e39\u67ab\u4e00 \u5b9a\u8fd8\u672a\u5c06\u5b9d\u85cf\u6398\u53bb\u3002\u201d\u4e91\u91cd\u9053\uff1a\u201c\u4f55\u4ee5\u89c1\u5f97\uff1f\u201d\u4e09\u82b1\u5251\u9053\uff1a\u201c\u4e00\u8005\u662f\u8fd9\u77f3\u6d1e\u4e0d\u50cf\u85cf\u5b9d\u4e4b\u6240\uff0c \u518d\u8005\u5f20\u4e39\u67ab\u5b64\u8eab\u4e00\u4eba\uff0c\u53c8\u5728\u90ed\u6d2a\u4e0e\u6d77\u9f99\u5e2e\u4f17\u4eba\u76d1\u89c6\u4e4b\u4e0b\uff0c\u4ed6\u518d\u6709\u672c\u4e8b\u4e5f\u4e0d\u80fd\u5c06\u5927\u6279\u5b9d\u85cf\u5e26\u51fa \u57ce\u53bb\u3002\u201d\u94c1\u81c2\u91d1\u733f\u9053\uff1a\u201c\u5e08\u5f1f\u6240\u89c1\u4e0d\u5dee\uff0c\u4f46\u82e5\u4ed6\u8fd8\u672a\u6398\u51fa\u5b9d\u85cf\uff0c\u5374\u53c8\u4e3a\u4f55\u79bb\u5f00\u4e86\u5feb\u6d3b\u6797\uff1f\u83ab \u975e\u5b9d\u85cf\u4e0d\u5728\u5feb\u6d3b\u6797\u4e2d\u4e48\uff1f\u201d\u4e91\u91cd\u5c0f\u5fc3\u518d\u77a7\uff0c\u5ffd\u89c1\u77f3\u7891\u65c1\u8fb9\u8fd8\u8d34\u7740\u4e00\u5f20\u7eb8\uff0c\u4e0a\u9762\u51e0\u884c\u5c0f\u5b57\u662f\uff1a \u201c\u4e00\u996e\u4e00\u5544\uff0c\u83ab\u975e\u5929\u5b9a\uff0c\u6731\u5bb6\u5929\u5b50\uff0c\u4f55\u5fc5\u8d39\u529b\u3002\u4e91\u91cd\u6211\u5144\uff0c\u8d70\u4e3a\u4e0a\u7b56\u3002\u5f1f\u5f20\u4e39\u67ab\u3002\u201d\u4e91\u91cd\u6c14 \u5f97\u54c7\u54c7\u5927\u53eb\uff0c\u94c1\u81c2\u91d1\u733f\u548c\u4e09\u82b1\u5251\u76f8\u5bf9\u82e6\u7b11\uff0c\u4e0d\u53d1\u4e00\u8a00\uff0c\u8fd9\u662f\u5df2\u662f\u9e21\u9e23\u4e94\u9f13\u4e86\u3002\u6b63\u662f\uff1a \u3000\u3000\u795e\u9f99\u89c1\u9996\u4e0d\u89c1\u5c3e\uff0c\u6c14\u715e\u4eac\u4e2d\u89c5\u5b9d\u4eba\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u840d\u8e2a\u4fa0\u5f71\u5f55\u300b\u2014\u2014\u7b2c\u5341\u4e03\u56de\u3000\u51b0\u96ea\u4ed9\u59ff\u957f\u6b4c\u6d88\u4fa0\u6c14\u3000\u98ce\u96f7\u624b\u7b14\u4e00\u753b\u5377\u6cb3\u5c71 \u6881\u7fbd\u751f\u300a\u840d\u8e2a\u4fa0\u5f71\u5f55\u300b \u7b2c\u5341\u4e03\u56de\u3000\u51b0\u96ea\u4ed9\u59ff\u957f\u6b4c\u6d88\u4fa0\u6c14\u3000\u98ce\u96f7\u624b\u7b14\u4e00\u753b\u5377\u6cb3\u5c71 \u3000\u3000\u8fd9\u65f6\u5f20\u4e39\u67ab\u5df2\u662f\u4e00\u53f6\u8f7b\u821f\u900d\u9065\u5728\u592a\u6e56\u4e4b\u4e0a\u3002\u4ed6\u53f3\u624b\u5212\u6d46\uff0c\u5de6\u624b\u62ff\u7740\u4e00\u628a\u91d1\u5149\u95ea\u95ea\u9501\u5319\uff0c \u653e\u76ee\u6e56\u5c71\uff0c\u9ad8\u58f0\u541f\u9053\uff1a\u201c\u592a\u6e56\u4e09\u4e07\u516d\u5343\u9877\uff0c\u96be\u6d17\u82f1\u96c4\u4eca\u53e4\u6101\uff01\u201d\u541f\u58f0\u63a0\u8fc7\u6e56\u9762\uff0c\u628a\u82a6\u82c7\u4e2d\u7684 \u6c99\u9e25\u767d\u9e6d\uff0c\u60ca\u5f97\u535c\u535c\u98de\u8d77\u3002 \u3000\u3000\u8fd9\u9501\u5319\u6b63\u662f\u4ed6\u5728\u5feb\u6d3b\u6797\u4e2d\u7684\u592a\u6e56\u77f3\u4e0b\u6240\u5f97\uff0c\u4ed6\u6309\u7740\u753b\u56fe\u6240\u793a\uff0c\u77e5\u9053\u5b9d\u85cf\u57cb\u5728\u5feb\u6d3b\u6797\u4e2d\uff0c \u56e0\u6b64\u4ed6\u624d\u5230\u5feb\u6d3b\u6797\u53bb\u4f5c\u90a3\u4e00\u573a\u8c6a\u8d4c\u3002\u4ed6\u65e9\u5c31\u4ece\u7956\u5148\u6240\u4f20\u77e5\u9053\u85cf\u5b9d\u4e4b\u5904\u57cb\u6709\u6bd2\u7bad\uff0c\u9884\u5148\u6709\u6240\u51c6 \u5907\u6545\u6b64\u6beb\u53d1\u65e0\u4f24\u3002\u8c01\u77e5\u79fb\u5f00\u4e86\u5c01\u6d1e\u7684\u7389\u7891\uff0c\u6d1e\u4e2d\u9664\u4e86\u8fd9\u679a\u91d1\u9501\u5319\u5916\uff0c\u5176\u4ed6\u5374\u662f\u4e00\u65e0\u6240\u6709\u3002\u4f46 \u5728\u91d1\u9501\u5319\u4e4b\u4e0a\uff0c\u5374\u53e6\u523b\u6709\u4e24\u884c\u5c0f\u5b57\u6587\u9053\uff1a\u201c\u592a\u6e56\u4e4b\u4e2d\uff0c\u897f\u6d1e\u5ead\u5c71\uff0c\u6709\u6b64\u9501\u5319\uff0c\u53ef\u63a2\u5b9d\u85cf\u3002\u201d \u539f\u6765\u5f20\u58eb\u8bda\u5f53\u5e74\u57cb\u5b9d\u4e4b\u524d\uff0c\u9009\u62e9\u5730\u70b9\uff0c\u715e\u8d39\u8e0c\u8e87\uff0c\u4ed6\u5728\u82cf\u5dde\u5efa\u4e1a\uff0c\u82e5\u7136\u57cb\u5728\u82cf\u5dde\uff0c\u6731\u65e0\u748b\u5fc5 \u80fd\u6599\u5230\u3002\u4f46\u82e5\u57cb\u5728\u5176\u4ed6\u5730\u65b9\uff0c\u8def\u9014\u642c\u8fd0\uff0c\u53c8\u6613\u6cc4\u9732\u98ce\u58f0\uff0c\u6700\u540e\u51b3\u5b9a\u57cb\u5728\u592a\u6e56\u7684\u897f\u6d1e\u5ead\u5c71\u3002\u81ea \u82cf\u5dde\u524d\u5f80\uff0c\u671d\u53d1\u5915\u81f3\uff0c\u5e76\u4e14\u4f5c\u4e86\u4e00\u756a\u5e03\u7f6e\u3002\u81f3\u4e8e\u753b\u56fe\u6240\u793a\u7684\u5feb\u6d3b\u6797\u85cf\u5b9d\u5730\u70b9\uff0c\u5374\u662f\u865a\u865a\u5b9e\u5b9e \u7684\u5e03\u7f6e\uff0c\u5176\u4e2d\u65e2\u6709\u63a2\u5b9d\u6240\u5fc5\u987b\u7684\u91d1\u9501\u5319\u53c8\u6709\u6781\u5389\u5bb3\u7684\u6bd2\u7bad\u3002\u4e3a\u4e86\u4fdd\u5b88\u79d8\u5bc6\uff0c\u5f53\u65f6\u53ea\u5c06\u6307\u793a \u201c\u85cf\u5b9d\u201d\u5730\u70b9\u7684\u82cf\u5dde\u753b\u56fe\u4ea4\u4e0e\u5e26\u201c\u5e7c\u4e3b\u201d\u51fa\u8d70\u7684\u5fc3\u8179\u6b66\u58eb\uff08\u77f3\u82f1\u7684\u7956\u5148\uff09\u5e76\u544a\u8bc9\u4ed6\u6d1e\u4e2d\u85cf\u6709 \u6bd2\u7bad\uff0c\u4e0e\u542f\u6d1e\u65f6\u9632\u5907\u6bd2\u7bad\u7684\u65b9\u6cd5\u3002\u81f3\u4e8e\u6d1e\u4e2d\u7684\u9501\u5319\u4e0e\u771f\u6b63\u85cf\u5b9d\u7684\u5730\u70b9\uff0c\u53ca\u5176\u4ed6\u79d8\u5bc6\u7684\u5e03\u7f6e\uff0c \u5373\u8fde\u90a3\u4e2a\u5fc3\u8179\u6b66\u58eb\u4e5f\u4e0d\u77e5\u9053\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u53d6\u5f97\u4e86\u91d1\u9501\u5319\u4e4b\u540e\uff0c\u4ecd\u5c06\u6d1e\u53e3\u5e03\u7f6e\u6062\u590d\u539f\u72b6\uff0c\u5728\u7ea2\u53d1\u5996\u9f99\u90ed\u6d2a\u7b49\u4eba\u6765\u5230\u4e4b\u524d\uff0c\u4fbf \u6e9c\u51fa\u4e86\u5feb\u6d3b\u6797\uff0c\u628a\u767d\u9a6c\u6258\u7ed9\u4e00\u4f4d\u65b0\u4ea4\u7684\u670b\u53cb\uff0c\u81ea\u5df1\u4fbf\u4e58\u90a3\u4f4d\u670b\u53cb\u9884\u5148\u51c6\u5907\u597d\u7684\u5c0f\u8239\uff0c\u5728\u82cf\u5dde \u4e07\u5e74\u6865\u4e0b\u653e\u821f\u5165\u6e56\uff0c\u6cdb\u821f\u534a\u591c\uff0c\u8fd9\u65f6\u5df2\u51fa\u4e86\u80e5\u53e3\u3002\u4f46\u89c1\u70df\u6ce2\u6d69\u6dfc\u98ce\u5e06\u9690\u9690\uff0c\u7fa4\u5cf0\u8d77\u4f0f\uff0c\u9690\u73b0 \u6e56\u4e2d\u3002\u5f20\u4e39\u67ab\u5374\u65e0\u5fc3\u8d4f\u73a9\uff0c\u5c06\u90a3\u679a\u91d1\u9501\u5319\u53cd\u590d\u7ec6\u770b\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u91d1\u9501\u5319\u7684\u6587\u5b57\u8bf4\uff0c\u6709\u6b64\u9501 \u5319\uff0c\u53ef\u63a2\u7389\u85cf\u3002\u897f\u6d1e\u5ead\u5c71\u6bd4\u5feb\u6d3b\u6797\u4f55\u6b62\u5927\u6570\u5341\u767e\u500d\uff0c\u5927\u6d77\u635e\u9488\uff0c\u5982\u4f55\u5bfb\u89c5\uff1f\u5b9d\u85cf\u4e5f\u8fd8\u7f62\u4e86\uff0c \u90a3\u5f20\u5730\u56fe\uff0c\u53ef\u662f\u6709\u5173\u4e2d\u534e\u56fd\u8fd0\uff01\u201d\u731b\u4e00\u62ac\u5934\uff0c\u53ea\u89c1\u4e07\u9877\u832b\u832b\uff0c\u6c34\u5929\u4e00\u8272\uff0c\u8c6a\u5174\u9044\u98de\uff0c\u987f\u6d88\u79ef \u95f7\uff0c\u6697\u81ea\u7b11\u9053\uff1a\u201c\u8239\u5230\u6ce2\u5fc3\u6d6a\u81ea\u5e73\uff0c\u5f53\u6b64\u4f73\u666f\uff0c\u5374\u4f5c\u675e\u5fe7\uff0c\u5c82\u975e\u611a\u7b28\u5462\u3002\u201d\u6536\u4e86\u9501\u5319\uff0c\u53cc\u624b \u5212\u6d46\uff0c\u6241\u821f\u4e00\u53f6\uff0c\u4e0d\u51cf\u98ce\u5e06\uff0c\u592a\u6e56\u4e03\u5341\u4e8c\u5cf0\u8fe4\u9026\u8fce\u6765\uff0c\u6709\u5982\u7fe1\u7fe0\u5c4f\u98ce\uff0c\u7247\u7247\u98de\u8fc7\uff0c\u7a7a\u7075\u7f25 \u7f08\uff0c\u70df\u5c9a\u6a2a\u9edb\uff0c\u6de1\u8fdc\u4f3c\u753b\u3002\u5f20\u4e39\u67ab\u60f3\u9053\uff1a\u201c\u91d1\u78a7\u8299\u84c9\u6620\u592a\u6e56\uff0c\u76f8\u4f20\u5947\u80dc\u7532\u4e1c\u5434\uff01\u8fd9\u4e24\u53e5\u548f\u592a \u6e56\u98ce\u5149\u7684\u8bd7\uff0c\u679c\u771f\u8bf4\u5f97\u4e0d\u9519\u3002\u201d \u3000\u3000\u821f\u884c\u4e0d\u4e45\uff0c\u897f\u6d1e\u5ead\u5c71\u7684\u4e3b\u5cf0\u7f25\u7f08\u5cf0\u5df2\u7136\u5728\u671b\uff0c\u897f\u6d1e\u5ead\u5c71\u867d\u8fdc\u4e0d\u53ca\u4e94\u5cb3\u540d\u5c71\u4e4b\u9ad8\u4e4b\u5927\uff0c\u4f46 \u60ac\u5d16\u7edd\u58c1\uff0c\u5947\u77f3\u5d99\u5ccb\uff0c\u5374\u4e5f\u4e88\u4eba\u4ee5\u5d14\u5d6c\u4e07\u4e08\u7684\u611f\u89c9\u3002\u5f20\u4e39\u67ab\u820d\u821f\u767b\u9646\uff0c\u53ea\u89c1\u5c71\u4e0b\u7530\u4ea9\u6210\u884c\uff0c \u5c71\u4e0a\u5c3d\u662f\u679c\u6811\uff0c\u6d53\u836b\u76f8\u63a5\uff0c\u82b1\u679c\u98d8\u9999\uff0c\u4e0d\u7981\u60f3\u9053\uff1a\u201c\u82e5\u5728\u6b64\u5c71\u7ed3\u5e90\u8bfb\u4e66\uff0c\u5012\u4e5f\u4e0d\u9519\u3002\u201d\u89c5\u8def \u767b\u5c71\uff0c\u6b63\u81ea\u5fc3\u65f7\u795e\u6021\uff0c\u5ffd\u89c1\u4e24\u4e2a\u7267\u7ae5\u9a91\u725b\u8fce\u9762\u800c\u6765\uff0c\u4e24\u53cc\u5c0f\u773c\u775b\u76ef\u7740\u5f20\u4e39\u67ab\uff0c\u4f3c\u4e4e\u5f88\u662f\u60ca \u8bb6\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u6211\u662f\u6765\u6e38\u5c71\u7684\uff0c\u8bf7\u95ee\u4e24\u4f4d\u5c0f\u54e5\uff0c\u4ece\u8fd9\u91cc\u767b\u5c71\u8def\u53ef\u597d\u8d70\uff1f\u201d\u4e24\u4e2a\u7267\u7ae5\u76f8\u5bf9\u770b \u4e86\u4e00\u773c\uff0c\u7c97\u58f0\u7c97\u6c14\u5730\u8bf4\u9053\uff1a\u201c\u4e0d\u77e5\u9053\u3002\u201d\u5f20\u4e39\u67ab\u5fc3\u9053\uff1a\u201c\u600e\u8fd9\u4e24\u4e2a\u7267\u7ae5\u5982\u6b64\u65e0\u793c\uff0c\u6bd4\u5728\u6fb9\u53f0 \u6751\u6240\u89c1\u7684\u5dee\u5f97\u8fdc\u4e86\u3002\u201d\u5ffd\u89c1\u90a3\u4e24\u4e2a\u7267\u7ae5\u5927\u58f0\u4e89\u5435\u8d77\u6765\uff0c\u540e\u9762\u7684\u7267\u7ae5\u8bf4\u524d\u9762\u7684\u7267\u7ae5\u6545\u610f\u8e0f\u5728\u6ce5 \u6f6d\u91cc\uff0c\u6e85\u6c61\u4e86\u4ed6\u7684\u8863\u88f3\uff0c\u524d\u9762\u7684\u53c8\u8bf4\u540e\u9762\u7684\u6545\u610f\u8ba9\u725b\u4e71\u8e22\u77f3\u5b50\uff0c\u77f3\u5b50\u6253\u7740\u4ed6\u7684\u8111\u888b\u3002\u5f20\u4e39\u67ab \u751a\u662f\u597d\u7b11\uff0c\u6b63\u60f3\u529d\u67b6\uff0c\u90a3\u4e24\u4e2a\u7267\u7ae5\u5374\u5ffd\u7136\u4ece\u5435\u67b6\u53d8\u4e3a\u6253\u67b6\uff0c\u9a71\u4f7f\u4e24\u6761\u725b\u4e92\u76f8\u8ffd\u9010\u89d2\u6597\uff0c\u5c71\u8def \u5d0e\u5c96\uff0c\u4e24\u6761\u86ee\u725b\u4e00\u4e0b\u5b50\u90fd\u5411\u5f20\u4e39\u67ab\u51b2\u6765\u3002\u5f20\u4e39\u67ab\u9aa4\u51fa\u4e0d\u610f\uff0c\u65e0\u5730\u95ea\u907f\uff0c\u201c\u554a\u5440\u201d\u4e00\u58f0\uff0c\u8feb\u5f97 \u594b\u8d77\u795e\u529b\uff0c\u53cc\u638c\u4e00\u4e2a\u201c\u91ce\u9a6c\u5206\u9b03\u201d\uff0c\u53ea\u542c\u5f97\u7830\u7830\u4e24\u58f0\uff0c\u4e24\u5339\u86ee\u725b\u7adf\u7ed9\u638c\u529b\u9707\u5f97\u5de6\u53f3\u5206\u5f00\uff0c\u4e24 \u8fb9\u8dcc\u5012\uff0c\u4e24\u4e2a\u7267\u7ae5\u5c16\u58f0\u5927\u53eb\u3002\u5f20\u4e39\u67ab\u672c\u6709\u9a88\u6307\u6d1e\u7a7f\u725b\u8179\u4e4b\u80fd\uff0c\u8fd9\u4e00\u638c\u5374\u53ea\u7528\u4e86\u4e09\u5206\u529b\u6c14\uff0c\u5fc3 \u9053\uff1a\u201c\u96be\u9053\u6211\u8fd9\u638c\u529b\u8fd8\u662f\u7528\u8fc7\u5927\uff0c\u7adf\u8dcc\u4f24\u4e86\u90a3\u4e2a\u5b69\u5b50\u4e0d\u6210\u3002\u201d\u751a\u662f\u5403\u60ca\u56de\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u4e24\u6761 \u725b\u56e2\u56e2\u4e71\u8dd1\uff0c\u4e24\u4e2a\u7267\u7ae5\u90fd\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u5fc3\u4e2d\u5947\u602a\uff0c\u6b63\u5f85\u56de\u53bb\u5bdf\u770b\uff0c\u5c71\u5761\u4e0a\u5ffd\u7136\u53c8\u94bb\u51fa\u4e24\u4e2a\u519c\u592b\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u5454\uff01\u9752\u5929 \u767d\u65e5\uff0c\u54ea\u91cc\u6765\u7684\u5f3a\u5f92\uff1f\u2026\u2026\u201d\u5f20\u4e39\u67ab\u6025\u9053\uff1a\u201c\u4e24\u4f4d\u5927\u54e5\u5bb9\u6211\u89c1\u544a\uff0c\u6211\u4e0d\u662f\u5f3a\u5f92\u2026\u2026\u201d\u8fd8\u672a\u8bf4 \u5b8c\u90a3\u4e24\u4e2a\u519c\u592b\u53c8\u559d\u9053\uff1a\u201c\u8fd8\u8bf4\u4e0d\u662f\u5f3a\u5f92\uff1f\u4e3a\u4f55\u5c06\u6211\u4eec\u7684\u725b\u6253\u4f24\uff0c\u5c06\u6211\u4eec\u7684\u5b69\u5b50\u63b3\u53bb\uff1f\u201d\u5f20\u4e39 \u67ab\u9053\uff1a\u201c\u8c01\u8bf4\u6211\u63b3\u4e86\u4f60\u4eec\u7684\u5b69\u5b50\uff1f\u4ed6\u3001\u4ed6\u4eec\u2026\u2026\u201d\u90a3\u4e24\u4e2a\u519c\u592b\u51b7\u7b11\u9053\uff1a\u201c\u4ed6\u3001\u4ed6\u4eec\u600e\u4e48\u5566\uff1f \u600e\u4e48\u4e0d\u89c1\u4e86\uff1f\u8981\u4e0d\u662f\u4f60\u628a\u4ed6\u4eec\u6536\u85cf\u8d77\u6765\uff0c\u5c31\u662f\u4f60\u628a\u4ed6\u4eec\u4ea4\u4e0e\u540c\u515a\uff0c\u62d0\u53bb\u5356\u4e86\u3002\u201d\u5f20\u4e39\u67ab\u7b11 \u9053\uff1a\u201c\u54ea\u6709\u6b64\u4e8b\uff1f\u4f60\u4eec\u5148\u53bb\u770b\u770b\u725b\u6709\u6ca1\u6709\u53d7\u4f24\uff0c\u7136\u540e\u627e\u90a3\u4e24\u4e2a\u5b69\u5b50\u5427\u3002\u201d\u90a3\u4e24\u4e2a\u519c\u592b\u7adf\u7136\u4e0d \u5bb9\u4ed6\u5206\u8bf4\u4e3e\u8d77\u9504\u5934\uff0c\u5de6\u8d77\u53f3\u843d\uff0c\u500f\u5730\u4fbf\u7167\u5934\u5288\u4e0b\u3002\u5f20\u4e39\u67ab\u5fae\u5403\u4e00\u60ca\uff0c\u8fd9\u4e24\u4e2a\u519c\u592b\u8eab\u624b\u7adf\u7136\u751a \u662f\u77eb\u6377\uff01\u770b\u90a3\u4e24\u67c4\u9504\u5934\u843d\u4e0b\uff0c\u5f20\u4e39\u67ab\u4e00\u4e2a\u201c\u76d8\u9f99\u7ed5\u6b65\u201d\uff0c\u8f7b\u8f7b\u4e00\u8f6c\u4e00\u95ea\uff0c\u53cc\u624b\u4e00\u62ff\uff0c\u5c06\u4e24\u67c4 \u9504\u5934\u4e00\u4e0b\u5b50\u90fd\u593a\u4e86\u8fc7\u6765\u3002\u90a3\u4e24\u4e2a\u519c\u592b\u5927\u53eb\u9053\uff1a\u201c\u6551\u547d\u5440\uff0c\u5f3a\u76d7\u6740\u4eba\u5566\uff01\u201d\u5f20\u4e39\u67ab\u53c8\u597d\u6c14\u53c8\u597d \u7b11\uff0c\u9053\uff1a\u201c\u6211\u82e5\u6709\u5fc3\u6740\u4f60\uff0c\u4f60\u65e9\u5c31\u6ca1\u547d\u4e86\uff0c\u4e71\u56b7\u4f5c\u751a\uff1f\u201d\u968f\u624b\u4e00\u6254\uff0c\u628a\u4e24\u67c4\u9504\u5934\u629b\u843d\u5c71\u3002\u8bf4 \u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\u5c71\u5773\u5904\u53c8\u5954\u51fa\u4e86\u4e03\u516b\u4e2a\u519c\u592b\uff0c\u5404\u5404\u9ad8\u4e3e\u9504\u5934\uff0c\u4e0d\u7531\u5206\u8bf4\uff0c\u4fbf\u4e00\u6d8c\u800c\u4e0a\uff0c\u524d\u540e\u5de6 \u53f3\uff0c\u4e03\u516b\u67c4\u9504\u5934\uff0c\u90fd\u5411\u5f20\u4e39\u67ab\u8981\u5bb3\u4e4b\u5904\u5288\u6765\u3002\u5f20\u4e39\u67ab\u597d\u4e0d\u70e6\u607c\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u65e0\u7aef\u7aef\u6253\u8fd9\u573a \u67b6\uff0c\u5b9e\u662f\u65e0\u8c13\u4e4b\u6781\u3002\u201d\u8eab\u5f62\u4e00\u8f6c\uff0c\u60f3\u4ece\u7f1d\u9699\u4e4b\u4e2d\u8d70\u51fa\uff0c\u54ea\u6599\u90a3\u4e03\u516b\u67c4\u9504\u5934\uff0c\u7adf\u4f3c\u7ec7\u6210\u4e86\u4e00\u9762 \u94c1\u7f51\uff0c\u5f20\u4e39\u67ab\u7684\u8eab\u6cd5\u5df2\u662f\u5feb\u5230\u6781\u70b9\uff0c\u4f46\u4e0d\u8bba\u8f6c\u5230\u54ea\u4e2a\u65b9\u4f4d\uff0c\u90fd\u6709\u9504\u5934\u8fce\u9762\u5288\u6765\u3002\u5f20\u4e39\u67ab\u5fc3\u4e2d \u4e00\u6014\uff1a\u8fd9\u660e\u660e\u662f\u9884\u5148\u6392\u7ec3\u597d\u7684\u9635\u6cd5\uff01\u5f53\u4e0b\u4e0d\u6562\u5927\u610f\uff0c\u63d0\u8d77\u7cbe\u795e\uff0c\u5728\u9504\u5934\u9635\u4e2d\u7a9c\u9ad8\u7eb5\u4f4e\uff0c\u6307\u4e1c \u6253\u897f\uff0c\u6307\u5357\u6253\u5317\uff0c\u970e\u5ffd\u4e4b\u95f4\uff0c\u628a\u90a3\u4e03\u516b\u4e2a\u519c\u592b\u8feb\u5f97\u9635\u811a\u677e\u52a8\uff0c\u8fde\u8fde\u540e\u9000\u3002\u53ef\u662f\u4ed6\u4eec\u9996\u5c3e\u76f8\u5e94 \u914d\u5408\u4f73\u5999\uff0c\u5f20\u4e39\u67ab\u9664\u975e\u628a\u4ed6\u4eec\u6253\u4f24\uff0c\u5426\u5219\u8981\u60f3\u593a\u53d6\u4ed6\u4eec\u624b\u4e2d\u7684\u9504\u5934\u5374\u4e5f\u5927\u975e\u6613\u4e8b\u3002 \u3000\u3000\u90a3\u4e03\u516b\u4e2a\u519c\u592b\u867d\u8d25\u4e0d\u4e71\uff0c\u5140\u662f\u82e6\u82e6\u7f20\u6597\uff0c\u4e0d\u80af\u9003\u8d70\u3002\u5f20\u4e39\u67ab\u4e00\u58f0\u957f\u5578\uff0c\u547c\u547c\u6570\u638c\uff0c\u628a\u4ed6 \u4eec\u903c\u51fa\u4e86\u79bb\u8eab\u4e00\u4e08\u4e4b\u5916\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u518d\u4e0d\u505c\u624b\uff0c\u6211\u53ef\u8981\u4e0d\u5ba2\u6c14\u5566\uff01\u201d\u90a3\u4e3a\u9996\u7684\u519c\u592b\u9053\uff1a \u201c\u4e0d\u5ba2\u6c14\u53c8\u5f85\u600e\u7684\uff0c\u72d7\u5f3a\u76d7\uff0c\u96be\u9053\u6211\u4eec\u6015\u4f60\u4e0d\u6210\uff1f\u201d\u5f20\u4e39\u67ab\u518d\u597d\u6db5\u517b\u4e5f\u7ed9\u4ed6\u4eec\u60f9\u5f97\u706b\u8d77\uff0c\u5fc3 \u9053\uff1a\u201c\u5f85\u6211\u62d4\u51fa\u5b9d\u5251\uff0c\u5c06\u4f60\u4eec\u8fd9\u51e0\u67c4\u9504\u5934\u4e00\u4e00\u524a\u65ad\uff0c\u770b\u4f60\u4eec\u6015\u662f\u4e0d\u6015\uff1f\u201d\u5de6\u638c\u62a4\u8eab\uff0c\u53f3\u624b\u6b63 \u5f85\u62d4\u5251\u5ffd\u542c\u5f97\u5c71\u4e0a\u6709\u4eba\u53eb\u9053\uff1a\u201c\u4f60\u4eec\u505a\u4ec0\u4e48\u6253\u67b6\uff1f\u201d\u5f20\u4e39\u67ab\u4ef0\u5934\u4e00\u770b\u53ea\u89c1\u90a3\u4eba\u4e09\u7efa\u957f\u987b\u989d\u5bbd \u9f3b\u5927\uff0c\u4f5c\u5112\u751f\u6253\u626e\uff0c\u5374\u53c8\u662f\u6b66\u4eba\u76f8\u8c8c\u3002\u90a3\u4e3a\u9996\u7684\u519c\u592b\u9053\uff1a\u201c\u8fd9\u5f3a\u76d7\u6253\u4f24\u4e86\u54b1\u4eec\u7684\u725b\uff0c\u53c8\u62d0\u8d70 \u54b1\u4eec\u7684\u5b69\u5b50\u3002\u201d\u90a3\u4eba\u9053\uff1a\u201c\u725b\u6ca1\u6709\u53d7\u4f24\u554a\u3002\u963f\u662d\uff0c\u963f\u6210\uff01\u201d\u5f20\u4e39\u67ab\u628a\u773c\u770b\u65f6\uff0c\u53ea\u89c1\u90a3\u4e24\u6761\u725b \u672c\u6765\u8fd8\u5728\u56e2\u56e2\u4e71\u8dd1\uff0c\u5374\u5ffd\u5730\u505c\u4f4f\u3002\u4e24\u4e2a\u7267\u7ae5\u54c8\u54c8\u5927\u7b11\uff0c\u4ece\u725b\u809a\u4e0b\u9762\u7ffb\u4e86\u4e0a\u6765\uff0c\u5411\u5f20\u4e39\u67ab\u626e\u4e2a \u9b3c\u8138\u3002\u5f20\u4e39\u67ab\u4e5f\u7ed9\u4ed6\u4eec\u5f15\u5f97\u7b11\u4e86\u8d77\u6765\uff0c\u5fc3\u9053\uff1a\u201c\u6211\u9053\u8fd9\u725b\u4e3a\u4ec0\u4e48\u56e2\u56e2\u4e71\u8dd1\u8dd1\u4e2a\u4e0d\u505c\uff0c\u539f\u6765\u662f \u8fd9\u4e24\u4e2a\u5c0f\u9b3c\u4f5c\u602a\u3002\u4ed6\u4eec\u9a91\u725b\u7684\u672c\u9886\u8981\u6bd4\u8499\u53e4\u4eba\u7684\u9a6c\u672f\u8fd8\u8981\u4fca\uff01\u201d\u7ee7\u800c\u53c8\u60f3\uff1a\u201c\u8fd9\u4e9b\u4eba\u6765\u5f97\u83ab \u540d\u5176\u5999\uff0c\u4e0d\u77e5\u662f\u6709\u610f\u8fd8\u662f\u65e0\u610f\uff0c\u5012\u4e0d\u53ef\u4e0d\u5c0f\u5fc3\u63d0\u9632\u3002\u201d \u3000\u3000\u5c71\u5761\u4e0a\u90a3\u8001\u4eba\u9053\uff1a\u201c\u5e84\u7a3c\u6c49\u7c97\u9c81\u65e0\u793c\uff0c\u4e00\u573a\u8bef\u4f1a\uff0c\u5ba2\u4eba\u4f11\u602a\u3002\u5484\uff0c\u4f60\u4eec\u8fd8\u4e0d\u5feb\u5411\u8fd9\u4f4d\u76f8 \u516c\u9053\u4e2a\u4e0d\u662f\uff0c\u4e0b\u7530\u53bb\u8015\u4f5c\u5427\u3002\u201d\u90a3\u4e03\u516b\u4e2a\u519c\u592b\u548c\u4e24\u4e2a\u7267\u7ae5\u5531\u4e86\u4e2a\u558f\uff0c\u7247\u523b\u4e4b\u95f4\u8d70\u5f97\u5e72\u5e72\u51c0 \u51c0\u3002 \u3000\u3000\u90a3\u8001\u4eba\u9053\uff1a\u201c\u76f8\u516c\u662f\u6765\u6e38\u5c71\u5417\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u6b63\u662f\u3002\u201d\u90a3\u8001\u4eba\u9053\uff1a\u201c\u4e03\u5341\u4e8c\u5cf0\u770b\u4e0d \u5c3d\uff0c\u832b\u832b\u4e07\u9877\u66f4\u6d88\u6101\u3002\u4f60\u6765\u6e38\u8d4f\u6e56\u5c71\uff0c\u6700\u5c11\u4e5f\u6709\u6570\u65e5\u52fe\u7559\u5427\uff1f\u201d\u5f20\u4e39\u67ab\u89c1\u90a3\u8001\u4eba\u5410\u5c5e\u4e0d\u51e1\uff0c \u8083\u7136\u95ee\u9053\uff1a\u201c\u4e0d\u6562\u8bf7\u95ee\u8001\u4e08\u59d3\u540d\u3002\u201d\u90a3\u8001\u4eba\u7b11\u9053\uff1a\u201c\u4eba\u751f\u767e\u5e74\uff0c\u5982\u767d\u9a79\u8fc7\u9699\uff0c\u4f55\u5fc5\u7559\u540d\uff0c\u4f60 \u53eb\u6211\u4e00\u58f0\u8001\u4e08\u6211\u79f0\u4f60\u4e00\u58f0\u76f8\u516c\uff0c\u5c82\u4e0d\u5e72\u8106\u5f97\u591a\uff1f\u4f55\u5fc5\u53bb\u8bb0\u90a3\u7f57\u7f57\u5506\u5506\u7684\u59d3\u540d\u3002\u201d\u5f20\u4e39\u67ab\u6027\u672c \u8c6a\u653e\uff0c\u8001\u4eba\u6240\u8a00\u6b63\u6295\u5176\u597d\u3002\u90a3\u8001\u4eba\u53c8\u9053\uff1a\u201c\u8001\u673d\u8717\u5c45\u5c31\u5728\u6b64\u5c71\u4e0a\uff0c\u6709\u4f4d\u670b\u53cb\u7ed9\u6211\u9898\u4e3a\u6d1e\u5ead\u5c71 \u5e84\uff0c\u76f8\u516c\u65e2\u6709\u6570\u65e5\u8fd9\u6e38\uff0c\u82e5\u4e0d\u5acc\u5f03\uff0c\u5c31\u8ba9\u8001\u914d\u7a0d\u5c3d\u5730\u4e3b\u4e4b\u8c0a\u5982\u4f55\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u8001\u4e08\u5982\u6b64 \u6d12\u8131\uff0c\u665a\u751f\u4e5f\u5c31\u539a\u989c\u53e8\u6270\u4e86\u3002\u53ea\u6015\u4e3a\u8001\u4e08\u589e\u4e86\u9ebb\u70e6\u3002\u201d\u90a3\u8001\u4eba\u53c8\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u73a9\u4f60\u7684\uff0c\u73a9 \u5f97\u5026\u4e86\u4fbf\u5230\u655d\u5e84\u6b47\u6b47\uff0c\u6709\u7f18\u5219\u805a\uff0c\u7f18\u5c3d\u5219\u53bb\uff0c\u90a3\u6709\u4ec0\u4e48\u9ebb\u70e6\u3002\u201d\u5f20\u4e39\u67ab\u6b63\u60f3\u4e00\u4eba\u5bfb\u5b9d\u89c5\u56fe\uff0c \u8001\u4eba\u6b64\u8a00\uff0c\u6b63\u5408\u5fc3\u610f\u3002\u8001\u4eba\u6307\u7740\u5c71\u8170\u7684\u4e00\u5ea7\u56ed\u6797\u9053\uff1a\u201c\u56ed\u4e2d\u867d\u65e0\u6240\u6709\uff0c\u852c\u83dc\u9c9c\u9c7c\u5374\u662f\u5e38\u5907\uff0c \u4f60\u8981\u6e38\u5c71\uff0c\u5c31\u8bf7\u81ea\u4fbf\uff0c\u665a\u4e0a\u56de\u6765\uff0c\u54b1\u4eec\u9c9c\u9c7c\u767d\u9152\uff0c\u518d\u4f5c\u503e\u8c08\u3002\u201d\u5f20\u4e39\u67ab\u62f1\u624b\u9053\u8c22\uff0c\u5fc3\u4e2d\u60f3 \u9053\uff1a\u201c\u8fd9\u8001\u4eba\u82e5\u975e\u6709\u9053\u7684\u9690\u8005\uff0c\u4ea6\u5fc5\u662f\u6e56\u6d77\u7684\u5f02\u4eba\u3002\u6211\u4eca\u6b21\u5230\u6765\uff0c\u7eb5\u7136\u627e\u4e0d\u5230\u5b9d\u85cf\u5730\u56fe\uff0c\u4e5f \u8981\u4ea4\u4ea4\u8fd9\u4e00\u4f4d\u98ce\u5c18\u524d\u8f88\u3002\u90a3\u4e00\u7fa4\u519c\u592b\uff0c\u770b\u6765\u4e5f\u5927\u6709\u6765\u5386\uff0c\u4e0d\u5e94\u5931\u4e4b\u4ea4\u81c2\u3002\u201d\u5f20\u4e39\u67ab\u601d\u6f6e\u8d77 \u4f0f\uff0c\u5728\u897f\u6d1e\u5ead\u5c71\u4e0a\u6e38\u4e86\u4e00\u4e2a\u4e0b\u5348\uff0c\u4e0d\u65f6\u53d1\u89c9\u6709\u91c7\u67f4\u6a35\u5b50\u6216\u6458\u91ce\u679c\u7684\u7adf\u5728\u5077\u5077\u76ef\u7740\u81ea\u5df1\uff0c\u5fc3\u4e2d \u66f4\u589e\u8be1\u5fc5\u4e4b\u611f\u3002\u5f20\u4e39\u67ab\u6e38\u4e86\u4e00\u4e2a\u4e0b\u5348\uff0c\u5c06\u5c71\u5f62\u5730\u52bf\uff0c\u8bb0\u5728\u5fc3\u4e2d\uff0c\u770b\u770b\u65e5\u843d\u897f\u5c71\uff0c\u4fbf\u4f9d\u8001\u4eba\u6240 \u7ea6\u56de\u8f6c\u5c71\u8170\uff0c\u53e9\u90a3\u201c\u6d1e\u5ead\u5c71\u5e84\u201d\u7684\u5e84\u95e8\u3002 \u3000\u3000\u5e84\u95e8\u7f13\u7f13\u6253\u5f00\uff0c\u5f20\u4e39\u67ab\u773c\u775b\u4e00\u4eae\uff0c\u53ea\u89c1\u9762\u524d\u7acb\u7740\u4e00\u4e2a\u5c11\u5973\u773c\u73e0\u6de1\u78a7\uff0c\u5bb9\u5149\u7115\u53d1\uff0c\u6709\u6c5f\u5357 \u5c11\u5973\u7684\u79c0\u6c14\uff0c\u4e5f\u6709\u5317\u5730\u80ed\u8102\u7684\u5065\u7f8e\u3002\u5f20\u4e39\u67ab\u6014\u4e86\u4e00\u6014\uff0c\u5fc3\u9053\uff1a\u201c\u4e91\u857e\u4e4b\u7f8e\u5982\u829d\u5170\u767e\u5408\uff0c\u6b64\u5973 \u4e4b\u7f8e\u5219\u5982\u73ab\u7470\u8299\u84c9\u3002\u82e5\u7136\u5e76\u7acb\uff0c\u60f3\u5fc5\u96be\u5206\u8f69\u8f7e\u3002\u201d\u6b63\u6b32\u5f00\u8a00\uff0c\u53ea\u89c1\u90a3\u5c11\u5973\u5ae3\u7136\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a \u201c\u8fd9\u4f4d\u76f8\u516c\u5c31\u662f\u6765\u6e38\u5c71\u7684\u90a3\u4f4d\u76f8\u516c\u5417\uff1f\u7239\u7239\u5df2\u5bf9\u6211\u8bf4\u4e86\uff0c\u8bf7\u4f60\u8fdb\u53bb\u3002\u201d \u3000\u3000\u5f20\u4e39\u67ab\u6270\u8896\u4e00\u8c22\uff0c\u968f\u90a3\u5c11\u5973\u8d70\u8fdb\u6d1e\u5ead\u5c71\u5e84\uff0c\u53ea\u89c1\u7d2b\u85e4\u76d8\u5f84\u7e41\u82b1\u7167\u773c\uff0c\u4ead\u69ad\u6c34\u77f3\uff0c\u53c2\u5dee\u9519 \u843d\uff0c\u63a9\u6620\u6709\u81f4\uff0c\u7adf\u7136\u662f\u7edd\u5999\u7684\u56ed\u6797\u5e03\u7f6e\uff0c\u867d\u4e0d\u53ca\u5feb\u6d3b\u6797\u4e4b\u5927\uff0c\u7cbe\u96c5\u5374\u8fc7\u4e4b\u3002\u90a3\u8001\u8005\u65e9\u5df2\u5728\u4ead \u4e2d\u7f6e\u6d12\u76f8\u8fce\uff0c\u89c1\u5f20\u4e39\u67ab\u56de\u6765\uff0c\u7b11\u9053\uff1a\u201c\u6e56\u5c71\u4e4b\u7f8e\u5982\u4f55\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u592a\u6e56\u5947\u80dc\u7532\u4e1c\u5434\uff0c\u6c34 \u8272\u5c71\u5149\u8d5b\u753b\u56fe\u3002\u53e4\u4eba\u65e9\u6709\u5b9a\u8bc4\uff0c\u665a\u751f\u9664\u4e86\u62dc\u5012\u6e56\u5c71\u4e4b\u5916\uff0c\u5c82\u6562\u7f6e\u5599\u3002\u201d\u90a3\u8001\u4eba\u7b11\u9053\uff1a\u201c\u53ef\u60dc \u6709\u4e9b\u4eba\u5bf9\u6b64\u6e56\u5c71\uff0c\u5c1a\u672a\u5fd8\u60c5\u540d\u5229\uff0c\u751a\u81f3\u8111\u4e2d\u5c3d\u662f\u94dc\u81ed\uff0c\u5c82\u4e0d\u53ef\u7b11\u53ef\u601c\uff1f\u201d\u5f20\u4e39\u67ab\u95fb\u8a00\u4e00\u6014\uff0c \u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u83ab\u975e\u4ed6\u77e5\u9053\u6211\u662f\u6765\u5bfb\u89c5\u5b9d\u85cf\u7684\u5417\uff1f\u201d\u7ee7\u800c\u81ea\u7b11\u591a\u7591\uff0c\u5fc3\u9053\uff1a\u201c\u6211\u5148\u7956\u85cf\u5b9d\u57cb \u56fe\uff0c\u6b64\u4e43\u7edd\u79d8\u4e4b\u4e8b\uff0c\u5373\u6211\u4e5f\u662f\u5f97\u4e86\u91d1\u9501\u5319\u4e4b\u540e\uff0c\u624d\u77e5\u57cb\u5728\u6b64\u5c71\u3002\u8fd9\u8001\u4eba\u5982\u4f55\u80fd\u77e5\uff1f\u9002\u624d\u8fd9 \u8bdd\uff0c\u60f3\u5fc5\u662f\u4ed6\u6cdb\u6cdb\u4e4b\u8bba\u3002\u201d \u3000\u3000\u4e24\u4eba\u996e\u9152\u503e\u8c08\uff0c\u90a3\u8001\u4eba\u4e0e\u4ed6\u8c08\u5c71\u8272\u6e56\u5149\uff0c\u8bcd\u7ae0\u5b57\u753b\uff0c\u751a\u4e3a\u76f8\u5f97\u3002\u53ea\u662f\u5927\u5bb6\u90fd\u907f\u514d\u95ee\u5bf9\u65b9 \u8eab\u4e16\u3002\u90a3\u8001\u4eba\u996e\u4e86\u51e0\u676f\uff0c\u9189\u6001\u6e10\u9732\uff0c\u6253\u4e86\u4e2a\u5475\u6b20\u8bf4\u9053\uff1a\u201c\u6211\u9189\u6b32\u7720\uff0c\u76f8\u516c\u81ea\u4fbf\u3002\u592a\u6e56\u591c\u8272\u4f73 \u7edd\u6b64\u5730\u95e8\u867d\u8bbe\u800c\u5e38\u5f00\uff0c\u76f8\u516c\u82e5\u6709\u5174\u81f4\u767b\u5cf0\u8d4f\u6708\uff0c\u5524\u5c0f\u5973\u966a\u4f34\u6216\u72ec\u81ea\u524d\u5f80\u5747\u53ef\uff0c\u56de\u6765\u4e0d\u5fc5\u6572\u95e8 \u53ea\u8981\u4e00\u63a8\u4fbf\u5f00\u4e86\u3002\u201d\u5f20\u4e39\u67ab\u5fc3\u9053\uff1a\u201c\u6b64\u8001\u7684\u662f\u53ef\u4eba\uff0c\u597d\u50cf\u77e5\u9053\u6211\u7684\u5fc3\u610f\u4e00\u6837\u3002\u592a\u6e56\u7fa4\u5cf0\u7f25 \u7f08\uff0c\u6d6e\u6c89\u78a7\u6ce2\uff0c\u5728\u6708\u8272\u4e4b\u4e0b\uff0c\u5b9a\u5fc5\u66f4\u7f8e\u3002\u201d \u3000\u3000\u90a3\u8001\u8005\u53eb\u5c11\u5973\u5e26\u5f20\u4e39\u67ab\u6b47\u606f\uff0c\u5c11\u5973\u76c8\u76c8\u4e00\u7b11\uff0c\u95ee\u9053\uff1a\u201c\u76f8\u516c\u662f\u7b2c\u4e00\u6b21\u6765\u6e38\u592a\u6e56\u7684\u5417\uff1f\u201d \u5f20\u4e39\u67ab\u9053\uff1a\u201c\u6b63\u662f\u3002\u201d\u90a3\u5c11\u5973\u7b11\u9053\uff1a\u201c\u76f8\u516c\u8bf4\u662f\u6765\u81ea\u5317\u65b9\uff0c\u6211\u770b\u5374\u4f3c\u6c5f\u5357\u4eba\u7269\u3002\u5440\uff0c\u597d\u50cf\u54b1 \u4eec\u8fd8\u5728\u54ea\u513f\u89c1\u8fc7\u4e00\u822c\uff0c\u76f8\u8c8c\u597d\u719f\u3002\u201d\u5f20\u4e39\u67ab\u7b11\u9053\uff1a\u201c\u59d1\u5a18\u8bf4\u7b11\u8bdd\u4e86\uff0c\u6211\u5012\u613f\u610f\u65e9\u8ba4\u8bc6\u59d1\u5a18\uff0c \u53ea\u53ef\u60dc\u4eca\u65e5\u624d\u6709\u6b64\u673a\u7f18\uff0c\u6765\u8d4f\u6e56\u5c71\u80dc\u666f\u3002\u201d \u3000\u3000\u90a3\u5c11\u5973\u4e00\u7b11\u4e0d\u8a00\uff0c\u8d70\u5230\u4e00\u5904\uff0c\u8bf4\u9053\uff1a\u201c\u76f8\u516c\u5c31\u5728\u6b64\u5904\u6b47\u6b47\u5427\uff0c\u5c71\u5c45\u7b80\u6162\uff0c\u8bf7\u52ff\u89c1\u8d23\u3002\u201d \u5f20\u4e39\u67ab\u4e00\u770b\uff0c\u53ea\u89c1\u4e00\u6240\u7cbe\u96c5\u7684\u623f\u5b50\u5efa\u5728\u8377\u5858\u4e4b\u4e2d\uff0c\u83b2\u82b1\u6b63\u5728\u76db\u5f00\uff0c\u7fe0\u76d6\u7ea2\u88f3\uff0c\u4e00\u6c34\u7686\u9999\u3002\u5f20 \u4e39\u67ab\u7b11\u9053\uff1a\u201c\u6b64\u5730\u5982\u540c\u4ed9\u5883\uff0c\u7687\u5e1d\u4e5f\u6ca1\u798f\u4f4f\uff0c\u600e\u8bf4\u7b80\u6162\uff1f\u201d\u90a3\u5c11\u5973\u4e00\u7b11\u8d70\u5f00\uff0c\u5fae\u98ce\u9001\u58f0\u53ea\u542c \u5f97\u90a3\u94f6\u94c3\u4f3c\u7684\u58f0\u97f3\u4f3c\u5632\u4f3c\u8bbd\uff1a\u201c\u771f\u662f\u4ee5\u8c8c\u53d6\u4eba\uff0c\u6362\u4e4b\u5b50\u7fbd\u3002\u5bf9\u6b64\u6e56\u5c71\uff0c\u5374\u63d0\u4fd7\u7269\uff0c\u7687\u5e1d\u503c\u591a \u5c11\u94b1\u4e00\u65a4\uff1f\u201d \u3000\u3000\u5f20\u4e39\u67ab\u5fc3\u9053\uff1a\u201c\u6709\u5176\u7236\u5fc5\u6709\u5176\u5973\uff0c\u6b64\u5973\u6027\u60c5\u5012\u4e5f\u6d12\u8131\u5f97\u5f88\u5462\u3002\u201d\u9661\u7136\u60f3\u8d77\u4e91\u857e\uff0c\u628a\u90a3\u5c11 \u5973\u7684\u5f71\u5b50\u538b\u4e86\u4e0b\u53bb\u3002\u72ec\u5bf9\u8377\u82b1\uff0c\u60f3\u8d77\u4eca\u65e5\u906d\u9047\u4e4b\u5947\uff0c\u4e0e\u5bfb\u5b9d\u7684\u6e3a\u832b\uff0c\u4e0d\u89c9\u5fc3\u601d\u6447\u6447\u6beb\u65e0\u7761 \u610f\u3002 \u3000\u3000\u731b\u4e00\u62ac\u5934\uff0c\u53ea\u89c1\u758f\u5f71\u6a2a\u659c\uff0c\u6de1\u9999\u5982\u9152\uff0c\u6708\u5149\u5165\u6237\uff0c\u6d9b\u58f0\u9001\u6797\uff0c\u5f20\u4e39\u67ab\u62ab\u8863\u51fa\u5c4b\uff0c\u63a8\u5f00\u540e \u95e8\uff0c\u767b\u5c71\u53bb\u770b\u592a\u6e56\u591c\u666f\u3002\u897f\u6d1e\u5ead\u5c71\u77d7\u7acb\u6e56\u5fc3\uff0c\u767b\u7f25\u7f08\u5cf0\uff0c\u7eb5\u9274\u5bbd\u5e7f\u516b\u767e\u91cc\u7684\u592a\u6e56\uff0c\u771f\u662f\u4e09\u4e07 \u516d\u5343\u9877\u7684\u6ce2\u5149\u6d9b\u5f71\uff0c\u5c3d\u6536\u773c\u5e95\uff0c\u5728\u6708\u8272\u4e4b\u4e0b\uff0c\u6e56\u5149\u6620\u7167\uff0c\u6bd4\u65e5\u95f4\u6240\u89c1\uff0c\u66f4\u662f\u7470\u4e3d\u5947\u8be1\uff0c\u975e\u7b14 \u58a8\u6240\u80fd\u5f62\u5bb9\u3002\u5f20\u4e39\u67ab\u5fc3\u9189\u795e\u9a70\uff0c\u60a0\u7136\u5982\u68a6\uff0c\u5ffd\u542c\u5f97\u6709\u5c11\u5973\u6b4c\u9053\uff1a\u201c\u7ea4\u4e91\u56db\u5377\u5929\u65e0\u6cb3\uff0c\u6e05\u98ce\u5439 \u7a7a\u6708\u8212\u6ce2\u3002\u6c99\u5e73\u6c34\u606f\u58f0\u5f71\u7edd\uff0c\u4e00\u676f\u76f8\u5c5e\u541b\u5f53\u6b4c\u3002\u6e05\u6d41\u8db3\u4ee5\u6da4\u5c18\u57a2\uff0c\u4eba\u751f\u4f55\u5fc5\u53f9\u574e\u5777\uff1f\u91d1\u94f6\u73e0 \u5b9d\u963f\u5835\u7269\uff0c\u4f1a\u5f53\u5c3d\u4ed8\u4e8e\u78a7\u6ce2\uff0c\u529d\u541b\u6709\u9152\u5f53\u81ea\u9189\uff0c\u6709\u9152\u4e0d\u996e\u5948\u6708\u4f55\uff1f\u201d\u6b4c\u58f0\u6447\u66f3\uff0c\u968f\u98ce\u98d8\u5165\u592a \u6e56\u3002\u5f20\u4e39\u67ab\u542c\u5f97\u5446\u4e86\uff0c\u5fc3\u9053\uff1a\u201c\u6b64\u5973\u96c6\u5510\u4eba\u8bd7\u53e5\uff0c\u53d1\u4e3a\u957f\u6b4c\uff0c\u83ab\u975e\u662f\u529d\u6211\u4e0d\u8981\u8d39\u795e\u53bb\u89c5\u90a3\u5b9d \u85cf\u4e48\uff1f\u5440\uff0c\u5979\u54ea\u91cc\u77e5\u9053\u6211\u7684\u5fc3\u4e8b\u3002\u6211\u5c82\u662f\u60f3\u72ec\u5360\u73e0\u5b9d\uff0c\u8d2a\u604b\u94dc\u81ed\u4e4b\u4eba\uff01\u201d\u5fcd\u4e0d\u7740\u53d1\u58f0\u548c\u9053\uff1a \u201c\u541b\u6b4c\u4e14\u4f11\u542c\u6211\u6b4c\uff0c\u6b64\u5cf0\u7a81\u5140\u6491\u5929\u6cb3\uff0c\u4e16\u95f4\u4ea6\u6709\u5947\u7537\u5b50\uff0c\u9876\u5929\u7acb\u5730\u5251\u6a2a\u78e8\uff01\u738b\u4faf\u73e0\u5b9d\u7686\u7caa \u571f\uff0c\u4f46\u6b32\u4e00\u753b\u5377\u5c71\u6cb3\uff01\u201d \u3000\u3000\u5f20\u4e39\u67ab\u6b4c\u58f0\u4e00\u505c\uff0c\u5ffd\u89c1\u90a3\u5c11\u5973\u5728\u5d99\u5ccb\u77f3\u7b0b\u4e1b\u4e2d\u5189\u5189\u51fa\u73b0\uff0c\u7b11\u9765\u5982\u82b1\uff0c\u8f7b\u8f7b\u5411\u4ed6\u62db\u624b\u3002\u5f20 \u4e39\u67ab\u4e0d\u7531\u81ea\u4e3b\u5730\u5411\u5979\u8d70\u53bb\uff0c\u53ea\u542c\u5f97\u90a3\u5c11\u5973\u9053\uff1a\u201c\u4f60\u5f53\u771f\u8981\u56fa\u6267\u5df1\u610f\u4e48\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u6211\u4e0d \u77e5\u59d1\u5a18\u610f\u4f55\u6240\u6307\uff1f\u4f46\u5927\u4e08\u592b\u505a\u4e8b\uff0c\u5c82\u80fd\u8f7b\u6613\u66f4\u6539\u3002\u201d\u90a3\u5c11\u5973\u9762\u8272\u5fae\u53d8\uff0c\u5ffd\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u60f3\u5230 \u6b64\u5c71\u76d7\u5b9d\uff0c\u90a3\u53ef\u4f11\u60f3\uff01\u201d\u5ffd\u5730\u9752\u5149\u75be\u95ea\uff0c\u90a3\u5c11\u5973\u500f\u5730\u62d4\u51fa\u4e00\u67c4\u77ed\u5251\uff0c\u5411\u5f20\u4e39\u67ab\u5f53\u80f8\u4fbf\u523a\u3002\u5f20 \u4e39\u67ab\u60ca\u9a87\u4e4b\u6781\uff0c\u98d8\u8eab\u6025\u95ea\uff0c\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4f60\u662f\u4f55\u4eba\uff1f\u201d\u90a3\u5c11\u5973\u8eab\u624b\u5feb\u6781\uff0c\u7728\u773c\u4e4b\u95f4\uff0c\u8fde\u523a\u6570 \u5251\uff0c\u5f20\u4e39\u67ab\u4e1c\u8eb2\u897f\u95ea\uff0c\u7ed9\u5979\u903c\u5165\u4e86\u4e71\u77f3\u4e1b\u4e2d\uff0c\u7a81\u7136\u73b0\u51fa\u6570\u4eba\uff0c\u90a3\u62db\u5f85\u81ea\u5df1\u7684\u6d1e\u5ead\u5e84\u4e3b\u624b\u6301\u4e00 \u67c4\u6e14\u53c9\u7adf\u8df3\u5728\u4e00\u5806\u77f3\u4e0a\uff0c\u5411\u81ea\u5df1\u5206\u5fc3\u75be\u523a\uff0c\u542c\u90a3\u6e14\u53c9\u6296\u7684\u55e1\u55e1\u4e4b\u58f0\uff0c\u7adf\u7136\u662f\u4e00\u4f4d\u6709\u4e0a\u4e58\u529f\u592b \u7684\u6b66\u6797\u9ad8\u624b\u3002\u5f20\u4e39\u67ab\u53eb\u9053\uff1a\u201c\u8001\u4e08\u4f55\u6545\u76f8\u903c\uff1f\u201d\u90a3\u8001\u4eba\u9053\uff1a\u201c\u54fc\uff0c\u4f60\u81ea\u5df1\u8fd8\u4e0d\u660e\u767d\uff1f\u770b\u4f60\u76f8 \u8c8c\uff0c\u6211\u672c\u4ee5\u4e3a\u4f60\u4e5f\u662f\u4e00\u4f4d\u96c5\u4eba\uff0c\u539f\u6765\u4f60\u5374\u662f\u540d\u5229\u718f\u5fc3\u7684\u6076\u6c49\uff01\u201d\u53e6\u5916\u90a3\u51e0\u4eba\u6b63\u662f\u65e5\u95f4\u6240\u89c1\u7684 \u519c\u592b\uff0c\u9f50\u58f0\u559d\u9053\uff1a\u201c\u6211\u65e9\u5c31\u77a7\u51fa\u4f60\u4e0d\u662f\u597d\u4eba\uff0c\u770b\u5200\u3001\u770b\u5251\u3001\u770b\u67aa\u3001\u770b\u621f\uff01\u201d\u90a3\u51e0\u4e2a\u519c\u592b\u8fd9\u65f6 \u624b\u4e0a\u62ff\u7684\u5df2\u4e0d\u662f\u9504\u5934\u800c\u662f\u5200\u67aa\u5251\u621f\u4e86\u3002\u5f20\u4e39\u67ab\u53c8\u60ca\u53c8\u9a87\uff0c\u6b32\u5f85\u5206\u8bf4\uff0c\u4f46\u5bf9\u65b9\u5175\u5668\u9f50\u4e0a\uff0c\u5c24\u5176 \u662f\u90a3\u8001\u8005\u7684\u6e14\u53c9\u548c\u90a3\u5c11\u5973\u7684\u77ed\u5251\u66f4\u662f\u8fc5\u903e\u98d8\u98ce\uff0c\u54ea\u5bb9\u5f97\u4ed6\u5206\u795e\u8fa9\u767d\u3002\u5f20\u4e39\u67ab\u7ed9\u4ed6\u4eec\u5728\u4e71\u77f3\u5806 \u4e2d\u56f4\u653b\uff0c\u9669\u8c61\u73af\u751f\uff0c\u53ea\u5f97\u62d4\u51fa\u767d\u4e91\u5b9d\u5251\uff0c\u6a2a\u62ab\u76f4\u523a\uff0c\u6709\u4e24\u4e2a\u519c\u592b\u7684\u5175\u5668\u7ed9\u4ed6\u524a\u53bb\u4e00\u622a\uff0c\u6025\u6025 \u540e\u9000\u3002\u5f20\u4e39\u67ab\u53eb\u9053\uff1a\u201c\u4f4f\u624b\uff01\u201d\u90a3\u8001\u8005\u7b11\u9053\uff1a\u201c\u9677\u5165\u6b64\u9635\uff0c\u6709\u5b9d\u4e5f\u6ca1\u7528\u4e86\uff01\u201d\u6e14\u53c9\u4e00\u6296\uff0c\u53c8 \u4e0a\u524d\u75be\u653b\uff0c\u5f20\u4e39\u67ab\u5bf9\u4ed6\u5c1a\u662f\u5fc3\u5b58\u656c\u610f\uff0c\u4e0d\u6b32\u524a\u4ed6\u7684\u5175\u5668\uff0c\u4e13\u627e\u5176\u4ed6\u7684\u4eba\uff0c\u5374\u4e0d\u6599\u90a3\u4e9b\u4eba\u4e00\u8fdb \u4e00\u9000\uff0c\u6765\u53bb\u5982\u6f6e\uff0c\u4e00\u89c1\u5251\u5230\uff0c\u8eab\u5f62\u5ffd\u5730\u4fbf\u6ca1\u5165\u4e71\u77f3\u5806\u4e2d\uff0c\u53e4\u602a\u4e4b\u6781\uff0c\u5f20\u4e39\u67ab\u51fa\u624b\u867d\u5feb\uff0c\u5374\u7adf \u7136\u518d\u4e5f\u78b0\u4e0d\u5230\u4ed6\u4eec\u7684\u5175\u5668\u4e86\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u7ec6\u770b\u65f6\uff0c\u53ea\u89c1\u8fde\u90a3\u8001\u8005\u7236\u5973\u5728\u5185\uff0c\u654c\u624b\u4e00\u5171\u516b\u4eba\uff0c\u5360\u7740\u516b\u4f4d\u65b9\u4f4d\uff0c\u5728\u77f3\u5806\u4e2d\u5ffd\u9690 \u5ffd\u73b0\uff0c\u4e1c\u780d\u4e00\u5200\uff0c\u897f\u523a\u4e00\u5251\uff0c\u9632\u4e0d\u80dc\u9632\u3002\u5f20\u4e39\u67ab\u5fc3\u60f3\uff1a\u201c\u6211\u53ea\u8ba4\u5b9a\u4e00\u4eba\u8ffd\u53bb\uff0c\u770b\u4f60\u5982\u4f55\u53ef\u8eb2 \u907f\u5f97\u4e86\u3002\u201d\u5148\u633a\u5251\u8ffd\u4e00\u519c\u592b\uff0c\u770b\u90a3\u519c\u592b\u8eab\u624b\u5e73\u5e38\uff0c\u8c01\u77e5\u4ed6\u5728\u4e71\u77f3\u5806\u4e2d\u5de6\u515c\u53f3\u7ed5\uff0c\u5f20\u4e39\u67ab\u8ddf\u4ed6 \u8f6c\u4e86\u4e24\u8f6c\uff0c\u5ffd\u7136\u4e0d\u89c1\u4e86\u4ed6\u7684\u8e2a\u8ff9\uff0c\u800c\u90a3\u5c11\u5973\u7684\u77ed\u5251\u548c\u53e6\u4e00\u4e2a\u519c\u592b\u7684\u957f\u67aa\u5374\u5ffd\u5730\u4ece\u5de6\u53f3\u88ad\u6765\uff0c \u8ffd\u90a3\u5c11\u5973\u65f6\uff0c\u4e5f\u662f\u8f6c\u773c\u95f4\u4fbf\u6ca1\u4e86\u8e2a\uff0c\u800c\u90a3\u8001\u8005\u5374\u53c8\u7a81\u5730\u5f53\u5934\u51fa\u73b0\uff0c\u6e14\u53c9\u95ea\u95ea\uff0c\u5411\u81ea\u5df1\u62a2\u653b\u3002 \u5f20\u4e39\u67ab\u5fc3\u9053\uff1a\u201c\u4ed6\u4eec\u5982\u6b64\u6218\u6cd5\uff0c\u5982\u4f55\u662f\u597d\uff1f\u201d\u9635\u4e2d\u516b\u4eba\uff0c\u9664\u4e86\u8001\u8005\u4e0e\u5c11\u5973\u4e4b\u5916\uff0c\u5176\u4ed6\u516d\u4eba\u5728 \u6c5f\u6e56\u4e4b\u4e0a\u6b66\u529f\u6216\u7b97\u4e0d\u9519\uff0c\u4f46\u5728\u5f20\u4e39\u67ab\u773c\u4e2d\u5374\u5c5e\u5bfb\u5e38\u3002\u4f46\u8fd9\u9635\u6cd5\u602a\u6781\uff0c\u5f20\u4e39\u67ab\u867d\u77e5\u9053\u53ea\u8981\u51fb\u7834 \u4e00\u73af\uff0c\u4fbf\u53ef\u7a81\u56f4\uff0c\u4f46\u60f3\u5c3d\u529e\u6cd5\uff0c\u5374\u7adf\u662f\u8d8a\u9677\u8d8a\u6df1\uff0c\u65e0\u6cd5\u53ef\u65bd\u3002\u518d\u8fc7\u7247\u523b\uff0c\u9635\u5f62\u8d8a\u903c\u8d8a\u7d27\uff0c\u5f20 \u4e39\u67ab\u5728\u4e71\u77f3\u5806\u4e2d\u4e1c\u7a9c\u897f\u95ea\uff0c\u8fde\u9632\u536b\u4ea6\u89c1\u8270\u96be\uff0c\u66f4\u4e0d\u7528\u8bf4\u8fd8\u624b\u653b\u51fb\u4e86\u3002\u5e78\u4e8f\u4ed6\u6709\u524a\u94c1\u5982\u6ce5\u7684\u5b9d \u5251\uff0c\u4f17\u4eba\u8fd8\u4e0d\u6562\u592a\u8fc7\u903c\u8fd1\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u731b\u7136\u4e00\u9192\uff1a\u8fd9\u9635\u6cd5\u5c82\u4e0d\u662f\u8bf8\u845b\u6b66\u4faf\u4f20\u4e0b\u7684\u516b\u9635\u56fe\uff1f\u7559\u5fc3\u7ec6\u770b\uff0c\u53ea\u89c1\u90a3\u516b\u4eba\u679c\u7136\u662f \u4f9d\u7740\u90a3\u4e71\u77f3\u6240\u5e03\u6210\u7684\u95e8\u6237\uff0c\u5206\u6210\u4f11\u3001\u751f\u3001\u4f24\u3001\u675c\u3001\u6b7b\u3001\u666f\u3001\u60ca\u3001\u5f00\u516b\u95e8\uff0c\u5404\u5b88\u4e00\u95e8\uff0c\u800c\u81ea\u5df1 \u8fd9\u65f6\u5374\u6b63\u88ab\u5f15\u5165\u6b7b\u95e8\u3002\u5f20\u4e39\u67ab\u5fc3\u4e2d\u4e00\u6014\uff1a\u61c2\u5f97\u5e03\u8fd9\u516b\u9635\u56fe\u7684\uff0c\u53e4\u4eca\u540d\u5c06\u4e5f\u6ca1\u51e0\u4eba\uff0c\u5374\u4e0d\u6599\u5728 \u8fd9\u91cc\u89c1\u5230\uff01\u518d\u7559\u5fc3\u770b\u65f6\uff0c\u53ea\u89c1\u628a\u5b88\u751f\u95e8\u7684\uff0c\u6b63\u662f\u90a3\u4f7f\u77ed\u5251\u7684\u5c11\u5973\u3002\u5f20\u4e39\u67ab\u8bc6\u7834\u9635\u6cd5\uff0c\u66f4\u4e0d\u8fdf \u7591\uff0c\u98de\u8eab\u4e00\u8d77\uff0c\u81ea\u6b7b\u95e8\u8df3\u5165\u60ca\u95e8\uff0c\u81ea\u60ca\u95e8\u8df3\u5165\u4f24\u95e8\uff0c\u518d\u8f6c\u5165\u675c\u95e8\uff0c\u7ed5\u8fc7\u4f11\u95e8\uff0c\u76f4\u95ef\u751f\u95e8\uff0c\u516b \u9635\u56fe\u767b\u65f6\u5927\u4e71\uff0c\u90a3\u5c11\u5973\u9762\u6709\u60ca\u8272\uff0c\u8fde\u8fde\u8eb2\u95ea\uff0c\u5f20\u4e39\u67ab\u5fc3\u4e2d\u867d\u6709\u4e0d\u5fcd\uff0c\u4f46\u4e3a\u7740\u8981\u95ef\u51fa\u9635\u56fe\uff0c\u957f \u5251\u95ea\u95ea\u4e0d\u79bb\u90a3\u5c11\u5973\u80cc\u5fc3\uff0c\u903c\u90a3\u5c11\u5973\u5f15\u81ea\u5df1\u51fa\u53bb\u3002\u773c\u770b\u5c31\u8981\u95ef\u51fa\u751f\u95e8\uff0c\u90a3\u5c11\u5973\u5ffd\u7136\u4e00\u58f0\u5c16\u53eb\uff0c \u4f3c\u662f\u9a87\u6781\u800c\u547c\uff0c\u5f20\u4e39\u67ab\u4e00\u6115\uff0c\u53ea\u9053\u662f\u81ea\u5df1\u4e0d\u5c0f\u5fc3\uff0c\u5251\u5c16\u78b0\u7740\u4e86\u90a3\u5c11\u5973\u7684\u76ae\u8089\uff0c\u5c31\u8fd9\u4e48\u7684\u4e00 \u505c\uff0c\u5ffd\u611f\u5730\u8f6c\u5929\u65cb\uff0c\u201c\u8f70\u9686\u201d\u4e00\u58f0\uff0c\u5730\u9762\u5ffd\u7136\u9677\u4e86\u4e00\u4e2a\u5927\u6d1e\uff0c\u5f20\u4e39\u67ab\u6574\u4e2a\u8eab\u8eaf\u8dcc\u4e86\u4e0b\u53bb\u3002\u539f \u6765\u4ed6\u6240\u7ad9\u7acb\u7684\u5730\u65b9\uff0c\u5e95\u4e0b\u662f\u4e2a\u9677\u9631\uff0c\u4e0a\u9762\u76d6\u4ee5\u6d6e\u6c99\uff0c\u82e5\u4ee5\u5f20\u4e39\u67ab\u7684\u8f7b\u529f\u672c\u4e8b\uff0c\u4e00\u63a0\u5373\u8fc7\uff0c\u539f \u53ef\u65e0\u4e8b\uff0c\u4f46\u88ab\u90a3\u5c11\u5973\u4e00\u547c\uff0c\u9aa4\u7136\u4e00\u4e2a\u7a81\u5140\uff0c\u505c\u4e86\u4e00\u505c\uff0c\u6d6e\u6c99\u652f\u6301\u4e0d\u4e86\u4ed6\u7684\u4f53\u91cd\uff0c\u7adf\u51fa\u5176\u4e0d\u610f \u5730\u7740\u4e86\u9053\u513f\u3002 \u3000\u3000\u597d\u4e2a\u5f20\u4e39\u67ab\uff0c\u5728\u534a\u7a7a\u4e00\u4e2a\u7b4b\u6597\uff0c\u51cf\u4e86\u90a3\u4e0b\u5760\u4e4b\u52bf\uff0c\u8f7b\u98d8\u98d8\u5730\u811a\u843d\u5b9e\u5730\u3002\u6d1e\u4e2d\u9ed1\u9edd\u9edd\u7684\u4f38 \u624b\u4e0d\u89c1\u4e94\u6307\uff0c\u5f20\u4e39\u67ab\u81ea\u6000\u4e2d\u53d6\u51fa\u4e00\u4e32\u591c\u660e\u73e0\uff0c\u6302\u5728\u5251\u5c16\uff0c\u73e0\u5149\u5251\u5149\uff0c\u76f8\u4e92\u8f89\u6620\uff0c\u53ea\u89c1\u8fd9\u6d1e\u6df1 \u5165\u4e0d\u53ef\u6d4b\uff0c\u8981\u6500\u4e0a\u53bb\u5df2\u4e0d\u53ef\u80fd\uff0c\u6d1e\u5e95\u51f9\u51f8\u4e0d\u5e73\uff0c\u6709\u4e00\u80a1\u6f6e\u6e7f\u7684\u9709\u81ed\u4e4b\u5473\uff0c\u4f3c\u662f\u4e00\u6761\u591a\u5e74\u4e0d\u7528 \u7684\u96a7\u9053\u3002\u5f20\u4e39\u67ab\u884c\u4e86\u8bb8\u4e45\uff0c\u624d\u884c\u5230\u5c3d\u5934\uff0c\u6478\u4e00\u6478\u5374\u662f\u5c71\u5ca9\u77f3\u58c1\u3002\u5f20\u4e39\u67ab\u53f9\u9053\uff1a\u201c\u6599\u4e0d\u5230\u65e0\u547d \u4e27\u4e8e\u6b64\uff0c\u6b7b\u4e86\u4e5f\u662f\u7cca\u91cc\u7cca\u6d82\u3002\u201d\u60f3\u8d77\u81ea\u5df1\u58ee\u5fd7\u672a\u916c\u4e0d\u7531\u5fc3\u4e2d\u5927\u6124\uff0c\u201c\u556a\u201d\u7684\u4e00\u62f3\u51fb\u5728\u77f3\u4e0a\uff0c \u90a3\u77f3\u5ffd\u7136\u5fae\u5fae\u9707\u52a8\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u5927\u559c\uff0c\u6025\u7528\u5b9d\u5251\u5728\u90a3\u5757\u77f3\u5934\u7684\u56db\u5468\u4e71\u5212\uff0c\u90a3\u5757\u77f3\u8d28\u4f3c\u4e4e\u7279\u522b\u677e\u8106\uff0c\u4e0d\u6d88\u591a\u4e45\uff0c\u6c99 \u77f3\u7eb7\u7eb7\u843d\u4e0b\u3002\u539f\u6765\u90a3\u5757\u77f3\u5934\u4e43\u662f\u88c5\u4e0a\u53bb\u7684\uff0c\u56db\u5468\u7c98\u8fde\u7684\u6c99\u77f3\u53bb\u6389\uff0c\u7acb\u89c1\u53ef\u4ee5\u6d3b\u52a8\uff0c\u5f20\u4e39\u67ab\u594b \u8d77\u795e\u529b\uff0c\u628a\u90a3\u5757\u77f3\u5934\u4e00\u63a8\uff0c\u201c\u8f70\u9686\u201d\u4e00\u58f0\uff0c\u8dcc\u4e0b\u5bf9\u9762\uff0c\u7559\u4e0b\u7684\u7f3a\u53e3\u6070\u597d\u53ef\u5bb9\u4e00\u4eba\u94bb\u8fc7\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u81ea\u6d1e\u53e3\u94bb\u51fa\uff0c\u5ffd\u611f\u4e00\u9635\u51b7\u5149\u8000\u773c\uff0c\u7ec6\u770b\u4e4b\u65f6\uff0c\u4e0d\u7981\u53c8\u60ca\u53c8\u559c\u3002\u539f\u6765\u6d1e\u53e3\u90a3\u8fb9\u53c8\u662f \u4e00\u6761\u96a7\u9053\uff0c\u53ea\u662f\u6bd4\u8d77\u8fd9\u8fb9\u5374\u77ed\u5f97\u591a\uff0c\u96a7\u9053\u7684\u5c3d\u5934\uff0c\u77d7\u7acb\u7740\u4e00\u6247\u77f3\u95e8\uff0c\u7adf\u662f\u4e00\u5757\u901a\u4f53\u6676\u83b9\u7684\u767d \u7389\u6240\u9020\u3002\u7389\u77f3\u4e0d\u5947\uff0c\u4f46\u8fd9\u6837\u5927\u7684\u4e00\u5757\u7389\u77f3\uff0c\u53ef\u662f\u65e0\u4ef7\u4e4b\u5b9d\u3002\u5f20\u4e39\u67ab\u6536\u4e86\u660e\u73e0\u5b9d\u5251\uff0c\u6478\u90a3\u7389 \u95e8\uff0c\u5149\u6e9c\u6e9c\u7684\u6ed1\u4e0d\u7559\u624b\uff0c\u5f20\u4e39\u67ab\u6469\u6332\u518d\u56db\uff0c\u5ffd\u7136\u53d1\u73b0\u7389\u95e8\u7684\u4fa7\u9762\u6709\u4e00\u4e2a\u5c0f\u5c0f\u7684\u5319\u5b54\uff0c\u628a\u91d1\u9501 \u5319\u6295\u5165\u53bb\u4e00\u8bd5\uff0c\u7528\u529b\u65cb\u8f6c\uff0c\u90a3\u7389\u95e8\u5e94\u624b\u800c\u542f\u3002\u5f20\u4e39\u67ab\u6536\u4e86\u9501\u5319\uff0c\u8fdb\u5165\u91cc\u9762\uff0c\u987a\u624b\u628a\u7389\u95e8\u4e00 \u5173\uff0c\u53ea\u89c1\u5149\u8292\u8000\u773c\uff0c\u6d1e\u4e2d\u5806\u6ee1\u91d1\u94f6\u73e0\u5b9d\uff0c\u5f20\u4e39\u67ab\u6025\u5728\u73e0\u5b9d\u5806\u4e2d\u5bfb\u89c5\uff0c\u627e\u51fa\u4e00\u4e2a\u7389\u5323\uff0c\u628a\u5323\u6253 \u5f00\uff0c\u5185\u4e2d\u653e\u7740\u4e00\u5f20\u5e73\u6298\u7684\u5927\u5730\u56fe\uff0c\u5f20\u4e39\u67ab\u5c55\u5f00\u5730\u56fe\uff0c\u501f\u7740\u5b9d\u6c14\u73e0\u5149\uff0c\u4ed4\u7ec6\u9605\u9274\uff0c\u53ea\u89c1\u8fd9\u5f20\u5730 \u56fe\u5341\u5206\u8be6\u7ec6\uff0c\u753b\u6709\u5404\u5904\u5c71\u79cd\u9669\u8981\u7684\u5730\u5f62\uff0c\u80cc\u540e\u8fd8\u6ce8\u6709\u5728\u5404\u5904\u9669\u8981\u7684\u653b\u5b88\u62d2\u654c\u4e4b\u6cd5\u3002\u8981\u77e5\u53e4\u4ee3 \u4e4b\u65f6\uff0c\u4ea4\u901a\u4e0d\u4fbf\uff0c\u5f88\u5c11\u6709\u4eba\u80fd\u5468\u6e38\u5168\u56fd\uff0c\u7ec6\u5bdf\u5730\u5f62\uff0c\u6240\u4ee5\u50cf\u8fd9\u6837\u7684\u4e00\u4efd\u5730\u56fe\u4e43\u662f\u7a00\u4e16\u4e4b\u5b9d\u3002 \u5f20\u4e39\u67ab\u60f3\u8d77\u4e86\u4e3a\u4e86\u7ed8\u8fd9\u4efd\u5730\u56fe\uff0c\u8d39\u5c3d\u6bd5\u751f\u5fc3\u529b\u7684\u5f6d\u548c\u5c1a\uff0c\u60f3\u8d77\u4e86\u5f6d\u548c\u5c1a\u6559\u81ea\u5df1\u5148\u7956\u5f20\u58eb\u8bda\u4e0e \u660e\u671d\u592a\u7956\u6731\u5143\u748b\u7684\u82e6\u5fc3\uff0c\u4e0e\u4ed6\u8d77\u4e49\u6297\u5143\u7684\u58ee\u70c8\u4e8b\u8ff9\uff0c\u4e0d\u7981\u6f78\u7136\u6cea\u4e0b\u3002\u518d\u7ec6\u770b\u65f6\u53ea\u89c1\u7389\u5323\u4e0a\u8fd8 \u6709\u4e24\u884c\u5c0f\u5b57\uff0c\u6587\uff1a\u201c\u6b64\u56fe\u51fa\u4e16\uff0c\u5927\u5468\u91cd\u5149\u3002\u201d\u60f3\u662f\u4ed6\u5148\u7956\u5f20\u58eb\u8bda\u9884\u6599\u5230\u6709\u540e\u4ee3\u5b50\u5b59\u53ef\u80fd\u5230 \u6b64\uff0c\u6240\u4ee5\u7559\u4e0b\u9057\u8a00\uff0c\u8981\u540e\u4ee3\u5b50\u5b59\u7ee7\u627f\u4ed6\u7684\u9057\u5fd7\uff0c\u706d\u6389\u5927\u660e\uff0c\u91cd\u5149\u5927\u5468\uff08\u5927\u5468\u662f\u5f20\u58eb\u8bda\u6240\u5efa\u56fd \u53f7\uff09\u3002\u5f20\u4e39\u67ab\u5411\u7389\u5323\u5730\u56fe\u62dc\u4e86\u516b\u62dc\uff0c\u671b\u7a7a\u7980\u9053\uff1a\u201c\u4e0d\u8096\u513f\u5b59\u5f20\u4e39\u67ab\u8981\u8bf7\u5217\u7956\u5217\u5b97\u539f\u5ba5\uff0c\u706d\u660e \u5174\u5468\u7684\u9057\u8bad\u53ea\u6015\u6211\u4e0d\u80fd\u4f9d\u7167\u4e86\u3002\u201d\u539f\u6765\u5f20\u4e39\u67ab\u6765\u53d6\u5730\u56fe\u4e0e\u5b9d\u85cf\uff0c\u5176\u4e2d\u5b9e\u662f\u542b\u6709\u6df1\u5fc3\uff0c\u4ed6\u662f\u60f3 \u5c06\u5730\u56fe\u732e\u4e0e\u4e8e\u8c26\uff0c\u8ba9\u4ed6\u53bb\u62b5\u6297\u5916\u654c\uff0c\u5b9d\u85cf\u4e5f\u51c6\u5907\u732e\u4e0e\u4e8e\u8c26\uff0c\u8ba9\u4ed6\u62ff\u53bb\u4f5c\u634d\u536b\u56fd\u5bb6\u7684\u4e49\u5175\u519b \u9977\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u5377\u597d\u5730\u56fe\uff0c\u5fc3\u9053\uff1a\u201c\u6211\u4e14\u5230\u6d1e\u53e3\u53bb\u5927\u58f0\u75be\u547c\uff0c\u8bf4\u660e\u6211\u8fd9\u7247\u4e3a\u56fd\u7684\u82e6\u5fc3\uff0c\u76fc\u90a3\u6d1e\u5ead \u5e84\u4e3b\u542c\u89c1\uff0c\u82e5\u7136\u4ed6\u4f53\u8c05\u6211\u8fd9\u7247\u82e6\u5fc3\uff0c\u5b9a\u7136\u5782\u4e0b\u957f\u7ef3\u5c06\u6211\u540a\u4e0a\u53bb\u3002\u201d\u4e3b\u610f\u6253\u5b9a\uff0c\u4fbf\u53bb\u5f00\u90a3\u7389 \u95e8\uff0c\u4e0d\u6599\u7528\u529b\u4e00\u63a8\uff0c\u7389\u95e8\u4e1d\u6beb\u4e0d\u52a8\uff0c\u539f\u6765\u81ea\u5df1\u5165\u95e8\u4e4b\u65f6\uff0c\u987a\u624b\u4e00\u5173\u7adf\u5c06\u7389\u95e8\u53c8\u5173\u7262\u4e86\u3002\u90a3\u7389 \u95e8\u91cc\u5916\u6709\u9501\uff0c\u5f20\u4e39\u67ab\u89c5\u5230\u5319\u5b54\uff0c\u7528\u91d1\u9501\u5319\u4e00\u8bd5\u5374\u662f\u5f00\u4e4b\u4e0d\u5f97\uff0c\u90a3\u95e8\u5916\u4e0e\u95e8\u5185\u7684\u9501\u5319\u5e76\u4e0d\u662f\u4e00 \u6837\u7684\u3002\u5f20\u4e39\u67ab\u6697\u53eb\u4e00\u58f0\uff1a\u201c\u82e6\u4e5f\uff01\u201d\u8fd9\u6d1e\u4e43\u662f\u5c06\u5c71\u8179\u4e2d\u95f4\u638f\u7a7a\uff0c\u81ea\u5df1\u7eb5\u6709\u5929\u5927\u672c\u9886\uff0c\u4e5f\u96be\u653b \u5c71\u800c\u51fa\u3002\u90a3\u7389\u95e8\u786c\u903e\u7cbe\u94a2\uff0c\u7528\u5b9d\u5251\u4e5f\u96be\u5241\u70c2\uff0c\u6d1e\u4e2d\u7a7a\u6709\u91d1\u94f6\uff0c\u5374\u65e0\u7cae\u98df\uff0c\u5916\u9762\u7684\u4eba\u7eb5\u7136\u60f3\u6765 \u63f4\u6551\uff0c\u6ca1\u6709\u81ea\u5df1\u8fd9\u679a\u91d1\u9501\u5319\uff0c\u4e5f\u5f00\u4e0d\u52a8\u5916\u9762\u7684\u95e8\u3002\u5f20\u4e39\u67ab\u5fc3\u9053\uff1a\u201c\u770b\u6765\u8fd9\u756a\u5fc5\u662f\u997f\u6b7b\u65e0 \u7591\uff01\u201d \u3000\u3000\u5f20\u4e39\u67ab\u7eb5\u518d\u80c6\u5927\uff0c\u8fd9\u65f6\u4e5f\u89c9\u4e00\u7247\u6b7b\u4ea1\u7684\u9634\u5f71\u7b3c\u7f69\u5934\u4e0a\u3002\u8bd5\u8bd5\u5927\u53eb\u51e0\u58f0\uff0c\u58f0\u97f3\u649e\u7740\u5c71\u77f3\u7389 \u95e8\uff0c\u53c8\u8361\u4e86\u56de\u6765\uff0c\u9707\u8033\u6b32\u804b\uff0c\u8981\u60f3\u4f20\u51fa\u5c71\u5916\uff0c\u90a3\u5e0c\u671b\u4e5f\u662f\u5fae\u4e4e\u5176\u5fae\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u5b9a\u4e86\u5b9a\u795e\uff0c\u5fc3\u9053\uff1a\u201c\u5e38\u4eba\u4e03\u65e5\u4e0d\u98df\u5fc5\u6b7b\uff0c\u6211\u7ec3\u6709\u6b66\u529f\uff0c\u53ef\u80fd\u6371\u5230\u5341\u65e5\uff0c\u8fd9\u5341\u65e5\u8fd9 \u4e2d\uff0c\u6211\u8be5\u505a\u4e9b\u4ec0\u4e48\u4ee5\u9063\u6b64\u771f\u6b63\u7684\u2018\u6709\u6daf\u4e4b\u751f\u2019\uff1f\u201d\u8111\u6d77\u4e2d\u6731\u3001\u5f20\u4e24\u5bb6\u7684\u51a4\u4ec7\uff0c\u4e91\u5f20\u4e24\u5bb6\u7684\u51a4 \u4ec7\u4e00\u4e00\u95ea\u8fc7\uff0c\u4e91\u857e\u7684\u5029\u5f71\u7a81\u7136\u6d6e\u73b0\u51fa\u6765\uff0c\u90a3\u4f3c\u559c\u4f3c\u6012\u5982\u6068\u5982\u7231\u7684\u795e\u60c5\u53c8\u6d3b\u5728\u5fc3\u5934\u3002\u5f20\u4e39\u67ab\u53f9 \u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u4eca\u751f\u4eca\u4e16\u54b1\u4eec\u662f\u518d\u96be\u76f8\u89c1\u4e86\u3002\u201d\u5c3d\u7ba1\u4e91\u857e\u6709\u8fc7\u51e0\u6b21\u8981\u7528\u5b9d\u5251\u6740\u4ed6\uff0c\u800c\u4eca\u4ed6\u60f3 \u8d77\u7684\u5374\u5c3d\u662f\u4e91\u857e\u7684\u67d4\u60c5\u871c\u610f\uff0c\u5fc3\u4e2d\u5ffd\u53d1\u5947\u60f3\uff1a\u201c\u4e91\u857e\u5c3d\u7ba1\u6709\u65f6\u5bf9\u6211\u73b0\u51fa\u6740\u6c14\uff0c\u5fc3\u80a0\u5374\u662f\u65e0\u9650 \u67d4\u6148\uff0c\u5440\uff0c\u5979\u4eba\u592a\u4ec1\u6148\uff0c\u521a\u6bc5\u4e0d\u8db3\uff0c\u662f\u4e00\u5927\u7f3a\u70b9\u3002\u8fd9\u6d1e\u5ead\u5e84\u5e84\u4e3b\u7684\u5973\u513f\uff0c\u5c3d\u7ba1\u4e00\u7247\u6e29\u67d4\uff0c\u5374 \u5e26\u7740\u7537\u513f\u82f1\u6c14\uff0c\u6211\u867d\u4e0e\u5979\u521d\u4ea4\uff0c\u5374\u6562\u65ad\u5b9a\u5979\u662f\u4e2a\u6562\u4f5c\u6562\u4e3a\u7684\u5973\u5b50\u3002\u82e5\u7136\u5c06\u4e91\u857e\u7684\u4f18\u70b9\u4e0e\u5979\u5408 \u800c\u4e3a\u4e00\uff0c\u5c82\u4e0d\u662f\u5929\u4e0b\u5b8c\u4eba\uff1f\u201d \u3000\u3000\u5f20\u4e39\u67ab\u88ab\u56f0\u6d1e\u4e2d\uff0c\u65e0\u804a\u4e4b\u6781\uff0c\u56db\u5904\u7ffb\u770b\u7956\u5148\u7684\u9057\u5b9d\uff0c\u5ffd\u7136\u5728\u73e0\u5b9d\u5806\u4e2d\u53c8\u53d1\u73b0\u4e00\u4e2a\u7389\u5323\uff0c \u4e0a\u9762\u523b\u6709\u5b57\u9053\uff1a\u201c\u5148\u5e08\u58a8\u5b9d\uff0c\u58eb\u8bda\u8c28\u85cf\u3002\u201d\u6253\u5f00\u4e00\u770b\uff0c\u53ea\u89c1\u91cc\u9762\u88c5\u6709\u96f6\u6563\u7684\u5730\u56fe\u4e0e\u672d\u8bb0\u4e4b \u7c7b\uff0c\u90a3\u4e9b\u5730\u56fe\u4e43\u662f\u5f6d\u548c\u5c1a\u6bcf\u5230\u4e00\u5730\u65f6\u8349\u8349\u753b\u4e0b\u6765\u7684\uff0c\u90a3\u5f20\u8be6\u7ec6\u7684\u5730\u56fe\u5c31\u662f\u7528\u8fd9\u4e9b\u8349\u56fe\u62fc\u5408\u8d77 \u6765\u7ed8\u5236\u7684\uff0c\u6709\u4e86\u90a3\u5f20\u5927\u5730\u56fe\uff0c\u8fd9\u4e9b\u8349\u56fe\u81ea\u662f\u65e0\u7528\uff0c\u4e0d\u8fc7\u4e5f\u53ef\u770b\u5230\u5f6d\u548c\u5c1a\u5f53\u5e74\u7ed8\u5236\u5730\u56fe\u65f6\u6240\u82b1 \u5fc3\u8840\u3002\u672d\u8bb0\u4e43\u662f\u4ed6\u5230\u6bcf\u4e00\u5904\u5730\u65b9\u6240\u5199\u4e0b\u7684\u968f\u7b14\uff0c\u5176\u4e2d\u6709\u98ce\u571f\u4eba\u60c5\uff0c\u4e5f\u6709\u5c31\u7740\u5c71\u5ddd\u5f62\u52bf\u800c\u8c08\u5230 \u7528\u5175\u7684\u8bae\u8bba\u3002\u5f20\u4e39\u67ab\u5fc3\u9053\uff1a\u201c\u8fd9\u4e9b\u672d\u8bb0\u5012\u5e94\u8be5\u597d\u597d\u7ed9\u4ed6\u6574\u7406\uff0c\u8f91\u6210\u4e13\u4e66\u3002\u201d\u4f46\u4e00\u60f3\u5230\u81ea\u5df1\u800c \u4eca\u53ea\u662f\u5728\u6d1e\u4e2d\u7b49\u6b7b\uff0c\u53c8\u4e0d\u89c9\u9eef\u7136\u3002 \u3000\u3000\u672d\u8bb0\u5806\u4e2d\u8fd8\u85cf\u6709\u4e00\u672c\u5c0f\u4e66\uff0c\u5f20\u4e39\u67ab\u62ff\u8d77\u4e00\u770b\uff0c\u53ea\u89c1\u4e0a\u9762\u5199\u7740\u300a\u7384\u529f\u8981\u8bc0\u300b\u56db\u5b57\uff0c\u7ffb\u5f00\u6765 \u8bfb\uff0c\u7b2c\u4e00\u53e5\u5c31\u662f\uff1a\u201c\u5b50\u66f0\uff1a\u8303\u56f4\u5929\u5730\u4e4b\u5316\u800c\u4e0d\u8fc7\uff0c\u5c82\u80fd\u51fa\u4e8e\u7406\u3001\u6c14\u3001\u8c61\u4e4e\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a \u201c\u5b54\u5b50\u54ea\u61c2\u5185\u529f\uff1f\u4e3a\u4f55\u5f15\u4ed6\u7684\u8bdd\uff1f\u201d\u518d\u8bfb\u4e0b\u53bb\u9053\uff1a\u201c\u8c61\u8005\u62f3\u4e4b\u5f62\u4e5f\u6c14\u8005\u62f3\u4e4b\u52bf\u4e5f\uff0c\u7406\u8005\u62f3\u4e4b \u529f\u4e5f\u3002\u7406\u6c14\u8c61\u5907\uff0c\u4e3e\u624b\u6295\u8db3\uff0c\u65e0\u4e0d\u903e\u77e9\u3002\u201d\u628a\u4fee\u7ec3\u4e0a\u4e58\u5185\u529f\u7684\u9053\u7406\uff0c\u89e3\u91ca\u5f97\u6e05\u6e05\u695a\u695a\u3002\u5f20\u4e39 \u67ab\u6697\u81ea\u53f9\u9053\uff1a\u201c\u770b\u4e86\u6b64\u4e66\uff0c\u65b9\u77e5\u81ea\u5df1\u6d45\u964b\u3002\u4e0e\u8fd9\u4f4d\u6b66\u5b66\u5927\u5e08\u76f8\u6bd4\uff0c\u6211\u4e0d\u8fc7\u662f\u8424\u706b\u4e4b\u5149\u3002\u201d \u3000\u3000\u5f20\u4e39\u67ab\u8d8a\u8bfb\u8d8a\u89c9\u6709\u5473\uff0c\u987b\u77e5\u90a3\u5f6d\u548c\u5c1a\uff08\u5373\u5f6d\u83b9\u7389\uff09\u8eab\u4e3a\u4e24\u4e2a\u5929\u5b50\uff08\u6731\u5143\u748b\u3001\u5f20\u58eb\u8bda\uff09\u7684 \u5e08\u7236\uff0c\u80f8\u4e2d\u6240\u5b66\u5b9e\u662f\u975e\u540c\u5c0f\u53ef\uff01\u90a3\u672c\u300a\u7384\u529f\u8981\u8bc0\u300b\u6240\u8bb2\u7684\u90fd\u662f\u57fa\u672c\u8981\u7406\uff0c\u5f20\u4e39\u67ab\u6b66\u5b66\u672c\u6709\u6839 \u5e95\u4eba\u53c8\u6781\u7aef\u806a\u660e\uff0c\u8bfb\u5b8c\u4e4b\u540e\uff0c\u53ea\u89c9\u4e00\u7406\u901a\u3001\u767e\u7406\u878d\uff0c\u8bb8\u591a\u6b66\u5b66\u4e0a\u7684\u7591\u96be\uff0c\u7adf\u7136\u8fce\u5203\u800c\u89e3\u3002\u5f20 \u4e39\u67ab\u7684\u5e08\u7956\u7384\u673a\u9038\u58eb\uff0c\u4f20\u6388\u56db\u5927\u5f1f\u5b50\uff0c\u90fd\u662f\u6bcf\u4eba\u53ea\u4f20\u4e00\u95e8\u7edd\u6280\uff0c\u5f20\u4e39\u67ab\u8bfb\u4e86\u6b64\u4e66\uff0c\u7ec6\u7ec6\u63e3\u6478 \u81ea\u5df1\u6240\u89c1\u8fc7\u7684\u5927\u5e08\u4f2f\u7684\u5927\u529b\u91d1\u521a\u624b\u529f\u592b\uff0c\u4e0e\u4e8c\u5e08\u4f2f\u6f6e\u97f3\u548c\u5c1a\u7684\u5916\u5bb6\u786c\u529f\uff0c\u53ea\u89c9\u5176\u4e2d\u90fd\u6709\u7406\u8def \u53ef\u5bfb\uff0c\u53ef\u4ee5\u65e0\u5e08\u81ea\u901a\uff0c\u4e0d\u7981\u72c2\u559c\uff0c\u5fc3\u9053\uff1a\u201c\u6211\u6709\u4e86\u6b64\u4e66\uff0c\u82e6\u5fc3\u8654\u4fee\uff0c\u5c06\u6765\u5c82\u4e0d\u662f\u5b66\u4efb\u4f55\u4e00\u6d3e \u7684\u6b66\u529f\u90fd\u53ef\u4ee5\u4e8b\u534a\u529f\u500d\uff0c\u5bb9\u6613\u5f97\u591a\uff01\u201d\u4f46\u4e00\u60f3\u5230\u81ea\u5df1\u56f0\u6b64\u7edd\u7a9f\uff0c\u5b9e\u96be\u9003\u51fa\u751f\u5929\uff0c\u7eb5\u7136\u5b66\u4e86\u7edd \u4e16\u6b66\u529f\u4e5f\u662f\u65e0\u7528\uff0c\u66f4\u662f\u795e\u4f24\u3002\u6b63\u662f\uff1a \u3000\u3000\u5f02\u5b9d\u5947\u4e66\u90fd\u5f97\u4e86\uff0c\u9677\u8eab\u7edd\u5883\u5948\u4f55\u5728\uff1f \u3000\u3000\u6b32\u77e5\u5f20\u4e39\u67ab\u80fd\u5426\u8131\u9669\uff1f\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u840d\u8e2a\u4fa0\u5f71\u5f55\u300b\u2014\u2014\u7b2c\u5341\u516b\u56de\u3000\u77f3\u9635\u6218\u6c1b\u8c6a\u60c5\u6d88\u79ef\u6028\u3000\u8377\u5858\u6708\u8272\u8bcd\u610f\u5bc4\u6df1\u5fc3 \u6881\u7fbd\u751f\u300a\u840d\u8e2a\u4fa0\u5f71\u5f55\u300b \u7b2c\u5341\u516b\u56de\u3000\u77f3\u9635\u6218\u6c1b\u8c6a\u60c5\u6d88\u79ef\u6028\u3000\u8377\u5858\u6708\u8272\u8bcd\u610f\u5bc4\u6df1\u5fc3 \u3000\u3000\u5f20\u4e39\u67ab\u751f\u6027\u8c41\u8fbe\uff0c\u518d\u7ffb\u8bfb\u90a3\u672c\u300a\u7384\u529f\u8981\u8bc0\u300b\uff0c\u5ffd\u800c\u7b11\u9053\uff1a\u201c\u671d\u95fb\u9053\uff0c\u5915\u6b7b\u53ef\u77e3\u3002\u8fd9\u4e5f\u662f \u5b54\u5b50\u8bf4\u7684\u3002\u6211\u800c\u4eca\u5f97\u6b64\u5f02\u4e66\uff0c\u5982\u95fb\u4e00\u4ee3\u5b97\u5e08\u4eb2\u4f20\u5927\u9053\uff0c\u53ef\u7aa5\u6b66\u5b66\u4e0d\u4f20\u4e4b\u79d8\uff0c\u83b7\u524d\u4eba\u672a\u6709\u4e4b\u7f18 \u90a3\u8fd8\u4e0d\u5fc3\u6ee1\u610f\u8db3\uff0c\u5374\u8fd8\u65a4\u65a4\u8ba1\u8f83\u81ea\u5df1\u80fd\u6d3b\u591a\u5c11\u5929\uff0c\u80f8\u895f\u5982\u6b64\u6ede\u800c\u4e0d\u5316\uff0c\u5c82\u4e0d\u4e3a\u53e4\u5723\u5148\u8d24\u6240 \u7b11\uff01\u201d\u5982\u6b64\u4e00\u60f3\u987f\u628a\u751f\u6b7b\u7f6e\u4e4b\u5ea6\u5916\uff0c\u5c31\u5728\u77f3\u7a9f\u4e4b\u4e2d\uff0c\u6309\u90a3\u5f02\u4e66\u6240\u6388\uff0c\u4fee\u4e60\u8d77\u4e0a\u4e58\u7684\u5185\u529f\u6765\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u6076\u6597\u534a\u65e5\uff0c\u672c\u5df2\u6e10\u611f\u9965\u997f\uff0c\u505a\u4e86\u4e00\u9635\u529f\u8bfe\uff0c\u6c14\u900f\u91cd\u5173\uff0c\u820c\u5e95\u751f\u6d25\uff0c\u53cd\u89c9\u901a\u4f53\u8212 \u6cf0\uff0c\u7eb3\u5934\u4fbf\u7761\uff0c\u4e00\u9192\u6765\uff0c\u6d1e\u4e2d\u73e0\u5149\u5b9d\u6c14\uff0c\u8000\u773c\u751f\u7f2c\uff0c\u4e5f\u4e0d\u77e5\u5916\u95f4\u662f\u767d\u5929\u8fd8\u662f\u9ed1\u591c\u3002\u5f20\u4e39\u67ab\u53c8 \u8bd5\u4f9d\u7740\u81ea\u5df1\u6240\u609f\u7684\u5999\u7406\uff0c\u63e3\u6478\u81ea\u5df1\u6240\u89c1\u8fc7\u7684\u5927\u5e08\u4f2f\u8463\u5cb3\u7684\u5927\u529b\u91d1\u521a\u624b\u529f\u592b\uff0c\u8bd5\u884c\u7ec3\u4e60\uff0c\u4e00\u638c \u63a5\u7740\u4e00\u638c\uff0c\u62cd\u90a3\u7389\u95e8\uff0c\u7389\u95e8\u7ed9\u638c\u529b\u9707\u5f97\u84ec\u84ec\u4f5c\u54cd\uff0c\u867d\u6253\u5b83\u4e0d\u5f00\uff0c\u542c\u8fd9\u638c\u529b\u51fb\u77f3\u4e4b\u58f0\uff0c\u4e5f\u77e5\u81ea \u5df1\u65e0\u5e08\u81ea\u901a\u7684\u91d1\u521a\u624b\u529f\u592b\uff0c\u7adf\u4e5f\u6709\u4e86\u51e0\u5206\u529f\u529b\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u997f\u4e86\u4e00\u5929\uff0c\u8fd8\u4e0d\u89c9\u600e\u6837\uff0c\u53ea\u662f\u53e3\u4e2d\u7126\u6e34\uff0c\u5374\u662f\u96be\u53d7\u4e4b\u6781\u3002\u8981\u77e5\u5e38\u4eba\u4e0d\u98df\uff0c\u53ef\u652f\u6301 \u81f3\u4e03\u65e5\u59cb\u6b7b\uff0c\u4f46\u82e5\u65e0\u6c34\u559d\uff0c\u5219\u4e09\u65e5\u5fc5\u6b7b\u3002\u5f20\u4e39\u67ab\u6b66\u529f\u867d\u9ad8\uff0c\u65e5\u4f59\u6ef4\u6c34\u4e0d\u8fdb\uff0c\u4ea6\u4e94\u5185\u5982\u711a\uff0c\u597d \u4e0d\u5bb9\u6613\u624d\u5728\u77f3\u58c1\u7684\u9699\u7f45\u4e4b\u4e2d\uff0c\u7b49\u5f97\u51e0\u6ef4\u6e17\u51fa\u6765\u7684\u6c34\u73e0\uff0c\u4ecd\u662f\u672a\u89e3\u7126\u6e34\u3002\u5f20\u4e39\u67ab\u5c4f\u795e\u9759\u6c14\u5728\u5fc3 \u4e2d\u9ed8\u8bf5\u90a3\u672c\u300a\u7384\u529f\u8981\u8bc0\u300b\uff0c\u4ece\u5934\u81f3\u5c3e\uff0c\u53c8\u4ece\u6700\u540e\u4e00\u5b57\u5012\u80cc\u56de\u6765\uff0c\u5fc3\u6709\u6240\u6ce8\uff0c\u7126\u6e34\u4e4b\u611f\u679c\u7136\u51cf \u5f31\u3002\u5982\u6b64\u8fd9\u822c\u7ffb\u6765\u590d\u53bb\u80cc\u4e86\u51e0\u904d\uff0c\u6b63\u5728\u6f5c\u5fc3\u9ed8\u8bf5\uff0c\u5ffd\u95fb\u5f97\u6709\u4e00\u9635\u7ec6\u5fae\u7684\u6089\u7d22\u4e4b\u58f0\uff0c\u63a5\u7740\u542c\u5f97 \u6709\u786c\u7269\u6316\u6398\u571f\u77f3\u4e4b\u58f0\uff0c\u5f20\u4e39\u67ab\u4e00\u8dc3\u800c\u8d77\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u662f\u8c01\uff1f\u201d\u5916\u9762\u7684\u4eba\u4e00\u58f0\u4e0d\u54cd\uff0c\u6316\u77f3\u6398\u571f \u5982\u6545\u3002\u5f20\u4e39\u67ab\u5947\u9053\uff1a\u201c\u82e5\u662f\u6709\u5fc3\u6551\u6211\uff0c\u4e3a\u4f55\u5374\u4e0d\u7b54\u8bdd\uff1f\u201d\u5916\u9762\u7684\u4eba\u6398\u4e86\u8bb8\u4e45\uff0c\u5f20\u4e39\u67ab\u594b\u8d77\u795e \u529b\uff0c\u4e00\u638c\u51fb\u53bb\uff0c\u78b0\u7740\u7389\u95e8\uff0c\u201c\u84ec\u201d\u7684\u4e00\u58f0\uff0c\u7389\u95e8\u52a8\u4e5f\u4e0d\u52a8\uff0c\u624b\u81c2\u5374\u51e0\u4e4e\u7ed9\u53cd\u9707\u5f97\u8131\u81fc\u3002\u5f20\u4e39 \u67ab\u60f3\u8d77\u8fd9\u7389\u95e8\u575a\u56fa\u5f02\u5e38\uff0c\u65ad\u975e\u666e\u901a\u7684\u94c1\u5668\u6240\u80fd\u5f00\uff0c\u82e5\u8bf4\u662f\u91cd\u6398\u5730\u4e0b\u4e00\u6761\u96a7\u9053\u8fdb\u6765\uff0c\u867d\u7136\u53ef \u80fd\uff0c\u4f46\u6316\u571f\u51ff\u77f3\uff0c\u5de5\u7a0b\u975e\u5c0f\uff0c\u53ea\u6015\u5730\u9053\u901a\u65f6\u81ea\u5df1\u5df2\u7ecf\u6e34\u6b7b\u997f\u6b7b\u4e86\u3002\u800c\u4e14\u542c\u5916\u9762\u6316\u571f\u4e4b\u58f0\uff0c\u53c8 \u4f3c\u4e4e\u53ea\u662f\u5b64\u8eab\u4e00\u4eba\uff0c\u51ed\u4e00\u4eba\u4e4b\u529b\uff0c\u90a3\u5c31\u66f4\u4e0d\u6613\u4e3a\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u6b63\u5728\u601d\u60f3\uff0c\u5ffd\u89c1\u7389\u95e8\u4e0b\uff0c\u77f3\u5c51\u7eb7\u98de\uff0c\u6ce5\u571f\u677e\u52a8\uff0c\u5f20\u4e39\u67ab\u7528\u5b9d\u5251\u5728\u91cc\u9762\u63a5\u7740\u90a3\u7f3a\u53e3 \u4e00\u6316\uff0c\u5916\u9762\u5ffd\u5730\u900f\u8fdb\u4e00\u4e1d\u4eae\u5149\uff0c\u539f\u6765\u5916\u9762\u7684\u4eba\uff0c\u5df2\u5728\u7389\u95e8\u4e4b\u4e0b\uff0c\u6316\u5f00\u571f\u77f3\uff0c\u6316\u51fa\u4e86\u4e00\u6761\u624b\u6307 \u822c\u5927\u5c0f\u7684\u5b54\u9053\u3002\u5f20\u4e39\u67ab\u5927\u5947\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u662f\u4ec0\u4e48\u7528\u610f\uff1f\u83ab\u975e\u662f\u60f3\u5148\u9001\u98df\u7269\u7ed9\u6211\uff0c\u8ba9\u6211\u656c\u5ef7\u6b8b \u5598\u5417\uff1f\u53ea\u662f\u8fd9\u5b54\u9053\u4e5f\u592a\u5c0f\u4e86\u3002\u201d\u4ed4\u7ec6\u542c\u65f6\uff0c\u5916\u9762\u6316\u571f\u4e4b\u58f0\u987f\u6b62\uff0c\u5b54\u9053\u4e2d\u6089\u7d22\u4e4b\u58f0\uff0c\u4f3c\u662f\u6709\u4ec0 \u4e48\u786c\u7269\uff0c\u4ece\u5916\u9762\u63a8\u585e\u8fdb\u6765\uff0c\u5f20\u4e39\u67ab\u5168\u795e\u6ce8\u89c6\uff0c\u9661\u7136\u95f4\u773c\u775b\u4e00\u4eae\uff0c\u4e00\u679a\u91d1\u5149\u95ea\u95ea\u7684\u9501\u5319\uff0c\u5df2\u4ece \u5b54\u9053\u585e\u4e86\u5165\u6765\uff0c\u5f20\u4e39\u67ab\u62ff\u8d77\u4e00\u770b\uff0c\u8fd9\u679a\u91d1\u9501\u5319\u548c\u81ea\u5df1\u5728\u5feb\u6d3b\u6797\u6240\u5f97\u7684\u90a3\u628a\uff0c\u7adf\u662f\u4e00\u6a21\u4e00\u6837\u3002 \u5f20\u4e39\u67ab\u4f55\u7b49\u673a\u4f36\uff0c\u6025\u6295\u8fdb\u5319\u5b54\u4e2d\u4e00\u8bd5\uff0c\u7389\u95e8\u5e94\u624b\u800c\u5f00\uff0c\u95e8\u5916\u7b11\u76c8\u76c8\u7684\u7ad9\u7740\u4e00\u4e2a\u5c11\u5973\uff01 \u3000\u3000\u5f20\u4e39\u67ab\u4e00\u89c1\uff0c\u51e0\u4e4e\u7591\u5728\u68a6\u4e2d\uff0c\u8fd9\u5c11\u5973\u7b11\u9765\u76c8\u76c8\u7ea2\u6655\u53cc\u988a\uff0c\u6b63\u662f\u6d1e\u5ead\u5e84\u4e3b\u7684\u5973\u513f\uff01\u53ea\u89c1\u5979 \u5de6\u624b\u628a\u957f\u5251\uff0c\u53f3\u624b\u6301\u5229\u51ff\uff0c\u5251\u5c16\u8fd8\u5e26\u7740\u6ce5\u571f\uff0c\u6d1e\u53e3\u6302\u7740\u4e00\u76cf\u78a7\u7eb1\u706f\u7b3c\uff0c\u60f3\u5fc5\u662f\u5979\u5e26\u6765\u7167\u660e \u7684\u3002\u7389\u95e8\u6253\u5f00\u4e4b\u540e\uff0c\u706f\u7b3c\u7684\u70db\u5149\u7ed9\u6d1e\u4e2d\u7684\u5b9d\u6c14\u73e0\u5149\u6620\u5f97\u9eef\u7136\u5931\u8272\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u6ee1\u8179\u7591\u56e2\uff0c\u62e2\u8896\u4e00\u63d6\uff0c\u9053\uff1a\u201c\u591a\u8c22\u59d1\u5a18\u76f8\u6551\u3002\u201d\u90a3\u5c11\u5973\u5ffd\u5730\u683c\u683c\u4e00\u7b11\uff0c\u63a9\u53e3\u8bf4 \u9053\uff1a\u201c\u5c11\u4e3b\u4eba\uff0c\u6211\u5bb6\u7b49\u4f60\u5df2\u7ecf\u7b49\u4e86\u4e09\u4ee3\u4e86\uff0c\u6628\u665a\u6211\u4eec\u4e0d\u77e5\u662f\u4f60\uff0c\u51e0\u4e4e\u4f24\u4e86\u4f60\u7684\u6027\u547d\uff0c\u4f60\u4e0d\u602a \u8d23\u6211\u4eec\uff0c\u53cd\u800c\u591a\u8c22\u4e48\uff1f\u201d\u5f20\u4e39\u67ab\u731b\u7136\u7701\u8d77\uff0c\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5feb\u522b\u8fd9\u6837\u79f0\u547c\uff0c\u6211\u7684\u7956\u5148\u5076\u7136 \u66fe\u79f0\u738b\u79f0\u5e1d\uff0c\u4e0e\u6211\u4f55\u5e72\uff1f\u6211\u59d3\u5f20\u540d\u4e39\u67ab\uff0c\u4f60\u53eb\u6211\u4e39\u67ab\u597d\u4e86\u3002\u201d\u90a3\u5c11\u5973\u9053\uff1a\u201c\u6211\u5728\u4e24\u4e2a\u6708\u524d\u5df2 \u7ecf\u77e5\u9053\u4f60\u7684\u540d\u5b57\uff0c\u90a3\u65f6\u6211\u5c31\u60f3\uff1a\u8fd9\u4e2a\u540d\u5b57\u771f\u7f8e\uff0c\u6211\u4eec\u7684\u6d1e\u5ead\u5c71\u8170\u4e5f\u79cd\u6709\u597d\u591a\u67ab\u6811\uff0c\u4f60\u770b\u5230 \u5417\uff1f\u201d \u3000\u3000\u8fd9\u5c11\u5973\u7b11\u8bed\u76c8\u76c8\uff0c\u5439\u6c14\u5982\u5170\uff0c\u4e0e\u5f20\u4e39\u67ab\u7adf\u7136\u4e00\u89c1\u5982\u6545\uff0c\u95f2\u804a\u8d77\u6765\uff0c\u5f20\u4e39\u67ab\u4e0d\u89c9\u5fc3\u4e2d\u6697 \u7b11\uff1a\u4e91\u857e\u662f\u5929\u771f\u4e4b\u4e2d\u5e26\u6709\u77dc\u6301\uff0c\u800c\u8fd9\u5c11\u5973\u5219\u662f\u5929\u771f\u4e4b\u4e2d\u5e26\u7740\u723d\u6717\uff0c\u6b63\u662f\u6625\u5170\u79cb\u83ca\uff0c\u5404\u64c5\u80dc \u573a\u3002\u5f20\u4e39\u67ab\u77a7\u5979\u4e00\u773c\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u522b\u5fd9\u544a\u8bc9\u6211\u4f60\u7684\u540d\u5b57\uff0c\u8ba9\u6211\u731c\u731c\uff0c\u4f60\u662f\u4e0d\u662f\u590d\u59d3\u6fb9\u53f0\uff0c\u540d \u5b57\u4e2d\u6709\u4e00\u4e2a\u2018\u660e\u2019\u5b57\u7684\uff1f\u201d\u90a3\u5c11\u5973\u9053\uff1a\u201c\u4f60\u731c\u5bf9\u4e86\uff0c\u662f\u4e0d\u662f\u6fb9\u53f0\u706d\u660e\u544a\u8bc9\u4f60\u7684\uff1f\u201d\u5f20\u4e39\u67ab\u7b11 \u9053\uff1a\u201c\u6fb9\u53f0\u5c06\u519b\u53ef\u4ece\u6765\u6ca1\u6709\u5bf9\u6211\u8bf4\u8fc7\u6709\u4f60\u8fd9\u6837\u4e00\u4f4d\u806a\u660e\u4f36\u4fd0\u7684\u59b9\u59b9\u3002\u201d\u90a3\u5c11\u5973\u4e5f\u7b11\u9053\uff1a\u201c\u53ea \u6015\u4ed6\u4ee5\u524d\u8fd8\u4e0d\u77e5\u9053\u6709\u6211\u8fd9\u4e2a\u7b28\u4e2b\u5934\u5462\u3002\u4ed6\u4e0a\u4e2a\u6708\u5306\u5306\u6765\u5230\u8fd9\u91cc\uff0c\u8ba4\u8bc6\u5bb6\u4eba\uff0c\u53ea\u4f4f\u4e86\u4e00\u5bb5\uff0c\u4fbf \u53c8\u8dd1\u4e86\u3002\u201d\u5f20\u4e39\u67ab\u8ba1\u7b97\u65e5\u671f\uff0c\u6fb9\u53f0\u706d\u660e\u5230\u592a\u6e56\u4e4b\u65e5\uff0c\u6b63\u662f\u756a\u738b\u5c06\u8981\u56de\u56fd\uff0c\u81ea\u5df1\u5728\u4eac\u4e2d\u89c1\u8fc7\u6fb9 \u53f0\u4e0e\u4e8e\u8c26\u4e4b\u540e\u3002\u4ed6\u7adf\u7136\u795e\u4e0d\u77e5\u9b3c\u4e0d\u89c9\u5730\u5077\u5077\u79bb\u4eac\u6570\u65e5\uff0c\u53ef\u7b11\u4eac\u4e2d\u7684\u9526\u8863\u536b\u7adf\u662f\u65e0\u4eba\u53d1\u89c9\uff0c\u4efb \u4ed6\u6765\u53bb\u3002 \u3000\u3000\u90a3\u5c11\u5973\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u6fb9\u53f0\u706d\u660e\u79bb\u5f00\u8fd9\u91cc\u4e4b\u540e\uff0c\u8fd8\u6ca1\u6709\u89c1\u8fc7\u4f60\u4e86\u3002\u4ed6\u4e0a\u4e2a\u6708\u6765\u65f6\uff0c\u8bf4 \u8d77\u4f60\u5077\u5165\u4e2d\u539f\uff0c\u53ef\u80fd\u4f1a\u5230\u82cf\u5dde\u8bbf\u5bfb\u5148\u4eba\u9057\u5b9d\uff0c\u53eb\u6211\u4eec\u7559\u610f\u3002\u53ef\u60dc\u4ed6\u6765\u53bb\u5306\u5306\uff0c\u6ca1\u6709\u8be6\u7ec6\u8bf4\u8d77 \u4f60\u7684\u5f62\u8c8c\uff0c\u6211\u4eec\u4ee5\u4e3a\u4f60\u4e5f\u50cf\u4ed6\u4e00\u6837\uff0c\u5728\u8499\u53e4\u591a\u5e74\u5df2\u662f\u80e1\u513f\u76f8\u8c8c\uff0c\u8c01\u77e5\u4f60\u6bd4\u6211\u4eec\u82cf\u676d\u7684\u5c11\u5e74\u5b50 \u5f1f\uff0c\u8fd8\u8981\u4fca\u79c0\u5f97\u591a\u3002\u201d\u8bf4\u5b8c\u4e4b\u540e\uff0c\u5ffd\u5730\u62bf\u5634\u4e00\u7b11\uff0c\u4f3c\u4e4e\u662f\u53d1\u89c9\u81ea\u5df1\u8bf4\u8bdd\u5b5f\u6d6a\uff0c\u4f46\u5374\u4e5f\u6ca1\u6709\u5bfb \u5e38\u5973\u513f\u5bb6\u7684\u7f9e\u6da9\u4e4b\u5bb9\u3002\u5f20\u4e39\u67ab\u5fc3\u4e2d\u6697\u7b11\uff1a\u6fb9\u53f0\u706d\u660e\u8c8c\u4f3c\u80e1\u513f\uff0c\u90a3\u662f\u56e0\u4e3a\u4ed6\u7684\u7956\u7236\u548c\u7236\u4eb2\u5a36\u7684 \u90fd\u662f\u80e1\u5987\uff0c\u5e76\u975e\u56e0\u4e3a\u5728\u8499\u53e4\u4f4f\u5f97\u4e45\u4e86\u76f8\u8c8c\u5c31\u4f1a\u53d8\u7684\uff0c\u53ef\u7b11\u8fd9\u5c11\u5973\u5929\u771f\u672a\u51ff\uff0c\u8fde\u8fd9\u4e2a\u9053\u7406\u90fd\u4e0d \u61c2\u5f97\u3002 \u3000\u3000\u8fd9\u5c11\u5973\u53c8\u9053\uff1a\u201c\u524d\u65e5\u4f60\u6765\u6e38\u5c71\u4e4b\u65f6\uff0c\u6211\u4eec\u5df2\u6709\u7591\u5fc3\uff0c\u53ea\u56e0\u6700\u8fd1\u6070\u5de7\u53d1\u751f\u4e00\u6869\u4e8b\u60c5\uff0c\u542c\u8bf4 \u6709\u4e00\u4e2a\u53db\u8d3c\u5077\u5230\u82cf\u5dde\u753b\u56fe\u7684\u526f\u672c\u731c\u7591\u5b9d\u85cf\u662f\u57cb\u5728\u5feb\u6d3b\u6797\u4e2d\uff0c\u534a\u6708\u6765\u4e0d\u65ad\u6709\u4eba\u5230\u5feb\u6d3b\u6797\u8e29\u63a2\uff0c \u6211\u4eec\u8fd9\u91cc\u7684\u79d8\u5bc6\u867d\u65e0\u5916\u4eba\u5f97\u77e5\uff0c\u4f46\u4e5f\u4e0d\u80fd\u4e0d\u5206\u5916\u63d0\u9632\u3002\u6240\u4ee5\u4f60\u524d\u65e5\u6765\u5230\u6b64\u5c71\u5468\u56f4\u5bdf\u770b\uff0c\u6211\u4eec \u8fd8\u4ee5\u4e3a\u4f60\u662f\u60f3\u6765\u76d7\u5b9d\u7684\u8d3c\u4eba\u5462\u3002\u201d \u3000\u3000\u5f20\u4e39\u67ab\u7b11\u9053\uff1a\u201c\u4f60\u770b\u6211\u7684\u76f8\u8c8c\u50cf\u5f3a\u76d7\u5417\uff1f\u201d\u5c11\u5973\u9053\uff1a\u201c\u5c31\u662f\u56e0\u4e3a\u4e0d\u50cf\uff0c\u8981\u4e0d\u7136\u4f60\u54ea\u91cc\u8fd8 \u6709\u6027\u547d\u3002\u6211\u7239\u7239\u542c\u4f60\u8c08\u5410\u98ce\u96c5\uff0c\u6478\u4e0d\u6e05\u4f60\u7684\u6765\u5386\u3002\u60f3\u8bd5\u63a2\u4f60\u662f\u4e0d\u662f\u5c11\u4e3b\uff0c\u53c8\u6015\u4e07\u4e00\u4e0d\u662f\uff0c\u8fd9 \u5929\u5927\u7684\u79d8\u5bc6\uff0c\u5c31\u8981\u6cc4\u51fa\u53bb\u3002\u6240\u4ee5\u53ea\u597d\u5b81\u6789\u6bcb\u7eb5\u5c06\u4f60\u56f0\u5728\u516b\u9635\u4e2d\uff0c\u4f46\u53c8\u6015\u8bef\u4f24\u597d\u4eba\uff0c\u6240\u4ee5\u624b\u4e0b \u7559\u60c5\uff0c\u8981\u4e0d\u7136\u4f60\u867d\u7136\u8bc6\u7834\u9635\uff0c\u6050\u4e5f\u4e0d\u6613\u95ef\u5f97\u51fa\u53bb\u3002\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u540e\u6765\u4f60\u4eec\u53c8\u600e\u6837\u8bc6\u7a7f\u6211\u7684 \u6765\u5386\u7684\u5462\uff1f\u201d\u90a3\u5c11\u5973\u7b11\u9053\uff1a\u201c\u666e\u5929\u4e4b\u4e0b\uff0c\u9664\u4e86\u4f60\u4e00\u4eba\u4e4b\u5916\uff0c\u8fd8\u6709\u8c01\u80fd\u591f\u4ece\u5916\u9762\u5f00\u542f\u8fd9\u4e2a\u7389 \u95e8\uff1f\u201d\u5f20\u4e39\u67ab\u4e5f\u7b11\u9053\uff1a\u201c\u666e\u5929\u4e4b\u4e0b\uff0c\u9664\u4e86\u4f60\u4e00\u4eba\u4e4b\u5916\uff0c\u4e5f\u6ca1\u6709\u8c01\u80fd\u591f\u6551\u6211\u51fa\u6765\u3002\u201d\u90a3\u5c11\u5973\u9887 \u6709\u5f97\u610f\u4e4b\u8272\uff0c\u7b11\u9053\uff1a\u201c\u53ef\u4e0d\u6b63\u662f\uff1f\u8fd9\u4e24\u628a\u91d1\u9501\u5319\u5c31\u8fd9\u4e48\u5de7\uff0c\u6211\u8fd9\u628a\u5f00\u4e0d\u8fdb\u53bb\uff0c\u4f60\u8fd9\u628a\u5f00\u4e0d\u51fa \u6765\u3002\u201d\u8bf4\u5230\u6b64\u5904\u9762\u4e0a\u5ffd\u7136\u98de\u8d77\u4e00\u9635\u7ea2\u6655\uff0c\u539f\u6765\u5979\u5c0f\u65f6\u542c\u5988\u5988\u8bf4\u8fc7\u8fd9\u6837\u7684\u4e00\u53e5\u8bdd\uff1a\u59fb\u7f18\u5339\u914d\u6709 \u5982\u9501\u5319\u5f00\u9501\u4e00\u628a\u9501\u5319\u4e00\u628a\u9501\uff0c\u4e1d\u6beb\u4e0d\u80fd\u52c9\u5f3a\u3002\u5979\u65e0\u610f\u4e4b\u4e2d\u8bf4\u51fa\u9501\u5319\u5f00\u9501\u7684\u8bdd\uff0c\u60f3\u8d77\u4e86\u6bcd\u4eb2\u4e4b \u8a00\uff0c\u4e0d\u89c9\u7f9e\u7ea2\u4e86\u8138\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u751a\u662f\u7eb3\u7f55\uff0c\u4e0d\u660e\u8fd9\u5c11\u5973\u4f55\u4ee5\u5ffd\u7136\u4e4b\u95f4\u5ff8\u5ff8\u4f5c\u6001\uff0c\u54b3\u4e86\u4e00\u58f0\u7b11\u9053\uff1a\u201c\u4f60\u7684\u59d3\u540d\u6211\u5df2 \u77e5\u9053\u4e09\u4e2a\u5b57\uff0c\u8fd8\u6709\u4e00\u4e2a\u5b57\u4e0d\u77e5\u9053\u5462\u3002\u201d\u90a3\u5c11\u5973\u9053\uff1a\u201c\u4f60\u770b\u6211\u53ef\u771f\u9ad8\u5174\u5f97\u50bb\u4e86\uff0c\u8fde\u59d3\u540d\u4e5f\u5fd8\u8bb0 \u544a\u8bc9\u4f60\uff0c\u6211\u53eb\u505a\u6fb9\u53f0\u955c\u660e\uff0c\u6211\u7239\u53eb\u505a\u6fb9\u53f0\u4ef2\u5143\uff0c\u6211\u7684\u592a\u7956\u53eb\u505a\u6fb9\u53f0\u5f52\u771f\uff0c\u662f\u4f60\u7956\u5f20\u7687\u5e1d\u624b\u4e0b \u7684\u5927\u5c06\u3002\u201d\u5f20\u4e39\u67ab\u7b11\u9053\uff1a\u201c\u4f60\u592a\u7956\u7684\u540d\u5b57\u6211\u77e5\u9053\u3002\u5982\u6b64\u8bf4\u6765\uff0c\u6211\u771f\u8981\u591a\u8c22\u4f60\u4eec\u4e00\u5bb6\u3002\u6fb9\u53f0\u5c06 \u519b\u968f\u6211\u4eec\u542b\u57a2\u5fcd\u8fb1\uff0c\u8fdc\u5904\u5f02\u56fd\uff0c\u4f5c\u5316\u5916\u4e4b\u6c11\u3002\u800c\u4f60\u4eec\u53c8\u4e3a\u6211\u5bb6\u5728\u8fd9\u4e2a\u5c71\u5934\u5b88\u4e86\u51e0\u4ee3\u3002\u201d\u6fb9\u53f0 \u955c\u660e\u7b11\u9053\uff1a\u201c\u5728\u8fd9\u91cc\u4f4f\u6709\u4ec0\u4e48\u4e0d\u597d\uff1f\u671d\u5915\u9762\u5bf9\u6e56\u5c71\uff0c\u4f60\u8fd8\u4e0d\u6ee1\u610f\u5417\uff1f\u201d\u5f20\u4e39\u67ab\u5fae\u5fae\u4e00\u7b11\uff0c\u6fb9 \u53f0\u955c\u660e\u5ffd\u7136\u201c\u554a\u54ce\u201d\u4e00\u58f0\uff0c\u53eb\u4e86\u8d77\u6765\uff0c\u9053\uff1a\u201c\u4f60\u77a7\uff0c\u6211\u53c8\u5fd8\u8bb0\u4e86\u4e00\u4ef6\u4e8b\u3002\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u5fd8 \u8bb0\u4ec0\u4e48\uff1f\u201d\u6fb9\u53f0\u955c\u660e\u9053\uff1a\u201c\u5fd8\u8bb0\u4f60\u56f0\u5728\u6d1e\u4e2d\u5df2\u7ecf\u662f\u4e00\u5929\u4e00\u591c\u4e86\u3002\u4f60\u77a7\uff0c\u6211\u7ed9\u4f60\u5e26\u4e86\u597d\u4e1c\u897f\u6765 \u5462\u3002\u201d\u8d70\u51fa\u6d1e\u53e3\uff0c\u5c06\u6401\u5728\u5730\u4e0a\u7684\u4e00\u4e2a\u5c0f\u82b1\u84dd\u63d0\u4e86\u8fdb\u6765\u3002\u84dd\u4e2d\u6709\u592a\u6e56\u6d1e\u5ead\u5c71\u7684\u540d\u679c\u767d\u6c99\u6787\u6777\uff0c \u8fd8\u6709\u5e72\u7cae\u8089\u812f\u3002\u5f20\u4e39\u67ab\u5148\u5403\u6787\u6777\uff0c\u540e\u56bc\u8089\u812f\uff0c\u771f\u89c9\u662f\u5e73\u751f\u4ece\u6240\u672a\u8d4f\u7684\u5999\u54c1\u3002 \u3000\u3000\u6fb9\u53f0\u955c\u660e\u5728\u6d1e\u4e2d\u4e1c\u77a7\u897f\u671b\uff0c\u628a\u73a9\u73e0\u5b9d\uff0c\u7b11\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u53e4\u5f80\u4eca\u6765\uff0c\u8bb8\u591a\u4eba\u60f3\u505a\u7687\u5e1d\u3002\u4f60 \u7684\u592a\u7956\u4e0d\u8fc7\u505a\u4e86\u51e0\u5e74\u7687\u5e1d\uff0c\u5c31\u79ef\u4e0b\u4e86\u8fd9\u4e48\u591a\u597d\u73a9\u7684\u4e1c\u897f\u3002\u201d\u628a\u51e0\u7c92\u591c\u660e\u73e0\u629b\u4e0a\u629b\u843d\uff0c\u50cf\u5c0f\u5b69 \u5b50\u73a9\u73a9\u5177\u4f3c\u7684\uff0c\u5ffd\u800c\u53c8\u7b11\u9053\uff1a\u201c\u8fd9\u4e9b\u4e1c\u897f\u786e\u662f\u597d\u73a9\u3002\u53ef\u662f\u65e2\u4e0d\u80fd\u6b62\u9965\uff0c\u53c8\u4e0d\u80fd\u6b62\u6e34\uff0c\u6211\u770b \u5440\uff0c\u8fd9\u4e9b\u73e0\u5b50\u8fd8\u4e0d\u5982\u6211\u7684\u6787\u6777\u3002\u201d\u5f20\u4e39\u67ab\u7b11\u9053\uff1a\u201c\u6240\u4ee5\u5440\uff0c\u6211\u5b81\u613f\u8981\u4f60\u7684\u6787\u6777\uff0c\u4e0d\u8981\u8fd9\u4e9b\u73e0 \u5b50\u3002\u201d\u6fb9\u53f0\u955c\u660e\u9053\uff1a\u201c\u4f60\u8bf4\u5f97\u597d\u542c\uff0c\u4f60\u82e5\u4e0d\u8981\u8fd9\u4e9b\u73e0\u5b9d\uff0c\u4e3a\u4f55\u5192\u4e86\u8fd9\u822c\u5927\u7684\u5371\u9669\uff0c\u4ece\u8499\u53e4\u4e00 \u76f4\u8dd1\u5230\u592a\u6e56\u6765\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u6211\u8981\u628a\u8fd9\u4e9b\u73e0\u5b9d\uff0c\u5c3d\u6570\u9001\u7ed9\u522b\u4eba\u3002\u201d\u6fb9\u53f0\u955c\u660e\u9053\uff1a\u201c\u9001\u4e0e\u4f55 \u4eba\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u9001\u4e0e\u660e\u671d\u7684\u7687\u5e1d\u3002\u201d\u6fb9\u53f0\u955c\u660e\u53eb\u9053\uff1a\u201c\u4ec0\u4e48\uff0c\u9001\u4e0e\u660e\u671d\u7684\u7687\u5e1d\uff1f\u660e\u671d\u7684 \u7687\u5e1d\u4e0d\u662f\u4f60\u5bb6\u7684\u5927\u4ec7\u4eba\u5417\uff1f\u201d \u3000\u3000\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u660e\u671d\u7684\u7687\u5e1d\u662f\u6211\u5bb6\u7684\u5927\u4ec7\u4eba\u3002\u201d\u6fb9\u53f0\u955c\u660e\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u8fd8\u8981\u5c06\u73e0\u5b9d \u9001\u4e0e\u4ed6\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u662f\u8981\u9001\u4e0e\u4ed6\u3002\u201d\u6fb9\u53f0\u955c\u660e\u9053\uff1a\u201c\u54fc\uff0c\u4e0d\u884c\uff0c\u4e0d\u884c\uff01\u73e0\u5b9d\u867d \u7136\u662f\u4f60\u4eec\u5f20\u5bb6\u7684\uff0c\u6211\u4eec\u66ff\u4f60\u5b88\u4e86\u51e0\u4ee3\uff0c\u4f60\u8981\u9001\u4e0e\u660e\u671d\u7687\u5e1d\uff0c\u53ef\u5f97\u95ee\u8fc7\u6211\u4eec\u3002\u201d\u5f20\u4e39\u67ab\u9053\uff1a \u201c\u6211\u4e00\u8bf4\u4f60\u4eec\u51c6\u4f1a\u540c\u610f\u3002\u201d\u4fbf\u5c06\u4ed6\u4e3a\u56fd\u7684\u82e6\u5fc3\u548c\u62b1\u8d1f\u8bf4\u4e86\u3002\u6fb9\u53f0\u955c\u660e\u7b11\u9053\uff1a\u201c\u54c8\uff0c\u539f\u6765\u5e76\u4e0d \u662f\u9001\u7ed9\u660e\u671d\u7687\u5e1d\uff0c\u662f\u9001\u7ed9\u6253\u9791\u5b50\u7684\u4eba\uff0c\u6211\u5012\u7ed9\u4f60\u5413\u4e86\u4e00\u8df3\u3002\u201d \u3000\u3000\u5f20\u4e39\u67ab\u628a\u534a\u84dd\u6787\u6777\u5403\u5b8c\uff0c\u6fb9\u53f0\u955c\u660e\u4ecd\u662f\u7559\u5728\u6d1e\u4e2d\u548c\u4ed6\u8bf4\u8bdd\u597d\u50cf\u5fd8\u8bb0\u4e86\u5916\u9762\u8fd8\u6709\u4eba\u5728\u7b49\u5f85 \u4ed6\u4eec\u7684\u6d88\u606f\u4f3c\u7684\u3002\u5f20\u4e39\u67ab\u4ece\u5979\u7684\u8bdd\u4e2d\u4e5f\u77e5\u9053\u4e86\u8bb8\u591a\u5173\u4e8e\u6fb9\u53f0\u4e00\u5bb6\u7684\u4e8b\u60c5\u3002 \u3000\u3000\u539f\u6765\u5f20\u58eb\u8bda\u5728\u8d25\u4ea1\u7684\u524d\u5915\uff0c\u5c06\u9057\u5b64\u6258\u4e0e\u6fb9\u53f0\u5f52\u771f\u3002\u90a3\u6fb9\u53f0\u706d\u660e\u7684\u7956\u7236\uff0c\u8fdc\u8d70\u8499\u53e4\uff0c\u5c06\u5feb \u6d3b\u6797\u7684\u201c\u85cf\u5b9d\u56fe\u201d\u6258\u4e0e\u4e00\u4e2a\u59d3\u77f3\u7684\u5fc3\u8179\u6b66\u58eb\uff0c\u5373\u8f70\u5929\u96f7\u77f3\u82f1\u7684\u7956\u5148\uff0c\u53c8\u6697\u4e2d\u8bf7\u6fb9\u53f0\u5f52\u771f\u7684\u5f1f \u5f1f\u5373\u6fb9\u53f0\u955c\u660e\u7684\u7956\u7236\u9547\u5b88\u5728\u897f\u6d1e\u5ead\u5c71\uff0c\u6697\u62a4\u5b9d\u85cf\uff0c\u5e76\u7559\u4e0b\u4e86\u4e00\u679a\u53ea\u80fd\u4ece\u91cc\u9762\u5f00\u51fa\u6765\u7684\u91d1\u9501 \u5319\uff0c\u5e03\u7f6e\u53ef\u7b97\u5341\u5206\u5468\u5bc6\u3002\u6392\u8d77\u8f88\u5206\uff0c\u6fb9\u53f0\u706d\u660e\u548c\u6fb9\u53f0\u955c\u660e\u662f\u5802\u5144\u59b9\uff0c\u4f46\u4e24\u652f\u4eba\u4e00\u5728\u6f20\u5317\uff0c\u4e00 \u5728\u6c5f\u5357\u5374\u662f\u51e0\u4ee3\u4e0d\u901a\u97f3\u8baf\uff0c\u76f4\u5230\u4e0a\u4e00\u4e2a\u6708\uff0c\u6fb9\u53f0\u706d\u660e\u4e58\u7740\u62a4\u9001\u756a\u738b\u4e4b\u4fbf\uff0c\u5077\u5077\u6e9c\u5230\u592a\u6e56\u4e00 \u884c\uff0c\u4ed6\u4eec\u624d\u77e5\u9053\u201c\u8001\u4e3b\u516c\u201d\uff08\u5f20\u58eb\u8bda\uff09\u5df2\u7ecf\u5728\u8499\u53e4\u7559\u4e0b\u4e86\u540e\u4ee3\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u89c1\u5979\u7b11\u8bed\u76c8\u76c8\uff0c\u5728\u73e0\u5149\u5b9d\u6c14\u6620\u7167\u4e4b\u4e0b\u5206\u5916\u59a9\u5a9a\uff0c\u5fc3\u4e2d\u4e00\u52a8\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u7684\u5c0f\u5144\u5f1f \u89c1\u4e86\u4f60\u4e00\u5b9a\u4f1a\u6b22\u559c\u4f60\u3002\u201d\u6fb9\u53f0\u955c\u660e\u8bf4\uff1a\u201c\u4ec0\u4e48\uff0c\u4f60\u7684\u5c0f\u5144\u5f1f\uff1f\u6211\u4e3a\u4ec0\u4e48\u8981\u4ed6\u6b22\u559c\uff1f\u201d\u5f20\u4e39\u67ab \u7b11\u9053\uff1a\u201c\u6211\u7684\u5c0f\u5144\u5f1f\u81ea\u5e7c\u5931\u4e86\u4eb2\u4eba\uff0c\u5b64\u82e6\u4f36\u4ec3\uff0c\u6ca1\u6709\u4eba\u548c\u5979\u73a9\uff0c\u4f60\u548c\u5979\u4e00\u822c\u5e74\u7eaa\uff0c\u4e0d\u6b63\u662f\u53ef \u4ee5\u505a\u4e2a\u6700\u597d\u7684\u670b\u53cb\u5417\uff1f\u201d\u6fb9\u53f0\u955c\u660e\u6012\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u8981\u6211\u966a\u4f60\u7684\u5c0f\u5144\u5f1f\u73a9\uff1f\u54fc\uff0c\u6211\u4e0d\u559c\u6b22\u548c\u81ed \u5c0f\u5b50\u73a9\uff01\u201d\u5176\u5b9e\u5f20\u4e39\u67ab\u4e5f\u662f\u201c\u81ed\u5c0f\u5b50\u201d\uff0c\u6fb9\u53f0\u955c\u660e\u4e00\u8bf4\u4e4b\u540e\uff0c\u7acb\u523b\u53c8\u53d1\u73b0\u81ea\u5df1\u8bf4\u8bdd\u7684\u7834\u7efd\uff0c \u4e0d\u89c9\u9762\u4e0a\u53c8\u6cdb\u8d77\u7ea2\u6f6e\u3002\u53ea\u542c\u5f97\u5f20\u4e39\u67ab\u7b11\u9053\uff1a\u201c\u6211\u7684\u5c0f\u5144\u5f1f\u4e0d\u662f\u81ed\u5c0f\u5b50\u3002\u201d\u6fb9\u53f0\u955c\u660e\u9053\uff1a\u201c\u4e0d \u662f\u81ed\u5c0f\u5b50\u662f\u9999\u5c0f\u5b50\u5440\u3002\u54fc\uff0c\u9999\u5c0f\u5b50\u6211\u4e5f\u4e0d\u559c\u6b22\u3002\u201d\u5f20\u4e39\u67ab\u7b11\u9053\uff1a\u201c\u4e5f\u4e0d\u662f\u9999\u5c0f\u5b50\uff0c\u5979\u5440\uff0c\u5979 \u662f\u4e00\u4f4d\u5c0f\u59d1\u5a18\u3002\u201d\u6fb9\u53f0\u955c\u660e\u4e00\u6014\uff0c\u9053\uff1a\u201c\u662f\u5c0f\u59d1\u5a18\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u662f\u5440\uff0c\u662f\u5c0f\u59d1\u5a18\u3002\u6211\u8ba4 \u8bc6\u5979\u65f6\uff0c\u5979\u5973\u626e\u7537\u88c5\uff0c\u6211\u53eb\u60ef\u4e86\u5979\u5c0f\u5144\u5f1f\uff0c\u8001\u662f\u6539\u4e0d\u8fc7\u53e3\u6765\u3002\u201d\u6fb9\u53f0\u955c\u660e\u89c1\u4ed6\u63d0\u8d77\u201c\u5c0f\u5144 \u5f1f\u201d\u65f6\uff0c\u8bf4\u5f97\u5341\u5206\u4eb2\u70ed\uff0c\u4e0d\u77e5\u600e\u7684\uff0c\u5fc3\u5934\u7a81\u7136\u6709\u4e00\u79cd\u9178\u6e9c\u6e9c\u7684\u611f\u89c9\uff0c\u7adf\u662f\u5e73\u751f\u4ece\u672a\u6709\u8fc7\u7684\u611f \u89c9\uff0c\u4f46\u4e5f\u662f\u4e00\u63a0\u5373\u8fc7\uff0c\u9762\u4e0a\u5e76\u6ca1\u6709\u73b0\u51fa\u4ec0\u4e48\uff0c\u53ef\u662f\u5f20\u4e39\u67ab\u5df2\u4f3c\u5bdf\u89c9\u4e86\u4ec0\u4e48\uff0c\u5fc3\u4e2d\u5bf9\u8fd9\u5c11\u5973\u9887 \u611f\u6b49\u610f\u3002 \u3000\u3000\u4e24\u4eba\u505c\u4e0b\u8bdd\u6765\uff0c\u8fc7\u4e86\u534a\u664c\uff0c\u5f20\u4e39\u67ab\u5ffd\u4f3c\u8bb0\u8d77\u4e00\u4e8b\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u7684\u7239\u7239\u4e3a\u4f55\u4e0d\u4e0b\u6765\uff1f\u201d\u6fb9 \u53f0\u955c\u660e\u9053\uff1a\u201c\u4ed6\u53d1\u73b0\u6709\u654c\u4eba\u4e0a\u5c71\u60f3\u5fc5\u662f\u53bb\u5e03\u7f6e\u516b\u9635\u56fe\u4e86\u3002\u201d\u8bf4\u5f97\u6beb\u4e0d\u5728\u4e4e\u3002\u5f20\u4e39\u67ab\u60ca\u9053\uff1a \u201c\u82e5\u6709\u654c\u4eba\u4e0a\u5c71\uff0c\u5c31\u5fc5\u5b9a\u662f\u624e\u624b\u7684\u5f3a\u654c\uff0c\u54b1\u4eec\u5feb\u51fa\u53bb\u77a7\uff01\u201d \u3000\u3000\u6fb9\u53f0\u955c\u660e\u9053\uff1a\u201c\u4ec0\u4e48\u624e\u624b\u7684\u5f3a\u654c\uff0c\u6599\u4e5f\u95ef\u4e0d\u8fc7\u6211\u7239\u624b\u4e2d\u7684\u6e14\u53c9\uff0c\u95ef\u5f97\u8fc7\u7239\u7239\u624b\u4e2d\u7684\u6e14 \u53c9\uff0c\u4e5f\u95ef\u4e0d\u8fc7\u90a3\u4e2a\u77f3\u9635\u3002\u201d\u5979\u5bf9\u7239\u7239\u7684\u6b66\u529f\u4e0e\u516b\u9635\u56fe\u7adf\u662f\u5341\u5206\u4fe1\u8d56\u3002\u5f20\u4e39\u67ab\u5fc3\u9053\uff1a\u201c\u5440\uff0c\u4f60 \u8fd9\u5c0f\u59ae\u5b50\u54ea\u91cc\u77e5\u9053\u5929\u5916\u6709\u5929\uff0c\u4eba\u5916\u6709\u4eba\u3002\u4eca\u756a\u6765\u7684\u654c\u4eba\u82e5\u975e\u5927\u5185\u9ad8\u624b\u5c31\u5b9a\u662f\u7ea2\u53d1\u5996\u9f99\u90a3\u73ed\u90aa \u9b54\u52b2\u654c\u3002\u201d\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u8fd8\u662f\u53bb\u77a7\u77a7\u7684\u597d\u3002\u201d\u6fb9\u53f0\u955c\u660e\u9053\uff1a\u201c\u597d\uff0c\u53bb\u5c31\u53bb\u5427\u3002\u201d\u4e0e\u5f20\u4e39\u67ab\u8d70 \u51fa\u77f3\u6d1e\u5173\u4e86\u7389\u95e8\uff0c\u901a\u8fc7\u96a7\u9053\uff0c\u6d1e\u53e3\u6302\u6709\u4e00\u6839\u957f\u7ef3\uff0c\u4e24\u4eba\u6500\u63f4\u800c\u4e0a\uff0c\u5916\u9762\u4e00\u7247\u707f\u70c2\u7684\u9633\u5149\uff0c\u770b \u5149\u5f71\u5df2\u662f\u6b63\u5348\u65f6\u5206\u3002 \u3000\u3000\u628a\u773c\u4e00\u671b\uff0c\u6d1e\u5ead\u5c71\u5e84\u5e84\u95e8\u7d27\u95ed\uff0c\u5c71\u8170\u7684\u4e71\u77f3\u4e1b\u4e2d\u4eba\u5f71\u5e62\u5e62\u4f20\u51fa\u4e86\u4e00\u9635\u9635\u5175\u5668\u7684\u5267\u70c8\u78b0\u51fb \u4e4b\u58f0\uff0c\u5f20\u4e39\u67ab\u6025\u5fd9\u52a0\u5feb\u811a\u6b65\uff0c\u8d76\u53bb\u52a9\u9635\u3002\u6fb9\u53f0\u955c\u660e\u9053\uff1a\u201c\u4f60\u6025\u4ec0\u4e48\uff1f\u6211\u7684\u5988\u5988\u548c\u59b9\u59b9\u90fd\u6765 \u4e86\uff0c\u8fd8\u6015\u5b83\u4ec0\u4e48\u5f3a\u654c\u3002\u201d\u5f20\u4e39\u67ab\u6628\u665a\u5230\u6d1e\u5ead\u5c71\u5e84\u6295\u5bbf\uff0c\u5e76\u6ca1\u6709\u89c1\u7740\u5973\u4e3b\u4eba\uff0c\u8be7\u9053\uff1a\u201c\u554a\uff0c\u539f \u6765\u4f60\u8fd8\u6709\u5988\u5988\u3002\u201d\u6fb9\u53f0\u955c\u660e\u9053\uff1a\u201c\u6211\u600e\u4e48\u6ca1\u6709\u5988\u5988\uff0c\u4e0d\u8fc7\u5979\u4f4f\u5728\u5916\u9762\uff0c\u5341\u5929\u534a\u6708\u624d\u56de\u6765\u4e00 \u6b21\uff0c\u6211\u521a\u624d\u89c1\u5979\u4e0a\u5230\u534a\u5c71\uff0c\u624d\u4e0b\u6765\u6551\u4f60\u3002\u201d\u5f20\u4e39\u67ab\u751a\u611f\u5947\u602a\u60f3\u9053\uff1a\u201c\u653e\u7740\u8fd9\u6837\u597d\u7684\u4eba\u95f4\u4ed9\u5883 \u4e0d\u4f4f\uff0c\u5374\u592b\u59bb\u5206\u5f00\uff0c\u4f4f\u5728\u5916\u9762\uff0c\u5374\u662f\u4e3a\u4f55\u5462\uff1f\u201d\u4f46\u8fd9\u65f6\u6025\u7740\u52a9\u9635\uff0c\u65e0\u6687\u591a\u95ee\u3002 \u3000\u3000\u4e24\u4eba\u6765\u5230\u516b\u9635\u56fe\u524d\uff0c\u4e0d\u89c9\u5927\u5403\u4e00\u60ca\uff0c\u9635\u4e2d\u56f0\u4f4f\u7684\u654c\u4eba\uff0c\u7adf\u662f\u4e2a\u4e2a\u6b66\u529f\u9ad8\u5f3a\u3002\u5c24\u5176\u5389\u5bb3\u7684 \u662f\u4e00\u4e2a\u8001\u6c49\u548c\u4e00\u4e2a\u9053\u4eba\uff0c\u90a3\u8001\u6c49\u7684\u5175\u5668\u602a\u5f02\u4e4b\u6781\uff0c\u5f62\u4f3c\u9f99\u5934\u62d0\u6756\uff0c\u53ef\u53c8\u6bd4\u666e\u901a\u7684\u9f99\u5934\u62d0\u6756\u591a \u4e86\u4e24\u6837\u4e1c\u897f\uff0c\u4e00\u6837\u662f\u5728\u62d0\u6756\u7684\u5c16\u7aef\uff0c\u4f38\u51fa\u4e00\u4e2a\u5f62\u5982\u624b\u638c\u7684\u4e1c\u897f\uff0c\u4e94\u679d\u660e\u6643\u6643\u7684\u5229\u94a9\uff0c\u6709\u5982\u624b \u6307\uff1b\u53e6\u4e00\u6837\u662f\u62d0\u6756\u4e0a\u957f\u6ee1\u5c16\u523a\uff0c\u821e\u52a8\u8d77\u6765\u6709\u5982\u6bdb\u8338\u8338\u7684\u733f\u81c2\uff0c\u4f5c\u52bf\u652b\u4eba\u3002\u90a3\u9053\u4eba\u7684\u5175\u5668\uff0c\u5374 \u662f\u4e00\u67c4\u957f\u5251\uff0c\u867d\u4e0d\u602a\u5f02\uff0c\u4f46\u62bd\u523a\u4e4b\u9645\uff0c\u98de\u8d77\u4e00\u6735\u6735\u5251\u82b1\u66f4\u662f\u9a87\u4eba\u3002\u53e6\u6709\u4e00\u4e2a\u5c11\u5e74\u519b\u5b98\uff0c\u638c\u98ce \u864e\u864e\uff0c\u77f3\u9635\u4e2d\u8f83\u5c0f\u7684\u77f3\u5757\uff0c\u7adf\u7136\u7ed9\u4ed6\u7684\u638c\u529b\u9707\u5f97\u98de\u9707\u8d77\u6765\u3002\u6fb9\u53f0\u955c\u660e\u518d\u4ed4\u7ec6\u77a7\u65f6\uff0c\u53ea\u89c1\u81ea\u5df1 \u7684\u7239\u7239\u867d\u7136\u628a\u5b88\u7740\u6b7b\u95e8\u8981\u6237\uff0c\u4f46\u662f\u5728\u5f3a\u654c\u56f4\u653b\u4e4b\u4e0b\uff0c\u9635\u52bf\u65bd\u5c55\u4e0d\u5f00\u3002 \u3000\u3000\u6fb9\u53f0\u955c\u660e\u4e00\u58f0\u5a07\u53f1\uff0c\u62d4\u51fa\u5229\u5251\uff0c\u5c31\u5f85\u95ef\u5165\u77f3\u9635\uff0c\u5ffd\u89c1\u5f20\u4e39\u67ab\u5b9a\u7740\u53cc\u775b\uff0c\u5982\u75f4\u4f3c\u5446\uff0c\u5140\u7acb \u4e0d\u52a8\u3002\u6fb9\u53f0\u955c\u660e\u55d4\u9053\uff1a\u201c\u4f60\u8fd9\u4eba\u662f\u600e\u4e48\u7684\uff1f\u521a\u624d\u90a3\u4e48\u7740\u6025\uff0c\u73b0\u5728\u5374\u53c8\u4e0d\u4e0a\u524d\u53bb\u52a9\u6211\u7684\u7239\u7239\uff0c \u4f60\u7b49\u4ec0\u4e48\uff1f\u201d\u5f20\u4e39\u67ab\u6697\u53eb\u7cdf\u7cd5\uff0c\u539f\u6765\u90a3\u8001\u6c49\u4e0e\u9053\u4eba\u6b63\u662f\u94c1\u81c2\u91d1\u733f\u9f99\u9547\u65b9\u4e0e\u4e09\u82b1\u5251\u7384\u7075\u5b50\uff0c\u8fd9 \u4e24\u4eba\u4e5f\u8fd8\u7f62\u4e86\uff0c\u90a3\u5c11\u5e74\u519b\u5b98\u5374\u662f\u4e91\u857e\u7684\u54e5\u54e5\uff0c\u65b0\u4e2d\u6069\u79d1\u6b66\u72b6\u5143\u7684\u4e91\u857e\u3002\u770b\u4e24\u8fb9\u6597\u5f97\u5982\u6b64\u6fc0 \u70c8\uff0c\u53ea\u6015\u4f1a\u6709\u6b7b\u4f24\u3002\u5f20\u4e39\u67ab\u5fc3\u9053\uff1a\u201c\u6211\u867d\u7136\u6697\u52a9\u4e91\u91cd\u4e2d\u4e86\u6069\u79d1\u72b6\u5143\uff0c\u53ea\u662f\u4ed6\u5fc3\u4e2d\u5bf9\u6211\u7684\u654c\u610f \u5b9e\u672a\u6d88\u9664\uff0c\u8bf4\u660e\u771f\u76f8\uff0c\u4ed6\u53c8\u4e0d\u80af\u76f8\u4fe1\uff0c\u5982\u4f55\u662f\u597d\uff1f\u6211\u82e5\u7136\u4e0a\u524d\u4e0e\u4ed6\u52a8\u624b\uff0c\u5c82\u4e0d\u8bef\u4f1a\u66f4\u6df1\uff1f\u201d \u5ffd\u89c1\u4e09\u82b1\u5251\u7384\u7075\u5b50\u7a81\u5c55\u7edd\u62db\uff0c\u5251\u82b1\u6735\u6735\u5411\u628a\u5b88\u675c\u95e8\u7684\u4e00\u4e2a\u8001\u5a46\u5a46\u6740\u53bb\uff0c\u90a3\u8001\u5a46\u5a46\u624b\u4f7f\u62d0\u6756\uff0c \u547c\u547c\u8fd8\u4e86\u4e24\u62db\uff0c\u4e91\u91cd\u5ffd\u7136\u8fde\u53d1\u4e09\u638c\uff0c\u52a9\u7384\u7075\u5b50\u5c06\u90a3\u8001\u5a46\u5a46\u903c\u5f97\u9000\u51fa\u4e86\u675c\u95e8\uff0c\u5f20\u4e39\u67ab\u53c8\u662f\u4e00 \u60ca\uff01 \u3000\u3000\u53e6\u4e00\u5b88\u5728\u60ca\u95e8\u7684\u5c11\u5973\u4e5f\u7ed9\u654c\u4eba\u903c\u5f97\u624b\u5fd9\u811a\u4e71\u3002\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u8fd9\u4e24\u4eba\u662f\u4f60\u7684\u5988\u5988\u548c\u59b9\u59b9 \u5417\uff1f\u201d\u6fb9\u53f0\u955c\u660e\u6012\u9053\uff1a\u201c\u600e\u4e48\uff0c\u4f60\u8fd8\u7b49\u4ec0\u4e48\uff1f\u201d\u8bf4\u8bdd\u4e4b\u95f4\u5df2\u5954\u51fa\u6570\u4e08\u4e4b\u5730\uff0c\u5f20\u4e39\u67ab\u4e00\u7b11\u9053\uff1a \u201c\u539f\u6765\u90fd\u662f\u719f\u4eba\uff01\u201d\u8eab\u5f62\u4e00\u8d77\uff0c\u500f\u5730\u62a2\u8fc7\u4e86\u6fb9\u53f0\u955c\u660e\u7684\u524d\u5934\uff0c\u5148\u5165\u77f3\u9635\uff0c\u957f\u5251\u4e00\u6307\uff0c\u53eb\u9053\uff1a \u201c\u6fb9\u53f0\u5927\u5a18\uff0c\u5b88\u7d27\u675c\u95e8\uff0c\u7389\u660e\u59b9\u5b50\uff0c\u8f6c\u8fc7\u4f11\u95e8\uff0c\u6211\u6765\u4e5f\uff01\u201d\u7eb5\u8eab\u4e00\u8dc3\uff0c\u63a0\u8fc7\u94c1\u81c2\u91d1\u733f\u7684\u5934 \u9876\uff0c\u5954\u5165\u751f\u95e8\uff0c\u4e0e\u6d1e\u5ead\u5e84\u4e3b\u6fb9\u53f0\u4ef2\u5143\u5e76\u80a9\u4e00\u7acb\uff0c\u5b88\u7a33\u4e86\u516b\u9635\u56fe\u7684\u95e8\u6237\u3002 \u3000\u3000\u539f\u6765\u4e91\u91cd\u90a3\u665a\u5728\u5feb\u6d3b\u6797\u4e00\u65e0\u6240\u5f97\uff0c\u53cd\u7ed9\u5f20\u4e39\u67ab\u7559\u5b57\u5632\u7b11\uff0c\u81ea\u662f\u4e0d\u80af\u7f62\u4f11\u3002\u5176\u5b9e\u5f20\u4e39\u67ab\u662f \u597d\u610f\u529d\u4ed6\uff0c\u4ed6\u5374\u5f53\u4e3a\u5632\u7b11\uff0c\u5f53\u4e0b\u6068\u6068\u7136\u56de\u8f6c\u629a\u8859\u3002\u7b2c\u4e8c\u65e5\u4eac\u4e2d\u7684\u4e03\u5927\u9ad8\u624b\u90fd\u5df2\u4f1a\u9f50\uff0c\u63a2\u51fa\u5f20 \u4e39\u67ab\u5df2\u8fdb\u4e86\u592a\u6e56\uff0c\u4e8e\u662f\u4e03\u5927\u9ad8\u624b\uff0c\u8fde\u540c\u4e91\u91cd\uff0c\u5171\u662f\u516b\u4eba\uff0c\u6025\u6025\u8ffd\u8e2a\u800c\u81f3\uff0c\u5c31\u5728\u5f20\u4e39\u67ab\u9677\u5165\u77f3 \u6d1e\u4e4b\u540e\u7684\u7b2c\u4e8c\u65e5\u65e5\u95f4\uff0c\u8ffd\u5230\u4e86\u897f\u6d1e\u5ead\u5c71\u5c71\u4e0a\u3002 \u3000\u3000\u6b63\u5728\u6ee1\u5c71\u641c\u7d22\uff0c\u5ffd\u542c\u5f97\u563f\u563f\u51b7\u7b11\u4e4b\u58f0\uff0c\u62ac\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u4e00\u4e2a\u6ee1\u5934\u767d\u53d1\u7684\u8001\u5a46\u5a46\uff0c\u626c\u7740\u4e00 \u9762\u9526\u7f0e\uff0c\u9526\u7f0e\u4e0a\u7ee3\u7740\u5341\u6735\u5927\u7ea2\u82b1\u5176\u4e2d\u4e03\u6735\u5468\u56f4\u56f4\u4ee5\u7ea2\u7ebf\uff0c\u5341\u5206\u523a\u76ee\u3002\u4e00\u4e2a\u4f8d\u536b\u5947\u9053\uff1a\u201c\u54a6\uff0c \u8fd9\u4e0d\u662f\u6fb9\u53f0\u6751\u8336\u4ead\u7684\u90a3\u4e2a\u8001\u59aa\u5417\uff1f\u5979\u7684\u5973\u513f\u5462\uff1f\u6211\u90a3\u65e5\u7ecf\u8fc7\u8336\u4ead\u6b63\u89c1\u5979\u7ee3\u8fd9\u9526\u7f0e\u4e0a\u7684\u7ea2 \u82b1\u3002\u201d\u53e6\u4e00\u4e2a\u5927\u5185\u9ad8\u624b\u9053\uff1a\u201c\u662f\u5440\uff0c\u90a3\u65e5\u6211\u7ecf\u8fc7\u82b1\u4ead\uff0c\u4e5f\u6b63\u89c1\u5979\u7ee3\u8fd9\u9526\u7f0e\u4e0a\u7684\u7ea2\u82b1\u3002\u5979\u8fd8\u8bf4 \u4ec0\u4e48\u8fd9\u662f\u7b2c\u5341\u6735\u3002\u201d\u4e91\u91cd\u5fc3\u4e2d\u4e00\u6014\uff0c\u60f3\u8d77\u81ea\u5df1\u90a3\u65e5\u79bb\u5f00\u8336\u4ead\u4e4b\u65f6\uff0c\u9526\u7f0e\u4e0a\u7684\u8fd8\u662f\u7b2c\u516b\u6735\u7ea2 \u82b1\uff0c\u5fd9\u95ee\u90a3\u4e24\u4e2a\u4f8d\u536b\u9053\uff1a\u201c\u4f60\u4eec\u90a3\u65e5\u662f\u4e0d\u662f\u5411\u5979\u4eec\u6253\u542c\u8fc7\u5f20\u4e39\u67ab\uff1f\u201d\u90a3\u4e24\u4e2a\u4f8d\u536b\u9053\uff1a\u201c\u662f \u5440\uff0c\u8fd9\u548c\u9526\u7f0e\u4e0a\u7684\u5927\u7ea2\u82b1\u53c8\u6709\u4ec0\u4e48\u5173\u7cfb\uff1f\u201d\u4e91\u91cd\u9053\uff1a\u201c\u8fd9\u4e2a\u8001\u5a46\u5a46\u5b9a\u662f\u5f20\u4e39\u67ab\u7684\u515a\u7fbd\uff01\u201d\u6025 \u6025\u98de\u8eab\u8ffd\u8d76\uff0c\u90a3\u8001\u5a46\u5a46\u53c8\u5c06\u9526\u7f0e\u4e00\u626c\uff0c\u9634\u607b\u607b\u7684\u8bf4\u9053\uff1a\u201c\u5440\uff0c\u53ef\u60dc\uff0c\u53ef\u60dc\uff01\u4f60\u4e5f\u6765\u4e86\uff01\u8fd9\u4e09 \u6735\u7ea2\u82b1\u4e5f\u8981\u7ed9\u660e\u513f\u6458\u4e0b\u6765\u4e86\u3002\u201d \u3000\u3000\u94c1\u81c2\u91d1\u733f\u5927\u6012\uff0c\u559d\u9053\uff1a\u201c\u5140\u4f60\u8fd9\u5996\u5987\uff0c\u88c5\u795e\u5f04\u9b3c\u3002\u201d\u7387\u5148\u4fbf\u8ffd\uff0c\u90a3\u8001\u5a46\u5a46\u8eab\u6cd5\u5947\u5feb\uff0c\u5de6 \u4e00\u515c\uff0c\u53f3\u4e00\u7ed5\uff0c\u4e0d\u6d88\u4e00\u76cf\u8336\u7684\u65f6\u523b\u5df2\u5c06\u4e91\u91cd\u4e0e\u5927\u5185\u4e03\u5927\u9ad8\u624b\uff0c\u90fd\u5e26\u5230\u4e86\u516b\u9635\u56fe\u524d\u9762\u3002\u4e91\u91cd\u89c1 \u4e71\u77f3\u5806\u53e0\uff0c\u6709\u5982\u91cd\u95e8\u53e0\u6237\uff0c\u5185\u4e2d\u9690\u6709\u715e\u6c14\uff0c\u4ed6\u867d\u4e0d\u8bc6\u516b\u9635\u56fe\uff0c\u5374\u6bd4\u90a3\u4e9b\u4eba\u591a\u8bfb\u8fc7\u51e0\u672c\u5175\u4e66\uff0c \u4e0d\u89c9\u4e00\u9635\u8e0c\u8e87\uff0c\u505c\u4e0b\u811a\u6b65\u3002\u5ffd\u89c1\u4e71\u77f3\u5806\u4e2d\uff0c\u73b0\u51fa\u4e00\u4e2a\u5c11\u5973\uff0c\u7b11\u9053\uff1a\u201c\u54c8\uff0c\u4f60\u4eec\u90fd\u6765\u4e86\u5417\uff1f\u4ed6 \u4eec\u7b49\u5019\u540c\u4f34\u5df2\u7b49\u5f97\u4e0d\u8010\u70e6\u4e86\u3002\u201d\u5c06\u624b\u4e00\u6307\uff0c\u53ea\u89c1\u5de6\u4fa7\u7684\u4e00\u5806\u77f3\u5806\u4e0a\u5e76\u5217\u7740\u4e03\u9897\u5934\u9885\uff0c\u4e0d\u77e5\u7528 \u4ec0\u4e48\u836f\u6c34\u70bc\u8fc7\uff0c\u9762\u76ee\u5c1a\u6829\u6829\u5982\u751f\u3002\u4e91\u91cd\u8ba4\u51fa\u5176\u4e2d\u4e00\u4eba\uff0c\u6b63\u662f\u90a3\u65e5\u7b56\u9a6c\u7ecf\u8fc7\u8336\u4ead\u7684\u90a3\u4e2a\u505c\u58eb\uff0c \u94c1\u81c2\u91d1\u733f\u4e0e\u4e09\u82b1\u5251\u4e5f\u8ba4\u51fa\u5176\u4e2d\u4e24\u4eba\u662f\u53f8\u793c\u592a\u76d1\u738b\u632f\u5e9c\u4e2d\u7684\u536b\u58eb\uff0c\u53e6\u4e00\u4e2a\u9ad8\u624b\u8ba4\u51fa\u4e00\u4eba\u662f\u6d77\u9f99 \u5e2e\u7684\u526f\u5e2e\u4e3b\uff0c\u60f3\u6765\u4ed6\u4eec\u90fd\u662f\u56e0\u4e3a\u6253\u542c\u5f20\u4e39\u67ab\u800c\u88ab\u8fd9\u4e24\u6bcd\u5973\u5272\u4e0b\u5934\u9885\u3002\u5927\u5185\u4e03\u5927\u9ad8\u624b\u90fd\u6fc0\u6012\uff0c \u6043\u7740\u827a\u9ad8\u80c6\u5927\u4e00\u9f50\u95ef\u5165\u4e86\u516b\u9635\u4e2d\uff0c\u4e91\u91cd\u8eab\u4e0d\u7531\u5df1\uff0c\u4e5f\u8ddf\u4f17\u4eba\u95ef\u5165\u77f3\u9635\u3002 \u3000\u3000\u77f3\u9635\u4e2d\u5f02\u58f0\u9aa4\u8d77\uff0c\u53ea\u89c1\u4e00\u4e2a\u8001\u8005\uff0c\u4e09\u7efa\u957f\u987b\uff0c\u63d0\u7740\u4e00\u628a\u6e14\u53c9\uff0c\u73b0\u51fa\u8eab\u6765\uff0c\u63a5\u7740\u73b0\u51fa\u51e0\u4e2a \u519c\u4eba\uff0c\u634f\u7684\u4e0d\u662f\u9504\u5934\uff0c\u5374\u662f\u5200\u67aa\u5251\u621f\uff0c\u5728\u4e71\u77f3\u5806\u4e2d\uff0c\u5ffd\u9690\u5ffd\u73b0\u3002\u94c1\u81c2\u91d1\u733f\u5927\u6012\uff0c\u559d\u9053\uff1a\u201c\u5148 \u628a\u8fd9\u8001\u513f\u64d2\u4e0b\u3002\u201d\u6d1e\u5ead\u5e84\u4e3b\u54c8\u54c8\u5927\u7b11\uff0c\u8fce\u9762\u5c31\u662f\u4e00\u53c9\uff0c\u94c1\u81c2\u91d1\u733f\u62d0\u6756\u4e00\u9707\uff0c\u6a2a\u51fb\u8fc7\u53bb\uff0c\u6d1e\u5ead \u5e84\u4e3b\u8eab\u5f62\u500f\u5ffd\u4e0d\u89c1\uff0c\u9661\u542c\u5f97\u8eab\u540e\u5229\u5203\u5288\u98ce\u4e4b\u58f0\uff0c\u90a3\u5c11\u5973\u624b\u4f7f\u53cc\u5200\uff0c\u4e00\u4e2a\u76d8\u65cb\uff0c\u4fbf\u4e0b\u6740\u7740\uff0c\u4e91 \u91cd\u547c\u7684\u4e00\u638c\u62cd\u51fa\uff0c\u90a3\u5c11\u5973\u53eb\u9053\uff1a\u201c\u597d\u5389\u5bb3\uff01\u201d\u8eab\u5b50\u4e00\u7f29\u53c8\u4e0d\u89c1\u4e86\uff0c\u4e09\u82b1\u5251\u7384\u7075\u5b50\u5c55\u5251\u4e00\u8ffd\uff0c \u90a3\u8001\u5a46\u5a46\u5ffd\u5730\u4e0d\u77e5\u4ece\u4ec0\u4e48\u5730\u65b9\u8df3\u51fa\uff0c\u5341\u6307\u5982\u94a9\uff0c\u671d\u7384\u7075\u5b50\u624b\u8155\u4e0e\u9876\u95e8\u53cc\u53cc\u6293\u4e0b\uff0c\u7adf\u7136\u662f\u5927\u529b \u9e70\u6293\u7684\u529f\u592b\u3002\u4e09\u82b1\u5251\u5fc3\u4e2d\u4e00\u51db\uff0c\u6025\u4f7f\u7edd\u62db\uff0c\u500f\u5730\u6296\u8d77\u4e09\u6735\u5251\u82b1\uff0c\u90a3\u8001\u5a46\u5a46\u4e00\u6293\u6293\u7a7a\uff0c\u7acb\u523b\u53c8 \u8f6c\u5165\u53e6\u4e00\u5904\u95e8\u6237\uff0c\u9635\u56fe\u5c55\u5f00\uff0c\u970e\u65f6\u95f4\uff0c\u5c06\u4e91\u91cd\u7b49\u516b\u4e2a\u4e00\u6d41\u9ad8\u624b\uff0c\u90fd\u56f0\u5728\u516b\u9635\u56fe\u4e2d\u3002 \u3000\u3000\u8fd9\u516b\u540d\u9ad8\u624b\u867d\u7136\u5404\u5404\u8eab\u6000\u7edd\u6280\uff0c\u4f46\u4e0d\u660e\u9635\u6cd5\uff0c\u654c\u4eba\u4e2a\u4e2a\u795e\u51fa\u9b3c\u6ca1\uff0c\u7adf\u7136\u88ab\u5206\u9694\u5f97\u9996\u5c3e\u4e0d \u80fd\u547c\u5e94\uff0c\u53ea\u6709\u6328\u6253\u7684\u4efd\u513f\u3002\u4e91\u91cd\u8f83\u6709\u673a\u8c0b\uff0c\u89c1\u4e0d\u662f\u8def\uff0c\u6025\u5fd9\u53eb\u9053\uff1a\u201c\u4ed6\u4eec\u5171\u662f\u516b\u4eba\uff0c\u54b1\u4eec\u4e5f \u662f\u516b\u4eba\uff0c\u5404\u81ea\u8ba4\u5b9a\u4e00\u4eba\uff0c\u4e0d\u8981\u4e71\u653b\u3002\u201d\u5982\u6b64\u4e00\u6765\uff0c\u5f62\u52bf\u6e10\u7a33\u3002\u90a3\u516b\u9635\u56fe\u867d\u662f\u5947\u5999\u65e0\u6bd4\uff0c\u6d1e\u5ead \u5e84\u4e3b\u5374\u53ea\u8bc6\u5f97\u4e09\u6210\uff0c\u5c1a\u672a\u80fd\u5c3d\u91cf\u53d1\u6325\uff0c\u52a0\u4ee5\u9664\u4e86\u4ed6\u592b\u59bb\u4e8c\u4eba\u529f\u529b\u6700\u9ad8\uff0c\u53ef\u4e0e\u4e91\u91cd\u7b49\u4eba\u5339\u654c\u4e4b \u5916\uff0c\u5176\u4ed6\u516d\u4eba\u548c\u5927\u5185\u7684\u4f17\u9ad8\u624b\u5374\u662f\u76f8\u5dee\u751a\u8fdc\uff0c\u8fd9\u4e00\u6765\u4e00\u8fb9\u4ed7\u7740\u9635\u56fe\u5965\u5999\uff0c\u4e00\u8fb9\u4ed7\u7740\u5b9e\u529b\u9ad8 \u5f3a\uff0c\u5728\u77f3\u9635\u4e4b\u4e2d\u6740\u5f97\u96be\u89e3\u96be\u5206\uff0c\u53cc\u65b9\u90fd\u662f\u9669\u62db\u8fed\u89c1\u3002 \u3000\u3000\u6b63\u5728\u6fc0\u6218\u4e4b\u9645\uff0c\u4e91\u91cd\u6e10\u6e10\u770b\u51fa\u7834\u7efd\uff0c\u6b63\u5728\u4e0e\u94c1\u81c2\u91d1\u733f\u5408\u529b\u903c\u8feb\u90a3\u8001\u5a46\u5a46\uff0c\u9661\u89c1\u5f20\u4e39\u67ab\u4e00 \u5251\u98de\u6765\uff0c\u53c8\u60ca\u53c8\u6012\uff0c\u6025\u53eb\uff1a\u201c\u7559\u795e\uff01\u201d\u94c1\u81c2\u91d1\u733f\u4e0e\u4e09\u82b1\u5251\u90fd\u66fe\u5728\u5f20\u4e39\u67ab\u4e0e\u4e91\u857e\u624b\u4e0b\u5403\u8fc7\u5927 \u4e8f\uff0c\u4ec7\u4eba\u76f8\u89c1\u5206\u5916\u773c\u7ea2\uff0c\u53cc\u53cc\u62a2\u4e0a\u3002\u5f20\u4e39\u67ab\u957f\u5251\u4e00\u632f\uff0c\u55e1\u55e1\u4f5c\u54cd\uff0c\u767d\u8863\u98d8\u98d8\uff0c\u5728\u516b\u9635\u56fe\u4e2d\u7a9c \u6765\u7a9c\u53bb\uff0c\u5de6\u4e00\u5251\uff0c\u53f3\u4e00\u5251\uff0c\u524d\u4e00\u5251\uff0c\u540e\u4e00\u5251\uff0c\u907f\u5f3a\u653b\u5f31\uff0c\u4e0d\u4e0e\u94c1\u81c2\u91d1\u733f\u3001\u4e09\u82b1\u5251\u53ca\u4e91\u91cd\u4e09\u4e2a \u529f\u529b\u6700\u9ad8\u7684\u4eba\u6b63\u9762\u63a5\u6218\uff0c\u5374\u628a\u5176\u4ed6\u4e94\u540d\u5927\u5185\u9ad8\u624b\uff0c\u53c8\u903c\u5f97\u5404\u5404\u5206\u5f00\uff0c\u4e0d\u80fd\u517c\u987e\u3002 \u3000\u3000\u6fb9\u53f0\u955c\u660e\u5927\u559c\u53eb\u9053\uff1a\u201c\u597d\u554a\uff01\u201d\u6d1e\u5ead\u5e84\u4e3b\u89c1\u5f20\u4e39\u67ab\u58f0\u4e1c\u51fb\u897f\uff0c\u6307\u5357\u6253\u5317\uff0c\u8eab\u5f62\u56db\u65b9\u51fa \u6ca1\uff0c\u5374\u53c8\u662f\u7d27\u5bf9\u7740\u6b7b\u95e8\u7684\u67a2\u7ebd\u8981\u6237\u7adf\u662f\u6df1\u660e\u9635\u6cd5\uff0c\u72b9\u5728\u81ea\u5df1\u4e4b\u4e0a\uff0c\u4e5f\u4e0d\u7981\u72c2\u559c\u53eb\u9053\uff1a\u201c\u8001\u4e3b \u516c\u6709\u540e\uff0c\u5927\u5468\u53ef\u4ee5\u91cd\u5149\u3002\u201d\u5f20\u58eb\u8bda\u8eab\u6b7b\u867d\u5df2\u4e03\u516b\u5341\u5e74\uff0c\u6fb9\u53f0\u4e00\u5bb6\uff0c\u63d0\u8d77\u4ed6\u65f6\u4ecd\u662f\u5524\u4e3a\u8001\u4e3b \u516c\u3002\u8fd9\u516b\u9635\u56fe\u672c\u662f\u5f6d\u548c\u5c1a\u4f20\u4e0e\u5f20\u58eb\u8bda\uff0c\u5f20\u58eb\u8bda\u56e0\u8981\u6fb9\u53f0\u5f52\u771f\u5b88\u62a4\u5b9d\u85cf\uff0c\u53c8\u5c06\u516b\u9635\u56fe\u4f20\u6388\u4e0e \u4ed6\uff0c\u800c\u4eca\u6d1e\u5ead\u5e84\u4e3b\u6fb9\u53f0\u4ef2\u5143\u89c1\u5f20\u4e39\u67ab\u6df1\u660e\u9635\u6cd5\uff0c\u4e0d\u5f85\u7ec6\u95ee\uff0c\u5df2\u77e5\u4ed6\u5b9a\u662f\u5c11\u4e3b\u65e0\u7591\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u4e0e\u6fb9\u53f0\u955c\u660e\u52a0\u5165\uff0c\u5f62\u52bf\u7a81\u53d8\uff0c\u9002\u624d\u662f\u516b\u5927\u9ad8\u624b\u7a0d\u5360\u4e0a\u98ce\uff0c\u800c\u53ea\u5374\u53ea\u7684\u6328\u6253\u7684\u4efd \u513f\u3002\u6fb9\u53f0\u955c\u660e\u56db\u5904\u6e38\u8d70\uff0c\u8fd0\u5251\u5982\u98ce\u5411\u90a3\u4e9b\u88ab\u5f20\u4e39\u67ab\u6405\u5f97\u5934\u660f\u773c\u82b1\u7684\u5927\u5185\u9ad8\u624b\uff0c\u4e1c\u8e22\u4e00\u811a\uff0c\u897f \u523a\u4e00\u5251\uff0c\u6740\u5f97\u5341\u5206\u75db\u5feb\u3002 \u3000\u3000\u628a\u5b88\u201c\u60ca\u201d\u95e8\u90a3\u5c11\u5973\u540d\u53eb\u6fb9\u53f0\u7389\u95e8\uff0c\u6b63\u662f\u6fb9\u53f0\u955c\u7684\u59b9\u59b9\uff0c\u5979\u521a\u624d\u88ab\u4e91\u91cd\u638c\u529b\u4e00\u9707\uff0c\u9669\u9669 \u8dcc\u5012\uff0c\u8fd9\u65f6\u89c1\u9635\u5f62\u5df2\u9690\uff0c\u654c\u4eba\u53ea\u6709\u9632\u5b88\u7684\u4efd\u513f\uff0c\u4e0d\u81ea\u7981\u5730\u8df3\u51fa\u95e8\u6237\uff0c\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u59d0\u59d0\uff0c\u4f60 \u4e0e\u6211\u6740\u8fd9\u25a1\uff0c\u4ed6\u521a\u624d\u6b3a\u8d1f\u6211\u3002\u201d\u628a\u624b\u4e00\u6307\u4e91\u91cd\uff0c\u6fb9\u53f0\u955c\u660e\u7b11\u9053\uff1a\u201c\u8fd9\u8fd8\u4e0d\u5bb9\u6613\uff01\u597d\uff0c\u4f60\u8e0f\u4e7e \u65b9\uff0c\u8fdb\u574e\u4f4d\uff0c\u653b\u4ed6\u53f3\u8fb9\u3002\u201d\u5411\u4e91\u91cd\u5206\u5fc3\u76f4\u523a\uff0c\u4e91\u91cd\u4e00\u638c\u8361\u5f00\uff0c\u65ad\u95e8\u5200\u626c\u7a7a\u4e00\u95ea\uff0c\u6b63\u5f85\u8fd8\u62db\uff0c \u4fa7\u9762\u9752\u5149\u4e00\u95ea\uff0c\u6fb9\u53f0\u955c\u660e\u7684\u5229\u5251\u53c8\u5df2\u653b\u5230\uff0c\u800c\u4e14\u4f4d\u7f6e\u5de7\u5999\uff0c\u6b63\u5728\u4ed6\u7684\u638c\u529b\u653b\u4e0d\u5230\u7684\u5730\u65b9\uff0c\u4e91 \u91cd\u98de\u8eab\u6025\u95ea\uff0c\u6fb9\u53f0\u955c\u660e\u6ed1\u4f3c\u6e38\u9c7c\uff0c\u9661\u5730\u4ece\u4ed6\u638c\u4e0b\u6ed1\u8fc7\uff0c\u5237\u7684\u4e00\u5251\uff0c\u6307\u4ed6\u9762\u95e8\u3002\u8fd9\u4e00\u5251\u6765\u5f97\u5feb \u6377\u4e4b\u6781\u4e91\u91cd\u53c8\u88ab\u903c\u5728\u4e24\u5806\u4e71\u77f3\u4e4b\u95f4\uff0c\u53ea\u80fd\u4fa7\u8eab\u8eb2\u95ea\u3002\u4f46\u56e0\u5730\u5f62\u592a\u7a84\uff0c\u770b\u8fd9\u6765\u52bf\uff0c\u7eb5\u7136\u8eb2\u5f97\u5f00 \u9762\u95e8\u8981\u5bb3\uff0c\u80a9\u5934\u4e5f\u53ea\u6050\u8981\u88ab\u90a3\u5229\u5251\u523a\u4e2a\u900f\u660e\u7a9f\u7abf\uff01 \u3000\u3000\u6309\u8bf4\u4e91\u91cd\u7684\u529f\u529b\u672c\u6765\u6bd4\u6fb9\u53f0\u955c\u660e\u59d0\u59b9\u9ad8\u51fa\u4e00\u7b79\uff0c\u5c31\u7b97\u4ee5\u4e00\u654c\u4e8c\uff0c\u7eb5\u4e0d\u80fd\u80dc\uff0c\u4e5f\u4e0d\u4f1a\u843d \u8d25\uff0c\u65e0\u5948\u5979\u4eec\u59d0\u59b9\u4e8c\u4eba\uff0c\u4ed7\u7740\u77f3\u9635\u7684\u5965\u5999\uff0c\u5148\u628a\u4e91\u91cd\u903c\u5f97\u5904\u8eab\u4e0d\u5229\u7684\u5730\u5f62\uff0c\u7136\u540e\u8054\u5251\u6025\u653b\uff0c \u987f\u65f6\u628a\u4e91\u91cd\u7f6e\u4e8e\u9669\u5883\u3002 \u3000\u3000\u6fb9\u53f0\u955c\u660e\u624b\u8155\u4e00\u7ffb\uff0c\u5237\u7684\u4e00\u5251\u523a\u53bb\uff0c\u5ffd\u542c\u5f97\u53ee\u5f53\u4e00\u58f0\uff0c\u53ea\u89c1\u5f20\u4e39\u67ab\u7a81\u7136\u4ece\u5de6\u4fa7\u7684\u4f24\u95e8\u8df3 \u51fa\uff0c\u5251\u5c16\u8f7b\u8f7b\u4e00\u62e8\uff0c\u628a\u81ea\u5df1\u7684\u5229\u5251\u62d4\u5f00\u3002\u5f20\u4e39\u67ab\u8fd9\u4e00\u4e0b\uff0c\u6fb9\u53f0\u955c\u660e\u5374\u662f\u4e07\u4e07\u6599\u60f3\u4e0d\u5230\uff0c\u8be7 \u9053\uff1a\u201c\u4f60\u5e72\u4ec0\u4e48\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u770b\u5728\u6211\u7684\u9762\u4e0a\uff0c\u8fd9\u4e00\u5251\u5c31\u4e0d\u523a\u4e86\u5427\u3002\u201d\u6fb9\u53f0\u955c\u660e\u83ab\u540d\u5176 \u5999\uff0c\u4f46\u89c1\u5f20\u4e39\u67ab\u7b11\u541f\u541f\u7684\u770b\u7740\u81ea\u5df1\uff0c\u5fc3\u4e2d\u4e00\u52a8\uff0c\u4f3c\u89c9\u4ed6\u7684\u76ee\u5149\u5177\u6709\u7edd\u5927\u7684\u9b54\u529b\uff0c\u4e0d\u7531\u81ea\u5df1\u5730 \u5c06\u5229\u5251\u64a4\u4e86\u56de\u6765\u3002\u6d1e\u5ead\u5e84\u4e3b\u4e5f\u597d\u751f\u60ca\u8be7\uff0c\u9ad8\u58f0\u95ee\u9053\uff1a\u201c\u8fd9\u519b\u5b98\u662f\u4ec0\u4e48\u4eba\u554a\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a \u201c\u4ed6\u8bf4\u6211\u662f\u4ed6\u7684\u5927\u4ec7\u4eba\u3002\u201d\u4e91\u91cd\u6012\u9053\uff1a\u201c\u8c01\u8981\u4f60\u624b\u4e0b\u7559\u60c5\uff0c\u6211\u4e0e\u4f60\u4e24\u5bb6\u4e4b\u4ec7\uff0c\u4eca\u751f\u4eca\u4e16\uff0c\u4f11 \u60f3\u5316\u89e3\u3002\u201d\u547c\u7684\u4e00\u638c\uff0c\u659c\u5288\u4e0b\u53bb\u3002\u6d1e\u5ead\u5e84\u4e3b\u66f4\u662f\u8be7\u5f02\uff0c\u770b\u8fd9\u60c5\u5f62\uff0c\u4e91\u91cd\u5bf9\u4ed6\u786e\u662f\u4ec7\u6df1\u4f3c\u6d77\uff0c \u4e0d\u77e5\u4f55\u4ee5\u5f20\u4e39\u67ab\u5374\u8981\u5904\u5904\u62a4\u4ed6\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u5de6\u638c\u6325\u4e86\u534a\u4e2a\u5706\u5f27\u7f13\u7f13\u63a8\u51fa\uff0c\u4e91\u91cd\u5fc3\u4e2d\u4e00\u6014\uff1a\u201c\u54a6\u4ed6\u51e0\u65f6\u4e5f\u5b66\u6210\u4e86\u5927\u529b\u91d1\u521a\u624b\u7684 \u529f\u592b\uff1f\u201d\u53cc\u638c\u76f8\u4ea4\uff0c\u5404\u9000\u4e09\u6b65\uff0c\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u4e91\u91cd\u543e\u5144\uff0c\u8d70\u4e3a\u4e0a\u8ba1\u3002\u201d\u4e91\u91cd\u66f4\u6012\uff0c\u9053\uff1a\u201c\u8c01 \u4e0e\u4f60\u79f0\u5144\u9053\u5f1f\uff1f\u201d\u547c\u7684\u53c8\u662f\u4e00\u638c\uff0c\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u6211\u95ee\u4f60\u4f55\u6240\u4e3a\u800c\u6765\uff1f\u201d\u94c1\u81c2\u91d1\u733f\u559d\u9053\uff1a\u201c\u4f60 \u5c06\u5b9d\u85cf\u4ea4\u51fa\uff0c\u6211\u4eec\u4fbf\u8d70\u3002\u201d\u6b64\u8a00\u5b9e\u662f\u8272\u5389\u5185\u834f\uff0c\u4ed6\u77e5\u4eca\u65e5\u4e4b\u6218\u8ba8\u4e0d\u4e86\u597d\uff0c\u4f46\u613f\u5f20\u4e39\u67ab\u80af\u653e\u4ed6 \u8d70\uff0c\u8981\u5b9d\u85cf\u4e4b\u8bdd\uff0c\u4e0d\u8fc7\u662f\u5982\u6b64\u8bf4\u8bf4\uff0c\u906e\u4e2a\u989c\u9762\u7f62\u4e86\u3002\u90a3\u6599\u5f20\u4e39\u67ab\u4ef0\u5929\u5927\u7b11\uff0c\u5ffd\u9053\uff1a\u201c\u539f\u6765\u4f60 \u4eec\u662f\u4e3a\u5148\u7956\u7684\u5b9d\u85cf\u800c\u6765\uff0c\u8fd9\u4e9b\u4e1c\u897f\u6211\u672c\u6765\u5c31\u60f3\u9001\u7ed9\u5927\u660e\u7687\u5e1d\uff0c\u6709\u4f60\u4eec\u4ee3\u52b3\u9001\u53bb\uff0c\u90a3\u662f\u6700\u597d\u4e0d \u8fc7\uff01\u201d\u6b64\u8a00\u4e00\u51fa\uff0c\u9664\u4e86\u6fb9\u53f0\u955c\u660e\u4e4b\u5916\uff0c\u4f59\u4eba\u65e0\u4e0d\u5403\u60ca\u3002\u6d1e\u5ead\u5e84\u4e3b\u9053\uff1a\u201c\u5c11\u4e3b\uff0c\u4f60\u8fd9\u662f\u4ec0\u4e48 \u8bdd\uff1f\u201d\u4e91\u91cd\u9053\uff1a\u201c\u5927\u4e08\u592b\u5b81\u6b7b\u4e0d\u8fb1\u3002\u5f20\u4e39\u67ab\uff0c\u4f60\u7109\u80fd\u5c61\u6b21\u620f\u5f04\u4e8e\u6211\uff1f\u201d\u4ed6\u628a\u5f20\u4e39\u67ab\u7684\u771f\u5fc3\u8bdd \u7adf\u5f53\u4f5c\u620f\u5f04\u4e4b\u8a00\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u4f60\u8981\u5982\u4f55\u624d\u80af\u76f8\u4fe1\uff1f\u201d\u4e91\u91cd\u4e00\u8a00\u4e0d\u53d1\uff0c\u547c\u547c\u547c\uff0c\u53c8\u662f\u8fde\u5288\u4e09\u638c\uff0c\u5f20\u4e39\u67ab\u597d \u751f\u6c14\u607c\uff0c\u5374\u4e5f\u65e0\u53ef\u5948\u4f55\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u54e8\u58f0\u56db\u8d77\uff0c\u534a\u5c71\u5761\u7684\u6811\u6728\u4e71\u77f3\u4e1b\u4e2d\u7a81\u7136\u7a9c\u51fa\u4e00\u5927\u6279\u4eba\uff0c\u9ad8\u77ee\u80a5\u7626\uff0c\u5947\u5f62\u602a\u72b6\uff0c\u6f2b \u5c71\u904d\u91ce\uff0c\u56db\u5904\u6740\u6765\u3002\u5f20\u4e39\u67ab\u5b9a\u775b\u770b\u65f6\uff0c\u4e3a\u9996\u4e8c\u4eba\uff0c\u4e00\u4e2a\u6ee1\u5934\u7ea2\u53d1\uff0c\u72b9\u5982\u4e00\u4e1b\u4e71\u8349\uff0c\u53c8\u4f3c\u4e00\u5806 \u706b\u4e91\u76d8\u5728\u5934\u4e0a\uff0c\u6b64\u4eba\u6b63\u662f\u6628\u65e5\u4e0e\u81ea\u5df1\u8c6a\u8d4c\u7684\u7ea2\u53d1\u5996\u9f99\u90ed\u6d2a\uff0c\u8fd9\u72b9\u7f62\u4e86\uff0c\u53e6\u4e00\u4e2a\u4eba\u9e70\u9f3b\u78a7\u773c\uff0c \u8eab\u9ad8\u4e03\u5c3a\u6709\u4f59\uff0c\u624b\u6301\u4e00\u53cc\u5f00\u5c71\u5927\u65a7\u5374\u662f\u74e6\u523a\u56fd\u592a\u5e08\u4e5f\u5148\u624b\u4e0b\u7684\u7b2c\u4e00\u540d\u52c7\u58eb\uff0c\u540d\u5524\u5bdf\u9c81\u56fe\uff0c\u6b66 \u529f\u4e4b\u5f3a\uff0c\u5728\u74e6\u523a\u56fd\u4e2d\uff0c\u4ec5\u5728\u6fb9\u53f0\u706d\u660e\u4e4b\u4e0b\u3002\u5f20\u4e39\u67ab\u89c1\u4e86\uff0c\u4e0d\u7531\u5f97\u5927\u5403\u4e00\u60ca\uff0c\u5fc3\u4e2d\u9a87\u9053\uff1a\u201c\u90ed \u6d2a\u662f\u738b\u632f\u7684\u5fc3\u8179\u6b66\u58eb\uff0c\u8fd9\u4e24\u4eba\u5982\u4f55\u80fd\u4f1a\u5408\u4e00\u8d77\uff0c\u83ab\u975e\u74e6\u523a\u5175\u5df2\u7ecf\u4fb5\u5165\u4e2d\u539f\u4e48\uff1f\u201d \u3000\u3000\u94c1\u81c2\u91d1\u733f\u4e00\u58f0\u6b22\u547c\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u4eec\u6765\u5f97\u6b63\u597d\uff0c\u53db\u8d3c\u5f20\u4e39\u67ab\u6b63\u5728\u8fd9\u513f\uff01\u201d\u90ed\u6d2a\u563f\u563f\u51b7\u7b11\uff0c \u628a\u624b\u4e00\u6325\uff0c\u5c06\u6d1e\u5ead\u5c71\u5e84\u7684\u4eba\u4e0e\u5927\u5185\u4e03\u5927\u9ad8\u624b\uff0c\u8fde\u540c\u4e91\u91cd\u5728\u5185\uff0c\u90fd\u56f4\u4e86\u8d77\u6765\u3002 \u3000\u3000\u94c1\u81c2\u91d1\u733f\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u53eb\u9053\uff1a\u201c\u5582\uff0c\u5582\uff01\u4f60\u4e0d\u8ba4\u5f97\u6211\u4eec\u5417\uff1f\u6211\u4eec\u516b\u4eba\u90fd\u662f\u7687\u4e0a\u6d3e\u6765 \u7684\uff01\u201d\u90ed\u6d2a\u51b7\u7b11\u9053\uff1a\u201c\u6211\u4eec\u90fd\u4e0d\u662f\u7687\u4e0a\u6d3e\u6765\u7684\uff01\u54fc\uff0c\u54fc\uff0c\u628a\u5b9d\u85cf\u548c\u5730\u56fe\u90fd\u732e\u51fa\u6765\uff01\u201d\u4e91\u91cd\u6012 \u53f1\u9053\uff1a\u201c\u4f60\u4eec\u6562\u9020\u53cd\u5417\uff1f\u5b9d\u85cf\u548c\u5730\u56fe\u662f\u7687\u4e0a\u8981\u7684\uff01\u201d\u90ed\u6d2a\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u5230\u74e6\u523a\u53bb\u627e\u7687\u4e0a\u5427\uff0c \u5b9d\u85cf\u548c\u5730\u56fe\u662f\u738b\u516c\u516c\u8981\u7684\uff01\u201d\u4e91\u91cd\u4e00\u6014\uff0c\u9053\uff1a\u201c\u4f60\u8bf4\u4ec0\u4e48\uff1f\u7687\u4e0a\u600e\u4e48\u5566\uff1f\u201d\u90ed\u6d2a\u7b11\u9053\uff1a\u201c\u6ca1 \u4ec0\u4e48\u74e6\u523a\u5927\u519b\u5df2\u8fdb\u4e86\u96c1\u95e8\u5173\u5566\uff01\u4f60\u7684\u7687\u4e0a\u5df2\u505a\u4e86\u74e6\u523a\u7684\u4fd8\u864f\u5566\uff01\u201d \u3000\u3000\u5f20\u4e39\u67ab\u53eb\u9053\uff1a\u201c\u4e91\u91cd\u543e\u5144\uff0c\u73b0\u5728\u4f60\u8be5\u660e\u767d\u4e86\u5417\uff1f\u5408\u529b\u5bf9\u5916\u662f\u4e3a\u4e0a\u8ba1\u3002\u201d\u4e00\u63a0\u800c\u524d\uff0c\u633a\u5251 \u4fbf\u523a\u90ed\u6d2a\u3002\u4e91\u91cd\u4e00\u58f0\u6012\u543c\uff0c\u65ad\u95e8\u5200\u4e00\u95ea\uff0c\u5de6\u638c\u547c\u7684\u4e00\u58f0\u968f\u7740\u5200\u5149\u5288\u53bb\uff0c\u76f4\u53d6\u756a\u5c06\uff0c\u5bdf\u9c81\u56fe\u632f \u81c2\u4e00\u683c\uff0c\u4e91\u91cd\u864e\u53e3\u6d41\u8840\uff0c\u65ad\u95e8\u5200\u51e0\u4e4e\u9707\u98de\u3002\u4f46\u5bdf\u9c81\u56fe\u7684\u53cc\u65a7\u5de6\u4e0a\u53f3\u843d\uff0c\u4e5f\u7ed9\u4e91\u91cd\u7684\u91d1\u521a\u638c\u529b \u9707\u5f97\u6b6a\u8fc7\u4e00\u8fb9\uff0c\u5927\u53eb\uff1a\u201c\u597d\u5440\uff0c\u4f60\u8fd9\u5a03\u5a03\u4e5f\u6709\u70b9\u529f\u592b\u3002\u201d\u7528\u8db3\u529b\u6c14\uff0c\u53cc\u65a7\u4e00\u5377\uff0c\u970d\u5730\u780d\u6765\uff0c \u6765\u52bf\u51f6\u731b\u4e4b\u6781\uff01 \u3000\u3000\u5f20\u4e39\u67ab\u90a3\u5251\u8fc5\u82e5\u96f7\u9706\uff0c\u90ed\u6d2a\u89c1\u8fc7\u4ed6\u7684\u5389\u5bb3\uff0c\u4e0d\u6562\u786c\u63a5\uff0c\u4e00\u4e2a\u76d8\u9f99\u7ed5\u6b65\uff0c\u659c\u95ea\u53d1\u62db\u3002\u5f20\u4e39 \u67ab\u767d\u8863\u98d8\u98d8\uff0c\u865a\u523a\u4e00\u5251\uff0c\u731b\u5730\u4e00\u4e2a\u7ffb\u8eab\uff0c\u5251\u628a\u4e00\u7ffb\uff0c\u53cd\u624b\u4e00\u5e26\uff0c\u5bdf\u9c81\u56fe\u7684\u5de6\u65a7\u6b63\u5728\u6cfc\u98ce\u780d \u5230\uff0c\u88ab\u4ed6\u65bd\u7528\u5de7\u529b\uff0c\u4e00\u7c98\u7c98\u51fa\u5916\u95e8\u3002\u4e91\u91cd\u6b63\u5728\u5403\u529b\uff0c\u5f97\u5f20\u4e39\u67ab\u66ff\u4ed6\u63a5\u4e86\u4e00\u62db\uff0c\u53e3\u4e2d\u4e0d\u8a00\uff0c\u5fc3 \u4e2d\u5374\u662f\u611f\u6fc0\u3002 \u3000\u3000\u5bdf\u9c81\u56fe\u53cc\u773c\u4e00\u7741\uff0c\u9053\uff1a\u201c\u54c8\uff0c\u5f20\u516c\u5b50\uff0c\u539f\u6765\u662f\u4f60\uff01\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u4f60\u4e0d\u5728\u74e6\u523a\uff0c\u5230\u8fd9\u6765 \u505a\u4ec0\u4e48\uff1f\u8fd9\u91cc\u987b\u4e0d\u662f\u4f60\u7684\u5730\u65b9\uff0c\u7ed9\u6211\u6eda\u56de\u53bb\uff01\u201d\u5bdf\u9c81\u56fe\u9053\uff1a\u201c\u4f60\u5bb6\u5c61\u53d7\u6211\u56fd\u56fd\u4e3b\u5927\u6069\uff0c\u5c45\u7136 \u4e5f\u6562\u80cc\u53db\u4e48\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u6211\u70e7\u53d8\u4e86\u7070\uff0c\u4e5f\u662f\u4e2d\u56fd\u4e4b\u4eba\uff0c\u7109\u80fd\u53d7\u4f60\u56fd\u4e3b\u7b3c\u7edc\uff01\u201d\u5bdf\u9c81\u56fe\u5927 \u6012\u9053\uff1a\u201c\u6211\u65e9\u770b\u51fa\u4f60\u5fc3\u6000\u4e8c\u5fd7\uff0c\u539f\u6765\u4f60\u679c\u771f\u662f\u79c1\u9003\u56de\u6765\u8981\u4e0e\u6211\u4eec\u4f5c\u5bf9\uff0c\u54fc\u3001\u54fc\uff0c\u5403\u6211\u4e00 \u65a7\uff01\u201d \u3000\u3000\u5f20\u4e39\u67ab\u5237\u5237\u4e8c\u5251\uff0c\u504f\u950b\u75be\u4e0a\uff0c\u5bdf\u9c81\u56fe\u53cc\u65a7\u4e00\u4e2a\u76d8\u65cb\uff0c\u72b9\u5982\u6cf0\u5c71\u538b\u9876\uff0c\u786c\u538b\u4e0b\u6765\uff0c\u5f20\u4e39\u67ab \u77e5\u4ed6\u529b\u5927\uff0c\u53ea\u53ef\u667a\u53d6\uff0c\u5c55\u5f00\u7edd\u9876\u7684\u8f7b\u8eab\u529f\u592b\uff0c\u4e0e\u4ed6\u5468\u65cb\u3002\u5bdf\u9c81\u56fe\u795e\u529b\u60ca\u4eba\u4e0d\u5728\u6fb9\u53f0\u706d\u660e\u4e4b \u4e0b\uff0c\u4f46\u8bba\u5230\u817e\u632a\u95ea\u5c55\u7684\u5c0f\u5de7\u529f\u592b\u5374\u662f\u4e0d\u5982\u3002\u4e24\u4eba\u77ac\u5373\u6597\u4e86\u5341\u6570\u62db\uff0c\u5bdf\u9c81\u56fe\u53cc\u65a7\u970d\u970d\uff0c\u5468\u56f4\u4e00 \u4e08\u4e4b\u5185\uff0c\u5168\u662f\u65a7\u5f71\u5251\u5149\u3002 \u3000\u3000\u8fd9\u65f6\u53cc\u65b9\u5df2\u6210\u6df7\u6218\u4e4b\u5c40\uff0c\u90ed\u6d2a\u5e26\u6765\u7684\u4eba\u7adf\u6709\u4e09\u56db\u5341\u4e4b\u591a\uff0c\u6709\u4e9b\u662f\u5978\u81e3\u738b\u632f\u6697\u4e2d\u7f51\u7f57\u7684\u6b66 \u58eb\uff0c\u6709\u4e9b\u662f\u6c5f\u5357\u9053\u4e0a\u7684\u9ed1\u5e2e\u4eba\u7269\u524d\u65e5\u60f3\u62a2\u5feb\u6d3b\u6797\u7684\u6d77\u9f99\u5e2e\u5e2e\u4e3b\u4e5f\u5728\u5185\u3002 \u3000\u3000\u90ed\u6d2a\u8fd9\u8fb9\u80dc\u5728\u4eba\u591a\uff0c\u4f46\u5f20\u4e39\u67ab\u8fd9\u8fb9\u5374\u6709\u597d\u51e0\u4e2a\u4e00\u6d41\u9ad8\u624b\uff0c\u94c1\u81c2\u91d1\u733f\u3001\u4e09\u82b1\u5251\u3001\u4e91\u91cd\u4ee5\u53ca \u6d1e\u5ead\u5e84\u4e3b\u592b\u59bb\u7b49\u4eba\uff0c\u90fd\u662f\u4e00\u8eab\u6b66\u529f\uff0c\u975e\u540c\u5c0f\u53ef\uff0c\u4f46\u4ee5\u5c11\u654c\u4f17\uff0c\u5374\u4e5f\u5403\u529b\u975e\u5e38\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u90fd\u9000\u5230\u516b\u9635\u56fe\u5185\u3002\u201d\u5bdf\u9c81\u56fe\u5927\u7b11\u9053\uff1a\u201c\u533a\u533a\u77f3\u9635\uff0c\u80fd\u5948\u6211\u4f55\uff1f\u201d\u53cc\u65a7\u6325 \u821e\uff0c\u7adf\u628a\u4e00\u5806\u77f3\u5934\uff0c\u5288\u5f97\u5012\u584c\uff0c\u6709\u4e24\u540d\u5927\u5185\u9ad8\u624b\uff0c\u62a2\u4e0a\u5835\u622a\uff0c\u5374\u56e0\u4e0d\u8bc6\u9635\u56fe\u4e4b\u5999\uff0c\u5288\u5f97\u5012 \u584c\uff0c\u6709\u4e24\u540d\u5927\u5185\u9ad8\u624b\uff0c\u62a2\u4e0a\u5835\u622a\uff0c\u5374\u56e0\u4e0d\u8bc6\u9635\u56fe\u4e4b\u5999\uff0c\u53cd\u8e0f\u5165\u6b7b\u95e8\uff0c\u5f20\u4e39\u67ab\u5927\u53eb\uff1a\u201c\u5feb \u9000\uff01\u201d\u5bdf\u9c81\u56fe\u5de6\u53f3\u5f00\u5f13\uff0c\u53cc\u65a7\u970d\u5730\u4e00\u5288\uff0c\u8fd9\u4e24\u540d\u9ad8\u624b\u9677\u8eab\u5728\u72ed\u7a84\u7684\u77f3\u9635\u4e4b\u4e2d\uff0c\u95ea\u907f\u4e0d\u4fbf\uff0c\u51b7 \u4e0d\u53ca\u9632\uff0c\u7adf\u7136\u7ed9\u5bdf\u9c81\u56fe\u4ece\u9876\u95e8\u76f4\u5288\u4e0b\u6765\uff0c\u5206\u6210\u4e24\u7247\u3002 \u3000\u3000\u5bdf\u9c81\u56fe\u54c8\u54c8\u5927\u7b11\uff0c\u9661\u89c9\u8eab\u540e\u51b7\u98ce\u75be\u5c04\uff0c\u56de\u8eab\u4e00\u65a7\uff0c\u786e\u4e86\u4e2a\u7a7a\uff0c\u53ea\u542c\u5f97\u201c\u55e4\u201d\u7684\u4e00\u54cd\uff0c\u8863 \u8896\u5df2\u7ed9\u5f20\u4e39\u67ab\u5229\u5251\u523a\u7a7f\uff0c\u5bdf\u9c81\u56fe\u6025\u5fd9\u62db\u67b6\uff0c\u500f\u5730\u53c8\u4e0d\u89c1\u4e86\u4eba\u5f71\u3002\u6b63\u5f85\u7a9c\u51fa\uff0c\u731b\u7136\u95f4\u53ea\u89c1\u767d\u5149 \u4e00\u95ea\uff0c\u5f20\u4e39\u67ab\u7b11\u563b\u563b\u5730\u4ece\u5de6\u4fa7\u4e71\u77f3\u5806\u4e2d\u73b0\u51fa\u8eab\u6765\uff0c\u5237\u7684\u4e00\u5251\uff0c\u5728\u5bdf\u9c81\u56fe\u7684\u53f3\u81c2\u5f00\u4e00\u9053\u4f24\u53e3\u3002 \u5bdf\u9c81\u56fe\u66b4\u8df3\u5982\u96f7\uff0c\u53cc\u65a7\u75be\u5288\uff0c\u4f46\u542c\u5f97\u8f70\u9686\u9686\u58f0\u5982\u5de8\u70ae\uff0c\u77f3\u5934\u7eb7\u98de\u4e4b\u4e2d\uff0c\u5f20\u4e39\u67ab\u8eab\u5f62\u4e00\u95ea\uff0c\u53c8 \u5728\u5bdf\u9c81\u56fe\u80a9\u4e0a\u523a\u4e86\u4e00\u5251\uff0c\u5bdf\u9c81\u56fe\u8981\u8fd8\u51fb\u65f6\uff0c\u5728\u6c99\u5c18\u6eda\u6eda\u4e4b\u4e2d\uff0c\u770b\u4e5f\u770b\u4e0d\u6e05\u695a\uff0c\u5f20\u4e39\u67ab\u53c8\u4e0d\u89c1 \u4e86\u3002\u672c\u6765\u4ee5\u5bdf\u9c81\u56fe\u7684\u6b66\u529f\uff0c\u5c1a\u7a0d\u5728\u5f20\u4e39\u67ab\u4e4b\u4e0a\uff0c\u4f46\u4e00\u8005\u662f\u5f20\u4e39\u67ab\u6df1\u8bc6\u9635\u56fe\u5de7\u5999\uff0c\u8fdb\u9000\u5f97\u5b9c\uff1b \u4e8c\u8005\u662f\u8f7b\u529f\u8f83\u9ad8\uff0c\u4ea6\u5360\u4e86\u4fbf\u5b9c\uff1b\u4e09\u8005\u662f\u5f20\u4e39\u67ab\u4e60\u4e86\u7384\u529f\u8981\u8bc0\uff0c\u6df1\u660e\u907f\u5f3a\u51fb\u5f31\u4e4b\u7406\u3002\u6545\u6b64\uff0c\u7adf \u7136\u5728\u970e\u65f6\u95f4\uff0c\u8fde\u523a\u4e86\u5bdf\u9c81\u56fe\u4e09\u5251\u3002 \u3000\u3000\u5bdf\u9c81\u56fe\u780d\u4e86\u51e0\u65a7\uff0c\u7cbe\u94a2\u65a7\u53e3\uff0c\u4e5f\u5df2\u5377\u4e86\u3002\u5fc3\u4e2d\u4e00\u6014\uff0c\u77e5\u9053\u5f92\u6043\u86ee\u529b\uff0c\u53ea\u6709\u5403\u4e8f\uff0c\u52a0\u4e0a\u5f20 \u4e39\u67ab\u795e\u51fa\u9b3c\u6ca1\uff0c\u66f4\u662f\u4ee4\u4eba\u80c6\u5bd2\u3002\u5bdf\u9c81\u56fe\u6c14\u7130\u987f\u706d\uff0c\u62a2\u7740\u5360\u5230\u4e00\u4e2a\u8f83\u5bbd\u9614\u7684\u5730\u5f62\uff0c\u53cc\u65a7\u5c55\u5f00\uff0c \u4e0a\u4f7f\u201c\u96ea\u82b1\u76d6\u9876\u201d\u4e0b\u4f7f\u201c\u67af\u6811\u76d8\u6839\u201d\uff0c\u628a\u5168\u8eab\u9632\u5f97\u4e2a\u98ce\u96e8\u4e0d\u900f\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u54c8\u54c8\u5927\u7b11\uff0c\u4e0d\u53bb\u7406\u4ed6\uff0c\u5374\u5728\u77f3\u9635\u4e4b\u4e2d\uff0c\u4e1c\u9a70\u897f\u63a0\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u53c8\u4f24\u4e86\u51e0\u4eba\u3002\u53ef\u662f \u654c\u4eba\u4f17\u591a\uff0c\u6740\u4e4b\u4e0d\u9000\uff0c\u6df7\u6218\u4e4b\u4e2d\u81ea\u5df1\u8fd9\u8fb9\uff0c\u53c8\u6709\u4e24\u540d\u5927\u5185\u9ad8\u624b\uff0c\u6b7b\u5728\u654c\u4eba\u5175\u5203\u4e4b\u4e0b\u3002 \u3000\u3000\u4e91\u91cd\u8fde\u7528\u91d1\u521a\u5927\u529b\u624b\u6cd5\uff0c\u4e5f\u6bd9\u4e86\u51e0\u4eba\uff0c\u5ffd\u89c1\u7ea2\u53d1\u5996\u9f99\u90ed\u6d2a\u6b63\u88ab\u6d1e\u5ead\u5e84\u4e3b\u7684\u6e14\u53c9\u8feb\u5f97\u8eab\u5f62 \u6b6a\u659c\u4e0d\u5b9a\uff0c\u4e0e\u81ea\u5df1\u76f8\u8ddd\u4e0d\u8fc7\u6570\u6b65\u4e4b\u9065\u3002\u4e91\u91cd\u6068\u6781\u90ed\u6d2a\uff0c\u5165\u5f00\u8eab\u8fb9\u7684\u654c\u4eba\uff0c\u731b\u8dc3\u800c\u524d\uff0c\u547c\u7684\u4e00 \u638c\u5c31\u671d\u90ed\u6d2a\u9876\u95e8\u5288\u4e0b\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u5f20\u4e39\u67ab\u53eb\u9053\uff1a\u201c\u5c0f\u5fc3\uff0c\u8fd9\u25a1\u638c\u4e0a\u6709\u6bd2\uff01\u201d\u4e91\u91cd\u5fc3\u4e2d\u4e00\u6014\uff0c\u638c\u52bf\u6536\u62e2\u4e0d\u4f4f\uff0c\u9661\u5730\u76f4 \u5288\u4e0b\u53bb\u3002\u4f46\u89c1\u90ed\u6d2a\u624b\u8155\u4e00\u7ffb\uff0c\u638c\u5fc3\u901a\u7ea2\u5982\u8840\uff0c\u201c\u84ec\u201d\u7684\u4e00\u58f0\uff0c\u53cc\u638c\u76f8\u4ea4\uff0c\u90ed\u6d2a\u4e00\u58f0\u5389\u53eb\uff0c\u624b \u8155\u5173\u8282\uff0c\u88ab\u4e91\u91cd\u4e00\u638c\u51fb\u6298\uff0c\u624b\u638c\u540a\u4e86\u4e0b\u6765\uff0c\u4e91\u91cd\u4e5f\u89c9\u638c\u5fc3\u4e00\u9ebb\uff0c\u8fde\u5fd9\u540e\u9000\u3002\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u4e91 \u5144\uff0c\u5feb\u8fd0\u771f\u5143\u4e4b\u6c14\uff0c\u4e0d\u8981\u8ba9\u6bd2\u6c14\u4e0a\u5347\u3002\u201d\u4e91\u91cd\u77a7\u4e86\u5f20\u4e39\u67ab\u4e00\u773c\uff0c\u8dcc\u5750\u5730\u4e0a\u3002\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u955c \u660e\uff0c\u4f60\u5b88\u62a4\u4ed6\uff0c\u4e0d\u51c6\u8ba9\u654c\u4eba\u78b0\u4ed6\u6beb\u53d1\u3002\u201d\u6fb9\u53f0\u955c\u660e\u4e5f\u77a7\u4e86\u5f20\u4e39\u67ab\u4e00\u773c\uff0c\u4e00\u58f0\u4e0d\u54cd\u5730\u6301\u5251\u5b88\u5728 \u4e91\u91cd\u8eab\u8fb9\u3002 \u3000\u3000\u6fb9\u53f0\u955c\u6709\u719f\u6089\u9635\u52bf\uff0c\u53c8\u6709\u5f20\u4e39\u67ab\u7b49\u5728\u5916\u7ebf\u6321\u7740\u654c\u4eba\uff0c\u679c\u7136\u9632\u5b88\u5f97\u5341\u5206\u4e25\u5bc6\u3002\u90a3\u90ed\u6d2a\u7684\u624b \u8155\u9aa8\u5934\uff0c\u7ed9\u4e91\u91cd\u638c\u529b\u51fb\u5f97\u7c89\u788e\uff0c\u75bc\u75db\u96be\u5f53\uff0c\u84e6\u7136\u4ece\u540c\u4f34\u624b\u4e2d\u62a2\u8fc7\u4e00\u5f20\u5229\u5203\uff0c\u201c\u55d6\u201d\u7684\u4e00\u4e0b\uff0c \u4ece\u65ad\u8155\u5904\u9f50\u6839\u5207\u4e0b\uff0c\u6577\u4e0a\u91d1\u521b\u836f\u6495\u4e0b\u8863\u895f\u5305\u624e\uff0c\u5389\u58f0\u53eb\u9053\uff1a\u201c\u6211\u6b7b\u4e0d\u4e86\uff0c\u4f60\u4eec\u52a0\u7d27\u5f3a\u653b\u3002\u201d \u4f17\u4eba\u89c1\u4ed6\u5982\u6b64\u51f6\u72e0\u4ea6\u90fd\u4e0d\u7981\u9a87\u7136\u3002 \u3000\u3000\u90a3\u8fb9\u5c11\u4e86\u90ed\u6d2a\u4e00\u4e2a\u9ad8\u624b\uff0c\u5b9e\u529b\u867d\u7136\u7a0d\u51cf\uff0c\u5374\u65e0\u5927\u788d\u3002\u5f20\u4e39\u67ab\u8fd9\u8fb9\uff0c\u5c11\u4e86\u4e91\u91cd\uff0c\u53c8\u8981\u62bd\u51fa \u6fb9\u53f0\u955c\u660e\u4e3a\u4ed6\u9632\u62a4\uff0c\u672c\u6765\u4eba\u5c11\uff0c\u9635\u52bf\u7acb\u89c1\u677e\u6563\u3002\u90ed\u6d2a\u5750\u5728\u5730\u4e0a\uff0c\u6325\u5355\u81c2\u6307\u6325\uff0c\u4e00\u9635\u5f3a\u653b\uff0c\u53cd \u800c\u5360\u4e86\u4f18\u52bf\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u89c1\u654c\u4eba\u52bf\u76db\uff0c\u76f8\u6301\u4e0b\u53bb\uff0c\u53ea\u6709\u5403\u4e8f\uff0c\u4f46\u53c8\u60f3\u4e0d\u5230\u7834\u654c\u4e4b\u6cd5\uff0c\u5fc3\u4e2d\u6697\u6697\u53eb\u82e6\u3002\u6fc0\u6218 \u591a\u65f6\uff0c\u867d\u8fde\u4f24\u4e86\u6570\u540d\u654c\u4eba\uff0c\u4f46\u81ea\u5df1\u8fd9\u8fb9\uff0c\u53c8\u6709\u4e00\u540d\u5927\u5185\u9ad8\u624b\u4e0e\u4e24\u540d\u5e84\u4e01\u53d7\u4e86\u91cd\u4f24\uff0c\u5f62\u52bf\u66f4\u662f \u5403\u7d27\u3002\u6b63\u81ea\u5fc3\u7126\uff0c\u5ffd\u542c\u5f97\u4e00\u9635\u60a0\u626c\u7684\u7b1b\u58f0\uff0c\u4ece\u5c71\u5761\u82b1\u6811\u4e4b\u95f4\u968f\u98ce\u98d8\u6765\uff0c\u6709\u4eba\u6b4c\u9053\uff1a\u201c\u8c01\u628a\u82cf \u676d\u66f2\u5b50\u8bb4\uff1f\u8377\u82b1\u5341\u91cc\u6842\u4e09\u79cb\uff0c\u90a3\u77e5\u5349\u6728\u65e0\u60c5\u7269\uff0c\u7275\u52a8\u957f\u6c5f\u4e07\u53e4\u6101\u3002\u5440\uff0c\u5440\uff0c\u7275\u52a8\u957f\u6c5f\u4e07\u53e4 \u6101\uff01\u201d\u6b4c\u58f0\u5999\u66fc\uff0c\u5982\u6028\u5982\u8bc9\uff0c\u8fd9\u6b63\u662f\u5f20\u4e39\u67ab\u753b\u4e0a\u7684\u9898\u8bd7\u3002 \u3000\u3000\u8fd9\u970e\u65f6\u95f4\uff0c\u5f20\u4e39\u67ab\u5fc3\u5934\uff0c\u5982\u6709\u7535\u6d41\u901a\u8fc7\uff0c\u987f\u65f6\u5446\u4e86\u3002\u53ea\u89c1\u82b1\u836b\u6df1\u5904\uff0c\u4e00\u4e2a\u5c11\u5973\uff0c\u624b\u6301\u77ed \u7b1b\uff0c\u7f13\u7f13\u884c\u6765\u3002\u8fd9\u5c11\u5973\u7a7f\u7740\u4e00\u8eab\u6e56\u6c34\u8272\u7684\u8863\u88f3\uff0c\u8863\u8882\u8f7b\u626c\uff0c\u59ff\u5bb9\u7edd\u8273\uff0c\u8f7b\u79fb\u83b2\u6b65\uff0c\u98d8\u98d8\u82e5 \u4ed9\u3002\u6fb9\u53f0\u955c\u660e\u5403\u4e86\u4e00\u60ca\u5fc3\u9053\uff1a\u201c\u8fd9\u96be\u9053\u662f\u592a\u6e56\u7684\u4ed9\u5973\u98de\u4e0a\u5c71\u5934\uff1f\u201d\u5979\u7d20\u6765\u4ee5\u8c8c\u7f8e\u81ea\u8d1f\uff0c\u800c\u4eca \u89c1\u4e86\u8fd9\u4e2a\u5c11\u5973\uff0c\u5b9b\u5982\u7a7a\u8c37\u5e7d\u5170\uff0c\u65e2\u6e05\u4e14\u8273\uff0c\u987f\u89c9\u81ea\u6127\u4e0d\u5982\u3002 \u3000\u3000\u53ea\u542c\u5f97\u5f20\u4e39\u67ab\u98a4\u58f0\u53eb\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff01\u201d\u6fb9\u53f0\u955c\u660e\u201c\u5475\u201d\u4e86\u4e00\u58f0\uff0c\u5fc3\u4e2d\u6709\u4e00\u80a1\u8bf4\u4e0d\u51fa\u7684\u5473 \u9053\u3002\u4e91\u91cd\u7684\u773c\u4e2d\u4e5f\u653e\u51fa\u4e86\u5f02\u6837\u7684\u5149\u8292\u3002 \u3000\u3000\u8fd9\u5c11\u5973\u7a81\u5982\u5176\u6765\uff0c\u4ea4\u6218\u53cc\u65b9\u90fd\u4e0d\u89c9\u7f13\u4e0b\u4e86\u624b\u3002\u90ed\u6d2a\u53eb\u9053\uff1a\u201c\u8fd9\u5c11\u5973\u5fc5\u662f\u90aa\u95e8\uff0c\u5206\u51fa\u4eba \u6765\uff0c\u6321\u5979\u5165\u9635\u3002\u201d\u90a3\u5c11\u5973\u4e00\u58f0\u4e0d\u53d1\u4ecd\u662f\u7f13\u7f13\u524d\u884c\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u7cbe\u795e\u9661\u632f\uff0c\u7a81\u7136\u4e00\u58f0\u957f\u5578\uff0c\u4ece\u4e00\u4e2a\u77f3\u5806\u4e0a\u98de\u8eab\u4e00\u63a0\u8df3\u4e0a\u7b2c\u4e8c\u4e2a\u77f3\u5806\uff0c\u8fd0\u5251\u5982\u98ce\uff0c \u8fde\u4f24\u6570\u654c\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u8df3\u51fa\u9635\u5916\u643a\u7740\u90a3\u4e2a\u5c11\u5973\u7684\u624b\uff0c\u6ef4\u6cea\u8bf4\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u4f60\u4e5f\u6765\u4e86\uff01\u201d \u3000\u3000\u90a3\u5c11\u5973\u4e00\u628a\u7529\u5f00\u5f20\u4e39\u67ab\u7684\u624b\uff0c\u55d6\u7684\u62d4\u51fa\u8170\u95f4\u4f69\u5251\u9053\uff1a\u201c\u6211\u7684\u54e5\u54e5\u5462\uff1f\u201d\u8fd9\u5c11\u5973\u6b63\u662f\u4e91 \u857e\u3002\u5979\u56e0\u6765\u5230\u4e86\u6c5f\u5357\u6587\u7269\u4e4b\u4e61\uff0c\u5df2\u65e0\u5317\u65b9\u9ed1\u9053\u4e0a\u9669\u6076\uff0c\u6240\u4ee5\u6539\u56de\u4e86\u5973\u88c5\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u4f60\u7684\u54e5\u54e5\u88ab\u56f0\u5728\u8fd9\u77f3\u9635\u4e4b\u4e2d\uff0c\u54b1\u4eec\u5148\u628a\u654c\u4eba\u6740\u6563\u4e86\u518d\u8bf4\u3002\u201d\u90ed\u6d2a\u72ec\u81c2\u6307 \u6325\uff0c\u5206\u5175\u5fa1\u654c\uff0c\u8c03\u51fa\u4e94\u540d\u597d\u624b\u62e6\u622a\u5f20\u3001\u4e91\u4e8c\u4eba\uff0c\u4ed6\u4eec\u6b3a\u8d1f\u4e91\u857e\u662f\u4e2a\u67d4\u5f31\u5c11\u5973\uff0c\u4e94\u4eba\u4e2d\u5012\u6709\u4e09 \u4eba\u5148\u6251\u4e91\u857e\u3002\u53ea\u89c1\u4e91\u857e\u62bd\u51fa\u5b9d\u5251\uff0c\u8f7b\u8f7b\u4e00\u5212\uff0c\u4fe1\u624b\u53d1\u62db\uff0c\u500f\u5730\u98de\u8d77\u4e00\u7247\u9752\u5149\u3002\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6 \u5feb\uff0c\u5f20\u4e39\u67ab\u5251\u62db\u540e\u53d1\u5148\u81f3\uff0c\u500f\u5730\u53c8\u98de\u8d77\u4e00\u7247\u767d\u5149\uff0c\u9752\u5149\u767d\u5149\uff0c\u4e92\u76f8\u4ea4\u7ec7\uff0c\u5e7b\u6210\u5f02\u5f69\uff0c\u5251\u82b1\u9519 \u843d\uff0c\u5982\u7e41\u661f\u70b9\u70b9\uff0c\u7eb7\u6d12\u4e0b\u6765\uff0c\u53cc\u5251\u4e00\u5408\uff0c\u5a01\u529b\u7edd\u4f26\uff0c\u7adf\u5728\u4e00\u62db\u4e4b\u5185\uff0c\u8fde\u523a\u4e86\u4e94\u4e2a\u654c\u4eba\u7684\u7a74 \u9053\uff0c\u8fd9\u4e94\u540d\u597d\u624b\uff0c\u8fde\u201c\u54fc\u201d\u4e5f\u672a\u54fc\u51fa\u4e00\u58f0\u4fbf\u7eb7\u7eb7\u5012\u5730\uff0c\u6eda\u4e0b\u5c71\u5761\u53bb\u4e86\u3002 \u3000\u3000\u90ed\u6d2a\u5927\u5403\u4e00\u60ca\uff0c\u53ea\u89c1\u5f20\u4e39\u67ab\u4e0e\u90a3\u5c11\u5973\uff0c\u8eab\u5f62\u4e00\u6643\uff0c\u5df2\u95ef\u5165\u9635\u4e2d\u3002\u4e24\u4eba\u5728\u77f3\u9635\u91cc\u5de6\u7a7f\u53f3 \u63d2\uff0c\u4fe8\u5982\u873b\u8713\u63a0\u6c34\uff0c\u5f69\u8776\u7a7f\u82b1\uff0c\u53cc\u5251\u6325\u821e\uff0c\u5251\u5149\u7f2d\u7ed5\u4e4b\u4e2d\u53ea\u89c1\u56db\u9762\u516b\u65b9\u90fd\u662f\u5f20\u3001\u4e91\u4e8c\u4eba\u7684\u8eab \u5f71\u3002\u77f3\u9635\u4e4b\u4e2d\uff0c\u9752\u767d\u4e8c\u8272\u5251\u5149\uff0c\u7fe9\u82e5\u60ca\u9e3f\uff0c\u5b9b\u5982\u6e38\u9f99\uff0c\u5ffd\u4e1c\u5ffd\u897f\uff0c\u5ffd\u805a\u5ffd\u6563\uff0c\u516b\u9635\u56fe\u867d\u7136\u662f \u91cd\u95e8\u53e0\u6237\uff0c\u5730\u5f62\u903c\u7a84\uff0c\u8fd9\u9752\u767d\u4e8c\u8272\u7684\u5251\u5149\uff0c\u6eda\u6765\u6eda\u53bb\uff0c\u5374\u5982\u6c34\u94f6\u6cfb\u5730\uff0c\u65e0\u5b54\u4e0d\u5165\uff0c\u53cc\u5251\u6240\u5230 \u4e4b\u5916\uff0c\u65e0\u4e0d\u62ab\u9761\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u90ed\u6d2a\u5e26\u6765\u7684\u4eba\u5df2\u6b7b\u4f24\u516b\u4e5d\u3002 \u3000\u3000\u5bdf\u9c81\u56fe\u53cc\u773c\u901a\u7ea2\uff0c\u62a2\u7740\u51fa\u6765\uff0c\u53cc\u65a7\u75be\u5288\uff0c\u5f20\u4e39\u67ab\u4e00\u58f0\u957f\u7b11\u53cd\u624b\u4e00\u5251\uff0c\u81ea\u5de6\u81f3\u5404\uff0c\u5212\u4e86\u4e00 \u9053\u5706\u5f27\uff1b\u4e91\u857e\u9752\u51a5\u5b9d\u5251\u626c\u7a7a\u4e00\u95ea\u4e5f\u81ea\u53f3\u81f3\u5de6\uff0c\u5212\u4e86\u4e00\u9053\u5706\u5f27\uff0c\u53cc\u5251\u4e00\u5408\uff0c\u5408\u6210\u4e00\u9053\u5149\u5708\uff0c\u7d27 \u7d27\u4e00\u7b8d\u3002\u53ea\u542c\u5f97\u4e00\u9635\u91d1\u94c1\u4ea4\u9e23\u4e4b\u58f0\uff0c\u5bdf\u9c81\u56fe\u7684\u53cc\u65a7\u9707\u5f97\u5012\u5377\u56de\u6765\u864e\u53e3\u6d41\u8840\uff0c\u51e0\u4e4e\u8131\u624b\u98de\u51fa\uff0c \u4ed6\u7d20\u4ee5\u795e\u529b\u81ea\u8d1f\uff0c\u6599\u4e0d\u5230\u5f20\u4e39\u67ab\u4e0e\u4e91\u857e\uff0c\u53cc\u5251\u9f50\u51fa\uff0c\u5c45\u7136\u786c\u63a5\u786c\u67b6\uff0c\u529b\u9053\u4e4b\u5f3a\uff0c\u8fd8\u8fdc\u5728\u4ed6\u4e4b \u4e0a\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u89c1\u4ed6\u65a7\u5934\u5c45\u7136\u5e76\u672a\u8131\u624b\uff0c\u4e5f\u6697\u6697\u60ca\u5f02\uff0c\u7b11\u9053\uff1a\u201c\u518d\u63a5\u8fd9\u62db\uff01\u201d\u4fa7\u8eab\u4e00\u5251\uff0c\u5feb\u82e5\u98d8 \u98ce\uff0c\u5bdf\u9c81\u56fe\u53cc\u65a7\u4e00\u5206\uff0c\u4e00\u62db\u201c\u6307\u5929\u5212\u5730\u201d\uff0c\u4e0a\u62a4\u5929\u5ead\uff0c\u4e0b\u65a9\u654c\u8db3\uff0c\u5ffd\u89c1\u5f20\u4e39\u67ab\u5251\u950b\u4e00\u6643\uff0c\u504f \u65c1\u4e00\u5f15\uff0c\u4e91\u857e\u5237\u7684\u4e00\u5251\uff0c\u7adf\u4ece\u4ed6\u7edd\u5bf9\u6599\u4e0d\u5230\u7684\u65b9\u4f4d\uff0c\u75be\u523a\u8fdb\u6765\uff0c\u6ce2\u7684\u4e00\u58f0\uff0c\u53cc\u65a7\u9f50\u9f50\u786e\u4e0b\uff0c \u5f20\u3001\u4e91\u4e8c\u4eba\u500f\u5730\u8df3\u5f00\uff0c\u5bdf\u9c81\u56fe\u53cc\u65a7\u72c2\u626b\uff0c\u4e71\u77f3\u7eb7\u98de\uff0c\u6709\u5982\u5c71\u5d29\u5730\u88c2\u3002\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u4f60\u56de\u53bb \u5427\uff01\u201d\u957f\u5251\u75be\u51fa\u8f7b\u8f7b\u5728\u4ed6\u80cc\u5fc3\u5927\u7a74\u70b9\u4e86\u4e00\u4e0b\uff0c\u5bdf\u9c81\u56fe\u7a81\u7136\u5927\u53eb\u4e00\u58f0\uff0c\u53cc\u65a7\u4e00\u629b\u53e3\u5410\u9c9c\u8840\uff0c\u6643 \u4e86\u51e0\u6643\u4e00\u8de4\u8dcc\u4e0b\uff0c\u5012\u5730\u4e0d\u8d77\u7adf\u662f\u6b7b\u4e86\u3002 \u3000\u3000\u90ed\u6d2a\u5fc3\u80c6\u4ff1\u88c2\uff0c\u8d81\u7740\u6c99\u77f3\u5f25\u7a7a\uff0c\u5355\u638c\u6491\u5730\uff0c\u5c45\u7136\u624b\u8db3\u5e76\u7528\u4f3c\u9640\u87ba\u822c\u5728\u5730\u4e0a\u6eda\u8f6c\uff0c\u89c5\u8def\u9003 \u751f\u3002\u6fb9\u53f0\u955c\u660e\u89d1\u4e2a\u6b63\u7740\uff0c\u559d\u58f0\uff1a\u201c\u54ea\u91cc\u8d70\uff1f\u201d\u8dc3\u51fa\u4e00\u5251\uff0c\u81ea\u524d\u5fc3\u7a7f\u5230\u540e\u5fc3\uff0c\u773c\u89c1\u4e5f\u4e0d\u80fd\u6d3b \u4e86\u3002 \u3000\u3000\u8fd9\u4e00\u6218\u60e8\u70c8\u5f02\u5e38\uff0c\u90ed\u6d2a\u5e26\u6765\u7684\u4eba\u5168\u519b\u8986\u6ca1\u3002\u5f20\u4e39\u67ab\u8fd9\u8fb9\uff0c\u5927\u5185\u4e03\u5927\u9ad8\u624b\uff0c\u6b7b\u4e86\u56db\u4eba\uff0c\u4f24 \u4e86\u4e00\u4eba\uff0c\u53ea\u6709\u94c1\u81c2\u91d1\u733f\u4e0e\u4e09\u82b1\u5251\u5e78\u5f97\u65e0\u6059\uff0c\u6d1e\u5ead\u5e84\u4e3b\u7684\u5e84\u4e01\u4e5f\u6b7b\u4f24\u4e86\u597d\u51e0\u4eba\uff0c\u8fd8\u6709\u4e91\u91cd\u53d7\u4e00 \u6bd2\u638c\u4e4b\u4f24\uff0c\u4f24\u52bf\u5982\u4f55\uff0c\u5c1a\u672a\u77e5\u9053\u3002 \u3000\u3000\u5f85\u5f97\u98ce\u5e73\u6c99\u6b62\uff0c\u5f20\u4e39\u67ab\u5f15\u7740\u4e91\u857e\u8d70\u5230\u4e91\u91cd\u8ddf\u524d\uff0c\u53ea\u89c1\u4e91\u91cd\u773c\u775b\u534a\u95ed\uff0c\u624b\u81c2\u540a\u6876\u822c\u7c97\u5927\u3002 \u4e91\u857e\u6cea\u627f\u53cc\u776b\uff0c\u6251\u4e0a\u524d\u9053\uff1a\u201c\u54e5\u54e5\uff01\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u5c0f\u5144\u5f1f\uff0c\u8ba9\u4f60\u54e5\u54e5\u6b47\u6b47\uff0c\u54b1\u4eec \u5148\u80cc\u4ed6\u56de\u5e84\u5b50\u53bb\u3002\u201d\u7ea2\u53d1\u5996\u9f99\u90a3\u4e00\u638c\u5267\u6bd2\u975e\u5e38\uff0c\u4e91\u91cd\u5e78\u4ed7\u7740\u5185\u529f\u6df1\u582a\uff0c\u8fd0\u6c14\u5fa1\u6bd2\uff0c\u8fd9\u624d\u4e0d\u81f3 \u4e8e\u4ee4\u6bd2\u6c14\u653b\u5fc3\uff0c\u4fdd\u5f97\u6027\u547d\u3002\u5f20\u4e39\u67ab\u963b\u6b62\u4e91\u857e\u591a\u4e0e\u4e91\u91cd\u8bf4\u8bdd\uff0c\u5b9e\u662f\u4e00\u756a\u597d\u610f\uff0c\u514d\u5f97\u4ee4\u4ed6\u5206\u795e\u3002 \u4e91\u857e\u54ea\u77e5\u5389\u5bb3\uff0c\u4e00\u9635\u6fc0\u52a8\uff0c\u5fcd\u4e0d\u7740\u53c8\u9053\uff1a\u201c\u54e5\u54e5\u4f60\u600e\u4e48\u5566\uff1f\u5927\uff0d\uff0d\u4e39\u67ab\uff0c\u4ed6\u7684\u4f24\u5389\u5bb3\u4e48\uff1f\u201d \u5979\u4ee5\u524d\u53eb\u60ef\u4e86\u5f20\u4e39\u67ab\u505a\u201c\u5927\u54e5\u201d\uff0c\u8fd9\u4e24\u5b57\u51e0\u4e4e\u51b2\u53e3\u800c\u51fa\uff0c\u5230\u4e86\u53e3\u8fb9\uff0c\u624d\u6539\u5524\u201c\u4e39\u67ab\u201d\uff0c\u8138\u4e0a \u4e0d\u89c9\u6cdb\u8d77\u4e00\u9635\u7ea2\u6f6e\uff0c\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u6ca1\uff0d\uff0d\u6ca1\u4ec0\u4e48\uff0c\u4f46\u8fd8\u662f\u8ba9\u4ed6\u6b47\u6b47\u7684\u597d\u3002\u201d \u3000\u3000\u4e91\u91cd\u5ffd\u5730\u5f20\u5f00\u4e86\u773c\uff0c\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u201d\u4e91\u857e\u9053\uff1a\u201c\u54e5\u54e5\uff0c\u6211\u662f\u4f60\u7684\u4eb2\u59b9\u3002\u201d\u4e91\u91cd\u77a5\u4e86\u5f20 \u4e39\u67ab\u4e00\u773c\uff0c\u5ffd\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u662f\u6211\u7684\u59b9\u5b50\uff0c\u83ab\u8ba4\u9519\u4eba\u4e86\u5427\uff1f\u201d\u4e91\u857e\u54ed\u9053\uff1a\u201c\u54e5\u54e5\uff0c\u4f60\u597d\u5fcd\u5fc3\uff0c \u6211\u627e\u4f60\u627e\u5f97\u597d\u8f9b\u82e6\u5440\uff01\u201d\u4e91\u91cd\u9053\uff1a\u201c\u6211\u6709\u8fd9\u6837\u597d\u7684\u59b9\u5b50\uff1f\u201d\u4e91\u857e\u9053\uff1a\u201c\u6211\u771f\u662f\u4f60\u7684\u4eb2\u59b9\u5b50 \u5440\uff0c\u4f60\u82e5\u4e0d\u4fe1\uff0d\uff0d\u201d\u4e91\u91cd\u5389\u58f0\u53f1\u9053\uff1a\u201c\u6709\u4f55\u51ed\u8bc1\uff1f\u201d\u4e91\u857e\u54ac\u4e86\u54ac\u7259\uff0c\u4ece\u6000\u4e2d\u6478\u51fa\u7f8a\u76ae\u8840\u4e66 \u9053\uff1a\u201c\u54e5\u54e5\uff0c\u4f60\u770b\uff01\u201d\u8fd9\u7f8a\u76ae\u8840\u4e66\u5144\u59b9\u4e24\u5404\u6709\u4e00\u4efd\uff0c\u81ea\u662f\u6700\u597d\u7684\u51ed\u8bc1\u3002\u4e91\u91cd\u659c\u773c\u4e00\u77a5\uff0c\u53ea\u89c1 \u4e24\u9897\u53c8\u5706\u53c8\u5927\u7684\u6cea\u73e0\u4ece\u4e91\u857e\u773c\u89d2\u843d\u4e0b\u6765\u3002\u4e91\u91cd\u9053\uff1a\u201c\u54fc\uff0c\u4f60\u8fd8\u6709\u8138\u62ff\u51fa\u7237\u7237\u7684\u8840\u4e66\uff1f\u201d\u4e91\u91cd \u5176\u5b9e\u662f\u5df2\u77e5\u5979\u662f\u59b9\u5b50\uff0c\u6545\u610f\u903c\u5979\u62ff\u51fa\u8840\u4e66\uff01\u4e91\u857e\u5fc3\u4e2d\u4e00\u9178\uff0c\u6cea\u73e0\u513f\u5728\u773c\u7736\u4e2d\u6253\u8f6c\uff0c\u5374\u662f\u54ed\u4e0d \u51fa\u6765\u3002\u4e91\u91cd\u4e00\u6307\u5f20\u4e39\u67ab\uff0c\u6b63\u60f3\u6570\u8bf4\uff0c\u5f20\u4e39\u67ab\u5ffd\u7136\u4e00\u8dc3\u800c\u524d\uff0c\u9a88\u6307\u5982\u621f\uff0c\u671d\u7740\u4e91\u91cd\u7684\u624b\u81c2\u91cd\u91cd \u4e00\u6233\u3002\u4e91\u857e\u60ca\u9053\uff1a\u201c\u4f60\u5e72\u4ec0\u4e48\uff1f\u201d\u4e91\u91cd\u5438\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5f20\u4e39\u67ab\uff0c\u4f60\u4e0d\u5fc5\u6545\u610f\u6765\u732e\u6bb7\u52e4\uff0c\u6211 \u5c31\u662f\u6b7b\u4e86\uff0c\u4e5f\u4e0d\u613f\u518d\u53d7\u4f60\u7684\u6069\u5178\u3002\u201d\u4e91\u857e\u8fd9\u624d\u9192\u8d77\uff0c\u8fd9\u4e43\u662f\u5f20\u4e39\u67ab\u62ff\u624b\u7684\u6025\u6551\u7edd\u6280\uff0c\u8017\u81ea\u5df1 \u771f\u5143\u4e4b\u6c14\uff0c\u66ff\u4e91\u91cd\u963b\u6ede\u4e86\u81c2\u4e0a\u8840\u6db2\u7684\u6d41\u52a8\uff0c\u514d\u5f97\u6bd2\u6c14\u6025\u901f\u4e0a\u5347\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u54b1\u4eec\u8fd8\u662f\u5feb\u56de\u5e84\u5b50\u53bb\u5427\uff0c\u6765\uff0c\u6765\uff0c\u54b1\u4eec\u8c08\u8c08\u3002\u201d\u4f38\u624b\u7275\u4e91\u857e\u7684\u8863 \u8896\u3002\u4e91\u857e\u77a7\u4e86\u54e5\u54e5\u4e00\u773c\uff0c\u624b\u8155\u4e00\u7ffb\uff0c\u5c06\u5f20\u4e39\u67ab\u7684\u624b\u7529\u8131\uff0c\u9762\u8272\u60e8\u767d\uff0c\u4e0d\u53d1\u4e00\u8a00\u3002\u5f20\u4e39\u67ab\u96be\u8fc7 \u4e4b\u6781\uff0c\u9eef\u7136\u9000\u4e0b\uff0c\u751a\u662f\u5c34\u5c2c\u3002 \u3000\u3000\u6fb9\u53f0\u5927\u5a18\u6447\u4e86\u6447\u5934\u3002\u6fb9\u53f0\u955c\u660e\u770b\u5f97\u5341\u5206\u60ca\u5f02\uff0c\u5fc3\u9053\uff1a\u201c\u542c\u5f20\u4e39\u67ab\u5728\u77f3\u6d1e\u4e2d\u4e4b\u8c08\u8bdd\u8bed\u6c14\uff0c \u770b\u4ed6\u5bf9\u5979\u5982\u6b64\u4eb2\u70ed\uff0c\u8fd9\u5c11\u5973\u5f53\u662f\u4ed6\u7684\u5fc3\u4e0a\u4e4b\u4eba\uff0c\u4f55\u4ee5\u5979\u5374\u5bf9\u4ed6\u51b7\u9177\u5982\u65af\uff1f\u201d\u62ac\u5934\u4e00\u671b\uff0c\u5ffd\u89c1 \u5f20\u4e39\u67ab\u5411\u5979\u8f7b\u8f7b\u62db\u624b\u3002 \u3000\u3000\u6fb9\u53f0\u955c\u660e\u6ee1\u8179\u72d0\u7591\uff0c\u8d70\u4e86\u8fc7\u53bb\uff0c\u53ea\u542c\u5f97\u5f20\u4e39\u67ab\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u4e91\u91cd\u6240\u53d7\u7684\u6bd2\u4f24\uff0c\u975e\u4ed6\u6240\u80fd \u81ea\u7597\u3002\u6211\u6709\u7956\u4f20\u7684\u4e39\u836f\uff0c\u6211\u6559\u4f60\u6cbb\u6cd5\uff0c\u4f60\u66ff\u6211\u628a\u4ed6\u533b\u597d\u3002\u201d\u6fb9\u53f0\u955c\u660e\u63a5\u8fc7\u4e86\u4e39\u836f\u95ee\u9053\uff1a\u201c\u8fd9 \u5c11\u5973\u662f\u4ec0\u4e48\u4eba\uff1f\u201d\u5f20\u4e39\u67ab\u82e6\u7b11\u9053\uff1a\u201c\u55ef\uff0c\u6211\u662f\u5979\u7684\u4ec7\u4eba\uff01\u201d \u3000\u3000\u6fb9\u53f0\u955c\u660e\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u5979\u662f\u4f60\u7684\u4ec7\u4eba\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u4e0d\uff0c\u6211\u662f\u5979\u7684\u4ec7\u4eba\u3002 \u4e0d\uff0c\u5979\u5f53\u6211\u662f\u5979\u7684\u4ec7\u4eba\u3002\u201d\u6fb9\u53f0\u955c\u660e\u9053\uff1a\u201c\u90a3\u4f60\u4e3a\u4f55\u4e0d\u4eb2\u81ea\u6cbb\u4ed6\uff0c\u5c06\u8fd9\u51a4\u4ec7\u5316\u89e3\uff1f\u201d\u5f20\u4e39\u67ab \u7b11\u9053\uff1a\u201c\u6211\u5c31\u662f\u4e0d\u60f3\u4ee4\u4ed6\u77e5\u9053\u3002\u514d\u5f97\u4ed6\u8bf4\u6211\u662f\u6545\u610f\u4e58\u4ed6\u4e4b\u5371\uff0c\u65bd\u6069\u671b\u62a5\u3002\u201d \u3000\u3000\u6d1e\u5ead\u5e84\u4e3b\u53eb\u4e00\u4e2a\u5e84\u4e01\u80cc\u8d77\u4e91\u91cd\uff0c\u4e91\u857e\u8ddf\u5728\u540e\u9762\uff0c\u5077\u5077\u5f80\u540e\u4e00\u77a7\uff0c\u5ffd\u89c1\u5f20\u4e39\u67ab\u4e0e\u6fb9\u53f0\u955c\u660e \u8033\u9b13\u25a1\u78e8\uff0c\u4f4e\u58f0\u8c08\u7b11\uff0c\u5fc3\u4e2d\u53c8\u662f\u4e00\u9178\uff0c\u60f3\u9053\uff1a\u201c\u597d\uff0c\u4f60\u4e0d\u7406\u6211\uff0c\u6211\u4e5f\u4e0d\u7406\u4f60\u3002\u6bd4\u5982\u4ece\u6765\u6ca1\u6709 \u8ba4\u8bc6\u8fc7\u8fd9\u4e00\u4e2a\u4eba\uff0c\u5927\u5bb6\u6563\u4e86\u5e72\u51c0\uff01\u201d\u67d4\u80a0\u5bf8\u65ad\uff0c\u5ffd\u89c9\u60b2\u4ece\u4e2d\u6765\uff0c\u4e0d\u53ef\u65ad\u7edd\uff0c\u6cea\u73e0\u6eda\u6eda\u6d41\u4e0b\u3002 \u6d1e\u5ead\u5e84\u4e3b\u5947\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4f60\u7684\u54e5\u54e5\u4f24\u52bf\u5e76\u65e0\u6076\u5316\uff0c\u4f60\u54ed\u4ec0\u4e48\uff1f\u201d\u4e91\u857e\u597d\u50cf\u542c\u800c\u4e0d\u95fb\uff0c\u4ecd\u662f\u545c \u545c\u54bd\u54bd\u555c\u6ce3\u4e0d\u6b62\u3002 \u3000\u3000\u56de\u5230\u6d1e\u5ead\u5c71\u5e84\uff0c\u5c71\u4e0b\u5df2\u662f\u708a\u70df\u56db\u8d77\u3002\u6d1e\u5ead\u5e84\u4e3b\u628a\u4e91\u91cd\u5b89\u987f\u5728\u4e00\u95f4\u9759\u5ba4\uff0c\u53eb\u4eba\u597d\u751f\u7167\u6599\u3002 \u53c8\u5fd9\u7740\u53eb\u5e84\u4e01\u5f04\u996d\uff0c\u94c1\u81c2\u91d1\u733f\u4e0e\u4e09\u82b1\u5251\u751a\u662f\u4e0d\u597d\u610f\u601d\uff0c\u6d1e\u5ead\u5e84\u4e3b\u751f\u6027\u8c41\u8fbe\uff0c\u7edd\u53e3\u4e0d\u63d0\u4ed6\u4eec\u6765 \u5bfb\u5b9d\u4e4b\u4e8b\uff0c\u4e24\u4eba\u5728\u5e2d\u95f4\u8c22\u4e86\u5f20\u4e39\u67ab\u6551\u547d\u4e4b\u6069\uff0c\u5404\u81ea\u5b89\u6b47\u3002 \u3000\u3000\u6fb9\u53f0\u955c\u660e\u53d7\u4e86\u5f20\u4e39\u67ab\u4e4b\u6258\uff0c\u665a\u996d\u8fc7\u540e\uff0c\u5e26\u4e86\u4e39\u836f\uff0c\u6084\u6084\u5f80\u4e91\u91cd\u7684\u9759\u5ba4\uff0c\u5ba4\u4e2d\u70db\u5f71\u6447\u7ea2\uff0c \u7eb1\u7a97\u4e0a\u73b0\u51fa\u4e91\u857e\u5f71\u5b50\u3002\u6fb9\u53f0\u955c\u660e\u811a\u6b65\u4e00\u505c\uff0c\u53ea\u542c\u5f97\u4e91\u857e\u8bf4\u9053\uff1a\u201c\u54e5\u54e5\uff01\u7237\u7237\u4e0d\u662f\u4ed6\u7236\u4eb2\u5bb3 \u7684\u3002\u4e8e\u9601\u8001\u5df2\u8bf4\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u8fd9\u514d\u4ec7\u4e0d\u62a5\u4e5f\u7f62\u3002\u201d\u4e91\u91cd\u9053\uff1a\u201c\u4e8c\u5341\u5e74\u7267\u9a6c\u4e4b\u4ec7\uff0c\u53c8\u5982\u4f55 \u8bf4\uff1f\u201d\u4e91\u857e\u9053\uff1a\u201c\u4ed6\u7236\u4eb2\u6b64\u4e8b\uff0c\u786e\u662f\u505a\u5f97\u4e0d\u8be5\uff0c\u4f46\u4e5f\u4e0d\u81f3\u4e8e\u4e0d\u5171\u6234\u5929\u3002\u201d\u4e91\u91cd\u51b7\u7b11\u9053\uff1a\u201c\u4f60 \u5012\u4f1a\u66ff\u4ec7\u4eba\u8bf4\u8bdd\uff01\u201d\u4e91\u857e\u54ed\u9053\uff1a\u201c\u54e5\u54e5\uff01\u201d\u4e91\u91cd\u9053\uff1a\u201c\u600e\u4e48\uff1f\u4e91\u5bb6\u7684\u513f\u5973\u4e0d\u8bb8\u8fd9\u4e48\u6ca1\u6709\u5fd7 \u6c14\uff01\u201d\u4e91\u857e\u54ac\u4e86\u54ac\u7259\uff0c\u628a\u773c\u6cea\u54bd\u4e86\u56de\u53bb\uff0c\u9053\uff1a\u201c\u4f60\u5e08\u7236\u4e5f\u8fd9\u4e48\u8bf4\uff0c\u4ed6\u8bf4\u5f20\u4e39\u67ab\u662f\u6211\u8f88\u4e2d\u4eba\uff0c \u5916\u654c\u4e3a\u91cd\uff0c\u80fd\u5316\u89e3\u4fbf\u5316\u89e3\u4e86\u5427\u3002\u201d\u4e91\u91cd\u53c8\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u5ffd\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u559c\u6b22\u8fd9\u59d3\u5f20\u7684\u5c0f \u5b50\uff01\u201d\u4e91\u857e\u672c\u6765\u5df2\u5fcd\u4f4f\u4e0d\u54ed\uff0c\u542c\u4e86\u6b64\u8bdd\uff0c\u53c8\u7f9e\u53c8\u6c14\u53c8\u6124\uff0c\u8bf4\u9053\uff1a\u201c\u8c01\u8bf4\u6211\u6b22\u559c\u4ed6\u4e86\uff0c\u4ed6\uff0d \uff0d\u201d\u4e91\u91cd\u622a\u7740\u8bf4\u9053\uff1a\u201c\u4f60\u6b22\u559c\u4ed6\u4e5f\u597d\uff0c\u4e0d\u6b22\u559c\u4ed6\u4e5f\u597d\uff0c\u603b\u4e4b\uff0c\u6211\u4e0d\u8bb8\u4f60\u5ac1\u4ed6\uff01\u201d\u4e91\u857e\u518d\u5fcd\u4e0d \u4f4f\uff0c\u51b2\u53e3\u8bf4\u9053\uff1a\u201c\u4ed6\u81ea\u6709\u610f\u4e2d\u4e4b\u4eba\uff0c\u6211\u8fd9\u751f\u4e0d\u5ac1\uff0c\u4f60\u4e0d\u5fc5\u4e3a\u6211\u64cd\u5fc3\uff01\u201d\u4e91\u91cd\u6014\u4e86\u4e00\u6014\uff0c\u5fc3\u5934 \u66f4\u6c14\uff0c\u60f3\u9053\uff1a\u201c\u539f\u6765\u4f60\u662f\u56e0\u4e3a\u5ac1\u4e0d\u4e0a\u4ed6\uff0c\u8fd9\u624d\u4e0d\u5ac1\u3002\u201d\u6b63\u60f3\u518d\u9a82\uff0c\u89c1\u4e91\u857e\u53cc\u773c\u901a\u7ea2\uff0c\u60f3\u8d77\u81ea \u5df1\u53ea\u6709\u8fd9\u4e48\u4e00\u4e2a\u59b9\u5b50\uff0c\u800c\u4e14\u662f\u5206\u4e86\u5341\u4f59\u5e74\u4e4b\u540e\u7b2c\u4e00\u6b21\u76f8\u9022\uff0c\u5fc3\u4e2d\u4ea6\u9887\u89c9\u4e0d\u5fcd\uff0c\u53f9\u4e86\u53e3\u6c14\uff0c\u5ffd \u542c\u5f97\u95e8\u5916\u6709\u4eba\u54b3\u4e86\u4e00\u58f0\uff0c\u623f\u95e8\u5f00\u5904\uff0c\u6fb9\u53f0\u955c\u660e\u8d70\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u4e91\u857e\u521a\u521a\u8bf4\u8d77\u5979\uff0c\u9661\u7136\u89c1\u5979\u6765\u5230\u52c9\u5f3a\u7b11\u4e86\u4e00\u7b11\u3002\u4e91\u91cd\u9053\uff1a\u201c\u4e0d\u6562\u6709\u52b3\u59d1\u5a18\u63a2\u671b\u3002\u201d\u6fb9\u53f0 \u955c\u660e\u9053\uff1a\u201c\u8ba9\u6211\u770b\u770b\u4f60\u4f24\u52bf\u3002\u201d\u4e91\u91cd\u9053\uff1a\u201c\u6ca1\u6709\u4ec0\u4e48\uff0c\u591a\u8c22\u5173\u5fc3\u3002\u4e91\u857e\uff0c\u4f60\u66ff\u6211\u9001\u8fd9\u4f4d\u59d1\u5a18 \u56de\u53bb\u3002\u201d\u6fb9\u53f0\u955c\u660e\u672c\u662f\u5fc3\u4e2d\u6709\u6c14\uff0c\u77a5\u4ed6\u4e00\u773c\uff0c\u89c1\u4ed6\u6545\u610f\u505a\u51fa\u6ca1\u4e8b\u7684\u6837\u5b50\uff0c\u5fcd\u4e0d\u4f4f\u5657\u55e4\u4e00\u7b11\uff0c \u9053\uff1a\u201c\u771f\u7684\u6ca1\u6709\u4ec0\u4e48\u5417\uff1f\u4f60\u5438\u53e3\u6c14\u770b\u770b\u3002\u201d \u3000\u3000\u4e91\u91cd\u9002\u624d\u4e0e\u4e91\u857e\u4e89\u8bba\uff0c\u52a8\u4e86\u771f\u6c14\uff0c\u4f24\u53e3\u53d1\u4f5c\uff0c\u6bd2\u6c14\u53c8\u5df2\u4e0a\u5347\uff0c\u5438\u4e86\u53e3\u6c14\uff0c\u80f8\u81c6\u53d1\u95f7\u6b32 \u5455\u3002\u6fb9\u53f0\u955c\u660e\u9053\uff1a\u201c\u4f60\u518d\u4e0d\u533b\u6cbb\uff0c\u8fc7\u4e0d\u4e86\u4eca\u665a\u5b50\u65f6\u3002\u5927\u4e08\u592b\u867d\u8bf4\u89c6\u6b7b\u5982\u5f52\uff0c\u8fd9\u6837\u6b7b\u4e86\uff0c\u5374\u4e5f \u672a\u514d\u4e0d\u503c\u3002\u5440\uff0c\u82e5\u7136\u662f\u6211\uff0c\u6211\u5c31\u4e0d\u5145\u8fd9\u95e8\u5b50\u7684\u82f1\u96c4\u597d\u6c49\u3002\u201d\u4e91\u91cd\u9762\u8272\u4e00\u53d8\uff0c\u9661\u7136\u95f4\u89c9\u5f97\u75db\u5f97 \u66f4\u751a\u3002\u4e91\u857e\u9053\uff1a\u201c\u6fb9\u53f0\u59d1\u5a18\uff0c\u4e0d\u80fd\u533b\u4e48\uff1f\u201d\u6fb9\u53f0\u955c\u660e\u9053\uff1a\u201c\u53ea\u6015\u4f60\u7684\u54e5\u54e5\u62d2\u4eba\u4e8e\u5343\u91cc\u4e4b \u5916\u3002\u201d\u8fd9\u8bdd\u5b9e\u662f\u6697\u542b\u25a1\u5f04\uff0c\u6307\u4ed6\u62d2\u7edd\u5f20\u4e39\u67ab\u4e4b\u4e8b\u800c\u8a00\u3002\u4e91\u91cd\u5374\u542c\u4e0d\u51fa\u6765\uff0c\u9053\uff1a\u201c\u59d1\u5a18\u8a00\u91cd \u4e86\uff0c\u6211\u5728\u8d35\u5e84\u4f5c\u5ba2\uff0c\u5b9e\u662f\u4e0d\u6562\u591a\u6240\u9ebb\u70e6\u3002\u201d\u4e91\u857e\u5fc3\u4e2d\u4e00\u52a8\uff0c\u60f3\u9053\uff1a\u201c\u539f\u6765\u5f20\u4e39\u67ab\u90fd\u544a\u8bc9\u4e86 \u5979\u3002\u201d\u5fc3\u4e2d\u53c8\u662f\u4e00\u9178\uff0c\u4f46\u4e3a\u7740\u54e5\u54e5\u6027\u547d\uff0c\u5fcd\u53d7\u59d4\u5c48\uff0c\u8bf4\u9053\uff1a\u201c\u82e5\u5f97\u59d1\u5a18\u533b\u6cbb\uff0c\u6211\u4eec\u5144\u59b9\u611f\u6fc0 \u4e0d\u5c3d\u3002\u201d\u6fb9\u53f0\u955c\u660e\u9053\uff1a\u201c\u611f\u6fc0\u4e0d\u5fc5\u3002\u201d\u672c\u60f3\u7eed\u8bf4\uff1a\u201c\u4f46\u6c42\u4f60\u4e0d\u6068\u6211\u9a82\u6211\uff0c\u6211\u5c31\u5fc3\u6ee1\u610f\u8db3\u3002\u201d \u8bdd\u5230\u53e3\u8fb9\uff0c\u8111\u6d77\u4e2d\u5ffd\u7136\u73b0\u51fa\u5f20\u4e39\u67ab\u8bda\u631a\u7684\u76ee\u5149\uff0c\u60f3\u9053\uff1a\u201c\u6211\u4f55\u82e6\u4f24\u4ed6\u5fc3\u7231\u4e4b\u4eba\u7684\u5fc3\u5462\u3002\u201d\u770b \u4e86\u4e91\u857e\u4e00\u773c\uff0c\u5fc3\u4e2d\u6697\u81ea\u53f9\u9053\uff1a\u201c\u8fd9\u59d1\u5a18\u6bd5\u7adf\u6bd4\u6211\u6709\u798f\u5f97\u591a\u3002\u201d \u3000\u3000\u6fb9\u53f0\u955c\u660e\u53d6\u51fa\u4e39\u836f\uff0c\u4e00\u79cd\u5185\u670d\uff0c\u4e00\u79cd\u5916\u6577\uff0c\u53c8\u53d6\u51fa\u4e00\u5f20\u94f6\u5200\uff0c\u4e00\u5305\u68c9\u82b1\uff0c\u53eb\u4e91\u857e\u5e2e\u5fd9\uff0c \u5c06\u4e91\u91cd\u8863\u8896\u5377\u8d77\uff0c\u94f6\u5200\u4ea4\u53c9\u5212\u4e86\u4e2a\u5341\u5b57\uff0c\u6349\u7740\u4e91\u91cd\u7684\u81c2\u818a\uff0c\u5341\u6307\u7d27\u6309\uff0c\u5c06\u8113\u8840\u6324\u4e86\u51fa\u6765\uff0c\u53c8 \u8165\u53c8\u81ed\uff0c\u4e00\u9762\u6324\u4e00\u9762\u7528\u836f\u5916\u6577\u3002\u4e91\u91cd\u8fd9\u6761\u81c2\u818a\uff0c\u672c\u6765\u662f\u9ebb\u6728\u5f97\u6beb\u65e0\u77e5\u89c9\uff0c\u6e10\u6e10\u89c9\u5f97\u6fb9\u53f0\u955c\u660e \u7684\u7ea4\u7ea4\u5341\u6307\uff0c\u5728\u81ea\u5df1\u7684\u808c\u8089\u4e0a\u6469\u6332\u8f6c\u52a8\uff0c\u6ed1\u817b\u817b\u7684\u597d\u4e0d\u8212\u670d\u3002\u4e91\u91cd\u5728\u6f20\u5317\u957f\u5927\uff0c\u5c11\u89c1\u5973\u5b50\uff0c \u66f4\u4f55\u51b5\u8fd9\u6837\u5065\u7f8e\u5a40\u5a1c\u7684\u5973\u5b50\uff0c\u987f\u65f6\u95f4\u53ea\u89c9\u5fc3\u5934\u535c\u535c\u4e71\u8df3\uff0c\u9762\u4e0a\u53d1\u70ed\u8bf4\u9053\uff1a\u201c\u59d1\u5a18\u5927\u6069\uff0c\u6ca1\u9f7f \u4e0d\u5fd8\uff0c\u53ea\u662f\u592a\u4eb5\u6e0e\u4e86\u59d1\u5a18\u4e86\uff01\u201d\u6fb9\u53f0\u955c\u660e\u5934\u4e5f\u4e0d\u62ac\uff0c\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u770b\u4f60\u4e5f\u662f\u4e2a\u6602\u85cf\u7537\u5b50\uff0c\u4e3a \u4f55\u50cf\u5973\u513f\u5bb6\u7684\u5ff8\u6029\u4f5c\u6001\uff1f\u201d\u4e91\u91cd\u7d20\u4ee5\u201c\u786c\u6c49\u201d\u81ea\u547d\uff0c\u82e5\u7136\u5e73\u65e5\u6709\u4eba\u8bf4\u4ed6\u5973\u513f\u4e4b\u6001\uff0c\u4ed6\u5fc5\u7136\u4f1a \u8ba4\u4e3a\u662f\u83ab\u5927\u7684\u4fae\u8fb1\u3002\u800c\u4eca\u88ab\u6fb9\u53f0\u955c\u660e\u8c03\u4f83\uff0c\u5374\u662f\u611f\u5230\u975e\u5e38\u8212\u670d\uff0c\u8138\u4e0a\u66f4\u53d1\u70ed\u4e86\u3002 \u3000\u3000\u4e91\u857e\u9053\uff1a\u201c\u591a\u8c22\u59d0\u59d0\uff0c\u836f\u5df2\u6577\u4e86\uff0c\u8ba9\u6211\u6765\u670d\u4f8d\u5427\u3002\u201d\u6fb9\u53f0\u955c\u660e\u6577\u5b8c\u4e86\u836f\uff0c\u4fbf\u60f3\u79bb\u5f00\uff0c\u542c \u4e86\u4e91\u857e\u7684\u8bdd\uff0c\u7acb\u523b\u653e\u624b\u3002\u4ea4\u4ee3\u4e86\u51e0\u4ef6\u670d\u4f8d\u75c5\u4eba\u8981\u6ce8\u610f\u7684\u4e8b\u60c5\uff0c\u95f2\u8bdd\u66f4\u4e0d\u591a\u8bf4\u4e00\u53e5\uff0c\u6de1\u7136\u7684\u548c \u4e91\u857e\u70b9\u4e86\u70b9\u5934\uff0c\u4fbf\u81ea\u79bb\u5f00\u3002\u4e91\u857e\u5fc3\u9053\uff1a\u201c\u8fd9\u5c11\u5973\u524d\u6765\u8d60\u836f\uff0c\u4e3a\u4f55\u5374\u51b7\u5f97\u5982\u6b64\u6015\u4eba\uff0c\u83ab\u975e\u5979\u542c \u5230\u6211\u7684\u8bdd\u4e86\u3002\u201d\u5fc3\u4e2d\u6014\u5fe1\u4e0d\u5b89\u3002 \u3000\u3000\u4e91\u91cd\u542c\u5f97\u811a\u6b65\u6e10\u8fdc\u6e10\u5bc2\uff0c\u62ac\u5934\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u6fb9\u53f0\u59d1\u5a18\u771f\u662f\u96be\u5f97\uff01\u201d\u773c\u4e2d\u7adf\u7136\u5145\u6ee1\u67d4\u60c5\u3002 \u4e91\u857e\u5fc3\u4e2d\u4e00\u52a8\uff0c\u60f3\u8d77\u5979\u65e5\u95f4\u548c\u5f20\u4e39\u67ab\u4eb2\u70ed\u7684\u60c5\u72b6\uff0c\u770b\u4e86\u54e5\u54e5\u4e00\u773c\uff0c\u6b32\u8bf4\u53c8\u6b62\u3002\u4e91\u91cd\u89c1\u59b9\u59b9\u5634 \u5507\u5fae\u52a8\uff0c\u773c\u5149\u4e2d\u6d41\u9732\u51fa\u4e00\u79cd\u975e\u5e38\u5947\u5f02\u7684\u795e\u60c5\uff0c\u4f3c\u662f\u601c\u60af\uff0c\u4f3c\u662f\u60f6\u6050\uff0c\u53c8\u4f3c\u662f\u7126\u8651\u4e0d\u5b89\uff0c\u5fc3\u4e2d \u5927\u60d1\u4e0d\u89e3\u3002 \u3000\u3000\u6fb9\u53f0\u955c\u660e\u6ee1\u8154\u5fc3\u4e8b\uff0c\u7a7f\u8fc7\u56de\u5eca\uff0c\u7ed5\u8fc7\u5047\u5c71\uff0c\u524d\u5f80\u89c1\u5f20\u4e39\u67ab\u590d\u547d\u3002\u5f20\u4e39\u67ab\u6240\u4f4f\u7684\u7cbe\u820d\u5efa\u5728 \u8377\u5858\u4e4b\u4e2d\uff0c\u8fd9\u65f6\u65b0\u6708\u521d\u4e0a\uff0c\u7761\u83b2\u6447\u66f3\uff0c\u5728\u6708\u5149\u4e4b\u4e0b\uff0c\u66f4\u663e\u5f97\u5206\u5916\u6e05\u5e7d\u3002 \u3000\u3000\u6708\u8272\u6f84\u660e\uff0c\u8377\u5858\u6cdb\u5f71\uff0c\u53ea\u89c1\u5f20\u4e39\u67ab\u767d\u8863\u5982\u96ea\uff0c\u501a\u69db\u6c89\u541f\uff0c\u8fdc\u8fdc\u671b\u53bb\uff0c\u5c31\u5982\u4eba\u5728\u7530\u7530\u8377\u53f6 \u4e4b\u4e2d\uff0c\u6735\u6735\u83b2\u8336\uff0c\u7fe0\u76d6\u7ea2\u88f3\uff0c\u56f4\u62e5\u7740\u4e00\u4e2a\u767d\u8863\u4e66\u751f\u201c\u4ea6\u72c2\u4ea6\u4fa0\u80fd\u54ed\u80fd\u6b4c\u3002\u201d\u542c\u4ed6\u54ed\u5f97\u60b2\u82e6\uff0c \u5fc3\u4e5f\u9178\u4e86\u3002\u5ffd\u800c\u54ed\u58f0\u4e00\u6b62\uff0c\u5f20\u4e39\u67ab\u53c8\u7b11\u4e86\u8d77\u6765\uff0c\u53cd\u590d\u541f\u9053\uff1a\u201c\u8863\u5e26\u6e10\u5bbd\u7ec8\u4e0d\u6094\uff0c\u4e3a\u4f0a\u6d88\u5f97\u4eba \u6194\u60b4\u3002\u65e2\u7136\u7518\u5fc3\u6194\u60b4\uff0c\u59cb\u7ec8\u4e0d\u6094\uff0c\u90a3\u53c8\u6709\u4ec0\u4e48\u53ef\u4ee5\u4f24\u5fc3\uff1f\u5440\uff0c\u5c0f\u5144\u5f1f\uff0c\u5c0f\u5144\u5f1f\uff0c\u4f60\u5c31\u662f\u518d\u5c06 \u6211\u72e0\u72e0\u6298\u78e8\uff0c\u6211\u4e5f\u7edd\u4e0d\u4f1a\u5bf9\u4f60\u57cb\u6028\u7684\u3002\u201d \u3000\u3000\u6fb9\u53f0\u955c\u660e\u542c\u4ed6\u5148\u524d\u4e00\u54ed\uff0c\u5df2\u662f\u5fc3\u9178\uff0c\u800c\u4eca\u542c\u4ed6\u54ed\u540e\u4e4b\u7b11\uff0c\u66f4\u89c9\u96be\u53d7\u3002\u987f\u65f6\u95f4\u4e0d\u89c9\u75f4\u4e86\uff0c \u731b\u4e00\u62ac\u5934\uff0c\u53ea\u89c1\u6708\u79fb\u82b1\u5f71\uff0c\u6597\u8f6c\u661f\u6a2a\uff0c\u542c\u5c71\u95e8\u5916\u66f4\u9f13\u4e4b\u58f0\uff0c\u6572\u7684\u5df2\u662f\u4e09\u66f4\u4e86\u3002\u6fb9\u53f0\u955c\u660e\u731b\u7136 \u7701\u8d77\uff0c\u81ea\u5df1\u6b64\u6765\uff0c\u539f\u4e3a\u7684\u662f\u5411\u5f20\u4e39\u67ab\u590d\u547d\uff0c\u62a5\u544a\u533b\u6cbb\u4e91\u91cd\u7684\u7ecf\u8fc7\uff0c\u53ef\u4e0d\u77e5\u600e\u7684\uff0c\u5fc3\u4e2d\u4e00\u9178\uff0c \u7adf\u662f\u5bf8\u6b65\u96be\u79fb\uff0c\u867d\u7136\u53ea\u8981\u7ed5\u8fc7\u5047\u5c71\uff0c\u5c31\u53ef\u4e0e\u5f20\u4e39\u67ab\u5bf9\u9762\u76f8\u8bed\uff0c\u4f46\u5979\u5374\u600e\u6837\u4e5f\u4e0d\u80af\u4ece\u5047\u5c71\u540e\u9732 \u51fa\u9762\u6765\uff0c\u5fc3\u4e2d\u5c3d\u81ea\u75f4\u75f4\u60f3\u9053\uff1a\u201c\u539f\u6765\u4ed6\u5bf9\u4e91\u857e\u7adf\u662f\u5982\u6b64\u7231\u6df1\u60c5\u91cd\uff0c\u5440\uff0c\u8863\u5e26\u6e10\u5bbd\u7ec8\u4e0d\u6094\uff0c\u4e3a \u4f0a\u6d88\u5f97\u4eba\u6194\u60b4\uff01\u82e5\u7136\u6709\u4eba\u5bf9\u6211\u5982\u6b64\uff0c\u6211\u5c31\u662f\u6b7b\u4e86\uff0c\u4e5f\u81ea\u7518\u5fc3\uff01\u201d\u5ffd\u53c8\u60f3\u9053\uff1a\u201c\u53ef\u60dc\u4ed6\u4eec\u4e24\u5bb6 \u7ed3\u4e0b\u6df1\u4ec7\uff0c\u9002\u624d\u542c\u4ed6\u4eec\u5144\u59b9\u8c08\u8bdd\uff0c\u4e91\u91cd\u53c8\u662f\u5982\u6b64\u56fa\u6267\uff0c\u8fd9\u5374\u5982\u4f55\u662f\u597d\uff1f\u201d\u77ac\u606f\u4e4b\u95f4\uff0c\u601d\u6f6e\u767e \u53d8\uff0c\u542c\u5f20\u4e39\u67ab\u75db\u54ed\u72c2\u6b4c\uff0c\u81ea\u5df1\u53ef\u771f\u613f\u5929\u4e0b\u6709\u60c5\u4eba\u90fd\u6210\u7737\u5c5e\uff0c\u4f46\u8111\u6d77\u4e2d\u6cdb\u51fa\u5f20\u4e39\u67ab\u4e0e\u4e91\u857e\u7684\u53cc \u53cc\u4fea\u5f71\u4e4b\u65f6\uff0c\u81ea\u5df1\u5374\u53c8\u5ffd\u5730\u60d8\u7136\u82e5\u6709\u6240\u5931\u3002\u6b63\u662f\uff1a \u3000\u3000\u4f3c\u6b64\u60c5\u6000\u96be\u81ea\u89e3\uff0c\u767e\u822c\u5e7d\u6028\u4e0a\u5fc3\u5934\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u840d\u8e2a\u4fa0\u5f71\u5f55\u300b\u2014\u2014\u7b2c\u5341\u4e5d\u56de\u3000\u67f3\u8272\u9752\u9752\u79bb\u6101\u4ed8\u6e56\u6c34\u3000\u70fd\u70df\u5904\u5904\u5192\u9669\u5165\u4eac\u534e \u6881\u7fbd\u751f\u300a\u840d\u8e2a\u4fa0\u5f71\u5f55\u300b \u7b2c\u5341\u4e5d\u56de\u3000\u67f3\u8272\u9752\u9752\u79bb\u6101\u4ed8\u6e56\u6c34\u3000\u70fd\u70df\u5904\u5904\u5192\u9669\u5165\u4eac\u534e \u3000\u3000\u9732\u51b7\u98ce\u5bd2\uff0c\u82b1\u679d\u98a4\u52a8\uff0c\u6fb9\u53f0\u955c\u660e\u6084\u7136\u72ec\u7acb\uff0c\u72ec\u81ea\u51dd\u601d\uff0c\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u5c11\u65f6\u5019\uff0c\u5ffd\u5730\u62ac \u5934\uff0c\u5f20\u4e39\u67ab\u5df2\u4e0d\u89c1\u4e86\u3002\u6fb9\u53f0\u955c\u660e\u60f3\u9053\uff1a\u201c\u60f3\u662f\u4ed6\u7b49\u4e0d\u89c1\u6211\uff0c\u56de\u53bb\u7761\u4e86\u3002\u201d\u8d70\u51fa\u5047\u5c71\uff0c\u5ffd\u89c1\u4e00 \u6761\u4eba\u5f71\uff0c\u5206\u82b1\u62c2\u67f3\uff0c\u9732\u51fa\u9762\u6765\uff0c\u5374\u662f\u4e91\u857e\u3002 \u3000\u3000\u6fb9\u53f0\u955c\u660e\u8fce\u4e0a\u53bb\u9053\uff1a\u201c\u4e91\u59d0\u59d0\u8fd9\u4e48\u665a\u4e86\uff0c\u8fd8\u672a\u7761\u4e48\uff1f\u201d\u4e91\u857e\u9aa4\u7136\u89c1\u5979\uff0c\u6014\u4e86\u4e00\u6014\uff0c\u542b\u7cca \u8bf4\u9053\uff1a\u201c\u6211\u521a\u670d\u4f8d\u54e5\u54e5\u7761\u4e86\uff0c\u51fa\u6765\u8d70\u8d70\u3002\u201d\u6fb9\u53f0\u955c\u660e\u9053\uff1a\u201c\u4ee4\u5144\u4f24\u52bf\u5982\u4f55\uff1f\u201d\u4e91\u857e\u9053\uff1a\u201c\u591a \u8c22\u59d0\u59d0\uff0c\u4f60\u7684\u533b\u9053\u771f\u662f\u9ad8\u660e\uff0c\u4ed6\u81c2\u4e0a\u7684\u80bf\u6bd2\u5df2\u7ecf\u6d88\u4e86\u5341\u4e4b\u516b\u4e5d\uff0c\u770b\u6765\u660e\u5929\u4fbf\u53ef\u8d77\u5e8a\u4e86\u3002\u201d\u5fc3 \u4e2d\u751a\u662f\u4e0d\u89e3\uff0c\u60f3\u9053\uff1a\u201c\u8fd9\u5973\u5b50\u9002\u624d\u524d\u6765\u8d60\u836f\uff0c\u751a\u4e3a\u51b7\u6de1\uff0c\u5374\u4f55\u4ee5\u5982\u4eca\u7a81\u7136\u53c8\u5bf9\u6211\u4eb2\u70ed\u5982 \u65af\uff1f\u201d \u3000\u3000\u6fb9\u53f0\u955c\u660e\u5fae\u7b11\u4e00\u7b11\uff0c\u8f7b\u8f7b\u629a\u7740\u4e91\u857e\u80a9\u818a\uff0c\u5728\u5979\u8033\u8fb9\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u59d0\u59d0\u4f60\u4e0d\u5fc5\u591a\u8c22\u6211\uff0c\u4f60 \u8be5\u591a\u8c22\u4e39\u67ab\u3002\u201d\u4e91\u857e\u55d4\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u201d\u6fb9\u53f0\u955c\u660e\u9053\uff1a\u201c\u836f\u662f\u4ed6\u7684\uff0c\u662f\u4ed6\u6559\u6211\u7684\u3002\u201d\u4e91\u857e \u201c\u5475\u201d\u4e86\u4e00\u58f0\uff0c\u4e00\u65f6\u95f4\u8bf4\u4e0d\u51fa\u8bdd\u3002\u53ea\u542c\u5f97\u6fb9\u53f0\u955c\u660e\u53c8\u9053\uff1a\u201c\u4ed6\u6628\u65e5\u89c1\u4e91\u5927\u54e5\u903c\u4f60\u62ff\u51fa\u7f8a\u76ae\u8840 \u4e66\uff0c\u4e0d\u613f\u8ba9\u4f60\u4eec\u77e5\u9053\u662f\u4ed6\u8d60\u836f\uff0c\u6240\u4ee5\u5047\u624b\u4e8e\u6211\u3002\u201d\u4e91\u857e\u5fc3\u9053\uff1a\u201c\u539f\u6765\u4ed6\u4eec\u4e8c\u4eba\u6628\u65e5\u8c08\u7684\u4e43\u662f \u6b64\u4e8b\uff0c\u6211\u5012\u8bef\u4f1a\u4e86\u3002\u201d\u60f3\u8d77\u5f20\u4e39\u67ab\u4e00\u7247\u82e6\u5fc3\uff0c\u6697\u81ea\u611f\u52a8\u51b2\u53e3\u8bf4\u9053\uff1a\u201c\u554a\u5440\uff0c\u4ed6\u53c8\u4f55\u5fc5\u5982 \u6b64\uff1f\u201d \u3000\u3000\u6fb9\u53f0\u955c\u660e\u53c8\u662f\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u82e5\u7136\u6211\u771f\u6b63\u6b22\u559c\u4e0a\u4e00\u4e2a\u4eba\u65f6\uff0c\u6211\u4e5f\u4f1a\u5982\u6b64\u3002\u53ea\u8981\u5bf9\u65b9\u5e78 \u798f\uff0c\u81ea\u5df1\u53d7\u4e9b\u59d4\u5c48\u4e5f\u7b97\u4e0d\u4e86\u4ec0\u4e48\u7684\u3002\u201d\u4e91\u857e\u53c8\u662f\u4e00\u6014\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u5973\u5b50\u4e0e\u6211\u521a\u521a\u76f8\u8bc6\uff0c\u4f55\u4ee5 \u4fbf\u5f00\u73a9\u7b11\uff1f\u201d\u4f46\u542c\u5979\u8bf4\u8bdd\uff0c\u5374\u4f3c\u751a\u662f\u8ba4\u771f\uff0c\u773c\u5149\u76f8\u63a5\uff0c\u5ffd\u89c9\u5979\u7684\u5fae\u7b11\u4e4b\u4e2d\uff0c\u7adf\u4f3c\u5e26\u6709\u4e00\u79cd\u51c4 \u51c9\u5473\uff0c\u5fc3\u4e2d\u53c8\u662f\u4e00\u52a8\u3002 \u3000\u3000\u6fb9\u53f0\u955c\u660e\u751a\u662f\u806a\u660e\uff0c\u4e00\u89c1\u4e91\u857e\u795e\u8272\u4fbf\u77e5\u5979\u5fc3\u4e2d\u7591\u8651\u672a\u6d88\uff0c\u6697\u4e2d\u54ac\u4e00\u54ac\u7259\uff0c\u5f3a\u81ea\u6291\u7740\u5fc3\u5934 \u7684\u6ce2\u52a8\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u54e5\u54e5\u4e5f\u662f\u4e00\u6761\u597d\u6c49\u5b50\uff0c\u53ea\u53ef\u60dc\u592a\u5014\u5f3a\u4e86\u3002\u201d\u4e91\u857e\u542c\u5979\u79f0\u8d5e\u81ea\u5df1\u7684\u54e5\u54e5\uff0c \u9887\u611f\u610f\u5916\uff0c\u7b11\u4e86\u7b11\u3002\u6fb9\u53f0\u955c\u660e\u5ffd\u9053\uff1a\u201c\u4f60\u53ea\u6709\u8fd9\u4e00\u4e2a\u54e5\u54e5\u5417\uff1f\u201d\u4e91\u857e\u9053\uff1a\u201c\u662f\u5440\uff0c\u6211\u5c31\u53ea\u6709 \u8fd9\u4e00\u4e2a\u54e5\u54e5\u3002\u201d\u6fb9\u53f0\u955c\u660e\u9053\uff1a\u201c\u5bb6\u4e2d\u5c31\u6ca1\u6709\u5176\u4ed6\u4eba\u4e86\u5417\uff1f\u201d\u4e91\u857e\u9053\uff1a\u201c\u8fd8\u6709\u5988\u5988\uff0c\u73b0\u5728\u8499 \u53e4\uff0c\u53ea\u662f\u4e0b\u843d\u4e0d\u660e\uff0c\u5c06\u8fd8\u6211\u8fd8\u8981\u627e\u5979\u3002\u201d\u6fb9\u53f0\u955c\u660e\u9053\uff1a\u201c\u9664\u4e86\u5988\u5988\uff0c\u5c31\u518d\u6ca1\u6709\u5176\u4ed6\u4eba\u4e86 \u5417\uff1f\u201d\u4e91\u857e\u9053\uff1a\u201c\u6ca1\u6709\u5566\uff0c\u6211\u54e5\u54e5\u5c1a\u672a\u6210\u4eb2\u5462\u3002\u201d\u6fb9\u53f0\u955c\u660e\u9053\uff1a\u201c\u554a\uff0c\u4f60\u8fd8\u6ca1\u5ac2\u5b50\uff1f\u201d\u4e91\u857e \u89c1\u5979\u95ee\u8bdd\uff0c\u4f3c\u6709\u610f\u65e0\u610f\u5730\u5f15\u81ea\u5df1\u8bf4\u51fa\u6765\uff0c\u5fc3\u4e2d\u4e00\u559c\uff0c\u60f3\u8d77\u81ea\u5df1\u54e5\u54e5\u5bf9\u5979\u5b9e\u662f\u751a\u662f\u610f\u601d\uff0c\u81ea\u5df1 \u4ee5\u4e3a\u5979\u6b22\u559c\u7684\u4e43\u662f\u5f20\u4e39\u67ab\uff0c\u8c01\u77e5\u5979\u5bf9\u54e5\u54e5\u4ea6\u4f3c\u6709\u610f\uff0c\u51e0\u4e4e\u60f3\u51b2\u53e3\u8bf4\u9053\uff1a\u201c\u82e5\u7136\u4f60\u80af\u505a\u6211\u7684\u5ac2 \u5b50\uff0c\u90a3\u662f\u6700\u597d\u4e0d\u8fc7\uff01\u201d\u53ea\u662f\u4e91\u857e\u6bd4\u8f83\u77dc\u6301\uff0c\u5bf9\u521d\u76f8\u8bc6\u4e4b\u4eba\uff0c\u4e0d\u80af\u591a\u5f00\u73a9\u7b11\u3002\u53ea\u662f\u559c\u4e0a\u7709\u68a2\uff0c \u5bf9\u6fb9\u53f0\u955c\u660e\u542b\u7b11\u70b9\u5934\uff0c\u9053\uff1a\u201c\u662f\u5440\uff0c\u6211\u8fd8\u6ca1\u6709\u5ac2\u5b50\u3002\u201d \u3000\u3000\u4e91\u857e\u54ea\u91cc\u77e5\u9053\uff0c\u6fb9\u53f0\u955c\u660e\u4e43\u662f\u5fcd\u7740\u5fc3\u4e2d\u9178\u82e6\uff0c\u6709\u610f\u89e3\u5f00\u4e91\u857e\u5bf9\u5979\u7684\u7591\u8651\u3002 \u3000\u3000\u6708\u5149\u5982\u6c34\uff0c\u4ece\u6811\u53f6\u7f1d\u95f4\u904d\u6d12\u4e0b\u6765\uff0c\u4e24\u4e2a\u5c11\u5973\u7684\u624b\u7d27\u7d27\u7275\u5728\u4e00\u8d77\uff0c\u4e24\u4e2a\u5c11\u5973\u7684\u5fc3\u4e5f\u5728\u5404\u81ea \u8dc3\u52a8\u3002\u9694\u7740\u8377\u5858\u671b\u53bb\uff0c\u78a7\u7eb1\u7a97\u4e0a\u73b0\u51fa\u4eba\u5f71\uff0c\u6fb9\u53f0\u955c\u660e\u7b11\u9053\uff1a\u201c\u5f20\u4e39\u67ab\u8fd8\u6ca1\u6709\u7761\uff0c\u4ed6\u6b63\u5728\u7b49\u7740 \u4f60\u5462\uff01\u201d\u4e91\u857e\u201c\u5478\u201d\u4e86\u4e00\u58f0\uff0c\u9762\u4e0a\u767b\u65f6\u53d1\u70ed\uff0c\u5979\u51fa\u6765\u6563\u6b65\u4e4b\u65f6\uff0c\u5fc3\u91cc\u662f\u6101\u80a0\u767e\u7ed3\uff0c\u60f3\u907f\u5f00\u5f20 \u4e39\u67ab\uff0c\u5374\u53c8\u60f3\u89c1\u5f20\u4e39\u67ab\u4e00\u9762\uff0c\u6240\u4ee5\u4e0d\u77e5\u4e0d\u89c9\u5730\u5411\u5f20\u4e39\u67ab\u4f4f\u5904\u884c\u6765\uff0c\u5fc3\u4e2d\u79d8\u5bc6\uff0c\u4e00\u4e0b\u7ed9\u6fb9\u53f0\u955c \u660e\u8bf4\u7834\uff0c\u4e0d\u89c9\u7f9e\u5f97\u6ee1\u8138\u901a\u7ea2\u3002\u6fb9\u53f0\u955c\u660e\u683c\u683c\u4e00\u7b11\uff0c\u6454\u8131\u4e86\u4e91\u857e\u7684\u624b\u7ed5\u8fc7\u5047\u5c71\uff0c\u9690\u8eab\u82b1\u6811\u4e1b \u4e2d\uff0c\u56de\u5934\u4e00\u671b\uff0c\u53ea\u89c1\u5f20\u4e39\u67ab\u5df2\u628a\u7a97\u5b50\u6253\u5f00\uff0c\u63a2\u51fa\u5934\u6765\uff0c\u4f4e\u58f0\u5728\u5524\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u5c0f\u5144\u5f1f\uff01\u201d \u4e91\u857e\u5e76\u4e0d\u5e94\u58f0\uff0c\u4f3c\u662f\u4e00\u7247\u832b\u7136\uff0c\u4f46\u5374\u4f4e\u7740\u5934\u7f13\u7f13\u5730\u5411\u8377\u5858\u884c\u53bb\u3002\u6fb9\u53f0\u955c\u660e\u60b2\u559c\u4ea4\u96c6\uff0c\u5fc3\u4e2d\u5ffd \u5730\u4e00\u9178\uff0c\u6cea\u73e0\u800c\u5fcd\u4e0d\u4f4f\u6ef4\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u518d\u8bf4\u4e91\u91cd\u4e00\u591c\u597d\u7761\uff0c\u9192\u6765\u4e4b\u540e\uff0c\u5df2\u662f\u65e5\u4e0a\u4e09\u7aff\u3002\u4e91\u91cd\u8bd5\u4e00\u6325\u52a8\u624b\u81c2\uff0c\u5df2\u662f\u6062\u590d\u539f\u72b6\uff0c\u53ea \u662f\u8eab\u4f53\u8fd8\u89c9\u865a\u8f6f\u3002\u4e91\u91cd\u559d\u4e86\u53e3\u6c34\u6362\u4e86\u8863\u670d\u3002\u8d70\u51fa\u9759\u5ba4\u3002\u8fd9\u6d1e\u5ead\u5c71\u5e84\u5e03\u7f6e\u5f97\u5341\u5206\u7cbe\u96c5\uff0c\u5047\u5c71\u6d1e \u58d1\u8377\u5858\u4ead\u69ad\uff0c\u70b9\u7f00\u5176\u95f4\uff0c\u771f\u662f\u7684\u5de7\u593a\u5929\u4e0b\uff0c\u8d5b\u4f3c\u56fe\u753b\uff0c\u56ed\u4e2d\u957f\u5ed3\u56db\u9762\u8d2f\u901a\uff0c\u9ad8\u4e0b\u66f2\u6298\uff0c\u82e5\u9690 \u82e5\u73b0\u3002\u4e91\u91cd\u4fe1\u6b65\u8d70\u53bb\uff0c\u8d70\u5230\u4e00\u5904\u5047\u5c71\u524d\u9762\uff0c\u5ffd\u542c\u5f97\u5047\u5c71\u4e4b\u540e\uff0c\u6709\u4eba\u5728\u5927\u58f0\u4e89\u8bba\u3002 \u3000\u3000\u4e00\u4e2a\u4eba\u9053\uff1a\u201c\u8fd9\u5b9d\u85cf\u54b1\u4eec\u66ff\u8001\u4e3b\u516c\u5b88\u4e86\u51e0\u4ee3\uff0c\u800c\u4eca\u5374\u8981\u9001\u4e0e\u4ed6\u7684\u5bf9\u5934\uff0c\u9001\u7ed9\u6731\u5bb6\u7687\u5e1d\uff0c \u8001\u4e3b\u516c\u5730\u4e0b\u6709\u7075\uff0c\u4e5f\u4e0d\u7791\u76ee\uff01\u201d\u4e00\u4e2a\u82cd\u8001\u7684\u58f0\u97f3\u9053\uff1a\u201c\u8fd9\u5374\u4e0d\u7136\uff0c\u5c11\u4e3b\u8bf4\u5f97\u597d\uff0c\u6614\u65e5\u662f\u4e24\u5bb6 \u4e89\u593a\u5929\u4e0b\uff0c\u800c\u4eca\u5374\u662f\u5f02\u65cf\u5165\u4fb5\uff0c\u6743\u8861\u8f7b\u91cd\uff0c\u8fd8\u662f\u540c\u5fc3\u5408\u529b\uff0c\u62b5\u5fa1\u5916\u654c\u4e3a\u9ad8\u3002\u201d\u53c8\u4e00\u4eba\u9053\uff1a \u201c\u6211\u5c31\u4e0d\u76f8\u4fe1\u6731\u5bb6\u5929\u5b50\u80af\u771f\u5fc3\u62b5\u5fa1\u5916\u654c\u3002\u201d\u5148\u524d\u90a3\u4e2a\u82cd\u8001\u7684\u58f0\u97f3\u9053\uff1a\u201c\u5927\u52bf\u6240\u8d8b\uff0c\u4ed6\u4e0d\u62b5\u5fa1 \u4e5f\u4e0d\u6210\u7684\u3002\u4f55\u51b5\u8fd8\u6709\u4e8e\u8c26\u7b49\u5fe0\u5fc3\u4e3a\u56fd\u7684\u5927\u81e3\uff0c\u6211\u610f\u5df2\u51b3\uff0c\u51b3\u9075\u4ece\u5c11\u4e3b\u7684\u5429\u5490\uff0c\u4f60\u7b49\u4f11\u5f97\u591a \u8a00\uff01\u201d\u4e91\u91cd\u5206\u8fa8\u51fa\u6765\uff0c\u8bf4\u8fd9\u8bdd\u7684\u6b63\u662f\u6d1e\u5ead\u5e84\u4e3b\u3002\u4e89\u8bba\u4e00\u756a\uff0c\u5352\u4e4b\u662f\u90fd\u540c\u610f\u4e86\u6d1e\u5ead\u5e84\u4e3b\u7684\u4e3b \u5f20\u3002 \u3000\u3000\u4e91\u91cd\u5fc3\u5934\u4e00\u9707\uff0c\u60f3\u9053\uff1a\u201c\u7687\u4e0a\u8fd8\u4ee5\u4e3a\u5f20\u4e39\u67ab\u53bb\u53d6\u5b9d\u85cf\u5730\u56fe\u662f\u60f3\u5b58\u5fc3\u9020\u53cd\uff0c\u5374\u539f\u6765\u4ed6\u771f\u7684 \u662f\u60f3\u732e\u7687\u4e0a\uff01\u201d\u5fc3\u60c5\u6fc0\u52a8\uff0c\u70ed\u8840\u6cb8\u817e\uff0c\u5ffd\u542c\u5f97\u6709\u4eba\u7b11\u9053\uff1a\u201c\u54c8\uff0c\u72b6\u5143\u5927\u4eba\uff0c\u4f60\u4e5f\u6765\u4e86\u5417\uff1f\u201d \u3000\u3000\u4e91\u91cd\u62ac\u5934\u4e00\u770b\uff0c\u957f\u5ed3\u4e0a\u8d70\u8fc7\u6765\u4e24\u4e2a\u4eba\uff0c\u6b63\u662f\u90a3\u65e5\u8336\u4ead\u6240\u89c1\u7684\u4e24\u6bcd\u5973\uff0c\u4e91\u91cd\u5df2\u77e5\u5979\u4eec\u7684\u8eab \u4efd\uff0c\u53eb\u4e86\u4e00\u58f0\u201c\u4f2f\u6bcd\u201d\u3002\u6fb9\u53f0\u5927\u5a18\u9053\uff1a\u201c\u600e\u4e48\uff0c\u4f24\u597d\u4e86\u5417\uff0c\u7b97\u4f60\u9020\u5316\uff01\u201d\u90a3\u5c0f\u59d1\u5a18\u6fb9\u53f0\u7389\u660e \u6dd8\u6c14\u4e4b\u6781\uff0c\u563b\u563b\u7b11\u9053\uff1a\u201c\u6211\u542c\u59d0\u59d0\u8bf4\uff0c\u4ed6\u6628\u665a\u8fd8\u633a\u5145\u597d\u6c49\u54e9\u3002\u201d\u4e91\u91cd\u9762\u4e0a\u4e00\u7ea2\uff0c\u6fb9\u53f0\u7389\u660e\u5ffd \u7136\u4e00\u58f0\u51b7\u7b11\uff0c\u638f\u51fa\u4e00\u9762\u9526\u7f0e\uff0c\u7389\u624b\u4e00\u626c\uff0c\u90a3\u9526\u7f0e\u4e0a\u7ee3\u7740\u5341\u6735\u5927\u7ea2\u82b1\uff0c\u8fce\u98ce\u62db\u5c55\uff0c\u5341\u5206\u523a\u76ee\u3002 \u3000\u3000\u4e91\u91cd\u5fc3\u4e2d\u4e00\u6014\uff0c\u6fb9\u53f0\u5927\u5a18\u7b11\u9053\uff1a\u201c\u660e\u513f\u4e0d\u51c6\u5413\u552c\u5ba2\u4eba\u3002\u201d\u6fb9\u53f0\u7389\u660e\u683c\u683c\u7b11\uff0c\u624b\u6307\u5728\u9526\u7f0e \u4e0a\u4e00\u753b\uff0c\u5c06\u90a3\u4e03\u6735\u56f4\u6709\u7ea2\u7ebf\u7684\u7ea2\u82b1\u5708\u4e86\u4e00\u5708\uff0c\u9053\uff1a\u201c\u8fd9\u4e03\u4e2a\u60f3\u52a0\u5bb3\u4e39\u67ab\u5927\u54e5\u7684\u574f\u86cb\u90fd\u7ed9\u6211\u4eec \u62c6\u4e0b\u6765\u5566\uff0c\u563f\u563f\uff0c\u8fd9\u4e09\u6735\u7ea2\u82b1\u51e1\u67ab\u5927\u54e5\u90fd\u4e0d\u51c6\u6211\u4eec\u78b0\u5b83\u4e00\u78b0\u3002\u201d\u4e91\u91cd\u77e5\u9053\u8fd9\u4e09\u6735\u7ea2\u82b1\u4e43\u662f\u4ee3 \u8868\u81ea\u5df1\u4e0e\u94c1\u81c2\u91d1\u733f\u3001\u4e09\u82b1\u5251\u4e8c\u4eba\uff0c\u5fc3\u4e2d\u5fae\u6120\u3002\u6fb9\u53f0\u5927\u5a18\u53c8\u7b11\u9053\uff1a\u201c\u5728\u8336\u4ead\u5185\u6211\u5df2\u770b\u51fa\u4e91\u76f8\u516c \u4e43\u662f\u597d\u4eba\uff0c\u660e\u513f\uff0c\u4e0d\u51c6\u518d\u80e1\u95f9\u5566\u3002\u201d \u3000\u3000\u539f\u6765\u6fb9\u53f0\u4e00\u5bb6\u56e0\u8d1f\u5b88\u5b9d\u7684\u91cd\u8d23\uff0c\u6240\u4ee5\u7531\u6d1e\u5ead\u5e84\u4e3b\u6fb9\u53f0\u4ef2\u5143\u5750\u9547\u897f\u6d1e\u5ead\u5c71\uff0c\u6fb9\u53f0\u5927\u5a18\u5219\u4e0e \u5c0f\u5973\u513f\u5728\u5916\u9762\u8bbe\u8336\u4ead\u4f5c\u4e3a\u8033\u76ee\u3002\u672a\u81f3\u6d1e\u5ead\u5c71\u5e84\u4e4b\u524d\uff0c\u8fde\u5f20\u4e39\u67ab\u4e5f\u4e0d\u77e5\u9053\u5979\u662f\u6d1e\u5ead\u5e84\u4e3b\u7684\u59bb \u5b50\u3002 \u3000\u3000\u6fb9\u53f0\u5927\u5a18\u9053\uff1a\u201c\u4e91\u76f8\u516c\uff0c\u6211\u4e0e\u4f60\u53bb\u770b\u4e00\u5b97\u7269\u4e8b\u3002\u201d\u4e91\u91cd\u968f\u5979\u8d70\u51fa\u957f\u5ed3\uff0c\u7ed5\u8fc7\u5047\u5c71\uff0c\u773c\u775b \u500f\u5730\u4e00\u4eae\uff0c\u53ea\u89c1\u8349\u5730\u4e0a\u5806\u6ee1\u91d1\u94f6\u73e0\u5b9d\uff0c\u6d1e\u5ead\u5e84\u4e3b\u4e0e\u90a3\u51e0\u4e2a\u519c\u592b\u6253\u626e\u7684\u4eba\u90fd\u5728\u65c1\u8fb9\u3002 \u3000\u3000\u6d1e\u5ead\u5e84\u4e3b\u9053\uff1a\u201c\u563f\uff0c\u4e91\u5927\u4eba\u4f60\u6765\u5f97\u6b63\u597d\uff01\u201d\u5429\u5490\u5e84\u4e01\u9053\uff1a\u201c\u8bf7\u5f20\u76f8\u516c\u6765\u3002\u201d\u6d1e\u5ead\u5e84\u4e3b\u672c \u6765\u662f\u5c0a\u79f0\u5f20\u4e39\u67ab\u4e3a\u201c\u5c11\u4e3b\u201d\uff0c\u5f20\u4e39\u67ab\u6267\u610f\u4e0d\u5141\uff0c\u6545\u6b64\u6539\u4ee5\u76f8\u516c\u79f0\u547c\u3002 \u3000\u3000\u4e0d\u4e00\u523b\uff0c\u53ea\u89c1\u5f20\u4e39\u67ab\u4e0e\u4e91\u857e\u4e8c\u4eba\u5728\u82b1\u5f84\u4e4b\u4e2d\u8d70\u51fa\uff0c\u4e91\u857e\u4e00\u89c1\u54e5\u54e5\uff0c\u7acb\u523b\u653e\u6162\u811a\u6b65\uff0c\u843d\u5728 \u5f20\u4e39\u67ab\u540e\u9762\u3002\u4e91\u91cd\u6697\u6697\u53f9\u4e86\u53e3\u6c14\u9762\u8272\u9887\u662f\u96be\u770b\uff0c\u4f46\u5df2\u4e0d\u4f3c\u6628\u65e5\u90a3\u822c\u607c\u6012\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u4e91\u5144\u4f24\u52bf\u5982\u4f55\uff1f\u201d\u4e91\u91cd\u672c\u6b32\u4e0d\u8bed\uff0c\u4f46\u4ecd\u662f\u51b7\u51b7\u5730\u70b9\u4e86\u70b9\u5934\uff0c\u9053\uff1a\u201c\u4e0d\u52b3\u6302 \u5fc3\uff0c\u6211\u8fd8\u6d3b\u7740\uff01\u201d\u5f20\u4e39\u67ab\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u90a3\u5c31\u597d\u5566\uff01\u201d\u5176\u5b9e\u4ed6\u65e9\u5df2\u77e5\u9053\u4e91\u91cd\u5b9a\u7136\u836f\u5230\u75c5 \u9664\uff0c\u8fd9\u8bdd\u5b9e\u662f\u660e\u77e5\u6545\u95ee\u3002 \u3000\u3000\u6d1e\u5ead\u5e84\u4e3b\u9053\uff1a\u201c\u8fd9\u4e9b\u73e0\u5b9d\u6211\u4eec\u5df2\u5b88\u4e86\u51e0\u4ee3\uff0c\u73b0\u5728\u53ef\u4ee5\u5378\u4e0b\u8fd9\u5343\u65a4\u91cd\u62c5\u4e86\u3002\u4e91\u5927\u4eba\uff0c\u4f60\u518d \u9759\u517b\u4e24\u5929\uff0c\u5c31\u52b3\u70e6\u4f60\u5c06\u8fd9\u4e9b\u73e0\u5b9d\u62bc\u8fd0\u56de\u4eac\uff0c\u7ed9\u4f60\u4eec\u7684\u7687\u5e1d\u505a\u519b\u8d39\u3002\u201d \u3000\u3000\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u6628\u65e5\u7ea2\u53d1\u5996\u9f99\u4e4b\u8a00\u5012\u5e76\u975e\u662f\u5047\uff0c\u5982\u4eca\u63a2\u5f97\u786e\u5b9e\u6d88\u606f\uff0c\u74e6\u523a\u5175\u679c\u7136\u6253\u8fdb\u4e86\u96c1 \u95e8\u5173\uff0c\u4e24\u56fd\u5df2\u7ecf\u5f00\u6218\u5566\uff01\u201d \u3000\u3000\u4e91\u91cd\u52c3\u7136\u5927\u6012\uff0c\u556a\u7684\u4e00\u638c\uff0c\u51fb\u5728\u5047\u5c71\u77f3\u4e0a\uff0c\u9053\uff1a\u201c\u6211\u4e0d\u626b\u5e73\u74e6\u523a\uff0c\u8a93\u4e0d\u4e3a\u4eba\u3002\u597d\uff0c\u6211\u7acb \u523b\u5c31\u5c06\u8fd9\u6279\u73e0\u5b9d\u62bc\u8fd0\u56de\u53bb\uff01\u201d\u8eab\u8eaf\u6447\u6643\uff0c\u5ffd\u7136\u4e00\u53e3\u9c9c\u8840\u5410\u4e86\u51fa\u6765\u3002\u4e91\u857e\u5927\u60ca\uff0c\u6025\u5fd9\u4e0a\u524d\u5c06\u4ed6 \u6276\u7740\uff0c\u5f20\u4e39\u67ab\u7ed9\u4ed6\u628a\u4e86\u628a\u8109\uff0c\u9053\uff1a\u201c\u4e0d\u5fc5\u60ca\u614c\uff0c\u8fd9\u662f\u4e00\u65f6\u52a8\u6012\u6240\u81f4\u3002\u4e91\u5144\uff0c\u4f60\u4e8c\u65e5\u4e4b\u540e\uff0c\u53ef \u4ee5\u5b8c\u5168\u5eb7\u590d\uff0c\u867d\u8bf4\u519b\u60c5\u7d27\u6025\uff0c\u4f46\u4e5f\u4e0d\u8fdf\u5728\u8fd9\u4e09\u5929\u3002\u8fd9\u6279\u73e0\u5b9d\uff0c\u5173\u7cfb\u91cd\u5927\uff0c\u5230\u65f6\u8bf7\u5e84\u4e3b\u6d3e\u4eba\u76f8 \u52a9\uff0c\u4e07\u4e0d\u80fd\u5728\u8def\u4e0a\u8ba9\u4eba\u52ab\u4e86\u3002\u201d \u3000\u3000\u6d1e\u5ead\u5e84\u4e3b\u9053\uff1a\u201c\u4f60\u5462\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u6211\u8fd8\u6709\u4e00\u6837\u6bd4\u8fd9\u6279\u73e0\u5b9d\u66f4\u8d35\u91cd\u7684\u4e1c\u897f\u2026\u2026\u201d\u6d1e\u5ead \u5e84\u4e3b\u63d2\u53e3\u9053\uff1a\u201c\u55ef\uff0c\u662f\u90a3\u5f20\u5730\u56fe\u5417\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u6b63\u662f\uff0c\u73b0\u5728\u654c\u5f3a\u6211\u5f31\uff0c\u6709\u8fd9\u5f20\u5730\u56fe\uff0c\u6211 \u4eec\u5728\u660e\uff0c\u654c\u4eba\u5728\u6697\uff0c\u8fd9\u5c31\u80dc\u4e8e\u591a\u52a0\u5341\u4e07\u96c4\u5175\uff01\u201d\u6d1e\u5ead\u5e84\u4e3b\u5ffd\u7136\u6447\u4e86\u6447\u5934\uff0c\u8138\u4e0a\u73b0\u51fa\u5fe7\u8651\u795e \u8272\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u6d1e\u5ead\u5e84\u4e3b\u9053\uff1a\u201c\u5f20\u76f8\u516c\uff0c\u4f60\u867d\u7136\u662f\u667a\u52c7\u53cc\u5168\uff0c\u4f46\u5b64\u8eab\u4e00\u4eba\uff0c\u6211\u5374 \u5b9e\u662f\u653e\u5fc3\u4e0d\u4e0b\u3002\u8fd9\u5f20\u5730\u56fe\uff0c\u6709\u5173\u4e2d\u534e\u56fd\u8fd0\uff0c\u5978\u81e3\u738b\u632f\uff0c\u53c8\u5df2\u77e5\u9053\u98ce\u58f0\uff0c\u524d\u65e5\u6240\u6d3e\u6765\u7684\u7ea2\u53d1\u5996 \u9f99\u7b49\u4eba\uff0c\u867d\u5df2\u5168\u519b\u8986\u6ca1\uff0c\u4f46\u96be\u4fdd\u4e0d\u4f1a\u518d\u6d3e\u4eba\u6765\u3002\u5343\u91cc\u8fe2\u8fe2\uff0c\u4f60\u5b64\u8eab\u4e00\u4eba\uff0c\u8def\u9014\u4e2d\u82e5\u7136\u51fa\u4e86\u4e8b \u60c5\uff0c\u6211\u4eec\u4e5f\u4e0d\u77e5\u9053\u3002\u201d\u5f20\u4e39\u67ab\u9ed8\u7136\u4e0d\u8bed\u3002\u6d1e\u5ead\u5e84\u4e3b\u53c8\u9053\uff1a\u201c\u6211\u672c\u5e94\u6d3e\u4eba\u4e0e\u4f60\u540c\u5f80\uff0c\u4f46\u8fd9\u91cc\u7684 \u4eba\uff0c\u6b66\u529f\u90fd\u5728\u76f8\u516c\u4e4b\u4e0b\uff0c\u82e5\u771f\u662f\u9047\u4e0a\u5f3a\u654c\uff0c\u53ea\u6015\u4e5f\u5e2e\u4e0d\u4e86\u516c\u5b50\u7684\u5fd9\u554a\u3002\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u6211\u6b64 \u53bb\u867d\u7136\u6709\u4e9b\u5192\u9669\uff0c\u4f46\u4e00\u5f20\u5730\u56fe\u8fd8\u4e0d\u663e\u773c\u3002\u4f60\u4eec\u62bc\u8fd0\u73e0\u5b9d\u5374\u5fc5\u987b\u591a\u4eba\uff0c\u5343\u4e07\u4e0d\u53ef\u4e3a\u6211\u800c\u5206\u8584\u4eba \u529b\u3002\u201d \u3000\u3000\u4e91\u91cd\u542c\u4ed6\u4eec\u4e89\u8bba\u4e0d\u4f11\uff0c\u5fc3\u4f3c\u8f98\u8f73\u4e71\u8f6c\uff0c\u5ffd\u5730\u62ac\u5934\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u857e\u59b9\uff0c\u4f60\u548c\u4ed6\u540c\u53bb\u3002\u201d \u6b64\u8a00\u4e00\u51fa\uff0c\u4f17\u7686\u6115\u7136\uff0c\u4e91\u857e\u53c8\u559c\u53c8\u60ca\uff0c\u82b3\u5fc3\u535c\u535c\u5730\u8df3\u3002\u4e91\u91cd\u9053\uff1a\u201c\u6211\u77e5\u4f60\u4eec\u53cc\u5251\u5408\u74a7\uff0c\u591a\u5f3a \u7684\u654c\u4eba\u4e5f\u53ef\u5e94\u4ed8\uff0c\u4f60\u53bb\u6211\u53ef\u653e\u5fc3\u3002\u201d\u5f20\u4e39\u67ab\u4e00\u63d6\u5230\u5730\uff0c\u9053\uff1a\u201c\u591a\u8c22\u4e91\u5144\uff01\u201d\u4e91\u91cd\u201c\u54fc\u201d\u4e86\u4e00 \u58f0\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u4ec0\u4e48\uff1f\u6211\u53ef\u4e0d\u662f\u4e3a\u4f60\u7740\u60f3\uff01\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u662f\u4e3a\u4e86\u8fd9\u5f20\u5730 \u56fe\uff0c\u90a3\u4e48\u6211\u5c31\u4e3a\u5927\u660e\u7684\u6c5f\u5c71\u5411\u4f60\u81f4\u656c\u5982\u4f55\uff1f\u201d\u4e91\u91cd\u9053\uff1a\u201c\u597d\uff0c\u4f60\u80af\u4e3a\u5927\u660e\u6c5f\u5c71\uff0c\u90a3\u4e48\u6211\u5411\u4f60 \u8fd8\u793c\u4e86\u3002\u201d\u5f53\u4e0b\u6270\u8896\u4e00\u63d6\uff0c\u4e91\u857e\u4e0d\u89c9\u9732\u51fa\u7b11\u5bb9\u3002 \u3000\u3000\u4e91\u91cd\u9053\uff1a\u201c\u857e\u59b9\uff0c\u4f60\u8fc7\u6765\uff01\u201d\u5144\u59b9\u643a\u624b\uff0c\u8d70\u5230\u82b1\u9634\u6df1\u5904\uff0c\u4e91\u91cd\u8f7b\u629a\u4e91\u857e\u79c0\u53d1\uff0c\u773c\u4e2d\u5145\u6ee1 \u601c\u60dc\u4e4b\u60c5\uff0c\u67d4\u58f0\u8bf4\u9053\uff1a\u201c\u59b9\u59b9\uff0c\u4f60\u602a\u6211\u4e48\uff1f\u201d\u4e91\u857e\u9053\uff1a\u201c\u54e5\u54e5\uff0c\u6211\u6b22\u559c\u6781\u4e86\uff01\u201d\u4e91\u91cd\u9053\uff1a \u201c\u81ea\u6211\u4eec\u5206\u6563\u4e4b\u540e\uff0c\u6211\u65e0\u65f6\u65e0\u523b\u4e0d\u5728\u60f3\u4f60\u3001\u5ff5\u4f60\uff0c\u6709\u65f6\u505a\u68a6\u4e5f\u68a6\u89c1\u4f60\uff0c\u68a6\u89c1\u4f60\u8fd8\u662f\u4e09\u5c81\u5927\u7684 \u6837\u5b50\uff0c\u5934\u4e0a\u68b3\u83f1\u53c9\u89d2\uff0c\u5728\u8349\u539f\u4e0a\u770b\u5988\u5988\u7267\u7f8a\u3002\u201d\u4e91\u857e\u60b2\u559c\u4ea4\u96c6\uff0c\u542b\u6cea\u8bf4\u9053\uff1a\u201c\u54e5\u54e5\uff0c\u6211\u77e5\u9053 \u4f60\u601c\u6211\u75bc\u6211\uff01\u201d\u4e91\u91cd\u5ffd\u5730\u53f9\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u540e\u6765\uff0c\u54b1\u4eec\u7b2c\u4e00\u6b21\u5728\u9752\u9f99\u5ce1\u89c1\u9762\uff0c\u90a3\u65f6\u4f60\u53c8\u626e\u7537 \u88c5\uff0c\u5e2e\u4ec7\u4eba\u4e0e\u6211\u4eec\u76f8\u6597\uff0c\u6211\u5c31\u60f3\uff0c\u8fd9\u4eba\u4e0d\u77e5\u662f\u54ea\u91cc\u89c1\u8fc7\u7684\uff0c\u5440\uff0c\u597d\u50cf\u662f\u6211\u81f3\u4eb2\u81f3\u8fd1\u7684\u4eba\uff0c\u6240 \u4ee5\u90a3\u65f6\u6211\u600e\u6837\u4e5f\u4e0b\u4e0d\u4e86\u6740\u624b\u3002\u201d\u4e91\u857e\u9053\uff1a\u201c\u5440\uff0c\u54b1\u4eec\u5144\u59b9\u7adf\u662f\u5fc3\u610f\u76f8\u901a\uff0c\u90a3\u65f6\uff0c\u6211\u4e5f\u662f\u8fd9 \u6837\u3002\u201d\u4e91\u91cd\u5ffd\u5730\u9053\uff1a\u201c\u6628\u65e5\uff0c\u6211\u77e5\u9053\u4f60\u679c\u7136\u662f\u6211\u7684\u59b9\u5b50\uff0c\u6211\u5f88\u6b22\u559c\u4f46\u53c8\u5f88\u75db\u5fc3\u3002\u5440\uff0c\u4f60\u7adf\u548c \u4ed6\u90a3\u6837\u4eb2\u70ed\u3002\u201d\u4e91\u857e\u5fc3\u5934\u4e00\u9707\uff0c\u5782\u4e0b\u5934\u6765\uff0c\u6cea\u73e0\u593a\u7736\u800c\u51fa\u3002\u4e91\u91cd\u9053\uff1a\u201c\u59b9\u59b9\uff0c\u4f60\u7684\u5251\u6cd5\u5df2\u5c3d \u8db3\u95ef\u8361\u6c5f\u6e56\uff0c\u5c31\u53ef\u60dc\u592a\u67d4\u5f31\u4e86\u3002\u59b9\u59b9\uff0c\u4f60\u662f\u4e91\u5bb6\u7684\u5973\u513f\uff0c\u6211\u8981\u4f60\u786c\u8d77\u5fc3\u80a0\u7b54\u5141\u6211\u4e00\u4ef6\u4e8b\u3002\u201d \u4e91\u857e\u9762\u8272\u60e8\u767d\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u54e5\u54e5\u8bf7\u8bf4\u3002\u201d\u4e91\u91cd\u9053\uff1a\u201c\u5f20\u4e39\u67ab\u4e4b\u4ec7\u6211\u53ef\u4ee5\u4e0d\u62a5\uff0c\u4f46\u65e0\u8bba\u5982 \u4f55\uff0c\u4ed6\u603b\u662f\u6211\u4eec\u7237\u7237\u5207\u9f7f\u75db\u6068\u7684\u4ec7\u4eba\u4e4b\u5b50\uff0c\u4f60\u4eca\u751f\u4eca\u4e16\uff0c\u7edd\u4e0d\u80fd\u4e0e\u4ed6\u6210\u4e3a\u592b\u59bb\u3002\u4f60\u4e0e\u4ed6\u62a4\u9001 \u5730\u56fe\uff0c\u90a3\u662f\u4e3a\u4e86\u5927\u660e\u6c5f\u5c71\uff0c\u8def\u4e0a\u540c\u884c\uff0c\u4f60\u53ef\u4e0d\u80fd\u4e3a\u4ed6\u751c\u8a00\u871c\u8bed\u6240\u9a97\u3002\u82e5\u7136\u4f60\u771f\u8981\u559c\u6b22\u4ed6\uff0c\u90a3 \u4e48\u54b1\u4eec\u5144\u59b9\u7684\u60c5\u5206\u5c31\u6b64\u4e00\u5200\u4e24\u65ad\uff01\u963f\u857e\uff0c\u6211\u7edd\u4e0d\u8bb8\u4f60\u4e0e\u4ed6\u6210\u4e3a\u592b\u5987\uff0c\u5c31\u662f\u8fd9\u4e00\u53e5\u8bdd\uff0c\u4f60\u7b54\u5141 \u8fd8\u662f\u4e0d\u7b54\u5141\uff0c\u4f60\u8bf4\uff0c\u4f60\u8bf4\u5440\uff01\u201d \u3000\u3000\u8fd9\u970e\u65f6\u95f4\uff0c\u4e91\u857e\u5fc3\u4e2d\u9178\u82e6\u96be\u8a00\uff0c\u54e5\u54e5\u82e5\u662f\u50cf\u6628\u5929\u90a3\u6837\uff0c\u786c\u90a6\u90a6\u7684\u75be\u8a00\u5389\u8272\u5475\u8d23\u5979\uff0c\u90a3\u4e48 \u5979\u4e5f\u8bb8\u4f1a\u8d1f\u6c14\u4e0d\u7b54\u3002\u7136\u800c\u6b64\u523b\uff0c\u54e5\u54e5\u5374\u662f\u7528\u54c0\u6c42\u7684\u773c\u5149\u5728\u770b\u7740\u5979\uff0c\u5728\u611f\u60c5\u7684\u6fc0\u52a8\u4e4b\u4e2d\uff0c\u4e91\u857e \u5fcd\u7740\u60b2\u75db\uff0c\u62ac\u5934\u51dd\u89c6\u5979\u7684\u54e5\u54e5\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u55ef\uff0c\u54e5\u54e5\uff0c\u6211\u7b54\u5141\u4f60\uff01\u201d \u3000\u3000\u5403\u8fc7\u65e9\u996d\uff0c\u5f20\u4e39\u67ab\u4e0e\u4e91\u857e\u8f9e\u522b\u4f17\u4eba\uff0c\u4e0b\u5c71\u6e21\u6e56\uff0c\u6fb9\u53f0\u7236\u5973\u76f4\u9001\u5230\u6e56\u8fb9\u3002\u6e56\u8fb9\u67f3\u8272\u9752\u9752\u5782 \u6768\u8986\u76d6\u4e4b\u4e0b\uff0c\u5df2\u5907\u597d\u8f7b\u821f\u4e00\u53f6\uff0c\u821f\u4e2d\u7f6e\u6709\u6d1e\u5ead\u5c71\u81ea\u917f\u7684\u7f8e\u9152\uff0c\u8fd8\u6709\u98ce\u5e72\u4e86\u7684\u5c71\u9e21\u91ce\u5473\uff0c\u90a3\u662f \u6d1e\u5ead\u5e84\u4e3b\u7684\u4e00\u756a\u5fc3\u610f\u3002\u6fb9\u53f0\u955c\u660e\u624b\u6500\u5782\u67f3\uff0c\u76ee\u9001\u4ed6\u4eec\u4e0a\u8239\uff0c\u5fc3\u4e2d\u6697\u5ff5\uff1a\u201c\u5782\u67f3\u5343\u4e1d\uff0c\u4e0d\u7cfb\u884c \u821f\u4f4f\u3002\u201d\u4e24\u53e5\u5c0f\u8bcd\u4e0d\u89c9\u9ed8\u7136\u795e\u4f24\u3002\u4e91\u857e\u9053\uff1a\u201c\u955c\u660e\u59d0\u59d0\uff0c\u591a\u70e6\u4f60\u7167\u6599\u6211\u7684\u54e5\u54e5\uff0c\u54b1\u4eec\u4ed6\u65e5\u5728 \u4eac\u518d\u89c1\u3002\u201d\u6fb9\u53f0\u955c\u660e\u4e5f\u7b11\u9053\uff1a\u201c\u4e91\u857e\u59d0\u59d0\u591a\u70e6\u4f60\u7167\u6599\u6211\u4eec\u5c11\u4e3b\u3002\u201d\u6d1e\u5ead\u5e84\u4e3b\u63a5\u53e3\u9053\uff1a\u201c\u795d\u4f60 \u4eec\u4e00\u8def\u5e73\u5b89\uff0c\u5c06\u5730\u56fe\u5e26\u5230\u4eac\u57ce\uff0c\u4e0d\u8d1f\u6211\u4eec\u6570\u4ee3\u76f8\u5b88\u7684\u5fc3\u610f\u3002\u201d\u4e91\u857e\u9762\u4e0a\u6cdb\u8d77\u4e00\u9635\u5a07\u7ea2\uff0c\u4f46\u6d1e \u5ead\u5e84\u4e3b\u8bf4\u5f97\u5982\u6b64\u5e84\u91cd\uff0c\u53ea\u597d\u88e3\u887d\u7b54\u8c22\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u7ecf\u8fc7\u51e0\u8bb8\u98ce\u6ce2\uff0c\u800c\u4eca\u53c8\u5f97\u4e0e\u4e91\u857e\u76f8\u805a\uff0c\u5fc3\u4e2d\u81ea\u662f\u5feb\u6170\u4e4b\u6781\uff0c\u653e\u821f\u4e2d\u6d41\uff0c\u62cd\u8237\u6b4c \u9053\uff1a\u201c\u5e94\u5ff5\u5cad\u8868\u7ecf\u5e74\uff0c\u5b64\u5149\u81ea\u7167\uff0c\u809d\u80c6\u7686\u51b0\u96ea\uff0c\u77ed\u9b13\u8427\u758f\u895f\u8896\u51b7\uff0c\u7a33\u6cdb\u6ca7\u6e9f\u7a7a\u9614\uff01\u201d\u5076\u4e00\u56de \u5934\uff0c\u5374\u89c1\u6fb9\u53f0\u955c\u660e\u8fd8\u662f\u624b\u6267\u5782\u67f3\uff0c\u6014\u6014\u5730\u76ee\u9001\u81ea\u5df1\u3002 \u3000\u3000\u4e91\u857e\u5fc3\u4e2d\u867d\u7136\u4e5f\u89c9\u9ad8\u5174\uff0c\u4f46\u9ad8\u5174\u4e4b\u4e2d\uff0c\u5374\u53c8\u4f3c\u5e26\u7740\u6de1\u6de1\u7684\u54c0\u6101\uff0c\u7f8a\u76ae\u8840\u4e66\u7684\u9634\u5f71\u867d\u7136\u6de1 \u4e86\uff0c\u4f46\u65b0\u7684\u9634\u5f71\uff0c\u5979\u54e5\u54e5\u90a3\u756a\u8a00\u8bed\u6240\u5e26\u6765\u7684\u9634\u5f71\uff0c\u5374\u53c8\u7b3c\u7f69\u5fc3\u5934\u3002\u5f20\u4e39\u67ab\u89c1\u4e91\u857e\u610f\u6b8a\u843d\u5bde\uff0c \u7b11\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u4f60\u600e\u4e48\u4e0d\u7b11\u5440\uff1f\u201d \u3000\u3000\u4e91\u857e\u8f7b\u5f04\u8863\u5e26\uff0c\u9053\uff1a\u201c\u6709\u4ec0\u4e48\u53ef\u7b11\u5440\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u54b1\u4eec\u80fd\u7ed3\u4f34\u540c\u884c\uff0c\u5c82\u975e\u4e00\u4e50\uff1f\u201d \u4e91\u857e\u9053\uff1a\u201c\u8fd9\u8def\u9014\u4e5f\u672a\u514d\u592a\u77ed\u4e86\u5440\uff01\u201d\u5f20\u4e39\u67ab\u4e00\u6014\uff0c\u968f\u5373\u660e\u767d\u5979\u7684\u8bdd\u4e2d\u542b\u610f\uff0c\u5fc3\u9053\uff1a\u201c\u662f \u554a\uff0c\u4eba\u751f\u7684\u65c5\u7a0b\u9065\u8fdc\uff0c\u54b1\u4eec\u8fd9\u4e00\u6bb5\u662f\u592a\u77ed\u4e86\u3002\u201d\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u5fc5\u8bf4\u6211\u5df2\u731c\u5f97\u51fa\u4f60\u54e5\u54e5\u5bf9\u4f60\u7684 \u8a00\u8bed\uff0c\u4f46\u8fd9\u4e0d\u5fc5\u5fc3\u7126\uff0c\u4f60\u54e5\u54e5\u65e2\u8bb8\u54b1\u4eec\u540c\u8d70\u8fd9\u4e00\u6bb5\u65c5\u9014\uff0c\u4e5f\u8bb8\u5c06\u6765\u5c31\u4f1a\u8ba9\u54b1\u4eec\u540c\u8d70\u66f4\u957f\u7684\u65c5 \u9014\u3002\u201d\u4e91\u857e\u4e00\u542c\u6b64\u8a00\uff0c\u5fc3\u4e2d\u4e00\u52a8\uff0c\u60f3\u9053\uff1a\u201c\u54e5\u54e5\u6628\u665a\u4e0e\u4eca\u671d\u4e4b\u95f4\uff0c\u679c\u7136\u5df2\u662f\u6709\u6240\u4e0d\u540c\u3002\u82e5\u5728 \u4ee5\u524d\uff0c\u4ed6\u54ea\u91cc\u80af\u8ba9\u6211\u4e0e\u4e39\u67ab\u540c\u884c\uff1f\u4ed6\u4ee5\u524d\u56fa\u6267\u4e4b\u6781\uff0c\u975e\u5411\u5f20\u4e39\u67ab\u62a5\u4ec7\u4e0d\u53ef\uff0c\u4f46\u800c\u4eca\u8fd9\u4ec7\u6068\u603b \u7b97\u5df2\u51cf\u4e86\u8bb8\u591a\u3002\u5440\uff0c\u5927\u54e5\u7684\u8bdd\u8bf4\u5f97\u6709\u7406\uff0c\u4e16\u95f4\u4e0a\u603b\u4e0d\u4f1a\u6709\u6c38\u8fdc\u4e0d\u53d8\u7684\u4e1c\u897f\u3002\u201d\u7136\u800c\u8f6c\u5ff5\u4e00 \u60f3\uff1a\u201c\u54e5\u54e5\u4eca\u65e9\u7684\u8bf4\u8bdd\uff0c\u53e5\u53e5\u52a8\u81ea\u771f\u60c5\uff0c\u53ea\u6015\u4ed6\u518d\u4e5f\u4e0d\u80fd\u8ba9\u6b65\u4e86\u3002\u201d\u5fc3\u4e2d\u53c8\u662f\u90c1\u90c1\u4e0d\u6b22\uff0c\u4f46 \u518d\u8f6c\u5ff5\u4e00\u60f3\uff0c\u81ea\u5df1\u4ece\u6765\u4e0d\u628a\u5a5a\u5ac1\u4e4b\u4e8b\u653e\u5728\u5fc3\u4e0a\uff0c\u53ea\u8981\u4e24\u4eba\u80fd\u591f\u65f6\u5e38\u89c1\u9762\uff0c\u4e0d\u81f3\u4e8e\u50cf\u4ec7\u654c\u822c\u7684 \u89c1\u9762\uff0c\u90a3\u4e48\u5df2\u662f\u4e8e\u613f\u5df2\u8db3\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u4e0d\u4f4f\u5730\u5fae\u7b11\u770b\u5979\uff0c\u4ed6\u65e9\u5df2\u731c\u900f\u4e86\u5979\u5fc3\u4e2d\u7684\u601d\u60f3\uff0c\u4e5f\u4e0d\u53bb\u6253\u6405\u5979\uff0c\u8ba9\u5979\u4e00\u76f4\u6c89\u601d\uff0c \u5728\u65e0\u8a00\u4e4b\u4e2d\u4eab\u53d7\u7740\u4eba\u751f\u7684\u5999\u5883\u3002 \u3000\u3000\u508d\u665a\u65f6\u5206\uff0c\u6e21\u8fc7\u592a\u6e56\uff0c\u5728\u82cf\u5dde\u4f4f\u5bbf\u4e00\u5bb5\u3002\u5f20\u4e39\u67ab\u4e0a\u6b21\u4e0a\u6d1e\u5ead\u5c71\u65f6\uff0c\u66fe\u5c06\u201c\u7167\u591c\u72ee\u5b50\u9a6c\u201d \u5bc4\u6258\u7ed9\u6fb9\u53f0\u5927\u5a18\u7684\u4e00\u4f4d\u4f84\u5b50\u7167\u7ba1\u8fd9\u6b21\u56de\u6765\u5148\u5c06\u5b9d\u9a6c\u53d6\u4e86\uff0c\u7b2c\u4e8c\u65e5\u4e00\u65e9\u5c31\u4e0e\u4e91\u857e\u8fde\u9a91\u5317\u4e0a\uff0c\u6cbf \u9014\u89c1\u592b\u9a6c\u7cae\u8f66\uff0c\u7edc\u7ece\u4e0d\u7edd\uff0c\u663e\u89c1\u519b\u60c5\u751a\u4e3a\u7d27\u6025\u3002 \u3000\u3000\u8e0f\u5165\u4e86\u6cb3\u5317\u5883\uff0c\u60c5\u52bf\u66f4\u662f\u4e0d\u5bf9\uff0c\u5317\u4e0a\u7684\u4eba\u5c11\u4e86\uff0c\u5357\u4e0b\u7684\u96be\u6c11\u5374\u8d8a\u6765\u8d8a\u591a\uff0c\u518d\u8d70\u4e24\u65e5\uff0c\u5317 \u4e0a\u7684\u4eba\uff0c\u9664\u4e86\u5f20\u3001\u4e91\u4e8c\u4eba\u4e4b\u5916\uff0c\u7adf\u662f\u7edd\u65e0\u4ec5\u6709\u3002\u9053\u8def\u7530\u91ce\uff0c\u90fd\u6324\u6ee1\u4e86\u9003\u96be\u7684\u4eba\u7fa4\uff0c\u6276\u8001\u643a \u5e7c\uff0c\u547c\u7237\u89c5\u5a18\uff0c\u4e00\u7247\u6218\u65f6\u7684\u51c4\u60e8\u666f\u8c61\uff0c\u60e8\u4e0d\u5fcd\u7779\u3002\u9053\u8def\u4f20\u95fb\uff0c\u6709\u7684\u8bf4\u8499\u53e4\u5175\u5df2\u6253\u8fdb\u4e86\u5c45\u5eb8 \u5173\uff0c\u6709\u7684\u8bf4\u5df2\u5230\u4e86\u6000\u67d4\u548c\u5bc6\u4e91\uff08\u4eac\u5e08\u5317\u9762\u7684\u4e24\u4e2a\u53bf\u5206\uff09\uff0c\u6709\u7684\u8bf4\u5df2\u8fc7\u4e86\u516b\u8fbe\u5cad\uff0c\u6709\u7684\u751a\u81f3\u8bf4 \u5df2\u5305\u56f4\u4e86\u5317\u4eac\u3002\u96be\u6c11\u4eec\u542c\u8bf4\u5f20\u4e39\u67ab\u4e0e\u4e91\u857e\u8fd8\u8981\u8d76\u5f80\u5317\u4eac\u90fd\u662f\u5927\u4e3a\u60ca\u8be7\uff0c\u7eb7\u7eb7\u529d\u4ed6\u4eec\u4e0d\u8981\u524d\u5f80 \u9001\u6b7b\u3002\u5f20\u4e39\u67ab\u7126\u6025\u975e\u5e38\uff0c\u7d22\u6027\u907f\u5f00\u5b98\u9053\uff0c\u4e13\u6284\u9669\u7a84\u96be\u884c\u7684\u5c0f\u8def\u884c\u8d70\uff0c\u518d\u8d70\u4e24\u65e5\uff0c\u9053\u8def\u884c\u4eba\u7edd \u8ff9\uff0c\u6751\u843d\u4ea6\u5df2\u5341\u5ba4\u4e5d\u7a7a\uff0c\u60f3\u662f\u5df2\u8feb\u8fd1\u6218\u533a\uff0c\u80fd\u9003\u96be\u7684\u90fd\u9003\u96be\u53bb\u4e86\u3002 \u3000\u3000\u8fd9\u65e5\u5f20\u3001\u4e91\u4e8c\u4eba\u5230\u4e86\u623f\u5c71\u9644\u8fd1\u7684\u4e00\u4e2a\u5c0f\u6751\u843d\uff0c\u89c5\u4e86\u534a\u65e5\uff0c\u53ea\u6709\u4e00\u5bb6\u519c\u6237\uff0c\u8fd8\u672a\u9003\u8d70\u3002\u8fd9 \u5bb6\u519c\u6237\uff0c\u53ea\u6709\u4e00\u4e2a\u8001\u59aa\uff0c\u4e00\u4e2a\u5c11\u5e74\uff0c\u6bcd\u5b50\u4e8c\u4eba\uff0c\u76f8\u4f9d\u4e3a\u547d\uff0c\u6bcd\u4eb2\u5e74\u8001\u4f53\u5f31\uff0c\u884c\u8d70\u4e0d\u52a8\uff0c\u513f\u5b50 \u4e0d\u5fcd\u820d\u5979\u72ec\u81ea\u9003\u751f\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u53e9\u95e8\u6c42\u5bbf\uff0c\u90a3\u8001\u59aa\u5fc3\u5730\u4ec1\u6148\uff0c\u867d\u5728\u5175\u8352\u9a6c\u4e71\u4e4b\u65f6\uff0c\u4e5f\u53eb\u513f\u5b50\u62db\u547c\u4ed6\u4eec\uff0c\u53ea\u662f\u5bb6\u4e2d \u7c73\u7cae\u6240\u5269\u65e0\u51e0\uff0c\u96be\u4ee5\u4e3a\u708a\uff0c\u5e78\u597d\u5f20\u4e39\u67ab\u8fd8\u6709\u4e00\u888b\u7092\u7c73\uff0c\u5c31\u9001\u4e86\u534a\u888b\u7ed9\u5979\uff0c\u53c8\u66ff\u5979\u770b\u75c5\uff0c\u77e5\u662f \u666e\u901a\u7684\u75e2\u75be\uff0c\u5f20\u4e39\u67ab\u968f\u8eab\u643a\u6709\u4e00\u4e9b\u65e5\u5e38\u5e94\u7528\u7684\u836f\u54c1\uff0c\u5c31\u5f00\u4e86\u4e00\u5242\u836f\u7c89\uff0c\u66ff\u5979\u6b62\u75e2\uff0c\u679c\u7136\u751a\u662f \u89c1\u6548\u3002\u95ee\u8d77\u6218\u4e8b\u6d88\u606f\uff0c\u4ed6\u4eec\u4e5f\u4e0d\u592a\u6e05\u695a\uff0c\u53ea\u662f\u524d\u4e24\u65e5\u542c\u5f97\u907f\u96be\u8def\u8fc7\u7684\u4eb2\u5a01\u8bf4\uff0c\u6000\u6765\u57ce\u5df2\u786e\u5b9e \u5931\u9677\u4e86\uff0c\u800c\u6000\u6765\u8ddd\u4ed6\u4eec\u6240\u4f4f\u7684\u6751\u5e84\uff0c\u4ec5\u4e0d\u8fc7\u767e\u6765\u91cc\u8def\u3002 \u3000\u3000\u4e91\u857e\u4e0a\u8def\u4e4b\u65f6\uff0c\u65e9\u5df2\u6539\u4e86\u7537\u513f\u88c5\u675f\uff0c\u519c\u5bb6\u6ca1\u6709\u591a\u4f59\u7684\u5ba2\u623f\u4ed6\u4eec\u5c31\u540c\u4f4f\u5728\u67f4\u623f\uff0c\u4e24\u4eba\u5fe7\u5fc3 \u56fd\u4e8b\uff0c\u90fd\u7761\u4e0d\u7740\u89c9\u3002\u4e09\u66f4\u65f6\u5206\uff0c\u5ffd\u542c\u5f97\u201c\u7830\u201d\u7684\u4e00\u58f0\u5de8\u54cd\uff0c\u519c\u5bb6\u7684\u6728\u95e8\u7ed9\u4eba\u649e\u5f00\uff0c\u5f20\u4e39\u67ab\u6025 \u5fd9\u8df3\u8d77\uff0c\u8d77\u51fa\u53bb\u770b\uff0c\u53ea\u89c1\u4e00\u4e2a\u519b\u5b98\u6253\u626e\u7684\u4eba\uff0c\u6ee1\u8138\u8840\u6c61\uff0c\u6267\u7740\u90a3\u4e2a\u519c\u5bb6\u5c11\u5e74\uff0c\u6c14\u6025\u8d25\u574f\u5730\u56b7 \u9053\uff1a\u201c\u5feb\u5f00\u996d\u7ed9\u8001\u5b50\u5403\uff0c\u4e0d\u7136\u5c31\u628a\u4f60\u6740\u4e86\uff01\u201d\u90a3\u8001\u59aa\u98a4\u5dcd\u5dcd\u5730\u8d70\u4e86\u51fa\u6765\uff0c\u53eb\u9053\uff1a\u201c\u8001\u603b\uff0c\u4f60 \u884c\u884c\u597d\uff0c\u653e\u4e86\u6211\u7684\u513f\u5b50\u5427\u3002\u201d\u90a3\u519b\u5b98\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u597d\uff0c\u4f60\u53bb\u5f04\u996d\u3002\u54c8\uff0c\u5999\u6781\u5566\uff0c\u8fd9 \u91cc\u5c45\u7136\u8fd8\u6709\u4e24\u5339\u9a6c\u3002\u628a\u4e00\u5339\u7ed9\u6211\uff0c\u53eb\u4f60\u7684\u513f\u5b50\u7ed9\u6211\u80cc\u4e1c\u897f\u3002\u201d\u8001\u59aa\u54ed\u9053\uff1a\u201c\u5f04\u996d\u53ef\u4ee5\uff0c\u4f46\u6211 \u4e09\u4e2a\u513f\u5b50\uff0c\u7ed9\u4f60\u4eec\u62c9\u8d70\u4e86\u4e24\u4e2a\uff0c\u73b0\u5728\u53ea\u6709\u8fd9\u4e00\u4e2a\u513f\u5b50\u5566\uff0c\u8001\u603b\uff0c\u4f60\u9ad8\u62ac\u8d35\u624b\uff0c\u653e\u4e86\u4ed6\u5427\u3002\u201d \u90a3\u519b\u5b98\u9a82\u9053\uff1a\u201c\u4f60\u8fd9\u8001\u7cca\u6d82\uff0c\u8499\u53e4\u5175\u5df2\u6253\u4e86\u8fdb\u6765\uff0c\u8c01\u90fd\u8981\u53bb\u6253\u4ed7\u3002\u201d\u659c\u773c\u4e00\u77a5\uff0c\u5ffd\u89c1\u5f20\u4e39\u67ab \u7ad9\u5728\u5c4b\u89d2\u6cb9\u706f\u9eef\u6de1\uff0c\u770b\u4e0d\u6e05\u9762\u5f71\u3002\u90a3\u519b\u5b98\u5927\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u8fd9\u8001\u6bcd\u732a\u8bf4\u8c0e\uff0c\u8fd9\u91cc\u4e0d\u662f\u8fd8\u6709 \u4e00\u4e2a\u5417\uff1f\u201d \u3000\u3000\u90a3\u519b\u5b98\u5de6\u624b\u6263\u7740\u519c\u5bb6\u5c11\u5e74\u7684\u8109\u95e8\u4e0d\u653e\uff0c\u817e\u51fa\u53f3\u624b\uff0c\u5c31\u6251\u4e0a\u524d\u53bb\u6293\u5f20\u4e39\u67ab\u3002\u5f20\u4e39\u67ab\u51b7\u51b0\u51b0 \u5730\u76ef\u4ed6\u4e00\u773c\uff0c\u9053\uff1a\u201c\u4f60\u4e0d\u53bb\u6253\u4ed7\u53cd\u6765\u6b3a\u4fae\u767e\u59d3\uff01\u201d\u53cd\u624b\u4e00\u64d2\uff0c\u53cc\u638c\u4e00\u4ea4\uff0c\u90a3\u519b\u5b98\u201c\u54a6\u201d\u7684\u4e00 \u58f0\u4e00\u62f3\u76f4\u6363\uff0c\u5f20\u4e39\u67ab\u53ea\u7528\u4e86\u4e09\u6210\u529b\u91cf\uff0c\u5ffd\u89c9\u90a3\u519b\u5b98\u4e00\u6293\u4e00\u62f3\uff0c\u7adf\u7136\u662f\u70b9\u82cd\u6d3e\u7684\u4e0a\u4e58\u6b66\u529f\uff0c\u5185 \u52b2\u4ea6\u751a\u6c89\u96c4\uff0c\u597d\u751f\u8be7\u5f02\uff0c\u4f7f\u4e2a\u201c\u8131\u888d\u89e3\u7532\u201d\uff0c\u80a9\u5934\u4e00\u77ee\uff0c\u6325\u638c\u4e00\u9001\uff0c\u5de6\u811a\u53c8\u98de\u8d77\u8e22\u4ed6\u624b\u8155\u3002 \u90a3\u519b\u5b98\u8feb\u5f97\u653e\u4e86\u519c\u5bb6\u5c11\u5e74\uff0c\u5de6\u62f3\u6a2a\u683c\uff0c\u53f3\u638c\u6258\u5f20\u4e39\u67ab\u7684\u811a\u5c16\uff0c\u5f20\u4e39\u67ab\u7a81\u5c06\u52b2\u529b\u4e00\u6536\uff0c\u8f7b\u98d8\u98d8 \u7684\u4e00\u5e26\uff0c\u90a3\u519b\u5b98\u201c\u54ce\u54df\u201d\u4e00\u58f0\uff0c\u8dcc\u5012\u5730\u4e0a\uff0c\u5ffd\u7136\u62ac\u5934\u8bf4\u9053\uff1a\u201c\u54a6\uff0c\u4f60\u4e0d\u662f\u5f20\u4e39\u67ab\u5417\uff1f\u4f60\u3001\u4f60 \u9976\u4e86\u6211\u5427\uff0c\u4e0d\u8981\u6349\u6211\u5230\u8499\u53e4\u53bb\u3002\u201d \u3000\u3000\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u80e1\u8bf4\uff0c\u8c01\u6349\u4f60\u5230\u8499\u53e4\u53bb\uff1f\u201d\u63d0\u8d77\u4e86\u90a3\u4e2a\u519b\u5b98\u8863\u8896\u4e00\u62b9\uff0c\u5c06\u4ed6\u9762\u4e0a\u7684\u8840\u6c61\u62b9 \u51c0\uff0c\u5b9a\u775b\u4e00\u770b\uff0c\u767b\u65f6\u5446\u4e86\uff0c\u8fd9\u519b\u5b98\u7adf\u7136\u662f\u5927\u5185\u603b\u7ba1\u5eb7\u8d85\u6d77\u3002\u5f20\u4e39\u67ab\u5728\u6821\u573a\u6bd4\u6b66\uff0c\u593a\u6b66\u72b6\u5143\u4e4b \u65f6\u66fe\u89c1\u8fc7\u4ed6\u966a\u7740\u7687\u5e1d\u5728\u770b\u53f0\u4e0a\u505a\u4e3b\u8003\u5b98\u3002 \u3000\u3000\u90a3\u8001\u5a46\u5a46\u677e\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5440\uff0c\u8fd9\u4e9b\u5b98\u7237\u4e5f\u771f\u6a2a\u86ee\u3002\u201d\u5ffd\u800c\u53c8\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5440\uff0c\u4ed6 \u4e5f\u53ef\u601c\uff0c\u4f24\u6210\u8fd9\u4e2a\u6837\u5b50\u3002\u201d\u5eb7\u8d85\u6d77\u8eab\u4e0a\u4e2d\u4e86\u5341\u51e0\u652f\u7bad\uff0c\u8863\u88f3\u90fd\u6cbe\u4e86\u9c9c\u8840\uff0c\u6591\u6591\u70b9\u70b9\uff0c\u6709\u4e24\u652f \u7bad\u4e14\u5c1a\u672a\u62d4\u51fa\uff0c\u53cc\u773c\u5931\u60ca\u65e0\u795e\uff0c\u663e\u89c1\u5f97\u5341\u5206\u75b2\u4e4f\u3002\u5f20\u4e39\u67ab\u5fc3\u9053\uff1a\u201c\u8fd9\u25a1\u4e5f\u771f\u4e86\u5f97\uff0c\u5c45\u7136\u5728\u53d7 \u4f24\u4e4b\u540e\uff0c\u7b4b\u75b2\u529b\u7aed\u4e4b\u65f6\uff0c\u8fd8\u80fd\u63a5\u6211\u4e24\u62db\u3002\u201d \u3000\u3000\u5f20\u4e39\u67ab\u4e00\u770b\uff0c\u4ed6\u6240\u53d7\u7684\u7bad\u4f24\u90fd\u662f\u5916\u4f24\uff0c\u65e0\u5927\u9632\u788d\uff0c\u5c06\u8fd8\u63d2\u5728\u4ed6\u5173\u8282\u4e4b\u5904\u7684\u4e24\u652f\u7bad\uff0c\u4e5f\u7528 \u8f7b\u5de7\u7684\u624b\u6cd5\u7ed9\u4ed6\u62d4\u4e86\uff0c\u5e76\u66ff\u4ed6\u6577\u4e0a\u4e86\u91d1\u521b\u836f\u3002\u90a3\u8001\u5a46\u5a46\u95ee\u9053\uff1a\u201c\u8fd9\u4f4d\u8001\u603b\u662f\u4f60\u670b\u53cb\u5417\uff1f\u201d\u5f20 \u4e39\u67ab\u542b\u7cca\u5e94\u4e86\u4e00\u58f0\uff0c\u597d\u751f\u60ed\u6127\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u82e5\u7136\u4ed6\u4eec\u77e5\u9053\u8fd9\u4eba\u610f\u662f\u5927\u5185\u603b\u7ba1\uff0c\u7687\u5e1d\u7684\u8138\u76ae \u4e5f\u90fd\u4e22\u5c3d\u4e86\u3002\u201d \u3000\u3000\u90a3\u8001\u59aa\u771f\u7684\u8981\u8fdb\u53bb\u5f04\u996d\uff0c\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u4e0d\u5fc5\u5566\u3002\u4f60\u4eec\u8fdb\u53bb\u7761\u5427\uff0c\u6211\u670d\u4f8d\u4ed6\u3002\u201d\u628a\u5269\u4e0b\u7684 \u534a\u888b\u7092\u7c73\uff0c\u6ce1\u4e86\u5f00\u6c34\uff0c\u9053\uff1a\u201c\u5eb7\u603b\u7ba1\uff0c\u4f60\u5c06\u5c31\u70b9\u5427\u3002\u201d \u3000\u3000\u5eb7\u8d85\u6d77\u5f53\u65e5\u5728\u6821\u573a\u6bd4\u6b66\u4e4b\u65f6\uff0c\u66fe\u4e0b\u4ee4\u8981\u6349\u5f20\u4e39\u67ab\uff0c\u8fd9\u65f6\u89c1\u4ed6\u5e76\u4e0d\u8bb0\u4ec7\uff0c\u8fd8\u66ff\u4ed6\u6cbb\u4f24\uff0c\u54ea \u91cc\u8fd8\u6562\u591a\u8bf4\u3002\u4ed6\u72fc\u541e\u864e\u54bd\uff0c\u628a\u5f20\u4e39\u67ab\u4ec5\u5269\u7684\u534a\u888b\u7092\u7c73\u5168\u90fd\u5403\u5b8c\uff0c\u7cbe\u795e\u6e10\u6e10\u6062\u590d\u3002\u5f20\u51e1\u67ab\u95ee \u9053\uff1a\u201c\u5eb7\u603b\u7ba1\uff0c\u4f60\u600e\u4e48\u4e0d\u8ddf\u968f\u7687\u4e0a\uff0c\u5355\u8eab\u9003\u5230\u8fd9\u513f\uff1f\u201d\u5eb7\u8d85\u6d77\u9053\uff1a\u201c\u5440\uff0c\u4e00\u8a00\u96be\u5c3d\u3002\u6211\u662f\u8ddf \u968f\u7687\u4e0a\uff0c\u6211\u4eec\u4e94\u5341\u4e07\u5927\u519b\u5168\u90fd\u57ae\u4e86\uff0c\u6211\u82e5\u4e0d\u9003\uff0c\u6027\u547d\u4e0d\u4fdd\uff01\u201d \u3000\u3000\u5f20\u4e39\u67ab\u5927\u5403\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u4f60\u672c\u6765\u662f\u8ddf\u968f\u7687\u4e0a\u7684\uff1f\u96be\u9053\u8499\u53e4\u5175\u5df2\u8fdb\u4e86\u5317\u4eac\u5417\uff1f\u201d\u5eb7 \u8d85\u6d77\u9053\uff1a\u201c\u4e0d\uff0c\u7687\u4e0a\u5fa1\u9a7e\u4eb2\u5f81\uff0c\u73b0\u5728\u6000\u6765\u57ce\u5916\uff0c\u5df2\u9677\u5165\u4e86\u654c\u4eba\u7684\u91cd\u91cd\u56f4\u56f0\u4e4b\u4e2d\u4e86\u3002\u201d\u5f20\u4e39\u67ab \u66f4\u60ca\u9053\uff1a\u201c\u4ec0\u4e48\uff0c\u7687\u4e0a\u5c45\u7136\u4f1a\u5fa1\u9a7e\u4eb2\u5f81\uff1f\u8fd9\u662f\u8c01\u7684\u4e3b\u610f\uff1f\u201d\u5eb7\u8d85\u6d77\u9053\uff1a\u201c\u8fd9\u662f\u738b\u516c\u516c\u7684\u4e3b \u610f\u3002\u201d\u5f20\u4e39\u67ab\u5927\u6012\uff0c\u201c\u556a\u201d\u7684\u4e00\u638c\uff0c\u628a\u996d\u684c\u65ab\u4e86\u4e00\u89d2\uff0c\u6012\u9053\uff1a\u201c\u738b\u632f\u8fd9\u25a1\uff0c\u597d\u6bd2\u7684\u5fc3\u80a0\uff01\u201d \u3000\u3000\u5eb7\u8d85\u6d77\u4e0d\u6562\u4f5c\u58f0\uff0c\u4e91\u857e\u8d70\u4e86\u51fa\u6765\uff0c\u9053\uff1a\u201c\u4f60\u4e0d\u8981\u751f\u6c14\uff0c\u518d\u95ee\u95ee\u4ed6\u3002\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u4e3a\u4ec0 \u4e48\u4e0d\u53eb\u4e8e\u8c26\u5927\u4eba\u9886\u5175\uff1f\u201d\u5eb7\u8d85\u6d77\u9053\uff1a\u201c\u671d\u5ef7\u4e4b\u4e8b\uff0c\u6211\u54ea\u61c2\u5f97\uff1f\u53ea\u542c\u4ed6\u4eec\u8bf4\u4e8e\u8c26\u662f\u6587\u5b98\uff0c\u4e0d\u80fd \u9886\u5175\u3002\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u54fc\uff0c\u4ed6\u4eec\u9886\u5175\u73b0\u5728\u600e\u4e48\u5566\uff1f\u201d\u5eb7\u8d85\u6d77\u9053\uff1a\u201c\u7687\u4e0a\u4e0e\u738b\u516c\u516c\u9886\u5175\uff0c\u4e03\u6708 \u5341\u516d\u65e5\u4ece\u5317\u4eac\u51fa\u53d1\uff0c\u5341\u4e5d\u65e5\u8fc7\u5c45\u5eb8\u5173\uff0c\u4e8c\u5341\u4e09\u65e5\u5230\u5ba3\u5e9c\uff0c\u516b\u6708\u521d\u4e00\u8fdb\u5230\u5927\u540c\u57ce\uff0c\u90a3\u65f6\u8fde\u65e5\u5927 \u98ce\u6025\u96e8\uff0c\u519b\u58eb\u6ca1\u5907\u5bd2\u8863\uff0c\u7adf\u7136\u5c31\u5728\u5927\u540c\u57ce\u51bb\u6b7b\u4e86\u51e0\u4e07\u4eba\uff0c\u672a\u89c1\u654c\u4eba\u519b\u5bb9\u5df2\u4e71\u3002\u5175\u90e8\u5c1a\u4e66\u909d\u5c18 \u5760\u9a6c\u91cd\u4f24\uff0c\u6237\u90e8\u5c1a\u4e66\u738b\u4f50\u594f\u8bf7\u56de\u5175\uff0c\u738b\u516c\u516c\u4e0d\u5141\uff0c\u5c31\u5728\u884c\u519b\u4e4b\u9645\uff0c\u7f5a\u4ed6\u8dea\u5728\u8349\u4e2d\u3002\u516b\u6708\u521d\u4e8c \u5148\u950b\u77f3\u4ea8\u548c\u74e6\u523a\u519b\u63a5\u6218\u4e8e\u9633\u548c\u53e3\uff0c\u5168\u519b\u8986\u6ca1\uff0c\u603b\u5175\u5b98\u6b66\u8fdb\u4f2f\u6731\u5195\uff0c\u5927\u540c\u603b\u7763\u519b\u52a1\u897f\u5b81\u4faf\u5b8b\u745b \u4e8c\u4eba\uff0c\u76f8\u7ee7\u6218\u6b7b\u3002\u5927\u540c\u603b\u5175\u90ed\u767b\u529d\u7687\u4e0a\u4ece\u7d2b\u8346\u5173\u9000\u5175\u53ef\u4fdd\u5b89\u5168\uff0c\u738b\u516c\u516c\u4e0d\u542c\u3002\u738b\u516c\u516c\u662f\u851a\u5dde \u4eba\uff0c\u4ed6\u8981\u9080\u5fa1\u9a7e\u4e34\u5e78\u4ed6\u7684\u5b85\u7b2c\uff0c\u6307\u6325\u5927\u519b\u5411\u851a\u5dde\u79fb\u52a8\uff0c\u884c\u4e86\u56db\u5341\u91cc\uff0c\u4ed6\u53c8\u5ffd\u7136\u6539\u4ee4\u5927\u519b\u8f6c\u5411 \u4e1c\u884c\uff0c\u8bf4\u662f\u6050\u6015\u519b\u9a6c\u635f\u6bc1\u4ed6\u7684\u7530\u7a3c\uff0c\u4e8e\u662f\u5faa\u539f\u8def\u5954\u56de\u5ba3\u5e9c\u3002\u521d\u5341\u65e5\u5230\u5ba3\u5e9c\uff0c\u654c\u519b\u4ea6\u5df2\u8ffd\u5230\uff0c \u5728\u9e5e\u513f\u5cad\u4e00\u6218\uff0c\u5168\u519b\u6e83\u5954\uff0c\u5927\u524d\u65e5\uff0c\u7687\u4e0a\u9003\u5230\u571f\u6728\u5821\uff0c\u654c\u519b\u524d\u950b\u65e9\u5df2\u4ece\u5c0f\u8def\u6284\u8fc7\u4e86\u571f\u6728\u5821\uff0c \u53cd\u8fc7\u6765\u5305\u56f4\u4e86\u3002\u201d \u3000\u3000\u5f20\u4e39\u67ab\u8d8a\u542c\u8d8a\u662f\u6c14\u6124\uff0c\u8fd9\u6b21\u201c\u5fa1\u9a7e\u4eb2\u5f81\u201d\uff0c\u884c\u519b\u548c\u9000\u519b\u7684\u8def\u7ebf\u4ee5\u53ca\u5e03\u7f6e\uff0c\u5206\u660e\u90fd\u662f\u738b\u632f \u6240\u5e03\u4e0b\u7684\u5708\u5957\uff0c\u4ee4\u660e\u519b\u4e00\u8d25\u6d82\u5730\u4e0d\u53ef\u6536\u62fe\u3002\u53ea\u542c\u5f97\u5eb7\u8d85\u6d77\u53c8\u9053\uff1a\u201c\u5e78\u6211\u89c1\u673a\u5f97\u65e9\uff0c\u4e58\u7740\u591c\u95f4 \u51b2\u4e86\u51fa\u6765\u3002\u8981\u4e0d\u7136\u88ab\u56f4\u5728\u571f\u6728\u5821\uff0c\u4e0d\u6218\u6b7b\u4e5f\u5f97\u997f\u6b7b\u3002\u201d \u3000\u3000\u5f20\u4e39\u67ab\u54fc\u4e86\u4e00\u58f0\uff0c\u5ffd\u9053\uff1a\u201c\u4f60\u80cc\u4e0a\u8fd9\u4e00\u5927\u5305\u4e1c\u897f\uff0c\u91cd\u7538\u7538\u7684\u662f\u751a\u7269\u4e8b\uff1f\u201d\u5eb7\u8d85\u6d77\u9762\u8272\u5927 \u53d8\uff0c\u5f20\u4e39\u67ab\u500f\u5730\u4f38\u624b\u5feb\u5982\u95ea\u7535\uff0c\u5c06\u4ed6\u80cc\u5305\u62a2\u4e86\u8fc7\u6765\uff0c\u6454\u843d\u5730\u4e0a\uff0c\u53ea\u89c1\u91d1\u5143\u5b9d\u6ee1\u5730\u90fd\u662f\u3002\u5f20\u4e39 \u67ab\u51b7\u7b11\u9053\uff1a\u201c\u539f\u6765\u4f60\u62c9\u592b\u4e3a\u7684\u662f\u66ff\u4f60\u80cc\u91d1\u5143\u5b9d\u3002\u201d\u5eb7\u8d85\u6d77\u966a\u7740\u7b11\u8138\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u70b9\u8d22\u7269\uff0c\u90fd \u662f\u5723\u4e0a\u5386\u6765\u6240\u8d50\uff0c\u5e76\u975e\u4e0d\u4e49\u4e4b\u8d22\u3002\u4eca\u65e5\u8499\u4f60\u76f8\u6551\uff0c\u54b1\u4eec\u5bf9\u5206\u4e86\u5427\u3002\u201d \u3000\u3000\u5f20\u4e39\u67ab\u51b7\u51b7\u4e00\u7b11\uff0c\u5ffd\u5730\u9762\u8272\u4e00\u7aef\uff0c\u65a5\u7740\uff1a\u201c\u4e8f\u4f60\u8fd8\u662f\u5927\u5185\u603b\u7ba1\uff0c\u4e8f\u4f60\u8fd8\u6562\u63d0\u7687\u4e0a\u7684\u6069 \u5178\uff0c\u7687\u4e0a\u65e2\u7136\u5bf9\u4f60\u4e0d\u8584\uff0c\u4e3a\u4f55\u4f60\u5728\u5371\u96be\u4e4b\u65f6\uff0c\u5f03\u4ed6\u800c\u8d70\uff1f\u201d\u5eb7\u8d85\u6d77\u4e00\u6014\uff0c\u4ed6\u77e5\u9053\u5f20\u4e39\u67ab\u662f\u7687 \u4e0a\u7684\u4ec7\u4eba\uff0c\u6599\u4e0d\u5230\u4ed6\u7adf\u4f1a\u4ee5\u6b64\u8a00\u76f8\u8d23\u3002\u53ea\u542c\u5f97\u5f20\u4e39\u67ab\u53c8\u9053\uff1a\u201c\u4f60\u4eca\u665a\u5c31\u5728\u6b64\u6b47\u6b47\uff0c\u660e\u513f\u4e00 \u65e9\uff0c\u6211\u548c\u4f60\u8d76\u56de\u571f\u6728\u5821\u53bb\u3002\u201d\u5eb7\u8d85\u6d77\u8a00\u9053\uff1a\u201c\u53bb\u9001\u6b7b\u5417\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u4f60\u98df\u56fd\u5bb6\u4ff8\u7984\uff0c\u5c31 \u662f\u660e\u77e5\u9001\u6b7b\uff0c\u4e5f\u662f\u8be5\u5f53\uff01\u4f55\u51b5\u9001\u6b7b\u4e5f\u4e0d\u6b62\u4f60\u4e00\u4eba\uff0c\u6211\u4eec\u90fd\u966a\u4f60\u9001\u6b7b\u3002\u201d \u3000\u3000\u5eb7\u8d85\u6d77\u9762\u8272\u53d1\u767d\uff0c\u5ffd\u5730\u5f2f\u4e0b\u8170\u6765\uff0c\u5c06\u5730\u4e0a\u7684\u91d1\u5143\u5b9d\u4e00\u4e2a\u4e2a\u62fe\u8d77\uff0c\u5f20\u4e39\u67ab\u4e0e\u4e91\u857e\u8fde\u8fde\u51b7 \u7b11\uff0c\u4e5f\u4e0d\u62e6\u4ed6\uff0c\u6709\u51e0\u4e2a\u91d1\u5143\u5b9d\u6eda\u5230\u6a90\u9636\u5e95\u4e0b\uff0c\u5f20\u4e39\u67ab\u7684\u767d\u9a6c\u548c\u4e91\u857e\u7684\u7ea2\u9a6c\u90fd\u5728\u90a3\u513f\u3002\u5eb7\u8d85\u6d77 \u722c\u5230\u9a6c\u8179\u4e0b\u53bb\u62fe\u91d1\u5143\u5b9d\uff0c\u7a81\u7136\u4e00\u8dc3\u800c\u8d77\uff0c\u6309\u7740\u767d\u9a6c\u7684\u9888\u9879\uff01 \u3000\u3000\u90a3\u201c\u7167\u591c\u72ee\u5b50\u9a6c\u201d\u795e\u9a8f\u975e\u5e38\uff0c\u4e00\u58f0\u6012\u5636\uff0c\u540e\u8e44\u53cd\u8e22\uff0c\u5f20\u4e39\u67ab\u559d\u9053\uff1a\u201c\u4f60\u5e72\u4ec0\u4e48\uff1f\u201d\u5eb7\u8d85 \u6d77\u6025\u5207\u4e4b\u95f4\uff0c\u5236\u670d\u4e0d\u4e86\u90a3\u5339\u767d\u9a6c\u53cd\u8eab\u8df3\u4e0a\u4e86\u4e91\u857e\u6240\u9a91\u7684\u7ea2\u9a6c\uff0c\u5927\u7b11\u9053\uff1a\u201c\u4ffa\u5eb7\u8d85\u6d77\u8fd8\u8981\u591a\u4eab \u51e0\u5e74\u6e05\u798f\uff0c\u6055\u4e0d\u966a\u4f60\u4eec\u9001\u6b7b\u5566\uff01\u201d\u4e00\u5200\u63d2\u5165\u9a6c\u81c0\uff0c\u7ea2\u9a6c\u8d1f\u75db\u72c2\u5954\u51b2\u51fa\u95e8\u5916\uff0c\u970e\u5ffd\u4e4b\u95f4\uff0c\u5df2\u6d88 \u5931\u5728\u8292\u8292\u591c\u8272\u4e4b\u4e2d\u3002 \u3000\u3000\u4e91\u857e\u9053\uff1a\u201c\u5927\u54e5\uff0c\u8ffd\u4ed6\u56de\u6765\uff01\u201d\u5f20\u4e39\u67ab\u6447\u4e86\u6447\u5934\u9053\uff1a\u201c\u8fd9\u6837\u7684\u4eba\uff0c\u8ffd\u56de\u6765\u4e5f\u6ca1\u7528\u3002\u201d\u957f \u957f\u5730\u53f9\u4e86\u53e3\u6c14\uff0c\u9893\u7136\u5750\u4e0b\uff0c\u9053\uff1a\u201c\u5cb3\u6b66\u7a46\u5f53\u5e74\u8bf4\u5f97\u597d\uff1a\u6587\u5b98\u7231\u94b1\u6b66\u5b98\u60dc\u547d\uff0c\u5927\u4e8b\u5c1a\u6709\u53ef\u4e3a \u5417\uff1f\u800c\u4eca\u7adf\u662f\u6587\u5b98\u6b66\u5b98\uff0c\u90fd\u7231\u94b1\u60dc\u547d\uff0c\u738b\u632f\u4e4b\u5978\uff0c\u4e0d\u4e0b\u4e8e\u79e6\u6867\uff0c\u6050\u6015\u5b8b\u4ee3\u7684\u5386\u53f2\uff0c\u5fbd\u3001\u94a6\u4e8c \u5e1d\u8499\u5c18\u4e4b\u8fb1\uff0c\u53c8\u5c06\u89c1\u4e4b\u4eca\u65e5\u4e86\u3002\u201d\u4e91\u857e\u9053\uff1a\u201c\u671d\u4e2d\u867d\u6709\u79e6\u6867\uff0c\u4ea6\u6709\u5cb3\u98de\uff0c\u4e8e\u9601\u8001\u7684\u5fe0\u5fc3\uff0c\u4e0d \u51cf\u4e8e\u5cb3\u6b66\u7a46\uff0c\u5927\u54e5\u4e0d\u5fc5\u7070\u5fc3\u3002\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u53ea\u53ef\u60dc\u4ed6\u6ca1\u6709\u5175\u6743\u3002\u5440\u6211\u6068\u4e0d\u5f97\u63d2\u7fc5\u98de\u5230\u5317 \u4eac\uff0c\u52a9\u4ed6\u4e00\u81c2\u4e4b\u529b\u3002\u201d \u3000\u3000\u4e24\u4eba\u5fc3\u6025\u975e\u5e38\uff0c\u793a\u5f85\u5929\u660e\u5c31\u544a\u522b\u4e86\u519c\u5bb6\u6bcd\u5b50\uff0c\u540c\u4e58\u767d\u9a6c\uff0c\u7edd\u5c18\u800c\u53bb\u3002\u884c\u4e0d\u591a\u4e45\uff0c\u5df2\u542c\u5f97 \u524d\u9762\u9f13\u89d2\u4e4b\u58f0\u3002\u5f20\u4e39\u67ab\u7b56\u9a6c\u767b\u4e0a\u4e00\u4e2a\u5c71\u4e18\uff0c\u628a\u76ee\u9065\u671b\uff0c\u53ea\u89c1\u524d\u9762\u65cc\u65d7\u62db\u5c55\uff0c\u6f2b\u5c71\u904d\u91ce\uff0c\u90fd\u662f \u8499\u53e4\u5175\u3002\u4e91\u857e\u82e6\u7b11\u9053\uff1a\u201c\u8fc7\u4e0d\u53bb\u5566\uff01\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u6709\u529e\u6cd5\u3002\u201d\u53eb\u4e91\u857e\u8eb2\u5728\u5c71\u4e0a\uff0c\u4ed6\u9a91\u9a6c\u4e0b \u5c71\uff0c\u7adf\u7136\u5954\u5165\u654c\u9635\u3002\u4e91\u857e\u5927\u60ca\u5931\u8272\u7247\u523b\u4e4b\u540e\uff0c\u5ffd\u89c1\u5f20\u4e39\u67ab\u4e0e\u4e24\u4e2a\u74e6\u523a\u519b\u5b98\u4e00\u540c\u56de\u6765\uff0c\u4e91\u857e\u5927 \u4e3a\u5947\u602a\u3002\u539f\u6765\u5f20\u4e39\u67ab\u7cbe\u901a\u8499\u53e4\u8bed\uff0c\u6000\u4e2d\u8fd8\u85cf\u6709\u5f53\u5e74\u9003\u51fa\u74e6\u523a\u4e4b\u65f6\uff0c\u6240\u5077\u5e26\u7684\u74e6\u523a\u519b\u4e2d\u4ee4\u7bad\uff0c \u4ed6\u5192\u79f0\u662f\u74e6\u523a\u5728\u6218\u524d\u6d3e\u6765\u4e2d\u56fd\u6f5c\u4f0f\u7684\u63a2\u5b50\uff0c\u679c\u7136\u54c4\u5f97\u4e24\u4e2a\u519b\u5b98\u76f8\u4fe1\u3002\u5f20\u4e39\u67ab\u8bf4\u662f\u5728\u9644\u8fd1\u5c71 \u4e0a\uff0c\u85cf\u6709\u53ef\u7591\u4e4b\u4eba\uff0c\u53eb\u4ed6\u4eec\u540c\u6765\u641c\u7d22\uff0c\u4e00\u4e0a\u571f\u4e18\uff0c\u5f20\u4e39\u67ab\u767b\u65f6\u53d8\u8138\uff0c\u7528\u91cd\u624b\u6cd5\u5c06\u4ed6\u4eec\u51fb\u6bd9\u3002 \u8fd9\u5c0f\u4e18\u79bb\u6218\u573a\u8fd8\u6709\u4e03\u516b\u91cc\uff0c\u524d\u9762\u74e6\u523a\u5175\u867d\u591a\uff0c\u5374\u65e0\u4e00\u4eba\u77e5\u6653\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u597d\u5566\uff0c\u54b1\u4eec\u5c31\u5192\u5145\u74e6\u523a\u519b\u5b98\uff0c\u4f60\u7684\u8499\u53e4\u8bed\u6ca1\u6709\u5fd8\u8bb0\u5427\uff1f\u201d\u4e91\u857e\u7b11\u9053\uff1a\u201c\u8fd8 \u6ca1\u5fd8\u8bb0\u3002\u60f3\u4e0d\u5230\u800c\u4eca\u53ef\u6d3e\u4e0a\u7528\u573a\u5566\u3002\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u6211\u5df2\u63a2\u542c\u6e05\u695a\uff0c\u4ed6\u4eec\u662f\u53f3\u536b\u519b\u4e2d\u7684\u7b2c\u4e09 \u961f\u7684\uff0c\u4ed6\u4eec\u8fd9\u4e00\u961f\uff0c\u6628\u5929\u6253\u4e86\u4e2a\u786c\u4ed7\uff0c\u5927\u7ea6\u662f\u78b0\u4e0a\u5f20\u98ce\u5e9c\u6240\u5e26\u7684\u5fa1\u6797\u519b\uff0c\u4f24\u4ea1\u516b\u4e5d\uff0c\u4ed6\u4eec\u6b63 \u5f85\u6574\u7f16\u5230\u5176\u4ed6\u961f\u53bb\uff0c\u54b1\u4eec\u5192\u5145\u4ed6\u4eec\u53bb\uff0c\u6b63\u662f\u5408\u9002\u3002\u8bb0\u5f97\uff0c\u4f60\u53eb\u54c8\u74e6\uff0c\u6211\u53eb\u8fbe\u83b1\u3002\u201d\u4e24\u4eba\u5265\u4e0b \u74e6\u523a\u519b\u5b98\u7684\u8863\u670d\uff0c\u867d\u7136\u4e0d\u5927\u5408\u8eab\uff0c\u5374\u4e5f\u906e\u63a9\u5f97\u8fc7\u3002\u4e24\u4eba\u4f0f\u5728\u5c71\u4e0a\uff0c\u5f85\u5f97\u508d\u665a\uff0c\u624d\u6084\u6084\u6e9c\u4e86\u51fa \u6765\uff0c\u7b56\u9a6c\u8fdb\u74e6\u523a\u519b\u9635\u5730\u3002\u5f20\u4e39\u67ab\u5bf9\u74e6\u523a\u5175\u5236\u7b49\u60c5\u51b5\uff0c\u90fd\u6781\u719f\u6089\uff0c\u74e6\u523a\u519b\u53c8\u5728\u5927\u80dc\u4e4b\u540e\uff0c\u9632\u5907 \u5e76\u4e0d\u5c0f\u5fc3\uff0c\u5c45\u7136\u88ab\u4ed6\u4eec\u7792\u6df7\u8fc7\u5173\uff0c\u6536\u5bb9\u5728\u4e00\u4e2a\u4e34\u65f6\u6210\u7acb\u7684\u536b\u961f\u4e4b\u4e2d\u3002 \u3000\u3000\u7b2c\u4e8c\u65e5\u4e00\u65e9\uff0c\u74e6\u523a\u52a1\u540e\u5907\u90e8\u961f\uff0c\u90fd\u4e00\u9f50\u5f00\u62d4\uff0c\u8d76\u5230\u571f\u6728\u5821\u589e\u63f4\uff0c\u5348\u996d\u8fc7\u540e\uff0c\u5230\u4e86\u6218\u573a\uff0c \u53ea\u89c1\u660e\u5175\u5df2\u88ab\u622a\u6210\u65e0\u6570\u5c0f\u80a1\uff0c\u4e1c\u5954\u897f\u7a9c\uff0c\u5f20\u4e39\u67ab\u4e00\u89c1\uff0c\u4e0d\u89c9\u5927\u60ca\u5931\u8272\uff01\u6b63\u662f\uff1a \u3000\u3000\u80e1\u5c18\u672a\u9756\u5c71\u6cb3\u53d8\uff0c\u6b63\u662f\u7537\u513f\u62a5\u56fd\u65f6\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u840d\u8e2a\u4fa0\u5f71\u5f55\u300b\u2014\u2014\u7b2c\u4e8c\u5341\u56de\u3000\u864e\u5e10\u86ee\u82b1\u75be\u60c5\u7f14\u9e33\u8c31\u3000\u6e05\u79cb\u4fea\u5f71\u5999\u8bed\u8ba2\u5fc3\u76df \u6881\u7fbd\u751f\u300a\u840d\u8e2a\u4fa0\u5f71\u5f55\u300b \u7b2c\u4e8c\u5341\u56de\u3000\u864e\u5e10\u86ee\u82b1\u75be\u60c5\u7f14\u9e33\u8c31\u3000\u6e05\u79cb\u4fea\u5f71\u5999\u8bed\u8ba2\u5fc3\u76df \u3000\u3000\u53ea\u542c\u5f97\u74e6\u523a\u5175\u5439\u8d77\u51b2\u950b\u53f7\u53f7\u89d2\uff0c\u91d1\u9f13\u5927\u9e23\uff0c\u5c71\u5934\u4e0a\u5347\u8d77\u4e86\u201c\u5e05\u201d\u5b57\u5927\u65d7\uff0c\u4e00\u4e2a\u756a\u738b\u6a21\u6837 \u6253\u626e\u7684\u4eba\uff0c\u5a01\u98ce\u51db\u51db\uff0c\u7b56\u9a6c\u5c71\u5934\u626c\u97ad\u9065\u6307\uff0c\u8fd9\u756a\u738b\u6b63\u662f\u603b\u63fd\u74e6\u523a\u519b\u653f\u5927\u6743\u7684\u592a\u5e08\u4e5f\u5148\u3002\u90a3\u88ab \u622a\u6210\u65e0\u6570\u5c0f\u80a1\u7684\u660e\u5175\u4e1c\u5954\u897f\u7a9c\uff0c\u74e6\u523a\u58eb\u5175\u56db\u9762\u5835\u622a\uff0c\u6b63\u5728\u6df7\u6218\u4e4b\u4e2d\uff0c\u5ffd\u89c1\u4e1c\u8fb9\u7684\u4e00\u5c0f\u80a1\u660e \u5175\uff0c\u7a81\u7136\u5728\u9635\u4e0a\u5347\u8d77\u4e00\u9762\u9f99\u65d7\uff0c\u74e6\u523a\u5175\u4e2a\u4e2a\u6b22\u547c\uff1a\u201c\u54c8\uff0c\u660e\u671d\u7684\u7687\u5e1d\u5728\u8fd9\u91cc\u4e86\uff01\u201d \u3000\u3000\u5f20\u4e39\u67ab\u6c14\u5f97\u54ac\u7259\u5207\u9f7f\uff0c\u5fc3\u9053\uff1a\u201c\u738b\u632f\u8fd9\u25a1\u771f\u662f\u72fc\u5fc3\u72d7\u80ba\uff0c\u4ed6\u8fd8\u6015\u654c\u4eba\u4e0d\u77e5\u9053\u7687\u4e0a\u7684\u6240\u5728 \u5462\u3002\u201d\u8fd9\u9f99\u65d7\u6b63\u662f\u738b\u632f\u5347\u8d77\uff0c\u6709\u610f\u62a5\u7ed9\u654c\u4eba\u77e5\u9053\u7684\u3002 \u3000\u3000\u660e\u671d\u7684\u7687\u5e1d\u7948\u9547\u88ab\u56f0\u5728\u571f\u6728\u5821\u4e00\u4e2a\u663c\u591c\uff0c\u773c\u89c1\u5927\u519b\u5d29\u6e83\uff0c\u4e00\u8d25\u6d82\u5730\uff0c\u4e0d\u53ef\u6536\u62fe\u3002\u6b63\u4e0e\u5f20 \u98ce\u5e9c\u5546\u8bae\uff0c\u60f3\u6cd5\u7a81\u56f4\uff0c\u5ffd\u89c1\u738b\u632f\u9762\u8272\u82cd\u51c9\uff0c\u8fdb\u6765\u62a5\u9053\uff1a\u201c\u7687\u4e0a\uff0c\u5927\u4e8b\u4e0d\u597d\uff0c\u654c\u519b\u7684\u94c1\u7532\u5175\u5df2 \u51b2\u81f3\u5e10\u524d\uff0c\u5feb\u53eb\u5f20\u7edf\u9886\u53bb\u62b5\u5fa1\u4e00\u9635\u3002\u201d\u5f20\u98ce\u5e9c\u9053\uff1a\u201c\u7687\u4e0a\u4f11\u60ca\uff0c\u6211\u4eca\u65e5\u62fc\u4e86\u6027\u547d\uff0c\u4e5f\u8981\u66ff\u7687 \u4e0a\u51b2\u5f00\u4e00\u6761\u8840\u8def\u3002\u201d\u5f20\u98ce\u5e9c\u5306\u5306\u51fa\u5e10\uff0c\u738b\u632f\u5ffd\u7136\u5978\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4e3b\u4e0a\uff0c\u4eca\u65e5\u4e4b\u4e8b\uff0c\u9664\u4e86\u59d4 \u5c48\u6295\u964d\uff0c\u522b\u65e0\u751f\u8def\uff0c\u8bf7\u4e3b\u4e0a\u5230\u74e6\u523a\u519b\u4e2d\u8bb2\u548c\u3002\u201d\u7948\u9547\u5927\u5403\u4e00\u60ca\u9053\uff1a\u201c\u7231\u537f\u600e\u51fa\u6b64\u8a00\uff1f\u201d\u738b\u632f \u677f\u8d77\u9762\u5b54\u559d\u9053\uff1a\u201c\u6b66\u58eb\u4f55\u5728\uff1f\u201d\u5e10\u4e2d\u6d8c\u51fa\u738b\u632f\u7684\u5fc3\u8179\u6b66\u58eb\u4e00\u4e0b\u5b50\u5c31\u628a\u7687\u5e1d\u7f1a\u4e86\u3002 \u3000\u3000\u5f20\u98ce\u5e9c\u65b9\u51b2\u51fa\u5e10\u5916\uff0c\u5ffd\u89c1\u9635\u4e0a\u5347\u8d77\u9f99\u65d7\uff0c\u59cb\u77e5\u662f\u738b\u632f\u7684\u5978\u8ba1\uff0c\u6b32\u5f85\u9000\u56de\u5e10\u4e2d\uff0c\u4fdd\u62a4\u7687 \u4e0a\uff0c\u74e6\u523a\u5175\u6765\u5f97\u6781\u4e3a\u8fc5\u75be\uff0c\u7728\u773c\u4e4b\u95f4\uff0c\u5df2\u7ed9\u622a\u65ad\uff0c\u56f0\u5728\u91cd\u56f4\u3002 \u3000\u3000\u4e91\u857e\u70ed\u8840\u6cb8\u817e\uff0c\u9053\uff1a\u201c\u5927\u54e5\uff0c\u54b1\u4eec\u53bb\u6740\u738b\u632f\u6551\u7687\u5e1d\u3002\u201d\u4ed6\u4eec\u8fd9\u4e00\u961f\uff0c\u4e43\u662f\u4e2d\u519b\uff0c\u524d\u9762\u4eba \u5c71\u4eba\u6d77\uff0c\u7eb5\u6709\u5b9d\u9a6c\uff0c\u4e5f\u96be\u51b2\u8fc7\u3002\u5f20\u4e39\u67ab\u82e6\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4eca\u65e5\u4e4b\u4e8b\uff0c\u4e0d\u662f\u786c\u62fc\u53ef\u4e86\u3002\u54b1\u4eec\u4e14 \u4e0a\u9ad8\u5730\u770b\u770b\u3002\u201d \u3000\u3000\u53ea\u89c1\u738b\u632f\u628a\u7687\u5e1d\u7f1a\u5728\u9a6c\u4e0a\uff0c\u4eb2\u81ea\u624b\u62ff\u767d\u65d7\uff0c\u8fce\u98ce\u62db\u5c55\uff0c\u6709\u4e9b\u5fe0\u4e8e\u7687\u4e0a\u7684\u536b\u58eb\u60f3\u6765\u89e3\u6551\uff0c \u5374\u7ed9\u738b\u632f\u7684\u6b66\u58eb\u6321\u4f4f\uff0c\u654c\u4eba\u773c\u770b\u5c31\u8981\u5408\u56f4\u5954\u81f3\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u9739\u96f3\u4e00\u58f0\u5927\u53eb\uff0c\u62a4\u536b\u5c06\u519b\u6a0a\u5fe0\u624b\u821e\u53cc\u9524\uff0c\u594b\u4e0d\u987e\u8eab\u5730\u98de\u9a6c\u51b2\u56de\uff0c\u74e6\u523a\u4e0e\u738b\u632f\u7684\u6b66 \u58eb\u524d\u540e\u5939\u653b\uff0c\u4e00\u9f50\u653e\u7bad\uff0c\u6a0a\u5fe0\u53cc\u9524\u53ea\u62a4\u524d\u5fc3\u3001\u5934\u76d6\u4e24\u5904\u8981\u5bb3\u90e8\u5206\uff0c\u5176\u4ed6\u80a9\u4e0a\u3001\u80cc\u5fc3\u4e2d\u4e86\u5341\u51e0 \u679d\u7bad\uff0c\u5140\u81ea\u4e0d\u5012\uff0c\u65cb\u98ce\u822c\u5730\u76f4\u51b2\u4e86\u5165\u6765\u3002\u738b\u632f\u89c1\u4ed6\u795e\u5a01\u51db\u51db\uff0c\u4e0d\u89c9\u60ca\u53eb\u9053\uff1a\u201c\u6a0a\u5c06\u519b\u6709\u8bdd\u597d \u8bf4\u3002\u201d\u6a0a\u5fe0\u5927\u559d\u4e00\u58f0\uff1a\u201c\u6211\u4eca\u65e5\u8981\u66ff\u5929\u4e0b\u9664\u6b64\u5978\u8d3c\uff01\u201d\u624b\u8d77\u4e00\u9524\uff0c\u628a\u738b\u632f\u6253\u4e8e\u9a6c\u4e0b\uff0c\u8eab\u4e0a\u4e5f \u4e2d\u4e86\u51e0\u5200\u3002\u6a0a\u5fe0\u54c8\u54c8\u5927\u7b11\uff0c\u5012\u8fc7\u9524\u5934\uff0c\u5411\u81ea\u5df1\u5934\u9885\u731b\u7684\u4e00\u9524\uff0c\u5b81\u6b7b\u4e0d\u8fb1\uff0c\u81ea\u6740\u6b7b\u4e86\u3002 \u3000\u3000\u74e6\u523a\u5175\u5982\u6f6e\u6c34\u822c\u4e00\u6d8c\u800c\u81f3\uff0c\u767b\u65f6\u628a\u660e\u671d\u7687\u5e1d\u64d2\u4e86\u3002\u94c1\u8e44\u8df5\u8e0f\uff0c\u4e00\u9635\u51b2\u6740\uff0c\u968f\u7687\u5e1d\u51fa\u5f81\u7684 \u5927\u81e3\u5982\u5c1a\u4e66\u909d\u5c18\u3001\u738b\u4f50\uff0c\u5b66\u58eb\u66f9\u9f10\u3001\u5f20\u76ca\uff0c\u82f1\u56fd\u516c\u5f20\u8f85\u7b49\u5168\u90fd\u5728\u6b64\u5f79\u727a\u7272\uff0c\u738b\u632f\u7684\u6b66\u58eb\u4e5f\u4f24 \u4ea1\u516b\u4e5d\u3002\u6b64\u4e00\u5f79\u4fbf\u662f\u660e\u53f2\u4e0a\u6700\u75db\u5fc3\u7684\u4e00\u5f79\uff0c\u53f2\u79f0\u201c\u571f\u6728\u4e4b\u53d8\u201d\u3002 \u3000\u3000\u5f20\u98ce\u5e9c\u89c1\u7687\u4e0a\u88ab\u64d2\uff0c\u201c\u54c7\u201d\u7684\u4e00\u53e3\u9c9c\u8840\u55b7\u4e86\u51fa\u6765\uff0c\u6025\u6012\u653b\u5fc3\uff0c\u6325\u5200\u529b\u6218\uff0c\u970e\u5ffd\u4e4b\u95f4\uff0c\u8fde \u628a\u5341\u51e0\u540d\u74e6\u523a\u5065\u513f\u5288\u4e8e\u9a6c\u4e0b\u3002\u4f46\u654c\u5175\u8d8a\u6765\u8d8a\u591a\uff0c\u6709\u5982\u94c1\u58c1\u94dc\u5899\uff0c\u54ea\u80fd\u51b2\u5f97\u51fa\u53bb\uff1f\u5f20\u98ce\u5e9c\u5927\u53eb \u4e00\u58f0\uff1a\u201c\u541b\u8fb1\u81e3\u4ea1\uff0c\u4e49\u65e0\u53cd\u987e\uff01\u201d\u6a2a\u5200\u56de\u780d\uff0c\u4fbf\u5f85\u81ea\u520e\uff0c\u5ffd\u5730\u654c\u9635\u4e00\u7bad\u98de\u6765\uff0c\u6b63\u4e2d\u624b\u8155\uff0c\u5b9d \u5200\u843d\u5730\uff0c\u767b\u65f6\u4e5f\u88ab\u654c\u5175\u64d2\u4e86\u3002 \u3000\u3000\u74e6\u523a\u5927\u83b7\u5168\u80dc\uff0c\u9e23\u91d1\u6536\u5175\uff0c\u5c31\u5728\u571f\u6728\u5821\u6e05\u626b\u51fa\u65b9\u5706\u6570\u91cc\u7684\u6218\u573a\uff0c\u5b89\u4e0b\u7bf7\u5e10\uff0c\u6740\u725b\u5bb0\u7f8a\uff0c \u72c2\u6b22\u5e86\u795d\u3002\u5f20\u4e39\u67ab\u4e0e\u4e91\u857e\u4e5f\u6742\u5728\u519b\u58eb\u4e4b\u4e2d\uff0c\u542c\u4ed6\u4eec\u8c08\u8bba\u3002\u53ea\u542c\u5f97\u4e00\u4e2a\u519b\u5b98\u9053\uff1a\u201c\u4eca\u665a\u4e3b\u5e05\u5e10 \u4e2d\u66f4\u6709\u70ed\u95f9\u770b\u5462\uff0c\u53ef\u60dc\u6211\u53ea\u662f\u5343\u592b\u957f\uff0c\u8fd8\u6ca1\u6709\u8d44\u683c\u770b\u8fd9\u573a\u70ed\u95f9\u7684\u620f\u3002\u201d\u53e6\u4e00\u4e2a\u519b\u5b98\u95ee\u9053\uff1a \u201c\u4ec0\u4e48\u70ed\u95f9\u7684\u620f\uff1f\u201d\u5148\u524d\u90a3\u519b\u5b98\u9053\uff1a\u201c\u542c\u8bf4\u4eca\u665a\u54b1\u4eec\u4e3b\u5e05\u8981\u903c\u660e\u671d\u7687\u5e1d\u9752\u8863\u4f8d\u9152\uff0c\u8fd9\u5c82\u4e0d\u5999 \u7edd\uff01\u201d\u53c8\u4e00\u4e2a\u519b\u5b98\u9053\uff1a\u201c\u660e\u671d\u7684\u7687\u5e1d\u88ab\u6211\u4eec\u64d2\u4e86\uff0c\u6211\u770b\u8fd9\u573a\u6218\u4e8b\u4e5f\u5c31\u5feb\u8981\u7ed3\u675f\uff0c\u54b1\u4eec\u90fd\u53ef\u4ee5 \u56de\u5bb6\u8fc7\u5e74\u4e86\u3002\u201d\u4ed6\u7684\u540c\u4f34\u9053\uff1a\u201c\u6211\u4eec\u8fd8\u672a\u8fdb\u5165\u5317\u4eac\uff0c\u4e2d\u534e\u5730\u5e7f\u4eba\u591a\u6740\u4e4b\u4e0d\u5c3d\uff0c\u7109\u80fd\u8fd9\u6837\u8f7b\u6613 \u7ed3\u675f\u3002\u201d\u90a3\u519b\u5b98\u7b11\u9053\uff1a\u201c\u6c49\u4eba\u628a\u5929\u5b50\u6bd4\u505a\u771f\u9f99\uff0c\u4f60\u60f3\uff0c\u7fa4\u9f99\u65e0\u9996\uff0c\u7109\u96be\u4f5c\u6218\uff1f\u8fd9\u7687\u5e1d\u8981\u4fdd\u5168 \u6027\u547d\uff0c\u53ea\u6709\u4e56\u4e56\u5730\u6295\u987a\u54b1\u4eec\uff0c\u53eb\u4ed6\u4e0b\u4e00\u9053\u547d\u4ee4\uff0c\u613f\u4f5c\u6211\u4eec\u7684\u5c5e\u56fd\uff0c\u90a3\u4e48\u5927\u660e\u6c5f\u5c71\uff0c\u5c82\u4e0d\u662f\u553e \u624b\u53ef\u5f97\u3002\u201d\u5f20\u4e39\u67ab\u5fe7\u5fc3\u5982\u711a\uff0c\u60f3\u9053\uff1a\u201c\u82e5\u7136\u5982\u6b64\uff0c\u786e\u662f\u53ef\u8651\u3002\u4f46\u613f\u8fd9\u4f4d\u660e\u671d\u7687\u5e1d\u4e0d\u662f\u8d2a\u751f\u6015 \u6b7b\u4e4b\u4eba\u3002\u201d\u5148\u524d\u90a3\u4f4d\u519b\u5b98\u53c8\u9053\uff1a\u201c\u660e\u671d\u7684\u519b\u961f\u662f\u4e0d\u8db3\u60e7\u4e86\uff0c\u53ea\u662f\u90a3\u96c1\u95e8\u5173\u5916\u7684\u91d1\u5200\u5be8\u4e3b\uff0c\u5c1a \u5728\u5173\u5916\u6d41\u7a9c\uff0c\u5ffd\u805a\u5ffd\u6563\u4e0d\u6613\u6251\u706d\uff0c\u8fd9\u5012\u662f\u4e2a\u5fc3\u8179\u4e4b\u60a3\u3002\u201d\u53e6\u4e00\u4e2a\u519b\u5b98\u7b11\u9053\uff1a\u201c\u4ed6\u7684\u5927\u5be8\u5df2\u7ed9 \u54b1\u4eec\u94f2\u5e73\uff0c\u91d1\u5200\u8001\u8d3c\u7236\u5b50\u867d\u7136\u9003\u8131\uff0c\u4ea6\u4e0d\u8fc7\u662f\u7663\u75a5\u4e4b\u60a3\u800c\u5df2\u3002\u800c\u4e14\u6709\u6fb9\u53f0\u5c06\u519b\u5728\u96c1\u95e8\u5173\u9a7b \u5b88\uff0c\u4ed6\u66f4\u662f\u65e0\u6cd5\u4f5c\u4e71\uff0c\u4f55\u8db3\u60e7\u54c9\u3002\u201d\u5f20\u4e39\u67ab\u4e0e\u4e91\u857e\u542c\u5f97\u5468\u5065\u7236\u5b50\u7684\u5b89\u5168\u6d88\u606f\uff0c\u53c8\u77e5\u9053\u6fb9\u53f0\u706d \u660e\u7684\u4e0b\u843d\uff0c\u5fc3\u4e2d\u7a0d\u7a0d\u5b89\u6170\u3002 \u3000\u3000\u518d\u8bf4\u660e\u671d\u7684\u7687\u5e1d\u7948\u9547\u88ab\u64d2\u4e4b\u540e\uff0c\u4e5f\u5148\u5c06\u4ed6\u56da\u5728\u4e2d\u519b\u5e10\u4e2d\uff0c\u5e10\u5916\u4e09\u91cd\u9632\u536b\uff0c\u5e10\u4e2d\u53e6\u6709\u4e09\u540d \u6b66\u827a\u9ad8\u5f3a\u7684\u6b66\u58eb\uff0c\u6309\u5251\u4e34\u89c6\uff0c\u5176\u4e2d\u4e4b\u4e00\u4fbf\u662f\u4e5f\u5148\u624b\u4e0b\u7684\u864e\u5c06\u989d\u5409\u591a\uff0c\u6b64\u4eba\u4e0d\u4f46\u4ee5\u4e03\u5341\u4e8c\u8def\u98ce \u96f7\u5251\u6cd5\u79f0\u96c4\u6f20\u5916\uff0c\u800c\u4e14\u4eba\u4ea6\u751a\u673a\u8b66\u3002\u7948\u9547\u4ee5\u5927\u660e\u7687\u5e1d\uff0c\u4e00\u65e6\u53d8\u4e3a\u74e6\u523a\u7684\u9636\u4e0b\u4e4b\u56da\uff0c\u5fc3\u4e2d\u53c8\u7f9e \u53c8\u6c14\u53c8\u6094\u53c8\u6124\uff0c\u542c\u8bf4\u5148\u4e5f\u8fd8\u8981\u4ed6\u665a\u4e0a\u9752\u8863\u4f8d\u9152\uff0c\u66f4\u662f\u7f9e\u6124\u5f97\u65e0\u5730\u81ea\u5bb9\uff0c\u5fc3\u4e2d\u4e03\u4e0a\u516b\u843d\uff0c\u60f3\u7740 \u4eca\u665a\u4e4b\u5bb4\u53bb\u5462\u8fd8\u662f\u4e0d\u53bb\uff1f\u82e5\u7136\u53bb\u4e86\uff0c\u90a3\u5c31\u50cf\u5b8b\u671d\u88ab\u91d1\u4eba\u63b3\u53bb\u7684\u5fbd\u3001\u94a6\u4e8c\u5e1d\u4e00\u6837\uff0c\u5c48\u8eab\u4e8b\u80e1\uff0c \u4e0d\u4f46\u6709\u8fb1\u56fd\u4f53\uff0c\u800c\u4e14\u6c38\u4e3a\u540e\u4e16\u6240\u7b11\uff0c\u4f46\u82e5\u7136\u4e0d\u53bb\uff0c\u53c8\u6050\u6709\u6027\u547d\u4e4b\u5fe7\uff0c\u5fc3\u4e2d\u5b9e\u662f\u8e0c\u8e87\u96be\u51b3\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u5e10\u5916\u6709\u4eba\u62a5\u9053\uff1a\u201c\u592a\u5e08\u8bf7\u989d\u5409\u591a\u5c06\u519b\u5230\u4e3b\u5e05\u5e10\u4e2d\u8c08\u8bdd\u3002\u201d\u4e00\u4e2a\u74e6\u523a\u519b\u5b98\u6367\u7740\u4ee4\u7bad \u8d70\u5165\uff0c\u989d\u5409\u591a\u5341\u5206\u7cbe\u7ec6\uff0c\u9a8c\u8fc7\u4ee4\u7bad\uff0c\u679c\u662f\u74e6\u523a\u519b\u4e2d\u6700\u9ad8\u7684\u4ee4\u7bad\uff0d\uff0d\u8fd9\u79cd\u4ee4\u7bad\u662f\u74e6\u523a\u56fd\u541b\u5fa1 \u8d50\uff0c\u7528\u7eff\u7389\u6240\u9020\u7684\u3002\u989d\u5409\u591a\u4ee5\u4e3a\u4e5f\u5148\u6709\u6025\u4e8b\u76f8\u8be2\uff0c\u63a5\u8fc7\u4ee4\u7bad\uff0c\u5306\u5306\u4fbf\u8d70\u3002 \u3000\u3000\u90a3\u4f20\u4ee4\u7684\u519b\u5b98\u89c1\u989d\u5409\u591a\u4e00\u51fa\u5e10\u95e8\uff0c\u5ffd\u5730\u4e00\u4e2a\u8f6c\u8eab\uff0c\u53cc\u81c2\u659c\u4f38\uff0c\u5411\u4e24\u540d\u6b66\u58eb\u7684\u8170\u95f4\u91cd\u91cd\u4e00 \u6233\uff0c\u624b\u6cd5\u8fc5\u75be\u4e4b\u6781\uff0c\u90a3\u4e24\u540d\u6b66\u58eb\u867d\u662f\u74e6\u523a\u56fd\u4e2d\u7684\u9ad8\u624b\uff0c\u84e6\u7136\u53d7\u88ad\uff0c\u6beb\u65e0\u62db\u67b6\u4e4b\u4f59\u5730\uff0c\u54fc\u4e5f\u4e0d \u54fc\u4e00\u58f0\uff0c\u7acb\u523b\u5012\u5730\u3002\u90a3\u519b\u5b98\u5fae\u5fae\u4e00\u7b11\uff0c\u5c06\u5934\u62c9\u4e0b\uff0c\u9053\uff1a\u201c\u7687\u4e0a\uff0c\u4f60\u8fd8\u8ba4\u5f97\u6211\u4e48\uff1f\u201d \u3000\u3000\u8fd9\u4f20\u4ee4\u7684\u519b\u5b98\u6b63\u662f\u5f20\u4e39\u67ab\uff0c\u4ed6\u7236\u4eb2\u5f20\u5b97\u5468\u5728\u74e6\u523a\u5b98\u62dc\u53f3\u4e1e\u76f8\uff0c\u4e0e\u4e5f\u5148\u7684\u7236\u4eb2\u8131\u6b22\u540c\u4e00\u73ed \u8f88\uff0c\u5728\u4e5f\u5148\u672a\u7ee7\u627f\u7236\u4f4d\u3001\u603b\u63fd\u5175\u6743\u4e4b\u524d\uff0c\u5f20\u5b97\u5468\u4e0e\u8131\u6b22\u6743\u529b\u4e0d\u76f8\u4e0a\u4e0b\uff0c\u540c\u53d7\u56fd\u541b\u5ba0\u4fe1\uff0c\u53ef\u4ee5 \u987e\u95ee\u519b\u52a1\uff0c\u74e6\u523a\u5148\u738b\u66fe\u5206\u8d50\u4ed6\u4eec\u7eff\u7389\u4ee4\u7bad\uff0c\u53ef\u4ee5\u547d\u4ee4\u4efb\u4f55\u519b\u5b98\u3002\u5176\u540e\u81f3\u4e5f\u5148\u7ee7\u4f4d\uff0c\u6743\u529b\u65e5 \u5927\uff0c\u81ea\u5c01\u592a\u5e08\uff0c\u5f20\u5b97\u5468\u4e3a\u4e86\u660e\u54f2\u4fdd\u8eab\u5bf9\u74e6\u523a\u7684\u519b\u52a1\u201c\u987e\u201d\u800c\u4e0d\u201c\u95ee\u201d\uff0c\u8fd9\u652f\u4ee4\u7bad\u5df2\u6709\u5341\u5e74\u4e0d \u7528\u4e86\u3002\u5f20\u4e39\u67ab\u5077\u8d70\u4e4b\u65f6\uff0c\u987a\u624b\u5c06\u8fd9\u652f\u4ee4\u7bad\u5077\u8d70\uff0c\u60f3\u4e0d\u5230\u7adf\u5728\u4eca\u65e5\u6d3e\u4e86\u7528\u573a\u3002 \u3000\u3000\u7687\u5e1d\u7948\u9547\u7741\u773c\u4e00\u77a7\uff0c\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\u3002\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u64c2\u53f0\u6bd4\u6b66\u4e4b\u65f6\uff0c\u6211\u9001\u7ed9\u4f60\u7684\u4fe1\uff0c\u4f60 \u770b\u4e86\u4e48\uff1f\u201d\u7687\u5e1d\u98a4\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u5c31\u662f\u5f20\u4e39\u67ab\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u5c31\u662f\u4f60\u6240\u8981\u641c\u6355\u7684\u5927 \u4ec7\u5bb6\u3002\u201d\u7687\u5e1d\u9053\uff1a\u201c\u597d\uff0c\u6211\u4eca\u65e5\u843d\u5728\u4f60\u7684\u624b\u4e2d\uff0c\u4f60\u4e5f\u4e0d\u5fc5\u6211\u8bf4\uff0c\u5feb\u5feb\u5c06\u6211\u4e00\u5200\u6740\u6389\u5c31\u662f\u3002\u201d \u5f20\u4e39\u67ab\u7b11\u9053\uff1a\u201c\u6211\u82e5\u8981\u6740\u4f60\uff0c\u5c82\u5f85\u4eca\u65e5\uff1f\u6211\u867d\u8eab\u7a7f\u80e1\u670d\uff0c\u5fc3\u5728\u6c49\u5bb6\u3002\u201d\u7687\u5e1d\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u5c31 \u6551\u6211\u51fa\u53bb\u3002\u201d\u5916\u9762\u91cd\u91cd\u9632\u536b\uff0c\u8981\u6551\u51fa\u53bb\uff0c\u8c08\u4f55\u5bb9\u6613\u3002\u5f20\u4e39\u67ab\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u7687\u4e0a\uff0c\u4eca\u65e5\u4e4b \u4e8b\uff0c\u53ea\u6709\u4f60\u81ea\u5df1\u53ef\u6551\u81ea\u5df1\u3002\u201d\u7687\u5e1d\u9053\uff1a\u201c\u6b64\u8bdd\u600e\u8bf4\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u4e5f\u5148\u4eca\u665a\u5fc5\u8feb\u4f60\u6295\u964d\uff0c \u4f60\u82e5\u6295\u964d\u4e0d\u4f46\u65ad\u9001\u4e86\u5927\u660e\u7684\u4e5d\u4e07\u91cc\u6c5f\u5c71\uff0c\u4f60\u7684\u6027\u547d\u4e5f\u5c06\u4e0d\u4fdd\u3002\u4f60\u82e5\u4e0d\u964d\u4e8e\u8c26\u5fc5\u7136\u805a\u96c6\u4e49\u5e08\uff0c \u4fdd\u571f\u4f5c\u6218\u3002\u74e6\u523a\u5185\u90e8\u4e0d\u548c\uff0c\u4e5f\u5148\u5c06\u6765\u5fc5\u7136\u5185\u5916\u53d7\u654c\uff0c\u4ed6\u6709\u987e\u5fcc\uff0c\u5c82\u6562\u6740\u4f60\uff1f\u4f60\u5fcd\u53d7\u4e00\u65f6\u4e4b \u82e6\uff0c\u4e0d\u4f46\u53ef\u4ee5\u4fdd\u5168\u6c5f\u5c71\uff0c\u5c06\u6765\u6211\u4eec\u4e5f\u5fc5\u6709\u529e\u6cd5\u6551\u4f60\u3002\u4f60\u5e76\u4e0d\u660f\u5eb8\uff0c\u8fd9\u9053\u7406\u4f60\u53ef\u81ea\u5df1\u60f3\u60f3\u3002\u201d \u7687\u5e1d\u6c89\u541f\u4e0d\u8bed\u3002\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u6211\u7956\u5148\u7684\u5b9d\u85cf\u5730\u56fe\uff0c\u6211\u90fd\u5df2\u53d6\u4e86\uff0c\u65e5\u5185\u5c31\u53ef\u8fd0\u81f3\u4eac\u5e08\uff0c\u6211\u5fc5\u5c3d \u529b\u534f\u52a9\u4e8e\u8c26\uff0c\u56fd\u4e8b\u5c1a\u6709\u53ef\u4e3a\uff0c\u4f60\u53ef\u4ee5\u4e0d\u5fc5\u591a\u8651\u3002\u201d \u3000\u3000\u5f20\u4e39\u67ab\u76ee\u5149\u70af\u70af\uff0c\u81ea\u6709\u4e00\u79cd\u679c\u6562\u51b3\u65ad\u7684\u795e\u60c5\uff0c\u4ee4\u4eba\u4fe1\u670d\uff0c\u7687\u5e1d\u5634\u5507\u5fae\u52a8\uff0c\u4f3c\u6b32\u8bf4\u8bdd\uff0c\u5374 \u53c8\u541e\u4e86\u56de\u53bb\u3002\u5f20\u4e39\u67ab\u53cc\u76ee\u4e00\u7741\uff0c\u9053\uff1a\u201c\u4f60\u7684\u5927\u81e3\u4e91\u9756\u66fe\u5728\u80e1\u8fb9\u7267\u9a6c\u4e8c\u5341\u5e74\uff0c\u59cb\u7ec8\u4e0d\u5c48\uff0c\u4f60\u8eab \u4e3a\u4e00\u56fd\u4e4b\u5c0a\uff0c\u5c82\u53ef\u4e0d\u5982\u81e3\u5b50\uff1f\u201d\u7687\u5e1d\u9053\uff1a\u201c\u597d\uff0c\u6211\u6b64\u8eab\u4e5f\u4e0d\u60f3\u751f\u8fd8\uff0c\u542c\u4f60\u7684\u8bdd\u5c31\u662f\u3002\u201d \u3000\u3000\u5f20\u4e39\u67ab\u5c1a\u5f85\u8bf4\u8bdd\uff0c\u5ffd\u542c\u5f97\u55e4\u7684\u4e00\u58f0\uff0c\u5e10\u7bf7\u6495\u4e3a\u4e24\u7247\uff0c\u53ea\u89c1\u989d\u5409\u591a\u65cb\u98ce\u4e00\u6837\u76f4\u95ef\u8fdb\u6765\uff0c\u671d \u5730\u4e0b\u4e00\u77a5\uff0c\u7acb\u523b\u66b4\u6012\u559d\u9053\uff1a\u201c\u597d\u5927\u80c6\u7684\u8d3c\u5b50\uff0c\u5403\u6211\u4e00\u5251\uff01\u201d\u8fd0\u5251\u5982\u98ce\uff0c\u4e00\u62db\u201c\u8fc5\u96f7\u538b\u9876\u201d\uff0c \u7acb\u523b\u5411\u5f20\u4e39\u67ab\u54bd\u5589\u76f4\u523a\u3002\u5f20\u4e39\u67ab\u867d\u7136\u77e5\u9053\u5047\u4f20\u4ee4\u7bad\uff0c\u53ea\u53ef\u4ee5\u9a97\u8fc7\u4e00\u65f6\uff0c\u5374\u4e5f\u6599\u60f3\u4e0d\u5230\u989d\u5409\u591a \u6765\u5f97\u5982\u6b64\u8fd9\u5feb\uff01 \u3000\u3000\u539f\u6765\u989d\u5409\u591a\u4eba\u751a\u673a\u8b66\uff0c\u63a5\u8fc7\u4ee4\u7bad\u521a\u8d70\u51fa\u5e10\u5916\uff0c\u84e6\u7136\u60f3\u9053\uff1a\u201c\u592a\u5e08\u8981\u6211\u76d1\u89c6\u660e\u671d\u7687\u5e1d\uff0c\u6b64 \u4e8b\u4f55\u7b49\u91cd\u8981\uff0c\u5c82\u6709\u5c06\u6211\u8c03\u5f00\u4e4b\u7406\u5462\uff1f\u800c\u4e14\u6240\u6d3e\u6765\u4f20\u4ee4\u7684\u519b\u5b98\uff0c\u9762\u5b54\u4ea6\u751a\u964c\u751f\uff0c\u82e5\u7136\u771f\u662f\u592a\u5e08 \u4f20\u4ee4\uff0c\u5e94\u8be5\u6d3e\u6211\u6240\u8ba4\u8bc6\u7684\u592a\u5e08\u7684\u5de6\u53f3\u4eb2\u4fe1\u624d\u662f\u3002\u800c\u4e14\u6b64\u4eba\u4f20\u4e86\u4ee4\u7bad\uff0c\u5e76\u4e0d\u968f\u6211\u51fa\u53bb\uff0c\u66f4\u662f\u53ef \u7591\u3002\u201d\u8d8a\u60f3\u8d8a\u89c9\u4e0d\u5999\uff0c\u7acb\u523b\u6298\u56de\uff0c\u6495\u5f00\u5e10\u7bf7\uff0c\u89c1\u81ea\u5df1\u7684\u4e24\u4e2a\u526f\u624b\u90fd\u5df2\u5012\u5728\u5730\u4e0a\uff0c\u5206\u660e\u662f\u7ed9\u654c \u4eba\u7528\u91cd\u624b\u6cd5\u70b9\u4e86\u7a74\u9053\uff0c\u8fd9\u519b\u5b98\u81ea\u662f\u5978\u7ec6\u65e0\u7591\uff0c\u4e0d\u5fc5\u7591\u95ee\u7acb\u523b\u51fa\u62db\u3002 \u3000\u3000\u8fd9\u4e00\u5251\u6765\u5f97\u8fc5\u6377\u4e4b\u6781\uff0c\u5f20\u4e39\u67ab\u6697\u9053\u4e00\u58f0\uff1a\u201c\u597d\u4e2a\u98ce\u96f7\u5251\u6cd5\u679c\u7136\u540d\u4e0d\u865a\u4f20\u3002\u201d\u4e00\u4f4e\u5934\u907f\u4e86 \u5f00\u53bb\uff0c\u5c82\u77e5\u989d\u5409\u591a\u7684\u98ce\u96f7\u5251\u6cd5\uff0c\u771f\u5982\u8fc5\u98ce\u66b4\u96e8\uff0c\u4e00\u62db\u63a5\u7740\u4e00\u62db\uff0c\u51cc\u5389\u4e4b\u6781\uff0c\u5e10\u5185\u65b9\u5706\u4e0d\u8fc7\u4e08 \u8bb8\u4e4b\u5730\uff0c\u5f20\u4e39\u67ab\u5c55\u5f00\u7edd\u9876\u7684\u8f7b\u8eab\u529f\u592b\u5de6\u907f\u53f3\u95ea\uff0c\u4e5f\u89c9\u751a\u96be\u5e94\u4ed8\u3002\u5e10\u5916\u4eba\u58f0\u5608\u6742\uff0c\u989d\u5409\u591a\u7684\u63f4 \u5175\u8f6c\u773c\u5c31\u5230\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c\u989d\u5409\u591a\u4e00\u5251\u5288\u4e2d\u5f20\u4e39\u67ab\u7684\u5934\u76d4\uff0c\u5ffd\u89c9\u5251\u5c16\u4e00\u6ed1\uff0c\u523a\u8fc7\u4e00\u8fb9\u3002\u539f\u6765 \u5f20\u4e39\u67ab\u5728\u5371\u6025\u4e4b\u4e2d\uff0c\u7a81\u51fa\u9669\u62db\uff0c\u6697\u8fd0\u5934\u529f\uff0c\u6545\u610f\u8ba9\u4ed6\u5288\u4e2d\u5934\u76d4\uff0c\u5c06\u5934\u4e00\u6446\uff0c\u501f\u5934\u76d4\u4e00\u6321\u4e4b \u529b\uff0c\u4ee5\u52bf\u5c31\u52bf\uff0c\u51cf\u4e86\u4ed6\u7684\u5251\u5288\u7684\u52b2\u9053\uff0c\u5c06\u4ed6\u7684\u5251\u5f15\u8fc7\u4e00\u8fb9\u3002\u8fd9\u4e00\u62db\u5b9e\u662f\u4f7f\u5f97\u9669\u6781\uff0c\u82e5\u7136\u529b\u5ea6 \u4e0d\u662f\u7528\u5f97\u6070\u5230\u597d\u5904\uff0c\u501f\u529b\u6d88\u52bf\u7684\u529f\u592b\u4e0d\u662f\u8fbe\u5230\u4e0a\u4e58\uff0c\u4ee5\u989d\u5409\u591a\u7684\u529f\u529b\uff0c\u8fd9\u4e00\u5251\u4e0d\u96be\u628a\u5934\u76d4\u5288 \u88c2\uff0c\u5c06\u5f20\u4e39\u67ab\u7684\u8111\u888b\u5272\u5f00\u3002 \u3000\u3000\u989d\u5409\u591a\u6014\u4e86\u4e00\u6014\uff0c\u5f20\u4e39\u67ab\u8eab\u624b\u4f55\u7b49\u5feb\u6377\uff0c\u5c31\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u5df2\u628a\u5e08\u7236\u7684\u767d\u4e91\u5b9d\u5251\u53d6\u5728\u624b \u4e2d\uff0c\u53cd\u624b\u4e00\u524a\uff0c\u53c8\u662f\u201c\u5f53\u201d\u7684\u4e00\u58f0\u989d\u5409\u591a\u7684\u5251\u5c16\u5df2\u65ad\u4e86\u4e00\u622a\u3002\u989d\u5409\u591a\u624b\u4e2d\u7684\u523a\u864e\u9752\u950b\uff0c\u4e5f\u662f \u7cbe\u91d1\u6240\u70bc\uff0c\u950b\u5229\u5f02\u5e38\uff0c\u800c\u4e14\u6bd4\u5e38\u4eba\u6240\u7528\u7684\u5251\u6c89\u91cd\u7684\u591a\uff0c\u60f3\u4e0d\u5230\u4e24\u5251\u4e00\u4ea4\uff0c\u7acb\u88ab\u622a\u65ad\uff0c\u4e0d\u7531\u5f97 \u5927\u5403\u4e00\u60ca\u3002\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u5f20\u4e39\u67ab\u633d\u5251\u821e\u4e86\u4e00\u9053\u5251\u5149\uff0c\u500f\u5730\u98de\u8eab\u4e00\u7a9c\uff0c\u201c\u55e4\u201d\u7684\u4e00\u58f0\uff0c\u523a \u7a7f\u5e10\u7bf7\u5de6\u624b\u4e00\u6495\uff0c\u7adf\u7136\u5c31\u4ece\u5e10\u9876\u7684\u7f3a\u53e3\u4e4b\u5904\u7a9c\u8eab\u98de\u51fa\u3002\u8fd9\u51e0\u4e0b\u529f\u592b\uff1a\u821e\u5251\u3001\u98de\u8eab\u3001\u6495\u5e10\u3001\u7a9c \u8d70\uff0c\u4e00\u6c14\u5475\u6210\uff0c\u5e72\u51c0\u5229\u843d\uff0c\u989d\u5409\u591a\u53c8\u60ca\u53c8\u5947\uff1a\u8fd9\u5978\u7ec6\u5c45\u7136\u6709\u5982\u6b64\u529f\u592b\uff01 \u3000\u3000\u4f46\u989d\u5409\u591a\u4e43\u662f\u74e6\u523a\u56fd\u4e2d\u6709\u6570\u7684\u9ad8\u624b\uff0c\u5c82\u80fd\u793a\u5f31\uff0c\u7acb\u523b\u4e5f\u821e\u8d77\u4e00\u6735\u5251\u82b1\uff0c\u4ece\u5f20\u4e39\u67ab\u6240\u6495\u5f00 \u7684\u7f3a\u53e3\u7a9c\u51fa\u3002\u53ea\u89c1\u5f20\u4e39\u67ab\u5df2\u63a0\u8fc7\u4e86\u7b2c\u4e8c\u9053\u5e10\u7bf7\uff0c\u989d\u5409\u591a\u5927\u559d\u4e00\u58f0\uff1a\u201c\u6349\u8d3c\uff01\u201d\u8ddf\u8e2a\u6025\u8ffd\uff0c\u5ffd \u542c\u5f97\u55e4\u55e4\u58f0\u54cd\uff0c\u5f20\u4e39\u67ab\u53cd\u624b\u4e00\u626c\uff0c\u4e00\u7bf7\u94f6\u5149\uff0c\u6709\u5982\u6025\u96e8\u9aa4\u6d12\uff0c\u98d8\u81f3\u9762\u524d\u3002\u8fd9\u662f\u5f20\u4e39\u67ab\u62ff\u624b\u7684 \u98de\u9488\u6697\u5668\uff0c\u989d\u5409\u591a\u8bc6\u5f97\u5389\u5bb3\uff0c\u957f\u5251\u4e00\u821e\uff0c\u98ce\u96f7\u5251\u6cd5\u4e00\u5c55\uff0c\u6d51\u8eab\u98ce\u96e8\u4e0d\u900f\uff0c\u5f20\u4e39\u67ab\u7684\u4e00\u628a\u98de \u9488\uff0c\u90fd\u88ab\u5251\u5149\u8361\u5f00\uff0c\u4f46\u4ed6\u4e5f\u8d81\u8fd9\u7a7a\u6321\uff0c\u53c8\u98de\u8eab\u63a0\u8fc7\u4e86\u7b2c\u4e09\u9053\u5e10\u7bf7\u3002 \u3000\u3000\u5176\u65f6\u5929\u521a\u5165\u9ed1\uff0c\u74e6\u523a\u519b\u4e2d\u7684\u6b66\u58eb\u7eb7\u7eb7\u8ffd\u51fa\uff0c\u5e10\u4e2d\u8b66\u53f7\u5927\u9e23\u5343\u7bad\u9f50\u53d1\uff0c\u5411\u5e10\u7bf7\u9876\u7684\u5f20\u4e39\u67ab \u9ed1\u5f71\u6512\u5c04\u3002\u5f20\u4e39\u67ab\u4e0d\u6562\u843d\u5730\uff0c\u4e00\u53e3\u6c14\u63a0\u8fc7\u4e86\u5341\u51e0\u9053\u5e10\u7bf7\uff0c\u989d\u5409\u591a\u4e0e\u4ece\u6b66\u58eb\u8854\u5c3e\u6025\u8ffd\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u7684\u8f7b\u529f\u8fdc\u5728\u989d\u5409\u591a\u4e4b\u4e0a\uff0c\u989d\u5409\u591a\u81ea\u662f\u8ffd\u4ed6\u4e0d\u4e0a\uff0c\u4f46\u74e6\u523a\u519b\u4e2d\u7684\u8b66\u53f7\u957f\u9e23\uff0c\u5404\u8425\u6b66 \u58eb\u9f50\u90fd\u51fa\u52a8\uff0c\u5f20\u4e39\u67ab\u8eab\u5f62\u5df2\u73b0\uff0c\u6210\u4e3a\u4f17\u77e2\u4e4b\u5730\uff0c\u6b32\u60f3\u9003\u8131\uff0c\u4ea6\u662f\u5343\u96be\u4e07\u96be\u3002\u5f20\u4e39\u67ab\u6325\u5251\u62d4 \u7bad\uff0c\u5728\u5e10\u7bf7\u4e0a\u4e1c\u5954\u897f\u7a9c\u3002\u4f46\u542c\u5f97\u4e00\u58f0\u58f0\u54cd\u7bad\u63a0\u7a7a\u800c\u8fc7\uff0c\u9510\u58f0\u523a\u8033\uff0c\u4e00\u652f\u4e00\u652f\u5730\u63a5\u7eed\u4f20\u4e0b\u53bb\uff0c \u5f20\u4e39\u67ab\u77e5\u9053\u8fd9\u662f\u74e6\u523a\u519b\u4e2d\u7684\u201c\u98de\u7bad\u4f20\u8b66\u201d\uff0c\u4e0d\u6d88\u591a\u65f6\uff0c\u5168\u519b\u90fd\u5df2\u77e5\u9053\uff0c\u5373\u7b97\u81ea\u5df1\u6709\u5929\u5927\u672c \u9886\uff0c\u74e6\u523a\u519b\u8fde\u8425\u767e\u91cc\uff0c\u7ec8\u662f\u96be\u4ee5\u9003\u8131\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u63a5\u8fde\u98de\u8fc7\u51e0\u5341\u9053\u5e10\u7bf7\uff0c\u5ffd\u89c1\u524d\u9762\u4e00\u7247\u7a7a\u65f7\u4e4b\u5730\uff0c\u5c06\u4e24\u8fb9\u519b\u8425\u9694\u5f00\uff0c\u524d\u9762\u7684\u5e10\u7bf7\u867d \u7136\u4ea6\u662f\u706b\u628a\u901a\u660e\uff0c\u5404\u4e2a\u5e10\u7bf7\u4e4b\u524d\u4ea6\u662f\u9690\u9690\u7ea6\u7ea6\u53ef\u89c1\u5de1\u903b\u7684\u6b66\u58eb\uff0c\u4f46\u8fd0\u5e76\u4e0d\u50cf\u8fd9\u8fb9\u4e00\u6837\uff0c\u5404\u8425 \u6b66\u58eb\u90fd\u6d8c\u51fa\u6765\u8ffd\u8d76\u3002\u5f20\u4e39\u67ab\u5fc3\u4e2d\u5927\u5947\uff0c\u636e\u4ed6\u6240\u77e5\uff0c\u74e6\u523a\u519b\u4ee4\u751a\u4e25\u8b66\u53f7\u4e00\u53d1\uff0c\u5404\u8425\u9f50\u52a8\uff0c\u751a\u4e3a \u5212\u4e00\uff0c\u65ad\u65e0\u8fd9\u4e00\u8fb9\u7d27\u5f20\uff0c\u90a3\u4e00\u8fb9\u5374\u662f\u677e\u61c8\u4e4b\u7406\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u96be\u9053\u662f\u4e24\u4e2a\u7edf\u5e05\u6307\u6325\u7684\u4e0d\u6210\uff1f \u4f46\u5373\u7b97\u662f\u4e24\u4e2a\u7edf\u5e05\uff0c\u5728\u74e6\u523a\u519b\u5236\u4e4b\u4e0b\uff0c\u63aa\u65bd\u4e5f\u4e0d\u5e94\u6709\u6240\u533a\u522b\u3002\u201d \u3000\u3000\u5f20\u4e39\u67ab\u867d\u662f\u7591\u56e2\u6ee1\u8179\uff0c\u4f46\u60c5\u52bf\u7d27\u8feb\uff0c\u4e0d\u9051\u591a\u60f3\uff0c\u7acb\u523b\u8df3\u4e0b\u63a0\u51fa\u6570\u5341\u4e08\u5730\uff0c\u53ea\u89c1\u540e\u9762\u5df2\u6709 \u5feb\u9a6c\u8ffd\u6765\u3002\u8fd9\u7247\u65f7\u5730\u4e0a\u6709\u5341\u51e0\u5806\u8349\u6599\uff0c\u6bcf\u4e00\u5806\u90fd\u50cf\u5ea7\u5c0f\u5c71\uff0c\u4e43\u662f\u74e6\u523a\u5f3a\u8feb\u6c11\u592b\u8fd0\u6765\uff0c\u4f5c\u4e3a\u9972 \u9a6c\u4e4b\u6599\u7528\u7684\uff0c\u5f20\u4e39\u67ab\u8eb2\u5165\u4e00\u4e2a\u8349\u5806\uff0c\u5fc3\u4e2d\u7b97\u8ba1\u5df2\u5b9a\uff0c\u82e5\u7136\u4e0d\u88ab\u4ed6\u4eec\u53d1\u73b0\uff0c\u5f85\u66f4\u6df1\u4eba\u9759\u4e4b\u540e\uff0c \u4fbf\u53ef\u6084\u6084\u6e9c\u8d70\uff1b\u82e5\u7136\u4ed6\u4eec\u5728\u6b64\u641c\u7d22\u5341\u591a\u4e2a\u8349\u5806\uff0c\u52bf\u5fc5\u52a8\u7528\u591a\u4eba\uff0c\u81ea\u5df1\u8eab\u4e0a\u7a7f\u7684\u662f\u74e6\u523a\u519b\u5b98\u7684 \u670d\u9970\u4ea6\u5927\u6709\u673a\u4f1a\uff0c\u53ef\u4ee5\u6df7\u6c34\u6478\u9c7c\uff0c\u53ea\u8981\u6084\u6084\u5730\u4e00\u94bb\u51fa\u6765\uff0c\u6df7\u8fdb\u519b\u58eb\u961f\u91cc\uff0c\u90a3\u4e48\u6700\u4e0d\u6d4e\u4e5f\u53ef\u6df7 \u8fc7\u4e00\u65f6\uff0c\u5f90\u56fe\u540e\u8ba1\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u5728\u8349\u5806\u4e2d\u521a\u4e00\u4f0f\u4e0b\uff0c\u5ffd\u542c\u5f97\u5657\u54e7\u4e00\u7b11\uff0c\u6709\u7269\u5982\u94c1\uff0c\u51b7\u51b0\u51b0\u7684\u89e6\u5934\u81ea\u5df1\u7684\u80cc\u5fc3\uff0c\u4e00 \u4e2a\u6781\u5176\u5a07\u5a9a\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u6211\u5df2\u7b49\u4f60\u591a\u65f6\u5566\uff0c\u4f60\u4e0d\u8981\u4e71\u52a8\uff0c\u4f60\u4e00\u52a8\u6211\u5c31\u8981\u5927\u53eb\u5927\u56b7\u5566\u3002\u201d\u5f20 \u4e39\u67ab\u60ca\u9a87\u4e4b\u6781\uff0c\u6218\u573a\u4e4b\u4e2d\u54ea\u91cc\u6765\u7684\u5973\u5b50\uff1f\u542c\u5979\u8bed\u6c14\uff0c\u53c8\u7adf\u4f3c\u6beb\u65e0\u6076\u610f\uff0c\u4fbf\u9053\uff1a\u201c\u597d\uff0c\u6211\u4e0d\u4e71 \u52a8\u4fbf\u662f\u3002\u201d\u90a3\u5973\u5b50\u53c8\u662f\u5657\u54e7\u4e00\u7b11\uff0c\u63b7\u4e0b\u4e00\u4ef6\u8863\u88f3\uff0c\u9053\uff1a\u201c\u4f60\u5feb\u8131\u4e0b\u519b\u670d\uff0c\u6362\u4e0a\u8fd9\u4ef6\u8863\u88f3\u3002\u7b49 \u4e00\u56de\u6211\u518d\u6765\u89c1\u4f60\u3002\u201d\u8bf4\u7f62\u4fbf\u94bb\u51fa\u8349\u5806\uff0c\u968f\u5373\u542c\u5f97\u4eba\u58f0\u5608\u6742\uff0c\u9a6c\u8e44\u5f97\u5f97\u4e4b\u58f0\uff0c\u4ece\u65f7\u5730\u4e0a\u9a70\u8fc7\uff0c \u6709\u4eba\u95ee\u9053\uff1a\u201c\u683c\u683c\u53ef\u89c1\u5230\u4e00\u4e2a\u519b\u5b98\u4ece\u8fd9\u91cc\u9003\u8d70\u5417\uff1f\u201d\u90a3\u5c11\u5973\u9053\uff1a\u201c\u89c1\u5440\uff0c\u4ed6\u9003\u5f97\u975e\u5e38\u4e4b\u5feb\uff0c \u6211\u8ffd\u8d76\u4e0d\u4e0a\uff0c\u558f\uff0c\u4ed6\u5c31\u662f\u4ece\u8fd9\u4e2a\u65b9\u5411\u9003\u8dd1\u7684\uff0c\u60f3\u6765\u6b64\u523b\u5df2\u63a0\u8fc7\u4e86\u6211\u4eec\u7684\u5973\u8425\uff0c\u5230\u524d\u9762\u53bb \u4e86\u3002\u201d\u90a3\u4e9b\u4eba\u8f70\u7136\u547c\u558a\uff0c\u7eb7\u7eb7\u8ffd\u8d76\uff0c\u970e\u5ffd\u4e4b\u95f4\uff0c\u8d70\u5f97\u5e72\u5e72\u51c0\u51c0\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u501f\u7740\u5175\u8425\u4e2d\u900f\u8fc7\u7684\u706b\u5149\uff0c\u4ed4\u7ec6\u4e00\u77a7\uff0c\u8fd9\u4ef6\u8863\u88f3\u7adf\u7136\u662f\u8499\u53e4\u5973\u9a91\u58eb\u60ef\u7a7f\u7684\u670d\u9970\uff0c\u8499 \u53e4\u4eba\u548c\u6ee1\u5dde\u4eba\u60ef\u79f0\u7687\u5ba4\u7684\u5973\u513f\u4e3a\u201c\u683c\u683c\u201d\uff0c\u4e0d\u7981\u53c8\u60ca\u53c8\u7591\uff0c\u4e3a\u4e86\u8131\u9669\uff0c\u59d1\u4e14\u5c06\u8863\u88f3\u7a7f\u4e0a\uff0c\u7537 \u626e\u5973\u88c5\uff0c\u53d8\u6210\u4e86\u4e00\u4e2a\u8499\u53e4\u7684\u5973\u9a91\u58eb\u3002\u8fc7\u4e86\u4e00\u4f1a\uff0c\u53ea\u542c\u5f97\u90a3\u5c11\u5973\u53eb\u9053\uff1a\u201c\u6362\u597d\u4e86\u5417\uff1f\u73b0\u5728\u53ef\u4ee5 \u51fa\u6765\u5566\u3002\u201d \u3000\u3000\u5f20\u4e39\u67ab\u5c06\u6362\u4e0b\u7684\u8863\u670d\u5377\u6210\u4e00\u5305\uff0c\u94bb\u51fa\u8349\u5806\uff0c\u53ea\u542c\u90a3\u5c11\u5973\u5657\u5657\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8ddf\u6211\u6765\u5427\uff01\u201d \u5f20\u4e39\u67ab\u53ea\u89c9\u5f97\u8fd9\u5c11\u5973\u8eab\u5f62\u597d\u719f\uff0c\u4f3c\u662f\u5728\u54ea\u513f\u89c1\u8fc7\u4e00\u822c\uff0c\u4e00\u65f6\u95f4\u5374\u60f3\u4e0d\u8d77\u6765\u3002 \u3000\u3000\u90a3\u5c11\u5973\u5728\u524d\u5f15\u8def\uff0c\u8d70\u5165\u5e10\u7bf7\uff0c\u5e10\u4e2d\u7adf\u7136\u5c3d\u662f\u5973\u5175\uff0c\u5f20\u4e39\u67ab\u8fd9\u624d\u604d\u7136\u5927\u609f\uff0c\u539f\u6765\u8fd9\u662f\u5973 \u8425\uff0c\u5973\u5175\u4eec\u6015\u4e0e\u7537\u5175\u6df7\u6742\uff0c\u6240\u4ee5\u9002\u624d\u53ea\u662f\u5b88\u7740\u8425\u5e10\uff0c\u5e76\u4e0d\u51fa\u6765\u641c\u6355\u3002\u5b88\u536b\u7684\u5973\u5175\u6ce8\u76ee\u51dd\u89c6\uff0c \u76ee\u5149\u5728\u5f20\u4e39\u67ab\u8eab\u4e0a\u8f6c\u6765\u8f6c\u53bb\uff0c\u4f3c\u662f\u9887\u4e3a\u8be7\u5f02\uff0c\u5f20\u4e39\u67ab\u4efb\u662f\u5982\u4f55\u6d12\u8131\u4e5f\u88ab\u5979\u4eec\u770b\u5f97\u4e0d\u597d\u610f\u601d\uff0c \u4e0d\u89c9\u4f4e\u4e0b\u5934\u6765\u3002\u53ea\u542c\u5979\u4eec\u95ee\u9053\uff1a\u201c\u683c\u683c\u56de\u6765\u4e86\u5417\uff1f\u5916\u9762\u51fa\u4e86\u4ec0\u4e48\u4e8b\u60c5\uff1f\u201d\u90a3\u5c11\u5973\u9053\uff1a\u201c\u542c\u8bf4 \u662f\u6349\u4e2a\u98de\u8d3c\uff0c\u4f60\u4eec\u4e0d\u5fc5\u591a\u7ba1\u3002\u201d\u90a3\u4e9b\u5973\u5175\u4eec\u53c8\u76ef\u4e86\u5f20\u4e39\u67ab\u4e00\u773c\uff0c\u5374\u662f\u4e0d\u6562\u591a\u95ee\u3002 \u3000\u3000\u90a3\u5c11\u5973\u5c06\u5f20\u4e39\u67ab\u5f15\u5165\u4e00\u5ea7\u5e10\u7bf7\uff0c\u63ed\u4e00\u5e10\u5e18\uff0c\u4f46\u95fb\u5f97\u7f15\u7f15\u5e7d\u9999\uff0c\u6c81\u4eba\u5fc3\u813e\u3002\u5f20\u4e39\u67ab\u628a\u773c\u770b \u65f6\uff0c\u4f46\u89c1\u5e10\u4e2d\u71c3\u7740\u4e00\u7089\u6a80\u9999\uff0c\u6446\u8bbe\u6709\u5927\u7406\u77f3\u56fe\u6848\u7684\u78a7\u7389\u5c0f\u51e0\uff0c\u5c0f\u51e0\u4e0a\u8fd8\u6709\u51e0\u675f\u6885\u82b1\uff0c\u74f6\u4e2d\u53f6 \u8273\u867d\u662f\u5728\u519b\u8425\u4e4b\u5185\uff0c\u5374\u5e03\u7f6e\u5f97\u6709\u5982\u95fa\u623f\uff0c\u5bcc\u8d35\u4e4b\u4e2d\u53c8\u5e26\u6709\u96c5\u6de1\u7684\u6c14\u6c1b\uff0c\u786e\u662f\u4e0d\u4fd7\u3002\u90a3\u5c11\u5973\u8131 \u4e0b\u5934\u5dfe\uff0c\u56de\u7738\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e39\u67ab\uff0c\u4f60\u8fd8\u8ba4\u5f97\u6211\u4e48\uff1f\u201d \u3000\u3000\u5706\u6848\u4e0a\u7ea2\u70db\u9ad8\u70e7\uff0c\u70db\u5149\u63a9\u6620\u4e4b\u4e0b\uff0c\u53ea\u89c1\u90a3\u5c11\u5973\u5bb9\u5149\u7115\u53d1\uff0c\u7b11\u76c8\u76c8\u5730\u770b\u7740\u81ea\u5df1\uff0c\u5f20\u4e39\u67ab\u6014 \u4e86\u4e00\u6014\uff0c\u731b\u7136\u7701\u8d77\uff0c\u9053\uff1a\u201c\u4f60\u662f\u8131\u4e0d\u82b1\u3002\u201d\u90a3\u5c11\u5973\u70b9\u5934\u7b11\u9053\uff1a\u201c\u6b63\u662f\u3002\u4e00\u522b\u591a\u5e74\uff0c\u4f60\u8fd8\u6ca1\u6709 \u5fd8\u8bb0\u6211\u554a\uff01\u201d\u5f20\u4e39\u67ab\u5fc3\u4e2d\u6697\u6697\u53eb\u82e6\uff0c\u539f\u6765\u8fd9\u8131\u4e0d\u82b1\u6b63\u662f\u74e6\u523a\u519b\u7edf\u5e05\u4e5f\u5148\u7684\u5973\u513f\uff0c\u4ed6\u4eec\u5728\u5c0f\u65f6 \u5019\u66fe\u4e00\u8d77\u6e38\u73a9\uff0c\u5230\u4e86\u5341\u4e09\u56db\u5c81\u4e4b\u540e\u4e00\u6765\u56e0\u5f20\u5b97\u5468\u4e0e\u4e5f\u5148\u9762\u548c\u5fc3\u4e0d\u548c\uff0c\u4e8c\u6765\u56e0\u513f\u5973\u5df2\u61c2\u4eba\u4e8b\uff0c \u8fd9\u624d\u5206\u9694\u5f00\u6765\u3002 \u3000\u3000\u53ea\u542c\u5f97\u90a3\u5c11\u5973\u683c\u683c\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8bb0\u5f97\u5c0f\u65f6\uff0c\u6709\u4e00\u5929\u6211\u548c\u4f60\u53bb\u6253\u730e\uff0c\u5728\u9e1f\u6602\u5c71\u4e0b\u7684\u7389\u955c\u6cc9 \u8fb9\u4e34\u6d41\u7167\u5f71\uff0c\u4f60\u8bf4\u6211\u50cf\u7537\u5b69\u5b50\uff0c\u6211\u5374\u8bf4\u4f60\u50cf\u4e2a\u5973\u5b69\u5b50\uff0c\u4f60\u53ef\u8bb0\u5f97\uff1f\u201d\u5f20\u4e39\u67ab\u542b\u7cca\u5e94\u4e86\u4e00\u58f0\uff0c \u90a3\u5c11\u5973\u7a81\u7136\u628a\u5f20\u4e39\u67ab\u4e00\u62c9\uff0c\u62c9\u5230\u4e00\u9762\u955c\u524d\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u4eca\u5929\u7a7f\u4e86\u6211\u7684\u8863\u88f3\uff0c\u66f4\u50cf\u5973\u5b69\u5b50\u4e86\uff0c \u4f60\u81ea\u5df1\u77a7\u77a7\u3002\u201d\u5f20\u4e39\u67ab\u9762\u4e0a\u4e00\u7ea2\uff0c\u5fc3\u9053\uff1a\u201c\u4e91\u857e\u6613\u9497\u800c\u5f01\uff0c\u6211\u5374\u6613\u5f01\u800c\u9497\uff0c\u82e5\u53eb\u5979\u77e5\u9053\uff0c\u5c82 \u4e0d\u88ab\u5979\u53d6\u7b11\u3002\u201d \u3000\u3000\u8131\u4e0d\u82b1\u7b11\u4e86\u4e00\u7b11\uff0c\u53c8\u9053\uff1a\u201c\u6211\u4eec\u51fa\u5f81\u524d\u5915\uff0c\u542c\u8bf4\u4f60\u5077\u5165\u4e2d\u56fd\uff0c\u95ee\u5f20\u4e1e\u76f8\uff0c\u5f20\u4e1e\u76f8\u53c8\u4e0d\u80af \u8bf4\uff0c\u53ea\u9053\u6211\u4eec\u4eca\u751f\u4e0d\u80fd\u518d\u89c1\u4e86\uff0c\u8c01\u77e5\u771f\u4e3b\u4fdd\u4f51\uff0c\u54b1\u4eec\u5374\u5728\u8fd9\u91cc\u76f8\u9022\u3002\u54b1\u4eec\u591a\u5e74\u4e0d\u89c1\uff0c\u4eca\u56de\u4f60 \u53ef\u8981\u5728\u6211\u8fd9\u513f\u591a\u4f4f\u51e0\u5929\u3002\u201d\u5f20\u4e39\u67ab\u60ca\u9053\uff1a\u201c\u8fd9\u5982\u4f55\u4f7f\u5f97\uff1f\u201d\u8131\u4e0d\u82b1\u9053\uff1a\u201c\u8fd9\u6709\u4ec0\u4e48\u4f7f\u4e0d\u5f97\uff1f \u5305\u4fdd\u4f60\u6ca1\u4eba\u77e5\u9053\uff0c\u5c31\u662f\u6709\u4eba\u77e5\u9053\uff0c\u5979\u4eec\u90fd\u662f\u6211\u7684\u5fc3\u8179\uff0c\u4e5f\u4e0d\u6562\u8bf4\u3002\u201d\u5f20\u4e39\u67ab\u8fde\u8fde\u6447\u624b\uff0c\u8131\u4e0d \u82b1\u9762\u8272\u4e00\u7aef\u5ffd\u9053\uff1a\u201c\u4f60\u82e5\u4e0d\u80af\uff0c\u6211\u5c31\u56b7\u51fa\u53bb\u5566\uff01\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u597d\u4f60\u56b7\u5427\uff0c\u5b9e\u5bf9\u4f60\u8bf4\u5427\uff0c\u4eca \u65e5\u6211\u4e43\u662f\u4f60\u7684\u654c\u4eba\uff0c\u4f60\u53ef\u628a\u6211\u7f1a\u4e86\uff0c\u732e\u4e0e\u4f60\u7684\u7236\u4eb2\u3002\u6211\u65e2\u6562\u5230\u4f60\u4eec\u7684\u519b\u8425\uff0c\u672c\u6765\u5c31\u4e0d\u51c6\u5907\u8981 \u8fd9\u6761\u6027\u547d\u3002\u201d\u90a3\u5c11\u5973\u542c\u4e86\uff0c\u5ffd\u7136\u53c8\u662f\u683c\u683c\u4e00\u7b11\uff0c\u5a07\u5a9a\u52a8\u4eba\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u6012\u9053\uff1a\u201c\u4f60\u7b11\u4ec0\u4e48\uff1f\u201d\u8131\u4e0d\u82b1\u9053\uff1a\u201c\u4f60\u8fd8\u662f\u5c0f\u65f6\u5019\u7684\u813e\u6c14\uff0c\u603b\u7231\u548c\u6211\u62ac\u6760\u3002\u4f60\u8bf4 \u4f60\u662f\u6211\u7684\u654c\u4eba\uff0c\u6211\u5374\u4e0d\u5f53\u4f60\u662f\u6211\u654c\u4eba\u5462\u3002\u518d\u8bf4\u4f60\u4e0d\u8981\u6027\u547d\uff0c\u96be\u9053\u4f60\u5c31\u4e0d\u4e3a\u4f60\u7236\u4eb2\u7740\u60f3\u5417\uff1f\u201d \u5f20\u4e39\u67ab\u6697\u6697\u5403\u60ca\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u6211\u7236\u4eb2\u5c1a\u5728\u74e6\u523a\uff0c\u8131\u4e0d\u4e86\u4e5f\u5148\u638c\u63e1\u3002\u800c\u4e14\u5c06\u6765\u5047\u82e5\u6211\u8981\u7b56\u52a8 \u74e6\u523a\u5185\u4e71\uff0c\u90a3\u8fd8\u9700\u8981\u6211\u7236\u4eb2\u76f8\u52a9\uff0c\u6210\u4ec1\u5bb9\u6613\uff0c\u590d\u56fd\u4e8b\u96be\uff0c\u6211\u4e14\u6682\u5fcd\u4e00\u65f6\u4e4b\u8fb1\u3002\u201d\u8131\u4e0d\u82b1\u89c1\u4ed6 \u4f4e\u5934\u4e0d\u8bed\uff0c\u53ea\u9053\u4ed6\u5df2\u5fc3\u5141\uff0c\u53c8\u7b11\u9053\uff1a\u201c\u5176\u5b9e\u4f4f\u5728\u8fd9\u91cc\u6709\u4ec0\u4e48\u4e0d\u597d\uff1f\u6211\u8fd9\u4e2a\u5730\u65b9\uff0c\u4f60\u5728\u74e6\u523a\u519b \u4e2d\u518d\u4e5f\u627e\u4e0d\u5230\u8fd9\u6837\u8212\u670d\u7684\u4f4f\u6240\u3002\u201d\u5f20\u4e39\u67ab\u8df3\u8d77\u6765\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u4f60\u53eb\u6211\u4f4f\u5728\u6b64\u5904\uff1f\u201d\u8131\u4e0d\u82b1 \u9053\uff1a\u201c\u4e0d\u4f4f\u5728\u8fd9\u91cc\u53c8\u4f4f\u5728\u54ea\u91cc\uff1f\u96be\u9053\u4f60\u51fa\u5916\u9762\u53bb\u548c\u5973\u5175\u4eec\u6df7\u5728\u4e00\u8d77\u5417\uff1f\u4f60\u4e0d\u7b11\u8bdd\uff0c\u6211\u4e5f\u6015\u7b11 \u8bdd\u554a\uff01\u201d\u5f20\u4e39\u67ab\u4e00\u60f3\uff0c\u786e\u662f\u4e3a\u96be\uff0c\u60f3\u8d77\u4e91\u857e\uff0c\u5fc3\u4e2d\u6697\u6697\u53eb\u82e6\u3002 \u3000\u3000\u8131\u4e0d\u82b1\u53eb\u5973\u5175\u5f04\u4e00\u6876\u70ed\u6c34\u8fdb\u6765\uff0c\u9053\uff1a\u201c\u4f60\u5728\u5e10\u540e\u6c90\u6d74\uff0c\u628a\u8eab\u4e0a\u7684\u6c61\u6ce5\u8349\u5c51\u90fd\u6d17\u5e72\u51c0\u4e86\uff0c \u514d\u5f97\u88ab\u4eba\u770b\u7834\u3002\u4f60\u4e0d\u5fc5\u7f9e\u7b54\u7b54\u7684\u6ca1\u4eba\u77a7\u4f60\u3002\u201d\u628a\u5e10\u5e54\u62c9\u5f00\uff0c\u63a8\u4ed6\u8fdb\u53bb\uff0c\u53c8\u987a\u624b\u66ff\u4ed6\u5c06\u5e10\u5e54\u62c9 \u4e0a\u906e\u5f97\u5bc6\u4e0d\u900f\u98ce\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u53ef\u653e\u5fc3\u4e86\u5427\uff0c\u7b49\u4f1a\u513f\u51fa\u6765\uff0c\u6211\u8fd8\u6709\u8bdd\u8981\u548c\u4f60\u8bf4\u3002\u201d \u3000\u3000\u5f20\u4e39\u67ab\u5fc3\u4e2d\u6697\u6697\u76d8\u7b97\u8131\u8eab\u4e4b\u8ba1\uff0c\u60f3\u6765\u60f3\u53bb\u5374\u662f\u5b9e\u65e0\u5584\u6cd5\uff0c\u5ffd\u542c\u5f97\u519b\u4e2d\u5201\u6597\u4e4b\u58f0\uff0c\u5916\u9762\u6b63 \u6572\u4e86\u4e8c\u66f4\uff0c\u6709\u4e2a\u5973\u5175\u8fdb\u6765\u62a5\u9053\uff1a\u201c\u683c\u683c\uff0c\u592a\u5e08\u524d\u6765\u770b\u4f60\u3002\u201d\u8131\u4e0d\u82b1\u9053\uff1a\u201c\u8bf7\u4ed6\u8fdb\u6765\u5427\u3002\u201d\u90a3 \u5973\u5175\u521a\u521a\u8de8\u51fa\u5e10\u7bf7\uff0c\u8131\u4e0d\u82b1\u53c8\u662f\u683c\u683c\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u4e0d\u8981\u5f04\u51fa\u58f0\u97f3\uff0c\u6211\u4e0d\u5bf9\u7238\u7238\u8bf4\u4f60\u5c31 \u662f\u3002\u201d \u3000\u3000\u5f20\u4e39\u67ab\u5fc3\u5934\u535c\u535c\u4e71\u8df3\uff0c\u4e00\u4f1a\u513f\u53ea\u542c\u5f97\u4e5f\u5148\u7684\u811a\u6b65\u4e4b\u58f0\u5df2\u7ecf\u8d70\u4e86\u8fdb\u6765\u3002\u8131\u4e0d\u82b1\u95ee\u9053\uff1a\u201c\u7239 \u7239\uff0c\u542c\u8bf4\u4f60\u4eca\u591c\u8981\u660e\u671d\u7684\u7687\u5e1d\u9752\u8863\u4f8d\u9152\uff0c\u600e\u4e48\u4f1a\u6709\u7a7a\u6765\u770b\u6211\uff1f\u55ef\uff0c\u4ec0\u4e48\u4e8b\u60c5\uff1f\u7239\u7239\uff0c\u4f60\u770b\u6765 \u597d\u50cf\u5f88\u4e0d\u9ad8\u5174\uff1f\u201d\u5f20\u4e39\u67ab\u5c4f\u606f\u547c\u5438\uff0c\u53ea\u542c\u5f97\u4e5f\u5148\u8bf4\u9053\uff1a\u201c\u5440\uff0c\u4eca\u665a\u4e4b\u4e8b\u5b9e\u662f\u610f\u6599\u4e0d\u5230\uff01\u201d\u8131 \u4e0d\u82b1\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u4e5f\u5148\u9053\uff1a\u201c\u6211\u4ee5\u4e3a\u660e\u671d\u7687\u5e1d\u4e00\u5b9a\u6015\u6b7b\u8d2a\u751f\uff0c\u53ea\u8981\u4e00\u964d\u987a\uff0c\u54b1\u4eec\u5c31\u53ef\u4ee5\u631f \u5929\u5b50\u4ee5\u4ee4\u660e\u81e3\uff0c\u90a3\u65f6\u660e\u671d\u7684\u6c5f\u5c71\uff0c\u54b1\u4eec\u53ef\u4ee5\u553e\u624b\u800c\u5f97\uff0c\u8c01\u77e5\u4ed6\u7adf\u6562\u6297\u547d\uff0c\u5c45\u7136\u4e0d\u6765\u8d74\u5bb4\u3002\u201d \u8131\u4e0d\u82b1\u8be7\u9053\uff1a\u201c\u4ed6\u6709\u8fd9\u6837\u5927\u80c6\uff1f\u201d\u4e5f\u5148\u9053\uff1a\u201c\u662f\u5440\uff0c\u6211\u4e5f\u610f\u6599\u4e0d\u5230\u3002\u201d\u5f20\u4e39\u67ab\u542c\u4e86\u5fc3\u4e2d\u6697\u6697 \u6b22\u559c\uff0c\u60f3\u9053\uff1a\u201c\u7948\u9547\u8fd8\u80fd\u6709\u8fd9\u70b9\u9aa8\u6c14\uff0c\u6bd4\u5b8b\u671d\u7684\u5fbd\u3001\u94a6\u4e8c\u5e1d\u597d\u591a\u4e86\uff0c\u4e5f\u4e0d\u6789\u8d39\u6211\u4e00\u7247\u82e6 \u5fc3\u3002\u201d \u3000\u3000\u53ea\u542c\u5f97\u4e5f\u5148\u8bf4\u9053\uff1a\u201c\u6211\u6740\u4ed6\u4e0d\u96be\uff0c\u4f46\u6740\u4ed6\u4e4b\u540e\uff0c\u53ea\u6015\u66f4\u6fc0\u8d77\u660e\u671d\u7684\u58eb\u6c14\uff0c\u6218\u4e89\u6301\u4e45\uff0c\u54b1 \u4eec\u4e5f\u672a\u5fc5\u6709\u597d\u5904\u3002\u542c\u8bf4\u963f\u523a\u77e5\u9662\uff08\u5373\u4ee5\u524d\u5230\u5317\u4eac\u51fa\u4f7f\u7684\u756a\u738b\uff09\u5728\u56fd\u5185\u6697\u81ea\u62db\u5175\u4e70\u9a6c\uff0c\u4f3c\u4e4e\u60f3 \u8d81\u6211\u51fa\u56fd\u8fdc\u5f81\uff0c\u9634\u8c0b\u593a\u6211\u7684\u6743\u67c4\u5462\uff0c\u6211\u5b9e\u662f\u653e\u5fc3\u4e0d\u4e0b\u3002\u201d\u8131\u4e0d\u82b1\u9053\uff1a\u201c\u7239\u7239\u6b66\u529f\u76d6\u4e16\uff0c\u4f55\u5fc5 \u6101\u70e6\u3002\u518d\u8bf4\u54b1\u4eec\u4eca\u65e5\u5927\u83b7\u5168\u80dc\uff0c\u66f4\u4e0d\u5e94\u8bb2\u4e27\u6c14\u7684\u8bdd\u3002\u201d\u4e5f\u5148\u7b11\u9053\uff1a\u201c\u6211\u513f\u8bf4\u7684\u6b63\u662f\u3002\u6211\u5c31\u8bf4 \u4ee4\u4f60\u9ad8\u5174\u7684\u8bdd\u3002\u55ef\uff0c\u4f60\u8fd8\u8bb0\u5f97\u5f20\u5b97\u5468\u7684\u513f\u5b50\u5f20\u4e39\u67ab\u5417\uff1f\u201d\u5f20\u4e39\u67ab\u542c\u4e86\uff0c\u4e0d\u7981\u53c8\u662f\u5927\u5403\u4e00\u60ca\u3002 \u3000\u3000\u8131\u4e0d\u82b1\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u4e5f\u5148\u9053\uff1a\u201c\u5f20\u5b97\u5468\u867d\u4e0d\u80af\u8bf4\uff0c\u4f46\u6211\u5df2\u63a2\u51fa\u4ed6\u662f\u5077\u5165\u4e2d\u56fd\u3002\u53ea\u662f\u6b64 \u4e8b\u4ecd\u4ee4\u6211\u601d\u7591\u3002\u201d\u8131\u4e0d\u82b1\u8a00\u9053\uff1a\u201c\u7239\u7239\u4f55\u4e8b\u601d\u7591\uff1f\u201d\u4e5f\u5148\u9053\uff1a\u201c\u5f20\u5bb6\u4e0e\u5927\u660e\u7687\u5e1d\u4e16\u4ee3\u51a4\u4ec7\uff0c \u6309\u8bf4\u5f20\u4e39\u67ab\u65ad\u65e0\u52a9\u654c\u4eba\u4e4b\u7406\u3002\u4f46\u6211\u8d77\u5175\u81f3\u4eca\uff0c\u5df2\u6709\u4e00\u6708\uff0c\u5f20\u4e39\u67ab\u82e5\u5728\u5173\u5185\uff0c\u53c8\u4f55\u4ee5\u4e0d\u5230\u6211\u519b \u4e2d\u62a5\u5230\uff1f\u8fd9\u6b63\u662f\u4ed6\u62a5\u4e16\u4ee3\u51a4\u4ec7\u7684\u5927\u597d\u826f\u673a\u5440\u3002\u201d\u8131\u4e0d\u82b1\u9053\uff1a\u201c\u4e5f\u8bb8\u4ed6\u88ab\u4e24\u519b\u9694\u65ad\uff0c\u672a\u5f97\u5176 \u4fbf\uff0c\u6240\u4ee5\u8fdf\u8fdf\u672a\u6765\u3002\u7239\u7239\u5e73\u5b9a\u4e86\u4e2d\u534e\u4e4b\u540e\uff0c\u4f55\u6101\u627e\u4ed6\u4e0d\u5230\uff1f\u201d\u4e5f\u5148\u7b11\u9053\uff1a\u201c\u90a3\u662f\u5f53\u7136\u3002\u6211\u4eca \u65e5\u9886\u5175\u5165\u5173\uff0c\u8981\u6349\u7684\u5c31\u662f\u8fd9\u4e24\u4e2a\u4eba\u3002\u201d\u8131\u4e0d\u82b1\u9053\uff1a\u201c\u54ea\u4e24\u4e2a\u4eba\uff1f\u201d\u4e5f\u5148\u9053\uff1a\u201c\u7b2c\u4e00\u4e2a\u662f\u660e\u671d \u7687\u5e1d\uff0c\u6349\u5230\u4e86\u4ed6\uff0c\u7eb5\u7136\u4ed6\u4e0d\u6295\u964d\uff0c\u660e\u5175\u4e5f\u6709\u987e\u5fcc\uff0c\u5927\u660e\u6c5f\u5c71\u8fdf\u65e9\u662f\u6211\u7684\u4e86\u3002\u201d\u8131\u4e0d\u82b1\u9053\uff1a \u201c\u7b2c\u4e8c\u4e2a\u5462\uff1f\u201d\u4e5f\u5148\u9053\uff1a\u201c\u7b2c\u4e8c\u4e2a\u4fbf\u662f\u5f20\u4e39\u67ab\u3002\u201d\u8131\u4e0d\u82b1\u9053\uff1a\u201c\u7239\u7239\u6349\u4ed6\u53ef\u662f\u8981\u6cbb\u4ed6\u5077\u5165\u4e2d \u56fd\u4e4b\u7f6a\u4e48\uff1f\u201d\u4e5f\u5148\u9053\uff1a\u201c\u4e5f\u662f\u4e5f\u4e0d\u662f\u3002\u201d\u8131\u4e0d\u82b1\u9053\uff1a\u201c\u6b64\u8bdd\u53c8\u600e\u8bb2\uff1f\u201d\u4e5f\u5148\u9053\uff1a\u201c\u5f20\u4e39\u67ab\u6587 \u6b66\u5168\u624d\uff0c\u53ef\u582a\u5165\u7528\u3002\u6211\u627e\u5230\u4e86\u4ed6\uff0c\u4ed6\u82e5\u4e0d\u80af\u4f9d\u987a\uff0c\u90a3\u6211\u5c31\u8981\u6cbb\u4ed6\u5077\u5165\u4e2d\u56fd\u4e4b\u7f6a\uff0c\u5c06\u4ed6\u6740\u4e86\uff0c \u514d\u4e3a\u540e\u60a3\u3002\u201d\u8131\u4e0d\u82b1\u201c\u554a\u5440\u201d\u4e00\u58f0\u9053\uff1a\u201c\u8fd9\u4e0d\u662f\u592a\u72e0\u4e86\u5417\uff1f\u201d\u4e5f\u5148\u4e00\u7b11\u8bf4\u9053\uff1a\u201c\u4ed6\u4e0e\u660e\u671d\u6709 \u4ec7\uff0c\u5341\u4e5d\u4f1a\u5f52\u987a\u6211\u4eec\u7684\uff0c\u513f\u5440\uff0c\u90a3\u5c31\u662f\u4f60\u7684\u559c\u4e8b\u6765\u4e86\u3002\u201d\u8131\u4e0d\u82b1\u6545\u4f5c\u7f9e\u6001\uff0c\u9762\u4e0a\u4e00\u7ea2\uff0c\u9053\uff1a \u201c\u7239\u7239\u4f60\u53c8\u5c06\u6211\u53d6\u7b11\u4e86\u3002\u201d\u4e5f\u5148\u5927\u7b11\u8bf4\u9053\uff1a\u201c\u4f60\u7239\u7239\u4e0d\u662f\u50bb\u5b50\uff0c\u65e9\u770b\u51fa\u4f60\u6b22\u559c\u5f20\u4e39\u67ab\u8fd9\u5c0f\u5b50 \u5566\uff0c\u4f60\u4eca\u5e74\u4e8c\u5341\u6709\u4e09\uff0c\u6309\u54b1\u4eec\u74e6\u523a\u7684\u89c4\u77e9\uff0c\u4f60\u65e9\u5c31\u8be5\u66ff\u6211\u62b1\u5b59\u5566\u3002\u591a\u5c11\u738b\u5b59\u516c\u5b50\u6c42\u4f60\u603b\u662f\u4e0d \u5141\uff0c\u7239\u7239\u4e5f\u4e0d\u5f3a\u4f60\uff0c\u8fd9\u662f\u4e3a\u4f55\uff0c\u5c31\u56e0\u4e3a\u6211\u77e5\u9053\u4f60\u662f\u60f3\u7b49\u90a3\u5f20\u4e39\u67ab\u3002\u597d\uff0c\u6211\u603b\u80fd\u53eb\u4f60\u5982\u613f\u3002\u201d \u8131\u4e0d\u82b1\u5fc3\u82b1\u6012\u653e\uff0c\u5374\u4f4e\u9996\u65e0\u8a00\u3002 \u3000\u3000\u4e5f\u5148\u5ffd\u9053\uff1a\u201c\u53ea\u662f\u4eca\u665a\u8fd9\u4e2a\u98de\u8d3c\uff0c\u80c6\u5927\u5305\u5929\uff0c\u5c45\u7136\u6562\u5077\u5165\u864e\u5e10\uff0c\u56fe\u52ab\u660e\u541b\uff0c\u800c\u4e14\u8fd8\u6709\u7eff \u7389\u7bad\uff0c\u6211\u53ef\u662f\u6709\u70b9\u7591\u5fc3\u3002\u201d\u8131\u4e0d\u82b1\u9053\uff1a\u201c\u7591\u5fc3\u662f\u8c01\uff1f\u201d\u4e5f\u5148\u9053\uff1a\u201c\u6211\u7591\u5fc3\u8fd9\u8d3c\u5c31\u662f\u5f20\u4e39 \u67ab\u3002\u201d\u8131\u4e0d\u82b1\u9053\uff1a\u201c\u7239\u7239\u4e0d\u662f\u8bf4\u8fc7\uff0c\u4ed6\u548c\u660e\u671d\u7687\u5e1d\u662f\u4e16\u4ee3\u51a4\u4ec7\u5417\uff1f\u201d\u4e5f\u5148\u9053\uff1a\u201c\u6240\u4ee5\u6211\u8fd8\u672a \u6562\u65ad\u5b9a\u662f\u4ed6\u3002\u636e\u6211\u6240\u77e5\uff0c\u8fd9\u79cd\u7eff\u7389\u4ee4\u7bad\u5148\u5e1d\u53ea\u8d50\u4e09\u4eba\uff0c\u4e00\u662f\u4f60\u7684\u7239\u7239\uff0c\u4e8c\u662f\u5f20\u5b97\u5468\uff0c\u4e09\u662f\u963f \u523a\u4eb2\u738b\uff0c\u6240\u4ee5\u4eca\u665a\u7684\u98de\u8d3c\uff0c\u82e5\u4e0d\u662f\u5f20\u4e39\u67ab\uff0c\u5c31\u662f\u963f\u523a\u4eb2\u738b\u7684\u4eba\uff0c\u5927\u7ea6\u4ed6\u4e5f\u60f3\u52ab\u6301\u660e\u671d\u7687\u5e1d\uff0c \u597d\u548c\u6211\u4e89\u9738\u3002\u597d\u5728\u8fd9\u4e8b\u60c5\u5e76\u4e0d\u96be\u67e5\uff0c\u5c06\u6765\u6211\u73ed\u5e08\u56de\u56fd\u540e\uff0c\u81ea\u7136\u8981\u5f04\u4e2a\u6c34\u843d\u77f3\u51fa\u3002\u4f46\u82e5\u7136\u662f\u5f20 \u4e39\u67ab\u6240\u4e3a\uff0c\u90a3\u4e48\u6211\u867d\u7136\u7231\u60dc\u4ed6\uff0c\u4e5f\u5b9a\u8981\u5c06\u4ed6\u6740\u6389\u3002\u201d\u8131\u4e0d\u82b1\u542c\u4e86\uff0c\u5fc3\u4e2d\u6697\u53eb\u201c\u597d\u9669\uff01\u201d\u60f3 \u9053\uff1a\u201c\u597d\u5728\u6211\u672a\u628a\u5f20\u4e39\u67ab\u7684\u8e2a\u8ff9\u8bf4\u51fa\u6765\u3002\u201d \u3000\u3000\u4e5f\u5148\u8f6c\u8fc7\u5934\u53bb\u5728\u7389\u51e0\u659f\u4e86\u676f\u8336\uff0c\u77a5\u773c\u4e4b\u95f4\uff0c\u5ffd\u89c1\u5e10\u5e54\u5fae\u52a8\u91cc\u9762\u4f3c\u6709\u58f0\u97f3\uff0c\u4e5f\u5148\u500f\u5730\u7ad9 \u8d77\uff0c\u559d\u9053\uff1a\u201c\u5e10\u5e54\u91cc\u8fd8\u6709\u8c01\u4eba\uff1f\u201d\u8f6c\u8fc7\u5934\u6765\uff0c\u53ea\u89c1\u8131\u4e0d\u82b1\u624b\u6447\u6a80\u6247\uff0c\u7b11\u9053\uff1a\u201c\u54ea\u80fd\u6709\u4eba\uff1f\u7239 \u7239\uff0c\u4f60\u6562\u60c5\u662f\u7ed9\u4eca\u665a\u7684\u98de\u8d3c\u5413\u614c\u4e86\uff0c\u5230\u5904\u7591\u795e\u7591\u9b3c\uff01\u201d\u4e5f\u5148\u9762\u8272\u4e00\u53d8\uff0c\u5ffd\u800c\u54c8\u54c8\u5927\u7b11\u3002 \u3000\u3000\u8131\u4e0d\u82b1\u529b\u6301\u9547\u5b9a\uff0c\u7528\u529b\u6325\u6247\uff0c\u53ea\u542c\u5f97\u4e5f\u5148\u7b11\u9053\uff1a\u201c\u4e2d\u534e\u6c14\u5019\u4e0e\u6211\u4eec\u8499\u53e4\u5927\u4e0d\u76f8\u540c\uff0c\u51c9\u79cb \u4e5d\u6708\uff0c\u54b1\u4eec\u90a3\u91cc\u5df2\u964d\u51b0\u96ea\uff0c\u8fd9\u91cc\u5374\u8fd8\u95f7\u70ed\u3002\u539f\u6765\u662f\u4f60\u7684\u6247\u5b50\u6247\u76f4\u5fae\u98ce\uff0c\u5012\u6559\u6211\u591a\u7591\u4e86\u3002\u201d\u8bf4 \u7f62\u53c8\u662f\u54c8\u54c8\u5927\u7b11\u3002\u4ed6\u53ef\u4e0d\u77e5\uff0c\u8131\u4e0d\u82b1\u4e5f\u662f\u5148\u89c1\u4e86\u5e10\u5e54\u98d8\u52a8\uff0c\u8fd9\u624d\u62a2\u8fc7\u6247\u5b50\u6247\u7684\u3002\u53ea\u56e0\u5979\u624b\u6cd5 \u5feb\u6781\uff0c\u4e5f\u5148\u53c8\u6b63\u597d\u8f6c\u8fc7\u5934\u53bb\u659f\u8336\uff0c\u6240\u4ee5\u6ca1\u6709\u89c9\u5bdf\u51fa\u6765\u3002 \u3000\u3000\u8131\u4e0d\u82b1\u5fc3\u4e2d\u6697\u6697\u57cb\u6028\u5f20\u4e39\u67ab\u5982\u6b64\u4e4b\u4e0d\u5c0f\u5fc3\uff0c\u53ea\u542c\u5f97\u4e5f\u5148\u53c8\u9053\uff1a\u201c\u6211\u800c\u4eca\u5df2\u4f20\u4ee4\u5168\u519b\uff0c\u82e5 \u975e\u6709\u6211\u4eb2\u7b14\u6587\u4e66\uff0c\u52a0\u76d6\u5c06\u519b\u5e05\u5370\u8c01\u4e5f\u4e0d\u8bb8\u63a5\u8fd1\u660e\u671d\u7687\u5e1d\u3002\u6211\u53c8\u628a\u519b\u4e2d\u7684\u5341\u4e8c\u52c7\u58eb\uff0c\u5168\u90fd\u8c03\u5230 \u864e\u5e10\u9632\u536b\uff0c\u4efb\u98de\u8d3c\u6709\u5929\u5927\u672c\u9886\uff0c\u4e5f\u4e0d\u80fd\u518d\u95ef\u8fdb\u6765\u5566\u3002\u53e6\u5916\u8fd8\u6709\u4e2a\u660e\u671d\u7684\u5fa1\u6797\u519b\u7edf\u9886\u5f20\u98ce\u5e9c\uff0c \u6211\u65e9\u5c31\u542c\u6fb9\u53f0\u706d\u660e\u8bf4\u8fc7\u4ed6\u7684\u540d\u5b57\uff0c\u4ece\u6628\u65e5\u4e4b\u4e8b\u770b\u6765\uff0c\u4ed6\u679c\u7136\u662f\u4e2a\u7537\u513f\uff0c\u82e5\u80fd\u5c06\u6b64\u4eba\u964d\u670d\uff0c\u6bd4 \u6211\u5e10\u4e2d\u7684\u5341\u4e8c\u52c7\u58eb\u90fd\u8981\u5f3a\u5f97\u591a\u3002\u597d\u5728\u4ed6\u53d7\u4e86\u7bad\u4f24\uff0c\u4e0d\u987b\u591a\u4eba\u770b\u7ba1\uff0c\u6211\u624d\u80fd\u628a\u4e8c\u5341\u52c7\u58eb\u90fd\u8c03\u4e86 \u8fc7\u6765\u3002\u201d \u3000\u3000\u8131\u4e0d\u82b1\u5bf9\u5f20\u98ce\u5e9c\u6b8a\u65e0\u5174\u8da3\uff0c\u5979\u62c5\u5fc3\u7684\u662f\u548c\u5f20\u4e39\u67ab\u7684\u5a5a\u4e8b\uff0c\u60f3\u8d77\u4e00\u4e8b\u95ee\u9053\uff1a\u201c\u7239\u7239\u548c\u5f20\u5b97 \u5468\u53ef\u548c\u597d\u4e86\uff1f\u201d\u4e5f\u5148\u7b11\u9053\uff1a\u201c\u4e5f\u6ca1\u4ec0\u4e48\u4e0d\u548c\u597d\uff0c\u5c06\u6765\u7ed3\u4e86\u4eb2\u5bb6\uff0c\u90a3\u5c31\u66f4\u597d\u5566\u3002\u201d\u53c8\u7b11\u9053\uff1a \u201c\u6599\u5f20\u5b97\u5468\u4e5f\u8131\u4e0d\u4e86\u6211\u7684\u638c\u63e1\u3002\u4ed6\u4eec\u5f20\u5bb6\u4e16\u4ee3\uff0c\u5e2e\u52a9\u6211\u56fd\u5efa\u7acb\u5178\u7ae0\u6587\u7269\u5236\u5ea6\uff0c\u4e5f\u7b97\u5f97\u5927\u6709\u529f \u52b3\u3002\u53ea\u662f\u4ed6\u4eec\u5984\u60f3\u501f\u6211\u4eec\u74e6\u523a\u7684\u5175\u529b\u590d\u4ed6\u5927\u5468\u7684\u6c5f\u5c71\uff0c\u5374\u54ea\u91cc\u6709\u8fd9\u6837\u4fbf\u5b9c\u4e4b\u4e8b\u3002\u6240\u4ee5\u8fd9\u6b21\u6211 \u8ba9\u4ed6\u5728\u56fd\u4e2d\u7559\u5b88\u3002\u4ed6\u4e5f\u5947\u602a\uff0c\u4ed6\u65e5\u60f3\u591c\u60f3\u65e0\u975e\u60f3\u7b49\u5230\u4eca\u65e5\u8fdb\u5175\u4e4b\u4e8b\uff0c\u800c\u4eca\u54b1\u4eec\u771f\u7684\u8fdb\u5175\u4e86\uff0c \u6211\u53eb\u4ed6\u7559\u5b88\uff0c\u4ed6\u5374\u6beb\u4e0d\u53cd\u5bf9\uff0c\u770b\u6837\u5b50\u8fd8\u662f\u6ee1\u9ad8\u5174\u7684\uff0c\u8fd9\u5012\u6559\u6211\u96be\u4e8e\u731c\u6d4b\u4e86\u3002\u4e0d\u8fc7\uff0c\u4ed6\u4e5f\u786e\u662f \u4e2a\u4eba\u624d\uff0c\u5f85\u6211\u5e73\u5b9a\u4e86\u4e2d\u56fd\u4e4b\u540e\uff0c\u81ea\u7acb\u4e3a\u7687\uff0c\u90a3\u65f6\u6211\u8fd8\u8981\u53eb\u4ed6\u505a\u6211\u7684\u5bb0\u76f8\u5462\u3002\u513f\u554a\uff0c\u6211\u505a\u4e86\u7687 \u5e1d\uff0c\u4f60\u5c31\u662f\u516c\u4e3b\u5566\uff01\u201d \u3000\u3000\u5ffd\u542c\u5f97\u5916\u9762\u5df2\u6253\u4e86\u4e09\u66f4\uff0c\u8131\u4e0d\u82b1\u7b11\u9053\uff1a\u201c\u7239\uff0c\u65f6\u5019\u4e0d\u65e9\uff0c\u4f60\u4e5f\u8be5\u4f11\u606f\u5566\u3002\u4f60\u660e\u65e5\u8fd8\u8981\u884c \u519b\uff0c\u8981\u6253\u4e0b\u5317\u4eac\uff0c\u4f60\u624d\u6709\u7687\u5e1d\u505a\u6211\u4e5f\u624d\u6709\u516c\u4e3b\u505a\u554a\uff01\u201d\u4e5f\u5148\u7b11\u9053\uff1a\u201c\u513f\u8bf4\u7684\u662f\u3002\u201d\u5f53\u5373\u4eb2\u4e86 \u5973\u513f\u4e00\u4e0b\uff0c\u79bb\u5f00\u5973\u8425\u3002 \u3000\u3000\u4e5f\u5148\u4e00\u8d70\uff0c\u8131\u4e0d\u82b1\u677e\u4e86\u53e3\u6c14\uff0c\u53ea\u89c9\u51b7\u6c57\u5df2\u900f\u7f57\u8863\uff0c\u4e00\u9762\u6362\u8863\uff0c\u4e00\u9762\u7b11\u9053\uff1a\u201c\u5f20\u5bb6\u54e5\u54e5\uff0c \u4f60\u77a7\u6211\u7239\u5bf9\u4f60\u591a\u597d\uff0c\u4f60\u53ef\u653e\u5fc3\u5566\u5427\uff01\u201d\u5e10\u5e54\u5185\u6beb\u65e0\u58f0\u606f\u3002\u8131\u4e0d\u82b1\u53c8\u7b11\u9053\uff1a\u201c\u6211\u7239\u5df2\u8d70\u5566\uff0c \u5582\uff0c\u4f60\u5feb\u4e9b\u6d17\u6fa1\u5427\uff0c\u662f\u4e0d\u662f\u6c34\u51c9\u4e86\uff0c\u8981\u4e0d\u8981\u518d\u6362\u4e00\u6876\u70ed\u6c34\u7ed9\u4f60\uff1f\u201d\u5e10\u5e54\u5185\u4ecd\u662f\u6beb\u65e0\u58f0\u606f\uff0c\u8131 \u4e0d\u82b1\u9053\uff1a\u201c\u5582\u5582\uff0c\u4f60\u600e\u4e48\u4e0d\u7406\u6211\uff1f\u201d\u4ecd\u662f\u65e0\u4eba\u56de\u7b54\u3002\u8131\u4e0d\u82b1\u67f3\u7709\u5012\u7ad6\uff0c\u8d70\u8fd1\u4e86\u53bb\uff0c\u4f38\u624b\u4e00 \u89e6\uff0c\u5374\u53c8\u4e0d\u6562\u62c9\u90a3\u5e10\u5e54\uff0c\u53ea\u6015\u5f20\u4e39\u67ab\u5df2\u8131\u4e86\u8863\u670d\uff0c\u8d64\u6761\u6761\u7684\u90a3\u53ef\u4e0d\u597d\u610f\u601d\u3002\u53c8\u53eb\u4e86\u4e24\u58f0\uff0c\u5f20 \u4e39\u67ab\u4ecd\u662f\u4e0d\u5e94\uff0c\u8131\u4e0d\u82b1\u6012\u6c14\u4e0a\u51b2\uff0c\u94f6\u7259\u4e00\u54ac\uff0c\u4e0d\u987e\u4e00\u5207\uff0c\u53cc\u624b\u4e00\u6495\uff0c\u731b\u5730\u628a\u90a3\u5e10\u5e54\u4e00\u4e0b\u62c9 \u5f00\uff01 \u3000\u3000\u8fd9\u4e00\u62c9\u987f\u4f7f\u8131\u4e0d\u82b1\u60ca\u5f97\u5446\u4e86\uff0c\u5e10\u5e54\u4e4b\u5185\uff0c\u7a7a\u7a7a\u5982\u4e5f\uff0c\u54ea\u91cc\u8fd8\u6709\u5f20\u4e39\u67ab\uff1f\u4ed4\u7ec6\u770b\u65f6\uff0c\u53ea\u89c1 \u5e10\u5e54\u540e\u8fb9\uff0c\u5df2\u7ed9\u5229\u5251\u5272\u5f00\uff0c\u5f20\u4e39\u67ab\u60f3\u5fc5\u5c31\u662f\u4ece\u5272\u88c2\u4e4b\u5904\u94bb\u51fa\u53bb\u7684\uff0c\u8131\u4e0d\u82b1\u8fd9\u4e00\u6c14\u975e\u540c\u5c0f\u53ef\uff0c \u5fc3\u9053\uff1a\u201c\u6211\u771f\u662f\u4e00\u65f6\u7cca\u6d82\uff0c\u6094\u4e0d\u8be5\u8ba9\u4ed6\u628a\u5b9d\u5251\u4e5f\u5e26\u8fdb\u53bb\u6d17\u6fa1\u3002\u201d\u518d\u4e00\u770b\u65f6\uff0c\u53ea\u89c1\u5730\u4e0a\u8fd8\u6709\u51e0 \u884c\u5c0f\u5b57\uff0c\u60f3\u662f\u7528\u5229\u5251\u5212\u51fa\u6765\u7684\uff0c\u90a3\u51e0\u884c\u5b57\u662f\uff1a\u201c\u591a\u627f\u76f8\u6551\u4e4b\u6069\uff0c\u5f02\u65e5\u5fc5\u6709\u4ee5\u62a5\uff0c\u65f6\u673a\u7d27\u8feb\u65e0 \u6687\u53d9\u513f\u65f6\u4e4b\u4e8b\uff0c\u4e24\u56fd\u76f8\u4e89\u66f4\u975e\u541b\u5b50\u8bba\u4ea4\u4e4b\u65f6\uff0c\u6211\u53bb\u4e5f\uff01\u5f20\u4e39\u67ab\u3002\u201d \u3000\u3000\u8131\u4e0d\u82b1\u6012\u6c14\u51b2\u51b2\uff0c\u5954\u51fa\u5e10\u7bf7\uff0c\u95ee\u5916\u9762\u5b88\u536b\u7684\u5973\u5175\uff0c\u5f20\u4e39\u67ab\u5df2\u7ecf\u53bb\u4e86\u591a\u65f6\u4e86\u3002\u8131\u4e0d\u82b1\u9053\uff1a \u201c\u4f60\u4e3a\u4f55\u4e0d\u62e6\u4f4f\u4ed6\uff1f\u201d\u90a3\u5973\u5175\u5c1a\u672a\u77e5\u5f20\u4e39\u67ab\u662f\u4e2a\u7537\u5b50\uff0c\u9053\uff1a\u201c\u5979\u662f\u8ddf\u4f60\u8fdb\u6765\u7684\uff0c\u4f60\u5429\u5490\u8fc7\u6211 \u4eec\u4e0d\u51c6\u6211\u4eec\u591a\u8a00\u3002\u5979\u8981\u51fa\u53bb\uff0c\u6211\u4eec\u5c82\u6562\u62e6\u963b\uff1f\u201d\u8131\u4e0d\u82b1\u6c14\u6781\u6012\u6781\u5374\u662f\u4e0d\u6562\u53d1\u4f5c\u3002 \u3000\u3000\u518d\u8bf4\u5f20\u98ce\u5e9c\u88ab\u64d2\u4e4b\u540e\uff0c\u88ab\u56da\u5728\u5de6\u4e2d\u519b\u5e10\uff0c\u5e10\u4e2d\u4e5f\u6709\u4e24\u4e2a\u6b66\u58eb\u5b88\u536b\uff0c\u5f20\u98ce\u5e9c\u5148\u662f\u77e2\u5fd7\u5c3d \u5fe0\uff0c\u767e\u822c\u6c42\u6b7b\uff0c\u4e0d\u80af\u8fdb\u98df\u3002\u74e6\u523a\u6b66\u58eb\u5949\u4e86\u4e5f\u5148\u4e4b\u547d\uff0c\u5374\u786c\u628a\u53c2\u6c64\u704c\u5165\u4ed6\u7684\u53e3\u4e2d\uff0c\u53c8\u66ff\u4ed6\u6577\u4e0a \u4e86\u91d1\u521b\u5723\u836f\u3002\u5f20\u98ce\u5e9c\u6240\u53d7\u7684\u4f24\u672c\u6765\u53ea\u662f\u4f24\u4e86\u5916\u9762\u76ae\u8089\uff0c\u5e76\u4e0d\u4e25\u91cd\uff0c\u5403\u4e86\u53c2\u6c64\uff0c\u6577\u4e86\u4f24\u836f\uff0c\u6b47 \u4e86\u4e00\u4f1a\uff0c\u7cbe\u795e\u6e10\u89c1\u6062\u590d\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u6211\u5c31\u662f\u6b7b\u4e86\uff0c\u4e5f\u8981\u591a\u62fc\u4ed6\u4eec\u51e0\u4e2a\u3002\u201d\u5982\u6b64\u4e00\u60f3\uff0c\u5b89\u7136 \u5403\u996d\u3002\u74e6\u523a\u6b66\u58eb\u53ea\u9053\u4ed6\u56de\u5fc3\u8f6c\u610f\uff0c\u5927\u4e3a\u6b22\u559c\u3002\u5c82\u77e5\u5f20\u98ce\u5e9c\u662f\u8981\u517b\u8db3\u7cbe\u795e\uff0c\u6697\u8fd0\u7384\u529f\uff0c\u6323\u8131\u624b \u9563\u811a\u94d0\uff0c\u7a81\u56f4\u800c\u51fa\uff0c\u518d\u5728\u756a\u8425\u4e4b\u4e2d\uff0c\u5927\u6740\u4e00\u901a\uff01 \u3000\u3000\u4e09\u66f4\u8fc7\u540e\uff0c\u74e6\u523a\u519b\u4e2d\u5bc2\u9759\u65e0\u58f0\uff0c\u9664\u4e86\u5b88\u591c\u8f6e\u503c\u7684\u536b\u58eb\u5916\uff0c\u5175\u58eb\u4eec\u5168\u5df2\u7761\u4e86\uff0c\u5f20\u98ce\u5e9c\u89c1\u65f6 \u673a\u5df2\u5230\uff0c\u6697\u8fd0\u4e00\u53e3\u4e39\u7530\u4e4b\u6c14\uff0c\u53cc\u81c2\u4e00\u632f\uff0c\u4e0d\u6599\u624b\u9563\u811a\u94d0\u5341\u5206\u575a\u56e0\uff0c\u9707\u4e4b\u4e0d\u65ad\uff0c\u53ea\u95f9\u5f97\u5f53\u25a1\u25a1 \u4e00\u7247\u54cd\u58f0\u3002\u90a3\u4e24\u4e2a\u6b66\u58eb\u6115\u7136\u8df3\u8d77\uff0c\u559d\u9053\uff1a\u201c\u4f60\u5e72\u4ec0\u4e48\uff1f\u201d\u5f20\u98ce\u5e9c\u53c8\u662f\u7528\u529b\u4e00\u632f\uff0c\u201c\u8feb\u5361\u201d\u4e00 \u58f0\uff0c\u54d7\u5566\u4e00\u54cd\uff0c\u624b\u9563\u7adf\u7ed9\u4ed6\u9707\u65ad\u4e86\u4e00\u4e2a\u73af\u8282\uff0c\u4e24\u4e2a\u6b66\u58eb\u5927\u60ca\uff0c\u6325\u5200\u6025\u4e0a\uff0c\u5c06\u4ed6\u5236\u6b62\uff0c\u5f20\u98ce\u5e9c \u53cc\u773c\u901a\u7ea2\uff0c\u5927\u559d\u4e00\u58f0\uff1a\u201c\u8fd1\u6211\u8005\u6b7b\uff01\u201d\u548c\u8eab\u4e00\u6251\uff0c\u624b\u9563\u6a2a\u626b\u8fc7\u53bb\uff0c\u7b2c\u4e00\u4e2a\u6b66\u58eb\u89c1\u4ed6\u52bf\u731b\uff0c\u4e0d \u6562\u4e0e\u4ed6\u76f8\u62fc\uff0c\u53c8\u4e0d\u6562\u6740\u6b7b\u4ed6\uff0c\u865a\u6643\u4e00\u5200\uff0c\u5411\u5de6\u4e00\u95ea\uff0c\u60f3\u4ece\u504f\u950b\u8fdb\u88ad\uff0c\u6311\u65ad\u4ed6\u7684\u8db3\u7b4b\uff0c\u5c82\u77e5\u5f20 \u98ce\u5e9c\u65e9\u6599\u5230\u4ed6\u6709\u6b64\u4e00\u62db\uff0c\u8eab\u5b50\u4e00\u5012\u5e26\u7740\u811a\u94d0\uff0c\u7a81\u7136\u5377\u5730\u4e00\u626b\uff0c\u90a3\u6b66\u58eb\u60e8\u53eb\u4e00\u58f0\uff0c\u819d\u76d6\u4ee5\u4e0b\uff0c \u7ed9\u4ed6\u626b\u5f97\u9f50\u6839\u65ad\u4e86\u3002 \u3000\u3000\u53e6\u4e00\u4e2a\u6b66\u58eb\u6b66\u529f\u751a\u9ad8\uff0c\u4eba\u4e5f\u673a\u7075\uff0c\u89c1\u72b6\u4e0d\u597d\uff0c\u8d76\u4e0a\u53bb\u5c31\u662f\u4e00\u5200\u3002\u5f20\u98ce\u5e9c\u5728\u5730\u4e0a\u4e00\u6eda\uff0c\u53cc \u8db3\u6a2a\u626b\uff0c\u90a3\u6b66\u58eb\u4e00\u8df3\u8df3\u5f00\uff0c\u5237\u5237\u5237\u8fde\u5288\u4e09\u5200\uff0c\u5f20\u98ce\u5e9c\u5e26\u7740\u624b\u9563\u811a\u94d0\uff0c\u95ea\u907f\u6781\u96be\uff0c\u90a3\u6b66\u58eb\u5200\u950b \u4e00\u6643\uff0c\u5200\u5c16\u5bf9\u51c6\u4ed6\u80a9\u4e0a\u7684\u7435\u7436\u8f6f\u9aa8\uff0c\u53ea\u8981\u4e00\u5200\u6311\u4e0b\uff0c\u5f20\u98ce\u5e9c\u5c31\u8981\u6210\u4e3a\u5e9f\u4eba\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u53ee\u5f53\u4e00\u58f0\uff0c\u90a3\u6b66\u58eb\u5c16\u5200\u843d\u5730\uff0c\u5f20\u98ce\u5e9c\u5927\u662f\u5947\u602a\uff0c\u6025\u5fd9\u8df3\u8d77\uff0c\u53ea\u89c1\u5e10\u7bf7\u5f00\u5904\uff0c\u4e24\u4e2a \u8499\u9762\u6b66\u58eb\u98ce\u822c\u6251\u4e86\u8fdb\u6765\uff01 \u3000\u3000\u5e10\u4e2d\u7684\u6b66\u58eb\u5927\u559c\u53eb\u9053\uff1a\u201c\u5feb\u6765\u5236\u670d\u8fd9\u4e2a\u6b7b\u56da\uff01\u201d\u8dc3\u8fc7\u4e00\u8fb9\u5f2f\u8170\u62fe\u5200\uff0c\u5c82\u77e5\u4e24\u4e2a\u8499\u9762\u6b66\u58eb \u4e00\u58f0\u4e0d\u54cd\uff0c\u500f\u5730\u53cc\u5251\u9f50\u51fa\u94f6\u5149\u4e00\u7ede\uff0c\u7acb\u523b\u628a\u90a3\u4e2a\u6b66\u58eb\u65a9\u4e3a\u4e24\u622a\uff01 \u3000\u3000\u5f20\u98ce\u5e9c\u5927\u559c\u53eb\u9053\uff1a\u201c\u662f\u4f60\uff1f\u201d\u4e24\u4e2a\u8499\u9762\u6b66\u58eb\u628a\u8499\u9762\u5dfe\u63ed\u4e86\u4e00\u89d2\uff0c\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u662f \u6211\uff01\u201d\u8fd9\u4e24\u4eba\u975e\u4ed6\uff0c\u6b63\u662f\u5f20\u4e39\u67ab\u4e0e\u4e91\u857e\u3002\u539f\u6765\u5f20\u4e39\u67ab\u542c\u5f97\u4e5f\u5148\u8c08\u8bdd\uff0c\u77e5\u9053\u5f20\u98ce\u5e9c\u8fd9\u8fb9\u7684\u5b88\u536b \u8f83\u758f\uff0c\u4e8e\u662f\u65bd\u7528\u5999\u8ba1\uff0c\u5148\u8d70\u51fa\u5973\u8425\uff0c\u518d\u6362\u4e0a\u8499\u53e4\u6b66\u58eb\u7684\u670d\u9970\uff0c\u65bd\u5c55\u7edd\u9876\u8f7b\u529f\uff0c\u6084\u6084\u6e9c\u56de\u8425 \u4e2d\uff0c\u7ea6\u4e86\u4e91\u857e\uff0c\u6b63\u597d\u53ca\u65f6\u8d76\u5230\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u4e0e\u4e91\u857e\u53cc\u5251\u9f50\u65bd\uff0c\u970e\u5ffd\u4e4b\u95f4\uff0c\u5c06\u5f20\u98ce\u5e9c\u7684\u624b\u9563\u811a\u94d0\u5168\u90fd\u524a\u65ad\uff0c\u8fd9\u65f6\u53ea\u542c\u5f97\u5e10\u5916\u4eba \u58f0\u9f0e\u6cb8\uff0c\u770b\u7740\u5c31\u8981\u6251\u8fdb\u5e10\u6765\u3002\u5f20\u98ce\u5e9c\u5927\u7b11\uff1a\u201c\u597d\u5440\uff0c\u4eca\u65e5\u54b1\u6b7b\u5f97\u503c\u4e86\uff01\u6740\u4e00\u4e2a\u591f\u672c\uff0c\u6740\u4e24\u4e2a \u6709\u5229\uff0c\u6211\u4eca\u65e5\u975e\u8d5a\u4e2a\u4e00\u672c\u5341\u5229\u4e0d\u53ef\uff01\u201d\u62a2\u8fc7\u4e00\u67c4\u519b\u5200\uff0c\u5c31\u8981\u51b2\u51fa\u53bb\u4e0e\u74e6\u523a\u6b66\u58eb\u62fc\u547d\uff0c\u5f20\u4e39\u67ab \u5ffd\u7136\u62e2\u6307\u4e00\u62c2\uff0c\u5f20\u98ce\u5e9c\u9a87\u9053\uff1a\u201c\u4f60\u4f60\u2026\u2026\u201d\u521a\u8bf4\u5f97\u4e24\u4e2a\u201c\u4f60\u201d\u5b57\uff0c\u53cc\u773c\u4e00\u9616\uff0c\u7acb\u523b\u6655\u5012\u3002\u4e91 \u857e\u77a7\u4e86\u5f20\u4e39\u67ab\u4e00\u773c\uff0c\u53ea\u542c\u5f97\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u4e0d\u80fd\u8ba9\u4ed6\u62fc\u547d\uff01\u201d\u628a\u5f20\u98ce\u5e9c\u80cc\u8d77\uff0c\u4e0e\u4e91\u857e\u53cc\u80a9\u4e00 \u5e76\uff0c\u53ea\u89c1\u90a3\u5e10\u7bf7\u500f\u5730\u88ab\u4eba\u6311\u5f00\uff0c\u65e0\u6570\u6b66\u58eb\uff0c\u4e00\u9f50\u6251\u8fdb\u3002\u5f20\u4e39\u67ab\u4e00\u5251\u98de\u51fa\uff0c\u5411\u53f3\u624b\u8fb9\u4f38\u5c55\uff0c\u5212 \u4e86\u534a\u4e2a\u5b64\u5f62\uff0c\u4e91\u857e\u4e5f\u4e00\u5251\u98de\u51fa\uff0c\u5411\u5de6\u624b\u8fb9\u4f38\u5c55\uff0c\u5212\u4e86\u534a\u4e2a\u5b64\u5f62\uff0c\u53cc\u5251\u4e00\u5408\uff0c\u5a01\u529b\u65e0\u6bd4\uff0c\u5408\u6210 \u4e86\u4e00\u9053\u5bd2\u5149\u8000\u76ee\u7684\u5149\u5708\u3002\u53ea\u542c\u5f97\u4e00\u7247\u65ad\u91d1\u621b\u7389\u4e4b\u58f0\uff0c\u88ab\u5251\u950b\u89e6\u53ca\u7684\u5175\u5203\u5168\u90fd\u65ad\u4e86\uff0c\u53cc\u5251\u76d8 \u65cb\uff0c\u5de6\u53f3\u98de\u821e\u5b9b\u5982\u94f6\u9f99\u5165\u6d77\uff0c\u5341\u8361\u5341\u51b3\uff0c\u4f17\u6b66\u58eb\u89c1\u6765\u52bf\uff0c\u4e0d\u7531\u81ea\u4e3b\u5730\u5de6\u53f3\u95ea\u907f\uff0c\u5f20\u3001\u4e91\u4e8c\u4eba \u5c31\u4ece\u7f3a\u53e3\u4e4b\u5904\u51b2\u51fa\uff0c\u98de\u8eab\u8df3\u4e0a\u4e34\u8fd1\u7684\u5e10\u7bf7\u3002\u74e6\u523a\u519b\u4e2d\u6700\u5389\u5bb3\u7684\u5341\u4e8c\u52c7\u58eb\u90fd\u8c03\u5230\u4e2d\u519b\u5e10\u4e2d\u5b88\u536b \u660e\u671d\u5929\u5b50\u53bb\u4e86\uff0c\u8fd9\u4e00\u8fb9\u53ea\u6709\u7b2c\u4e8c\u6d41\u7684\u597d\u624b\uff0c\u8f7b\u529f\u8fdc\u5728\u5f20\u3001\u4e91\u4e8c\u4eba\u4e4b\u540e\uff0c\u773c\u7741\u7741\u5730\u770b\u4ed6\u4eec\u63a0\u8fc7 \u5341\u51e0\u9053\u5e10\u7bf7\uff0c\u7adf\u662f\u65e0\u80fd\u9694\u622a\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u5fae\u5fae\u4e00\u7b11\uff0c\u64ae\u5507\u4e00\u5578\uff0c\u53ea\u542c\u5f97\u9a6c\u58f0\u5636\u9e23\u5c31\u5728\u9644\u8fd1\u3002\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u597d\u5566\uff0c\u54b1\u4eec\u8131\u9669 \u5566\uff01\u201d\u8df3\u4e0b\u5e10\u7bf7\uff0c\u5230\u4e86\u4e24\u4e2a\u519b\u8425\u8854\u63a5\u4e4b\u95f4\u7684\u9699\u5730\uff0c\u53ea\u89c1\u90a3\u5339\u201c\u7167\u591c\u72ee\u5b50\u9a6c\u201d\u6447\u9996\u6446\u5c3e\uff0c\u5df2\u5728 \u90a3\u91cc\u7b49\u5019\u4e3b\u4eba\u3002\u5176\u65f6\u5df2\u662f\u5c06\u8fd1\u56db\u66f4\uff0c\u74e6\u523a\u519b\u4e2d\uff0c\u9664\u4e86\u5b88\u591c\u7684\u6b66\u58eb\u4e4b\u5916\uff0c\u58eb\u5175\u90fd\u5df2\u719f\u7761\uff0c\u867d\u7136 \u7ecf\u6b64\u4e00\u95f9\uff0c\u4f46\u56e0\u5f20\u4e39\u67ab\u4ed6\u4eec\u9003\u5f97\u592a\u5feb\uff0c\u4ed6\u4eec\u8fd8\u6765\u4e0d\u53ca\u8ffd\u51fa\uff0c\u5f20\u4e39\u67ab\u5df2\u5e26\u4e86\u5f20\u98ce\u5e9c\u9003\u51fa\u9669\u5883\uff0c \u8de8\u4e0a\u767d\u9a6c\u4e86\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u5c06\u5f20\u98ce\u5e9c\u7f1a\u5728\u9a6c\u8179\u4e4b\u4e0b\uff0c\u7b11\u9053\uff1a\u201c\u8ba9\u4ed6\u597d\u597d\u5730\u7761\u4e00\u5927\u89c9\u3002\u201d\u539f\u6765\u5f20\u4e39\u67ab\u7684\u70b9\u7a74\u624b \u6cd5\uff0c\u751a\u662f\u795e\u5947\uff0c\u6709\u4f24\u4eba\u7684\u4e0e\u4e0d\u4f24\u4eba\u7684\u5206\u522b\uff0c\u4ed6\u70b9\u4e86\u5f20\u98ce\u5e9c\u7684\u660f\u7761\u7a74\uff0c\u53ea\u4ee4\u4ed6\u660f\u660f\u7761\u53bb\uff0c\u6beb\u4e0d \u59a8\u788d\u4ed6\u7684\u547c\u5438\u8840\u6d41\u3002\u5f20\u4e39\u67ab\u4e4b\u6240\u4ee5\u5982\u6b64\uff0c\u4e43\u662f\u56e0\u4e3a\u5f20\u98ce\u5e9c\u7bad\u4f24\u672a\u66fe\u75ca\u6108\uff0c\u4e0d\u5b9c\u62fc\u547d\u4e4b\u6545\u3002\u5f20 \u98ce\u5e9c\u7acb\u4e86\u62fc\u6b7b\u6b89\u541b\u4e4b\u5fd7\uff0c\u82e5\u597d\u8a00\u76f8\u529d\uff0c\u4e5f\u5fc5\u4e0d\u80af\u542c\u4ece\uff0c\u662f\u4ee5\u5f20\u4e39\u67ab\u53ea\u597d\u51fa\u6b64\u4e00\u7740\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u5feb\u4e0a\u6765\u5427\uff01\u201d\u4e91\u857e\u7565\u4e00\u8fdf\u7591\uff0c\u4fbf\u4e5f\u98de\u8eab\u4e0a\u9a6c\uff0c\u4e24\u4eba\u6324\u5728\u9a6c\u4e0a\uff0c\u96be \u514d\u8033\u9b13\u25a1\u78e8\uff0c\u808c\u80a4\u76f8\u63a5\uff0c\u4e91\u857e\u53ea\u89c9\u4e00\u80a1\u6696\u6d41\uff0c\u4f3c\u662f\u4ece\u5f20\u4e39\u67ab\u8eab\u4e0a\uff0c\u4f20\u64ad\u8fc7\u6765\uff0c\u4e0d\u7531\u5f97\u53cc\u988a\u6655 \u7ea2\u5fc3\u795e\u5982\u9189\u3002\u90a3\u767d\u9a6c\u4e00\u58f0\u957f\u5636\uff0c\u9a6e\u7740\u4e09\u4eba\u98de\u8dd1\uff0c\u74e6\u523a\u9a91\u5175\uff0c\u867d\u7136\u95fb\u58f0\u8ffd\u8d76\uff0c\u5374\u662f\u8ffd\u4e4b\u4e0d\u53ca\u3002 \u3000\u3000\u8fd9\u767d\u9a6c\u795e\u9a8f\u4e4b\u6781\uff0c\u4e0d\u6d88\u534a\u4e2a\u65f6\u8fb0\uff0c\u5df2\u8dd1\u51fa\u4e09\u56db\u5341\u91cc\uff0c\u5c06\u571f\u6728\u5821\u7684\u74e6\u523a\u5927\u8425\uff0c\u8fdc\u8fdc\u629b\u5728\u540e \u9762\u3002\u6cbf\u9014\u867d\u5076\u800c\u6709\u74e6\u523a\u5de1\u591c\u7684\u9a91\u5175\uff0c\u95fb\u58f0\u62e6\u622a\uff0c\u5374\u54ea\u80fd\u6321\u5f97\u4f4f\u5f20\u3001\u4e91\u4e8c\u4eba\u53cc\u5251\u5408\u74a7\u7684\u5a01\u529b\uff0c \u53ea\u6789\u9001\u4e86\u6027\u547d\u7f62\u4e86\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u8131\u4e86\u9669\u5883\uff0c\u6c14\u6717\u795e\u6e05\uff0c\u5fc3\u4e2d\u81ea\u662f\u6b22\u559c\u4e4b\u6781\u3002\u90a3\u767d\u9a6c\u8fce\u98ce\u98de\u8dd1\uff0c\u4e91\u857e\u7684\u79c0\u53d1\u4e5f\u8fce\u98ce \u98d8\u62c2\uff0c\u5f20\u4e39\u67ab\u5728\u524d\u9762\uff0c\u65f6\u4e0d\u65f6\u89c9\u5f97\u4e91\u857e\u7684\u79c0\u53d1\u62c2\u7740\u81ea\u5df1\u7684\u9888\u9879\uff0c\u75d2\u75d2\u7684\u597d\u4e0d\u8212\u670d\uff0c\u4e0d\u7531\u5f97 \u201c\u5657\u55e4\u201d\u7b11\u51fa\u58f0\u6765\u3002\u4e91\u857e\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4f60\u53eb\u767d\u9a6c\u6162\u70b9\u8d70\u5427\u3002\u201d \u3000\u3000\u5f20\u4e39\u67ab\u653e\u677e\u9a6c\u25a1\uff0c\u7f13\u7f13\u800c\u884c\uff0c\u5076\u4e00\u62ac\u5934\uff0c\u53ea\u89c1\u7389\u5b87\u65e0\u5c18\uff0c\u87fe\u5bab\u5f71\u6ee1\uff0c\u5929\u8fb9\u660e\u6708\uff0c\u6070\u4f3c\u51b0 \u76d8\u3002\u6708\u5149\u60a0\u60a0\u5730\u6d12\u4e0b\u6765\uff0c\u56db\u91ce\u5982\u8499\u4e0a\u4e00\u5c42\u8584\u96fe\u8f7b\u7ee1\uff0c\u666f\u8272\u6e05\u5e7d\u7f8e\u5999\u3002\u731b\u7136\u7701\u8d77\uff0c\u4eca\u5915\u4f55\u5915\uff0c \u6b63\u662f\u4e2d\u79cb\uff0c\u4e0d\u89c9\u7b11\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u54b1\u4eec\u4eca\u5e74\u8fd9\u4e2a\u4e2d\u79cb\u8282\u53ef\u8fc7\u5f97\u771f\u6709\u610f\u601d\u3002\u201d\u4e91\u857e\u53d6\u7b11\u9053\uff1a \u201c\u662f\u554a\uff0c\u4e2d\u79cb\u8282\u53c8\u540d\u56e2\u5706\u8282\uff0c\u4f60\u548c\u4e5f\u5148\u7684\u5973\u513f\u4eca\u5bb5\u53ef\u6b63\u662f\u4eba\u6708\u540c\u5706\u554a\uff01\u201d\u5f20\u4e39\u67ab\u4fa7\u76ee\u56de\u7768\uff0c \u4f46\u89c9\u4e91\u857e\u7b11\u8bed\u76c8\u76c8\uff0c\u5439\u6c14\u5982\u5170\uff0c\u5fc3\u795e\u4e00\u8361\uff0c\u5ffd\u5730\u7b11\u9053\uff1a\u201c\u6218\u573a\u770b\u660e\u6708\u9a6c\u4e0a\u8d4f\u6e05\u79cb\uff0c\u5c0f\u5144\u5f1f\uff0c \u4f46\u613f\u54b1\u4eec\u5e74\u5e74\u6709\u4eca\u5915\uff0c\u4f60\u8bf4\u5f97\u597d\uff0c\u4eca\u5bb5\u6b63\u662f\u4eba\u6708\u540c\u5706\uff0c\u4e5f\u5148\u7684\u5973\u513f\u53ef\u8981\u7fa1\u715e\u4f60\u5462\uff01\u201d\u5f20\u4e39\u67ab \u7684\u8bf4\u8bdd\u65e2\u542b\u84c4\uff0c\u53c8\u663e\u9732\uff0c\u900f\u9732\u4e86\u7231\u610f\uff0c\u53c8\u53cd\u8fc7\u6765\u53d6\u7b11\u4e91\u857e\u3002\u4e91\u857e\u5927\u7f9e\uff0c\u542b\u55d4\u8bf4\u9053\uff1a\u201c\u5927\u54e5\uff0c \u4f60\u518d\u53d6\u7b11\uff0c\u6211\u5c31\u8df3\u4e0b\u9a6c\u53bb\uff0c\u4e0d\u518d\u548c\u4f60\u540c\u4e58\u4e86\u3002\u201d \u3000\u3000\u5f20\u4e39\u67ab\u7d22\u6027\u5728\u9a6c\u80cc\u4e0a\u56de\u8f6c\u5934\u6765\uff0c\u89c1\u4e91\u857e\u4f3c\u559c\u4f3c\u55d4\uff0c\u4e5f\u4e0d\u89c9\u5fc3\u795e\u5982\u9189\uff0c\u4e00\u970e\u65f6\u95f4\u8bb8\u591a\u5464\u548f \u4e2d\u79cb\u7684\u6e05\u8bcd\u4e3d\u53e5\uff0c\u90fd\u6d8c\u4e0a\u5fc3\u5934\u3002\u4e91\u857e\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4f60\u50bb\u4e86\u4e48\uff1f\u201d\u5f20\u4e39\u67ab\u4e00\u6307\u660e\u6708\uff0c\u66fc\u58f0\u541f \u9053\uff1a\u201c\u4f46\u613f\u4eba\u957f\u4e45\uff0c\u5343\u91cc\u5171\u5a75\u5a1f\u3002\u201d\u8fd9\u662f\u82cf\u4e1c\u5761\u300a\u6c34\u8c03\u6b4c\u5934\u300b\u8bcd\u4e2d\u540d\u53e5\u3002\u4e91\u857e\u63a5\u7740\u541f\u9053\uff1a \u201c\u4eba\u6709\u60b2\u6b22\u79bb\u5408\uff0c\u6708\u6709\u9634\u6674\u5706\u7f3a\uff0c\u6b64\u4e8b\u53e4\u96be\u5168\u3002\u5927\u54e5\uff0c\u4f60\u53ef\u522b\u53ea\u8bb0\u5f97\u6700\u540e\u4e24\u53e5\u800c\u4e0d\u8bb0\u5f97\u8fd9\u51e0 \u53e5\u554a\uff01\u201d\u8bf4\u4e86\u4e4b\u540e\uff0c\u795e\u8272\u9eef\u7136\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u672c\u662f\u501f\u8bcd\u5bc4\u610f\uff1a\u201c\u4f46\u613f\u4eba\u957f\u4e45\uff0c\u5343\u91cc\u5171\u5a75\u5a1f\u3002\u201d\u5e0c\u671b\u80fd\u548c\u4e91\u857e\u767d\u5934\u5055\u8001\uff0c\u957f\u5bf9\u6708 \u534e\u3002\u4e91\u857e\u5fc3\u4e2d\u867d\u7136\u611f\u52a8\uff0c\u5374\u8bb0\u8d77\u4e86\u54e5\u54e5\u7684\u8bdd\uff0c\u6240\u4ee5\u4e5f\u501f\u8bcd\u5bc4\u610f\uff1a\u201c\u4eba\u6709\u60b2\u6b22\u79bb\u5408\uff0c\u6708\u6709\u9634\u6674 \u5706\u7f3a\uff0c\u5317\u4e0b\u53e4\u96be\u5168\u3002\u201d\u6697\u793a\u524d\u9014\u832b\u832b\uff0c\u672a\u53ef\u9884\u6599\uff0c\u53ea\u6050\u826f\u8fb0\u7f8e\u666f\u8d4f\u5fc3\u4e50\u4e8b\uff0c\u81ea\u53e4\u96be\u5168\u3002\u4e91\u857e \u672c\u662f\u591a\u6101\u5584\u611f\u4e4b\u4eba\uff0c\u8bf4\u4e86\u4e4b\u540e\uff0c\u81ea\u5df1\u53c8\u89c9\u96be\u8fc7\uff0c\u60b2\u4ece\u4e2d\u6765\uff0c\u4e0d\u53ef\u65ad\u7edd\u3002 \u3000\u3000\u4e00\u7247\u6d6e\u4e91\uff0c\u5189\u5189\u98d8\u8fc7\uff0c\u5929\u8fb9\u660e\u6708\u6070\u88ab\u4e91\u906e\uff0c\u4e91\u857e\u5f3a\u7b11\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4f60\u770b\uff0c\u4e16\u4e0a\u54ea\u80fd\u6709\u4eba \u957f\u597d\u3001\u6708\u957f\u5706\uff01\u201d\u5f20\u4e39\u67ab\u4e5f\u4e00\u7b11\u8bf4\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u4f60\u53ef\u8bb0\u5f97\u5973\u8bd7\u4eba\u6731\u6dd1\u771f\u7684\u4e00\u9996\u8bd7\uff1f\u201d\u4e91\u857e \u95ee\u9053\uff1a\u201c\u54ea\u4e00\u9996\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u4e5f\u662f\u4e2d\u79cb\u4e4b\u591c\u4f5c\u7684\u3002\u90a3\u4e00\u591c\u6731\u6dd1\u771f\u89c1\u6708\u88ab\u4e91\u906e\uff0c\u611f\u6000\u8eab \u4e16\uff0c\u56e0\u800c\u5199\u4e86\u8fd9\u4e00\u9996\u8bd7\u3002\u201d\u9053\uff1a\u201c\u4e0d\u8bb8\u87fe\u870d\u6b64\u591c\u660e\uff0c\u4eca\u77e5\u5929\u610f\u662f\u65e0\u60c5\uff01\u4f55\u5f53\u62e8\u53bb\u95f2\u4e91\u96fe\uff0c\u653e \u51fa\u5149\u8f89\u4e07\u91cc\u6e05\uff01\u201d\u6731\u6dd1\u771f\u662f\u5b8b\u4ee3\u6700\u7740\u540d\u7684\u4e24\u4f4d\u5973\u8bcd\u4eba\u4e4b\u4e00\uff08\u53e6\u4e00\u4f4d\u662f\u674e\u6e05\u7167\uff09\uff0c\u674e\u6e05\u7167\u5ac1\u5f97 \u4e2a\u597d\u4e08\u592b\uff0c\u5979\u5374\u5ac1\u4e86\u4e2a\u6751\u592b\u4fd7\u5b50\uff0c\u6240\u4ee5\u4e00\u751f\u6291\u90c1\uff0c\u8bd7\u8bcd\u4e2d\u603b\u662f\u5e26\u7740\u6d53\u91cd\u7684\u54c0\u4f24\uff0c\u6240\u4ee5\u5979\u7684\u8bd7 \u8bcd\u96c6\u53eb\u505a\u300a\u65ad\u80a0\u96c6\u300b\u3002 \u3000\u3000\u4e91\u857e\u542c\u5f97\u5f20\u4e39\u67ab\u5531\u51fa\u4e86\u6731\u6dd1\u771f\u8fd9\u4e00\u9996\u8bd7\uff0c\u5fc3\u4e2d\u4e00\u52a8\uff0c\u4e0d\u89c9\u60f3\u9053\uff1a\u201c\u6731\u6dd1\u771f\u9047\u4eba\u4e0d\u6dd1\uff0c\u4ee5 \u81f4\u90c1\u90c1\u7ec8\u751f\uff0c\u96be\u9053\u6211\u4e5f\u8981\u5b66\u5979\u7684\u6837\u5b50\uff1f\u201d\u53ea\u542c\u5f97\u5f20\u4e39\u67ab\u4e00\u7b11\u8bf4\u9053\uff1a\u201c\u6731\u6dd1\u771f\u7684\u8bd7\u8bcd\u6bcf\u591a\u54c0 \u611f\uff0c\u4f46\u8fd9\u4e00\u9996\u5374\u5e76\u4e0d\u5c3d\u54c0\u611f\uff0c\u8fd8\u6709\u5f88\u5927\u80c6\u7684\u5e0c\u671b\uff0c\u5979\u660e\u77e5\u9053\u5929\u610f\u65e0\u60c5\uff0c\u4f46\u5374\u76fc\u671b\u80fd\u62e8\u53bb\u4e91 \u96fe\uff0c\u653e\u51fa\u5149\u8f89\uff01\u6731\u6dd1\u771f\u662f\u4e2a\u5f31\u5973\u5b50\uff0c\u5979\u6ca1\u6709\u529e\u6cd5\u53bb\u62e8\u4e91\u96fe\uff0c\u4f60\u53ef\u4e0d\u662f\u5f31\u5973\u5b50\u554a\uff01\u6731\u6dd1\u771f\u53ea\u80fd \u5e0c\u671b\uff0c\u4f60\u5374\u53ef\u4ee5\u505a\u5230\u3002\u201d \u3000\u3000\u4e91\u857e\u542c\u4e86\u6b64\u8bdd\uff0c\u5fc3\u4e2d\u601d\u6f6e\u8d77\u4f0f\uff0c\u60f3\u9053\uff1a\u201c\u6211\u7684\u54e5\u54e5\u4e0d\u8bb8\u6211\u548c\u4e39\u67ab\u76f8\u597d\uff0c\u5c31\u6b63\u5982\u6731\u6dd1\u771f\u7684 \u8bd7\u6240\u8bf4\u2018\u4e0d\u8bb8\u87fe\u870d\u6b64\u591c\u660e\uff0c\u4eca\u77e5\u5929\u610f\u662f\u65e0\u60c5\u2019\u4e00\u6837\u3002\u4f46\u6211\u54e5\u54e5\u7684\u8bdd\uff0c\u6211\u5c31\u8981\u628a\u4ed6\u5f53\u6210\u2018\u5929 \u610f\u2019\u5417\uff1f\u2018\u4f55\u5f53\u62e8\u53bb\u95f2\u4e91\u96fe\uff0c\u653e\u51fa\u5149\u8f89\u4e07\u91cc\u6e05\uff01\u2019\u4e0d\u8bb8\u6708\u660e\u3001\u906e\u63a9\u6708\u534e\u7684\u4e91\u96fe\uff0c\u539f\u8be5\u62e8\u53bb \u7684\uff01\u4f46\u53c8\u600e\u6837\u624d\u80fd\u62e8\u53bb\u5462\uff1f\u201d\u731b\u4e00\u62ac\u5934\u5ffd\u89c1\u90a3\u7247\u6d6e\u4e91\u5df2\u98d8\u53bb\uff0c\u6708\u4eae\u53c8\u9732\u51fa\u6765\u4e86\uff01 \u3000\u3000\u8fd9\u4e24\u4eba\u5386\u5c3d\u98ce\u6ce2\uff0c\u5c61\u7ecf\u9669\u96be\uff0c\u4eca\u9704\u59cb\u5f97\u540c\u4e58\u767d\u9a6c\uff0c\u5171\u8d4f\u6708\u534e\uff0c\u867d\u7136\u5fc3\u601d\u4e0d\u5c3d\u76f8\u540c\uff0c\u4f46\u90fd \u611f\u5230\u8fd9\u662f\u4eba\u751f\u81f3\u7f8e\u4e4b\u5883\u3002\u4e24\u4eba\u8033\u9b13\u25a1\u78e8\uff0c\u5598\u606f\u76f8\u95fb\uff0c\u808c\u80a4\u76f8\u63a5\uff0c\u770b\u7740\u5929\u8fb9\u660e\u6708\u5347\u8d77\u843d\u4e0b\uff0c\u53ea \u611f\u4e07\u8bed\u5343\u8a00\uff0c\u8bf4\u4e4b\u4e0d\u5c3d\uff0c\u4f46\u5374\u53c8\u4e0d\u5fc5\u591a\u8bf4\uff0c\u5f7c\u6b64\u5fc3\u610f\uff0c\u5c3d\u90fd\u5728\u65e0\u8a00\u4e4b\u4e2d\uff0c\u5fc3\u9886\u795e\u4f1a\u4e86\u3002 \u3000\u3000\u767d\u9a6c\u7f13\u7f13\u524d\u884c\uff0c\u4e0d\u77e5\u4e0d\u89c9\uff0c\u4e1c\u65b9\u5df2\u767d\uff0c\u524d\u9762\u74e6\u523a\u7684\u519b\u8425\uff0c\u9690\u7ea6\u53ef\u89c1\uff0c\u4e5f\u5148\u7684\u4e3b\u529b\u5728\u571f\u6728 \u5821\uff0c\u5148\u950b\u5219\u5df2\u8feb\u8fd1\u5317\u4eac\uff0c\u6240\u4ee5\u6cbf\u9014\u4e8c\u767e\u4f59\u91cc\uff0c\u6bcf\u9694\u5341\u91cc\u516b\u91cc\u4e4b\u5730\uff0c\u5c31\u6709\u74e6\u523a\u7684\u7889\u5821\u6216\u8005\u519b \u8425\u3002\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u53ef\u4ee5\u653e\u5f20\u98ce\u5e9c\u4e0b\u6765\u4e86\u3002\u201d\u5f20\u98ce\u5e9c\u88ab\u7f1a\u5728\u9a6c\u8179\u4e4b\u4e0b\u6c89\u7761\u672a\u9192\uff0c\u5f20\u4e39\u67ab\u5c06\u4ed6\u89e3 \u4e0b\uff0c\u8f7b\u8f7b\u4e00\u62cd\uff0c\u5f20\u98ce\u5e9c\u4e00\u89c9\u9192\u6765\uff0c\u53ea\u89c9\u7cbe\u795e\u9971\u6ee1\uff0c\u9163\u7545\u4e4b\u6781\uff0c\u628a\u773c\u4e00\u671b\u8be7\u9053\uff1a\u201c\u8fd9\u662f\u4ec0\u4e48\u5730 \u65b9\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u8fd9\u91cc\u79bb\u571f\u6728\u5821\u5927\u7ea6\u5df2\u6709\u767e\u91cc\u4e4b\u5730\u4e86\u3002\u201d\u5f20\u98ce\u5e9c\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u4e39\u67ab\uff0c\u4f60 \u4e3a\u4f55\u4e0d\u8bb8\u6211\u4e3a\u541b\u6b7b\u8282\uff1f\u201d\u4e39\u67ab\u9053\uff1a\u201c\u4f60\u4e00\u6b7b\u4e8b\u5c0f\uff0c\u4f46\u82e5\u4eba\u4eba\u90fd\u8981\u4e3a\u541b\u6b7b\u8282\uff0c\u53c8\u6709\u8c01\u66ff\u5927\u660e\u6c5f \u5c71\u6b7b\u8282\uff1f\u7687\u5e1d\u6b7b\u4e86\u8fd8\u6709\u7687\u5e1d\uff0c\u6c5f\u5c71\u9677\u4e8e\u5937\u72c4\uff0c\u53ef\u5c31\u96be\u4ee5\u6062\u590d\u554a\uff0c\u4f55\u51b5\u4f60\u7684\u7687\u5e1d\u4e5f\u6ca1\u6709\u6b7b\uff01\u201d \u5f20\u98ce\u5e9c\u60a0\u60a0\u9192\u609f\uff0c\u5374\u9053\uff1a\u201c\u4f46\u54b1\u4eec\u5374\u600e\u751f\u5230\u5f97\u4e86\u5317\u4eac\uff1f\u201d\u6b63\u662f\uff1a \u3000\u3000\u86df\u9f99\u867d\u51fa\u6d77\uff0c\u70fd\u706b\u5c1a\u5f25\u5929\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u840d\u8e2a\u4fa0\u5f71\u5f55\u300b\u2014\u2014\u7b2c\u4e8c\u5341\u4e00\u56de\u3000\u5927\u529b\u9664\u51f6\u5c06\u519b\u8868\u5fc3\u8ff9\u3000\u8d64\u8bda\u4e3a\u56fd\u4fa0\u58eb\u51fa\u8fb9\u5173 \u6881\u7fbd\u751f\u300a\u840d\u8e2a\u4fa0\u5f71\u5f55\u300b \u7b2c\u4e8c\u5341\u4e00\u56de\u3000\u5927\u529b\u9664\u51f6\u5c06\u519b\u8868\u5fc3\u8ff9\u3000\u8d64\u8bda\u4e3a\u56fd\u4fa0\u58eb\u51fa\u8fb9\u5173 \u3000\u3000\u5ffd\u542c\u5f97\u8e44\u58f0\u5f97\u5f97\uff0c\u539f\u6765\u662f\u4e24\u9a91\u74e6\u523a\u7684\u5de1\u67e5\u3002\u5f20\u4e39\u67ab\u7b11\u9053\uff1a\u201c\u5c31\u5728\u8fd9\u4e24\u4eba\u8eab\u4e0a\uff0c\u6211\u4fdd\u7ba1\u53eb \u5927\u54e5\u5230\u5f97\u4e86\u5317\u4eac\u3002\u201d\u90a3\u4e24\u9a91\u5de1\u67e5\u89c1\u5f20\u4e39\u67ab\u4e0e\u4e91\u857e\u90fd\u662f\u74e6\u523a\u519b\u5b98\u7684\u670d\u9970\uff0c\u5374\u4f34\u7740\u4e00\u4e2a\u6c49\u4eba\u519b \u5b98\uff0c\u4e0d\u89c9\u5927\u5947\uff0c\u6025\u5fd9\u4e0a\u524d\u67e5\u95ee\uff0c\u5f20\u4e39\u67ab\u4e0e\u4e91\u857e\u500f\u5730\u62bd\u51fa\u5b9d\u5251\uff0c\u51fa\u624b\u5982\u7535\uff0c\u4e00\u4e0b\u5b50\u5c31\u5c06\u90a3\u4e24\u4eba \u7684\u5175\u5668\u6253\u98de\uff0c\u628a\u5b9d\u5251\u67b6\u5728\u4ed6\u4eec\u5934\u4e0a\u3002\u5f20\u4e39\u67ab\u559d\u9053\uff1a\u201c\u4f60\u8981\u6b7b\u8fd8\u662f\u8981\u6d3b\uff1f\u201d\u90a3\u4e24\u4eba\u9053\uff1a\u201c\u8981 \u6d3b\u3002\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u597d\uff0c\u5c0f\u5144\u5f1f\uff0c\u628a\u8fd9\u4eba\u62c9\u5f00\u767e\u6b65\uff0c\u95ee\u4ed6\u4eca\u65e5\u53e3\u4ee4\uff01\u201d\u4e91\u857e\u4f9d\u8a00\u5c06\u90a3\u4eba\u62c9\u51fa \u767e\u6b65\u4e4b\u9065\uff0c\u53ea\u542c\u5f97\u5f20\u4e39\u67ab\u9ad8\u58f0\u8bf4\u9053\uff1a\u201c\u597d\u73b0\u5728\u5f00\u59cb\u95ee\u4ed6\u4eec\u53e3\u4ee4\uff0c\u82e5\u4ed6\u4eec\u4e24\u4eba\u6240\u8bf4\u4e0d\u540c\uff0c\u90a3\u5c31 \u5fc5\u662f\u5f04\u5047\uff0c\u4f60\u53ef\u4ee5\u4e00\u5251\u628a\u4ed6\u6740\u4e86\uff01\u201d\u5f20\u4e39\u67ab\u5185\u529f\u5df2\u6709\u706b\u5019\uff0c\u4e2d\u6c14\u5145\u6c9b\uff0c\u767e\u6b65\u4e4b\u9065\uff0c\u8bf4\u8bdd\u4e5f\u53ef \u4ee5\u6e05\u6e05\u695a\u695a\u5730\u542c\u5230\uff0c\u5bfb\u5e38\u4e4b\u4eba\uff0c\u5373\u5927\u53eb\u5927\u56b7\u5bf9\u65b9\u4e5f\u672a\u5fc5\u542c\u5f97\u771f\u5207\u3002 \u3000\u3000\u5f20\u98ce\u5e9c\u5927\u4e3a\u4f69\u670d\uff0c\u5fc3\u9053\uff1a\u201c\u5f20\u4e39\u67ab\u679c\u7136\u662f\u5fc3\u7ec6\u5982\u5c18\uff0c\u82e5\u7136\u4e0d\u662f\u5206\u5f00\u6765\u95ee\uff0c\u4ed6\u4eec\u8bf4\u4e86\u4e2a\u5047 \u7684\u53e3\u4ee4\uff0c\u54b1\u4eec\u4e5f\u96be\u4ee5\u5206\u8fa8\u3002\u201d\u5f20\u4e39\u67ab\u95ee\u4e86\u53e3\u4ee4\uff0c\u518d\u95ee\u4e91\u857e\uff0c\u4e91\u857e\u9053\uff1a\u201c\u4ed6\u8bf4\u4eca\u65e5\u7684\u53e3\u4ee4\u4e43\u662f \u5ae6\u5a25\u3002\u201d\u539f\u6765\u74e6\u523a\u519b\u4e2d\u4e5f\u77e5\u6628\u591c\u662f\u6c49\u4eba\u7684\u4e2d\u79cb\u4f73\u8282\uff0c\u4fbf\u5373\u666f\u53d6\u4e86\u201c\u5ae6\u5a25\u201d\u4e8c\u5b57\u4f5c\u4eca\u65e5\u7684\u53e3 \u4ee4\u3002\u5f20\u4e39\u67ab\u7b11\u9053\uff1a\u201c\u5bf9\u4e86\uff0c\u4ed6\u4eec\u4e0d\u6562\u5f04\u5047\u3002\u201d\u4e91\u857e\u5c06\u90a3\u4eba\u62c9\u4e86\u56de\u6765\uff0c\u5f20\u4e39\u67ab\u5265\u4e0b\u4ed6\u4eec\u7684\u5916 \u8863\uff0c\u5c06\u4e24\u4e2a\u74e6\u523a\u9a91\u5175\u7f1a\u5728\u4e00\u68f5\u6811\u4e0a\uff0c\u8bf4\u9053\uff1a\u201c\u59d4\u5c48\u4f60\u4eec\u4e00\u4e0b\uff0c\u7b49\u4f60\u4eec\u7684\u540c\u4f34\u6765\u89e3\u6551\u5427\u3002\u201d\u53eb \u5f20\u98ce\u5e9c\u4e5f\u6362\u4e0a\u4e86\u74e6\u523a\u519b\u5b98\u7684\u670d\u9970\uff0c\u5206\u4e58\u4e86\u62a2\u6765\u7684\u6218\u9a6c\uff0c\u75be\u9a70\u800c\u53bb\u3002 \u3000\u3000\u5f20\u98ce\u5e9c\u719f\u6089\u9053\u8def\uff0c\u4e13\u62e3\u5c0f\u8def\u884c\u8d70\uff0c\u907f\u5f00\u74e6\u523a\u7684\u5927\u8425\uff0c\u6cbf\u9014\u867d\u9047\u89c1\u4e0d\u5c11\u74e6\u523a\u7684\u5361\u5175\u67e5\u95ee\uff0c \u4e00\u8bf4\u53e3\u4ee4\uff0c\u679c\u7136\u901a\u884c\u65e0\u963b\uff0c\u65e5\u843d\u4e4b\u524d\uff0c\u5df2\u5230\u4e86\u5317\u4eac\u6548\u5916\u3002\u74e6\u523a\u5148\u950b\u5df2\u5728\u5317\u4eac\u6548\u5916\u6446\u4e0b\u6218\u9635\uff0c \u4e24\u519b\u5bf9\u5792\uff0c\u4e2d\u95f4\u662f\u4e00\u5927\u7247\u65e0\u4eba\u5730\u5e26\u3002\u5f20\u98ce\u5e9c\u7b49\u4e09\u4eba\u5192\u9669\u51b2\u8fc7\u53bb\uff0c\u660e\u5175\u7eb7\u7eb7\u653e\u7bad\uff0c\u4e09\u4eba\u4e00\u9762\u62e8 \u7bad\uff0c\u4e00\u9762\u75be\u9a70\u3002\u5728\u5317\u4eac\u6548\u5916\u7b51\u58d5\u5fa1\u654c\u7684\u6b63\u662f\u5fa1\u6797\u519b\u7684\u526f\u7edf\u9886\u6768\u5a01\u4e0e\u8f66\u9a91\u90fd\u5c09\u6a0a\u4fca\uff0c\u5f20\u98ce\u5e9c\u672a \u5230\u9635\u524d\uff0c\u5df2\u88ab\u8ba4\u51fa\uff0c\u6768\u5a01\u7acb\u523b\u4e0b\u4ee4\u505c\u6b62\u653e\u7bad\uff0c\u5c06\u4e09\u4eba\u8fce\u5165\u8425\u5185\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u5e2d\u4e0d\u6687\u6696\uff0c\u7acb\u5373\u95ee\u9053\uff1a\u201c\u519b\u4e2d\u58eb\u6c14\u5982\u4f55\uff1f\u201d\u6768\u5a01\u4f4e\u58f0\u9053\uff1a\u201c\u542c\u5f97\u8c23\u4f20\uff0c\u8bf4\u662f\u7687\u4e0a \u5df2\u5728\u571f\u6728\u5821\u88ab\u4fd8\uff0c\u4e0d\u6562\u6b3a\u7792\uff0c\u519b\u5fc3\u53ef\u662f\u6709\u70b9\u6447\u52a8\u3002\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u7687\u4e0a\u88ab\u4fd8\u4e4b\u4e8b\u4e0d\u662f\u8c23\u4f20\uff0c \u8fd9\u662f\u771f\u7684\u3002\u4f60\u5feb\u9001\u6211\u4eec\u5165\u57ce\uff0c\u9762\u89c1\u4e8e\u5927\u4eba\u3002\u201d\u6a0a\u4fca\u95ee\u9053\uff1a\u201c\u6211\u7684\u54e5\u54e5\u5462\uff1f\u201d\u4ed6\u7684\u54e5\u54e5\u4e43\u662f\u6a0a \u5fe0\uff0c\u5f20\u98ce\u5e9c\u6325\u6cea\u8bf4\u9053\uff1a\u201c\u4f60\u7684\u54e5\u54e5\u5df2\u6177\u6168\u6210\u4ec1\u4e86\uff0c\u671b\u4f60\u7ee7\u627f\u4ed6\u7684\u9057\u5fd7\uff0c\u575a\u5b88\u4eac\u90fd\u3002\u201d\u5c06\u6a0a\u5fe0 \u9524\u51fb\u738b\u632f\uff0c\u6b7b\u6218\u4e0d\u5c48\u7b49\u7b49\u58ee\u70c8\u7684\u4e8b\u8ff9\u8bf4\u51fa\uff0c\u4f17\u4eba\u90fd\u662f\u5927\u4e3a\u611f\u52a8\u3002 \u3000\u3000\u6768\u5a01\u8bf7\u4ed6\u4eec\u4e09\u4eba\u6362\u8fc7\u670d\u9970\uff0c\u7acb\u5373\u9001\u4ed6\u4eec\u5165\u57ce\uff0c\u57ce\u4e2d\u5c45\u6c11\u4e09\u4e09\u4e94\u4e94\u7fa4\u96c6\u8857\u5934\uff0c\u63a2\u542c\u6218\u4e8b\u7684 \u6d88\u606f\uff0c\u4eba\u4eba\u90fd\u5e26\u7740\u60b2\u6124\u7684\u795e\u8272\u3002\u5f20\u4e39\u67ab\u4e0e\u4e91\u857e\u6025\u5fd9\u8d76\u5230\u4e8e\u8c26\u7684\u4f4f\u6240\uff0c\u5176\u65f6\u5df2\u662f\u4e09\u66f4\uff0c\u4e8e\u8c26\u5bb6 \u4e2d\u8fd8\u662f\u706f\u706b\u901a\u660e\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u53e9\u95e8\u6c42\u89c1\uff0c\u4e0d\u4e00\u523b\uff0c\u5927\u95e8\u6253\u5f00\uff0c\u7ba1\u5bb6\u7684\u9053\uff1a\u201c\u5927\u4eba\u6b63\u5728\u4e2d\u5802\uff0c\u8bf7\u4f60\u4eec\u8fdb\u53bb\u3002\u201d\u5f20 \u4e39\u67ab\u6b65\u4e0a\u77f3\u9636\uff0c\u53ea\u89c1\u4e8e\u8c26\u5b64\u8eab\u4e00\u4eba\u5728\u5385\u5802\u4e0a\u6765\u56de\u8e31\u6b65\u3002\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u4e8e\u5927\u4eba\uff0c\u6211\u4eec\u56de\u6765 \u4e86\u3002\u201d\u4e8e\u9053\uff1a\u201c\u55ef\uff0c\u4f60\u4eec\u56de\u6765\u4e86\uff1f\u201d\u4ecd\u7136\u5728\u4e0d\u505c\u5730\u8e31\u6b65\uff0c\u4e91\u857e\u4e0d\u89c9\u5927\u5947\uff0c\u5fc3\u9053\uff1a\u201c\u4e8e\u8c26\u4e0e\u5f20 \u4e39\u67ab\u4e43\u662f\u5fd8\u5e74\u4e4b\u4ea4\uff0c\u5f85\u6211\u4eec\u90fd\u662f\u6709\u5982\u5b50\u4f84\uff0c\u4f55\u4ee5\u5982\u4eca\u89c1\u4e86\uff0c\u5374\u51b7\u6de1\u5982\u65af\uff1f\u201d\u7981\u4e0d\u4f4f\u8bf4\u9053\uff1a \u201c\u90a3\u5f20\u5730\u56fe\u6211\u4eec\u5df2\u5e26\u56de\u6765\u4e86\uff0c\u8fd8\u6709\u5f20\u5927\u54e5\u7956\u5148\u7684\u5b9d\u85cf\uff0c\u968f\u540e\u4e5f\u5c31\u53ef\u4ee5\u8fd0\u6765\u4e86\u3002\u201d\u4e8e\u8c26\u9762\u4e0a\u63a0 \u8fc7\u4e00\u4e1d\u559c\u8272\uff0c\u4f46\u7709\u5fc3\u7684\u91cd\u7ed3\u4ecd\u672a\u89e3\u5f00\u8bf4\u9053\uff1a\u201c\u662f\u4e48\uff1f\u53ea\u6015\u5df2\u7ecf\u8fdf\u4e86\u3002\u201d\u4ecd\u7136\u5728\u6765\u56de\u8e31\u6b65\u3002\u5f20 \u4e39\u67ab\u77e5\u4ed6\u5b9a\u662f\u6709\u6781\u91cd\u5927\u7684\u4e8b\u59d4\u51b3\u4e0d\u4e0b\uff0c\u793a\u610f\u4e91\u857e\u4e0d\u5fc5\u591a\u8a00\uff0c\u7eb5\u76ee\u56db\u987e\uff0c\u53ea\u89c1\u6a90\u9636\u4e0b\u6709\u4e00\u5927\u5806 \u77f3\u7070\uff0c\u4e24\u8fb9\u5899\u4e0a\uff0c\u5265\u843d\u4e4b\u5904\u751a\u591a\uff0c\u7070\u6c34\u53ea\u626b\u4e86\u4e00\u534a\u3002\u5f20\u4e39\u67ab\u5fc3\u4e2d\u53f9\u9053\uff1a\u201c\u82e5\u975e\u773c\u89c1\uff0c\u8c01\u6562\u76f8 \u4fe1\u4e8e\u9601\u8001\u5982\u6b64\u6e05\u8d2b\u3002\u5c4b\u5b87\u7834\u65e7\uff0c\u53ea\u53eb\u5bb6\u4eba\u81ea\u5df1\u7c89\u9970\u4fee\u8865\u3002\u201d\u62ac\u5934\u4e00\u671b\uff0c\u53c8\u89c1\u5927\u5802\u4e4b\u4e0a\uff0c\u6302\u7740 \u4e00\u5f20\u6761\u5e45\uff0c\u5199\u7684\u662f\u4e00\u9996\u4e03\u8a00\u8bd7\uff0c\u8bd7\u9053\uff1a\u201c\u5343\u9524\u4e07\u51fb\u51fa\u6df1\u5c71\uff0c\u70c8\u706b\u711a\u70e7\u82e5\u7b49\u95f2\uff0c\u7c89\u9aa8\u788e\u8eab\u5168\u4e0d \u6015\uff0c\u8981\u7559\u6e05\u767d\u5728\u4eba\u95f4\uff01\u201d\u8fd9\u9996\u8bd7\u4e43\u662f\u548f\u77f3\u7070\u4e4b\u8bd7\uff0c\u5de6\u4e0b\u89d2\u6709\u4e00\u884c\u5c0f\u5b57\uff0c\u9898\u7684\u662f\uff1a\u201c\u74e6\u523a\u56f4\u57ce \u4e4b\u65e5\uff0c\u5076\u5fc6\u65e7\u4f5c\uff0c\u611f\u800c\u5f55\u6b64\uff0c\u4e8e\u8c26\u81ea\u9898\u3002\u201d \u3000\u3000\u5f20\u4e39\u67ab\u5fc3\u4e2d\u4e00\u52a8\uff0c\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u4e8e\u5927\u4eba\uff0c\u65e2\u7136\u7c89\u9aa8\u788e\u8eab\u5168\u4e0d\u6015\uff0c\u90a3\u53c8\u4f55\u5fc5\u6015\u5bb5\u5c0f\u7684\u8bae \u8bba\uff0c\u53f2\u5b98\u7684\u8bec\u9677\uff1f\u201d\u4e8e\u8c26\u77bf\u7136\u4e00\u60ca\u53cc\u76ee\u70af\u70af\uff0c\u4ef0\u89c6\u957f\u7a7a\uff0c\u5ffd\u800c\u53f9\u9053\uff1a\u201c\u8d24\u4f84\uff0c\u53ea\u6709\u4f60\u4e00\u4eba\u77e5 \u9053\u6211\u7684\u5fc3\u610f\u3002\u53ea\u662f\u5179\u4e8b\u4f53\u5927\uff0c\u7c89\u9aa8\u788e\u8eab\u72b9\u5728\u5176\u6b21\uff0c\u53ea\u6050\u6211\u5c06\u6765\u8981\u8499\u4e0b\u4e0d\u767d\u4e4b\u51a4\u3002\u201d\u5f20\u4e39\u67ab \u9053\uff1a\u201c\u5f53\u4eca\u5929\u5b50\u65e2\u5df2\u88ab\u4fd8\uff0c\u5927\u4eba\u5f53\u4e3a\u5927\u660e\u7684\u6c5f\u5c71\u7740\u60f3\uff0c\u5f53\u673a\u7acb\u65ad\uff0c\u6b64\u5176\u65f6\u77e3\u3002\u5373\u7b97\u4ed6\u65e5\u7687\u5e1d \u964d\u7f6a\uff0c\u7c89\u9aa8\u788e\u8eab\uff0c\u4f46\u5927\u4eba\u5df2\u7559\u6e05\u767d\u62d3\u4eba\u95f4\uff0c\u4e07\u4e16\u5343\u79cb\uff0c\u6c38\u5782\u9752\u53f2\uff0c\u53c8\u4f55\u8db3\u60e7\uff1f\u201d\u4e8e\u8c26\u7709\u5fc3\u7684 \u91cd\u7ed3\u4e00\u4e0b\u89e3\u5f00\u62cd\u6848\u8bf4\u9053\uff1a\u201c\u8d24\u4f84\u8bf4\u7684\u662f\u3002\u6211\u660e\u65e5\u4fbf\u7acb\u65b0\u541b\uff0c\u5c3d\u6740\u9006\u515a\uff0c\u4eb2\u81ea\u7763\u6218\u4e5d\u95e8\uff01\u201d \u3000\u3000\u539f\u6765\u4e8e\u8c26\u5df2\u63a5\u5230\u7687\u5e1d\u88ab\u4fd8\u7684\u6d88\u606f\uff0c\u5fc3\u4e2d\u4e5f\u81ea\u6599\u5230\u74e6\u523a\u5fc5\u7136\u631f\u5929\u5b50\u4ee5\u4e3a\u8981\u631f\uff0c\u5bf9\u4ed8\u4e4b\u7b56\uff0c \u53ea\u6709\u53e6\u7acb\u7687\u5e1d\uff0c\u8868\u793a\u6297\u6218\u5230\u5e95\u7684\u51b3\u5fc3\u3002\u53ef\u662f\u81ea\u5df1\u5e76\u975e\u7687\u5ba4\u4e2d\u4eba\uff0c\u7531\u81ea\u5df1\u51fa\u5934\u53e6\u7acb\u7687\u5e1d\uff0c\u8fd9\u8d23 \u4efb\u53ef\u662f\u592a\u8fc7\u91cd\u5927\u3002\u654c\u515a\u7684\u8bae\u8bba\u6253\u51fb\uff0c\u7687\u5ba4\u91cc\u9762\u7684\u871a\u77ed\u6d41\u957f\u7b49\u7b49\uff0c\u90fd\u5728\u610f\u6599\u4e4b\u4e2d\u3002\u800c\u4e14\u4ed6\u65e5\u88ab \u4fd8\u7684\u7687\u5e1d\uff0c\u82e5\u7136\u5f97\u91ca\u653e\u5f52\u6765\uff0c\u4e0d\u80af\u8c05\u89e3\u7684\u8bdd\uff0c\u90a3\u79cd\u906d\u53d7\u706d\u95e8\u4e4b\u7978\uff0c\u4e5f\u975e\u610f\u5916\u3002\u6240\u4ee5\u601d\u91cf\u4e86\u4e00 \u65e5\u4e00\u591c\uff0c\u4ecd\u662f\u8e0c\u8e87\u672a\u51b3\uff0c\u76f4\u5230\u5f20\u4e39\u67ab\u5256\u9648\u5229\u5bb3\uff0c\u6177\u6168\u8fdb\u8a00\u4e4b\u540e\uff0c\u4e8e\u8c26\u624d\u628a\u4e00\u5207\u7f6e\u4e4b\u5ea6\u5916\uff0c\u4ee5 \u7edd\u5927\u7684\u3001\u8d85\u4eba\u7684\u9b44\u529b\uff0c\u5728\u5386\u53f2\u4e0a\u5199\u4e0b\u4e86\u8f89\u714c\u7684\u4e00\u9875\u3002 \u3000\u3000\u7b2c\u4e8c\u65e5\u4e8e\u8c26\u805a\u96c6\u4e86\u671d\u4e2d\u6b63\u76f4\u7684\u5927\u81e3\uff0c\u51b3\u5b9a\u4e86\u5bf9\u654c\u7684\u65b9\u7565\uff0c\u9996\u5148\u62e5\u7acb\u4e86\u7948\u9547\u7684\u5f1f\u5f1f\u7948\u94b0\u505a \u7687\u5e1d\uff08\u5373\u660e\u4ee3\u5b97\uff09\uff0c\u9065\u5c0a\u7948\u9547\u4e3a\u201c\u592a\u4e0a\u7687\u201d\u3002\u8ddf\u7740\u4e0b\u4ee4\u5c3d\u6740\u738b\u632f\u7684\u515a\u7fbd\u3002 \u3000\u3000\u7948\u94b0\u5373\u4f4d\uff0c\u56fd\u53f7\u201c\u666f\u6cf0\u201d\uff0c\u542c\u4e86\u4e8e\u8c26\u4e4b\u8ba1\uff0c\u4e00\u65e5\u4e4b\u95f4\uff0c\u628a\u5978\u5ba6\u738b\u632f\u5728\u4eac\u4e2d\u7684\u515a\u7fbd\u4e09\u767e\u4f59 \u4eba\uff0c\u5c3d\u6570\u6740\u4e86\uff0c\u5373\u4e0b\u4ee4\u53eb\u4e8e\u8c26\u517c\u4efb\u5175\u90e8\u5c1a\u4e66\uff0c\u7763\u6218\u4e5d\u95e8\uff0c\u767b\u65f6\u519b\u5fc3\u632f\u594b\uff0c\u6c11\u6c14\u6cb8\u817e\uff0c\u5c31\u5728\u5317 \u4eac\u5c55\u5f00\u4e86\u4e00\u573a\u58ee\u70c8\u7684\u4fdd\u536b\u90fd\u95e8\u4e4b\u6218\u3002 \u3000\u3000\u4e5f\u5148\u64d2\u83b7\u4e86\u660e\u671d\u7684\u7687\u5e1d\u7948\u9547\u4e4b\u540e\uff0c\u672c\u6765\u4ee5\u4e3a\u5317\u4eac\u53ef\u4ee5\u553e\u624b\u800c\u5f97\uff0c\u4e2d\u539f\u53ef\u4ee5\u4f20\u6a84\u800c\u5b9a\uff0c\u54ea \u77e5\u4e8e\u8c26\u53e6\u7acb\u65b0\u541b\uff0c\u53ec\u5929\u4e0b\u4e49\u5e08\uff0c\u5174\u5175\u52e4\u738b\uff0c\u4e5f\u5148\u53c8\u60ca\u53c8\u6012\uff0c\u7acb\u5373\u6325\u5175\u56f4\u653b\u5317\u4eac\u3002\u5341\u6708\u521d\u4e5d\u653b \u7834\u7d2b\u8346\u5173\uff0c\u5341\u4e00\u65e5\u5148\u950b\u5230\u4e86\u5317\u4eac\u7684\u897f\u76f4\u95e8\u5916\uff0c\u7948\u94b0\u5df2\u60f3\u8bb2\u548c\uff0c\u4e8e\u8c26\u6781\u529b\u4e3b\u6218\uff0c\u5c31\u5728\u5317\u4eac\u57ce\u4e2d \u6fc0\u6218\u4e94\u65e5\u4e94\u591c\uff0c\u74e6\u523a\u519b\u867d\u7136\u653b\u7834\u4e86\u5f70\u4eea\u95e8\u3001\u5fb7\u80dc\u95e8\uff0c\u4f46\u5b88\u57ce\u7684\u519b\u58eb\uff0c\u5168\u519b\u6b7b\u6218\uff0c\u5317\u4eac\u7684\u767e\u4f59 \u4e07\u5c45\u6c11\uff0c\u4e0d\u5206\u7537\u5973\u8001\u5f31\uff0c\u4e5f\u90fd\u767b\u57ce\u534f\u52a9\u4f5c\u6218\uff0c\u5f13\u7bad\u4e0d\u591f\uff0c\u5c45\u6c11\u5c31\u62c6\u4e86\u81ea\u5df1\u7684\u623f\u5c4b\uff0c\u7528\u7816\u77f3\u6295 \u51fb\u654c\u4eba\uff0c\u4e94\u65e5\u4e94\u591c\uff0c\u6740\u58f0\u9707\u5929\uff0c\u74e6\u523a\u519b\u867d\u7136\u9aa0\u608d\uff0c\u4e5f\u4e0d\u89c9\u80c6\u5bd2\u3002\u5230\u4e86\u7b2c\u516d\u65e5\uff0c\u6709\u51e0\u8def\u52e4\u738b\u4e49 \u5e08\uff0c\u5df2\u517c\u7a0b\u8d76\u5230\uff0c\u65cc\u65d7\u62db\u5c55\uff0c\u5728\u5317\u4eac\u57ce\u5934\uff0c\u5df2\u53ef\u9065\u9065\u671b\u89c1\u3002\u5f20\u98ce\u5e9c\u7387\u9886\u5fa1\u6797\u519b\u51b2\u6740\u51fa\u53bb\uff0c\u8fde \u65a9\u654c\u8425\u4e09\u5458\u731b\u5c06\uff0c\u4e8e\u8c26\u4e00\u58f0\u53f7\u4ee4\uff0c\u5317\u4eac\u57ce\u5185\uff0c\u519b\u6c11\u9f50\u8d77\uff0c\u5f00\u95e8\u653b\u654c\u3002\u4e5f\u5148\u6050\u6015\u518d\u50f5\u6301\u4e0b\u53bb\uff0c \u660e\u671d\u7684\u5404\u8def\u63f4\u519b\u5c3d\u81f3\uff0c\u90a3\u65f6\u52bf\u5c06\u53d7\u5185\u5916\u5939\u653b\uff0c\u5f52\u8def\u4e5f\u53ef\u80fd\u53d7\u660e\u5175\u622a\u65ad\uff0c\u8861\u91cf\u5168\u5c40\uff0c\u53ea\u597d\u4e0b\u4ee4 \u9000\u519b\uff0c\u74e6\u523a\u5728\u5341\u6708\u5341\u4e00\u653b\u5165\u897f\u76f4\u95e8\uff0c\u5230\u5341\u6708\u5341\u4e03\u9000\u5175\uff0c\u4f24\u4ea1\u4e86\u4e03\u516b\u4e07\u4eba\uff0c\u4e00\u65e0\u6240\u5f97\u3002 \u3000\u3000\u5341\u516b\u65e5\uff0c\u5317\u4eac\u57ce\u5916\u5df2\u65e0\u654c\u8e2a\uff0c\u901a\u5dde\u3001\u6cb3\u5357\u7684\u51e0\u8def\u4e49\u519b\u9646\u7eed\u5165\u57ce\uff0c\u8fd9\u51e0\u8def\u4e49\u519b\u4ea6\u4e0d\u8fc7\u51e0\u4e07 \u4eba\uff0c\u6bd4\u8d77\u74e6\u523a\u7684\u5175\u529b\uff0c\u5b9e\u662f\u5fae\u4e0d\u8db3\u9053\uff0c\u60f3\u4e0d\u5230\u51ed\u7740\u5317\u4eac\u519b\u6c11\u7684\u58eb\u6c14\uff0c\u631f\u7740\u5185\u5916\u5939\u653b\u7684\u5a01\u52bf\uff0c \u7adf\u628a\u74e6\u523a\u5927\u519b\u5413\u8d70\uff0c\u771f\u662f\u4eba\u4eba\u9ad8\u5174\uff0c\u4e2a\u4e2a\u6b22\u547c\u3002\u4e8e\u8c26\u63a5\u5f85\u5404\u8def\u4e49\u5e08\uff0c\u53d1\u73b0\u5176\u4e2d\u4e00\u8def\uff0c\u7adf\u662f\u6765 \u81ea\u9065\u8fdc\u7684\u6c5f\u82cf\uff0c\u53ea\u6709\u6570\u767e\u4eba\u3002\u539f\u6765\u8fd9\u8def\u4e49\u5e08\uff0c\u4fbf\u662f\u4e91\u91cd\u6240\u7387\u9886\u7684\u4ee5\u6fb9\u53f0\u5e84\u4e3b\u7684\u5e84\u4e01\u4e3a\u4e3b\uff0c\u518d \u5728\u6cbf\u9014\u62db\u96c6\u4e49\u58eb\u6240\u7ec4\u6210\u7684\u4e49\u5e08\u3002\u672c\u6765\u5df2\u805a\u96c6\u4e86\u4e00\u5343\u591a\u4ece\uff0c\u7ecf\u8fc7\u6fc0\u6218\u4f24\u4ea1\u5927\u534a\uff0c\u8fde\u4e91\u91cd\u5728\u4e5f\u6218 \u9635\u4e4b\u4e2d\u5931\u843d\uff0c\u73b0\u5728\u8fd9\u8def\u4e49\u5e08\u4e43\u662f\u7531\u94c1\u81c2\u91d1\u733f\u9f99\u9547\u65b9\u6240\u7387\u9886\u3002\u4ed6\u4eec\u4e0d\u8d1f\u5f20\u4e39\u67ab\u7684\u91cd\u6258\uff0c\u679c\u7136\u628a \u5f20\u58eb\u8bda\u6240\u9057\u4e0b\u7684\u5b9d\u85cf\uff0c\u4e00\u4ef6\u4e0d\u5931\uff0c\u8fd0\u5230\u4e86\u5317\u4eac\u3002 \u3000\u3000\u4e8e\u8c26\u6025\u5fd9\u5c06\u94c1\u81c2\u91d1\u733f\u9f99\u9547\u65b9\u4e0e\u4e09\u82b1\u5251\u7384\u7075\u5b50\u8bf8\u4eba\u8bf7\u5230\u4f4f\u6240\u4e0e\u5f20\u4e39\u67ab\u3001\u4e91\u857e\u76f8\u89c1\uff0c\u4e91\u857e\u542c \u5f97\u54e5\u54e5\u5931\u843d\uff0c\u5927\u60ca\u5931\u8272\uff0c\u6025\u95ee\u60c5\u5f62\u3002\u94c1\u81c2\u91d1\u733f\u8bf4\u9053\uff1a\u201c\u6628\u65e5\u6fc0\u6218\u4e4b\u4e2d\uff0c\u4e91\u72b6\u5143\u53eb\u6211\u4eec\u4fdd\u62a4\u5b9d \u7269\u51b2\u5f00\u4e00\u6761\u8840\u8def\uff0c\u4ed6\u81ea\u5df1\u6bbf\u540e\uff0c\u4e3a\u6211\u4eec\u62b5\u5fa1\u8ffd\u5175\uff0c\u90a3\u4f4d\u6fb9\u53f0\u59d1\u5a18\u7387\u9886\u5341\u4f59\u540d\u5bb6\u4e01\uff0c\u4e5f\u5728\u5de6\u7ffc \u63a9\u62a4\u3002\u6211\u4eec\u660e\u77e5\u5371\u9669\uff0c\u4f46\u4e3a\u4e86\u4fdd\u62a4\u5b9d\u85cf\uff0c\u4e5f\u53ea\u5f97\u542c\u4ece\u4ed6\u7684\u4e3b\u610f\u3002\u540e\u6765\u6211\u4eec\u4e0e\u4e91\u72b6\u5143\u53ca\u6fb9\u53f0\u59d1 \u5a18\u90fd\u88ab\u74e6\u523a\u519b\u6240\u622a\u65ad\uff0c\u4e91\u72b6\u5143\u5341\u5206\u52c7\u731b\uff0c\u773c\u770b\u5df2\u6740\u5f00\u4e00\u6761\u8840\u8def\uff0c\u4e0d\u6599\u5ffd\u542c\u5f97\u4e00\u58f0\u5f13\u54cd\uff0c\u6fb9\u53f0 \u59d1\u5a18\u4e2d\u4e86\u4e00\u7bad\uff0c\u51b2\u4e0d\u51fa\u6765\uff0c\u4e91\u72b6\u5143\u56de\u53bb\u6551\u5979\uff0c\u5c31\u8fd9\u6837\u4e24\u4eba\u90fd\u5931\u843d\u4e86\uff01\u201d \u3000\u3000\u4e91\u857e\u542c\u4e86\u54e5\u54e5\u5931\u843d\u7684\u7ecf\u8fc7\uff0c\u66f4\u662f\u5fe7\u5f62\u4e8e\u8272\u3002\u4e8e\u8c26\u9053\uff1a\u201c\u597d\u5728\u654c\u5175\u5df2\u9000\uff0c\u6211\u7acb\u523b\u4e0b\u4ee4\u6d3e\u4eba \u5230\u4eac\u90ca\u5404\u5904\u6253\u5bfb\uff0c\u603b\u53ef\u5bfb\u7740\u3002\u201d\u4e91\u857e\u542c\u4e86\uff0c\u7a0d\u7a0d\u5bbd\u5fc3\uff0c\u4f46\u60f3\u5230\u54e5\u54e5\u5728\u5343\u519b\u4e07\u9a6c\u4e4b\u4e2d\uff0c\u800c\u4e14\u8981 \u6551\u62a4\u53d7\u4e86\u4f24\u7684\u6fb9\u53f0\u955c\u660e\uff0c\u662f\u5426\u80fd\u591f\u8131\u9669\u751f\u8fd8\uff0c\u8fd8\u662f\u7591\u95ee\uff0c\u4f46\u4e8b\u5df2\u81f3\u6b64\uff0c\u4ea6\u53ea\u6709\u6307\u671b\u4e8e\u8c26\u80fd\u628a \u4ed6\u627e\u56de\u6765\u4e86\u3002 \u3000\u3000\u4e91\u91cd\u90a3\u65e5\u4e5f\u786e\u662f\u60ca\u9669\u65e0\u6bd4\uff0c\u6fb9\u53f0\u955c\u660e\u4e2d\u7bad\u4e4b\u540e\uff0c\u4e91\u91cd\u8d76\u8fc7\u53bb\u6551\uff0c\u9677\u5165\u91cd\u56f4\u3002\u4e91\u91cd\u5927\u65bd\u521a \u52c7\uff0c\u53f3\u624b\u65ad\u95e8\u5200\u821e\u6210\u4e00\u9053\u5149\u5708\uff0c\u5c06\u6fb9\u53f0\u955c\u660e\u4e5f\u7b3c\u7f69\u5728\u5200\u5149\u4e4b\u5185\uff0c\u5de6\u624b\u8fd0\u91d1\u521a\u638c\u7684\u529f\u592b\uff0c\u654c\u4eba \u8fd1\u8eab\u5c31\u5c06\u4ed6\u4e00\u638c\u6253\u6b7b\uff0c\u6fc0\u6218\u591a\u65f6\uff0c\u8fde\u6bd9\u654c\u5175\u6570\u5341\uff0c\u53ef\u662f\u654c\u4eba\u4f17\u591a\u6740\u4e4b\u4e0d\u5c3d\uff0c\u6e10\u89c9\u7b4b\u75b2\u529b\u5026\u3002 \u6b63\u5728\u5371\u6025\u4e07\u5206\u4e4b\u9645\uff0c\u5ffd\u542c\u5f97\u654c\u4eba\u91d1\u9f13\u9f50\u9e23\uff0c\u5439\u8d77\u51b2\u950b\u53f7\u89d2\uff0c\u56f4\u653b\u81ea\u5df1\u7684\u654c\u5175\u7eb7\u7eb7\u62e5\u5411\u524d\u9762\u3002 \u539f\u6765\u662f\u57ce\u4e2d\u6740\u51fa\uff0c\u4e5f\u5148\u8c03\u5175\u4e0a\u53bb\u589e\u63f4\uff0c\u5bf9\u4e91\u91cd\u7684\u538b\u529b\u4fbf\u81ea\u5df1\u7136\u51cf\u8f7b\u4e86\u3002 \u3000\u3000\u4e91\u91cd\u5e76\u4e0d\u77e5\u9053\u5176\u4e2d\u7f18\u6545\uff0c\u4e00\u89c1\u6709\u673a\u53ef\u4e58\uff0c\u7acb\u523b\u7eb5\u9a6c\u5954\u51fa\uff0c\u4fdd\u62a4\u6fb9\u53f0\u955c\u660e\u843d\u8352\u800c\u9003\uff0c\u534a\u4e2a \u65f6\u8fb0\u4e4b\u540e\uff0c\u5df2\u5c06\u6218\u573a\u8fdc\u8fdc\u629b\u5728\u540e\u9762\u3002\u4e91\u91cd\u677e\u4e86\u53e3\u6c14\uff0c\u5ffd\u89c1\u6fb9\u53f0\u955c\u660e\u9762\u8272\u82cd\u767d\uff0c\u4e91\u91cd\u95ee\u9053\uff1a \u201c\u600e\u4e48\u5566\uff1f\u201d\u6fb9\u53f0\u955c\u660e\u9053\uff1a\u201c\u6ca1\u4ec0\u4e48\u3002\u201d\u4f46\u5df2\u63e1\u4e0d\u7d27\u7ef3\u25a1\uff0c\u5728\u9a6c\u80cc\u4e0a\u5a07\u8eaf\u4e71\u98a4\uff0c\u6447\u6447\u6b32\u5760\u3002 \u4e91\u91cd\u5fae\u5fae\u4e00\u7b11\uff0c\u67d4\u58f0\u8bf4\u9053\uff1a\u201c\u6fb9\u53f0\u59b9\u5b50\uff0c\u6211\u4ee5\u524d\u53d7\u4f24\u4e4b\u65f6\uff0c\u591a\u8499\u4f60\u7684\u6551\u62a4\uff0c\u4f60\u66fe\u6559\u8fc7\u6211\u4e0d\u8981 \u786c\u633a\uff0c\u4f60\u53ef\u8bb0\u5f97\u4e48\uff1f\u201d\u8bf4\u5b8c\u4e4b\u540e\uff0c\u5728\u9a6c\u80cc\u4e0a\u98de\u8eab\u4e00\u8df3\uff0c\u8df3\u5230\u6fb9\u53f0\u955c\u660e\u7684\u9a6c\u4e0a\uff0c\u62a2\u8fc7\u7ef3\u25a1\uff0c\u6276 \u7d27\u6fb9\u53f0\u955c\u660e\uff0c\u8bf4\u9053\uff1a\u201c\u6fb9\u53f0\u59b9\u5b50\uff0c\u4f60\u4e14\u6b47\u6b47\uff0c\u54b1\u4eec\u627e\u4e00\u5904\u4eba\u5bb6\uff0c\u8eb2\u5b83\u51e0\u5929\uff0c\u5f85\u4f60\u517b\u597d\u4e86\u4f24\uff0c \u518d\u60f3\u6cd5\u5165\u4eac\u3002\u201d\u6fb9\u53f0\u955c\u660e\u5bf9\u4e91\u91cd\u6b8a\u65e0\u597d\u611f\uff0c\u4f46\u89c1\u4ed6\u67d4\u60c5\u4f3c\u6c34\uff0c\u52a0\u610f\u6276\u6301\u5fc3\u4e2d\u4e5f\u81ea\u611f\u52a8\u3002 \u3000\u3000\u6218\u573a\u9644\u8fd1\u7684\u6751\u843d\uff0c\u4e00\u7247\u788e\u74e6\u9893\u57a3\uff0c\u4e0d\u89c1\u4eba\u8ff9\u3002\u4e91\u91cd\u5fc3\u4e2d\u6b63\u5728\u5fe7\u8651\uff0c\u5ffd\u89c1\u524d\u9762\u6751\u8fb9\uff0c\u4e00\u5ea7 \u501a\u5c71\u5efa\u7b51\u7684\u5c4b\u5b87\uff0c\u5c1a\u5c5e\u5b8c\u6574\uff0c\u559c\u9053\uff1a\u201c\u5929\u65e0\u7edd\u4eba\u4e4b\u8def\uff0c\u8fd9\u91cc\u7adf\u7136\u8fd8\u6709\u4e00\u5904\u4eba\u5bb6\u3002\u201d\u6fb9\u53f0\u955c\u660e \u6447\u4e86\u6447\u5934\uff0c\u9053\uff1a\u201c\u8fd9\u4eba\u5bb6\u53ea\u6050\u6015\u4e0d\u662f\u4ec0\u4e48\u597d\u8def\u9053\uff0c\u4e91\u5144\uff0c\u4f60\u53ef\u8981\u5c0f\u5fc3\u3002\u201d\u4e91\u91cd\u9053\uff1a\u201c\u7ba1\u5b83\u662f \u4ec0\u4e48\u8def\u9053\uff0c\u4f60\u517b\u4f24\u8981\u7d27\u3002\u201d\u6276\u6fb9\u53f0\u955c\u660e\u4e0b\u9a6c\uff0c\u4fbf\u53bb\u53e9\u95e8\u3002 \u3000\u3000\u95e8\u5185\u6709\u4eba\u5927\u58f0\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\uff1f\u201d\u4e91\u91cd\u4e00\u542c\u8fd9\u58f0\u97f3\u597d\u719f\uff0c\u7b54\u9053\uff1a\u201c\u6211\u662f\u4ece\u6c5f\u82cf\u6765\u7684\u4e49 \u519b\uff0c\u6b32\u501f\u5b9d\u5e84\u4e00\u6b47\u3002\u201d\u90a3\u95e8\u5440\u7684\u4e00\u58f0\u5f00\u4e86\uff0c\u53ea\u542c\u5f97\u91cc\u9762\u7684\u4eba\u53eb\u9053\uff1a\u201c\u554a\u5440\uff0c\u539f\u6765\u662f\u4e91\u72b6 \u5143\u3002\u201d\u58f0\u97f3\u5fae\u5fae\u98a4\u6296\uff0c\u4f3c\u662f\u53c8\u60ca\u53c8\u559c\uff0c\u9887\u51fa\u610f\u5916\u3002\u4e91\u91cd\u4e00\u770b\uff0c\u53ea\u89c1\u91cc\u9762\u4e24\u4eba\u5e76\u80a9\u800c\u7acb\uff0c\u7adf\u662f \u4ee5\u524d\u5bab\u4e2d\u7684\u6b66\u58eb\u8def\u660e\u3001\u8def\u4eae\u3002 \u3000\u3000\u4e91\u91cd\u8be7\u9053\uff1a\u201c\u4e24\u4f4d\u8def\u5144\u600e\u4e48\u8fd8\u5728\u8fd9\u513f\uff1f\u201d\u8def\u660e\u9053\uff1a\u201c\u534a\u6708\u4e4b\u524d\uff0c\u6211\u89c1\u654c\u5175\u5165\u5bc7\uff0c\u544a\u5047\u56de \u6765\uff0c\u60f3\u62a4\u9001\u5bb6\u4eba\u5165\u4eac\u907f\u96be\uff0c\u4e0d\u6599\u654c\u5175\u6765\u5f97\u592a\u5feb\uff0c\u4ee5\u81f4\u88ab\u622a\u65ad\u4e86\uff0c\u8fdb\u4eac\u4e0d\u5f97\uff0c\u53ea\u597d\u6682\u907f\u4e61\u95f4\u3002 \u5440\u8fd9\u4f4d\u5973\u82f1\u96c4\u4e5f\u662f\u4e49\u519b\u4e48\uff1f\u96be\u5f97\u96be\u5f97\uff0c\u5979\u7adf\u7136\u53d7\u4e86\u4f24\uff1f\u5feb\u5feb\u8fdb\u6765\u6211\u8fd9\u91cc\u6709\u4e0a\u597d\u7684\u91d1\u521b\u7075 \u836f\u3002\u201d\u8bf4\u7740\u4fbf\u5e26\u9886\u4e91\u91cd\u8fdb\u5165\u82b1\u5385\u3002 \u3000\u3000\u8def\u660e\u9053\uff1a\u201c\u4e24\u4f4d\u6b47\u6b47\uff0c\u5148\u559d\u4e00\u676f\u70ed\u8336\u3002\u201d\u53eb\u5bb6\u4eba\u732e\u8336\u6765\u3002\u6fb9\u53f0\u955c\u660e\u5fc3\u601d\u7f1c\u5bc6\uff0c\u6697\u81ea\u60f3 \u9053\uff1a\u201c\u8fd9\u4e24\u4eba\u65e2\u662f\u4eac\u4e2d\u7684\u6b66\u58eb\uff0c\u4f55\u4ee5\u5728\u4eac\u57ce\u5371\u6025\u4e4b\u9645\uff0c\u5c1a\u51c6\u4ed6\u544a\u5047\u8fd8\u5bb6\uff1f\u800c\u4e14\u74e6\u523a\u5927\u519b\u8fc7 \u5904\uff0c\u9e21\u72ac\u4e0d\u5b81\uff0c\u5bb6\u5bb6\u7834\u788e\uff0c\u4f55\u4ee5\u4ed6\u4eec\u8fd9\u4e00\u5bb6\u72ec\u81ea\u4fdd\u6301\u5b8c\u6574\uff1f\u201d\u653e\u773c\u56db\u671b\uff0c\u89c1\u82b1\u5385\u4e4b\u5185\uff0c\u6446\u6709 \u8bf8\u822c\u5175\u5668\uff0c\u66f4\u662f\u7591\u5fc3\u3002\u6b64\u65f6\u4e91\u91cd\u5df2\u7aef\u8d77\u8336\u676f\uff0c\u6fb9\u53f0\u955c\u660e\u6025\u5fd9\u8fde\u6253\u773c\u8272\uff0c\u4e91\u91cd\u7adf\u4f3c\u4e1d\u6beb\u672a\u89c9\uff0c \u628a\u8336\u676f\u7aef\u5230\u5507\u8fb9\uff0c\u6fb9\u53f0\u955c\u660e\u5fc3\u4e2d\u5927\u6025\uff0c\u51e0\u4e4e\u5c31\u8981\u558a\u51fa\u58f0\u6765\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u201c\u25a1\u25a1\u201d\u4e00\u58f0\uff0c\u8336\u676f\u5760\u5730\uff0c\u4e91\u91cd\u53eb\u9053\uff1a\u201c\u54ce\u5440\uff0c\u4e0d\u597d\uff0c\u8bf7\u6055\u5c0f\u5f1f\u5931\u624b\uff0c\u6362\u8fc7\u4e00\u676f \u5427\u3002\u201d\u8bdd\u58f0\u672a\u4e86\uff0c\u5730\u4e0a\u5df2\u6e85\u8d77\u4e86\u4e00\u6e9c\u706b\u5149\uff0c\u676f\u4e2d\u76db\u7684\u54ea\u91cc\u662f\u8336\uff1f\u7adf\u662f\u4e00\u676f\u6bd2\u836f\uff01\u539f\u6765\u4e91\u91cd\u4e5f \u5df2\u751f\u7591\uff0c\u731b\u7136\u60f3\u8d77\u8def\u660e\u3001\u8def\u4eae\u4e43\u662f\u738b\u632f\u7684\u5fc3\u8179\u6b66\u58eb\uff0c\u4e91\u91cd\u867d\u7136\u8fd8\u672a\u77e5\u9053\u738b\u632f\u5728\u571f\u6728\u5821\u53db\u53d8\u88ab \u6a0a\u5fe0\u6253\u6b7b\u7b49\u7b49\u60c5\u4e8b\uff0c\u4f46\u738b\u632f\u4e4b\u5978\uff0c\u5929\u4e0b\u65e0\u4eba\u4e0d\u77e5\uff0c\u5373\u7b97\u662f\u6fb9\u53f0\u955c\u660e\u4e0d\u6253\u773c\u8272\u4e91\u91cd\u4e5f\u81ea\u5c0f\u5fc3\u6212 \u5907\u3002 \u3000\u3000\u8def\u5bb6\u5144\u5f1f\u9634\u8c0b\u8d25\u9732\uff0c\u4e00\u58f0\u5927\u543c\uff0c\u5404\u81ea\u62a2\u4e86\u5175\u5668\uff0c\u7acb\u523b\u56f4\u7740\u4e91\u91cd\u52a8\u624b\u3002\u8def\u660e\u4f7f\u7684\u662f\u4e00\u53e3\u957f \u5251\uff0c\u8def\u4eae\u4f7f\u7684\u662f\u4e00\u9762\u94c1\u724c\uff0c\u94c1\u724c\u821e\u52a8\uff0c\u547c\u547c\u631f\u98ce\uff0c\u90a3\u53e3\u957f\u5251\uff0c\u5c31\u5728\u94c1\u724c\u540e\u9762\u4e00\u4f38\u4e00\u7f29\uff0c\u4e13\u5236 \u654c\u4eba\u4e09\u5341\u516d\u9053\u5927\u7a74\u3002\u8fd9\u8def\u5bb6\u7684\u6df7\u5143\u724c\u6cd5\uff0c\u5929\u4e0b\u9a70\u540d\uff0c\u914d\u4ee5\u957f\u5251\uff0c\u66f4\u662f\u5584\u5b88\u80fd\u653b\uff0c\u5389\u5bb3\u65e0\u6bd4\u3002 \u3000\u3000\u4e91\u91cd\u4e00\u638c\u62a4\u80f8\uff0c\u5355\u5200\u8fce\u654c\uff0c\u6012\u58f0\u559d\u9053\uff1a\u201c\u4f60\u4eec\u5144\u5f1f\u60f3\u9020\u53cd\u5417\uff1f\u201d\u8def\u660e\u5927\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c \u6b63\u662f\u9020\u53cd\u3002\u6211\u8bf4\u4f60\u8fd8\u8499\u5728\u9f13\u91cc\uff0c\u771f\u662f\u4e0d\u77e5\u5929\u9ad8\u5730\u539a\uff01\u201d\u4e91\u91cd\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u8def\u4eae\u9053\uff1a\u201c\u6211\u95ee \u4f60\u4f60\u5e26\u4e49\u519b\u5165\u4eac\uff0c\u662f\u4e0d\u662f\u4e3a\u4e86\u52e4\u738b\u800c\u6765\uff1f\u201d\u4e91\u91cd\u8fde\u5288\u4e09\u5200\uff0c\u6321\u8fc7\u94c1\u724c\uff0c\u67b6\u5f00\u957f\u5251\u6717\u58f0\u8bf4\u9053\uff1a \u201c\u90a3\u4e2a\u5f53\u7136\uff01\u201d\u8def\u4eae\u5927\u7b11\u9053\uff1a\u201c\u4f60\u7684\u7687\u5e1d\u8001\u5b50\u65e9\u5df2\u505a\u4e86\u74e6\u523a\u7684\u4fd8\u864f\u5566\u3002\u5e38\u8a00\u9053\u5f97\u597d\uff0c\u8bc6\u65f6\u52a1 \u8005\u4e3a\u4fca\u6770\uff0c\u4f60\u5feb\u5feb\u653e\u4e0b\u5175\u5668\uff0c\u968f\u6211\u4eec\u540c\u964d\u74e6\u523a\uff0c\u90a3\u5c1a\u53ef\u4ee5\u4fdd\u4f4f\u529f\u540d\u5bcc\u8d35\uff0c\u5426\u5219\u74e6\u523a\u5927\u519b\uff0c\u5c31 \u5728\u9644\u8fd1\uff0c\u4f60\u662f\u660e\u671d\u7684\u72b6\u5143\uff0c\u5c31\u662f\u6211\u4e0d\u6740\u4f60\uff0c\u4f60\u4e5f\u96be\u9003\u4e00\u6b7b\uff01\u201d \u3000\u3000\u4e91\u91cd\u6124\u6012\u4e4b\u6781\uff0c\u5f3a\u6291\u5fc3\u5934\u4e4b\u706b\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u539f\u6765\u4e24\u4f4d\u90fd\u662f\u8bc6\u65f6\u52a1\u7684\u4fca\u7070\uff0c\u5931\u656c\u5931\u656c\uff01\u201d \u8def\u660e\u5c1a\u4ee5\u4e3a\u4e91\u91cd\u88ab\u4ed6\u8bf4\u52a8\uff0c\u51d1\u4e0a\u524d\u9053\uff1a\u201c\u4e91\u5144\u610f\u4e0b\u5982\u4f55\uff1f\u201d\u4e91\u91cd\u5927\u559d\u4e00\u58f0\uff1a\u201c\u6211\u610f\u6b32\u53d6\u4f60\u7684 \u72d7\u547d\uff01\u201d\u731b\u5730\u4e00\u5200\u5288\u4e0b\uff0c\u53ea\u542c\u5f97\u201c\u5580\u5693\u201d\u4e00\u58f0\uff0c\u8def\u660e\u7684\u957f\u5251\u5df2\u65ad\u4e86\u4e00\u622a\uff0c\u51fa\u5176\u4e0d\u610f\uff0c\u51e0\u4e4e\u8131 \u624b\u98de\u53bb\u3002\u4e91\u91cd\u8fd9\u5200\u6765\u52bf\u6781\u731b\uff0c\u4e00\u5200\u5288\u8fc7\uff0c\u4f59\u52bf\u672a\u8870\uff0c\u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c\u53c8\u4e0e\u8def\u4eae\u7684\u94c1\u724c\u78b0\u4e2a\u6b63 \u7740\uff0c\u4e24\u4eba\u90fd\u7ed9\u9707\u5f97\u864e\u53e3\u53d1\u70ed\u3002 \u3000\u3000\u8def\u4eae\u6012\u9053\uff1a\u201c\u4f60\u6709\u591a\u5927\u672c\u9886\uff0c\u80c6\u6562\u51fa\u53e3\u72c2\u8a00\uff01\u201d\u624b\u8155\u4e00\u7ffb\u94c1\u724c\u4e00\u633a\uff0c\u7adf\u7136\u4e00\u62db\u201c\u6cf0\u5c71\u538b \u9876\u201d\uff0c\u5f53\u5934\u75be\u5288\u3002\u8def\u5bb6\u7684\u6df7\u5143\u724c\u6cd5\uff0c\u4e3b\u529b\u5c31\u662f\u8fd9\u9762\u94c1\u724c\uff0c\u8def\u4eae\u7684\u6c14\u529b\u8fdc\u5728\u4ed6\u54e5\u54e5\u4e4b\u4e0a\uff0c\u8fd9\u94c1 \u724c\u4e00\u538b\uff0c\u5c11\u8bf4\u4e5f\u6709\u5343\u65a4\u4e4b\u529b\uff01 \u3000\u3000\u4e91\u91cd\u624b\u8155\u4e00\u7ffb\uff0c\u8f6c\u8fc7\u5200\u80cc\uff0c\u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c\u53c8\u78d5\u5728\u94c1\u724c\u4e4b\u4e0a\uff0c\u8fd9\u4e00\u4e0b\u6765\u52bf\u66f4\u731b\uff0c\u53ea\u89c1\u706b \u82b1\u98de\u5904\uff0c\u8def\u4eae\u7684\u94c1\u724c\u5d29\u4e86\u4e00\u4e2a\u7f3a\u53e3\uff0c\u4e91\u91cd\u7684\u5200\u5934\u4e5f\u5f2f\u6210\u94a9\u5f62\u3002\u53cc\u65b9\u90fd\u5403\u4e86\u4e00\u60ca\uff0c\u5404\u9000\u4e09\u6b65\uff0c \u8def\u660e\u8d70\u504f\u950b\u75be\u4e0a\uff0c\u53c8\u518d\u53d1\u52a8\u653b\u52bf\uff0c\u9752\u94a2\u5251\u5bd2\u5149\u4e00\u95ea\uff0c\u5374\u523a\u5411\u6fb9\u53f0\u955c\u660e\uff0c\u6fb9\u53f0\u955c\u660e\u7bad\u4f24\u53d1\u4f5c\uff0c \u624b\u8f6f\u65e0\u529b\uff0c\u865a\u67b6\u4e00\u5251\uff0c\u9669\u9669\u8dcc\u5012\u3002\u4e91\u91cd\u5927\u543c\u4e00\u58f0\uff0c\u8f6c\u5200\u75be\u5288\uff0c\u8def\u4eae\u7684\u94c1\u724c\u53c8\u538b\u4e86\u8fc7\u6765\uff0c\u4e91\u91cd \u6321\u5728\u6fb9\u53f0\u955c\u660e\u8eab\u524d\u4e0d\u987e\u751f\u6b7b\uff0c\u547c\u547c\u547c\u8fde\u5288\u4e09\u5200\uff0c\u5c06\u8def\u5bb6\u5144\u5f1f\u903c\u9000\u51e0\u6b65\u3002\u6fb9\u53f0\u955c\u660e\u8eb2\u5230\u5c4b\u89d2\uff0c \u53eb\u9053\uff1a\u201c\u4e91\u5927\u54e5\uff0c\u4f60\u5c3d\u7ba1\u6740\u654c\uff0c\u4e0d\u5fc5\u987e\u6211\u3002\u201d \u3000\u3000\u4e91\u91cd\u5598\u4e86\u53e3\u6c14\uff0c\u6325\u5200\u53c8\u4e0a\uff0c\u8def\u660e\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u771f\u662f\u4e0d\u5230\u9ec4\u6cb3\u5fc3\u4e0d\u6b7b\uff0c\u4e0d\u89c1\u68fa\u6750\u4e0d\u6d41\u6cea\uff0c \u4e14\u53eb\u4f60\u77e5\u9053\u5389\u5bb3\uff0c\u770b\u5251\uff01\u201d\u4e00\u53e3\u5251\u6709\u5982\u6bd2\u86c7\u5410\u4fe1\uff0c\u968f\u7740\u94c1\u724c\u8fdb\u9000\u4f38\u7f29\uff0c\u5251\u5251\u6307\u5411\u4e91\u91cd\u8981\u5bb3\u3002 \u4e91\u91cd\u5c55\u5f00\u4e94\u864e\u65ad\u95e8\u5200\u6cd5\uff0c\u6d51\u8eab\u4e0a\u4e0b\u6cdb\u8d77\u4e00\u7247\u94f6\u5149\uff0c\u65f6\u4e0d\u65f6\u4e5f\u5728\u5200\u5149\u4e4b\u4e2d\u53d1\u638c\u51fb\u654c\uff0c\u53cc\u65b9\u90fd\u662f \u6709\u653b\u6709\u5b88\uff0c\u5728\u65b9\u5706\u4e0d\u53ca\u4e00\u4e08\u7684\u6597\u5ba4\u4e4b\u5185\uff0c\u6597\u5f97\u975e\u5e38\u6fc0\u70c8\uff0c\u5730\u65b9\u72ed\u7a84\uff0c\u5927\u5bb6\u95ea\u907f\u90fd\u96be\uff0c\u51e0\u4e4e\u6bcf \u4e00\u62db\u90fd\u662f\u786c\u6253\u786c\u62fc\u3002 \u3000\u3000\u8def\u660e\u3001\u8def\u4eae\u5251\u76fe\u9f50\u65bd\uff0c\u8bad\u7ec3\u6709\u7d20\uff0c\u914d\u5408\u5f97\u5341\u5206\u7eaf\u719f\uff0c\u6216\u8005\u5251\u968f\u76fe\u53d1\uff0c\u6216\u8005\u76fe\u63a9\u5251\u653b\uff0c\u5e26 \u5b88\u5e26\u653b\uff0c\u9996\u5c3e\u547c\u5e94\uff0c\u7aef\u7684\u662f\u65e0\u61c8\u53ef\u51fb\u3002\u5f53\u5e74\u8def\u660e\u3001\u8def\u4eae\u66fe\u5728\u4eac\u4e2d\u4e0e\u5f20\u98ce\u5e9c\u6bd4\u6b66\uff0c\u5f20\u98ce\u5e9c\u4e5f\u5360 \u4e0d\u4e86\u4ed6\u4eec\u7684\u4fbf\u5b9c\uff0c\u4e91\u91cd\u6b66\u529f\u7565\u900a\u4e8e\u5f20\u98ce\u5e9c\uff0c\u66f4\u662f\u89c9\u5f97\u5403\u529b\uff0c\u517c\u4e4b\u4e91\u91cd\u5728\u767e\u4e07\u519b\u4e2d\u6740\u51fa\uff0c\u53c8\u5954 \u6ce2\u4e86\u534a\u65e5\uff0c\u6c14\u529b\u66f4\u662f\u51cf\u4e86\u51e0\u5206\uff0c\u6597\u4e86\u4e00\u767e\u4f59\u62db\uff0c\u6e10\u89c9\u529b\u4e0d\u4ece\u5fc3\uff0c\u6240\u53d1\u7684\u62db\u6570\u6bcf\u53d7\u654c\u4eba\u7275\u5236\uff0c \u653b\u4e0d\u51fa\u53bb\u3002 \u3000\u3000\u53c8\u6597\u4e86\u4e8c\u4e09\u5341\u62db\uff0c\u8def\u5bb6\u5144\u5f1f\u653b\u52bf\u66f4\u76db\uff0c\u8def\u660e\u7b11\u9053\uff1a\u201c\u4e91\u91cd\u4f60\u8fd8\u4e0d\u670d\u5417\uff1f\u5982\u4eca\u629b\u5200\u8ba4\u8d25\uff0c \u6211\u4eec\u5c1a\u53ef\u9976\u4f60\u4e0d\u6b7b\u3002\u201d\u4e91\u91cd\u5927\u6012\u54ac\u7740\u7259\u6839\uff0c\u53c8\u5288\u51e0\u5200\uff0c\u53ea\u89c9\u654c\u4eba\u724c\u91cd\u5982\u5c71\uff0c\u538b\u529b\u8d8a\u6765\u8d8a\u91cd\uff0c \u5b9e\u662f\u96be\u4ee5\u62b5\u654c\uff0c\u4e0d\u7531\u5f97\u51c9\u4e86\u534a\u622a\uff0c\u5fc3\u9053\uff1a\u201c\u6211\u4fbf\u6b7b\u4e86\uff0c\u4e5f\u4e0d\u7518\u53d7\u7ad6\u5b50\u4e4b\u8fb1\uff01\u201d\u6b63\u60f3\u6a2a\u5200\u81ea \u520e\uff0c\u731b\u5730\u60f3\u9053\uff1a\u201c\u4f46\u6211\u82e5\u6b7b\u4e86\uff0c\u6fb9\u53f0\u59b9\u5b50\u5c82\u4e0d\u662f\u8981\u843d\u5728\u8d3c\u5b50\u624b\u4e2d\uff1f\u201d\u659c\u773c\u4e00\u77a5\uff0c\u53ea\u89c1\u6fb9\u53f0\u955c \u660e\u6ee1\u9762\u5fe7\u6025\u7684\u795e\u60c5\uff0c\u76ee\u4e0d\u8f6c\u775b\u5730\u6ce8\u89c6\u81ea\u5df1\uff0c\u773c\u5149\u4e4b\u4e2d\uff0c\u542b\u6709\u7126\u6025\u3001\u611f\u6fc0\u3001\u9f13\u52b1\u3001\u4fe1\u6258\u8bf8\u822c\u8868 \u60c5\u3002\u4e91\u91cd\u7cbe\u795e\u4e00\u632f\uff0c\u5ffd\u7136\u5927\u559d\u4e00\u58f0\uff0c\u731b\u5730\u4e00\u638c\u626b\u53bb\uff0c\u8fd9\u4e00\u638c\u4e43\u662f\u4ed6\u62fc\u4e86\u5168\u8eab\u7684\u6c14\u529b\uff0c\u65bd\u5c55\u91d1 \u521a\u5927\u529b\u624b\u6700\u731b\u7684\u6740\u7740\uff0c\u7aef\u7684\u662f\u53d1\u82e5\u96f7\u9706\u3002\u53ea\u542c\u5f97\u4e00\u58f0\u5de8\u54cd\uff0c\u638c\u7f18\u62cd\u5728\u94c1\u724c\u4e4b\u4e0a\uff0c\u8def\u4eae\u5927\u53eb\u4e00 \u58f0\uff0c\u94c1\u724c\u9707\u5f97\u8131\u624b\u98de\u53bb\uff0c\u864e\u53e3\u6d41\u8840\uff0c\u4e00\u6761\u81c2\u818a\uff0c\u9ebb\u6728\u5f97\u4e0d\u80fd\u52a8\u5f39\u3002 \u3000\u3000\u8fd9\u4e00\u4e0b\u5927\u51fa\u8def\u5bb6\u5144\u5f1f\u610f\u6599\u4e4b\u5916\uff0c\u4e91\u91cd\u4e00\u62db\u5f97\u624b\uff0c\u72b6\u5982\u75af\u864e\u75be\u6251\u800c\u524d\uff0c\u5927\u559d\u4e00\u58f0\uff0c\u53c8\u662f\u4e00 \u638c\uff0c\u5411\u8def\u660e\u62e6\u8170\u731b\u626b\u3002\u8def\u660e\u8fd8\u7b97\u673a\u7075\uff0c\u6025\u5fd9\u95ea\u907f\uff0c\u4e91\u91cd\u4e00\u638c\u5288\u4e0b\uff0c\u8f6c\u624b\u4e00\u62ff\uff0c\u5c06\u8def\u660e\u7684\u957f\u5251 \u593a\u5230\u624b\u4e2d\uff0c\u201c\u5580\u5693\u201d\u4e00\u58f0\uff0c\u90a3\u67c4\u957f\u5251\u4e5f\u6298\u65ad\u4e86\u3002\u8def\u660e\u3001\u8def\u4eae\u5fc3\u610f\u5982\u4e00\uff0c\u4e0d\u5fc5\u62db\u547c\uff0c\u5df2\u540c\u65f6\u9000 \u51fa\u5c4b\u5916\uff0c\u4e24\u5144\u5f1f\u5ffd\u5730\u540c\u58f0\u5927\u7b11\u3002 \u3000\u3000\u4e91\u91cd\u4e0d\u7531\u5f97\u6014\u4e86\u4e00\u6014\uff0c\u6b63\u60f3\u6251\u51fa\uff0c\u731b\u542c\u5f97\u6fb9\u53f0\u955c\u660e\u53eb\u58f0\uff1a\u201c\u4e0d\u597d\uff01\u201d\u90a3\u5c4b\u5b50\u7a81\u7136\u65cb\u8f6c\u8d77 \u6765\uff0c\u7728\u773c\u4e4b\u95f4\uff0c\u5929\u660f\u5730\u6697\uff0c\u65e5\u6708\u65e0\u5149\uff0c\u8f70\u9686\u9686\u51e0\u58f0\u5de8\u54cd\u3002\u539f\u6765\u8fd9\u95f4\u82b1\u5385\u7adf\u7136\u5e03\u6709\u673a\u5173\uff0c\u56db\u9762 \u90fd\u5d4c\u6709\u94a2\u677f\uff0c\u8fd9\u65f6\u4e00\u9f50\u843d\u4e0b\uff0c\u987f\u65f6\u5c06\u8fd9\u95f4\u6b3e\u5ba2\u7684\u82b1\u5385\u53d8\u6210\u4e86\u56da\u4eba\u7684\u76d1\u72f1\u3002 \u3000\u3000\u4e91\u91cd\u66b4\u8df3\u5982\u96f7\uff0c\u4e00\u638c\u51fb\u53bb\uff0c\u53ea\u75db\u5f97\u4ed6\u80f3\u818a\u51e0\u4e4e\u6298\u4e86\uff0c\u54ea\u91cc\u52a8\u5f97\u5206\u6beb\u3002\u5916\u9762\u8def\u660e\u3001\u8def\u4eae\u7b11 \u9053\uff1a\u201c\u4e91\u91cd\u4f60\u5c11\u53d1\u813e\u6c14\uff0c\u5728\u91cc\u9762\u9759\u9759\u8eba\u51e0\u5929\u5427\uff0c\u53ea\u662f\u6055\u6211\u4eec\u4e0d\u62db\u5f85\u4f60\u4e86\u3002\u201d\u8bdd\u4e2d\u4e4b\u610f\uff0c\u660e\u660e \u662f\u8981\u997f\u4e91\u91cd\u51e0\u5929\uff0c\u7136\u540e\u518d\u6765\u6536\u62fe\u4ed6\u3002\u4e91\u91cd\u53c8\u6012\u53c8\u6c14\uff0c\u53ea\u662f\u65e0\u53ef\u5948\u4f55\u3002 \u3000\u3000\u539f\u6765\u8def\u660e\u3001\u8def\u4eae\u4e43\u662f\u524d\u51e0\u5929\u4ece\u4eac\u57ce\u4e2d\u79c1\u81ea\u9003\u51fa\u6765\u7684\uff0c\u90a3\u65f6\u4e8e\u8c26\u5df2\u7acb\u4e86\u65b0\u7687\u5e1d\uff0c\u6b63\u5728\u5927\u6355 \u738b\u632f\u7684\u515a\u7fbd\u3002\u8def\u660e\u3001\u8def\u4eae\u5e73\u65e5\u51fa\u5165\u738b\u632f\u5e9c\u4e2d\uff0c\u4e92\u76f8\u52fe\u7ed3\uff0c\u8bb8\u591a\u4eba\u90fd\u77e5\u9053\u4ed6\u4eec\u662f\u738b\u632f\u7684\u5fc3\u8179\uff0c \u4ed6\u4eec\u4e5f\u751a\u673a\u7075\uff0c\u4e00\u89c1\u98ce\u58f0\u4e0d\u597d\uff0c\u7acb\u523b\u9003\u8dd1\uff0c\u5148\u56de\u5bb6\u4e2d\u6599\u7406\uff0c\u6b63\u60f3\u5efa\u7acb\u4e00\u4ef6\u529f\u52b3\uff0c\u4ee5\u4f5c\u6295\u5954\u74e6 \u523a\u7684\u89c1\u9762\u4e4b\u793c\uff0c\u6070\u6070\u9047\u7740\u4e91\u91cd\u5230\u6765\uff0c\u662f\u4ee5\u4fbf\u65bd\u6bd2\u624b\u3002 \u3000\u3000\u4e91\u91cd\u5728\u9ed1\u6697\u4e4b\u4e2d\u6478\u7d22\uff0c\u6fb9\u53f0\u955c\u660e\u9053\uff1a\u201c\u55ef\uff0c\u6211\u5728\u8fd9\u513f\u3002\u201d\u4e91\u91cd\u5c0f\u5fc3\u7ffc\u7ffc\u5730\u6328\u8fd1\u8fc7\u53bb\uff0c\u5ffd \u542c\u5f97\u6fb9\u53f0\u955c\u660e\u201c\u54ce\u54df\u201d\u4e00\u58f0\u53eb\u5c06\u8d77\u6765\uff0c\u539f\u6765\u4e91\u91cd\u78b0\u7740\u5979\u7684\u4f24\u53e3\u3002\u4e91\u91cd\u62b1\u6b49\u9053\uff1a\u201c\u6fb9\u53f0\u59d1\u5a18\uff0c \u6211\u6b7b\u4e0d\u8db3\u60dc\uff0c\u53ea\u662f\u4eca\u65e5\u7d2f\u4e86\u4f60\u4e86\u3002\u201d\u6fb9\u53f0\u955c\u660e\u672c\u60f3\u9a82\u4ed6\u6bdb\u624b\u6bdb\u811a\u7684\uff0c\u542c\u4ed6\u4e00\u8bf4\uff0c\u53cd\u89c9\u4e0d\u5b89\uff0c \u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u4e0d\uff0c\u662f\u6211\u7d2f\u4e86\u4f60\u4e86\uff0c\u4f60\u672c\u6765\u53ef\u4ee5\u9003\u51fa\u53bb\u7684\u3002\u201d \u3000\u3000\u4e91\u91cd\u5fc3\u4e2d\u751c\u4e1d\u4e1d\u7684\uff0c\u9053\uff1a\u201c\u4f60\u4f24\u53e3\u75db\u5417\uff1f\u201d\u6fb9\u53f0\u955c\u660e\u9053\uff1a\u201c\u53cd\u6b63\u54b1\u4eec\u90fd\u662f\u8981\u6b7b\u7684\u4e86\uff0c\u8fd8 \u7ba1\u5b83\u75db\u4e0e\u4e0d\u75db\uff1f\u201d\u4e91\u91cd\u9053\uff1a\u201c\u4e0d\u6211\u4e0d\u613f\u610f\u89c1\u4f60\u75db\u82e6\u3002\u201d\u5ba4\u4e2d\u6f06\u9ed1\u5982\u58a8\uff0c\u9664\u4e86\u6fb9\u53f0\u955c\u660e\u7684\u526a\u6c34 \u53cc\u77b3\u4e4b\u5916\uff0c\u4e91\u91cd\u5176\u5b9e\u5e76\u6ca1\u89c1\u4ec0\u4e48\u3002\u6fb9\u53f0\u955c\u660e\u7ecf\u4e86\u8fd9\u573a\u60a3\u96be\uff0c\u5bf9\u4e91\u91cd\u618e\u6076\u7684\u5fc3\u60c5\u5df2\u51cf\u4e86\u51e0\u5206\uff0c \u542c\u4e86\u8fd9\u8bdd\uff0c\u66f4\u662f\u5fc3\u4e2d\u611f\u52a8\uff0c\u4f4e\u5934\u4e0d\u8bed\u3002\u4e91\u91cd\u9053\uff1a\u201c\u4f60\u89e3\u4e0b\u8863\u670d\uff0c\u8ba9\u6211\u7ed9\u4f60\u6577\u836f\u3002\u201d\u6cbb\u5916\u4f24\u7684 \u91d1\u521b\u4f24\uff0c\u4e00\u822c\u4f1a\u6b66\u4e4b\u4eba\uff0c\u90fd\u662f\u968f\u8eab\u5907\u7740\uff0c\u4e0d\u8fc7\u9002\u624d\u5306\u5306\u9003\u547d\uff0c\u65e0\u6687\u6577\u4f24\u7f62\u4e86\u3002\u4e91\u91cd\u4e00\u9762\u8bf4 \u8bdd\uff0c\u4e00\u9762\u8f7b\u8f7b\u5730\u4f38\u624b\u8fc7\u53bb\uff0c\u9053\uff1a\u201c\u4f60\u62ff\u7740\u6211\u7684\u624b\uff0c\u5f15\u5230\u4f24\u53e3\u4e0a\u53bb\u3002\u201d\u6fb9\u53f0\u955c\u660e\u9762\u4e0a\u4e00\u70ed\uff0c\u4f46 \u4e00\u60f3\u5728\u8fd9\u6697\u5ba4\u4e4b\u4e2d\uff0c\u89e3\u9664\u4e86\u8863\u88f3\uff0c\u4e5f\u65e0\u5173\u7cfb\uff0c\u5979\u6027\u60c5\u672c\u6765\u723d\u6717\u8c6a\u8fc8\uff0c\u4fbf\u4e0d\u63a8\u5f00\u4e91\u91cd\u7684\u624b\uff0c\u89e3 \u4e86\u4e0a\u8863\uff0c\u8ba9\u4ed6\u6577\u4f24\u3002 \u3000\u3000\u6fb9\u53f0\u955c\u660e\u7684\u7bad\u4f24\uff0c\u4e00\u5728\u80a9\u5934\uff0c\u4e00\u5728\u9888\u9879\u4e0b\u9762\u7684\u80cc\u6881\uff0c\u4e91\u91cd\u66ff\u5979\u6cbb\u4f24\uff0c\u89e6\u624b\u4e4b\u5904\uff0c\u80a4\u82e5\u51dd \u8102\uff0c\u53ea\u611f\u5fc3\u4e2d\u5feb\u7f8e\uff0c\u96be\u4ee5\u5f62\u5bb9\u3002\u5ffd\u542c\u5f97\u6fb9\u53f0\u955c\u660e\u5e7d\u5e7d\u8bf4\u9053\uff1a\u201c\u4f60\u82f1\u96c4\u5e74\u5c11\uff0c\u9ad8\u6444\u79d1\u540d\uff0c\u8fd9\u6837 \u4e0d\u660e\u4e0d\u767d\u7684\u6b7b\u4e86\uff0c\u5c82\u4e0d\u53ef\u60dc\uff01\u201d\u4e91\u91cd\u9053\uff1a\u201c\u5f20\u4e39\u67ab\u6240\u6258\u7684\u5b9d\u85cf\uff0c\u4eca\u65e5\u5b9a\u53ef\u62a4\u9001\u81f3\u4eac\uff0c\u6211\u4e00\u5fc3 \u62a5\u56fd\uff0c\u800c\u4eca\u603b\u7b97\u505a\u4e86\u4e00\u70b9\u4e8b\u60c5\uff0c\u6b7b\u4ea6\u65e0\u61be\u3002\u201d\u6fb9\u53f0\u955c\u660e\u5fc3\u6f6e\u6ce2\u6d8c\uff0c\u5bf9\u4e91\u91cd\u7684\u89c2\u611f\u53c8\u6539\u4e86\u51e0 \u5206\uff0c\u5fc3\u9053\uff1a\u201c\u6b64\u4eba\u867d\u7136\u6027\u60c5\u56fa\u6267\uff0c\u6c14\u91cf\u4e5f\u7a0d\u5acc\u6d45\u7a84\uff0c\u5374\u4e5f\u8fd8\u6709\u53ef\u53d6\u4e4b\u5904\u3002\u201d \u3000\u3000\u6fb9\u53f0\u955c\u660e\u4e0e\u4e91\u91cd\u5728\u6697\u5ba4\u4e4b\u4e2d\u9ed8\u9ed8\u76f8\u5bf9\uff0c\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u5c11\u65f6\u5019\uff0c\u5ffd\u542c\u5f97\u5916\u9762\u9a6c\u58f0\u5636\u5636\u6765\u7684 \u4f3c\u4e4e\u4e0d\u6b62\u4e00\u9a91\u3002\u4e91\u91cd\u8bf4\u9053\uff1a\u201c\u4e0d\u597d\u3002\u5317\u4eac\u5728\u654c\u4eba\u5305\u56f4\u4e4b\u4e2d\uff0c\u8fd9\u6765\u7684\u5b9a\u662f\u74e6\u523a\u519b\u5175\uff0c\u82e5\u7136\u4ed6\u4eec \u5c06\u6211\u4eec\u64d2\u53bb\u732e\u7ed9\u74e6\u523a\uff0c\u90a3\u6211\u5c31\u5b81\u613f\u81ea\u6740\uff0c\u4f60\u53ef\u8981\u539f\u8c05\u6211\u4e0d\u80fd\u518d\u7167\u62a4\u4f60\u4e86\u3002\u201d\u6fb9\u53f0\u955c\u660e\u7b11\u9053\uff1a \u201c\u4f60\u6b7b\u4e86\u96be\u9053\u6211\u8fd8\u72ec\u81ea\u6d3b\u5417\uff1f\u6211\u82e5\u5fcd\u8fb1\u5077\u751f\uff0c\u4e5f\u5bf9\u4e0d\u4f4f\u5f20\u4e39\u67ab\u5440\uff01\u201d\u4e91\u91cd\u542c\u4e86\uff0c\u5fc3\u4e2d\u4e00\u9635\u9163 \u7545\uff0c\u4f46\u542c\u5979\u63d0\u5230\u4e86\u5f20\u4e39\u67ab\uff0c\u5374\u53c8\u5f88\u4e0d\u81ea\u7136\uff0c\u5fc3\u9053\uff1a\u201c\u539f\u6765\u5979\u628a\u5f20\u4e39\u67ab\u770b\u5f97\u6bd4\u6211\u91cd\u8981\u5f97\u591a\u3002\u201d \u3000\u3000\u53ea\u542c\u5f97\u90a3\u9a6c\u8e44\u58f0\u6e10\u6e10\u6765\u8fd1\uff0c\u5230\u4e86\u95e8\u524d\u505c\u4e0b\uff0c\u8fc7\u4e86\u4e00\u4f1a\uff0c\u4fbf\u542c\u5f97\u811a\u6b65\u4e4b\u58f0\u8d70\u6765\uff0c\u4e91\u91cd\u5fcd\u4e0d \u4f4f\u548c\u6fb9\u53f0\u955c\u660e\u53cc\u624b\u76f8\u63e1\uff0c\u53c8\u8fc7\u4e86\u4e00\u4f1a\uff0c\u5ffd\u542c\u5f97\u6709\u4e00\u4e2a\u7c97\u72b7\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u8fd9\u91cc\u9762\u5173\u7684\u662f\u4ec0\u4e48 \u4eba\u5440\uff1f\u201d\u4e91\u91cd\u5413\u4e86\u4e00\u8df3\uff0c\u5728\u6fb9\u53f0\u955c\u660e\u8033\u8fb9\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u6fb9\u53f0\u706d\u660e\uff01\u662f\u74e6\u523a\u7684\u7b2c\u4e00\u52c7 \u58eb\u3002\u201d\u6fb9\u53f0\u955c\u660e\u9053\uff1a\u201c\u55ef\uff0c\u6211\u4e5f\u542c\u51fa\u6765\u4e86\uff0c\u4ed6\u662f\u6211\u7684\u5802\u5144\uff0c\u4eca\u5e74\u4e94\u6708\u4e4b\u95f4\uff0c\u66fe\u6084\u6084\u5730\u5230\u8fc7\u82cf \u5dde\uff0c\u5728\u6211\u4eec\u7684\u6d1e\u5ead\u5c71\u5e84\u4f4f\u4e86\u597d\u51e0\u5929\u3002\u201d\u4e91\u91cd\u5c1a\u672a\u5341\u5206\u6e05\u695a\u6fb9\u53f0\u4e00\u5bb6\u7684\u5e95\u7ec6\uff0c\u5fc3\u4e2d\u4ecd\u662f\u60ca\u7591\u4ea4 \u96c6\uff0c\u60f3\u9053\uff1a\u201c\u6fb9\u53f0\u706d\u660e\u6b66\u529f\u9ad8\u6781\uff0c\u82e5\u7136\u7ed9\u4ed6\u64d2\u7740\uff0c\u60f3\u81ea\u6740\u4e5f\u4e0d\u53ef\u80fd\u3002\u201d\u53ea\u542c\u5f97\u6fb9\u53f0\u955c\u660e\u53c8 \u9053\uff1a\u201c\u4f60\u4e0d\u8981\u56b7\uff0c\u54b1\u4eec\u4eca\u65e5\u547d\u4e0d\u8be5\u7edd\uff0c\u4f60\u542c\u6211\u7684\u54e5\u54e5\u548c\u4ed6\u4eec\u8bf4\u4e9b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u53ea\u542c\u5f97\u8def\u660e\u7b54\u9053\uff1a\u201c\u7980\u544a\u6fb9\u53f0\u5c06\u519b\uff0c\u8fd9\u91cc\u9762\u5173\u7684\u662f\u975e\u5e38\u4eba\u7269\uff01\u201d\u6fb9\u53f0\u706d\u660e\u9053\uff1a\u201c\u4ec0\u4e48\u4eba \u7269\uff1f\u201d\u8def\u4eae\u9053\uff1a\u201c\u8bf4\u51fa\u6765\u597d\u4ee4\u5c06\u519b\u6b22\u559c\uff0c\u8fd9\u91cc\u9762\u5173\u7684\uff0c\u4e00\u4e2a\u662f\u4eca\u5e74\u6b66\u79d1\u7684\u6b66\u72b6\u5143\u4e91\u91cd\uff0c\u4ee5\u524d \u662f\u5fa1\u6797\u519b\u7684\u7edf\u9886\uff0c\u5c06\u519b\u4e0a\u6b21\u6765\u4eac\uff0c\u60f3\u5fc5\u4e5f\u89c1\u8fc7\u4ed6\uff0c\u4ed6\u5728\u5fa1\u6797\u519b\u4e2d\u7684\u5730\u4f4d\uff0c\u5982\u4eca\u4ec5\u4ec5\u5728\u5f20\u98ce\u5e9c \u4e4b\u4e0b\uff0c\u8fd9\u53ef\u4e0d\u662f\u91cd\u8981\u7684\u4eba\u7269\u5417\uff1f\u53e6\u4e00\u4f4d\u662f\u4e2a\u5973\u7684\uff0c\u542c\u8bf4\u662f\u6c5f\u82cf\u6765\u7684\u4ec0\u4e48\u4e49\u519b\u5973\u5c06\uff0c\u54c8\uff0c\u8fd9\u4e2a \u5973\u7684\u957f\u5f97\u8fd8\u771f\u662f\u6f02\u4eae\u5462\uff01\u6211\u672c\u6765\u8981\u7b49\u4ed6\u4eec\u997f\u4e86\u51e0\u5929\uff0c\u518d\u5c06\u4ed6\u4eec\u7f1a\u5230\u5927\u8425\u5448\u732e\u7684\uff0c\u5c06\u519b\u6765\u5f97\u6b63 \u597d\uff0c\u8fd9\u4e24\u4eba\u5c31\u4efb\u7531\u5c06\u519b\u5904\u7f6e\u4e86\u3002\u201d\u6fb9\u53f0\u706d\u660e\u201c\u54a6\u201d\u4e00\u4e86\u58f0\uff0c\u9053\uff1a\u201c\u662f\u6c5f\u82cf\u6765\u7684\u5973\u5b50\uff1f\u54e6\uff0c\u5979 \u59d3\u4ec0\u4e48\u5462\uff1f\u201d\u8def\u660e\u9053\uff1a\u201c\u6211\u4eec\u5c1a\u6211\u6687\u5ba1\u95ee\u5979\uff0c\u5c06\u519b\u770b\u4e86\uff0c\u82e5\u7136\u6b22\u559c\uff0c\u7559\u5979\u4e0b\u6765\uff0c\u6211\u4eec\u7edd\u4e0d\u5728 \u592a\u5e08\u9762\u524d\uff0c\u900f\u9732\u534a\u53e5\u3002\u201d\u592a\u5e08\u6307\u7684\u4e43\u662f\u4e5f\u5148\uff0c\u8def\u660e\u3001\u8def\u4eae\u7adf\u7136\u628a\u6fb9\u53f0\u955c\u660e\u5f53\u4f5c\u793c\u7269\uff0c\u732e\u7ed9\u5979 \u7684\u54e5\u54e5\uff0c\u6fb9\u53f0\u955c\u660e\u542c\u4e86\uff0c\u53c8\u597d\u6c14\uff0c\u53c8\u597d\u7b11\u3002 \u3000\u3000\u53ea\u542c\u5f97\u6fb9\u53f0\u706d\u660e\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u4f60\u628a\u4ed6\u4eec\u653e\u51fa\u6765\uff0c\u8ba9\u6211\u770b\u770b\u5427\u3002\u201d\u731b\u7136\u95f4\uff0c\u90a3\u5c4b\u5b50\u53c8\u662f\u4e00 \u9635\u65cb\u8f6c\uff0c\u94a2\u7a97\u4e00\u9f50\u5f00\u542f\uff0c\u4e91\u91cd\u773c\u775b\u4e00\u4eae\uff0c\u91cd\u89c1\u5929\u5149\uff0c\u623f\u95e8\u4e5f\u201c\u5440\u201d\u7684\u4e00\u58f0\u5f00\u4e86\u3002\u4f46\u89c1\u6fb9\u53f0\u706d \u660e\u9762\u4f3c\u5bd2\u971c\uff0c\u51db\u7136\u95ee\u9053\uff1a\u201c\u5c31\u662f\u4ed6\u4eec\u5417\uff1f\u201d\u8def\u660e\u9053\uff1a\u201c\u662f\uff0c\u5c06\u519b\u5c31\u662f\u4ed6\u4eec\u3002\u5440\uff0c\u5c06\u519b\uff0c\u53ef\u6709 \u4ec0\u4e48\u4e0d\u5bf9\u5417\uff1f\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u53ea\u542c\u5f97\u201c\u8f70\u201d\u7684\u4e00\u58f0\uff0c\u6fb9\u53f0\u706d\u660e\u51fa\u624b\u5982\u7535\uff0c\u5c06\u8def\u660e\u3001\u8def\u4eae\uff0c\u4e00\u624b \u4e00\u4e2a\u5012\u63d0\u8d77\u6765\uff0c\u628a\u4e24\u5144\u5f1f\u5bf9\u5934\u4e00\u649e\uff0c\u8111\u6d46\u8ff8\u6d41\uff0c\u663e\u89c1\u4e0d\u80fd\u6d3b\u4e86\u3002 \u3000\u3000\u6fb9\u53f0\u955c\u660e\u559c\u6781\u800c\u6ce3\u4e00\u8dc3\u4e0a\u524d\u62b1\u7740\u6fb9\u53f0\u706d\u660e\u9053\uff1a\u201c\u54e5\u54e5\u3002\u201d\u6fb9\u53f0\u706d\u660e\u9053\uff1a\u201c\u5440\uff0c\u4f60\u53d7\u4e86\u7bad \u4f24\uff0c\u8ba9\u6211\u770b\uff0c\u54e6\uff0c\u8fd8\u597d\uff0c\u4e0d\u788d\u4e8b\u7684\u3002\u4f60\u8fd9\u6b21\u8def\u9014\u8f9b\u82e6\uff0c\u53c8\u7ecf\u9669\u96be\uff0c\u521a\u624d\u53c8\u4e2d\u4e86\u8def\u5bb6\u5144\u5f1f\u7684\u5708 \u5957\u60f3\u5fc5\u5413\u574f\u4f60\u4e86\u3002\u4e0d\u8fc7\uff0c\u5c11\u5e74\u4e4b\u4eba\u591a\u7ecf\u9669\u96be\uff0c\u5386\u7ec3\u5386\u7ec3\u4e5f\u597d\u3002\u201d\u4e91\u91cd\u7ad9\u5728\u4e00\u8fb9\uff0c\u6014\u6014\u5730\u770b\u7740 \u6fb9\u53f0\u706d\u660e\uff0c\u8bf4\u4e0d\u51fa\u8bdd\u3002\u6fb9\u53f0\u706d\u660e\u8bf4\u9053\uff1a\u201c\u4e91\u91cd\u5144\uff0c\u771f\u662f\u673a\u7f18\u51d1\u5de7\uff0c\u54b1\u4eec\u53c8\u4f1a\u9762\u4e86\u3002\u8fd9\u6b21\u4f60\u4e0d \u5fc5\u518d\u548c\u6211\u62fc\u6597\u4e86\u3002\u201d\u7b11\u4e86\u4e00\u7b11\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u8fd9\u6b21\u5230\u82cf\u5dde\uff0c\u53ef\u89c1\u5230\u4e86\u5f20\u4e39\u67ab\u4e48\uff1f\u201d\u4e91\u91cd\u9053\uff1a \u201c\u89c1\u7740\u4e86\u3002\u201d\u6fb9\u53f0\u706d\u660e\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u5bb6\u7684\u4ec7\u6068\u548c\u89e3\u4e86\uff1f\u201d\u4e91\u91cd\u9ed8\u9ed8\u4e0d\u7b54\uff0c\u6fb9\u53f0\u955c\u660e\u6447\u4e86\u6447 \u5934\u3002\u6fb9\u53f0\u706d\u660e\u9053\uff1a\u201c\u8fd9\u662f\u4f60\u4eec\u5bb6\u4e8b\uff0c\u6211\u662f\u5916\u4eba\uff0c\u4e0d\u4fbf\u591a\u7ba1\u3002\u53ea\u662f\u6211\u6258\u4f60\u51e0\u53e5\u8bdd\uff0c\u4f60\u8fd9\u6b21\u5165 \u4eac\uff0c\u89c1\u5230\u5f20\u4e39\u67ab\uff0c\u53ef\u53eb\u4ed6\u5bbd\u5fc3\uff0c\u73b0\u5728\u5317\u4eac\u4e4b\u56f4\u5df2\u89e3\uff0c\u74e6\u523a\u5927\u519b\uff0c\u4e0d\u65e5\u4e4b\u5185\uff0c\u6050\u6015\u4e5f\u8981\u73ed\u5e08\u56de \u56fd\u4e86\u3002\u201d\u6fb9\u53f0\u955c\u660e\u559c\u9053\uff1a\u201c\u554a\uff0c\u771f\u7684\uff1f\u54e5\u54e5\uff0c\u8fd9\u662f\u4e5f\u5148\u544a\u8bc9\u4f60\u7684\u4e48\uff1f\u201d \u3000\u3000\u6fb9\u53f0\u706d\u660e\u9053\uff1a\u201c\u4ed6\u624d\u4e0d\u4f1a\u4eb2\u53e3\u544a\u8bc9\u6211\u5462\u3002\u53ea\u662f\u770b\u8fd9\u5f62\u52bf\uff0c\u4e5f\u975e\u9000\u5175\u4e0d\u53ef\u3002\u6211\u672c\u6765\u662f\u5949\u4ed6 \u4e4b\u547d\uff0c\u5728\u96c1\u95e8\u5173\u7559\u5b88\u7684\uff0c\u4ed6\u6015\u660e\u671d\u7684\u5404\u8def\u4e49\u519b\u9f50\u96c6\uff0c\u65ad\u4ed6\u7684\u540e\u8def\uff0c\u53eb\u6211\u5c06\u96c1\u95e8\u5173\u7684\u5175\uff0c\u5206\u4e86 \u4e00\u534a\uff0c\u8d76\u6765\u63a5\u5e94\u4ed6\u3002\u6211\u6697\u4e2d\u901a\u77e5\u4e86\u91d1\u5200\u5be8\u4e3b\uff0c\u53eb\u4ed6\u4eec\u5728\u6211\u8d77\u7a0b\u4e4b\u65e5\uff0c\u6697\u88ad\u96c1\u95e8\u5173\uff0c\u524d\u65e5\u63a5\u5230 \u6d88\u606f\uff0c\u8bf4\u662f\u96c1\u95e8\u5173\u7684\u74e6\u523a\u5b88\u5175\u548c\u5de1\u903b\u5173\u5916\u7684\u6d41\u52a8\u9a91\u5175\uff0c\u7ed9\u91d1\u5200\u5be8\u4e3b\u5947\u5175\u7a81\u88ad\uff0c\u4f24\u4ea1\u4e86\u4e00\u5927 \u534a\uff0c\u4e5f\u5148\u7edd\u5bf9\u60f3\u4e0d\u5230\u662f\u6211\u4ece\u4e2d\u7ed9\u4ed6\u6363\u4e71\uff0c\u53ea\u9053\u662f\u56e0\u6211\u8d70\u540e\uff0c\u96c1\u95e8\u5173\u5175\u529b\u5206\u8584\uff0c\u6240\u4ee5\u624d\u6709\u6b64 \u8d25\u3002\u8fd9\u4ef6\u4e8b\u5f88\u4ee4\u519b\u5fc3\u9707\u52a8\uff0c\u52a0\u4e4b\u74e6\u523a\u56fd\u5185\u60c5\u5f62\u4e5f\u4e0d\u5b89\u7a33\u3002\u6211\u770b\u4ed6\u4e0d\u51fa\u534a\u6708\uff0c\u5fc5\u7136\u9000\u5175\u3002\u201d \u3000\u3000\u4e91\u91cd\u542c\u5f97\u5446\u4e86\uff0c\u4ed6\u60f3\u4e5f\u60f3\u4e0d\u5230\u6fb9\u53f0\u706d\u660e\u4f1a\u5982\u6b64\u8fd9\u822c\uff0c\u6697\u52a9\u660e\u671d\u3002\u6fb9\u53f0\u955c\u660e\u95ee\u9053\uff1a\u201c\u54b1\u4eec \u7684\u4e3b\u516c\u73b0\u4e0b\u5982\u4f55\uff1f\u201d\u6fb9\u53f0\u955c\u660e\u53e3\u4e2d\u7684\u201c\u4e3b\u516c\u201d\uff0c\u6307\u7684\u4e43\u662f\u5f20\u4e39\u67ab\u7684\u7236\u4eb2\u5f20\u5b97\u5468\uff0c\u4e91\u91cd\u542c\u4ed6\u4eec \u63d0\u8d77\u4ec7\u4eba\u7684\u540d\u5b57\uff0c\u5fc3\u4e2d\u53c8\u662f\u4e00\u6014\u3002\u6fb9\u53f0\u706d\u660e\u82e6\u7b11\u4e00\u58f0\u8bf4\u9053\uff1a\u201c\u4e3b\u516c\u65e5\u6765\u751a\u662f\u82e6\u607c\uff0c\u4ed6\u65e2\u5ff5\u5ff5 \u4e0d\u5fd8\u6536\u590d\u5927\u5468\u7684\u6c5f\u5c71\uff0c\u4f46\u53c8\u4e0d\u613f\u74e6\u523a\u5360\u4e86\u4e2d\u534e\uff0c\u662f\u4ee5\u5fc3\u4e2d\u77db\u76fe\u3002\u6211\u4e5f\u529d\u89e3\u4e0d\u6765\u3002\u201d \u3000\u3000\u6fb9\u53f0\u706d\u660e\u4e00\u770b\u65e5\u5f71\uff0c\u9053\uff1a\u201c\u6211\u5949\u4e5f\u5148\u4e4b\u547d\u6765\u53d6\u8def\u660e\u3001\u8def\u4eae\u56de\u53bb\uff0c\u800c\u4eca\u53ea\u597d\u62a5\u9053\u4ed6\u88ab\u4ec7\u5bb6 \u6740\u4e86\u3002\u65f6\u5019\u4e0d\u65e9\uff0c\u6211\u8be5\u8d70\u5566\u3002\u201d\u8bf4\u7f62\u51fa\u4e86\u8def\u5bb6\uff0c\u4ed6\u5e26\u6765\u7684\u536b\u58eb\u90fd\u5728\u95e8\u5916\u5de1\u903b\uff0c\u81ea\u7136\u4e5f\u548c\u4ed6\u4e00 \u540c\u53bb\u4e86\u3002 \u3000\u3000\u4e91\u91cd\u4e0e\u6fb9\u53f0\u955c\u660e\u5f85\u80e1\u5175\u8d70\u540e\uff0c\u6025\u6025\u8de8\u9a6c\u4e0a\u4eac\uff0c\u5317\u4eac\u4e4b\u56f4\u5df2\u89e3\uff0c\u5468\u56f4\u6570\u5341\u91cc\u5185\u5df2\u65e0\u654c\u8e2a\uff0c \u4e24\u4eba\u8d70\u4e86\u4e09\u5341\u591a\u91cc\uff0c\u4fbf\u9047\u89c1\u660e\u5175\u5f15\u5165\u4eac\u90fd\uff0c\u4e0e\u5f20\u4e39\u67ab\u3001\u4e91\u857e\u76f8\u89c1\uff0c\u4e91\u857e\u81ea\u662f\u559c\u51fa\u671b\u5916\u3002\u4e91\u91cd \u7ecf\u6b64\u4e00\u5f79\uff0c\u5bf9\u5f20\u4e39\u67ab\u7684\u4ec7\u6068\uff0c\u53c8\u51cf\u4e86\u51e0\u5206\uff0c\u5f53\u4e0b\u5404\u9053\u7ecf\u8fc7\uff0c\u4e0d\u5fc5\u7ec6\u8868\u3002 \u3000\u3000\u4e49\u519b\u9646\u7eed\u5165\u4eac\uff0c\u4e8e\u8c26\u5c06\u5f20\u58eb\u8bda\u7684\u5b9d\u85cf\u6362\u4e86\u94f6\u5b50\u62e8\u4e86\u519b\u9977\uff0c\u53c8\u6709\u8be6\u7ec6\u7684\u519b\u7528\u5730\u56fe\uff0c\u58eb\u6c14\u5927 \u632f\uff0c\u63a5\u8fde\u6253\u4e86\u51e0\u573a\u80dc\u4ed7\uff0c\u534a\u6708\u4e4b\u540e\uff0c\u74e6\u523a\u5927\u519b\u679c\u7136\u9000\u51fa\u96c1\u95e8\u5173\u5916\u3002 \u3000\u3000\u4e00\u65e5\uff0c\u4e8e\u8c26\u5c06\u5f20\u4e39\u67ab\u4e0e\u4e91\u857e\u5524\u5230\u4f4f\u6240\uff0c\u9053\uff1a\u201c\u6709\u4ef6\u4e8b\u60c5\uff0c\u751a\u662f\u8270\u9669\uff0c\u8d24\u4f84\u53ef\u613f\u505a\u4e48\uff1f\u201d \u5f20\u4e39\u67ab\u9053\uff1a\u201c\u5927\u4eba\u6709\u6240\u5429\u5490\uff0c\u8d74\u6c64\u8e48\u706b\uff0c\u5728\u6240\u4e0d\u8f9e\u3002\u201d\u4e8e\u8c26\u6c89\u541f\u534a\u664c\uff0c\u9053\uff1a\u201c\u6628\u591c\u6211\u5199\u4e86\u4e00 \u9996\u8bd7\uff0c\u4f60\u5148\u770b\u770b\u3002\u201d\u5f20\u4e39\u67ab\u5c55\u5f00\u8bd7\u7b3a\uff0c\u53ea\u89c1\u4e0a\u9762\u5199\u7684\u4e00\u9996\u4e03\u5f8b\uff0c\u8bd7\u9053\uff1a\u201c\u9732\u5e03\u661f\u9a70\u4e0a\u7389\u4eac\uff0c \u4e09\u8fb9\u5bc7\u864f\u4e00\u65f6\u5e73\uff0c\u4eba\u95f4\u7389\u77f3\u94ed\u52cb\u4e1a\u5929\u4e0a\u94f6\u6cb3\u6d17\u7532\u5175\u3002\u718a\u864e\u6709\u52b3\u54b8\u8fdb\u94c1\uff0c\u72ac\u7f8a\u65e0\u8ba1\u53ef\u5077\u751f\uff0c\u4ece \u4eca\u6d77\u5b87\u98ce\u5c18\u9759\uff0c\u5e99\u7b97\u5e94\u77e5\u51fa\u5723\u660e\u3002\u201d\u201c\u9732\u5e03\u201d\u662f\u6307\u53e4\u65f6\u544a\u6377\u7684\u6587\u6536\u201c\u718a\u864e\u201d\u662f\u6307\u5efa\u6709\u6218\u529f\u7684 \u5c06\u58eb\u3002\u5b83\u7684\u5927\u610f\u662f\u8bf4\uff0c\u73b0\u5728\u6253\u4e86\u80dc\u4ed7\u6709\u529f\u7684\u5c06\u58eb\u90fd\u83b7\u5f97\u63d0\u5347\uff0c\u5356\u56fd\u7684\u5978\u8d3c\u5219\u65e0\u6cd5\u82df\u6d3b\u4e86\uff0c\u4f46 \u613f\u4ece\u6b64\u8fb9\u5883\u5b81\u9759\uff0c\u5c11\u52a8\u5e72\u6208\u3002\u5f20\u4e39\u67ab\u770b\u4e86\uff0c\u65e9\u77e5\u5176\u610f\uff0c\u541f\u9053\uff1a\u201c\u4eba\u95f4\u7389\u77f3\u94ed\u52cb\u4e1a\uff0c\u5929\u4e0a\u94f6\u6cb3 \u5148\u7532\u5175\u3002\u8bd7\u597d\uff0c\u8bd7\u4eba\u7684\u80f8\u895f\u66f4\u4e0d\u53ef\u53ca\u3002\u5927\u4eba\u4e4b\u610f\u53ef\u662f\u60f3\u4e0e\u74e6\u523a\u8c0b\u548c\u4e48\uff1f\u201d\u4e8e\u8c26\u9053\uff1a\u201c\u6b63\u662f\uff0c \u5929\u4e0b\u65e0\u4e0d\u606f\u4e4b\u5e72\u6208\uff0c\u5982\u4eca\u54b1\u4eec\u6253\u4e86\u80dc\u4ed7\uff0c\u4e0e\u4ed6\u8c08\u548c\uff0c\u5e76\u65e0\u5c48\u8fb1\uff0c\u592a\u4e0a\u7687\uff08\u6307\u82f1\u5b97\u7948\u9547\uff09\u8499\u5c18 \u5f02\u56fd\uff0c\u54b1\u4eec\u603b\u8be5\u8bbe\u6cd5\u63a5\u4ed6\u56de\u6765\u3002\u201d\u5f20\u4e39\u67ab\u5fc3\u5934\u4e00\u9707\uff0c\u60f3\u9053\uff1a\u201c\u539f\u6765\u4ed6\u662f\u6b32\u628a\u88ab\u56da\u7684\u7687\u5e1d\u6551\u56de \u6765\u3002\u4f46\u5982\u4eca\u5df2\u53e6\u7acb\u65b0\u541b\uff0c\u592a\u4e0a\u7687\u56de\u6765\uff0c\u53ea\u6050\u5bf9\u4e8e\u8c26\u96be\u4ee5\u8c05\u89e3\uff0c\u90a3\u65f6\u5c82\u4e0d\u662f\u7d2f\u4ed6\u53d7\u7978\uff1f\u201d\u53ea\u542c \u5f97\u4e8e\u8c26\u8bf4\u9053\uff1a\u201c\u575a\u4f84\uff0c\u6211\u610f\u5df2\u51b3\u65e0\u53ef\u52a8\u6447\u3002\u4e2a\u4eba\u7684\u6210\u8d25\u8363\u8fb1\u7b97\u4e0d\u4e86\u4ec0\u4e48\uff0c\u5929\u671d\u7684\u4e00\u56fd\u4e4b\u541b\uff0c \u603b\u4e0d\u80fd\u957f\u4f5c\u654c\u4eba\u4fd8\u864f\u3002\u4f60\u4eec\u5148\u4e3a\u6211\u53bb\u63a2\u542c\u6d88\u606f\uff0c\u7136\u540e\u6211\u518d\u6d3e\u9063\u6b63\u5f0f\u7684\u4f7f\u8282\uff0c\u524d\u5f80\u8bae\u548c\uff0c\u8fce\u63a5 \u592a\u4e0a\u7687\u56de\u6765\u3002\u518d\u8005\uff0c\u4e5f\u5148\u91ce\u5fc3\u975e\u5c0f\uff0c\u53ea\u6050\u4ed6\u5c0f\u8d25\u4e4b\u540e\uff0c\u53c8\u6765\u7b2c\u4e8c\u6b21\u8fdb\u4fb5\uff0c\u8d24\u4f84\u6b64\u53bb\uff0c\u53ef\u4ee5\u7b56 \u52a8\u5c0a\u5927\u4eba\u4e0e\u963f\u523a\u77e5\u9662\u7b49\u7ed9\u4ed6\u63a3\u8098\uff0c\u4e5f\u662f\u5947\u529f\u4e00\u4ef6\u3002\u201d\u5f20\u4e39\u67ab\u60f3\u4e86\u4e00\u60f3\uff0c\u6168\u7136\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u6211 \u660e\u65e5\u4fbf\u53bb\uff0c\u672c\u6765\u6211\u4e0d\u613f\u518d\u56de\u74e6\u523a\uff0c\u4f46\u4e3a\u4e86\u6b64\u4e8b\uff0c\u5c31\u662f\u5200\u952f\u5f53\u524d\uff0c\u4e5f\u5f53\u53bb\u4e86\u3002\u53ea\u662f\u6211\u4e00\u4eba\u53bb \u4e48\uff1f\u201d\u4e8e\u8c26\u9053\uff1a\u201c\u6211\u5df2\u4e0e\u4e91\u91cd\u8bf4\u597d\uff0c\u8ba9\u4e91\u857e\u4e0e\u4f60\u4e00\u540c\u53bb\u3002\u542c\u8bf4\u4f60\u4eec\u53cc\u5251\u5408\u74a7\uff0c\u6240\u5411\u65e0\u654c\uff0c\u662f \u4e48\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u90a3\u4e0d\u8fc7\u662f\u6ca1\u78b0\u7740\u9ad8\u624b\u7f62\u4e86\u3002\u4e0d\u8fc7\u6709\u5979\u540c\u884c\uff0c\u603b\u597d\u4e00\u70b9\uff0c\u53ef\u4ee5\u5e94\u4ed8\u8f83\u5f3a\u7684 \u654c\u4eba\uff0c\u90a3\u4e5f\u662f\u4e8b\u5b9e\u3002\u201d\u4e8e\u8c26\u5fae\u5fae\u4e00\u7b11\uff0c\u7b11\u4e2d\u5927\u6709\u6df1\u610f\u3002 \u3000\u3000\u7b2c\u4e8c\u65e5\u5f20\u3001\u4e91\u4e8c\u4eba\u544a\u522b\u4f17\u4eba\uff0c\u4e00\u540c\u79bb\u4eac\uff0c\u8fd9\u6b21\u4e07\u91cc\u540c\u884c\uff0c\u5fc3\u60c5\u66f4\u662f\u8212\u7545\u3002\u5f20\u4e39\u67ab\u8def\u4e0a\u7b11 \u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u4e0a\u6b21\u4ece\u6c5f\u82cf\u8fdb\u4eac\u65f6\uff0c\u4f60\u66fe\u8bf4\u8fc7\u65c5\u7a0b\u82e6\u77ed\uff0c\u5982\u4eca\u524d\u5f80\u74e6\u523a\uff0c\u65c5\u7a0b\u53ef\u957f\u5f97\u591a \u4e86\u3002\u201d\u4e91\u857e\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e5f\u6709\u8d70\u5c3d\u7684\u4e00\u5929\u3002\u201d\u5f20\u4e39\u67ab\u4e00\u7b11\u541f\u9053\uff1a\u201c\u4eba\u95f4\u4e0d\u5c11\u574e\u5777\u8def\uff0c\u5192 \u96ea\u51b2\u5bd2\u4e0a\u65c5\u7a0b\u3002\u54b1\u4eec\u8fd9\u4e00\u751f\u8be5\u8d70\u591a\u5c11\u574e\u5777\u7684\u9053\u8def\uff0c\u54ea\u6709\u8d70\u5b8c\u4e4b\u65e5\uff01\u201d\u4e91\u857e\u5fc3\u795e\u52a8\u8361\uff0c\u77e5\u4ed6\u662f \u60f3\u6c42\u81ea\u5df1\u505a\u4ed6\u4e00\u751f\u7684\u4f34\u4fa3\uff0c\u5fc3\u4e2d\u81ea\u662f\u611f\u6fc0\u4ed6\u7684\u75f4\u60c5\u4e00\u7247\uff0c\u4f46\u60f3\u8d77\u54e5\u54e5\u7684\u5429\u5490\u5374\u53c8\u4e0d\u7981\u9ed8\u7136\uff0c \u53ea\u597d\u5047\u4f5c\u4e0d\u61c2\u4ed6\u7684\u7528\u610f\uff0c\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u9178\u79c0\u624d\uff0c\u4e0d\u8981\u518d\u541f\u8bd7\u5566\uff0c\u518d\u4e0d\u8d76\u8def\uff0c\u518d\u803d\u6401\u4e00\u4e9b\u65f6 \u65e5\uff0c\u90a3\u4e48\u53ea\u6050\u672a\u5230\u5173\u5916\uff0c\u5c31\u5df2\u5927\u96ea\u7eb7\u98de\uff0c\u90a3\u65f6\u624d\u771f\u662f\u5192\u96ea\u51b2\u5bd2\u54e9\uff01\u201d \u3000\u3000\u4e24\u4eba\u4e00\u8def\u8c08\u8c08\u7b11\u7b11\uff0c\u5012\u4e0d\u5bc2\u5bde\uff0c\u53ea\u662f\u6bcf\u9022\u5f20\u4e39\u67ab\u8c08\u5230\u4e24\u4eba\u4e4b\u4e8b\uff0c\u4e91\u857e\u603b\u662f\u907f\u4e86\u5f00\u53bb\u3002\u8fd9 \u65e5\u5230\u4e86\u9633\u66f2\uff0c\u5927\u5175\u4e4b\u540e\uff0c\u57ce\u4e2d\u7684\u5e97\u94fa\uff0c\u534a\u6570\u5c1a\u672a\u5f00\u95e8\uff0c\u4f46\u5f20\u4e39\u67ab\u521d\u9047\u4e91\u857e\u7684\u90a3\u95f4\u9152\u697c\uff0c\u5374\u662f \u9152\u65d7\u62db\u5c55\u3002\u5f20\u4e39\u67ab\u7b11\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u4f60\u8fd8\u8bb0\u5f97\u8fd9\u95f4\u9152\u697c\u5417\uff1f\u201d\u4e91\u857e\u9053\uff1a\u201c\u6211\u4e00\u751f\u4e5f\u5fd8\u4e0d \u4e86\uff01\u201d\u5f20\u4e39\u67ab\u559c\u9053\uff1a\u201c\u554a\uff0c\u5c0f\u5144\u5f1f\uff01\u771f\u96be\u5f97\u4f60\u6211\u5fc3\u610f\u5982\u4e00\u2026\u2026\u201d\u4e91\u857e\u622a\u7740\u8bf4\u9053\uff1a\u201c\u4ec0\u4e48\u5fc3\u610f \u5982\u4e00\uff0c\u6211\u5fd8\u4e0d\u4e86\u4f60\u5728\u8fd9\u9152\u697c\u4e0a\u5077\u6211\u7684\u94b1\uff0c\u5f04\u5f97\u6211\u51e0\u4e4e\u5f53\u573a\u51fa\u4e11\uff01\u201d\u5f20\u4e39\u67ab\u7b11\u9053\uff1a\u201c\u597d\u5566\uff0c\u54b1 \u4eec\u4e0d\u8981\u6597\u5634\uff0c\u91cd\u4e34\u65e7\u5730\uff0c\u524d\u4e8b\u96be\u5fd8\uff0c\u54b1\u4eec\u8be5\u4e0a\u53bb\u75db\u996e\u51e0\u676f\u3002\u5c0f\u5144\u5f1f\uff0c\u4f60\u653e\u5fc3\uff0c\u8fd9\u56de\u6211\u8bf7\u5ba2\uff0c \u4e0d\u518d\u8bf4\u4f60\u5403\u767d\u98df\u5566\uff01\u201d\u4e91\u857e\u542c\u4ed6\u63d0\u8d77\u65e7\u4e8b\uff0c\u4e0d\u89c9\u56de\u7738\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u82e5\u6562\u518d\u65bd\u7a7a\u7a7a\u5999\u624b\uff0c\u770b \u6211\u4e0d\u6253\u6298\u4f60\u7684\u9aa8\u5934\u3002\u201d\u4e24\u4eba\u5c06\u9a6c\u7cfb\u597d\uff0c\u4e92\u76f8\u8c03\u7b11\uff0c\u6b65\u4e0a\u9152\u697c\u3002 \u3000\u3000\u9633\u66f2\u6536\u590d\u672a\u4e45\uff0c\u697c\u4e0a\u996e\u5ba2\u65e0\u591a\uff0c\u5f20\u4e39\u67ab\u8fd8\u8bb0\u5f97\u4ee5\u524d\u5750\u7684\u662f\u5357\u9762\u4e34\u7a97\u7684\u5ea7\u5934\uff0c\u4fbf\u4e0e\u4e91\u857e\u5360 \u4e86\u90a3\u5f20\u684c\u5b50\uff0c\u53eb\u5802\u500c\u62ff\u4e86\u4e00\u58f6\u6c7e\u9152\uff0c\u5207\u4e24\u65a4\u725b\u8089\uff0c\u4e00\u53e3\u6c14\u559d\u4e86\u4e09\u676f\uff0c\u7b11\u9053\uff1a\u201c\u90a3\u65f6\u6211\u53ea\u5b64\u5355 \u4e00\u4eba\u5728\u6b64\u72ec\u914c\uff0c\u4f60\u4e5f\u662f\u4e00\u4eba\uff0c\u6211\u8bb0\u5f97\u4f60\u8001\u662f\u62ff\u773c\u89d2\u779f\u6211\uff0c\u597d\u5566\uff0c\u5982\u4eca\u662f\u4e24\u4e2a\u4eba\u4e86\u3002\u4f60\u4e5f\u4e0d\u5fc5 \u518d\u5077\u5077\u770b\u6211\u4e86\u3002\u201d\u4e91\u857e\u7f9e\u9053\uff1a\u201c\u8bf4\u8bdd\u5c0f\u58f0\u70b9\u513f\uff0c\u8c01\u62ff\u773c\u89d2\u779f\u4f60\uff0c\u90a3\u65f6\u6211\u770b\u89c1\u4f60\u4e00\u526f\u9178\u6001\uff0c\u5341 \u5206\u53ef\u7b11\uff0c\u53c8\u6709\u8d3c\u4eba\u5077\u5077\u8ddf\u7740\u4f60\uff0c\u4f60\u4e5f\u6beb\u4e0d\u77e5\u9053\uff0c\u6240\u4ee5\u591a\u770b\u4f60\u4e24\u773c\u7f62\u4e86\u3002\u5440\uff0c\u8c01\u77e5\u9053\u4f60\u662f\u6545\u610f \u620f\u5f04\u4e8e\u6211\uff0c\u65e7\u4e8b\u4e0d\u8bf4\u4e5f\u8fd8\u7f62\u4e86\uff0c\u8bf4\u8d77\u6765\u6211\u73b0\u5728\u8fd8\u5728\u607c\u4f60\uff01\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u771f\u7684\uff1f\u201d\u4e00\u534a\u8ba4\u771f \u4e00\u534a\u5f00\u73a9\u7b11\u7684\u795e\u6c14\u3002\u4e91\u857e\u5c06\u4ed6\u6ca1\u6cd5\uff0c\u6c14\u9053\uff1a\u201c\u4f60\u7684\u5fc3\u80a0\u771f\u574f\uff01\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u662f\u4e48\uff1f\u90a3\u4e48\u6211 \u662f\u4e2a\u574f\u54e5\u54e5\u4e86\uff1f\u201d\u4e91\u857e\u9053\uff1a\u201c\u4f60\u518d\u6c14\u6211\u6211\u5c31\u4e0d\u548c\u4f60\u8bf4\u4e86\u3002\u201d \u3000\u3000\u5f20\u4e39\u67ab\u53c8\u559d\u4e86\u4e00\u676f\uff0c\u7b11\u9053\uff1a\u201c\u8bb0\u5f97\u90a3\u65e5\u76ef\u68a2\u6211\u7684\u4e24\u4e2a\u5c0f\u8d3c\u5728\u8fd9\u4e1c\u9762\u7684\u5ea7\u5934\u3002\u201d\u56de\u5934\u4e00 \u671b\uff0c\u53ea\u89c1\u4e1c\u9762\u5ea7\u5934\uff0c\u4e5f\u5750\u7740\u6709\u4eba\uff0c\u4e43\u662f\u4e00\u4e2a\u9752\u8863\u9053\u58eb\uff0c\u76f8\u8c8c\u8f69\u6602\u3002\u4e91\u857e\u7b11\u9053\uff1a\u201c\u8fd9\u4e2a\u8be5\u4e0d\u662f \u8d3c\u4eba\u4e86\u3002\u201d\u8bf4\u7f62\u4e5f\u996e\u4e86\u4e00\u676f\u3002 \u3000\u3000\u4e91\u857e\u867d\u4e0d\u6b32\u91cd\u63d0\u65e7\u4e8b\uff0c\u5176\u5b9e\u91cd\u4e34\u65e7\u5730\uff0c\u60f3\u8d77\u4e0e\u5f20\u4e39\u67ab\u521d\u6b21\u89c1\u9762\u7684\u60c5\u666f\uff0c\u4e5f\u662f\u611f\u89e6\u751a\u591a\uff0c \u60f3\u9053\uff1a\u201c\u90a3\u65f6\u6211\u5bf9\u4ed6\u751a\u662f\u618e\u538c\uff0c\u60f3\u4e0d\u5230\u5982\u4eca\u7adf\u6210\u77e5\u5df1\uff0c\u66f4\u60f3\u4e0d\u5230\u4ed6\u53c8\u662f\u6211\u7684\u4ec7\u4eba\uff0c\u800c\u6211\u7684\u54e5 \u54e5\u5374\u6b7b\u6b7b\u8bb0\u7740\u4e0a\u4ee3\u6709\u4ec7\u6068\u3002\u4eba\u751f\u4e4b\u4e8b\uff0c\u786e\u662f\u6599\u60f3\u4e0d\u5230\u3002\u201d\u4e0e\u5f20\u4e39\u67ab\u628a\u76cf\u503e\u8c08\uff0c\u5fc3\u4e2d\u5341\u5206\u7545 \u5feb\uff0c\u4e0d\u77e5\u4e0d\u89c9\u53c8\u591a\u559d\u4e86\u51e0\u676f\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u5ffd\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u6b64\u53bb\u5341\u591a\u91cc\uff0c\u5c31\u662f\u9ed1\u77f3\u5e84\u4e86\u3002\u4f60\u4e0d\u8981\u53bb\u62dc\u8bbf\u62dc\u8bbf\u4f60\u7684\u5cb3\u4e08\u5927\u4eba \u5417\uff1f\u201d\u4e91\u857e\u6014\u4e86\u4e00\u6014\uff0c\u60f3\u8d77\u4e86\u4e0e\u77f3\u7fe0\u51e4\u6d1e\u623f\u82b1\u70db\u4e4b\u591c\u7684\u6ed1\u7a3d\u60c5\u4e8b\uff0c\u4e00\u53e3\u9152\u51e0\u4e4e\u55b7\u4e86\u51fa\u6765\u3002\u5f20 \u4e39\u67ab\u6b63\u8272\u8bf4\u9053\uff1a\u201c\u96be\u4e3a\u4f60\u90a3\u4f4d\u5a07\u59bb\u7b49\u4e86\u4f60\u8fd9\u4e48\u4e9b\u65f6\u5019\uff0c\u5728\u95fa\u4e2d\u7a7a\u62c5\u4e86\u865a\u540d\u3002\u73b0\u5728\u7ecf\u8fc7\u4e86\u8fd9\u573a \u6218\u4e89\u7684\u707e\u96be\uff0c\u4f60\u4e5f\u8be5\u53bb\u770b\u770b\u5979\uff0c\u597d\u53eb\u5979\u653e\u5fc3\u3002\u201d \u3000\u3000\u4e91\u857e\u5fc3\u4e2d\u4e00\u52a8\uff0c\u60f3\u8d77\u4e86\u77f3\u7fe0\u51e4\u7684\u75f4\u60c5\u4e00\u7247\uff0c\u5fc3\u9053\uff1a\u201c\u662f\u554a\u6211\u771f\u7684\u5e94\u8be5\u53bb\u770b\u770b\u5979\u624d\u662f\u3002\u53ef \u662f\u8981\u4e0d\u8981\u544a\u8bc9\u5979\u6211\u7684\u5e90\u5c71\u771f\u9762\u76ee\u5462\uff1f\u201d\u8981\u77e5\u4e91\u857e\u521d\u4e0b\u5c71\u4e4b\u65f6\uff0c\u7a1a\u6c14\u672a\u9664\uff0c\u4e54\u88c5\u7537\u5b50\uff0c\u5047\u5192\u65b0 \u90ce\u4e4b\u4e8b\uff0c\u4e5f\u53ea\u662f\u56e0\u4e3a\u4e00\u65f6\u96be\u4ee5\u8131\u8eab\uff0c\u4f5c\u4e3a\u620f\u800d\uff0c\u60f3\u4e0d\u5230\u77f3\u7fe0\u51e4\u5374\u5bf9\u5979\u82e6\u82e6\u7ea0\u7f20\uff0c\u628a\u5979\u5f53\u4f5c\u53ef \u4ee5\u4ed8\u6258\u7ec8\u8eab\u7684\u4e08\u592b\u3002\u5982\u4eca\u4e91\u857e\u5728\u6c5f\u6e56\u4e0a\u7ecf\u8fc7\u4e00\u756a\u5386\u7ec3\uff0c\u4eba\u4e5f\u957f\u6210\u4e86\u8bb8\u591a\uff0c\u60f3\u8d77\u6b64\u4e8b\uff0c\u4e0d\u7531\u5f97 \u5fc3\u4e2d\u6b49\u7136\u3002\u62ac\u5934\u4e00\u671b\uff0c\u53ea\u89c1\u5f20\u4e39\u67ab\u4f3c\u7b11\u975e\u7b11\u5730\u671b\u7740\u5979\uff0c\u4e91\u857e\u6c14\u9053\uff1a\u201c\u4f60\u7b11\u4ec0\u4e48\uff1f\u4f60\u4e0d\u662f\u4e5f\u66fe \u7ecf\u7537\u626e\u5973\u88c5\uff0c\u51e0\u4e4e\u548c\u90a3\u4f4d\u4ec0\u4e48\u4e5f\u5148\u5c0f\u59d0\u6d1e\u4e86\u623f\u5417\uff1f\u201d\u5f20\u4e39\u67ab\u7b11\u9053\uff1a\u201c\u6211\u53ef\u6ca1\u6709\u548c\u4eba\u5bb6\u6210\u4eb2 \u5440\u3002\u201d\u4e91\u857e\u9053\uff1a\u201c\u597d\uff0c\u54b1\u4eec\u5feb\u4e9b\u559d\u5b8c\u4e86\u9152\u5c31\u53bb\u627e\u5979\uff0c\u544a\u8bc9\u5979\u6211\u7684\u771f\u76f8\u3002\u5440\uff0c\u53ea\u4e0d\u77e5\u5468\u5c71\u6c11\u73b0 \u5728\u4f55\u65b9\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u4f60\u81ea\u5df1\u7684\u4e8b\u8fd8\u6ca1\u6709\u641e\u6e05\u695a\uff0c\u53c8\u60f3\u505a\u5a92\u4e86\u5417\uff1f\u6211\u95ee\u4f60\uff0c\u4f60\u8981\u4e0d\u8981\u6362\u8fc7 \u4e00\u5957\u8863\u88f3\uff0c\u8981\u4e0d\u7136\u77f3\u5c0f\u59d0\u89c1\u4e86\u4f60\uff0c\u53c8\u8981\u7f20\u7740\u4f60\u4e0d\u653e\u4f60\u8d70\u4e86\u3002\u201d\u4e91\u857e\u51fa\u4eac\u4e4b\u65f6\uff0c\u53c8\u5df2\u6539\u56de\u7537 \u88c5\uff0c\u4f4e\u5934\u4e00\u671b\u81ea\u5df1\uff0c\u4f4e\u58f0\u7b11\u9053\uff1a\u201c\u4f60\u8bf4\u8bdd\u5c0f\u58f0\u70b9\u513f\uff0c\u90a3\u4e2a\u9053\u58eb\u4f3c\u4e4e\u5728\u6ce8\u610f\u6211\u4eec\u5462\u3002\u201d\u5f20\u4e39\u67ab \u9053\uff1a\u201c\u4ed6\u53c8\u4e0d\u662f\u8d3c\u4eba\uff0c\u4f60\u53ef\u4e0d\u5fc5\u62c5\u5fc3\u3002\u201d \u3000\u3000\u4e91\u857e\u5fc3\u4e2d\u6709\u4e8b\uff0c\u80e1\u4e71\u559d\u5b8c\u4e86\u9152\uff0c\u9053\uff1a\u201c\u54b1\u4eec\u8d70\u5427\u3002\u201d\u62a2\u53bb\u4f1a\u5e10\uff0c\u7b11\u9053\uff1a\u201c\u504f\u4e0d\u8981\u4f60\u8bf7 \u5ba2\u3002\u201d\u4f38\u624b\u638f\u94b1\uff0c\u94b1\u888b\u7adf\u7136\u4e0d\u7ffc\u800c\u98de\u4e0d\u7531\u5f97\u6014\u4e86\u4e00\u6014\uff0c\u5fc3\u9053\uff1a\u201c\u5927\u54e5\u53c8\u4f5c\u5f04\u6211\u4e86\uff1f\u201d\u53eb\u9053\uff1a \u201c\u5feb\u5c06\u6211\u7684\u94b1\u888b\u62ff\u6765\uff01\u201d\u56de\u5934\u4e00\u671b\uff0c\u5ffd\u89c1\u90a3\u9752\u8863\u9053\u58eb\u7ad9\u5728\u65c1\u8fb9\uff0c\u5f20\u4e39\u67ab\u201c\u556a\u201d\u7684\u4e00\u638c\u5411\u4ed6\u6253 \u53bb\uff0c\u559d\u9053\uff1a\u201c\u5149\u5929\u5316\u65e5\u4e4b\u4e0b\uff0c\u4f60\u6562\u4f5c\u8d3c\uff01\u201d\u90a3\u9053\u58eb\u53d8\u81c2\u4e00\u8fce\uff0c\u8f7b\u63cf\u6de1\u5199\u5730\u5c06\u5f20\u4e39\u67ab\u7684\u638c\u529b\u5378 \u5f00\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u6562\u6253\u4eba\uff1f\u201d\u4e91\u857e\u5403\u4e86\u4e00\u60ca\uff0c\u8fd9\u9053\u58eb\u7684\u624b\u6cd5\u597d\u5feb\uff0c\u5c45\u7136\u63a5\u5f97\u4e86\u5f20\u4e39\u67ab\u7684\u4e00\u638c\uff0c \u6b63\u60f3\u52a0\u5165\u56e2\u6218\uff0c\u5f20\u4e39\u67ab\u8eab\u624b\u4f55\u7b49\u5feb\u6377\uff0c\u500f\u5730\u5316\u638c\u4e3a\u62ff\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u539f\u6765\u4f60\u8fd8\u662f\u4e2a\u4f1a\u5bb6\uff01\u201d\u4e00 \u6293\u6293\u53bb\uff0c\u5c06\u4e91\u857e\u90a3\u4e2a\u88ab\u5077\u7684\u94b1\u888b\uff0c\u4e00\u4e0b\u5b50\u6293\u4e86\u56de\u6765\uff0c\u559d\u9053\uff1a\u201c\u8d43\u7269\u5728\u6b64\u4f60\u8fd8\u6709\u4f55\u8bdd\u8bf4\uff1f\u201d\u53ea \u542c\u5f97\u201c\u55e4\u201d\u7684\u4e00\u58f0\uff0c\u90a3\u9053\u58eb\u7684\u9053\u888d\u88ab\u5f20\u4e39\u67ab\u6495\u4e86\u4e00\u89d2\u8863\u8896\uff0c\u90a3\u9053\u58eb\u4f7f\u4e86\u4e2a\u201c\u91d1\u8749\u8131\u58f3\u201d\u7684\u8eab \u6cd5\u500f\u5730\u4ece\u5f20\u4e39\u67ab\u7684\u638c\u529b\u7b3c\u7f69\u4e4b\u4e0b\uff0c\u8131\u51fa\u8eab\u6765\uff0c\u817e\u8eab\u4e00\u8dc3\uff0c\u7adf\u7136\u4ece\u7a97\u53e3\u8df3\u4e0b\u53bb\u4e86\u3002 \u3000\u3000\u5e97\u4e3b\u4eba\u5927\u53eb\u9053\uff1a\u201c\u5582\u5582\uff0c\u6211\u7684\u9152\u94b1\uff0c\u5feb\u6765\u4eba\u5440\u6709\u5f3a\u76d7\uff01\u201d\u5f20\u4e39\u67ab\u6025\u5fd9\u6253\u5f00\u94b1\u888b\uff0c\u62ff\u51fa\u4e00 \u952d\u5927\u94f6\uff0c\u653e\u5728\u684c\u4e0a\uff0c\u9053\uff1a\u201c\u90fd\u7b97\u6211\u7684\u5e10\u3002\u201d\u8fd9\u952d\u5927\u94f6\uff0c\u5373\u8fde\u90a3\u9053\u58eb\u548c\u9152\u94b1\u5728\u5185\u4e5f\u8db3\u591f\u4ed8\u6709 \u4f59\uff0c\u5e97\u4e3b\u4eba\u559c\u51fa\u671b\u5916\uff0c\u6b63\u60f3\u9053\u8c22\uff0c\u5f20\u4e39\u67ab\u6446\u8131\u4e86\u5e97\u4e3b\u4eba\u7684\u7ea0\u7f20\uff0c\u5df2\u62c9\u4e86\u4e91\u857e\uff0c\u4e5f\u4e00\u540c\u8df3\u4e0b\u53bb \u4e86\u3002 \u3000\u3000\u8857\u4e0a\u884c\u4eba\u7a00\u5c11\uff0c\u53ea\u89c1\u90a3\u9053\u58eb\u9a91\u4e86\u4e00\u9a91\u5feb\u9a6c\uff0c\u5df2\u51b2\u51fa\u57ce\u95e8\u3002\u5f20\u4e39\u67ab\u6025\u5fd9\u8de8\u4e0a\u201c\u7167\u591c\u72ee\u5b50 \u9a6c\u201d\u9053\uff1a\u201c\u5feb\u8ffd\uff01\u201d\u4e91\u857e\u9053\uff1a\u201c\u94b1\u888b\u5df2\u62ff\u56de\u6765\uff0c\u4f55\u5fc5\u518d\u53bb\u7406\u4ed6\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u4e0d\uff0c\u8fd9\u9053\u58eb \u8eab\u624b\u975e\u51e1\uff0c\u4e00\u5b9a\u4e0d\u662f\u666e\u901a\u7684\u5c0f\u8d3c\uff0c\u6211\u975e\u95ee\u4e2a\u660e\u767d\u4e0d\u53ef\uff01\u201d\u7167\u591c\u72ee\u5b50\u9a6c\u4e00\u58f0\u957f\u5636\uff0c\u56db\u8e44\u75be\u8d70\uff0c \u4e91\u857e\u53ea\u597d\u8ddf\u5728\u540e\u9762\u3002\u6b63\u662f\uff1a \u3000\u3000\u4f55\u65b9\u6765\u602a\u8d3c\uff0c\u4fa0\u58eb\u8d77\u7591\u5fc3\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u840d\u8e2a\u4fa0\u5f71\u5f55\u300b\u2014\u2014\u7b2c\u4e8c\u5341\u4e8c\u56de\u3000\u6d45\u7b11\u8f7b\u98a6\u4eba\u524d\u4f5c\u5a07\u6001\u3000\u6167\u56e0\u5170\u679c\u52ab\u540e\u8bc1\u60c5\u5fc3 \u6881\u7fbd\u751f\u300a\u840d\u8e2a\u4fa0\u5f71\u5f55\u300b \u7b2c\u4e8c\u5341\u4e8c\u56de\u3000\u6d45\u7b11\u8f7b\u98a6\u4eba\u524d\u4f5c\u5a07\u6001\u3000\u6167\u56e0\u5170\u679c\u52ab\u540e\u8bc1\u60c5\u5fc3 \u3000\u3000\u5f20\u4e39\u67ab\u7684\u201c\u7167\u591c\u72ee\u5b50\u9a6c\u201d\u56fa\u7136\u662f\u795e\u9a8f\u975e\u5e38\uff0c\u5373\u4e91\u857e\u7684\u5750\u9a91\u4e5f\u662f\u4e8e\u8c26\u4f5c\u4e3b\u6240\u8d60\u9001\u7684\u5fa1\u82d1\u540d \u9a6c\uff0c\u867d\u4ecd\u4e0d\u53ca\u201c\u7167\u591c\u72ee\u5b50\u9a6c\u201d\uff0c\u4f46\u4ea6\u53ef\u65e5\u884c\u5343\u91cc\uff0c\u4e24\u4eba\u8ffd\u51fa\u9633\u66f2\u53bf\u57ce\uff0c\u4e0d\u6d88\u591a\u4e45\uff0c\u5c31\u8ffd\u4e0a\u4e86 \u90a3\u9752\u8863\u9053\u58eb\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u559d\u9053\uff1a\u201c\u4f4f\u9a6c\uff01\u201d\u90a3\u9752\u8863\u9053\u58eb\u6115\u7136\u56de\u987e\uff0c\u5ffd\u800c\u5927\u7b11\u9053\uff1a\u201c\u4f60\u77e5\u9053\u6211\u7f3a\u5c11\u76d8\u7f20\uff0c\u8981 \u7ed9\u6211\u9001\u94b1\u6765\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u9152\u697c\u4eba\u6742\uff0c\u4e0d\u4fbf\u591a\u8c08\uff0c\u9053\u957f\u5982\u4eca\u8fd8\u8981\u620f\u800d\u5417\uff1f\u201d\u90a3\u9053\u58eb\u9762\u8272\u4e00 \u6c89\u9053\uff1a\u201c\u8c01\u4e0e\u4f60\u620f\u800d\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u65e2\u975e\u620f\u800d\uff0c\u5c31\u8bf7\u5c06\u6765\u5386\u544a\u77e5\u3002\u201d\u9752\u8863\u9053\u58eb\u9053\uff1a\u201c\u6211\u5e73 \u751f\u5077\u94b1\uff0c\u4ece\u65e0\u5931\u624b\uff0c\u4eca\u65e5\u88ab\u4f60\u64d2\u4f4f\u8fd8\u4e86\u4f60\u4e5f\u5c31\u7f62\u4e86\uff0c\u4f60\u5374\u8fd8\u6765\u8ffd\u6211\uff0c\u8fd9\u5206\u660e\u662f\u4f60\u6709\u94b1\u7684\u5927\u7237 \u8981\u6765\u620f\u800d\u6211\uff0c\u54fc\uff0c\u54fc\uff0c\u5403\u6211\u4e00\u5251\uff01\u201d\u8bf4\u5f97\u751a\u662f\u8ba4\u771f\u4e0d\u50cf\u662f\u5f00\u73a9\u7b11\uff0c\u4e00\u8bed\u752b\u6bd5\uff0c\u679c\u7136\u62d4\u51fa\u957f \u5251\uff0c\u8fce\u9762\u5c31\u662f\u4e00\u62db\u201c\u91d1\u9488\u5f15\u7ebf\u201d\u5237\u7684\u523a\u6765\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u4e00\u95ea\u95ea\u8fc7\uff0c\u90a3\u9053\u58eb\u51fa\u624b\u5982\u98ce\uff0c\u8fde\u73af\u4e09\u5251\u4e0d\u4f4f\u653b\u51fb\uff0c\u5f20\u4e39\u67ab\u770b\u4ed6\u7684\u5251\u6cd5\uff0c\u7adf\u662f\u6b66\u5f53 \u6d3e\u7684\u8fde\u73af\u593a\u547d\u5251\u6cd5\uff0c\u6014\u4e86\u4e00\u6014\u3002\u53ea\u542c\u5f97\u90a3\u9053\u58eb\u559d\u9053\uff1a\u201c\u4f60\u4ed7\u7740\u9a6c\u5feb\uff0c\u7b97\u4ec0\u4e48\u82f1\u96c4\uff1f\u201d\u5f20\u4e39\u67ab \u5fc3\u4e2d\u4e00\u52a8\uff0c\u60f3\u9053\uff1a\u201c\u83ab\u975e\u4ed6\u662f\u6709\u610f\u8bd5\u6211\u7684\u5251\u6cd5\uff1f\u201d\u4e00\u8dc3\u4e0b\u9a6c\u9053\uff1a\u201c\u597d\uff0c\u6211\u5c31\u966a\u9053\u957f\u8d70\u51e0 \u62db\uff01\u201d \u3000\u3000\u90a3\u9752\u8863\u9053\u58eb\u4e5f\u81ea\u9a6c\u80cc\u4e00\u8dc3\u800c\u4e0b\uff0c\u66f4\u4e0d\u642d\u8bdd\uff0c\u53cd\u624b\u4e00\u5251\uff0c\u5f84\u523a\u5f20\u4e39\u67ab\u7684\u201c\u9b42\u95e8\u7a74\u201d\uff0c\u53c8\u662f \u4e00\u62db\u5389\u5bb3\u7684\u6740\u624b\u3002\u5f20\u4e39\u67ab\u5fc3\u4e2d\u6709\u6c14\uff0c\u8fd8\u4e86\u4e00\u62db\u201c\u6a2a\u67b6\u91d1\u6881\u201d\uff0c\u63a5\u624b\u4e00\u62db\u201c\u91d1\u87fe\u620f\u6d6a\u201d\uff0c\u5251\u950b \u4e00\u98a4\uff0c\u5251\u82b1\u9519\u843d\uff0c\u4e00\u62db\u4e4b\u5185\u5206\u523a\u9053\u58eb\u7684\u4e09\u9053\u5927\u7a74\uff0c\u90a3\u9053\u58eb\u53eb\u58f0\uff1a\u201c\u597d\u5389\u5bb3\uff01\u201d\u4e00\u4e2a\u76d8\u9f99\u7ed5 \u6b65\uff0c\u6a2a\u5251\u4e00\u62ab\uff0c\u8eab\u5f62\u4e00\u8f6c\uff0c\u5c06\u5f20\u4e39\u67ab\u7684\u653b\u52bf\u89e3\u5f00\uff0c\u9000\u6b65\u8f6c\u8eab\uff0c\u9661\u7136\u95f4\u53c8\u523a\u51fa\u4e00\u5251\u3002\u5f20\u4e39\u67ab\u5fc3 \u4e2d\u4e5f\u6697\u6697\u4f69\u670d\uff0c\u60f3\u9053\uff1a\u201c\u6b64\u4eba\u5251\u6cd5\u8fdc\u5728\u677e\u77f3\u9053\u4eba\u4e4b\u4e0a\uff0c\u5b9a\u662f\u6b66\u5f53\u6d3e\u4e2d\u6709\u6570\u7684\u9ad8\u624b\u4e86\u3002\u201d\u5f53\u4e0b \u5168\u795e\u8d2f\u6ce8\uff0c\u5c06\u767e\u53d8\u7384\u673a\u5251\u6cd5\u65bd\u5c55\u51fa\u6765\uff0c\u5251\u5f71\u98d8\u98d8\uff0c\u5de6\u4e00\u5251\uff0c\u53f3\u4e00\u5251\uff0c\u4e0a\u4e00\u5251\uff0c\u4e0b\u4e00\u5251\uff0c\u5251\u52bf \u5982\u8679\uff0c\u53d8\u5316\u65e0\u5b9a\uff0c\u4e00\u53e3\u6c14\u523a\u4e86\u4e0a\u8def\u8ffd\u98ce\u516b\u5251\uff0c\u516b\u5251\u523a\u5b8c\uff0c\u90a3\u9053\u58eb\u521a\u7f13\u5f97\u53e3\u6c14\uff0c\u5f20\u4e39\u67ab\u51fa\u5176\u4e0d \u610f\uff0c\u5237\u7684\u53c8\u662f\u4e00\u5251\u201c\u4e91\u6a2a\u79e6\u5cad\u201d\u53d8\u4e3a\u201c\u96ea\u62e5\u84dd\u5173\u201d\uff0c\u4e00\u5251\u524a\u53bb\uff0c\u53ea\u542c\u5f97\u201c\u55e4\u201d\u7684\u4e00\u58f0\uff0c\u90a3\u9053 \u58eb\u7684\u9053\u51a0\u7adf\u7ed9\u5f20\u4e39\u67ab\u4e00\u5251\u524a\u6389\u3002 \u3000\u3000\u90a3\u9053\u58eb\u554a\u5440\u4e00\u58f0\uff0c\u8fde\u8fde\u540e\u9000\uff0c\u53eb\u9053\uff1a\u201c\u554a\u5440\uff0c\u771f\u662f\u5077\u9e21\u4e0d\u7740\u8680\u628a\u7c73\uff0c\u602a\u4e0d\u5f97\u677e\u77f3\u5e08\u5f1f\u5403 \u4e86\u5927\u4e8f\uff0c\u53d1\u8a93\u7ec8\u751f\u4e0d\u518d\u4f7f\u5251\u3002\u201d\u677e\u77f3\u9053\u4eba\u5373\u662f\u4ee5\u524d\u5e2e\u52a9\u6c99\u6d9b\u7236\u5b50\uff0c\u56fe\u52ab\u5f20\u4e39\u67ab\u7684\u5b9d\u9a6c\uff0c\u88ab\u5f20 \u4e39\u67ab\u6740\u5f97\u60e8\u8d25\u7684\u90a3\u4e2a\u4eba\u3002\u5f20\u4e39\u67ab\u542c\u4e86\u7591\u4e91\u5927\u8d77\uff0c\u6309\u5251\u95ee\u9053\uff1a\u201c\u9053\u957f\u6b64\u6765\uff0c\u4e3a\u7684\u5c31\u662f\u8981\u4e0e\u677e\u77f3 \u9053\u4eba\u62a5\u4ec7\u4e48\uff1f\u201d \u3000\u3000\u9752\u8863\u9053\u4eba\u54c8\u54c8\u5927\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u70b9\u5c0f\u4e8b\u4e5f\u8981\u62a5\u4ec7\uff0c\u6211\u54ea\u6709\u8fd9\u4e9b\u95f2\u5de5\u592b\uff1f\u770b\u4f60\u7684\u5750\u9a91\u548c\u4f60\u6240 \u4f7f\u7684\u5251\u6cd5\uff0c\u4f60\u5b9a\u7136\u662f\u5f20\u4e39\u67ab\u4e86\uff0c\u597d\u5728\u6211\u8bd5\u4f60\u4e00\u8bd5\uff0c\u5426\u5219\u4f60\u5c31\u8981\u8d70\u51a4\u6789\u8def\u3002\u6211\u95ee\u4f60\uff0c\u4f60\u4eec\u53ef\u662f \u8981\u53bb\u9ed1\u77f3\u5e84\u4e48\uff1f\u201d \u3000\u3000\u5f20\u4e39\u67ab\u6014\u4e86\u4e00\u6014\uff0c\u6309\u5251\u95ee\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u90a3\u9752\u8863\u9053\u4eba\u9053\uff1a\u201c\u6ca1\u4ec0\u4e48\uff0c\u4e0d\u8fc7\u4f60\u5230\u9ed1\u77f3\u5e84\u5b9a \u7136\u89c1\u4e0d\u7740\u8f70\u5929\u96f7\u5c31\u662f\u4e86\u3002\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u4ed6\u4e0d\u5728\u9ed1\u77f3\u5e84\u5728\u4ec0\u4e48\u5730\u65b9\uff1f\u201d\u90a3\u9053\u58eb\u9053\uff1a\u201c\u5728\u4ed6\u628a \u5f1f\u6c99\u6d9b\u7684\u5c71\u5be8\u91cc\u3002\u201d\u77f3\u82f1\u4e0e\u6c99\u6d9b\u8fc7\u5f80\u4ea4\u60c5\u867d\u597d\uff0c\u4f46\u81ea\u4ece\u628a\u5973\u513f\u8bb8\u914d\u7ed9\u4e91\u857e\u4e4b\u540e\uff0c\u4e0e\u6c99\u6d9b\u7236\u5b50 \u5df2\u6e10\u758f\u8fdc\u3002\u5f20\u4e39\u67ab\u542c\u4e86\u5c06\u4fe1\u5c06\u7591\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u8bdd\u53ef\u771f\uff1f\u201d\u90a3\u9053\u58eb\u9053\uff1a\u201c\u9a97\u4f60\u4f5c\u751a\uff1f\u6c99\u6d9b\u8fd1\u65e5 \u5927\u9080\u7eff\u6797\u8c6a\u6770\uff0c\u8d2b\u9053\u4e5f\u5728\u88ab\u9080\u4e4b\u5217\uff0c\u53ea\u662f\u4e0d\u613f\u53bb\u7f62\u4e86\u3002\u6211\u5728\u4ed6\u7684\u5c71\u4e0b\u6295\u4e86\u8c22\u8d34\uff0c\u5c3d\u4e86\u6c5f\u6e56\u4e0a \u7684\u793c\u8282\u4fbf\u5f84\u81ea\u8d70\u4e86\uff0c\u53ef\u5de7\u78b0\u7740\u77f3\u82f1\u6b63\u5728\u4e0a\u5c71\u3002\u201d\u4e91\u857e\u63d2\u53e3\u95ee\u9053\uff1a\u201c\u4ed6\u7684\u5973\u513f\u5462\uff1f\u201d\u90a3\u9053\u58eb \u9053\uff1a\u201c\u4ed6\u7684\u5973\u513f\u81ea\u7136\u662f\u548c\u4ed6\u5728\u4e00\u8d77\uff0c\u8fd8\u52b3\u4f60\u8fd9\u4f4d\u5c0f\u54e5\u5173\u6ce8\u4e48\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u6562\u95ee\u9053\u957f\u5927 \u540d\uff1f\u201d\u90a3\u9053\u58eb\u9053\uff1a\u201c\u8d2b\u9053\u662f\u6b66\u5f53\u5c71\u7684\u9053\u58eb\uff0c\u9053\u53f7\u8d64\u971e\u3002\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u539f\u6765\u662f\u8d64\u971e\u9053\u957f\uff0c\u4e45 \u4ef0\u4e86\uff01\u201d\u5f20\u4e39\u67ab\u4e4b\u8a00\u5e76\u975e\u5ba2\u5957\uff0c\u8fd9\u8d64\u971e\u9053\u4eba\u5728\u6b66\u5f53\u6d3e\u7684\u9053\u58eb\u4e2d\u7d20\u6709\u4fa0\u540d\u3002 \u3000\u3000\u8d64\u971e\u9053\u4eba\u5ffd\u9053\uff1a\u201c\u8d2b\u9053\u8fd8\u542c\u5f97\u4e00\u4e9b\u9053\u8def\u7684\u4f20\u8a00\uff0c\u5c1a\u4e0d\u77e5\u662f\u771f\u662f\u5047\uff1f\u201d\u5f20\u4e39\u67ab\u6025\u9053\uff1a\u201c\u4ec0 \u4e48\u4f20\u8a00\uff1f\u201d\u8d64\u971e\u9053\u4eba\u9053\uff1a\u201c\u542c\u8bf4\u74e6\u523a\u5927\u519b\u5360\u636e\u8fd9\u4e00\u5e26\u7684\u65f6\u5019\uff0c\u5bf9\u6c99\u6d9b\u7236\u5b50\u9887\u5356\u4ea4\u60c5\uff0c\u6240\u4ee5\u4ed6 \u7684\u5c71\u5be8\u5c1a\u5f97\u4fdd\u5168\u3002\u201d\u5f20\u4e39\u67ab\u5403\u4e86\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u77f3\u82f1\u77e5\u9053\u5417\uff1f\u201d\u8d64\u971e\u9053\uff1a\u201c\u90a3\u6211\u5c31\u4e0d\u77e5\u9053\u4e86\u3002 \u6211\u672c\u60f3\u5bf9\u77f3\u82f1\u8bf4\u7684\uff0c\u65e0\u5948\u6c99\u6d9b\u7684\u4eba\u966a\u7740\u4ed6\uff0c\u672a\u6709\u673a\u4f1a\u4e0e\u4ed6\u5355\u72ec\u76f8\u8c08\u3002\u201d\u5f20\u4e39\u67ab\u201c\u54ce\u5440\u201d\u4e00\u58f0 \u8df3\u4e86\u8d77\u6765\uff0c\u62f1\u624b\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u9053\u957f\u6307\u5f15\u3002\u201d\u7ffb\u8eab\u4e0a\u9a6c\uff0c\u7acb\u523b\u5954\u8dd1\u3002\u8d64\u971e\u9053\u4eba\u4e5f\u72ec\u81ea\u5411\u4e1c\u8d70 \u4e86\u3002 \u3000\u3000\u8def\u4e0a\u4e91\u857e\u95ee\u9053\uff1a\u201c\u8fd9\u9053\u4eba\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u542c\u4ed6\u53e3\u6c14\uff0c\u6c99\u6d9b\u7236\u5b50\u5fc5\u6709\u56fe\u8c0b\uff0c \u6781\u53ef\u80fd\u662f\u5e03\u4e0b\u5708\u5957\uff0c\u8bf1\u77f3\u82f1\u4e0a\u5f53\u7684\u3002\u4ed6\u521a\u624d\u5728\u9152\u697c\u76f8\u8bd5\uff0c\u662f\u60f3\u8bd5\u51fa\u54b1\u4eec\u7684\u8eab\u4efd\uff0c\u6307\u5f15\u54b1\u4eec\u53bb \u6551\u77f3\u82f1\u3002\u201d\u4e91\u857e\u60ca\u9053\uff1a\u201c\u6709\u8fd9\u4e48\u5927\u7684\u5371\u9669\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u53cd\u6b63\u54b1\u4eec\u9a6c\u5feb\uff0c\u5c31\u5148\u5230\u9ed1\u77f3\u5e84\u53bb \u770b\u770b\uff0c\u82e5\u7136\u77f3\u8001\u82f1\u96c4\u5f53\u771f\u4e0d\u5728\uff0c\u54b1\u4eec\u518d\u53bb\u8ddf\u6c99\u6d9b\u7b97\u5e10\u3002\u201d \u3000\u3000\u4e24\u4eba\u98de\u9a6c\u8d76\u8def\uff0c\u4e0d\u5230\u534a\u4e2a\u65f6\u8fb0\uff0c\u4fbf\u8d76\u5230\u4e86\u9ed1\u77f3\u5e84\u524d\u3002\u53ea\u89c1\u5e84\u95e8\u5927\u5f00\uff0c\u91cc\u9762\u4e00\u7247\u5608\u5608\u6742\u6742 \u7684\u58f0\u97f3\uff0c\u5f20\u3001\u4e91\u4e8c\u4eba\u62d4\u5251\u95ef\u65f6\uff0c\u91cc\u9762\u4e24\u4e2a\u5c71\u5be8\u5934\u76ee\u6a21\u6837\u7684\u4eba\u51fa\u6765\u62e6\u622a\uff0c\u4ea4\u624b\u4e0d\u5230\u4e09\u4e2a\u56de\u5408\uff0c \u4fbf\u7ed9\u5f20\u3001\u4e91\u4e8c\u4eba\u6740\u4f24\u6251\u5730\uff0c\u53ea\u89c1\u77f3\u5bb6\u7684\u5e84\u4e01\u5341\u4e4b\u516b\u4e5d\u5df2\u88ab\u6346\u7f1a\uff0c\u53ea\u6709\u51e0\u4e2a\u6b66\u529f\u8f83\u5f3a\u7684\u8fd8\u5728\u91cc \u9762\u4e0e\u55bd\u5175\u25a1\u6740\u3002\u5f20\u4e39\u67ab\u4e0e\u4e91\u857e\u5927\u5c55\u795e\u5a01\uff0c\u5de6\u4e00\u62f3\u53f3\u4e00\u811a\uff0c\u6740\u8fdb\u6740\u51fa\uff0c\u4e0d\u8fc7\u534a\u4e2a\u65f6\u8fb0\uff0c\u5c06\u4fb5\u88ad \u9ed1\u77f3\u5e84\u7684\u55bd\u5175\u5168\u90fd\u70b9\u4e86\u7a74\u9053\uff0c\u628a\u5e84\u4e01\u4e00\u4e00\u89e3\u6551\uff0c\u95ee\u8d77\u60c5\u7531\uff0c\u5e84\u4e01\u8bf4\u9053\uff1a\u201c\u5e84\u4e3b\u53bb\u540e\uff0c\u4e0d\u5230\u534a \u5929\uff0c\u8fd9\u73ed\u76d7\u5c31\u6740\u6765\u4e86\uff0c\u8d77\u521d\u6211\u4eec\u8fd8\u4ee5\u4e3a\u4ed6\u4eec\u662f\u6c99\u6d9b\u7684\u624b\u4e0b\uff0c\u4e0e\u5e84\u4e3b\u6709\u4ea4\u60c5\uff0c\u4fbf\u653e\u4ed6\u4eec\u8fdb\u6765\uff0c \u54ea\u77e5\u4ed6\u4eec\u5c45\u7136\u6562\u660e\u706b\u6253\u52ab\uff01\u8fd9\u771f\u662f\u9ed1\u77f3\u5e84\u4e4b\u8fb1\uff0c\u5e84\u4e3b\u82e5\u7136\u5f97\u77e5\uff0c\u5b9a\u8981\u4e86\u4ed6\u4eec\u7684\u72d7\u547d\uff01\u201d\u5f20\u4e39 \u67ab\u89e3\u5f00\u4e86\u4e00\u4e2a\u5934\u76ee\u7684\u7a74\u9053\uff0c\u559d\u9053\uff1a\u201c\u662f\u6c99\u6d9b\u53eb\u4f60\u4eec\u6765\u7684\u4e48\uff1f\u6765\u5e72\u4ec0\u4e48\u4e8b\u60c5\uff1f\u201d \u3000\u3000\u90a3\u5934\u76ee\u9887\u662f\u5f3a\u786c\uff0c\u95ed\u53e3\u4e0d\u7b54\uff0c\u5f20\u4e39\u67ab\u5fae\u5fae\u4e00\u7b11\uff0c\u5728\u4ed6\u80c1\u4e0b\u4e00\u6233\u559d\u9053\uff1a\u201c\u4f60\u8bf4\u4e0d\u8bf4\uff1f\u201d\u8fd9 \u4e00\u6233\u662f\u5f20\u4e39\u67ab\u7684\u72ec\u95e8\u70b9\u7a74\u624b\u6cd5\uff0c\u4e0d\u6d88\u7247\u523b\uff0c\u90a3\u5934\u76ee\u53ea\u89c9\u4f53\u5185\u5982\u904d\u5e03\u94f6\u9488\uff0c\u4e71\u6233\u4e71\u94bb\uff0c\u5fcd\u53d7\u4e0d \u4f4f\u614c\u5fd9\u8ba8\u9976\u3002\u5f20\u4e39\u67ab\u5bf9\u4e91\u857e\u7b11\u9053\uff1a\u201c\u6211\u672c\u4e0d\u613f\u65bd\u6b64\u9177\u5211\uff0c\u4f46\u5bf9\u4ed8\u8fd9\u79cd\u4eba\uff0c\u9664\u6b64\u4e4b\u5916\uff0c\u5374\u662f\u65e0 \u6cd5\u3002\u201d\u90a3\u5934\u76ee\u9053\uff1a\u201c\u6c99\u5be8\u4e3b\u5429\u5490\u6211\u4eec\uff0c\u5c06\u9ed1\u77f3\u5e84\u6240\u6709\u7684\u4e1c\u897f\u5168\u90fd\u642c\u56de\u5c71\u5be8\uff0c\u5c24\u5176\u662f\u4ed6\u6240\u85cf\u7684 \u5b57\u753b\u66f4\u4e0d\u53ef\u5c11\u4e86\u4e00\u5f20\u3002\u201d\u5f20\u4e39\u67ab\u4e00\u542c\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u6c99\u6d9b\u4e4b\u5fd7\u5b9a\u7136\u4e0d\u5728\u8d22\u7269\uff0c\u4ed6\u641c\u5bfb\u5b57\u753b\uff0c \u770b\u6765\u5b9a\u662f\u4ee5\u4e3a\u90a3\u5f20\u85cf\u5b9d\u7684\u5730\u56fe\u8fd8\u5728\u77f3\u5bb6\u4e86\uff0c\u53ea\u662f\u6b64\u4e8b\u4ed6\u5982\u4f55\u5f97\u77e5\uff1f\u201d\u4e91\u857e\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4f60\u60f3 \u4e9b\u4ec0\u4e48\u5462\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u8d64\u971e\u4e4b\u8a00\u4e0d\u5047\uff0c\u8fd9\u6c99\u6d9b\u5b9a\u662f\u79c1\u901a\u74e6\u523a\u65e0\u7591\u5566\u3002\u201d\u4e00\u638c\u62cd\u4e0b\uff0c\u5c06\u90a3 \u540d\u5934\u76ee\u7684\u7a74\u9053\u89e3\u4e86\uff0c\u5bf9\u77f3\u5bb6\u7684\u7ba1\u5bb6\u9053\uff1a\u201c\u4f60\u5c06\u8fd9\u4f19\u5f3a\u76d7\u90fd\u6346\u7f1a\u4e86\uff0c\u5f85\u4f60\u5bb6\u7684\u5e84\u4e3b\u56de\u6765\uff0c\u518d\u4f5c \u9053\u7406\u3002\u201d \u3000\u3000\u5f20\u4e39\u67ab\u4e0e\u4e91\u857e\u79bb\u5f00\u77f3\u5bb6\uff0c\u6025\u6025\u8d76\u8def\u3002\u6c99\u6d9b\u7684\u5c71\u5be8\u5728\u9644\u8fd1\u7684\u516d\u6a1f\u5c71\uff0c\u79bb\u9ed1\u77f3\u5e84\u7ea6\u4e09\u5341\u91cc \u5730\uff0c\u5f20\u3001\u4e91\u4e8c\u4eba\u9a6c\u5feb\uff0c\u4e0d\u5230\u534a\u4e2a\u65f6\u8fb0\uff0c\u4fbf\u5df2\u8d76\u81f3\u5c71\u4e0b\u3002\u53ea\u89c1\u5c71\u5be8\u8fde\u5c71\u800c\u8d77\uff0c\u52bf\u5982\u957f\u9f99\uff0c\u5c71\u5cf0 \u4e0a\u7889\u5821\u7f57\u5217\uff0c\u53e4\u6728\u53c2\u5929\uff0c\u9887\u662f\u96c4\u4f1f\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u4e0e\u4e91\u857e\u5c06\u9a6c\u653e\u4e86\u53cc\u53cc\u4e0a\u5c71\uff0c\u773a\u671b\u7684\u55bd\u5175\u559d\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u4f60\u5bb6\u5be8 \u4e3b\u9080\u8bf7\u7684\u5bbe\u5ba2\u3002\u201d\u55bd\u5175\u9053\uff1a\u201c\u5c06\u8bf7\u8d34\u62ff\u6765\u3002\u201d\u5f20\u4e39\u67ab\u628a\u624b\u4e00\u626c\uff0c\u9053\uff1a\u201c\u63a5\u597d\u4e86\uff01\u201d\u90a3\u55bd\u5175\u7741 \u773c\u4e00\u77a7\u7a7a\u65e0\u4e00\u7269\uff0c\u6b63\u60f3\u559d\u95ee\uff0c\u9661\u7136\u95f4\u5ffd\u89c9\u5fc3\u7a9d\u4e00\u9ebb\uff0c\u7acb\u523b\u6655\u5012\u3002\u539f\u6765\u662f\u5f20\u4e39\u67ab\u65bd\u5c55\u795e\u9488\u5999 \u6280\uff0c\u523a\u4e86\u4ed6\u7684\u7a74\u9053\uff0c\u8981\u8fc7\u4e86\u5341\u4e8c\u4e2a\u65f6\u8fb0\u4e4b\u540e\uff0c\u65b9\u80fd\u81ea\u89e3\u3002 \u3000\u3000\u5f20\u3001\u4e91\u4e8c\u4eba\u65bd\u5c55\u7edd\u9876\u8f7b\u529f\uff0c\u8f7b\u767b\u5de7\u7eb5\uff0c\u9047\u6709\u62e6\u622a\u7684\u5934\u76ee\uff0c\u80fd\u907f\u8fc7\u4fbf\u907f\u8fc7\uff0c\u4e0d\u80fd\u907f\u8fc7\u4fbf\u7528 \u98de\u9488\u5c06\u4ed6\u5c04\u5012\uff0c\u4e0d\u6d88\u591a\u4e45\uff0c\u4fbf\u5df2\u5230\u4e86\u5c71\u4e0a\uff0c\u9661\u89c1\u4e00\u5c42\u5ced\u58c1\u62d4\u5730\u800c\u8d77\uff0c\u524d\u9762\u9664\u4e86\u4e00\u6839\u77f3\u6881\u4e4b \u5916\uff0c\u65e0\u8def\u53ef\u901a\u3002\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u6b64\u5730\u9669\u8981\uff0c\u7ecf\u8fc7\u5c0f\u5fc3\uff01\u201d\u8e0f\u4e0a\u77f3\u6881\uff0c\u4e91\u857e\u8ddf\u5728\u540e\u9762\uff0c\u65b9\u81f3\u4e2d \u9014\uff0c\u5ffd\u542c\u5f97\u80cc\u540e\u5f13\u5f26\u75be\u54cd\uff0c\u4e71\u7bad\u9f50\u53d1\uff0c\u4e91\u857e\u65e9\u62d4\u51fa\u5b9d\u5251\uff0c\u821e\u8d77\u4e00\u5708\u94f6\u8679\uff0c\u7b11\u9053\uff1a\u201c\u4e71\u7bad\u80fd\u5948 \u6211\u4f55\uff1f\u201d\u8bdd\u58f0\u672a\u4e86\uff0c\u5ced\u58c1\u4e0a\u7a81\u7136\u8df3\u4e0b\u4e00\u4eba\u3002\u5f20\u4e39\u67ab\u4e00\u62db\u201c\u4e3e\u706b\u71ce\u5929\u201d\uff0c\u5251\u950b\u4e0a\u6233\uff0c\u53ea\u89c9\u6765\u4eba \u8155\u52b2\u5947\u5927\uff0c\u5f53\u7684\u4e00\u58f0\uff0c\u864e\u53e3\u53d1\u70ed\uff0c\u90a3\u4eba\u5df2\u8dc3\u4e86\u4e0b\u6765\uff0c\u5728\u5f20\u3001\u4e91\u4e2d\u95f4\u4e00\u63d2\uff0c\u60f3\u628a\u4e91\u857e\u786c\u751f\u751f\u6454 \u4e0b\u77f3\u6881\uff01 \u3000\u3000\u77f3\u6881\u72ed\u7a84\uff0c\u53cc\u5251\u96be\u4e8e\u65bd\u5c55\uff0c\u5f20\u4e39\u67ab\u5ffd\u7136\u5c16\u53eb\u4e00\u58f0\uff0c\u8eab\u8eaf\u4e00\u98a4\uff0c\u8dc3\u51fa\u77f3\u6881\u3002\u4e91\u857e\u4e00\u58f0\u9a87 \u53eb\uff0c\u90a3\u4eba\u4ee5\u4e3a\u5f20\u4e39\u67ab\u5df2\u7ecf\u5931\u8db3\u5760\u4e0b\u5fc3\u4e2d\u5927\u559c\uff0c\u98de\u811a\u4fbf\u8e22\u3002\u54ea\u6599\u5f20\u4e39\u67ab\u65bd\u5c55\u8be1\u8ba1\uff0c\u53cc\u8db3\u4ecd\u7136\u52fe \u7d27\u77f3\u6881\uff0c\u84e6\u5730\u4e00\u628a\u98de\u9488\uff0c\u8fce\u9762\u6492\u53bb\uff0c\u90a3\u4eba\u65e0\u53ef\u95ea\u907f\uff0c\u767e\u5fd9\u4e4b\u4e2d\uff0c\u8eab\u5f62\u51ed\u7a7a\u62d4\u8d77\u4e08\u8bb8\uff0c\u5c06\u98de\u9488 \u907f\u8fc7\uff0c\u4f46\u5f20\u4e39\u67ab\u4e0e\u4e91\u857e\u8d81\u6b64\u673a\u4f1a\uff0c\u4ea6\u5df2\u5b89\u7136\u5730\u901a\u8fc7\u4e86\u77f3\u6881\u3002\u90a3\u4eba\u72c2\u53eb\u4e00\u58f0\uff0c\u53c8\u518d\u6251\u4e0b\uff0c\u540c\u65f6 \u5c71\u5cf0\u4e0a\u4ea6\u5df2\u7a9c\u4e0b\u51e0\u4eba\uff0c\u5e03\u6210\u4e86\u7284\u89d2\u4e4b\u52bf\u3002\u5f20\u4e39\u67ab\u89c1\u90a3\u4eba\u6b66\u529f\u9ad8\u5f3a\uff0c\u5fc3\u4e2d\u4e5f\u81ea\u4e00\u6014\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u90a3\u4eba\u4e00\u58f0\u60ca\u53eb\uff0c\u559d\u9053\uff1a\u201c\u54fc\uff0c\u539f\u6765\u662f\u4f60\uff01\u201d\u5f20\u4e39\u67ab\u4e5f\u559d\u9053\uff1a\u201c\u54fc\uff0c\u539f\u6765\u662f\u4f60\uff01\u201d \u9002\u624d\u5728\u77f3\u6881\u4e4b\u4e0a\uff0c\u53cc\u65b9\u867d\u6362\u4e86\u51e0\u62db\uff0c\u4f46\u90a3\u662f\u95ea\u7535\u822c\u7684\u88ad\u51fb\uff0c\u5927\u5bb6\u5168\u795e\u8d2f\u6ce8\u5e94\u4ed8\u5bf9\u65b9\u7684\u6740\u624b\uff0c \u65e0\u6687\u7559\u5fc3\u9762\u8c8c\uff0c\u8fd9\u65f6\u770b\u6e05\u695a\u4e86\uff0c\u4e0d\u7ea6\u800c\u540c\u5730\u53eb\u51fa\u58f0\u6765\u3002 \u3000\u3000\u8fd9\u4eba\u6b63\u662f\u4e5f\u5148\u5e10\u4e0b\u7684\u7b2c\u4e00\u540d\u6b66\u58eb\u989d\u5409\u591a\uff0c\u5f20\u4e39\u67ab\u5728\u571f\u6728\u5821\u7684\u519b\u8425\u4e2d\u66fe\u4e0e\u4ed6\u4ea4\u8fc7\u624b\uff0c\u6df1\u77e5 \u4ed6\u6b66\u529f\u9ad8\u5f3a\uff0c\u5728\u74e6\u523a\u56fd\u4e2d\uff0c\u4ec5\u5728\u6fb9\u53f0\u706d\u660e\u4e4b\u4e0b\uff0c\u4e0d\u6562\u5927\u610f\uff0c\u6025\u5fd9\u53eb\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u54b1\u4eec\u64d2\u8d3c \u64d2\u4e3b\uff0c\u5148\u628a\u8fd9\u4eba\u5e9f\u4e86\uff01\u201d\u4e91\u857e\u5251\u8d70\u504f\u950b\uff0c\u5237\u7684\u4e00\u5251\u523a\u51fa\uff0c\u53cc\u5251\u5408\u74a7\uff0c\u5947\u5999\u65e0\u6bd4\uff0c\u989d\u5409\u591a\u62db\u6570 \u672a\u53d1\uff0c\u4e24\u53e3\u660e\u6643\u6643\u7684\u5229\u5251\u5df2\u540c\u65f6\u903c\u8fd1\u9762\u95e8\u3002\u989d\u5409\u591a\u5927\u559d\u4e00\u58f0\uff0c\u6a2a\u5251\u4e00\u5c01\uff0c\u54ea\u5c01\u5f97\u4f4f\uff0c\u53ea\u542c\u5f97 \u201c\u5580\u5693\u201d\u4e24\u58f0\uff0c\u624b\u4e2d\u7684\u957f\u5251\u5df2\u65ad\u4e3a\u56db\u6bb5\uff0c\u989d\u5409\u591a\u98de\u8eab\u4e00\u8dc3\uff0c\u53cc\u5251\u4f59\u5a01\u672a\u5c3d\uff0c\u6a2a\u524a\u8fc7\u53bb\uff0c\u987f\u65f6 \u4f24\u4e86\u4e24\u4eba\u3002\u989d\u5409\u591a\u6025\u81ea\u540c\u4f34\u624b\u4e2d\u62a2\u8fc7\u4e00\u53e3\u957f\u5251\uff0c\u5f20\u3001\u4e91\u4e8c\u4eba\u53cc\u5251\u53c8\u5230\uff0c\u8fd9\u65f6\u4ed6\u4e0d\u6562\u786c\u67b6\uff0c\u5251 \u950b\u4e00\u98a4\u4f7f\u51fa\u98ce\u96f7\u5251\u6cd5\u7684\u7edd\u62db\u201c\u96f7\u7535\u4ea4\u8f70\u201d\uff0c\u867d\u662f\u4e00\u53e3\u666e\u901a\u7684\u957f\u5251\uff0c\u7ecf\u4ed6\u4e00\u6296\uff0c\u4e5f\u81ea\u55e1\u55e1\u6709 \u58f0\uff0c\u5251\u82b1\u8000\u773c\uff0c\u4e00\u53e3\u5251\u5c31\u5982\u540c\u5316\u4e86\u5341\u6570\u53e3\u4e00\u822c\u3002\u5f20\u4e39\u67ab\u53eb\u4e00\u58f0\u201c\u597d\uff01\u201d\u53cc\u5251\u4e00\u63a0\u800c\u8fc7\u53ea\u542c\u5f97 \u53c8\u662f\u201c\u55e4\u201d\u7684\u4e00\u58f0\uff0c\u989d\u5409\u591a\u7684\u5934\u7f28\u53c8\u88ab\u524a\u4e86\u3002\u4f46\u4ed6\u90a3\u4e00\u62db\u865a\u865a\u5b9e\u5b9e\uff0c\u53d8\u5316\u751a\u591a\uff0c\u7adf\u7136\u5728\u53cc\u5251 \u6025\u88ad\u4e4b\u4e0b\uff0c\u8131\u8eab\u95ea\u8fc7\uff0c\u5f20\u4e39\u67ab\u524a\u4e0d\u65ad\u4ed6\u7684\u5175\u5668\uff0c\u4e5f\u662f\u9887\u51fa\u610f\u5916\uff01 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u4e91\u857e\u5237\u7684\u4e00\u5251\u5206\u5fc3\u76f4\u523a\uff0c\u5f20\u4e39\u67ab\u5251\u5149\u4e00\u7ed5\uff0c\u5374\u622a\u4e0b\u76d8\uff0c\u53cc\u5251\u4e00\u5408\u5b9b\u5982 \u4e00\u9053\u5149\u73af\uff0c\u628a\u989d\u5409\u591a\u7b8d\u5728\u5f53\u4e2d\u3002\u53cc\u5251\u5408\u74a7\uff0c\u5a01\u529b\u4e00\u62db\u5927\u8fc7\u4e00\u62db\uff0c\u989d\u5409\u591a\u82e5\u7136\u8981\u907f\u4e91\u857e\u90a3\u4e00\u5251 \u7a7f\u5fc3\u4e4b\u7978\uff0c\u53cc\u811a\u5c31\u5f97\u88ab\u5f20\u4e39\u67ab\u90a3\u4e00\u5251\u524a\u65ad\uff1b\u82e5\u8981\u907f\u5f00\u5f20\u4e39\u67ab\u7684\u6740\u624b\uff0c\u4e91\u857e\u90a3\u4e00\u5251\u5c31\u96be\u8eb2\u907f\uff0c \u6216\u662f\u53d7\u4f24\u6b8b\u5e9f\uff0c\u6216\u662f\u547d\u4e27\u5f53\u573a\uff0c\u8fd9\u4e24\u8005\u4e4b\u95f4\uff0c\u53ea\u80fd\u9009\u62e9\u5176\u4e00\u3002 \u3000\u3000\u989d\u5409\u591a\u5fc3\u5934\u4e00\u60ca\u60f3\u9053\uff1a\u201c\u6211\u5c31\u662f\u6b7b\u4e86\u4e5f\u4e0d\u80fd\u65ad\u8db3\u53d7\u8fb1\u3002\u201d\u632f\u5251\u4e0b\u8fce\uff0c\u5148\u62a4\u4e0b\u76d8\uff0c\u4e91\u857e\u4e00 \u5251\u75be\u8fdb\uff0c\u773c\u770b\u5c31\u8981\u7a7f\u5fc3\u800c\u8fc7\uff0c\u5ffd\u89c9\u4e00\u80a1\u52b2\u98ce\uff0c\u51b2\u9762\u800c\u6765\uff0c\u4e91\u857e\u8f7b\u8f7b\u4e00\u95ea\uff0c\u5b9d\u5251\u523a\u7a7a\uff0c\u6b63\u62df\u6362 \u62db\u53ea\u542c\u5f97\u5f53\u7684\u4e00\u58f0\uff0c\u989d\u5409\u591a\u4e00\u58f0\u5389\u53eb\uff0c\u5012\u8dc3\u51fa\u4e00\u4e08\u5f00\u5916\u3002\u63a5\u7740\u6709\u4e00\u4e2a\u7c97\u8c6a\u7684\u58f0\u97f3\u5927\u559d\u9053\uff1a \u201c\u4f4f\u624b\uff01\u201d\u9762\u524d\u7a81\u7136\u591a\u4e86\u4e00\u4eba\uff0c\u8499\u7740\u9762\u5b54\uff0c\u53ea\u9732\u51fa\u4e00\u53cc\u70af\u70af\u6709\u5149\u7684\u5927\u773c\u775b\uff0c\u53cc\u62f3\u6025\u88ad\u3002\u6551\u4e86 \u989d\u5409\u591a\u6027\u547d\u7684\u5c31\u662f\u8fd9\u4e2a\u4eba\uff01 \u3000\u3000\u8fd9\u51e0\u4e0b\u90fd\u6765\u5f97\u8fc5\u75be\u5f02\u5e38\uff0c\u989d\u5409\u591a\u7684\u957f\u5251\u867d\u7ed9\u5f20\u4e39\u67ab\u524a\u65ad\uff0c\u80eb\u9aa8\u4e5f\u53d7\u4e86\u5251\u4f24\uff0c\u4f46\u5374\u4fdd\u4f4f\u4e86 \u6027\u547d\uff0c\u8fd9\u65f6\u6b63\u5728\u65c1\u8fb9\u5598\u6c14\u3002\u90a3\u8499\u9762\u4eba\u9053\uff1a\u201c\u4e24\u4f4d\u65e2\u7136\u62dc\u5c71\uff0c\u8bf7\u4f9d\u6c5f\u6e56\u89c4\u77e9\uff0c\u5148\u5230\u5927\u5be8\u518d\u8bf4\uff0c \u5c82\u53ef\u4e0d\u5206\u7682\u767d\uff0c\u5c31\u5728\u5be8\u524d\u25a1\u6740\uff1f\u201d\u8fd9\u4eba\u7adf\u7136\u80fd\u5728\u53cc\u5251\u5408\u74a7\u4e4b\u4e0b\uff0c\u5c06\u989d\u5409\u591a\u62a2\u6551\u51fa\u6765\uff0c\u6b66\u529f\u4e4b \u5f3a\uff0c\u5b9e\u662f\u96be\u4ee5\u4f30\u91cf\uff01\u5f20\u4e39\u67ab\u4e5f\u4e0d\u7531\u5f97\u6697\u6697\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u600e\u4e48\u6c99\u6d9b\u7236\u5b50\uff0c\u5c45\u7136\u80fd\u9080\u5f97\u8fd9 \u6837\u7684\u9ad8\u660e\u4e4b\u58eb\uff1f\u4eca\u65e5\u4e4b\u4e8b\uff0c\u53ea\u6050\u4e0d\u662f\u8f7b\u6613\u53ef\u4e86\uff01\u201d \u3000\u3000\u4e91\u857e\u5ffd\u9053\uff1a\u201c\u4f60\u662f\u80e1\u4eba\u8fd8\u662f\u6c49\u4eba\uff1f\u201d\u90a3\u4eba\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u4f60\u8fd9\u8bdd\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d\u4e91\u857e \u9053\uff1a\u201c\u770b\u4f60\u5916\u8c8c\u4f3c\u662f\u4e00\u4e2a\u6c49\u4eba\uff0c\u4f46\u5374\u5e2e\u52a9\u80e1\u4eba\uff0c\u83ab\u975e\u4f60\u4e5f\u81ea\u77e5\u7f9e\u803b\uff0c\u6240\u4ee5\u8499\u4e0a\u9762\u5b54\u4e48\uff1f\u201d\u90a3 \u4eba\u52c3\u7136\u5927\u6012\uff0c\u817e\u8eab\u4e00\u8dc3\uff0c\u6a2a\u638c\u4e00\u62b9\uff0c\u653b\u52bf\u98d8\u5ffd\uff0c\u731b\u4e0b\u6740\u624b\uff0c\u5f20\u4e39\u67ab\u6025\u5fd9\u4e00\u5251\u523a\u51fa\uff0c\u53cc\u5251\u4e00 \u5408\uff0c\u5206\u523a\u90a3\u4eba\u7684\u5de6\u53f3\u80a9\u4e95\u7a74\uff0c\u90a3\u8499\u9762\u4eba\u7684\u638c\u52bf\u602a\u5f02\u65e0\u6bd4\uff0c\u6bcf\u62db\u53d1\u51fa\uff0c\u90fd\u4f3c\u4e4e\u662f\u540c\u65f6\u8fdb\u88ad\u4e8c \u4eba\uff0c\u98d8\u5ffd\u65e0\u5b9a\uff0c\u7728\u773c\u4e4b\u95f4\uff0c\u62c6\u4e86\u4e09\u62db\u3002\u5f20\u3001\u4e91\u4e8c\u4eba\u5251\u6cd5\uff0c\u4e43\u662f\u7384\u673a\u9038\u58eb\u6bd5\u751f\u5fc3\u529b\u6240\u521b\uff0c\u4fe1\u624b \u53d1\u62db\uff0c\u81ea\u7136\u914d\u5408\uff0c\u5999\u5230\u6beb\u5dc5\uff0c\u90a3\u4eba\u6321\u4e86\u4e09\u62db\uff0c\u5c1a\u672a\u5403\u4e8f\uff0c\u63a5\u5230\u4e86\u7b2c\u56db\u62db\u3001\u7b2c\u4e94\u62db\uff0c\u6e10\u89c9\u5e94\u4ed8 \u8270\u96be\uff0c\u53cc\u5251\u653b\u52bf\u50ac\u7d27\uff0c\u4e00\u53e3\u6c14\u53c8\u8fde\u8fdb\u4e09\u62db\uff0c\u6740\u5f97\u90a3\u4eba\u8fde\u8fde\u540e\u9000\u3002\u4e91\u857e\u51b7\u7b11\u9053\uff1a\u201c\u4e71\u81e3\u8d3c\u5b50\uff0c \u4eba\u4eba\u5f97\u800c\u8bdb\u4e4b\uff0c\u6211\u4e0e\u4f60\u8bb2\u4ec0\u4e48\u6c5f\u6e56\u89c4\u77e9\uff1f\u201d\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u53c8\u62a2\u653b\u4e86\u4e09\u62db\uff0c\u90a3\u4eba\u53ea\u6709\u62db\u67b6\u4e4b\u529f\uff0c \u5df2\u65e0\u8fd8\u624b\u4e4b\u529b\u4e86\u3002\u5f20\u4e39\u67ab\u5ffd\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u4f4f\u624b\uff01\u201d\u4e91\u857e\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u6b64\u4eba \u4ee5\u4e00\u53cc\u8089\u638c\uff0c\u63a5\u4e86\u54b1\u4eec\u5341\u62db\u6709\u591a\uff0c\u4e5f\u7b97\u5f97\u662f\u4e00\u540d\u597d\u6c49\u4e86\uff0c\u6740\u4e86\u4ed6\u4ed6\u4e5f\u4e0d\u670d\uff0c\u597d\uff0c\u5c31\u968f\u4ed6\u5148\u5230 \u5c71\u5be8\u91cc\u770b\u770b\u3002\u201d\u4e91\u857e\u5fc3\u4e2d\u9887\u4e0d\u4ee5\u4e3a\u7136\uff0c\u4f46\u5f53\u7740\u4eba\u524d\uff0c\u5374\u4e5f\u4e0d\u4fbf\u4e0e\u5f20\u4e39\u67ab\u4e89\u6267\uff0c\u53ea\u597d\u505c\u624b\u3002\u5979 \u53ef\u4e0d\u77e5\uff0c\u5f20\u4e39\u67ab\u6b63\u5728\u7528\u5fc3\u63a8\u6d4b\u90a3\u4eba\u7684\u6765\u5386\uff0c\u90a3\u4eba\u7684\u6b66\u529f\u867d\u7136\u602a\u5f02\uff0c\u4f46\u5728\u62c6\u4e86\u5341\u4f59\u62db\u4e4b\u540e\uff0c\u5f20 \u4e39\u67ab\u5df2\u53d1\u89c9\u6709\u7ebf\u7d22\u53ef\u5bfb\u3002 \u3000\u3000\u90a3\u8499\u9762\u4eba\u77a7\u4e86\u5f20\u3001\u4e91\u4e8c\u4eba\u4e00\u773c\uff0c\u5ffd\u9053\uff1a\u201c\u4f60\u4eec\u7684\u5251\u6cd5\u662f\u4f55\u4eba\u6240\u6388\uff1f\u201d\u4e91\u857e\u9053\uff1a\u201c\u4f60\u8fd9\u25a1 \u5c82\u914d\u95ee\u6211\u7684\u5e08\u5c0a\uff1f\u201d\u90a3\u4eba\u4e00\u6012\uff0c\u5c31\u60f3\u53d1\u4f5c\uff0c\u5374\u53c8\u5fcd\u7740\uff0c\u201c\u54fc\u201d\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u5c0f\u5a03\u5a03\u4e0d\u77e5\u597d \u574f\uff0c\u7b49\u4f1a\u513f\u518d\u4e0e\u4f60\u4eec\u89c1\u4e2a\u771f\u7ae0\uff01\u201d \u3000\u3000\u8499\u9762\u4eba\u5728\u524d\u5e26\u5f15\uff0c\u8fdb\u5165\u5c71\u5be8\uff0c\u5e26\u8fdb\u4e86\u201c\u805a\u4e49\u5385\u201d\u3002\u8fd9\u5ea7\u5927\u5385\u5341\u5206\u5bbd\u655e\uff0c\u5c31\u5982\u4e00\u4e2a\u6709\u4e0a\u76d6 \u7684\u6f14\u6b66\u573a\u4e00\u6837\uff0c\u5385\u4e2d\u5750\u6ee1\u4e86\u4e09\u5c71\u4e94\u5cb3\u7684\u4eba\u7269\uff0c\u89c1\u5f20\u4e39\u67ab\u4e0e\u4e91\u857e\u4e8c\u4eba\uff0c\u6cf0\u7136\u81ea\u82e5\uff0c\u6ee1\u4e0d\u5728\u4e4e\u5730 \u7f13\u7f13\u884c\u6765\uff0c\u65e0\u4e0d\u4fa7\u76ee\u800c\u89c6\u3002\u4e91\u857e\u4e00\u773c\u77a5\u53bb\uff0c\u53ea\u89c1\u77f3\u82f1\u7236\u5973\u88ab\u56f4\u5728\u5f53\u4e2d\uff0c\u77f3\u7fe0\u51e4\u4fcf\u773c\u76c8\u76c8\uff0c\u76ef \u7740\u81ea\u5df1\uff0c\u4e00\u526f\u4f3c\u6028\u4f3c\u559c\u7684\u795e\u60c5\uff0c\u6b63\u6b32\u5f20\u53e3\u800c\u547c\uff0c\u77f3\u82f1\u5374\u62a2\u5148\u8bf4\u9053\uff1a\u201c\u8d24\u5a7f\uff0c\u4f60\u4e5f\u6765\u4e86\uff1f\u8fd9\u91cc \u7684\u4e8b\u4e0e\u4f60\u65e0\u5e72\uff01\u201d\u5f20\u4e39\u67ab\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e0e\u4ed6\u65e0\u5e72\uff0c\u90a3\u5b9a\u662f\u4e0e\u6211\u6709\u5e72\u4e86\uff1f\u201d\u508d\u7740\u77f3\u82f1\u4e00\u540c \u5750\u4e0b\u3002\u6c99\u6d9b\u6012\u76ee\u800c\u89c6\uff0c\u9053\uff1a\u201c\u597d\u5440\uff0c\u4f60\u8981\u62db\u63fd\u8fc7\u6765\uff0c\u90a3\u662f\u6700\u597d\u4e0d\u8fc7\uff01\u201d\u6c99\u6d9b\u7684\u513f\u5b50\u6c99\u65e0\u5fcc\u66f4 \u662f\u5706\u7741\u53cc\u76ee\uff0c\u6012\u89c6\u4e91\u857e\uff0c\u770b\u6837\u5b50\u4f3c\u662f\u6068\u4e0d\u5f97\u4e00\u53e3\u628a\u5979\u541e\u4e0b\u53bb\u4f3c\u7684\u3002\u539f\u6765\u4ed6\u5140\u81ea\u4ee5\u4e3a\u4e91\u857e\u4e0e\u77f3 \u7fe0\u51e4\u5df2\u6210\u592b\u5987\uff0c\u6068\u201c\u4ed6\u201d\u62a2\u4e86\u81ea\u5df1\u7684\u5fc3\u4e0a\u4e4b\u4eba\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u77f3\u8001\u82f1\u96c4\uff0c\u8fd9\u7a76\u7adf\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d\u77f3\u82f1\u672a\u53ca\u56de\u7b54\uff0c\u6c99\u6d9b\u5df2\u6717\u58f0\u53d1\u8bdd\u9053\uff1a \u201c\u77f3\u5927\u54e5\uff0c\u8bc6\u65f6\u52a1\u8005\u4e3a\u4fca\u6770\uff0c\u76ee\u4e0b\u660e\u671d\u6c14\u6570\u5df2\u5c3d\uff0c\u5f20\u58eb\u8bda\u7684\u5927\u5468\uff0c\u90a3\u66f4\u4e0d\u7528\u8bf4\u4e86\uff0c\u4f60\u51e0\u66fe\u89c1 \u8fc7\u6b7b\u7070\u8fd8\u53ef\u590d\u71c3\u4e48\uff1f\u4f60\u4f55\u5fc5\u8fd8\u82e6\u5fc3\u505a\u6b7b\u4eba\u5bb6\u5974\uff0c\u66ff\u4ed6\u4fdd\u7ba1\u5b9d\u7269\uff1f\u201d \u3000\u3000\u77f3\u82f1\u662f\u59dc\u6842\u4e4b\u6027\uff0c\u8001\u800c\u5f25\u8fa3\uff0c\u95fb\u8a00\u5927\u6012\uff0c\u5f3a\u6291\u5fc3\u5934\u4e4b\u706b\uff0c\u53d1\u4e3a\u51b7\u7b11\u9053\uff1a\u201c\u4f9d\u4f60\u4e4b\u8bf4\uff0c\u54b1 \u4eec\u5012\u8be5\u505a\u74e6\u523a\u7684\u5974\u624d\u4e86\uff1f\u201d\u6c99\u6d9b\u9762\u5b54\u6da8\u5f97\u901a\u7ea2\uff0c\u751a\u662f\u5c34\u5c2c\uff0c\u52c9\u5f3a\u7b11\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4e5f\u4e0d\u662f\u8fd9\u4e48 \u8bf4\u7684\u3002\u201d\u77f3\u82f1\u559d\u9053\uff1a\u201c\u662f\u600e\u4e48\u8bf4\uff1f\u201d\u6c99\u6d9b\u9053\uff1a\u201c\u4f60\u628a\u90a3\u5e45\u753b\u56fe\u62ff\u51fa\u6765\uff0c\u54b1\u4eec\u627e\u5230\u4e86\u5f20\u58eb\u8bda\u6240 \u57cb\u4e0b\u7684\u5b9d\u85cf\u4e4b\u540e\uff0c\u8d81\u7740\u5929\u4e0b\u7eb7\u4e71\uff0c\u5c3d\u53ef\u505a\u4e00\u756a\u5927\u4e8b\uff0c\u7eb5\u4f7f\u4e0d\u6295\u9760\u74e6\u523a\uff0c\u4ea6\u53ef\u81ea\u7acb\u4e3a\u738b\uff01\u201d\u77f3 \u82f1\u8a00\u9053\uff1a\u201c\u8c01\u544a\u8bc9\u4f60\u6211\u6709\u90a3\u5e45\u753b\u56fe\uff0c\u8bf4\u5440\uff0c\u5feb\u8bf4\uff01\u201d\u77f3\u82f1\u662f\u664b\u3001\u9655\u4e24\u7701\u7684\u6b66\u6797\u76df\u4e3b\uff0c\u867d\u5728\u654c \u5be8\u4e4b\u4e2d\uff0c\u5a01\u98ce\u5c1a\u5728\uff0c\u6c99\u6d9b\u88ab\u4ed6\u7684\u773c\u5149\u4e00\u8feb\uff0c\u5fc3\u80c6\u4e00\u5bd2\uff0c\u7adf\u81ea\u8bb7\u8bb7\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002\u5ffd\u542c\u5f97\u4e00\u4e2a\u6c99 \u54d1\u7684\u58f0\u97f3\u9053\uff1a\u201c\u662f\u6211\u544a\u8bc9\u4ed6\u7684\uff0c\u600e\u4e48\uff1f\u201d\u77f3\u82f1\u628a\u773c\u4e00\u770b\uff0c\u8bf4\u8bdd\u7684\u4eba\u9762\u76ee\u9752\u80bf\uff0c\u76f8\u8c8c\u7c97\u8c6a\uff0c\u77aa \u7740\u4e24\u53ea\u773c\u775b\uff0c\u751a\u662f\u4e0d\u900a\u3002\u77f3\u82f1\u6012\u706b\u52c3\u53d1\u6307\u7740\u90a3\u4eba\u559d\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u201d\u5f20\u4e39\u67ab\u51b7\u51b7\u4e00\u7b11\u63a5\u58f0\u8bf4 \u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u4e5f\u5148\u624b\u4e0b\u5750\u7b2c\u4e00\u628a\u4ea4\u6905\u7684\u6b66\u58eb\u989d\u5409\u591a\uff0c\u6211\u8bf4\u5f97\u4e0d\u9519\u5427\uff1f\u201d \u3000\u3000\u989d\u5409\u591a\u6027\u60c5\u9c81\u83bd\uff0c\u4e0d\u77e5\u5229\u5bb3\uff0c\u4ed6\u5403\u4e86\u5f20\u3001\u4e91\u4e8c\u4eba\u7684\u5927\u4e8f\uff0c\u88ab\u6253\u5f97\u9762\u9752\u5507\u80bf\uff0c\u4e00\u53e3\u95f7\u6c14\u6b63 \u81ea\u53d1\u4e0d\u51fa\u6765\uff0c\u89c1\u6c99\u6d9b\u8bb7\u8bb7\u8bf4\u4e0d\u51fa\u53e3\uff0c\u6001\u5ea6\u6a21\u68f1\uff0c\u4ed6\u4e0d\u77e5\u8fd9\u662f\u6c99\u6d9b\u6709\u6240\u907f\u5fcc\uff0c\u7adf\u81ea\u7206\u4e86\u51fa\u6765\u3002 \u5f53\u4e0b\u542c\u5f97\u5f20\u4e39\u67ab\u6307\u8bc1\uff0c\u50b2\u7136\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u54b1\u4eec\u74e6\u523a\u5175\u5f3a\u9a6c\u58ee\uff0c\u9080\u4f60\u8054\u76df\uff0c\u6b63\u662f\u7ed9\u4f60\u9762\u5b50\uff0c \u4f60\u8fd9\u5c0f\u5b50\u4e0d\u670d\uff0c\u54b1\u4eec\u5355\u6253\u72ec\u6597\uff0c\u518d\u4e0e\u4f60\u89c1\u4e2a\u771f\u7ae0\u3002\u201d\u4ed6\u7684\u8bdd\u4e00\u534a\u9488\u5bf9\u5f20\u4e39\u67ab\uff0c\u4e00\u534a\u9488\u5bf9\u6c99 \u6d9b\u3002\u6b64\u8a00\u4e00\u51fa\uff0c\u9664\u4e86\u6c99\u6d9b\u7684\u5fc3\u8179\u6b7b\u515a\u4e0e\u65e9\u88ab\u74e6\u523a\u6536\u4e70\u4e86\u7684\u4eba\u4e4b\u5916\uff0c\u5012\u6709\u4e00\u534a\u5b58\u4e86\u6212\u5fc3\uff0c\u6253\u5b9a \u4e3b\u610f\uff0c\u4e0d\u80af\u518d\u4e3a\u6c99\u6d9b\u5356\u529b\u3002 \u3000\u3000\u77f3\u82f1\u53cc\u773c\u5706\u7741\uff0c\u62c2\u8896\u800c\u8d77\u6b63\u60f3\u53d1\u4f5c\uff0c\u53ea\u542c\u5f97\u5f20\u4e39\u67ab\u53c8\u9053\uff1a\u201c\u4f60\u4eec\u4e5f\u6789\u8d39\u4e86\u5fc3\u673a\u4e86\u3002\u4e3a\u4e86 \u8fd9\u4e00\u5e45\u753b\u56fe\uff0c\u5c06\u77f3\u8001\u82f1\u96c4\u8bf1\u5230\u6b64\u95f4\uff0c\u53c8\u53bb\u6d17\u52ab\u4ed6\u7684\u4f4f\u5b85\uff0c\u8d39\u5c3d\u5fc3\u673a\uff0c\u4e00\u65e0\u6240\u5f97\uff0c\u5802\u5802\u4e00\u4e2a\u5be8 \u4e3b\u505a\u9f20\u7a83\u72d7\u5077\u4e4b\u8f88\uff0c\u4e0d\u6015\u5929\u4e0b\u82f1\u96c4\u803b\u7b11\uff1f\u201d\u77f3\u82f1\u542c\u5f97\u5bb6\u88ab\u6d17\u52ab\uff0c\u66f4\u662f\u6124\u6012\uff0c\u201c\u556a\u201d\u7684\u4e00\u638c\uff0c \u5c06\u9762\u524d\u7684\u8336\u51e0\uff0c\u5207\u4e86\u4e00\u89d2\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u53e4\u4eba\u5272\u5e2d\u7edd\u4ea4\uff0c\u6211\u4eca\u65e5\u5207\u51e0\u660e\u5fd7\u3002\u6c99\u6d9b\u8001\u8d3c\uff0c\u6211\u4e0e \u4f60\u5144\u5f1f\u4e4b\u8c0a\u5df2\u7edd\uff0c\u4f60\u518d\u903c\u6211\uff0c\u6211\u5c31\u4e0d\u5ba2\u6c14\u4e86\uff01\u201d \u3000\u3000\u6c99\u6d9b\u9762\u4e0a\u4e00\u9635\u9752\u4e00\u9635\u7ea2\uff0c\u628a\u5fc3\u4e00\u6a2a\uff0c\u4e5f\u5927\u58f0\u559d\u9053\uff1a\u201c\u77f3\u8001\u5339\u592b\uff0c\u4f60\u4eca\u65e5\u4e0d\u628a\u753b\u56fe\u4ea4\u51fa\uff0c \u60f3\u751f\u51fa\u6b64\u5be8\uff0c\u4e07\u4e07\u4e0d\u80fd\uff01\u201d\u628a\u624b\u4e00\u6325\uff0c\u5c31\u60f3\u6765\u4e2a\u7fa4\u6bb4\u5f3a\u593a\u3002 \u3000\u3000\u5ffd\u89c1\u5bd2\u5149\u4e00\u95ea\uff0c\u5f20\u4e39\u67ab\u5237\u7684\u62d4\u5251\u51fa\u9798\uff0c\u624b\u8098\u4e00\u649e\uff0c\u5c06\u6c99\u6d9b\u649e\u51fa\u4e00\u4e08\u5f00\u5916\uff0c\u6c99\u6d9b\u7684\u515a\u7fbd\u5927 \u58f0\u9f13\u566a\uff0c\u6b63\u60f3\u4e0a\u524d\uff0c\u53ea\u89c1\u5f20\u4e39\u67ab\u53f3\u624b\u6301\u5251\uff0c\u5de6\u624b\u5df2\u5c55\u51fa\u753b\u56fe\uff0c\u54c8\u54c8\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u8981\u753b\u56fe\u7684 \u51b2\u7740\u6211\u6765\uff0c\u6211\u624d\u662f\u8fd9\u5e45\u753b\u7684\u4e3b\u4eba\uff01\u4e0d\u8fc7\uff0c\u4f60\u4eec\u8981\u4e86\u53bb\u4e5f\u6ca1\u6709\u7528\uff0c\u82cf\u5dde\u7684\u5b9d\u85cf\u4e0e\u5730\u56fe\uff0c\u6211\u65e9\u5df2 \u53d1\u6398\u6765\uff0c\u90fd\u732e\u7ed9\u4e86\u5f53\u4eca\u7684\u5927\u660e\u5929\u5b50\u5566\uff01\u201d\u6b64\u8a00\u4e00\u51fa\uff0c\u5408\u5be8\u60ca\u8bb6\uff0c\u90fd\u731c\u4e0d\u900f\u8fd9\u5c11\u5e74\u662f\u4f55\u6765\u5386\uff0c \u8bf4\u7684\u662f\u771f\u662f\u5047\uff1f\u6b63\u5728\u6b64\u65f6\uff0c\u5ffd\u542c\u5f97\u53c8\u6709\u4e00\u4eba\u51b7\u7b11\u9053\uff1a\u201c\u5f20\u4e39\u67ab\uff0c\u4f60\u7684\u8bdd\u9a97\u5f97\u8c01\u6765\uff1f\u201d \u3000\u3000\u8bf4\u8bdd\u7684\u4eba\u662f\u989d\u5409\u591a\u7684\u526f\u624b\uff0c\u540d\u5524\u5409\u5f70\u963f\uff0c\u4ed6\u662f\u4e5f\u5148\u5e9c\u4e2d\u7684\u536b\u58eb\uff0c\u4e0d\u6bd4\u989d\u5409\u591a\u5e38\u5728\u519b\u4e2d\uff0c \u6545\u6b64\u8ba4\u5f97\u5f20\u4e39\u67ab\u3002\u989d\u5409\u591a\u542c\u4e86\u6b64\u8a00\uff0c\u6014\u4e86\u4e00\u6014\u9053\uff1a\u201c\u4f60\u5c31\u662f\u53f3\u4e1e\u76f8\u5f20\u5b97\u5468\u7684\u513f\u5b50\u5417\uff1f\u592a\u5e08 \uff08\u4e5f\u5148\uff09\u6b63\u5728\u627e\u4f60\uff0c\u5feb\u5feb\u968f\u6211\u56de\u53bb\u5427\uff01\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u6211\u6b63\u8981\u53bb\u89c1\u4f60\u7684\u592a\u5e08\uff0c\u53ef\u4e0d\u662f\u968f\u4f60\u56de \u53bb\uff01\u6211\u662f\u4e2d\u56fd\u4e4b\u4eba\uff0c\u8c01\u66ff\u4f60\u74e6\u523a\u505a\u4e8b\u60c5\uff1f\u201d\u5409\u5f70\u963f\u9053\uff1a\u201c\u4f60\u5bb6\u4e0e\u6731\u660e\u4e43\u662f\u4e16\u4ec7\uff0c\u4f60\u82e5\u6398\u51fa\u5b9d \u85cf\u4e0e\u5730\u56fe\uff0c\u5c82\u6709\u732e\u7ed9\u4ec7\u4eba\u7684\u9053\u7406\uff1f\u8fd9\u6837\u5427\uff0c\u5b9d\u85cf\u662f\u4f60\u5bb6\u6240\u6709\uff0c\u6211\u4eec\u4e0d\u8981\u4f60\u7684\uff0c\u5730\u56fe\u62ff\u4e0e\u6211\uff0c \u5f85\u6211\u732e\u7ed9\u592a\u5e08\uff0c\u4f60\u4e0d\u5fc5\u518d\u5f00\u73a9\u7b11\u4e86\u3002\u201d\u5f20\u4e39\u67ab\u4e00\u811a\u8e0f\u5728\u6905\u4e0a\uff0c\u5c06\u753b\u4e00\u626c\uff0c\u559d\u9053\uff1a\u201c\u8c01\u4e0e\u4f60\u5f00 \u73a9\u7b11\uff1f\u4f60\u6709\u80c6\u5c31\u81ea\u5df1\u6765\u62ff\uff01\u201d \u3000\u3000\u5409\u5f70\u963f\u8e0c\u8e87\u4e0d\u524d\uff0c\u51e0\u4e2a\u6697\u85cf\u7684\u8499\u53e4\u6b66\u58eb\u4e5f\u4e0d\u6562\u9732\u9762\uff0c\u9080\u6765\u7684\u5404\u8def\u9ed1\u9053\u4eba\u7269\uff0c\u6709\u4e00\u5927\u534a\u4e0d \u613f\u6cbe\u8fd9\u8d9f\u6d51\u6c34\uff0c\u6c99\u6d9b\u7684\u515a\u7fbd\u88ab\u4ed6\u7684\u58f0\u5a01\u6240\u6151\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u4e5f\u672a\u6709\u4eba\u633a\u8eab\u800c\u51fa\u3002 \u3000\u3000\u77f3\u7fe0\u51e4\u8f7b\u8f7b\u501a\u504e\u7740\u4e91\u857e\uff0c\u5728\u8033\u8fb9\u67d4\u58f0\u8bf4\u9053\uff1a\u201c\u8fd9\u4e9b\u65e5\u5b50\uff0c\u4f60\u4e5f\u60f3\u5ff5\u6211\u5417\uff1f\u201d\u4e91\u857e\u5c0f\u58f0\u8bf4 \u9053\uff1a\u201c\u4f60\u77a7\u8fd9\u4e48\u591a\u4eba\u5728\u77a7\u7740\u54b1\u4eec\u5462\uff0c\u4eca\u65e5\u53ea\u6050\u96be\u4ee5\u9003\u51fa\u751f\u5929\uff0c\u4f60\u8fd8\u6709\u5fc3\u60c5\u4e0e\u6211\u8bf4\u6b64\u95f2\u8bdd\uff1f\u201d \u805a\u4e49\u5385\u5185\u5916\u4e09\u5c42\u90fd\u5df2\u4f0f\u4e0b\u7532\u5175\uff0c\u77f3\u82f1\u8fd9\u8fb9\u53ea\u6709\u56db\u4eba\uff0c\u867d\u7136\u6b66\u827a\u9ad8\u5f3a\uff0c\u786e\u5b9e\u4e5f\u96be\u4ee5\u95ef\u51fa\u53bb\u3002\u77f3 \u7fe0\u51e4\u5bf9\u8fd9\u4e00\u5207\u5374\u4f3c\u6beb\u4e0d\u653e\u5728\u5fc3\u4e0a\uff0c\u6084\u6084\u7b11\u9053\uff1a\u201c\u6211\u95f7\u4e86\u5c06\u8fd1\u4e00\u5e74\uff0c\u8fd9\u4e9b\u95f2\u8bdd\u4eca\u65e5\u4e0d\u8bf4\uff0c\u4f55\u65f6 \u518d\u8bf4\u5462\uff1f\u4eca\u65e5\u4e0d\u7ba1\u80fd\u4e0d\u80fd\u9003\u51fa\uff0c\u4e0e\u4f60\u6b7b\u5728\u4e00\u9053\uff0c\u4e5f\u662f\u7518\u5fc3\u3002\u201d\u77f3\u7fe0\u51e4\u4e0e\u4e91\u857e\u7a7a\u6709\u592b\u5987\u4e4b\u540d\uff0c \u5374\u65e0\u592b\u5987\u4e4b\u5b9e\uff0c\u5206\u522b\u591a\u65f6\u76f8\u601d\u65e5\u5207\uff0c\u4e00\u65e6\u89c1\u9762\uff0c\u5fcd\u8010\u4e0d\u4f4f\uff0c\u7adf\u8d81\u7740\u5927\u5385\u4e2d\u5608\u5608\u6742\u6742\u7684\u5f53\u513f\uff0c \u5c0f\u58f0\u5730\u5927\u8c08\u60c5\u8bdd\u3002 \u3000\u3000\u4e91\u857e\u6b63\u81ea\u62ff\u5979\u6ca1\u6cd5\uff0c\u84e6\u7136\u95f4\u5ffd\u89c1\u4e24\u6761\u5927\u6c49\uff0c\u633a\u8eab\u800c\u51fa\uff0c\u6251\u5411\u5f20\u4e39\u67ab\u3002\u8fd9\u4e24\u4e2a\u4e43\u662f\u6c99\u6d9b\u9080 \u6765\u7684\u5e2e\u624b\uff0c\u90fd\u7ec3\u6709\u5927\u529b\u795e\u62f3\u7684\u529f\u592b\uff0c\u770b\u5f20\u4e39\u67ab\u5e74\u7eaa\u9752\u9752\uff0c\u4e0d\u628a\u4ed6\u653e\u5728\u5fc3\u4e0a\uff0c\u4e00\u62e5\u800c\u4e0a\uff0c\u4e00\u4e2a \u65bd\u5c55\u64d2\u62ff\u624b\u626d\u5f20\u4e39\u67ab\u7684\u81c2\u818a\uff0c\u4e00\u4e2a\u4fbf\u6765\u593a\u753b\u3002\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u53ea\u89c1\u5bd2\u5149\u4e00\u95ea\uff0c\u5f20\u4e39\u67ab\u98de\u811a \u4e00\u8e22\uff0c\u6765\u626d\u81c2\u818a\u7684\u90a3\u6761\u6c49\u5b50\uff0c\u78b0\u4e5f\u6ca1\u6709\u78b0\u7740\u5f20\u4e39\u67ab\uff0c\u81ea\u5df1\u7684\u81c2\u818a\u53cd\u800c\u7ed9\u4ed6\u4e00\u5251\u65a9\u65ad\uff0c\u6655\u6b7b\u8fc7 \u53bb\uff0c\u90a3\u62a2\u753b\u7684\u6c49\u5b50\u4e5f\u7ed9\u5f20\u4e39\u67ab\u4e00\u811a\u8e22\u98de\uff0c\u80eb\u9aa8\u90fd\u6298\u65ad\u4e86\u3002\u5f20\u4e39\u67ab\u6a2a\u5251\u559d\u9053\uff1a\u201c\u597d\u4e0d\u8981\u8138\uff0c\u4f60 \u4eec\u60f3\u501a\u591a\u4e3a\u80dc\u5417\uff1f\u201d \u3000\u3000\u6c99\u6d9b\u9762\u8272\u94c1\u9752\u5fc3\u9053\uff1a\u201c\u8fd9\u4e2a\u65f6\u5019\u8c01\u8fd8\u4e0e\u4f60\u8bb2\u6c5f\u6e56\u89c4\u77e9\uff1f\u201d\u6b63\u60f3\u4e0b\u4ee4\uff0c\u6765\u4e2a\u7fa4\u6bb4\uff0c\u90a3\u6551\u51fa \u989d\u5409\u591a\u7684\u8499\u9762\u4eba\uff0c\u8fd9\u65f6\u5374\u5ffd\u5730\u5f00\u58f0\u8bf4\u9053\uff1a\u201c\u597d\u6781\uff0c\u597d\u6781\uff0c\u4eca\u65e5\u79cb\u9ad8\u6c14\u723d\uff0c\u6b63\u597d\u8212\u6563\u7b4b\u9aa8\uff0c\u5355 \u6253\u72ec\u6597\uff0c\u90a3\u662f\u6700\u597d\u4e0d\u8fc7\uff01\u201d\u58f0\u82e5\u6d2a\u949f\uff0c\u9707\u5f97\u5927\u5385\u5185\u55e1\u55e1\u4f5c\u54cd\u3002\u6c99\u6d9b\u770b\u4ed6\u4e00\u773c\uff0c\u8bdd\u5230\u53e3\u8fb9\uff0c\u5374 \u53c8\u7559\u4f4f\uff0c\u5fc3\u9053\uff1a\u201c\u5c31\u662f\u5355\u6253\u72ec\u6597\uff0c\u4e5f\u96be\u7d2f\u6b7b\u4ed6\u4eec\uff01\u201d \u3000\u3000\u77f3\u7fe0\u51e4\u72b9\u81ea\u504e\u7740\u4e91\u857e\uff0c\u7ec6\u8c08\u60c5\u8bdd\uff0c\u5ffd\u89c1\u6c99\u6d9b\u7684\u513f\u5b50\u6c99\u65e0\u5fcc\u53cc\u638c\u4e00\u9519\uff0c\u6251\u4e0a\u524d\u6765\uff0c\u6717\u58f0\u8bf4 \u9053\uff1a\u201c\u6211\u5148\u8bf7\u6559\u4e91\u76f8\u516c\u51e0\u62db\uff01\u201d\u4ed6\u6700\u6068\u4e91\u857e\uff0c\u8fd9\u65f6\u89c1\u4e24\u4eba\u60c5\u8bdd\u5581\u5581\uff0c\u66f4\u662f\u770b\u4e0d\u8fc7\u773c\uff0c\u6240\u4ee5\u5148 \u6765\u6311\u6218\u3002\u4e91\u857e\u6025\u5fd9\u63a8\u5f00\u4e86\u77f3\u7fe0\u51e4\uff0c\u5c06\u9752\u51a5\u5b9d\u5251\u62d4\u5728\u624b\u4e2d\u3002 \u3000\u3000\u4e91\u857e\u66fe\u4e0e\u6c99\u65e0\u5fcc\u5728\u9ed1\u77f3\u5e84\u5916\u7684\u677e\u6797\u4ea4\u8fc7\u624b\uff0c\u6df1\u77e5\u4ed6\u6b66\u529f\u867d\u7136\u4e0d\u5f31\uff0c\u5374\u8fd8\u4e0d\u662f\u81ea\u5df1\u7684\u5bf9 \u624b\uff0c\u6545\u6b64\u5e76\u4e0d\u600e\u6837\u653e\u5728\u5fc3\u4e0a\u3002\u54ea\u77e5\u6c99\u65e0\u5fcc\u6765\u52bf\u8fc5\u75be\u975e\u5e38\uff0c\u638c\u6cd5\u5c24\u5176\u602a\u5f02\uff0c\u5c0f\u81c2\u4e00\u5f2f\uff0c\u5de6\u638c\u81ea \u5185\u800c\u5916\u6325\u4e86\u4e00\u4e2a\u5706\u5f27\uff0c\u53f3\u638c\u8ddf\u7740\u201c\u547c\u201d\u7684\u4e00\u58f0\u63a8\u51fa\uff0c\u4e91\u857e\u7528\u4e86\u4e00\u62db\u201c\u8131\u888d\u8ba9\u4f4d\u201d\uff0c\u5de6\u811a\u5411\u659c \u65b9\u8e0f\u51fa\u4e00\u6b65\uff0c\u80a9\u5934\u4e00\u7f29\uff0c\u53cd\u624b\u4e00\u5251\u524a\u51fa\uff0c\u5148\u907f\u654c\u62db\uff0c\u518d\u524a\u654c\u8155\uff0c\u672c\u6765\u7a33\u5065\u975e\u5e38\uff0c\u54ea\u77e5\u6c99\u65e0\u5fcc \u5de6\u638c\u867d\u7136\u5148\u53d1\uff0c\u5728\u534a\u9014\u4e00\u5212\uff0c\u53f3\u638c\u5374\u662f\u540e\u53d1\u5148\u81f3\uff0c\u638c\u98ce\u5230\u5904\uff0c\u9690\u9690\u6709\u4e00\u80a1\u8165\u5473\u3002\u4e91\u857e\u5fc3\u4e2d\u4e00 \u6014\uff0c\u53ea\u542c\u5f97\u6c99\u65e0\u5fcc\u5927\u559d\u4e00\u58f0\uff1a\u201c\u7740\uff01\u201d\u7d2b\u9ed1\u8272\u7684\u638c\u7f18\u5288\u5230\u80f8\u524d\uff01 \u3000\u3000\u638c\u98ce\u5251\u5f71\u4e4b\u4e2d\uff0c\u53ea\u89c1\u4e00\u6761\u4eba\u5f71\u51cc\u7a7a\u98de\u8d77\uff0c\u201c\u55e4\u201d\u7684\u4e00\u54cd\uff0c\u6c99\u65e0\u5fcc\u811a\u6b65\u8dc4\u8e09\uff0c\u88e4\u7ba1\u8d34\u7740\u80ef \u9aa8\u4e4b\u5904\uff0c\u7adf\u7ed9\u5229\u5251\u7a7f\u8fc7\uff0c\u4e91\u857e\u4e5f\u5012\u8dc3\u51fa\u4e00\u4e08\u5f00\u5916\uff0c\u8fd9\u4e00\u4e0b\uff0c\u4e24\u4eba\u90fd\u662f\u9887\u51fa\u610f\u5916\u3002 \u3000\u3000\u539f\u6765\u6c99\u65e0\u5fcc\u81ea\u4ece\u90a3\u6b21\u632b\u8d25\u4e4b\u540e\uff0c\u5343\u65b9\u767e\u8ba1\u62a5\u4ec7\uff0c\u62dc\u4e86\u4e00\u4f4d\u82d7\u6d1e\u7684\u602a\u4eba\u4e3a\u5e08\uff0c\u7ec3\u4e86\u4e00\u79cd\u6781 \u5176\u602a\u5f02\u7684\u90aa\u95e8\u7684\u9634\u98ce\u6bd2\u7802\u638c\uff0c\u638c\u6cd5\u56fa\u7136\u602a\u5f02\uff0c\u638c\u529b\u66f4\u662f\u6b79\u6bd2\uff0c\u6b66\u529f\u5e73\u5eb8\u8005\uff0c\u88ab\u4ed6\u638c\u98ce\u626b\u7740\uff0c \u4fbf\u4f1a\u4e2d\u6bd2\uff0c\u6b66\u529f\u9ad8\u5f3a\u8005\uff0c\u88ab\u4ed6\u6253\u4e2d\uff0c\u4e03\u65e5\u4e4b\u540e\uff0c\u4e5f\u5b9a\u8eab\u4ea1\u3002\u6c99\u65e0\u5fcc\u521a\u624d\u7a81\u51fa\u602a\u62db\uff0c\u731d\u7136\u4e00 \u51fb\uff0c\u81ea\u4ee5\u4e3a\u5fc5\u4f1a\u5288\u4e2d\uff0c\u54ea\u77e5\u4e91\u857e\u867d\u7136\u4e0d\u8bc6\u8fd9\u79cd\u638c\u6cd5\uff0c\u4f46\u8bba\u5230\u672c\u8eab\u7684\u771f\u5b9e\u529f\u592b\uff0c\u5374\u8fdc\u5728\u6c99\u65e0\u5fcc \u4e4b\u4e0a\uff0c\u5c24\u5176\u8eab\u6cd5\u7684\u8f7b\u7075\uff0c\u66f4\u975e\u6c99\u65e0\u5fcc\u53ef\u6bd4\uff0c\u6545\u6b64\u5728\u5371\u6025\u4e4b\u4e2d\uff0c\u4ecd\u80fd\u968f\u673a\u5e94\u53d8\uff0c\u907f\u4e86\u5f00\u53bb\uff0c\u800c \u4e14\u8fd8\u4e86\u4e00\u5251\u3002 \u3000\u3000\u6c99\u65e0\u5fcc\u4e2d\u4e86\u4e00\u5251\uff0c\u5e78\u672a\u523a\u7740\u9aa8\u5934\uff0c\u4f46\u4ea6\u751a\u4e3a\u75bc\u75db\uff0c\u6c14\u5f97\u54c7\u54c7\u5927\u53eb\uff0c\u53cc\u638c\u4e00\u9519\uff0c\u53c8\u518d\u6251 \u4e0a\u3002\u4e91\u857e\u7ecf\u4e86\u4e00\u62db\uff0c\u5206\u5916\u5c0f\u5fc3\uff0c\u5c55\u5f00\u7a7f\u82b1\u7ed5\u6811\u7684\u8eab\u6cd5\uff0c\u4e0e\u4ed6\u6e38\u6597\uff0c\u970e\u5ffd\u4e4b\u95f4\uff0c\u53ea\u89c1\u56db\u9762\u516b\u65b9 \u90fd\u662f\u4e91\u857e\u7684\u4eba\u5f71\uff0c\u6c99\u65e0\u5fcc\u8fde\u5979\u7684\u8863\u88f3\u4e5f\u6cbe\u4e0d\u7740\u3002\u7ea6\u6597\u4e86\u4e8c\u5341\u591a\u62db\uff0c\u4e91\u857e\u5251\u52bf\u8d8a\u53d1\u50ac\u7d27\uff0c\u6c99\u65e0 \u5fcc\u60c5\u77e5\u4e0d\u654c\uff0c\u4f46\u53c8\u4e0d\u7518\u8d25\u4e0b\uff0c\u62fc\u7740\u4e24\u8d25\u4ff1\u4f24\uff0c\u7a81\u5728\u5251\u5149\u4e4b\u4e2d\u6251\u8fdb\uff0c\u4e00\u62db\u201c\u659c\u5288\u534e\u5c71\u201d\u62fc\u7740\u727a \u7272\u4e00\u6761\u81c2\u818a\u4e5f\u8981\u5c06\u6bd2\u638c\u5370\u5728\u4e91\u857e\u9762\u4e0a\u3002\u4e91\u857e\u4f55\u7b49\u673a\u7075\uff0c\u970d\u5730\u4e00\u4e2a\u201c\u51e4\u70b9\u5934\u201d\uff0c\u9752\u51a5\u5b9d\u5251\u53cd\u624b \u4e00\u64a9\uff0c\u75be\u8d77\u800c\u8fce\uff0c\u6c99\u65e0\u5fcc\u7684\u90a3\u6761\u81c2\u818a\uff0c\u773c\u770b\u5c31\u8981\u88ab\u5979\u786c\u751f\u751f\u5730\u5378\u4e0b\u3002 \u3000\u3000\u5ffd\u5730\u4e00\u4eba\u4ece\u65c1\u8dc3\u51fa\uff0c\u5de6\u624b\u4e00\u62c9\uff0c\u53f3\u624b\u4e00\u6293\uff0c\u540c\u65f6\u4e4b\u95f4\uff0c\u65e2\u628a\u6c99\u65e0\u5fcc\u62c9\u9000\uff0c\u53c8\u653b\u5411\u4e91\u857e\u7684 \u8109\u95e8\u3002\u8fd9\u4eba\u957f\u76f8\u751a\u602a\uff0c\u8eab\u8eaf\u7626\u957f\u6709\u5982\u4e00\u6761\u7af9\u7bd9\uff0c\u5341\u6307\u957f\u722a\uff0c\u4e4c\u9ed1\u53d1\u5149\uff0c\u9634\u607b\u607b\u5730\u7b11\u9053\uff1a\u201c\u77f3 \u5e84\u4e3b\u7684\u7231\u5a7f\u679c\u662f\u4e0d\u51e1\uff0c\u5f85\u6211\u6765\u9886\u6559\u51e0\u62db\u3002\u201d\u8fd9\u4eba\u6b63\u662f\u6c99\u65e0\u5fcc\u65b0\u62dc\u7684\u5e08\u7236\uff0c\u82d7\u7586\u5f02\u4eba\u8d64\u795e\u5b50\uff0c \u4ed6\u4ece\u8d35\u5dde\u4e91\u6e38\u81f3\u5317\u65b9\uff0c\u5317\u65b9\u7684\u8c6a\u6770\u5341\u4e5d\u4e0d\u77e5\u4ed6\u7684\u6765\u5386\u3002 \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u4e24\u4eba\u5df2\u4ea4\u4e0a\u624b\u3002\u867d\u7136\u662f\u540c\u6837\u7684\u4e00\u5957\u638c\u6cd5\uff0c\u4f46\u5728\u8d64\u795e\u5b50\u624b\u4e2d\u4f7f\u51fa\u6765\uff0c\u6bd4\u6c99\u65e0\u5fcc \u4f55\u6b62\u5389\u5bb3\u5341\u500d\uff01\u5728\u5251\u5149\u7f2d\u7ed5\u4e4b\u4e2d\u4ed6\u5c45\u7136\u7167\u6837\u4f38\u51fa\u957f\u722a\uff0c\u6495\u3001\u62ff\u3001\u6293\u3001\u6251\uff0c\u6709\u5982\u9b3c\u9b45\uff0c\u6bcf\u4e00\u53d1 \u62db\u9aa8\u8282\u683c\u683c\u4f5c\u54cd\uff0c\u4e91\u857e\u4e0d\u7531\u5f97\u5927\u4e3a\u9a87\u5f02\uff0c\u6025\u628a\u9752\u51a5\u5b9d\u5251\u821e\u6210\u4e00\u56e2\u94f6\u8679\uff0c\u4e0d\u6c42\u6709\u529f\uff0c\u5148\u6c42\u65e0 \u8fc7\u3002 \u3000\u3000\u8d64\u795e\u5b50\u6570\u6251\u4e0d\u8fdb\uff0c\u7a81\u7136\u5927\u543c\u4e00\u58f0\uff0c\u53cc\u638c\u7ffb\u98de\uff0c\u8fde\u73af\u731b\u626b\uff0c\u76f4\u5982\u5de8\u65a7\u5f00\u5c71\uff0c\u94c1\u9524\u51ff\u77f3\uff0c\u638c \u98ce\u6fc0\u8361\uff0c\u4e00\u80a1\u5bd2\u6c14\u76f4\u900f\u8fc7\u6765\uff0c\u4e91\u857e\u7684\u5251\u70b9\u6bcf\u88ab\u9707\u6b6a\uff0c\u66f4\u5947\u602a\u7684\u662f\u5fc3\u5934\u6e10\u89c9\u70e6\u8e81\uff0c\u706b\u6c14\u4e0a\u5347\uff0c \u50cf\u662f\u7ed9\u4eba\u6fc0\u6012\uff0c\u4e0d\u53ef\u81ea\u5236\u3002\u5979\u672c\u6765\u6253\u5b9a\u4e3b\u610f\uff0c\u53ea\u5b88\u4e0d\u653b\uff0c\u4f46\u6597\u4e86\u4e09\u4e94\u5341\u62db\uff0c\u65e0\u540d\u706b\u8d77\uff0c\u4fbf\u81ea \u6309\u637a\u4e0d\u4f4f\uff0c\u5c61\u5c61\u51b2\u51fa\u5708\u5b50\uff0c\u4e0e\u8d64\u795e\u5b50\u5f3a\u653b\u5bf9\u62fc\u3002\u539f\u6765\u8d64\u795e\u5b50\u7684\u9634\u98ce\u6bd2\u7802\u638c\u4e0d\u4f46\u53cc\u638c\u542b\u6709\u5267 \u6bd2\uff0c\u800c\u4e14\u638c\u98ce\u6fc0\u8361\uff0c\u51b7\u6c14\u6c81\u808c\uff0c\u53ef\u4ee5\u523a\u6fc0\u654c\u4eba\u7684\u795e\u7ecf\uff0c\u4ee4\u654c\u4eba\u81ea\u4e71\u6b65\u9aa4\u3002 \u3000\u3000\u8d64\u795e\u5b50\u6b63\u662f\u8981\u5f15\u5979\u5bf9\u653b\uff0c\u6fc0\u6218\u4e4b\u4e2d\uff0c\u4e91\u857e\u4e00\u5251\u523a\u51fa\uff0c\u76f4\u62b5\u524d\u5fc3\uff0c\u53c8\u72e0\u53c8\u51c6\uff0c\u770b\u6765\u8d64\u795e\u5b50 \u65e0\u53ef\u518d\u907f\uff0c\u5374\u89c1\u4ed6\u5ffd\u5730\u5927\u543c\u4e00\u58f0\u8eab\u5f62\u9aa4\u8d77\uff0c\u5341\u6307\u51cc\u7a7a\u6293\u4e0b\uff0c\u77f3\u7fe0\u51e4\u60ca\u53eb\u4e00\u58f0\uff0c\u9669\u4e9b\u6655\u5012\u3002\u9661 \u7136\u95f4\u5ffd\u542c\u5f97\u6ee1\u5802\u54c4\u7b11\u4e4b\u58f0\uff0c\u7741\u773c\u4e00\u770b\uff0c\u4e0d\u7981\u60ca\u5f97\u5446\u4e86\uff01\u8d64\u795e\u5b50\u4e0e\u4e91\u857e\u5df2\u95f4\u76f8\u8ddd\u4e00\u4e08\u5f00\u5916\uff0c\u80a9 \u4e0a\u8863\u88f3\u7834\u88c2\uff0c\u72b6\u751a\u72fc\u72c8\u3002\u4f46\u77f3\u7fe0\u51e4\u5fc3\u76ee\u4e2d\u7684\u5982\u610f\u90ce\u541b\uff0c\u5374\u6bd4\u4ed6\u8fd8\u8981\u72fc\u72c8\u5341\u500d\uff0c\u5934\u6234\u7684\u675f\u53d1\u91d1 \u51a0\u88c2\u4e3a\u4e24\u534a\uff0c\u8fd9\u4e5f\u7f62\u4e86\uff0c\u5305\u5934\u7684\u9752\u5dfe\u4e5f\u88ab\u6495\u5f00\u534a\u8fb9\uff0c\u7adf\u9732\u51fa\u534a\u5934\u79c0\u53d1\uff0c\u867d\u7136\u624e\u7ecf\u7ea2\u7eeb\uff0c\u4f46\u5df2 \u770b\u51fa\u662f\u5973\u513f\u88c5\u675f\uff01\u539f\u6765\u9002\u624d\u90a3\u4e00\u62db\uff0c\u53cc\u65b9\u90fd\u662f\u9669\u6781\uff0c\u4e91\u857e\u5904\u5728\u4e0b\u98ce\uff0c\u8c41\u51fa\u6027\u547d\uff0c\u7528\u5e08\u95e8\u7684\u6551 \u6025\u7edd\u62db\u201c\u6781\u76ee\u6ca7\u6ce2\u201d\u4e00\u5251\u524a\u51fa\uff0c\u8d64\u795e\u5b50\u82e5\u4ecd\u7136\u7528\u529b\u6293\u4e0b\uff0c\u867d\u53ef\u6d1e\u7a7f\u4e91\u857e\u7684\u8111\u76d6\uff0c\u4f46\u4e91\u857e\u8fd9\u4e00 \u5251\u4e5f\u8981\u81ea\u4ed6\u524d\u5fc3\u76f4\u900f\u540e\u5fc3\u3002\u6545\u6b64\u53cc\u65b9\u90fd\u632a\u52a8\u8eab\u5f62\uff0c\u624b\u6cd5\u53d8\u6362\u504f\u4e86\u51c6\u5934\uff0c\u4e91\u857e\u4e00\u5251\u52fe\u7834\u4ed6\u80a9\u4e0a \u7684\u8863\u88f3\uff0c\u800c\u8d64\u795e\u5b50\u4e5f\u4e00\u6293\u6293\u7834\u4e86\u5979\u7684\u675f\u53d1\u91d1\u51a0\uff0c\u8fde\u5305\u5934\u7684\u9752\u5dfe\u4e5f\u6495\u5f00\u4e86\u4e00\u534a\uff01 \u3000\u3000\u6ee1\u5802\u54c4\u7b11\u4e4b\u4e2d\uff0c\u8d64\u795e\u5b50\u5410\u4e86\u4e00\u53e3\u553e\u6cab\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff1a\u201c\u7b97\u8001\u5b50\u5012\u9709\uff0c\u78b0\u7740\u4f60\u8fd9\u4e2a\u4eba\u5996\uff0c \u8001\u5b50\u4e0d\u4e0e\u5a18\u513f\u52a8\u624b\uff01\u201d\u4e91\u857e\u6c14\u5f97\u9762\u8272\u53d8\u7d2b\uff0c\u9752\u51a5\u5251\u4e00\u6325\uff0c\u53c8\u60f3\u62fc\u547d\uff0c\u5ffd\u542c\u5f97\u5f20\u4e39\u67ab\u67d4\u58f0\u8bf4 \u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u4f60\u4e14\u6b47\u4e00\u4f1a\u513f\uff01\u201d\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u5df2\u5c06\u8d64\u795e\u5b50\u622a\u7740\uff0c\u53cc\u65b9\u52a8\u8d77\u624b\u6765\u3002 \u3000\u3000\u7b11\u58f0\u7ee7\u7eed\u4e0d\u7edd\uff0c\u5343\u767e\u5bf9\u773c\u775b\u90fd\u671d\u7740\u4e91\u857e\u77a7\u6765\uff0c\u77f3\u82f1\u7236\u5973\u60ca\u5f02\u4e4b\u6781\uff0c\u5c24\u5176\u662f\u77f3\u7fe0\u51e4\u66f4\u662f\u5446 \u82e5\u6728\u9e21\uff0c\u8f9b\u9178\u3001\u5931\u671b\u3001\u8be7\u5f02\u3001\u60b2\u75db\uff0c\u8bf4\u4e0d\u51fa\u5fc3\u4e2d\u7684\u5473\u9053\u3002\u5979\u4e07\u4e07\u6599\u4e0d\u5230\u65e5\u5915\u76f8\u601d\u7684\u5982\u610f\u90ce\u541b \u7adf\u7136\u4e5f\u540c\u81ea\u5df1\u4e00\u6837\uff0c\u662f\u4e2a\u5c11\u5973\uff01\u53ea\u89c1\u4e91\u857e\u54ac\u7740\u5634\u5507\uff0c\u9762\u8272\u5c34\u5c2c\uff0c\u5c06\u5305\u5934\u7684\u9752\u5dfe\u53c8\u5df2\u5305\u624e\u597d\uff0c \u9762\u4e0a\u7f9e\u6127\u7684\u795e\u60c5\uff0c\u66f4\u50cf\u4e00\u4e2a\u95fa\u4e2d\u5c11\u5973\u3002\u77f3\u7fe0\u51e4\u51c9\u4e86\u534a\u622a\uff0c\u4ecd\u662f\u4e0d\u76f8\u4fe1\uff0c\u4e5f\u4e0d\u987e\u5728\u4f17\u76ee\u77aa\u77aa\u4e4b \u4e0b\uff0c\u6328\u8fd1\u4e91\u857e\uff0c\u5c31\u5728\u5979\u8033\u8fb9\u95ee\u9053\uff1a\u201c\u4e91\u76f8\u516c\uff0c\u4f60\u4e3a\u4ec0\u4e48\u6b22\u559c\u5c06\u5934\u53d1\u7559\u5f97\u8fd9\u4e48\u957f\uff1f\u4f60\u3001\u4f60\u3001\u4f60 \u7a76\u7adf\u662f\u7537\u5b50\u8fd8\u662f\u5973\u5a07\u5a18\uff1f\u201d\u4e91\u857e\u6ee1\u9762\u901a\u7ea2\uff0c\u5979\u672c\u6765\u662f\u51c6\u5907\u5bf9\u77f3\u7fe0\u51e4\u8bf4\u660e\u771f\u76f8\u7684\uff0c\u4f46\u5728\u6b64\u65f6\u6b64 \u5730\u6b64\u79cd\u60c5\u5f62\u4e4b\u4e0b\uff0c\u88ab\u77f3\u7fe0\u51e4\u8fd9\u6837\u8ffd\u95ee\uff0c\u7adf\u81ea\u8bb7\u8bb7\u4e0d\u80fd\u51fa\u53e3\uff0c\u77f3\u7fe0\u51e4\u4f38\u51fa\u53cc\u6307\u5728\u5979\u80c1\u4e0b\u4e00\u6233\uff0c \u9053\uff1a\u201c\u51a4\u5bb6\uff0c\u4f60\u8bf4\u5440\uff01\u201d\u5ffd\u89c9\u6c14\u6c1b\u6709\u5f02\u6ee1\u5802\u7684\u7b11\u58f0\u90fd\u505c\u4e0b\u6765\uff0c\u539f\u6765\u5f20\u4e39\u67ab\u4e0e\u8d64\u795e\u5b50\u6b63\u6597\u5230\u6fc0 \u70c8\u4e4b\u5904\uff0c\u4f17\u4eba\u7684\u6ce8\u610f\u529b\u90fd\u88ab\u5438\u5f15\u53bb\u4e86\u3002 \u3000\u3000\u53ea\u89c1\u4e91\u857e\u4e5f\u5b9a\u4e86\u773c\u775b\uff0c\u51dd\u89c6\u7740\u573a\u4e2d\u7684\u6076\u6597\uff0c\u773c\u5149\u4e2d\u5145\u6ee1\u5173\u6000\u5fe7\u8651\u7684\u795e\u60c5\uff0c\u77f3\u7fe0\u51e4\u5fc3\u4e2d\u53c8 \u662f\u4e00\u51c9\uff0c\u5982\u6b64\u795e\u60c5\uff0c\u5982\u6b64\u773c\u5149\uff0c\u9664\u4e86\u662f\u60c5\u4eba\u4e4b\u5916\uff0c\u518d\u4e5f\u627e\u4e0d\u5230\u7b2c\u4e8c\u79cd\u89e3\u91ca\u3002\u770b\u6765\u201c\u4ed6\u201d\u4e4b\u5173 \u5fc3\u5f20\u4e39\u67ab\u5c31\u50cf\u81ea\u5df1\u5173\u5fc3\u201c\u4ed6\u201d\u4e00\u6837\uff0c\u662f\u90a3\u4e48\u7684\u771f\u631a\u800c\u81ea\u7136\u6d41\u9732\uff01\u77f3\u7fe0\u51e4\u5fc3\u4e2d\u7684\u5e0c\u671b\u5c31\u50cf\u6c34\u4e2d \u7684\u660e\u6708\uff0c\u7a81\u7ed9\u987d\u7ae5\u7528\u77f3\u5934\u6253\u788e\uff0c\u4e5f\u8bf4\u4e0d\u51fa\u662f\u60cb\u60dc\u8fd8\u662f\u60b2\u51c9\uff01 \u3000\u3000\u573a\u4e2d\u5f20\u4e39\u67ab\u4e0e\u8d64\u795e\u5b50\u52a8\u624b\u5df2\u8fc7\u767e\u62db\uff0c\u5f20\u4e39\u67ab\u7684\u5185\u529f\u706b\u5019\u6bd4\u4e91\u857e\u8981\u6df1\u5f97\u591a\uff0c\u8d64\u795e\u5b50\u7684\u9634\u98ce \u6bd2\u7802\u638c\u5bf9\u4ed6\u6beb\u65e0\u4f5c\u7528\uff0c\u5f20\u4e39\u67ab\u89c1\u62db\u62c6\u62db\uff0c\u89c1\u5f0f\u62c6\u5f0f\uff0c\u4e0d\u75be\u4e0d\u5f90\uff0c\u4e00\u70b9\u4e5f\u4e0d\u70e6\u8e81\u3002\u8d64\u795e\u5b50\u4e1d\u6beb \u4e5f\u5360\u4e0d\u5230\u4fbf\u5b9c\uff0c\u81ea\u5df1\u53cd\u800c\u706b\u8d77\uff0c\u72c2\u543c\u4e00\u58f0\uff0c\u638c\u6293\u517c\u65bd\uff0c\u65f6\u800c\u51cc\u7a7a\u98de\u6251\uff0c\u65f6\u800c\u5377\u5730\u64d2\u62ff\uff0c\u64d2\u62ff \u6251\u51fb\u4e4b\u4e2d\uff0c\u6742\u4ee5\u6293\u88c2\uff0c\u70b9\u6253\u4e4b\u6cd5\uff0c\u5341\u6307\u4e4c\u9ed1\u7684\u957f\u7532\u5c31\u5982\u6bd2\u5203\u4e00\u822c\uff0c\u5ffd\u4f38\u5ffd\u7f29\uff0c\u624b\u811a\u8d77\u5904\uff0c\u5168 \u5e26\u52b2\u98ce\uff0c\u4e00\u6d3e\u51f6\u731b\u7c97\u72b7\u4e4b\u52bf\uff0c\u4ee4\u4eba\u60ca\u5fc3\u9a87\u76ee\uff01\u770b\u5f20\u4e39\u67ab\u65f6\uff0c\u5374\u4ecd\u662f\u6c14\u5b9a\u795e\u95f2\uff0c\u8863\u8882\u98d8\u98d8\uff0c\u5251 \u52bf\u8f7b\u7075\u7fd4\u52a8\uff0c\u6f47\u6d12\u4e4b\u6781\uff01\u5251\u5149\u56db\u5c04\uff0c\u5ffd\u53d6\u5ffd\u6563\uff0c\u6709\u5982\u6d41\u6c34\u884c\u4e91\u4e1d\u6beb\u4e0d\u89c1\u5403\u529b\uff0c\u4f46\u5374\u5904\u5904\u5236\u7740 \u673a\u5148\u3002\u8d64\u795e\u5b50\u4e0d\u7531\u5f97\u5012\u62bd\u4e00\u53e3\u51b7\u6c14\uff0c\u5fc3\u4e2d\u597d\u751f\u5947\u5f02\uff0c\u81ea\u601d\uff1a\u8fd9\u638c\u6cd5\u4e43\u662f\u6211\u5728\u82d7\u5c71\u4e4b\u4e2d\uff0c\u770b\u9e1f \u517d\u6251\u51fb\u4e4b\u52bf\uff0c\u81ea\u521b\u51fa\u6765\u7684\uff0c\u6c99\u65e0\u5fcc\u5f97\u6211\u4f20\u6388\uff0c\u4ea6\u672a\u5168\u6653\uff0c\u5982\u4f55\u6b64\u4eba\u5374\u50cf\u751a\u4e3a\u719f\u6089\uff0c\u6bcf\u6bcf\u5728\u6211 \u62db\u5f0f\u53d8\u6362\u4e4b\u524d\uff0c\u5c31\u8fce\u5934\u72d9\u51fb\uff0c\u4ee4\u6211\u4e0d\u80fd\u65bd\u5c55\uff1f\u4ed6\u54ea\u77e5\u5f20\u4e39\u67ab\u81ea\u5728\u77f3\u6d1e\u4e4b\u4e2d\uff0c\u5f97\u4e86\u5f6d\u548c\u5c1a\u7684\u9057 \u4e66\uff0d\uff0d\u300a\u7384\u529f\u8981\u8bc0\u300b\u4e4b\u540e\uff0c\u9886\u609f\u5404\u79cd\u6b66\u5b66\u7684\u539f\u7406\uff0c\u5404\u5bb6\u5404\u6d3e\u7684\u6b66\u529f\uff0c\u7ecf\u4ed6\u8fc7\u76ee\u4e4b\u540e\uff0c\u5c31\u53ef\u4ee5 \u65e0\u5e08\u81ea\u901a\u3002\u4ed6\u770b\u4e86\u6c99\u65e0\u5fcc\u4e0e\u4e91\u857e\u76f8\u6597\u7684\u4e00\u573a\uff0c\u53c8\u770b\u4e86\u8d64\u795e\u5b50\u4e0e\u4e91\u857e\u76f8\u6597\u7684\u4e00\u573a\uff0c\u81ea\u5df1\u53c8\u63a5\u4e86 \u8d64\u795e\u5b50\u4e00\u767e\u4f59\u62db\uff0c\u5bf9\u8fd9\u79cd\u638c\u6cd5\u7684\u53d8\u5316\u6765\u52bf\uff0c\u5df2\u662f\u4e86\u7136\u80f8\u4e2d\uff0c\u66f4\u52a0\u4e0a\u4ed6\u7684\u529f\u529b\uff0c\u4ea6\u7a0d\u80dc\u8d64\u795e\u5b50 \u4e00\u7b79\uff0c\u4ed6\u624b\u4e2d\u7684\u767d\u4e91\u5251\u53c8\u662f\u5b9d\u5251\uff0c\u8d64\u795e\u5b50\u7684\u6bd2\u7802\u638c\u867d\u7136\u5389\u5bb3\uff0c\u5374\u4e0d\u6562\u4e0e\u4e4b\u76f8\u78b0\u3002\u6709\u6b64\u51e0\u6837\u4fbf \u5b9c\uff0c\u6545\u6b64\u767e\u4f59\u62db\u4e4b\u540e\uff0c\u4fbf\u5360\u5c3d\u4e0a\u98ce\uff0c\u6740\u5f97\u8d64\u795e\u5b50\u53ea\u6709\u62db\u67b6\u4e4b\u529f\uff0c\u6beb\u65e0\u8fd8\u624b\u4e4b\u529b\uff01\u8d64\u795e\u5b50\u89c1\u72b6 \u4e0d\u5999\u8d8a\u53d1\u5fc3\u614c\uff0c\u865a\u6293\u4e00\u628a\uff0c\u4fbf\u601d\u9003\u8d70\uff0c\u5f20\u4e39\u67ab\u4e00\u58f0\u51b7\u7b11\uff0c\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u5996\u4eba\uff0c\u4e14\u7559\u4e0b\u4e00\u70b9\u8bb0 \u53f7\uff01\u201d\u638c\u98ce\u5251\u5f71\u4e4b\u4e2d\uff0c\u53ea\u542c\u5f97\u201c\u5580\u5693\u201d\u4e00\u58f0\uff0c\u8d64\u795e\u5b50\u7684\u4e00\u6761\u81c2\u818a\u5df2\u7ed9\u4ed6\u786c\u751f\u751f\u5207\u4e0b\u3002\u5385\u4e0a\u5404 \u8def\u9ed1\u9053\u4eba\u7269\uff0c\u54d7\u7136\u60ca\u547c\uff0c\u8d64\u795e\u5b50\u6367\u7740\u65ad\u81c2\uff0c\u6324\u5f00\u4f17\u4eba\u5954\u51fa\u5c71\u5be8\uff0c\u56de\u5934\u9a82\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u5341\u5e74 \u4e4b\u540e\uff0c\u7956\u5e08\u7237\u8fd8\u8981\u627e\u4f60\u62a5\u4ec7\uff01\u201d\u5f20\u4e39\u67ab\u63d0\u8d77\u5b9d\u5251\uff0c\u5728\u8863\u8896\u4e0a\u4e00\u62b9\uff0c\u9053\uff1a\u201c\u597d\uff0c\u6211\u7b49\u4f60\u5c31 \u662f\uff01\u201d\u4f17\u4eba\u89c1\u8d64\u795e\u5b50\u65ad\u81c2\u4e4b\u540e\uff0c\u8fd8\u80fd\u5954\u8dd1\u5982\u98de\uff0c\u5982\u6b64\u51f6\u72e0\uff0c\u4e5f\u4e0d\u7981\u9a87\u7136\u3002\u5f20\u4e39\u67ab\u672c\u6765\u65e0\u610f\u4ee4 \u4ed6\u6b8b\u5e9f\uff0c\u53ea\u56e0\u4ed6\u9a82\u4e86\u4e91\u857e\u4e00\u53e5\u201c\u4eba\u5996\u201d\uff0c\u6240\u4ee5\u624d\u5207\u4e0b\u4ed6\u7684\u81c2\u818a\uff0c\u8fd9\u65f6\u4e5f\u81ea\u6709\u70b9\u540e\u6094\u3002\u540e\u6765\u8fc7 \u4e86\u5341\u4f59\u5e74\u540e\uff0c\u8d64\u795e\u5b50\u679c\u7136\u518d\u627e\u5f20\u4e39\u67ab\u4e3a\u96be\uff0c\u8fd9\u662f\u540e\u8bdd\uff0c\u6309\u4e0b\u4e0d\u8868\u3002 \u3000\u3000\u90a3\u4e9b\u4e09\u5c71\u4e94\u5cb3\u4eba\u9a6c\uff0c\u89c1\u8d64\u795e\u5b50\u5982\u6b64\u51f6\u72e0\uff0c\u5c1a\u81ea\u53d7\u521b\uff0c\u5fc3\u4e2d\u6240\u6151\uff0c\u90fd\u4e0d\u6562\u51fa\u6765\u5355\u72ec\u6311\u6218\uff0c \u6c99\u6d9b\u4e00\u6a2a\u4e86\u5fc3\uff0c\u53c8\u60f3\u6307\u6325\u624b\u4e0b\u7fa4\u6bb4\u3002\u5ffd\u542c\u5f97\u6709\u4eba\u7b11\u9053\uff1a\u201c\u597d\u5251\u6cd5\uff0c\u597d\u5251\u6cd5\uff0c\u5f85\u6211\u4e5f\u6765\u9886\u6559\u51e0 \u62db\u5427\uff01\u201d \u3000\u3000\u5f20\u4e39\u67ab\u4e00\u770b\uff0c\u53ea\u89c1\u51fa\u6765\u6311\u6218\u7684\u6b63\u662f\u90a3\u8499\u9762\u4eba\uff0c\u4f46\u89c1\u4ed6\u53ea\u9732\u51fa\u53cc\u773c\uff0c\u95ea\u95ea\u653e\u5149\uff0c\u663e\u5f97\u5341\u5206 \u8be1\u79d8\u3002\u4e91\u857e\u51db\u7136\u4e00\u60ca\uff01\u5355\u6253\u72ec\u6597\u53ea\u6050\u5f20\u4e39\u67ab\u4e0d\u662f\u4ed6\u7684\u5bf9\u624b\u3002\u90a3\u8499\u9762\u4eba\u968f\u4fbf\u7acb\u4e86\u4e00\u4e2a\u95e8\u6237\u559d \u9053\uff1a\u201c\u8fdb\u62db\u5427\uff01\u201d\u5f20\u4e39\u67ab\u628a\u5251\u4e00\u63d2\uff0c\u9053\uff1a\u201c\u65e2\u7136\u9601\u4e0b\u4e0d\u4eae\u5175\u5203\uff0c\u6211\u4e5f\u966a\u9601\u4e0b\u8d70\u4e00\u8d9f\u62f3\u3002\u201d\u4e91 \u857e\u7709\u5934\u4e00\u76b1\uff0c\u5fc3\u9053\uff1a\u201c\u5f20\u4e39\u67ab\u4e5f\u592a\u81ea\u5927\u4e86\uff0c\u8fd9\u4eba\u80fd\u62b5\u5fa1\u53cc\u5251\u5408\u74a7\u5230\u5341\u62db\u4e4b\u5916\uff0c\u529f\u529b\u5c82\u662f\u5bfb \u5e38\uff0c\u4ed7\u5b9d\u5251\u4e4b\u529b\uff0c\u6216\u8bb8\u80fd\u6253\u4e2a\u5e73\u624b\uff0c\u4e0e\u4ed6\u6bd4\u62f3\uff0c\u90a3\u662f\u51c6\u8d25\u65e0\u7591\u3002\u201d\u4e0d\u7531\u5f97\u66ff\u5f20\u4e39\u67ab\u6697\u6697\u62c5 \u5fc3\u3002 \u3000\u3000\u90a3\u4eba\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u90a3\u5c31\u8bf7\u9601\u4e0b\u8d50\u62db\u3002\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u5ba2\u4e0d\u50ed\u4e3b\uff0c\u8fd8\u662f\u5148 \u8bf7\u9601\u4e0b\u6307\u6559\u3002\u201d\u90a3\u4eba\u7b11\u9053\uff1a\u201c\u5f20\u76f8\u516c\u5904\u5904\u90fd\u4e0d\u80af\u5360\u4eba\u4fbf\u5b9c\uff0c\u7684\u786e\u662f\u540d\u5bb6\u5f1f\u5b50\u7684\u6c14\u6d3e\uff0c\u5176\u5b9e\u54b1 \u4eec\u90fd\u662f\u5ba2\u4eba\u3002\u4f46\u5f20\u76f8\u516c\u65e2\u7136\u8981\u6211\u5148\u884c\u732e\u4e11\uff0c\u90a3\u5c31\u53ea\u597d\u50ed\u8d8a\u4e86\u3002\u201d\u5c0f\u81c2\u4e00\u5f2f\uff0c\u84e6\u7136\u5c31\u662f\u4e00\u62db \u201c\u5f2f\u5f13\u5c04\u6708\u201d\uff0c\u624b\u6307\u70b9\u5411\u5f20\u4e39\u67ab\u80f8\u819b\u201c\u7384\u7391\u201d\u5927\u7a74\u3002 \u3000\u3000\u8fd9\u8499\u9762\u4eba\u7684\u70b9\u7a74\u624b\u6cd5\u8fc5\u75be\u975e\u5e38\uff0c\u4f46\u5f20\u4e39\u67ab\u662f\u4f55\u7b49\u6837\u4eba\uff0c\u7109\u80fd\u7ed9\u4ed6\u70b9\u4e2d\uff0c\u5c31\u5728\u4ed6\u7684\u6307\u5934\u6cbe \u8863\u4e4b\u9645\uff0c\u5f20\u4e39\u67ab\u84e6\u5730\u541e\u80f8\u5438\u8179\uff0c\u8eab\u624b\u9661\u7136\u79fb\u540e\u4e00\u5c3a\uff0c\u53f3\u638c\u4e00\u8d77\uff0c\u4e00\u62db\u201c\u4e2d\u6d41\u7825\u67f1\u201d\uff0c\u6a2a\u622a\u8fc7 \u53bb\u638c\u5fc3\u4e0e\u4ed6\u7684\u53cc\u6307\uff0c\u78b0\u4e2a\u6b63\u7740\uff0c\u5f20\u4e39\u67ab\u8fd9\u4e00\u638c\u6709\u5f00\u7891\u88c2\u77f3\u4e4b\u80fd\uff0c\u5c31\u7b97\u5185\u529f\u6709\u4e86\u706b\u5019\u7684\u4eba\uff0c\u4f3c \u8fd9\u6837\u7684\u53ea\u51ed\u53cc\u6307\u4e4b\u529b\uff0c\u7ed9\u4ed6\u4e00\u4e2a\u6a2a\u65a9\uff0c\u53cc\u6307\u4e5f\u8981\u62d7\u6298\u3002\u54ea\u77e5\u8fd9\u8499\u9762\u4eba\u7684\u624b\u6307\u7adf\u7136\u575a\u903e\u94a2\u6761\uff0c \u5728\u5f20\u4e39\u67ab\u7684\u638c\u5fc3\u4e00\u6233\uff0c\u8fc5\u5373\u6536\u56de\uff0c\u8d5e\u9053\uff1a\u201c\u5e74\u7eaa\u9752\u9752\uff0c\u6709\u8fd9\u6837\u7684\u529f\u529b\uff0c\u786e\u662f\u540e\u751f\u53ef\u754f\uff0c\u518d\u63a5 \u8fd9\u62db\uff01\u201d\u53d8\u6307\u4e3a\u638c\uff0c\u624b\u638c\u9aa4\u7136\u4ece\u53f3\u8098\u4e0b\u7a7f\u51fa\uff0c\u8f7b\u98d8\u98d8\u5730\u62cd\u4e86\u51fa\u6765\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u5fc3\u5934\u4e00\u9707\uff0c\u521a\u624d\u7ed9\u4ed6\u5728\u638c\u5fc3\u4e00\u6233\uff0c\u53c8\u9178\u53c8\u9ebb\uff0c\u82e5\u975e\u81ea\u5df1\u8fd1\u6765\u5185\u529f\u9887\u6709\u8fdb\u955c\uff0c\u51e0\u4e4e \u7981\u53d7\u4e0d\u4f4f\uff0c\u6b63\u81ea\u60ca\u5f02\uff0c\u53ea\u89c1\u90a3\u4eba\u638c\u52bf\u98d8\u5ffd\uff0c\u5982\u6309\u5982\u62cd\uff0c\u4e0d\u6562\u6020\u6162\uff0c\u6025\u7528\u65b0\u8fd1\u81ea\u5b66\u7684\u5927\u529b\u91d1\u521a \u624b\u6cd5\uff0c\u518d\u63a5\u4e00\u638c\u3002\u90a3\u4eba\u51fa\u638c\u751a\u8f7b\uff0c\u53cc\u638c\u4e00\u4ea4\u52b2\u529b\u5374\u5982\u6392\u5c71\u5012\u6d77\uff0c\u5f20\u4e39\u67ab\u7684\u5927\u529b\u91d1\u521a\u638c\u7ed9\u4ed6\u4e00 \u4e0b\u53cd\u51fb\uff0c\u52b2\u529b\u5bf9\u6d88\uff0c\u53cc\u65b9\u90fd\u5404\u9000\u540e\u4e09\u6b65\uff0c\u4f46\u90a3\u4eba\u9762\u8272\u4e0d\u53d8\uff0c\u800c\u5f20\u4e39\u67ab\u5374\u5df2\u864e\u53e3\u53d1\u9ebb\uff0c\u65c1\u4eba\u770b \u4e0d\u51fa\u6765\uff0c\u5f20\u4e39\u67ab\u5374\u662f\u81ea\u77e5\uff1a\u8fd9\u8499\u9762\u4eba\u7684\u529f\u529b\u5b9e\u662f\u5728\u5df1\u4e4b\u4e0a\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u6ee1\u8179\u72d0\u7591\uff0c\u8fd9\u4eba\u521a\u624d\u6240\u663e\u9732\u7684\u94c1\u6307\u529f\u592b\uff0c\u6b63\u662f\u6b66\u6797\u7edd\u5b66\u7684\u201c\u4e00\u6307\u7985\u529f\u201d\uff0c\u800c\u9002\u624d \u8fd9\u4e00\u638c\uff0c\u5374\u53c8\u662f\u94c1\u7435\u7436\u7684\u624b\u6cd5\uff0c\u94c1\u7435\u7436\u624b\u4e0d\u6bd4\u4e00\u62d7\u7985\u529f\uff0c\u4f1a\u8005\u751a\u591a\uff0c\u4f46\u4f3c\u4ed6\u90a3\u6837\u4f7f\u5f97\u51fa\u795e\u5165 \u5316\u5374\u662f\u5c11\u6709\u3002\u5f20\u4e39\u67ab\u81ea\u601d\uff1a\u8fd9\u4eba\u5206\u660e\u662f\u6b66\u6797\u4e2d\u9876\u513f\u5c16\u513f\u7684\u89d2\u8272\uff0c\u4f55\u4ee5\u4f1a\u4e0e\u6c99\u6d9b\u6df7\u5728\u4e00\u9053\uff1f\u800c \u542c\u4ed6\u7684\u8bdd\u53c8\u597d\u50cf\u77e5\u9053\u81ea\u5df1\u7684\u5e08\u627f\uff0c\u5bf9\u8fd9\u4eba\u7684\u6765\u5386\uff0c\u5b9e\u662f\u6349\u6478\u4e0d\u900f\u3002\u53ea\u542c\u5f97\u90a3\u4eba\u53c8\u7b11\u9053\uff1a\u201c\u5f88 \u4e45\u4ee5\u6765\u672a\u4e0e\u5f3a\u624b\u5bf9\u654c\uff0c\u4eca\u65e5\u5f97\u63a5\u540d\u5bb6\u5f1f\u5b50\u7684\u9ad8\u62db\u771f\u662f\u5feb\u4f55\u5982\u4e4b\uff01\u201d\u55d6\u3001\u55d6\u3001\u55d6\uff0c\u53c8\u662f\u4e00\u8fde\u62cd \u51fa\u4e09\u638c\uff0c\u4f3c\u865a\u4f3c\u5b9e\uff0c\u4f3c\u6309\u4f3c\u70b9\uff0c\u6bcf\u4e00\u62db\u90fd\u662f\u62db\u91cc\u5957\u62db\uff0c\u5f0f\u4e2d\u5957\u5f0f\uff0c\u6697\u85cf\u7740\u5389\u5bb3\u7684\u6740\u624b\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u5c55\u5f00\u201c\u98ce\u4e71\u843d\u82b1\u201d\u7684\u8eab\u6cd5\uff0c\u5728\u8eb2\u95ea\u4e4b\u4e2d\u4e5f\u8fdb\u62db\u53cd\u51fb\u4e00\u6b65\u4e0d\u9000\uff0c\u8fde\u63a5\u4e86\u4e09\u62db\uff0c\u5934\u4e00 \u62db\u7528\u592a\u6781\u62f3\u7684\u201c\u5982\u5c01\u4f3c\u95ed\u201d\uff0c\u5c06\u8499\u9762\u4eba\u7684\u638c\u52bf\u5316\u5f00\uff1b\u7b2c\u4e8c\u62db\u7528\u5c11\u6797\u62f3\u7684\u201c\u9b41\u661f\u8e22\u6597\u201d\uff0c\u817f\u638c \u517c\u65bd\uff0c\u7528\u786c\u529f\u7684\u4ee5\u653b\u4e3a\u5b88\u7684\u62db\u6570\uff0c\u8feb\u654c\u4eba\u6362\u62db\uff1b\u7b2c\u4e09\u62db\u5374\u7528\u5e08\u95e8\u72ec\u521b\u7684\u201c\u767e\u53d8\u7384\u673a\u638c\u6cd5\u201d\uff0c \u5c06\u654c\u4eba\u7684\u6765\u638c\u9ecf\u51fa\u5916\u95e8\u3002\u90a3\u8499\u9762\u4eba\u89c1\u4ed6\u77ac\u606f\u4e4b\u95f4\uff0c\u8fde\u7528\u4e86\u4e09\u79cd\u4e0d\u540c\u7684\u62f3\u6cd5\uff0c\u4e5f\u4f3c\u4e4e\u751a\u4e3a\u8be7 \u5f02\uff0c\u5fae\u5fae\u7684\u201c\u566b\u201d\u4e86\u4e00\u58f0\u3002 \u3000\u3000\u4e24\u4eba\u62f3\u6765\u811a\u5f80\uff0c\u8f6c\u773c\u95f4\u53c8\u6597\u4e86\u4e8c\u4e09\u5341\u62db\uff0c\u5f20\u4e39\u67ab\u5b66\u4e86\u300a\u7384\u529f\u8981\u8bc0\u300b\u4e4b\u540e\uff0c\u81ea\u5df1\u4fee\u4e60\u6240\u89c1 \u8fc7\u7684\u5404\u6d3e\u6b66\u529f\uff0c\u8fd9\u65f6\u4fbf\u8fde\u7528\u5404\u6d3e\u7684\u7cbe\u5999\u62db\u6570\uff0c\u5316\u89e3\u8499\u9762\u4eba\u7684\u653b\u52bf\u3002\u867d\u56e0\u4fee\u4e60\u7684\u65f6\u65e5\u5c1a\u77ed\uff0c\u672a \u5f97\u5404\u5bb6\u7cbe\u9ad3\uff0c\u4f46\u4e5f\u8db3\u4ee4\u4eba\u773c\u82b1\u7f2d\u4e71\uff0c\u5927\u611f\u60ca\u5947\u3002 \u3000\u3000\u90a3\u8499\u9762\u4eba\u4ecd\u662f\u65bd\u5c55\u94c1\u7435\u7436\u624b\uff0c\u4e2d\u95f4\u6742\u4ee5\u4e00\u6307\u7985\u529f\uff0c\u653b\u52bf\u4e1d\u6beb\u4e0d\u7f13\u3002\u5f20\u4e39\u67ab\u867d\u8fde\u7528\u5404\u6d3e\u624b \u6cd5\uff0c\u4f46\u4e5f\u53ea\u80fd\u5728\u4e00\u65f6\u4e4b\u95f4\uff0c\u4e71\u4eba\u773c\u76ee\uff0c\u4e45\u6218\u4e4b\u4e0b\uff0c\u7ec8\u662f\u5403\u4e8f\u3002\u4e09\u5341\u62db\u8fc7\u540e\uff0c\u6e10\u611f\u5403\u529b\uff0c\u7d22\u6027 \u6452\u9664\u5404\u6d3e\u7684\u6b66\u529f\u4e0d\u7528\uff0c\u53ea\u7528\u5e08\u95e8\u72ec\u521b\u7684\u5927\u987b\u5f25\u638c\u5f0f\uff0c\u62b1\u5143\u5b88\u4e00\uff0c\u4ee5\u53cc\u638c\u62a4\u7740\u5168\u8eab\uff0c\u53ea\u5b88\u4e0d \u653b\u3002 \u3000\u3000\u5927\u987b\u5f25\u638c\u5f0f\uff0c\u5708\u5b50\u751a\u5c0f\uff0c\u4f46\u5374\u9632\u62a4\u4e25\u5bc6\uff0c\u6c89\u7a33\u51dd\u91cd\uff0c\u53cd\u51fb\u4e4b\u529b\u751a\u5f3a\uff0c\u90a3\u4eba\u8feb\u5207\u4e4b\u95f4\uff0c\u4e5f \u81ea\u653b\u4e0d\u8fdb\u53bb\u3002\u4f46\u4ed6\u7684\u94c1\u7435\u7436\u624b\u7aef\u7684\u662f\u795e\u5999\u975e\u5e38\uff0c\u6709\u65f6\u638c\u529b\u631f\u98ce\uff0c\u547c\u547c\u4f5c\u54cd\uff0c\u5a01\u731b\u975e\u5e38\uff0c\u6709\u65f6 \u5374\u53c8\u8f7b\u98d8\u5730\u4e00\u62cd\uff0c\u5230\u8feb\u8eab\u4e4b\u65f6\uff0c\u52b2\u529b\u624d\u731d\u7136\u53d1\u51fa\uff0c\u6559\u4eba\u6839\u672c\u5206\u4e0d\u51fa\u4ed6\u7684\u865a\u5b9e\u8f7b\u91cd\uff0c\u771f\u662f\u9632\u4e0d \u80dc\u9632\u3002\u800c\u4e2d\u95f4\u6742\u7528\u7684\u4e00\u6307\u7985\u529f\uff0c\u66f4\u662f\u5389\u5bb3\uff0c\u6240\u6307\u4e4b\u5904\uff0c\u5168\u662f\u4eba\u8eab\u5927\u7a74\u3002\u5f20\u4e39\u67ab\u8d8a\u53d1\u7591\u5fc3\uff0c\u8fd9 \u8499\u9762\u4eba\u7684\u94c1\u7435\u7436\u624b\u51fa\u795e\u5165\u5316\uff0c\u548c\u6fb9\u53f0\u706d\u660e\u4e0d\u76f8\u4e0a\u4e0b\uff0c\u4f46\u4ed6\u7684\u4e00\u6307\u7985\u529f\u6fb9\u53f0\u706d\u660e\u5374\u662f\u4e0d\u4f1a\u3002\u82e5 \u7136\u4e24\u4eba\u4e0d\u662f\u540c\u51fa\u4e00\u95e8\uff0c\u4f55\u4ee5\u94c1\u7435\u7436\u7684\u624b\u6cd5\u5982\u6b64\u76f8\u4f3c\uff1f\u4f46\u82e5\u8bf4\u662f\u540c\u51fa\u4e00\u95e8\uff0c\u4f55\u4ee5\u4ed6\u53c8\u72ec\u4f1a\u4e00\u6307 \u7985\u529f\uff1f\u96be\u9053\u662f\u4ed6\u4eec\u7684\u5e08\u7236\u4e5f\u6709\u504f\u5fc3\u4e0d\u6210\uff1f\u800c\u4e14\u6fb9\u53f0\u706d\u660e\u53ea\u8bf4\u8fc7\u4ed6\u6709\u4e00\u4e2a\u5e08\u59b9\uff0c\u4ece\u672a\u8bf4\u8fc7\u4ed6\u8fd8 \u6709\u5e08\u5144\u5f1f\u3002\u4e24\u4eba\u4e4b\u95f4\uff0c\u6709\u5426\u6e0a\u6e90\uff0c\u4e5f\u8fd8\u662f\u96be\u731c\u6d4b\u3002 \u3000\u3000\u4e24\u4eba\u53c8\u6597\u4e86\u4e09\u4e94\u5341\u62db\uff0c\u8499\u9762\u4eba\u5ffd\u638c\u5ffd\u6307\uff0c\u7740\u7740\u8fdb\u903c\uff0c\u5f20\u4e39\u67ab\u7684\u5927\u987b\u5f25\u638c\u5f0f\u867d\u7136\u795e\u5999\uff0c\u4f46 \u5185\u529f\u7a0d\u900a\uff0c\u6e10\u89c9\u96be\u4ee5\u62b5\u654c\u8fd9\u4e24\u79cd\u4e0a\u4e58\u6b66\u529f\u3002\u6218\u5230\u5206\u9645\uff0c\u90a3\u8499\u9762\u4eba\u559d\u9053\uff1a\u201c\u5c0f\u5fc3\u63a5\u62db\uff01\u201d\u5de6\u638c \u4e00\u6258\u5f20\u4e39\u67ab\u7684\u8098\u5c16\uff0c\u53f3\u6307\u5ffd\u5730\u4e00\u6233\uff0c\u5f20\u4e39\u67ab\u82e5\u5728\u907f\u5f00\u4ed6\u7684\u4e00\u6307\u7985\u529f\uff0c\u5c31\u5f97\u7ed9\u4ed6\u7684\u94c1\u7435\u7436\u624b\u63a8 \u9001\u51fa\u53bb\uff01 \u3000\u3000\u53ea\u89c1\u5f20\u4e39\u67ab\u4e00\u4e2a\u65cb\u8eab\uff0c\u53cc\u6307\u4e00\u5212\uff0c\u53cd\u638c\u4e00\u626b\uff0c\u8fd9\u4e00\u638c\u4e5f\u6b63\u662f\u94c1\u7435\u7436\u7684\u624b\u6cd5\uff0c\u800c\u90a3\u4e00\u5212\u5374 \u662f\u662f\u4f3c\u800c\u975e\u7684\u4e00\u6307\u7985\u529f\uff08\u4e00\u6307\u7985\u529f\u6700\u5c11\u4e5f\u5f97\u6709\u5341\u5e74\u4ee5\u4e0a\u7684\u529f\u529b\uff0c\u4e0d\u662f\u671d\u5915\u95f4\u53ef\u4ee5\u5077\u5b66\uff0c\u5f20\u4e39 \u67ab\u6240\u7528\u7684\u53ea\u662f\u4e00\u6307\u7985\u529f\u7684\u6307\u6cd5\u59ff\u52bf\uff09\u3002\u4f46\u5982\u4e00\u6765\uff0c\u5df2\u8db3\u4ee4\u90a3\u4eba\u60ca\u5f02\u4e0d\u5df2\uff0c\u653b\u52bf\u4e00\u7f13\uff0c\u53c8\u5fae\u5fae \u5730\u201c\u566b\u201d\u4e86\u4e00\u58f0\u3002\u5f20\u4e39\u67ab\u8d81\u52bf\u75be\u4e0a\u53c8\u7528\u767e\u53d8\u7384\u673a\u638c\u6cd5\u62a2\u5360\u4e86\u6709\u5229\u7684\u65b9\u4f4d\u3002\u90a3\u4eba\u6014\u4e86\u4e00\u6014\uff0c\u5ffd \u5730\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u4f60\u597d\u806a\u660e\uff0c\u51e0\u4e4e\u9a97\u8fc7\u4e86\u6211\uff01\u201d\u9a88\u6307\u5982\u621f\uff0c\u4f38\u624b\u4e00\u63a2\uff0c\u53c8\u70b9\u5f20\u4e39\u67ab\u810a\u9aa8\u7684 \u201c\u5929\u67f1\u7a74\u201d\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u4e00\u95ea\u95ea\u5f00\uff0c\u90a3\u4eba\u75be\u8fdb\u4e00\u62db\uff0c\u638c\u529b\u5982\u5c71\uff0c\u5f20\u4e39\u67ab\u582a\u582a\u62b5\u6321\u5f97\u4f4f\u3002\u53c8\u6597\u4e86\u5341\u4f59\u4e8c\u5341 \u62db\uff0c\u90a3\u4eba\u53cc\u638c\u9f50\u51fa\uff0c\u4e00\u865a\u4e00\u5b9e\uff0c\u5de6\u638c\u547c\u547c\u631f\u98ce\uff0c\u5374\u662f\u865a\u62db\uff0c\u53f3\u638c\u8f7b\u8f7b\u62cd\u4e0b\uff0c\u5374\u662f\u5b9e\u62db\uff0c\u5f20\u4e39 \u67ab\u8fd0\u52b2\u63a5\u4ed6\u7684\u5de6\u638c\uff0c\u4e00\u63a5\u4e4b\u4e0b\uff0c\u7acb\u77e5\u4e0a\u5f53\u3002\u90a3\u4eba\u53f3\u638c\u52b2\u529b\u4e00\u53d1\uff0c\u5c06\u5f20\u4e39\u67ab\u53cc\u638c\u8feb\u7740\uff0c\u5ffd\u5730\u54c8 \u54c8\u7b11\u9053\uff1a\u201c\u4f60\u6240\u8a00\u975e\u5047\uff0c\u5f20\u58eb\u8bda\u7684\u5b9d\u85cf\u548c\u5f6d\u548c\u5c1a\u7684\u5947\u4e66\u679c\u7136\u90fd\u88ab\u4f60\u53d1\u6398\u53bb\u4e86\uff0c\u4ffa\u5728\u8fd9\u91cc\uff0c\u8fd8 \u6709\u4ec0\u4e48\u610f\u601d\uff1f\u201d\u865a\u6643\u4e00\u638c\uff0c\u7a81\u7136\u5411\u540e\u4e00\u7eb5\uff0c\u5954\u51fa\u5c71\u5be8\u3002\u8fd9\u8499\u9762\u4eba\u7a81\u5982\u5176\u6765\uff0c\u7a81\u5982\u5176\u53bb\uff0c\u5982\u795e \u9f99\u4e4b\u89c1\u9996\u4e0d\u89c1\u5c3e\uff0c\u4f17\u4eba\u9f50\u90fd\u60ca\u6115\uff0c\u5373\u5f20\u4e39\u67ab\u4ea6\u662f\u767e\u601d\u4e0d\u89e3\uff1a\u518d\u6597\u4e0b\u53bb\uff0c\u90a3\u4eba\u5206\u660e\u53ef\u80dc\uff0c\u5374\u53c8 \u4f55\u4ee5\u7a81\u7136\u4f4f\u624b\uff1f \u3000\u3000\u90a3\u8499\u9762\u4eba\u662f\u968f\u989d\u5409\u591a\u6765\u7684\uff0c\u59cb\u7ec8\u4e0d\u4ee5\u771f\u9762\u76ee\u793a\u4eba\uff0c\u5373\u6c99\u6d9b\u7236\u5b50\u4e5f\u4e0d\u77e5\u9053\u4ed6\u7684\u6765\u5386\uff0c\u53ea\u662f \u89c1\u4ed6\u6b66\u529f\u597d\u5f97\u51fa\u5947\uff0c\u6545\u6b64\u597d\u751f\u656c\u754f\u3002\u8499\u9762\u4eba\u4e00\u8d70\uff0c\u6c99\u6d9b\u89c1\u52bf\u4e0d\u597d\uff0c\u7acb\u5373\u4e0b\u4ee4\u7fa4\u6bb4\u3002\u989d\u5409\u591a\u9002 \u624d\u65ad\u5251\u53d7\u8fb1\uff0c\u5403\u4e86\u5927\u4e8f\uff0c\u8fd9\u65f6\u6025\u6b32\u62a5\u4ec7\uff0c\u62a2\u5728\u5934\u91cc\uff0c\u5f20\u4e39\u67ab\u54c8\u54c8\u4e00\u7b11\uff0c\u4e0e\u4e91\u857e\u6253\u4e86\u4e00\u4e2a\u62db \u547c\uff0c\u500f\u65f6\u95f4\u53cc\u5251\u9f50\u51fa\uff0c\u989d\u5409\u591a\u62a2\u8fc7\u4e00\u67c4\u957f\u5251\uff0c\u521a\u6321\u5f97\u4e24\u62db\uff0c\u5f20\u3001\u4e91\u4e8c\u4eba\u51fa\u624b\u592a\u5feb\uff0c\u6c99\u6d9b\u7684\u515a \u7fbd\u8fd8\u672a\u8d76\u5f97\u53ca\u63a5\u5e94\uff0c\u53ea\u542c\u5f97\u201c\u5580\u5693\u201d\u4e00\u58f0\u989d\u5409\u591a\u7684\u957f\u5251\u53c8\u7ed9\u524a\u65ad\u4e86\u3002\u4ed6\u7684\u526f\u624b\u5409\u5f70\u963f\u53eb\u9053\uff1a \u201c\u5f20\u4e39\u67ab\uff0c\u4f60\u5bb6\u5c61\u53d7\u6211\u56fd\u5927\u6069\uff0c\u4f60\u4f55\u4ee5\u5982\u6b64\u4e0d\u660e\u4e8b\u7406\uff1f\u201d\u62d4\u5200\u62db\u67b6\uff0c\u5f20\u4e39\u67ab\u4e00\u5251\u524a\u51fa\uff0c\u4f59\u52bf \u672a\u8870\uff0c\u5251\u5149\u4e00\u7ed5\uff0c\u53c8\u628a\u5409\u5f70\u963f\u7684\u4f69\u5200\u524a\u65ad\u4e86\uff0c\u5409\u5f70\u963f\u5927\u60ca\u5931\u8272\uff0c\u53eb\u9053\uff1a\u201c\u5f20\u4e39\u67ab\uff0c\u4f60\u3001 \u4f60\u2026\u2026\u201d\u8bdd\u672a\u8bf4\u5b8c\uff0c\u4e91\u857e\u7684\u5251\u62db\u63a5\u8fde\u800c\u81f3\uff0c\u5409\u5f70\u963f\u7684\u6b66\u827a\u5728\u989d\u5409\u591a\u4e4b\u4e0b\uff0c\u5982\u4f55\u6321\u5f97\u4f4f\u53cc\u5251\u5408 \u74a7\u4e4b\u529b\uff1f\u88ab\u4e91\u857e\u4e00\u5251\u659c\u524a\uff0c\u767b\u65f6\u6b7b\u4e8e\u975e\u547d\u3002\u989d\u5409\u591a\u6a2a\u8dc3\u4e09\u6b65\uff0c\u9661\u542c\u5f97\u4e00\u58f0\u5927\u559d\u4eba\u8fd8\u672a\u5230\uff0c\u5df2 \u662f\u52b2\u98ce\u8d2f\u80f8\uff0c\u539f\u6765\u6b63\u5de7\u78b0\u7740\u77f3\u82f1\u51fa\u624b\u3002\u77f3\u82f1\u7ef0\u53f7\u53eb\u201c\u8f70\u5929\u96f7\u201d\uff0c\u4ee5\u8e51\u4e91\u5251\u672f\u3001\u98de\u8757\u77f3\u6697\u5668\u3001 \u8f70\u96f7\u638c\u53f7\u79f0\u6b66\u6797\u4e09\u7edd\uff0c\u8fd9\u4e00\u638c\u4e4b\u529b\uff0c\u4f55\u6b62\u5343\u65a4\uff0c\u989d\u57cb\u591a\u521a\u521a\u88ab\u5f20\u4e39\u67ab\u4e0e\u4e91\u857e\u4e8c\u4eba\u6740\u5f97\u5934\u660f\u773c \u82b1\uff0c\u4e0d\u8fa8\u5357\u5317\uff0c\u8fd9\u65f6\u53c8\u78b0\u4e0a\u77f3\u82f1\uff0c\u660f\u5934\u660f\u8111\uff0c\u8eb2\u907f\u4e0d\u53ca\uff0c\u88ab\u77f3\u82f1\u201c\u535c\u201d\u7684\u4e00\u638c\u51fb\u4e2d\u540e\u5fc3\uff0c\u62a4 \u8eab\u7684\u9501\u5b50\u9ec4\u91d1\u7532\u4e5f\u7ed9\u9707\u88c2\uff0c\u767b\u65f6\u4e00\u5927\u53e3\u9c9c\u8840\u55b7\u4e86\u51fa\u6765\uff0c\u4e5f\u5e78\u800c\u6709\u62a4\u8eab\u7532\u4e00\u6321\uff0c\u8981\u4e0d\u7eb5\u7136\u4ed6\u6b66 \u529f\u518d\u9ad8\uff0c\u6027\u547d\u4e5f\u662f\u96be\u4fdd\u3002\u9976\u662f\u5982\u6b64\uff0c\u4e5f\u5df2\u6655\u5012\u5730\u4e0a\uff0c\u968f\u6765\u7684\u6b66\u58eb\uff0c\u7acb\u523b\u5c06\u4ed6\u62ac\u8d77\uff0c\u4e0d\u6562\u518d \u6218\uff0c\u72fc\u72c8\u800c\u9003\u3002 \u3000\u3000\u6c99\u6d9b\u8bf7\u6765\u7684\u90a3\u6279\u4e09\u5c71\u4e94\u5cb3\u7684\u4eba\u9a6c\uff0c\u6709\u4e00\u5927\u534a\u6000\u6709\u4e8c\u5fc3\uff0c\u89c1\u52bf\u4e0d\u597d\uff0c\u5148\u81ea\u8d70\u4e86\uff0c\u6709\u4e00\u5c0f\u534a \u5fc3\u8179\u6b7b\u515a\uff0c\u89c1\u5f20\u3001\u4e91\u4e8c\u4eba\u53cc\u5251\u7684\u5a01\u529b\u65e0\u6bd4\uff0c\u4e5f\u81ea\u80c6\u5bd2\u3002\u5f20\u4e39\u67ab\u54c8\u54c8\u5927\u7b11\uff0c\u6307\u4e1c\u6253\u897f\uff0c\u6307\u5357\u6253 \u5317\u628a\u7fa4\u8d3c\u6740\u5f97\u843d\u82b1\u6d41\u6c34\uff0c\u4f46\u654c\u4f17\u6211\u5bee\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u8fd8\u662f\u672a\u80fd\u95ef\u51fa\u91cd\u56f4\u3002\u77f3\u82f1\u5927\u559d\u9053\uff1a\u201c\u64d2\u8d3c \u5148\u64d2\u738b\uff0c\u6c99\u8001\u8d3c\u6211\u5148\u4e0e\u4f60\u7b97\u5e10\uff01\u201d\u6251\u5165\u4eba\u4e1b\uff0c\u8ffd\u8d76\u6c99\u6d9b\uff0c\u6c99\u6d9b\u5ffd\u5730\u4e00\u58f0\u547c\u5578\uff0c\u515a\u7fbd\u5982\u6f6e\u75be \u9000\uff0c\u5f20\u4e39\u67ab\u7b49\u4eba\u6014\u4e86\u4e00\u6014\uff0c\u7fa4\u8d3c\u9000\u51fa\u4e86\u201c\u805a\u4e49\u5385\u201d\uff0c\u5ffd\u542c\u5f97\u8f70\u9686\u4e00\u58f0\u5de8\u54cd\uff0c\u6c99\u6d9b\u7684\u515a\u7fbd\u5df2\u628a \u5343\u65a4\u95f8\u653e\u4e0b\uff0c\u5185\u5916\u9694\u65ad\uff01 \u3000\u3000\u5916\u9762\u4f0f\u6709\u5f13\u5f29\u624b\u548c\u52fe\u9570\u624b\uff0c\u4ee5\u77f3\u82f1\u548c\u5f20\u4e39\u67ab\u4e4b\u529b\uff0c\u7eb5\u80fd\u5c06\u5343\u65a4\u95f8\u6258\u8d77\uff0c\u4f46\u5916\u9762\u7684\u6bd2\u7bad\uff0c \u5fc5\u7136\u4e58\u673a\u5c04\u6765\uff0c\u96be\u4ee5\u9632\u5907\u3002\u77f3\u82f1\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u597d\uff0c\u54b1\u4eec\u7b97\u662f\u88ab\u4ed6\u56f0\u5728\u8fd9\u91cc\u5566\uff01\u201d\u6c99\u6d9b\u5728 \u5916\u9762\u53eb\u9053\uff1a\u201c\u628a\u90a3\u5e45\u753b\u7ed9\u6211\uff0c\u7f34\u4e86\u5175\u68b0\uff0c\u6211\u8fd8\u53ef\u4ee5\u5ff5\u6614\u65e5\u516b\u62dc\u4e4b\u60c5\uff0c\u653e\u4f60\u4eec\u4e0b\u5c71\u3002\u201d\u4e91\u857e\u7b11 \u4e86\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4ed6\u4eec\u8fd8\u4e0d\u4fe1\u4f60\u5df2\u628a\u5b9d\u85cf\u53d6\u53bb\uff0c\u5c31\u662f\u7ed9\u4ed6\u753b\u56fe\u4ed6\u4e5f\u65e0\u7528\u3002\u201d\u5f20\u4e39\u67ab\u9053\uff1a \u201c\u6211\u504f\u4e0d\u7ed9\u4ed6\u3002\u201d\u77f3\u82f1\u9053\uff1a\u201c\u6b63\u662f\u3002\u8fd9\u662f\u5148\u4e3b\u9057\u7269\uff0c\u5c82\u53ef\u7ed9\u4ed6\uff1f\u201d\u4e91\u857e\u4e5f\u7b11\u9053\uff1a\u201c\u6211\u4e5f\u662f\u8bf4 \u7b11\u800c\u5df2\uff0c\u54b1\u4eec\u5c31\u662f\u88ab\u56f0\u800c\u6b7b\uff0c\u4e5f\u4e0d\u80fd\u5c48\u8fb1\u6c42\u5b58\u3002\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u6211\u4e00\u5411\u7b11\u4f60\u67d4\u5f31\uff0c \u4f60\u539f\u6765\u4e5f\u6709\u7537\u5b50\u7684\u6c14\u6168\u3002\u201d\u8fd9\u5f53\u7136\u4e5f\u662f\u8bf4\u7b11\u4e4b\u8bcd\u3002\u4e91\u857e\u5374\u8ba4\u8d77\u771f\u6765\uff0c\u5550\u4e86\u4e00\u53e3\u9053\uff1a\u201c\u5478\uff0c\u53ea \u6709\u4f60\u4eec\u7537\u5b50\u624d\u662f\u82f1\u96c4\u8c6a\u6770\u4e48\uff1f\u201d \u3000\u3000\u8fd9\u65f6\u805a\u4e49\u5385\u5185\u53ea\u5269\u4e0b\u4e86\u77f3\u82f1\u7b49\u56db\u4eba\uff0c\u4e91\u857e\u6b64\u8a00\u4e00\u51fa\uff0c\u77f3\u82f1\u7236\u5973\u5168\u90fd\u53d8\u8272\u3002\u77f3\u7fe0\u51e4\u504e\u8fd1\u4e91 \u857e\uff0c\u62c9\u5979\u7684\u624b\uff0c\u98a4\u58f0\u8bf4\u9053\uff1a\u201c\u4e91\u76f8\u516c\uff0c\u4f60\u5f53\u771f\u662f\u4e2a\u5973\u5b50\u4e48\uff1f\u201d\u4e91\u857e\u9762\u7ea2\u8fc7\u8033\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a \u201c\u59d0\u59d0\uff0c\u4f60\u8bf4\u5f97\u4e0d\u9519\uff0c\u6211\u5f53\u771f\u662f\u4e2a\u5973\u5b50\uff01\u201d\u77f3\u7fe0\u51e4\u82b1\u5bb9\u5931\u8272\uff0c\u6307\u7740\u4e91\u857e\u9053\uff1a\u201c\u5c0f\u51a4\u5bb6\uff0c\u4f60\uff0c \u4f60\u2026\u2026\u201d\u54fd\u54bd\u7740\u8bf4\u4e0d\u4e0b\u53bb\u3002\u4e91\u857e\u7f9e\u60ed\u4e0d\u5df2\uff0c\u9053\uff1a\u201c\u597d\u59d0\u59d0\uff0c\u662f\u6211\u4e00\u65f6\u6dd8\u6c14\uff0c\u6b3a\u9a97\u4e86\u4f60\u3002\u59d0 \u59d0\uff0c\u4f60\u522b\u607c\u6012\uff0c\u6211\u3001\u6211\u8fd8\u6709\u4e00\u4f4d\u4e49\u5144\u2026\u2026\u201d\u77f3\u7fe0\u51e4\u674f\u8138\u751f\u55d4\u6012\u9053\uff1a\u201c\u8c01\u7ba1\u4f60\u4ec0\u4e48\u4e49\u5144\uff0c\u5440\uff0c \u5c0f\u51a4\u5bb6\uff0c\u4f60\u4e00\u70b9\u4e5f\u4e0d\u77e5\u9053\u6211\u7684\u5fc3\u4e8b\uff01\u201d\u77f3\u7fe0\u51e4\u6b64\u65f6\u867d\u5df2\u660e\u77e5\u5979\u662f\u4e2a\u5973\u5b50\uff0c\u4f46\u8bf4\u8bdd\u539f\u53e3\u6c14\uff0c\u4ecd \u662f\u5c06\u5979\u5f53\u4f5c\u7537\u5b50\u770b\u5f85\uff0c\u5f20\u4e39\u67ab\u542c\u4e86\uff0c\u4e0d\u89c9\u5931\u7b11\u3002\u77f3\u82f1\u6bd4\u8f83\u8001\u6210\u6301\u91cd\uff0c\u5c06\u5f20\u4e39\u67ab\u62c9\u8fc7\u4e00\u8fb9\u7ec6\u7ec6 \u76d8\u95ee\uff0c\u5f20\u4e39\u67ab\u5c06\u4e91\u857e\u7684\u6765\u5386\u8bf4\u4e86\uff0c\u53c8\u7b11\u9053\uff1a\u201c\u5f53\u65f6\u662f\u4f60\u62e9\u5a7f\u5fc3\u5207\uff0c\u4e91\u857e\u53c8\u662f\u5c0f\u5b69\u5b50\u5fc3\u6027\uff0c\u8981 \u4e0d\u7136\u4e5f\u4e0d\u81f3\u4e8e\u95f9\u4e86\u8fd9\u573a\u7b11\u8bdd\u3002\u597d\u5728\u4e5f\u4e0d\u8fc7\u8499\u4e86\u4f60\u4eec\u4e00\u5e74\uff0c\u4e0d\u81f3\u4e8e\u8bef\u4e86\u4ee4\u5a9b\u7684\u9752\u6625\u3002\u91d1\u5200\u5468\u5065 \u7684\u513f\u5b50\u4f60\u662f\u89c1\u8fc7\u7684\u4e86\uff0c\u4f60\u8bf4\u6b64\u4eba\u5728\u540e\u8f88\u4e4b\u4e2d\uff0c\u4e5f\u7b97\u5f97\u662f\u4e00\u4f4d\u5c11\u5e74\u4fca\u6770\u5427\uff1f\u201d\u77f3\u82f1\u4e00\u542c\uff0c\u81ea\u7136 \u77e5\u4ed6\u8bdd\u4e2d\u4e4b\u610f\uff0c\u6ca1\u7cbe\u6253\u91c7\uff0c\u7b54\u9053\uff1a\u201c\u5973\u513f\u7684\u5a5a\u4e8b\uff0c\u6211\u4e5f\u4e0d\u518d\u7ba1\u5566\u3002\u5468\u5c71\u6c11\u561b\uff0c\u82e5\u4e0e\u4e91\u76f8\u516c\u76f8 \u6bd4\uff0c\u90a3\u81ea\u7136\u6bd4\u4e0d\u4e0a\u3002\u4f46\u4e5f\u8fd8\u7b97\u5f97\u662f\u4e2a\u6709\u51fa\u606f\u7684\u5b69\u5b50\uff01\u201d\u77f3\u82f1\u53eb\u60ef\u4e86\uff0c\u4e00\u65f6\u8f6c\u4e0d\u8fc7\u53e3\uff0c\u4e5f\u50cf\u4ed6 \u5973\u513f\u4e00\u6837\uff0c\u4ecd\u7136\u53eb\u4e91\u857e\u505a\u201c\u76f8\u516c\u201d\u3002\u5f20\u4e39\u67ab\u53c8\u4e0d\u89c9\u4e00\u7b11\u3002\u77f3\u82f1\u5ffd\u9053\uff1a\u201c\u5c11\u4e3b\uff0c\u6211\u5931\u4e86\u4e00\u4f4d\u7231 \u5a7f\uff0c\u4f46\u5374\u8981\u606d\u559c\u4f60\u5566\u3002\u201d\u53cd\u8fc7\u6765\u53d6\u7b11\u5f20\u4e39\u67ab\u3002\u8fd9\u4e00\u53d6\u7b11\uff0c\u5374\u52fe\u8d77\u4e86\u5f20\u4e39\u67ab\u7684\u5fc3\u4e8b\uff0c\u53f9\u53e3\u6c14 \u9053\uff1a\u201c\u559c\u4ece\u4f55\u6765\uff1f\u201d\u77f3\u82f1\u9053\uff1a\u201c\u4f60\u4eec\u771f\u662f\u5929\u9020\u5730\u8bbe\u7684\u4e00\u5bf9\u74a7\u4eba\uff0c\u6211\u7684\u4e2b\u5934\u54ea\u914d\u5f97\u4e0a\u4e91\u76f8\u516c\uff0c \u5979\u5c31\u662f\u4e0d\u80af\uff0c\u6211\u4e5f\u8981\u53eb\u5979\u628a\u4e91\u76f8\u516c\u8ba9\u4e0e\u4f60\u3002\u4f60\u4eec\u51e0\u65f6\u8bf7\u559d\u559c\u9152\uff0c\u54c8\u54c8\uff0c\u8fd9\u4e5f\u662f\u6b66\u6797\u7684\u4e00\u6bb5\u4f73 \u8bdd\u5440\uff01\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u8a00\u4e4b\u8fc7\u65e9\uff0c\u8a00\u4e4b\u8fc7\u65e9\uff01\u77f3\u8001\u82f1\u96c4\uff0c\u4f60\u8fd8\u6709\u6240\u4e0d\u77e5\u3002\u201d\u5c06\u5f20\u3001\u4e91\u4e24\u5bb6\u7684 \u51a4\u4ec7\u8bf4\u4e86\uff0c\u77f3\u82f1\u60ca\u8be7\u4e0d\u5df2\u3002 \u3000\u3000\u90a3\u8fb9\u53a2\u77f3\u7fe0\u51e4\u4ecd\u4e0e\u4e91\u857e\u7d6e\u7d6e\u53e8\u53e8\u5730\u8bf4\u4e2a\u4e0d\u5b8c\uff0c\u77f3\u7fe0\u51e4\u4e00\u5411\u628a\u4e91\u857e\u5f53\u4f5c\u5979\u7406\u60f3\u7684\u592b\u5a7f\uff0c\u8fd9 \u65f6\u81ea\u662f\u4f24\u5fc3\u4e0d\u5df2\u3002\u4e91\u857e\u867d\u7136\u751a\u662f\u5c34\u5c2c\uff0c\u4f46\u4ea6\u4e3a\u5979\u611f\u52a8\uff0c\u5ffd\u9053\uff1a\u201c\u597d\u59d0\u59d0\uff0c\u6211\u6b64\u751f\u4e0d\u5ac1\uff0c\u966a\u4f60 \u5c31\u662f\uff01\u201d \u3000\u3000\u77f3\u7fe0\u51e4\u9762\u4e0a\u63a0\u8fc7\u4e00\u4e1d\u7b11\u5bb9\uff0c\u9053\uff1a\u201c\u4f60\u8bdd\u5f53\u771f\uff1f\u201d\u4e91\u857e\u5b69\u5b50\u4e4b\u6c14\u4ecd\u7136\u672a\u8131\uff0c\u7b11\u9053\uff1a\u201c\u600e\u4e0d \u5f53\u771f\uff1f\u4f46\u6211\u7684\u597d\u59d0\u59d0\u5440\uff0c\u6211\u6709\u4e00\u4e2a\u5144\u5f1f\uff0c\u4f60\u5374\u6ca1\u6709\u3002\u6211\u4e0d\u5ac1\u4eba\u81ea\u53ef\uff0c\u4f60\u4e0d\u5ac1\u4eba\uff0c\u8c01\u63a5\u4f60\u4eec\u77f3 \u5bb6\u7684\u9999\u706f\u540e\u4ee3\uff1f\u201d\u77f3\u7fe0\u51e4\u5550\u4e86\u4e00\u53e3\uff0c\u77a7\u4e86\u5f20\u4e39\u67ab\u4e00\u773c\u5ffd\u9053\uff1a\u201c\u4e91\u76f8\u516c\uff0c\u6211\u77e5\u9053\u4f60\u8bdd\u4e0d\u7531\u5fc3\uff0c \u6211\u867d\u7136\u662f\u4e2a\u50bb\u4e2b\u5934\uff0c\u4e5f\u65e9\u770b\u51fa\u8c01\u662f\u4f60\u7684\u5fc3\u4e0a\u4eba\u4e86\u3002\u201d\u4e91\u857e\u4e5f\u7ed9\u5979\u7684\u8bdd\u5f15\u8d77\u611f\u89e6\uff0c\u53f9\u4e86\u53e3\u6c14\uff0c \u9893\u7136\u8bf4\u9053\uff1a\u201c\u6211\u6b64\u751f\u6c38\u4e0d\u5ac1\u4eba\uff0c\u4f60\u82e5\u4e0d\u4fe1\uff0c\u6211\u7ed9\u4f60\u53d1\u4e2a\u8a93\uff01\u201d\u77f3\u7fe0\u51e4\u63a9\u4f4f\u5979\u7684\u53e3\u9053\uff1a\u201c\u597d\u7aef \u7aef\u7684\uff0c\u53d1\u4ec0\u4e48\u8a93\u5462\uff1f\u5440\uff0c\u6211\u6709\u4e86\u4f60\u8fd9\u6837\u4e00\u4f4d\u597d\u59b9\u59b9\uff0c\u4e5f\u5c31\u5f88\u5fc3\u6ee1\u610f\u8db3\u4e86\u3002\u201d \u3000\u3000\u77f3\u82f1\u7d20\u6027\u8c41\u8fbe\uff0c\u867d\u7136\u4e00\u65f6\u4e0d\u5feb\uff0c\u6b64\u523b\u4ea6\u5df2\u6d88\u9664\uff0c\u5bf9\u5973\u513f\u7b11\u9053\uff1a\u201c\u5999\u6781\uff0c\u5999\u6781\uff0c\u4f60\u4eec\u65e2\u7136 \u8ba4\u4e86\u59d0\u59b9\uff0c\u4e91\u76f8\u516c\u600e\u4e48\u8fd8\u4e0d\u6765\u62dc\u89c1\u6211\u8fd9\u4e2a\u4e49\u7236\uff1f\u201d\u4e91\u857e\u4e00\u7b11\u800c\u8d77\uff0c\u8d70\u5230\u77f3\u82f1\u8ddf\u524d\uff0c\u76c8\u76c8\u4e0b \u62dc\uff0c\u77f3\u82f1\u5c06\u5979\u6276\u8d77\uff0c\u9053\uff1a\u201c\u4e91\u76f8\u516c\uff0c\u751f\u53d7\u4f60\u4e86\uff01\u201d\u5f20\u4e39\u67ab\u54c8\u54c8\u4e00\u7b11\u9053\uff1a\u201c\u8fd8\u53eb\u4e91\u76f8\u516c\uff1f\u201d\u6b64 \u8a00\u4e00\u51fa\uff0c\u4f17\u4eba\u4ff1\u90fd\u5931\u7b11\u3002 \u3000\u3000\u8fd9\u65f6\u5df2\u8fd1\u9ec4\u660f\uff0c\u5916\u9762\u53eb\u56a3\u4e4b\u58f0\uff0c\u4ecd\u7136\u6b64\u65ad\u5f7c\u7eed\uff0c\u805a\u4e49\u5385\u5185\u5e76\u65e0\u98df\u7269\u3002\u5e78\u5f20\u4e39\u67ab\u4e0e\u4e91\u857e\u968f \u8eab\u643a\u6709\u5e72\u7cae\uff0c\u53d6\u6765\u7ed9\u56db\u4eba\u5403\u4e86\u3002\u4e91\u857e\u9053\uff1a\u201c\u4eca\u65e5\u5c06\u5c31\u8fc7\u4e86\uff0c\u660e\u65e5\u5982\u4f55\uff1f\u201d\u5f20\u4e39\u67ab\u7b11\u9053\uff1a\u201c\u660e \u65e5\u6101\u6765\u660e\u65e5\u5fe7\uff0c\u4f55\u5fc5\u53bb\u7ba1\uff1f\u201d\u56db\u4eba\u8c08\u8c08\u7b11\u7b11\uff0c\u5012\u4e0d\u5bc2\u5bde\uff0c\u5916\u9762\u6c99\u6d9b\u7b49\u4eba\uff0c\u60e7\u4ed6\u4eec\u53cc\u5251\u5408\u74a7\u7684 \u5a01\u529b\uff0c\u4e0d\u6562\u8fdb\u6765\u5077\u88ad\u3002 \u3000\u3000\u662f\u591c\u5f20\u4e39\u67ab\u4e0e\u77f3\u82f1\u8f6e\u73ed\u770b\u5b88\uff0c\u4e91\u857e\u4e0e\u7fe0\u51e4\u5728\u6905\u4e0a\u8054\u201c\u5e8a\u201d\u591c\u8bdd\uff0c\u5404\u8bc9\u522b\u540e\u4e4b\u60c5\uff0c\u4eb2\u4eb2\u70ed \u70ed\uff0c\u5012\u771f\u7684\u6709\u5982\u4e00\u53cc\u59d0\u59b9\u3002\u4e91\u857e\u95ee\u9053\uff1a\u201c\u90a3\u6b21\u54b1\u4eec\u5728\u9752\u9f99\u5ce1\u5206\u624b\uff0c\u4f60\u7239\u7239\u6765\u4fe1\u50ac\u4f60\u56de\u53bb\uff0c\u7a76 \u7adf\u662f\u4e3a\u4e86\u4f55\u4e8b\uff1f\u201d\u77f3\u7fe0\u51e4\u9053\uff1a\u201c\u8fd8\u4e0d\u662f\u4e3a\u4e86\u90a3\u5e45\u53e4\u602a\u7684\u753b\u56fe\uff0c\u6211\u7239\u7239\u542c\u8bf4\uff0c\u74e6\u523a\u56fd\u4e0d\u77e5\u600e\u5730 \u5df2\u77e5\u9053\u8fd9\u5e45\u753b\u56fe\u5728\u6211\u5bb6\u4e2d\uff0c\u8981\u6d3e\u4eba\u6765\u52ab\u593a\u3002\u56e0\u6b64\u6211\u7239\u7239\u53eb\u6211\u56de\u53bb\uff0c\u4e3e\u5bb6\u9003\u5230\u996e\u9a6c\u5ddd\u84dd\u5be8\u4e3b\u90a3 \u91cc\u907f\u7978\uff0c\u6211\u4eec\u5168\u5bb6\u8fd8\u662f\u6218\u540e\u624d\u56de\u6765\u7684\u3002\u60f3\u4e0d\u5230\u6c99\u6d9b\u8fd9\u8001\u8d3c\u4e0e\u4e5f\u5148\u52fe\u7ed3\uff0c\u8fd8\u662f\u653e\u6211\u4eec\u4e0d\u8fc7\u3002\u201d \u4e91\u857e\u7b11\u9053\uff1a\u201c\u4ed6\u4eec\u54ea\u91cc\u77e5\u9053\uff0c\u8fd9\u5e45\u753b\u56fe\u65e9\u5df2\u5230\u4e86\u6211\u5927\u54e5\u624b\u4e2d\u3002\u201d\u77f3\u7fe0\u51e4\u542c\u5979\u53eb\u201c\u5927\u54e5\u201d\u53eb\u5f97 \u5982\u6b64\u4eb2\u70ed\uff0c\u5fc3\u4e2d\u53c8\u662f\u4e00\u9178\uff0c\u9053\uff1a\u201c\u4f60\u6709\u4e86\u54e5\u54e5\uff0c\u5c31\u5fd8\u4e86\u59d0\u59d0\u4e86\uff01\u201d\u4e91\u857e\u53c8\u53f9\u4e86\u53e3\u6c14\uff0c\u5979\u662f\u4e2a \u5973\u5b69\u513f\u5bb6\uff0c\u4e0d\u4f3c\u5f20\u4e39\u67ab\u7684\u65e0\u6240\u907f\u5fcc\uff0c\u8574\u84c4\u5728\u5fc3\u4e2d\u7684\u6101\u601d\uff0c\u5373\u7b97\u5bf9\u7740\u60c5\u5982\u59d0\u59b9\u7684\u77f3\u7fe0\u51e4\uff0c\u4e5f\u4e0d \u80af\u8a00\u8bf4\u3002 \u3000\u3000\u77f3\u7fe0\u51e4\u89c1\u5979\u795e\u60c5\u5947\u7279\uff0c\u751a\u662f\u8be7\u5f02\uff0c\u5f53\u4e0b\u4e5f\u4e0d\u4fbf\u591a\u6240\u76d8\u95ee\uff0c\u4e24\u4eba\u8c08\u8c08\u8bf4\u8bf4\uff0c\u4e0d\u89c9\u6726\u80e7\u7761 \u53bb\u3002\u4e5f\u4e0d\u77e5\u7761\u4e86\u591a\u4e45\uff0c\u5ffd\u542c\u5f97\u5916\u9762\u4eba\u58f0\u55a7\u54d7\uff0c\u5f20\u4e39\u67ab\u53eb\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u4f60\u5feb\u8d77\u6765\u770b\uff01\u4f60\u8bf4\u66f9 \u64cd\uff0c\u66f9\u64cd\u4fbf\u5230\uff0c\u4f60\u77a7\uff0c\u8fd9\u53ef\u4e0d\u662f\u4f60\u90a3\u4f4d\u4e49\u5144\u5230\u4e86\uff01\u201d\u4e91\u857e\u8d77\u8eab\u4e00\u770b\u5df2\u662f\u7b2c\u4e8c\u65e5\u7684\u65e9\u6668\uff0c\u5343\u65a4 \u95f8\u53ea\u62e6\u7740\u6b63\u9762\u5927\u95e8\uff0c\u4e24\u65c1\u5899\u58c1\u8fd8\u6709\u7bad\u773c\uff0c\u53ea\u89c1\u5916\u65cc\u65d7\u62db\u5c55\uff0c\u6709\u4e24\u9762\u5927\u65d7\uff0c\u7279\u522b\u9192\u76ee\uff0c\u4e00\u8fb9\u7ea2 \u65e5\uff0c\u4e00\u8fb9\u660e\u6708\uff0c\u6b63\u662f\u91d1\u5200\u5be8\u4e3b\u7684\u6807\u5fd7\uff0d\uff0d\u65e5\u6708\u53cc\u65d7\uff01 \u3000\u3000\u5916\u9762\u6740\u58f0\u9707\u5929\uff0c\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u5468\u5c71\u6c11\u6765\u5f97\u771f\u662f\u5408\u65f6\u3002\u201d\u8bed\u5e26\u53cc\u5173\uff0c\u4e91\u857e\u4e0d\u89c9\u62bf\u5634\u4e00\u7b11\u3002 \u8fc7\u4e86\u4e00\u4f1a\uff0c\u25a1\u6740\u4e4b\u58f0\u6e10\u6e10\u9759\u6b62\uff0c\u5343\u65a4\u95f8\u4e5f\u7ed9\u5916\u9762\u7684\u4eba\u5408\u529b\u540a\u8d77\uff0c\u9633\u5149\u8000\u773c\uff0c\u5468\u5c71\u6c11\u7f13\u7f13\u8d70\u8fdb \u805a\u4e49\u5385\u6765\u3002 \u3000\u3000\u4e91\u857e\u6628\u65e5\u9732\u4e86\u5e90\u5c71\u771f\u76f8\uff0c\u7d22\u6027\u6362\u56de\u4e86\u5973\u5b50\u7684\u8863\u88f3\uff0c\u5468\u5c71\u6c11\u4e00\u89c1\uff0c\u9887\u662f\u60ca\u5947\uff0c\u4e0e\u4f17\u4eba\u6253\u4e86 \u62db\u547c\uff0c\u53c8\u5411\u4e91\u857e\u77a5\u4e86\u4e00\u773c\u3002\u4e91\u857e\u7b11\u9053\uff1a\u201c\u6211\u6258\u4f60\u7684\u4e8b\u60c5\uff0c\u6211\u5df2\u7ecf\u81ea\u5df1\u8bf4\u6e05\u695a\u5566\u3002\u201d\u4e91\u857e\u6362\u4e86 \u5973\u88c5\uff0c\u4e00\u7b11\u4e4b\u4e0b\uff0c\u68a8\u6da1\u9690\u73b0\uff0c\u6709\u5982\u521d\u5f00\u7684\u767e\u5408\u82b1\uff0c\u5728\u5468\u5c71\u6c11\u773c\u4e2d\u66f4\u589e\u7f8e\u4e3d\uff0c\u5468\u5c71\u6c11\u4e0d\u89c9\u5fc3\u4e2d \u4e00\u52a8\uff0c\u4f46\u89c1\u5f20\u4e39\u67ab\u4f3c\u7b11\u975e\u7b11\u5730\u671b\u7740\u81ea\u5df1\uff0c\u53c8\u4e0d\u89c9\u723d\u7136\u82e5\u5931\u3002\u8981\u77e5\u5468\u5c71\u6c11\u672c\u6765\u662f\u5355\u604b\u4e91\u857e\uff0c\u4f46 \u81ea\u77e5\u9053\u4e91\u857e\u5bf9\u5f20\u4e39\u67ab\u7684\u60c5\u610f\u4e4b\u540e\uff0c\u5373\u5df2\u5e38\u5e38\u81ea\u4ef0\uff0c\u5230\u4e86\u6fb9\u53f0\u706d\u660e\u6697\u52a9\u4ed6\u4eec\u6253\u80dc\u4ed7\uff0c\u8bf4\u660e\u4e86\u5f20 \u4e39\u67ab\u4e3a\u56fd\u7684\u82e6\u5fc3\u4e4b\u540e\uff0c\u5468\u5c71\u6c11\u66f4\u662f\u4e0b\u4e86\u51b3\u5fc3\u9000\u51fa\u8fd9\u4e00\u573a\u65e0\u671b\u7684\u60c5\u573a\u89d2\u9010\uff0c\u6240\u4ee5\u6b64\u65f6\u867d\u7136\u5fc3\u4e2d \u4e00\u52a8\uff0c\u4f46\u8fc5\u5373\u53c8\u538b\u6291\u4e0b\u6765\u3002 \u3000\u3000\u77f3\u82f1\u9053\uff1a\u201c\u5468\u8d24\u4f84\uff0c\u4f60\u600e\u7684\u77e5\u9053\u6211\u4eec\u88ab\u56f0\u6b64\u5c71\uff0c\u5f15\u5175\u6765\u6551\uff1f\u201d\u8fd9\u4e00\u95ee\u4e5f\u6b63\u662f\u4f17\u4eba\u5fc3\u4e2d\u7684 \u7591\u95ee\uff0c\u4e0d\u7ea6\u800c\u540c\u5730\u5927\u5bb6\u90fd\u770b\u7740\u5468\u5c71\u6c11\u3002\u53ea\u542c\u5f97\u5468\u5c71\u6c11\u8bf4\u9053\uff1a\u201c\u5728\u74e6\u523a\u5165\u4fb5\u4e4b\u65f6\uff0c\u6211\u4eec\u6d41\u6563\u56db \u65b9\uff0c\u73b0\u4e0b\u6218\u4e8b\u5df2\u7ecf\u7ed3\u675f\uff0c\u6211\u4eec\u91cd\u65b0\u96c6\u7ed3\uff0c\u60f3\u56de\u5230\u65e7\u65e5\u7684\u57fa\u5730\uff0c\u6628\u65e5\u884c\u519b\u81f3\u9644\u8fd1\u624e\u8425\uff0c\u665a\u4e0a\u5c31 \u51fa\u4e86\u4e00\u6869\u602a\u4e8b\u3002\u201d\u77f3\u82f1\u9053\uff1a\u201c\u4ec0\u4e48\u602a\u4e8b\uff1f\u201d\u5468\u5c71\u6c11\u9053\uff1a\u201c\u6709\u4e00\u4e2a\u8499\u9762\u4eba\u591c\u665a\u5077\u5165\u519b\u8425\uff0c\u98de\u5200 \u9012\u7b80\uff0c\u4fe1\u4e0a\u5199\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u8bf4\u4f60\u4eec\u4e2d\u4e86\u6c99\u6d9b\u5708\u5957\uff0c\u88ab\u56f0\u5728\u8fd9\u513f\u3002\u8fd9\u8499\u9762\u4eba\u6b66\u529f\u5353\u7edd\uff0c\u5f85\u6211\u4eec \u53d1\u73b0\u4e4b\u65f6\uff0c\u4ed6\u5df2\u4f3c\u4e00\u7198\u70df\u7684\u8d70\u4e86\u3002\u201d\u5f20\u4e39\u67ab\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u662f\u8499\u9762\u4eba\uff1f\u201d\u5fc3\u4e2d\u5927\u662f\u7591\u60d1\u3002 \u5468\u5c71\u6c11\u9053\uff1a\u201c\u662f\u5440\uff0c\u8fd9\u8499\u9762\u4eba\u6765\u65e0\u8e2a\uff0c\u53bb\u65e0\u8ff9\uff0c\u771f\u4e0d\u77e5\u4ed6\u662f\u4f55\u6765\u5386\uff1f\u5bb6\u7236\u8bf4\uff0c\u5b81\u53ef\u4fe1\u5176\u6709\uff0c \u4e0d\u53ef\u4fe1\u5176\u65e0\uff0c\u65e2\u7136\u77f3\u8001\u82f1\u96c4\u9047\u96be\uff0c\u54b1\u4eec\u4e0d\u80fd\u4e0d\u6551\uff0c\u6545\u6b64\u53eb\u5c0f\u4f84\u9886\u5175\u524d\u6765\u3002\u201d\u5f20\u4e39\u67ab\u4e0e\u4e91\u857e\u90fd \u5728\u6697\u6697\u7eb3\u7f55\uff0c\u4e0d\u77e5\u8fd9\u8499\u9762\u4eba\u662f\u5426\u5373\u90a3\u8499\u9762\u4eba\uff1f \u3000\u3000\u5468\u5c71\u6c11\u53c8\u9053\uff1a\u201c\u5728\u74e6\u523a\u5165\u4fb5\u7684\u65f6\u671f\uff0c\u5bb6\u7236\u66fe\u51e0\u6b21\u6d3e\u4eba\u5230\u77f3\u8001\u4f2f\u7684\u5b9d\u5e84\u63a2\u671b\uff0c\u77f3\u8001\u4f2f\u907f\u96be \u672a\u56de\uff0c\u662f\u4ee5\u65e0\u7531\u81f4\u8baf\u3002\u201d\u77f3\u82f1\u9053\uff1a\u201c\u591a\u8c22\u4f60\u7239\u7239\u7684\u5173\u6000\uff0c\u6539\u65e5\u6211\u518d\u53bb\u95ee\u5019\u3002\u201d\u770b\u5468\u5c71\u6c11\uff0c\u53ea \u89c9\u4ed6\u4e5f\u662f\u4e00\u8868\u4eba\u6750\uff0c\u867d\u7136\u5c1a\u6bd4\u4e0d\u4e0a\u5f20\u4e39\u67ab\u4e0e\u4e91\u857e\uff0c\u4f46\u4ea6\u4e0d\u4fd7\u3002 \u3000\u3000\u4f17\u4eba\u5728\u6c99\u6d9b\u7684\u5c71\u5be8\u4e2d\u5403\u8fc7\u5348\u996d\uff0c\u5f20\u4e39\u67ab\u4e0e\u4e91\u857e\u56e0\u6025\u7740\u8d76\u8def\uff0c\u5148\u884c\u544a\u522b\u3002\u77f3\u82f1\u7236\u5973\u4e0e\u5468\u5c71 \u6c11\u76f4\u9001\u5230\u5c71\u4e0b\uff0c\u5f20\u4e39\u67ab\u4e0e\u4e91\u857e\u64ae\u5507\u4e00\u5578\uff0c\u90a3\u5339\u7167\u591c\u72ee\u5b50\u9a6c\u4e0e\u4e91\u857e\u7684\u5185\u82d1\u5fa1\u9a6c\u5148\u540e\u800c\u81f3\uff0c\u5468\u5c71 \u6c11\u89c1\u4e91\u857e\u8de8\u4e0a\u9a6c\u80cc\uff0c\u5ffd\u7136\u8bb0\u8d77\u4e00\u4e8b\uff0c\u9053\uff1a\u201c\u4e91\u59d1\u5a18\uff0c\u4e14\u6162\u3002\u201d\u4e91\u857e\u5728\u9a6c\u80cc\u4e0a\u56de\u5934\u8bf4\u9053\uff1a\u201c\u5468 \u5927\u54e5\uff0c\u6709\u4f55\u89c1\u6559\uff1f\u201d\u5468\u5c71\u6c11\u9053\uff1a\u201c\u4f60\u548c\u77f3\u59d1\u5a18\u7684\u4e8b\u60c5\u65e2\u7136\u8bf4\u6e05\u695a\u4e86\uff0c\u90a3\u5c31\u4e0d\u5fc5\u6211\u518d\u66ff\u4f60\u591a\u8d39 \u5507\u820c\u5566\u3002\u8fd9\u4e1c\u897f\u4f60\u6536\u56de\u53bb\u3002\u201d\u8bf4\u7f62\uff0c\u5728\u6000\u4e2d\u53d6\u51fa\u4e00\u652f\u78a7\u7389\u73ca\u745a\u3002\u6b63\u662f\uff1a \u3000\u3000\u63a5\u6728\u79fb\u82b1\u8ba1\u5df2\u9042\uff0c\u4f55\u987b\u91cd\u89c5\u78a7\u73ca\u745a\uff1f \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u840d\u8e2a\u4fa0\u5f71\u5f55\u300b\u2014\u2014\u7b2c\u4e8c\u5341\u4e09\u56de\u3000\u5341\u8f7d\u91cd\u6765\u82b3\u5fc3\u4f24\u5f80\u4e8b\u3000\u4e24\u756a\u9080\u6597\u5e73\u5730\u8d77\u7591\u4e91 \u6881\u7fbd\u751f\u300a\u840d\u8e2a\u4fa0\u5f71\u5f55\u300b \u7b2c\u4e8c\u5341\u4e09\u56de\u3000\u5341\u8f7d\u91cd\u6765\u82b3\u5fc3\u4f24\u5f80\u4e8b\u3000\u4e24\u756a\u9080\u6597\u5e73\u5730\u8d77\u7591\u4e91 \u3000\u3000\u8fd9\u652f\u73ca\u745a\u6b63\u662f\u5468\u5065\u9001\u4e0e\u4e91\u857e\uff0c\u800c\u4e91\u857e\u53c8\u62ff\u6765\u9001\u7ed9\u77f3\u7fe0\u51e4\u4f5c\u8058\u793c\u7684\u4fe1\u7269\uff0c\u540e\u6765\u4e91\u857e\u53c8\u5c06\u5b83 \u7559\u4e0e\u5468\u5c71\u6c11\uff0c\u6258\u4ed6\u53bb\u5411\u77f3\u82f1\u8bf4\u660e\u771f\u76f8\uff0c\u4ee5\u4fbf\u9000\u4eb2\u7684\u3002\u5468\u5c71\u6c11\u638f\u51fa\u73ca\u745a\uff0c\u77f3\u7fe0\u51e4\u60f3\u8d77\u66fe\u4e3a\u8fd9\u652f \u73ca\u745a\u6004\u8fc7\u8bb8\u591a\u95f2\u6c14\uff0c\u4e0d\u89c9\u9762\u7ea2\u8fc7\u8033\u3002\u5468\u5c71\u6c11\u638f\u51fa\u73ca\u745a\uff0c\u6b63\u60f3\u9012\u4e0a\u9a6c\u80cc\uff0c\u4e91\u857e\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u8fd9\u73ca\u745a\u672c\u662f\u4f60\u5bb6\u7684\u4e1c\u897f\uff0c\u628a\u6765\u4e0e\u6211\u4f5c\u751a\uff1f\u201d\u8f7b\u8f7b\u4e00\u62cd\uff0c\u9a8f\u9a6c\u5636\u98ce\uff0c\u4e0e\u5f20\u4e39\u67ab\u5e76\u8f94\u5954\u9a70\uff0c\u7247 \u523b\u4e4b\u95f4\uff0c\u5df2\u6ca1\u5165\u9ec4\u6c99\u6f20\u6f20\u4e4b\u4e2d\uff0c\u5269\u4e0b\u5468\u5c71\u6c11\u5446\u5446\u5730\u7ad9\u5728\u5c71\u4e0b\uff0c\u4e0d\u77e5\u6240\u63aa\u3002 \u3000\u3000\u4e24\u4eba\u9a6c\u884c\u8fc5\u901f\uff0c\u7b2c\u4e8c\u65e5\u4e00\u65e9\u5df2\u8fc7\u4e86\u96c1\u95e8\u5173\uff0c\u5173\u5916\u662f\u6c49\u80e1\u63a5\u58e4\u4e4b\u5730\uff0c\u8499\u53e4\u4eba\u4ee5\u6e38\u7267\u4e3a\u4e3b\uff0c \u5973\u5b50\u9a91\u9a6c\uff0c\u6781\u662f\u5e73\u5e38\u3002\u56e0\u6b64\u4e91\u857e\u4e5f\u5c31\u4e0d\u5fc5\u518d\u6539\u7537\u88c5\u3002\u5f20\u4e39\u67ab\u5bf9\u7740\u7389\u4eba\uff0c\u5728\u8349\u539f\u4e4b\u4e0a\u5954\u9a70\uff0c\u5fc3 \u80f8\u66f4\u89c9\u8212\u7545\uff0c\u7b11\u9053\uff1a\u201c\u82e5\u5f97\u4e0e\u4f60\u6d6a\u8ff9\u98ce\u5c18\uff0c\u5c31\u662f\u4e00\u751f\u90fd\u8fd9\u6837\u5954\u6ce2\u6211\u4e5f\u5fc3\u7518\u60c5\u613f\u3002\u201d\u4e91\u857e\u8f7b\u63a0 \u4e91\u9b13\uff0c\u56de\u7738\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u50bb\u54e5\u54e5\u53c8\u8bf4\u50bb\u8bdd\u5566\uff01\u201d\u5f20\u4e39\u67ab\u76ca\u89c9\u5fc3\u65d7\u6447\u6447\uff0c\u4e0d\u53ef\u6291\u6b62\u3002\u98de\u9a6c\u8d70\u8fc7 \u96c1\u95e8\u5173\uff0c\u96c1\u95e8\u5173\u7684\u660e\u671d\u7edf\u5175\u5c1a\u672a\u56de\u6765\uff0c\u6218\u706b\u4e4b\u540e\uff0c\u53ea\u89c1\u4e00\u7247\u9893\u57a3\uff0c\u51e0\u540d\u620d\u5352\u3002\u5f20\u4e39\u67ab\u6b63\u81ea\u611f \u6168\uff0c\u5ffd\u542c\u5f97\u4e91\u857e\u53f9\u4e86\u53e3\u6c14\uff0c\u4e39\u67ab\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u4f60\u600e\u4e48\u5566\uff1f\u201d\u4e91\u857e\u9053\uff1a\u201c\u6211\u60f3\u8d77\u4e86\u5c0f\u65f6\u5019\u968f \u7237\u7237\u56de\u6765\u65f6\u7684\u60c5\u666f\uff0c\u54ce\uff0c\u4e0d\u77e5\u4e0d\u89c9\u5df2\u662f\u5341\u5e74\u4e86\uff01\u5c31\u5728\u8fd9\u513f\uff0c\u6211\u8fd8\u8bb0\u5f97\u90a3\u662f\u5341\u6708\u5341\u4e94\u7684\u665a\u4e0a\uff0c \u6211\u7237\u7237\u5c31\u5728\u8fd9\u513f\u5c06\u8840\u4e66\u4ea4\u4ed8\u4e0e\u6211\u3002\u201d\u63d0\u8d77\u8840\u4e66\uff0c\u5fc3\u4e2d\u4e0d\u89c9\u4e00\u9635\u96be\u8fc7\uff0c\u76f8\u5bf9\u9eef\u7136\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u4eba\u751f\u51e0\u4f55\uff1f\u4f55\u5fc5\u5c3d\u8bb0\u8d77\u90a3\u4e9b\u4e0d\u5feb\u610f\u4e4b\u4e8b\u3002\u201d\u4e24\u4eba\u7b56\u9a6c\u7f13\u884c\u3002\u4e91\u857e\u9053\uff1a\u201c\u4eba \u751f\u771f\u662f\u5947\u602a\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u600e\u4e48\u5947\u602a\uff1f\u201d\u4e91\u857e\u542b\u60c5\u8109\u8109\uff0c\u770b\u4ed6\u4e00\u773c\u6b32\u8bf4\u53c8\u6b62\u3002\u5f20\u4e39\u67ab\uff1a \u201c\u4e16\u4e8b\u53d8\u5e7b\uff0c\u6bcf\u6bcf\u51fa\u4eba\u610f\u5916\uff0c\u6bd4\u5982\u6211\u5427\uff0c\u6211\u672c\u4ee5\u4e3a\u4eca\u751f\u4eca\u4e16\uff0c\u4e0d\u4f1a\u518d\u51fa\u96c1\u95e8\u5173\u7684\u4e86\uff0c\u54ea\u77e5\u800c \u4eca\u53c8\u5230\u6b64\u5730\u3002\u6240\u4ee5\u4f60\u4ee5\u4e3a\u5947\u602a\u7684\u4e8b\u60c5\u4e5f\u672a\u5fc5\u5947\u602a\u3002\u6709\u4e9b\u770b\u6765\u7edd\u4e0d\u53ef\u80fd\u4e4b\u4e8b\uff0c\u8bf4\u4e0d\u5b9a\u5ffd\u7136\u4e4b\u95f4 \u5c31\u987a\u7406\u6210\u7ae0\u5730\u89e3\u51b3\u4e86\u3002\u201d\u8bdd\u4e2d\u542b\u6709\u6df1\u610f\uff0c\u8fd9\u5239\u90a3\u95f4\uff0c\u4e91\u857e\u7684\u5fc3\u5934\u63a0\u8fc7\u4e86\u7237\u7237\u8840\u4e66\u7684\u9634\u5f71\uff0c\u63a0 \u8fc7\u4e86\u54e5\u54e5\u4e25\u5389\u7684\u9762\u5bb9\uff0c\u4e00\u62ac\u5934\u5374\u53c8\u89c1\u7740\u5f20\u4e39\u67ab\u90a3\u50cf\u51ac\u65e5\u9633\u5149\u4e00\u6837\u7684\u6e29\u6696\u7684\u7b11\u5bb9\uff0c\u987f\u89c9\u6ee1\u5929\u9634 \u973e\uff0c\u90fd\u88ab\u626b\u9664\u5e72\u51c0\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u7b56\u9a6c\u508d\u7740\u4e91\u857e\uff0c\u6b63\u60f3\u518d\u6e29\u8a00\u5f00\u89e3\uff0c\u4ed6\u8de8\u4e0b\u7684\u7167\u591c\u72ee\u5b50\u9a6c\u5ffd\u7136\u4e00\u58f0\u957f\u5636\uff0c\u5411\u524d\u75be \u5954\uff0c\u8fd9\u5339\u9a6c\u7adf\u7136\u4e0d\u542c\u4e3b\u4eba\u7684\u63a7\u5236\uff0c\u771f\u662f\u4ece\u6765\u672a\u6709\u4e4b\u4e8b\u3002\u5f20\u4e39\u67ab\u4e00\u63d0\u7ef3\u25a1\uff0c\u5ffd\u53c8\u60f3\u9053\uff1a\u201c\u8fd9\u5339 \u9a6c\u5982\u6b64\u98de\u5954\uff0c\u5fc5\u6709\u7f18\u6545\uff0c\u6211\u4e14\u770b\u5b83\u5c06\u6211\u8f7d\u5230\u54ea\u513f\u3002\u201d\u653e\u677e\u7ef3\u25a1\uff0c\u90a3\u5339\u9a6c\u7adf\u7136\u4e0d\u4f9d\u7740\u6b63\u8def\u800c \u884c\uff0c\u5faa\u7740\u5c71\u8fb9\u7684\u5c0f\u9053\uff0c\u4e0a\u9ad8\u7a9c\u4f4e\uff0c\u4e00\u8def\u5636\u9e23\u4e0d\u5df2\uff0c\u4e91\u857e\u653e\u9a6c\u8ffd\u8d76\uff0c\u603b\u843d\u540e\u534a\u91cc\u4e4b\u9065\u3002\u8dd1\u4e86\u4e00 \u9635\uff0c\u5ffd\u542c\u5f97\u524d\u9762\u4e5f\u6709\u9a6c\u58f0\u5636\u9e23\uff0c\u597d\u50cf\u4e92\u4e3a\u547c\u5e94\u3002\u5f20\u4e39\u67ab\u5411\u524d\u4e00\u671b\uff0c\u53ea\u89c1\u5c71\u5761\u4e4b\u4e0b\uff0c\u6709\u4e24\u4e2a\u4eba \u6b63\u5728\u25a1\u6740\uff0c\u4e00\u5339\u767d\u9a6c\uff0c\u548c\u81ea\u5df1\u7684\u7167\u591c\u72ee\u5b50\u9a6c\u4e00\u6a21\u4e00\u6837\uff0c\u5954\u4e86\u51fa\u6765\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u770b\u6e05\u695a\u65f6\uff0c\u4e0d\u7981\u5927\u5403\u4e00\u60ca\u3002\u539f\u6765\u8fd9\u4e00\u5bf9\u25a1\u6740\u7684\u6c49\u5b50\u5176\u4e2d\u4e00\u4e2a\u6b63\u662f\u81ea\u5df1\u7684\u4e8c\u5e08\u4f2f\u6f6e \u97f3\u548c\u5c1a\uff0c\u5bf9\u624b\u662f\u4e00\u4e2a\u56db\u5341\u591a\u5c81\uff0c\u7565\u663e\u53d1\u80d6\uff0c\u4f46\u8eab\u624b\u5374\u975e\u5e38\u77eb\u6377\u7684\u4e2d\u5e74\u4eba\u3002\u6f6e\u97f3\u548c\u5c1a\u4f7f\u4e00\u6839\u7c97 \u5982\u7897\u53e3\u7684\u7985\u6756\uff0c\u6a2a\u626b\u76f4\u5288\uff0c\u821e\u5f97\u547c\u547c\u98ce\u54cd\uff0c\u6b63\u662f\u4f5b\u95e8\u6700\u5389\u5bb3\u7684\u4f0f\u9b54\u6756\u6cd5\u3002\u90a3\u6c49\u5b50\u5ffd\u638c\u5ffd\u6307\uff0c \u6216\u5288\u6216\u6233\uff0c\u62db\u6570\u8fc5\u6377\u4e4b\u6781\uff0c\u800c\u4e14\u624b\u6cd5\u602a\u5f02\uff0c\u6f6e\u97f3\u548c\u5c1a\u7684\u4f0f\u9b54\u6756\u6cd5\u4f55\u7b49\u51f6\u731b\uff0c\u5374\u6bcf\u6bcf\u88ab\u4ed6\u8f7b\u98d8 \u98d8\u7684\u4e00\u638c\u540e\u5f00\uff0c\u5c31\u5728\u638c\u98ce\u6756\u5f71\u4e4b\u4e2d\uff0c\u6b3a\u8eab\u75be\u8fdb\uff0c\u51fa\u6307\u70b9\u6f6e\u97f3\u548c\u5c1a\u7684\u7a74\u9053\uff0c\u6bcf\u6b21\u51fa\u6307\uff0c\u6f6e\u97f3\u548c \u5c1a\u867d\u80fd\u907f\u5f00\uff0c\u4e5f\u4e0d\u514d\u673a\u4f36\u4f36\u5730\u6253\u4e2a\u51b7\u6218\u3002\u5f20\u4e39\u67ab\u5fc3\u4e2d\u4e00\u6014\uff1a\u8fd9\u6c49\u5b50\u7684\u638c\u6cd5\u6307\u6cd5\u548c\u65e5\u524d\u6240\u89c1\u7684 \u90a3\u4e2a\u8499\u9762\u4eba\u7adf\u662f\u4e00\u6a21\u4e00\u6837\uff0c\u6240\u4f7f\u7684\u90fd\u662f\u6700\u4e0a\u4e58\u7684\u94c1\u7435\u7436\u638c\u548c\u4e00\u6307\u7985\u7684\u529f\u592b\uff01 \u3000\u3000\u5c71\u5761\u4e0b\u8fd8\u6709\u4e00\u4e2a\u5973\u5b50\u5fae\u7b11\u89c2\u6218\uff0c\u8fd9\u5973\u5b50\u5e74\u7ea6\u4e09\u5341\u591a\u5c81\uff0c\u9762\u5982\u6ee1\u6708\uff0c\u59ff\u5bb9\u7aef\u6b63\uff0c\u4f3c\u662f\u4e00\u4e2a \u5927\u5bb6\u5c11\u5987\uff0c\u5176\u5b9e\u5374\u662f\u4e2a\u672a\u66fe\u51fa\u5ac1\u7684\u8001\u59d1\u5a18\u3002\u5979\u4e00\u9762\u770b\u4e00\u9762\u53d1\u7b11\u3002\u6f6e\u97f3\u548c\u5c1a\u8eab\u8eaf\u9b41\u68a7\uff0c\u624b\u6325\u7985 \u6756\u7adf\u88ab\u90a3\u4e2a\u6c49\u5b50\u4e00\u53cc\u8089\u638c\u8feb\u5f97\u624b\u5fd9\u811a\u4e71\uff0c\u6f6e\u97f3\u548c\u5c1a\u4f3c\u662f\u751a\u4e3a\u607c\u6012\u731b\u7684\u4e00\u62db\u201c\u72ec\u5288\u534e\u5c71\u201d\uff0c\u4e3e \u7985\u4ed7\u5f53\u5934\u5288\u4e0b\uff0c\u90a3\u6c49\u5b50\u4e00\u95ea\u95ea\u5f00\uff0c\u6f6e\u97f3\u548c\u5c1a\u53bb\u52bf\u592a\u731b\uff0c\u6536\u52bf\u4e0d\u53ca\uff0c\u4e00\u6756\u6253\u4e0b\uff0c\u7838\u5230\u5730\u4e0a\uff0c\u6253 \u5f97\u6c99\u77f3\u7eb7\u98de\u3002\u90a3\u6c49\u5b50\u54c8\u54c8\u4e00\u7b11\uff0c\u51fa\u6307\u5982\u7535\uff0c\u5411\u6f6e\u97f3\u80c1\u4e0b\u4e00\u6233\uff0c\u6f6e\u97f3\u548c\u5c1a\u6b66\u529f\u4e5f\u7b97\u9ad8\u5f3a\uff0c\u5728\u6b64 \u7edd\u9669\u4e4b\u9645\uff0c\u7adf\u7136\u4ee5\u7985\u6756\u652f\u5730\uff0c\u4e00\u4e2a\u7b4b\u6597\u5012\u7ffb\u8d77\u6765\uff0c\u867d\u7136\u907f\u5f00\u4e86\u654c\u4eba\u7684\u4e00\u8bb0\u6740\u624b\uff0c\u4f46\u4ea6\u5df2\u663e\u5f97 \u72fc\u72c8\u5f02\u5e38\uff01\u90a3\u4e2d\u5e74\u5973\u5b50\u5ffd\u5730\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u7384\u673a\u9038\u58eb\u95e8\u4e0b\uff0c\u4ea6\u4e0d\u8fc7\u5982\u6b64\u800c\u5df2\uff0c\u54c8\u54c8\uff0c\u771f\u662f \u6d6a\u5f97\u865a\u540d\u3002\u201d \u3000\u3000\u5f20\u4e39\u67ab\u7709\u5934\u4e00\u76b1\uff0c\u4fbf\u6b32\u4e0a\u524d\uff0c\u5ffd\u5730\u60f3\u9053\uff1a\u201c\u8fd9\u6c49\u5b50\u5206\u660e\u5c31\u662f\u90a3\u8499\u9762\u4eba\uff0c\u4ed6\u548c\u4e5f\u5148\u7684\u6b66\u58eb \u540c\u884c\u5230\u6c99\u6d9b\u5c71\u5be8\uff0c\u540e\u6765\u5374\u53c8\u5f15\u4e86\u5468\u5c71\u6c11\u524d\u6765\u76f8\u6551\uff0c\u771f\u4ee4\u4eba\u731c\u4e0d\u900f\u4ed6\u7684\u6765\u5386\u3002\u4e0d\u77e5\u4ed6\u4f55\u4ee5\u5374\u8981 \u4e0e\u6211\u7684\u4e8c\u5e08\u4f2f\u4e3a\u96be\uff1f\u201d\u56de\u5934\u4e00\u770b\uff0c\u4e91\u857e\u7684\u5feb\u9a6c\u5df2\u5982\u98de\u800c\u6765\uff0c\u5c1a\u5dee\u534a\u91cc\u672a\u5230\u3002\u81ea\u5df1\u7684\u90a3\u5339\u7167\u591c \u72ee\u5b50\u9a6c\u5219\u548c\u6f6e\u97f3\u548c\u5c1a\u7684\u90a3\u5339\u767d\u9a6c\u5728\u4e00\u5904\u25a1\u78e8\u6328\u64e6\uff0c\u4e92\u76f8\u5b09\u620f\u3002\u539f\u6765\u6f6e\u97f3\u548c\u5c1a\u8fd9\u5339\u767d\u9a6c\u4e43\u662f\u5f20 \u5b97\u5468\u7684\u5750\u9a91\uff0c\u6f6e\u97f3\u548c\u5c1a\u4e0a\u6b21\u5230\u74e6\u523a\u591c\u63a2\u5f20\u5e9c\u4e4b\u65f6\uff0c\u8c22\u5929\u534e\u6697\u52a9\u4ed6\u8131\u9669\uff0c\u5077\u9001\u4e0e\u4ed6\u7684\u3002\u8fd9\u5339\u9a6c \u548c\u5f20\u4e39\u67ab\u7684\u7167\u591c\u72ee\u5b50\u9a6c\u4e43\u662f\u6bcd\u5b50\uff0c\u6545\u6b64\u5f20\u4e39\u67ab\u7684\u9a6c\u8fdc\u8fdc\u542c\u89c1\u5b83\u7684\u5636\u58f0\uff0c\u5c31\u4e0d\u542c\u4e3b\u4eba\u7684\u63a7\u5236\uff0c \u5954\u6765\u76f8\u89c1\u3002 \u3000\u3000\u7247\u523b\u4e4b\u95f4\uff0c\u4e91\u857e\u5df2\u7ecf\u6765\u5230\uff0c\u5411\u6218\u573a\u4e00\u671b\uff0c\u5931\u58f0\u53eb\u9053\uff1a\u201c\u90a3\u4e0d\u662f\u6f6e\u97f3\u5e08\u4f2f\u5417\uff1f\u6f6e\u97f3\u5e08 \u4f2f\uff01\u201d\u6f6e\u97f3\u548c\u5c1a\u6597\u5f97\u6b63\u7d27\uff0c\u88ab\u90a3\u6c49\u5b50\u8feb\u5f97\u900f\u4e0d\u8fc7\u6c14\u6765\uff0c\u7adf\u4e0d\u80fd\u5206\u5fc3\u56de\u987e\uff0c\u542c\u4e86\u4e91\u857e\u7684\u53eb\u58f0\uff0c \u4e5f\u4e0d\u80fd\u56de\u7b54\u3002\u90a3\u6c49\u5b50\u5374\u51b2\u7740\u5f20\u3001\u4e91\u4e8c\u4eba\u9f87\u7259\u54a7\u5634\u5730\u7b11\u4e86\u4e00\u7b11\u9053\uff1a\u201c\u771f\u662f\u4eba\u751f\u65e0\u5904\u4e0d\u9022\u541b\uff0c\u53c8 \u89c1\u7740\u4f60\u4eec\u4e86\uff0c\u8fd9\u4e2a\u7cdf\u548c\u5c1a\u7adf\u662f\u4f60\u4eec\u7684\u5e08\u4f2f\u5417\uff1f\u201d\u6f6e\u97f3\u5927\u6012\uff0c\u6325\u7985\u6756\u6cfc\u98ce\u75be\u626b\uff0c\u65e0\u5948\u654c\u624b\u592a \u5f3a\uff0c\u6f6e\u97f3\u548c\u5c1a\u529b\u4e0d\u4ece\u5fc3\uff0c\u53cd\u800c\u7ed9\u4ed6\u5728\u80a9\u5934\u4e00\u637a\uff0c\u811a\u6b65\u8e09\u8dc4\uff0c\u6447\u6447\u6b32\u5012\uff01 \u3000\u3000\u7384\u673a\u9038\u58eb\u95e8\u4e0b\u7684\u56db\u5927\u5f1f\u5b50\uff0c\u4ee5\u8c22\u5929\u534e\u6b66\u529f\u6700\u5f3a\uff0c\u4e91\u857e\u7684\u5e08\u7236\u98de\u5929\u9f99\u5973\u53f6\u76c8\u76c8\u5728\u9762\u58c1\u5341\u4e8c \u5e74\u4e4b\u540e\uff0c\u6b66\u529f\u5927\u8fdb\uff0c\u4e5f\u4e0d\u5728\u8c22\u5929\u534e\u4e4b\u4e0b\uff0c\u5927\u5f1f\u5b50\u91d1\u521a\u624b\u8463\u5cb3\u6b66\u5b66\u7684\u9020\u8be3\u4e0d\u53ca\u8c22\u5929\u534e\u548c\u53f6\u76c8 \u76c8\uff0c\u4f46\u5916\u5bb6\u529f\u592b\u767b\u5cf0\u9020\u6781\uff0c\u91d1\u521a\u624b\u5929\u4e0b\u65e0\u53cc\uff0c\u5185\u5bb6\u7684\u529f\u592b\u4ea6\u6709\u76f8\u5f53\u9020\u8be3\uff0c\u6240\u4ee5\u53ea\u8bba\u529f\u529b\uff0c\u5219 \u8fd8\u8981\u6570\u4ed6\u3002\u81f3\u4e8e\u6f6e\u97f3\u548c\u5c1a\uff0c\u5219\u56e0\u4ed6\u6027\u5b50\u66b4\u8e81\uff0c\u7ec3\u4e0d\u4e86\u6700\u4e0a\u4e58\u7684\u6b66\u529f\uff0c\u53ea\u5f97\u4e86\u5e08\u7236\u7684\u4e00\u5957\u4f0f\u9b54 \u4ed7\u6cd5\u548c\u5916\u5bb6\u786c\u529f\u3002\u867d\u7136\u53ea\u6b64\u4e00\u5957\u6756\u6cd5\uff0c\u5df2\u662f\u53d7\u7528\u4e0d\u5c3d\uff0c\u5728\u6c5f\u6e56\u4e0a\u7f55\u9022\u5bf9\u624b\uff0c\u4f46\u4e00\u65e6\u9047\u5230\u4e86\u50cf \u8fd9\u6c49\u5b50\u90a3\u6837\u9876\u513f\u5c16\u513f\u7684\u4eba\u7269\uff0c\u53ef\u5c31\u4e0d\u514d\u76f8\u5f62\u89c1\u7ecc\uff0c\u5904\u5904\u53d7\u5236\u4e8e\u4eba\uff0c\u8fd9\u65f6\u7ed9\u4ed6\u4e00\u637a\uff0c\u7adf\u662f\u6447\u6447 \u6b32\u5012\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u53eb\u9053\uff1a\u201c\u4e8c\u5e08\u4f2f\uff0c\u4f60\u4e14\u6b47\u4e00\u4f1a\u513f\u3002\u6709\u4e8b\u5c0f\u8f88\u670d\u5176\u52b3\u6211\u66ff\u4f60\u63a5\u51e0\u62db\u5427\uff01\u201d\u62d4\u5251\u51fa \u9798\uff0c\u5411\u7740\u90a3\u6c49\u5b50\u9053\uff1a\u201c\u524d\u8f88\u8bf7\u6307\u6559\u6211\u4eec\u662f\u7384\u673a\u9038\u58eb\u95e8\u4e0b\u7b2c\u4e09\u4ee3\u5f1f\u5b50\uff0c\u5c0f\u8f88\u8bf7\u524d\u8f88\u8d50\u62db\uff0c\u4e0d\u6562 \u5355\u72ec\u5e73\u6597\uff0c\u8bf7\u6055\u6211\u4eec\u65e0\u793c\uff0c\u4e00\u9f50\u4e0a\u4e86\u3002\u201d\u957f\u5251\u4e00\u6325\uff0c\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\u4f60\u4e5f\u6765\u5411\u524d\u8f88\u8ba8\u6559\u4e24\u62db \u5427\uff01\u201d\u4e91\u857e\u5e94\u58f0\u51fa\u5251\uff0c\u53cc\u5251\u4e00\u5408\uff0c\u987f\u65f6\u98de\u8d77\u4e24\u9053\u94f6\u8679\uff0c\u4ea4\u53c9\u4e00\u526a\uff0c\u90a3\u6c49\u5b50\u5411\u5f20\u4e39\u67ab\u62cd\u4e00\u638c\uff0c \u5411\u4e91\u857e\u6233\u4e00\u6307\uff0c\u5206\u7528\u94c1\u7435\u7436\u4e0e\u4e00\u6307\u7985\u7684\u529f\u592b\u5bf9\u4ed8\u4e24\u4eba\u3002\u53cc\u5251\u5408\u74a7\uff0c\u4f55\u7b49\u5389\u5bb3\uff0c\u6709\u5982\u957f\u6c5f\u6d6a \u6d8c\uff0c\u5927\u6d77\u6f6e\u751f\uff0c\u4e00\u62db\u7d27\u8fc7\u4e00\u62db\uff0c\u66f4\u52a0\u4e0a\u5f20\u4e39\u67ab\u7684\u6b66\u529f\uff0c\u5728\u6bd5\u5bb6\u76f8\u6597\u4e4b\u65f6\uff0c\u5df2\u80fd\u548c\u6f6e\u97f3\u548c\u5c1a\u6253 \u4e2a\u5e73\u624b\uff0c\u5f97\u4e86\u300a\u7384\u529f\u8981\u8bc0\u300b\u4e4b\u540e\uff0c\u6b66\u529f\u7cbe\u8fdb\uff0c\u66f4\u5728\u6f6e\u97f3\u548c\u5c1a\u4e4b\u4e0a\u3002\u6240\u4ee5\u53cc\u5251\u5408\u74a7\uff0c\u5341\u62db\u4e00 \u8fc7\uff0c\u7acb\u523b\u628a\u90a3\u4eba\u8feb\u5f97\u53ea\u6709\u62db\u67b6\u4e4b\u529f\uff0c\u6beb\u65e0\u8fd8\u624b\u4e4b\u529b\u3002\u90a3\u4eba\u9053\uff1a\u201c\u53cc\u5251\u5408\u74a7\uff0c\u5a01\u529b\u679c\u662f\u4e0d\u51e1\uff0c \u5e08\u59b9\uff0c\u4f60\u4e5f\u6765\u89c1\u8bc6\u89c1\u8bc6\u3002\u201d\u90a3\u4e2d\u5e74\u5973\u5b50\u5e94\u4e86\u4e00\u58f0\uff0c\u4e5f\u4e0d\u89c1\u5979\u600e\u6837\u4f5c\u52bf\uff0c\u6643\u773c\u4e4b\u95f4\u5c31\u5230\u4e86\u9762 \u524d\uff0c\u53ea\u89c1\u5979\u55d6\u55d6\u4e24\u58f0\uff0c\u62d4\u51fa\u4e24\u822c\u5175\u5668\uff0c\u5de6\u624b\u662f\u4e00\u67c4\u91d1\u94a9\uff0c\u53f3\u624b\u662f\u4e00\u67c4\u94f6\u5149\u95ea\u95ea\u7684\u957f\u5251\uff0c\u957f\u5251 \u4e00\u6307\uff0c\u91d1\u94a9\u4e00\u62c9\uff0c\u5f20\u4e39\u67ab\u4e0e\u4e91\u857e\u4e8c\u4eba\u90fd\u4e0d\u7531\u5f97\u9000\u4e86\u4e09\u6b65\uff0c\u5f20\u4e39\u67ab\u5251\u52bf\u5de6\u5c55\uff0c\u4e91\u857e\u5251\u52bf\u53f3\u5c55\uff0c \u5408\u6210\u4e86\u4e00\u9053\u5706\u5f27\uff0c\u5c06\u8fd9\u5bf9\u7537\u5973\u4e5f\u8feb\u51fa\u4e86\u5251\u5149\u5708\u5916\u3002 \u3000\u3000\u90a3\u5973\u5b50\u597d\u4e0d\u5389\u5bb3\uff0c\u5de6\u94a9\u53f3\u5251\uff0c\u7adf\u7136\u4e00\u9000\u5373\u8fdb\uff0c\u4e24\u624b\u4e0d\u540c\u7684\u5175\u5668\u5728\u4e00\u77ac\u4e4b\u95f4\u90fd\u8fde\u8fdb\u4e09\u62db\u3002 \u90a3\u6c49\u5b50\u5ffd\u800c\u7528\u7435\u7436\u638c\uff0c\u5ffd\u800c\u7528\u4e00\u6307\u7985\uff0c\u653b\u52bf\u4e5f\u9aa4\u7136\u8f6c\u76db\uff0c\u5f20\u4e39\u67ab\u6321\u4e86\u4e24\u62db\uff0c\u4e00\u62db\u201c\u98de\u9f99\u5728 \u5929\u201d\u914d\u5408\u7740\u4e91\u857e\u7684\u201c\u6f5c\u9f99\u5165\u5730\u201d\u53cc\u5251\u4e00\u4e0a\u4e00\u4e0b\uff0c\u6321\u4f4f\u4e86\u654c\u4eba\u7684\u94a9\u3001\u5251\u3001\u638c\u3001\u6307\u56db\u79cd\u4e0d\u540c\u7684\u653b \u52bf\u3002\u90a3\u5973\u5b50\u4e5f\u4e0d\u7531\u5f97\u8f7b\u542f\u6731\u5507\uff0c\u8d5e\u4e86\u4e00\u4e2a\u201c\u597d\u201d\u5b57\u3002\u5f20\u4e39\u67ab\u5ffd\u9053\uff1a\u201c\u8bf7\u95ee\u4e24\u4f4d\u548c\u6fb9\u53f0\u706d\u660e\u662f \u600e\u4e48\u4e2a\u79f0\u547c\uff1f\u201d \u3000\u3000\u539f\u6765\u4e0d\u4f46\u90a3\u6c49\u5b50\u7684\u94c1\u7435\u7436\u638c\u6cd5\u548c\u6fb9\u53f0\u706d\u660e\u76f8\u540c\uff0c\u5373\u8fd9\u5973\u5b50\u7684\u91d1\u94a9\u8def\u6570\uff0c\u4e5f\u548c\u6fb9\u53f0\u706d\u660e\u7684 \u5434\u94a9\u5251\u6cd5\u4e00\u6a21\u4e00\u6837\u3002\u53ea\u662f\u6fb9\u53f0\u706d\u660e\u4f7f\u7684\u5175\u5668\u662f\u53cc\u94a9\uff0c\u800c\u8fd9\u5973\u5b50\u5219\u9664\u4e86\u91d1\u94a9\u4e4b\u5916\u8fd8\u591a\u4e00\u67c4\u957f \u5251\uff0c\u6240\u4ee5\u62db\u6570\u66f4\u89c1\u602a\u5f02\u3002 \u3000\u3000\u90a3\u5973\u5b50\u6014\u4e86\u4e00\u6014\uff0c\u5ffd\u800c\u7b11\u9053\uff1a\u201c\u6211\u4eec\u53ea\u60f3\u89c1\u8bc6\u7384\u673a\u9038\u58eb\u72ec\u521b\u7684\u6b66\u529f\uff0c\u8c01\u8010\u70e6\u542c\u4f60\u5bfb\u6839\u7a76 \u5e95\uff1f\u201d\u5de6\u624b\u4e00\u8d77\uff0c\u91d1\u5149\u4e00\u95ea\uff0c\u53c8\u662f\u4e00\u94a9\u94a9\u6765\u3002\u5f20\u4e39\u67ab\u78b0\u4e86\u4e00\u4e2a\u9489\u5b50\u5fc3\u4e2d\u4e5f\u81ea\u6709\u70b9\u751f\u6c14\uff0c\u6697 \u9053\uff1a\u201c\u597d\uff0c\u6211\u5c31\u8ba9\u4f60\u4eec\u89c1\u8bc6\u89c1\u8bc6\u6211\u5e08\u7956\u7684\u72ec\u521b\u6b66\u529f\uff01\u201d\u5251\u52bf\u8d8a\u53d1\u50ac\u7d27\uff0c\u53cc\u5251\u5ffd\u5206\u5ffd\u5408\uff0c\u6709\u5982 \u53cc\u9f99\u620f\u6c34\uff0c\u5251\u52bf\u5982\u8679\uff0c\u53d8\u5316\u5947\u5e7b\uff0c\u987f\u65f6\u5c06\u90a3\u5bf9\u7537\u5973\u88f9\u5728\u5251\u5149\u4e4b\u4e2d\u3002 \u3000\u3000\u4f46\u8fd9\u5bf9\u654c\u4eba\u7684\u6b66\u529f\u59d4\u5b9e\u592a\u5f3a\uff0c\u8868\u9762\u770b\u6765\uff0c\u867d\u4f3c\u88ab\u53cc\u5251\u6240\u56f0\u65e0\u80fd\u4e3a\u529b\uff0c\u5176\u5b9e\u5374\u662f\u6697\u65bd\u5999 \u624b\uff0c\u7740\u7740\u53cd\u51fb\u3002\u7247\u523b\u4e4b\u95f4\uff0c\u53c8\u6597\u4e86\u4e94\u4e03\u5341\u62db\uff0c\u5f20\u4e39\u67ab\u4e5f\u8fd8\u7f62\u4e86\uff0c\u4e91\u857e\u6839\u57fa\u7a0d\u5dee\uff0c\u5185\u529f\u8f83\u5f31\uff0c \u88ab\u4ed6\u4eec\u7684\u6f5c\u529b\u53cd\u51fb\uff0c\u80f8\u53e3\u5982\u53d7\u91cd\u538b\uff0c\u7adf\u547c\u5438\u7d27\u5f20\uff0c\u6e10\u611f\u4e0d\u652f\u3002\u5f20\u4e39\u67ab\u4e0d\u7531\u5f97\u5012\u5438\u4e86\u4e00\u53e3\u51c9 \u6c14\uff0c\u5fc3\u9053\uff1a\u201c\u679c\u7136\u662f\u5929\u5916\u6709\u5929\uff0c\u4eba\u5916\u6709\u4eba\u3002\u6211\u53ea\u9053\u53cc\u5251\u5408\u74a7\uff0c\u5929\u4e0b\u65e0\u654c\uff0c\u54ea\u77e5\u8fd8\u662f\u7ed9\u8fd9\u5bf9\u7537 \u5973\uff0c\u5360\u4e86\u4e0a\u98ce\u3002\u201d\u5176\u5b9e\u4e0d\u662f\u53cc\u5251\u7684\u5a01\u529b\u4e0d\u5f3a\uff0c\u800c\u662f\u4e91\u857e\u7684\u529f\u529b\u4e0e\u654c\u4eba\u76f8\u53bb\u751a\u8fdc\uff0c\u6240\u4ee5\u5c1a\u4e0d\u80fd \u5c06\u53cc\u5251\u4e4b\u5a01\uff0c\u53d1\u6325\u5f97\u6dcb\u6f13\u5c3d\u81f4\u3002 \u3000\u3000\u6f6e\u97f3\u548c\u5c1a\u6b47\u606f\u4e86\u4e00\u4f1a\uff0c\u89c1\u5f20\u4e39\u67ab\u4e0e\u4e91\u857e\u6218\u654c\u4eba\u4e0d\u4e0b\uff0c\u4e00\u6325\u7985\u6756\uff0c\u53c8\u52a0\u5165\u6218\u56e2\uff0c\u6f6e\u97f3\u548c\u5c1a \u7684\u529f\u529b\u5728\u5f20\u4e39\u67ab\u4e4b\u4e0b\uff0c\u5374\u5728\u4e91\u857e\u4e4b\u4e0a\uff0c\u5f20\u3001\u4e91\u4e8c\u4eba\u53cc\u5251\u5408\u74a7\uff0c\u4e0e\u654c\u4eba\u672c\u662f\u76f8\u5dee\u65e0\u51e0\uff0c\u6f6e\u97f3\u548c \u5c1a\u4e00\u52a0\u5165\u6765\uff0c\u4ee5\u4e09\u654c\u4e8c\uff0c\u6e10\u6e10\u62c9\u6210\u5e73\u624b\u3002 \u3000\u3000\u53c8\u6fc0\u6597\u4e86\u4e09\u4e94\u5341\u62db\uff0c\u4ecd\u662f\u4e0d\u5206\u9ad8\u4e0b\uff0c\u5ffd\u542c\u5f97\u9a6c\u8e44\u5f97\u5f97\uff0c\u8fdc\u8fdc\u4f20\u6765\uff0c\u7247\u523b\u4e4b\u540e\uff0c\u53ea\u89c1\u4e00\u4eba \u7b56\u9a6c\u800c\u6765\uff0c\u8170\u60ac\u957f\u5251\u610f\u6001\u6f47\u6d12\uff0c\u77a5\u4e86\u4e00\u773c\uff0c\u5ffd\u5730\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u8fde\u6211\u7684\u5f92\u5f1f\u90fd\u6218\u4e0d\u4e0b\uff0c\u8fd8\u66ff\u4e0a \u5b98\u8001\u602a\u6491\u4ec0\u4e48\u95e8\u9762\uff1f\u201d\u5f20\u4e39\u67ab\u5927\u559c\u53eb\u9053\uff1a\u201c\u5e08\u7236\uff01\u201d\u539f\u6765\u6765\u7684\u4eba\u6b63\u662f\u8c22\u5929\u534e\uff01 \u3000\u3000\u8c22\u5929\u534e\u9053\uff1a\u201c\u6f6e\u97f3\u5e08\u5144\uff0c\u4f60\u4e14\u6b47\u4e00\u4f1a\u513f\uff0c\u5f85\u6211\u89c1\u8bc6\u89c1\u8bc6\u4e0a\u5b98\u8001\u602a\u95e8\u4e0b\u7684\u6b66\u529f\u3002\u91d1\u94a9\u4ed9 \u5b50\uff0c\u6211\u5148\u5411\u4f60\u8bf7\u6559\uff0c\u4e4c\u8001\u4e8c\uff0c\u4f60\u518d\u4e0e\u6211\u7684\u5f92\u513f\u591a\u6253\u4e00\u4f1a\u5427\u3002\u201d\u539f\u6765\u8fd9\u5bf9\u7537\u5973\uff0c\u7537\u7684\u53eb\u505a\u4e4c\u8499 \u592b\uff0c\u672c\u662f\u4e0a\u5b98\u5929\u91ce\u7684\u4e8c\u5f1f\u5b50\uff0c\u4e0a\u5b98\u5929\u91ce\u6614\u65e5\u66fe\u4e0e\u7384\u673a\u9038\u58eb\u4e89\u96c4\uff0c\u5267\u6597\u4e09\u65e5\u4e09\u591c\u4e0d\u5206\u80dc\u8d1f\u3002\u4ed6 \u6709\u51e0\u79cd\u6781\u5389\u5bb3\u7684\u529f\u592b\uff0c\u4e00\u6307\u7985\u5c31\u662f\u5176\u4e2d\u4e4b\u4e00\u3002\u53ef\u662f\u4ed6\u7684\u529f\u592b\u751a\u602a\uff0c\u5176\u4e2d\u7684\u4e00\u6307\u7985\u4e0e\u53e6\u4e00\u79cd\u529f \u592b\u5fc5\u987b\u7ae5\u7537\u7ae5\u5973\u624d\u80fd\u4fee\u7ec3\uff0c\u800c\u4e14\u5373\u7b97\u5728\u70bc\u6210\u4e4b\u540e\uff0c\u82e5\u4e00\u7ed3\u5a5a\uff0c\u529f\u529b\u5c31\u8981\u5927\u51cf\u3002\u6240\u4ee5\u4e0a\u5b98\u5929\u91ce \u5728\u6536\u5f92\u4e4b\u540e\uff0c\u5fc5\u5148\u95ee\u660e\u5f92\u5f1f\u6b64\u751f\u7ed3\u4e0d\u7ed3\u5a5a\uff0c\u82e5\u7518\u613f\u4e0d\u7ed3\u5a5a\u7684\u624d\u4f20\u4ee5\u4e00\u6307\u7985\u529f\uff0c\u5927\u5f1f\u5b50\u6fb9\u53f0\u706d \u660e\u56e0\u81ea\u5df1\u8fd9\u4e00\u652f\u4eba\u8fdc\u8d70\u5f02\u56fd\uff0c\u4e0d\u613f\u7edd\u540e\uff0c\u6240\u4ee5\u6ca1\u6709\u7b54\u5e94\uff0c\u56e0\u800c\u4e5f\u5c31\u53ea\u5f97\u4e86\u5434\u94a9\u5251\u6cd5\u548c\u5176\u4ed6\u7684 \u5185\u5916\u529f\u592b\uff0c\u4e00\u6307\u7985\u529f\u5374\u6ca1\u6709\u5b66\u5230\u3002\u4e8c\u5f1f\u5b50\u4e4c\u8499\u592b\u8d2a\u5f97\u4e0a\u4e58\u529f\u592b\uff0c\u4e00\u5165\u95e8\u5c31\u7b54\u5e94\u6b64\u751f\u8a93\u4e0d\u7ed3 \u5a5a\u3002\u90a3\u5973\u5b50\u53eb\u505a\u6797\u4ed9\u97f5\uff0c\u5916\u53f7\u91d1\u94a9\u4ed9\u5b50\uff0c\u662f\u4e0a\u5b98\u5929\u91ce\u7684\u4e09\u5f1f\u5b50\uff0c\u4e5f\u662f\u4e00\u5165\u95e8\u5c31\u7b54\u5e94\u4e0d\u7ed3\u5a5a\u3002 \u6797\u4ed9\u97f5\u5341\u4f59\u5e74\u524d\uff0c\u7f8e\u8273\u975e\u5e38\uff0c\u4e4c\u8499\u592b\u4e0e\u5979\u540c\u95e8\u4e60\u6280\uff0c\u65e5\u4e45\u751f\u60c5\u612b\uff0c\u6797\u4ed9\u97f5\u662f\u4e2a\u5973\u5b50\uff0c\u8f83\u4e3a\u6c89 \u9759\uff0c\u6ca1\u6709\u8868\u9732\u51fa\u6765\uff0c\u4e4c\u8499\u592b\u5374\u662f\u5927\u80c6\u8ffd\u6c42\uff0c\u6709\u8bb8\u591a\u75d5\u8ff9\u843d\u5728\u4e0a\u5b98\u5929\u91ce\u7684\u773c\u91cc\u3002 \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u672c\u610f\u8981\u8c03\u6559\u51fa\u51e0\u4e2a\u51fa\u8272\u7684\u5f1f\u5b50\uff0c\u518d\u4e0e\u7384\u673a\u9038\u58eb\u4e00\u51b3\u96cc\u96c4\uff0c\u4ed6\u53c8\u6700\u4e0d\u6b22\u559c\u522b\u4eba\u8a00 \u800c\u65e0\u4fe1\uff0c\u4e00\u53d1\u73b0\u4e86\u4e8c\u5f1f\u5b50\u4e4c\u8499\u592b\u5bf9\u6797\u4ed9\u97f5\u6000\u6709\u5f02\u5fc3\uff0c\u4e0d\u7981\u52c3\u7136\u5927\u6012\uff0c\u4e00\u6c14\u4e4b\u4e0b\uff0c\u7adf\u5c06\u4ed6\u8d76\u51fa \u95e8\u5899\uff0c\u6240\u4ee5\u6fb9\u53f0\u706d\u660e\u5bf9\u522b\u4eba\u8bf4\u8d77\uff0c\u5c31\u53ea\u662f\u8bf4\u81ea\u5df1\u53ea\u6709\u4e00\u4e2a\u5e08\u59b9\uff0c\u800c\u6ca1\u6709\u63d0\u53ca\u4e4c\u8499\u592b\u4e86\u3002 \u3000\u3000\u4e4c\u8499\u592b\u88ab\u9010\u51fa\u5e08\u95e8\u4e4b\u540e\uff0c\u4e00\u65b9\u9762\u662f\u5bf9\u5e08\u95e8\u4ecd\u751a\u4f9d\u604b\uff0c\u4e00\u65b9\u9762\u4e5f\u662f\u60b2\u6124\u83ab\u540d\uff0c\u5fc3\u4e2d\u81ea\u601d\uff1a \u5929\u4e0b\u96be\u9053\u5c31\u6ca1\u6709\u4e00\u79cd\u66f4\u4e0a\u4e58\u7684\u6b66\u529f\uff0c\u53ef\u4ee5\u592b\u5987\u53cc\u4fee\u7684\u4e48\uff1f\u5e08\u7236\u7684\u4e00\u6307\u7985\u529f\uff0c\u7ed3\u5a5a\u4e4b\u540e\u5c31\u4f1a\u529f \u529b\u51cf\u5f31\uff0c\u636e\u5e08\u7236\u8bf4\u90a3\u662f\u56e0\u4e3a\u6cc4\u4e86\u771f\u5143\u4e4b\u6c14\uff0c\u574f\u4e86\u201c\u7ae5\u5b50\u529f\u201d\u7684\u7f18\u6545\uff0c\u4f46\u5047\u82e5\u6709\u4e00\u79cd\u4e0a\u4e58\u7684\u5185 \u529f\uff0c\u53ef\u4ee5\u4fdd\u4f4f\u771f\u5143\u4e4b\u6c14\u7684\uff0c\u90a3\u4e48\u7ed3\u5a5a\u53c8\u6709\u4f55\u59a8\uff1f\u4e4c\u8499\u592b\u56e0\u4e3a\u6709\u6b64\u4e00\u5ff5\uff0c\u6240\u4ee5\u4e91\u6e38\u5929\u4e0b\uff0c\u4e00\u5fc3 \u4e00\u610f\u60f3\u5bfb\u89c5\u4e00\u79cd\u6b63\u5b97\u7684\u66f4\u4e0a\u4e58\u7684\u5185\u529f\uff0c\u5341\u4f59\u5e74\u6765\uff0c\u5374\u6ca1\u6709\u5bfb\u5230\u3002\u4ed6\u5c11\u5e74\u4e4b\u65f6\u66fe\u542c\u6fb9\u53f0\u706d\u660e\u8c08 \u8d77\u5f20\u58eb\u8bda\u548c\u5f6d\u548c\u5c1a\u7684\u65e7\u4e8b\uff0c\u542c\u8bf4\u5f6d\u548c\u5c1a\u6709\u4e00\u672c\u9057\u4e66\u53eb\u300a\u7384\u529f\u8981\u8bc0\u300b\uff0c\u867d\u7136\u4e0d\u77e5\u5185\u5bb9\uff0c\u4f46\u4ee5\u5f6d \u548c\u5c1a\u90a3\u4e48\u9ad8\u7684\u672c\u9886\u800c\u4e66\u540d\u53c8\u53eb\u505a\u300a\u7384\u529f\u8981\u8bc0\u300b\uff0c\u60f3\u5fc5\u5185\u4e2d\u5927\u6709\u9053\u7406\u3002\u662f\u4ee5\u4ed6\u4e5f\u60f3\u5bfb\u89c5\u8fd9\u672c \u4e66\u3002\u4e00\u6708\u4e4b\u524d\uff0c\u4ed6\u56de\u5230\u8499\u53e4\uff0c\u78b0\u5230\u4e86\u4e5f\u5148\u624b\u4e0b\u7684\u6b66\u58eb\u989d\u5409\u591a\uff0c\u8bf4\u662f\u5df2\u63a2\u51fa\u5f20\u58eb\u8bda\u7684\u5b9d\u85cf\u548c\u90a3 \u672c\u9057\u4e66\u90fd\u57cb\u5728\u82cf\u5dde\uff0c\u5173\u952e\u5219\u662f\u5728\u77f3\u82f1\u5bb6\u4e2d\u7684\u4e00\u5e45\u753b\u56fe\u3002\u989d\u5409\u591a\u77e5\u4ed6\u66fe\u662f\u6fb9\u53f0\u706d\u660e\u7684\u5e08\u5f1f\uff0c\u4fbf \u9080\u4ed6\u76f8\u52a9\uff0c\u4ed6\u65e0\u53ef\u65e0\u4e0d\u53ef\uff0c\u4fbf\u968f\u4e86\u989d\u5409\u591a\u5230\u6c99\u6d9b\u7684\u5c71\u5be8\uff0c\u6070\u597d\u9047\u5230\u5f20\u4e39\u67ab\uff0c\u8fd9\u624d\u77e5\u9053\u300a\u7384\u529f \u8981\u8bc0\u300b\u5df2\u7ed9\u5f20\u4e39\u67ab\u53d6\u53bb\u3002\u4ed6\u662f\u957f\u8f88\uff0c\u53c8\u81ea\u8d1f\u662f\u6b66\u6797\u4e2d\u6210\u540d\u7684\u4eba\u7269\uff0c\u81ea\u7136\u4e0d\u4fbf\u5411\u5c0f\u8f88\u8981\u4e66\uff0c\u6545 \u6b64\u6084\u7136\u800c\u9000\u3002\u4ed6\u5bf9\u5f02\u65cf\u4ea6\u65e0\u597d\u611f\uff0c\u4f46\u4ed6\u56e0\u4e13\u5fc3\u4e00\u610f\u8981\u5b66\u4e0a\u4e58\u5185\u529f\uff0c\u5bf9\u74e6\u523a\u4e0e\u660e\u671d\u7684\u4e24\u56fd\u76f8\u4e89 \u4e4b\u4e8b\uff0c\u4ea6\u4e0d\u751a\u5173\u5fc3\uff0c\u4f46\u4ed6\u4e5f\u4e0d\u613f\u5f20\u4e39\u67ab\u6bc1\u5728\u989d\u5409\u591a\u4e0e\u6c99\u6d9b\u7684\u624b\u91cc\uff0c\u81f4\u4f7f\u5947\u4e66\u843d\u5165\u8499\u53e4\u6b66\u58eb\u7684 \u624b\u4e2d\uff0c\u6545\u6b64\u4ed6\u9000\u51fa\u4e86\u6c99\u6d9b\u7684\u5c71\u5be8\u4e4b\u540e\uff0c\u5374\u53c8\u6697\u5730\u91cc\u53bb\u5411\u91d1\u5200\u5be8\u4e3b\u62a5\u4fe1\u3002 \u3000\u3000\u81f3\u4e8e\u91d1\u94a9\u4ed9\u5b50\u6797\u4ed9\u97f5\uff0c\u867d\u7136\u8868\u9762\u6ca1\u663e\u9732\uff0c\u5fc3\u4e2d\u5bf9\u4e4c\u8499\u592b\u4e5f\u662f\u5ff5\u5ff5\u4e0d\u5fd8\u3002\u5979\u5728\u5e08\u95e8\u5341\u5e74\u4e4b \u540e\uff0c\u6b66\u529f\u5df2\u6709\u6210\u5c31\uff0c\u4e0a\u5b98\u5929\u91ce\u9063\u5979\u4e0b\u5c71\uff0c\u81ea\u7acb\u95e8\u6237\uff0c\u5979\u5c31\u5728\u96c1\u95e8\u5173\u5916\u7684\u4e00\u5ea7\u5c71\u4e2d\uff0c\u6f5c\u5fc3\u82e6 \u7ec3\uff0c\u4e5f\u4e0d\u6536\u5f92\u5f1f\u3002\u4e4c\u8499\u592b\u51e0\u5929\u4e4b\u524d\u627e\u5230\u4e86\u5979\uff0c\u4e24\u4eba\u63d0\u8d77\u522b\u540e\u4e4b\u60c5\uff0c\u5404\u81ea\u51c4\u6006\u3002\u4f46\u7981\u4e8e\u5e08\u95e8\u7684 \u7ea6\u675f\uff0c\u4ecd\u4e0d\u6562\u8c08\u5a5a\u8bba\u5ac1\u3002\u540e\u6765\u4e4c\u8499\u592b\u8bf4\u8d77\uff0c\u8bf4\u662f\u63a2\u5f97\u7384\u673a\u9038\u58eb\u6709\u4e24\u4e2a\u5f1f\u5b50\uff0c\u5373\u5c06\u51fa\u5173\uff0c\u6797\u4ed9 \u97f5\u9053\uff1a\u201c\u5e08\u7236\u5341\u5e74\u6765\u5fc3\u613f\uff0c\u5c31\u662f\u8981\u80dc\u8fc7\u90a3\u7384\u673a\u9038\u58eb\uff0c\u53ea\u4e0d\u77e5\u8fd9\u51e0\u5341\u5e74\u6765\uff0c\u7384\u673a\u9038\u58eb\u53c8\u521b\u4e86\u4ec0 \u4e48\u5947\u7279\u7684\u6b66\u529f\u3002\u4ed6\u4e5f\u60f3\u95e8\u4e0b\u7684\u5f1f\u5b50\u80dc\u8fc7\u7384\u673a\u9038\u58eb\u7684\u5f1f\u5b50\uff0c\u597d\u66ff\u4ed6\u4e89\u5149\u3002\u4f60\u6211\u4e0d\u5982\u5230\u96c1\u95e8\u5173 \u5916\uff0c\u9080\u6597\u7384\u673a\u9038\u58eb\u7684\u90a3\u4e24\u540d\u5f1f\u5b50\uff0c\u80dc\u4e86\u56fa\u597d\uff0c\u5c31\u662f\u4e0d\u80fd\u80dc\uff0c\u4e5f\u603b\u53ef\u4ee5\u63a2\u51fa\u4e00\u4e9b\u865a\u5b9e\uff0c\u4e3a\u5e08\u95e8 \u7acb\u4e00\u5927\u529f\u3002\u4e5f\u8bb8\u5e08\u7236\u5c31\u56e0\u6b64\u4f1a\u8ba9\u4f60\u91cd\u5217\u95e8\u5899\u4e86\u3002\u201d\u4e4c\u8499\u592b\u7ed9\u5979\u8bf4\u52a8\uff0c\u4fbf\u540c\u5230\u96c1\u95e8\u5173\u5916\u4e00\u4e2a\u9669 \u8981\u4e4b\u5730\u62e6\u622a\uff0c\u4e4c\u8499\u592b\u672c\u63a2\u542c\u51fa\u7384\u673a\u9038\u58eb\u90a3\u4e24\u540d\u5f1f\u5b50\uff0c\u662f\u4e00\u7537\u4e00\u5973\uff0c\u4f46\u622a\u5230\u4e4b\u65f6\uff0c\u5374\u53ea\u89c1\u6f6e\u97f3 \u548c\u5c1a\u4e00\u4eba\u3002\u8fd9\u5c31\u662f\u4e4c\u8499\u592b\u4e0e\u6f6e\u97f3\u548c\u5c1a\u76f8\u6597\u7684\u524d\u56e0\u540e\u679c\u3002 \u3000\u3000\u65e0\u5de7\u4e0d\u5de7\uff0c\u53cc\u65b9\u6b63\u5728\u6fc0\u6218\u4e4b\u65f6\uff0c\u8c22\u5929\u534e\u7b56\u9a6c\u6765\u5230\uff0c\u53eb\u9053\uff1a\u201c\u6f6e\u97f3\u5e08\u5144\uff0c\u4f60\u4e14\u6b47\u4e00\u4f1a \u513f\u3002\u201d\u9752\u94a2\u5251\u4e00\u4eae\uff0c\u4fbf\u5411\u91d1\u94a9\u4ed9\u5b50\u6797\u4ed9\u97f5\u6311\u6218\u3002\u6f6e\u97f3\u548c\u5c1a\u5411\u8c22\u5929\u534e\u77a5\u4e86\u4e00\u773c\uff0c\u610f\u9887\u4e0d\u5fff\uff0c\u4f46 \u4e5f\u4e0d\u8a00\u8bed\u3002 \u3000\u3000\u6797\u4ed9\u97f5\u9053\uff1a\u201c\u4f60\u662f\u8c22\u5929\u534e\u5417\uff1f\u201d\u8c22\u5929\u534e\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8c22\u5929\u534e\u6b63\u662f\u533a\u533a\u3002\u201d\u6797\u4ed9\u97f5\u9053\uff1a \u201c\u6211\u7d20\u95fb\u7384\u673a\u9038\u58eb\u95e8\u4e0b\uff0c\u4ee5\u8c22\u5929\u534e\u7684\u6b66\u529f\u6700\u5f3a\uff0c\u4eca\u65e5\u4f60\u6765\u5f97\u6b63\u597d\uff0c\u6211\u4e5f\u60f3\u89c1\u8bc6\u89c1\u8bc6\u4f60\u7684\u6b66 \u529f\u3002\u201d\u5de6\u624b\u4e00\u8d77\uff0c\u970d\u5730\u4fbf\u662f\u4e00\u94a9\uff0c\u8c22\u5929\u534e\u53cd\u624b\u4e00\u5251\uff0c\u8eab\u968f\u5251\u52bf\uff0c\u4e00\u7275\u4e00\u5f15\uff0c\u6797\u4ed9\u97f5\u88ab\u4ed6\u5e26\u52a8 \u4e24\u6b65\uff0c\u91d1\u94a9\u51e0\u4e4e\u8131\u624b\uff0c\u4e0d\u7981\u5927\u5403\u4e00\u60ca\u3002\u987b\u77e5\u94a9\u593a\u4e4b\u7c7b\u7684\u5175\u5668\uff0c\u672c\u6765\u662f\u7528\u4ee5\u514b\u5236\u5200\u5251\u7684\uff0c\u800c\u4eca \u6797\u4ed9\u97f5\u7684\u91d1\u94a9\u53cd\u88ab\u8c22\u5929\u534e\u7684\u9752\u94a2\u5251\u6240\u514b\uff0c\u4e8b\u5c5e\u53cd\u5e38\uff0c\u54ea\u5f97\u4e0d\u60ca\uff01\u8c22\u5929\u534e\u5251\u968f\u8eab\u8f6c\uff0c\u6ef4\u6e9c\u6e9c\u5730 \u8f6c\u4e86\u534a\u4e2a\u5706\u5708\uff0c\u5251\u628a\u4e00\u7ffb\uff0c\u5251\u8eab\u8d34\u7740\u91d1\u94a9\u5251\u5c16\u4fbf\u523a\u654c\u8155\uff0c\u8fd9\u4e00\u62db\u6b63\u662f\u767e\u53d8\u7384\u673a\u5251\u6cd5\u4e2d\u7684\u4e00\u4e2a \u6700\u7cbe\u5999\u7684\u62db\u6570\u3002\u6797\u4ed9\u97f5\u53f3\u624b\u5251\u62db\u5df2\u53d1\uff0c\u4e00\u62db\u201c\u7389\u5973\u7a7f\u9488\u201d\uff0c\u75be\u523a\u8c22\u5929\u534e\u80f8\u53e3\u7684\u201c\u7384\u73b0\u7a74\u201d\uff0c \u8fd9\u662f\u201c\u56f4\u9b4f\u6551\u8d75\u201d\u4e4b\u7b56\uff0c\u8981\u8feb\u8c22\u5929\u534e\u64a4\u5251\u56de\u6551\u3002\u8c22\u5929\u534e\u5fc3\u4e2d\u6697\u7b11\uff1a\u201c\u6211\u7109\u80fd\u7ed9\u4f60\u523a\u7740\uff1f\u201d\u8eab \u5f62\u7565\u7565\u4e00\u504f\uff0c\u5251\u8eab\u4ecd\u7136\u9ecf\u7740\u91d1\u94a9\uff0c\u5251\u5c16\u5f80\u4e0a\u4e00\u6311\u3002\u54ea\u77e5\u5c31\u5728\u8fd9\u5239\u90a3\u95f4\uff0c\u6797\u4ed9\u97f5\u8d81\u7740\u8c22\u5929\u534e\u7a0d \u5931\u5e73\u8861\uff0c\u91d1\u94a9\u4e00\u62c9\uff0c\u970d\u5730\u4fbf\u8131\u4e86\u51fa\u6765\uff0c\u5251\u5149\u4e00\u6643\uff0c\u6539\u523a\u4e3a\u62b9\uff0c\u4e00\u62db\u201c\u5e73\u6c99\u843d\u96c1\u201d\uff0c\u6a2a\u524a\u8fc7 \u53bb\uff0c\u8fd9\u4e24\u4e0b\u624b\u6cd5\uff0c\u5229\u843d\u5e72\u51c0\uff0c\u62ff\u634f\u65f6\u5019\uff0c\u4e0d\u5dee\u6beb\u53d1\uff0c\u5c06\u4e0b\u98ce\u4e4b\u52bf\u7acb\u523b\u626d\u8f6c\u8fc7\u6765\u3002\u8c22\u5929\u534e\u4e5f\u4e0d \u7981\u5931\u58f0\u8d5e\u597d\uff0c\u9053\uff1a\u201c\u91d1\u94a9\u4ed9\u5b50\uff0c\u679c\u7136\u540d\u4e0d\u865a\u4f20\uff01\u201d\u6a2a\u5251\u4e00\u5c01\uff0c\u5c06\u91d1\u94a9\u94f6\u5251\u4e00\u9f50\u8361\u5f00\uff0c\u9752\u94a2\u5251 \u5de6\u8d77\u53f3\u843d\uff0c\u4e00\u53e3\u6c14\u8fde\u524a\u516b\u5251\uff0c\u90fd\u7528\u540c\u4e00\u624b\u6cd5\uff0c\u770b\u6765\u6beb\u4e0d\u51fa\u5947\uff0c\u4f46\u516b\u5251\u4e00\u6c14\u5475\u6210\uff0c\u7adf\u628a\u6797\u4ed9\u97f5 \u8feb\u5f97\u53ea\u80fd\u62db\u67b6\uff0c\u5fc3\u4e2d\u4e5f\u81ea\u6697\u6697\u4f69\u670d\uff1a\u8fd9\u8c22\u5929\u534e\u7684\u6b66\u529f\u679c\u7136\u6bd4\u4ed6\u7684\u5e08\u5144\u8981\u9ad8\u660e\u8bb8\u591a\u3002 \u3000\u3000\u8c22\u5929\u534e\u52a8\u624b\u4e4b\u65f6\uff0c\u5f20\u4e39\u67ab\u4e5f\u4e0e\u4e4c\u8499\u592b\u518d\u5ea6\u4ea4\u624b\uff0c\u8fd9\u56de\u662f\u5f20\u4e39\u67ab\u5355\u72ec\u63a5\u6218\uff0c\u6709\u610f\u76f8\u8ba9\uff0c\u4e0d \u7528\u53cc\u5251\u4e4b\u529b\u8feb\u4ed6\u3002\u672c\u6765\u5f20\u4e39\u67ab\u4e0d\u662f\u4e4c\u8499\u592b\u7684\u5bf9\u624b\uff0c\u4f46\u4e4c\u8499\u592b\u5148\u6218\u6f6e\u97f3\u548c\u5c1a\uff0c\u540e\u6218\u5f20\u4e39\u67ab\u4e0e\u4e91 \u857e\u6c14\u529b\u6d88\u8017\u4e0d\u5c11\uff0c\u4e09\u5341\u62db\u4e4b\u5185\uff0c\u7adf\u7136\u5360\u4e0d\u4e86\u5f20\u4e39\u67ab\u7684\u4fbf\u5b9c\u3002 \u3000\u3000\u8c22\u5929\u534e\u659c\u773c\u4e00\u77a5\uff0c\u89c1\u7231\u5f92\u6b66\u529f\u7cbe\u8fdb\uff0c\u597d\u5f97\u51fa\u5947\u751a\u662f\u60ca\u5f02\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4e4c\u8499\u592b\uff0c\u4f60\u8fde\u6211 \u7684\u5f92\u5f1f\u4e5f\u6218\u4e0d\u4e0b\u5417\uff1f\u201d\u4e4c\u8499\u592b\u5927\u6124\uff0c\u547c\u547c\u547c\u8fde\u626b\u4e09\u638c\uff0c\u5728\u638c\u98ce\u5251\u5f71\u4e4b\u4e2d\uff0c\u6b3a\u8eab\u76f4\u8fdb\uff0c\u8fd0\u4e00\u6307 \u7985\u7684\u529f\u592b\uff0c\u523a\u63a2\u5f20\u4e39\u67ab\u7684\u7a74\u9053\uff0c\u7740\u7740\u62a2\u653b\u3002\u5f20\u4e39\u67ab\u673a\u7075\u4e4b\u6781\uff0c\u6025\u5fd9\u7f29\u5c0f\u5708\u5b50\uff0c\u4ed7\u5b9d\u5251\u4e4b\u529b\uff0c \u62a4\u7740\u5168\u8eab\uff0c\u4e4c\u8499\u592b\u653b\u52bf\u867d\u7136\u5f3a\u52b2\uff0c\u8feb\u5207\u4e4b\u95f4\uff0c\u4e5f\u7834\u4e0d\u4e86\u5f20\u4e39\u67ab\u7684\u5b88\u52bf\u3002 \u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\uff0c\u53cc\u65b9\u5df2\u6597\u4e86\u4e03\u516b\u5341\u62db\uff0c\u8c22\u5929\u534e\u5251\u52bf\u7eb5\u6a2a\uff0c\u5c06\u6797\u4ed9\u97f5\u8feb\u5f97\u8fde\u8fde\u540e\u9000\uff0c\u663e\u7136\u5360\u4e86 \u5f88\u5927\u7684\u4f18\u52bf\uff0c\u4e4c\u8499\u592b\u4e5f\u5df2\u62a2\u5f97\u4e0a\u98ce\uff0c\u4f46\u5f20\u4e39\u67ab\u5374\u8fd8\u80fd\u81ea\u4fdd\u3002\u8c22\u5929\u534e\u54c8\u54c8\u5927\u7b11\uff0c\u9053\uff1a\u201c\u4e4c\u8001 \u4e8c\uff0c\u5feb\u4e00\u767e\u62db\u5566\uff0c\u4f60\u8fd8\u80dc\u4e0d\u4e86\u6211\u7684\u5f92\u5f1f\u5417\uff1f\u201d \u3000\u3000\u4e4c\u8499\u592b\u6218\u4e00\u4e2a\u5c0f\u8f88\u4e0d\u4e0b\uff0c\u81ea\u89c9\u751a\u96be\u4e3a\u60c5\uff0c\u53c8\u89c1\u6797\u4ed9\u97f5\u5904\u5728\u4e0b\u98ce\uff0c\u4e0d\u518d\u604b\u6218\uff0c\u5f3a\u7b11\u89e3\u5632 \u9053\uff1a\u201c\u8c22\u5929\u534e\uff0c\u4f60\u7684\u5f92\u5f1f\u679c\u7136\u540d\u4e0d\u865a\u4f20\uff0c\u6211\u770b\u4f60\u4e5f\u4e0d\u89c1\u5f97\u6bd4\u4ed6\u9ad8\u660e\u591a\u5c11\u3002\u6211\u5bf9\u6709\u672c\u4e8b\u7684\u540e\u8f88 \u7d20\u6765\u7231\u60dc\uff0c\u5c31\u8ba9\u4ed6\u5598\u4e00\u53e3\u6c14\u5427\uff0c\u4eca\u65e5\u4e0d\u5fc5\u518d\u6597\u4e86\uff0c\u6539\u5929\u6211\u518d\u5411\u4f60\u9886\u6559\u3002\u201d\u4e0e\u6797\u4ed9\u97f5\u5148\u540e\u8df3\u51fa \u5708\u5b50\uff0c\u5411\u897f\u5317\u5954\u8d70\u3002\u8c22\u5929\u534e\u542c\u4ed6\u4eec\u81ea\u53bb\uff0c\u7b11\u5bf9\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u4f60\u54ea\u513f\u5b66\u6765\u7684\u8fd9\u8eab\u6b66\u529f\uff0c\u518d\u8fc7\u4e24 \u5e74\uff0c\u6211\u771f\u4e0d\u6562\u518d\u505a\u4f60\u7684\u5e08\u7236\u5566\uff01\u201d\u53c8\u5bf9\u6f6e\u97f3\u548c\u5c1a\u9053\uff1a\u201c\u4eca\u65e5\u54b1\u4eec\u867d\u7136\u7a0d\u5360\u4e0a\u98ce\uff0c\u4f46\u8fd9\u4e24\u4eba\u7684 \u529f\u592b\uff0c\u786e\u662f\u6b66\u6797\u7f55\u89c1\uff0c\u5f92\u5f1f\u5c1a\u4e14\u5982\u6b64\uff0c\u90a3\u4e0a\u5b98\u8001\u602a\u7684\u6b66\u529f\uff0c\u5b9e\u5c5e\u6df1\u4e0d\u53ef\u6d4b\uff0c\u54b1\u4eec\u7684\u5e08\u7236\u4e0d\u60f3 \u4e0e\u4ed6\u4eb2\u81ea\u52a8\u624b\uff0c\u6211\u53ea\u6015\u6211\u4e0e\u56db\u59b9\u4e8c\u4eba\uff0c\u8054\u5251\u6597\u4ed6\uff0c\u4e5f\u96be\u4fdd\u4e0d\u843d\u8d25\u5462\u3002\u201d \u3000\u3000\u5f20\u4e39\u67ab\u6b63\u60f3\u5411\u5e08\u7236\u8bf4\u660e\u5f97\u5230\u5f6d\u548c\u5c1a\u9057\u4e66\u4e4b\u4e8b\uff0c\u5ffd\u89c1\u6f6e\u97f3\u548c\u5c1a\u9762\u8272\u94c1\u9752\uff0c\u9053\uff1a\u201c\u54fc\uff0c\u4f60\u8fd8 \u8bb0\u5f97\u5e08\u7236\u4e48\uff1f\u201d\u8c22\u5929\u534e\u9053\uff1a\u201c\u5e08\u5144\u4f60\u8bf4\u4ec0\u4e48\uff1f\u201d\u6f6e\u97f3\u548c\u5c1a\u9053\uff1a\u201c\u6211\u8fd8\u4ee5\u4e3a\u4f60\u4eca\u65e5\u4e0d\u6765\u4e86 \u5462\uff1f\u201d\u8c22\u5929\u534e\u9053\uff1a\u201c\u5e08\u5144\uff0c\u4f60\u662f\u602a\u6211\u6765\u8fdf\u4e86\u4e48\uff1f\u201d\u6f6e\u97f3\u548c\u5c1a\u9053\uff1a\u201c\u4e91\u857e\uff0c\u4f60\u6765\u5f97\u6b63\u597d\uff0c\u4f60\u77e5 \u9053\u4eca\u65e5\u662f\u4ec0\u4e48\u65e5\u5b50\u5417\uff1f\u201d\u4e91\u857e\u6014\u4e86\u6014\uff0c\u65c5\u9014\u4e2d\u5fd8\u8bb0\u65f6\u65e5\uff0c\u4f46\u524d\u6628\u4e24\u665a\uff0c\u90fd\u89c1\u6708\u5706\uff0c\u60f3\u5fc5\u4e0d\u662f \u5341\u4e94\uff0c\u5c31\u662f\u5341\u516d\u3002\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u4eca\u65e5\u662f\u6b63\u7edf\u5341\u4e09\u5e74\u5341\u6708\u5341\u516d\u3002\u201d\u4e91\u857e\u731b\u7136\u7701\u8d77\uff0c\u4eca\u65e5\u6b63\u662f\u5979 \u7237\u7237\u6b7b\u96be\u7684\u7b2c\u5341\u5468\u5e74\u3002\u5f53\u65e5\u60c5\u666f\u53c8\u4e00\u5e55\u4e00\u5e55\u5730\u4ece\u8111\u4e2d\u95ea\u8fc7\uff0c\u672c\u5df2\u6a21\u7cca\u4e86\u7684\u60c5\u666f\uff0c\u7a81\u7136\u95f4\u53c8\u6e05 \u6670\u8d77\u6765\uff0c\u73e0\u6cea\u4e0d\u7981\u7c0c\u7c0c\u800c\u4e0b\u3002 \u3000\u3000\u6f6e\u97f3\u548c\u5c1a\u9053\uff1a\u201c\u8c22\u5929\u534e\uff0c\u54b1\u4eec\u5341\u5e74\u4e4b\u524d\u5728\u8fd9\u91cc\u8bf4\u4e86\u4ec0\u4e48\u8bdd\u6765\uff1f\u201d\u8c22\u5929\u534e\u9053\uff1a\u201c\u54b1\u4eec\u5f53\u65e5 \u5728\u8fd9\u91cc\u51fb\u638c\u4e3a\u76df\uff0c\u4e00\u4e2a\u629a\u5b64\uff0c\u4e00\u4e2a\u62a5\u4ec7\u3002\u4f60\u8981\u5c06\u4e91\u9756\u7684\u5b59\u5973\u5e26\u56de\u53bb\u4ea4\u7ed9\u56db\u59b9\uff0c\u629a\u517b\u6210\u4eba\uff0c\u6211 \u8981\u5230\u74e6\u523a\u53bb\u6740\u5f20\u5b97\u5468\u3002\u201d\u6f6e\u97f3\u548c\u5c1a\u6602\u5934\u51b7\u7b11\uff0c\u9053\uff1a\u201c\u539f\u6765\u4f60\u4e5f\u8fd8\u8bb0\u5f97\u5982\u6b64\u6e05\u695a\u3002\u4e91\u857e\u4f60\u8fc7 \u6765\u3002\u201d\u4e91\u857e\u632a\u524d\u4e24\u6b65\u3002\u6f6e\u97f3\u9053\uff1a\u201c\u4f60\u77a7\uff0c\u8fd9\u5f53\u65e5\u7684\u5973\u5a03\u513f\u5982\u4eca\u5df2\u6210\u4e86\u4e00\u540d\u51fa\u8272\u7684\u5973\u5251\u5ba2\u5566\uff0c \u6211\u8be5\u505a\u7684\u5df2\u7ecf\u505a\u4e86\u3002\u4f60\u5462\uff1f\u4f60\u5c06\u5f20\u5b97\u5468\u7684\u9996\u7ea7\u5e26\u6765\u6ca1\u6709\uff1f\u201d\u8c22\u5929\u534e\u7b54\u9053\uff1a\u201c\u6ca1\u6709\uff01\u201d\u6f6e\u97f3\u548c \u5c1a\u54fc\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u539f\u6765\u4f60\u662f\u8d2a\u56fe\u5bcc\u8d35\u8146\u989c\u4e8b\u654c\u5566\uff01\u201d\u547c\u7684\u4e00\u6756\uff0c\u5c31\u5411\u8c22\u5929\u534e\u5f53\u5934\u626b\u4e0b\u3002\u8c22 \u5929\u534e\u4e00\u95ea\u95ea\u5f00\uff0c\u9053\uff1a\u201c\u4e14\u6162\uff0c\u56db\u59b9\u5462\uff1f\u5979\u6765\u4e86\u6ca1\u6709\uff1f\u201d\u6f6e\u97f3\u548c\u5c1a\u52c3\u7136\u5927\u6012\uff0c\u559d\u9053\uff1a\u201c\u4f60\u6562\u81ea \u6043\u6b66\u529f\uff0c\u6b3a\u538b\u5e08\u5144\u5417\uff1f\u6211\u4e0d\u8981\u56db\u59b9\u5e2e\u624b\uff0c\u5148\u5c31\u8981\u5c06\u4f60\u6253\u4e09\u767e\u7985\u6756\uff0c\u4f60\u6709\u80c6\u6b3a\u5e08\u706d\u957f\u5c31\u4eae\u5251\u5c06 \u6211\u6740\u4e86\uff01\u201d\u8c22\u5929\u534e\u9053\uff1a\u201c\u4e0d\uff0c\u4e0d\u662f\u8fd9\u4e2a\u610f\u601d\uff0c\u6211\u6599\u60f3\u56db\u59b9\u8be5\u4e0e\u4f60\u4e00\u9f50\u6765\u5230\u4e3a\u4f55\u5374\u4e0d\u89c1\u5979\uff1f\u201d \u6f6e\u97f3\u548c\u5c1a\u672c\u6765\u662f\u7ea6\u4e86\u5e08\u59b9\u53f6\u76c8\u76c8\u4e00\u540c\u51fa\u96c1\u95e8\u5173\uff0c\u627e\u8c22\u5929\u534e\u7b97\u5e10\uff0c\u6f6e\u97f3\u548c\u5c1a\u9a6c\u5feb\uff0c\u6240\u4ee5\u5148\u5230\u3002 \u4f46\u60f3\u8d77\u803d\u6401\u4e86\u8fd9\u4e48\u4e9b\u65f6\u5019\u53f6\u76c8\u76c8\u4e5f\u8be5\u6765\u4e86\uff0c\u4e0d\u89c9\u4e5f\u662f\u4e00\u6014\u3002\u8c22\u5929\u534e\u9053\uff1a\u201c\u7b49\u5230\u56db\u59b9\u6765\u4e86\uff0c\u54b1 \u4eec\u518d\u628a\u8bdd\u8bf4\u6e05\u695a\u3002\u201d\u6f6e\u97f3\u548c\u5c1a\u706b\u6c14\u53c8\u8d77\u559d\u9053\uff1a\u201c\u54c8\uff0c\u539f\u6765\u4f60\u773c\u91cc\u5c31\u6ca1\u6709\u6211\u8fd9\u4e2a\u5e08\u5144\u4e86\u5417\uff1f\u201d \u5927\u559d\u4e00\u58f0\uff0c\u5f53\u5934\u53c8\u662f\u4e00\u6756\uff01 \u3000\u3000\u6f6e\u97f3\u548c\u5c1a\u6027\u5b50\u66b4\u8e81\u4e4b\u6781\uff0c\u52a8\u624b\u4e0d\u80fd\u81ea\u4f11\uff0c\u4e0d\u7531\u5206\u8bf4\uff0c\u547c\u547c\u547c\uff0c\u4e00\u8fde\u626b\u4e86\u4e03\u516b\u6756\uff0c\u628a\u8c22\u5929 \u534e\u5f04\u5f97\u557c\u7b11\u7686\u975e\uff0c\u8feb\u5f97\u65bd\u5c55\u6700\u4e0a\u4e58\u7684\u5185\u5bb6\u529f\u592b\uff0c\u888d\u8896\u4e00\u6325\u5c06\u6f6e\u97f3\u548c\u5c1a\u7684\u7985\u6756\u88f9\u4f4f\uff0c\u7b11\u9053\uff1a \u201c\u4e39\u67ab\uff0c\u4f60\u4e5f\u6765\u5f97\u6b63\u597d\uff0c\u4f60\u5411\u4e8c\u5e08\u4f2f\u8bf4\u53bb\u3002\u201d\u6f6e\u97f3\u548c\u5c1a\u9053\uff1a\u201c\u5f20\u4e39\u67ab\u7684\u4e8b\u60c5\u6211\u4e5f\u77e5\u9053\u5927\u534a\uff0c \u4ed6\u5012\u4e0d\u6127\u662f\u4e2a\u597d\u7537\u513f\u3002\u4f46\u7236\u8fd8\u7236\uff0c\u5b50\u8fd8\u5b50\uff0c\u9f99\u751f\u4e5d\u79cd\uff0c\u7236\u5b50\u5144\u5f1f\uff0c\u5404\u5404\u4e0d\u540c\u3002\u5f20\u5b97\u5468\u7ec8\u5f52\u662f \u74e6\u523a\u7684\u4e1e\u76f8\uff0c\u662f\u901a\u756a\u5356\u56fd\u7684\u5978\u8d3c\u3002\u6b64\u4e8b\u4e0e\u5f20\u4e39\u67ab\u65e0\u5173\uff0c\u6211\u53ea\u95ee\u4f60\u80cc\u76df\u4e4b\u7f6a\u3002\u201d\u6f6e\u97f3\u548c\u5c1a\u8fde\u73e0 \u70ae\u822c\u7684\u53d1\u8bdd\uff0c\u7b80\u76f4\u4e0d\u5bb9\u65c1\u4eba\u7f6e\u5599\uff0c\u8bdd\u5c1a\u672a\u5b8c\uff0c\u7985\u6756\u4e00\u62bd\uff0c\u53c8\u5411\u8c22\u5929\u534e\u5288\u5934\u6253\u53bb\u3002\u4f0f\u9b54\u6756\u6cd5\u5c55 \u5f00\uff0c\u6709\u5982\u4e00\u4e2a\u6d6a\u5934\u63a5\u7740\u4e00\u4e2a\u6d6a\u5934\uff0c\u8fde\u7eed\u4e0d\u65ad\uff0c\u770b\u6765\u4f3c\u4e4e\u975e\u628a\u4ed6\u7684\u7985\u6756\u593a\u51fa\u624b\u53bb\uff0c\u96be\u4ee5\u81ea\u4f11\u3002 \u3000\u3000\u8c22\u5929\u534e\u8fde\u8fde\u82e6\u7b11\uff0c\u5de6\u95ea\u53f3\u8eb2\uff0c\u5f20\u4e39\u67ab\u54b3\u4e86\u4e00\u58f0\uff0c\u60f3\u8d77\u6b64\u4e8b\u5343\u5934\u4e07\u7eea\uff0c\u4e0d\u77e5\u4ece\u4f55\u8bf4\u8d77\uff0c\u6b63 \u5f85\u59d4\u5a49\u9648\u8f9e\uff0c\u5ffd\u542c\u5f97\u4e00\u58f0\u602a\u54cd\uff0c\u63a0\u8fc7\u7a7a\u9645\uff0c\u5176\u58f0\u545c\u545c\u7c7b\u4f3c\u80e1\u4eba\u7684\u53f7\u89d2\uff0c\u4f46\u5374\u5c16\u9510\u7684\u591a\u3002\u4e91\u857e \u9762\u8272\u4e00\u53d8\u53eb\u9053\uff1a\u201c\u5927\u54e5\u4f60\u968f\u6211\u6765\uff01\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\u60c5\uff1f\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u8c22\u5929\u534e\u888d\u8896\u4e00 \u6325\uff0c\u5c06\u6f6e\u97f3\u548c\u5c1a\u7684\u7985\u6756\u8361\u5f00\uff0c\u8eab\u5f62\u4e00\u8d77\u6709\u5982\u9e70\u96bc\u7a7f\u6797\uff0c\u53ea\u4e00\u63a0\u5c31\u63a0\u5230\u4e86\u6f6e\u97f3\u548c\u5c1a\u7684\u90a3\u5339\u767d\u9a6c \u8eab\u8fb9\u3002\u90a3\u767d\u9a6c\u4f3c\u662f\u5403\u4e86\u4e00\u60ca\uff0c\u6602\u9996\u4eba\u7acb\uff0c\u524d\u8e44\u75be\u8e22\uff0c\u8c22\u5929\u534e\u8dc3\u4e0a\u9a6c\u80cc\u4e00\u6309\u767d\u9a6c\u9888\u9879\uff0c\u8f7b\u8f7b\u4e00 \u62cd\uff0c\u90a3\u9a6c\u56db\u8e44\u75be\u5954\uff0c\u5636\u9e23\u4e0d\u5df2\uff0c\u4f3c\u662f\u4e0d\u670d\uff0c\u4f46\u5374\u65e0\u53ef\u5948\u4f55\u3002\u6f6e\u97f3\u548c\u5c1a\u5927\u6012\uff0c\u559d\u9053\uff1a\u201c\u4f60\u6562\u5077 \u6211\u7684\u5b9d\u9a6c\u9003\u8dd1\uff1f\u201d\u5176\u5b9e\u8fd9\u767d\u9a6c\u672c\u6765\u662f\u8c22\u5929\u534e\u5077\u4e0e\u4ed6\u7684\uff0c\u4ed6\u6025\u4e0d\u62e9\u8a00\uff0c\u5f20\u4e39\u67ab\u542c\u4e86\u4e5f\u4e0d\u89c9\u597d \u7b11\u3002 \u3000\u3000\u4f46\u89c1\u4e91\u857e\u65e9\u4e5f\u98de\u8eab\u4e0a\u9a6c\uff0c\u5411\u524d\u75be\u5954\uff0c\u5728\u9a6c\u4e0a\u56de\u5934\uff0c\u4e0d\u4f4f\u5730\u5411\u5f20\u4e39\u67ab\u62db\u624b\u3002\u6f6e\u97f3\u548c\u5c1a\u53eb \u9053\uff1a\u201c\u4e39\u67ab\uff0c\u8ba9\u4f60\u7684\u767d\u9a6c\u7ed9\u6211\u3002\u201d\u5f20\u4e39\u67ab\u7b11\u9053\uff1a\u201c\u4e8c\u5e08\u4f2f\uff0c\u4f60\u4eca\u65e5\u8017\u5c3d\u7cbe\u795e\uff0c\u6b47\u4e00\u6b47\u5427\uff0c\u56de \u5934\u6211\u518d\u5411\u4f60\u8bf7\u5b89\u3002\u201d\u98de\u8eab\u4e0a\u9a6c\uff0c\u4e0d\u7406\u6f6e\u97f3\uff0c\u4e00\u80a1\u52b2\u5730\u5411\u524d\u8ffd\u8d76\uff0c\u6f6e\u97f3\u6c14\u5f97\u66b4\u8df3\u5982\u96f7\uff0c\u53ea\u5f97\u8981 \u4e86\u8c22\u5929\u534e\u7684\u5750\u9a91\u3002\u4f46\u524d\u9762\u8fd9\u4e09\u5339\u9a6c\uff0c\u90fd\u662f\u4e16\u6240\u7a00\u6709\u7684\u5b9d\u9a6c\uff0c\u8c22\u5929\u534e\u4e58\u6765\u7684\u9ec4\u9aa0\u9a6c\uff0c\u867d\u7136\u4e5f\u662f \u8499\u53e4\u826f\u9a79\uff0c\u5374\u662f\u671b\u5c18\u83ab\u53ca\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u7684\u7167\u591c\u72ee\u5b50\u9a6c\u6700\u5feb\uff0c\u4e0d\u4e00\u523b\u5c31\u8d76\u8fc7\u4e86\u5e08\u7236\uff0c\u8c22\u5929\u534e\u867d\u5df2\u5236\u670d\u90a3\u5339\u767d\u9a6c\uff0c\u4f46\u8fd8\u672a \u719f\uff0c\u4e00\u8def\u8d70\u4e00\u8def\u6323\u624e\uff0c\u53cd\u800c\u843d\u5728\u4e91\u857e\u7684\u9a6c\u540e\u3002\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u5e08\u7236\uff0c\u4ec0\u4e48\u4e8b\u60c5\uff1f\u201d\u8c22\u5929\u534e\u6325\u624b \u9053\uff1a\u201c\u4f60\u8ddf\u4e91\u59d1\u5a18\u5148\u53bb\uff0c\u4e0d\u5fc5\u95ee\u3002\u201d\u5f20\u4e39\u67ab\u62cd\u9a6c\u75be\u8ffd\uff0c\u4e0d\u4e00\u523b\u53c8\u8d76\u4e0a\u4e91\u857e\uff0c\u53ea\u542c\u5f97\u7a7a\u9645\u602a\u58f0 \u6447\u66f3\uff0c\u4e00\u957f\u4e00\u77ed\uff0c\u8d8a\u542c\u8d8a\u6e05\u695a\u4e86\u3002\u5f20\u4e39\u67ab\u4e0e\u4e91\u857e\u5e76\u8f94\u98de\u9a70\uff0c\u8fc7\u4e86\u4e00\u4f1a\uff0c\u90a3\u602a\u58f0\u6025\u4fc3\u5730\u54cd\u4e86\u51e0 \u4e0b\uff0c\u4ee5\u540e\u4fbf\u4e0d\u518d\u95fb\u3002\u4e91\u857e\u82b1\u5bb9\u53d8\u8272\uff0c\u4fa7\u8033\u503e\u542c\uff0c\u201c\u54a6\u201d\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5927\u54e5\uff0c\u8fd9\u58f0\u97f3\u600e\u4e48\u5c31 \u6ca1\u6709\u4e86\uff1f\u201d\u5f20\u4e39\u67ab\u5fcd\u8010\u4e0d\u4f4f\uff0c\u53c8\u95ee\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u8fd9\u5230\u5e95\u662f\u4ec0\u4e48\u4e8b\u60c5\uff1f\u4f60\u795e\u8272\u614c\u5f20\uff0c\u6240\u4e3a\u4f55 \u6765\uff1f\u201d \u3000\u3000\u4e91\u857e\u9053\uff1a\u201c\u6211\u7684\u5e08\u5085\u9047\u9669\uff01\u201d\u5f20\u4e39\u67ab\u5403\u4e86\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u4f60\u7684\u5e08\u5085\uff1f\u201d\u4e91\u857e\u9053\uff1a\u201c\u4e0d\u9519\uff0c \u8fd9\u58f0\u97f3\u662f\u6211\u5e08\u5085\u53d1\u51fa\u7684\u544a\u6025\u58f0\u97f3\u53ea\u6709\u6211\u548c\u4e09\u5e08\u4f2f\u542c\u5f97\u61c2\u3002\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u4f60\u5e08\u5085\u7684\u6b66\u529f\uff0c\u5f53 \u4eca\u4e4b\u4e16\uff0c\u80fd\u53ca\u5f97\u4e0a\u5979\u7684\uff0c\u4e5f\u4e0d\u8fc7\u6709\u9650\u51e0\u4eba\uff0c\u600e\u4e48\u5979\u4f1a\u9047\u9669\uff1f\u201d\u4e91\u857e\u9053\uff1a\u201c\u8fd9\u786e\u662f\u5979\u53d1\u51fa\u7684\u544a \u6025\u58f0\u3002\u201d\u5c0f\u5bd2\u5c71\u4e0a\u6709\u4e00\u79cd\u4fee\u7af9\uff0c\u5f04\u6210\u5439\u7ba1\uff0c\u53d1\u58f0\u5c16\u9510\uff0c\u5341\u91cc\u4e4b\u5185\uff0c\u90fd\u53ef\u542c\u89c1\uff0c\u52a0\u4e0a\u98de\u5929\u9f99\u5973 \u6df1\u6e5b\u7684\u5185\u529f\uff0c\u4e00\u5439\u8d77\u6765\uff0c\u5728\u50fb\u9759\u4e4b\u5730\uff0c\u4e8c\u5341\u91cc\u5916\uff0c\u4e5f\u53ef\u4f20\u5230\u3002\u98de\u5929\u9f99\u5973\u5728\u8fd8\u672a\u53d7\u7f5a\u9762\u58c1\u4e4b \u524d\uff0c\u66fe\u5c06\u5b83\u5f04\u4e3a\u73a9\u5177\uff0c\u620f\u5bf9\u8c22\u5929\u534e\u8bf4\u8fc7\u4ee5\u540e\u5982\u9047\u6709\u4ec0\u4e48\u6025\u4e8b\uff0c\u5c31\u7528\u8fd9\u7af9\u7ba1\u53d1\u58f0\u62db\u5524\u3002\u5230\u4e86\u4e91 \u857e\u4e0a\u5c71\u4e4b\u540e\uff0c\u4e24\u5e08\u5f92\u5728\u7a7a\u5c71\u4e2d\u540c\u5ea6\u5341\u5e74\uff0c\u65e0\u8bdd\u4e0d\u8c08\uff0c\u6240\u4ee5\u4e91\u857e\u4e5f\u77e5\u9053\u8fd9\u5439\u7ba1\u7684\u529f\u80fd\u3002\u5176\u4ed6\u540c \u95e8\uff0c\u5219\u662f\u65e0\u4e00\u77e5\u6653\u3002 \u3000\u3000\u5439\u7ba1\u4e4b\u58f0\u5ffd\u6b62\uff0c\u90a3\u5f53\u7136\u662f\u7ed9\u654c\u4eba\u6bc1\u4e86\uff0c\u751a\u6216\u9047\u4e86\u9669\u4e5f\u8bf4\u4e0d\u5b9a\u3002\u5f20\u4e39\u67ab\u4e0d\u89c9\u5fc3\u4e2d\u4e00\u6014\uff1a\u4e0a \u5b98\u5929\u91ce\u8fdc\u5728\u8499\u85cf\u4ea4\u754c\u7684\u6df1\u5c71\uff0c\u9664\u4e86\u662f\u4ed6\uff0c\u5f53\u4eca\u4e4b\u4e16\uff0c\u80fd\u5236\u670d\u98de\u5929\u9f99\u5973\u7684\uff0c\u6050\u6015\u5c31\u53ea\u6709\u5979\u7684\u5e08 \u7236\u7384\u673a\u9038\u58eb\uff0c\u5176\u4ed6\u7684\u4eba\u8fde\u6fb9\u53f0\u706d\u660e\u3001\u8c22\u5929\u534e\u7b49\u90fd\u7b97\u4e0a\uff0c\u6700\u591a\u4e5f\u4e0d\u8fc7\u6253\u4e2a\u5e73\u624b\u3002\u90a3\u4e48\u96be\u9053\u662f\u4e0a \u5b98\u5929\u91ce\u6765\u4e86\u4e48\uff1f\u4ee5\u4ed6\u7684\u8f88\u5206\u5730\u4f4d\uff0c\u82e5\u8bf4\u8981\u4e3a\u4e86\u4e3a\u96be\u4e00\u4e2a\u540e\u8f88\uff0c\u4e07\u91cc\u8fe2\u8fe2\u5730\u8d76\u6765\u90a3\u5b9e\u662f\u96be\u4ee5\u7f6e \u4fe1\u3002\u4f46\u9664\u4e86\u662f\u4ed6\uff0c\u5374\u53c8\u662f\u8c01\uff1f\u8c01\u80fd\u6709\u90a3\u4e48\u9ad8\u7684\u672c\u9886\uff1f\u4e91\u857e\u4e5f\u662f\u5982\u6b64\u60f3\u6cd5\u795e\u60c5\u8d8a\u89c1\u60f6\u6050\u3002\u90a3\u5439 \u7ba1\u4e4b\u58f0\u6b62\u4e86\uff0c\u4e24\u4eba\u4e0d\u77e5\u5411\u4f55\u65b9\u8ffd\u5bfb\uff0c\u4e91\u857e\u9053\uff1a\u201c\u5927\u54e5\uff0c\u8fd9\u600e\u4e48\u529e\uff1f\u201d\u521a\u624d\u7684\u58f0\u97f3\u81ea\u7fa4\u5c71\u4e4b\u4e2d \u53d1\u51fa\uff0c\u7ecf\u8fc7\u56de\u65cb\u9707\u8361\uff0c\u4e0d\u6bd4\u7a7a\u65f7\u4e4b\u5730\uff0c\u5bb9\u6613\u8fa8\u522b\u65b9\u5411\uff0c\u5f20\u4e39\u67ab\u4e5f\u4e0d\u77e5\u8be5\u600e\u4e48\u529e\u3002 \u3000\u3000\u5ffd\u89c1\u524d\u9762\u4e24\u9a91\u5954\u9a70\uff0c\u539f\u6765\u5f20\u3001\u4e91\u4e8c\u4eba\u9a6c\u5feb\uff0c\u7adf\u8d76\u4e0a\u65e9\u5c31\u8d70\u4e86\u7684\u4e4c\u8499\u592b\u4e0e\u6797\u4ed9\u97f5\u3002\u4e4c\u8499\u592b \u56de\u9996\u7b11\u9053\uff1a\u201c\u5f20\u4e39\u67ab\uff0c\u4f60\u4eec\u8fd8\u8981\u25a1\u6740\u5417\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u4e0d\u6562\uff0c\u8bf7\u95ee\u8fd9\u91cc\u53ef\u662f\u4f4f\u6709\u4e00\u4f4d\u4e16\u5916 \u9ad8\u4eba\uff1f\u201d\u4e4c\u8499\u592b\u7b11\u9053\uff1a\u201c\u4e16\u5916\u9ad8\u4eba\uff0c\u5c82\u662f\u4f60\u4eec\u6240\u80fd\u89c1\u7684\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u4e0d\u7ba1\u4ed6\u89c1\u662f\u4e0d\u89c1\uff0c \u4f46\u6c42\u524d\u8f88\u6307\u5f15\u3002\u201d\u4e4c\u8499\u592b\u9053\uff1a\u201c\u4f60\u5012\u5f88\u6709\u793c\u8c8c\uff0c\u4e09\u59b9\u4f60\u95ee\u4e00\u95ee\u3002\u201d\u91d1\u94a9\u4ed9\u5b50\u6797\u4ed9\u97f5\u53d1\u58f0\u957f \u5578\uff0c\u8fc7\u4e86\u4e00\u9635\uff0c\u53ea\u542c\u5f97\u53e6\u5916\u4e00\u79cd\u5578\u58f0\u4ece\u5929\u800c\u964d\uff0c\u5165\u8033\u64bc\u5fc3\uff0c\u5c31\u5982\u6709\u4eba\u5728\u8033\u8fb9\u53d1\u5578\u4e00\u822c\uff0c\u529f\u529b \u4e4b\u6df1\uff0c\u5b9e\u662f\u4e0d\u53ef\u601d\u8bae\u3002\u6797\u4ed9\u97f5\u6447\u4e86\u6447\u5934\u9053\uff1a\u201c\u8fd9\u4f4d\u9ad8\u4eba\uff0c\u4eca\u65e5\u4ec0\u4e48\u4eba\u4e5f\u4e0d\u89c1\u3002\u201d \u3000\u3000\u4f46\u8ddd\u79bb\u5df2\u8fd1\uff0c\u4e0d\u6bd4\u65b9\u624d\uff0c\u5f20\u4e39\u67ab\u5df2\u542c\u51fa\u662f\u4ece\u9644\u8fd1\u4e00\u4e2a\u5c71\u5934\u53d1\u51fa\u6765\u7684\uff0c\u7684\u62f1\u624b\u9053\uff1a\u201c\u591a\u8c22 \u6307\u5f15\uff01\u201d\u4e0e\u4e91\u857e\u7b56\u9a6c\u75be\u5954\u3002\u6797\u4ed9\u97f5\u9053\uff1a\u201c\u4f60\u4eec\u4e0d\u5f97\u5141\u53ef\uff0c\u79c1\u95ef\u4e0a\u53bb\uff0c\u60f3\u627e\u6b7b\u4e48\uff1f\u5440\uff0c\u4f60\u4eec\u5e74 \u7eaa\u9752\u9752\uff0c\u6b7b\u4e86\u5c82\u4e0d\u53ef\u60dc\uff1f\u201d\u5f20\u3001\u4e91\u4e8c\u4eba\u54ea\u80af\u542c\u5979\u5520\u53e8\uff0c\u7b56\u9a6c\u5982\u98de\u4e0d\u4e00\u523b\u5c31\u5230\u4e86\u5c71\u811a\uff0c\u5c06\u4e4c\u8499 \u592b\u4e0e\u6797\u4ed9\u97f5\u8fdc\u8fdc\u629b\u5728\u540e\u9762\u3002\u4e24\u4eba\u5c06\u9a6c\u653e\u4e86\uff0c\u65bd\u5c55\u8f7b\u529f\u63d0\u7eb5\u4e4b\u672f\uff0c\u75be\u884c\u4e0a\u5c71\uff0c\u4e0a\u5230\u534a\u5c71\uff0c\u5c71\u98ce \u5439\u6765\u4fbf\u95fb\u5f97\u4e00\u697c\u5f02\u9999\uff0c\u6c81\u4eba\u5fc3\u813e\u3002\u4e91\u857e\u9053\uff1a\u201c\u8fd9\u662f\u6211\u5e08\u5085\u65e5\u5e38\u7528\u7684\u81ea\u5236\u7684\u2018\u767e\u82b1\u9999\u2019\uff01\u201d\u5f20 \u4e39\u67ab\u542c\u4e86\uff0c\u5fc3\u91cc\u4e00\u5bbd\uff0c\u98de\u5929\u9f99\u5973\u679c\u7136\u662f\u5728\u6b64\u5730\u4e86\u3002\u4e24\u4eba\u66f4\u52a0\u5feb\u811a\u6b65\uff0c\u4e0d\u4e00\u523b\u5c31\u5230\u4e86\u5cf0\u5dc5\u3002\u6b63 \u662f\uff1a \u3000\u3000\u60ca\u542c\u5f02\u58f0\u5929\u5916\u5524\uff0c\u5c71\u4e2d\u53c8\u518d\u9047\u5947\u4eba\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u840d\u8e2a\u4fa0\u5f71\u5f55\u300b\u2014\u2014\u7b2c\u4e8c\u5341\u56db\u56de\u3000\u7d2b\u7af9\u6797\u4e2d\u9ad8\u4eba\u8bd5\u53cc\u5251\u3000\u592a\u5e08\u5e9c\u5185\u4fa0\u58eb\u9189\u9999\u95fa \u6881\u7fbd\u751f\u300a\u840d\u8e2a\u4fa0\u5f71\u5f55\u300b \u7b2c\u4e8c\u5341\u56db\u56de\u3000\u7d2b\u7af9\u6797\u4e2d\u9ad8\u4eba\u8bd5\u53cc\u5251\u3000\u592a\u5e08\u5e9c\u5185\u4fa0\u58eb\u9189\u9999\u95fa \u3000\u3000\u5c71\u4e0a\u6709\u4e00\u5ea7\u5c3c\u5eb5\uff0c\u5eb5\u65c1\u4e00\u7247\u7d2b\u7af9\u6797\uff0c\u56f4\u4ee5\u7ea2\u5899\uff0c\u5343\u4e1b\u4fee\u7af9\u9ad8\u903e\u5899\u5934\uff0c\u666f\u81f4\u5341\u5206\u5e7d\u96c5\u3002\u6108 \u8fd1\u90a3\u9999\u6c14\u6108\u6d53\u3002\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u600e\u4e48\u4e0d\u542c\u89c1\u5175\u5668\u78d5\u51fb\u7684\u58f0\u97f3\uff1f\u201d\u4e91\u857e\u4e5f\u662f\u60ca\u7591\u4e0d\u5b9a\uff0c\u62bd\u51fa\u5b9d \u5251\uff0c\u811a\u5c16\u4e00\u70b9\u7acb\u523b\u65bd\u5c55\u4e0a\u4e58\u8f7b\u529f\uff0c\u8eab\u5b50\u5e73\u7a7a\u62d4\u8d77\u3002\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u6b64\u5730\u5b9a\u6709\u524d\u8f88\u9ad8\u4eba\uff0c\u4e0d\u53ef\u5192 \u6627\u3002\u201d\u4f38\u624b\u8981\u62c9\uff0c\u5df2\u6765\u4e0d\u53ca\u3002 \u3000\u3000\u4e91\u857e\u8dc3\u4e0a\u5899\u5934\uff0c\u5ffd\u542c\u5f97\u4e00\u58f0\u51b7\u7b11\uff0c\u597d\u50cf\u6709\u4eba\u5728\u8033\u8fb9\u559d\u9053\uff1a\u201c\u64a4\u5251\uff01\u201d\u58f0\u97f3\u67d4\u6da6\uff0c\u7adf\u4f3c\u5973 \u5b50\u4e4b\u58f0\uff0c\u4e91\u857e\u5fc3\u4e2d\u4e00\u6014\uff0c\u53ea\u89c9\u5251\u67c4\u4e00\u98a4\uff0c\u4f3c\u662f\u88ab\u4ec0\u4e48\u4e1c\u897f\u5f80\u5916\u4e00\u626f\u4f3c\u7684\uff0c\u4e91\u857e\u8eab\u8eaf\u6643\u4e86\u51e0 \u6643\uff0c\u51e0\u4e4e\u8dcc\u4e0b\u5899\u5934\u3002\u5e78\u800c\u5979\u5e74\u6765\u6b66\u529f\u9887\u6709\u8fdb\u5883\uff0c\u5b9d\u5251\u672a\u81f4\u8131\u624b\uff0c\u56de\u5934\u4e00\u671b\uff0c\u53ea\u89c1\u5f20\u4e39\u67ab\u4e5f\u8dc3 \u4e86\u4e0a\u6765\uff0c\u9762\u4e0a\u4ea6\u662f\u9732\u51fa\u60ca\u5f02\u7684\u795e\u8272\u3002\u539f\u6765\u4ed6\u8dc3\u4e0a\u4e4b\u65f6\uff0c\u4e5f\u4e0e\u4e91\u857e\u4e00\u6837\u8033\u8fb9\u4f3c\u542c\u5f97\u6709\u4eba\u559d\u4ee4 \u201c\u64a4\u5251\u201d\u4e4b\u58f0\uff0c\u4ed6\u7684\u529f\u529b\u8f83\u4e91\u857e\u9ad8\u51fa\u4e00\u7b79\uff0c\u7acb\u5373\u8fa8\u51fa\u5fae\u98ce\u98d2\u7136\u7684\u58f0\u606f\uff0c\u6025\u5c06\u8863\u8896\u4e00\u62c2\uff0c\u53ea\u542c \u5f97\u201c\u55e4\u201d\u7684\u4e00\u58f0\u90a3\u201c\u6697\u5668\u201d\u5df2\u9644\u5728\u8896\u4e0a\uff0c\u4f4e\u5934\u4e00\u770b\uff0c\u7adf\u662f\u4e00\u7247\u7af9\u53f6\uff0c\u800c\u4e14\u7adf\u7136\u628a\u81ea\u5df1\u7684\u8863\u8896 \u5212\u4e86\u4e00\u9053\u53e3\u5b50\uff0c\u5c31\u5982\u7528\u8584\u5200\u7247\u62c9\u8fc7\u4e00\u822c\uff0c\u5f20\u4e39\u67ab\u4e5f\u4e0d\u7531\u5f97\u5927\u5403\u4e00\u60ca\uff0c\u8fd9\u79cd\u201c\u6458\u53f6\u98de\u82b1\u4f24\u4eba\u7acb \u6b7b\u201d\u7684\u529f\u592b\uff0c\u53ea\u662f\u542c\u5e08\u7236\u8bf4\u8fc7\uff0c\u81ea\u5df1\u53ef\u8fd8\u662f\u73b0\u5728\u624d\u7b2c\u4e00\u6b21\u89c1\u5230\uff01 \u3000\u3000\u518d\u770b\u4e91\u857e\u90a3\u53e3\u5b9d\u5251\u65f6\uff0c\u53ea\u89c1\u5251\u5203\u88ab\u4e24\u7247\u8584\u8584\u7684\u7af9\u53f6\u5305\u4f4f\uff0c\u4e91\u857e\u7684\u5b9d\u5251\u53ef\u4ee5\u524a\u94c1\u5982\u6ce5\uff0c\u4f46 \u5bf9\u4ed8\u5176\u8584\u5982\u7eb8\u7684\u7af9\u53f6\uff0c\u5374\u662f\u6beb\u65e0\u7740\u529b\u4e4b\u5904\u3002\u771f\u60f3\u4e0d\u5230\u90a3\u4eba\u662f\u600e\u4e48\u7ec3\u7684\uff0c\u7adf\u80fd\u5c06\u7af9\u53f6\u5f53\u6210\u6697 \u5668\uff0c\u800c\u4e14\u6709\u90a3\u4e48\u5927\u7684\u52b2\u529b\u3002\u5c31\u5728\u6b64\u65f6\uff0c\u7af9\u6797\u91cc\u4e5f\u4f20\u51fa\u4e00\u58f0\u60ca\u5947\u7684\u5fae\u201c\u566b\u201d\u58f0\uff0c\u4f3c\u662f\u90a3\u4f4d\u524d\u8f88 \u9ad8\u4eba\uff0c\u5bf9\u5f20\u3001\u4e91\u4e8c\u4eba\u7684\u529f\u529b\uff0c\u4e5f\u9887\u4e3a\u611f\u5230\u610f\u5916\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u5f1f\u5b50\u5f20\u4e39\u67ab\u3001\u4e91\u857e\u8def\u8fc7\u6b64\u5c71\uff0c\u4e0d\u77e5\u524d\u8f88\u5728\u6b64\u8bf7\u6055\u5192\u6627\u3002\u201d\u901a\u544a\u4e4b\u540e\uff0c\u53ea\u542c \u5f97\u5148\u524d\u90a3\u58f0\u97f3\u53c8\u9053\uff1a\u201c\u4f60\u4eec\u4e5f\u662f\u7384\u673a\u9038\u58eb\u7684\u95e8\u4e0b\u5417\uff1f\u597d\uff0c\u90fd\u7ed9\u6211\u4e0b\u6765\u3002\u201d\u5f20\u4e39\u67ab\u544a\u4e86\u4e2a\u7f6a\uff0c \u4e0e\u4e91\u857e\u4e00\u540c\u8dc3\u4e0b\uff0c\u53ea\u89c1\u7af9\u6797\u6df1\u5904\uff0c\u6709\u4e24\u4e2a\u5973\u4eba\u6b63\u5728\u6bd4\u5251\uff0c\u4e00\u4e2a\u662f\u4e2d\u5e74\u7f8e\u5987\uff0c\u53e6\u4e00\u4e2a\u5374\u662f\u767d\u53d1 \u6ee1\u5934\u7684\u8001\u5a46\u5a46\u3002 \u3000\u3000\u4e91\u857e\u53c8\u60ca\u53c8\u559c\uff0c\u53eb\u9053\uff1a\u201c\u5e08\u5085\uff0c\u4f60\u597d\uff01\u662f\u5f1f\u5b50\u6765\u4e86\uff01\u201d\u90a3\u4e2d\u5e74\u7f8e\u5987\u6b63\u5728\u5403\u7d27\uff0c\u53ea\u662f \u201c\u55ef\u201d\u4e86\u4e00\u58f0\uff0c\u7adf\u4e0d\u6562\u5206\u5fc3\u8bf4\u8bdd\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u542c\u4e86\u4e91\u857e\u7684\u79f0\u547c\uff0c\u81ea\u7136\u77e5\u9053\u8fd9\u4e2d\u5e74\u7f8e\u5987\u4fbf\u662f\u98de\u5929\u9f99\u5973\u53f6\u76c8\u76c8\uff0c\u4ed6\u4e45\u95fb\u8fd9\u4f4d\u5e08\u53d4\u7684 \u5251\u6cd5\u4e0e\u81ea\u5df1\u7684\u5e08\u7236\u9f50\u540d\uff0c\u8fd9\u65f6\u4ed4\u7ec6\u4e00\u770b\uff0c\u53ea\u89c1\u5979\u624b\u6301\u4e00\u628a\u666e\u901a\u7684\u9752\u94a2\u5251\uff0c\u6240\u4f7f\u7684\u62db\u6570\u4e0e\u4e91\u857e \u7684\u5251\u6cd5\u76f8\u540c\uff0c\u4f46\u8f7b\u7075\u8fc5\u6377\u4e4b\u5904\uff0c\u5374\u4e0d\u77e5\u9ad8\u51fa\u591a\u5c11\uff01\u5251\u4f7f\u5f97\u5982\u6b64\u8fc5\u75be\uff0c\u4f46\u5374\u4e0d\u95fb\u534a\u70b9\u98ce\u58f0\uff0c\u771f \u6709\u5982\u6d41\u6c34\u884c\u4e91\uff0c\u6781\u5c3d\u795e\u5999\u3002\u5f20\u4e39\u67ab\u5fc3\u9053\uff1a\u201c\u679c\u7136\u540d\u4e0d\u865a\u4f20\u3002\u53ef\u60dc\u6211\u7684\u5e08\u7236\u8fd8\u6ca1\u6709\u8d76\u5230\uff0c\u8981\u4e0d \u7136\u4ed6\u4eec\u4e8c\u4eba\u53cc\u5251\u5408\u74a7\uff0c\u5b9a\u80fd\u6218\u80dc\u8fd9\u4e2a\u8001\u5a46\u5a46\uff01\u201d\u539f\u6765\u98de\u5929\u9f99\u5973\u5df2\u7136\u5389\u5bb3\u4e4b\u6781\uff0c\u4f46\u90a3\u4f4d\u8001\u5a46\u5a46 \u8fd8\u66f4\u8981\u9ad8\u660e\u5f97\u591a\uff0c\u5979\u4f7f\u7684\u53ea\u662f\u7247\u7af9\u7247\u524a\u6210\u5251\u5f62\uff0c\u867d\u7136\u88ab\u98de\u5929\u9f99\u5973\u7684\u5251\u5149\u88f9\u4f4f\uff0c\u4f46\u5f20\u4e39\u67ab\u5374\u770b \u5f97\u51fa\u6765\u98de\u5929\u9f99\u5973\u5374\u662f\u5904\u5904\u88ab\u5979\u514b\u4f4f\u3002 \u3000\u3000\u4f60\u9053\u98de\u5929\u9f99\u5973\u53c8\u662f\u600e\u4e48\u6765\u5230\u8fd9\u7af9\u6797\u7684\uff1f\u539f\u6765\u5979\u8fd9\u6b21\u4e0b\u5c71\uff0c\u6b63\u662f\u5fc3\u4e8b\u91cd\u91cd\u3002\u6f6e\u97f3\u548c\u5c1a\u8981\u5979 \u966a\u540c\u53bb\u8d23\u95ee\u8c22\u5929\u534e\uff0c\u82e5\u7136\u8bc1\u5b9e\u8c22\u5929\u534e\u662f\u53db\u5e08\u6295\u654c\uff0c\u5c31\u8981\u5979\u5408\u529b\u5c06\u8c22\u5929\u534e\u9664\u53bb\u3002\u5979\u4e0e\u8c22\u5929\u534e\u5f7c \u6b64\u6709\u60c5\uff0c\u867d\u7136\u5206\u522b\u4e86\u5341\u4e8c\u5e74\u4ecd\u662f\u5f7c\u6b64\u601d\u5ff5\uff0c\u5979\u7d20\u6765\u77e5\u9053\u8c22\u5929\u534e\u4e3a\u4eba\u7cbe\u7ec6\uff0c\u4ed6\u82e5\u7136\u771f\u662f\u6295\u5230\u5f20 \u5b97\u5468\u95e8\u4e0b\uff0c\u5fc5\u7136\u53e6\u6709\u7528\u5fc3\uff0c\u53ef\u662f\u672a\u77e5\u9053\u786e\u5207\u7684\u4e8b\u5b9e\u4e4b\u524d\uff0c\u5374\u65e0\u6cd5\u8bf4\u670d\u6f6e\u97f3\u548c\u5c1a\u3002\u56e0\u6b64\u5979\u4e5f\u53ea \u597d\u4e0d\u4e3a\u8c22\u5929\u534e\u8fa9\u89e3\uff0c\u5c31\u540c\u6f6e\u97f3\u548c\u5c1a\u4e0b\u5c71\u3002\u5c06\u5230\u96c1\u95e8\u5173\u4e4b\u65f6\uff0c\u5979\u5fc3\u60c5\u52a8\u8361\u4e4b\u6781\uff0c\u4e00\u65b9\u9762\u662f\u56e0\u610f \u4e2d\u4eba\u5373\u5c06\u89c1\u9762\uff0c\u6545\u6b64\u6fc0\u52a8\uff1b\u4e00\u65b9\u9762\u4e5f\u5bb3\u6015\u8c22\u5929\u534e\u4e0d\u80af\u628a\u771f\u6b63\u7684\u4e8b\u5b9e\u8bf4\u51fa\u6765\u3002\u82e5\u7136\u6f6e\u97f3\u548c\u5c1a\u8981 \u5979\u52a8\u624b\u90a3\u5c82\u4e0d\u662f\u5de6\u53f3\u4e3a\u96be\u3002 \u3000\u3000\u5979\u76d8\u7b97\u4e4b\u4e0b\uff0c\u5b9a\u4e86\u4e00\u8ba1\uff0c\u6628\u665a\u5728\u96c1\u95e8\u5173\u5185\u7684\u65c5\u820d\u6295\u5bbf\u4e4b\u65f6\u5979\u5c31\u5bf9\u6f6e\u97f3\u8bf4\uff0c\u8bf4\u662f\u81ea\u5df1\u8fde\u65e5 \u5954\u9a70\uff0c\u4e0d\u60ef\u5173\u5916\u7684\u6c14\u5019\uff0c\u8eab\u4f53\u6709\u70b9\u4e0d\u9002\uff0c\u8fd9\u665a\u51c6\u5907\u8fd0\u7528\u6c14\u529f\u7597\u6cd5\uff0c\u6062\u590d\u7cbe\u795e\uff0c\u6050\u6015\u660e\u65e5\u4e0d\u80fd \u65e9\u8d77\uff0c\u63a8\u8bf4\u6f6e\u97f3\u9a6c\u5feb\uff0c\u53eb\u6f6e\u97f3\u5148\u884c\uff0c\u81ea\u5df1\u968f\u540e\u5373\u5230\u3002\u5176\u5b9e\u5979\u672a\u5230\u56db\u66f4\uff0c\u5c31\u5df2\u5148\u53bb\uff0c\u5979\u662f\u60f3\u8d76 \u5728\u7ea6\u4f1a\u5730\u70b9\u7684\u524d\u9762\uff0c\u5148\u628a\u8c22\u5929\u534e\u622a\u7740\uff0c\u95ee\u660e\u539f\u59d4\u3002\u5979\u987e\u8651\u5230\u8c22\u5929\u534e\u7684\u505a\u6cd5\uff0c\u5fc5\u662f\u4e3a\u4e86\u67d0\u4e00\u673a \u5bc6\u7684\u4e8b\uff0c\u4e5f\u8bb8\u4e0d\u613f\u544a\u77e5\u6f6e\u97f3\u548c\u5c1a\uff0c\u4f46\u5374\u5fc5\u5b9a\u4f1a\u544a\u8bc9\u81ea\u5df1\u3002\u6f6e\u97f3\u548c\u5c1a\u662f\u4e2a\u9c81\u83bd\u4e4b\u4eba\uff0c\u54ea\u77e5\u5e08\u59b9 \u7684\u7528\u5fc3\uff0c\u4ed6\u52a8\u8eab\u4e4b\u65f6\uff0c\u8fd8\u4ee5\u4e3a\u5e08\u59b9\u6b63\u5728\u9163\u7761\u5462\u3002 \u3000\u3000\u98de\u5929\u9f99\u5973\u53f6\u76c8\u76c8\u7684\u8f7b\u529f\u5728\u540c\u95e8\u4e4b\u4e2d\u53f7\u79f0\u7b2c\u4e00\uff0c\u5979\u56db\u66f4\u52a8\u8eab\u5929\u4eae\u4e4b\u540e\uff0c\u5df2\u5230\u4e86\u96c1\u95e8\u5173\uff0c\u518d \u5411\u524d\u884c\uff0c\u610f\u56fe\u4e0e\u8c22\u5929\u534e\u76f8\u649e\u3002\u5979\u6765\u5f97\u592a\u65e9\uff0c\u53c8\u8d70\u4e86\u7ea6\u6478\u4e00\u4e2a\u65f6\u8fb0\uff0c\u4ecd\u672a\u89c1\u8c22\u5929\u534e\u7684\u8e2a\u8ff9\uff0c\u5979 \u4e0d\u7981\u5fc3\u4e2d\u6697\u7b11\uff0c\u7b11\u81ea\u5df1\u592a\u8fc7\u5fc3\u6025\uff0c\u5f53\u4e0b\u653e\u7f13\u811a\u6b65\u8d70\u5165\u4e00\u5904\u5c71\u8c37\u3002\u8fd9\u5c71\u8c37\u6b63\u662f\u4ece\u74e6\u523a\u901a\u5411\u96c1\u95e8 \u5173\u7684\u4e00\u5904\u8981\u9698\uff0c\u8c37\u4e2d\u5730\u6c14\u6696\u548c\uff0c\u5c71\u5761\u4e0a\u6885\u82b1\u6742\u5f00\uff0c\u98ce\u666f\u751a\u7f8e\uff0c\u98de\u5929\u9f99\u5973\u5c31\u5728\u8fd9\u91cc\u7b49\u5019\u8c22\u5929 \u534e\u3002\u5c71\u98ce\u5439\u6765\uff0c\u5ffd\u95fb\u5f97\u4e00\u7f15\u5f02\u9999\uff0c\u6c81\u4eba\u813e\u8151\uff0c\u53f6\u76c8\u76c8\u5fc3\u4e2d\u4e00\u6014\uff0c\u539f\u6765\u8fd9\u79cd\u9999\u5473\u4e43\u662f\u5979\u5728\u5e08\u7236 \u7384\u673a\u9038\u58eb\u7684\u9759\u5ba4\u4e2d\u95fb\u8fc7\u7684\uff0c\u8fd9\u79cd\u9999\u5473\u975e\u5170\u975e\u9e9d\uff0c\u9999\u8fdc\u800c\u6e05\u3002\u5f53\u65f6\u53f6\u76c8\u76c8\u5c31\u5f88\u5947\u602a\uff0c\u5e08\u7236\u5e74\u5df2 \u4e03\u65ec\uff0c\u4e3a\u4f55\u8fd8\u50cf\u81ea\u5df1\u4e00\u6837\u559c\u6b22\u7528\u9999\u6599\uff1f\u4f46\u4ee5\u5e08\u7236\u7684\u5c0a\u4e25\uff0c\u5979\u5f53\u7136\u4e0d\u6562\u591a\u95ee\u3002 \u3000\u3000\u6b64\u9645\uff0c\u5979\u53c8\u95fb\u5230\u8fd9\u79cd\u5f02\u9999\uff0c\u4e0e\u5e08\u7236\u9759\u5ba4\u4e2d\u7684\u90a3\u80a1\u9999\u5473\uff0c\u4e00\u6a21\u4e00\u6837\uff0c\u5fc3\u4e2d\u66f4\u662f\u5947\u602a\u3002\u770b\u770b \u5929\u8272\uff0c\u8ddd\u79bb\u4e2d\u5348\u5c1a\u8fdc\uff0c\u4e0d\u7531\u5f97\u8ffd\u8e2a\u8fd9\u79cd\u9999\u5473\uff0c\u76f4\u4e0a\u5cf0\u5dc5\uff0c\u4f46\u89c1\u4e00\u5ea7\u5c3c\u5eb5\uff0c\u5eb5\u65c1\u4e00\u7247\u7d2b\u7af9\u6797\uff0c \u90a3\u80a1\u5f02\u9999\u5c31\u662f\u4ece\u8fd9\u7247\u7d2b\u7af9\u6797\u98d8\u6563\u51fa\u6765\u7684\u3002 \u3000\u3000\u53f6\u76c8\u76c8\u8d70\u5165\u7d2b\u7af9\u6797\u4e2d\uff0c\u5979\u4e5f\u50cf\u5f20\u4e39\u67ab\u4e0e\u4e91\u857e\u4e00\u6837\uff0c\u53d7\u5230\u90a3\u8001\u5a46\u5a46\u7af9\u53f6\u6697\u5668\u7684\u88ad\u51fb\uff0c\u4ee5\u5979 \u7684\u529f\u529b\uff0c\u5f53\u7136\u4e0d\u4f1a\u53d7\u5230\u4f24\u5bb3\uff0c\u4f46\u4ea6\u5df2\u77e5\u9053\u7d2b\u7af9\u6797\u4e2d\u7684\u9690\u8005\uff0c\u4e00\u5b9a\u662f\u4f4d\u524d\u8f88\u9ad8\u4eba\uff0c\u5f53\u4e0b\u901a\u77e5\u6c42 \u89c1\u9053\uff1a\u201c\u5f1f\u5b50\u7384\u673a\u9038\u58eb\u95e8\uff0c\u8bf7\u95ee\u524d\u8f88\u6cd5\u8bb3\u3002\u201d\u54ea\u77e5\u4e00\u8a00\u752b\u6bd5\uff0c\u53ea\u89c1\u90a3\u8001\u5a46\u5a46\u9762\u8272\u500f\u5730\u4e00\u53d8\uff0c \u53d1\u51fa\u51b7\u51b7\u7684\u7b11\u58f0\u3002 \u3000\u3000\u53f6\u76c8\u76c8\u6b63\u81ea\u60ca\u8be7\uff0c\u90a3\u8001\u5a46\u5a46\u51b7\u51b7\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u662f\u7384\u673a\u9038\u58eb\u7684\u95e8\u4e0b\u4e48\uff1f\u7d20\u95fb\u7384\u673a\u9038\u58eb \u7684\u6b66\u529f\uff0c\u5929\u4e0b\u7b2c\u4e00\uff0c\u4f60\u6562\u4f69\u5251\u5165\u6797\uff0c\u5f53\u7136\u662f\u7cbe\u4e8e\u5251\u6cd5\u7684\u4e86\uff0c\u597d\uff0c\u6211\u5c31\u8bd5\u4f60\u4e00\u8bd5\uff0c\u4ece\u5176\u5f92\u800c\u89c2 \u5176\u5e08\uff0c\u770b\u770b\u7384\u673a\u9038\u58eb\u7684\u5251\u672f\uff0c\u53c8\u6709\u4ec0\u4e48\u522b\u521b\u7684\u65b0\u62db\uff1f\u201d\u53f6\u76c8\u76c8\u542c\u5979\u8fd9\u8bdd\uff0c\u597d\u4f3c\u662f\u4e0e\u81ea\u5df1\u5e08\u7236 \u76f8\u8bc6\uff0c\u54ea\u6562\u52a8\u624b\uff0c\u5f53\u4e0b\u8d54\u7f6a\u8bf4\u9053\uff1a\u201c\u5f1f\u5b50\u4e0d\u77e5\u6b64\u5904\u89c4\u77e9\uff0c\u4e0d\u51c6\u4f69\u5251\u5165\u6797\uff0c\u8bf7\u6055\u5192\u6627\u3002\u201d\u54ea\u77e5 \u8fd9\u8001\u5a46\u5a46\u751a\u662f\u4e0d\u8fd1\u4eba\u60c5\uff0c\u98de\u5929\u9f99\u5973\u8d8a\u63a8\u8f9e\uff0c\u5979\u5c31\u8d8a\u53d1\u751f\u6c14\uff0c\u975e\u903c\u98de\u5929\u9f99\u5973\u52a8\u624b\u4e0d\u53ef\u3002 \u3000\u3000\u98de\u5929\u9f99\u5973\u65e0\u5948\uff0c\u53ea\u597d\u4eae\u51fa\u5251\u6765\uff0c\u9053\uff1a\u201c\u8bf7\u524d\u8f88\u8d50\u62db\u3002\u201d\u90a3\u8001\u5a46\u5a46\u53d6\u4e86\u4e00\u7247\u7af9\u7247\uff0c\u624b\u638c\u524a \u4e86\u51e0\u524a\uff0c\u524a\u6210\u5251\u5f62\uff0c\u9053\uff1a\u201c\u597d\u5427\u4f60\u82e5\u80fd\u524a\u65ad\u6211\u7684\u7af9\u7247\uff0c\u6211\u5c31\u653e\u4f60\u4e0b\u5c71\u3002\u8981\u4e0d\u7136\u4f60\u5c31\u7559\u5728\u8fd9\u513f \u4f34\u6211\uff0c\u7b49\u4f60\u7684\u5e08\u7236\u6765\u5e26\u4f60\u56de\u53bb\u5427\u3002\u201d\u98de\u5929\u9f99\u5973\u4e5f\u662f\u4e00\u526f\u5014\u5f3a\u7684\u6027\u513f\uff0c\u95fb\u8a00\u4e0d\u7981\u5fc3\u5185\u6697\u6697\u751f \u6c14\uff0c\u60f3\u9053\uff1a\u201c\u6211\u7684\u767e\u53d8\u7384\u673a\u5251\u6cd5\u4f55\u7b49\u795e\u5999\uff0c\u5c82\u6709\u524a\u4e0d\u65ad\u8fd9\u7af9\u7247\u4e4b\u7406\uff0c\u6211\u4e0d\u8fc7\u656c\u4f60\u662f\u4f4d\u524d\u8f88\u7f62 \u4e86\uff0c\u96be\u9053\u5f53\u771f\u6015\u4f60\u4e0d\u6210\uff1f\u201d \u3000\u3000\u5f53\u4e0b\u4eae\u5f00\u5251\u5f0f\uff0c\u5404\u81ea\u51fa\u62db\uff0c\u98de\u5929\u9f99\u5973\u5934\u4e00\u62db\u5c31\u7528\u5e08\u95e8\u7684\u7edd\u62db\u201c\u4e91\u9ae6\u4e09\u821e\u201d\uff0c\u4e00\u62db\u4e09\u5f0f\uff0c \u5251\u5c16\u4e00\u70b9\uff0c\u5373\u5206\u6210\u4e09\u8def\u5377\u6765\uff0c\u8981\u5c06\u90a3\u7af9\u7247\u4e00\u4e0b\u7ede\u65ad\u3002\u54ea\u77e5\u8fd9\u8001\u5a46\u5a46\u7684\u6b66\u529f\u771f\u4e2a\u795e\u5947\uff0c\u5979\u7684\u7af9 \u5251\u7adf\u7136\u4ece\u5251\u5149\u5305\u56f4\u4e4b\u4e0b\uff0c\u76f4\u9012\u8fdb\u6765\uff0c\u98de\u5929\u9f99\u5973\u524a\u5979\u7684\u7af9\u5251\uff0c\u5979\u7684\u7af9\u5251\u5374\u662f\u5982\u5f71\u968f\u5f62\uff0c\u9644\u5728\u98de \u5929\u9f99\u5973\u7684\u5251\u4e0a\uff0c\u9976\u662f\u98de\u5929\u9f99\u5973\u4f55\u7b49\u5feb\u6377\uff0c\u5979\u5374\u50cf\u7eb8\u624e\u7684\u4eba\u4e00\u6837\uff0c\u968f\u7740\u98de\u5929\u9f99\u5973\u7684\u5251\u8def\u98d8\u6765\u6643 \u53bb\uff0c\u4f11\u8bf4\u524a\u4e0d\u65ad\u5979\u7684\u7af9\u5251\uff0c\u8fde\u5979\u7684\u8863\u88f3\u4e5f\u6cbe\u4e0d\u7740\u3002\u98de\u5929\u9f99\u5973\u5927\u60ca\uff0c\u632f\u8d77\u7cbe\u795e\uff0c\u4e00\u9635\u5f3a\u653b\uff0c\u90a3 \u8001\u5a46\u5a46\u53ea\u662f\u65bd\u5c55\u7c98\u8fde\u4e8c\u8bc0\uff0c\u5c31\u5c06\u98de\u5929\u9f99\u5973\u7684\u653b\u52bf\uff0c\u8f7b\u63cf\u6de1\u5199\u5730\u4e00\u4e00\u5316\u5f00\uff0c\u8fde\u8fde\u51b7\u7b11\u9053\uff1a\u201c\u7384 \u673a\u9038\u58eb\u6240\u521b\u5251\u6cd5\u4ea6\u4e0d\u8fc7\u5982\u662f\uff0c\u770b\u6765\u4f60\u662f\u6ce8\u5b9a\u8981\u966a\u6211\u8fd9\u8001\u5a46\u5b50\u7684\u4e86\uff01\u201d \u3000\u3000\u65e5\u5f71\u6e10\u6e10\u79fb\u52a8\uff0c\u770b\u770b\u5df2\u5230\u5348\u65f6\uff0c\u98de\u5929\u9f99\u5973\u53c8\u6025\u53c8\u6012\uff0c\u60f3\u8131\u8eab\u53c8\u88ab\u5979\u7684\u7af9\u5251\u7f20\u7740\uff0c\u6446\u8131\u4e0d \u4e86\u3002\u4e8e\u662f\u53d6\u51fa\u5439\u7ba1\uff0c\u53d1\u58f0\u53ec\u5524\u3002\u90a3\u8001\u5a46\u5a46\u542c\u4e86\u4e00\u9635\u9053\uff1a\u201c\u54a6\uff0c\u8fd9\u5439\u7ba1\u5012\u5f88\u6709\u8da3\uff0c\u600e\u4e48\u6211\u8fd9\u7af9 \u6797\u5374\u9009\u4e0d\u51fa\u8fd9\u6837\u597d\u7684\u7af9\u5b50\u5462\uff1f\u8fd9\u5439\u7ba1\u7684\u58f0\u97f3\u4e5f\u5f88\u597d\u542c\uff0c\u501f\u7ed9\u6211\u77a7\u77a7\u884c\u4e0d\u884c\uff1f\u201d\u53f6\u76c8\u76c8\u4e0d\u7406\u4e0d \u776c\uff0c\u4e00\u9762\u4e0e\u5979\u8fc7\u62db\u52a8\u624b\uff0c\u4e00\u9762\u9f13\u8db3\u6c14\u529b\uff0c\u5c06\u7af9\u7ba1\u5439\u5f97\u66f4\u4e3a\u54cd\u4eae\uff0c\u90a3\u8001\u5a46\u5a46\u7af9\u5251\u4e00\u6307\uff0c\u5c06\u53f6\u76c8 \u76c8\u7684\u9752\u94a2\u5251\u7275\u8fc7\u4e00\u8fb9\uff0c\u5de6\u540e\u4e00\u4f38\uff0c\u4fbf\u6765\u62a2\u53f6\u76c8\u76c8\u7684\u5439\u7ba1\u3002\u53f6\u76c8\u76c8\u5728\u5c0f\u5bd2\u5c71\u9762\u58c1\u5341\u4e8c\u5e74\uff0c\u9664\u4e86 \u7cbe\u4fee\u5251\u6cd5\u4e4b\u5916\uff0c\u8fd8\u7ec3\u6210\u4e86\u4e24\u79cd\u6781\u5389\u5bb3\u7684\u529f\u592b\uff0c\u4e00\u79cd\u662f\u6d41\u4e91\u8896\u6cd5\uff0c\u80fd\u7528\u5f69\u8896\u4f5c\u4e3a\u8f6f\u97ad\u5377\u654c\u4eba\u7684 \u5175\u5668\uff1b\u4e00\u79cd\u662f\u4e5d\u661f\u5b9a\u5f62\u9488\uff0c\u80fd\u7528\u98de\u9488\u540c\u65f6\u5c04\u654c\u4eba\u7684\u4e5d\u5904\u7a74\u9053\u3002\u8fd9\u65f6\u89c1\u8001\u5a46\u5a46\u4f38\u624b\u62a2\u5439\u7ba1\uff0c\u53f3 \u8fb9\u9732\u51fa\u7834\u7efd\uff0c\u6025\u5c06\u5f69\u8896\u4e00\u626c\uff0c\u5c31\u628a\u5979\u7684\u7af9\u5251\u5377\u7740\uff0c\u6b63\u60f3\u4e00\u593a\uff0c\u53ea\u542c\u5f97\u55e4\u7684\u4e00\u58f0\uff0c\u5f69\u8896\u5df2\u7ed9\u90a3 \u8001\u5a46\u5a46\u53cc\u6307\u4e00\u5212\u5212\u65ad\u4e86\u4e00\u622a\uff0c\u5439\u7ba1\u4e5f\u7ed9\u5979\u62a2\u53bb\u4e86\u3002\u90a3\u8001\u5a46\u5a46\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u4e00\u624b\u529f\u592b\u8fd8\u7b97\u4e0d \u4fd7\uff0c\u53ef\u60dc\u5185\u52b2\u7a0d\u5dee\uff0c\u8fd8\u662f\u5f04\u4e0d\u65ad\u6211\u7684\u7af9\u5251\uff0c\u6ca1\u8bf4\u7684\uff0c\u4f60\u8fd8\u5f97\u7559\u5728\u8fd9\u91cc\u966a\u6211\u73a9\u73a9\u3002\u201d \u3000\u3000\u90a3\u8001\u5a46\u5a46\u7684\u7af9\u5251\u7ed9\u98de\u5929\u9f99\u5973\u7684\u5f69\u8896\u4e00\u5377\uff0c\u867d\u7136\u77ac\u606f\u4e4b\u95f4\u4fbf\u8131\u4e86\u51fa\u6765\uff0c\u4f46\u4e5f\u7ed9\u9707\u5f00\u4e86\u53c9\uff0c \u4e0d\u8fc7\u672a\u66fe\u6298\u65ad\u3002\u800c\u98de\u5929\u9f99\u5973\u7684\u8863\u8896\u5374\u7ed9\u5979\u5212\u65ad\u4e00\u622a\uff0c\u5439\u7ba1\u53c8\u88ab\u62a2\u53bb\uff0c\u6bd4\u5bf9\u8d77\u6765\uff0c\u81ea\u662f\u90a3\u8001\u5a46 \u5a46\u5927\u5360\u4e0a\u98ce\u3002\u4f46\u5979\u7684\u8f88\u5206\u6781\u5c0a\uff0c\u89c1\u98de\u5929\u9f99\u5973\u6709\u8fd9\u4e00\u624b\u529f\u592b\uff0c\u4e5f\u4e0d\u7981\u6697\u6697\u4f69\u670d\u3002\u98de\u5929\u9f99\u5973\u5403\u4e86 \u5927\u4e8f\uff0c\u7b2c\u4e8c\u5957\u7edd\u6280\u53c8\u63a5\u8fde\u800c\u81f3\uff0c\u624b\u6307\u4e00\u5f39\uff0c\u628a\u5939\u5728\u6307\u7aef\u7684\u4e5d\u661f\u5b9a\u5f62\u9488\u63a5\u8fde\u98de\u51fa\uff0c\u8fd9\u4e5d\u661f\u5b9a\u5f62 \u9488\u53ef\u4ee5\u540c\u65f6\u6253\u4e5d\u5904\u7a74\u9053\uff0c\u5389\u5bb3\u975e\u5e38\u3002\u90a3\u8001\u5a46\u5a46\u5c06\u5439\u7ba1\u62a2\u4e86\u4e4b\u540e\uff0c\u968f\u5373\u7b11\u9053\uff1a\u201c\u8fd9\u73a9\u610f\u513f\u5012\u6709 \u8da3\uff0c\u6211\u5439\u5439\u770b\u3002\u201d\u51d1\u8fd1\u5507\u8fb9\u4e00\u5439\uff0c\u53d1\u58f0\u6e05\u8d8a\uff0c\u6bd4\u98de\u5929\u9f99\u5973\u5c1a\u80dc\u51e0\u5206\uff0c\u98de\u5929\u9f99\u5973\u7684\u4e5d\u661f\u5b9a\u5f62\u9488 \u521a\u521a\u53d1\u51fa\uff0c\u88ab\u5979\u7684\u5439\u7ba1\u4e00\u5439\uff0c\u90fd\u98de\u6563\u4e86\u3002\u90a3\u8001\u5a46\u5a46\u7b11\u9053\uff1a\u201c\u4f60\u7684\u5251\u6cd5\u8fd8\u672a\u5c3d\u5c55\u6240\u957f\uff0c\u54b1\u4eec\u8fd8 \u662f\u6bd4\u5251\u7684\u597d\u3002\u201d\u7af9\u5251\u4e00\u6325\uff0c\u53c8\u628a\u98de\u5929\u9f99\u5973\u7684\u9752\u94a2\u5251\u80f6\u7740\u4e86\u3002 \u3000\u3000\u65e5\u8fc7\u4e2d\u5929\uff0c\u76f8\u6597\u5df2\u6709\u4e00\u4e2a\u591a\u65f6\u8fb0\uff0c\u98de\u5929\u9f99\u5973\u5140\u662f\u8131\u4e0d\u4e86\u8eab\u60f3\u8d77\u8c22\u5929\u534e\u8fd9\u65f6\u5df2\u5e94\u5230\u96c1\u95e8\u5173 \u5916\u7684\u7ea6\u4f1a\u5730\u70b9\uff0c\u5439\u7ba1\u4e4b\u58f0\u4e0d\u77e5\u4ed6\u80fd\u5426\u542c\u5230\uff0c\u5fc3\u4e2d\u65e2\u662f\u7126\u6025\uff0c\u53c8\u662f\u70e6\u607c\u3002\u5ffd\u89c1\u5916\u9762\u6709\u4eba\u8df3\u5165\uff0c \u521d\u65f6\u8fd8\u4ee5\u4e3a\u662f\u8c22\u5929\u534e\uff0c\u5374\u4e0d\u6599\u662f\u81ea\u5df1\u7684\u7231\u5f92\u4e91\u857e\uff0c\u4e91\u857e\u7684\u8eab\u540e\u8fd8\u6709\u4e00\u4e2a\u4fca\u6717\u7684\u5c11\u5e74\u3002\u98de\u5929\u9f99 \u5973\u672a\u66fe\u89c1\u8fc7\u5f20\u4e39\u67ab\uff0c\u4f46\u53ea\u4e00\u77a5\u4e4b\u95f4\u5df2\u611f\u5230\u4ed6\u7709\u5b87\u4e4b\u95f4\u9690\u9690\u8574\u85cf\u7684\u82f1\u6c14\uff0c\u89c9\u5f97\u8fd9\u4eba\u7684\u672c\u9886\uff0c\u65ad \u4e0d\u4f1a\u5728\u81ea\u5df1\u7684\u5f92\u5f1f\u4e4b\u4e0b\u3002 \u3000\u3000\u4e91\u857e\u89c1\u5e08\u5085\u6218\u90a3\u8001\u5a46\u5a46\u4e0d\u4e0b\uff0c\u751a\u662f\u60ca\u5947\uff0c\u4e0e\u5f20\u4e39\u67ab\u6253\u4e86\u4e00\u4e2a\u773c\u8272\uff0c\u4e0a\u524d\u8bf4\u9053\uff1a\u201c\u5e08\u5085\uff0c \u6709\u4e8b\u5f1f\u5b50\u670d\u5176\u52b3\uff0c\u8bf7\u8ba9\u6211\u4eec\u63a5\u8fd9\u4f4d\u8001\u524d\u8f88\u51e0\u62db\uff0c\u4e5f\u597d\u957f\u70b9\u89c1\u8bc6\u3002\u201d\u98de\u5929\u9f99\u5973\u770b\u4e86\u4ed6\u4eec\u4e00\u773c\uff0c \u5fc3\u60f3\u8fd9\u8001\u5a46\u5b50\u8fde\u6211\u4e5f\u6597\u4e0d\u8fc7\u4f60\u4eec\u7109\u80fd\u63a5\u624b\uff0c\u771f\u662f\u4e0d\u77e5\u5929\u9ad8\u5730\u539a\u3002\u4f46\u8fd9\u8bdd\u5374\u4e0d\u597d\u5728\u5916\u4eba\u9762\u524d\u8bf4 \u51fa\uff0c\u6b63\u81ea\u8e0c\u8e87\uff0c\u90a3\u8001\u5a46\u5a46\u5374\u5ffd\u5730\u628a\u7af9\u5251\u4e00\u6536\u8df3\u51fa\u5708\u5b50\uff0c\u7b11\u9053\uff1a\u201c\u597d\uff0c\u6211\u6700\u6b22\u559c\u6709\u80c6\u8bc6\u7684\u5c11\u5e74 \u4eba\uff0c\u4f60\u4eec\u662f\u7384\u673a\u9038\u58eb\u7684\u7b2c\u4e09\u4ee3\u5f1f\u5b50\u5417\uff1f\u5b66\u4e86\u4e9b\u4ec0\u4e48\u672c\u9886\uff0c\u4e0a\u6765\u8bd5\u7ed9\u6211\u770b\u3002\u201d \u3000\u3000\u98de\u5929\u9f99\u5973\u677e\u4e86\u53e3\u6c14\uff0c\u542c\u90a3\u8001\u5a46\u5a46\u7684\u8bf4\u6cd5\uff0c\u5e76\u65e0\u6076\u610f\uff0c\u6599\u5979\u4e0d\u4f1a\u5bf9\u4e24\u4e2a\u5c0f\u8f88\u65bd\u5c55\u6740\u624b\uff0c\u4fbf \u9053\uff1a\u201c\u597d\uff0c\u4f60\u4eec\u5c0f\u5fc3\u63a5\u8fd9\u4f4d\u8001\u524d\u8f88\u51e0\u62db\u5427\u3002\u201d \u3000\u3000\u90a3\u8001\u5a46\u5a46\u4e1d\u6beb\u4e0d\u4ee5\u4e3a\u610f\uff0c\u5f00\u53c9\u7684\u7af9\u5251\u6a2a\u5728\u80f8\u524d\uff0c\u9053\uff1a\u201c\u600e\u4e48\u4e0d\u8fdb\u62db\u5440\uff01\u201d\u5f20\u4e39\u67ab\u4e0e\u4e91\u857e \u5404\u629a\u5251\u67c4\uff0c\u65bd\u4e86\u4e00\u793c\uff0c\u9053\uff1a\u201c\u8bf7\u8001\u524d\u8f88\u6307\u6559\uff01\u201d\u9661\u7136\u95f4\u53cc\u5251\u9f50\u51fa\uff0c\u4e00\u5de6\u4e00\u53f3\uff0c\u5251\u5230\u4e2d\u9014\uff0c\u5ffd \u5730\u5408\u6210\u4e00\u4e2a\u5706\u5b64\uff0c\u62e6\u8170\u75be\u526a\uff01 \u3000\u3000\u90a3\u8001\u5a46\u5a46\u521d\u610f\u4ee5\u4e3a\u8fd9\u4e24\u4eba\u65e2\u662f\u7384\u673a\u9038\u58eb\u7684\u7b2c\u4e09\u4ee3\u5f1f\u5b50\uff0c\u529f\u592b\u518d\u597d\u4e5f\u597d\u4e0d\u5230\u54ea\u91cc\u53bb\uff0c\u4e0e\u4ed6 \u4eec\u5bf9\u62db\uff0c\u5b8c\u5168\u662f\u4ee5\u4e00\u79cd\u620f\u800d\u7684\u5fc3\u60c5\u51fa\u4e4b\uff0c\u4e07\u4e07\u6599\u4e0d\u5230\u53cc\u5251\u5408\u74a7\uff0c\u5389\u5bb3\u5982\u65af\uff01\u4e00\u89c1\u8fd9\u5251\u52bf\u7684\u51f6 \u731b\u5a01\u529b\uff0c\u4e0d\u7531\u5f97\u5927\u5403\u4e00\u60ca\uff0c\u76f8\u8ddd\u6781\u8fd1\uff0c\u8981\u65bd\u5c55\u7c98\u8fde\u4e4b\u8bc0\uff0c\u4ea6\u6765\u4e0d\u53ca\u3002\u8fd9\u5239\u90a3\u95f4\uff0c\u53ea\u89c1\u94f6\u8679\u73af \u7ed5\u4e4b\u4e2d\uff0c\u4e00\u6761\u9ed1\u5f71\u51cc\u7a7a\u98de\u8d77\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u5de6\u8098\u75be\u8d77\uff0c\u4e00\u649e\u4e91\u857e\uff0c\u5c06\u4e91\u857e\u649e\u5f97\u9000\u540e\u51e0\u6b65\uff0c\u53ea\u89c1\u90a3\u8001\u5a46\u5a46\u5df2\u7b11\u541f\u541f\u5730\u53c8\u62e6\u5728\u81ea \u5df1\u7684\u9762\u524d\uff0c\u5927\u58f0\u8d5e\u9053\uff1a\u201c\u597d\uff01\u5c11\u5e74\u4eba\u518d\u6765\uff0c\u518d\u6765\uff01\u201d\u539f\u6765\u90a3\u8001\u5a46\u5a46\u56e0\u6025\u8feb\u4e4b\u95f4\uff0c\u7528\u7af9\u5251\u62db\u67b6 \u5df2\u6765\u4e0d\u53ca\uff0c\u53ea\u597d\u65bd\u5c55\u5e73\u751f\u7edd\u6280\uff0c\u4e00\u4e2a\u201c\u7ec6\u80f8\u5de7\u7ffb\u4e91\u201d\u98de\u8dc3\u8d77\u6765\uff0c\u5012\u7eb5\u4e08\u8bb8\uff0c\u800c\u5c31\u5728\u8fd9\u4e00\u8dc3\u4e00 \u7eb5\u4e4b\u95f4\uff0c\u8863\u8896\u5de6\u53f3\u4e00\u62c2\uff0c\u5c06\u53cc\u5251\u8361\u5f00\uff0c\u8fd9\u8001\u5a46\u5a46\u6570\u5341\u5e74\u529f\u529b\uff0c\u5c82\u6bd4\u5bfb\u5e38\uff0c\u53cc\u8896\u4e00\u62c2\uff0c\u529b\u903e\u5343 \u65a4\uff0c\u4e0d\u4f46\u628a\u53cc\u5251\u8361\u5f00\uff0c\u4f59\u52bf\u672a\u5c3d\uff0c\u52bf\u5c06\u62c2\u5230\u4e8c\u4eba\u8eab\u4e0a\u3002\u5f20\u4e39\u67ab\u8bc6\u5f97\u5389\u5bb3\uff0c\u6545\u6b64\u6025\u5fd9\u65bd\u5c55\u5de7 \u529b\uff0c\u5c06\u4e91\u857e\u649e\u9000\u51e0\u6b65\uff0c\u81ea\u5df1\u4e5f\u8fde\u5fd9\u95ea\u5f00\u907f\u5f00\u950b\u9510\uff0c\u8fd9\u624d\u5f97\u4ee5\u4e24\u65e0\u4f24\u635f\u3002 \u3000\u3000\u90a3\u8001\u5a46\u5a46\u88ab\u8feb\u65bd\u5c55\u7edd\u62db\uff0c\u6b63\u81ea\u540e\u6094\uff0c\u751f\u6015\u91cd\u4f24\u4e86\u8fd9\u4e24\u4e2a\u5c11\u5e74\u4eba\uff0c\u5c82\u4e0d\u53ef\u60dc\uff0c\u5ffd\u89c1\u5f20\u4e39\u67ab \u6296\u9732\u4e86\u8fd9\u4e00\u624b\u4e0a\u4e58\u7684\u529f\u592b\uff0c\u4e0d\u7981\u53c8\u662f\u60ca\u5947\uff0c\u53c8\u662f\u6b22\u559c\uff0c\u5f53\u4e0b\u7af9\u5251\u4e00\u6325\uff0c\u62a2\u5148\u5c01\u7740\u4e8c\u4eba\u7684\u5251 \u8def\uff0c\u53c8\u518d\u4ea4\u950b\u3002 \u3000\u3000\u8fd9\u4e00\u6b21\u8001\u5a46\u5a46\u5df2\u77e5\u9053\u53cc\u5251\u5408\u74a7\u7684\u5a01\u529b\uff0c\u518d\u4e5f\u4e0d\u6562\u4ee5\u6e38\u620f\u7684\u6001\u5ea6\u51fa\u4e4b\uff0c\u7af9\u5251\u76d8\u65cb\u98de\u821e\uff0c\u6bd4 \u6597\u98de\u5929\u9f99\u5973\u4e4b\u65f6\u66f4\u662f\u8ba4\u771f\u3002\u5f20\u3001\u4e91\u4e8c\u4eba\u4ea6\u662f\u7aed\u5168\u529b\uff0c\u628a\u53cc\u5251\u5408\u74a7\u7684\u5a01\u529b\u5c3d\u91cf\u53d1\u6325\uff0c\u5947\u62db\u5999 \u7740\uff0c\u5c42\u51fa\u4e0d\u7a77\uff0c\u5728\u4e94\u5341\u62db\u4e4b\u5185\u90a3\u8001\u5a46\u5a46\u7adf\u7136\u5360\u4e0d\u5230\u4ed6\u4eec\u534a\u70b9\u4fbf\u5b9c\u3002 \u3000\u3000\u98de\u5929\u9f99\u5973\u5728\u65c1\u8fb9\u770b\u5f97\u5446\u4e86\uff0c\u8fd9\u5c11\u5e74\u7684\u5251\u6cd5\u548c\u81ea\u5df1\u6388\u4e0e\u4e91\u857e\u7684\u5251\u6cd5\u7adf\u7136\u914d\u5408\u5f97\u5999\u5230\u6beb\u5dc5\uff0c \u6bcf\u62db\u51fa\u624b\uff0c\u90fd\u662f\u6781\u5176\u81ea\u7136\uff0c\u597d\u50cf\u5404\u4f7f\u5404\u7684\uff0c\u6709\u5982\u5e73\u65f6\u7ec3\u4e60\u5251\u672f\u4e00\u822c\uff0c\u53cc\u5251\u4e00\u8054\uff0c\u5374\u53c8\u5982\u5929\u8863 \u65e0\u7f1d\uff0c\u65e0\u61c8\u53ef\u51fb\u3002\u66f4\u5947\u602a\u7684\u662f\uff0c\u5f20\u4e39\u67ab\u6240\u4f7f\u7684\u5251\u6cd5\uff0c\u98de\u5929\u9f99\u5973\u611f\u5230\u975e\u5e38\u719f\u8bc6\uff0c\u4f46\u5374\u53c8\u8bf4\u4e0d\u51fa \u540d\u6765\u3002\u98de\u5929\u9f99\u5973\u4e0d\u7981\u6697\u6697\u79f0\u5947\uff0c\u5fc3\u4e2d\u4e00\u52a8\uff0c\u60f3\u9053\uff1a\u201c\u5f53\u5e74\u5e08\u7236\u5c06\u4e24\u5957\u5251\u6cd5\uff0c\u5206\u6388\u8c22\u5929\u534e\u4e0e \u6211\uff0c\u4e0d\u8bb8\u4e92\u76f8\u4f20\u6388\u3002\u96be\u9053\u8fd9\u5c11\u5e74\u6240\u4f7f\u7684\u5251\u6cd5\uff0c\u5c31\u662f\u6211\u6240\u672a\u89c1\u8fc7\u7684\u3001\u8c22\u5929\u534e\u6240\u5f97\u7684\u90a3\u5957\u5251 \u6cd5\uff1f\u201d \u3000\u3000\u8fd9\u65f6\u573a\u4e2d\u6597\u5f97\u8d8a\u53d1\u6fc0\u70c8\uff0c\u65f6\u95f4\u4e00\u4e45\uff0c\u90a3\u8001\u5a46\u5a46\u6e10\u6e10\u5360\u4e86\u4e0a\u98ce\uff0c\u5979\u624b\u4e2d\u4f7f\u7684\u867d\u662f\u7af9\u5251\uff0c\u4f46 \u529b\u900f\u5251\u5c16\uff0c\u8feb\u8fc7\u6765\u65f6\uff0c\u5374\u5982\u5929\u98ce\u6d77\u96e8\uff0c\u538b\u5f97\u4e0d\u900f\u4e0d\u8fc7\u6c14\u6765\u3002\u5f20\u3001\u4e91\u4e8c\u4eba\u81ea\u7ed3\u8bc6\u4e4b\u540e\uff0c\u53cc\u5251\u5408 \u74a7\u6240\u5411\u65e0\u654c\uff0c\u5373\u4e4c\u8499\u592b\u4e0e\u91d1\u94a9\u4ed9\u5b50\u6797\u4ed9\u97f5\u4e8c\u4eba\u8054\u624b\uff0c\u4e5f\u4e0d\u8fc7\u4e0e\u4ed6\u4eec\u6253\u4e2a\u5e73\u624b\uff0c\u60f3\u4e0d\u5230\u8fd9\u8001\u5a46 \u5a46\u7528\u4e00\u67c4\u7af9\u5251\uff0c\u4e0d\u4f46\u80fd\u5c06\u53cc\u5251\u5408\u74a7\u7684\u5a01\u529b\uff0c\u4e00\u4e00\u5316\u89e3\uff0c\u800c\u4e14\u8fd8\u80fd\u7740\u7740\u62a2\u5148\uff0c\u5c06\u5f20\u4e39\u67ab\u4e0e\u4e91\u857e \u6740\u5f97\u53ea\u6709\u62db\u67b6\u4e4b\u529f\uff0c\u65e0\u8fd8\u624b\u4e4b\u529b\u3002\u5f20\u4e39\u67ab\u6b63\u60f3\u8ba4\u8f93\uff0c\u5ffd\u542c\u5f97\u90a3\u8001\u5a46\u5a46\u53eb\u9053\uff1a\u201c\u6765\u7684\u662f\u8c01\uff1f\u7ed9 \u6211\u64a4\u5251\uff01\u201d\u6325\u5251\u65cb\u8eab\u4e4b\u9645\uff0c\u6458\u4e86\u4e00\u628a\u7af9\u53f6\uff0c\u7528\u201c\u6ee1\u5929\u82b1\u96e8\u201d\u7684\u624b\u6cd5\uff0c\u98de\u6d12\u51fa\u53bb\u3002\u8fd9\u970e\u90a3\u95f4\u53ea \u542c\u5f97\u4e00\u7247\u55e4\u55e4\u58f0\u54cd\uff0c\u5341\u51e0\u7247\u7af9\u53f6\u5728\u7a7a\u4e2d\u98de\u821e\uff0c\u8f7b\u98d8\u98d8\u5730\u843d\u4e86\u4e0b\u6765\u3002\u8fd9\u8001\u5a46\u5a46\u4e5f\u6599\u5230\u6765\u4eba\u662f\u4e2a \u5f3a\u654c\uff0c\u6240\u4ee5\u51fa\u624b\u5c31\u662f\u5341\u51e0\u7247\u7af9\u53f6\u6697\u5668\uff0c\u54ea\u77e5\u8fd8\u662f\u4e0d\u80fd\u5c06\u6765\u4eba\u7684\u5175\u5668\u6253\u7529\uff0c\u770b\u6765\u8fd9\u4eba\u7684\u529f\u529b\u6bd4 \u98de\u5929\u9f99\u5973\u8fd8\u80dc\u4e00\u7b79\u3002 \u3000\u3000\u98de\u5929\u9f99\u5973\u773c\u775b\u4e00\u4eae\uff0c\u53ea\u89c1\u5899\u5934\u4e0a\u7684\u4eba\u8f7b\u8f7b\u8df3\u4e0b\uff0c\u4e0d\u662f\u522b\u4eba\u6b63\u662f\u5341\u4e8c\u5e74\u6765\u82e6\u82e6\u601d\u5ff5\u7684\u8c22\u5929 \u534e\u3002\u8c22\u5929\u534e\u9053\uff1a\u201c\u56db\u59b9\u4f60\u597d\u3002\u201d\u53f6\u76c8\u76c8\u9053\uff1a\u201c\u4e09\u54e5\uff0c\u4f60\u597d\uff0c\u89c1\u5230\u4e8c\u5e08\u5144\u4e86\u5417\uff1f\u201d\u8c22\u5929\u534e\u6b63\u60f3 \u7b54\u8bdd\uff0c\u53ea\u542c\u5f97\u90a3\u8001\u5a46\u5a46\u53eb\u9053\uff1a\u201c\u4f60\u4e5f\u662f\u7384\u673a\u9038\u58eb\u7684\u95e8\u4e0b\u5417\uff1f\u6765\uff0c\u6765\uff0c\u4f60\u4e5f\u6765\u8bd5\u51e0\u62db\u3002\u201d\u8c22\u5929 \u534e\u4e00\u7b11\u9053\uff1a\u201c\u56db\u59b9\uff0c\u54b1\u4eec\u4e14\u5148\u522b\u53d9\u522b\u60c5\uff0c\u96be\u5f97\u5728\u6b64\u9047\u5230\u9ad8\u4eba\uff0c\u54b1\u4eec\u4e14\u5408\u7ec3\u4e00\u5957\u5251\u6cd5\u3002\u4e39\u67ab\uff0c \u4f60\u4eec\u4e0d\u662f\u8fd9\u4f4d\u8001\u524d\u8f88\u7684\u5bf9\u624b\uff0c\u8fd8\u4e0d\u8ba4\u8f93\u5417\uff1f\u201d\u5f20\u4e39\u67ab\u4e0e\u4e91\u857e\u53cc\u5251\u4e00\u6536\uff0c\u9000\u51fa\u5708\u5b50\uff0c\u4ecd\u7136\u5404\u81ea \u624b\u629a\u5251\u67c4\uff0c\u65bd\u4e86\u4e00\u793c\uff0c\u9053\uff1a\u201c\u8c22\u8001\u524d\u8f88\u8d50\u62db\uff0c\u589e\u76ca\u4e0d\u5c11\u3002\u201d\u6c14\u5b9a\u795e\u95f2\u867d\u8d25\u4e0d\u4e71\u3002\u90a3\u8001\u5a46\u5a46 \u9053\uff1a\u201c\u4f60\u4eec\u4e8c\u4eba\u80fd\u63a5\u5230\u4e94\u5341\u62db\u5f00\u5916\uff0c\u4e5f\u4e0d\u80fd\u7b97\u8f93\u4e86\u3002\u597d\uff0c\u6362\u4f60\u4eec\u7684\u5e08\u7236\u4e0a\u6765\u3002\u201d \u3000\u3000\u98de\u5929\u9f99\u5973\u5598\u606f\u5df2\u8fc7\uff0c\u9053\uff1a\u201c\u6211\u4eec\u4e5f\u662f\u4e24\u4eba\u9f50\u4e0a\u3002\u201d\u90a3\u8001\u5a46\u5a46\u9053\uff1a\u201c\u8fd9\u4fbf\u6700\u597d\u4e0d\u8fc7\uff0c\u6211\u6b63 \u60f3\u89c1\u8bc6\u89c1\u8bc6\u7384\u673a\u9038\u58eb\u95e8\u6700\u7cbe\u5999\u7684\u6b66\u529f\u3002\u201d\u8c22\u5929\u534e\u77a5\u4e86\u90a3\u8001\u5a46\u5a46\u4e00\u773c\uff0c\u5ffd\u9053\uff1a\u201c\u8001\u524d\u8f88\u4e0e\u5bb6\u5e08 \u7684\u6e0a\u6e90\uff0c\u53ef\u80fd\u8d50\u544a\u4e48\uff1f\u201d\u90a3\u8001\u5a46\u5a46\u5ffd\u5730\u52c3\u7136\u53d1\u6012\uff0c\u9053\uff1a\u201c\u7384\u673a\u9038\u58eb\u81ea\u8d1f\u5929\u4e0b\u7b2c\u4e00\uff0c\u6211\u8fd9\u4e2a\u8001 \u5a46\u5b50\u5c82\u6562\u9ad8\u6500\u3002\u4f60\u4eec\u4e5f\u4e0d\u5fc5\u5957\u4ec0\u4e48\u4ea4\u60c5\uff0c\u628a\u7384\u673a\u9038\u58eb\u6240\u6388\u7684\u6b66\u529f\u5c3d\u91cf\u65bd\u5c55\u4fbf\u662f\u3002\u201d\u98de\u5929\u9f99\u5973 \u597d\u751f\u8be7\u5f02\uff0c\u542c\u8fd9\u8001\u5a46\u5a46\u7684\u8bed\u6c14\uff0c\u7adf\u662f\u4e0e\u81ea\u5df1\u7684\u5e08\u7236\u6709\u4ec0\u4e48\u5fc3\u75c5\u8fc7\u8282\u7684\u3002\u53ea\u89c1\u8c22\u5929\u534e\u5fae\u5fae\u4e00 \u7b11\uff0c\u9053\uff1a\u201c\u606d\u656c\u4e0d\u5982\u4ece\u547d\uff0c\u90a3\u5c31\u8bf7\u6055\u5c0f\u8f88\u65e0\u793c\u4e86\u3002\u201d\u624b\u8155\u4e00\u7ffb\uff0c\u5237\u7684\u4e00\u5251\u523a\u51fa\uff0c\u98de\u5929\u9f99\u5973\u4e5f \u8ddf\u7740\u968f\u624b\u523a\u4e86\u4e00\u5251\u3002\u98de\u5929\u9f99\u5973\u8fd9\u4e00\u5251\u672c\u6765\u662f\u4e00\u62db\u8d77\u624b\u7684\u62db\u5f0f\u6781\u4e3a\u5bfb\u5e38\uff0c\u5979\u4e5f\u4e0d\u5e0c\u671b\u8fd9\u4e00\u62db\u5c31 \u80fd\u7ed9\u654c\u4eba\u4ec0\u4e48\u5a01\u80c1\uff0c\u54ea\u77e5\u53cc\u5251\u4e00\u5408\uff0c\u5a01\u529b\u51fa\u4eba\u610f\u8868\uff0c\u867d\u662f\u6700\u5bfb\u5e38\u7684\u62db\u5f0f\uff0c\u7adf\u628a\u90a3\u8001\u5a46\u5a46\u903c\u5f97 \u8fde\u9000\u4e09\u6b65\u3002\u98de\u5929\u9f99\u5973\u4e0d\u7981\u5927\u559c\uff0c\u5fc3\u9053\uff1a\u201c\u5e08\u7236\u6240\u521b\u7684\u5251\u6cd5\uff0c\u679c\u7136\u662f\u795e\u5999\u5f97\u4e0d\u53ef\u601d\u8bae\uff01\u201d \u3000\u3000\u8c22\u3001\u53f6\u4e8c\u4eba\u6240\u4f7f\u7684\u5251\u6cd5\u4e0e\u5f20\u3001\u4e91\u4e8c\u4eba\u9002\u624d\u6240\u7528\u7684\u4e00\u6a21\u4e00\u6837\u4f46\u529f\u529b\u4e0d\u540c\uff0c\u5a01\u529b\u53c8\u5f3a\u4e86\u51e0 \u500d\u3002\u90a3\u8001\u5a46\u5a46\u9053\uff1a\u201c\u4eca\u65e5\u624d\u89c1\u8bc6\u7384\u673a\u9038\u58eb\u7684\u771f\u6b63\u6b66\u529f\u3002\u201d\u7af9\u5251\u4e00\u6296\uff0c\u987f\u65f6\u53ea\u89c1\u7d2b\u7af9\u6797\u4e2d\uff0c\u56db \u9762\u516b\u65b9\u90fd\u662f\u90a3\u8001\u5a46\u5a46\u7684\u8eab\u5f62\uff0c\u767d\u53d1\u98d8\u62c2\uff0c\u8863\u8896\u6325\u821e\uff0c\u7af9\u53f6\u98d8\u843d\uff0c\u914d\u4e0a\u7af9\u5251\u7684\u795e\u5947\u62db\u6570\uff0c\u5a01\u529b \u4e5f\u715e\u662f\u60ca\u4eba\uff01\u8c22\u5929\u534e\u4e0d\u614c\u4e0d\u5fd9\uff0c\u53cc\u8db3\u9489\u7262\u5730\u9762\uff0c\u5c06\u5e08\u4f20\u5251\u6cd5\uff0c\u4e00\u4e00\u4f7f\u5f00\uff0c\u53f6\u76c8\u76c8\u4e5f\u5b66\u4ed6\u7684\u6837 \u5b50\uff0c\u628a\u767e\u53d8\u7384\u673a\u5251\u6cd5\uff0c\u4f7f\u5f97\u98ce\u96e8\u4e0d\u900f\uff0c\u53cc\u5251\u77e2\u77eb\uff0c\u6709\u5982\u7389\u9f99\u76f8\u6597\uff0c\u4efb\u90a3\u8001\u5a46\u5a46\u7684\u8eab\u5f62\u5982\u4f55\u98d8 \u5ffd\uff0c\u5982\u4f55\u6df1\u582a\uff0c\u5374\u603b\u88ab\u53cc\u5251\u62e6\u4f4f\uff0c\u4e0d\u80fd\u8fdb\u5230\u79bb\u4e8c\u4eba\u516b\u5c3a\u4e4b\u5185\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u4e0e\u4e91\u857e\u770b\u5f97\u76ee\u7729\u795e\u8ff7\uff0c\u8d8a\u53d1\u9886\u609f\u53cc\u5251\u5408\u74a7\u7684\u5999\u7528\u3002\u4e24\u65b9\u25a1\u62fc\u4e86\u7ea6\u6709\u4e94\u5341\u62db\uff0c\u5ffd\u542c \u5f97\u8c22\u5929\u534e\u53eb\u9053\uff1a\u201c\u8bf7\u6055\u5c0f\u8f88\u5192\u72af\u4e86\u3002\u201d\u8eab\u5f62\u75be\u8d77\uff0c\u6709\u5982\u5927\u96c1\uff0c\u53f6\u76c8\u76c8\u4e5f\u4e00\u4e2a\u76d8\u65cb\uff0c\u98de\u8eab\u53cd\u624b \u4e00\u524a\uff0c\u53cc\u5251\u51fa\u624b\u9aa4\u653b\uff0c\u53ea\u542c\u5f97\u88c2\u5e1b\u4e4b\u58f0\u4e0e\u7834\u7af9\u4e4b\u58f0\u540c\u65f6\u53d1\u51fa\uff0c\u90a3\u8001\u5a46\u5a46\u7684\u7af9\u5251\u88ab\u524a\u4e3a\u56db\u7247\uff0c \u4e24\u8fb9\u7684\u8863\u8896\u4e5f\u90fd\u7ed9\u5272\u4e86\u4e00\u622a\uff01 \u3000\u3000\u8c22\u5929\u534e\u4e0e\u53f6\u76c8\u76c8\u540c\u65f6\u6536\u5251\uff0c\u8fde\u9053\uff1a\u201c\u5f97\u7f6a\u3002\u201d\u90a3\u8001\u5a46\u5a46\u5f03\u4e86\u7af9\u5251\uff0c\u9893\u7136\u8bf4\u9053\uff1a\u201c\u6211\u7559\u4e0d \u4f4f\u4f60\u4eec\uff0c\u4f60\u4eec\u8d70\u5427\u3002\u201d\u5979\u5728\u7d2b\u7af9\u6797\u4e2d\u8654\u4fee\u4e86\u51e0\u5341\u5e74\uff0c\u81ea\u4ee5\u4e3a\u53ef\u4ee5\u4e0e\u7384\u673a\u9038\u58eb\u4e00\u6bd4\uff0c\u54ea\u77e5\u8fd8\u662f \u8d25\u5728\u7384\u673a\u9038\u58eb\u5f92\u5f1f\u7684\u624b\u4e0b\u3002 \u3000\u3000\u56db\u4eba\u8d70\u51fa\u7af9\u6797\uff0c\u98de\u5929\u9f99\u5973\u53f6\u76c8\u76c8\u9053\uff1a\u201c\u8fd9\u4e2a\u8001\u5a46\u5a46\u7684\u6b66\u529f\u786e\u662f\u8fdc\u975e\u6211\u7b49\u6240\u53ca\uff0c\u6211\u770b\u5f53\u4eca \u4e4b\u4e16\uff0c\u9664\u4e86\u54b1\u4eec\u7684\u5e08\u7236\u4e0e\u4e0a\u5b98\u5929\u91ce\u8fd9\u4e2a\u8001\u9b54\u5934\u4e4b\u5916\uff0c\u6050\u6015\u5c31\u8981\u6570\u5230\u5979\u4e86\u3002\u201d\u4e91\u857e\u63d2\u53e3\u9053\uff1a \u201c\u82e5\u7136\u4ed6\u4eec\u8f83\u91cf\u8d77\u6765\uff0c\u90a3\u624d\u597d\u770b\u5462\u3002\u201d\u8c22\u5929\u534e\u7b11\u9053\uff1a\u201c\u4e5f\u8bb8\u4ed6\u4eec\u65e9\u5df2\u8f83\u91cf\u8fc7\u4e86\uff0c\u53ea\u662f\u4f60\u6211\u751f \u5f97\u592a\u8fdf\u6ca1\u798f\u5f97\u89c1\u7f62\u4e86\u3002\u201d\u53f6\u76c8\u76c8\u9053\uff1a\u201c\u6211\u770b\u5979\u4e0e\u54b1\u4eec\u5e08\u7236\u5fc5\u6709\u6e0a\u6e90\uff0c\u4e09\u54e5\uff0c\u542c\u4f60\u7684\u53e3\u6c14\uff0c\u4f60 \u597d\u50cf\u77e5\u9053\u5979\u7684\u6765\u5386\u3002\u201d\u8c22\u5929\u534e\u9053\uff1a\u201c\u54b1\u4eec\u8fd9\u6d3e\u77e5\u9053\u5979\u7684\u6765\u5386\u7684\uff0c\u9664\u4e86\u5e08\u7236\u4e4b\u5916\uff0c\u6050\u6015\u53ea\u6709\u5927 \u5e08\u5144\u3002\u6211\u9690\u7ea6\u542c\u5927\u5e08\u5144\u8bf4\u8fc7\uff0c\u8bf4\u662f\u5e08\u7236\u4e0e\u4e0a\u5b98\u5929\u91ce\u7684\u4ec7\u6028\uff0c\u4e0d\u5355\u53ea\u662f\u4e3a\u4e86\u4e89\u6b66\u6797\u7684\u76df\u4e3b\uff0c\u5176 \u4e2d\u8fd8\u7275\u6d89\u4e86\u4e00\u4e2a\u672c\u9886\u6781\u9ad8\u7684\u5947\u5973\u5b50\uff0c\u5f53\u65f6\u6211\u4fbf\u95ee\u5176\u8be6\uff0c\u5927\u5e08\u5144\u5374\u4e0d\u80af\u8bf4\u5e08\u7236\u7684\u5f80\u4e8b\u3002\u201d\u53f6\u76c8 \u76c8\u9053\uff1a\u201c\u5927\u5e08\u5144\u5462\uff1f\u201d\u8c22\u5929\u534e\u9053\uff1a\u201c\u6211\u591a\u5e74\u6ca1\u89c1\u7740\u4ed6\u3002\u542c\u8bf4\u4f60\u4eec\u5bf9\u6211\u9887\u6709\u8bef\u4f1a\uff1f\u201d\u53f6\u76c8\u76c8 \u9053\uff1a\u201c\u6b63\u662f\uff0c\u4f60\u5728\u74e6\u523a\u5341\u5e74\uff0c\u5230\u5e95\u662f\u5e72\u4e9b\u4ec0\u4e48\u4e8b\u60c5\uff0c\u600e\u4e48\u4f1a\u6295\u5230\u5f20\u5b97\u5468\u7684\u95e8\u4e0b\uff1f\u201d\u8c22\u5929\u534e\u7b11 \u4e86\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e39\u67ab\uff0c\u6211\u7ed9\u4f60\u5f15\u89c1\u3002\u56db\u59b9\uff0c\u4ed6\u4fbf\u662f\u5f20\u5b97\u5468\u7684\u513f\u5b50\uff0c\u4e5f\u662f\u6211\u5728\u74e6\u523a\u6240\u6536\u7684\u5f92 \u5f1f\u3002\u201d\u53f6\u76c8\u76c8\u597d\u751f\u60ca\u8bb6\uff0c\u9053\uff1a\u201c\u4f60\u6536\u7684\u597d\u5f92\u5f1f\uff0c\u602a\u4e0d\u5f97\u4ed6\u521a\u624d\u4e0e\u4e91\u857e\u53cc\u5251\u5408\u74a7\uff0c\u5728\u4e94\u5341\u62db\u4e4b \u5185\u5c45\u7136\u80fd\u4e0e\u90a3\u8001\u5a46\u5a46\u6253\u6210\u5e73\u624b\u3002\u201d\u5fc3\u4e2d\u751a\u662f\u7591\u60d1\uff1a\u96be\u9053\u8c22\u5929\u534e\u5c31\u53ea\u662f\u4e3a\u4e86\u8981\u6536\u4e00\u4e2a\u597d\u5f92\u5f1f\uff0c \u800c\u4e0d\u60dc\u5c48\u8eab\u6295\u5230\u5f20\u5b97\u5468\u7684\u95e8\u4e0b\uff1f\u8c22\u5929\u534e\u9053\uff1a\u201c\u6b64\u4e8b\u8bf4\u6765\u8bdd\u957f\u5566\uff0c\u54b1\u4eec\u5148\u53bb\u627e\u4e8c\u5e08\u5144\u5427\u3002\u201d\u56db \u4eba\u5230\u4e86\u5c71\u811a\uff0c\u4e91\u857e\u4e0e\u5e08\u5085\u540c\u4e58\u4e8e\u8c26\u6240\u8d60\u7684\u5927\u5185\u826f\u9a6c\uff0c\u5f20\u4e39\u67ab\u4e0e\u5e08\u7236\u4e58\u7167\u591c\u72ee\u5b50\u9a6c\uff0c\u4e0d\u6d88\u534a\u4e2a \u65f6\u8fb0\uff0c\u5df2\u8d76\u5230\u96c1\u95e8\u5173\u5916\u7684\u539f\u6765\u7ea6\u4f1a\u4e4b\u5904\u3002\u4e00\u8def\u90fd\u4e0d\u89c1\u6f6e\u97f3\u548c\u5c1a\u7684\u8e2a\u8ff9\uff0c\u53f6\u76c8\u76c8\u5947\u9053\uff1a\u201c\u4e8c\u5e08 \u5144\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u201d\u8c22\u5929\u534e\u9053\uff1a\u201c\u54b1\u4eec\u9a6c\u5feb\uff0c\u8d70\u904d\u8fd9\u96c1\u95e8\u5173\u5916\u65b9\u5706\u767e\u91cc\u4e4b\u5730\uff0c\u603b\u53ef\u4ee5\u627e\u5f97\u7740 \u4ed6\u3002\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u90a3\u4e48\u54b1\u4eec\u4fbf\u5206\u5934\u53bb\u627e\u5427\u3002\u201d\u8c22\u5929\u534e\u9053\uff1a\u201c\u4e0d\u5fc5\u4f60\u4eec\u4e00\u8d77\uff0c\u74e6\u523a\u56fd\u4e2d\uff0c\u915d \u917f\u5de8\u53d8\uff0c\u4f60\u7236\u4eb2\u4e5f\u8bb8\u4f1a\u6709\u5371\u9669\uff0c\u6211\u82e5\u4e0d\u662f\u4e3a\u4e86\u4e8c\u5e08\u5144\u4e4b\u7ea6\uff0c\u4eca\u5929\u8fd8\u4e0d\u4f1a\u6765\u5462\u3002\u4f60\u548c\u4e91\u857e\u5feb\u9a6c \u52a0\u97ad\uff0c\u5148\u5165\u74e6\u523a\u5427\u3002\u201d\u5f20\u4e39\u67ab\u6025\u9053\uff1a\u201c\u4ec0\u4e48\u5371\u9669\uff1f\u201d\u8c22\u5929\u534e\u9053\uff1a\u201c\u4e5f\u5148\u5df2\u6000\u7591\u4f60\u7236\u4eb2\u6000\u6709\u5f02 \u5fc3\u3002\u4ed6\u9000\u5175\u56de\u56fd\u4e4b\u540e\uff0c\u5bf9\u7be1\u4f4d\u4e4b\u4e8b\uff0c\u56fe\u8c0b\u66f4\u6025\uff0c\u6b63\u662f\u53f8\u9a6c\u662d\u4e4b\u5fc3\uff0c\u8def\u4eba\u7686\u77e5\uff0c\u53ea\u6015\u5728\u65e6\u5915\u4e4b \u95f4\uff0c\u5c31\u8981\u4e3e\u4e8b\u4e86\u3002\u201d\u5f20\u4e39\u67ab\u542c\u4e86\u5e08\u7236\u7684\u8bdd\uff0c\u4f3c\u4e4e\u81ea\u5df1\u7684\u7236\u4eb2\u5df2\u6539\u53d8\u521d\u8877\uff0c\u613f\u610f\u6697\u52a9\u660e\u671d\uff0c\u6b63 \u662f\u65e2\u559c\u4e14\u5fe7\uff0c\u5f53\u4e0b\u4e5f\u65e0\u6687\u518d\u95ee\uff0c\u7acb\u523b\u5411\u5e08\u7236\u544a\u8f9e\uff0c\u4e0e\u4e91\u857e\u7b56\u9a6c\u800c\u53bb\u3002\u8c22\u5929\u534e\u770b\u7740\u4ed6\u4eec\u7684\u80cc \u5f71\uff0c\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u4ed6\u4eec\u6bd4\u6211\u4eec\u5e78\u8fd0\u591a\u4e86\u3002\u201d\u98de\u5929\u9f99\u5973\u4e0d\u7981\u9762\u4e0a\u98de\u7ea2\uff0c\u5f20\u4e39\u67ab\u4e0e\u4e91\u857e\u770b\u6765\u6b63\u662f \u5979\u4e0e\u8c22\u5929\u534e\u7684\u5f71\u5b50\u3002 \u3000\u3000\u6309\u4e0b\u8c22\u5929\u534e\u4e0e\u53f6\u76c8\u76c8\u4e0d\u8868\u3002\u4e14\u8bf4\u5f20\u3001\u4e91\u4e8c\u4eba\u5feb\u9a6c\u75be\u9a70\uff0c\u6df1\u5165\u74e6\u523a\uff0c\u4e03\u65e5\u4e4b\u540e\uff0c\u5df2\u9a70\u9a8b\u5728 \u73e0\u7a46\u6c81\u65d7\u8349\u539f\u4e4b\u4e0a\uff0c\u7a7f\u8fc7\u8fd9\u4e2a\u8349\u613f\uff0c\u518d\u8d70\u4e8c\u767e\u4f59\u91cc\uff0c\u5c31\u53ef\u4ee5\u5230\u74e6\u523a\u7684\u4eac\u57ce\u4e86\u3002\u5f20\u4e39\u67ab\u4e0e\u4e91\u857e \u7684\u5750\u9a91\uff0c\u90fd\u662f\u65e5\u884c\u4e94\u767e\u91cc\u4ee5\u4e0a\u7684\u5b9d\u9a6c\uff0c\u5f20\u4e39\u67ab\u5fc3\u60c5\u7a0d\u7a0d\u8212\u5c55\uff0c\u7b11\u9053\uff1a\u201c\u8fd8\u6709\u4e24\u65e5\uff0c\u5c31\u53ef\u4ee5\u5230 \u4e86\u3002\u201d\u4ece\u9a6c\u978d\u4e0a\u89e3\u4e0b\u4e00\u4e2a\u846b\u82a6\uff0c\u846b\u82a6\u4e2d\u6709\u8def\u4e0a\u6240\u6cbd\u7684\u9a6c\u5976\u9152\uff0c\u9053\uff1a\u201c\u8bb8\u4e45\u6ca1\u6709\u5c1d\u5230\u8fd9\u79cd\u9152\u7684 \u6ecb\u5473\u5566\uff0c\u5c0f\u5144\u5f1f\uff0c\u4f60\u4e5f\u559d\u4e00\u70b9\u5417\uff1f\u201d\u5f20\u4e39\u67ab\u6570\u4ee3\u5728\u74e6\u523a\u5c45\u4f4f\uff0c\u5bf9\u74e6\u523a\u7684\u5c71\u5ddd\u7269\u4ea7\uff0c\u81ea\u6709\u4e00\u80a1 \u6d53\u539a\u7684\u611f\u60c5\uff0c\u9a6c\u5976\u9152\u867d\u7136\u8fdc\u8fdc\u4e0d\u5982\u4e2d\u56fd\u7684\u540d\u9152\uff0c\u4ed6\u5374\u559d\u5f97\u6d25\u6d25\u6709\u5473\u3002\u4e91\u857e\u6447\u6447\u5934\u9053\uff1a\u201c\u6211\u4e0d \u559d\uff0c\u6211\u6015\u8fd9\u9a6c\u5976\u9152\u7684\u9178\u5473\u3002\u201d\u5f20\u4e39\u67ab\u62d4\u5f00\u585e\u5b50\uff0c\u628a\u846b\u82a6\u4e2d\u7684\u9a6c\u5976\u9152\u503e\u5165\u53e3\u4e2d\uff0c\u653e\u58f0\u6b4c\u9053\uff1a \u201c\u541b\u4e0d\u89c1\u8d70\u9a6c\u5ddd\u884c\u96ea\u6d77\u8fb9\uff0c\u5e73\u6c99\u83bd\u83bd\u9ec4\u5165\u5929\u3002\u8f6e\u53f0\u4e5d\u6708\u98ce\u591c\u543c\uff0c\u4e00\u5ddd\u788e\u77f3\u5927\u5982\u6597\uff0c\u968f\u98ce\u6ee1\u5730 \u77f3\u4e71\u8d70\u3002\u5c0f\u5144\u5f1f\uff0c\u8fd9\u51e0\u53e5\u8bd7\u5199\u585e\u5916\u98ce\u5149\u5199\u5f97\u771f\u597d\uff0c\u4f60\u770b\u53ef\u4e0d\u6b63\u662f\u6211\u4eec\u773c\u524d\u7684\u666f\u81f4\u5417\uff1f\u201d\u4e91\u857e \u9053\uff1a\u201c\u4f60\u770b\u96ea\u7247\u7eb7\u98de\uff0c\u96ea\u610f\u6b63\u6d53\uff0c\u73b0\u5728\u5df2\u662f\u585e\u5916\u6df1\u51ac\uff0c\u96ea\u6d77\u96be\u884c\uff0c\u6bd4\u8f6e\u53f0\u4e5d\u6708\u66f4\u5bd2\u51b7\u5f97\u591a \u4e86\uff0c\u4f60\u8fd8\u662f\u5feb\u5feb\u8d76\u8def\u5427\u3002\u201d\u8349\u539f\u4e0a\u9ec4\u6c99\u5f25\u6f2b\uff0c\u96ea\u51dd\u5982\u6d77\uff0c\u8fdc\u8fdc\u671b\u53bb\uff0c\u4e00\u7247\u8083\u6740\u8427\u6761\u7684\u666f\u8c61\u3002 \u5e86\u67ab\u7b11\u9053\uff1a\u201c\u51ac\u5929\u5df2\u6df1\uff0c\u6625\u5929\u4e5f\u5c31\u4e0d\u4f1a\u8fdc\u4e86\u3002\u201d\u53c8\u5495\u565c\u5495\u565c\u5730\u559d\u4e86\u51e0\u5927\u53e3\u9152\uff0c\u7ee7\u7eed\u9ad8\u6b4c\u5510\u8bd7 \u4eba\u5c91\u53c2\u7684\u8fd9\u9996\u300a\u897f\u5f81\u300b\u8bd7\u9053\uff1a\u201c\u5308\u5974\u8349\u9ec4\u9a6c\u6b63\u80a5\uff0c\u91d1\u5c71\u897f\u89c1\u70df\u5c18\u98de\uff0c\u6c49\u5bb6\u5927\u5c06\u897f\u51fa\u5e08\u3002\u5440\uff0c \u5c0f\u5144\u5f1f\uff0c\u54b1\u4eec\u867d\u4e0d\u662f\u6c49\u5bb6\u5927\u5c06\uff0c\u4f46\u6b64\u884c\u7684\u91cd\u8981\uff0c\u4e5f\u4e0d\u4e9a\u4e8e\u5927\u5c06\u51fa\u5e08\u5462\u3002\u201d\u4e00\u846b\u82a6\u7684\u5976\u9152\u7ed9\u4ed6 \u559d\u5f97\u6d93\u6ef4\u65e0\u5b58\uff0c\u9152\u610f\u8d8a\u53d1\u98de\u4e0a\u7709\u68a2\u3002\u4e91\u857e\u53d6\u7b11\u9053\uff1a\u201c\u4ea6\u72c2\u4ea6\u4fa0\u771f\u540d\u58eb\uff0c\u80fd\u54ed\u80fd\u6b4c\u8fc8\u4fd7\u6d41\u3002\u4f60 \u4e0d\u4e3a\u540d\u58eb\uff0c\u5374\u4e3a\u4fa0\u58eb\uff0c\u5c82\u4e0d\u53ef\u60dc\uff1f\u201d\u5f20\u4e39\u67ab\u5927\u7b11\u9053\uff1a\u201c\u540d\u58eb\u503c\u591a\u5c11\u94b1\u4e00\u65a4\uff1f\u4fa0\u58eb\u4e5f\u4e0d\u5fc5\u5b58\u5fc3 \u53bb\u505a\u3002\u6211\u4f46\u613f\u968f\u7740\u81ea\u5df1\u7684\u5fc3\u4e8b\u884c\u4e8b\uff0c\u4e0d\u5fc5\u5728\u4e34\u6b7b\u4e4b\u65f6\uff0c\u7559\u5728\u9057\u61be\uff0c\u90a3\u4fbf\u4e0d\u7b97\u865a\u5ea6\u6b64\u751f\u4e86\u3002\u201d \u8bdd\u8bed\u4e2d\u9690\u6307\u4ed6\u4e0e\u4e91\u857e\u7684\u5a5a\u4e8b\uff0c\u5e94\u8be5\u987a\u5176\u81ea\u7136\uff0c\u4e0d\u5e94\u4e3a\u4e86\u4ed6\u4eba\u800c\u8fdd\u80cc\u81ea\u5df1\u7684\u5fc3\u610f\u3002\u4e91\u857e\u542c\u4e86\u9ed8 \u7136\u4e0d\u8bed\u3002\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u4f60\u5728\u60f3\u4ec0\u4e48\u5440\uff1f\u201d\u4e91\u857e\u5f3a\u7b11\u9053\uff1a\u201c\u6211\u5728\u60f3\uff0c\u6211\u5728\u60f3\uff0d\uff0d\u5440\uff0c \u4e3a\u4f55\u6211\u4eec\u884c\u4e86\u591a\u65e5\uff0c\u8def\u4e0a\u5374\u603b\u78b0\u4e0d\u89c1\u5357\u4e0b\u907f\u51ac\u7684\u7267\u6c11\u3002\u5c91\u53c2\u7684\u8bd7\u8bf4\uff1a\u91d1\u5c71\u897f\u89c1\u70df\u5c18\u98de\uff0c\u54b1\u4eec \u5374\u53ea\u662f\u4f46\u89c1\u5c18\u98de\uff0c\u4e0d\u89c1\u70df\u98de\u5462\uff01\u201d \u3000\u3000\u8499\u53e4\u5730\u65b9\uff0c\u6bcf\u5230\u51ac\u5929\uff0c\u5e38\u6709\u7267\u6c11\u5357\u4e0b\u907f\u51ac\uff0c\u517c\u505a\u751f\u610f\uff0c\u91c7\u529e\u65e5\u5e38\u7528\u7269\uff0c\u5230\u5f00\u6625\u4e4b\u540e\uff0c\u56de \u53bb\u8d29\u5356\u3002\u8fd9\u51e0\u65e5\u6765\uff0c\u5f20\u4e39\u67ab\u4e5f\u597d\u751f\u5947\u602a\uff0c\u4f55\u4ee5\u4e0d\u89c1\u7267\u6c11\u7684\u9a6c\u7fa4\u3002\u6b63\u8bf4\u8bdd\u95f4\u5ffd\u542c\u5f97\u6709\u9a7c\u94c3\u58f0 \u54cd\uff0c\u5f20\u4e39\u67ab\u7b11\u9053\uff1a\u201c\u4f60\u77a7\uff0c\u8fd9\u4e0d\u662f\u5357\u4e0b\u7684\u7267\u6c11\u6765\u4e86\uff1f\u201d\u8fdc\u8fdc\u671b\u53bb\uff0c\u53ea\u89c1\u4e00\u5339\u9a86\u9a7c\uff0c\u51e0\u9a91\u9a6c \u5339\uff0c\u4e91\u857e\u9053\uff1a\u201c\u770b\u6765\u4e5f\u53ea\u662f\u4e00\u5bb6\u5357\u8fc1\u7684\u7267\u6c11\u3002\u5f80\u5e74\u4ed6\u4eec\u603b\u662f\u7ed3\u96c6\u6210\u7fa4\u7684\u3002\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u4f60 \u770b\uff0c\u540e\u9762\u8fd8\u6709\u4eba\uff0d\uff0d\u54a6\uff0c\u4e0d\u662f\u7267\u4eba\uff0c\u662f\u8499\u53e4\u5175\uff01\u201d \u3000\u3000\u524d\u9762\u6c99\u5c18\u6eda\u6eda\uff0c\u7ea6\u6709\u5341\u591a\u9a91\u8499\u53e4\u5175\u5feb\u9a6c\u8ffd\u6765\uff0c\u4e0d\u4e00\u523b\u5c31\u8ffd\u4e0a\u90a3\u51e0\u4e2a\u7267\u6c11\uff0c\u62c9\u62c9\u626f\u626f\uff0c\u970e \u65f6\u95f4\u53ea\u542c\u5f97\u7537\u5b50\u7684\u53eb\u58f0\u4e0e\u5973\u5b50\u7684\u54ed\u58f0\u54cd\u6210\u4e00\u7247\u3002\u4e91\u857e\u9053\uff1a\u201c\u5440\uff0c\u662f\u62c9\u592b\uff0c\u600e\u4e48\u8fde\u5973\u5b50\u4e5f\u62a2\uff1f \u54fc\u54b1\u4eec\u89c1\u4e86\uff0c\u53ef\u4e0d\u80fd\u4e0d\u7406\uff01\u201d\u8bf4\u5f97\u5341\u5206\u6c14\u6124\uff0c\u5f20\u4e39\u67ab\u6709\u4e86\u51e0\u5206\u9152\u610f\u9053\uff1a\u201c\u597d\uff0c\u54b1\u4eec\u628a\u90a3\u7fa4\u8499 \u53e4\u5175\u90fd\u6740\u4e86\uff0c\u5c06\u9a6c\u5339\u9001\u7ed9\u7267\u6c11\u3002\u201d\u4e91\u857e\u9053\uff1a\u201c\u4e0d\uff0c\u4e0d\uff0c\u4e0d\u51c6\u4f60\u6740\u4e00\u4e2a\u4eba\uff0c\u5c06\u90a3\u7fa4\u8499\u53e4\u5175\u9a71\u6563 \u4e5f\u5c31\u7b97\u4e86\u3002\u201d\u5f20\u4e39\u67ab\u77e5\u9053\u4e91\u857e\u5fc3\u6148\uff0c\u539f\u662f\u6545\u610f\u548c\u5979\u8bf4\u7b11\u7684\uff0c\u5f53\u4e0b\u7b11\u9053\uff1a\u201c\u597d\uff0c\u4f9d\u4f60\u5c31\u662f\u3002\u201d \u3000\u3000\u4e24\u4eba\u98de\u9a6c\u4e0a\u524d\uff0c\u53ea\u89c1\u51e0\u4e2a\u8499\u53e4\u5175\u6b63\u5728\u62a2\u4e00\u4e2a\u5c11\u5973\uff0c\u53e6\u5916\u51e0\u4e2a\u5374\u7528\u5f13\u7bad\u6307\u7740\u4e24\u4e2a\u7267\u6c11\uff0c\u5927 \u58f0\u9a82\u9053\uff1a\u201c\u4f60\u4eec\u4e3a\u4f55\u4e0d\u542c\u592a\u5e08\u7684\u547d\u4ee4\uff0c\u79c1\u81ea\u8fc1\u79fb\uff1f\u201d\u90a3\u4e24\u4e2a\u7267\u6c11\u4e00\u8001\u4e00\u5c11\uff0c\u8001\u7684\u9053\uff1a\u201c\u6211\u4eec \u968f\u4f60\u4eec\u56de\u53bb\u5427\uff0c\u6211\u7684\u5973\u513f\uff0c\u4f60\u53ef\u4e0d\u80fd\u62a2\u8d70\uff01\u201d\u90a3\u4e9b\u8499\u53e4\u5175\u559d\u9053\uff1a\u201c\u4f60\u4eec\u8fdd\u80cc\u4e86\u592a\u5e08\u7684\u547d\u4ee4\uff0c \u5168\u5bb6\u90fd\u8981\u5904\u7f5a\u3002\u201d\u4e91\u857e\u5927\u6012\uff0c\u62cd\u9a6c\u4e0a\u524d\u3002\u90a3\u4e9b\u8499\u53e4\u5175\u53eb\u9053\uff1a\u201c\u54a6\uff0c\u8fd9\u4e24\u5339\u9a6c\u53ef\u771f\u4e0d\u9519\uff0c\u8fd8\u662f \u4e24\u4e2a\u6c49\u4eba\u5462\uff01\u201d\u4e00\u62e5\u800c\u524d\uff0c\u5f20\u4e39\u67ab\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u8981\u9a6c\uff0c\u5c31\u9001\u4e0e\u4f60\u4eec\u5427\uff0c\u53ea\u662f\u6015\u4f60\u4eec\u9a7e\u9a6d\u4e0d \u4e86\uff01\u201d\u7167\u591c\u72ee\u5b50\u9a6c\u56db\u8e44\u4e71\u8e22\uff0c\u7247\u523b\u4e4b\u95f4\u5c06\u90a3\u4e9b\u8499\u53e4\u5175\u90fd\u8e22\u5f97\u4eba\u4ef0\u9a6c\u7ffb\uff0c\u4e00\u4e2a\u8499\u53e4\u519b\u5b98\u6b3a\u8d1f\u4e91 \u857e\u662f\u4e2a\u5973\u5b50\uff0c\u4e0a\u524d\u6349\u5979\uff0c\u4e91\u857e\u8863\u8896\u4e00\u6325\uff0c\u7acb\u523b\u5c06\u4ed6\u6454\u4e86\u4e00\u4e2a\u7b4b\u6597\u3002\u5f20\u4e39\u67ab\u559d\u9053\uff1a\u201c\u4f60\u4eec\u82e5\u6562 \u901e\u51f6\uff0c\u8bf7\u770b\u6b64\u9a6c\uff01\u201d\u4fe1\u624b\u4e00\u638c\uff0c\u8f7b\u8f7b\u62cd\u51fa\u7528\u7684\u5374\u662f\u5927\u529b\u91d1\u521a\u624b\u7684\u91cd\u624b\u6cd5\uff0c\u53ea\u4e00\u638c\u5c31\u628a\u90a3\u8499\u53e4 \u519b\u5b98\u7684\u5750\u9a91\u6253\u5f97\u9a6c\u8111\u5f00\u82b1\uff0c\u5012\u6bd9\u5730\u4e0a\u3002 \u3000\u3000\u90a3\u4e9b\u8499\u53e4\u5175\u7ed9\u5f20\u4e39\u67ab\u8fd9\u4e00\u624b\u5413\u5f97\u9b42\u98de\u9b44\u6563\uff0c\u638c\u6bd9\u5954\u9a6c\uff0c\u5c11\u8bf4\u4e5f\u6709\u5343\u65a4\u6c14\u529b\uff0c\u9a6c\u72b9\u5982\u6b64\uff0c \u4eba\u4f55\u4ee5\u582a\uff1f\u4e00\u4e2a\u4e2a\u5446\u82e5\u6728\u9e21\u3002\u4e91\u857e\u6012\u6015\u7a0d\u6d88\uff0c\u89c1\u4ed6\u4eec\u8fd9\u526f\u53c8\u60ca\u53c8\u6015\u7684\u795e\u6c14\uff0c\u4e0d\u89c9\u5657\u55e4\u4e00\u7b11 \u9053\uff1a\u201c\u4f60\u4eec\u8fd8\u4e0d\u5feb\u6eda\uff0c\u60f3\u627e\u6b7b\u4e48\uff1f\u201d\u90a3\u7fa4\u8499\u53e4\u5175\u53d1\u4e00\u58f0\u558a\uff0c\u5404\u5404\u8df3\u4e0a\u5750\u9a91\uff0c\u6ca1\u547d\u5954\u9003\uff0c\u53ea\u53ef \u601c\u90a3\u4e2a\u519b\u5b98\u4e22\u4e86\u5750\u9a91\uff0c\u7a7f\u7740\u4e00\u4ee5\u7f8a\u76ae\u9a6c\u9774\uff0c\u8dcc\u8dcc\u649e\u649e\u5730\u8dd1\u5f97\u5341\u5206\u72fc\u72c8\u3002 \u3000\u3000\u90a3\u5e74\u8001\u7267\u6c11\u4e0a\u524d\u62dc\u8c22\u3002\u5f20\u4e39\u67ab\u95ee\u9053\uff1a\u201c\u4ed6\u4eec\u8bf4\u4ec0\u4e48\u592a\u5e08\u7684\u547d\u4ee4\uff0c\u7a76\u7adf\u662f\u4f55\u547d\u4ee4\uff1f\u201d\u90a3\u7267 \u6c11\u9053\uff1a\u201c\u592a\u5e08\uff08\u4e5f\u5148\uff09\u56de\u56fd\u4e4b\u540e\u5c31\u4e0b\u4e86\u4e00\u9053\u547d\u4ee4\uff0c\u8bf4\u662f\u4eca\u51ac\u4e00\u5f8b\u4e0d\u51c6\u8fc1\u79fb\uff0c\u7b49\u62bd\u4e86\u65b0\u5175\u4e4b \u540e\uff0c\u624d\u51c6\u5230\u5357\u8fb9\u7267\u9a6c\u3002\u8bb8\u591a\u5c0f\u4f19\u5b50\u90fd\u7ed9\u62c9\u53bb\u5f53\u5175\u4e86\u3002\u6211\u5e74\u7eaa\u5df2\u8001\uff0c\u53ea\u6709\u4e00\u4e2a\u513f\u5b50\u548c\u8fd9\u4e2a\u5c0f\u599e \u599e\uff08\u5973\u513f\uff09\uff0c\u82e5\u7136\u4ed6\u88ab\u62bd\u53bb\u5f53\u5175\uff0c\u6211\u548c\u5973\u513f\u53ef\u5c31\u6ca1\u6cd5\u6d3b\u5566\u3002\u56e0\u6b64\uff0c\u624d\u6084\u6084\u9003\u51fa\u6765\uff0c\u82e5\u88ab\u67e5 \u5230\uff0c\u5c31\u5f53\u662f\u65e9\u5df2\u5357\u8fc1\uff0c\u8fd8\u6ca1\u6709\u77e5\u9053\u547d\u4ee4\u3002\u8c01\u77e5\u4ed6\u4eec\u6839\u672c\u4e0d\u5bb9\u5206\u8fa9\uff0c\u5c31\u8981\u62a2\u6211\u7684\u5973\u513f\u3002\u201d\u5f20\u4e39 \u67ab\u5fc3\u9053\uff1a\u201c\u4e5f\u5148\u5982\u6b64\u7740\u6025\u62bd\u5175\uff0c\u53ea\u6015\u5c31\u8981\u4e3e\u4e8b\uff0c\u7be1\u593a\u74e6\u523a\u56fd\u541b\u7684\u7687\u4f4d\u4e86\u3002\u201d\u6302\u5ff5\u81ea\u5df1\u7236\u4eb2\u7684 \u5b89\u5168\uff0c\u65e0\u6687\u591a\u95ee\uff0c\u4fbf\u60f3\u544a\u8f9e\u3002\u53ea\u89c1\u4e91\u857e\u62c9\u7740\u90a3\u4e2a\u5c11\u5973\u7684\u624b\u5ffd\u7136\u95ee\u9053\uff1a\u201c\u4f60\u4eec\u662f\u54ea\u91cc\u7684\u4eba\uff1f\u4f60 \u53eb\u4ec0\u4e48\u540d\u5b57\uff1f\u201d\u773c\u5149\u4e2d\u663e\u51fa\u6b22\u6b23\u4e0e\u5947\u5f02\u7684\u795e\u60c5\u3002 \u3000\u3000\u90a3\u5c11\u5973\u9053\uff1a\u201c\u6211\u4eec\u662f\u6115\u7f57\u90e8\u843d\u7684\u4eba\uff0c\u672c\u6765\u662f\u4f4f\u5728\u5510\u53e4\u62c9\u5c71\u5357\u9762\u5ce1\u8c37\u7684\uff0c\u6211\u540d\u53eb\u59ec\u829d \u7f57\u2026\u2026\u201d\u4e91\u857e\u63a5\u53e3\u9053\uff1a\u201c\u59ec\u829d\u7f57\u9492\u88c1\u6e83\u201c\u88c1\u6f9c\u61ac\u6096\ue0df\u612b\u919a\u5251\u3000\u8e66\u5de7\u5025\ue18f\ue267\u8bc4\u50d6\u8c62\u8c12? \u51fa\u5979\u7684\u540d\u5b57\uff0c\u6014\u4e86\u4e00\u6014\uff0c\u770b\u770b\u4e91\u857e\u7684\u9762\u5b54\uff0c\u4f3c\u4e4e\u662f\u5728\u54ea\u91cc\u89c1\u8fc7\u4e00\u822c\uff0c\u5374\u53c8\u601d\u7d22\u4e0d\u8d77\u3002\u5f20\u4e39\u67ab \u4e5f\u597d\u751f\u5947\u602a\uff0c\u53ea\u542c\u5f97\u4e91\u857e\u58f0\u97f3\u98a4\u6296\u6025\u58f0\u95ee\u9053\uff1a\u201c\u90a3\u4f4d\u5b89\u829d\u7f57\u8bbf\u8309\u8bc4\u6d17\u7afd\u9518\u4e56\u8c00\u62a2? \u5417\uff1f\u201d\u90a3\u5c11\u5973\u9053\uff1a\u201c\u4f60\u662f\u95ee\u90a3\u4f4d\u5ac1\u4e0e\u6c49\u4eba\u7684\u8001\u5927\u5a18\uff1f\u201d\u4e91\u857e\u9053\uff1a\u201c\u6b63\u662f\u3002\u201d\u5bfb\u5c11\u5973\u201c\u54ce\u5440\u201d \u4e00\u58f0\u53eb\u9053\uff1a\u201c\u4f60\u662f\u4e91\u3001\u4e91\u2026\u2026\u201d\u4e91\u857e\u9053\uff1a\u201c\u6211\u5c31\u662f\u4e91\u857e\u3002\u4f60\u8bb0\u5f97\u5417\uff0c\u5c0f\u65f6\u5019\uff0c\u6211\u4eec\u65f6\u65f6\u5230\u5ce1 \u8c37\u53bb\u770b\u4ed6\u4eec\u653e\u7f8a\uff1f\u201d \u3000\u3000\u4e91\u857e\u662f\u4e03\u5c81\u4e4b\u65f6\u79bb\u5f00\u8499\u53e4\u7684\uff0c\u5c0f\u65f6\u5019\u7684\u4e8b\u60c5\u8fd8\u4f9d\u7a00\u8bb0\u5f97\uff0c\u8fd9\u5c11\u5973\u662f\u5979\u7ae5\u5e74\u65f6\u5019\u7684\u670b\u53cb\uff0c \u5979\u95ee\u7684\u90a3\u4f4d\u5b89\u829d\u7f57\u8bbf\u8309\u8bc4\u6d17\u7afd\u954e\ue433\u64ac\ue41e\u54ea\u76d6\u4f4f\uff34\u8bc4\u4fda\u6bcd\u76d6\u81ea\u77a5\u5367\u8bff\u6652\u6015\u8869\ue37f\ue497\u7f69? \u65f6\uff0c\u5a36\u4e86\u80e1\u5973\u4e3a\u59bb\uff0c\u6b63\u662f\u548c\u90a3\u5c11\u5973\u540c\u4e00\u90e8\u843d\u7684\u4eba\uff0c\u4e91\u6f84\u79bb\u5f00\u8499\u53e4\u4e4b\u65f6\uff0c\u6015\u8d70\u6f0f\u98ce\u58f0\uff0c\u8fde\u59bb\u5b50 \u4e5f\u6ca1\u6709\u544a\u8bc9\u3002 \u3000\u3000\u90a3\u5c11\u5973\u89c1\u4e86\u513f\u65f6\u7684\u6e38\u4f34\uff0c\u5df2\u6210\u4e3a\u4e00\u4f4d\u8eab\u624b\u975e\u51e1\u7684\u5973\u4fa0\uff0c\u5fc3\u4e2d\u81ea\u662f\u6b22\u559c\u65e0\u9650\uff0c\u4f46\u542c\u5f97\u4e91\u857e \u95ee\u8d77\u6bcd\u4eb2\uff0c\u795e\u6c14\u500f\u53c8\u8f6c\u4e3a\u54c0\u4f24\u3002\u90a3\u8001\u4eba\u66ff\u5973\u513f\u7b54\u9053\uff1a\u201c\u4f60\u4eec\u90a3\u5e74\u7a81\u7136\u5931\u8e2a\uff0c\u4f60\u6bcd\u4eb2\u65e5\u54ed\u591c \u54ed\uff0c\u54ed\u5f97\u773c\u775b\u90fd\u574f\u4e86\uff0c\u770b\u4e1c\u897f\u6a21\u7cca\uff0c\u914b\u957f\u53ef\u601c\u5979\u5c31\u53eb\u5979\u53bb\u5e2e\u9972\u9a6c\uff0c\u73b0\u5728\u5927\u7ea6\u8fd8\u5728\u914b\u957f\u5bb6\u91cc\u3002 \u914b\u957f\u8fd8\u56e0\u6b64\u8bf4\u6c49\u4eba\u90fd\u662f\u9760\u4e0d\u4f4f\u7684\uff0c\u5ba3\u5e03\u4ece\u6b64\u4e0d\u51c6\u4e0e\u6c49\u4eba\u901a\u5a5a\u3002\u201d\u4e91\u857e\u542c\u4e86\uff0c\u568e\u5555\u5927\u54ed\u3002\u5f20\u4e39 \u67ab\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u5f85\u6211\u4eec\u7684\u4e8b\u60c5\u529e\u59a5\u4e4b\u540e\uff0c\u7acb\u523b\u53bb\u627e\u4f60\u7684\u6bcd\u4eb2\u3002\u597d\u5728\u4f2f\u6bcd\u5c1a\u5728\u4eba\u95f4\uff0c\u5982\u4eca\u53c8 \u77e5\u9053\u4e86\u5979\u7684\u786e\u8baf\uff0c\u8fd9\u662f\u4e0d\u5e78\u4e2d\u4e4b\u5e78\u5440\uff0c\u8fd8\u54ed\u4ec0\u4e48\u5462\uff1f\u201d\u4e91\u857e\u7768\u4e86\u5f20\u4e39\u67ab\u4e00\u773c\uff0c\u60b2\u6124\u4e4b\u610f\uff0c\u6ea2 \u4e8e\u8bcd\u8868\uff0c\u4f46\u8fd8\u662f\u542c\u5f20\u4e39\u67ab\u6240\u529d\uff0c\u62ed\u4e86\u773c\u6cea\uff0c\u8de8\u9a6c\u767b\u7a0b\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u95f7\u95f7\u4e0d\u4e50\uff0c\u5f88\u4e3a\u4e91\u857e\u6bcd\u4eb2\u7684\u906d\u9047\u96be\u8fc7\uff0c\u5c24\u5176\u5728\u60f3\u5230\u4e91\u857e\u6bcd\u4eb2\u4e4b\u6240\u4ee5\u81f3\u6b64\uff0c\u8ffd\u7a76\u539f \u56e0\uff0c\u5f52\u6839\u5230\u5e95\uff0c\u8fd8\u662f\u7531\u4e8e\u81ea\u5df1\u7236\u4eb2\u7684\u9519\u8bef\u9020\u6210\uff0c\u5fc3\u4e2d\u66f4\u662f\u81ea\u548e\u4e0d\u5b89\uff0c\u53ea\u6709\u6697\u4e2d\u53d1\u8a93\uff0c\u5c06\u6765\u5b9a \u8981\u8bbe\u6cd5\u66ff\u7236\u4eb2\u8d4e\u7f6a\u3002 \u3000\u3000\u4e00\u8def\u5317\u884c\uff0c\u8499\u53e4\u5175\u8d8a\u6765\u8d8a\u591a\u9047\u5230\uff0c\u5e78\u5728\u4e8c\u4eba\u9a6c\u5feb\uff0c\u4e00\u89c1\u5c31\u7ed5\u8def\u800c\u884c\uff0c\u8499\u53e4\u5175\u5c31\u662f\u60f3\u76d8\u95ee \u4e5f\u8ffd\u4e0d\u4e0a\u3002\u4e24\u65e5\u4e4b\u540e\uff0c\u5230\u4e86\u74e6\u523a\u7684\u4eac\u90fd\uff0c\u5f20\u4e39\u67ab\u4e0e\u4e91\u857e\u65e9\u6362\u4e86\u5f53\u5730\u7267\u6c11\u7684\u8863\u88f3\uff0c\u5f53\u4f5c\u662f\u8fdb\u4eac \u57ce\u6765\u4e70\u4e1c\u897f\u8fc7\u51ac\u7684\u3002 \u3000\u3000\u5f20\u3001\u4e91\u4e8c\u4eba\u5728\u4e00\u95f4\u4e2d\u7b49\u5ba2\u5e97\u4f4f\u4e0b\uff0c\u628a\u9a6c\u5339\u5b89\u987f\u597d\u540e\uff0c\u7136\u540e\u51fa\u95e8\u3002\u5f20\u5bb6\u76f8\u5e9c\u9760\u8fd1\u7687\u57ce\uff0c\u524d \u9762\u662f\u5341\u5b57\u5927\u8857\uff0c\u5e73\u65f6\u8f66\u6c34\u9a6c\u9f99\uff0c\u5341\u5206\u70ed\u95f9\uff0c\u8fd9\u65e5\u5374\u662f\u884c\u4eba\u7a00\u5c11\uff0c\u51b7\u51b7\u6e05\u6e05\uff0c\u5f20\u4e39\u67ab\u4e00\u8e0f\u4e0a\u8fd9 \u6761\u8857\uff0c\u5c31\u611f\u5230\u4e00\u79cd\u5f02\u6837\u7684\u6c14\u6c1b\uff0c\u5fc3\u4e2d\u6697\u77e5\u4e0d\u5999\u3002\u672c\u6765\u7a7f\u8fc7\u5927\u8857\uff0c\u5c31\u53ef\u671b\u89c1\u76f8\u5e9c\uff0c\u5f20\u4e39\u67ab\u4e34\u65f6 \u53d8\u8ba1\uff0c\u643a\u4e86\u4e91\u857e\uff0c\u4ece\u4e00\u6761\u5c0f\u5df7\u7ed5\u53bb\u8eb2\u5728\u8857\u89d2\u4e00\u671b\uff0c\u53ea\u89c1\u5dcd\u5ce8\u7684\u76f8\u5e9c\u4e4b\u524d\uff0c\u6709\u8bb8\u591a\u536b\u5175\u5de1\u903b\uff0c \u800c\u4e14\u8fd9\u4e9b\u536b\u5175\u7684\u9762\u5b54\uff0c\u5f20\u4e39\u67ab\u4e00\u4e2a\u4e5f\u8ba4\u4e0d\u5f97\uff0c\u5206\u660e\u4e0d\u662f\u81ea\u5df1\u5e9c\u4e2d\u7684\u6b66\u58eb\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u626f\u4e86\u4e91\u857e\u4e00\u4e0b\uff0c\u6025\u5fd9\u6084\u6084\u6e9c\u8d70\u3002\u8f6c\u8fc7\u51e0\u6761\u8857\uff0c\u627e\u5230\u4e00\u95f4\u5c0f\u5c0f\u7684\u9152\u5e97\uff0c\u5f20\u4e39\u67ab\u9053\uff1a \u201c\u54b1\u4eec\u4e14\u5148\u796d\u4e86\u4e94\u810f\u5e99\u518d\u8bf4\u3002\u201d\u8fdb\u5165\u9152\u5bb6\uff0c\u8981\u4e86\u4e00\u65a4\u5364\u725b\u8089\uff0c\u53c8\u8981\u4e24\u65a4\u8499\u53e4\u6700\u540d\u8d35\u7684\u4e00\u79cd\u9152 \uff0d\u9999\u8349\u7ea2\u8393\u9152\uff0c\u5364\u725b\u8089\u662f\u8499\u53e4\u6700\u5bfb\u5e38\u7684\u98df\u7269\u90a3\u5c0f\u9152\u5bb6\u81ea\u7136\u5907\u6709\uff0c\u9999\u8349\u7ea2\u8393\u9152\u5374\u6ca1\u6709\uff0c\u5f20\u4e39\u67ab \u53d6\u51fa\u4e00\u952d\u5927\u94f6\uff0c\u53eb\u9152\u4fdd\u5230\u9644\u8fd1\u7684\u9152\u94fa\u53bb\u4e70\u3002\u90a3\u9152\u4fdd\u89c1\u8fd9\u4e24\u4e2a\u201c\u7267\u4eba\u201d\u51fa\u624b\u8c6a\u9614\uff0c\u751a\u662f\u60ca\u5f02\uff0c \u4e70\u56de\u6765\u65f6\uff0c\u90a3\u9152\u4fdd\u5c06\u9152\u6367\u4e0a\uff0c\u6b63\u8981\u4f38\u624b\u5230\u8170\u5c01\u91cc\u53d6\u94f6\u5b50\u53e3\u4e2d\u8bf4\u9053\uff1a\u201c\u4e00\u65a4\u9999\u8349\u7ea2\u8393\u9152\u8981\u4e00\u4e24 \u56db\u94b1\u94f6\u5b50\uff0c\u4e24\u65a4\u662f\u3001\u662f\uff0d\uff0d\u201d\u5f20\u4e39\u67ab\u4e00\u6446\u624b\u9053\uff1a\u201c\u4e0d\u5fc5\u627e\u4e86\uff0c\u5269\u4e0b\u7684\u94b1\u90fd\u8d4f\u7ed9\u4f60\u3002\u201d\u90a3\u952d\u5927 \u94f6\uff0c\u8db3\u503c\u5341\u4e24\uff0c\u4e24\u65a4\u9999\u8349\u8393\u9152\u503c\u4e0d\u8fc7\u4e8c\u4e24\u516b\u94b1\uff0c\u5f20\u4e39\u67ab\u8fd9\u4e00\u8d4f\u4fbf\u662f\u4e03\u4e24\u4e8c\u94b1\uff0c\u90a3\u9152\u4fdd\u81ea\u662f\u6b22 \u559c\u65e0\u9650\uff0c\u8c22\u4e86\u53c8\u8c22\u3002\u5e97\u4e2d\u5e76\u65e0\u5176\u4ed6\u5ba2\u4eba\u9152\u4fdd\u4fbf\u4e00\u76f4\u7ad9\u5728\u5f20\u4e39\u67ab\u7684\u65c1\u8fb9\u4f8d\u5019\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u996e\u4e86\u51e0\u676f\uff0c\u88c5\u505a\u6e29\u4e0d\u7ecf\u5fc3\u5730\u95ee\u9053\uff1a\u201c\u524d\u9762\u90a3\u6761\u5927\u8857\u90a3\u95f4\u5927\u5c4b\u662f\u8c01\u4eba\u7684\uff1f\u201d\u9152\u4fdd \u9053\uff1a\u201c\u5ba2\u5b98\u4e0d\u77e5\u9053\u5417\uff1f\u90a3\u662f\u53f3\u4e1e\u76f8\u5f20\u5b97\u5468\u7684\u76f8\u5e9c\u3002\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u554a\uff0c\u602a\u4e0d\u5f97\u90a3\u4e48\u5927\u7684\u6c14 \u6d3e\u3002\u76f8\u5e9c\u524d\u9762\u6709\u90a3\u4e48\u591a\u7684\u536b\u5175\uff0c\u884c\u4eba\u90fd\u4e0d\u6562\u7ecf\u8fc7\uff0c\u5728\u90a3\u6761\u8857\u505a\u751f\u610f\u7684\u5c82\u4e0d\u5012\u9709\uff1f\u201d\u9152\u4fdd\u5c0f\u58f0 \u8bf4\u9053\uff1a\u201c\u4ee5\u524d\u6ca1\u90a3\u4e48\u591a\u536b\u5175\u7684\uff0c\u542c\u8bf4\u8fd9\u4e9b\u536b\u5175\u662f\u592a\u5e08\u6d3e\u53bb\u7684\u3002\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u662f\u5417\uff1f\u662f\u4e0d\u662f \u5f20\u4e1e\u76f8\u5f97\u7f6a\u4e86\u592a\u5e08\uff0c\u6240\u4ee5\u592a\u5e08\u628a\u4ed6\u7684\u76f8\u90b8\u5360\u4e86\uff1f\u201d\u9152\u4fdd\u6447\u6447\u5934\u9053\uff1a\u201c\u8fd9\u6211\u4eec\u53ef\u4e0d\u77e5\u9053\u3002\u4f46\u6bcf \u5929\u8fd8\u89c1\u6709\u76f8\u5e9c\u7684\u4e0b\u4eba\u5728\u536b\u5175\u770b\u7ba1\u4e0b\u51fa\u8857\u5e02\u4e70\u83dc\uff0c\u542c\u8bf4\u5f20\u4e1e\u76f8\u8fd8\u5728\u5e9c\u4e2d\u3002\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u4f60\u6d88 \u606f\u5012\u7075\u3002\u201d\u90a3\u9152\u4fdd\u5f97\u4e86\u8d4f\u94b1\uff0c\u53c8\u7ed9\u5f20\u4e39\u67ab\u4e00\u8d5e\uff0c\u53c8\u9053\uff1a\u201c\u6211\u4eec\u4e0e\u76f8\u5e9c\u867d\u9694\u7740\u4e00\u6761\u5927\u8857\uff0c\u4e5f\u7b97 \u5f97\u662f\u90bb\u8fd1\u7684\u8857\u574a\uff0c\u5f20\u4e1e\u76f8\u6bcf\u5929\u4e0a\u65e9\u671d\u65f6\uff0c\u90fd\u8981\u4ece\u6211\u4eec\u8fd9\u513f\u7ecf\u8fc7\u7684\uff0c\u8fd9\u51e0\u5929\u5374\u6ca1\u89c1\u4ed6\u4e0a\u671d\u3002\u5f20 \u4e1e\u76f8\u6700\u6b22\u559c\u5403\u7f8a\u809d\uff0c\u8fd9\u51e0\u5929\u8fd8\u662f\u7167\u6837\u7684\u4e70\u3002\u201d\u5f20\u4e39\u67ab\u5fc3\u4e2d\u7a0d\u5bbd\uff0c\u60f3\u9053\uff1a\u201c\u539f\u6765\u7236\u4eb2\u662f\u7ed9\u4e5f\u5148 \u8f6f\u7981\u4e86\uff0c\u4ed6\u65e2\u4e0d\u6562\u4e0b\u624b\u6740\u5bb3\uff0c\u5374\u8f6f\u7981\u6211\u7236\u4eb2\u4f5c\u751a\uff1f\u201d \u3000\u3000\u6d88\u606f\u63a2\u660e\u4e4b\u540e\uff0c\u5f20\u3001\u4e91\u4e8c\u4eba\u56de\u5230\u65c5\u5e97\uff0c\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u4f60\u5230\u9694\u90bb\u7684\u65c5\u5e97\u53bb\u53e6\u5f00\u4e00 \u95f4\u623f\u5b50\uff0c\u665a\u4e0a\u82e5\u6ca1\u6709\u4e8b\u60c5\u53d1\u751f\uff0c\u6211\u518d\u53bb\u627e\u4f60\u540c\u5230\u76f8\u5e9c\u4e00\u63a2\u3002\u201d\u4e91\u857e\u9053\uff1a\u201c\u4f55\u6545\u8981\u5982\u6b64\u5e03 \u7f6e\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u6709\u5907\u65e0\u60a3\uff0c\u4f60\u542c\u6211\u7684\u8bdd\u4fbf\u662f\u3002\u201d\u4e91\u857e\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\u4f9d\u4f60\u4fbf\u662f\u3002\u4eca\u665a\u6211 \u7b49\u4f60\u6765\u3002\u53ef\u662f\u4f60\u7684\u5bb6\u4e2d\u6211\u662f\u4e0d\u53bb\u7684\uff01\u201d\u5f20\u4e39\u67ab\u77e5\u5979\u5fc3\u4e2d\u5c1a\u6709\u82a5\u8482\uff0c\u4e00\u7b11\u9053\uff1a\u201c\u4e5f\u597d\uff0c\u90a3\u5c31\u4ee5 \u540e\u518d\u8bf4\u3002\u6211\u518d\u6c42\u4f60\u4e00\u4ef6\u4e8b\uff0c\u4f60\u5728\u9644\u8fd1\u7684\u5927\u8857\u5c0f\u5df7\uff0c\u5077\u5077\u5728\u5404\u5904\u5899\u811a\u523b\u4e0a\u8fd9\u4e9b\u8bb0\u53f7\u3002\u201d\u5c06\u5e08\u95e8 \u7ea6\u4f1a\u7684\u6697\u53f7\u8bf4\u4e0e\u4e91\u857e\uff0c\u53eb\u5979\u4f9d\u8a00\u884c\u4e8b\u3002 \u3000\u3000\u5403\u8fc7\u665a\u996d\uff0c\u5df2\u662f\u65e5\u843d\u9ec4\u660f\uff0c\u5f20\u4e39\u67ab\u6b63\u60f3\u53bb\u627e\u4e91\u857e\uff0c\u5e97\u5c0f\u4e8c\u5ffd\u8fdb\u6765\u62a5\u9053\uff1a\u201c\u6709\u5b98\u4eba\u6765\u8bbf\u8d35 \u5ba2\u3002\u201d\u5f20\u4e39\u67ab\u51db\u7136\u4e00\u60ca\uff0c\u53ea\u89c1\u623f\u95e8\u5f00\u5904\uff0c\u4e00\u4e2a\u8499\u53e4\u519b\u5b98\u8d70\u4e86\u8fdb\u6765\uff0c\u6b63\u662f\u4e5f\u5148\u5e10\u4e0b\u7684\u7b2c\u4e00\u540d\u6b66 \u58eb\u989d\u5409\u591a\u3002 \u3000\u3000\u53ea\u89c1\u989d\u5409\u591a\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5f20\u4e39\u67ab\u4f60\u771f\u597d\u80c6\u91cf\uff0c\u8fd8\u6562\u5230\u8fd9\u91cc\u6765\uff01\u201d\u5f20\u4e39\u67ab\u7b11\u9053\uff1a\u201c\u4f60 \u4e5f\u771f\u597d\u80c6\u91cf\uff0c\u8fd8\u6562\u5230\u8fd9\u91cc\u6765\uff0c\u4f60\u7684\u4f24\u597d\u4e86\u5417\uff1f\u201d\u989d\u5409\u591a\u5728\u6c99\u6d9b\u5c71\u5be8\u65f6\uff0c\u66fe\u5403\u8fc7\u5f20\u4e39\u67ab\u7684\u5927 \u4e8f\uff0c\u53c8\u7ed9\u77f3\u82f1\u6253\u4e86\u4e00\u638c\uff0c\u5e78\u6709\u62a4\u8eab\u91d1\u7532\uff0c\u5c06\u517b\u534a\u6708\uff0c\u5df2\u662f\u75ca\u6108\u3002\u989d\u5409\u591a\u9053\uff1a\u201c\u62dc\u541b\u6240\u8d50\uff0c\u603b \u7b97\u6211\u7684\u5934\u9aa8\u8fd8\u633a\u5f97\u4f4f\u3002\u4e0d\u81f3\u7ed9\u4f60\u89c1\u7b11\u554a\u3002\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u4f60\u4eca\u665a\u5230\u6b64\uff0c\u610f\u6b32\u4f55\u4e3a\uff1f\u8fd9\u91cc\u53ef\u4e0d \u662f\u6253\u67b6\u7684\u5730\u65b9\u3002\u201d\u989d\u5409\u591a\u9053\uff1a\u201c\u6211\u6b64\u6765\u53ef\u4e0d\u662f\u627e\u4f60\u62a5\u4ec7\uff0c\u5f53\u7136\uff0c\u53ea\u8981\u4f60\u613f\u610f\u7684\u8bdd\uff0c\u54b1\u4eec\u65e5\u540e \u8fd8\u53ef\u4ee5\u518d\u6bd4\u3002\u6211\u6b64\u6765\u662f\u5411\u4f60\u8d3a\u559c\u7684\uff01\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u559c\u4ece\u4f55\u6765\uff1f\u201d\u989d\u5409\u591a\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u5b50\u597d \u9020\u5316\uff0c\u592a\u5e08\u5df2\u5c3d\u77e5\u9053\u4f60\u7684\u6240\u4f5c\u6240\u4e3a\uff0c\u5bf9\u4f60\u8fd8\u662f\u7279\u522b\u65bd\u6069\uff0c\u4eca\u665a\u8bf7\u4f60\u53bb\u8d74\u5bb4\u3002\u201d\u5f20\u4e39\u67ab\u9053\uff1a \u201c\u54c8\uff0c\u8bf7\u6211\u53bb\u8d74\u5bb4\uff1f\u201d\u989d\u5409\u591a\u9053\uff1a\u201c\u6b63\u662f\uff0c\u4f60\u5feb\u6362\u8863\u670d\uff0c\u4e8b\u5230\u5982\u4eca\uff0c\u4e5f\u4e0d\u5fc5\u85cf\u5934\u9732\u5c3e\uff0c\u5047\u626e \u7267\u4eba\u4e86\u3002\u201d\u5f20\u4e39\u67ab\u8fb9\u6362\u8863\u670d\u8fb9\u7b11\u9053\uff1a\u201c\u592a\u5e08\u7684\u8033\u76ee\u5012\u5f88\u7075\u901a\u5440\uff01\u201d\u989d\u5409\u591a\u7b11\u9053\uff1a\u201c\u4f60\u806a\u660e\u522b \u4eba\u4e5f\u4e0d\u50bb\u5440\uff01\u592a\u5e08\u8bf4\u4f60\u4e00\u751f\u806a\u660e\u4f46\u4e5f\u6709\u4e00\u65f6\u7cca\u6d82\u3002\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u989d\u5409\u591a\u7b11\u9053\uff1a \u201c\u4f60\u51fa\u624b\u8c6a\u9614\uff0c\u5411\u9152\u4fdd\u6253\u63a2\u6d88\u606f\uff0c\u90a3\u9152\u4fdd\u8fc7\u540e\u4e00\u60f3\uff0c\u5c82\u6562\u4e0d\u62a5\u544a\u5b98\u5dee\uff1f\u201d\u5176\u5b9e\u6b64\u4e8b\u65e9\u5728\u5f20\u4e39 \u67ab\u610f\u6599\u4e4b\u4e2d\uff0c\u4ed6\u4e5f\u6599\u5230\u4e5f\u5148\u53ef\u80fd\u4f1a\u6709\u6b64\u201c\u9080\u8bf7\u201d\uff0c\u6240\u4ee5\u5728\u9152\u5bb6\u4e00\u56de\u6765\u540e\uff0c\u5c31\u53eb\u4e91\u857e\u642c\u5230\u522b \u5904\u3002 \u3000\u3000\u989d\u5409\u591a\u53c8\u9053\uff1a\u201c\u4f60\u90a3\u4f4d\u6f02\u4eae\u7684\u5c0f\u5ab3\u5987\u5462\uff1f\u201d\u5f20\u4e39\u67ab\u53f1\u9053\uff1a\u201c\u80e1\u8bf4\uff0c\u5979\u662f\u6211\u7684\u5e08\u59b9\u3002\u201d\u989d \u5409\u591a\u9053\uff1a\u201c\u7ba1\u4f60\u662f\u5ab3\u5987\u4e5f\u597d\uff0c\u5e08\u59b9\u4e5f\u597d\uff0c\u5979\u5728\u54ea\u513f\uff1f\u201d\u5f20\u4e39\u67ab\u4e00\u7b11\u9053\uff1a\u201c\u592a\u5e08\u795e\u673a\u5999\u7b97\uff0c\u8fd9 \u4e5f\u7b97\u4e0d\u51fa\u6765\u5417\uff1f\u6211\u7684\u5e08\u59b9\u53ef\u6bd4\u6211\u806a\u660e\u5f97\u591a\uff0c\u6211\u662f\u62fc\u4e86\u4e00\u6b7b\u56de\u5230\u8fd9\u513f\u6765\u7684\uff0c\u5979\u53ef\u8fd8\u8981\u591a\u6d3b\u51e0 \u5e74\u3002\u5979\u6015\u53d7\u7275\u7d2f\uff0c\u65e9\u5df2\u8d70\u5566\u3002\u201d\u989d\u5409\u591a\u67e5\u8fc7\u4e0b\uff0c\u77e5\u9053\u4e91\u857e\u672a\u5230\u5348\u65f6\uff0c\u5df2\u5148\u642c\u51fa\uff0c\u4fe1\u4e86\u5f20\u4e39\u67ab \u7684\u8bdd\uff0c\u7b11\u9053\uff1a\u201c\u7b97\u5979\u89c1\u673a\uff0c\u592a\u5e08\u7edd\u4e0d\u5bb9\u5979\u7559\u5728\u4e0a\u4eac\u3002\u8d70\u5427\uff0c\u592a\u5e08\u5bf9\u4f60\u597d\u5f97\u5f88\u5462\uff0c\u4f60\u53ef\u4e0d\u5fc5\u53bb \u62fc\u6b7b\u4e86\u3002\u201d \u3000\u3000\u5f20\u4e39\u67ab\u6362\u4e86\u8863\u88f3\uff0c\u623f\u94b1\u65e9\u5df2\u6709\u989d\u5409\u591a\u4ee3\u4ed8\uff0c\u5f20\u4e39\u67ab\u5728\u51e0\u4e2a\u6b66\u58eb\u7684\u966a\u540c\u4e0b\uff0c\u767b\u4e0a\u6d3e\u6765\u63a5\u4ed6 \u7684\u9a6c\u8f66\uff0c\u4e0d\u8fc7\u534a\u4e2a\u65f6\u8fb0\uff0c\u5c31\u5230\u4e86\u4e5f\u5148\u7684\u592a\u5e08\u5e9c\u3002\u592a\u5e08\u5e9c\u6bd4\u5f20\u5b97\u5468\u7684\u76f8\u5e9c\u66f4\u662f\u5dcd\u5ce8\u534e\u4e3d\uff0c\u5916\u4e09 \u91cd\u5185\u4e09\u91cd\uff0c\u94c1\u95e8\u6df1\u9501\uff0c\u8fdb\u4e86\u516d\u91cd\u5927\u95e8\uff0c\u6b66\u58eb\u4eec\u9ad8\u58f0\u547c\u9053\uff1a\u201c\u5ba2\u4eba\u5230\uff01\u201d\u4e2d\u95e8\u500f\u5730\u6253\u5f00\uff0c\u53ea\u89c1 \u5c4b\u4e2d\u706f\u706b\u8f89\u714c\uff0c\u4e5f\u5148\u5750\u5728\u4e2d\u5802\uff0c\u4f20\u4ee4\u9053\uff1a\u201c\u8bf7\u5ba2\u4eba\u8fdb\u6765\uff01\u201d \u3000\u3000\u5f20\u4e39\u67ab\u795e\u8272\u81ea\u82e5\uff0c\u6f47\u6d12\u5982\u5e38\uff0c\u6b65\u4e0a\u77f3\u9636\uff0c\u53ea\u89c1\u4e00\u4e2a\u6b66\u58eb\u4e0a\u524d\u6765\u6276\uff0c\u53e3\u4e2d\u56b7\u9053\uff1a\u201c\u8fd9\u91cc\u95e8 \u574e\u592a\u9ad8\uff0c\u5c0f\u5fc3\u70b9\u513f\u3002\u201d\u5f20\u4e39\u67ab\u4e00\u77a7\u8fd9\u6b66\u58eb\u7684\u51fa\u624b\uff0c\u7adf\u662f\u5927\u529b\u9e70\u722a\u529f\uff0c\u5f53\u4e0b\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u6211\u81ea\u5df1\u4f1a\u8d70\uff0c\u4f60\u5012\u662f\u8981\u5c0f\u5fc3\u70b9\u513f\uff01\u201d\u53cc\u81c2\u4e00\u632f\uff0c\u5c06\u90a3\u6b66\u58eb\u6325\u5f97\u8dc4\u556e\u8e09\u8e09\u7684\u540e\u9000\u51e0\u6b65\uff0c\u4f46\u53cc \u81c2\u88ab\u4ed6\u6240\u6293\u4e4b\u5904\uff0c\u4e5f\u9690\u9690\u751f\u75db\uff0c\u5f20\u4e39\u67ab\u4e5f\u5403\u4e86\u4e00\u60ca\uff0c\u8fd9\u6b66\u58eb\u7684\u672c\u4e8b\u7adf\u7136\u8fd8\u5728\u989d\u5409\u591a\u4e4b\u4e0a\u3002\u4f46 \u795e\u8272\u4ecd\u662f\u4e1d\u6beb\u4e0d\u53d8\uff0c\u5927\u8e0f\u6b65\u5730\u8d70\u8fdb\u4e2d\u5802\u3002 \u3000\u3000\u53ea\u542c\u5f97\u4e5f\u5148\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4e24\u5e74\u4e0d\u89c1\uff0c\u8d24\u4f84\u66f4\u957f\u5f97\u4e00\u8868\u4eba\u624d\u4e86\u3002\u6587\u624d\u6b66\u827a\uff0c\u90fd\u662f\u51fa\u8272\u5f53 \u884c\uff0c\u771f\u4e43\u53ef\u559c\u53ef\u8d3a\u5440\uff01\u201d\u5f20\u4e39\u67ab\u8fd8\u4e86\u793c\uff0c\u4e5f\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u4e24\u5e74\u4e0d\u89c1\uff0c\u592a\u5e08\u529f\u4e1a\u66f4\u5f6a\u70b3\u4e86\u3002\u4f4d \u9ad8\u6743\u91cd\uff0c\u56fd\u4eba\u77e5\u6709\u592a\u5e08\u800c\u4e0d\u77e5\u6709\u541b\u7687\uff0c\u771f\u4e43\u53ef\u559c\u53ef\u8d3a\u5440\uff01\u201d\u8fd9\u8bdd\u8bf4\u5f97\u9488\u950b\u76f8\u5bf9\uff0c\u542c\u662f\u79f0\u8d5e\uff0c \u5b9e\u662f\u5632\u8bbd\uff0c\u524d\u4e00\u53e5\u5632\u7b11\u4e5f\u5148\u4fb5\u534e\u4e4b\u8d25\u800c\u540e\u4e00\u53e5\u6697\u9a82\u4e5f\u5148\u60f3\u7be1\u74e6\u523a\u7687\u4f4d\u7684\u91ce\u5fc3\u3002\u4e5f\u5148\u5e72\u7b11\u51e0 \u58f0\uff0c\u9053\uff1a\u201c\u597d\u8bf4\uff0c\u597d\u8bf4\uff0c\u8d24\u4f84\u8fdc\u9053\u5f52\u6765\uff0c\u4e14\u5148\u5750\u4e0b\u559d\u9152\u3002\u201d \u3000\u3000\u4e5f\u5148\u8eab\u65c1\u5750\u7684\u662f\u4e00\u4f4d\u8eab\u6750\u9ad8\u5927\u7684\u50e7\u4eba\uff0c\u659f\u4e86\u6ee1\u6ee1\u7684\u4e00\u676f\u9152\u5ffd\u9053\uff1a\u201c\u6211\u5148\u656c\u5f20\u516c\u5b50\u4e00 \u676f\u3002\u201d\u53cc\u6307\u52fe\u7740\u9152\u676f\uff0c\u8f7b\u8f7b\u4e00\u65cb\uff0c\u90a3\u9152\u676f\u6ef4\u6e9c\u6e9c\u5730\u8f6c\u4e2a\u4e0d\u505c\uff0c\u676f\u4e2d\u9152\u6ce2\u6d6a\u8d77\u4f0f\uff0c\u5374\u662f\u4e1d\u6beb\u4e0d \u6ea2\u3002\u5f20\u4e39\u67ab\u4e00\u770b\u8fd9\u50e7\u4eba\u656c\u9152\u7684\u624b\u6cd5\uff0c\u751a\u662f\u602a\u5f02\uff0c\u9152\u676f\u6765\u52bf\u751a\u6025\uff0c\u7adf\u4f3c\u7ed9\u4ed6\u7684\u6307\u529b\u63a8\u5230\u81ea\u5df1\u7684 \u9762\u524d\u3002\u5f20\u4e39\u67ab\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u672a\u9886\u6559\u5927\u5e08\u6cd5\u53f7\u3002\u201d\u638c\u5fc3\u4e00\u644a\uff0c\u63a5\u7740\u676f\u5e95\uff0c\u808c\u8089\u5185\u9677\uff0c\u5c06\u90a3 \u80a1\u52b2\u529b\u5316\u4e8e\u65e0\u5f62\uff0c\u624b\u638c\u4e00\u6c89\uff0c\u53cc\u6307\u4e0a\u52fe\uff0c\u5c06\u9152\u676f\u63a5\u4e86\u8fc7\u6765\uff0c\u4e00\u996e\u800c\u5c3d\u3002\u90a3\u50e7\u4eba\u9762\u4e0a\u5fae\u5fae\u53d8 \u8272\uff0c\u5f20\u4e39\u67ab\u4e5f\u6709\u51e0\u5206\u60ca\u8be7\uff0c\u50e7\u4eba\u9732\u7684\u8fd9\u624b\uff0c\u4e0d\u77e5\u8005\u770b\u6765\u5982\u53d8\u620f\u6cd5\uff0c\u5176\u5b9e\u5374\u662f\u4e00\u79cd\u6df1\u6e5b\u7684\u5185 \u529f\uff0c\u9152\u676f\u7ed9\u4ed6\u7684\u5185\u529b\u6240\u8feb\uff0c\u6765\u52bf\u6025\u52b2\uff0c\u4f46\u9152\u65e2\u4e0d\u6ea2\uff0c\u676f\u4ea6\u4e0d\u88c2\uff0c\u529b\u5ea6\u5fc5\u987b\u7528\u5f97\u5de7\u5999\u4e4b\u6781\u3002\u5f20 \u4e39\u67ab\u82e5\u975e\u4e60\u4e86\u300a\u7384\u529f\u8981\u8bc0\u300b\uff0c\u63a5\u676f\u4e4b\u65f6\uff0c\u7eb5\u4e0d\u53d7\u4f24\uff0c\u9152\u4ea6\u5fc5\u5b9a\u6cfc\u6e85\u4e86\u3002\u5f53\u4e0b\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u8fd9 \u50e7\u4eba\u7684\u672c\u4e8b\u53c8\u6bd4\u9002\u624d\u90a3\u6b66\u58eb\u9ad8\u4e86\u4e00\u7b79\uff0c\u90a3\u6b66\u58eb\u672c\u4e8b\u867d\u9ad8\uff0c\u6211\u8fd8\u53ef\u5236\u670d\u5f97\u4f4f\uff0c\u8fd9\u50e7\u4eba\u82e5\u4e0e\u6211\u5bf9 \u654c\uff0c\u80dc\u8d1f\u5374\u96be\u4ee5\u6b32\u6599\u3002\u4e5f\u5148\u4e0d\u77e5\u4ece\u54ea\u91cc\u53c8\u5ef6\u63fd\u4e86\u8fd9\u4e9b\u5f02\u4eba\u3002\u201d \u3000\u3000\u4e5f\u5148\u9053\uff1a\u201c\u6211\u7ed9\u8d24\u4f84\u4ecb\u7ecd\uff0c\u8fd9\u662f\u897f\u85cf\u7ea2\u6559\u7684\u9752\u8c37\u6cd5\u5e08\u3002\u201d\u53c8\u6307\u7740\u5148\u524d\u90a3\u6b66\u58eb\u9053\uff1a\u201c\u8fd9\u4f4d \u662f\u5410\u8c37\u6d51\u7684\u52c7\u58eb\u9ebb\u7ffc\u8d5e\u3002\u201d\u5f20\u4e39\u67ab\u4e0e\u4e24\u4eba\u5206\u522b\u5e72\u4e86\u4e00\u676f\uff0c\u4e5f\u5148\u9053\uff1a\u201c\u6211\u4ee5\u4e3a\u8d24\u4f84\u8fd9\u6b21\u8fdc\u6e38\uff0c \u4e50\u800c\u5fd8\u8fd4\u4e86\u3002\u5230\u8fc7\u8bb8\u591a\u5730\u65b9\u5427\uff1f\u201d\u5f20\u4e39\u67ab\u7b11\u9053\uff1a\u201c\u6211\u8fd9\u6b21\u4ece\u585e\u5317\u76f4\u5230\u6c5f\u5357\uff0c\u4e2d\u534e\u7269\u4ea7\u4e30\u9976\uff0c \u4eba\u7269\u4fca\u79c0\uff0c\u771f\u4e43\u82b1\u82b1\u4e16\u754c\uff0c\u9526\u7ee3\u6c5f\u5c71\u3002\u53ef\u60dc\u592a\u5e08\u53ea\u5230\u5317\u4eac\u57ce\u5916\u4fbf\u6298\u56de\u6765\u3002\u201d\u4e5f\u5148\u9762\u8272\u4e00\u53d8\uff0c \u9053\uff1a\u201c\u4e2d\u539f\u4e4b\u5730\u4ed6\u65e5\u6211\u5b9a\u8981\u4e00\u53bb\u4ee5\u5f00\u773c\u754c\uff0c\u5230\u65f6\u8fd8\u8bf7\u8d24\u5012\u5bfc\u8def\u3002\u201d\u5f20\u4e39\u67ab\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c \u9053\uff1a\u201c\u6628\u591c\u6211\u68a6\u4e2d\u4e5f\u66fe\u518d\u8fc7\u4e2d\u539f\uff0c\u53ef\u60dc\u68a6\u4ea6\u4e0d\u957f\uff0c\u4e00\u4e0b\u5c31\u9192\u3002\u201d \u3000\u3000\u5f20\u4e39\u67ab\u8bcd\u950b\u9510\u5229\uff0c\u51b7\u5632\u52bf\u8bbd\uff0c\u5484\u5484\u903c\u4eba\u3002\u4e5f\u5148\u6c89\u4f4f\u4e86\u6c14\uff0c\u54c8\u54c8\u4e00\u7b11\uff0c\u4e3e\u676f\u4e00\u996e\u800c\u5c3d\uff0c \u9053\uff1a\u201c\u8d24\u4f84\u66f4\u4f1a\u8bf4\u8bdd\u4e86\u3002\u6211\u5e74\u8001\u8bcd\u62d9\uff0c\u60f3\u4ec0\u4e48\u5c31\u8bf4\u4ec0\u4e48\uff0c\u575a\u4f84\u8bf7\u52ff\u4ecb\u610f\u3002\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u8bf7 \u592a\u5e08\u6307\u6559\u3002\u201d\u4e5f\u5148\u9053\uff1a\u201c\u575a\u4f84\u8fd9\u6b21\u5f52\u6765\uff0c\u60f3\u8fd8\u672a\u89c1\u7740\u4ee4\u5c0a\u3002\u6211\u5148\u66ff\u575a\u4f84\u63a5\u98ce\uff0c\u60f3\u4ee4\u5c0a\u4e0d\u81f4\u89c1 \u602a\u3002\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u6211\u66ff\u5bb6\u7236\u591a\u8c22\u592a\u5e08\u7684\u597d\u610f\u3002\u201d\u4e5f\u5148\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u591a\u8c22\u4ec0\u4e48\uff1f\u201d\u5f20\u4e39 \u67ab\u9053\uff1a\u201c\u5bb6\u7236\u957f\u5e74\u5fd9\u788c\uff0c\u8fd9\u6b21\u592a\u5e08\u6069\u5178\uff0c\u5f97\u4ee5\u6446\u8131\u4fd7\u52a1\uff0c\u5728\u5bb6\u4e2d\u9759\u517b\u5b9e\u662f\u6c42\u4e4b\u4e0d\u5f97\uff0c\u5c82\u53ef\u4e0d \u8c22\uff1f\u201d\u4e5f\u5148\u542c\u4e86\uff0c\u5ffd\u7136\u54c8\u54c8\u5927\u7b11\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u662f\u5426\u5c0f\u81f4\u5931\u8a00\u60f9\u592a\u5e08\u89c1\u7b11\uff1f\u201d\u4e5f\u5148\u9053\uff1a\u201c\u8d24\u4f84\u4e0d\u662f\u5931\u8a00\uff0c\u5374\u662f\u6545\u610f\u77eb\u60c5\u63a9 \u9970\u3002\u4fd7\u8bed\u4e91\uff1a\u77e5\u5b50\u83ab\u82e5\u7236\uff0c\u77e5\u7236\u4ea6\u5f53\u662f\u83ab\u82e5\u5b50\u3002\u8001\u592b\u56fa\u7136\u60f3\u5230\u4e2d\u534e\uff0c\u4ee4\u5c0a\u53c8\u4f55\u5c1d\u4e0d\u60f3\u91cd\u56de\u6545 \u571f\u5462\uff0c\u54b1\u4eec\u6253\u5f00\u5929\u7a97\u8bf4\u4eae\u8bdd\u5427\uff0c\u4ee4\u5c0a\u80fd\u4e0d\u80fd\u56de\u53bb\uff0c\u90a3\u5c31\u8981\u5168\u770b\u8d24\u4f84\u4f60\u4e86\u3002\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u8bf7 \u592a\u5e08\u660e\u8a00\u3002\u201d\u4e5f\u5148\u9053\uff1a\u201c\u6211\u8fd9\u6b21\u5175\u62b5\u5317\u4eac\uff0c\u5374\u529f\u4e8f\u4e00\u7bd1\uff0c\u86ee\u5b50\u4e8e\u8c26\u7684\u987d\u6297\uff0c\u56fa\u7136\u662f\u51fa\u6211\u610f \u5916\uff0c\u5185\u90e8\u7684\u63a3\u8098\uff0c\u4ea6\u662f\u8feb\u4ee4\u6211\u9000\u5175\u7684\u539f\u56e0\u3002\u575a\u4f84\u662f\u81ea\u5df1\u4eba\uff0c\u6211\u4e0d\u59a8\u5bf9\u4f60\u4e00\u8bf4\uff0e\u201d\u5f20\u4e39\u67ab\u9053\uff1a \u201c\u5bb6\u7236\u5c82\u6562\u63a3\u8098\u592a\u5e08\u5462\uff1f\u201d\u4e5f\u5148\u7b11\u9053\uff1a\u201c\u6211\u4e0d\u662f\u8bf4\u4f60\u7684\u7236\u4eb2\uff0c\u6211\u662f\u8bf4\u963f\u523a\u77e5\u9662\u3002\u963f\u523a\u5728\u897f\u90e8 \u62e5\u5175\u81ea\u91cd\u4e0d\u542c\u53f7\u4ee4\uff0c\u8d24\u4f84\u60f3\u8fd8\u4e0d\u77e5\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u6211\u521a\u521a\u56de\u6765\uff0c\u662f\u4e0d\u77e5\u9053\u3002\u201d\u4e5f\u5148\u9053\uff1a \u201c\u76ee\u4e0b\u74e6\u523a\u4e09\u5206\uff0c\u56fd\u541b\u5eb8\u5f31\u4e0d\u80fd\u62c5\u5f53\u56fd\u8fd0\u3002\u82e5\u8981\u79f0\u96c4\u585e\u5916\uff0c\u996e\u9a6c\u957f\u6c5f\uff0c\u53ea\u6709\u6211\u548c\u963f\u523a\u53ef\u4ee5\u505a \u5230\u4e86\u3002\u201d\u5f20\u4e39\u67ab\u51b7\u51b7\u4e00\u7b11\uff0c\u53ea\u542c\u5f97\u4e5f\u5148\u53c8\u9053\uff1a\u201c\u963f\u523a\u8e81\u731b\u65e0\u8c0b\uff0c\u975e\u662f\u6211\u6562\u81ea\u8c6a\uff0c\u5957\u4f60\u4eec\u6c49\u4eba \u7684\u8bdd\u8bf4\uff0c\u5b9e\u662f\u5929\u4e0b\u82f1\u96c4\u552f\u4f7f\u541b\u4e0e\u64cd\u8033\uff01\u8001\u592b\u4e0d\u624d\uff0c\u80c6\u6562\u81ea\u6bd4\u66f9\u64cd\u3002\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u8c01\u662f\u5218 \u5907\uff1f\u201d\u4e5f\u5148\u7b11\u9053\uff1a\u201c\u541b\u5bb6\u7236\u5b50\uff0c\u4fbf\u662f\u5218\u5907\u3002\u4ee4\u5c0a\u6587\u6b66\u5168\u624d\uff0c\u4e45\u63e1\u6743\u67c4\uff0c\u6df1\u77e5\u74e6\u523a\u56fd\u60c5\uff0c\u82e5\u4e0e \u6211\u8054\u5408\uff0c\u4e0d\u96be\u5c06\u963f\u523a\u526a\u9664\uff0c\u7136\u540e\u518d\u6325\u5175\u5357\u4e0b\uff0c\u5f53\u53ef\u9042\u4ee4\u5c0a\u996e\u9a6c\u957f\u6c5f\u3001\u91cd\u56de\u6545\u91cc\u4e4b\u613f\u3002\u201d\u5f20\u4e39 \u67ab\u542c\u4e86\u6012\u6c14\u4e0a\u5347\uff0c\u5374\u5f3a\u81ea\u5fcd\u7740\uff0c\u53ea\u542c\u5f97\u4e5f\u5148\u53c8\u9053\uff1a\u201c\u4e94\u65e5\u4e4b\u524d\uff0c\u6211\u66fe\u6709\u5bc6\u51fd\uff0c\u4e0e\u4ee4\u5c0a\u5546\u8bae\uff0c \u53ea\u662f\u4ee4\u5c0a\u81f3\u4eca\u5c1a\u672a\u7b54\u590d\u3002\u4e16\u5144\u662f\u660e\u767d\u4eba\uff0c\u662f\u4ee5\u60f3\u8bf7\u4e16\u5144\u56de\u5bb6\u4e4b\u540e\uff0c\u66ff\u8001\u592b\u4e00\u529d\u4ee4\u5c0a\u3002\u201d \u3000\u3000\u8bf4\u8bdd\u81f3\u6b64\uff0c\u5f20\u4e39\u67ab\u5df2\u4e86\u7136\u4e8e\u5fc3\uff0c\u539f\u6765\u4e5f\u5148\u60f3\u4e0e\u7236\u4eb2\u8054\u5408\uff0c\u201c\u8ba8\u4f10\u201d\u963f\u523a\uff0c\u526a\u9664\u4e86\u653f\u654c\u4e4b \u540e\uff0c\u7136\u540e\u518d\u7be1\u4f4d\u79f0\u738b\uff0c\u60f3\u662f\u4e5f\u5148\u89c1\u7236\u4eb2\u5c1a\u672a\u7b54\u590d\uff0c\u6240\u4ee5\u5c06\u4ed6\u8f6f\u7981\u8d77\u6765\u3002\u5fc3\u4e2d\u6697\u81ea\u76d8\u7b97\uff1a\u76ee\u4e0b \u5175\u6743\u64cd\u5728\u4e5f\u5148\u624b\u4e2d\uff0c\u7236\u4eb2\u7684\u6027\u547d\uff0c\u4ea6\u5728\u4e5f\u5148\u638c\u4e0a\u3002\u82e5\u901e\u4e00\u65f6\u4e4b\u6c14\u5c06\u4ed6\u65a5\u8d23\uff0c\u540e\u679c\u582a\u8651\u3002\u800c\u4e14 \u6b64\u4e8b\u7275\u6d89\u4e2d\u56fd\u7684\u56fd\u8fd0\uff0c\u770b\u4eca\u65e5\u7684\u5f62\u52bf\uff0c\u963f\u523a\u4e5f\u4e0d\u662f\u4e5f\u5148\u7684\u654c\u624b\uff0c\u4ed6\u5c31\u662f\u4e0d\u8054\u5408\u7236\u4eb2\uff0c\u4e5f\u53ef\u4ee5 \u7be1\u4f4d\u79f0\u738b\uff0c\u4ed6\u4e4b\u6240\u4ee5\u8981\u6c42\u7236\u4eb2\u76f8\u52a9\uff0c\u4e0d\u8fc7\u662f\u4e3a\u4e86\u66f4\u53ef\u4ee5\u79f0\u64cd\u80dc\u5238\u7f62\u4e86\u3002\u5f53\u4eca\u4e0a\u7b56\uff0c\u5e94\u8be5\u662f\u7528 \u7f13\u5175\u4e4b\u8ba1\uff0c\u5f85\u4e8e\u8c26\u91cd\u5efa\u65b0\u519b\u4e4b\u540e\uff0c\u5373\u7b97\u4e5f\u5148\u7edf\u4e00\u74e6\u523a\uff0c\u90a3\u4e5f\u4e0d\u8db3\u4e3a\u60e7\u4e86\u3002 \u3000\u3000\u53ea\u662f\u6b64\u65f6\u6b64\u9645\uff0c\u4e5f\u5148\u7b49\u7740\u56de\u7b54\uff0c\u5b9e\u662f\u96be\u4ee5\u62d6\u5ef6\u3002\u4e5f\u5148\u53c8\u903c\u95ee\u4e86\u4e00\u53e5\u9053\uff1a\u201c\u54b1\u4eec\u5c61\u4ee3\u4e16 \u4ea4\uff0c\u65e0\u8bdd\u4e0d\u53ef\u76f8\u8c08\u3002\u8d24\u4f84\u610f\u4e0b\u5982\u4f55\u554a\uff1f\u6562\u8bf7\u660e\u4ee5\u544a\u6211\u3002\u201d\u5f20\u4e39\u67ab\u5ffd\u5730\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u7693\u6708 \u5f53\u7a7a\u7f8e\u9152\u76c8\u6a3d\uff0c\u8c08\u519b\u56fd\u5927\u4e8b\uff0c\u5c82\u4e0d\u592a\u715e\u98ce\u666f\u4e48\uff1f\u5148\u996e\u4e09\u676f\uff0c\u592a\u5e08\uff0c\u656c\u4f60\u4e09\u676f\uff0c\u6765\u5440\uff0c\u5e72 \u5440\uff01\u201d\u4e5f\u5148\u6014\u4e86\u4e00\u6014\uff0c\u5fc3\u4e2d\u4e0d\u60a6\uff0c\u53ef\u662f\u4e3a\u4e86\u793c\u8c8c\uff0c\u4e0d\u5f97\u4e0d\u4e0e\u4ed6\u5e72\u676f\u3002\u5e72\u4e86\u4e09\u676f\u4e4b\u540e\uff0c\u4e5f\u5148\u6b63 \u60f3\u8bf4\u8bdd\uff0c\u5ffd\u95fb\u5f97\u73af\u4f69\u53ee\u5f53\uff0c\u73e0\u5e18\u63ed\u5904\uff0c\u4e00\u4e2a\u7f8e\u8c8c\u7684\u5c11\u5973\u8d70\u4e86\u51fa\u6765\u3002\u8fd9\u5c11\u5973\u6b63\u662f\u4e5f\u5148\u7684\u5973\u513f\u8131 \u4e0d\u82b1\u3002 \u3000\u3000\u53ea\u542c\u5f97\u8131\u4e0d\u82b1\u5a07\u58f0\u7b11\u9053\uff1a\u201c\u55ef\uff0c\u5f20\u5927\u54e5\uff0c\u679c\u7136\u662f\u4f60\uff0c\u6211\u8fd8\u9053\u7239\u7239\u662f\u9a97\u6211\u5462\uff01\u201d\u539f\u6765\u5728\u571f \u6728\u5821\u4e4b\u591c\uff0c\u4e5f\u5148\u77e5\u9053\u4e86\u5973\u513f\u7684\u5fc3\u4e8b\u4e4b\u540e\uff0c\u66fe\u7b54\u5e94\u66ff\u5979\u627e\u56de\u5f20\u4e39\u67ab\uff0c\u4e3a\u5979\u4e3b\u5a5a\uff0c\u53ef\u662f\u4e0d\u4e45\u5c31\u5728 \u5317\u4eac\u5175\u8d25\u5f52\u56fd\uff0c\u8131\u4e0d\u82b1\u53ea\u9053\u4eca\u751f\u6c38\u4e0d\u80fd\u4e0e\u5f20\u4e39\u67ab\u518d\u9047\u4e86\u3002\u5979\u7236\u4eb2\u5bf9\u5979\u8bf4\u4eca\u665a\u6709\u5979\u6e34\u6b32\u4e00\u89c1\u7684 \u4eba\u524d\u6765\u8d74\u5bb4\uff0c\u5979\u8fd8\u4ee5\u4e3a\u662f\u5979\u7236\u4eb2\u6545\u610f\u5c06\u5979\u620f\u5f04\u3002 \u3000\u3000\u4e5f\u5148\u672c\u6765\u5429\u5490\u5979\u8981\u5f85\u9152\u5e2d\u5c06\u7ec8\u4e4b\u65f6\u518d\u51fa\u6765\uff0c\u5979\u8feb\u4e0d\u53ca\u5f85\uff0c\u9152\u672a\u4e09\u5de1\uff0c\u5df2\u5148\u81ea\u8d70\u51fa\u3002\u5f20\u4e39 \u67ab\u4e00\u89c1\uff0c\u6b63\u5408\u5fc3\u610f\uff0c\u7acb\u523b\u4e0a\u524d\uff0c\u65bd\u4e86\u4e00\u793c\uff0c\u9053\uff1a\u201c\u4eca\u65e5\u5e78\u5f97\u518d\u89c1\uff0c\u5148\u656c\u4f60\u4e09\u676f\uff01\u201d\u8131\u4e0d\u82b1\u7709 \u5f00\u773c\u7b11\uff0c\u4e0e\u5f20\u4e39\u67ab\u5404\u5c06\u4e09\u5927\u676f\u9152\u4e00\u996e\u800c\u5c3d\uff0c\u5f20\u4e39\u67ab\u4e0d\u5f85\u4e5f\u5148\u8bf4\u8bdd\u53c8\u9053\uff1a\u201c\u5728\u571f\u6728\u5821\u4e4b\u65f6\uff0c\u8499 \u4f60\u6b3e\u5f85\uff0c\u518d\u656c\u4f60\u4e09\u676f\uff01\u201d\u8131\u4e0d\u82b1\u5a07\u7b11\u9053\uff1a\u201c\u4f60\u4e5f\u5f97\u966a\u6211\u559d\u5440\u3002\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u8fd9\u4e2a\u5f53\u7136\uff01\u201d \u4e0d\u5f85\u76f8\u529d\uff0c\u4fbf\u7aef\u8d77\u9152\u676f\uff0c\u5c06\u4e09\u5927\u676f\u70c8\u9152\uff0c\u4e00\u4e00\u503e\u5165\u53e3\u4e2d\uff0c\u6709\u5982\u9cb8\u541e\u725b\u996e\u3002\u4e5f\u5148\u7709\u5934\u4e00\u76b1\uff0c \u9053\uff1a\u201c\u5973\u513f\uff0c\u4f60\u4e71\u996e\u4e00\u6c14\uff0c\u83ab\u8981\u9189\u4e86\uff0c\u53eb\u5927\u54e5\u7b11\u4f60\u5931\u793c\u3002\u201d\u8fd9\u8bdd\u660e\u8bf4\u5973\u513f\uff0c\u5b9e\u662f\u6697\u8bf4\u5f20\u4e39 \u67ab\uff0c\u8131\u4e0d\u82b1\u4e0d\u660e\u5176\u610f\uff0c\u7b11\u9053\uff1a\u201c\u533a\u533a\u51e0\u676f\u9152\u6211\u54ea\u4f1a\u9189\uff0c\u96be\u9053\u5f20\u5144\u5f1f\u8fd9\u6837\u597d\u610f\uff0d\uff0d\u201d\u4e5f\u5148\u7709\u5934 \u53c8\u662f\u4e00\u76b1\uff0c\u8131\u4e0d\u82b1\u7b11\u9053\uff1a\u201c\u597d\u7f57\u55e6\u7684\u7239\u7239\uff0c\u7b97\u6211\u6015\u4e86\u4f60\uff0c\u6211\u4e0d\u559d\u4fbf\u662f\u3002\u5f20\u5144\u5f1f\uff0c\u6211\u8fd8\u656c\u4f60\u4e09 \u676f\uff01\u201d\u5f20\u4e39\u67ab\u4e0d\u5f85\u5979\u659f\u9152\uff0c\u7acb\u9053\uff1a\u201c\u597d\u6781\u5566\uff0c\u597d\u6781\u5566\uff0c\u6211\u5168\u9886\u4e86\uff01\u201d\u81ea\u5df1\u659f\u9152\uff0c\u53c8\u559d\u4e86\u6ee1\u6ee1 \u7684\u4e09\u676f\u3002\u8131\u4e0d\u82b1\u66f4\u662f\u6b22\u559c\u5fd8\u5f62\uff0c\u5927\u7b11\u9053\uff1a\u201c\u5f20\u5144\u5f1f\u679c\u662f\u5feb\u4eba\uff0c\u6211\u8bf4\uff0c\u4f60\u8fd8\u8be5\u518d\u559d\u4e09\u676f\uff0c\u4f60\u5728 \u571f\u6728\u5821\u4e0d\u8f9e\u800c\u884c\u8be5\u4e0d\u8be5\u7f5a\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u5440\uff0c\u8be5\u7f5a\uff0c\u8be5\u7f5a\uff01\u201d\u62a2\u8fc7\u9152\u58f6\uff0c\u81ea\u659f\u81ea\u996e\uff0c\u53c8\u559d \u4e86\u6ee1\u6ee1\u7684\u4e09\u676f\uff01 \u3000\u3000\u4e5f\u5148\u9053\uff1a\u201c\u9152\u5df2\u5dee\u4e0d\u591a\u4e86\uff0c\u5403\u70b9\u89e3\u9152\u7684\u9c9c\u9c7c\u6c64\u5427\uff01\u201d\u5f20\u4e39\u67ab\u5ffd\u800c\u62ab\u5f00\u8863\u895f\uff0c\u54c8\u54c8\u5927\u7b11 \u9053\uff1a\u201c\u9152\u9022\u77e5\u5df1\u5343\u676f\u5c11\uff0c\u8bdd\u4e0d\u6295\u673a\u534a\u53e5\u591a\uff01\u5440\u5440\uff01\u8bdd\u4e0d\u6295\u673a\u534a\u53e5\u591a\uff01\u5343\u676f\u672a\u5230\u5c31\u4e0d\u7ed9\u6211\u559d \u4e86\uff1f\u201d\u4e5f\u5148\u9053\uff1a\u201c\u5f20\u4e16\u5144\u9189\u4e86\uff01\u201d\u5f20\u4e39\u67ab\u624b\u821e\u8db3\u8e48\uff0c\u53eb\u9053\uff1a\u201c\u8c01\u8bf4\u6211\u9189\uff0c\u8c01\u8bf4\u6211\u9189\uff1f\u6211\u518d\u559d \u7ed9\u4f60\u770b\u3002\u201d\u4e00\u4f38\u624b\u53c8\u62a2\u9152\u58f6\uff0c\u4e5f\u5148\u629b\u4e86\u4e00\u4e2a\u773c\u8272\uff0c\u6b66\u58eb\u9ebb\u7ffc\u8d5e\u4e0a\u524d\u62e6\u963b\u9053\uff1a\u201c\u5f20\u516c\u5b50\u4e0d\u8981\u559d \u4e86\uff01\u201d\u624b\u638c\u4e00\u6309\uff0c\u5f20\u4e39\u67ab\u559d\u9053\uff1a\u201c\u4f60\u6562\u4e0d\u7ed9\u6211\u559d\uff1f\u201d\u53cd\u624b\u4e00\u6325\uff0c\u9ebb\u7ffc\u8d5e\u5012\u9000\u4e09\u6b65\uff0c\u9152\u58f6\u8dcc \u7ffb\uff0c\u9762\u7ea2\u8033\u70ed\u3002\u4e5f\u5148\u6c89\u58f0\u9053\uff1a\u201c\u8d24\u4f84\u4fdd\u91cd\uff0c\u9152\u80fd\u4f24\u4eba\uff0c\u4e0d\u8981\u559d\u4e86\u3002\u201d\u5f20\u4e39\u67ab\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5343 \u53e4\u4ee5\u6765\uff0c\u53ea\u95fb\u4e3b\u4eba\u529d\u9152\uff0c\u672a\u95fb\u4e3b\u4eba\u7981\u9152\u7684\u9053\u7406\uff0c\u54c8\u54c8\uff0c\u54c8\u54c8\uff0c\u54c8\u2026\u2026\u201d\u4e5f\u5148\u9053\uff1a\u201c\u5f20\u4e16\u5144\u771f \u7684\u9189\u4e86\uff0c\u5feb\u5f04\u70b9\u9192\u9152\u7684\u4e1c\u897f\u6765\uff01\u201d\u5f20\u4e39\u67ab\u624b\u821e\u8db3\u8e48\uff0c\u72c2\u6001\u6bd5\u9732\uff0c\u5927\u53eb\u9053\uff1a\u201c\u6211\u672a\u9189\uff0c\u6211\u672a \u9189\uff01\u201d\u5ffd\u5730\u4e00\u8de4\u8dcc\u5012\uff0c\u53e3\u89d2\u6d41\u6d8e\uff0c\u7ee7\u800c\u5410\u51fa\u9152\u6765\uff0c\u9152\u6c14\u55b7\u4eba\uff0c\u4e2d\u4eba\u6b32\u5455\u3002\u4e5f\u5148\u6447\u4e86\u6447\u5934\u9053\uff1a \u201c\u597d\u5c0f\u5b50\uff0c\u6545\u610f\u559d\u9189\uff0c\u96be\u9053\u8fd9\u6837\u6211\u5c31\u653e\u8fc7\u4f60\u4e86\u3002\u201d\u8131\u4e0d\u82b1\u9053\uff1a\u201c\u7239\uff0c\u4f60\u8bf4\u4ec0\u4e48\uff1f\u201d\u4e5f\u5148\u9053\uff1a \u201c\u4e0d\u5e72\u4f60\u4e8b\u3002\u53ea\u8981\u4ed6\u80af\u542c\u8bdd\uff0c\u6211\u603b\u4e0d\u4f1a\u5c06\u4ed6\u65ab\u4e86\u3002\u201d\u8131\u4e0d\u82b1\u9053\uff1a\u201c\u4e0d\u542c\u8bdd\u4e5f\u4e0d\u5e94\u65ab\u4ed6\u3002\u201d\u4e5f \u5148\u9053\uff1a\u201c\u4f60\u5c11\u8bf4\u4e24\u53e5\uff0c\u5feb\u53eb\u4eba\u5c06\u4ed6\u6276\u5230\u540e\u623f\u6b47\u606f\u3002\u201d \u3000\u3000\u5f20\u4e39\u67ab\u53cc\u76ee\u7d27\u95ed\uff0c\u56db\u80a2\u653e\u8f6f\uff0c\u53e3\u89d2\u6b6a\u54a7\u55b7\u51fa\u4e00\u80a1\u80a1\u9152\u6c14\uff0c\u4fca\u4fcf\u7684\u9762\u5e9e\u6da8\u5f97\u901a\u7ea2\uff0c\u6d3b\u50cf\u4e00 \u4e2a\u70c2\u9189\u5982\u6ce5\u7684\u9152\u9b3c\uff0c\u4f46\u5fc3\u4e2d\u5374\u662f\u6e05\u9192\u975e\u5e38\u3002\u53ea\u542c\u5f97\u9752\u8c37\u6cd5\u5e08\u7684\u811a\u6b65\u58f0\u8f7b\u8f7b\u5730\u8d70\u8fc7\u53bb\uff0c\u4f38\u624b\u642d \u7740\u4ed6\u7684\u8109\u95e8\uff0c\u5f20\u4e39\u67ab\u6697\u8fd0\u300a\u7384\u529f\u8981\u8bc0\u300b\u4e2d\u7684\u9006\u6c14\u4e71\u8109\u4e4b\u6cd5\uff0c\u8109\u640f\u6025\u4fc3\u4e71\u8df3\uff0c\u547c\u5438\u4ea6\u4e0d\u8c03\u548c\u3002 \u9752\u8c37\u6cd5\u5e08\u628a\u4e86\u4e00\u4e0b\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u25a1\u771f\u662f\u9189\u4e86\uff01\u201d\u6b66\u58eb\u9ebb\u7ffc\u8d5e\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u597d\u72e1\u733e\uff0c\u6211\u770b\u4ed6\u662f \u6545\u610f\u704c\u9189\u81ea\u5df1\u7684\u3002\u201d\u4e5f\u5148\u9053\uff1a\u201c\u4ed6\u7236\u4eb2\u5728\u6211\u638c\u63e1\u4e4b\u4e2d\uff0c\u4e5f\u4e0d\u6101\u4ed6\u98de\u5230\u54ea\u91cc\u53bb\u3002\u4eca\u65e5\u4ed6\u9152\u9189 \u4e86\uff0c\u660e\u65e5\u4ed6\u603b\u8981\u56de\u590d\uff0c\u53eb\u4e24\u4e2a\u4eba\u62ac\u4ed6\u8fdb\u540e\u623f\u53bb\uff0c\u82b1\u513f\uff0c\u4f60\u4e5f\u53bb\u7167\u6599\u7167\u6599\u3002\u201d \u3000\u3000\u8131\u4e0d\u82b1\u5e94\u4e86\u4e00\u58f0\uff0c\u5f20\u4e39\u67ab\u611f\u89c9\u5230\u6709\u4e24\u540d\u6b66\u58eb\uff0c\u4e00\u5148\u4e00\u540e\u5c06\u81ea\u5df1\u624b\u8db3\u62ac\u8d77\uff0c\u5fc3\u4e2d\u6697\u7b11\uff0c\u5374 \u6545\u610f\u4f5c\u51fa\u6c89\u9189\u719f\u7761\u7684\u6a21\u6837\uff0c\u53d1\u51fa\u9f3e\u58f0\u3002\u53ea\u542c\u5f97\u4e5f\u5148\u95ee\u9053\uff1a\u201c\u9752\u8c37\u6cd5\u5e08\uff0c\u8fd9\u51e0\u65e5\u8f9b\u82e6\u4f60\u4e86\uff0c\u7687 \u5bab\u4e2d\u6ca1\u6709\u4ec0\u4e48\u53ef\u7591\u4e4b\u4e8b\u5427\uff1f\u201d\u5f20\u4e39\u67ab\u7565\u4e00\u7528\u529b\uff0c\u65bd\u5c55\u201c\u5343\u65a4\u5760\u201d\u7684\u91cd\u8eab\u6cd5\uff0c\u90a3\u4e24\u540d\u6b66\u58eb\u5982\u53d7 \u91cd\u538b\uff0c\u8d70\u52a8\u4e0d\u4fbf\uff0c\u6da8\u5f97\u6ee1\u9762\u901a\u7ea2\uff0c\u4e3a\u4e86\u6015\u4e5f\u5148\u8bf4\u4ed6\u4eec\u6ca1\u7528\uff0c\u53c8\u4e0d\u6562\u4f5c\u58f0\uff0c\u53ea\u597d\u6162\u6162\u79fb\u52a8\u3002\u53ea \u542c\u5f97\u9752\u8c37\u6cd5\u5e08\u7b54\u9053\uff1a\u201c\u7687\u5bab\u5728\u6211\u4eec\u76d1\u89c6\u4e4b\u4e0b\uff0c\u5185\u5916\u9694\u7edd\uff0c\u6ca1\u6709\u4eba\u6562\u8fdb\u6765\u4e0e\u7687\u4e0a\u5bc6\u8bae\uff0c\u592a\u5e08\u653e \u5fc3\u597d\u5566\uff01\u201d\u5f20\u4e39\u67ab\u5fc3\u4e2d\u4e00\u6014\uff0c\u60f3\u9053\uff1a\u201c\u539f\u6765\u4e5f\u5148\u56fe\u8c0b\u7be1\u4f4d\uff0c\u7adf\u662f\u5982\u6b64\u4e4b\u6025\uff0c\u8fde\u74e6\u523a\u56fd\u541b\u4e5f\u88ab \u4ed6\u4eec\u6697\u4e2d\u770b\u7ba1\u8d77\u6765\u5566\u3002\u201d\u4e5f\u5148\u5978\u7b11\u4e24\u58f0\uff0c\u7eed\u9053\uff1a\u201c\u6599\u4ed6\u4e5f\u4e0d\u6562\u4e0e\u5916\u95f4\u52fe\u901a\uff0c\u4e0d\u8fc7\u4ecd\u662f\u5c0f\u5fc3\u7684 \u597d\u3002\u4eca\u665a\u8fd8\u662f\u4f60\u548c\u9ebb\u7ffc\u8d5e\u5230\u7687\u5bab\u53bb\u8f6e\u503c\u5427\u3002\u54a6\uff0c\u4f60\u4eec\u600e\u4e48\u8d70\u5f97\u6162\u817e\u817e\u7684\uff1f\u62cd\u78b0\u4f24\u4ed6\u4e48\uff1f\u201d\u524d \u4e24\u53e5\u662f\u5bf9\u9752\u8c37\u6cd5\u5e08\u8bf4\u7684\uff0c\u540e\u4e24\u53e5\u5374\u662f\u5bf9\u90a3\u4e24\u540d\u6b66\u58eb\u8bf4\u7684\uff0c\u5f20\u4e39\u67ab\u8d81\u6b64\u65f6\u673a\uff0c\u89e3\u4e86\u201c\u5343\u65a4\u5760\u201d \u7684\u91cd\u8eab\u6cd5\uff0c\u4e24\u4eba\u80a9\u4e0a\u8f7b\uff0c\u7b54\u9053\uff1a\u201c\u6b63\u662f\uff0c\u6211\u4eec\u89c1\u5f20\u516c\u5b50\u9189\u5f97\u5982\u6b64\uff0c\u771f\u6015\u78b0\u7740\u4e86\u4ed6\u3002\u201d\u4e5f\u5148 \u9053\uff1a\u201c\u6015\u4ec0\u4e48\uff1f\u4ed6\u662f\u7ec3\u8fc7\u6b66\u529f\u7684\u4eba\uff0c\u4f60\u5f53\u662f\u7eb8\u624e\u7684\u4e48\uff1f\u201d\u4e24\u540d\u6b66\u58eb\u8fde\u8fde\u79f0\u662f\uff0c\u653e\u5f00\u811a\u6b65\uff0c\u5c06 \u5f20\u4e39\u67ab\u625b\u5165\u540e\u623f\uff0c\u5fc3\u4e2d\u6697\u9a82\u5f20\u4e39\u67ab\u6363\u9b3c\u3002\u8fd9\u4e24\u540d\u6b66\u58eb\u4e43\u662f\u6700\u4f4e\u7ea7\u7684\u6b66\u58eb\uff0c\u7ed9\u4e5f\u5148\u6d3e\u4f5c\u4e0b\u4eba\u4f7f \u7528\uff0c\u5fc3\u4e2d\u4e5f\u81ea\u6709\u6c14\uff0c\u6545\u6b64\u867d\u6709\u6240\u7591\uff0c\u5374\u4e0d\u5411\u4e5f\u5148\u76f4\u8bf4\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u8eba\u5728\u5e8a\u4e0a\uff0c\u4f46\u89c9\u9526\u5e10\u9999\u6d53\uff0c\u5e8a\u6e29\u88ab\u6696\uff0c\u5fc3\u4e2d\u7b11\u9053\uff1a\u201c\u4e5f\u5148\u7684\u5bb6\u4eba\u4e5f\u771f\u61c2\u5f97\u4eab\u53d7\uff0c \u5ba2\u623f\u4e2d\u4e5f\u718f\u540d\u9999\u3002\u201d\u8fc7\u4e86\u4e00\u9635\uff0c\u53ea\u89c1\u8131\u4e0d\u82b1\u8d70\u8fdb\u623f\u6765\uff0c\u5750\u5728\u5e8a\u6cbf\uff0c\u5a07\u58f0\u7b11\u9053\uff1a\u201c\u771f\u9189\u6210\u8fd9\u4e2a \u6837\u5b50\u5417\uff1f\u201d\u5f20\u4e39\u67ab\u5047\u88c5\u719f\u7761\uff0c\u4e0d\u7406\u4e0d\u776c\uff0c\u5ffd\u89c9\u4e00\u80a1\u8fa3\u5473\u51b2\u5165\u9f3b\u4e2d\uff0c\u4e0d\u7531\u81ea\u5df1\u5730\u6253\u4e86\u4e00\u4e2a\u201c\u54c8 \u55e4\u201d\uff0c\u539f\u6765\u662f\u8131\u4e0d\u82b1\u7528\u8499\u53e4\u7279\u6709\u7684\u89e3\u9152\u9999\u6599\u6765\u55b7\u5f20\u4e39\u67ab\uff0c\u5f20\u4e39\u67ab\u6253\u4e2a\u5475\u6b20\uff0c\u7ffb\u8f6c\u8eab\u8eaf\uff0c\u8131\u4e0d \u82b1\u683c\u683c\u7b11\u9053\uff1a\u201c\u9192\u6765\u9192\u6765\uff0c\u6211\u7ed9\u4f60\u7aef\u89e3\u9152\u6c64\u6765\u4e86\u3002\u201d\u5f20\u4e39\u67ab\u5514\u5440\u4f5c\u54cd\uff0c\u5ffd\u5730\u5927\u7b11\u9053\uff1a\u201c\u54c8 \u54c8\uff0c\u4eca\u591c\u6211\u4e0d\u8d70\u4e86\uff0c\u5916\u9762\u767d\u9aa8\u5982\u5c71\uff0c\u6211\u6015\uff0c\u6211\u6015\u5440\uff01\u201d\u8131\u4e0d\u82b1\u9053\uff1a\u201c\u5582\uff0c\u4f60\u9192\u9192\uff0c\u8fd9\u91cc\u4e0d\u662f \u571f\u6728\u5821\uff0c\u54ea\u6765\u7684\u767d\u9aa8\u5982\u5c71\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u8c01\u8bf4\u4e0d\u662f\u571f\u6728\u5821\uff1f\u4f60\u542c\uff0c\u5916\u9762\u4e0d\u662f\u5175\u9a6c\u53ae\u6740\u4e4b \u58f0\uff1f\u201d\u6b63\u662f\uff1a \u3000\u3000\u8bc8\u9189\u4f6f\u72c2\u65bd\u5999\u8ba1\uff0c\u5f53\u5802\u6c14\u715e\u5973\u5a07\u5a25\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u840d\u8e2a\u4fa0\u5f71\u5f55\u300b\u2014\u2014\u7b2c\u4e8c\u5341\u4e94\u56de\u3000\u77f3\u5854\u85cf\u9f99\u95ef\u5173\u52ab\u5929\u5b50\u3000\u4e39\u5fc3\u62a5\u56fd\u62d4\u5251\u62a4\u4ec7\u4eba \u6881\u7fbd\u751f\u300a\u840d\u8e2a\u4fa0\u5f71\u5f55\u300b \u7b2c\u4e8c\u5341\u4e94\u56de\u3000\u77f3\u5854\u85cf\u9f99\u95ef\u5173\u52ab\u5929\u5b50\u3000\u4e39\u5fc3\u62a5\u56fd\u62d4\u5251\u62a4\u4ec7\u4eba \u3000\u3000\u8131\u4e0d\u82b1\u7b11\u9053\uff1a\u201c\u8fd9\u662f\u5e9c\u4e2d\u7684\u6b66\u58eb\u51fa\u5dee\uff0c\u4e0d\u5fc5\u60ca\u6050\u3002\u201d\u4f38\u624b\u66ff\u5f20\u4e39\u67ab\u63a2\u70ed\uff0c\u5f20\u4e39\u67ab\u5ffd\u5730\u624b \u6307\u4e00\u52fe\uff0c\u53e3\u4e00\u5f20\uff0c\u54c7\u7684\u4e00\u58f0\uff0c\u5c06\u9002\u624d\u6240\u5403\u7684\u9152\u83dc\u90fd\u5455\u4e86\u51fa\u6765\uff0c\u8131\u4e0d\u82b1\u6700\u559c\u7231\u7684\u4e00\u4ef6\u5939\u6bb5\u65b0 \u88c5\uff0c\u7ed9\u4ed6\u6495\u88c2\uff0c\u5455\u51fa\u7684\u9152\u83dc\uff0c\u76f4\u55b7\u5165\u8863\u5185\uff0c\u6cb9\u817b\u817b\u7684\u9e21\u7247\u8089\u5c51\uff0c\u6cbe\u4e0a\u80f8\u818a\u3002\u8131\u4e0d\u82b1\u867d\u662f\u86ee \u5973\uff0c\u4f46\u751f\u6027\u7231\u6d01\uff0c\u4e0d\u89c9\u76b1\u7709\u9053\uff1a\u201c\u600e\u4e48\u8fd8\u662f\u9189\u6210\u8fd9\u4e2a\u6837\u5b50\uff1f\u201d\u634f\u7740\u9f3b\u5b50\uff0c\u7ed9\u4ed6\u7aef\u6765\u4e00\u7897\u89e3\u9152 \u7684\u767e\u5408\u53c2\u6c64\u3002\u5f20\u4e39\u67ab\u628a\u624b\u4e00\u6325\uff0c\u53eb\u9053\uff1a\u201c\u6211\u9189\u6b32\u7720\u541b\u4e14\u53bb\uff01\u5514\uff0c\u5514\uff0c\u82e5\u7136\u4e0d\u53bb\u518d\u4e09\u676f\uff01\u201d\u90a3 \u7897\u6c64\u7ed9\u4ed6\u4e00\u62c2\uff0c\u767b\u65f6\u6cfc\u7ffb\uff0c\u90fd\u6e85\u5728\u8131\u4e0d\u82b1\u8eab\u4e0a\uff0c\u7897\u4e5f\u8dcc\u5f97\u7c89\u788e\u3002\u8131\u4e0d\u82b1\u7ed9\u4ed6\u4e00\u62c2\uff0c\u624b\u8155\u75bc \u75db\uff0c\u53ea\u89c1\u5f20\u4e39\u67ab\u7eb3\u5934\u53c8\u7761\uff0c\u53cc\u624b\u4e71\u6253\u5e8a\u6cbf\uff0c\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u4ed6\u7adf\u7136\u9189\u5f97\u8fd9\u6837\u5389\u5bb3\uff0c\u8fde\u89e3\u9152\u7684\u4e94 \u8fa3\u8fd4\u9b42\u9999\u4e5f\u6ca1\u6709\u7528\u3002\u201d\u8131\u4e0d\u82b1\u7ed9\u4ed6\u5455\u5410\u5f97\u6ee1\u8eab\u90fd\u662f\u6c61\u7269\uff0c\u6c14\u5473\u6781\u4e4b\u96be\u95fb\uff0c\u53c8\u6015\u7ed9\u4ed6\u6253\u7740\uff0c\u53ea \u597d\u9000\u4e86\u51fa\u53bb\u3002\u53ea\u542c\u5f97\u5f20\u4e39\u67ab\u5514\u5440\u53eb\u9053\uff1a\u201c\u7a97\u5b50\u6253\u5f00\uff0c\u4e0d\u8981\u628a\u706f\u5439\u7184\uff0c\u6211\u6015\u9ed1\u5440\u4f60\u77e5\u4e0d\u77e5 \u9053\uff1f\u201d\u4f3c\u9189\u975e\u9189\uff0c\u8131\u4e0d\u82b1\u521a\u4e00\u56de\u5934\uff0c\u5f20\u4e39\u67ab\u53c8\u201c\u54c7\u201d\u7684\u4e00\u53e3\u5455\u5410\u51fa\u6765\u3002\u8131\u4e0d\u82b1\u53f9\u4e86\u53e3\u6c14\uff0c\u8d70 \u51fa\u53bb\u6362\u8863\uff0c\u53eb\u4f8d\u5973\u66ff\u4ed6\u6536\u62fe\u6253\u626b\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u6446\u8131\u4e86\u8131\u4e0d\u82b1\u7684\u7ea0\u7f20\uff0c\u5fc3\u4e2d\u751a\u662f\u5f97\u610f\uff0c\u4f46\u4e00\u60f3\u5230\u4e5f\u5148\u7be1\u4f4d\u5728\u5373\uff0c\u4ecd\u662f\u660e\u671d\u4e4b\u7978\uff0c \u5140\u662f\u60f3\u4e0d\u51fa\u5982\u4f55\u5e94\u4ed8\uff0c\u5fc3\u4e2d\u53c8\u4e0d\u89c9\u6101\u70e6\u3002\u6309\u8bf4\u4ed6\u6b64\u65f6\u82e5\u8981\u523a\u6740\u4e5f\u5148\uff0c\u90a3\u4e5f\u5e76\u975e\u96be\u4e8b\u3002\u4e0d\u8fc7\u523a \u6740\u4e00\u4eba\uff0c\u5e76\u4e0d\u80fd\u4ece\u6839\u672c\u6d88\u5f25\u4e24\u56fd\u4e4b\u95f4\u7684\u5e72\u6208\u6218\u7978\uff0c\u800c\u4e14\u88ab\u4fd8\u864f\u7684\u660e\u671d\u7687\u5e1d\u66f4\u4f1a\u56e0\u6b64\u4e00\u6765\uff0c\u7edd \u4e86\u751f\u8fd8\u4e4b\u671b\u3002\u4e8e\u8c26\u4e0e\u5f20\u4e39\u67ab\u7684\u62b1\u8d1f\u90fd\u662f\u613f\u4e0e\u90bb\u56fd\u548c\u7766\u76f8\u5904\uff0c\u6545\u6b64\u5f20\u4e39\u67ab\u7edd\u4e0d\u613f\u6548\u5bfb\u5e38\u7684\u523a\u5ba2 \u6240\u4e3a\u5f92\u901e\u4e00\u65f6\u4e4b\u5feb\u3002 \u3000\u3000\u53ea\u542c\u5f97\u5e9c\u4e2d\u6572\u4e86\u4e09\u66f4\uff0c\u4ece\u7a97\u53e3\u671b\u51fa\u53bb\uff0c\u4f46\u89c1\u65b0\u6708\u5728\u5929\uff0c\u5fae\u98ce\u52a8\u6811\uff0c\u5f20\u4e39\u67ab\u60f3\u6765\u60f3\u53bb\uff0c\u8fd8 \u662f\u60f3\u4e0d\u51fa\u6700\u597d\u7684\u529e\u6cd5\u3002\u5ffd\u89c1\u7a97\u5916\u679d\u5934\uff0c\u9ed1\u5f71\u4e00\u98d8\uff0c\u5f20\u4e39\u67ab\u672a\u53ca\u51fa\u58f0\uff0c\u6765\u4eba\u5df2\u7ad9\u5728\u5e8a\u524d\uff0c\u7aef\u7684 \u662f\u8fc5\u6377\u5f97\u51fa\u4eba\u610f\u5916\u3002\u5f20\u4e39\u67ab\u770b\u6e05\u695a\u65f6\uff0c\u4e0d\u7531\u5f97\u559c\u51fa\u671b\u5916\uff0c\u539f\u6765\u5374\u662f\u81ea\u5df1\u7684\u5e08\u7236\u8c22\u5929\u534e\u3002 \u3000\u3000\u8c22\u5929\u534e\u4f4e\u58f0\u9053\uff1a\u201c\u6211\u4ece\u4f60\u5728\u57ce\u4e2d\u7559\u4e0b\u7684\u6697\u53f7\uff0c\u5bfb\u5230\u4e91\u857e\uff0c\u77e5\u9053\u4f60\u88ab\u56f0\u5728\u8fd9\u513f\uff0c\u4e8b\u4e0d\u5b9c \u8fdf\uff0c\u4f60\u5feb\u5feb\u968f\u6211\u8d70\u5427\u3002\u201d\u5f20\u4e39\u67ab\u8a00\u9053\uff1a\u201c\u6211\u82e5\u8981\u8d70\uff0c\u65e9\u5c31\u8d70\u4e86\u3002\u201d\u5c06\u4e3a\u96be\u4e4b\u5904\uff0c\u7ea6\u7565\u4e00\u8bf4\u3002 \u8c22\u5929\u534e\u70b9\u70b9\u5934\u9053\uff1a\u201c\u90a3\u4f60\u6253\u7b97\u5982\u4f55\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u56db\u5e08\u53d4\uff08\u98de\u5929\u9f99\u5973\uff09\u6765\u4e86\u5417\uff1f\u201d\u8c22\u5929\u534e \u9053\uff1a\u201c\u6765\u4e86\uff0c\u5728\u5ba2\u5bd3\u91cc\u966a\u4f34\u4e91\u857e\u3002\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u4e8c\u5e08\u4f2f\u5462\uff1f\u201d\u8c22\u5929\u534e\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u6ca1\u5bfb \u7740\u3002\u201d\u4f3c\u6709\u8bb8\u591a\u8bdd\u8981\u8bf4\u3002\u5f20\u4e39\u67ab\u6025\u9053\uff1a\u201c\u6211\u73b0\u5728\u5df2\u60f3\u597d\u8131\u8eab\u4e4b\u8ba1\uff0c\u660e\u65e5\u5f53\u53ef\u51fa\u53bb\uff0c\u90a3\u65f6\u518d\u8be6 \u7ec6\u503e\u8c08\u3002\u73b0\u5728\u4e8b\u4e0d\u5b9c\u8fdf\uff0c\u8bf7\u4f60\u548c\u53f6\u5e08\u53d4\u5373\u523b\u5230\u7687\u5bab\u53bb\u3002\u201d\u8c22\u5929\u534e\u9053\uff1a\u201c\u5e72\u4ec0\u4e48\uff1f\u201d\u5f20\u4e39\u67ab\u5728 \u4ed6\u5e08\u7236\u8033\u8fb9\u4f4e\u58f0\u8bf4\u4e86\u51e0\u53e5\u3002\u6682\u4e14\u6309\u4e0b\u4e0d\u8868\u3002 \u3000\u3000\u8c22\u5929\u534e\u53bb\u540e\uff0c\u5f20\u4e39\u67ab\u5982\u89e3\u4e86\u5fc3\u5934\u4e4b\u7ed3\uff0c\u8f7b\u677e\u8212\u5feb\uff0c\u653e\u6000\u7761\u4e86\u4e00\u89c9\u3002\u4e5f\u4e0d\u77e5\u7761\u4e86\u591a\u5c11\u65f6 \u5019\uff0c\u5ffd\u88ab\u58f0\u54cd\u60ca\u9192\uff0c\u62ac\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u623f\u4e2d\u5750\u7740\u4e5f\u5148\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u6025\u5fd9\u5750\u8d77\uff0c\u53ea\u89c1\u9633\u5149\u900f\u8fdb\u7eb1\u7a97\u5df2\u662f\u7b2c\u4e8c\u65e5\u7684\u6e05\u6668\u3002\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u592a\u5e08\uff0c\u4f60\u597d\u65e9 \u554a\uff01\u201d\u4e5f\u5148\u9053\uff1a\u201c\u5514\uff0c\u65e9\uff01\u4f60\u9152\u9192\u4e86\u5417\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u6628\u665a\u5931\u793c\uff0c\u8bf7\u592a\u5e08\u52ff\u7f6a\u3002\u201d\u4e5f\u5148 \u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u60f3\u597d\u4e86\u5417\uff1f\u4f60\u4eec\u7236\u5b50\u662f\u5426\u613f\u4e0e\u6211\u8054\u540c\uff0c\u526a\u9664\u963f\u523a\uff0c\u5171\u56fe\u5bcc\u8d35\uff1f\u201d\u5f20 \u4e39\u67ab\u9053\uff1a\u201c\u60f3\u597d\u5566\uff0c\u6211\u6b63\u6709\u8bdd\u8981\u4e0e\u592a\u5e08\u4e00\u8bf4\u3002\u201d\u4e5f\u5148\u9053\uff1a\u201c\u4f60\u8bf4\u3002\u201d\u5f20\u4e39\u67ab\u89c1\u4ed6\u7709\u5934\u6253\u7ed3\u8138 \u4f3c\u5bd2\u51b0\uff0c\u5fc3\u4e2d\u5df2\u6599\u5230\u662f\u4ec0\u4e48\u4e8b\u60c5\uff0c\u6697\u6697\u597d\u7b11\u3002 \u3000\u3000\u539f\u6765\u6628\u591c\u9752\u8c37\u6cd5\u5e08\u4e0e\u9ebb\u7ffc\u8d5e\u7167\u5e38\u5230\u5bab\u4e2d\u8f6e\u503c\uff0c\u66ff\u4e5f\u5148\u6697\u4e2d\u76d1\u89c6\u7687\u5ba4\u7684\u52a8\u9759\uff0c\u4e09\u66f4\u8fc7\u540e\uff0c \u5ffd\u89c1\u6709\u4e24\u6761\u9ed1\u5f71\uff0c\u4ece\u5bab\u4e2d\u98d8\u7136\u800c\u51fa\uff0c\u4e24\u4eba\u4e0a\u53bb\u62e6\u622a\u95ee\u8bdd\uff0c\u90a3\u9ed1\u5f71\u51fa\u624b\u5982\u7535\uff0c\u53ea\u4e00\u62db\u5c31\u628a\u9752\u8c37 \u6cd5\u5e08\u8111\u888b\u524a\u4e86\uff0c\u9ebb\u7ffc\u8d5e\u6b66\u529f\u867d\u9ad8\uff0c\u4e5f\u4e0d\u8fc7\u63a5\u4e86\u4e24\u62db\uff0c\u5c31\u88ab\u654c\u4eba\u524a\u4e86\u8033\u6735\u3002\u53ea\u542c\u5f97\u654c\u4eba\u7b11\u9053\uff1a \u201c\u9976\u4f60\u4e00\u547d\uff0c\u62a5\u4e0e\u4e5f\u5148\u77e5\u9053\uff0c\u4ed6\u82e5\u53ea\u662f\u60f3\u5728\u74e6\u523a\u79f0\u738b\uff0c\u8fd9\u4e2a\u54b1\u4eec\u4e0d\u7ba1\uff0c\u4f46\u6b32\u5728\u7be1\u4f4d\u4e4b\u540e\uff0c\u518d \u4fb5\u4e2d\u534e\uff0c\u54b1\u4eec\u5374\u662f\u9976\u4ed6\u4e0d\u5f97\u3002\u201d\u8bf4\u8bdd\u7684\u662f\u4e24\u4e2a\u6c49\u4eba\uff0c\u4e00\u6643\u4e0d\u89c1\u3002\u8fd9\u6d88\u606f\u4eca\u65e9\u4e5f\u5148\u5f97\u77e5\uff0c\u771f\u662f \u53c8\u60ca\u53c8\u6c14\uff0c\u65e2\u9a87\u4e14\u6101\u3002\u4ee4\u4e5f\u5148\u60ca\u9a87\u7684\u662f\uff1a\u9752\u8c37\u6cd5\u5e08\u662f\u7ea2\u6559\u5587\u561b\u4e2d\u7684\u6709\u540d\u4eba\u7269\uff0c\u9ebb\u7ffc\u8d5e\u7684\u6b66\u529f \u4e5f\u5728\u4e5f\u5148\u5e10\u4e0b\u53f7\u79f0\u7b2c\u4e00\u540d\u6b66\u58eb\u7684\u989d\u5409\u591a\u4e4b\u4e0a\uff0c\u800c\u8fd9\u4e24\u4f4d\u88ab\u4e5f\u5148\u5f53\u4f5c\u5de6\u53f3\u624b\u7684\u4eba\u7269\uff0c\u5374\u88ab\u654c\u4eba \u4e0d\u8d39\u5439\u7070\u4e4b\u529b\uff0c\u6740\u6b7b\u523a\u4f24\uff0c\u800c\u4e14\u53ea\u4e0d\u8fc7\u662f\u4e00\u4e24\u62db\u7684\u529f\u592b\uff01\u8bbe\u82e5\u8fd9\u4e24\u4eba\u5230\u592a\u5e08\u5e9c\u884c\u523a\uff0c\u4f55\u4eba\u53ef \u4ee5\u9632\u5fa1\uff1f\u4ee4\u4e5f\u5148\u5fe7\u6101\u7684\u662f\uff1a\u8fd9\u4e24\u4e2a\u6c49\u4eba\u660e\u660e\u662f\u4ece\u4e2d\u56fd\u6765\u7684\uff0c\u5374\u6697\u62a4\u74e6\u523a\u7687\u5ba4\uff0c\u8fd8\u770b\u51fa\u4ed6\u7684\u5fc3 \u610f\uff0c\u53ea\u6015\u7be1\u4f4d\u4e4b\u8c0b\u4e5f\u8981\u53d7\u5230\u83ab\u5927\u969c\u788d\u3002 \u3000\u3000\u4e5f\u5148\u903c\u5f20\u4e39\u67ab\u56de\u590d\u4ed6\u6628\u665a\u7684\u95ee\u9898\uff0c\u5f20\u4e39\u67ab\u4e00\u7b11\u8bf4\u9053\uff1a\u201c\u592a\u5e08\u4f60\u4e45\u5386\u620e\u884c\uff0c\u60f3\u5fc5\u719f\u77e5\u5175 \u6cd5\u3002\u201d\u4e5f\u5148\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u5175\u6cd5\u6709\u4e91\uff1a\u5907\u591a\u5219\u5206\u529b\u8584\u5219\u8d25\u3002\u6700\u5fcc\u51e0\u65b9\u9762\u540c\u65f6\u4f5c \u6218\uff0c\u5404\u56fd\u90fd\u8981\u4e89\u53d6\u2018\u4e0e\u56fd\u2019\uff08\u6309\u201c\u4e0e\u56fd\u201d\uff1a\u8fd9\u4e00\u540d\u8bcd\u672c\u662f\u4e2d\u56fd\u53e4\u4ee3\u7684\u7528\u8bed\uff0c\u81f3\u8fd1\u4ee3\u53c8\u590d\u901a \u7528\uff09\uff0c\u8054\u6a2a\u5408\u7eb5\uff0c\u53ea\u60f3\u591a\u6811\u4e0e\u56fd\uff0c\u5c11\u6811\u654c\u4eba\uff0c\u5c31\u662f\u8fd9\u4e2a\u9053\u7406\u3002\u201d\u4e5f\u5148\u9053\uff1a\u201c\u8fd9\u9053\u7406\u6211\u5c82\u80fd\u4e0d \u77e5\uff1f\u6240\u4ee5\u624d\u60f3\u4f60\u6211\u643a\u624b\uff0c\u5148\u7edf\u4e00\u4e86\u74e6\u523a\u518d\u8bf4\u3002\u201d\u5f20\u4e39\u67ab\u7b11\u9053\uff1a\u201c\u6211\u7236\u5b50\u7684\u529b\u91cf\u6709\u9650\uff0c\u4e2d\u56fd\u7684 \u529b\u91cf\u65e0\u7a77\u3002\u201d\u4e5f\u5148\u9ed8\u7136\u4e0d\u8bed\u3002\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u6211\u8fd9\u6b21\u6df1\u5165\u4e2d\u539f\uff0c\u6df1\u611f\u4e2d\u56fd\u5730\u5927\u4eba\u591a\uff0c\u82e5\u7528\u5f97\u5176 \u5f53\uff0c\u4e0d\u8981\u8bf4\u4e00\u4e2a\u74e6\u523a\uff0c\u5c31\u662f\u5341\u4e2a\u74e6\u523a\u4e5f\u52a8\u6447\u4e0d\u5f97\u3002\u201d\u4e5f\u5148\u9053\uff1a\u201c\u4f60\u662f\u7ed9\u660e\u671d\u4f5c\u8bf4\u5ba2\u4e48\uff1f\u201d\u5f20 \u4e39\u67ab\u5927\u7b11\u9053\uff1a\u201c\u6211\u7684\u8eab\u4e16\uff0c\u4f60\u5c82\u6709\u4e0d\u77e5\uff0c\u6211\u4f55\u81f3\u4e8e\u4e3a\u660e\u671d\u4f5c\u8bf4\u5ba2\u3002\u82e5\u5b9a\u8981\u8bf4\u6211\u662f\u8bf4\u5ba2\uff0c\u90a3\u4e48 \u6211\u662f\u4e3a\u4e86\u4e2d\u56fd\u4e5f\u4e3a\u4e86\u74e6\u523a\uff0c\u524d\u6765\u5411\u4f60\u6e38\u8bf4\u3002\u201d\u4e5f\u5148\u9053\uff1a\u201c\u597d\uff0c\u4f60\u8bf4\u3002\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u76ee\u4e0b\u4e2d \u56fd\u4e8e\u8c26\u5f53\u653f\uff0c\u6574\u519b\u7ecf\u6b66\uff0c\u4e0a\u4e00\u6b21\u8fdb\u5175\u4e2d\u56fd\uff0c\u5c1a\u53ef\u4ee5\u6253\u5230\u5317\u4eac\uff0c\u8bbe\u82e5\u4f60\u4e0b\u4e00\u6b21\u518d\u8fdb\u5175\u4e2d\u56fd\uff0c\u53ea \u6015\u6253\u5165\u8fb9\u5173\u4e5f\u672a\u5fc5\u53ef\u80fd\u3002\u975e\u4f46\u6b64\u4e5f\uff0c\u8bbe\u82e5\u4e2d\u56fd\u77e5\u9053\u4f60\u60f3\u7be1\u4f4d\u79f0\u738b\uff0c\u518d\u56fe\u79f0\u9738\uff0c\u5b83\u7d22\u6027\u6325\u5175\u5317 \u8fdb\uff0c\u4e0e\u963f\u523a\u8054\u76df\uff0c\u4e3a\u74e6\u523a\u5e73\u4e71\uff0c\u4f60\u53c8\u5982\u4f55\uff1f\u201d \u3000\u3000\u4e5f\u5148\u4e0d\u7531\u5f97\u5fc3\u4e2d\u4e00\u6014\uff0c\u5f20\u4e39\u67ab\u8fd9\u8bdd\u82e5\u662f\u534a\u5e74\u4e4b\u524d\u6240\u8bf4\uff0c\u4ed6\u5fc5\u5b9a\u5927\u7b11\u4e0d\u5df2\uff0c\u90a3\u65f6\u4ed6\u4ee5\u4e3a\u4e2d \u56fd\u6307\u65e5\u53ef\u5e73\uff0c\u54ea\u4f1a\u5c06\u660e\u671d\u7684\u519b\u961f\u653e\u5728\u773c\u4e0b\u3002\u7ecf\u8fc7\u5317\u4eac\u8fd9\u4e00\u573a\u5927\u6218\u4e4b\u540e\uff0c\u4ed6\u624d\u611f\u5230\u4e2d\u56fd\u5b9e\u662f\u4e0d \u6613\u541e\u5e76\u3002\u5230\u4e86\u6700\u8fd1\uff0c\u4e8e\u8c26\u6574\u987f\u8fb9\u5173\uff0c\u53c8\u9760\u4e86\u5f6d\u548c\u5c1a\u9057\u4e0b\u7684\u5730\u56fe\uff0c\u63a5\u8fde\u6253\u4e86\u51e0\u6b21\u80dc\u4ed7\uff0c\u5c06\u74e6\u523a \u5bc7\u8fb9\u7684\u519b\u961f\u90fd\u9a71\u9010\u56de\u53bb\uff0c\u4e5f\u5148\u66f4\u662f\u5fc3\u60ca\uff0c\u6e10\u6e10\u611f\u5230\u53cd\u4e86\u8fc7\u6765\u660e\u671d\u7684\u519b\u961f\u4e5f\u8db3\u4ee5\u6784\u6210\u4ed6\u7684\u5a01\u80c1 \u4e86\u3002\u8fd9\u65f6\u542c\u4e86\u5f20\u4e39\u67ab\u7684\u8bdd\uff0c\u8868\u9762\u867d\u7136\u4e0d\u9732\u795e\u8272\u597d\u50cf\u4e0d\u4ee5\u4e3a\u610f\uff0c\u5176\u5b9e\u5374\u662f\u5fc3\u4e2d\u6697\u60ca\u3002\u5f20\u4e39\u67ab\u53c8 \u9053\uff1a\u201c\u8fd9\u6b21\u6211\u6df1\u5165\u4e2d\u534e\uff0c\u5bdf\u89c9\u4e2d\u56fd\u6c11\u6c14\u6fc0\u6602\u786e\u5b9e\u662f\u4e0d\u53ef\u8f7b\u4fae\u3002\u5c24\u5176\u4ed6\u4eec\u7684\u7687\u5e1d\u5728\u571f\u6728\u5821\u88ab\u4f60 \u6240\u4fd8\uff0c\u4e3e\u56fd\u4e0a\u4e0b\uff0c\u66f4\u8ba4\u4e3a\u662f\u5947\u803b\u5927\u8fb1\u3002\u6050\u6015\u4f60\u672a\u6325\u519b\u5357\u4e0b\uff0c\u4ed6\u4eec\u5df2\u5148\u81ea\u8981\u5317\u4e0a\u62a5\u4ec7\u4e86\u3002\u592a\u5e08 \u4f60\u5175\u529b\u867d\u5f3a\uff0c\u4e5f\u672a\u80fd\u5916\u5fa1\u4e2d\u534e\u4e3e\u56fd\u4e4b\u5175\u5185\u6297\u963f\u523a\u5357\u90e8\u7684\u52b2\u65c5\u5427\uff1f\u201d\u4e5f\u5148\u5e72\u54b3\u4e24\u58f0\uff0c\u795e\u8272\u6e10 \u53d8\uff0c\u5374\u4ecd\u662f\u786c\u7740\u5934\u76ae\u8bf4\u9053\uff1a\u201c\u6211\u62e5\u6709\u96c4\u5175\u5341\u4e07\uff0c\u6218\u5c06\u5343\u5458\uff0c\u5373\u7b97\u4e2d\u56fd\u4e0e\u963f\u523a\u5185\u5916\u5939\u653b\uff0c\u6700\u591a \u4ea6\u4e0d\u8fc7\u7389\u77f3\u4ff1\u711a\u800c\u5df2\uff0c\u5927\u4e08\u592b\u751f\u4e0d\u4e3a\u9738\u4e3b\uff0c\u6b7b\u4ea6\u5f53\u4e3a\u9b3c\u96c4\uff0c\u6709\u4f55\u8db3\u60e7\uff1f\u201d\u5f20\u4e39\u67ab\u54c8\u54c8\u5927\u7b11\uff0c \u9053\uff1a\u201c\u82e5\u662f\u5c1a\u672a\u51fa\u5e08\uff0c\u5c31\u6b7b\u4e8e\u975e\u547d\uff0c\u90a3\u53c8\u5982\u4f55\uff1f\u4f55\u51b5\u6210\u738b\u8d25\u5bc7\uff0c\u81ea\u6709\u516c\u8bba\uff0c\u53ea\u6015\u592a\u5e08\u81ea\u547d\u82f1 \u96c4\uff0c\u540e\u4eba\u5374\u672a\u5fc5\u5c06\u4f60\u6bd4\u4e3a\u5b5f\u5fb7\uff08\u66f9\u64cd\uff09\u3002\u201d\u4e5f\u5148\u88ab\u4ed6\u8bf4\u5f97\u6c14\u9981\uff0c\u9053\uff1a\u201c\u660e\u671d\u6731\u5bb6\u671d\u8fc1\uff0c\u771f\u662f \u5982\u6b64\u6068\u6211\uff0c\u8981\u6d3e\u4eba\u523a\u6740\u6211\u4e48\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u636e\u6211\u6240\u77e5\uff0c\u660e\u671d\u786e\u662f\u6d3e\u6709\u5251\u5ba2\u524d\u6765\uff0c\u4f1a\u4e0d\u4f1a\u6740 \u4f60\uff0c\u90a3\u5c31\u8981\u770b\u4f60\u7684\u6240\u4f5c\u6240\u4e3a\u4e86\u3002\u201d\u4e5f\u5148\u60f3\u8d77\u6628\u665a\u4e4b\u4e8b\uff0c\u4e0d\u89c9\u6c57\u6bdb\u76f4\u7ad6\uff0c\u5374\u4ecd\u4e0d\u613f\u793a\u5f31\uff0c\u6545\u610f \u7b11\u9053\uff1a\u201c\u660e\u671d\u6709\u9ad8\u624b\u5251\u5ba2\uff0c\u96be\u9053\u6211\u6ca1\u6709\u529b\u8db3\u65a9\u86df\u4f0f\u864e\u7684\u52c7\u58eb\u4e48\uff1f\u201d\u5f20\u4e39\u67ab\u53c8\u662f\u4e00\u9635\u54c8\u54c8\u5927 \u7b11\uff0c\u9053\uff1a\u201c\u4f60\u7684\u52c7\u58eb\u53ea\u662f\u4e00\u6279\u9152\u56ca\u996d\u888b\uff0c\u4e2d\u4ec0\u4e48\u7528\uff1f\u53ea\u6015\u771f\u8981\u78b0\u7740\u9ad8\u624b\u4e4b\u65f6\uff0c\u4e0d\u8fc7\u4e00\u62db\uff0c\u5c31 \u8981\u88ab\u4eba\u524a\u6389\u8111\u888b\u4e86\uff01\u201d\u4e5f\u5148\u4e00\u6014\uff0c\u8df3\u8d77\u6765\u9053\uff1a\u201c\u6628\u665a\u4e4b\u4e8b\uff0c\u4f60\u77e5\u9053\u4e48\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u4ec0\u4e48 \u4e8b\u60c5\uff1f\u6211\u4e0d\u8fc7\u8bf4\u7f62\u4e86\uff0c\u4f60\u7684\u6b66\u58eb\u771f\u7684\u88ab\u4eba\u4e00\u62db\u524a\u6389\u8111\u888b\u4e48\uff1f\u201d\u4e5f\u5148\u60ca\u7591\u4e0d\u5df2\uff0c\u5fc3\u9053\uff1a\u201c\u4ed6\u6628 \u665a\u70c2\u9189\u5982\u6ce5\uff0c\u8db3\u4e0d\u51fa\u6237\uff0c\u6562\u60c5\u771f\u662f\u968f\u53e3\u8bf4\u8bf4\uff0c\u4e0d\u8fc7\u4ed6\u8bf4\u7684\u5012\u975e\u5047\u8bdd\u3002\u201d\u5f20\u4e39\u67ab\u53c8\u7b11\u9053\uff1a\u201c\u662f \u54ea\u4f4d\u52c7\u58eb\u7ed9\u4eba\u6740\u4e86\uff1f\u201d\u4e5f\u5148\u9053\uff1a\u201c\u6ca1\u4ec0\u4e48\uff0c\u6628\u665a\u662f\u6709\u523a\u5ba2\u4e0d\u8fc7\u5df2\u88ab\u6211\u4eec\u9010\u8d70\u4e86\u3002\u6211\u4eec\u4e5f\u6709\u4e00 \u4e8c\u4eba\u53d7\u4f24\u3002\u201d\u5f20\u4e39\u67ab\u563b\u563b\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u90a3\u5c31\u771f\u7b97\u4f60\u4eec\u9020\u5316\u4e86\uff01\u201d\u5176\u5b9e\u6628\u665a\u4e4b\u4e8b\uff0c\u539f\u6765\u5c31\u662f\u4ed6 \u7684\u7b56\u5212\u3002\u6740\u6389\u9752\u8c37\u6cd5\u5e08\uff0c\u524a\u6389\u9ebb\u7ffc\u8d5e\u8033\u6735\u7684\u4eba\uff0c\u4e43\u662f\u8c22\u5929\u534e\u4e0e\u53f6\u76c8\u76c8\u3002 \u3000\u3000\u4e5f\u5148\u53e3\u867d\u5f3a\u786c\uff0c\u5fc3\u4e2d\u5374\u662f\u8d8a\u60f3\u8d8a\u614c\uff0c\u53ea\u542c\u5f97\u5f20\u4e39\u67ab\u53c8\u9053\uff1a\u201c\u592a\u5e08\u76ee\u524d\u7684\u56fe\u8c0b\uff0c\u6050\u975e\u5584 \u7b56\u3002\u201d\u4e5f\u5148\u9053\uff1a\u201c\u90a3\u4f60\u53c8\u6709\u4f55\u9ad8\u89c1\u5462\uff1f\u201d\u5f20\u4e39\u67ab\u6b63\u6b32\u7545\u6240\u6b32\u8a00\uff0c\u5ffd\u542c\u5f97\u5916\u9762\u4eba\u58f0\u5608\u6742\uff0c\u4e5f\u5148 \u7709\u5934\u4e00\u76b1\uff0c\u5524\u8fdb\u4eba\u6765\uff0c\u95ee\u662f\u4f55\u4e8b\u3002 \u3000\u3000\u90a3\u4eba\u9053\uff1a\u201c\u6709\u51e0\u4e2a\u53eb\u5316\u5b50\u8981\u95ef\u8fdb\u5e9c\u4e2d\u5f3a\u5316\uff0c\u8ba8\u538c\u5f97\u5f88\uff01\u201d\u4e5f\u5148\u76b1\u7709\u9053\uff1a\u201c\u8981\u4e48\u5c31\u968f\u4fbf\u65bd \u820d\u4e00\u70b9\uff0c\u8981\u4e48\u5c31\u8d76\u4ed6\u4eec\u51fa\u53bb\uff0c\u8fd9\u4e5f\u503c\u5f97\u5927\u60ca\u5c0f\u602a\u4e48\uff1f\u201d\u6325\u624b\u53eb\u90a3\u4eba\u51fa\u53bb\u3002\u5f20\u4e39\u67ab\u5fc3\u5ff5\u4e00\u52a8\uff0c \u6b63\u81ea\u601d\u91cf\uff0c\u53ea\u542c\u5f97\u4e5f\u5148\u91cd\u53c8\u95ee\u9053\uff1a\u201c\u5f20\u4e16\u5144\u90a3\u4f60\u53c8\u6709\u4f55\u9ad8\u89c1\uff1f\u201d \u3000\u3000\u5f20\u4e39\u67ab\u5fae\u5fae\u4e00\u7b11\uff0c\u6162\u6761\u65af\u7406\u5730\u8bf4\u9053\uff1a\u201c\u592a\u5e08\u82e5\u6b32\u5b89\u5185\u5219\u5fc5\u987b\u5148\u548c\u5916\uff0c\u8fd9\u624d\u53ef\u514d\u53d7\u5185\u5916\u5939 \u653b\u3002\u4e2d\u534e\u5730\u5e7f\u4eba\u591a\uff0c\u7269\u4ea7\u4e30\u9976\uff0c\u74e6\u523a\u82e5\u4e0d\u4fb5\u5b83\uff0c\u5b83\u4e00\u5b9a\u4e0d\u4f1a\u8fdb\u5175\u4fb5\u4f60\u3002\u6211\u770b\uff0c\u4e0d\u5982\u628a\u5927\u660e\u5929 \u5b50\u9001\u56de\u4e2d\u56fd\uff0c\u7f14\u7ed3\u548c\u7ea6\uff0c\u662f\u4e3a\u4e0a\u7b56\uff01\u201d\u4e5f\u5148\u6c89\u541f\u4e0d\u8bed\u3002\u5f20\u4e39\u67ab\u7b11\u9053\uff1a\u201c\u4f60\u4ee5\u524d\u5728\u571f\u6728\u5821\u4e4b \u65f6\uff0c\u5343\u65b9\u767e\u8ba1\uff0c\u5c06\u660e\u671d\u7684\u5929\u5b50\u4fd8\u864f\uff0c\u4e0d\u8fc7\u662f\u60f3\u6301\u6b64\u4ee5\u4e3a\u8981\u631f\u7f62\u4e86\u3002\u76ee\u4e0b\u4e8e\u8c26\u5df2\u53e6\u7acb\u65b0\u541b\uff0c\u518d \u7559\u4ed6\u5728\u6b64\uff0c\u53cd\u800c\u662f\u4e2a\u7978\u80ce\u3002\u201d\u4e5f\u5148\u7ec6\u60f3\uff0c\u786e\u662f\u9053\u7406\uff0c\u9053\uff1a\u201c\u6211\u4e0e\u660e\u671d\u5927\u5c0f\u6570\u767e\u6218\uff0c\u80dc\u591a\u8d25 \u5c11\u3002\u96be\u9053\u8981\u6211\u9001\u660e\u671d\u5929\u5b50\u56de\u53bb\uff0c\u5411\u4e8e\u86ee\u5b50\u6c42\u548c\u5417\uff1f\u201d\u5f20\u4e39\u67ab\u542c\u4ed6\u8bf4\u8bdd\uff0c\u77e5\u4ed6\u5df2\u662f\u60c5\u613f\uff0c\u53ea\u4e0d \u8fc7\u4e3a\u4e86\u9762\u5b50\u95ee\u9898\uff0c\u9042\u7b11\u9053\uff1a\u201c\u4e24\u56fd\u7f14\u548c\uff0c\u5404\u4ee5\u5144\u5f1f\u4e4b\u793c\u5f80\u6765\uff0c\u6709\u4f55\u5c48\u8fb1\uff1f\u592a\u5e08\u82e5\u4e0d\u6b32\u5148\u63d0\u548c \u7ea6\uff0c\u90a3\u5c31\u8bf7\u4e2d\u56fd\u5148\u6d3e\u4f7f\u81e3\uff0c\u5230\u74e6\u523a\u8bae\u548c\uff0c\u4ea6\u793a\u4e3a\u4e0d\u53ef\u3002\u201d\u4e5f\u5148\u773c\u73e0\u4e00\u8f6c\u9053\uff1a\u201c\u4f60\u600e\u6562\u66ff\u4e8e\u86ee \u5b50\u7b54\u5141\u6b64\u4e8b\uff1f\u4f60\u3001\u4f60\u662f\u4f55\u4eba\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u5b9e\u4e0d\u76f8\u7792\uff0c\u6211\u8fd9\u6b21\u91cd\u56de\u74e6\u523a\uff0c\u4e8b\u524d\u89c1\u8fc7\u4e8e\u8c26\u3002 \u6211\u6240\u8bf4\u7684\u76f8\u4fe1\u4e0d\u4f1a\u8fdd\u4e86\u4e8e\u8c26\u4e4b\u610f\u3002\u201d\u4e5f\u5148\u9893\u7136\u5750\u4e0b\uff0c\u8fc7\u4e86\u534a\u664c\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u5fd8\u4e86\u4e16\u4ec7\uff0c\u5c45\u7136 \u4e3a\u6731\u5bb6\u5929\u5b50\u6548\u529b\u5417\uff1f\u201d\u5f20\u4e39\u67ab\u54c8\u54c8\u4e00\u7b11\uff0c\u4ece\u5bb9\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u662f\u4e3a\u4efb\u4f55\u4eba\u6548\u529b\uff0c\u800c\u662f\u4e3a\u4e2d\u56fd\u4e0e \u74e6\u523a\u6548\u529b\u3002\u8bf7\u95ee\u548c\u7ea6\u7f14\u6210\u5c82\u975e\u4e24\u56fd\u82cd\u751f\u4e4b\u798f\uff1f\u201d\u4e5f\u5148\u53c8\u9ed8\u7136\u4e0d\u8bed\uff0c\u8fc7\u4e86\u534a\u664c\uff0c\u8bf4\u9053\uff1a\u201c\u4e24\u56fd \u8bae\u548c\u4e4b\u540e\uff0c\u4f60\u7559\u5728\u4f55\u65b9\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u6211\u662f\u4e2d\u56fd\u4e4b\u4eba\uff0c\u81ea\u7136\u56de\u5230\u4e2d\u56fd\u3002\u201d\u4e5f\u5148\u9053\uff1a\u201c\u90a3\u4f60 \u662f\u8981\u4e0e\u6211\u4f5c\u5bf9\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u592a\u5e08\u82e5\u4e0d\u8fdb\u5175\u4fb5\u5165\u4e2d\u56fd\uff0c\u6211\u53c8\u5c82\u4f1a\u4e0e\u4f60\u4f5c\u5bf9\uff1f\u201d\u4e5f\u5148\u9053\uff1a \u201c\u4f60\u7236\u4eb2\u5462\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u6211\u4ea6\u5fc5\u529d\u4ed6\u56de\u56fd\uff0c\u4ee5\u5ea6\u665a\u5e74\u3002\u201d\u4e5f\u5148\u9053\uff1a\u201c\u4f60\u4eec\u4e0d\u6015\u88ab\u660e\u671d\u5929 \u5b50\u6740\u5bb3\u5417\uff1f\u201d\u5f20\u4e39\u67ab\u7b11\u9053\uff1a\u201c\u90a3\u4e5f\u662f\u6211\u4eec\u5fc3\u7518\u60c5\u613f\uff0c\u4e0d\u987b\u592a\u5e08\u8fc7\u8651\u3002\u201d \u3000\u3000\u4e5f\u5148\u6414\u9996\u5f98\u5f8a\uff0c\u5fc3\u4e2d\u601d\u6f6e\u8d77\u4f0f\uff0c\u60f3\u8d77\u5f20\u4e39\u67ab\u4e4b\u8a00\uff0c\u679c\u7136\u6709\u7406\uff0c\u6743\u8861\u5229\u5bb3\uff0c\u81ea\u5df1\u82e5\u6b32\u7edf\u4e00 \u74e6\u523a\uff0c\u5b9e\u662f\u4e0d\u5b9c\u518d\u4e0e\u660e\u671d\u4e3a\u654c\u3002\u53c8\u60f3\u9053\uff1a\u201c\u5f20\u5b97\u5468\u7236\u5b50\u96c4\u624d\u5927\u7565\uff0c\u7559\u5728\u74e6\u523a\uff0c\u53c8\u4e0d\u80fd\u6536\u4e3a\u5df1 \u7528\u90a3\u4e5f\u53ea\u662f\u5f92\u589e\u52b2\u654c\u800c\u5df2\u3002\u4e0d\u5982\u4e5f\u8ba9\u4ed6\u4eec\u56de\u56fd\uff0c\u4e50\u5f97\u5b89\u5fc3\u3002\u5f85\u6211\u4ed6\u65e5\u7edf\u4e00\u74e6\u523a\u4e4b\u540e\uff0c\u5175\u7cbe\u7cae \u8db3\uff0c\u548c\u7ea6\u968f\u65f6\u53ef\u6495\uff0c\u90a3\u65f6\u518d\u4fb5\u5165\u4e2d\u534e\uff0c\u53c8\u5c82\u6015\u5f20\u4e39\u67ab\u4e0e\u6211\u4f5c\u5bf9\u3002\u53ea\u662f\u5973\u513f\u5a5a\u4e8b\u6015\u4e0d\u80fd\u5982\u613f \u4e86\u3002\u201d \u3000\u3000\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u5927\u4e08\u592b\u4e00\u8a00\u800c\u51b3\uff0c\u592a\u5e08\u5c1a\u6709\u4f55\u7591\u8651\uff1f\u201d\u4e5f\u5148\u53cc\u76ee\u70af\u70af\uff0c\u6bc5\u7136\u8bf4\u9053\uff1a\u201c\u597d\uff0c \u6211\u4f9d\u4f60\u6240\u8a00\u4fbf\u662f\u3002\u53ea\u662f\u6211\u4e5f\u5148\u4ea6\u4e0d\u662f\u53d7\u4eba\u6b3a\u8d1f\u7684\u4eba\uff0c\u660e\u671d\u82e5\u6d3e\u523a\u5ba2\u6765\u6697\u7b97\u6211\uff0c\u6211\u5373\u4e0b\u4ee4\u7ed9\u90e8 \u4e0b\u8bf8\u5c06\uff1a\u6211\u82e5\u6709\u4e0d\u6d4b\uff0c\u8981\u4ed6\u4eec\u5373\u523b\u6325\u519b\u5357\u4e0b\uff0c\u62fc\u4e2a\u7389\u77f3\u4ff1\u711a\uff01\u201d \u3000\u3000\u6b64\u8a00\u8272\u5389\u5185\u834f\uff0c\u5b9e\u662f\u6050\u6015\u81ea\u5df1\u7684\u751f\u547d\u4f1a\u6709\u5371\u9669\u3002\u5f20\u4e39\u67ab\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e2d\u56fd\u4e4b\u4eba\uff0c\u6700 \u8bb2\u4fe1\u4e49\u3002\u4f60\u82e5\u771f\u5fc3\u4e0e\u4e2d\u56fd\u7f14\u548c\uff0c\u4e2d\u56fd\u5c82\u4f1a\u6d3e\u523a\u5ba2\u6740\u4f60\u3002\u201d\u4e5f\u5148\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4fbf\u4e00\u8a00\u4e3a\u5b9a\u3002\u5f85 \u660e\u671d\u7684\u4f7f\u8005\u5230\u6765\uff0c\u6211\u4fbf\u4e0e\u4ed6\u8bae\u548c\u3002\u81f3\u4e8e\u524a\u5e73\u963f\u523a\u7684\u53db\u4e71\uff0c\u8fd9\u4e8b\u4f60\u53c8\u6709\u4f55\u9ad8\u89c1\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a \u201c\u6211\u7236\u5b50\u65e2\u5df2\u51b3\u610f\u56de\u56fd\uff0c\u4f60\u4eec\u74e6\u523a\u7684\u4e8b\u60c5\uff0c\u6211\u4eec\u518d\u4e0d\u63d2\u624b\u4e86\u3002\u201d\u4e5f\u5148\u9053\uff1a\u201c\u597d\uff0c\u4f46\u6c42\u4f60\u4eec\u7f6e \u8eab\u4e8b\u5916\uff0c\u6211\u4e5f\u4e0d\u4e3a\u96be\u4f60\u4eec\u3002\u4f60\u56de\u53bb\u5427\uff0c\u660e\u65e5\u53ef\u53eb\u4f60\u7236\u4eb2\u4e0a\u671d\uff0c\u4eb2\u9012\u8f9e\u5448\u3002\u201d\u5f20\u4e39\u67ab\u81ea\u6668\u81f3 \u5348\uff0c\u8d39\u5c3d\u5fc3\u529b\uff0c\u5c06\u4e5f\u5148\u8bf4\u670d\uff0c\u5fc3\u4e2d\u6b22\u559c\u65e0\u9650\uff0c\u5f53\u4e0b\u4ee5\u5f85\u957f\u8f88\u4e4b\u793c\u544a\u8f9e\uff0c\u8de8\u51fa\u623f\u95e8\uff0c\u5ffd\u53c8\u60f3\u8d77 \u4e00\u4e8b\uff0c\u4e3e\u6b65\u8e0c\u8e87\u3002\u4e5f\u5148\u9053\uff1a\u201c\u4f60\u5c1a\u6709\u53ef\u6c42\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u82e5\u8499\u592a\u5e08\u6069\u51c6\uff0c\u6211\u5c1a\u6b32\u89c1\u660e\u671d\u5929 \u5b50\u4e00\u9762\u3002\u201d \u3000\u3000\u4e5f\u5148\u60f3\u4e86\u4e00\u60f3\uff0c\u9053\uff1a\u201c\u4e5f\u597d\uff0c\u4f60\u8bf4\u4e0e\u4ed6\u542c\uff0c\u4e5f\u53eb\u4ed6\u77e5\u9053\u6211\u7684\u597d\u610f\u3002\u201d\u53eb\u4e86\u4e24\u540d\u6b66\u58eb\u8fdb \u6765\uff0c\u53c8\u60f3\u4e86\u4e00\u60f3\uff0c\u5ffd\u9053\uff1a\u201c\u6211\u4e5f\u4e0e\u4f60\u4e00\u9f50\u53bb\u5427\u3002\u201d\u4e24\u540d\u6b66\u58eb\u89c1\u592a\u5e08\u5c45\u7136\u5f15\u5f20\u4e39\u67ab\u53bb\u89c1\u660e\u671d\u7684 \u7687\u5e1d\u5fc3\u4e2d\u751a\u662f\u9a87\u5f02\u3002 \u3000\u3000\u660e\u671d\u88ab\u4fd8\u7684\u7687\u5e1d\u82f1\u5b97\u7948\u9547\u539f\u6765\u5c31\u88ab\u56da\u5728\u592a\u5e08\u5e9c\u91cc\u4e00\u4e2a\u4f9b\u5949\u4f5b\u50cf\u7684\u77f3\u5854\u5185\u3002\u77f3\u9636\u4e09\u5c42\uff0c\u6bcf \u5c42\u90fd\u6709\u6b66\u58eb\u628a\u5b88\uff0c\u79d8\u5bc6\u4e4b\u6781\uff0c\u8fde\u74e6\u523a\u56fd\u541b\uff0c\u90fd\u4e0d\u77e5\u9053\u4fd8\u864f\u88ab\u56da\u4e4b\u6240\u3002 \u3000\u3000\u7948\u9547\u88ab\u56da\u77f3\u5854\uff0c\u5df2\u8fbe\u4e09\u6708\u3002\u6240\u53d7\u7684\u79cd\u79cd\u6c14\u82e6\uff0c\u96be\u4ee5\u8a00\u5ba3\u3002\u8fd9\u65e5\u5728\u77f3\u5854\u4e4b\u4e2d\u542c\u5916\u9762\u6714\u98ce\u6012 \u53f7\uff0c\u5317\u96c1\u5357\u98de\uff0c\u54c0\u9e23\u5929\u9645\uff0c\u4e0d\u89c9\u60b2\u4ece\u4e2d\u6765\uff0c\u96be\u4ee5\u6b62\u6b47\u3002\u4ed6\u8eab\u4e0a\u8863\u888d\u5df2\u7834\uff0c\u5317\u5730\u82e6\u5bd2\uff0c\u4e5f\u5148\u5374 \u4ecd\u7136\u4e0d\u7ed9\u4ed6\u6dfb\u6362\u65b0\u8863\uff0c\u60f3\u8d77\u516d\u5bab\u7c89\u9edb\u65e7\u65e5\u7e41\u534e\uff0c\u6b63\u81ea\u4f24\u5fc3\u6b32\u7edd\uff0c\u5ffd\u89c1\u77f3\u95e8\u5f00\u5904\uff0c\u4e5f\u5148\u4e0e\u5f20\u4e39 \u67ab\u5e76\u80a9\u8d70\u5165\u3002\u7948\u9547\u5403\u4e86\u4e00\u60ca\uff0c\u53ea\u542c\u5f97\u4e5f\u5148\u95ee\u9053\uff1a\u201c\u4f60\u8ba4\u5f97\u4ed6\u5417\uff1f\u201d\u7948\u9547\u731c\u4e0d\u900f\u5f20\u4e39\u67ab\u7684\u6765 \u610f\uff0c\u60ca\u9b42\u4e0d\u5b9a\uff0c\u55eb\u55eb\u5685\u5685\uff0c\u542b\u7cca\u7b54\u5e94\u3002\u4e5f\u5148\u7b11\u9053\uff1a\u201c\u4ed6\u662f\u4f60\u7684\u4ec7\u4eba\uff0c\u53c8\u662f\u4f60\u7684\u6069\u4eba\uff0c\u4f60\u77e5\u9053 \u5417\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u6c42\u592a\u5e08\u51c6\u6211\u4e0e\u4ed6\u5355\u72ec\u9762\u8c08\u7247\u523b\u3002\u201d\u4e5f\u5148\u9053\uff1a\u201c\u597d\u5427\uff0c\u4f60\u4eec\u4e2d\u56fd\u4eba\u505a\u7684\u4e8b \u60c5\uff0c\u771f\u662f\u4ee4\u6211\u731c\u60f3\u4e0d\u5230\uff01\u4f60\u4eec\u4e24\u5bb6\u6614\u65e5\u66fe\u4e89\u593a\u5929\u4e0b\uff0c\u5982\u4eca\u5374\u53c8\u8981\u4fc3\u819d\u8c08\u5fc3\u4e86\uff01\u201d\u77f3\u5854\u9876\u5c42\u95f4 \u4e3a\u4e24\u8fb9\uff0c\u7948\u9547\u88ab\u56da\u5728\u5185\u8fdb\u7684\u6597\u5ba4\u4e4b\u4e2d\uff0c\u4e5f\u5148\u81ea\u51fa\u5916\u4e0e\u5b88\u536b\u7684\u6b66\u58eb\u4eec\u95f2\u8bdd\u3002 \u3000\u3000\u7948\u9547\u745f\u7f29\u4e0d\u5b89\uff0c\u53ea\u89c9\u5f20\u4e39\u67ab\u7684\u773c\u5149\u4f3c\u5229\u526a\u822c\u5728\u4ed6\u9762\u4e0a\u626b\u6765\u626b\u53bb\uff0c\u5ffd\u5730\u7b11\u9053\uff1a\u201c\u4f60\u505a\u60ef\u7687 \u5e1d\uff0c\u4ece\u672a\u5c1d\u8fc7\u4eba\u751f\u82e6\u5473\uff0c\u5403\u4e00\u70b9\u82e6\u4e5f\u597d\u3002\u201d\u7948\u9547\u5927\u6124\uff0c\u6012\u9053\uff1a\u201c\u539f\u6765\u4f60\u4ee5\u524d\u662f\u5047\u4f5c\u597d\u5fc3\uff01\u6211 \u4ea6\u77e5\u9053\u5eb6\u6c11\u4e4b\u4ec7\u6613\u89e3\uff0c\u5929\u5b50\u4e4b\u4ec7\u96be\u89e3\uff0c\u4f60\u65e2\u662f\u4e5f\u5148\u7684\u4eb2\u4fe1\uff0c\u6211\u4f46\u6c42\u4f60\u51c6\u6211\u5168\u5c38\uff0c\u8981\u6740\u901f\u6740\uff0c \u5929\u5b50\u4e0d\u80fd\u53d7\u8fb1\uff01\u201d\u5f20\u4e39\u67ab\u4f3c\u7b11\u975e\u7b11\u5168\u4e0d\u7406\u4f1a\u4ed6\u7684\u8bdd\uff0c\u81ea\u987e\u81ea\u5730\u8bf4\u9053\uff1a\u201c\u4f60\u53d7\u4e86\u8fd9\u573a\u82e6\u96be\uff0c\u4ee5 \u540e\u4e5f\u5e94\u77e5\u9053\u8be5\u600e\u6837\u53bb\u505a\u7687\u5e1d\u4e86\u5427\uff1f\u5c06\u6765\u4f60\u56de\u5bab\u4e4b\u540e\uff0c\u53ef\u4e0d\u8981\u5fd8\u4e86\u4eca\u65e5\u6240\u53d7\u4e4b\u82e6\u5475\uff01\u201d\u7948\u9547\u6014 \u4e86\u4e00\u6014\uff0c\u5ffd\u5730\u8df3\u8d77\u6765\u9053\uff1a\u201c\u4f60\u8bf4\u7684\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5f20\u4e39\u67ab\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u6700\u591a\u4e0d\u8fc7\u51e0\u6708\uff0c\u4f60\u5c31\u53ef\u4ee5\u56de\u53bb\u5566\uff01\u201d\u7948\u9547\u51e0\u4e4e\u4e0d\u6562\u76f8\u4fe1\u81ea\u5df1\u7684\u8033 \u6735\uff0c\u98a4\u58f0\u8bf4\u9053\uff1a\u201c\u771f\u7684\uff0c\u662f\u4e5f\u5148\u4eb2\u53e3\u5bf9\u4f60\u8bf4\u7684\u4e48\uff1f\u4ed6\u80af\u653e\u6211\u5f52\u56fd\u8ba9\u6211\u91cd\u767b\u5b9d\u5ea7\u5417\uff0c\u91cd\u767b\u5b9d \u5ea7\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u4e0d\u662f\u4e5f\u5148\u613f\u610f\u653e\u4f60\u5f52\u56fd\uff0c\u662f\u4e8e\u8c26\u8981\u63a5\u4f60\u56de\u53bb\u3002\u201d\u7948\u9547\u7b11\u5bb9\u987f\u655b\uff0c\u4f3c\u662f\u4ece \u6696\u5ba4\u4e4b\u4e2d\u7a81\u7136\u6389\u8fdb\u51b0\u7a9f\uff0c\u8138\u4e0a\u73b0\u51fa\u4e00\u6d3e\u6124\u6012\u800c\u53c8\u7edd\u671b\u7684\u795e\u60c5\uff0c\u6307\u7740\u5f20\u4e39\u67ab\u9a82\u9053\uff1a\u201c\u6211\u867d\u88ab \u56da\uff0c\u8fd8\u662f\u5929\u5b50\uff0c\u4f60\u600e\u6562\u518d\u4e09\u620f\u5f04\u4e8e\u6211\uff1f\u201d\u5f20\u4e39\u67ab\u65e2\u89c9\u53ef\u7b11\uff0c\u53c8\u89c9\u53ef\u601c\uff0c\u76ef\u7740\u7948\u9547\u8bf4\u9053\uff1a\u201c\u4f60 \u82e5\u6307\u671b\u654c\u4eba\u81ea\u613f\u653e\u4f60\u56de\u53bb\uff0c\u90a3\u662f\u7ec8\u751f\u4f11\u60f3\u3002\u53ea\u6709\u4e2d\u56fd\u7684\u4eba\u8981\u4f60\u56de\u53bb\uff0c\u4f60\u624d\u6709\u4e00\u7ebf\u751f\u673a\u3002\u4f60\u4ee5 \u4e3a\u53ea\u6709\u4e5f\u5148\u624d\u64cd\u6709\u751f\u6740\u4e4b\u6743\u4e48\uff1f\u5b9e\u8bdd\u5bf9\u4f60\u8bf4\u5427\uff0c\u4f60\u7684\u547d\u8fd0\u64cd\u5728\u4e8e\u8c26\u624b\u4e2d\uff0c\u4e8e\u8c26\u8bf4\u4f60\u80fd\u591f\u56de \u53bb\uff0c\u4f60\u5c31\u80fd\u591f\u56de\u53bb\uff01\u201d\u8fd9\u970e\u65f6\u95f4\uff0c\u7948\u9547\u53ea\u89c9\u5f20\u4e39\u67ab\u7684\u773c\u5149\u3001\u795e\u6c14\u548c\u8bed\u8c03\u90fd\u542b\u6709\u4e00\u80a1\u4ee4\u4eba\u4fe1\u670d \u7684\u529b\u91cf\uff0c\u53eb\u4eba\u4e0d\u6562\u6000\u7591\u3002\u7948\u9547\u987f\u65f6\u88ab\u4ed6\u9547\u6151\u4f4f\u4e86\uff0c\u55eb\u55eb\u5685\u5685\u5730\u9053\uff1a\u201c\u8fd9\u662f\u600e\u4e48\u4e2a\u8bb2\u6cd5\uff1f\u201d\u5f20 \u4e39\u67ab\u9053\uff1a\u201c\u5c31\u56e0\u4e3a\u4f60\u597d\u574f\u4e5f\u7b97\u662f\u4e00\u56fd\u4e4b\u541b\uff0c\u7559\u5728\u654c\u4eba\u624b\u4e2d\uff0c\u603b\u662f\u4e2d\u56fd\u7684\u803b\u8fb1\uff0c\u5c31\u662f\u56e0\u4e3a\u8fd9\u4e2a \u7f18\u6545\uff0c\u6240\u4ee5\u6211\u4eec\u8981\u4f60\u56de\u53bb\u3002\u6709\u4e2d\u56fd\u505a\u4f60\u7684\u9760\u5c71\uff0c\u4e5f\u5148\u600e\u6562\u4e0d\u653e\u4f60\u56de\u53bb\uff1f\u201d\u7ea6\u7565\u5730\u5c06\u4e2d\u56fd\u548c\u74e6 \u523a\u7684\u5f53\u524d\u5f62\u52bf\u5206\u6790\u7ed9\u4ed6\u542c\uff0c\u7948\u9547\u53c8\u60ca\u53c8\u559c\uff0c\u9053\uff1a\u201c\u6211\u82e5\u80fd\u591f\u56de\u53bb\u91cd\u767b\u5b9d\u5ea7\uff0c\u5fc5\u7136\u5c01\u4f60\u505a\u4e00\u4e2a \u5927\u5927\u7684\u5b98\uff0c\u4f60\u8bf4\u4f60\u6b22\u559c\u505a\u4ec0\u4e48\uff1f\u5fa1\u6797\u519b\u7edf\u9886\u8fd8\u662f\u4e5d\u95e8\u63d0\u7763\uff0c\u518d\u4e0d\u7136\u5c31\u505a\u5175\u90e8\u5c1a\u4e66\uff0c\u6211\u603b\u80fd\u5982 \u4f60\u6240\u613f\u3002\u201d\u5f20\u4e39\u67ab\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u56de\u53bb\u4e4b\u540e\uff0c\u662f\u5426\u518d\u505a\u7687\u5e1d\uff0c\u90a3\u662f\u4f60\u4eec\u7687\u5ba4\u5185\u90e8\u7684\u4e8b\u60c5\uff0c\u8fd9 \u4e2a\u6211\u548c\u4e8e\u8c26\u90fd\u7ba1\u4e0d\u7740\u3002\u6211\u4e5f\u4e0d\u5e0c\u7f55\u4f60\u7684\u5b98\u513f\uff01\u201d\u7948\u9547\u7a0d\u611f\u5931\u671b\uff0c\u5583\u5583\u8bf4\u9053\uff1a\u201c\u80fd\u56de\u53bb\u5c31\u597d\uff0c \u80fd\u56de\u53bb\u5c31\u597d\uff01\u201d\u4f3c\u4e4e\u60f3\u8d77\u4ec0\u4e48\uff0c\u5ffd\u53c8\u7cbe\u795e\u4e00\u632f\uff0c\u9053\uff1a\u201c\u6ee1\u671d\u6587\u6b66\u591a\u662f\u6211\u7684\u4eb2\u4fe1\u4e4b\u4eba\uff0c\u7948\u94b0\u62a2 \u4e0d\u4e86\u6211\u7684\u5b9d\u5ea7\u7684\uff0c\u6211\u56de\u53bb\u4e4b\u540e\uff0c\u4ed6\u81ea\u7136\u8981\u8ba9\u6211\u518d\u4e3a\u5929\u5b50\u3002\u4f60\u4e0d\u505a\u5b98\u4e5f\u884c\uff0c\u6211\u53ef\u4ee5\u968f\u4f60\u6b22\u559c\u7ed9 \u4f60\u8d4f\u8d50\u3002\u201d\u5f20\u4e39\u67ab\u538c\u70e6\u4e4b\u6781\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6211\u4ec0\u4e48\u90fd\u4e0d\u8981\uff0c\u53ea\u6c42\u4f60\u4e00\u4e8b\u3002\u201d\u7948\u9547\u9053\uff1a\u201c\u4ec0\u4e48 \u4e8b\u60c5\uff0c\u6211\u90fd\u53ef\u4ee5\u7b54\u5e94\u3002\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u4f60\u56de\u53bb\u4e4b\u540e\uff0c\u82e5\u7136\u91cd\u4e3a\u7687\u5e1d\uff0c\u4f60\u5bf9\u4e8e\u8c26\u600e\u6837\uff1f\u201d\u7948\u9547 \u9053\uff1a\u201c\u8fd9\u4e2a\uff0d\uff0d\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u4ed6\u5728\u4f60\u88ab\u4fd8\u4e4b\u540e\uff0c\u53e6\u7acb\u65b0\u541b\uff0c\u4f60\u5fc3\u4e2d\u4e00\u5b9a\u5f88\u6068\u4ed6\u4e86\uff1f\u201d\u7948\u9547 \u5fd9\u9053\uff1a\u201c\u4e0d\uff0c\u4e0d\uff0c\u6211\u56de\u53bb\u4e4b\u540e\uff0c\u9a6c\u4e0a\u5c06\u4ed6\u8fde\u5347\u4e09\u7ea7\u3002\u201d\u4e8e\u8c26\u76ee\u4e0b\u5df2\u662f\u5185\u9601\u5b66\u58eb\uff08\u76f8\u5f53\u4e8e\u4e1e \u76f8\uff09\u517c\u5175\u90e8\u5c1a\u4e66\uff0c\u5b98\u5c45\u4e00\u54c1\uff0c\u6839\u672c\u5c31\u4e0d\u53ef\u80fd\u518d\u5347\u4e09\u7ea7\u3002\u7948\u9547\u53e3\u4e0d\u62e9\u8a00\uff0c\u80e1\u8bf4\u4e00\u901a\uff0c\u5f20\u4e39\u67ab\u53c8 \u597d\u6c14\u53c8\u597d\u7b11\uff0c\u9053\uff1a\u201c\u4e8e\u9601\u8001\u4e5f\u4e0d\u662f\u8d2a\u56fe\u5bcc\u8d35\u7684\u4eba\uff0c\u4f46\u613f\u4f60\u56de\u53bb\u4e4b\u540e\uff0c\u624b\u4e0b\u7559\u60c5\u9976\u4ed6\u4e00\u6b7b\u5c31\u597d \u5566\u3002\u201d\u7948\u9547\u8fde\u8fde\u8bf4\u9053\uff1a\u201c\u8fd9\u4e2a\u81ea\u7136\uff0c\u8fd9\u4e2a\u81ea\u7136\uff01\u201d\u5f20\u4e39\u67ab\u5389\u58f0\u559d\u9053\uff1a\u201c\u4f60\u8bdd\u53ef\u771f\uff1f\u201d\u7948\u9547\u6014 \u4e86\u4e00\u6014\uff0c\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u5929\u5b50\u65e0\u620f\u8a00\uff01\u201d \u3000\u3000\u5f20\u4e39\u67ab\u5fae\u5fae\u4e00\u7b11\uff0c\u6b63\u6b32\u8bf4\u8bdd\uff0c\u5ffd\u542c\u5f97\u5916\u9762\u4f20\u6765\u4e86\u53eb\u5316\u5b50\u5531\u201c\u83b2\u82b1\u843d\u201d\u7684\u58f0\u97f3\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u5fc3\u4e2d\u4e00\u6014\uff0c\u542c\u5f97\u5916\u9762\u5531\u9053\uff1a\u201c\u4e00\u6735\u4e00\u679d\u83b2\u82b1\uff0c\u7687\u5e1d\u4e5f\u66fe\u4e3a\u53eb\u5316\uff0c\u7687\u5e1d\u8f6e\u6d41\u505a\uff0c\u660e \u5e74\u5230\u6211\u5bb6\u3002\u8fd9\u91cc\u85cf\u6709\u4e2a\u597d\u5b9d\u8d1d\uff0c\u6211\u4eec\u8981\u5411\u4f60\u8ba8\u5316\u5566\uff01\u201d\u4e0b\u9762\u4eba\u58f0\u5608\u6742\uff0c\u4f3c\u662f\u5728\u5411\u90a3\u4e9b\u53eb\u5316\u5b50 \u8ffd\u9010\uff0c\u5ffd\u542c\u5f97\u5916\u9762\u7684\u6b66\u58eb\u53eb\u9053\uff1a\u201c\u6709\u523a\u5ba2\uff01\u201d\u63a5\u7740\u201c\u5495\u549a\u201d\u4e00\u58f0\u4e00\u4e2a\u6b66\u58eb\u521a\u7a97\u53e3\u8df3\u51fa\uff0c\u8fd8\u672a \u8df3\u4e0a\u5c4b\u6a90\uff0c\u5c31\u7ed9\u4eba\u6253\u8dcc\u5760\u5730\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u4e0d\u7531\u5f97\u5403\u4e86\u4e00\u60ca\uff1a\u8fd9\u53eb\u5316\u5b50\u597d\u4fca\u7684\u529f\u592b\uff01\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u53ea\u542c\u5f97\u201c\u8f70\u9686\u201d\u4e00\u58f0 \u5de8\u54cd\uff0c\u56da\u623f\u7684\u7a97\u53e3\u7ed9\u4eba\u6253\u5f00\uff0c\u4e00\u4e2a\u53eb\u5316\u5b50\u8df3\u4e86\u8fdb\u6765\uff0c\u53f3\u624b\u6301\u68d2\u5de6\u624b\u4e00\u4f38\uff0c\u5411\u7740\u7948\u9547\u5f53\u5934\u6293 \u4e0b\u3002\u7948\u9547\u5413\u5f97\u201c\u54c7\u54c7\u201d\u5927\u53eb\uff0c\u5f20\u4e39\u67ab\u4e0d\u53ca\u62d4\u5251\uff0c\u9a88\u6307\u4e00\u6233\uff0c\u90a3\u4eba\u5ffd\u5730\u53eb\u9053\uff1a\u201c\u5f20\u4e39\u67ab\u662f \u4f60\uff01\u201d\u8eab\u5f62\u4e00\u95ea\uff0c\u8fc5\u5373\u98de\u8d77\u4e00\u811a\uff0c\u53c8\u8e22\u7948\u9547\u7684\u819d\u76d6\uff01 \u3000\u3000\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u5440\uff0c\u539f\u6765\u662f\u6bd5\u8001\u524d\u8f88\uff01\u201d\u6bd5\u9053\u51e1\u90a3\u4e00\u817f\u6765\u52bf\u751a\u52b2\uff0c\u5f20\u4e39\u67ab\u53ea\u5f97\u4f7f\u51fa\u5927\u529b\u91d1 \u521a\u624b\u6cd5\uff0c\u5728\u4ed6\u811a\u5e95\u8f7b\u8f7b\u4e00\u637a\uff0c\u6bd5\u9053\u51e1\u5012\u8dc3\u51fa\u53bb\uff0c\u80cc\u810a\u78b0\u5899\uff0c\u6c14\u547c\u547c\u5730\u53eb\u9053\uff1a\u201c\u5f20\u4e39\u67ab\uff0c\u4f60\u95ea \u5f00\u4e00\u8fb9\uff01\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u6709\u8bdd\u597d\u8bf4\uff0c\u4e0d\u8981\u5413\u552c\u8fd9\u843d\u96be\u7687\u5e1d\u5566\uff01\u201d\u6bd5\u9053\u51e1\u6012\u9053\uff1a\u201c\u4f60\u600e\u4e48\u5566\uff1f \u4f60\u66ff\u4e5f\u5148\u505a\u770b\u95e8\u72d7\u5417\uff1f\u201d\u624b\u8d77\u4e00\u68d2\u5f53\u78c1\u7838\u4e0b\uff0c\u5f20\u4e39\u67ab\u54ea\u6709\u65f6\u95f4\u7ec6\u8bf4\uff0c\u53ea\u5f97\u62d4\u51fa\u767d\u4e91\u5b9d\u5251\uff0c\u53cd \u5251\u4e00\u6325\uff0c\u201c\u5f53\u201d\u7684\u4e00\u58f0\u706b\u82b1\u98de\u6e85\uff0c\u4e24\u4eba\u624b\u8155\u90fd\u89c9\u9178\u9ebb\u3002\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u6bd5\u8001\u524d\u8f88\uff0c\u4f60\u5148\u8d70\u51fa\u6b64 \u95e8\u6307\u5b9a\u4e2a\u5730\u70b9\uff0c\u6211\u518d\u53bb\u62dc\u5019\u8046\u6559\u3002\u201d\u6bd5\u9053\u51e1\u4e0d\u5bb9\u5206\u8bf4\uff0c\u8fde\u5288\u4e09\u68d2\uff0c\u7740\u7740\u62a2\u8fdb\uff0c\u5de6\u624b\u4e00\u4f38\u4e00 \u7f29\uff0c\u4ecd\u7136\u60f3\u6293\u7687\u5e1d\u3002 \u3000\u3000\u8fd9\u65f6\u4e0b\u9762\u5608\u5608\u6742\u6742\uff0c\u53ea\u542c\u5f97\u5175\u5668\u78d5\u51fb\u4e4b\u58f0\uff0c\u9707\u8033\u6b32\u804b\uff0c\u4e5f\u5148\u5728\u5916\u9762\u5927\u56b7\u5927\u53eb\uff0c\u53eb\u4e9b\u4ec0 \u4e48\uff0c\u5f20\u4e39\u67ab\u5374\u542c\u4e0d\u51fa\u6765\u3002\u53ea\u89c1\u623f\u95e8\u5f00\u5904\uff0c\u4e24\u4e2a\u6b66\u58eb\u63d0\u5200\u62a2\u8fdb\uff0c\u6bd5\u9053\u51e1\u4e00\u4e2a\u76d8\u9f99\u7ed5\u6b65\uff0c\u964d\u9f99\u68d2 \u4e00\u62db\u201c\u4e91\u6a2a\u79e6\u5cad\u201d\uff0c\u81ea\u5de6\u81f3\u53f3\uff0c\u4e00\u5c01\u4e00\u626b\uff0c\u4e24\u4e2a\u6b66\u58eb\u624b\u4e2d\u7684\u5355\u5200\u90fd\u7ed9\u78d5\u98de\u3002\u6bd5\u9053\u51e1\u5706\u7741\u53cc\u76ee \u731b\u5730\u559d\u9053\uff1a\u201c\u907f\u6211\u8005\u751f\u6321\u6211\u8005\u6b7b\uff01\u201d\u6bd5\u9053\u51e1\u7ef0\u53f7\u201c\u9707\u4e09\u754c\u201d\uff0c\u8fd9\u4e00\u559d\u795e\u5a01\u51db\u51db\uff0c\u715e\u662f\u60ca\u4eba\uff0c \u4e24\u540d\u6b66\u58eb\u4e0d\u7531\u81ea\u5df1\u5730\u8fde\u8fde\u540e\u9000\u3002\u8fd9\u65f6\u53ea\u542c\u5f97\u201c\u683c\u25a1\u683c\u25a1\u201d\u7684\u6c89\u91cd\u811a\u6b65\u58f0\uff0c\u54ce\u54df\u54df\u7684\u547c\u53eb\u58f0\uff0c \u4e52\u4e52\u4e53\u4e53\u7684\u78b0\u51fb\u58f0\uff0c\u4f3c\u662f\u6709\u4eba\u4ece\u4e0b\u9762\u76f4\u6253\u4e0a\u6765\u3002\u6bd5\u9053\u51e1\u6ee1\u9762\u6740\u6c14\uff0c\u6781\u529b\u60f3\u95ef\u8fc7\u5f20\u4e39\u67ab\u7684\u963b \u62e6\uff0c\u8ffd\u9010\u7687\u5e1d\u3002\u5f20\u4e39\u67ab\u559d\u9053\uff1a\u201c\u4f60\u6293\u4ed6\u505a\u4ec0\u4e48\uff1f\u201d\u6bd5\u9053\u51e1\u559d\u9053\uff1a\u201c\u4f60\u5fd8\u4e86\u524d\u4ee3\u7684\u51a4\u4ec7\u5417\uff1f\u8fd9 \u25a1\u4e0d\u914d\u505a\u7687\u5e1d\uff0c\u4f60\u62a4\u4ed6\u4f5c\u4ec0\u4e48\uff1f\u54b1\u4eec\u5c06\u4ed6\u52ab\u56de\u4e2d\u56fd\uff0c\u53e6\u8d77\u4e49\u5e08\u3002\u201d\u5f20\u4e39\u67ab\u6014\u4e86\u4e00\u6014\uff1a\u539f\u6765\u6bd5 \u9053\u51e1\u8fd8\u6709\u62a2\u593a\u5929\u4e0b\u7684\u96c4\u5fc3\u3002\u6b63\u6b32\u8bf4\u8bdd\uff0c\u53ea\u542c\u5f97\u5916\u9762\u53c8\u662f\u4e00\u58f0\u5de8\u54cd\uff0c\u77f3\u5854\u7b2c\u4e09\u5c42\u7684\u5854\u95e8\u5df2\u7ed9\u4eba \u6253\u5f00\uff0c\u4e00\u4e2a\u4eba\u7c97\u58f0\u5927\u53eb\u9053\uff1a\u201c\u54c8\uff0c\u5999\u6781\u5566\uff0c\u4f60\u4e5f\u5728\u8fd9\u91cc\uff0c\u5148\u5403\u6d12\u5bb6\u4e09\u767e\u7985\u6756\uff01\u201d\u5374\u662f\u8c22\u5929\u534e \u4e0e\u53f6\u76c8\u76c8\u904d\u5bfb\u89c5\u4e0d\u89c1\u7684\u6f6e\u97f3\u548c\u5c1a\u3002\u5f20\u4e39\u67ab\u4e00\u773c\u77a5\u51fa\uff0c\u53ea\u89c1\u4e5f\u5148\u8eb2\u5728\u4e00\u4e2a\u89d2\u843d\uff0c\u6b63\u6307\u6325\u536b\u58eb\u5835 \u622a\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u5927\u5403\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u4e8c\u5e08\u4f2f\u751f\u6027\u7c97\u9c81\uff0c\u83ab\u4e0d\u8981\u88ab\u4ed6\u4e00\u6756\u6253\u6b7b\u4e5f\u5148\uff0c\u8fd9\u4e8b\u53ef\u5c31\u9ebb \u70e6\uff01\u4e5f\u5148\u7684\u513f\u5b50\u548c\u90e8\u5c06\u8fd8\u6709\u51e0\u5341\u4e07\u5927\u519b\uff0c\u82e5\u56e0\u6b64\u800c\u53c8\u5f15\u8d77\u4e24\u56fd\u7684\u4e00\u573a\u5927\u6218\u53ea\u6050\u6d41\u8840\u4e0d\u6b62\u5343 \u91cc\u3002\u201d\u6b32\u8981\u95ef\u51fa\uff0c\u5374\u53c8\u88ab\u6bd5\u9053\u51e1\u7684\u964d\u9f99\u68d2\u5c01\u4f4f\u3002\u5f20\u4e39\u67ab\u4e60\u4e86\u300a\u7384\u529f\u8981\u8bc0\u300b\u4e4b\u540e\uff0c\u6b66\u529f\u5df2\u6bd4\u6bd5 \u9053\u51e1\u9ad8\u51fa\u4e00\u7b79\uff0c\u4f46\u8feb\u5207\u4e4b\u95f4\u5374\u662f\u95ef\u4e0d\u51fa\u53bb\uff0c\u4f55\u51b5\u4ed6\u53c8\u4e0d\u60f3\u4f24\u4eba\u3002\u5f20\u4e39\u67ab\u5fc3\u4e2d\u5927\u6025\uff0c\u5ffd\u5730\u53eb \u9053\uff1a\u201c\u9707\u4e09\u754c\uff0c\u4f60\u8fd8\u6709\u6c5f\u6e56\u4fe1\u4e49\u5417\uff1f\u201d\u6bd5\u9053\u51e1\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u8981\u62a2 \u5929\u4e0b\uff0c\u4e5f\u8fd8\u8f6e\u4e0d\u5230\u4f60\uff01\u201d\u5f20\u4e39\u67ab\u521d\u6b21\u5165\u5173\u4e4b\u65f6\uff0c\u66fe\u5e26\u4e86\u7956\u5148\u7684\u4fe1\u7269\uff0d\uff0d\u90a3\u5e45\u82cf\u5dde\u85cf\u5b9d\u56fe\uff0c\u5230 \u8fc7\u6bd5\u9053\u51e1\u7684\u5bb6\u4e2d\uff0c\u5f53\u65f6\u4e24\u4eba\u66fe\u6bd4\u8fc7\u4e00\u573a\uff0c\u6bd5\u9053\u51e1\u8f93\u4e86\u4e00\u62db\uff0c\u8bf4\u8fc7\u4ee5\u540e\u5929\u5927\u7684\u4e8b\u60c5\u90fd\u8ba9\u5f20\u4e39\u67ab \u8bf4\u8bdd\uff0c\u4ea6\u5373\u662f\u6697\u793a\u5f20\u4e39\u67ab\u82e5\u8981\u4e89\u593a\u5929\u4e0b\uff0c\u4ed6\u53ea\u80fd\u5e2e\u52a9\uff0c\u4e0d\u4f1a\u4f5c\u5bf9\u3002\u6b64\u65f6\u5f20\u4e39\u67ab\u6b64\u8a00\u4e00\u51fa\uff0c\u6bd5 \u9053\u51e1\u867d\u4ecd\u5fc3\u6709\u4e0d\u7518\uff0c\u964d\u9f99\u68d2\u7684\u62db\u6570\u5374\u5df2\u7f13\u6162\u4e0b\u6765\uff0c\u5ffd\u5730\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u597d\uff0c\u5c31\u8ba9\u4f60\u5566\uff01\u201d\u8eab\u5f62 \u4e00\u6643\uff0c\u4ece\u6253\u7834\u7684\u7a97\u53e3\u7a9c\u51fa\u3002 \u3000\u3000\u7948\u9547\u5413\u5f97\u9762\u65e0\u4eba\u8272\uff0c\u5140\u81ea\u8eb2\u5728\u89d2\u843d\u5598\u6c14\uff0c\u5f20\u4e39\u67ab\u65e0\u6687\u518d\u7406\u4f1a\u4ed6\uff0c\u6025\u5fd9\u4e00\u8dc3\u800c\u51fa\u3002\u53ea\u89c1\u6f6e \u97f3\u548c\u5c1a\u5c06\u90a3\u6839\u7897\u53e3\u822c\u7c97\u5927\u7684\u7985\u4ed7\u821e\u5f97\u547c\u547c\u4f5c\u54cd\uff0c\u4e0e\u4ed6\u5bf9\u654c\u7684\u662f\u989d\u5409\u591a\u548c\u53e6\u5916\u4e24\u5916\u6b66\u58eb\u3002\u989d\u5409 \u591a\u6b66\u529f\u867d\u4e0d\u5f31\uff0c\u4f46\u6f6e\u97f3\u548c\u5c1a\u7684\u5916\u5bb6\u529f\u592b\u767b\u5cf0\u9020\u6781\uff0c\u4e00\u767e\u96f6\u516b\u8def\u4f0f\u9b54\u6756\u6cd5\u51cc\u5389\u975e\u5e38\uff0c\u6bcf\u4e00\u6756\u6253 \u4e0b\u90fd\u662f\u529b\u903e\u5343\u94a7\uff0c\u5c06\u989d\u5409\u591a\u4e0e\u90a3\u4e24\u540d\u6b66\u58eb\u6740\u5f97\u53ea\u6709\u62db\u67b6\u4e4b\u529f\uff0c\u5e76\u65e0\u8fd8\u624b\u4e4b\u529b\u3002\u8bf4\u6765\u4e8b\u4e5f\u51d1 \u5de7\uff0c\u4e5f\u5148\u65b0\u8058\u7684\u4e24\u540d\u9ad8\u624b\uff0c\u9752\u8c37\u6cd5\u5e08\u4e0e\u9ebb\u7ffc\u8d5e\u6b66\u529f\u4e0d\u5728\u6f6e\u97f3\u4e4b\u4e0b\uff0c\u4f46\u8fd9\u4e24\u4eba\u6070\u5de7\u5728\u6628\u665a\u88ab\u8c22 \u5929\u534e\u4e0e\u53f6\u76c8\u76c8\u53cc\u5251\u5251\u74a7\uff0c\u4e0d\u8fc7\u4e24\u62db\uff0c\u5c31\u5f04\u5f97\u4e00\u6b7b\u4e00\u4f24\uff0c\u8fd9\u4e5f\u9020\u6210\u4e86\u6bd5\u9053\u51e1\u4e0e\u6f6e\u97f3\u548c\u5c1a\u80fd\u987a\u5229 \u95ef\u8fdb\u7684\u539f\u56e0\u3002 \u3000\u3000\u4e5f\u5148\u89c1\u5f20\u4e39\u67ab\u51b2\u51fa\uff0c\u51b7\u7b11\u4e00\u58f0\u8bf4\u9053\uff1a\u201c\u54fc\uff0c\u4f60\u4eec\u6c49\u4eba\u597d\u6ca1\u4fe1\u4e49\u3002\u201d\u5f20\u4e39\u67ab\u4e00\u8a00\u4e0d\u53d1\uff0c\u7a81 \u5730\u8dc3\u800c\u524d\uff0c\u4f38\u624b\u5c31\u6293\u6f6e\u97f3\u548c\u5c1a\u7684\u7985\u6756\uff0c\u6f6e\u97f3\u5927\u6012\u559d\u9053\uff1a\u201c\u4f60\u4eec\u5e08\u5f92\u90fd\u4e0d\u662f\u597d\u4eba\uff01\u201d\u7985\u6756\u5411\u524d \u4e00\u633a\uff0c\u5f20\u4e39\u67ab\u500f\u5730\u6536\u638c\u95ea\u5f00\u3002\u5f20\u4e39\u67ab\u8fd9\u4e00\u6293\u6070\u662f\u65f6\u5019\uff0c\u8fd9\u65f6\u6f6e\u97f3\u548c\u5c1a\u6b63\u7528\u5230\u4e00\u62db\u201c\u529b\u5212\u9e3f \u6c9f\u201d\uff0c\u52bf\u82e5\u96f7\u9706\uff0c\u989d\u5409\u591a\u4e07\u96be\u62b5\u6321\uff0c\u5374\u7ed9\u5f20\u4e39\u67ab\u7528\u5de7\u529b\u5378\u5f00\u6756\u52bf\u3002\u989d\u5409\u591a\u4e58\u673a\u8df3\u51fa\u5708\u5b50\uff0c\u90a3 \u4e24\u4e2a\u6b66\u58eb\u4e5f\u8ddf\u7740\u9000\u4e0b\uff0c\u770b\u5f20\u4e39\u67ab\u5982\u4f55\u5bf9\u4ed8\u3002 \u3000\u3000\u6f6e\u97f3\u53c8\u7c97\u58f0\u559d\u9053\uff1a\u201c\u4e39\u67ab\uff0c\u4f60\u6562\u72af\u4e0a\u4f5c\u4e71\uff1f\u4f60\u518d\u963b\u62e6\uff0c\u770b\u6211\u6562\u4e0d\u6562\u5c06\u4f60\u4e00\u6756\u6253\u6b7b\uff1f\u201d\u5f20 \u4e39\u67ab\u9053\uff1a\u201c\u4f60\u5c31\u662f\u5c06\u6211\u6253\u6b7b\uff0c\u6211\u4e5f\u8981\u4f60\u9000\u51fa\u6b64\u5730\uff01\u201d\u6f6e\u97f3\u548c\u5c1a\u7985\u6756\u4e00\u6325\uff0c\u62e6\u8170\u75be\u626b\uff0c\u5f20\u4e39\u67ab \u7684\u5378\u529b\u5de7\u62db\uff0c\u53ea\u80fd\u5076\u4e00\u4f7f\u7528\uff0c\u4e0d\u6562\u7a7a\u624b\u5bf9\u4ed8\u6f6e\u97f3\u7684\u7985\u6756\uff0c\u53ea\u5f97\u6325\u5251\u76f8\u8fce\uff0c\u5e08\u4f2f\u5e08\u4f84\uff0c\u5c31\u5728\u6597 \u5ba4\u4e4b\u4e2d\u5927\u6218\u3002\u5f20\u4e39\u67ab\u5728\u521d\u6b21\u5165\u5173\u4e4b\u65f6\uff0c\u4e0e\u5e08\u4f2f\u5df2\u4e0d\u76f8\u4e0a\u4e0b\uff0c\u8fd9\u65f6\u4ed6\u6b66\u529f\u7cbe\u8fdb\uff0c\u65e9\u5728\u6f6e\u97f3\u4e4b \u4e0a\u3002\u6f6e\u97f3\u548c\u5c1a\u8fde\u6325\u4e86\u5341\u6570\u6756\uff0c\u5f20\u4e39\u67ab\u7adf\u662f\u4e00\u6b65\u4e0d\u9000\uff0c\u5251\u62db\u968f\u7740\u6756\u52bf\u6240\u79fb\uff0c\u6f6e\u97f3\u548c\u5c1a\u7684\u7985\u6756\u6253 \u5411\u4f55\u65b9\uff0c\u90fd\u7ed9\u4ed6\u7d27\u7d27\u5c01\u4f4f\uff01 \u3000\u3000\u6f6e\u97f3\u548c\u5c1a\u6c14\u6012\u4ea4\u5e76\uff0c\u731b\u626b\u4e00\u6756\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u4e39\u67ab\uff0c\u4f60\u76ee\u4e2d\u5c1a\u6709\u5c0a\u957f\u5417\uff1f\u201d\u5f20\u4e39\u67ab\u5fae\u5fae \u7b11\u9053\uff1a\u201c\u8bf7\u5e08\u4f2f\u6055\u7f6a\uff0c\u8bf4\u4ec0\u4e48\u4e5f\u5f97\u8bf7\u5e08\u4f2f\u5148\u9000\u51fa\u8fd9\u91cc\uff0c\u4ee5\u540e\u6211\u518d\u5411\u4f60\u6162\u6162\u8d54\u7f6a\u3002\u201d\u6b64\u8a00\u4e00 \u51fa\uff0c\u5ba4\u4e2d\u4f17\u6b66\u58eb\u90fd\u662f\u4e00\u6115\uff1a\u201c\u54a6\uff0c\u539f\u6765\u8fd8\u662f\u5e08\u4f2f\u548c\u5e08\u4f84\u54e9\uff01\u201d\u201c\u54c8\u54c8\uff0c\u5999\u6781\u5566\uff0c\u5e08\u4f2f\u539f\u6765\u8fd8 \u6253\u4e0d\u8fc7\u5e08\u4f84\uff01\u201d\u201c\u672c\u4e8b\u4e0d\u6d4e\uff0c\u5374\u4ee5\u8001\u538b\u4eba\uff0c\u597d\u4e0d\u8981\u8138\uff01\u201d\u8c08\u8bba\u4e0e\u8ba5\u7b11\u8fd9\u58f0\uff0c\u55a7\u95f9\u4e00\u7247\uff0c\u6f6e\u97f3 \u548c\u5c1a\u6c14\u5f97\u6ee1\u9762\u901a\u7ea2\uff0c\u9661\u7136\u5927\u559d\u9053\uff1a\u201c\u5c0f\u755c\u751f\uff0c\u4ee5\u540e\u6211\u518d\u4e0e\u4f60\u7b97\u5e10\u3002\u201d\u7985\u6756\u4e00\u62d6\uff0c\u51b2\u51fa\u77f3\u5854\uff0c \u53ea\u82e6\u4e86\u68af\u95f4\u7684\u6b66\u58eb\uff0c\u7ed9\u4ed6\u4e00\u9635\u4e71\u6253\uff0c\u4e2a\u4e2a\u53d7\u4f24\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u4ece\u7a97\u53e3\u671b\u51fa\uff0c\u53ea\u89c1\u6bd5\u9053\u51e1\u5df2\u7387\u9886\u4e09\u4e2a\u4e5e\u4e10\uff0c\u51b2\u51fa\u91cd\u56f4\uff0c\u770b\u8fd9\u4e09\u4e2a\u4e5e\u4e10\u7684\u8eab\u624b\uff0c\u4ea6 \u662f\u975e\u51e1\uff0c\u4e0b\u9762\u867d\u6709\u6570\u5341\u540d\u6b66\u58eb\uff0c\u5374\u662f\u963b\u62e6\u4e0d\u4f4f\u3002\u6f6e\u97f3\u548c\u5c1a\u4e00\u51fa\uff0c\u4e94\u4eba\u4f1a\u5408\uff0c\u8fc5\u5373\u4fbf\u95ef\u51fa\u53bb \u4e86\u3002\u5f20\u4e39\u67ab\u5fc3\u9053\uff1a\u201c\u8fd9\u51e0\u4e2a\u53eb\u5316\u5b50\u4e5f\u771f\u672c\u4e8b\uff0c\u4e0d\u77e5\u4ed6\u4eec\u600e\u4f1a\u63a2\u542c\u5f97\u51fa\u7687\u5e1d\u56da\u5728\u6b64\u5730\u3002\u201d \u3000\u3000\u4e5f\u5148\u4e5f\u501a\u7740\u7a97\u53e3\u89c2\u671b\uff0c\u8fd9\u65f6\u677e\u4e86\u53e3\u6c14\uff0c\u56de\u8fc7\u9762\u6765\uff0c\u53ea\u542c\u5f97\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u8bf7\u592a\u5e08\u6055\u7f6a\uff0c\u655d \u5e08\u4f2f\u4ee5\u4e3a\u6211\u56f0\u5728\u6b64\uff0c\u6709\u6240\u8bef\u4f1a\uff0c\u6211\u81ea\u4f1a\u627e\u4ed6\u89e3\u91ca\u3002\u6211\u6562\u62c5\u4fdd\u4ee5\u540e\u518d\u4e5f\u6ca1\u6709\u4eba\u6765\u9a9a\u6270\u4f60\u5566\u3002\u201d \u4e5f\u5148\u4eb2\u773c\u89c1\u4ed6\u51fa\u4e86\u5168\u529b\uff0c\u62b5\u5fa1\u5e08\u4f2f\uff0c\u89e3\u4e86\u81ea\u5df1\u7684\u5371\u9669\uff0c\u5bf9\u4ed6\u751a\u6709\u597d\u611f\uff0c\u7b11\u9053\uff1a\u201c\u597d\u5566\uff0c\u54b1\u4eec \u8fd8\u662f\u7167\u4eca\u65e9\u7684\u8bdd\u529e\u4e8b\u3002\u4f60\u4e5f\u4e0d\u5fc5\u591a\u6240\u7591\u8651\u5566\uff01\u201d\u5f20\u4e39\u67ab\u8c22\u4e86\u4e00\u793c\uff0c\u4e5f\u5148\u9053\uff1a\u201c\u73b0\u5728\u53ef\u4ee5\u8fdb\u53bb \u518d\u770b\u770b\u4f60\u4eec\u7684\u7687\u4e0a\u5566\uff01\u201d\u4e0e\u5f20\u4e39\u67ab\u5e76\u80a9\u8d70\u5165\u3002\u53ea\u89c1\u7948\u9547\u9762\u8272\u82cd\u767d\uff0c\u5140\u81ea\u501a\u7740\u5899\u58c1\u53d1\u6296\uff0c\u4e5f\u5148 \u5fae\u5fae\u4e00\u7b11\uff0c\u5fc3\u9053\uff1a\u201c\u8ba9\u4ed6\u56de\u53bb\u518d\u505a\u7687\u5e1d\uff0c\u5012\u662f\u4e8e\u6211\u6709\u5229\u3002\u201d\u8bf4\u9053\uff1a\u201c\u54c8\uff0c\u4f60\u53d7\u60ca\u5566\uff0c\u82e6\u5c3d\u7518 \u6765\uff0c\u5f85\u4f60\u4eec\u7684\u4f7f\u8005\u5230\u6765\uff0c\u4f60\u5c31\u53ef\u4ee5\u56de\u53bb\u518d\u4eab\u798f\u5566\u3002\u4f46\u613f\u4f60\u4e0d\u8981\u5fd8\u4e86\u6211\u7684\u597d\u5904\u624d\u597d\u3002\u201d\u7948\u9547\u6b63 \u60f3\u9053\u8c22\uff0c\u5ffd\u89c1\u5f20\u4e39\u67ab\u5411\u4ed6\u6253\u4e86\u4e2a\u773c\u8272\uff0c\u731b\u7136\u7701\u609f\u81ea\u5df1\u4e43\u662f\u4e00\u56fd\u4e4b\u541b\uff0c\u4e5f\u5148\u4e0d\u8fc7\u662f\u74e6\u523a\u7684\u592a \u5e08\uff0c\u82e5\u5411\u4ed6\u8c22\u6069\uff0c\u5b9e\u662f\u6709\u8fb1\u56fd\u4f53\u3002\u4e8e\u662f\u4e00\u633a\u80f8\u812f\uff0c\u9053\uff1a\u201c\u4e0d\u52b3\u6709\u793c\uff0c\u4f60\u7684\u597d\u5904\u6211\u8bb0\u4f4f\u5566\uff01\u201d \u5f20\u4e39\u67ab\u9053\uff1a\u201c\u592a\u5e08\uff0c\u6211\u8fd8\u8981\u6c42\u4f60\u4e00\u4e8b\u3002\u201d\u4e5f\u5148\u9053\uff1a\u201c\u4f55\u4e8b\u8bf7\u8bf4\u3002\u201d\u5f20\u4e39\u67ab\u5c06\u8eab\u4e0a\u4e00\u4ef6\u8f7b\u8f6f\u7684 \u72d0\u76ae\u62ab\u80a9\u8131\u4e86\u4e0b\u6765\uff0c\u9053\uff1a\u201c\u6c42\u592a\u5e08\u51c6\u6211\u5c06\u8fd9\u4ef6\u62ab\u80a9\u9001\u4e0e\u4ed6\u3002\u201d\u4e5f\u5148\u4f5c\u4e86\u4e00\u4e2a\u60ca\u8be7\u7684\u8868\u60c5\uff0c \u9053\uff1a\u201c\u5440\uff0c\u6211\u4e8b\u5fd9\u7167\u6599\u4e0d\u5230\uff0c\u5e95\u4e0b\u7684\u4eba\u4e5f\u771f\u758f\u5ffd\u7adf\u6ca1\u6709\u7ed9\u4f60\u4eec\u7684\u7687\u4e0a\u6dfb\u7f6e\u65b0\u8863\uff1f\u6765\u4eba\u5440\uff01\u201d \u9a6c\u4e0a\u53eb\u6765\u770b\u5b88\u7684\u4eba\u5429\u5490\u4ed6\u7ed9\u7948\u9547\u5ea6\u8eab\uff0c\u7f6e\u6362\u65b0\u7684\u76ae\u8863\uff0c\u53c8\u5429\u5490\u6bcf\u9910\u996e\u98df\uff0c\u90fd\u8981\u7167\u81ea\u5df1\u6240\u5403\u7684 \u591a\u5f04\u4e00\u4efd\uff0c\u9001\u4e0e\u7948\u9547\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u4ecd\u7136\u5c06\u62ab\u80a9\u63b7\u4e0b\uff0c\u968f\u5728\u4e5f\u5148\u4e4b\u540e\uff0c\u8f6c\u8eab\u8d70\u51fa\uff0c\u4e34\u884c\u4e00\u77a5\uff0c\u53ea\u89c1\u7948\u9547\u773c\u4e2d\uff0c\u6709\u4e24\u70b9 \u6676\u83b9\u7684\u6cea\u5149\u3002\u5f20\u4e39\u67ab\u5fc3\u9053\uff1a\u201c\u770b\u4ed6\u5982\u6b64\uff0c\u5fc3\u4e2d\u60f3\u4e5f\u5e94\u6709\u6240\u611f\u52a8\u3002\u4f46\u613f\u4ed6\u80fd\u8bb0\u4f4f\u4eca\u65e5\u4e4b\u4e8b\uff0c\u4ee5 \u540e\u56de\u53bb\uff0c\u4e0d\u8981\u96be\u4e3a\u4e8e\u8c26\u624d\u597d\u3002\u201d \u3000\u3000\u5f20\u4e39\u67ab\u6015\u8131\u4e0d\u82b1\u7ea0\u7f20\uff0c\u51fa\u4e86\u77f3\u5854\uff0c\u6025\u5fd9\u544a\u8f9e\uff0c\u5148\u5230\u65c5\u820d\u53bb\u770b\u4e91\u857e\uff0c\u4e0d\u6599\u4e91\u857e\u5374\u5df2\u4e0d\u5728\uff0c \u53ea\u7559\u4e0b\u4e00\u5c01\u4fe1\u3002\u6b63\u662f\uff1a \u3000\u3000\u624d\u79bb\u864e\u7a74\u9f99\u6f6d\u5730\uff0c\u67f3\u6697\u82b1\u660e\u53c8\u4e00\u6751\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u840d\u8e2a\u4fa0\u5f71\u5f55\u300b\u2014\u2014\u7b2c\u4e8c\u5341\u516d\u56de\u3000\u52ab\u540e\u5269\u4f59\u751f\u5973\u513f\u6cea\u6d12\u3000\u95e8\u524d\u4f24\u6c38\u522b\u516c\u5b50\u60c5\u4f24 \u6881\u7fbd\u751f\u300a\u840d\u8e2a\u4fa0\u5f71\u5f55\u300b \u7b2c\u4e8c\u5341\u516d\u56de\u3000\u52ab\u540e\u5269\u4f59\u751f\u5973\u513f\u6cea\u6d12\u3000\u95e8\u524d\u4f24\u6c38\u522b\u516c\u5b50\u60c5\u4f24 \u3000\u3000\u4e91\u857e\u7684\u4fe1\u4e0a\u53ea\u662f\u5be5\u5be5\u6570\u884c\uff0c\u53eb\u4ed6\u8bf8\u4e8b\u529e\u59a5\u4e4b\u540e\uff0c\u5373\u5230\u4e1c\u95e8\u5916\u7684\u78a7\u7f57\u5c71\u4e0a\u76f8\u4f1a\u3002\u90a3\u78a7\u7f57\u5c71 \u662f\u4e2a\u540d\u80dc\u4e4b\u5730\uff0c\u9760\u8fd1\u74e6\u523a\u4eac\u57ce\uff0c\u5c71\u4e0a\u6709\u51e0\u5904\u4eba\u5bb6\u3002\u5f20\u4e39\u67ab\u770b\u4fe1\u4e4b\u540e\uff0c\u5fc3\u4e2d\u6697\u6697\u7eb3\u7f55\uff1a\u4e91\u857e\u4ece \u672a\u5230\u8fc7\u74e6\u523a\u4eac\u57ce\uff0c\u4eba\u5730\u751f\u758f\uff0c\u600e\u4e48\u4f1a\u4f4f\u5230\u78a7\u7f57\u5c71\u4e0a\uff1f\u800c\u4e14\u53c8\u6ca1\u5199\u660e\u4f4f\u5740\uff0c\u627e\u8d77\u6765\u5c82\u4e0d\u9ebb\u70e6\uff1f \u53c8\u60f3\u5230\u5979\u6025\u6025\u8fc1\u5c45\uff0c\u5b9a\u662f\u9003\u907f\u4e5f\u5148\u7684\u4fa6\u9a91\uff0c\u514d\u4e0d\u4e86\u4e3a\u5979\u62c5\u5fe7\u3002 \u3000\u3000\u4e91\u857e\u65e2\u8d70\uff0c\u5f20\u4e39\u67ab\u53ea\u597d\u5148\u884c\u56de\u5bb6\u3002\u4e5f\u5148\u6d3e\u6765\u76d1\u89c6\u7684\u536b\u58eb\u679c\u7136\u5168\u5df2\u64a4\u8d70\uff0c\u6fb9\u53f0\u706d\u660e\u7ed9\u4ed6\u5f00 \u95e8\uff0c\u4e24\u4eba\u76f8\u89c1\uff0c\u81ea\u6709\u4e00\u756a\u6b22\u559c\u3002\u6fb9\u53f0\u706d\u660e\u9053\uff1a\u201c\u524d\u51e0\u65e5\u6211\u4eec\u88ab\u56f0\u5728\u5e9c\u4e2d\uff0c\u771f\u662f\u95f7\u6781\u4e86\uff0c\u4f9d\u6211 \u7684\u6027\u513f\u771f\u60f3\u6253\u51fa\u53bb\u3002\u53ea\u662f\u4e3b\u516c\u5374\u575a\u51b3\u4e0d\u8bb8\u3002\u201d\u5f20\u4e39\u67ab\u7b11\u9053\uff1a\u201c\u8fd8\u662f\u4e0d\u8981\u6253\u7684\u597d\u3002\u6211\u7684\u7236\u4eb2 \u5462\uff1f\u201d\u6fb9\u53f0\u706d\u660e\u9053\uff1a\u201c\u4e3b\u516c\u8fd1\u65e5\u5fc3\u4e8b\u91cd\u91cd\uff0c\u4f60\u56de\u6765\u4e86\u6b63\u597d\u3002\u4ed6\u5c31\u5728\u4e66\u623f\u5185\u3002\u201d \u3000\u3000\u5f20\u4e39\u67ab\u8f7b\u8f7b\u8d70\u8fdb\u4e66\u623f\uff0c\u53ea\u89c1\u7236\u4eb2\u6b63\u5728\u652f\u5934\u9ed8\u5750\u82e5\u6709\u6240\u601d\u3002\u5f20\u4e39\u67ab\u53eb\u4e86\u4e00\u58f0\u201c\u7239\u7239\u201d\uff0c\u5f20 \u5b97\u5468\u9053\uff1a\u201c\u55ef\uff0c\u4f60\u56de\u6765\u4e86\uff0c\u6211\u8fd8\u4ee5\u4e3a\u4eca\u751f\u96be\u4ee5\u518d\u89c1\u4f60\u5462\uff01\u201d\u773c\u6cea\u6f78\u7136\u800c\u4e0b\u3002\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u4e0d \u5b5d\u513f\u56de\u6765\u8bf7\u7f6a\u4e86\u3002\u201d\u5f20\u5b97\u5468\u9053\uff1a\u201c\u6211\u542c\u6fb9\u53f0\u5c06\u519b\u8bf4\u4f60\u5df2\u5230\u8fc7\u82cf\u5dde\u4e86\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u6b63\u662f\u4e3a \u6b64\u8bf7\u7f6a\uff0c\u7956\u5148\u7684\u5b9d\u85cf\u548c\u90a3\u5f20\u5730\u56fe\u6211\u90fd\u5df2\u53d1\u6398\u6765\uff0c\u4f46\u5374\u9001\u7ed9\u660e\u671d\u7684\u4e8e\u8c26\uff0c\u8ba9\u4ed6\u5e2e\u52a9\u6731\u5bb6\u5929\u5b50\uff0c \u6253\u9000\u74e6\u523a\u4e86\u3002\u201d\u5f20\u5b97\u5468\u9053\uff1a\u201c\u4f60\u7684\u884c\u4e3a\uff0c\u6211\u4ece\u6fb9\u53f0\u5c06\u519b\u53e3\u4e2d\u4ea6\u5df2\u7ea6\u7565\u77e5\u9053\uff0c\u4f60\u6b64\u4e3e\u5bf9\u4e2d\u56fd\u6709 \u529f\uff0c\u4f46\u54b1\u4eec\u5f20\u5bb6\u5374\u6c38\u65e0\u673a\u4f1a\u518d\u4e89\u5929\u4e0b\u4e86\u3002\u201d\u5f20\u4e39\u67ab\u9ed8\u7136\u4e0d\u8bed\uff0c\u6b63\u60f3\u63aa\u8bcd\u529d\u8bf4\uff0c\u5f20\u5b97\u5468\u53c8\u53f9\u53e3 \u6c14\u9053\uff1a\u201c\u751f\u4e0d\u613f\u4e3a\u4e0a\u67f1\u56fd\uff0c\u6b7b\u72b9\u4e0d\u613f\u4f5c\u960e\u7f57\uff0c\u960e\u7f57\u70b9\u9b3c\u5fc3\u5e38\u5fcd\uff0c\u67f1\u56fd\u5fe7\u6c11\u4e8b\u66f4\u591a\u3002\u6211\u7ecf\u8fc7\u4e86 \u8fd9\u573a\u5de8\u53d8\uff0c\u96c4\u5fc3\u58ee\u5fd7\uff0c\u5df2\u6e10\u6d88\u78e8\u3002\u5bb0\u76f8\u4ea6\u4e0d\u613f\u505a\u4e86\uff0c\u505a\u7687\u5e1d\u90a3\u66f4\u9ebb\u70e6\uff0c\u4f60\u65e2\u4e0d\u613f\u4f5c\u5f00\u56fd\u4e4b \u541b\uff0c\u6211\u4ea6\u613f\u5c31\u6b64\u7ec8\u8001\u5f02\u56fd\u4e86\u3002\u4f60\u505a\u7684\u4e8b\u60c5\u6211\u4e0d\u602a\u4f60\u5c31\u662f\u3002\u201d\u5f20\u4e39\u67ab\u529d\u9053\uff1a\u201c\u7239\uff0c\u843d\u53f6\u5f52\u6839\uff0c \u6211\u8fd8\u662f\u671b\u4f60\u91cd\u56de\u6545\u571f\u3002\u201d\u5f20\u5b97\u5468\u53c8\u53f9\u4e86\u53e3\u6c14\u6325\u6325\u624b\u9053\uff1a\u201c\u4f60\u65e5\u6765\u52b3\u7d2f\uff0c\u5148\u53bb\u6b47\u6b47\u5427\uff0c\u4eca\u665a\u518d \u8bf4\u3002\u201d \u3000\u3000\u665a\u996d\u4e4b\u540e\uff0c\u5f20\u4e39\u67ab\u4e0e\u7236\u4eb2\u6f2b\u6b65\u56ed\u4e2d\uff0c\u4f46\u89c1\u660e\u6708\u4e4b\u4e0b\uff0c\u82b1\u5f71\u6276\u758f\uff0c\u7ee3\u69db\u96d5\u680f\uff0c\u98ce\u5149\u5982\u6614\u3002 \u4e24\u7236\u5b50\u501a\u680f\u76f8\u5bf9\uff0c\u4e45\u4e45\u65e0\u8a00\u3002\u5f20\u4e39\u67ab\u6298\u4e0b\u4e00\u6735\u6885\u82b1\uff0c\u9053\uff1a\u201c\u6b64\u5904\u6885\u82b1\u5f00\u5f97\u6bd4\u5f80\u5e74\u66f4\u597d\u4e86\u3002\u201d \u5f20\u5b97\u5468\u9053\uff1a\u201c\u662f\u4e48\uff1f\u4f60\u5230\u8fc7\u82cf\u5dde\u6545\u5bab\uff0c\u90a3\u91cc\u7684\u98ce\u5149\u5982\u4f55\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u90a3\u91cc\u5df2\u7ed9\u5b98\u5bb6\u5356 \u51fa\uff0c\u4f5c\u4e3a\u571f\u9738\u7684\u56ed\u6797\uff0c\u58c1\u4e0a\u7684\u7891\u5e16\u4ea6\u5df2\u5265\u843d\u6a21\u7cca\u4e86\u3002\u201d\u5f20\u5b97\u5468\u4e0d\u80dc\u53f9\u606f\u3002\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u7239\u7239 \u4e0d\u5fc5\u62c5\u5fc3\uff0c\u90a3\u5730\u65b9\u53c8\u7ed9\u5b69\u513f\u8d62\u56de\u6765\u4e86\u3002\u201d\u5f20\u5b97\u5468\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u5f20\u4e39\u67ab\u5c06\u5f53\u65e5\u4e0e\u4e5d\u5934\u72ee\u5b50\u8d4c \u5feb\u6d3b\u6797\u4e4b\u4e8b\u8bf4\u4e86\u4e00\u4e0b\uff0c\u5f20\u5b97\u5468\u867d\u7136\u5fc3\u4e8b\u6ee1\u6000\uff0c\u4e5f\u7ed9\u4ed6\u5f15\u5f97\u54c8\u54c8\u5927\u7b11\u3002\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u4e3a\u513f\u4e0d \u5b5d\uff0c\u4f46\u613f\u80fd\u4f8d\u5949\u7239\u7239\u56de\u53bb\uff0c\u8ba9\u7239\u7239\u5728\u56ed\u4e2d\u5b89\u4eab\u665a\u5e74\u3002\u201d\u5f20\u5b97\u5468\u66f4\u53f9\u53e3\u6c14\uff0c\u795e\u60c5\u843d\u6f20\u4e4b\u6781\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u7239\u7239\u6b63\u597d\u8d81\u6b64\u673a\u4f1a\uff0c\u9000\u51fa\u662f\u975e\u4e4b\u573a\u3002\u201d\u5c06\u4eca\u65e9\u4e0e\u4e5f\u5148\u7684\u8c08\u8bdd\uff0c\u90fd\u544a\u8bc9\u4e86\u7236 \u4eb2\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u5df2\u64c5\u4f5c\u4e3b\u5f20\u66ff\u7239\u7239\u7b54\u5141\u4e86\u4e5f\u5148\uff0c\u660e\u513f\u4e00\u65e9\u9012\u4e0a\u8f9e\u5448\uff0c\u4e0d\u518d\u505a\u8fd9\u52b3\u4ec0\u5b50\u7684\u74e6\u523a \u4e1e\u76f8\u4e86\u3002\u201d\u5f20\u5b97\u5468\u9053\uff1a\u201c\u8fd9\u6b63\u5408\u6211\u7684\u5fc3\u610f\uff0c\u505a\u4e86\u4e8c\u5341\u591a\u5e74\u7684\u4e1e\u76f8\u6211\u662f\u89c9\u5f97\u5f88\u75b2\u5026\u4e86\u3002\u5f53\u5e74\u672c \u5c31\u65e0\u5fc3\u505a\u8fd9\u4e1e\u76f8\u7684\u3002\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u4e91\u65e0\u5fc3\u800c\u51fa\u5cab\uff0c\u9e1f\u5026\u98de\u800c\u77e5\u8fd8\u3002\u7239\u7239\uff0c\u54b1\u4eec\u8fd8\u662f\u91cd\u56de\u5bb6 \u56ed\u7684\u597d\u3002\u201d\u5f20\u5b97\u5468\u53c8\u53f9\u4e86\u53e3\u6c14\uff0c\u4f4e\u58f0\u541f\u9053\uff1a\u201c\u4e91\u65e0\u5fc3\u800c\u51fa\u5cab\uff0c\u9e1f\u5026\u98de\u800c\u77e5\u8fd8\u3002\u9676\u6e0a\u660e\u8fd9\u4e24\u53e5 \u8bf4\u5f97\u597d\uff0c\u5f52\u53bb\u6765\u516e\uff0c\u662f\u5e94\u8be5\u5f52\u53bb\u7684\u65f6\u5019\u4e86\u3002\u201d\u5f20\u4e39\u67ab\u559c\u9053\uff1a\u201c\u90a3\u4e48\u7239\u7239\u660e\u65e9\u9012\u4e0a\u8f9e\u5448\uff0c\u54b1\u4eec \u5f85\u660e\u671d\u7684\u4f7f\u81e3\u5230\u6765\uff0c\u4e24\u56fd\u8bae\u548c\u4e4b\u540e\uff0c\u4fbf\u884c\u5f52\u56fd\u3002\u201d\u5f20\u5b97\u5468\u6447\u4e86\u6447\u5934\uff0c\u5ffd\u5730\u6c89\u58f0\u7b54\u9053\uff1a\u201c\u6211\u6240 \u8bf4\u7684\u5f52\u53bb\uff0c\u4e0d\u662f\u4f60\u6240\u8bf4\u7684\u5f52\u56fd\u3002\u201d\u5f20\u4e39\u67ab\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u5f20\u5b97\u5468\u9053\uff1a\u201c\u9152\u9611\u5e2d\u6563 \u4eba\u5f52\u53bb\uff0c\u5bcc\u8d35\u7e41\u534e\u4e00\u68a6\u7a7a\u3002\u6211\u5728\u5c18\u4e16\u6df7\u4e86\u516d\u5341\u5e74\uff0c\u4e5f\u5e94\u5f52\u53bb\u4e86\u3002\u201d\u58f0\u8c03\u82cd\u51c9\u4e4b\u6781\uff0c\u539f\u6765\u4ed6\u8bf4 \u7684\u201c\u5f52\u53bb\u201d\u6307\u7684\u4e43\u662f\u201c\u6492\u624b\u5f52\u897f\u201d\u3002\u5f20\u4e39\u67ab\u98a4\u58f0\u8bf4\u9053\uff1a\u201c\u7239\u7239\u8001\u5f53\u76ca\u58ee\uff0c\u8ddd\u767e\u5e74\u4e4b\u671f\u5c1a\u8fdc\uff0c \u4f55\u4e3a\u51fa\u6b64\u4e0d\u7965\u4e4b\u8a00\uff01\u201d\u5f20\u5b97\u5468\u51c4\u7136\u7b11\u9053\uff1a\u201c\u5929\u4e0b\u65e0\u4e0d\u6563\u4e4b\u7b75\u5e2d\u3002\u201d\u5f20\u4e39\u67ab\u6025\u9053\uff1a\u201c\u6c5f\u5357\u6c34\u8f6f \u5c71\u6e29\uff0c\u6b63\u5b9c\u56de\u53bb\u9890\u517b\u3002\u201d\u5f20\u5b97\u5468\u9053\uff1a\u201c\u6211\u8fd8\u6709\u9762\u76ee\u91cd\u56de\u6c5f\u5357\u5417\uff1f\u6614\u65e5\u695a\u9738\u738b\u4e0d\u80af\u6e21\u8fc7\u4e4c\u6c5f\uff0c \u4ed6\u4e5f\u662f\u4e0d\u613f\u91cd\u89c1\u6c5f\u4e1c\u7236\u8001\u5440\uff01\u201d\u77db\u76fe\u82e6\u95f7\u7684\u5fc3\u60c5\u6ea2\u4e8e\u8a00\u8868\u3002\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u8fd9\u600e\u4e48\u80fd\u76f8\u6bd4 \u5440\uff1f\u201d\u72b9\u5f85\u529d\u8bf4\uff0c\u5f20\u5b97\u5468\u6446\u6446\u624b\u9053\uff1a\u201c\u6211\u610f\u5df2\u51b3\uff0c\u4e0d\u5fc5\u591a\u8a00\uff0c\u4e1e\u76f8\u4e4b\u804c\u53ef\u8f9e\uff0c\u7956\u5148\u7684\u571f\u5730\u662f \u4e0d\u613f\u91cd\u8e0f\u4e86\u3002\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u90a3\u4e48\u7239\u7239\u662f\u5426\u8ba4\u4e3a\u5b69\u513f\u6b64\u6b21\u4e2d\u56fd\u4e4b\u884c\u662f\u505a\u9519\u4e86\uff1f\u201d\u5f20\u5b97\u5468\u62ac\u9996 \u671b\u5929\uff0c\u8fdc\u5904\u9690\u9690\u4f20\u6765\u80e1\u7b33\u4e4b\u58f0\uff0c\u534a\u664c\u8bf4\u9053\uff1a\u201c\u82e5\u7136\u662f\u6211\u5e74\u8f7b\u56db\u5341\u5e74\uff0c\u6211\u4e5f\u4f1a\u50cf\u4f60\u8fd9\u6837\u5e72\u7684\u3002 \u56e0\u4eba\u6210\u4e8b\uff0c\u5927\u4e0d\u53ef\u9760\u3002\u73b0\u5728\u6211\u5df2\u77e5\u9053\u60f3\u501f\u74e6\u523a\u7684\u52bf\u529b\u6062\u590d\u6211\u4eec\u5927\u5468\u7684\u56fd\u8fd0\uff0c\u8fd9\u60f3\u6cd5\u662f\u9519\u7684 \u4e86\u3002\u201d\u5f20\u4e39\u67ab\u65e2\u5fe7\u4e14\u559c\uff0c\u6fc0\u52a8\u53eb\u9053\uff1a\u201c\u7239\u2026\u2026\u201d\u5f20\u5b97\u5468\u622a\u7740\u8bf4\u9053\uff1a\u201c\u4e0d\u5fc5\u8bf4\u4e86\u3002\u54ce\uff0c\u4e0d\u8fc7\u6211 \u53ef\u5f97\u63d0\u9192\u4f60\uff0c\u4e5f\u5148\u6b64\u4eba\uff0c\u751a\u662f\u72e1\u733e\uff0c\u8fd8\u5f97\u63d0\u9632\u4ed6\u53cd\u590d\u624d\u597d\u3002\u5440\uff0c\u6211\u4f46\u613f\u660e\u671d\u7684\u4f7f\u81e3\u5feb\u5feb\u5230 \u6765\u3002\u6211\u7eb5\u6b7b\u5728\u74e6\u523a\uff0c\u4e5f\u7ec8\u4e8e\u5fd8\u4e0d\u4e86\u4e2d\u56fd\u5440\u3002\u542c\u4f60\u6240\u8bf4\uff0c\u4e8e\u8c26\u662f\u767e\u5e74\u96be\u9047\u7684\u8d24\u81e3\uff0c\u4f46\u613f\u4e2d\u56fd\u4ece \u6b64\u56fd\u8fd0\u660c\u9686\uff0c\u6211\u80fd\u89c1\u7740\u4ed6\u6d3e\u6765\u7684\u4eba\u4e5f\u597d\u3002\u201d \u3000\u3000\u8fd9\u970e\u65f6\u95f4\uff0c\u5f20\u4e39\u67ab\u89c9\u5f97\u4e0e\u7236\u4eb2\u8ddd\u79bb\u5f88\u8fd1\u53c8\u4f3c\u5f88\u8fdc\uff0c\u611f\u89c9\u5230\u7236\u4eb2\u5fc3\u5f26\u7684\u8df3\u52a8\u53c8\u4f3c\u89c9\u4e0d\u80fd\u7406 \u89e3\uff0c\u6b63\u81ea\u51dd\u601d\uff0c\u5ffd\u89c1\u82b1\u6811\u6276\u758f\u4e4b\u5904\uff0c\u4eba\u5f71\u4e00\u95ea\uff0c\u9661\u542c\u5f97\u6fb9\u53f0\u706d\u660e\u559d\u9053\uff1a\u201c\u4f55\u4eba\u5982\u6b64\u6597\u80c6\uff0c\u64c5 \u95ef\u76f8\u5e9c\uff1f\u201d\u547c\u7684\u4e00\u638c\u5288\u53bb\uff0c\u53ea\u542c\u5f97\u201c\u25a1\u523a\u201d\u4e00\u58f0\uff0c\u4e00\u68f5\u82b1\u6811\uff0c\u767b\u65f6\u65ad\u4e86\uff0c\u4e00\u4e2a\u7070\u8863\u4eba\u4ece\u82b1\u6811 \u4e1b\u4e2d\u76f4\u7a9c\u51fa\u6765\uff0c\u6fb9\u53f0\u706d\u660e\u8e09\u8e09\u8dc4\u8dc4\u5730\u5012\u9000\u51e0\u6b65\u624d\u7a33\u5f97\u4f4f\u8eab\u5f62\u3002\u5f20\u4e39\u67ab\u5927\u5403\u4e00\u60ca\uff1a\u8c01\u4eba\u6709\u6b64\u529f \u529b\uff1f\u53ea\u542c\u5f97\u90a3\u4eba\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4e39\u67ab\uff0c\u4f60\u56de\u6765\u4e86\uff1f\u201d\u5f20\u4e39\u67ab\u5b9a\u6674\u4e00\u770b\u5374\u662f\u81ea\u5df1\u7684\u5927\u5e08\u4f2f\u8463\u5cb3\uff0c \u6b22\u559c\u4e4b\u6781\uff0c\u7acb\u523b\u4ecb\u7ecd\u4ed6\u4e0e\u7236\u4eb2\u76f8\u89c1\uff0c\u966a\u4ed6\u56de\u8f6c\u5ba2\u5385\u3002 \u3000\u3000\u5bbe\u4e3b\u5750\u5b9a\uff0c\u8463\u5cb3\u555c\u4e86\u53e3\u8336\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6fb9\u53f0\u5c06\u519b\uff0c\u4f60\u7684\u94c1\u7435\u7436\u638c\u529f\u592b\u6bd4\u4ee5\u524d\u66f4\u4fca \u4e86\u3002\u201d\u6fb9\u53f0\u706d\u660e\u4e5f\u7b11\u9053\uff1a\u201c\u4f60\u7684\u5927\u529b\u91d1\u521a\u624b\u4e5f\u66f4\u96be\u62b5\u6321\u4e86\u3002\u201d\u5f20\u5b97\u5468\u9053\uff1a\u201c\u5c0f\u513f\u8fd9\u6b21\u5728\u56fd\u5185 \u5f97\u5e08\u4f2f\u7167\u987e\uff0c\u611f\u6fc0\u4e0d\u5c3d\u3002\u201d\u8463\u5cb3\u9053\uff1a\u201c\u655d\u5e08\u5f1f\u5728\u74e6\u523a\u5341\u5e74\uff0c\u5f97\u4f60\u7167\u987e\u6211\u66f4\u611f\u6fc0\u5462\uff01\u201d\u53c8\u7b11 \u9053\uff1a\u201c\u4e1e\u76f8\u4e4b\u5fc3\uff0c\u6211\u4eca\u591c\u59cb\u77e5\uff0c\u655d\u5e08\u5f1f\u679c\u7136\u6ca1\u6709\u8bf4\u9519\uff0c\u597d\u5728\u6211\u6ca1\u6709\u9c81\u83bd\u884c\u4e8b\u3002\u201d\u5f20\u4e39\u67ab\u5fc3\u4e2d \u4e00\u6014\u60f3\u9053\uff1a\u201c\u5e78\u800c\u4ed6\u542c\u5230\u6211\u7239\u7239\u534a\u622a\u7684\u8c08\u8bdd\uff0c\u82e5\u662f\u4e8c\u5e08\u4f2f\uff0c\u53ea\u6015\u4e00\u6765\u5c31\u8981\u52a8\u624b\u4e86\u3002\u201d \u3000\u3000\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u5e08\u4f2f\u89c1\u5230\u6211\u7684\u5e08\u7236\u4e86\u5417\uff1f\u201d\u8463\u5cb3\u9053\uff1a\u201c\u89c1\u7740\u5566\u3002\u201d\u5f20\u5b97\u5468\u9053\uff1a\u201c\u8c22\u5148\u751f\u53bb \u4e86\u591a\u65e5\uff0c\u4e8b\u5148\u6211\u6beb\u4e0d\u77e5\u9053\uff0c\u62c5\u5fc3\u5f97\u5f88\u3002\u4ed6\u65e2\u56de\u5230\u4eac\u57ce\uff0c\u4f55\u4ee5\u4e0d\u4e0e\u5148\u751f\u540c\u6765\uff1f\u201d\u8463\u5cb3\u555c\u4e86\u53e3 \u8336\uff0c\u6c89\u541f\u4e0d\u8bed\u3002\u6fb9\u53f0\u706d\u660e\u9053\uff1a\u201c\u4e5f\u5148\u7684\u536b\u58eb\u867d\u5df2\u64a4\u9000\uff0c\u96be\u4fdd\u4ed6\u4e0d\u4f1a\u518d\u6d3e\u4eba\u6765\u6697\u63a2\u3002\u6211\u5230\u524d\u9762 \u67e5\u591c\u770b\u770b\u3002\u201d\u8bdd\u6bd5\u5373\u884c\u3002\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u6fb9\u53f0\u5c06\u519b\u4e5f\u5fd2\u591a\u5fc3\uff0c\u4ed6\u6015\u6211\u4eec\u6709\u4ec0\u4e48\u8bdd\u4e0d\u4fbf\u5728\u4ed6\u9762\u524d \u8bf4\u3002\u201d\u8463\u5cb3\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u6240\u8981\u8bf4\u7684\u6b63\u662f\u4ed6\u5e08\u7236\u7684\u4e8b\u60c5\u3002\u201d\u6fb9\u53f0\u706d\u660e\u7684\u5e08\u7236\u4e0a\u5b98\u5929\u91ce\u6b63\u662f\u7384 \u673a\u9038\u58eb\u7684\u5bf9\u5934\u3002\u5f20\u4e39\u67ab\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u600e\u4e48\uff1f\u4e0a\u5b98\u8fd9\u8001\u9b54\u5934\u4e0d\u662f\u65e9\u5df2\u57cb\u540d\u9690\u4e16\uff0c\u96be\u9053\u73b0\u5728 \u53c8\u518d\u51fa\u5c71\u4e86\u4e48\uff1f\u201d \u3000\u3000\u8463\u5cb3\u9053\uff1a\u201c\u4ed6\u53ef\u6ca1\u6709\u51fa\u5c71\uff0c\u4f46\u6211\u4eec\u5374\u8981\u7ed9\u4ed6\u53bb\u62dc\u5c71\u4e86\u3002\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u8463\u5cb3 \u9053\uff1a\u201c\u8fd9\u8001\u9b54\u5934\u4e0d\u77e5\u600e\u4e48\u6253\u542c\u5230\u6211\u4eec\u51e0\u5e08\u5144\u5f1f\u90fd\u5728\u74e6\u523a\uff0c\u6d3e\u4eba\u901a\u77e5\u4e86\u6211\uff0c\u8981\u6211\u4eec\u8fdb\u5c71\u53bb\u8c12 \u4ed6\u3002\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u4ed6\u8fd9\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d\u8463\u5cb3\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u77e5\u9053\u5440\u3002\u5927\u7ea6\u662f\u60f3\u8f83\u8003\u8f83\u8003\u6211\u4eec \u5427\u3002\u4ed6\u662f\u8001\u524d\u8f88\uff0c\u65e2\u6709\u6b64\u547d\uff0c\u4e0d\u53ef\u4e0d\u4f9d\u7684\u3002\u201d\u5f20\u4e39\u67ab\u6c89\u541f\u8bf4\u9053\uff1a\u201c\u53ef\u4e0d\u77e5\u6fb9\u53f0\u5c06\u519b\u77e5\u9053\u6b64\u4e8b \u5426\uff1f\u201d\u8463\u5cb3\u9762\u8272\u4e00\u6c89\uff0c\u9053\uff1a\u201c\u4ed6\u82e5\u4e0d\u8bf4\uff0c\u4f60\u4f11\u63d0\u8d77\u3002\u201d\u6b66\u6797\u4e2d\u89c4\u77e9\uff0c\u4e24\u6d3e\u7684\u5c0a\u957f\u82e5\u6709\u76f8\u4e89\uff0c \u95e8\u4eba\u5f1f\u5b50\u7eb5\u6709\u5f80\u6765\uff0c\u4e5f\u5e94\u907f\u5fcc\u3002\u5f20\u4e39\u67ab\u5bf9\u8fd9\u4e9b\u89c4\u77e9\u672c\u4e0d\u653e\u5728\u5fc3\u4e2d\uff0c\u4f46\u89c1\u5e08\u4f2f\u8bf4\u5f97\u5982\u6b64\u90d1\u91cd\uff0c \u4e5f\u5c31\u4e0d\u597d\u591a\u6240\u8bf4\u8bdd\u3002 \u3000\u3000\u8463\u5cb3\u7eed\u9053\uff1a\u201c\u4e09\u5341\u5e74\u524d\uff0c\u54b1\u4eec\u7684\u5e08\u7236\u4e0e\u4e0a\u5b98\u5929\u91ce\u5728\u5ce8\u5d4b\u4e4b\u5dc5\uff0c\u6597\u4e86\u4e09\u65e5\u4e09\u591c\u4e0d\u5206\u80dc\u8d1f\uff0c \u90a3\u65f6\u672c\u6709\u4e09\u5341\u5e74\u4e4b\u540e\u91cd\u4f1a\u4e4b\u7ea6\u3002\u4f46\u4e0d\u4e45\u4ed6\u4eec\u4e24\u4eba\u5c31\u90fd\u9690\u5c45\uff0c\u4e00\u5728\u4e2d\u539f\uff0c\u4e00\u5728\u8499\u8fb9\uff0c\u5f7c\u6b64\u4e0d\u76f8 \u5f80\u6765\u3002\u6211\u4e5f\u4ee5\u4e3a\u8fd9\u4e8b\u8bf4\u8fc7\u4fbf\u7b97\u4e86\u3002\u54ea\u77e5\u4eca\u5e74\u6625\u521d\uff0c\u542c\u8fd9\u91cc\u7684\u4e00\u4f4d\u6b66\u6797\u670b\u53cb\u8bf4\uff0c\u4e0a\u5b98\u5929\u91ce\u4ecd\u6709 \u610f\u8df5\u7ea6\u3002\u6240\u4ee5\u6211\u624d\u8d76\u56de\u53bb\u901a\u77e5\u4f60\u7684\u5e08\u7956\uff0c\u5f53\u65f6\u4ed6\u8001\u4eba\u5bb6\u4e0d\u7f6e\u53ef\u5426\uff0c\u53ea\u8bf4\u4f60\u4eec\u5148\u5230\u74e6\u523a\u53bb\u5427\u3002 \u8fd8\u4e0d\u77e5\u4ed6\u4f1a\u4e0d\u4f1a\u6765\u5462\u3002\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u6211\u542c\u5e08\u7236\u8bf4\u8fc7\uff0c\u5e08\u7956\u6240\u521b\u7684\u53cc\u5251\u5408\u74a7\u7684\u7384\u673a\u5251\u6cd5\uff0c\u5c31 \u662f\u51c6\u5907\u5bf9\u4ed8\u8fd9\u8001\u9b54\u5934\u7684\uff0c\u60f3\u6765\u4ed6\u8001\u4eba\u5bb6\u4e0d\u613f\u4eb2\u81ea\u51fa\u624b\u4e86\u3002\u201d\u8463\u5cb3\u9053\uff1a\u201c\u53cc\u5251\u5408\u74a7\u7684\u5a01\u529b\u6211\u5c1a \u672a\u89c1\uff0c\u4e09\u5e08\u5f1f\u548c\u56db\u5e08\u59b9\u867d\u7136\u806a\u988d\u8fc7\u4eba\uff0c\u6bd4\u6211\u5f3a\u5f97\u591a\uff0c\u4f46\u82e5\u8bf4\u8981\u5bf9\u4ed8\u90a3\u9b54\u5934\uff0c\u90a3\u5374\u8fd8\u76f8\u5dee\u5c1a \u8fdc\u3002\u201d\u5f20\u4e39\u67ab\u6df1\u77e5\u53cc\u5251\u5408\u74a7\u7684\u5a01\u529b\uff0c\u5bf9\u8463\u5cb3\u4e4b\u8a00\uff0c\u6b8a\u4e0d\u76f8\u4fe1\u3002\u4f46\u4e0d\u613f\u5728\u5e08\u4f2f\u9762\u524d\u5938\u8000\u81ea\u5df1\u5e08 \u7236\u7684\u5251\u6cd5\uff0c\u4ea6\u4e0d\u51fa\u58f0\u3002\u8463\u5cb3\u5ffd\u9053\uff1a\u201c\u4e39\u67ab\uff0c\u4f60\u7684\u5c0f\u53cb\u5462\uff1f\u201d \u3000\u3000\u8463\u5cb3\u53e3\u4e2d\u6240\u8bf4\u7684\u201c\u5c0f\u53cb\u201d\uff0c\u5f53\u7136\u6307\u7684\u4e43\u662f\u4e91\u857e\u3002\u5f20\u4e39\u67ab\u5fc3\u5934\u4e00\u8df3\uff0c\u4ed6\u5c1a\u672a\u4e0e\u7236\u4eb2\u8c08\u8fc7\uff0c \u4e0d\u613f\u4fbf\u5373\u63d0\u51fa\uff0c\u5f53\u4e0b\u629b\u4e86\u4e00\u4e2a\u773c\u8272\uff0c\u8463\u5cb3\u4f3c\u89e3\u4e0d\u89e3\uff0c\u9053\uff1a\u201c\u4f60\u5c31\u4e0d\u6302\u5ff5\u5979\u4e86\u5417\uff1f\u201d\u5f20\u5b97\u5468 \u9053\uff1a\u201c\u67ab\u513f\uff0c\u4f60\u65e2\u4e0e\u597d\u53cb\u540c\u6765\uff0c\u5c31\u8be5\u8bf7\u4ed6\u6765\u89c1\u6211\u5440\u3002\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u4ed6\u6709\u4e8b\u5148\u8d70\u4e86\u3002\u201d\u8463\u5cb3 \u9053\uff1a\u201c\u5979\u4e0d\u662f\u8981\u5230\u5510\u53e4\u62c9\u5c71\u5357\u9762\u7684\u5ce1\u8c37\u53bb\u627e\u6bcd\u4eb2\u5417\uff1f\u201d\u5f20\u4e39\u67ab\u5fc3\u5934\u53c8\u662f\u4e00\u8df3\uff1a\u539f\u6765\u8463\u5cb3\u4ea6\u5df2 \u89c1\u7740\u4e91\u857e\u4e86\uff0c\u8981\u4e0d\u7136\u4ed6\u4e0d\u4f1a\u77e5\u9053\u6b64\u4e8b\u3002\u5f53\u4e0b\u6b22\u559c\u4e4b\u60c5\u60c5\u4e0d\u81ea\u7981\u5730\u6d41\u9732\u51fa\u6765\uff0c\u4ed6\u662f\u7edd\u9876\u806a\u660e\u7684 \u4eba\uff0c\u5f53\u7136\u731c\u5230\u4e91\u857e\u4e4b\u4f4f\u5230\u78a7\u7f57\u5c71\u4e43\u662f\u8463\u5cb3\u7684\u5b89\u6392\u4e86\u3002 \u3000\u3000\u5f20\u5b97\u5468\u9762\u4e0a\u73b0\u51fa\u7591\u60d1\u7684\u795e\u60c5\uff0c\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\u670b\u53cb\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u4e00\u4f4d\u809d\u80c6\u7167\u4eba\u7684\u670b \u53cb\u3002\u201d\u5f20\u5b97\u5468\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u4ed6\u65e5\u4f60\u4e00\u5b9a\u8981\u8bf7\u4ed6\u5230\u54b1\u4eec\u5bb6\u91cc\u6765\u3002\u201d\u5f20\u4e39\u67ab\u5e94\u4e86\u4e00\u58f0\uff0c\u60f3\u8d77 \u4e91\u857e\u53d1\u8a93\u4e0d\u613f\u89c1\u4ed6\u7236\u4eb2\uff0c\u5fc3\u4e2d\u65e0\u9650\u51c4\u9178\u3002 \u3000\u3000\u8463\u5cb3\u53c8\u9053\uff1a\u201c\u4e0a\u5b98\u9b54\u5934\u5c31\u5728\u5510\u53e4\u62c9\u5c71\u5317\u9762\u7684\u9ad8\u5cf0\uff0c\u4ece\u5357\u9762\u5ce1\u8c37\u6115\u7f57\u65cf\u4eba\u805a\u5c45\u4e4b\u5730\u5317\u884c\uff0c \u722c\u4e0a\u5317\u9762\u7684\u9ad8\u5cf0\uff0c\u5927\u7ea6\u6709\u4e09\u65e5\u7684\u8def\u7a0b\u3002\u9002\u624d\u5f20\u5927\u4eba\u95ee\u8d77\u5929\u534e\uff0c\u4ed6\u5df2\u7ecf\u5148\u53bb\u4e86\u3002\u201d\u5f20\u4e39\u67ab\u95ee \u9053\uff1a\u201c\u4e0a\u5b98\u5929\u91ce\u53eb\u4f60\u4eec\u4f55\u65f6\u62dc\u5c71\uff1f\u201d\u8463\u5cb3\u9053\uff1a\u201c\u65e5\u671f\u5c1a\u672a\u786e\u5b9a\uff0c\u603b\u5728\u6e05\u660e\u4e4b\u524d\u3002\u5929\u534e\u5148\u8d70\uff0c \u662f\u6211\u53eb\u4ed6\u53bb\u5148\u4f1a\u4e00\u4f4d\u6b66\u6797\u670b\u53cb\uff0c\u5fc5\u8981\u4e4b\u65f6\uff0c\u51fa\u6765\u8c03\u89e3\u7684\u3002\u4f60\u7684\u4e8c\u5e08\u4f2f\u5462\uff1f\u542c\u8bf4\u4ed6\u4e5f\u6765\u4e86\uff0c\u53ea \u662f\u5929\u534e\u548c\u6211\u90fd\u8fd8\u6ca1\u89c1\u7740\u4ed6\u3002\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u4ed6\u548c\u9707\u4e09\u754c\u6bd5\u9053\u51e1\u5728\u4e00\u8d77\u5462\u3002\u201d\u5f53\u4e0b\u5c06\u6628\u591c\u53d1\u751f \u4e4b\u4e8b\uff0c\u7ea6\u7565\u8bf4\u4e86\u4e00\u904d\u3002\u8463\u5cb3\u7b11\u9053\uff1a\u201c\u6f6e\u97f3\u7684\u813e\u6c14\u8fd8\u662f\u4f9d\u7136\u5982\u6545\u3002\u597d\u5427\uff0c\u6211\u518d\u9017\u7559\u51e0\u5929\uff0c\u627e\u5230 \u4ed6\u540e\u548c\u4ed6\u8bf4\u8bdd\u3002\u201d\u5f20\u4e39\u67ab\u5ffd\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u660e\u5929\u6211\u4e5f\u5148\u8d70\u4e86\u3002\u201d \u3000\u3000\u5f20\u5b97\u5468\u6115\u7136\u9053\uff1a\u201c\u67ab\u513f\uff0c\u4f60\u521a\u56de\u6765\uff0c\u600e\u4e48\u53c8\u8d70\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u5e08\u5c0a\u6709\u4e8b\uff0c\u5f1f\u5b50\u670d\u5176 \u52b3\uff0c\u6211\u7684\u5e08\u7236\u65e2\u7136\u524d\u5f80\u5c65\u9669\uff0c\u6211\u600e\u80fd\u4e0d\u8ffd\u968f\u5462\uff1f\u201d\u5f20\u5b97\u5468\u60f3\u81ea\u5df1\u7684\u513f\u5b50\u4e43\u662f\u8c22\u5929\u534e\u4e00\u624b\u57f9\u517b \u6210\u6750\u7684\uff0c\u5f20\u4e39\u67ab\u6240\u8bf4\u7684\u81ea\u662f\u6b63\u7406\uff0c\u5f53\u4e0b\u867d\u89c9\u9eef\u7136\uff0c\u5374\u4e5f\u4e0d\u52a0\u963b\u6320\u3002\u53ea\u662f\u95ee\u9053\uff1a\u201c\u4f60\u90a3\u5339\u7167\u591c \u72ee\u5b50\u9a6c\u5462\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u6211\u90a3\u4f4d\u670b\u53cb\u5e26\u5b83\u5148\u8d70\u4e86\u3002\u201d\u5f20\u5b97\u5468\u201c\u54e6\u201d\u4e86\u4e00\u58f0\uff0c\u5fc3\u9053\uff1a\u201c\u4ed6\u548c \u8fd9\u4f4d\u670b\u53cb\u4ea4\u60c5\u786e\u662f\u4e0d\u6bd4\u5bfb\u5e38\u3002\u201d\u5fc3\u4e2d\u8d8a\u53d1\u60f3\u77e5\u9053\u90a3\u662f\u4f55\u4eba\u3002 \u3000\u3000\u7b2c\u4e8c\u65e5\u4e00\u65e9\u8463\u5cb3\u548c\u5f20\u4e39\u67ab\u5411\u5f20\u5b97\u5468\u8f9e\u884c\uff0c\u5f20\u5b97\u5468\u9053\uff1a\u201c\u6211\u9001\u4f60\u4eec\u51fa\u53bb\u3002\u201d\u643a\u7740\u513f\u5b50\u7684 \u624b\uff0c\u7f13\u7f13\u800c\u884c\uff0c\u8463\u5cb3\u5219\u5728\u6fb9\u53f0\u706d\u660e\u966a\u4f34\u4e4b\u4e0b\uff0c\u5148\u5230\u95e8\u524d\u76f8\u5019\u3002\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u7239\uff0c\u4f60\u56de\u53bb\u5427\uff0c \u4f60\u8fd8\u8981\u4e0a\u671d\u5462\u3002\u201d\u5f20\u5b97\u5468\u9053\uff1a\u201c\u8f9e\u5448\u6628\u591c\u6211\u5df2\u4fee\u597d\u4e86\uff0c\u4e0d\u5fc5\u7740\u5fd9\u3002\u4ece\u6b64\u6211\u65e0\u5b98\u4e00\u8eab\u8f7b\uff0c\u53ea\u6709 \u76fc\u671b\u4f60\u56de\u6765\u4e86\u3002\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u7239\u7239\u4e0d\u5fc5\u6302\u5fc3\uff0c\u6211\u548c\u5e08\u7236\u90fd\u4f1a\u56de\u6765\u7684\u3002\u201d\u5f20\u5b97\u5468\u9053\uff1a\u201c\u53ea\u6050 \u4f60\u56de\u6765\u8fd9\u540e\uff0c\u53c8\u8981\u8d70\u4e86\u3002\u4f60\u56de\u6765\u65f6\uff0c\u660e\u671d\u7684\u4f7f\u81e3\u60f3\u4ea6\u5e94\u5f53\u6765\u4e86\u3002\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u4e0d \u4e0e\u6211\u4eec\u4e00\u540c\u56de\u53bb\uff1f\u201d\u5f20\u5b97\u5468\u9053\uff1a\u201c\u6628\u591c\u65e9\u5df2\u8bf4\u8fc7\uff0c\u4e0d\u5fc5\u591a\u8bf4\u4e86\u3002\u201d\u5f20\u4e39\u67ab\u5ffd\u9053\uff1a\u201c\u5927\u4eba\u53ef\u8fd8 \u8bb0\u5f97\u4ee5\u524d\u90a3\u4f4d\u660e\u671d\u7684\u4f7f\u81e3\u4e91\u9756\u5417\uff1f\u201d \u3000\u3000\u5f20\u5b97\u5468\u6014\u4e86\u4e00\u6014\uff0c\u5f20\u4e39\u67ab\u53ea\u89c9\u4ed6\u7684\u638c\u5fc3\u6dcc\u6c57\uff0c\u5fae\u5fae\u53d1\u6296\u3002\u8fc7\u4e86\u534a\u664c\uff0c\u5f20\u5b97\u5468\u53f9\u4e86\u53e3\u6c14\uff0c \u8bf4\u9053\uff1a\u201c\u5440\uff0c\u4e09\u5341\u5e74\u4e86\uff0c\u4e09\u5341\u5e74\u524d\u4e4b\u4e8b\u8fd8\u5386\u5386\u5982\u5728\u76ee\u524d\uff0c\u4e91\u4f7f\u81e3\u662f\u6211\u751f\u5e73\u6240\u89c1\u7684\u7b2c\u4e00\u6761\u786c \u6c49\uff0c\u6211\u600e\u4f1a\u4e0d\u8bb0\u5f97\uff1f\u7b97\u8d77\u6765\u4ed6\u56de\u56fd\u4e5f\u6709\u5341\u5e74\u4e86\u3002\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u4ed6\u521a\u8e0f\u8fdb\u56fd\u95e8\uff0c\u4fbf\u88ab\u738b\u632f\u5047 \u4f20\u5723\u65e8\uff0c\u5c06\u4ed6\u5bb3\u6b7b\u4e86\u3002\u201d\u5f20\u5b97\u5468\u9053\uff1a\u201c\u8fd9\u4e8b\u60c5\u6211\u4ea6\u542c\u8bf4\u3002\u5440\uff0c\u90fd\u662f\u6211\u7684\u7f6a\u8fc7\u3002\u60f3\u90a3\u65f6\u6211\u5c11\u5e74 \u6c14\u76db\uff0c\u6068\u6781\u660e\u671d\u7684\u5929\u5b50\uff0c\u8fde\u540c\u6548\u5fe0\u660e\u671d\u7684\u4eba\uff0c\u6211\u90fd\u618e\u6068\uff0c\u4ee5\u81f3\u4ee4\u4e91\u9756\u5728\u51b0\u5929\u96ea\u5730\u7684\u6e56\u8fb9\uff0c\u7267 \u9a6c\u4e86\u4e8c\u5341\u5e74\u3002\u4ed6\u4e8c\u5341\u5e74\u6765\u996e\u51b0\u56bc\u96ea\uff0c\u5bf9\u6731\u5bb6\u5929\u5b50\u59cb\u7ec8\u662f\u4e39\u5fc3\u4e00\u7247\uff0c\u4ed6\u867d\u7136\u662f\u4e0e\u6211\u4f5c\u5bf9\uff0c\u6211\u5012 \u5f88\u4f69\u670d\u4ed6\u7684\u3002\u8fd1\u5e74\u6765\u6211\u4e00\u60f3\u5230\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u5c31\u89c9\u5f97\u96be\u8fc7\uff0c\u8fd9\u662f\u6211\u751f\u5e73\u6240\u4f5c\u7684\u552f\u4e00\u7f6a\u5b7d\u3002\u6211\u5012\u5e0c \u671b\u5c06\u6765\u660e\u671d\u6d3e\u6765\u7684\u4f7f\u81e3\uff0c\u4e5f\u50cf\u4e91\u9756\u4e00\u6837\uff0c\u662f\u4e2a\u94c1\u94ee\u94ee\u7684\u786c\u6c49\u3002\u201d\u5f20\u4e39\u67ab\u5ffd\u9053\uff1a\u201c\u542c\u8bf4\u4e91\u9756\u8fd8 \u7559\u4e0b\u4e24\u4e2a\u5b59\u513f\uff0c\u4e00\u7537\u4e00\u5973\uff0c\u5e74\u5c81\u548c\u6211\u5dee\u4e0d\u591a\u3002\u201d\u5f20\u5b97\u5468\u9053\uff1a\u201c\u662f\u5417\uff0c\u4f46\u613f\u80fd\u89c1\u7740\u4ed6\u4eec\u3002\u201d\u5f20 \u4e39\u67ab\u9053\uff1a\u201c\u82e5\u7136\u4ed6\u4eec\u6709\u6c42\u52a9\u4e8e\u4f60\u7684\u5730\u65b9\uff0c\u4f60\u613f\u610f\u5417\uff1f\u201d\u5f20\u5b97\u5468\u9053\uff1a\u201c\u4f60\u662f\u6211\u6240\u5b9d\u8d1d\u7684\u513f\u5b50\uff0c \u82e5\u7136\u8981\u4e3a\u4e86\u4ed6\u4eec\uff0c\u820d\u5f03\u4e86\u4f60\uff0c\u6211\u4e5f\u60c5\u613f\u3002\u201d\u5ffd\u53c8\u53f9\u9053\uff1a\u201c\u4ed6\u4eec\u82e5\u7136\u8fd8\u5728\u4eba\u4e16\u957f\u5927\u6210\u4eba\uff0c\u5b9a\u77e5 \u4ed6\u7237\u7237\u5f53\u5e74\u4e4b\u4e8b\uff0c\u4ed6\u4eec\u4e00\u5b9a\u5c06\u6211\u5f53\u4f5c\u4ec7\u4eba\uff0c\u53c8\u600e\u4f1a\u5411\u6211\u6c42\u52a9\uff1f\u201d\u5f20\u4e39\u67ab\u542c\u4ed6\u7236\u4eb2\u6240\u8bf4\u7684\u8bdd\uff0c \u51fa\u4e8e\u80ba\u8151\uff0c\u5fc3\u4e2d\u5927\u6170\uff0c\u53ea\u542c\u5f97\u4ed6\u7236\u4eb2\u53c8\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\u8fd9\u4e24\u4e2a\u5b69\u5b50\u4e0b\u843d\uff1f\u201d\u5f20\u4e39\u67ab\u672c\u60f3\u5c06 \u4ed6\u4e0e\u4e91\u857e\u4e4b\u4e8b\u8bf4\u77e5\uff0c\u4f46\u4e00\u8f6c\u5ff5\u95f4\uff0c\u5374\u5fcd\u7740\u4e0d\u8bf4\uff0c\u53ea\u9053\uff1a\u201c\u542c\u8bf4\u4ed6\u4eec\u4e5f\u8ddf\u4e86\u660e\u5e08\uff0c\u5b66\u6210\u4e86\u4e00\u8eab \u6b66\u827a\uff0c\u4e91\u9756\u7684\u5b59\u513f\u597d\u50cf\u8fd8\u5728\u660e\u671d\u4e3a\u5b98\u5462\uff0c\u6211\u662f\u542c\u5f97\u6c5f\u6e56\u4e0a\u7684\u670b\u53cb\u8bf4\u7684\u3002\u201d\u5f20\u5b97\u5468\u559c\u9053\uff1a\u201c\u8fd9 \u6837\u6211\u5c31\u5b89\u5fc3\u4e86\u3002\u4f46\u613f\u5c06\u6765\u660e\u671d\u6d3e\u6765\u7684\u4f7f\u8005\uff0c\u5c31\u662f\u4e91\u9756\u7684\u5b59\u513f\u3002\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u5df2\u5230\u4e86\u95e8\u8fb9\u3002\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u7239\u7239\u4fdd\u91cd\u3002\u201d\u548c\u8463\u5cb3\u8d70\u51fa\u540e\u95e8\uff0c\u53ea\u89c1\u5f20\u5b97\u5468\u6cea\u5149 \u83b9\u7136\uff0c\u8fd8\u501a\u5728\u95e8\u8fb9\u51dd\u671b\u3002 \u3000\u3000\u8463\u5cb3\u9053\uff1a\u201c\u5929\u534e\u5e08\u5f1f\u771f\u6709\u8010\u5fc3\u8fdc\u89c1\uff0c\u73b0\u5728\u6211\u624d\u77e5\u9053\u4ed6\u80af\u7559\u5728\u4f60\u4eec\u5bb6\u4e2d\u5341\u5e74\u7684\u7406\u7531\u3002\u4f60\u7684 \u7236\u4eb2\u613f\u6697\u52a9\u4e2d\u56fd\uff0c\u770b\u6765\u4e5f\u5148\u4ea6\u5174\u4e0d\u8d77\u4ec0\u4e48\u6ce2\u6d6a\u4e86\u3002\u201d \u3000\u3000\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u5e08\u4f2f\uff0c\u54b1\u4eec\u73b0\u5728\u4e0a\u54ea\u513f\uff1f\u201d\u8463\u5cb3\u9053\uff1a\u201c\u5f53\u7136\u662f\u4e0a\u78a7\u7f57\u5c71\u5440\uff0c\u4f60\u7684\u5c0f\u5144\u5f1f\u6b63 \u5728\u6302\u5ff5\u4f60\u5462\u3002\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u539f\u6765\u662f\u4f60\u8001\u53eb\u5979\u4e0a\u5c71\u53bb\u4f4f\u7684\u3002\u201d\u8463\u5cb3\u9053\uff1a\u201c\u78a7\u7f57\u5c71\u4e0a\u6709\u6211\u7684\u4e00 \u4f4d\u670b\u53cb\uff0c\u4e91\u857e\u5728\u5ba2\u5e97\u5c45\u4f4f\uff0c\u7ec8\u662f\u4e0d\u59a5\uff0c\u56e0\u6b64\u6211\u53eb\u5979\u5230\u8fd9\u4f4d\u670b\u53cb\u5bb6\u4e2d\u6682\u4f4f\u3002\u201d \u3000\u3000\u4e24\u4eba\u811a\u7a0b\u751a\u5feb\uff0c\u4e0d\u5230\u4e00\u523b\u5c31\u6765\u5230\u4e86\u78a7\u7f57\u5c71\u3002\u5bd2\u51ac\u8083\u6740\uff0c\u6ee1\u5c71\u9ec4\u53f6\uff0c\u4f46\u5f20\u4e39\u67ab\u5fc3\u4e2d\u5374\u5145\u6ee1 \u751f\u6c14\uff0c\u5bf9\u7740\u6b8b\u51ac\u814a\u6708\uff0c\u5374\u5982\u770b\u89c1\u4e86\u660e\u5a9a\u7684\u6625\u5149\u3002\u8d70\u4e0a\u534a\u5c71\uff0c\u53ea\u89c1\u5c71\u5761\u4e0a\u4e00\u5bb6\u4eba\u5bb6\uff0c\u571f\u5899\u6728 \u95e8\uff0c\u5012\u4e5f\u9f50\u6574\uff0c\u95e8\u524d\u501a\u7740\u4e00\u4e2a\u5c11\u5973\uff0c\u6b63\u662f\u4e91\u857e\u3002\u5f20\u4e39\u67ab\u53eb\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u5c0f\u5144\u5f1f\uff0c\u6211\u56de\u6765 \u4e86\uff01\u201d\u4e91\u857e\u6de1\u6de1\u5e94\u4e86\u4e00\u58f0\uff0c\u795e\u60c5\u751a\u662f\u51b7\u6f20\u3002\u8463\u5cb3\u77a7\u4e86\u4ed6\u4eec\u4e00\u773c\uff0c\u6447\u6447\u5934\u9053\uff1a\u201c\u4f60\u4eec\u771f\u662f\u4e00\u5bf9 \u51a4\u5bb6\u3002\u201d \u3000\u3000\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u6211\u548c\u7236\u4eb2\u8c08\u8d77\u5f53\u5e74\u4e4b\u4e8b\uff0c\u4ed6\u751a\u662f\u540e\u6094\u3002\u201d\u6b63\u60f3\u544a\u8bc9\u4e91\u857e\u4ed6\u7684\u7236\u4eb2\u662f\u600e\u6837\u76fc \u671b\u80fd\u89c1\u5230\u4ed6\u4eec\uff0c\u4e91\u857e\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6211\u4e5f\u5728\u540e\u6094\u5462\u3002\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u540e\u6094\u4ec0\u4e48\uff1f\u201d\u4e91\u857e\u9053\uff1a \u201c\u6211\u7684\u7237\u7237\u7267\u9a6c\uff0c\u6211\u7684\u6bcd\u4eb2\u73b0\u5728\u7ed9\u4eba\u5bb6\u653e\u7f8a\uff0c\u5c06\u6765\u82e5\u548c\u4f60\u4e00\u9053\u89c1\u7740\u6bcd\u4eb2\uff0c\u6211\u4e5f\u4e0d\u77e5\u8be5\u600e\u8bf4 \u597d\u3002\u201d\u5f20\u4e39\u67ab\u53f9\u4e86\u53e3\u6c14\u3002\u539f\u6765\u4e91\u857e\u662f\u89c9\u5f97\u548c\u4ed6\u76f8\u597d\uff0c\u5bf9\u4e0d\u8d77\u6bcd\u4eb2\uff0c\u6545\u6b64\u540e\u6094\u3002\u8463\u5cb3\u7b11\u9053\uff1a \u201c\u4f60\u4eec\u8fd9\u4e24\u4e2a\u5c0f\u5bb6\u4f19\u4e00\u89c1\u9762\u5c31\u5509\u58f0\u53f9\u6c14\uff0c\u771f\u4ee4\u6211\u8fd9\u8001\u5934\u5b50\u83ab\u540d\u5176\u5999\uff0c\u6709\u8bdd\u8fdb\u91cc\u9762\u53bb\u8bf4\u3002\u201d\u5f20 \u4e39\u67ab\u53f9\u6c14\u9053\uff1a\u201c\u6211\u5c31\u662f\u8d74\u6c64\u8e48\u706b\uff0c\u4e5f\u8981\u540c\u4f60\u5bfb\u7740\u6bcd\u4eb2\u3002\u5c06\u6765\u4e0d\u8bba\u4f2f\u6bcd\u600e\u6837\u8d23\u602a\u6211\uff0c\u6211\u4e5f\u7518 \u53d7\u3002\u201d\u4e91\u857e\u5ffd\u5730\u5657\u55e4\u4e00\u7b11\u9053\uff1a\u201c\u8d23\u602a\u4f60\u505a\u4ec0\u4e48\uff1f\u6211\u7684\u6bcd\u4eb2\u751f\u5e73\u4ece\u4e0d\u8d23\u602a\u4eba\u7684\u3002\u522b\u4f5c\u5f97\u90a3\u6837\u53ef \u601c\u76f8\u5566\u3002\u201d\u4e00\u7b11\u4e4b\u4e0b\uff0c\u6625\u610f\u76ce\u7136\uff0c\u597d\u50cf\u6ee1\u5929\u7684\u9634\u973e\u90fd\u88ab\u9633\u5149\u9a71\u9010\u4e86\u3002 \u3000\u3000\u8463\u5cb3\u7684\u670b\u53cb\u662f\u4e00\u4f4d\u5ba2\u5c45\u8499\u53e4\u7684\u56de\u65cf\u6b66\u5e08\uff0c\u751a\u662f\u8c6a\u723d\uff0c\u63a5\u4ed6\u4eec\u8fdb\u95e8\u4e4b\u540e\uff0c\u4fbf\u81ea\u53bb\u6d17\u5265\u6628\u65e5 \u730e\u6765\u7684\u4e00\u5934\u9ec4\u7f8a\uff0c\u7ed9\u4ed6\u4eec\u4e0b\u9152\u3002\u4e09\u4eba\u5750\u5b9a\uff0c\u4e91\u857e\u9053\uff1a\u201c\u4e09\u5e08\u4f2f\u548c\u5e08\u7236\u6628\u5929\u5df2\u7ecf\u8d70\u4e86\u3002\u201d\u8463\u5cb3 \u8bf4\u9053\uff1a\u201c\u6211\u5df2\u4e0e\u4e39\u67ab\u8bf4\u8fc7\uff0c\u6211\u8fd8\u8981\u5728\u8fd9\u91cc\u9017\u7559\u51e0\u5929\uff0c\u5f85\u5bfb\u89c1\u4f60\u7684\u4e8c\u5e08\u4f2f\u548c\u6bd5\u9053\u51e1\u4e4b\u540e\uff0c\u518d\u8d76 \u5230\u5510\u53e4\u62c9\u5c71\u7684\u5357\u9ad8\u5cf0\u8d74\u4f1a\u3002\u4f60\u4eec\u5bfb\u5230\u4e86\u4e91\u857e\u7684\u6bcd\u4eb2\u540e\uff0c\u4e5f\u8981\u5373\u65f6\u8d76\u5f80\uff0c\u4e5f\u8bb8\u54b1\u4eec\u8001\u5e7c\u4e24\u4ee3\uff0c \u90fd\u8981\u5408\u6597\u90a3\u8001\u9b54\u5934\u5462\uff01\u201d\u4e91\u857e\u9053\uff1a\u201c\u90a3\u8001\u9b54\u5934\u5c31\u8fd9\u6837\u5389\u5bb3\u5417\uff1f\u201d\u8463\u5cb3\u9053\uff1a\u201c\u54b1\u4eec\u5408\u6597\u4ed6\uff0c\u6211 \u770b\u4e5f\u8fd8\u6ca1\u6709\u628a\u63e1\u5fc5\u80dc\u5462\u3002\u201d\u4e91\u857e\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u5c82\u4e0d\u662f\u6bd4\u7d2b\u7af9\u6797\u4e2d\u90a3\u4f4d\u8001\u5a46\u5a46\u8fd8\u8981\u5389 \u5bb3\uff1f\u201d\u8463\u5cb3\u4e00\u6014\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\u8001\u5a46\u5a46\uff1f\u201d\u4e91\u857e\u60f3\u8d77\u8c22\u5929\u534e\u7684\u8bdd\uff0c\u8bf4\u662f\u6b64\u4e8b\u9664\u4e86\u5e08\u7956\u4e4b\u5916\uff0c\u53ea \u6709\u5927\u5e08\u4f2f\u77e5\u9053\uff0c\u7acb\u5373\u95ee\u9053\uff1a\u201c\u662f\u4e00\u4f4d\u4e0d\u80af\u900f\u9732\u59d3\u540d\uff0c\u80fd\u591f\u7528\u7af9\u53f6\u4f5c\u6697\u5668\u6253\u4eba\u7684\u8001\u5a46\u5a46\u3002\u5927\u5e08 \u4f2f\uff0c\u4f60\u77e5\u9053\u5979\u7684\u6765\u5386\u5417\uff1f\u201d\u5f53\u4e0b\u5c06\u90a3\u65e5\u5728\u7d2b\u7af9\u6797\u4e2d\u6240\u9047\u5230\u7684\u4e8b\u60c5\u4e00\u4e00\u8bf4\u4e0e\u8463\u5cb3\u77e5\u9053\u3002\u8463\u5cb3 \u9053\uff1a\u201c\u60f3\u4e0d\u5230\u8fd9\u4f4d\u8001\u524d\u8f88\u8fd8\u5728\u4eba\u95f4\uff0c\u5c1a\u672a\u5fd8\u60c5\u5f53\u5e74\u4e4b\u4e8b\u3002\u5979\u65e2\u7136\u73b0\u8eab\uff0c\u5c06\u6765\u6216\u8bb8\u4e5f\u4f1a\u63d2\u624b\uff0c \u4e8b\u60c5\u53ea\u6050\u6015\u66f4\u9ebb\u70e6\u4e86\u3002\u201d\u4e91\u857e\u9053\uff1a\u201c\u5979\u5230\u5e95\u662f\u4ec0\u4e48\u4eba\uff1f\u201d\u8463\u5cb3\u9053\uff1a\u201c\u5979\u548c\u54b1\u4eec\u7684\u7956\u5e08\u4e0e\u90a3\u4e2a \u8001\u9b54\u5934\u5927\u7ea6\u90fd\u6709\u8fc7\u4e00\u6bb5\u6e0a\u6e90\u3002\u53ea\u662f\u54b1\u4eec\u505a\u5c0f\u8f88\u7684\u4e0d\u4fbf\u8c08\u8bba\uff0c\u5c06\u6765\u4f60\u81ea\u7136\u4f1a\u77e5\u9053\u7684\u3002\u201d\u4e91\u857e\u4e0d \u6562\u518d\u95ee\uff0c\u5fc3\u4e2d\u66f4\u662f\u7eb3\u95f7\u3002 \u3000\u3000\u5403\u8fc7\u4e86\u5348\u996d\uff0c\u65b9\u4ea4\u4e2d\u5348\uff0c\u4e91\u857e\u601d\u6bcd\u60c5\u5207\uff0c\u50ac\u5f20\u4e39\u67ab\u6536\u62fe\uff0c\u8f9e\u522b\u4e86\u4e3b\u4eba\u548c\u5927\u5e08\u4f2f\uff0c\u5148\u884c\u52a8 \u8eab\u3002\u90a3\u5339\u7167\u591c\u72ee\u5b50\u9a6c\u88ab\u4e91\u857e\u5e26\u5230\u6b64\u5730\uff0c\u591a\u65e5\u4e0d\u89c1\u4e3b\u4eba\uff0c\u89c1\u5f20\u4e39\u67ab\u8d70\u8fd1\uff0c\u4fbf\u6602\u9996\u957f\u5636\u8868\u793a\u4eb2 \u70ed\u3002\u5f20\u4e39\u67ab\u624b\u629a\u9a6c\u9888\uff0c\u7b11\u9053\uff1a\u201c\u53c8\u7528\u5f97\u7740\u4f60\u4e86\u3002\u201d\u4e0e\u4e91\u857e\u5404\u81ea\u8de8\u4e0a\u5b9d\u9a6c\uff0c\u7edd\u5c18\u800c\u53bb\u3002 \u3000\u3000\u65f6\u5e8f\u5df2\u662f\u6df1\u51ac\uff0c\u6108\u5411\u5317\u884c\uff0c\u6714\u98ce\u6108\u70c8\uff0c\u9053\u8def\u90fd\u5df2\u88ab\u96ea\u63a9\u76d6\u767d\u832b\u832b\u4e00\u7247\uff0c\u4e0e\u539f\u91ce\u76f8\u8fde\uff0c\u5206 \u8fa8\u4e0d\u51fa\u3002\u8def\u4e0a\u7edd\u5c11\u884c\u4eba\uff0c\u5f20\u4e39\u67ab\u5728\u9a6c\u524d\u626c\u97ad\uff0c\u9ad8\u58f0\u653e\u6b4c\u9053\uff1a\u201c\u4f46\u5f97\u4e24\u5fc3\u5982\u767d\u96ea\uff0c\u4e0d\u6559\u534a\u70b9\u67d3 \u5c18\u57c3\u3002\u201d\u4e91\u857e\u9053\uff1a\u201c\u9178\u79c0\u624d\uff0c\u4f60\u518d\u98ce\u5440\u4e91\u5440\u7684\u4e00\u541f\uff0c\u98ce\u96ea\u4e00\u6765\uff0c\u90a3\u5c31\u66f4\u51b7\u5f97\u96be\u884c\u4e86\u3002\u201d\u5f20\u4e39 \u67ab\u7b11\u9053\uff1a\u201c\u518d\u5927\u7684\u98ce\u96ea\u4e5f\u51b7\u4e0d\u4e86\u6211\u7684\u5fc3\u3002\u201d\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u98ce\u96ea\u679c\u7136\u6765\u4e86\u3002 \u3000\u3000\u96ea\u7247\u7eb7\u98de\uff0c\u6714\u98ce\u6012\u53f7\uff0c\u4fe8\u5982\u6709\u4e07\u9a6c\u5954\u817e\u4e4b\u52bf\uff0c\u5f20\u4e39\u67ab\u4e0e\u4e91\u857e\u9006\u98ce\u5954\u9a70\uff0c\u8863\u895f\u4e0a\u3001\u9a6c\u978d\u4e0a \u5c3d\u662f\u96ea\u82b1\uff0c\u5f20\u4e39\u67ab\u7d22\u6027\u89e3\u5f00\u8863\u7ebd\u62ab\u895f\u8fce\u98ce\uff0c\u626c\u97ad\u987e\u76fc\uff0c\u5927\u547c\u75db\u5feb\u3002\u4e91\u857e\u5ffd\u9053\uff1a\u201c\u54a6\uff0c\u4f60\u542c\uff0c \u8fd9\u662f\u98ce\u58f0\u8fd8\u662f\u5578\u58f0\uff1f\u201d\u5f20\u4e39\u67ab\u4fa7\u8033\u7ec6\u8fa8\u97f3\u54cd\uff0c\u5947\u9053\uff1a\u201c\u98ce\u58f0\u4e2d\u5939\u6742\u7740\u6e05\u5578\u4e4b\u58f0\uff0c\u8fd8\u6709\u9a6c\u8e44\u8ffd \u9010\u7684\u58f0\u97f3\u5462\u3002\u800c\u4e14\u53d1\u5578\u4e4b\u4eba\uff0c\u5b9a\u662f\u6b66\u529f\u9ad8\u660e\u4e4b\u58eb\uff0c\u54b1\u4eec\u4e0a\u524d\u770b\u770b\u3002\u201d \u3000\u3000\u5f20\u3001\u4e91\u4e8c\u4eba\u653e\u9a6c\u98de\u8dd1\uff0c\u8dd1\u4e86\u7247\u523b\uff0c\u53ea\u89c1\u524d\u9762\u767d\u7691\u7691\u7684\u96ea\u5730\u4e0a\uff0c\u6709\u4e00\u56e2\u9ed1\u5f71\u6eda\u6765\u6eda\u53bb\uff0c\u6b63 \u662f\u4e24\u6761\u5927\u6c49\u5728\u96ea\u5730\u4e0a\u25a1\u6253\u3002\u65c1\u8fb9\u8fd8\u6709\u4e09\u9a91\u5065\u9a6c\uff0c\u9a6c\u4e0a\u9a91\u5ba2\u662f\u4e24\u4e2a\u5973\u4eba\u548c\u4e00\u4e2a\u8eab\u8eaf\u9b41\u68a7\u7684\u5927 \u6c49\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u4f3c\u4e4e\u662f\u6211\u4eec\u8ba4\u8bc6\u7684\u670b\u53cb\u3002\u201d\u518d\u653e\u9a6c\u8d70\u4e86\u534a\u91cc\u4e4b\u5730\uff0c\u52d2\u7740\u9a6c\u5934\uff0c\u5411\u524d\u4e00\u770b\uff0c \u539f\u6765\u524d\u9762\u90a3\u51e0\u4e2a\u4eba\u6b63\u662f\u9ed1\u767d\u6469\u8bc3\u548c\u4ed6\u4eec\u7684\u6ce2\u65af\u59bb\u5b50\uff0c\u5728\u96ea\u5730\u4e0a\u548c\u4eba\u25a1\u6253\u7684\u662f\u9ed1\u6469\u8bc3\u3002\u5f20\u4e39\u67ab \u53eb\u4e86\u4e00\u58f0\uff0c\u518d\u770b\u6e05\u695a\u65f6\u66f4\u5947\u602a\u4e86\uff0c\u548c\u9ed1\u6469\u8bc3\u25a1\u6253\u7684\u4eba\u7adf\u662f\u4ee5\u524d\u660e\u671d\u7684\u5927\u5185\u603b\u7ba1\u5eb7\u8d85\u6d77\uff01 \u3000\u3000\u53ea\u89c1\u90a3\u5eb7\u8d85\u6d77\u4e00\u8eab\u8499\u53e4\u7267\u6c11\u7684\u670d\u9970\uff0c\u8863\u88f3\u5df2\u88ab\u9ed1\u6469\u8bc3\u6293\u88c2\u51e0\u5904\uff0c\u66f4\u663e\u5f97\u5f62\u5bb9\u6194\u60b4\uff0c\u6ee1\u9762 \u98ce\u5c18\u4e4b\u8272\u3002\u5eb7\u8d85\u6d77\u7684\u6c14\u529b\u8fdc\u4e0d\u53ca\u9ed1\u6469\u8bc3\uff0c\u5c31\u5728\u5f20\u4e39\u67ab\u52d2\u9a6c\u800c\u89c2\u7684\u65f6\u5019\uff0c\u53ea\u89c1\u4ed6\u53c8\u88ab\u9ed1\u6469\u8bc3\u6454 \u4e86\u4e00\u4e2a\u7b4b\u6597\u3002\u5f20\u4e39\u67ab\u6b63\u81ea\u5947\u602a\u4ed6\u4eec\u4e3a\u4ec0\u4e48\u6253\u67b6\uff0c\u53ea\u89c1\u5eb7\u8d85\u6d77\u6454\u4e86\u4e00\u7b4b\u6597\uff0c\u7acb\u523b\u7ffb\u8eab\u8d77\u6765\u62d4\u51fa \u4e00\u67c4\u9a6c\u5200\uff0c\u72e0\u72e0\u5730\u5411\u9ed1\u6469\u8bc3\u5288\u53bb\uff0c\u53e3\u4e2d\u9a82\u9053\uff1a\u201c\u6076\u5f3a\u76d7\uff0c\u80c6\u602a\u5728\u592a\u5c81\u5934\u4e0a\u52a8\u571f\uff0c\u5077\u6211\u7684\u4e1c \u897f\uff0c\u8d76\u5feb\u8fd8\u6765\uff0c\u4e07\u4e8b\u7686\u4f11\uff0c\u5426\u5219\u5c31\u4e00\u5200\u5c06\u4f60\u5288\u4e86\uff01\u201d\u9ed1\u6469\u8bc3\u54c8\u54c8\u5927\u7b11\u62d4\u51fa\u7eff\u7389\u5b9d\u6756\uff0c\u53cd\u624b\u4e00 \u8fce\uff0c\u53ea\u542c\u5f97\u5f53\u25a1\u4e00\u58f0\uff0c\u706b\u82b1\u98de\u6e85\uff0c\u5eb7\u8d85\u6d77\u7684\u9a6c\u5200\u78b0\u4e86\u4e00\u4e2a\u7f3a\u53e3\u3002\u9ed1\u6469\u8bc3\u7b11\u9053\uff1a\u201c\u6211\u8fd8\u672a\u89c1\u8fc7 \u592a\u5c81\u54e9\uff0c\u4f60\u597d\u597d\u548c\u6211\u8bf4\uff0c\u8fd8\u6709\u5546\u91cf\uff0c\u4f60\u82e5\u60f3\u901e\u5f3a\uff0c\u54fc\uff0c\u54fc\uff01\u770b\u662f\u4f60\u4e00\u5200\u5288\u4e86\u6211\uff0c\u8fd8\u662f\u6211\u4e00\u6756 \u6253\u788e\u4f60\u7684\u72d7\u817f\uff01\u201d\u8bdd\u8bf4\u4e4b\u95f4\uff0c\u4e24\u4eba\u624b\u5e95\u90fd\u4e0d\u653e\u677e\uff0c\u77ac\u606f\u4e4b\u95f4\u5df2\u6362\u4e86\u4e09\u56db\u62db\u3002\u5f20\u4e39\u67ab\u5341\u5206\u5947 \u602a\uff0c\u9ed1\u767d\u6469\u8bc3\u6240\u505a\u7684\u73e0\u5b9d\u4e70\u5356\uff0c\u89c4\u6a21\u4e4b\u5927\uff0c\u4e16\u65e0\u5339\u654c\uff0c\u4f55\u81f3\u4e8e\u8981\u5077\u5eb7\u8d85\u6d77\u7684\u4e1c\u897f\uff1f\u4f46\u770b\u90a3\u9ed1 \u6469\u8bc3\u6756\u6cd5\u867d\u7136\u51cc\u5389\uff0c\u5374\u662f\u672a\u4e0b\u6740\u624b\uff0c\u53c8\u4f3c\u4e4e\u662f\u6709\u610f\u76f8\u8ba9\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u77e5\u9053\u5eb7\u8d85\u6d77\u4e0d\u662f\u9ed1\u6469\u8bc3\u7684\u5bf9\u624b\uff0c\u5fc3\u9053\uff1a\u201c\u6b64\u4eba\u867d\u884c\u4e3a\u5351\u9119\uff0c\u4f46\u603b\u7b97\u548c\u6211\u6709\u4e00\u9762\u4e4b \u96c5\uff0c\u4e0d\u77e5\u4ed6\u4f55\u6545\u4e0e\u9ed1\u767d\u6469\u8bc3\u53d1\u751f\u7ea0\u7eb7\uff0c\u4e0d\u5982\u6211\u4e0a\u524d\u66ff\u4ed6\u4eec\u8c03\u89e3\u5427\u3002\u201d\u7eb5\u9a6c\u4e0a\u524d\uff0c\u5c31\u5728\u8fd9\u4e00\u77ac \u95f4\u53ea\u542c\u5f97\u5eb7\u8d85\u6d77\u60ca\u53eb\u4e00\u58f0\uff0c\u8fde\u8fde\u540e\u9000\u3002 \u3000\u3000\u767d\u6469\u8bc3\u9a7b\u9a6c\u89c2\u6597\uff0c\u8fd9\u65f6\u4e5f\u770b\u6e05\u695a\u662f\u5f20\u4e39\u67ab\u6765\u4e86\uff0c\u6b22\u559c\u4e4b\u6781\u53eb\u9053\uff1a\u201c\u5927\u54e5\uff0c\u662f\u5f20\u516c\u5b50\u6765 \u4e86\uff01\u201d\u9ed1\u6469\u8bc3\u53eb\u9053\uff1a\u201c\u5f20\u516c\u5b50\u6765\u5f97\u6b63\u597d\uff0c\u4f60\u628a\u90a3\u51e0\u4ef6\u5b9d\u8d1d\u7ed9\u4ed6\u77a7\u77a7\uff0c\u770b\u4ed6\u8ba4\u5f97\u4e48\uff1f\u201d\u5f20\u4e39\u67ab \u9053\uff1a\u201c\u4ec0\u4e48\u5b9d\u8d1d\uff1f\u201d\u5eb7\u8d85\u6d77\u89c1\u662f\u5f20\u4e39\u67ab\uff0c\u5fc3\u4e2d\u66f4\u662f\u5403\u60ca\uff0c\u4f46\u53c8\u5e0c\u671b\u4ed6\u80fd\u5e2e\u52a9\u81ea\u5df1\uff0c\u6025\u5fd9\u53eb \u9053\uff1a\u201c\u8fd9\u4e24\u4e2a\u5f3a\u76d7\uff0c\u5077\u76d7\u4e86\u6211\u7684\u5b9d\u8d1d\uff0c\u4e39\u67ab\uff0c\u4f60\u7ed9\u6211\u4e3b\u6301\u516c\u9053\uff01\u201d \u3000\u3000\u5f20\u4e39\u67ab\u95ee\uff1a\u201c\u4f60\u6709\u4ec0\u4e48\u5b9d\u8d1d\uff1f\u201d\u8df3\u4e0b\u9a6c\u6765\u6b63\u60f3\u4e0a\u53bb\u529d\u89e3\uff0c\u53ea\u542c\u5f97\u9ed1\u6469\u8bc3\u5927\u7b11\u9053\uff1a\u201c\u662f \u554a\uff0c\u4f60\u6709\u4ec0\u4e48\u5b9d\u8d1d\uff1f\u4f60\u6628\u65e5\u4e0d\u77e2\u53e3\u5426\u8ba4\u8eab\u6709\u5b9d\u7269\uff0c\u600e\u4e48\u73b0\u5728\u53c8\u8bf4\u662f\u4f60\u7684\u4e86\uff1f\u201d\u5eb7\u8d85\u6d77\u6025\u9053\uff1a \u201c\u4e39\u67ab\uff0c\u90a3\u771f\u662f\u6211\u7684\u5b9d\u8d1d\u3002\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u4f60\u54ea\u91cc\u6765\u7684\u5b9d\u8d1d\uff1f\u201d\u767d\u6469\u8bc3\u62ff\u51fa\u4e00\u4e2a\u9ec4\u5e03\u5305\u88f9\uff0c \u9012\u7ed9\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u4f60\u77a7\u90fd\u5728\u91cc\u9762\uff0c\u6211\u770b\u90a3\u51e0\u4ef6\u5b9d\u7269\uff0c\u6765\u8def\u4e0d\u6b63\uff0c\u6562\u60c5\u4e5f\u662f\u8fd9\u25a1\u5077\u6765\u7684\uff0c\u4f60\u7ed9 \u6211\u4eec\u77a7\u77a7\uff0c\u7ed9\u6211\u4eec\u8ba4\u8ba4\u8fd9\u51e0\u4ef6\u5b9d\u7269\u7684\u6765\u5386\u3002\u201d \u3000\u3000\u5f20\u4e39\u67ab\u5fc3\u5ff5\u4e00\u52a8\uff0c\u8fd9\u9ec4\u5e03\u5305\u88b1\u4e43\u662f\u4ed6\u89c1\u8fc7\u7684\u3002\u660e\u519b\u5728\u571f\u6728\u5821\u88ab\u56f4\u4e4b\u65f6\uff0c\u5eb7\u8d85\u6d77\u9635\u4e0a\u79c1 \u9003\uff0c\u5230\u4e00\u5bb6\u519c\u5bb6\u6295\u5bbf\uff0c\u6070\u597d\u88ab\u5f20\u3001\u4e91\u4e8c\u4eba\u649e\u89c1\uff0c\u4ed6\u80cc\u4e0a\u80cc\u7684\u5c31\u662f\u8fd9\u4e2a\u9ec4\u5305\u88b1\uff0c\u91cc\u9762\u90fd\u662f\u91d1\u5143 \u5b9d\uff0c\u5f53\u65f6\u66fe\u88ab\u5f20\u4e39\u67ab\u63b7\u4e8e\u9636\u4e0b\uff0c\u4ed6\u62fe\u8d77\u6765\u5c31\u9003\u8dd1\u4e86\u3002\u5f20\u4e39\u67ab\u5fc3\u9053\uff1a\u201c\u8fd9\u51e0\u4e2a\u91d1\u5143\u5b9d\u600e\u4f1a\u653e\u5728 \u9ed1\u767d\u6469\u8bc3\u5fc3\u4e0a\uff1f\u201d\u89e3\u5f00\u5305\u91cc\uff0c\u5ffd\u89c1\u5b9d\u5149\u5916\u9732\uff0c\u539f\u6765\u9664\u4e86\u5341\u51e0\u952d\u91d1\u5143\u5b9d\u4e4b\u5916\uff0c\u8fd8\u6709\u597d\u51e0\u4ef6\u5f02\u5b9d \u5947\u73cd\uff01 \u3000\u3000\u4e00\u4ef6\u662f\u5c3a\u4f59\u957f\u7684\u78a7\u7389\u73ca\u745a\uff0c\u901a\u4f53\u6676\u83b9\uff0c\u6beb\u5143\u7455\u75b5\uff0c\u6bd4\u4e91\u857e\u9001\u7ed9\u77f3\u7fe0\u51e4\u505a\u8058\u793c\u7684\u90a3\u652f\u73ca\u745a \u8fd8\u8981\u540d\u8d35\u5f97\u591a\u3002\u4e00\u652f\u662f\u5d4c\u6709\u4e24\u9897\u201c\u732b\u513f\u773c\u201d\u5b9d\u77f3\u7684\u5934\u7c2a\uff0c\u7c2a\u4e0a\u6709\u201c\u5b5d\u6b23\u7687\u540e\u201d\u51e0\u4e2a\u7c40\u6587\u7bc6 \u5b57\u3002\u53e6\u4e00\u6837\u662f\u9547\u7eb8\u7528\u7684\u5b9d\u77f3\u72ee\u5b50\u3002\u8fd8\u6709\u4e00\u6837\u5c31\u66f4\u540d\u8d35\u4e86\uff0c\u7adf\u662f\u6b63\u7edf\u7687\u5e1d\u7684\u9f99\u7eb9\u6c49\u7389\u79c1\u7ae0\uff0c\u6709 \u201c\u6b63\u7edf\u7687\u5e1d\u4e4b\u5370\u201d\u51e0\u4e2a\u91d1\u6587\u523b\u5b57\uff0c\u90a3\u662f\u4ec5\u6b21\u4e8e\u56fd\u73ba\u7684\u5b9d\u7269\u3002\u53e6\u5916\u8fd8\u6709\u4e00\u4ef6\u5546\u4ee3\u7684\u53e4\u8463\uff0c\u4e00\u4e32 \u73cd\u73e0\u9879\u94fe\uff0c\u90fd\u5728\u4ef7\u503c\u8fde\u57ce\u7684\u5927\u5185\u5b9d\u7269\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u51b7\u51b7\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u54ea\u91cc\u6765\u7684\u8fd9\u4e9b\u5b9d\u7269\uff1f\u201d\u5eb7\u8d85\u6d77\u9053\uff1a\u201c\u90fd\u662f\u7687\u4e0a\u5386\u5e74\u8d4f\u8d50\u6211 \u7684\u3002\u201d\u5f20\u4e39\u67ab\u51b7\u7b11\u9053\uff1a\u201c\u7687\u4e0a\u8fde\u4ed6\u7684\u79c1\u7ae0\u548c\u7687\u540e\u7684\u5934\u7c2a\u90fd\u8d4f\u7ed9\u4f60\u5417\uff1f\u201d\u8fd9\u65f6\u5f20\u4e39\u67ab\u5df2\u662f\u5fc3\u4e2d \u4e86\u4e86\u6599\u60f3\u5b9a\u662f\u5eb7\u8d85\u6d77\u5728\u571f\u6728\u5821\u79c1\u9003\u4e4b\u65f6\uff0c\u628a\u7687\u5e1d\u968f\u8eab\u643a\u5e26\u7684\u73cd\u5b9d\u4e00\u53e4\u8111\u513f\u5077\u4e86\uff0c\u4ee5\u81f3\u8fde\u90a3 \u201c\u5929\u5b50\u4e4b\u5370\u201d\u4ee5\u53ca\u7687\u540e\u9001\u4e0e\u7687\u5e1d\u7559\u5ff5\u7684\u5934\u7c2a\u90fd\u4e00\u540c\u76d7\u53bb\u3002\u521a\u4ece\u571f\u6728\u5821\u9003\u51fa\u4e4b\u65f6\u4ed6\u8fd8\u4e0d\u6562\u5305\u5728 \u5305\u88b1\u5185\uff0c\u6240\u4ee5\u5f53\u65f6\u5f20\u4e39\u67ab\u6ca1\u6709\u53d1\u73b0\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u6240\u6599\u4e0d\u5dee\uff0c\u90a3\u4e9b\u73cd\u5b9d\u90fd\u662f\u5eb7\u8d85\u6d77\u5077\u81ea\u6b63\u7edf\u7687\u5e1d\u8eab\u4e0a\u7684\u3002\u90a3\u65f6\u4ed6\u4ee5\u4e3a\u4e2d\u56fd\u5fc5\u88ab\u74e6\u523a \u6240\u706d\uff0c\u5929\u4e0b\u5b9a\u5c06\u5927\u4e71\uff0c\u6240\u4ee5\u4ed6\u60f3\u5077\u4e86\u8fd9\u4e9b\u73cd\u5b9d\uff0c\u7136\u540e\u9690\u59d3\u57cb\u540d\u505a\u4e2a\u5bcc\u5bb6\u7fc1\u3002\u4e0d\u6599\u540e\u6765\u4e5f\u5148\u5175 \u8d25\u65b0\u7687\u767b\u57fa\uff0c\u5eb7\u8d85\u6d77\u505a\u8d3c\u5fc3\u865a\uff0c\u800c\u4e14\u4ed6\u7684\u4e24\u4e2a\u5e08\u53d4\u94c1\u81c2\u91d1\u733f\u4e0e\u4e09\u82b1\u5251\u53c8\u90fd\u7ed9\u5f20\u4e39\u67ab\u6536\u670d\uff0c\u6295 \u4e86\u4e8e\u8c26\uff0c\u5bf9\u4ed6\u4e34\u9635\u79c1\u9003\u7684\u884c\u4e3a\u5f88\u662f\u4e0d\u9f7f\u3002\u4ed6\u751f\u6015\u5e08\u53d4\u8ffd\u67e5\uff0c\u53c8\u6015\u65b0\u5e1d\u77e5\u9053\u4ed6\u5077\u4e86\u6b63\u7edf\u7687\u5e1d\u7684 \u5b9d\u7269\u6545\u6b64\u628a\u5fc3\u4e00\u6a2a\uff0c\u9003\u5230\u8499\u53e4\uff0c\u60f3\u5728\u8499\u53e4\u8d2d\u7f6e\u7267\u573a\uff0c\u5b89\u4eab\u4f59\u751f\uff0c\u4f46\u90a3\u4e9b\u5b9d\u7269\u5374\u53c8\u96be\u4ee5\u8131\u624b\u3002 \u4ed6\u53c8\u60f3\u732e\u7ed9\u4e5f\u5148\uff0c\u5728\u74e6\u523a\u6c42\u4e00\u5b98\u534a\u804c\u7684\uff0c\u6b63\u81ea\u8e0c\u8e87\u4e0d\u5b9a\uff0c\u5374\u5728\u8def\u4e0a\u78b0\u5230\u4e86\u9ed1\u767d\u6469\u8bc3\uff0c\u9ed1\u767d\u6469 \u8bc3\u505a\u4e86\u51e0\u5341\u5e74\u7684\u73e0\u5b9d\u4e70\u5356\uff0c\u4e00\u770b\u5c31\u77e5\u9053\u4ed6\u8eab\u4e0a\u85cf\u6709\u975e\u5e38\u7684\u5b9d\u7269\uff0c\u5bf9\u4ed6\u7684\u6765\u5386\u751a\u662f\u6000\u7591\uff0c\u5f53\u65f6 \u672c\u60f3\u5411\u4ed6\u6536\u4e70\uff0c\u4f46\u5eb7\u8d85\u6d77\u77e2\u53e3\u5426\u8ba4\uff0c\u9ed1\u6469\u8bc3\u4e00\u65f6\u6027\u8d77\uff0c\u5c31\u5728\u665a\u4e0a\u65bd\u5c55\u7a7a\u7a7a\u5999\u624b\uff0c\u5c06\u4ed6\u7684\u5b9d\u7269 \u4ee5\u53ca\u9ec4\u5e03\u5305\u88b1\u5185\u7684\u91d1\u5143\u5b9d\u90fd\u5c3d\u884c\u5077\u4e86\u3002 \u3000\u3000\u6b64\u65f6\u5eb7\u8d85\u6d77\u88ab\u5f20\u4e39\u67ab\u8d28\u95ee\uff0c\u987f\u65f6\u53e3\u54d1\uff0c\u7b54\u4e0d\u51fa\u8bdd\u6765\u3002\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u4e8f\u4f60\u662f\u5927\u5185\u603b\u7ba1\uff0c\u7687 \u5e1d\u5f85\u4f60\u4e0d\u8584\uff0c\u4f60\u5728\u5371\u96be\u4e4b\u9645\uff0c\u5f03\u4ed6\u800c\u9003\u5df2\u662f\u8be5\u6b7b\uff0c\u8fd8\u6562\u5077\u5185\u5e9c\u7684\u5b9d\u7269\uff01\u201d\u9ed1\u6469\u8bc3\u5927\u7b11\u9053\uff1a \u201c\u679c\u7136\u4f60\u4e5f\u662f\u5077\u6765\u7684\u3002\u54c8\uff0c\u4f60\u8fd8\u662f\u4ec0\u4e48\u5927\u5185\u603b\u7ba1\u5417\uff1f\u597d\uff0c\u5403\u6211\u4e00\u6756\u5427\uff01\u201d\u5929\u6469\u6756\u6cd5\u4e00\u5c55\uff0c\u6709 \u5982\u5929\u98ce\u6d77\u96e8\uff0c\u903c\u4eba\u800c\u6765\uff0c\u500f\u5730\u4fbf\u4e0b\u6740\u624b\u3002\u5eb7\u8d85\u6d77\u65bd\u5c55\u5e73\u751f\u672c\u9886\uff0c\u4f7f\u5c3d\u5403\u5976\u6c14\u529b\uff0c\u6321\u4e86\u4e94\u62db\uff0c \u7b2c\u516d\u62db\u518d\u4e5f\u62db\u67b6\u4e0d\u4f4f\uff0c\u9a6c\u5200\u7ed9\u9ed1\u6469\u8bc3\u4e00\u6756\u6253\u98de\uff0c\u6756\u5934\u4e0b\u6233\uff0c\u773c\u770b\u5c31\u8981\u63d2\u8fdb\u4ed6\u7684\u4e39\u7530\u8981\u7a74\u3002\u5f20 \u4e39\u67ab\u5fc3\u6709\u4e0d\u5fcd\uff0c\u53eb\u9053\uff1a\u201c\u9976\u4ed6\u4e00\u547d\uff0c\u5e9f\u4e86\u4ed6\u7684\u6b66\u529f\u5427\uff01\u201d\u9ed1\u6469\u8bc3\u4e00\u6756\u4e0b\u6233\uff0c\u6756\u5934\u4e00\u504f\uff0c\u4fbf\u5728 \u4ed6\u7684\u80a9\u5934\u91cd\u91cd\u51fb\u4e86\u4e00\u8bb0\uff0c\u53ef\u601c\u5eb7\u8d85\u6d77\u80a9\u4e0a\u7684\u7435\u7436\u9aa8\u5df2\u88ab\u6572\u788e\uff0c\u6240\u7ec3\u7684\u91d1\u949f\u7f69\u4e5f\u7ed9\u7834\u4e86\uff0c\u6b66\u529f \u5c3d\u5e9f\uff0c\u53ea\u80fd\u50cf\u5e38\u4eba\u4e00\u6837\u7684\u4e86\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u7b11\u9053\uff1a\u201c\u4eba\u4e3a\u8d22\u6b7b\uff0c\u9e1f\u4e3a\u98df\u4ea1\u3002\u4f60\u4eca\u5e78\u800c\u4e0d\u6b7b\uff0c\u7b97\u5929\u5927\u7684\u9020\u5316\uff0c\u4ee5\u540e\u597d\u597d\u505a\u4eba \u5427\u3002\u201d\u5eb7\u8d85\u6d77\u5f97\u9976\u4e86\u6027\u547d\uff0c\u54ea\u91cc\u8fd8\u6562\u8bf4\u8bdd\uff0c\u6025\u6025\u843d\u8352\u800c\u9003\uff0c\u4ed6\u4ece\u6000\u6709\u91cd\u5b9d\u53d8\u6210\u8eab\u65e0\u4e00\u6587\u7684\u7a77 \u6c49\uff0c\u6b66\u529f\u53c8\u5e9f\uff0c\u540e\u6765\u53ea\u597d\u5728\u7267\u573a\u66ff\u4eba\u505a\u5de5\uff0c\u52b3\u788c\u4e00\u751f\uff0c\u90c1\u90c1\u800c\u6b7b\u3002 \u3000\u3000\u5eb7\u8d85\u6d77\u8d70\u540e\uff0c\u9ed1\u767d\u6469\u8bc3\u91cd\u4e0e\u5f20\u4e39\u67ab\u65bd\u793c\u76f8\u89c1\uff0c\u5f7c\u6b64\u5927\u7b11\u3002\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u4f60\u4eec\u4ece\u54ea\u91cc \u6765\uff1f\u201d\u9ed1\u6469\u8bc3\u9053\uff1a\u201c\u6211\u521a\u4ece\u5370\u5ea6\u505a\u4e86\u4e00\u8d9f\u4e70\u5356\u56de\u6765\uff0c\u524d\u65e5\u624d\u7ecf\u8fc7\u5510\u53e4\u62c9\u5c71\u3002\u201d\u5f20\u4e39\u67ab\u5fc3\u5934\u4e00 \u52a8\u9053\uff1a\u201c\u90a3\u662f\u6115\u7f57\u65cf\u7684\u5730\u65b9\u554a\uff0c\u4f60\u4eec\u6709\u89c1\u7740\u914b\u957f\u5417\uff1f\u201d\u767d\u6469\u8bc3\u7b11\u9053\uff1a\u201c\u6211\u4eec\u662f\u4e70\u5356\u4eba\uff0c\u54ea\u6709 \u95f2\u529f\u592b\u53bb\u62dc\u4f1a\u914b\u957f\u3002\u5012\u662f\u53e6\u6709\u4e00\u4e9b\u8d35\u4eba\u53bb\u62dc\u4f1a\u4ed6\u4e86\uff0c\u914b\u957f\u8fd9\u51e0\u5929\u6b63\u5fd9\u7740\u5462\u3002\u201d\u5f20\u4e39\u67ab\u9053\uff1a \u201c\u4ec0\u4e48\u4eba\u53bb\u62dc\u4f1a\u4ed6\uff1f\u201d\u9ed1\u6469\u8bc3\u9053\uff1a\u201c\u8bf4\u662f\u4e5f\u5148\u7684\u4f7f\u8005\u3002\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u55ef\u662f\u4e5f\u5148\u7684\u4f7f\u8005 \u5417\uff1f\u201d\u767d\u6469\u8bc3\u9053\uff1a\u201c\u542c\u8bf4\u4e5f\u5148\u8981\u6536\u4e70\u4ed6\uff0c\u5171\u540c\u5bf9\u4ed8\u963f\u523a\uff0c\u6211\u4e5f\u662f\u5728\u8def\u4e0a\u542c\u5f97\u670b\u53cb\u8bf4\u7684\uff0c\u770b\u6765 \u74e6\u523a\u5c06\u6709\u5185\u4e71\uff0c\u6211\u4eec\u7684\u540c\u884c\u6015\u6218\u4e71\u4e4b\u4e2d\u4f1a\u6709\u635f\u5931\uff0c\u90fd\u51c6\u5907\u5357\u4e0b\u3002\u5440\uff0c\u4f60\u7684\u7236\u4eb2\u662f\u74e6\u523a\u5bb0\u76f8\uff0c \u8fd9\u4e8b\u60c5\u4f60\u8fd8\u4e0d\u77e5\u9053\u5417\uff1f\u201d \u3000\u3000\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u542c\u5230\u4e00\u70b9\u98ce\u58f0\u3002\u201d\u773c\u73e0\u4e00\u8f6c\uff0c\u5ffd\u9053\uff1a\u201c\u4f60\u4eec\u5c06\u90a3\u4e24\u4ef6\u5b9d\u7269\uff0c\u56fe\u7ae0\u548c\u7389\u7c2a\u8ba9 \u7ed9\u6211\u5427\u3002\u5bb6\u7236\u5728\u74e6\u523a\u4eac\u57ce\u8fd8\u6709\u70b9\u4ea7\u4e1a\uff0c\u90fd\u6298\u4ef7\u4e0e\u4f60\u4ea4\u6362\u5427\u3002\u201d\u9ed1\u6469\u8bc3\u5927\u7b11\u9053\uff1a\u201c\u4e0d\u5356\u4e0d \u5356\uff01\u201d\u8fd9\u4e24\u6837\u4e1c\u897f\uff0c\u4e00\u4ef6\u662f\u56fd\u5b9d\uff0c\u4e00\u4ef6\u662f\u7687\u540e\u7684\u4e1c\u897f\uff0c\u5f20\u4e39\u67ab\u60f3\u8d4e\u56de\u6765\u5c06\u6765\u9001\u8fd8\u6b63\u7edf\u7687\u5e1d\uff0c \u542c\u9ed1\u6469\u8bc3\u8bf4\u4e0d\u5356\uff0c\u751a\u662f\u5931\u671b\u3002\u53ea\u542c\u5f97\u9ed1\u6469\u8bc3\u53c8\u7b11\u9053\uff1a\u201c\u5356\u662f\u4e0d\u5356\uff0c\u4f46\u53ef\u4ee5\u9001\u7ed9\u4f60\uff0c\u53cd\u6b63\u662f\u62fe \u6765\u7684\u3002\u4e0d\u6b62\u662f\u90a3\u4e24\u4ef6\u5b9d\u7269\uff0c\u8fd9\u9ec4\u5e03\u5305\u88b1\u91cc\u9762\u7684\u90fd\u9001\u4e0e\u4f60\uff01\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u4ec0\u4e48\uff0c\u8fd9\u600e\u4e48 \u884c\uff1f\u201d\u9ed1\u6469\u8bc3\u53c8\u5927\u7b11\u9053\uff1a\u201c\u5929\u4e0b\u5c31\u53ea\u8bb8\u4f60\u4ed7\u4e49\u758f\u8d22\u5417\uff1f\u4e0a\u6b21\u8499\u4f60\u53d1\u8fd8\u6211\u4eec\u8f93\u6389\u7684\u5730\u4e0b\u5b9d\u85cf\uff0c \u8fd9\u51e0\u4ef6\u4e1c\u897f\u4f60\u65e2\u5408\u7528\uff0c\u5c31\u4e00\u5b9a\u8981\u8bf7\u4f60\u6536\u4e0b\u4e86\u3002\u201d\u5f20\u4e39\u67ab\u773c\u73e0\u4e00\u8f6c\u7b11\u9053\uff1a\u201c\u597d\uff0c\u65e2\u7136\u4e24\u4f4d\u8fd9\u6837 \u6177\u6168\uff0c\u90a3\u6211\u4e5f\u5c31\u4e0d\u518d\u5ba2\u6c14\uff0c\u5168\u6536\u4e0b\u4e86\u3002\u6211\u8fd8\u8981\u8bf7\u4f60\u4eec\u5144\u5f1f\u4ee3\u505a\u4e00\u4e8b\u3002\u201d \u3000\u3000\u9ed1\u767d\u6469\u8bc3\u5e73\u751f\u5bf9\u8c01\u90fd\u4e0d\u4e70\u5e10\uff0c\u552f\u72ec\u4f69\u670d\u5f20\u4e39\u67ab\uff0c\u5f53\u4e0b\u4fbf\u8bf4\u9053\uff1a\u201c\u4f60\u8bf4\u5427\uff0c\u5929\u5927\u7684\u4e8b\u60c5\uff0c \u6211\u4eec\u5144\u5f1f\u4e5f\u80fd\u4e3a\u4f60\u62c5\u5f53\u3002\u201d\u5f20\u4e39\u67ab\u5fae\u7b11\u9053\uff1a\u201c\u4e5f\u4e0d\u662f\u4ec0\u4e48\u5927\u4e8b\uff0c\u8bf7\u4f60\u4eec\u987a\u4fbf\u66ff\u6211\u5e26\u4e00\u5c01 \u4fe1\u3002\u201d\u9ed1\u6469\u8bc3\u9053\uff1a\u201c\u9001\u7ed9\u8c01\u7684\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u4f60\u4eec\u6b64\u884c\uff0c\u5927\u7ea6\u8981\u7ecf\u8fc7\u963f\u523a\u77e5\u9662\u7ba1\u8f96\u7684\u897f\u90e8 \u90e8\u843d\u5427\uff1f\u201d\u767d\u6469\u8bc3\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u662f\u8981\u9001\u4fe1\u7ed9\u963f\u523a\u5417\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u6b63\u662f\u3002\u201d\u65c5\u9014\u6ca1\u6709\u7eb8 \u7b14\uff0c\u5f20\u4e39\u67ab\u5c31\u7528\u5b9d\u5251\u5728\u4e00\u5757\u7f8a\u76ae\u4e0a\u523a\u51fa\u5b57\u8ff9\uff0c\u201c\u5199\u201d\u597d\u4e86\u4e00\u5c01\u4fe1\uff0c\u53c8\u53d6\u4e86\u4e24\u4ef6\u73cd\u5b9d\uff0c\u4ea4\u7ed9\u9ed1 \u6469\u8bc3\u9053\uff1a\u201c\u5c31\u70e6\u4f60\u4eec\u5c06\u8fd9\u5c01\u4fe1\u548c\u8fd9\u4e24\u4ef6\u73cd\u5b9d\uff0c\u9001\u7ed9\u963f\u523a\u3002\u201d\u9ed1\u6469\u8bc3\u968f\u624b\u6536\u4e0b\uff0c\u5f53\u4e0b\u4e0e\u5f20\u4e39\u67ab \u544a\u522b\uff0c\u5206\u5934\u8d76\u8def\u3002 \u3000\u3000\u4e91\u857e\u95ee\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4f60\u5199\u7684\u662f\u4ec0\u4e48\u4fe1\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u66ff\u6115\u7f57\u914b\u957f\u4e0e\u963f\u523a\u76f8\u7ea6\u8054\u76df\u7684 \u4fe1\u3002\u201d\u4e91\u857e\u8be7\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\u6115\u7f57\u914b\u957f\u4f1a\u4e0e\u963f\u523a\u8054\u76df\uff1f\u201d\u5f20\u4e39\u67ab\u7b11\u9053\uff1a\u201c\u6b64\u4e8b\u5df2\u5728\u6211\u5b89\u6392 \u4e4b\u4e2d\u4e86\uff0c\u4e09\u65e5\u4e4b\u540e\uff0c\u4f60\u5c31\u77e5\u9053\u4e86\u3002\u201d \u3000\u3000\u4e24\u4eba\u7684\u5750\u9a91\uff0c\u90fd\u662f\u4e16\u6240\u7f55\u89c1\u7684\u5b9d\u9a6c\uff0c\u867d\u98ce\u96ea\u8def\u6ed1\uff0c\u6bcf\u65e5\u4ecd\u80fd\u8d70\u4e09\u56db\u767e\u91cc\uff0c\u4e09\u65e5\u4e4b\u540e\uff0c\u679c \u7136\u8d76\u5230\u4e86\u5510\u53e4\u62c9\u5c71\u7684\u5c71\u5357\uff0c\u4e24\u4eba\u653e\u7f13\u7ef3\u25a1\uff0c\u6162\u6162\u8d70\u8fdb\u5ce1\u8c37\u3002 \u3000\u3000\u4e91\u857e\u653e\u773c\u65e7\u6e38\u4e4b\u5730\uff0c\u7ae5\u5e74\u60c5\u4e8b\uff0c\u4f9d\u7a00\u5c1a\u80fd\u8bb0\u5fc6\uff0c\u4e91\u857e\u6307\u70b9\u6cbf\u9014\u666f\u7269\uff0c\u8bf4\u662f\u5728\u90a3\u68f5\u5927\u6811\u4e0b \u66fe\u548c\u90bb\u5bb6\u7684\u5973\u4f34\u6349\u8ff7\u85cf\uff0c\u90a3\u4e2a\u5927\u77f3\u8fb9\uff0c\u66fe\u662f\u5979\u7ecf\u5e38\u5750\u5367\u7684\u5730\u65b9\uff0c\u8bf4\u7740\u8bf4\u7740\uff0c\u4e0d\u89c9\u6ef4\u4e0b\u6cea\u6765\uff0c \u663e\u5f97\u65e2\u662f\u5174\u594b\uff0c\u53c8\u662f\u60b2\u51c9\u3002\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u5c31\u8981\u89c1\u7740\u5988\u5988\u4e86\uff0c\u8fd8\u54ed\u4ec0\u4e48\uff1f\u201d\u4e91\u857e\u63e9\u4e86\u773c\u6cea\uff0c \u9053\uff1a\u201c\u6211\u662f\u592a\u9ad8\u5174\u4e86\u3002\u55ef\uff0c\u55ef\uff0c\u4f60\u8bf4\u6211\u597d\u4e0d\u597d\u548c\u4f60\u4e00\u540c\u53bb\u89c1\u5979\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u6709\u4ec0\u4e48\u4e0d \u597d\uff0c\u6015\u5988\u5988\u7b11\u8bdd\u4f60\u5417\uff1f\u201d\u4e91\u857e\u9053\uff1a\u201c\u5c31\u6015\u5979\u77e5\u9053\u4f60\u662f\u6211\u5bb6\u7684\u4ec7\u4eba\u3002\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u53ea\u8981\u4f60\u4e0d \u628a\u6211\u5f53\u4f5c\u4ec7\u4eba\uff0c\u4f2f\u6bcd\u4e5f\u4e00\u5b9a\u4f1a\u5c06\u6211\u5f53\u4f5c\u4f84\u5b50\u770b\u5f85\u3002\u201d\u4e91\u857e\u4e00\u60f3\u6bcd\u4eb2\u662f\u4e2a\u6781\u6148\u7965\u7684\u5fc3\u5730\u5584\u826f\u7684 \u5973\u4eba\uff0c\u5982\u679c\u628a\u548c\u5f20\u4e39\u67ab\u7684\u4e8b\u8be6\u7ec6\u7ed9\u5979\u8bf4\u4e2a\u6e05\u695a\uff0c\u5979\u4e00\u5b9a\u4e0d\u4f1a\u602a\u8d23\uff0c\u53ea\u8981\u6bcd\u4eb2\u5141\u8bb8\uff0c\u5c31\u4e0d\u6015\u54e5 \u54e5\u963b\u6320\uff0c\u60f3\u5230\u6b64\u5904\uff0c\u4e0d\u89c9\u5c55\u7709\u4e00\u7b11\u3002\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u4f60\u7b11\u4ec0\u4e48\uff1f\u201d\u4e91\u857e\u9053\uff1a\u201c\u5c31\u8981\u89c1\u7740\u5988\u5988 \u4e86\uff0c\u96be\u9053\u8fd8\u4e0d\u9ad8\u5174\u5417\uff1f\u201d \u3000\u3000\u5ffd\u800c\u60f3\u8d77\u5988\u5988\u73b0\u5728\u6b63\u5728\u914b\u957f\u5bb6\u505a\u9972\u9a6c\u7684\u4f63\u5987\uff0c\u4e0d\u77e5\u53d7\u5c3d\u591a\u5c11\u59d4\u5c48\u8f9b\u9178\uff0c\u53c8\u4e0d\u89c9\u60b2\u4ece\u4e2d \u6765\uff0c\u7b11\u5bb9\u987f\u655b\uff0c\u6101\u9501\u7709\u7aef\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u4f5c\u4e86\u4e00\u4e2a\u9b3c\u8138\uff0c\u7b11\u9053\uff1a\u201c\u5ffd\u54ed\u5ffd\u7b11\uff0c\u4f55\u82e6\u6765\u54c9\uff01\u201d\u4e91\u857e\u7ed9\u4ed6\u9017\u5f97\u53c8\u662f\u5c55\u989c\u4e00\u7b11\uff0c \u9053\uff1a\u201c\u4f60\u4e5f\u662f\u8fd9\u6837\u7684\u554a\u3002\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u90a3\u4e48\u54b1\u4eec\u662f\u8d8a\u6765\u8d8a\u76f8\u50cf\u4e86\u3002\u201d\u4e91\u857e\u674f\u9762\u98de\u971e\u9053\uff1a \u201c\u6cb9\u5634\u6ed1\u820c\uff0c\u4e0d\u518d\u548c\u4f60\u8bf4\u7b11\u4e86\uff0c\u54b1\u4eec\u5feb\u53bb\u89c1\u914b\u957f\u3002\u201d \u3000\u3000\u5f20\u3001\u4e91\u4e8c\u4eba\u9a8f\u9a6c\u96d5\u978d\uff0c\u4e3e\u6b62\u4e0d\u51e1\uff0c\u65e9\u5c31\u5f15\u4eba\u6ce8\u610f\uff0c\u8d70\u8fdb\u5ce1\u8c37\u4fbf\u6709\u4eba\u8dd1\u53bb\u62a5\u544a\u914b\u957f\uff0c\u8bf4\u662f \u6709\u5982\u6b64\u8fd9\u822c\u7684\u4e24\u4e2a\u964c\u751f\u4eba\u8fdb\u6765\u3002\u4e91\u857e\u5728\u524d\u5e26\u5f15\uff0c\u5230\u4e86\u914b\u957f\u95e8\u524d\u8bf4\u51fa\u6765\u610f\uff0c\u7acb\u523b\u6709\u4eba\u8fdb\u53bb\u901a \u62a5\uff0c\u914b\u957f\u95e8\u524d\uff0c\u5f20\u706f\u7ed3\u5f69\uff0c\u663e\u7136\u662f\u62db\u5f85\u7740\u8d35\u5bbe\u3002\u5f20\u4e39\u67ab\u7b49\u4e86\u4e00\u9635\uff0c\u914b\u957f\u4fbf\u6d3e\u4eba\u5524\u4ed6\u4eec\u8fdb\u53bb\u3002 \u3000\u3000\u5f20\u3001\u4e91\u4e8c\u4eba\u5c06\u9a6c\u5339\u4ea4\u7ed9\u4e0b\u4eba\u6599\u7406\uff0c\u4fbf\u968f\u7740\u201c\u54c8\u90a3\u201d\uff08\u66ff\u914b\u957f\u7ba1\u4e8b\u7684\u4ec6\u4eba\uff09\u8fdb\u53bb\u3002\u54c8\u90a3\u5c06 \u4ed6\u4eec\u5e26\u8fdb\u4e00\u95f4\u623f\u5b50\uff0c\u623f\u4e2d\u70e7\u7740\u4e24\u4e2a\u201c\u706b\u7095\u201d\uff0c\u6696\u878d\u878d\u4e00\u5ba4\u5982\u6625\uff0c\u54c8\u90a3\u8bf7\u4ed6\u4eec\u201c\u4e0a\u7095\u201d\uff08\u5317\u65b9 \u4e60\u4fd7\uff0c\u6bcf\u5230\u51ac\u5929\u5728\u571f\u7095\u4e4b\u4e0b\u70e7\u706b\uff0c\u71c3\u6599\u6216\u662f\u9a6c\u7caa\u6216\u662f\u7164\u70ad\uff0c\u6b64\u7095\u53ef\u4f5c\u7761\u5e8a\uff0c\u6709\u5ba2\u4eba\u6765\u65f6\uff0c\u4fbf \u8bf7\u4ed6\u4eec\u5750\u5728\u7095\u4e0a\u53d6\u6696\u3002\uff09\uff0c\u8bf4\u9053\uff1a\u201c\u914b\u957f\u6b63\u5728\u524d\u5385\u62db\u5f85\u5bbe\u5ba2\uff0c\u5429\u5490\u4f60\u4eec\u5728\u6b64\u7b49\u5019\uff0c\u4ed6\u53eb\u2018\u5439 \u5fe0\u2019\u6765\u63a5\u5f85\u4f60\u4eec\uff0c\u6709\u4ec0\u4e48\u4e8b\u60c5\uff0c\u53ef\u4ee5\u548c\u2018\u5439\u5fe0\u2019\u8bf4\u3002\u201d\u5439\u5fe0\u4e43\u662f\u4e00\u4e2a\u90e8\u843d\u4e2d\u7684\u201c\u6cd5\u5e08\u201d\uff0c\u6743 \u529b\u4ec5\u5728\u914b\u957f\u4e4b\u4e0b\uff0c\u914b\u957f\u6d3e\u5439\u5fe0\u6765\u63a5\u5f85\u4ed6\u4eec\uff0c\u5df2\u7b97\u662f\u5341\u5206\u770b\u91cd\u3002 \u3000\u3000\u4e91\u857e\u6025\u4e8e\u60f3\u89c1\u914b\u957f\u95ee\u6bcd\u4eb2\u7684\u6d88\u606f\uff0c\u542c\u8bf4\u914b\u957f\u4e0d\u80fd\u63a5\u89c1\u4ed6\u4eec\u751a\u662f\u5931\u671b\uff0c\u542c\u5230\u5916\u9762\u9a6c\u5636\u4e4b \u58f0\uff0c\u6b63\u662f\u5f20\u4e39\u67ab\u548c\u81ea\u5df1\u90a3\u4e24\u5339\u9a6c\u7684\u53eb\u58f0\uff0c\u4e0d\u89c9\u60f3\u9053\uff1a\u201c\u4e0d\u77e5\u8fd9\u4e24\u5339\u9a6c\u662f\u4e0d\u662f\u6211\u6bcd\u4eb2\u53bb\u7167\u6599\uff1f \u5440\uff0c\u6211\u4eec\u5728\u8fd9\u6696\u548c\u7684\u623f\u5b50\u91cc\u505a\u914b\u957f\u7684\u5bbe\u5ba2\uff0c\u5979\u5374\u5728\u9a6c\u53a9\u91cc\u66ff\u6211\u4eec\u9972\u9a6c\u3002\u201d\u5fc3\u4e2d\u90c1\u90c1\u4e0d\u4e50\uff0c\u5750 \u5728\u7095\u4e0a\uff0c\u4e0d\u53d1\u4e00\u8a00\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u5374\u5728\u6709\u4e00\u642d\u6ca1\u4e00\u642d\u7684\u548c\u62db\u5f85\u4ed6\u4eec\u7684\u90a3\u4e2a\u201c\u54c8\u90a3\u201d\u804a\u5929\u3002\u5f20\u4e39\u67ab\u95ee\u9053\uff1a\u201c\u914b\u957f\u62db\u5f85 \u4ec0\u4e48\u5bbe\u5ba2\uff1f\u201d\u54c8\u90a3\u9053\uff1a\u201c\u542c\u8bf4\u662f\u4e5f\u5148\u7684\u4f7f\u8005\u3002\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u4ed6\u4eec\u4e0d\u662f\u65e9\u5c31\u6765\u4e86\u5417\uff1f\u201d\u54c8\u90a3 \u7b54\u9053\uff1a\u201c\u662f\u5440\uff0c\u4ed6\u4eec\u5df2\u7ecf\u6765\u4e86\u4e03\u5929\u3002\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u90a3\u4e48\u4e3a\u4f55\u73b0\u5728\u624d\u76db\u7b75\u62db\u5f85\uff1f\u201d\u54c8\u90a3\u652f\u652f \u543e\u543e\uff0c\u6b32\u8bf4\u4e0d\u8bf4\u3002\u5f20\u4e39\u67ab\u5fae\u5fae\u4e00\u7b11\u6478\u51fa\u4e00\u952d\u91d1\u5b50\uff0c\u9053\uff1a\u201c\u4f60\u5728\u8fd9\u91cc\u8f9b\u82e6\u4e86\uff0c\u8fd9\u952d\u91d1\u5b50\u9001\u7ed9\u4f60 \u4e70\u9152\u559d\u3002\u201d\u54c8\u90a3\u66ff\u914b\u957f\u7ba1\u4e8b\uff0c\u5e73\u65f6\u6240\u5f97\u7684\u8d4f\u8d50\u6700\u591a\u662f\u4e00\u4e24\u952d\u5c0f\u94f6\uff0c\u51e0\u66fe\u89c1\u8fc7\u8fd9\u4e48\u5927\u7684\u4e00\u5757\u91d1 \u5b50\uff1f\u7981\u4e0d\u4f4f\u7709\u5f00\u773c\u7b11\uff0c\u63a5\u8fc7\u91d1\u5b50\uff0c\u8fde\u8fde\u9053\u8c22\uff0c\u4e0d\u5f85\u5f20\u4e39\u67ab\u518d\u95ee\uff0c\u4fbf\u81ea\u884c\u544a\u8bc9\u4ed6\u9053\uff1a\u201c\u542c\u8bf4\u4eca \u5929\u914b\u957f\u51c6\u5907\u548c\u4e5f\u5148\u8ba2\u76df\uff0c\u73b0\u5728\u5916\u9762\u76db\u7b75\u62db\u5f85\uff0c\u6050\u6015\u5c31\u8981\u4e3e\u884c\u4eea\u5f0f\u3002\u201d \u3000\u3000\u5f20\u4e39\u67ab\u5fc3\u4e2d\u4e00\u60ca\uff0c\u6697\u9053\uff1a\u201c\u5e78\u559c\u6765\u5feb\u4e00\u6b65\u3002\u201d\u914b\u957f\u6307\u5b9a\u6765\u63a5\u5f85\u4ed6\u4eec\u7684\u90a3\u4f4d\u201c\u5439\u5fe0\u201d\u8fd8\u672a \u89c1\u5230\uff0c\u5f20\u4e39\u67ab\u5ffd\u7136\u7ad9\u8d77\u6765\u8bf4\u9053\uff1a\u201c\u90a3\u4e48\u771f\u662f\u5de7\u6781\u4e86\uff0c\u6211\u4eec\u4e5f\u662f\u592a\u5e08\u6d3e\u6765\u7684\u4eba\uff0c\u6b63\u597d\u8d76\u5f97\u53ca\u89c1 \u89c1\u4ed6\u4eec\u3002\u6211\u4eec\u7684\u592a\u5e08\u89c1\u4ed6\u4eec\u4e45\u4e0d\u56de\u6765\uff0c\u6240\u4ee5\u6d3e\u6211\u4eec\u6765\u95ee\u8baf\u5462\u3002\u201d\u53c8\u638f\u51fa\u4e24\u952d\u91d1\u5b50\uff0c\u9053\uff1a\u201c\u8bf7 \u4f60\u4ee3\u6211\u4eec\u732e\u7ed9\u5439\u5fe0\uff0c\u4f5c\u4e3a\u656c\u795e\u7684\u793c\u91d1\u3002\u8bf7\u4ed6\u4e0d\u5fc5\u7b49\u5019\u6211\u4eec\u4e86\u3002\u660e\u65e5\u6211\u518d\u53bb\u62dc\u4f1a\u4ed6\u3002\u201d\u54c8\u90a3\u89c1 \u5f20\u4e39\u67ab\u51fa\u624b\u9614\u7ef0\u4e4b\u6781\uff0c\u5fc3\u9053\uff1a\u201c\u6562\u60c5\u4ed6\u4eec\u771f\u662f\u4e5f\u5148\u6d3e\u6765\u7684\u4eba\uff0c\u8981\u4e0d\u7136\u54ea\u6709\u8fd9\u6837\u9614\u6c14\u3002\u201d\u4fbf \u9053\uff1a\u201c\u90a3\u4e48\u6211\u8bf7\u793a\u914b\u957f\uff0c\u53eb\u4ed6\u6d3e\u4eba\u5e26\u4f60\u8fdb\u53bb\u3002\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u4e0d\u5fc5\u518d\u60ca\u52a8\u8fd9\u4e48\u591a\u4eba\u4e86\uff0c\u6211\u4eec \u81ea\u5df1\u4f1a\u8fdb\u53bb\u3002\u4f60\u8fd8\u8981\u5728\u8fd9\u91cc\u7b49\u5019\u5439\u5fe0\u5462\u3002\u201d\u95ee\u660e\u524d\u5385\u6240\u5728\uff0c\u4e0d\u5f85\u5206\u8bf4\uff0c\u4fbf\u548c\u4e91\u857e\u8de8\u51fa\u623f\u95e8\u3002 \u54c8\u90a3\u53d7\u4e86\u5f20\u4e39\u67ab\u7684\u91d1\u5b50\uff0c\u53c8\u88ab\u4ed6\u62ff\u8bdd\u552c\u7740\u7adf\u7136\u4e0d\u6562\u62e6\u963b\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u548c\u4e91\u857e\u8d70\u51fa\u623f\u95e8\uff0c\u6025\u5954\u5411\u524d\u5385\uff0c\u914b\u957f\u5bb6\u4e2d\u7684\u4ec6\u4eba\u4e0d\u77e5\u4ed6\u4eec\u7684\u6765\u5386\uff0c\u53ea\u9053\u662f\u914b\u957f\u8bf7 \u6765\u7684\uff0c\u90fd\u6ca1\u6709\u963b\u62e6\u3002\u4e24\u4eba\u4e00\u76f4\u8d70\u8fdb\u5ba2\u5385\uff0c\u53ea\u89c1\u91cc\u9762\u70db\u5149\u660e\u4eae\uff0c\u914b\u957f\u6b63\u5728\u5411\u4e24\u4f4d\u8d35\u4eba\u656c\u9152\u3002 \u3000\u3000\u9aa4\u7136\u4e4b\u95f4\uff0c\u89c1\u5f20\u4e39\u67ab\u4e0e\u4e91\u857e\u8d70\u8fdb\uff0c\u5385\u4e0a\u8bf8\u4eba\uff0c\u65e0\u4e0d\u76f8\u987e\u8be7\u5f02\uff0c\u4e5f\u5148\u7684\u4f7f\u8005\u89c1\u8fd9\u4e24\u4eba\u8863\u670d \u534e\u4e3d\uff0c\u5668\u5b87\u4e0d\u51e1\uff0c\u4ee5\u4e3a\u662f\u914b\u957f\u9080\u8bf7\u6765\u7684\u5bbe\u5ba2\uff0c\u88ab\u5f20\u4e39\u67ab\u773c\u5149\u4e00\u626b\uff0c\u4e0d\u7531\u81ea\u4e3b\u5730\u7ad9\u4e86\u8d77\u6765\uff0c\u70b9 \u9996\u4e3a\u793c\u3002\u914b\u957f\u56e0\u6b64\u4e5f\u8bef\u4f1a\u4ed6\u4eec\u662f\u8d35\u5bbe\u7684\u53cb\u4eba\uff0c\u8d70\u4e0a\u524d\u53bb\u8fce\u63a5\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u5fae\u5fae\u4e00\u7b11\uff0c\u5c06\u4e00\u5c01\u4fe1\u9012\u7ed9\u914b\u957f\uff0c\u672a\u5f85\u914b\u957f\u53d1\u95ee\uff0c\u53c8\u5c06\u90a3\u4ef6\u78a7\u7389\u73ca\u745a\u4e0e\u5b9d\u77f3\u72ee\u5b50\uff0c \u53d6\u4e86\u51fa\u6765\uff0c\u653e\u5728\u684c\u4e0a\uff0c\u8fd9\u4e24\u4ef6\u4e1c\u897f\u662f\u7687\u5e1d\u968f\u8eab\u6240\u643a\u5e26\u7684\u5927\u5185\u5947\u73cd\uff0c\u4e00\u53d6\u51fa\u6765\uff0c\u6beb\u5149\u56db\u5c04\uff0c\u7aef \u7684\u975e\u540c\u5c0f\u53ef\uff0c\u914b\u957f\u773c\u90fd\u5b9a\u4e86\u3002\u53ea\u542c\u5f97\u5f20\u4e39\u67ab\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u8fd9\u70b9\u8584\u793c\uff0c\u655d\u4e3b\u4eba\u8bf7\u914b\u957f\u4e00\u5b9a\u8981\u8d4f \u9762\u6536\u4e0b\u3002\u201d\u914b\u957f\u9053\uff1a\u201c\u600e\u6562\u5f53\u592a\u5e08\u518d\u8d50\u91cd\u793c\u3002\u201d\u4ed6\u8fd8\u4ee5\u4e3a\u9001\u793c\u7684\u662f\u4e5f\u5148\uff0c\u4e00\u770b\u90a3\u4fe1\uff0c\u53ea\u89c1\u5177 \u540d\u7684\u4e43\u662f\u963f\u523a\u77e5\u9662\uff0c\u5403\u4e86\u4e00\u60ca\uff0c\u5c34\u5c2c\u4e4b\u6781\u3002\u5f20\u4e39\u67ab\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u655d\u4e0a\u8bf7\u738b\u7237\u5373\u7b54\u76df\u7ea6\uff0c\u5171\u51fb \u4e5f\u5148\uff01\u201d \u3000\u3000\u6b64\u8a00\u4e00\u51fa\u4e5f\u5148\u7684\u4e24\u4e2a\u4f7f\u8005\u53c8\u60ca\u53c8\u6012\uff0c\u767b\u65f6\u8df3\u8d77\u6765\u9053\uff1a\u201c\u4f60\u662f\u4f55\u4eba\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u54b1\u4eec \u90fd\u662f\u540c\u884c\uff0c\u4f60\u4eec\u662f\u4e5f\u5148\u7684\u4f7f\u8005\uff0c\u6211\u662f\u963f\u523a\u7684\u4f7f\u8005\u3002\u201d\u4e5f\u5148\u7684\u4f7f\u8005\u6012\u9053\uff1a\u201c\u4f60\u6562\u6765\u7834\u574f\u54b1\u4eec\u7684 \u76df\u7ea6\u3002\u8bf7\u738b\u7237\u53d1\u547d\u4ee4\uff0c\u5c06\u8fd9\u4e24\u4eba\u64d2\u4e0b\uff0c\u732e\u7ed9\u592a\u5e08\u3002\u201d\u914b\u957f\u8e0c\u8e87\u4e0d\u51b3\uff0c\u5f20\u4e39\u67ab\u7b11\u9053\uff1a\u201c\u8bf7\u738b\u7237 \u4e09\u601d\u800c\u884c\u3002\u4e5f\u5148\u864e\u72fc\u4e4b\u6027\uff0c\u541e\u5e76\u4e86\u963f\u523a\u4e4b\u540e\uff0c\u4f60\u7109\u80fd\u72ec\u5b58\uff1f\u201d\u4e5f\u5148\u7684\u4f7f\u8005\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u25a1\u597d \u751f\u5927\u80c6\uff0c\u7adf\u6562\u516c\u7136\u6311\u62d4\uff0c\u8bcb\u6bc1\u592a\u5e08\uff0c\u738b\u7237\u8bf7\u901f\u4e0b\u4ee4\uff0c\u5c06\u8fd9\u4e24\u4eba\u64d2\u4e0b\u4e86\u3002\u201d\u914b\u957f\u89c1\u90a3\u4e24\u4e2a\u4e5f\u5148 \u4f7f\u8005\u8dcb\u6248\u975e\u5e38\u518d\u4e09\u50ac\u4fc3\uff0c\u5fc3\u4e2d\u4e0d\u60a6\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6211\u81ea\u6709\u5206\u6570\u3002\u4e0d\u52b3\u4e24\u4f4d\u8d39\u795e\u3002\u201d\u5f20\u4e39\u67ab\u53c8 \u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u76ee\u4e0b\u60c5\u52bf\uff0c\u4e5f\u5148\u5175\u5f3a\uff0c\u963f\u523a\u529b\u5f31\uff0c\u52a9\u5f3a\u6291\u5f31\u4e8b\u60c5\u751a\u6613\u3002\u4e0d\u8fc7\u5440\uff0c\u738b\u7237\u53ef\u6709\u5426\u60f3 \u5230\uff1a\u529b\u5f3a\u8005\u96be\u4ee5\u6297\u8861\uff0c\u529b\u5f31\u8005\u6613\u4e8e\u76f8\u5904\u4e48\uff1f\u201d\u914b\u957f\u5fc3\u4e2d\u4e00\u6014\uff1a\u8fd9\u6b63\u662f\u4ed6\u4e03\u65e5\u4ee5\u6765\uff0c\u8fdf\u8fdf\u672a\u7b54 \u590d\u4e5f\u5148\u8ba2\u76df\u7684\u539f\u56e0\u3002\u8fd9\u65f6\u4e00\u542c\u5f20\u4e39\u67ab\u8fd9\u4e24\u53e5\u8bdd\uff0c\u6709\u5982\u88ab\u5229\u9488\u523a\u4e86\u4e00\u4e0b\uff0c\u51b7\u6c57\u76f4\u6d41\uff0c\u6697\u81ea\u601d \u91cf\uff1a\u201c\u6b64\u8bdd\u8bf4\u5f97\u5f53\u771f\u4e0d\u9519\uff01\u4e5f\u5148\u5175\u529b\u6bd4\u6211\u5f3a\u6570\u500d\uff0c\u4e8b\u6210\u4e4b\u540e\uff0c\u4ed6\u82e5\u4e00\u65e6\u53cd\u8138\uff0c\u6211\u662f\u6beb\u65e0\u529e\u6cd5 \u62b5\u6321\u3002\u963f\u523a\u5175\u529b\u4e0e\u6211\u5dee\u4e0d\u591a\uff0c\u4ed6\u8981\u8054\u5408\u5404\u65cf\u914b\u957f\u5171\u6297\u4e5f\u5148\uff0c\u90a3\u4e48\u4e8b\u6210\u4e4b\u540e\uff0c\u5f7c\u6b64\u8fd8\u53ef\u76f8\u5b89\uff0c \u5404\u4fdd\u7586\u571f\u3002\u201d \u3000\u3000\u4e5f\u5148\u7684\u4e24\u4e2a\u4f7f\u8005\u89c1\u914b\u957f\u773c\u5149\u95ea\u70c1\uff0c\u663e\u5f97\u5fc3\u601d\u4e0d\u5b9a\uff0c\u53c8\u6025\u53c8\u6012\uff0c\u751f\u6015\u6709\u53d8\uff0c\u8fd9\u4e24\u4eba\u90fd\u662f\u4e5f \u65e0\u5e10\u4e0b\u7684\u6b66\u5b98\uff0c\u5200\u6cd5\u751a\u7cbe\uff0c\u4e00\u65f6\u6c14\u8d77\uff0c\u4e0d\u5f85\u601d\u91cf\uff0c\u4fbf\u53cc\u53cc\u62d4\u5200\u6765\u65a9\u5f20\u4e39\u67ab\u3002\u5f20\u4e39\u67ab\u505a\u4e86\u4e00\u4e2a \u9b3c\u8138\uff0c\u628a\u624b\u4e00\u5f15\uff0c\u8f7b\u8f7b\u4e00\u95ea\uff0c\u95ea\u5230\u914b\u957f\u80cc\u540e\uff0c\u90a3\u4e24\u53e3\u5200\u6536\u52bf\u4e0d\u53ca\u51e0\u4e4e\u780d\u5230\u914b\u957f\u8eab\u4e0a\u3002\u914b\u957f\u52c3 \u7136\u5927\u6012\uff0c\u559d\u9053\uff1a\u201c\u7ed9\u6211\u62ff\u4e0b\u8fd9\u4e24\u4e2a\u51f6\u5f92\uff01\u201d\u4e5f\u5148\u7684\u4e24\u4e2a\u4f7f\u8005\u6012\u9053\uff1a\u201c\u8c01\u6562\u62ff\u6211\uff1f\u201d\u547c\u547c\u4e24\u5200 \u5c06\u914b\u957f\u536b\u58eb\u7684\u5175\u5203\u6253\u98de\uff0c\u5c31\u60f3\u95ef\u51fa\u5385\u53bb\uff0c\u9661\u7136\u95f4\u5ffd\u89c9\u817f\u5f2f\u4e00\u9ebb\uff0c\u4e0d\u7531\u81ea\u5df1\u5730\u5c48\u819d\u8dea\u5012\u5728\u5f20\u4e39 \u67ab\u9762\u524d\uff0c\u5f20\u4e39\u67ab\u7b11\u9053\uff1a\u201c\u4f55\u6545\u5982\u6b64\u524d\u5028\u800c\u540e\u606d\uff1f\u201d\u914b\u957f\u7684\u536b\u58eb\u62a2\u4e0a\u524d\u6765\uff0c\u4e00\u4e0b\u5c31\u628a\u8fd9\u4e24\u540d\u4f7f \u8005\u8e22\u7ffb\u7ed1\u4e2a\u7ed3\u5b9e\u3002\u8fd9\u4e24\u4e2a\u4f7f\u8005\u7cca\u91cc\u7cca\u6d82\uff0c\u88ab\u4eba\u64d2\u4e86\uff0c\u8fd8\u4e0d\u77e5\u9053\u8fd9\u662f\u5f20\u4e39\u67ab\u7684\u6697\u7b97\u3002 \u3000\u3000\u914b\u957f\u547d\u4ee4\u536b\u58eb\u5c06\u4e5f\u5148\u7684\u4e24\u4e2a\u4f7f\u8005\u5e26\u4e0b\uff0c\u5173\u7981\u8d77\u6765\uff0c\u6bc5\u7136\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u6211\u4e0e\u4f60\u4eec\u7684\u77e5\u9662\u8ba2 \u76df\u3002\u201d\u4ed6\u867d\u7136\u754f\u60e7\u4e5f\u5148\uff0c\u4f46\u4e8b\u5230\u5982\u4eca\uff0c\u52bf\u6210\u9a91\u864e\uff0c\u4e5f\u4e0d\u7531\u4ed6\u4e0d\u4e0e\u963f\u523a\u8054\u76df\uff0c\u4ee5\u56fe\u81ea\u4fdd\u4e86\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u4e0e\u914b\u957f\u5f53\u4e0b\u6b43\u8840\u4e3a\u76df\uff0c\u4e91\u857e\u5728\u65c1\u8fb9\u770b\u5f97\u6697\u6697\u53d1\u7b11\uff0c\u5fc3\u9053\uff1a\u201c\u4e39\u67ab\u771f\u662f\u795e\u5999\u83ab\u6d4b\uff0c \u53e4\u602a\u4e4b\u6781\uff01\u4ed6\u5047\u5192\u963f\u523a\u7684\u4f7f\u8005\uff0c\u5c45\u9a97\u5f97\u914b\u957f\u8fd9\u4e48\u76f8\u4fe1\u3002\u201d\u5176\u5b9e\u5f20\u4e39\u67ab\u65e9\u5df2\u6599\u5230\u6709\u4eca\u65e5\u4e4b\u4e8b\uff0c \u5728\u6258\u9ed1\u6469\u8bc3\u5e26\u4fe1\u4e4b\u65f6\uff0c\u5df2\u5c06\u8ba2\u5212\u5199\u5728\u7f8a\u76ae\u4e4b\u4e0a\u6258\u4ed6\u4ea4\u7ed9\u963f\u523a\u4e86\uff0c\u8fd9\u76df\u7ea6\u963f\u523a\u5c06\u6765\u5fc5\u7136\u627f\u8ba4\uff0c \u6240\u4ee5\u4ed6\u8fd9\u4f7f\u8005\u5012\u5e76\u4e0d\u662f\u7eaf\u5c5e\u5047\u5192\u3002 \u3000\u3000\u8ba2\u76df\u4e4b\u540e\uff0c\u914b\u957f\u5c31\u7528\u9152\u5e2d\u62db\u5f85\u4ed6\u4eec\u3002\u4e91\u857e\u5fc3\u6025\u5982\u711a\uff0c\u60f3\u8d77\u6bcd\u4eb2\uff0c\u9152\u96be\u4e0b\u54bd\uff0c\u5ba2\u5957\u4e00\u756a\u4e4b \u540e\uff0c\u6025\u5fd9\u95ee\u9053\uff1a\u201c\u8bf7\u95ee\u738b\u7237\uff0c\u6709\u6ca1\u6709\u8fd9\u6837\u4e00\u4f4d\u9972\u9a6c\u7684\u8001\u5927\u5a18\uff1f\u201d\u5c06\u6bcd\u4eb2\u5f62\u8c8c\uff0c\u51ed\u81ea\u5df1\u7684\u8bb0 \u5fc6\uff0c\u7ea6\u7565\u63cf\u8ff0\u3002\u914b\u957f\u89c1\u8d35\u5ba2\u5ffd\u7136\u95ee\u8d77\u4e00\u4f4d\u9972\u9a6c\u7684\u5927\u5a18\uff0c\u5341\u5206\u60ca\u8be7\uff0c\u60f3\u4e86\u4e00\u60f3\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u50cf \u6709\u8fd9\u4e48\u4e00\u4e2a\u4eba\uff0c\u6211\u4e5f\u8bb0\u4e0d\u6e05\u695a\u4e86\u3002\u5f85\u6211\u95ee\u95ee\u7ba1\u7406\u9a6c\u623f\u7684\u54c8\u90a3\u3002\u201d \u3000\u3000\u7247\u523b\u4e4b\u540e\uff0c\u7ba1\u7406\u9a6c\u623f\u7684\u54c8\u90a3\u5df2\u88ab\u914b\u957f\u4f20\u6765\uff0c\u4e91\u857e\u53c8\u95ee\u4e86\u4e00\u904d\uff0c\u54c8\u90a3\u6414\u9996\u601d\u7d22\uff0c\u8fc7\u4e86\u8bb8 \u4e45\uff0c\u624d\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u662f\u6709\u8fd9\u6837\u7684\u4e00\u4f4d\u8001\u5927\u5a18\u3002\u201d\u4e91\u857e\u5927\u559c\uff0c\u6025\u9053\uff1a\u201c\u8bf7\u90a3\u4f4d\u8001\u5927\u5a18\u51fa \u6765\uff0c\u6211\u4eec\u6e34\u6b32\u4e0e\u5979\u4e00\u89c1\u3002\u201d\u4e91\u857e\u672c\u60f3\u8bf4\u660e\u8fd9\u8001\u5927\u5a18\u5c31\u662f\u5979\u7684\u6bcd\u4eb2\uff0c\u4f46\u8bdd\u5230\u53e3\u8fb9\u5374\u53c8\u5fcd\u7740\uff0c\u60f3 \u7b49\u5230\u76f8\u8ba4\u4e4b\u540e\uff0c\u518d\u5411\u914b\u957f\u8bf4\u660e\u539f\u59d4\u514d\u5f97\u914b\u957f\u96be\u4e3a\u60c5\u3002 \u3000\u3000\u90a3\u7ba1\u9a6c\u623f\u7684\u54c8\u90a3\u53c8\u6414\u4e86\u6414\u5934\uff0c\u534a\u664c\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u8001\u5927\u5a18\u5230\u5e9c\u4e2d\u9972\u9a6c\uff0c\u90a3\u662f\u4e03\u5e74\u524d\u4e4b\u4e8b \u4e86\uff0c\u55ef\uff0c\u5979\u73b0\u5728\uff0d\uff0d\u201d\u4e91\u857e\u5fc3\u5934\u4e00\u8df3\uff0c\u53eb\u9053\uff1a\u201c\u5979\u73b0\u5728\u600e\u4e48\u4e86\uff1f\u201d\u54c8\u90a3\u60ca\u5f02\u4e4b\u6781\uff0c\u770b\u4e86\u4e91\u857e \u4e00\u773c\u9053\uff1a\u201c\u5979\u73b0\u5728\u5df2\u4e0d\u5728\u8fd9\u513f\u4e86\u3002\u4e09\u5e74\u524d\u5979\u79bb\u5f00\u8fd9\u513f\uff0c\u542c\u8bf4\u8fd8\u662f\u4f4f\u5728\u539f\u6765\u7684\u5730\u65b9\u3002\u55ef\uff0c\u5979\u7684 \u5883\u9047\u5f88\u662f\u60b2\u60e8\uff0c\u4e0d\u8fc7\u561b\uff0c\u73b0\u5728\u542c\u8bf4\u5012\u597d\u4e86\u70b9\u513f\u3002\u201d \u3000\u3000\u54c8\u90a3\u7d6e\u7d6e\u4e0d\u4f11\u5730\u8fd8\u5f85\u8bf4\u90a3\u8001\u5927\u5a18\u7684\u4e8b\u60c5\uff0c\u4e91\u857e\u7ad9\u8d77\u6765\u9053\uff1a\u201c\u597d\uff0c\u6211\u4eec\u73b0\u5728\u5c31\u60f3\u53bb\u89c1\u90a3\u4f4d \u8001\u5927\u5a18\uff0c\u738b\u7237\uff0c\u54b1\u4eec\u544a\u8f9e\u4e86\u3002\u201d\u914b\u957f\u548c\u54c8\u90a3\u90fd\u662f\u60ca\u8be7\u4e4b\u6781\uff0c\u683c\u4e8e\u793c\u8282\uff0c\u4e0d\u4fbf\u5411\u8d35\u5bbe\u76d8\u95ee\u3002\u914b \u957f\u9053\uff1a\u201c\u8981\u6211\u6d3e\u4eba\u7ed9\u4f60\u5e26\u8def\u5417\uff1f\u201d\u4e91\u857e\u9053\uff1a\u201c\u6211\u81ea\u5df1\u8ba4\u5f97\u3002\u201d\u5306\u5306\u4e00\u793c\uff0c\u4fbf\u4e0e\u5f20\u4e39\u67ab\u544a\u8f9e\u51fa \u95e8\u3002\u7b49\u4ed6\u4eec\u53bb\u4e86\u4e4b\u540e\uff0c\u7ba1\u9a6c\u623f\u7684\u90a3\u4f4d\u54c8\u90a3\u624d\u60f3\u8d77\u4e91\u857e\u7684\u9762\u8c8c\u548c\u90a3\u4f4d\u8001\u5927\u5a18\u751a\u4e3a\u76f8\u4f3c\u3002 \u3000\u3000\u4e91\u857e\u548c\u5f20\u4e39\u67ab\u53d6\u4e86\u9a6c\u5339\uff0c\u89c5\u8def\u524d\u5f80\uff0c\u4e00\u8def\u4e0a\u4e91\u857e\u9ed8\u4e0d\u4f5c\u58f0\u795e\u60c5\u5174\u594b\u4e4b\u6781\uff0c\u6cea\u73e0\u6ef4\u4e86\u4e0b \u6765\uff0c\u63e9\u5e72\u4e86\u4e00\u6b21\u53c8\u6ef4\u4e00\u6b21\u3002\u8d70\u4e86\u4e00\u9635\uff0c\u4e91\u857e\u731b\u5730\u52d2\u5f80\u9a6c\u25a1\uff0c\u9053\uff1a\u201c\u8f6c\u8fc7\u8fd9\u6761\u5c0f\u6eaa\uff0c\u524d\u9762\u90a3\u5bb6 \u9ec4\u571f\u6ce5\u623f\u5c31\u662f\u6211\u7684\u5bb6\u4e86\u3002\u5509\uff0c\u95e8\u524d\u7684\u6885\u82b1\u8fd8\u662f\u50cf\u65e7\u65f6\u4e00\u6837\u3002\u5c71\u5761\u540e\u7684\u677e\u6811\u4e5f\u8fd8\u6ca1\u6709\u65a9\u4f10\uff0c\u5c0f \u65f6\u5019\u5988\u5988\u5e38\u5728\u677e\u6797\u91cc\u5531\u6b4c\u7ed9\u6211\u542c\u3002\u201d\u5f20\u4e39\u67ab\u8df3\u4e0b\u9a6c\u6765\uff0c\u4e00\u7b11\u9053\uff1a\u201c\u82e6\u5c3d\u7518\u6765\uff0c\u4f2f\u6bcd\u4eca\u5929\u89c1\u5230 \u4f60\uff0c\u4e0d\u77e5\u8be5\u591a\u9ad8\u5174\u5462\uff01\u201d \u3000\u3000\u4e91\u857e\u671b\u89c1\u5bb6\u95e8\uff0c\u5fc3\u4e2d\u65e0\u9650\u8f9b\u9178\uff0c\u500f\u65f6\u95f4\uff0c\u513f\u65f6\u60c5\u4e8b\uff0c\u90fd\u4e00\u4e00\u6d8c\u4e0a\u5fc3\u5934\uff0c\u4e0d\u81ea\u89c9\u5730\u5531\u8d77\u5c0f \u65f6\u5019\u6bcd\u4eb2\u6559\u5979\u7684\u7267\u7f8a\u5c0f\u8c03\uff1a \u3000\u3000\u6211\u968f\u7740\u5988\u5988\u53bb\u7267\u7f8a\uff0c \u3000\u3000\u7f8a\u513f\u5403\u8349\u5403\u5f97\u6b22\uff0c \u3000\u3000\u5c71\u5761\u7684\u82b1\u513f\u5f00\u5f97\u9999\uff0c \u3000\u3000\u5988\u5988\u7684\u6b4c\u513f\u5531\u5f97\u54cd\uff0c \u3000\u3000\u6211\u7684\u5c0f\u5fc3\u809d\u771f\u6b22\u7545\u3002 \u3000\u3000\u54ce\u5440\uff0c\u5929\u8fb9\u76d8\u65cb\u7740\u5927\u5140\u9e70\uff0c \u3000\u3000\u5b83\u8981\u6293\u53bb\u54b1\u4eec\u7684\u5c0f\u7ef5\u7f8a\uff0c \u3000\u3000\u5c0f\u7ef5\u7f8a\u8eb2\u8eb2\u95ea\u95ea\u771f\u53ef\u601c\u3002 \u3000\u3000\u4e0d\u8981\u6015\u5440\uff0c\u6211\u7684\u5c0f\u5fc3\u809d\uff0c \u3000\u3000\u5c0f\u7ef5\u7f8a\u9760\u5728\u6bcd\u4eb2\u8eab\u65c1\uff0c \u3000\u3000\u4f60\u4e5f\u9760\u7740\u4eb2\u5a18\uff0c \u3000\u3000\u54ea\u4e00\u5904\u5730\u65b9\u90fd\u6ca1\u6709\u6bcd\u4eb2\u7684\u8eab\u8fb9\u5b89\u5168\u3002 \u3000\u3000\u5140\u9e70\u6293\u4e0d\u53bb\u5c0f\u7ef5\u7f8a\uff0c \u3000\u3000\u4e5f\u6ca1\u6709\u8c01\u80fd\u62a2\u53bb\u6211\u7684\u5c0f\u5fc3\u809d\u3002 \u3000\u3000\u4e91\u857e\u4e00\u8fb9\u5531\u4e00\u8fb9\u8d70\u8fd1\u5bb6\u95e8\uff0c\u5f20\u4e39\u67ab\u773c\u89d2\u4e5f\u4e0d\u89c9\u6da6\u6e7f\u4e86\u3002\u5ffd\u542c\u5f97\u5440\u7684\u4e00\u58f0\uff0c\u90a3\u4e24\u6247\u7834\u95e8\u5ffd \u5730\u6253\u5f00\uff0c\u4e00\u4e2a\u5305\u7740\u5934\u5dfe\u7684\u8499\u53e4\u5927\u5a18\u8d70\u4e86\u51fa\u6765\uff0c\u989c\u5bb9\u6194\u60b4\uff0c\u4e24\u53ea\u773c\u775b\u772f\u6210\u4e00\u6761\u7f1d\uff0c\u8863\u88f3\u867d\u7136\u8fd8 \u7b97\u5e72\u51c0\uff0c\u4f46\u5374\u9489\u4e0a\u65e0\u6570\u8865\u9489\u3002\u4e91\u857e\u6cea\u5982\u6cc9\u6d8c\uff0c\u98de\u5954\u4e0a\u524d\uff0c\u62b1\u7740\u90a3\u4e2a\u5927\u5a18\u3002\u90a3\u8001\u5927\u5a18\u6cea\u4e0b\u5982 \u96e8\uff0c\u63fd\u7740\u4e91\u857e\uff0c\u98a4\u58f0\u53eb\u9053\uff1a\u201c\u6211\u7b49\u4e86\u5341\u5e74\u4e86\uff0c\u771f\u7684\u662f\u4f60\u5417\uff1f\u6211\u7684\u5c0f\u5fc3\u809d\uff01\u201d\u4e91\u857e\u54bd\u6cea\u7b11\u9053\uff1a \u201c\u5a18\uff0c\u662f\u6211\u5440\uff0c\u4f60\u770b\u4e0d\u89c1\u6211\u5417\uff1f\u201d\u90a3\u8001\u5927\u5a18\u9053\uff1a\u201c\u51d1\u8fd1\u4e00\u70b9\u8ba9\u6211\u77a7\uff0c\u554a\uff0c\u679c\u7136\u662f\u6211\u7684\u5c0f\u5b9d \u8d1d\uff0c\u5c0f\u5fc3\u809d\uff01\u201d\u53ef\u601c\u4e91\u857e\u7684\u6bcd\u4eb2\uff0c\u5f53\u5e74\u56e0\u4e3a\u5979\u7684\u4e08\u592b\u548c\u5973\u513f\u7a81\u7136\u592b\u8e2a\uff0c\u54ed\u5f97\u6cea\u90fd\u51e0\u4e4e\u5e72\u4e86\uff0c \u89c6\u529b\u6a21\u7cca\uff0c\u867d\u5c1a\u672a\u5168\u76f2\uff0c\u4f46\u5728\u4e09\u5c3a\u4e4b\u5916\uff0c\u4fbf\u53ea\u89c1\u4e00\u56e2\u9ed1\u5f71\uff0c\u5979\u8fde\u5973\u513f\u7684\u9762\u5bb9\u90fd\u770b\u4e0d\u6e05\u695a\u4e86\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u5fc3\u4e2d\u65e0\u9650\u96be\u8fc7\uff0c\u60f3\u9053\uff1a\u201c\u5c06\u8fd9\u4f4d\u5584\u826f\u7684\u8001\u5927\u5a18\u7d2f\u6210\u8fd9\u4e2a\u6837\u5b50\uff0c\u5440\uff0c\u8fd9\u90fd\u662f\u6211\u5bb6\u7684 \u7f6a\u8fc7\u3002\u201d\u4ed6\u4e00\u8def\u6765\u65f6\uff0c\u6240\u60f3\u597d\u7684\u5343\u8a00\u4e07\u8bed\uff0c\u6240\u60f3\u597d\u7684\u5b89\u6170\u5979\u4eec\u6bcd\u5973\u7684\u8bdd\uff0c\u7adf\u7136\u4e00\u53e5\u4e5f\u8bf4\u4e0d\u51fa \u6765\u4e86\uff0c\u53ea\u662f\u832b\u7136\u5730\u8d70\u4e0a\u524d\u53bb\u3002\u53ef\u662f\u4e91\u857e\u548c\u5979\u7684\u6bcd\u4eb2\u6b63\u5728\u62b1\u5934\u76f8\u54ed\u597d\u50cf\u7adf\u7136\u5fd8\u8bb0\u4e86\u8eab\u8fb9\u8fd8\u6709\u5f20 \u4e39\u67ab\u8fd9\u4e2a\u4eba\u3002 \u3000\u3000\u8fd9\u4e00\u77ac\u95f4\uff0c\u5f20\u4e39\u67ab\u53ea\u89c9\u5f97\u6bd4\u4e91\u857e\u8fd8\u8981\u52a0\u500d\u9178\u82e6\uff0c\u5ffd\u542c\u5f97\u90a3\u8001\u5927\u5a18\u53eb\u9053\uff1a\u201c\u963f\u857e\u7684\u7239\uff0c\u4f60 \u542c\u89c1\u4e86\u5417\uff1f\u201d\u5c4b\u5185\u53c8\u8d70\u51fa\u4e00\u4e2a\u4eba\u6765\uff0c\u4e91\u857e\u62ac\u5934\u4e00\u770b\uff0c\u4e0d\u89c9\u5446\u4e86\u3002 \u3000\u3000\u53ea\u89c1\u8fd9\u4eba\u9762\u4e0a\u4ea4\u53c9\u7740\u51e0\u9053\u4f24\u75db\uff0c\u4e00\u8df7\u4e00\u62d0\u5730\u8d70\u4e86\u51fa\u6765\uff0c\u539f\u6765\u662f\u8ddb\u4e86\u4e00\u8db3\uff0c\u5934\u53d1\u7a00\u758f\uff0c\u4e00 \u534a\u6591\u767d\uff0c\u8863\u88f3\u4e5f\u662f\u7834\u7834\u70c2\u70c2\uff0c\u795e\u6c14\u6781\u662f\u9a87\u4eba\u3002\u4e91\u857e\u9aa4\u773c\u4e4b\u95f4\uff0c\u51e0\u4e4e\u8ba4\u4e0d\u51fa\u4ed6\u662f\u8c01\u6765\uff0c\u542c\u5f97\u6bcd \u4eb2\u558a\u4ed6\u505a\u201c\u963f\u857e\u7684\u7239\u201d\uff0c\u5fc3\u5934\u535c\u901a\u4e00\u8df3\uff0c\u8fd9\u624d\u4ece\u4e11\u964b\u7684\u989c\u5bb9\u9690\u7ea6\u770b\u51fa\u5979\u7236\u4eb2\u5f53\u5e74\u7684\u9762\u8c8c\u3002\u6b63 \u662f\uff1a \u3000\u3000\u8270\u96be\u5386\u52ab\u4f59\u751f\u5728\uff0c\u7236\u5973\u91cd\u9022\u6700\u65ad\u80a0\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u840d\u8e2a\u4fa0\u5f71\u5f55\u300b\u2014\u2014\u7b2c\u4e8c\u5341\u4e03\u56de\u3000\u6069\u6028\u96be\u5fd8\u8c6a\u60c5\u5316\u98de\u7d6e\u3000\u60c5\u75f4\u4e0d\u6094\u9b54\u7a9f\u7f14\u77e5\u4ea4 \u6881\u7fbd\u751f\u300a\u840d\u8e2a\u4fa0\u5f71\u5f55\u300b \u7b2c\u4e8c\u5341\u4e03\u56de\u3000\u6069\u6028\u96be\u5fd8\u8c6a\u60c5\u5316\u98de\u7d6e\u3000\u60c5\u75f4\u4e0d\u6094\u9b54\u7a9f\u7f14\u77e5\u4ea4 \u3000\u3000\u539f\u6765\u4e91\u857e\u7684\u7236\u4eb2\u4e91\u6f84\uff0c\u5f53\u5e74\u62a4\u9001\u4e91\u9756\u56de\u56fd\uff0c\u5728\u96c1\u95e8\u5173\u5916\u7684\u5c71\u5934\uff0c\u9047\u7740\u8ffd\u5175\uff0c\u4ed6\u62fc\u6b7b\u65ad \u540e\uff0c\u53d7\u4e86\u91cd\u4f24\uff0c\u8dcc\u4e0b\u6df1\u8c37\u3002\u5f53\u65f6\u6f6e\u97f3\u548c\u5c1a\u7b49\u4eba\u5728\u9ed1\u591c\u4e4b\u4e2d\uff0c\u542c\u5230\u4ed6\u51c4\u60e8\u7684\u53eb\u58f0\uff0c\u53c8\u89c1\u4ed6\u4ece\u60ac \u5ca9\u8df3\u4e0b\uff0c\u90fd\u4ee5\u4e3a\u4ed6\u5fc5\u6b7b\u65e0\u7591\uff0c\u5373\u4e91\u857e\u5144\u59b9\uff0c\u4ea6\u65ad\u65ad\u6599\u4e0d\u5230\u4ed6\u4eec\u7684\u7236\u4eb2\u5c1a\u5728\u4eba\u4e16\u3002 \u3000\u3000\u8c01\u77e5\u4e91\u6f84\u5e76\u6ca1\u6709\u6b7b\uff0c\u4ed6\u8dcc\u4e0b\u4e4b\u65f6\u88ab\u6811\u6748\u4e00\u6321\uff0c\u867d\u8dcc\u8ddb\u4e86\u4e00\u8db3\uff0c\u9762\u5bb9\u4e5f\u7ed9\u5c16\u5229\u7684\u4e71\u77f3\u5212 \u6bc1\uff0c\u4f46\u5374\u4fdd\u5168\u4e86\u6027\u547d\u3002\u53ef\u662f\u4ed6\u867d\u6ca1\u6b7b\uff0c\u6240\u906d\u9047\u7684\u5374\u6bd4\u6b7b\u8fd8\u96be\u53d7\uff01\u4ed6\u53d7\u4e86\u91cd\u4f24\uff0c\u5728\u5c71\u8c37\u4e4b\u4e2d\u53c8 \u65e0\u4eba\u76f8\u6551\uff0c\u53ea\u597d\u5403\u6b7b\u5c38\u8eab\u4e0a\u7684\u5e72\u7cae\uff08\u5728\u683c\u6597\u4e4b\u4e2d\uff0c\u4ea6\u6709\u8bb8\u591a\u8499\u53e4\u5175\u88ab\u6253\u6b7b\u800c\u8dcc\u843d\u4e0b\u9762\u7684\uff09\uff0c \u6e34\u4e86\u5c31\u996e\u96ea\u6c34\uff0c\u8fd9\u6837\u7684\u517b\u4e86\u51e0\u65e5\uff0c\u6c14\u529b\u5c45\u7136\u6e10\u6e10\u6062\u590d\uff0c\u722c\u51fa\u8c37\u53bb\uff0c\u5728\u96c1\u95e8\u5173\u5916\u4e5e\u98df\u6d41\u6d6a\uff0c\u4e0d \u4e45\u5c31\u6253\u63a2\u5230\u4e91\u9756\u5728\u96c1\u95e8\u5173\u9047\u96be\u7684\u6d88\u606f\uff0c\u4ed6\u5fc3\u7070\u610f\u51b7\uff0c\u53ea\u89c9\u5929\u5730\u832b\u832b\uff0c\u66f4\u65e0\u4e00\u5904\u662f\u81ea\u5df1\u7acb\u8db3\u4e4b \u5730\u3002 \u3000\u3000\u4ed6\u5e78\u800c\u672a\u6b7b\uff0c\u4f46\u811a\u8ddb\u5bb9\u6bc1\uff0c\u6b66\u529f\u5c3d\u5931\uff0c\u51e0\u4e4e\u6210\u4e86\u5e9f\u4eba\uff0c\u5728\u96c1\u95e8\u5173\u5916\u6d41\u6d6a\u3002\u53c8\u56e0\u4e91\u9756\u60e8 \u6b7b\uff0c\u81ea\u5df1\u4ea6\u662f\u201c\u53db\u9006\u201d\u4e4b\u540e\uff0c\u4e07\u4e07\u4e0d\u80fd\u8fc7\u96c1\u95e8\u5173\u91cd\u56de\u4e2d\u56fd\uff0c\u8981\u4e0d\u662f\u4ed6\u8fd8\u6709\u4e24\u4e2a\u513f\u5973\uff0c\u5fc3\u4e2d\u5c1a \u6709\u4e00\u70b9\u6302\u5ff5\uff0c\u4ed6\u65e9\u5c31\u5728\u96c1\u95e8\u5173\u5916\u7684\u8352\u91ce\u4e4b\u4e2d\u81ea\u5c3d\u4e86\u3002 \u3000\u3000\u4ed6\u6d41\u6d6a\u4e86\u5e74\u4f59\uff0c\u60f3\u6765\u60f3\u53bb\uff0c\u53ea\u6709\u91cd\u56de\u74e6\u523a\uff0c\u5c31\u8fd9\u6837\uff0c\u518d\u8e0f\u904d\u4e07\u6c34\u5343\u5c71\uff0c\u6709\u65f6\u7ed9\u4eba\u505a\u77ed \u5de5\uff0c\u6ca1\u4eba\u8bf7\u65f6\u5c31\u4e5e\u98df\uff0c\u7ecf\u8fc7\u65e0\u6570\u8f9b\u9178\u75db\u82e6\uff0c\u53c8\u4ece\u96c1\u95e8\u5173\u5916\u56de\u5230\u4e86\u8499\u53e4\u5317\u8fb9\u5510\u53e4\u62c9\u5c71\u5357\u9762\u7684\u5ce1 \u8c37\uff0c\u627e\u5230\u4e86\u4ed6\u59bb\u5b50\u7684\u90e8\u843d\u3002 \u3000\u3000\u8fd9\u65f6\u4e91\u857e\u7684\u6bcd\u4eb2\u5df2\u5728\u914b\u957f\u5bb6\u4e2d\u505a\u9972\u9a6c\u7684\u4ec6\u5987\uff0c\u4e91\u6f84\u53c8\u8d39\u4e86\u8bb8\u591a\u5fc3\u529b\uff0c\u6258\u4eba\u5c06\u81ea\u5df1\u56de\u6765\u7684 \u6d88\u606f\u4f20\u7ed9\u5979\uff0c\u592b\u59bb\u91cd\u9022\uff0c\u604d\u5982\u9694\u4e16\u3002\u4e91\u6f84\u7684\u59bb\u5b50\u8f9e\u4e86\u9972\u9a6c\u4e4b\u804c\uff0c\u56de\u5230\u8001\u5bb6\uff0c\u4e0e\u4ed6\u540c\u4f4f\uff0c\u5979\u89c6 \u529b\u6d88\u5931\uff0c\u5df2\u7ecf\u4e0d\u80fd\u66ff\u4eba\u653e\u7f8a\u3002\u5e78\u559c\u4e91\u6f84\u6b66\u529f\u867d\u5931\uff0c\u5230\u5e95\u662f\u7ec3\u8fc7\u6b66\u7684\u4eba\uff0c\u6c14\u529b\u5c1a\u5728\uff0c\u8fd8\u53ef\u4ee5\u66ff \u4eba\u505a\u5de5\uff0c\u5c31\u8fd9\u6837\u4e08\u592b\u505a\u5de5\uff0c\u59bb\u5b50\u66ff\u4eba\u7f1d\u8863\u670d\uff0c\u52c9\u5f3a\u652f\u6491\uff0c\u5ea6\u8fc7\u8270\u82e6\u7684\u65e5\u5b50\uff0c\u4f46\u8fd9\u6837\u5df2\u6bd4\u6d41\u6d6a \u4e4b\u65f6\u597d\u5f97\u591a\u4e86\u3002\u4e91\u6f84\u767d\u5929\u5e72\u6d3b\uff0c\u665a\u4e0a\u91cd\u7ec3\u6b66\u529f\uff0c\u5fc3\u5982\u69c1\u6728\uff0c\u8fc7\u4e00\u5929\u7b97\u4e00\u5929\uff0c\u8d77\u521d\u8fd8\u60f3\u5ff5\u513f \u5973\uff0c\u8fd8\u5b58\u7740\u5e0c\u671b\uff0c\u6e10\u6e10\u8fde\u5e0c\u671b\u4e4b\u706b\u4ea6\u5df2\u7184\u706d\uff0c\u81ea\u5fd6\u6b64\u751f\u7ec8\u5f52\u8981\u65e0\u58f0\u65e0\u606f\u5730\u6b7b\u5728\u5f02\u4e61\u4e86\u3002 \u3000\u3000\u54ea\u77e5\u8fd8\u6709\u8fd9\u4e00\u5929\uff0c\u8fd8\u6709\u91cd\u89c1\u5973\u513f\u4e4b\u65e5\u3002 \u3000\u3000\u4e91\u6f84\u7684\u7a81\u7136\u51fa\u73b0\uff0c\u4e91\u857e\u771f\u662f\u505a\u68a6\u4e5f\u6ca1\u6709\u60f3\u5230\u3002\u5979\u6014\u6014\u5730\u671b\u7740\u7236\u4eb2\uff0c\u671b\u7740\u9762\u5bb9\u4e11\u964b\u3001\u8ddb\u8db3 \u82cd\u8001\u7684\u7236\u4eb2\uff0c\u201c\u5440\uff0c\u8fd8\u672a\u5230\u4e94\u5341\u5c31\u5934\u53d1\u6591\u767d\u4e86\uff01\u201d\u4ece\u7236\u4eb2\u6194\u60b4\u7684\u989c\u5bb9\uff0c\u6591\u767d\u7684\u5934\u53d1\uff0c\u8ddb\u4e86\u7684 \u8db3\u4f24\u4e86\u7684\u9762\uff0c\u4e91\u857e\u4e0d\u6d88\u4ed6\u8bf4\u4e00\u53e5\u8bdd\uff0c\u5df2\u770b\u51fa\u4e86\u4ed6\u5341\u5e74\u6765\u8f9b\u9178\u75db\u695a\u7684\u7ecf\u5386\uff0c\u6240\u53d7\u7684\u79cd\u79cd\u96be\u4ee5\u60f3 \u8c61\u7684\u6298\u78e8\u3002\u4e91\u857e\u53eb\u4e86\u4e00\u58f0\uff0c\u6251\u5230\u5979\u7236\u4eb2\u7684\u8eab\u4e0a\uff0c\u5973\u513f\u7684\u773c\u6cea\u6ef4\u5728\u7236\u4eb2\u7684\u5fc3\u4e0a\uff0c\u7236\u4eb2\u7684\u773c\u6cea\u4e5f \u6e7f\u900f\u4e86\u5973\u513f\u7684\u8863\u88f3\uff0c\u7236\u5973\u7684\u773c\u6cea\u6df7\u5728\u4e00\u8d77\u4e5f\u4e0d\u77e5\u662f\u6b22\u559c\u8fd8\u662f\u60b2\u9178\uff01 \u3000\u3000\u6b64\u666f\u6b64\u60c5\uff0c\u4efb\u662f\u5f20\u4e39\u67ab\u5982\u4f55\u6d12\u8131\uff0c\u4e5f\u4e0d\u7981\u89e6\u76ee\u51c4\u6006\uff0c\u60f3\u597d\u7684\u4e07\u8bed\u5343\u8a00\uff0c\u90fd\u8bf4\u4e0d\u51fa\u53e3\u3002\u4ed6 \u77e5\u9053\u4e91\u857e\u8fd9\u65f6\u5341\u5206\u96be\u8fc7\uff0c\u8981\u4eba\u5b89\u6170\uff0c\u4f46\u5374\u53c8\u6709\u8c01\u77e5\u9053\uff0c\u4ed6\u5fc3\u4e2d\u7684\u96be\u8fc7\uff0c\u6bd4\u4e91\u857e\u66f4\u80dc\u4e07\u5206\uff0c\u800c \u4e14\u5929\u5730\u4e4b\u95f4\uff0c\u66f4\u65e0\u4e00\u4eba\u80fd\u7ed9\u4ed6\u5b89\u6170\u3002 \u3000\u3000\u4e24\u7236\u5973\u62b1\u5934\u75db\u54ed\uff0c\u826f\u4e45\u826f\u4e45\uff0c\u773c\u6cea\u6e10\u6536\uff0c\u4e91\u6f84\u8fd9\u624d\u53d1\u89c9\u65c1\u8fb9\u8fd8\u7ad9\u7740\u4e00\u4e2a\u5c11\u5e74\uff0c\u8fd9\u5c11\u5e74\u662f \u548c\u81ea\u5df1\u7684\u5973\u513f\u540c\u6765\u7684\u3002\u4e91\u6f84\u671b\u4e86\u5f20\u4e39\u67ab\u4e00\u773c\uff0c\u53ea\u89c1\u8fd9\u5c11\u5e74\u4e00\u8eab\u534e\u670d\uff0c\u82f1\u4fca\u4e4b\u4e2d\u900f\u7740\u5112\u96c5\u4e4b \u6c14\uff0c\u4f46\u5374\u4e24\u773c\u65e0\u795e\uff0c\u5446\u82e5\u6728\u9e21\uff0c\u4e0d\u7981\u95ee\u9053\uff1a\u201c\u963f\u857e\uff0c\u4ed6\u662f\u4f55\u4eba\uff1f\u201d \u3000\u3000\u4e91\u857e\u542c\u8fd9\u4e00\u95ee\uff0c\u604d\u5982\u5728\u6076\u68a6\u4e2d\u521d\u9192\u8fc7\u6765\uff0c\u5374\u53c8\u7a81\u95fb\u60ca\u96f7\u75be\u54cd\u3002\u5979\u7236\u4eb2\u867d\u662f\u4f4e\u58f0\u8bf4\u8bdd\uff0c\u4f46 \u6bcf\u4e00\u4e2a\u5b57\u90fd\u5982\u4e00\u4e2a\u7126\u96f7\uff0c\u9739\u5728\u5979\u7684\u5fc3\u4e0a\u3002\u8bb8\u4e45\u4ee5\u6765\uff0c\u5979\u5c31\u60f3\u597d\u4e00\u756a\u8bdd\u8981\u5411\u6bcd\u4eb2\u89e3\u91ca\uff0c\u53ef\u662f\u5982 \u4eca\u89c1\u4e86\u6bcd\u4eb2\uff0c\u53c8\u610f\u5916\u5730\u89c1\u4e86\u7236\u4eb2\uff0c\u60f3\u597d\u7684\u8bdd\u8bed\uff0c\u4e5f\u50cf\u5f20\u4e39\u67ab\u4e00\u6837\u8bf4\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u4e91\u857e\u7684\u6bcd\u4eb2\u7528\u529b\u7741\u5f00\u773c\u775b\uff0c\u773c\u524d\u4f9d\u7a00\u770b\u89c1\u4e00\u4e2a\u767d\u8863\u4eba\u5f71\uff0c\u5979\u542b\u6cea\u5fae\u7b11\u9053\uff1a\u201c\u963f\u857e\uff0c\u90a3\u5c0f \u4f19\u5b50\u662f\u548c\u4f60\u540c\u6765\u7684\u5417\uff1f\u544a\u8bc9\u5988\u5988\u77e5\u9053\uff0c\u4ed6\u662f\u8c01\uff1f\u201d\u8bdd\u8bed\u8bf4\u5f97\u5341\u5206\u6e29\u67d4\uff0c\u53ef\u4ee5\u60f3\u89c1\u5979\u6bcd\u4eb2\u6b63\u662f \u671f\u5f85\u201c\u53cc\u559c\u4e34\u95e8\u201d\uff0c\u4ee5\u6b22\u8fce\u5973\u513f\u7684\u5fc3\uff0c\u6b22\u8fce\u5973\u513f\u7684\u7537\u53cb\u3002 \u3000\u3000\u5979\u54ea\u80fd\u60f3\u5230\uff0c\u8fd9\u6e29\u67d4\u7684\u8bdd\u8bed\u5374\u53d8\u6210\u4e00\u6839\u6839\u5229\u9488\uff0c\u523a\u5728\u5973\u513f\u5fc3\u4e0a\uff0c\u4e91\u857e\u5ffd\u800c\u79bb\u5f00\u4e86\u7236\u4eb2\u7684 \u6000\u62b1\uff0c\u53c8\u624b\u63a9\u9762\uff0c\u4f4e\u58f0\u5730\u8bf4\u9053\uff1a\u201c\u4ed6\u3001\u4ed6\u59d3\u5f20\uff01\u201d \u3000\u3000\u201c\u4ec0\u4e48\uff0c\u4ed6\u59d3\u5f20\uff1f\u201d\u4e91\u6f84\u4e0d\u81ea\u89c9\u5730\u558a\u4e86\u51fa\u6765\uff0c\u8fd9\u5341\u5e74\u6765\uff0c\u4ed6\u5bf9\u5f20\u5b97\u5468\u6068\u4e4b\u5165\u9aa8\uff0c\u53ea\u542c\u5230 \u4e00\u4e2a\u201c\u5f20\u201d\u5b57\uff0c\u5df2\u662f\u96be\u4ee5\u81ea\u5236\uff0c\u611f\u5230\u65e0\u9650\u618e\u6076\u3002\u4e91\u857e\u558a\u4e86\u4e00\u58f0\uff0c\u53c8\u6251\u5230\u7236\u4eb2\u8eab\u4e0a\uff0c\u53ea\u89c1\u7236\u4eb2 \u597d\u50cf\u77f3\u50cf\u4e00\u6837\u7684\u7acb\u7740\uff0c\u9762\u4e0a\u6beb\u65e0\u8868\u60c5\uff0c\u8eab\u5b50\u5fae\u5fae\u5411\u540e\u9000\u7f29\uff0c\u624b\u6307\u4e5f\u4e0d\u78b0\u5979\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u518d\u4e5f\u5fcd\u53d7\u4e0d\u4f4f\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u59d3\u5f20\uff0c\u6211\u662f\u5f20\u5b97\u5468\u7684\u513f\u5b50\uff0c\u5982\u4eca\u5411\u8001\u4f2f\u8bf7 \u7f6a\u6765\u4e86\uff01\u201d\u8fd9\u970e\u90a3\u95f4\uff0c\u53ea\u89c1\u4e91\u6f84\u9762\u4e0a\u808c\u8089\u62bd\u7f29\uff0c\u7275\u52a8\u9762\u4e0a\u7684\u4f24\u75d5\uff0c\u795e\u6c14\u66f4\u662f\u96be\u770b\uff0c\u9ed8\u4e0d\u4f5c \u58f0\uff0c\u5ffd\u7136\u50cf\u706b\u5c71\u7206\u53d1\u4e00\u6837\uff0c\u54ac\u7740\u7259\u6839\uff0c\u4e3e\u8d77\u62f3\u5934\uff0c\u4e00\u624b\u63a8\u5f00\u4e91\u857e\uff0c\u5c31\u8981\u8dd1\u4e0a\u524d\u53bb\u3002 \u3000\u3000\u4e91\u857e\u53c8\u4e0d\u7531\u81ea\u5df1\u5730\u56b7\u4e86\u4e00\u58f0\uff0c\u624b\u81c2\u4e00\u62ac\u6258\u4f4f\u4e86\u7236\u4eb2\u7684\u624b\u3002\u4e91\u6f84\u53ea\u89c9\u864e\u53e3\u53d1\u75bc\uff0c\u4e0d\u80fd\u5f80\u524d \u79fb\u52a8\u534a\u6b65\uff0c\u8fd9\u4e00\u77ac\u95f4\uff0c\u4ed6\u4ec0\u4e48\u4e5f\u660e\u767d\u4e86\uff0c\u8fd9\u5c0f\u4f19\u5b50\u662f\u81ea\u5df1\u6700\u6700\u75db\u6068\u7684\u4ec7\u4eba\u7684\u513f\u5b50\uff0c\u4e5f\u662f\u5973\u513f \u5fc3\u4e2d\u6700\u6b22\u559c\u7684\u4eba\u3002 \u3000\u3000\u4e91\u857e\u731b\u7136\u9192\u8d77\uff0c\u6562\u60c5\u662f\u81ea\u5df1\u4e0d\u77e5\u4e0d\u89c9\u4e4b\u95f4\uff0c\u7528\u529b\u7528\u5f97\u592a\u8fc7\u4e86\uff0c\u6025\u6025\u677e\u5f00\u53cc\u624b\uff0c\u8f7b\u8f7b\u5730\u62c9 \u5979\u7236\u4eb2\u7684\u8863\u8896\u3002\u53ea\u89c1\u7236\u4eb2\u53c8\u662f\u7528\u529b\u4e00\u6454\uff0c\u90a3\u7834\u70c2\u7684\u8863\u8896\u767b\u65f6\u626f\u65ad\u4e86\u4e00\u622a\uff0c\u7236\u4eb2\u76ef\u4e86\u5973\u513f\u4e00 \u773c\uff0c\u5ffd\u5730\u628a\u7834\u70c2\u7684\u5916\u8863\u4e00\u628a\u6495\u5f00\uff0c\u5411\u7740\u4e91\u857e\u515c\u5934\u4e00\u6454\uff0c\u5478\u4e86\u4e00\u53e3\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u8d70\u5427\uff0c\u6211 \u8fd9\u91cc\u7834\u6237\u7a77\u5bb6\uff0c\u4e0d\u6562\u62db\u5f85\u4f60\u4eec\u5c11\u7237\u5c0f\u59d0\uff01\u201d \u3000\u3000\u8fd9\u4e00\u77ac\u95f4\uff0c\u4e91\u857e\u6709\u5982\u89e6\u7535\u4e00\u822c\uff0c\u5168\u8eab\u9707\u6296\uff0c\u7231\u6068\u6069\u4ec7\uff0c\u7f9e\u60ed\u81ea\u759a\uff0c\u767e\u822c\u60c5\u7eea\uff0c\u500f\u7136\u4e4b \u95f4\uff0c\u90fd\u6d8c\u4e0a\u5fc3\u5934\u3002\u5979\u832b\u7136\u76f4\u7acb\uff0c\u770b\u770b\u7236\u6bcd\uff0c\u53c8\u770b\u770b\u5f20\u4e39\u67ab\uff0c\u8111\u4e2d\u7a7a\u7a7a\u6d1e\u6d1e\u7684\uff0c\u597d\u50cf\u795e\u7ecf\u5168\u90fd \u9ebb\u6728\u77e5\u89c9\u4e5f\u6d88\u5931\u4e86\u3002\u5f20\u4e39\u67ab\u9762\u8272\u60e8\u767d\uff0c\u51dd\u671b\u7740\u5979\uff0c\u53ea\u89c1\u5979\u6162\u6162\u5730\u4f38\u51fa\u624b\u6765\uff0c\u5ffd\u5730\u628a\u8eab\u4e0a\u7a7f\u7684 \u90a3\u4ef6\u7d2b\u8272\u7684\u7f57\u8863\uff0c\u7528\u529b\u4e00\u6495\uff0c\u4e5f\u6454\u5230\u5730\u4e0a\u3002\u5f20\u4e39\u67ab\u6e05\u6e05\u695a\u695a\u5730\u8bb0\u5f97\uff0c\u8fd9\u4ef6\u7d2b\u8272\u7684\u7f57\u8863\uff0c\u6b63\u662f \u4e91\u857e\u9732\u4e86\u5973\u513f\u672c\u76f8\u4e4b\u540e\uff0c\u7b2c\u4e00\u665a\u6240\u6362\u7684\u8863\u88f3\uff0c\u8bb0\u5f97\u90a3\u65f6\u548c\u5979\u5728\u53e4\u5893\u7684\u5bc6\u5ba4\u4e4b\u4e2d\uff0c\u5728\u70db\u5149\u63a9\u6620 \u4e4b\u4e0b\uff0c\u4ed6\u8fd8\u5567\u5567\u79f0\u8d5e\u8fc7\u5979\u7684\u7f8e\u4e3d\u3002\u8fd9\u4ef6\u7d2b\u7f57\u8863\u5728\u4ed6\u4eec\u4e24\u4eba\u7684\u5fc3\u5934\uff0c\u90fd\u66fe\u7ecf\u5360\u8fc7\u4e00\u4e2a\u4f4d\u7f6e\uff0c\u6709 \u4e00\u6bb5\u7f8e\u597d\u7684\u56de\u5fc6\u3002\u7136\u800c\u8fd9\u4ef6\u7d2b\u7f57\u8863\u5982\u4eca\u5df2\u88ab\u4e91\u857e\u4eb2\u624b\u6495\u6210\u788e\u7247\uff0c\u6240\u6709\u7684\u7f8e\u597d\u7684\u56de\u5fc6\uff0c\u4e5f\u597d\u50cf \u8fd9\u4ef6\u7f57\u8863\u4e00\u6837\uff0c\u88ab\u6495\u788e\u4e86\uff0c\u968f\u98ce\u800c\u901d\uff0c\u6c38\u4e0d\u590d\u56de\uff01 \u3000\u3000\u5f20\u4e39\u67ab\u53eb\u4e86\u4e00\u58f0\uff0c\u53ea\u89c1\u4e91\u857e\u5934\u4e5f\u4e0d\u62ac\uff0c\u5de6\u624b\u62d6\u7740\u7236\u4eb2\uff0c\u53f3\u624b\u62d6\u7740\u6bcd\u4eb2\uff0c\u8d70\u8fdb\u67f4\u95e8\uff0c\u63a5\u7740 \u662f\u201c\u7830\u201d\u7684\u4e00\u58f0\u67f4\u95e8\u4e5f\u5173\u4e0a\u4e86\uff0c\u4e24\u6247\u7834\u95e8\uff0c\u5c06\u4e24\u4eba\u5206\u5f00\uff0c\u95e8\u91cc\u95e8\u5916\uff0c\u5df2\u9694\u7edd\u6210\u4e24\u4e2a\u4e16\u754c\u3002\u5f20 \u4e39\u67ab\u7edd\u671b\u4e4b\u6781\uff0c\u4e91\u857e\u8d70\u8fdb\u95e8\u5185\uff0c\u5c06\u4ed6\u5173\u5728\u95e8\u5916\u4e4b\u65f6\uff0c\u7adf\u7136\u6ca1\u6709\u56de\u5934\u671b\u4ed6\u4e00\u773c\uff01 \u3000\u3000\u4e91\u857e\u8d70\u8fdb\u5c4b\u5185\uff0c\u6c14\u529b\u5168\u90fd\u6d88\u5931\uff0c\u4ece\u95e8\u5916\u8e0f\u8fdb\u95e8\u5185\uff0c\u53ea\u4e0d\u8fc7\u662f\u4ec5\u4ec5\u7684\u4e00\u6b65\u8ddd\u79bb\uff0c\u7136\u800c\u8de8\u8fc7 \u8fd9\u4e00\u6b65\uff0c\u5374\u6bd4\u8d70\u8fc7\u4e07\u6c34\u5343\u5c71\u8fd8\u8981\u56f0\u96be\uff0c\u4e91\u857e\u51e0\u4e4e\u662f\u7aed\u5c3d\u5e73\u751f\u7684\u6c14\u529b\uff0c\u624d\u8de8\u8fc7\u4e86\u8fd9\u4e00\u6b65\u3002\u8e0f\u8fdb \u95e8\u5185\uff0c\u5979\u518d\u4e5f\u652f\u6301\u4e0d\u4e86\u81ea\u5df1\uff0c\u9893\u7136\u5012\u5728\u5730\u4e0a\u3002\u53ea\u542c\u5f97\u95e8\u5916\u9a6c\u5636\uff0c\u60b2\u51c9\u4e4b\u6781\uff0c\u8fd9\u662f\u4e91\u857e\u90a3\u5339\u5b9d \u9a6c\u7684\u53eb\u58f0\uff0c\u542c\u8fd9\u53eb\u58f0\uff0c\u4f3c\u4e4e\u5b83\u4e5f\u6b63\u5728\u604b\u604b\u4e0d\u820d\u5730\u79bb\u5f00\u5b83\u7684\u597d\u53cb\uff0c\u4ece\u4e2d\u539f\u8d70\u5230\u8499\u8fb9\uff0c\u4e07\u91cc\u540c \u884c\uff0c\u8fd9\u4e24\u5339\u9a6c\u4e5f\u597d\u50cf\u7ed3\u6210\u4e0d\u53ef\u5206\u5f00\u7684\u597d\u53cb\u4e86\u3002\u4e91\u857e\u7684\u9a6c\u5728\u60b2\u9e23\uff0c\u8fdc\u5904\u5f20\u4e39\u67ab\u7684\u90a3\u5339\u5b9d\u9a6c\u5728\u60b2 \u9e23\uff0c\u201c\u9a6c\u9e23\u98ce\u8427\u8427\u201d\uff0c\u98ce\u58f0\u4f20\u9001\u9a6c\u9e23\u4e4b\u58f0\uff0c\u66f4\u597d\u50cf\u4e24\u4e2a\u597d\u670b\u53cb\u5728\u751f\u79bb\u6b7b\u522b\u4e4b\u65f6\uff0c\u60b2\u6b4c\u916c\u7b54\u3002 \u9a6c\u72b9\u5982\u6b64\u4eba\u4f55\u4ee5\u582a\uff1f\u4e91\u857e\u5728\u95e8\u5185\u60e8\u53eb\u4e00\u58f0\uff0c\u6655\u5012\u5730\u4e0a\uff0c\u8033\u8fb9\u9690\u7ea6\u542c\u5f97\u6bcd\u4eb2\u53eb\u9053\uff1a\u201c\u5440\uff0c\u597d\u53ef \u601c\u7684\u5b69\u5b50\uff01\u201d \u3000\u3000\u4f46\u8fd8\u6709\u4eba\u6bd4\u4e91\u857e\u66f4\u8981\u53ef\u601c\uff0c\u90a3\u662f\u5f20\u4e39\u67ab\u3002\u4e91\u857e\u6b64\u9645\uff0c\u5c1a\u6709\u7236\u6bcd\u5728\u8eab\u65c1\u629a\u6170\u7740\u5979\uff0c\u53ef\u662f\u5f20 \u4e39\u67ab\u7684\u6ee1\u6000\u51c4\u695a\uff0c\u5374\u8fde\u627e\u4e00\u4e2a\u4eba\u8bc9\u8bf4\u4e5f\u4e0d\u80fd\u591f\u3002\u4ed6\u7edd\u671b\u5230\u4e86\u6781\u70b9\u3002\u5982\u75f4\u5982\u72c2\uff0c\u5929\u5730\u832b\u832b\uff0c\u5b64 \u8eab\u53ea\u5f71\uff0c\u7adf\u4e0d\u77e5\u8be5\u8d70\u5230\u4f55\u5904\uff1f \u3000\u3000\u4ed6\u4fe1\u9a6c\u6240\u4e4b\uff0c\u53ea\u89c1\u5510\u53e4\u62c9\u5c71\u9ad8\u8038\u4e91\u9704\uff0c\u4ed6\u4f9d\u7a00\u8bb0\u5f97\uff0c\u81ea\u5df1\u7684\u5e08\u7236\u66fe\u7ea6\u8fc7\u4ed6\u5728\u5317\u9ad8\u5cf0\u76f8 \u4f1a\uff0c\u597d\u50cf\u662f\u8981\u53bb\u62dc\u4f1a\u4ec0\u4e48\u9b54\u5934\u3002\u5f20\u4e39\u67ab\u672c\u6765\u662f\u806a\u660e\u7edd\u9876\uff0c\u8bb0\u6027\u8fc7\u4eba\uff0c\u7136\u800c\u5fc3\u7075\u4e0a\u7684\u91cd\u521b\uff0c\u7adf \u4f7f\u4ed6\u9677\u5165\u4e86\u534a\u75af\u72c2\u7684\u72b6\u6001\uff0c\u9664\u4e86\u4e91\u857e\u548c\u5979\u7684\u4e8b\u60c5\u4e4b\u5916\uff0c\u5176\u4ed6\u7684\u4e8b\u60c5\u90fd\u53ea\u80fd\u8bb0\u5f97\u4e00\u9cde\u534a\u722a\uff0c\u8fde \u90a3\u8001\u9b54\u5934\u662f\u8c01\uff0c\u5e08\u7236\u4e3a\u4f55\u8981\u53bb\u62dc\u4f1a\u4ed6\u4ed6\u90fd\u8bb0\u4e0d\u8d77\u6765\u4e86\u3002\u8fd8\u5e78\u4ed6\u5c1a\u8bb0\u5f97\u6709\u4e00\u4e2a\u5e08\u7236\uff0c\u4ed6\u5fc3\u5934\u7684 \u90c1\u79ef\uff0c\u6b63\u8981\u627e\u4e00\u4e2a\u4eba\u503e\u5410\uff0c\u4e8e\u662f\u4ed6\u6cbf\u7740\u5510\u53e4\u62c9\u5c71\u7b56\u89c2\u800c\u884c\uff0c\u8d70\u4e86\u4e24\u5929\u628a\u9a6c\u653e\u5728\u5c71\u4e0b\uff0c\u8ba9\u5b83\u81ea \u884c\u89c5\u98df\uff0c\u81ea\u5df1\u5355\u72ec\u767b\u5c71\u3002 \u3000\u3000\u5c71\u9ad8\u5165\u4e91\uff0c\u6773\u4e0d\u89c1\u4eba\uff0c\u5f20\u4e39\u67ab\u8d8a\u8d70\u8d8a\u89c9\u5f97\u5b64\u5bc2\uff0c\u8d8a\u8d70\u8d8a\u6000\u5ff5\u548c\u4e91\u857e\u5e76\u9a6c\u540c\u884c\u7684\u60c5\u666f\u3002\u4ed6 \u548c\u4e91\u857e\u66fe\u5728\u6625\u6696\u82b1\u5f00\u4e4b\u65e5\uff0c\u8e0f\u904d\u5c71\u6e29\u6c34\u6696\u7684\u6c5f\u5357\uff0c\u4e5f\u66fe\u5728\u6714\u98ce\u6012\u53f7\u7684\u65e5\u5b50\uff0c\u7a7f\u8fc7\u98ce\u6c99\u6f20\u6f20\u7684 \u5317\u65b9\u539f\u91ce\uff0c\u7136\u800c\u4e0d\u8bba\u662f\u5c71\u6e29\u6c34\u6696\u7684\u6c5f\u5357\u6216\u662f\u98ce\u6c99\u6f20\u6f20\u7684\u585e\u5317\uff0c\u73b0\u5728\u56de\u60f3\u8d77\u6765\uff0c\u90fd\u662f\u7f8e\u5230\u6781 \u70b9\uff0c\u751c\u5230\u6781\u70b9\u3002\u4ed6\u597d\u51e0\u6b21\u5728\u6c89\u601d\u4e4b\u9645\u8fd8\u4ee5\u4e3a\u4e91\u857e\u5c1a\u5728\u8eab\u8fb9\uff0c\u9ad8\u58f0\u5730\u53eb\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u5c0f\u5144 \u5f1f\uff01\u201d\u53ef\u662f\u8352\u5c71\u6df1\u8c37\u4e4b\u4e2d\uff0c\u53ea\u542c\u5230\u81ea\u5df1\u7684\u56de\u58f0\uff0c\u201c\u5c0f\u5144\u5f1f\u201d\u518d\u4e5f\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u5c31\u8fd9\u6837\u5982\u75f4\u5982\u72c2\u5730\u72ec\u81ea\u8d70\u4e0a\u5510\u53e4\u62c9\u5c71\uff0c\u7b2c\u4e00\u65e5\u8fd8\u6709\u70b9\u6e05\u9192\uff0c\u8bb0\u5f97\u81ea\u5df1\u6b64\u6765\u662f\u8981\u627e \u5e08\u7236\uff0c\u7b2c\u4e8c\u65e5\u5c31\u8ff7\u8ff7\u7cca\u7cca\uff0c\u4e0d\u77e5\u9053\u81ea\u5df1\u4e3a\u4ec0\u4e48\u4f1a\u5355\u72ec\u5728\u8fd9\u8352\u5c71\u4e4b\u4e2d\u3002\u89c1\u7740\u5c71\u82b1\u67af\u6811\u602a\u77f3\u5947 \u5cf0\uff0c\u773c\u524d\u90fd\u5e7b\u51fa\u4e91\u857e\u7684\u5f62\u8c61\uff0c\u542c\u5230\u6d41\u6cc9\u5c71\u6da7\u7684\u58f0\u97f3\uff0c\u4e5f\u597d\u50cf\u4e91\u857e\u5728\u547c\u5524\u4ed6\uff0c\u7136\u800c\u8fd9\u201c\u547c\u5524\u201d \u4e4b\u58f0\u500f\u5ffd\u53c8\u53d8\u6210\u4e86\u90a3\u201c\u7830\u201d\u7684\u4e00\u58f0\u5173\u95e8\u7684\u58f0\u97f3\uff0c\u5f20\u4e39\u67ab\u6c38\u8fdc\u5fd8\u4e0d\u6389\u8fd9\u4e2a\u58f0\u97f3\u3002\u8fd9\u58f0\u97f3\u5728\u8ffd\u9010 \u7740\u4ed6\uff0c\u4ed6\u4e0d\u6562\u4e0b\u5c71\uff0c\u832b\u65e0\u76ee\u7684\u5730\u5411\u5c71\u4e0a\u8dd1\uff0c\u597d\u50cf\u8fd9\u6837\u5c31\u53ef\u4ee5\u8eb2\u5f00\u90a3\u4e2a\u58f0\u97f3\uff0c\u907f\u5f00\u90a3\u4e2a\u4ee4\u4eba\u538c \u70e6\u7684\u5c71\u4e0b\u7684\u4e16\u754c\u3002 \u3000\u3000\u7b2c\u4e8c\u65e5\u508d\u665a\uff0c\u4ed6\u8d70\u5230\u4e86\u5c71\u9876\uff0c\u505c\u4e0b\u8db3\u6765\uff0c\u5ffd\u89c9\u8179\u4e2d\u9965\u6e34\uff0c\u8fd9\u624d\u8bb0\u5f97\u81ea\u5df1\u968f\u8eab\u643a\u5e26\u7684\u5e72\u7cae \u5728\u767b\u5c71\u7684\u7b2c\u4e00\u65e5\u5df2\u7ecf\u5403\u5b8c\uff0c\u8fd9\u4e00\u5929\u7adf\u7136\u6ca1\u6709\u5403\u8fc7\u534a\u70b9\u4e1c\u897f\uff0c\u9965\u997f\u4f7f\u4ed6\u7a0d\u7a0d\u6e05\u9192\uff0c\u60f3\u8d77\u81ea\u5df1\u8be5 \u53bb\u627e\u70b9\u5403\u7684\u4e1c\u897f\uff0c\u62ac\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u5c71\u4e0a\u4e00\u95f4\u77f3\u5c4b\u9690\u9690\u5192\u51fa\u708a\u70df\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u54ea\u91cc\u77e5\u9053\u8fd9\u6b63\u662f\u81ea\u5df1\u5e08\u95e8\u7684\u5927\u5bf9\u5934\uff0c\u4e0a\u5b98\u5929\u91ce\u6240\u5c45\u7684\u77f3\u5ba4\u3002\u8fd9\u65f6\u4ed6\u53ea\u77e5\u9053\u8981\u627e\u5403 \u7684\u4e1c\u897f\uff0c\u4ed6\u8dd1\u53bb\u63a8\u95e8\uff0c\u90a3\u4e24\u6247\u77f3\u95e8\u5173\u5f97\u7d27\u7d27\u7684\u63a8\u5b83\u4e0d\u52a8\uff0c\u8fd9\u4e24\u6247\u77f3\u95e8\u5728\u4ed6\u773c\u4e2d\u500f\u53c8\u5e7b\u6210\u4e91\u857e \u5bb6\u7684\u90a3\u4e24\u6247\u7834\u95e8\uff0c\u201c\u55ef\uff0c\u6211\u8981\u8d70\u8fdb\u95e8\u5185\uff01\u201d\u95e8\u5185\u597d\u50cf\u4fbf\u6709\u4e91\u857e\uff0c\u4ed6\u4e5f\u4e0d\u77e5\u662f\u4ece\u54ea\u513f\u6765\u7684\u6c14 \u529b\uff0c\u731b\u5730\u8fd0\u7528\u91d1\u521a\u5927\u529b\u624b\u7684\u529f\u592b\uff0c\u5728\u77f3\u95e8\u4e0a\u91cd\u91cd\u5730\u51fb\u4e86\u4e24\u638c\uff0c\u90a3\u77f3\u95e8\u7adf\u7136\u7ed9\u4ed6\u7684\u91d1\u521a\u638c\u529b\u9707 \u5f00\u4e86\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u95e8\u5185\u4e00\u58f0\u602a\u7b11\uff1a\u201c\u4ec0\u4e48\u4eba\u8fd9\u6837\u5927\u80c6\uff0c\u6562\u6bc1\u574f\u6211\u7684\u95e8\u6237\uff01\u201d\u9694\u7740\u77f3\u95e8\uff0c\u90a3\u7b11\u58f0\u5374\u50cf \u5229\u5203\u4e00\u822c\u523a\u8fdb\u4ed6\u7684\u8033\u9f13\uff0c\u5f20\u4e39\u67ab\u51db\u7136\u4e00\u60ca\uff0c\u8fd9\u53ef\u6016\u7684\u7b11\u58f0\u548c\u4e91\u857e\u7684\u7b11\u58f0\u7b80\u76f4\u6709\u5982\u591c\u83ba\u4e4b\u4e8e\u67ad \u9e1f\u201c\u8fd9\u91cc\u9762\u6ca1\u6709\u4e91\u857e\uff0c\u5440\uff0c\u6211\u6765\u5230\u8fd9\u91cc\u662f\u505a\u4ec0\u4e48\u5462\uff1f\u201d\u8fd9\u970e\u65f6\u95f4\u5f20\u4e39\u67ab\u7684\u795e\u5fd7\u53c8\u8f6c\u6a21\u7cca\uff0c\u9965 \u997f\u4ea6\u5df2\u5fd8\u5374\u3002\u500f\u5ffd\u4e4b\u95f4\uff0c\u5ffd\u89c1\u51e0\u6761\u9ed1\u5f71\u5411\u81ea\u5df1\u5954\u6765\uff0c\u5f20\u4e39\u67ab\u672c\u80fd\u5730\u8fd0\u7528\u6b66\u529f\u76f8\u6297\uff0c\u4f38\u6307\u51fa \u638c\uff0c\u7adf\u5728\u9ed1\u6f06\u6f06\u7684\u77f3\u5ba4\u4e2d\u65bd\u7528\u4e0a\u4e58\u7684\u70b9\u7a74\u529f\u592b\uff0c\u53ea\u542c\u5f97\u201c\u5495\u549a\u5495\u549a\u201d\u51e0\u58f0\u75be\u54cd\uff0c\u90a3\u51e0\u6761\u9ed1\u5f71 \u90fd\u6251\u5012\u5730\u4e0a\u3002\u5c31\u5728\u6b64\uff0c\u53ea\u89c1\u91cc\u9762\u7684\u4e00\u95f4\u5bc6\u5ba4\u77f3\u95e8\u4e00\u5f00\uff0c\u4e00\u6761\u9ed1\u5f71\u73b0\u51fa\u8eab\u6765\uff0c\u4eba\u8fd8\u672a\u5230\uff0c\u52b2\u98ce \u5148\u5230\uff0c\u5f20\u4e39\u67ab\u5ffd\u611f\u5730\u8f6c\u5929\u65cb\uff0c\u4e00\u4ea4\u8dcc\u5012\uff0c\u4eba\u4e8b\u4e0d\u77e5\u3002 \u3000\u3000\u8fd9\u51e0\u4e2a\u88ab\u4ed6\u70b9\u5012\u7684\u4eba\u662f\u4e0a\u5b98\u5929\u91ce\u7684\u4f8d\u8005\uff0c\u4e0a\u5b98\u5929\u91ce\u8fd9\u65f6\u6b63\u4ece\u5bc6\u5ba4\u4e4b\u4e2d\u8d70\u4e86\u51fa\u6765\u3002 \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u6b66\u529f\u76d6\u4e16\uff0c\u4e14\u6709\u201c\u9b54\u5934\u201d\u4e4b\u53f7\uff0c\u51e0\u5341\u5e74\u6765\uff0c\u9690\u5c45\u6b64\u5c71\uff0c\u6b66\u6797\u9ad8\u624b\uff0c\u4e0d\u6562\u4ece\u4ed6\u5c45 \u5904\u7684\u9644\u8fd1\u7ecf\u8fc7\uff0c\u5374\u4e0d\u6599\u88ab\u5f20\u4e39\u67ab\u9707\u584c\u4e86\u4ed6\u7684\u77f3\u95e8\u3002\u4e0a\u5b98\u5929\u91ce\u521d\u65f6\u8fd8\u4ee5\u4e3a\u662f\u7384\u673a\u9038\u58eb\uff0c\u4f46\u8f6c\u5ff5 \u4e00\u60f3\uff0c\u4ee5\u7384\u673a\u9038\u58eb\u7684\u8eab\u4efd\uff0c\u7edd\u4e0d\u4f1a\u8fd9\u6837\u65e0\u793c\uff0c\u5fc3\u4e2d\u6781\u662f\u5947\u602a\uff0c\u5230\u4ed6\u9065\u7528\u201c\u4e00\u6307\u7985\u201d\u7684\u529f\u592b\uff0c \u70b9\u5012\u4e86\u5f20\u4e39\u67ab\u4e4b\u540e\uff0c\u4fbf\u6025\u6025\u70b9\u71c3\u706f\u706b\uff0c\u8981\u770b\u8fd9\u4e2a\u80c6\u6562\u5728\u592a\u5c81\u5934\u4e0a\u52a8\u571f\u7684\u7a76\u662f\u4f55\u4eba\uff1f \u3000\u3000\u8fd9\u4e00\u770b\u66f4\u4ee4\u4e0a\u5b98\u5929\u91ce\u60ca\u8be7\uff0c\u53ea\u89c1\u5012\u5728\u5730\u4e0a\u7684\u7adf\u662f\u4e00\u4e2a\u9762\u5982\u51a0\u7389\u7684\u7f8e\u5c11\u5e74\uff0c\u53ea\u662f\u5f62\u5bb9\u6194 \u60b4\uff0c\u4f3c\u75c5\u975e\u75c5\uff0c\u770b\u6837\u5b50\u4e0d\u8fc7\u4e8c\u5341\u6765\u5c81\u7684\u6837\u5b50\u3002\u4e0a\u5b98\u5929\u91ce\u6240\u5b66\u751a\u5e7f\uff0c\u533b\u535c\u661f\u76f8\uff0c\u65e0\u6240\u4e0d\u80fd\uff0c\u4e00 \u89c1\u60c5\u72b6\uff0c\u4fbf\u77e5\u5176\u4e2d\u5b9a\u6709\u8e4a\u8df7\uff0c\u8bd5\u66ff\u5f20\u4e39\u67ab\u628a\u8109\uff0c\u4e00\u628a\u4e4b\u4e0b\uff0c\u5177\u6709\u7edd\u4e16\u6b66\u529f\u7684\u4e0a\u5b98\u5929\u91ce\uff0c\u4e5f\u4e0d \u7981\u5927\u4e3a\u5947\u602a\u3002 \u3000\u3000\u8981\u77e5\u4ed6\u7684\u4e00\u6307\u7985\u529f\uff0c\u5df2\u7ec3\u81f3\u51fa\u795e\u5165\u5316\u4e4b\u5883\uff0c\u6240\u70b9\u8005\u53c8\u662f\u5f20\u4e39\u67ab\u80c1\u4e0b\u7684\u8f6f\u9ebb\u7a74\uff0c\u6309\u7406\u6765 \u8bf4\uff0c\u9644\u8fd1\u7684\u8840\u6d41\u53d7\u5230\u963b\u6ede\uff0c\u8109\u640f\u5fc5\u7136\u8fdf\u7f13\u3002\u4f46\u5f20\u4e39\u67ab\u7684\u8109\u8c61\u5374\u662f\u5982\u5e38\uff0c\u53ea\u662f\u5fae\u73b0\u51fa\u865a\u5f31\u7684\u8ff9 \u8c61\uff0c\u6df1\u901a\u533b\u7406\u8005\u4e00\u63a2\u4fbf\u77e5\u8fd9\u4e43\u662f\u56e0\u9965\u997f\u6240\u81f4\uff0c\u800c\u5e76\u975e\u662f\u53d7\u4e86\u70b9\u7a74\u7684\u5f71\u54cd\u3002 \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u82e5\u7136\u662f\u7edd\u9876\u7684\u9ad8\u624b\uff0c\u50cf\u7384\u673a\u9038\u58eb\u8fd9\u6837\u7684\u4eba\uff0c\u8fd8\u53ef\u4ee5\u7528\u95ed\u7a74\u6cd5\u6765\u9632 \u5fa1\u6211\u7684\u4e00\u6307\u7985\u529f\uff0c\u4f46\u82e5\u7528\u95ed\u7a74\u6cd5\u867d\u88ab\u70b9\u4e2d\uff0c\u4ea6\u4e0d\u81f3\u4e8e\u6655\u53a5\uff0c\u800c\u4e14\u5728\u8109\u8c61\u4e2d\u4ea6\u6ca1\u6709\u95ed\u7a74\u7684\u8ff9 \u8c61\u3002\u6b64\u4eba\u65e2\u88ab\u70b9\u5012\uff0c\u5374\u53c8\u5e76\u65e0\u4f24\u635f\uff0c\u4e0d\u77e5\u662f\u4f55\u7f18\u6545\uff1f\u96be\u9053\u4e16\u4e0a\u8fd8\u6709\u53e6\u4e00\u79cd\u6211\u6240\u4e0d\u77e5\u6653\u7684\u795e\u5947 \u7684\u5185\u529f\u4e48\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u5f53\u771f\u6ca1\u6709\u6599\u5230\uff0c\u4e16\u4e0a\u679c\u7136\u8fd8\u6709\u4e00\u79cd\u4ed6\u6240\u4e0d\u77e5\u6653\u7684\u795e\u5947\u5185\u529f\uff0c\u90a3\u5c31\u662f\u5f6d\u548c\u5c1a\u6240\u7740 \u7684\u300a\u7384\u529f\u8981\u8bc0\u300b\u91cc\u6240\u8f7d\u7684\u529f\u592b\u3002\u4e0a\u5b98\u5929\u91ce\u6240\u4e60\u7684\u5185\u529f\uff0c\u8d70\u7684\u4e43\u662f\u602a\u5f02\u7684\u4e00\u8def\uff0c\u5389\u5bb3\u662f\u5389\u5bb3\u5230 \u4e86\u6781\u70b9\uff0c\u4f46\u5374\u8fdc\u8fdc\u4e0d\u53ca\u5f6d\u548c\u5c1a\u7684\u201c\u7384\u529f\u201d\u6765\u5f97\u7eaf\u6b63\u3002\u6545\u6b64\u5f20\u4e39\u67ab\u529f\u529b\u867d\u5c1a\u8fdc\u8fdc\u4e0d\u53ca\u4e0a\u5b98\u5929 \u91ce\uff0c\u4f46\u88ab\u4ed6\u7684\u4e00\u6307\u7985\u529f\u9065\u9065\u70b9\u4e2d\u7a74\u9053\u4e4b\u65f6\uff0c\u5374\u81ea\u7136\u80fd\u8fd0\u529f\u4e0e\u4e4b\u76f8\u6297\uff0c\u6240\u4ee5\u867d\u7136\u6655\u53a5\uff0c\u5374\u65e0\u4f24 \u635f\u3002 \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u53c8\u60f3\u9053\uff1a\u201c\u8fd9\u5c11\u5e74\u5e74\u7eaa\u9752\u9752\uff0c\u53c8\u5728\u9965\u997f\u4e4b\u4e2d\uff0c\u5c45\u7136\u80fd\u5728\u4e3e\u624b\u6295\u8db3\u4e4b\u95f4\uff0c\u4fbf\u5c06\u6211 \u7684\u56db\u4e2a\u4f8d\u8005\u4e00\u9f50\u5236\u670d\uff0c\u8fd9\u7b49\u672c\u4e8b\u975e\u6709\u4e8c\u4e09\u5341\u5e74\u7684\u529f\u529b\uff0c\u7edd\u96be\u505a\u5230\uff0c\u96be\u9053\u4ed6\u662f\u5728\u5a18\u80ce\u91cc\u4fbf\u7ec3\u6b66 \u529f\u7684\u4e48\uff1f\u201d\u731b\u5730\u5fc3\u4e2d\u4e00\u60ca\uff1a\u83ab\u975e\u4ed6\u662f\u5927\u5bf9\u5934\u7384\u673a\u9038\u58eb\u7684\u5f1f\u5b50\uff1f\u4f46\u8f6c\u5ff5\u4e00\u60f3\uff0c\u5373\u7b97\u662f\u7384\u673a\u9038\u58eb \u7684\u5f1f\u5b50\uff0c\u5e74\u7eaa\u9752\u9752\uff0c\u4ea6\u4e0d\u5e94\u5177\u6709\u5982\u6b64\u6b66\u529f\uff0c\u800c\u4e14\u4ed6\u5e94\u4ed8\u201c\u4e00\u6307\u7985\u201d\u7684\u529f\u592b\uff0c\u4e5f\u4e0d\u50cf\u7384\u673a\u9038\u58eb \u8fd9\u4e00\u8def\u7684\u529f\u592b\u3002 \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u767e\u601d\u4e0d\u5f97\u5176\u89e3\uff0c\u4ed6\u867d\u6709\u201c\u9b54\u5934\u201d\u4e4b\u53f7\uff0c\u5374\u4ea6\u6709\u90a3\u201c\u601c\u624d\u201d\u4e4b\u5ff5\uff0c\u5f53\u4e0b\u5c06\u5f20\u4e39\u67ab \u70b9\u9192\u3002\u5f20\u4e39\u67ab\u8ff7\u8ff7\u7cca\u7cca\uff0c\u773c\u775b\u4e5f\u4e0d\u7741\u5f00\uff0c\u7adf\u4e0d\u77e5\u81ea\u5df1\u66fe\u505a\u8fc7\u4f55\u4e8b\uff0c\u4e00\u6709\u77e5\u89c9\uff0c\u4fbf\u56b7\u9053\uff1a\u201c\u5c0f \u5144\u5f1f\uff0c\u5c0f\u5144\u5f1f\u3002\u201d\u4e0a\u5b98\u5929\u91ce\u5012\u4e86\u4e00\u7897\u8336\u653e\u5728\u4ed6\u7684\u53e3\u8fb9\uff0c\u53ea\u542c\u5f97\u5f20\u4e39\u67ab\u53c8\u56b7\u9053\uff1a\u201c\u5440\uff0c\u5440\uff0c\u5c0f \u5144\u5f1f\uff0c\u4f60\u4e0d\u6b22\u559c\u9a6c\u5976\u9152\uff0c\u6211\u4e5f\u4e0d\u559d\u8fd9\u9a6c\u5976\u9152\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u5fc3\u9053\uff1a\u201c\u8fd9\u4eba\u795e\u601d\u7eb7\u4e71\uff0c\u602a\u4e0d\u5f97\u5728\u8109\u8c61\u4e4b\u4e2d\uff0c\u6709\u5fc3\u706b\u90c1\u7ed3\u4e4b\u8c61\u3002\u201d\u9053\uff1a\u201c\u597d\uff0c \u4f60\u4e0d\u8981\u9a6c\u5976\u9152\uff0c\u7528\u9178\u8461\u8404\u9152\u6765\u9001\u4e73\u916a\u5427\u3002\u201d\u53e6\u5916\u53d6\u8fc7\u4e00\u5976\u916a\uff0c\u4ecd\u5c06\u90a3\u7897\u9999\u8336\u79fb\u5f00\u4e86\u53c8\u518d\u62ff\u56de \u7ed9\u4ed6\u3002\u5f20\u4e39\u67ab\u8ff7\u8ff7\u7cca\u7cca\uff0c\u5c06\u5976\u916a\u548c\u9999\u8336\u90fd\u4e00\u9f50\u559d\u4e86\uff0c\u53eb\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u5c0f\u5144\u5f1f\uff0c\u8fd9\u624d\u662f\u6211\u7684 \u597d\u5144\u5f1f\uff0c\u6211\u8e0f\u8fdb\u95e8\u6765\uff0c\u4f60\u4e0d\u518d\u8d76\u6211\u4e86\uff1f\u54c8\u54c8\uff0c\u4f60\u4e0d\u518d\u8d76\u6211\u4e86\uff01\u201d\u84e6\u5730\u5411\u957f\u6905\u4e00\u5012\uff0c\u547c\u547c\u719f \u7761\uff0c\u4ed6\u5b63\u5b9e\u662f\u592a\u75b2\u5026\u4e86\u3002 \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u4e0d\u77e5\u600e\u7684\uff0c\u53ea\u89c9\u8fd9\u5c11\u5e74\u4e0e\u81ea\u5df1\u751a\u662f\u6295\u7f18\uff0c\u60f3\u9053\uff1a\u201c\u6211\u8fd9\u7897\u9999\u8336\u5185\u6709\u6b64\u5c71\u7279\u4ea7\u7684 \u96ea\u53c2\uff0c\u53ef\u4ee5\u517b\u6c14\u6d3b\u8840\uff0c\u52a0\u4e0a\u8fd9\u7897\u4e73\u916a\uff0c\u4ed6\u4fbf\u518d\u7761\u4e00\u5929\u4e0d\u5403\u4e1c\u897f\u4e5f\u662f\u65e0\u59a8\u3002\u201d\u5f53\u4e0b\u5c06\u5f20\u4e39\u67ab\u62b1 \u56de\u81ea\u5df1\u7684\u4e66\u623f\uff0c\u4fbf\u8ba9\u4ed6\u5728\u81ea\u5df1\u5e73\u65f6\u7761\u5348\u89c9\u7684\u6e29\u7389\u69bb\u4e0a\u5b89\u6b47\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u4e00\u89c9\u9192\u6765\uff0c\u5df2\u662f\u7b2c\u4e8c\u65e5\u7684\u4e0b\u5348\uff0c\u53ea\u89c9\u9690\u9690\u5e7d\u9999\uff0c\u6c81\u4eba\u5fc3\u813e\uff0c\u7741\u773c\u4e00\u770b\uff0c\u53ea\u89c1\u9633\u5149 \u900f\u8fc7\u7a97\u6237\uff0c\u7a97\u53e3\u4f9b\u7740\u4e00\u76c6\u829d\u5170\uff0c\u7a97\u6237\u4e24\u8fb9\u6302\u7740\u4e00\u526f\u5bf9\u8054\uff0c\u8054\u9053\uff1a\u201c\u96be\u5fd8\u6069\u6028\u96be\u5fd8\u4f60\uff0c\u53ea\u4e3a\u60c5 \u75f4\u53ea\u4e3a\u771f\u3002\u201d\u623f\u4e2d\u5e03\u7f6e\u7cbe\u96c5\uff0c\u58c1\u4e0a\u8fd8\u6709\u4e00\u5e45\u4e66\u56fe\uff0c\u753b\u4e2d\u4e00\u7247\u7d2b\u7af9\u6797\uff0c\u6797\u4e2d\u4e00\u4e2a\u7d2b\u8863\u5c11\u5973\uff0c\u957f \u7709\u5165\u9b13\uff0c\u4f3c\u559c\u4f3c\u55d4\u3002\u5f20\u4e39\u67ab\u5fc3\u4e2d\u4e00\u6014\uff1a\u753b\u4e2d\u7684\u666f\u8c61\uff0c\u597d\u50cf\u5728\u54ea\u513f\u89c1\u8fc7\u4e00\u822c\uff0c\u8fde\u753b\u4e2d\u7684\u5c11\u5973\uff0c \u90a3\u8eab\u6750\u4f53\u6001\uff0c\u4e5f\u8c61\u548c\u81ea\u5df1\u6709\u4e00\u9762\u4e4b\u7f18\u3002\u5f20\u4e39\u67ab\u91cd\u8bfb\u8054\u8bed\uff1a\u201c\u96be\u5fd8\u6069\u6028\u96be\u5fd8\u4f60\uff0c\u53ea\u4e3a\u60c5\u75f4\u53ea\u4e3a \u771f\u3002\u201d\u5982\u9189\u5982\u75f4\uff0c\u53ea\u89c9\u4e91\u857e\u7684\u5f71\u5b50\u5728\u773c\u524d\u6d6e\u6643\uff0c\u7d2b\u7af9\u6797\u4e2d\u7684\u5c11\u5973\u7a81\u5730\u5316\u6210\u4e86\u4e91\u857e\uff0c\u597d\u50cf\u8981\u4ece \u753b\u56fe\u4e2d\u8df3\u51fa\u6765\uff0c\u8f6c\u773c\u4e4b\u95f4\u53c8\u6d88\u5931\u4e86\u3002\u5f20\u4e39\u67ab\u81ea\u8a00\u81ea\u7b11\u9053\uff1a\u201c\u5929\u5730\u4e4b\u95f4\u54ea\u8fd8\u6709\u4eba\u6bd4\u5f97\u4e0a\u6211\u7684\u5c0f \u5144\u5f1f\uff0c\u753b\u4e2d\u5c11\u5973\u867d\u7f8e\u4e5f\u96be\u53ca\u5979\u4e07\u4e00\u3002\u201d\u4e0d\u77e5\u4e0d\u89c9\u62ff\u8d77\u4e66\u6848\u7684\u7eb8\u7b14\uff0c\u753b\u4e86\u4e00\u5f20\u53c8\u4e00\u753b\uff0c\u753b\u7684\u90fd \u662f\u4e91\u857e\u7684\u8096\u50cf\uff0c\u6709\u542b\u7f9e\u7684\u4e91\u857e\uff0c\u6709\u5e26\u7b11\u7684\u4e91\u857e\uff0c\u6709\u8584\u6012\u7684\u4e91\u857e\uff0c\u6709\u4f6f\u55d4\u7684\u4e91\u857e\uff0c\u6709\u60f9\u601c\u7684\u4e91 \u857e\uff0c\u79cd\u79cd\u795e\u60c5\uff0c\u79cd\u79cd\u4f53\u6001\uff0c\u4e00\u4e00\u63cf\u7ed8\u5728\u7eb8\u4e0a\uff0c\u5174\u72b9\u672a\u5df2\uff0c\u53c8\u753b\u4e86\u4e00\u5e45\u5979\u548c\u81ea\u5df1\u5e76\u9a6c\u5954\u9a70\u7684\u56fe \u753b\uff0c\u9898\u4e0a\u4e00\u9996\u5c0f\u8bcd\u9053\uff1a\u201c\u63a0\u6c34\u60ca\u9e3f\uff0c\u5bfb\u5de2\u4e73\u71d5\uff0c\u4e91\u5c71\u8bb0\u5f97\u66fe\u76f8\u89c1\uff0c\u53ef\u601c\u8e0f\u5c3d\u53bb\u6765\u679d\uff0c\u5bd2\u6797\u6f20 \u6f20\u65e0\u7531\u9762\u3002\u4eba\u9694\u5929\u6cb3\uff0c\u58f0\u7591\u7981\u9662\uff0c\u5fc3\u9b42\u6f2b\u9010\u79cb\u9b42\u8f6c\uff0c\u6c34\u6d41\u82b1\u8c22\u4e0d\u5173\u60c5\uff0c\u6e05\u6eaa\u7a7a\u8574\u8bcd\u4eba\u6028\u3002\u201d \u753b\u5b8c\u63b7\u7b14\u957f\u7b11\u5ffd\u5730\u53c8\u545c\u545c\u75db\u54ed\u8d77\u6765\u3002 \u3000\u3000\u5ffd\u89c9\u6709\u4eba\u5728\u81ea\u5df1\u80a9\u4e0a\u8f7b\u8f7b\u4e00\u62cd\uff0c\u62ac\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u4e00\u4e2a\u5934\u53d1\u6591\u767d\u7684\u8001\u4eba\uff0c\u76f8\u8c8c\u867d\u7136\u51f6\u6076\uff0c \u773c\u5149\u4e2d\u5374\u4f3c\u4e4e\u5bf9\u81ea\u5df1\u900f\u9732\u7740\u65e0\u9650\u7684\u540c\u60c5\u4e0e\u5173\u5207\uff0c\u53ea\u542c\u4ed6\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u4f60\u54ed\u4ec0\u4e48\uff1f\u201d \u5f20\u4e39\u67ab\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u4f60\u53c8\u7b11\u4ec0\u4e48\uff1f\u201d\u90a3\u8001\u5934\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u771f\u60f3\u4e0d\u5230\u5929\u5730\u4e4b\u95f4\uff0c\u7adf\u7136\u8fd8\u6709 \u4f60\u6211\u4e24\u4e2a\u75f4\u4eba\uff01\u201d\u4e24\u4eba\u76f8\u5bf9\uff0c\u54ed\u4e86\u4e00\u9635\uff0c\u53c8\u7b11\u4e86\u4e00\u9635\uff0c\u90a3\u8001\u5934\u9053\uff1a\u201c\u4f60\u6628\u665a\u53eb\u4e86\u4e00\u665a\u5c0f\u5144 \u5f1f\u3001\u5c0f\u5144\u5f1f\uff0c\u4f60\u7684\u5c0f\u5144\u5f1f\u5728\u54ea\u91cc\uff1f\u201d\u5f20\u4e39\u67ab\u4e0d\u7406\u4e0d\u776c\uff0c\u62ff\u8d77\u81ea\u5df1\u6240\u753b\u7684\u5341\u51e0\u5f20\u4e91\u857e\u7684\u56fe\u50cf\uff0c \u9010\u4e00\u7ec6\u770b\uff0c\u53c8\u545c\u545c\u5730\u75db\u54ed\u8d77\u6765\u3002 \u3000\u3000\u90a3\u8001\u5934\u9053\uff1a\u201c\u54c8\uff0c\u8fd9\u5c31\u662f\u4f60\u7684\u5c0f\u5144\u5f1f\u5417\uff1f\u201d\u5f20\u4e39\u67ab\u56b7\u9053\uff1a\u201c\u4f60\u600e\u6562\u77aa\u7740\u773c\u775b\u770b\u6211\u7684\u5c0f\u5144 \u5f1f\uff0c\u54fc\uff0c\u54fc\uff0c\u6211\u8981\u6253\u4f60\u8fd9\u4e2a\u6ca1\u793c\u8c8c\u7684\u7cdf\u8001\u5934\u5b50\u3002\u201d\u4e00\u638c\u626b\u53bb\uff0c\u90a3\u8001\u5934\u7ad6\u8d77\u4e00\u6307\uff0c\u8f7b\u8f7b\u4e00\u70b9\uff0c \u5f20\u4e39\u67ab\u7684\u91d1\u521a\u638c\u529b\uff0c\u88ab\u4ed6\u6307\u5934\u8f7b\u8f7b\u4e00\u89e6\uff0c\u5168\u90fd\u6d88\u89e3\uff0c\u5ffd\u5730\u53c8\u50cf\u4e00\u4e2a\u6cc4\u4e86\u6c14\u7684\u76ae\u7403\uff0c\u5bf9\u7740\u4e00\u5f20 \u4e91\u857e\u7684\u56fe\u50cf\u54ed\u9053\uff1a\u201c\u5440\uff0c\u5440\uff0c\u6211\u4e0d\u8bb8\u522b\u4eba\u77aa\u7740\u773c\u775b\u770b\u4f60\uff0c\u4e3a\u4ec0\u4e48\u4f60\u5374\u53c8\u77aa\u7740\u773c\u775b\u770b\u6211\uff1f\u201d\u90a3 \u4e00\u5f20\u6b63\u662f\u4e91\u857e\u53d1\u6012\u7684\u56fe\u50cf\u3002 \u3000\u3000\u90a3\u8001\u5934\u53f9\u4e86\u53e3\u6c14\uff0c\u81ea\u8a00\u81ea\u8bed\u9053\uff1a\u201c\u51e0\u5341\u5e74\u524d\uff0c\u82e5\u6709\u4eba\u6562\u591a\u770b\u6211\u7684\u829d\u5170\u4e00\u773c\uff0c\u6211\u4e5f\u4f1a\u6253 \u4ed6\u3002\u201d\u8fd9\u4e00\u77ac\u95f4\uff0c\u53ea\u89c9\u773c\u524d\u8fd9\u4e2a\u5c11\u5e74\uff0c\u5c31\u662f\u81ea\u5df1\u5f53\u5e74\u7684\u5f62\u8c61\uff0c\u4e0d\u89c9\u95ee\u9053\uff1a\u201c\u4f60\u7684\u5c0f\u5144\u5f1f\u4e3a\u4ec0 \u4e48\u79bb\u5f00\u4f60\u5462\uff1f\u201d\u5f20\u4e39\u67ab\u77aa\u4e86\u90a3\u8001\u5934\u4e00\u773c\uff0c\u9053\uff1a\u201c\u4f60\u90fd\u77e5\u9053\u4e86\uff0c\u8fd8\u95ee\u6211\u4f5c\u4ec0\u4e48\uff1f\u201d\u8001\u5934\u8be7\u9053\uff1a \u201c\u600e\u4e48\uff1f\u201d\u5f20\u4e39\u67ab\u541f\u9053\uff1a\u201c\u96be\u5fd8\u6069\u6028\u96be\u5fd8\u4f60\uff0c\u53ea\u4e3a\u60c5\u75f4\u53ea\u4e3a\u771f\u3002\u8fd9\u4e0d\u662f\u4f60\u5199\u7684\u4e48\uff1f\u4f60\u82e5\u4e0d\u77e5 \u9053\u6211\u548c\u4e91\u857e\u7684\u4e8b\u60c5\uff0c\u53c8\u600e\u4e48\u5199\u5f97\u51fa\u8fd9\u526f\u8054\u8bed\uff1f\u201d \u3000\u3000\u90a3\u8001\u5934\u542c\u4ed6\u8fd9\u8bdd\uff0c\u4e5f\u4e0d\u89c9\u75f4\u4e86\uff0c\u5fc3\u9053\uff1a\u201c\u539f\u6765\u6069\u6028\u96be\u5fd8\uff0c\u76f8\u601d\u60c5\u5b7d\uff0c\u90fd\u662f\u4e00\u822c\u3002\u201d\u5ffd\u5730 \u62cd\u6848\u5927\u7b11\u9053\uff1a\u201c\u4e09\u5341\u5e74\u524d\u662f\u6211\uff0c\u4e09\u5341\u5e74\u540e\u662f\u4f60\uff0c\u5f7c\u6b64\u5f7c\u6b64\uff0c\u4e14\u8ba9\u5929\u4e0b\u60c5\u75f4\u540c\u58f0\u4e00\u54ed\uff01\u201d\u7b11\u58f0 \u672a\u505c\uff0c\u5c31\u4e0e\u5f20\u4e39\u67ab\u62b1\u5934\u75db\u54ed\uff0c\u8fd9\u4e00\u54ed\u58f0\u4f20\u6797\u91ce\uff0c\u60ca\u5f97\u77f3\u5ba4\u4e2d\u7684\u4f8d\u8005\u9762\u9762\u76f8\u89d1\uff0c\u4e2a\u4e2a\u5947\u602a\uff0c\u4ed6 \u4eec\u90fd\u4ee5\u4e3a\u4e0a\u5b98\u5929\u91ce\u4f1a\u6740\u4e86\u90a3\u4e2a\u5c11\u5e74\u7684\uff0c\u54ea\u6599\u5230\u4ed6\u4eec\u7adf\u50cf\u591a\u5e74\u7684\u77e5\u5df1\uff0c\u4e00\u89c1\u9762\u5c31\u54ed\u5440\u7b11\u5440\u5730\u95f9 \u4e2a\u4e0d\u4f11\u3002\u90a3\u51e0\u4e2a\u4f8d\u8005\u670d\u4f8d\u4e0a\u5b98\u5929\u91ce\u591a\u5e74\uff0c\u867d\u7136\u90fd\u77e5\u9053\u4ed6\u559c\u6012\u65e0\u5e38\uff0c\u4f46\u5374\u4ece\u65e0\u4eca\u65e5\u4e4b\u602a\u7edd\uff01 \u3000\u3000\u4e24\u4eba\u5927\u54ed\u4e00\u58f0\uff0c\u90a3\u8001\u5934\u5927\u53eb\u9053\uff1a\u201c\u4eca\u65e5\u8fd9\u4e00\u54ed\u771f\u662f\u75db\u5feb\u6781\u4e86\uff0c\u54c8\u54c8\uff0c\u4e09\u5341\u5e74\u6765\u90c1\u79ef\uff0c\u4eca \u65e5\u5f97\u9047\u540c\u75c5\u76f8\u601c\u4e4b\u4eba\uff01\u201d\u54ed\u58f0\u8f6c\u4e3a\u7b11\u58f0\uff0c\u5f20\u4e39\u67ab\u4e5f\u4e0d\u77e5\u4e0d\u89c9\u5730\u8ddf\u4ed6\u7b11\u4e86\u8d77\u6765\uff0c\u4f46\u89c9\u8fd9\u4e00\u54ed\u4e4b \u540e\u5fc3\u4e2d\u8212\u670d\u8bb8\u591a\uff0c\u8111\u7b4b\u6e10\u6e10\u6e05\u9192\uff0c\u4e0d\u89c9\u95ee\u9053\uff1a\u201c\u6211\u600e\u4e48\u4f1a\u5230\u8fd9\u513f\u6765\u7684\uff1f\u201d \u3000\u3000\u90a3\u8001\u5934\u7b11\u9053\uff1a\u201c\u662f\u5440\uff0c\u6211\u4e5f\u6b63\u8981\u95ee\u4f60\uff0c\u4f60\u600e\u4e48\u4f1a\u6765\u5230\u8fd9\u513f\u7684\uff1f\u201d\u5f20\u4e39\u67ab\u82e6\u82e6\u601d\u7d22\uff0c\u5140\u662f \u60f3\u4e0d\u8d77\u81ea\u5df1\u4e3a\u4f55\u4f1a\u6765\u5230\u8fd9\u513f\uff0c\u53ea\u8bb0\u5f97\u81ea\u5df1\u548c\u4e91\u857e\u4e4b\u95f4\u7684\u4e8b\u60c5\uff0c\u8bb0\u5f97\u4e91\u857e\u7684\u5bb6\uff0c\u5c31\u5728\u8fd9\u5c71\u7684\u5357 \u9762\u5ce1\u8c37\uff0c\u597d\u50cf\u662f\u81ea\u5df1\u88ab\u5979\u5173\u5728\u95e8\u5916\u4e4b\u540e\uff0c\u5c31\u8dd1\u5230\u8fd9\u513f\uff0c\u4e3a\u7684\u5c31\u662f\u8981\u627e\u8fd9\u4e2a\u8001\u5934\u6765\u4e00\u8bc9\u8877\u66f2\u4f3c \u7684\u3002\u5f20\u4e39\u67ab\u81ea\u81ea\u7136\u7136\u5730\u89c9\u5f97\uff0c\u8fd9\u4e2a\u8001\u5934\u662f\u613f\u542c\u81ea\u5df1\u7684\u5fc3\u4e8b\uff0c\u800c\u81ea\u5df1\u53c8\u662f\u613f\u610f\u5c06\u5fc3\u4e8b\u544a\u8bc9\u4ed6\u7684 \u4eba\u3002 \u3000\u3000\u4e8e\u662f\u5f20\u4e39\u67ab\u7d6e\u7d6e\u53e8\u53e8\uff0c\u628a\u81ea\u5df1\u548c\u4e91\u857e\u4e4b\u95f4\u7684\u6069\u6028\u60c5\u5b7d\uff0c\u4e1c\u626f\u4e00\u6bb5\uff0c\u897f\u626f\u4e00\u6bb5\u5730\u544a\u8bc9\u4e86\u90a3 \u4e2a\u8001\u5934\uff0c\u53d9\u8ff0\u7684\u6b21\u5e8f\u6709\u65f6\u98a0\u5012\uff0c\u6709\u65f6\u53c8\u6f0f\u4e86\u4e00\u6bb5\uff0c\u8bf4\u4e86\u4e00\u5927\u7247\u4e4b\u540e\uff0c\u7136\u540e\u518d\u8865\u8ff0\uff0c\u4e1c\u9cde\u897f \u722a\uff0c\u4e00\u4e2a\u7247\u6bb5\u4e00\u4e2a\u7247\u6bb5\u7684\u60c5\u8282\u90fd\u51e0\u4e4e\u8fde\u4e32\u4e0d\u8d77\u6765\u3002\u90a3\u8001\u5934\u542c\u4e86\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u548c\u5979\u7684\u6b66\u529f\u662f\u4f55 \u4eba\u6240\u6388\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u6211\u548c\u5979\u662f\u540c\u95e8\uff0c\u5979\u548c\u6211\u662f\u540c\u95e8\uff0c\u6211\u7684\u5e08\u7236\u662f\u8c01\uff1f\u5979\u7684\u5e08\u7236\u662f\u8c01\uff1f\u201d \u82e6\u82e6\u601d\u7d22\uff0c\u4e00\u4e0b\u5b50\u5374\u8bb0\u4e0d\u8d77\u6765\u3002\u90a3\u8001\u5934\u9053\uff1a\u201c\u4f60\u542c\u8fc7\u7384\u673a\u9038\u58eb\u7684\u540d\u5b57\u4e48\uff1f\u201d \u3000\u3000\u5f20\u4e39\u67ab\u731b\u5730\u4e00\u6298\u8111\u888b\uff0c\u53eb\u9053\uff1a\u201c\u662f\u4e86\uff0c\u6211\u8bb0\u8d77\u6765\u4e86\uff0c\u6211\u7684\u5e08\u7956\u53eb\u505a\u7384\u673a\u9038\u58eb\uff0c\u7384\u673a\u9038\u58eb \u5c31\u662f\u6211\u7684\u5e08\u7956\u3002\u7384\u673a\u9038\u58eb\u4f20\u4e0b\u4e24\u5957\u5251\u6cd5\uff0c\u5206\u5f00\u4f20\u6388\uff0c\u6240\u5b66\u4e4b\u4eba\uff0c\u53ea\u51c6\u77e5\u9053\u81ea\u5df1\u8fd9\u5957\u5251\u6cd5\uff0c\u4e0d \u8bb8\u77e5\u9053\u53e6\u5916\u90a3\u4e00\u5957\u5251\u6cd5\uff0c\u5077\u5b66\u534a\u62db\uff0c\u5c31\u8981\u88ab\u7f5a\u9762\u58c1\u5341\u4e94\u5e74\u3002\u6211\u662f\u5728\u74e6\u523a\u4eac\u57ce\u5b66\u6280\u7684\uff0c\u5440\uff0c\u6211 \u662f\u8ddf\u8c01\u5b66\u7684\uff0c\u4e0d\u77e5\u9053\uff0c\u4e0d\u77e5\u9053\uff0c\u5979\u5728\u5c0f\u5bd2\u5c71\u9762\u58c1\u5341\u4e8c\u5e74\uff0c\u4e24\u5957\u5251\u6cd5\u5f7c\u6b64\u4e0d\u51c6\u5077\u5b66\uff0c\u5440\uff0c\u7136\u540e \u5ffd\u7136\u76f8\u9047\uff0c\u53cc\u5251\u4e00\u5408\u74a7\u5c31\u914d\u5408\u5f97\u5999\u5230\u6beb\u5dc5\uff0c\u5929\u4e0b\u65e0\u654c\uff0c\u54c8\u54c8\uff0c\u5929\u4e0b\u65e0\u654c\uff01\u201d \u3000\u3000\u90a3\u8001\u5934\u59cb\u800c\u8272\u53d8\uff0c\u7ee7\u800c\u5927\u7b11\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u5c11\u5e74\u771f\u662f\u75af\u5f97\u5389\u5bb3\uff0c\u9759\u517b\u4e86\u4e00\u5929\u4e00\u591c\uff0c\u795e\u667a\u8fd8 \u662f\u5982\u6b64\u8ff7\u7cca\u3002\u4ed6\u82e5\u662f\u7384\u673a\u9038\u58eb\u7684\u5f92\u5b59\u53c8\u7109\u80fd\u5728\u74e6\u523a\u4eac\u57ce\u4e60\u6280\uff1f\u4ed6\u7684\u7231\u4fa3\u6bd4\u4ed6\u8fd8\u5c0f\uff0c\u600e\u5730\u53c8\u5ffd \u7136\u4f1a\u8dd1\u5230\u56db\u5ddd\u7684\u5c0f\u5bd2\u5c71\u53bb\u9762\u58c1\u4e00\u5341\u4e8c\u5e74\uff1f\u5b66\u6210\u6b66\u827a\u53c8\u9762\u58c1\u5341\u4e8c\u5e74\u65b9\u624d\u4e0e\u4ed6\u76f8\u9047\uff0c\u5c82\u4e0d\u662f\u534a\u8001 \u5f90\u5a18\u4e86\u4e48\uff1f\u5929\u5730\u4e4b\u95f4\uff0c\u53c8\u600e\u4f1a\u8fde\u5bf9\u65b9\u7684\u4e00\u62db\u5251\u6cd5\u90fd\u672a\u89c1\u8fc7\uff0c\u800c\u53c8\u80fd\u914d\u5408\u5230\u5999\u5230\u6beb\u5dc5\u7684\uff1f\u8fd8\u8bf4 \u5929\u4e0b\u65e0\u654c\uff0c\u90a3\u5c82\u4e0d\u662f\u5728\u8bf4\u68a6\u8bdd\u4e48\uff1f\u518d\u8bf4\u4ee5\u4ed6\u7684\u529f\u529b\uff0c\u82e5\u8bf4\u662f\u7384\u673a\u9038\u58eb\u7684\u5f92\u5f1f\uff0c\u6211\u8fd8\u6709\u70b9\u76f8 \u4fe1\uff0c\u7384\u673a\u9038\u58eb\u7684\u5f92\u5b59\uff0c\u5c82\u80fd\u6321\u5f97\u6211\u7684\u4e00\u6307\uff1f\u5927\u7ea6\u4ed6\u7684\u5e08\u7236\u662f\u4e00\u4e2a\u4e0d\u9732\u540d\u59d3\u7684\u6b66\u6797\u5f02\u4eba\uff0c\u5927\u7ea6 \u4ed6\u542c\u8fc7\u7384\u673a\u9038\u58eb\u7684\u540d\u5b57\uff0c\u7cca\u91cc\u7cca\u6d82\u5c31\u628a\u4ed6\u8bf4\u6210\u81ea\u5df1\u7684\u5e08\u7956\u3002\u201d\u4e0a\u5b98\u5929\u91ce\u54ea\u91cc\u6599\u60f3\u5f97\u5230\uff0c\u5f20\u4e39 \u67ab\u8bf4\u7684\u7adf\u662f\u5b9e\u60c5\uff0c\u53ea\u662f\u4ed6\u8bb0\u5fc6\u4e0d\u6e05\uff0c\u8bf4\u8bdd\u4e0d\u660e\uff0c\u4ed6\u672c\u6765\u8bb0\u5f97\u662f\u4e91\u857e\u7684\u5e08\u7236\u88ab\u7f5a\u5728\u5c0f\u5bd2\u5c71\u9762\u58c1 \u5341\u4e8c\u5e74\u7684\u8bdd\u8bf4\u5f97\u4e0d\u6e05\u695a\uff0c\u5374\u4ee4\u4e0a\u5b98\u5929\u91ce\u8bef\u4f1a\u4ed6\u662f\u8bf4\u4e91\u857e\u4e86\uff0c\u66f4\u52a0\u4e0a\u4ed6\u6628\u665a\u6240\u663e\u9732\u7684\u5185\u529f\uff0c\u5e76 \u975e\u7384\u673a\u9038\u58eb\u4e00\u6d3e\uff0c\u6545\u5f15\u4e0a\u5b98\u5929\u91ce\u8d8a\u53d1\u4e0d\u4fe1\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u8bf4\u5b8c\u4e4b\u540e\uff0c\u9053\uff1a\u201c\u4f60\u53c8\u662f\u8c01\uff1f\u4f60\u4e3a\u4f55\u4f4f\u5728\u8fd9\u91cc\uff1f\u96be\u9053\u662f\u4f60\u7684\u5c0f\u5144\u5f1f\u4e5f\u629b\u5f03\u4e86\u4f60 \u4e48\uff1f\u201d\u4e0a\u5b98\u5929\u91ce\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u7684\u5c0f\u5144\u5f1f\u5b81\u613f\u5728\u7d2b\u7af9\u6797\u4e2d\u9762\u58c1\u4e09\u5341\u5e74\uff0c\u4e5f\u4e0d\u613f\u5230\u8fd9\u96ea\u5c71\u6765\u89c1 \u6211\u4e00\u9762\u3002\u5475\uff0c\u5c0f\u5144\u5f1f\uff0c\u6211\u7ed9\u4f60\u8bf4\u4e00\u4e2a\u6545\u4e8b\u3002\u201d \u3000\u3000\u201c\u4e09\u5341\u5e74\u524d\uff0c\u6709\u4e00\u4e2a\u7eff\u6797\u5927\u76d7\u548c\u4e00\u4e2a\u6b66\u6797\u5251\u5ba2\uff0c\u4e24\u4eba\u90fd\u81ea\u5938\u662f\u5929\u4e0b\u65e0\u654c\uff0c\u4e0d\uff0c\u4e0d\u662f\u81ea \u5938\uff0c\u4f60\u6240\u8bf4\u7684\u53cc\u5251\u5408\u74a7\u5929\u4e0b\u65e0\u654c\uff0c\u90a3\u662f\u5047\u7684\uff0c\u4ed6\u4eec\u4e24\u4eba\u7684\u5929\u4e0b\u65e0\u654c\u90a3\u662f\u771f\u7684\u3002\u201d\u5f20\u4e39\u67ab\u9053\uff1a \u201c\u90a3\u7a76\u7adf\u8c01\u65b9\u662f\u5929\u4e0b\u65e0\u654c\uff1f\u201d\u4e0a\u5b98\u5929\u91ce\u9053\uff1a\u201c\u73b0\u5728\u4e5f\u8fd8\u4e0d\u77e5\u9053\u5440\uff0c\u4f60\u82e5\u8981\u77e5\u9053\uff0c\u53ef\u5728\u8fd9\u91cc\u591a \u4f4f\u51e0\u5929\u3002\u8bdd\u8bf4\u8fd9\u4e24\u4eba\u90fd\u81ea\u8d1f\u662f\u5929\u4e0b\u65e0\u654c\uff0c\u5374\u504f\u504f\u90fd\u4e00\u540c\u7231\u4e0a\u4e86\u53e6\u4e00\u4f4d\u4e5f\u81ea\u8d1f\u662f\u5929\u4e0b\u65e0\u654c\u7684\u5973 \u5b50\uff0c\u8fd9\u5973\u5b50\u548c\u90a3\u7eff\u6797\u5927\u76d7\u5435\u67b6\u7684\u65f6\u5019\u591a\uff0c\u8c08\u7b11\u65f6\u5019\u5c11\uff0c\u5927\u7ea6\u662f\u4ed6\u90a3\u5927\u76d7\u540d\u58f0\u4e0d\u597d\uff0c\u6240\u4ee5\u5979\u867d \u548c\u90a3\u5251\u5ba2\u6027\u60c5\u4e0d\u6295\uff0c\u5374\u5e38\u5e38\u5374\u627e\u4ed6\u3002\u5440\u90a3\u5251\u5ba2\u771f\u574f\uff0c\u4ed6\u56e0\u4e3a\u548c\u90a3\u5927\u76d7\u4f5c\u5bf9\uff0c\u5c31\u6545\u610f\u6298\u78e8\u90a3\u4e2a \u5973\u5b50\uff0c\u597d\u53eb\u90a3\u5927\u76d7\u4f24\u5fc3\u3002\u90a3\u5927\u76d7\u4e00\u751f\u6c14\uff0c\u5c31\u4e0e\u4ed6\u5728\u5ce8\u5d4b\u4e4b\u5dc5\uff0c\u6bd4\u4e86\u4e09\u65e5\u4e09\u591c\uff0c\u4e0d\u5206\u80dc\u8d1f\uff0c\u90a3 \u5927\u76d7\u91d1\u76c6\u6d17\u624b\uff0c\u9041\u8ff9\u8499\u8fb9\uff0c\u4ed6\u672c\u610f\u662f\u82f1\u96c4\u91cd\u82f1\u96c4\uff0c\u6709\u610f\u5c06\u4ed6\u6240\u5fc3\u7231\u7684\u4eba\uff0c\u8ba9\u4e0e\u90a3\u4f4d\u5251\u5ba2\uff0c \u54fc\uff0c\u54fc\uff0c\u8c01\u77e5\u90a3\u5251\u5ba2\u5374\u662f\u574f\u5230\u900f\u9876\u3002\u201d \u3000\u3000\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u600e\u4e48\u574f\u6cd5\uff1f\u201d\u4e0a\u5b98\u5929\u91ce\u9053\uff1a\u201c\u6bd4\u6b66\u4e4b\u540e\uff0c\u90a3\u5251\u5ba2\u5c31\u629b\u5f03\u4e86\u90a3\u4e2a\u5973\u5b50\uff0c\u600e\u6837 \u8bf4\u4e5f\u4e0d\u7406\u5979\uff0c\u8ba9\u5979\u72ec\u81ea\u5230\u7d2b\u7af9\u6797\u4e2d\u75db\u54ed\u3002\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u5440\uff0c\u8fd9\u5251\u5ba2\u771f\u8981\u4e0d\u5f97\uff0c\u600e\u4e48\u53ef\u4ee5\u629b \u5f03\u4e00\u4e2a\u949f\u60c5\u81ea\u5df1\u7684\u4eba\u3002\u201d\u4ed6\u53ef\u4e0d\u77e5\u9053\u90a3\u8001\u5934\u6240\u8bf4\u7684\u5251\u5ba2\u4fbf\u662f\u4ed6\u7684\u5e08\u7956\u7384\u673a\u9038\u58eb\uff0c\u5927\u76d7\u662f\u4e0a\u5b98 \u5929\u91ce\u81ea\u5df1\uff0c\u90a3\u5973\u5b50\u5219\u662f\u524d\u65f6\u5728\u7d2b\u7af9\u68ee\u4e2d\u6240\u89c1\u7684\u90a3\u4e2a\u8001\u5a46\u5a46\uff0c\u59d3\u8427\u540d\u5524\u97f5\u5170\uff0c\u4e0a\u5b98\u5929\u91ce\u4e66\u623f\u4e2d \u6240\u4f9b\u7684\u90a3\u76c6\u829d\u5170\uff0c\u5c31\u662f\u7eaa\u5ff5\u5979\u7684\u3002 \u3000\u3000\u4f46\u4e0a\u5b98\u5929\u91ce\u8bf4\u7684\u4e5f\u6709\u4e0d\u5c3d\u4e0d\u5b9e\u4e4b\u5904\uff0c\u4e0a\u5b98\u5929\u91ce\u7231\u8427\u97f5\u5170\uff0c\u7384\u673a\u9038\u58eb\u53ef\u6ca1\u6709\u7231\u5979\uff0c\u4ed6\u4e24\u4eba \u6027\u60c5\u4e0d\u6295\u4e5f\u662f\u4e8b\u5b9e\uff0c\u539f\u56e0\u5374\u4e0d\u662f\u7531\u4e8e\u7231\u60c5\u4e0a\u7684\u7ea0\u7eb7\u3002\u8427\u97f5\u5170\u5c11\u65f6\u6b66\u529f\u6781\u9ad8\uff0c\u4eba\u53c8\u7f8e\u8c8c\uff0c\u56e0\u6b64 \u5979\u6709\u4e00\u79cd\u5947\u602a\u7684\u6b32\u671b\uff0c\u5e0c\u671b\u5929\u4e0b\u82f1\u96c4\u90fd\u62dc\u5012\u81ea\u5df1\u7684\u77f3\u69b4\u88d9\u4e0b\u3002\u5979\u5e76\u4e0d\u6b22\u559c\u4e0a\u5b98\u5929\u91ce\uff0c\u4f46\u5374\u56e0 \u4e0a\u5b98\u5929\u91ce\u7684\u8ffd\u9010\u800c\u611f\u5230\u6ee1\u8db3\u3002\u7384\u673a\u9038\u58eb\u5c31\u56e0\u4e3a\u4e0d\u6b22\u559c\u5979\u8fd9\u79cd\u54c1\u6027\u800c\u758f\u8fdc\u5979\uff0c\u5979\u5374\u504f\u504f\u8981\u53bb\u62db \u60f9\u7384\u673a\u9038\u58eb\u3002\u5979\u8fd9\u79cd\u9700\u8981\u201c\u81ea\u6211\u6ee1\u8db3\u201d\u7684\u6b32\u671b\u8d8a\u6765\u8d8a\u5f3a\u70c8\uff0c\u7adf\u5e0c\u671b\u4e24\u540d\u81ea\u8d1f\u662f\u201c\u5929\u4e0b\u65e0\u654c\u201d \u7684\u4eba\u90fd\u4e3a\u5979\u800c\u6b7b\uff0c\u6700\u5c11\u4e5f\u8981\u4e3a\u5979\u800c\u4f5c\u751f\u6b7b\u7684\u51b3\u6597\uff0c\u56e0\u6b64\u5979\u6709\u610f\u65e0\u610f\u5730\u5236\u9020\u7ea0\u7eb7\uff0c\u4fc3\u6210\u4e24\u4eba\u4e3a \u5979\u800c\u51b3\u6597\u3002\u4e0a\u5b98\u5929\u91ce\u4e00\u610f\u7231\u5979\uff0c\u81ea\u7136\u4e2d\u8ba1\uff0c\u7384\u673a\u9038\u58eb\u672c\u60f3\u907f\u5f00\uff0c\u4f46\u88ab\u4e0a\u5b98\u5929\u91ce\u6240\u8feb\uff0c\u4ed6\u53c8\u4e0d \u613f\u5728\u4e0a\u5b98\u5929\u91ce\u9762\u524d\uff0c\u8bf4\u8427\u97f5\u5170\u7684\u574f\u8bdd\uff0c\u5373\u63ed\u7834\u5979\u7684\u7528\u5fc3\uff0c\u56e0\u6b64\u53d8\u6210\u4e86\u6709\u82e6\u96be\u8a00\uff0c\u907f\u65e0\u53ef\u907f\uff0c \u8fd9\u624d\u6709\u5ce8\u5d4b\u5c71\u5dc5\u90a3\u4e09\u65e5\u4e09\u591c\u7684\u6bd4\u6b66\u3002\u6bd4\u6b66\u4e4b\u540e\uff0c\u7384\u673a\u9038\u58eb\u53ea\u89c9\u5929\u4e0b\u5973\u4eba\u90fd\u662f\u7978\u6c34\uff0c\u6027\u60c5\u5927 \u53d8\uff0c\u5bf9\u8427\u97f5\u5170\u66f4\u4e0d\u5047\u8f9e\u8272\uff0c\u5e72\u8106\u5c31\u62d2\u7edd\u5979\u518d\u4e0a\u95e8\u6c42\u89c1\uff0c\u907f\u4e4b\u6709\u5982\u86c7\u874e\u3002\u8427\u97f5\u5170\u4e3a\u4e86\u6ee1\u8db3\u5979\u90a3 \u4e00\u70b9\u865a\u8363\u4e4b\u5fc3\u53cd\u800c\u7406\u5230\u4e24\u4e2a\u6b66\u6797\u5947\u58eb\u90fd\u79bb\u5979\u800c\u53bb\uff0c\u81ea\u5c0a\u5fc3\u66f4\u662f\u53d7\u5230\u6781\u5927\u7684\u6253\u51fb\uff0c\u56e0\u6b64\u4e5f\u5c31\u7edd \u8ff9\u6c5f\u6e56\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u4e0d\u77e5\u5185\u91cc\u60c5\u7531\uff0c\u53ea\u662f\u89c9\u5f97\u4e00\u4e2a\u4eba\u4e0d\u5e94\u629b\u5f03\u4e00\u4e2a\u949f\u60c5\u81ea\u5df1\u7684\u4eba\uff0c\u5c31\u50cf\u4e91\u857e\u4e0d\u5e94\u629b\u5f03 \u4ed6\u4e00\u6837\uff0c\u6545\u6b64\u987a\u7740\u4e0a\u5b98\u5929\u91ce\u7684\u53e3\u6c14\uff0c\u5927\u9a82\u90a3\u4e2a\u5251\u5ba2\uff0c\u4e24\u4eba\u8bf4\u8bdd\u751a\u662f\u6295\u673a\uff0c\u4e0a\u5b98\u5929\u91ce\u5c31\u7559\u4ed6\u5728 \u77f3\u5ba4\u4e2d\u4f4f\u4e0b\uff0c\u8981\u4ed6\u6bcf\u65e5\u7528\u5185\u529f\u81ea\u7597\uff0c\u5e0c\u671b\u4ed6\u7ecf\u8fc7\u51e0\u65e5\u7684\u9759\u517b\u4e4b\u540e\u53ef\u4ee5\u6162\u6162\u6062\u590d\u8bb0\u5fc6\u3002 \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u53bb\u540e\uff0c\u5f20\u4e39\u67ab\u7a81\u7136\u60f3\u8d77\u5ce8\u5d4b\u6bd4\u6b66\u7684\u6545\u4e8b\uff0c\u597d\u50cf\u81ea\u5df1\u4ee5\u524d\u66fe\u542c\u4eba\u8bf4\u8fc7\uff0c\u4f46\u4e00\u518d\u601d \u7d22\uff0c\u5374\u53c8\u60f3\u4e0d\u8d77\u6765\uff0c\u53ea\u662f\u9690\u9690\u89c9\u5f97\uff0c\u5728\u6bd4\u6b66\u7684\u4e24\u4eba\u4e4b\u4e2d\uff0c\u6709\u4e00\u4e2a\u548c\u81ea\u5df1\u5927\u6709\u5173\u7cfb\u3002 \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u6240\u5b66\u751a\u5e7f\uff0c\u8bd7\u8bcd\u6b4c\u8d4b\uff0c\u4ea6\u66fe\u6d89\u730e\uff0c\u6bcf\u65e5\u4ed6\u90fd\u8fdb\u4e66\u623f\u4e0e\u5f20\u4e39\u67ab\u503e\u8c08\u4e00\u756a\uff0c\u4e24\u4eba\u90fd \u81ea\u8ba4\u201c\u60c5\u75f4\u201d\uff0c\u8bf4\u5230\u4f24\u5fc3\u4e4b\u5904\u5c31\u62b1\u5934\u5927\u54ed\uff0c\u8bf4\u5230\u5feb\u610f\u4e4b\u5904\u53c8\u5927\u7b11\u4e00\u573a\uff0c\u5982\u6b64\u8fd9\u822c\u5730\u95f9\u4e86\u51e0 \u65e5\uff0c\u5f20\u4e39\u67ab\u5fc3\u5934\u7684\u90c1\u7ed3\uff0c\u6709\u4e00\u4e2a\u4eba\u53ef\u4ee5\u8bc9\u8bf4\uff0c\u6e10\u6e10\u6e32\u6cc4\uff0c\u795e\u667a\u6bd4\u521d\u4e0a\u5c71\u65f6\u6e05\u695a\u8bb8\u591a\u3002\u8fd9\u4e00\u65e5 \u5728\u4e66\u623f\u4e2d\u72ec\u81ea\u601d\u7d22\uff0c\u5ffd\u7136\u8bb0\u8d77\u662f\u81ea\u5df1\u7684\u5e08\u7236\u7ea6\u81ea\u5df1\u4e0a\u5c71\u6765\u62dc\u4f1a\u4e00\u4e2a\u201c\u9b54\u5934\u201d\u7684\uff0c\u8fd9\u201c\u9b54\u5934\u201d \u662f\u8c01\uff0c\u540d\u5b57\u4e00\u65f6\u8fd8\u60f3\u4e0d\u8d77\u6765\uff0c\u6b63\u60f3\u53bb\u627e\u4e0a\u5b98\u5929\u91ce\uff0c\u95ee\u4ed6\u8fd9\u5c71\u4e0a\u53ef\u6709\u4ec0\u4e48\u6b66\u529f\u6781\u5389\u5bb3\u7684\u201c\u9b54 \u5934\u201d\uff0c\u5ffd\u542c\u5f97\u4e0a\u5b98\u5929\u91ce\u5728\u5916\u9762\u9ad8\u58f0\u8bf4\u8bdd\uff0c\u4f3c\u4e4e\u6b63\u5728\u5bf9\u4ec0\u4e48\u5927\u53d1\u813e\u6c14\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u5728\u4e66\u623f\u4e2d\u53ea\u542c\u5f97\u4e0a\u5b98\u5929\u91ce\u5728\u5916\u9762\u9a82\u9053\uff1a\u201c\u4e4c\u8499\u592b\uff0c\u4f60\u8fd8\u6709\u80c6\u6765\u89c1\u6211\u5417\uff1f\u201d\u4e00\u4e2a\u4e2d \u5e74\u6c49\u5b50\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u81ea\u79bb\u5e08\u95e8\u65e0\u65e5\u6216\u5fd8\uff0c\u5e08\u7236\u6240\u6388\u7684\u4e00\u6307\u7985\u529f\u592b\uff0c\u6211\u65e5\u65e5\u7ec3\u4e60\uff0c\u4e5f\u6ca1\u6709\u95f4 \u65ad\u8fc7\uff0c\u6c42\u5e08\u7236\u8bb8\u6211\u91cd\u5217\u95e8\u5899\u3002\u201d\u4e0a\u5b98\u5929\u91ce\u9053\uff1a\u201c\u7ec3\u8fd9\u79cd\u6700\u4e0a\u4e58\u7684\u529f\u592b\uff0c\u7ec8\u751f\u4e0d\u8bb8\u7ed3\u5a5a\uff0c\u4f60\u5374 \u6709\u60c5\u6b32\u4e4b\u5ff5\uff0c\u72af\u4e86\u4f60\u8fdb\u95e8\u4e4b\u65f6\u6240\u53d1\u7684\u5927\u8a93\uff0c\u6211\u5c82\u80fd\u518d\u6536\u7559\u4f60\u3002\u4f60\u5b66\u4e0d\u5230\u4e0a\u4e58\u529f\u592b\uff0c\u6bd4\u4e0d\u4e0a\u7384 \u673a\u9038\u58eb\u7684\u5f1f\u5b50\uff0c\u6211\u7684\u9762\u76ae\u5c82\u975e\u4e5f\u8981\u7ed9\u4f60\u4e22\u5c3d\uff1f\u201d\u90a3\u6c49\u5b50\u9053\uff1a\u201c\u4eca\u540e\u6211\u53d1\u8a93\u4e0d\u518d\u52a8\u60c5\uff0c\u5e76\u613f\u5c06 \u529f\u8d4e\u7f6a\u3002\u201d\u4e0a\u5b98\u5929\u91ce\u9053\uff1a\u201c\u4f60\u6709\u4ec0\u4e48\u529f\uff1f\u201d\u90a3\u6c49\u5b50\u9053\uff1a\u201c\u6211\u5df2\u63a2\u5f97\u7384\u673a\u9038\u58eb\u6b66\u529f\u7684\u5965\u79d8\u3002\u201d \u4e0a\u5b98\u5929\u91ce\u9053\uff1a\u201c\u4ec0\u4e48\u5965\u79d8\uff0c\u4f60\u8bf4\u8bf4\u770b\u3002\u201d\u58f0\u97f3\u867d\u5f88\u5e73\u6de1\uff0c\u5185\u5fc3\u5374\u662f\u6fc0\u52a8\u3002\u90a3\u6c49\u5b50\u9053\uff1a\u201c\u6211\u548c \u7384\u673a\u9038\u58eb\u7684\u95e8\u4e0b\u5728\u96c1\u95e8\u5173\u5916\u5df2\u5148\u89c1\u8fc7\u4e00\u9635\uff0c\u4ed6\u4eec\u4e5f\u4e0d\u89c1\u5f97\u6bd4\u5f1f\u5b50\u5f3a\u5230\u54ea\u91cc\uff0c\u53ea\u662f\u4ed6\u4eec\u6709\u4e00\u5957 \u6781\u5389\u5bb3\u7684\u6b66\u529f\u2026\u2026\u201d\u4e0a\u5b98\u5929\u91ce\u6025\u9053\uff1a\u201c\u4ec0\u4e48\u6b66\u529f\uff0c\u6bd4\u5f97\u4e0a\u6211\u7684\u4e00\u6307\u7985\u529f\u4e48\uff1f\u201d\u90a3\u6c49\u5b50\u9053\uff1a \u201c\u8fd9\u6b66\u529f\u548c\u4e00\u6307\u7985\u4e0d\u662f\u540c\u4e00\u8def\u6570\uff0c\u4ed6\u4eec\u6709\u4e00\u5957\u4e24\u4eba\u5408\u4fbf\u7684\u5251\u6cd5\uff0c\u53cc\u5251\u5408\u74a7\uff0c\u5389\u5bb3\u65e0\u6bd4\uff01\u201d\u4e0a \u5b98\u5929\u91ce\u201c\u566b\u201d\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\uff0c\u53cc\u5251\u5408\u74a7\uff1f\u771f\u7684\u6709\u53cc\u5251\u5408\u74a7\uff0c\u6211\u5c31\u4e0d\u76f8\u4fe1\u5b83\u5929\u4e0b\u65e0 \u654c\uff01\u201d\u58f0\u97f3\u4e2d\u663e\u51fa\u8be7\u5f02\u7684\u5fc3\u60c5\u3002\u5f20\u4e39\u67ab\u542c\u4e86\uff0c\u4ea6\u89c9\u5947\u602a\uff0c\u7a81\u7136\u95f4\u597d\u50cf\u88ab\u62e8\u53bb\u4e00\u5c42\u8ff7\u96fe\uff0c\u5fc3 \u9053\uff1a\u201c\u6211\u7684\u5e08\u7956\u5c31\u662f\u7384\u673a\u9038\u58eb\uff0c\u8fd9\u53cc\u5251\u5408\u74a7\u5c31\u662f\u6211\u548c\u4e91\u857e\u6240\u5f97\u7684\u7edd\u6280\u3002\u5440\uff0c\u539f\u6765\u8fd9\u8001\u5934\u5c31\u662f \u6211\u5e08\u7236\u6240\u8981\u62dc\u4f1a\u7684\u90a3\u8001\u2018\u9b54\u5934\u2019\uff01\u201d \u3000\u3000\u5f20\u4e39\u67ab\u60f3\u8d77\u8fd9\u51e0\u65e5\u7684\u60c5\u5f62\uff0c\u5fc3\u9053\uff1a\u201c\u539f\u6765\u6211\u548c\u8fd9\u8001\u9b54\u5934\u540c\u4f4f\u4e86\u51e0\u5929\uff0c\u4f46\u8fd9\u8001\u9b54\u5934\u5176\u5b9e\u4e5f \u5e76\u6ca1\u6709\u4ec0\u4e48\u53ef\u6015\u5440\uff01\u201d\u53c8\u60f3\u9053\uff1a\u201c\u5e08\u7956\u4e0d\u77e5\u662f\u4e3a\u4ec0\u4e48\u548c\u4ed6\u7ed3\u6028\u7684\uff1f\u5440\uff0c\u83ab\u975e\u4ed6\u6240\u8bf4\u7684\u90a3\u4e2a\u6545 \u4e8b\u90a3\u4e24\u4e2a\u81ea\u8d1f\u5929\u4e0b\u65e0\u654c\u7684\u6b66\u6797\u5947\u58eb\uff0c\u5c31\u662f\u4ed6\u548c\u5e08\u7956\uff1f\u201d\u5f20\u4e39\u67ab\u672c\u6765\u5fc3\u6027\u7075\u654f\uff0c\u800c\u4eca\u795e\u5fd7\u6e10\u6e10 \u6062\u590d\uff0c\u6240\u6599\u679c\u7136\u4e0d\u5dee\u3002\u6b63\u5728\u8ddf\u7740\u8fd9\u6761\u7ebf\u7d22\uff0c\u82e6\u82e6\u8ffd\u601d\u81ea\u5df1\u5e73\u751f\u7684\u7ecf\u5386\uff0c\u5ffd\u542c\u5f97\u5916\u9762\u4e0a\u5b98\u5929\u91ce \u53c8\u9a82\u9053\uff1a\u201c\u662f\u8c01\u5f15\u4f60\u4e0a\u5c71\u7684\uff0c\u662f\u4e0d\u662f\u4ed9\u97f5\u8fd9\u4e2a\u4e2b\u5934\uff1f\u201d\u90a3\u6c49\u5b50\u9053\uff1a\u201c\u4e0d\u9519\u662f\u5e08\u59b9\u3002\u5e08\u7236\u653e \u5fc3\uff0c\u6211\u7edd\u4e0d\u4f1a\u548c\u5e08\u59b9\u518d\u8c08\u5a5a\u5ac1\u4e4b\u4e8b\u3002\u201d\u4e0a\u5b98\u5929\u91ce\u5389\u58f0\u53f1\u9053\uff1a\u201c\u4f60\u5728\u89c1\u6211\u4e4b\u524d\u5148\u7ea6\u89c1\u5e08\u59b9\uff0c\u8fd9 \u5df2\u7ecf\u72af\u4e86\u6212\u6761\uff0c\u4f60\u77e5\u8fc7\u4e48\uff1f\u73b0\u5728\u7f5a\u4f60\u5728\u9759\u5ba4\u4e4b\u4e2d\u601d\u8fc7\uff0c\u975e\u5f97\u6211\u7684\u5429\u5490\uff0c\u4e0d\u51c6\u64c5\u81ea\u79bb\u5f00\u3002\u201d\u9a82 \u5f97\u867d\u7136\u5389\u5bb3\uff0c\u5176\u5b9e\u5df2\u662f\u51c6\u4ed6\u91cd\u5217\u95e8\u5899\uff0c\u4e4c\u8499\u592b\u5927\u559c\uff0c\u53e9\u5934\u8c22\u6069\u3002\u5f20\u4e39\u67ab\u5374\u5728\u4e66\u623f\u4e2d\u60f3\u9053\uff1a \u201c\u8fd9\u8001\u9b54\u5934\u679c\u7136\u4e0d\u8fd1\u4eba\u60c5\uff0c\u4ed6\u81ea\u5df1\u81ea\u547d\u2018\u60c5\u75f4\u2019\uff0c\u5374\u4e0d\u8bb8\u4ed6\u95e8\u4e0b\u5f1f\u5b50\u8c08\u5a5a\u8bba\u5ac1\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u5c06\u4e4c\u8499\u592b\u5173\u5728\u9759\u5ba4\u4e4b\u540e\uff0c\u5429\u5490\u4f8d\u8005\u9053\uff1a\u201c\u73b0\u5728\u6211\u4e5f\u8981\u8fdb\u9759\u5ba4\u7ec3\u529f\uff0c\u9664\u975e\u662f\u7384\u673a \u9038\u58eb\u7684\u95e8\u4e0b\u5230\u6765\uff0c\u5426\u5219\u4e0d\u8bb8\u8fdb\u6765\u6253\u6270\u3002\u201d\u8bf4\u5b8c\u8fd9\u540e\u4e0d\u4e45\uff0c\u5916\u95f4\u4e00\u7247\u5bc2\u9759\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u8d8a\u60f3\u8d8a\u66ff\u90a3\u6c49\u5b50\u4e0d\u503c\uff0c\u4ed6\u751f\u5c31\u4e00\u526f\u6253\u62b1\u4e0d\u5e73\u7684\u813e\u6c14\u7adf\u7136\u8d70\u51fa\u4e66\u623f\uff0c\u62c9\u7740\u4e00\u4e2a\u4f8d \u8005\uff0c\u5c31\u95ee\u4ed6\u9002\u624d\u90a3\u6c49\u5b50\u5173\u5728\u54ea\u91cc\u3002\u4f8d\u8005\u77e5\u9053\u4ed6\u662f\u4e0a\u5b98\u5929\u91ce\u8fd1\u5e74\u6765\u6700\u76f8\u8c08\u5f97\u6765\u7684\u4eba\uff0c\u867d\u4e0d\u77e5\u4ed6 \u7684\u6765\u5386\uff0c\u4f46\u4e0d\u6562\u4e0d\u544a\u8bc9\u4ed6\u3002 \u3000\u3000\u4f8d\u8005\u5c06\u5f20\u4e39\u67ab\u5e26\u5230\u9759\u5ba4\uff0c\u53e9\u95e8\u8bf4\uff1a\u201c\u5e08\u7236\u7684\u4e00\u4f4d\u670b\u53cb\u524d\u6765\u89c1\u4f60\uff0c\u8fd9\u662f\u4f60\u7684\u673a\u7f18\uff0c\u4f60\u6709\u4ec0 \u4e48\u4e3a\u96be\u4e4b\u4e8b\uff0c\u53ef\u4ee5\u8bf7\u8fd9\u4f4d\u5ba2\u4eba\u66ff\u4f60\u5411\u5e08\u7236\u6c42\u60c5\u3002\u201d\u4e4c\u8499\u592b\u5728\u91cc\u9762\u542c\u5f97\u4f8d\u8005\u5982\u6b64\u8bf4\u8bdd\uff0c\u5fc3\u4e2d\u60ca \u8be7\u4e4b\u6781\uff0c\u60f3\u9053\uff1a\u201c\u5e08\u7236\u8f88\u4efd\u4e4b\u9ad8\uff0c\u9664\u4e86\u7384\u673a\u9038\u58eb\u4e4b\u5916\uff0c\u5f53\u4e16\u65e0\u4e0e\u4f26\u6bd4\uff0c\u6709\u8c01\u914d\u79f0\u5f97\u4e0a\u662f\u4ed6\u7684 \u670b\u53cb\uff1f\u800c\u4e14\u542c\u4f8d\u8005\u7684\u53e3\u6c14\uff0c\u597d\u50cf\u8fd8\u662f\u5e08\u7236\u6240\u5c0a\u656c\u7684\u670b\u53cb\u3002\u201d\u623f\u95e8\u6253\u5f00\uff0c\u5f20\u4e39\u67ab\u4e00\u811a\u8de8\u8fdb\uff0c\u987a \u624b\u63a9\u4e0a\u623f\u95e8\uff0c\u4e4c\u8499\u592b\u62ac\u5934\u4e00\u770b\uff0c\u4e0d\u7981\u5446\u4e86\u3002 \u3000\u3000\u53ea\u542c\u5f97\u4e4c\u8499\u592b\u98a4\u58f0\u95ee\u9053\uff1a\u201c\u4f60\u3001\u4f60\u3001\u4f60\u4e0d\u662f\u8c22\u5929\u534e\u7684\u5f92\u5f1f\u5f20\u4e39\u67ab\u4e48\uff1f\u201d\u5f20\u4e39\u67ab\u731b\u5730\u4e00\u6298 \u8111\u888b\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u7684\u5e08\u7236\u53eb\u8c22\u5929\u534e\uff0c\u8c22\u5929\u534e\u662f\u6211\u7684\u5e08\u7236\uff01\u201d\u4e4c\u8499\u592b\u89c1\u4ed6\u795e\u6001\u5927\u5f02 \u5e38\u4eba\uff0c\u597d\u50cf\u5fd8\u8bb0\u4e86\u4ec0\u4e48\u4e8b\u60c5\uff0c\u5ffd\u7136\u6709\u4eba\u63d0\u8d77\uff0c\u663e\u51fa\u53c8\u60ca\u53c8\u559c\uff0c\u6709\u5982\u5927\u68a6\u521d\u9192\u7684\u795e\u6c14\uff0c\u4e0d\u7981\u53c8 \u95ee\u9053\uff1a\u201c\u4f60\u6211\u5e08\u95e8\u7ed3\u6709\u5927\u4ec7\uff0c\u4f60\u662f\u6211\u7684\u5bf9\u5934\uff0c\u4f60\u77e5\u9053\u4e48\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u4eec\u662f\u6211\u4eec \u7684\u5bf9\u5934\uff0c\u54c8\uff0c\u6211\u8bb0\u8d77\u6765\u4e86\uff0c\u4f60\u548c\u6211\u4ea4\u8fc7\u4e24\u6b21\u624b\uff0c\u4e00\u6b21\u662f\u5728\u5c71\u5be8\uff0c\u4e00\u6b21\u662f\u5728\u96c1\u95e8\u5173\u5916\u3002\u201d\u8bb0\u867d \u8bb0\u4e86\u8d77\u6765\uff0c\u4f46\u5fc3\u4e2d\u8fd8\u9690\u9690\u89c9\u5f97\uff0c\u4ed6\u548c\u4e4c\u8499\u592b\u4ea4\u624b\uff0c\u53c8\u4e0d\u4f3c\u4ec5\u662f\u56e0\u4e3a\u5e08\u95e8\u4ec7\u6028\u8fd9\u6837\u7b80\u5355\u3002\u4e4c\u8499 \u592b\u9053\uff1a\u201c\u90a3\u4f60\u4e3a\u4f55\u6765\u5230\u8fd9\u513f\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u662f\u5440\uff0c\u6211\u4e3a\u4f55\u6765\u5230\u8fd9\u513f\u5462\uff1f\u201d\u5ffd\u7136\u6602\u9996\u541f\u9053\uff1a \u201c\u96be\u5fd8\u6069\u6028\u96be\u5fd8\u4f60\uff0c\u53ea\u4e3a\u60c5\u75f4\u53ea\u4e3a\u771f\u3002\u5582\uff0c\u4f60\u4e0d\u662f\u662f\u60c5\u75f4\uff1f\u201d\u4e4c\u8499\u592b\u9053\uff1a\u201c\u4f60\u8bf4\u4ec0\u4e48\uff1f\u201d\u5f20 \u4e39\u67ab\u5927\u58f0\u9053\uff1a\u201c\u6211\u8bf4\u4f60\u4e0d\u662f\u60c5\u75f4\uff0c\u4f60\u4e3a\u4f55\u8981\u629b\u5f03\u4f60\u7684\u5e08\u59b9\uff1f\u201d\u5f20\u4e39\u67ab\u4f3c\u75af\u975e\u75af\uff0c\u8bdd\u8bed\u5374\u89e6\u52a8 \u4e86\u4e4c\u8499\u592b\u7684\u5fc3\u4e8b\uff0c\u4e0d\u7981\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u8c01\u8bf4\u6211\u629b\u5f03\u4e86\u5979\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u90a3\u4f60\u4e3a\u4f55\u4e0d\u6562\u4e0e\u5979\u8c08 \u5a5a\u8bba\u5ac1\uff1f\u201d\u4e4c\u8499\u592b\u9053\uff1a\u201c\u4f60\u77e5\u9053\u4ec0\u4e48\uff1f\u6211\u4eec\u8fd9\u4e00\u6d3e\u7684\u4e0a\u4e58\u529f\u592b\uff0c\u987b\u8981\u4fdd\u6301\u7ae5\u5b50\u4e4b\u8eab\uff0c\u4e00\u7ed3\u4e86 \u5a5a\u529f\u592b\u5c31\u5b66\u4e0d\u6210\u4e86\u3002\u201d\u5f20\u4e39\u67ab\u5927\u7b11\u9053\uff1a\u201c\u54ea\u6709\u8fd9\u6837\u7684\u9053\u7406\uff1f\u9664\u975e\u4f60\u5b66\u7684\u4e0d\u662f\u6b63\u5b97\u7684\u7384\u95e8\u5185 \u529f\u3002\u54ea\uff0c\u6211\u4e14\u8ba9\u4f60\u5f00\u5f00\u773c\u754c\u3002\u201d\u4ece\u6000\u4e2d\u53d6\u51fa\u90a3\u672c\u300a\u7384\u529f\u8981\u8bc0\u300b\uff0c\u9053\uff1a\u201c\u6211\u628a\u8fd9\u4e66\u501f\u4e0e\u4f60\uff0c\u4f60 \u7528\u8fd9\u79cd\u7384\u529f\u505a\u57fa\u7840\uff0c\u518d\u7ec3\u4f60\u7684\u4e00\u6307\u7985\u53bb\u3002\u4e0a\u5b98\u8001\u9b54\u82e5\u8fd8\u7981\u4f60\u8c08\u5a5a\u8bba\u5ac1\uff0c\u4f60\u5c31\u5c06\u8fd9\u672c\u4e66\u62ff\u7ed9\u4ed6 \u770b\uff0c\u82e5\u8fd8\u4e0d\u51c6\uff0c\u6211\u5c31\u66ff\u4f60\u6253\u4ed6\u4e00\u987f\uff0c\u8fd8\u8981\u5c06\u4ed6\u4eb2\u624b\u6240\u5199\u7684\u8054\u8bed\u4e00\u628a\u6495\u6389\u3002\u201d \u3000\u3000\u4e4c\u8499\u592b\u4e45\u5df2\u60f3\u5f97\u8fd9\u672c\u300a\u7384\u529f\u8981\u8bc0\u300b\uff0c\u89c1\u4e86\u5927\u559c\uff0c\u53c8\u89c1\u5f20\u4e39\u67ab\u72b6\u7c7b\u75af\u75f4\uff0c\u751f\u6015\u4ed6\u5c31\u4f1a\u53cd \u6094\uff0c\u5fd9\u9053\uff1a\u201c\u597d\u597d\uff0c\u6211\u591a\u8c22\u4f60\u5566\u3002\u4f60\u5feb\u56de\u53bb\u5427\uff0c\u514d\u5f97\u5e08\u7236\u77e5\u9053\u4e86\u8d23\u602a\u3002\u201d \u3000\u3000\u5f20\u4e39\u67ab\u54c8\u54c8\u5927\u7b11\uff0c\u8d70\u56de\u4e66\u623f\uff0c\u5f97\u610f\u4e4b\u6781\u3002\u4ed6\u601d\u7d22\u5f80\u4e8b\uff0c\u751a\u662f\u4f24\u795e\uff0c\u4e0d\u89c9\u7eb3\u5934\u4fbf\u7761\u3002\u4e5f\u4e0d \u77e5\u7761\u4e86\u591a\u4e45\uff0c\u5916\u9762\u5ffd\u7136\u4f20\u6765\u4e86\u5175\u5668\u4ea4\u51fb\u7684\u58f0\u97f3\uff0c\u5f20\u4e39\u67ab\u4e00\u4e0b\u8df3\u8d77\uff0c\u8dd1\u5230\u5916\u9762\uff0c\u4e00\u4e2a\u4f8d\u8005\u90fd\u4e0d \u89c1\u4e86\uff0c\u6253\u5f00\u9759\u5ba4\uff0c\u4e4c\u8499\u592b\u4e5f\u4e0d\u89c1\u4e86\u3002\u5f20\u4e39\u67ab\u8d70\u51fa\u77f3\u5ba4\uff0c\u53ea\u89c1\u5916\u9762\u5c71\u5934\uff0c\u5927\u6811\u4e4b\u4e0b\uff0c\u6709\u4e00\u7537\u4e00 \u5973\uff0c\u624b\u6301\u957f\u5251\uff0c\u4e0e\u4e0a\u5b98\u5929\u91ce\u6253\u5f97\u6b63\u70c8\uff0c\u7537\u7684\u662f\u4ed6\u7684\u5e08\u7236\u8c22\u5929\u534e\uff0c\u5973\u7684\u4ed6\u4e5f\u8bb0\u4e86\u8d77\u6765\uff0c\u4e43\u662f\u4e91 \u857e\u7684\u5e08\u7236\u98de\u5929\u9f99\u5973\u53f6\u76c8\u76c8\u3002\u4e4c\u8499\u592b\u548c\u51e0\u4e2a\u4f8d\u8005\u7ad9\u5728\u65c1\u8fb9\u3002\u8c22\u5929\u534e\u4e0e\u98de\u5929\u9f99\u5973\u89c1\u5f20\u4e39\u67ab\u7a81\u7136\u4ece \u77f3\u5ba4\u4e2d\u8df3\u51fa\u6765\uff0c\u90fd\u4e0d\u7981\u5927\u4e3a\u5947\u602a\u3002\u6b63\u662f\uff1a \u3000\u3000\u6069\u6028\u65e0\u7aef\u8c01\u4e0e\u89e3\uff1f\u4e14\u770b\u9038\u58eb\u6597\u9b54\u5934\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u840d\u8e2a\u4fa0\u5f71\u5f55\u300b\u2014\u2014\u7b2c\u4e8c\u5341\u516b\u56de\u3000\u4e07\u91cc\u8fdc\u6765\u5f02\u4e61\u5bfb\u8001\u6bcd\u3000\u5345\u5e74\u91cd\u4f1a\u9038\u58eb\u6597\u9b54\u5934 \u6881\u7fbd\u751f\u300a\u840d\u8e2a\u4fa0\u5f71\u5f55\u300b \u7b2c\u4e8c\u5341\u516b\u56de\u3000\u4e07\u91cc\u8fdc\u6765\u5f02\u4e61\u5bfb\u8001\u6bcd\u3000\u5345\u5e74\u91cd\u4f1a\u9038\u58eb\u6597\u9b54\u5934 \u3000\u3000\u5f20\u4e39\u67ab\u8d70\u51fa\u77f3\u5ba4\uff0c\u89c1\u5927\u6811\u4e4b\u4e0b\uff0c\u4e00\u7537\u4e00\u5973\uff0c\u624b\u6301\u957f\u5251\uff0c\u4e0e\u4e0a\u5b98\u5929\u91ce\u6253\u5f97\u6b63\u70c8\uff0c\u5f20\u4e39\u67ab\u795e \u5fd7\u6e10\u6e10\u6e05\u9192\uff0c\u89c9\u5f97\u8fd9\u5bf9\u7537\u5973\u7684\u9762\u8c8c\u597d\u719f\uff0c\u731b\u7136\u60f3\u8d77\uff1a\u7537\u7684\u4e43\u662f\u81ea\u5df1\u7684\u5e08\u7236\u8c22\u5929\u534e\uff0c\u5973\u7684\u4e43\u662f \u4e91\u857e\u7684\u5e08\u7236\u53f6\u76c8\u76c8\u3002\u5fc3\u4e2d\u6697\u60ca\uff0c\u81ea\u8a00\u81ea\u8bed\u9053\uff1a\u201c\u55ef\uff0c\u4ed6\u679c\u771f\u662f\u6211\u4eec\u7684\u5927\u5bf9\u5934\uff01\u201d\u4e00\u9635\u8ff7\u60d8\uff0c \u5446\u7acb\u89c2\u6218\u3002 \u3000\u3000\u53ea\u89c1\u8c22\u5929\u534e\u4e0e\u53f6\u76c8\u76c8\u4e00\u5de6\u4e00\u53f3\uff0c\u53cc\u5251\u8054\u653b\uff0c\u5251\u52bf\u5feb\u6377\u65e0\u4f26\u6709\u5982\u957f\u6c5f\u6d6a\u6d8c\uff0c\u5927\u6f20\u6c99\u626c\uff0c\u800c \u4e14\u62db\u91cc\u6709\u62db\uff0c\u5f0f\u4e2d\u5957\u5f0f\uff0c\u53d8\u5316\u5947\u5e7b\uff0c\u5374\u53c8\u914d\u5408\u5f97\u5999\u5230\u6beb\u5dc5\u3002\u5f20\u4e39\u67ab\u8bc6\u5f97\u4e2a\u4e2d\u5965\u5999\uff0c\u5c1a\u81ea\u76ee\u7729 \u795e\u8ff7\uff0c\u65c1\u89c2\u7684\u4e4c\u8499\u592b\u7b49\u4eba\uff0c\u66f4\u662f\u77eb\u820c\u96be\u4e0b\u3002\u4f46\u90a3\u4e0a\u5b98\u5929\u91ce\uff0c\u6b66\u529f\u4e4b\u9ad8\uff0c\u5df2\u5230\u4e86\u4e0d\u53ef\u601d\u8bae\u7684\u5730 \u6b65\uff0c\u4ed6\u7adf\u7136\u4ee5\u4e00\u53cc\u8089\u638c\uff0c\u62b5\u6321\u53cc\u5251\u5408\u74a7\u7684\u653b\u52bf\uff0c\u6bcf\u4e00\u4e3e\u624b\u6295\u8db3\u4e4b\u95f4\uff0c\u90fd\u662f\u653b\u654c\u4e4b\u6240\u5fc5\u6551\uff0c\u6240 \u4ee5\u5728\u8868\u9762\u770b\u6765\uff0c\u4ed6\u867d\u4f3c\u5728\u53cc\u5251\u5a01\u529b\u7b3c\u7f69\u4e4b\u4e0b\uff0c\u6709\u5982\u4e00\u53f6\u5b64\u821f\uff0c\u5728\u94f6\u5149\u6ce2\u6d9b\u4e4b\u4e2d\u6323\u624e\uff0c\u4f46\u5f20\u4e39 \u67ab\u5374\u5df2\u770b\u51fa\uff0c\u53cc\u5251\u5408\u74a7\u7684\u795e\u5947\u62db\u6570\uff0c\u90fd\u88ab\u4ed6\u8f7b\u63cf\u6de1\u5199\u5730\u4e00\u4e00\u5316\u5f00\uff0c\u6bd4\u8d77\u90a3\u7d2b\u7af9\u6797\u4e2d\u7684\u8001\u5a46 \u5a46\uff0c\u53c8\u4e0d\u77e5\u9ad8\u5f3a\u51e0\u500d\uff01\u5fc3\u4e2d\u6697\u6697\u66ff\u5e08\u7236\u62c5\u5fe7\u3002 \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u4e5f\u662f\u5403\u60ca\u975e\u5c0f\uff0c\u624d\u76f8\u4fe1\u5f20\u4e39\u67ab\u6240\u8bf4\u7684\u4e0d\u662f\u865a\u8a00\uff0c\u4e16\u95f4\u679c\u771f\u6709\u8fd9\u6837\u4e00\u5957\u795e\u5947\u7684\u5251 \u6cd5\uff0c\u82e5\u4e0d\u662f\u81ea\u5df1\u529f\u529b\u6df1\u539a\uff0c\u96be\u4fdd\u4e0d\u4f1a\u843d\u8d25\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u5f1f\u5b50\u5982\u6b64\uff0c\u5e08\u7236\u53ef\u77e5\u3002\u201d\u5bf9\u7384\u673a\u9038 \u58eb\u4e0d\u7531\u5f97\u6697\u6697\u4f69\u670d\u3002\u6b63\u5728\u5403\u7d27\u4e4b\u9645\uff0c\u8c22\u5929\u534e\u4e0e\u53f6\u76c8\u76c8\u89c1\u5f20\u4e39\u67ab\u7a81\u7136\u4ece\u5927\u5bf9\u5934\u7684\u77f3\u5ba4\u4e2d\u8d70\u51fa\uff0c \u6014\u4e86\u4e00\u6014\uff0c\u4ed6\u4eec\u672c\u5df2\u5904\u5728\u4e0b\u98ce\uff0c\u8fd9\u5fae\u4e00\u5206\u795e\uff0c\u66f4\u7ed9\u4e0a\u5b98\u5929\u91ce\u8fde\u8fde\u53cd\u51fb\uff0c\u4e0a\u5b98\u5929\u91ce\u8fde\u5288\u4e09\u638c\uff0c \u5c06\u4e8c\u4eba\u903c\u9000\u51e0\u6b65\uff0c\u5ffd\u5730\u53eb\u9053\uff1a\u201c\u5f20\u4e39\u67ab\uff0c\u539f\u6765\u4f60\u4e5f\u662f\u7384\u673a\u9038\u58eb\u95e8\u4e0b\u7684\uff0c\u597d\u5427\uff0c\u4f60\u4e5f\u4e00\u5e76\u6765 \u5427\uff01\u201d \u3000\u3000\u5f20\u4e39\u67ab\u8fd9\u65f6\u5df2\u8bb0\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u5e08\u7236\u7ea6\u81ea\u5df1\u4e0e\u4e91\u857e\u5230\u6b64\u5c71\u4e2d\u5408\u529b\u6597\u8fd9\u4e2a\u8001\u9b54\u5934\u6765\u7684\u3002\u4f46\u4ed6 \u867d\u7136\u795e\u5fd7\u6e10\u590d\uff0c\u5fc3\u4e2d\u4ecd\u662f\u4e00\u7247\u832b\u7136\u3002\u53ea\u89c9\u4e0a\u5b98\u5929\u91ce\u4e0e\u81ea\u5df1\u6c14\u5473\u76f8\u6295\uff0c\u5e76\u4e0d\u4f3c\u4e00\u4e2a\u201c\u8001\u9b54 \u5934\u201d\uff0c\u5fc3\u4e2d\u53ea\u662f\u60f3\u9053\uff1a\u201c\u4ed6\u8bf4\u7684\u90a3\u4e2a\u6545\u4e8b\uff0c\u90a3\u8d1f\u5fc3\u7684\u5251\u5ba2\u662f\u8c01\u5462\uff1f\u662f\u4ed6\u8fd8\u662f\u5e08\u7956\uff1f\u201d \u3000\u3000\u542c\u5f97\u4e0a\u5b98\u5929\u91ce\u8fd9\u4e48\u4e00\u53eb\uff0c\u5f20\u4e39\u67ab\u624b\u629a\u5251\u67c4\uff0c\u8e0c\u8e87\u672a\u51b3\uff0c\u77a0\u76ee\u4e0d\u77e5\u6240\u5bf9\u3002\u4e4c\u8499\u592b\u89c1\u4ed6\u5931\u9b42 \u843d\u9b44\u7684\u6837\u5b50\uff0c\u4e0a\u524d\u4e00\u62cd\u4ed6\u7684\u80a9\u5934\u9053\uff1a\u201c\u54b1\u4eec\u6765\u6bd4\u4e00\u573a\u5427\u3002\u55ef\uff0c\u591a\u8c22\u4f60\u501f\u90a3\u672c\u7384\u529f\u8981\u8bc0\u4e0e \u6211\u3002\u201d\u5728\u4e4c\u8499\u592b\u5fc3\u4e2d\uff0c\u5b9e\u662f\u6015\u5f20\u4e39\u67ab\u529f\u529b\u5c1a\u6d45\u6321\u4e0d\u4e86\u4ed6\u5e08\u7236\u7684\u62f3\u811a\uff0c\u6545\u6b64\u60f3\u5047\u610f\u4e0e\u5f20\u4e39\u67ab\u6bd4 \u6597\u4e0a\u573a\uff0c\u8ba9\u4ed6\u4ea4\u4ee3\u8fc7\u53bb\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u597d\u7aef\u7aef\u7684\u6211\u548c\u4f60\u6253\u505a\u4ec0\u4e48\uff1f\u5582\uff0c\u4f60\u5e08\u7236\u7684\u51fa\u8eab\u662f\u5251\u5ba2\u8fd8\u662f\u5f3a\u76d7\uff1f\u201d\u4e4c\u8499\u592b \u89c1\u4ed6\u8bf4\u8bdd\u75af\u75af\u766b\u766b\uff0c\u4e0d\u7981\u4e00\u6115\u3002\u5f20\u4e39\u67ab\u6b63\u60f3\u518d\u95ee\uff0c\u5ffd\u542c\u5f97\u5c71\u540e\u53c8\u662f\u4e00\u9635\u5175\u5668\u4ea4\u51fb\u7684\u58f0\u97f3\uff0c\u4e24 \u7537\u4e00\u5973\u8fb9\u6253\u8fb9\u8d70\uff0c\u6e10\u6e10\u903c\u8fd1\u3002\u90a3\u4e24\u4e2a\u7537\u5b50\uff0c\u5149\u5934\u7684\u662f\u6f6e\u97f3\u548c\u5c1a\uff0c\u9762\u5982\u9505\u5e95\u3001\u4e00\u5934\u4e71\u53d1\u7684\u662f\u9707 \u4e09\u754c\u6bd5\u9053\u51e1\uff0c\u4ed6\u4eec\u88ab\u4e00\u4e2a\u5de6\u624b\u6301\u91d1\u94a9\uff0c\u53f3\u624b\u6301\u94f6\u5251\u7684\u5973\u5b50\u4e00\u8def\u8ffd\u51fb\uff0c\u6b63\u6740\u5f97\u96be\u5206\u96be\u89e3\u6c14\u5598\u5401 \u5401\u3002 \u3000\u3000\u539f\u6765\u90a3\u65e5\u5728\u96c1\u95e8\u5173\u5916\uff0c\u6f6e\u97f3\u548c\u5c1a\u6000\u7591\u8c22\u5929\u534e\u53d8\u8282\u6295\u654c\uff0c\u8ffd\u4e4b\u4e0d\u4e0a\uff0c\u5728\u8349\u539f\u4e0a\u5f98\u5f8a\u4e4b\u9645\uff0c \u5374\u9047\u89c1\u4e86\u9707\u4e09\u754c\u6bd5\u9053\u51e1\uff0c\u4e24\u4eba\u5230\u4e5f\u5148\u7684\u592a\u5e08\u5e9c\u53c8\u95f9\u4e86\u4e00\u573a\uff0c\u540e\u6765\u88ab\u8463\u5cb3\u627e\u5230\uff0c\u5411\u4ed6\u4eec\u7ec6\u7ec6\u89e3 \u91ca\u8bf4\u660e\u8c22\u5929\u534e\u7684\u7528\u5fc3\uff0c\u6f6e\u97f3\u548c\u5c1a\u624d\u77e5\u662f\u4e00\u573a\u8bef\u4f1a\uff0c\u597d\u751f\u540e\u6094\u3002\u8463\u5cb3\u7ea6\u4ed6\u4eec\u4f9d\u671f\u5230\u5ff5\u9752\u5510\u53e4\u62c9 \u5c71\uff0c\u4ed6\u4eec\u6bd4\u8c22\u3001\u53f6\u4e8c\u4eba\u843d\u540e\u4e00\u6b65\uff0c\u4e0a\u5c71\u8fd9\u65f6\uff0c\u5374\u9047\u89c1\u4e86\u56de\u5c71\u62dc\u89c1\u5e08\u7236\u7684\u91d1\u94a9\u4ed9\u5b50\u6797\u4ed9\u97f5\uff0c\u4e00 \u8a00\u4e0d\u5408\uff0c\u4fbf\u751f\u6076\u6597\u3002\u4e0a\u5b98\u5929\u91ce\u95e8\u4e0b\uff0c\u4ee5\u91d1\u94a9\u4ed9\u5b50\u7684\u6b66\u529f\u6700\u4e3a\u7cbe\u5999\uff0c\u8db3\u53ef\u4e0e\u8c22\u5929\u534e\u3001\u53f6\u76c8\u76c8\u65d7 \u9f13\u76f8\u5f53\uff0c\u6bd4\u6f6e\u97f3\u548c\u5c1a\u5374\u9ad8\u51fa\u8bb8\u591a\uff0c\u5de6\u94a9\u53f3\u5251\uff0c\u5947\u62db\u8fed\u51fa\uff0c\u6f6e\u97f3\u548c\u5c1a\u867d\u7136\u6709\u9707\u4e09\u754c\u76f8\u52a9\uff0c\u4ee5\u4e8c \u654c\u4e00\uff0c\u4ecd\u662f\u7a0d\u5904\u4e0b\u98ce\u3002 \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u53eb\u9053\uff1a\u201c\u4f60\u4eec\u90fd\u662f\u7384\u673a\u9038\u58eb\u7684\u95e8\u4e0b\u5417\uff1f\u597d\uff0c\u4e00\u5e76\u4e0a\u6765\uff0c\u4f60\u4eec\u5408\u529b\u4e0e\u6211\u76f8\u6597\uff0c\u53ea \u8981\u80fd\u6253\u6210\u5e73\u624b\uff0c\u6211\u5c31\u8ba9\u7384\u673a\u8fd9\u8001\u5934\u513f\u505a\u6b66\u6797\u76df\u4e3b\u4e86\u3002\u201d\u6797\u4ed9\u97f5\u4e00\u53e3\u6c14\u8fde\u8fdb\u4e09\u94a9\uff0c\u8fde\u8ffd\u4e8c\u5251\uff0c \u5c06\u6bd5\u9053\u51e1\u4e0e\u6f6e\u97f3\u548c\u5c1a\u6740\u5f97\u53ea\u80fd\u62db\u67b6\uff0c\u5ffd\u7136\u53cc\u94a9\u4e00\u677e\uff0c\u4e24\u4eba\u6536\u52bf\u4e0d\u53ca\uff0c\u6c14\u5598\u5618\u5618\uff0c\u9669\u9669\u8dcc\u5012\u3002 \u6797\u4ed9\u97f5\u7b11\u8bf4\u9053\uff1a\u201c\u8fd9\u4e24\u4e2a\u4e0d\u987b\u5e08\u7236\u6253\u53d1\u4e86\uff0c\u8ba9\u4ed6\u4eec\u518d\u6b47\u606f\u4e00\u4f1a\uff0c\u7136\u540e\u62db\u547c\u4ed6\u5427\u3002\u201d\u6f6e\u97f3\u548c\u5c1a \u4e0e\u6bd5\u9053\u51e1\u90fd\u662f\u706b\u7206\u7684\u6027\u5b50\uff0c\u52c3\u7136\u5927\u6012\uff0c\u4e00\u9f50\u8dc3\u8d77\uff0c\u5ffd\u89c1\u5f20\u4e39\u67ab\u8d70\u5230\u9762\u524d\uff0c\u5b9a\u7740\u773c\u795e\u6ce8\u610f\u4ed6 \u4eec\uff0c\u9762\u8272\u975e\u5e38\u53e4\u602a\uff0c\u81ea\u8a00\u81ea\u8bed\u9053\uff1a\u201c\u8fd9\u662f\u4e8c\u5e08\u4f2f\uff0c\u8fd9\u662f\uff0c\u8fd9\u662f\u2026\u2026\u201d\u6bd5\u9053\u51e1\u53eb\u9053\uff1a\u201c\u5f20\u4e39 \u67ab\uff0c\u4f60\u5e72\u4ec0\u4e48\uff1f\u4f60\u4e0d\u8ba4\u5f97\u6211\u5417\uff1f\u6211\u662f\u2026\u2026\u201d\u5f20\u4e39\u67ab\u4e00\u62cd\u8111\u888b\u7a81\u7136\u5927\u547c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u662f\u9707\u4e09 \u754c\u6bd5\u9053\u51e1\uff01\u201d\u6f6e\u97f3\u548c\u5c1a\u9053\uff1a\u201c\u6211\u5df2\u660e\u767d\u4f60\u5e08\u7236\u7684\u7528\u5fc3\u4e86\uff0c\u4f60\u4ee5\u524d\u72af\u4e0a\u4e4b\u4e8b\uff0c\u6211\u4ea6\u4e0d\u8ffd\u7a76\u4f60 \u4e86\uff0c\u4f60\u600e\u4e48\u8fd8\u4e0d\u4e0a\u53bb\u52a9\u4f60\u5e08\u7236\uff1f\u201d\u5f20\u4e39\u67ab\u8fd9\u65f6\u6b63\u5728\u7528\u5fc3\u601d\u7d22\uff0c\u60f3\u9053\uff1a\u201c\u6211\u5e08\u7236\u6709\u4ec0\u4e48\u7528 \u5fc3\uff1f\u201d\u9690\u9690\u8bb0\u5f97\u5e08\u7236\u662f\u5728\u74e6\u523a\u4eac\u57ce\u4e00\u95f4\u5927\u5c4b\u91cc\u5c45\u4f4f\uff0c\u90a3\u4eba\u5bb6\u6709\u4e00\u4e2a\u5927\u82b1\u56ed\uff0c\u5e08\u7236\u5c31\u662f\u5728\u82b1\u56ed \u4e2d\u4f20\u6388\u81ea\u5df1\u7684\u5251\u672f\u7684\u3002\u8fd9\u65f6\u4ed6\u4f9d\u7a00\u8bb0\u8d77\u4e86\u81ea\u5df1\u7684\u8eab\u4e16\uff0c\u8bb0\u8d77\u4e86\u660e\u671d\u4e0e\u74e6\u523a\u4e24\u56fd\u4ea4\u5175\u4e4b\u4e8b\uff0c\u6b63 \u5728\u8ddf\u7740\u8fd9\u6761\u7ebf\u7d22\u8ffd\u5fc6\uff0c\u5ffd\u542c\u5f97\u53ee\u53ee\u5f53\u5f53\u4e00\u7247\u54cd\u58f0\uff0c\u659c\u773c\u4e00\u77a5\uff0c\u53ea\u89c1\u4e0a\u5b98\u5929\u91ce\u957f\u8896\u6325\u821e\uff0c\u628a\u8c22 \u5929\u534e\u4e0e\u53f6\u76c8\u76c8\u7684\u4e24\u67c4\u957f\u5251\u62c2\u5f97\u5f7c\u6b64\u76f8\u649e\uff0c\u53cc\u5251\u5408\u74a7\u7684\u5947\u5999\u62db\u6570\uff0c\u767b\u65f6\u88ab\u4ed6\u6253\u4e71\u3002\u6f6e\u97f3\u548c\u5c1a\u4e0d \u7981\u60ca\u53eb\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u4e39\u67ab\uff0c\u4f60\u8fd8\u4e0d\u5feb\u53bb\uff01\u201d\u4ed6\u81ea\u5df1\u4e5f\u4e3e\u8d77\u7985\u6756\uff0c\u6b63\u62df\u4e00\u8dc3\u800c\u8d77\uff0c\u5374\u88ab\u91d1\u94a9 \u4ed9\u5b50\u5de6\u624b\u4e00\u94a9\u53f3\u624b\u4e00\u5251\uff0c\u8f7b\u8f7b\u62e6\u7740\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u7a81\u7136\u95ee\u9053\uff1a\u201c\u4e8c\u5e08\u4f2f\uff0c\u6211\u4eec\u7684\u5e08\u7956\u662f\u5f3a\u76d7\u8fd8\u662f\u5251\u5ba2\u5462\uff1f\u201d\u6f6e\u97f3\u548c\u5c1a\u6c14\u5f97\u66b4\u8df3\u5982 \u96f7\uff0c\u559d\u9053\uff1a\u201c\u4f60\u75af\u4e86\u5417\uff1f\u201d\u5f20\u4e39\u67ab\u624b\u6301\u5251\u67c4\uff0c\u5fc3\u610f\u672a\u51b3\uff0c\u5ffd\u89c1\u5c71\u5761\u66f2\u5f84\uff0c\u53c8\u8f6c\u51fa\u4e24\u4e2a\u4eba\u6765\uff0c \u8fd9\u4e00\u770b\u987f\u65f6\u4ee4\u4ed6\u5fc3\u5f26\u98a4\u6296\uff0c\u8840\u8109\u6cb8\u817e\u3002\u539f\u6765\u662f\u4e00\u4e2a\u5c11\u5973\u6276\u7740\u4e00\u4e2a\u8ddb\u8db3\u8001\u4eba\uff0c\u8d70\u5230\u5c71\u4e0a\uff0c\u6b63\u662f \u4e91\u857e\u7236\u5973\uff01\u5f20\u4e39\u67ab\u51e0\u4e4e\u7591\u5fc3\u81ea\u5df1\u662f\u5728\u6076\u68a6\u4e4b\u4e2d\uff0c\u4e0d\u7531\u81ea\u5df1\u7684\u5927\u53eb\u201c\u5c0f\u5144\u5f1f\uff0c\u5c0f\u5144\u5f1f\uff01\u201d\u53ea\u89c1 \u4e91\u857e\u82b1\u5bb9\u53d8\u8272\uff0c\u773c\u89d2\u7740\u6cea\u73e0\uff0c\u773c\u5149\u4f3c\u662f\u5411\u81ea\u5df1\u671b\u6765\uff0c\u4f3c\u7d27\u95ed\u6731\u5507\uff0c\u4e0d\u53d1\u8a00\u8bed\u3002 \u3000\u3000\u4e91\u857e\u7684\u7236\u4eb2\u6301\u7740\u62d0\u6756\uff0c\u4e00\u8df7\u4e00\u62d0\uff0c\u5728\u5973\u513f\u6276\u6396\u4e4b\u4e0b\uff0c\u8d70\u4e0a\u5c71\u6765\uff0c\u76ee\u5149\u5982\u526a\u5411\u5f20\u4e39\u67ab\u4e00 \u626b\uff0c\u773c\u5149\u4e2d\u5145\u6ee1\u9119\u5937\u618e\u6068\u7684\u795e\u60c5\u3002\u5f20\u4e39\u67ab\u53ea\u89c9\u4e00\u80a1\u5bd2\u610f\u76f4\u900f\u5fc3\u5934\uff0c\u5ffd\u542c\u5f97\u6f6e\u97f3\u548c\u5c1a\u5927\u53eb\u9053\uff1a \u201c\u5582\u4f60\u3001\u4f60\u662f\u8c01\uff1f\u5440\uff0c\u4f60\u4e0d\u662f\u4e91\u6f84\u5e08\u5f1f\u5417\uff1f\u4f60\u6ca1\u6709\u6b7b\uff01\u201d\u4e00\u8dc3\u800c\u8d77\u62b1\u7740\u4e91\u6f84\uff0c\u4e24\u5e08\u5144\u5f1f\u76f8\u5bf9 \u6d41\u6cea\uff0c\u4e91\u857e\u7ad9\u5728\u65c1\u8fb9\uff0c\u4e5f\u7981\u4e0d\u4f4f\u4ee5\u8896\u8bd5\u6cea\uff0c\u5f20\u4e39\u67ab\u76ee\u5149\u4e00\u5230\uff0c\u5979\u53c8\u6025\u5fd9\u626d\u5934\u907f\u5f00\u3002 \u3000\u3000\u6f6e\u97f3\u548c\u5c1a\u6027\u60c5\u66b4\u8e81\uff0c\u5374\u662f\u4e00\u526f\u70ed\u80a0\uff0c\u62b1\u7740\u4e91\u6f84\u53f9\u9053\uff1a\u201c\u5341\u5e74\u4e0d\u89c1\uff0c\u4f60\u600e\u4e48\u5f04\u6210\u8fd9\u4e2a\u6837 \u5b50\uff1f\u201d\u6f6e\u97f3\u548c\u5c1a\u672c\u6765\u6bd4\u4e91\u6f84\u5e74\u7eaa\u8fd8\u5927\u51e0\u5c81\uff0c\u800c\u4eca\u4e91\u6f84\u5934\u53d1\u6591\u767d\uff0c\u5f62\u5bb9\u6194\u60b4\uff0c\u770b\u8d77\u6765\u5374\u6bd4\u6f6e\u97f3 \u548c\u5c1a\u82cd\u8001\u8bb8\u591a\uff01 \u3000\u3000\u6f6e\u97f3\u548c\u5c1a\u7d6e\u7d6e\u4e0d\u4f11\u5730\u95ee\u957f\u95ee\u77ed\uff0c\u539f\u6765\u4e91\u6f84\u4ece\u5973\u513f\u53e3\u4e2d\u5f97\u77e5\u540c\u95e8\u5144\u5f1f\u76f8\u7ea6\u5728\u6b64\u5c71\u76f8\u4f1a\uff0c\u4ed6 \u867d\u77e5\u5f20\u4e39\u67ab\u4e5f\u5b9a\u7136\u4f1a\u5230\uff0c\u4f46\u4e3a\u4e86\u4e00\u89c1\u540c\u95e8\uff0c\u6240\u4ee5\u4e0d\u8f9e\u8270\u82e6\uff0c\u53eb\u5973\u513f\u6276\u4e0a\u5c71\u6765\u3002\u8fd9\u5341\u591a\u5929\u6765\uff0c \u4ed6\u7236\u5973\u4fe9\u90fd\u6781\u529b\u907f\u514d\u8c08\u53ca\u5f20\u5bb6\uff0c\u4e91\u6f84\u4ece\u90a3\u5929\u7684\u60c5\u666f\uff0c\u4e5f\u77e5\u9053\u4e86\u5973\u513f\u5bf9\u5f20\u4e39\u67ab\u7684\u60c5\u610f\uff0c\u867d\u7136\u5f53 \u65e5\u53d1\u4f5c\uff0c\u8fc7\u540e\u4fbf\u7edd\u53e3\u4e0d\u63d0\uff0c\u4e5f\u4e0d\u5bf9\u4e91\u857e\u8d23\u5907\u3002\u4f46\u4e91\u857e\u4ece\u4ed6\u7684\u795e\u8272\uff0c\u5df2\u77e5\u9053\u6b64\u751f\u518d\u4e5f\u65e0\u671b\u4e0e\u5f20 \u4e39\u67ab\u91cd\u805a\u3002\u6b64\u9645\u5979\u5fc3\u5982\u5200\u7ede\uff0c\u4e00\u534a\u662f\u4e3a\u4e86\u7236\u4eb2\u7684\u906d\u9047\u800c\u4f24\u5fc3\uff0c\u4e00\u534a\u5374\u4e5f\u56e0\u4e3a\u81ea\u5df1\u7684\u5883\u9047\u800c\u843d \u6cea\u3002 \u3000\u3000\u6b63\u662f\u5404\u81ea\u4f24\u5fc3\uff0c\u5404\u6709\u6000\u62b1\uff0c\u5ffd\u542c\u5f97\u5f53\u25a1\u25a1\u4e00\u7247\u5175\u5203\u4ea4\u51fb\u4e4b\u58f0\uff0c\u53ea\u89c1\u4e0a\u5b98\u5929\u91ce\u957f\u8896\u6325\u821e\uff0c \u53c8\u628a\u8c22\u5929\u534e\u4e0e\u53f6\u76c8\u76c8\u7684\u4e24\u67c4\u957f\u5251\u62c2\u5f97\u4e92\u76f8\u78b0\u51fb\uff0c\u53cc\u5251\u5408\u74a7\u7684\u5a01\u529b\uff0c\u5168\u5728\u5b83\u914d\u5408\u7684\u5999\u5230\u6beb\u5dc5\uff0c \u4e00\u62db\u534a\u5f0f\uff0c\u90fd\u4e0d\u80fd\u6709\u4e1d\u6beb\u9519\u4e71\u3002\u800c\u4eca\u88ab\u4e0a\u5b98\u5929\u91ce\u5f3a\u4ee5\u6700\u4e0a\u4e58\u7684\u7a7a\u624b\u5165\u767d\u5203\u7684\u529f\u592b\uff0c\u5229\u7528\u4e86\u4e24 \u6761\u957f\u8896\uff0c\u5c31\u5982\u591a\u4e86\u4e00\u53cc\u624b\u4e00\u822c\uff0c\u7adf\u5728\u53cc\u5251\u7b3c\u7f69\u4e4b\u4e0b\uff0c\u5f3a\u5c06\u5251\u52bf\u6253\u4e71\uff0c\u767b\u65f6\u9669\u8c61\u73af\u751f\uff0c\u8d8a\u6765\u8d8a \u89c1\u5403\u7d27\u3002 \u3000\u3000\u4e91\u857e\u8033\u542c\u6f6e\u97f3\u548c\u5c1a\u60ca\u547c\u4e4b\u58f0\uff0c\u773c\u89c1\u5e08\u7236\u4ed3\u7687\u4e4b\u8272\uff0c\u5ffd\u5730\u4e00\u8dc3\u800c\u8d77\uff0c\u62d4\u51fa\u9752\u51a5\u5b9d\u5251\u5c31\u51b2\u5165 \u9635\u4e2d\u3002\u53f6\u76c8\u76c8\u60ca\u547c\uff1a\u201c\u5feb\u9000\uff01\u201d\u4e0a\u5b98\u5929\u91ce\u4e00\u8896\u62c2\u6765\uff0c\u9053\uff1a\u201c\u5c0f\u599e\u513f\uff0c\u4f60\u4e5f\u8981\u6765\u8d81\u70ed\u95f9\u5417\uff1f\u201d \u8fd9\u4e00\u62c2\u624b\u4e0b\u7559\u60c5\uff0c\u53ea\u7528\u4e86\u4e09\u6210\u529b\u91cf\uff0c\u53f6\u76c8\u76c8\u7684\u957f\u5251\u88ab\u4ed6\u4e00\u62c2\u62c2\u5f00\u4f59\u52bf\u672a\u5c3d\uff0c\u5377\u5230\u4e91\u857e\u5251\u4e0a\uff0c \u4e91\u857e\u53ea\u89c9\u864e\u53e3\u9ebb\u75db\uff0c\u957f\u5251\u51e0\u6b32\u8131\u624b\u98de\u53bb\u3002\u5c31\u5728\u6b64\u9645\uff0c\u5ffd\u89c1\u767d\u5149\u4e00\u95ea\uff0c\u5f20\u4e39\u67ab\u51b2\u4e86\u5165\u6765\u3002\u4e0a\u5b98 \u5929\u91ce\u7b11\u9053\uff1a\u201c\u4f60\u4e5f\u6765\u4e86\u5417\uff1f\u201d\u8c22\u5929\u534e\u957f\u5251\u5e73\u524a\uff0c\u4e0a\u5b98\u5929\u91ce\u5de6\u8896\u98de\u626c\u53f3\u8896\u672a\u64a4\uff0c\u5ffd\u542c\u5f97\u55e4\u7684\u4e00 \u58f0\uff0c\u4e0a\u5b98\u5929\u91ce\u7684\u4e00\u53ea\u8863\u8896\uff0c\u7adf\u88ab\u5f20\u4e39\u67ab\u7684\u5b9d\u5251\u524a\u4e86\u4e00\u622a\u3002 \u3000\u3000\u6309\u8bf4\u5f20\u4e39\u67ab\u7684\u6b66\u529f\u5c1a\u4e0d\u5982\u4ed6\u7684\u5e08\u7236\uff0c\u6bd4\u8d77\u4e0a\u5b98\u5929\u91ce\u5dee\u5f97\u66f4\u8fdc\uff0c\u600e\u80fd\u524a\u65ad\u4ed6\u7684\u8863\u8896\uff1f\u4e00\u8005 \u662f\u56e0\u4e3a\u4e0a\u5b98\u5929\u91ce\u9002\u624d\u90a3\u4e00\u62c2\u7528\u610f\u4e0d\u8fc7\u60f3\u593a\u4e91\u857e\u7684\u5b9d\u5251\uff0c\u4ec5\u7528\u4e86\u4e09\u5206\u529b\u91cf\uff1b\u4e8c\u8005\u662f\u53d7\u4e86\u8c22\u3001\u53f6 \u4e8c\u4eba\u7684\u7275\u5236\uff1b\u4e09\u662f\u5f20\u4e39\u67ab\u7684\u5b9d\u5251\u524a\u94c1\u5982\u6ce5\uff0c\u5439\u6bdb\u7acb\u65ad\uff0c\u8863\u8896\u867d\u4e0d\u53d7\u529b\uff0c\u4f46\u4ed6\u5374\u85c9\u7740\u4e0a\u5b98\u5929\u91ce \u5c06\u64a4\u672a\u64a4\u4e4b\u9645\u7684\u90a3\u4e00\u62c2\u4e4b\u52bf\uff0c\u501f\u529b\u6253\u547c\uff0c\u4e00\u524a\u594f\u529f\u3002 \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u4e5f\u4e0d\u7981\u5403\u4e86\u4e00\u60ca\uff0c\u7528\u8db3\u52b2\u529b\uff0c\u53cc\u8896\u4e00\u6325\uff0c\u5c06\u56db\u67c4\u957f\u5251\u62c2\u5f97\u53ee\u53ee\u5f53\u5f53\u4f5c\u54cd\u8d5e\u9053\uff1a \u201c\u597d\u4e00\u628a\u524a\u94c1\u5982\u6ce5\u7684\u5b9d\u5251\u5440\uff01\u201d\u5f20\u3001\u4e91\u53cc\u5251\u7a81\u5730\u7531\u5408\u800c\u5206\uff0c\u4e91\u857e\u4f7f\u51fa\u4e00\u62db\u201c\u6d41\u661f\u8d76\u6708\u201d\uff0c\u5f20 \u4e39\u67ab\u4f7f\u51fa\u4e00\u62db\u201c\u767d\u8679\u8d2f\u65e5\u201d\uff0c\u4e00\u70b9\u9762\u95e8\uff0c\u4e00\u523a\u80f8\u819b\uff0c\u9752\u5149\u767d\u5149\uff0c\u4e0a\u4e0b\u6643\u52a8\uff0c\u4ea4\u53c9\u7a7f\u63d2\u3002\u4e0a\u5b98 \u5929\u91ce\u8fdb\u9000\u4e09\u6b65\uff0c\u957f\u8896\u4e00\u4f38\u4e00\u7f29\uff0c\u5ffd\u5730\u8f7b\u98d8\u98d8\u5730\u62cd\u51fa\u4e09\u638c\uff0c\u62db\u6570\u5201\u94bb\u53e4\u602a\u4e4b\u6781\uff0c\u5f20\u4e39\u67ab\u4e0d\u6562\u63a5 \u8fde\u8fdb\u653b\uff0c\u659c\u8eab\u4e00\u8ba9\uff0c\u4e0a\u5b98\u5929\u91ce\u5df2\u5728\u4e00\u8f6c\u8eab\u4e4b\u95f4\uff0c\u53c8\u5c06\u8c22\u5929\u534e\u4e0e\u53f6\u76c8\u76c8\u53cc\u5251\u5408\u74a7\u7684\u62db\u6570\u5316\u89e3\u5f00 \u4e86\u3002 \u3000\u3000\u8fd9\u4e00\u6218\u6fc0\u70c8\u4e4b\u6781\uff0c\u8c22\u3001\u53f6\u3001\u5f20\u3001\u4e91\u56db\u53e3\u5251\u5206\u6210\u4e24\u5bf9\uff0c\u524d\u540e\u5de6\u53f3\uff0c\u7ec7\u6210\u4e00\u7247\u5149\u7f51\uff0c\u4f7f\u5230\u75be \u5904\uff0c\u56db\u53e3\u5251\u5c31\u50cf\u5316\u6210\u5343\u767e\u53e3\u5251\uff0c\u628a\u4e0a\u5b98\u5929\u91ce\u56f4\u5728\u5f53\u4e2d\uff0c\u98ce\u96e8\u4e0d\u900f\u3002\u4e0a\u5b98\u5929\u91ce\u6c89\u7740\u5e94\u6218\uff0c\u6216\u6325 \u8896\u6216\u51fa\u638c\uff0c\u6240\u4f7f\u7684\u90fd\u662f\u6700\u4e0a\u4e58\u7684\u529f\u592b\uff0c\u7adf\u5728\u5251\u5149\u7b3c\u7f69\u4e4b\u4e0b\uff0c\u8fde\u8fde\u53cd\u51fb\uff0c\u6218\u5f97\u4e2a\u96be\u89e3\u96be\u5206\u3002 \u3000\u3000\u6f6e\u97f3\u548c\u5c1a\u5fd8\u4e86\u8bf4\u8bdd\uff0c\u6276\u7740\u4e91\u6f84\u5168\u795e\u89c2\u6218\uff0c\u4e4c\u8499\u592b\u4e0e\u6797\u4ed9\u97f5\u4e8c\u4eba\uff0c\u4e5f\u770b\u5f97\u5f20\u76ee\u7ed3\u820c\uff0c\u4e0d\u77e5 \u4e0d\u89c9\u5730\u504e\u501a\u5728\u4e00\u8d77\u3002\u6b63\u5728\u5168\u795e\u8d2f\u6ce8\uff0c\u770b\u5f97\u7d27\u5f20\u4e4b\u9645\uff0c\u5ffd\u4f3c\u542c\u5f97\u4eba\u58f0\uff0c\u4e4c\u8499\u592b\u56de\u5934\u4e00\u770b\uff0c\u53ea\u89c1 \u4e00\u4e2a\u5e74\u7ea6\u4e94\u65ec\uff0c\u72b6\u5982\u4e61\u4e0b\u8001\u5934\u7684\u6c49\u5b50\uff0c\u53cc\u624b\u6367\u7740\u4e00\u4ef6\u4e1c\u897f\uff0c\u75be\u5954\u800c\u4e0a\u3002\u4e4c\u8499\u592b\u5927\u5403\u4e00\u60ca\uff0c\u8ba4 \u5f97\u8fd9\u8001\u6c49\u4e43\u662f\u7384\u673a\u9038\u58eb\u7684\u9996\u5f92\uff0c\u91d1\u521a\u624b\u8463\u5cb3\uff0c\u7384\u673a\u9038\u58eb\u95e8\u4e0b\uff0c\u82e5\u8bba\u529f\u529b\uff0c\u6570\u4ed6\u6700\u9ad8\u3002\u4e4c\u8499\u592b \u8fd8\u672a\u770b\u6e05\u695a\u4ed6\u6367\u7684\u662f\u4ec0\u4e48\u4e1c\u897f\uff0c\u53ea\u9053\u4ed6\u4e5f\u662f\u4e0a\u524d\u52a9\u6218\uff0c\u5fc3\u5ff5\u4e00\u52a8\uff0c\u60f3\u9053\uff1a\u201c\u5e08\u7236\u529b\u6218\u56db\u4eba\uff0c \u582a\u582a\u6253\u4e2a\u5e73\u624b\uff0c\u82e5\u518d\u52a0\u4e0a\u8463\u5cb3\uff0c\u53ea\u6050\u96be\u9003\u4e00\u8d25\uff0c\u6298\u4e86\u76db\u540d\u3002\u201d\u8463\u5cb3\u4ece\u4ed6\u8eab\u8fb9\u63a0\u8fc7\uff0c\u4e4c\u8499\u592b\u4e0d \u5047\u7d2f\u7d22\u53cd\u624b\u5c31\u662f\u4e00\u638c\uff0c\u5176\u4e2d\u6742\u4ee5\u4e00\u6307\u7985\u7684\u529f\u592b\uff0c\u8463\u5cb3\u559d\u9053\uff1a\u201c\u4f11\u5f97\u65e0\u793c\uff01\u201d\u8fd9\u4e00\u77ac\u95f4\u5ffd\u89c9\u5f97 \u6797\u4ed9\u97f5\u4e5f\u626f\u4e86\u4ed6\u4e00\u4e0b\uff0c\u4e4c\u8499\u592b\u5fc3\u4e2d\u4e00\u9707\u672a\u53ca\u7f29\u624b\uff0c\u53cc\u638c\u5df2\u4ea4\uff0c\u4ed6\u4e00\u6307\u7985\u7684\u529f\u529b\u672a\u900f\u6307\u5c16\uff0c\u88ab \u91d1\u521a\u624b\u4e00\u9707\uff0c\u767b\u65f6\u8dcc\u51fa\u4e00\u4e08\u5f00\u5916\u3002 \u3000\u3000\u53ea\u89c1\u8463\u5cb3\u75be\u5954\u800c\u4e0a\uff0c\u5ffd\u5730\u5c48\u4e86\u534a\u819d\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u5bb6\u5e08\u5dee\u9063\u5f1f\u5b50\u5411\u524d\u8f88\u8bf7\u5b89\u3002\u201d\u539f\u6765\u4ed6 \u624b\u4e2d\u6367\u7684\u4e43\u662f\u7384\u673a\u9038\u58eb\u7684\u62dc\u5323\u3002\u7167\u6c5f\u6e56\u89c4\u77e9\uff0c\u66ff\u50cf\u7384\u673a\u9038\u58eb\u8fd9\u6837\u4e00\u4f4d\u6b66\u6797\u5927\u5b97\u5e08\u6367\u62dc\u5323\u524d\u6765 \u62dc\u5c71\u7684\u4eba\uff0c\u4e4c\u8499\u592b\u7edd\u4e0d\u5e94\u963b\u6321\uff0c\u800c\u4e0a\u5b98\u5929\u91ce\u4e5f\u5fc5\u987b\u4eb2\u63a5\u62dc\u5323\u3002\u53ea\u662f\u4e0a\u5b98\u5929\u91ce\u6b63\u5728\u56db\u53e3\u5251\u5305\u56f4 \u4e4b\u4e0b\uff0c\u5982\u4f55\u80fd\u817e\u51fa\u624b\u6765\uff1f \u3000\u3000\u5ffd\u542c\u4e0a\u5b98\u5929\u91ce\u54c8\u54c8\u5927\u7b11\uff0c\u9053\uff1a\u201c\u4e0d\u5fc5\u591a\u793c\uff01\u201d\u53ea\u89c1\u4ed6\u53cc\u8896\u98de\u626c\uff0c\u84e6\u5730\u53cc\u624b\u4ece\u8896\u4e2d\u4f38\u51fa\uff0c \u6643\u773c\u4e4b\u95f4\uff0c\u5c31\u5411\u8c22\u3001\u53f6\u3001\u5f20\u3001\u4e91\u56db\u4eba\u6307\u4e86\u56db\u6307\uff0c\u8fd9\u6b63\u662f\u4ed6\u6700\u5389\u5bb3\u7684\u4e00\u6307\u7985\u529f\uff0c\u56db\u4eba\u90fd\u4e0d\u7531\u81ea \u5df1\u5730\u9000\u4e86\u4e00\u6b65\u3002\u4e0a\u5b98\u5929\u91ce\u98de\u8eab\u4e00\u8d77\uff0c\u957f\u8896\u4e0b\u5782\uff0c\u604d\u82e5\u957f\u86df\u5438\u6c34\uff0c\u7728\u773c\u4e4b\u95f4\uff0c\u5c31\u628a\u62dc\u5323\u4ece\u8463\u5cb3 \u624b\u4e2d\u5377\u53bb\uff0c\u8463\u5cb3\u4e0d\u7981\u9a87\u7136\u3002\u8fd9\u624b\u529f\u592b\u5e72\u51c0\u4e4b\u6781\uff0c\u4ece\u7edd\u4e0d\u53ef\u80fd\u7684\u60c5\u51b5\u4e0b\u53d6\u5230\u62dc\u5323\uff0c\u771f\u662f\u96be\u4ee5\u601d \u8bae\u3002\u8463\u5cb3\u65bd\u4e86\u4e00\u793c\uff0c\u521a\u521a\u7ad9\u8fc7\u4e00\u8fb9\uff0c\u5ffd\u542c\u5f97\u4e4c\u8499\u592b\u3001\u6797\u4ed9\u97f5\u540c\u58f0\u5c16\u53eb\uff0c\u5f20\u4e39\u67ab\u7684\u5b9d\u5251\u5df2\u63d2\u5230 \u4e86\u4e0a\u5b98\u5929\u91ce\u7684\u80a9\u5934\u3002 \u3000\u3000\u539f\u6765\u5f20\u4e39\u67ab\u719f\u4e60\u300a\u7384\u529f\u8981\u8bc0\u300b\uff0c\u300a\u7384\u529f\u8981\u8bc0\u300b\u8bb2\u7684\u662f\u6b66\u672f\u7684\u539f\u7406\uff0c\u4e00\u7406\u901a\uff0c\u767e\u7406\u901a\uff0c\u6240 \u4ee5\u719f\u4e60\u300a\u7384\u529f\u8981\u8bc0\u300b\u4e4b\u540e\uff0c\u5b66\u4ec0\u4e48\u529f\u592b\u90fd\u53ef\u4ee5\u65e0\u5e08\u81ea\u901a\uff0c\u4e8b\u534a\u529f\u500d\u3002\u5f20\u4e39\u67ab\u9002\u624d\u65c1\u89c2\uff0c\u770b\u4e0a \u5b98\u5929\u91ce\u8fd0\u7528\u5404\u79cd\u4e0a\u4e58\u529f\u592b\u529b\u538b\u8c22\u5929\u534e\u4e0e\u53f6\u76c8\u76c8\u53cc\u5251\u5408\u74a7\u7684\u5a01\u529b\uff0c\u5bf9\u4ed6\u7684\u6b66\u529f\u95e8\u8def\uff0c\u5df2\u7565\u77e5\u6897 \u6982\uff0c\u5230\u81ea\u5df1\u4eb2\u81ea\u63a5\u62db\u4e4b\u540e\uff0c\u66f4\u8fdb\u4e00\u6b65\uff0c\u6478\u5230\u4e86\u653b\u5b88\u5e94\u5bf9\u4e4b\u9053\uff0c\u53ea\u56e0\u529f\u529b\u5dee\u592a\u8fdc\uff0c\u8981\u4e0d\u7136\u65e9\u5c31 \u53ef\u4ee5\u53cd\u653b\u3002\u5982\u4eca\u4e0a\u5b98\u5929\u91ce\u901e\u5f3a\u597d\u80dc\uff0c\u5728\u56db\u5251\u56f4\u653b\u4e4b\u4e0b\u786c\u63a5\u62dc\u5323\uff0c\u77ac\u606f\u4e4b\u95f4\uff0c\u786c\u7528\u4e00\u6307\u7985\u529f\uff0c \u8fde\u8fde\u903c\u9000\u56db\u4eba\uff0c\u7cbe\u5999\u662f\u7cbe\u5999\u6781\u4e86\uff0c\u53ef\u662f\u5de6\u80a9\u5374\u9732\u51fa\u4e00\u4e1d\u7834\u7efd\uff0c\u5f20\u4e39\u67ab\u89d1\u4e2a\u6b63\u7740\uff0c\u4e58\u865a\u5373\u5165\uff0c \u5251\u5c16\u4e00\u52a8\uff0c\u70b9\u5230\u4e86\u4e0a\u5b98\u5929\u91ce\u5de6\u8fb9\u7684\u80a9\u4e95\u7a74\u3002\u53cc\u5251\u5408\u74a7\uff0c\u914d\u5408\u5f97\u4e0d\u5dee\u6beb\u5398\uff0c\u5f20\u4e39\u67ab\u7684\u5251\u62db\u65b9 \u51fa\uff0c\u4e91\u857e\u7684\u9752\u51a5\u5251\u4e5f\u81ea\u7136\u8ddf\u7740\u523a\u51fa\uff0c\u5237\u7684\u4e00\u58f0\uff0c\u5251\u5c16\u89e6\u5230\u4e86\u4e0a\u5b98\u5929\u91ce\u53f3\u8fb9\u7684\u80a9\u4e95\u7a74\u3002 \u3000\u3000\u201c\u80a9\u8fdb\u7a74\u201d\u4e43\u4eba\u8eab\u4e09\u5341\u516d\u9053\u5927\u7a74\u4e4b\u4e00\uff0c\u4e0e\u7435\u7436\u9aa8\u76f8\u8fde\uff0c\u88ab\u4eba\u70b9\u4e2d\uff0c\u53ea\u987b\u4ee5\u6307\u5934\u4e4b\u529b\uff0c\u91cd \u5219\u6b8b\u5e9f\uff0c\u8f7b\u4ea6\u762b\u75ea\u3002\u8c22\u5929\u534e\u5927\u559c\u4e0e\u53f6\u76c8\u76c8\u53cc\u5251\u6025\u65f6\uff0c\u4fbf\u8981\u8feb\u4e0a\u5b98\u5929\u91ce\u4f5c\u57ce\u4e0b\u4e4b\u76df\u3002\u54ea\u77e5\u4e0a\u5b98 \u5929\u91ce\u7684\u529f\u592b\u786e\u5df2\u7ec3\u5230\u4e86\u51fa\u795e\u5165\u5316\u7684\u5883\u754c\uff0c\u5f20\u4e39\u67ab\u5251\u5c16\u89e6\u53ca\u4ed6\u7684\u80a9\u5934\uff0c\u6b63\u60f3\u9053\u58f0\uff1a\u201c\u5f97\u7f6a\u201d\uff0c \u5ffd\u89c9\u4ed6\u7684\u80a9\u5934\u4e0b\u6c89\uff0c\u4e00\u80a1\u529b\u91cf\u5f80\u4e0b\u7275\u5f15\uff0c\u767d\u4e91\u5b9d\u5251\u7adf\u88ab\u9ecf\u7740\uff0c\u62bd\u4e0d\u51fa\u6765\uff0c\u53ea\u5f97\u7528\u52b2\u4e0b\u523a\uff0c\u53ef \u662f\u5251\u5c16\u6240\u89e6\uff0c\u8f6f\u7ef5\u7ef5\u7684\uff0c\u7adf\u523a\u4e0d\u7834\u4ed6\u7684\u8863\u88f3\u3002\u770b\u4e91\u857e\u65f6\u4ea6\u662f\u5982\u6b64\uff0c\u90a3\u53e3\u9752\u51a5\u5b9d\u5251\u9489\u5b9e\u4e0a\u5b98\u5929 \u91ce\u53f3\u8fb9\u7684\u80a9\u5934\uff0c\u4e5f\u4f3c\u7262\u7262\u9644\u7740\u4e00\u822c\u3002 \u3000\u3000\u8c22\u5929\u534e\u4e0e\u53f6\u76c8\u76c8\u5c1a\u672a\u77e5\u9053\u5176\u4e2d\u5df2\u751f\u53d8\u5316\uff0c\u89c1\u5f92\u513f\u5f97\u624b\uff0c\u5fc3\u4e2d\u5927\u559c\uff0c\u53cc\u5251\u6025\u8fdb\uff0c\u4ed6\u4eec\u4e8c\u4eba \u53cc\u5251\u5408\u74a7\u7684\u529f\u592b\u53c8\u6bd4\u5f20\u4e39\u67ab\u4e0e\u4e91\u857e\u5f3a\u4e86\u51e0\u500d\uff0c\u4f46\u89c1\u5251\u5149\u970d\u970d\uff0c\u5251\u6c14\u5982\u8679\uff0c\u500f\u5730\u5408\u6210\u4e00\u4e2a\u5149 \u73af\uff0c\u62e6\u8170\u4fbf\u65a9\u3002\u4e0a\u5b98\u5929\u91ce\u53eb\u58f0\u201c\u6765\u5f97\u597d\uff01\u201d\u53cc\u8896\u4e00\u6296\uff0c\u8c22\u3001\u53f6\u4e8c\u4eba\u7684\u53cc\u5251\uff0c\u88ab\u4ed6\u7684\u957f\u8896\u5305 \u7740\uff0c\u957f\u8896\u6325\u52a8\uff0c\u7adf\u53d1\u51fa\u4e00\u80a1\u52b2\u529b\uff0c\u968f\u7740\u5251\u52bf\uff0c\u5de6\u53f3\u79fb\u52a8\uff0c\u5c06\u4e4b\u5316\u89e3\u3002 \u3000\u3000\u8fd9\u4e00\u6765\uff0c\u53cc\u65b9\u6210\u4e86\u50f5\u6301\u4e4b\u5c40\uff0c\u4e0a\u5b98\u5929\u91ce\u7528\u53cc\u80a9\u627f\u63a5\u5f20\u3001\u4e91\u4e8c\u4eba\u7684\u9a8f\u4e0d\uff0c\u7528\u53cc\u8896\u62b5\u6321\u8c22\u3001 \u53f6\u4e8c\u4eba\u53cc\u5251\uff0c\u5373\u662f\u4ee5\u4e00\u4eba\u7684\u5185\u52b2\u6765\u62b5\u5fa1\u56db\u4e2a\u4eba\u7684\u4e24\u5bf9\u53cc\u5251\u5408\u74a7\u7684\u5a01\u529b\uff0c\u4e0a\u5b98\u5929\u91ce\u7684\u6b66\u529f\u867d\u5df2 \u7ec3\u5230\u4e86\u901a\u7384\u7684\u5883\u754c\uff0c\u4e5f\u611f\u5403\u529b\u975e\u5e38\u3002\u4f46\u8c22\u3001\u53f6\u3001\u5f20\u3001\u4e91\u56db\u4eba\u4e5f\u88ab\u4ed6\u7684\u5185\u52b2\u7275\u5f15\uff0c\u56db\u53e3\u957f\u5251\u90fd \u6446\u8131\u4e0d\u5f00\u3002 \u3000\u3000\u8fd9\u5f62\u52bf\u9669\u6076\u4e4b\u6781\uff0c\u7aef\u7684\u662f\u52bf\u6210\u9a91\u864e\uff0c\u8c01\u6709\u534a\u70b9\u4e0d\u614e\uff0c\u90fd\u6709\u6027\u547d\u4e4b\u5371\uff0c\u4e24\u5bb6\u5f1f\u5b50\u90fd\u60ca\u5fc3\u9a87 \u76ee\uff0c\u770b\u5f97\u51b7\u6c57\u76f4\u6d41\uff0c\u53ef\u662f\u8c01\u4e5f\u6ca1\u6709\u90a3\u6837\u9ad8\u7684\u529f\u592b\uff0c\u6562\u4e0a\u524d\u5316\u89e3\u3002 \u3000\u3000\u6b63\u5728\u6781\u7aef\u7d27\u5f20\u4e4b\u9645\uff0c\u5ffd\u89c1\u4e0a\u5b98\u5929\u91ce\u9000\u4e86\u4e00\u6b65\uff0c\u53f3\u80a9\u4e00\u6c89\uff0c\u4e91\u857e\u8eab\u8eaf\u98a4\u6296\uff0c\u5251\u5c16\u5728\u4ed6\u80a9\u4e0a \u8df3\u52a8\uff0c\u4f46\u8c22\u5929\u534e\u4e0e\u53f6\u76c8\u76c8\u5374\u8ddf\u7740\u8feb\u524d\u4e00\u6b65\uff0c\u9762\u8272\u51dd\u91cd\uff0c\u663e\u5f97\u751a\u662f\u7528\u529b\u3002\u4e91\u6f84\u62c5\u5fc3\u7231\u5973\uff0c\u4e0d\u7531 \u81ea\u5df1\u5730\u53eb\u51fa\u58f0\u6765\uff0c\u58f0\u72b9\u672a\u6b47\uff0c\u5ffd\u542c\u5f97\u54c8\u54c8\u7684\u5927\u7b11\u4e4b\u58f0\uff0c\u5c71\u9e23\u8c37\u5e94\u573a\u4e2d\u7a81\u7136\u591a\u4e86\u4e00\u4e2a\u8001\u5934\u3002 \u3000\u3000\u8fd9\u8001\u5934\u76f8\u8c8c\u6e05\u77cd\uff0c\u987b\u7709\u7686\u767d\uff0c\u4f46\u9762\u8272\u7ea2\u6da6\uff0c\u5f62\u5982\u6ee1\u6708\uff0c\u5374\u4f3c\u5a74\u513f\uff0c\u7aef\u7684\u662f\u97f3\u989c\u9e64\u53d1\uff0c\u9053 \u9aa8\u4ed9\u98ce\uff0c\u5728\u573a\u8bf8\u4eba\uff0c\u4e2a\u4e2a\u90fd\u6709\u4e00\u8eab\u60ca\u4eba\u7684\u6b66\u529f\uff0c\u5374\u4e0d\u77e5\u4ed6\u662f\u600e\u4e48\u6765\u7684\u3002 \u3000\u3000\u8fd9\u8001\u5934\u6b63\u662f\u7384\u673a\u9038\u58eb\u3002\u6f6e\u97f3\u548c\u5c1a\u4e0e\u4e91\u6f84\u559c\u4e0d\u81ea\u80dc\uff0c\u521a\u53eb\u5f97\u4e00\u58f0\u5e08\u7236\uff0c\u53ea\u89c1\u7384\u673a\u9038\u58eb\u5df2\u98d8 \u7136\u8fdb\u5165\u6597\u573a\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u8001\u670b\u53cb\uff0c\u4e3a\u5c0f\u8f88\u52a8\u4e86\u771f\u6c14\u6709\u4ec0\u4e48\u610f\u601d\uff1f\u201d\u4ed6\u624b\u62c5\u62c2\u5c18\uff0c\u84e6\u7136\u51fa \u624b\uff0c\u5728\u56db\u53e3\u957f\u5251\u4e0a\u5404\u62c2\u4e00\u4e0b\uff0c\u53ea\u542c\u5f97\u94ee\u94ee\u51e0\u58f0\uff0c\u56db\u53e3\u5251\u767b\u65f6\u90fd\u53cd\u5f39\u8d77\u6765\uff0c\u7384\u673a\u9038\u58eb\u559d\u9053\uff1a \u201c\u4f60\u4eec\u5bf9\u957f\u8f88\u4f11\u5f97\u65e0\u793c\uff0c\u90fd\u9000\u4e0b\u542c\u6211\u5429\u5490\uff01\u201d \u3000\u3000\u4e94\u4eba\u90fd\u5982\u91ca\u91cd\u8d1f\u3002\u539f\u6765\u521a\u624d\u6b63\u5230\u4e86\u6700\u7d27\u8981\u7684\u5173\u5934\uff0c\u4e91\u857e\u7684\u529f\u592b\u6700\u5f31\uff0c\u88ab\u4e0a\u5b98\u5929\u91ce\u53f3\u80a9\u7684 \u7275\u5f15\u4e4b\u529b\u6240\u5438\uff0c\u51e0\u4e4e\u5c31\u8981\u62b5\u6321\u4e0d\u4f4f\uff0c\u4f46\u8c22\u3001\u53f6\u4e8c\u4eba\u4e58\u673a\u8fdb\u903c\uff0c\u5374\u5360\u4e86\u4e0a\u98ce\u3002\u82e5\u7136\u7384\u673a\u9038\u58eb\u4e0d \u6765\u5f88\u53ef\u80fd\u4e24\u8d25\u4ff1\u4f24\uff01 \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4e09\u5341\u5e74\u91cd\u4f1a\uff0c\u4f60\u679c\u7136\u7ec3\u5230\u4e86\u901a\u7384\u5999\u5883\uff0c\u6709\u5f92\u5982\u6b64\uff0c\u4e3a\u5e08\u53ef \u77e5\uff0c\u8fd9\u6b66\u6797\u76df\u4e3b\u7684\u5b9d\u5ea7\uff0c\u6211\u4e5f\u4e0d\u518d\u4e0e\u4f60\u4e89\u4e86\uff01\u201d\u7384\u673a\u9038\u58eb\u7b11\u9053\uff1a\u201c\u8001\u5144\u4f55\u5fc5\u592a\u8c26\uff0c\u8bf4\u6765\u6211\u8be5 \u6211\u7518\u62dc\u4e0b\u98ce\u3002\u201d\u7384\u673a\u9038\u58eb\u7a77\u4e00\u751f\u5fc3\u529b\uff0c\u521b\u4e86\u53cc\u5251\u5408\u74a7\u7684\u5251\u6cd5\uff0c\u81ea\u4ee5\u4e3a\u5929\u4e0b\u65e0\u654c\uff0c\u54ea\u77e5\u8c22\u3001\u53f6 \u4e8c\u4eba\u53cc\u5251\u5408\u74a7\uff0c\u7adf\u88ab\u4e0a\u5b98\u5929\u91ce\u514b\u4f4f\uff0c\u518d\u52a0\u4e0a\u4e86\u5f20\u3001\u4e91\u4e8c\u4eba\uff0c\u624d\u80fd\u548c\u4ed6\u6253\u6210\u5e73\u624b\uff0c\u6545\u6b64\u7384\u673a\u9038 \u58eb\u5bf9\u4ed6\u4e5f\u662f\u771f\u5fc3\u4f69\u670d\uff0c\u5e76\u975e\u5ba2\u5957\u3002 \u3000\u3000\u4e24\u4eba\u6b63\u5728\u60fa\u60fa\u76f8\u60dc\uff0c\u4e92\u9053\u4f69\u670d\u4e4b\u9645\uff0c\u5ffd\u542c\u5f97\u4e00\u58f0\u6e05\u5578\uff0c\u9690\u82e5\u9f99\u541f\uff0c\u970e\u5ffd\u4e4b\u95f4\uff0c\u573a\u4e2d\u53c8\u591a \u4e86\u4e00\u4e2a\u4eba\uff0c\u5f20\u4e39\u67ab\u4e00\u770b\uff0c\u6b63\u662f\u7d2b\u7af9\u6797\u4e2d\u7684\u90a3\u4e2a\u8001\u5a46\u5a46\u3002\u8fd9\u970e\u90a3\u95f4\uff0c\u53ea\u89c1\u4e0a\u5b98\u5929\u91ce\u9762\u8272\u500f\u53d8\uff0c \u4f4e\u58f0\u541f\u9053\uff1a\u201c\u96be\u5fd8\u6069\u6028\u96be\u5fd8\u4f60\uff0c\u53ea\u4e3a\u60c5\u75f4\u53ea\u4e3a\u771f\u3002\u201d\u5f20\u4e39\u67ab\u8131\u53e3\u95ee\u9053\uff1a\u201c\u4f60\u4eec\u8c01\u662f\u5251\u5ba2\uff0c\u8c01 \u662f\u5f3a\u76d7\uff1f\u201d\u8c22\u5929\u534e\u5927\u5403\u4e00\u60ca\u5fc3\u9053\uff1a\u201c\u5f20\u4e39\u67ab\u806a\u660e\u7edd\u9876\uff0c\u4f55\u4ee5\u5728\u4e24\u4f4d\u8001\u524d\u8f88\u9762\u524d\u51fa\u6b64\u65e0\u793c\u4e4b \u8a00\uff1f\u201d\u89c1\u4ed6\u4e00\u526f\u5931\u9b42\u843d\u9b44\u7684\u6837\u5b50\uff0c\u83ab\u540d\u5176\u5999\uff0c\u751a\u662f\u60f6\u6050\u3002 \u3000\u3000\u53ea\u542c\u4e0a\u5b98\u5929\u91ce\u5927\u7b11\u9053\uff1a\u201c\u5e84\u4e3b\u6653\u68a6\u8ff7\u8774\u8776\uff0c\u77ed\u68a6\u7531\u6765\u6700\u6613\u9192\u3002\u4f55\u5fc5\u518d\u95ee\u8c01\u662f\u5251\u5ba2\uff0c\u8c01\u662f \u5f3a\u76d7\uff1f\u4eca\u65e5\u5f3a\u76d7\u5251\u5ba2\u4e0d\u6253\u4e0d\u6210\u76f8\u8bc6\uff0c\u6211\u5728\u8fd9\u53a2\u8d54\u793c\u5566\uff01\u201d\u84e6\u7136\u62e2\u638c\u4e00\u6325\uff0c\u5341\u6307\u6697\u6697\u8fd0\u529f\u52b2\uff0c \u4f7f\u51fa\u6700\u5389\u5bb3\u7684\u4e00\u6307\u7985\u529f\u3002 \u3000\u3000\u539f\u6765\u4e0a\u5b98\u5929\u91ce\u867d\u7136\u6e10\u609f\uff0c\u4f46\u5fc3\u4e2d\u8fd8\u6709\u4e00\u70b9\u597d\u80dc\u4e4b\u5ff5\uff0c\u4ed6\u672c\u6765\u5df2\u613f\u7518\u62dc\u4e0b\u98ce\uff0c\u5ffd\u89c1\u4e09\u5341\u5e74 \u524d\u7684\u610f\u4e2d\u4eba\u7a81\u7136\u6765\u5230\u4f3c\u7b11\u975e\u7b11\uff0c\u76ee\u5149\u597d\u50cf\u770b\u7740\u4ed6\u7684\u5bf9\u5934\uff0c\u4e0d\u7531\u5f97\u5fc3\u4e2d\u4e00\u9178\uff0c\u4e89\u80dc\u4e4b\u5fc3\u5ffd\u8d77\uff0c \u7adf\u7136\u8fd8\u8981\u518d\u8bd5\u4e00\u8bd5\u7384\u673a\u9038\u58eb\u3002 \u3000\u3000\u7384\u673a\u9038\u58eb\u5fae\u5fae\u4e00\u7b11\uff0c\u5408\u638c\u4e00\u63d6\uff0c\u53ea\u89c1\u4e0a\u5b98\u5929\u91ce\u7684\u8863\u8896\u597d\u50cf\u4e00\u6e56\u6625\u6c34\u88ab\u98ce\u5439\u522e\uff0c\u8361\u8d77\u5fae \u6ce2\uff0c\u98d8\u98d8\u6b32\u8d77\u3002\u7384\u673a\u9038\u58eb\u7a81\u7136\u6643\u4e86\u4e24\u6643\uff0c\u62f1\u624b\u8bf4\u9053\uff1a\u201c\u8001\u5144\u7684\u6b66\u529f\u5929\u4e0b\u7b2c\u4e00\uff0c\u6211\u7518\u62dc\u4e0b\u98ce \u5566\uff01\u201d\u4e00\u8f6c\u8eab\u4fbf\u8981\u4e0b\u5c71\u3002 \u3000\u3000\u65c1\u4eba\u770b\u4e0d\u6e05\u695a\uff0c\u90a3\u8001\u5a46\u5a46\u548c\u4e0a\u5b98\u5929\u91ce\u81ea\u5df1\u5374\u662f\u5fc3\u4e2d\u660e\u767d\uff1a\u90a3\u662f\u7384\u673a\u9038\u58eb\u6545\u610f\u8ba9\u56de\u4e00\u62db\u3002 \u4e0a\u5b98\u5929\u91ce\u51fa\u6307\u5728\u5148\uff0c\u5374\u88ab\u7384\u673a\u9038\u58eb\u7684\u638c\u529b\u5b8c\u5168\u5316\u89e3\uff0c\u4f59\u52bf\u672a\u5c3d\uff0c\u638c\u529b\u9707\u8361\uff0c\u6545\u6b64\u80fd\u5c06\u4e0a\u5b98\u5929 \u91ce\u7684\u8863\u8896\u6380\u8d77\u3002\u800c\u540e\u6765\u7384\u673a\u9038\u58eb\u7684\u8eab\u5f62\u6643\u52a8\uff0c\u72b6\u4f3c\u4e0d\u80dc\u6307\u529b\uff0c\u90a3\u5374\u662f\u6545\u610f\u88c5\u51fa\u6765\u7684\u3002 \u3000\u3000\u7384\u673a\u9038\u58eb\u8ba9\u56de\u4e00\u62db\uff0c\u8f6c\u8eab\u6b32\u8d70\uff0c\u90a3\u8001\u5a46\u5a46\u5ffd\u7136\u4e00\u8dc3\u800c\u524d\uff0c\u7af9\u6756\u4e00\u52fe\uff0c\u52fe\u4f4f\u4e86\u7384\u673a\u9038\u58eb\u7684 \u8863\u895f\u3002\u7384\u673a\u9038\u58eb\u82e6\u7b11\u9053\uff1a\u201c\u6211\u5df2\u7ecf\u670d\u8f93\u5566\uff0c\u4f60\u8fd8\u7f20\u6211\u4f5c\u4ec0\u4e48\uff1f\u201d\u4e0a\u5b98\u5929\u91ce\u53eb\u9053\uff1a\u201c\u7384\u673a\u8001 \u513f\uff0c\u6211\u4e0d\u9886\u4f60\u8fd9\u4e2a\u4eba\u60c5\uff0c\u8be5\u8d70\u7684\u662f\u6211\uff0c\u4f60\u7559\u5728\u8fd9\u91cc\uff0c\u4f46\u613f\u4f60\u597d\u597d\u770b\u5f85\u5979\u5427\uff01\u201d \u3000\u3000\u90a3\u8001\u5a46\u5a46\u4f38\u624b\u4e00\u62db\uff0c\u4e0a\u5b98\u5929\u91ce\u6b32\u8d70\u53c8\u505c\uff0c\u53ea\u542c\u5f97\u90a3\u8001\u5a46\u5a46\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4eba\u90fd\u662f\u4e0d\u5fc5 \u8d70\uff0c\u8bba\u8d77\u6b66\u529f\uff0c\u4f60\u4eec\u4e24\u4eba\u90fd\u662f\u5929\u4e0b\u7b2c\u4e00\uff0c\u4e0d\u5fc5\u518d\u4e89\u4e5f\u4e0d\u5fc5\u518d\u8ba9\u5566\u3002\u201d\u8fd9\u8001\u5a46\u5a46\u6240\u8bf4\u7684\u5012\u975e\u504f \u8892\uff0c\u987b\u77e5\u4e0a\u5b98\u5929\u91ce\u6076\u6597\u4e86\u534a\u65e5\u6709\u591a\uff0c\u5185\u52b2\u81ea\u662f\u6709\u6240\u635f\u8017\uff0c\u8981\u4e0d\u7136\u4ee5\u4ed6\u82e6\u5fc3\u6240\u7ec3\u7684\u4e00\u6307\u7985\u529f\u5bf9 \u6297\u7384\u673a\u9038\u58eb\u7684\u91d1\u521a\u638c\u529b\uff0c\u5b9e\u662f\u5c1a\u672a\u53ef\u77e5\u3002 \u3000\u3000\u7384\u673a\u9038\u58eb\u7709\u5934\u4e00\u76b1\uff0c\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u8981\u4e0d\u662f\u4f60\u7acb\u5fc3\u8981\u770b\u6211\u4eec\u76f8\u6597\uff0c\u8c01\u613f\u610f\u82e5\u8fd9\u9ebb\u70e6\u3002\u201d\u53ea \u542c\u5f97\u90a3\u8001\u5a46\u5a46\u5ffd\u800c\u53f9\u53e3\u6c14\u8bf4\u9053\uff1a\u201c\u6643\u773c\u4e4b\u95f4\u4fbf\u8fc7\u4e86\u4e09\u5341\u5e74\uff0c\u54b1\u4eec\u4e09\u4e2a\u4eba\u90fd\u8001\u5566\u3002\u5e74\u8f7b\u65f6\u5019\u7684 \u80e1\u95f9\uff0c\u73b0\u5728\u60f3\u6765\uff0c\u5b9e\u662f\u751a\u662f\u597d\u7b11\u3002\u4eba\u5bff\u51e0\u4f55\uff1f\u518d\u80e1\u95f9\u4e0b\u53bb\uff0c\u5f92\u4e3a\u540e\u4e16\u6240\u7b11\u3002\u5c11\u5e74\u65f6\u89e3\u4e0d\u5f00\u7684 \u7ed3\uff0c\u8001\u5e74\u65f6\u603b\u53ef\u89e3\u5f00\u3002\u7384\u673a\u54e5\u54e5\uff0c\u4e0a\u5b98\u5f1f\u5f1f\uff0c\u54b1\u4eec\u4e09\u4eba\u4ece\u4eca\u4e4b\u540e\u4e0d\u518d\u5206\u5f00\uff0c\u5171\u7814\u6700\u4e0a\u4e58\u7684\u6b66 \u529f\uff0c\u7559\u4e00\u70b9\u5fc3\u5f97\u7ed9\u540e\u8f88\uff0c\u5c82\u4e0d\u662f\u751a\u597d\uff1f\u201d\u7384\u673a\u9038\u58eb\u542c\u5979\u8bf4\u5f97\u6781\u4e3a\u8bda\u5976\uff0c\u7981\u4e0d\u4f4f\u5fc3\u4e2d\u4e00\u52a8\uff0c\u4e09 \u5341\u5e74\u6765\u8ba8\u538c\u5979\u7684\u5fc3\u60c5\uff0c\u7adf\u88ab\u8fd9\u4e00\u573a\u8bdd\u5b8c\u5168\u6e05\u89e3\u3002\u4e0a\u5b98\u5929\u91ce\u66f4\u662f\u5fc3\u60c5\u6fc0\u8361\uff0c\u542c\u5979\u201c\u54e5\u54e5\u3001\u5f1f \u5f1f\u201d\u53eb\u5f97\u751a\u662f\u4eb2\u6635\uff0c\u4eff\u4f5b\u8fd8\u662f\u5f53\u5e74\u7684\u5c0f\u59ae\u5b50\u8427\u97f5\u5170\uff0c\u5fcd\u4e0d\u4f4f\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u5979\u8bf4\u7684\u679c\u7136\u6bd4\u6211\u8981 \u609f\u5f97\u5f7b\u5e95\uff0c\u5c11\u5e74\u65f6\u89e3\u4e0d\u5f00\u7684\u7ed3\uff0c\u8fd9\u4e2a\u65f6\u5019\u6839\u672c\u5c31\u4e0d\u6210\u95ee\u9898\u5566\u3002\u201d\u4ed6\u660e\u767d\u8427\u97f5\u5170\u6240\u8bf4\u7684 \u201c\u7ed3\u201d\uff0c\u90a3\u81ea\u7136\u662f\u6307\u4ed6\u4eec\u4e09\u4eba\u4e4b\u95f4\u7684\u7231\u6068\u7ea0\u7f20\uff0c\u800c\u4eca\u5927\u5bb6\u90fd\u5df2\u5230\u4e86\u53e4\u7a00\u4e4b\u5e74\uff0c\u7edd\u4e0d\u4f1a\u518d\u8c08\u5a5a \u8bba\u5ac1\uff0c\u90a3\u4e48\u4e09\u4e2a\u4eba\u82e5\u90fd\u6210\u4e3a\u77e5\u5df1\u540c\u53c2\u6b66\u5b66\uff0c\u4e0d\u5206\u5f7c\u6b64\uff0c\u8fd9\u79cd\u611f\u60c5\u7684\u5883\u754c\uff0c\u5c82\u662f\u5f53\u5e74\u6240\u80fd\u4f01 \u53ca\uff1f \u3000\u3000\u4f60\u9053\u8427\u97f5\u5170\u4f55\u4ee5\u80fd\u7a81\u7136\u8bf4\u51fa\u6b64\u79cd\u201c\u609f\u9053\u201d\u4e4b\u8a00\uff0c\u539f\u6765\u5979\u5728\u7d2b\u7af9\u6797\u4e2d\u4e09\u5341\u5e74\uff0c\u7ecf\u5386\u5c3d\u5404\u79cd \u5fc3\u60c5\u7684\u6ce2\u52a8\uff0c\u59cb\u800c\u5bf9\u7384\u673a\u9038\u58eb\u6124\u6068\uff0c\u5bf9\u4e0a\u5b98\u5929\u91ce\u5931\u671b\uff0c\u7ec8\u800c\u4e5f\u6e10\u6e10\u60f3\u5230\u8fd9\u79cd\u79cd\u7ea0\u7eb7\uff0c\u90fd\u662f\u56e0 \u81ea\u5df1\u865a\u8363\u4e00\u5ff5\u800c\u8d77\u3002\u5230\u4e86\u4e09\u5341\u5e74\u7684\u671f\u9650\u5c06\u5230\uff0c\u6094\u6068\u4e4b\u5ff5\u66f4\u6d53\uff0c\u60f3\u8d77\u4e0d\u5e94\u56e0\u4e3a\u81ea\u5df1\u81f4\u4ee4\u4e24\u4e2a\u6b66 \u6797\u5f02\u4eba\u7ec8\u751f\u7ed3\u6028\uff0c\u6545\u6b64\u6025\u6025\u8d76\u6765\uff0c\u5374\u53c8\u76ee\u51fb\u4ed6\u4eec\u4e92\u76f8\u8c26\u8ba9\u7684\u4e00\u5e55\uff0c\u56e0\u800c\u7acb\u5fc3\u66ff\u4ed6\u4eec\u5316\u89e3\u3002 \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u6b63\u5728\u5fc3\u601d\u5982\u6f6e\uff0c\u5ffd\u89c1\u5f92\u5f1f\u6797\u4ed9\u97f5\u4e0a\u524d\u7980\u9053\uff1a\u201c\u5e08\u5c0a\u8bf7\u4f60\u770b\u770b\u8499\u592b\u5e08\u5144\u3002\u201d\u4e0a\u5b98 \u5929\u91ce\u659c\u773c\u4e00\u77a5\uff0c\u53ea\u89c1\u4e4c\u8499\u592b\u76d8\u819d\u5750\u5728\u5730\u4e0b\uff0c\u5934\u4e0a\u5192\u51fa\u70ed\u817e\u817e\u7684\u767d\u6c14\u3002 \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u5403\u4e86\u4e00\u60ca\uff0c\u8fc5\u5373\u53c8\u73b0\u51fa\u8be7\u5f02\u7684\u795e\u8272\uff0c\u9053\uff1a\u201c\u539f\u6765\u4ed6\u662f\u4e2d\u4e86\u91d1\u521a\u638c\u4e86\u3002\u201d\u8463\u5cb3\u751a \u4e3a\u60f6\u6050\uff0c\u534a\u5c48\u7740\u819d\uff0c\u7980\u544a\u7384\u673a\u9038\u58eb\u9053\uff1a\u201c\u662f\u5f1f\u5b50\u5448\u9012\u62dc\u5323\uff0c\u4e00\u65f6\u4e0d\u614e\uff0c\u6253\u4f24\u4e86\u4ed6\uff0c\u5f1f\u5b50\u613f\u4ee5 \u672c\u8eab\u529f\u529b\uff0c\u52a9\u4ed6\u590d\u539f\u3002\u201d\u7384\u673a\u9038\u58eb\u6447\u4e86\u6447\u5934\uff0c\u5ffd\u800c\u8bf4\u9053\uff1a\u201c\u4e0a\u5b98\u8001\u5144\uff0c\u8fd9\u56de\u4ffa\u662f\u771f\u7684\u670d\u4e86\u3002 \u60f3\u4e0d\u5230\u4f60\u7684\u5f92\u5f1f\u4e5f\u6709\u8fd9\u6837\u7cbe\u5999\u7684\u5185\u529f\uff0c\u8fd9\u624d\u662f\u771f\u6b63\u4e0a\u4e58\u7684\u529f\u592b\uff0c\u6bd4\u5c06\u8d77\u6765\uff0c\u6211\u4ee5\u524d\u6240\u5b66\u7684\u53ea \u80fd\u7b97\u662f\u91ce\u72d0\u7985\u4e86\u3002\u201d \u3000\u3000\u6b64\u8a00\u4e00\u51fa\uff0c\u4e24\u6d3e\u95e8\u4e0b\u5f1f\u5b50\u65e0\u4e0d\u9a87\u5f02\uff0c\u4e0d\u77e5\u7384\u673a\u9038\u58eb\u8bf4\u7684\u7a76\u662f\u4ec0\u4e48\u529f\u592b\uff1f\u4e0a\u5b98\u5929\u91ce\u82e6\u7b11 \u9053\uff1a\u201c\u82e5\u7136\u4f60\u7684\u662f\u91ce\u72d0\u7985\uff0c\u6211\u7684\u5c31\u8fde\u65c1\u95e8\u5de6\u9053\u4e5f\u8c08\u4e0d\u4e0a\u3002\u201d\u7f13\u7f13\u8d70\u5230\u4e4c\u8499\u592b\u9762\u524d\uff0c\u4f38\u624b\u63a2 \u8109\uff0c\u8138\u4e0a\u795e\u8272\u8d8a\u53d1\u60ca\u5947\u3002\u987b\u77e5\u91d1\u521a\u638c\u529b\uff0c\u975e\u540c\u5c0f\u53ef\uff0c\u4e4c\u8499\u592b\u786c\u63a5\u4e86\u4e00\u638c\uff0c\u4ee5\u4ed6\u7684\u529f\u529b\uff0c\u6700\u5c11 \u8981\u4e03\u65e5\u65b9\u80fd\u590d\u539f\uff0c\u800c\u73b0\u5728\u4e0a\u5b98\u5929\u91ce\u63a2\u4ed6\u7684\u8109\u606f\uff0c\u53d1\u89c9\u4ed6\u6c14\u8840\u8fd0\u884c\uff0c\u81ea\u7136\u8212\u7545\uff0c\u7adf\u662f\u5373\u5c06\u590d \u539f\u3002\u7ec6\u5bdf\u4e4b\u4e0b\uff0c\u4e4c\u8499\u592b\u6240\u8fd0\u7684\u6c14\u529f\u7adf\u7136\u4e0d\u662f\u81ea\u5df1\u6240\u4f20\u7684\u5fc3\u6cd5\uff0c\u4ed6\u529f\u529b\u5e76\u6ca1\u6709\u7a81\u7136\u52a0\u6df1\uff0c\u53ea\u56e0 \u8fd0\u6c14\u5f97\u6cd5\uff0c\u800c\u5c31\u81ea\u7136\u800c\u7136\u80fd\u628a\u91d1\u521a\u638c\u529b\u9707\u8361\u7684\u4e94\u810f\u8c03\u6574\u590d\u539f\uff0c\u8fd9\u771f\u771f\u662f\u4e0d\u53ef\u601d\u8bae\uff01 \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u82e6\u7b11\u4e00\u58f0\uff0c\u731b\u5730\u4f38\u638c\u5728\u4e4c\u8499\u592b\u80cc\u5fc3\u62cd\u4e86\u4e00\u4e0b\uff0c\u559d\u58f0\u201c\u8d77\uff01\u201d\u4e4c\u8499\u592b\u679c\u7136\u5e94\u58f0\u800c \u8d77\uff0c\u884c\u52a8\u5982\u5e38\u3002\u4e0a\u5b98\u5929\u91ce\u7528\u672c\u8eab\u7684\u529f\u529b\uff0c\u52a9\u4ed6\u5373\u523b\u590d\u539f\u4e4b\u540e\uff0c\u7acb\u5373\u95ee\u9053\uff1a\u201c\u662f\u54ea\u4f4d\u9ad8\u4eba\u6307\u70b9 \u4f60\u7684\uff1f\u4f60\u53ef\u4ee5\u53e6\u6295\u660e\u5e08\uff0c\u4e0d\u5fc5\u518d\u5728\u6211\u7684\u95e8\u4e0b\u4e86\uff01\u201d\u4e4c\u8499\u592b\u60f6\u6050\u4e4b\u6781\uff0c\u9053\uff1a\u201c\u5f1f\u3001\u5f1f\u5b50\u8fd0\u7528\u5916 \u6d3e\u529f\u592b\uff0c\u6c42\u5e08\u5c0a\u6055\u7f6a\u3002\u5f1f\u5b50\u5e76\u65e0\u522b\u4eba\u6307\u70b9\u3002\u201d\u4e0a\u5b98\u5929\u91ce\u51b7\u7b11\u9053\uff1a\u201c\u6ca1\u4eba\u6307\u70b9\uff0c\u4f60\u65e0\u5e08\u81ea\u901a \u5417\uff1f\u201d\u5f20\u4e39\u67ab\u95ea\u8eab\u8d8a\u4f17\u800c\u51fa\uff0c\u5148\u5411\u5e08\u7956\u53e9\u8bf7\u5b89\u3002\u7384\u673a\u9038\u58eb\u95ee\u9053\uff1a\u201c\u8fd9\u662f\u8c01\u6536\u7684\u5f1f\u5b50\uff1f\u201d\u8c22\u5929 \u534e\u9053\uff1a\u201c\u8fd9\u662f\u6211\u6536\u7684\u5f1f\u5b50\u5f20\u4e39\u67ab\u3002\u201d\u7384\u673a\u9038\u58eb\u7b11\u9053\uff1a\u201c\u4f60\u6536\u7684\u5f1f\u5b50\u6bd4\u6211\u6536\u7684\u5f1f\u5b50\u5f3a\u5f97\u591a\u4e86\u3002 \u5c06\u6765\u4ed6\u7684\u6210\u5c31\uff0c\u4e0d\u4f46\u5728\u4f60\u4eec\u4e4b\u4e0a\uff0c\u8fde\u6211\u4e5f\u4e0d\u5982\u4ed6\u3002\u201d\u8c22\u5929\u534e\u53c8\u60ca\u53c8\u559c\uff0c\u9053\uff1a\u201c\u5e08\u7236\u592a\u5938\u5956\u4ed6 \u4e86\u3002\u201d\u5f20\u4e39\u67ab\u5411\u5e08\u7956\u53e9\u4e86\u4e2a\u5934\uff0c\u53c8\u5411\u4e0a\u5b98\u5929\u91ce\u65bd\u793c\u8bf4\u9053\uff1a\u201c\u6211\u77e5\u9053\u662f\u8c01\u6307\u70b9\u4ed6\u7684\u3002\u201d\u4e0a\u5b98\u5929 \u91ce\u9053\uff1a\u201c\u662f\u8c01\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u90a3\u662f\u767e\u5e74\u524d\u7684\u53e4\u4eba\u3002\u201d\u4e0a\u5b98\u5929\u91ce\u9053\uff1a\u201c\u80e1\u8bf4\u3002\u201d\u5411\u7384\u673a\u9038\u58eb \u9053\uff1a\u201c\u4f60\u7684\u5f92\u5b59\u5728\u6211\u7684\u77f3\u5ba4\u4e03\u65e5\uff0c\u6211\u7ed9\u4ed6\u770b\u4e86\u8109\u8c61\uff0c\u4f3c\u662f\u60a3\u6709\u5fc3\u75c5\uff0c\u795e\u5fd7\u672a\u6e05\uff0c\u4f60\u5f97\u597d\u597d\u7ed9 \u4ed6\u6cbb\u4e00\u6cbb\u3002\u201d\u5f20\u4e39\u67ab\u5ffd\u800c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u8c01\u8bf4\u6211\u795e\u5fd7\u672a\u6e05\uff1f\u6211\u77e5\u9053\u4f60\u662f\u60c5\u75f4\uff0c\u4e09\u5341\u5e74\u524d\u662f\u4e2a\u5f3a \u76d7\u3002\u4f46\u4f60\u53ea\u987e\u81ea\u5df1\u75f4\u60c5\uff0c\u5374\u4e0d\u7406\u4f60\u7684\u95e8\u5f92\u7684\u6b7b\u6d3b\uff0c\u786c\u751f\u751f\u8981\u62c6\u6563\u4ed6\u4eec\uff0c\u6211\u5fc3\u6709\u4e0d\u670d\uff0c\u6240\u4ee5\u8bf7 \u90a3\u4f4d\u53e4\u4eba\u6307\u70b9\u4ed6\u4e86\u3002\u201d\u6b64\u8a00\u4e00\u51fa\uff0c\u4f17\u4eba\u53c8\u662f\u5927\u60ca\u5931\u8272\uff0c\u60f3\u4e0d\u5230\u5f20\u4e39\u67ab\u5bf9\u4e0a\u5b98\u5929\u91ce\u5982\u6b64\u65e0\u793c\uff0c \u7384\u673a\u9038\u58eb\u5374\u4e0d\u8bf4\u8bdd\uff0c\u4f3c\u662f\u6b63\u5728\u7528\u5fc3\u731c\u5ea6\uff0c\u4e0d\u628a\u5f20\u4e39\u67ab\u7684\u8bdd\u5f53\u4f5c\u620f\u8a00\u3002\u4e0a\u5b98\u5929\u91ce\u5fc3\u4e2d\u4e00\u52a8\uff0c\u5ffd \u9053\uff1a\u201c\u4e4c\u8499\u592b\uff0c\u4ed6\u8bf4\u7684\u8bdd\u662f\u771f\u7684\u5417\uff1f\u201d\u4e4c\u8499\u592b\u9053\uff1a\u201c\u4e00\u70b9\u4e0d\u9519\u3002\u201d\u5728\u6000\u4e2d\u53d6\u51fa\u4e00\u672c\u4e66\u6765\u3002 \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u63a5\u8fc7\u90a3\u672c\u5c0f\u4e66\u4e00\u770b\uff0c\u53ea\u89c1\u4e0a\u9762\u9898\u7740\u300a\u7384\u529f\u8981\u8bc0\u300b\u56db\u5b57\uff0c\u4e0b\u9762\u7684\u7f72\u540d\u662f\uff1a\u201c\u5f6d\u83b9 \u7389\u7740\u201d\u3002\u5f20\u4e39\u67ab\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6211\u9a97\u4e86\u4f60\u6ca1\u6709\uff1f\u6b64\u4eba\u5c82\u4e0d\u662f\u767e\u4f59\u5e74\u524d\u505a\u8fc7\u4e24\u4f4d\u7687\u5e1d\u5e08\u7236\u7684\u4eba\uff1f \u4f60\u81ea\u5df1\u63ed\u5f00\u770b\u770b\u5427\uff0c\u770b\u4f60\u8fd8\u4f1a\u4e0d\u4f1a\u575a\u6301\u5fc5\u987b\u4ee5\u7ae5\u5b50\u4e4b\u8eab\u624d\u80fd\u5b66\u4f60\u90a3\u52b3\u4ec0\u5b50\u7684\u4e00\u6307\u7985\u529f\u592b\uff1f\u201d \u4e0a\u5b98\u5929\u91ce\u60ca\u547c\u9053\uff1a\u201c\u539f\u6765\u5f6d\u548c\u5c1a\u7684\u9057\u7740\u5728\u4f60\u7684\u624b\u4e0a\uff0c\u662f\u4f60\u501f\u7ed9\u4ed6\u7684\uff1f\u201d\u5f20\u4e39\u67ab\u5fae\u7b11\u4e0d\u8bed\uff0c\u5ffd \u800c\u6717\u58f0\u541f\u9053\uff1a\u201c\u613f\u6c42\u4e00\u6ef4\u6768\u679d\u9732\uff0c\u6d12\u4f5c\u4eba\u95f4\u5e76\u8482\u83b2\u3002\u51e1\u662f\u5929\u4e0b\u6709\u60c5\u4eba\uff0c\u672c\u6765\u90fd\u8be5\u6210\u7737\u5c5e\u3002\u201d \u4e0a\u5b98\u5929\u91ce\u5fc3\u60c5\u6fc0\u8361\uff0c\u987b\u77e5\u8fd9\u672c\u300a\u7384\u529f\u8981\u8bc0\u300b\u4e43\u662f\u6b66\u6797\u4e2d\u7684\u65e0\u4ef7\u4e4b\u5b9d\uff0c\u5f20\u4e39\u67ab\u4e3a\u4e86\u8981\u7389\u6210\u4e4c\u8499 \u592b\u4e0e\u6797\u4ed9\u97f5\u7684\u4e00\u6bb5\u59fb\u7f18\uff0c\u7adf\u80af\u501f\u7ed9\u4ed6\u770b\uff0c\u5b9e\u5c5e\u96be\u5f97\u3002\u8fd9\u4e00\u77ac\u95f4\uff0c\u6069\u6028\u60c5\u5b7d\u90fd\u5df2\u5728\u4e0a\u5b98\u5929\u91ce\u5fc3 \u5934\u5316\u89e3\uff0c\u5ffd\u800c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u771f\u6709\u4f60\u7684\uff0c\u4f60\u624d\u662f\u5929\u4e0b\u7b2c\u4e00\u60c5\u75f4\u3002\u201d\u63fd\u7740\u5f20\u4e39\u67ab\u5927\u7b11\u3002 \u7384\u673a\u9038\u58eb\u7b11\u9053\uff1a\u201c\u4e0a\u5b98\u5144\uff0c\u4f60\u771f\u662f\u672a\u8131\u8d64\u5b50\u4e4b\u5fc3\uff0c\u4e0e\u4e09\u5341\u5e74\u524d\u4e00\u6a21\u4e00\u6837\u554a\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u653e\u5f00\u4e86\u5f20\u4e39\u67ab\uff0c\u9762\u8272\u4e00\u7aef\u5bf9\u4e4c\u8499\u592b\u3001\u6797\u4ed9\u97f5\u9053\uff1a\u201c\u4f60\u4eec\u90fd\u662f\u6211\u7684\u597d\u5f1f\u5b50\uff0c\u6211\u8bef \u4e86\u4f60\u4eec\u5341\u591a\u5e74\u5566\u3002\u73b0\u5728\u6211\u5c06\u4e0d\u8bb8\u5a5a\u5ac1\u7684\u6212\u5f8b\u53d6\u6d88\uff0c\u8fd9\u95f4\u77f3\u5ba4\u4e5f\u7559\u7ed9\u4f60\u4eec\u4e86\u3002\u201d\u4e4c\u8499\u592b\u4e0e\u6797\u4ed9 \u97f5\u5927\u559c\u8fc7\u671b\uff0c\u53cc\u53cc\u8dea\u5728\u5730\u4e0a\uff0c\u8c22\u5e08\u5c0a\u6069\u5178\u3002\u4e0a\u5b98\u5929\u91ce\u7b11\u9053\uff1a\u201c\u4f60\u8be5\u8c22\u4ed6\u624d\u662f\u3002\u201d\u4e4c\u8499\u592b\u72c2\u559c \u4e4b\u4e2d\uff0c\u66f4\u65e0\u6687\u987e\u5230\u8f88\u5206\uff0c\u679c\u7136\u5411\u5f20\u4e39\u67ab\u65bd\u4e86\u4e00\u793c\uff0c\u5e76\u5c06\u300a\u7384\u529f\u8981\u8bc0\u300b\u9001\u8fd8\u7ed9\u4ed6\u3002\u4ed6\u8d44\u8d28\u867d\u4e0d \u5982\u5f20\u4e39\u67ab\u4e4b\u806a\u6167\uff0c\u4f46\u8fd9\u51e0\u65e5\u4e4b\u4e2d\uff0c\u5df2\u5c06\u300a\u7384\u529f\u8981\u8bc0\u300b\u4e2d\u7ec3\u6c14\u4e4b\u6cd5\u719f\u8bb0\u4e8e\u5fc3\uff0c\u4e0d\u5fc5\u518d\u770b\u4e86\u3002 \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u4ef0\u5929\u5927\u7b11\u9053\uff1a\u201c\u6211\u5e73\u751f\u5927\u6218\u5c0f\u6218\u4e0d\u4e0b\u5343\u6570\u767e\u573a\uff0c\u4ee5\u4eca\u65e5\u8fd9\u4e00\u6218\u6700\u4e3a\u75db\u5feb\u4e86\uff01\u5929 \u4e0b\u7b2c\u4e00\u7684\u540d\u5934\u867d\u4e89\u4e0d\u5230\uff0c\u6069\u6028\u7f6a\u5b7d\u90fd\u5df2\u5168\u6d88\u3002\u7384\u673a\u8001\u5144\uff0c\u662f\u54b1\u4eec\u8be5\u8d70\u7684\u65f6\u5019\u4e86\uff01\u201d\u5ffd\u800c\u5411\u5c71 \u4e0b\u4e00\u77a5\u5411\u4e4c\u8499\u592b\u9053\uff1a\u201c\u4f60\u7684\u5927\u5e08\u5144\u4e5f\u6765\u4e86\uff0c\u4ed6\u6765\u7684\u6b63\u662f\u65f6\u5019\u3002\u201d \u3000\u3000\u6fb9\u53f0\u706d\u660e\u8d70\u4e0a\u5c71\u6765\uff0c\u89c1\u5e08\u7236\u8ddf\u7384\u673a\u9038\u58eb\u5e76\u80a9\u800c\u7acb\uff0c\u751a\u4e3a\u60ca\u5f02\uff0c\u4ed6\u672c\u6765\u662f\u53d7\u5f20\u5b97\u5468\u4e4b\u6258\uff0c \u6015\u4e0a\u5b98\u5929\u91ce\u8bef\u4f24\u4e86\u5f20\u4e39\u67ab\uff0c\u8bf7\u4ed6\u6765\u5173\u7167\u7684\u3002\u800c\u4eca\u89c1\u6b64\u60c5\u5f62\uff0c\u60f3\u662f\u4e24\u5bb6\u5df2\u8a00\u5f52\u4e8e\u597d\uff0c\u5fc3\u4e2d\u653e\u4e0b \u4e00\u5757\u77f3\u5934\uff0c\u8f6c\u773c\u4e00\u770b\uff0c\u53ea\u89c1\u88ab\u9010\u51fa\u5e08\u95e8\u7684\u4e4c\u8499\u592b\u4e0e\u5e08\u59b9\u6797\u4ed9\u97f5\u76f8\u4f9d\u76f8\u504e\uff0c\u7ad9\u5728\u5e08\u7236\u8eab\u65c1\uff0c\u72b6 \u6781\u4eb2\u70ed\uff0c\u6fb9\u53f0\u706d\u660e\u66f4\u662f\u5947\u5f02\u4e07\u5206\u3002 \u3000\u3000\u6fb9\u53f0\u706d\u660e\u662f\u5f20\u4e39\u67ab\u81ea\u5e7c\u5373\u548c\u4ed6\u5728\u4e00\u8d77\u7684\u4eba\uff0c\u5f20\u4e39\u67ab\u795e\u5fd7\u672c\u6765\u5c31\u6062\u590d\u4e86\u516d\u4e03\u5206\uff0c\u4e00\u89c1\u4e86\u6fb9 \u53f0\u706d\u660e\uff0c\u5e7c\u5e74\u60c5\u4e8b\uff0c\u4e00\u4e00\u5728\u5fc3\u5934\u6d8c\u8d77\uff0c\u4e5f\u8bb0\u8d77\u4e86\u81ea\u5df1\u7684\u8eab\u4e16\u4e0e\u56fd\u6068\u5bb6\u4ec7\uff0c\u8dd1\u4e0a\u524d\u53bb\u62c9\u7740\u6fb9\u53f0 \u706d\u660e\u9053\uff1a\u201c\u6fb9\u53f0\u5c06\u519b\uff0c\u6211\u7684\u7236\u4eb2\u6ca1\u4e8b\u4e48\uff1f\u201d\u6fb9\u53f0\u706d\u660e\u9053\uff1a\u201c\u4ed6\u6b63\u76fc\u671b\u4f60\u56de\u53bb\u3002\u201d\u4e0a\u5b98\u5929\u91ce \u9053\uff1a\u201c\u4f60\u4eec\u65e9\u5c31\u8ba4\u8bc6\u7684\uff1f\u201d\u6fb9\u53f0\u706d\u660e\u9053\uff1a\u201c\u7980\u544a\u5e08\u7236\uff0c\u4ed6\u662f\u6211\u7684\u5c0f\u4e3b\u4eba\u3002\u201d\u4e0a\u5b98\u5929\u91ce\u54c8\u54c8\u5927 \u7b11\u9053\uff1a\u201c\u7384\u673a\u8001\u5144\uff0c\u770b\u54b1\u4eec\u95e8\u4e0b\u65e9\u5c31\u662f\u4e00\u5bb6\u4eba\uff0c\u54b1\u4eec\u8fd8\u4e89\u6597\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5929\u91ce\u5c06\u6fb9\u53f0\u706d\u660e\u62db\u5230\u8ddf\u524d\u9053\uff1a\u201c\u6211\u5df2\u51b3\u610f\u79bb\u5f00\u6b64\u5730\uff0c\u4ed9\u97f5\u8ddf\u4e86\u6211\u8fd9\u4e48\u591a\u5e74\uff0c\u8fd9\u95f4\u77f3 \u5ba4\uff0c\u6211\u5c31\u7559\u7ed9\u5979\u4f5c\u5ac1\u5986\uff0c\u8ba9\u5979\u4e0e\u4e4c\u8499\u592b\u5728\u8fd9\u91cc\u9759\u4fee\u3002\u4ece\u4eca\u65e5\u8d77\uff0c\u7531\u4f60\u505a\u6211\u6d3e\u7684\u638c\u95e8\u5f1f\u5b50\uff0c\u4f60 \u8981\u597d\u597d\u7763\u4fc3\u5e08\u5f1f\u3001\u5e08\u59b9\u4eec\u52e4\u7ec3\u6b66\u529f\u3002\u201d\u6797\u4ed9\u97f5\u773c\u5708\u4e00\u7ea2\u9053\uff1a\u201c\u5e08\u7236\u5728\u8fd9\u91cc\u4f4f\u5f97\u597d\u597d\u7684\uff0c\u4f55\u5fc5 \u8981\u8d70\uff1f\u8ba9\u6211\u4eec\u591a\u670d\u4f8d\u4f60\u51e0\u5e74\uff0c\u4ee5\u62a5\u5e08\u6069\u5427\u3002\u201d\u4e0a\u5b98\u5929\u91ce\u4e00\u7b11\u8bf4\u9053\uff1a\u201c\u4e09\u5341\u5e74\u524d\uff0c\u6211\u56e0\u4e3a\u6253\u4e0d \u8d62\u7384\u673a\u8001\u5934\uff0c\u9003\u5230\u6b64\u5730\uff0c\u73b0\u5728\u6069\u6028\u5168\u6d88\uff0c\u6211\u8fd8\u4e0d\u56de\u5230\u4e2d\u539f\u53bb\u505a\u4ec0\u4e48\u4e48\uff1f\u4f60\u6709\u4e86\u4f34\u513f\uff0c\u6211\u4e5f\u8981 \u627e\u4e2a\u8001\u4f34\u554a\uff01\u201d\u6fb9\u53f0\u706d\u660e\u8dea\u4e0b\u9886\u547d\u3002\u6797\u4ed9\u97f5\u7ed9\u4ed6\u8bf4\u5f97\u8138\u6cdb\u7ea2\u6f6e\uff0c\u5ff8\u6029\u7b11\u9053\uff1a\u201c\u53ea\u8981\u5e08\u7236\u665a\u5e74 \u5feb\u4e50\uff0c\u6211\u4e5f\u5c31\u653e\u5fc3\u5566\u3002\u201d\u4e0e\u4e4c\u8499\u592b\u4e00\u540c\u8dea\u4e0b\u8c22\u6069\u3002 \u3000\u3000\u7384\u673a\u9038\u58eb\u9053\uff1a\u201c\u770b\u6765\u6211\u4e5f\u8981\u4ea4\u4ee3\u4e00\u4e9b\u4e8b\u60c5\u4e86\u3002\u201d\u5c06\u95e8\u4e0b\u5f1f\u5b50\u90fd\u62db\u5230\u8ddf\u524d\uff0c\u8bf4\u9053\uff1a\u201c\u8463\u5cb3 \u8001\u6210\u6301\u91cd\uff0c\u8ddf\u6211\u6700\u4e45\uff0c\u6b64\u540e\u672c\u95e8\u4e00\u5207\u4e8b\u60c5\uff0c\u90fd\u7531\u4ed6\u6267\u638c\u3002\u5929\u534e\u4e0e\u76c8\u76c8\uff0c\u8d44\u8d28\u6700\u4f73\uff0c\u5404\u5f97\u6211\u7684 \u534a\u5957\u5251\u6cd5\uff0c\u4ece\u4eca\u4ee5\u540e\u51c6\u8bb8\u4f60\u4eec\u4e92\u76f8\u4f20\u6388\uff0c\u5251\u53ef\u5408\u74a7\uff0c\u4eba\u4ea6\u53ef\u4ee5\u5408\u74a7\uff0c\u5c31\u7531\u4f60\u4eec\u7684\u5927\u5e08\u5144\u4e3b\u5a5a \u597d\u4e86\u3002\u201d\u8c22\u5929\u534e\u4e0e\u53f6\u76c8\u76c8\u5341\u51e0\u5e74\u7684\u5fc3\u613f\u5f97\u507f\uff0c\u81ea\u662f\u6b22\u559c\u65e0\u9650\uff0c\u4f46\u5728\u5c0f\u8f88\u9762\u524d\uff0c\u5374\u4e0d\u597d\u610f\u601d\u8868 \u9732\u51fa\u6765\uff0c\u53ea\u662f\u6de1\u6de1\u5730\u76f8\u89c6\u4e00\u7b11\u3002\u8463\u5cb3\u4e0a\u524d\u5411\u5e08\u5f1f\u3001\u5e08\u59b9\u9053\u8d3a\uff0c\u5fc3\u4e2d\u6781\u662f\u9ad8\u5174\uff0c\u5374\u4e5f\u5fae\u611f\u5230\u4e00 \u70b9\u8f9b\u9178\u3002\u539f\u6765\u4ed6\u5bf9\u5e08\u59b9\u4e5f\u65e9\u6709\u5fc3\u610f\uff0c\u53ea\u56e0\u77e5\u9053\u5e08\u59b9\u5fc3\u5411\u5929\u534e\uff0c\u6240\u4ee5\u4e8c\u5341\u5e74\u6765\uff0c\u4ece\u65e0\u8868\u9732\u3002\u4eca \u65e5\u89c1\u5e08\u5f1f\u3001\u5e08\u59b9\u53cc\u5251\u5408\u74a7\uff0c\u786e\u662f\u5929\u9020\u5730\u8bbe\u7684\u4e00\u5bf9\u74a7\u4eba\uff0c\u8fd9\u4e00\u70b9\u70b9\u8f9b\u9178\u4e5f\u5c31\u5347\u534e\uff0c\u597d\u50cf\u6de1\u6de1\u7684 \u8f7b\u70df\uff0c\u5728\u9633\u5149\u4e4b\u4e0b\u6d88\u5931\u4e86\u3002 \u3000\u3000\u7384\u673a\u9038\u58eb\u53c8\u8a00\u9053\uff1a\u201c\u4e91\u6f84\u5728\u6211\u95e8\u4e0b\u65e5\u5b50\u6700\u77ed\uff0c\u6b66\u529f\u4ea6\u672a\u7ec3\u6210\uff0c\u672c\u8eab\u53c8\u5386\u5c3d\u52ab\u96be\uff0c\u82e5\u8bf4\u6211 \u5fc3\u4e2d\u8fd8\u6709\u672a\u4e86\u4e4b\u4e8b\uff0c\u90a3\u5c31\u662f\u8bb0\u6302\u4ed6\u4e86\u3002\u6211\u8d70\u4e86\u4e4b\u540e\uff0c\u4f60\u4eec\u90fd\u8981\u597d\u597d\u5730\u7167\u987e\u4ed6\u3002\u8463\u5cb3\uff0c\u4f60\u53ef\u4ee5 \u5c06\u672c\u95e8\u7684\u5185\u529f\u5fc3\u6cd5\uff0c\u4ee3\u6211\u4f20\u6388\u7ed9\u4ed6\u3002\u53ea\u8981\u52e4\u594b\u82e6\u7ec3\uff0c\u5c06\u6765\u8fd8\u53ef\u6709\u6210\u3002\u201d\u4e91\u6f84\u4e0d\u7981\u568e\u5555\u75db\u54ed\uff0c \u5f20\u4e39\u67ab\u96be\u8fc7\u975e\u5e38\uff0c\u7adf\u4e0d\u6562\u5411\u4e91\u857e\u518d\u77a7\u4e00\u773c\u3002 \u3000\u3000\u8463\u5cb3\u9053\uff1a\u201c\u5e08\u5f1f\u6b7b\u91cc\u9003\u751f\uff0c\u800c\u4eca\u7236\u5973\u91cd\u4f1a\uff0c\u53c8\u8499\u5e08\u6069\uff0c\u82e6\u5c3d\u7518\u6765\uff0c\u4e0d\u5fc5\u592a\u4f24\u5fc3\u4e86\u3002\u201d\u7384 \u673a\u9038\u58eb\u8f7b\u629a\u4e91\u857e\u7684\u5934\u53d1\u9053\uff1a\u201c\u4f60\u6709\u8fd9\u6837\u4e00\u4f4d\u5982\u82b1\u4f3c\u7389\u7684\u5b5d\u987a\u5973\u513f\uff0c\u6bd4\u6211\u5f3a\u5f97\u591a\u4e86\u3002\u4eba\u751f\u4e00 \u4e16\uff0c\u53ea\u6c42\u95ee\u5fc3\u65e0\u6127\uff0c\u4fbf\u6765\u5f97\u5b89\u4e50\uff0c\u53bb\u5f97\u5b89\u4e50\uff0c\u4f60\u662f\u5fe0\u81e3\u5b5d\u5b50\u96c6\u4e8e\u4e00\u8eab\uff0c\u53c8\u6709\u4f73\u513f\u4f73\u5973\uff0c\u867d\u7136 \u9645\u9047\u574e\u5777\uff0c\u7ec6\u60f3\u8d77\u6765\uff0c\u4ea6\u65e0\u7f3a\u9677\uff0c\u4e0d\u5fc5\u518d\u54ed\u4e86\u3002\u201d \u3000\u3000\u4e91\u6f84\u6536\u4e86\u773c\u6cea\uff0c\u867d\u611f\u5e08\u95e8\u6e29\u6696\uff0c\u5fc3\u4e2d\u7684\u60b2\u6124\u4ecd\u672a\u7a0d\u51cf\u3002\u60f3\u8d77\u81ea\u5df1\u4ec7\u4eba\u7684\u513f\u5b50\u53c8\u6b63\u662f\u81ea\u5df1 \u7684\u5e08\u4f84\u800c\u4e14\u662f\u5e08\u7236\u6700\u8d5e\u8bb8\u7684\u4eba\uff0c\u8fd9\u4ec7\u6068\u4e0d\u4f46\u4e0d\u80fd\u62a5\uff0c\u800c\u4e14\u4e0d\u4fbf\u5728\u5e08\u5144\u4eec\u7684\u9762\u524d\u8bf4\u51fa\u6765\uff0c\u5fc3\u4e2d \u6291\u90c1\u66f4\u751a\u3002\u53ea\u542c\u5f97\u7384\u673a\u9038\u58eb\u53c8\u7b11\u9053\uff1a\u201c\u6700\u4ee4\u6211\u6b22\u559c\u7684\u662f\u54b1\u4eec\u4e00\u4ee3\u5f3a\u8fc7\u4e00\u4ee3\uff0c\u5929\u534e\u7684\u5f1f\u5b50\u5f20\u4e39 \u67ab\u5c06\u6765\u5b9a\u80fd\u5149\u5927\u6211\u95e8\uff0c\u53ea\u8981\u614e\u6212\u8bef\u7528\u806a\u660e\uff0c\u6210\u5c31\u4e0d\u53ef\u9650\u91cf\uff0c\u4f60\u4eec\u8981\u597d\u597d\u770b\u5f85\u4ed6\u3002\u201d \u3000\u3000\u65e5\u5f71\u897f\u659c\uff0c\u5929\u6e10\u9ec4\u660f\uff0c\u90a3\u8001\u5a46\u5a46\u624b\u6301\u7af9\u6756\uff0c\u8f7b\u8f7b\u6325\u4e86\u534a\u4e2a\u5706\u5708\uff0c\u9053\uff1a\u201c\u63a8\u5f00\u5c18\u4e16\u4e8b\uff0c\u8df3 \u51fa\u4e94\u884c\u4e2d\u3002\u504f\u4f60\u4eec\u6709\u8fd9\u4e48\u591a\u4ea4\u5f85\u4e0d\u6e05\u7684\u4e8b\uff01\u201d\u4e0a\u5b98\u5929\u91ce\u62cd\u624b\u7b11\u9053\uff1a\u201c\u8bf4\u5f97\u597d\uff0c\u8bf4\u5f97\u597d\uff01\u4ece\u4eca \u91ce\u9e64\u95f2\u4e91\u4f34\uff0c\u4e0d\u6094\u60c5\u75f4\u4e0d\u6094\u771f\u3002\u7384\u673a\u8001\u5144\uff0c\u6b63\u662f\u54b1\u4eec\u8be5\u8d70\u7684\u65f6\u5019\u4e86\uff01\u201d\u7384\u673a\u9038\u58eb\u5411\u4f17\u5f1f\u5b50\u6325 \u624b\u4e00\u7b11\uff0c\u4e5f\u6717\u58f0\u541f\u9053\uff1a\u201c\u53c2\u900f\u534e\u4e25\u771f\u5999\u8c1b\uff0c\u83e9\u63d0\u975e\u6811\u955c\u975e\u53f0\uff01\u201d\u4e09\u4eba\u4e00\u540c\u62cd\u638c\u5927\u7b11\uff0c\u5065\u6b65\u5982 \u98de\uff0c\u5728\u9ec4\u660f\u6b8b\u7167\u4e4b\u4e2d\uff0c\u98d8\u7136\u800c\u53bb\u3002\u4e24\u6d3e\u5f1f\u5b50\u90fd\u8dea\u4e0b\u9001\u884c\uff0c\u53ea\u89c1\u8fd9\u4e09\u4e2a\u8001\u4eba\u7fbd\u8863\u98d8\u98d8\uff0c\u500f\u5ffd\u4e4b \u95f4\uff0c\u6ca1\u4e86\u8e2a\u8ff9\u3002 \u3000\u3000\u8463\u5cb3\u5fc3\u4e2d\u6697\u6697\u53f9\u606f\uff0c\u6fb9\u53f0\u706d\u660e\u4e5f\u6709\u8bb8\u591a\u611f\u89e6\uff1a\u60f3\u4e0d\u5230\u8fd9\u4e24\u4e2a\u5927\u5bf9\u5934\u7adf\u662f\u5982\u6b64\u8fd9\u822c\u7684\u8a00\u5f52 \u4e8e\u597d\uff0c\u6bd4\u5c06\u8d77\u6765\uff0c\u4e16\u4e0a\u6709\u591a\u5c11\u4e8b\u60c5\u90fd\u53ea\u662f\u9e21\u866b\u877c\u8681\u4e4b\u4e89\u3002\u731b\u4e00\u62ac\u5934\uff0c\u5ffd\u89c1\u5f20\u4e39\u67ab\u8dea\u5728\u540e\u8fb9\uff0c \u5140\u672a\u8d77\u8eab\uff0c\u76ee\u5149\u5446\u6ede\uff0c\u51dd\u89c6\u524d\u5c71\uff0c\u773c\u6cea\u4f3c\u6b32\u593a\u7736\u800c\u51fa\u5374\u53c8\u54ed\u4e0d\u51fa\u6765\uff0c\u4e00\u526f\u5931\u795e\u843d\u9b44\u7684\u6837\u5b50\uff0c \u5982\u75f4\u5982\u5446\u3002\u6fb9\u53f0\u706d\u660e\u5403\u4e86\u4e00\u60ca\uff0c\u8d70\u8fc7\u53bb\u5c06\u5f20\u4e39\u67ab\u8f7b\u8f7b\u6276\u8d77\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u600e\u4e48\u5566\uff01\u201d \u3000\u3000\u5f20\u4e39\u67ab\u6b64\u65f6\u6b63\u662f\u60b2\u4ece\u4e2d\u6765\uff0c\u4e0d\u53ef\u65ad\u7edd\uff01\u4ed6\u773c\u89c1\u4e4c\u8499\u592b\u4e0e\u6797\u4ed9\u97f5\u3001\u81ea\u5df1\u7684\u5e08\u7236\u4e0e\u4e91\u857e\u7684\u5e08 \u7236\u90fd\u5df2\u4e86\u5374\u5fc3\u613f\uff0c\u53ea\u662f\u81ea\u5df1\u4e0e\u610f\u4e2d\u4eba\u5374\u662f\u54ab\u5c3a\u5929\u6daf\uff0c\u53ef\u671b\u800c\u4e0d\u53ef\u5373\uff0c\u8fd9\u5176\u95f4\u5c31\u50cf\u9694\u7740\u4e00\u9053\u65e0 \u5f62\u7684\u95e8\u6237\uff0c\u95e8\u5916\u7684\u4eba\u8d70\u4e0d\u8fdb\u53bb\uff0c\u95e8\u5185\u7684\u4eba\u6ca1\u52c7\u6c14\u8d70\u51fa\u6765\u3002\u6fb9\u53f0\u706d\u660e\u8fde\u95ee\u4e24\u58f0\uff0c\u5f20\u4e39\u67ab\u5ffd\u7136\u4ef0 \u5934\u541f\u9053\uff1a\u201c\u96be\u5fd8\u6069\u6028\u96be\u5fd8\u4f60\uff0c\u53ea\u4e3a\u60c5\u75f4\u53ea\u4e3a\u771f\u3002\u6789\u4f60\u662f\u8001\u9b54\u5934\u7684\u5f1f\u5b50\uff0c\u8fd9\u4e24\u53e5\u8bd7\u90fd\u4e0d\u61c2\u5f97\uff0c \u95ee\u6211\u4f5c\u4ec0\u4e48\uff1f\u54c8\u54c8\uff0c\u4f60\u662f\u8c01\uff1f\u6211\u662f\u8c01\uff1f\u5979\u53c8\u662f\u8c01\uff1f\u5929\u82e5\u6709\u60c5\u5929\u4ea6\u8001\u6447\u6447\u5e7d\u6068\u80fd\u7981\u3002\u6211\u6b32\u95ee\u5929 \u5929\u4e0d\u5e94\uff0c\u4f60\u6765\u95ee\u6211\u6211\u4f55\u77e5\uff1f\u201d\u5f20\u4e39\u67ab\u88ab\u89e6\u8d77\u4e86\u5fc3\u4e8b\uff0c\u5ffd\u89c9\u4e00\u7247\u8ff7\u60d8\uff0c\u795e\u5fd7\u53c8\u6e10\u5931\u5e38\u6001\u3002 \u3000\u3000\u8fd9\u970e\u90a3\u95f4\uff0c\u4e91\u857e\u4e5f\u662f\u4f24\u5fc3\u65e0\u9650\uff0c\u53ea\u89c1\u5f20\u4e39\u67ab\u7684\u773c\u5149\u6162\u6162\u5730\u79fb\u52a8\uff0c\u51dd\u89c6\u5979\u7684\u9762\u5e9e\uff0c\u8fd9\u76ee\u5149 \u4e2d\u542b\u6709\u591a\u5c11\u5e7d\u6028\uff0c\u591a\u5c11\u7231\u601c\uff01\u56de\u5934\u4e00\u77a5\uff0c\u53ea\u89c1\u7236\u4eb2\u7684\u773c\u5149\u4e5f\u5728\u76ef\u7740\u81ea\u5df1\uff0c\u8fd9\u76ee\u5149\u4e2d\u53c8\u662f\u542b\u6709 \u591a\u5c11\u6124\u6068\uff0c\u591a\u5c11\u4f24\u5fc3\uff01\u7236\u4eb2\u6194\u60b4\u7684\u989c\u5bb9\u6e10\u6e10\u5728\u9762\u524d\u6269\u5927\uff0c\u906e\u8fc7\u4e86\u5f20\u4e39\u67ab\u7684\u5f71\u5b50\uff0c\u4e91\u857e\u5728\u5f20\u4e39 \u67ab\u7684\u76ee\u5149\u4e0e\u5979\u63a5\u89e6\u7684\u90a3\u4e00\u5239\u90a3\uff0c\u51e0\u4e4e\u8981\u53eb\u51fa\u58f0\u6765\uff0c\u7136\u800c\u8fc5\u5373\u53c8\u538b\u4e86\u4e0b\u53bb\u3002\u5979\u56de\u907f\u4e86\u5f20\u4e39\u67ab\u7684 \u76ee\u5149\u53c8\u56de\u907f\u4e86\u7236\u4eb2\u7684\u76ee\u5149\uff0c\u8fd9\u4e24\u4eba\u90fd\u662f\u5979\u6700\u6700\u5fc3\u7231\u7684\u4eba\uff0c\u5979\u4e0d\u5fcd\u4ee4\u8fd9\u4e24\u4eba\u4f24\u5fc3\uff0c\u7136\u800c\u5979\u53c8\u4e0d \u80fd\u4e0d\u4ee4\u4ed6\u4eec\u4f24\u5fc3\u3002\u5979\u54bd\u4e0b\u4e86\u81ea\u5df1\u7684\u773c\u6cea\uff0c\u5979\u4e0d\u6562\u770b\u8fd9\u4e24\u4e2a\u4e16\u4e0a\u6700\u7231\u60dc\u81ea\u5df1\u7684\u4eba\uff0c\u5979\u4e0d\u6562\u60f3\u8c61 \u8fd9\u4e24\u4eba\u7684\u5fc3\u4e2d\u611f\u89e6\u5982\u4f55\uff0c\u5979\u81ea\u5df1\u7684\u5fc3\u5374\u5148\u81ea\u788e\u4e86\u3002 \u3000\u3000\u6b64\u60c5\u6b64\u666f\u4e0d\u8bf4\u81ea\u660e\u3002\u8463\u5cb3\u3001\u8c22\u5929\u534e\u548c\u53f6\u76c8\u76c8\u90fd\u4f4e\u4e0b\u4e86\u5934\u3002\u8fd9\u79cd\u96be\u4ee5\u5206\u89e3\u7684\u6069\u6028\uff0c\u5373\u7b97\u5e08 \u5f92\u4e4b\u4eb2\uff0c\u4e5f\u4e0d\u77e5\u5982\u4f55\u6392\u89e3\u3002\u5c71\u98ce\u5439\u6765\uff0c\u6bcf\u4eba\u90fd\u89c9\u5f97\u4e00\u80a1\u51b7\u6c14\u76f4\u900f\u5fc3\u5934\u3002\u6b63\u662f\uff1a \u3000\u3000\u8fd9\u822c\u5e7d\u6028\u96be\u5206\u89e3\uff0c\u624d\u4e0b\u7709\u5934\uff0c\u53c8\u4e0a\u5fc3\u5934\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u840d\u8e2a\u4fa0\u5f71\u5f55\u300b\u2014\u2014\u7b2c\u4e8c\u5341\u4e5d\u56de\u3000\u89e6\u666f\u4f24\u60c5\u7a77\u6751\u5636\u9a8f\u9a6c\u3000\u795e\u673a\u5999\u7b97\u6ce5\u6cbc\u9677\u8ffd\u5175 \u6881\u7fbd\u751f\u300a\u840d\u8e2a\u4fa0\u5f71\u5f55\u300b \u7b2c\u4e8c\u5341\u4e5d\u56de\u3000\u89e6\u666f\u4f24\u60c5\u7a77\u6751\u5636\u9a8f\u9a6c\u3000\u795e\u673a\u5999\u7b97\u6ce5\u6cbc\u9677\u8ffd\u5175 \u3000\u3000\u5bd2\u98ce\u98d2\u98d2\uff0c\u5f20\u4e39\u67ab\u4e0e\u4e91\u857e\u76f8\u5bf9\u800c\u7acb\uff0c\u5404\u81ea\u65e0\u8bed\u5404\u81ea\u51c4\u51c9\u3002\u6fb9\u53f0\u706d\u660e\u6447\u4e86\u6447\u5934\uff0c\u8f7b\u8f7b\u53f9 \u606f\uff0c\u5ffd\u5728\u5f20\u4e39\u67ab\u7684\u8033\u8fb9\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u629b\u5f97\u4e0b\u5927\u660e\u4e5d\u4e07\u91cc\u9526\u7ee3\u6cb3\u5c71\uff0c\u96be\u9053\u5c31\u629b\u4e0d\u5f00\u4e00\u4e2a\u5973 \u5b50\uff1f\u201d\u5f20\u4e39\u67ab\u5fc3\u5934\u4e00\u9707\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u201d\u6fb9\u53f0\u706d\u660e\u9053\uff1a\u201c\u4f60\u7684\u7236\u4eb2\u6307\u4f60\u91cd\u5149\u5927\u5468\uff0c\u4f60\u4e3a\u4e86\u4e0d \u8ba9\u4e2d\u534e\u4e07\u91cc\u7684\u9526\u7ee3\u6cb3\u5c71\u6ca6\u4e8e\u5937\u72c4\uff0c\u5192\u4e86\u591a\u5c11\u8270\u5371\uff0c\u732e\u5b9d\u732e\u56fe\uff0c\u633d\u6551\u4e86\u5927\u660e\u5929\u4e0b\u3002\u4f60\u5e1d\u738b\u4e4b\u4e1a \u5c1a\u81ea\u53ef\u5f03\u8fd8\u6709\u4ec0\u4e48\u6069\u6028\u4e0d\u80fd\u629b\u5f00\uff1f\u201d\u5f20\u4e39\u67ab\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u6211\u89c6\u5e1d\u738b\u5982\u7caa\u571f\u2026\u2026\u201d\u6fb9\u53f0\u706d \u660e\u7d27\u63a5\u7740\u9053\uff1a\u201c\u7956\u56fd\u6cb3\u5c71\u5f85\u4f60\u56de\u3002\u201d\u5f20\u4e39\u67ab\u9762\u8272\u500f\u800c\u4e00\u53d8\uff0c\u7531\u767d\u8f6c\u7ea2\uff0c\u6fb9\u53f0\u706d\u660e\u7684\u58f0\u97f3\u867d\u7136 \u4e0d\u5927\uff0c\u5374\u5982\u5728\u4ed6\u7684\u5fc3\u4e0a\u54cd\u8d77\u4e86\u4e00\u4e2a\u7126\u96f7\uff0c\u8fd9\u970e\u65f6\u95f4\uff0c\u4ed6\u60f3\u8d77\u4e86\u81ea\u5df1\u4ece\u6f20\u5317\u8d76\u5f80\u6c5f\u5357\uff0c\u53c8\u4ece\u6c5f \u5357\u91cd\u56de\u6f20\u5317\uff0c\u5386\u5c3d\u4e07\u6c34\u5343\u5c71\uff0c\u7ecf\u8fc7\u65e0\u7a77\u52ab\u96be\u6240\u4e3a\u7684\u662f\u4ec0\u4e48\uff1f\u8fd8\u4e0d\u662f\u4e3a\u4e86\u81ea\u5df1\u4e00\u756a\u58ee\u5fd7\uff0c\u4e3a\u4e86 \u4fdd\u5168\u4e2d\u534e\u7684\u9526\u7ee3\u6cb3\u5c71\uff0c\u4e3a\u4e86\u8981\u4f7f\u4e2d\u56fd\u548c\u74e6\u523a\u6c38\u606f\u5e72\u6208\uff0c\u56db\u90bb\u548c\u7766\u3002\u8fd9\u756a\u7406\u60f3\u800c\u4eca\u5373\u5c06\u5b9e\u73b0\uff0c \u81ea\u5df1\u5374\u8fd9\u6837\u9893\u5510\uff01\u5f20\u4e39\u67ab\u672c\u662f\u806a\u660e\u7edd\u9876\uff0c\u6781\u80fd\u5206\u8fa8\u662f\u975e\u4e4b\u4eba\uff0c\u5982\u6b64\u4e00\u60f3\uff0c\u987f\u89c9\u80f8\u4e2d\u70ed\u8840\u6cb8 \u817e\uff0c\u4e0d\u80fd\u81ea\u5df1\uff0c\u795e\u5fd7\u7acb\u5373\u6e05\u9192\uff0c\u54ac\u4e00\u54ac\u7259\uff0c\u5ffd\u800c\u8bf4\u9053\uff1a\u201c\u6fb9\u53f0\u5c06\u519b\uff0c\u591a\u8c22\u4f60\u6765\u63a5\u6211\uff0c\u54b1\u4eec\u8d70 \u5427\u3002\u201d\u5411\u5e08\u7236\u3001\u5e08\u53d4\u4f2f\u4eec\u884c\u4e86\u4e00\u793c\uff0c\u773c\u5149\u4ece\u4e91\u857e\u9762\u4e0a\u4e00\u63a0\u800c\u8fc7\uff0c\u6025\u6025\u8f6c\u8eab\u4fbf\u8d70\u3002\u80cc\u540e\u4f20\u6765\u4e86 \u8c22\u5929\u534e\u4e0e\u53f6\u76c8\u76c8\u7684\u53f9\u606f\u4e4b\u58f0\u3002\u4e91\u857e\u9893\u7136\u5750\u5728\u5730\u4e0a\uff0c\u773c\u6cea\u6d41\u4e0d\u51fa\u6765\u3002\u597d\u5728\u5f20\u4e39\u67ab\u4e0d\u6562\u56de\u5934\uff0c\u82e5 \u7136\u56de\u5934\uff0c\u53ea\u8981\u671b\u5979\u4e00\u773c\uff0c\u4e24\u4eba\u53ea\u6050\u5c31\u8981\u75db\u54ed\u76f8\u62e5\uff0c\u8c01\u4e5f\u4e0d\u5fcd\u8d70\u5f00\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u4e0e\u6fb9\u53f0\u706d\u660e\u8d70\u5230\u5c71\u4e0b\uff0c\u65e5\u5934\u5df2\u843d\uff0c\u661f\u661f\u6b63\u5728\u5929\u8fb9\u7728\u773c\uff0c\u4e24\u4eba\u5c31\u5728\u5c71\u811a\u7684\u730e\u6237\u4eba\u5bb6 \u501f\u5bbf\u4e00\u665a\u3002\u7b2c\u4e8c\u65e5\u4e00\u65e9\uff0c\u5f20\u4e39\u67ab\u5728\u5c71\u811a\u5bfb\u5230\u4e86\u4ed6\u7684\u90a3\u5339\u7167\u591c\u72ee\u5b50\u9a6c\uff0c\u90a3\u5339\u9a6c\u771f\u662f\u5b9d\u9a6c\uff0c\u5f20\u4e39 \u67ab\u5728\u5c71\u4e0a\u9017\u7559\u4e86\u5dee\u4e0d\u591a\u5341\u65e5\uff0c\u5b83\u5728\u5c71\u4e0b\u81ea\u89c5\u6c34\u8349\uff0c\u7adf\u7136\u4e00\u76f4\u7b49\u7740\u4e3b\u4eba\uff0c\u6ca1\u6709\u79bb\u5f00\uff0c\u4e00\u89c1\u4e3b \u4eba\uff0c\u4fbf\u5636\u53eb\u8df3\u8dc3\uff0c\u6b22\u6b23\u4e4b\u6781\u3002\u5f20\u4e39\u67ab\u63fd\u7740\u9a6c\u9888\uff0c\u60f3\u8d77\u4e86\u4e0e\u4e91\u857e\u5e76\u9a6c\u9a70\u9a71\u7684\u60c5\u666f\uff0c\u53c8\u4e0d\u7981\u51c4\u7136 \u6cea\u4e0b\u3002 \u3000\u3000\u6fb9\u53f0\u706d\u660e\u9053\uff1a\u201c\u6709\u6b64\u5b9d\u9a6c\uff0c\u54b1\u4eec\u4e0d\u987b\u5341\u65e5\uff0c\u4fbf\u53ef\u8d76\u56de\u90fd\u57ce\u5566\u3002\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u74e6\u523a\u4eac\u57ce \u8fd1\u4e8b\u5982\u4f55\uff1f\u201d\u6fb9\u53f0\u706d\u660e\u9053\uff1a\u201c\u5916\u8868\u867d\u7136\u5e73\u9759\uff0c\u5176\u5b9e\u5374\u662f\u5c71\u96e8\u6b32\u6765\u3002\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d \u6fb9\u53f0\u706d\u660e\u9053\uff1a\u201c\u963f\u523a\u77e5\u9662\u8054\u7edc\u5404\u90e8\uff0c\u6b32\u8d77\u4e49\u5175\u3002\u4e5f\u5148\u6025\u6b32\u4e0e\u4e2d\u56fd\u8bb2\u548c\uff0c\u6211\u79bb\u5f00\u90fd\u57ce\u4e4b\u65e5\uff0c\u542c \u8bf4\u5927\u660e\u671d\u5ef7\u5df2\u6d3e\u51fa\u8bb2\u548c\u7684\u4f7f\u8005\u4e86\u3002\u4f46\u613f\u8fd9\u4f7f\u8005\u80fd\u5728\u4ed6\u4eec\u4e24\u65b9\u4ea4\u5175\u4e4b\u524d\u6765\u5230\uff0c\u5426\u5219\u4ecd\u6050\u6709 \u53d8\u3002\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u6211\u7236\u4eb2\u5462\uff1f\u201d\u6fb9\u53f0\u706d\u660e\u9053\uff1a\u201c\u4ed6\u5df2\u8f9e\u4e86\u5bb0\u76f8\u804c\u4f4d\uff0c\u73b0\u5728\u4e13\u5019\u5927\u660e\u7684\u4f7f\u8005 \u5230\u6765\u3002\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u4ed6\u8fd8\u6ca1\u6709\u51b3\u5fc3\u56de\u56fd\u5417\uff1f\u201d\u6fb9\u53f0\u706d\u660e\u6447\u4e86\u6447\u5934\u9053\uff1a\u201c\u73b0\u5728\u8c01\u4e5f\u4e0d\u6562\u529d \u4ed6\u3002\u4ed6\u7559\u5728\u74e6\u523a\u90fd\u57ce\uff0c\u867d\u8bf4\u5df2\u65e0\u804c\u4f4d\uff0c\u4e5f\u5148\u4ecd\u662f\u4e0d\u653e\u5fc3\u4ed6\u3002\u4e45\u4f4f\u6b64\u95f4\uff0c\u53ea\u6050\u5fc5\u6709\u5371\u9669\uff0c\u770b\u6765 \u53ea\u6709\u4f60\u52a8\u529d\u4ed6\u4e86\u3002\u201d \u3000\u3000\u5f20\u4e39\u67ab\u542c\u4e86\uff0c\u60f3\u8d77\u81ea\u5df1\u8fd9\u51e0\u65e5\u5931\u9b42\u843d\u9b44\uff0c\u5fc5\u4e4e\u8bef\u4e86\u5927\u4e8b\uff0c\u5fc3\u4e2d\u6697\u547c\u60ed\u6127\u3002\u8de8\u4e0a\u5b9d\u9a6c\uff0c\u7acb \u5373\u8d76\u8def\u3002 \u3000\u3000\u4e00\u8def\u4e4b\u4e0a\uff0c\u6fb9\u53f0\u706d\u660e\u90fd\u4e0d\u6562\u548c\u4ed6\u63d0\u8d77\u4e91\u857e\uff0c\u9a6c\u884c\u8fc5\u901f\uff0c\u4e2d\u5348\u65f6\u5206\uff0c\u7ecf\u8fc7\u5510\u53e4\u62c9\u5c71\u5357\u9762\u5ce1 \u8c37\u6115\u7f57\u65cf\u7684\u805a\u5c45\u4e4b\u5730\uff0c\u5341\u591a\u5929\u524d\u5f20\u4e39\u67ab\u66fe\u4e0e\u4e91\u857e\u62dc\u4f1a\u8fc7\u8be5\u65cf\u7684\u914b\u957f\uff0c\u8349\u539f\u4e0a\u6709\u4e9b\u7267\u4eba\u8fd8\u8ba4\u8bc6 \u4ed6\u8fdc\u8fdc\u8ddf\u4ed6\u62db\u547c\uff0c\u5f20\u4e39\u67ab\u6025\u5fd9\u5feb\u9a6c\u52a0\u97ad\uff0c\u75be\u9a70\u800c\u8fc7\uff0c\u7d2f\u5f97\u6fb9\u53f0\u706d\u660e\u8d76\u4e86\u597d\u4e00\u4f1a\u5b50\u624d\u8d76\u5f97\u4e0a\u3002 \u3000\u3000\u6fb9\u53f0\u706d\u660e\u4e0d\u77e5\u5c31\u91cc\uff0c\u7b11\u9053\uff1a\u201c\u4e39\u67ab\uff0c\u4f60\u4eba\u7f18\u5012\u5f88\u597d\u554a\uff01\u201d\u5f20\u4e39\u67ab\u5728\u9eef\u7136\u4e0d\u8bed\u3002\u5ffd\u542c\u5f97\u9a6c \u5636\u4e4b\u58f0\uff0c\u90a3\u5339\u201c\u7167\u591c\u72ee\u5b50\u9a6c\u201d\u7a81\u7136\u653e\u6162\u811a\u6b65\uff0c\u5636\u545c\u76f8\u5e94\u3002\u5f20\u4e39\u67ab\u4e3e\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u9053\u65c1\u4e00\u95f4\u7834 \u7834\u70c2\u70c2\u7684\u6ce5\u5c4b\uff0c\u5c4b\u5b50\u5916\u8fb9\u7684\u67af\u6811\u4e0a\uff0c\u6b63\u7cfb\u7740\u4e91\u857e\u90a3\u5339\u7ea2\u9a6c\uff0c\u539f\u6765\u6b63\u7ecf\u8fc7\u4e91\u857e\u7684\u5bb6\uff0c\u4e91\u857e\u56e0\u8981 \u6276\u6301\u8001\u7236\u4e0a\u5c71\uff0c\u4e58\u9a6c\u4e0d\u4fbf\uff0c\u6240\u4ee5\u5c06\u5b83\u7559\u5728\u5bb6\u91cc\u3002\u4e24\u5339\u9a6c\u76f8\u5bf9\u5636\u9e23\uff0c\u56db\u8e44\u8df3\u8dc3\uff0c\u6fb9\u53f0\u706d\u660e\u597d\u751f \u5947\u602a\u7b11\u9053\uff1a\u201c\u8fd9\u662f\u8c01\u4eba\u6240\u5c45\uff1f\u77a7\u4e0d\u51fa\u8fd9\u95f4\u7834\u5c4b\u7684\u4e3b\u4eba\u5012\u517b\u6709\u4e00\u5339\u5343\u91cc\u826f\u9a79\u3002\u4e39\u67ab\uff0c\u600e\u4e48\uff0c\u600e \u4e48\u4f60\u7684\u9a6c\u513f\u2026\u2026\u201d\u6b63\u60f3\u8bf4\u201c\u600e\u4e48\u4f60\u7684\u9a6c\u513f\u5012\u597d\u50cf\u4e0e\u5b83\u662f\u591a\u5e74\u7684\u8001\u53cb\u4f3c\u7684\uff1f\u201d\u5ffd\u89c1\u5f20\u4e39\u67ab\u9762\u5982 \u7070\u571f\uff0c\u773c\u4e2d\u542b\u6cea\u6b32\u6ef4\uff0c\u6fb9\u53f0\u706d\u660e\u5927\u4e3a\u60ca\u9a87\uff0c\u6025\u5fd9\u505c\u53e3\u4e0d\u8bed\u3002\u53ea\u542c\u5f97\u5f20\u4e39\u67ab\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u4ef0 \u5929\u541f\u9053\uff1a\u201c\u90a3\u582a\u91cd\u8fc7\u4f24\u5fc3\u5730\uff0c\u9ec4\u53f6\u897f\u98ce\u603b\u65ad\u80a0\u3002\u5440\u5440\uff0c\u9a6c\u72b9\u5982\u6b64\uff0c\u4eba\u4f55\u4ee5\u582a\uff1f\u201d\u7834\u5c4b\u5185\u5ffd\u7136 \u4f20\u51fa\u4eba\u58f0\u4f3c\u662f\u5c4b\u5185\u7684\u4e3b\u4eba\u6b63\u8981\u8d76\u51fa\u6765\uff0c\u5f20\u4e39\u67ab\u5ffd\u5730\u91cd\u91cd\u4e00\u97ad\uff0c\u6253\u5728\u767d\u9a6c\u80cc\u4e0a\uff0c\u8fd9\u5339\u9a6c\u76f8\u968f\u5f20 \u4e39\u67ab\u591a\u5e74\uff0c\u672a\u5c1d\u53d7\u8fc7\u4e3b\u4eba\u5982\u6b64\u97ad\u6253\uff0c\u7acb\u523b\u653e\u5f00\u56db\u8e44\u75be\u8dd1\uff0c\u52bf\u5982\u5954\u96f7\u9010\u7535\uff0c\u628a\u6fb9\u53f0\u706d\u660e\u8fdc\u8fdc\u7529 \u5728\u540e\u9762\u3002\u6fb9\u53f0\u706d\u660e\u6447\u4e86\u6447\u5934\uff0c\u53eb\u9053\uff1a\u201c\u4e39\u67ab\uff0c\u4f60\u5fc3\u91cc\u4e0d\u75db\u5feb\uff0c\u4f55\u82e6\u4f5c\u8d31\u755c\u751f\uff1f\u201d\u5f20\u4e39\u67ab\u75db\u54ed \u5931\u58f0\uff0c\u8f7b\u6276\u9a6c\u80cc\uff0c\u8fd9\u9a6c\u4e00\u653e\u5f00\u4e86\u811a\u6b65\uff0c\u54ea\u6536\u5f97\u4f4f\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u8dd1\u51fa\u4e86\u5341\u591a\u91cc\u8def\uff0c\u5f85\u5f97\u6fb9\u53f0\u706d \u660e\u8d76\u4e0a\u6765\u65f6\uff0c\u53ea\u89c1\u5f20\u4e39\u67ab\u5df2\u6536\u4e86\u773c\u6cea\uff0c\u505c\u5728\u4e00\u95f4\u9053\u65c1\u7684\u9152\u8086\u95e8\u524d\u3002\u6fb9\u53f0\u706d\u660e\u867d\u7136\u89c1\u5f20\u4e39\u67ab\u7684 \u72c2\u6001\uff0c\u4e5f\u4e3a\u4ed6\u4eca\u65e5\u7684\u5927\u5931\u5e38\u6001\u800c\u62c5\u5fc3\uff0c\u505c\u9a6c\u95ee\u9053\uff1a\u201c\u4e39\u67ab\uff0c\u4f60\u600e\u4e48\u5566\uff1f\u201d \u3000\u3000\u5f20\u4e39\u67ab\u5927\u58f0\u9053\uff1a\u201c\u6765\u6765\uff0c\u54b1\u4eec\u4e14\u5728\u8fd9\u91cc\u75db\u996e\u4e00\u573a\u3002\u201d\u6fb9\u53f0\u706d\u660e\u9053\uff1a\u201c\u54b1\u4eec\u8fd8\u8981\u8d76\u8def\u3002\u201d \u5f20\u4e39\u67ab\u7b11\u9053\uff1a\u201c\u6709\u9152\u4fbf\u5f53\u4e00\u9189\uff0c\u9189\u4e86\u6b63\u597d\u8d76\u8def\u3002\u6fb9\u53f0\u5c06\u519b\uff0c\u4f60\u4eca\u65e5\u600e\u7684\u8fd9\u4e48\u4e0d\u723d\u5feb\uff1f\u201d\u4e0d\u7531 \u5206\u8bf4\uff0c\u5c06\u6fb9\u53f0\u706d\u660e\u62c9\u5165\u9152\u8086\uff0c\u53eb\u9053\uff1a\u201c\u6709\u9a6c\u5976\u9152\u4e48\uff1f\u201d\u9a6c\u5976\u9152\u662f\u8499\u53e4\u6700\u666e\u901a\u7684\u8d31\u4ef7\u9152\uff0c\u9152\u8086 \u4e3b\u4eba\u7ffb\u8d77\u4e86\u4e00\u53cc\u767d\u773c\uff0c\u9053\uff1a\u201c\u9a6c\u5976\u9152\u6709\u7684\u662f\uff0c\u4f60\u8981\u591a\u5c11\uff0c\u8bf7\u5148\u4ed8\u94b1\u3002\u201d\u5f20\u4e39\u67ab\u5927\u58f0\u53eb\u9053\uff1a \u201c\u6253\u516d\u4e03\u65a4\u6765\u3002\u201d\u556a\u7684\u5c06\u4e00\u952d\u5927\u94f6\u4e22\u5230\u9152\u67dc\u4e0a\uff0c\u9053\uff1a\u201c\u8fd9\u662f\u9152\u94b1\uff0c\u90fd\u628a\u7ed9\u4f60\uff0c\u4f11\u5f97\u7f57\u5506\uff0c\u4ffa \u4e0d\u559c\u6b22\u4f60\u767d\u773c\u770b\u4eba\uff0c\u4f60\u77e5\u9053\u4e48\uff1f\u201d\u9152\u8086\u4e3b\u4eba\u5413\u4e86\u4e00\u8df3\uff0c\u8d76\u5fd9\u6362\u4e86\u4e00\u526f\u7b11\u8138\uff0c\u5fc3\u4e2d\u5374\u9053\uff1a\u201c\u8fd9 \u5c0f\u4f19\u5b50\u539f\u6765\u662f\u5148\u5728\u522b\u5904\u559d\u9189\u4e86\u3002\u201d \u3000\u3000\u8fd9\u95f4\u5c0f\u9152\u8086\u7684\u9a6c\u5976\u9152\u917f\u5f97\u53c8\u9178\u53c8\u6da9\uff0c\u6fb9\u53f0\u706d\u660e\u559d\u4e86\u4e24\u53e3\u5c31\u76b1\u8d77\u7709\u5934\uff0c\u53ea\u89c1\u5f20\u4e39\u67ab\u5982\u957f\u9cb8 \u5438\u5ddd\uff0c\u4e00\u8fde\u5c3d\u4e86\u516d\u4e03\u5927\u7897\uff0c\u8fde\u8fde\u53eb\u9053\uff1a\u201c\u597d\u9152\uff0c\u597d\u9152\uff01\u201d\u9189\u773c\u8ff7\u79bb\u4e2d\u4e91\u857e\u7684\u5f71\u5b50\u4e0d\u4f4f\u6643\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u8bb0\u8d77\u521d\u4e0e\u4e91\u857e\u7f14\u4ea4\u4e4b\u65f6\u7684\u60c5\u666f\uff0c\u90a3\u65f6\u81ea\u5df1\u4ea6\u66fe\u996e\u4e86\u4e00\u5927\u846b\u82a6\u7684\u9a6c\u5976\u9152\uff0c\u72c2\u6b4c\u75db \u54ed\uff0c\u62ab\u5fc3\u76f8\u89c1\u3002\u800c\u4eca\u56de\u9996\u524d\u5c18\uff0c\u4f0a\u4eba\u5df2\u6773\uff0c\u7981\u4e0d\u4f4f\u60b2\u4ece\u4e2d\u6765\uff0c\u4e0d\u53ef\u65ad\u7edd\u3002 \u3000\u3000\u6fb9\u53f0\u706d\u660e\u53ea\u559d\u4e86\u51e0\u53e3\uff0c\u773c\u770b\u90a3\u516d\u4e03\u65a4\u9152\u90fd\u5feb\u8981\u88ab\u5f20\u4e39\u67ab\u4e00\u4eba\u559d\u5b8c\u3002\u6fb9\u53f0\u706d\u660e\u8fde\u8fde\u50ac\u9053\uff1a \u201c\u597d\u5566\uff0c\u5e94\u8be5\u8d70\u5566\u3002\u201d\u5f20\u4e39\u67ab\u82e6\u7b11\u4e00\u58f0\u653e\u4e0b\u9152\u76c5\uff0c\u5ffd\u542c\u5f97\u5916\u9762\u53c8\u6709\u9a6c\u5636\u4e4b\u58f0\uff0c\u6709\u4eba\u53eb\u9053\uff1a \u201c\u7fe0\u51e4\uff0c\u4f60\u77a7\uff0c\u771f\u662f\u5f20\u4e39\u67ab\u7684\u90a3\u5339\u7167\u591c\u72ee\u5b50\u9a6c\uff01\u201d \u3000\u3000\u53ea\u89c1\u4e00\u7537\u4e00\u5973\u98de\u6b65\u5165\u6765\uff0c\u8d70\u5728\u524d\u9762\u7684\u662f\u5468\u5c71\u6c11\uff0c\u540e\u9762\u7684\u662f\u77f3\u7fe0\u51e4\u3002\u5468\u5c71\u6c11\u9053\uff1a\u201c\u4e39\u67ab\u6211 \u627e\u5f97\u4f60\u597d\u82e6\uff0c\u5374\u60f3\u4e0d\u5230\u5728\u8fd9\u91cc\u76f8\u89c1\u3002\u201d\u77f3\u7fe0\u51e4\u5374\u201c\u54a6\u201d\u4e86\u4e00\u58f0\uff0c\u60ca\u8be7\u8bf4\u9053\uff1a\u201c\u4e39\u67ab\uff0c\u4e91\u857e\u59d0 \u59d0\u5462\uff1f\u5979\u600e\u4e48\u4e0d\u548c\u4f60\u4e00\u9053\uff1f\u201d \u3000\u3000\u5f20\u4e39\u67ab\u6447\u6447\u6643\u6643\u541f\u9053\uff1a\u201c\u4eba\u6709\u60b2\u6b22\u79bb\u5408\uff0c\u6709\u6708\u9634\u6674\u5706\u7f3a\uff0c\u6b64\u4e8b\u53e4\u96be\u5168\uff01\u4f60\u7559\u4e0d\u4f4f\u5979\uff0c\u6211 \u53c8\u600e\u80fd\u7559\u5f97\u4f4f\u5979\u3002\u5440\uff0c\u5440\uff0c\u4f46\u613f\u4eba\u957f\u4e45\uff0c\u5343\u91cc\u5171\u5a75\u5a1f\u3002\u201d\u77f3\u7fe0\u51e4\u53ea\u9053\u5f20\u4e39\u67ab\u62ff\u5979\u7684\u65e7\u4e8b\u6765\u5f00 \u73a9\u7b11\uff0c\u53d6\u7b11\u5979\u4ee5\u524d\u8bef\u5c06\u4e91\u857e\u5f53\u4f5c\u7537\u5b50\uff0c\u75f4\u7f20\u4e91\u857e\u4e4b\u4e8b\uff0c\u53cc\u988a\u901a\u7ea2\u5550\u4e86\u4e00\u53e3\u9053\uff1a\u201c\u4eba\u5bb6\u6709\u6b63\u7ecf \u4e8b\u627e\u4f60\uff0c\u4f60\u5374\u80e1\u8bf4\u516b\u9053\uff01\u201d \u3000\u3000\u5f20\u4e39\u67ab\u970d\u7136\u4e00\u60ca\uff0c\u9152\u610f\u9192\u4e86\u51e0\u5206\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u4eec\u600e\u4e48\u5230\u6b64\u5730\u627e\u6211\uff1f\u201d\u77f3\u7fe0\u51e4\u7b11\u9053\uff1a\u201c\u6211 \u4eec\u5230\u4e86\u4e91\u857e\u59d0\u59d0\u5bb6\u4e2d\uff0c\u89c1\u5230\u4e91\u4f2f\u6bcd\u4e86\u3002\u4f60\u548c\u4e91\u857e\u59d0\u59d0\u662f\u4e0d\u662f\u95f9\u4e86\u522b\u626d\uff1f\u4f2f\u6bcd\u8bf4\u4f60\u672c\u6765\u662f\u548c\u4e91 \u857e\u4e00\u540c\u6765\u627e\u5979\u7684\uff0c\u540e\u6765\u5374\u72ec\u81ea\u8d70\u4e86\u3002\u5979\u53c8\u8bf4\u857e\u59d0\u59d0\u524d\u51e0\u5929\u521a\u548c\u5979\u7236\u4eb2\u51fa\u95e8\uff0c\u6211\u8fd8\u4ee5\u4e3a\u4ed6\u4eec\u662f \u627e\u4f60\u5462\u3002\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u6211\u9002\u624d\u8def\u8fc7\u4e4b\u65f6\uff0c\u597d\u50cf\u542c\u5f97\u91cc\u9762\u6709\u51e0\u4e2a\u4eba\u8bf4\u8bdd\u7684\u58f0\u97f3\uff0c\u539f\u6765 \u662f\u4f60\u4eec\u3002\u201d\u77f3\u7fe0\u51e4\u9053\uff1a\u201c\u6211\u4eec\u521a\u521a\u5bfb\u5230\uff0c\u624d\u5750\u5f97\u4e00\u4f1a\u513f\uff0c\u5c31\u542c\u5f97\u4f60\u90a3\u5339\u5b9d\u8d1d\u9a6c\u513f\u7684\u53eb\u58f0\uff0c\u6211 \u4eec\u8d76\u51fa\u6765\uff0c\u4f60\u5df2\u7ecf\u53bb\u5f97\u8fdc\u4e86\u3002\u6211\u4eec\u6025\u6025\u8ffd\u8d76\uff0c\u8d76\u5230\u73b0\u5728\u624d\u8ffd\u4e0a\u4f60\u4eec\u3002\u54a6\uff0c\u8bf4\u6765\u6211\u5012\u8981\u95ee\u4f60 \u4e86\uff0c\u4f60\u5c31\u7b97\u548c\u4e91\u857e\u59d0\u59d0\u95f9\u4e86\u522b\u626d\uff0c\u4e5f\u4e0d\u8be5\u5982\u6b64\u65e0\u793c\uff0c\u600e\u4e48\u8fc7\u5176\u95e8\u800c\u4e0d\u5165\u5462\uff1f\u4e91\u4f2f\u6bcd\u591a\u53ef\u601c\uff0c \u4f60\u4e5f\u8be5\u53bb\u770b\u770b\u5979\u3002\u201d \u3000\u3000\u5f20\u4e39\u67ab\u500f\u7136\u53d8\u8272\uff0c\u773c\u5982\u5b9a\u73e0\uff0c\u5782\u9996\u80f8\u81c6\u3002\u77f3\u7fe0\u51e4\u597d\u751f\u5947\u602a\u9053\uff1a\u201c\u4e91\u857e\u59d0\u59d0\u6027\u60c5\u6700\u4e3a\u548c \u987a\uff0c\u4e00\u5b9a\u662f\u4f60\u5f97\u7f6a\u4e86\u5979\uff0c\u6240\u4ee5\u5979\u624d\u4e0d\u7406\u4f60\u3002\u4ec0\u4e48\u4e8b\u513f\uff0c\u8bf4\u7ed9\u6211\u542c\u542c\uff0c\u6211\u66ff\u4f60\u5411\u5979\u8d54\u7f6a\u3002\u201d\u683c \u683c\u5730\u7b11\u4e2a\u4e0d\u4f11\u3002\u6fb9\u53f0\u706d\u660e\u6025\u5fd9\u63d2\u53e3\u9053\uff1a\u201c\u4f60\u5148\u8bf4\u6b63\u7ecf\u4e8b\u5427\uff0c\u4f60\u8fd8\u6ca1\u6709\u544a\u8bc9\u6211\u4eec\uff0c\u662f\u8c01\u544a\u8bc9\u4f60 \u4e91\u857e\u7684\u4f4f\u5740\uff1f\u201d\u77f3\u7fe0\u51e4\u7b11\u4e2a\u4e0d\u4f11\u9053\uff1a\u201c\u8fd9\u4e0d\u662f\u6b63\u7ecf\u4e8b\u5417\uff1f\u201d\u72b9\u5f85\u53d6\u7b11\uff0c\u5ffd\u89c1\u5f20\u4e39\u67ab\u9762\u8272\u60e8 \u767d\uff0c\u4e45\u4e45\u4e0d\u8bed\uff0c\u6014\u4e86\u4e00\u6014\uff0c\u6025\u5fd9\u6536\u53e3\u3002 \u3000\u3000\u5468\u5c71\u6c11\u9053\uff1a\u201c\u660e\u671d\u5df2\u6d3e\u51fa\u4f7f\u81e3\uff0c\u5c31\u5c06\u5230\u74e6\u523a\u6765\u8c08\u548c\u4e86\u3002\u201d\u6fb9\u53f0\u706d\u660e\u9053\uff1a\u201c\u8fd9\u4e2a\u6211\u65e9\u5df2\u77e5 \u9053\u3002\u201d\u5468\u5c71\u6c11\u9053\uff1a\u201c\u4f60\u731c\u4f7f\u81e3\u662f\u8c01\uff1f\u201d\u5f20\u4e39\u67ab\u5b9a\u4e86\u5b9a\u795e\uff0c\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u662f\u8c01\uff1f\u201d\u5468\u5c71\u6c11 \u9053\uff1a\u201c\u5c31\u662f\u4e91\u857e\u7684\u54e5\u54e5\uff01\u201d\u5f20\u4e39\u67ab\u5446\u4e86\u4e00\u5446\uff0c\u60f3\u8d77\u4e91\u91cd\u7d20\u6765\u5bf9\u81ea\u5df1\u542b\u6709\u654c\u610f\uff0c\u5982\u4eca\u4e00\u6765\uff0c\u81ea \u5df1\u548c\u4e91\u857e\u7684\u4e8b\u60c5\u66f4\u7edd\u671b\u4e86\u3002\u77f3\u7fe0\u51e4\u95ee\u9053\uff1a\u201c\u600e\u4e48\uff0c\u4f60\u4e0d\u9ad8\u5174\u5417\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u9ad8\u5174\u8fd8\u6765\u4e0d \u53ca\u5462\uff01\u4e91\u91cd\u505a\u4f7f\u81e3\uff0c\u90a3\u662f\u6700\u597d\u4e0d\u8fc7\u4e86\uff01\u201d \u3000\u3000\u5f20\u4e39\u67ab\u6240\u8bf4\u7684\u5012\u975e\u865a\u4f2a\u4e4b\u8bed\uff0c\u800c\u662f\u51fa\u81ea\u80ba\u8151\u3002\u987b\u77e5\u4e91\u91cd\u7684\u7237\u7237\u5f53\u5e74\u51fa\u4f7f\u74e6\u523a\uff0c\u7267\u9a6c\u80e1\u8fb9 \u53d7\u5c3d\u6298\u78e8\u3002\u800c\u4eca\u4e2d\u56fd\u7531\u5f31\u8f6c\u5f3a\uff0c\u7531\u4ed6\u7684\u5b59\u513f\u518d\u6765\u51fa\u4f7f\uff0c\u8fd9\u771f\u662f\u6700\u75db\u5feb\u4e4b\u4e8b\u3002\u4f55\u51b5\u4e91\u91cd\u4e00\u5fc3\u4e3a \u56fd\u521a\u5f3a\u80fd\u5e72\uff0c\u6bd4\u4ed6\u7684\u7237\u7237\u72b9\u80dc\u51e0\u5206\uff0c\u7531\u4ed6\u51fa\u4f7f\uff0c\u53ef\u89c1\u4e8e\u8c26\u77e5\u4eba\u4e4b\u660e\u3002\u5f20\u4e39\u67ab\u867d\u89c9\u4e91\u91cd\u5bf9\u81ea\u5df1 \u7684\u8bef\u4f1a\u4e4b\u6df1\uff0c\u751a\u662f\u9057\u61be\uff0c\u4f46\u90a3\u662f\u79c1\u4e8b\uff0c\u6545\u6b64\u542c\u5f97\u4e91\u91cd\u51fa\u4f7f\uff0c\u867d\u7981\u4e0d\u4f4f\u5446\u4e86\u4e00\u5446\uff0c\u5374\u4e3a\u56fd\u5bb6\u6df1 \u5e86\u5f97\u4eba\u3002 \u3000\u3000\u5468\u5c71\u6c11\u9053\uff1a\u201c\u4e91\u91cd\u7ecf\u8fc7\u96c1\u95e8\u5173\u4e4b\u65f6\uff0c\u66fe\u4e0e\u6211\u4eec\u76f8\u89c1\uff0c\u662f\u4ed6\u6258\u6211\u53bb\u5411\u4ed6\u7684\u6bcd\u4eb2\u62a5\u4fe1\uff0c\u8bf7\u5979 \u8001\u4eba\u5bb6\u5230\u74e6\u523a\u4eac\u57ce\u76f8\u4f1a\u7684\u3002\u60f3\u4e0d\u5230\u4ed6\u7684\u7236\u4eb2\u8fd8\u6d3b\u7740\u3002\u4f2f\u6bcd\u8bf4\uff0c\u5979\u7b49\u5230\u4e91\u857e\u56de\u6765\u65f6\uff0c\u518d\u548c\u4ed6\u4eec \u7236\u5973\u4e00\u540c\u4e0a\u4eac\u3002\u4e0d\u5fc5\u6211\u966a\u4e86\u3002\u201d\u5f20\u4e39\u67ab\u542c\u5230\u201c\u4e91\u857e\u201d\u4e8c\u5b57\uff0c\u8eab\u8eaf\u5fae\u5fae\u98a4\u6296\uff0c\u5468\u5c71\u6c11\u77a7\u4e86\u4ed6\u4e00 \u773c\uff0c\u53c8\u9053\uff1a\u201c\u4e91\u91cd\u5e26\u4e86\u5341\u516b\u540d\u5fa1\u524d\u4f8d\u536b\u505a\u968f\u4ece\uff0c\u53e6\u5916\u8fd8\u6709\u51e0\u4f4d\u5973\u5b50\u968f\u884c\u3002\u201d\u6fb9\u4ee5\u706d\u660e\u5947\u9053\uff1a \u201c\u4ec0\u4e48\uff0c\u8fd8\u6709\u5a18\u513f\u4eec\u968f\u884c\uff1f\u201d\u5468\u5c71\u6c11\u7b11\u9053\uff1a\u201c\u6fb9\u53f0\u5c06\u519b\uff0c\u542c\u8bf4\u968f\u884c\u7684\u5c31\u662f\u4f60\u7684\u59b9\u5b50\u82b3\u540d\u53eb\u505a \u6fb9\u53f0\u955c\u660e\u7684\u3002\u201d\u6fb9\u53f0\u706d\u660e\u559c\u9053\uff1a\u201c\u54c8\u5979\u4e5f\u6765\u4e86\u3002\u60f3\u662f\u6211\u7684\u5802\u53d4\u5979\u7684\u7236\u4eb2\u6d1e\u5ead\u5e84\u4e3b\u53eb\u5979\u6765\u63a5\u6211 \u7684\u3002\u201d\u5468\u5c71\u6c11\u9053\uff1a\u201c\u4e00\u70b9\u4e0d\u9519\uff0c\u606d\u559c\u4f60\u4eec\uff0c\u4f60\u4eec\u90fd\u53ef\u4ee5\u56de\u56fd\u4e86\u3002\u201d\u6b47\u4e86\u4e00\u6b47\uff0c\u53c8\u9053\uff1a\u201c\u90a3\u51e0 \u4e2a\u5973\u5b50\u90fd\u662f\u4f60\u4eec\u6fb9\u53f0\u6751\u7684\u4eba\uff0c\u662f\u4f60\u7684\u59b9\u5b50\u53eb\u5979\u4eec\u540c\u6765\u4f5c\u4f34\u7684\u3002\u201d\u6fb9\u53f0\u706d\u660e\u5fc3\u9053\uff1a\u201c\u955c\u660e\u8fd9\u5c0f \u599e\u513f\u5012\u60f3\u5f97\u5468\u5230\uff0c\u60f3\u662f\u4e0d\u613f\u5b64\u8eab\u4e0e\u4e91\u91cd\u4e00\u8d77\uff0c\u4ee5\u514d\u8d3b\u4eba\u53e3\u5b9e\u3002\u5440\uff0c\u4e39\u67ab\u5982\u6b64\u90c1\u95f7\uff0c\u82e5\u7136\u5c06\u955c \u660e\u8bb8\u914d\u4e0e\u4ed6\uff0c\u5012\u662f\u4e24\u5168\u5176\u7f8e\u3002\u201d\u6b63\u81ea\u9050\u601d\u53ea\u542c\u5f97\u5468\u5c71\u6c11\u53c8\u9053\uff1a\u201c\u4ed6\u4eec\u662f\u5929\u671d\u7684\u4f7f\u8282\uff0c\u4e00\u8def\u6709 \u4eba\u63a5\u5f85\uff0c\u6bcf\u5929\u53ea\u80fd\u8d70\u4e94\u516d\u5341\u91cc\u3002\u4e5f\u8bb8\u8fd8\u8981\u5341\u591a\u624d\u80fd\u5230\u74e6\u523a\u4eac\u57ce\u5462\u3002\u6211\u5012\u662f\u4e3a\u4ed6\u4eec\u62c5\u5fc3\u3002\u201d\u5f20 \u4e39\u67ab\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u5468\u5c71\u6c11\u9053\uff1a\u201c\u4e24\u56fd\u5728\u6218\u4e71\u4e4b\u540e\uff0c\u5230\u5904\u90fd\u6709\u9ed1\u9053\u7684\u4eba\u7269\u5d1b\u8d77\u3002\u4e91\u91cd\u867d\u7136\u5e26 \u4e86\u5341\u516b\u540d\u5fa1\u524d\u4f8d\u536b\uff0c\u4e5f\u5f97\u63d0\u9632\u53d1\u751f\u610f\u5916\u3002\u5728\u96c1\u95e8\u5173\u5185\uff0c\u6709\u6211\u4eec\u4f20\u4e0b\u4e86\u7eff\u6797\u7bad\u53ef\u4fdd\u65e0\u4e8b\u3002\u5230\u4e86 \u96c1\u95e8\u5173\u5916\uff0c\u90a3\u5c31\u975e\u6211\u4eec\u4e4b\u529b\u6240\u53ca\u4e86\u3002\u201d\u6fb9\u53f0\u706d\u660e\u9053\uff1a\u201c\u8fd9\u6b21\u662f\u4e5f\u5148\u6709\u5fc3\u5411\u660e\u671d\u8c08\u548c\uff0c\u660e\u671d\u7684 \u4f7f\u81e3\u82e5\u5728\u74e6\u523a\u5883\u5185\u51fa\u4e8b\uff0c\u4ed6\u4e5f\u96be\u4ee5\u4e0b\u53f0\u3002\u201d\u5468\u5c71\u6c11\u9053\uff1a\u201c\u8bdd\u867d\u5982\u6b64\uff0c\u4f46\u4e5f\u5148\u5978\u72e1\uff0c\u4e2d\u5916\u7686 \u77e5\uff0c\u5fc3\u8179\u96be\u6d4b\u3002\u4f55\u51b5\u74e6\u523a\u4e5f\u5728\u56db\u5206\u4e94\u88c2\u4e4b\u4e2d\uff0c\u672a\u5fc5\u90fd\u542c\u4e5f\u5148\u53f7\u4ee4\u3002\u74e6\u523a\u7684\u7eff\u6797\u5927\u76d7\u90a3\u66f4\u4e0d\u7528 \u8bf4\u4e86\u3002\u8fd8\u662f\u5c0f\u5fc3\u63d0\u9632\u7684\u597d\u3002\u6211\u5c31\u662f\u60f3\u548c\u4f60\u4eec\u5546\u91cf\uff0c\u8981\u4e0d\u8981\u6d3e\u51e0\u4e2a\u5f97\u529b\u7684\u4eba\u53bb\u63a5\u4ed6\u4eec\u5462\uff1f\u201d \u3000\u3000\u5f20\u4e39\u67ab\u4e00\u76f4\u9ed8\u9ed8\u4e0d\u8bed\uff0c\u542c\u8bf4\u81f3\u6b64\uff0c\u5ffd\u7136\u53eb\u9053\uff1a\u201c\u5468\u5927\u54e5\uff0c\u77f3\u8d24\u59b9\uff0c\u6211\u656c\u4f60\u4eec\u4e00\u7897\u9152\uff01\u201d \u7aef\u8d77\u5927\u7897\uff0c\u4e00\u996e\u800c\u5c3d\u3002\u5468\u5c71\u6c11\u3001\u77f3\u7fe0\u51e4\u6115\u7136\u770b\u4ed6\uff0c\u53ea\u89c1\u5f20\u4e39\u67ab\u559d\u5b8c\u4e4b\u540e\uff0c\u5c06\u7897\u4e00\u6454\u54c8\u54c8\u7b11 \u9053\uff1a\u201c\u5468\u5927\u54e5\uff0c\u6211\u7684\u5c0f\u9a6c\u5feb\uff0c\u5148\u8d70\u4e00\u6b65\u4e86\u3002\u4f60\u653e\u5fc3\uff0c\u6211\u62c5\u4fdd\u4e91\u5927\u54e5\u5e73\u5b89\u5230\u8fbe\u74e6\u523a\u4eac\u57ce\uff01\u201d\u98de \u8eab\u4e0a\u9a6c\uff0c\u90a3\u9a6c\u4e00\u58f0\u957f\u5636\uff0c\u653e\u5f00\u56db\u8e44\uff0c\u7acb\u523b\u7edd\u5c18\u800c\u53bb\u3002\u6fb9\u53f0\u706d\u660e\u7684\u5750\u9a91\u662f\u8499\u53e4\u6700\u4f73\u7684\u9a6c\u79cd\uff0c\u72b9 \u81ea\u8d76\u5b83\u4e0d\u4e0a\uff0c\u5468\u5c71\u6c11\u4e0e\u77f3\u7fe0\u51e4\u7684\u9a6c\u90a3\u5c31\u66f4\u4e0d\u7528\u8bf4\u4e86\u3002 \u3000\u3000\u4e09\u65e5\u4e4b\u540e\uff0c\u5f20\u4e39\u67ab\u56de\u5230\u74e6\u523a\u4eac\u57ce\uff0c\u4f46\u89c1\u8857\u9053\u4e0a\u884c\u4eba\u7199\u6765\u6518\u5f80\uff0c\u7eb7\u7eb7\u6270\u6270\uff0c\u4e89\u8d2d\u7cae\u98df\u3002\u539f \u6765\u662f\u4ed6\u4eec\u95fb\u5f97\u98ce\u58f0\uff0c\u751f\u6015\u4e5f\u5148\u592a\u5e08\u4e0e\u963f\u523a\u77e5\u9662\u5f00\u6218\uff0c\u6545\u6b64\u5148\u628a\u65e5\u5e38\u6240\u9700\u8981\u7684\u7269\u54c1\u56e4\u79ef\u8d77\u6765\u3002 \u5f20\u4e39\u67ab\u5fc3\u4e2d\u53f9\u9053\uff1a\u201c\u82e5\u7136\u5929\u4e0b\u5347\u5e73\uff0c\u6c38\u65e0\u6218\u4e8b\uff0c\u90a3\u53ef\u591a\u597d\uff01\u201d\u53c8\u60f3\u9053\uff1a\u201c\u6218\u6c1b\u5f25\u6f2b\uff0c\u6218\u673a\u7d27 \u8feb\uff0c\u4e5f\u5148\u66f4\u8981\u6025\u4e8e\u4e0e\u4e2d\u56fd\u8c0b\u548c\u4e86\u3002\u770b\u6765\u4e91\u91cd\u7684\u8fd0\u6c14\u8981\u6bd4\u4ed6\u7684\u7237\u7237\u597d\u5f97\u591a\uff0c\u8fd9\u6b21\u4ed6\u5b9a\u53ef\u4e0d\u8fb1\u4f7f \u547d\uff0c\u987a\u5229\u7f14\u548c\uff0c\u5e76\u5c06\u4ed6\u4eec\u7684\u7687\u5e1d\u8001\u513f\u63a5\u56de\u53bb\u4e86\u3002\u201d\u56de\u5230\u5bb6\u4e2d\uff0c\u53ea\u89c1\u5bb6\u4eba\u7980\u9053\uff1a\u201c\u5c11\u7237\uff0c\u4f60\u73b0 \u5728\u624d\u56de\u6765\uff0c\u8001\u7237\u65e5\u65e5\u90fd\u5728\u76fc\u671b\u4f60\u5462\u3002\u8001\u7237\u8fd9\u51e0\u65e5\u90fd\u8eba\u5728\u5e8a\u4e0a\uff0c\u4e0d\u4f4f\u5730\u53eb\u4eba\u51fa\u53bb\u63a2\u671b\uff0c\u770b\u4f60\u56de \u6765\u6ca1\u6709\u5462\u3002\u201d \u3000\u3000\u5f20\u4e39\u67ab\u5403\u4e86\u4e00\u60ca\uff0c\u6025\u5fd9\u8d76\u5f80\u4e66\u623f\uff0c\u53ea\u89c1\u7236\u4eb2\u72ec\u81ea\u4e00\u4eba\uff0c\u5750\u5728\u4e66\u684c\u65c1\u8fb9\u5199\u5b57\uff0c\u542c\u5230\u4eba\u58f0\uff0c \u95ee\u9053\uff1a\u201c\u662f\u8c01\uff1f\u201d\u5f20\u4e39\u67ab\u677e\u4e86\u53e3\u6c14\uff0c\u5e94\u9053\uff1a\u201c\u662f\u6211\u3002\u7239\uff0c\u4f60\u6ca1\u4e8b\u4e48\uff1f\u201d\u5f20\u5b97\u5468\u56de\u8fc7\u5934\u6765\uff0c \u9053\uff1a\u201c\u6fb9\u53f0\u5c06\u519b\u5462\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u4ed6\u7684\u9a6c\u6162\uff0c\u5927\u7ea6\u8981\u5230\u660e\u5929\u65e9\u4e0a\u624d\u80fd\u5230\u5bb6\u3002\u542c\u5bb6\u4eba\u8bf4\uff0c\u4f60 \u8001\u4eba\u5bb6\u70b9\u4e0d\u8212\u670d\uff0c\u662f\u4ec0\u4e48\u75c5\uff1f\u8bf7\u7684\u662f\u54ea\u4f4d\u5927\u592b\uff1f\u201d\u5f20\u5b97\u5468\u9053\uff1a\u201c\u96be\u5f97\u4f60\u8fd9\u6837\u6302\u5ff5\u6211\u3002\u4e5f\u6ca1\u6709 \u4ec0\u4e48\uff0c\u662f\u8001\u6bdb\u75c5\uff0c\u8fd9\u534a\u6708\u6765\u5929\u6c14\u4e0d\u597d\uff0c\u843d\u4e86\u5341\u51e0\u5929\u96e8\uff0c\u524d\u65e5\u624d\u653e\u6674\uff0c\u6211\u7684\u819d\u76d6\u5173\u8282\u53c8\u4f5c\u75db \u4e86\u3002\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u4e3a\u4f55\u4e0d\u8bf7\u5927\u592b\uff1f\u201d\u5f20\u5b97\u5468\u7b11\u9053\uff1a\u201c\u6211\u6b63\u8981\u8bf4\u7ed9\u4f60\u542c\uff0c\u4f60\u5728\u77f3\u5ba4\u4e2d\u5e26\u56de\u90a3 \u51e0\u672c\u5f6d\u548c\u5c1a\u7684\u672d\u8bb0\u771f\u662f\u6709\u7528\u3002\u539f\u6765\u5176\u4e2d\u8fd8\u6709\u533b\u6cbb\u5173\u8282\u75bc\u75db\u7684\u7597\u6cd5\uff0c\u636e\u4e66\u4e0a\u8bf4\uff0c\u5c31\u7b97\u624b\u8db3\u8ddb \u4e86\uff0c\u4e5f\u53ef\u4ee5\u7528\u67f3\u679d\u63a5\u9aa8\u4e4b\u6cd5\u914d\u4ee5\u9488\u7078\u6cbb\u7597\uff0c\u5c06\u5b83\u533b\u597d\u5462\u3002\u201d\u5f6d\u548c\u5c1a\u5f53\u5e74\u6bcf\u5230\u4e00\u5904\u5730\u65b9\u90fd\u5199\u4e0b \u968f\u7b14\uff0c\u5176\u4e2d\u6709\u98ce\u571f\u4eba\u60c5\uff0c\u6709\u5c31\u7740\u5c71\u5ddd\u5f62\u52bf\u800c\u8c08\u5230\u7528\u5175\u7684\u8bae\u8bba\uff0c\u6709\u5404\u5730\u7684\u89c1\u95fb\u548c\u6536\u96c6\u7684\u5404\u79cd\u6c11 \u95f4\u9a8c\u65b9\uff0c\u6797\u6797\u603b\u603b\uff0c\u6240\u8bb0\u751a\u6742\u3002\u7559\u5728\u77f3\u6d1e\u4e4b\u4e2d\u7684\u672c\u6765\u662f\u65ad\u7b80\u96f6\u7bc7\uff0c\u5f20\u4e39\u67ab\u62ff\u4e86\u56de\u6765\u4e4b\u540e\u52a0\u4ee5 \u6574\u7406\uff0c\u8f91\u6210\u4e13\u4e66\uff0c\u7559\u5728\u5bb6\u4e2d\uff0c\u7ed9\u7236\u4eb2\u9605\u89c8\u3002\u5982\u4eca\u542c\u7236\u4eb2\u8bf4\u8d77\uff0c\u8fd9\u624d\u8bb0\u5f97\u5176\u4e2d\u679c\u7136\u6709\u8fd9\u4e00\u6761\uff0c \u5fc3\u4e2d\u4e00\u52a8\uff0c\u95ee\u9053\uff1a\u201c\u7239\u7239\uff0c\u4f60\u8bd5\u8fc7\u6ca1\u6709\uff1f\u201d\u5f20\u5b97\u5468\u7ad9\u8d77\u6765\u8d70\u4e86\u51e0\u6b65\uff0c\u53c8\u4f38\u811a\u8e22\u4e86\u51e0\u4e0b\uff0c\u9053\uff1a \u201c\u6211\u662f\u6628\u5929\u624d\u8bd5\u7528\u4ed6\u7684\u7597\u6cd5\u7684\uff0c\u53eb\u4eba\u5728\u811a\u677f\u7684\u7a74\u9053\u4e0a\u523a\u4e86\u51e0\u9488\uff0c\u679c\u7136\u4eca\u65e5\u4fbf\u80fd\u8d70\u52a8\u4e86\u3002\u201d\u5f20 \u4e39\u67ab\u9053\uff1a\u201c\u8fd9\u6837\u7075\u9a8c\uff0c\u53ef\u771f\u662f\u4e86\u4e0d\u5f97\u3002\u8fd9\u672c\u4e66\u6211\u53ef\u5f97\u518d\u4ed4\u7ec6\u5730\u8bfb\u4e00\u8bfb\u3002\u201d\u5f20\u5b97\u5468\u9053\uff1a\u201c\u5f6d\u548c \u5c1a\u662f\u6211\u4eec\u5927\u5468\u7684\u56fd\u5e08\uff0c\u505a\u8fc7\u4e24\u4e2a\u5929\u5b50\u7684\u5e08\u7236\uff0c\u5b66\u7a76\u5929\u4e0b\uff0c\u5f53\u7136\u662f\u975e\u540c\u5c0f\u53ef\uff0c\u4f60\u662f\u5e94\u8be5\u4ed4\u7ec6\u5730 \u8bfb\u8bfb\u3002\u201d\u5728\u4e66\u6848\u4e0a\u62bd\u51fa\u90a3\u672c\u4e66\uff0c\u4ea4\u4e0e\u4e86\u5f20\u4e39\u67ab\uff0c\u53eb\u4ed6\u5728\u81ea\u5df1\u8eab\u8fb9\u5750\u4e0b\uff0c\u559d\u4e86\u53e3\u8336\uff0c\u7b11\u9053\uff1a \u201c\u542c\u8bf4\u660e\u671d\u7684\u4f7f\u8005\u5c31\u8981\u6765\uff0c\u6211\u53ef\u653e\u4e0b\u5fc3\u4e86\uff0c\u4f46\u4e0d\u77e5\u6765\u7684\u662f\u8c01\uff1f\u82e5\u7136\u80fd\u50cf\u5f53\u5e74\u7684\u4e91\u9756\uff0c\u90a3\u5c31\u597d \u4e86\u3002\u201d\u8bf4\u7740\uff0c\u8bf4\u7740\uff0c\u58f0\u8c03\u5ffd\u8f6c\u82cd\u51c9\uff0c\u5f20\u4e39\u67ab\u77e5\u4ed6\u662f\u60f3\u8d77\u5f53\u5e74\u4e4b\u4e8b\uff0c\u5fc3\u4e2d\u5185\u759a\u3002\u8fd9\u970e\u90a3\u95f4\uff0c\u4e91 \u6f84\u6194\u60b4\u7684\u989c\u5bb9\uff0c\u4e91\u91cd\u5014\u5f3a\u7684\u5f62\u8c8c\uff0c\u4e91\u857e\u695a\u695a\u53ef\u601c\u7684\u6837\u5b50\uff0c\u4e00\u4e00\u5728\u5fc3\u5934\u6cdb\u8d77\uff0c\u60f3\u9053\uff1a\u201c\u6211\u7239\u7239 \u867d\u7136\u6b32\u89e3\u524d\u4ec7\uff0c\u4f46\u8fd9\u51a4\u4ec7\u5374\u5982\u4f55\u89e3\u5f97\uff1f\u201d \u3000\u3000\u5f20\u5b97\u5468\u9053\uff1a\u201c\u4e39\u67ab\uff0c\u4f60\u60f3\u4ec0\u4e48\uff1f\u201d\u5f20\u4e39\u67ab\u52c9\u5f3a\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6ca1\u4ec0\u4e48\uff0c\u6211\u4e5f\u5728\u731c\u660e\u671d\u7684\u4f7f \u81e3\u662f\u8c01\u5462\u3002\u201d\u4ed6\u8d77\u521d\u672c\u60f3\u628a\u4e91\u91cd\u51fa\u4f7f\u4e4b\u4e8b\u544a\u77e5\u7236\u4eb2\uff0c\u4f46\u8f6c\u5ff5\u4e00\u60f3\uff0c\u4e91\u6f84\u7236\u5b50\u5bf9\u81ea\u5df1\u4e00\u5bb6\u7684\u6028 \u6124\u5982\u6b64\u4e4b\u6df1\uff0c\u53ea\u6015\u5c06\u6765\u96be\u4ee5\u76f8\u8c05\uff0c\u82e5\u7136\u5982\u5b9e\u544a\u77e5\u7236\u4eb2\uff0c\u4ed6\u5b9a\u66f4\u4e3a\u4f24\u5fc3\uff0c\u66f4\u591a\u81ea\u759a\uff0c\u6545\u6b64\u5fcd \u4f4f\u3002 \u3000\u3000\u4e24\u7236\u5b50\u6c89\u9ed8\u4e00\u9635\uff0c\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u7239\uff0c\u4f60\u7684\u5fc3\u610f\u8fd8\u6ca1\u6539\uff1f\u201d\u5f20\u5b97\u5468\u81ea\u662f\u77e5\u4ed6\u6240\u6307\uff0c\u82e6\u7b11 \u9053\uff1a\u201c\u5230\u660e\u671d\u7684\u4f7f\u81e3\u6765\u540e\uff0c\u4f60\u5c31\u8ddf\u4ed6\u56de\u56fd\u3002\u4f46\u4e0d\u51c6\u4f60\u4f5c\u660e\u671d\u7684\u5b98\u3002\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u7239\u7239\u4f60 \u5462\uff1f\u201d\u5f20\u5b97\u5468\u9053\uff1a\u201c\u6211\u6b64\u751f\u53ea\u6709\u68a6\u4e2d\u56de\u5230\u6c5f\u5357\u4e86\u3002\u5510\u8bcd\u4eba\u97e6\u5e84\u8bf4\uff1a\u672a\u8001\u83ab\u8fd8\u4e61\uff0c\u8fd8\u4e61\u987b\u65ad \u80a0\u3002\u6211\u662f\u8001\u4ea6\u4e0d\u8fd8\u4e61\uff0c\u7686\u56e0\u6015\u65ad\u80a0\u3002\u4e39\u67ab\u4f60\u4f11\u5f97\u518d\u63d0\uff01\u201d\u5f20\u4e39\u67ab\u6253\u4e86\u4e00\u4e2a\u5bd2\u5664\uff0c\u611f\u89c9\u5230\u7236\u4eb2 \u5fc3\u5982\u69c1\u6728\uff0c\u7eb5\u662f\u6625\u987a\u5927\u5730\uff0c\u4e1c\u98ce\u5439\u62c2\uff0c\u4e5f\u96be\u4ee5\u53d1\u82bd\uff0c\u4e00\u4f4e\u5934\uff0c\u53ea\u89c1\u4e66\u684c\u4e0a\u7684\u4e00\u5f20\u8bcd\u7b3a\u58a8\u8ff9\u672a \u5e72\uff0c\u90a3\u662f\u9646\u6e38\u300a\u6c81\u56ed\u6625\u300b\u8bcd\u7684\u524d\u51e0\u53e5\uff1a\u201c\u5b64\u9e64\u5f52\u98de\uff0c\u518d\u8fc7\u8fbd\u5929\uff0c\u6362\u5c3d\u65e7\u4eba\uff0c\u5ff5\u7d2f\u7d2f\u67af\u51a2\uff0c\u832b \u832b\u68a6\u5883\uff0c\u738b\u4faf\u877c\u8681\uff0c\u6bd5\u7adf\u6210\u5c18\u3002\u201d\u60f3\u662f\u56e0\u4e3a\u81ea\u5df1\u8fdb\u6765\u6253\u65ad\uff0c\u6240\u4ee5\u6ca1\u6709\u5199\u5b8c\u3002\u7236\u4eb2\u5fc3\u60c5\u5982\u6b64\u8870 \u98d2\uff0c\u5f20\u4e39\u67ab\u4e0d\u7981\u5728\u5fc3\u4e2d\u53f9\u4e86\u53e3\u6c14\uff0c\u6b32\u8bf4\u8fd8\u4f11\u3002 \u3000\u3000\u8fd9\u4e00\u665a\u5f20\u5b97\u5468\u65ad\u65ad\u7eed\u7eed\u505a\u4e86\u597d\u51e0\u4e2a\u68a6\uff0c\u68a6\u4e2d\u6e38\u904d\u6c5f\u5357\u2026\u2026\u5929\u4eae\u9192\u6765\uff0c\u4e61\u601d\u66f4\u6d53\uff0c\u60b2\u601d\u66f4 \u751a\u3002\u5ffd\u542c\u5f97\u5bb6\u4eba\u6572\u95e8\u62a5\u9053\uff1a\u201c\u6fb9\u53f0\u5c06\u519b\u548c\u5c11\u7237\u5411\u5927\u4eba\u8bf7\u5b89\u3002\u201d\u5f20\u5b97\u5468\u7acb\u5373\u62ab\u8863\u800c\u8d77\u8d70\u8fdb\u4e66 \u623f\uff0c\u89c1\u6fb9\u53f0\u706d\u660e\u5df2\u5728\u90a3\u91cc\u76f8\u5019\uff0c\u5f20\u4e39\u67ab\u7acb\u5728\u4e00\u8fb9\u3002\u5f20\u5b97\u5468\u9053\uff1a\u201c\u6fb9\u53f0\u5c06\u519b\uff0c\u4f60\u56de\u6765\u4e86\uff1f\u4e39\u67ab \u771f\u4e0d\u61c2\u4e8b\uff0c\u5c31\u662f\u4ed6\u6025\u7740\u8981\u56de\u6765\u89c1\u6211\uff0c\u4e5f\u4e0d\u8fdf\u5728\u8fd9\u4e00\u65e5\u534a\u65e5\uff0c\u4ed6\u6043\u7740\u9a6c\u5feb\uff0c\u628a\u4f60\u6487\u5728\u540e\u9762\uff0c\u5b9e \u662f\u4e0d\u5e94\u8be5\u3002\u201d\u5f20\u4e39\u67ab\u5fc3\u5185\u4e00\u9178\uff0c\u5fc3\u9053\uff1a\u201c\u7239\u5440\uff0c\u4f60\u54ea\u77e5\u9053\u6211\u5306\u5306\u56de\u6765\u5c31\u662f\u4e3a\u4e86\u8981\u518d\u5306\u5306\u79bb \u53bb\u3002\u201d \u3000\u3000\u6fb9\u53f0\u706d\u660e\u9053\uff1a\u201c\u542f\u7980\u4e3b\u516c\uff0c\u516c\u5b50\u60f3\u4e0e\u6211\u8d76\u5230\u5357\u8fb9\uff0c\u9a6c\u4e0a\u5c31\u8d70\uff0c\u7279\u6765\u5411\u4e3b\u516c\u544a\u8f9e\u3002\u201d\u5f20\u5b97 \u5468\u5403\u4e86\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u624d\u56de\u6765\u4e86\u53c8\u8981\u8d70\uff1f\u201d\u6fb9\u53f0\u706d\u660e\u9053\uff1a\u201c\u542c\u8bf4\u660e\u671d\u7684\u4f7f\u81e3\u5df2\u8fdb\u5165\u74e6 \u523a\uff0c\u6211\u4eec\u610f\u6b32\u524d\u53bb\u63a5\u4ed6\u3002\u201d\u5f20\u5b97\u5468\u9053\uff1a\u201c\u4f60\u8ba4\u5f97\u660e\u671d\u7684\u4f7f\u81e3\u5417\uff1f\u201d\u6fb9\u53f0\u706d\u660e\u65e9\u5f97\u4e86\u5f20\u4e39\u67ab\u7684 \u5631\u5490\uff0c\u6447\u4e86\u6447\u5934\u9053\uff1a\u201c\u867d\u4e0d\u8ba4\u5f97\uff0c\u4f46\u4e0a\u6b21\u516c\u5b50\u56de\u56fd\uff0c\u6211\u968f\u963f\u523a\u51fa\u4f7f\uff0c\u90fd\u66fe\u5f97\u5230\u660e\u671d\u4e8e\u9601\u8001\u4e8e \u8c26\u7684\u62db\u5f85\uff0c\u542c\u8bf4\u8fd9\u4f4d\u4f7f\u81e3\u662f\u4e8e\u8c26\u4eb2\u81ea\u6311\u9009\u7684\u4eba\uff0c\u793c\u5c1a\u5f80\u6765\uff0c\u6211\u4eec\u4f3c\u8be5\u524d\u53bb\u63a5\u4ed6\uff0c\u4ee5\u514d\u4ed6\u5728\u9014 \u4e2d\u53d1\u751f\u5371\u9669\u3002\u201d\u8bf4\u8bdd\u4e4b\u65f6\uff0c\u53ea\u89c1\u5f20\u4e39\u67ab\u773c\u4e2d\u9690\u6709\u6cea\u73e0\uff0c\u6fb9\u53f0\u706d\u660e\u77e5\u9053\u5c0f\u4e3b\u4eba\u7684\u5fc3\u4e8b\uff0c\u4e5f\u6b63\u662f \u4e3a\u4e86\u5c0f\u4e3b\u4eba\uff0c\u8fd9\u624d\u7b2c\u4e00\u6b21\u5411\u4e3b\u516c\u8bf4\u8c0e\u3002\u6fb9\u53f0\u706d\u660e\u770b\u4e86\u5f20\u4e39\u67ab\u4e00\u773c\uff0c\u5fc3\u4e2d\u4ea6\u611f\u8f9b\u9178\u96be\u8fc7\u3002 \u3000\u3000\u5f20\u5b97\u5468\u7f13\u7f13\u7ad9\u8d77\uff0c\u624b\u634b\u6591\u767d\u7684\u80e1\u987b\uff0c\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u6211\u5df2\u8001\u4e86\uff0c\u4e0d\u80fd\u518d\u4e3a\u4e2d\u56fd\u5c3d\u529b\uff0c\u4f60 \u4eec\u5e74\u8f7b\uff0c\u81ea\u6709\u62b1\u8d1f\uff0c\u597d\u5427\uff0c\u4f60\u4eec\u8d70\u5427\uff01\u201d\u5f20\u4e39\u67ab\u6cea\u73e0\u6eda\u4e0b\uff0c\u5e73\u65f6\u867d\u89c9\u7236\u4eb2\u4e0e\u81ea\u5df1\u6709\u6240\u8ddd\u79bb\uff0c \u4f46\u8fd9\u4e00\u970e\u90a3\uff0c\u4e24\u7236\u5b50\u5374\u662f\u5fc3\u610f\u76f8\u901a\u3002\u5f20\u4e39\u67ab\u62b1\u4e86\u7236\u4eb2\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u7239\u7239\uff0c\u4f60\u81ea\u5df1\u73cd\u91cd\uff01\u201d\u8f6c \u8eab\u4fbf\u8d70\u51fa\u4e66\u623f\u3002 \u3000\u3000\u80cc\u540e\u9690\u7ea6\u542c\u7236\u4eb2\u541f\u9053\uff1a\u201c\u6625\u82b1\u79cb\u6708\u4f55\u65f6\u4e86\uff0c\u5f80\u4e8b\u77e5\u591a\u5c11\uff1f\u5c0f\u697c\u6628\u591c\u53c8\u4e1c\u98ce\uff0c\u6545\u56fd\u4e0d\u582a\u56de \u9996\u6708\u660e\u4e2d\u3002\u201d\u5f20\u4e39\u67ab\u4e0d\u6562\u56de\u5934\uff0c\u4e0e\u6fb9\u53f0\u706d\u660e\u6025\u6025\u8d70\u51fa\u5927\u95e8\uff0c\u8de8\u4e0a\u9a6c\u80cc\u4fbf\u8d70\u3002 \u3000\u3000\u4ed6\u4eec\u5fc3\u6025\u5982\u711a\uff0c\u8981\u8d76\u5f80\u5357\u8fb9\u8fce\u63a5\u660e\u671d\u7684\u4f7f\u8005\u3002\u660e\u671d\u7684\u4f7f\u8005\u4e91\u91cd\u4e5f\u662f\u5fc3\u6025\u5982\u711a\uff0c\u8981\u8d76\u5230\u74e6 \u523a\u4eac\u57ce\u4f1a\u89c1\u4ed6\u4eec\u3002 \u3000\u3000\u4e91\u91cd\u4ed6\u4eec\u662f\u65b0\u5e74\u7684\u7b2c\u4e8c\u5929\u79bb\u5f00\u5317\u4eac\uff0c\u8fd9\u65f6\u8d70\u4e86\u4e00\u4e2a\u591a\u6708\uff0c\u5df2\u6df1\u5165\u74e6\u523a\u56fd\u5883\u3002\u51ac\u53bb\u6625\u6765\uff0c \u79ef\u96ea\u521d\u878d\uff0c\u5c71\u91ce\u95f4\u5df2\u6709\u4e86\u4e00\u70b9\u7eff\u610f\uff0c\u8fd9\u65e5\u4ed6\u4eec\u8d70\u8fc7\u5c71\u5cad\u7ef5\u4e98\u7684\u8352\u539f\uff0c\u6570\u5341\u91cc\u4e0d\u89c1\u4eba\u5bb6\uff0c\u5c71\u5934 \u4e0a\u53ea\u5076\u7136\u89c1\u6709\u51e0\u53ea\u5140\u9e70\u4f4e\u98de\u89c5\u98df\uff0c\u5c71\u5761\u4e00\u7247\u9ec4\u571f\uff0c\u53ea\u5076\u800c\u89c1\u6709\u51e0\u679d\u7a00\u7a00\u758f\u758f\u7684\u6986\u6811\uff0c\u62bd\u51fa\u65b0 \u82bd\u3002\u6fb9\u53f0\u955c\u660e\u53f9\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u8499\u53e4\u5730\u65b9\u8352\u51c9\u5982\u6b64\uff0c\u4e0d\u8bf4\u6c5f\u5357\uff0c\u5373\u5728\u5317\u4eac\uff0c\u6843\u82b1\u4e5f\u5df2\u5f00\u4e86\u3002\u201d \u6709\u4e00\u4e2a\u5230\u8fc7\u8499\u53e4\u7684\u968f\u4ece\u7b11\u9053\uff1a\u201c\u8fd9\u5730\u65b9\u8fd8\u672a\u7b97\u8352\u51c9\uff0c\u5230\u4e86\u5317\u8fb9\uff0c\u96ea\u5730\u51b0\u5929\uff0c\u90a3\u624d\u8352\u51c9\u5462\u3002\u82cf \u6b66\u7267\u7f8a\u7684\u5317\u6d77\u8fb9\uff0c\u522b\u8bf4\u4eba\u70df\uff0c\u8fde\u9e1f\u513f\u4e5f\u89c1\u4e0d\u7740\uff0c\u6e34\u4e86\u53ea\u559d\u96ea\u6c34\uff0c\u997f\u4e86\u5c31\u53ea\u6709\u4e00\u5473\u70e4\u7f8a\u8089 \u5403\u3002\u201d\u4e91\u91cd\u542c\u4ed6\u63d0\u8d77\u201c\u82cf\u6b66\u7267\u7f8a\u201d\uff0c\u4e0d\u7981\u60f3\u8d77\u7237\u7237\uff0c\u5fc3\u4e2d\u60b2\u6124\u9eef\u7136\u4e0d\u8bed\u3002\u6fb9\u53f0\u955c\u660e\u6e29\u67d4\u5730\u671b \u4e86\u4ed6\u4e00\u773c\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u91cc\u8fd8\u6709\u4e00\u4e9b\u91ce\u8349\u548c\u5c71\u6d1e\uff0c\u9a6c\u513f\u53ef\u4ee5\u6b47\u606f\uff0c\u6211\u770b\u54b1\u4eec\u4eca\u591c\u53ea\u80fd\u5728\u6b64\u5730\u624e \u8425\u4e86\u3002\u201d\u4e91\u91cd\u5fd9\u9053\uff1a\u201c\u5bf9\u5566\uff0c\u53cd\u6b63\u4eca\u65e5\u4e0d\u80fd\u8d70\u8fc7\u8fd9\u4e2a\u8352\u539f\uff0c\u660e\u65e5\u518d\u8d70\u5427\u3002\u4f60\u521d\u5230\u8499\u53e4\uff0c\u5b9a\u662f \u5f88\u4e0d\u4e60\u60ef\u4e86\u3002\u65e9\u70b9\u4f11\u606f\u3002\u201d\u6fb9\u53f0\u955c\u660e\u9053\uff1a\u201c\u4e5f\u6ca1\u4ec0\u4e48\uff0c\u5c31\u662f\u624b\u811a\u957f\u4e86\u51bb\u75ae\uff0c\u6709\u70b9\u9ebb\u70e6\uff0c\u6162\u6162 \u4e5f\u4e60\u60ef\u4e86\u3002\u201d\u5176\u5b9e\u5979\u5bf9\u8499\u53e4\u7684\u6c14\u5019\u8fd8\u672a\u4e60\u60ef\uff0c\u5bf9\u4e91\u91cd\u7684\u813e\u6c14\u5374\u5df2\u6162\u6162\u4e60\u60ef\u4e86\u3002\u4e91\u91cd\u662f\u4e2a\u786c\u76f4 \u7684\u6c49\u5b50\uff0c\u867d\u7136\u6ca1\u6709\u5f20\u4e39\u67ab\u90a3\u4e00\u4efd\u98ce\u6d41\u6f47\u6d12\uff0c\u4f46\u5bf9\u5979\u5374\u662f\u4f53\u8d34\u5165\u5fae\uff0c\u5173\u5fc3\u4e4b\u5904\uff0c\u6beb\u4e0d\u63a9\u9970\u5730\u8868 \u9732\u51fa\u6765\u3002 \u3000\u3000\u4e91\u91cd\u9009\u4e86\u4e00\u5904\u80cc\u98ce\u7684\u5c71\u5773\u5730\u65b9\u5b89\u4e0b\u5e10\u5e55\uff0c\u4e0e\u968f\u4ece\u62fe\u4e86\u4e00\u4e9b\u67af\u679d\u751f\u8d77\u706b\u6765\uff0c\u5403\u8fc7\u665a\u9910\u4e4b \u540e\uff0c\u4e91\u91cd\u8d70\u8fdb\u6fb9\u53f0\u955c\u660e\u7684\u5e10\u5e55\u966a\u5979\u8c08\u8bdd\u89e3\u95f7\u3002\u6fb9\u53f0\u955c\u660e\u5ffd\u9053\uff1a\u201c\u5f20\u4e39\u67ab\u4e0e\u4f60\u7684\u59b9\u59b9\u82e5\u7136\u662f\u77e5 \u9053\u4e86\u54b1\u4eec\u5230\u6765\uff0c\u4e0d\u77e5\u591a\u6b22\u559c\u5462\uff01\u5c71\u6c11\u54e5\u54e5\u524d\u53bb\u62a5\u4fe1\uff0c\u60f3\u6765\u5df2\u89c1\u7740\u4ed6\u4eec\u4e86\u3002\u54b1\u4eec\u5230\u4e86\u74e6\u523a\uff0c\u603b \u6709\u51e0\u5929\u803d\u6401\uff0c\u624d\u9012\u56fd\u4e66\uff0c\u4f60\u770b\u8981\u4e0d\u8981\u5148\u5230\u5f20\u5bb6\u53bb\u627e\u4ed6\u4eec\uff1f\u201d\u4e91\u91cd\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u5230 \u5f20\u5bb6\u627e\u8c01\uff1f\u5f20\u4e39\u67ab\u6216\u8005\u4f1a\u5728\u5bb6\u4e2d\u7b49\u4f60\uff0c\u4e91\u857e\u82e5\u4f4f\u5728\u5f20\u5bb6\uff0c\u90a3\u5c31\u4e0d\u662f\u6211\u7684\u59b9\u59b9\u3002\u201d\u6fb9\u53f0\u955c\u660e\u5657 \u55e4\u4e00\u7b11\uff0c\u5c0f\u6307\u5934\u6233\u4e86\u4ed6\u4e00\u4e0b\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u4e2a\u725b\u813e\u6c14\u51e0\u65f6\u624d\u6539\uff1f\u6709\u4ec0\u4e48\u4e0d\u5171\u6234\u5929\u4e4b\u4ec7\u503c\u5f97\u5982\u6b64 \u803f\u803f\u4e8e\u5fc3\u5462\uff1f\u8fd9\u6b21\u82e5\u4e0d\u662f\u4e8f\u4e86\u5f20\u4e39\u67ab\uff0c\u4e8e\u9601\u8001\u4e5f\u4e0d\u4f1a\u77e5\u9053\u74e6\u523a\u7684\u5185\u60c5\uff0c\u4e24\u56fd\u4e4b\u95f4\uff0c\u4e5f\u4e0d\u4f1a\u8fd9 \u6837\u5feb\u4fbf\u540c\u610f\u8c08\u548c\uff0c\u5168\u4e8f\u4e86\u4ed6\uff0c\u624d\u6709\u4f60\u8fd9\u4e2a\u8bae\u548c\u7684\u4f7f\u8005\u5462\uff01\u201d\u4e91\u91cd\u7ed9\u5979\u8bf4\u5f97\u4f4e\u4e0b\u4e86\u5934\uff0c\u60f3\u8d77\u5f20 \u4e39\u67ab\u679c\u7136\u662f\u4e00\u7247\u4e39\u5fc3\uff0c\u4e3a\u4e86\u4e2d\u56fd\uff0c\u9ed8\u7136\u4e0d\u8bed\u3002\u4f46\u5fc3\u4e2d\u4ecd\u662f\u4e0d\u5e0c\u671b\u59b9\u59b9\u4f4f\u5728\u5f20\u5bb6\u3002\u6fb9\u53f0\u955c\u660e\u53c8 \u9053\uff1a\u201c\u8fd9\u6b21\u5230\u4e86\u74e6\u523a\uff0c\u4f60\u5b9e\u5728\u5e94\u8be5\u5148\u89c1\u89c1\u4e39\u67ab\uff0c\u8c22\u4e00\u8c22\u4ed6\u3002\u201d\u4e91\u91cd\u9053\uff1a\u201c\u4e8e\u9601\u8001\u6709\u4e66\u4fe1\u4e0e \u4ed6\uff0c\u6211\u5f53\u7136\u4e0e\u4ed6\u76f8\u89c1\u3002\u53ea\u662f\u6211\u4e24\u5bb6\u4ec7\u6df1\u5982\u6d77\uff0c\u770b\u5728\u4ed6\u8fd9\u6b21\u4e3a\u4e86\u5927\u660e\u6c5f\u5c71\u5954\u6ce2\u51fa\u529b\u7684\u4efd\u4e0a\uff0c\u6211 \u53ef\u4ee5\u4e0d\u8ba1\u524d\u4ec7\uff0c\u4f46\u8981\u6211\u4e0e\u4ed6\u5316\u654c\u4e3a\u53cb\uff0c\u90a3\u53ef\u529e\u4e0d\u5230\uff01\u201d \u3000\u3000\u6fb9\u53f0\u955c\u660e\u5fae\u5fae\u4e00\u7b11\uff0c\u7ad6\u8d77\u5c0f\u6307\u5934\u53c8\u5728\u4ed6\u7684\u989d\u89d2\u6233\u4e86\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u4e8f\u4f60\u662f\u5927\u4e08\u592b\uff0c\u6c14\u91cf\u5982 \u6b64\u72ed\u5c0f\uff0c\u8fd8\u4e0d\u53ca\u6211\u7b49\u5973\u6d41\u4e4b\u8f88\uff0c\u6211\u4eec\u4e0e\u4f60\u7684\u6731\u5bb6\u5929\u5b50\u4e5f\u662f\u4e16\u4ec7\uff0c\u6211\u4eec\u5b88\u4e86\u51e0\u4ee3\u7684\u73cd\u5b9d\uff0c\u7ed3\u679c \u8fd8\u4e0d\u662f\u90fd\u62ff\u4e86\u51fa\u6765\u732e\u7ed9\u671d\u5ef7\u3002\u5f20\u4e39\u67ab\u82e5\u662f\u8bb0\u4ec7\uff0c\u4ed6\u4e5f\u4e0d\u4f1a\u8bbe\u8c0b\u5212\u7b56\uff0c\u8981\u4e8e\u9601\u8001\u53bb\u63a5\u7687\u5e1d\u8001\u513f \u56de\u6765\u4e86\u3002\u201d\u6fb9\u53f0\u955c\u660e\u5fc3\u76f4\u53e3\u5feb\uff0c\u4f83\u4f83\u800c\u8c08\u3002\u4e91\u91cd\u5fc3\u5934\u4e00\u9707\uff0c\u601d\u6f6e\u52a8\u8361\uff0c\u5fc3\u9053\uff1a\u201c\u96be\u9053\u6211\u5c31\u4e0d \u5982\u5f20\u4e39\u67ab\uff1f\u201d\u8fd9\u970e\u90a3\u95f4\uff0c\u7f8a\u76ae\u8840\u4e66\u7684\u9634\u5f71\u53c8\u906e\u4e0a\u6765\uff0c\u4e91\u91cd\u5fc3\u7eea\u7d0a\u4e71\u82e6\u607c\u975e\u5e38\uff0c\u4f4e\u4e0b\u5934\u53ea\u987e\u628a \u70e4\u719f\u7684\u7f8a\u817f\u6495\u5f00\u6765\u5403\u3002 \u3000\u3000\u6fb9\u53f0\u955c\u660e\u6b63\u6b32\u518d\u8bf4\uff0c\u5ffd\u89c1\u4e91\u91cd\u4f0f\u5728\u5730\u4e0b\uff0c\u9762\u8272\u5927\u5f02\uff0c\u6fb9\u53f0\u955c\u660e\u5947\u9053\uff1a\u201c\u4f60\u505a\u4ec0\u4e48\uff1f\u201d\u4e91 \u91cd\u4e00\u8dc3\u800c\u8d77\uff0c\u9053\uff1a\u201c\u6709\u5927\u961f\u7684\u519b\u9a6c\u5411\u8fd9\u8fb9\u6765\uff01\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u53ea\u542c\u5f97\u545c\u545c\u7684\u53f7\u89d2\u4e4b\u58f0\uff0c\u63a5\u7740\u662f \u5c16\u9510\u7684\u7fbd\u7bad\u7834\u7a7a\u4e4b\u58f0\uff0c\u63a0\u8fc7\u5e10\u7bf7\u3002\u4f8d\u536b\u8fdb\u6765\u62a5\u9053\uff1a\u201c\u524d\u54e8\u53d1\u73b0\u6709\u4e00\u961f\u4eba\u9a6c\uff0c\u5411\u54b1\u4eec\u8fd9\u91cc\u6563 \u5f00\uff0c\u56db\u9762\u5305\u56f4\uff0c\u9ed1\u591c\u4e4b\u4e2d\uff0c\u4e0d\u77e5\u4eba\u6570\u591a\u5c11\uff0c\u4e5f\u4e0d\u89c1\u65d7\u5e1c\u756a\u53f7\u3002\u8bf7\u4e91\u5927\u4eba\u4e0b\u4ee4\u5982\u4f55\u5bf9\u4ed8\uff01\u201d\u4e91 \u91cd\u9053\uff1a\u201c\u8352\u5c71\u91ce\u8c37\uff0c\u6765\u7684\u5b9a\u7136\u662f\u52ab\u8425\u7684\u5f3a\u76d7\uff0c\u4f60\u4eec\u5341\u516b\u4eba\u79bb\u5f00\u5e10\u5e55\uff0c\u4e24\u4e2a\u4e00\u7ec4\uff0c\u5404\u81ea\u63a9\u853d\uff0c \u4e00\u89c1\u4eba\u5f71\uff0c\u7acb\u523b\u7528\u5f13\u7bad\u5c04\u4ed6\u3002\u201d\u4f8d\u536b\u5e94\u547d\u51fa\u53bb\u3002\u6fb9\u53f0\u955c\u660e\u9053\uff1a\u201c\u4f60\u5462\uff1f\u201d\u4e91\u91cd\u9053\uff1a\u201c\u4f60\u4eec\u90fd \u5230\u6211\u7684\u5e10\u5e55\u4e2d\u3002\u201d\u6fb9\u53f0\u955c\u660e\u9053\uff1a\u201c\u4f60\u4e0d\u51fa\u53bb\u5417\uff1f\u201d\u4e91\u91cd\u9053\uff1a\u201c\u6211\u624b\u6301\u4f7f\u8282\uff0c\u8eab\u6000\u56fd\u4e66\uff0c\u5e10\u5e55 \u4e2d\u6709\u81f4\u9001\u74e6\u523a\u56fd\u541b\u7684\u793c\u7269\uff0c\u5982\u4f55\u80fd\u64c5\u79bb\u6b64\u5730\u3002\u4f60\u6240\u5e26\u7684\u51e0\u4f4d\u5973\u5175\uff0c\u5728\u9ed1\u591c\u4e4b\u4e2d\u4e5f\u4e0d\u4fbf\u5916\u51fa\u5fa1 \u654c\uff0c\u4e0d\u5982\u4e0e\u6211\u4e00\u540c\u9547\u5b88\u5e10\u4e2d\uff0c\u8c05\u8fd9\u4e9b\u5c71\u91ce\u8349\u8d3c\uff0c\u4e5f\u6ca1\u6709\u4ec0\u4e48\u80fd\u8010\u3002\u201d\u6fb9\u53f0\u955c\u660e\u542c\u4e86\uff0c\u5fc3\u4e2d\u6697 \u6697\u611f\u6fc0\uff0c\u4e91\u91cd\u8bf4\u7684\u8981\u4fdd\u62a4\u5e10\u4e2d\u7684\u671d\u5ef7\u793c\u7269\u56fa\u662f\u5b9e\u60c5\uff0c\u4f46\u8fd8\u6709\u4e00\u4e2a\u539f\u56e0\u4ed6\u672a\u660e\u8a00\uff0c\u800c\u6fb9\u53f0\u955c\u660e \u81ea\u5df1\u77e5\u9053\u7684\u5374\u6b63\u662f\u4e3a\u4e86\u5979\u4eec\u3002\u4e00\u8005\u6015\u6fb9\u53f0\u955c\u660e\u7684\u5973\u5175\u5728\u5916\u9762\u8d70\u6563\uff0c\u88ab\u8d3c\u4eba\u63a0\u53bb\u73b7\u8fb1\uff1b\u4e8c\u8005\u662f \u6fb9\u53f0\u955c\u660e\u8fd9\u51e0\u65e5\u51bb\u75ae\u53d1\u4f5c\u5f97\u5f88\u5389\u5bb3\uff0c\u624b\u811a\u5173\u8282\u4e5f\u9690\u9690\u4f5c\u75db\uff0c\u884c\u52a8\u4e0d\u5f88\u5229\u843d\uff0c\u6545\u6b64\u4e91\u91cd\u8981\u5979\u7559 \u5728\u5e10\u4e2d\uff0c\u7978\u798f\u4e0e\u5171\u3002 \u3000\u3000\u5e03\u7f6e\u65b9\u7ae3\uff0c\u8d3c\u4eba\u5df2\u5927\u4e3e\u88ad\u6765\uff0c\u53ea\u542c\u5f97\u5916\u9762\u6d41\u77e2\u55e4\u55e4\u4e4b\u58f0\uff0c\u4e0d\u7edd\u4e8e\u8033\uff0c\u63a5\u7740\u662f\u4e00\u7247\u51b2\u6740\u58f0 \u97f3\uff0c\u56db\u5904\u54cd\u8d77\u4e86\u91d1\u94c1\u4ea4\u9e23\u4e4b\u58f0\uff0c\u63a5\u7740\u662f\u547c\u53f7\u5954\u8dd1\u4e4b\u58f0\u3002\u4e91\u91cd\u7b11\u9053\uff1a\u201c\u8fd9\u4e9b\u8d3c\u4eba\u5c1d\u5230\u5389\u5bb3 \u4e86\u3002\u201d\u4e91\u91cd\u4f0f\u5730\u542c\u58f0\u7684\u672c\u9886\u751a\u662f\u9ad8\u660e\uff0c\u542c\u5916\u9762\u7684\u58f0\u97f3\uff0c\u5df2\u77e5\u662f\u8d3c\u4eba\u53d7\u4e86\u632b\u6298\u3002 \u3000\u3000\u4e91\u91cd\u6b63\u5728\u4e0e\u5973\u5175\u8bf4\u7b11\uff0c\u5ffd\u542c\u5f97\u201c\u55e4\u201d\u7684\u4e00\u58f0\uff0c\u4e00\u7bf7\u84dd\u706b\uff0c\u5728\u5e10\u5e55\u5916\u9762\u70e7\u71c3\u8d77\u6765\u3002\u4e91\u91cd\u53eb \u9053\uff1a\u201c\u4e0d\u597d\uff01\u201d\u6025\u5fd9\u51fa\u53bb\u6251\u706b\uff0c\u5e10\u5e55\u4e00\u63ed\uff0c\u5916\u9762\u9aa4\u7684\u4e00\u80a1\u52b2\u98ce\u522e\u8fdb\uff0c\u56db\u4e94\u4e2a\u8499\u9762\u4eba\u4e00\u540c\u95ef\u4e86 \u8fdb\u6765\u3002\u8fd9\u51e0\u4e2a\u4eba\u501f\u86c7\u7130\u7bad\u7684\u54cd\u58f0\u4f5c\u4e3a\u63a9\u62a4\uff0c\u5c45\u7136\u6559\u4e91\u91cd\u4e0d\u80fd\u4e8b\u524d\u53d1\u89c9\uff0c\u8f7b\u8eab\u7684\u529f\u592b\uff0c\u786e\u662f\u4e0d \u540c\u51e1\u4fd7\u3002 \u3000\u3000\u8fd9\u51e0\u4e2a\u8499\u9762\u4eba\u8eab\u624b\u77eb\u6377\uff0c\u4e00\u51b2\u8fdb\u6765\u7acb\u523b\u5411\u4e91\u91cd\u65bd\u5c55\u6740\u624b\uff0c\u4e91\u91cd\u5927\u559d\u4e00\u58f0\uff0c\u53cd\u624b\u4e00\u638c\uff0c\u5c06 \u4e00\u4e2a\u8499\u9762\u4eba\u6253\u5f97\u98de\u51fa\u5e10\u5916\u3002 \u3000\u3000\u4e91\u91cd\u7684\u5927\u529b\u91d1\u521a\u638c\u5de6\u53f3\u5f00\u5f13\uff0c\u5de6\u638c\u4e00\u53d1\uff0c\u53f3\u638c\u7ee7\u51fa\uff0c\u5ffd\u7136\u4e00\u638c\u6253\u7a7a\uff0c\u6b63\u9762\u7684\u90a3\u4e2a\u8499\u9762\u4eba \u5341\u6307\u4e00\u5c48\uff0c\u6402\u5934\u4fbf\u6293\uff0c\u7adf\u662f\u5927\u529b\u9e70\u722a\u7684\u529f\u592b\u3002\u4e91\u91cd\u541e\u80f8\u5438\u8179\uff0c\u5de6\u638c\u4e00\u6536\u5f80\u91cc\u4e00\u5207\uff0c\u90a3\u4eba \u201c\u566b\u201d\u4e86\u4e00\u58f0\uff0c\u6c89\u638c\u4e00\u622a\uff0c\u5728\u5e10\u5e55\u7684\u725b\u6cb9\u70db\u5149\u4e4b\u4e0b\uff0c\u53ea\u89c1\u8fd9\u4eba\u7684\u624b\u638c\u5e7b\u6210\u6697\u7d2b\u7684\u989c\u8272\uff0c\u4e91\u91cd \u5403\u4e86\u4e00\u60ca\uff0c\u4e00\u4e2a\u98de\u8eab\u65cb\u6b65\uff0c\u817e\u7684\u4e00\u811a\u5c06\u4fa7\u9762\u4e00\u4e2a\u8499\u9762\u4eba\u8e22\u4e86\u4e00\u4e2a\u7b4b\u6597\uff0c\u907f\u5f00\u4e86\u90a3\u4e00\u6293\u4e4b\u52bf\uff0c \u8fd9\u65f6\u6fb9\u53f0\u955c\u660e\u4e5f\u5df2\u62d4\u51fa\u4f69\u5251\uff0c\u4e0e\u53e6\u5916\u90a3\u51e0\u4e2a\u8499\u9762\u4eba\u6df7\u6218\u3002 \u3000\u3000\u4e91\u91cd\u53eb\u9053\uff1a\u201c\u63d0\u9632\u4ed6\u4eec\u7684\u722a\u5b50\uff0c\u72d7\u722a\u5b50\u6709\u6bd2\uff01\u201d\u6b63\u9762\u7684\u90a3\u4e2a\u8499\u9762\u4eba\u4f3c\u4e4e\u662f\u4e2a\u8001\u8005\uff0c\u563f\u563f \u51b7\u7b11\uff0c\u4e0e\u53e6\u4e00\u4e2a\u4f7f\u952f\u9f7f\u5200\u7684\u5bb6\u4f19\u5939\u653b\u4e91\u91cd\u3002\u4e91\u91cd\u8fb9\u6253\u8fb9\u77a7\uff0c\u53ea\u89c1\u6fb9\u53f0\u955c\u660e\u4e0e\u90a3\u4e24\u4e2a\u8499\u9762\u4eba\u4e5f \u6597\u5f97\u6b63\u70c8\uff0c\u5176\u4e2d\u4e00\u4e2a\u8eab\u6750\u597d\u719f\u4f3c\u4e4e\u5728\u54ea\u513f\u89c1\u8fc7\u4e00\u822c\uff0c\u751a\u4e3a\u4e86\u5f97\uff0c\u6240\u7528\u7684\u4e5f\u662f\u8d64\u7802\u638c\u5939\u4ee5\u9e70\u722a \u529f\uff0c\u4f46\u638c\u6cd5\u602a\u5f02\uff0c\u4f3c\u4e4e\u6bd4\u9762\u524d\u8fd9\u4e2a\u8001\u8005\u8fd8\u80dc\u51e0\u5206\u3002\u6fb9\u53f0\u955c\u660e\u4f7f\u5f00\u5bb6\u4f20\u7684\u5357\u5cb3\u5251\u6cd5\uff0c\u8f7b\u7075\u6c89 \u7a33\uff0c\u517c\u800c\u6709\u4e4b\uff0c\u4e5f\u5c3d\u62b5\u6321\u5f97\u4f4f\uff0c\u53ea\u662f\u5979\u884c\u52a8\u4e0d\u5927\u65b9\u4fbf\uff0c\u8df3\u8dc3\u4e4b\u9645\uff0c\u5fae\u663e\u5446\u6ede\u3002\u90a3\u4e24\u4e2a\u8499\u9762\u4eba \u8fc5\u5373\u770b\u51fa\u4e86\u5979\u5f31\u70b9\u6240\u5728\uff0c\u53cc\u638c\u4e00\u5200\uff0c\u4e13\u653b\u4e0b\u76d8\uff0c\u6218\u5230\u5206\u9645\u90a3\u4e2a\u8499\u9762\u4eba\u7a81\u7136\u4f7f\u4e86\u4e00\u8bb0\u602a\u62db\uff0c\u638c \u7cfb\u9762\u95e8\uff0c\u6fb9\u53f0\u955c\u660e\u6a2a\u5251\u4e00\u5c01\uff0c\u4ed6\u7a81\u7136\u5411\u5730\u4e0b\u4e00\u5012\uff0c\u53cc\u638c\u4e00\u4f38\uff0c\u5c31\u62ff\u6fb9\u53f0\u955c\u660e\u7684\u7ea4\u8db3\u3002\u6fb9\u53f0\u955c \u660e\u98de\u811a\u4fbf\u8e22\uff0c\u88ab\u4ed6\u6293\u7740\u5de6\u8db3\u8db3\u8ddf\uff0c\u731b\u5730\u4e00\u9001\uff0c\u6fb9\u53f0\u955c\u660e\u51cc\u7a7a\u98de\u8d77\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u4ed6\u7684\u540c \u4f34\u624b\u821e\u5355\u5200\u6478\u51fa\u98de\u7d22\uff0c\u5411\u524d\u4e00\u6296\uff0c\u7acb\u523b\u4e0a\u524d\uff0c\u610f\u6b32\u751f\u64d2\u3002 \u3000\u3000\u4e91\u91cd\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u594b\u8d77\u795e\u529b\uff0c\u5927\u559d\u4e00\u58f0\uff0c\u547c\u7684\u4e00\u638c\u626b\u53bb\uff0c\u4e0d\u60dc\u4e0e\u90a3\u8499\u9762\u8001\u8005\u7684\u6bd2\u638c \u786c\u78b0\u3002\u8fd9\u4e00\u638c\u6709\u5f00\u5c71\u5288\u77f3\u4e4b\u52bf\uff0c\u82e5\u7136\u786c\u78b0\uff0c\u4e91\u91cd\u6700\u591a\u4e2d\u6bd2\uff0c\u90a3\u8001\u8005\u7684\u624b\u81c2\u975e\u6298\u65ad\u4e0d\u53ef\uff0c\u90a3\u8001 \u8005\u4e0d\u6562\u786c\u63a5\uff0c\u9000\u540e\u4e00\u95ea\uff0c\u53e6\u4e00\u4e2a\u8499\u9762\u4eba\u7684\u952f\u9f7f\u5200\u521a\u5230\uff0c\u88ab\u4e91\u91cd\u5de6\u624b\u6293\u7740\u5200\u67c4\uff0c\u786c\u62d6\u8fc7\u6765\uff0c\u53f3 \u638c\u4e00\u5288\uff0c\u7acb\u523b\u5c06\u4ed6\u5288\u5f97\u5934\u9885\u7834\u88c2\u3002 \u3000\u3000\u4e24\u8fb9\u52a8\u4f5c\u90fd\u662f\u5feb\u5982\u95ea\u7535\uff0c\u4e91\u91cd\u6446\u8131\u4e86\u90a3\u4e24\u4e2a\u8499\u9762\u4eba\uff0c\u6b63\u6b32\u5954\u524d\uff0c\u5ffd\u542c\u5f97\u60e8\u53eb\u4e00\u58f0\u3002\u539f\u6765 \u6fb9\u53f0\u955c\u660e\u867d\u56e0\u51bb\u75ae\u53d1\u4f5c\uff0c\u5173\u8282\u4f5c\u75db\uff0c\u8f7b\u529f\u53d7\u4e86\u5f71\u54cd\uff0c\u4f46\u6839\u5e95\u8fd8\u5728\uff0c\u5979\u88ab\u90a3\u4e2a\u8499\u9762\u4eba\u6293\u7740\u8db3\u6839 \u4e00\u9001\uff0c\u5c31\u501f\u8fd9\u4e00\u9001\u4e4b\u52bf\uff0c\u4e00\u89e6\u5e10\u9876\uff0c\u7acb\u523b\u5728\u534a\u7a7a\u4e2d\u4e00\u4e2a\u7ffb\u8eab\uff0c\u51cc\u7a7a\u4e0b\u523a\u3002\u8fd9\u4e00\u5251\u6709\u5982\u9e70\u96bc\u4fef \u5544\uff0c\u53c8\u72e0\u53c8\u51c6\uff0c\u4f7f\u5355\u5200\u7684\u8499\u9762\u4eba\u7adf\u88ab\u5979\u4e00\u5251\u523a\u7a7f\u4e86\u54bd\u5589\u3002\u98de\u7d22\u629b\u51fa\uff0c\u4e5f\u521a\u597d\u5f39\u5728\u5979\u7684\u8eab\u4e0a\u3002 \u3000\u3000\u65bd\u6697\u7b97\u7684\u90a3\u4e2a\u8499\u9762\u4eba\u521a\u521a\u7ad9\u8d77\uff0c\u4e91\u91cd\u7684\u638c\u52bf\u5df2\u5982\u6392\u5c71\u5012\u6d77\u822c\u5730\u653b\u6765\uff0c\u90a3\u8499\u9762\u4eba\u54ea\u91cc\u6562 \u63a5\uff0c\u5413\u5f97\u9762\u65e0\u4eba\u8272\uff0c\u8fde\u8fde\u540e\u9000\u3002\u90a3\u8499\u9762\u8001\u8005\u6025\u5fd9\u5728\u540e\u5939\u653b\uff0c\u638c\u631f\u8165\u98ce\uff0c\u786c\u6293\u4e91\u91cd\u7684\u80a9\u5934\uff0c\u4e91 \u91cd\u547c\u7684\u4e00\u638c\uff0c\u6b63\u8981\u5f97\u624b\uff0c\u5ffd\u89c9\u80a9\u5934\u5fae\u75db\uff0c\u8feb\u5f97\u7f29\u80a9\u6c89\u8098\uff0c\u638c\u950b\u4e00\u504f\u867d\u662f\u4ecd\u7136\u6253\u4e2d\u90a3\u4e2a\u8499\u9762 \u4eba\uff0c\u4f46\u638c\u529b\u5df2\u5378\u4e86\u4e00\u534a\u3002\u4f46\u9976\u662f\u5982\u6b64\uff0c\u90a3\u8499\u9762\u4eba\u4e5f\u51e0\u4e4e\u722c\u4e0d\u8d77\u6765\u3002 \u3000\u3000\u4e91\u91cd\u8dc3\u51fa\u4e24\u6b65\uff0c\u65e0\u6687\u8ffd\u51fb\u90a3\u4e2a\u88ab\u81ea\u5df1\u6253\u4f24\u7684\u8499\u9762\u4eba\uff0c\u5148\u6765\u5bdf\u770b\u6fb9\u53f0\u955c\u660e\u3002\u90a3\u8499\u9762\u8001\u8005 \u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u6293\u8d77\u90a3\u4e2a\u53d7\u4f24\u7684\u540c\u4f34\uff0c\u7acb\u523b\u51b2\u51fa\u5e10\u5e55\u3002 \u3000\u3000\u6fb9\u53f0\u955c\u660e\u5df2\u81ea\u884c\u89e3\u4e86\u7ef3\u7d22\uff0c\u7b11\u76c8\u76c8\u7ad9\u4e86\u8d77\u6765\u7b11\u8a00\u9053\uff1a\u201c\u597d\u9669\uff01\u201d\u4e91\u91cd\u9053\uff1a\u201c\u6ca1\u4ec0\u4e48 \u5417\uff1f\u201d\u6fb9\u53f0\u955c\u660e\u9053\uff1a\u201c\u6ca1\u4ec0\u4e48\u3002\u201d\u4e91\u91cd\u7709\u5934\u4e00\u76b1\uff0c\u9053\uff1a\u201c\u4f60\u628a\u9774\u5b50\u8131\u4e86\uff0c\u55ef\uff0c\u5c06\u889c\u5b50\u4e5f\u8131 \u4e86\uff0c\u8ba9\u6211\u770b\u770b\u4f60\u7684\u811a\u677f\u3002\u201d\u6fb9\u53f0\u955c\u660e\u9762\u4e0a\u4e00\u7ea2\uff0c\u9053\uff1a\u201c\u5e72\u4ec0\u4e48\uff1f\u201d\u4e91\u91cd\u9053\uff1a\u201c\u524d\u6b21\u6211\u5728\u592a\u6e56 \u5c71\u5e84\uff0c\u53d7\u4e86\u7ea2\u53d1\u5996\u9f99\u7684\u6bd2\u638c\u6240\u4f24\uff0c\u662f\u4f60\u670d\u4f8d\u6211\uff0c\u73b0\u5728\u8be5\u8f6e\u5230\u6211\u6765\u670d\u4f8d\u4f60\u4e86\u3002\u201d\u6fb9\u53f0\u955c\u660e\u9053\uff1a \u201c\u6211\u9694\u7740\u9774\u889c\uff0c\u88ab\u4ed6\u6293\u4e86\u4e00\u4e0b\uff0c\u5c31\u53d7\u4f24\u4e86\u4e48\uff1f\u201d\u610f\u9887\u4e0d\u4fe1\uff0c\u8131\u5f00\u9774\u889c\u4e00\u770b\u53ea\u89c1\u811a\u677f\u4e0a\u679c\u7136\u6709 \u91d1\u94b1\u822c\u5927\u5c0f\u7684\u7ea2\u5370\u3002\u4e91\u91cd\u9053\uff1a\u201c\u597d\u5389\u5bb3\u3002\u5e78\u597d\u6709\u9774\u889c\u9694\u7740\u3002\u201d\u62ff\u8d77\u6fb9\u53f0\u955c\u660e\u7684\u4f69\u5251\uff0c\u5728\u7ea2\u5370 \u5468\u56f4\u5212\u4e86\u4e00\u4e2a\u5706\u5708\uff0c\u5c06\u6bd2\u8840\u6324\u51fa\uff0c\u6577\u4e0a\u4e86\u884c\u519b\u6240\u7528\u7684\u6d88\u6bd2\u6563\uff0c\u9053\uff1a\u201c\u4f60\u4e14\u6b47\u6b47\uff0c\u660e\u513f\u770b\u4f24\u52bf \u5982\u4f55\uff0c\u518d\u66ff\u4f60\u6cbb\u3002\u201d\u4e91\u91cd\u8bf4\u5f97\u751a\u4f3c\u8f7b\u63cf\u6de1\u5199\uff0c\u5176\u5b9e\u5fc3\u4e2d\u5374\u662f\u7126\u6025\u975e\u5e38\u3002\u4ed6\u7528\u7684\u4e0d\u662f\u5bf9\u75c7\u7684\u89e3 \u836f\uff0c\u867d\u7136\u6bd2\u8840\u5df2\u7ecf\u6324\u51fa\uff0c\u8fd9\u836f\u4e5f\u6709\u6d88\u6bd2\u4e4b\u529f\uff0c\u4f46\u5230\u5e95\u653e\u5fc3\u4e0d\u4e0b\uff0c\u751f\u6015\u6b8b\u7559\u7684\u6bd2\u6c14\uff0c\u4f1a\u5728\u91cc\u9762 \u4f5c\u602a\uff0c\u867d\u4e0d\u81f4\u6b7b\uff0c\u4e5f\u53ef\u80fd\u4ee4\u5979\u8db3\u8ddb\u6b8b\u5e9f\u3002 \u3000\u3000\u6fb9\u53f0\u955c\u660e\u5374\u4f3c\u6beb\u4e0d\u5728\u4e4e\uff0c\u7709\u773c\u76c8\u76c8\uff0c\u82b3\u5fc3\u6b63\u81ea\u65e0\u9650\u6b23\u6170\u3002\u4e91\u91cd\u7684\u5c0f\u5fc3\u670d\u4f8d\uff0c\u5173\u5207\u4e4b\u60c5\uff0c \u6ea2\u4e8e\u8f9e\u8868\u3002\u6fb9\u53f0\u955c\u660e\u5927\u4e3a\u611f\u52a8\uff0c\u7981\u4e0d\u4f4f\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u6bd4\u8d77\u5f20\u4e39\u67ab\u6765\uff0c\u4ed6\u867d\u7136\u7a0d\u4e3a\u7c97\u9c81\uff0c\u4f46\u5bf9 \u6211\u7684\u4e00\u7247\u771f\u8bda\uff0c\u5374\u4e5f\u4e0d\u5728\u5f20\u4e39\u67ab\u5bf9\u4e91\u857e\u4e4b\u4e0b\u3002\u201d\u7b11\u5bf9\u4e91\u91cd\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u8981\u53ea\u987e\u6211\uff0c\u4f60\u4e5f\u88ab\u90a3 \u8499\u9762\u8001\u8d3c\u6293\u4e86\u4e0b\u5462\u3002\u201d\u4e91\u91cd\u7b54\u9053\uff1a\u201c\u6211\u7a7f\u6709\u62a4\u8eab\u7684\u9501\u5b50\u9ec4\u91d1\u7532\uff0c\u4e0d\u59a8\u4e8b\u7684\u3002\u201d\u5c06\u6218\u888d\u8131\u4e0b\u4e86 \u4e00\u770b\u53ea\u89c1\u62a4\u8eab\u7532\u4e5f\u88ab\u6293\u88c2\u4e86\u4e00\u5904\uff0c\u5e78\u800c\u672a\u4f24\u76ae\u8089\u3002\u6fb9\u53f0\u955c\u660e\u548b\u9053\uff1a\u201c\u8fd9\u8499\u9762\u4eba\u597d\u5389\u5bb3\uff0c\u529f\u529b \u6bd4\u6697\u7b97\u6211\u7684\u90a3\u4e2a\u9ad8\u5f97\u591a\u3002\u201d \u3000\u3000\u8c08\u8bdd\u4e4b\u95f4\uff0c\u5973\u5175\u5df2\u628a\u88ab\u86c7\u7130\u7bad\u5f15\u8d77\u7684\u5c0f\u706b\u5934\u6251\u706d\uff0c\u8fc7\u4e86\u7247\u523b\uff0c\u53ea\u542c\u5f97\u25a1\u6740\u4e4b\u58f0\u6e10\u6e10\u9759 \u6b62\uff0c\u53ea\u6709\u5728\u7a7a\u4e2d\u547c\u5578\u7684\u7fbd\u7bad\u4e4b\u58f0\uff0c\u8fd8\u5728\u6b64\u8d77\u5f7c\u843d\u3002\u536b\u58eb\u8fdb\u6765\u62a5\u9053\uff1a\u201c\u6258\u4e91\u5927\u4eba\u7684\u6d2a\u798f\uff0c\u8d3c\u4eba \u5df2\u7ecf\u9000\u4e86\u3002\u201d\u4e91\u91cd\u9053\uff1a\u201c\u90fd\u9000\u4e86\u5417\uff1f\u201d\u536b\u58eb\u9053\uff1a\u201c\u4ed6\u4eec\u4f3c\u4e4e\u662f\u627c\u5b88\u7740\u56db\u9762\u7684\u9ad8\u5730\u53ea\u5411\u6211\u4eec\u653e \u7bad\uff0c\u5374\u4e0d\u51b2\u8fc7\u6765\u4e86\u3002\u201d\u4e91\u91cd\u9053\uff1a\u201c\u4ed6\u4eec\u5f3a\u653b\u4e0d\u6210\uff0c\u60f3\u662f\u8981\u56f0\u6bd9\u6211\u4eec\uff0c\u4f60\u4eec\u4ecd\u8981\u5c0f\u5fc3\uff0c\u4e0d\u53ef\u677e \u61c8\u3002\u6709\u4eba\u53d7\u4f24\u6ca1\u6709\uff1f\u201d\u536b\u58eb\u9053\uff1a\u201c\u53ea\u6709\u4e24\u4eba\u53d7\u4e86\u7bad\u4f24\uff0c\u4e00\u4eba\u53d7\u4e86\u5200\u4f24\u90fd\u4e0d\u4e25\u91cd\u3002\u201d\u4e91\u91cd\u9053\uff1a \u201c\u5c06\u4ed6\u4eec\u6276\u8fdb\u5e10\u6765\uff0c\u53eb\u5973\u5175\u66ff\u4ed6\u4eec\u5305\u88f9\u4f24\u53e3\u3002\u201d\u4e91\u91cd\u6240\u5e26\u7684\u5341\u516b\u4e2a\u4f8d\u4ece\uff0c\u90fd\u662f\u5fa1\u524d\u7684\u4e00\u4e8c\u7b49 \u4f8d\u536b\uff0c\u4e2a\u4e2a\u6b66\u529f\u9ad8\u5f3a\uff0c\u4e00\u53ef\u5f53\u767e\uff0c\u6240\u4ee5\u6bd4\u5bf9\u4e4b\u4e0b\uff0c\u635f\u5931\u751a\u5fae\u3002 \u3000\u3000\u5973\u5175\u4eec\u624b\u5fd9\u811a\u4e71\uff0c\u521a\u521a\u66ff\u4e09\u4e2a\u53d7\u4f24\u7684\u6218\u58eb\u624e\u597d\u4f24\u53e3\uff0c\u53ea\u542c\u5f97\u536b\u58eb\u53c8\u8fdb\u6765\u62a5\u9053\uff1a\u201c\u8d3c\u5175\u5728 \u5c71\u5934\u4e0a\u70e7\u8d77\u706b\u5806\uff0c\u9ed1\u70df\u51b2\u5929\uff0c\u4e0d\u77e5\u4f55\u6545\uff1f\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u53c8\u542c\u5f97\u5916\u9762\u5c16\u9510\u7684\u53f7\u89d2\u4e4b\u58f0\u54cd\u4e86\u8d77 \u6765\u3002 \u3000\u3000\u53f7\u89d2\u6025\u54cd\uff0c\u4f46\u5374\u5e76\u65e0\u8d3c\u4eba\u51b2\u6765\u3002\u4e91\u91cd\u9053\uff1a\u201c\u4e0d\u597d\uff0c\u4ed6\u4eec\u70b9\u71c3\u70fd\u706b\uff0c\u5439\u8d77\u53f7\u89d2\uff0c\u5b9a\u662f\u62db\u96c6 \u63f4\u5175\uff0c\u53ea\u6015\u62c2\u6653\u4e4b\u524d\uff0c\u8fd8\u6709\u4e00\u573a\u6076\u6597\u3002\u201d\u53eb\u968f\u4ece\u4eec\u4ecd\u6309\u4ee5\u524d\u7684\u6218\u6597\u90e8\u7f72\uff0c\u4e24\u4eba\u4e00\u7ec4\uff0c\u6563\u5728\u5e10 \u5e55\u56db\u8fb9\u3002 \u3000\u3000\u8d3c\u5175\u7684\u53f7\u89d2\u54cd\u4e86\u4e00\u9635\u53c8\u505c\u4e0b\u4e86\uff0c\u53ea\u6709\u706b\u70df\u968f\u98ce\u98d8\u6765\uff0c\u5916\u8fb9\u4e00\u7247\u5bc2\u9759\u3002\u4e91\u91cd\u4e0a\u524d\u4ed4\u7ec6\u5bdf\u89c6 \u6fb9\u53f0\u955c\u660e\u95ee\u9053\uff1a\u201c\u597d\u4e00\u70b9\u4e48\uff1f\u201d\u6fb9\u53f0\u955c\u660e\u9053\uff1a\u201c\u8212\u670d\u591a\u4e86\u3002\u201d\u79c0\u7709\u4e00\u7ad6\uff0c\u5ffd\u9053\uff1a\u201c\u6211\u770b\u8fd9\u4e9b \u8d3c\u5175\uff0c\u4e0d\u662f\u666e\u901a\u7684\u5f3a\u76d7\u3002\u201d\u4e91\u91cd\uff1a\u201c\u600e\u4e48\uff1f\u201d\u6fb9\u53f0\u955c\u660e\u9053\uff1a\u201c\u82e5\u7136\u662f\u5fd7\u5728\u5077\u8425\u52ab\u7269\u7684\u666e\u901a\u5f3a \u76d7\uff0c\u4ed6\u4eec\u4e5f\u4e0d\u5fc5\u8499\u7740\u9762\u5b54\u4e86\u3002\u201d\u4e91\u91cd\u9053\uff1a\u201c\u4f60\u4ee5\u4e3a\u662f\u8499\u53e4\u5175\u4e48\uff1f\u4f11\u8bf4\u4e5f\u5148\u4e0d\u6562\u5982\u6b64\u80c6\u5927\u5984 \u4e3a\uff0c\u90a3\u4e09\u4e2a\u88ab\u6211\u4eec\u6253\u6b7b\u7684\u8499\u9762\u4eba\uff0c\u6211\u5df2\u53eb\u4eba\u68c0\u67e5\u8fc7\u4e86\uff0c\u90fd\u662f\u6c49\u4eba\u3002\u201d\u6fb9\u53f0\u955c\u660e\u9053\uff1a\u201c\u90a3\u4ed6\u4eec \u4e3a\u4f55\u8981\u8499\u7740\u9762\u5b54\uff1f\u8499\u53e4\u5883\u5185\uff0c\u53c8\u600e\u4f1a\u6709\u8fd9\u8bb8\u591a\u6c49\u4eba\u5f3a\u76d7\uff1f\u201d\u4e91\u91cd\u7709\u95f4\u4e00\u76b1\uff0c\u5ffd\u5730\u8bf4\u9053\uff1a\u201c\u4ed6 \u4eec\u662f\u6015\u88ab\u6211\u4eec\u8ba4\u5f97\uff0c\u7528\u6bd2\u624b\u4f24\u4f60\u7684\u90a3\u4e2a\u8499\u9762\u4eba\u8eab\u5f62\u597d\u719f\uff0c\u6211\u4f3c\u4e4e\u662f\u5728\u54ea\u91cc\u89c1\u8fc7\u4f3c\u7684\u3002\u201d\u6fb9\u53f0 \u955c\u660e\u9053\uff1a\u201c\u4f60\u518d\u60f3\u4e00\u60f3\u3002\u201d\u4e91\u91cd\u9053\uff1a\u201c\u54e6\u6211\u8bb0\u8d77\u4e86\uff0c\u90a3\u662f\u6211\u5728\u6821\u573a\u6bd4\u6b66\uff0c\u593a\u6b66\u72b6\u5143\u4e4b\u65f6\uff0c\u6240 \u89c1\u8fc7\u7684\u3002\u53ea\u662f\u90a3\u65f6\u6765\u6bd4\u6b66\u7684\u4e3e\u5b50\u751a\u591a\uff0c\u6211\u53c8\u6ca1\u6709\u548c\u4ed6\u4ea4\u624b\uff0c\u5374\u60f3\u4e0d\u8d77\u4ed6\u5230\u5e95\u662f\u8c01\u3002\u201d \u3000\u3000\u6b47\u4e86\u4e00\u6b47\uff0c\u4e91\u91cd\u53f9\u606f\u9053\uff1a\u201c\u53ef\u60dc\u521a\u624d\u6ca1\u6709\u5c06\u4ed6\u64d2\u7740\u3002\u201d\u521a\u521a\u8bf4\u5230\u6b64\u5904\uff0c\u5e10\u7bf7\u5ffd\u7136\u5982\u53d7\u91cd \u7269\u6240\u538b\uff0c\u51f9\u9690\u4e0b\u6765\uff0c\u4e91\u91cd\u5927\u60ca\u8dc3\u8d77\u3002\u53ea\u89c1\u5e10\u7bf7\u9661\u5730\u88c2\u5f00\u4e00\u4e2a\u5927\u6d1e\uff0c\u4e00\u4e2a\u4eba\u4e22\u4e86\u4e0b\u6765\uff0c\u6b63\u662f\u90a3 \u4e2a\u4f24\u4e86\u6fb9\u53f0\u955c\u660e\u7684\u8499\u9762\u5bb6\u4f19\u3002\u4e91\u91cd\u53eb\u9053\uff1a\u201c\u662f\u54ea\u4f4d\u9ad8\u4eba\u5728\u4e0e\u6211\u76f8\u620f\uff1f\u201d\u5ffd\u89c1\u4ece\u88c2\u53e3\u5904\u53c8\u8dc3\u8fdb \u4e00\u4eba\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6211\u66ff\u4f60\u5c06\u6076\u8d3c\u64d2\u6765\u600e\u8bf4\u76f8\u620f\uff1f\u201d\u6fb9\u53f0\u955c\u660e\u559c\u6781\u800c\u547c\uff0c\u539f\u6765\u6765\u7684\u7adf\u662f\u5f20\u4e39 \u67ab\u3002 \u3000\u3000\u4e91\u91cd\u7741\u5927\u4e86\u773c\uff0c\u505a\u58f0\u4e0d\u5f97\uff0c\u5fc3\u9053\uff1a\u201c\u5f20\u4e39\u67ab\u7aef\u7684\u795e\u51fa\u9b3c\u6ca1\u4e0d\u53ef\u601d\u8bae\u3002\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u4f60 \u5c06\u4ed6\u7684\u9762\u5177\u62c9\u4e0b\u4e00\u770b\u3002\u201d\u90a3\u8499\u9762\u4eba\u4f3c\u4e4e\u662f\u88ab\u5f20\u4e39\u67ab\u70b9\u4e86\u7a74\u9053\uff0c\u6454\u5012\u5730\u4e0a\uff0c\u52a8\u5f39\u4e0d\u5f97\u3002\u4e91\u91cd\u62c9 \u4e0b\u4ed6\u7684\u9762\u5177\uff0c\u539f\u6765\u5374\u662f\u6c99\u65e0\u5fcc\u3002\u4e91\u91cd\u8bb0\u5f97\u4ed6\u5728\u6821\u573a\u6bd4\u6b66\u4e4b\u65f6\u88ab\u94c1\u81c2\u91d1\u733f\u7684\u5e08\u5012\u9646\u5c55\u9e4f\u6253\u4e0b\u64c2 \u53f0\u7684\uff0c\u5f53\u65f6\u53ea\u4ee5\u4e3a\u4ed6\u662f\u4e00\u4e2a\u666e\u901a\u7684\u4e3e\u5b50\uff0c\u5374\u6599\u4e0d\u5230\u4ed6\u662f\u7eb5\u6a2a\u4e24\u56fd\u8fb9\u5883\u7684\u5927\u8d3c\u3002 \u3000\u3000\u4e91\u91cd\u6012\u6c14\u51b2\u51b2\uff0c\u9053\uff1a\u201c\u5f20\u5144\uff0c\u4f60\u628a\u4ed6\u7a74\u9053\u89e3\u5f00\uff0c\u5f85\u6211\u5ba1\u95ee\u4ed6\u3002\u201d\u5f20\u4e39\u67ab\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4ed6 \u4eec\u5df2\u6765\u4e86\u63f4\u5175\uff0c\u8fd8\u6709\u9ad8\u624b\u76f8\u52a9\uff0c\u5c31\u8981\u518d\u6765\u8fdb\u653b\uff0c\u54ea\u6709\u65f6\u95f4\u5bb9\u4f60\u7ec6\u7ec6\u5ba1\u95ee\uff1f\u201d\u6fb9\u53f0\u955c\u660e\u77e5\u9053\u5f20 \u4e39\u67ab\u667a\u8ba1\u591a\u7aef\uff0c\u6c99\u65e0\u5fcc\u53c8\u662f\u4ed6\u6240\u64d2\u6765\u5fc5\u77e5\u5e95\u7ec6\uff0c\u7acb\u523b\u8bf4\u9053\uff1a\u201c\u5f20\u5927\u54e5\uff0c\u54b1\u4eec\u4eba\u5c11\uff0c\u53ea\u6050\u4e0d\u8010 \u4e45\u3002\u8fd8\u8981\u8bf7\u4f60\u8bbe\u6cd5\u3002\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u4e91\u5144\uff0c\u90a3\u5c31\u8bf7\u6055\u6211\u6bdb\u9042\u81ea\u8350\uff0c\u501f\u7bb8\u4ee3\u7b79\u4e86\u3002\u201d\u4e91\u91cd\u6b64\u65f6 \u5bf9\u5f20\u4e39\u67ab\u4ea6\u662f\u751a\u4e3a\u4f69\u670d\uff0c\u9053\uff1a\u201c\u8bf7\u4f60\u65bd\u4ee4\u4fbf\u662f\u3002\u201d \u3000\u3000\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u7acb\u523b\u64a4\u8d70\uff01\u201d\u4e91\u91cd\u9053\uff1a\u201c\u9ed1\u591c\u4e4b\u4e2d\uff0c\u4e0d\u77e5\u654c\u4eba\u865a\u5b9e\uff0c\u53c8\u6709\u5987\u5973\uff0c\u64a4\u8d70\u5c82\u4e0d \u66f4\u4e3a\u5371\u9669\uff1f\u201d\u6fb9\u53f0\u955c\u660e\u5fae\u7b11\u9053\uff1a\u201c\u5f20\u5927\u54e5\u5fc5\u6709\u9ad8\u89c1\u3002\u201d\u4e91\u91cd\u9ed8\u4e0d\u4f5c\u58f0\u3002\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u4f60\u5c06\u8981 \u4ea4\u4e0e\u74e6\u523a\u7684\u793c\u7269\uff0c\u90fd\u653e\u5728\u4e00\u5339\u9a6c\u4e0a\u3002\u53eb\u5176\u4ed6\u7684\u4eba\u90fd\u5f03\u4e86\u9a6c\u5339\uff0c\u968f\u6211\u51b2\u51fa\uff0c\u4fdd\u4f60\u6beb\u65e0\u4f24\u635f\uff0c\u800c \u4e14\u53ef\u7acb\u5927\u529f\u3002\u201d \u3000\u3000\u4e91\u91cd\u534a\u4fe1\u534a\u7591\uff0c\u77a7\u4e86\u6fb9\u53f0\u955c\u660e\u4e00\u773c\uff0c\u6fb9\u53f0\u955c\u660e\u9053\uff1a\u201c\u4f60\u4e0d\u5fc5\u4e3a\u6211\u62c5\u5fc3\uff0c\u6211\u80fd\u8d70\u8def\u3002\u201d\u4e00 \u8dc3\u800c\u8d77\u3002\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u539f\u6765\u662f\u6fb9\u53f0\u59b9\u5b50\u53d7\u4e86\u4f24\u4e48\uff1f\u65e2\u80fd\u8d70\u52a8\uff0c\u4fbf\u8d70\u65e0\u9632\uff0c\u8fc7\u4e00\u4e2a\u65f6\u8fb0\uff0c\u6211\u66ff \u4f60\u6cbb\u3002\u201d\u53eb\u5973\u5175\u9009\u4e86\u4e00\u5339\u597d\u9a6c\uff0c\u5c06\u539a\u7ed2\u5305\u7740\u9a6c\u8e44\uff0c\u628a\u8981\u5e26\u7684\u4e1c\u897f\u90fd\u653e\u5728\u9a6c\u80cc\u4e0a\u3002\u4e91\u91cd\u4e5f\u53eb\u4f8d \u536b\u51fa\u53bb\u4f20\u4ee4\uff0c\u4e00\u4e2a\u4f20\u9012\uff0c\u4e0d\u4e00\u4f1a\uff0c\u5341\u516b\u540d\u968f\u4ece\u90fd\u96c6\u4e2d\u8d77\u6765\uff0c\u5377\u8d77\u5e10\u7bf7\uff0c\u80cc\u8d77\u4f24\u8005\uff0c\u6084\u6084\u5730\u968f \u7740\u5f20\u4e39\u67ab\u64a4\u8d70\u3002\u4e34\u8d70\u4e4b\u65f6\uff0c\u5f20\u4e39\u67ab\u53eb\u4ed6\u4eec\u5728\u6bcf\u5339\u9a6c\u7684\u5c41\u80a1\u4e0a\u90fd\u63d2\u4e0a\u4e00\u5200\uff0c\u90a3\u4e9b\u9a6c\u8d1f\u75db\u72c2\u5636\u9f50 \u5411\u654c\u4eba\u7684\u9635\u5730\u51b2\u53bb\uff0c\u5a01\u52bf\u6781\u662f\u5413\u4eba\uff0c\u9ed1\u591c\u4e4b\u4e2d\uff0c\u654c\u4eba\u53ea\u4ee5\u4e3a\u4ed6\u4eec\u53cd\u653b\u5077\u88ad\uff0c\u614c\u5fd9\u8fce\u6218\u3002\u5f20\u4e39 \u67ab\u8d81\u7740\u654c\u4eba\u6df7\u4e71\u4e4b\u65f6\uff0c\u5df2\u5e26\u7740\u4f17\u4eba\u8e51\u624b\u8e51\u811a\u5730\u6392\u6210\u4e00\u6761\u6563\u5175\u7ebf\u4ece\u897f\u8fb9\u7684\u4e00\u6761\u5c0f\u8def\u51b2\u51fa\u3002 \u3000\u3000\u6bcf\u4e2a\u4eba\u90fd\u6709\u8f7b\u529f\u7684\u5e95\u5b50\uff0c\u9a6c\u8e44\u5305\u4e0a\u539a\u7ed2\uff0c\u8d70\u8def\u4e5f\u65e0\u58f0\u97f3\uff0c\u53c8\u662f\u5728\u6df7\u4e71\u4e4b\u4e2d\uff0c\u654c\u4eba\u7adf\u6ca1\u53d1 \u89c9\u3002\u8d70\u4e86\u4e00\u9635\uff0c\u4e91\u91cd\u5947\u9053\uff1a\u201c\u8fd9\u6761\u8def\u600e\u4e48\u6ca1\u6709\u654c\u4eba\u628a\u5b88\uff1f\u201d\u5f20\u4e39\u67ab\u7b11\u9053\uff1a\u201c\u8fd9\u6761\u8def\u6ca1\u6709\u51fa \u53e3\uff0c\u662f\u4e2a\u7edd\u5730\uff0c\u6709\u5341\u6765\u4e2a\u54e8\u5175\u7ed9\u6211\u7ed3\u679c\u4e86\u3002\u5c0f\u5fc3\uff0c\u4e0b\u9762\u4e00\u6bb5\u8def\u8d8a\u6765\u8d8a\u9669\u4e86\u3002\u201d\u4e24\u65c1\u5c71\u77f3\u5d99 \u5ccb\uff0c\u8346\u68d8\u906e\u9053\uff0c\u5f20\u4e39\u67ab\u624b\u6301\u5b9d\u5251\uff0c\u7275\u7740\u9a6c\u5339\uff0c\u9886\u5148\u5f00\u9053\uff0c\u4f17\u4f8d\u4ece\u90fd\u662f\u4e00\u8eab\u6b66\u529f\uff0c\u62ab\u8346\u65a9\u68d8\uff0c \u4e0d\u4e00\u523b\u5c31\u5230\u4e86\u5916\u9762\u3002\u6708\u9ed1\u98ce\u9ad8\uff0c\u53ea\u6709\u51e0\u70b9\u758f\u661f\uff0c\u9ed1\u9edd\u9edd\u7684\u770b\u4e0d\u6e05\u5916\u9762\u7684\u5730\u5f62\uff0c\u4f46\u89c9\u5f97\u5916\u9762\u662f \u4e00\u5927\u7247\u5bbd\u9614\u7684\u8349\u5730\uff0c\u4f3c\u4e4e\u662f\u4e24\u5c71\u4e4b\u95f4\u7684\u5ce1\u8c37\u3002 \u3000\u3000\u4e91\u91cd\u5618\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u51b2\u662f\u51b2\u51fa\u53bb\u4e86\uff0c\u4f46\u7eb5\u9a6c\u4e4b\u8ba1\uff0c\u53ea\u80fd\u9a97\u8fc7\u4e00\u65f6\uff0c\u524d\u9762\u6709\u5927\u5c71\u6321\u8def\uff0c \u9ed1\u591c\u4e4b\u4e2d\u5982\u4f55\u8d8a\u8fc7\uff1f\u7ec8\u987b\u7ed9\u4ed6\u4eec\u53d1\u89c9\u3002\u201d\u5f20\u4e39\u67ab\u7b11\u9053\uff1a\u201c\u6211\u6b63\u8981\u5f15\u4ed6\u4eec\u5230\u6b64\u5730\u6765\u3002\u201d\u6307\u6325\u4f17 \u4eba\u62a2\u4e0a\u9ad8\u5730\u57cb\u4f0f\u3002\u8fc7\u4e86\u4e00\u523b\uff0c\u53ea\u89c1\u706b\u5149\u873f\u8713\uff0c\u6709\u5982\u957f\u9f99\uff0c\u679c\u7136\u662f\u8d3c\u5175\u53d1\u73b0\uff0c\u8ffd\u8e2a\u524d\u6765\u3002\u5f20\u4e39 \u67ab\u5f85\u654c\u4eba\u8e0f\u5165\u8349\u5730\u5ffd\u5730\u54c8\u54c8\u5927\u7b11\uff0c\u7b11\u58f0\u4e00\u53d1\uff0c\u56db\u9762\u5c71\u9e23\u8c37\u5e94\uff0c\u9ed1\u591c\u4e4b\u4e2d\uff0c\u654c\u4eba\u4e0d\u77e5\u9053\u4ed6\u4eec\u8eb2 \u5728\u4f55\u65b9\uff0c\u56db\u5904\u4e71\u6251\uff0c\u9aa4\u7136\u95f4\uff0c\u5ffd\u542c\u5f97\u547c\u53f7\u6551\u547d\u4e4b\u58f0\u56db\u8d77\u3002\u5f20\u4e39\u67ab\u559d\u9053\uff1a\u201c\u5c06\u77f3\u5934\u6eda\u4e0b\uff0c\u6253\u8fd9 \u4e9b\u738b\u516b\u7f94\u5b50\uff01\u201d\u5c71\u4e0a\u591a\u7684\u662f\u78e8\u76d8\u5927\u7684\u5ca9\u77f3\uff0c\u5bfb\u5e38\u4eba\u6570\u4eba\u63a8\u4e4b\u4e0d\u52a8\uff0c\u4e91\u91cd\u7684\u4f8d\u4ece\u5374\u4e2a\u4e2a\u90fd\u6709\u6570 \u767e\u65a4\u6c14\u529b\uff0c\u4e00\u58f0\u4ee4\u4e0b\uff0c\u5927\u77f3\u7eb7\u7eb7\u5411\u5c71\u4e0b\u6eda\u53bb\u3002\u706b\u628a\u5149\u4e2d\uff0c\u53ea\u89c1\u8d3c\u5175\u5728\u8349\u5730\u4e0a\u6323\u624e\u4e71\u6eda\uff0c\u5341\u4e4b \u516b\u4e5d\u90fd\u597d\u50cf\u77ee\u4e86\u4e00\u622a\u4f3c\u7684\uff0c\u7ad9\u4e0d\u8d77\u6765\u3002\u4e71\u77f3\u4e00\u6eda\uff0c\u538b\u5728\u8eab\u4e0a\uff0c\u66f4\u662f\u60e8\u4e0d\u5fcd\u7779\u3002 \u3000\u3000\u4e91\u91cd\u4ed4\u7ec6\u770b\u65f6\uff0c\u53ea\u89c1\u8349\u5730\u4e0a\u6ce5\u6d46\u6709\u5982\u6cb8\u4e86\u7684\u6c34\u4e00\u822c\uff0c\u5c42\u5c42\u6d8c\u8d77\u3002\u539f\u6765\u4e0b\u9762\u7adf\u662f\u4e00\u4e2a\u5927\u6cbc \u6cfd\uff0c\u4e0a\u9762\u8986\u7740\u7eff\u840d\uff0c\u9ed1\u591c\u671b\u53bb\u5c31\u50cf\u4e00\u5927\u7247\u6bdb\u8338\u8338\u7684\u8349\u5730\u3002\u8d3c\u4eba\u9677\u5728\u6cbc\u6cfd\u4e4b\u4e2d\uff0c\u5df2\u662f\u96be\u4e8e\u6323\u624e \u51fa\u6765\uff0c\u7ed9\u77f3\u5934\u6eda\u4e2d\u7684\u66f4\u662f\u65ad\u624b\u6298\u8db3\uff0c\u7acb\u906d\u6ca1\u9876\u3002\u4e91\u91cd\u5927\u5403\u4e00\u60ca\uff0c\u539f\u6765\u4ed6\u4eec\u521a\u624d\u7adf\u662f\u4ece\u6cbc\u6cfd\u8fb9 \u7f18\u901a\u8fc7\uff0c\u8981\u4e0d\u662f\u5f20\u4e39\u67ab\u719f\u8bc6\u5730\u5f62\uff0c\u9ed1\u591c\u4e4b\u4e2d\uff0c\u5b9a\u7136\u5931\u8db3\u3002 \u3000\u3000\u6fb9\u53f0\u955c\u660e\u9053\uff1a\u201c\u9976\u4e86\u4ed6\u4eec\u5427\u3002\u201d\u5f20\u4e39\u67ab\u4e0b\u4ee4\u505c\u6b62\u6eda\u77f3\uff0c\u5374\u5bf9\u4e91\u91cd\u7b11\u9053\uff1a\u201c\u55bd\u5175\u5475\u6055\uff0c\u9996 \u6076\u96be\u9976\u3002\u6211\u548c\u4f60\u53bb\u6349\u4ed6\u4eec\u4e00\u4e24\u4e2a\u4eba\u3002\u6fb9\u53f0\u59b9\u5b50\uff0c\u4f60\u5728\u8fd9\u91cc\u7a0d\u5f85\u7247\u523b\u3002\u201d \u3000\u3000\u5f20\u4e39\u67ab\u5e26\u4e91\u91cd\u4ece\u5c71\u5761\u7ed5\u51fa\uff0c\u8fd9\u65f6\u4ece\u6cbc\u6cfd\u4e2d\u6323\u624e\u5f97\u8131\u7684\u8d3c\u5175\u5df2\u662f\u6e83\u4e0d\u6210\u519b\uff0c\u7eb7\u7eb7\u9003\u8d70\uff0c \u5f20\u3001\u4e91\u4e8c\u4eba\u6084\u6084\u63a9\u51fa\uff0c\u53ea\u89c1\u9002\u624d\u90a3\u8499\u9762\u8001\u6c49\u548c\u53e6\u4e00\u4e2a\u8499\u9762\u4eba\u6bbf\u540e\uff0c\u4e00\u8def\u5406\u559d\uff0c\u8981\u4e71\u519b\u805a\u5408\u3002 \u5f20\u4e39\u67ab\u4e0e\u4e91\u91cd\u9661\u5730\u8df3\u51fa\uff0c\u5f20\u4e39\u67ab\u5411\u90a3\u8499\u9762\u8001\u8005\u4e00\u5251\u523a\u53bb\u75be\u82e5\u98d8\u98ce\uff0c\u90a3\u8001\u8005\u5411\u65c1\u4e00\u95ea\uff0c\u547c\u7684\u4e00 \u638c\u6a2a\u626b\uff0c\u5c82\u77e5\u5f20\u4e39\u67ab\u8eab\u6cd5\u6bd4\u4ed6\u66f4\u5feb\uff0c\u4f3c\u662f\u65e9\u5df2\u6599\u5b9a\u4ed6\u7684\u9000\u8def\uff0c\u5251\u950b\u4e00\u504f\uff0c\u6070\u6070\u523a\u4e2d\u4ed6\u7684\u80a9 \u5934\uff0c\u90a3\u8001\u8005\u4e00\u638c\u5288\u7a7a\uff0c\u65e9\u5df2\u5931\u4e86\u91cd\u5fc3\uff0c\u53c8\u4e2d\u4e86\u4e00\u5251\uff0c\u7acb\u523b\u4e00\u4e2a\u5012\u683d\u8471\u8dcc\u5728\u5730\u4e0a\uff0c\u5f20\u4e39\u67ab\u4e00\u628a \u6293\u7740\u4ed6\u7684\u8863\u9886\uff0c\u50cf\u9ebb\u9e70\u6349\u5154\u4e00\u6837\u5c06\u4ed6\u63d0\u8d77\u6765\u3002\u4e91\u91cd\u6251\u5411\u90a3\u8499\u9762\u4eba\uff0c\u53cd\u624b\u4e00\u638c\u4e5f\u6b63\u6253\u7740\uff0c\u5374\u542c \u5f97\u58f0\u5982\u8d25\u9769\u90a3\u4eba\u6643\u4e86\u4e00\u4e0b\uff0c\u7adf\u672a\u8dcc\u5012\uff0c\u539f\u6765\u4ed6\u91cc\u9762\u7a7f\u6709\u62a4\u8eab\u7684\u7280\u725b\u76ae\u5957\u3002\u4e91\u91cd\u4e00\u638c\u5c06\u4ed6\u7684\u76ae \u5957\u9707\u88c2\uff0c\u5de6\u53f3\u5f00\u5f13\uff0c\u7b2c\u4e8c\u638c\u8ddf\u7740\u8fde\u73af\u75be\u626b\uff0c\u90a3\u4eba\u54fc\u4e86\u4e00\u58f0\uff0c\u9a88\u6307\u5411\u4e91\u91cd\u8170\u95f4\u4e00\u6233\uff0c\u8fc5\u5373\u53cd\u8eab \u4e00\u811a\uff0c\u811a\u5c16\u4e0a\u6311\u4e91\u91cd\u7684\u624b\u8155\u3002\u8fd9\u4e24\u62db\u7528\u5f97\u72e0\u75be\u975e\u5e38\uff0c\u7adf\u662f\u897f\u85cf\u5929\u9f99\u6d3e\u7684\u4e0a\u4e58\u6b66\u529f\uff0c\u90a3\u4e00\u6233\u4e00 \u8e22\u672c\u4e0d\u7a00\u5947\uff0c\u4f46\u8fde\u63a5\u4e24\u62db\u5374\u6559\u4eba\u975e\u95ea\u907f\u4e0d\u53ef\uff0c\u4e91\u91cd\u53ea\u5f97\u64a4\u638c\u62a4\u8eab\uff0c\u90a3\u4eba\u6e9c\u6ed1\u4e4b\u6781\uff0c\u7acb\u523b\u9003 \u8dd1\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u64d2\u4e86\u90a3\u4e2a\u8499\u9762\u8001\u8005\uff0c\u8f6c\u8fc7\u8eab\u6765\u5bf9\u4e2a\u6b63\u7740\uff0c\u90a3\u4eba\u731b\u53d1\u4e00\u62f3\uff0c\u5f20\u4e39\u67ab\u5c06\u8499\u9762\u8001\u8005\u5f80\u524d \u4e00\u6321\uff0c\u4e00\u4e2a\u95ea\u8eab\uff0c\u5de6\u624b\u4e00\u626c\uff0c\u53ea\u542c\u5f97\u90a3\u8499\u9762\u8001\u8005\u6740\u730e\u822c\u5730\u558a\u5c06\u8d77\u6765\uff0c\u4e2d\u95f4\u6742\u6709\u5c16\u9510\u7684\u53eb\u58f0\uff0c \u5374\u662f\u90a3\u9003\u8d70\u7684\u8499\u9762\u4eba\u6240\u53d1\u3002\u5f20\u4e39\u67ab\u54c8\u54c8\u5927\u7b11\uff0c\u770b\u90a3\u8499\u9762\u8001\u8005\uff0c\u5374\u5df2\u7ecf\u88ab\u540c\u4f34\u7684\u62f3\u5934\u6253\u5f97\u6655\u6b7b \u53bb\u4e86\u3002 \u3000\u3000\u4e91\u91cd\u6307\u7740\u90a3\u9003\u4eba\u7684\u80cc\u5f71\u9053\uff1a\u201c\u8fd9\u4eba\u7684\u6b66\u529f\u6700\u5f3a\uff0c\u53ea\u7a0d\u900a\u4e8e\u6211\u8f88\uff0c\u5728\u4eca\u665a\u6765\u6697\u88ad\u7684\u654c\u4eba \u4e2d\uff0c\u4ee5\u4ed6\u6700\u4e3a\u9ad8\u660e\u4e86\u3002\u5f20\u5144\u4f55\u6545\u653e\u4ed6\u9003\u8d70\uff1f\u201d\u5f20\u4e39\u67ab\u7b11\u9053\uff1a\u201c\u5f53\u6349\u4fbf\u6349\uff0c\u5f53\u653e\u4fbf\u653e\uff0c\u8fd9\u4e2a\u4eba \u561b\uff0c\u8fd8\u662f\u653e\u4ed6\u9003\u8d70\u7684\u597d\u3002\u201d\u4e91\u91cd\u89c9\u4ed6\u6545\u5f04\u7384\u865a\uff0c\u9887\u4e3a\u4e0d\u60a6\uff0c\u4f46\u53c8\u6015\u4ed6\u53e6\u6709\u795e\u673a\u5999\u7b97\uff0c\u53ea\u6709\u4e0d \u518d\u8bd8\u95ee\u3002 \u3000\u3000\u4e24\u4eba\u56de\u8f6c\u539f\u6765\u7684\u5730\u65b9\uff0c\u8fd8\u672a\u5230\u4e00\u987f\u996d\u7684\u65f6\u523b\u3002\u6fb9\u53f0\u955c\u660e\u8d5e\u9053\uff1a\u201c\u597d\u6781\uff01\u5173\u516c\u676f\u9152\u65a9\u534e\u96c4 \u4ea6\u4e0d\u8fc7\u5982\u65af\uff01\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u597d\u5566\uff0c\u4eca\u591c\u6ca1\u4e8b\u4e86\uff0c\u4ed6\u4eec\u53ef\u4ee5\u5b89\u5b89\u9759\u9759\u5730\u7761\u4e00\u89c9\u5566\u3002\u81f3\u4e8e\u4f60 \u6211\uff0c\u53ef\u8fd8\u6709\u4e9b\u672a\u4e86\u4e4b\u4e8b\uff0c\u4e91\u5144\uff0c\u73b0\u5728\u662f\u8be5\u4f60\u5347\u5802\u5ba1\u95ee\u4e86\u3002\u201d\u53eb\u4f17\u4eba\u642d\u8d77\u5e10\u7bf7\uff0c\u5404\u81ea\u6b47\u606f\uff0c\u4ed6 \u548c\u4e91\u91cd\u3001\u6fb9\u53f0\u955c\u660e\u4e09\u4eba\u5374\u7528\u51b7\u6c34\u55b7\u9192\u90a3\u4e2a\u8499\u9762\u8001\u6c49\uff0c\u625b\u8fdb\u5e10\u5e55\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u65e9\u6599\u5230\u662f\u8c01\uff0c\u62c9\u4e0b\u9762\u5177\u4e00\u770b\uff0c\u679c\u7136\u662f\u6c99\u65e0\u5fcc\u7684\u7236\u4eb2\u6c99\u6d9b\u3002\u5f20\u4e39\u67ab\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u53db \u53cb\u6c42\u8363\uff0c\u901a\u756a\u5356\u56fd\uff0c\u767e\u6b7b\u4e0d\u8db3\u4ee5\u853d\u5176\u8f9c\uff0c\u4eca\u591c\u4e4b\u4e8b\uff0c\u5e78\u65e9\u5728\u6211\u7684\u610f\u6599\u4e4b\u4e2d\uff0c\u5426\u5219\u4e24\u56fd\u4e4b\u95f4\uff0c \u5c82\u4e0d\u662f\u53c8\u7ed9\u4f60\u6405\u8d77\u4e00\u573a\u6218\u4e8b\uff1f\u201d\u4e91\u91cd\u4e5f\u559d\u9053\uff1a\u201c\u5927\u660e\u7684\u4f7f\u8005\u4e0e\u4f60\u4f55\u51a4\u4f55\u4ec7\uff0c\u4f60\u4f55\u6545\u8981\u5c06\u6211\u4eec \u6740\u5bb3\uff1f\u5feb\u5feb\u4ece\u5b9e\u62db\u6765\uff0c\u5426\u5219\u6709\u4f60\u82e6\u5403\u3002\u201d\u6c99\u6d9b\u53eb\u9053\uff1a\u201c\u6211\u5b8c\u5168\u65e0\u610f\u5c06\u4f60\u4eec\u6740\u5bb3\uff0c\u66f4\u975e\u60f3\u6311\u8d77 \u4e24\u56fd\u5e72\u6208\u3002\u201d\u4e91\u91cd\u9053\uff1a\u201c\u90a3\u4f60\u4e3a\u4f55\u5e26\u9886\u55bd\u5175\u524d\u6765\u5077\u88ad\uff1f\u201d\u6c99\u6d9b\u9053\uff1a\u201c\u8fd9\u3001\u8fd9\u2026\u2026\u201d\u8bb7\u8bb7\u4e0d\u6562 \u51fa\u53e3\u3002\u5f20\u4e39\u67ab\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u8bf4\u4e0d\u8bf4\uff1f\u201d\u9a88\u8d77\u53cc\u6307\uff0c\u5411\u6c99\u6d9b\u80c1\u4e0b\u4e00\u6233\uff0c\u6c99\u6d9b\u987f\u611f\u6709\u5982\u5343 \u767e\u94f6\u9488\u523a\u4f53\uff0c\u75db\u82e6\u96be\u5f53\uff0c\u9053\uff1a\u201c\u4f60\u9976\u4e86\u6211\u5427\uff0c\u6211\u8bf4\uff0c\u6211\u8bf4\u3002\u201d\u5f20\u4e39\u67ab\u5411\u4ed6\u7684\u76f8\u5e94\u7a74\u9053\u4e00\u62cd\uff0c \u89e3\u4e86\u8fd9\u72ec\u95e8\u70b9\u7a74\u7684\u529f\u592b\uff0c\u9053\uff1a\u201c\u82e5\u6709\u534a\u5b57\u865a\u8a00\uff0c\u518d\u53eb\u4f60\u6328\u6211\u4e00\u6307\u3002\u201d\u6c99\u6d9b\u9053\uff1a\u201c\u662f\u4e5f\u5148\u6307\u4f7f \u6211\u7684\u3002\u201d\u4e91\u91cd\u5403\u4e86\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u80e1\u8bf4\u3002\u201d\u6c99\u6d9b\u9053\uff1a\u201c\u4e5f\u5148\u672c\u610f\u53eb\u6211\u4eec\u5c06\u4f60\u63b3\u53bb\uff0c\u7136\u540e\u518d\u7531\u4ed6 \u6d3e\u5175\u6551\u56de\u3002\u4f2a\u4f5c\u662f\u5b98\u519b\u6253\u8d3c\uff0c\u8fd9\u6837\u4f60\u4fbf\u843d\u5728\u4ed6\u7684\u638c\u63e1\u4e4b\u4e2d\uff0c\u4e0d\u7531\u4f60\u4e0d\u5bf9\u4ed6\u611f\u6069\u6234\u5fb7\u3002\u201d\u4e91\u91cd \u4e00\u65f6\u4e4b\u95f4\u5c1a\u60f3\u4e0d\u901a\uff0c\u5f20\u4e39\u67ab\u7b11\u9053\uff1a\u201c\u8fd9\u8ba1\u7b56\u597d\u6bd2\uff0c\u771f\u662f\u4e00\u77f3\u4e09\u9e1f\u4e4b\u8ba1\u3002\u7b2c\u4e00\u662f\u5148\u6298\u4f60\u5929\u671d\u4f7f \u8005\u7684\u5a01\u98ce\uff0c\u53eb\u4f60\u626b\u5c3d\u989c\u9762\u3002\u201d\u6fb9\u53f0\u955c\u660e\u9053\uff1a\u201c\u4ed6\u5c06\u4f60\u6551\u56de\uff0c\u4f60\u843d\u5728\u4ed6\u7684\u624b\u4e2d\uff0c\u7b49\u4e8e\u662f\u4fd8\u864f\u7684 \u8eab\u4efd\uff0c\u8bf4\u8bdd\u4e5f\u4e0d\u54cd\u5566\u3002\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u8fd9\u6837\uff0c\u5728\u7f14\u548c\u4e4b\u65f6\uff0c\u4ed6\u4fbf\u4e5f\u5360\u5c3d\u4fbf\u5b9c\u63d0\u51fa\u5c48\u8fb1\u7684\u6761 \u4ef6\uff0c\u4f60\u5728\u4ed6\u7684\u638c\u63e1\u4e4b\u4e2d\uff0c\u786c\u4e5f\u786c\u4e0d\u8d77\u6765\u5566\u3002\u5f53\u7136\u8fd9\u90fd\u662f\u4ed6\u7684\u4e00\u53a2\u60c5\u613f\u3002\u201d\u4e91\u91cd\u4ed4\u7ec6\u4e00\u60f3\uff0c\u81ea \u53f9\u8111\u7b4b\u8fdf\u949d\uff0c\u4e0d\u53ca\u5f20\u4e39\u67ab\u548c\u6fb9\u53f0\u955c\u660e\u7684\u5fc3\u601d\u7075\u654f\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u4e5f\u5148\u6d3e\u6765\u7684\u5b98\u5175\uff0c\u548c\u4f60\u4eec\u5728\u4ec0\u4e48\u5730\u65b9\u76f8\u7ea6\u78b0\u5934\uff1f\u201d\u6c99\u6d9b\u9053\uff1a\u201c\u5c31\u5728\u524d\u5c71\u5c71 \u53e3\u3002\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u679c\u7136\u4f60\u5e76\u65e0\u865a\u8a00\uff0c\u597d\uff0c\u9976\u4f60\u4e0d\u6b7b\u3002\u201d\u5728\u4ed6\u8eab\u4e0a\u62cd\u4e86\u4e24\u4e0b\uff0c\u5c06\u4ed6\u7684\u7435\u7436\u9aa8 \u9707\u788e\uff0c\u628a\u4ed6\u7684\u6c14\u529b\u5168\u90fd\u7834\u53bb\uff0c\u53eb\u4ed6\u7ec8\u8eab\u6b8b\u5e9f\uff0c\u7eb5\u6709\u6bd2\u638c\uff0c\u4e5f\u4e0d\u80fd\u8fd0\u7528\u4f24\u4eba\u3002\u53c8\u5c06\u6c99\u65e0\u5fcc\u63d0 \u6765\uff0c\u4e5f\u4f9d\u6cd5\u70ae\u5236\uff0c\u5c06\u4ed6\u4eec\u4e8c\u4eba\u63a8\u51fa\u5e10\u5916\uff0c\u53eb\u4ed6\u4eec\u81ea\u5df1\u89c5\u8def\u9003\u751f\u3002 \u3000\u3000\u4e91\u91cd\u9053\uff1a\u201c\u660e\u513f\u5982\u4f55\u5e94\u4ed8\u74e6\u523a\u7684\u5b98\u5175\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u4f60\u5148\u597d\u597d\u5730\u7761\u4e00\u89c9\uff0c\u517b\u8db3\u7cbe\u795e\uff0c \u81ea\u80fd\u5e94\u4ed8\u3002\u603b\u4e4b\u4f60\u7edd\u4e0d\u4f1a\u4e22\u8138\u4fbf\u662f\u3002\u201d\u6fb9\u53f0\u955c\u660e\u9053\uff1a\u201c\u5f20\u5927\u54e5\u795e\u673a\u5999\u7b97\uff0c\u771f\u662f\u4eba\u6240\u4e0d\u53ca\uff0c\u600e \u4e48\u4e8b\u4e8b\u90fd\u50cf\u5728\u4f60\u7684\u610f\u6599\u4e4b\u4e2d\uff1f\u4f60\u96be\u9053\u6709\u672a\u535c\u5148\u77e5\u7684\u672c\u9886\uff1f\u201d\u4e91\u91cd\u4e5f\u6709\u8bb8\u591a\u7591\u60d1\uff0c\u60f3\u8bf7\u5f20\u4e39\u67ab \u89e3\u91ca\uff0c\u5f20\u4e39\u67ab\u4e00\u7b11\u6446\u624b\u9053\uff1a\u201c\u5929\u673a\u4e0d\u53ef\u6cc4\u6f0f\uff0c\u660e\u513f\u4e00\u65e9\uff0c\u4f60\u4eec\u5168\u90fd\u77e5\u9053\uff0c\u4f55\u5fc5\u7740\u6025\u3002\u4e91\u5144\uff0c \u4f60\u4eec\u90fd\u8be5\u7761\u5566\u3002\u201d \u3000\u3000\u4e91\u91cd\u6ee1\u809a\u76ae\u7eb3\u95f7\uff0c\u6b63\u60f3\u53bb\u7761\uff0c\u5f20\u4e39\u67ab\u5ffd\u9053\uff1a\u201c\u6211\u51e0\u4e4e\u5fd8\u4e86\u4e00\u4e8b\uff0c\u4f60\u4e14\u7b49\u4e00\u4f1a\u513f\u3002\u6fb9\u53f0\u59b9 \u5b50\uff0c\u4f60\u7684\u811a\u611f\u89c9\u5982\u4f55\uff1f\u201d\u6fb9\u53f0\u955c\u660e\u8bd5\u8d70\u4e24\u6b65\uff0c\u9053\uff1a\u201c\u597d\u50cf\u6709\u70b9\u4e0d\u80fd\u7528\u52b2\u3002\u201d\u5377\u8d77\u88e4\u811a\u4e00\u770b\uff0c \u4e91\u91cd\u60ca\u547c\u9053\uff1a\u201c\u817f\u809a\u5b50\u90fd\u7ea2\u80bf\u5566\uff0c\u4e39\u67ab\uff0c\u4f60\u4e0d\u662f\u8bf4\u6709\u529e\u6cd5\u5305\u5979\u6cbb\u597d\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u4e0d\u9519\uff0c \u4f46\u8981\u4f60\u7ed9\u5979\u6765\u6cbb\u3002\u201d\u53d6\u51fa\u4e00\u679a\u94f6\u9488\u9053\uff1a\u201c\u4f60\u5728\u5979\u811a\u8ddf\u6d8c\u6cc9\u7a74\u523a\u4e24\u9488\uff0c\u518d\u5728\u5c3e\u95fe\u7684\u51e4\u5c3e\u7a74\u523a\u4e24 \u9488\uff0c\u660e\u513f\u4e00\u65e9\uff0c\u7ea2\u80bf\u4fbf\u6d88\uff0c\u597d\uff0c\u4f60\u4e0d\u5fc5\u7740\u5fd9\uff0c\u6211\u518d\u8be6\u7ec6\u6559\u4f60\u9488\u7078\u4e4b\u6cd5\u3002\u201d\u6307\u624b\u753b\u811a\u5730\u8bf4\u4e86\u4e00 \u904d\uff0c\u53c8\u9053\uff1a\u201c\u74e6\u523a\u6c14\u5019\u4e0d\u597d\uff0c\u8bb8\u591a\u4eba\u90fd\u4f1a\u5f97\u5173\u8282\u75bc\u75db\u4e4b\u75c7\uff0c\u6211\u8fd9\u9488\u7078\u4e4b\u6cd5\uff0c\u4e0d\u4f46\u80fd\u6cbb\u7b4b\u9aa8\u9ebb \u75f9\uff0c\u8fde\u811a\u8ddb\u4e86\u90fd\u80fd\u6cbb\u597d\uff0c\u4e91\u5144\uff0c\u4f60\u4e0d\u53ef\u4e0d\u5b66\u3002\u201d\u4e91\u91cd\u5fc3\u9053\uff1a\u201c\u5979\u53c8\u4e0d\u662f\u811a\u8ddb\uff0c\u8981\u4f60\u8fd9\u6837\u7f57 \u5506\uff1f\u201d\u5bf9\u5f20\u4e39\u67ab\u7684\u7d6e\u7d6e\u4e0d\u4f11\uff0c\u751a\u611f\u5fc3\u70e6\uff0c\u9053\uff1a\u201c\u6539\u65e5\u518d\u5b66\u4e5f\u4e0d\u8fdf\u3002\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u975e\u5b66\u4e0d \u53ef\uff01\u4f60\u6015\u9ebb\u70e6\u662f\u4e0d\u662f\uff1f\u597d\uff0c\u6211\u5c06\u8fd9\u79d8\u672c\u90fd\u4ea4\u7ed9\u4f60\u5566\u3002\u6fb9\u53f0\u59b9\u5b50\uff0c\u4f60\u975e\u7763\u7740\u4ed6\u5b66\u4e0d\u53ef\u3002\u201d\u6478\u51fa \u4e00\u672c\u4e66\uff0c\u5c06\u5176\u4e2d\u4e4b\u4e00\u7ae0\u6495\u4e0b\uff0c\u786c\u585e\u5230\u4e91\u91cd\u7684\u624b\u4e2d\u3002\u4e91\u91cd\u5927\u4e3a\u5947\u602a\u3002\u6b63\u662f\uff1a \u3000\u3000\u6df1\u5fc3\u541b\u4e0d\u8bc6\uff0c\u597d\u610f\u540e\u6765\u77e5\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u840d\u8e2a\u4fa0\u5f71\u5f55\u300b\u2014\u2014\u7b2c\u4e09\u5341\u56de\u3000\u529b\u6297\u91d1\u724c\u820d\u751f\u6551\u826f\u53cb\u3000\u8eab\u586b\u70ae\u53e3\u62fc\u6b7b\u62a4\u6a80\u90ce \u6881\u7fbd\u751f\u300a\u840d\u8e2a\u4fa0\u5f71\u5f55\u300b \u7b2c\u4e09\u5341\u56de\u3000\u529b\u6297\u91d1\u724c\u820d\u751f\u6551\u826f\u53cb\u3000\u8eab\u586b\u70ae\u53e3\u62fc\u6b7b\u62a4\u6a80\u90ce \u3000\u3000\u6fb9\u53f0\u955c\u660e\u5fc3\u601d\u7075\u654f\uff0c\u89c1\u5f20\u4e39\u67ab\u4e00\u5b9a\u8981\u5c06\u90a3\u51e0\u9875\u533b\u4e66\u585e\u5230\u4e91\u91cd\u624b\u4e2d\uff0c\u6599\u77e5\u5176\u4e2d\u5fc5\u6709\u7f18\u6545\uff0c \u7b11\u9053\uff1a\u201c\u65e2\u7136\u662f\u5f20\u5927\u54e5\u4e00\u756a\u597d\u610f\u4f60\u5c31\u63a5\u4e0b\u5427\u3002\u201d\u4e91\u91cd\u6700\u542c\u5979\u8bdd\uff0c\u89c1\u5979\u8fd9\u4e48\u4e00\u8bf4\u4e5f\u5c31\u62ff\u4e86\u8fc7 \u6765\uff0c\u5fc3\u4e2d\u5374\u662f\u6697\u6697\u5947\u602a\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u597d\u5566\uff0c\u4f60\u5feb\u66ff\u6fb9\u53f0\u59b9\u5b50\u6cbb\u4f24\uff0c\u6211\u4e0d\u6253\u6405\u4f60\u4eec\u5566\u3002\u201d\u4e00\u7b11\u6380\u5e18\u800c\u51fa\u3002 \u3000\u3000\u7b2c\u4e8c\u65e5\u4e00\u65e9\uff0c\u5f20\u4e39\u67ab\u4fbf\u628a\u4e91\u91cd\u5524\u9192\uff0c\u95ee\u9053\uff1a\u201c\u6fb9\u53f0\u59b9\u5b50\u7684\u4f24\u52bf\u5982\u4f55\uff1f\u201d\u4e91\u91cd\u7b11\u9053\uff1a\u201c\u4f60 \u6240\u4f20\u7684\u90a3\u9488\u7078\u4e4b\u672f\uff0c\u771f\u662f\u795e\u5947\u6781\u4e86\uff0c\u4e0b\u9488\u4e4b\u540e\uff0c\u4e0d\u8fc7\u534a\u4e2a\u65f6\u8fb0\uff0c\u5979\u5df2\u80fd\u884c\u8d70\u5982\u5e38\u4e86\u3002\u201d\u5f20\u4e39 \u67ab\u9053\uff1a\u201c\u90a3\u4e48\u54b1\u4eec\u73b0\u5728\u4fbf\u62d4\u961f\u51fa\u53d1\uff0c\u8fd8\u6709\u4e00\u573a\u597d\u620f\u5728\u540e\u5934\u5462\u3002\u201d\u4e91\u91cd\u6ee1\u809a\u76ae\u7eb3\u95f7\uff0c\u4e0d\u77e5\u5f20\u4e39 \u67ab\u4f55\u4ee5\u4f1a\u77e5\u9053\u4ed6\u4eec\u6628\u591c\u9047\u96be\uff0c\u66f4\u6599\u4e0d\u5230\u4ed6\u8fd8\u6709\u4ec0\u4e48\u795e\u673a\u5999\u7b97\uff0c\u53ea\u597d\u4efb\u4ece\u4ed6\u6765\u6446\u5e03\u3002 \u3000\u3000\u5341\u516b\u540d\u8ddf\u968f\u4e91\u91cd\u51fa\u4f7f\u7684\u536b\u58eb\uff0c\u5728\u6628\u665a\u90a3\u573a\u6fc0\u70c8\u7684\u6218\u6597\u4e2d\uff0c\u53ea\u662f\u8f7b\u4f24\u4e86\u4e09\u4eba\uff0c\u90fd\u80fd\u9a91\u9a6c\u3002 \u6c99\u6d9b\u7684\u8d3c\u5175\uff0c\u4e00\u534a\u9677\u5728\u6cbc\u6cfd\u4e4b\u4e2d\u65e9\u5df2\u60e8\u906d\u6ca1\u9876\uff0c\u4e22\u4e0b\u7684\u9a6c\u5339\uff0c\u904d\u5730\u90fd\u662f\uff0c\u4e91\u91cd\u53eb\u968f\u4ece\u9009\u4e86\u4e8c \u5341\u591a\u9a91\u597d\u9a6c\uff0c\u5217\u961f\u8d70\u51fa\u8c37\u3002 \u3000\u3000\u521a\u51fa\u524d\u5c71\u4fbf\u542c\u5f97\u8fdc\u5904\u6709\u9a6c\u961f\u5954\u9a70\uff0c\u8fd8\u9690\u9690\u6742\u6709\u547c\u53eb\u4e4b\u58f0\u3002\u4e91\u91cd\u5947\u9053\uff1a\u201c\u597d\u50cf\u662f\u4e00\u961f\u6e83 \u5175\u3002\u201d\u5f20\u4e39\u67ab\u7b11\u9053\uff1a\u201c\u597d\u620f\u5c31\u8981\u767b\u573a\uff0c\u4f60\u7b49\u7740\u77a7\u4fbf\u662f\u3002\u201d\u8f6c\u8fc7\u4e00\u4e2a\u5c71\u5773\uff0c\u5ffd\u89c1\u524d\u9762\u5c18\u5934\u5927 \u8d77\uff0c\u4e00\u961f\u8499\u53e4\u5175\u8fce\u9762\u800c\u6765\uff0c\u53ea\u6709\u4e8c\u4e09\u5341\u9a91\u7684\u6837\u5b50\uff0c\u8863\u7532\u4e0d\u5168\uff0c\u9a6c\u5636\u4eba\u5598\uff0c\u519b\u5bb9\u51cc\u4e71\uff0c\u663e\u7136\u662f \u66fe\u6253\u4e86\u4e00\u573a\u8d25\u4ed7\u3002 \u3000\u3000\u4e91\u91cd\u60ca\u7591\u4e0d\u5b9a\uff0c\u53ea\u89c1\u524d\u9762\u7684\u4e00\u540d\u8499\u53e4\u519b\u5b98\uff0c\u4f9d\u7740\u4e2d\u56fd\u6b66\u58eb\u7684\u793c\u8282\uff0c\u5728\u9a6c\u80cc\u4e0a\u62b1\u62f3\u8bf4\u9053\uff1a \u201c\u4e91\u4f7f\u81e3\u9a7e\u4e34\u655d\u56fd\uff0c\u6211\u4eec\u6709\u5931\u8fce\u63a5\uff0c\u8bf7\u4f7f\u81e3\u6055\u7f6a\u3002\u201d\u4e91\u91cd\u9053\uff1a\u201c\u4f60\u4eec\u662f\u4ec0\u4e48\u4eba\uff1f\u201d\u90a3\u519b\u5b98 \u9053\uff1a\u201c\u6211\u4eec\u662f\u5949\u592a\u5e08\u4e4b\u547d\uff0c\u63a5\u4f7f\u81e3\u5230\u655d\u56fd\u4eac\u57ce\u7684\u3002\u5440\uff0c\u5f20\u516c\u5b50\u4e5f\u5728\u8fd9\u91cc\uff1f\u90a3\u597d\u6781\u4e86\u3002\u201d\u8fd9\u519b \u5b98\u6b63\u662f\u4e5f\u5148\u5e10\u4e0b\u7684\u7b2c\u4e00\u6b66\u58eb\u989d\u5409\u591a\uff0c\u4ed6\u89c1\u7740\u4e86\u5f20\u4e39\u67ab\uff0c\u4e0d\u7531\u81ea\u5df1\u5730\u663e\u51fa\u5c34\u5c2c\u7684\u795e\u8272\uff0c\u867d\u7136\u5bd2 \u51b7\uff0c\u989d\u4e0a\u5374\u6c81\u51fa\u6c57\u73e0\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u7684\u592a\u5e08\u7167\u6599\u5f97\u771f\u662f\u5468\u5230\u3002\u201d\u7b56\u9a6c\u4e0a\u524d\uff0c\u84e6\u7136\u4f38\u624b\u4e00\u6293\uff0c\u5c06 \u989d\u5409\u591a\u65c1\u8fb9\u7684\u4e00\u540d\u519b\u5b98\u786c\u751f\u751f\u5730\u4ece\u9a6c\u80cc\u4e0a\u5012\u62fd\u8fc7\u6765\u3002\u90a3\u519b\u5b98\u4e5f\u597d\u4e00\u4e86\u5f97\uff0c\u88ab\u5f20\u4e39\u67ab\u51fa\u5176\u4e0d\u610f \u5730\u4ece\u9a6c\u80cc\u4e0a\u6293\u8d77\uff0c\u8eab\u5b50\u817e\u7a7a\uff0c\u8fd8\u5c45\u7136\u8e22\u51fa\u4e24\u811a\uff0c\u4f46\u8fc5\u5373\u88ab\u5f20\u4e39\u67ab\u70b9\u4e86\u9ebb\u7a74\uff0c\u4e0d\u80fd\u52a8\u5f39\u3002 \u3000\u3000\u8fd9\u4e00\u4e0b\u5927\u51fa\u4f17\u4eba\u610f\u5916\uff0c\u989d\u5409\u591a\u559d\u9053\uff1a\u201c\u5f20\u516c\u5b50\uff0c\u4f60\u5c82\u53ef\u5982\u6b64\u65e0\u793c\uff01\u201d\u5f20\u4e39\u67ab\u53cc\u624b\u4e00\u6495\uff0c \u5c06\u90a3\u519b\u5b98\u7684\u519b\u8863\u6495\u4e0b\uff0c\u53c8\u5265\u5f00\u4e86\u4ed6\u91cc\u9762\u6240\u7a7f\u7684\u62a4\u8eab\u76ae\u5957\uff0c\u5c06\u4ed6\u4e00\u65cb\uff0c\u9732\u51fa\u80cc\u810a\uff0c\u53ea\u89c1\u80cc\u810a\u4e0a \u523a\u7740\u4e00\u4e2a\u8349\u4e66\u7684\u201c\u8d3c\u201d\u5b57\u3002\u5f20\u4e39\u67ab\u5927\u7b11\u9053\uff1a\u201c\u662f\u8c01\u65e0\u793c\uff1f\u4f60\u4e5f\u66fe\u8bfb\u8fc7\u4e2d\u56fd\u4e4b\u4e66\uff0c\u8fd9\u4e2a\u8d3c\u5b57\u4f60 \u8ba4\u5f97\u5417\uff1f\u5e78\u4e8f\u6211\u65e9\u505a\u4e0b\u8bb0\u53f7\u3002\u201d\u5c06\u90a3\u519b\u5b98\u4e00\u6254\uff0c\u4e91\u91cd\u8eab\u8fb9\u7684\u536b\u58eb\u6025\u5fd9\u63a5\u8fc7\u3002\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u4e91 \u4f7f\u81e3\u8fd9\u25a1\u5c31\u662f\u6628\u665a\u8131\u9003\u7684\u90a3\u4e2a\u8499\u9762\u8d3c\u4eba\uff0c\u540d\u53eb\u9ebb\u7ffc\u8d5e\uff0c\u53c8\u662f\u74e6\u523a\u592a\u5e08\u5e10\u4e0b\u7684\u6b66\u58eb\uff0c\u4f60\u5e26\u7740 \u4ed6\uff0c\u9001\u56de\u7ed9\u4e5f\u5148\u5427\uff01\u201d \u3000\u3000\u989d\u5409\u591a\u5927\u543c\u4e00\u58f0\uff0c\u62d4\u5200\u4fbf\u65ab\uff0c\u5f20\u4e39\u67ab\u4e3e\u5251\u76f8\u8fce\uff0c\u6321\u4e86\u51e0\u62db\u5ffd\u800c\u7eb5\u58f0\u5927\u7b11\u9053\uff1a\u201c\u4f60\u6628\u665a\u53d7 \u7684\u82e6\u5934\u8fd8\u4e0d\u591f\u5417\uff1f\u4f60\u613f\u843d\u5728\u6211\u7684\u624b\u4e2d\u8fd8\u662f\u613f\u843d\u5728\u4f60\u592a\u5e08\u4ec7\u5bb6\u7684\u624b\u91cc\uff1f\u201d\u989d\u5409\u591a\u6014\u4e86\u4e00\u6014\u9a82 \u9053\uff1a\u201c\u6628\u665a\u7684\u4e8b\u60c5\u539f\u6765\u90fd\u662f\u4f60\u5c0f\u5b50\u4ece\u4e2d\u6363\u9b3c\uff01\u201d\u4e00\u62db\u201c\u529b\u5288\u534e\u5c71\u201d\u5200\u950b\u76f4\u843d\uff0c\u4e00\u526f\u62fc\u547d\u7684\u795e \u6c14\uff0c\u5f20\u4e39\u67ab\u6697\u8fd0\u5185\u52b2\uff0c\u501f\u529b\u53cd\u524a\uff0c\u4e3e\u8d77\u767d\u4e91\u5b9d\u5251\u5411\u4e0a\u4e00\u64a9\uff0c\u53ea\u542c\u5f97\u53ee\u5f53\u4e00\u58f0\uff0c\u5200\u5251\u76f8\u4ea4\uff0c\u989d \u5409\u591a\u7684\u539a\u80cc\u65ab\u5c71\u5200\u7684\u5200\u5934\u7adf\u7136\u65ad\u4e86\uff01\u989d\u5409\u591a\u62e8\u5200\u4fbf\u8d70\u3002\u5f20\u4e39\u67ab\u7b11\u9053\uff1a\u201c\u4f60\u8d70\u4e5f\u8d70\u4e0d\u6389\u554a\uff0c\u4f60 \u77a7\u662f\u8c01\u6765\u4e86\u3002\u201d \u3000\u3000\u53ea\u542c\u5f97\u4e00\u58f0\u9a6c\u5636\uff0c\u9a6c\u8e44\u6025\u54cd\uff0c\u8fdc\u8fdc\u671b\u53bb\uff0c\u53ea\u89c1\u4e00\u56e2\u767d\u5f71\uff0c\u8f6c\u773c\u4e4b\u95f4\uff0c\u4fbf\u5230\u4e86\u9762\u524d\uff0c\u7aef\u7684 \u662f\u58f0\u5982\u5954\u96f7\uff0c\u52bf\u5982\u95ea\u7535\uff0c\u6fb9\u53f0\u955c\u660e\u4e00\u58f0\u6b22\u547c\uff0c\u5927\u53eb\u201c\u54e5\u54e5\u201d\uff0c\u539f\u6765\u6765\u7684\u4e43\u662f\u6fb9\u53f0\u706d\u660e\uff0c\u4ed6\u7684 \u5750\u9a91\u6b63\u662f\u5f20\u4e39\u67ab\u7684\u90a3\u5339\u7167\u591c\u72ee\u5b50\u9a6c\u3002 \u3000\u3000\u989d\u5409\u591a\u5413\u5f97\u9b42\u98de\u9b44\u6563\uff0c\u521a\u53eb\u5f97\u4e00\u58f0\uff1a\u201c\u6fb9\u53f0\u5c06\u519b\u2026\u2026\u201d\u6fb9\u53f0\u706d\u660e\u5927\u7b11\u9053\uff1a\u201c\u8d3c\u25a1\u4e4c\uff0c\u4eca \u65e5\u53eb\u4f60\u8bc6\u5f97\u4ffa\u6fb9\u53f0\u706d\u660e\uff01\u201d\u5288\u9762\u4e00\u62f3\uff0c\u5c06\u989d\u5409\u591a\u51fb\u5012\u3002\u6fb9\u53f0\u706d\u660e\u5728\u4e5f\u5148\u4e0b\u4ee4\u56f4\u56f0\u5f20\u5b97\u5468\u7684\u5e9c \u90b8\u4e4b\u65f6\uff0c\u66fe\u53d7\u591f\u4e86\u989d\u5409\u591a\u7684\u6c14\uff0c\u800c\u4ed6\u8f9e\u4e86\u5b98\u804c\uff0c\u65e0\u6240\u987e\u5fcc\uff0c\u8fd9\u624d\u6cc4\u4e86\u5fc3\u5934\u4e4b\u6124\u3002 \u3000\u3000\u989d\u5409\u591a\u7684\u6b8b\u5175\u867d\u7136\u8fd8\u6709\u4e8c\u4e09\u5341\u9a91\uff0c\u4f46\u8c01\u4e0d\u77e5\u9053\u6fb9\u53f0\u706d\u660e\u4e43\u662f\u74e6\u523a\u56fd\u4e2d\u7684\u7b2c\u4e00\u5458\u864e\u5c06\uff0c\u88ab \u4ed6\u4e00\u559d\uff0c\u80c6\u5b50\u5c0f\u7684\u6709\u51e0\u4e2a\u7adf\u7136\u5012\u649e\u9a6c\u4e0b\uff0c\u5176\u4ed6\u5168\u90fd\u9003\u4e86\u3002\u6fb9\u53f0\u706d\u660e\u5c06\u989d\u5409\u591a\u7ed1\u4e2a\u7ed3\u5b9e\uff0c\u6fb9\u53f0 \u955c\u660e\u6b63\u5f85\u548c\u4ed6\u53d9\u8bdd\uff0c\u5ffd\u89c1\u524d\u9762\u53c8\u662f\u5c18\u5934\u5927\u8d77\u3002\u4e91\u91cd\u60ca\u9053\uff1a\u201c\u4e5f\u5148\u5c45\u7136\u6562\u5982\u6b64\u5984\u4f5c\u80e1\u4e3a\uff0c\u6d3e\u4e86 \u5927\u519b\u6765\u5417\uff1f\u201d\u6fb9\u53f0\u706d\u660e\u7b11\u9053\uff1a\u201c\u8fd9\u4e0d\u662f\u4e5f\u5148\u7684\u5175\u3002\u201d\u7247\u523b\u4e4b\u540e\uff0c\u90a3\u961f\u4eba\u9a6c\u6765\u5230\uff0c\u7ecf\u8fc7\u6fb9\u53f0\u706d \u660e\u5f15\u89c1\uff0c\u539f\u6765\u662f\u74e6\u523a\u4e00\u4e2a\u90e8\u843d\u7684\u914b\u957f\uff0c\u8fd9\u4e2a\u90e8\u843d\u7684\u8001\u914b\u957f\u88ab\u4e5f\u5148\u6240\u6740\uff0c\u5f3a\u8feb\u73b0\u5728\u7684\u914b\u957f\u5f52 \u9644\uff0c\u81f3\u6700\u8fd1\u4e5f\u5148\u4e0e\u963f\u523a\u4e92\u76f8\u4e89\u6743\uff0c\u8fd9\u4e2a\u90e8\u843d\u81ea\u7136\u800c\u7136\u5730\u6295\u4e86\u963f\u523a\u3002\u989d\u5409\u591a\u672c\u6765\u5e26\u6709\u4e94\u767e\u540d\u7cbe \u9510\u9a91\u5175\uff0c\u6628\u665a\u88ab\u8fd9\u4e2a\u90e8\u843d\u5077\u88ad\uff0c\u51e0\u4e4e\u5168\u519b\u8986\u6ca1\u3002\u521a\u624d\u9003\u8d70\u7684\u4e8c\u4e09\u5341\u9a91\uff0c\u4e5f\u90fd\u7ed9\u4ed6\u4eec\u6d3b\u6349\u4e86\u3002 \u3000\u3000\u4e24\u4e0b\u4e00\u8bf4\uff0c\u4e91\u91cd\u8fd9\u624d\u77e5\u9053\u5176\u4e2d\u7684\u539f\u59d4\u3002\u539f\u6765\u5f20\u4e39\u67ab\u4e0e\u6fb9\u53f0\u706d\u660e\u5357\u4e0b\u8fce\u63a5\u4e91\u91cd\uff0c\u5728\u534a\u8def\u4e0a \u89c1\u7740\u989d\u5409\u591a\u8fd9\u652f\u519b\u961f\u79fb\u52a8\uff0c\u5f20\u4e39\u67ab\u591c\u63a2\u8425\u5e10\uff0c\u6070\u5de7\u78b0\u7740\u989d\u5409\u591a\u4e0e\u6c99\u6d9b\u5546\u91cf\u8ba1\u8c0b\uff0c\u4f20\u8fbe\u4e5f\u5148\u7684 \u5bc6\u4ee4\uff0c\u53eb\u6c99\u6d9b\u52ab\u6301\u4e2d\u56fd\u7684\u4f7f\u81e3\uff0c\u518d\u7531\u989d\u5409\u591a\u51fa\u5934\u76f8\u6551\u3002\u5f20\u4e39\u67ab\u6b63\u6101\u4eba\u5c11\uff0c\u96be\u4ee5\u4e00\u9762\u62b5\u6321\u989d\u5409 \u591a\u7684\u4e94\u767e\u7cbe\u5175\uff0c\u4e00\u9762\u62b5\u6321\u6c99\u6d9b\u7684\u8d3c\u4f17\uff0c\u4e0e\u6fb9\u53f0\u706d\u660e\u4e00\u8bf4\uff0c\u77e5\u9053\u9644\u8fd1\u7684\u90e8\u843d\u5c31\u662f\u4e5f\u5148\u7684\u4ec7\u5bb6\uff0c \u4e8e\u662f\u5b9a\u4e0b\u5999\u8ba1\uff0c\u7531\u5f20\u4e39\u67ab\u53bb\u5f15\u6c99\u6d9b\u7684\u8d3c\u5175\u9677\u5165\u6cbc\u6cfd\uff0c\u7531\u6fb9\u53f0\u706d\u660e\u4e58\u4ed6\u7684\u5b9d\u9a6c\u53bb\u8bf4\u670d\u90a3\u4e2a\u90e8\u843d \u7684\u914b\u957f\u51fa\u5175\u3002\u4e24\u4e0b\u51d1\u5408\uff0c\u679c\u7136\u4e00\u4e3e\u594f\u529f\u3002 \u3000\u3000\u81f3\u4e8e\u90a3\u4e2a\u6b66\u58eb\u9ebb\u7ffc\u8d5e\u672c\u548c\u989d\u5409\u591a\u4e00\u4f19\u540c\u6765\uff0c\u4ed6\u662f\u5728\u6c99\u6d9b\u521d\u6b21\u5077\u88ad\u4e91\u91cd\u7684\u5e10\u5e55\u5931\u5229\u4e4b\u540e\uff0c \u770b\u5230\u4fe1\u53f7\u70df\u706b\uff0c\u524d\u6765\u76f8\u52a9\u7684\u3002\u4e0d\u6599\u5374\u88ab\u4e91\u91cd\u4e00\u638c\u9707\u88c2\u4ed6\u7684\u62a4\u8eab\u76ae\u5957\uff0c\u5f20\u4e39\u67ab\u4e58\u673a\u7528\u98de\u9488\u4ece\u88c2 \u53e3\u6253\u8fdb\uff0c\u5728\u4ed6\u8eab\u4e0a\u523a\u4e86\u5927\u5927\u7684\u4e00\u4e2a\u201c\u8d3c\u201d\u5b57\u3002\u800c\u4eca\u88ab\u5f53\u573a\u62c6\u7a7f\uff0c\u5c06\u4ed6\u6349\u83b7\uff0c\u81ea\u662f\u65e0\u8bdd\u53ef\u8bf4\u3002 \u3000\u3000\u90a3\u90e8\u843d\u7684\u914b\u957f\u548c\u4e91\u91cd\u76f8\u89c1\uff0c\u4e92\u732e\u201c\u54c8\u8fbe\u201d\uff08\u4e00\u79cd\u4e1d\u7ee2\u624b\u5e15\u8868\u793a\u5bf9\u5ba2\u4eba\u7684\u5c0a\u91cd\uff09\u3002\u53cc\u65b9\u534f \u5b9a\uff0c\u9664\u4e86\u989d\u5409\u591a\u548c\u9ebb\u7ffc\u8d5e\u7531\u4e91\u91cd\u5e26\u8d70\u4e4b\u5916\uff0c\u5176\u4ed6\u63b3\u83b7\u7684\u4eba\u9a6c\u6b66\u5668\uff0c\u90fd\u5f52\u90a3\u4e2a\u914b\u957f\u3002\u4e91\u91cd\u968f\u4ece \u7684\u9a6c\u5339\uff0c\u8fd9\u65f6\u4e5f\u90fd\u5df2\u622a\u83b7\uff0c\u6240\u6709\u7269\u8d44\u65e0\u4e00\u9057\u5931\u3002\u90a3\u914b\u957f\u5f97\u6fb9\u53f0\u706d\u660e\u4e4b\u52a9\uff0c\u6253\u4e86\u4e00\u4e2a\u5927\u5927\u7684\u80dc \u4ed7\uff0c\u53c8\u83b7\u5f97\u6570\u767e\u5339\u9a6c\u4e0e\u8bb8\u591a\u6b66\u5668\uff0c\u975e\u5e38\u6ee1\u610f\uff0c\u4e00\u518d\u9053\u8c22\uff0c\u5e76\u81ea\u52a8\u62a4\u9001\u4e86\u4e91\u91cd\u4e00\u7a0b\u3002 \u3000\u3000\u9001\u51fa\u5c71\u53e3\uff0c\u90a3\u914b\u957f\u9886\u5175\u56de\u53bb\uff0c\u4e91\u91cd\u4e00\u884c\uff0c\u7ee7\u7eed\u8d76\u8def\u3002\u8fd9\u65f6\u5df2\u662f\u4e2d\u5348\u65f6\u5206\uff0c\u9633\u5149\u666e\u7167\uff0c\u5bd2 \u6c14\u987f\u6d88\uff0c\u4e91\u91cd\u63fd\u8f94\u626c\u97ad\uff0c\u610f\u5174\u751a\u8c6a\uff0c\u5bf9\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u6628\u665a\u5168\u4e8f\u4e86\u4f60\uff0c\u4e5f\u5148\u60f3\u7ed9\u54b1\u4eec\u4e00\u4e2a\u4e0b\u9a6c \u5a01\u5c82\u77e5\u53cd\u7ed9\u54b1\u4eec\u62ff\u7740\u4e86\u4ed6\u7684\u628a\u67c4\u3002\u201d\u5f20\u4e39\u67ab\u5fae\u5fae\u4e00\u7b11\u3002\u6fb9\u53f0\u955c\u660e\u9053\uff1a\u201c\u4e91\u5927\u54e5\uff0c\u6628\u665a\u4f60\u6307\u6325 \u82e5\u5b9a\uff0c\u54b1\u4eec\u5f97\u514d\u707e\u96be\uff0c\u4f60\u7684\u529f\u52b3\u4e5f\u4e0d\u5c0f\u5440\u3002\u201d\u7b56\u9a6c\u508d\u7740\u4e91\u91cd\uff0c\u5e76\u8f94\u800c\u884c\u3002\u6fb9\u53f0\u706d\u660e\u770b\u5728\u773c \u91cc\uff0c\u5fc3\u4e2d\u7b11\u9053\uff1a\u201c\u539f\u6765\u8fd9\u5c0f\u59ae\u5b50\u65e9\u9009\u4e2d\u4e86\u5fc3\u4e0a\u4e4b\u4eba\u4e86\u3002\u201d\u770b\u4ed6\u4eec\u4e8c\u4eba\u4eb2\u5bc6\u7684\u6837\u5b50\uff0c\u60f3\u8d77\u5f20\u4e39 \u67ab\u5931\u610f\u7684\u906d\u9047\uff0c\u4e0d\u7981\u6697\u6697\u4e3a\u5c11\u4e3b\u4f24\u5fc3\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u4e5f\u81ea\u6709\u70b9\u9eef\u7136\u795e\u4f24\u3002\u4e91\u91cd\u6b63\u5728\u5174\u5934\u5ffd\u7136\u95ee\u9053\uff1a\u201c\u857e\u59b9\u5462\uff1f\u5979\u600e\u4e48\u4e0d\u548c\u4f60\u540c\u6765\uff0c\u72ec \u81ea\u4e00\u4eba\u7559\u5728\u74e6\u523a\u4eac\u57ce\u5417\uff1f\u201d\u8fd9\u8bdd\u4ed6\u65e9\u5df2\u60f3\u95ee\uff0c\u53ea\u56e0\u6628\u665a\u4e00\u591c\u7eb7\u6270\uff0c\u76f4\u81f3\u5982\u4eca\uff0c\u624d\u6709\u65f6\u95f4\u95f2\u8bdd \u5bb6\u5e38\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u5446\u4e86\u4e00\u5446\uff0c\u5f3a\u81ea\u6291\u7740\u5fc3\u5934\u7684\u6fc0\u52a8\uff0c\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u55ef\u5979\u6ca1\u6709\u540c\u6765\uff0c\u5979\u56de\u5bb6\u63a2\u671b\u6bcd\u4eb2 \u53bb\u4e86\u3002\u201d\u4e91\u91cd\u5927\u559c\u9053\uff1a\u201c\u4e0d\u77e5\u6211\u7684\u6bcd\u4eb2\u53ef\u8fd8\u5728\u4e16\u5417\uff1f\u201d\u6fb9\u53f0\u706d\u660e\u9053\uff1a\u201c\u542c\u8bf4\u4ee4\u5c0a\u4e5f\u65e9\u5df2\u56de\u5bb6 \u53bb\u5462\u3002\u4e91\u5927\u4eba\uff0c\u8fd9\u6b21\u4f60\u4eec\u5408\u5bb6\u56e2\u5706\uff0c\u771f\u662f\u559c\u4e0a\u52a0\u559c\u5440\uff01\u201d\u4e91\u91cd\u559c\u6781\u82e5\u72c2\u53eb\u9053\uff1a\u201c\u771f\u7684\uff1f\u201d\u6fb9 \u53f0\u706d\u660e\u9053\uff1a\u201c\u8fd9\u8fd8\u80fd\u6709\u5047\uff1f\u53ea\u662f\uff0d\uff0d\u201d\u5ffd\u89c1\u5f20\u4e39\u67ab\u5411\u4ed6\u779f\u4e86\u4e00\u4e2a\u773c\u8272\uff0c\u4e0b\u9762\u7684\u8bdd\u7acb\u523b\u54bd\u4f4f\u3002 \u4e91\u91cd\u9053\uff1a\u201c\u53ea\u662f\u4ec0\u4e48\uff1f\u201d\u6fb9\u53f0\u706d\u660e\u9053\uff1a\u201c\u53ea\u662f\u8def\u9014\u9065\u8fdc\uff0c\u4ed6\u4eec\u4e0d\u77e5\u80fd\u5426\u8d76\u6765\u548c\u4f60\u76f8\u89c1\u3002\u201d\u4e91 \u91cd\u7b11\u9053\uff1a\u201c\u6211\u5c31\u662f\u5728\u74e6\u523a\u4eac\u57ce\u591a\u7559\u51e0\u5929\uff0c\u4e5f\u8981\u7b49\u5019\u4ed6\u4eec\u3002\u201d\u89c1\u5f20\u4e39\u67ab\u795e\u60c5\u51b7\u6f20\uff0c\u9887\u4e3a\u4e0d\u60a6\uff0c \u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u662f\u4e86\uff0c\u6211\u4eec\u4e91\u5bb6\u4e0e\u4ed6\u4eec\u5f20\u5bb6\u672c\u6765\u5c31\u662f\u4e16\u4ec7\uff0c\u4ed6\u542c\u8bf4\u6211\u7236\u4eb2\u8fd8\u5728\u4eba\u4e16\uff0c\u81ea\u7136\u4e0d\u9ad8 \u5174\u4e86\u3002\u5440\uff0c\u8fd9\u4eba\u80f8\u895f\u6c14\u5ea6\uff0c\u672c\u6765\u8c6a\u8fc8\uff0c\u4f46\u5728\u8fd9\u5173\u8282\u4e0a\u5934\uff0c\u4e5f\u672a\u514d\u663e\u51fa\u6c14\u91cf\u72ed\u7a84\u4e86\u3002\u4e5f\u597d\uff0c\u8fd9 \u6837\u6211\u5c31\u53ef\u5c11\u62c5\u4e00\u91cd\u5fc3\u4e8b\uff0c\u4ed6\u548c\u963f\u857e\u4e0d\u5206\u5f00\u4e5f\u5f97\u5206\u5f00\u4e86\u3002\u201d \u3000\u3000\u7ecf\u8fc7\u4e86\u8fd9\u4e00\u573a\u707e\u96be\u4e4b\u540e\uff0c\u4e91\u91cd\u5bf9\u5f20\u4e39\u67ab\u7684\u618e\u6068\u53c8\u51cf\u8f7b\u4e86\u51e0\u5206\uff0c\u751a\u81f3\u53ef\u4ee5\u8bf4\uff0c\u4ed6\u5df2\u7ecf\u6839\u672c \u4e0d\u5c06\u5f20\u4e39\u67ab\u5f53\u4f5c\u4ec7\u4eba\u770b\u5f85\u4e86\u3002\u53ea\u662f\u5bf9\u4e24\u5bb6\u7684\u4ec7\u6068\uff0c\u8fd8\u6709\u70b9\u770b\u4e0d\u5f00\uff0c\u4e0d\u613f\u4e91\u857e\u548c\u4ed6\u7ed3\u5408\u3002\u7ecf\u8fc7 \u4e86\u8fd9\u4e00\u573a\u707e\u96be\u4e4b\u540e\uff0c\u4e00\u8def\u4e0a\u4e5f\u5c31\u5e73\u5b89\u65e0\u4e8b\uff0c\u4e0d\u5fc5\u7ec6\u8868\u3002\u8d70\u4e86\u5341\u591a\u5929\uff0c\u5230\u4e86\u74e6\u523a\u4eac\u57ce\uff0c\u4e91\u91cd\u505c \u4e0b\u9a6c\u6765\uff0c\u9065\u671b\u74e6\u523a\u4eac\u57ce\uff0c\u5fc3\u4e2d\u65e0\u9650\u611f\u6168\uff0c\u60f3\u8d77\u81ea\u5df1\u5e7c\u5e74\uff0c\u66fe\u5728\u74e6\u523a\u5ea6\u8fc7\u6700\u8f9b\u9178\u7684\u5c81\u6708\uff0c\u800c\u4eca \u8d35\u4e3a\u4f7f\u81e3\uff0c\u8863\u9526\u91cd\u6765\uff0c\u5728\u626c\u7709\u5410\u6c14\u4e4b\u9645\uff0c\u60f3\u8d77\u81ea\u5df1\u4e09\u4ee3\u5728\u74e6\u523a\u7684\u906d\u9047\uff0c\u4e0d\u81ea\u89c9\u5730\u843d\u4e0b\u6cea\u6765\uff0c \u4e5f\u4e0d\u77e5\u9053\u662f\u6b22\u559c\u8fd8\u662f\u60b2\u4f24\u3002 \u3000\u3000\u53ea\u542c\u5f97\u4e09\u58f0\u70ae\u54cd\uff0c\u57ce\u95e8\u5927\u5f00\uff0c\u74e6\u523a\u56fd\u738b\u65e9\u5c31\u63a5\u5230\u4e86\u4e2d\u56fd\u4f7f\u81e3\u5230\u6765\u7684\u6d88\u606f\uff0c\u6d3e\u51fa\u4e13\u4f7f\u6b22 \u8fce\u3002\u4e5f\u5148\u4e5f\u6d3e\u51fa\u4eba\u6765\u8fce\u63a5\uff0c\u4ed6\u4eec\u4e0d\u89c1\u989d\u5409\u591a\u7684\u90a3\u961f\u9a91\u5175\u62a4\u9001\uff0c\u5927\u4e3a\u5947\u602a\u3002\u4ed6\u4eec\u505a\u68a6\u4e5f\u6599\u60f3\u4e0d \u5230\uff0c\u989d\u5409\u591a\u548c\u9ebb\u7ffc\u8d5e\u65e9\u53d8\u6210\u4e86\u4fd8\u864f\uff0c\u73b0\u5728\u6b63\u88ab\u56da\u5728\u5bc6\u4e0d\u901a\u98ce\u7684\u9aa1\u8f66\u4e4b\u4e2d\u3002\u81f3\u4e8e\u5f20\u4e39\u67ab\u548c\u6fb9\u53f0 \u706d\u660e\uff0c\u4e00\u542c\u5230\u8fce\u5bbe\u793c\u70ae\uff0c\u65e9\u5c31\u98de\u9a6c\u8dd1\u5f00\uff0c\u907f\u5f00\u6b63\u95e8\uff0c\u4ece\u7b2c\u4e8c\u4e2a\u57ce\u95e8\u8fdb\u57ce\uff0c\u56de\u5bb6\u53bb\u4e86\u3002 \u3000\u3000\u4e5f\u5148\u7b49\u5019\u660e\u671d\u4f7f\u81e3\u7684\u6d88\u606f\uff0c\u6b63\u662f\u5750\u5367\u4e0d\u5b89\uff0c\u542c\u5f97\u56de\u6765\u7684\u4eba\u62a5\u9053\uff0c\u660e\u671d\u7684\u4f7f\u81e3\u5e26\u4e86\u5341\u516b\u540d \u968f\u4ece\uff0c\u8fd8\u6709\u51e0\u540d\u5973\u7737\uff0c\u4e2a\u4e2a\u4eba\u5f3a\u9a6c\u58ee\uff0c\u888d\u7532\u9c9c\u660e\uff0c\u5168\u4e0d\u4f3c\u9884\u6599\u4e2d\u7684\u53d7\u5230\u88ad\u51fb\uff0c\u8863\u7532\u4e0d\u5168\uff0c\u9a6c \u75b2\u4eba\u5026\u7684\u6837\u5b50\u3002\u81f3\u4e8e\u989d\u5409\u591a\u8fde\u540c\u7684\u4e94\u767e\u9a91\u5175\uff0c\u66f4\u662f\u8fde\u4e00\u4e2a\u5f71\u5b50\u4e5f\u89c1\u4e0d\u5230\u3002\u4e5f\u5148\u5403\u4e86\u4e00\u60ca\uff0c\u5927 \u611f\u83ab\u540d\u5176\u5999\uff0c\u5fc3\u9053\uff1a\u201c\u989d\u5409\u591a\u4e0e\u9ebb\u7ffc\u8d5e\u6b66\u529f\u9ad8\u5f3a\uff0c\u4eba\u53c8\u7cbe\u660e\uff0c\u8fd8\u6709\u4e94\u767e\u9a91\u5175\u4e0e\u6c99\u6d9b\u7684\u55bd\u5175\u76f8 \u52a9\uff0c\u7edd\u65e0\u5931\u624b\u4e4b\u7406\u3002\u7eb5\u7b97\u5931\u624b\uff0c\u4e5f\u603b\u8be5\u6709\u4eba\u9003\u56de\u62a5\u4fe1\uff0c\u600e\u7684\u5374\u4e00\u4e2a\u4e5f\u4e0d\u89c1\uff01\u96be\u9053\u8fd9\u660e\u671d\u7684\u4f7f \u8005\u662f\u5929\u795e\u4e0d\u6210\uff1f\u201d\u767e\u601d\u4e0d\u89e3\uff0c\u6574\u665a\u65e0\u7720\uff0c\u7b2c\u4e8c\u65e5\u4e00\u65e9\uff0c\u4fbf\u6d3e\u4eba\u5230\u5ba2\u6808\u8bf7\u4f7f\u81e3\u5230\u592a\u5e08\u5e9c\u4e2d\u76f8 \u4f1a\u3002 \u3000\u3000\u4e5f\u5148\u662f\u74e6\u523a\u7684\u592a\u5e08\uff0c\u53c8\u81ea\u5df1\u59d4\u4efb\u81ea\u5df1\u505a\u8fd9\u6b21\u8bae\u548c\u7684\u5168\u6743\u5927\u81e3\uff0c\u4f9d\u7167\u793c\u8282\uff0c\u4e91\u91cd\u4e5f\u5f53\u53bb\u62dc \u8bbf\u4ed6\u3002\u4e8e\u662f\u5e26\u4e86\u56db\u540d\u968f\u4ece\uff0c\u8fd8\u5e26\u4e86\u4e00\u8f86\u9aa1\u8f66\uff0c\u524d\u5f80\u62dc\u4f1a\u3002 \u3000\u3000\u4e5f\u5148\u4e00\u65e9\u8d77\u6765\u76f8\u5019\uff0c\u597d\u4e0d\u5bb9\u6613\u7b49\u5230\u5c06\u8fd1\u4e2d\u5348\u65f6\u5206\uff0c\u624d\u5f97\u5230\u536b\u58eb\u7684\u62a5\u544a\uff0c\u8bf4\u662f\u660e\u671d\u7684\u4f7f\u81e3 \u5df2\u7ecf\u6765\u5230\uff0c\u8fd8\u8ddf\u6709\u4e00\u8f86\u9aa1\u8f66\u3002\u4e5f\u5148\u5fc3\u4e2d\u6697\u6697\u7eb3\u95f7\uff0c\u60f3\u9053\uff1a\u201c\u96be\u9053\u4ed6\u4eec\u5e26\u4e86\u4e00\u9aa1\u8f66\u7684\u793c\u7269\u6765\uff0c \u8fd9\u4e9b\u793c\u7269\u4e00\u5b9a\u662f\u7b28\u91cd\u7684\u4e1c\u897f\u4e86\u3002\u201d\u7acb\u523b\u6253\u5f00\u4e2d\u5802\uff0c\u5c06\u4f8d\u4ece\u7559\u5728\u9636\u4e0b\uff0c\u8bf7\u4f7f\u81e3\u767b\u5802\u76f8\u89c1\u3002 \u3000\u3000\u4e91\u91cd\u76f8\u8c8c\u8f69\u6602\uff0c\u610f\u6001\u51dd\u91cd\uff0c\u5728\u4e24\u884c\u536b\u58eb\u7684\u5200\u67aa\u5251\u621f\u4e1b\u4e2d\u7a7f\u8fc7\uff0c\u50b2\u7136\u4e0d\u60e7\uff0c\u4e00\u6b65\u4e00\u6b65\uff0c\u8e0f \u5165\u4e2d\u5802\uff0c\u4e5f\u5148\u4e00\u89c1\uff0c\u4e0d\u89c9\u5446\u4e86\u3002\u8fd9\u4eba\u7684\u76f8\u8c8c\uff0c\u597d\u4f3c\u5728\u54ea\u513f\u89c1\u8fc7\u4e00\u822c\uff01\u8fd9\u4e00\u5239\u90a3\u95f4\uff0c\u53e6\u4e00\u4e2a\u660e \u671d\u4f7f\u81e3\u7684\u5f71\u5b50\u7a81\u7136\u4ece\u5fc3\u5934\u63a0\u8fc7\uff0c\u90a3\u662f\u4e09\u5341\u5e74\u524d\u7684\u4e91\u9756\uff0c\u5728\u74e6\u523a\u7267\u9a6c\u4e8c\u5341\u5e74\u7684\u660e\u671d\u4f7f\u81e3\uff0c\u90a3\u4e0d \u5c48\u4e0d\u6320\u3001\u50b2\u7136\u633a\u7acb\u7684\u5f71\u5b50\uff0c\u548c\u773c\u524d\u8fd9\u4e2a\u5c11\u5e74\u7b80\u76f4\u4e00\u6a21\u4e00\u6837\u3002 \u3000\u3000\u4e91\u91cd\u4e0a\u524d\u76f8\u89c1\uff0c\u9001\u4e0a\u4e2d\u56fd\u7687\u5e1d\u7684\u793c\u7269\uff0c\u65e0\u975e\u662f\u7389\u5982\u610f\u6c49\u767d\u7389\u4e4b\u7c7b\uff0c\u90a3\u662f\u4e24\u56fd\u5f80\u6765\u7684\u793c \u8282\uff0c\u4f5c\u4e3a\u5bf9\u522b\u56fd\u5927\u81e3\u7684\u4e00\u79cd\u656c\u610f\uff0c\u867d\u7136\u4e5f\u662f\u8d35\u91cd\u4e4b\u7269\uff0c\u4f46\u5374\u5e76\u975e\u7279\u522b\u7684\u73cd\u5b9d\u3002\u4e91\u91cd\u5411\u4e5f\u5148\u8f6c \u8fbe\u7687\u4e0a\u7684\u95ee\u5019\uff0c\u4e0d\u4ea2\u4e0d\u5351\uff0c\u5b8c\u5168\u9002\u5408\u5927\u56fd\u4f7f\u81e3\u7684\u8eab\u4efd\u3002\u4e5f\u5148\u8bf7\u90fd\u59d3\u540d\uff0c\u542c\u8bf4\u4e5f\u662f\u59d3\u201c\u4e91\u201d\uff0c \u5fc3\u91cc\u5148\u5403\u4e86\u4e00\u60ca\uff0c\u5f3a\u7b11\u8bf4\u9053\uff1a\u201c\u771f\u5de7\u6781\u4e86\uff0c\u4e09\u5341\u5e74\u524d\u6765\u7684\u90a3\u4f4d\u4f7f\u81e3\uff0c\u4e5f\u662f\u59d3\u4e91\u3002\u201d\u4e91\u91cd\u7b11 \u9053\uff1a\u201c\u8fd8\u6709\u66f4\u5de7\u7684\u5462\uff01\u4e09\u5341\u5e74\u524d\u662f\u7237\u7237\u51fa\u4f7f\uff0c\u4e09\u5341\u5e74\u540e\u662f\u4ed6\u5b59\u513f\u51fa\u4f7f\uff0c\u8bf7\u6559\u592a\u5e08\uff0c\u8fd9\u4e5f\u7b97\u5f97 \u662f\u4e2a\u4f73\u8bdd\u5427\u3002\u201d\u4e5f\u5148\u9762\u8272\u500f\u53d8\uff0c\u6025\u5fd9\u5e72\u7b11\u51e0\u58f0\uff0c\u9053\uff1a\u201c\u4f73\u8bdd\uff0c\u4f73\u8bdd\uff01\u201d\u60ca\u60f6\u5931\u8272\uff0c\u624b\u8db3\u65e0\u63aa \u7684\u795e\u60c5\uff0c\u90fd\u8868\u9732\u4e86\u51fa\u6765\u3002\u4e91\u91cd\u5f97\u610f\u4e4b\u6781\uff0c\u54c8\u54c8\u4e00\u7b11\uff0c\u903c\u7d27\u4e00\u53e5\u9053\uff1a\u201c\u6211\u8fd9\u6b21\u51fa\u4f7f\uff0c\u4e8b\u5148\u4e5f\u5b66 \u4f1a\u4e86\u517b\u9a6c\u7684\u672c\u4e8b\uff0c\u5fc5\u8981\u4e4b\u65f6\uff0c\u4e5f\u51c6\u5907\u5728\u8d35\u56fd\u4e45\u7559\u5462\uff01\u201d \u3000\u3000\u4e5f\u5148\u5c34\u5c2c\u4e4b\u6781\uff0c\u8fde\u8fde\u5e72\u7b11\u9053\uff1a\u201c\u4e91\u5927\u4eba\u771f\u7231\u8bf4\u7b11\u8bdd\uff0c\u54c8\u54c8\u4e91\u5927\u4eba\u771f\u7231\u8bf4\u7b11\u8bdd\uff01\u201d\u54b3\u4e86\u4e00 \u58f0\uff0c\u637b\u987b\u8bf4\u9053\uff1a\u201c\u4e91\u5927\u4eba\u6b64\u6b21\u51fa\u4f7f\uff0c\u655d\u56fd\u6709\u5931\u8fdc\u8fce\uff0c\u8001\u592b\u5728\u6b64\u544a\u7f6a\u4e86\u3002\u4e91\u5927\u4eba\u8fdc\u6d89\u5173\u5c71\uff0c\u4e00 \u8def\u4e0a\u8f9b\u82e6\u4e86\uff0c\u8f9b\u82e6\u4e86\uff01\u201d\u4e5f\u5148\u8bf4\u6b64\u756a\u8bdd\uff0c\u4e00\u6765\u662f\u60f3\u626d\u8f6c\u8bdd\u9898\uff0c\u4e8c\u6765\u662f\u60f3\u4fa7\u9762\u8bd5\u63a2\u4ed6\u8def\u4e0a\u6709\u5426 \u51fa\u4e8b\u3002\u4e91\u91cd\u51b7\u51b7\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e5f\u6ca1\u4ec0\u4e48\uff0c\u53ea\u662f\u8e0f\u5165\u8d35\u56fd\u56fd\u5883\u4e4b\u540e\uff0c\u5076\u800c\u9047\u8fc7\u51e0\u4e2a\u5c0f\u8d3c\u3002\u201d\u4e5f \u5148\u5413\u4e86\u4e00\u8df3\uff0c\u968f\u5373\u60f3\u9053\uff1a\u201c\u82e5\u662f\u51e0\u4e2a\u5c0f\u8d3c\uff0c\u90a3\u5c31\u4e0d\u4f1a\u662f\u989d\u5409\u591a\u4ed6\u4eec\u4e86\u3002\u201d\u8fde\u5fd9\u8bf4\u9053\uff1a\u201c\u5728\u4ec0 \u4e48\u5730\u65b9\u9047\u7684\u8d3c\u4eba\uff1f\u4e91\u5927\u4eba\u8bb0\u5f97\u4e48\uff1f\u90a3\u4e9b\u5730\u65b9\u5b98\u6709\u4e8f\u804c\u5b88\uff0c\u5f85\u6211\u7acb\u523b\u5c06\u4ed6\u4eec\u64a4\u804c\u67e5\u529e\u3002\u201d\u4e91\u91cd \u7b11\u9053\uff1a\u201c\u4e0d\u5fc5\u4e86\uff0c\u53cd\u6b63\u6211\u4e5f\u6ca1\u6709\u4e1d\u6beb\u635f\u5931\uff0c\u6211\u79c1\u4eba\u8fd8\u6709\u4e00\u70b9\u4e0d\u6210\u656c\u610f\u7684\u793c\u7269\u8981\u5b5d\u656c\u592a\u5e08\u3002\u201d \u4e5f\u5148\u7709\u5f00\u773c\u7b11\uff0c\u9053\uff1a\u201c\u4e91\u5927\u4eba\u4f55\u7528\u8fd9\u6837\u5ba2\u6c14\u3002\u201d\u4e91\u91cd\u9053\uff1a\u201c\u8bf7\u592a\u5e08\u51c6\u6211\u7684\u968f\u4ece\u5c06\u8f66\u4e0a\u7684\u793c\u7269 \u62ff\u4e0a\u5385\u6765\u5427\u3002\u201d\u4e5f\u5148\u5fc3\u9053\uff1a\u201c\u6211\u6240\u6599\u4e0d\u5dee\uff0c\u8f66\u4e0a\u88c5\u7684\u679c\u7136\u662f\u793c\u7269\u3002\u8fd9\u4e9b\u7c97\u91cd\u7684\u793c\u7269\uff0c\u8c05\u4e5f\u4e0d \u662f\u4ec0\u4e48\u597d\u4e1c\u897f\u3002\u201d\u4f46\u8fd9\u5230\u5e95\u662f\u4e2d\u56fd\u4f7f\u81e3\u7684\u793c\u7269\uff0c\u81ea\u5df1\u6b63\u6101\u6b64\u4eba\u5014\u5f3a\uff0c\u96be\u4ee5\u5bf9\u4ed8\uff0c\u96be\u5f97\u4ed6\u7adf\u5148 \u5bf9\u81ea\u5df1\u8868\u793a\u656c\u610f\uff0c\u90a3\u81ea\u7136\u662f\u5927\u589e\u5149\u5f69\u3002\u56e0\u6b64\u4e5f\u5148\u5bf9\u793c\u7269\u7684\u8d35\u91cd\u4e0e\u5426\uff0c\u5012\u5728\u5176\u6b21\uff0c\u6ee1\u6000\u9ad8\u5174\u5730 \u4e00\u9762\u8c26\u8ba9\uff0c\u4e00\u9762\u53eb\u4eba\u95ea\u5f00\u4e00\u6761\u9053\u8def\uff0c\u8ba9\u4e91\u91cd\u7684\u4f8d\u4ece\u5c06\u793c\u7269\u625b\u4e0a\u5385\u6765\u3002 \u3000\u3000\u4e91\u91cd\u5fae\u5fae\u4e00\u7b11\uff0c\u4e5f\u5148\u653e\u773c\u770b\u65f6\uff0c\u53ea\u89c1\u4e91\u91cd\u7684\u56db\u4e2a\u968f\u4ece\uff0c\u625b\u7740\u4e24\u4e2a\u9ebb\u888b\uff0c\u8d70\u4e0a\u5385\u6765\u3002\u4e5f\u5148 \u8fd8\u4ee5\u4e3a\u91cc\u9762\u88c5\u7684\u662f\u4e2d\u56fd\u7684\u571f\u4ea7\uff0c\u6697\u7b11\u4e91\u91cd\u51fa\u624b\u5bd2\u9178\uff0c\u9ebb\u888b\u5728\u5730\u4e0a\u91cd\u91cd\u4e00\u987f\uff0c\u5ffd\u542c\u5f97\u201c\u54ce\u5440\u201d \u4e00\u58f0\uff0c\u5728\u91cc\u9762\u4f20\u4e86\u51fa\u6765\uff0c\u888b\u53e3\u4e00\u5f00\uff0c\u4e24\u4e2a\u88ab\u6346\u7f1a\u5f97\u50cf\u50bb\u5b50\u4e00\u6837\u7684\u4eba\u6eda\u5728\u5730\u4e0a\uff0c\u5176\u4e2d\u4e00\u4e2a\u8fd8\u8892 \u80f8\u9732\u80cc\uff0c\u80cc\u810a\u4e0a\u9732\u51fa\u4e86\u4e00\u4e2a\u8349\u4e66\u7684\u201c\u8d3c\u201d\u5b57\u3002\u4e91\u91cd\u7b11\u9053\uff1a\u201c\u5c31\u662f\u8fd9\u4e00\u70b9\u4e0d\u6210\u656c\u610f\u7684\u793c\u7269\uff0c\u8bf7 \u592a\u5e08\u7b11\u7eb3\uff01\u201d \u3000\u3000\u8fd9\u4e24\u4e2a\u4eba\u4e0d\u95ee\u53ef\u77e5\uff0c\u81ea\u662f\u88ab\u4fd8\u864f\u7684\u989d\u5409\u591a\u4e0e\u9ebb\u7ffc\u8d5e\uff0c\u4ed6\u4eec\u88ab\u56da\u5728\u9ebb\u888b\u4e4b\u4e2d\u591a\u65e5\uff0c\u5934\u660f\u8111 \u80c0\uff0c\u5ffd\u88ab\u89e3\u5f00\u7a74\u9053\uff0c\u9aa4\u89c1\u5149\u4eae\uff0c\u6025\u5fd9\u8df3\u8d77\uff0c\u7b2c\u4e00\u773c\u5c31\u77a7\u89c1\u4e5f\u5148\uff0c\u8fd8\u4ee5\u4e3a\u662f\u81ea\u5df1\u4eba\u89e3\u6551\u7684\uff0c\u4e0d \u7981\u72c2\u559c\u53eb\u9053\uff1a\u201c\u592a\u5e08\uff0d\uff0d\u201d \u3000\u3000\u4e5f\u5148\u9aa4\u5403\u4e00\u60ca\uff0c\u4f46\u4ed6\u4e43\u4e00\u4ee3\u5978\u96c4\uff0c\u77ac\u5373\u4e4b\u95f4\uff0c\u4fbf\u731c\u5230\u4e86\u8fd9\u662f\u600e\u4e48\u4e00\u56de\u4e8b\u60c5\uff0c\u9762\u8272\u4e00\u6c89\uff0c \u7acb\u523b\u559d\u9053\uff1a\u201c\u4f60\u4eec\u8fd9\u4e24\u4e2a\u5c0f\u8d3c\u5c45\u7136\u6562\u5192\u72af\u5929\u671d\u4f7f\u8005\uff0c\u6765\u4eba\u5440\uff0c\u5148\u62c9\u4e0b\u53bb\u6253\u4e09\u767e\u5927\u677f\uff0c\u518d\u6253\u8fdb \u5929\u7262\uff0c\u8ba9\u6211\u88c1\u5904\u3002\u201d\u989d\u5409\u591a\u3001\u9ebb\u7ffc\u8d5e\u5413\u5f97\u9b42\u98de\u9b44\u6563\uff0c\u53ea\u542c\u5f97\u540c\u4f34\u536b\u58eb\u8f70\u7136\u5927\u559d\uff0c\u5c06\u4ed6\u4eec\u7684\u58f0 \u97f3\u63a9\u76d6\u8fc7\u53bb\uff0c\u8fde\u62d6\u5e26\u62fd\u5730\u628a\u4ed6\u4eec\u62c9\u8fdb\u540e\u5802\u3002 \u3000\u3000\u4e91\u91cd\u53c8\u662f\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u592a\u5e08\u65e5\u7406\u4e07\u673a\uff0c\u503c\u4e0d\u5f97\u4e3a\u4e24\u4e2a\u5c0f\u8d3c\u8d39\u795e\uff0c\u6240\u4ee5\u6211\u6562\u4e8e\u8d8a\u4fce\u4ee3 \u25a1\uff0c\u5c06\u4ed6\u4eec\u64d2\u732e\u3002\u201d\u4e5f\u5148\u9762\u8272\u6da8\u5f97\u901a\u7ea2\uff0c\u9053\uff1a\u201c\u8fd9\u4e24\u4e2a\u5c0f\u8d3c\uff0c\u771f\u662f\u4e22\u4e86\u6211\u7684\u9762\u5b50\u3002\u54b3\u54b3\uff0c\u4e00 \u5b9a\u8981\u91cd\u91cd\u5904\u7f5a\uff0c\u91cd\u91cd\u5904\u7f5a\uff01\u201d\u4e91\u91cd\u4e00\u8a00\u4e0d\u53d1\uff0c\u53ea\u662f\u51b7\u51b7\u5730\u770b\u7740\u4ed6\u8ba9\u4ed6\u81ea\u8bf4\u81ea\u8bdd\u3002\u4e5f\u5148\u8d8a\u8bf4\u8d8a \u614c\uff0c\u987b\u77e5\u8fd9\u4e8c\u4eba\u662f\u4ed6\u5e10\u4e0b\u6570\u4e00\u6570\u4e8c\u7684\u6b66\u58eb\uff0c\u8fd8\u5e26\u6709\u4e94\u767e\u94c1\u9a91\uff0c\u5c1a\u6709\u6c99\u6d9b\u534f\u52a9\uff0c\u7adf\u7136\u7ed9\u4e91\u91cd\u8f7b \u63cf\u6de1\u5199\u5730\u5168\u90fd\u89e3\u51b3\uff0c\u8fd8\u6d3b\u6349\u4e86\u6765\uff0c\u4e5f\u5148\u600e\u5f97\u4e0d\u60ca\uff1f\u66f4\u517c\u4e91\u91cd\u770b\u7740\u4ed6\u7684\u90a3\u526f\u795e\u6c14\uff0c\u5c31\u50cf\u5ba1\u95ee\u4e00 \u822c\uff0c\u4e5f\u5148\u81ea\u8bf4\u81ea\u8bdd\uff0c\u8bf4\u5230\u540e\u6765\uff0c\u9762\u8272\u7531\u7ea2\u8f6c\u767d\uff0c\u7b80\u76f4\u4e0d\u77e5\u6240\u4e91\u3002 \u3000\u3000\u4e91\u91cd\u89c1\u4e5f\u5148\u7a98\u6001\u6bd5\u9732\uff0c\u5fc3\u4e2d\u6697\u7b11\u9053\uff1a\u201c\u4eca\u65e5\u5df2\u5f04\u5f97\u4ed6\u591f\u53d7\u7684\u4e86\uff0c\u4e14\u7f62\uff0c\u4e0d\u5fc5\u518d\u903c\u4ed6\u4e86\uff0c \u4e5f\u514d\u5f97\u4ed6\u8001\u7f9e\u6210\u6012\uff0c\u53cd\u800c\u6a2a\u751f\u679d\u8282\uff0c\u8bef\u4e86\u548c\u8c08\u3002\u201d\u4e8e\u662f\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e00\u56fd\u4e4b\u5185\u826f\u83a0\u4e0d \u9f50\uff0c\u6709\u51e0\u4e2a\u5c0f\u8d3c\uff0c\u4ea6\u662f\u5bfb\u5e38\u4e4b\u4e8b\uff0c\u592a\u5e08\u4e0d\u5fc5\u4ecb\u6000\uff0c\u54b1\u4eec\u8fd8\u662f\u5546\u8c08\u548c\u7ea6\u5427\u3002\u201d\u4e5f\u5148\u677e\u4e86\u53e3\u6c14\uff0c \u9053\uff1a\u201c\u4e91\u5927\u4eba\u8bf4\u7684\u662f\u3002\u201d\u4e91\u91cd\u53d6\u51fa\u4e00\u672c\u5c0f\u6298\uff0c\u9012\u8fc7\u53bb\u9053\uff1a\u201c\u8fd9\u662f\u6211\u4eec\u7684\u548c\u7ea6\u8349\u6848\uff0c\u8bf7\u592a\u5e08\u8fc7 \u76ee\u3002\u201d\u90a3\u662f\u4e8e\u8c26\u62df\u5b9a\u7684\u548c\u7ea6\uff0c\u4e3b\u8981\u5185\u5bb9\u5f88\u7b80\u5355\uff0c\u65e0\u975e\u5404\u4fdd\u7586\u571f\uff0c\u5e73\u7b49\u76f8\u5f85\uff0c\u53cc\u65b9\u6c38\u4e0d\u518d\u52a8\u5e72 \u6208\u4e4b\u7c7b\u3002\u9644\u6b3e\u662f\u7559\u5728\u74e6\u523a\u7684\u4e2d\u56fd\u201c\u592a\u4e0a\u7687\u201d\uff08\u5373\u88ab\u4fd8\u7684\u660e\u82f1\u5b97\u7948\u9547\uff09\uff0c\u5fc5\u987b\u7acb\u5373\u9001\u56de\u3002\u4e5f\u5148 \u7565\u7565\u4e00\u770b\u6c89\u541f\u4e0d\u8bed\u3002\u4ed6\u672c\u6765\u53e6\u8ba2\u6709\u4e00\u4efd\u8349\u6848\uff0c\u4eff\u4ee5\u524d\u5b8b\u671d\u548c\u8fbd\u91d1\u4e24\u56fd\u6240\u8ba2\u7684\u548c\u7ea6\u524d\u4f8b\uff0c\u8981\u660e \u671d\u56fd\u541b\u81ea\u5c45\u4e8e\u5c0f\u8f88\uff0c\u4e0e\u74e6\u523a\u7f14\u4e3a\u201c\u53d4\u4f84\u4e4b\u56fd\u201d\uff0c\u5e76\u8981\u6bcf\u5e74\u7f34\u7eb3\u4e09\u767e\u4e07\u4e24\u94f6\u5b50\uff0c\u4e94\u4e07\u5339\u7ef8\u7f0e\uff0c \u603b\u4e4b\u60f3\u5360\u4e2d\u4e2d\u7684\u4fbf\u5b9c\u3002\u5374\u60f3\u4e0d\u5230\u5f04\u5de7\u53cd\u62d9\uff0c\u4ed6\u8d39\u5c3d\u673a\u8c0b\uff0c\u539f\u6b32\u628a\u660e\u671d\u7684\u4f7f\u81e3\u73a9\u5f04\u4e8e\u80a1\u638c\u4e4b \u4e0a\uff0c\u5374\u53cd\u800c\u88ab\u660e\u671d\u7684\u4f7f\u81e3\u62ff\u7740\u4e86\u4ed6\u7684\u628a\u67c4\u3002\u8fd9\u65f6\u88ab\u4e91\u91cd\u7684\u5a01\u4eea\u9547\u6151\uff0c\u4e5f\u5148\u6709\u5982\u88ab\u51b2\u8d25\u4e86\u7684\u516c \u9e21\u4e00\u6837\uff0c\u81ea\u5df1\u6240\u62df\u8ba2\u7684\u8349\u6848\uff0c\u653e\u5728\u888b\u4e2d\uff0c\u7adf\u4e0d\u6562\u6478\u51fa\u6765\u3002\u4e91\u91cd\u6b63\u5bb9\u8bf4\u9053\uff1a\u201c\u4e2d\u56fd\u662f\u793c\u4eea\u4e4b \u90a6\uff0c\u800c\u4eca\u610f\u6b32\u4e0e\u8d35\u56fd\u7f14\u4e3a\u5144\u5f1f\u4e4b\u56fd\uff0c\u4ee5\u5f80\u4e4b\u4e8b\uff0c\u4e00\u6982\u4e0d\u548e\uff0c\u8fd9\u548c\u7ea6\u4e24\u4e0d\u5403\u4e8f\u3002\u82e5\u592a\u5e08\u5802\u6709\u4e09 \u5fc3\u4e24\u610f\uff0c\u4ee5\u4e3a\u4e2d\u56fd\u53ef\u6b3a\uff0c\u90a3\u4e48\u6211\u4eec\u8fb9\u5173\u4ea6\u6709\u5341\u4e07\u96c4\u5175\uff0c\u4e5f\u53ef\u4ee5\u548c\u592a\u5e08\u5468\u65cb\u4e00\u4e0b\u3002\u201d\u4e91\u91cd\u7684\u8bdd \u8bf4\u7684\u6709\u67d4\u6709\u521a\uff0c\u6781\u4e3a\u5f97\u4f53\u3002\u4e5f\u5148\u4e0a\u6b21\u4fb5\u5165\u4e2d\u56fd\uff0c\u867d\u7136\u5728\u571f\u6728\u5821\u5927\u83b7\u5168\u80dc\uff0c\u4fd8\u864f\u4e86\u660e\u671d\u7687\u5e1d\uff0c \u4f46\u63a5\u7740\u5c31\u5728\u5317\u4eac\u5403\u4e86\u4e00\u4e2a\u5927\u8d25\u4ed7\uff0c\u88ab\u8d76\u51fa\u96c1\u95e8\u5173\uff0c\u8bf4\u8d77\u6765\u8fd9\u573a\u6218\u4e8b\uff0c\u4e92\u6709\u80dc\u8d25\uff0c\u8c01\u90fd\u4e0d\u80fd\u4ee5 \u6218\u80dc\u56fd\u81ea\u5c45\u3002\u660e\u671d\u63d0\u51fa\u7684\u548c\u7ea6\u5b9e\u662f\u516c\u5141\u4e4b\u6781\u3002\u4e5f\u5148\u76db\u6c14\u5df2\u6298\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u8fd9\u4f7f\u81e3\u96be\u4ee5\u5bf9\u4ed8 \u5f97\u6781\uff0c\u7b80\u76f4\u6bd4\u5f53\u5e74\u4ed6\u7684\u7237\u7237\u8fd8\u8981\u5389\u5bb3\uff0c\u518d\u62d6\u5ef6\u4e5f\u8ba8\u4e0d\u4e86\u4fbf\u5b9c\u3002\u201d\u66f4\u517c\u53c8\u8981\u987e\u8651\u5230\u963f\u523a\u7684\u5185 \u5fe7\uff0c\u4e8e\u662f\u53ea\u597d\u63a5\u8fc7\u4e91\u91cd\u7684\u8349\u6848\uff0c\u7ea6\u597d\u5f85\u74e6\u523a\u56fd\u738b\u8fc7\u76ee\u4e4b\u540e\uff0c\u518d\u5b9a\u671f\u5546\u8c08\u3002 \u3000\u3000\u548c\u8bae\u8c08\u5f97\u751a\u4e3a\u987a\u5229\uff0c\u4e0d\u8fc7\u5341\u5929\uff0c\u53cc\u65b9\u90fd\u5df2\u540c\u610f\u7b7e\u5b57\uff0c\u5c31\u4ee5\u4e2d\u56fd\u6240\u63d0\u51fa\u7684\u548c\u7ea6\u4e3a\u4f9d\u636e\uff0c\u53ea \u4e0d\u8fc7\u6539\u4e86\u4e9b\u4e2a\u522b\u7684\u5b57\u53e5\u3002\u53cc\u65b9\u8c08\u59a5\uff1a\u5728\u548c\u7ea6\u7b7e\u8ba2\u4e4b\u540e\u7684\u7b2c\u4e8c\u65e5\uff0c\u5c31\u7531\u660e\u671d\u4f7f\u81e3\u8fce\u63a5\u4ed6\u4eec\u7684 \u201c\u592a\u4e0a\u7687\u201d\u56de\u56fd\uff0c\u8fd9\u65f6\u88ab\u4fd8\u7684\u7687\u5e1d\u7948\u9547\u4ea6\u5df2\u8fc1\u51fa\u56da\u623f\uff0c\u88ab\u5b89\u7f6e\u5728\u74e6\u523a\u7687\u5bab\u4e4b\u4e2d\uff0c\u5f85\u4ee5\u56fd\u541b\u4e4b \u793c\u4e86\u3002\u5728\u548c\u8bae\u5546\u8c08\u7684\u671f\u95f4\u4e2d\uff0c\u5f20\u4e39\u67ab\u66fe\u6d3e\u4eba\u9001\u4fe1\u7ed9\u4e91\u91cd\uff0c\u9080\u4e91\u91cd\u5230\u4ed6\u5bb6\u4e2d\u4e00\u53d9\u3002\u4e91\u91cd\u8bb0\u7740\u4e16 \u4ec7\uff0c\u867d\u7136\u5bf9\u5f20\u4e39\u67ab\u5df2\u65e0\u6068\u610f\uff0c\u4f46\u4ea6\u4e0d\u613f\u524d\u5f80\u3002\u5f20\u4e39\u67ab\u4e5f\u6ca1\u6709\u6765\u770b\u4ed6\u3002 \u3000\u3000\u8f6c\u77ac\u4fbf\u5230\u4e86\u660e\u671d\u4f7f\u81e3\u79bb\u5f00\u74e6\u523a\u7684\u524d\u5915\u3002\u8fd9\u4e00\u665a\u4e91\u91cd\u5174\u594b\u975e\u5e38\uff0c\u5728\u5ba2\u6808\u4e2d\u8e31\u6765\u8e31\u53bb\uff0c\u7761\u4e0d \u7740\u89c9\u3002\u5728\u53e6\u4e00\u5904\u5730\u65b9\uff0c\u4e5f\u6709\u4e24\u4e2a\u4eba\u5174\u594b\u975e\u5e38\uff0c\u7761\u4e0d\u7740\u89c9\u3002\u8fd9\u4e24\u4e2a\u4eba\u4fbf\u662f\u5f20\u4e39\u67ab\u548c\u4ed6\u7684\u7236\u4eb2\uff0c \u4e0d\u8fc7\u4ed6\u4eec\u7236\u5b50\u7684\u5fc3\u60c5\u53c8\u5404\u6709\u4e0d\u540c\u3002\u5f20\u5b97\u5468\u662f\u5728\u5174\u594b\u4e4b\u4e2d\u53c8\u5e26\u6709\u6781\u6df1\u6c89\u7684\u60b2\u51c9\uff0c\u8fd9\u65f6\uff0c\u6b63\u5728\u82b1 \u56ed\u91cc\u501a\u7740\u680f\u6746\u548c\u5f20\u4e39\u67ab\u8bf4\u8bdd\u3002 \u3000\u3000\u8fd9\u51e0\u65e5\u6765\uff0c\u5f20\u5b97\u5468\u4f3c\u67af\u69c1\u7684\u6811\u6728\u4e00\u6837\uff0c\u6625\u98ce\u867d\u5df2\u5439\u62c2\u5927\u5730\u4f46\u67af\u6811\u4e0a\u5374\u6ca1\u6709\u4e00\u679d\u65b0\u82bd\uff0c\u4e00 \u7247\u7eff\u53f6\u3002\u4ed6\u628a\u81ea\u5df1\u5173\u95ed\u5728\u4e66\u5fc3\u4e4b\u5185\uff0c\u8fde\u513f\u5b50\u4e5f\u5f88\u5c11\u8bf4\u8bdd\uff0c\u5bf9\u660e\u671d\u4f7f\u8005\u5230\u6765\u7684\u6d88\u606f\uff0c\u4ed6\u4e5f\u7edd\u53e3 \u4e0d\u63d0\uff0c\u8fd9\u53cd\u5e38\u7684\u6c89\u9ed8\uff0c\u5bb6\u4e2d\u7684\u4eba\u90fd\u4e3a\u4ed6\u62c5\u5fc3\uff0c\u5f20\u4e39\u67ab\u672c\u6765\u60f3\u53bb\u62dc\u4f1a\u4e91\u91cd\uff0c\u4e5f\u4e3a\u4e86\u7236\u4eb2\uff0c\u4e0d\u6562 \u79bb\u5f00\u5bb6\u95e8\u534a\u6b65\u3002 \u3000\u3000\u8fd9\u4e00\u665a\uff0c\u5f20\u5b97\u5468\u7a81\u7136\u5c06\u513f\u5b50\u5524\u6765\uff0c\u7236\u5b50\u4fe9\u5728\u82b1\u56ed\u4e2d\u5f98\u5f8a\u6f2b\u6b65\uff0c\u4e45\u4e45\u4e0d\u8bed\uff0c\u770b\u770b\u6708\u4eae\u5df2\u5347 \u81f3\u4e2d\u5929\uff0c\u5f20\u5b97\u5468\u53f9\u4e86\u53e3\u6c14\u541f\u9053\uff1a\u201c\u4eca\u591c\u56ed\u4e2d\u6708\uff0c\u660e\u5e74\u53ea\u72ec\u770b\u3002\u201d\u659c\u501a\u680f\u6746\uff0c\u9065\u671b\u4e91\u6d77\uff0c\u4f3c\u9996 \u60f3\u900f\u8fc7\u4e91\u6d77\uff0c\u770b\u5230\u4ed6\u68a6\u4e2d\u6e38\u904d\u7684\u6c5f\u5357\u3002\u5f20\u4e39\u67ab\u6cea\u54bd\u5fc3\u9178\uff0c\u53eb\u9053\uff1a\u201c\u7239\u7239\u3002\u201d\u5f20\u5b97\u5468\u51c4\u7136\u4e00 \u7b11\uff0c\u5ffd\u7136\u95ee\u9053\uff1a\u201c\u542c\u8bf4\u548c\u7ea6\u5df2\u7b7e\uff0c\u660e\u671d\u7684\u4f7f\u8005\u660e\u5929\u4fbf\u8981\u56de\u56fd\u4e86\uff0c\u662f\u4e48\uff1f\u201d\u8fd9\u8fd8\u662f\u7b2c\u4e00\u6b21\u95ee\u53ca \u660e\u671d\u7684\u4f7f\u8005\u3002\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u662f\u7684\u3002\u201d\u5f20\u5b97\u5468\u9053\uff1a\u201c\u8fd9\u4f4d\u4f7f\u81e3\u4e5f\u662f\u59d3\u4e91\u7684\uff0c\u662f\u4e48\uff1f\u201d\u5f20\u4e39\u67ab \u9053\uff1a\u201c\u662f\u7684\u3002\u201d\u4ed6\u5fc3\u4e2d\u5df2\u60f3\u8fc7\u5343\u904d\u4e07\u904d\uff0c\u4e91\u91cd\u65e2\u4e0d\u613f\u89c1\u4ed6\u7236\u4eb2\uff0c\u4ed6\u4e5f\u4e0d\u6562\u5c06\u4e91\u91cd\u7684\u8eab\u4efd\u544a\u8bc9 \u8001\u7236\u3002\u5f20\u5b97\u5468\u9053\uff1a\u201c\u8fd9\u4f4d\u4f7f\u81e3\u4e0d\u8fb1\u4f7f\u547d\uff0c\u6bd4\u5f53\u5e74\u7684\u4e91\u9756\u8fd8\u5f3a\uff01\u201d\u4ed6\u8fd8\u672a\u77e5\u9053\u8fd9\u4f4d\u4f7f\u81e3\u5c31\u662f\u4e91 \u9756\u7684\u5b59\u5b50\u3002\u5f20\u4e39\u67ab\u542b\u7b11\u70b9\u4e86\u70b9\u5934\uff0c\u5f20\u5b97\u5468\u5ffd\u9053\uff1a\u201c\u67ab\u513f\uff0c\u90a3\u4e48\u4f60\u660e\u5929\u4e5f\u8be5\u8d70\u4e86\uff01\u201d \u3000\u3000\u5f20\u4e39\u67ab\u5fc3\u4e2d\u4e00\u9707\uff0c\u8fd9\u613f\u671b\u4ed6\u5df2\u60f3\u4e86\u591a\u5e74\uff0c\u4f46\u800c\u4eca\u4ece\u4ed6\u7684\u7236\u4eb2\u53e3\u4e2d\u8bf4\u51fa\u6765\uff0c\u4ed6\u7684\u5fc3\u5934\u5374\u6709 \u4e00\u79cd\u8bf4\u4e0d\u51fa\u7684\u6ecb\u5473\uff0c\u4ed6\u77e5\u9053\u5f97\u5f88\u6e05\u695a\uff0c\u82e5\u7136\u81ea\u5df1\u660e\u5929\u4e00\u8d70\uff0c\u90a3\u5c31\u662f\u548c\u7236\u4eb2\u6c38\u65e0\u518d\u89c1\u4e4b\u671f\u4e86\u3002 \u751f\u79bb\u6b7b\u522b\uff0c\u6614\u4eba\u6240\u60b2\uff0c\u4f55\u51b5\u662f\u81ea\u5df1\u7684\u751f\u8eab\u8001\u7236\uff01\u5f20\u4e39\u67ab\u6291\u4f4f\u4e86\u5fc3\u5934\u7684\u98a4\u52a8\uff0c\u660e\u77e5\u7236\u4eb2\u4e0d\u4f1a\u7b54 \u5e94\uff0c\u4ecd\u7136\u95ee\u9053\uff1a\u201c\u7239\uff0c\u90a3\u4f60\u5462\uff1f\u201d\u5f20\u5b97\u5468\u6210\u8272\u4e00\u6c89\uff0c\u5ffd\u800c\u53c8\u7b11\u9053\uff1a\u201c\u4f60\u7684\u4e1c\u897f\u6211\u90fd\u5df2\u66ff\u4f60\u6536 \u62fe\u597d\u4e86\uff0c\u8fd9\u662f\u6700\u540e\u4e00\u6b21\u7167\u6599\u4f60\u4e86\u3002\u201d\u5f20\u4e39\u67ab\u5fc3\u60c5\u6fc0\u52a8\uff0c\u51b2\u53e3\u8bf4\u9053\uff1a\u201c\u7239\uff0c\u4f60\u4e0d\u8d70\u90a3\u6211\u4e5f\u7559\u5728 \u8fd9\u513f\u4f34\u4f60\u3002\u201d\u5f20\u5b97\u5468\u67d4\u58f0\u8bf4\u9053\uff1a\u201c\u4e0d\u4f60\u8981\u8d70\uff01\u4f60\u5e74\u7eaa\u8fd8\u8f7b\u5450\u3002\u6fb9\u53f0\u5c06\u519b\u548c\u4f60\u4e00\u540c\u8d70\uff0c\u6211\u5df2\u7ecf \u544a\u8bc9\u4ed6\u4e86\u3002\u201d \u3000\u3000\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u6fb9\u53f0\u5c06\u519b\u4e5f\u8d70\uff1f\u2026\u2026\u201d\u4e0b\u9762\u7684\u4e00\u540d\u201c\u90a3\u4e48\u4f60\u5c82\u4e0d\u662f\u66f4\u5b64\u5355\u4e86\uff1f\u201d\u8bf4\u4e0d\u51fa \u6765\uff0c\u5f20\u5b97\u5468\u5fae\u7b11\u9053\uff1a\u201c\u662f\u7684\uff0c\u6fb9\u53f0\u5c06\u519b\uff0d\uff0d\u201d\u5ffd\u89c1\u9762\u524d\u4eba\u5f71\u4e00\u95ea\uff0c\u6fb9\u53f0\u706d\u660e\u5954\u5230\u9762\u524d\u3002\u5f20\u5b97 \u5468\u7b11\u5bb9\u672a\u655b\uff0c\u6b63\u60f3\u8bf4\u9053\uff1a\u201c\u8bdd\u8bf4\u66f9\u64cd\uff0c\u66f9\u64cd\u5c31\u5230\u3002\u201d\u53ea\u542c\u5f97\u6fb9\u53f0\u706d\u660e\u6c14\u5401\u5401\uff0c\u98a4\u58f0\u8bf4\u9053\uff1a \u201c\u4e3b\u516c\uff0c\u4e0d\u597d\u4e86\uff01\u201d\u5f20\u5b97\u5468\u4ece\u6765\u672a\u89c1\u8fc7\u6fb9\u53f0\u706d\u660e\u8fd9\u6837\u614c\u5f20\uff0c\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\u60c5\uff1f\u201d\u6fb9\u53f0\u706d\u660e \u9053\uff1a\u201c\u54b1\u4eec\u7684\u5e9c\u90b8\u5df2\u88ab\u4eba\u5305\u56f4\u4e86\uff01\u201d\u5f20\u4e39\u67ab\u51dd\u795e\u4e00\u542c\uff0c\u679c\u7136\u542c\u51fa\u4e86\u5916\u9762\u7684\u4eba\u58f0\u3002\u5f20\u5b97\u5468\u8fd8\u662f \u795e\u8272\u5982\u5e38\u9053\uff1a\u201c\u90a3\u4e48\u54b1\u4eec\u5c31\u51fa\u53bb\u77a7\u77a7\u3002\u201d \u3000\u3000\u5f20\u4e39\u67ab\u4e0e\u6fb9\u53f0\u706d\u660e\u8df3\u4e0a\u5899\u5934\uff0c\u53ea\u89c1\u5e9c\u90b8\u56db\u5468\u56f4\u4e86\u51e0\u5c42\uff0c\u5bf9\u7740\u6b63\u95e8\u8fd8\u6709\u4e00\u5c0a\u7ea2\u8863\u5927\u70ae\uff01\u8499 \u53e4\u4eba\u6700\u5148\u628a\u706b\u836f\u8fd0\u7528\u5230\u6218\u4e89\u4e0a\uff0c\u5f53\u5e74\u6a2a\u626b\u6b27\u6d32\uff0c\u5c31\u4ed7\u7740\u706b\u5668\u4e4b\u529b\u4e0d\u5c0f\uff0c\u60f3\u4e0d\u5230\u800c\u4eca\u7adf\u7528\u6765\u5bf9 \u4ed8\u5f20\u5bb6\u3002\u5728\u7ea2\u8863\u5927\u70ae\u7684\u540e\u9762\uff0c\u4e00\u6392\u5e76\u5217\u7740\u4e09\u9a91\u5065\u9a6c\uff0c\u90a3\u662f\u989d\u5409\u591a\u9ebb\u7ffc\u8d5e\u548c\u9752\u8c37\u6cd5\u5e08\u7684\u5e08\u5144\u767d \u5c71\u6cd5\u5e08\u3002 \u3000\u3000\u8499\u53e4\u5175\u70b9\u7740\u677e\u679d\u706b\u628a\uff0c\u4e00\u89c1\u5f20\u4e39\u67ab\u7ad9\u4e86\u51fa\u6765\uff0c\u8f70\u5929\u4ef7\u7684\u5927\u58f0\u5406\u559d\uff0c\u5f20\u4e39\u67ab\u529b\u6301\u9547\u5b9a\u5411\u4e0b \u9762\u53d1\u8bdd\u9053\uff1a\u201c\u4f60\u4eec\u6765\u505a\u4ec0\u4e48\uff1f\u201d\u4ed6\u8fd0\u6c14\u4f20\u58f0\uff0c\u6709\u5982\u9f99\u541f\u864e\u5578\uff0c\u5c06\u8499\u53e4\u5175\u5608\u5608\u6742\u6742\u7684\u58f0\u97f3\u90fd\u538b \u4e86\u4e0b\u53bb\u3002\u989d\u5409\u591a\u62cd\u9a6c\u4e0a\u524d\uff0c\u5bf9\u7740\u5899\u5934\uff0c\u5927\u58f0\u7b11\u9053\uff1a\u201c\u5f20\u4e39\u67ab\uff0c\u4eca\u65e5\u770b\u4f60\u8fd8\u6709\u4ec0\u4e48\u624b\u6bb5\uff1f\u4f60\u8981 \u6b7b\u8fd8\u662f\u8981\u751f\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u600e\u4e48\u6837\uff1f\u201d\u989d\u5409\u591a\u9053\uff1a\u201c\u82e5\u7136\u8981\u751f\uff0c\u4f60\u5c31\u81ea\u5df1\u52a8\u624b\uff0c\u628a\u5bb6\u4e2d\u7684 \u4eba\u5168\u90fd\u7f1a\u4e86\u3002\u53ea\u7559\u4e0b\u4f60\u7684\u7236\u4eb2\u53ef\u4ee5\u4e0d\u7f1a\uff0c\u7136\u540e\u6253\u5f00\u5927\u95e8\uff0c\u8ba9\u6211\u4eec\u5c06\u4f60\u4eec\u7236\u5b50\u5e26\u53bb\u4ea4\u7ed9\u592a\u5e08\uff0c \u7531\u592a\u5e08\u53d1\u843d\u3002\u201d\u5f20\u4e39\u67ab\u201c\u54fc\u201d\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u82e5\u7136\u4e0d\u5462\uff1f\u201d\u989d\u5409\u591a\u9053\uff1a\u201c\u6211\u7559\u70b9\u65f6\u95f4\uff0c\u8ba9\u4f60\u4eec \u60f3\u4e2a\u6e05\u695a\u3002\u8fd9\u5c0a\u5927\u70ae\uff0c\u4f60\u8be5\u770b\u89c1\u4e86\u5427\u3002\u4f60\u4efb\u6b66\u529f\u518d\u5f3a\u5341\u500d\u4e5f\u96be\u62b5\u6321\u3002\u9650\u4f60\u4eec\u4e94\u66f4\u7b54\u590d\uff0c\u82e5\u7136 \u6562\u9053\u534a\u4e2a\u4e0d\u5b57\uff0c\u8fd8\u60f3\u62b5\u6297\u7684\u8bdd\uff0c\u90a3\u4e48\u5bf9\u4e0d\u4f4f\uff0c\u5929\u4e00\u4eae\u6211\u5c31\u5411\u4f60\u4eec\u5f00\u70ae\uff01\u201d \u3000\u3000\u5f20\u5b97\u5468\u9053\uff1a\u201c\u67ab\u513f\uff0c\u4e0b\u6765\u3002\u201d\u5f20\u4e39\u67ab\u548c\u6fb9\u53f0\u706d\u660e\u8d70\u5230\u5f20\u5b97\u5468\u9762\u524d\uff0c\u5f20\u5b97\u5468\u9053\uff1a\u201c\u770b\u6765\u4e5f \u5148\u8fd9\u25a1\u975e\u5f97\u6211\u800c\u4e0d\u7518\u5fc3\uff0c\u5c31\u7531\u6211\u8ddf\u4ed6\u4eec\u8d70\u5427\uff01\u4f60\u548c\u6fb9\u53f0\u5c06\u519b\u4e00\u8eab\u6b66\u529f\uff0c\u76f8\u673a\u53ef\u4ee5\u9003\u8d70\uff01\u201d\u5f20 \u4e39\u67ab\u9053\uff1a\u201c\u4e0d\u80fd\uff01\u6211\u4eec\u7edd\u4e0d\u80fd\u8ba9\u4f60\u53d7\u4e5f\u5148\u4e4b\u8fb1\uff01\u201d\u5f20\u5b97\u5468\u60f3\u4e86\u4e00\u60f3\uff0c\u5ffd\u800c\u6717\u58f0\u7b11\u9053\uff1a\u201c\u597d\u5fd7 \u6c14\uff0c\u597d\u5fd7\u6c14\uff01\u54b1\u4eec\u4e24\u4e09\u4ee3\u6765\uff0c\u5728\u74e6\u523a\u5c48\u8fb1\u6c42\u751f\uff0c\u6c14\u4e5f\u53d7\u591f\u4e86\u3002\u800c\u4eca\u4e2d\u56fd\u5df2\u5f3a\uff0c\u662f\u4e0d\u80fd\u518d\u53d7\u4ed6 \u7684\u4fae\u8fb1\u3002\u597d\u5427\uff0c\u90a3\u5c31\u8ba9\u6211\u548c\u5bb6\u4eba\u6b7b\u5728\u8fd9\u513f\uff0c\u4f60\u4eec\u4ece\u540e\u95e8\u6740\u51fa\uff01\u201d\u5f20\u4e39\u67ab\u65a9\u9489\u622a\u94c1\u5730\u9053\uff1a\u201c\u4e0d \u80fd\uff01\u201d\u6fb9\u53f0\u706d\u660e\u4e5f\u9053\uff1a\u201c\u8981\u6b7b\u6211\u4e5f\u548c\u4e3b\u516c\u6b7b\u5728\u4e00\u5904\u3002\u201d\u5f20\u5b97\u5468\u542b\u6cea\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u90fd\u662f\u6211\u7684\u597d \u513f\u5b50\u3001\u597d\u90e8\u4e0b\uff0c\u5440\uff0c\u53ea\u662f\u6211\u7d2f\u4e86\u4f60\u4eec\u4e86\u3002\u201d\u5f20\u5b97\u5468\u60f3\u8d77\u4ed6\u548c\u4ed6\u7684\u7236\u4eb2\u4e24\u4ee3\uff0c\u4e3a\u4e86\u4e00\u5ff5\u4e4b\u5dee\uff0c \u60f3\u501f\u74e6\u523a\u7684\u5175\u529b\u4e0e\u660e\u671d\u518d\u4e89\u593a\u6c5f\u5c71\uff0c\u4e0d\u60dc\u5728\u74e6\u523a\u4e3a\u5b98\uff0c\u66ff\u74e6\u523a\u6574\u519b\u7ecf\u6b66\uff0c\u8d39\u4e86\u591a\u5c11\u5fc3\u529b\uff0c\u628a \u74e6\u523a\u53d8\u6210\u5f3a\u56fd\uff0c\u4e0d\u6599\u5230\u5934\u6765\u53cd\u81ea\u98df\u5176\u679c\uff0c\u4e0d\u4f46\u81ea\u5df1\u7684\u56fd\u5bb6\u51e0\u4e4e\u88ab\u74e6\u523a\u6240\u706d\uff0c\u800c\u4eca\u8fde\u81ea\u5df1\u4e00 \u5bb6\uff0c\u4e5f\u8981\u6bc1\u5728\u4e5f\u5148\u7684\u70ae\u706b\u4e4b\u4e0b\uff01 \u3000\u3000\u5916\u9762\u53c8\u4f20\u6765\u4e86\u989d\u5409\u591a\u7684\u53eb\u58f0\uff1a\u201c\u60f3\u597d\u6ca1\u6709\uff1f\u6700\u8fdf\u5929\u4eae\u6211\u4eec\u5c31\u5f00\u70ae\u4e86\uff01\u201d\u5f20\u4e39\u67ab\u6789\u6709\u4e00\u809a \u76ae\u667a\u8ba1\u8fd9\u65f6\u4e5f\u60f3\u4e0d\u51fa\u529e\u6cd5\u5bf9\u4ed8\uff0c\u770b\u7740\u7236\u4eb2\u90a3\u60b2\u6124\u7684\u795e\u60c5\uff0c\u5fc3\u4e2d\u65e0\u9650\u7126\u6025\uff01 \u3000\u3000\u8fd9\u4e2a\u65f6\u5019\uff0c\u5728\u53e6\u4e00\u5904\u5730\u65b9\uff0c\u4e5f\u6709\u4e00\u4e2a\u7126\u6025\u975e\u5e38\uff0c\u8fd9\u4e2a\u4eba\u5374\u662f\u4e5f\u5148\u7684\u5973\u513f\u8131\u4e0d\u82b1\u3002 \u3000\u3000\u8131\u4e0d\u82b1\u81ea\u7136\u77e5\u9053\u548c\u7ea6\u5df2\u7ecf\u7b7e\u4e86\u7684\u6d88\u606f\uff0c\u77e5\u9053\u660e\u671d\u7684\u4f7f\u81e3\u660e\u5929\u4e00\u65e9\u4fbf\u8981\u79bb\u5f00\uff0c\u4e5f\u6599\u5230\u5f20\u4e39 \u67ab\u5fc5\u7136\u4f1a\u8ddf\u968f\u660e\u671d\u7684\u4f7f\u81e3\u56de\u56fd\uff0c\u5fc3\u4e2d\u60b2\u82e6\uff0c\u6101\u7709\u4e0d\u5c55\uff0c\u5979\u7236\u4eb2\u4e5f\u770b\u4e86\u51fa\u6765\u3002\u8fd9\u65e5\u665a\u95f4\uff0c\u4e5f\u5148 \u559d\u4e86\u51e0\u676f\u9152\uff0c\u610f\u5174\u751a\u6d53\uff0c\u5bf9\u5973\u513f\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u5fc5\u4f24\u5fc3\uff0c\u6211\u770b\u5f20\u4e39\u67ab\u660e\u5929\u672a\u5fc5\u4f1a\u8d70\uff0c\u6211\u6709\u6cd5\u5b50 \u5c06\u4ed6\u5f04\u56de\u6765\u3002\u6211\u53ea\u6709\u4f60\u4e00\u4e2a\u5973\u513f\uff0c\u4f60\u8981\u5929\u4e0a\u7684\u6708\u4eae\u6211\u4e5f\u4f1a\u7ed9\u4f60\u62ff\u4e0b\u6765\u3002\u82b1\u513f\uff0c\u4f60\u77a7\u7239\u591a\u75bc \u4f60\uff01\u201d\u8131\u4e0d\u82b1\u53c8\u60ca\u53c8\u559c\uff0c\u518d\u95ee\u7236\u4eb2\u4e4b\u65f6\uff0c\u4e5f\u5148\u5374\u53ea\u987e\u559d\u9152\u4e0d\u518d\u8bf4\u4e86\u3002 \u3000\u3000\u8fd9\u665a\uff0c\u8131\u4e0d\u82b1\u6ee1\u6000\u5fc3\u4e8b\uff0c\u4e0d\u77e5\u7236\u4eb2\u5f04\u7684\u662f\u751a\u7384\u865a\uff0c\u5348\u591c\u65f6\u5206\uff0c\u5ffd\u542c\u5f97\u5916\u9762\u5ba2\u5385\u6709\u4eba\u8bf4 \u8bdd\uff0c\u8131\u4e0d\u82b1\u5fcd\u4e0d\u4f4f\u6084\u6084\u8d77\u6765\uff0c\u8eb2\u5728\u5c4f\u98ce\u540e\u9762\u5077\u542c\u3002 \u3000\u3000\u5ba2\u5385\u5185\u6709\u4e24\u4e2a\u4eba\uff0c\u4e00\u4e2a\u662f\u5979\u7684\u7236\u4eb2\u4e5f\u5148\uff0c\u53e6\u4e00\u4e2a\u5219\u662f\u4ed6\u4eec\u592a\u5e08\u5e9c\u7684\u603b\u7ba1\u7a9d\u624e\u5408\u3002\u8131\u4e0d\u82b1 \u5c4f\u606f\u547c\u5438\uff0c\u51dd\u795e\u9759\u542c\u3002\u53ea\u542c\u5f97\u7236\u4eb2\u95ee\u9053\uff1a\u201c\u660e\u671d\u7684\u4f7f\u81e3\u5929\u4e00\u4eae\u5c31\u51fa\u57ce\uff0c\u54b1\u4eec\u7684\u793c\u7269\u9f50\u5907 \u4e48\uff1f\u201d\u7a9d\u624e\u5408\u9053\uff1a\u201c\u90fd\u9f50\u5907\u4e86\u3002\u201d\u4e5f\u5148\u9053\uff1a\u201c\u59d3\u4e91\u90a3\u5c0f\u5b50\u771f\u4e0d\u597d\u5bf9\u4ed8\u554a\uff0c\u8c22\u5929\u8c22\u5730\uff0c\u4ed6\u53bb\u4e86 \u6211\u53ef\u5b89\u4e50\u4e86\u3002\u201d\u7a9d\u624e\u5408\u9053\uff1a\u201c\u592a\u5e08\u662f\u4e0d\u662f\u4e5f\u8981\u53bb\u9001\u884c\uff1f\u201d\u4e5f\u5148\u9053\uff1a\u201c\u4f60\u4ee3\u8868\u6211\u53bb\uff0c\u63a8\u8bf4\u6211\u6709 \u75c5\u5427\u3002\u53cd\u6b63\u6709\u56fd\u738b\u9001\u4ed6\u4eec\u51fa\u57ce\uff0c\u4e5f\u591f\u9686\u91cd\u7684\u4e86\u3002\u201d \u3000\u3000\u8131\u4e0d\u82b1\u89c1\u4ed6\u4eec\u8bf4\u6765\u8bf4\u53bb\uff0c\u90fd\u662f\u5173\u4e8e\u660e\u65e5\u9001\u884c\u7684\u4e8b\uff0c\u4e0d\u611f\u5174\u8da3\uff0c\u6b63\u60f3\u56de\u53bb\u7761\u89c9\uff0c\u5ffd\u542c\u5f97\u7236 \u4eb2\u95ee\u9053\uff1a\u201c\u90a3\u5c0a\u7ea2\u8863\u5927\u70ae\uff0c\u5a01\u529b\u6781\u5927\uff0c\u4f60\u770b\u70ae\u58f0\u4f1a\u4e0d\u4f1a\u4f20\u51fa\u57ce\u5916\uff1f\u201d\u7a9d\u624e\u5408\u9053\uff1a\u201c\u5f20\u5b97\u5468\u7684 \u5e9c\u90b8\u79bb\u57ce\u95e8\u5341\u91cc\u6709\u96f6\uff0c\u8fd9\u70ae\u58f0\u53ef\u4f20\u5341\u91cc\uff0c\u5929\u4eae\u4e4b\u65f6\uff0c\u4ed6\u4eec\u5df2\u7ecf\u51fa\u57ce\uff0c\u53c8\u9694\u7740\u4e00\u5835\u539a\u539a\u7684\u57ce \u5899\uff0c\u5c31\u662f\u542c\u89c1\uff0c\u4e5f\u4e0d\u8fc7\u50cf\u7206\u7af9\u4e00\u6837\u7684\u58f0\u97f3\uff0c\u4e0d\u4f1a\u8d77\u7591\u7684\u3002\u201d\u8131\u4e0d\u82b1\u5403\u4e86\u4e00\u60ca\uff0c\u53ea\u542c\u5f97\u7a9d\u624e\u5408 \u53c8\u9053\uff1a\u201c\u800c\u4e14\u4e0d\u4e00\u5b9a\u8981\u653e\u70ae\uff0c\u4ed6\u4eec\u5728\u70ae\u53e3\u4e4b\u4e0b\uff0c\u8fd8\u4e0d\u4e56\u4e56\u5730\u81ea\u5df1\u7ed1\u6765\u542c\u592a\u5e08\u53d1\u843d\u4e48\uff1f\u201d\u4e5f\u5148 \u9053\uff1a\u201c\u5f20\u5b97\u5468\u7236\u5b50\u90fd\u662f\u4e00\u526f\u786c\u6027\u5b50\uff0c\u5c24\u5176\u662f\u5f20\u4e39\u67ab\uff0c\u66f4\u662f\u5403\u8f6f\u4e0d\u5403\u786c\uff0c\u6211\u77a7\u4ed6\u4eec\u662f\u5b81\u6b7b\u4e0d \u5c48\u3002\u201d\u505c\u4e86\u4e00\u505c\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u5f20\u4e39\u67ab\u6587\u6b66\u53cc\u5168\uff0c\u5012\u771f\u662f\u4e2a\u4eba\u624d\uff0c\u53ef\u60dc\u4ed6\u4e0d\u80af\u4e3a\u6211\u6240\u7528\uff0c\u8fd8\u5904 \u5904\u548c\u6211\u6363\u4e71\u3002\u8fd9\u6837\u7684\u4eba\u82e5\u653e\u4ed6\u56de\u56fd\uff0c\u7ec8\u662f\u74e6\u523a\u5fc3\u8179\u4e4b\u60a3\u5440\uff0c\u4f46\u613f\u4ed6\u5982\u4f60\u6240\u8a00\uff0c\u964d\u987a\u4e8e\u6211\u3002\u8981 \u4e0d\u7136\u4e5f\u53ea\u6709\u4e0d\u987e\u8131\u4e0d\u82b1\u7684\u4f24\u5fc3\uff0c\u5c06\u4ed6\u9664\u4e86\u3002\u201d\u539f\u6765\u4e5f\u5148\u5728\u90a3\u65e5\u4e8b\u540e\uff0c\u76d8\u95ee\u989d\u5409\u591a\u4e0e\u9ebb\u7ffc\u8d5e\uff0c \u77e5\u9053\u8ba1\u6551\u4e91\u91cd\uff0c\u6d3b\u6349\u6c99\u6d9b\uff0c\u6d88\u706d\u4e5f\u5148\u6d3e\u53bb\u7684\u4e94\u767e\u94c1\u9a91\u7b49\u7b49\u4e8b\u60c5\uff0c\u90fd\u662f\u5f20\u4e39\u67ab\u5e72\u51fa\u6765\u7684\u3002\u4e5f\u5148 \u53c8\u60ca\u53c8\u6012\uff0c\u65e9\u5c31\u5b9a\u4e0b\u4eca\u65e5\u70ae\u8f70\u4e4b\u7b56\u3002\u4f46\u5728\u660e\u671d\u7684\u4f7f\u81e3\u672a\u79bb\u5f00\u4e4b\u524d\uff0c\u5374\u4e0d\u80fd\u884c\u3002\u6240\u4ee5\u4e00\u5b9a\u8981\u7b49 \u5230\u5929\u4eae\u4e4b\u65f6\uff0c\u660e\u671d\u7684\u4f7f\u81e3\u79bb\u57ce\u4e4b\u540e\u3002 \u3000\u3000\u8131\u4e0d\u82b1\u542c\u5f97\u6bdb\u9aa8\u609a\u7136\u5fc3\u4e2d\u7126\u6025\u4e4b\u6781\u3002\u542c\u5f97\u5916\u9762\u6572\u4e86\u4e09\u66f4\uff0c\u7236\u4eb2\u5429\u5490\u7a9d\u624e\u5408\u4e00\u4e9b\u4e8b\u60c5\u4e4b \u540e\uff0c\u624d\u56de\u53bb\u5b89\u6b47\u3002\u4e5f\u5148\u7684\u623f\u95f4\u6b63\u5728\u8131\u4e0d\u82b1\u7684\u623f\u95f4\u5bf9\u9762\uff0c\u8131\u4e0d\u82b1\u8eb2\u5728\u5e8a\u4e0a\uff0c\u53ea\u89c1\u7236\u4eb2\u623f\u4e2d\u706f\u706b \u672a\u706d\u4eba\u5f71\u5728\u7a97\u5e18\u4e0a\u79fb\u6765\u79fb\u53bb\uff0c\u60f3\u662f\u4ed6\u5fc3\u60c5\u7d27\u5f20\uff0c\u6545\u6b64\u6df1\u591c\u4e0d\u7720\u3002\u8131\u4e0d\u82b1\u6bd4\u5979\u7236\u4eb2\u8fd8\u8981\u7d27\u5f20\u767e \u500d\uff0c\u82e6\u82e6\u601d\u7d22\uff0c\u76d8\u7b97\u6551\u5f20\u4e39\u67ab\u4e4b\u8ba1\uff0c\u4f46\u7236\u4eb2\u672a\u7761\uff0c\u5979\u600e\u6562\u8d70\u51fa\u623f\u95f4\u3002 \u3000\u3000\u597d\u4e0d\u5bb9\u6613\u7b49\u5230\u7236\u4eb2\u623f\u4e2d\u706f\u706b\u7184\u706d\uff0c\u8131\u4e0d\u82b1\u5618\u4e86\u53e3\u6c14\uff0c\u4e00\u8dc3\u800c\u8d77\u3002\u5ffd\u5730\u9192\u8d77\u5916\u9762\u8fd8\u6709\u4eba\u5b88 \u536b\uff0c\u81ea\u5df1\u51fa\u53bb\uff0c\u4ed6\u4eec\u56fa\u7136\u4e0d\u6562\u62e6\u963b\uff0c\u4f46\u5fc5\u7136\u60ca\u52a8\u7236\u4eb2\u3002\u8131\u4e0d\u82b1\u60f3\u4e86\u4e00\u60f3\u6084\u6084\u5730\u5c06\u7761\u5728\u91cc\u623f\u7684 \u4f8d\u5973\u5524\u9192\uff0c\u53eb\u5979\u70eb\u4e86\u4e24\u58f6\u70ed\u9152\uff0c\u9001\u7ed9\u5728\u82b1\u56ed\u503c\u591c\u7684\u4e24\u4e2a\u536b\u58eb\u559d\uff0c\u5c31\u5047\u8bf4\u662f\u56e0\u4e3a\u5929\u5bd2\u5730\u51bb\uff0c\u592a \u5e08\u7279\u522b\u8d4f\u8d50\u7684\u3002\u9152\u4e2d\u6697\u4e0b\u4e86\u9ebb\u836f\u3002 \u3000\u3000\u8131\u4e0d\u82b1\u5fc3\u4e2d\u4e03\u4e0a\u516b\u843d\uff0c\u751f\u6015\u90a3\u4e24\u4e2a\u536b\u58eb\u4e0d\u4e0a\u5708\u5957\uff0c\u542c\u5916\u9762\u94dc\u58f6\u6ef4\u6f0f\u4e4b\u58f0\uff0c\u6068\u4e0d\u5f97\u6709\u4ec0\u4e48 \u529e\u6cd5\u628a\u65f6\u95f4\u7559\u4f4f\u3002\u597d\u4e0d\u5bb9\u6613\u76fc\u5f97\u90a3\u4f8d\u5973\u56de\u6765\u62a5\u9053\uff1a\u4e24\u4e2a\u536b\u58eb\u4e0d\u7591\u6709\u5047\uff0c\u679c\u7136\u9189\u4e86\u3002\u8131\u4e0d\u82b1\u65e9 \u5df2\u6362\u4e86\u591c\u884c\u8863\u670d\uff0c\u6025\u5fd9\u6084\u6084\u6e9c\u51fa\u5954\u5165\u82b1\u56ed\uff0c\u4ece\u5899\u5934\u4e0a\u4e00\u8dc3\u8df3\u51fa\u3002\u8fd9\u65f6\u592a\u5e08\u5e9c\u4e2d\u5df2\u6572\u4e86\u56db\u66f4 \u4e86\u3002 \u3000\u3000\u8fd9\u65f6\u4e91\u91cd\u5728\u5bbe\u9986\u4e4b\u4e2d\uff0c\u4e5f\u662f\u5174\u594b\u975e\u5e38\uff0c\u7761\u4e0d\u7740\u89c9\u3002\u74e6\u523a\u56fd\u738b\u5df2\u4e0e\u4ed6\u7ea6\u597d\uff0c\u660e\u65e5\u4e00\u65e9\uff0c\u5c31 \u4ee5\u9001\u5929\u671d\u56fd\u541b\u4e4b\u793c\uff0c\u5c06\u660e\u671d\u88ab\u4fd8\u7684\u7687\u5e1d\u7948\u9547\uff0c\u9001\u5230\u57ce\u95e8\uff0c\u4e0e\u4e91\u91cd\u4f1a\u9f50\uff0c\u4e00\u540c\u5f52\u56fd\u3002\u8fd9\u662f\u6700\u5c0a \u656c\u7684\u793c\u8282\uff0c\u4e0d\u5fc5\u4e91\u91cd\u5230\u74e6\u523a\u671d\u4e0a\u53bb\u8f9e\u884c\u3002 \u3000\u3000\u5916\u9762\u661f\u6708\u4ea4\u8f89\uff0c\u5929\u7a7a\u4e00\u7247\u660e\u51c0\u3002\u4e91\u91cd\u501a\u680f\u9065\u671b\u5fc3\u9053\uff1a\u201c\u770b\u8fd9\u5149\u666f\uff0c\u660e\u65e5\u8be5\u662f\u4e2a\u98ce\u548c\u65e5\u4e3d \u7684\u6674\u5929\u3002\u51ac\u53bb\u6625\u6765\uff0c\u91cd\u5f52\u6545\u56fd\uff0c\u7687\u4e0a\u4e0d\u77e5\u8be5\u591a\u4e48\u9ad8\u5174\u5462\uff01\u201d\u60f3\u8d77\u81ea\u5df1\u6b64\u884c\uff0c\u5e78\u800c\u4e0d\u8fb1\u4f7f\u547d\uff0c \u4e0d\u4f46\u7f14\u4e86\u548c\u7ea6\uff0c\u8fd8\u5c06\u7f81\u7559\u5f02\u56fd\u7684\u7687\u5e1d\u63a5\u56de\uff0c\u8fd9\u6837\u7684\u4e8b\u60c5\uff0c\u51e0\u5343\u5e74\u6765\uff0c\u53f2\u518c\u6240\u65e0\uff0c\u4e91\u91cd\u4e3a\u88ab\u4fd8 \u7684\u7687\u5e1d\u800c\u6b22\u6b23\uff0c\u4e5f\u4e3a\u81ea\u5df1\u7684\u5e78\u8fd0\u800c\u5e86\u5e78\u3002 \u3000\u3000\u4f46\u5728\u9ad8\u5174\u4e4b\u4e2d\uff0c\u5374\u4e5f\u6709\u54c0\u6101\u3002\u5728\u5373\u5c06\u79bb\u5f00\u74e6\u523a\u7684\u524d\u5915\uff0c\u4e91\u91cd\u81ea\u7136\u800c\u7136\u5730\u66f4\u52a0\u60f3\u8d77\u4e86\u81ea\u5df1 \u7684\u7236\u6bcd\u548c\u59b9\u59b9\uff0c\u96be\u9053\u4ed6\u4eec\u8fd8\u4e0d\u77e5\u9053\u81ea\u5df1\u5df2\u7ecf\u6765\u5230\u8fd9\u7684\u6d88\u606f\uff1f\u5468\u5c71\u6c11\u4e0d\u662f\u5df2\u7ecf\u89c1\u4e86\u4ed6\u4eec\u4e48\uff1f\u4e3a \u4f55\u8fd8\u4e0d\u5230\u4eac\u57ce\u6765\u548c\u81ea\u5df1\u76f8\u4f1a\uff1f\u79cd\u79cd\u7591\u60d1\uff0c\u90fd\u5728\u5fc3\u5934\u6d8c\u8d77\u3002\u4e91\u91cd\u672c\u610f\u8981\u591a\u7559\u51e0\u65e5\uff0c\u7b49\u5f85\u5bb6\u4eba\u56e2 \u805a\u7684\uff0c\u53ef\u662f\u60f3\u4e0d\u5230\u548c\u7ea6\u7f14\u7ed3\u5f97\u90a3\u4e48\u987a\u5229\uff0c\u800c\u7948\u9547\u53c8\u8feb\u4e0d\u53ca\u5f85\u5730\u65e5\u65e5\u6d3e\u4eba\u50ac\u4e91\u91cd\u8d77\u884c\uff0c\u8fd9\u4e2a\u88ab \u4fd8\u7684\u7687\u5e1d\u5fc3\u4e2d\u6240\u60f3\u7684\u65e0\u975e\u662f\u65e9\u65e5\u8d76\u56de\uff0c\u91cd\u767b\u5927\u5b9d\uff0c\u4ed6\u54ea\u91cc\u4f1a\u77e5\u9053\u4e91\u91cd\u7684\u5fc3\u4e8b\u3002 \u3000\u3000\u5728\u79bb\u5f00\u7684\u524d\u5915\uff0c\u4e91\u91cd\u4e5f\u81ea\u7136\u5730\u60f3\u5230\u4e86\u5f20\u4e39\u67ab\uff0c\u8fd9\u6b21\u51fa\u4f7f\u7684\u6210\u529f\uff0c\u5927\u534a\u662f\u9760\u4e86\u5f20\u4e39\u67ab\u4e4b \u529b\uff0c\u53ef\u662f\u4e3a\u4e86\u4e24\u5bb6\u7684\u4e16\u4ec7\uff0c\u4ed6\u4e0d\u613f\u5230\u5f20\u5bb6\u62dc\u4f1a\u81ea\u5df1\u7956\u7236\u7684\u4ec7\u4eba\uff0c\u800c\u5f20\u4e39\u67ab\u4e5f\u4e0d\u6765\u770b\u4ed6\u3002\u4e91\u91cd \u4e0d\u77e5\u600e\u7684\uff0c\u4e00\u60f3\u8d77\u6765\uff0c\u5c31\u89c9\u5fc3\u4e2d\u6005\u60d8\uff0c\u8fd9\u671f\u95f4\u6fb9\u53f0\u955c\u660e\u4e5f\u66fe\u529d\u8fc7\u4ed6\u4e0d\u4e0b\u6570\u5341\u6b21\uff0c\u529d\u4ed6\u548c\u5f20\u5bb6 \u91ca\u5acc\u4fee\u597d\uff0c\u53ef\u662f\u7f8a\u76ae\u8840\u4e66\u7684\u9634\u5f71\u8fd8\u91cd\u91cd\u5730\u538b\u5728\u5fc3\u5934\uff0c\u4ed6\u600e\u80af\u8e0f\u5165\u4ec7\u4eba\u7684\u5bb6\u95e8\uff1f\u4f46\u867d\u7136\u5982\u6b64\uff0c \u4ed6\u5bf9\u8fd9\u4e0d\u4e45\u4e4b\u524d\u8fd8\u89c6\u4e3a\u4ec7\u4eba\u7684\u5f20\u4e39\u67ab\uff0c\u5374\u6709\u4e86\u4e00\u79cd\u820d\u4e0d\u5f97\u5206\u5f00\u7684\u611f\u60c5\u4e86\u3002 \u3000\u3000\u201c\u5f20\u4e39\u67ab\u660e\u65e9\u4f1a\u4e0d\u4f1a\u8d76\u6765\u548c\u6211\u540c\u884c\u5462\uff1f\u201d\u4e91\u91cd\u60f3\u8d77\u4e86\u8fd9\u4e2a\u95ee\u9898\uff0c\u5fc3\u60c5\u77db\u76fe\u4e4b\u6781\u3002\u4ed6\u5fc3\u5e95 \u91cc\u4f3c\u4e4e\u662f\u76fc\u671b\u4ed6\u80fd\u8d76\u6765\uff0c\u4f46\u53c8\u4f3c\u4e4e\u4e0d\u60f3\u4ed6\u8d76\u6765\uff0c\u82e5\u7136\u4ed6\u771f\u7684\u8d76\u6765\uff0c\u548c\u81ea\u5df1\u91cd\u5f52\u6545\u56fd\uff0c\u90a3\u4e48\u5c06 \u6765\u81ea\u5df1\u7684\u7236\u4eb2\u600e\u6837\u770b\u6cd5\uff0c\u4ed6\u5bf9\u4e91\u857e\u7684\u7ea0\u7f20\uff0c\u53c8\u80af\u4e0d\u80af\u5c31\u6b64\u5272\u5f00\uff1f\u81ea\u5df1\u7684\u7236\u4eb2\u4f1a\u4e0d\u4f1a\u9a82\u81ea\u5df1\u548c \u59b9\u59b9\u662f\u4e00\u5bf9\u4e0d\u8096\u7684\u513f\u5973\uff1f \u3000\u3000\u6b22\u6b23\u3001\u5fe7\u8651\u3001\u6069\u6028\u3001\u6101\u70e6\uff0c\u79cd\u79cd\u60c5\u7eea\uff0c\u6253\u6210\u4e86\u4e00\u4e2a\u4e2a\u7ed3\uff0c\u7ed3\u5728\u5fc3\u4e2d\uff0c\u526a\u4e0d\u65ad\uff0c\u7406\u8fd8\u4e71\uff0c \u4e91\u91cd\u8fd8\u662f\u6709\u751f\u4ee5\u6765\u7b2c\u4e00\u6b21\u4f53\u9a8c\u5230\u8fd9\u79cd\u5fc3\u60c5\u3002\u4ed6\u72ec\u501a\u680f\u6746\uff0c\u601d\u6765\u60f3\u53bb\uff0c\u4e0d\u77e5\u4e0d\u89c9\u5730\u5df2\u542c\u5f97\u5916\u9762 \u6253\u4e86\u56db\u66f4\u4e86\u3002 \u3000\u3000\u4e91\u91cd\u6b63\u60f3\u56de\u623f\u7a0d\u7761\u7247\u523b\uff0c\u5ffd\u542c\u5f97\u4e0b\u9762\u4eba\u58f0\u5608\u6742\uff0c\u968f\u4ece\u4e0a\u6765\u62a5\u9053\uff0c\u5ba2\u6808\u91cc\u8df3\u8fdb\u4e86\u4e00\u4e2a\u8499\u9762 \u7684\u591c\u884c\u4eba\uff0c\u53e3\u53e3\u58f0\u58f0\u8bf4\u8981\u7acb\u5373\u8c12\u89c1\u4f7f\u81e3\uff0c\u4e0d\u77e5\u662f\u5426\u523a\u5ba2\uff0c\u8bf7\u4e91\u91cd\u5904\u7f6e\u3002\u4e91\u91cd\u5927\u4e3a\u5947\u602a\uff0c\u60f3\u4e86 \u4e00\u60f3\u9053\uff1a\u201c\u597d\uff0c\u8ba9\u4ed6\u8fdb\u6765\u3002\u201d\u8fc7\u4e86\u4e00\u9635\uff0c\u536b\u58eb\u5c06\u4e00\u9ed1\u8863\u5c11\u5e74\u63a8\u4e86\u4e0a\u6765\uff0c\u662f\u8499\u53e4\u6b66\u58eb\u7684\u88c5\u675f\uff0c \u4f46\u8eab\u6750\u82d7\u6761\uff0c\u5374\u4e0e\u4e00\u822c\u8499\u53e4\u6b66\u5e72\u7684\u7c97\u8c6a\uff0c\u5927\u4e0d\u76f8\u7c7b\u3002 \u3000\u3000\u4e91\u91cd\u597d\u751f\u5947\u602a\uff0c\u9053\uff1a\u201c\u4f60\u6df1\u591c\u6c42\u89c1\uff0c\u662f\u4f55\u4e8b\u60c5\uff1f\u662f\u8c01\u4eba\u9063\u4f60\u6765\u7684\uff1f\u201d\u90a3\u9752\u5e74\u6b66\u58eb\u9762\u4e0a\u8499 \u7740\u4e00\u5757\u9ed1\u5dfe\uff0c\u9732\u51fa\u4e00\u53cc\u660e\u5982\u79cb\u6c34\u7684\u773c\u775b\uff0c\u53ea\u89c1\u4ed6\u773c\u6ce2\u4e00\u8f6c\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u8bf7\u5927\u4eba\u6452\u9000\u5de6 \u53f3\u3002\u201d\u4e91\u91cd\u7684\u4f8d\u4ece\u6000\u7591\u4ed6\u662f\u523a\u5ba2\uff0c\u4e00\u4eba\u4e0a\u524d\u7980\u9053\uff1a\u201c\u8bf7\u5927\u4eba\u5c0f\u5fc3\u3002\u201d\u53e6\u4e00\u4eba\u4fbf\u5f85\u4e0a\u524d\u641c\u4ed6\u7684 \u8eab\u5b50\uff0c\u90a3\u6b66\u58eb\u9661\u5730\u95ea\u5f00\u4e24\u6b65\uff0c\u773c\u5149\u4e2d\u9732\u51fa\u53c8\u7f9e\u53c8\u607c\u7684\u795e\u60c5\u3002\u4e91\u91cd\u5fc3\u4e2d\u4e00\u52a8\uff0c\u6325\u624b\u8bf4\u9053\uff1a\u201c\u4f60 \u4eec\u90fd\u4e0b\u53bb\u5427\uff0c\u54b1\u4eec\u5929\u671d\u4f7f\u8005\uff0c\u4ee5\u8bda\u5f85\u4eba\uff0c\u4f55\u987b\u6212\u60e7\u3002\u201d\u5f85\u968f\u4ece\u8d70\u5f00\uff0c\u4e91\u91cd\u968f\u624b\u5173\u4e0a\u623f\u95e8\uff0c\u7b11 \u9053\uff1a\u201c\u73b0\u5728\u53ef\u4ee5\u89c1\u544a\u4e86\u5427\uff1f\u201d \u3000\u3000\u53ea\u89c1\u90a3\u5e74\u9752\u6b66\u58eb\u5c06\u9762\u5dfe\u6495\u4e0b\uff0c\u8131\u4e86\u6597\u7bf7\uff0c\u5374\u539f\u6765\u662f\u4e2a\u4fcf\u751f\u751f\u7684\u8499\u53e4\u5c11\u5973\u3002\u5979\u7b2c\u4e00\u53e5\u8bdd\u4fbf \u662f\uff1a\u201c\u6211\u662f\u4e5f\u5148\u7684\u5973\u513f\uff01\u201d\u4e91\u91cd\u5413\u4e86\u4e00\u8df3\uff0c\u90a3\u6b66\u58eb\u5973\u626e\u7537\u88c5\uff0c\u65e9\u5df2\u88ab\u4ed6\u770b\u51fa\uff0c\u4e0d\u8db3\u60ca\u5f02\uff0c\u4f46 \u5979\u7adf\u662f\u4e5f\u5148\u7684\u5973\u513f\uff0c\u6b64\u4e8b\u5374\u662f\u4e91\u91cd\u4e07\u4e07\u6599\u60f3\u4e0d\u5230\uff01\u4e91\u91cd\u4e0d\u77e5\u4e5f\u5148\u800d\u4ec0\u4e48\u82b1\u62db\uff0c\u6025\u5fd9\u8d77\u7acb\u8ba9 \u5ea7\uff0c\u9053\uff1a\u201c\u5c0a\u5927\u4eba\u6709\u4f55\u89c1\u6559\uff1f\u4e3a\u4f55\u8981\u4f60\u524d\u6765\uff1f\u201d \u3000\u3000\u8131\u4e0d\u82b1\u6447\u4e86\u6447\u5934\uff0c\u8868\u793a\u5e76\u975e\u7236\u4eb2\u9063\u6765\u3002\u4e91\u91cd\u66f4\u662f\u5947\u602a\uff0c\u53ea\u89c1\u8131\u4e0d\u82b1\u795e\u8272\u4ed3\u7687\uff0c\u51b2\u53e3\u8bf4 \u9053\uff1a\u201c\u4e91\u5927\u4eba\uff0c\u4f60\u548c\u5f20\u4e39\u67ab\u662f\u4e0d\u662f\u597d\u53cb\uff1f\u201d\u4e91\u91cd\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u8131\u4e0d\u82b1\u9053\uff1a\u201c\u5982\u4eca\u5df2\u6572\u4e86\u56db \u66f4\uff0c\u53ea\u8981\u5929\u8272\u4e00\u4eae\uff0c\u5f20\u4e39\u67ab\u5168\u5bb6\u8001\u5e7c\uff0c\u90fd\u8981\u5316\u4e3a\u98de\u7070\uff01\u4ed6\u7684\u6027\u547d\u5982\u4eca\u60ac\u5728\u4f60\u7684\u624b\u4e2d\uff0c\u4f60\u6551\u4ed6 \u8fd8\u662f\u4e0d\u6551\uff1f\u201d\u4e91\u91cd\u60ca\u9a87\u4e4b\u6781\uff0c\u6025\u5fd9\u95ee\u9053\uff1a\u201c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d\u8131\u4e0d\u82b1\u9053\uff1a\u201c\u6211\u7236\u4eb2\u6068\u4ed6\u52a9 \u4f60\uff0c\u66f4\u6015\u4ed6\u56de\u5230\u4e2d\u534e\uff0c\u5c06\u6765\u6c38\u4e3a\u74e6\u523a\u4e4b\u60a3\uff0c\u6240\u4ee5\u5df2\u6d3e\u5175\u56f4\u4e86\u4ed6\u7684\u5e9c\u90b8\uff0c\u53ea\u5f85\u5929\u8272\u4e00\u4eae\uff0c\u5c31\u8981 \u7528\u70ae\u6765\u8f70\uff01\u201d\u4e91\u91cd\u9053\uff1a\u201c\u6211\u5982\u4f55\u53ef\u4ee5\u6551\u4ed6\u5462\uff1f\u201d\u8131\u4e0d\u82b1\u9053\uff1a\u201c\u7acb\u523b\u5230\u5f20\u5bb6\u53bb\uff01\u201d \u3000\u3000\u4e91\u91cd\u4ea6\u662f\u806a\u660e\u4e4b\u4eba\uff0c\u60ca\u60f6\u7a0d\u5b9a\uff0c\u5fc3\u4e2d\u4e00\u60f3\uff0c\u4fbf\u77e5\u5176\u7406\uff0c\u81ea\u5df1\u662f\u4e2d\u56fd\u7684\u4f7f\u81e3\uff0c\u82e5\u7136\u8d76\u5230\u5f20 \u5bb6\uff0c\u4e5f\u5148\u6b63\u6025\u4e8e\u4e0e\u4e2d\u56fd\u5abe\u548c\uff0c\u4ed6\u7eb5\u6709\u5929\u5927\u7684\u80c6\u5b50\u4e5f\u4e0d\u6562\u5f00\u70ae\u3002\u4ed6\u8981\u7b49\u5f85\u5929\u4eae\u52a8\u624b\uff0c\u90a3\u81ea\u7136\u4e5f \u662f\u907f\u514d\u7ed9\u81ea\u5df1\u77e5\u9053\u3002 \u3000\u3000\u8fd9\u4e00\u77ac\u95f4\uff0c\u4e91\u91cd\u5fc3\u5934\u6709\u5982\u5e73\u9759\u7684\u6d77\u6d0b\u7a81\u7136\u88ab\u98ce\u66b4\u6fc0\u8d77\u5343\u91cd\u6ce2\u6d6a\uff0c\u8fd9\u4e00\u53bb\u4e0d\u4f46\u8981\u8e0f\u5165\u4ec7\u4eba \u7684\u5bb6\u95e8\uff0c\u800c\u4e14\u8bef\u4e86\u884c\u7a0b\uff0c\u800c\u8fd9\u65e5\u671f\u53c8\u662f\u74e6\u523a\u7684\u56fd\u738b\u548c\u660e\u671d\u7684\u201c\u592a\u4e0a\u7687\u201d\u4e0e\u4ed6\u65e9\u7ea6\u5b9a\u7684\u4e86\uff01 \u3000\u3000\u8131\u4e0d\u82b1\u53cc\u775b\u6ce8\u5b9a\u4e91\u91cd\uff0c\u51e0\u4e4e\u6025\u5f97\u8981\u6d41\u4e0b\u6cea\u6765\uff0c\u5ffd\u5730\u98a4\u58f0\u53eb\u9053\uff1a\u201c\u4f60\u5230\u5e95\u6551\u4ed6\u8fd8\u662f\u4e0d \u6551\uff1f\u201d\u4e91\u91cd\u5fc3\u4e2d\u70e6\u4e71\u4e4b\u6781\uff0c\u8111\u6d77\u4e2d\u9661\u5730\u95ea\u8fc7\u5f20\u4e39\u67ab\u90a3\u4e30\u795e\u6f47\u6d12\u7684\u5f71\u5b50\uff0c\u95ea\u8fc7\u5728\u81ea\u5df1\u9047\u96be\u4e4b \u65f6\uff0c\u5f20\u4e39\u67ab\u63ed\u5f00\u5e10\u5e55\uff0c\u7b11\u541f\u541f\u5730\u7a81\u5982\u800c\u6765\u7684\u795e\u60c5\u3002\u8fd9\u6837\u7684\u4eba\uff0c\u8c01\u80fd\u5fcd\u5fc3\u8ba9\u4ed6\u6b7b\u53bb\uff1f \u3000\u3000\u4e0d\u5f85\u8131\u4e0d\u82b1\u518d\u95ee\uff0c\u4e91\u91cd\u5df2\u84e6\u7136\u8df3\u8d77\uff0c\u6253\u5f00\u623f\u95e8\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u6d3e\u4e24\u4e2a\u4eba\u7acb\u523b\u98de\u8d76\u53bb\u74e6\u523a\u7687 \u5bab\uff0c\u901a\u77e5\u9ec4\u95e8\u5b98\uff0c\u53eb\u4ed6\u7acb\u5373\u8f6c\u8fbe\u74e6\u523a\u56fd\u738b\uff0c\u8bf4\u6211\u660e\u5929\u4e0d\u8d70\uff01\u201d\u968f\u4ece\u4eec\u4e00\u62e5\u800c\u8fdb\u7eb7\u7eb7\u95ee\u9053\uff1a \u201c\u600e\u4e48\uff1f\u201d\u4e91\u91cd\u9053\uff1a\u201c\u4f60\u4eec\u7acb\u523b\u6574\u88c5\uff0c\u968f\u6211\u51fa\u53d1\uff0c\u6211\u8981\u53bb\u62dc\u4f1a\u5f20\u5b97\u5468\uff01\u201d\u8fd9\u65f6\u4ed6\u628a\u8a93\u6b7b\u4e0d\u5165 \u4ec7\u4eba\u4e4b\u95e8\u7684\u8a93\u8a00\u65e9\u5df2\u629b\u4e4b\u8111\u540e\u4e86\u3002 \u3000\u3000\u521a\u624d\u90a3\u4e00\u9635\u9a9a\u52a8\uff0c\u6fb9\u53f0\u955c\u660e\u4ea6\u5df2\u60ca\u8d77\uff0c\u8fd9\u65f6\u6b63\u7ad9\u5728\u4e91\u91cd\u7684\u5367\u5ba4\u95e8\u524d\uff0c\u77a5\u89c1\u4e00\u4e2a\u8499\u53e4\u5c11 \u5973\uff0c\u8138\u4e0a\u5e26\u7740\u7b11\u5bb9\uff0c\u773c\u89d2\u5374\u6301\u7740\u6cea\u73e0\uff0c\u800c\u4e14\u8fd8\u7d27\u7d27\u5730\u63e1\u7740\u4e91\u91cd\u7684\u624b\uff0c\u5fc3\u4e2d\u6b63\u5728\u83ab\u540d\u5176\u5999\uff0c\u5ffd \u95fb\u5f97\u4e91\u91cd\u8bf4\u51fa\u8981\u53bb\u62dc\u4f1a\u5f20\u5b97\u5468\u7684\u8bdd\uff0c\u66f4\u662f\u60ca\u8be7\u3002\u4e91\u91cd\u9053\uff1a\u201c\u597d\u5440\uff0c\u6fb9\u53f0\u59b9\u5b50\uff0c\u4f60\u4e5f\u53bb\uff01\u201d\u6fb9 \u53f0\u955c\u660e\u5fc3\u4e2d\u6b22\u559c\u65e0\u9650\uff0c\u65e0\u6687\u518d\u95ee\u60c5\u7531\u542b\u6cea\u7b11\u9053\uff1a\u201c\u662f\u5440\uff0c\u54b1\u4eec\u65e9\u5c31\u8be5\u53bb\u4e86\uff01\u201d\u8fd9\u65f6\u5979\u624d\u548c\u8131 \u4e0d\u82b1\u4e92\u76f8\u8bf7\u95ee\u59d3\u540d\u3002 \u3000\u3000\u5ba2\u6808\u79bb\u7687\u5bab\u4e0d\u8fdc\uff0c\u79bb\u5f20\u5bb6\u5374\u6709\u516d\u4e03\u91cc\u8def\uff0c\u4e91\u91cd\u4e00\u884c\u4e58\u7740\u5feb\u9a6c\uff0c\u5728\u6df1\u66f4\u591c\u9759\u51b2\u51fa\u8857\u5934\uff0c\u81ea \u7136\u5f15\u8d77\u9a9a\u52a8\uff0c\u4f46\u4ed6\u4eec\u6253\u7740\u660e\u671d\u4f7f\u8005\u7684\u5927\u7ea2\u706f\u7b3c\uff0c\u5374\u4e5f\u65e0\u4eba\u6562\u4e88\u62e6\u963b\u3002\u4e91\u91cd\u4e3a\u4e86\u907f\u514d\u7ecf\u8fc7\u7687 \u5bab\uff0c\u6284\u8fc7\u50fb\u9759\u7684\u8857\u5df7\uff0c\u7ed5\u9053\u800c\u884c\uff0c\u521a\u521a\u8f6c\u51fa\u8461\u8404\u5927\u8857\uff0c\u8fd9\u662f\u74e6\u523a\u4eac\u57ce\u4e2d\u5fc3\u7684\u5927\u8857\uff0c\u8d70\u5230\u5c3d \u5934\uff0c\u518d\u8f6c\u8fc7\u897f\u8fb9\uff0c\u5c31\u53ef\u671b\u89c1\u5f20\u5b97\u5468\u7684\u4e1e\u76f8\u5e9c\u3002\u6a2a\u8857\u91cc\u7a81\u7136\u5954\u51fa\u4e00\u9a91\u5065\u9a6c\u62e6\u5728\u524d\u9762\uff0c\u4e91\u91cd\u559d \u9053\uff1a\u201c\u6211\u662f\u5927\u660e\u4f7f\u8005\uff0c\u8c01\u6562\u62e6\u963b\uff1f\u201d\u9a6c\u4e0a\u4eba\u8eab\u624b\u77eb\u6377\uff0c\u7ed9\u4e91\u91cd\u7684\u9a6c\u5934\u4e00\u51b2\u4e00\u4e2a\u7b4b\u6597\u7ffb\u5728\u5730 \u4e0a\uff0c\u4ecd\u7136\u7aef\u7aef\u6b63\u6b63\u5730\u8dea\u5728\u5730\u4e0a\uff0c\u53cc\u624b\u9ad8\u4e3e\u4e00\u9762\u91d1\u724c\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u660e\u671d\u5929\u5b50\u5fa1\u65e8\uff0c\u8bf7\u4e91\u5927\u4eba \u63a5\u8bcf\u3002\u201d \u3000\u3000\u4e91\u91cd\u5403\u4e86\u4e00\u60ca\uff0c\u968f\u4ece\u4e0a\u524d\uff0c\u628a\u706f\u7b3c\u4e00\u7167\uff0c\u4e91\u91cd\u5b9a\u6674\u4e00\u770b\uff0c\u8ba4\u51fa\u662f\u5728\u571f\u6728\u5821\u660e\u5175\u5927\u8d25\u4e4b\u65f6 \u88ab\u74e6\u523a\u519b\u4fd8\u53bb\u7684\u5927\u5185\u4f8d\u536b\u4e4b\u4e00\u3002\u90a3\u6b21\u7687\u5e1d\u8eab\u8fb9\u7684\u4f8d\u536b\uff0c\u9664\u4e86\u6218\u6b7b\u4e0e\u81ea\u6740\u4e4b\u5916\uff0c\u8fd8\u6709\u56db\u4e94\u4e2a \u4eba\uff0c\u540c\u7687\u5e1d\u4e00\u9f50\u88ab\u74e6\u523a\u6240\u4fd8\uff0c\u521d\u65f6\u672c\u662f\u5206\u5f00\u56da\u7981\uff0c\u81f3\u4e91\u91cd\u5230\u6765\u8c08\u548c\u4e4b\u540e\uff0c\u74e6\u523a\u56fd\u738b\u5c06\u7948\u9547\u63a5 \u5230\u7687\u5bab\uff0c\u5f85\u4ee5\u541b\u4e3b\u4e4b\u793c\uff0c\u62e8\u4e86\u4e00\u5ea7\u5bab\u6bbf\u7ed9\u4ed6\u5c45\u4f4f\uff0c\u8fd9\u51e0\u4e2a\u536b\u58eb\u4e5f\u5c31\u88ab\u91ca\u653e\u51fa\u6765\uff0c\u4ecd\u7136\u8ba9\u4ed6\u4eec \u4f8d\u5019\u4ed6\u4eec\u7684\u6545\u4e3b\u3002 \u3000\u3000\u7528\u91d1\u724c\u547d\u4ee4\u5927\u5c06\uff0c\u4e43\u662f\u4e2d\u56fd\u7687\u671d\u7684\u60ef\u4f8b\uff08\u5b8b\u4ee3\u7684\u5cb3\u98de\u5c31\u662f\u88ab\u7687\u5e1d\u4e00\u8fde\u53d1\u5341\u4e8c\u9053\u91d1\u724c\u53ec \u56de\uff09\u3002\u7948\u9547\u5728\u76ee\u524d\u4e25\u683c\u6765\u8bf4\uff0c\u5b9e\u5728\u8fd8\u662f\u4fd8\u864f\u7684\u8eab\u4efd\uff0c\u4ed6\u5374\u4ecd\u4e0d\u5fd8\u201c\u7956\u5236\u201d\uff0c\u8fd9\u91d1\u724c\u81ea\u7136\u662f\u501f \u6765\u7684\u4e86\u3002\u7948\u9547\u4f3c\u4e4e\u6015\u4e91\u91cd\u8fd8\u4e0d\u76f8\u4fe1\uff0c\u91d1\u724c\u4e4b\u5916\uff0c\u5c1a\u6709\u8bcf\u4e66\uff0c\u8bcf\u4e0a\u5199\u7740\u4e00\u884c\u8349\u5b57\uff1a\u201c\u5ba3\u4f7f\u81e3\u4e91 \u91cd\u8fdb\u5bab\u671d\u89c1\u3002\u201d\u91d1\u724c\u52a0\u4e0a\u62db\u4e66\uff0c\u800c\u4e14\u662f\u6df1\u591c\u76f8\u53ec\uff0c\u53ef\u4ee5\u6599\u60f3\uff0c\u90a3\u4e00\u5b9a\u662f\u6781\u7d27\u6025\u7684\u5927\u4e8b\uff0c\u6240\u4ee5 \u624d\u5982\u6b64\u90d1\u91cd\u3002 \u3000\u3000\u4e91\u91cd\u628a\u8bcf\u4e66\u63a5\u8fc7\u4e00\u770b\uff0c\u90a3\u4e0a\u9762\u8fd8\u76d6\u6709\u660e\u671d\u5929\u5b50\u7684\u79c1\u7ae0\uff0c\u5b57\u8ff9\u4e5f\u786e\u662f\u7948\u9547\u624b\u4e66\uff0c\u90a3\u81ea\u7136\u662f \u4e0d\u4f1a\u5047\u7684\u3002\u4e91\u91cd\u5403\u4e86\u4e00\u60ca\uff0c\u4e0d\u77e5\u6240\u63aa\u3002\u73b0\u5728\u8ddd\u79bb\u5929\u4eae\u53ea\u6709\u4e00\u4e2a\u65f6\u8fb0\uff0c\u82e5\u7136\u53bb\u671d\u89c1\u7687\u4e0a\uff0c\u53ea\u6050 \u65f6\u8fb0\u4e00\u5230\uff0c\u5f20\u4e39\u67ab\u5168\u5bb6\u8001\u5e7c\u5c31\u8981\u5728\u70ae\u706b\u4e4b\u4e0b\u5316\u6210\u98de\u7070\uff01\u4f46\u82e5\u4e0d\u53bb\u8fd9\u4e0d\u63a5\u5723\u65e8\u7684\u7f6a\u540d\u53ef\u662f\u975e\u540c \u5c0f\u53ef\uff01\u4e91\u91cd\u62ff\u7740\u8bcf\u4e66\uff0c\u8e0c\u8e87\u96be\u51b3\uff0c\u6fb9\u53f0\u955c\u660e\u53eb\u9053\uff1a\u201c\u5230\u4e86\u5f20\u5bb6\u4e4b\u540e\u518d\u5165\u5bab\u671d\u89c1\u3002\u201d\u4e91\u91cd\u9053\uff1a \u201c\u597d\u5c31\u662f\u8fd9\u6837\u3002\u201d\u90a3\u6367\u91d1\u724c\u7684\u536b\u58eb\u4ecd\u7136\u8dea\u5728\u9a6c\u524d\uff0c\u4e0d\u6562\u8d77\u8eab\uff0c\u4e91\u91cd\u9053\uff1a\u201c\u4f60\u56de\u53bb\u7980\u544a\u7687\u4e0a \u5427\uff0c\u660e\u65e9\u6682\u4e0d\u52a8\u8eab\uff0c\u6700\u8fdf\u5348\u95f4\uff0c\u6211\u4e00\u5b9a\u8fdb\u5bab\u671d\u89c1\u3002\u201d\u90a3\u536b\u58eb\u4ecd\u7136\u76f4\u633a\u633a\u7684\u8dea\u7740\uff0c\u4e0d\u80af\u62ff\u56de\u91d1 \u724c\u3002\u5ffd\u542c\u5f97\u540e\u9762\u9a6c\u94c3\u4e4b\u58f0\u6025\u4fc3\u5730\u54cd\uff0c\u53c8\u662f\u4e00\u9a91\u9a8f\u9a6c\u5954\u4e86\u4e0a\u6765\uff0c\u9a6c\u4e0a\u4eba\u4e00\u8dc3\u800c\u4e0b\uff0c\u53c8\u8dea\u5728\u4e91\u91cd \u7684\u524d\u9762\u3002 \u3000\u3000\u8fd9\u4eba\u4e5f\u662f\u4f3a\u5019\u7948\u9547\u7684\u536b\u58eb\uff0c\u50cf\u5148\u524d\u90a3\u4e2a\u536b\u58eb\u4e00\u6837\uff0c\u4e5f\u662f\u4e00\u624b\u9ad8\u4e3e\u91d1\u724c\uff0c\u4e00\u624b\u638f\u51fa\u8bcf\u4e66\uff0c \u8bcf\u4e66\u4e0a\u5199\u9053\uff1a\u201c\u5ba3\u4f7f\u81e3\u4e91\u91cd\u7acb\u5373\u8fdb\u5bab\u671d\u89c1\u3002\u201d\u5b57\u53e5\u4e0e\u4e0a\u4e00\u8bcf\u4e66\u76f8\u540c\uff0c\u53ea\u662f\u591a\u4e86\u201c\u7acb\u5373\u201d\u4e8c \u5b57\uff0c\u4e91\u91cd\u6367\u7740\u8bcf\u4e66\uff0c\u624b\u6307\u98a4\u6296\uff0c\u6ca1\u6709\u4e3b\u610f\u3002\u8131\u4e0d\u82b1\u53eb\u9053\uff1a\u201c\u7ba1\u5b83\u4ec0\u4e48\u8bcf\u4e66\uff0c\u54b1\u4eec\u8fd8\u662f\u7167\u521a\u624d \u7684\u8bf4\u6cd5\u3002\u201d\u8bdd\u58f0\u672a\u4e86\uff0c\u53c8\u662f\u4e00\u9a91\u5feb\u9a6c\u8ffd\u6765\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u4e91\u5927\u4eba\u63a5\u8bcf\uff01\u201d\u8fd9\u662f\u4e91\u91cd\u65e7\u65e5\u7684\u540c \u50da\uff0c\u7687\u5e1d\u8d34\u8eab\u7684\u4f8d\u536b\uff0c\u6a0a\u5fe0\u4e4b\u5f1f\u6a0a\u4fca\u3002\u53ea\u89c1\u4ed6\u4e5f\u662f\u4e00\u624b\u9ad8\u4e3e\u91d1\u724c\uff0c\u4e00\u624b\u9012\u8fc7\u8bcf\u4e66\uff0c\u8bcf\u4e66\u7684\u5b57 \u53e5\u4e0e\u524d\u4e00\u5c01\u5b8c\u5168\u76f8\u540c\uff0c\u4f46\u5728\u90a3\u201c\u7acb\u5373\u201d\u4e24\u5b57\u65c1\u8fb9\uff0c\u53c8\u6253\u4e86\u4e24\u4e2a\u5708\u5708\uff0c\u8868\u793a\u5341\u4e07\u706b\u6025\u4e4b\u610f\u3002\u4e91 \u91cd\u95ee\u9053\uff1a\u201c\u6a0a\u4f8d\u536b\uff0c\u7a76\u7adf\u662f\u4ec0\u4e48\u4e8b\u60c5\uff1f\u201d\u6a0a\u4fca\u9053\uff1a\u201c\u54b1\u4e5f\u4e0d\u77e5\u662f\u751a\u4e8b\u60c5\u53ea\u662f\u7687\u4e0a\u4eb2\u53e3\u5429\u5490\uff0c \u4e00\u5b9a\u8981\u4e91\u5927\u4eba\u7acb\u523b\u8fdb\u5bab\u671d\u89c1\u4e0d\u5f97\u7a3d\u5ef6\u3002\u201d \u3000\u3000\u4e91\u91cd\u53f9\u4e86\u53e3\u6c14\uff0c\u987b\u77e5\u8fd9\u91d1\u724c\u53ec\u5524\uff0c\u5b9e\u662f\u6700\u4e25\u91cd\u7684\u5723\u65e8\uff0c\u6614\u65e5\u5b8b\u671d\u7684\u540d\u5c06\u5cb3\u98de\uff0c\u5c1a\u81ea\u4e0d\u6562 \u8fdd\u6297\uff0c\u4f55\u51b5\u4e91\u91cd\uff1f\u800c\u4e14\u4ed6\u4e5f\u6015\u5bab\u4e2d\u6709\u53d8\uff0c\u653b\u8d25\u5782\u6210\uff0c\u4e24\u76f8\u6743\u8861\uff0c\u81ea\u662f\u7687\u5e1d\u66f4\u4e3a\u91cd\u8981\u3002\u4e91\u91cd\u63a5 \u4e86\u4e09\u9762\u91d1\u724c\uff0c\u53ea\u5f97\u62e8\u8f6c\u9a6c\u5934\u5bf9\u6fb9\u53f0\u955c\u660e\u9053\uff1a\u201c\u597d\uff0c\u4f60\u4eec\u5148\u53bb\u3002\u201d\u7acb\u523b\u7b56\u9a6c\u98de\u5954\uff0c\u4e0e\u7948\u9547\u7684\u4e09 \u4e2a\u536b\u58eb\u540c\u8fdb\u7687\u5bab\u3002 \u3000\u3000\u6fb9\u53f0\u955c\u660e\u5df2\u4ece\u8131\u4e0d\u82b1\u53e3\u4e2d\u77e5\u9053\u5f20\u5bb6\u4e4b\u4e8b\uff0c\u7126\u6025\u975e\u5e38\uff0c\u5fc3\u4e2d\u6068\u9053\uff1a\u201c\u5f20\u4e39\u67ab\u633d\u6551\u4e86\u660e\u671d\u7684 \u6c5f\u5c71\uff0c\u8fd9\u5012\u9709\u7684\u660e\u671d\u5929\u5b50\u5374\u8981\u7d2f\u5f20\u4e39\u67ab\u9001\u4e86\u6027\u547d\uff01\u201d\u4f46\u4e91\u91cd\u51b3\u610f\u8981\u53bb\uff0c\u5979\u81ea\u662f\u96be\u4ee5\u963b\u62e6\uff0c\u53ea \u597d\u7387\u9886\u4e91\u91cd\u7684\u5341\u516b\u540d\u968f\u4ece\uff0c\u5feb\u9a6c\u75be\u5954\u3002 \u3000\u3000\u54ea\u77e5\u5728\u5927\u8857\u7684\u897f\u8fb9\uff0c\u74e6\u523a\u7684\u4eac\u5e08\u592a\u5c09\uff08\u6b66\u5b98\u540d\uff0c\u76f8\u5f53\u4e8e\u660e\u671d\u7684\u4e5d\u95e8\u63d0\u7763\uff09\u65e9\u5df2\u4e25\u9635\u76f8 \u5f85\u3002\u4e91\u91cd\u7684\u536b\u961f\u957f\u4e0a\u524d\u53eb\u9053\uff1a\u201c\u54b1\u4eec\u5949\u4e91\u5927\u4eba\u4e4b\u547d\uff0c\u524d\u5f80\u62dc\u8bbf\u4f60\u4eec\u7684\u53f3\u4e1e\u76f8\u3002\u201d\u90a3\u8499\u53e4\u592a\u5c09 \u9053\uff1a\u201c\u90a3\u4f60\u4eec\u7684\u4e91\u4f7f\u81e3\u5462\uff1f\u201d\u968f\u4ece\u9053\uff1a\u201c\u4e91\u5927\u4eba\u521a\u521a\u5949\u8bcf\u8fdb\u5bab\uff0c\u5c31\u8981\u8d76\u6765\u3002\u201d\u8499\u53e4\u592a\u5c09\u9053\uff1a \u201c\u65e2\u7136\u5982\u6b64\uff0c\u90a3\u5c31\u7b49\u4e91\u4f7f\u81e3\u6765\u4e86\u518d\u8bf4\u5427\u3002\u6211\u4eec\u5949\u547d\u4fdd\u62a4\u660e\u671d\u4f7f\u8282\uff0c\u4f60\u4eec\u7684\u4f7f\u81e3\u4e0d\u5728\uff0c\u8fd9\u62c5\u5b50 \u6211\u4eec\u53ef\u6311\u4e0d\u8d77\u3002\u201d \u3000\u3000\u8131\u4e0d\u82b1\u6084\u6084\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u51b2\u8fc7\u53bb\u3002\u201d\u53ea\u662f\u90a3\u8fb9\u8499\u53e4\u592a\u5c09\u65e9\u5df2\u4e0b\u4e86\u547d\u4ee4\uff0c\u94c1\u9a91\u6a2a\u5217\uff0c\u5f13\u7bad \u624b\u3001\u7eca\u9a6c\u7d22\u90fd\u5df2\u51c6\u5907\u505c\u5f53\uff0c\u4e25\u9635\u76f8\u5f85\u3002\u6fb9\u53f0\u955c\u660e\u4e0e\u4e91\u91cd\u7684\u968f\u4ece\u8bc6\u5f97\u5927\u4f53\uff0c\u77e5\u9053\u82e5\u7136\u786c\u51b2\uff0c\u4e8b \u60c5\u5c31\u4e0d\u53ef\u6536\u62fe\uff0c\u4e24\u56fd\u5e2e\u4ea4\uff0c\u4e5f\u8bb8\u56e0\u6b64\u7834\u88c2\u3002\u4f55\u51b5\u654c\u4f17\u6211\u5be1\uff0c\u4ea6\u672a\u5fc5\u51b2\u5f97\u8fc7\u53bb\uff0c\u6025\u5fd9\u6b62\u7740\u8131\u4e0d \u82b1\uff0c\u4ecd\u7136\u548c\u4ed6\u4eec\u8bf4\u7406\u3002\u53ef\u662f\u8499\u53e4\u592a\u5c09\u4e0b\u4e86\u547d\u4ee4\uff0c\u4fbf\u9000\u5165\u9635\u4e2d\uff0c\u4efb\u4e91\u91cd\u7684\u4f8d\u4ece\u53eb\u56b7\u4ed6\u7adf\u6beb\u4e0d\u7b54 \u7406\u3002 \u3000\u3000\u4e24\u8fb9\u50f5\u6301\u4e0d\u4e0b\uff0c\u6fb9\u53f0\u955c\u660e\u548c\u90a3\u5341\u516b\u540d\u968f\u4ece\u90fd\u6025\u5f97\u5982\u70ed\u9505\u4e0a\u7684\u8682\u8681\uff0c\u7a7a\u81ea\u5fc3\u7126\uff0c\u6beb\u65e0\u529e \u6cd5\uff0c\u770b\u6765\u53ea\u5f97\u7b49\u5019\u4e91\u91cd\u8d76\u56de\u4e86\u3002\u53ef\u662f\u4ed6\u4eec\u53ef\u7b49\uff0c\u5f20\u4e39\u67ab\u5374\u4e0d\u80fd\u7b49\u3002\u53ea\u542c\u5f97\u57ce\u697c\u4e0a\u6572\u8d77\u4e94\u66f4\uff0c \u518d\u8fc7\u4e9b\u65f6\uff0c\u5929\u8272\u5c31\u8981\u4eae\u4e86\uff01\u8131\u4e0d\u82b1\u5ffd\u7136\u5927\u53eb\u4e00\u58f0\uff0c\u9a70\u9a6c\u5411\u524d\u51b2\u53bb\uff01\u6fb9\u53f0\u955c\u660e\u60f3\u62c9\u4e5f\u62c9\u4e0d\u4f4f\uff01 \u3000\u3000\u8499\u53e4\u5175\u5ffd\u89c1\u4e00\u4e2a\u672c\u65cf\u7684\u5c11\u5973\u51b2\u6765\uff0c\u6014\u4e86\u4e00\u6014\uff0c\u5f13\u7bad\u624b\u62c9\u7740\u5f13\u5f26\uff0c\u4e0d\u6562\u653e\u7bad\uff0c\u6320\u94a9\u624b\u7684\u7eca \u9a6c\u7d22\u4e5f\u4e0d\u6562\u629b\u51fa\u53bb\u3002\u9ed1\u591c\u4e4b\u4e2d\uff0c\u521d\u65f6\u672c\u770b\u4e0d\u6e05\u695a\uff0c\u4f46\u5230\u4e86\u9635\u524d\uff0c\u5728\u677e\u679d\u706b\u628a\u7167\u8000\u4e4b\u4e0b\uff0c\u5374\u6709 \u8fc7\u534a\u6570\u7684\u5b98\u5b98\u8ba4\u5f97\u662f\u4e5f\u5148\u7684\u5973\u513f\u8131\u4e0d\u82b1\uff01\u8499\u53e4\u7684\u7537\u5973\u4e4b\u9632\u672c\u4e0d\u5982\u4e2d\u539f\u4e25\u8c28\uff0c\u8131\u4e0d\u82b1\u53c8\u597d\u9a91\u9a6c \u5c04\u7bad\uff0c\u4e0e\u8bb8\u591a\u519b\u5b98\u90fd\u5f88\u719f\u8bc6\u3002 \u3000\u3000\u90a3\u8499\u53e4\u592a\u5c09\u6025\u5fd9\u4e0a\u524d\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u5949\u4e86\u592a\u5e08\u4e4b\u547d\uff0c\u4e0d\u8bb8\u95f2\u4eba\u901a\u8fc7\u3002\u201d\u8131\u4e0d\u82b1\u67f3\u7709\u5012\u7ad6\uff0c \u65a5\u9053\uff1a\u201c\u6211\u662f\u95f2\u4eba\u4e48\uff1f\u6211\u4e5f\u662f\u5949\u4e86\u6211\u7239\u7239\u4e4b\u547d\uff0c\u4e00\u5b9a\u8981\u8fc7\uff01\u201d\u62cd\u9a6c\u76f4\u51b2\u3002\u8499\u53e4\u592a\u5c09\u89c1\u8131\u4e0d\u82b1 \u4ece\u660e\u671d\u4f7f\u8005\u90a3\u8fb9\u51b2\u8fc7\u6765\uff0c\u867d\u89c9\u6781\u4e3a\u5947\u602a\uff0c\u4f46\u8c01\u90fd\u77e5\u9053\u5979\u662f\u592a\u5e08\u7684\u7231\u5973\uff0c\u89c1\u5979\u53d1\u8d77\u6cfc\u6765\uff0c\u6a2a\u51b2 \u76f4\u95ef\uff0c\u65e0\u4eba\u6562\u52a0\u62e6\u963b\uff0c\u53ea\u597d\u4e24\u8fb9\u95ea\u5f00\u3002\u8131\u4e0d\u82b1\u51b2\u8fc7\u4e86\u91cd\u56f4\uff0c\u62ac\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u4e1c\u8fb9\u5929\u9645\uff0c\u5df2\u9732 \u51fa\u4e00\u7ebf\u66d9\u5149\uff01 \u3000\u3000\u6b64\u65f6\u5f20\u5bb6\u88ab\u56f4\uff0c\u5408\u5bb6\u4e0a\u4e0b\uff0c\u90fd\u6025\u5f97\u5982\u70ed\u9505\u4e0a\u7684\u8682\u8681\u4e00\u822c\uff0c\u53ea\u6709\u5f20\u5b97\u5468\u795e\u8272\u81ea\u5982\uff0c\u4f3c\u4e4e\u5bf9 \u751f\u6b7b\u90fd\u5df2\u4e0d\u653e\u5728\u5fc3\u4e0a\u3002\u5f20\u4e39\u67ab\u4ea6\u662f\u751a\u4e3a\u9547\u5b9a\uff0c\u4f46\u60f3\u8d77\u4e34\u7ec8\u4e4b\u524d\uff0c\u4e0d\u80fd\u89c1\u7740\u4e91\u857e\u4e00\u9762\uff0c\u5fc3\u4e2d\u5374 \u662f\u65e0\u9650\u60b2\u75db\u3002 \u3000\u3000\u8fd9\u5bb6\u4eba\u56e2\u5750\u5728\u56f4\u5899\u4e4b\u4e0b\uff0c\u56f4\u5899\u5916\u9762\u65f6\u4e0d\u65f6\u4f20\u6765\u4e86\u8499\u53e4\u5175\u53eb\u56a3\u7684\u58f0\u97f3\uff0c\u90a3\u662f\u6b7b\u4ea1\u7684\u5a01\u80c1\u3002 \u56f4\u5899\u5185\u4e00\u7247\u9759\u5bc2\uff0c\u53ea\u542c\u5f97\u6572\u4e86\u4e09\u66f4\uff0c\u4e0d\u4e45\u53c8\u6572\u4e86\u56db\u66f4\uff0c\u5317\u56fd\u7684\u51ac\u591c\u751a\u957f\uff0c\u4f46\u5728\u8fd9\u7fa4\u5728\u6b7b\u795e\u9634 \u5f71\u4e0b\u7684\u4eba\u4eec\uff0c\u5374\u611f\u89c9\u5230\u201c\u5bd2\u5bb5\u82e6\u77ed\u201d\uff01 \u3000\u3000\u65f6\u95f4\u6162\u6162\u8fc7\u53bb\uff0c\u6b7b\u4ea1\u7684\u9634\u5f71\u8d8a\u6765\u8d8a\u91cd\uff0c\u56f4\u5899\u5916\u9762\u53eb\u56a3\u7684\u58f0\u97f3\u4e5f\u8d8a\u6765\u8d8a\u5927\uff0c\u597d\u50cf\u56db\u66f4\u521a\u6572 \u4e86\u4e0d\u4e45\uff0c\u57ce\u697c\u4e0a\u53c8\u4f20\u6765\u4e86\u4e94\u66f4\u7684\u7684\u58f0\u97f3\u3002\u5f20\u4e39\u67ab\u53f9\u4e86\u53e3\u6c14\uff0c\u8dea\u5728\u7236\u4eb2\u9762\u524d\uff0c\u9053\uff1a\u201c\u7239\u7239\u8fd8\u6709 \u4ec0\u4e48\u5429\u5490\u5417\uff1f\u201d\u5f20\u5b97\u5468\u8f7b\u8f7b\u629a\u6478\u513f\u5b50\u7684\u53d1\u5934\uff0c\u542b\u7b11\u8bf4\u9053\uff1a\u201c\u82e5\u662f\u4e00\u5e74\u4e4b\u524d\u6b7b\u4e86\uff0c\u6211\u5c06\u6b7b\u4e0d\u7791 \u76ee\u3002\u5982\u4eca\u5462\uff0c\u4f60\u603b\u7b97\u4e3a\u4e2d\u56fd\u505a\u4e86\u4e00\u4e9b\u4e8b\u60c5\uff0c\u6211\u5462\uff0c\u4e5f\u51fa\u8fc7\u4e00\u70b9\u70b9\u529b\uff0c\u867d\u7136\u8fd8\u672a\u80fd\u8d4e\u7f6a\uff0c\u5fc3\u4e2d \u5374\u4e5f\u65e0\u61be\u4e86\u3002\u201d\u7b11\u5f97\u751a\u662f\u51c4\u51c9\u3002\u5f20\u4e39\u67ab\u89c1\u4ed6\u7236\u4eb2\u9762\u8272\u5947\u5f02\u4e4b\u6781\uff0c\u7981\u4e0d\u4f4f\u5fc3\u5934\u4e00\u52a8\uff0c\u4f46\u6b64\u65f6\u6b64 \u9645\uff0c\u8fd8\u6709\u4ec0\u4e48\u53ef\u95ee\uff1f\u5f20\u4e39\u67ab\u53ea\u662f\u89c9\u5f97\u5728\u4e34\u6b7b\u4e4b\u524d\uff0c\u4ed6\u7236\u4eb2\u7684\u5fc3\u610f\u548c\u81ea\u5df1\u7279\u522b\u76f8\u901a\uff0c\u4ed6\u611f\u5230\u6709 \u751f\u4ee5\u6765\u4ece\u6765\u672a\u66fe\u4e0e\u7236\u4eb2\u6709\u8fc7\u50cf\u6b64\u523b\u7684\u63a5\u8fd1\uff01 \u3000\u3000\u6fb9\u53f0\u706d\u660e\u4e5f\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e3b\u516c\uff0c\u54b1\u4eec\u4eca\u65e5\u4e92\u76f8\u544a\u8f9e\uff01\u201d\u5411\u5f20\u5b97\u5468\u62dc\u4e86\u4e09\u62dc\u3002\u4ed6\u5fc3\u610f\u5df2 \u51b3\uff0c\u8981\u5728\u654c\u4eba\u7684\u70ae\u5f39\u6765\u5230\u4e4b\u524d\u5c31\u6a2a\u94a9\u81ea\u520e\u3002\u8fd9\u65f6\u5df2\u6572\u4e86\u4e94\u66f4\uff0c\u518d\u8fc7\u7247\u523b\u5929\u8272\u5c31\u8981\u4eae\u4e86\uff01 \u3000\u3000\u5ffd\u542c\u5f97\u5916\u9762\u4e00\u9635\u53eb\u58f0\uff0c\u6fb9\u53f0\u706d\u660e\u9053\uff1a\u201c\u5929\u8fd8\u672a\u4eae\u4ed6\u4eec\u5c31\u8981\u70ae\u8f70\u4e86\uff1f\u201d\u53cc\u94a9\u4e00\u6a2a\uff0c\u5f20\u4e39\u67ab \u9053\uff1a\u201c\u5440\uff0c\u4e0d\u50cf\uff01\u201d\u6fb9\u53f0\u706d\u660e\u505c\u4e0b\u53cc\u94a9\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\u4e0d\u50cf\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u597d\u50cf\u662f\u6709\u4ec0\u4e48\u4eba \u6765\u4e86\u3002\u54a6\uff0c\u6765\u4eba\u6b63\u5728\u548c\u4ed6\u4eec\u25a1\u6740\uff01\u201d\u8df3\u4e0a\u5899\u5934\u4e00\u671b\uff0c\u53ea\u89c1\u534a\u91cc\u4e4b\u5916\uff0c\u6709\u4e09\u5339\u5065\u9a6c\u51b2\u5165\u540e\u9635\uff0c \u56f4\u5728\u524d\u9762\u7684\u8499\u53e4\u5175\u4e5f\u7981\u4e0d\u4f4f\u9a9a\u52a8\u8d77\u6765\uff0c\u53ea\u662f\u90a3\u5c0a\u7ea2\u8863\u5927\u70ae\u8fd8\u5bf9\u51c6\u81ea\u5df1\u7684\u5bb6\u95e8\u3002 \u3000\u3000\u989d\u5409\u591a\u5e26\u6765\u7684\u6b66\u58eb\u90fd\u662f\u767e\u91cc\u9009\u4e00\u7684\u7cbe\u9510\uff0c\u4e2a\u4e2a\u80fd\u62c9\u4e94\u767e\u5f3a\u5f13\uff0c\u4e00\u58f0\u4ee4\u4e0b\uff0c\u5343\u7bad\u5982\u8757\uff0c\u7eb7 \u7eb7\u5411\u90a3\u4e09\u9a91\u5065\u9a6c\u5c04\u53bb\u3002\u53ea\u542c\u5f97\u547c\u559d\u58f0\u4e2d\uff0c\u6218\u9a6c\u72c2\u5636\uff0c\u8fdc\u8fdc\u671b\u53bb\uff0c\u53ea\u89c1\u90a3\u4e09\u5339\u9a6c\u8df3\u8d77\u4e00\u4e08\u6765 \u9ad8\uff0c\u9a6c\u8179\u9a6c\u80cc\u90fd\u88ab\u5229\u7bad\u6d1e\u7a7f\uff0c\u9a6c\u8eab\u5168\u88ab\u9c9c\u8840\u67d3\u7ea2\uff0c\u72c2\u5636\u8df3\u8dc3\uff0c\u5ffd\u7136\u56db\u8e44\u4e00\u5c48\uff0c\u8df3\u7ffb\u5730\u4e0b\u3002\u90a3 \u4e09\u4e2a\u9a91\u58eb\u9a91\u672f\u7cbe\u7edd\uff0c\u53ea\u89c1\u4ed6\u4eec\u4e00\u4e2a\u7b4b\u6597\uff0c\u5728\u9a6c\u80cc\u4e0a\u51cc\u7a7a\u98de\u8d77\uff0c\u500f\u5ffd\u4e4b\u95f4\uff0c\u98de\u8d77\u4e00\u7247\u7eff\u5149\uff0c\u8ddf \u7740\u4e00\u56e2\u767d\u5149\uff0c\u4e00\u9053\u9752\u5149\u4e5f\u4ea4\u53c9\u98de\u8d77\uff0c\u5229\u7bad\u4e00\u8fd1\uff0c\u4fbf\u7eb7\u7eb7\u5815\u5730\uff0c\u5f20\u4e39\u67ab\u8fd9\u65f6\u624d\u770b\u5f97\u6e05\u695a\uff0c\u6765\u7684 \u4e09\u4eba\u6b63\u662f\u8f70\u5929\u96f7\u77f3\u82f1\u548c\u9ed1\u767d\u6469\u8bc3\uff01\u9ed1\u6469\u8bc3\u6325\u52a8\u7eff\u7389\u6756\uff0c\u767d\u6469\u8bc3\u6325\u52a8\u767d\u7389\u6756\uff0c\u77f3\u82f1\u6325\u52a8\u9752\u94a2 \u5251\uff0c\u821e\u5230\u6025\u65f6\uff0c\u4fbf\u53ea\u89c1\u7eff\u5149\u3001\u767d\u5149\u3001\u9752\u5149\u4e09\u4e2a\u5149\u7403\uff0c\u76f4\u51b2\u654c\u9635\u3002 \u3000\u3000\u8499\u53e4\u6b66\u58eb\u7eb7\u7eb7\u5835\u622a\uff0c\u9ed1\u767d\u6469\u8bc3\u4e00\u58f0\u6012\u543c\uff0c\u6325\u6756\u4e71\u6253\uff0c\u6253\u5f97\u4eba\u4ef0\u9a6c\u7ffb\uff0c\u6709\u4e9b\u8f7b\u529f\u8f83\u597d\u7684\uff0c \u8dcc\u7ffb\u4e4b\u540e\uff0c\u4ecd\u7136\u51b2\u4e0a\uff0c\u5374\u53c8\u88ab\u77f3\u82f1\u5251\u6233\u638c\u5288\uff0c\u7b80\u76f4\u8fd1\u4e0d\u4e86\u8eab\u3002\u8fd9\u4e09\u4eba\u6a2a\u51b2\u76f4\u649e\uff0c\u9510\u4e0d\u53ef\u6321\uff0c \u773c\u770b\u5c31\u51b2\u5230\u4e2d\u592e\u3002\u767d\u5c71\u6cd5\u5e08\u5927\u6012\uff0c\u62a2\u4e0a\u524d\u53bb\u515c\u62e6\uff0c\u7b2c\u4e00\u4e2a\u78b0\u7740\u77f3\u82f1\uff0c\u767d\u5c71\u6cd5\u5e08\u4e00\u62db\u201c\u72ec\u5288\u534e \u5c71\u201d\u7897\u53e3\u822c\u7c97\u5927\u7684\u7985\u6756\u5f53\u5934\u626b\u4e0b\u3002\u8fd9\u767d\u5c71\u6cd5\u5e08\u4e43\u662f\u9752\u8c37\u6cd5\u5e08\u7684\u5e08\u5144\uff0c\u6b66\u529f\u5728\u989d\u5409\u591a\u4e4b\u4e0a\uff0c\u8fd9 \u4e00\u6756\u4e4b\u529b\uff0c\u8db3\u6709\u5343\u65a4\uff0c\u5251\u6756\u76f8\u4ea4\uff0c\u5f53\u7684\u4e00\u58f0\uff0c\u98de\u8d77\u4e00\u7bf7\u706b\u661f\uff0c\u767d\u5c71\u6cd5\u5e08\u5927\u559d\u4e00\u58f0\u201c\u5012\u4e0b\uff01\u201d \u7985\u6756\u529b\u538b\uff0c\u77f3\u82f1\u8eab\u8eaf\u5fae\u5fae\u4e00\u6643\uff0c\u5ffd\u5730\u7b11\u9053\uff1a\u201c\u4e0d\u89c1\u5f97\uff01\u201d\u624b\u8155\u4e00\u7ffb\uff0c\u9752\u94a2\u5251\u7a81\u7136\u8131\u4e86\u51fa\u6765\uff0c \u626c\u7a7a\u4e00\u95ea\uff0c\u8f6c\u950b\u4fbf\u6233\u767d\u5c71\u6cd5\u5e08\u7684\u80a9\u80cc\u3002\u767d\u5c71\u6cd5\u5e08\u81ea\u6043\u6c14\u529b\u8fc7\u4eba\uff0c\u5374\u4e0d\u6599\u9002\u624d\u90a3\u4e00\u6756\u5e76\u672a\u5c06\u654c \u4eba\u6253\u7ffb\uff0c\u5251\u6756\u76f8\u4ea4\uff0c\u81ea\u5df1\u7684\u864e\u53e3\u4e5f\u9690\u9690\u53d1\u75bc\uff0c\u6b63\u5728\u5403\u60ca\uff0c\u7a81\u7136\u95f4\u53ea\u89c1\u5251\u5149\uff0c\u4e0d\u89c1\u4eba\u5f71\uff0c\u654c\u4eba \u610f\u5df2\u8f6c\u5230\u4e86\u81ea\u5df1\u7684\u80cc\u540e\u53d1\u62db\u3002\u77f3\u82f1\u4ee5\u98de\u8757\u77f3\u3001\u60ca\u96f7\u638c\u3001\u8e51\u4e91\u5251\u4e09\u7edd\u9a70\u540d\u6b66\u6797\uff0c\u5c24\u5176\u662f\u8e51\u5251 \u6cd5\uff0c\u98d8\u5ffd\u5f02\u5e38\uff0c\u6700\u4e3a\u96be\u654c\u3002\u767d\u5c71\u6cd5\u5e08\u95ea\u5f00\u4e24\u5251\uff0c\u6b63\u5728\u5012\u8f6c\u6756\u5934\uff0c\u60f3\u6321\u5f00\u4ed6\u7684\u7b2c\u4e09\u5251\uff0c\u53ea\u542c\u5f97 \u77f3\u82f1\u5927\u559d\u4e00\u58f0\u201c\u7740\uff01\u201d\u9752\u94a2\u5251\u5728\u7985\u6756\u4e0a\u4e00\u78b0\uff0c\u9aa4\u5730\u53cd\u5f39\u8d77\u6765\uff0c\u53cd\u624b\u4e00\u5251\uff0c\u5728\u767d\u5c71\u6cd5\u5e08\u7684\u80a9\u5934 \u5212\u4e86\u4e00\u9053\u4f24\u53e3\u3002\u767d\u5c71\u6cd5\u5e08\u7ec3\u6709\u4e00\u8eab\u201c\u94c1\u5e03\u886b\u201d\u7684\u529f\u592b\uff0c\u4e2d\u4e86\u4e00\u5251\u5c45\u7136\u4e0d\u5012\uff0c\u7985\u6756\u5728\u5730\u4e0a\u4e00\u70b9 \u8dc3\u51fa\u4e00\u4e08\u5f00\u5916\uff0c\u62a1\u6756\u7ffb\u8eab\uff0c\u5c1a\u6b32\u25a1\u6740\uff0c\u77f3\u82f1\u65e9\u5df2\u51b2\u5165\u9635\u4e2d\u53bb\u4e86\u3002 \u3000\u3000\u767d\u5c71\u6cd5\u5e08\u6012\u543c\u5982\u96f7\uff0c\u5ffd\u542c\u5f97\u4e00\u58f0\u559d\u9053\uff1a\u201c\u8d3c\u25a1\u4e4c\u9b3c\u53eb\u5f97\u8ba8\u538c\uff0c\u5403\u6211\u4e00\u6756\uff01\u201d\u767d\u5c71\u6cd5\u5e08\u6b63 \u81ea\u53d1\u706b\uff0c\u89c1\u9ed1\u6469\u8bc3\u75be\u5954\u800c\u6765\uff0c\u5927\u543c\u4e00\u58f0\uff0c\u7985\u6756\u62e6\u8170\u4fbf\u626b\u3002\u54ea\u77e5\u811a\u6b65\u521a\u8d77\uff0c\u9ed1\u6469\u8bc3\u5df2\u5230\u4e86\u8ddf \u524d\uff0c\u7eff\u7389\u6756\u4e00\u6311\uff0c\u6709\u5982\u94c1\u68d2\u51fb\u51b2\uff0c\u55e1\u7684\u4e00\u58f0\u5de8\u54cd\uff0c\u767d\u5c71\u6cd5\u5e08\u7684\u7985\u6756\u8131\u624b\u98de\u5230\u534a\u7a7a\uff0c\u5413\u5f97\u9b42\u9b44 \u9f50\u98de\u3002\u4ed6\u81ea\u5df1\u4ee5\u4e3a\u6c14\u529b\u60ca\u4eba\uff0c\u54ea\u77e5\u9ed1\u6469\u8bc3\u6bd4\u4ed6\u8fd8\u8981\u5389\u5bb3\uff0c\u773c\u89c1\u9ed1\u6469\u8bc3\u7b2c\u4e8c\u6756\u5df2\u6253\u4e0b\uff0c\u767d\u5c71\u6cd5 \u5e08\u54ea\u91cc\u6562\u63a5\uff0c\u6025\u5fd9\u659c\u8dc3\u6570\u6b65\uff0c\u6070\u649e\u5230\u767d\u6469\u8bc3\u9762\u524d\u3002\u767d\u6469\u8bc3\u9a82\u9053\uff1a\u201c\u8d3c\u25a1\u4e4c\uff0c\u9633\u5173\u6709\u8def\u4f60\u4e0d \u8d70\uff0c\u5730\u72f1\u65e0\u95e8\u4f60\u649e\u8fdb\u6765\uff0c\u4f60\u65e2\u649e\u5230\u6211\u7684\u8ddf\u524d\uff0c\u4e14\u5403\u6211\u4e00\u6756\uff01\u201d\u547c\u7684\u4e00\u58f0\uff0c\u987a\u624b\u4e00\u6756\uff0c\u5c06\u767d\u5c71 \u6cd5\u5e08\u6253\u7ffb\uff0c\u4e24\u6761\u817f\u90fd\u9f50\u6839\u65ad\u4e86\u3002 \u3000\u3000\u77f3\u82f1\u51b2\u5165\u9635\u4e2d\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u9ed1\u77f3\u5e84\u4e16\u88ad\u9f99\u9a91\u90fd\u5c09\u77f3\u82f1\u6c42\u89c1\u4e3b\u516c\uff01\u201d\u539f\u6765\u77f3\u82f1\u7684\u5148\u7956\u662f \u5f20\u58eb\u8bda\u7684\u4eb2\u4fe1\u536b\u58eb\uff0c\u88ab\u5c01\u4e3a\u201c\u9f99\u9a91\u90fd\u5c09\u201d\u4e4b\u804c\uff0c\u800c\u4eca\u77f3\u82f1\u6765\u5230\uff0c\u4ecd\u7136\u63a5\u7167\u4ee5\u524d\u7687\u5ba4\u7684\u4e3b\u4ec6\u4e4b \u793c\u901a\u540d\u7980\u62a5\uff0c\u6c42\u89c1\u5f20\u5b97\u5468\u3002\u5f20\u5b97\u5468\u70ed\u6cea\u76c8\u7736\uff0c\u6276\u7740\u513f\u5b50\u7684\u80a9\uff0c\u8d70\u4e0a\u56f4\u5899\uff0c\u8bf4\u9053\uff1a\u201c\u67ab\u513f\uff0c\u4f60 \u53eb\u4ed6\u5feb\u8d70\u5427\uff01\u201d\u9ed1\u767d\u6469\u8bc3\u4e5f\u53eb\u9053\uff1a\u201c\u5f20\u4e39\u67ab\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u51b2\u51fa\u6765\uff1f\u8001\u670b\u53cb\u6765\u4e86\u4f60\u4e5f\u4e0d\u51fa\u6765\u63a5 \u4e48\uff1f\u201d \u3000\u3000\u5f20\u4e39\u67ab\u4e00\u58f0\u82e6\u7b11\uff0c\u6b63\u6b32\u8bf4\u8bdd\uff0c\u9661\u7136\u95f4\uff0c\u5ffd\u89c1\u5305\u56f4\u4ed6\u5bb6\u7684\u6b66\u58eb\u5206\u5f00\u4e24\u8fb9\uff0c\u73b0\u51fa\u4e00\u6761\u901a\u9053\uff0c \u90a3\u5c0a\u7ea2\u8863\u5927\u70ae\u9002\u624d\u88ab\u4eba\u5899\u6321\u4f4f\uff0c\u800c\u4eca\u4e5f\u663e\u9732\u4e86\u51fa\u6765\u3002\u77f3\u82f1\u89c1\u4e86\u5927\u5403\u4e00\u60ca\uff0c\u53ea\u542c\u5f97\u989d\u5409\u591a\u5927\u53eb \u9053\uff1a\u201c\u4f60\u4eec\u518d\u4e0a\u524d\u4e00\u6b65\uff0c\u6211\u5c31\u5f00\u70ae\uff01\u201d\u989d\u5409\u591a\u542c\u4ed6\u4eec\u7684\u79f0\u547c\uff0c\u77e5\u9053\u4ed6\u4eec\u4e0e\u5f20\u4e39\u67ab\u7236\u5b50\u7684\u5173 \u7cfb\uff0c\u6599\u4ed6\u4eec\u4e0d\u6562\u8ba9\u5f20\u5bb6\u6bc1\u70ae\u706b\uff0c\u6545\u6b64\u7acb\u65bd\u606b\u5413\u3002 \u3000\u3000\u5176\u5b9e\u90a3\u7ea2\u8863\u5927\u70ae\uff0c\u8f6c\u79fb\u4e0d\u4fbf\uff0c\u7edd\u4e0d\u80fd\u6253\u5230\u9ed1\u767d\u6469\u8bc3\u4ed6\u4eec\uff1b\u800c\u5176\u65f6\u521a\u6253\u8fc7\u4e94\u66f4\u4e0d\u4e45\uff0c\u5929\u5c1a \u672a\u4eae\uff0c\u989d\u5409\u591a\u4ea6\u4e0d\u6562\u5411\u5f20\u5bb6\u5f00\u70ae\uff0c\u53ea\u8981\u9ed1\u767d\u6469\u8bc3\u4e0e\u77f3\u82f1\u51b2\u4e0a\uff0c\u5f20\u5bb6\u4e4b\u56f4\u7acb\u89e3\u3002\u53ef\u662f\u5f20\u4e39\u67ab\u4e0e \u77f3\u82f1\u7b49\u4eba\u90fd\u4e0d\u77e5\u5176\u4e2d\u7684\u5fae\u5999\u5173\u7cfb\uff0c\u5c24\u5176\u662f\u77f3\u82f1\uff0c\u89c1\u90a3\u5c0a\u5927\u70ae\u5bf9\u51c6\u5f20\u5bb6\uff0c\u66f4\u662f\u4e0d\u6562\u52a8\u624b\u3002 \u3000\u3000\u9ed1\u767d\u6469\u8bc3\u6c14\u5f97\u54c7\u54c7\u5927\u53eb\uff0c\u7528\u5370\u5ea6\u65b9\u8a00\u53fd\u54e9\u5495\u565c\u7684\u4e71\u9a82\uff0c\u53ef\u4ea6\u4e0d\u6562\u5411\u524d\u79fb\u52a8\u534a\u6b65\u3002\u989d\u5409\u591a \u54c8\u54c8\u5927\u7b11\uff0c\u9a6c\u5200\u4e00\u6307\uff0c\u559d\u9053\uff1a\u201c\u90fd\u7ed9\u6211\u9000\u5230\u767e\u6b65\u4e4b\u5916\uff0c\u5426\u5219\u5f00\u70ae\uff01\u201d\u77f3\u82f1\u4e0e\u9ed1\u767d\u6469\u8bc3\u65e0\u53ef\u5948 \u4f55\uff0c\u53ea\u597d\u4f9d\u8a00\u9000\u51fa\u767e\u6b65\u4e4b\u5916\uff0c\u989d\u5409\u591a\u7acb\u523b\u547d\u4eba\u5728\u7a7a\u5730\u4e0a\u6492\u4e0b\u5c16\u89d2\u6bd2\u84ba\u85dc\uff0c\u7559\u4e0b\u4e00\u767e\u540d\u5f13\u7bad\u624b \u642d\u597d\u5f13\u5f26\uff0c\u5bf9\u51c6\u4ed6\u4eec\uff0c\u77f3\u82f1\u7b49\u4e09\u4eba\u672c\u4e8b\u518d\u597d\uff0c\u4e5f\u4e0d\u80fd\u540c\u65f6\u4e0a\u6321\u5f13\u7bad\uff0c\u4e0b\u626b\u84ba\u85dc\uff0c\u773c\u7741\u7741\u5730\u770b \u7740\u654c\u4eba\u5e03\u7f6e\uff0c\u5fc3\u4e2d\u4e03\u4e0a\u516b\u843d\u3002 \u3000\u3000\u7693\u6708\u897f\u6c89\uff0c\u758f\u661f\u6e10\u9690\uff0c\u4e1c\u65b9\u5929\u9645\uff0c\u5148\u662f\u9732\u51fa\u4e00\u7ebf\u66d9\u5149\uff0c\u4e0d\u4e45\u5c31\u4ece\u9ed1\u6c89\u6c89\u7684\u4e91\u5e55\u4e2d\u900f\u51fa\u5149 \u4eae\uff0c\u6d6e\u4e91\u56db\u5c55\uff0c\u4ece\u9ed1\u8272\u53d8\u4e3a\u7070\u767d\uff0c\u4e0d\u4e45\u53c8\u4ece\u7070\u767d\u8272\u7684\u4e91\u6735\u4e2d\u900f\u51fa\u4e00\u7247\u6a59\u8272\u7684\u5149\u8292\uff0c\u9ed1\u591c\u5df2 \u901d\uff0c\u671d\u9633\u521d\u5347\uff0c\u5929\u8272\u5df2\u7ecf\u5927\u4eae\u4e86\u3002 \u3000\u3000\u989d\u5409\u591a\u6602\u5934\u7741\u76ee\uff0c\u5bf9\u7740\u5899\u5934\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u5982\u4f55\uff1f\u201d\u5f20\u4e39\u67ab\u795e\u8272\u81ea\u5982\uff0c\u51b7\u51b7\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u6709\u751a\u5982\u4f55\uff1f\u6211\u867d\u6b7b\u72b9\u751f\uff0c\u4f60\u751f\u4e0d\u5982\u6b7b\uff01\u201d\u989d\u5409\u591a\u9053\uff1a\u201c\u5f20\u4e39\u67ab\uff0c\u4f60\u6267\u8ff7\u4e0d\u609f\uff0c\u6211\u53ea\u6709\u5f00\u70ae \u4e86\uff01\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u5c3d\u7ba1\u5f00\u70ae\uff0c\u4e0d\u5fc5\u591a\u8a00\uff01\u201d\u989d\u5409\u591a\u9053\uff1a\u201c\u6211\u73b0\u5728\u4ece\u4e00\u6570\u81f3\u5341\uff0c\u5230\u6570\u81f3\u5341 \u65f6\uff0c\u7acb\u5373\u5f00\u70ae\u3002\u877c\u8681\u5c1a\u4e14\u8d2a\u751f\uff0c\u4f60\u4ed4\u7ec6\u60f3\u60f3\u3002\u201d\u5f20\u4e39\u67ab\u9119\u5937\u4e00\u7b11\uff0c\u8df3\u4e0b\u5899\u5934\uff0c\u6839\u672c\u4e0d\u4e88\u7406 \u4f1a\u3002 \u3000\u3000\u970e\u65f6\u95f4\u5899\u5916\u5899\u5185\u4e00\u7247\u9759\u5bc2\uff0c\u989d\u5409\u591a\u9ad8\u58f0\u6570\u9053\uff1a\u201c\u4e00\u3001\u4e8c\u3001\u4e09\u3001\u56db\u2014\u2014\u201d\u5f20\u4e39\u67ab\u7d27\u7d27\u63e1\u7740 \u7236\u4eb2\u7684\u624b\uff0c\u6fb9\u53f0\u706d\u660e\u5012\u8f6c\u5434\u94a9\uff0c\u5c16\u5203\u5bf9\u51c6\u80f8\u53e3\uff0c\u6c89\u91cd\u51dd\u51b7\u7684\u7a7a\u6c14\u4e2d\u7ee7\u7eed\u4f20\u6765\u6570\u76ee\u5b57\u7684\u547c\u58f0\uff1a \u201c\u4e94\u3001\u516d\u3001\u4e03\u3001\u516b\u2014\u2014\u4e5d\u2014\u2014\u201d\u6fb9\u53f0\u706d\u660e\u5434\u94a9\u4e00\u62c9\uff0c\u4ed6\u4ee5\u5927\u5c06\u7684\u8eab\u5206\uff0c\u53ea\u80fd\u81ea\u6740\uff0c\u4e0d\u80fd\u88ab \u6740\uff0c\u94a9\u5c16\u5d4c\u5165\u8089\u5185\uff0c\u53ea\u8981\u518d\u7528\u529b\u4e00\u62c9\uff0c\u7acb\u523b\u4fbf\u8981\u819b\u5f00\u8179\u88c2\u3002\u201c\u4e5d\u201d\u5b57\u4e4b\u540e\uff0c\u4e45\u4e45\u65e0\u58f0\uff0c\u5ffd\u542c \u5916\u9762\u4e00\u58f0\u5c16\u53eb\u201c\u4e0d\u51c6\u5f00\u70ae\uff01\u201d \u3000\u3000\u6fb9\u53f0\u706d\u660e\u9053\uff1a\u201c\u54a6\uff0c\u662f\u4e00\u4e2a\u5973\u5b50\uff01\u201d\u4e0e\u5f20\u4e39\u67ab\u8df3\u4e0a\u5899\u5934\uff0c\u53ea\u89c1\u5728\u7ea2\u8863\u5927\u70ae\u7684\u65c1\u8fb9\uff0c\u4e00\u4e2a \u8499\u53e4\u5c11\u5973\u6b63\u7528\u5200\u6307\u7740\u70ae\u624b\uff0c\u5f20\u4e39\u67ab\u4f4e\u4f4e\u53eb\u4e86\u4e00\u58f0\uff1a\u201c\u662f\u8131\u4e0d\u82b1\uff01\u201d\u8131\u4e0d\u82b1\u62ac\u8d77\u5934\uff0c\u5ae3\u7136\u4e00 \u7b11\uff0c\u53ea\u89c1\u5979\u82b1\u5bb9\u4e0d\u6574\uff0c\u4e91\u9b13\u84ec\u4e71\uff0c\u5934\u4e0a\u7684\u7389\u9497\u6447\u6447\u6b32\u5760\uff0c\u663e\u89c1\u662f\u4ed3\u7687\u8d76\u5230\u3002 \u3000\u3000\u989d\u5409\u591a\u5706\u7741\u53cc\u76ee\uff0c\u9053\uff1a\u201c\u4e0d\u51c6\u653e\u70ae\uff0c\u662f\u8c01\u8bf4\u7684\uff1f\u201d\u8131\u4e0d\u82b1\u9053\uff1a\u201c\u4f60\u8033\u6735\u804b\u5417\uff1f\u542c\u4e0d\u6e05 \u695a\uff1f\u662f\u6211\u8bf4\u7684\uff01\u201d\u989d\u5409\u591a\u662f\u4e5f\u5148\u7684\u5bb6\u5c06\uff0c\u5e73\u65f6\u5bf9\u8131\u4e0d\u82b1\u5949\u627f\u5f97\u552f\u6050\u4e0d\u5468\uff0c\u8131\u4e0d\u82b1\u81ea\u4ee5\u4e3a\u53ef\u5c06 \u4ed6\u9547\u4f4f\uff0c\u54ea\u6599\u989d\u5409\u591a\u65e9\u5f97\u4e86\u4e5f\u5148\u7684\u5429\u5490\uff0c\u8c01\u4e5f\u4e0d\u8bb8\u963b\u62e6\uff0c\u53ea\u89c1\u4ed6\u606d\u606d\u656c\u656c\u5730\u5bf9\u8131\u4e0d\u82b1\u65bd\u4e86\u4e00 \u793c\uff0c\u9053\uff1a\u201c\u542c\u6e05\u695a\u4e86\uff0c\u8bf7\u90e1\u4e3b\u95ea\u5f00\uff01\u201d\u9661\u5730\u5927\u58f0\u559d\u9053\uff1a\u201c\u5f00\u70ae\uff01\u201d \u3000\u3000\u8131\u4e0d\u82b1\u6c14\u5f97\u67f3\u7709\u5012\u7ad6\uff0c\u559d\u9053\uff1a\u201c\u8c01\u653e\u70ae\u6211\u5c31\u628a\u8c01\u65ab\u4e86\uff01\u989d\u5409\u591a\u4f60\u6562\u4e0d\u542c\u6211\u7684\u8bdd\uff1f\u201d\u90a3\u70ae \u624b\u4e00\u9635\u8fdf\u7591\uff0c\u62ff\u7740\u706b\u7ef3\u7684\u624b\u98a4\u98a4\u6296\u6296\uff0c\u4e0d\u6562\u71c3\u70b9\u3002\u989d\u5409\u591a\u6de1\u6de1\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u8981\u542c\u592a\u5e08\u7684 \u8bdd\uff01\u201d\u8131\u4e0d\u82b1\u9053\uff1a\u201c\u6211\u7236\u4eb2\u53eb\u6211\u8d76\u6765\uff0c\u5c31\u662f\u8981\u5429\u5490\u4f60\u4eec\u8fd9\u4e00\u53e5\u8bdd\uff0c\u4e0d\u51c6\u5f00\u70ae\uff01\u201d\u8fd9\u53e5\u8bdd\u82e5\u7136 \u662f\u8131\u4e0d\u82b1\u4e00\u6765\u5230\u4fbf\u5982\u6b64\u8bf4\uff0c\u4e5f\u8bb8\u80fd\u5c06\u989d\u5409\u591a\u9a97\u8fc7\uff0c\u6b64\u9645\u989d\u5409\u591a\u542c\u4e86\u5979\u98a4\u6296\u7684\u8bed\u8c03\uff0c\u770b\u4e86\u5979\u60f6 \u6025\u7684\u795e\u60c5\uff0c\u5374\u7edd\u4e0d\u76f8\u4fe1\uff0c\u53ea\u89c1\u4ed6\u53c8\u5bf9\u8131\u4e0d\u82b1\u65bd\u4e86\u4e00\u793c\uff0c\u606d\u606d\u656c\u656c\u5730\u8bf4\u9053\uff1a\u201c\u90a3\u4e48\u592a\u5e08\u7684\u624b\u8c15 \u5462\uff1f\u201d\u8131\u4e0d\u82b1\u65a5\u9053\uff1a\u201c\u6211\u662f\u4ed6\u7684\u5973\u513f\uff0c\u8981\u4ec0\u4e48\u624b\u8c15\uff1f\u201d\u989d\u5409\u591a\u5f2f\u8170\u97a0\u4e86\u4e2a\u8eac\uff0c\u9053\uff1a\u201c\u4e0d\u89c1\u624b \u8c15\uff0c\u6055\u6211\u4e0d\u6562\u63a5\u65e8\uff0c\u8bf7\u90e1\u4e3b\u95ea\u5f00\u3002\u201d\u5927\u58f0\u559d\u9053\uff1a\u201c\u653e\u70ae\uff01\u518d\u4e0d\u653e\u6211\u5c31\u5148\u628a\u4f60\u65ab\u4e86\uff01\u201d \u3000\u3000\u90a3\u70ae\u624b\u624b\u98a4\u811a\u9707\uff0c\u64e6\u71c3\u706b\u77f3\uff0c\u5411\u706b\u7ef3\u4e00\u70b9\uff0c\u5ffd\u89c1\u4e00\u6761\u9ed1\u5f71\uff0c\u7a81\u7136\u6251\u81f3\uff0c\u559d\u9053\uff1a\u201c\u4f60\u9053\u6211 \u4e0d\u6562\u65ab\u4f60\uff01\u201d\u624b\u8d77\u5200\u843d\uff0c\u90a3\u70ae\u624b\u8fd8\u672a\u53eb\u5f97\u51fa\u58f0\uff0c\u7adf\u88ab\u8131\u4e0d\u82b1\u4e00\u5200\u65ab\u4e86\u3002\u8131\u4e0d\u82b1\u968f\u624b\u637b\u7184\u706b \u7ef3\uff0c\u5c06\u8eab\u5b50\u5835\u7740\u70ae\u53e3\uff0c\u6c14\u547c\u547c\u7684\u53eb\u9053\uff1a\u201c\u8c01\u6562\u4e0a\u6765\uff0c\u6211\u5c31\u628a\u8c01\u65ab\u4e86\uff01\u201d \u3000\u3000\u989d\u5409\u591a\u4e07\u4e07\u6599\u60f3\u4e0d\u5230\u8131\u4e0d\u82b1\u7adf\u7136\u5982\u6b64\u6492\u6cfc\uff0c\u5f53\u771f\u505a\u4e86\u51fa\u6765\uff0c\u4e00\u65f6\u95f4\u5012\u6ca1\u4e86\u4e3b\u610f\u3002\u4ed6\u6b66\u529f \u867d\u6bd4\u8131\u4e0d\u82b1\u9ad8\u5f97\u4e0d\u77e5\u591a\u5c11\uff0c\u4f46\u8131\u4e0d\u82b1\u7a76\u7adf\u662f\u91d1\u679d\u7389\u53f6\uff0c\u4ed6\u600e\u6562\u53bb\u78b0\u5979\u4e00\u4e0b\uff01 \u3000\u3000\u6b63\u5728\u50f5\u6301\uff0c\u5ffd\u89c1\u4e00\u9a91\u9a6c\u5982\u98de\u5954\u81f3\uff0c\u9a6c\u4e0a\u4eba\u4e00\u8df3\u4e0b\u6765\uff0c\u5c31\u5927\u58f0\u559d\u9053\uff1a\u201c\u4e3a\u4f55\u8fd8\u4e0d\u653e\u70ae\uff01\u201d \u8fd9\u4eba\u6b63\u662f\u592a\u5e08\u5e9c\u7684\u603b\u7ba1\u7a9d\u624e\u5408\u3002\u989d\u5409\u591a\u9053\uff1a\u201c\u90e1\u4e3b\u4e0d\u8bb8\uff01\u201d\u7a9d\u624e\u5408\u6ee1\u9762\u6740\u6c14\uff0c\u5927\u58f0\u8bf4\u9053\uff1a \u201c\u592a\u5e08\u4eb2\u53e3\u5429\u5490\uff0c\u4e0d\u8bba\u662f\u8c01\uff0c\u82e5\u6562\u963b\u62e6\uff0c\u90fd\u53ef\u4ee5\u628a\u4ed6\u6740\u6389\uff01\u8fd9\u662f\u624b\u4ee4\uff01\u201d\u624b\u4ee4\u4e0a\u5199\u5f97\u5206\u660e\uff0c \u5373\u4f7f\u628a\u4ed6\u7684\u5973\u513f\u6740\u4e86\uff0c\u4e5f\u662f\u6709\u529f\u65e0\u7f6a\u3002 \u3000\u3000\u989d\u5409\u591a\u80c6\u6c14\u987f\u58ee\uff0c\u9053\uff1a\u201c\u9ebb\u7ffc\u8d5e\uff0c\u4f60\u4e0a\u53bb\u628a\u90e1\u4e3b\u8bf7\u5f00\uff01\u201d\u8131\u4e0d\u82b1\u72c2\u53eb\u9053\uff1a\u201c\u8c01\u6562\u4e0a \u6765\uff1f\u201d\u62ab\u5934\u6563\u53d1\uff0c\u7389\u9497\u6a2a\u5760\uff0c\u5982\u75af\u5982\u72c2\u3002\u7a9d\u624e\u5408\u8fc8\u524d\u4e00\u6b65\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u90e1\u4e3b\u4f60\u542c\u6e05\u695a\u4e86\uff0c \u8d76\u5feb\u79bb\u5f00\uff0c\u4e0d\u53ef\u56fa\u6267\uff0c\u592a\u5e08\u53eb\u4f60\u4e0e\u6211\u56de\u5bb6\u3002\u201d \u3000\u3000\u8131\u4e0d\u82b1\u7a81\u7136\u54c7\u7684\u4e00\u58f0\u54ed\u4e86\u51fa\u6765\uff0c\u5979\u4f24\u5fc3\u5df2\u6781\uff0c\u4e0d\u5355\u662f\u4e3a\u4e86\u5f20\u4e39\u67ab\uff0c\u800c\u662f\u7b2c\u4e00\u6b21\u77e5\u9053\u7236\u4eb2 \u662f\u600e\u6837\u5bf9\u5979\u3002\u5979\u662f\u4e5f\u5148\u7684\u72ec\u751f\u5973\u513f\uff0c\u4e5f\u5148\u5e73\u7d20\u5bf9\u5979\u5343\u4f9d\u767e\u987a\uff0c\u51e0\u4e4e\u662f\u5979\u8981\u5929\u4e0a\u7684\u6708\u4eae\u4ed6\u4e5f\u7b54 \u5e94\u4e3a\u5979\u62ff\u4e0b\uff0c\u54ea\u77e5\u5230\u4e86\u8fd9\u4e2a\u5173\u5934\uff0c\u5979\u7236\u4eb2\u7adf\u7136\u5429\u5490\u5bb6\u5c06\uff0c\u8fd8\u5f53\u4f17\u5ba3\u5e03\uff0c\u8bf4\u662f\u53ef\u4ee5\u5c06\u5979\u6740\u6389\u3002 \u5979\u4e07\u4e07\u6599\u4e0d\u5230\u7236\u4eb2\u8fd9\u6837\u72e0\u5fc3\uff0c\u539f\u6765\u7236\u4eb2\u7684\u7231\u4e5f\u7adf\u7136\u662f\u5047\u7684\uff01\u5929\u5730\u95f4\u6709\u4ec0\u4e48\u4e8b\u60c5\u6bd4\u8fd9\u4e2a\u66f4\u4ee4\u513f \u5973\u4f24\u5fc3\uff1f\u5c24\u5176\u662f\u8c61\u8131\u4e0d\u82b1\u8fd9\u6837\u5a07\u7eb5\u60ef\u4e86\u7684\u5973\u513f\u3002 \u3000\u3000\u7a9d\u624e\u5408\u9053\uff1a\u201c\u4f60\u54ed\u4e5f\u6ca1\u6709\u7528\uff0c\u4f60\u518d\u4e0d\u79bb\u5f00\uff0c\u6211\u4eec\u5c31\u4e0d\u5ba2\u6c14\u4e86\uff0c\u5feb\u968f\u6211\u56de\u5bb6\u5427\u3002\u201d\u8131\u4e0d\u82b1 \u4f24\u5fc3\u5230\u4e86\u6781\u70b9\uff0c\u53cd\u800c\u54ed\u4e0d\u51fa\u6765\uff0c\u4e3e\u8896\u62b9\u4e86\u6cea\u75d5\uff0c\u8eab\u5b50\u4ecd\u7136\u5835\u7740\u70ae\u53e3\uff0c\u795e\u8272\u5341\u5206\u53ef\u6015\uff0c\u989d\u5409\u591a \u9053\uff1a\u201c\u9ebb\u7ffc\u8d5e\uff0c\u4f60\u628a\u5979\u62c9\u5f00\u3002\u201d\u9ebb\u7ffc\u8d5e\u56e0\u88ab\u5f20\u4e39\u67ab\u5728\u8eab\u4e0a\u523a\u4e86\u4e00\u4e2a\u201c\u8d3c\u201d\u5b57\uff0c\u6068\u4e0d\u5f97\u628a\u5f20\u5bb6 \u5168\u90fd\u6bc1\u706d\uff0c\u8fd9\u65f6\u5f97\u592a\u5e08\u7684\u624b\u8c15\uff0c\u5927\u4e86\u80c6\u5b50\uff0c\u8d70\u8fc7\u53bb\u4fbf\u62c9\u8131\u4e0d\u82b1\u7684\u8863\u8896\u3002 \u3000\u3000\u8131\u4e0d\u82b1\u4e3e\u8896\u4e00\u62c2\uff0c\u201c\u5478\u201d\u7684\u4e00\u58f0\uff0c\u553e\u6d8e\u5410\u5230\u9ebb\u7ffc\u8d5e\u8eab\u4e0a\u3002\u9ebb\u7ffc\u8d5e\u6014\u4e86\u4e00\u6014\uff0c\u53cd\u624b\u64d2\u62ff\uff0c \u628a\u8131\u4e0d\u82b1\u53cc\u624b\u626d\u5728\u80cc\u540e\uff0c\u9ebb\u7ffc\u8d5e\u6b66\u529f\u6bd4\u5979\u9ad8\u5f3a\u6570\u500d\uff0c\u8fd9\u4e00\u628a\u64d2\u62ff\u624b\u53c8\u7528\u5f97\u5341\u5206\u5201\u6bd2\uff0c\u8131\u4e0d\u82b1 \u52a8\u5f39\u4e0d\u5f97\uff0c\u7a81\u7136\u548c\u8eab\u4e00\u6251\uff0c\u6251\u5230\u9ebb\u7ffc\u8d5e\u8eab\u4e0a\uff0c\u5f20\u5f00\u6a31\u6843\u5c0f\u53e3\uff0c\u72e0\u72e0\u5730\u5411\u9ebb\u7ffc\u8d5e\u80a9\u5934\u4e00\u54ac\uff0c\u9ebb \u7ffc\u8d5e\u6599\u4e0d\u5230\u5979\u6709\u6b64\u4e00\u7740\uff0c\u8499\u53e4\u5730\u65b9\u867d\u7136\u4e0d\u6bd4\u4e2d\u56fd\uff0c\u7537\u5973\u4e4b\u95f4\uff0c\u5e76\u65e0\u201c\u6388\u53d7\u4e0d\u4eb2\u201d\u7684\u793c\u6559\u5b58 \u5728\uff0c\u4f46\u9ebb\u7ffc\u8d5e\u4e0e\u8131\u4e0d\u82b1\u7a76\u7adf\u662f\u5974\u624d\u4e4b\u5bf9\u4e3b\u5b50\uff0c\u9aa4\u7136\u88ab\u8131\u4e0d\u82b1\u6251\u5728\u8eab\u4e0a\uff0c\u5413\u5f97\u624b\u8db3\u65e0\u63aa\uff0c\u8fd9\u4e00 \u53e3\u54ac\u4e0b\uff0c\u5165\u8089\u4e09\u5206\uff0c\u9ebb\u7ffc\u8d5e\u53c8\u60ca\u53c8\u75db\uff0c\u64d2\u62ff\u624b\u81ea\u7136\u89e3\u4e86\uff0c\u7a9d\u624e\u5408\u5927\u53eb\u9053\uff1a\u201c\u4e0d\u5fc5\u987e\u5fcc\uff0c\u5c06\u51fb \u6655\uff01\u201d\u9ebb\u7ffc\u8d5e\u7eb5\u8eab\u4e00\u638c\uff0c\u5ffd\u542c\u5f97\u201c\u55e4\u55e4\u201d\u4e24\u58f0\uff0c\u539f\u6765\u662f\u8131\u4e0d\u82b1\u85cf\u5728\u8eab\u5185\u7684\u4e24\u652f\u8896\u7bad\uff0c\u9002\u624d\u53cc \u624b\u88ab\u626d\uff0c\u653e\u4e0d\u51fa\u6765\u3002\u8fd9\u8896\u7bad\u4e43\u662f\u5979\u5e73\u65e5\u6253\u730e\u6240\u7528\u7684\u6bd2\u7bad\uff0c\u76f8\u8ddd\u65e2\u8fd1\uff0c\u9ebb\u7ffc\u8d5e\u731d\u4e0d\u53ca\u9632\uff0c\u4e24\u8fb9 \u5fc3\u623f\uff0c\u7adf\u88ab\u6bd2\u7bad\u5c04\u5165\uff0c\u4f46\u8131\u4e0d\u82b1\u4e5f\u88ab\u4ed6\u7684\u638c\u529b\u9707\u5f97\u5012\u5728\u5730\u4e0a\u3002 \u3000\u3000\u7a9d\u624e\u5408\u5927\u60ca\uff0c\u6025\u5fd9\u62a2\u4e0a\uff0c\u53ea\u89c1\u8131\u4e0d\u82b1\u4e00\u8dc3\u800c\u8d77\uff0c\u5c16\u58f0\u53eb\u9053\uff1a\u201c\u5f20\u54e5\u54e5\uff0c\u4e0d\u662f\u6211\u4e0d\u6551\u4f60\uff0c \u6211\u5df2\u5c3d\u529b\u4e86\uff01\u201d\u5012\u8f6c\u5200\u67c4\uff0c\u4e00\u5200\u63d2\u5165\u80f8\u819b\uff0c\u56de\u8eab\u5012\u4e0b\uff0c\u53cc\u624b\u72b9\u81ea\u7d27\u7d27\u62b1\u7740\u70ae\u8eab\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u5728\u57ce\u5899\u4e0a\u770b\u5230\u6772\u4e86\uff0c\u8131\u4e0d\u82b1\u7adf\u7136\u4e3a\u4ed6\u800c\u6b7b\uff01\u8fd9\u970e\u90a3\u95f4\uff0c\u5f20\u4e39\u67ab\u53ea\u89c9\u4e00\u9635\u5fc3\u9178\uff0c\u5e73 \u7d20\u538c\u6076\u5979\u7684\u5fc3\u60c5\u5168\u90fd\u6d88\u4e86\uff0c\u4e0d\u89c9\u54ed\u51fa\u58f0\u6765\uff0c\u53eb\u9053\uff1a\u201c\u8131\u4e0d\u82b1\u59b9\u59b9\uff0c\u6211\u9886\u4f60\u7684\u60c5\u4e86\uff01\u201d\u53ef\u662f\u8131 \u4e0d\u82b1\u5df2\u6b7b\uff0c\u5f20\u4e39\u67ab\u7b2c\u4e00\u6b21\u53eb\u5979\u505a\u201c\u59b9\u59b9\u201d\u7684\u5145\u6ee1\u611f\u60c5\u7684\u58f0\u97f3\uff0c\u5979\u5df2\u542c\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u9ebb\u7ffc\u8d5e\u6bd9\u547d\uff0c\u8131\u4e0d\u82b1\u81ea\u6740\uff0c\u5168\u90fd\u51fa\u4eba\u610f\u5916\uff0c\u5728\u573a\u7684\u8499\u53e4\u6b66\u58eb\u4e2a\u4e2a\u6014\u7740\uff0c\u5664\u4e0d\u51fa\u58f0\u3002\u7a9d\u624e \u5408\u53eb\u9053\uff1a\u201c\u628a\u5979\u62c9\u5f00\uff0c\u5f00\u70ae\uff01\u201d\u989d\u5409\u591a\u7528\u529b\u626f\u5f00\u8131\u4e0d\u82b1\u62b1\u7740\u70ae\u8eab\u7684\u53cc\u624b\uff0c\u53ea\u89c1\u70ae\u53e3\u5df2\u88ab\u67d3\u5f97 \u901a\u7ea2\uff0c\u9c9c\u8840\u8fd8\u5728\u6c69\u6c69\u5730\u6d41\u5165\u3002\u6b63\u662f\uff1a \u3000\u3000\u62da\u628a\u5a07\u8eaf\u586b\u70ae\u53e3\uff0c\u9999\u9b42\u72b9\u81ea\u62a4\u6a80\u90ce\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u840d\u8e2a\u4fa0\u5f71\u5f55\u300b\u2014\u2014\u7b2c\u4e09\u5341\u4e00\u56de\u3000\u5251\u6c14\u5982\u8679\u5eff\u5e74\u771f\u68a6\u5e7b\u3000\u67d4\u60c5\u4f3c\u6c34\u4e00\u7b11\u89e3\u6069\u4ec7 \u6881\u7fbd\u751f\u300a\u840d\u8e2a\u4fa0\u5f71\u5f55\u300b \u7b2c\u4e09\u5341\u4e00\u56de\u3000\u5251\u6c14\u5982\u8679\u5eff\u5e74\u771f\u68a6\u5e7b\u3000\u67d4\u60c5\u4f3c\u6c34\u4e00\u7b11\u89e3\u6069\u4ec7 \u3000\u3000\u989d\u5409\u591a\u54ac\u4e00\u54ac\u7259\uff0c\u626d\u8f6c\u4e86\u5934\uff0c\u4e0d\u6562\u770b\u8131\u4e0d\u82b1\u53ef\u6015\u7684\u8138\u5b54\uff0c\u53cd\u624b\u4e00\u7529\uff0c\u5c06\u8131\u4e0d\u82b1\u7684\u5c38\u8eab\u629b \u5230\u4e00\u65c1\uff0c\u64e6\u71c3\u706b\u77f3\uff0c\u4e00\u4e0b\u5b50\u5c31\u628a\u706b\u7ef3\u70b9\u7740\uff0c\u8fc5\u5373\u8df3\u5230\u4e00\u8fb9\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u4e5f\u4e0d\u6562\u518d\u770b\uff0c\u8df3\u4e0b\u57ce\u5899\uff0c\u5de6\u624b\u62d6\u7740\u7236\u4eb2\uff0c\u53f3\u624b\u62d6\u7740\u6fb9\u53f0\u706d\u660e\u51c4\u7136\u7b11\u9053\uff1a\u201c\u7239\uff0c\u6fb9 \u53f0\u5c06\u519b\uff0c\u54b1\u4eec\u4eca\u65e5\u4e00\u540c\u8d70\u4e86\uff01\u201d\u6fb9\u53f0\u706d\u660e\u867d\u7136\u4e0d\u89c1\u5916\u9762\u60c5\u5f62\uff0c\u4f46\u542c\u5230\u662f\u989d\u5409\u591a\u4eb2\u81ea\u653e\u70ae\uff0c\u65e9 \u5df2\u4e0d\u4f5c\u5e78\u5b58\u4e4b\u60f3\uff0c\u5434\u94a9\u4e00\u4e3e\uff0c\u4ea6\u5411\u5fc3\u623f\u63d2\u53bb\u3002 \u3000\u3000\u4e91\u91cd\u88ab\u7948\u9547\u4e09\u9053\u91d1\u724c\uff0c\u53ec\u53bb\u671d\u89c1\u3002\u7948\u9547\u88ab\u74e6\u523a\u56fd\u738b\u5b89\u7f6e\u5728\u7687\u5bab\u5185\u53f3\u8fb9\u7684\u4e00\u5ea7\u504f\u6bbf\uff0c\u4e91\u91cd \u968f\u7740\u4e09\u4e2a\u536b\u58eb\uff0c\u5524\u5f00\u5bab\u95e8\uff0c\u8d70\u8fc7\u4e00\u5f2f\u5f2f\u66f2\u66f2\u7684\u901a\u9053\uff0c\u597d\u4e0d\u5bb9\u6613\u8d70\u5230\u4e86\u90a3\u5ea7\u5bab\u6bbf\u7684\u95e8\u524d\uff0c\u5b88\u95e8 \u536b\u58eb\u8fdb\u53bb\u901a\u62a5\uff0c\u8fc7\u4e86\u597d\u4e00\u4f1a\u5b50\uff0c\u90a3\u536b\u58eb\u51fa\u6765\u8bf4\u9053\uff1a\u201c\u4e91\u5927\u4eba\uff0c\u8bf7\u4f60\u5728\u8fd9\u91cc\u7b49\u5019\u53ec\u5524\u3002\u201d\u4e91\u91cd \u5fc3\u6025\u5982\u711a\uff0c\u9053\uff1a\u201c\u7687\u4e0a\u53ec\u6211\u7acb\u523b\u9762\u89c1\uff0c\u600e\u4e48\u8fd8\u8981\u6211\u7b49\u5019\uff1f\u201d\u536b\u58eb\u9053\uff1a\u201c\u7687\u4e0a\u6b63\u5728\u5403\u7740\u71d5\u7a9d\uff0c \u8fd8\u672a\u5403\u5b8c\u5462\uff01\u201d\u4e91\u91cd\u53c8\u6025\u53c8\u6c14\uff0c\u60f3\u4e0d\u5230\u7687\u4e0a\u63a5\u4e8c\u8fde\u4e09\u5730\u7528\u91d1\u724c\u50ac\u4fc3\u5374\u539f\u6765\u8fd8\u6709\u8fd9\u6837\u7684\u95f2\u60c5\u9038 \u81f4\uff0c\u5728\u5403\u71d5\u7a9d\u3002 \u3000\u3000\u53c8\u8fc7\u4e86\u4e00\u4f1a\uff0c\u501f\u7528\u7684\u8499\u53e4\u5c0f\u592a\u76d1\u624d\u51fa\u6765\u9053\u4e2a\u201c\u8bf7\u201d\u5b57\uff0c\u4e91\u91cd\u4e09\u6b65\u5e76\u4f5c\u4e24\u6b65\uff0c\u8dd1\u5165\u5bab\u4e2d\uff0c \u53ea\u89c1\u7948\u9547\u5750\u5728\u4e00\u5f20\u5b89\u4e50\u6905\u4e0a\uff0c\u56db\u4e2a\u74e6\u523a\u56fd\u738b\u9063\u6765\u4f3a\u5019\u4ed6\u7684\u5c0f\u592a\u76d1\u6b63\u5728\u66ff\u4ed6\u6376\u80cc\uff0c\u7948\u9547\u9762\u8272\u60a0 \u95f2\u4e1d\u6beb\u4e0d\u8c61\u6709\u6025\u4e8b\u7684\u6837\u5b50\u3002 \u3000\u3000\u4e91\u91cd\u5fcd\u7740\u4e00\u809a\u5b50\u6c14\uff0c\u8dea\u5012\u5730\u4e0a\uff0c\u4e09\u547c\u4e07\u5c81\u3002\u7948\u9547\u62c9\u4e86\u957f\u55d3\u5b50\uff0c\u6162\u541e\u541e\u5730\u9053\uff1a\u201c\u537f\u5bb6\u5e73 \u8eab\uff0c\u8d50\u5750\u3002\u201d\u4e91\u91cd\u722c\u4e86\u8d77\u6765\uff0c\u5e76\u4e0d\u5c31\u5750\uff0c\u5148\u81ea\u95ee\u9053\uff1a\u201c\u7687\u4e0a\u6709\u4f55\u7d27\u8981\u7684\u4e8b\u60c5\uff0c\u53ec\u5524\u4e3a\u81e3\uff1f\u201d \u3000\u3000\u7948\u9547\u54b3\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u662f\u5440\uff0c\u662f\u6709\u7d27\u8981\u7684\u4e8b\u60c5\u3002\u6715\u5ffd\u7136\u60f3\u8d77\uff0c\u54b1\u4eec\u660e\u65e5\u867d\u7136\u5f52\u56fd\uff0c\u5230\u5e95 \u5728\u74e6\u523a\u4e00\u573a\uff0c\u53d7\u4ed6\u62db\u5f85\uff0c\u4ed6\u4eec\u662f\u4e3b\uff0c\u54b1\u4eec\u662f\u5bbe\uff0c\u4ed6\u4eec\u656c\u91cd\u54b1\u4eec\uff0c\u54b1\u4eec\u4e5f\u4e0d\u53ef\u6ca1\u4e86\u793c\u8282\uff0c\u74e6\u523a \u56fd\u738b\u8981\u4eb2\u81ea\u9001\u6715\u51fa\u57ce\uff0c\u54b1\u4eec\u82e5\u7136\u53d7\u4e4b\uff0c\u4f3c\u4e4e\u6709\u4e9b\u8fc7\u5206\u3002\u4e0d\u5982\u7531\u4f60\u63a5\u6211\u51fa\u5bab\uff0c\u54b1\u4eec\u9012\u8868\u8f9e\u884c\uff0c \u74e6\u523a\u56fd\u738b\u82e5\u8981\u6765\u9001\uff0c\u54b1\u4eec\u5728\u57ce\u5916\u7b49\u4ed6\uff0c\u8fd9\u6837\u624d\u5408\u76ae\u6b64\u76f8\u656c\u4e4b\u793c\u3002\u201d \u3000\u3000\u539f\u6765\u662f\u8fd9\u4e2a\u201c\u6025\u4e8b\u201d\uff0c\u4e91\u91cd\u51e0\u4e4e\u6c14\u5f97\u8bf4\u4e0d\u51fa\u8bdd\u6765\uff0c\u7948\u9547\u5728\u74e6\u523a\u88ab\u56da\u671f\u95f4\uff0c\u6240\u53d7\u662f\u4f55\u7b49 \u201c\u62db\u5f85\u201d\uff0c\u4e91\u91cd\u4ea6\u65e9\u5df2\u5c31\u4ece\u5f20\u4e39\u67ab\u7684\u53e3\u4e2d\u77e5\u9053\uff0c\u60f3\u4e0d\u5230\u4ed6\u800c\u4eca\u53cd\u800c\u4e0d\u987e\u5927\u660e\u5929\u5b50\u7684\u8eab\u4efd\uff0c\u8981 \u9012\u8868\u8f9e\u884c\uff0c\u8981\u8bb2\u4ec0\u4e48\u201c\u76f8\u656c\u4e4b\u793c\u201d\u3002 \u3000\u3000\u4e91\u91cd\u659c\u773c\u4e00\u77a5\uff0c\u53ea\u89c1\u90a3\u56db\u4e2a\u5c0f\u592a\u76d1\u5728\u5077\u5077\u5730\u7b11\u3002\u4e91\u91cd\u5fc3\u5ff5\u4e00\u52a8\uff0c\u5ffd\u7136\u95f4\u95ee\u9053\uff1a\u201c\u8fd9\u771f\u662f \u7687\u4e0a\u7684\u610f\u601d\u5417\uff1f\u201d\u7948\u9547\u9762\u8272\u4e00\u7aef\u65a5\u9053\uff1a\u201c\u4e91\u91cd\uff0c\u4f60\u77e5\u9053\u5931\u8a00\u4e4b\u7f6a\u5417\uff1f\u8fd9\u5f53\u7136\u662f\u5be1\u4eba\u7684\u610f \u601d\u3002\u201d\u5176\u5b9e\u8fd9\u662f\u4e5f\u5148\u53d1\u89c9\u8131\u4e0d\u82b1\u5077\u8d70\u4e4b\u540e\uff0c\u65e9\u6599\u5230\u5979\u8981\u53bb\u9080\u8bf7\u4e91\u91cd\u7684\u4e00\u7740\uff0c\u6240\u4ee5\u4e00\u9762\u6d3e\u4eba\u963b \u62e6\uff0c\u4e00\u9762\u6d3e\u7a9d\u624e\u5408\u5411\u989d\u5409\u591a\u4f20\u4ee4\uff0c\u4e00\u9762\u6d3e\u4eba\u5165\u5bab\u5a01\u80c1\u7948\u9547\uff0c\u8981\u4ed6\u5982\u6b64\u5982\u6b64\uff0c\u4e09\u7ba1\u9f50\u4e0b\uff0c\u65e0\u975e \u662f\u60f3\u963b\u6320\u4e91\u91cd\uff0c\u4f7f\u5f97\u4ed6\u4e5f\u6ca1\u6cd5\u6551\u8d70\u5f20\u4e39\u67ab\u7236\u5b50\u3002 \u3000\u3000\u7687\u5bab\u5c31\u5728\u4e5f\u5148\u52bf\u529b\u63a7\u5236\u4e4b\u4e0b\uff0c\u4ed6\u5f53\u7136\u53ef\u4ee5\u64cd\u7eb5\u81ea\u5982\uff0c\u7948\u9547\u751f\u6015\u4e5f\u5148\u4e0d\u653e\u4ed6\u5f52\u56fd\uff0c\u88ab\u4ed6\u4e00 \u5413\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u4e0d\u5fc5\u4e3a\u8fd9\u793c\u8282\u4e4b\u4e8b\u81f4\u751f\u53d8\u5366\u3002\u201d\u679c\u7136\u542c\u4e5f\u5148\u6240\u6307\uff0c\u5c06\u4e91\u91cd\u53ec\u4e86\u8fdb\u6765\u3002\u800c\u4e14 \u8fd8\u8981\u5728\u81e3\u5b50\u9762\u524d\u7ef4\u6301\u81ea\u5df1\u7684\u9762\u5b50\uff0c\u4e00\u53e3\u54ac\u5b9a\u662f\u81ea\u5df1\u7684\u610f\u601d\u3002 \u3000\u3000\u7948\u9547\u8d23\u4e86\u4e91\u91cd\u51e0\u53e5\uff0c\u9762\u8272\u4e00\u8f6c\uff0c\u8bf4\u9053\uff1a\u201c\u59d1\u5ff5\u4f60\u6b64\u6b21\u51fa\u4f7f\u6709\u529f\uff0c\u6715\u4e0d\u7f6a\u4f60\u3002\u6715\u800c\u4eca\u5c31\u6d3e \u4eba\u9012\u8868\u7ed9\u74e6\u523a\u56fd\u541b\u3002\u4f60\u5728\u6b64\u7b49\u6211\u5f85\u6211\u8d4f\u8d50\u4e86\u5bab\u4e2d\u7684\u4ec6\u5f79\u4e4b\u540e\uff0c\u5929\u4eae\u4e4b\u65f6\uff0c\u54b1\u4eec\u5c31\u8d70\u3002\u201d\u4e91\u91cd \u5ffd\u5730\u6297\u58f0\u8bf4\u9053\uff1a\u201c\u7687\u4e0a\u4f60\u4e0d\u5fc5\u6d3e\u4eba\u9012\u8868\u4e86\uff0c\u6211\u5df2\u901a\u77e5\u74e6\u523a\u56fd\u738b\uff0c\u660e\u513f\u4e0d\u8d70\uff01\u201d \u3000\u3000\u7948\u9547\u5927\u60ca\u8272\u53d8\u5389\u58f0\u65a5\u9053\uff1a\u201c\u4f60\u3001\u4f60\u3001\u4f60\u600e\u6562\u64c5\u81ea\u4f5c\u4e3b\uff1f\u201d\u4e91\u91cd\u9053\uff1a\u201c\u6211\u8981\u53bb\u62dc\u4f1a\u5f20\u4e39 \u67ab\u3002\u201d\u7948\u9547\u66f4\u60ca\uff0c\u62cd\u6848\u53eb\u9053\uff1a\u201c\u4ec0\u4e48\uff0c\u4f60\u8981\u53bb\u62dc\u4f1a\u5f20\u4e39\u67ab\uff1f\u4f60\u77e5\u9053\u4ed6\u4eec\u662f\u5f20\u8d3c\u5f20\u58eb\u8bda\u7684\u540e\u88d4 \u4e48\uff1f\u6715\u4e0d\u5c06\u4ed6\u4eec\u62bc\u89e3\u56de\u56fd\uff0c\u5904\u4ee5\u6781\u5211\uff0c\u5df2\u662f\u5bbd\u539a\u65e0\u6bd4\uff0c\u4f60\u8fd8\u8981\u53bb\u62dc\u4f1a\u4ed6\u4eec\uff01\u54fc\u3001\u54fc\uff0c\u771f\u662f\u5c82 \u6709\u6b64\u7406\uff01\u201d\u4e91\u91cd\u795e\u8272\u4e0d\u53d8\u8bf4\u9053\uff1a\u201c\u7687\u4e0a\uff0c\u4f60\u77e5\u9053\u4e48\uff1f\u8fd9\u6b21\u4e24\u56fd\u8c08\u548c\uff0c\u8981\u8fce\u63a5\u7687\u4e0a\u56de\u56fd\uff0c\u8fd9\u56fa \u7136\u662f\u4e8e\u9601\u8001\u7684\u4e3b\u5f20\uff0c\u4f46\u4e5f\u662f\u5f20\u4e39\u67ab\u7684\u4e3b\u610f\u3002\u8981\u4e0d\u662f\u5f20\u4e39\u67ab\u63a2\u77e5\u74e6\u523a\u7684\u865a\u5b9e\uff0c\u7980\u544a\u4e8e\u8c26\uff0c\u54b1\u4eec \u8fd8\u4e0d\u6562\u5bf9\u4e5f\u5148\u8fd9\u6837\u7684\u5f3a\u786c\u5462\uff01\u201d\u7948\u9547\u9762\u8272\u82cd\u767d\uff0c\u201c\u54fc\u201d\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u4f9d\u4f60\u8bf4\u6765\uff0c\u5f20\u4e39\u67ab\u5012\u662f \u5fe0\u5fc3\u4e3a\u6715\u4e86\uff1f\u201d\u4e91\u91cd\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4ed6\u662f\u5fe0\u5fc3\u4e3a\u56fd\uff01\u201d\u7948\u9547\u9053\uff1a\u201c\u4f60\u4e3a\u53cd\u8d3c\u8bf4\u8bdd\uff0c\u5f97\u4e86\u4ed6\u4ec0\u4e48 \u597d\u5904\uff1f\u201d\u4e91\u91cd\u6ee1\u8154\u60b2\u6124\u51e0\u4e4e\u8bf4\u4e0d\u51fa\u8bdd\u6765\uff0c\u5ffd\u542c\u5f97\u5bab\u4e2d\u6253\u4e86\u4e94\u66f4\uff0c\u5fc3\u4e2d\u4e00\u6025\uff0c\u51b2\u53e3\u8bf4\u9053\uff1a\u201c\u4e5f \u5148\u8981\u70ae\u8f70\u5f20\u5bb6\uff0c\u5fae\u81e3\u4e0e\u5f20\u5bb6\u4ec7\u6df1\u5982\u6d77\uff0c\u4f46\u4ea6\u7518\u613f\u53d7\u965b\u4e0b\u5904\u7f6a\uff0c\u5fc5\u7136\u8981\u53bb\u6551\u51fa\u5f20\u5bb6\u3002\u8bf4\u5230\u597d \u5904\uff0c\u965b\u4e0b\u53d7\u4e86\u4ed6\u7684\u597d\u5904\uff0c\u5374\u8fd8\u4e0d\u77e5\uff0c\u4e8e\u9601\u8001\u4e3a\u965b\u4e0b\u53ec\u96c6\u5929\u4e0b\u4e49\u5e08\uff0c\u51fb\u8d25\u4e5f\u5148\uff0c\u5176\u4e2d\u7684\u519b\u9977\uff0c \u5360\u4e86\u4e00\u534a\uff0c\u5c31\u662f\u5f20\u4e39\u67ab\u6350\u51fa\u6765\u7684\uff01\u201d\u7948\u9547\u4e24\u773c\u7ffb\u767d\uff0c\u8fde\u58f0\u8bf4\u9053\uff1a\u201c\u8fd9\u3001\u8fd9\u662f\u4ec0\u4e48\u8bdd\uff1f\u4f60\u3001 \u4f60\u3001\u4f60\u662f\u98df\u6211\u5927\u660e\u4ff8\u7984\u7684\u81e3\u5b50\u4e48\uff1f\u4f60\u3001\u4f60\u3001\u4f60\u66ff\u4ed6\u8bf4\u8bdd\uff0c\u5c45\u7136\u8fdd\u6297\u541b\u547d\uff1f\u201d\u4e91\u91cd\u70ed\u6cea\u76c8\u7736\uff0c \u62ac\u5934\u4e00\u770b\u66d9\u8272\u5df2\u73b0\uff0c\u628a\u5fc3\u4e00\u6a2a\uff0c\u4f83\u4f83\u8bf4\u9053\uff1a\u201c\u5fae\u81e3\u77e5\u9053\u8fdd\u6297\u541b\u547d\u7f6a\u5f53\u5904\u6b7b\uff0c\u6211\u53bb\u4e86\u5f20\u5bb6\u4e4b \u540e\uff0c\u5f53\u81ea\u5c3d\u4ee5\u62a5\u7687\u4e0a\u77e5\u9047\u4e4b\u6069\uff0c\u8ba9\u7687\u4e0a\u518d\u8bf7\u4e8e\u9601\u8001\u6d3e\u7b2c\u4e8c\u4e2a\u4f7f\u81e3\u6765\u8fce\u63a5\u7687\u4e0a\u56de\u56fd\u3002\u201d \u3000\u3000\u7948\u9547\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u8981\u77e5\u4ed6\u65e5\u76fc\u591c\u76fc\uff0c\u597d\u4e0d\u5bb9\u6613\u76fc\u5230\u4eca\u65e5\u5f97\u4ee5\u91cd\u56de\u6545\u56fd\uff0c\u518d\u4e3a\u5929\u5b50\uff0c \u82e5\u7136\u4e91\u91cd\u771f\u662f\u4e00\u610f\u5b64\u884c\uff0c\u820d\u4ed6\u800c\u53bb\uff0c\u4e0d\u77e5\u4f55\u65f6\u624d\u80fd\u6d3e\u7b2c\u4e8c\u4e2a\u4f7f\u81e3\uff0c\u7b2c\u4e8c\u4e2a\u4f7f\u81e3\u4e5f\u672a\u5fc5\u80fd\u6709\u4ed6 \u90a3\u822c\u672c\u4e8b\uff0c\u591c\u957f\u68a6\u591a\uff0c\u53ea\u6015\u7687\u5e1d\u68a6\u4e5f\u7ec8\u4e8e\u7834\u788e\u3002\u7948\u9547\u60f3\u81f3\u6b64\u5904\uff0c\u4e0d\u89c9\u51b7\u6c57\u76f4\u6d41\uff0c\u58f0\u8c03\u4e00\u8f6c\uff0c \u6025\u5fd9\u8a00\u9053\uff1a\u201c\u537f\u5bb6\u6709\u8bdd\u597d\u8bf4\u3002\u201d\u4e91\u91cd\u9053\uff1a\u201c\u4e5f\u5148\u72fc\u5b50\u91ce\u5fc3\uff0c\u5bf9\u965b\u4e0b\u5e76\u65e0\u597d\u610f\u3002\u4ed6\u5982\u4eca\u5b9e\u662f\u88ab \u8feb\u4e0e\u6211\u56fd\u8c08\u548c\uff0c\u4e0d\u5f97\u4e0d\u5c14\u3002\u7687\u4e0a\uff0c\u4f60\u76f8\u4fe1\u4e5f\u5148\uff0c\u4e0d\u5982\u76f8\u4fe1\u5f20\u4e39\u67ab\u3002\u6211\u800c\u4eca\u8d70\u4e86\uff01\u201d\u7948\u9547\u6025\u5fd9 \u53eb\u9053\uff1a\u201c\u537f\u5bb6\u4e14\u4f4f\uff01\u201d \u3000\u3000\u4e91\u91cd\u7126\u6025\u4e4b\u6781\uff0c\u4f46\u542c\u5230\u7687\u4e0a\u547c\u5524\uff0c\u4e0d\u5f97\u4e0d\u56de\u8fc7\u5934\u6765\uff0c\u9053\uff1a\u201c\u7687\u4e0a\u6709\u4f55\u5429\u5490\uff1f\u201d\u7948\u9547\u98a4\u58f0 \u8bf4\u9053\uff1a\u201c\u6715\u4e0e\u4f60\u4e00\u540c\u53bb\u3002\u201d\u539f\u6765\u7948\u9547\u89c1\u963b\u62e6\u4e0d\u4f4f\u4e91\u91cd\uff0c\u751f\u6015\u81ea\u5df1\u7559\u5728\u74e6\u523a\u7687\u5bab\uff0c\u4f1a\u906d\u5230\u4e5f\u5148 \u8feb\u5bb3\uff08\u5176\u5b9e\u4e5f\u5148\u6025\u4e8e\u6c42\u548c\uff0c\u53ea\u6562\u5bf9\u4ed6\u6050\u5413\uff0c\u4e07\u4e0d\u6562\u52a0\u5bb3\u4e8e\u4ed6\uff09\u3002\u5728\u60a3\u5f97\u60a3\u5931\u7684\u5fc3\u60c5\u4e4b\u4e0b\uff0c\u8003 \u8651\u518d\u4e09\uff0c\u89c9\u5f97\u8fd8\u662f\u548c\u4e91\u91cd\u4e00\u9053\uff0c\u8f83\u4e3a\u5b89\u5168\u53ef\u9760\u3002 \u3000\u3000\u8fd9\u4e00\u8981\u6c42\uff0c\u9887\u51fa\u4e91\u91cd\u610f\u5916\uff0c\u4e91\u91cd\u56de\u5934\u4e00\u770b\uff0c\u89c1\u7948\u9547\u795e\u60c5\uff0c\u597d\u50cf\u5bb3\u6015\u730e\u4eba\u7684\u5154\u5b50\u4e00\u822c\uff0c\u4e0e \u9002\u624d\u88c5\u6a21\u4f5c\u6837\u7684\u6012\u72ee\u795e\u6001\uff0c\u524d\u540e\u5224\u82e5\u4e24\u4eba\u3002\u4e91\u91cd\u5fc3\u4e2d\u4e0d\u81ea\u89c9\u5730\u6cdb\u8d77\u4e00\u79cd\u538c\u6076\u4e0e\u601c\u60af\u7684\u6df7\u5408\u60c5 \u7eea\u6765\uff0c\u89c9\u5f97\u8fd9\u4e2a\u201c\u4e07\u4eba\u4e4b\u4e0a\u201d\u7684\u7687\u5e1d\uff0c\u5176\u5b9e\u5341\u5206\u6e3a\u5c0f\uff0c\u4f46\u8fd8\u662f\u606d\u606d\u656c\u656c\u5730\u5c48\u4e86\u534a\u819d\uff0c\u627f\u63a5 \u201c\u5723\u65e8\u201d\u3002 \u3000\u3000\u66d9\u8272\u6e10\u663e\u6653\u5bd2\u903c\u4eba\uff0c\u7948\u9547\u9053\uff1a\u201c\u4e14\u5f85\u6715\u52a0\u4e0a\u4e00\u4ef6\u8863\u88f3\u3002\u201d\u8d70\u5165\u5185\u5ba4\uff0c\u6253\u5f00\u8863\u67dc\uff0c\u5f53\u773c\u4e4b \u5904\uff0c\u4e00\u4ef6\u767d\u8272\u7684\u72d0\u76ae\u62ab\u80a9\u6446\u5728\u5f53\u4e2d\uff0c\u8fd9\u6b63\u662f\u7948\u9547\u88ab\u4e5f\u5148\u56da\u4e8e\u77f3\u5854\u65f6\uff0c\u5f20\u4e39\u67ab\u4ece\u8eab\u4e0a\u89e3\u4e0b\u9001\u7ed9 \u4ed6\u7684\u3002\u7948\u9547\u4e00\u89c1\uff0c\u89e6\u8d77\u5f53\u65e5\u60c5\u666f\uff0c\u4e0d\u89c9\u62ff\u8d77\u62ab\u80a9\uff0c\u6469\u6332\u4e00\u4e0b\uff0c\u53c8\u628a\u62ab\u80a9\u629b\u5f00\uff0c\u5fc3\u4e2d\u70e6\u8e81\uff0c\u6311 \u6765\u62e3\u53bb\u8fd8\u662f\u9009\u4e0d\u5230\u5408\u610f\u7684\u8863\u670d\u3002 \u3000\u3000\u66d9\u8272\u4e00\u5f00\uff0c\u6668\u5149\u6e10\u6e10\u900f\u5165\u7a97\u6237\uff0c\u4e91\u91cd\u53eb\u9053\uff1a\u201c\u7687\u4e0a\uff0c\u8bf7\u6055\u5fae\u81e3\u4e0d\u80fd\u518d\u7b49\u5019\u4e86\uff01\u201d\u8fd9\u4e00\u58f0 \u4ee4\u7948\u9547\u5728\u8ff7\u832b\u4e4b\u4e2d\u60ca\u9192\u8fc7\u6765\uff0c\u624b\u8db3\u65e0\u63aa\u5730\u968f\u624b\u4fbf\u6293\u8d77\u4e00\u4ef6\u62ab\u5728\u8eab\u4e0a\u53eb\u9053\uff1a\u201c\u6211\u5c31\u6765\u5566\u3002\u201d\u5230 \u4ed6\u4e0e\u4e91\u91cd\u51fa\u4e86\u7687\u5bab\u4e4b\u65f6\uff0c\u624d\u53d1\u89c9\u81ea\u5df1\u968f\u624b\u62ff\u8d77\uff0c\u62ab\u5728\u8eab\u4e0a\u7684\u5c31\u662f\u5f20\u4e39\u67ab\u9001\u7ed9\u4ed6\u7684\u90a3\u4ef6\u72d0\u76ae\u62ab \u80a9\uff01 \u3000\u3000\u4e91\u91cd\u7684\u968f\u4ece\u8fd8\u88ab\u56f0\u5728\u8857\u5fc3\uff0c\u81f3\u4e91\u91cd\u4e0e\u7948\u9547\u5230\u65f6\uff0c\u90a3\u4e2a\u8499\u53e4\u592a\u5c09\u624d\u8bb8\u901a\u8fc7\uff0c\u8fd9\u65f6\u5df2\u7ecf\u662f\u5929 \u8272\u5927\u4eae\u4e86\u3002 \u3000\u3000\u4e91\u91cd\u8de8\u9a6c\u75be\u9a70\uff0c\u5f20\u4e39\u67ab\u4eb2\u5207\u7684\u7b11\u5bb9\u73b0\u5728\u9a6c\u524d\uff0c\u4f3c\u662f\u6b63\u5728\u5411\u4ed6\u62db\u624b\u3002\u4ec0\u4e48\u7f8a\u76ae\u8840\u4e66\uff0c\u4ec0\u4e48 \u5bb6\u4ec7\u4e16\u6068\uff0c\u8fd9\u65f6\u5168\u90fd\u88ab\u5f20\u4e39\u67ab\u7684\u5f71\u5b50\u9a71\u9010\uff0c\u53ea\u6709\u4e00\u4e2a\u5ff5\u5934\u5360\u636e\u5728\u4e91\u91cd\u7684\u5fc3\u5934\uff1a\u201c\u5fc5\u987b\u5c3d\u5feb\u5730 \u8d76\u5230\u5f20\u5bb6\uff0c\u5c06\u5f20\u4e39\u67ab\u4ece\u6b7b\u795e\u7684\u624b\u4e2d\u6551\u51fa\uff01\u201d \u3000\u3000\u201c\u662f\u4e0d\u662f\u592a\u8fdf\u4e86\u5462\uff1f\u5929\u5df2\u4eae\u4e86\uff0c\u671d\u9633\u4e5f\u5347\u8d77\u6765\u4e86\uff01\u201d\u4e91\u91cd\u653e\u9a6c\u98de\u5954\uff0c\u6068\u4e0d\u5f97\u628a\u65f6\u95f4\u62d6 \u4f4f\uff0c\u597d\u5728\u4e00\u76f4\u542c\u4e0d\u5230\u70ae\u58f0\u3002\u4f46\u8fd9\u5374\u4ee4\u4e91\u91cd\u66f4\u662f\u7d27\u5f20\uff0c\u66f4\u662f\u5fc3\u60ca\u80c6\u6218\uff0c\u597d\u50cf\u4e00\u4e2a\u5f85\u51b3\u7684\u6b7b\u56da\uff0c \u65f6\u95f4\u5df2\u5230\uff0c\u5374\u662f\u8fdf\u8fdf\u4e0d\u89c1\u523d\u5b50\u624b\u7684\u5200\u65a7\u780d\u4e0b\uff0c\u6bcf\u4e00\u79d2\u79cd\u7684\u7b49\u5f85\uff0c\u5c31\u50cf\u4e00\u5e74\u90a3\u4e48\u957f\u4e45\uff0c\u8c01\u77e5\u9053 \u70ae\u5f39\u5728\u4ec0\u4e48\u65f6\u5019\u6253\u51fa\uff0c\u4e5f\u8bb8\u5c31\u56e0\u4e3a\u8fdf\u4e86\u534a\u6b65\uff0c\u94f8\u6210\u4e86\u7ec8\u751f\u6094\u6068\u7684\u8fc7\u9519\u3002 \u3000\u3000\u4e91\u91cd\u72c2\u97ad\u5750\u9a91\uff0c\u628a\u7687\u5e1d\u4e5f\u7529\u5728\u540e\u9762\uff0c\u4e00\u53e3\u6c14\u8d76\u5230\u4e86\u5f20\u5bb6\u95e8\u524d\uff0c\u53ea\u89c1\u8499\u53e4\u5175\u4f0f\u5728\u5730\u4e0a\uff0c\u4e00 \u5c0a\u7ea2\u8863\u5927\u70ae\u5bf9\u51c6\u5f20\u5bb6\uff0c\u70ae\u53e3\u6b63\u5728\u5192\u70df\u3002\u4e91\u91cd\u5927\u53eb\u4e00\u58f0\uff0c\u5237\u7684\u4e00\u97ad\uff0c\u62bd\u5f97\u90a3\u5339\u6218\u9a6c\u8df3\u4e86\u8d77\u6765\uff0c \u5411\u90a3\u5c0a\u5927\u70ae\u98de\u5954\u8fc7\u53bb\u3002\u5341\u516b\u540d\u968f\u4ece\u4e00\u9f50\u5927\u53eb\uff1a\u201c\u5927\u660e\u4f7f\u8005\u5230\uff01\u201d \u3000\u3000\u5f20\u4e39\u67ab\u6b63\u5728\u7791\u76ee\u5f85\u6b7b\uff0c\u5ffd\u542c\u5f97\u56f4\u5899\u5916\u9762\u7684\u53eb\u58f0\uff0c\u8fd9\u4e00\u559c\u975e\u540c\u5c0f\u53ef\uff0c\u9661\u5730\u4e00\u8dc3\u800c\u8d77\uff0c\u6b63\u89c1 \u6fb9\u53f0\u706d\u660e\u6a2a\u94a9\u81ea\u520e\uff0c\u6025\u5fd9\u5c06\u4ed6\u7684\u5434\u94a9\u62a2\u4e0b\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u542c\uff0c\u662f\u4e91\u91cd\u6765\u5566\uff01\u201d\u4e00\u8df3\u8df3\u4e0a\u56f4\u5899\u3002 \u3000\u3000\u5f20\u5b97\u5468\u5f90\u5f90\u5f20\u5f00\u773c\u775b\uff0c\u9053\uff1a\u201c\u662f\u8c01\u6765\u5566\uff1f\u201d\u6fb9\u53f0\u706d\u660e\u9053\uff1a\u201c\u54b1\u4eec\u547d\u4e0d\u8be5\u7edd\uff0c\u662f\u660e\u671d\u7684\u4f7f \u8005\u6765\u62dc\u4f1a\u4f60\u5566\u3002\u201d\u8fd9\u65f6\u5f20\u5b97\u5468\u4e5f\u542c\u6e05\u695a\u4e86\uff0c\u5916\u9762\u4f20\u6765\u7684\u679c\u7136\u662f\u66ff\u201c\u5929\u671d\u4f7f\u8005\u201d\u559d\u9053\u7684\u58f0\u97f3\u3002 \u660e\u671d\u7684\u4f7f\u8005\u7adf\u7136\u4f1a\u6765\u5230\u4ed6\u7684\u5bb6\u95e8\uff0c\u6b64\u9645\u6bd4\u53d7\u4e5f\u5148\u7684\u70ae\u8f70\u66f4\u51fa\u4e4e\u4ed6\u610f\u6599\u4e4b\u5916\uff0c\u5f20\u5b97\u5468\u7709\u5b87\u4e4b\u95f4 \u63a0\u8fc7\u4e00\u4e1d\u7b11\u610f\uff0c\u4f46\u968f\u5373\u53c8\u4f4e\u4e0b\u4e86\u5934\u957f\u957f\u5730\u53f9\u4e86\u53e3\u6c14\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u8df3\u4e0a\u56f4\u5899\uff0c\u4e00\u773c\u770b\u89c1\u4e91\u91cd\u5feb\u9a6c\u5954\u6765\uff0c\u518d\u4e00\u773c\uff0c\u53ea\u89c1\u5bf9\u51c6\u4ed6\u5bb6\u7684\u90a3\u5c0a\u7ea2\u8863\u5927\u70ae\uff0c\u70ae \u53e3\u6b63\u5728\u5192\u51fa\u767d\u70df\u3002\u5f20\u4e39\u67ab\u773c\u524d\u4e00\u9ed1\uff0c\u521a\u83b7\u5f97\u5e0c\u671b\u4e4b\u540e\u7684\u7edd\u671b\uff0c\u51e0\u4e4e\u4ee4\u4ed6\u4e5f\u652f\u6301\u4e0d\u4f4f\u3002 \u3000\u3000\u6fb9\u53f0\u706d\u660e\u89c1\u5f20\u4e39\u67ab\u5728\u5899\u5934\u4e0a\u6447\u6447\u6b32\u5760\uff0c\u53eb\u9053\uff1a\u201c\u5582\uff0c\u4f60\u600e\u4e48\u5566\uff1f\u201d\u5f20\u4e39\u67ab\u5b9a\u4e00\u5b9a\u795e\uff0c\u5927 \u58f0\u53eb\u9053\uff1a\u201c\u4e91\u91cd\u5144\uff0c\u5feb\u5feb\u8d70\u5f00\uff0c\u4f11\u8981\u9001\u6b7b\uff01\u201d\u5728\u6700\u5371\u9669\u7684\u65f6\u5019\u53ef\u4ee5\u770b\u89c1\u5230\u771f\u631a\u7684\u53cb\u8c0a\u3002\u5f20\u4e39 \u67ab\u4e0e\u4e91\u91cd\u90fd\u628a\u81ea\u5df1\u7684\u751f\u6b7b\u7f6e\u4e4b\u5ea6\u5916\uff0c\u4e00\u4e2a\u4ecd\u9a6c\u4e0d\u505c\u8e44\uff0c\u4e00\u4e2a\u5728\u5927\u58f0\u547c\u53eb\uff0c\u5c31\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u5ffd \u542c\u5f97\u201c\u545c\u201d\u7684\u4e00\u58f0\uff0c\u767d\u70df\u56db\u6563\uff0c\u70ae\u5f39\u6253\u51fa\u6765\u4e86\u3002 \u3000\u3000\u4e91\u91cd\u5c16\u53eb\u4e00\u58f0\uff0c\u5fc3\u5934\u50cf\u88ab\u4e00\u5ea7\u5927\u5c71\u7a81\u7136\u538b\u4e0b\uff0c\u4e00\u5207\u7edd\u671b\uff01\u5ffd\u542c\u5f97\u70ae\u58f0\u6697\u54d1\uff0c\u5b8c\u5168\u4e0d\u50cf\u90a3 \u5728\u6218\u573a\u4e0a\u542c\u60ef\u7684\u5927\u70ae\u4e4b\u58f0\uff0c\u5f20\u76ee\u4e00\u770b\uff0c\u53ea\u89c1\u90a3\u70ae\u5f39\u5192\u7740\u767d\u70df\uff0c\u53ea\u6253\u5230\u8ddd\u79bb\u70ae\u53e3\u7684\u4e09\u4e08\u4e4b\u5730\uff0c \u5728\u5730\u4e0a\u6eda\u4e86\u51e0\u6eda\uff0c\u6eda\u4e0b\u6c34\u6c9f\uff0c\u7adf\u7136\u6ca1\u6709\u7206\u70b8\u3002 \u3000\u3000\u539f\u6765\u90a3\u5c0a\u7ea2\u8863\u5927\u70ae\u7684\u70ae\u53e3\uff0c\u88ab\u8131\u4e0d\u82b1\u7684\u70ed\u8840\u6ce8\u5165\uff0c\u70ae\u819b\u6da6\u6e7f\u3002\u73b0\u4ee3\u7684\u5927\u70ae\uff0c\u5728\u6570\u5343\u53d1\u4e4b \u4e2d\uff0c\u4e5f\u5076\u6709\u4e00\u4e24\u53d1\u662f\u6253\u4e0d\u54cd\uff0c\u4f55\u51b5\u662f\u53e4\u4ee3\u7684\u5927\u70ae\uff0c\u706b\u5668\u7edd\u5bf9\u6ca1\u6709\u73b0\u5728\u7684\u7cbe\u826f\uff0c\u706b\u836f\u53d7\u4e86\u6f6e \u6e7f\uff0c\u6253\u4e86\u51fa\u6765\u4e5f\u4e0d\u80fd\u7206\u70b8\u3002 \u3000\u3000\u4e91\u91cd\u5927\u559c\u5982\u72c2\uff0c\u7acb\u523b\u98de\u8eab\u4e0b\u9a6c\uff0c\u8d76\u7d27\u62cd\u95e8\uff0c\u5341\u516b\u540d\u968f\u4ece\u4e5f\u8ddf\u7740\u9c7c\u8d2f\u800c\u5165\u3002\u989d\u5409\u591a\u8fd9\u65f6\u7eb5 \u6709\u5929\u5927\u7684\u80c6\u5b50\uff0c\u4e5f\u4e0d\u6562\u518d\u653e\u7b2c\u4e8c\u70ae\uff01 \u3000\u3000\u5f20\u4e39\u67ab\u8df3\u4e0b\u5899\u5934\uff0c\u6253\u5f00\u5927\u95e8\uff0c\u4e24\u4eba\u7d27\u7d27\u76f8\u62e5\uff0c\u6cea\u773c\u76f8\u5bf9\uff0c\u4e00\u5207\u6069\u6069\u6028\u6028\u90fd\u629b\u5728\u4e91\u5916\u3002\u5ffd \u542c\u5f97\u5f20\u4e39\u67ab\u53eb\u9053\uff1a\u201c\u7239\u2026\u2026\u201d\u4e91\u91cd\u626d\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u5f20\u5b97\u5468\u98a4\u5dcd\u5dcd\u5730\u671d\u7740\u4ed6\u4eec\u8d70\u6765\u3002\u4e91\u91cd\u5fc3\u4e2d \u4e00\u6c89\uff1a\u539f\u6765\u8fd9\u4eba\u4fbf\u662f\u5f20\u4e39\u67ab\u7684\u7236\u4eb2\uff0c\u662f\u81ea\u5df1\u51fa\u4e86\u5a18\u80ce\uff0c\u4e00\u6709\u77e5\u89c9\u4e4b\u540e\uff0c\u4fbf\u65e0\u65e5\u65e0\u65f6\u4e0d\u5728\u5207\u9f7f \u75db\u6068\u7684\u4ec7\u4eba\uff01\u8fd9\u4ec7\u4eba\u73b0\u5728\u6b63\u5728\u671b\u7740\u81ea\u5df1\uff0c\u5634\u9afb\u5fae\u5fae\u5f00\u9616\uff0c\u4f3c\u4e4e\u662f\u6709\u5343\u8a00\u4e07\u8bed\uff0c\u8981\u8bf4\u53c8\u8bf4\u4e0d\u51fa \u6765\uff0c\u5e03\u6ee1\u76b1\u7eb9\u7684\u8138\u4e0a\u73b0\u51fa\u5149\u5f69\uff0c\u5e26\u7740\u4e00\u79cd\u5947\u7279\u7684\u8868\u60c5\uff0c\u4f3c\u4e4e\u662f\u5728\u7b49\u5f85\u4e00\u4ef6\u6e34\u671b\u5df2\u4e45\u7684\u4e8b\u60c5\uff0c \u53c8\u4f3c\u7236\u4eb2\u5728\u8fce\u63a5\u81ea\u5df1\u4e45\u5df2\u672a\u5f52\u5bb6\u7684\u513f\u5b50\u3002\u8fd9\u795e\u60c5\u4ee4\u4e91\u91cd\u5176\u540e\u5728\u4e00\u751f\u4e2d\u4e5f\u6c38\u8fdc\u4e0d\u80fd\u5fd8\u8bb0\u3002 \u3000\u3000\u4e91\u91cd\u75db\u82e6\u5730\u53eb\u4e86\u4e00\u58f0\uff0c\u8fd9\u5f62\u5bb9\u67af\u69c1\u3001\u6ee1\u5934\u767d\u53d1\u7684\u8001\u4eba\uff0c\u54ea\u6709\u4e00\u70b9\u50cf\u81ea\u5df1\u60f3\u8c61\u4e2d\u90a3\u4e2a\u9634\u6bd2 \u9669\u72e0\u7684\u5978\u8d3c\uff1f\u96be\u9053\u81ea\u5df1\u80fd\u5fcd\u5fc3\u628a\u5229\u5200\u63d2\u5165\u8fd9\u5782\u6b7b\u7684\u8001\u4eba\u7684\u80f8\u819b\uff1f\u5f20\u5b97\u5468\u4e00\u6b65\u4e00\u6b65\u6765\u5f97\u66f4\u8fd1 \u4e86\u3002\u4e91\u91cd\u89e6\u4e00\u89e6\u5341\u51e0\u5e74\u6765\u85cf\u5728\u8d34\u8eab\u7684\u7f8a\u76ae\u5907\u4e66\uff0c\u72e0\u72e0\u5730\u5411\u5f20\u5b97\u5468\u76ef\u4e86\u4e00\u773c\uff0c\u5ffd\u7136\u53c8\u628a\u5934\u8f6c\u8fc7 \u4e00\u8fb9\uff0c\u4e00\u6454\u6454\u5f00\u4e86\u5f20\u4e39\u67ab\u7d27\u7d27\u62b1\u7740\u81ea\u5df1\u7684\u624b\u81c2\u3002 \u3000\u3000\u5f20\u5b97\u5468\u5fc3\u75db\u5982\u5272\uff0c\u8fd9\u5014\u5f3a\u618e\u6076\u7684\u773c\u5149\uff0c\u4e0e\u4e09\u5341\u5e74\u524d\u7684\u4e91\u9756\u662f\u4e00\u6a21\u4e00\u6837\u554a\uff01\u5f20\u5b97\u5468\u4ec0\u4e48\u4e5f \u660e\u767d\u4e86\uff0c\u9893\u7136\u5730\u5750\u5728\u5730\u4e0a\uff0c\u53ea\u89c1\u4e91\u91cd\u8f6c\u8fc7\u4e86\u8eab\uff0c\u98a4\u58f0\u53eb\u9053\uff1a\u201c\u4e8b\u60c5\u5df2\u4e86\uff0c\u54b1\u4eec\u8d70\u5427\u3002\u201d \u3000\u3000\u5f20\u4e39\u67ab\u5446\u82e5\u6728\u9e21\uff0c\u770b\u770b\u7236\u4eb2\uff0c\u53c8\u770b\u770b\u4e91\u91cd\uff0c\u4ec0\u4e48\u8bdd\u4e5f\u8bf4\u4e0d\u51fa\u6765\u3002\u6fb9\u53f0\u955c\u660e\u6b63\u4e0e\u54e5\u54e5\u76f8 \u53d9\uff0c\u8dd1\u8fc7\u6765\u9053\uff1a\u201c\u4ec0\u4e48\uff0c\u624d\u6765\u4e86\u53c8\u8981\u8d70\u4e86\uff1f\u201d\u5e73\u7d20\u53ea\u8981\u6fb9\u53f0\u955c\u660e\u8bf4\u8bdd\uff0c\u4e91\u91cd\u65e0\u6709\u4e0d\u4f9d\uff0c\u4f46\u6b64 \u9645\u5374\u5982\u5931\u9b42\u843d\u9b44\uff0c\u542c\u800c\u4e0d\u95fb\uff0c\u4ecd\u7136\u662f\u671d\u7740\u5927\u95e8\u76f4\u8d70\u3002 \u3000\u3000\u5ffd\u53c8\u542c\u5f97\u5916\u9762\u8e44\u58f0\u5f97\u5f97\uff0c\u5954\u5230\u95e8\u524d\uff0c\u621b\u7136\u800c\u6b62\uff0c\u597d\u51e0\u4e2a\u58f0\u97f3\u540c\u65f6\u53eb\u9053\uff1a\u201c\u5927\u660e\u5929\u5b50\u9a7e\u5e78 \u5f20\u5bb6\u3002\u201d\u539f\u6765\u7948\u9547\u9a6c\u8fdf\uff0c\u73b0\u5728\u624d\u5230\uff0c\u4ed6\u867d\u7136\u5c1a\u672a\u8131\u4fd8\u864f\u7684\u8eab\u4efd\uff0c\u4ecd\u672a\u5fd8\u8bb0\u6446\u7687\u5e1d\u7684\u67b6\u5b50\u3002 \u3000\u3000\u56ed\u5185\u65e0\u4eba\u7406\u4f1a\uff0c\u5f20\u5b97\u5468\u5750\u5728\u77f3\u4e0a\uff0c\u52a8\u4e5f\u4e0d\u52a8\uff1b\u6fb9\u53f0\u706d\u660e\u6a2a\u76ee\u6012\u89c6\uff0c\u77aa\u4e86\u4ed6\u4e00\u773c\uff0c\u53c8\u56de\u8fc7 \u6765\uff0c\u4ecd\u7136\u548c\u59b9\u59b9\u8bf4\u8bdd\uff0c\u53ea\u6709\u4e91\u91cd\u548c\u4ed6\u7684\u968f\u4ece\uff0c\u6b62\u4f4f\u4e86\u811a\u6b65\u3002\u7948\u9547\u597d\u751f\u6ca1\u8da3\uff0c\u559d\u9053\uff1a\u201c\u8c01\u662f\u5f20 \u5b97\u5468\uff0c\u4e3a\u4f55\u4e0d\u6765\u63a5\u9a7e\uff1f\u201d\u5f20\u5b97\u5468\u6602\u9996\u5411\u5929\uff0c\u597d\u50cf\u6839\u672c\u5c31\u770b\u4e0d\u89c1\u7948\u9547\u8fd9\u4e00\u4e2a\u4eba\uff0c\u7948\u9547\u8ba4\u4e0d\u5f97\u5f20 \u5b97\u5468\u5374\u8ba4\u5f97\u5f20\u4e39\u67ab\uff0c\u671d\u7740\u5f20\u4e39\u67ab\u559d\u9053\uff1a\u201c\u4f60\u7236\u4eb2\u5462\uff1f\u4f60\u7236\u5b50\u4e43\u53db\u9006\u4e4b\u540e\uff0c\u6715\u4eca\u7279\u964d\u6d2a\u6069\uff0c\u514d \u4e8e\u8ffd\u7a76\u3002\u4f60\u7b49\u5c1a\u4e0d\u6765\u63a5\u9a7e\u4e48\uff1f\u201d\u5f20\u4e39\u67ab\u51b7\u51b7\u4e00\u7b11\uff0c\u7948\u9547\u53ea\u89c9\u5f97\u4ed6\u7684\u773c\u5149\u5c04\u5230\u81ea\u5df1\u7684\u72d0\u76ae\u62ab\u80a9 \u4e0a\uff0c\u4e0d\u89c9\u5f97\u9762\u4e0a\u4e00\u7ea2\uff0c\u5fc3\u4e2d\u6c14\u59a5\uff0c\u672c\u6765\u662f\u5927\u58f0\u8bf4\u8bdd\uff0c\u8d8a\u8bf4\u8d8a\u5f31\uff0c\u8bf4\u5230\u540e\u9762\u51e0\u4e2a\u5b57\u65f6\uff0c\u7b80\u76f4\u53ea \u6709\u4ed6\u81ea\u5df1\u624d\u542c\u89c1\u4e86\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u51b7\u51b7\u4e00\u7b11\uff0c\u5ffd\u5730\u4ece\u6000\u4e2d\u638f\u51fa\u4e00\u5305\u4e1c\u897f\uff0c\u63b7\u4e8e\u5730\u4e0a\uff0c\u9053\uff1a\u201c\u8fd9\u4e24\u4ef6\u4e1c\u897f\u4f60\u597d\u751f\u4fdd \u7ba1\uff0c\u4f11\u8981\u518d\u4e22\u5931\u4e86\uff01\u201d\u65e9\u6709\u536b\u58eb\u5c06\u5b83\u62fe\u8d77\uff0c\u5448\u5230\u7948\u9547\u9762\u524d\uff0c\u89e3\u5f00\u4e00\u770b\uff0c\u91cc\u9762\u5305\u7740\u7684\u4e24\u4ef6\u4e1c \u897f\uff0c\u4e00\u4ef6\u662f\u523b\u6709\u201c\u6b63\u7edf\u7687\u5e1d\u4e4b\u5370\u201d\u7684\u9f99\u7eb9\u6c49\u7389\u79c1\u7ae0\uff0c\u90a3\u662f\u4ec5\u6b21\u4e8e\u56fd\u73ba\u7684\u5b9d\u7269\uff1b\u53e6\u5916\u4e00\u4ef6\u5219\u662f \u7687\u540e\u9001\u7ed9\u7948\u9547\u7684\u78a7\u7389\u5934\u7c2a\u3002\u8fd9\u4e24\u4ef6\u4e1c\u897f\u90fd\u662f\u7948\u9547\u5728\u571f\u6728\u5821\u6218\u4e71\u4e4b\u65f6\uff0c\u88ab\u4ed6\u7684\u5927\u5185\u603b\u7ba1\u5eb7\u8d85\u6d77 \u76d7\u53bb\u7684\u3002\u5f20\u4e39\u67ab\u4ece\u5eb7\u8d85\u6d77\u7684\u624b\u4e2d\u62a2\u56de\uff0c\u73b0\u5728\u624d\u6709\u673a\u4f1a\u8fd8\u7ed9\u4ed6\u3002 \u3000\u3000\u7948\u9547\u66f4\u4e3a\u7f9e\u6012\uff0c\u7687\u5e1d\u7684\u9762\u5b50\u7adf\u88ab\u4e22\u5c3d\uff0c\u4f46\u5fc3\u4e2d\u865a\u602f\uff0c\u60f3\u53d1\u4f5c\u53c8\u53d1\u4f5c\u4e0d\u51fa\u6765\u3002\u6b63\u6b32\u62ff\u4e91\u91cd \u51fa\u6c14\uff0c\u5ffd\u89c1\u4e09\u4e2a\u602a\u4eba\u5982\u98de\u8dd1\u8fdb\uff0c\u524d\u5934\u4e24\u4e2a\uff0c\u76f8\u8c8c\u76f8\u540c\uff0c\u4e00\u9ed1\u4e00\u767d\uff0c\u624b\u821e\u8db3\u8e48\uff0c\u5927\u547c\u5c0f\u53eb\uff0c\u66f4 \u4f3c\u65c1\u82e5\u65e0\u4eba\u3002 \u3000\u3000\u8fd9\u4e09\u4e2a\u4eba\u4e43\u662f\u8f70\u5929\u96f7\u77f3\u82f1\u548c\u9ed1\u767d\u6469\u8bc3\uff0c\u8499\u53e4\u5175\u64a4\u8d70\uff0c\u4ed6\u4eec\u7acb\u5373\u626b\u5c3d\u84ba\u85dc\uff0c\u8d76\u6765\u76f8\u4f1a\u3002\u7948 \u9547\u7684\u536b\u58eb\u559d\u9053\uff1a\u201c\u4f55\u6765\u72c2\u5f92\uff0c\u60ca\u52a8\u5723\u9a7e\uff01\u201d\u4e0a\u524d\u963b\u62e6\uff0c\u77f3\u82f1\u7765\u7768\u659c\u89c6\uff0c\u626b\u4e86\u7948\u9547\u4e00\u773c\uff0c\u53cc\u624b \u4e00\u4f38\uff0c\u628a\u4e24\u4e2a\u536b\u58eb\u5939\u9886\u63d0\u8d77\u6765\uff0c\u6454\u51fa\u4e08\u5916\uff0c\u9ed1\u767d\u6469\u8bc3\u54c8\u54c8\u5927\u7b11\uff0c\u53cc\u6756\u9f50\u4f38\uff0c\u4e5f\u5c06\u4e24\u4e2a\u536b\u58eb\u6454 \u5f97\u56db\u811a\u671d\u5929\u3002\u7948\u9547\u5927\u60ca\u6025\u5fd9\u540e\u9000\uff0c\u53ea\u89c1\u9ed1\u767d\u6469\u8bc3\u62c9\u7740\u5f20\u4e39\u67ab\u6b22\u547c\u8df3\u8dc3\uff0c\u77f3\u82f1\u5219\u8dea\u5012\u5f20\u5b97\u5468\u8ddf \u524d\u3002 \u3000\u3000\u5f20\u5b97\u5468\u6276\u8d77\u77f3\u82f1\uff0c\u81ea\u5df1\u5374\u6447\u6447\u6643\u6643\uff0c\u597d\u50cf\u7ad9\u7acb\u4e0d\u7a33\uff0c\u4ecd\u7136\u5750\u4e0b\u3002\u77f3\u82f1\u6cea\u54bd\u5fc3\u9178\uff0c\u53eb\u4e86\u4e00 \u58f0\uff1a\u201c\u4e3b\u516c\u3002\u201d\u5f20\u5b97\u5468\u9053\uff1a\u201c\u77f3\u5c06\u519b\uff0c\u8fd9\u51e0\u5341\u5e74\u4e8f\u4e86\u4f60\u4e86\u3002\u201d\u77f3\u82f1\u5148\u7956\u662f\u5f20\u58eb\u8bda\u7684\u9f99\u9a91\u90fd \u5c09\uff0c\u6545\u6b64\u5f20\u5b97\u5468\u4ee5\u201c\u5c06\u519b\u201d\u79f0\u4ed6\u3002\u77f3\u82f1\u9053\uff1a\u201c\u56fd\u5b9d\uff08\u6307\u90a3\u5e45\u753b\uff09\u5df2\u5f52\u56de\u5c11\u4e3b\uff0c\u53ef\u60dc\u6c5f\u5c71\u4ecd\u975e \u5927\u5468\u3002\u201d\u5f20\u5b97\u5468\u6447\u624b\u82e6\u7b11\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u5168\u90fd\u77e5\u9053\u4e86\uff0c\u4e0d\u5fc5\u8bf4\u5566\u3002\u4eba\u751f\u4f46\u613f\u5fc3\u65e0\u6127\uff0c\u593a\u9738\u4e89 \u738b\u5e95\u4e8b\u7531\uff01\u201d \u3000\u3000\u7948\u9547\u5fc3\u4e2d\u4e00\u6014\uff0c\u6307\u7740\u4e91\u91cd\u8bf4\u9053\uff1a\u201c\u86ee\u91ce\u9119\u592b\uff0c\u4e0d\u53ef\u76f8\u5904\u3002\u4e91\u72b6\u5143\uff0c\u4f60\u5feb\u4fdd\u9a7e\u56de\u671d\u3002\u201d\u4e91 \u91cd\u4ecd\u7136\u662f\u90a3\u526f\u5931\u9b42\u843d\u9b44\u7684\u6837\u5b50\uff0c\u4e0d\u8a00\u4e0d\u8bed\u3002\u7948\u9547\u6012\u9053\uff1a\u201c\u4f60\u4eec\u90fd\u75af\u5566\uff01\u201d\u4e91\u91cd\u95ea\u8fc7\u4e00\u8fb9\uff0c\u5e26 \u7740\u968f\u4ece\uff0c\u95f7\u58f0\u4e0d\u54cd\u5730\u62a4\u536b\u4e24\u65c1\uff0c\u521a\u521a\u8d70\u5230\u56ed\u95e8\uff0c\u4e91\u91cd\u5ffd\u7136\u53c8\u505c\u4f4f\u4e86\u811a\u6b65\uff0c\u9762\u8272\u5237\u5730\u53d8\u5f97\u60e8\u5982 \u767d\u7eb8\u3002 \u3000\u3000\u53ea\u89c1\u4e00\u4e2a\u7f8e\u8c8c\u5982\u82b1\u7684\u5c11\u5973\uff0c\u6276\u7740\u4e00\u4e2a\u5f62\u5bb9\u6194\u60b4\u3001\u5934\u53d1\u7a00\u758f\u6591\u767d\u7684\u8001\u5934\uff0c\u8d70\u5165\u95e8\u6765\u3002\u8fd9\u8001 \u5934\u9762\u4e0a\u4ea4\u53c9\u7740\u51e0\u9053\u4f24\u75d5\uff0c\u8ddb\u4e86\u4e00\u8db3\uff0c\u5728\u5c11\u5973\u7684\u6276\u6301\u4e4b\u4e0b\u4e00\u8df7\u4e00\u62d0\u5730\u8d70\u7740\uff0c\u9762\u4e0a\u795e\u6c14\u6781\u662f\u9a87 \u4eba\uff0c\u7948\u9547\u4e0d\u89c9\u6253\u4e86\u4e00\u4e2a\u5bd2\u5664\u3002\u53ea\u542c\u5f97\u4e91\u91cd\u7a81\u7136\u98a4\u58f0\u53eb\u9053\uff1a\u201c\u7239\uff01\u201d\u8dd1\u4e0a\u524d\u53bb\uff0c\u62b1\u7740\u90a3\u8001\u5934\u3002 \u3000\u3000\u4e91\u6f84\u7406\u4e5f\u4e0d\u7406\uff0c\u7adf\u7136\u4e00\u624b\u5c06\u513f\u5b50\u63a8\u5f00\uff0c\u76ee\u4e0d\u8f6c\u775b\u5730\u76ef\u7740\u5f20\u5b97\u5468\uff0c\u4e00\u6b65\u4e00\u6b65\uff0c\u671d\u4ed6\u8d70\u53bb\u3002 \u8fd9\u53ef\u6015\u7684\u795e\u6c14\uff0c\u4ee4\u77f3\u82f1\u4e5f\u5413\u5f97\u95ea\u5f00\u4e00\u8fb9\u3002\u77f3\u82f1\u62ac\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u5728\u4e91\u6f84\u7236\u5973\u4e4b\u540e\uff0c\u8fd8\u6709\u81ea\u5df1\u7684 \u5973\u513f\u3001\u5973\u5a7f\uff1a\u77f3\u7fe0\u51e4\u548c\u5468\u5c71\u6c11\u3002\u77f3\u82f1\u6025\u5fd9\u6487\u5f00\u5f20\u5b97\u5468\uff0c\u4e0a\u53bb\u8fce\u63a5\u5973\u513f\uff0c\u5468\u5c71\u6c11\u548c\u77f3\u7fe0\u51e4\u4e5f\u4e0d \u6562\u4f5c\u58f0\uff0c\u9762\u8272\u6c89\u6697\u3002 \u3000\u3000\u539f\u6765\u4e91\u6f84\u56e0\u4e3a\u8ddb\u4e86\u4e00\u8db3\uff0c\u96be\u4ee5\u8d70\u8def\uff0c\u6240\u4ee5\u4eca\u65e5\u624d\u5230\u74e6\u523a\u4eac\u57ce\uff0c\u81f3\u5ba2\u6808\u4e00\u95ee\uff0c\u59cb\u77e5\u4e91\u91cd\u7adf \u7136\u5230\u4e86\u5f20\u5bb6\u3002\u4e91\u6f84\u8fd9\u4e00\u6c14\u975e\u540c\u5c0f\u53ef\uff0c\u7acb\u523b\u903c\u5973\u513f\u5c06\u4ed6\u5e26\u6765\uff0c\u8fd9\u65f6\u4ed6\u91cd\u89c1\u513f\u5b50\u7684\u6b22\u6b23\uff0c\u65e9\u5df2\u88ab \u9762\u7779\u4ec7\u4eba\u7684\u75db\u6068\u6240\u906e\u76d6\u4e86\u3002 \u3000\u3000\u8fd9\u970e\u90a3\u95f4\uff0c\u5f20\u4e39\u67ab\u5982\u53d7\u96f7\u51fb\uff0c\u9762\u8272\u4e5f\u5237\u5730\u4e00\u4e0b\u53d8\u5f97\u60e8\u767d\u3002\u773c\u524d\u5c31\u662f\u81ea\u5df1\u9b42\u7275\u68a6\u8426\u7684\u201c\u5c0f \u5144\u5f1f\u201d\u3002\u53ef\u662f\u4e91\u857e\u5374\u770b\u90fd\u4e0d\u770b\u4ed6\u4e00\u773c\u3002\u53ea\u6709\u4e91\u6f84\u7684\u773c\u5149\u8c61\u5229\u5203\u4e00\u6837\uff0c\u5728\u5272\u7740\u4ed6\u7684\u5fc3\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u53eb\u4e86\u4e00\u58f0\uff0c\u5929\u4e0d\u6015\u5730\u4e0d\u6015\u7684\u4ed6\uff0c\u8fd9\u65f6\u4e5f\u611f\u5230\u96be\u4ee5\u8a00\u5ba3\u7684\u6218\u6817\uff0c\u4e91\u6f84\u7684\u795e\u6c14\u6bd4\u8d77\u5c06 \u4e91\u857e\u5f3a\u8feb\u79bb\u5f00\u4ed6\u65f6\u66f4\u4ee4\u4eba\u9a87\u6015\u3002\u53ea\u89c1\u4ed6\u4e00\u6b65\u4e00\u6b65\u8d70\u5230\u4e86\u5f20\u5b97\u5468\u7684\u9762\u524d\uff0c\u770b\u6837\u5b50\u4ec0\u4e48\u4e8b\u90fd\u505a\u5f97 \u51fa\u6765\u3002 \u3000\u3000\u5f20\u5b97\u5468\u62ac\u8d77\u773c\u775b\uff0c\u53ea\u89c1\u4e91\u6f84\u7ad9\u5728\u4ed6\u7684\u9762\u524d\uff0c\u51b0\u51b7\u7684\u773c\u5149\uff0c\u51b0\u51b7\u7684\u9762\u5b54\uff0c\u72e0\u72e0\u5730\u76ef\u7740\u4ed6\uff0c \u52a8\u4e5f\u4e0d\u52a8\uff0c\u5c31\u5982\u4e00\u5c0a\u7528\u5927\u7406\u77f3\u96d5\u6210\u7684\u590d\u4ec7\u9b54\u9b3c\uff01\u5f20\u4e39\u67ab\u548c\u4e91\u91cd\u90fd\u540c\u65f6\u53eb\u4e86\u4e00\u58f0\uff0c\u5954\u4e0a\u524d\u53bb\uff0c \u4e91\u6f84\u5934\u4e5f\u4e0d\u56de\uff0c\u53cd\u624b\u4e00\u638c\uff0c\u5c31\u6253\u4e86\u4e91\u91cd\u4e00\u8bb0\u8033\u5149\uff0c\u4e91\u91cd\u8dea\u5728\u5730\u4e0a\u53eb\u9053\uff1a\u201c\u7239\uff0c\u79bb\u5f00\u8fd9\u513f\u5427\uff0c \u79bb\u5f00\u8fd9\u513f\u5427\uff01\u201d\u5f20\u4e39\u67ab\u4e5f\u4e0a\u53bb\u6276\u7740\u5f20\u5b97\u5468\u7684\u80a9\u5934\uff0c\u9053\uff1a\u201c\u7239\uff0c\u4f60\u56de\u53bb\u6b47\u6b47\u5427\uff01\u201d\u5f20\u5b97\u5468\u4e5f\u662f \u5934\u4e5f\u4e0d\u56de\uff0c\u624b\u81c2\u8f7b\u8f7b\u4e00\u62d4\uff0c\u5c06\u5f20\u4e39\u67ab\u63a8\u5f00\u3002\u4e91\u857e\u4e5f\u5fcd\u4e0d\u4f4f\u4e86\uff0c\u63a9\u9762\u54ed\u6ce3\uff0c\u4f4e\u4f4e\u53eb\u4e86\u4e00\u58f0 \u201c\u7239\uff01\u201d\u4e91\u6f84\u4ecd\u7136\u542c\u800c\u4e0d\u95fb\uff0c\u597d\u50cf\u6574\u4e2a\u4e16\u754c\u4e0a\u5c31\u53ea\u5269\u4e0b\u4e86\u4e00\u4e2a\u5f20\u5b97\u5468\uff0c\u4ed6\u72e0\u72e0\u5730\u76ef\u7740\u5f20\u5b97 \u5468\uff0c\u90a3\u773c\u5149\u7adf\u4f3c\u5305\u542b\u4e86\u4eba\u95f4\u6240\u6709\u7684\u6028\u6068\uff01 \u3000\u3000\u5f20\u5b97\u5468\u5ffd\u5730\u6de1\u6de1\u5730\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u65e9\u5c31\u6599\u5230\u4e86\u4eca\u65e5\uff0c\u6211\u800c\u4eca\u5c31\u53bb\u627e\u4f60\u7684\u7236\u4eb2\u4e91\u9756\u5927\u4eba\u4eb2 \u81ea\u9053\u6b49\uff0c\u8fd9\u6837\uff0c\u4f60\u6211\u4e24\u5bb6\u7684\u51a4\u4ec7\u603b\u53ef\u4ee5\u6d88\u89e3\u4e86\u5427\uff01\u201d\u8bdd\u58f0\u8d8a\u6765\u8d8a\u5f31\uff0c\u8bf4\u5230\u6700\u540e\u4e00\u4e2a\u5b57\uff0c\u5ffd\u7136 \u7ffb\u8eab\u8dcc\u5012\uff0c\u8033\u9f3b\u6d41\u8840\uff0c\u5bc2\u7136\u4e0d\u52a8\uff0c\u7adf\u662f\u6b7b\u4e86\u3002\u539f\u6765\u5f20\u5b97\u5468\u65e9\u5df2\u840c\u6b7b\u5fd7\uff0c\u89c1\u4e86\u4e91\u91cd\u4e4b\u540e\uff0c\u5c31\u5077 \u5077\u541e\u4e0b\u4e86\u65e9\u5df2\u51c6\u5907\u3001\u968f\u8eab\u643a\u5e26\u7684\u6bd2\u836f\uff0c\u8fd9\u6bd2\u836f\u542b\u6709\u201c\u9e64\u9876\u7ea2\u201d\u6240\u70bc\u7684\u7c89\u672b\uff0c\u6070\u6070\u5c31\u662f\u4e91\u9756\u5f53 \u5e74\u88ab\u738b\u632f\u6bd2\u6b7b\u7684\u90a3\u79cd\u6bd2\u836f\uff0c\u7eb5\u6709\u91d1\u4e39\u5999\u836f\uff0c\u4ea6\u96be\u76f8\u6551\u3002 \u3000\u3000\u5f20\u5b97\u5468\u7a81\u7136\u81ea\u6740\u8eab\u4ea1\uff0c\u5728\u573a\u7684\u4eba\u8c01\u4e5f\u6ca1\u6709\u6599\u5230\u3002\u5f20\u4e39\u67ab\u9762\u8272\u5982\u6b7b\uff0c\u773c\u775b\u53d1\u76f4\uff0c\u54ed\u4e0d\u51fa\u58f0 \u6765\u3002\u4e91\u857e\u60e8\u53eb\u4e00\u58f0\uff0c\u8dcc\u5012\u5730\u4e0a\u3002\u4e91\u6f84\u4e5f\u50cf\u6cc4\u6c14\u7684\u76ae\u7403\uff0c\u9893\u7136\u5730\u5750\u4e0b\u3002\u6fb9\u53f0\u706d\u660e\u548c\u77f3\u82f1\u9ad8\u53eb \u201c\u4e3b\u516c\u201d\uff0c\u4e91\u91cd\u8df3\u4e0a\u524d\u53bb\u60f3\u6276\u5f20\u4e39\u67ab\uff0c\u5f20\u4e39\u67ab\u5ffd\u7136\u63a9\u9762\u72c2\u5954\uff0c\u4e00\u8dc3\u8dc3\u4e0a\u6b63\u5728\u56ed\u4e2d\u8349\u5730\u4e0a\u5403\u8349 \u7684\u767d\u9a6c\uff0c\u90a3\u5339\u7167\u591c\u72ee\u5b50\u9a6c\u4e00\u58f0\u957f\u5636\uff0c\u9a6e\u7740\u4e3b\u4eba\uff0c\u7bad\u4e00\u822c\u5730\u5c04\u51fa\u56ed\u95e8\uff0c\u500f\u5ffd\u4e0d\u89c1\u3002 \u3000\u3000\u56ed\u4e2d\u9759\u5bc2\u5982\u6b7b\uff0c\u53ea\u6709\u4e91\u857e\u7684\u4f4e\u4f4e\u555c\u6ce3\u4e4b\u58f0\u3002 \u3000\u3000\u4e24\u4e2a\u6708\u540e\uff0c\u6b63\u662f\u6c5f\u5357\u521d\u590f\uff0c\u98ce\u5149\u660e\u5a9a\u7684\u65f6\u8282\uff0c\u84df\u5dde\u57ce\u5916\uff0c\u6709\u4e00\u4e2a\u5c11\u5e74\uff0c\u9a91\u7740\u4e00\u5339\u767d\u9a6c\uff0c \u5355\u9a91\u72ec\u884c\u3002\u8fd9\u5c11\u5e74\u4fbf\u662f\u5f20\u4e39\u67ab\u3002 \u3000\u3000\u4e24\u4e2a\u6708\u7684\u65f6\u5149\u4e0d\u7b97\u957f\uff0c\u4f46\u4e16\u5c40\u53c8\u5df2\u8d77\u4e86\u4e00\u756a\u53d8\u5316\u3002\u4e91\u91cd\u5c06\u7948\u9547\u63a5\u56de\u4e4b\u540e\uff0c\u7948\u9547\u7684\u5f1f\u5f1f\uff0c \u73b0\u4efb\u7687\u5e1d\u7948\u94b0\uff08\u660e\u4ee3\u5b97\uff09\u4e0d\u80af\u8ba9\u4f4d\uff0c\u7948\u9547\u4e00\u56de\u6765\u5c31\u88ab\u4ed6\u56da\u5728\u7687\u57ce\u91cc\u7684\u5357\u5bab\uff0c\u540d\u4e49\u4e0a\u5c0a\u4e3a\u201c\u592a \u4e0a\u7687\u201d\uff0c\u5b9e\u9645\u4e0a\u662f\u4e2a\u56da\u72af\u3002\u7948\u9547\u7684\u7687\u5e1d\u68a6\u843d\u4e86\u7a7a\uff0c\u4e8e\u8c26\u6574\u987f\u56fd\u5bb6\u7684\u7f8e\u68a6\u4e5f\u843d\u4e86\u7a7a\uff0c\u56e0\u4e3a\u7948\u94b0 \u73b0\u5728\u5df2\u4e0d\u5fc5\u501a\u4ed7\u4e8e\u8c26\u4e86\uff0c\u7948\u94b0\u5265\u593a\u4e86\u4e8e\u8c26\u7684\u6743\u67c4\uff0c\u53ea\u53eb\u4ed6\u505a\u4e00\u6302\u540d\u7684\u201c\u5175\u90e8\u5c1a\u4e66\u201d\uff0c\u4e0d\u8bb8\u4ed6 \u518d\u5e72\u9884\u671d\u5ef7\u7684\u201c\u65bd\u653f\u5927\u8ba1\u201d\u3002 \u3000\u3000\u738b\u632f\u7b49\u4e00\u73ed\u65e7\u65f6\u6743\u8d35\u90fd\u5df2\u5012\u4e0b\uff0c\u4f46\u5f88\u5feb\u5c31\u6709\u4e00\u73ed\u65b0\u7684\u6743\u8d35\u722c\u8d77\u6765\uff0c\u201c\u541b\u81e3\u9189\u4e50\u5e86\u592a \u5e73\u201d\uff0c\u660f\u660f\u7136\u7eb7\u7eb7\u7136\u3002\u7b80\u76f4\u5fd8\u8bb0\u4e86\u90a3\u201c\u571f\u6728\u5821\u4e4b\u53d8\u201d\uff0c\u56fd\u5bb6\u9669\u88ab\u706d\u4ea1\u7684\u60e8\u75db\u4e86\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u5931\u610f\u60c5\u573a\uff0c\u60e8\u906d\u5bb6\u96be\uff0c\u66f4\u52a0\u4e0a\u4f24\u5fc3\u56fd\u4e8b\uff0c\u4ed6\u6084\u6084\u7684\u5728\u5317\u4eac\u8eb2\u4e86\u51e0\u5929\uff0c\u8fde\u4e8e\u8c26\u4e5f\u4e0d \u53bb\u89c1\uff0c\u5c31\u5355\u9a91\u72ec\u884c\uff0c\u56de\u5230\u6c5f\u5357\u3002 \u3000\u3000\u6c5f\u5357\u660e\u5a9a\u7684\u98ce\u5149\uff0c\u5e76\u6ca1\u6709\u89e3\u9664\u4ed6\u5fc3\u4e2d\u7684\u60b2\u75db\uff0c\u4ed6\u7b56\u9a6c\u6162\u884c\u8d70\u5230\u82cf\u5dde\u57ce\u5916\uff0c\u5ffd\u5730\u4ef0\u5929\u541f \u9053\uff1a\u201c\u5929\u9053\u65e0\u5e38\u4eba\u4e8b\u6539\uff0c\u6c5f\u5c71\u5386\u52ab\u5269\u65b0\u6101\uff01\u201d\u4ece\u6000\u4e2d\u638f\u51fa\u4e00\u7eb8\u67d3\u6ee1\u6cea\u75d5\u7684\u4fe1\uff0c\u4fe1\u7b3a\u4e0a\u7684\u5b57 \u53e5\uff0c\u4ed6\u65e9\u5df2\u8bfb\u4e86\u6570\u5341\u767e\u904d\uff0c\u4e0d\u7528\u770b\u4ed6\u4e5f\u80cc\u5f97\u51fa\u6765\u3002\u90a3\u5c01\u4fe1\u662f\u4ed6\u7236\u4eb2\u5728\u4e34\u6b7b\u7684\u524d\u4e00\u5915\uff0c\u5077\u5077\u653e \u5728\u4ed6\u7684\u8863\u888b\u4e2d\u7559\u7ed9\u4ed6\u7684\u3002\u90a3\u5c01\u4fe1\u662f\u8fd9\u6837\u5199\u7684\uff1a \u3000\u3000\u543e\u4ee5\u5f53\u5e74\u4e00\u5ff5\u4e4b\u5dee\uff0c\u8bef\u6295\u74e6\u523a\uff0c\u7ed3\u6028\u4e91\u5bb6\u3002\u6211\u867d\u4e0d\u6740\u4f2f\u4ec1\u4f2f\u4ec1\u7531\u6211\u800c\u6b7b\uff0c\u4e91\u9756\u5b50\u5b59\uff0c\u6068 \u543e\u5982\u4ec7\uff0c\u7406\u6240\u5f53\u7136\u3002\u543e\u4eca\u51b3\u610f\u4ee5\u6b7b\u8d4e\u7f6a\uff0c\u975e\u4e3a\u4e91\u5bb6\uff0c\u4ea6\u4e3a\u65e0\u989c\u91cd\u5f52\u6545\u56fd\u4e5f\u3002\u4eba\u751f\u5fc5\u6709\u6b7b\uff0c\u543e \u4ee5\u8870\u66ae\u4e4b\u5e74\uff0c\u5f97\u89c1\u5927\u6c49\u4f7f\u81e3\uff0c\u5a01\u64ad\u5f02\u56fd\uff0c\u6b7b\u800c\u65e0\u6068\u3002\u4f60\u89c1\u8bc6\u80dc\u6211\u767e\u500d\uff0c\u6709\u5b50\u5982\u6b64\uff0c\u6211\u53ef\u65e0\u7275 \u6302\u77e3\u3002\u6211\u6b7b\u540e\u4f60\u5f53\u7acb\u5373\u5f52\u56fd\uff0c\u4e0e\u4e91\u5bb6\u91ca\u5acc\u4fee\u597d\uff0c\u8d4e\u6211\u7f6a\u884c\u3002\u4f60\u4e0e\u4e91\u9756\u5b59\u5973\u76f8\u7231\u76f8\u601c\u4e4b\u4e8b\uff0c\u6fb9 \u53f0\u5c06\u519b\u4ea6\u5df2\u544a\u4e0e\u6211\u77e5\u3002\u6b64\u4e8b\u82e5\u6210\uff0c\u6211\u66f4\u65e0\u61be\u77e3\u3002 \u3000\u3000\u7236\u4eb2\u7684\u5f71\u5b50\u5728\u5f20\u4e39\u67ab\u5fc3\u4e2d\u6cdb\u8d77\uff1a\u7236\u4eb2\u505a\u8fc7\u9519\u4e8b\uff0c\u4e5f\u505a\u8fc7\u597d\u4e8b\uff0c\u4ed6\u5e2e\u52a9\u4e86\u74e6\u523a\u5f3a\u5927\uff0c\u4e5f\u6697 \u4e2d\u5e2e\u52a9\u7956\u56fd\u6253\u51fb\u4e86\u4e5f\u5148\u3002\u5f20\u4e39\u67ab\u5e74\u8f7b\u65f6\u89c9\u5f97\u4e0d\u53ef\u7406\u89e3\u7684\u7236\u4eb2\uff0c\u800c\u4eca\u5df2\u5b8c\u5168\u53ef\u4ee5\u7406\u89e3\u4e86\u3002\u7236\u4eb2 \u50cf\u4ed6\u4e00\u6837\u9a84\u50b2\uff08\u53ef\u60dc\u8fd9\u9a84\u50b2\u5374\u5f15\u4ed6\u8d70\u5165\u6b67\u9014\uff09\uff0c\u7236\u4eb2\u4e5f\u50cf\u4ed6\u4e00\u6837\u8840\u7ba1\u4e2d\u6d41\u7684\u662f\u4e2d\u56fd\u4eba\u7684\u8840 \u6db2\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u5728\u5fc3\u4e2d\u91cd\u8bfb\u4e86\u8fd9\u5c01\u4fe1\u4e00\u904d\uff0c\u53e6\u4e00\u4e2a\u5f71\u5b50\u53c8\u6cdb\u4e0a\u6765\uff0c\u8fd9\u662f\u4e91\u857e\uff0c\u662f\u7236\u4eb2\u5e0c\u671b\u4ed6\u80fd\u591f \u4e0e\u4e4b\u7ed3\u5408\u7684\u4e91\u857e\uff01\u53ef\u662f\u7ecf\u8fc7\u4e86\u90a3\u4e00\u573a\u4f24\u5fc3\u60e8\u75db\u7684\u4e8b\u4ef6\u4e4b\u540e\uff0c\u6b64\u751f\u6b64\u4e16\uff0c\u53ea\u6050\u6015\u662f\u76f8\u89c1\u65e0\u65af\uff0c \u8fd8\u8bf4\u4ec0\u4e48\u8c08\u5a5a\u8bba\u5ac1\uff1f\u5f20\u4e39\u67ab\u8fd9\u4e24\u4e2a\u6708\u6765\u6101\u80a0\u5bf8\u65ad\uff0c\u51e0\u4e4e\u53c8\u5230\u4e86\u5982\u75f4\u5982\u72c2\u7684\u5730\u6b65\u3002\u8fd9\u6b21\u5f52\u6765\uff0c \u672c\u6b32\u501f\u6c5f\u5357\u666f\u8272\uff0c\u804a\u89e3\u6101\u70e6\uff0c\u54ea\u77e5\u4e0d\u5230\u6c5f\u5357\uff0c\u8fd8\u81ea\u7f62\u4e86\uff0c\u4e00\u5230\u6c5f\u5357\uff0c\u5374\u4e0d\u7531\u81ea\u5df1\u5730\u66f4\u60f3\u8d77\u4e91 \u857e\uff0c\u60f3\u5f53\u5e74\u5e76\u8f94\u540c\u6765\uff0c\u4e5f\u6b63\u662f\u8fd9\u4e2a\u6885\u5b50\u9ec4\u65f6\uff0c\u69b4\u82b1\u521d\u653e\u7684\u5b63\u8282\uff0c\u4e00\u8def\u4e0a\u66fe\u7559\u4e0b\u591a\u5c11\u7b11\u58f0\uff0c\u591a \u5c11\u6cea\u75d5\uff0c\u5230\u800c\u4eca\u5374\u771f\u50cf\u674e\u6e05\u7167\u8bcd\u6240\u8bf4\u7684\u201c\u7269\u662f\u4eba\u975e\u4e8b\u4e8b\u4f11\uff0c\u65e0\u8bed\u6cea\u5148\u6d41\u3002\u201d\u66f4\u4f24\u5fc3\u7684\u662f\uff1a \u201c\u67d4\u80a0\u5df2\u65ad\u65e0\u7531\u65ad\u201d\uff0c\u201c\u6cea\u5df2\u5c3d\uff0c\u90a3\u80fd\u6d41\uff01\u201d \u3000\u3000\u53e4\u57ce\u5982\u753b\uff0c\u666f\u8272\u8fd8\u4f3c\u5f53\u5e74\u7684\u6d45\u7b11\u7684\u8f7b\u9891\uff0c\u4e0d\u4f4f\u5730\u5728\u773c\u524d\u6447\u6643\uff0c\u5f20\u4e39\u67ab\u7981\u4e0d\u4f4f\u4f4e\u4f4e\u5730\u53f9\u4e86 \u4e00\u58f0\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u4e00\u5207\u90fd\u592a\u8fdf\u4e86\u554a\uff01\u201d \u3000\u3000\u5ffd\u542c\u5f97\u4e00\u58f0\u5a07\u7b11\uff0c\u5f20\u4e39\u67ab\u7684\u8033\u8fb9\u5c31\u4f3c\u542c\u5f97\u4e91\u857e\u8bf4\u9053\uff1a\u201c\u8c01\u8bf4\u592a\u8fdf\uff1f\u4f60\u600e\u4e48\u4e0d\u7b49\u6211\u554a\uff1f\u201d \u5f20\u4e39\u67ab\u56de\u5934\u4e00\u770b\u671b\uff0c\u53ea\u89c1\u4e00\u5339\u67a3\u7ea2\u9a6c\u4e0a\uff0c\u9a91\u7684\u6b63\u662f\u4e91\u857e\uff0c\u6d45\u7b11\u76c8\u76c8\uff0c\u8fd8\u662f\u5f53\u5e74\u6a21\u6837\u3002 \u3000\u3000\u8fd9\u662f\u68a6\u5883\uff0c\u8fd8\u662f\u771f\u4eba\uff1f\u5f20\u4e39\u67ab\u53c8\u60ca\u53c8\u559c\uff0c\u53ea\u89c1\u4e91\u857e\u7b56\u9a6c\u884c\u6765\uff0c\u4f4e\u7709\u4e00\u7b11\uff0c\u62db\u624b\u8bf4\u9053\uff1a \u201c\u50bb\u54e5\u54e5\uff0c\u4f60\u4e0d\u8ba4\u5f97\u6211\u4e48\uff1f\u201d\u5440\uff0c\u8fd9\u7adf\u7136\u4e0d\u662f\u68a6\u5883\uff01\u5f20\u4e39\u67ab\u5927\u559c\u82e5\u72c2\uff0c\u53eb\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u771f \u7684\u662f\u4f60\u6765\u4e86\uff1f\u771f\u7684\u8fd8\u4e0d\u592a\u8fdf\uff1f\u201d\u4e91\u857e\u9053\uff1a\u201c\u4ec0\u4e48\u8fdf\u4e0d\u8fdf\u7684\u554a\uff1f\u4f60\u4e0d\u662f\u8bf4\u8fc7\u4efb\u51ed\u8def\u9014\u5982\u4f55\u9065 \u8fdc\uff0c\u603b\u4f1a\u8d76\u5230\u7684\u4e48\uff1f\u4f60\u770b\u770b\uff0c\u4e0d\u4f46\u6211\u8d76\u4e86\u6765\uff0c\u4ed6\u4eec\u4e5f\u8d76\u6765\u4e86\uff01\u201d \u3000\u3000\u5f20\u4e39\u67ab\u62ac\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u4e91\u857e\u7684\u7236\u4eb2\u4e91\u6f84\u4e5f\u5728\u9a6c\u80cc\u4e0a\u542b\u7b11\u5730\u770b\u7740\u4ed6\u4eec\uff0c\u9762\u4e0a\u867d\u7136\u4ecd\u6709\u5200 \u75d5\uff0c\u4f46\u5374\u662f\u4e00\u6d3e\u6148\u7965\uff0c\u6beb\u65e0\u6028\u6bd2\u7684\u795e\u8272\u4e86\u3002\u4ed6\u52d2\u4f4f\u4e86\u9a6c\uff0c\u4e00\u8dc3\u800c\u4e0b\uff0c\u77eb\u5065\u975e\u5e38\uff0c\u539f\u6765\u4ed6\u7684\u8ddb \u811a\u5df2\u7ecf\u88ab\u4e91\u91cd\u7528\u5f20\u4e39\u67ab\u6240\u6559\u7684\u6cd5\u5b50\u533b\u597d\u4e86\u3002\u7ecf\u8fc7\u4e86\u90a3\u573a\u4e8b\u53d8\u4e4b\u540e\uff0c\u4ed6\u7684\u6028\u6c14\u5df2\u6d88\uff0c\u53c8\u4ece\u513f\u5973 \u53e3\u4e2d\u77e5\u9053\u5f20\u4e39\u67ab\u7684\u82e6\u5fc3\uff0c\u8fde\u4ed6\u7684\u6b8b\u5e9f\u4e5f\u662f\u5f20\u4e39\u67ab\u9884\u5148\u5b89\u6392\uff0c\u5047\u624b\u4e91\u91cd\u533b\u597d\u7684\uff0c\u4e0a\u4e00\u4ee3\u7684\u4e8b \u60c5\uff0c\u4e0a\u4e00\u4ee3\u5df2\u7ecf\u4e86\u7ed3\uff0c\u8fd8\u6709\u4ec0\u4e48\u597d\u8bf4\u5462\uff1f \u3000\u3000\u4e91\u6f84\u540e\u9762\u8fd8\u6709\u51e0\u5339\u5750\u9a91\uff0c\u90a3\u662f\u4e91\u91cd\u548c\u4ed6\u7684\u6bcd\u4eb2\uff0c\u6fb9\u53f0\u706d\u660e\u548c\u4ed6\u7684\u59b9\u59b9\uff0c\u4e00\u9f50\u770b\u7740\u4ed6\u4eec\uff0c \u5fae\u5fae\u542b\u7b11\u3002\u6fb9\u53f0\u955c\u660e\u7b56\u9a6c\u4e0a\u524d\u4e24\u6b65\uff0c\u4e0e\u4e91\u91cd\u540c\u884c\uff0c\u626c\u97ad\u7b11\u9053\uff1a\u201c\u4e39\u67ab\uff0c\u5feb\u6d3b\u6797\u4e2d\u5df2\u5e03\u7f6e\u4e00 \u65b0\uff0c\u56ed\u6797\u66f4\u7f8e\uff0c\u4f60\u8fd8\u4e0d\u8fdb\u57ce\u4e48\uff1f\u201d\u5f20\u4e39\u67ab\u5982\u5728\u68a6\u4e2d\u521d\u9192\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u4f60\u4e5f\u8fdb\u57ce \u4e48\uff1f\u201d\u4e91\u857e\u76c8\u76c8\u4e00\u7b11\uff0c\u79cd\u79cd\u6069\u4ec7\uff0c\u822c\u822c\u60c5\u7231\uff0c\u90fd\u5c3d\u6eb6\u5728\u8fd9\u4e00\u7b11\u4e4b\u4e2d\u3002 \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u76c8\u76c8\u4e00\u7b11\uff0c\u5c3d\u628a\u6069\u4ec7\u4e86\u3002\u8d76\u4e0a\u6c5f\u5357\u6625\u672a\u6773\uff0c\u6625\u8272\u82b1\u5bb9\u76f8\u7167\u3002 \u3000\u3000\u6628\u5bb5\u82e6\u96e8\u8fde\u7ef5\uff0c\u4eca\u671d\u4e3d\u65e5\u6674\u5929\uff0c\u6101\u7eea\u90fd\u968f\u67f3\u7d6e\uff0c\u968f\u98ce\u5316\u4f5c\u8f7b\u70df\u3002"}),showOnHomepage:!0}]}}]);