"use strict";(self.webpackChunkwebsite=self.webpackChunkwebsite||[]).push([[41362],{36223:(e,n,s)=>{s.r(n),s.d(n,{default:()=>a});var t=s(74848);const a=[{name:"\u5999\u7edd\u5929\u9999",content:(0,t.jsx)(t.Fragment,{children:"\u7b2c \u4e00 \u56de\u3000\u8d77\u6b7b\u56de\u751f \u3000\u3000\u5bd2\u610f\u6599\u5ced\uff0c\u6708\u9ed1\u98ce\u9ad8\u3002 \u3000\u3000\u6d59\u4e1c\u5929\u53f0\u5c71\u9e93\u7684\u5d0e\u5c96\u5c0f\u5f84\u4e0a\uff0c\u6b63\u661f\u98de\u4e38\u6cfb\u822c\u7684\u5954\u884c\u7740\u4e00\u8001\u4e00\u5c11\u4e24\u4e2a\u4eba\u5f71\u3002 \u3000\u3000\u76f4\u5230\u90a3\u4e24\u6761\u4eba\u5f71\u5954\u884c\u7684\u811a\u6b65\u7f13\u4e0b\u6765\u540e\uff0c\u624d\u770b\u6e05\u4ed6\u4eec\u7684\u8863\u7740\u9762\u8c8c\u3002 \u3000\u3000\u90a3\u5e74\u8001\u7684\u4e71\u53d1\u62ab\u80a9\uff0c\u80f8\u524d\u957f\u9aef\uff0c\u8eab\u7740\u7070\u888d\uff0c\u814b\u4e0b\u8fd8\u5939\u7740\u4e00\u4e2a\u52a8\u4e5f\u4e0d\u52a8\u7684\u5973\u5b50\uff0e \u3000\u3000\u90a3\u5e74\u5e7c\u7684\u53ea\u6709\u4e03\u516b\u5c81\uff0e\u5934\u633d\u53cc\u9afb\uff0c\u84dd\u8272\u8863\u88e4\uff0c\u751f\u5f97\u7709\u6e05\u76ee\u79c0\uff0c\u2014\u8138\u4e56\u987a\u800c\u53c8\u6167\u9ee0\u6a21\u6837\u3002 \u3000\u3000\u53ea\u542c\u90a3\u84dd\u8863\u7ae5\u5b50\u53eb\u9053\uff1a\u201c\u5916\u516c\uff0c\u6211\u4eec\u4e00\u53e3\u6c14\u5954\u884c\u4e86\u5341\u51e0\u91cc\u8def\uff0c\u60a8\u53c8\u5e26\u7740\u5a18\uff0c\u8be5\u4f11\u606f\u4f11\u606f \u4e86\u5427\uff1f\u201d \u3000\u3000\u7070\u888d\u8001\u4eba\u201c\u55ef\u201d\u4e86\u4e00\u58f0\uff0c\u8f7b\u8f7b\u628a\u814b\u4e0b\u7684\u5973\u5b50\uff0c\u653e\u5728\u8def\u65c1\u4e00\u5757\u5927\u9752\u77f3\u4e0a\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u6708\u513f\u5df2\u7531\u4e91\u5c42\u4e2d\u900f\u51fa\u4e86\u4eae\u5149\uff0c\u7167\u89c1\u90a3\u5973\u5b50\u6ee1\u8eab\u8840\u6c61\uff0c\u4f46\u5374\u63a9\u4e0d\u4f4f\u5979\u90a3\u66fe\u4f7f\u5929\u4e0b\u6b66 \u6797\u4eba\u4eba\u503e\u5012\u7684\u7edd\u4e16\u59ff\u5bb9\u3002 \u3000\u3000\u90a3\u5973\u5b50\u9759\u9759\u7684\u4ef0\u5367\u5728\u9752\u77f3\u4e0a\uff0e\u4f9d\u7136\u52a8\u4e5f\u4e0d\u52a8\u3002 \u3000\u3000\u84dd\u8863\u7ae5\u5b50\u53cc\u7738\u6ee1\u5659\u6cea\u6c34\uff0c\u60c5\u4e0d\u81ea\u7981\u6251\u5012\u5728\u90a3\u5973\u5b50\u8eab\u65c1\uff0c\u54c0\u58f0\u51c4\u5389\u7684\u53eb\u9053\uff1a\u201c\u5a18\uff01\u5feb\u9192 \u9192\uff0c\u6211\u662f\u7fce\u513f\uff0c\u5a18\uff01\u5feb\u7741\u5f00\u773c\u6765\u770b\u770b\u6211\uff01\u201d \u3000\u3000\u7070\u888d\u8001\u4eba\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u5374\u5e76\u672a\u8bf4\u4ec0\u4e48\uff0e \u3000\u3000\u5ffd\u89c1\u84dd\u8863\u7ae5\u5b50\u56de\u8eab\u6293\u4f4f\u7070\u888d\u8001\u4eba\u7684\u8863\u895f\u9053\uff1a\u201c\u5916\u516c\uff0c\u5a18\u600e\u4e48\u4e0d\u8bf4\u8bdd\u5462\uff1f\u201d \u3000\u3000\u7070\u888d\u8001\u4eba\u590d\u53c8\u53f9\u606f\u9053\uff1a\u201c\u50bb\u5b69\u5b50\uff0c\u4f60\u5a18\u5df2\u6b7b\u4e86\uff0c\u4eba\u6b7b\u90a3\u6709\u8fd8\u4f1a\u8bf4\u8bdd\u7684\uff1f\u201d \u3000\u3000\u7fce\u513f\u4e0d\u7531\u6376\u80f8\u987f\u8db3\u9053\uff1a\u201c\u4e0d\uff01\u5a18\u6ca1\u6b7b\u3002\u5979\u662f\u5929\u4e0b\u7eff\u6797\u76df\u4e3b\uff0c\u5979\u82e5\u6b7b\u4e86\uff0c\u5929\u4e0b\u7eff\u6797\u53c8\u6709\u8c01 \u6765\u9886\u5bfc\uff1f\u201d \u3000\u3000\u7070\u888d\u8001\u4eba\u9ed8\u7136\u6447\u6447\u5934\u9053\uff1a\u201c\u5979\u4e3a\u4f60\u517b\u7236\u80e1\u67cf\u9f84\u62a5\u4ec7\u800c\u6b7b\uff0c\u4f60\u6628\u665a\u660e\u660e\u5728\u573a\u4eb2\u773c\u770b\u5230\u7684\uff0c \u4f60\u517b\u7236\u80e1\u67cf\u9f84\uff0c\u5f53\u5e74\u82f1\u96c4\u76d6\u4e16\uff0c\u8fd8\u4e0d\u662f\u7167\u6837\u88ab\u4eba\u6740\u6b7b\uff1f\u4f60\u5a18\u4e00\u4e2a\u5973\u4eba\uff0c\u80fd\u6b7b\u5f97\u8fd9\u6837\u58ee\u70c8\uff0c\u82e5 \u5979\u6b7b\u540e\u6709\u77e5\uff0c\u4e5f\u8be5\u7791\u76ee\u4e5d\u6cc9\u4e4b\u4e0b\u4e86\uff01\u201d \u3000\u3000\u7fce\u513f\u5706\u7741\u7740\u2014\u5bf9\u6124\u6012\u800c\u53c8\u4f24\u75db\u65e0\u6bd4\u7684\u5927\u773c\u775b\uff0c\u81ea\u8a00\u81ea\u8bed\u9053\uff1a\u201c\u5916\u516c\uff0c\u5c06\u6765\u7fce\u513f\u957f\u5927\u540e\uff0c \u4e5f\u8981\u66ff\u5a18\u62a5\u4ec7\uff01\u201d \u3000\u3000\u7070\u888d\u8001\u4eba\u60e8\u7136\u4e00\u7b11\u9053\uff1a\u201c\u50bb\u5b69\u5b50\uff0c\u4f60\u5a18\u8fd9\u6b21\u4e3a\u4f60\u4e49\u7236\u80e1\u67cf\u9f84\u62a5\u4ec7\uff0c\u5df2\u7ecf\u917f\u6210\u5929\u4e0b\u6b66\u6797\u7a7a \u524d\u6d69\u52ab\uff0c\u591a\u5c11\u6b66\u6797\u5f53\u4ee3\u9ad8\u4eba\uff0c\u90fd\u60e8\u6b7b\u5728\u6628\u665a\u4e00\u573a\u6fc0\u6218\u53ae\u6740\u4e4b\u4e2d\uff0c\u6069\u6028\u60c5\u4ec7\uff0c\u5df2\u7ecf\u65e0\u6cd5\u5206\u6e05 \u4e86\uff01 \u3000\u3000\u7531\u4e8e\u8fd9\u573a\u6fc0\u6218\u53ae\u6740\uff0c\u5df2\u4f7f\u9ed1\u767d\u4e24\u9053\u5143\u6c14\u5927\u4f24\uff0e\u4f60\u8fd8\u6709\u4ec0\u4e48\u4ec7\u53ef\u62a5\uff0c\u53c8\u8981\u5411\u8c01\u62a5\u4ec7\uff1f\u201d \u3000\u3000\u7fce\u513f\u795e\u8272\u6108\u89c1\u60b2\u621a\uff0c\u5446\u4e86\u5446\u9053\uff1a\u201c\u5916\u516c\uff0e\u4f60\u7a76\u7adf\u8981\u628a\u6211\u5a18\u7684\u9057\u4f53\uff0c\u5e26\u5230\u4ec0\u4e48\u5730\u65b9\u5462\uff1f\u201d \u3000\u3000\u7070\u888d\u8001\u4eba\u4ef0\u671b\u591c\u7a7a\uff0c\u534a\u664c\u624d\u9053\uff1a\u201c\u5916\u516c\u4f4f\u5728\u957f\u767d\u5c71\uff0c\u5f53\u7136\u662f\u8981\u628a\u5979\u5e26\u56de\u957f\u767d\u5c71\u57cb\u846c \u7f57\uff01\u201d \u3000\u3000\u7fce\u513f\u7728\u52a8\u4e00\u5bf9\u5927\u773c\u775b\u9053\uff1a\u201c\u957f\u767d\u5c71\u79bb\u8fd9\u91cc\u6709\u591a\u8fdc\uff1f\u201d \u3000\u3000\u7070\u888d\u8001\u4eba\u9053\uff1a\u201c\u8fdc\u5f97\u5f88\uff0c\u6709\u597d\u51e0\u5343\u91cc\u8def\u3002\u201d \u3000\u3000\u7fce\u513f\u5403\u60ca\u7684\u554a\u4e86\u58f0\u9053\uff1a\u201c\u5916\u516c\u600e\u80fd\u5e26\u7740\u5a18\u8d70\u8fd9\u4e48\u8fdc\u7684\u8def\uff1f\u201d \u3000\u3000\u7070\u888d\u8001\u4eba\u9762\u8272\u51dd\u91cd\u9053\uff1a\u201c\u8fd9\u662f\u4f60\u5a18\u4e34\u7ec8\u524d\u7684\u9057\u8a00\uff0c\u8981\u6211\u72ec\u529b\u628a\u5979\u7684\u5c38\u4f53\u5e26\u8d70\uff0c\u6211\u4e0d\u80fd\u8f9c \u8d1f\u4e86\u5979\u7684\u4e00\u756a\u5631\u6258\uff0c\u7b49\u79bb\u5f00\u5929\u53f0\u5c71\u6211\u4eec\u4fbf\u96c7\u4e00\u8f86\u9aa1\u5b50\u8d76\u8def\uff0c\u5230\u90a3\u65f6\u5c31\u7528\u4e0d\u7740\u50cf\u73b0\u5728\u8fd9\u6837\u5403\u529b \u4e86\uff0e\u201d \u3000\u3000\u7fce\u513f\u63e9\u4e86\u4e00\u4e0b\u989d\u89d2\u7684\u6c57\u6c34\u9053\uff1a\u201c\u5916\u516c\uff0c\u6211\u53e3\u6e34\u7684\u5f88\uff0c\u4ec0\u4e48\u5730\u65b9\u6709\u6c34\uff1f\u201d \u3000\u3000\u7070\u888d\u8001\u4eba\u5411\u524d\u4e00\u6307\u9053\uff1a\u201c\u524d\u9762\u4e0d\u8fdc\u6709\u6761\u5c71\u6da7\uff0c\u6211\u5e26\u4f60\u53bb\u3002\u201d \u3000\u3000\u7fce\u513f\u6709\u4e9b\u4e0d\u6b7b\u5fc3\uff0c\u56de\u5934\u671b\u671b\u8eba\u5728\u5927\u9752\u77f3\u4e0a\u7684\u5973\u5b50\u2014\u773c\u9053\uff1a\u201c\u53ef\u662f\u5a18\u6ca1\u6709\u4eba\u770b\u5b88\uff01\u201d \u3000\u3000\u7070\u888d\u8001\u4eba\u7565\u2014\u72b9\u8c6b\u9053\uff1a\u201c\u6211\u4eec\u9a6c\u4e0a\u56de\u6765\uff0c\u8fd9\u4e48\u77ed\u7684\u65f6\u95f4\uff0c\u4e0d\u4f1a\u6709\u4eba\u6765\u7684\uff0e\u201d \u3000\u3000\u8bf4\u7f62\uff0c\u5f53\u5148\u4e3a\u7fce\u513f\u5e26\u8def\uff0c\u76f4\u5411\u524d\u9762\u4e0d\u8fdc\u5904\u7684\u5c71\u6da7\u5954\u53bb\u3002 \u3000\u3000\u8fd9\u4e00\u8001\u4e00\u5c11\uff0c\u679c\u7136\u5f88\u5feb\u4fbf\u8d76\u56de\u6765\u3002 \u3000\u3000\u7fce\u513f\u8d70\u5728\u524d\u9762\uff0c\u5f53\u4ed6\u8fd4\u56de\u539f\u5904\u65f6\uff0c\u7acb\u65f6\u5931\u58f0\u5927\u53eb\u9053\uff1a\u201c\u5916\u516c\uff0c\u5a18\u4e0d\u89c1\u4e86\uff01\u201d \u3000\u3000\u7070\u888d\u8001\u4eba\u4e4d\u542c\u4f3c\u4e4e\u6709\u4e9b\u4e0d\u4fe1\uff0c\u76f4\u5230\u4ed6\u5954\u8fd1\u524d\u67e5\u770b\u8fc7\u4ee5\u540e\uff0c\u624d\u6115\u7136\u6123\u5728\u5f53\u5730\u3002 \u3000\u3000\u90a3\u653e\u5728\u5927\u9752\u77f3\u4e0a\u7684\u5973\u5b50\u8c37\u5bd2\u9999\uff0c\u7adf\u771f\u7684\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u8fd9\u6837\u77ed\u6682\u7684\u65f6\u95f4\uff0c\u7adf\u7136\u53d1\u751f\u5982\u6b64\u5de8\u53d8\uff0c\u5979\u7a76\u7adf\u662f\u88ab\u4ec0\u4e48\u4eba\u5e26\u8d70\u4e86\u5462\uff1f\u2026\u2026 \u3000\u3000\u5c31\u5728\u7070\u888d\u8001\u4eba\u6115\u7136\u4e0d\u77e5\u6240\u63aa\u4e4b\u9645\u3002\u84e6\u5730\uff0c\u4e00\u9635\u8863\u895f\u98d8\u98ce\u4e4b\u58f0\uff0c\u7531\u8fdc\u800c\u8fd1\uff0c\u63a5\u7740\u5927\u7ea6\u5341\u6761 \u5de6\u53f3\u4eba\u5f71\uff0c\u7531\u6765\u65f6\u7684\u8def\u4e0a\uff0c\u98de\u5feb\u7684\u5954\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u970e\u65f6\u6765\u5230\u773c\u524d\u3002 \u3000\u3000\u7070\u888d\u8001\u4eba\u4f55\u7b49\u76ee\u529b\uff0c\u4e0d\u7b49\u5bf9\u65b9\u8fd1\u524d\uff0c\u4fbf\u5df2\u770b\u51fa\u8fd9\u4f19\u4eba\u6b63\u662f\u8c37\u5bd2\u9999\u6614\u65e5\u7684\u5fc3\u8179\u4eb2\u4fe1\uff0c\u5176\u4e2d \u6709\u7537\u6709\u5973\uff0e \u3000\u3000\u4e3a\u9996\u7684\u662f\u66fe\u505a\u8fc7\u526f\u76df\u4e3b\u7684\u949f\u4e00\u8c6a\uff0c\u4f9d\u6b21\u662f\u4f59\u4ea6\u4e50\u3001\u9ea6\u5c0f\u660e\u3001\u6587\u5929\u751f\u4ee5\u53ca\u6c5f\u5317\u56db\u9f99\u7684\u51fa\u4e91 \u9f99\u59dc\u5b8f\u3001\u98de\u5929\u9f99\u4f55\u5b97\u8f89\u3001\u591a\u722a\u9f99\u674e\u6770\u548c\u55b7\u706b\u9f99\u5218\u9707\uff0e \u3000\u3000\u53e6\u6709\u4e24\u540d\u98ce\u97f5\u695a\u695a\uff0c\u59ff\u8272\u52a8\u4eba\u7684\u5973\u5b50\uff0c\u662f\u4e00\u8eab\u767d\u8863\u7684\u82d7\u7d20\u5170\u548c\u7d2b\u8863\u7d2b\u88d9\u7684\u4e07\u6620\u971e\u3002 \u3000\u3000\u7070\u888d\u8001\u4eba\u7ec8\u4e8e\u660e\u767d\u4e86\uff0c\u662f\u600e\u4e48\u56de\u4e8b\uff0c\u539f\u6765\u8c37\u5bd2\u9999\u7684\u9057\u4f53\u662f\u88ab\u4ed6\u4eec\u642c\u8d70\u4e86\u3002 \u3000\u3000\u4f46\u4ed6\u5374\u4e0d\u80fd\u5c31\u6b64\u7f62\u4f11\uff0c\u56e0\u4e3a\u8c37\u5bd2\u9999\u4e34\u7ec8\u524d\uff0c\u5f53\u4f17\u5ba3\u5e03\u8981\u81ea\u5df1\u628a\u5979\u7684\u9057\u4f53\u72ec\u529b\u5e26\u8d70\uff0c\u518d\u7531 \u7fce\u513f\u4eb2\u624b\u57cb\u846c\uff0c\u4ed6\u4e0d\u80fd\u8f9c\u8d1f\u5979\u7684\u4e34\u7ec8\u9057\u8a00\u3002 \u3000\u3000\u8fd8\u6ca1\u6709\u7b49\u4ed6\u5f00\u53e3\uff0c\u4fbf\u89c1\u949f\u4e00\u8c6a\u62b1\u62f3\u4e00\u793c\u9053\uff1a\u201c\u539f\u6765\u662f\u5e9e\u8001\u524d\u8f88\u5728\u8fd9\u91cc\uff0e\u5e9e\u8001\u524d\u8f88\u4e00\u8def\u5e26 \u7740\u8c37\u76df\u4e3b\u9057\u4f53\uff0c\u4e00\u5b9a\u5f88\u8f9b\u82e6\u4e86\uff1f\u201d \u3000\u3000\u7070\u888d\u8001\u4eba\u54fc\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u949f\u4e00\u8c6a\uff0c\u5c11\u6765\u8fd9\u4e00\u5957\uff0c\u4f60\u4eec\u628a\u8c37\u5bd2\u9999\u7684\u9057\u4f53\u76d7\u8d70\uff0e\u5374\u53cd\u800c\u88c5\u8154 \u4f5c\u52bf\u6765\u95ee\u8001\u592b\uff0c\u7b80\u76f4\u662f\u732a\u516b\u6212\u4e0b\u5c71\u2014\u4e00\u5012\u6253\u4e00\u8019\uff0e\u201d \u3000\u3000\u6b64\u8bed\u4e00\u51fa\uff0c\u949f\u4e00\u8c6a\u7b49\u516b\u7537\u4e8c\u5973\uff0c\u5168\u90fd\u4e3a\u4e4b\u4e00\u6014\uff0e \u3000\u3000\u7070\u888d\u8001\u4eba\u72b9\u81ea\u4f59\u6012\u672a\u606f\uff0c\u559d\u9053\uff1a\u201c\u5feb\u5feb\u628a\u8c37\u5bd2\u9999\u90a3\u4e2b\u5934\u9057\u4f53\u4ea4\u51fa\u6765\uff0c\u5426\u5219\uff0c\u8001\u592b\u8c41\u4e0a\u8fd9 \u6761\u547d\u4e0d\u8981\uff0c\u4e5f\u8981\u8ddf\u4f60\u4eec\u62fc\u4e2a\u4f60\u6b7b\u6211\u6d3b\uff01\u201d \u3000\u3000\u949f\u4e00\u8c6a\u6b63\u8981\u4e0a\u524d\u7406\u8bba\uff0c\u5374\u88ab\u4f59\u4ea6\u4e50\u4e00\u628a\u62c9\u4f4f\u9053\uff1a\u201c\u949f\u5144\u7a0d\u5b89\u52ff\u8e81\uff0c\u5f85\u5144\u5f1f\u548c\u5e9e\u8001\u524d\u8f88\u8bb2 \u51e0\u53e5\u8bdd\u3002\u201d \u3000\u3000\u7070\u888d\u8001\u4eba\u9053\uff1a\u201c\u4f60\u6709\u4ec0\u4e48\u8bdd\u8bb2\uff1f\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u4e00\u5411\u6587\u58eb\u6253\u626e\uff0c\u4e3a\u4eba\u4e5f\u65af\u6587\u5112\u96c5\uff0c\u6df1\u6df1\u65bd\u4e86\u4e00\u793c\u9053\uff1a\u201c\u5e9e\u8001\u524d\u8f88\u8bf7\u8bf4\u5b9e\u8bdd\uff0c\u8c37\u76df \u4e3b\u7684\u9057\u4f53\u5f53\u771f\u4e0d\u89c1\u4e86\u4e48\uff1f\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u8fd9\u4e24\u53e5\u8bdd\u867d\u7136\u95ee\u5f97\u4e0d\u75be\u4e0d\u5f90\uff0c\u800c\u4e14\u9887\u6709\u793c\u8c8c\uff0c\u4f46\u542c\u5728\u7070\u888d\u8001\u4eba\u8033\u6735\u91cc\uff0c\u5374\u4e0d\u557b\u706b \u4e0a\u52a0\u6cb9\uff0c\u5f53\u4e0b\u9b13\u53d1\u6012\u5f20\uff0c\u5927\u543c\u9053\uff1a\u201c\u4f59\u4ea6\u4e50\uff0c\u4f60\u8fd8\u6562\u62ff\u4eba\u5f00\u5fc3\uff01\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u7b97\u662f\u6c89\u4f4f\u4e86\u6c14\uff0c\u5fae\u5fae\u2014\u7b11\u9053\uff1a\u201c\u665a\u8f88\u662f\u6b63\u6b63\u7ecf\u7ecf\u5411\u5e9e\u524d\u8f88\u95ee\u8bdd\uff0c\u600e\u8bf4\u662f\u62ff\u8001\u4eba\u5bb6 \u5f00\u5fc3\uff1f\u201d \u3000\u3000\u7070\u888d\u8001\u4eba\u6012\u4e0d\u53ef\u904f\u9053\uff1a\u201c\u8c37\u5bd2\u9999\u7684\u9057\u4f53\u521a\u624d\u8fd8\u5728\u8fd9\u91cc\uff0c\u660e\u660e\u662f\u88ab\u4f60\u4eec\u76d7\u8d70\u4e86\uff01\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u795e\u8272\u4e00\u6014\u9053\uff1a\u201c\u6709\u8fd9\u79cd\u4e8b\uff1f\u8c37\u76df\u4e3b\u7684\u9057\u4f53\u662f\u600e\u4e48\u5f04\u4e22\u7684\uff1f\u201d \u3000\u3000\u7070\u888d\u8001\u4eba\u9053\uff1a\u201d\u8001\u592b\u82e5\u770b\u5230\u5979\u662f\u88ab\u4f60\u4eec\u76d7\u8d70\u7684\uff0c\u73b0\u5728\u4e5f\u5c31\u7528\u4e0d\u7740\u591a\u8d39\u53e3\u820c\u4e86\uff1f\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u9ed8\u4e86\u4e00\u9ed8\uff0c\u4e0d\u518d\u7406\u4f1a\u7070\u888d\u8001\u4eba\uff0c\u76ee\u5149\u8f6c\u6ce8\u7fce\u513f\u9053\uff1a\u201c\u7fce\u513f\uff0c\u4f60\u548c\u5e9e\u8001\u524d\u8f88\u5728\u4e00 \u8d77\uff0c\u4e00\u5b9a\u77e5\u9053\u662f\u600e\u4e48\u56de\u4e8b\u4e86\uff1f\u201d \u3000\u3000\u7fce\u513f\u8fd9\u4e24\u4e09\u5e74\u6765\uff0c\u4e00\u76f4\u548c\u4f59\u4ea6\u4e50\u7b49\u4eba\u751f\u6d3b\u5728\u4e00\u8d77\uff0c\u5f53\u7136\u662f\u65e0\u8bdd\u4e0d\u8c08\uff0c\u5fd9\u9053\uff1a\u201c\u4f59\u53d4\u53d4\uff0c \u5916\u516c\u8bf4\u7684\u4e0d\u9519\uff0c\u4ed6\u5e26\u7740\u6211\u5a18\u548c\u6211\uff0c\u7531\u4e07\u82b1\u5bab\u4e00\u53e3\u6c14\u8dd1\u5230\u8fd9\u91cc\uff0e\u56e0\u4e3a\u8981\u4f11\u606f\uff0c\u5c31\u628a\u5a18\u653e\u5728\u90a3\u5927 \u9752\u77f3\u4e0a\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u7740\uff0c\u8fd8\u7279\u522b\u62ac\u624b\u4e00\u6307\u90a3\u5757\u5927\u9752\u77f3\uff0e \u3000\u3000\u4f59\u4ea6\u4e50\u7709\u5934\u5fae\u76b1\u9053\uff1a\u201c\u65e2\u7136\u4f60\u4eec\u5728\u8fd9\u91cc\u4f11\u606f\uff0c\u4eba\u600e\u4f1a\u4e0d\u89c1\u4e86\u5462\uff1f\u201d \u3000\u3000\u7fce\u513f\u9053\uff1a\u201c\u662f\u6211\u53e3\u6e34\uff0c\u5916\u516c\u5e26\u7740\u6211\u5230\u524d\u9762\u5c0f\u6eaa\u627e\u6c34\uff0c\u5f53\u6211\u4eec\u56de\u6765\u65f6\uff0c\u5a18\u5c31\u4e0d\u89c1\u5566\uff01\u201d \u3000\u3000\u201c\u4f60\u4eec\u79bb\u5f00\u8fd9\u91cc\u6709\u591a\u957f\u65f6\u95f4\uff1f\u201d \u3000\u3000\u201c\u53ea\u662f\u4e0d\u5927\u4e00\u4f1a\u5de5\u592b\u3002\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u56de\u5934\u671b\u4e86\u671b\u968f\u6765\u7684\u4f17\u4eba\u4e00\u773c\uff0c\u9ed8\u9ed8\u4e0d\u8bed\u3002 \u3000\u3000\u5728\u540c\u6765\u7684\u516b\u7537\u4e8c\u5973\u5f53\u4e2d\uff0c\u9ea6\u5c0f\u660e\u5e74\u7eaa\u6700\u5c0f\uff0c\u6027\u5b50\u4e5f\u6700\u66b4\uff0c\u800c\u4e14\u559c\u6012\u65e0\u5e38\uff0c\u4e00\u5411\u9664\u4e86\u8c37\u5bd2 \u9999\u7684\u8bdd\u4ed6\u4e0d\u6562\u4e0d\u542c\uff0c\u53ef\u8bf4\u4e0d\u628a\u4efb\u4f55\u4eba\u653e\u5728\u773c\u91cc\uff0e \u3000\u3000\u5728\u4f17\u4eba\u9ed8\u9ed8\u65e0\u8bed\u4e4b\u4e0b\uff0c\u4ed6\u5374\u51b7\u54fc\u4e00\u58f0\uff0c\u7a81\u7136\u4e0a\u524d\u4e24\u6b65\uff0c\u559d\u9053\uff1a\u201c\u5e9e\u58eb\u51b2\uff0c\u4f60\u5c11\u5728\u8fd9\u91cc\u6545 \u5f04\u7384\u865a\uff0c\u73b0\u5728\u82e5\u628a\u8c37\u76df\u4e3b\u7684\u9057\u4f53\u4ea4\u51fa\u6765\u4fbf\u7f62\uff0c\u4e0d\u7136\u7684\u8bdd\u522b\u60f3\u6d3b\u7740\u79bb\u5f00\u8fd9\u91cc\uff01\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u6012\u6781\u4e4b\u4e0b\uff0c\u53cd\u800c\u62c2\u9aef\u4e00\u7b11\u9053\uff1a\u201c\u5c0f\u5a03\u513f\uff0c\u53ef\u80fd\u8c37\u5bd2\u9999\u90a3\u4e2b\u5934\u6d3b\u7740\u4e4b\u65f6\uff0c\u628a\u4f60\u60ef\u574f \u4e86\uff0c\u4eca\u665a\u8001\u592b\u5012\u5f88\u60f3\u8d81\u8fd9\u673a\u4f1a\u6559\u8bad\u6559\u8bad\u4f60\uff01\u201d \u3000\u3000\u5728\u949f\u4e00\u8c6a\u7b49\u516b\u7537\u4e8c\u5973\u4e2d\uff0c\u867d\u7136\u949f\u4e00\u8c6a\u548c\u4f59\u4ea6\u4e50\u90fd\u662f\u6b66\u529f\u9ad8\u4e0d\u53ef\u6d4b\uff0c\u72ec\u5f53\u4e00\u9762\u7684\u4e00\u65f6\u4e4b \u96c4\uff0c\u4f46\u7eaf\u4ee5\u6b66\u529f\u800c\u8bba\uff0c\u5374\u4ee5\u9ea6\u5c0f\u660e\u4e3a\u6700\u9ad8\uff0c\u4ed6\u5e74\u5c11\u6c14\u76db\uff0c\u53c8\u5929\u751f\u597d\u6597\uff0c\u95fb\u8a00\u4e4b\u4e0b\uff0c\u7acb\u523b\u7ffb\u8155 \u62d4\u51fa\u957f\u5251\u3002 \u3000\u3000\u8fd8\u662f\u4f59\u4ea6\u4e50\u6c89\u7740\u51b7\u9759\uff0c\u4e00\u89c1\u4e0d\u5999\uff0c\u8fde\u5fd9\u6321\u5728\u9ea6\u5c0f\u660e\u8eab\u524d\u9053\uff1a\u201c\u9ea6\u5c0f\u5144\u5f1f\uff0c\u5728\u4e8b\u60c5\u6ca1\u5f04\u6e05 \u695a\u4e4b\u524d\uff0c\u4e0d\u53ef\u83bd\u649e\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u53f1\u9053\uff1a\u201c\u4ec0\u4e48\u6ca1\u5f04\u6e05\u695a\uff0c\u65e2\u7136\u76df\u4e3b\u7684\u9057\u4f53\u65b9\u624d\u8fd8\u5728\u8fd9\u91cc\uff0c\u54b1\u4eec\u4e3a\u4ec0\u4e48\u6ca1\u770b\u89c1\uff0c\u96be \u9053\u8fd9\u4e48\u77ed\u7684\u65f6\u95f4\u5c31\u4f1a\u88ab\u4eba\u76d7\u8d70\u4e0d\u6210\uff1f\u201d \u3000\u3000\u8bf4\u7740\u62e8\u5f00\u4f59\u4ea6\u4e50\uff0c\u559d\u9053\uff1a\u201c\u5e9e\u58eb\u51b2\uff0c\u5c0f\u7237\u8ba9\u4f60\u5148\u51fa\u624b\u3002\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u563f\u563f\u4e00\u7b11\u9053\uff1a\u201c\u8001\u592b\u65e9\u5c31\u542c\u8bf4\u4f60\u5c0f\u5b50\u539f\u662f\u5929\u53f0\u4e07\u82b1\u5bab\u7684\u4eba\uff0c\u5148\u968f\u4e07\u82b1\u5bab\u4e3b\u4f5f\u516c\u5e38 \u5b66\u8fc7\u4e09\u62db\u4e24\u5f0f\uff0c\u540e\u53c8\u6539\u6295\u5230\u795e\u6756\u7fc1\u9093\u79cb\u95e8\u4e0b\u3002 \u3000\u3000\u6b66\u6797\u4e2d\u8bb2\u7a76\u7684\u4e00\u4eba\u4e0d\u62dc\u4e8c\u5e08\uff0c\u4f60\u8fde\u8fd9\u70b9\u89c4\u77e9\u90fd\u4e0d\u61c2\uff0e\u53ef\u89c1\u5fc5\u5b9a\u662f\u4e2a\u6742\u79cd\u65e0\u7591\uff0c\u5c0f\u5b50\uff0c\u7528 \u4e0d\u7740\u987e\u5fcc\uff0c\u5c3d\u7ba1\u52a8\u624b\u5427\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u84e6\u5730\u817e\u8eab\u800c\u8d77\uff0c\u76f4\u5411\u5e9e\u58eb\u51b2\u63a0\u53bb\uff0e\u4eba\u5c1a\u672a\u5230\uff0c\u5251\u5c16\u5df2\u7efd\u5f00\u4e24\u6735\u94f6\u8292\uff0c\u5206\u523a\u5e9e\u58eb\u51b2 \u201c\u5c06\u53f0\u201d\u3001\u201c\u671f\u95e8\u201d\u4e24\u5904\u5927\u7a74\uff0e \u3000\u3000\u52a8\u4f5c\u5feb\u5f97\u6709\u5982\u7535\u5149\u77f3\u706b\uff0c\u5a01\u52bf\u51cc\u5389\u81f3\u6781\uff01 \u3000\u3000\u5e9e\u58eb\u51b2\u624e\u6869\u5982\u5c71\uff0c\u811a\u4e0b\u5e76\u672a\u79bb\u5f00\u539f\u5730\uff0e\u5bbd\u5927\u7684\u888d\u8896\u4e00\u62c2\uff0c\u547c\u7684\u2014\u638c\uff0c\u8fce\u7740\u523a\u6765\u7684\u5251\u52bf\u62cd \u4e86\u51fa\u53bb\u3002 \u3000\u3000\u4ed6\u542b\u6124\u51fa\u624b\uff0c\u8fd9\u4e00\u638c\u5a01\u52bf\u6709\u5982\u6392\u5c71\u5012\u6d77\uff0c\u6d6a\u51fb\u7901\u77f3\uff0c\u5e26\u52a8\u5f97\u5730\u9762\u98de\u6c99\u8d70\u77f3\uff0c\u8fde\u8fdc\u5728\u4e24\u4e09 \u4e08\u5916\u7684\u949f\u4e00\u8c6a\u7b49\u4eba\uff0c\u90fd\u8863\u8882\u98d8\u98d8\uff0c\u7ad9\u7acb\u4e0d\u7a33\u3002 \u3000\u3000\u987b\u77e5\u5e9e\u58eb\u51b2\u7684\u6b66\u5b66\u9020\u8be3\uff0c\u5728\u5f53\u4eca\u4e4b\u4e16\uff0c\u5df2\u96be\u5f97\u627e\u51fa\u51e0\u4eba\u6562\u4e8e\u4e0e\u4ed6\u6297\u8861\uff0e\u66f4\u4f55\u51b5\u9ea6\u5c0f\u660e\u6628 \u665a\u65e9\u5df2\u529b\u6218\u800c\u5026\u3002 \u3000\u3000\u4ed6\u5728\u5e9e\u58eb\u51b2\u7684\u638c\u98ce\u8fce\u9762\u649e\u51fb\u4e4b\u4e0b\uff0c\u4e0d\u4f46\u523a\u51fa\u7684\u5251\u52bf\u88ab\u8feb\u8361\u504f\uff0c\u8fde\u75be\u6251\u5411\u524d\u7684\u8eab\u8eaf\uff0c\u4e5f\u88ab \u8feb\u540e\u5012\u9000\u56de\u6765\u3002 \u3000\u3000\u597d\u5728\u662f\u9ea6\u5c0f\u660e\uff0c\u843d\u5730\u4e4b\u540e\uff0c\u5e76\u65e0\u635f\u4f24\uff0c\u82e5\u6362\u4e86\u4e00\u822c\u6b66\u6797\u4eba\u7269\uff0c\u52bf\u5fc5\u88ab\u638c\u529b\u51fb\u6210\u91cd\u4f24\u3002 \u3000\u3000\u4f59\u4ea6\u4e50\u5fd6\u5ea6\u773c\u4e0b\u60c5\u52bf\uff0c\u5982\u679c\u4ed6\u4eec\u2014\u4f19\u4eba\u5c55\u5f00\u8054\u624b\u5408\u653b\uff0c\u867d\u53ef\u80dc\u5f97\u5e9e\u58eb\u51b2\uff0c\u4f46\u5374\u6ca1\u6709\u628a\u63e1 \u5c06\u5bf9\u65b9\u5236\u670d\u3002 \u3000\u3000\u4e8e\u662f\u4f59\u4ea6\u4e50\u518d\u5ea6\u62e6\u5411\u9ea6\u5c0f\u660e\u8eab\u524d\u9053\uff1a\u201c\u5e9e\u8001\u524d\u8f88\uff0c\u665a\u8f88\u5c31\u7b97\u76f8\u4fe1\u4f60\u65b9\u624d\u8bf4\u7684\u8bdd\u4e0d\u5047\uff0c\u4f46 \u4f60\u603b\u8be5\u5bf9\u6211\u4eec\u6709\u4e2a\u4ea4\u4ee3\uff01\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u53cc\u76ee\u7728\u52a8\u4e86\u4e00\u9635\u9053\uff1a\u201c\u8001\u592b\u5bf9\u4f60\u4eec\u8fd8\u6709\u4ec0\u4e48\u53ef\u4ea4\u4ee3\u7684\uff1f\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u4f3c\u4e4e\u5df2\u89c9\u51fa\u8c37\u5bd2\u9999\u7684\u9057\u4f53\uff0c\u5e76\u975e\u88ab\u5bf9\u65b9\u4e00\u4f19\u4eba\u76d7\u8d70\uff0c\u987f\u4e86\u987f\u9053\uff1a\u201c\u8fd9\u662f\u8001\u592b\u81ea\u5df1 \u7684\u4e8b\uff0c\u4e3a\u4ec0\u4e48\u8981\u5411\u4f60\u4eec\u4ea4\u4ee3\uff1f\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u9053\uff1b\u201c\u8001\u524d\u8f88\u8bdd\u4e0d\u80fd\u8fd9\u4e48\u8bf4\uff0c\u665a\u8f88\u4eec\u90fd\u662f\u8c37\u76df\u4e3b\u751f\u524d\u7684\u5fe0\u5fc3\u5c5e\u4e0b\uff0c\u82e5\u4f60\u80fd\u5b8c\u6210\u5979 \u7684\u4e34\u7ec8\u5631\u6258\uff0c\u628a\u5979\u5e26\u56de\u957f\u767d\u5c71\u5165\u571f\u4e3a\u5b89\uff0c\u6211\u4eec\u611f\u8c22\u8fd8\u6765\u4e0d\u53ca\u3002 \u3000\u3000\u4f46\u662f\u504f\u504f\u4f60\u5374\u628a\u5979\u7684\u9057\u4f53\u4e22\u5931\uff0c\u8fd9\u8ba9\u6211\u4eec\u8fd9\u4e9b\u8c37\u76df\u4e3b\u7684\u751f\u524d\u5c5e\u4e0b\uff0c\u5982\u4f55\u80fd\u4e0d\u5411\u4f60\u8ffd \u7a76\uff1f\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u88ab\u95ee\u5f97\u6709\u4e9b\u8bed\u585e\uff0c\u5e72\u54b3\u4e86\u51e0\u58f0\u9053\uff1a\u201c\u5982\u679c\u8fd9\u4e8b\u5f53\u771f\u4e0d\u662f\u4f60\u4eec\u6697\u4e2d\u641e\u9b3c\uff0c\u73b0\u5728\u552f\u4e00 \u7684\u529e\u6cd5\uff0c\u5c31\u662f\u9a6c\u4e0a\u5c55\u5f00\u641c\u7d22\u3002\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u9053\uff1a\u201c\u4eba\u5df2\u88ab\u76d7\uff0c\u53c8\u5230\u54ea\u91cc\u641c\u7d22\uff1f\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u9053\uff1a\u201c\u4e8b\u60c5\u662f\u521a\u624d\u4e0d\u4e45\u624d\u53d1\u751f\u7684\uff0c\u90a3\u76d7\u5c38\u4e4b\u4eba\uff0c\u5fc5\u4e0d\u53ef\u80fd\u8d70\u8fdc\uff0c\u73b0\u5728\u6709\u5404\u4f4d\u5e2e \u5fd9\uff0e\u8001\u592b\u76f8\u4fe1\u5fc5\u53ef\u627e\u5230\u3002\u201d \u3000\u3000\u5ffd\u542c\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u4e07\u2014\u627e\u4e0d\u5230\u8c37\u76df\u4e3b\u9057\u4f53\uff0c\u5e9e\u8001\u524d\u8f88\u5fc5\u987b\u7b54\u5e94\u6211\u4eec\u4e00\u4ef6\u4e8b\uff01\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u6123\u4e86\u695e\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u5fc5\u987b\u628a\u7fce\u513f\u5c0f\u5144\u5f1f\u7559\u4e0b\uff01\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u51b7\u7136\u7b11\u9053\uff1a\u201c\u5c82\u6709\u6b64\u7406\uff01\u7fce\u513f\u662f\u8001\u592b\u7684\u4eb2\u5916\u5b59\uff0c\u5982\u4eca\u8c37\u5bd2\u9999\u65e2\u7136\u5df2\u7ecf\u4e0d\u5728\uff0c\u5728\u8fd9 \u4e16\u4e0a\uff0c\u552f\u6709\u8001\u592b\u662f\u4ed6\u7684\u552f\u4e00\u4eb2\u4eba\uff0e\u5f53\u7136\u8981\u7531\u8001\u592b\u629a\u517b\u3002\u201d \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u8001\u524d\u8f88\u6b64\u8a00\u5dee\u77e3\uff0c\u7fce\u513f\u5c0f\u5144\u5f1f\u867d\u7136\u662f\u4f60\u7684\u4eb2\u5916\u5b59\uff0c\u4f46\u8c37\u76df\u4e3b\u5374\u5e76\u4e0d\u662f\u4f60\u7684 \u5973\u513f\uff0c\u5f53\u5e74\u82e5\u4e0d\u662f\u8c37\u76df\u4e3b\u628a\u4ed6\u6551\u8d77\u5e26\u56de\u629a\u517b\uff0c\u4ed6\u90a3\u80fd\u6d3b\u5230\u4eca\u5929\uff1f\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u54fc\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u949f\u8001\u5f1f\u8fd9\u8bdd\u56fa\u7136\u4e0d\u9519\uff0c\u8001\u592b\u5c31\u662f\u4e3a\u4e86\u8c37\u5bd2\u9999\u5bf9\u7fce\u513f\u6709\u6551\u547d\u4e4b\u6069\uff0c \u53c8\u6709\u629a\u517b\u4e4b\u60c5\uff0c\u6240\u4ee5\u624d\u4e00\u76f4\u5bf9\u5979\u6697\u4e2d\u76f8\u52a9\uff0c\u4f46\u73b0\u5728\u7ec8\u7a76\u6b7b\u4e86\uff0c\u8fd9\u7ee7\u7eed\u629a\u517b\u7fce\u513f\u7684\u8d23\u4efb\uff0c\u9664\u4e86 \u8001\u592b\u8fd8\u6709\u54ea\u4e2a\uff1f\u201d \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u5e9e\u8001\u524d\u8f88\u7684\u8bdd\u56fa\u7136\u4e0d\u9519\uff0c\u4f46\u665a\u8f88\u4eec\u662f\u8c37\u76df\u4e3b\u7684\u65e7\u90e8\u5c5e\uff0c\u8c37\u76df\u4e3b\u6b7b\u4e86\uff0c\u6211\u4eec \u7167\u6837\u4e5f\u6709\u8d23\u4efb\u629a\u517b\u5979\u7684\u9057\u5b64\uff01\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u51b7\u7b11\u9053\uff1a\u201c\u6709\u8001\u592b\u5728\uff0c\u8fd8\u8f6e\u4e0d\u5230\u4f60\u4eec\uff01\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u89c1\u4e8b\u60c5\u53c8\u8981\u95f9\u50f5\uff0c\u5f88\u53ef\u80fd\u518d\u5ea6\u5f15\u8d77\u62da\u640f\uff0e\u53ea\u597d\u6392\u4f17\u800c\u51fa\uff0c\u9053\uff1a\u201c\u5e9e\u8001\u524d\u8f88\uff0c\u949f \u5144\uff0c\u4e24\u4f4d\u7528\u4e0d\u7740\u4e89\u5435\uff0c\u5728\u4e0b\u5012\u6709\u4e2a\u5706\u6ee1\u89e3\u51b3\u7684\u529e\u6cd5\uff0e\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u4e0d\u5c51\u7684\u9053\uff1a\u201c\u4f60\u6709\u4ec0\u4e48\u529e\u6cd5\uff1f\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u9053\uff1a\u201c\u529e\u6cd5\u5f88\u7b80\u5355\uff0c\u4e5f\u5341\u5206\u516c\u5e73\uff0c\u4e0d\u59a8\u8ba9\u7fce\u513f\u5c0f\u5144\u5f1f\u81ea\u884c\u51b3\u5b9a\u4ed6\u8981\u5f52\u8c01\u629a\u517b\u3002\u201d \u3000\u3000\u6b64\u8bed\u4e00\u51fa\uff0c\u949f\u4e00\u8c6a\u7b49\u4e03\u7537\u4e8c\u5973\u679c\u7136\u60c5\u7eea\u90fd\u5e73\u9759\u4e0b\u6765\uff0e\u6240\u6709\u76ee\u5149\u4e0d\u7ea6\u800c\u540c\u9f50\u9f50\u96c6\u4e2d\u5728\u7fce\u513f \u8138\u4e0a\u3002 \u3000\u3000\u4f59\u4ea6\u4e50\u8fd9\u529e\u6cd5\u663e\u7136\u5bf9\u4ed6\u4eec\u6709\u5229\uff0c\u56e0\u4e3a\u7fce\u513f\u5728\u56db\uff0e\u4e94\u5c81\u65f6\uff0c\u4fbf\u7531\u8c37\u5bd2\u9999\u6551\u56de\u6276\u517b\uff0c\u628a\u949f\u2014 \u8c6a\u7b49\u4eba\u65e9\u5c31\u89c6\u4e3a\u4eb2\u4eba\u3002 \u3000\u3000\u800c\u5e9e\u58eb\u51b2\u662f\u4ed6\u7684\u5916\u516c\uff0c\u5374\u662f\u6628\u591c\u624d\u77e5\u9053\u7684\u4e8b\uff0e\u4f55\u51b5\u4ed6\u548c\u5e9e\u58eb\u51b2\u76f8\u5904\u5c1a\u4e0d\u8db3\u2014\u5929\uff0c\u6839\u672c\u4e0d \u53ef\u80fd\u4ea7\u751f\u591a\u5927\u611f\u60c5\uff0e \u3000\u3000\u5e9e\u58eb\u51b2\u4f55\u7b49\u8001\u8fa3\uff0c\u5f53\u7136\u662f\u660e\u767d\u8fd9\u529e\u6cd5\u5bf9\u81ea\u5df1\u5927\u5927\u7684\u4e0d\u5229\u3002 \u3000\u3000\u4f46\u4ed6\u82e5\u662f\u7acb\u5373\u53cd\u5bf9\uff0e\u5374\u53c8\u7b49\u4e8e\u81ea\u884c\u8ba4\u8f93\u3002\u65e0\u5948\u4e4b\u4e0b\uff0c\u53ea\u597d\u8f6c\u8fc7\u5934\u9053\uff1a\u201c\u7fce\u513f\uff0c\u4f60\u4eca\u5e74\u5df2 \u7ecf\u4e03\u516b\u5c81\u4e86\uff0c\u603b\u8be5\u6709\u4eb2\u758f\u4e4b\u5206\uff01\u201d \u3000\u3000\u7fce\u513f\u70b9\u70b9\u5934\u9053\uff1a\u201c\u7fce\u513f\u81ea\u7136\u77e5\u9053\u8c01\u4eb2\u8c01\u758f\uff0e\u5a18\u5728\u4e16\u7684\u65f6\u5019\uff0c\u4ee5\u5a18\u6700\u4eb2\u3002\u201d \u3000\u3000\u4ed6\u53e3\u4e2d\u4e4b\u5a18\uff0c\u4e0d\u6d88\u8bf4\u662f\u6307\u4ed6\u517b\u6bcd\u8c37\u5bd2\u9999\u3002 \u3000\u3000\u5e9e\u58eb\u51b2\u987f\u4e86\u4e00\u987f\u9053\uff1a\u201c\u90a3\u4e48\u73b0\u5728\u5462\uff1f\u201d \u3000\u3000\u8fd9\u662f\u6700\u5927\u7684\u5173\u952e\u65f6\u523b\uff0c\u5728\u573a\u6240\u6709\u7684\u4eba\u90fd\u8feb\u4e0d\u53ca\u5f85\u7684\uff0c\u51dd\u795e\u9759\u542c\u7fce\u513f\u5982\u4f55\u56de\u7b54\u3002 \u3000\u3000\u7fce\u513f\u7741\u7740\u4e00\u5bf9\u95ea\u4eae\u7684\u5927\u773c\u775b\uff0c\u89c6\u7ebf\u5148\u671b\u5411\u5e9e\u58eb\u51b2\uff0c\u7136\u540e\u7f13\u7f13\u8f6c\u5230\u949f\u4e00\u8c6a\u3001\u4f59\u4ea6\u4e50\u3001\u9ea6\u5c0f \u660e\u3001\u6587\u5929\u751f\u3001\u82d7\u7d20\u5170\u3001\u6c5f\u5317\u56db\u9f99\u7b49\u516b\u7537\u4e8c\u5973\u8138\u4e0a\uff0c\u4f46\u5374\u4e45\u4e45\u4e0d\u66fe\u5f00\u53e3\u3002 \u3000\u3000\u5e9e\u58eb\u51b2\u5fc3\u4e0b\u5927\u6025\uff0c\u4e0d\u5f97\u4e0d\u4ee5\u5145\u6ee1\u611f\u60c5\u7684\u8bed\u6c14\u9053\uff1a\u201c\u7fce\u513f\uff0c\u4f60\u65e2\u77e5\u4eb2\u758f\u4e4b\u5206\uff0c\u5c31\u8be5\u660e\u767d\u54b1 \u4eec\u4e2d\u95f4\u6709\u7740\u8840\u8089\u76f8\u8fde\u4e4b\u60c5\u7684\uff0c\u4ed6\u4eec\u867d\u7136\u4e5f\u5f85\u4f60\u4e0d\u9519\uff0c\u5374\u603b\u662f\u5916\u4eba\uff0c\u66f4\u4f55\u51b5\u2026\u2026\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u987f\u4e86\u4e00\u987f\uff0c\u53c8\u9053\uff1a\u201c\u66f4\u4f55\u51b5\u4ed6\u4eec\u4ece\u524d\u6240\u4ee5\u5f85\u4f60\u5f88\u597d\uff0c\u53ea\u662f\u770b\u5728\u4f60\u517b\u6bcd\u8c37\u5bd2\u9999\u7684\u4efd \u4e0a\u3002\u5982\u4eca\u8c37\u5bd2\u9999\u5df2\u6b7b\uff0c\u4f60\u5982\u679c\u8ddf\u4e86\u4ed6\u4eec\u8d70\uff0c\u4ed6\u4eec\u4eca\u540e\u662f\u5426\u8fd8\u50cf\u4ee5\u524d\u90a3\u822c\u5f85\u4f60\uff0c\u90a3\u5c31\u5f88\u96be\u8bf4 \u4e86\uff01\u201d \u3000\u3000\u949f\u4e00\u8c6a\u51b7\u7b11\u9053\uff1a\u201c\u5e9e\u8001\u524d\u8f88\uff0c\u73b0\u5728\u662f\u7fce\u513f\u5c0f\u5144\u5f1f\u81ea\u5df1\u8bf4\u5fc3\u91cc\u8bdd\u7684\u65f6\u5019\uff0e\u7528\u4e0d\u7740\u4f60\u8001\u4eba\u5bb6 \u65bd\u884c\u653b\u5fc3\u6218\u672f\uff01\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u4e5f\u63a5\u53e3\u9053\uff1a\u201c\u949f\u5144\u8bf4\u5f97\u5bf9\uff0c\u82e5\u5e9e\u8001\u524d\u8f88\u4e34\u65f6\u52a8\u4e4b\u4ee5\u60c5\uff0c\u90a3\u5c31\u4e0d\u7b97\u7fce\u513f\u5c0f\u5144\u5f1f\u7684\u81ea \u7531\u51b3\u5b9a\u4e86\uff0c\u800c\u4e14\u66f4\u5931\u516c\u5e73\u3002\u201d \u3000\u3000\u679c\u7136\uff0c\u7fce\u513f\u7684\u89c6\u7ebf\u4f9d\u7136\u76ef\u5728\u4f17\u4eba\u68c0\u4e0a\uff0c\u5374\u4e00\u76f4\u4e0d\u80af\u5f00\u53e3\u3002 \u3000\u3000\u53ea\u542c\u82d7\u7d20\u5170\u9053\uff1a\u201c\u7fce\u513f\uff0c\u5feb\u5feb\u8bf4\u8bdd\uff0c\u7528\u4e0d\u7740\u5bb3\u6015\uff01\u201d \u3000\u3000\u82d7\u7d20\u5170\u8fd9\u53e5\u8bdd\uff0c\u7167\u6837\u4e5f\u662f\u5728\u8fd0\u7528\u653b\u5fc3\u6218\uff0e \u3000\u3000\u56e0\u4e3a\uff0e\u8fd9\u51e0\u5e74\u6765\uff0c\u7fce\u513f\u7684\u751f\u6d3b\u8d77\u5c45\uff0c\u591a\u534a\u662f\u7531\u5979\u534f\u52a9\u8c37\u5bd2\u9999\u7167\u987e\uff0c\u7fce\u513f\u5bf9\u5979\uff0c\u51e0\u4e4e\u4e5f\u50cf \u5bf9\u8c37\u5bd2\u9999\u4e00\u6837\u3002 \u3000\u3000\u5e9e\u58eb\u51b2\u62c5\u5fc3\u7fce\u513f\u88ab\u82d7\u7d20\u5170\u6253\u52a8\uff0c\u4ed6\u5728\u6b66\u6797\u4e2d\uff0c\u4e0d\u8bba\u8eab\u4efd\u5730\u4f4d\uff0c\u90fd\u975e\u6bd4\u5bfb\u5e38\uff0c\u4e00\u65e6\u7fce\u513f\u5f00 \u53e3\u4e0d\u613f\u8ddf\u968f\u81ea\u5df1\uff0c\u4ed6\u5728\u4f17\u76ee\u777d\u777d\u4e4b\u4e0b\uff0e\u5c31\u5fc5\u987b\u8ba4\u5e10\u3002 \u3000\u3000\u4e3a\u4e86\u4e0d\u4f7f\u7fce\u513f\u505a\u51fa\u51b3\u5b9a\uff0c\u5e9e\u58eb\u51b2\u7acb\u5373\u9ad8\u58f0\u8bf4\u9053\uff1a\u201c\u65e2\u7136\u7fce\u513f\u4e0d\u613f\u5f53\u7740\u4f17\u4eba\u4e4b\u9762\u8868\u660e\u5fc3 \u610f\uff0c\u90a3\u5c31\u7528\u4e0d\u7740\u903c\u4ed6\u3002\u73b0\u5728\u6700\u91cd\u8981\u7684\u4e8b\uff0c\u662f\u627e\u56de\u8c37\u5bd2\u9999\u7684\u5c38\u4f53\uff0c\u82e5\u4e3a\u7fce\u513f\u7684\u4e8b\u803d\u8bef\u4e45\u4e86\uff0c\u53cd \u800c\u56e0\u5c0f\u5931\u5927\uff0c\u4f9d\u8001\u592b\u4e4b\u610f\uff0c\u8fd8\u662f\u9a6c\u4e0a\u5c55\u5f00\u641c\u5bfb\u7684\u597d\u3002\u201d \u3000\u3000\u949f\u2014\u8c6a\u9053\uff1a\u201c\u5c31\u4f9d\u8001\u524d\u8f88\u7684\u8bdd\uff0c\u54b1\u4eec\u9a6c\u4e0a\u5c55\u5f00\u641c\u7d22\uff01\u201d \u3000\u3000\u5728\u4e07\u82b1\u5bab\u5730\u4e0b\u7684\u5c71\u8179\u5bc6\u5ba4\u4e2d\uff0c\u58a8\u7389\u84b2\u56e2\u4e0a\u6b63\u5750\u7740\u4e00\u4f4d\u9762\u8272\u7ea2\u6da6\uff0c\u4e94\u7efa\u957f\u9aef\u7684\u4e2d\u5e74\u5112\u58eb\u3002 \u3000\u3000\u4ed6\u5c31\u662f\u5df2\u5e74\u5c4a\u4e00\u767e\u516d\u5341\u9ad8\u9f84\u7684\u4e09\u5999\u8001\u4eba\u3002\u53ea\u662f\u7531\u5916\u8868\u770b\u6765\uff0c\u4ecd\u50cf\u4e00\u4f4d\u4e2d\u5e74\u6a21\u6837\u7684\u4eba\u800c \u5df2\u3002 \u3000\u3000\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u4e09\u5999\u8001\u4eba\u624d\u7f13\u7f13\u7531\u58a8\u7389\u84b2\u56e2\u4e0a\u7ad9\u4e86\u8d77\u6765\uff0c\u8d70\u5411\u53e6\u4e00\u95f4\u5bc6\u5ba4\u3002 \u3000\u3000\u5728\u4e09\u5999\u8001\u4eba\u6253\u5750\u7684\u77f3\u5ba4\u4e2d\uff0c\u56db\u58c1\u770b\u4e0d\u5230\u6709\u4f55\u6d1e\u95e8\uff0c\u4f46\u89c1\u4ed6\u5728\u77f3\u58c1\u4e0a\u8f7b\u8f7b\u6572\u4e86\u4e09\u4e0b\uff0c\u77f3\u58c1 \u7acb\u523b\u65e0\u58f0\u65e0\u606f\u7684\u88c2\u5f00\u4e00\u4e2a\u6d1e\u95e8\u3002 \u3000\u3000\u4e09\u5999\u8001\u4eba\u7acb\u5373\u8fc8\u6b65\u800c\u5165\u3002 \u3000\u3000\u8fd9\u662f\u53e6\u2014\u95f4\u8303\u56f4\u4e0d\u5165\u7684\u77f3\u5ba4\uff0c\u4e2d\u95f4\u9694\u7740\u4e00\u5c42\u674f\u9ec4\u8272\u7684\u5e03\u5e54\u3002 \u3000\u3000\u6380\u5f00\u5e03\u5e54\uff0c\u4e00\u773c\u5c31\u77a5\u89c1\u90a3\u5f20\u8272\u6cdb\u6df1\u7d2b\u7684\u6696\u7389\u5e8a\u69bb\uff0c\u5e8a\u69bb\u4e0a\u9759\u9759\u7684\u8eba\u7740\u2014\u4e2a\u56fd\u8272\u5929\u9999\uff0c\u59ff \u5bb9\u7edd\u4e16\u7684\u5973\u5b50\u3002 \u3000\u3000\u5979\u6b63\u662f\u5728\u5929\u53f0\u5c71\u9e93\u88ab\u76d7\u8d70\u7684\u5973\u5c38\uff0c\u5929\u4e0b\u7eff\u6797\u76df\u4e3b\u8c37\u5bd2\u9999\u3002 \u3000\u3000\u4e09\u5999\u8001\u4eba\u795e\u60c5\u51dd\u91cd\u7684\u6ce8\u89c6\u4e86\u8c37\u5bd2\u9999\u597d\u4e00\u9635\uff0c\u624d\u957f\u957f\u53f9\u606f\u4e00\u58f0\u3002 \u3000\u3000\u63a5\u7740\u4ed6\u4ece\u7389\u69bb\u65c1\u9648\u5217\u7684\u5341\u51e0\u53ea\u7389\u74f6\u4e2d\uff0c\u5404\u81ea\u5012\u51fa\u4e00\u7c92\u4e39\u4e38\uff0c\u64ac\u5f00\u8c37\u5bd2\u9999\u7259\u5173\uff0c\u4e3a\u5979\u5582\u4e86 \u4e0b\u53bb\u3002 \u3000\u3000\u5927\u7ea6\u76cf\u8336\u5de5\u592b\u4e4b\u540e\uff0c\u8c37\u5bd2\u9999\u5df2\u9f3b\u606f\u6e10\u6d53\uff0c\u80f8\u53e3\u5fae\u89c1\u8d77\u4f0f\u3002 \u3000\u3000\u53c8\u8fc7\u4e86\u4e0d\u4e45\uff0e\u5979\u5df2\u7f13\u7f13\u7741\u5f00\u90a3\u5bf9\u4eae\u4e3d\u5374\u4f3c\u65e0\u795e\u7684\u53cc\u7738\u3002 \u3000\u3000\u5f53\u5979\u7684\u76ee\u5149\u4e00\u63a5\u89e6\u4e09\u5999\u8001\u4eba\uff0c\u7acb\u523b\u795e\u8272\u4e00\u6014\uff0c\u63a5\u7740\u554a\u4e86\u4e00\u58f0\uff0c\u53d1\u51fa\u5a07\u6175\u65e0\u529b\u7684\u5fae\u5f31\u58f0\u97f3 \u9053\uff1a\u201c\u5e08\u7236\uff0c\u539f\u6765\u662f\u4f60\u8001\u4eba\u5bb6\uff0c\u6211\u600e\u4e48\u4f1a\u5728\u8fd9\u91cc\uff1f\u201d \u3000\u3000\u4e09\u5999\u8001\u4eba\u6df1\u60c5\u800c\u53c8\u601c\u60dc\u7684\u9053\uff1a\u201c\u4f60\u5df2\u7ecf\u5728\u8fd9\u7389\u5e8a\u4e0a\u8eba\u4e86\u4e03\u65e5\u4e03\u591c\uff0c\u6211\u4e00\u76f4\u62c5\u5fc3\u4f60\u4e0d\u80fd\u6d3b \u56de\u6765\uff0c\u8fd8\u597d\uff0c\u4f60\u7ec8\u4e8e\u6d3b\u56de\u6765\u4e86\uff01\u201d \u3000\u3000\u8c37\u5bd2\u9999\u72b9\u5982\u5927\u68a6\u521d\u9192\uff0c\u5446\u4e86\u5f88\u4e45\u624d\u9053\uff1a\u201c\u6211\u771f\u7684\u6b7b\u8fc7\u4e48\uff1f\u201d \u3000\u3000\u4e09\u5999\u8001\u4eba\u9053\uff1a\u201c\u4f60\u867d\u7136\u6b7b\u4e86\u5c06\u8fd1\u4e24\u4e2a\u65f6\u8fb0\uff0c\u4f46\u90a3\u4e0d\u662f\u771f\u6b7b\uff0c\u4eba\u6b7b\u4e0d\u80fd\u590d\u6d3b\uff0c\u8001\u592b\u7167\u6837\u4e5f \u65e0\u6cd5\u6551\u4f60\uff01\u201d \u3000\u3000\u201c\u8fd9\u2026\u2026\u53c8\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d \u3000\u3000\u201c\u65e5\u524d\u8001\u592b\u4f20\u6388\u4f60\u6b66\u529f\u65f6\uff0e\u5df2\u5728\u4f60\u4f53\u5185\u6ce8\u5165\u4e86\u2018\u4e5d\u9633\u7384\u7f61\u2019\u4e4b\u6c14\uff0c\u518d\u52a0\u4e0a\u4f60\u4ece\u524d\u5df2\u7531\u4e07 \u82b1\u5bab\u4e3b\u4f5f\u516c\u5e38\u5904\u5b66\u4e60\u5f97\u2018\u4e09\u5143\u4e5d\u7075\u7384\u529f\u2019\uff0c\u4e8c\u6c14\u5728\u4f53\u5185\u5408\u800c\u4e3a\u4e00\uff0c\u867d\u7136\u8109\u606f\u5df2\u7ecf\u505c\u6b62\uff0c\u4ecd\u53ef \u4fdd\u6301\u771f\u5143\u4e0e\u7f61\u6c14\u4e0d\u6563\uff0c\u53ea\u8981\u4e0d\u8d85\u8fc7\u4e00\u663c\u591c\uff0e\u8001\u592b\u5c31\u6709\u529e\u6cd5\u628a\u4f60\u6551\u6d3b\u3002\u201d \u3000\u3000\u4e09\u5999\u8001\u4eba\u8bf4\u7740\uff0c\u6307\u4e86\u6307\u7f57\u5217\u5728\u7389\u69bb\u4e00\u4fa7\u7684\u5341\u51e0\u53ea\u7389\u74f6\u9053\uff1a\u201c\u66f4\u4f55\u51b5\u8fd9\u4e9b\u4e39\u836f\uff0c\u90fd\u662f\u8001\u592b \u8d39\u6bd5\u751f\u529f\u529b\u70bc\u5236\u800c\u6210\uff0c\u51e0\u4e4e\u6bcf\u79cd\u8349\u836f\u90fd\u6709\u8d77\u6b7b\u56de\u751f\u4e4b\u6548\uff0c\u4e3a\u4e86\u6551\u4f60\uff0c\u8001\u592b\u4e5f\u53ea\u6709\u62ff\u51fa\u6765\u4e00\u7528 \u4e86\uff01\u201d \u3000\u3000\u201c\u591a\u8c22\u5e08\u7236\u642d\u6551\uff0e\u5927\u6069\u5927\u5fb7\uff0c\u5f1f\u5b50\u771f\u4e0d\u77e5\u4f55\u5e74\u4f55\u6708\u624d\u80fd\u62a5\u7b54\uff01\u201d \u3000\u3000\u8c37\u5bd2\u9999\u8fb9\u8bf4\u8fb9\u6b32\u652f\u6491\u7740\u722c\u8d77\u8eab\u6765\u3002 \u3000\u3000\u4e09\u5999\u8001\u4eba\u8fde\u5fd9\u6447\u624b\u963b\u6b62\u9053\uff1a\u201c\u4e0d\u53ef\uff0c\u4f60\u7684\u4f24\u52bf\u592a\u91cd\uff0e\u5df2\u7ecf\u4f24\u53ca\u5185\u8151\uff0c\u6211\u867d\u7136\u4e3a\u4f60\u670d\u4e0b\u4e86 \u2018\u56de\u5929\u751f\u808c\u4e39\u2019\uff0c\u53c8\u6577\u8fc7\u2018\u590d\u9aa8\u6d88\u708e\u6563\u2019\uff0c\u4f46\u5374\u4e0d\u662f\u4e03\u5929\u4e03\u591c\u4fbf\u80fd\u590d\u539f\u7684\uff0c\u5fc5\u987b\u5c3d\u91cf\u4fdd\u6301\u9759 \u6b62\uff0c\u624d\u80fd\u597d\u5f97\u5feb\u4e9b\u3002\u201d \u3000\u3000\u201c\u5e08\u7236\u53ef\u89c1\u8fc7\u5f1f\u5b50\u5f53\u65f6\u662f\u600e\u4e48\u6b7b\u7684\uff1f\u201d \u3000\u3000\u201c\u90a3\u665a\u7684\u6fc0\u6218\uff0c\u8001\u592b\u2014\u76f4\u9690\u8eab\u9644\u8fd1\u6697\u5904\uff0c\u4e00\u5207\u90fd\u770b\u5f97\u6e05\u695a\u3002\u201d \u3000\u3000\u201c\u5f1f\u5b50\u53ea\u8bb0\u5f97\u5f53\u65f6\u4eb2\u624b\u6740\u6b7b\u4e09\u4eba\uff0e\u6700\u540e\u53c8\u81ea\u5df1\u6740\u4e86\u81ea\u5df1\u3002\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0e\u6b66\u5f53\u56db\u9633\u4e2d\u7684\u7d2b\u9633\u3001\u91d1\u9633\u4ee5\u53ca\u5c11\u6797\u7684\u5929\u660e\u548c\u5c1a\uff0c\u90fd\u6b7b\u5728\u4f60\u7684\u5251\u4e0b\uff0e\u6700\u540e\u4f60\u4e5f\u56e0 \u4e3a\u80f8\u53e3\u88ab\u91d1\u9633\u523a\u4e86\u4e00\u5251\uff0c\u81ea\u77e5\u65e0\u6cd5\u6d3b\u547d\uff0c\u7d22\u6027\u81ea\u523a\u80f8\u819b\u800c\u4ea1\u3002\u201d \u3000\u3000\u8c37\u5bd2\u9999\u9eef\u7136\u4e00\u53f9\u9053\uff1a\u201c\u4f60\u8001\u4eba\u5bb6\u53ef\u77e5\u9053\u5c11\u6797\u7684\u5929\u660e\u5927\u5e08\uff0c\u4e5f\u66fe\u6536\u8fc7\u6211\u505a\u8bb0\u540d\u5f1f\u5b50\uff1f\u201d \u3000\u3000\u4e09\u5999\u8001\u4eba\u6447\u5934\u9053\uff1a\u201c\u8001\u592b\u4e0d\u77e5\u9053\u8fd8\u6709\u8fd9\u79cd\u4e8b\u3002\u201d \u3000\u3000\u8c37\u5bd2\u9999\u9053\uff1a\u201c\u5f53\u5e74\u5c11\u6797\u548c\u6b66\u5f53\u9519\u6740\u4e86\u5f1f\u5b50\u7684\u5927\u54e5\uff0c\u5f1f\u5b50\u53c8\u9519\u6740\u4e86\u5929\u660e\u5927\u5e08\uff0c\u504f\u504f\u4f60\u8001\u4eba \u5bb6\u53c8\u9650\u5b9a\u5f1f\u5b50\u90a3\u665a\u53ea\u80fd\u6740\u56db\u4e2a\u4eba\uff0c\u5f1f\u5b50\u5728\u4f24\u91cd\u4e4b\u4e0b\uff0c\u4fbf\u53ea\u80fd\u62ff\u81ea\u5df1\u7684\u6027\u547d\u5145\u6570\uff01\u201d \u3000\u3000\u4e09\u5999\u8001\u4eba\u5fae\u5fae\u6447\u5934\u9053\uff1a\u201c\u4f60\u9519\u4e86\uff0c\u8001\u592b\u5e76\u6ca1\u89c4\u5b9a\u4f60\u975e\u6740\u56db\u4e2a\u4eba\u4e0d\u53ef\uff0c\u90a3\u665a\u4f60\u82e5\u4e0d\u81ea\u523a\u80f8 \u819b\uff0c\u8001\u592b\u4f55\u81f3\u8017\u8d39\u4e03\u65e5\u4e03\u591c\u7684\u5de5\u592b\u6551\u4f60\uff01\u201d \u3000\u3000\u8c37\u5bd2\u9999\u6b49\u7136\u4e00\u7b11\u9053\uff1a\u201c\u5f1f\u5b50\u5bf9\u5927\u54e5\u7684\u5927\u4ec7\u5df2\u62a5\uff0c\u552f\u6709\u4e00\u6b7b\uff0c\u624d\u80fd\u8ffd\u968f\u5927\u54e5\u4e8e\u4e5d\u6cc9\u4e4b\u4e0b\uff0c \u82e5\u4e00\u4e2a\u4eba\u6d3b\u5728\u4e16\u4e0a\uff0c\u751f\u547d\u8fd8\u6709\u4f55\u610f\u4e49\uff1f\u201d \u3000\u3000\u4e09\u5999\u8001\u4eba\u8138\u8272\u6108\u89c1\u51dd\u91cd\uff0c\u8bb8\u4e45\u624d\u4ef0\u5934\u559f\u7136\u2014\u53f9\u9053\uff1a\u201c\u5982\u679c\u8001\u592b\u6240\u6599\u4e0d\u5dee\uff0c\u4f60\u7684\u5927\u4ec7\u5e76\u4e0d \u66fe\u62a5\uff01\u201d \u3000\u3000\u8c37\u5bd2\u9999\u5403\u60ca\u9053\uff1a\u201c\u5e08\u7236\u8fd9\u8bdd\u2026\u2026\uff1f\u201d \u3000\u3000\u4e09\u5999\u8001\u4eba\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u5f53\u5e74\u771f\u6b63\u6740\u5bb3\u4f60\u592b\u80e1\u67cf\u9f84\u7684\u4eba\uff0c\u7edd\u4e0d\u53ef\u80fd\u662f\u6b66\u5f53\u7684\u7d2b\u9633\u3001\u91d1\u9633\u548c \u5c11\u6797\u7684\u5929\u660e\u548c\u5c1a\uff0c\u7eb5\u7136\u80e1\u67cf\u9f84\u662f\u6b7b\u5728\u8fd9\u4e9b\u4eba\u624b\u91cc\uff0c\u4e5f\u53ea\u80fd\u7b97\u662f\u8bef\u6740\u3002\u201d \u3000\u3000\u8c37\u5bd2\u9999\u9053\uff1a\u201c\u5f1f\u5b50\u77e5\u9053\uff0e\u8fd8\u6709\u4e00\u4e2a\u53eb\u8303\u7389\u6606\u7684\uff0c\u4ed6\u81ea\u79f0\u523a\u4e86\u6211\u5927\u54e5\u81f4\u547d\u4e00\u5251\uff0c\u53ef\u60dc\u90a3\u665a \u4ed6\u5e76\u4e0d\u5728\u573a\uff0c\u5f1f\u5b50\u6ca1\u6cd5\u6740\u4ed6\uff01\u201d \u3000\u3000\u201c\u4e0d\u5fc5\u518d\u8c08\u8fd9\u4e8b\u4e86\uff0c\u4f60\u8fd8\u662f\u517b\u4f24\u8981\u7d27\u3002\u201d \u3000\u3000\u8c37\u5bd2\u9999\u53ea\u89c9\u6709\u5343\u8a00\u4e07\u8bed\uff0c\u4e0d\u5410\u4e0d\u5feb\uff0c\u56e0\u89c1\u4e09\u5999\u8001\u4eba\u4e0d\u613f\u518d\u63d0\u90a3\u665a\u4e4b\u4e8b\uff0c\u6ce8\u89c6\u4e86\u8fd9\u4f4d\u4e00\u4ee3 \u5947\u4eba\u5f88\u4e45\u3002 \u3000\u3000\u5979\u5ffd\u7136\u5931\u58f0\u9053\uff1a\u201c\u5e08\u7236\uff0c\u4f60\u8001\u4eba\u5bb6\u600e\u80fd\u53c8\u6062\u590d\u4e86\u539f\u6765\u7684\u6a21\u6837\uff1f\u201d \u3000\u3000\u4e09\u5999\u8001\u4eba\u4e0d\u52a8\u58f0\u8272\u9053\uff1a\u201c\u4f60\u53ef\u662f\u770b\u51fa\u8001\u592b\u6709\u4ec0\u4e48\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u8c37\u5bd2\u9999\u9053\uff1a\u201c\u5f53\u5e74\u5f1f\u5b50\u534a\u4e2a\u6708\u524d\uff0c\u521d\u8fdb\u8fd9\u5730\u4e0b\u5bc6\u5ba4\uff0c\u4f60\u8001\u4eba\u5bb6\u6b63\u662f\u73b0\u5728\u7684\u6a21\u6837\u3002\u4f46\u540e\u6765 \u60a8\u5374\u4e00\u5929\u5929\u53d1\u987b\u53d8\u767d\uff0c\u4eba\u4e5f\u663e\u5f97\u8d8a\u6765\u8d8a\u82cd\u8001\uff0c\u6700\u540e\u5c31\u2026\u2026\u201d \u3000\u3000\u201c\u5c31\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u8c37\u5bd2\u9999\u660e\u660e\u8bb0\u5f97\u4e09\u5999\u8001\u4eba\u4e0a\u6b21\uff0c\u6700\u540e\u5df2\u7ecf\u5b89\u7136\u957f\u901d\uff0c\u5374\u53c8\u4e0d\u6562\u8bf4\u51fa\u53e3\u3002 \u3000\u3000\u4e0d\u8fc7\u8fd9\u5f88\u5feb\u5979\u5c31\u8f6c\u5ff5\uff1a\u4e09\u5999\u8001\u4eba\u65e2\u7136\u8fde\u6b7b\u53bb\u7684\u4eba\u90fd\u80fd\u6551\u6d3b\uff0c\u4e0a\u6b21\u7684\u6b7b\uff0c\u4e5f\u8bb8\u4e0d\u662f\u771f\u6b7b\uff0c \u4ee5\u4ed6\u7684\u76d6\u4e16\u4fee\u4e3a\uff0c\u4f3c\u4e4e\u8fde\u63a7\u5236\u81ea\u8eab\u7684\u751f\u6b7b\uff0e\u4e5f\u5e76\u975e\u96be\u4e8b\u3002 \u3000\u3000\u4e09\u5999\u8001\u4eba\u5c82\u80fd\u770b\u4e0d\u51fa\u8c37\u5bd2\u9999\u8981\u8bf4\u4ec0\u4e48\uff0c\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u90a3\u4e9b\u5929\u8001\u592b\u4e3a\u4e86\u4f20\u6388\u4f60\u6b66 \u529f\uff0c\u51e0\u4e4e\u5df2\u8017\u5c3d\u4f53\u5185\u771f\u5143\uff0c\u4e0d\u77e5\u4e0d\u89c9\u987b\u53d1\u53d8\u767d\u3002\u8fd9\u4e9b\u5929\u867d\u7136\u4e3a\u4f60\u7597\u4f24\uff0c\u4e5f\u5fd9\u5f97\u65e5\u591c\u4e0d\u505c\uff0c\u4f46 \u5374\u4e0d\u5fc5\u8017\u8d39\u5185\u529b\uff0e\u662f\u4ee5\u987b\u53d1\u53c8\u6062\u590d\u4e86\u539f\u6765\u7684\u989c\u8272\u3002\u81f3\u4e8e\u4f60\u770b\u5230\u6211\u5df2\u6b7b\u53bb\uff0c\u90a3\u4e0d\u8fc7\u662f\u6545\u610f\u5047\u6b7b \u800c\u5df2\u3002\u201d \u3000\u3000\u201c\u5e08\u7236\u4e3a\u4ec0\u4e48\u8981\u5047\u6b7b\uff1f\u5f53\u65f6\u4f7f\u5f97\u5f1f\u5b50\u51e0\u4e4e\u4f24\u5fc3\u6b32\u7edd\u3002\u201d \u3000\u3000\u201c\u56e0\u4e3a\u5f53\u65f6\u4f60\u62a5\u4ec7\u5728\u5373\uff0c\u552f\u6709\u6211\u5047\u88c5\u6b7b\u53bb\uff0c\u624d\u80fd\u4f7f\u4f60\u5fc3\u65e0\u6742\u5ff5\uff0c\u7075\u53f0\u6e05\u7eaf\uff0e\u5426\u5219\uff0c\u4f60\u5982 \u4f55\u80fd\u4e13\u5fc3\u4e00\u610f\u5bf9\u654c\uff1f\u201d \u3000\u3000\u8c37\u5bd2\u9999\u9ed8\u7136\u4e86\u8bb8\u4e45\u9053\uff1a\u201c\u4e0d\u77e5\u5f1f\u5b50\u8fd9\u4f24\u52bf\uff0c\u4ec0\u4e48\u65f6\u5019\u624d\u80fd\u75ca\u6108\u590d\u539f\uff1f\u201d \u3000\u3000\u4e09\u5999\u8001\u4eba\u9053\uff1a\u201c\u90a3\u8981\u770b\u4f60\u81ea\u5df1\u4e86\uff0c\u5982\u679c\u4f60\u80fd\u5fc3\u65e0\u6742\u5ff5\uff0c\u4e00\u610f\u9759\u517b\uff0e\u5341\u5929\u534a\u6708\u4fbf\u53ef\u590d\u539f\uff0e \u5426\u5219\uff0c\u8fde\u8001\u592b\u4e5f\u96be\u4ee5\u62c5\u4fdd\u4f60\u4f55\u65f6\u624d\u80fd\u6062\u590d\uff0e\u201d \u3000\u3000\u201c\u53ef\u662f\u6709\u4e9b\u4e8b\u60c5\u5f1f\u5b50\u4e0d\u80fd\u4e0d\u60f3\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u4e8b\u80fd\u4f7f\u5f97\u4f60\u975e\u60f3\u4e0d\u53ef\uff1f\u201d \u3000\u3000\u201c\u5f1f\u5b50\u8eab\u4e3a\u7eff\u6797\u76df\u4e3b\uff0c\u624b\u4e0b\u7edf\u8f96\u7740\u4e0d\u5c11\u4e09\u5c71\u4e94\u5cb3\u7684\u4eba\u7269\uff0c\u5982\u4eca\u4ed6\u4eec\u5728\u7fa4\u9f99\u65e0\u9996\u7684\u60c5\u5f62 \u4e0b\uff0e\u52bf\u5fc5\u4e3a\u4e89\u593a\u7ee7\u627f\u7eff\u6797\u76df\u4e3b\u4e4b\u4f4d\u800c\u917f\u6210\u5185\u6218\uff0c\u8fd9\u6b63\u662f\u5f1f\u5b50\u653e\u5fc3\u4e0d\u4e0b\u7684\u4e8b\u3002\u201d \u3000\u3000\u201c\u7eff\u6797\u660e\u4e3b\u4e4b\u4f4d\uff0c\u4ee5\u5e74\u9ad8\u6253\u5fb7\uff0e\u4e14\u6b66\u529f\u51fa\u4f17\u8005\u5f97\u4e4b\uff0c\u96be\u9053\u6ca1\u6709\u4f60\uff0c\u5c31\u65e0\u4eba\u7edf\u5fa1\u5929\u4e0b\u7eff\u6797 \u4e86\u4e48\uff1f\u201d \u3000\u3000\u8c37\u5bd2\u9999\u542c\u51fa\u4e09\u5999\u8001\u4eba\u7684\u8bed\u6c14\uff0c\u5bf9\u81ea\u5df1\u9887\u6709\u8d23\u5907\u4e4b\u610f\uff0c\u4f46\u5fc3\u4e2d\u6240\u60f3\u4e4b\u4e8b\uff0e\u5374\u53c8\u4e0d\u80fd\u4e0d\u8bf4\uff0c \u987f\u4e86\u4e00\u987f\u9053\uff1a\u201c\u5e08\u7236\u7684\u8bdd\u56fa\u7136\u4e0d\u9519\uff0c\u4f46\u5317\u5cb3\u8ff7\u8e2a\u8c37\u7eff\u6797\u603b\u5be8\u5185\u4eba\u5458\u590d\u6742\uff0e\u5f1f\u5b50\u82e5\u4e0d\u80fd\u56de\u53bb\u4e3b \u6301\u5927\u5c40\uff0e\u52bf\u5fc5\u5f15\u8d77\u5185\u90e8\u7eb7\u4e89\uff0c\u53e6\u4e00\u573a\u6d41\u8840\u706b\u62fc\uff0c\u5fc5\u5f53\u65e0\u6cd5\u907f\u514d\u3002\u201d \u3000\u3000\u4e09\u5999\u8001\u4eba\u7565\u4e00\u6c89\u541f\u9053\uff1a\u201c\u4f60\u4e14\u8bf4\u8bf4\u770b\uff0c\u6709\u54ea\u4e9b\u4eba\u5b58\u4e0b\u4e86\u7aa5\u89c6\u7eff\u6797\u76df\u4e3b\u4e4b\u5fc3\uff1f\u201d \u3000\u3000\u8c37\u5bd2\u9999\u9053\uff1a\u201c\u4e00\u53df\u3001\u4e8c\u5947\u3001\u56db\u602a\uff0c\u5f53\u5e74\u90fd\u662f\u9ed1\u9053\u5de8\u64d8\uff0e\u800c\u5fc3\u5730\u9634\u6c89\u9669\u6076\uff0e\u6840\u50b2\u96be\u9a6f\u3002\u5f53 \u5148\u592b\u80e1\u67cf\u9f84\u5728\u4e16\u4efb\u7eff\u6797\u76df\u4e3b\u65f6\uff0c\u4ed6\u4eec\u6151\u4e8e\u5148\u592b\u7684\u5a01\u671b\u548c\u6b66\u529f\uff0c\u81ea\u7136\u4e0d\u6562\u5174\u98ce\u4f5c\u6d6a\u3002\u540e\u6765\u5f1f\u5b50 \u7ee7\u4efb\u7eff\u6797\u76df\u4e3b\uff0c\u57fa\u4e8e\u67d0\u79cd\u5fae\u5999\u539f\u56e0\uff0c\u4ed6\u4eec\u4ecd\u4e0d\u5f97\u4e0d\u6682\u65f6\u4e0d\u6562\u8f7b\u4e3e\u5984\u52a8\u3002 \u3000\u3000\u5982\u4eca\u5f1f\u5b50\u4e0d\u5728\uff0c\u5317\u5cb3\u8ff7\u8e2a\u8c37\u7eff\u6797\u603b\u5be8\u5185\u52bf\u5fc5\u4f1a\u56e0\u4ed6\u4eec\u7684\u4e89\u593a\u7eff\u6797\u76df\u4e3b\u4e4b\u4f4d\uff0c\u800c\u5f15\u8d77\u4e00\u573a \u7a7a\u524d\u6740\u52ab\u3002\u201d \u3000\u3000\u201c\u90a3\u5c31\u628a\u7eff\u6797\u76df\u4e3b\u4e4b\u4f4d\u8ba9\u7ed9\u4ed6\u4eec\u53c8\u6709\u4f55\u59a8\uff1f\u201d \u3000\u3000\u201c\u5e08\u7236\u53ef\u77e5\u9053\uff0c\u82e5\u8ba9\u8fd9\u822c\u4eba\u767b\u4e0a\u7eff\u6797\u76df\u4e3b\u4e4b\u4f4d\uff0c\u90a3\u5c31\u662f\u5929\u4e0b\u5927\u4e71\uff0e\u6c5f\u6e56\u4e2d\u7684\u4ec7\u6740\u7eb7\u4e89\uff0c \u5c06\u6c38\u65e0\u4f11\u6b62\u4e4b\u65e5\u4e86\uff01\u201d \u3000\u3000\u201c\u4f60\u6614\u65e5\u7684\u90e8\u5c5e\uff0c\u662f\u5426\u8fd8\u6709\u4eba\u4e0d\u670d\u4ed6\u4eec\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u6709\uff0c\u5728\u5317\u5cb3\u8c1c\u8e2a\u8c37\u7eff\u6797\u603b\u5be8\u5185\uff0c\u81ea\u5148\u592b\u80e1\u67cf\u9f84\u521b\u7acb\u4e4b\u521d\uff0c\u4fbf\u5206\u6210\u4e24\u5927\u6d3e\u7cfb\uff0c\u65b9\u624d \u8bf4\u7684\u90a3\u4e9b\u9ed1\u9053\u5de8\u64d8\uff0c\u4ee5\u7f57\u6d6e\u4e00\u53df\u970d\u5143\u4f3d\u4e3a\u9996\u3002 \u3000\u3000\u53e6\u4e00\u6d3e\u5219\u4ee5\u62e5\u6234\u5148\u592b\u80e1\u67cf\u9f84\u7684\u949f\u4e00\u8c6a\u548c\u4f59\u4ea6\u4e50\u4e3a\u9996\uff0e\u4e24\u5927\u6d3e\u7cfb\u4e00\u76f4\u660e\u4e89\u6697\u6597\uff0c\u53ef\u8bf4\u4ece\u6765 \u4e0d\u66fe\u505c\u6b62\u8fc7\u3002\u201d \u3000\u3000\u4e09\u5999\u8001\u4eba\u542c\u5230\u8fd9\u91cc\uff0e\u4e5f\u89c9\u51fa\u7684\u786e\u4e8b\u6001\u4e25\u91cd\uff0c\u957f\u957f\u5401\u4e86\u4e00\u53e3\u6c14\u9053\uff1a\u201c\u8001\u592b\u76f8\u4fe1\u4f60\u7684\u8bdd\u4e0d \u5047\uff0c\u4f46\u4f60\u73b0\u5728\u5185\u4f24\u672a\u590d\uff0e\u6839\u672c\u65e0\u6cd5\u56de\u5317\u5cb3\u8ff7\u8e2a\u8c37\u7eff\u6797\u603b\u5be8\u53bb\u3002\u201d \u3000\u3000\u8c37\u5bd2\u9999\u9eef\u7136\u4e00\u53f9\uff0c\u4e0d\u518d\u8a00\u8bed\u3002 \u3000\u3000\u4e09\u5999\u8001\u4eba\u4e5f\u8f6c\u8eab\u800c\u53bb\uff0c\u8fb9\u8d70\u8fb9\u9053\uff1a\u201c\u597d\u597d\u517b\u4f24\u8981\u7d27\uff0c\u522b\u80e1\u601d\u4e71\u60f3\uff0e\u5916\u9762\u7684\u4e8b\uff0e\u4e0d\u8bba\u5929\u7ffb \u5730\u8986\uff0e\u4e5f\u6682\u65f6\u522b\u653e\u5728\u5fc3\u4e0a\uff01\u201d \u3000\u3000\u949f\u4e00\u8c6a\u3001\u4f59\u4ea6\u4e50\u3001\u9ea6\u5c0f\u660e\u3001\u6587\u5929\u751f\u3001\u82d7\u7d20\u5170\u3001\u4e07\u6620\u971e\uff0c\u4ee5\u53ca\u6c5f\u5317\u56db\u9f99\u7b49\u516b\u7537\u4e8c\u5973\uff0e\u90a3\u665a \u4e0e\u5e9e\u58eb\u51b2\u5206\u5934\u641c\u7d22\u8c37\u5bd2\u9999\u9057\u4f53\u3002 \u3000\u3000\u76f4\u5230\u6b21\u65e5\u5929\u660e\uff0e\u59cb\u7ec8\u6beb\u65e0\u6240\u83b7\u3002 \u3000\u3000\u5f53\u4ed6\u4eec\u516b\u7537\u4e8c\u5973\u5929\u660e\u4f1a\u5408\u540e\uff0c\u5374\u53c8\u4e0d\u89c1\u4e86\u5e9e\u58eb\u51b2\u548c\u7fce\u513f\u3002 \u3000\u3000\u4ed6\u4eec\u4e0d\u7ea6\u800c\u540c\u5730\u610f\u8bc6\u5230\uff0c\u4e00\u5b9a\u662f\u4e0a\u4e86\u5e9e\u58eb\u51b2\u8fd9\u8001\u5bb6\u4f19\u7684\u5f53\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u9996\u5148\u53d1\u8a00\u9053\uff1a\u201c\u54b1\u4eec\u4e0d\u5fc5\u627e\u5566\uff01\u54b1\u4eec\u5df2\u7ecf\u4e2d\u4e86\u5e9e\u58eb\u51b2\u90a3\u8001\u5c0f\u5b50\u7684\u8c03\u864e\u79bb\u5c71\u8ba1 \u5566\uff01\u201d \u3000\u3000\u6587\u5929\u751f\u8f83\u4e3a\u5929\u771f\uff0c\u832b\u7136\u95ee\u9053\uff1a\u201c\u9ea6\u5c0f\u4fa0\u8fd9\u8bdd\uff0c\u6709\u4ec0\u4e48\u6839\u636e\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4e8b\u60c5\u5f88\u660e\u663e\uff0c\u90a3\u8001\u5bb6\u4f19\u5e26\u7740\u8c37\u76df\u4e3b\u7684\u9057\u4f53\u8d76\u8def\uff0c\u4e00\u5b9a\u8d70\u5f97\u4e0d\u5feb\uff0e\u4ed6\u6599\u5b9a\u54b1 \u4eec\u5fc5\u5b9a\u968f\u540e\u8d76\u6765\uff0e\u6240\u4ee5\u624d\u5728\u54b1\u4eec\u5230\u8fbe\u4e4b\u524d\uff0e\u5148\u628a\u8c37\u76df\u4e3b\u7684\u9057\u4f53\u85cf\u8d77\uff0e\u7136\u540e\u518d\u85c9\u5927\u5bb6\u5c55\u5f00\u641c\u7d22 \u7684\u65f6\u95f4\u5077\u5077\u5e26\u8d70\u3002\u201d \u3000\u3000\u6587\u5929\u751f\u9894\u9996\u9053\uff1a\u201c\u9ea6\u5c0f\u4fa0\u7684\u8bdd\u6709\u7406\uff0c\u90a3\u54b1\u4eec\u73b0\u5728\u5c31\u8be5\u518d\u53bb\u8ffd\u8d76\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u4e00\u5411\u6027\u60c5\u6025\u8e81\uff0c\u95fb\u8a00\u7acb\u5373\u9ad8\u58f0\u9053\uff1a\u201c\u5bf9\uff01\u54b1\u4eec\u9a6c\u4e0a\u53bb\u8ffd\u90a3\u8001\u5bb6\u4f19\uff01\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u9053\uff1a\u201c\u6211\u770b\u4e0d\u5fc5\u5566\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u8f6c\u5934\u95ee\u9053\uff1a\u201c\u96be\u9053\u54b1\u4eec\u5c31\u8ba9\u90a3\u8001\u5bb6\u4f19\u767d\u767d\u628a\u8c37\u76df\u4e3b\u7684\u9057\u4f53\u5e26\u8d70\uff1f\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u4e0d\u614c\u4e0d\u5fd9\u7684\u9053\uff1a\u201c\u5373\u4f7f\u8c37\u76df\u4e3b\u7684\u9057\u4f53\u771f\u88ab\u5e9e\u58eb\u51b2\u5e26\u8d70\uff0c\u8fd9\u5c71\u4e0a\u5230\u5904\u90fd\u662f\u5c94\u8def\uff0c\u65f6 \u95f4\u53c8\u9694\u4e86\u8fd9\u4e48\u4e45\uff0e\u6839\u672c\u4e0d\u53ef\u80fd\u8ffd\u5230\u4ed6\uff0c\u66f4\u4f55\u51b5\u54b1\u4eec\u53e6\u6709\u4e00\u4ef6\u66f4\u91cd\u8981\u7684\u5927\u4e8b\u8981\u529e\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\u8fd8\u6709\u6bd4\u627e\u56de\u8c37\u76df\u4e3b\u9057\u4f53\u66f4\u91cd\u8981\u7684\uff1f\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u626b\u63a0\u4e86\u4f17\u4eba\u4e00\u773c\u9053\uff1a\u201c\u773c\u4e0b\u6700\u91cd\u8981\u7684\u4e8b\uff0c\u662f\u5fc5\u987b\u5c3d\u5feb\u8d76\u56de\u5317\u5cb3\u8ff7\u8e2a\u8c37\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u4f3c\u662f\u4ecd\u672a\u542c\u61c2\u5bf9\u65b9\u8bdd\u4e2d\u4e4b\u610f\uff0c\u95ee\u9053\uff1a\u201c\u6025\u7740\u8d76\u56de\u5317\u5cb3\u8ff7\u8e2a\u8c37\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u9053\uff1a\u201c\u4f17\u6240\u5468\u77e5\uff0c\u7f57\u6d6e\u4e09\u53df\u970d\u5143\u4f3d\u65e9\u5df2\u5b58\u4e0b\u4e89\u593a\u7eff\u6797\u76df\u4e3b\u4e4b\u5fc3\uff0c\u73b0\u5728\u8c37\u76df\u4e3b\u5df2 \u6b7b\uff0c\u5728\u4ed6\u6765\u8bf4\uff0c\u53ef\u8c13\u673a\u4f1a\u96be\u5f97\u3002 \u3000\u3000\u773c\u4e0b\u7684\u60c5\u52bf\u5f88\u660e\u663e\uff0c\u54b1\u4eec\u548c\u970d\u5143\u4f3d\u4e24\u65b9\uff0e\u8c01\u5148\u56de\u5230\u5317\u5cb3\u8c1c\u8e2a\u8c37\u603b\u5be8\uff0c\u8c01\u5c31\u591a\u4e00\u4efd\u5e0c\u671b\u767b \u4e0a\u7eff\u6797\u76df\u4e3b\u4e4b\u4f4d\uff0c\u96be\u9053\u8bf8\u4f4d\u8fd8\u4e0d\u660e\u8fd9\u4e00\u6b65\u68cb\uff1f\u201d \u3000\u3000\u6b64\u8bed\u4e00\u51fa\uff0c\u5728\u573a\u4f17\u4eba\u90fd\u89c9\u51fa\u4f59\u4ea6\u4e50\u7684\u8bdd\u6709\u7406\u3002 \u3000\u3000\u4f59\u4ea6\u4e50\u63a5\u7740\u518d\u9053\uff1a\u201c\u5982\u679c\u6211\u6240\u6599\u4e0d\u5dee\uff0c\u7f57\u6d6e\u4e00\u53df\u970d\u5143\u4f3d\u548c\u5cad\u5357\u4e8c\u5947\uff0e\u4ee5\u53ca\u6c5f\u5357\u56db\u602a\u4e00\u4f19 \u4eba\uff0c\u65e9\u5df2\u5148\u54b1\u4eec\u8d76\u56de\u5317\u5cb3\u8ff7\u8e2a\u8c37\uff0c\u5426\u5219\u54b1\u4eec\u4e0d\u4f1a\u5728\u8def\u4e0a\u59d1\u7ec8\u6ca1\u78b0\u5230\u4ed6\u4eec\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u8fd8\u662f\u6709\u70b9\u4e0d\u670d\u6c14\uff0c\u54fc\u4e86\u58f0\u9053\uff1a\u201c\u7167\u4f60\u8fd9\u6837\u8bf4\uff0c\u54b1\u4eec\u5c31\u4e0d\u7ba1\u8c37\u76df\u4e3b\u7684\u9057\u4f53\u4e86\u4e48\uff1f\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u9053\uff1a\u201c\u5e76\u975e\u4e0d\u7ba1\uff0c\u800c\u662f\u4e8b\u6709\u8f7b\u91cd\u7f13\u6025\uff0c\u54b1\u4eec\u8d76\u56de\u5317\u5cb3\u8ff7\u8e2a\u8c37\uff0c\u62e5\u6234\u949f\u5144\u767b\u4e0a\u7eff\u6797 \u76df\u4e3b\u4e4b\u4f4d\u540e\uff0c\u518d\u628a\u8c37\u76df\u4e3b\u7684\u9057\u4f53\u627e\u56de\uff0c\u7167\u6837\u4e0d\u8fdf\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u5230\u90a3\u65f6\u4e0d\u77e5\u9694\u4e86\u591a\u4e45\uff0c\u53c8\u5230\u54ea\u91cc\u53bb\u627e\uff1f\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u9053\uff1a\u201c\u4f60\u653e\u5fc3\uff0c\u5e9e\u58eb\u51b2\u4e0d\u662f\u574f\u4eba\uff0c\u4f55\u51b5\u8c37\u76df\u4e3b\u53c8\u662f\u4ed6\u5916\u5b59\u7fce\u513f\u7684\u517b\u6bcd\uff0c\u4ed6\u628a\u8c37\u76df \u4e3b\u7684\u9057\u4f53\u5e26\u56de\u957f\u767d\u5c71\uff0c\u4e00\u5b9a\u4f1a\u597d\u597d\u5b89\u846c\uff0c\u54b1\u4eec\u5c06\u6765\u6d3e\u4eba\u5230\u957f\u767d\u5c71\uff0c\u628a\u8c37\u76df\u4e3b\u7684\u9057\u9ab8\u8fce\u56de\uff0c\u548c \u80e1\u524d\u76df\u4e3b\u7684\u9057\u9ab8\u5408\u846c\u4e00\u5904\uff0c\u4e0d\u4e5f\u662f\u5f88\u597d\u4e48\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u65e0\u6cd5\u518d\u53cd\u9a73\uff0c\u7ec8\u4e8e\u5b89\u9759\u4e0b\u6765\uff0c\u4f46\u5374\u63a5\u7740\u53c8\u5bfb\u4e0a\u522b\u7684\u8bdd\u9898\uff0c\u9f3b\u5b54\u91cc\u51b7\u54fc\u2014\u58f0\u9053\uff1a \u201c\u4f60\u65b9\u624d\u8bf4\u54b1\u4eec\u5c06\u6765\u56de\u5230\u5317\u5cb3\u8ff7\u8e2a\u8c37\u540e\uff0c\u8981\u62e5\u6234\u8c01\u4e3a\u7eff\u6797\u76df\u4e3b\uff1f\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u9053\uff1a\u201c\u81ea\u7136\u662f\u949f\u5144\u949f\u2014\u8c6a\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u4e00\u5b9a\u8981\u62e5\u6234\u59d3\u949f\u7684\uff1f\u201d \u3000\u3000\u201c\u949f\u5144\u539f\u6765\u5df2\u505a\u8fc7\u7eff\u6797\u76df\u4e3b\uff0c\u540e\u6765\u80e1\u67cf\u9f84\u91cd\u767b\u7eff\u6797\u76df\u4e3b\u4e4b\u4f4d\uff0c\u949f\u5144\u548c\u970d\u5143\u4f3d\u53c8\u540c\u4e3a\u5f53\u65f6 \u7684\u526f\u76df\u4e3b\uff0c\u73b0\u5728\u80e1\u76df\u4e3b\u548c\u8c37\u76df\u4e3b\u5df2\u4e0d\u5728\uff0c\u7531\u949f\u5144\u6765\u7ee7\u4efb\u76df\u4e3b\uff0c\u672c\u6765\u5c31\u662f\u7406\u6240\u5f53\u7136\u4e4b\u4e8b\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u62cd\u4e86\u62cd\u80f8\u812f\u9053\uff1a\u201c\u96be\u9053\u6211\u9ea6\u5c0f\u660e\u5c31\u4e0d\u53ef\u4ee5\u5f53\u7eff\u6797\u76df\u4e3b\uff1f\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u6447\u6447\u5934\u9053\uff1a\u201c\u4f60\u867d\u7136\u5728\u54b1\u4eec\u8fd9\u4f19\u4eba\u4e2d\uff0c\u6b66\u529f\u53ef\u80fd\u6700\u9ad8\uff0c\u4f46\u7ec8\u7a76\u5e74\u7eaa\u592a\u5c0f\uff0c\u4e5f\u8c08\u4e0d \u4e0a\u6c5f\u6e56\u7ecf\u9a8c\u9605\u5386\uff0c\u7531\u4f60\u6765\u505a\u7eff\u6797\u76df\u4e3b\uff0c\u53ea\u6015\u8fd8\u4e0d\u592a\u5408\u9002\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u6709\u4ec0\u4e48\u4e0d\u5408\u9002\uff0e\u80e1\u76df\u4e3b\u662f\u6211\u7684\u5e08\u5144\uff0c\u8c37\u76df\u4e3b\u662f\u6211\u7684\u5e08\u5ac2\uff0c\u4ed6\u4eec\u6b7b\u4e86\uff0c\u81ea\u7136 \u5e94\u8be5\u7531\u6211\u6765\u505a\uff01\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u6de1\u7136\u7b11\u9053\uff1a\u201c\u505a\u76df\u4e3b\u8981\u6709\u4eba\u62e5\u6234\u624d\u6210\uff0e\u4f60\u81ea\u4fe1\u6709\u591a\u5c11\u4eba\u80af\u62e5\u6234\u4f60\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4f60\u62e5\u4e0d\u62e5\u6234\u6211\uff1f\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u53c8\u662f\u4e00\u7b11\u9053\uff1a\u201c\u5f88\u96be\u8bf4\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u76ee\u5149\u6a2a\u626b\u5168\u573a\u9053\uff1a\u201c\u4f60\u4eec\u5927\u5bb6\u8c01\u62e5\u6234\u6211\u8bf7\u4e3e\u624b\uff1f\u201d \u3000\u3000\u5728\u573a\u4f17\u4eba\u5e76\u65e0\u4e1d\u6beb\u53cd\u5e94\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u9ed8\u4e86\u4e00\u9ed8\uff0e\u518d\u95ee\u9053\uff1a\u201c\u53cd\u5bf9\u6211\u5f53\u7eff\u6797\u76df\u4e3b\u7684\u8bf7\u4e3e\u624b\uff1f\u201d \u3000\u3000\u8fd8\u662f\u6beb\u65e0\u52a8\u9759\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u867d\u6709\u4e9b\u4e0b\u4e0d\u4e86\u53f0\u9636\uff0c\u5374\u8fd8\u662f\u563f\u563f\u7b11\u9053\uff1a\u201c\u65e2\u7136\u5927\u5bb6\u6ca1\u8bf4\u8bdd\u7684\uff0c\u90a3\u4fbf\u8868\u793a\u5e76\u4e0d\u53cd \u5bf9\u3002\u65e2\u7136\u4e0d\u53cd\u5bf9\uff0c\u90a3\u6211\u5c31\u662f\u672a\u6765\u7684\u7eff\u6797\u76df\u4e3b\u5566\uff01\u201d \u3000\u3000\u53ea\u542c\u949f\u4e00\u8c6a\u51b7\u7b11\u9053\uff1a\u201c\u9ea6\u5c0f\u660e\uff0c\u4f60\u82e5\u5f53\u4e0a\u7eff\u6797\u76df\u4e3b\uff0c\u96be\u9053\u970d\u5143\u4f3d\u4e5f\u4e0d\u53cd\u5bf9\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u8c01\u53cd\u5bf9\u6211\u5c31\u6740\u8c01\uff0c\u970d\u5143\u4f3d\u90a3\u8001\u738b\u516b\u86cb\uff0c\u6211\u65e9\u5c31\u770b\u7740\u4e0d\u987a\u773c\u5566\uff01\u201d \u3000\u3000\u949f\u2014\u8c6a\u62f1\u62f1\u624b\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0e\u6211\u949f\u4e00\u8c6a\u7b2c\u4e00\u4e2a\u62e5\u6234\u4f60\uff01\u201d \u3000\u3000\u949f\u4e00\u8c6a\u8fd9\u2014\u53e5\u8bdd\uff0c\u7acb\u523b\u4f7f\u5f97\u5168\u573a\u7686\u60ca\uff0e\u8c01\u90fd\u4e0d\u76f8\u4fe1\u949f\u4e00\u8c6a\u80af\u4e56\u4e56\u7684\u628a\u7eff\u6797\u76df\u4e3b\u5b9d\u5ea7\uff0e\u62f1 \u624b\u8ba9\u7ed9\u4e00\u4e2a\u5c1a\u672a\u6210\u5e74\u7684\u5b69\u5b50\u624b\u4e2d\u3002 \u3000\u3000\u53ea\u6709\u4f59\u4ea6\u4e50\u5fc3\u91cc\u6709\u6570\u3002 \u3000\u3000\u539f\u6765\u949f\u4e00\u8c6a\u60f3\u5230\uff0e\u4ee5\u5df1\u65b9\u7684\u529b\u91cf\u5bf9\u4ed8\u970d\u5143\u4f3d\u90a3\u4f19\u4eba\uff0c\u5b9e\u611f\u4e0d\u8db3\u3002 \u3000\u3000\u5c24\u5176\u4ed6\u4e0e\u970d\u5143\u4f3d\u6570\u5ea6\u4ea4\u624b\uff0c\u65e9\u77e5\u5bf9\u65b9\u6b66\u529f\u9ad8\u4e0d\u53ef\u6d4b\uff0c\u5c06\u6765\u4e0e\u5bf9\u65b9\u4e89\u593a\u7eff\u6797\u5b9d\u5ea7\u7684\u2014\u756a\u6fc0 \u6218\u2026\u2026 \u3000\u3000\u9e7f\u6b7b\u8c01\u624b\uff0c\u5b9e\u5728\u96be\u4ee5\u9884\u6599\uff0c\u81ea\u5df1\u6b63\u597d\u5229\u7528\u9ea6\u5c0f\u660e\u4ee3\u6253\u3002\u800c\u9ea6\u5c0f\u660e\u6b66\u529f\u867d\u9ad8\uff0c\u5374\u53c8\u56e0\u5e74\u5e7c \u7f3a\u4e4f\u667a\u8c0b\uff0c\u5c4a\u65f6\u5fc5\u4e0d\u8d39\u5439\u7070\u4e4b\u529b\uff0c\u7531\u81ea\u5df1\u7a33\u767b\u7eff\u6797\u76df\u4e3b\u5b9d\u5ea7\u3002 \u3000\u3000\u8fd9\u2014\u7740\u68cb\uff0e\u53ea\u6709\u4f59\u4ea6\u4e50\u660e\u767d\uff0c\u4f59\u4ea6\u4e50\u662f\u201c\u667a\u591a\u661f\u201d\u7c7b\u578b\u7684\u4eba\u7269\uff0c\u53c8\u4e0e\u949f\u4e00\u8c6a\u4e00\u5411\u79c1\u4ea4\u751a \u5bc6\uff0c\u56e0\u4e4b\u5bf9\u949f\u2014\u8c6a\u7684\u4e00\u756a\u8bdd\uff0c\u624d\u4e0d\u8868\u5f02\u8bae\u3002 \u3000\u3000\u4f46\u9ea6\u5c0f\u660e\u5374\u8fd8\u6709\u4e9b\u4e0d\u4fe1\uff0c\u4e24\u773c\u7728\u52a8\u4e86\u2014\u9635\uff0c\u9053\uff1a\u201c\u949f\u4e00\u8c6a\uff0c\u4f60\u65b9\u624d\u7684\u8bdd\uff0c\u53ef\u662f\u51fa\u4e8e\u771f \u5fc3\uff1f\u201d \u3000\u3000\u949f\u4e00\u8c6a\u4e0d\u52a8\u58f0\u8272\u9053\uff1a\u201c\u949f\u67d0\u4f55\u82e6\u9a97\u4f60\u4e00\u4e2a\u5c0f\u5b69\u5b50\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4f60\u5c31\u662f\u56e0\u4e3a\u6211\u5e74\u7eaa\u5c0f\uff0c\u6240\u4ee5\u624d\u89c9\u5f97\u5bb9\u6613\u9a97\uff01\u201d \u3000\u3000\u949f\u4e00\u8c6a\u53cd\u95ee\u9053\uff1a\u201c\u4f60\u8981\u6211\u600e\u6837\u8868\u793a\u624d\u80fd\u76f8\u4fe1\u5462\uff1f\u96be\u9053\u8fd8\u8981\u5bf9\u5929\u53d1\u8a93\u4e0d\u6210\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u60f3\u4e86\u60f3\u9053\uff1a\u201c\u7528\u4e0d\u7740\u53d1\u8a93\u5566\uff01\u5c06\u6765\u6211\u6218\u80dc\u4e86\u970d\u5143\u4f3d\u90a3\u8001\u738b\u516b\u86cb\uff0c\u4f60\u82e5\u4e0d\u670d\uff0c\u53ea\u7ba1 \u518d\u6765\u627e\u6211\u8f83\u91cf\u3002\u201d \u3000\u3000\u63a5\u7740\u53c8\u626b\u4e86\u5728\u573a\u4f17\u4eba\u4e00\u773c\u9053\uff1a\u201c\u521a\u624d\u949f\u4e00\u8c6a\u7684\u8bdd\uff0c\u4f60\u4eec\u5927\u5bb6\u90fd\u542c\u89c1\u4e86\uff0c\u5c06\u6765\u4ed6\u82e5\u4e0d\u8ba4 \u5e10\uff0c\u4f60\u4eec\u5927\u5bb6\u90fd\u8981\u7ad9\u51fa\u6765\u4f5c\u8bc1\uff01\u201d \u3000\u3000\u4f17\u4eba\u8fd8\u662f\u6ca1\u4eba\u7b54\u8154\u3002 \u3000\u3000\u4f59\u4ea6\u4e50\u9053\uff1a\u201c\u4e0d\u5fc5\u518d\u4e3a\u8fd9\u4e8b\u803d\u8bef\u65f6\u95f4\u4e86\uff0e\u5927\u5bb6\u8d76\u5feb\u56de\u5317\u5cb3\u8ff7\u8e2a\u8c37\u8981\u7d27\uff01\u201d \u3000\u3000\u53ea\u89c1\u82d7\u7d20\u5170\u671b\u7740\u6c5f\u5317\u56db\u9f99\u7684\u8001\u5927\u51fa\u4e91\u9f99\u59dc\u5b8f\u9053\uff1a\u201c\u59dc\u5927\u4fa0\uff0c\u5728\u54b1\u4eec\u8fd9\u4e9b\u4eba\u4e2d\uff0c\u970d\u65e0\u4f3d\u4f3c \u4e4e\u5bf9\u4f60\u8fd8\u6ca1\u5b58\u591a\u5927\u6212\u5fc3\uff0e\u6700\u597d\u7531\u4f60\u96c7\u2014\u5339\u5feb\u9a6c\u8d76\u56de\u53bb\uff0c\u4ee5\u4fbf\u5bf9\u6211\u4eec\u9884\u4f5c\u63a5\u5e94\u3002\u201d \u3000\u3000\u59dc\u5b8f\u7acb\u5373\u540c\u610f\u9053\uff1a\u201c\u4e5f\u597d\uff0c\u90a3\u4e48\u6211\u8fd9\u5c31\u5148\u8d70\u4e86\uff01\u201d \u3000\u3000\u949f\u4e00\u8c6a\u7b49\u4e00\u884c\u4e5d\u4e2a\u4eba\uff0c\u7531\u6d59\u4e1c\u5929\u53f0\u5c71\uff0c\u4e0d\u8db3\u4e00\u4e2a\u6708\uff0c\u4fbf\u5df2\u8d76\u56de\u5317\u5cb3\u3002 \u3000\u3000\u8fd9\u65e5\u3002 \u3000\u3000\u4f17\u4eba\u5df2\u5230\u8fbe\u8fdb\u5165\u8ff7\u8e2a\u8c37\u7684\u8c37\u53e3\u3002 \u3000\u3000\u5317\u5cb3\u8ff7\u8e2a\u8c37\u7684\u5929\u4e0b\u7eff\u6797\u603b\u5be8\uff0c\u4e43\u662f\u4e09\u5e74\u524d\u7531\u524d\u4efb\u7eff\u6797\u76df\u4e3b\u80e1\u67cf\u9f84\u4e00\u624b\u7b56\u5212\u521b\u5efa\u7684\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u7b49\u4eba\u968f\u80e1\u67cf\u9f84\u8c37\u5bd2\u9999\u524d\u540e\u4e24\u4efb\u76df\u4e3b\uff0c\u5728\u8fd9\u91cc\u4f4f\u4e86\u4e24\u4e09\u5e74\uff0c\u4e0d\u6d88\u8bf4\u5bf9\u8c37\u5185\u7684\u5730\u5f62\uff0c \u4ee5\u53ca\u5404\u79cd\u8bbe\u65bd\uff0c\u4e86\u5982\u6307\u638c\u3002 \u3000\u3000\u4f46\u5c40\u5916\u4eba\u82e5\u8fdb\u5165\u8c37\u5185\uff0e\u5374\u7edd\u5c11\u4e0d\u4e86\u8ff7\u5931\u65b9\u5411\u7684\uff01 \u3000\u3000\u4e5d\u4e2a\u4eba\u4e2d\u4ecd\u7531\u949f\u4e00\u8c6a\u8d70\u5728\u524d\u9762\uff0c\u521a\u8981\u8fdb\u5165\u8c37\u53e3\uff0c\u4fbf\u6709\u56db\u540d\u4f69\u5200\u7684\u6c49\u5b50\uff0e\u7531\u4e00\u4fa7\u7a9c\u4e86\u51fa \u6765\uff0e\u963b\u4f4f\u4e86\u53bb\u8def\u3002 \u3000\u3000\u8fd9\u56db\u4eba\u5168\u90fd\u7350\u5934\u9f20\u8111\uff0c\u5c16\u5634\u7334\u816e\uff0c\u4e3a\u9996\u7684\u662f\u4e2a\u8eab\u6750\u77ee\u58ee\u7684\u4e2d\u5e74\u6c49\u5b50\uff0e\u540d\u53eb\u6731\u9ad8\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u7b49\u4eba\u90fd\u8ba4\u8bc6\u6731\u9ad8\uff0c\u77e5\u9053\u4ed6\u662f\u7f57\u6d6e\u4e00\u53df\u970d\u65e0\u4f3d\u624b\u4e0b\u55bd\u7f57\u4e2d\u7684\u4e00\u540d\u5fc3\u8179\u5c0f\u5934\u76ee\u3002 \u3000\u3000\u53ea\u542c\u6731\u9ad8\u559d\u9053\uff1a\u201c\u7ad9\u4f4f\uff01\u201d \u3000\u3000\u949f\u4e00\u8c6a\u4e3a\u67e5\u660e\u771f\u76f8\uff0c\u4e0d\u4fbf\u7acb\u5373\u53d1\u4f5c\uff0c\u5f53\u4e0b\u4f9d\u8a00\u505c\u4e0b\u811a\u6b65\uff0c\u5e76\u56de\u5934\u793a\u610f\u4f17\u4eba\u4e5f\u522b\u518d\u7ee7\u7eed\u524d \u8fdb\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u6309\u637a\u4e0d\u4f4f\uff0c\u53eb\u9053\uff1a\u201c\u4ec0\u4e48\uff0a\uff0a\uff0a\u7ad9\u4f4f\uff0c\u5148\u5bb0\u4e86\u8fd9\u738b\u516b\u86cb\u518d\u8bf4\uff01\u201d \u3000\u3000\u949f\u4e00\u8c6a\u62c5\u5fc3\u9ea6\u5c0f\u660e\u592a\u8fc7\u83bd\u649e\u574f\u4e8b\uff0c\u5fd9\u9053\uff1a\u201c\u9ea6\u5c0f\u5144\u5f1f\u8bf7\u5fcd\u4e00\u65f6\u4e4b\u6c14\uff0c\u8fd9\u4e8b\u8fd8\u662f\u6682\u65f6\u4ea4\u7ed9 \u6211\u6765\u5904\u7406\u8f83\u597d\u3002\u201d \u5367\u9f99\u751f\u300a\u5999\u7edd\u5929\u9999\u300b \u7b2c \u4e8c \u56de\u3000\u6b66\u4e89\u76df\u4e3b \u3000\u3000\u5728\u8fd9\u540c\u65f6\uff0c\u4f59\u4ea6\u4e50\u4e5f\u793a\u610f\u9ea6\u5c0f\u660e\u8981\u4fdd\u6301\u51b7\u9759\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u53ea\u597d\u6682\u65f6\u9690\u5fcd\u4e0b\u6765\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u4e0a\u524d\u4e24\u6b65\uff0c\u8bed\u6c14\u5e73\u548c\u7684\u9053\uff1a\u201c\u6731\u9ad8\uff0c\u4f60\u8981\u8c01\u7ad9\u4f4f\uff1f\u201d \u3000\u3000\u6731\u9ad8\u659c\u7784\u4e86\u4e00\u773c\u9053\uff1a\u201c\u81ea\u7136\u662f\u8981\u4f60\u4eec\u7ad9\u4f4f\uff01\u201d \u3000\u3000\u949f\u4e00\u8c6a\u4ecd\u672a\u53d1\u4f5c\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u8be5\u8ba4\u8bc6\u6211\u662f\u8c01\uff1f\u201d \u3000\u3000\u6731\u9ad8\u9053\uff1a\u201c\u5728\u4e0b\u5f53\u7136\u8ba4\u8bc6\u4f60\u662f\u4ece\u524d\u7684\u949f\u526f\u76df\u4e3b\u3002\u201d \u3000\u3000\u201c\u4f60\u65e2\u7136\u77e5\u9053\u6211\u662f\u81ea\u5df1\u4eba\uff0c\u5c31\u4e0d\u8be5\u963b\u4f4f\u53bb\u8def\u3002\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u4ece\u524d\u4f60\u4eec\u662f\u81ea\u5df1\u4eba\uff0c\u53ef\u60dc\u73b0\u5728\u5df2\u7ecf\u4e0d\u662f\u4e86\u3002\u201d \u3000\u3000\u201c\u4f60\u51ed\u4ec0\u4e48\u8bf4\u6211\u4eec\u4e0d\u662f\u81ea\u5df1\u4eba\uff1f\u201d \u3000\u3000\u201c\u8fd9\u662f\u65b0\u4efb\u76df\u4e3b\u4ea4\u4ee3\u7684\u3002\u201d \u3000\u3000\u201c\u65b0\u76df\u4e3b\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u662f\u4ece\u524d\u7684\u970d\u526f\u76df\u4e3b\u3002\u201d \u3000\u3000\u949f\u4e00\u8c6a\u7b49\u4eba\u7acb\u523b\u660e\u767d\uff0c\u7f57\u6d6e\u4e00\u53df\u970d\u65e0\u4f3d\u5df2\u7ecf\u5148\u4ed6\u4eec\u8d76\u56de\u8c1c\u8e2a\u8c37\uff0c\u679c\u771f\u4e0d\u51fa\u6240\u6599\u3002 \u3000\u3000\u4ed6\u7b11\u4e86\u7b11\u9053\uff1a\u201c\u8fd9\u6837\u8bf4\u4f60\u662f\u5949\u970d\u5143\u4f3d\u4e4b\u547d\uff0c\u4e0d\u51c6\u6211\u4eec\u8fdb\u8c37\u4e86\uff1f\u201d \u3000\u3000\u6731\u9ad8\u51b7\u58f0\u9053\uff1a\u201c\u65e2\u7136\u4f60\u4eec\u5fc3\u7406\u6709\u6570\uff0c\u5c31\u8be5\u65e9\u65e9\u9000\u51fa\u8c37\u53e3\uff01\u201d \u3000\u3000\u949f\u4e00\u8c6a\u9634\u6c89\u4e00\u7b11\u9053\uff1a\u201c\u597d\u8bf4\uff0e\u6211\u60f3\u8981\u4f60\u8fdb\u8c37\u901a\u62a5\u970d\u5143\u4f3d\uff0c\u8981\u4ed6\u4eb2\u81ea\u51fa\u6765\u8fce\u63a5\u3002\u201d \u3000\u3000\u6731\u9ad8\u51b7\u7b11\u9053\uff1a\u201c\u6211\u4eec\u662f\u5949\u547d\u5b88\u8c37\u53e3\u7684\uff0c\u6ca1\u6709\u76df\u4e3b\u7684\u4ee4\u8c15\uff0c\u8c01\u90fd\u4e0d\u80fd\u64c5\u79bb\u5c97\u4f4d\uff01\u201d \u3000\u3000\u6b64\u523b\u949f\u2014\u8c6a\u8eab\u540e\u4f17\u4eba\uff0c\u65e9\u5df2\u6012\u706b\u76f4\u51b2\uff0c\u5c24\u5176\u662f\u9ea6\u5c0f\u660e\uff0c\u66f4\u662f\u6c14\u5f97\u76ee\u9f87\u6b32\u88c2\uff0c\u60c5\u4e0d\u81ea\u7981\u51b2 \u4e0a\u524d\uff0c\u63a2\u81c2\u628a\u949f\u4e00\u8c6a\u62e8\u5411\u4e00\u8fb9\u9053\uff1a\u201c\u949f\u4e00\u8c6a\uff0e\u4f60\u8fd8\u5a46\u5a46\u5988\u5988\u7684\u8ddf\u8fd9\u738b\u516b\u86cb\u7f57\u55e6\u4ec0\u4e48\uff0e\u5148\u5bb0\u4e86 \u4ed6\u8fc7\u8fc7\u9690\u518d\u8bf4\uff01\u201d \u3000\u3000\u8bf4\u8bdd\u95f4\uff0e\u957f\u5251\u65e9\u5df2\u51fa\u9798\uff0c\u9ea6\u5c0f\u660e\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u72d7\u5a18\u517b\u7684\uff0c\u521a\u624d\u8bf4\u4ec0\u4e48\uff1f\u518d\u8bf4\u2014\u904d\u7ed9\u6211\u542c \u542c\uff01\u201d \u3000\u3000\u6731\u9ad8\u4e0d\u77e5\u9ea6\u5c0f\u660e\u7684\u5389\u5bb3\uff0e\u4e24\u773c\u4e00\u77aa\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u5b50\u9a82\u8c01\u738b\u516b\u86cb\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4e0d\u4f46\u4f60\u662f\u738b\u516b\u86cb\uff0e\u8fde\u4f60\u7956\u5b97\u4e5f\u662f\u738b\u516b\u86cb\uff01\u4f60\u6562\u4e0d\u670d\u6c14\uff01\u201d \u3000\u3000\u6731\u9ad8\u6b63\u6b32\u6325\u52a8\u624b\u4e2d\u7684\u539a\u80cc\u5934\u5200\uff0c\u53ea\u89c1\u5bd2\u5149\u2014\u63a0\uff0c\u95ea\u7535\u822c\u76f4\u5411\u524d\u80f8\u523a\u51fa\uff0e\u8fde\u53eb\u90fd\u4e0d\u66fe\u53eb \u51fa\uff0e\u4fbf\u88ab\u9ea6\u5c0f\u660e\u523a\u4e2a\u524d\u540e\u7686\u901a\u3002 \u3000\u3000\u53e6\u4e09\u540d\u55bd\u7f57\uff0c\u89c1\u9ea6\u5c0f\u660e\u51fa\u624b\u5982\u6b64\u8f9b\u8fa3\u72e0\u6bd2\uff0c\u7acb\u5373\u62d4\u817f\u5411\u540e\u72c2\u5954\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u5c82\u80af\u8ba9\u4ed6\u4eec\u9003\u8131\uff0c\u53cc\u80a9\u4e00\u6643\uff0e\u4eba\u5df2\u817e\u7a7a\u800c\u8d77\u3002 \u3000\u3000\u4eba\u5728\u7a7a\u4e2d\uff0e\u5b9d\u5251\u94f6\u8292\u6d41\u52a8\uff0c\u4f46\u95fb\u201c\u535f\u3001\u535f\u3001\u535f\u201d\u7684\u4e09\u58f0\u95f7\u54cd\uff0c\u4e09\u9897\u4eba\u5934\uff0c\u5df2\u50cf\u5207\u897f\u74dc\u822c \u7684\u6eda\u4e0b\u5730\u6765\u3002 \u3000\u3000\u4ed6\u4e00\u53e3\u6c14\u8fde\u6740\u56db\u4eba\uff0c\u4ec5\u662f\u7728\u773c\u95f4\u7684\u5de5\u592b\uff0c\u770b\u5f97\u4e0d\u5c11\u4eba\u90fd\u9707\u9a87\u4e0d\u5df2\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u8fd8\u5251\u5165\u9798\uff0c\u563f\u563f\u7b11\u9053\uff1a\u201c\u8fd9\u56db\u4e2a\u732b\u5934\u72d7\u8033\uff0e\u5c45\u7136\u8fd8\u6562\u5728\u54b1\u4eec\u9762\u524d\u901e\u5f3a\uff0c\u771f\u4ed6\u5988\u7684 \u662f\u54b8\u9c7c\u653e\u751f\u2014\uff0d\uff0d\u4e0d\u77e5\u6b7b\u6d3b\uff01\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u9053\uff1a\u201c\u5c0f\u660e\uff0c\u4f60\u603b\u8be5\u7559\u4ed6\u4eec\u4e00\u4e2a\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u7559\u4e00\u4e2a\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u9053\uff1a\u201c\u7559\u4e00\u4e2a\u8ba9\u4ed6\u56de\u53bb\u62a5\u4fe1\uff0c\u5411\u970d\u5143\u4f3d\u62a5\u4fe1\uff0c\u4e5f\u8bb8\u970d\u5143\u4f3d\u771f\u4f1a\u51fa\u6765\u8fce\u63a5\u54b1\u4eec\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u54fc\u4e86\u58f0\u9053\uff1a\u201c\u82e5\u8ba9\u4ed6\u4eec\u56de\u53bb\u62a5\u4fe1\u3001\u970d\u5143\u4f3d\u53cd\u800c\u6709\u5907\uff0c\u54b1\u4eec\u73b0\u5728\u51b2\u8fdb\u53bb\uff0e\u6b63\u53ef\u6740\u4ed6 \u4e2a\u63aa\u624b\u4e0d\u53ca\u3002\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u6447\u5934\u9053\uff1a\u201c\u8fd9\u6761\u8c37\u9053\uff0c\u6bcf\u9694\u51e0\u5341\u6b65\u4fbf\u6709\u4e00\u5904\u5173\u5361\uff0c\u54b1\u4eec\u5373\u4f7f\u51b2\u8fdb\u53bb\uff0c\u970d\u5143\u4f3d\u7167\u6837 \u4e5f\u4f1a\u65e9\u6709\u51c6\u5907\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u54b1\u4eec\u8fc7\u4e00\u9053\u5173\u5361\uff0e\u4fbf\u6740\u4ed6\u4eec\u51e0\u4e2a\uff0c\u4e00\u76f4\u6740\u5230\u603b\u5be8\uff0e\u81f3\u5c11\u53ef\u4ee5\u524a\u5f31\u970d\u5143\u4f3d\u7684 \u4e0d\u5c11\u5175\u529b\u3002\u201d \u3000\u3000\u5ffd\u95fb\u82d7\u7d20\u5170\u9053\uff1a\u201c\u59dc\u5927\u4fa0\u5fc5\u5b9a\u5148\u54b1\u4eec\u800c\u5230\uff0c\u4e3a\u4ec0\u4e48\u5230\u73b0\u5728\u8fd8\u4e0d\u89c1\u4ed6\u7684\u4eba\u5f71\uff1f\u201d \u3000\u3000\u5979\u56e0\u662f\u81ea\u5df1\u8ba9\u51fa\u4e91\u9f99\u59dc\u5b8f\u5148\u56de\u8ff7\u8e2a\u8c37\uff0c\u8d1f\u8d23\u63a5\u5e94\uff0c\u96be\u514d\u5fc3\u91cc\u7740\u6025\uff0e\u4e07\u4e00\u59dc\u5b8f\u51fa\u4e86\u5dee\u9519\uff0c \u7b49\u4e8e\u5979\u81ea\u5df1\u5bb3\u4e86\u4ed6\uff0c\u662f\u4ee5\u8d77\u4e0a\u987e\u8651\u3002 \u3000\u3000\u4f59\u4ea6\u4e50\u5401\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u770b\u60c5\u5f62\u59dc\u8001\u5927\u5f88\u53ef\u80fd\u51f6\u591a\u5409\u5c11\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u56b7\u9053\uff1a\u201c\u8fd9\u4e9b\u4e8b\u73b0\u5728\u7528\u4e0d\u7740\u8ba8\u8bba\uff0c\u51b2\u8fdb\u603b\u5be8\u89c1\u4e86\u970d\u5143\u4f3d\u90a3\u8001\u738b\u516b\u86cb\u81ea\u7136\u4fbf\u77e5\u5206 \u6653\u3002\u201d \u3000\u3000\u60c5\u52bf\u5df2\u7ecf\u5982\u6b64\uff0c\u949f\u4e00\u8c6a\u7b49\u4eba\u53ea\u6709\u5f00\u59cb\u524d\u8fdb\u3002 \u3000\u3000\u5947\u602a\u7684\u662f\u4e00\u8def\u4e4b\u4e0a\uff0c\u5176\u4f59\u5173\u5361\u5e76\u4e0d\u89c1\u6709\u4eba\u628a\u5b88\u3002 \u3000\u3000\u4f17\u4eba\u6709\u5982\u8fdb\u65e0\u4eba\u4e4b\u5883\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u548c\u4f59\u4ea6\u4e50\u5fc3\u91cc\u6709\u6570\uff0c\u8d8a\u662f\u8fd9\u6837\uff0c\u8d8a\u5fc5\u987b\u63d0\u9ad8\u8b66\u89c9\u3002 \u3000\u3000\u4f59\u4ea6\u4e50\u8fb9\u8d70\u8fb9\u9053\uff1a\u201c\u5c0f\u660e\u5144\u5f1f\uff0e\u6211\u6709\u51e0\u53e5\u8bdd\uff0c\u4e0d\u77e5\u4f60\u542c\u4e0d\u542c\u5f97\u8fdb\u53bb\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u6211\u7684\u8033\u6735\u4e0d\u804b\uff0c\u600e\u4f1a\u542c\u4e0d\u8fdb\u8bdd\u53bb\uff1f\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u9053\uff1a\u201c\u90a3\u5c31\u597d\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u6709\u4e9b\u4e0d\u8010\u9053\uff1b\u201c\u6709\u8bdd\u5c31\u5feb\u8bb2\uff0c\u522b\u803d\u8bef\u65f6\u95f4\uff01\u201d \u3000\u3000\u201c\u6211\u7684\u8bdd\u5f88\u7b80\u5355\uff0c\u53ea\u8981\u6c42\u4f60\u4f9d\u4ece\u4e00\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u201c\u4f60\u8fd9\u4eba\u8d8a\u6765\u8d8a\u7f57\u55e6\uff0c\u8981\u8bf4\u5c31\u8bf4\uff0c\u4f55\u5fc5\u7a77\u62d6\uff01\u201d \u3000\u3000\u201c\u597d\uff0c\u6211\u73b0\u5728\u5c31\u8bf4\uff0c\u5f85\u4f1a\u513f\u89c1\u4e86\u970d\u5143\u4f3d\uff0e\u4f60\u6700\u597d\u6682\u65f6\u522b\u5f00\u53e3\uff0c\u5148\u7531\u949f\u5144\u548c\u6211\u6765\u5e94\u4ed8\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff0c\u53ea\u51c6\u4f60\u4eec\u8bb2\u8bdd\uff0e\u5374\u4e0d\u51c6\u6211\u5f00\u53e3\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u4f60\u7684\u6027\u5b50\u6700\u6025\u8e81\uff0c\u5f88\u53ef\u80fd\u4e00\u5f00\u53e3\u4fbf\u574f\u4e86\u5927\u4e8b\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u4e00\u5411\u5bf9\u4f59\u4ea6\u4e50\u8f83\u4e3a\u5c0a\u656c\uff0c\u5176\u539f\u56e0\u4e0d\u5916\u4f59\u4ea6\u4e50\u662f\u8bfb\u4e66\u4eba\uff0c\u8db3\u667a\u591a\u8c0b\uff0c\u8fde\u80e1\u67cf\u9f84\u548c\u8c37 \u5bd2\u9999\u90fd\u5bf9\u4ed6\u5341\u5206\u793c\u9047\uff0c\u81ea\u5df1\u5f53\u7136\u4e5f\u8981\u656c\u4ed6\u4e09\u5206\u3002 \u3000\u3000\u5f53\u4e0b\uff0c\u7565\u4e00\u6c89\u5fd6\u9053\uff1a\u201c\u597d\u5427\uff01\u6211\u542c\u4f60\u7684\uff0c\u4f46\u5374\u4e0d\u80fd\u8981\u6211\u4e00\u76f4\u4e0d\u5f00\u53e3\u3002\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u9053\uff1a\u201c\u90a3\u662f\u81ea\u7136\uff0c\u5230\u65f6\u95f4\u6211\u4f1a\u793a\u610f\uff0e\u8981\u4f60\u600e\u6837\u505a\u7684\u3002\u201d \u3000\u3000\u53c8\u884c\u4e86\u4e94\u516d\u91cc\uff0e\u79bb\u603b\u5be8\u5df2\u4e0d\u8fc7\u6570\u7bad\u4e4b\u9065\uff0c\u4f9d\u7136\u4e0d\u89c1\u534a\u4e2a\u4eba\u5f71\u3002 \u3000\u3000\u5feb\u5230\u8fbe\u603b\u5be8\u5916\u7684\u5e7f\u573a\u65f6\uff0c\u53ea\u89c1\u805a\u8bae\u5385\u5927\u95e8\u524d\uff0c\u5df2\u6392\u5217\u4e86\u5341\u51e0\u4e2a\u4eba\u3002 \u3000\u3000\u5f53\u5148\u4e00\u4eba\uff0e\u91d1\u7b8d\u675f\u53d1\uff0c\u9762\u5982\u6de1\u91d1\uff0c\u80f8\u5782\u82b1\u767d\u957f\u9aef\uff0c\u5168\u8eab\u7070\u8863\uff0c\u8138\u5f62\u5947\u957f\uff0e\u53cc\u76ee\u66b4\u7a81\uff0c\u4e24 \u592a\u9633\u7a74\u9ad8\u9ad8\u9686\u8d77\uff0c\u8170\u7d27\u4e00\u6761\u901a\u4f53\u4e4c\u9ed1\u7684\u86c7\u5934\u8f6f\u97ad\u3002 \u3000\u3000\u6b63\u662f\u751f\u6027\u66b4\u5389\uff0e\u6076\u6bd2\u9634\u9669\uff0c\u51f6\u540d\u662d\u5f70\u7684\u7f57\u6d6e\u4e00\u53df\u970d\u5143\u4f3d\u3002 \u3000\u3000\u5728\u970d\u5143\u4f3d\u8eab\u65c1\uff0c\u5206\u7ad9\u7740\u5cad\u5357\u4e8c\u5947\uff0c\u5de6\u8fb9\u662f\u8eab\u7740\u5929\u84dd\u957f\u886b\uff0c\u9e70\u9f3b\u9e5e\u773c\u7684\u641c\u9b42\u624b\u5df4\u5929\u4e49\uff1b\u53f3 \u8fb9\u662f\u5fc3\u5730\u9669\u72e0\u3001\u751f\u6027\u6b8b\u66b4\u7684\u62d8\u9b44\u7d22\u5b8b\u5929\u94ce\u3002 \u3000\u3000\u518d\u4e24\u65c1\u4fbf\u662f\u6c5f\u5357\u56db\u602a\u7684\u5c71\u9b48\u6bdb\u8d8a\u3001\u6c34\u9b3c\u6d77\u767b\u3001\u5730\u864e\u5305\u901a\u3001\u6797\u9b54\u82d7\u5f3a\u3002 \u3000\u3000\u53e6\u5916\u51e0\u540d\uff0e\u4e5f\u90fd\u662f\u6b66\u529f\u4e0d\u5f31\u7684\u970d\u5143\u4f3d\u5fc3\u8179\u624b\u4e0b\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u4ecd\u7136\u8d70\u5728\u6700\u524d\uff0e\u8fd8\u6ca1\u7b49\u4ed6\u5f00\u53e3\uff0c\u970d\u5143\u4f3d\u4fbf\u5df2\u8d8a\u524d\u51e0\u6b65\uff0c\u62b1\u62f3\u9ad8\u58f0\u9053\uff1a\u201c\u8bf8\u4f4d\u8fd4\u56de \u8ff7\u8e2a\u8c37\uff0c\u8001\u592b\u672a\u66fe\u8fdc\u8fce\uff0c\u5931\u793c\uff01\u5931\u793c\uff01\u201d \u3000\u3000\u949f\u4e00\u8c6a\u5728\u672a\u5f04\u6e05\u5bf9\u65b9\u846b\u82a6\u91cc\u5356\u7684\u662f\u4ec0\u4e48\u836f\u4e4b\u524d\uff0c\u53ea\u597d\u4e5f\u53cc\u624b\u4e00\u62f1\u9053\uff1a\u201c\u4e0d\u6562\u5f53\uff0c\u949f\u67d0\u7b49 \u4eba\u6765\u8fdf\u4e00\u6b65\uff0c\u8fd8\u671b\u970d\u5144\u6d77\u51fd\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u7acb\u523b\u6446\u51fa\u4fa7\u8eab\u8083\u5ba2\u59ff\u6001\uff0c\u62ac\u624b\u5f80\u5927\u95e8\u5185\u4e00\u6307\u9053\uff1a\u201c\u9152\u5bb4\u5df2\u5728\u805a\u4e49\u5385\u6446\u597d\uff0c\u949f\u5144\u548c \u8bf8\u4f4d\u5feb\u8bf7\u5165\u5e2d\uff0c\u8001\u592b\u4e3a\u8bf8\u4f4d\u63a5\u98ce\u6d17\u5c18\u3002\u201d \u3000\u3000\u949f\u4e00\u8c6a\u7b49\u4e5d\u4eba\u65e9\u5df2\u6697\u4e2d\u6212\u5907\uff0c\u9c7c\u8d2f\u8fdb\u5165\u5927\u5385\u3002 \u3000\u3000\u5927\u5385\u5185\uff0e\u679c\u7136\u5df2\u6446\u597d\u4e09\u684c\u9152\u5e2d\u3002 \u3000\u3000\u9152\u83dc\u8fd8\u70ed\u6c14\u817e\u817e\uff0c\u663e\u7136\u662f\u521a\u7aef\u4e0a\u6765\u4e0d\u4e45\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u7b49\u4eba\u5e76\u672a\u7acb\u5373\u5165\u5e2d\uff0c\u5374\u5728\u5927\u5385\u4e00\u65c1\u7684\u5ea7\u4f4d\u4e0a\u5750\u3002 \u3000\u3000\u8c01\u90fd\u660e\u767d\uff0c\u8fd9\u662f\u4e00\u573a\u9e3f\u95e8\u5bb4\u4f3c\u7684\u805a\u4f1a\uff0c\u66f4\u4f55\u51b5\u9152\u83dc\u662f\u5426\u201c\u5e72\u51c0\u201d\uff0c\u4e5f\u96be\u4ee5\u9884\u6599\u3002 \u3000\u3000\u970d\u5143\u4f3d\u563f\u563f\u8fde\u7b11\u51e0\u58f0\u9053\uff1a\u201c\u8bf8\u4f4d\u8fdc\u9014\u8dcb\u6d89\uff0c\u4e00\u5b9a\u65e9\u5c31\u997f\u4e86\uff0c\u5c31\u8be5\u65e9\u4e9b\u5165\u5e2d\u3002\u201d \u3000\u3000\u8fd9\u8001\u9b54\u5934\u8bf4\u7684\u4e0d\u5047\uff0e\u949f\u4e00\u8c6a\u7b49\u4eba\u786e\u662f\u65e9\u5df2\u9965\u80a0\u8f98\u8f98\uff0c\u770b\u4e86\u684c\u4e0a\u7684\u4f73\u917f\u7f8e\u9994\uff0e\u96be\u514d\u998b\u6d8e\u6b32 \u6ef4\u3002 \u3000\u3000\u4f46\u6b64\u65f6\u6b64\u5730\uff0c\u5374\u53c8\u7edd\u4e0d\u80fd\u8f7b\u6613\u5165\u5e2d\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u6545\u610f\u4e0d\u63d0\u9152\u5bb4\u4e4b\u4e8b\uff0c\u5374\u53c8\u62f1\u624b\u9053\uff1a\u201c\u606d\u559c\u970d\u5144\uff01\u606d\u559c\u970d\u5144\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u8138\u8272\u5fae\u5fae\u2014\u53d8\u9053\uff1a\u201c\u4f46\u4e0d\u77e5\u8001\u592b\u559c\u4ece\u4f55\u6765\uff1f\u201d \u3000\u3000\u949f\u4e00\u8c6a\u5fc3\u77e5\u82e5\u65f6\u95f4\u803d\u8bef\u8fc7\u4e45\u3001\u5fc5\u5b9a\u5bf9\u5df1\u65b9\u4e0d\u5229\uff0c\u4e0d\u52a8\u58f0\u8272\uff0c\u4e00\u7b11\u9053\uff1a\u201c\u65b9\u624d\u5144\u5f1f\u5728\u8c37 \u5916\uff0c\u542c\u4eba\u8bf4\u970d\u5144\u5df2\u8eab\u4efb\u7eff\u6797\u76df\u4e3b\u5927\u4f4d\uff0c\u96be\u9053\u8fd8\u4e0d\u503c\u5f97\u949f\u67d0\u4e3a\u970d\u5144\u5e86\u8d3a\u3002\u201d \u3000\u3000\u987f\u65f6\uff0c\u5927\u5385\u5185\u53d8\u5f97\u9e26\u96c0\u65e0\u58f0\u3002 \u3000\u3000\u53cc\u65b9\u6240\u6709\u7684\u4eba\u5fc3\u7406\u90fd\u6709\u6570\uff0c\u8fd9\u662f\u6700\u7d27\u8981\u7684\u5173\u952e\u65f6\u523b\uff0c\u970d\u5143\u4f3d\u548c\u949f\u4e00\u8c6a\u4e4b\u95f4\u82e5\u4e00\u8a00\u4e0d\u5408\uff0c \u4e00\u573a\u60e8\u70c8\u7684\u706b\u62fc\u4fbf\u7acb\u523b\u53d1\u751f\u3002 \u3000\u3000\u970d\u5143\u4f3d\u4e0d\u6127\u662f\u4f4d\u8001\u5978\u5de8\u733e\u7684\u9b54\u5934\uff0c\u8138\u8272\u77ac\u606f\u53d8\u4e86\u51e0\u53d8\uff0c\u63a5\u7740\u4ef0\u9762\u6253\u4e2a\u54c8\u54c8\u9053\uff1a\u201c\u949f\u5144\uff0c \u5e38\u8a00\u9053\u5f97\u597d\u2018\u5929\u4e0d\u53ef\u4e00\u65e5\u65e0\u65e5\uff0e\u56fd\u4e0d\u53ef\u4e00\u65e5\u65e0\u541b\u2019\uff0e\u54b1\u4eec\u8fd9\u8ff7\u8e2a\u8c37\u7eff\u6797\u603b\u5be8\uff0c\u5f53\u7136\u4e5f\u4e0d\u80fd\u2014 \u5929\u6ca1\u4eba\u4e3b\u6301\u3002 \u3000\u3000\u8001\u592b\u6bd4\u949f\u5144\u65e9\u56de\u6765\u51e0\u5929\uff0c\u4e49\u4e0d\u5bb9\u8f9e\u7684\u8981\u6682\u4ee3\u76df\u4e3b\u4e4b\u4f4d\uff0c\u5982\u4eca\u949f\u5144\u5df2\u7ecf\u56de\u6765\uff0c\u4e8b\u60c5\u5c31\u53e6\u5f53 \u522b\u8bba\u4e86\u3002\u201d \u3000\u3000\u8fd9\u51e0\u53e5\u8bdd\uff0c\u8bf4\u5f97\u9887\u4e3a\u51a0\u5195\u5802\u7687\u3002 \u3000\u3000\u4f7f\u5f97\u949f\u4e00\u8c6a\u7b49\u4eba\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u5927\u6709\u83ab\u6d4b\u9ad8\u6df1\u4e4b\u611f\u3002 \u3000\u3000\u5927\u5385\u5185\u6c89\u5bc2\u4e86\u5f88\u4e45\uff0c\u624d\u542c\u4f59\u4ea6\u4e50\u9053\uff1a\u201c\u7167\u970d\u526f\u76df\u4e3b\u65b9\u624d\u8bdd\u4e2d\u4e4b\u610f\uff0e\u662f\u5426\u5df2\u51c6\u5907\u62e5\u7acb\u949f\u526f \u76df\u4e3b\u7ee7\u4efb\uff1f\u201d \u3000\u3000\u8fd9\u51e0\u53e5\u8bdd\uff0e\u7b49\u4e8e\u662f\u5148\u53d1\u5236\u4eba\uff0c\u4f59\u4ea6\u4e50\u6240\u4ee5\u62a2\u5148\u5f00\u53e3\uff0c\u4e0d\u5916\u662f\u89c9\u5f97\u949f\u4e00\u8c6a\u662f\u5f53\u4e8b\u4eba\uff0e\u82e5\u4ed6 \u81ea\u5df1\u51fa\u9762\uff0c\u96be\u514d\u6709\u5931\u98ce\u5ea6\uff0c\u5fc5\u987b\u6709\u4eba\u4ee3\u4e3a\u53d1\u8a00\uff0e\u624d\u53ef\u8d5a\u5230\u4fbf\u5b9c\u3002 \u3000\u3000\u4f59\u4ea6\u4e50\u8bb2\u8fc7\u8bdd\u540e\uff0c\u5927\u5385\u5185\u6240\u6709\u4eba\u7684\u76ee\u5149\uff0c\u90fd\u4e0d\u7ea6\u800c\u540c\u96c6\u4e2d\u5728\u970d\u5143\u4f3d\u8138\u4e0a\u3002 \u3000\u3000\u8c01\u77e5\u970d\u5143\u4f3d\u5374\u53ea\u662f\u7b11\u58f0\u8fde\u8fde\uff0c\u4f3c\u4e4e\u5bf9\u4f59\u4ea6\u4e50\u65b9\u624d\u7684\u8bdd\uff0c\u5e76\u672a\u542c\u8fc7\u4e00\u822c\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u7d27\u9760\u4f59\u4ea6\u4e50\u800c\u5750\u7684\u9ea6\u5c0f\u660e\uff0c\u5374\u5df2\u6709\u4e9b\u6c89\u4e0d\u4f4f\u6c14\uff0c\u8f7b\u8f7b\u7528\u8098\u89e6\u4e86\u4f59\u4ea6\u4e50\u4e00\u4e0b\uff0e\u4f4e\u58f0 \u9053\uff1a\u201c\u4f60\u600e\u4e48\u8fd9\u6837\u95ee\u90a3\u8001\u738b\u516b\u86cb\uff1f\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u4e0d\u89e3\u9ea6\u5c0f\u660e\u7528\u610f\uff0e\u4e5f\u4f4e\u58f0\u9053\uff1a\u201c\u6211\u8fd9\u6837\u95ee\u4ed6\u6709\u4ec0\u4e48\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u54b1\u4eec\u5728\u8def\u4e0a\u5df2\u7ecf\u8bb2\u597d\uff0e\u5c06\u6765\u7eff\u6797\u76df\u4e3b\u7531\u6211\u6765\u5e72\uff0e\u82e5\u90a3\u8001\u738b\u516b\u86cb\u7b54\u5e94\u8ba9\u7ed9\u949f \u4e00\u8c6a\uff0c\u6211\u8fd8\u600e\u597d\u610f\u601d\u518d\u8ddf\u4ed6\u4e89\uff1f\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u4e0d\u7981\u54d1\u7136\u5931\u7b11\u9053\uff1a\u201c\u4f60\u6700\u597d\u5148\u522b\u63d0\u610f\u89c1\uff0c\u5230\u65f6\u5019\u53ea\u6709\u4f60\u4e50\u7684\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u5982\u679c\u5230\u65f6\u5019\u6211\u4e0d\u597d\u610f\u601d\u548c\u949f\u4e00\u8c6a\u8bb2\uff0e\u4f60\u5fc5\u987b\u4ee3\u6211\u8ddf\u4ed6\u8bb2\uff0c\u53ea\u8981\u4f60\u7b54\u5e94\u8fd9\u6761 \u4ef6\uff0c\u6211\u5c31\u6682\u65f6\u4e0d\u5f00\u53e3\u3002\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u6709\u4e9b\u557c\u7b11\u7686\u975e\u7684\u9053\uff1a\u201c\u597d\u5427\uff01\u6211\u4e00\u5b9a\u4e0d\u8ba9\u4f60\u5403\u4e8f\u3002\u201d \u3000\u3000\u4e24\u4eba\u8fd9\u4e00\u9635\u8c08\u8bdd\uff0e\u56e0\u58f0\u97f3\u4f4e\uff0e\u53c8\u88ab\u970d\u5143\u4f3d\u7684\u7b11\u58f0\u63a9\u4f4f\uff0c\u662f\u4ee5\u5176\u4ed6\u7684\u4eba\u53ea\u77e5\u4ed6\u4eec\u5728\u8bf4\u8bdd\uff0c \u5374\u5e76\u672a\u542c\u6e05\u7a76\u7adf\u8bf4\u4e86\u4e9b\u4ec0\u4e48\u3002 \u3000\u3000\u4f59\u4ea6\u4e50\u5fcd\u4e0d\u4f4f\u63d0\u9ad8\u58f0\u97f3\u9053\uff1a\u201c\u970d\u526f\u76df\u4e3b\u4e3a\u4ec0\u4e48\u4e0d\u56de\u7b54\u5728\u4e0b\u7684\u8bdd\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u4f9d\u7136\u5475\u5475\u800c\u7b11\u9053\uff1a\u201c\u4f59\u5144\u7684\u8bdd\uff0c\u8001\u592b\u867d\u7136\u542c\u5230\uff0c\u53ef\u60dc\u6ca1\u529e\u6cd5\u56de\u7b54\u3002\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u9053\uff1a\u201c\u4f59\u67d0\u4e0d\u89e3\u970d\u526f\u76df\u4e3b\u8bdd\u4e2d\u4e4b\u610f\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u56de\u987e\u4e86\u4e8c\u5947\uff0e\u56db\u602a\u7b49\u5341\u4f59\u4eba\u4e00\u773c\u9053\uff1a\u201c\u8001\u592b\u867d\u662f\u5f53\u4e8b\u4eba\uff0c\u4f46\u82e5\u64c5\u81ea\u4f5c\u4e3b\uff0e\u5bf9\u4ed6\u4eec \u5374\u603b\u662f\u4e00\u79cd\u65e0\u6cd5\u4ea4\u4ee3\u7684\u4e8b\u3002\u201d \u3000\u3000\u201c\u970d\u526f\u76df\u4e3b\u53ef\u5426\u518d\u8bf4\u660e\u767d\u4e9b\uff1f\u201d \u3000\u3000\u201c\u7531\u8c01\u6765\u7ee7\u4efb\u76df\u4e3b\uff0c\u5fc5\u987b\u8bf8\u4f4d\u516c\u51b3\uff0c\u5e76\u4f5c\u8001\u592b\u4e00\u4eba\u6240\u53ef\u4f5c\u4e3b\uff0c\u4f59\u5144\u4e3a\u4ec0\u4e48\u4e0d\u5148\u5f81\u6c42\u5404\u4f4d \u7684\u610f\u89c1\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u5978\u733e\u65e0\u6bd4\uff0c\u5728\u573a\u7684\u5c5e\u4e8e\u4ed6\u7684\u5fc3\u8179\u624b\u4e0b\uff0c\u81f3\u5c11\u6709\u5341\u51e0\u4eba\u4e4b\u591a\uff0c\u800c\u949f\u4e00\u8c6a\u7b49\u4e00\u65b9\uff0c\u5374 \u53ea\u6709\u4e5d\u4eba\uff0c\u82e5\u4ed8\u8bf8\u516c\u610f\u8868\u51b3\uff0c\u8c01\u5403\u4e8f\u5360\u4fbf\u5b9c\u81ea\u662f\u4e0d\u96be\u9884\u89c1\u3002 \u3000\u3000\u53ea\u89c1\u5cad\u5357\u4e8c\u5947\u4e2d\u7684\u641c\u9b42\u624b\u5df4\u5929\u4e49\u9996\u5148\u54cd\u5e94\u9053\uff1a\u201c\u970d\u76df\u4e3b\u8bf4\u5f97\u5bf9\uff0c\u7531\u8c01\u6765\u7ee7\u4efb\u76df\u4e3b\uff0c\u5e94\u8be5 \u7531\u5728\u573a\u7684\u6240\u6709\u4eba\u516c\u610f\u63a8\u4e3e\u624d\u7b97\u516c\u5e73\u3002\u201d \u3000\u3000\u5df4\u5929\u4e49\u5c45\u7136\u516c\u7136\u79f0\u970d\u5143\u4f3d\u4e3a\u970d\u76df\u4e3b\uff0e\u600e\u4e0d\u4ee4\u949f\u4e00\u8c6a\u65b9\u9762\u7684\u7fa4\u8c6a\u8d8a\u53d1\u6c14\u6124\u96be\u5f53\u3002 \u3000\u3000\u4f59\u4ea6\u4e50\u4e0d\u75be\u4e0d\u5f90\u7684\u9053\uff1a\u201c\u970d\u526f\u76df\u4e3b\u548c\u5df4\u5f53\u5bb6\u7684\u53ef\u8ba4\u4e3a\u8fd9\u6837\u516c\u5e73\u4e48\uff1f\u201d \u3000\u3000\u5df4\u5929\u4e49\u9053\uff1a\u201c\u8fd9\u6837\u5f53\u7136\u6700\u516c\u5e73\u4e0d\u8fc7\uff0e\u96be\u9053\u4eba\u5bb6\u7684\u610f\u89c1\uff0c\u8fd8\u8d76\u4e0d\u4e0a\u4e00\u4e2a\u4eba\u7684\u610f\u89c1\uff1f\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u51b7\u7b11\u9053\uff1a\u201c\u5728\u4e0b\u60f3\u8bf7\u5df4\u5f53\u5bb6\u7684\uff0e\u6570\u6570\u5f7c\u6b64\u90a3\u65b9\u9762\u4eba\u591a\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u8fde\u5fd9\u563f\u563f\u7b11\u9053\uff1a\u201c\u4f59\u5144\u8fd9\u6837\u8bf4\u8bdd\u5c31\u4e0d\u5bf9\u5566\uff01\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u9053\uff1a\u201c\u970d\u526f\u76df\u4e3b\u8ba4\u4e3a\u6709\u4ec0\u4e48\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u54b1\u4eec\u672c\u662f\u4e00\u5bb6\u4eba\uff0c\u80e1\u76df\u4e3b\u548c\u8c37\u76df\u4e3b\u5728\u4e16\u65f6\uff0c\u54b1\u4eec\u540c\u662f\u4ed6\u4e8c\u4eba\u7684\u5c5e\u4e0b\uff0c\u82e5\u4f59 \u5144\u786c\u8981\u5206\u51fa\u5f7c\u6b64\uff0e\u53ea\u6015\u5c31\u6709\u7834\u574f\u56e2\u7ed3\uff0c\u6311\u62e8\u79bb\u95f4\u4e4b\u5acc\u5427\uff1f\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u4e00\u5411\u53cd\u5e94\u654f\u6377\uff0e\u64c5\u4e8e\u968f\u673a\u5e94\u4fbf\uff0c\u5374\u672a\u6599\u5230\u5bf9\u65b9\u7684\u8a00\u8bed\u5982\u6b64\u7280\u5229\uff0e\u800c\u4e14\u65e0\u61c8\u53ef\u51fb\uff0c \u987f\u4e86\u4e00\u987f\u9053\uff1a\u201c\u970d\u526f\u76df\u4e3b\u8fd9\u51e0\u53e5\u8bdd\u56fa\u7136\u591f\u51a0\u5195\u5802\u7687\uff0e\u4f46\u662f\u4f59\u67d0\u5374\u8ba4\u4e3a\u5927\u5927\u4e0d\u7136\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u9634\u9669\u5730\u7b11\u4e86\u51e0\u58f0\u9053\uff1a\u201c\u4f59\u5144\u8ba4\u4e3a\u8001\u592b\u7684\u8bdd\uff0c\u9519\u5728\u90a3\u91cc\uff1f\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u9053\uff1a\u201c\u970d\u526f\u76df\u4e3b\u548c\u949f\u526f\u76df\u4e3b\u4e00\u5411\u6c34\u706b\u4e0d\u5bb9\uff0c\u8fd9\u662f\u4eba\u6240\u5171\u77e5\u7684\u4e8b\uff0e\u7528\u4e0d\u7740\u5728\u4e0b\u591a\u6240 \u8d39\u8f9e\uff0e\u4ece\u524d\u8ff7\u8e2a\u8c37\u867d\u6709\u82f1\u96c4\u76d6\u4e16\u7684\u80e1\u76df\u4e3b\u548c\u624d\u667a\u51fa\u4f17\u7684\u80e1\u592b\u4eba\u7edf\u8f96\uff0c\u4f46\u5185\u90e8\u5374\u59cb\u7ec8\u672a\u80fd\u6253\u6210 \u4e00\u7247\u3002 \u3000\u3000\u76ee\u524d\u5728\u970d\u526f\u76df\u4e3b\u8eab\u540e\u7684\u8fd9\u822c\u4eba\uff0c\u90a3\u4e00\u4e2a\u4e0d\u662f\u4f60\u7684\u5fc3\u8179\u624b\u4e0b\uff0c\u82e5\u73b0\u5728\u4e3e\u884c\u516c\u51b3\uff0e\u4eba\u5c11\u7684\u4e00 \u65b9\u5f53\u7136\u5403\u4e8f\uff0e\u8fd9\u662f\u4e09\u5c81\u5b69\u7ae5\u90fd\u77e5\u9053\u7684\u4e8b\uff0c\u4f55\u7528\u5728\u4e0b\u591a\u8bf4\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u7b11\u9053\uff1a\u201c\u4f59\u5144\u8bf4\u7684\u679c\u7136\u6709\u7406\uff0e\u65e2\u7136\u5982\u6b64\uff0c\u90a3\u5c31\u5e72\u8106\u628a\u8ff7\u8e2a\u8c37\u7684\u4e0a\u5343\u5f1f\u5144\u5168\u6570\u96c6 \u5408\uff0c\u95ee\u95ee\u4ed6\u4eec\u8be5\u7531\u8c01\u6765\u7ee7\u4efb\u76df\u4e3b\u3002\u4f59\u5144\u5fc5\u5b9a\u77e5\u9053\u8fd9\u4e0a\u5343\u5f1f\u5144\uff0c\u5e76\u4e0d\u5168\u662f\u8001\u592b\u7684\u4eba\u5427\uff1f\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u5f53\u7136\u4e0d\u80af\u540c\u610f\u970d\u5143\u4f3d\u8fd9\u9879\u63d0\u8bae\uff0c\u82e5\u5728\u80e1\u67cf\u9f84\u751f\u524d\uff0c\u970d\u5143\u4f3d\u7684\u5fc3\u8179\u624b\u4e0b\uff0c\u7684\u786e\u6700\u591a \u53ea\u5360\u534a\u6570\uff0e\u7136\u800c\u5728\u80e1\u67cf\u9f84\u6b7b\u540e\uff0c\u8c37\u5bd2\u9999\u4e3a\u4e86\u66ff\u80e1\u67cf\u9f84\u52df\u96c6\u62a5\u4ec7\u7684\u529b\u91cf\uff0c\u5fc5\u987b\u79bb\u8c37\u904d\u8d70\u4e09\u5c71\u4e94 \u5cb3\u3002 \u3000\u3000\u56e0\u4e4b\u5728\u79bb\u8c37\u524d\uff0c\u4fbf\u5df2\u628a\u4e0d\u5c11\u624b\u4e0b\u9063\u6563\uff0c\u504f\u504f\u9063\u6563\u7684\u90fd\u662f\u81ea\u5df1\u7684\u4eba\u3002 \u3000\u3000\u800c\u970d\u5143\u4f3d\u7684\u5fc3\u8179\uff0c\u5374\u5e76\u65e0\u4eba\u79bb\u5f00\u3002\u4ee5\u76ee\u524d\u800c\u8bba\uff0c\u8ff7\u8e2a\u8c37\u5185\uff0c\u80e1\u67cf\u9f84\u7684\u65e7\u90e8\u5c5e\u5df2\u7ecf\u4e0d\u591a\uff0c \u82e5\u9f50\u96c6\u6240\u6709\u7684\u4eba\u63a8\u9009\u7ee7\u4efb\u76df\u4e3b\uff0c\u5b9a\u6bd4\u6b64\u523b\u66f4\u4e3a\u4e0d\u5229\u3002 \u3000\u3000\u60f3\u5230\u8fd9\u91cc\uff0c\u4f59\u4ea6\u4e50\u7acb\u5373\u6de1\u7136\u4e00\u7b11\u9053\uff1a\u201c\u5728\u4e0b\u6709\u53e5\u8bdd\u60f3\u8bf4\uff0c\u8fd8\u8bf7\u970d\u526f\u76df\u4e3b\u5343\u4e07\u522b\u8bf4\u4f59\u67d0\u6709 \u610f\u6e0e\u72af\u3002\u201d \u3000\u3000\u201c\u4f59\u5144\u8bf7\u8bf4\uff0c\u5f7c\u6b64\u662f\u4e00\u5bb6\u4eba\uff0c\u5e94\u8be5\u662f\u65e0\u8bdd\u4e0d\u8c08\u624d\u5bf9\u3002\u201d \u3000\u3000\u201c\u4f59\u67d0\u5e76\u975e\u4e0d\u540c\u610f\u53ec\u96c6\u8c37\u5185\u6240\u6709\u5f1f\u5144\u516c\u51b3\uff0c\u800c\u662f\u89c9\u5f97\u82e5\u5982\u6b64\u505a\uff0c\u53cd\u800c\u5bb9\u6613\u8bef\u4e86\u5927\u4e8b\u3002\u201d \u3000\u3000\u201c\u4f59\u5144\u8bf7\u9053\u5176\u8be6\uff01\u201d \u3000\u3000\u201c\u8ff7\u8e2a\u8c37\u6709\u4e0a\u5343\u4e4b\u4f17\uff0c\u610f\u89c1\u5fc5\u5b9a\u83ab\u8877\u4e00\u662f\uff0c\u4e07\u4e00\u9009\u51fa\u4e2a\u6df7\u86cb\u6765\u7ee7\u4efb\u76df\u4e3b\uff0c\u8bf7\u95ee\u970d\u526f\u76df\u4e3b \u662f\u5426\u5728\u573a\u53cc\u65b9\uff0c\u6bcf\u4e2a\u4eba\u90fd\u987b\u4e0b\u573a\uff0c\u4e00\u4e2a\u4e2a\u7684\u8f83\u91cf\uff1f\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u53c8\u9053\uff1a\u201c\u8fd9\u6b21\u4e0e\u4e09\u5e74\u524d\u970d\u526f\u76df\u4e3b\u548c\u80e1\u524d\u76df\u4e3b\u4e89\u593a\u7eff\u6797\u9886\u5bfc\u6743\u7684\u60c5\u52bf\u5b8c\u5168\u4e0d\u540c\uff0c\u90a3 \u6b21\u7fa4\u9f99\u65e0\u9996\uff0c\u81ea\u7136\u4eba\u4eba\u90fd\u6709\u4e89\u96c4\u79f0\u9738\u4e4b\u5fc3\uff0e\u800c\u73b0\u5728\u7684\u5f62\u52bf\u5374\u5f88\u660e\u663e\u2026\u2026\u201d \u3000\u3000\u970d\u5143\u4f3d\u4e0d\u7b49\u4f59\u4ea6\u4e50\u8bf4\u5b8c\uff0c\u622a\u53e3\u9053\uff1a\u201c\u4f59\u5144\u8ba4\u4e3a\u54ea\u91cc\u4e0d\u540c\uff1f\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u9053\uff1a\u201c\u773c\u4e0b\u5728\u573a\u5404\u4f4d\uff0c\u6beb\u65e0\u7591\u95ee\u662f\u4ee5\u970d\u526f\u76df\u4e3b\u548c\u949f\u526f\u76df\u4e3b\u6b66\u529f\u6700\u9ad8\uff0c\u8eab\u4efd\u5730\u4f4d\u4e5f \u76f8\u7b49\uff0c\u53ea\u8981\u4e24\u4f4d\u5728\u6b66\u529f\u4e0a\u80fd\u5206\u51fa\u80dc\u8d1f\u5c31\u53ef\uff0c\u4f55\u5fc5\u7fa4\u8d77\u5408\u62fc\u4f24\u4e86\u548c\u6c14\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u6e5b\u6e5b\u76ee\u5149\uff0c\u8f6c\u5411\u949f\u2014\u8c6a\u9053\uff1a\u201c\u949f\u5144\u4ee5\u4e3a\u5982\u4f55\uff1f\u201d \u3000\u3000\u949f\u4e00\u8c6a\u6de1\u6de1\u4e00\u7b11\u9053\uff1a\u201c\u65e2\u7136\u975e\u8d70\u8fd9\u4e00\u6b65\u4e0d\u53ef\uff0e\u5728\u4e0b\u4e5f\u53ea\u6709\u5728\u970d\u5144\u53f0\u524d\u6597\u80c6\u9886\u6559\u4e86\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u563f\u563f\u7b11\u9053\uff1a\u201c\u5982\u4f55\u8f83\u91cf\uff0c\u6700\u597d\u5148\u8bf7\u4f59\u5144\u5212\u51fa\u9053\u513f\u6765\uff01\u201d \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u5f7c\u6b64\u672c\u6765\u5df2\u662f\u2014\u5bb6\u4eba\uff0c\u4e3a\u4e86\u514d\u4f24\u548c\u6c14\uff0c\u6700\u597d\u662f\u505a\u4e00\u573a\u4e0d\u5fc5\u6d41\u8840\u6302\u5f69\u7684\u8f83 \u91cf\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u949f\u5144\u8bf7\u518d\u8bf4\u660e\u767d\u4e9b\uff1f\u201d \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u5200\u67aa\u65e0\u773c\uff0c\u62f3\u811a\u65e0\u60c5\u3002\u4e00\u65e6\u52a8\u624b\u62fc\u640f\uff0e\u96be\u514d\u6709\u6240\u4f24\u4ea1\uff0c\u5f7c\u6b64\u5c31\u5728\u8f7b\u529f\u4e0a\u89c1 \u9ad8\u4e0b\u5982\u4f55\uff1f\u201d \u3000\u3000\u970d\u5143\u52a0\u7b11\u9053\uff1a\u201c\u5f88\u597d\uff0c\u5982\u679c\u5728\u8f7b\u529f\u4e0a\u5206\u4e0d\u51fa\u9ad8\u4e0b\uff0c\u53c8\u5f53\u5982\u4f55\uff1f\u201d \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u4e0d\u59a8\u518d\u7ee7\u7eed\u8f83\u91cf\u5176\u4ed6\u65b9\u9762\u7684\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u626b\u89c6\u4e86\u5927\u5385\u4e00\u773c\u9053\uff1a\u201c\u8fd9\u805a\u4e49\u5385\u867d\u7136\u5bbd\u655e\uff0c\u82e5\u65bd\u5c55\u8f7b\u529f\uff0c\u8fd8\u662f\u5acc\u5c0f\u4e86\u4e9b\uff1f\u201d \u3000\u3000\u949f\u4e00\u8c6a\u5f53\u5148\u8d70\u51fa\u5927\u5385\u9053\uff1a\u201c\u4e00\u5207\u6089\u542c\u5c0a\u4fbf\uff0c\u90a3\u5c31\u5230\u5916\u8fb9\u6765\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u968f\u5373\u4e5f\u8ddf\u51fa\u5927\u5385\u4e4b\u5916\u3002 \u3000\u3000\u53cc\u65b9\u4eba\u9a6c\uff0e\u4e5f\u540c\u65f6\u8d77\u5ea7\u8d70\u51fa\u5927\u5385\u3002 \u3000\u3000\u5927\u5385\u5916\uff0c\u662f\u4e00\u7247\u5927\u7ea6\u4ea9\u8bb8\u89c1\u65b9\u7684\u5e7f\u573a\uff0c\u5e7f\u573a\u56db\u5468\uff0c\u6709\u51e0\u682a\u5de8\u5927\u7684\u677e\u67cf\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u4e00\u62b1\u62f3\u9053\uff1a\u201c\u5728\u4e0b\u4e0d\u6562\u5360\u5148\uff0c\u970d\u526f\u76df\u4e3b\u5148\u8bf7\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u5927\u523a\u524c\u7684\u9053\uff1a\u201c\u5982\u6b64\u8001\u592b\u5c31\u5148\u884c\u732e\u4e11\u4e86\uff01\u201d \u3000\u3000\u4ed6\u6700\u540e\u4e00\u4e2a\u5b57\u521a\u521a\u51fa\u53e3\uff0c\u53cc\u80a9\u5fae\u4e00\u6643\u52a8\uff0c\u4eba\u5df2\u817e\u7a7a\u800c\u8d77\u3002 \u3000\u3000\u8863\u8882\u98d8\u98d8\uff0e\u50cf\u53ea\u5de8\u5927\u82cd\u9e70\u822c\uff0c\u4e00\u98de\u51b2\u5929\uff0c\u76f4\u98de\u8d77\u4e03\u516b\u4e08\u9ad8\uff0c\u7136\u540e\u8eab\u8eaf\u7531\u76f4\u5347\u8f6c\u4e3a\u5e73\u5c04\uff0c \u5411\u4e00\u68f5\u4f1e\u5f62\u866c\u677e\u4e0a\u843d\u53bb\u3002 \u3000\u3000\u53ea\u89c1\u4ed6\u5728\u5feb\u63a5\u8fd1\u6811\u9876\u65f6\uff0c\u8eab\u5b50\u5ffd\u7531\u5012\u7acb\u53d8\u4e3a\u76d8\u5750\uff0c\u7adf\u7136\u56db\u5e73\u516b\u7a33\u7684\u5750\u5728\u4e00\u7247\u677e\u9488\u4e4b\u4e0a\u3002 \u800c\u90a3\u677e\u9488\uff0e\u5374\u4ec5\u662f\u5fae\u5fae\u5f2f\u66f2\u4e86\u4e9b\uff0c\u6811\u68a2\u534a\u70b9\u4e0d\u66fe\u6643\u52a8\u3002 \u3000\u3000\u8fd9\u6b63\u662f\u4f20\u8a00\u4e0a\u7684\u201c\u5750\u53f6\u5367\u82b1\u201d\u65e0\u4e0a\u8f7b\u529f\uff0e\u53ea\u770b\u5f97\u5728\u573a\u7684\u4eba\u90fd\u4e0d\u7981\u6697\u6697\u53eb\u7edd\uff0c\u51e0\u4e4e\u8fde\u5927\u6c14 \u4e5f\u65e0\u6cd5\u5598\u51fa\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0e\u970d\u5143\u4f3d\u84e6\u5730\u5f20\u53e3\u55b7\u51fa\u4e00\u7c92\u553e\u73e0\uff0c\u5411\u7a7a\u4e2d\u5c04\u53bb\uff01 \u3000\u3000\u63a5\u7740\u201c\u5427\u201d\u7684\u2014\u58f0\u3002 \u3000\u3000\u5730\u4e0a\u7adf\u591a\u4e86\u4e00\u53ea\u6b7b\u9e1f\u3002 \u3000\u3000\u970d\u5143\u4f3d\u5c82\u6b62\u8868\u6f14\u4e86\u8f7b\u529f\uff0c\u7b49\u4e8e\u52a0\u2014\u756a\u53c8\u663e\u9732\u4e86\u4e00\u624b\u6c14\u529f\u7edd\u6280\u3002 \u3000\u3000\u53ea\u542c\u5f97\u949f\u4e00\u8c6a\u53eb\u9053\uff1a\u201c\u970d\u526f\u76df\u4e3b\u795e\u6280\u83ab\u6d4b\uff0c\u5728\u4e0b\u7b97\u662f\u5f00\u4e86\u4e00\u6b21\u773c\u754c\uff01\u201d \u3000\u3000\u949f\u4e00\u6beb\u867d\u7136\u8bf4\u5f97\u8bed\u6c14\u8f7b\u677e\uff0e\u4f46\u4f59\u4ea6\u4e50\u7b49\u4eba\u5374\u83ab\u4e0d\u4e3a\u4ed6\u5927\u611f\u7d27\u5f20\uff0e \u3000\u3000\u6b63\u5728\u8fd9\u65f6\uff0e\u7a81\u89c1\u4e00\u53ea\u5de8\u5927\u7684\u513f\u9e70\uff0c\u632f\u7ffc\u800c\u6765\u3002 \u3000\u3000\u53ea\u89c1\u949f\u4e00\u8c6a\u731b\u4e00\u5438\u6c14\uff0c\u8eab\u5f62\u7535\u5c04\u822c\u76f4\u5411\u90a3\u5140\u9e70\u63a0\u53bb\u3002 \u3000\u3000\u5f53\u4f17\u4eba\u6b63\u5728\u832b\u7136\u4e0d\u77e5\uff0c\u949f\u4e00\u8c6a\u4e3a\u4f55\u6765\u4e4b\u9645\uff0e\u5374\u89c1\u4ed6\u53cc\u811a\u5df2\u8f7b\u8f7b\u843d\u5728\u90a3\u5140\u9e70\u7684\u4e24\u7ffc\u4e4b\u4e0a\uff0c \u5e76\u51fa\u58f0\u559d\u9053\uff1a\u201c\u5feb\u98de\uff01\u201d \u3000\u3000\u90a3\u5140\u9e70\u5f53\u771f\u5c31\u50cf\u61c2\u5f97\u4eba\u8bed\u822c\uff0e\u8f7d\u7740\u949f\u4e00\u8c6a\u9ad8\u5927\u7684\u8eab\u8eaf\uff0c\u73af\u7ed5\u5e7f\u573a\u76d8\u65cb\u8d77\u6765\uff0c\u8db3\u8db3\u98de\u4e86\u4e8c \u531d\uff01 \u3000\u3000\u949f\u4e00\u8c6a\u624d\u8dc3\u8eab\u800c\u4e0b\uff0e\u843d\u5728\u539f\u6765\u7ad9\u7acb\u4e4b\u5904\u3002 \u3000\u3000\u5f53\u4ed6\u811a\u843d\u5b9e\u5730\uff0c\u90a3\u5140\u9e70\u5c1a\u672a\u98de\u8fdc\u3002 \u3000\u3000\u53ea\u89c1\u4ed6\u201c\u535f\u201d\u7684\u4e00\u58f0\uff0c\u5f20\u53e3\u4e00\u9053\u6c14\u67f1\uff0c\u731b\u5411\u5140\u9e70\u5c04\u53bb\u3002 \u3000\u3000\u90a3\u5140\u9e70\u5728\u4ed6\u55b7\u51fa\u4e00\u53e3\u6c14\u67f1\u4e4b\u540e\uff0c\u987f\u65f6\u8eab\u5b50\u4e00\u504f\uff0c\u9669\u4e9b\u6454\u843d\u5728\u5730\u4e0a\uff0c\u76f4\u4e0b\u6c89\u4e86\u4e24\u4e09\u5c3a\uff0e\u624d \u7a33\u4f4f\u53cc\u7ffc\uff0c\u7ee7\u7eed\u98de\u53bb\u3002 \u3000\u3000\u53cc\u65b9\u8fd9\u2014\u9635\u8f7b\u529f\u8f83\u6280\uff0c\u867d\u7136\u5df2\u5230\u6b64\u7ed3\u675f\uff0c\u4f46\u8c01\u9ad8\u8c01\u4f4e\uff0c\u53cc\u65b9\u89c2\u4f17\u5374\u770b\u6cd5\u4e0d\u2014\uff0e\u5b9e\u5728\u5206\u4e0d \u51fa\u771f\u6b63\u7ed3\u679c\u3002 \u3000\u3000\u970d\u5143\u4f3d\u8868\u793a\u98ce\u5ea6\uff0c\u671b\u7740\u4f59\u4ea6\u4e50\u9053\uff1a\u201c\u65c1\u89c2\u8005\u6e05\uff0e\u5f53\u5c40\u8005\u8ff7\u3002\u4f59\u5144\u7b97\u662f\u65c1\u89c2\u8005\uff0c\u8001\u592b\u4e5f\u6df1 \u4fe1\u4f60\u5904\u4e8b\u516c\u6b63\u516c\u5e73\uff0c\u4f9d\u4f59\u5144\u4e4b\u89c1\uff0c\u65b9\u624d\u8fd9\u4e00\u9635\u8f7b\u529f\u8f83\u6280\uff0c\u4e0d\u77e5\u662f\u5b70\u4f18\u5b70\u52a3\uff1f\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u65e9\u77e5\u970d\u5143\u4f3d\u8001\u5978\u5de8\u6ed1\uff0c\u540c\u65f6\u4e5f\u786e\u5b9e\u96be\u4e0b\u5b9a\u8bba\uff0c\u6de1\u6de1\u2014\u7b11\u9053\uff1a\u201c\u4f9d\u5728\u4e0b\u89c2\u770b\uff0c\u65b9\u624d \u8fd9\u4e00\u9635\u8be5\u662f\u4e2a\u5e73\u5206\u79cb\u8272\u4e4b\u5c40\uff0e\u4e0d\u8fc7\u2018\u2019\u2018\u2019\u2018\u201d \u3000\u3000\u970d\u5143\u4f3d\u7709\u5934\u2014\u76b1\u9053\uff1a\u201c\u4e0d\u8fc7\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u9053\uff1a\u201c\u4e0d\u8fc7\u4f9d\u5728\u4e0b\u6765\u8bf4\uff0c\u4eca\u5929\u5374\u662f\u5927\u5927\u5f00\u4e86\u4e00\u6b21\u773c\u754c\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u5f97\u4e0d\u75be\u4e0d\u5f90\uff0c\u4e00\u6d3e\u4ece\u5bb9\u6a21\u6837\u3002 \u3000\u3000\u970d\u5143\u4f3d\u5374\u6697\u81ea\u9a82\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u5b9e\u5728\u53ef\u6076\uff0c\u8bf4\u8bdd\u8fd8\u5e26\u5927\u5598\u6c14\u7684\uff01\u201d \u3000\u3000\u597d\u5728\u4ed6\u559c\u6012\u4e0d\u5f62\u4e8e\u8272\uff0c\u63a5\u7740\u89c6\u7ebf\u518d\u8f6c\u5411\u949f\u2014\u8c6a\u9053\uff1a\u201c\u949f\u5144\uff0e\u65e2\u7136\u8fd9\u5934\u4e00\u9635\u6ca1\u6709\u7ed3\u679c\uff0c\u54b1 \u4eec\u603b\u4e0d\u80fd\u5c31\u6b64\u7f62\u4f11\u5427\uff1f\u201d \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u90a3\u662f\u81ea\u7136\uff0c\u970d\u526f\u76df\u4e3b\u4e0d\u59a8\u8bf4\u51fa\u4e0b\u2014\u6b65\u8be5\u5982\u4f55\u8fdb\u884c\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u542c\u8bf4\u949f\u5144\u5728\u638c\u3001\u6307\u4e0a\u7684\u9020\u8be3\uff0c\u4e5f\u975e\u5e38\u4eba\u53ef\u6bd4\uff0e\u53ef\u5426\u518d\u6bd4\u4e00\u6bd4\u638c\u3001\u6307\u4e0a\u5de5 \u592b\uff1f\u201d \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff0c\u201c\u6089\u542c\u5c0a\u4fbf\uff0e\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u4e0d\u8fc7\u4e3a\u4e86\u907f\u514d\u4f24\u5bb3\uff0c\u8fd8\u662f\u4e0d\u5fc5\u8fd1\u8eab\u653b\u51fb\u7684\u597d\u3002\u201d \u3000\u3000\u949f\u2014\u8c6a\u9053\uff1a\u201c\u90a3\u662f\u8bf4\u4ecd\u662f\u8868\u6f14\u6027\u8d28\u4e86\uff1f\u970d\u526f\u76df\u4e3b\u53ef\u5426\u547d\u4eba\u642c\u5f20\u684c\u4e86\u6765\u3002\u684c\u5b50\u4e0a\u518d\u6446\u51e0\u5200 \u7eb8\u3002\u201d \u3000\u3000\u805a\u4e49\u5385\u5185\uff0c\u65e2\u6709\u684c\u5b50\uff0e\u53c8\u6709\u7eb8\u5f20\u3002 \u3000\u3000\u5728\u970d\u5143\u4f3d\u7684\u5429\u5490\u4e0b\uff0e\u5f88\u5feb\u4fbf\u642c\u6765\u4e00\u5f20\u684c\u5b50\u548c\u5341\u5200\u9ec4\u7eb8\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u8fd9\u6b21\u5c31\u7531\u5728\u4e0b\u6597\u80c6\u5360\u5148\u4e86\uff01\u201d \u3000\u3000\u8bf4\u7740\uff0e\u53f3\u624b\u5df2\u8f7b\u8f7b\u6309\u5728\u90a3\u5341\u5200\u645e\u5728\u4e00\u8d77\u7684\u9ec4\u7eb8\u4e0a\u3002 \u3000\u3000\u4f46\u89c1\u4ed6\u624b\u638c\u5728\u7eb8\u4e0a\u4e0d\u505c\u79fb\u52a8\uff0c\u4e94\u6307\u4e5f\u4e0d\u4f4f\u76d8\u6405\uff0c\u5927\u7ea6\u76cf\u8336\u529f\u592b\uff0e\u624d\u7f29\u56de\u624b\u6765\uff0e\u9000\u7acb\u4e00 \u4fa7\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u90a3\u684c\u4e0a\u5341\u5200\u645e\u5728\u4e00\u8d77\u7684\u9ec4\u7eb8\uff0c\u4f9d\u7136\u539f\u6837\u4e0d\u53d8\u7684\u6446\u5728\u90a3\u91cc\uff0c\u4f7f\u5f97\u4e24\u65c1\u89c2\u770b\u7684\u4eba\uff0c\u51e0 \u4e4e\u90fd\u4e0d\u660e\u767d\u4ed6\u5728\u5f04\u4ec0\u4e48\u7384\u865a\u3002 \u3000\u3000\u53ea\u542c\u5f97\u970d\u5143\u4f3d\u563f\u563f\u7b11\u9053\uff1a\u201c\u949f\u5144\u8fd9\u4e00\u624b\u2018\u6413\u7eb8\u6210\u7070\u2019\u7684\u6307\u638c\u795e\u529f\uff0c\u679c\u7136\u4ee4\u8001\u592b\u4f69\u670d\uff01\u201d \u3000\u3000\u949f\u4e00\u8c6a\u7acb\u5373\u55b7\u51fa\u2014\u53e3\u6c14\u7bad\uff0c\u5411\u90a3\u5341\u5200\u9ec4\u7eb8\u5439\u53bb\uff0e \u3000\u3000\u987f\u65f6\uff0c\u90a3\u5341\u5200\u9ec4\u7eb8\uff0c\u5df2\u53d8\u6210\u4e00\u56e2\u9ec4\u7070\uff0c\u64a4\u843d\u4e08\u4f59\u65b9\u5706\u7684\u4e00\u5730\uff0c\u800c\u90a3\u5f20\u684c\u5b50\uff0e\u5374\u770b\u4e0d\u51fa\u534a \u70b9\u635f\u4f24\u75d5\u8ff9\u3002 \u3000\u3000\u970d\u5143\u4f3d\u8f6c\u8fc7\u5934\u9053\uff1a\u201c\u8001\u592b\u7528\u4e0d\u7740\u4efb\u4f55\u9053\u5177\uff0c\u5c31\u5728\u5341\u6b65\u5916\u7684\u90a3\u68f5\u866c\u677e\u7684\u6811\u5e72\u4e0a\uff0c\u6bd4\u5212\u6bd4\u5212 \u5982\u4f55\uff1f\u201d \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u970d\u526f\u76df\u4e3b\u8bf7\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u626c\u8d77\u53f3\u81c2\uff0c\u201c\u547c\u201d\u7684\u4e00\u638c\uff0c\u9065\u7a7a\u76f4\u5411\u90a3\u866c\u677e\u7684\u6811\u5e72\u4e0a\u5288\u53bb\u3002 \u3000\u3000\u5947\u602a\u7684\u662f\u4ed6\u638c\u529b\u5288\u51fa\u4e4b\u540e\uff0c\u53f3\u81c2\u5e76\u672b\u64a4\u56de\uff0c\u4f9d\u7136\u5e73\u4e3e\u5411\u524d\uff0e\u4e94\u6307\u5374\u4e0d\u4f4f\u7ffb\u6405\uff0c\u4e45\u4e45\u624d\u6536 \u52bf\u9000\u56de\u4e24\u6b65\u3002 \u3000\u3000\u53ea\u542c\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u970d\u526f\u76df\u4e3b\u8fd8\u662f\u4f4d\u51fa\u8272\u7684\u6307\u62f3\u4e66\u6cd5\u5bb6\uff01\u201d \u3000\u3000\u4e24\u65c1\u89c2\u770b\u7684\u4eba\uff0c\u731b\u95fb\u949f\u4e00\u8c6a\u8fd9\u53e5\u6ca1\u5934\u6ca1\u8111\u7684\u8bdd\uff0c\u6839\u672c\u5f04\u4e0d\u6e05\u662f\u600e\u4e48\u56de\u4e8b\u3002 \u3000\u3000\u5c31\u5728\u949f\u4e00\u8c6a\u7684\u8bdd\u521a\u521a\u8bf4\u5b8c\uff0c\u5ffd\u542c\u4e00\u9635\u5288\u556a\u58f0\u54cd\uff0e\u90a3\u866c\u677e\u7684\u6811\u5e72\uff0c\u7adf\u7136\u6389\u4e0b\u4e00\u5927\u7247\u6811\u76ae\uff0e \u540c\u65f6\u4e5f\u663e\u51fa\u4e86\u6570\u884c\u6df1\u5165\u6728\u5185\u7684\u5b57\u8ff9\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u56e0\u79bb\u5f97\u8f83\u8fd1\uff0c\u4e14\u53c8\u76ee\u529b\u8fc7\u4eba\uff0e\u4f9d\u7a00\u770b\u51fa\u90a3\u51e0\u884c\u5b57\uff0c\u662f\u201c\u82e5\u6b66\u529f\u96be\u5206\u80dc\u8d1f\uff0c\u5219\u76df\u4e3b \u4e4b\u4f4d\uff0e\u5e94\u4ee5\u5e74\u957f\u8005\u5c45\u4e4b\u3002\u201d \u3000\u3000\u4f46\u53cc\u65b9\u5176\u4ed6\u7684\u4eba\uff0c\u56e0\u76f8\u9694\u8f83\u8fdc\uff0e\u867d\u731c\u5230\u6811\u5e72\u4e0a\u5fc5\u5b9a\u6709\u5b57\uff0c\u5374\u4e0d\u77e5\u5199\u4e86\u4e9b\u4ec0\u4e48\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u5fae\u5fae\u4e00\u7b11\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5e74\u9ad8\u672a\u5fc5\u6709\u5fb7\uff0c\u970d\u526f\u76df\u4e3b\u603b\u542c\u8bf4\u8fc7\u8fd9\u53e5\u8bdd\u5427\uff1f\u201d \u3000\u3000\u4ed6\u8bdd\u58f0\u751a\u4f4e\uff0c\u5206\u660e\u662f\u7ed9\u970d\u5143\u4f3d\u7559\u4e86\u51e0\u5206\u60c5\u9762\u3002 \u3000\u3000\u970d\u5143\u4f3d\u8138\u4e0a\u2014\u70ed\uff0c\u62c5\u5fc3\u7559\u4e0b\u8bdd\u67c4\uff0c\u968f\u624b\u518d\u9065\u7a7a\u5288\u51fa\u4e00\u638c\u3002\u6b63\u597d\u628a\u90a3\u51e0\u884c\u5b57\u6bc1\u53bb\u3002 \u3000\u3000\u4ed6\u6599\u60f3\u8fd9\u6b21\u5728\u6307\u3001\u638c\u5de5\u592b\u4e0a\u4e5f\u4e0d\u5bb9\u6613\u5206\u51fa\u9ad8\u4e0b\uff0c\u5475\u5475\u7b11\u4e86\u4e00\u9635\u9053\uff1a\u201c\u770b\u6765\u8001\u592b\u548c\u949f\u5144\u60f3 \u5c31\u6b64\u7f62\u624b\u662f\u4e0d\u6210\u7684\u5566\uff01\u201d \u3000\u3000\u949f\u4e00\u8c6a\u4e0d\u52a8\u58f0\u8272\u9053\uff1a\u201c\u970d\u526f\u76df\u4e3b\u53ea\u7ba1\u518d\u5212\u51fa\u9053\u513f\u6765\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u8868\u793a\u8c26\u8ba9\uff0c\u62f1\u4e86\u62f1\u624b\u9053\uff1a\u201c\u4e0d\u77e5\u949f\u5144\u6709\u4f55\u9ad8\u89c1\uff1f\u201d \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u5982\u679c\u970d\u526f\u76df\u4e3b\u4ecd\u4e0d\u613f\u4ee5\u5175\u5203\u76f8\u4e89\uff0c\u90a3\u5c31\u6bd4\u6bd4\u5185\u529f\u4e86\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u4e0d\u89c9\u6697\u559c\uff0c\u5fd6\u9053\uff1a\u201c\u8c01\u90fd\u77e5\u9053\uff0c\u4e60\u6b66\u4e4b\u4eba\uff0c\u5e74\u9f84\u8d8a\u5927\uff0c\u5185\u529f\u8d8a\u6df1\uff0c\u8fd9\u4fbf\u662f\u6240\u8c13\u7684 \u2018\u706b\u5019\u2019\u3002\u82e5\u65e0\u51e0\u5341\u5e74\u7684\u4fee\u4e3a\uff0e\u7edd\u96be\u8fbe\u5230\u2018\u7089\u706b\u7eaf\u9752\u2019\u4e4b\u5883\uff0e\u949f\u2014\u8c6a\u867d\u7136\u8eab\u624b\u9ad8\u7edd\uff0c\u4f46\u5728\u5185 \u529f\u4e0a\uff0c\u5374\u96be\u671b\u8001\u592b\u9879\u80cc\u2026\u2026\u201d \u3000\u3000\u60f3\u5230\u8fd9\u91cc\uff0c\u7acb\u5373\u563f\u563f\u7b11\u9053\uff1a\u201c\u8001\u592b\u5c31\u4f9d\u949f\u5144\u6240\u8a00\uff0e\u4f46\u4e0d\u77e5\u5982\u4f55\u6bd4\u6cd5\uff1f\u201d \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u5f7c\u6b64\u5e2d\u5730\u800c\u5750\uff0e\u5404\u51fa\u53cc\u638c\u4e92\u62b5\uff0c\u7136\u540e\u518d\u8fd0\u6c14\u903c\u51fa\u5185\u52b2\uff0c\u90a3\u4e2a\u4e0d\u652f\u5012\u5367\u4e0b \u53bb\uff0c\u6216\u8005\u5148\u884c\u7f62\u624b\uff0c\u5c31\u7b97\u843d\u8d25\u3002\u201d \u3000\u3000\u5982\u6b64\u6bd4\u6cd5\uff0c\u8d8a\u53d1\u4e2d\u4e86\u970d\u5143\u4f3d\u5fc3\u610f\uff0c\u5f53\u4e0b\u76ee\u5149\u73af\u626b\u4e86\u5168\u573a\u4e00\u773c\uff0c\u5fae\u7b11\u9053\uff1a\u201c\u5f53\u7740\u53cc\u65b9\u4f17\u4eba \u4e4b\u9762\uff0e\u5728\u4f17\u76ee\u777d\u777d\u4e4b\u4e0b\uff0c\u7eb5\u7136\u6709\u5fc3\u60f3\u8d56\uff0c\u53ea\u6015\u4e5f\u96be\u4ee5\u505a\u5f97\u51fa\u6765\uff0c\u949f\u5144\u529e\u6cd5\u9ad8\u660e\uff0c\u5b9e\u53eb\u8001\u592b\u5fc3 \u670d\u53e3\u670d\uff01\u201d \u3000\u3000\u8bf4\u5b8c\u8bdd\uff0c\u5f53\u5148\u76d8\u819d\u5750\u4e0b\uff0c\u4f38\u51fa\u53cc\u638c\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u5411\u524d\u8d70\u4e86\u4e24\u6b65\uff0c\u5728\u970d\u5143\u4f3d\u5bf9\u9762\u5750\u4e0b\uff0c\u4e5f\u7f13\u7f13\u4f38\u51fa\u4e86\u53cc\u638c\u3002 \u3000\u3000\u4e24\u4eba\u5728\u8f7b\u529f\u548c\u638c\u3001\u6307\u5de5\u592b\u8f83\u91cf\u4e4b\u65f6\uff0c\u5df2\u4e92\u751f\u6212\u5fc3\u3002 \u3000\u3000\u6b64\u65f6\u5bf9\u9762\u800c\u5750\uff0e\u56db\u638c\u76f8\u62b5\uff0c\u8981\u4ee5\u5404\u4eba\u5185\u529f\u4fee\u4e3a\uff0c\u533a\u5206\u9ad8\u4e0b\uff0c\u81ea\u662f\u4e0d\u6562\u5927\u610f\uff0c\u6ee1\u8138\u73b0\u51fa\u8083 \u7a46\u4e4b\u8272\uff0e\u51dd\u795e\u76ee\u6ce8\u5bf9\u65b9\u3002 \u3000\u3000\u552f\u6709\u970d\u5143\u4f3d\u56e0\u5fc3\u6709\u6240\u6043\uff0e\u663e\u5f97\u8f83\u4e3a\u8f7b\u677e\u4e9b\u3002 \u3000\u3000\u8fd9\u662f\u4e00\u573a\u786c\u786c\u7684\u8f83\u52b2\uff0e\u867d\u7136\u5916\u8868\u770b\u4f3c\u5e73\u9759\uff0c\u4f46\u60c5\u52bf\u5374\u7d27\u5f20\u65e0\u6bd4\uff0c\u4e24\u4eba\u4e2d\u4efb\u4f55\u4e00\u4eba\u4e00\u9732\u51fa \u8d25\u8ff9\uff0c\u7acb\u523b\u4fbf\u8981\u5931\u53bb\u5929\u4e0b\u7eff\u6797\u76df\u4e3b\u7684\u5b9d\u5ea7\u3002 \u3000\u3000\u6bd4\u6b66\u8f83\u6280\uff0e\u65e0\u8bba\u662f\u62f3\u638c\u5200\u5251\uff0c\u53cc\u65b9\u8fc7\u62db\uff0c\u53ef\u5c01\u6321\u8dc3\u907f\uff0c\u552f\u6709\u773c\u4e0b\u8fd9\u79cd\u8f83\u91cf\u4e4b\u6cd5\uff0c\u5168\u51ed\u771f \u529f\u5b9e\u5b66\uff0c\u534a\u70b9\u4e5f\u52c9\u5f3a\u4e0d\u5f97\u3002 \u3000\u3000\u5982\u679c\u4e00\u65b9\u8f83\u5dee\uff0c\u534a\u9014\u60f3\u62bd\u624b\u800c\u9000\u90fd\u4e0d\u80fd\u591f\u3002 \u3000\u3000\u5728\u573a\u53cc\u65b9\u90fd\u662f\u6b66\u6797\u9ad8\u624b\uff0c\u5c82\u6709\u4e0d\u77e5\u5229\u5bb3\u4e4b\u7406\uff0c\u56e0\u4e4b\u5728\u970d\u5143\u4f3d\u548c\u949f\u2014\u8c6a\u5750\u5b9a\u540e\uff0c\u7167\u6837\u4e5f\u4e2a \u4e2a\u795e\u8272\u51dd\u91cd\uff0c\u5c4f\u606f\u4e0d\u52a8\u3002 \u3000\u3000\u4e8c\u4eba\u56db\u638c\u76f8\u62b5\uff0c\u8d77\u521d\u76ee\u5149\u70af\u70af\u6ce8\u89c6\u5bf9\u65b9\uff0c\u7247\u523b\u4e4b\u540e\uff0c\u5df2\u540c\u65f6\u655b\u6536\u773c\u795e\uff0c\u773c\u5ec9\u4f4e\u5782\uff0c\u52a8\u4e5f \u4e0d\u52a8\u3002 \u3000\u3000\u5168\u573a\u4e00\u7247\u5bc2\u9759\u3002 \u3000\u3000\u7ea6\u6709\u4e00\u76cf\u70ed\u8336\u5de5\u592b\uff0c\u53ea\u89c1\u4e8c\u4eba\u80f8\u8179\u8d77\u4f0f\uff0c\u9f3b\u606f\u52a0\u91cd\uff0c\u8138\u4e0a\u4e5f\u6cdb\u8d77\u7ea2\u6da6\u4e4b\u8272\u3002 \u3000\u3000\u53c8\u8fc7\u4e86\u7247\u523b\u5de5\u592b\u3002 \u3000\u3000\u4e8c\u4eba\u7ea2\u6da6\u7684\u8138\u4e0a\uff0c\u5189\u5189\u5730\u900f\u84b8\u51fa\u4e00\u80a1\u8584\u8584\u70ed\u6c14\uff0c\u540c\u65f6\u4e00\u633a\u4e0a\u4f53\uff0c\u5634\u89d2\u5fae\u52a8\uff0c\u4f3c\u662f\u5728\u8fdb\u5438 \u771f\u6c14\u3002 \u3000\u3000\u8fd9\u65f6\u53cc\u65b9\u7684\u4eba\uff0e\u90fd\u5c06\u773c\u5149\u6295\u6ce8\u5728\u4e8c\u4eba\u624b\u81c2\u4e4b\u4e0a\uff0c\u4f46\u89c1\u4e8c\u4eba\u7684\u8863\u8896\uff0e\u7adf\u7136\u6162\u6162\u7684\u9f13\u80c0\u800c \u8d77\u3002 \u3000\u3000\u949f\u2014\u8c6a\u56e0\u8eab\u7740\u52b2\u88c5\uff0c\u8863\u8896\u7d27\u7a84\uff0c\u5c1a\u770b\u4e0d\u51fa\u4ec0\u4e48\u53d8\u5316\u3002 \u3000\u3000\u90a3\u970d\u5143\u4f3d\u56e0\u8863\u8896\u5bbd\u9614\uff0c\u60c5\u5f62\u4fbf\u81ea\u4e0d\u540c\uff0e\u53ea\u89c1\u4ed6\u90a3\u8863\u8896\uff0c\u4e0d\u4f46\u4e3a\u4e00\u80a1\u7f61\u6c14\u6240\u9f13\u80c0\u9686\u8d77\uff0c\u800c \u4e14\u6e10\u5411\u4e0a\u7ffb\u5377\uff0c\u9732\u51fa\u4e86\u534a\u622a\u624b\u81c2\uff0e \u3000\u3000\u540c\u65f6\uff0e\u4e8c\u4eba\u7684\u8863\u886b\uff0c\u4e5f\u8d77\u4e86\u4e00\u9635\u731b\u70c8\u7684\u6ce2\u8361\uff0e\u5b9b\u5982\u7acb\u8eab\u5728\u5927\u98ce\u4e4b\u4e2d\uff0e\u5439\u5f97\u8863\u5e26\u98d8\u62c2\uff0e \u3000\u3000\u53c8\u76f8\u6301\u4e86\u534a\u76cf\u8336\u65f6\u95f4\uff0c\u4e8c\u4eba\u9762\u8272\u6e10\u6e10\u8d64\u7ea2\uff0c\u9752\u7b4b\u66b4\u7a81\uff0c\u90a3\u970d\u5143\u4f3d\u7684\u624b\u81c2\u4e5f\u9661\u7136\u7c97\u58ee\u4e00 \u500d\u3002 \u3000\u3000\u4e8c\u4eba\u76d8\u5750\u4e4b\u5904\uff0c\u4f3c\u662f\u98d5\u98d5\u8d77\u2014\u9635\u65cb\u98ce\uff0c\u6fc0\u5e26\u5f97\u5730\u4e0a\u7684\u5c18\u571f\uff0c\u8349\u53f6\uff0c\u56f4\u7ed5\u7740\u4e8c\u4eba\u7ffb\u817e\u76d8 \u98de\u3002 \u3000\u3000\u4e24\u65c1\u56f4\u89c2\u7684\u7fa4\u8c6a\uff0c\u867d\u662f\u6c5f\u6e56\u6210\u540d\u4eba\u7269\uff0c\u770b\u5230\u773c\u4e0b\u8fd9\u7b49\u60c5\u5f62\uff0c\u4e5f\u4e0d\u7981\u5927\u611f\u60ca\u9a87\uff0c\u51e0\u4e4e\u8fde\u5927 \u6c14\u4e5f\u4e0d\u6562\u51fa\uff0c\u77ac\u4e5f\u4e0d\u77ac\u7684\u51dd\u76ee\u6ce8\u89c6\u3002 \u3000\u3000\u731b\u7136\u95f4\uff0c\u573a\u4e2d\u54cd\u8d77\u4e24\u58f0\u957f\u5618\uff0c\u4e8c\u4eba\u5404\u5410\u51fa\u4e00\u53e3\u957f\u6c14\uff0e\u7741\u5f00\u53cc\u773c\uff0c\u5c04\u9732\u51fa\u6e5b\u6e5b\u7cbe\u5149\u3002\u970d\u5143 \u4f3d\u9888\u5934\u5fae\u62ac\uff0e\u62c2\u80f8\u957f\u9aef\u56db\u5904\u98de\u626c\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u4e5f\u2014\u633a\u8170\u8eaf\uff0c\u53cc\u76ee\u5706\u7741\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u65b9\u624d\u5728\u8f7b\u529f\u548c\u6307\u638c\u4e0a\u8f83\u52b2\u4e4b\u65f6\uff0c\u5df2\u8017\u53bb\u4e0d\u5c11\u771f\u529b\uff0c\u518d\u7ecf\u8fc7\u8fd9\u4e00\u9635\u6bd4\u62fc\u5185\u529f\uff0c\u989d\u89d2 \u4e0a\u5df2\u662f\u6c57\u6c34\u6d94\u6d94\u3002 \u3000\u3000\u9661\u7136\u4e4b\u95f4\uff0c\u573a\u4e2d\u6fc0\u53d1\u51fa\u4e00\u9635\u6c89\u95f7\u7684\u51b2\u51fb\u4e4b\u58f0\u3002 \u3000\u3000\u6c99\u571f\u53c8\u662f\u4e00\u9635\u7ffb\u626c\uff0e\u53ea\u89c1\u970d\u5143\u4f3d\u3001\u949f\u4e00\u8c6a\u4e8c\u4eba\u8eab\u8eaf\u540c\u65f6\u5411\u540e\u4e00\u503e\uff0c\u7ea2\u6da6\u7684\u8138\u8272\uff0c\u7a81\u8f6c\u9752 \u767d\uff0c\u6d94\u6d94\u6c57\u6c34\uff0c\u7adf\u5982\u8c46\u7c92\u4e00\u822c\uff0c\u987a\u816e\u800c\u4e0b\u3002 \u3000\u3000\u573a\u4e2d\u7fa4\u8c6a\u89c1\u6b64\u60c5\u5f62\uff0e\u65e9\u77e5\u4e8c\u4eba\u53c8\u5df2\u786c\u62fc\u4e86\u4e00\u6b21\uff0e \u3000\u3000\u4e8c\u4eba\u867d\u662f\u5404\u88ab\u5bf9\u65b9\u9707\u5f97\u7565\u7565\u540e\u503e\uff0c\u4f46\u8eab\u8eaf\u4f9d\u7136\u5750\u5728\u539f\u5730\u4e0d\u52a8\uff0c\u56db\u638c\u500f\u5206\u5373\u5408\uff0c\u8f7b\u8f7b\u76f8\u62b5 \u4e00\u8d77\uff0c\u53c8\u540c\u65f6\u5408\u4e0a\u53cc\u76ee\u3002 \u3000\u3000\u4e00\u9635\u5267\u70c8\u7684\u62fc\u640f\u8fc7\u540e\uff0c\u53c8\u6682\u65f6\u6062\u590d\u4e86\u5e73\u9759\u3002 \u3000\u3000\u4e24\u4eba\u76f8\u5bf9\u800c\u5750\uff0c\u56db\u638c\u63a5\u89e6\uff0c\u795e\u8272\u95f4\u5341\u5206\u5e73\u548c\uff0e \u3000\u3000\u4f46\u5728\u573a\u4e4b\u4eba\uff0c\u5374\u5fc3\u4e2d\u90fd\u660e\u767d\uff0c\u8fd9\u4e0d\u8fc7\u662f\u5927\u98ce\u66b4\u524d\u7684\u2014\u6bb5\u6682\u65f6\u5e73\u9759\uff0c\u4e00\u573a\u66f4\u51f6\u6076\u7684\u640f\u6597\uff0e \u5373\u5c06\u7d27\u63a5\u5c55\u5f00\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u770b\u5230\u8fd9\u91cc\uff0c\u4e5f\u6697\u6697\u5fd6\u9053\uff1b\u201c\u597d\u5728\u6211\u542c\u4e86\u4f59\u4ea6\u4e50\u7684\u8bdd\uff0c\u6682\u65f6\u4e0d\u5f00\u53e3\u4e0d\u51fa\u5934\uff0c\u82e5\u5f53\u771f \u6211\u548c\u970d\u5143\u4f3d\u8fd9\u8001\u738b\u516b\u86cb\u6bd4\u8d5b\u6307\u638c\u5de5\u592b\u548c\u5185\u529f\uff0c\u4e00\u5b9a\u4e0d\u662f\u4ed6\u7684\u5bf9\u624b\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u6240\u60f3\u7684\u534a\u70b9\u4e0d\u5dee\uff0c\u4ed6\u4e4b\u6240\u4ee5\u5728\u6b66\u529f\u4e0a\uff0c\u770b\u4f3c\u80dc\u8fc7\u949f\u4e00\u8c6a\uff0c\u53ea\u4e0d\u8fc7\u662f\u62db\u672f\u5947\u5965\uff0c\u51fa \u624b\u72e0\u8fa3\uff0c\u4ee4\u4eba\u83ab\u6d4b\u9ad8\u6df1\u800c\u5df2\u3002 \u3000\u3000\u82e5\u8bba\u771f\u6b63\u7684\u529f\u529b\u6df1\u539a\uff0c\u603b\u662f\u5e74\u9f84\u592a\u8f7b\uff0c\u706b\u5019\u4e0d\u591f\uff0c\u8f83\u4e4b\u949f\u4e00\u8c6a\u548c\u970d\u5143\u4f3d\u5b9e\u5728\u8fd8\u5dee\u4e0a\u4e86\u4e00 \u7b79\u3002 \u3000\u3000\u548c\u9ea6\u5c0f\u660e\u7d27\u9760\u7ad9\u7acb\u7684\u4f59\u4ea6\u4e50\uff0e\u4f3c\u4e4e\u5df2\u770b\u51fa\u9ea6\u5c0f\u660e\u5728\u60f3\u4e9b\u4ec0\u4e48\uff0c\u8f7b\u89e6\u4e86\u4ed6\u4e00\u4e0b\uff0c\u4f4e\u58f0\u9053\uff1a \u201c\u522b\u80e1\u601d\u4e71\u60f3\uff0c\u4ed6\u4eec\u4e24\u4e2a\u5373\u5c06\u8fdb\u5165\u751f\u6b7b\u5173\u5934\uff0c\u54b1\u4eec\u8981\u968f\u65f6\u51c6\u5907\u51fa\u624b\u63a5\u5e94\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u77e5\u9053\u4e86\uff0c\u6211\u4e00\u5207\u542c\u4f60\u7684\u3002\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u970d\u5143\u4f3d\u548c\u949f\u4e00\u8c6a\u7684\u8138\u8272\uff0c\u5df2\u9010\u6e10\u6062\u590d\u6b63\u5e38\uff0c\u76f8\u89e6\u7684\u56db\u53ea\u624b\u638c\uff0e\u7f13\u6162\u7684\u5411\u540e\u79fb\u5f00\u3002 \u3000\u3000\u76f8\u8ddd\u7ea6\u534a\u5c3a\u5de6\u53f3\u65f6\uff0c\u5ffd\u542c\u970d\u5143\u4f3d\u5410\u6c14\u51fa\u58f0\uff0c\u8eab\u5b50\u4e00\u503e\uff0c\u53cc\u638c\u75be\u5411\u524d\u5410\uff0e\u638c\u98ce\u8fc7\u540e\uff0c\u5730\u9762 \u6c99\u571f\uff0c\u987a\u7740\u624b\u638c\u7684\u63a8\u52a8\uff0c\u626c\u8d77\u4e09\u3001\u56db\u5c3a\u9ad8\u7684\u4e00\u56e2\u70df\u5c18\u3002 \u3000\u3000\u970d\u5143\u4f3d\u7d20\u4ee5\u96c4\u6d51\u7684\u5185\u529b\uff0c\u9a70\u540d\u6b66\u6797\uff0c\u6b64\u65f6\u9002\u9047\u52b2\u654c\uff0c\u53c8\u5b58\u4e86\u975e\u593a\u4e0b\u7eff\u6797\u76df\u4e3b\u5b9d\u5ea7\u4e0d\u53ef\u4e4b \u5fc3\u3002 \u3000\u3000\u51fa\u638c\u76f8\u640f\uff0c\u81ea\u662f\u84c4\u52bf\u800c\u53d1\uff0c\u2014\u638c\u63a8\u51fa\uff0c\u500f\u53c8\u6536\u56de\uff0c\u63a5\u7740\u518d\u5ea6\u75be\u63a8\u800c\u51fa\uff0e\u8fd9\u6837\u8fde\u7eed\u6536\u63a8\u4e86 \u56db\u4e94\u6b21\u3002 \u3000\u3000\u638c\u98ce\u7684\u6fc0\u8361\uff0c\u66f4\u5982\u6012\u6d77\u72c2\u6d9b\u4e00\u822c\uff0e\u6c79\u6d8c\u6cb8\u817e\uff0c\u76f4\u5411\u949f\u4e00\u8c6a\u51b2\u51fb\u800c\u53bb\u3002 \u3000\u3000\u8fd9\u7b49\u51f6\u731b\u6d51\u539a\u7684\u5185\u529b\uff0c\u8db3\u80fd\u5012\u7891\u62d4\u6811\uff0e\u53ea\u770b\u5f97\u53cc\u65b9\u4e4b\u4eba\uff0e\u5168\u90fd\u60ca\u5fc3\u52a8\u9b44\uff0e\u4e00\u9f50\u5c06\u76ee\u5149\u6295 \u6ce8\u5230\u949f\u4e00\u8c6a\u8eab\u4e0a\u3002 \u3000\u3000\u53ea\u89c1\u949f\u4e00\u8c6a\u53cc\u76ee\u5706\u7741\uff0e\u5168\u795e\u8d2f\u6ce8\uff0c\u76ef\u89c6\u7740\u970d\u5143\u4f3d\u7684\u52a8\u4f5c\uff0c\u4e5f\u662f\u53cc\u638c\u5410\u9001\uff0c\u4e0d\u8fc7\u4ed6\u7684\u638c \u52bf\uff0e\u4e0e\u5bf9\u65b9\u6070\u6070\u76f8\u53cd\u3002 \u3000\u3000\u4eba\u63a8\u4ed6\u7f29\uff0e\u4eba\u6536\u4ed6\u63a8\uff0c\u4e8c\u4eba\u4e00\u63a8\u4e00\u9001\uff0c\u4e92\u76f8\u8fce\u5408\u3002 \u3000\u3000\u4e24\u4eba\u5168\u662f\u5185\u5bb6\u9ad8\u4e8e\uff0c\u4e00\u63a8\u2014\u9001\u4e4b\u52bf\uff0c\u770b\u4f3c\u8f7b\u6de1\uff0c\u5176\u5b9e\u5374\u662f\u5168\u529b\u7684\u76f8\u62fc\uff0c\u4e8c\u4eba\u76ee\u4e0d\u8f6c\u775b\u7684 \u770b\u7740\u5bf9\u65b9\uff0c\u6536\u81c2\u9001\u638c\uff0c\u4e1d\u6beb\u4e0d\u6562\u5927\u610f\u3002 \u3000\u3000\u5c31\u8fd9\u6837\uff0e\u53cc\u65b9\u63a8\u9001\u4e86\u7ea6\u6709\u5341\u4e2a\u6765\u56de\uff0c\u9661\u7136\u4e24\u58f0\u95f7\u54fc\uff0c\u4e24\u4eba\u7a81\u7136\u540c\u65f6\u5411\u540e\u5f39\u9707\u51fa\u56db\u4e94\u5c3a \u8fdc\u3002 \u3000\u3000\u573a\u4e2d\u7fa4\u8c6a\uff0c\u4e0d\u7531\u4e00\u9635\u9a9a\u52a8\uff0c\u80c6\u5c0f\u7684\u7adf\u7136\u60ca\u53eb\u51fa\u58f0\u3002 \u3000\u3000\u518d\u770b\u4e8c\u4eba\uff0c\u867d\u7136\u5404\u88ab\u5bf9\u65b9\u5185\u529b\u9707\u5f39\u51fa\u6570\u5c3a\u8fdc\u8fd1\uff0c\u4f46\u539f\u5750\u7684\u8eab\u5f62\uff0c\u5374\u5206\u6beb\u672a\u53d8\uff0c\u4f9d\u7136\u539f\u5f0f \u4e0d\u52a8\u3002 \u3000\u3000\u5df2\u6062\u590d\u6b63\u5e38\u7684\u8138\u8272\uff0c\u6b64\u65f6\u53c8\u8f6c\u9752\u767d\uff0c\u540c\u65f6\u4e8c\u4eba\u7684\u5634\u5507\uff0c\u4e5f\u5728\u5fae\u5fae\u6296\u98a4\uff0e\u53cc\u76ee\u5782\u95ed\u3002 \u3000\u3000\u53cc\u65b9\u5fae\u5fae\u7741\u5f00\u773c\uff0e\u4e92\u671b\u4e86\u4e00\u4e0b\uff0c\u4e00\u8bed\u672a\u53d1\uff0c\u53c8\u81ea\u7f13\u7f13\u95ed\u4e0a\uff0c\u624b\u629a\u4e39\u7530\uff0c\u9ed8\u9ed8\u8c03\u606f\u4e86\u4e00 \u9635\u3002 \u3000\u3000\u5927\u7ea6\u6709\u4e00\u76cf\u70ed\u8336\u5de5\u592b\u4e4b\u540e\uff0c\u970d\u5143\u4f3d\u7a81\u7136\u4e00\u8038\u53cc\u80a9\uff0c\u539f\u5750\u59ff\u52bf\u4e0d\u53d8\uff0c\u8eab\u8eaf\u51cc\u7a7a\u800c\u8d77\uff0c\u76f4\u5411 \u949f\u4e00\u8c6a\u5750\u8eab\u4e4b\u5904\u649e\u53bb\u3002 \u3000\u3000\u949f\u2014\u8c6a\u53cc\u638c\u5e73\u80f8\u5411\u524d\u2014\u63a8\uff0c\u7acb\u65f6\u6709\u4e00\u80a1\u6697\u52b2\u968f\u638c\u98ce\u800c\u51fa\uff0c\u6709\u5982\u4e00\u9053\u65e0\u5f62\u7684\u5899\u58c1\u822c\uff0c\u786c\u662f \u628a\u970d\u5143\u4f3d\u649e\u6765\u7684\u8eab\u5b50\u6321\u4f4f\u3002 \u3000\u3000\u970d\u5143\u4f3d\u5411\u524d\u75be\u51b2\u7684\u8eab\u5b50\uff0c\u88ab\u90a3\u65e0\u5f62\u52b2\u9053\u4e00\u62e6\uff0c\u7acb\u65f6\u53c8\u5012\u98de\u56de\u53bb\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u53cc\u638c\u63a8\u51fa\u4e4b\u540e\uff0c\u8eab\u5b50\u4e5f\u9aa4\u7136\u98de\u8d77\u3002 \u3000\u3000\u970d\u5143\u4f3d\u5374\u539f\u52bf\u75be\u6c89\u800c\u4e0b\uff0c\u8eab\u5b50\u4e00\u7740\u5730\uff0c\u7acb\u5373\u53cc\u638c\u4e00\u9f50\u63a8\u51fa\u3002 \u3000\u3000\u8fd9\u5f53\u513f\uff0c\u4e24\u4eba\u640f\u51fb\u4e4b\u52bf\u5927\u53d8\uff0e\u949f\u4e00\u8c6a\u8eab\u60ac\u534a\u7a7a\uff0c\u970d\u5143\u4f3d\u5374\u76d8\u819d\u5750\u5728\u5730\u4e0a\uff0c\u4e24\u4eba\u638c\u529b\u865a\u7a7a \u7684\u4e00\u63a5\uff0e\u949f\u4e00\u8c6a\u9661\u7136\u5728\u7a7a\u4e2d\u7ffb\u4e86\u4e00\u4e2a\u7b4b\u6597\uff0c\u5012\u98de\u51fa\u4e03\u516b\u5c3a\u8fdc\u3002 \u3000\u3000\u539f\u6765\u4e24\u4eba\u5185\u529b\u76f8\u5dee\u4e0d\u591a\uff0c\u90a3\u4e2a\u865a\u7a7a\u843d\u638c\uff0c\u90a3\u4e2a\u5c31\u96be\u4ee5\u63a5\u5f97\u5bf9\u65b9\u638c\u52b2\u3002 \u3000\u3000\u970d\u5143\u4f3d\u53d1\u51fa\u4e00\u638c\u51fb\u9000\u949f\u4e00\u8c6a\u540e\uff0c\u5e76\u672a\u8d81\u52bf\u8ffd\u51fb\uff0c\u5374\u95ed\u4e0a\u53cc\u76ee\u4f11\u606f\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u7ffb\u4e86\u4e00\u4e2a\u7b4b\u6597\u4e4b\u540e\uff0c\u4ecd\u7136\u539f\u52bf\u4e0d\u53d8\u7684\u843d\u5728\u5730\u4e0a\uff0c\u9759\u5750\u4f11\u606f\u3002 \u3000\u3000\u539f\u6765\u970d\u5143\u4f3d\u81ea\u77e5\u5185\u529b\u5df2\u8017\u53bb\u5927\u534a\uff0c\u800c\u4e14\u5176\u65f6\u5bf9\u65b9\u5df2\u843d\u535c\u5730\u6765\uff0c \u3000\u3000\u82e5\u65bd\u88ad\u4e0d\u6210\uff0e\u53cd\u800c\u8981\u4f24\u5728\u5bf9\u65b9\u638c\u4e0b\u3002 \u3000\u3000\u8861\u91cf\u5229\u5bb3\u5f97\u5931\uff0c\u662f\u4ee5\u4e0d\u6562\u8f7b\u4e3e\u5984\u52a8\uff0c\u51b3\u5b9a\u4ecd\u5e94\u7a33\u624e\u7a33\u6253\uff0c\u540c\u65f6\u4ed6\u81ea\u5fd6\u5bf9\u65b9\u5185\u529b\u4e00\u5b9a\u6bd4\u81ea \u5df1\u6d88\u8017\u7684\u66f4\u591a\uff0c\u53ea\u8981\u82e6\u6491\u4e0b\u53bb\uff0c\u6700\u540e\u80dc\u5229\u5fc5\u5b9a\u5c5e\u4e8e\u81ea\u5df1\u3002 \u3000\u3000\u4e24\u4eba\u53c8\u5f00\u59cb\u8fd0\u6c14\u8c03\u606f\u3002 \u3000\u3000\u7ecf\u8fc7\u4e24\u6b21\u640f\u51fb\u4e4b\u540e\uff0e\u573a\u4e2d\u4e4b\u4eba\uff0c\u90fd\u5df2\u770b\u51fa\u4e24\u4eba\u529f\u592b\u5728\u4f2f\u4ef2\u4e4b\u95f4\uff0e\u7eb5\u7136\u970d\u5143\u4f3d\u7a0d\u6df1\u539a\u4e9b\uff0c \u4e5f\u9ad8\u4e0d\u51fa\u591a\u5c11\u3002 \u3000\u3000\u9e7f\u6b7b\u60df\u624b\uff0c\u8c01\u4e5f\u65e0\u6cd5\u77a7\u5f97\u51fa\u6765\uff0c\u662f\u4ee5\u4e2a\u4e2a\u90fd\u8d8a\u53d1\u7d27\u5f20\u3002 \u3000\u3000\u5f53\u4e8b\u53cc\u529b\u867d\u5df2\u505c\u4e0b\u640f\u6597\uff0c\u4f46\u89c2\u6218\u4e4b\u4eba\uff0c\u4ecd\u7136\u4e2a\u4e2a\u5c4f\u606f\u51dd\u795e\u800c\u7acb\uff0c\u4f3c\u4e4e\u6bd4\u5f53\u4e8b\u4eba\u8fd8\u8981\u7d27\u5f20 \u5f97\u591a\u3002 \u3000\u3000\u5927\u7ea6\u53c8\u8fc7\u4e86\u987f\u996d\u5de5\u592b\u4e4b\u4e45\uff0c\u4e24\u4eba\u53c8\u540c\u65f6\u7741\u5f00\u773c\uff0c\u76f8\u4e92\u4e0a\u671b\uff0c\u540c\u65f6\u7ad9\u8d77\u8eab\u5b50\uff0e\u5411\u524d\u8d70\u4e86\u51e0 \u6b65\u3002 \u3000\u3000\u9762\u5bf9\u9762\u518d\u5750\u4e0b\u6765\uff0c\u5404\u81ea\u7f13\u7f13\u4f38\u51fa\u53cc\u638c\uff0c\u63a8\u5728\u4e00\u8d77\u3002 \u3000\u3000\u53ea\u89c1\u4e24\u4eba\u7684\u8138\u8272\uff0c\u53c8\u5f00\u59cb\u4e25\u8083\u8d77\u6765\uff0c\u5404\u4eba\u5934\u4e0a\u70ed\u6c14\u84b8\u84b8\u76f4\u5f80\u4e0a\u5192\uff0c\u76f8\u6301\u4e0d\u4e45\uff0c\u4e24\u4eba\u8eab\u8eaf \u90fd\u5f00\u59cb\u5fae\u5fae\u98a4\u6296\u8d77\u6765\u3002 \u3000\u3000\u4f46\u4e24\u4eba\u76f8\u6301\u4e4b\u52bf\uff0c\u4ecd\u7136\u4fdd\u6301\u7740\u5e73\u8861\uff0c\u505c\u5728\u539f\u4f4d\u4e0a\u3002 \u3000\u3000\u8c01\u4e5f\u65e0\u6cd5\u5411\u524d\u63a8\u8fdb\u4e00\u5bf8\uff01 \u3000\u3000\u5728\u573a\u4e4b\u4eba\uff0c\u5df2\u77a7\u51fa\u4e24\u4eba\u62fc\u640f\uff0c\u8fd9\u6b21\u662f\u771f\u6b63\u8fdb\u5165\u751f\u6b7b\u5173\u5934\u3002 \u3000\u3000\u5176\u4e2d\u53ea\u8981\u6709\u4e00\u4eba\u7a0d\u7a0d\u4e0d\u652f\uff0c\u53e6\u4e00\u4eba\u7acb\u65f6\u5c06\u4f1a\u4ee5\u6392\u5c71\u5012\u6d77\u4e4b\u52bf\uff0c\u51b2\u51fb\u8fc7\u53bb\uff0c\u8d8b\u52bf\u628a\u5bf9\u65b9\u51fb \u6bd9\u3002 \u3000\u3000\u5ffd\u542c\u970d\u5143\u4f3d\u5410\u6c14\u5f00\u58f0\uff0c\u4e00\u9635\u98a4\u52a8\uff0c\u53cc\u638c\u5411\u524d\u63a8\u8fdb\u4e86\u4e00\u5bf8\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u573a\u4e2d\u5df2\u4e0d\u4f3c\u521a\u624d\u90a3\u822c\u5c18\u571f\u98de\u626c\u3002 \u3000\u3000\u53cd\u800c\u5f02\u5e38\u65f7\u9759\uff0c\u5c18\u4e0d\u626c\u3001\u8349\u4e0d\u60ca\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u88ab\u970d\u5143\u4f3d\u5411\u524d\u63a8\u8fdb\u4e00\u5bf8\u4e4b\u540e\uff0c\u7acb\u5448\u4e0d\u652f\u72b6\u6001\uff01\u8eab\u8eaf\u5411\u540e\u503e\u659c\uff0c\u80dc\u8272\u53d8\u6210\u4e00\u7247\u7d2b \u7ea2\uff0e \u3000\u3000\u987f\u65f6\uff0c\u4f59\u4ea6\u4e50\u7b49\u4eba\u5168\u611f\u7d27\u7ef7\u7684\u5fc3\u5f26\u6b32\u65ad\uff0c\u2014\u4e2a\u4e2a\u4ff1\u90fd\u9a87\u7136\u5931\u8272\u3002 \u3000\u3000\u4f46\u4e8c\u5947\u3001\u56db\u602a\u7b49\u4e00\u4f19\u4eba\u5374\u5168\u90fd\u7709\u98de\u8272\u821e\uff0c\u51e0\u4e4e\u8981\u8dc3\u8eab\u6b22\u547c\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u5343\u5747\u4e00\u53d1\uff0c\u80dc\u8d1f\u7acb\u5206\u4e4b\u9645\uff0e\u7a81\u7136\u949f\u4e00\u8c6a\u5927\u559d\u4e00\u58f0\uff0c\u53cc\u638c\u75be\u6536\u590d\u53c8\u63a8\u51fa\u3002 \u3000\u3000\u5728\u8fd9\u2014\u58f0\u5de8\u9707\u4e4b\u540e\uff0c\u970d\u5143\u4f3d\u548c\u949f\u4e00\u8c6a\u540c\u65f6\u4ef0\u8eab\u5411\u540e\u5012\u53bb\uff0e\u5404\u81ea\u55b7\u51fa\u4e00\u53e3\u9c9c\u8840\uff0c\u7136\u540e\u52a8\u4e5f \u4e0d\u52a8\uff0e \u3000\u3000\u7fa4\u8c6a\u4e00\u89c1\u60c5\u5f62\u4e0d\u5bf9\uff0c\u970d\u5143\u4f3d\u4e00\u65b9\u7684\u641c\u9b42\u624b\u5df4\u5929\u4e49\u548c\u949f\u4e00\u8c6a\u4e00\u65b9\u7684\u6587\u58eb\u6253\u626e\u7684\u4f59\u4ea6\u4e50\uff0e\u4e0d \u7ea6\u800c\u540c\u7684\u6025\u6025\u8dc3\u8fd1\u4e24\u4eba\u8eab\u65c1\uff0e \u3000\u3000\u53ea\u89c1\u970d\u5143\u4f3d\u548c\u949f\u4e00\u8c6a\u90fd\u53cc\u76ee\u7d27\u95ed\uff0c\u5634\u89d2\u9c9c\u8840\u7f13\u7f13\u6d41\u51fa\uff0e \u3000\u3000\u663e\u7136\uff0e\u4e24\u4eba\u5168\u5df2\u5185\u529b\u8017\u5c3d\uff0c\u660f\u53a5\u8fc7\u53bb\uff0e \u3000\u3000\u5df4\u5929\u4e49\u548c\u4f59\u4ea6\u4e50\u90fd\u662f\u6c5f\u6e56\u9605\u5386\u6781\u6df1\u4e4b\u4eba\uff0c\u5fc3\u77e5\u82e5\u8fd9\u65f6\u628a\u4e24\u4eba\u6276\u8d77\uff0c\u53cd\u800c\u4f1a\u4f24\u4e86\u4ed6\u4eec\u7684\u5185 \u8151\u3002\u56e0\u4e4b\uff0c\u867d\u5954\u5230\u970d\u5143\u4f3d\u548c\u949f\u4e00\u8c6a\u8eab\u65c1\uff0c\u5374\u8c01\u90fd\u6ca1\u52a8\u624b\u6400\u6276\uff0e \u5367\u9f99\u751f\u300a\u5999\u7edd\u5929\u9999\u300b \u7b2c \u4e09 \u56de\u3000\u8282\u5916\u751f\u679d \u3000\u3000\u8db3\u8db3\u76cf\u8336\u5de5\u592b\u4e4b\u540e\uff0c\u970d\u5143\u4f3d\u548c\u949f\u4e00\u8c6a\u624d\u7f13\u7f13\u7741\u5f00\u773c\uff0c\u540c\u65f6\u6323\u624e\u7740\u5750\u4e86\u8d77\u6765\u3002 \u3000\u3000\u4f59\u4ea6\u4e50\u51dd\u671b\u4e86\u949f\u4e00\u8c6a\u4e00\u9635\u9053\uff1a\u201c\u949f\u5144\uff0c\u4f60\u611f\u89c9\u5982\u4f55\uff1f\u201d \u3000\u3000\u949f\u4e00\u8c6a\u60e8\u7136\u4e00\u7b11\uff1a\u201c\u53ea\u8981\u970d\u526f\u76df\u4e3b\u8fd8\u6709\u610f\u7ee7\u7eed\u8f83\u91cf\uff0c\u949f\u67d0\u5c82\u80fd\u4e0d\u820d\u547d\u5949\u966a\uff01\u201d \u3000\u3000\u4ed6\u867d\u7136\u8bdd\u58f0\u65e0\u529b\uff0c\u4f46\u5374\u5145\u6ee1\u4e86\u6177\u6168\u3001\u60b2\u58ee\u3002 \u3000\u3000\u4f59\u4ea6\u4e50\u518d\u671b\u5411\u970d\u5143\u4f3d\u9053\uff1a\u201c\u4f9d\u970d\u526f\u76df\u4e3b\u4e4b\u610f\u5462\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u54a7\u4e86\u54a7\u5634\u9053\uff1a\u201c\u4f9d\u8001\u592b\u4e4b\u89c1\uff0c\u82e5\u53cc\u65b9\u518d\u4ee5\u5185\u529f\u62da\u640f\u4e0b\u53bb\uff0c\u53ea\u6015\u76f4\u5230\u8017\u5c3d\u6700\u540e\u4e00\u53e3 \u6c14\uff0c\u4e5f\u672a\u5fc5\u80fd\u6709\u771f\u6b63\u7ed3\u679c\u3002\u201d \u3000\u3000\u949f\u4e00\u8c6a\u63a5\u53e3\u9053\uff1a\u201c\u83ab\u975e\u970d\u526f\u76df\u4e3b\u53c8\u6709\u4e86\u65b0\u4e3b\u610f\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u54b1\u4eec\u5148\u524d\u5df2\u628a\u8bdd\u8bf4\u660e\uff0c\u82e5\u8f7b\u529f\uff0c\u5185\u529f\u548c\u62f3\u638c\u4e0a\u96be\u5206\u9ad8\u4e0b\uff0c\u90a3\u5c31\u4e0d\u5f97\u4e0d\u5728\u5175 \u5203\u4e0a\u89c1\u4e2a\u771f\u7ae0\u3002\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u6697\u9053\uff1a\u201c\u8fd9\u8001\u5bb6\u4f19\u5185\u529f\u7cbe\u7eaf\uff0c\u4f53\u529b\u6062\u590d\u5f97\u53ef\u80fd\u6bd4\u949f\u5144\u5feb\u4e9b\uff0c\u5f85\u4f1a\u513f\u52a8\u8d77\u624b\u6765\uff0c\u5c82 \u4e0d\u88ab\u4ed6\u8d5a\u4e86\u4fbf\u5b9c\u2026\u2026\u201d \u3000\u3000\u60f3\u5230\u8fd9\u91cc\uff0c\u5fd9\u9053\uff1a\u201c\u4e24\u4f4d\u65b9\u624d\u8fde\u756a\u8f83\u6280\uff0c\u5fc5\u5b9a\u5df2\u7ecf\u5927\u4f24\u5143\u6c14\uff0c\u5982\u679c\u8981\u5728\u5175\u5203\u4e0a\u89c1\u771f\u7ae0\uff0c \u4e0d\u59a8\u62d6\u5230\u660e\u5929\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u5374\u9053\uff1a\u201c\u4eca\u65e5\u4e8b\uff0c\u4eca\u65e5\u6bd5\uff0c\u4f55\u5fc5\u62d6\u5230\u660e\u5929\u3002\u201d \u3000\u3000\u7531\u6b64\u53ef\u89c1\uff0c\u4ed6\u81ea\u4fe1\u6062\u590d\u5f97\u8f83\u949f\u4e00\u8c6a\u5feb\uff0e\u5bf9\u4e8e\u6700\u540e\u7684\u80dc\u5229\u751a\u6709\u628a\u63e1\u3002 \u3000\u3000\u4f59\u4ea6\u4e50\u6697\u611f\u5fc3\u5934\u6c89\u91cd\uff0c\u8fd8\u6ca1\u6765\u5f97\u53ca\u5f00\u53e3\uff0e\u5df2\u542c\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u4f59\u5144\u4e0d\u5fc5\u4e3a\u6211\u62c5\u5fc3\uff0c\u970d\u526f\u76df \u4e3b\u8bf4\u5f97\u5bf9\uff0c\u4eca\u5929\u7684\u4e8b\u5c31\u8be5\u4eca\u5929\u89e3\u51b3\u3002\u201d \u3000\u3000\u641c\u9b42\u624b\u5df4\u5929\u4e49\u53ef\u80fd\u5df2\u6599\u970d\u5143\u4f3d\u6700\u540e\u4e00\u6218\u5fc5\u80dc\uff0c\u4e3a\u907f\u514d\u949f\u4e00\u8c6a\u53d8\u5366\uff0c\u968f\u5373\u4e0d\u58f0\u4e0d\u54cd\u7684\u9000\u56de \u4e00\u65c1\u3002 \u3000\u3000\u4ed6\u8fd9\u4e00\u8d77\uff0c\u7b49\u4e0b\u4e5f\u8981\u4f59\u4ea6\u4e50\u79bb\u5f00\u3002 \u3000\u3000\u8fd9\u5f53\u513f\uff0c\u970d\u5143\u4f3d\u548c\u949f\u4e00\u8c6a\u4e5f\u7f13\u7f13\u7ad9\u4e86\u8d77\u6765\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u957f\u957f\u5401\u4e86\u4e00\u53e3\u6c14\u9053\uff1a\u201c\u970d\u526f\u76df\u4e3b\u60f3\u7acb\u5373\u52a8\u7528\u5175\u5203\u5462\uff1f\u8fd8\u662f\u5148\u6bd4\u8bd5\u62f3\u811a\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u5982\u679c\u80fd\u4ece\u62f3\u811a\u4e0a\u5206\u51fa\u80dc\u8d1f\uff0c\u90a3\u5c31\u7528\u4e0d\u7740\u518d\u52a8\u7528\u5175\u5203\u4e86\uff0c\u5f7c\u6b64\u4e43\u662f\u4e00\u5bb6\u4eba\uff0c \u4e0d\u8bba\u4f24\u4e86\u90a3\u4e2a\uff0c\u90fd\u4e0d\u662f\u8001\u592b\u6240\u613f\u89c1\u7684\u3002\u201d \u3000\u3000\u949f\u2014\u8c6a\u9053\uff1a\u201c\u5728\u4e0b\u606d\u656c\u4e0d\u5982\u4ece\u547d\uff01\u201d \u3000\u3000\u8fd9\u6b21\uff0c\u4ed6\u4e0d\u518d\u8c26\u900a\uff0c\u6700\u540e\u4e00\u4e2a\u5b57\u521a\u521a\u843d\u97f3\uff0c\u4fbf\u5ffd\u7684\u6b3a\u8eab\u800c\u8fdb\uff0c\u4e00\u62db\u201c\u76f4\u53e9\u5929\u95e8\u201d\uff0e\u5f53\u80f8 \u76f4\u51fb\u8fc7\u53bb\uff0e \u3000\u3000\u970d\u5143\u4f3d\u770b\u4ed6\u8bf4\u6253\u5c31\u6253\uff0e\u4f59\u97f3\u672a\u7edd\uff0c\u62f3\u5df2\u903c\u5230\uff0c\u4e0d\u7981\u5927\u6012\uff0c\u51b7\u7b11\u2014\u58f0\uff0e\u6a2a\u81c2\u51fa\u638c\uff0c\u4e00\u62db \u201c\u6a2a\u67b6\u91d1\u6881\u201d\uff0c\u6f5c\u8fd0\u5185\u529b\uff0c\u786c\u5bf9\u62f3\u52bf\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u4f3c\u662f\u5b58\u4e0b\u901f\u6218\u901f\u51b3\u4e4b\u5fc3\uff0c\u907f\u5f00\u5bf9\u65b9\u5de6\u638c\uff0c\u53f3\u81c2\u53c8\u52a0\u51e0\u5206\u52b2\u529b\uff0c\u4e00\u62db\u201c\u91d1\u521a\u8212 \u81c2\u201d\uff0e\u518d\u51fb\u8fc7\u53bb\u3002 \u3000\u3000\u4f46\u95fb\u7830\u7136\u4e00\u9707\uff0c\u53cc\u65b9\u53f3\u62f3\u63a5\u5b9e\uff0c\u5404\u81ea\u88ab\u9707\u5f97\u9000\u4e86\u4e00\u6b65\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u52c7\u731b\u527d\u608d\uff0c\u2014\u9000\u5373\u4e0a\uff0c\u53cc\u638c\u5408\u51fb\uff0c\u4e00\u62db\u201c\u53cc\u98ce\u8d2f\u8033\u201d\uff0c\u75be\u51fb\u8fc7\u53bb\uff0c\u53f3\u817f\u540c\u65f6\u98de\u8d77 \u201c\u9b41\u661f\u8e22\u6597\u201d\uff0c\u76f4\u8e22\u5c0f\u8179\u3002 \u3000\u3000\u970d\u5143\u4f3d\u51b7\u51b7\u4e00\u7b11\uff0c\u53cc\u81c2\u5e73\u80f8\u63a8\u51fa\uff0c\u5411\u5de6\u53f3\u2014\u5206\uff0c\u4e00\u62db\u201c\u4e8c\u9f99\u5206\u6c34\u201d\uff0c\u4e0d\u4f46\u786c\u63a5\u4e86\u949f\u4e00\u8c6a \u2014\u62db\u201c\u53cc\u98ce\u8d2f\u8033\u201d\uff0c\u540c\u65f6\u53f3\u817f\u5e73\u626b\u800c\u51fa\uff0c\u731b\u8fce\u5bf9\u65b9\u8e22\u6765\u53f3\u811a\u3002 \u3000\u3000\u4e24\u4eba\u4f60\u6765\u6211\u5f80\uff0c\u8f6c\u77ac\u95f4\u4fbf\u5bf9\u62c6\u4e86\u4e03\u516b\u62db\u3002 \u3000\u3000\u5176\u5b9e\uff0c\u4e8c\u4eba\u6253\u6765\u867d\u7136\u60ca\u9669\u7edd\u4f26\uff0c\u5374\u5e76\u4e0d\u6fc0\u70c8\uff0c\u4e5f\u770b\u4e0d\u51fa\u6709\u4f55\u7cbe\u5f69\u5904\u3002 \u3000\u3000\u8fd9\u662f\u56e0\u4e3a\u4ed6\u4eec\u5148\u524d\u5df2\u5c06\u5185\u529b\u6d88\u8017\u5f97\u5dee\u4e0d\u591a\uff0c\u4f53\u529b\u5c1a\u672a\u5168\u590d\uff0c\u6b64\u523b\u7684\u6fc0\u6218\uff0c\u53ea\u662f\u54ac\u7259\u82e6\u6491 \u800c\u5df2\u3002 \u3000\u3000\u7728\u773c\u53c8\u662f\u5341\u51e0\u56de\u5408\u8fc7\u53bb\uff0e\u4ecd\u662f\u4e2a\u4e0d\u80dc\u4e0d\u8d25\u4e4b\u5c40\u3002 \u3000\u3000\u4f46\u51b7\u773c\u65c1\u89c2\u7684\u4f59\u4ea6\u4e50\uff0e\u5374\u5df2\u770b\u51fa\u82e5\u518d\u6218\u4e0b\u53bb\uff0c\u949f\u4e00\u8c6a\u5fc5\u5b9a\u540e\u529b\u4e0d\u7ee7\uff0c\u800c\u970d\u5143\u4f3d\u5374\u81f3\u5c11\u6709 \u516d\u6210\u53d6\u80dc\u7684\u5e0c\u671b\u3002 \u3000\u3000\u4ed6\u5fc3\u5934\u5927\u6025\u4e4b\u4e0b\uff0c\u7acb\u5373\u4ee5\u201c\u4f20\u97f3\u5165\u5bc6\u201d\u5bf9\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0e\u73b0\u5728\u8be5\u4f60\u2014\u663e\u8eab\u624b\u7684\u65f6 \u5019\u4e86\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u65e9\u5df2\u7b49\u5f97\u4e0d\u8010\u70e6\uff0c\u95fb\u8a00\u6beb\u4e0d\u8fdf\u7591\uff0e\u63a2\u624b\u62d4\u51fa\u5b9d\u5251\uff0c\u4e00\u8dc3\u800c\u81f3\u970d\u5143\u4f3d\u548c\u949f\u4e00\u8c6a\u8eab \u524d\uff0c\u5927\u559d\u9053\uff1a\u201c\u4f4f\u624b\uff01\u201d \u3000\u3000\u949f\u4e00\u8c6a\u6b63\u5e0c\u671b\u9ea6\u5c0f\u660e\u53ca\u65f6\u524d\u6765\u63a5\u5e94\uff0e\u7acb\u5373\u62a2\u653b\u4e00\u62db\uff0c\u628a\u970d\u5143\u4f3d\u7a0d\u7a0d\u903c\u9000\uff0e\u7136\u540e\u8dc3\u9000\u4e00\u4e08 \u4e4b\u5916\u3002 \u3000\u3000\u970d\u5143\u4f3d\u5931\u53bb\u5bf9\u624b\uff0e\u53ea\u597d\u4e5f\u505c\u4e0b\u8eab\u6765\uff0c\u4e00\u9762\u559d\u9053\uff1a\u201c\u5927\u80c6\uff01\u4f60\u8fd9\u5c0f\u5b50\u6363\u7684\u4ec0\u4e48\u86cb\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u563b\u563b\u4e00\u7b11\u9053\uff1a\u201c\u5728\u4e0b\u5c31\u6363\u4f60\u8fd9\u72d7\u86cb\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u4e0d\u89c9\u4e03\u7a8d\u751f\u70df\uff0c\u5927\u543c\u9053\uff1a\u201c\u518d\u4e0d\u6eda\u5f00\uff0c\u8001\u592b\u5c31\u5148\u8981\u4e86\u4f60\u7684\u72d7\u547d\uff01\u201d \u3000\u3000\u539f\u6765\u970d\u5143\u4f3d\u53ea\u77e5\u9ea6\u5c0f\u660e\u662f\u80e1\u67cf\u9f84\u5728\u4e24\u5e74\u524d\u6536\u5bb9\u7684\u4e00\u540d\u8fdc\u652f\u5e08\u5f1f\uff0e\u5e76\u4e0d\u6e05\u695a\u4ed6\u7684\u8eab\u624b\u5982 \u4f55\uff0e\u56e0\u4e4b\u4ece\u6765\u672a\u628a\u4ed6\u653e\u5728\u773c\u91cc\u3002 \u3000\u3000\u8fd9\u4e5f\u96be\u602a\u970d\u5143\u4f3d\u6709\u6b64\u60f3\u6cd5\uff0c\u8bd5\u60f3\u4e00\u4e2a\u5341\u4e03\u516b\u5c81\u4e73\u81ed\u672a\u5e72\u7684\u5b69\u5b50\uff0e\u5373\u4f7f\u6b66\u529f\u518d\u9ad8\uff0c\u4e5f\u9ad8\u4e0d \u4e86\u591a\u5c11\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u53cd\u800c\u6c89\u4f4f\u4e86\u6c14\uff0e\u53c8\u662f\u563b\u563b\u2014\u7b11\u9053\uff1a\u201c\u5728\u4e0b\u662f\u6709\u4e8b\u627e\u4f60\u7684\uff0c\u4f60\u51ed\u4ec0\u4e48\u53eb\u6211\u6eda\u5f00\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u53f1\u9053\uff1a\u201c\u4f60\u627e\u8001\u592b\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4f60\u548c\u6211\u4eec\u949f\u8001\u5927\uff0c\u65b9\u624d\u662f\u5426\u5728\u4e89\u593a\u7eff\u6797\u76df\u4e3b\u4e4b\u4f4d\uff1f\u201d \u3000\u3000\u201c\u5e9f\u8bdd\uff01\u201d \u3000\u3000\u201c\u65e2\u7136\u4f60\u4eec\u662f\u5728\u4e89\u593a\u7eff\u6797\u76df\u4e3b\u4e4b\u4f4d\uff0e\u90a3\u5c31\u8868\u793a\u8c01\u80dc\u4e86\u8c01\u5c31\u662f\u7eff\u6797\u76df\u4e3b\uff0c\u5982\u679c\u73b0\u5728\u6709\u4e2a\u4eba \u6bd4\u4f60\u4eec\u6b66\u529f\u66f4\u9ad8\uff0c\u8981\u4e89\u593a\u7eff\u6797\u76df\u4e3b\uff0e\u90a3\u8be5\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u51b7\u7b11\u9053\uff1a\u201c\u4e0d\u77e5\u5728\u573a\u4e4b\u4eba\uff0e\u8fd8\u6709\u90a3\u4e00\u4e2a\u6b66\u529f\u6bd4\u8001\u592b\u548c\u949f\u526f\u76df\u4e3b\u66f4\u9ad8\uff1f\u201d \u3000\u3000\u201c\u5c31\u6709\u8fd9\u4e48\u4e00\u4f4d\u3002\u201d \u3000\u3000\u201c\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u4f60\u8fdc\u770b\uff01\u201d \u3000\u3000\u201c\u7528\u4e0d\u7740\u8fdc\u770b\u3002\u201d \u3000\u3000\u201c\u90a3\u5c31\u8fd1\u89c2\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u8138\u8272\u4e00\u53d8\uff0c\u987b\u53d1\u6012\u5f20\uff0c\u559d\u9053\uff1a\u201c\u597d\u4e2a\u4e0d\u77e5\u6b7b\u6d3b\u7684\u72c2\u5c0f\u5b50\uff0c\u7adf\u6562\u5728\u8fd9\u79cd\u8282\u9aa8\u773c\u4e0a\uff0c \u6765\u620f\u800d\u8001\u592b\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u7728\u773c\u9053\uff1a\u201c\u6211\u8bf4\u7684\u5b8c\u5168\u662f\u5b9e\u8bdd\uff0c\u4f60\u597d\u50cf\u8fd8\u4e0d\u4fe1\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u4e0d\u613f\u518d\u8ddf\u9ea6\u5c0f\u660e\u591a\u8d39\u53e3\u820c\uff0c\u8f6c\u5934\u9053\uff1a\u201c\u5df4\u8001\u5f1f\uff0c\u628a\u8fd9\u5c0f\u5b50\u7ed9\u6211\u62ff\u4e0b\uff0c\u5f85\u4f1a\u513f\u518d\u542c \u5019\u53d1\u843d\uff01\u201d \u3000\u3000\u641c\u9b42\u624b\u5df4\u5929\u4e49\u521a\u8981\u4e0a\u524d\uff0c\u9ea6\u5c0f\u660e\u5374\u6446\u624b\u9053\uff1a\u201c\u59d3\u5df4\u7684\u522b\u5148\u4e0a\u6765\uff0c\u6211\u548c\u970d\u526f\u76df\u4e3b\u8fd8\u6709\u8bdd \u8bb2\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u53ea\u597d\u54b3\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u5b50\u8fd8\u6709\u4ec0\u4e48\u8bdd\u8bb2\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u5982\u679c\u6211\u8d25\u5728\u5df4\u5929\u4e49\u4e4b\u624b\uff0c\u8fd9\u7eff\u6797\u76df\u4e3b\u4e4b\u4f4d\uff0c\u662f\u5426\u5c31\u8ba9\u7ed9\u5df4\u5929\u4e49\uff1f\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u679c\u7136\u6709\u6548\uff0e\u970d\u5143\u4f3d\u603b\u4e0d\u80fd\u628a\u7eff\u6797\u76df\u4e3b\u4e4b\u4f4d\u8ba9\u7ed9\u5df4\u5929\u4e49\u3002 \u3000\u3000\u4e8b\u5230\u5982\u4eca\uff0e\u4ed6\u4e5f\u53ea\u6709\u4e0d\u8ba1\u8eab\u4efd\u5730\u4f4d\uff0c\u4eb2\u81ea\u51fa\u624b\u6559\u8bad\u6559\u8bad\u8fd9\u4e2a\u72c2\u5984\u7684\u65e0\u6cd5\u65e0\u5929\u7684\u540e\u751f\u665a\u8f88 \u4e86\u3002 \u3000\u3000\u4ee5\u970d\u5143\u4f3d\u7684\u9884\u6599\uff0c\u6700\u591a\u4e09\u62db\uff0c\u4fbf\u53ef\u628a\u9ea6\u5c0f\u660e\u5236\u670d\uff0c\u7edd\u5bf9\u7528\u4e0d\u5230\u56db\u62db\u3002 \u3000\u3000\u5f53\u4e0b\uff0c\u4ed6\u5175\u5203\u4e5f\u4e0d\u53d6\uff0e\u51b7\u51b7\u4e00\u7b11\u9053\uff1a\u201c\u8001\u592b\u5c31\u51b3\u5b9a\u7834\u4f8b\u4eb2\u81ea\u51fa\u624b\u3002\u5c0f\u5b50\uff0e\u4e0d\u5fc5\u5bb3\u6015\uff0e\u4f60 \u5c31\u8fdb\u62db\u5427\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u7b11\u9053\uff1a\u201c\u8001\u5bb6\u4f19\uff0c\u4f60\u600e\u4e48\u4e0d\u52a8\u5bb6\u4f19\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u5bf9\u4ed8\u4f60\u8fd9\u79cd\u6bdb\u5934\u5c0f\u5b50\uff0c\u54ea\u91cc\u8fd8\u7528\u5f97\u7740\u5bb6\u4f19\uff0e\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u672c\u60f3\u4e5f\u5f03\u5251\u4e0d\u7528\uff0c\u6765\u4e2a\u5f92\u624b\u76f8\u640f\uff0c\u4f46\u53c8\u81ea\u5fd6\uff0c\u4ec5\u4ee5\u638c\u529b\u76f8\u62fc\uff0c\u5b9a\u7136\u4e0d\u662f\u970d\u5143\u4f3d\u5bf9 \u624b\u3002 \u3000\u3000\u662f\u4ee5\u4ed6\u4e0d\u518d\u591a\u8a00\uff0c\u6296\u624b\u2014\u5251\uff0c\u7efd\u5f00\u6735\u6735\u5251\u82b1\uff0c\u95ea\u7535\u822c\u5411\u970d\u5143\u4f3d\u524d\u80f8\u523a\u53bb\u3002 \u3000\u3000\u5728\u573a\u53cc\u65b9\u89c2\u6218\u4e4b\u4eba\uff0c\u9664\u949f\u4e00\u8c6a\u4e00\u65b9\u591a\u6570\u77e5\u9053\u9ea6\u5c0f\u660e\uff0c\u51fa\u624b\u5947\u8be1\u62db\u672f\u96be\u6d4b\u5916\uff0c\u970d\u5143\u4f3d\u7684\u4e00 \u65b9\uff0c\u8c01\u4e5f\u6ca1\u628a\u9ea6\u5c0f\u660e\u653e\u5728\u773c\u91cc\uff0c\u751a\u81f3\u8ba4\u4e3a\u4ed6\u662f\u6d3b\u5f97\u4e0d\u8010\u70e6\u81ea\u5bfb\u6b7b\u8def\uff0e \u3000\u3000\u970d\u5143\u4f3d\u6697\u8fd0\u5185\u529b\uff0c\u201c\u547c\u201d\u7684\u4e00\u638c\uff0c\u5288\u51fa\u4e00\u8bb0\u521a\u731b\u65e0\u4fe6\u7684\u638c\u98ce\uff0c\u6392\u5c71\u5012\u6d77\u822c\u76f4\u5411\u9ea6\u5c0f\u660e\u649e \u53bb\u3002 \u3000\u3000\u5728\u4ed6\u7684\u9884\u6599\uff0c\u8fd9\u4e00\u638c\u5288\u51fa\u4e4b\u540e\uff0c\u9ea6\u5c0f\u660e\u4e0d\u6b7b\u4e5f\u5fc5\u91cd\u4f24\uff0e \u3000\u3000\u9ea6\u5c0f\u660e\u679c\u7136\u88ab\u970d\u5143\u4f3d\u8fd9\u4e00\u638c\u8feb\u5f97\u62ff\u6869\u4e0d\u4f4f\uff0c\u523a\u51fa\u7684\u5251\u950b\u4e5f\u504f\u5411\u4e00\u4fa7\uff0c\u4e0d\u7531\u6697\u9053\uff1a\u201c\u8fd9\u8001 \u5c0f\u5b50\u7684\u638c\u529b\u679c\u7136\u5389\u5bb3\uff01\u201d \u3000\u3000\u4ed6\u5fc3\u5ff5\u7535\u8f6c\uff0c\u7acb\u5373\u6ed1\u6b65\u65cb\u8eab\uff0c\u53c8\u662f\u4e00\u5251\u659c\u659c\u523a\u51fa\uff0e \u3000\u3000\u970d\u5143\u4f3d\u53d1\u638c\u4e4b\u540e\uff0c\u773c\u524d\u5374\u4e0d\u89c1\u4e86\u9ea6\u5c0f\u660e\u7684\u8e2a\u5f71\u3002 \u3000\u3000\u4ed6\u672c\u4ee5\u4e3a\u9ea6\u5c0f\u660e\u5fc5\u88ab\u638c\u98ce\u9707\u6454\u51fa\u53bb\uff0c\u6b63\u8981\u6536\u52bf\u56de\u8eab\uff0c\u5374\u7a81\u7136\u5bd2\u8292\u4e00\u95ea\uff0c\u9ea6\u5c0f\u660e\u7684\u5251\u5c16\u5df2 \u903c\u8fd1\u4ed6\u7684\u5de6\u808b\u3002 \u3000\u3000\u539f\u6765\u9ea6\u5c0f\u660e\u662f\u5229\u7528\u201c\u6458\u661f\u6b65\u201d\u95ea\u5f00\u4e86\u970d\u65e0\u4f3d\u7684\u4e00\u51fb\uff0c\u4e0d\u4f46\u5378\u53bb\u4e86\u970d\u5143\u4f3d\u7684\u638c\u98ce\uff0c\u800c\u4e14\u65cb \u8eab\u518d\u8fdb\uff0c\u8eab\u6cd5\u8be1\u5f02\u5f97\u7b80\u76f4\u795e\u9b3c\u83ab\u6d4b\u3002 \u3000\u3000\u970d\u5143\u4f3d\u4e00\u65f6\u5927\u610f\uff0c\u9669\u4e9b\u88ab\u2014\u5251\u523a\u4e2d\uff0e \u3000\u3000\u5230\u8fd9\u65f6\u970d\u5143\u4f3d\u624d\u89c9\u51fa\uff0c\u8fd9\u5c0f\u4f19\u5b50\u7adf\u771f\u7684\u662f\u83ab\u6d4b\u9ad8\u6df1\uff0c\u5fc3\u5934\u6697\u51db\u4e4b\u4e0b\uff0c\u53ea\u597d\u4e00\u677e\u8170\u4e2d\u6263 \u628a\uff0c\u63a3\u51fa\u4e86\u4ed6\u90a3\u901a\u4f53\u4e4c\u9ed1\u7684\u86c7\u5934\u8f6f\u97ad\uff0e \u3000\u3000\u53ea\u542c\u9ea6\u5c0f\u660e\u563f\u563f\u7b11\u9053\uff1b\u201c\u8001\u5bb6\u4f19\uff0c\u5230\u5e95\u4eae\u51fa\u5175\u5203\u6765\u4e86\u5427\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u65e9\u5df2\u6c14\u5f97\u987b\u53d1\u6012\u5f20\uff0c\u66f4\u4e0d\u7b54\u8bdd\uff0e\u4e00\u626c\u8155\uff0c\u6296\u5f00\u86c7\u5934\u8f6f\u97ad\uff0c\u72b9\u5982\u4e4c\u9f99\u51fa\u6d1e\u822c\uff0c\u4e00\u62db \u201c\u4e4c\u9f99\u7ede\u67f1\u201d\uff0e\u76f4\u5411\u9ea6\u5c0f\u660e\u62e6\u8170\u626b\u53bb\uff0e \u3000\u3000\u86c7\u5934\u8f6f\u97ad\u8db3\u6709\u516b\u4e5d\u5c3a\u957f\uff0c\u4e00\u97ad\u626b\u51fa\uff0c\u5728\u4ed6\u7684\u9884\u6599\u4e2d\uff0c\u9ea6\u5c0f\u660e\u8eab\u6cd5\u518d\u5feb\uff0c\u4e5f\u96be\u4ee5\u8eb2\u8fc7\u7684\u3002 \u3000\u3000\u88ab\u626b\u4e2d\u4e4b\u540e\uff0c\u52bf\u5fc5\u62e6\u8170\u65ad\u4e3a\u4e24\u622a\u3002 \u3000\u3000\u5c82\u77e5\u97ad\u52bf\u51fa\u624b\u4e4b\u540e\uff0c\u7a81\u89c9\u91cd\u91cf\u52a0\u91cd\uff0c\u8fd9\u624d\u770b\u51fa\u9ea6\u5c0f\u660e\u4e0d\u77e5\u7528\u7684\u4ec0\u4e48\u6cd5\u672f\uff0c\u7adf\u7136\u7ad9\u5728\u97ad\u8eab \u4e4b\u4e0a\u3002 \u3000\u3000\u8fd9\u60c5\u666f\u7b80\u76f4\u662f\u89c1\u6240\u672a\u89c1\uff0c\u949f\u4e00\u8c6a\u4e00\u65b9\u7684\u4eba\uff0c\u4e0d\u7531\u9f50\u58f0\u559d\u8d77\u91c7\u6765\u3002 \u3000\u3000\u4f46\u970d\u5143\u4f3d\u65b9\u9762\u7684\u4eba\uff0c\u5374\u90fd\u4e2a\u4e2a\u9a87\u7136\u5931\u8272\u3002 \u3000\u3000\u597d\u5728\u970d\u5143\u4f3d\u5e76\u672a\u8fc7\u4efd\u5403\u60ca\uff0c\u5fc3\u60f3\u53ea\u8981\u628a\u97ad\u62fc\u529b\u7529\u52a8\uff0c\u5e76\u4e0d\u96be\u628a\u5bf9\u65b9\u6454\u4e0b\u97ad\u6765\u3002 \u3000\u3000\u504f\u504f\u9ea6\u5c0f\u660e\u7684\u53cc\u811a\uff0c\u50cf\u4fd8\u88ab\u7c98\u5728\u97ad\u4e0a\u4e00\u822c\uff0e\u970d\u5143\u4f3d\u4e0d\u8bba\u5982\u4f55\u7529\u52a8\uff0e\u59cb\u7ec8\u65e0\u6cd5\u5c06\u4ed6\u629b\u51fa\u3002 \u3000\u3000\u800c\u9ea6\u5c0f\u660e\u624b\u6267\u957f\u5251\u6b63\u7ad9\u5728\u86c7\u5934\u8f6f\u97ad\u4e2d\u95f4\uff0c\u56db\u5c3a\u5de6\u53f3\u7684\u8ddd\u79bb\uff0c\u53ea\u8981\u5251\u4e00\u523a\u51fa\uff0e\u5fc5\u53ef\u523a\u4e2d\u970d \u5143\u4f3d\u4e0a\u76d8\u3002 \u3000\u3000\u970d\u5143\u4f3d\u5927\u611f\u60ca\u614c\u4e4b\u4e0b\uff0e\u552f\u4e00\u7684\u529e\u6cd5\uff0c\u53ea\u6709\u677e\u624b\u5f03\u97ad\u3002 \u3000\u3000\u4e0d\u8fc7\uff0e\u4ed6\u6697\u5fd6\u867d\u7136\u6492\u624b\u5f03\u97ad\u662f\u79cd\u9887\u5931\u989c\u9762\u7684\u4e8b\uff0c\u4f46\u8fd9\u2014\u677e\u624b\uff0c\u81f3\u5c11\u4f1a\u5c06\u9ea6\u5c0f\u660e\u6454\u5f97\u4e03\u6655 \u516b\u7d20\uff0c\u80dc\u8005\u4e00\u65b9\uff0c\u7167\u6837\u8fd8\u662f\u5f52\u4e8e\u81ea\u5df1\u3002 \u3000\u3000\u5c82\u6599\u677e\u624b\u5f03\u97ad\u4e4b\u540e\uff0c\u9ea6\u5c0f\u660e\u60ac\u7a7a\u7ffb\u4e86\u4e24\u4e2a\u7b4b\u6597\uff0c\u7adf\u7136\u6beb\u53d1\u672a\u635f\u7684\u7ad9\u5728\u5bf9\u9762\u3002 \u3000\u3000\u597d\u5728\u970d\u5143\u4f3d\u5df2\u8d81\u77ac\u95f4\uff0c\u7ffb\u8155\u7531\u80a9\u62d4\u51fa\u4e86\u4ed6\u7684\u53e6\u4e00\u5175\u5668\u201c\u9752\u9f99\u593a\u201d\u3002 \u3000\u3000\u9752\u9f99\u593a\u662f\u4e00\u7c7b\u4f3c\u9e21\u722a\u7684\u5947\u5f62\u5175\u5203\uff0c\u653e\u773c\u5f53\u4eca\u6c5f\u6e56\uff0c\u53ea\u6709\u970d\u5143\u4f3d\u4f7f\u7528\u8fd9\u79cd\u72ec\u95e8\u5175\u5203\uff0c\u4e5f\u662f \u4ed6\u8d56\u4ee5\u6210\u540d\u7684\u8d81\u624b\u6b66\u5668\u3002 \u3000\u3000\u51e1\u662f\u548c\u4ed6\u4ea4\u8fc7\u624b\u7684\u9ed1\u767d\u4e24\u9053\u4eba\u7269\uff0c\u83ab\u4e0d\u5bf9\u4ed6\u8fd9\u9752\u9f99\u593a\u671b\u800c\u751f\u754f\u3002 \u3000\u3000\u970d\u5143\u4f3d\u628a\u9752\u9f99\u593a\u5728\u624b\u91cc\u6382\u4e86\u4e00\u6382\u9053\uff1a\u201c\u5c0f\u5b50\uff0e\u522b\u5f97\u610f\uff0c\u73b0\u5728\u8be5\u662f\u8981\u4f60\u547d\u7684\u65f6\u5019\u5566\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u5c11\u7f57\u55e6\uff0c\u73b0\u5728\u4e0d\u662f\u4f60\u8001\u738b\u5356\u74dc\uff0c\u81ea\u5356\u81ea\u5938\u5439\u5927\u725b\u7684\u65f6\u5019\uff01\u201d \u3000\u3000\u8fd9\u4e00\u6b21\u4e24\u4eba\u4ea4\u4e0a\u4e86\u624b\uff0c\u53ef\u8c13\u516b\u4ed9\u8fc7\u6d77\u2014\u2014\u5404\u663e\u795e\u901a\u3002 \u3000\u3000\u970d\u5143\u4f3d\u548c\u9ea6\u5c0f\u660e\u4e00\u8001\u4e00\u5c11\uff0c\u4e00\u4e2a\u662f\u529f\u529b\u6df1\u539a\uff0c\u51fa\u62db\u6c89\u7a33\u8f9b\u8fa3\uff1b\u4e00\u4e2a\u662f\u8840\u6c14\u65b9\u521a\uff0c\u624b\u6cd5\u8be1 \u5f02\u96be\u6d4b\u3002 \u3000\u3000\u4e24\u4eba\u4e00\u53e3\u6c14\u62fc\u640f\u4e86\u5c06\u8fd1\u767e\u62db\uff0c\u8d77\u521d\u8fd8\u662f\u5e73\u5206\u79cb\u8272\u4e4b\u5c40\uff0c\u4f46\u767e\u62db\u8fc7\u540e\uff0c\u970d\u5143\u4f3d\u5374\u5df2\u6e10\u611f\u4e0d \u652f\u3002 \u3000\u3000\u800c\u9ea6\u5c0f\u660e\u5219\u6108\u6218\u6108\u52c7\uff0c\u7740\u7740\u8fdb\u903c\uff0c\u903c\u5f97\u970d\u5143\u4f3d\u7adf\u6709\u4e9b\u624b\u5fd9\u811a\u4e71\u8d77\u6765\u3002 \u3000\u3000\u5176\u5b9e\uff0c\u4e24\u4eba\u82e5\u8bba\u529f\u529b\u7684\u6df1\u539a\uff0c\u9ea6\u5c0f\u660e\u7edd\u5bf9\u96be\u671b\u970d\u5143\u4f3d\u9879\u80cc\uff0c\u4f46\u970d\u5143\u4f3d\u56e0\u548c\u949f\u4e00\u8c6a\u5bf9\u9635 \u65f6\uff0c\u5185\u529b\u5df2\u6d88\u8017\u5927\u534a\uff0c\u6b64\u756a\u51fa\u624b\uff0c\u5df2\u6210\u5f3a\u5f29\u4e4b\u672b\uff0c\u518d\u52a0\u9ea6\u5c0f\u660e\u51fa\u62db\u4e0d\u6309\u724c\u7406\u51fa\u724c\uff0c\u5f53\u7136\u88ab\u8feb \u5f97\u96be\u4ee5\u62db\u67b6\u3002 \u3000\u3000\u6fc0\u6218\u4e2d\uff0e\u7a81\u95fb\u9ea6\u5c0f\u660e\u66b4\u543c\u4e00\u58f0\uff0e\u63a5\u7740\u5bd2\u8292\u2014\u655b\uff0c\u5251\u5c16\u76f4\u6307\u970d\u5143\u4f3d\u524d\u80f8\u3002 \u3000\u3000\u970d\u5143\u4f3d\u5374\u7ad9\u5728\u90a3\u91cc\uff0e\u52a8\u4e5f\u4e0d\u52a8\u3002 \u3000\u3000\u539f\u6765\u8fd9\u65f6\u970d\u5143\u4f3d\u7684\u9752\u9f99\u593a\uff0e\u5df2\u88ab\u9ea6\u5c0f\u660e\u4e0d\u77e5\u7528\u7684\u4ec0\u4e48\u624b\u6cd5\uff0c\u51fb\u843d\u5728\u5730\u4e0a\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u54c8\u54c8\u4e00\u7b11\u9053\uff1a\u201c\u8001\u5bb6\u4f19\uff0c\u73b0\u5728\u8fd8\u6709\u4ec0\u4e48\u8bdd\u8bf4\uff0c\u7eff\u6797\u76df\u4e3b\u8be5\u662f\u6211\u7684\u4e86\u5427\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u9762\u8272\u94c1\u9752\uff0c\u50cf\u50f5\u5c38\u822c\u7ad9\u5728\u539f\u5730\uff0e\u54ea\u91cc\u8fd8\u80fd\u8bf4\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u8f6c\u8eab\u5927\u6b65\u6765\u5230\u949f\u2014\u8c6a\u8eab\u524d\uff0c\u6de1\u6de1\u4e00\u7b11\u9053\uff1a\u201c\u949f\u8001\u5927\uff0c\u4f60\u770b\u8be5\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u949f\u4e00\u8c6a\u5f04\u5f97\u4f3c\u4e4e\u96be\u4ee5\u7b54\u8bdd\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u7b11\u9053\uff1a\u201c\u4f60\u522b\u7d27\u5f20\uff0c\u8fd9\u7eff\u6797\u76df\u4e3b\u7684\u5b9d\u5ea7\uff0e\u8fd8\u662f\u7531\u4f60\u5750\u7684\u597d\uff0c\u6211\u8d1f\u8d23\u4fdd\u4f60\u3002\u201d \u3000\u3000\u949f\u4e00\u8c6a\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u8981\u8ba9\u4f4d\u7ed9\u6211\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u6211\u5e74\u7eaa\u8f7b\uff0c\u8981\u505a\u7eff\u6797\u76df\u4e3b\uff0c\u5c06\u6765\u673a\u4f1a\u8fd8\u5f88\u591a\uff0c\u4f60\u7684\u5e74\u7eaa\u6bd4\u6211\u5927\uff0c\u667a\u8c0b\u4e5f\u6bd4 \u6211\u9ad8\uff0c\u6240\u4ee5\u6211\u65e9\u5c31\u60f3\u8fc7\uff0e\u5373\u4f7f\u5f97\u5230\u7eff\u6797\u76df\u4e3b\u4e4b\u4f4d\uff0e\u4e5f\u8981\u8ba9\u7ed9\u4f60\uff0c\u8fd9\u53eb\u2018\u4f60\u505a\u7687\u5e1d\u6211\u4fdd \u9a7e\u2019\u3002\u201d \u3000\u3000\u949f\u4e00\u8c6a\u4e3a\u4e86\u8868\u793a\u98ce\u5ea6\uff0c\u4e00\u76b1\u7709\u5934\u9053\uff1a\u201c\u8fd9\u7eff\u6797\u76df\u4e3b\u4e4b\u4f4d\u662f\u4f60\u81ea\u5df1\u4e89\u6765\u7684\uff0c\u8fd8\u662f\u4f60\u81ea\u5df1\u5750 \u7684\u597d\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4f60\u600e\u4e48\u8fd9\u6837\u7f57\u55e6\uff0c\u5176\u5b9e\u4eca\u5929\u6211\u80dc\u5f97\u5f88\u4fa5\u5e78\uff0c\u4f55\u51b5\u7531\u6211\u6765\u505a\u7eff\u6797\u76df\u4e3b\uff0c\u5fc5\u5b9a \u6709\u5f88\u591a\u4eba\u5fc3\u91cc\u4e0d\u670d\u3002\u201d \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u5ffd\u542c\u970d\u5143\u4f3d\u559d\u9053\uff1a\u201c\u5feb\u8fc7\u6765\u628a\u8fd9\u5c0f\u5b50\u62ff\u4e0b\uff01\u201d \u3000\u3000\u63a5\u7740\u4e00\u4e2a\u5e74\u5728\u4e09\u65ec\u5de6\u53f3\uff0c\u4e00\u8eab\u5929\u84dd\u957f\u886b\uff0c\u770b\u6765\u5341\u5206\u6587\u5f31\uff0e\u4f46\u6c14\u5ea6\u5374\u6781\u6c89\u7a33\u7684\u7537\u5b50\uff0c\u5df2\u4e00 \u95ea\u6765\u5230\u970d\u5143\u4f3d\u8eab\u4fa7\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u4e00\u89c1\u4e4b\u4e0b\uff0e\u4e0d\u89c9\u8138\u8272\u9aa4\u53d8\uff0c\u5931\u58f0\u53eb\u9053\uff1a\u201c\u5f20\u5e08\u5144\uff0c\u4f60\u600e\u4e48\u6765\u5230\u8fd9\u91cc\uff1f\u201d \u3000\u3000\u90a3\u7537\u5b50\u76ee\u5149\u5446\u6ede\uff0c\u53cc\u624b\u5404\u6263\u7740\u4e00\u53cc\u91d1\u5149\u707f\u70c2\u7684\u91d1\u5708\uff0c\u671b\u4e86\u9ea6\u5c0f\u660e\u4e00\u773c\uff0c\u4f3c\u4e4e\u6709\u4e9b\u4e0d\u76f8 \u4fe1\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u5fc3\u5934\u4e00\u9707\uff0e\u8fde\u5fd9\u53c8\u53eb\u9053\uff1a\u201c\u5f20\u5e08\u5144\uff0c\u83ab\u975e\u4f60\u5df2\u670d\u4e86\u9634\u624b\u9b54\u7684\u72ec\u95e8\u6bd2\u836f\u2018\u5411\u5fc3\u9732\u2019 \u4e86\uff1f\u201d \u3000\u3000\u90a3\u7537\u5b50\u4f9d\u7136\u5e76\u4e0d\u7b54\u8bdd\u3002 \u3000\u3000\u539f\u6765\u8fd9\u4eba\u53eb\u5f20\u656c\u5b89\uff0c\u548c\u9ea6\u5c0f\u660e\u540c\u662f\u795e\u6756\u7fc1\u9146\u79cb\u7684\u95e8\u4e0b\u5f1f\u5b50\uff0c\u8bba\u6b66\u529f\u6bd4\u9ea6\u5c0f\u660e\u53c8\u8981\u9ad8\u51fa\u4e00 \u7b79\u3002 \u3000\u3000\u53ea\u542c\u970d\u5143\u4f3d\u563f\u563f\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0e\u4ed6\u662f\u88ab\u8001\u592b\u704c\u4e0b\u4e86\u2018\u5411\u5fc3\u9732\u2019\uff0c\u4e00\u5207\u542c\u547d\u8001\u592b\u9a71\u4f7f\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u5927\u611f\u60ca\u8be7\u9053\uff1a\u201c\u2018\u5411\u5fc3\u9732\u2019\u662f\u9634\u624b\u4e00\u9b54\u7684\u72ec\u95e8\u6bd2\u836f\uff0c\u4f60\u662f\u600e\u4e48\u5f97\u5230\u7684\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u76ae\u7b11\u8089\u4e0d\u7b11\u9053\uff1a\u201c\u8001\u592b\u662f\u600e\u6837\u5f97\u5230\u2018\u5411\u5fc3\u9732\u2019\u7684\uff0c\u6ca1\u6709\u5fc5\u8981\u544a\u8bc9\u4f60\uff0c\u4f60\u53ea\u8981\u77e5\u9053 \u2018\u5411\u5fc3\u9732\u2019\u7684\u5389\u5bb3\u5c31\u6210\u4e86\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u5f53\u7136\u660e\u767d\u2018\u5411\u5fc3\u9732\u201d\u7684\u5389\u5bb3\uff0c\u4e0a\u6b21\u8c37\u5bd2\u9999\u4e3a\u4e86\u66ff\u592b\u62a5\u4ec7\uff0e\u66fe\u4ece\u9634\u624b\u2014\u9b54\u624b\u4e2d\u76d7\u6765 \u2018\u5411\u5fc3\u9732\u2019\u7ed9\u795e\u6756\u7fc1\u9146\u79cb\u704c\u4e0b\uff0c\u4f7f\u9146\u79cb\u8ff7\u5931\u672c\u6027\uff0e\u7518\u5fc3\u4e3a\u8c37\u5bd2\u9999\u5f79\u4f7f\uff0c\u76f4\u5230\u8c37\u5bd2\u9999\u6b7b\u524d\uff0c\u624d \u5f97\u5230\u89e3\u836f\u6062\u590d\u4e86\u7406\u667a\u3002 \u3000\u3000\u5982\u4eca\u5f20\u656c\u5b89\u4e5f\u88ab\u704c\u4e0b\u4e86\u2018\u5411\u5fc3\u9732\u2019\uff0c\u9ea6\u5c0f\u660e\u600e\u80fd\u4e0d\u5927\u611f\u9707\u61be\u3002 \u3000\u3000\u53ea\u542c\u970d\u5143\u4f3d\u51b7\u7b11\u8fde\u58f0\u9053\uff1a\u201c\u5c0f\u5b50\uff0c\u8fde\u4f60\u5e08\u7236\u9146\u79cb\u90fd\u65e0\u6cd5\u62b5\u6297\u2018\u5411\u5fc3\u9732\u2019\u7684\u8ff7\u6bd2\uff0c\u4f60\u8fd9\u5e08 \u5144\u5f20\u656c\u5b89\u5c31\u66f4\u4e0d\u7528\u63d0\u4e86\uff01\u73b0\u5728\u4f60\u8fd8\u6709\u4ec0\u4e48\u8bdd\u8bf4\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u518d\u671b\u4e86\u5f20\u656c\u5b89\u4e00\u773c\u9053\uff1a\u201c\u4f60\u8981\u628a\u4ed6\u600e\u6837\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u9634\u7b11\u9053\uff1a\u201c\u4f60\u8be5\u95ee\u8001\u592b\u51c6\u5907\u628a\u4f60\u600e\u4e48\u6837\u3002\u201d \u3000\u3000\u201c\u4f60\u8bf4\u8981\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u201c\u6211\u8981\u4ed6\u6740\u4e86\u4f60\uff0c\u770b\u8fd9\u7eff\u6797\u76df\u4e3b\u7684\u5b9d\u5ea7\uff0c\u5230\u5e95\u7531\u8c01\u6765\u5ea7\u3002\u201d\u9ea6\u5c0f\u660e\u987f\u65f6\u8d8a\u53d1\u4e0d\u5b89\u8d77\u6765\uff0c \u65b9\u624d\u6218\u8d25\u970d\u5143\u4f3d\u7684\u610f\u6ee1\u5fd7\u5f97\u4e4b\u60c5\uff0c\u9a6c\u4e0a\u6d88\u5931\u7684\u65e0\u5f71\u65e0\u8e2a\u3002 \u3000\u3000\u53ea\u89c1\u970d\u5143\u4f3d\u671b\u7740\u5f20\u656c\u5b89\u9053\uff1a\u201c\u8001\u6123\u5728\u90a3\u91cc\u505a\u4ec0\u4e48\uff1f\u8fd8\u4e0d\u5feb\u5feb\u628a\u8fd9\u5c0f\u5b50\u6536\u62fe\u6389\uff01\u201d \u3000\u3000\u5f20\u656c\u5b89\u53cc\u76ee\u5446\u6ede\u7684\u7728\u4e86\u51e0\u7728\uff0c\u7acb\u523b\u8dc3\u8eab\u2014\u6251\uff0c\u53cc\u73af\u9f50\u51fa\uff0e\u76f4\u5411\u9ea6\u5c0f\u660e\u653b\u53bb\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u56e0\u89c1\u5bf9\u65b9\u662f\u81ea\u5df1\u5e08\u5144\uff0e\u4e0d\u613f\u81ea\u76f8\u6b8b\u6740\uff0c\u6025\u6025\u628a\u8eab\u95ea\u5411\u4e00\u4fa7\uff0c\u4e00\u9762\u5927\u53eb\u9053\uff1a\u201c\u5f20\u5e08 \u5144\uff0c\u54b1\u4eec\u662f\u81ea\u5df1\u4eba\uff0e\u4f60\u522b\u53d7\u90a3\u8001\u5bb6\u4f19\u7684\u6446\u5e03\uff01\u201d \u3000\u3000\u987b\u77e5\u2018\u5411\u5fc3\u9732\u2019\u53ea\u80fd\u4f7f\u4eba\u8ff7\u5c16\u672c\u6027\uff0c\u670d\u95ed\u4e4b\u4eba\u5728\u6b66\u529f\u4e0a\u5374\u5e76\u4e0d\u7a0d\u51cf\uff0c \u3000\u3000\u9ea6\u5c0f\u660e\u8fd9\u4e00\u6218\u5df2\u65e0\u6cd5\u907f\u514d\uff0c\u4e5f\u5c31\u53ea\u597d\u62a1\u5251\u8fce\u51fb\uff0c\u5e08\u5144\u5f1f\u4e8c\u4eba\uff0e\u7acb\u65f6\u5c55\u5f00\u2014\u573a\u6fc0\u70c8\u640f\u6740\u3002 \u3000\u3000\u8d77\u521d\u9ea6\u5c0f\u660e\u96be\u514d\u4ecd\u6709\u4e9b\u987e\u5fcc\uff0c\u751f\u6015\u4f24\u4e86\u5bf9\u65b9\u3002 \u3000\u3000\u4f46\u5f20\u656c\u5b89\u5374\u59cb\u7ec8\u6ca1\u6709\u8fd9\u79cd\u5fc3\u7406\u538b\u529b\uff0c\u4ed6\u7684\u6b66\u529f\uff0e\u672c\u6765\u5c31\u6bd4\u9ea6\u5c0f\u660e\u7565\u80dc\u4e00\u7b79\uff0c\u5728\u5168\u529b\u65bd\u5c55 \u4e4b\u4e0b\uff0c\u5f88\u5feb\u4fbf\u903c\u4f7f\u9ea6\u5c0f\u660e\u843d\u5165\u4e0b\u98ce\u3002 \u3000\u3000\u4e24\u4eba\u8f6c\u77ac\u95f4\u5bf9\u62c6\u4e86\u767e\u62db\u4ee5\u4e0a\u3002 \u3000\u3000\u6b64\u65f6\uff0e\u9ea6\u5c0f\u660e\u5df2\u9000\u5230\u949f\u4e00\u8c6a\u548c\u4f59\u4ea6\u4e50\u7b49\u4eba\u8eab\u4fa7\uff0c\u903c\u5f97\u949f\u4e00\u8c6a\u548c\u4f59\u4ea6\u4e50\u7b49\u4eba\u90fd\u7eb7\u7eb7\u5411\u2014\u4fa7 \u95ea\u5f00\u3002 \u3000\u3000\u53c8\u7f20\u6597\u4e86\u4e94\u516d\u5341\u62db\u540e\uff0c\u4f59\u4ea6\u4e50\u770b\u51fa\u9ea6\u5c0f\u660e\u5df2\u6e10\u611f\u4e0d\u652f\uff0c\u53ea\u5f97\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u5c0f\u660e\u5feb\u5feb\u9000 \u4e0b\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u4f9d\u8a00\u5411\u540e\u98d8\u9000\u4e24\u4e08\u6709\u4f59\u3002 \u3000\u3000\u5f20\u656c\u5b89\u5e76\u672a\u8ffd\uff0c\u4e5f\u968f\u5373\u505c\u4e0b\u6765\uff0c\u75f4\u75f4\u5446\u5446\u5730\u7ad9\u5728\u539f\u5730\u4e0d\u52a8\u3002 \u3000\u3000\u970d\u5143\u4f3d\u5927\u6b65\u5411\u524d\uff0c\u624b\u62c2\u957f\u9aef\uff0e\u5475\u5475\u7b11\u9053\uff1a\u201c\u4ed6\u4eec\u4e24\u4eba\u6b63\u5230\u80dc\u8d1f\u7acb\u5206\u7684\u5173\u5934\uff0c\u4f59\u5148\u751f\u5ffd\u7136 \u51fa\u58f0\u559d\u6b62\uff0e\u4e0d\u77e5\u662f\u4f55\u7528\u5fc3\uff1f\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u9053\uff1a\u201c\u4eca\u65e5\u4e4b\u5c40\uff0e\u8be5\u662f\u970d\u526f\u76df\u4e3b\u548c\u949f\u5144\u4e92\u4e89\u76df\u4e3b\u4e4b\u4f4d\uff0c\u4ed6\u4eec\u4e24\u4eba\u7eb5\u7136\u62fc\u4e2a\u4f60\u6b7b\u6211 \u6d3b\uff0c\u4e5f\u4e8e\u4e8b\u65e0\u76ca\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u68ee\u68ee\u4e00\u7b11\u9053\uff1a\u201c\u90a3\u5f88\u597d\uff0c\u8001\u592b\u60c5\u613f\u7ee7\u7eed\u548c\u949f\u4e00\u8c6a\u51b3\u4e00\u96cc\u96c4\uff01\u201d \u3000\u3000\u5ffd\u542c\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u4e0d\u5fc5\u4e86\uff0c\u5728\u4e0b\u60c5\u613f\u628a\u7eff\u6797\u76df\u4e3b\u4e4b\u4f4d\uff0c\u8ba9\u4e0e\u970d\u526f\u76df\u4e3b\uff01\u201d \u3000\u3000\u5f53\u65e5\u665a\u996d\u540e\u3002 \u3000\u3000\u949f\u4e00\u8c6a\uff0e\u4f59\u4ea6\u4e50\uff0e\u9ea6\u5c0f\u660e\uff0c\u6587\u5929\u751f\uff0c\u82d7\u7d20\u5170\u3001\u4e07\u6620\u971e\u4ee5\u53ca\u6c5f\u5317\u56db\u9f99\u4e2d\u7684\u98de\u5929\u9f99\u4f55\u5b97\u8f89\u3001 \u591a\u722a\u9f99\u674e\u6770\uff0c\u55b7\u706b\u9f99\u5218\u9707\uff0c\u6b63\u96c6\u4e00\u5ba4\u5728\u5bc6\u8bae\u5927\u4e8b\u3002 \u3000\u3000\u53ea\u542c\u98de\u5929\u9f99\u4f55\u5b97\u8f89\u9053\uff1a\u201c\u526f\u76df\u4e3b\uff0c\u4f60\u4e3a\u4ec0\u4e48\u5e73\u767d\u7b54\u5e94\uff0c\u628a\u76df\u4e3b\u4e4b\u4f4d\u8ba9\u7ed9\u970d\u5143\u4f3d\u90a3\u8001\u738b\u516b \u86cb\uff1f\u201d \u3000\u3000\u949f\u4e00\u8c6a\u957f\u957f\u5401\u53e3\u6c14\u9053\uff1a\u201c\u5f53\u65f6\u7684\u60c5\u52bf\u5f88\u660e\u663e\uff0c\u5c0f\u660e\u5144\u5f1f\u548c\u5f20\u656c\u5b89\u82e5\u518d\u6218\u4e0b\u53bb\uff0e\u5fc5\u5b9a\u843d\u8d25 \u65e0\u7591\uff0e\u5373\u4f7f\u6211\u4e0d\u8ba9\uff0c\u4e5f\u96be\u4ee5\u633d\u56de\u5927\u5c40\u3002\u201d \u3000\u3000\u4f55\u5b97\u8f89\u8fd8\u662f\u6709\u4e9b\u4e0d\u670d\u6c14\u9053\uff1a\u201c\u5373\u4fbf\u9ea6\u5c0f\u660e\u843d\u8d25\uff0c\u6211\u4eec\u8fd9\u4e9b\u4eba\u7167\u6837\u8fd8\u53ef\u4ee5\u518d\u6218\uff0c\u7edd\u5bf9\u4e0d\u80fd \u628a\u76df\u4e3b\u4e4b\u4f4d\u62f1\u624b\u8ba9\u4eba\uff01\u201d \u3000\u3000\u949f\u4e00\u8c6a\u51c4\u51c9\u4e00\u7b11\u9053\uff1a\u201c\u8fd9\u662f\u4f60\u7684\u60f3\u6cd5\uff0c\u5176\u5b9e\u54b1\u4eec\u8c01\u90fd\u5e94\u8be5\u660e\u767d\uff0c\u76ee\u524d\u8c1c\u8e2a\u8c37\u7684\u4e0a\u5343\u4eba \u4e2d\uff0c\uff0c\u53ef\u8c13\u7edd\u5927\u591a\u6570\u90fd\u662f\u970d\u5143\u4f3d\u7684\u5fc3\u8179\u3002 \u3000\u3000\u5373\u4fbf\u970d\u5143\u4f3d\u628a\u76df\u4e3b\u4e4b\u4f4d\u8ba9\u7ed9\u54b1\u4eec\uff0e\u54b1\u4eec\u4e5f\u7167\u6837\u96be\u4ee5\u53d1\u53f7\u65bd\u4ee4\u3002\u201d \u3000\u3000\u4f55\u5b97\u8f89\u54fc\u4e86\u58f0\u9053\uff1a\u201c\u96be\u9053\u526f\u76df\u4e3b\u5c31\u81ea\u5c48\u4e8e\u970d\u5143\u4f3d\u90a3\u738b\u516b\u86cb\u4e4b\u4e0b\uff1f\u201d \u3000\u3000\u949f\u4e00\u8c6a\u4e0d\u52a8\u58f0\u8272\u7684\u6447\u6447\u5934\u9053\uff1a\u201c\u4e0d\u52b3\u4f60\u4e3a\u6211\u591a\u8651\uff0c\u6211\u5df2\u6709\u4e86\u6253\u7b97\u3002\u201d \u3000\u3000\u4f55\u5b97\u8f89\u9053\uff1a\u201c\u526f\u76df\u4e3b\u6709\u4ec0\u4e48\u6253\u7b97\uff1f\u201d \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u6211\u5df1\u51b3\u5b9a\u5728\u970d\u5143\u4f3d\u660e\u5929\u5c31\u4f4d\u4e4b\u524d\uff0c\u79bb\u5f00\u8fd9\u8ff7\u8e2a\u8c37\uff0c\u4ece\u6b64\u4e0d\u518d\u8fc7\u95ee\u8fd9\u91cc\u7684 \u4e8b\u3002\u201d \u3000\u3000\u6b64\u8bed\u4e00\u51fa\uff0c\u5728\u573a\u6240\u6709\u7684\u4eba\uff0c\u4e2a\u4e2a\u5927\u60ca\u5931\u8272\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u62a2\u7740\u5927\u58f0\u9053\uff1a\u201c\u949f\u8001\u5927\uff0e\u4f60\u4e0d\u80fd\u8d70\uff0e\u4f60\u82e5\u4e00\u8d70\uff0c\u8c01\u6765\u9886\u5bfc\u6211\u4eec\uff1f\u201d \u3000\u3000\u4f55\u5b97\u8f89\u4e5f\u8fde\u5fd9\u63a5\u9053\uff1a\u201c\u526f\u76df\u4e3b\u4e0d\u80fd\u8d70\uff0c\u8fd8\u6709\u4e00\u4ef6\u5927\u4e8b\uff0e\u5fc5\u987b\u7531\u4f60\u51fa\u9762\u3002\u201d \u3000\u3000\u949f\u4e00\u8c6a\u54e6\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\u5fc5\u987b\u7531\u6211\u51fa\u9762\uff1f\u201d \u3000\u3000\u4f55\u5b97\u8f89\u9053\uff1a\u201c\u6211\u4eec\u59dc\u8001\u5927\u5728\u5929\u53f0\u5c71\u65f6\uff0e\u5949\u547d\u5148\u884c\u56de\u8c1c\u8e2a\u8c37\u63a5\u5e94\uff0c\u4eca\u5929\u5374\u2014\u4e0b\u6ca1\u4e86\u4ed6\u51fa \u9762\uff0c\u53ef\u80fd\u5df3\u88ab\u970d\u5143\u4f3d\u6263\u62bc\uff0c\u6216\u8005\u88ab\u4ed6\u4e0b\u4e86\u6bd2\u624b\uff0c\u4f60\u603b\u8be5\u548c\u970d\u5143\u4f3d\u4ea4\u6d89\uff0c\u628a\u4ed6\u6551\u51fa\u6765\u624d\u6210\u3002 \u3000\u3000\u539f\u6765\u6c5f\u5317\u56db\u9f99\u539f\u4e3a\u6c5f\u5317\u4e94\u9f99\uff0c\u4f9d\u5e8f\u662f\u8001\u5927\u51fa\u4e91\u9f99\u59dc\u5b8f\u3001\u8001\u4e8c\u5165\u4e91\u9f99\u94b1\u67c4\u3001\u8001\u4e09\u98de\u5929\u9f99\u4f55 \u5b97\u8f89\u3001\u8001\u56db\u591a\u722a\u9f99\u674e\u6770\u3001\u8001\u4e94\u55b7\u706b\u9f99\u5218\u9707\u3002 \u3000\u3000\u53ea\u56e0\u8001\u4e8c\u5165\u4e91\u9f99\u94b1\u67c4\uff0c\u4e24\u5e74\u524d\u88ab\u5f20\u656c\u5b89\u8bef\u6740\u3001\u662f\u4ee5\u76ee\u524d\u53ea\u5269\u4e0b\u56db\u9f99\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u4e00\u695e\u9053\uff1a\u201c\u4f59\u5144\u6709\u4f55\u89c1\u6559\uff1f\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u9053\uff1a\u201c\u8fd9\u8ff7\u8e2a\u8c37\u662f\u80e1\u524d\u76df\u4e3b\u4e00\u624b\u521b\u7acb\u7684\u57fa\u4e1a\uff0c\u540e\u6765\u53c8\u7531\u8c37\u524d\u76df\u4e3b\u7ee7\u76df\u4e3b\u4e4b\u4f4d\uff0c\u54b1 \u4eec\u90fd\u662f\u8877\u5fc3\u62e5\u6234\u80e1\u3001\u8c37\u4e24\u4f4d\u76df\u4e3b\u7684\u4eba\uff0c\u600e\u53ef\u7f6e\u4ed6\u4eec\u624b\u521b\u7684\u57fa\u4e1a\u4e8e\u4e0d\u987e\u3002\u201d \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u53ef\u662f\uff0c\u76ee\u524d\u5927\u6743\u5df2\u843d\u5165\u970d\u5143\u4f3d\u4e4b\u624b\uff0c\u54b1\u4eec\u8fd8\u6709\u4ec0\u4e48\u80fd\u529b\u91cd\u6574\u8fd9\u80e1\u3001\u8c37\u4e24\u4f4d \u76df\u4e3b\u7684\u57fa\u4e1a\uff1f\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u6de1\u6de1\u2014\u7b11\u9053\uff1a\u201c\u949f\u5144\u8fd9\u8bdd\u9519\u5566\uff01\u5927\u4e08\u592b\u80fd\u5c48\u80fd\u4f38\uff0c\u53ea\u8981\u54b1\u4eec\u80fd\u5fcd\u8fb1\u8d1f\u91cd\uff0c\u4f55\u6101\u4e0d \u80fd\u628a\u7eff\u6797\u76df\u4e3b\u4e4b\u4f4d\uff0c\u7531\u970d\u5143\u4f3d\u624b\u4e2d\u593a\u56de\u3002\u201d \u3000\u3000\u201c\u4f59\u5144\u8bf7\u9053\u5176\u8be6\u3002\u201d \u3000\u3000\u201c\u5927\u5bb6\u90fd\u77e5\u9053\uff0c\u5728\u8ff7\u8e2a\u8c37\u5185\uff0c\u5f53\u5e74\u5fe0\u5fc3\u62e5\u6234\u80e1\u524d\u76df\u4e3b\u7684\u4eba\u4e0d\u5c11\uff0c\u4f46\u5728\u80e1\u524d\u76df\u4e3b\u6b7b\u540e\uff0c\u5374 \u51e0\u4e4e\u5168\u88ab\u8c37\u524d\u76df\u4e3b\u9063\u6563\u3002\u54b1\u4eec\u5c31\u5c06\u8fd9\u4e9b\u4eba\u8bbe\u6cd5\u53ec\u96c6\u56de\u6765\uff0c\u9f99\u5176\u5d02\u5c71\u4e09\u96c4\uff0c\u4e2a\u4e2a\u6b66\u529f\u9ad8\u4e0d\u53ef \u6d4b\uff0c\u82e5\u4ed6\u4eec\u56de\u6765\u4ee5\u540e\uff0e\u4f55\u6101\u970d\u5143\u4f3d\u4e0d\u62f1\u624b\u8ba9\u4f4d\u3002\u201d \u3000\u3000\u949f\u2014\u8c6a\u4f3c\u662f\u5fc3\u6709\u6240\u52a8\uff0c\u7565\u4e00\u6c89\u541f\u9053\uff1a\u201c\u4f59\u5144\u80fd\u5426\u8bbe\u6cd5\u628a\u5f20\u656c\u5b89\u8fd9\u4eba\u9664\u53bb\uff01\u201d \u3000\u3000\u53ea\u542c\u9ea6\u5c0f\u660e\u5927\u58f0\u9053\uff1a\u201c\u4e0d\u884c\uff01\u5f20\u656c\u5b89\u662f\u6211\u5e08\u5144\uff0c\u4e0d\u80fd\u9664\u53bb\uff01\u201d \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u82e5\u4e0d\u80fd\u9664\u53bb\uff0c\u5c31\u5fc5\u987b\u8bbe\u6cd5\u627e\u5230\u89e3\u836f\uff0c\u9634\u624b\u4e00\u9b54\u5c31\u4f4f\u5728\u5415\u6881\u5c71\uff0c\u79bb\u5317\u5cb3\u4e0d\u8fc7 \u65ec\u65e5\u8def\u7a0b\uff0c\u73b0\u5728\u7684\u95ee\u9898\u662f\u4e0d\u6613\u627e\u5230\u4e00\u4e2a\u548c\u9634\u624b\u4e00\u9b54\u6709\u4ea4\u60c5\u7684\u4eba\u3002\u201d \u3000\u3000\u949f\u4e00\u8c6a\u4e0d\u89c9\u671b\u5411\u82d7\u7d20\u5170\u3002 \u3000\u3000\u539f\u6765\u82d7\u7d20\u5170\u66fe\u662f\u9634\u624b\u4e00\u9b54\u7684\u95e8\u4e0b\u5f1f\u5b50\uff0c\u53ea\u56e0\u7231\u6155\u80e1\u67cf\u9f84\u7684\u76d6\u4e16\u82f1\u8c6a\uff0c\u624d\u53db\u79bb\u5e08\u95e8\uff0e\u6539\u6295 \u80e1\u67cf\u9f84\u968f\u4f8d\u8c37\u5bd2\u9999\uff0c\u6210\u4e86\u8c37\u5bd2\u9999\u7684\u83ab\u9006\u4e4b\u4ea4\u3002 \u3000\u3000\u4f59\u4ea6\u4e50\u7acb\u523b\u6447\u5934\u9053\uff1a\u201c\u949f\u5144\u53ef\u662f\u60f3\u8981\u82d7\u59d1\u5a18\u524d\u5f80\u5415\u6881\u5c71\uff0c\u5411\u9634\u624b\u2014\u9b54\u6c42\u53d6\u2018\u5411\u5fc3\u9732\u2019\u89e3 \u836f\uff1f\u201d \u3000\u3000\u949f\u4e00\u8c6a\u64a4\u56de\u76ee\u5149\u9053\uff1a\u201c\u9664\u4e86\u82d7\u59d1\u5a18\uff0c\u6211\u5b9e\u5728\u60f3\u4e0d\u51fa\u7b2c\u4e8c\u4e2a\u4eba\u3002\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u6447\u5934\u9053\uff1a\u201c\u4f60\u53c8\u9519\u5566\uff01\u9634\u624b\u4e00\u9b54\u65e9\u5df2\u628a\u82d7\u59d1\u5a18\u6068\u4e4b\u5165\u9aa8\uff0c\u82e5\u7531\u82d7\u59d1\u5a18\u5230\u5415\u6881\u5c71\uff0c \u90a3\u5c31\u6709\u53bb\u65e0\u56de\u4e86\uff01\u201d \u3000\u3000\u53ea\u542c\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u6211\u53bb\uff01\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u9053\uff1a\u201c\u4f60\u8ddf\u9634\u624b\u4e00\u9b54\u53ef\u6709\u4ea4\u60c5\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u201c\u90a3\u4ed6\u600e\u4e48\u4f1a\u7ed9\u4f60\u89e3\u836f\uff1f\u201d \u3000\u3000\u201c\u6211\u53ef\u4ee5\u8ddf\u4ed6\u8981\uff0e\u96be\u9053\u8ba8\u996d\u7684\u8fd8\u8981\u8ddf\u88ab\u8ba8\u7684\u6709\u4ea4\u60c5\uff1f\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u7b11\u9053\uff1a\u201c\u987b\u77e5\u9634\u624b\u4e00\u9b54\u7684\u2018\u5411\u5fc3\u9732\u2019\u548c\u89e3\u836f\uff0c\u662f\u4ed6\u8017\u8d39\u5927\u534a\u751f\u5149\u9634\u624d\u7814\u5236\u51fa\u6765 \u7684\uff0c\u4ed6\u628a\u8fd9\u4e24\u79cd\u836f\u7269\u89c6\u540c\u751f\u547d\uff0c\u5c82\u80af\u8f7b\u6613\u65bd\u820d\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u8f6c\u4e86\u8f6c\u773c\u73e0\u9053\uff1a\u201c\u4ed6\u82e5\u4e0d\u7ed9\uff0c\u6211\u5c31\u786c\u8981\uff01\u201d \u3000\u3000\u201c\u4ec0\u4e48\u53eb\u786c\u8981\uff1f\u201d \u3000\u3000\u201c\u786c\u8981\u5c31\u662f\u548c\u4ed6\u5e72\u4e0a\u4e00\u67b6\u3002\u201d \u3000\u3000\u201c\u4f60\u81ea\u4fe1\u80fd\u6253\u5f97\u8fc7\u4ed6\uff1f\u201d \u3000\u3000\u201c\u82e5\u6253\u4ed6\u4e0d\u8fc7\uff0c\u6211\u5c31\u7528\u5077\u7684\uff0c\u529e\u6cd5\u591a\u5f97\u5f88\uff0c\u4e0d\u4fe1\u6d3b\u4eba\u4f1a\u88ab\u5c3f\u618b\u6b7b\uff01\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u70b9\u4e86\u70b9\u5934\u9053\uff1a\u201c\u597d\uff0c\u90a3\u5c31\u7531\u4f60\u53bb\u8ba8\u89e3\u836f\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u6ee1\u9762\u5f97\u610f\u4e4b\u8272\uff0e\u518d\u9053\uff1a\u201c\u662f\u5426\u73b0\u5728\u5c31\u8981\u52a8\u8eab\uff1f\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u9053\uff1a\u201c\u4e0d\u5fc5\u6025\u5728\u4e00\u65f6\uff0c\u7b49\u660e\u5929\u53c2\u52a0\u8fc7\u970d\u5143\u4f3d\u7684\u5c31\u804c\u5927\u5178\u4e0d\u8fdf\uff0c\u5426\u5219\u660e\u5929\u4f60\u4e0d\u5728 \u573a\uff0c\u5f88\u5bb9\u6613\u5f15\u8d77\u970d\u5143\u4f3d\u7684\u7591\u5fc3\uff0c\u5bf9\u4f60\u548c\u6211\u4eec\u73b0\u5728\u573a\u7684\u4eba\uff0e\u90fd\u5927\u5927\u4e0d\u5229\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u51b7\u54fc\u4e00\u58f0\u9053\uff1a\u201c\u6211\u5c31\u662f\u4e0d\u60f3\u89c1\u90a3\u8001\u738b\u516b\u86cb\u5c31\u804c\u65f6\uff0c\u626c\u626c\u5f97\u610f\u7684\u718a\u6837\uff0e\u6240\u4ee5\u624d\u8981\u73b0 \u5728\u52a8\u8eab\uff01\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u62cd\u62cd\u9ea6\u5c0f\u660e\u80a9\u8180\u9053\uff1a\u201c\u5c0f\u4e0d\u5fcd\u5219\u4e71\u5927\u8c0b\uff0e\u8fd8\u662f\u5fcd\u8010\u4e9b\u7684\u597d\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9ed8\u4e86\u4e00\u9ed8\u9053\uff1a\u201c\u597d\u5427\uff0c\u6211\u542c\u4f60\u7684\uff0e\u4e0d\u8fc7\u6211\u8fd8\u6709\u2014\u4e2a\u8981\u6c42\u3002\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u9053\uff1a\u201c\u4ec0\u4e48\u8981\u6c42\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u671b\u4e86\u82d7\u7d20\u5170\u2014\u773c\u9053\uff1a\u201c\u8981\u82d7\u59d0\u59d0\u966a\u6211\u4e00\u9053\u53bb\uff0e\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u8981\u82d7\u59d1\u5a18\u4e00\u8d77\u53bb\uff1f\u201d \u3000\u3000\u201c\u5415\u6881\u5c71\u90a3\u4e48\u5927\uff0e\u60f3\u627e\u5230\u9634\u624b\u4e00\u9b54\u7684\u4f4f\u5904\uff0c\u4e0d\u662f\u4ef6\u5bb9\u6613\u7684\u4e8b\uff0c\u82d7\u59d0\u59d0\u65e2\u7136\u505a\u8fc7\u9634\u624b\u2014\u9b54 \u7684\u95e8\u4e0b\uff0e\u7531\u5979\u5e26\u8def\uff0c\u81ea\u7136\u4e8b\u60c5\u529e\u7684\u4f1a\u5feb\u4e00\u4e9b\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u82d7\u59d1\u5a18\u4e0d\u6562\u89c1\u9634\u624b\u2014\u9b54\u3002\u201d \u3000\u3000\u201c\u5979\u7528\u4e0d\u7740\u89c1\uff0c\u53ea\u8981\u628a\u6211\u5e26\u5230\u5730\u70b9\uff0c\u5979\u5728\u5916\u9762\u7b49\uff0e\u6211\u5355\u72ec\u8fdb\u53bb\u89c1\u9634\u624b\u4e00\u9b54\u5c31\u53ef\u3002\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u8f6c\u5411\u82d7\u7d20\u5170\u95ee\u9053\uff1a\u201c\u8fd9\u529e\u6cd5\u82d7\u59d1\u5a18\u662f\u5426\u540c\u610f\uff1f\u201d \u3000\u3000\u82d7\u7d20\u5170\u9053\uff1a\u201c\u65e2\u7136\u9ea6\u5c0f\u5144\u5f1f\u8981\u6211\u5e26\u8def\uff0e\u6211\u5c31\u966a\u4ed6\u53bb\u2014\u8d9f\u3002\u201d \u3000\u3000\u4f17\u4eba\u53c8\u8ba1\u8bae\u4e86\u4e00\u9635\uff0e\u624d\u5404\u81ea\u6563\u53bb\uff0e\u56de\u623f\u5c31\u5bdd\u3002 \u5367\u9f99\u751f\u300a\u5999\u7edd\u5929\u9999\u300b \u7b2c \u56db \u56de\u3000\u5367\u85aa\u5c1d\u80c6 \u3000\u3000\u6574\u4e2a\u8ff7\u8e2a\u8c37\u5185\u5728\u4e0a\u5343\u4eba\u4e2d\uff0c\u76ee\u524d\u53ea\u6709\u4e24\u4e2a\u5973\u4eba\uff0c\u4e00\u4e2a\u662f\u6c5f\u6e56\u9605\u5386\u6781\u6df1\u7684\u82d7\u7d20\u5170\uff0c\u3000\u4e00\u4e2a \u662f\u5c0f\u59d1\u72ec\u5904\u7684\u4e07\u6620\u971e\u3002 \u3000\u3000\u5979\u4eec\u4ecd\u5728\u539f\u6765\u7684\u5c45\u5ba4\u3002 \u3000\u3000\u8fd9\u91cc\u662f\u6797\u836b\u4e2d\uff0c\u6709\u4e00\u5e62\u72ec\u95e8\u72ec\u9662\u7684\u5b85\u7b2c\uff0c\u5b85\u95e8\u5916\u7eff\u67f3\u6210\u836b\uff0c\u4e00\u5ead\u9662\u5185\u4fee\u7af9\u538b\u6a90\uff0e\u770b\u6765\u8d8a \u53d1\u9ad8\u96c5\u5e7d\u9759\u3002 \u3000\u3000\u539f\u6765\u8fd9\u5904\u5ead\u9662\uff0c\u6b63\u662f\u8c37\u5bd2\u9999\u751f\u524d\u6240\u5728\uff0e\u82d7\u7d20\u5170\u548c\u4e07\u6620\u971e\u56e0\u4e3a\u90fd\u662f\u8c37\u5bd2\u9999\u6700\u4eb2\u8fd1\u4e4b\u4eba\uff0c\u6240 \u4ee5\u4e5f\u4f4f\u8fd9\u91cc\u3002 \u3000\u3000\u970d\u5143\u4f3d\u867d\u662f\u63d0\u524d\u7531\u5929\u53f0\u5c71\u8d76\u56de\uff0c\u5728\u4e0d\u4fbf\u8fc7\u4efd\u66b4\u9732\u91ce\u5fc3\u7684\u60c5\u5f62\u4e0b\uff0c\u4ed6\u5e76\u672a\u6d3e\u4eba\u8fdb\u5165\u5360\u4f4f\u3002 \u3000\u3000\u4ece\u524d\u8c37\u5bd2\u9999\u4f4f\u8fd9\u91cc\u65f6\uff0c\u8fd8\u6709\u56db\u540d\u4e2b\u73af\uff0c\u7531\u4e8e\u4eba\u591a\uff0c\u518d\u52a0\u4e0a\u6709\u4e2a\u4eba\u89c1\u4eba\u7231\u7684\u7fce\u513f\uff0c\u663e\u5f97\u5e76 \u4e0d\u5bc2\u5bde\u3002 \u3000\u3000\u5982\u4eca\u53ea\u5269\u4e0b\u82d7\u7d20\u5170\u548c\u4e07\u6620\u971e\uff0c\u96be\u514d\u6709\u4e9b\u5b64\u5355\uff0c\u4e24\u4eba\u7761\u4e0d\u7740\uff0c\u76f4\u804a\u5230\u5c06\u8fd1\u4e8c\u66f4\uff0c\u624d\u5404\u81ea\u56de \u623f\u4f11\u606f\u3002 \u3000\u3000\u82d7\u7d20\u5170\u95ed\u4e0a\u623f\u95e8\uff0c\u4e3a\u9632\u591c\u95f4\u6709\u52a8\u9759\uff0c\u53ea\u662f\u5408\u8863\u800c\u5367\u3002 \u3000\u3000\u521a\u6726\u80e7\u7761\u53bb\uff0c\u7a81\u95fb\u7a97\u5916\u53d1\u51fa\u811a\u6b65\u58f0\u54cd\uff0c\u4f3c\u662f\u6709\u4eba\u6b63\u5411\u7a97\u4e0b\u63a5\u8fd1\u3002 \u3000\u3000\u82d7\u7d20\u5170\u89c9\u51fa\u4e0d\u5bf9\uff0c\u7acb\u5373\u7531\u5e8a\u4e0a\u8dc3\u8eab\u800c\u8d77\uff0c\u6b63\u8981\u51fa\u58f0\u559d\u95ee\uff0c\u5374\u5ffd\u611f\u5589\u5934\u5e72\u6da9\uff0c\u63a5\u7740\u4e00\u80a1\u5f02 \u9999\uff0c\u6251\u9f3b\u800c\u6765\u3002 \u3000\u3000\u968f\u7740\u5fc3\u795e\u604d\u60da\uff0e\u4e00\u9635\u5934\u6655\u773c\u82b1\uff0c\u51e0\u81f3\u5931\u53bb\u77e5\u89c9\u3002 \u3000\u3000\u7d27\u63a5\u7740\uff0e\u6709\u4e24\u540d\u9ed1\u8863\u4eba\u7834\u7a97\u800c\u5165\u3002 \u3000\u3000\u6b64\u523b\u82d7\u7d20\u5170\u6839\u672c\u558a\u53eb\u4e0d\u51fa\uff0c\u4f46\u5979\u8fd8\u662f\u62fc\u529b\u7684\u53eb\u9053\uff1a\u201c\u4f60\u4eec\u662f\u4ec0\u4e48\u4eba\uff0c\u6765\u8fd9\u91cc\u60f3\u505a\u4ec0 \u4e48\uff1f\u201d \u3000\u3000\u5979\u867d\u7136\u7528\u5c3d\u6c14\u529b\uff0c\u8bf4\u51fa\u8fd9\u4e24\u53e5\uff0e\u58f0\u97f3\u5374\u5fae\u5f31\u5f97\u5f88\uff0c\u6839\u672c\u65e0\u6cd5\u60ca\u9192\u4f4f\u5728\u53e6\u4e00\u623f\u95f4\u7684\u4e07\u6620 \u971e\u3002 \u3000\u3000\u5979\u54ea\u91cc\u77e5\u9053\uff0c\u4f4f\u5728\u53e6\u4e00\u623f\u95f4\u7684\u4e07\u6620\u971e\u65e9\u5df2\u88ab\u4e24\u540d\u9ed1\u8863\u4eba\u7528\u85b0\u9999\uff0c\u5148\u884c\u85b0\u660f\u8fc7\u53bb\u3002 \u3000\u3000\u53ea\u542c\u4e00\u540d\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u5148\u628a\u5979\u70b9\u4e86\u7a74\u9053\u518d\u8bf4\uff0c\u514d\u5f97\u51fa\u95e8\u65f6\u88ab\u4eba\u6697\u4e2d\u770b\u51fa\u7834\u7efd\u3002\u201d \u3000\u3000\u53e6\u4e00\u9ed1\u8863\u4eba\u7acb\u5373\u51fa\u624b\uff0c\u70b9\u4e86\u82d7\u7d20\u5170\u7684\u7a74\u9053\uff0c\u5e76\u628a\u5979\u67b6\u4e0b\u5730\u6765\u3002 \u3000\u3000\u5148\u524d\u8bf4\u8bdd\u90a3\u9ed1\u8863\u4eba\uff0e\u518d\u628a\u5e8a\u4e0a\u7684\u88ab\u8925\u53e0\u597d\uff0c\u770b\u4e0d\u51fa\u534a\u70b9\u96f6\u4e71\u75d5\u8ff9\u3002 \u3000\u3000\u7136\u540e\u4e24\u4eba\u2014\u58f0\u4e0d\u54cd\u7684\uff0e\u628a\u4e1a\u5df2\u660f\u8ff7\u4e0d\u9192\u7684\u82d7\u7d20\u5170\u67b6\u51fa\u95e8\u53bb\u3002 \u3000\u3000\u5f53\u82d7\u7d20\u5170\u9192\u6765\u65f6\uff0e\u624d\u53d1\u89c9\u81ea\u5df1\u662f\u8eba\u5728\u53e6\u4e00\u4e2a\u4ece\u672a\u5230\u8fc7\u7684\u623f\u95f4\u91cc\u3002 \u3000\u3000\u5979\u6478\u4e86\u6478\u8eab\u4e0a\uff0c\u4e0d\u89c9\u554a\u4e86\u2014\u58f0\u3002 \u3000\u3000\u539f\u6765\uff0e\u6b64\u523b\u5df2\u8fd1\u4e4e\u8d64\u8eab\u88f8\u4f53\uff0c\u3000\u4e0b\u8eab\u53ea\u6709\u4e00\u6761\u5185\u88e4\uff0c\u4e0a\u4f53\u4e5f\u4ec5\u4ec5\u662f\u4e00\u4e2a\u809a\u515c\uff0e\u90a3\u4fee\u957f\u817b \u6ed1\u7684\u53cc\u817f\u548c\u96ea\u767d\u7c89\u5ae9\u7684\u7389\u81c2\uff0e\u5168\u9732\u5728\u5916\u9762\u3002 \u3000\u3000\u597d\u5728\u5728\u611f\u89c9\u4e0a\uff0c\u4e0b\u8eab\u8fd8\u4e0d\u66fe\u88ab\u4eba\u8e42\u8e8f\u8fc7\u3002 \u3000\u3000\u8fd9\u91cc\u7a76\u7adf\u662f\u4ec0\u4e48\u5730\u65b9\u5462\uff1f \u3000\u3000\u5e8a\u4e0a\u53e6\u653e\u7740\u4e00\u5f20\u77ee\u684c\uff0c\u684c\u4e0a\u7ea2\u70db\u9ad8\u70e7\uff0c\u5e76\u6446\u7740\u4e00\u58f6\u9152\uff0c\u4e24\u53ea\u7a7a\u76c5\u548c\u51e0\u789f\u5c0f\u83dc\u3002 \u3000\u3000\u5979\u60f3\u7acb\u5373\u8d77\u8eab\uff0c\u4f46\u56e0\u627e\u4e0d\u5230\u8863\u670d\uff0c\u800c\u4e14\u56db\u80a2\u65e0\u529b\uff0c\u53ea\u597d\u6323\u624e\u7740\u5750\u4e86\u8d77\u6765\uff0e\u62c9\u8fc7\u9526\u88ab\u906e\u4f4f \u8eab\u5b50\u3002 \u3000\u3000\u6b63\u5728\u832b\u7136\u65e0\u63aa\u7684\u5f53\u513f\uff0e\u95e8\u5916\u5df2\u6709\u4e86\u811a\u6b65\u58f0\u3002 \u3000\u3000\u63a5\u7740\u4e00\u4eba\u63a8\u95e8\u800c\u5165\uff0c\u8d6b\u7136\u662f\u6ee1\u9762\u90aa\u6c14\u7684\u970d\u5143\u4f3d\u3002 \u3000\u3000\u82d7\u7d20\u5170\u7acb\u5373\u54ac\u7259\u5207\u9f7f\u9053\uff1a\u201c\u970d\u5143\u4f3d\uff0c\u539f\u6765\u662f\u4f60\u505a\u7684\u597d\u4e8b\uff0c\u4f60\u628a\u6211\u63b3\u5230\u8fd9\u91cc\u6765\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u6d8e\u7740\u8138\u7b11\u9053\uff1a\u201c\u82d7\u59d1\u5a18\uff0c\u4f60\u8bf4\u5bf9\u4e86\uff0c\u8001\u592b\u7684\u786e\u505a\u7684\u662f\u597d\u4e8b\uff0e\u76ee\u7684\u5c31\u662f\u8981\u54b1\u4eec\u4e24\u4e2a \u6210\u5c31\u597d\u4e8b\uff01\u201d \u3000\u3000\u82d7\u7d20\u5170\u4e00\u53e3\u553e\u5473\uff0c\u76f4\u5550\u5230\u970d\u5143\u4f3d\u8138\u4e0a\u9053\uff1a\u201c\u597d\u4e2a\u65e0\u803b\u7684\u6df7\u8d26\u4e1c\u897f\uff0c\u6211\u4ece\u524d\u77e5\u9053\u4f60\u53ea\u662f\u9634 \u72e0\u6bd2\u8fa3\uff0c\u60f3\u4e0d\u5230\u8fd8\u4e0b\u6d41\u65e0\u803b\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u6beb\u4e0d\u52a8\u6012\uff0c\u62ac\u624b\u5c06\u8138\u62ed\u5e72\uff0c\u518d\u563f\u563f\u7b11\u9053\uff1a\u201c\u4f60\u8981\u9a82\u53ea\u7ba1\u9a82\uff0e\u53cd\u6b63\u4f60\u5df2\u662f\u8001\u592b\u7684\u4eba \u4e86\uff0e\u8001\u4eba\u88ab\u4f60\u9a82\u5f97\u51e0\u53e5\uff0e\u4e5f\u7b97\u4e0d\u4e86\u4ec0\u4e48\uff01\u201d \u3000\u3000\u201c\u4f60\u5230\u5e95\u8981\u628a\u6211\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u201c\u96be\u9053\u4f60\u5fc3\u91cc\u8fd8\u4e0d\u660e\u767d\uff1f\u201d \u3000\u3000\u201c\u6211\u6709\u4ec0\u4e48\u660e\u767d\uff1f\u201d \u3000\u3000\u201c\u90a3\u4e48\u8001\u592b\u5c31\u5b9e\u5bf9\u4f60\u8bf4\uff0c\u8001\u592b\u660e\u5929\u5c31\u8981\u767b\u4e0a\u7eff\u6797\u76df\u4e3b\u5b9d\u5ea7\uff0c\u7f3a\u5c11\u7684\u5c31\u662f\u2014\u4f4d\u538b\u5be8\u5927 \u4eba\u2026\u2026\u201d \u3000\u3000\u201c\u4f60\u60f3\u8981\u6211\u505a\u4f60\u7684\u538b\u5be8\u592b\u4eba\uff1f\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u73b0\u5728\u4f60\u5df2\u638c\u63e1\u5728\u6211\u624b\u4e2d\uff0e\u4e0d\u7b54\u5e94\u4e5f\u5f97\u7b54\u5e94\u3002\u201d \u3000\u3000\u201c\u5982\u679c\u6211\u4e0d\u7b54\u5e94\uff0c\u4f60\u51c6\u5907\u628a\u6211\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u8138\u8272\u77ac\u606f\u53d8\u4e86\u51e0\u53d8\u3002\u63a5\u7740\u624b\u62c2\u957f\u9aef\uff0e\u9634\u68ee\u68ee\u8fde\u7b11\u51e0\u58f0\u9053\uff1a\u201c\u8001\u592b\u628a\u4f60\u5f04\u5230\u8fd9\u91cc \u6765\uff0c\u949f\u4e00\u8c6a\u7b49\u4eba\u5168\u4e0d\u77e5\u60c5\uff0c\u82e5\u73b0\u5728\u8001\u592b\u8981\u4f60\u6b7b\uff0e\u7167\u6837\u4e5f\u662f\u795e\u4e0d\u77e5\u9b3c\u4e0d\u89c9\uff0c\u4f60\u603b\u4e0d\u4f1a\u81ea\u5bfb\u6b7b\u8def \u5427\uff1f\u201d \u3000\u3000\u82d7\u7d20\u5170\u679c\u7136\u542c\u5f97\u6697\u751f\u51db\u9a87\uff0c\u970d\u5143\u4f3d\u4e00\u5411\u5fc3\u72e0\u624b\u8fa3\uff0c\u4ed6\u662f\u8bf4\u5f97\u51fa\uff0c\u505a\u5f97\u5230\uff0e\u82e5\u5c31\u8fd9\u6837\u5e73\u767d \u9001\u4e86\u547d\uff0c\u5728\u5979\u6765\u8bf4\uff0c\u786e\u662f\u4e00\u4ef6\u5fc3\u4e0d\u7518\u60c5\u4e0d\u613f\u7684\u4e8b\u3002 \u3000\u3000\u970d\u5143\u4f3d\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u8001\u592b\u6740\u4e86\u4f60\u4e4b\u540e\uff0e\u949f\u4e00\u8c6a\u90a3\u822c\u4eba\u7684\u6027\u547d\uff0c\u7167\u6837\u4e5f\u5728\u8001\u592b\u638c\u63e1\u4e4b \u4e2d\uff01\u201d \u3000\u3000\u82d7\u7d20\u5170\u6108\u52a0\u4e0d\u5b89\u3002 \u3000\u3000\u971c\u5143\u4f3d\u518d\u9053\uff1a\u201c\u82d7\u59d1\u5a18\uff0c\u8ddf\u4e86\u8001\u592b\uff0c\u6709\u4ec0\u4e48\u4e0d\u597d\uff0c\u5230\u90a3\u65f6\u5728\u5929\u4e0b\u7eff\u6797\u4e2d\uff0c\u4f60\u5c31\u662f\u4e00\u4eba\u4e4b \u4e0b\uff0c\u4e07\u4eba\u4e4b\u4e0a\u7684\u7b2c\u4e8c\u53f7\u4eba\u7269\uff0c\u8363\u534e\u5bcc\u8d35\uff0c\u4eab\u7528\u4e0d\u5c3d\uff0c\u9664\u4e86\u5929\u4e0a\u7684\u6708\u4eae\uff0c\u4f60\u60f3\u8981\u4ec0\u4e48\uff0c\u8001\u592b\u90fd \u7b54\u5e94\u4f60\uff01\u201d \u3000\u3000\u82d7\u7d20\u5170\u5e7d\u5e7d\u53f9\u4e86\u2014\u53e3\u6c14\u9053\uff1a\u201c\u96be\u9053\u4f60\u4e00\u5b9a\u975e\u8981\u538b\u5be8\u592b\u4eba\u4e0d\u53ef\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u7b11\u9053\uff1a\u201c\u8001\u592b\u8eab\u4e3a\u5929\u4e0b\u7eff\u6797\u76df\u4e3b\uff0e\u5fc5\u987b\u6709\u4e2a\u538b\u5be8\u592b\u4eba\uff0c\u624d\u7b97\u5341\u5168\u5341\u7f8e\uff0c\u800c\u4e14\u6709\u524d \u4f8b\u53ef\u5faa\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u524d\u4f8b\uff1f\u201d \u3000\u3000\u201c\u80e1\u67cf\u9f84\u62c5\u4efb\u7eff\u6797\u76df\u4e3b\u65f6\uff0c\u8c37\u5bd2\u9999\u4fbf\u662f\u4ed6\u7684\u538b\u5be8\u592b\u4eba\uff0c\u800c\u4e14\u538b\u5be8\u592b\u4eba\u88ab\u6276\u4e3a\u76df\u4e3b\uff0c\u4e5f\u662f \u4ef6\u5f88\u81ea\u7136\u7684\u4e8b\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e48\u8bf4\u4f60\u5f53\u4e0a\u7eff\u6797\u76df\u4e3b\u4ee5\u540e\uff0c\u5f88\u5feb\u5c31\u8981\u6b7b\uff0e\u4f60\u662f\u4e0d\u662f\u8fd9\u610f\u601d\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u5475\u5475\u4e00\u9635\u8fde\u7b11\uff0c\u63a5\u7740\u5750\u4e0a\u4e86\u5e8a\uff0c\u8f7b\u8f7b\u62cd\u4e86\u82d7\u7d20\u5170\u4e00\u4e0b\u9999\u80a9\u9053\uff1a\u201c\u5c0f\u5b9d\u8d1d\uff0c\u4f60\u8981 \u9a82\uff0c\u5c3d\u7ba1\u9a82\uff0c\u8001\u592b\u7edd\u5bf9\u4e0d\u751f\u4f60\u7684\u6c14\uff0c\u53ea\u8981\u4f60\u80af\u7b54\u5e94\u505a\u8001\u592b\u7684\u4e50\u5be8\u592b\u4eba\u5c31\u597d\uff01\u201d \u3000\u3000\u82d7\u7d20\u5170\u4f4e\u9996\u4e0d\u8bed\uff0e\u6c89\u541f\u4e86\u534a\u54cd\u9053\uff1a\u201c\u505a\u4f60\u7684\u538b\u5be8\u592b\u4eba\u53ef\u4ee5\uff0e\u4f46\u4f60\u5fc5\u987b\u7b54\u5e94\u6211\u4e00\u4e2a\u6761 \u4ef6\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u7acb\u523b\u54a7\u5634\u800c\u7b11\u9053\uff1a\u201c\u522b\u8bf4\u4e00\u4e2a\u6761\u4ef6\uff0c\u5c31\u662f\u4e00\u767e\u4e2a\uff0c\u8001\u592b\u4e5f\u7b54\u5e94\uff0e\u7a76\u7adf\u4ec0\u4e48\u6761\u4ef6\uff1f \u5feb\u8bf4\uff01\u201d \u3000\u3000\u82d7\u7d20\u5170\u9053\uff0e\u201c\u5fc5\u987b\u767e\u65e5\u4e4b\u540e\uff0c\u54b1\u4eec\u624d\u80fd\u540c\u623f\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u7b11\u5bb9\u4e00\u655b\uff0c\u5446\u4e86\u5446\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u8981\u90a3\u4e48\u4e45\uff0c\u8001\u592b\u54ea\u80fd\u7b49\u5f97\uff1f\u201d \u3000\u3000\u82d7\u7d20\u5170\u6b63\u8272\u9053\uff1a\u201c\u4f60\u7b49\u4e0d\u5f97\u4e5f\u5f97\u7b49\uff0c\u56e0\u4e3a\u6211\u5df2\u5bf9\u5929\u53d1\u8fc7\u8a93\u3002\u201d \u3000\u3000\u201c\u4f60\u53d1\u8fc7\u4ec0\u4e48\u8a93\uff1f\u201d \u3000\u3000\u201c\u8c37\u76df\u4e3b\u751f\u524d\u5f85\u6211\u5982\u540c\u4eb2\u59d0\u59b9\uff0c\u4e3a\u4e86\u62a5\u7b54\u5979\u7684\u77e5\u9047\u4e4b\u6069\uff0c\u5728\u5979\u6b7b\u540e\uff0e\u6211\u5c31\u53d1\u8a93\uff0c\u8981\u5728\u8ff7 \u8e2a\u8c37\u4e3a\u5979\u5b88\u5b5d\u767e\u65e5\uff0c\u5728\u8fd9\u6bb5\u5b88\u5b5d\u671f\u95f4\uff0e\u5f53\u7136\u4e0d\u80fd\u548c\u4f60\u540c\u623f\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u7acb\u5373\u6beb\u4e0d\u72b9\u8c6b\u5730\u9894\u9996\u9053\uff1a\u201c\u597d\uff0e\u8001\u592b\u5c31\u7b54\u5e94\u4f60\uff0c\u4e0d\u8fc7\u5728\u540d\u4e49\u4e0a\uff0c\u660e\u5929\u4f60\u5c31\u662f\u8001\u592b \u7684\u538b\u5be8\u592b\u4eba\u4e86\u3002\u201d \u3000\u3000\u5176\u5b9e\uff0c\u970d\u5143\u4f3d\u6240\u4ee5\u7b54\u5e94\u5f97\u8fd9\u4e48\u5feb\uff0c\u662f\u4ed6\u65e9\u5df2\u6210\u7af9\u5728\u80f8\uff0c\u53ea\u597d\u7565\u65bd\u624b\u6bb5\uff0e\u4ed6\u6709\u529e\u6cd5\u5728\u4e09\u4e94 \u5929\u5185\uff0c\u4fbf\u53ef\u548c\u82d7\u7d20\u5170\u5b8c\u6210\u597d\u4e8b\u3002 \u3000\u3000\u66f2\u7d20\u5170\u76ee\u5149\u63a0\u8fc7\u684c\u4e0a\u7684\u9152\u83dc\u9053\uff1a\u201c\u8fd9\u4e9b\u4e1c\u897f\u662f\u505a\u4ec0\u4e48\u7684\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u6ee1\u9762\u966a\u7b11\u9053\uff1a\u201c\u5f53\u7136\u662f\u7ed9\u4f60\u538b\u60ca\u6d88\u591c\u7684\uff0c\u6765\uff0c\u8001\u592b\u966a\u4f60\u5bf9\u996e\u51e0\u76c5\uff01\u201d \u3000\u3000\u4e07\u6620\u971e\u9192\u6765\u65f6\uff0c\u5df2\u662f\u65e9\u996d\u65f6\u523b\u3002 \u3000\u3000\u8fde\u5979\u81ea\u5df1\u4e5f\u4e0d\u89e3\uff0e\u6628\u665a\u4e3a\u4ec0\u4e48\u8fd9\u6837\u597d\u7761\u3002 \u3000\u3000\u5979\u7a7f\u597d\u8863\u670d\u4e0b\u5e8a\uff0e\u6765\u5230\u82d7\u7d20\u5170\u623f\u95f4\uff0e\u51c6\u5907\u558a\u5979\u4e00\u8d77\u53bb\u7528\u996d\u3002 \u3000\u3000\u8c01\u77e5\u5ba4\u5185\u5e76\u65e0\u4eba\u5f71\uff0c\u6599\u60f3\u5fc5\u5df2\u5148\u884c\u5230\u4e86\u996d\u5385\uff0c\u4fbf\u2014\u4e2a\u4eba\u51fa\u95e8\u518d\u8d76\u5f80\u996d\u5385\u3002 \u3000\u3000\u5728\u8c37\u5bd2\u9999\u751f\u524d\uff0c\u8fd9\u91cc\u6709\u5355\u72ec\u7684\u5c0f\u53a8\u623f\u3002 \u3000\u3000\u5982\u4eca\u53ea\u5269\u4e0b\u82d7\u7d20\u5170\u548c\u4e07\u6620\u971e\u4e24\u4eba\u4f4f\uff0c\u4e0d\u6d88\u8bf4\u5fc5\u987b\u5230\u949f\u4e00\u8c6a\u90a3\u4f19\u4eba\u7684\u996d\u5385\u7528\u996d\u3002 \u3000\u3000\u6765\u5230\u996d\u5385\uff0c\u949f\u4e00\u8c6a\u7b49\u4e00\u4f19\u4eba\u65e9\u5df2\u5230\u9f50\uff0c\u6b63\u5728\u7b49\u5019\u82d7\u7d20\u5170\u548c\u4e07\u6620\u971e\u3002 \u3000\u3000\u53ea\u542c\u9ea6\u5c0f\u660e\u62a2\u7740\u95ee\u9053\uff1a\u201c\u5927\u5bb6\u7b49\u4f60\u4eec\u5f88\u4e45\u4e86\uff0e\u82d7\u59d0\u59d0\u600e\u4e48\u6ca1\u6765\uff1f\u201d \u3000\u3000\u4e07\u6620\u971e\u4e00\u6123\u9053\uff1a\u201c\u82d7\u59d1\u5a18\u623f\u91cc\u6ca1\u4eba\uff0c\u6211\u8fd8\u4ee5\u4e3a\u5979\u5148\u6765\u4e86\u5462\uff1f\u201d \u3000\u3000\u4f17\u4eba\u6599\u60f3\u5230\u82d7\u7d20\u5170\u5fc5\u662f\u4e00\u65e9\u5230\u5c71\u4e0a\u6563\u6b65\u53bb\u4e86\uff0c\u6240\u4ee5\u5e76\u672a\u628a\u8fd9\u4ef6\u4e8b\u653e\u5728\u5fc3\u4e0a\uff0c\u63a5\u7740\u4fbf\u5f00\u59cb \u7528\u996d\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u4e0d\u7518\u5bc2\u5bde\uff0c\u8fb9\u5403\u8fb9\u9053\uff1a\u201c\u4f59\u5148\u751f\uff0c\u5927\u7ea6\u65e9\u996d\u540e\uff0c\u5c31\u662f\u970d\u5143\u4f3d\u90a3\u8001\u738b\u516b\u86cb\u4e3e\u884c\u5c31\u804c \u5927\u5178\u7684\u65f6\u5019\u4e86\uff0c\u54b1\u4eec\u8981\u4e0d\u8981\u53c2\u52a0\uff1f\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u9053\uff1a\u201c\u5f53\u7136\u8981\u53c2\u52a0\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u54fc\u4e86\u58f0\u9053\uff1a\u201c\u773c\u770b\u90a3\u8001\u738b\u516b\u86cb\u626c\u626c\u5f97\u610f\u7684\u767b\u4e0a\u5b9d\u5ea7\uff0e\u5728\u54b1\u4eec\u6765\u8bf4\uff0c\u5b9e\u5728\u662f\u4ef6\u4e0d\u8212 \u670d\u7684\u4e8b\uff01\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u5401\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u4e0d\u8212\u670d\u662f\u5fc3\u91cc\u7684\u4e8b\uff0c\u8868\u9762\u4e0a\u5374\u5fc5\u987b\u88c5\u6210\u9ad8\u5174\u7684\u6837\u5b50\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u51b7\u58f0\u9053\uff1a\u201c\u505a\u4eba\u8981\u8868\u91cc\u4e00\u81f4\uff0c\u53e3\u662f\u5fc3\u975e\uff0c\u90a3\u7b97\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u6162\u6761\u65af\u7406\u9053\uff1a\u201c\u6211\u5728\u8fd9\u79cd\u5e74\u7eaa\u7684\u65f6\u5019\uff0c\u4e5f\u662f\u8fd9\u4e48\u60f3\uff0e\u7b49\u4f60\u4eba\u751f\u9605\u5386\u4e00\u591a\uff0e\u5230\u4e86\u6211 \u8fd9\u79cd\u5e74\u7eaa\uff0c\u53ef\u80fd\u5c31\u4e0d\u662f\u73b0\u5728\u7684\u60f3\u6cd5\u4e86\u3002\u201d \u3000\u3000\u201c\u4f60\u624d\u4e0d\u8fc7\u4e09\u5341\u51fa\u5934\uff01\u201d \u3000\u3000\u201c\u4e0d\u9519\u3002\u6240\u4ee5\u970d\u5143\u4f3d\u624d\u80fd\u79f0\u5f97\u4e0a\u8001\u5978\u5de8\u733e\u3002\u201d \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0e\u5cad\u5357\u4e8c\u5947\u4e2d\u7684\u8001\u5927\u641c\u9b42\u624b\u5df4\u5929\u4e49\u3002\u5306\u5306\u8d70\u4e86\u8fdb\u6765\uff0c\u671b\u7740\u5728\u573a\u4f17\u4eba\u4e00\u62b1\u62f3\u9053\uff1a \u201c\u76df\u4e3b\u5c31\u804c\u5927\u5178\u9a6c\u4e0a\u5c31\u8981\u4e3e\u884c\uff0c\u8bf8\u4f4d\u7528\u8fc7\u996d\u540e\uff0e\u5c31\u8bf7\u5feb\u4e9b\u524d\u53bb\uff01\u201d \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u65b0\u4efb\u76df\u4e3b\u7684\u5c31\u804c\u5927\u5178\u4e0d\u77e5\u5728\u54ea\u91cc\u4e3e\u884c\uff1f\u201d \u3000\u3000\u201c\u5c31\u5728\u805a\u4e49\u5385\u3002\u201d \u3000\u3000\u201c\u5144\u5f1f\u8fd9\u8fb9\u7684\u4eba\uff0e\u662f\u5426\u8981\u5168\u4f53\u53c2\u52a0\u3002\u201d \u3000\u3000\u5df4\u5929\u4e49\u626b\u63a0\u7fa4\u8c6a\u4e00\u773c\uff0c\u7b11\u9053\uff1a\xb7\u5728\u573a\u8bf8\u4f4d\uff0e\u90fd\u662f\u54b1\u4eec\u8c1c\u8e2a\u8c37\u7684\u9996\u8981\u4eba\u7269\uff0e\u6700\u597d\u80fd\u4e00\u8d77\u53c2 \u52a0\uff0e\u8fd9\u4e0d\u4f46\u662f\u8bf8\u4f4d\u7684\u5149\u5f69\uff0c\u800c\u4e14\u65b0\u4efb\u76df\u4e3b\u4e5f\u6709\u9762\u5b50\u3002\u201d \u3000\u3000\u5df4\u5929\u4e49\u8bf4\u5b8c\u8bdd\uff0c\u53c8\u505a\u4e86\u4e2a\u73af\u63d6\uff0c\u624d\u8f6c\u8eab\u79bb\u53bb\u3002 \u3000\u3000\u970d\u5143\u4f3d\u6d3e\u4ed6\u624b\u4e0b\u7684\u7b2c\u4e00\u53f7\u5fc3\u8179\u5927\u5c06\u6765\u901a\u77e5\uff0e\u603b\u7b97\u9887\u7ed9\u949f\u4e00\u8c6a\u7b49\u4eba\u9762\u5b50\uff0e\u5426\u5219\uff0e\u4ed6\u5c3d\u53ef\u6d3e \u4e00\u540d\u542c\u5dee\u7684\u5c0f\u55bd\u7f57\u6765\u901a\u77e5\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u62c5\u5fc3\u4f17\u4eba\u8bae\u8bba\u7eb7\u7eb7\uff0c\u53c8\u751f\u4e8b\u7aef\uff0c\u5306\u5306\u7528\u8fc7\u996d\uff0c\u7ad9\u8d77\u8eab\u9053\uff1a\u201c\u5927\u5bb6\u5c31\u8bf7\u4e00\u8d77\u5230\u805a\u4e49 \u5385\u5427\uff01\u201d \u3000\u3000\u4f17\u4eba\u9042\u8d77\u8eab\u3002 \u3000\u3000\u5ffd\u542c\u9ea6\u5c0f\u660e\u56b7\u9053\uff1a\u201c\u53ef\u662f\u82d7\u59d0\u59d0\u5230\u73b0\u5728\u8fd8\u6ca1\u56de\u6765\uff0c\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u65f6\u95f4\u5df2\u5230\uff0c\u4f17\u4eba\u4e5f\u987e\u4e0d\u5f97\u518d\u7406\u4f1a\u8fd9\u4ef6\u4e8b\uff0c\u968f\u5373\u5728\u949f\u4e00\u8c6a\u8eab\u540e\uff0c\u5f80\u805a\u4e49\u5385\u8fdb\u53d1\u3002 \u3000\u3000\u5927\u5178\u4eea\u5f0f\u5728\u5927\u5385\u5185\u4e3e\u884c\u3002 \u3000\u3000\u5927\u5385\u867d\u5927\uff0c\u4e5f\u53ea\u80fd\u5bb9\u7eb3\u767e\u4eba\u5de6\u53f3\u800c\u5df2\uff0c\u56da\u4e4b\uff0c\u51e1\u662f\u83b7\u5f97\u53c2\u52a0\u76db\u5178\u7684\uff0c\u90fd\u662f\u8ff7\u8e2a\u8c37\u5185\u7684\u5927 \u5c0f\u5934\u76ee\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u7b49\u4eba\u5230\u8fbe\u65f6\uff0c\u805a\u4e49\u5385\u5185\u65e9\u5df2\u6324\u6ee1\u4e86\u4eba\u3002 \u3000\u3000\u597d\u5728\u949f\u4e00\u8c6a\u7b49\u4eba\u7684\u5e2d\u4f4d\u5df2\u9884\u5148\u7559\u7f6e\uff0c\u800c\u4e14\u89c2\u793c\u5e2d\u4e0a\u6700\u5c0a\u7684\u4e00\u5e2d\uff0e\u4fbf\u662f\u949f\u4e00\u8c6a\u7684\u5ea7\u4f4d\uff0c\u8fd9 \u662f\u56e0\u949f\u4e00\u8c6a\u662f\u526f\u76df\u4e3b\uff0c\u5728\u8ff7\u8e2a\u8c37\u5185\uff0c\u9664\u4e86\u970d\u5143\u4f3d\uff0c\u5f53\u7136\u4ee5\u4ed6\u6700\u5c0a\u3002 \u3000\u3000\u4f17\u4eba\u5750\u4e0b\u540e\uff0c\u624d\u6ce8\u610f\u5230\u5927\u5385\u5185\u7684\u5e03\u7f6e\u9648\u8bbe\u3002 \u3000\u3000\u53ea\u89c1\u6b63\u4e2d\u9760\u58c1\u5904\uff0c\u6446\u4e86\u4e00\u5f20\u9ad8\u5927\u6728\u51e0\uff0c\u6728\u51e0\u4e0a\u9ad8\u70e7\u7740\u4e00\u5bf9\u513f\u81c2\u7c97\u7ec6\u7684\u7ea2\u70db\uff0c\u53e6\u5916\u5217\u4e86\u51e0 \u74f6\u82b1\u548c\u51e0\u789f\u7d20\u679c\u3002 \u3000\u3000\u6728\u51e0\u524d\u518d\u6446\u4e86\u4e00\u5f20\u516b\u4ed9\u684c\uff0c\u684c\u4e0a\u6709\u4e2a\u5927\u9999\u7089\uff0c\u65c1\u8fb9\u653e\u4e00\u53cc\u9999\u7b52\uff0e\u53e6\u6709\u4e00\u5927\u7f38\u6d12\u548c\u51e0\u4e2a\u9152 \u76c5\u3002 \u3000\u3000\u6700\u663e\u773c\u7684\uff0c\u8fd8\u662f\u516b\u4ed9\u684c\u540e\u65b9\uff0c\u5b89\u653e\u5728\u6728\u67b6\u4e0a\u7684\u90a3\u53ea\u5168\u8eab\u67d3\u6210\u7ea2\u8272\u7684\u5927\u732a\u516c\uff0e\u770b\u6837\u5b50\u8db3\u6709 \u5343\u65a4\u4ee5\u4e0a\u91cd\u3002 \u3000\u3000\u5728\u5927\u732a\u516c\u5de6\u53f3\uff0e\u5206\u653e\u7740\u4e00\u4e2a\u7f8a\u5934\u548c\u4e00\u4e2a\u725b\u5934\u3002 \u3000\u3000\u53f8\u4eea\u8d5e\u793c\u7684\u662f\u5df4\u5927\u4e49\uff0e\u4ed6\u7ad9\u5728\u516b\u4ed9\u684c\u65c1\uff0e\u867d\u7136\u9e70\u9f3b\u9e5e\u773c\uff0c\u6ee1\u9762\u9634\u9669\u51f6\u6076\u4e4b\u76f8\uff0e\u4f46\u8868\u73b0\u5f97 \u5374\u795e\u6001\u8083\u7a46\uff0c\u3000\u2014\u526f\u6bd5\u606d\u6bd5\u656c\u6a21\u6837\u3002 \u3000\u3000\u5927\u5385\u5185\u56e0\u4eba\u6570\u592a\u591a\uff0c\u96be\u514d\u6709\u4eba\u4ea4\u5934\u63a5\u8033\uff0c\u5581\u5581\u79c1\u8bed\uff0c\u770b\u8d77\u6765\u95f9\u54c4\u54c4\u7684\u4e71\u6210\u4e00\u56e2\u3002 \u3000\u3000\u5ffd\u542c\u5df4\u5929\u4e49\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u65b0\u4f4f\u76df\u4e3b\u5230\uff0e\u5168\u4f53\u8083\u7acb\uff01\u201d \u3000\u3000\u7acb\u523b\uff0e\u5927\u5385\u5185\u9e26\u96c0\u65e0\u58f0\uff0c\u4e00\u4e2a\u4e2a\u5168\u4ece\u5ea7\u4f4d\u4e0a\u7ad9\u4e86\u8d77\u6765\u3002 \u3000\u3000\u949f\u2014\u8c6a\u7b49\u4eba\uff0c\u9648\u9ea6\u5c0f\u660e\u5916\uff0c\u4e5f\u90fd\u4f9d\u8a00\u8d77\u7acb\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u53ea\u89c1\u6709\u4ed6\u4e00\u4e2a\u4eba\u5750\u7740\uff0c\u65e0\u5948\u4e4b\u4e0b\uff0c\u968f\u540e\u4e5f\u53ea\u6709\u8ddf\u7740\u7ad9\u8d77\u6765\u3002 \u3000\u3000\u63a5\u7740\uff0c\u7531\u5927\u5385\u5de6\u4fa7\u7684\u5c4f\u98ce\u540e\uff0c\u4f20\u6765\u811a\u6b65\u58f0\u54cd\uff0e\u7f57\u6d6e\u4e00\u53df\u970d\u5143\u4f3d\u8fc8\u7740\u6c89\u7a33\u7684\u516b\u5b57\u6b65\uff0c\u3000\u2014 \u6447\u4e00\u6446\u7684\u8fdb\u6765\u3002 \u3000\u3000\u539f\u6765\u5927\u5385\u5de6\u4fa7\u7684\u5c4f\u98ce\u540e\uff0c\u53e6\u6709\u4e00\u9053\u4fa7\u95e8\uff0c\u4e0d\u9700\u7ecf\u5927\u95e8\u4fbf\u53ef\u8fdb\u5165\u5927\u5385\u3002 \u3000\u3000\u5927\u4eba\u7269\u90fd\u662f\u7531\u540e\u95e8\u8d70\u5411\u524d\u53f0\u7684\u3002 \u3000\u3000\u970d\u5143\u4f3d\u4eca\u5929\u4f3c\u662f\u7279\u522b\u7ecf\u8fc7\u4e00\u756a\u4fee\u9970\uff0c\u663e\u5f97\u7cbe\u795e\u6296\u64de\uff0c\u5bb9\u5149\u7115\u53d1\u3002 \u3000\u3000\u9887\u6709\u76df\u4e3b\u7684\u67b6\u52bf\u3002 \u3000\u3000\u970d\u5143\u4f3d\u7f13\u6b65\u8d70\u5230\u4f9b\u684c\u524d\uff0c\u8083\u5bb9\u7ad9\u597d\uff0c\u4f4e\u58f0\u5bf9\u5df4\u5929\u4e49\u9053\uff1a\u201c\u5ba3\u8bfb\u9882\u8bcd\uff01\u201d \u3000\u3000\u5df4\u5929\u4e49\u968f\u5373\u5c55\u5f00\u65e9\u5df2\u634f\u5728\u624b\u4e2d\u7684\u6587\u7a3f\uff0c\u9ad8\u58f0\u6717\u8bfb\u5230\uff1a\u201c\u552f\u6211\u7eff\u6797\uff0c\u5929\u4e0b\u72ec\u5c0a\u3002\u7edf\u5fa1\u4e94 \u5cb3\uff0e\u626b\u8361\u51f6\u6c1b\u3002\u56fd\u6cf0\u6c11\u5b89\uff0c\u6df1\u5e86\u5f97\u4eba\u3002\u552f\u6211\u9996\u9886\uff0c\u82f1\u660e\u65e0\u4f26\u3002\u800c\u4eca\u800c\u540e\uff0c\u4e00\u4f53\u5171\u94a6\uff01\u201d \u3000\u3000\u8fd9\u7bc7\u9882\u8bcd\u4e0d\u77e5\u51fa\u4e8e\u4f55\u4eba\u624b\u7b14\uff0e\u591f\u5f97\u4e0a\u51a0\u5195\u5802\u7687\uff0c\u4f46\u4f59\u4ea6\u4e50\u7b49\u4eba\u542c\u540e\uff0e\u5374\u90fd\u6697\u7b11\u4e0d\u5df2\u3002 \u3000\u3000\u970d\u5143\u4f3d\u76f4\u7b49\u9882\u8bcd\u8bf5\u5b8c\u540e\uff0c\u624d\u539f\u5730\u6446\u4e86\u4e2a\u4e0d\u4e2d\u4e0d\u897f\u7684\u65e0\u540d\u67b6\u5f0f\uff0c\u63a5\u7740\u5f00\u59cb\u4e0a\u9999\u732e\u793c\uff0c\u5386\u65f6 \u534a\u523b\uff0e\u603b\u7b97\u5b8c\u6210\u4e86\u5c31\u804c\u4eea\u5f0f\u3002 \u3000\u3000\u53ea\u662f\u5728\u573a\u6240\u6709\u7684\u4eba\uff0c\u90fd\u4e2a\u77e5\u4ed6\u62dc\u7684\u662f\u8c01\uff1f \u3000\u3000\u8fd9\u67f1\u9999\u7a76\u7adf\u662f\u4e3a\u54ea\u4e00\u4f4d\u7956\u5e08\u7237\u70e7\u7684\uff1f \u3000\u3000\u6709\u4eba\u8bf4\u662f\u6b66\u5723\u5173\u516c\u3002 \u3000\u3000\u53ea\u56e0\u672c\u51e0\u540e\u7684\u5899\u58c1\u6302\u4e86\u4e00\u5e45\u7ea2\u5e54\uff0c\u800c\u90a3\u7ea2\u5e54\u5374\u4e00\u76f4\u4e0d\u66fe\u6380\u5f00\u3002 \u3000\u3000\u6240\u4ee5\uff0c\u7b54\u6848\u4e00\u76f4\u5e76\u4e0d\u80af\u5b9a\u3002 \u3000\u3000\u5168\u53ea\u662f\u731c\u6d4b\u4e4b\u8bcd\u3002 \u3000\u3000\u597d\u5728\u8c1c\u9898\u4e0d\u4e45\u540e\u4fbf\u5728\u970d\u5143\u4f3d\u6380\u5f00\u7ea2\u5e54\u4e0b\uff0c\u663e\u9732\u7b54\u6848\u3002 \u3000\u3000\u679c\u7136\u662f\u6b66\u5173\u516c\u3002 \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u5404\u4f4d\uff0c\u672c\u76df\u4e3b\u9664\u4e86\u5c31\u804c\u7eff\u6797\u76df\u4e3b\u4e4b\u4f4d\u5916\uff0e\u8fd8\u6709\u4e00\u4ef6\u5929\u5927\u559c\u4e8b\u8981\u5ba3\u5e03\u3002\u201d \u3000\u3000\u4ed6\u4e0d\u5f85\u5176\u4ed6\u4eba\u53d1\u8a00\uff0e\u5df2\u7136\u53c8\u9053\uff1a\u201c\u672c\u76df\u4e3b\u5df2\u7eb3\u4e86\u4f4d\u538b\u5be8\u592b\u4eba\uff0c\u73b0\u5728\u5c31\u8981\u5979\u51fa\u6765\u548c\u5404\u4f4d\u89c1 \u9762\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u8bdd\u8bf4\u5b8c\uff0c\u5411\u5df4\u5929\u4e49\u65bd\u4e86\u4e2a\u773c\u8272\u3002 \u3000\u3000\u5df4\u5929\u4e49\u7acb\u5373\u9ad8\u58f0\u6717\u9053\uff1a\u201c\u76df\u4e3b\u592b\u4eba\u9a7e\u5230\u3002\u201d \u3000\u3000\u5c31\u89c1\u5927\u5385\u5de6\u4fa7\u5c4f\u98ce\u540e\u7684\u4fa7\u95e8\uff0e\u8885\u5a1c\u8d70\u8fdb\u4e00\u540d\u767d\u8863\u5a07\u4f53\u7684\u5c11\u5987\u3002 \u3000\u3000\u8d6b\u7136\u7adf\u662f\u82d7\u7d20\u5170\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u7b49\u4f17\u4eba\uff0c\u81ea\u662f\u6115\u4f4f\u4e86\u3002 \u3000\u3000\u82d7\u7d20\u5170\u5165\u5385\u540e\uff0c\u4fbf\u795e\u8272\u81ea\u82e5\u7684\u8d70\u5411\u970d\u5143\u4f3d\u8eab\u65c1\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u4e0d\u6562\u7f6e\u4fe1\u9053\uff1a\u201c\u82d7\u59d0\u59d0\uff0c\u4f60\u8981\u5ac1\u7ed9\u970d\u2026\u2026\u201d \u3000\u3000\u4e0d\u5f85\u4ed6\u8bf4\u5b8c\uff0c\u82d7\u7d20\u5170\u5df2\u5fae\u7b11\u70b9\u5934\u9053\uff1a\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u543c\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u8981\u5ac1\u7ed9\u4ed6\uff01\u201d \u3000\u3000\u82d7\u7d20\u5170\u9053\uff1a\u201c\u80fd\u5ac1\u7ed9\u5929\u4e0b\u7eff\u6797\u76df\u4e3b\uff0c\u662f\u6211\u68a6\u5bd0\u4ee5\u6c42\u7684\u4e8b\uff0c\u6839\u672c\u7528\u4e0d\u7740\u518d\u8bf4\u660e\u7406\u7531\u3002\u201d \u3000\u3000\u201c\u6211\u4e0d\u4fe1\uff0c\u4f60\u8fd9\u8bdd\u662f\u51fa\u81ea\u771f\u5fc3\uff01\u201d \u3000\u3000\u201c\u4f60\u4e0d\u76f8\u4fe1\u4e5f\u6ca1\u529e\u6cd5\uff0c\u8001\u5b9e\u5bf9\u4f60\u8bf4\uff0c\u5f53\u5e74\u6211\u5c31\u662f\u56e0\u4e3a\u5d07\u62dc\u7231\u6155\u80e1\u524d\u76df\u4e3b\u7684\u82f1\u96c4\u6c14\u6982\uff0c\u6240 \u4ee5\u624d\u80cc\u53db\u9634\u624b\u4e00\u9b54\uff0c\u8ddf\u4ed6\u6765\u5230\u8ff7\u8e2a\u8c37\u3002 \u3000\u3000\u5374\u56e0\u80e1\u524d\u76df\u4e3b\u5df2\u6709\u59bb\u5ba4\uff0e\u800c\u4e14\u4ed6\u7684\u592b\u4eba\u5404\u65b9\u9762\u90fd\u80dc\u8fc7\u6211\u5f88\u591a\uff0c\u903c\u5f97\u6211\u4e0d\u6562\u518d\u5984\u751f\u4ed6 \u5ff5\uff01\u201d \u3000\u3000\u5979\u8bed\u6c14\u7a0d\u6b47\uff0e\u63a5\u7740\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u5982\u4eca\u8ff7\u8e2a\u8c37\u6362\u4e86\u65b0\u4efb\u76df\u4e3b\uff0c\u800c\u65b0\u76df\u4e3b\u53c8\u6b63\u7f3a\u4e00\u4f4d\u538b\u5be8\u592b \u4eba\uff0e\u65e2\u80fd\u627f\u4ed6\u770b\u5f97\u8d77\uff0c\u8fd9\u673a\u4f1a\u6211\u5982\u4f55\u80fd\u8ba9\u5b83\u9519\u8fc7\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u5475\u5475\u7b11\u9053\uff1a\u201c\u672c\u76df\u4e3b\u6709\u4e86\u538b\u5be8\u592b\u4eba\uff0c\u5728\u5404\u4f4d\u540c\u9053\u7684\u534f\u52a9\u4e0b\uff0e\u4eca\u540e\u6709\u51b3\u5fc3\u5fc5\u80fd\u628a\u8ff7 \u8e2a\u8c37\u6cbb\u7406\u5f97\u66f4\u4e3a\u51fa\u8272\u3002\u201d \u3000\u3000\u4eea\u5f0f\u5b8c\u6210\u4e4b\u540e\uff0c\u63a5\u4e0b\u53bb\u4fbf\u662f\u5171\u996e\u201c\u540c\u76df\u9152\u201d\u3002 \u3000\u3000\u539f\u6765\u516b\u4ed9\u684c\u4e0a\u90a3\u2014\u5927\u7f38\u9152\uff0e\u5c31\u79f0\u4e3a\u201c\u540c\u76df\u9152\u201d\uff0c\u684c\u4e0a\u6709\u5341\u51e0\u53cc\u9152\u76c5\uff0c\u7531\u6c5f\u5357\u56db\u602a\u4e2d\u7684\u5c71 \u9b48\u6bdb\u8d8a\u548c\u6c34\u9b3c\u6d77\u767b\u8d1f\u8d23\u5012\u9152\u3002 \u3000\u3000\u6240\u6709\u5728\u573a\u53c2\u52a0\u5927\u5178\u4e4b\u4eba\uff0c\u90fd\u8981\u5230\u516b\u4ed9\u684c\u524d\u559d\u4e00\u86ca\u9152\uff0e\u7136\u540e\u81ea\u884c\u79bb\u5f00\u5927\u5385\u3002 \u3000\u3000\u767e\u4eba\u4ee5\u4e0a\u7684\u7eff\u6797\u597d\u6c49\uff0c\u5f88\u5feb\u4fbf\u559d\u8fc7\u201c\u540c\u76df\u9152\u201d\u6563\u53bb\uff0c\u4e0d\u8fc7\uff0c\u5927\u5385\u5185\u4ecd\u7559\u4e0b\u4e8c\u5341\u51e0\u4eba\u3002 \u3000\u3000\u8fd9\u4e8c\u5341\u51e0\u4eba\uff0c\u9664\u970d\u5143\u4f3d\u548c\u82d7\u7d20\u5170\u5916\uff0e\u4fbf\u662f\u970d\u5143\u4f3d\u7684\u5fc3\u8179\u624b\u4e0b\u5cad\u5357\u4e8c\u5947\u548c\u6c5f\u5357\u56db\u602a\u7b49\u5404\u7ea7 \u5934\u76ee\u3002\u53e6\u4e00\u65b9\u9762\u5219\u662f\u4ee5\u949f\u4e00\u8c6a\u4e3a\u9996\u7684\u2014\u4f19\u4eba\u3002 \u3000\u3000\u539f\u6765\u8fd9\u662f\u970d\u5143\u4f3d\u5c31\u804c\u540e\uff0c\u6240\u4e3e\u884c\u7684\u7b2c\u4e00\u6b21\u9996\u8111\u4f1a\u8bae\u3002 \u3000\u3000\u65b0\u5b98\u4e0a\u4efb\u4e09\u628a\u706b\uff0c\u6beb\u65e0\u7591\u95ee\uff0e\u8fd9\u662f\u970d\u5143\u4f3d\u7684\u7b2c\u4e00\u628a\u706b\u3002 \u3000\u3000\u5927\u5385\u5185\uff0c\u8fd9\u65f6\u5df2\u628a\u684c\u6905\u91cd\u65b0\u6392\u653e\u3002\u5f53\u4e2d\u4e24\u4e2a\u5ea7\u4f4d\uff0c\u4e24\u4fa7\u4e24\u6392\u5ea7\u4f4d\uff0e\u4f7f\u4eba\u60f3\u8d77\u5f53\u5e74\u6881\u5c71\u6cca \u4e00\u767e\u96f6\u516b\u6761\u597d\u6c49\u5728\u5fe0\u7236\u5802\u4e0a\u7684\u91d1\u4ea4\u6905\u3002 \u3000\u3000\u8fd9\u60c5\u5f62\uff0e\u4f3c\u4e4e\u4e5f\u6709\u4e9b\u5dee\u4e0d\u591a\u3002 \u3000\u3000\u8fd9\u65f6\uff0e\u5bf9\u949f\u4e00\u8c6a\u548c\u4f59\u4ea6\u4e50\u6765\u8bf4\uff0c\u8be5\u662f\u6700\u654f\u611f\u7684\u4e00\u523b\uff0e\u56db\u4e3a\u4ed6\u4eec\u5f04\u4e0d\u6e05\u4e2d\u95f4\u7684\u4e24\u4e2a\u5ea7\u4f4d\uff0c \u9664\u970d\u5143\u4f3d\u5916\uff0c\u53e6\u4e00\u4f4d\u5b50\u7a76\u7adf\u7531\u8c01\u5750\uff1f \u3000\u3000\u679c\u7136\u4e0d\u51fa\u6240\u6599\uff0e\u970d\u5143\u4f3d\u5df2\u62c9\u7740\u82d7\u7d20\u5170\u51c6\u5907\u5c31\u4f4d\u3002 \u3000\u3000\u4f59\u4ea6\u4e50\u7acb\u5373\u8de8\u4e0a\u524d\u4e24\u6b65\uff0c\u51b7\u7136\u9ad8\u58f0\u9053\uff1a\u201c\u4e14\u6162\uff0c\u7b49\u4f59\u67d0\u628a\u8bdd\u8bf4\u5b8c\u540e\uff0c\u76df\u4e3b\u518d\u8bf7\u5165\u5ea7\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u4e0d\u52a8\u58f0\u8272\u9053\uff1a\u201c\u4f59\u5148\u751f\u6709\u8bdd\u8bf7\u8bb2\uff01\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u9053\uff1a\u201c\u8bf7\u95ee\u76df\u4e3b\uff0e\u8fd9\u5f53\u4e2d\u4e24\u4e2a\u5ea7\u4f4d\u662f\u4ec0\u4e48\u4eba\u5750\u7684\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u5475\u5475\u7b11\u9053\uff1a\u201c\u56fd\u6709\u56fd\u6cd5\uff0e\u5bb6\u6709\u5bb6\u89c4\uff0c\u90a3\u4e24\u4e2a\u5ea7\u4f4d\uff0e\u5f53\u7136\u662f\u8001\u592b\u548c\u592b\u4eba\u7684\u3002\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u51b7\u7b11\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0e\u949f\u526f\u76df\u4e3b\u53c8\u5750\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u5411\u4e24\u65c1\u4e0a\u9996\u7b2c\u4e00\u4e2a\u5ea7\u4f4d\u4e00\u6307\u9053\uff1a\u201c\u8001\u592b\u65e9\u5df2\u66ff\u949f\u526f\u76df\u4e3b\u7559\u597d\u4e86\u4f4d\u7f6e\u3002\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u9053\uff1a\u201c\u526f\u76df\u4e3b\u548c\u76df\u4e3b\u867d\u7136\u5728\u804c\u4f4d\u4e0a\u7a0d\u6709\u5dee\u522b\uff0c\u4f46\u5374\u7406\u5e94\u5e73\u8d77\u5e73\u5750\uff0c\u76df\u4e3b\u8fd9\u79cd\u552f\u6211 \u72ec\u5c0a\u7684\u5b89\u6392\uff0c\u603b\u6709\u4e9b\u8bf4\u4e0d\u8fc7\u53bb\u5427\uff1f\u201d \u3000\u3000\u53ea\u542c\u82d7\u7d20\u5170\u62a2\u7740\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0e\u4e0d\u59a8\u7531\u6211\u548c\u949f\u526f\u76df\u4e3b\u4e92\u6362\u4e00\u4e0b\u4f4d\u7f6e\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u90a3\u600e\u4e48\u53ef\u4ee5\uff0c\u5982\u679c\u4f59\u5148\u751f\u8ba4\u4e3a\u4e0d\u59a5\uff0c\u8001\u592b\u5c31\u628a\u6b63\u4e2d\u6539\u8bbe\u4e09\u4e2a\u5ea7\u4f4d\u3002\u201d \u3000\u3000\u4e00\u76f4\u4e0d\u66fe\u5f00\u53e3\u7684\u949f\u2014\u8c6a\u5230\u8fd9\u65f6\u624d\u6de1\u6de1\u4e00\u7b11\u9053\uff1a\u201c\u90a3\u4e5f\u4e0d\u5fc5\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u83ab\u975e\u949f\u526f\u76df\u4e3b\u8fd8\u53e6\u6709\u9ad8\u89c1\uff1f\u201d \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u4f9d\u5728\u4e0b\u4e4b\u610f\uff0e\u4e2d\u95f4\u7684\u4e24\u628a\u4ea4\u6905\uff0c\u90fd\u8be5\u7a7a\u7740\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u53cc\u76ee\u6e5b\u6e5b\u7cbe\u5149\u95ea\u4e86\u51e0\u95ea\u9053\uff1a\u201c\u8001\u592b\u8fd9\u5c31\u4e0d\u660e\u767d\u4e86\uff1f\u201d \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u60f3\u8fd9\u8ff7\u8e2a\u8c37\uff0c\u4e43\u662f\u80e1\u524d\u76df\u4e3b\u4e00\u624b\u6240\u5efa\u7acb\uff0c\u5e76\u7531\u8c37\u76df\u4e3b\u7ee7\u7eed\u60e8\u6de1\u7ecf\u8425\uff0e\u624d\u6709 \u4eca\u65e5\u7684\u89c4\u6a21\u3002\u4e3a\u4e86\u8868\u793a\u5bf9\u4ed6\u4eec\u4e24\u4f4d\u524d\u4efb\u76df\u4e3b\u7684\u5c0a\u5d07\uff0c\u5728\u4e0b\u5efa\u8bae\u628a\u8fd9\u4e2d\u95f4\u4e24\u4e2a\u5ea7\u4f4d\u7a7a\u8d77\u6765\uff0c\u9664 \u4e86\u7eaa\u5ff5\u524d\u8d24\uff0c\u540c\u65f6\u4e5f\u53ef\u5f70\u663e\u76df\u4e3b\u7684\u8c26\u8ba9\u98ce\u5ea6\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u521d\u767b\u4f4d\uff0e\u542c\u8fc7\u8fd9\u756a\u8bdd\uff0e\u5185\u5fc3\u867d\u7136\u6c14\u607c\uff0c\u4f46\u4e3a\u4e86\u6536\u5356\u4eba\u5fc3\u548c\u4e0d\u4fbf\u4e0e\u949f\u4e00\u8c6a\u7b49\u4eba\u9a6c\u4e0a \u53cd\u76ee\uff0c\u53ea\u6709\u6682\u65f6\u9690\u5fcd\u4e0d\u53d1\u3002 \u3000\u3000\u4ed6\u6c49\u988a\u4e00\u9635\u62bd\u6410\uff0c\u7ec8\u4e8e\u5475\u5475\u7b11\u9053\uff1a\u201c\u82e5\u975e\u949f\u526f\u76df\u4e3b\u63d0\u8d77\uff0c\u8001\u592b\u5012\u758f\u5ffd\u4e86\u8fd9\u4e00\u70b9\uff0c\u8001\u592b\u51b3 \u5b9a\u542c\u4ece\u949f\u526f\u76df\u4e3b\u7684\u5b89\u6392\uff0e\u73b0\u5728\u5927\u5bb6\u5c31\u8bf7\u5c31\u5ea7\u3002\u201d \u3000\u3000\u8bf4\u7740\u7387\u5148\u5728\u53f3\u9996\u4e00\u6392\u5ea7\u4f4d\u7684\u9996\u5ea7\u5750\u4e0b\uff0e\u4e0b\u9762\u4f9d\u6b21\u662f\u82d7\u7d20\u5170\u3001\u5cad\u5357\u4e8c\u5947\u3001\u6c5f\u5357\u56db\u602a\u7b49\u4eba\u3002 \u3000\u3000\u5de6\u9762\u4e00\u6392\u5ea7\u4f4d\uff0e\u81ea\u7136\u662f\u4ee5\u949f\u4e00\u8c6a\u4e3a\u9996\uff0c\u63a5\u4e0b\u53bb\u662f\u4f59\u4ea6\u4e50\u3001\u9ea6\u5c0f\u660e\u3001\u6c5f\u5317\u4e09\u9f99\u3001\u6587\u5929\u751f\u548c \u4e07\u6620\u971e\u3002 \u3000\u3000\u5982\u6b64\u2014\u6765\uff0c\u53cc\u65b9\u603b\u7b97\u5df2\u7ecf\u5e73\u8d77\u5e73\u5750\u3002 \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u8001\u592b\u9996\u5148\u8981\u5ba3\u5e03\u7684\uff0c\u5f53\u5e74\u80e1\u524d\u76df\u4e3b\u624b\u8ba2\u7684\u56db\u5927\u6212\u5f8b\uff0e\u4ecd\u7136\u7ee7\u7eed\u6709\u6548\uff0c\u8001\u592b \u5bf9\u80e1\u524d\u76df\u4e3b\uff0e\u4e00\u5411\u5d07\u656c\u6709\u52a0\uff0c\u6240\u4ee5\u624d\u51b3\u5b9a\u8427\u5f52\u66f9\u968f\u3002\u4eca\u5929\u8fd9\u7b2c\u4e00\u6b21\u4f1a\u8bae\uff0c\u53ea\u662f\u8981\u542c\u542c\u8bf8\u4f4d\u5bf9 \u4eca\u540e\u5e94\u5174\u5e94\u9769\u4e4b\u4e8b\uff0e\u6709\u4f55\u9ad8\u89c1\uff1f\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u7acb\u5373\u8bf4\u9053\uff1a\u201c\u5e94\u5174\u5e94\u9769\u4e4b\u4e8b\uff0e\u5b9e\u5728\u592a\u591a\uff0e\u4e0d\u59a8\u96c6\u601d\u5e7f\u76ca\uff0e\u4eca\u540e\u518d\u6162\u6162\u63d0\u51fa\u3002\u76ee\u524d \u6700\u91cd\u8981\u7684\u4e00\u4ef6\u4e8b\uff0c\u662f\u8bbe\u6cd5\u628a\u8c37\u524d\u76df\u4e3b\u7684\u9057\u9ab8\u8fce\u56de\uff0c\u4ee5\u4fbf\u548c\u80e1\u76df\u4e3b\u5408\u846c\u4e00\u5904\uff0e\u8fd9\u6837\u505a\u4e5f\u7b97\u970d\u76df \u4e3b\u5bf9\u80e1\u3001\u8c37\u4e24\u4f4d\u6b7b\u53bb\u7684\u76df\u4e3b\uff0e\u5c3d\u4e86\u2014\u756a\u5fc3\u610f\uff01\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u8fd9\u756a\u8bdd\uff0c\u663e\u7136\u662f\u60f3\u4f7f\u970d\u5143\u4f3d\u548c\u5e9e\u58eb\u51b2\u5f15\u8d77\u6b63\u9762\u51b2\u7a81\uff0c\u5e0c\u671b\u85c9\u5e9e\u58eb\u51b2\u4e4b\u624b\uff0e\u9664\u53bb\u970d \u5143\u4f3d\u3002 \u3000\u3000\u56e0\u5e9e\u58eb\u51b2\u7684\u6b66\u529f\uff0c\u5728\u5f53\u4eca\u6b66\u6797\uff0e\u5df2\u7edd\u5c11\u6709\u4eba\u80fd\u4e0e\u4e4b\u6297\u8861\u3002 \u3000\u3000\u970d\u5143\u4f3d\u5c82\u80fd\u4e0d\u77e5\u4f59\u4ea6\u4e50\u7528\u610f\uff0e\u62c2\u9aef\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u4f59\u5148\u751f\u53ef\u77e5\u8c37\u524d\u76df\u4e3b\u7684\u9057\u4f53\uff0c\u5728\u4ec0\u4e48 \u4eba\u624b\u91cc\uff1f\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u9053\uff1a\u201c\u90a3\u5929\u5728\u5929\u53f0\u4e07\u82b1\u5bab\u524d\uff0c\u8c37\u524d\u76df\u4e3b\u7684\u9057\u9ab8\u88ab\u8c01\u5e26\u8d70\uff0c\u970d\u76df\u4e3b\u5fc5\u5b9a\u5df2\u7ecf\u4eb2\u773c\u89c1 \u8fc7\uff1f\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0e\u4e0d\u4f46\u8001\u592b\u770b\u5230\uff0c\u4eca\u5929\u5728\u573a\u8bf8\u4f4d\uff0c\u53ef\u8bf4\u65e0\u4eba\u4e0d\u66fe\u770b\u5230\u3002\u8c37\u524d\u76df\u4e3b\u4e34\u7ec8\u524d\uff0e\u4eb2\u81ea\u4ea4 \u4ee3\u5e9e\u58eb\u51b2\u628a\u5979\u7684\u9057\u4f53\u5e26\u8d70\uff0c\u82e5\u54b1\u4eec\u518d\u53bb\u627e\u5e9e\u58eb\u51b2\u628a\u9057\u9ab8\u8fce\u56de\uff0c\u5c82\u4e0d\u6709\u8fdd\u8c37\u76df\u4e3b\u9057\u8a00\uff1f\u201d \u3000\u3000\u201c\u970d\u76df\u4e3b\u8bdd\u867d\u4e0d\u9519\uff0e\u4f46\u8c37\u524d\u76df\u4e3b\u6b7b\u540e\uff0c\u4e0d\u80fd\u548c\u80e1\u524d\u76df\u4e3b\u592b\u59bb\u5408\u846c\u2014\u5904\uff0e\u603b\u662f\u4ef6\u5927\u5927\u61be \u4e8b\uff0c\u54b1\u4eec\u65e2\u7136\u90fd\u505a\u8fc7\u4ed6\u4eec\u5fe0\u8d1e\u5c5e\u4e0b\uff0c\u4fbf\u4e0d\u80fd\u4e0d\u4e3a\u4ed6\u4eec\u5c3d\u4e9b\u5fc3\u529b\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u9634\u68ee\u68ee\u7684\u8138\u8272\u4e0a\u62bd\u6410\u4e86\u51e0\u4e0b\u9053\uff1a\u201c\u5e9e\u58eb\u51b2\u8fdc\u5728\u957f\u767d\u5c71\uff0c\u8981\u8fce\u56de\u8c37\u524d\u76df\u4e3b\u9057\u9ab8\uff0c\u4e5f \u5e76\u975e\u77ed\u65f6\u95f4\u5185\u53ef\u4ee5\u529e\u5230\u7684\uff0c\u8fd9\u4e8b\u65e5\u540e\u518d\u8bae\u5982\u4f55\uff1f\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u9053\uff1a\u201c\u6b63\u56e0\u4e3a\u957f\u767d\u5c71\u8def\u7a0b\u9065\u8fdc\uff0c\u6240\u4ee5\u624d\u5e94\u8be5\u53ca\u65e9\u884c\u52a8\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u9894\u9996\u9053\uff1a\u201c\u4e5f\u597d\uff0e\u8001\u592b\u521d\u4efb\u76df\u4e3b\uff0e\u767e\u5e9f\u5f85\u5174\uff0c\u5fc5\u987b\u7559\u5728\u8ff7\u8e2a\u8c37\u4e3b\u6301\u4e00\u5207\uff0e\u5c31\u8bf7\u4f59 \u5148\u751f\u6311\u9009\u51e0\u4f4d\u5f97\u529b\u9ad8\u624b\uff0c\u524d\u5f80\u957f\u767d\u5c71\u628a\u8c37\u524d\u76df\u4e3b\u9057\u9ab8\u8fce\u56de\u6765\u5982\u4f55\uff1f\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u4e07\u4e07\u60f3\u4e0d\u5230\uff0e\u53cd\u88ab\u970d\u5143\u4f3d\u5c06\u4e86\u4e00\u519b\uff0c\u53ef\u89c1\u8fd9\u8001\u9b54\u5934\u5b9e\u5728\u72e1\u733e\uff0c\u8fde\u81ea\u5df1\u8fd9\u201c\u667a\u591a \u661f\u201d\u4e5f\u5fc5\u672a\u201c\u6597\u201d\u5f97\u8fc7\u4ed6\u3002 \u3000\u3000\u597d\u5728\u949f\u4e00\u8c6a\u7acb\u5373\u5c94\u5f00\u8bdd\u9053\uff1a\u201c\u8fd9\u4e8b\u6682\u65f6\u4e0d\u8c08\u4e5f\u7f62\uff0c\u5728\u4e0b\u8fd8\u53e6\u6709\u4e00\u4e8b\uff0c\u60f3\u95ee\u95ee\u970d\u76df\u4e3b \u5462\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u949f\u5144\u6709\u8bdd\u8bf7\u8bb2\u3002\u201d \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u6c5f\u5317\u56db\u9f99\u4e2d\u7684\u8001\u5927\u59dc\u5b8f\uff0c\u5f53\u65e5\u662f\u7531\u5929\u53f0\u5c71\u63d0\u524d\u8d76\u56de\u8ff7\u8e2a\u8c37\uff0c\u662f\u5426\u5df2\u88ab\u970d\u76df \u4e3b\u6d3e\u9063\u5230\u5916\u9762\u529e\u4e8b\u53bb\u4e86\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u4f3c\u662f\u5403\u4e86\u4e00\u60ca\u9053\uff1a\u201c\u4ec0\u4e48\uff0e\u59dc\u8001\u5927\u5df2\u7ecf\u63d0\u524d\u56de\u6765\u4e86\uff1f\u600e\u4e48\u4ed6\u4e0d\u66fe\u6765\u89c1\u8fc7\u8001\u592b\uff1f\u201d \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u970d\u76df\u4e3b\u771f\u7684\u6ca1\u89c1\u4ed6\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u4e00\u76b1\u7709\u5934\u9053\uff1a\u201c\u53ef\u4e0d\u53ef\u80fd\u662f\u5728\u8def\u4e0a\u51fa\u4e86\u4e8b\uff1f\u201d \u3000\u3000\u8fd9\u65f6\u98de\u5929\u9f99\u4f55\u5b97\u8f89\u3001\u591a\u722a\u9f99\u674e\u6770\u3001\u55b7\u706b\u9f99\u5218\u9707\u7b49\u4e09\u4eba\uff0c\u90fd\u5df2\u6309\u637a\u4e0d\u4f4f\uff0c\u8822\u8822\u6b32\u52a8\uff0c\u5927\u6709 \u5251\u62d4\u5f29\u5f20\u4e4b\u52bf\u3002 \u3000\u3000\u4ed6\u4eec\u51e0\u6761\u9f99\u662f\u6362\u5e16\u5f1f\u5144\uff0c\u3000\u4e00\u5411\u60c5\u540c\u624b\u8db3\uff0e\u6599\u60f3\u8001\u5927\u51fa\u4e91\u9f99\u5fc5\u5b9a\u662f\u51f6\u591a\u5409\u5c11\uff0c\u867d\u660e\u77e5\u52a8 \u4e0d\u5f97\u970d\u5143\u4f3d\uff0c\u5374\u65e0\u6cd5\u904f\u5236\u5fc3\u4e2d\u6012\u706b\u3002 \u3000\u3000\u4f59\u4ea6\u4e50\u62c5\u5fc3\u4e8b\u60c5\u95f9\u5927\uff0c\u5bf9\u5df1\u65b9\u4e0d\u5229\uff0c\u5fd9\u5411\u4e09\u4eba\u6697\u9012\u773c\u8272\uff0c\u4e00\u9762\u8bed\u6c14\u5e73\u548c\u7684\u9053\uff1a\u201c\u770b\u6765\u59dc \u5f53\u5bb6\u7684\uff0e\u4e5f\u8bb8\u662f\u5728\u8def\u4e0a\u51fa\u4e86\u4e8b\uff0c\u82e5\u8fc7\u51e0\u5929\u518d\u4e0d\u56de\u6765\u2026\u2026\u201d \u3000\u3000\u970d\u5143\u4f3d\u7acb\u5373\u63a5\u9053\uff1a\u201c\u8001\u592b\u56de\u5934\u5c31\u6d3e\u4eba\u4f20\u8c15\u5927\u6c5f\u5357\u5317\u5404\u5904\u5206\u5be8\u5206\u8235\uff0e\u8981\u4ed6\u4eec\u7acb\u5373\u5c55\u5f00\u4fa6 \u5bdf\uff01\u201d \u3000\u3000\u4e00\u573a\u4f1a\u8bae\uff0c\u5c31\u8fd9\u6837\u7ed3\u675f\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u7b49\u4eba\u79bb\u5f00\u805a\u4e49\u5385\uff0c\u4e00\u540c\u6765\u5230\u82d7\u7d20\u5170\u548c\u4e07\u6620\u971e\u6240\u4f4f\u7684\u72ec\u7acb\u5ead\u9662\u3002 \u3000\u3000\u7ecf\u8fc7\u4e00\u9635\u8ba1\u8bae\uff0c\u4ed6\u4eec\u51b3\u5b9a\u5168\u4f53\u4f4f\u8fdb\u6765\u3002 \u3000\u3000\u5e76\u628a\u8fd9\u5ead\u9662\u53d6\u540d\u201c\u67cf\u9f84\u9662\u201d\uff0c\u8fd9\u6837\u4e00\u6765\uff0e\u4e07\u6620\u971e\u624d\u653e\u4e86\u5fc3\uff0e\u5426\u5219\u8ba9\u5979\u4e00\u4e2a\u4eba\u5982\u4f55\u8fd8\u6562\u5728 \u8fd9\u4f4f\u4e0b\u53bb\u3002 \u3000\u3000\u63a5\u7740\uff0e\u4f17\u4eba\u53c8\u8c08\u8bba\u8d77\u82d7\u7d20\u5170\u505a\u4e86\u970d\u5143\u4f3d\u538b\u5be8\u592b\u4eba\u7684\u4e8b\uff0e \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u82d7\u59d0\u59d0\u5c45\u7136\u53d8\u4e86\u5fc3\uff0e\u80cc\u53db\u4e86\u54b1\u4eec\uff0e\u8fd9\u5012\u662f\u4ef6\u602a\u4e8b\u3002\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u9053\uff1a\u201c\u4e07\u59d1\u5a18\uff0e\u82d7\u59d1\u5a18\u6628\u665a\u56de\u6765\u540e\uff0e\u6709\u4ec0\u4e48\u5f02\u72b6\u6ca1\u6709\uff1f\u201d \u3000\u3000\u4e07\u6620\u971e\u9053\uff1a\u201c\u6628\u665a\u56de\u6765\uff0c\u6211\u548c\u5979\u804a\u5929\u804a\u5230\u5c06\u8fd1\u4e09\u66f4\uff0c\u6839\u672c\u770b\u4e0d\u51fa\u5979\u6709\u4ec0\u4e48\u4e0d\u5bf9\u3002\u201d \u3000\u3000\u201c\u5979\u662f\u4ec0\u4e48\u65f6\u5019\u79bb\u5f00\u8fd9\u91cc\u7684\uff1f\u201d \u3000\u3000\u201c\u6628\u665a\u6211\u4e00\u7761\u5c31\u7761\u5230\u5929\u4eae\u3001\u8d77\u5e8a\u540e\uff0e\u5230\u5979\u623f\u91cc\u627e\u5979\uff0c\u624d\u77e5\u9053\u5979\u5df2\u4e0d\u5728\u3002\u201d \u3000\u3000\u201c\u5979\u623f\u95f4\u91cc\u662f\u5426\u5f88\u51cc\u4e71\uff1f\u201d \u3000\u3000\u201c\u4ec0\u4e48\u90fd\u662f\u6574\u6574\u9f50\u9f50\u7684\uff0c\u8fde\u88ab\u8925\u90fd\u53e0\u5f97\u597d\u597d\u7684\u3002\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u7f04\u9ed8\u4e86\u534a\u54cd\uff0c\u4e0d\u518d\u8a00\u8bed\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4e0d\u7ba1\u82d7\u59d1\u5a18\u505a\u4e86\u970d\u5143\u4f3d\u90a3\u8001\u738b\u516b\u86cb\u7684\u538b\u5be8\u592b\u4eba\uff0e\u662f\u81ea\u613f\u8fd8\u662f\u88ab\u8feb\u7684\uff0c\u53cd\u6b63 \u7b2c\u4e00\u4e2a\u53d7\u5bb3\u4eba\u662f\u6211\uff01\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u9053\uff1a\u201c\u4f60\u53d7\u7684\u4ec0\u4e48\u5bb3\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u5230\u5415\u6881\u5c71\u6ca1\u6709\u5e26\u8def\u7684\uff0c\u600e\u80fd\u8bf4\u4e0d\u53d7\u5bb3\u3002\u201d \u3000\u3000\u53ea\u542c\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u5927\u5bb6\u4e0d\u5fc5\u8ba8\u8bba\u8fd9\u4e8b\u4e86\uff0c\u6211\u63d0\u8bae\u54b1\u4eec\u662f\u5426\u8be5\u5230\u96ea\u5cf0\u53bb\u796d\u62dc\u80e1\u524d\u76df\u4e3b\uff1f\u201d \u3000\u3000\u4ed6\u8fd9\u63d0\u8bae\uff0e\u7acb\u523b\u5f97\u5230\u5728\u573a\u6240\u6709\u4eba\u7684\u5171\u540c\u54cd\u5e94\u3002\u4f17\u4eba\u968f\u5373\u5e26\u4e86\u9999\u70db\u7eb8\u7b94\uff0c\u5f80\u96ea\u5cf0\u800c\u6765\u3002 \u3000\u3000\u96ea\u5cf0\u5c31\u5728\u8ff7\u8e2a\u8c37\uff0c\u8c37\u540e\u6570\u91cc\u8def\u7a0b\uff0e\u662f\u7b14\u76f4\u7684\u4e00\u5ea7\u9661\u5cfb\u7edd\u5cf0\u3002 \u3000\u3000\u5cf0\u9876\u7ec8\u5e74\u79ef\u96ea\u4e0d\u878d\u3002\u5f53\u5e74\u80e1\u67cf\u9f84\u88ab\u8bef\u4f24\u81f4\u6b7b\u540e\uff0c\u8c37\u5bd2\u9999\u4e0d\u77e5\u54ea\u91cc\u6765\u7684\u4e00\u80a1\u5947\u5f02\u529b\u91cf\uff0c\u7adf \u80fd\u62b1\u7740\u80e1\u67cf\u9f84\u7684\u9057\u4f53\uff0c\u4e58\u591c\u72ec\u529b\u6500\u4e0a\u8fd9\u5ea7\u7edd\u5cf0\u3002\u7136\u540e\u5728\u949f\u2014\u8c6a\u7684\u534f\u52a9\u4e0b\uff0e\u628a\u80e1\u67cf\u9f84\u63a9\u57cb\u5728\u51b0 \u96ea\u4e4b\u4e2d\u3002 \u3000\u3000\u8c37\u5bd2\u9999\u6240\u4ee5\u8981\u628a\u80e1\u67cf\u9f84\u7684\u9057\u4f53\uff0c\u57cb\u5728\u7ec8\u5e74\u4e0d\u878d\u7684\u51b0\u96ea\u4e4b\u4e2d\uff0e\u4e0d\u5916\u662f\u8981\u4ed6\u9057\u4f53\u4e0d\u574f\uff0c\u4ee5\u4fbf \u65e5\u540e\u4ecd\u6709\u673a\u4f1a\u77bb\u4ef0\u5230\u4ed6\u7684\u9057\u5bb9\u3002 \u3000\u3000\u53ef\u60dc\u5728\u80e1\u67cf\u9f84\u53bb\u4e16\u540e\uff0c\u8c37\u5bd2\u9999\u4e3a\u62a5\u592b\u4ec7\uff0c\u5217\u5904\u5954\u8d70\uff0c\u4e00\u76f4\u6ca1\u518d\u5230\u8fc7\u96ea\u5cf0\u3002 \u3000\u3000\u81f3\u4e8e\u80e1\u6015\u9f84\u7684\u57cb\u5c38\u4e4b\u5904\uff0c\u4e5f\u53ea\u6709\u8c37\u5bd2\u9999\u548c\u949f\u4e00\u8c6a\u4e24\u4eba\u77e5\u9053\u3002 \u3000\u3000\u7fa4\u8c6a\u4eec\u5728\u51b0\u96ea\u524d\uff0e\u711a\u9999\u5316\u7eb8\uff0e\u796d\u62dc\u4e86\u4e00\u756a\u4e4b\u540e\u3002 \u3000\u3000\u5ffd\u7136\u4e07\u6620\u971e\u63d0\u8bae\uff0c\u5228\u5f00\u51b0\u96ea\uff0e\u4f9b\u5927\u5bb6\u518d\u5ea6\u77bb\u4ef0\u4e00\u6b21\u80e1\u67cf\u9f84\u7684\u9057\u5bb9\uff01 \u3000\u3000\u5728\u540c\u6765\u7684\u7fa4\u8c6a\u4e2d\uff0c\u5982\u679c\u82d7\u7d20\u5170\u4e5f\u5728\uff0c\u6700\u6000\u5ff5\u80e1\u67cf\u9f84\u7684\uff0c\u81ea\u7136\u662f\u5979\u3002 \u3000\u3000\u56e0\u4e3a\u5979\u5bf9\u80e1\u67cf\u9f84\u6709\u79cd\u4e0d\u53ef\u544a\u4eba\u7684\u7279\u6b8a\u611f\u60c5\uff0e\u5426\u5219\u5f53\u5e74\u5979\u5c31\u4e2a\u4f1a\u80cc\u53db\u5e08\u95e8\uff0e\u968f\u80e1\u67cf\u9f84\u6765\u5230 \u8ff7\u8e2a\u8c37\u4e86\u3002 \u3000\u3000\u9664\u4e86\u82d7\u7d20\u5170\u5916\uff0c\u548c\u80e1\u67cf\u9f84\u6700\u4eb2\u7684\uff0e\u4fbf\u662f\u4e07\u6620\u971e\uff0c\u56e0\u4e3a\u5979\u7684\u7236\u4eb2\u795e\u97ad\u98de\u68ad\u4e07\u6653\u5149\uff0c\u662f\u80e1\u67cf \u9f84\u7684\u7ed3\u4e49\u76df\u5144\u3002 \u3000\u3000\u4e07\u6653\u5149\u6b7b\u540e\uff0e\u5e78\u8d56\u80e1\u67cf\u9f84\u6536\u5bb9\u4e86\u5979\uff0c\u5f85\u5979\u6709\u5982\u4eb2\u751f\u513f\u5973\u4e00\u822c\uff0e\u56e0\u4e4b\uff0c\u5979\u624d\u63d0\u8bae\u8981\u77bb\u4ef0\u80e1 \u67cf\u9f84\u7684\u9057\u5bb9\u3002 \u3000\u3000\u8fd9\u63d0\u8bae\u7acb\u523b\u5f97\u5230\u6240\u6709\u4eba\u7684\u540c\u610f\uff0c\u4e8e\u662f\u5404\u81ea\u638f\u51fa\u5175\u5203\uff0c\u5f00\u59cb\u5728\u51b0\u96ea\u4e2d\u5c55\u5f00\u6316\u6398\u3002 \u3000\u3000\u5c82\u77e5\u5f53\u5c06\u90a3\u5ea7\u51b0\u96ea\u5806\u6316\u5230\u5df2\u8fd1\u5730\u9762\uff0c\u80e1\u67cf\u9f84\u7684\u9057\u4f53\u7adf\u7136\u4e0d\u77e5\u53bb\u5411\u3002 \u3000\u3000\u8fd9\u5904\u5cf0\u9876\u8303\u56f4\u6781\u5c0f\uff0c\u53ea\u4e0d\u8fc7\u6570\u4e08\u65b9\u5706\uff0c\u949f\u4e00\u8c6a\u53c8\u660e\u660e\u8bb0\u5f97\u80e1\u67cf\u9f84\u7684\u63a9\u57cb\u4e4b\u5904\uff0c\u5c38\u4f53\u5374\u5df2 \u4e0d\u89c1\uff01 \u3000\u3000\u600e\u80fd\u4e0d\u8bf4\u662f\u4e00\u5927\u602a\u4e8b\uff1f \u3000\u3000\u4e00\u65f6\u4e4b\u95f4\uff0c\u6240\u6709\u7684\u4eba\uff0c\u90fd\u6014\u5728\u5f53\u5730\u3002 \u3000\u3000\u83ab\u975e\u5df2\u88ab\u970d\u5143\u4f3d\u6240\u76d7\uff1f \u3000\u3000\u970d\u5143\u4f3d\u76d7\u5c38\u4f53\u53c8\u6709\u4f55\u5c45\u5fc3\uff1f \u3000\u3000\u5728\u65e0\u6cd5\u89e3\u5f00\u60ac\u7591\u8c1c\u56e2\u4e4b\u4e0b\uff0c\u7fa4\u8c6a\u53ea\u597d\u628a\u6316\u5f00\u7684\u51b0\u96ea\uff0e\u91cd\u65b0\u79fb\u56de\u539f\u5904\uff0c\u3000\u4e0b\u5c71\u800c\u53bb\u3002 \u3000\u3000\u5c31\u5728\u5f53\u65e5\u4e0b\u5348\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u72ec\u81ea\u4e00\u4eba\u79bb\u5f00\u8ff7\u8e2a\u8c37\uff0c\u5f80\u5415\u6881\u5c71\u800c\u6765\uff0e \u3000\u3000\u521a\u51fa\u8c37\u53e3\u4e0d\u4e45\uff0e\u5ffd\u89c1\u4e00\u540d\u5168\u8eab\u767d\u8863\u7684\u7f8e\u8273\u5c11\u5987\uff0c\u8fce\u9762\u800c\u6765\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u51e0\u4e4e\u4e0d\u6562\u76f8\u4fe1\u81ea\u5df1\u7684\u773c\u775b\uff0c\u8fd9\u5c11\u5987\u8d6b\u7136\u662f\u82d7\u7d20\u5170\u3002 \u3000\u3000\u82d7\u7d20\u5170\u62e6\u4f4f\u9ea6\u5c0f\u660e\u7684\u53bb\u8def\u9053\uff1a\u201c\u4f60\u8981\u5230\u54ea\u91cc\u53bb\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u4e0d\u89c9\u60c5\u7eea\u6fc0\u52a8\uff0e\u53cc\u76ee\u7d27\u76ef\u7740\u82d7\u7d20\u5170\u7684\u8138\u8272\u9053\uff1a\u201c\u82d7\u59d0\u59d0\uff0c\u73b0\u5728\u56db\u4e0b\u65e0\u4eba\uff0e\u4e0d\u59a8\u8bf4 \u5b9e\u8bdd\uff0c\u4f60\u6295\u9760\u4e86\u970d\u5143\u4f3d\u90a3\u4e2a\u8001\u738b\u516b\u86cb\uff0c\u5230\u5e95\u662f\u771f\u5fc3\uff0c\u8fd8\u662f\u5047\u610f\uff1f\u201d \u3000\u3000\u8c01\u77e5\u82d7\u7d20\u5170\u7adf\u5ae3\u7136\u4e00\u7b11\u9053\uff1a\u201c\u5f53\u7136\u662f\u51fa\u4e8e\u6211\u7684\u81ea\u613f\uff0c\u4f60\u95ee\u8fd9\u4e9b\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u5446\u4e86\u4e00\u5446\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u4f60\u7adf\u771f\u7684\u505a\u51fa\u80cc\u53db\u6211\u4eec\u5927\u5bb6\u7684\u4e8b\uff1f\u201d \u3000\u3000\u82d7\u7d20\u5170\u4f9d\u7136\u7b11\u5bb9\u672b\u655b\u9053\uff1a\u201c\u5c0f\u660e\u5144\u5f1f\uff0c\u4f60\u9519\u5566\uff01\u6211\u4f55\u66fe\u80cc\u53db\u4f60\u4eec\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u4e00\u54ac\u7259\uff0c\u51b7\u58f0\u9053\uff1a\u201c\u60f3\u5f53\u5e74\u6211\u5e08\u5144\u80e1\u67cf\u9f84\u548c\u5e08\u5ac2\u8c37\u5bd2\u9999\u5f85\u4f60\u4e0d\u8584\uff0c\u800c\u970d\u5143\u4f3d\u5374\u4e00 \u76f4\u662f\u4ed6\u4eec\u4e24\u4f4d\u7684\u5fc3\u8179\u4e4b\u60a3\uff0c\u73b0\u5728\u4f60\u7518\u505a\u970d\u5143\u4f3d\u7684\u59bb\u5b50\uff0e\u600e\u80fd\u4e0d\u8bf4\u662f\u80cc\u53db\u4e86\u4ed6\u4eec\uff0c\u4e5f\u80cc\u53db\u4e86\u6211 \u4eec\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u56e0\u662f\u9146\u79cb\u7684\u5f1f\u5b50\uff0c\u800c\u9146\u79cb\u53c8\u662f\u80e1\u67cf\u9f84\u7684\u5e08\u53d4\uff0e\u662f\u4ee5\u80e1\u67cf\u9f84\u548c\u8c37\u5bd2\u9999\u751f\u524d\uff0e\u4ed6\u5bf9\u4e24 \u4eba\u90fd\u662f\u4ee5\u5e08\u5144\u548c\u5e08\u5ac2\u76f8\u79f0\u3002 \u3000\u3000\u82d7\u7d20\u5170\u6447\u5934\u9053\uff1a\u201c\u4f60\u53c8\u9519\u5566\uff01\u73b0\u5728\u949f\u526f\u76df\u4e3b\u548c\u4f60\u4eec\u51e0\u4f4d\uff0c\u4ecd\u662f\u8ff7\u8e2a\u8c37\u7eff\u6797\u603b\u5be8\u7684\u4eba\uff0e\u800c \u970d\u5143\u4f3d\u662f\u76df\u4e3b\uff0c\u4f60\u4eec\u5927\u5bb6\u600e\u53ef\u4e0d\u62e5\u6234\u4ed6\uff1f\u6211\u8ddf\u4ed6\u53c8\u6709\u4ec0\u4e48\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u51e0\u4e4e\u6c14\u660f\uff0c\u731b\u4e00\u8dfa\u811a\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u4f60\u7adf\u8bf4\u51fa\u8fd9\u79cd\u8bdd\u6765\u3002\u82d7\u59d1\u5a18\uff0e\u518d\u89c1\u5566\uff01\u201d \u3000\u3000\u8bf4\u5b8c\u8bdd\uff0c\u4ed6\u7531\u82d7\u7d20\u5170\u8eab\u65c1\u64e6\u8eab\u800c\u8fc7\uff0c\u5feb\u6b65\u5411\u524d\u75be\u5954\u800c\u53bb\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u81ea\u5e7c\u65e0\u7236\u65e0\u6bcd\uff0e\u5148\u88ab\u5929\u53f0\u4e07\u82b1\u5bab\u4e3b\u4f5f\u516c\u5e38\u6536\u517b\uff0c\u540e\u6765\u53c8\u6539\u6295\u9146\u79cb\u95e8\u4e0b\uff0c\u56e0\u4e4b\uff0e\u5bf9 \u6bd4\u4ed6\u5e74\u957f\u4e14\u53c8\u59ff\u5bb9\u79c0\u4e3d\u7684\u5973\u5b50\uff0c\u7279\u522b\u5177\u6709\u597d\u611f\u3002 \u3000\u3000\u4ece\u524d\uff0c\u4ed6\u5bf9\u8c37\u5bd2\u9999\u4fbf\u79c1\u4e0b\u7231\u6155\u4e0d\u5df2\uff0c\u6b63\u56e0\u5982\u6b64\uff0c\u4ed6\u5728\u8ff7\u8e2a\u8c37\uff0c\u9664\u4e86\u670d\u80e1\u67cf\u9f84\u5916\uff0e\u5728\u8c37\u5bd2 \u9999\u9762\u524d\uff0c\u4e5f\u662f\u767e\u4f9d\u767e\u987a\u3002 \u3000\u3000\u8fde\u5e26\u7684\u4e5f\u5bf9\u82d7\u7d20\u5170\u5177\u6709\u597d\u611f\uff0c\u6240\u4ee5\u6628\u65e5\u4ed6\u624d\u8981\u6c42\u82d7\u7d20\u5170\u548c\u4ed6\u4e00\u8d77\u5230\u5415\u6881\u5c71\u3002 \u3000\u3000\u4f46\u5982\u4eca\u82d7\u7d20\u5170\u53d8\u4e86\uff0c\u53d8\u5f97\u4f7f\u4ed6\u7531\u7231\u6155\u800c\u751f\u5992\u6068\uff0e\u7136\u800c\u4ecd\u7136\u4e0d\u5fcd\u5fc3\u4f24\u5bb3\u5979\uff0c\u53ea\u6709\u8d1f\u6c14\u4e0d\u518d \u548c\u5979\u642d\u8baa\u3002 \u3000\u3000\u8c01\u77e5\u521a\u8d70\u51e0\u6b65\uff0c\u4fbf\u542c\u8eab\u540e\u53eb\u9053\uff1a\u201c\u522b\u8d70\uff0c\u6211\u8fd8\u6709\u8bdd\u8ddf\u4f60\u8bb2\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u6b62\u6b65\u56de\u8eab\u9053\uff1a\u201c\u8bdd\u4e0d\u6295\u673a\u534a\u53e5\u591a\uff0c\u4f60\u73b0\u5728\u5df2\u662f\u970d\u5143\u4f3d\u90a3\u8001\u738b\u516b\u86cb\u7684\u4eba\u4e86\uff0e\u8ddf\u6211\u8fd8 \u6709\u4ec0\u4e48\u8bdd\u53ef\u8bb2\uff1f\u201d \u3000\u3000\u82d7\u7d20\u5170\u5ffd\u7136\u86fe\u7709\u4e00\u8038\uff0c\u6ee1\u9762\u51b0\u971c\uff0c\u53f1\u9053\uff1a\u201c\u4f4f\u5634\uff0c\u970d\u76df\u4e3b\u53ef\u662f\u4f60\u968f\u4fbf\u9a82\u7684\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u543c\u9053\uff1a\u201c\u6211\u504f\u8981\u9a82\u90a3\u8001\u738b\u516b\u86cb\uff0c\u4f60\u51c6\u5907\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u82d7\u7d20\u5170\u8f7b\u54b3\u4e00\u58f0\uff0c\u6b63\u8272\u9053\uff1a\u201c\u8981\u77e5\u9053\u54b1\u4eec\u7eff\u6797\u4e4b\u4e2d\uff0c\u6240\u4ee5\u4f1a\u88ab\u5c11\u6797\u3001\u6b66\u5f53\u7b49\u81ea\u55bb\u4e3a\u6b63\u5927 \u95e8\u6d3e\u4e2d\u4eba\u770b\u4e0d\u8d77\uff0c\u4e3b\u8981\u5c31\u662f\u56e0\u4e3a\u4e92\u4e0d\u76f8\u5bb9\uff0c\u5404\u81ea\u5360\u5c71\u4e3a\u738b\u3002 \u3000\u3000\u5982\u4eca\u970d\u5143\u4f3d\u5df2\u662f\u7eff\u6797\u76df\u4e3b\uff0e\u6211\u4eec\u6b63\u8be5\u5bf9\u4ed6\u4e00\u4f53\u62e5\u6234\uff0c\u82e5\u518d\u52fe\u5fc3\u6597\u89d2\uff0c\u5c82\u4e0d\u81ea\u53d6\u706d\u4ea1\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u54fc\u4e86\u58f0\u9053\uff1a\u201c\u4f60\u60f3\u6765\u6559\u8bad\u6211\uff1f\u82d7\u59d1\u5a18\uff0e\u4f60\u4e00\u4e2a\u6295\u964d\u53d8\u8282\u5df2\u7ecf\u591f\u4e86\uff0e\u7528\u4e0d\u7740\u518d\u8bb2\u522b \u7684\u5e9f\u8bdd\uff0c\u4eca\u5929\u82e5\u4e0d\u770b\u5728\u80e1\u3001\u8c37\u4e24\u4f4d\u524d\u76df\u4e3b\u7684\u4efd\u4e0a\uff0c\u6211\u5c31\u2026\u2026\u201d \u3000\u3000\u201c\u4f60\u5c31\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u201c\u6211\u5c31\u6740\u4e86\u4f60\uff01\u8fd9\u91cc\u56db\u4e0b\u65e0\u4eba\uff0e\u6211\u8981\u6740\u4f60\uff0e\u6839\u672c\u4e0d\u8d39\u5439\u7070\u4e4b\u529b\uff01\u201d \u3000\u3000\u82d7\u7d20\u5170\u7adf\u771f\u7684\u62ac\u8d77\u4e24\u53ea\u767d\u4f3c\u7389\u8f6f\u5982\u7ef5\u7684\u7ea4\u7ea4\u7389\u624b\u3001\u89e3\u5f00\u9886\u53e3\u95f4\u7684\u8863\u94ae\uff0c\u9732\u51fa\u6676\u83b9\u7c89\u5ae9\u7684 \u7389\u9888\u9053\uff1a\u201c\u5c0f\u660e\uff0c\u65e2\u8981\u6740\u6211\uff0c\u5c31\u8fc7\u6765\u6740\u5427\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u8f7b\u6309\u5251\u628a\uff0c\u9ed1\u767d\u5206\u660e\u7684\u773c\u73e0\u8f6c\u4e86\u51e0\u8f6c\uff0c\u5374\u53c8\u6447\u5934\u9053\uff1a\u201c\u4f60\u662f\u970d\u5143\u4f3d\u7684\u8001\u5a46\uff0e\u5c06\u6765 \u603b\u6709\u4e00\u5929\u4f1a\u6b7b\u5728\u90a3\u8001\u738b\u516b\u86cb\u7684\u624b\u91cc\uff0c\u7528\u4e0d\u7740\u6211\u6765\u6740\u3002\u201d \u3000\u3000\u201c\u4f60\u65e2\u4e0d\u6740\u6211\uff0c\u5c31\u8be5\u56de\u7b54\u6211\u7684\u8bdd\uff01\u201d \u3000\u3000\u201c\u6709\u8bdd\u5feb\u8bb2\uff0c\u6709\u5c41\u5feb\u653e\uff01\u201d \u3000\u3000\u82d7\u7d20\u5170\u76b1\u7709\u9053\uff1a\u201c\u4f60\u51fa\u8c37\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u51b7\u7b11\u9053\uff1a\u201c\u53ef\u6709\u5fc5\u8981\u544a\u8bc9\u4f60\uff01\u201d \u3000\u3000\u201c\u6211\u662f\u76df\u4e3b\u592b\u4eba\uff0c\u6709\u6743\u8fc7\u95ee\u3002\u201d \u3000\u3000\u201c\u8fde\u970d\u5143\u4f3d\u90a3\u8001\u738b\u516b\u86cb\u90fd\u6ca1\u95ee\uff0c\u4f60\u4f55\u5fc5\u591a\u7ba1\u95f2\u4e8b\uff01\u201d \u3000\u3000\u201c\u5176\u5b9e\u4f60\u4e0d\u8bf4\u6211\u4e5f\u660e\u767d\uff0c\u4f60\u662f\u5230\u5415\u6881\u5c71\u6c42\u53d6\u2018\u5411\u5fc3\u9732\u2019\u89e3\u836f\u3002\u201d \u3000\u3000\u201c\u4f60\u65e2\u7136\u77e5\u9053\uff0c\u90a3\u5c31\u66f4\u4e0d\u8be5\u660e\u77e5\u6545\u95ee\u3002\u201d \u3000\u3000\u82d7\u7d20\u5170\u7f04\u9ed8\u4e86\u534a\u54cd\u9053\uff1a\u201c\u6211\u529d\u4f60\u522b\u53bb\u4e86\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4f60\u8fd9\u8bdd\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u82d7\u7d20\u5170\u9053\uff1a\u201c\u6211\u66fe\u505a\u8fc7\u9634\u624b\u4e00\u9b54\u7684\u95e8\u4e0b\uff0c\u5f53\u7136\u660e\u4e86\u9634\u624b\u4e00\u9b54\u7684\u4e3a\u4eba\uff0c\u4ed6\u4f4f\u7684\u90a3\u5904\u5c71\u6d1e\uff0c \u673a\u5173\u91cd\u91cd\uff0c\u4ed6\u672c\u4eba\u53c8\u9634\u9669\u6bd2\u8fa3\uff0c\u4f60\u82e5\u5f53\u771f\u524d\u53bb\uff0c\u53ea\u6015\u51f6\u591a\u5409\u5c11\uff0c\u7b49\u4e8e\u8089\u5305\u5b50\u6253\u72d7\u3000\u2014\u6709\u53bb\u65e0 \u56de\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u7efd\u989c\u4e00\u7b11\uff0c\u9732\u51fa\u96ea\u767d\u800c\u53c8\u6574\u9f50\u7684\u524d\u9f7f\u9053\uff1a\u201c\u539f\u6765\u4f60\u8fd8\u5173\u5fc3\u6211\uff1f\u201d \u3000\u3000\u82d7\u7d20\u5170\u9053\uff1a\u201c\u4f60\u66fe\u662f\u80e1\u3001\u8c37\u4e24\u4f4d\u524d\u76df\u4e3b\u7684\u5f97\u529b\u624b\u4e0b\uff0c\u66fe\u4e3a\u4ed6\u4eec\u51fa\u8fc7\u4e0d\u5c11\u529b\uff0c\u6211\u5f53\u7136\u5173\u5fc3 \u4f60\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u53c8\u662f\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u4f60\u82e5\u5fc3\u91cc\u8fd8\u6709\u80e1\u3001\u8c37\u4e24\u4f4d\u76df\u4e3b\uff0c\u5c31\u4e0d\u8be5\u5ac1\u7ed9\u970d\u5143\u4f3d\u90a3\u8001\u738b\u516b \u86cb\uff01\u201d \u3000\u3000\u201c\u8fd9\u662f\u4e24\u56de\u4e8b\uff0c\u4e0d\u80fd\u6df7\u4e3a\u4e00\u8c08\u3002\u201d \u3000\u3000\u201c\u90a3\u6709\u8fd9\u79cd\u4e24\u56de\u4e8b\uff1f\u7b80\u76f4\u662f\u5c82\u6709\u6b64\u7406\uff01\u201d \u3000\u3000\u201c\u54b1\u4eec\u4e0d\u8c08\u8fd9\u4e9b\u597d\u4e0d\u597d\uff0c\u6211\u53ea\u6c42\u4f60\u522b\u5230\u5415\u6881\u5c71\u53bb\u3002\u201d \u3000\u3000\u201c\u6211\u5df2\u7ecf\u53bb\u5b9a\u4e86\uff0c\u4f60\u518d\u8bf4\u4e5f\u662f\u5e9f\u8bdd\u3002\u201d \u3000\u3000\u201c\u5c0f\u660e\uff0c\u542c\u6211\u7684\u8bdd\uff0c\u6b64\u53bb\u5b9e\u5728\u592a\u5371\u9669\u4e86\uff0c\u4f60\u8fd8\u5e74\u8f7b\uff0c\u51ed\u4f60\u7684\u6b66\u529f\uff0c\u5c06\u6765\u5728\u6b66\u6797\u4e2d\u4e0d\u96be\u6709 \u51fa\u5934\u4e4b\u65e5\uff0c\u8bf4\u4e0d\u5b9a\u8fde\u76df\u4e3b\u4e5f\u6709\u4f60\u5e72\u7684\uff0c\u82e5\u8fd9\u6837\u628a\u6027\u547d\u5e73\u767d\u9001\u5728\u9634\u624b\u4e00\u9b54\u624b\u91cc\uff0e\u5b9e\u5728\u662f\u4ef6\u53ef\u60dc \u7684\u4e8b\u60c5\uff01\u201d \u3000\u3000\u201c\u6211\u660e\u767d\u4f60\u963b\u62e6\u6211\u53bb\u7684\u539f\u56e0\uff1f\u201d \u3000\u3000\u201c\u4f60\u8bf4\uff0e\u4ec0\u4e48\u539f\u56e0\uff1f\u201d \u3000\u3000\u201c\u4f60\u62c5\u5fc3\u6211\u53d6\u56de\u89e3\u836f\uff0c\u6551\u4e86\u5f20\u5e08\u5144\uff0e\u90a3\u65f6\u970d\u5143\u4f3d\u7684\u76df\u4e3b\u5c31\u5e72\u4e0d\u6210\uff0c\u970d\u5143\u4f3d\u57ae\u4e86\u53f0\uff0c\u4f60\u4e5f \u5e72\u4e0d\u6210\u76df\u4e3b\u592b\u4eba\uff0c\u5bf9\u4e48\uff1f\u201d \u3000\u3000\u82d7\u7d20\u5170\u76b1\u7709\u4e00\u7b11\u9053\uff1a\u201c\u4f60\u60f3\u5f97\u592a\u591a\u4e86\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4e00\u70b9\u4e5f\u4e0d\u591a\uff0e\u522b\u770b\u6211\u5e74\u7eaa\u8f7b\uff0c\u53ea\u8981\u4f60\u7fc5\u8180\u4e00\u52a8\uff0c\u6211\u5c31\u77e5\u9053\u8981\u5f80\u54ea\u91cc\u98de \u5566\uff01\u201d \u3000\u3000\u82d7\u7d20\u5170\u65e0\u5948\u7684\u6447\u6447\u5934\u9053\uff1a\u201c\u65e2\u7136\u65e0\u6cd5\u529d\u963b\u4f60\uff0c\u90a3\u5c31\u7531\u4f60\u53bb\u5427\uff0e\u4f46\u613f\u4ee5\u540e\u8fd8\u80fd\u518d\u770b\u5230\u4e86 \u4f60\uff01\u201d \u3000\u3000\u201c\u4f60\u4e0d\u5fc5\u770b\u6211\u5566\uff01\u6211\u54ea\u91cc\u6709\u970d\u5143\u4f3d\u90a3\u8001\u516b\u86cb\u597d\u770b\uff01\u201d \u3000\u3000\u201c\u4ed6\u54ea\u91cc\u597d\u770b\uff1f\u201d \u3000\u3000\u201c\u4ed6\u6bd4\u6211\u591a\u4e86\u51e0\u6837\u4e1c\u897f\uff0c\u5f53\u7136\u597d\u770b\u3002\u201d \u3000\u3000\u201c\u6bd4\u4f60\u591a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u6211\u6ca1\u6709\u80e1\u5b50\uff0e\u4e5f\u6ca1\u76b1\u7eb9\uff0c\u4ed6\u5374\u6ee1\u8138\u6a2a\u8089\uff0e\u6211\u662f\u4e2a\u65e0\u540d\u5c0f\u5352\uff0c\u4ed6\u5374\u662f\u5802\u5802\u7684\u5929\u4e0b\u7eff\u6797\u76df \u4e3b\uff0c\u8fd9\u8fd8\u4e0d\u591f\u4e48\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u8bf4\u5b8c\u8bdd\uff0c\u5934\u4e5f\u4e0d\u56de\uff0c\u75be\u5954\u800c\u53bb\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u4e00\u53e3\u6c14\u5954\u884c\u4e86\u597d\u51e0\u91cc\u8def\u624d\u505c\u4e0b\u811a\u6765\u3002 \u3000\u3000\u4ed6\u867d\u7136\u5014\u5f3a\u6210\u6027\uff0c\u4f46\u5bf9\u82d7\u7d20\u5170\u90a3\u756a\u8bdd\uff0c\u5374\u4e5f\u4e0d\u80fd\u65e0\u52a8\u4e8e\u8877\u3002 \u3000\u3000\u9634\u624b\u2014\u9b54\u90a3\u8001\u9b54\u5934\uff0c\u7684\u786e\u9634\u9669\u6bd2\u8fa3\uff0c\u6076\u540d\u662d\u5f70\uff0e \u3000\u3000\u5f53\u5e74\u82e5\u4e0d\u662f\u82d7\u7d20\u5170\u820d\u547d\u6697\u4e2d\u76f8\u52a9\uff0c\u8fde\u80e1\u67cf\u9f84\u90a3\u6837\u76d6\u4e16\u82f1\u96c4\u7684\u4eba\u7269\uff0c\u90fd\u5dee\u4e00\u70b9\u683d\u5728\u4ed6\u624b \u91cc\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u867d\u7136\u81ea\u5fd6\u81ea\u5df1\u7684\u6b66\u529f\u4e0d\u51e1\uff0c\u4f46\u82e5\u548c\u80e1\u67cf\u9f84\u76f8\u6bd4\uff0c\u4e0d\u8bba\u6b66\u529f\u548c\u673a\u667a\uff0c\u5b9e\u5728\u8fd8\u5dee\u5f97\u592a \u8fdc\u3002 \u3000\u3000\u4e00\u8def\u6653\u884c\u591c\u5bbf\uff0c\u4e03\u516b\u5929\u540e\uff0c\u5df2\u5230\u8fbe\u5415\u7cb1\u5c71\u3002 \u3000\u3000\u5415\u6881\u5c71\u7ef5\u5ef6\u6570\u767e\u91cc\uff0c\u7eb5\u8d70\u4e8e\u9ec4\u3001\u6c7e\u4e24\u6cb3\u4e4b\u95f4\uff0c\u5317\u63a5\u4e91\u4e2d\u5c71\uff0c\u5728\u664b\u7701\u5c71\u8109\u4e2d\uff0c\u5e73\u5747\u6700\u9ad8\uff0e \u3000\u3000\u9ea6\u5c0f\u660e\u5728\u5165\u5c71\u4e4b\u524d\uff0c\u7279\u5730\u5e26\u4e86\u4e9b\u5e72\u7cae\uff0c\u5165\u5c71\u4e4b\u540e\uff0c\u81ea\u6668\u81f3\u5348\uff0c\u7adf\u7136\u6ca1\u53d1\u73b0\u4e00\u6237\u4eba\u5bb6\uff0e\u66f4 \u522b\u8bf4\u80fd\u9047\u5230\u8def\u4eba\u3002 \u5367\u9f99\u751f\u300a\u5999\u7edd\u5929\u9999\u300b \u7b2c \u4e94 \u56de\u3000\u72ec\u95ef\u9b54\u6d1e \u3000\u3000\u9ea6\u5c0f\u660e\u4e00\u4e2a\u4eba\u5728\u91cd\u5c71\u53e0\u5d82\u4e2d\uff0c\u8dcb\u6d89\u5954\u6ce2\u7740\u3002 \u3000\u3000\u4ed6\u770b\u770b\u592a\u9633\u5373\u5c06\u897f\u6c89\uff0c\u4f9d\u7136\u6beb\u65e0\u6240\u83b7\u3002 \u3000\u3000\u9762\u5bf9\u65e0\u5c3d\u7684\u5c71\u5ce6\u65f7\u91ce\uff0e\u3000\u4e00\u5411\u5929\u4e0d\u6015\u5730\u4e0d\u6015\u7684\u4ed6\uff0c\u4e5f\u6709\u4e9b\u80c6\u602f\u8d77\u6765\u3002 \u3000\u3000\u56db\u91ce\u832b\u832b\uff0e\u8fde\u591c\u665a\u4f4f\u5bbf\u4e4b\u5904\uff0c\u4e5f\u96be\u4ee5\u5bfb\u89c5\u3002 \u3000\u3000\u6b63\u5728\u5f98\u5f8a\u65e0\u4e3b\u4e4b\u65f6\uff0c\u5ffd\u542c\u524d\u9762\u5c97\u5ce6\u8f6c\u811a\u5904\uff0c\u4f20\u6765\u811a\u6b65\u58f0\u54cd\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u987f\u65f6\u5982\u83b7\u6551\u661f\uff0c\u6025\u6b65\u5411\u811a\u6b65\u58f0\u5904\u5954\u53bb\u3002 \u3000\u3000\u6765\u4eba\u5f88\u5feb\u4fbf\u51fa\u73b0\u5728\u8f6c\u89d2\u5904\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u4e0d\u89c9\u773c\u524d\u4e00\u4eae\uff0c\u6765\u4eba\u7adf\u662f\u4f4d\u59ff\u5bb9\u7edd\u4f73\uff0c\u98ce\u97f5\u695a\u695a\u7684\u767d\u8863\u5973\u90ce\u3002 \u3000\u3000\u8bf4\u6765\u4e5f\u602a\uff0e\u9ea6\u5c0f\u660e\u5bf9\u8fd9\u767d\u8863\u5973\u90ce\u7adf\u6709\u4e9b\u4f3c\u66fe\u76f8\u8bc6\u7684\u9762\u719f\u611f\u89c9\u3002 \u3000\u3000\u539f\u6765\u8fd9\u767d\u8863\u5973\u90ce\uff0c\u4e0d\u8bba\u9762\u8c8c\u3001\u8eab\u6750\u548c\u7a7f\u7740\uff0c\u90fd\u9887\u4f3c\u82d7\u7d20\u5170\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u56e0\u5bf9\u82d7\u7d20\u5170\u65e9\u5c31\u79c1\u4e0b\u7231\u6155\uff0c\u81ea\u7136\u5bf9\u773c\u524d\u7684\u767d\u8863\u5973\u90ce\u4e5f\u9887\u6709\u597d\u611f\u3002 \u3000\u3000\u770b\u8fd9\u767d\u8863\u5973\u90ce\u7684\u6c14\u8d28\u548c\u7a7f\u7740\uff0e\u7edd\u4e0d\u4f3c\u5c71\u4e2d\u5c45\u6c11\u5bb6\u4eba\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u4e24\u4eba\u5df2\u7ecf\u8d70\u8fd1\uff0c\u9ea6\u5c0f\u660e\u521a\u8981\u5f00\u53e3\uff0c\u90a3\u767d\u8863\u5973\u90ce\u5df2\u554a\u4e86\u4e00\u58f0\uff0c\u62a2\u5148\u95ee\u9053\uff1a\u201c\u4f60\u53ef\u662f \u5317\u5cb3\u6052\u5c71\u8ff7\u8e2a\u8c37\u7684\u4eba\uff1f\u600e\u4e48\u6765\u5230\u8fd9\u91cc\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u6014\u4e86\u6014\u9053\uff1a\u201c\u59d1\u5a18\u600e\u4f1a\u8ba4\u8bc6\u6211\uff1f\u201d \u3000\u3000\u767d\u8863\u5973\u90ce\u7b11\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u66fe\u89c1\u8fc7\u4f60\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u832b\u7136\uff0c\u9053\uff1a\u201c\u59d1\u5a18\u5728\u54ea\u513f\u89c1\u8fc7\u6211\uff1f\u201d \u3000\u3000\u767d\u8863\u5973\u90ce\u9053\uff1a\u201c\u4e0a\u4e2a\u6708\uff0c\u5728\u5929\u53f0\u5c71\u4e07\u82b1\u5bab\u524d\uff0c\u6211\u66fe\u770b\u5230\u4f60\u8ddf\u968f\u5728\u7eff\u6797\u76df\u4e3b\u8c37\u5bd2\u9999\u7684\u8eab \u65c1\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u7acb\u5373\u4e5f\u8f7b\u54e6\u2014\u58f0\u9053\uff1a\u201c\u83ab\u975e\u59d1\u5a18\u4e5f\u53c2\u52a0\u8fc7\u90a3\u6b21\u60e8\u7edd\u4eba\u5bf0\u7684\u5927\u4f1a\u6218\uff1f\u201d \u3000\u3000\u767d\u8863\u5973\u90ce\u70b9\u70b9\u5934\u9053\uff1a\u201c\u6211\u867d\u7136\u5f53\u65f6\u5728\u573a\uff0e\u4f46\u5374\u4e0d\u66fe\u52a8\u8fc7\u624b\uff0c\u53ea\u80fd\u7b97\u662f\u2014\u540d\u8896\u624b\u65c1\u89c2 \u8005\u3002\u201d \u3000\u3000\u201c\u4e0d\u77e5\u59d1\u5a18\u662f\u54ea\u95e8\u54ea\u6d3e\u7684\u95e8\u4e0b\uff1f\u201d \u3000\u3000\u201c\u6211\u65e0\u95e8\u65e0\u6d3e\uff0e\u5f53\u65f6\u662f\u5e08\u7236\u53bb\u7684\u3002\u201d \u3000\u3000\u201c\u4ee4\u5e08\u662f\u54ea\u4f4d\u524d\u8f88\u9ad8\u4eba\uff1f\u201d \u3000\u3000\u767d\u8863\u5973\u90ce\u4f3c\u662f\u9887\u611f\u4e3a\u96be\u5730\u987f\u4e86\u4e00\u987f\u9053\uff1a\u201c\u4f60\u4f55\u5fc5\u95ee\u90a3\u4e48\u591a\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u51b2\u53e3\u53eb\u9053\uff1a\u201c\u4ee4\u5e08\u53ef\u662f\u9634\u624b\u4e00\u9b54\u8001\u524d\u8f88\uff1f\u201d \u3000\u3000\u53ea\u56e0\u9634\u624b\u4e00\u9b54\u81f3\u4eca\u65e0\u4eba\u77e5\u9053\u4ed6\u7684\u771f\u6b63\u59d3\u540d\uff0c\u5f53\u7740\u4ed6\u7684\u5f1f\u5b50\u9762\u524d\uff0c\u867d\u7136\u5f84\u79f0\u201c\u9634\u624b\u4e00\u9b54\u201d \u4e0d\u592a\u793c\u8c8c\uff0c\u4f46\u53c8\u627e\u4e0d\u5230\u522b\u7684\u4ee3\u540d\u8bcd\u3002 \u3000\u3000\u767d\u8863\u5973\u90ce\u8e59\u4e86\u8e59\u9edb\u7709\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\uff0c\u627e\u662f\u4ed6\u8001\u4eba\u5bb6\u7684\u95e8\u4e0b\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u9634\u624b\u8001\u524d\u8f88\u4f4f\u5728\u5415\u6881\u5c71\uff0e\u8fd9\u91cc\u6b63\u662f\u5415\u6881\u5c71\uff0c\u59d1\u5a18\u5728\u5c71\u4e0a\u51fa\u73b0\uff0c\u6211\u5f53\u7136\u731c\u5f97 \u5230\u3002\u201d \u3000\u3000\u767d\u8863\u5973\u90ce\u9053\uff1a\u201c\u7b97\u662f\u88ab\u4f60\u731c\u4e2d\u4e86\u3002\u201d \u3000\u3000\u8fd9\u771f\u662f\u5e94\u4e86\u201c\u8e0f\u7834\u94c1\u978b\u65e0\u89c5\u5904\uff0e\u5f97\u6765\u5168\u4e0d\u8d39\u5de5\u592b\u201d\u90a3\u53e5\u8bdd\uff0c\u9ea6\u5c0f\u660e\u987f\u65f6\u6b23\u559c\u82e5\u72c2\u3002 \u3000\u3000\u53ea\u542c\u767d\u8863\u5973\u90ce\u9053\uff1a\u201c\u4f60\u8fd8\u6ca1\u8bb2\uff0c\u5230\u5415\u6881\u5c71\u6765\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u6b63\u662f\u6765\u6c42\u89c1\u4ee4\u5e08\u9634\u624b\u8001\u524d\u8f88\uff0c\u5e78\u597d\u9047\u89c1\u4e86\u59d1\u5a18\u3002\u201d \u3000\u3000\u767d\u8863\u5973\u90ce\u7728\u4e86\u7728\u661f\u7738\u9053\uff1a\u201c\u4f60\u6c42\u89c1\u5bb6\u5e08\uff0c\u5fc5\u5b9a\u6709\u91cd\u8981\u4e8b\u60c5\u4e86\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u6697\u9053\uff1a\u201c\u9634\u624b\u4e00\u9b54\u548c\u6211\u5e08\u7236\u53ef\u80fd\u6709\u4ea4\u60c5\uff0c\u82e5\u4e0d\u628a\u5e08\u7236\u9146\u79cb\u62ac\u51fa\u6765\uff0c\u4ed6\u53ef\u80fd\u4e0d\u80af\u63a5 \u89c1\u3002\u201d \u3000\u3000\u60f3\u5230\u8fd9\u91cc\uff0c\u968f\u5373\u88c5\u6a21\u4f5c\u6837\u7684\u9053\uff1a\u201c\u5728\u4e0b\u662f\u5949\u5bb6\u5e08\u4e4b\u547d\uff0c\u6765\u6c42\u89c1\u4ee4\u5e08\u7684\u3002\u201d \u3000\u3000\u201c\u4f60\u5e08\u7236\u53c8\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u5bb6\u5e08\u4eba\u79f0\u795e\u6756\u7fc1\uff0e\u59d3\u9146\u540d\u79cb\uff0e\u201d \u3000\u3000\u201c\u539f\u6765\u4f60\u662f\u9102\u8001\u524d\u8f88\u7684\u5f1f\u5b50\uff0c\u5931\u793c\u5931\u793c\uff01\u201d \u3000\u3000\u201c\u59d1\u5a18\u53ef\u5426\u4e3a\u6211\u5e26\u8def\uff1f\u201d \u3000\u3000\u201c\u65e2\u7136\u4f60\u662f\u5949\u4ee4\u5e08\u4e4b\u547d\u6765\u89c1\u5bb6\u5e08\uff0e\u6211\u5f53\u7136\u8981\u66ff\u4f60\u5e26\u8def\uff0c\u90a3\u5c31\u968f\u6211\u8d70\u5427\uff01\u201d \u3000\u3000\u767d\u8863\u5973\u90ce\u8bf4\u7740\uff0e\u8f6c\u8eab\u5f80\u524d\u8d70\u53bb\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u8fb9\u8d70\u8fb9\u95ee\u9053\uff1a\u201c\u8bf7\u95ee\u59d1\u5a18\u4e0a\u59d3\u82b3\u540d\uff1f\u201d \u3000\u3000\u767d\u8863\u5973\u90ce\u9053\uff1a\u201c\u4f60\u8be5\u5148\u544a\u8bc9\u6211\u3001\u59d3\u4ec0\u4e48\u53eb\u4ec0\u4e48\u624d\u597d\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u6211\u53eb\u9ea6\u5c0f\u660e\u3002\u201d \u3000\u3000\u767d\u8863\u5973\u90ce\u9053\uff1a\u201c\u6211\u8bb0\u4e0b\u4e86\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u59d1\u5a18\u8fd8\u6ca1\u544a\u77e5\u82b3\u540d\uff1f\u201d \u3000\u3000\u201c\u6211\u53ea\u8d1f\u8d23\u66ff\u4f60\u5e26\u8def\uff0c\u81f3\u4e8e\u59d3\u4ec0\u4e48\u53eb\u4ec0\u4e48\uff0c\u4f60\u4e0d\u5fc5\u77e5\u9053\u3002\u201d \u3000\u3000\u201c\u59d1\u5a18\u771f\u662f\u4e0d\u5403\u4e8f\u554a\uff01\u201d \u3000\u3000\u201c\u8fd9\u6709\u4ec0\u4e48\u5403\u4e8f\u4e0d\u5403\u4e8f\uff0c\u6211\u77e5\u9053\u4f60\u7684\u540d\u5b57\uff0c\u662f\u4e3a\u4e86\u5411\u5bb6\u5e08\u901a\u62a5\uff0c\u4f60\u77e5\u4e0d\u77e5\u9053\u6211\u7684\u540d\u5b57\uff0c \u5374\u4e00\u70b9\u5173\u7cfb\u4e5f\u6ca1\u6709\uff0c\u6211\u8bf4\u5f97\u4e0d\u5bf9\u4e48\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u53ea\u597d\u518d\u95ee\u522b\u7684\u9053\uff1a\u201c\u8d35\u5904\u79bb\u8fd9\u91cc\u8fd8\u6709\u591a\u5c11\u8def\u7a0b\uff1f\u201d \u3000\u3000\u767d\u8863\u5973\u90ce\u9053\uff1a\u201c\u8fd9\u4e5f\u7528\u4e0d\u7740\u6211\u544a\u8bc9\u4f60\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u4e00\u6123\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u767d\u8863\u5973\u90ce\u9053\uff1a\u201c\u6211\u544a\u8bc9\u4f60\u662f\u90a3\u4e48\u591a\u8def\uff0c\u4e0d\u544a\u8bc9\u4f60\u4e5f\u662f\u90a3\u4e48\u591a\u8def\uff0c\u53cd\u6b63\u5fc5\u987b\u4f60\u8d70\u5230\u4e86\u624d\u80fd \u7b97\u6570\uff0c\u6211\u544a\u8bc9\u4e86\u4f60\u662f\u4e0d\u662f\u6beb\u65e0\u7528\u5904\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u8fde\u78b0\u4e24\u4e2a\u9489\u5b50\uff0c\u800c\u4e14\u78b0\u5f97\u7b80\u76f4\u557c\u7b11\u7686\u975e\uff0c\u4f46\u73b0\u5728\u662f\u6709\u6c42\u4e8e\u4eba\uff0c\u53c8\u53d1\u4e0d\u5f97\u813e\u6c14\uff0c\u53ea \u597d\u4e0d\u518d\u8a00\u8bed\uff0c\u8ddf\u7740\u8d70\u8def\u3002 \u3000\u3000\u8f6c\u8fc7\u51e0\u9053\u5c71\u5c97\uff0c\u8fdb\u5165\u4e00\u6761\u72ed\u8c37\u3002 \u3000\u3000\u72ed\u8c37\u5c3d\u5934\uff0c\u51fa\u73b0\u4e00\u4e2a\u6d1e\u53e3\uff0c\u6d1e\u53e3\u4e0a\u65b9\u7684\u4e00\u5757\u5927\u77f3\u677f\u4e0a\uff0c\u8d6b\u7136\u5199\u7740\u201c\u4e5d\u5929\u7384\u6d1e\u201d\u516b\u4e2a\u9ed1\u6f06 \u5927\u5b57\uff0c\u4f3c\u4e4e\u4e5f\u6709\u7740\u2014\u79cd\u83ab\u540d\u7684\u9634\u68ee\u4e4b\u611f\u3002 \u3000\u3000\u96be\u602a\u8fd9\u9b54\u5934\u88ab\u79f0\u4e3a\u9634\u624b\u4e00\u9b54\uff0c\u8fde\u4f4f\u5904\u7684\u540d\u79f0\u4e5f\u9634\u6c14\u68ee\u68ee\u3002 \u3000\u3000\u8db3\u8db3\u4e00\u76cf\u70ed\u8336\u5de5\u592b\uff0c\u90a3\u767d\u5973\u90ce\u624d\u7531\u6d1e\u5185\u8d70\u51fa\u3002 \u3000\u3000\u8fd9\u65f6\u5929\u8272\u5df2\u665a\uff0c\u6d1e\u5185\u53c8\u2014\u7247\u6f06\u9ed1\uff0c\u82e5\u975e\u9ea6\u5c0f\u660e\u89c6\u529b\u5f02\u4e8e\u5e38\u4eba\uff0c\u6839\u672c\u770b\u4e0d\u6e05\u8d70\u51fa\u6765\u7684\u662f\u767d \u8863\u5973\u90ce\u3002 \u3000\u3000\u767d\u8863\u5973\u90ce\u9053\uff1a\u201c\u968f\u6211\u6765\u5427\uff01\u201d \u3000\u3000\u201c\u4ee4\u5e08\u53ef\u662f\u8981\u63a5\u89c1\u6211\uff1f\u201d \u3000\u3000\u201c\u5e9f\u8bdd\uff0e\u4ed6\u8001\u4eba\u5bb6\u82e5\u4e0d\u63a5\u89c1\u4f60\uff0c\u6211\u4f1a\u8981\u4f60\u8fdb\u53bb\u4e48\uff1f\u201d \u3000\u3000\u8fdb\u5165\u6d1e\u5185\uff0e\u8d8a\u53d1\u9ed1\u5f97\u4f38\u624b\u4e0d\u89c1\u4e94\u6307\uff0c\u7531\u811a\u6b65\u58f0\u53d1\u51fa\u7684\u55e1\u55e1\u56de\u54cd\uff0e\u4e0d\u96be\u5224\u65ad\u51fa\u901a\u9053\u5fc5\u5b9a\u5f88 \u6df1\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u642d\u8baa\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u4e0d\u70b9\u706f\uff1f\u201d \u3000\u3000\u767d\u8863\u5973\u90ce\u51b7\u58f0\u9053\uff1a\u201c\u5982\u679c\u70b9\u706f\uff0c\u600e\u80fd\u79f0\u4e3a\u7384\u9634\u6d1e\u5e9c\uff1f\u201d \u3000\u3000\u201c\u8fd8\u6709\u591a\u8fdc\uff1f\u201d \u3000\u3000\u201c\u8be5\u5230\u7684\u65f6\u5019\u5c31\u5230\u4e86\u3002\u201d \u3000\u3000\u767d\u8863\u5973\u90ce\u63a5\u7740\u9053\uff1a\u201c\u6ce8\u610f\u5bf9\u6b63\u6211\u7684\u8eab\u540e\u8d70\uff0e\u514d\u5f97\u78b0\u4e0a\u7981\u5236\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u5fc3\u5934\u4e00\u9707\u9053\uff1a\u201c\u83ab\u975e\u8fd9\u901a\u9053\u4e0a\u8fd8\u6709\u4ec0\u4e48\u673a\u5173\u8bbe\u7f6e\uff1f\u201d \u3000\u3000\u767d\u8863\u5973\u90ce\u9053\uff1a\u201c\u8fd9\u662f\u673a\u5bc6\uff0c\u6211\u4e0d\u80fd\u968f\u4fbf\u8bb2\uff0c\u53ea\u8981\u4f60\u5bf9\u6b63\u6211\u8eab\u540e\u8d70\uff0c\u5c31\u4e0d\u4f1a\u5403\u4e8f\u3002\u201d \u3000\u3000\u679c\u7136\uff0c\u767d\u8863\u5973\u90ce\u884c\u8fdb\u95f4\u5e76\u975e\u76f4\u8d70\uff0c\u800c\u662f\u4e09\u6b65\u4e00\u5f2f\uff0c\u4e24\u6b65\u4e00\u8f6c\u7684\u4e0d\u89c4\u5219\u8d70\u6cd5\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u53ea\u5f97\u5728\u540e\u9762\u7167\u6837\u8ddf\u8fdb\u3002 \u3000\u3000\u8fd9\u6761\u901a\u9053\u5b9e\u5728\u591f\u957f\uff0c\u8db3\u8db3\u524d\u8fdb\u4e86\u4e8c\u4e09\u5341\u4e08\u8ddd\u79bb\uff0c\u4ecd\u672a\u5230\u8fbe\u5c3d\u5934\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u4e3a\u907f\u514d\u518d\u78b0\u9489\u5b50\uff0c\u60f3\u95ee\u5374\u53c8\u4e0d\u65b9\u4fbf\u95ee\u3002 \u3000\u3000\u53c8\u524d\u8fdb\u4e86\u51e0\u5341\u6b65\uff0c\u901a\u9053\u4e2d\u51fa\u73b0\u4e86\u5c94\u8def\uff0c\u4f46\u56e0\u5149\u7ebf\u592a\u6697\uff0c\u6839\u672c\u65e0\u6cd5\u89c2\u5bdf\u5c94\u8def\u5185\u662f\u4f55\u666f\u8c61\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u6697\u9053\uff1a\u201c\u8fd9\u5c71\u6d1e\u5982\u6b64\u6df1\u800c\u590d\u6742\uff0c\u5fc5\u5b9a\u4f4f\u7740\u4e0d\u5c11\u4eba\uff0c\u770b\u6765\u9634\u624b\u4e00\u9b54\u8001\u9b54\u5934\uff0c\u5728\u5415\u6881 \u5c71\u4e5f\u7b97\u5f97\u662f\u5360\u5c71\u4e3a\u738b\u4e86\uff01\u201d \u3000\u3000\u8fd9\u65f6\u901a\u9053\u65e9\u5df2\u8f6c\u6298\u65b9\u5411\uff0c\u800c\u4e14\u9891\u9891\u90fd\u662f\u5f2f\u9053\uff0c\u7b80\u76f4\u6709\u4e9b\u4e5d\u5f2f\u5341\u516b\u8f6c\uff0c\u8f6c\u5f97\u9ea6\u5c0f\u660e\u6655\u5934\u8f6c \u5411\u3002 \u3000\u3000\u53ea\u542c\u767d\u8863\u5973\u90ce\u505c\u4e0b\u811a\u6b65\u9053\uff1a\u201c\u5230\u5566\uff01\u4f60\u8fd8\u662f\u8981\u5728\u5916\u9762\u7a0d\u7b49\uff0c\u6211\u8fdb\u53bb\u901a\u62a5\u3002\u201d \u3000\u3000\u8fd9\u6b21\u767d\u8863\u5973\u90ce\u51fa\u6765\u7684\u5f88\u5feb\uff0e\u62db\u4e86\u62db\u624b\u9053\uff1a\u201c\u8fdb\u6765\u5427\uff01\u201d \u3000\u3000\u8fdb\u5165\u4e00\u9053\u77f3\u95e8\uff0e\u77f3\u95e8\u5185\u4ecd\u662f\u6697\u9ed1\u5982\u6f06\u3002 \u3000\u3000\u77f3\u95e8\u5185\u662f\u77f3\u5ba4\uff0e\u76f4\u7a7f\u8fc7\u4e09\u9053\u77f3\u95e8\uff0c\u4e5f\u7a7f\u8fc7\u4e09\u95f4\u77f3\u5ba4\uff0c\u767d\u8863\u5973\u90ce\u624d\u9053\uff1a\u201c\u8fd9\u91cc\u5c31\u662f\u5bb6\u5e08\u7684 \u5c45\u5ba4\uff0c\u4f60\u73b0\u4efb\u5c31\u53ef\u62dc\u89c1\u4ed6\u8001\u4eba\u5bb6\u5566\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u53ea\u542c\u5f97\u5760\u5165\u4e94\u91cc\u96fe\u4e2d\uff0e\u77f3\u5ba4\u5185\u4f9d\u7136\u4e00\u7247\u9634\u6697\uff0c\u6839\u672c\u8fde\u4e2a\u4eba\u5f71\u90fd\u770b\u4e0d\u5230\uff0c\u9634\u624b\u4e00\u9b54 \u4e60\u60ef\u4e86\u6697\u65e0\u5929\u65e5\u7684\u751f\u6d3b\uff0c\u603b\u4e0d\u8be5\u8fde\u63a5\u89c1\u5ba2\u4eba\u65f6\uff0e\u4e5f\u4e0d\u70b9\u706f\u3002 \u3000\u3000\u201c\u4ee4\u5e08\u7a76\u7adf\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u201c\u5bb6\u5e08\u5c31\u5750\u5728\u9760\u58c1\u5904\u7684\u77f3\u5e8a\u4e0a\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u51dd\u76ee\u5411\u524d\u770b\u53bb\uff0c\u4ecd\u770b\u4e0d\u5230\u534a\u4e2a\u4eba\u5f71\uff0e \u3000\u3000\u4ed6\u53ea\u597d\u95ed\u4e0a\u773c\u775b\uff0c\u7a0d\u8fc7\u4e86\u2014\u4f1a\u7741\u5f00\u518d\u770b\uff0c\u7ecf\u9a8c\u544a\u8bc9\u4ed6\uff0c\u8fd9\u6837\u6bd4\u8f83\u5bb9\u6613\u5728\u6697\u4e2d\u89c1\u7269\u3002 \u3000\u3000\u8fd9\u6b21\u679c\u7136\u53d1\u73b0\u524d\u65b9\u4e08\u4f59\u5916\uff0c\u6709\u5f20\u77f3\u5e8a\uff0c\u5e8a\u4e0a\u9690\u7136\u5750\u7740\u4e00\u4e2a\u4eba\u5f71\u3002 \u3000\u3000\u5728\u5e8a\u524d\u4e24\u65c1\uff0c\u8d6b\u7136\u53c8\u5404\u6709\u4e24\u4e2a\u7ad9\u7acb\u7684\u767d\u8272\u4eba\u5f71\uff0e \u3000\u3000\u767d\u8272\u4eba\u5f71\u770b\u5f97\u8f83\u4e3a\u6e05\u6670\uff0e\u5206\u660e\u662f\u56db\u540d\u767d\u8863\u767d\u88d9\u7684\u5999\u9f84\u5c11\u5973\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u8fd9\u624d\u604d\u7136\u800c\u609f\uff0c\u9634\u624b\u4e00\u9b54\u7684\u624b\u4e0b\u4eba\u4e3a\u4ec0\u4e48\u90fd\u559c\u6b22\u7a7f\u767d\u8863\uff0c\u539f\u6765\u662f\u4e3a\u4e86\u5728\u6697\u9ed1\u5982\u6f06 \u7684\u6d1e\u7a74\u4e2d\uff0e\u6613\u4e8e\u8fa8\u8ba4\u4e4b\u6545\u3002 \u3000\u3000\u8fde\u53db\u79bb\u9634\u624b\u4e00\u9b54\u7684\u82d7\u7d20\u5170\uff0c\u5230\u5982\u4eca\u4ecd\u4e60\u4ee5\u4e3a\u5e38\u7a7f\u7740\u767d\u8272\u8863\u88d9\u3002 \u3000\u3000\u4ed6\u7d20\u95fb\u9634\u624b\u4e00\u9b54\u6700\u597d\u5973\u8272\uff0c\u56e0\u4e4b\uff0c\u8fde\u670d\u4f8d\u4ed6\u7684\uff0c\u4e5f\u975e\u5e74\u8f7b\u8c8c\u7f8e\u7684\u5973\u5b50\u4e0d\u53ef\uff0e\u6536\u5f92\u66f4\u662f\u53ea \u6536\u5973\u5f92\uff0e\u4e0d\u6536\u7537\u5f1f\u5b50\u3002 \u3000\u3000\u5c24\u5176\u9634\u624b\u2014\u9b54\u5bf9\u5f85\u5973\u5f1f\u5b50\uff0c\u4e00\u5411\u4ea6\u5f92\u4ea6\u59be\uff0c\u5927\u9006\u5e38\u4f26\u3002 \u3000\u3000\u6b64\u523b\u770b\u6765\uff0c\u679c\u7136\u6709\u4e9b\u8bdd\u4e0d\u865a\u4f20\u3002 \u3000\u3000\u5f53\u4e0b\u9ea6\u5c0f\u660e\u606d\u606d\u656c\u656c\u7684\u62f1\u624b\u4e00\u793c\u9053\uff1a\u201c\u665a\u8f88\u9ea6\u5c0f\u660e\uff0c\u62dc\u89c1\u9634\u8001\u524d\u8f88\u3002\u201d \u3000\u3000\u4ed6\u8bdd\u51fa\u53e3\u540e\uff0c\u624d\u89c9\u5f97\u79f0\u547c\u6709\u4e9b\u4e0d\u59a5\u3002 \u3000\u3000\u679c\u7136\uff0c\u9634\u624b\u4e00\u9b54\u7acb\u5373\u51b7\u54fc\u2014\u58f0\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u8001\u592b\u5e76\u4e0d\u59d3\u9634\uff0c\u4f60\u53e3\u79f0\u9634\u8001\u524d\u8f88\uff0c\u7b80\u76f4\u662f \u5c82\u6709\u6b64\u7406\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u8001\u524d\u8f88\u53ef\u5426\u5c06\u5c0a\u59d3\u5927\u540d\u8d50\u544a\uff1f\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u6012\u9053\uff1a\u201c\u653e\u8086\uff0e\u666e\u5929\u4e4b\u4e0b\uff0e\u8001\u592b\u4ece\u4e0d\u66fe\u5bf9\u4efb\u4f55\u4eba\u544a\u77e5\u540d\u8bb3\uff0e\u4f60\u4e0d\u8fc7\u4e00\u4e2a\u540e\u751f\u665a \u8f88\uff0c\u5c45\u7136\u63d0\u51fa\u8fd9\u79cd\u8981\u6c42\uff0e\u7b80\u81ea\u662f\u79c3\u5b50\u6253\u4f1e\u4e00\uff0d\uff0d\u65e0\u6cd5\u65e0\u5929\uff01\u201d \u3000\u3000\u201c\u65e2\u7136\u5982\u6b64\uff0c\u665a\u8f88\u4fbf\u5c31\u4e0d\u4fbf\u518d\u95ee\u4e86\uff01\u201d \u3000\u3000\u201c\u4f60\u53ef\u662f\u9146\u79cb\u7684\u95e8\u4e0b\uff1f\u201d \u3000\u3000\u201c\u665a\u8f88\u6b63\u662f\u5949\u5bb6\u5e08\u4e4b\u547d\uff0e\u524d\u6765\u62dc\u89c1\u8001\u524d\u8f88\u7684\u3002\u201d \u3000\u3000\u201c\u5e9f\u8bdd\uff01\u5982\u679c\u4f60\u4e0d\u662f\u5949\u9146\u79cb\u4e4b\u547d\uff0c\u591f\u4ec0\u4e48\u8d44\u683c\u6765\u89c1\u8001\u592b\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u4e07\u6ca1\u6599\u5230\uff0c\u5bf9\u65b9\u548c\u81ea\u5df1\u89c1\u9762\u4e4b\u540e\u7684\u8fd9\u51e0\u53e5\u8bdd\uff0e\u5c45\u7136\u5982\u6b64\u50b2\u6162\u65e0\u793c\uff0c\u82e5\u4f9d\u4ed6\u4ee5\u5f80\u7684 \u6027\u5b50\uff0c\u5373\u4f7f\u62da\u7740\u4e00\u6b7b\uff0e\u4e5f\u8981\u9876\u649e\u4e0a\u51e0\u53e5\u3002 \u3000\u3000\u4f46\u4ed6\u73b0\u5728\u6709\u6c42\u4e8e\u4eba\uff0c\u4e5f\u53ea\u80fd\u59d4\u66f2\u6c42\u5168\u4e86\uff01 \u3000\u3000\u53ea\u542c\u9634\u624b\u4e00\u9b54\u5f00\u95e8\u89c1\u5c71\u7684\u95ee\u9053\uff1a\u201c\u9146\u79cb\u6253\u53d1\u4f60\u6765\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u665a\u8f88\u5949\u5bb6\u5e08\u4e4b\u547d\uff0e\u6765\u5411\u8001\u524d\u8f88\u6c42\u53d6\u89e3\u836f\u3002\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u54fc\u4e86\u58f0\u9053\uff1a\u201c\u8001\u592b\u4e0e\u4ee4\u5e08\u867d\u66fe\u76f8\u8bc6\uff0c\u5374\u8c08\u4e0d\u4e0a\u4ec0\u4e48\u4ea4\u60c5\uff0c\u4ed6\u8981\u5411\u8001\u592b\u8ba8\u7684\u4ec0\u4e48 \u89e3\u836f\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u2018\u5411\u5fc3\u9732\u2019\u7684\u89e3\u836f\u3002\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u51b7\u51b7\u7b11\u9053\uff1a\u201c\u9146\u79cb\u4e5f\u592a\u4e0d\u77e5\u5ea6\u5fb7\u91cf\u529b\uff0c\u60f3\u90a3\u2018\u5411\u5fc3\u9732\u2019\u89e3\u836f\uff0c\u4e43\u662f\u8001\u592b\u7a77\u5c3d\u5927 \u534a\u751f\u7cbe\u529b\u624d\u7814\u5236\u800c\u6210\uff0c\u4ed6\u51ed\u4ec0\u4e48\u5411\u8001\u592b\u53d6\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u987f\u65f6\u50cf\u88ab\u6d47\u4e86\u4e00\u5934\u51b7\u6c34\uff0c\u81ea\u5df1\u7531\u5317\u5cb3\u8fe2\u8fe2\u6570\u767e\u91cc\uff0c\u957f\u9014\u8dcb\u6d89\uff0e\u597d\u4e0d\u5bb9\u6613\u624d\u89c1\u5230\u9634 \u624b\u2014\u9b54\u3002 \u3000\u3000\u5982\u82e5\u4ed6\u7684\u5e0c\u671b\u6210\u7a7a\uff0c\u8ba9\u4ed6\u6709\u4f55\u9762\u76ee\u56de\u53bb\uff0c\u518d\u89c1\u949f\u4e00\u8c6a\u7b49\u4eba\u5462\uff1f \u3000\u3000\u9ea6\u5c0f\u660e\u5446\u4e86\u534a\u54cd\uff0c\u624d\u53c8\u62b1\u62f3\u4e00\u793c\u9053\uff1a\u201c\u5bb6\u5e08\u7684\u786e\u6025\u5f85\u2018\u5411\u5fc3\u9732\u2019\u89e3\u836f\u4e00\u7528\uff0c\u8fd8\u671b\u8001\u524d\u8f88 \u4e0d\u541d\u6168\u8d50\u3002\u201d \u3000\u3000\u8c01\u77e5\u4ed6\u8bdd\u672a\u8bf4\u5b8c\uff0c\u7a81\u611f\u4e00\u7f15\u521a\u731b\u4e2d\u800c\u53c8\u5e26\u7740\u9634\u67d4\u7684\u6697\u52b2\uff0c\u81ea\u5411\u524d\u80f8\u2018\u7487\u7391\u7a74\u2019\u88ad\u6765\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u5728\u6beb\u65e0\u6212\u5907\u4e4b\u4e0b\uff0e\u95ea\u8eab\u5df2\u6765\u4e0d\u53ca\uff0e\u88ab\u9634\u624b\u4e00\u9b54\u2018\u9634\u98ce\u6307\u2019\u70b9\u4e2a\u6b63\u7740\u3002 \u3000\u3000\u8fd9\u4e00\u6307\u70b9\u5f97\u4ed6\u987f\u65f6\u5168\u8eab\u52b2\u529b\u5168\u5931\uff0c\u800c\u4e14\u4f53\u5185\u5bd2\u5f7b\u5fc3\u813e\uff0c\u597d\u5728\u4eba\u5c1a\u4f9d\u7136\u6e05\u9192\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u5927\u53eb\u9053\uff1a\u201c\u8001\u524d\u8f88\uff0c\u8981\u7ed9\u5c31\u7ed9\uff0c\u4e0d\u7ed9\u5c31\u7f62\uff0c\u51fa\u624b\u4f24\u4eba\uff0c\u8fd9\u7b97\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u559d\u9053\uff1a\u201c\u628a\u8fd9\u5c0f\u5b50\u62bc\u5230\u2018\u5bd2\u51b0\u5ba4\u2019\uff01\u201d \u3000\u3000\u5148\u524d\u5e26\u8def\u7684\u90a3\u767d\u8863\u5973\u90ce\uff0c\u3000\u7acb\u5373\u4e0a\u524d\u67b6\u4f4f\u9ea6\u5c0f\u660e\u4e00\u6761\u80f3\u818a\u9053\uff1a\u201c\u8ddf\u6211\u8d70\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u5df2\u7ecf\u65e0\u529b\u53cd\u6297\uff0c\u53ea\u597d\u4efb\u4eba\u6446\u5e03\u3002 \u3000\u3000\u79bb\u5f00\u9634\u624b\u4e00\u9b54\u7684\u5c45\u5ba4\uff0c\u53c8\u56de\u5230\u6765\u65f6\u7684\u901a\u9053\uff0c\u8f6c\u5f2f\u62d0\u89d2\uff0c\u5f88\u5feb\u4fbf\u6765\u5230\u4e00\u5904\u77f3\u95e8\u524d\u3002 \u3000\u3000\u767d\u8863\u5973\u90ce\u9053\uff1a\u201c\u8fd9\u91cc\u9762\u5c31\u662f\u2018\u5bd2\u51b0\u5ba4\u2019\uff0c\u968f\u6211\u8fdb\u53bb\uff01\u51c9\u5feb\u51c9\u5feb\u5427\uff01\u201d \u3000\u3000\u539f\u6765\uff0c\u8fdb\u5165\u90a3\u6d1e\u95e8\u4e4b\u540e\uff0c\u5374\u662f\u4e00\u5c42\u4e00\u5c42\u5411\u4e0b\u7684\u77f3\u7ea7\uff0c\u8d77\u521d\u8fd8\u4e0d\u89c9\u5f97\u600e\u6837\uff0c\u4f46\u8d70\u4e0b\u5341\u4f59\u7ea7 \u540e\uff0c\u4fbf\u5df2\u5f00\u59cb\u5bd2\u6c14\u88ad\u4eba\u3002 \u3000\u3000\u7136\u540e\u6bcf\u4e0b\u4e00\u7ea7\uff0c\u4fbf\u89c9\u5f97\u5bd2\u6c14\u52a0\u91cd\u4e86\u4e9b\uff0e\u5f53\u8d70\u5230\u8fd1\u767e\u7ea7\u65f6\uff0c\u7b80\u76f4\u5df2\u50cf\u8fdb\u5165\u4e86\u51b0\u7a96\u3002 \u3000\u3000\u53ca\u81f3\u5230\u8fbe\u6700\u5e95\u4e00\u5c42\uff0c\u5df2\u662f\u782d\u9aa8\u523a\u808c\uff0c\u5168\u8eab\u53d1\u6296\uff0c\u624b\u811a\u50f5\u786c\uff0c\u60f3\u50cf\u4e2d\u5373\u4f7f\u662f\u51b0\u5929\u96ea\u5730\u7684\u5317 \u6781\uff0c\u4e5f\u4e0d\u53ef\u80fd\u5bd2\u51b7\u5230\u8fd9\u79cd\u7a0b\u5ea6\u3002 \u3000\u3000\u5947\u602a\u7684\u662f\u767d\u8863\u5973\u90ce\u5374\u884c\u52a8\u5982\u5e38\uff0c\u4f3c\u662f\u4e1d\u6beb\u4e0d\u8981\u5f71\u54cd\u3002 \u3000\u3000\u5e95\u5c42\u662f\u4e00\u95f4\u4e24\u4e08\u65b9\u5706\u7684\u77f3\u5ba4\u3002 \u3000\u3000\u767d\u8863\u5973\u90ce\u7ec8\u4e8e\u70b9\u8d77\u77f3\u5ba4\u4e00\u89d2\u7684\u4e00\u76cf\u6cb9\u706f\uff0c\u8fd9\u662f\u9ea6\u5c0f\u660e\u8fdb\u5165\u201c\u4e5d\u5929\u7384\u6d1e\u5e9c\u201d\u6240\u89c1\u7684\u552f\u4e00\u5149 \u4eae\u3002 \u3000\u3000\u77f3\u5ba4\u5185\u4ec5\u6709\u4e00\u5f20\u77f3\u5e8a\uff0e\u5e8a\u4e0a\u5149\u79c3\u79c3\u7684\u5e76\u65e0\u88ab\u8925\uff0c\u5730\u4e0a\u4e5f\u5e76\u65e0\u684c\u6905\u6446\u8bbe\uff0e \u3000\u3000\u6b64\u523b\uff0e\u9ea6\u5c0f\u660e\u5df2\u51bb\u5f97\u8138\u8272\u53d1\u9752\uff0c\u51e0\u4e4e\u8fde\u547c\u51fa\u7684\u96fe\u6c14\uff0c\u90fd\u8981\u5728\u7a7a\u6c14\u4e2d\u6253\u7ed3\u3002 \u3000\u3000\u767d\u8863\u5973\u90ce\u9053\uff1a\u201c\u4f60\u5c31\u5728\u8fd9\u513f\u5f85\u7740\u5427\uff0c\u8fc7\u4e24\u4e2a\u65f6\u8fb0\u540e\uff0c\u6211\u518d\u6765\u770b\u770b\u4f60\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u51bb\u5f97\u5634\u91cc\u5636\u5636\u5566\u5566\u5730\u9053\uff1a\u201c\u4f60\u4e0d\u5fc5\u6765\u5566\uff01\u201d \u3000\u3000\u767d\u8863\u5973\u90ce\u832b\u7136\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4e24\u4e2a\u65f6\u8fb0\u4ee5\u540e\uff0c\u6211\u5fc5\u5b9a\u51bb\u6b7b\uff0e\u6b7b\u4eba\u8fd8\u6709\u4ec0\u4e48\u597d\u770b\u7684\uff1f\u201d \u3000\u3000\u767d\u8863\u5973\u90ce\u7b11\u9053\uff1a\u201c\u6211\u60f3\u4f60\u8fd8\u4e0d\u81f3\u4e8e\u6b7b\u5f97\u90a3\u4e48\u5feb\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u54ac\u4e86\u54ac\u7259\u9053\uff1a\u201c\u6211\u5df2\u7ecf\u51bb\u6210\u8fd9\u6837\u5b50\uff0c\u59d1\u5a18\u8fd8\u5728\u7b11\uff0c\u770b\u6765\u59d1\u5a18\u7684\u5fc3\u80a0\u548c\u4ee4\u5e08\u2014\u6837\u72e0 \u6bd2\uff01\u201d \u3000\u3000\u767d\u8863\u5973\u90ce\u9053\uff1a\u201c\u8fd9\u79cd\u4e8b\u60c5\u6211\u770b\u5f97\u591a\u5566\uff0c\u600e\u53ef\u80fd\u5355\u72ec\u53ef\u601c\u4f60\uff01\u201d \u3000\u3000\u8bf4\u5b8c\u8bdd\uff0e\u53c8\u62bf\u5634\u4e00\u7b11\u9053\uff1a\u201c\u6211\u8be5\u8d70\u5566\uff01\u4f60\u597d\u597d\u7684\u4f11\u606f\uff0c\u522b\u80e1\u601d\u4e71\u60f3\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u5fd9\u9053\uff1a\u201c\u6162\u8d70\uff0c\u6211\u8fd8\u6709\u8bdd\u8981\u95ee\uff1f\u201d \u3000\u3000\u201c\u4f60\u60f3\u95ee\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u4ee4\u5e08\u4e0d\u7ed9\u89e3\u836f\u5c31\u7b97\u4e86\uff0e\u4e3a\u4ec0\u4e48\u8fd9\u6837\u5904\u7f6e\u6211\uff1f\u201d \u3000\u3000\u201c\u8fd9\u4e8b\u6211\u4e0d\u6e05\u695a\uff0e\u4f60\u4e3a\u4ec0\u4e48\u5f53\u65f6\u4e0d\u95ee\u4ed6\uff01\u201d \u3000\u3000\u201d\u90a3\u8001\u2026\u2026\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u672c\u60f3\u8bf4\u201c\u90a3\u8001\u738b\u516b\u86cb\u201d\uff0e\u597d\u5728\u53ca\u65f6\u6536\u4f4f\uff0e\u6ca1\u518d\u8bf4\u4e0b\u53bb\u3002 \u3000\u3000\u767d\u8863\u5973\u90ce\u9053\uff1a\u201c\u4f60\u8981\u8bf4\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u6211\u662f\u8bf4\u4ee4\u5e08\u8001\u524d\u8f88\u592a\u4e0d\u8bb2\u7406\u4e86\uff0c\u4ed6\u6839\u672c\u4e0d\u7ed9\u6211\u95ee\u660e\u539f\u56e0\u7684\u673a\u4f1a\u3002\u201d \u3000\u3000\u767d\u8863\u5973\u90ce\u4f3c\u662f\u4e0d\u613f\u4e45\u5f85\uff0c\u51b7\u54fc\u4e00\u58f0\uff0c\u8f6c\u8eab\u800c\u53bb\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0e\u3000\u4e00\u9635\u8f67\u8f67\u4e4b\u58f0\u8fc7\u540e\uff0c\u7adf\u7136\u7531\u77f3\u58c1\u95f4\u5192\u51fa\u4e00\u9762\u77f3\u95e8\uff0c\u751f\u751f\u628a\u51fa\u53e3\u7684\u2014\u9762\u5c01\u4f4f \u4e86\u3002 \u3000\u3000\u5982\u6b64\u4e00\u6765\uff0c\u9ea6\u5c0f\u660e\u7b49\u4e8e\u662f\u88ab\u5c01\u5728\u4e00\u4e2a\u5bc6\u4e0d\u901a\u98ce\u7684\u77f3\u5ba4\u4e2d\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u672c\u60f3\u5728\u767d\u8863\u5973\u90ce\u8d70\u540e\uff0e\u5077\u5077\u767b\u4e0a\u77f3\u7ea7\u6700\u4e0a\u4e00\u5c42\u907f\u5bd2\uff0c\u73b0\u5728\u8fde\u8fd9\u5e0c\u671b\u4e5f\u6210\u6ce1\u5f71\u3002 \u3000\u3000\u8eab\u4e0a\u8d8a\u6765\u8d8a\u51b7\uff0c\u51b7\u5f97\u4ed6\u53ea\u597d\u5728\u539f\u5730\u8dd1\u6b65\u3002 \u3000\u3000\u8fd9\u79cd\u65b9\u6cd5\u679c\u7136\u6709\u6548\uff0e\u4e0d\u4e45\uff0c\u5bd2\u610f\u5df2\u51cf\u8f7b\u4e86\u8bb8\u591a\u3002 \u3000\u3000\u4f46\u4ed6\u603b\u4e0d\u80fd\u4e00\u76f4\u8dd1\u4e0b\u53bb\uff0e\u66f4\u4f55\u51b5\u2018\u7487\u7391\u7a74\u2019\u88ab\u70b9\u4e4b\u540e\uff0c\u5168\u8eab\u65e0\u529b\uff0e\u8dd1\u4e45\u4e86\u52bf\u5fc5\u65e0\u6cd5\u652f \u6301\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u4e00\u5411\u9a84\u6a2a\u6210\u6027\uff0e\u5f88\u5c11\u6015\u8fc7\u8c01\uff0e\u4f46\u6b64\u65f6\u6b64\u5730\uff0c\u4ed6\u5374\u4e0d\u7531\u5f97\u8d8a\u60f3\u8d8a\u6015\u3002 \u3000\u3000\u73b0\u5728\u4ed6\u7b49\u4e8e\u8eab\u5728\u5929\u7f57\u5730\u7f51\u4e4b\u4e2d\uff0c\u8981\u60f3\u51fa\u53bb\uff0e\u6839\u672c\u662f\u4e0d\u53ef\u80fd\u7684\u4e8b\uff0c\u5c31\u8fd9\u6837\u5c0f\u5c0f\u5e74\u7eaa\u6b7b\u53bb\uff0c \u4efb\u8c01\u4e5f\u4e0d\u7518\u5fc3\u60c5\u613f\u3002 \u3000\u3000\u539f\u5730\u8dd1\u76f4\u8dd1\u4e86\u987f\u996d\u5de5\u592b\uff0e\u5b9e\u5728\u8dd1\u4e0d\u52a8\u4e86\uff0c\u53ea\u597d\u505c\u4e0b\u6765\u4f11\u606f\u3002 \u3000\u3000\u5c82\u77e5\u4e00\u505c\u4e0b\u6765\uff0e\u5c31\u53c8\u51bb\u5f97\u5168\u8eab\u53d1\u6296\u3002 \u3000\u3000\u6b63\u8981\u82e6\u7740\u518d\u8dd1\uff0c\u5ffd\u542c\u8f67\u8f67\u4e4b\u58f0\u53c8\u8d77\u3002 \u3000\u3000\u63a5\u7740\uff0c\u77f3\u95e8\u6253\u5f00\uff0e\u767d\u8863\u5973\u90ce\u8d70\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u8fd9\u5728\u9ea6\u5c0f\u660e\u6765\u8bf4\uff0c\u4f3c\u4e4e\u53c8\u51fa\u73b0\u4e86\u4e00\u7ebf\u4e2d\u673a\u3002 \u3000\u3000\u201c\u59d1\u5a18\uff0e\u597d\u50cf\u8fd8\u4e0d\u5230\u4e24\u4e2a\u65f6\u8fb0\u5427\uff1f\u201d \u3000\u3000\u767d\u8863\u5973\u90ce\u677f\u7740\u9762\u5b54\uff0c\u549b\u4e86\u58f0\u9053\uff1a\u201c\u90a3\u6709\u90a3\u4e48\u5feb\u7684\uff0c\u8fde\u534a\u4e2a\u65f6\u8fb0\u4e5f\u4e0d\u5230\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u59d1\u5a18\u63d0\u524d\u6765\u4e86\uff0c\u5fc5\u5b9a\u6709\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u767d\u8863\u5973\u90ce\u9053\uff1a\u201c\u6211\u5949\u547d\u6765\u544a\u8bc9\u4f60\u4e00\u4ef6\u4e8b\uff01\u201d \u3000\u3000\u201c\u4ec0\u4e48\u4e8b\uff1f\u59d1\u5a18\u5feb\u8bf7\u8bf4\u660e\uff01\u201d \u3000\u3000\u201c\u4f60\u6b7b\u5b9a\u4e86\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u554a\u4e86\u58f0\u9053\uff1a\u201c\u4f60\u542c\u8c01\u8bf4\u7684\uff1f\u201d \u3000\u3000\u767d\u8863\u5973\u90ce\u9053\uff1a\u201c\u5bb6\u5e08\u8bf4\u7684\uff0c\u96be\u9053\u8fd8\u4e0d\u7b97\u6570\uff1f\u201d \u3000\u3000\u201c\u4ed6\u4e3a\u4ec0\u4e48\u8981\u8fd9\u6837\u5904\u7f6e\u6211\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u4f60\u9a97\u4e86\u4ed6\u8001\u4eba\u5bb6\u3002\u201d \u3000\u3000\u201c\u6211\u90a3\u91cc\u9a97\u8fc7\u4ed6\uff1f\u201d \u3000\u3000\u201c\u4f60\u867d\u7136\u66fe\u5728\u9146\u79cb\u95e8\u4e0b\u8fc7\uff0c\u4f46\u540e\u6765\u5374\u6539\u6295\u4e86\u80e1\u67cf\u9f84\u548c\u8c37\u5bd2\u9999\uff0c\u76ee\u524d\u662f\u5317\u5cb3\u8ff7\u8e2a\u8c37\u7684\u4eba\u3002 \u5bb6\u5e08\u5bf9\u8ff7\u8e2a\u8c37\u65b9\u9762\uff0c\u4e00\u5411\u6700\u75db\u6068\u4e0d\u8fc7\uff0c\u5c06\u6765\u6709\u673a\u4f1a\uff0c\u3000\u4e00\u5b9a\u8981\u94f2\u5e73\u4f60\u4eec\u90a3\u7eff\u6797\u603b\u5be8\uff0c\u4f60\u662f\u8ff7 \u8e2a\u8c37\u7684\u4eba\u3001\u53c8\u6b3a\u9a97\u4ed6\uff0c\u4ed6\u5f53\u7136\u6068\u4f60\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u5401\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u539f\u6765\u662f\u8fd9\u4e48\u56de\u4e8b\uff0c\u4ed6\u6253\u7b97\u628a\u6211\u600e\u6837\u5904\u7f6e\uff1f\u201d \u3000\u3000\u767d\u8863\u5973\u90ce\u9053\uff1a\u201c\u60f3\u8981\u4f60\u6b7b\uff0c\u90a3\u8fd8\u4e0d\u7b80\u5355\uff0e\u53ea\u8981\u628a\u4f60\u56da\u5728\u2018\u5bd2\u51b0\u5ba4\u2019\u91cc\u4e0d\u7ba1\uff0c\u4f11\u5f88\u5feb\u5c31\u4f1a \u51bb\u6b7b\uff0e\u4e0d\u8fc7\u2026\u2026\u201d \u3000\u3000\u201c\u5982\u679c\u628a\u4f60\u51bb\u6b7b\uff0e\u90a3\u672a\u514d\u592a\u4fbf\u5b9c\u4f60\u5566\uff01\u4ed6\u660e\u5929\u518d\u4f20\u4f60\u95ee\u8bdd\uff0c\u7136\u540e\u518d\u4eb2\u624b\u6740\u4f60\u3002\u201d \u3000\u3000\u201c\u8981\u6740\u5c31\u6740\u597d\u5566\uff0e\u4f55\u5fc5\u8fd8\u518d\u95ee\u8bdd\uff01\u201d \u3000\u3000\u201c\u4f60\u5982\u679c\u4e0d\u9ad8\u5174\u4ed6\u95ee\uff0c\u4e0d\u59a8\u73b0\u5728\u7531\u6211\u4ee3\u95ee\uff0e\u6211\u518d\u56de\u7980\u4ed6\u8001\u4eba\u5bb6\uff0e\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u7f04\u9ed8\u4e86\u534a\u54cd\u9053\uff1a\u201c\u597d\u5427\uff0c\u4f60\u8981\u95ee\u4ec0\u4e48\u53ea\u7ba1\u95ee\uff01\u201d \u3000\u3000\u767d\u8863\u5973\u90ce\u9053\uff1a\u201c\u8ff7\u8e2a\u8c37\u6709\u4e2a\u53eb\u82d7\u7d20\u5170\u7684\u5973\u4eba\uff0c\u4f60\u4e00\u5b9a\u77e5\u9053\u5427\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u5fc3\u4e2d\u4e00\u52a8\uff0e\u6697\u9053\uff1a\u201c\u82d7\u7d20\u5170\u5f53\u521d\u4e5f\u662f\u9634\u624b\u4e00\u9b54\u7684\u5f1f\u5b50\uff0c\u6211\u82e5\u8bf4\u8ff7\u8e2a\u8c37\u6ca1\u6709\u6b64\u4eba\uff0c \u4e00\u5b9a\u7792\u4ed6\u4e0d\u8fc7\u2026\u2026\u201d \u3000\u3000\u53ea\u542c\u767d\u8863\u5973\u90ce\u50ac\u4fc3\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u4e0d\u80af\u56de\u7b54\u6211\u7684\u8bdd\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u7acb\u523b\u7b54\u9053\uff1a\u201c\u4e0d\u9519\uff0e\u8ff7\u8e2a\u8c37\u6709\u4e2a\u53eb\u82d7\u7d20\u5170\u7684\u3002\u201d \u3000\u3000\u767d\u8863\u5973\u90ce\u4f3c\u662f\u9887\u4e3a\u5173\u5207\u7684\u9053\uff1a\u201c\u5979\u73b0\u5728\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u5f88\u597d\u3002\u201d \u3000\u3000\u201c\u4f60\u53ef\u77e5\u9053\u5979\u7684\u51fa\u8eab\u6765\u5386\uff1f\u201d \u3000\u3000\u201c\u542c\u8bf4\u5979\u4ece\u524d\u4e5f\u662f\u4ee4\u5e08\u7684\u95e8\u4e0b\uff0c\u7b97\u6765\u8be5\u662f\u4f60\u7684\u5e08\u59d0\u3002\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u5979\u4ece\u524d\u662f\u6211\u7684\u5e08\u59d0\uff0c\u53ef\u5374\u53db\u79bb\u5e08\u95e8\uff0c\u8fd9\u6bb5\u7ecf\u8fc7\uff0e\u4e0d\u77e5\u4f60\u6e05\u4e0d\u6e05\u695a\uff1f\u201d \u3000\u3000\u201c\u8fd9\u79cd\u4e8b\u6211\u4f55\u5fc5\u6e05\u695a\uff01\u201d \u3000\u3000\u201c\u90a3\u6211\u5c31\u544a\u8bc9\u4f60\uff0e\u4e09\u5e74\u524d\uff0c\u5bb6\u5e08\u8bbe\u8ba1\u628a\u5f53\u65f6\u7eff\u6797\u76df\u4e3b\u80e1\u67cf\u9f84\u8bf1\u5230\u5357\u660c\u57ce\u5357\u836f\u738b\u5e99\u2026\u2026\u201d \u3000\u3000\u201c\u4ee4\u5e08\u4e3a\u4ec0\u4e48\u8981\u628a\u80e1\u524d\u76df\u4e3b\u8bf1\u5230\u90a3\u91cc\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u662f\u51c6\u5907\u628a\u4ed6\u9664\u53bb\u3002\u201d \u3000\u3000\u201c\u540e\u6765\uff1f\u201d \u3000\u3000\u201c\u80e1\u67cf\u9f84\u773c\u770b\u5c31\u8981\u4e2d\u8ba1\uff0c\u8c01\u60f3\u82d7\u7d20\u5170\u5374\u5bf9\u4ed6\u6697\u4e2d\u76f8\u52a9\uff0c\u4ee5\u81f3\u5bb6\u5e08\u529f\u4e8f\u4e00\u8489\uff0c\u6ca1\u80fd\u8fbe\u5230\u9664 \u53bb\u80e1\u67cf\u9f84\u7684\u5fc3\u613f\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u80e1\u524d\u76df\u4e3b\u4ece\u5357\u660c\u836f\u738b\u5e99\u56de\u6765\uff0e\u4e0d\u5230\u534a\u5e74\uff0c\u4fbf\u56e0\u88ab\u4eba\u8bef\u6740\u800c\u53bb\u4e16\uff0e\u4ee4\u5e08\u7684\u76ee\u7684\u4e5f\u7b97 \u8fbe\u5230\u5566\uff01\u201d \u3000\u3000\u201c\u53ef\u662f\u5bb6\u5e08\u76ee\u524d\u6700\u6068\u7684\u662f\u82d7\u7d20\u5170\uff0c\u5f53\u7136\u4e5f\u6068\u4f60\u4eec\u8ff7\u8e2a\u8c37\u7684\u4eba\uff0c\u8fd9\u5c31\u662f\u4ed6\u8001\u4eba\u5bb6\u8981\u628a\u4f60\u5904 \u6b7b\u7684\u539f\u56e0\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u6f20\u7136\u4e00\u7b11\u9053\uff1a\u201c\u8fd9\u6837\u8bf4\u8be5\u662f\u82d7\u7d20\u5170\u5bb3\u4e86\u6211\uff1f\u6211\u628a\u82d7\u7d20\u5170\u6349\u6765\u4ea4\u7ed9\u4ee4\u5e08\uff0e\u4ed6\u4f1a\u4e0d\u4f1a \u7ed9\u6211\u2018\u5411\u5fc3\u9732\u2019\u89e3\u836f\uff1f\u201d \u3000\u3000\u767d\u8863\u5973\u90ce\u4f3c\u662f\u795e\u8272\u2014\u53d8\uff0c\u7728\u4e86\u7728\u773c\u7738\u9053\uff1a\u201c\u4f60\u81ea\u4fe1\u6709\u80fd\u529b\u628a\u5979\u6349\u6765\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u82d7\u7d20\u5170\u867d\u7136\u6b66\u529f\u4e0d\u9519\uff0c\u4f46\u5979\u5374\u7edd\u4e0d\u662f\u6211\u7684\u5bf9\u624b\uff01\u201d \u3000\u3000\u767d\u8863\u5973\u90ce\u9053\uff1a\u201c\u8fd9\u6837\u8bf4\u4f60\u7684\u6b66\u529f\u2014\u5b9a\u5f88\u9ad8\u4e86\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u6211\u73b0\u5728\u5df2\u7ecf\u88ab\u4ee4\u5e05\u5236\u4f4f\uff0c\u4e0d\u60f3\u518d\u8001\u738b\u5356\u74dc\uff0c\u4f60\u8fd8\u8981\u95ee\u4ec0\u4e48\uff1f\u5c31\u5feb\u4e9b\u95ee\uff0c\u6211 \u8fd8\u8981\u8dd1\u6b65\u3002\u201d \u3000\u3000\u767d\u8863\u5973\u90ce\u6123\u4e86\u6123\u9053\uff1a\u201c\u8dd1\u6b65\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u81ea\u7136\u662f\u8dd1\u6b65\u9a71\u5bd2\uff0c\u65b9\u624d\u6211\u5df2\u8dd1\u4e86\u5f88\u4e45\uff0c\u4e0d\u7136\u65e9\u5c31\u50f5\u4e86\u3002\u201d \u3000\u3000\u767d\u8863\u5973\u90ce\u5fae\u5fae\u4e00\u7b11\uff0c\u7f13\u7f13\u4ece\u6000\u91cc\u638f\u51fa\u4e00\u4e2a\u7eb8\u5305\u9053\uff1a\u201c\u7eb8\u5305\u91cc\u662f\u7c92\u2018\u6696\u5fc3\u4e39\u2019\u4f60\u670d\u4e0b\u540e\u5c31 \u4e0d\u51b7\u4e86\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u4f38\u624b\u63a5\u8fc7\u9053\uff1a\u201c\u8c22\u8c22\u59d1\u5a18\uff0c\u60f3\u4e0d\u5230\u4f60\u4f1a\u5bf9\u6211\u8fd9\u6837\u597d\uff01\u201d \u3000\u3000\u8c01\u77e5\u767d\u8863\u5973\u90ce\u5374\u54fc\u4e86\u58f0\u9053\uff1a\u201c\u6211\u4e3a\u4ec0\u4e48\u8981\u5bf9\u4f60\u597d\uff0e\u8fd9\u662f\u5bb6\u5e08\u8981\u6211\u9001\u7ed9\u4f60\u7684\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u5927\u5927\u4e00\u6014\u9053\uff1a\u201c\u4ed6\u4e0d\u662f\u8981\u6740\u6211\u4e48\uff1f\u201d \u3000\u3000\u767d\u8863\u5973\u90ce\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u201c\u90a3\u4ed6\u4e3a\u4ec0\u4e48\u53c8\u8981\u6551\u4eba\uff1f\u201d \u3000\u3000\u201c\u4ed6\u51b3\u5b9a\u660e\u5929\u4eb2\u624b\u6740\u4f60\uff0c\u56e0\u4e3a\u62c5\u5fc3\u4f60\u4f1a\u51bb\u6b7b\uff0c\u6240\u4ee5\u624d\u8981\u6682\u65f6\u4fdd\u4f4f\u4f60\u7684\u547d\uff0e\u5982\u679c\u4f60\u4eca\u591c\u51bb \u6b7b\uff0c\u4ed6\u8fd8\u6740\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u82e6\u7b11\u4e86\u4e00\u4e0b\uff0e\u9053\uff1a\u201c\u4ee4\u5e08\u679c\u7136\u60f3\u7684\u7528\u5230\uff0c\u8fd8\u662f\u8bf7\u59d1\u5a18\u4ee3\u6211\u8c22\u8c22\u4ed6\u3002\u201d \u3000\u3000\u767d\u8863\u5973\u90ce\u9053\uff1a\u201c\u4f60\u670d\u4e0b\u2018\u6696\u5fc3\u4e39\u2019\u5c31\u4e0d\u4f1a\u518d\u51b7\uff0c\u6211\u73b0\u5728\u8be5\u8d70\u5566\uff01\u201d \u3000\u3000\u201c\u53ef\u662f\u6211\u809a\u5b50\u997f\u3002\u201d \u3000\u3000\u201c\u4f60\u4e0d\u662f\u81ea\u5df1\u5e26\u7740\u5e72\u7cae\u4e48\uff1f\u201d \u3000\u3000\u201c\u5e72\u7cae\u592a\u51b7\u592a\u786c\uff0c\u6211\u60f3\u5403\u70b9\u70ed\u7684\u3002\u201d \u3000\u3000\u201c\u53eb\u4eba\u5c31\u8981\u6b7b\u5566\uff01\u4e0d\u5403\u4e5f\u6ca1\u5173\u7cfb\u3002\u201d \u3000\u3000\u201c\u59d1\u5a18\u9519\u5566\uff01\u72af\u4eba\u4e34\u5211\u524d\u3001\u90fd\u6709\u5927\u5403\u5927\u559d\u4e00\u987f\u7684\u89c4\u77e9\u3001\u96be\u9053\u6211\u8fd8\u6bd4\u4e0d\u4e0a\u2014\u4e2a\u6b7b\u56da\uff1f\u201d \u3000\u3000\u201c\u90a3\u6211\u5c31\u5230\u53a8\u623f\u770b\u770b\uff0c\u5982\u679c\u8fd8\u6709\u5269\u996d\u5269\u83dc\uff0c\u9a6c\u4e0a\u5c31\u66ff\u4f60\u9001\u6765\u3002\u201d \u3000\u3000\u767d\u8863\u5973\u90ce\u8d70\u540e\uff0c\u9ea6\u5c0f\u660e\u968f\u5373\u6253\u5f00\u7eb8\u5305\uff0c\u628a\u90a3\u7c92\u2018\u6696\u5fc3\u4e39\u2019\u670d\u4e0b\u3002 \u3000\u3000\u672c\u6765\uff0c\u4ed6\u539f\u60f3\u5e72\u8106\u51bb\u6b7b\u7b97\u4e86\uff0e\u514d\u5f97\u518d\u88ab\u9634\u624b\u4e00\u9b54\u6d3b\u5bb0\uff0e \u3000\u3000\u4f46\u53c8\u89c9\u5f97\u51bb\u6b7b\u4e00\u5b9a\u8981\u5f88\u4e45\u65f6\u95f4\uff0c\u6bd4\u88ab\u6740\u66f4\u8981\u53d7\u7f6a\uff0c\u4f55\u51b5\u4e5f\u4e0d\u4e00\u5b9a\u767e\u5206\u4e4b\u767e\u5c31\u975e\u6b7b\u4e0d\u53ef\uff0c \u603b\u4f1a\u6709\u4e00\u7ebf\u751f\u673a\u3002 \u3000\u3000\u670d\u4e0b\u2018\u6696\u5fc3\u4e39\u2019\uff0c\u679c\u7136\u4e0d\u5230\u987f\u996d\u5de5\u592b\uff0e\u4fbf\u5bd2\u610f\u5c3d\u9664\u3002 \u3000\u3000\u53c8\u8fc7\u4e0d\u4e45\uff0c\u767d\u53cb\u5973\u90ce\u4fbf\u63d0\u7740\u4e00\u4e2a\u98df\u76d2\u8fdb\u6765\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u559c\u51fa\u671b\u5916\uff0c\u56e0\u4e3a\u98df\u76d2\u91cc\u7684\u83dc\u996d\u90fd\u662f\u70ed\u7684\uff0c\u800c\u4e14\u8fd8\u6709\u534a\u58f6\u9152\u3002 \u3000\u3000\u767d\u8863\u5973\u90ce\u628a\u996d\u83dc\u5728\u5730\u4e0a\u6446\u597d\u9053\uff1a\u201c\u5feb\u5403\u5427\uff01\u514d\u5f97\u51c9\u4e86\u3002\u201d \u3000\u3000\u767d\u8863\u5973\u90ce\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u65b9\u624d\u8bf4\u8fc7\uff0c\u7f6a\u72af\u4e34\u5211\u524d\u90fd\u8981\u559d\u9152\u4e48\uff1f\u6240\u4ee5\u6211\u4e0d\u5f97\u4e0d\u66ff\u4f60\u51c6\u5907 \u9152\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u4e0d\u60f3\u548c\u5bf9\u65b9\u6597\u5634\uff0c\u5306\u5306\u7528\u8fc7\u9152\u996d\uff0c\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u6211\u6709\u4ef6\u4e8b\uff0c\u4e0d\u65b9\u4fbf\u542f\u9f7f\u3002\u201d \u3000\u3000\u201c\u4f60\u53ea\u6709\u4e00\u591c\u597d\u6d3b\uff0e\u8fd8\u6709\u4ec0\u4e48\u4e0d\u65b9\u4fbf\u542f\u9f7f\u7684\uff1f\u201d \u3000\u3000\u201c\u5982\u679c\u8981\u65b9\u4fbf\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u201c\u4f60\u5fcd\u4e00\u5fcd\u4e0d\u6210\u4e48\uff1f\u201d \u3000\u3000\u201c\u90a3\u600e\u4e48\u5fcd\u5f97\u4f4f\uff01\u201d \u3000\u3000\u767d\u8863\u5973\u90ce\u62ac\u624b\u5411\u58c1\u89d2\u2014\u6307\u9053\uff1a\u201c\u90a3\u91cc\u6709\u5757\u5927\u77f3\u677f\uff0c\u6380\u5f00\u77f3\u677f\uff0c\u4e0b\u9762\u662f\u4e2a\u5927\u6d1e\uff0c\u4e0d\u8bba\u5927\u65b9 \u4fbf\u5c0f\u65b9\u4fbf\u90fd\u53ef\u4ee5\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u62bd\u4e86\u62bd\u9f3b\u5b50\u9053\uff1a\u201c\u2018\u5bd2\u51b0\u5ba4\u2019\u91cc\u4f3c\u4e4e\u95fb\u4e0d\u5230\u81ed\u5473\u3002\u201d \u3000\u3000\u767d\u8863\u5973\u90ce\u832b\u7136\u9053\uff1a\u201c\u2018\u5bd2\u51b0\u5ba4\u2019\u91cc\u600e\u4e48\u4f1a\u6709\u81ed\u5473\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4e0b\u9762\u90a3\u4e2a\u5751\u6d1e\u5982\u679c\u6ee1\u4e86\uff0c\u600e\u4f1a\u4e0d\u81ed\uff1f\u201d \u3000\u3000\u767d\u8863\u5973\u90ce\u7b11\u9053\uff1a\u201c\u6380\u5f00\u77f3\u677f\u4f60\u5c31\u77e5\u9053\u5566\uff01\u90a3\u5751\u6d1e\u4e0b\u9762\u7684\u5c71\u6da7\uff0c\u810f\u4e1c\u897f\u843d\u4e0b\u53bb\uff0e\u9a6c\u4e0a\u5c31\u88ab \u6da7\u6c34\u51b2\u8d70\uff0c\u90a3\u91cc\u8fd8\u80fd\u7559\u4e0b\u6c14\u5473\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u5185\u5fc3\u4e00\u52a8\u9053\uff1a\u201c\u7531\u5751\u901a\u5230\u4e0b\u9762\u5c71\u6da7\uff0c\u4e0d\u77e5\u6709\u591a\u6df1\uff1f\u201d \u3000\u3000\u767d\u8863\u5973\u90ce\u9053\uff1a\u201c\u603b\u6709\u4e09\u4e94\u4e08\u6df1\u5427\uff01\u4f60\u95ee\u8fd9\u4e9b\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4e0d\u8fc7\u968f\u4fbf\u95ee\u95ee\u3002\u201d \u3000\u3000\u767d\u8863\u5973\u90ce\u7b49\u9ea6\u5c0f\u660e\u5403\u5b8c\uff0e\u628a\u7897\u76d8\u6536\u8fdb\u98df\u76d2\uff0e\u4e34\u8d70\u65f6\u56de\u5934\u671b\u4e86\u671b\u9ea6\u5c0f\u660e\u4e00\u773c\u9053\uff1a\u201c\u65b9\u4fbf\u65f6 \u6700\u597d\u8c28\u614e\u4e9b\uff0c\u4e07\u4e00\u6454\u4e0b\u53bb\uff0c\u4e0d\u662f\u95f9\u7740\u73a9\u7684\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u7b49\u767d\u8863\u5973\u90ce\u8d70\u540e\uff0c\u7acb\u5373\u6380\u5f00\u58c1\u89d2\u95f4\u5730\u4e0a\u7684\u77f3\u677f\u3002 \u3000\u3000\u8fd9\u662f\u4ed6\u552f\u4e00\u9003\u751f\u7684\u51fa\u8def\uff0c\u60f3\u4e0d\u5230\u767d\u8863\u5973\u90ce\u7adf\u80af\u5bf9\u4ed6\u6cc4\u9732\u8fd9\u9879\u673a\u5bc6\uff0e\u5426\u5219\uff0c\u4ed6\u53c8\u600e\u4f1a\u60f3\u5230 \u7531\u6b64\u9003\u751f\u7684\u3002 \u3000\u3000\u90a3\u5751\u6d1e\u5927\u7ea6\u5c3a\u8bb8\u65b9\u5706\uff0c\u4ee5\u9ea6\u5c0f\u660e\u7684\u8eab\u6750\uff0c\u8db3\u53ef\u901a\u8fc7\uff0c\u4e0b\u9762\u4e5f\u679c\u7136\u662f\u900f\u7a7a\u7684\uff0c\u56e0\u4e3a\u4e00\u6380\u5f00 \u77f3\u677f\uff0c\u4fbf\u6709\u4e00\u9635\u98ce\u6d8c\u4e86\u4e0a\u6765\u3002 \u3000\u3000\u53ea\u662f\u6b64\u65f6\uff0e\u6b63\u503c\u6df1\u591c\uff0e\u76ee\u5149\u518d\u597d\uff0c\u4e5f\u65e0\u6cd5\u770b\u6e05\u4e0b\u9762\u7684\u666f\u7269\uff0e\u66f4\u65e0\u6cd5\u770b\u51fa\u79bb\u5c71\u6da7\u7a76\u7adf\u6709\u591a \u6df1\u3002 \u3000\u3000\u5176\u5b9e\uff0c\u82e5\u6362\u4e86\u666e\u901a\u4eba\uff0c\u5373\u4f7f\u770b\u6e05\u4e0b\u9762\u7684\u666f\u8c61\uff0c\u4e5f\u4e0d\u6562\u4e0b\u53bb\uff0c\u82e5\u771f\u662f\u4e09\u4e94\u4e08\u7684\u6df1\u5ea6\uff0c\u6389\u4e0b \u53bb\u5c82\u6709\u6d3b\u547d\u4e4b\u7406\u3002 \u3000\u3000\u4f46\u9ea6\u5c0f\u660e\u5374\u7528\u4e0d\u7740\u987e\u8651\u8fd9\u4e9b\uff0c\u4ee5\u4ed6\u7684\u8eab\u624b\u548c\u8f7b\u529f\uff0c\u5373\u4f7f\u5341\u4e08\u516b\u4e08\u7684\u6df1\u5ea6\uff0c\u8dc3\u4e0b\u53bb\u4e5f\u53ef\u6beb \u53d1\u4e0d\u4f24\uff0e \u3000\u3000\u504f\u504f\u4ed6\u5df2\u88ab\u9634\u624b\u4e00\u9b54\u70b9\u4e86\u7a74\u9053\uff0c\u5168\u8eab\u52b2\u529b\u5c3d\u5931\uff0c\u82e5\u6b64\u523b\u7531\u5751\u6d1e\u8df3\u4e0b\u53bb\uff0c\u7167\u6837\u4e5f\u662f\u6b7b\u8def\u4e00 \u6761\uff0e \u3000\u3000\u4ed6\u660e\u767d\uff0e\u4ee5\u4e00\u822c\u70b9\u7a74\u624b\u6cd5\u800c\u8bba\uff0c\u6700\u591a\u4e24\u4e2a\u65f6\u8fb0\uff0e\u7a74\u9053\u4fbf\u4f1a\u4e0d\u89e3\u81ea\u5f00\uff0c\u4f46\u82e5\u4f7f\u7528\u7684\u662f\u72ec\u95e8 \u7279\u522b\u624b\u6cd5\uff0e\u90a3\u5c31\u5f88\u96be\u8bb2\u4e86\u3002 \u3000\u3000\u4f30\u8ba1\u65f6\u95f4\uff0e\u773c\u4e0b\u5927\u7ea6\u662f\u4e8c\u66f4\u5de6\u53f3\u3002 \u3000\u3000\u79bb\u5929\u4eae\u8fd8\u6709\u4e09\u4e2a\u65f6\u8fb0\uff0c\u53ea\u8981\u9634\u624b\u4e00\u9b54\u7528\u7684\u662f\u666e\u901a\u70b9\u7a74\u624b\u6cd5\uff0c\u4ed6\u5c31\u7528\u4e0d\u7740\u7740\u6025\uff0e\u53ef\u4ee5\u65ad\u5b9a \u7684\u662f\uff0c\u9634\u624b\u4e00\u9b54\u7edd\u4e0d\u53ef\u80fd\u5728\u5929\u4eae\u524d\u6740\u4ed6\u3002 \u3000\u3000\u4e0d\u7ba1\u5982\u4f55\uff0c\u4ed6\u8fd8\u662f\u5e0c\u671b\u7a74\u9053\u80fd\u8d8a\u5feb\u89e3\u5f00\u8d8a\u597d\uff0c\u4e8e\u662f\uff0c\u4ed6\u76d8\u817f\u5750\u5728\u5730\u4e0a\uff0c\u6697\u8fd0\u4e39\u7530\u4e4b\u6c14\uff0c \u5e0c\u671b\u80fd\u63d0\u524d\u51b2\u5f00\u7a74\u9053\u3002 \u3000\u3000\u5c82\u77e5\u7a74\u9053\u5df2\u7ecf\u88ab\u70b9\uff0e\u4e39\u7530\u4e4b\u6c14\u65e0\u6cd5\u96c6\u4e2d\uff0e\u6839\u672c\u4e0d\u80fd\u7740\u529b\u8fd0\u6c14\uff0c\u8bf4\u5f97\u660e\u767d\u4e00\u70b9\uff0e\u5c31\u662f\u4f7f\u4e0d \u4e0a\u52b2\u6765\u3002 \u3000\u3000\u4ed6\u5728\u9893\u7136\u65e0\u5948\u4e4b\u4e0b\uff0e\u867d\u7136\u4e0d\u518d\u8fd0\u6c14\u51b2\u7a74\uff0e\u5185\u5fc3\u5374\u8d8a\u53d1\u6025\u8e81\u8d77\u6765\u3002 \u3000\u3000\u5927\u7ea6\u4e00\u4e2a\u65f6\u8fb0\u8fc7\u540e\uff0c\u51fa\u4e4e\u4ed6\u610f\u6599\u4e4b\u5916\u7684\uff0c\u7adf\u7136\u5728\u4e0d\u77e5\u4e0d\u89c9\u4e2d\uff0e\u4ed6\u7684\u7a74\u9053\u4e0d\u89e3\u81ea\u5f00\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u5f53\u771f\u5927\u559c\u8fc7\u671b\uff0e\u7acb\u5373\u7531\u5730\u4e0a\u8dc3\u8eab\u800c\u8d77\uff0e\u6b63\u8981\u7531\u5751\u6d1e\u8dc3\u4e0b\u65f6\uff0c\u5374\u5ffd\u7136\u8f6c\u5ff5\u9053\uff1a\u201c\u6211 \u4f55\u4e0d\u7b49\u5929\u8272\u9ece\u660e\u65f6\uff0c\u770b\u6e05\u4e0b\u9762\u666f\u8c61\u540e\u518d\u4e0b\u53bb\uff0e\u53cd\u6b63\u9634\u624b\u4e00\u9b54\u90a3\u8001\u738b\u516b\u86cb\u5df2\u51b3\u5b9a\u5929\u4eae\u540e\u518d\u6740\u6389 \u6211\u3002 \u3000\u3000\u5728\u5929\u4eae\u4e4b\u524d\uff0c\u4ed6\u4e0d\u53ef\u80fd\u524d\u6765\uff0e\u5373\u4f7f\u4ed6\u6253\u5f00\u77f3\u95e8\u8fdb\u6765\u65f6\uff0c\u6211\u518d\u8df3\u4e0b\u53bb\u4e5f\u6765\u5f97\u53ca\u3002\u201d \u3000\u3000\u4ed6\u6240\u4ee5\u505a\u6b64\u51b3\u5b9a\uff0c\u4e0d\u5916\u662f\u62c5\u5fc3\u53d7\u4e86\u767d\u8863\u5973\u90ce\u7684\u9a97\uff0c\u4e07\u4e00\u4e0b\u9762\u662f\u4e07\u4e08\u65e0\u5e95\u7684\u6df1\u6e0a\uff0c\u5373\u4f7f\u8f7b \u529f\u5df2\u5230\u8fbe\u767b\u5cf0\u9020\u6781\u4e4b\u5883\uff0e\u53ea\u6015\u4ecd\u96be\u4ee5\u4fdd\u4f4f\u6027\u547d\u3002 \u3000\u3000\u5728\u611f\u89c9\u4e0a\uff0c\u4eca\u591c\u786e\u662f\u6700\u957f\u7684\u4e00\u591c\uff0c\u79f0\u5f97\u4e0a\u662f\u201c\u5ea6\u591c\u5982\u5e74\u201d\uff0e \u3000\u3000\u5751\u6d1e\u4e0b\u65b9\u7684\u666f\u7269\uff0c\u7ec8\u4e8e\u6e10\u6e10\u6e05\u6670\u8d77\u6765\uff0c\u534a\u70b9\u4e0d\u9519\uff0c\u4e0b\u9762\u662f\u4e00\u5904\u6df1\u6c9f\u5de8\u58d1\uff0e\u6df1\u5ea6\u4e5f\u6b63\u5982\u767d \u8863\u5973\u90ce\u6240\u8a00\uff0e\u5927\u7ea6\u4e09\u4e94\u4e08\u7684\u5149\u666f\u3002 \u3000\u3000\u7531\u6b64\u53ef\u89c1\uff0c\u8fd9\u5904\u2018\u5bd2\u51b0\u5ba4\u2019\u6b63\u5728\u4e00\u5757\u5de8\u5927\u7684\u7a81\u5ca9\u4e0a\uff0e\u82e5\u518d\u5411\u5916\u6316\u6398\uff0c\u90a3\u5c31\u8981\u900f\u7a7a\u4e86\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u4e0d\u60f3\u518d\u7b49\uff0c\u5f53\u5373\u53cc\u624b\u6849\u4f4f\u5751\u6d1e\u4e24\u65c1\uff0c\u53cc\u817f\u5148\u884c\u60ac\u7a7a\u6c89\u4e0b\uff0c\u7136\u540e\u53cc\u624b\u4e00\u677e\uff0c\u76f4\u843d\u800c \u4e0b\u3002 \u3000\u3000\u5f53\u843d\u4e0b\u5927\u7ea6\u4e00\u4e08\u8ddd\u79bb\u540e\uff0c\u5df2\u53ef\u63a5\u89e6\u5230\u5d16\u58c1\u3002 \u3000\u3000\u63a5\u7740\u8fd0\u8d77\u8f7b\u529f\uff0c\u8f6c\u77ac\u4fbf\u843d\u4e0b\u6765\u3002 \u3000\u3000\u867d\u7136\u4ed6\u7684\u8f7b\u529f\uff0c\u5df2\u5230\u8fbe\u843d\u82b1\u98d8\u53f6\uff0c\u5bf8\u8349\u4e0d\u60ca\u7684\u5883\u5730\uff0c\u4f46\u56e0\u4ee5\u8fd9\u79cd\u65b9\u5f0f\u964d\u843d\u4e0d\u6613\u63a7\u5236\uff0e\u843d \u4e0b\u4e4b\u540e\uff0c\u53cc\u811a\u4ecd\u7136\u9677\u5165\u6da7\u4e2d\u534a\u622a\u3002 \u3000\u3000\u4ed6\u4e00\u8dc3\u51fa\u6c34\u4e0a\u5cb8\uff0e\u6b64\u523b\u7684\u6b22\u6109\u4e4b\u60c5\uff0c\u4e0d\u96be\u60f3\u89c1\uff0c\u50cf\u6b7b\u56da\u9022\u5230\u4e86\u5927\u8d66\uff0c\u6b7b\u91cc\u9003\u751f\uff0e\u5230\u73b0\u5728 \u4ed6\u624d\u9886\u4f1a\u5230\u751f\u547d\u7684\u53ef\u8d35\u3002 \u3000\u3000\u4ed6\u6253\u91cf\u5f62\u52bf\uff0c\u5fc3\u77e5\u53ea\u8981\u987a\u7740\u6da7\u6c34\u7684\u4e0b\u6e38\u8d70\uff0c\u4e0d\u96be\u627e\u5230\u9053\u8def\u3002 \u3000\u3000\u8fd9\u6761\u5c71\u8c37\u6781\u957f\uff0c\u8db3\u8db3\u8d70\u4e86\u987f\u996d\u5de5\u592b\uff0c\u624d\u5230\u8fbe\u8c37\u53e3\u3002 \u3000\u3000\u521a\u51fa\u4e86\u8c37\u53e3\uff0c\u7a81\u89c1\u4e00\u62b9\u767d\u5f71\uff0c\u7531\u8def\u65c1\u63a0\u51fa\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u95ea\u7535\u822c\u63a3\u51fa\u957f\u5251\uff0e\u6b63\u6b32\u51fa\u624b\u8fce\u654c\u3002 \u3000\u3000\u53ea\u542c\u4e00\u4e2a\u8106\u751f\u751f\u7684\u58f0\u97f3\u9053\uff1a\u201c\u522b\u51fa\u624b\uff01\u662f\u6211\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u6025\u6025\u6536\u56de\u5251\u52bf\uff0c\u6123\u4e86\u6123\u9053\uff1a\u201c\u59d1\u5a18\u600e\u4e48\u5728\u8fd9\u91cc\uff1f\u201d \u3000\u3000\u539f\u6765\u7531\u8def\u65c1\u95ea\u51fa\u4e4b\u4eba\uff0c\u7adf\u662f\u767d\u8863\u5973\u90ce\u3002 \u3000\u3000\u767d\u8863\u5973\u90ce\u5ae3\u7136\u4e00\u7b11\u9053\uff1a\u201c\u96be\u9053\u6211\u5c31\u4e0d\u53ef\u4ee5\u548c\u4f60\u518d\u89c1\u4e00\u9762\u4e48\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u81ea\u6628\u65e5\u548c\u767d\u8863\u5973\u90ce\u89c1\u9762\u540e\uff0c\u6697\u89c1\u5979\u4eba\u957f\u5f97\u5f88\u7f8e\uff0c\u800c\u4e14\u9887\u4f3c\u82d7\u7d20\u5170\uff0e\u5374\u603b\u89c9\u5f97\u5979\u8fc7 \u4e8e\u51b7\u9177\u65e0\u60c5\u3002 \u3000\u3000\u6b64\u523b\uff0c\u5979\u8fd9\u5ae3\u7136\u4e00\u7b11\uff0c\u7ec8\u4e8e\u4f7f\u4ed6\u611f\u89c9\u5230\u5979\u7684\u7f8e\u4e3d\u52a8\u4eba\uff0c\u4f3c\u4e4e\u4e5f\u6709\u6e29\u67d4\u591a\u60c5\u7684\u4e00\u9762\u3002 \u3000\u3000\u201c\u59d1\u5a18\u53c8\u6709\u4ec0\u4e48\u4e8b\u89c1\u6211\uff0e\u83ab\u975e\u8981\u6349\u6211\u56de\u53bb\uff1f\u201d \u3000\u3000\u201c\u4f60\u771f\u7684\u4ee5\u4e3a\u6211\u662f\u8981\u6349\u4f60\u56de\u53bb\uff1f\u201d \u3000\u3000\u201c\u4f60\u662f\u9634\u624b\u4e00\u9b54\u90a3\u4e2a\u8001\u738b\u516b\u86cb\u7684\u4eba\uff0c\u6211\u79c1\u81ea\u9003\u8d70\uff0c\u6beb\u65e0\u7591\u95ee\u7684\u4f60\u662f\u6765\u6349\u6211\u56de\u53bb\uff0e\u53ef \u60dc\u2026\u2026\u201d \u3000\u3000\u201c\u53ef\u60dc\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u53ef\u60dc\u4f60\u7edd\u5bf9\u4e0d\u662f\u6211\u7684\u5bf9\u624b\uff0c\u5373\u4f7f\u73b0\u5728\u9634\u624b\u4e00\u9b54\u90a3\u4e2a\u8001\u738b\u516b\u86cb\u6765\u4e86\uff0c\u6211\u4e5f\u672a\u5fc5\u5c31\u6015\u4ed6 \u554a\uff01\u201d \u3000\u3000\u767d\u8863\u5973\u90ce\u53c8\u662f\u4e00\u7b11\uff0c\u8fd9\u4e00\u7b11\u7b11\u5f97\u6709\u5982\u6625\u82b1\u7efd\u653e\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u6211\u82e5\u8981\u6349\u4f60\uff0c\u53c8\u4f55\u5fc5\u653e \u4f60\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u6211\u600e\u4f1a\u662f\u4f60\u653e\u7684\uff1f\u201d \u3000\u3000\u767d\u8863\u5973\u90ce\u9053\uff1a\u201c\u2018\u5bd2\u51b0\u5ba4\u2019\u4e0b\u90a3\u77f3\u677f\u7684\u79d8\u5bc6\uff0c\u662f\u8c01\u544a\u8bc9\u4f60\u7684\uff1f\u6628\u591c\u6211\u5728\u2018\u5bd2\u51b0\u5ba4\u2019\u4e34\u8d70 \u65f6\uff0c\u8fd8\u7279\u522b\u63d0\u9192\u4e86\u4f60\u4e00\u53e5\uff0c\u4f60\u662f\u806a\u660e\u4eba\uff0c\u4e0d\u8be5\u4f1a\u4e0d\u8fc7\u610f\u6765\uff1f\u201d \u3000\u3000\u5176\u5b9e\uff0e\u9ea6\u5c0f\u660e\u65e9\u5c31\u6709\u6240\u8b66\u609f\uff0c\u53ea\u662f\u96be\u514d\u5fc3\u4e2d\u7eb3\u95f7\u800c\u5df2\uff0e\u6b64\u523b\u542c\u767d\u8863\u5973\u90ce\u4e00\u8bf4\uff0c\u7ec8\u4e8e\u5b8c\u5168 \u604d\u7136\u800c\u609f\u3002 \u3000\u3000\u767d\u8863\u5973\u90ce\u518d\u9053\uff1a\u201c\u5b9e\u5bf9\u4f60\u8bb2\uff0c\u8fde\u90a3\u2018\u6696\u5fc3\u4e39\u2019\u4e5f\u662f\u6211\u5077\u5077\u7ed9\u4f60\u7684\uff0c\u5bb6\u5e08\u6839\u672c\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u5927\u4e3a\u611f\u52a8\uff0c\u62b1\u62f3\u6df1\u6df1\u2014\u793c\u9053\uff1a\u201c\u591a\u8c22\u59d1\u5a18\u6551\u547d\u4e4b\u6069\uff0c\u5927\u6069\u5927\u5fb7\uff0c\u5fc5\u5f53\u540e\u62a5\u3002\u53ea\u662f \u5728\u4e0b\u62c5\u5fc3\u56de\u53bb\u4e4b\u540e\uff0c\u9634\u624b\u4e00\u9b54\u90a3\u8001\u738b\u516b\u86cb\u5fc5\u5b9a\u653e\u4e0d\u8fc7\u4f60\u3002\u201d \u3000\u3000\u201c\u6211\u56de\u53bb\u53ea\u6709\u6b7b\u8def\u4e00\u6761\uff0e\u201d \u3000\u3000\u201c\u8fd9\u6837\u8bf4\u662f\u6211\u8fde\u7d2f\u59d1\u5a18\u4e86\uff0c\u59d1\u5a18\u4e3a\u4e86\u6551\u6211\u800c\u727a\u7272\uff0c\u7a76\u7adf\u4e3a\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u4e3a\u4e86\u6211\u81ea\u5df1\u3002\u201d \u3000\u3000\u201c\u59d1\u5a18\u8fd9\u8bdd\uff1f\u2026\u201d \u3000\u3000\u767d\u8863\u5973\u90ce\u6de1\u6de1\u4e00\u7b11\u9053\uff1a\u201c\u96be\u9053\u6211\u5fc5\u987b\u518d\u56de\u2018\u4e5d\u5929\u7384\u9634\u6d1e\u5e9c\u2019\u4e0d\u53ef\u4e48\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u8fde\u5fd9\u9894\u9996\u9053\uff1a\u201c\u5bf9\uff0c\u59d1\u5a18\u5927\u53ef\u4e0d\u5fc5\u518d\u56de\u53bb\uff0c\u53ea\u662f\u6211\u60f3\u77e5\u9053\u4f60\u51c6\u5907\u5230\u54ea\u91cc\u53bb\uff0c\u81f3\u5c11 \u6211\u8be5\u62a4\u9001\u4f60\u4e00\u7a0b\u3002\u201d \u3000\u3000\u767d\u8863\u5973\u90ce\u9053\uff1a\u201c\u6211\u8981\u5230\u54ea\u91cc\uff0c\u4f60\u8be5\u5fc3\u91cc\u6709\u6570\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u4e0d\u89c9\u559c\u9053\uff1a\u201c\u539f\u6765\u59d1\u5a18\u662f\u8981\u968f\u6211\u5230\u5317\u5cb3\u8ff7\u8e2a\u8c37\uff1f\u201d \u3000\u3000\u201c\u7b97\u88ab\u4f60\u731c\u5bf9\u4e86\uff0c\u4e0d\u7136\u6211\u4f55\u5fc5\u6551\u4f60\u3002\u201d \u3000\u3000\u201c\u90a3\u592a\u597d\u4e86\uff0c\u81f3\u5c11\u6211\u4eec\u7eff\u6797\u603b\u5be8\u53c8\u6dfb\u4e86\u4e00\u4efd\u529b\u91cf\uff0e\u201d \u3000\u3000\u201c\u4f60\u53ef\u77e5\u9053\u6211\u8981\u5230\u5317\u5cb3\u8ff7\u8e2a\u8c37\u7684\u539f\u56e0\uff1f\u201d \u3000\u3000\u201c\u59d1\u5a18\u8bf7\u8bf4\u660e\u767d\u3002\u201d \u3000\u3000\u201c\u4f60\u6628\u5929\u4e0d\u662f\u95ee\u8fc7\u6211\u7684\u59d3\u540d\u4e48\uff0c\u6211\u73b0\u5728\u544a\u8bc9\u4f60\uff0c\u5f53\u4f60\u77e5\u9053\u6211\u662f\u8c01\uff0c\u81ea\u7136\u5c31\u77e5\u9053\u6211\u8981\u79bb\u5f00 \u2018\u4e5d\u5929\u7384\u9634\u6d1e\u5e9c\u2019\u7684\u539f\u56e0\u4e86\uff0e\u201d \u3000\u3000\u201c\u59d1\u5a18\u8bf7\u5feb\u8bf4\u51fa\u82b3\u540d\uff01\u201d \u3000\u3000\u201c\u6211\u53eb\u82d7\u7d20\u82d3\uff01\u201d \u3000\u3000\u201c\u83ab\u975e\u548c\u82d7\u7d20\u5170\u662f\u59d0\u59b9\uff1f\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0e\u5979\u662f\u6211\u59d0\u59d0\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u8d8a\u53d1\u559c\u51fa\u671b\u5916\uff0c\u96be\u602a\u5979\u7684\u6a21\u6837\uff0e\u548c\u82d7\u7d20\u5170\u5341\u5206\u76f8\u4f3c\uff0c\u4e5f\u96be\u602a\u5979\u4f1a\u80cc\u53db\u9634\u624b\u2014\u9b54\uff0c \u6539\u6295\u8ff7\u8e2a\u8c37\u3002 \u3000\u3000\u82d7\u7d20\u82d3\u5e7d\u5e7d\u4e00\u53f9\uff0c\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u81ea\u4ece\u4e09\u5e74\u524d\u59d0\u59d0\u968f\u80e1\u67cf\u9f84\u800c\u8d70\u540e\uff0c\u6211\u5c31\u53d7\u5bb6\u5e08\u767e\u822c\u76d1 \u89c6\uff0c\u800c\u4e14\u5dee\u4e00\u70b9\u88ab\u4ed6\u5904\u6b7b\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u53ef\u662f\uff0e\u73b0\u5728\u90a3\u8001\u738b\u516b\u86cb\u597d\u50cf\u5bf9\u4f60\u5f88\u4fe1\u4efb\u3002\u201d \u3000\u3000\u82d7\u7d20\u82d3\u9053\uff1a\u201c\u8fd9\u5b8c\u5168\u662f\u6211\u5bf9\u4ed6\u7684\u5fe0\u8d1e\u8868\u73b0\u6240\u4e89\u53d6\u7684\u3002\u201d \u3000\u3000\u5979\u8bf4\u7740\uff0c\u56de\u5230\u8def\u65c1\u62ce\u8d77\u4e00\u4e2a\u884c\u56ca\u9053\uff1a\u201c\u8fd9\u662f\u6211\u7684\u968f\u8eab\u8d35\u91cd\u4e4b\u7269\uff0c\u6709\u8bdd\u6162\u6162\u518d\u8bf4\uff0e\u73b0\u5728\u6700 \u8981\u7d27\u7684\u4fbf\u662f\u5feb\u5feb\u79bb\u5f00\u6b64\u5730\uff0e\u82e5\u88ab\u4ed6\u8ffd\u4e0a\uff0c\u4f60\u6211\u5c31\u5168\u6d3b\u4e0d\u6210\u4e86\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u6211\u5bf9\u8fd9\u91cc\u4e0d\u719f\uff0e\u5c31\u8bf7\u59d1\u5a18\u524d\u9762\u5e26\u8def\uff01\u201d \u3000\u3000\u4e8e\u662f\uff0c\u4e24\u4eba\u2014\u524d\u4e00\u540e\uff0c\u6025\u6025\u5411\u524d\u5954\u884c\u3002 \u3000\u3000\u79bb\u5f00\u8c37\u53e3\u4e0d\u8fdc\u5904\uff0c\u524d\u9762\u4fbf\u662f\u4e00\u7247\u4e1b\u6797\u3002 \u3000\u3000\u5c31\u5728\u4ed6\u4eec\u5feb\u63a5\u8fd1\u90a3\u7247\u4e1b\u6797\u65f6\uff0e\u7a81\u7136\u7531\u91cc\u9762\u59d7\u59d7\u8d70\u51fa\u56db\u540d\u7eff\u8863\u5c0f\u5a62\u3002 \u3000\u3000\u82d7\u7d20\u82d3\u731b\u89c1\u90a3\u56db\u540d\u7eff\u8863\u5c0f\u5a62\uff0c\u7acb\u523b\u8138\u8272\u5927\u53d8\uff0c\u60c5\u4e0d\u81ea\u7981\u505c\u4e0b\u811a\u6b65\u3002 \u5367\u9f99\u751f\u300a\u5999\u7edd\u5929\u9999\u300b \u7b2c \u516d \u56de\u3000\u529b\u6551\u4f73\u4eba \u3000\u3000\u9ea6\u5c0f\u660e\u4e5f\u89c9\u5f97\u4e0d\u5999\uff0c\u6025\u6025\u95ee\u9053\uff1a\u201c\u5979\u4eec\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u82d7\u7d20\u82d3\u5c1a\u672a\u6765\u5f97\u53ca\u5f00\u53e3\u3002 \u3000\u3000\u4e00\u4e2a\u53d1\u633d\u9053\u9afb\uff0e\u624b\u6267\u62c2\u5c18\uff0c\u8138\u957f\u5982\u9a6c\uff0e\u53cc\u98a7\u9ad8\u7a81\uff0e\u80f8\u5782\u767d\u9aef\uff0c\u8eab\u62ab\u9ed1\u888d\uff0c\u6ee1\u9762\u6b7b\u7070\u7684\u7626 \u9ad8\u8001\u8005\uff0c\u7f13\u7f13\u7531\u6797\u5185\u8d70\u4e86\u51fa\u6765\u3002 \u3000\u3000\u8fd9\u4eba\u6b63\u662f\u540d\u9707\u6b66\u6797\u7684\u4e00\u4ee3\u9b54\u5934\u9634\u624b\u4e00\u9b54\u3002 \u3000\u3000\u82d7\u7d20\u82d3\u8d8a\u53d1\u795e\u8272\u60e8\u53d8\uff0c\u51e0\u4e4e\u50f5\u5728\u5f53\u5730\u3002 \u3000\u3000\u9634\u624b\u4e00\u9b54\u4e00\u5bf9\u767d\u591a\u9ed1\u5c11\u7684\u773c\u775b\uff0c\u95ea\u5c04\u7740\u6e5b\u6e5b\u7cbe\u5149\uff0c\u8fb9\u8d70\u8fb9\u563f\u563f\u51b7\u7b11\u9053\uff1a\u201c\u8d31\u5a62\uff0c\u73b0\u5728\u8fd8 \u6709\u4ec0\u4e48\u8bdd\u8bf4\uff1f\u201d \u3000\u3000\u82d7\u7d20\u82d3\u54ea\u91cc\u8fd8\u80fd\u8bf4\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u9634\u624b\u4e00\u9b54\u53c8\u51b7\u7b11\u4e86\u4e00\u9635\uff0c\u7b11\u5f97\u6709\u5982\u9b3c\u57df\u91cc\u5439\u6765\u4e00\u9635\u9634\u98ce\uff0c\u63a5\u9053\uff1a\u201c\u81ea\u4ece\u82d7\u7d20\u5170\u90a3\u8d31\u5a62\u53db \u79bb\u5e08\u95e8\u540e\uff0c\u8001\u592b\u5c31\u6709\u610f\u628a\u4f60\u5904\u6b7b\uff01 \u3000\u3000\u504f\u4f60\u8fd9\u8d31\u5a62\u7adf\u80fd\u5728\u8001\u592b\u9762\u524d\uff0c\u8868\u73b0\u5f97\u767e\u4f9d\u767e\u987a\uff0c\u9a97\u5f97\u8001\u592b\u5c45\u7136\u76f8\u4fe1\uff0c\u4f60\u662f\u771f\u7684\u5fe0\u8d1e\u4e0d \u8d30\uff01 \u3000\u3000\u60f3\u4e0d\u5230\u4f60\u7ec8\u4e8e\u73b0\u51fa\u4e86\u539f\u5f62\uff0c\u81ea\u5df1\u9003\u8d70\u4e0d\u7b97\uff0c\u8fd8\u80c6\u6562\u653e\u8d70\u59d3\u9ea6\u7684\u5c0f\u5b50\uff0c\u4f60\u81ea\u5df1\u8bf4\uff0c\u8981\u600e\u4e48 \u6b7b\u6cd5\uff1f\u201d \u3000\u3000\u53ea\u89c1\u9ea6\u5c0f\u660e\u4e00\u633a\u80f8\uff0c\u957f\u5251\u5df2\u7ecf\u51fa\u9798\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u5927\u58f0\u559d\u9053\uff1a\u201c\u9634\u624b\u4e00\u9b54\uff0c\u4f60\u7528\u4e0d\u7740\u53d1\u5a01\uff0c\u82d7\u59d1\u5a18\u4f1a\u6015\u4f60\uff0c\u5728\u4e0b\u6211\u5374\u6015\u4e0d\u4e86\u4f60 \u5440\uff01\u201d \u3000\u3000\u9634\u624b\u2014\u9b54\u4e24\u773c\u659c\u7784\u4e86\u51e0\u773c\u9053\uff1a\u201c\u5c0f\u5b50\uff0c\u522b\u6025\uff0c\u8001\u592b\u5148\u5bb0\u4e86\u5979\u4ee5\u540e\uff0c\u96be\u9053\u8fd8\u4f1a\u7559\u4e0b\u5b50 \u4f60\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u53cc\u76ee\u5706\u7741\u9053\uff1a\u201c\u6709\u5c0f\u7237\u5728\uff0e\u4f60\u8fd9\u8001\u6df7\u86cb\u5c31\u522b\u60f3\u52a8\u82d7\u59d1\u5a18\u2014\u6839\u6c57\u6bdb\uff01\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u4e24\u988a\u4e00\u9635\u62bd\u6410\u9053\uff1a\u201c\u4f60\u9a82\u8c01\u8001\u6df7\u86cb\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u5f53\u7136\u662f\u9a82\u4f60\uff0c\u53eb\u4f60\u8001\u6df7\u86cb\uff0c\u662f\u5bf9\u4f60\u5ba2\u6c14\u3002\u201d \u3000\u3000\u201c\u5982\u679c\u4e0d\u5ba2\u6c14\u5462\uff1f\u201d \u3000\u3000\u201c\u4f60\u5c31\u662f\u8001\u738b\u516b\u86cb\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u867d\u7136\u4e00\u5411\u5929\u4e0d\u6015\u5730\u4e0d\u6015\uff0c\u6b64\u523b\u96be\u514d\u4e5f\u60f3\u5230\u81ea\u5df1\u5fc5\u5b9a\u51f6\u591a\u5409\u5c11\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u65e9\u542c\u8bf4\u8fc7\uff0c\u5f53\u5e74\u8fde\u80e1\u67cf\u9f84\u90fd\u9669\u4e9b\u683d\u5728\u8fd9\u8001\u9b54\u5934\u624b\u91cc\uff0c\u81ea\u5df1\u6b66\u529f\u518d\u9ad8\uff0c\u4e5f\u9ad8\u4e0d\u8fc7\u80e1 \u67cf\u9f84\uff0e \u3000\u3000\u56e0\u4e4b\uff0c\u9ea6\u5c0f\u660e\u5f00\u53e3\u5c31\u9a82\uff0c\u9664\u4e86\u85c9\u4ee5\u58ee\u80c6\u5916\uff0c\u53e6\u4e00\u4e2a\u60f3\u6cd5\uff0c\u5c31\u662f\u80fd\u75db\u75db\u5feb\u5feb\u9a82\u4e0a\u4e00\u987f\uff0e\u603b \u662f\u8d5a\u5230\u7684\u3002 \u3000\u3000\u9634\u624b\u4e00\u9b54\u5982\u4f55\u5fcd\u5f97\u4e0b\u8fd9\u53e3\u6c14\uff0c\u53ea\u89c1\u4ed6\u80a9\u4e0d\u6643\u52a8\uff0c\u817f\u4e0d\u5c48\u819d\uff0c\u500f\u5ffd\u95f4\u5df2\u5230\u4e86\u9ea6\u5c0f\u660e\u7684\u8ddf \u524d\uff0e \u3000\u3000\u53f3\u624b\u62c2\u5c18\u4e00\u626c\uff0c\u4e00\u62db\u201c\u5012\u8f6c\u9634\u9633\u201d\u75be\u626b\u800c\u4e0b\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u987f\u611f\u4e00\u80a1\u521a\u731b\u65e0\u4fe6\u7684\u9634\u51b7\u52b2\u98ce\uff0c\u6709\u5982\u60ca\u6d9b\u9a87\u6d6a\u822c\u76f4\u5411\u81ea\u5df1\u649e\u6765\u3002 \u3000\u3000\u4ed6\u731b\u5438\u4e00\u53e3\u771f\u6c14\uff0c\u4e00\u9762\u6697\u8fd0\u5185\u529b\uff0c\u51dd\u805a\u7f61\u6c14\u62a4\u8eab\u3002 \u3000\u3000\u63a5\u7740\uff0c\u9ea6\u5c0f\u660e\u7eb5\u8eab\u800c\u8d77\uff0e\u5934\u4e0a\u811a\u4e0b\uff0e\u65bd\u51fa\u4e86\u4e00\u5f0f\u201c\u8fc5\u96f7\u51fb\u9876\u201d\uff0c\u95ea\u7535\u822c\u653b\u5411\u9634\u624b\u2014\u9b54\u4e0a \u76d8\u3002 \u3000\u3000\u9634\u624b\u4e00\u9b54\u51b7\u51b7\u4e00\u7b11\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u4f60\u60f3\u627e\u6b7b\uff01\u201d \u3000\u3000\u4e00\u9762\u201c\u547c\u201d\u7684\u4e00\u58f0\uff0e \u3000\u3000\u53c8\u62cd\u51fa\u4e00\u638c\u3002 \u3000\u3000\u8fd9\u4e00\u638c\u638c\u98ce\u4e4b\u731b\uff0c\u66f4\u80dc\u65b9\u624d\u62c2\u5c18\u626b\u51fa\u7684\u201c\u5012\u8f6c\u9634\u9633\u201d\uff0c\u4f7f\u5f97\u9ea6\u5c0f\u660e\u51cc\u7a7a\u7684\u8eab\u5b50\uff0c\u751f\u751f\u9707 \u6454\u56de\u6765\uff0e \u3000\u3000\u6240\u5e78\u9ea6\u5c0f\u660e\u8f7b\u529f\u4f73\u5999\uff0c\u843d\u5730\u4e4b\u540e\uff0c\u4ecd\u80fd\u4e0d\u6447\u4e0d\u6643\uff0e \u3000\u3000\u5982\u679c\u6362\u4e86\u4e00\u822c\u6b66\u6797\u4eba\u7269\uff0c\u81f3\u5c11\u4f1a\u6454\u4e2a\u8fde\u6eda\u5e26\u722c\u3002 \u3000\u3000\u9634\u624b\u4e00\u9b54\u89c1\u62c2\u5c18\u548c\u638c\u529b\uff0c\u5c45\u7136\u90fd\u6ca1\u4f24\u7740\u9ea6\u5c0f\u660e\uff0c\u4ed6\u4e0d\u89c9\u4e5f\u6697\u6697\u5730\u4e3a\u5bf9\u65b9\u7684\u8eab\u624b\u79f0\u8d5e\u4e0d \u5df2\uff01 \u3000\u3000\u9ea6\u5c0f\u660e\u529f\u592b\u65e0\u6cd5\u4e0e\u9634\u624b\u4e00\u9b54\u6297\u8861\uff0e\u65e2\u7136\u786c\u62fc\u4e0d\u6210\uff0c\u4fbf\u53ea\u6c42\u80fd\u8bbe\u6cd5\u8131\u8eab\u3002 \u3000\u3000\u597d\u5728\u9634\u624b\u4e00\u9b54\u5e76\u4e0d\u8ffd\u88ad\uff0e\u5374\u7ad9\u5728\u539f\u5730\uff0c\u5de6\u81c2\u5e73\u4f38\uff0c\u638c\u52bf\u5fae\u626c\uff0c\u4e94\u6307\u7b95\u5f20\uff0c\u4e0d\u5927\u4e00\u4f1a\uff0c\u624b \u81c2\u51e0\u4e4e\u7c97\u4e86\u2014\u500d\u3002 \u3000\u3000\u638c\u6307\u4e4b\u95f4\uff0c\u4e5f\u970e\u65f6\u989c\u8272\u53d8\u9ed1\uff0c\u800c\u4e14\u53d1\u51fa\u54af\u54af\u58f0\u54cd\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u53ea\u770b\u5f97\u5fc3\u5934\u6697\u51db\uff0c\u5fd6\u9053\uff1a\u201c\u4f20\u8bf4\u4e2d\uff0c\u8fd9\u8001\u738b\u516b\u86cb\u5df2\u7ec3\u5c31\u9634\u98ce\u638c\uff0c\u83ab\u975e\u8981\u53d1\u51fa\u9634\u98ce \u638c\u529b\uff1f\u201d \u3000\u3000\u53ea\u542c\u9634\u624b\u4e00\u9b54\u563f\u563f\u4e00\u7b11\u9053\uff1a\u201c\u5c0f\u5b50\uff0c\u4f60\u53ef\u6562\u8fc7\u6765\u63a5\u8001\u592b\u4e00\u638c\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u51b7\u54fc\u4e00\u58f0\u9053\uff1a\u201c\u8001\u738b\u516b\u86cb\uff0c\u4f60\u82e5\u81ea\u4fe1\u9634\u98ce\u638c\u5389\u5bb3\uff0c\u4f55\u4e0d\u539f\u5730\u53d1\u51fa\u4e00\u638c\u6765\u8bd5\u8bd5 \u5427\uff01\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u9053\uff1a\u201c\u597d\u5427\uff01\u4f60\u5c31\u8bd5\u8bd5\u770b\u3002\u201d \u3000\u3000\u8bf4\u7f62\uff0c\u679c\u7136\u539f\u5730\u4e0d\u52a8\uff0e\u5de6\u638c\u4e00\u626c\u4e00\u63a8\uff0e \u3000\u3000\u638c\u98ce\u631f\u7740\u98ce\u6c99\u8d70\u77f3\uff0c\u76f4\u649e\u8fc7\u6765\uff01 \u3000\u3000\u8fd9\u65f6\uff0c\u53cc\u65b9\u76f8\u8ddd\u81f3\u5c11\u4e24\u4e08\u5f00\u5916\uff0c\u9ea6\u5c0f\u660e\u4ecd\u6709\u4e9b\u7ad9\u811a\u4e0d\u4f4f\u3002 \u3000\u3000\u5c24\u5176\u90a3\u638c\u98ce\u72b9\u5982\u4ece\u51b0\u7a96\u4e2d\u6d8c\u51fa\u6765\u7684\u4e00\u80a1\u5bd2\u98ce\uff0c\u76f4\u80fd\u4fb5\u808c\u523a\u9aa8\uff0c\u4f7f\u5f97\u9ea6\u5c0f\u660e\u4e00\u9762\u6025\u6025\u8dc3 \u9000\uff0c\u4e00\u9762\u8fde\u6253\u51e0\u4e2a\u5bd2\u98a4\u3002 \u3000\u3000\u5c82\u77e5\u521a\u521a\u8eb2\u8fc7\u4e00\u638c\uff0c\u9634\u624b\u4e00\u9b54\u7684\u7b2c\u4e8c\u638c\u53c8\u5df2\u88ad\u6765\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u8fde\u5fd9\u53eb\u9053\uff1a\u201c\u82d7\u59d1\u5a18\uff0c\u54b1\u4eec\u5feb\u8d70\uff01\u201d \u3000\u3000\u867d\u7136\u773c\u4e0b\u5df2\u662f\u5728\u8c37\u53e3\u4e4b\u5916\uff0c\u4f46\u4e24\u65c1\u4ecd\u662f\u60ac\u5d16\u5ced\u58c1\uff0c\u800c\u4e14\u5fc5\u987b\u7a7f\u8fc7\u524d\u9762\u90a3\u7247\u6797\u624d\u53ef\u8131\u79bb\u9669 \u5883\u3002 \u3000\u3000\u597d\u5728\u9ea6\u5c0f\u660e\u6599\u5b9a\u9634\u624b\u4e00\u9b54\u7684\u8f7b\u529f\uff0e\u5fc5\u4e0d\u53ef\u80fd\u80dc\u8fc7\u4e86\u81ea\u5df1\uff0c\u56e0\u4e4b\uff0c\u5e76\u4e0d\u62c5\u5fc3\u4f1a\u88ab\u5bf9\u65b9\u6349 \u4f4f\uff0e \u3000\u3000\u90a3\u77e5\u521a\u63a0\u51fa\u56db\u4e94\u4e08\u540e\uff0c\u4fbf\u542c\u5230\u8eab\u540e\u82d7\u7d20\u82d3\u7684\u5c16\u53eb\u58f0\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u6025\u6025\u56de\u5934\u770b\u53bb\uff01 \u3000\u3000\u53ea\u89c1\u82d7\u7d20\u82d3\u5df2\u88ab\u9634\u624b\u4e00\u9b54\u6349\u4f4f\uff0c\u6b63\u50cf\u63d0\u5c0f\u9e21\u822c\u63d0\u5728\u624b\u91cc\u3002 \u3000\u3000\u82d7\u7d20\u82d3\u66fe\u6551\u8fc7\u9ea6\u5c0f\u660e\uff0c\u9ea6\u5c0f\u660e\u89c1\u6b64\u60c5\u5f62\uff0c\u5c82\u80fd\u5355\u72ec\u9003\u8d70\u3002 \u3000\u3000\u4ed6\u7acb\u5373\u505c\u6b65\u6b62\u8eab\uff0c\u9ad8\u53eb\u9053\uff1a\u201c\u9634\u624b\u4e00\u9b54\uff0c\u6628\u591c\u662f\u6211\u81ea\u5df1\u8bbe\u6cd5\u9003\u8131\u7684\uff0c\u4e0d\u5e72\u82d7\u59d1\u5a18\u7684\u4e8b\u3002 \u4f60\u6709\u672c\u9886\u53ea\u7ba1\u627e\u6211\uff0c\u62ff\u82d7\u59d1\u5a18\u51fa\u6c14\uff0c\u7b97\u4ec0\u4e48\u4e1c\u897f\uff01\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u6839\u672c\u4e0d\u7406\u4f1a\u9ea6\u5c0f\u660e\uff0c\u559d\u4ee4\u8eab\u540e\u7684\u56db\u540d\u7eff\u8863\u5c0f\u5a62\u9053\uff1a\u201c\u628a\u2018\u65ad\u80a0\u9500\u9aa8\u6c64\u2019\u7ed9\u8fd9\u53db \u5e05\u706d\u7956\u7684\u8d31\u5a62\u704c\u4e0b\uff01\u201d \u3000\u3000\u53ea\u542c\u82d7\u7d20\u82d3\u98a4\u58f0\u60e8\u53eb\u9053\uff1a\u201c\u5e08\u7236\u9976\u547d\uff0c\u5e08\u7236\u9976\u547d\uff0e\u5f1f\u5b50\u4e0b\u6b21\u518d\u4e5f\u4e0d\u6562\u4e86\uff01\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u68ee\u68ee\u7b11\u9053\uff1a\u201c\u54ea\u91cc\u8fd8\u6709\u4e0b\u6b21\u3002\u4f60\u4eec\u8fd8\u7ad9\u5728\u90a3\u91cc\u505a\u4ec0\u4e48\uff1f\u5feb\u704c\uff01\u201d \u3000\u3000\u56db\u540d\u7eff\u8863\u5c0f\u5a62\u4e2d\u7684\u4e00\u4eba\uff0c\u7acb\u523b\u4ece\u6000\u91cc\u638f\u51fa\u4e00\u53ea\u62c7\u6307\u5927\u5c0f\u7684\u7d2b\u8272\u74f7\u74f6\uff0c\u62d4\u5f00\u74f6\u585e\uff0c\u6765\u5230\u82d7 \u7d20\u82d3\u8ddf\u524d\u3002 \u3000\u3000\u82d7\u7d20\u82d3\u65e9\u5df2\u5413\u5f97\u9762\u65e0\u4eba\u8272\uff0c\u4f53\u4f3c\u7b5b\u7ce0\u822c\u6296\u4e2a\u4e0d\u505c\u3002 \u3000\u3000\u5979\u8ddf\u9634\u624b\u4e00\u9b54\u591a\u5e74\uff0e\u5f53\u7136\u77e5\u9053\u201c\u65ad\u80a0\u9500\u9aa8\u6c64\u201d\u7684\u5389\u5bb3\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u53ea\u611f\u4f53\u5185\u2014\u80a1\u70ed\u8840\u76f4\u51b2\uff0c\u5927\u559d\u9053\uff1a\u201c\u4f4f\u624b\uff01\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u563f\u563f\u7b11\u9053\uff1a\u201c\u5c0f\u5b50\uff0e\u624d\u4e0d\u8fc7\u4e00\u591c\u65f6\u95f4\uff0c\u4f60\u5c31\u5bf9\u5979\u8fd9\u4e48\u5173\u5fc3\u3002\u8001\u592b\u8fd9\u2018\u65ad\u80a0\u9500\u9aa8 \u6c64\u2019\u704c\u4e0b\u4e4b\u540e\uff0c\u4e0d\u6d88\u7247\u523b\uff0c\u8fd9\u8d31\u5a62\u4fbf\u8981\u80a0\u65ad\u9aa8\u6467\uff0c\u5316\u6210\u4e00\u56e2\u8840\u6c34\u3002\u4f60\u5982\u679c\u559c\u6b22\u5979\uff0c\u6700\u597d\u8d81\u73b0 \u5728\u591a\u770b\u5979\u51e0\u773c\uff0e\u518d\u8fc7\u7247\u523b\uff0c\u5c31\u522b\u60f3\u518d\u770b\u5230\u5979\u4e86\uff01\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u8fb9\u8bf4\u8fb9\u63a2\u51fa\u5de6\u624b\uff0c\u634f\u4f4f\u82d7\u7d20\u82d3\u4e0b\u989a\uff0c\u4f7f\u5f97\u82d7\u7d20\u82d3\u975e\u5f20\u5f00\u5634\u4e0d\u53ef\u3002 \u3000\u3000\u90a3\u624b\u6301\u7d2b\u8272\u74f7\u74f6\u7684\u7eff\u8863\u5c0f\u5a62\uff0c\u7acb\u5373\u8fd1\u524d\u4e24\u6b65\uff0c\u74f6\u53e3\u5bf9\u6b63\u82d7\u7d20\u82d3\u7684\u5634\u5411\u4e0b\u5012\u53bb\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u770b\u5230\u8fd9\u91cc\uff0c\u90a3\u987e\u4e00\u5207\uff0c\u8fde\u4eba\u5e26\u5251\uff0e\u4e00\u63a0\u800c\u81f3\uff01 \u3000\u3000\u76f4\u5411\u9634\u624b\u4e00\u9b54\u649e\u53bb\uff01 \u3000\u3000\u9ea6\u5c0f\u660e\u8fd9\u4e00\u8bb0\u8fde\u4eba\u5e26\u5251\uff0c\u5411\u9634\u624b\u2014\u9b54\u4e00\u95ea\u800c\u81f3\u7684\u653b\u51fb\uff0c\u5206\u660e\u662f\u6b66\u5b66\u4e2d\u6700\u96be\u4e60\u6210\u7684\u201c\u5fa1\u5251 \u672f\u201d\u3002 \u3000\u3000\u9634\u624b\u4e00\u9b54\u4e4d\u89c1\u9ea6\u5c0f\u660e\u5c0f\u5c0f\u5e74\u7eaa\u5c45\u7136\u5728\u5251\u672f\u4e0a\u6709\u6b64\u9020\u8be3\uff0c\u4e5f\u4e0d\u514d\u6697\u6697\u5403\u60ca\uff0c\u60ca\u5f97\u4ed6\u53ea\u597d\u677e \u5f00\u634f\u4f4f\u82d7\u7d20\u82d3\u4e0b\u989a\u7684\u5de6\u624b\u3002 \u3000\u3000\u8d81\u52bf\u4ef0\u8155\uff0c\u5288\u51fa\u4e00\u638c\uff0c\u4f46\u8fd9\u6b21\u5288\u51fa\u7684\u638c\u529b\uff0c\u5e76\u672a\u5c06\u5bf9\u65b9\u963b\u4f4f\u3002 \u3000\u3000\u8fd9\u662f\u56e0\u4e3a\u9634\u624b\u4e00\u9b54\u4f27\u4fc3\u51fa\u624b\uff0c\u4ee5\u81f4\u529b\u9053\u672a\u80fd\u7528\u5b9e\u4e4b\u6545\u3002 \u3000\u3000\u9634\u624b\u4e00\u9b54\u5927\u6012\u4e4b\u4e0b\uff0c\u867d\u7136\u53f3\u624b\u62c2\u5c18\uff0c\u63a5\u7740\u6325\u51fa\uff0c\u4f46\u5de6\u8170\u8863\u670d\u8fd8\u662f\u88ab\u523a\u4e86\u4e00\u4e2a\u5927\u6d1e\uff0c\u82e5\u975e \u4ed6\u95ea\u907f\u5f97\u5feb\uff0c\u5f88\u53ef\u80fd\u5c31\u5403\u4e0a\u5927\u4e8f\u3002 \u3000\u3000\u8fd9\u8001\u9b54\u5934\u7acb\u5373\u8138\u4e0a\u9752\u7b4b\u66b4\u8d77\uff0e\u4e0d\u5f97\u4e0d\u653e\u4e0b\u82d7\u7d20\u82d3\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u81ea\u77e5\u529f\u529b\u65e0\u6cd5\u4e0e\u5bf9\u65b9\u76f8\u6297\uff0e\u7136\u800c\u4e3a\u4e86\u6551\u82d7\u7d20\u82d3\u6027\u547d\uff0c\u5374\u53c8\u4e0d\u5f97\u4e0d\u548c\u9634\u624b\u2014\u9b54\u529b\u62fc \u5230\u5e95\u3002 \u3000\u3000\u6b64\u523b\uff0e\u9ea6\u5c0f\u660e\u53ea\u80fd\u4ed7\u7740\u7075\u5de7\u7684\u8eab\u6cd5\uff0c\u4ee5\u53ca\u5e08\u95e8\u7edd\u6280\u201c\u6458\u661f\u6b65\u201d\uff0c\u548c\u9634\u624b\u4e00\u9b54\u5c55\u5f00\u4e86\u6e38 \u6597\uff01 \u3000\u3000\u9ea6\u5c0f\u660e\u7684\u8fd9\u79cd\u8c41\u51fa\u6027\u547d\uff0c\u5168\u529b\u8fce\u4e0a\u7684\u6253\u6cd5\uff0e\u5c45\u7136\u80fd\u591f\u4e00\u53e3\u6c14\u548c\u9634\u624b\u4e00\u9b54\u82e6\u6491\u4e86\u4e8c\u4e09\u5341 \u62db\uff0e \u3000\u3000\u9634\u624b\u4e00\u9b54\u867d\u7136\u6253\u5f97\u5360\u5c3d\u4e0a\u98ce\uff0c\u5374\u59cb\u7ec8\u65e0\u6cd5\u5c06\u9ea6\u5c0f\u660e\u5236\u670d\u3002 \u3000\u3000\u53c8\u62fc\u6597\u4e86\u4e8c\u4e09\u5341\u62db\uff01 \u3000\u3000\u9ea6\u5c0f\u660e\u7ec8\u4e8e\u6e10\u611f\u4e0d\u652f\uff0c\u4f46\u4e3a\u4e86\u6551\u82d7\u7d20\u82d3\uff0c\u5374\u53c8\u4e0d\u80fd\u4e0d\u5f3a\u81ea\u82e6\u6491\uff0e\u548c\u9634\u624b\u4e00\u9b54\u786c\u62fc\u4e86\u4e0b \u53bb\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\u3002 \u3000\u3000\u7a81\u542c\u6797\u5185\u54cd\u8d77\u4e00\u9635\u8863\u8882\u98d8\u98ce\u4e4b\u58f0\u3002 \u3000\u3000\u63a5\u7740\u4e24\u540d\u7070\u8863\u50e7\u4eba\u548c\u4e24\u540d\u4e2d\u5e74\u9053\u58eb\uff0c\u5df2\u7531\u6797\u5185\u95ea\u8eab\u800c\u51fa\uff0e \u3000\u3000\u4e24\u540d\u50e7\u4eba\u5404\u6267\u6212\u5200\uff0c\u4e24\u540d\u9053\u58eb\u5206\u6301\u957f\u5251\uff0c\u5728\u9634\u624b\u4e00\u9b54\u548c\u9ea6\u5c0f\u660e\u6fc0\u6218\u4e4b\u5904\u7684\u4e24\u4e08\u5f00\u5916\u7ad9\u4f4f \u4e86\u3002 \u3000\u3000\u56db\u4eba\u90fd\u9762\u8272\u51dd\u91cd\uff0c\u770b\u4e0d\u51fa\u4efb\u4f55\u8868\u60c5\u3002 \u3000\u3000\u9634\u624b\u4e00\u9b54\u548c\u9ea6\u5c0f\u660e\u5728\u8fd9\u79cd\u60c5\u5f62\u4e0b\uff0c\u4e0d\u5f97\u4e0d\u81ea\u52a8\u505c\u4e0b\u6765\u3002 \u3000\u3000\u9634\u624b\u4e00\u9b54\u662f\u5728\u9ed1\u9053\u4e0a\u8d70\u4e86\u5927\u534a\u751f\u7684\u90aa\u95e8\u4eba\u7269\uff0c\u4e00\u5411\u5bf9\u6b66\u6797\u4e2d\u6b63\u5927\u95e8\u6d3e\u7684\u5c11\u6797\u3001\u6b66\u5f53\u4eba\u7269 \u4e0d\u5177\u597d\u611f\u3002 \u3000\u3000\u6b64\u523b\uff0c\u867d\u5f04\u4e0d\u6e05\u4e24\u50e7\u4e24\u9053\u7684\u8eab\u4efd\u6765\u5386\uff0c\u5374\u5df2\u9884\u77e5\u5fc5\u5bf9\u81ea\u5df1\u4e0d\u5229\uff0c\u6b63\u8981\u51fa\u58f0\u559d\u95ee\uff0c\u53ea\u89c1\u6797 \u4e2d\u7f13\u7f13\u8d70\u51fa\u4e00\u50e7\u4e00\u9053\u3002 \u3000\u3000\u8d70\u5728\u524d\u9762\u7684\u50e7\u4eba\uff0c\u5e74\u7ea6\u516d\u65ec\uff0c\u65b9\u9762\u5927\u8033\uff0c\u6148\u7709\u5584\u76ee\uff0c\u8eab\u62ab\u9ec4\u8272\u8888\u88df\uff0c\u8eab\u8eaf\u9ad8\u5927\uff0c\u795e\u6001\u548c \u853c\u4e2d\u5e26\u7740\u5a01\u4e25\u3002 \u3000\u3000\u540e\u9762\u7684\u90a3\u540d\u9053\u957f\uff0c\u5e74\u5728\u4e94\u65ec\u5de6\u53f3\uff0c\u4e94\u7efa\u957f\u9aef\uff0c\u8eab\u7740\u85cf\u9752\u8272\u9053\u888d\uff0c\u80cc\u63d2\u957f\u5251\uff0c\u624b\u6267\u62c2\u5c18\uff0c \u4e00\u526f\u4ed9\u98ce\u9053\u9aa8\u6a21\u6837\uff0e \u3000\u3000\u8fd9\u2014\u50e7\u4e00\u9053\u7684\u51fa\u73b0\uff0c\u987f\u4f7f\u9634\u624b\u2014\u9b54\u5fc3\u751f\u51db\u9a87\u3002 \u3000\u3000\u9634\u624b\u4e00\u9b54\u5f53\u7136\u8ba4\u8bc6\u4ed6\u4eec\u4e00\u50e7\u4e00\u9053\uff0c\u524d\u9762\u7684\u9ec4\u888d\u50e7\u4eba\uff0c\u6b63\u662f\u5c11\u6797\u6d3e\u7684\u638c\u95e8\u4eba\u5929\u7985\u5927\u5e08\uff0c\u540e \u9762\u90a3\u9053\u4eba\uff0c\u5219\u662f\u6b66\u5f53\u56db\u9633\u4e2d\u7684\u9752\u9633\u9053\u957f\u3002 \u3000\u3000\u53ea\u542c\u5f97\u5929\u7985\u5927\u5e08\u5ba3\u4e86\u4e00\u58f0\u4f5b\u53f7\u9053\uff1a\u201c\u5929\u53f0\u4e07\u82b1\u5bab\u4e00\u522b\uff0c\u4e0d\u89c9\u53c8\u5c06\u4e24\u4e2a\u6708\uff0c\u4eca\u65e5\u8def\u8fc7\u5b9d\u5c71 \u5c1a\u672a\u8d8b\u5e9c\u62dc\u6664\uff0c\u4fbf\u5df2\u53c8\u4e0e\u9634\u624b\u65bd\u4e3b\u76f8\u4f1a\uff0c\u53ef\u8c13\u6709\u7f18\u4e86\uff01\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u53ea\u597d\u62f1\u624b\u4e3a\u793c\uff0c\u5374\u672a\u5f00\u53e3\u8bf4\u8bdd\uff0e \u3000\u3000\u5929\u7985\u5927\u5e08\u56de\u5934\u671b\u4e86\u9ea6\u5c0f\u660e\u4e00\u773c\u9053\uff1a\u201c\u8fd9\u4f4d\u5c0f\u5144\u5f1f\u4e0d\u77e5\u662f\u90a3\u4f4d\u9ad8\u4eba\u7684\u95e8\u4e0b\uff0c\u65b9\u624d\u5c45\u7136\u80fd\u548c \u9634\u624b\u65bd\u4e3b\u62fc\u6597\u6570\u5341\u56de\u5408\uff0c\u4ecd\u4f59\u52c7\u53ef\u5f53\u3002\u82e5\u5c06\u6765\u5047\u4ee5\u65f6\u65e5\uff0c\u524d\u9014\u5f53\u771f\u662f\u4e0d\u53ef\u9650\u91cf\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u5148\u540e\u53d7\u827a\u4e8e\u4e07\u82b1\u5bab\u4e3b\u4f5f\u516c\u5e38\u548c\u795e\u6756\u7fc1\uff0c\u8fd9\u4e24\u4eba\u867d\u4e0d\u7b97\u5927\u5978\u5927\u6076\uff0c\u5374\u4e5f\u662f\u6b63\u5c11\u90aa \u591a\u3002 \u3000\u3000\u518d\u52a0\u4e0a\u80e1\u67cf\u9f84\u548c\u8c37\u5bd2\u9999\u4e4b\u6b7b\uff0c\u4e5f\u90fd\u4e0e\u5c11\u6797\u3001\u6b66\u5f53\u4e24\u6d3e\u6709\u5173\uff0c\u56e0\u4e4b\uff0c\u95fb\u8a00\u51b7\u54fc\u4e00\u58f0\u9053\uff1a \u201c\u8001\u548c\u5c1a\uff0c\u5728\u4e0b\u7528\u4e0d\u7740\u4f60\u95ee\uff0c\u4e5f\u7528\u4e0d\u7740\u4f60\u5938\uff01\u201d \u3000\u3000\u5929\u7985\u5927\u5e08\u62c2\u987b\u4e00\u7b11\u9053\uff1a\u201c\u597d\u4e2a\u5014\u5f3a\u7684\u5c11\u5e74\uff0c\u4f60\u867d\u7136\u4e00\u8eab\u6b66\u529f\uff0c\u5df2\u7b97\u51fa\u7c7b\u62d4\u8403\uff0c\u4f46\u76ee\u524d\u5374 \u7edd\u975e\u9634\u624b\u65bd\u4e3b\u5bf9\u624b\u3002\u518d\u6597\u4e0b\u53bb\uff0c\u52bf\u5fc5\u5403\u4e8f\u4e0d\u53ef\uff0c\u8001\u8872\u53ca\u65f6\u8d76\u6765\uff0c\u4e5f\u8bb8\u5bf9\u4f60\u5927\u6709\u5e2e\u52a9\u201d\u3002 \u3000\u3000\u53ea\u542c\u9634\u624b\u4e00\u9b54\u9634\u68ee\u68ee\u4e00\u7b11\u9053\uff1a\u201c\u5929\u7985\u548c\u5c1a\uff0c\u4f60\u53ef\u662f\u8981\u5e2e\u90a3\u5c0f\u5b50\u6765\u5bf9\u4ed8\u8001\u592b\uff1f\u201d \u3000\u3000\u5929\u7985\u5927\u5e08\u53c8\u5ba3\u4e86\u58f0\u4f5b\u53f7\u9053\uff1a\u201c\u8001\u7eb3\u81ea\u4e0a\u6b21\u7ecf\u8fc7\u5929\u53f0\u4e07\u82b1\u5bab\u4e00\u573a\u6b66\u6797\u6d69\u52ab\uff0c\u5df2\u53d1\u8a93\u4e0d\u518d\u8f7b \u5f00\u6740\u6212\u3002\u6839\u672c\u4e0d\u60f3\u5e2e\u5fd9\u9634\u624b\u65bd\u4e3b\uff0c\u6216\u8fd9\u5c0f\u5144\u5f1f\u4efb\u4f55\u4e00\u65b9\uff01\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u9053\uff1a\u201c\u65e2\u7136\u4f60\u4e0d\u60f3\u591a\u7ba1\u95f9\u4e8b\uff0e\u90a3\u5c31\u6700\u597d\u8d70\u5f00\uff01\u201d \u3000\u3000\u5929\u7985\u5927\u5e08\u6447\u5934\u9053\uff1a\u201c\u8001\u8872\u867d\u4e0d\u60f3\u5e2e\u52a9\u4f60\u4eec\u4efb\u4f55\u4e00\u65b9\uff0c\u73b0\u5728\u5374\u8fd8\u4e0d\u80fd\u8d70\u5f00\uff0e\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u9053\uff1a\u201c\u4e0d\u77e5\u8001\u548c\u5c1a\u8fd8\u6709\u4ec0\u4e48\u8d35\u5e72\uff1f\u201d \u3000\u3000\u5929\u7985\u5927\u5e08\u89c6\u7ebf\u7f13\u7f13\u8f6c\u5411\u5446\u5728\u90a3\u91cc\u4e0d\u77e5\u6240\u9519\u7684\u82d7\u7d20\u82d3\u9053\uff1a\u201c\u4e0d\u77e5\u8fd9\u59d1\u5a18\u72af\u4e86\u4ec0\u4e48\u7f6a\u8fc7\uff0c\u60f9 \u5f97\u9634\u624b\u65bd\u4e3b\u8981\u4e3a\u5979\u704c\u4e0b\u6bd2\u836f\uff1f\u201d \u3000\u3000\u201c\u65b9\u624d\u7684\u4e8b\u4f60\u770b\u5230\u4e86\uff1f\u201d \u3000\u3000\u5929\u7985\u5927\u5e08\u9053\uff1a\u201c\u8001\u8872\u548c\u9752\u9633\u9053\u5144\u9690\u8eab\u5728\u6797\u5185\uff0c\u5df2\u7ecf\u770b\u4e86\u5f88\u4e45\u4e86\uff0c\u82e5\u975e\u4e3a\u4e86\u6551\u4eba\uff0c\u4e5f\u5c31\u4e0d \u51fa\u6765\u4e86\u3002\u201d \u3000\u3000\u201c\u8fd9\u662f\u8001\u592b\u81ea\u5bb6\u7684\u4e8b\uff0c\u53ea\u6015\u4f60\u8fd8\u7ba1\u4e0d\u7740\u3002\u201d \u3000\u3000\u5929\u7985\u5927\u5e08\u9053\uff1a\u201c\u6740\u4eba\u5bb3\u547d\uff0e\u975e\u540c\u5c0f\u53ef\uff0c\u8001\u8872\u867d\u4e0e\u6b64\u4e8b\u65e0\u5173\uff0c\u4f46\u65e2\u5df2\u9047\u4e0a\uff0c\u5374\u4e5f\u4e0d\u80fd\u8896\u624b \u65c1\u89c2\u4e0d\u7ba1\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u770b\u5230\u8fd9\u91cc\uff0c\u6697\u9053\uff1a\u201c\u770b\u6765\u51ed\u6211\u4e00\u4eba\u4e4b\u529b\uff0c\u662f\u65e0\u6cd5\u6551\u51fa\u82d7\u59d1\u5a18\u7684\uff0e\u4e3a\u4e86\u6551\u4eba\uff0c\u6b64\u523b \u5fc5\u987b\u6c42\u52a9\u4e8e\u5929\u7985\u548c\u9752\u9633\u624d\u6210\uff01\u201d \u3000\u3000\u60f3\u5230\u8fd9\u91cc\uff0c\u9ea6\u5c0f\u660e\u7acb\u5373\u9ad8\u58f0\u9053\uff1a\u201c\u8001\u7985\u5e08\uff0c\u90a3\u4f4d\u59d1\u5a18\u59d3\u82d7\uff0c\u5979\u662f\u9634\u624b\u8001\u9b54\u5934\u7684\u95e8\u4e0b\u5f1f \u5b50\u3002\u201d \u3000\u3000\u5929\u7985\u5927\u5e08\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u539f\u6765\u9634\u624b\u65bd\u4e3b\u662f\u8981\u5904\u6b7b\u81ea\u5df1\u7684\u95e8\u4e0b\u5f1f\u5b50\uff0c\u5374\u662f\u4e3a\u4f55\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u8fd9\u4f4d\u82d7\u59d1\u5a18\u770b\u4e0d\u60ef\u9634\u624b\u8001\u9b54\u5934\u7684\u9634\u9669\u90aa\u6076\uff0c\u51b3\u5b9a\u9003\u51fa\u4ed6\u90a3\u2018\u4e5d\u5929\u7384\u9634\u6d1e \u5e9c\u2019\uff0c\u4e0d\u60f3\u88ab\u9634\u624b\u8001\u9b54\u5934\u534a\u8def\u622a\u4f4f\uff0c\u6240\u4ee5\u624d\u8981\u7528\u2018\u65ad\u80a0\u9500\u9aa8\u6c64\u2019\u628a\u5979\u6bd2\u6b7b\u3002\u201d \u3000\u3000\u5929\u7985\u5927\u5e08\u7565\u4e00\u6c89\u541f\uff0c\u518d\u95ee\u9053\uff1a\u201c\u90a3\u4e48\u5c0f\u5144\u5f1f\uff0e\u4e3a\u4ec0\u4e48\u548c\u9634\u624b\u65bd\u4e3b\u6253\u4e86\u8d77\u6765\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u665a\u8f88\u6628\u665a\u88ab\u9634\u624b\u8001\u9b54\u56da\u7981\u4e86\u4e00\u591c\uff0c\u591a\u4e8f\u82d7\u59d1\u5a18\u642d\u6551\uff0e\u624d\u5f97\u6b7b\u91cc\u9003\u751f\uff0e\u5982 \u4eca\uff0c\u9634\u624b\u8001\u9b54\u5934\u8981\u6740\u82d7\u59d1\u5a18\uff0e\u665a\u8f88\u5c82\u80fd\u5750\u89c6\u4e0d\u7ba1\uff0e\u201d \u3000\u3000\u5929\u7985\u5927\u5e08\u53cc\u624b\u5408\u5341\u9053\uff1a\u201c\u5584\u54c9\uff01\u5584\u54c9\uff01\u5c0f\u5144\u5f1f\u77e5\u6069\u56fe\u62a5\uff0e\u5341\u5206\u96be\u5f97\uff0c\u8001\u8872\u51b3\u5b9a\u5e2e\u52a9\u4f60\uff0c \u6551\u51fa\u82d7\u59d1\u5a18\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u8fde\u5fd9\u62b1\u4e86\u62b1\u62f3\u9053\uff1a\u201c\u591a\u8c22\u8001\u7985\u5e08\u76f8\u52a9\uff0c\u73b0\u5728\u665a\u8f88\u624d\u77e5\u9053\u5c11\u6797\u3001\u6b66\u5f53\u786e\u5b9e\u662f\u6b66\u6797\u4e2d \u4e24\u5927\u5149\u660e\u95e8\u6d3e\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u8fd9\u8bdd\u786e\u5b9e\u662f\u53d1\u81ea\u5185\u5fc3\uff0e\u6b64\u523b\u4e0d\u4f46\u4e0d\u518d\u654c\u89c6\u5c11\u6797\u3001\u6b66\u5f53\uff0c\u800c\u4e14\uff0c\u8fd8\u5145\u6ee1\u4e86\u611f\u6fc0\u4e4b \u60c5\u3002 \u3000\u3000\u9634\u624b\u4e00\u9b54\u5fd6\u5ea6\u76ee\u524d\u60c5\u52bf\uff0c\u5982\u679c\u4e0d\u7b54\u5e94\u5bf9\u65b9\u7684\u8981\u6c42\uff0c\u53cc\u65b9\u5fc5\u5b9a\u53c8\u8d77\u62fc\u6218\uff0c\u5929\u7985\u5927\u5e08\u8eab\u4e3a\u5c11 \u6797\u638c\u95e8\uff0c\u4e00\u5411\u9886\u8896\u5929\u4e0b\u6b66\u6797\uff0e \u3000\u3000\u4ed6\u4e43\u5f53\u4ee3\u6b66\u6797\u5927\u5b97\u5e08\u8eab\u4efd\uff0c\u6b66\u529f\u76d6\u4e16\uff0c\u66f4\u52a0\u6709\u6b66\u5f53\u56db\u9633\u4e4b\u4e00\u7684\u9752\u9633\u76f8\u52a9\uff0c\u4e00\u65e6\u95f9\u53cd\uff0c\u5403 \u4e8f\u7684\u6beb\u65e0\u7591\u95ee\u5fc5\u5b9a\u662f\u81ea\u5df1\uff0e \u3000\u3000\u4f46\u9634\u624b\u4e00\u9b54\u5374\u4e0d\u4fbf\u592a\u8fc7\u793a\u5f31\uff0c\u54fc\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u8001\u592b\u5904\u7f6e\u81ea\u5df1\u7684\u95e8\u4e0b\u5f1f\u5b50\uff0c\u8001\u548c\u5c1a\u63d2\u624b\u8fc7 \u95ee\uff0c\u4e0d\u89c9\u5f97\u592a\u591a\u4e8b\u4e86\u4e48\uff1f\u201d \u3000\u3000\u5929\u7985\u5927\u5e08\u53cc\u76ee\u795e\u5149\u4e00\u95ea\uff0c\u9762\u751f\u5fae\u6120\u9053\uff1a\u201c\u82d7\u59d1\u5a18\u7eb5\u7136\u6709\u53db\u79bb\u5e08\u95e8\u4e4b\u5fc3\uff0c\u4e5f\u7f6a\u4e0d\u81f3\u6b7b\uff0c\u8001 \u673d\u5c82\u80fd\u89c1\u6b7b\u4e0d\u6551\uff1f\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u563f\u563f\u2014\u7b11\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u8001\u592b\u5c31\u60c5\u613f\u4e70\u4f60\u8001\u548c\u5c1a\u4e00\u4e2a\u9762\u5b50\uff0c\u628a\u5979\u5e26\u56de\u6d1e\u5e9c\uff0c \u518d\u884c\u5904\u7f6e\u3002\u201d \u3000\u3000\u5929\u7985\u5927\u5e08\u9053\uff1a\u201c\u9001\u4f5b\u9001\u4e0a\u5929\uff0c\u8001\u8872\u65e2\u7136\u8981\u6551\u8fd9\u4f4d\u82d7\u59d1\u5a18\uff0c\u5c82\u80fd\u773c\u770b\u5979\u518d\u5165\u864e\u53e3\u3002\u201d \u3000\u3000\u201c\u8001\u548c\u5c1a\u653e\u5fc3\uff0c\u8001\u592b\u7edd\u4e0d\u6740\u5979\u5c31\u662f\u3002\u201d \u3000\u3000\u5ffd\u89c1\u9752\u9633\u9053\u957f\u7ffb\u8155\u62d4\u51fa\u957f\u5251\uff0c\u8138\u8272\u4e00\u6c89\uff0c\u559d\u9053\uff1a\u201c\u5927\u5e08\u7528\u4e0d\u7740\u518d\u548c\u4ed6\u5520\u5520\u53e8\u53e8\uff0c\u4ed6\u82e5\u4e0d \u80af\u653e\u4e0b\u82d7\u59d1\u5a18\uff0c\u5c31\u7531\u8d2b\u9053\u548c\u4ed6\u89c1\u4e2a\u9ad8\u4f4e\uff01\u201d \u3000\u3000\u539f\u6765\u6b66\u5f53\u56db\u9633\uff0e\u9664\u524d\u4efb\u638c\u95e8\u7d2b\u9633\u5916\uff0c\u53e6\u4e09\u9633\u4fbf\u662f\u91d1\u9633\uff0e\u9752\u9633\u548c\u767d\u9633\u3002 \u3000\u3000\u5929\u53f0\u4e07\u82b1\u5bab\u4e00\u6218\uff0c\u7d2b\u9633\u3001\u91d1\u9633\u548c\u767d\u9633\u5168\u5df2\u60e8\u6b7b\uff0c\u56db\u9633\u4e2d\u76ee\u524d\u53ea\u5269\u4e0b\u9752\u9633\u4e00\u4eba\u3002 \u3000\u3000\u56e0\u4e4b\uff0c\u76ee\u524d\u7684\u9752\u9633\uff0c\u5728\u56de\u5230\u6b66\u5f53\u4e09\u5143\u89c2\u540e\uff0c\u5df2\u7ee7\u4efb\u5e08\u5144\u7d2b\u9633\u63a5\u4efb\u4e86\u6b66\u5f53\u638c\u95e8\u3002 \u3000\u3000\u4ed6\u5e74\u7eaa\u8f83\u8f7b\uff0c\u6db5\u517b\u4ea6\u4e0d\u53ca\u5929\u7985\u5927\u5e08\u6df1\u539a\uff0c\u662f\u4ee5\u5f88\u5bb9\u6613\u4fbf\u88ab\u6fc0\u6012\uff0e \u3000\u3000\u53ea\u542c\u9ea6\u5c0f\u660e\u53eb\u9053\uff1a\u201c\u9053\u957f\u8bf4\u5f97\u5bf9\uff0c\u9634\u624b\u2014\u9b54\u90a3\u8001\u738b\u516b\u86cb\uff0e\u5929\u751f\u8d31\u9aa8\u5934\uff0c\u5bf9\u4ed8\u4ed6\u52a8\u7528\u6b66 \u529b\uff0e\u624d\u80fd\u89e3\u51b3\u95ee\u9898\u3002\u60a8\u8bf7\u5feb\u5feb\u51fa\u624b\uff0c\u6211\u4e5f\u9a6c\u4e0a\u4e0b\u573a\u5e2e\u5fd9\u3002\u201d \u3000\u3000\u9634\u624b\u2014\u9b54\u4e00\u89c1\u4e0d\u5999\uff0c\u4e0d\u5f97\u4e0d\u89c1\u98ce\u8f6c\u8235\uff0c\u4e8e\u7b11\u4e86\u51e0\u58f0\u9053\uff1a\u201c\u65e2\u7136\u4f60\u4eec\u5c11\u6797\u3001\u6b66\u5f53\u770b\u597d\u4e86\u8fd9 \u8d31\u5a62\uff0c\u8001\u592b\u5c31\u9001\u4e2a\u987a\u6c34\u4eba\u60c5\uff0c\u53c8\u6709\u4f55\u4e0d\u53ef\uff0e\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u62c5\u5fc3\u9634\u624b\u2014\u9b54\u9010\u4e0b\u6bd2\u624b\uff0c\u7acb\u523b\u5927\u53eb\u9053\uff1a\u201c\u82d7\u59d1\u5a18\uff0c\u8fd8\u4e0d\u5feb\u8fc7\u6765\uff01\u201d \u3000\u3000\u82d7\u7d20\u82d3\u968f\u5373\u5feb\u6b65\u5411\u9ea6\u5c0f\u660e\u505c\u8eab\u4e4b\u5904\u5954\u6765\u3002 \u3000\u3000\u9634\u624b\u4e00\u9b54\u53ef\u80fd\u5df2\u65e0\u989c\u5f85\u5728\u5f53\u573a\uff0e\u5411\u56db\u540d\u7eff\u8863\u5c0f\u5a62\u4e00\u6325\u624b\u9053\uff1a\u201c\u54b1\u4eec\u8d70\uff01\u201d \u3000\u3000\u8f6c\u77ac\u95f4\uff0c\u4e94\u4eba\u5df2\u5165\u8c37\u53e3\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u82d7\u59d1\u5a18\u8fd8\u4e0d\u5feb\u5feb\u62dc\u8c22\u5929\u7985\u5927\u5e08\u548c\u9752\u9633\u9053\u957f\uff01\u201d \u3000\u3000\u82d7\u7d20\u82d3\u4f9d\u8a00\u671b\u7740\u5929\u7985\u5927\u5e08\u548c\u9752\u9633\u9053\u957f\u8dea\u62dc\u4e0b\u53bb\u3002 \u3000\u3000\u5929\u7985\u5927\u5e08\u5355\u638c\u7acb\u80f8\u7b54\u793c\u9053\uff1a\u201c\u59d1\u5a18\u8bf7\u8d77\uff0c\u4e0d\u77e5\u59d1\u5a18\u8981\u53bb\u4f55\u5904\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u62a2\u7740\u7b54\u9053\uff1a\u201c\u5c31\u5bf9\u8001\u7985\u5e08\u5b9e\u8bf4\u4e86\u5427\uff01\u82d7\u59d1\u5a18\u8981\u968f\u665a\u8f88\u5230\u5317\u5cb3\u8ff7\u8e2a\u8c37\u3002\u201d \u3000\u3000\u5929\u63b8\u5927\u5e08\u53cc\u76ee\u795e\u5149\u4e00\u95ea\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u8001\u8872\u5bf9\u5c0f\u5144\u5f1f\u4f3c\u66fe\u76f8\u8bc6\uff0c\u4e0a\u6b21\u5927\u53f0\u5c71\u4e07\u82b1\u5bab\u6fc0\u6218 \u65f6\uff0c\u4f60\u53ef\u662f\u968f\u5728\u8c37\u5bd2\u9999\u76df\u4e3b\u8eab\u65c1\uff1f\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u80e1\u524d\u76df\u4e3b\u662f\u665a\u8f88\u5e08\u5144\uff0c\u8c37\u524d\u76df\u4e3b\u662f\u665a\u8f88\u5e08\u5ac2\u3002\u201d \u3000\u3000\u201c\u5c0f\u5144\u5f1f\u56de\u5230\u5317\u5cb3\u8ff7\u8e2a\u8c37\u591a\u4e45\u4e86\uff1f\u201d \u3000\u3000\u201c\u5df2\u6709\u534a\u4e2a\u591a\u6708\u4e86\u3002\u201d \u3000\u3000\u201c\u76ee\u524d\u7531\u4ec0\u4e48\u4eba\u7ee7\u4efb\u7eff\u6797\u76df\u4e3b\uff1f\u201d \u3000\u3000\u201c\u5927\u7985\u5e08\u4e0d\u63d0\u4e5f\u7f62\uff01\u201d \u3000\u3000\u5929\u7985\u5927\u5e08\u5fae\u5fae\u4e00\u6014\u9053\uff1a\u201c\u542c\u5c0f\u5144\u5f1f\u8bed\u6c14\uff0c\u4f3c\u4e4e\u5bf9\u65b0\u4efb\u76df\u4e3b\u2026\u2026\u2026\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u65b0\u4efb\u76df\u4e3b\u662f\u970d\u5143\u4f3d\u90a3\u8001\u738b\u516b\u86cb\uff0c\u8001\u63b8\u5e08\u2014\u5b9a\u4e5f\u8ba4\u5f97\u4ed6\u3002\u201d \u3000\u3000\u5929\u7985\u5927\u5e08\u795e\u8272\u5fae\u53d8\uff0c\u9ed8\u4e86\u4e00\u9ed8\u9053\uff1a\u201c\u4f60\u4eec\u4e3a\u4ec0\u4e48\u8981\u63a8\u4e3e\u970d\u5143\u4f3d\uff0c\u7531\u4ed6\u7ee7\u4efb\u7eff\u6797\u76df\u4e3b\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u8c01\u4f1a\u63a8\u4e3e\u970d\u5143\u4f3d\u5462\uff1f\u662f\u7ecf\u8fc7\u2014\u756a\u6bd4\u6b66\u51b3\u6597\u540e\uff0c\u624d\u7531\u4ed6\u5750\u4e0a\u4e86\u7eff\u6797\u76df\u4e3b\u7684\u5b9d \u5ea7\u3002\u201d \u3000\u3000\u201c\u96be\u9053\u8ff7\u8e2a\u8c37\u5c31\u4ee5\u970d\u5143\u4f3d\u6b66\u529f\u6700\u9ad8\uff1f\u8001\u8872\u542c\u8bf4\u80e1\u3001\u8c37\u4e24\u4f4d\u76df\u4e3b\u7684\u624b\u4e0b\u4e2d\uff0c\u6709\u4f4d\u53eb\u949f\u2014\u8c6a \u7684\uff0e\u53e6\u6709\u4f4d\u53eb\u4f59\u4ea6\u4e50\u7684\u90fd\u6b66\u529f\u4e0d\u5f31\u3002 \u3000\u3000\u4ed6\u4eec\u4e24\u4eba\u4e3a\u4eba\u4e5f\u6b63\u6d3e\u4e9b\uff0c\u82e5\u662f\u7531\u4ed6\u4eec\u4e24\u4eba\u5176\u4e2d\u4e4b\u2014\u7ee7\u4efb\u7eff\u6797\u76df\u4e3b\uff0c\u603b\u6bd4\u970d\u5143\u4f3d\u8981\u597d\u5f97\u591a \u4e86\u3002\u201d \u3000\u3000\u201c\u8001\u7985\u5e08\u8bf4\u5f97\u5bf9\u6781\u5566\uff01\u53ef\u662f\u970d\u5143\u4f3d\u90a3\u8001\u738b\u516b\u86cb\u65e9\u6709\u9884\u8c0b\uff0c\u4ed6\u4e0d\u77e5\u90a3\u91cc\u76d7\u6765\u7684\u2018\u54cd\u5fc3 \u9732\u2019\uff0c\u7adf\u7136\u6709\u9ad8\u624b\u542c\u4ed6\u9a71\u4f7f\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u63a5\u7740\u628a\u5f53\u65f6\u4e89\u593a\u7eff\u6797\u76df\u4e3b\u7684\u7ecf\u8fc7\uff0c\u8bf4\u4e86\u4e00\u904d\u3002 \u3000\u3000\u5929\u7985\u5927\u5e08\u6c89\u541f\u4e86\u534a\u54cd\uff0c\u957f\u957f\u5401\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u970d\u5143\u4f3d\u7ee7\u4efb\u7eff\u6797\u76df\u4e3b\uff0e\u53ea\u6015\u4e0d\u4e45\u4e4b\u540e\uff0c\u7eff\u6797 \u4e2d\u53c8\u8981\u6380\u8d77\u2014\u573a\u6d69\u52ab\u4e86\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u4e0d\u89c9\u5fc3\u4e2d\u4e00\u52a8\u9053\uff1a\u201c\u665a\u8f88\u5012\u662f\u6709\u4e00\u4e2a\u529e\u6cd5\uff0c\u53ea\u662f\u4e0d\u77e5\u8001\u7985\u5e08\u80af\u4e0d\u80af\u7b54\u5e94\u5e2e\u5fd9 \u5462\uff1f\u201d \u3000\u3000\u5929\u7985\u5927\u5e08\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\u8bf7\u8bf4\u8bf4\u770b\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u8001\u7985\u5e08\u7684\u6b66\u529f\u76d6\u4e16\uff0c\u4e0d\u5982\u7531\u8001\u7985\u5e08\u60a8\u4eb2\u7387\u5c11\u6797\u9ad8\u624b\uff0c\u5230\u5317\u5cb3\u8ff7\u8e2a\u8c37\u8d70\u4e00 \u8d9f\u3002\u201d \u3000\u3000\u201c\u8001\u8872\u53bb\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u51ed\u8001\u7985\u5e08\u7684\u6b66\u529f\u6765\u8bf4\uff0c\u5b9a\u53ef\u6740\u4e86\u970d\u5143\u4f3d\uff0c\u5c82\u4e0d\u4e5f\u6d88\u5f2d\u4e86\u7eff\u6797\u4e2d\u7684\u2014\u573a\u6d69 \u52ab\u3002\u201d \u3000\u3000\u201c\u90a3\u7531\u8c01\u6765\u7ee7\u4efb\u7eff\u6797\u76df\u4e3b\u5462\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u5982\u679c\u8001\u7985\u5e08\u5ba2\u6c14\uff0c\u5c31\u8ba9\u7ed9\u949f\u4e00\u8c6a\u5e72\uff0c\u82e5\u8001\u7985\u5e08\u81ea\u5df1\u60f3\u5e72\uff0c\u6211\u4eec\u5c31\u62e5\u6234\u4f60 \u5e72\u3002\u201d \u3000\u3000\u5929\u7985\u5927\u5e08\u4e0d\u7531\u5475\u5475\u5927\u7b11\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\u8bf4\u5f97\u592a\u5929\u771f\u4e86\uff0c\u8001\u8872\u8eab\u4e3a\u4f5b\u95e8\u5f1f\u5b50\uff0c\u5c82\u80fd\u518d\u6df7\u8eab\u7eff \u6797\uff0c\u8fd9\u4e0d\u662f\u5929\u5927\u7b11\u8bdd\u4e48\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u8f6c\u4e86\u8f6c\u773c\u73e0\u9053\uff1a\u201c\u90a3\u5c31\u8ba9\u7ed9\u949f\u4e00\u8c6a\u5e72\uff0c\u4ed6\u867d\u7136\u8d76\u4e0d\u4e0a\u6211\u80e1\u5e08\u5144\u548c\u8c37\u5e08\u5ac2\uff0e\u81f3\u5c11\u4e5f \u7b97\u4e2a\u4eba\u624d\u3002\u201d \u3000\u3000\u5929\u7985\u5927\u5e08\u9053\uff1a\u201c\u8bdd\u867d\u8fd9\u6837\u8bf4\uff0c\u4f46\u4f5b\u95e8\u89c4\u6212\uff0e\u8001\u8872\u4e0d\u5f97\u4e0d\u9075\u3002\u201d \u3000\u3000\u201c\u8001\u7985\u5e08\u8bf4\u7684\u662f\u4ec0\u4e48\u89c4\u6212\uff1f\u201d \u3000\u3000\u5929\u7985\u5927\u5e08\u9053\uff1a\u201c\u4eba\u4e0d\u72af\u6211\uff0c\u6211\u4e0d\u72af\u4eba\uff0e\u9664\u975e\u970d\u5143\u4f3d\u72af\u4e0a\u5c11\u6797\uff0c\u5426\u5219\u8001\u8872\u7edd\u4e0d\u5e94\u4e3b\u52a8\u8fc7\u95ee \u7eff\u6797\u4e4b\u4e8b\u3002\u201d \u3000\u3000\u201c\u8001\u7985\u5e08\u8fd9\u8bdd\u9519\u4e86\uff01\u201d \u3000\u3000\u201c\u9519\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u201c\u65b9\u624d\u8001\u7985\u5e08\u4e3b\u52a8\u6551\u4e86\u82d7\u59d1\u5a18\uff0e\u53ef\u662f\u9634\u624b\u2014\u9b54\u5e76\u6ca1\u72af\u60a8\uff01\u201d \u3000\u3000\u5929\u7985\u5927\u5e08\u6709\u4e9b\u8bed\u585e\uff0c\u987f\u4e86\u4e00\u987f\u9053\uff1a\u201c\u8bdd\u4e0d\u80fd\u4e00\u6982\u800c\u8bba\uff0c\u4e8b\u60c5\u662f\u88ab\u8001\u8872\u51d1\u5de7\u9047\u4e0a\u4e86\uff0c\u8001\u8872 \u5c82\u80fd\u89c1\u6b7b\u4e0d\u6551\uff1f\u201d \u3000\u3000\u5ffd\u542c\u9752\u9633\u9053\u957f\u9053\uff1a\u201c\u5927\u5e08\uff0e\u65e2\u7136\u5df2\u7ecf\u6551\u4e86\u8fd9\u4f4d\u59d1\u5a18\uff0c\u54b1\u4eec\u8fd8\u662f\u8d76\u8def\u8981\u7d27\u3002\u201d \u3000\u3000\u5929\u7985\u5927\u5e08\u9894\u9996\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\u5c31\u8bf7\u56de\u5317\u5cb3\u8ff7\u8e2a\u8c37\u53bb\uff0c\u5f7c\u6b64\u540e\u4f1a\u6709\u671f\uff0c\u201d \u3000\u3000\u63a5\u7740\u76ee\u6ce8\u82d7\u7d20\u82d3\u9053\uff1a\u201c\u8001\u8872\u60c5\u613f\u5e26\u8fd9\u59d1\u5a18\u79bb\u5f00\u5415\u6881\u5c71\uff0c\u4e0d\u77e5\u82d7\u59d1\u5a18\u662f\u5426\u540c\u610f\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u6025\u6025\u62a2\u7740\u9053\uff1a\u201c\u82d7\u59d1\u5a18\u662f\u8981\u968f\u6211\u5230\u5317\u5cb3\u8ff7\u8e2a\u8c37\u7684\uff0e\u8001\u7985\u5e08\u4e3a\u4ec0\u4e48\u8981\u5e26\u82d7\u59d1\u5a18\u8d70 \u5462\uff1f\u201d \u3000\u3000\u5929\u7985\u5927\u5e08\u9053\uff1a\u201c\u8001\u8872\u770b\u82d7\u59d1\u5a18\u6167\u6839\u5929\u751f\uff0c\u8d44\u8d28\u4e0d\u9519\uff0c\u60f3\u66ff\u5ce8\u5d4b\u638c\u95e8\u6536\u5979\u4e3a\u8bb0\u540d\u5f1f\u5b50\uff0c\u66f4 \u4f55\u51b5\u5979\u82e5\u968f\u5c0f\u5144\u5f1f\u56de\u5317\u5cb3\u8ff7\u8e2a\u8c37\u53bb\uff0c\u5f88\u53ef\u80fd\u5403\u970d\u5143\u4f3d\u7684\u4e8f\uff0c\u5230\u90a3\u65f6\u5019\u53ea\u6015\u5c0f\u5144\u5f1f\u4e5f\u96be\u4ee5\u4fdd\u62a4 \u5979\u7684\u5b89\u5168\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u867d\u7136\u5185\u5fc3\u5927\u4e0d\u60c5\u613f\uff0c\u4f46\u82d7\u7d20\u82d3\u7684\u4e00\u6761\u547d\uff0c\u5374\u53c8\u662f\u5929\u7985\u5927\u5e08\u6551\u4e0b\u7684\uff0c\u4ed6\u8981\u5e26\u5979\u8d70\uff0c \u9ea6\u5c0f\u660e\u603b\u662f\u4e0d\u4fbf\u52a0\u4ee5\u963b\u62e6\u7684\uff0e \u3000\u3000\u9ea6\u5c0f\u660e\u53ea\u597d\u95ee\u82d7\u7d20\u82d3\u9053\uff1a\u201c\u59d1\u5a18\u53ef\u613f\u968f\u8001\u7985\u5e08\u53bb\uff1f\u201d \u3000\u3000\u82d7\u7d20\u82d3\u4f3c\u662f\u4e5f\u5927\u611f\u4e3a\u96be\uff0c\u5979\u770b\u770b\u9ea6\u5c0f\u660e\uff0c\u518d\u770b\u770b\u5929\u7985\u5927\u5e08\uff0c\u3000\u4e00\u65f6\u4e4b\u95f4\uff0c\u771f\u4e0d\u77e5\u5982\u4f55\u56de \u7b54\u624d\u597d\uff0e \u3000\u3000\u53ea\u542c\u9752\u9633\u9053\u957f\u6717\u58f0\u9053\uff1a\u201c\u59d1\u5a18\u8fd8\u4e0d\u5feb\u5feb\u53e9\u8c22\uff0c\u5929\u7985\u5927\u5e08\u662f\u4e00\u6d3e\u5927\u5b97\u5e08\u8eab\u4efd\uff0e\u4ed6\u80af\u66ff\u4f60\u63a8 \u8350\u540d\u5e08\uff0c\u8fd9\u7b49\u673a\u4f1a\uff0c\u5c82\u53ef\u9519\u8fc7\uff01\u201d \u3000\u3000\u82d7\u7d20\u82d3\u7a0d\u7a0d\u72b9\u8c6b\u4e86\u4e00\u4e0b\u3002 \u3000\u3000\u5979\u7acb\u5373\u53e3\u79f0\u5927\u5e08\uff0c\u5012\u8eab\u76c8\u76c8\u62dc\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u5929\u7985\u5927\u5e08\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u8d77\u6765\u968f\u8001\u8872\u8d70\u5427\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u5185\u5fc3\u4e00\u9635\u6005\u60d8\u7a7a\u865a\u4e4b\u540e\uff0c\u8fde\u5fd9\u5411\u5929\u7985\u5927\u5e08\u62b1\u62f3\u2014\u793c\u9053\uff1a\u201c\u8001\u7985\u5e08\uff0c\u665a\u8f88\u5148\u8d70\u4e00\u6b65 \u4e86\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u2014\u53e3\u6c14\u5954\u51fa\u4e86\u4e1b\u6797\u3002 \u3000\u3000\u4ed6\u53c8\u5954\u884c\u4e86\u56db\u4e94\u91cc\u8def\u624d\u653e\u7f13\u811a\u6b65\u3002 \u3000\u3000\u4ed6\u5185\u5fc3\u786e\u662f\u6709\u79cd\u96be\u4ee5\u540d\u72b6\u7684\u5931\u843d\u611f\u3002 \u3000\u3000\u4f3c\u4e4e\u8fde\u4ed6\u81ea\u5df1\u90fd\u4e0d\u6e05\u695a\uff0c\u4ed6\u5df2\u6df1\u6df1\u7231\u4e0a\u4e86\u82d7\u7d20\u82d3\uff0c\u9ea6\u5c0f\u660e\u5c0f\u5c0f\u5e74\u7eaa\uff0c\u5c45\u7136\u4f1a\u4e3a\u60c5\u6240\u56f0 \u554a\u3002 \u3000\u3000\u8fd9\u5728\u4ece\u524d\uff0e\u662f\u4ed6\u8fde\u505a\u68a6\u4e5f\u60f3\u4e0d\u5229\u7684\u4e8b\u3002 \u3000\u3000\u8bf4\u8d77\u6765\uff0c\u8fd9\u8be5\u662f\u4e00\u4ef6\u5f88\u81ea\u7136\u7684\u4e8b\u3002 \u3000\u3000\u56e0\u4e3a\u5728\u6b64\u4ee5\u524d\uff0c\u9ea6\u5c0f\u660e\u5185\u5fc3\u4e00\u76f4\u5bf9\u82d7\u7d22\u5170\u6709\u79cd\u83ab\u540d\u7684\u7231\u6155\uff0c\u53ea\u56e0\u4e3a\u4e24\u4eba\u5e74\u9f84\u76f8\u5dee\u751a\u591a\uff0c \u4e0d\u6562\u8868\u660e\u81ea\u5df1\u800c\u5df2\u3002 \u3000\u3000\u5982\u4eca\u82d7\u7d20\u82d3\u65e2\u662f\u82d7\u7d20\u5170\u7684\u59b9\u59b9\uff0e\u6a21\u6837\u4e5f\u9887\u4e3a\u8fd1\u4f3c\uff0c\u867d\u7136\u5bf9\u65b9\u7684\u5e74\u7eaa\u4ecd\u6bd4\u81ea\u5df1\u5927\uff0c\u5374\u4e5f\u5927 \u4e0d\u4e86\u51e0\u5c81\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u53c8\u600e\u80fd\u4e0d\u5bf9\u82d7\u7d20\u82d3\u4e00\u89c1\u949f\u60c5\u5462\uff1f \u3000\u3000\u504f\u504f\u5929\u7985\u5927\u5e08\u5374\u53c8\u628a\u82d7\u7d20\u82d3\u5e26\u8d70\u4e86\uff0c\u8fd9\u662f\u591a\u4e48\u715e\u98ce\u666f\u7684\u4e8b\uff01 \u3000\u3000\u9ea6\u5c0f\u660e\u4e00\u8def\u6025\u6025\u5954\u884c\u3002 \u3000\u3000\u4e24\u65e5\u540e\uff0c\u4ed6\u4fbf\u5df2\u5230\u8fbe\u592a\u539f\u3002 \u3000\u3000\u592a\u539f\u53c8\u540d\u9633\u66f2\uff0c\u56e0\u9ec4\u6cb3\u5341\u91cc\u4e00\u66f2\uff0c\u53c8\u5c45\u5176\u9633\u800c\u5f97\u540d\u3002 \u3000\u3000\u4f4d\u4e8e\u6c7e\u6cb3\u4e0a\u6e38\uff0e\u4e94\u4ee3\u65f6\u5218\u77e5\u8fdc\u66fe\u5728\u6b64\u79f0\u5e1d\uff0c\u968b\u672b\u674e\u6e0a\u4e5f\u662f\u8d77\u5175\u4e8e\u6b64\uff0c\u56e0\u6b64\uff0c\u800c\u5f97\u5230\u4e86\u5929 \u4e0b\u3002 \u3000\u3000\u56e0\u4e4b\uff0c\u592a\u539f\u5728\u5c71\u897f\u79f0\u5f97\u4e0a\u662f\u7b2c\u4e00\u540d\u57ce\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u6765\u5230\u4e86\u592a\u539f\uff0c\u5929\u8272\u5df2\u665a\u3002 \u3000\u3000\u4ed6\u51b3\u5b9a\u5728\u57ce\u5185\u6295\u5bbf\u6682\u4f4f\u4e00\u665a\u3002 \u3000\u3000\u4ed6\u4f4f\u7684\u8fd9\u5bb6\u5ba2\u6808\uff0c\u5c31\u53eb\u9633\u66f2\u8001\u6808\uff0c\u95e8\u9762\u6781\u5927\uff0e \u3000\u3000\u8db3\u6709\u51e0\u5341\u95f4\u5ba2\u623f\uff0c\u53e6\u6709\u4e24\u5904\u6e05\u5e7d\u8de8\u9662\uff0c\u4e13\u4f9b\u5bcc\u5546\u5de8\u8d3e\u4f4f\u5bbf\u4e4b\u7528\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u56e0\u6240\u5e26\u76d8\u7f20\u751a\u591a\uff0c\u53c8\u559c\u6b22\u6446\u6d3e\u5934\uff0c\u4e5f\u4f4f\u5728\u8de8\u9662\u4e0a\u623f\u5185\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u521a\u521a\u8fdb\u5165\u4e0a\u623f\u4e0d\u4e45\uff0c\u4ed6\u4fbf\u542c\u5230\u5929\u4e95\u4e2d\uff0c\u6709\u4e2a\u6e05\u8106\u7684\u5973\u5b50\u58f0\u97f3\uff0c\u6b63\u548c\u5e97\u5c0f\u4e8c\u5728\u8bb2 \u8bdd\u3002 \u3000\u3000\u8fd9\u58f0\u97f3\u662f\u591a\u4e48\u719f\u6089\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u6025\u6025\u7531\u7a97\u5185\u5411\u5916\u671b\u53bb\u3002 \u3000\u3000\u987f\u65f6\u4f7f\u9ea6\u5c0f\u660e\u4e00\u9635\u60ca\u559c\uff01 \u3000\u3000\u8fd9\u8bf4\u8bdd\u7684\u5973\u5b50\uff0c\u8d6b\u7136\u6b63\u662f\u4ed6\u4e24\u65e5\u6765\u9b42\u7275\u68a6\u8426\u7684\u82d7\u7d20\u82d3\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u60c5\u4e0d\u81ea\u7981\uff0c\u7531\u5ba4\u5185\u51b2\u4e86\u51fa\u6765\u3002 \u3000\u3000\u4ed6\u5927\u53eb\u9053\uff1a\u201c\u82d7\u59d1\u5a18\uff01\u4f60\u600e\u4e48\u6765\u4e86\uff0c\u6211\u5728\u8fd9\u91cc\uff01\u201d \u3000\u3000\u82d7\u7d20\u82d3\u4e5f\u987f\u611f\u4e00\u9635\u60ca\u559c\uff0c\u5fd9\u9053\uff1a\u201c\u592a\u597d\u4e86\uff0c\u6211\u7ec8\u4e8e\u8ffd\u4e0a\u4f60\u4e86\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u90a3\u91cc\u8fd8\u7b49\u5f97\uff0c\u6025\u6025\u62c9\u82d7\u7d20\u82d3\u7684\u624b\u9053\uff1a\u201c\u5feb\u8ddf\u6211\u5230\u5c4b\u91cc\u53bb\uff0c\u54b1\u4eec\u5230\u91cc\u9762\u518d\u8bf4\u5427\uff01\u201d \u3000\u3000\u8fdb\u5165\u623f\u5185\uff0c\u9ea6\u5c0f\u660e\u8fde\u5fd9\u6c8f\u4e86\u4e00\u76c5\u8336\u9012\u4e0a\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u4e00\u5411\u6840\u50b2\u96be\u8bad\uff0c\u4ed6\u4ece\u6765\u4e0d\u628a\u4efb\u4f55\u4eba\u653e\u5728\u773c\u91cc\u3002 \u3000\u3000\u5982\u4eca\u4ed6\u7adf\u7136\u5bf9\u82d7\u7d20\u82d3\u732e\u6bb7\u52e4\uff0e \u3000\u3000\u82d7\u7d20\u82d3\u63a5\u8fc7\u8336\u9053\uff1a\u201c\u516c\u5b50\u5c31\u5728\u8fd9\u95f4\u4e0a\u623f\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u5feb\u522b\u53eb\u6211\u516c\u5b50\uff0c\u6539\u4e2a\u79f0\u547c\u597d\u4e0d\u597d\uff1f\u201d \u3000\u3000\u82d7\u7d20\u82d3\u7f9e\u7b54\u7b54\u7684\u9053\uff1a\u201c\u90a3\u6211\u53eb\u4f60\u4ec0\u4e48\u5462\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u5c31\u53eb\u6211\u7684\u540d\u5b57\u597d\u5566\uff0c\u6211\u4ee5\u540e\u5c31\u53eb\u4f60\u82d7\u59d0\u59d0\u3002\u201d \u3000\u3000\u82d7\u7d20\u82d3\u4e0d\u89c9\u8138\u4e0a\u7ea2\u6655\u98de\u988a\uff0c\u4f4e\u4e0b\u5934\u9053\uff1a\u201c\u8fd9\u6837\u597d\u5417\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u597d\uff0c\u8fd9\u6837\u54b1\u4eec\u5c31\u4f1a\u663e\u5f97\u4eb2\u2026\u2026\u201d \u3000\u3000\u201c\u4eb2\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u672c\u6765\u8981\u8bf4\uff1a\u201c\u5c31\u4f1a\u663e\u5f97\u4eb2\u70ed\u4e9b\u201d\uff0c\u53ea\u597d\u6539\u4e86\u4e00\u4e2a\u5b57\uff1a\u201c\u4eb2\u2026\u2026\u4eb2\u5207\u4e9b\uff01\u4f60\u6bd4\u6211\u5e74 \u957f\uff0c\u6211\u4ee5\u540e\u5c31\u53eb\u4f60\u82d7\u86c6\u59d0\u5427\u3002\u201d \u3000\u3000\u82d7\u7d22\u82d3\u9053\uff1a\u201c\u8fd8\u662f\u53eb\u6211\u82d7\u59d1\u5a18\u597d\u5566\uff01\u867d\u7136\u6211\u7684\u5e74\u7eaa\u53ef\u80fd\u6bd4\u4f60\u5927\u4e9b\uff0c\u53ef\u4e5f\u8fd8\u4e0d\u81f3\u4eb2\u5207\u5230\u505a \u59d0\u59d0\u7684\u5730\u6b65\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u8fd9\u65f6\u4e5f\u60f3\u5230\uff0c\u4e0d\u80fd\u628a\u5bf9\u65b9\u79f0\u547c\u5f97\u8fc7\u5206\u4eb2\u70ed\uff0e\u4ee5\u514d\u56de\u5230\u5317\u5cb3\u8ff7\u8e2a\u8c37\u540e\uff0c\u88ab\u4eba\u62ff\u6765\u505a \u53d6\u7b11\u7684\u8bdd\u67c4\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u773c\u89c1\u82d7\u7d20\u82d3\u628a\u4e00\u86ca\u8336\u559d\u5b8c\uff0c\u624d\u8feb\u4e0d\u53ca\u5f85\u7684\u9053\uff1a\u201c\u4f60\u8fd8\u6ca1\u8bf4\uff0c\u662f\u600e\u4e48\u6765\u5230\u4e86\u592a\u539f \u7684\uff0c\u5929\u7985\u8001\u7985\u5e08\u4ed6\u4eec\u5462\uff0c\u83ab\u975e\u4e5f\u4f4f\u8fdb\u4e86\u8fd9\u5bb6\u5ba2\u6808\uff1f\u201d \u3000\u3000\u82d7\u7d20\u82d3\u9053\uff1a\u201c\u5927\u5e08\u548c\u9752\u9633\u9053\u957f\u5728\u4eca\u5929\u4e0a\u5348\uff0c\u4fbf\u5728\u8def\u4e0a\u548c\u6211\u5206\u624b\u4e86\u3002\u201d \u3000\u3000\u201c\u662f\u5929\u7985\u5927\u5e08\u653e\u4f60\u8d70\u7684\uff1f\u201d \u3000\u3000\u201c\u5927\u5e08\u82e5\u4e0d\u653e\u6211\uff0e\u6211\u600e\u6562\u968f\u4fbf\u79bb\u5f00\u4ed6\u3002\u201d \u3000\u3000\u201c\u4ed6\u4e0d\u662f\u8981\u628a\u4f60\u5e26\u5230\u5ce8\u5d4b\u5417\uff1f\u201d \u3000\u3000\u201c\u6211\u4e5f\u4e0d\u77e5\u9053\u4ed6\u8001\u4eba\u5bb6\u4e3a\u4ec0\u4e48\u6539\u53d8\u4e3b\u610f\uff0c\u6211\u548c\u5927\u5e08\u5206\u624b\u540e\uff0c\u4fbf\u4e00\u8def\u6025\u8d76\uff0c\u5e0c\u671b\u80fd\u8ffd\u5230 \u4f60\uff0e\u8fd8\u597d\uff0c\u7ec8\u4e8e\u5728\u5ba2\u6808\u9047\u4e0a\u4e86\u4f60\u3002\u201d \u3000\u3000\u201c\u5929\u7985\u5927\u5e08\u548c\u9752\u9633\u9053\u957f\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u201d \u3000\u3000\u201c\u4ed6\u4eec\u6ca1\u8bb2\uff0c\u6211\u4e5f\u4e0d\u65b9\u4fbf\u95ee\uff0e\u770b\u6837\u5b50\u4ed6\u4eec\u53ef\u80fd\u6709\u4ec0\u4e48\u6025\u4e8b\uff0c\u4e5f\u8bb8\u4e0d\u65b9\u4fbf\u5e26\u6211\u540c\u884c\uff0c\u6240\u4ee5 \u624d\u4f1a\u4e2d\u9053\u6539\u53d8\u4e86\u4e3b\u610f\u3002\u201d \u3000\u3000\u8fd9\u65f6\u5e97\u5c0f\u4e8c\u53c8\u63d0\u4e86\u4e00\u58f6\u8336\u8fdb\u6765\uff0c\u9ea6\u5c0f\u660e\u4ea4\u5f85\u518d\u4e3a\u82d7\u7d20\u82d3\u51c6\u5907\u95f4\u4e0a\u623f\u3002 \u3000\u3000\u5e97\u5c0f\u4e8c\u9053\uff1a\u201c\u8fd9\u4f4d\u59d1\u5a18\u5b9a\u5f97\u6b63\u597d\uff0c\u5ba2\u623f\u521a\u597d\u53ea\u5269\u4e0b\u4e00\u95f4\u4e86\uff0e\u82e5\u518d\u6765\u665a\u4e9b\uff0c\u5176\u4ed6\u7684\u5ba2\u4eba\u4f4f \u8fdb\uff0c\u59d1\u5a18\u5c31\u53ea\u597d\u5230\u522b\u5bb6\u5ba2\u6808\u53bb\u5566\uff01\u201d \u3000\u3000\u7a7a\u4e0b\u7684\u4e00\u95f4\u4e0a\u623f\uff0c\u6b63\u597d\u5728\u9ea6\u5c0f\u660e\u7684\u623f\u95f4\u9694\u58c1\uff0c\u82d7\u7d20\u82d3\u8fdb\u53bb\u4e4b\u540e\uff0c\u9ea6\u5c0f\u660e\u60f3\u5230\u5979\u4e00\u8def\u8f9b \u82e6\uff0e\u9700\u8981\u4e0a\u5e8a\u4f11\u606f\uff0c\u4fbf\u4e0d\u518d\u6253\u6270\uff0c\u597d\u5728\u6765\u65e5\u65b9\u957f\uff0c\u4eca\u540e\u89c1\u9762\u673a\u4f1a\u591a\u5f97\u5f88\u3002 \u5367\u9f99\u751f\u300a\u5999\u7edd\u5929\u9999\u300b \u7b2c \u4e03 \u56de\u3000\u591c\u63a2\u5996\u5973 \u3000\u3000\u65f6\u95f4\u624d\u665a\u996d\u521a\u8fc7\uff0c\u9ea6\u5c0f\u660e\u4e0d\u60f3\u592a\u65e9\u5c31\u5bdd\uff0c\u4fbf\u5728\u8de8\u9662\u4e2d\u6563\u8d77\u6b65\u6765\uff0e \u3000\u3000\u8fd9\u5904\u8de8\u9662\u5341\u5206\u5e7d\u9759\uff0c\u5899\u8fb9\u82b1\u5703\u4e2d\u8fd8\u79cd\u7740\u4e0d\u5c11\u82b1\u8349\u3002 \u3000\u3000\u5fae\u98ce\u5f90\u6765\uff0c\u82b1\u82b3\u6251\u9f3b\uff0e \u3000\u3000\u5982\u679c\u80fd\u6709\u4e09\u4e94\u597d\u53cb\u5728\u5ead\u4e2d\u5c0f\u914c\u2014\u756a\uff0c\u5012\u4e0d\u5931\u662f\u6869\u8d4f\u5fc3\u4e50\u4e8b\uff01 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u5ffd\u542c\u524d\u9762\u67dc\u53f0\u4e0a\uff0e\u6709\u4eba\u5435\u4e86\u8d77\u6765\u3002 \u3000\u3000\u800c\u4e14\uff0c\u5176\u4e2d\u6709\u4e00\u4eba\u55d3\u95e8\u5947\u5927\u3002\u53ea\u542c\u90a3\u4eba\u9053\uff1a\u201c\u4ffa\u5165\u4f60\u5976\u5976\u7684\uff0c\u8001\u5b50\u4eec\u8f9b\u8f9b\u82e6\u82e6\u4e00\u5927\u65e9\u8d76 \u8def\u8d76\u5230\u73b0\u5728\uff0c\u597d\u4e0d\u5bb9\u6613\u8fdb\u4e86\u57ce\uff0c\u627e\u5230\u4f60\u4eec\u8fd9\u5bb6\u5ba2\u6808\uff0c\u5374\u53c8\u8bf4\u6ca1\u6709\u4f4f\u5904\uff0e\u65e2\u7136\u6ca1\u6709\u4f4f\u5904\uff0c\u4f60\u4eec \u4ed6\u5976\u5976\u7684\u8fd8\u5f00\u7684\u4ec0\u4e48\u5ba2\u6808\uff01\u201d \u3000\u3000\u53e6\u2014\u4e2a\u966a\u7740\u5c0f\u5fc3\u58f0\u97f3\u9053\uff1a\u201c\u5927\u7237\uff0c\u5c0f\u53f7\u7684\u5ba2\u623f\uff0c\u786e\u5b9e\u4f4f\u6ee1\u4e86\uff0e\u201d \u3000\u3000\u5148\u524d\u8bf4\u8bdd\u7684\u90a3\u5927\u55d3\u95e8\u9053\uff1a\u201c\u53bb\u4f60\u5976\u5976\u7684\uff0c\u628a\u638c\u67dc\u7684\u627e\u6765\uff0c\u4ed6\u5982\u679c\u518d\u8bf4\u6ca1\u4e0a\u623f\uff0c\u4ffa\u5c31\u5bb0\u4e86 \u4ed6\uff01\u201d \u3000\u3000\u63a5\u7740\u53c8\u2014\u4eba\u7684\u58f0\u97f3\u9053\uff1a\u201c\u8001\u4e09\uff0c\u521a\u624d\u4f19\u8ba1\u8bf4\u8fc7\u6709\u4e24\u4e2a\u8de8\u9662\uff0c\u54b1\u4eec\u4e14\u5230\u8de8\u9662\u770b\u770b\uff01\u201d \u3000\u3000\u90a3\u5927\u55d3\u95e8\u9053\uff1a\u201c\u4f19\u8ba1\uff01\u7ed9\u8001\u5b50\u4eec\u5e26\u8def\uff01\u201d \u3000\u3000\u4f19\u8ba1\u98a4\u7740\u58f0\u97f3\u9053\uff1a\u201c\u4e0d\u7792\u4e09\u4f4d\u5927\u7237\uff0c\u8de8\u9662\u4e5f\u5ba2\u6ee1\u4e86\uff01\u201d \u3000\u3000\u90a3\u5927\u55d3\u95e8\u543c\u9053\uff1a\u201c\u6ee1\u4f60\u5a18\u4e2a\u718a\uff0c\u4f60\u53ea\u7ba1\u7ed9\u8001\u5b50\u5e26\u8def\uff01\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u9ea6\u5c0f\u660e\u65e9\u542c\u51fa\u5927\u55d3\u95e8\u7684\u53e3\u97f3\uff0c\u5341\u5206\u8033\u719f\u3002 \u3000\u3000\u53ea\u662f\u4e00\u65f6\u4e4b\u95f4\uff0c\u4ed6\u53c8\u60f3\u4e0d\u8d77\u6765\u662f\u8c01\uff1f \u3000\u3000\u521a\u8981\u5230\u67dc\u53f0\u770b\u770b\uff0c\u5bf9\u65b9\u5374\u5df2\u8fdb\u4e86\u8de8\u9662\uff0e \u3000\u3000\u6765\u4eba\u662f\u4e09\u540d\u8eab\u4f69\u5175\u5203\u7684\u5927\u6c49\uff0e\u4e00\u540d\u5e97\u5c0f\u4e8c\u5728\u65c1\u606d\u606d\u656c\u656c\u7684\u966a\u7740\u3002 \u3000\u3000\u8fd9\u4e09\u4e2a\u90fd\u56db\u65ec\u4e0a\u4e0b\u5e74\u7eaa\u3002 \u3000\u3000\u8d70\u5728\u6700\u524d\u9762\u7684\u4e00\u4e2a\uff0c\u8eab\u6750\u77ee\u80d6\uff0c\u6b65\u5c65\u6c89\u7a33\u3002 \u3000\u3000\u5728\u77ee\u80d6\u7537\u5b50\u7684\u8eab\u540e\uff0c\u5219\u662f\u4e2a\u9aa8\u7626\u5982\u67f4\uff0c\u72b9\u5982\u7af9\u6746\u822c\u7684\u6c49\u5b50\u3002 \u3000\u3000\u552f\u6709\u6700\u540e\u4e00\u4e2a\uff0c\u957f\u76f8\u6700\u7a81\u51fa\uff0c\u6b64\u4eba\u8eab\u9ad8\u516b\u5c3a\uff0c\u8138\u8272\u8d64\u7ea2\uff0c\u77ed\u9aed\u5982\u621f\uff0c\u6839\u6839\u89c1\u8089\uff0e\u58ee\u5f97\u6709 \u5982\u4e00\u5ea7\u94c1\u5854\u3002 \u3000\u3000\u5728\u4ed6\u80cc\u540e\u8fd8\u659c\u63d2\u4e86\u4e00\u67c4\u5f00\u5c71\u5200\uff0c\u5200\u9762\u8db3\u6709\u534a\u5c3a\u5bbd\uff0c\u795e\u5a01\u51db\u51db\uff0c\u770b\u8d77\u6765\u5341\u5206\u5413\u4eba\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u7acb\u523b\u8ba4\u51fa\uff0e\u8fd9\u4e09\u4e2a\u8d6b\u7136\u662f\u540d\u632f\u9c81\u4e1c\u7684\u5d02\u5c71\u4e09\u96c4\uff0c\u8001\u5927\u53eb\u7b11\u9762\u4f5b\u9c8d\u8d85\uff0c\u8001\u4e8c\u53eb\u9b3c\u8bf8 \u845b\u6d2a\u6cfd\uff0c\u8001\u4e09\u53eb\u52c7\u91d1\u521a\u738b\u5927\u5eb7\u3002 \u3000\u3000\u65b9\u624d\u90a3\u4e2a\u5927\u55d3\u95e8\u9a82\u4eba\u7684\uff0c\u6b63\u662f\u8001\u4e09\u52c7\u91d1\u521a\u738b\u5927\u5eb7\uff0e \u3000\u3000\u539f\u6765\u5f53\u5e74\u4e89\u593a\u5929\u4e0b\u7eff\u6797\u76df\u4e3b\u65f6\uff0c\u5d02\u5c71\u4e09\u96c4\u4e5f\u53c2\u52a0\u4e86\u5317\u5cb3\u6052\u5c71\u5927\u4f1a\uff0e \u3000\u3000\u8fd9\u4e09\u4eba\u867d\u7136\u4e5f\u90fd\u51fa\u8eab\u7eff\u6797\u8349\u83bd\uff0e\u5374\u9887\u80fd\u884c\u4fa0\u4ed7\u4e49\u3002 \u3000\u3000\u56e0\u4e4b\uff0c\u4ed6\u4eec\u867d\u548c\u80e1\u67cf\u9f84\u7d20\u4e0d\u76f8\u8bc6\uff0c\u4f46\u5728\u80e1\u67cf\u9f84\u767b\u4e0a\u7eff\u6797\u76df\u4e3b\u4e4b\u540e\uff0c\u5728\u80e1\u67cf\u9f84\u7684\u4eba\u683c\u611f\u53ec \u4e4b\u4e0b\uff0c\u4ed6\u4eec\u5c45\u7136\u80af\u8877\u5fc3\u62e5\u6234\uff0c\u6210\u4e3a\u80e1\u67cf\u9f84\u7684\u5fe0\u8d1e\u4e0d\u4e8c\u7684\u5fc3\u8179\u5c5e\u4e0b\u3002 \u3000\u3000\u540e\u6765\u80e1\u67cf\u9f84\u6b7b\u540e\uff0c\u4ed6\u4eec\u518d\u4e0e\u949f\u4e00\u8c6a\u7b49\u4eba\u5171\u540c\u62e5\u6234\u8c37\u5bd2\u9999\u7ee7\u4efb\u76df\u4e3b\uff0e \u3000\u3000\u76f4\u5230\u8c37\u5bd2\u9999\u4e3a\u62a5\u592b\u4ec7\uff0c\u5fc5\u987b\u79bb\u5f00\u8ff7\u8e2a\u8c37\uff0c\u9063\u6563\u624b\u4e0b\u4e00\u90e8\u5206\u65f6\uff0c\u4ed6\u4eec\u5d02\u5c71\u4e09\u96c4\u624d\u91cd\u8fd4\u5d02 \u5c71\u3002 \u3000\u3000\u8fd9\u4e00\u6643\uff0c\u4e0d\u89c9\u5c31\u5c06\u8fd1\u4e09\u5e74\u65f6\u95f4\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u5f53\u5e74\u662f\u968f\u80e1\u67cf\u9f84\u8fdb\u5165\u8ff7\u8e2a\u8c37\u7684\uff0e\u6240\u4ee5\u4ed6\u548c\u5d02\u5c71\u4e09\u96c4\u65e9\u5c31\u8ba4\u8bc6\uff0c\u4e0d\u8fc7\u5f53\u65f6\u6781\u5c11\u4ea4 \u5f80\u3002 \u3000\u3000\u800c\u4e14\uff0c\u76f8\u5904\u7684\u65f6\u95f4\u4e5f\u5e76\u4e0d\u957f\u3002 \u3000\u3000\u4e0d\u7b49\u9ea6\u5c0f\u660e\u5f00\u53e3\uff0e\u52c7\u91d1\u521a\u738b\u5927\u5eb7\u65e9\u5df2\u8d8a\u8fc7\u8001\u5927\u7b11\u9762\u4f5b\u9c8d\u8d85\u548c\u8001\u4e8c\u9b3c\u8bf8\u845b\u6d2a\u6cfd\uff0c\u8d8b\u524d\u5927\u53eb \u9053\uff1a\u201c\u597d\u54c7\uff0c\u8fd9\u4e0d\u662f\u9ea6\u8001\u5f1f\u4e48\uff0c\u4f60\u600e\u4e48\u4e5f\u5230\u592a\u539f\u6765\u4e86\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u5411\u4e09\u4eba\u62f1\u4e86\u62f1\u624b\u9053\uff1a\u201c\u4e09\u4f4d\u8be5\u5148\u8bf4\u8bf4\uff0e\u4e3a\u4ec0\u4e48\u5343\u91cc\u8fe2\u8fe2\u7684\uff0c\u8ba9\u4f60\u4eec\u7531\u5d02\u5c71\u8d76\u5230\u8fd9 \u91cc\u6765\uff1f\u201d \u3000\u3000\u738b\u5927\u5eb7\u4e00\u5411\u8bdd\u591a\uff0c\u4eba\u53c8\u723d\u5feb\uff0c\u62a2\u7740\u8bf4\u9053\uff1a\u201c\u542c\u8bf4\u8c37\u76df\u4e3b\u4e5f\u6b7b\u4e86\uff0c\u8fd9\u4e8b\u5230\u5e95\u662f\u771f\u7684\uff0c\u8fd8\u662f \u5047\u7684\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u5f53\u7136\u662f\u771f\u7684\u3002\u8c37\u76df\u4e3b\u6b7b\u65f6\uff0c\u5f53\u65f6\u5c0f\u5f1f\u5728\u65c1\u4eb2\u773c\u6240\u89c1\u3002\u201d \u3000\u3000\u738b\u5927\u5eb7\u4e00\u8dfa\u811a\u9053\uff1a\u201c\u8001\u5929\u771f\u662f\u6ca1\u957f\u773c\uff0c\u8ba9\u80e1\u76df\u4e3b\u6b7b\u4e86\uff0c\u5c31\u662f\u5927\u5927\u4e0d\u8be5\uff0c\u73b0\u5728\u53c8\u8ba9\u8c37\u76df\u4e3b \u6b7b\u4e86\uff0c\u8c37\u76df\u4e3b\u7a76\u7adf\u600e\u4e48\u6b7b\u7684\uff1f\u51f6\u624b\u662f\u8c01\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u795e\u8272\u9eef\u7136\u9053\uff1a\u201c\u5f53\u65f6\u662f\u4e00\u573a\u6df7\u6218\uff0c\u8c37\u76df\u4e3b\u4e5f\u6740\u4e86\u4e0d\u5c11\u4eba\uff0c\u5c11\u6797\u7684\u5929\u660e\u548c\u5c1a\uff0c\u6b66\u5f53 \u7684\u7d2b\u9633\u3001\u91d1\u9633\u4e24\u4e2a\u725b\u9f3b\u5b50\uff0c\u90fd\u6b7b\u5728\u5979\u624b\u4e2d\uff0c\u5979\u81ea\u5df1\u4e5f\u8eab\u53d7\u91cd\u4f24\uff0c\u5979\u89c1\u5927\u4ec7\u5df2\u62a5\uff0c\u81ea\u5df1\u53c8\u4f24 \u91cd\uff0c\u6700\u540e\u5c31\u81ea\u523a\u80f8\u819b\u800c\u6b7b\u3002\u201d \u3000\u3000\u738b\u5927\u5eb7\u53f3\u62f3\u5728\u5de6\u638c\u4e0a\u731b\u5730\u4e00\u6376\u9053\uff1a\u201c\u53ef\u60dc\u5440\u53ef\u60dc\uff01\u8fde\u8c37\u76df\u4e3b\u8fd9\u6837\u7684\u597d\u4eba\u4e5f\u6b7b\u4e86\uff01\u54b1\u4eec\u6d3b \u7740\u8fd8\u6709\u5565\u610f\u601d\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u738b\u5f53\u5bb6\u7684\u8fd8\u6ca1\u8bf4\u660e\uff0c\u4e3a\u4ec0\u4e48\u5230\u592a\u539f\u6765\u7684\uff1f\u201d \u3000\u3000\u738b\u5927\u5eb7\u9053\uff1a\u201c\u4ffa\u4eec\u5f1f\u5144\u4e09\u4e2a\uff0e\u542c\u8bf4\u8c37\u76df\u4e3b\u6b7b\u4e86\u7684\u6d88\u606f\uff0e\u4fbf\u51b3\u5b9a\u8981\u66ff\u5979\u62a5\u4ec7\uff0c\u5f53\u5373\u7531\u5d02\u5c71 \u52a8\u8eab\uff0c\u5f80\u5317\u5cb3\u8ff7\u8e2a\u8c37\u8d76\u6765\u3002 \u3000\u3000\u4eca\u5929\u521a\u597d\u6765\u5230\u592a\u539f\uff0e\u5c31\u8fd9\u4e48\u5de7\uff0c\u5728\u5ba2\u6808\u9047\u4e0a\u4e86\u4f60\u9ea6\u8001\u5f1f\u3002 \u3000\u3000\u9ea6\u8001\u5f1f\uff0c\u8bb0\u5f97\u4e24\u5e74\u524d\u4ffa\u4eec\u79bb\u5f00\u8ff7\u8e2a\u8c37\u65f6\uff0c\u4f60\u8fd8\u662f\u4e2a\u5341\u4e94\u516d\u5c81\u7684\u5b69\u5b50\uff0c\u4e24\u5e74\u4e0d\u89c1\uff0c\u5df2\u957f\u6210 \u5927\u4eba\u5566\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u7b11\u9053\uff1a\u201c\u4eba\u5f53\u7136\u4f1a\u957f\u5927\u7684\uff0e\u201d \u3000\u3000\u738b\u5927\u5eb7\u9053\uff1a\u201c\u8bb0\u5f97\u5f53\u65f6\u4f60\u7684\u4e00\u8eab\u6b66\u529f\uff0c\u5c31\u5f88\u4e86\u4e0d\u8d77\uff0c\u73b0\u5728\u8fc7\u4e86\u4e24\u5e74\u591a\uff0c\u4f60\u7684\u6b66\u529f\u2014\u5b9a\u66f4 \u4e86\u4e0d\u8d77\u5566\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u7b11\u4e86\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u518d\u9ad8\u4e5f\u9ad8\u4e0d\u8fc7\u738b\u5f53\u5bb6\u7684\uff0c\u4f60\u521a\u624d\u5728\u67dc\u53f0\u90a3\u8fb9\uff0e\u8ddf\u4ed6\u4eec\u5435\u4ec0 \u4e48\uff1f\u201d \u3000\u3000\u738b\u5927\u5eb7\u4f3c\u4e4e\u53c8\u6765\u4e86\u6c14\uff0c\u4e24\u53ea\u725b\u773c\u4e00\u77aa\u9053\uff1a\u201c\u4ed6\u5976\u5976\u7684\uff0c\u4ffa\u4eec\u4e09\u4e2a\u8f9b\u8f9b\u82e6\u82e6\u7684\u8d76\u4e86\u4e00\u5929 \u8def\uff0c\u8c01\u77e5\u6765\u5230\u8fd9\u5bb6\u9e1f\u5ba2\u6808\uff0c\u4ed6\u4eec\u7adf\u8bf4\u4e0a\u623f\u5df2\u6ee1\uff0c\u4f60\u8bf4\u6c14\u4eba\u4e0d\u6c14\u4eba\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u90a3\u5c31\u4e0d\u59a8\u518d\u6362\u4e0a\u4e00\u5bb6\uff0c\u592a\u539f\u662f\u4e2a\u5927\u57ce\uff0e\u5ba2\u6808\u2014\u5b9a\u4e0d\u5c11\u3002\u201d \u3000\u3000\u738b\u5927\u5eb7\u54fc\u4e86\u58f0\u9053\uff1a\u201c\u5176\u5b9e\u5148\u524d\u4ffa\u4eec\u5df2\u7ecf\u53bb\u8fc7\u4e86\u4e24\u5bb6\uff0e\u4e5f\u662f\u5ba2\u6ee1\u4e86\uff0c\u73b0\u5728\u4ffa\u4eec\u6765\u5230\u8fd9\u4e00 \u5bb6\uff0c\u975e\u8981\u4ed6\u4eec\u60f3\u60f3\u529e\u6cd5\uff0c\u7a7a\u51fa\u4e0a\u623f\u6765\uff0c\u4e0d\u7136\u5c31\u628a\u4ed6\u4eec\u7684\u5ba2\u6808\u7838\u6389\uff01\u201d \u3000\u3000\u5ffd\u542c\u8eab\u65c1\u7684\u4f19\u8ba1\u53eb\u9053\uff1a\u201c\u6211\u4eec\u638c\u67dc\u7684\u6765\u5566\uff01\u201d \u3000\u3000\u8fd9\u4f4d\u638c\u67dc\u7684\uff0c\u5e74\u7ea6\u4e94\u65ec\u4e0a\u4e0b\uff0c\u5706\u8138\u65e0\u987b\uff0c\u4e00\u56e2\u548c\u6c14\u76f8\uff0c\u4e00\u8fdb\u8de8\u9662\u5c31\u671b\u7740\u5d02\u5c71\u4e09\u96c4\u9ad8\u62f1\u53cc \u624b\u9053\uff1a\u201c\u62b1\u6b49\u62b1\u6b49\uff01\u8ba9\u4e09\u4f4d\u5927\u7237\u4e45\u7b49\u4e86\uff01\u201d \u3000\u3000\u738b\u5927\u5eb7\u9ad8\u58f0\u9053\uff1a\u201c\u4f60\u7528\u4e0d\u7740\u8bf4\u597d\u542c\u7684\uff0e\u5feb\u70b9\u627e\u623f\u5b50\u7ed9\u4ffa\u4eec\u4f4f\u8981\u7d27\uff01\u201d \u3000\u3000\u638c\u67dc\u7684\u4f9d\u7136\u6253\u8eac\u4f5c\u63d6\u966a\u7b11\u9053\uff1b\u201c\u8fd9\u4f4d\u5927\u7237\uff0c\u5c0f\u53f7\u7684\u786e\u5ba2\u623f\u5df2\u6ee1\u4e86\uff0c\u5982\u679c\u4e09\u4f4d\u5927\u7237\u4e00\u5b9a\u8981 \u4f4f\u5728\u8fd9\u91cc\uff0c\u6211\u4eec\u53ea\u6709\u628a\u5e10\u623f\u5148\u751f\u6216\u4f19\u8ba1\u4eec\u7684\u4f4f\u5904\uff0c\u8ba9\u7ed9\u4e09\u4f4d\u5927\u7237\uff0e\u201d \u3000\u3000\u738b\u5927\u5eb7\u6478\u7740\u4e0b\u5df4\uff0c\u6410\u4e86\u6410\u9f3b\u5b50\u9053\uff1a\u201c\u5947\u602a\uff0c\u4e3a\u4ec0\u4e48\u592a\u539f\u57ce\u7684\u5ba2\u6808\uff0c\u5bb6\u5bb6\u90fd\u5ba2\u6ee1\uff1f\u201d \u3000\u3000\u638c\u67dc\u7684\u5ffd\u7136\u7728\u7728\u773c\uff0c\u5e26\u70b9\u795e\u79d8\u610f\u5473\u7b11\u4e86\u7b11\u9053\uff1a\u201c\u5927\u7237\u6709\u6240\u4e0d\u77e5\uff0c\u8fd9\u51e0\u5929\u592a\u539f\u57ce\u5185\u7684\u5ba2 \u6808\uff0c\u5bb6\u5bb6\u5ba2\u6ee1\u662f\u6709\u539f\u56e0\u7684\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u539f\u56e0\uff1f\u201d \u3000\u3000\u638c\u67dc\u7684\u9053\uff1a\u201c\u56e0\u4e3a\u592a\u539f\u57ce\u6700\u8fd1\u51e0\u5929\uff0e\u5ffd\u7136\u6d8c\u6765\u4e00\u5927\u6279\u5404\u8272\u5404\u6837\u7684\u4eba\u7269\uff0e\u4ed6\u4eec\u5168\u662f\u770b\u8df3\u821e \u7684\u3002\u201d \u3000\u3000\u738b\u5927\u5eb7\u6123\u4e86\u4e00\u695e\u9053\uff1a\u201c\u8df3\u821e\uff1f\u4ec0\u4e48\u53eb\u8df3\u821e\uff1f\u201d \u3000\u3000\u638c\u67dc\u7684\u54bd\u4e0b\u2014\u53e3\u553e\u6cab\u9053\uff1a\u201c\u8fd9\u540d\u8bcd\u5c0f\u4eba\u4e5f\u662f\u6700\u8fd1\u624d\u542c\u5230\uff0c\u53cd\u6b63\u5c31\u662f\u4e24\u6761\u817f\u5728\u53f0\u4e0a\u4e71\u8e66\u4e71 \u8df3\u7684\uff0e\u201d \u3000\u3000\u5de5\u5927\u5eb7\u76b1\u4e86\u76b1\u4e24\u9053\u6d53\u7709\u9053\uff1a\u201c\u90a3\u6709\u4ec0\u4e48\u597d\u770b\u7684\uff1f\u4e09\u5c81\u7684\u5c0f\u5b69\u5b50\uff0c\u4e5f\u4f1a\u4e71\u8e66\u4e71\u8df3\uff0e\u201d \u3000\u3000\u638c\u67dc\u7684\u53c8\u662f\u795e\u79d8\u4e00\u7b11\u9053\uff1a\u201c\u5927\u7237\u56e0\u4e3a\u6ca1\u770b\u8fc7\uff0c\u5f53\u7136\u4e0d\u77e5\u9053\u5999\u5904\u5728\u54ea\u91cc\uff0e\u201d \u3000\u3000\u738b\u5927\u5eb7\u4e5f\u662f\u4e00\u6123\u9053\uff1a\u201c\u4f60\u8bf4\u5999\u5904\u5728\u54ea\u91cc\uff1f\u65e2\u662f\u4e71\u8e66\u4e71\u8df3\uff0c\u54ea\u91cc\u6765\u7684\u5999\u5904\uff1f\u201d \u3000\u3000\u638c\u67dc\u7684\u8138\u4e0a\u2014\u7ea2\uff0c\u5e72\u54b3\u4e24\u58f0\u9053\uff1a\u201c\u56e0\u4e3a\u8df3\u821e\u7684\uff0c\u6ca1\u7a7f\u88e4\u5b50\u3002\u201d \u3000\u3000\u738b\u5927\u5eb7\u554a\u4e86\u58f0\u9053\uff1a\u201c\u6709\u8fd9\u79cd\u4e8b\uff0c\u8df3\u821e\u90a3\u4eba\uff0c\u662f\u7537\u7684\u8fd8\u662f\u5973\u7684\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u662f\u5973\u7684\uff0c\u5982\u679c\u662f\u7537\u7684\uff0c\u90a3\u8fd8\u6709\u4ec0\u4e48\u770b\u5934\uff1f\u201d \u3000\u3000\u201c\u770b\u8df3\u821e\u7684\u4e0e\u5ba2\u6808\u5ba2\u6ee1\uff0c\u53c8\u6709\u4ec0\u4e48\u5173\u7cfb\u5462\uff1f\u201d \u3000\u3000\u201c\u5927\u7237\u6709\u6240\u4e0d\u77e5\uff0c\u6211\u4eec\u592a\u539f\u57ce\u6c11\u98ce\u7eaf\u6734\uff0c\u8c01\u4e5f\u4e0d\u60f3\u53bb\u770b\u5973\u4eba\u4e0d\u7a7f\u88e4\u5b50\u8df3\u821e\uff0c\u6240\u4ee5\u51e1\u662f\u53bb \u770b\u8df3\u821e\u7684\u4eba\uff0c\u7edd\u5927\u591a\u6570\u662f\u7531\u5916\u5730\u95fb\u98ce\u8d76\u6765\u7684\uff0e\u8fd9\u5c31\u662f\u592a\u539f\u57ce\u5185\u5bb6\u5bb6\u5ba2\u6808\u90fd\u5ba2\u6ee1\u7684\u539f\u56e0\u3002\u201d \u3000\u3000\u201c\u90a3\u5973\u4eba\u5728\u4ec0\u4e48\u5730\u65b9\u8df3\uff1f\u201d \u3000\u3000\u201c\u5728\u57ce\u4e1c\u4e00\u5bb6\u5c0f\u620f\u56ed\u91cc\u3002\u201d \u3000\u3000\u201d\u770b\u6837\u5b50\u4f60\u53ef\u80fd\u53bb\u770b\u8fc7\uff0c\u4e0d\u7136\u4e0d\u4f1a\u77e5\u9053\u5f97\u8fd9\u4e48\u6e05\u695a\u3002\u201d \u3000\u3000\u638c\u67dc\u7684\u8138\u4e0a\u4e00\u70ed\uff0c\u5148\u662f\u6414\u4e86\u6414\u5934\uff0c\u7136\u540e\u4ed6\u624d\u54a7\u5634\u7b11\u9053\uff1a\u201c\u5c0f\u4eba\u4e00\u65f6\u597d\u5947\uff0c\u53c8\u6709\u670b\u53cb\u5f3a\u62c9 \u7740\u53bb\uff0c\u4e0d\u5f97\u5df2\u6628\u665a\u53bb\u770b\u4e86\u4e00\u573a\uff0e\u201d \u3000\u3000\u738b\u5927\u5eb7\u7b11\u9053\uff1a\u201c\u597d\u4e0d\u597d\u770b\uff1f\u201d \u3000\u3000\u638c\u67dc\u7684\u987f\u4e86\u987f\u9053\uff1a\u201c\u5176\u5b9e\u53ea\u662f\u770b\u4e2a\u65b0\u5947\uff0c\u559c\u6b22\u770b\u7684\u767e\u770b\u4e0d\u538c\uff0c\u4e0d\u60f3\u770b\u7684\uff0c\u5f88\u53ef\u80fd\u56de\u6765\u8981 \u4f5c\u5455\u4e09\u5929\uff0e\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u4e09\u96c4\u4e2d\u7684\u8001\u4e8c\u9b3c\u8bf8\u845b\u6d2a\u6cfd\u5df2\u6709\u4e9b\u4e0d\u8010\uff0c\u8f7b\u54b3\u4e00\u58f0\u9053\uff1a\u201c\u8001\u4e09\u522b\u8ddf\u638c\u67dc\u7684\u80e1\u626f\u4e2a\u6ca1 \u5b8c\uff0c\u627e\u7761\u89c9\u7684\u5730\u65b9\u8981\u7d27\u3002\u201d \u3000\u3000\u638c\u67dc\u7684\u72b9\u8c6b\u4e86\u534a\u664c\u9053\uff1a\u201c\u8fd9\u6837\u5427\uff01\u53e6\u4e00\u5904\u8de8\u9662\u91cc\uff0c\u6709\u95f4\u623f\u5ba2\u662f\u4e34\u65f6\u653e\u4e1c\u897f\u7528\u7684\uff0c\u56de\u5934\u5c31 \u8981\u4f19\u8ba1\u6574\u7406\u51fa\u6765\u3002\u53ea\u662f\u90a3\u95f4\u623f\u95f4\u5c0f\u4e86\u4e9b\uff0c\u53ea\u80fd\u7761\u4e24\u4f4d\u2026\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u6ca1\u5173\u7cfb\uff0c\u5269\u4e0b\u4e00\u4f4d\u5c31\u8ddf\u6211\u4f4f\u5728\u4e00\u8d77\u597d\u5566\uff01\u201d \u3000\u3000\u738b\u5927\u5eb7\u62a2\u7740\u9053\uff1a\u201c\u90a3\u5c31\u7531\u4ffa\u548c\u9ea6\u8001\u5f1f\u5408\u4f4f\u4e00\u4e2a\u623f\u95f4\u597d\u5566\uff01\u201d \u3000\u3000\u638c\u67dc\u7684\u89c1\u4e8b\u60c5\u5df2\u7ecf\u89e3\u51b3\uff0c\u7acb\u5373\u5e26\u7740\u7b11\u9762\u4f5b\u9c8d\u8d85\u548c\u9b3c\u8bf8\u845b\u6d2a\u6cfd\u4e24\u4eba\uff0c\u5f80\u53e6\u4e00\u5904\u8de8\u9662\u800c\u53bb \u4e86\uff01 \u3000\u3000\u9ea6\u5c0f\u660e\u4e5f\u968f\u5373\u628a\u52c7\u91d1\u521a\u738b\u5927\u5eb7\uff0c\u9886\u8fdb\u81ea\u5df1\u6240\u4f4f\u7684\u4e0a\u623f\u3002 \u3000\u3000\u4ed6\u672c\u60f3\u628a\u9694\u58c1\u7684\u82d7\u7d20\u82d3\u53eb\u6765\uff0c\u548c\u738b\u5927\u5eb7\u76f8\u89c1\uff0c\u4f46\u53c8\u60f3\u5230\u82d7\u7d20\u82d3\u53ef\u80fd\u5df2\u7ecf\u7761\u4e0b\uff0c\u4e0d\u5e94\u518d\u53bb \u6253\u6270\u4eba\u5bb6\u3002 \u3000\u3000\u738b\u5927\u5eb7\u81ea\u52a8\u6c8f\u4e86\u76c5\u8336\uff0c\u2014\u4ef0\u8116\u5b50\uff0c\u5495\u561f\u5495\u561f\u559d\u5e72\u9053\uff1a\u201c\u521a\u624d\u8fd8\u6ca1\u95ee\u4f60\uff0c\u73b0\u5728\u7684\u76df\u4e3b\u662f \u8c01\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u6599\u60f3\u65b0\u4efb\u76df\u4e3b\u7684\u4ee4\u7b26\uff0c\u5c1a\u672a\u4f20\u5230\u5d02\u5c71\uff0c\u6447\u4e86\u6447\u5934\u9053\uff1a\u201c\u4e0d\u63d0\u4e5f\u7f62\uff0c\u63d0\u8d77\u53c8\u53eb\u4eba\u5fc3 \u91cc\u6709\u6c14\uff01\u201d \u3000\u3000\u738b\u5927\u5eb7\u9053\uff1a\u201c\u636e\u4ffa\u731c\u6d4b\u4e00\u5b9a\u662f\u949f\u526f\u76df\u4e3b\u6276\u6b63\u4e86\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u5982\u679c\u662f\u949f\u2014\u8c6a\u5f53\u4e0a\u76df\u4e3b\uff0e\u8c01\u90fd\u6ca1\u8bdd\u8bf4\uff0c\u53ef\u60dc\u4e0d\u662f\u4ed6\u3002\u201d \u3000\u3000\u201c\u4f60\u8bf4\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u970d\u5143\u4f3d\u90a3\u8001\u738b\u516b\u86cb\uff01\u201d \u3000\u3000\u738b\u5927\u5eb7\u4e24\u773c\u2014\u8e6c\uff0c\u8fde\u80e1\u5b50\u90fd\u6839\u6839\u7fd8\u4e86\u8d77\u6765\uff0c\u543c\u9053\uff1a\u201c\u539f\u6765\u662f\u4ed6\uff0c\u90a3\u72d7\u5a18\u517b\u7684\u5f53\u4e0a\u7eff\u6797\u76df \u4e3b\uff0c\u5c82\u4e0d\u8981\u5f04\u5f97\u5929\u4e0b\u5927\u4e71\uff01\u201d\u3000\u2019 \u3000\u3000\u9ea6\u5c0f\u660e\u51b7\u51b7\u4e00\u7b11\u9053\uff1a\u201c\u4eba\u5bb6\u672c\u4e8b\u5927\uff0c\u54b1\u4eec\u4e0d\u670d\u6c14\u53c8\u6709\u4ec0\u4e48\u529e\u6cd5\uff01\u201d \u3000\u3000\u738b\u5927\u5eb7\u54ac\u7259\u5207\u9f7f\u7684\u5401\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u5976\u5976\u7684\uff0c\u4ffa\u4eec\u672c\u4ee5\u4e3a\u65b0\u76df\u4e3b\u662f\u949f\u4e00\u8c6a\uff0c\u8fd9\u6b21\u8001\u8fdc\u7531\u5d02 \u5c71\u8d76\u5f80\u5317\u5cb3\u8ff7\u8e2a\u8c37\uff0c\u4e0d\u5916\u662f\u66ff\u4ed6\u58ee\u58ee\u58f0\u52bf\uff0e\u5982\u679c\u8981\u66ff\u8c37\u76df\u4e3b\u62a5\u4ec7\uff0c\u4ffa\u4eec\u4e5f\u597d\u5e2e\u5e2e\u5fd9\u3002\u73b0\u5728\uff0c \u65e2\u7136\u662f\u970d\u5143\u4f3d\u90a3\u72d7\u5a18\u517b\u7684\u5f53\u4e0a\u4e86\u76df\u4e3b\uff0c\u5e72\u8106\u660e\u5929\u4e00\u65e9\uff0c\u4ffa\u4eec\u4e09\u4e2a\u5c31\u518d\u56de\u5d02\u5c71\uff0c\u514d\u5f97\u770b\u5230\u90a3\u72d7 \u5a18\u517b\u7684\u5fc3\u91cc\u4e0d\u8212\u670d\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u5fd9\u9053\uff1a\u201c\u738b\u5f53\u5bb6\u7684\uff0c\u4f60\u8fd9\u8bdd\u9519\u5566\uff01\u201d \u3000\u3000\u738b\u5927\u5eb7\u53c8\u662f\u4e24\u773c\u4e00\u77aa\u9053\uff1a\u201c\u9519\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u54b1\u4eec\u5c31\u662f\u56e0\u4e3a\u4eba\u624b\u4e0d\u8db3\uff0c\u529b\u91cf\u592a\u5c0f\uff0c\u6240\u4ee5\u624d\u4f1a\u88ab\u970d\u5143\u4f3d\u90a3\u8001\u738b\u516b\u86cb\u5f53\u4e0a\u4e86 \u76df\u4e3b\uff0e\u5982\u679c\u4f60\u4eec\u4e09\u4f4d\u5230\u4e86\u8ff7\u8e2a\u8c37\uff0c\u7b49\u4e8e\u7ed9\u54b1\u4eec\u6dfb\u4e86\u751f\u529b\u519b\uff0c\u5c06\u6765\u58f0\u52bf\u58ee\u5927\uff0c\u4f55\u6101\u4e0d\u80fd\u628a\u7eff\u6797 \u76df\u4e3b\u4e4b\u4f4d\uff0c\u7531\u90a3\u8001\u738b\u516b\u86cb\u624b\u91cc\u593a\u56de\u6765\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u8bf4\u7684\u662f\u771f\u5fc3\u8bdd\uff0e\u4ed6\u786e\u5b9e\u5e0c\u671b\u5d02\u5c71\u4e09\u96c4\u80fd\u591f\u91cd\u8fd4\u5317\u5cb3\u8ff7\u8e2a\u8c37\uff0c\u5927\u5bb6\u2014\u540c\u5bf9\u4ed8\u90a3\u970d\u5143 \u4f3d\uff0e \u3000\u3000\u738b\u5927\u5eb7\u8f6c\u52a8\u7740\u725b\u773c\uff0c\u6c89\u541f\u4e86\u534a\u54cd\u9053\uff1a\u201c\u4f60\u8bf4\u7684\u679c\u7136\u6709\u9053\u7406\uff0c\u90a3\u4e48\u4ffa\u4eec\u4e09\u4e2a\u5c31\u4e0d\u56de\u5d02\u5c71 \u5566\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u90a3\u5c31\u597d\u3002\u4f60\u8f9b\u82e6\u4e86\u4e00\u5929\uff0c\u8be5\u65e9\u65e9\u4f11\u606f\u4e86\u5427\uff1f\u201d \u3000\u3000\u5c82\u77e5\u738b\u5927\u5eb7\u56e0\u5fc3\u91cc\u6709\u6c14\uff0c\u6839\u672c\u5c31\u4e0d\u60f3\u7761\uff0c\u5ffd\u7136\u4ed6\u5fc3\u4e2d\u4e00\u52a8\u9053\uff1a \u3000\u3000\u201c\u65f6\u95f4\u8fd8\u65e9\uff0c\u54b1\u4eec\u51fa\u53bb\u73a9\u73a9\uff0c\u597d\u4e0d\u597d\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4f60\u51c6\u5907\u5230\u54ea\u91cc\u73a9\uff1f\u201d \u3000\u3000\u201c\u65b9\u624d\u638c\u67dc\u7684\u4e0d\u662f\u8bf4\u6709\u4e0d\u7a7f\u88e4\u5b50\u8df3\u821e\u7684\u4e48\uff1f\u54b1\u4eec\u5c31\u53bb\u770b\u770b\u5982\u4f55\uff1f\u201d \u3000\u3000\u201c\u90a3\u591a\u96be\u770b\uff01\u201d \u3000\u3000\u201c\u8c01\u8bf4\u96be\u770b\uff0c\u5728\u4ffa\u4eec\u5bb6\u4e61\u5c71\u4e1c\uff0c\u4e0d\u8bba\u5927\u59d1\u5a18\u5c0f\u5ab3\u5987\uff0c\u90fd\u662f\u5927\u95e8\u4e0d\u51fa\uff0c\u4e8c\u95e8\u4e0d\u8fc8\uff0c\u60f3\u770b\u5230 \u5979\u4eec\u7684\u624b\u90fd\u4e0d\u5bb9\u6613\uff01\u73b0\u5728\uff0c\u5c45\u7136\u6709\u5973\u4eba\u80af\u8131\u88e4\u5b50\u7ed9\u4eba\u770b\uff0c\u8fd9\u82e5\u5728\u4ffa\u4eec\u5bb6\u4e61\uff0c\u5c31\u662f\u82b1\u2014\u4e07\u4e24\u94f6 \u5b50\uff0c\u4e5f\u770b\u4e0d\u5230\u7684\uff01\u201d \u3000\u3000\u8fd9\u51e0\u53e5\u8bdd\uff0c\u4e0d\u7531\u8bf4\u5f97\u9ea6\u5c0f\u660e\u5927\u4e3a\u5fc3\u52a8\uff0e \u3000\u3000\u522b\u770b\u4ed6\u5728\u52a8\u624b\u53ae\u6740\u7684\u573a\u5408\uff0c\u66fe\u89c1\u8fc7\u4e0d\u5c11\u5927\u573a\u9762\uff0c\u4f46\u4e00\u76f4\u5230\u73b0\u5728\uff0c\u5374\u662f\u5c1a\u672a\u89c1\u8fc7\u5973\u4eba\u8131\u4e86 \u88e4\u5b50\u662f\u4ec0\u4e48\u6837\u5b50\uff1f \u3000\u3000\u4e0d\u8fc7\uff0e\u505a\u6740\u4eba\u653e\u706b\u7684\u4e8b\uff0c\u9ea6\u5c0f\u660e\u80c6\u5b50\u867d\u5927\uff0c\u4f46\u8981\u63d0\u5230\u7537\u5973\u4e4b\u95f4\u7684\u4e8b\uff0c\u4ed6\u7684\u8138\u76ae\u8fd8\u662f\u5f88\u8584 \u7684\uff01 \u3000\u3000\u56e0\u4e3a\uff0c\u4ed6\u5185\u5fc3\u867d\u7136\u6709\u610f\uff0c\u5374\u4e0d\u4fbf\u51fa\u53e3\u8868\u793a\u3002 \u3000\u3000\u738b\u5927\u5eb7\u662f\u8001\u6c5f\u6e56\uff0c\u5c82\u4f1a\u770b\u4e0d\u51fa\u9ea6\u5c0f\u660e\u5df2\u9887\u6709\u610f\u601d\uff0e \u3000\u3000\u6b63\u597d\u8fd9\u65f6\uff0c\u5e97\u5c0f\u4e8c\u53c8\u9001\u8336\u6765\u4e86\u3002 \u3000\u3000\u738b\u5927\u5eb7\u4fbf\u558a\u4f4f\u4e86\u5e97\u5c0f\u4e8c\u9053\uff1a\u201c\u4f19\u8ba1\uff0c\u542c\u8bf4\u6709\u5bb6\u620f\u56ed\uff0c\u8fd9\u51e0\u5929\u6709\u4eba\u4e0d\u7a7f\u88e4\u5b50\u8df3\u821e\uff0c\u4f60\u77e5\u4e0d \u77e5\u90a3\u5bb6\u620f\u56ed\u600e\u4e48\u8d70\u6cd5\uff1f\u201d \u3000\u3000\u5e97\u4f19\u8ba1\u7ea2\u7740\u8138\u7b11\u9053\uff1a\u201c\u542c\u8bf4\u90a3\u53eb\u8131\u8863\u821e\uff0c\u4e0d\u53eb\u8131\u88e4\u821e\u3002\u201d \u3000\u3000\u738b\u5927\u5eb7\u7b11\u9053\uff1a\u201c\u88e4\u5b50\u4e5f\u662f\u8863\u670d\uff0c\u8131\u88e4\u5b50\u548c\u8131\u8863\u670d\u6839\u672c\u6ca1\u5206\u522b\uff0c\u4f60\u7a76\u7adf\u77e5\u9053\u4e0d\u77e5\u9053\u8def \u5462\uff1f\u201d \u3000\u3000\u5e97\u4f19\u9053\uff1b\u201c\u5c0f\u7684\u4f4f\u5728\u592a\u539f\u57ce\uff0c\u5f53\u7136\u77e5\u9053\u90a3\u5bb6\u620f\u56ed\uff0c\u4e0d\u8fc7\u5730\u70b9\u5f88\u504f\u50fb\uff0c\u751f\u4eba\u627e\u8d77\u6765\u4e0d\u592a\u65b9 \u4fbf\u3002\u201d \u3000\u3000\u201c\u4f60\u4eec\u638c\u67dc\u7684\u4e0d\u662f\u8bf4\u5728\u57ce\u4e1c\u4e48\uff1f\u201d \u3000\u3000\u201c\u867d\u7136\u5728\u57ce\u4e1c\uff0c\u5374\u662f\u5728\u80e1\u540c\u91cc\uff0c\u5fc5\u987b\u6162\u6162\u6253\u542c\uff0e\u624d\u80fd\u627e\u5230\u3002\u201d \u3000\u3000\u201c\u4f60\u53bb\u770b\u8fc7\u8131\u88e4\u821e\u6ca1\u6709\uff1f\u201d \u3000\u3000\u201c\u5973\u4eba\u5f53\u4f17\u8131\u88e4\u5b50\uff0c\u7537\u4eba\u8c01\u4e0d\u60f3\u770b\uff0c\u53ef\u662f\u5c0f\u7684\u4e00\u6765\u6ca1\u65f6\u95f4\uff0c\u4e8c\u6765\u7968\u4ef7\u4e5f\u592a\u8d35\uff0c\u8db3\u591f\u5c0f\u7684 \u4e2a\u628a\u6708\u7684\u85aa\u6c34\uff0c\u5b9e\u5728\u770b\u4e0d\u8d77\u3002\u201d \u3000\u3000\u201c\u7968\u4ef7\u591a\u5c11\uff1f\u201d \u3000\u3000\u201c\u542c\u8bf4\u8981\u4e94\u4e24\u94f6\u5b50\uff01\u201d \u3000\u3000\u738b\u5927\u5eb7\u62cd\u4e86\u62cd\u8170\u9053\uff1a\u201c\u4ffa\u8170\u91cc\u94f6\u5b50\u591a\u7684\u662f\uff0c\u5373\u4fbf\u4e94\u767e\u4e24\u4e00\u5f20\u7968\uff0c\u4e5f\u4e70\u5f97\u8d77\uff0c\u5c31\u7531\u4f60\u5e26\u8def \u4ffa\u8bf7\u5ba2\uff0c\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u5e97\u4f19\u8ba1\u89c1\u738b\u5927\u5eb7\u5c45\u7136\u80af\u82b1\u4e94\u4e24\u94f6\u5b50\uff0c\u8bf7\u81ea\u5df1\u53bb\u770b\u5973\u4eba\u4e0d\u7a7f\u8863\u670d\uff0c\u5c82\u6709\u4e0d\u52a8\u5fc3\u4e4b\u7406\uff0c\u53ea\u662f \u53c8\u62c5\u5fc3\u5e97\u91cc\u6709\u4e8b\uff0c\u4e0d\u6562\u7acb\u523b\u7b54\u5e94\uff0e \u3000\u3000\u201c\u4f60\u8fd8\u72b9\u8c6b\u4ec0\u4e48\uff0c\u662f\u4e0d\u662f\u65f6\u95f4\u5df2\u7ecf\u8fc7\u4e86\uff1f\u201d \u3000\u3000\u5e97\u4f19\u8ba1\u9053\uff1a\u201c\u6ca1\u8fc7\u6ca1\u8fc7\uff0c\u73b0\u5728\u53bb\u4e86\u6b63\u597d\uff0c\u6211\u542c\u8bf4\u665a\u4e0a\u6709\u4e24\u573a\uff0c\u73b0\u5728\u5927\u6982\u7b2c\u4e00\u573a\u624d\u521a\u521a\u5f00 \u59cb\uff0e\u201d \u3000\u3000\u201c\u90a3\u4f60\u8fd8\u7b49\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5e97\u4f19\u8ba1\u9053\uff1a\u201c\u5c0f\u7684\u62c5\u5fc3\u6808\u4e0a\u6709\u4e8b\uff0c\u4f55\u51b5\u82e5\u88ab\u638c\u67dc\u7684\u77e5\u9053\uff0c\u53d7\u7f5a\u662f\u5c0f\u4e8b\uff0c\u8bf4\u4e0d\u5b9a\u4f1a\u7838\u6389\u996d \u7897\uff01\u201d \u3000\u3000\u738b\u5927\u5eb7\u7b11\u9053\uff1a\u201c\u522b\u8bf4\u5f97\u90a3\u4e48\u597d\u542c\uff0c\u4f60\u4eec\u638c\u67dc\u7684\u81ea\u5df1\u90fd\u770b\u8fc7\uff0c\u5dde\u5b98\u5df2\u7ecf\u653e\u706b\uff0c\u600e\u80fd\u4e0d\u51c6\u767e \u59d3\u70b9\u706f\uff1f\u201d \u3000\u3000\u5e97\u4f19\u8ba1\u9053\uff1a\u201c\u5927\u7237\u8bdd\u867d\u8fd9\u4e48\u8bf4\uff0c\u4f46\u6211\u4eec\u638c\u67dc\u7684\u5374\u662f\u8bf4\u529e\u4eba\u5c31\u529e\u4eba\uff01\u201d \u3000\u3000\u738b\u5927\u5eb7\u62cd\u62cd\u80f8\u812f\u9053\uff1a\u201c\u5976\u5976\u7684\uff0c\u7f57\u55e6\u4ec0\u4e48\uff1f\u4ed6\u82e5\u8bf4\u529e\u4f60\uff0c\u4ffa\u5c31\u529e\u4ed6\uff0c\u770b\u4ed6\u6562\u4e0d\u6562\u52a8\u4f60\u4e00 \u6839\u6c57\u6bdb\uff0c\u8d70\uff01\u201d \u3000\u3000\u738b\u5927\u5eb7\u548c\u9ea6\u5c0f\u660e\u5728\u5e97\u4f19\u8ba1\u7684\u966a\u540c\u4e0b\uff0c\u51fa\u4e86\u9633\u66f2\u5ba2\u6808\u5927\u95e8\uff0e \u3000\u3000\u8fd9\u65f6\uff0c\u8857\u4e0a\u65e9\u5df2\u534e\u706f\u521d\u4e0a\u3002 \u3000\u3000\u884c\u4eba\u7199\u6765\u6518\u5f80\uff0c\u597d\u4e0d\u70ed\u95f9\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u642d\u8baa\u7740\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u4e0d\u628a\u9c8d\u8001\u5927\u548c\u6d2a\u8001\u4e8c\u4e5f\u2014\u8d77\u627e\u6765\uff1f\u201d \u3000\u3000\u738b\u5927\u5eb7\u7b11\u4e86\u7b11\u9053\uff1a\u201c\u4ed6\u4eec\u4e24\u4e2a\u90fd\u662f\u77f3\u7070\u8111\u888b\uff0c\u82e5\u662f\u544a\u8bc9\u4e86\u4ed6\u4eec\uff0c\u53ea\u6015\u8fde\u4ffa\u4e5f\u6765\u4e0d\u6210 \u5566\uff01\u201d \u3000\u3000\u201c\u96be\u9053\u4ed6\u4eec\u5bf9\u5973\u4eba\u6ca1\u5174\u8da3\uff1f\u201d \u3000\u3000\u201c\u8001\u4e8c\u5e38\u8bf4\uff0c\u8981\u975e\u793c\u52ff\u52a8\uff0c\u975e\u793c\u52ff\u89c6\u3002\u770b\u5973\u4eba\u8131\u88e4\u5b50\u8fd9\u79cd\u4e8b\uff0c\u4ed6\u867d\u7136\u5fc3\u91cc\u6709\u5174\u8da3\uff0c\u8868\u9762 \u4e5f\u4e0d\u80fd\u52a8\u3002\u201d \u3000\u3000\u8f6c\u8fc7\u51e0\u6761\u8857\u5df7\uff0c\u5f88\u5feb\u4fbf\u8fdb\u5165\u4e00\u6761\u80e1\u540c\u3002 \u3000\u3000\u5e97\u4f19\u8ba1\u5411\u91cc\u9762\u4e00\u6307\u9053\uff1a\u201c\u8fd9\u5bb6\u620f\u56ed\u5b50\u5c31\u5728\u91cc\u9762\uff01\u201d \u3000\u3000\u5176\u5b9e\u5e97\u4f19\u8ba1\u5c31\u662f\u4e0d\u8bf4\uff0c\u738b\u5927\u5eb7\u548c\u9ea6\u5c0f\u660e\u4e5f\u770b\u5f97\u51fa\u3002 \u3000\u3000\u56e0\u4e3a\u91cc\u9762\u6709\u4e0d\u5c11\u4eba\u8fdb\u8fdb\u51fa\u51fa\uff0c\u8fd9\u79cd\u5730\u65b9\uff0c\u4e0d\u662f\u620f\u56ed\u5b50\uff0c\u5fc5\u5b9a\u662f\u7a91\u5b50\u9986\uff0c\u5e73\u5e38\u4eba\u5bb6\uff0c\u95e8\u524d \u90a3\u6709\u8fd9\u79cd\u666f\u8c61\uff0e \u3000\u3000\u8fdb\u5165\u56ed\u5b50\uff0e\u91cc\u9762\u5bb9\u91cf\u867d\u7136\u4e0d\u5927\u3002 \u3000\u3000\u4f46\u5374\u6324\u6ee1\u4e86\u8db3\u8db3\u51e0\u767e\u4eba\u3002 \u3000\u3000\u5947\u602a\u7684\u662f\u660e\u660e\u6709\u5ea7\u4f4d\uff0c\u591a\u6570\u4eba\u5374\u6324\u524d\u9762\u7ad9\u7740\u3002 \u3000\u3000\u751a\u81f3\uff0e\u6709\u4e0d\u5c11\u4eba\u8fd8\u722c\u5230\u53f0\u53e3\u4e0a\u3002 \u3000\u3000\u573a\u5185\u4e00\u7247\u4e71\u54c4\u54c4\uff0c\u540e\u9762\u5374\u7a7a\u4e86\u4e00\u5927\u7247\u7a7a\u5ea7\u4f4d\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u53ef\u80fd\u7b2c\u4e00\u573a\u5df2\u8868\u6f14\u5b8c\uff0c\u6b63\u662f\u4f11\u606f\u65f6\u523b\uff0c\u56e0\u4e4b\uff0c\u53f0\u4e0a\u4e0d\u89c1\u6709\u4eba\uff0e \u3000\u3000\u738b\u5927\u5eb7\u603b\u662f\u6709\u8eab\u4efd\u5730\u4f4d\u7684\u4eba\uff0c\u4e0d\u4fbf\u5f80\u524d\u6324\uff0c\u4fbf\u548c\u9ea6\u5c0f\u660e\u3001\u5e97\u4f19\u8ba1\u4e09\u4eba\uff0c\u5728\u540e\u9762\u7a7a\u4f4d\u4e0a\u5750 \u4e0b\uff0e \u3000\u3000\u4ed6\u4e00\u8fb9\u5600\u5495\u9053\uff1a\u201c\u5976\u5976\u7684\uff0c\u8fd9\u4e9b\u738b\u516b\u86cb\u4e3a\u4ec0\u4e48\u6709\u5ea7\u4f4d\u4e0d\u5750\uff0c\u5374\u504f\u8981\u5f80\u524d\u6324\uff0c\u5b9e\u5728\u662f\u8d31\u9aa8 \u5934\uff01\u201d \u3000\u3000\u5e97\u4f19\u8ba1\u9053\uff1a\u201c\u96be\u9053\u5927\u7237\u8fd8\u4e0d\u660e\u767d\uff0c\u4ed6\u4eec\u6324\u5728\u524d\u9762\uff0c\u624d\u80fd\u770b\u5f97\u6e05\u695a\u3002\u201d \u3000\u3000\u738b\u5927\u5eb7\u54fc\u4e86\u58f0\u9053\uff1a\u201c\u518d\u5f80\u524d\u6324\u4e5f\u94bb\u4e0d\u8fdb\u53bb\uff0e\u201d \u3000\u3000\u5e97\u4f19\u8ba1\u5fcd\u4e0d\u4f4f\u7b11\u51fa\u58f0\u9053\uff1a\u201c\u4f60\u8001\u4eba\u5bb6\u771f\u4f1a\u8bf4\u7b11\u8bdd\u3002\u201d \u3000\u3000\u5176\u5b9e\uff0c\u5e97\u4f19\u8ba1\u4e5f\u65e9\u5c31\u6068\u4e0d\u5f97\u722c\u5230\u53f0\u53e3\u53bb\uff0c\u56e0\u5fc5\u987b\u966a\u7740\u4e24\u4f4d\u5ba2\u4eba\uff0c\u4e0d\u5f97\u4e0d\u8001\u8001\u5b9e\u5b9e\u5750\u5728\u90a3 \u91cc\u800c\u5df2\uff0e \u3000\u3000\u5ffd\u7136\uff0c\u9ea6\u5c0f\u660e\u8f7b\u8f7b\u62cd\u4e00\u4e0b\u5e97\u4f19\u8ba1\u80a9\u8180\u9053\uff1a\u201c\u4f19\u8ba1\uff0c\u7528\u4e0d\u7740\u62c5\u5fc3\u4f1a\u53d7\u5230\u4f60\u4eec\u638c\u67dc\u7684\u5904\u5206 \u5566\uff01\u201d \u3000\u3000\u5e97\u4f19\u8ba1\u6123\u4e86\u6123\u9053\uff1a\u201c\u516c\u5b50\u600e\u4e48\u5ffd\u7136\u53c8\u63d0\u5230\u8fd9\u4e2a\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u62ac\u624b\u5f80\u5de6\u524d\u65b9\u4e00\u6307\u9053\uff1a\u201c\u4f60\u770b\u90a3\u4e2a\u662f\u4e0d\u662f\u4f60\u4eec\u638c\u67dc\u7684\uff1f\u201d \u3000\u3000\u5e97\u4f19\u8ba1\u4f9d\u7740\u624b\u52bf\u7559\u610f\u770b\u53bb\u3002 \u3000\u3000\u679c\u7136\u524d\u4e94\u516d\u6392\u9760\u5de6\u89d2\u7684\u5ea7\u4f4d\u4e0a\uff0c\u5750\u7740\u4e00\u4eba\u6b63\u662f\u638c\u67dc\u7684\u3002 \u3000\u3000\u53ea\u56e0\u638c\u67dc\u7684\u5750\u5728\u9760\u5de6\u4e00\u89d2\uff0e\u6240\u4ee5\u80fd\u6e05\u695a\u7684\u770b\u5230\u4ed6\u7684\u4fa7\u5f71\uff0c\u5982\u679c\u5728\u524d\u9762\u6b63\u4e2d\uff0c\u90a3\u5c31\u4e0d\u6613\u8fa8 \u8ba4\u4e86\uff0e \u3000\u3000\u9ea6\u5c0f\u660e\u7b11\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u600e\u4e48\u6837\uff1f\u662f\u4f60\u4eec\u638c\u67dc\u7684\u5427\uff01\u8fde\u4ed6\u81ea\u5df1\u90fd\u6765\u4e86\uff0e\u4f60\u8fd8\u6015\u4ec0\u4e48\u5462\uff1f\u201d \u3000\u3000\u5e97\u4f19\u8ba1\u5411\u4ed6\u5410\u4e86\u53f6\u820c\u5934\u9053\uff1a\u201c\u6211\u4eec\u638c\u67dc\u7684\u4e00\u5411\u662f\u5f88\u6b63\u6d3e\u7684\u4eba\uff0c\u600e\u4e48\u4ed6\u4e5f\u5230\u8fd9\u79cd\u5730\u65b9\u6765 \u4e86\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u8fd9\u79cd\u4eba\u5230\u5904\u90fd\u662f\uff0c\u5634\u91cc\u4ec1\u4e49\u9053\u5fb7\uff0c\u9aa8\u5b50\u91cc\u5374\u7537\u76d7\u5973\u5a3c\uff0c\u3000\u4e00\u70b9\u4e5f\u4e0d\u503c\u5f97\u5947 \u602a\u3002\u201d \u3000\u3000\u53ea\u542c\u5e97\u4f19\u8ba1\u554a\u4e86\u58f0\u9053\uff1a\u201c\u90a3\u8fb9\u597d\u50cf\u662f\u53e6\u5916\u4e24\u4f4d\u5927\u7237\uff01\u201d \u3000\u3000\u738b\u5927\u5eb7\u4e00\u6123\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u5b50\u522b\u80e1\u8bf4\u516b\u9053\uff0c\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u5e97\u4f19\u8ba1\u7528\u624b\u5f80\u53f3\u524d\u4e00\u6307\u9053\uff1a\u201c\u4f60\u770b\u90a3\u4e24\u4f4d\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u738b\u5927\u5eb7\u987a\u7740\u5e97\u4f19\u8ba1\u624b\u52bf\u671b\u4e86\u53bb\u3002 \u3000\u3000\u679c\u7136\u5728\u524d\u65b9\u4e24\u4e08\u4e4b\u5916\uff0c\u5750\u7740\u7b11\u9762\u4f5b\u9c8d\u8d85\u548c\u9b3c\u8bf8\u845b\u6d2a\u6cfd\u3002 \u3000\u3000\u597d\u5728\u4ed6\u4eec\u5750\u5728\u524d\u9762\uff0c\u5373\u4f7f\u5076\u800c\u56de\u5934\uff0e\u4e5f\u4e0d\u6613\u770b\u5230\u738b\u5927\u5eb7\u4e09\u4eba\uff0c\u66f4\u4f55\u51b5\u8d8a\u5f80\u540e\u5149\u7ebf\u8d8a\u6697 \u7684\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u8bb2\u4ed6\u4eec\u975e\u793c\u52ff\u89c6\uff0c\u975e\u793c\u52ff\u52a8\u4e48\uff1f\u201d \u3000\u3000\u738b\u5927\u5eb7\u5e72\u54b3\u4e86\u4e24\u58f0\u9053\uff1a\u201c\u771f\u60f3\u4e0d\u5230\u4ed6\u4eec\u4e24\u4e2a\u4e5f\u6765\u4e86\uff0c\u65e2\u7136\u54b1\u4eec\u4e5f\u6765\u4e86\uff0e\u5c31\u7b11\u4e0d\u5f97\u4ed6 \u4eec\u3002\u201d \u3000\u3000\u6b63\u8bf4\u8bdd\u95f4\uff0e\u5df2\u7531\u540e\u53f0\u8d70\u51fa\u4e00\u540d\u7350\u5934\u9f20\u76ee\u762a\u4e09\u6a21\u6837\u7684\u5e74\u8f7b\u4eba\uff0c\u5ba3\u5e03\u8282\u76ee\u5f00\u59cb\u3002 \u3000\u3000\u53f0\u4e0b\u7acb\u523b\u79e9\u5e8f\u7a0d\u4e3a\u597d\u4e86\u4e9b\uff0e\u738b\u5927\u5eb7\u3001\u9ea6\u5c0f\u660e\u3001\u5e97\u4f19\u8ba1\u4e5f\u4e0d\u518d\u5f00\u53e3\uff0e\u805a\u7cbe\u4f1a\u795e\u7684\u628a\u89c6\u7ebf\u79fb \u5411\u53f0\u4e0a\u3002 \u3000\u3000\u8c01\u77e5\u51fa\u6765\u4e00\u5973\u5b50\uff0c\u5374\u662f\u5531\u5c0f\u66f2\u7684\u3002 \u3000\u3000\u8fd9\u5973\u5b50\u7a7f\u7740\u7d27\u8eab\u8863\u88d9\uff0c\u53cc\u988a\u64e6\u7740\u539a\u80ed\u8102\uff0e\u5634\u5507\u6d82\u5f97\u50cf\u521a\u5403\u8fc7\u4e00\u53ea\u6d3b\u9e21\uff0c\u5728\u7434\u5f26\u7684\u4f34\u594f \u4e0b\uff0c\u5f00\u59cb\u5f15\u9888\u9ad8\u6b4c\u8d77\u6765\u3002 \u3000\u3000\u738b\u5927\u5eb7\u7b49\u4eba\u53ea\u9053\u5531\u5f97\u5fc5\u7136\u5f88\u52a8\u542c\uff0c\u5c82\u77e5\u7adf\u50cf\u6740\u9e2d\u2014\u80a1\uff0c\u5947\u602a\u7684\u662f\u4f9d\u7136\u80fd\u83b7\u5f97\u53f0\u4e0b\u4e0d\u5c11\u638c \u58f0\u3002 \u3000\u3000\u63a5\u7740\uff0c\u53c8\u4e0a\u6765\u4e00\u4e2a\u7537\u7684\u5531\u5c0f\u66f2\uff0e \u3000\u3000\u8fd9\u7537\u7684\u5356\u76f8\u548c\u90a3\u53f8\u4eea\u7684\u5dee\u4e0d\u591a\uff0c\u4e5f\u662f\u8d3c\u7709\u9f20\u76ee\uff0c\u5531\u8d77\u6765\u90a3\u58f0\u97f3\uff0e\u6709\u5982\u6740\u725b\uff0c\u6bd4\u9a74\u53eb\u8fd8\u8981 \u900a\u8272\u4e09\u5206\u3002 \u3000\u3000\u5f04\u5f97\u738b\u5927\u5eb7\u7b49\u4eba\uff0e\u51e0\u4e4e\u8981\u63a9\u4e0a\u8033\u6735\u3002 \u3000\u3000\u738b\u5927\u5eb7\u5583\u5583\u9a82\u9053\uff1a\u201c\u4ffa\u5165\u4ed6\u5976\u5976\u7684\uff0c\u4ffa\u7684\u58f0\u97f3\u5c31\u591f\u523a\u8033\u7684\uff0c\u53f0\u4e0a\u8fd9\u738b\u516b\u86cb\u8fde\u8bf4\u8bdd\u90fd\u4e0d\u591f \u683c\uff01 \u3000\u3000\u4ed6\u5c45\u7136\u8fd8\u6562\u5728\u5927\u5ead\u5e7f\u4f17\u5531\u6b4c\u5356\u94b1\u3002\u201d \u3000\u3000\u8a00\u4e0b\u4e4b\u610f\u5f88\u4e0d\u5c51\u3002 \u3000\u3000\u679c\u7136\uff0c\u8fd9\u7537\u7684\u5531\u540e\uff0c\u638c\u58f0\u4e0d\u591a\u3002 \u3000\u3000\u5176\u5b9e\uff0c\u8fd9\u822c\u542c\u4f17\uff0c\u6839\u672c\u542c\u4e0d\u51fa\u597d\u574f\uff0c\u53ea\u56e0\u6240\u6709\u53f0\u4e0b\u4eba\u5168\u662f\u7537\u7684\uff0c\u57fa\u4e8e\u540c\u6027\u76f8\u65a5\u539f\u7406\uff0c\u7537 \u4eba\u8868\u6f14\u4fbf\u4e0d\u53d7\u6b22\u8fce\u7f62\u4e86\u3002 \u3000\u3000\u63a5\u4e0b\u53bb\u4fbf\u662f\u51fa\u6765\u51e0\u4e2a\u5973\u7684\uff0c\u5728\u53f0\u4e0a\u4f38\u81c2\u8e6c\u817f\uff0c\u518d\u52a0\u4e0a\u6447\u80f8\u6446\u81c0\uff0c\u8c01\u4e5f\u4e0d\u77e5\u5979\u4eec\u5728\u505a\u4e9b\u4ec0 \u4e48\uff0c\u53cd\u6b63\u5979\u4eec\u6ca1\u95f2\u7740\u5440\uff01 \u3000\u3000\u738b\u5927\u5eb7\u51b7\u54fc\u7740\u9053\uff1a\u201c\u5976\u5976\u7684\uff0c\u8fd9\u7b97\u4ec0\u4e48\u8df3\u821e\uff0c\u53ea\u8981\u6709\u817f\u6709\u80f3\u818a\u90a3\u4e2a\u4e0d\u4f1a\u3002\u4f19\u8ba1\uff0c\u6ca1\u7a7f\u88e4 \u5b50\u7684\u662f\u54ea\u4e2a\uff1f\u201d \u3000\u3000\u5e97\u4f19\u8ba1\u6b63\u770b\u5f97\u51fa\u795e\uff0c\u95fb\u8a00\u4e00\u6447\u8111\u888b\u9053\uff1a\u201c\u90a3\u4e2a\u8fd8\u6ca1\u51fa\u6765\u3002\u201d \u3000\u3000\u738b\u5927\u5eb7\u4e0d\u89c9\u54a6\u4e86\u58f0\u9053\uff1a\u201c\u5976\u5976\u7684\uff0c\u4f60\u65e2\u7136\u4ece\u524d\u6ca1\u6765\u8fc7\uff0c\u600e\u4e48\u77e5\u9053\u53f0\u4e0a\u6709\u6ca1\u6709\u5979\u51fa\u6765 \u5462\uff1f\u201d \u3000\u3000\u5e97\u4f19\u8ba1\u9053\uff1a\u201c\u53ef\u662f\u5c0f\u7684\u5728\u522b\u5904\u770b\u89c1\u8fc7\u5979\u3002\u201d \u3000\u3000\u201c\u5728\u54ea\u91cc\u89c1\u8fc7\u5979\uff1f\u201d \u3000\u3000\u201c\u5f85\u4f1a\u513f\u5c0f\u7684\u518d\u8ddf\u5927\u7237\u8bb2\uff0e\u5927\u7237\u8fd8\u662f\u5148\u770b\u8df3\u821e\u8981\u7d27\uff01\u201d \u3000\u3000\u201c\u4ffa\u5165\u4ed6\u5976\u5976\u7684\uff0c\u8fd9\u79cd\u8df3\u6cd5\uff0c\u6709\u4ec0\u4e48\u597d\u770b\u7684\uff0c\u8857\u4e0a\u8d70\u8def\u7684\u5973\u4eba\uff0c\u54ea\u4e00\u4e2a\u90fd\u6bd4\u5979\u4eec\u597d \u770b\u3002\u201d \u3000\u3000\u8fd9\u51e0\u4e2a\u5973\u7684\uff0c\u76f4\u5728\u53f0\u4e0a\u78e8\u8e6d\u4e86\u987f\u996d\u5de5\u592b\uff0c\u624d\u8df3\u6bd5\u8fd4\u56de\u540e\u53f0\u3002 \u3000\u3000\u53ea\u89c1\u90a3\u7350\u5934\u9f20\u76ee\u762a\u4e09\u6a21\u6837\u7684\u53f8\u4eea\u53c8\u8d70\u51fa\u53f0\u53e3\uff0c\u5ba3\u5e03\u4e0b\u9762\u662f\u5927\u8f74\u620f\uff0c\u7531\u5973\u4e3b\u89d2\u8df3\u201c\u56de\u65cb\u68a6 \u91cc\u821e\u201d\u3002 \u3000\u3000\u542c\u8bf4\u5973\u4e3b\u89d2\u8981\u51fa\u573a\uff0c\u53f0\u4e0b\u987f\u65f6\u9e26\u96c0\u65e0\u58f0\uff0c\u5c4f\u606f\u4ee5\u5f85\uff0e \u3000\u3000\u5973\u4e3b\u89d2\u5f88\u5feb\u4fbf\u51fa\u573a\u4e86\uff0c\u62ab\u7740\u4e00\u88ad\u9ed1\u8272\u6597\u84ec\u6a21\u6837\u7684\u5916\u5957\u3002 \u3000\u3000\u8fd9\u5973\u4eba\u770b\u6765\u5927\u7ea6\u4e09\u5341\u5de6\u53f3\uff0c\u8eab\u6750\u4e2d\u7b49\uff0c\u5706\u5706\u7684\u8138\u86cb\uff0c\u867d\u7136\u770b\u4e0d\u51fa\u4e1d\u6beb\u7684\u7f8e\u611f\uff0c\u4f46\u5374\u5341\u5206 \u98ce\u9a9a\u3002 \u3000\u3000\u800c\u4e14\u5145\u6ee1\u4e86\u4e00\u80a1\u6deb\u90aa\u4e4b\u6c14\u3002 \u3000\u3000\u738b\u5927\u5eb7\u4e00\u76b1\u7709\u9053\uff1a\u201c\u4f19\u8ba1\uff0c\u4f60\u770b\u770b\uff0c\u5979\u8fde\u8863\u670d\u90fd\u7a7f\u5f97\u597d\u597d\u7684\uff0c\u8c01\u8bf4\u6ca1\u6709\u7a7f\u88e4\u5b50\u8df3\u821e \u561b\uff1f\u201d \u3000\u3000\u5e97\u4f19\u8ba1\u9053\uff1a\u201c\u5927\u7237\u8bf7\u8010\u5fc3\u7684\u770b\uff0c\u5f85\u4f1a\u513f\u5979\u5c31\u4f1a\u4e00\u4ef6\u4e00\u4ef6\u7684\u8131\u5566\uff01\u201d \u3000\u3000\u738b\u5927\u5eb7\u54fc\u4e86\u58f0\u9053\uff1a\u201c\u5c31\u90a3\u79cd\u957f\u76f8\uff0c\u518d\u8131\u4e5f\u5f15\u4e0d\u8d77\u8001\u5b50\u7684\u5174\u8da3\u3002\u201d \u3000\u3000\u5e97\u4f19\u8ba1\u9053\uff1a\u201c\u5927\u7237\u7684\u773c\u5149\u9ad8\uff0c\u53f0\u4e0b\u8fd9\u4e9b\u4eba\u7684\u773c\u5149\uff0c\u600e\u80fd\u8ddf\u4f60\u8001\u4eba\u5bb6\u6bd4\uff01\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u90a3\u5973\u4eba\u65e9\u5df2\u5a9a\u773c\u4e71\u629b\uff0e\u5728\u53f0\u4e0a\u8df3\u4e86\u8d77\u6765\u3002 \u3000\u3000\u5979\u8df3\u788d\u4e5f\u662f\u4e71\u4e03\u516b\u7cdf\uff0c\u6709\u65f6\u50cf\u53d1\u4e86\u7f8a\u988a\u75af\u4f3c\u7684\uff01 \u3000\u3000\u8c01\u4e5f\u4e0d\u77e5\u9053\uff0c\u662f\u5728\u73a9\u4ec0\u4e48\u660e\u5802\uff1f \u3000\u3000\u4e0d\u8fc7\u53f0\u4e0b\u7684\u6c14\u6c1b\uff0c\u5374\u5df2\u88ab\u4ed6\u201c\u9017\u201d\u5f97\u6e10\u6e10\u70ed\u70c8\u8d77\u6765\uff0e \u3000\u3000\u90a3\u5973\u7684\u8fb9\u628a\u9ed1\u8272\u5916\u8863\u5378\u4e0b\uff0c\u91cc\u9762\u9732\u51fa\u4e00\u4ef6\u8749\u5180\u822c\u7684\u8584\u7eb1\u3002 \u3000\u3000\u8fd9\u4e00\u4e0b\uff0c\u738b\u5927\u5eb7\u7ec8\u4e8e\u770b\u50bb\u4e86\u773c\uff0e \u3000\u3000\u56e0\u4e3a\u90a3\u8584\u7eb1\u5b9e\u5728\u592a\u8584\u4e86\uff0c\u8584\u5f97\u51e0\u4e4e\u5df2\u5230\u900f\u660e\u7684\u7a0b\u5ea6\uff0e \u3000\u3000\u679c\u7136\uff0c\u90a3\u5973\u4eba\u7684\u8584\u7eb1\u4e4b\u5185\uff0c\u4f3c\u4e4e\u6ca1\u7a7f\u8863\u670d\uff0c\u80f8\u524d\u9686\u8d77\u4e24\u5ea7\u9ad8\u5cf0\u548c\u5c0f\u8179\u4e4b\u4e0b\u53cc\u817f\u4e2d\u95f4\u7684\u91cd \u8981\u90e8\u4f4d\uff0c\u770b\u6765\u82e5\u9690\u82e5\u73b0\u3002 \u3000\u3000\u8fd9\u65f6\u53f0\u4e0b\u9760\u8fd1\u524d\u53f0\u7684\u89c2\u4f17\uff0e\u90fd\u50cf\u5728\u8ddf\u7740\u90a3\u5973\u4eba\u7684\u4e24\u6761\u817f\u65cb\u8f6c\u7740\uff0c\u6709\u5982\u6ce2\u6d6a\u4f3c\u7684\u5728\u4e0d\u4f4f\u8d77 \u4f0f\u3002 \u3000\u3000\u738b\u5927\u5eb7\u6123\u4e86\u6123\u9053\uff1a\u201c\u5976\u5976\u7684\uff0c\u4ed6\u4eec\u8fd9\u662f\u5728\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u90a3\u5e97\u4f19\u8ba1\u8fd8\u771f\u50cf\u662f\u4e00\u4f4d\u884c\u5bb6\uff0c\u7b11\u9053\uff1a\u201c\u96be\u9053\u5927\u7237\u8fd8\u770b\u4e0d\u51fa\u5417\uff1f\u4ed6\u4eec\u662f\u8981\u76ef\u7740\u5f80\u90a3\u5730\u65b9 \u770b\uff01\u201d \u3000\u3000\u738b\u5927\u5eb7\u9053\uff1a\u201c\u90a3\u5730\u65b9\u662f\u4ec0\u4e48\u5730\u65b9\uff1f\u201d \u3000\u3000\u5e97\u4f19\u8ba1\u8138\u4e0a\u2014\u70ed\u9053\uff1a\u201c\u60a8\u8001\u522b\u62ff\u5c0f\u7684\u5f00\u5fc3\u5566\uff01\u90a3\u5730\u65b9\u5f53\u7136\u662f\u4ed6\u4eec\u60f3\u770b\u7684\u5730\u65b9\u3002\u201d \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u90a3\u5973\u4eba\u8fde\u8584\u7eb1\u4e5f\u5df2\u8131\u4e0b\u629b\u53bb\uff01 \u3000\u3000\u5168\u8eab\u679c\u7136\u662f\u5149\u6e9c\u6e9c\u3001\u8d64\u88f8\u88f8\u4e00\u4e1d\u672a\u6302\uff0e \u3000\u3000\u5982\u679c\uff0e\u8bf4\u662f\u4e00\u4e1d\u672b\u6302\uff0c\u4e5f\u4e0d\u5c3d\u7136\u3002 \u3000\u3000\u56e0\u4e3a\u4e00\u2014 \u3000\u3000\u90a3\u5973\u4eba\u5728\u201c\u91cd\u8981\u90e8\u4f4d\u201d\uff0c\u8fd8\u8d34\u4e0a\u4e86\u4e00\u7247\u50cf\u6811\u53f6\u822c\u7684\u4e1c\u897f\u3002 \u3000\u3000\u738b\u5927\u5eb7\u54fc\u4e86\u58f0\u9053\uff1a\u201c\u90a3\u662f\u5757\u4ec0\u4e48\u4e1c\u897f\uff1f\u201d \u3000\u3000\u5e97\u4f19\u8ba1\u9053\uff1a\u201c\u597d\u50cf\u662f\u7247\u6811\u53f6\uff0e\u201d \u3000\u3000\u738b\u5927\u5eb7\u9053\uff1b\u201c\u6811\u53f6\u600e\u80fd\u957f\u5728\u90a3\u79cd\u5730\u65b9\uff0c\u5979\u7a76\u7adf\u662f\u68f5\u4ec0\u4e48\u6811\uff1f\u201d \u3000\u3000\u5e97\u4f19\u8ba1\u563b\u563b\u7b11\u9053\uff1a\u201c\u53ef\u80fd\u662f\u68f5\u8001\u5b9d\u6811\u5427\uff01\u201d \u3000\u3000\u738b\u5927\u5eb7\u9053\uff1a\u201c\u4ed6\u5976\u5976\u7684\u718a\uff0c\u5979\u82e5\u662f\u68f5\u8001\u5b9d\u6811\uff0c\u90a3\u6f58\u91d1\u83b2\u4e5f\u591f\u8d44\u683c\u7ad6\u4e2a\u8d1e\u8282\u724c\u574a\u4e86\uff01\u201d \u3000\u3000\u90a3\u5973\u4eba\u8131\u5149\u540e\uff0c\u4ecd\u5728\u53f0\u4e0a\u4e1c\u5954\u897f\u8dd1\u7684\uff0c\u6447\u6643\u4e86\u4e00\u9635\uff0c\u624d\u7fd8\u7740\u5c41\u80a1\u8dd1\u56de\u540e\u53f0\u3002 \u3000\u3000\u4e8e\u662f\u89c2\u4f17\u4e5f\u8ddf\u7740\u6563\u573a\u3002 \u3000\u3000\u79bb\u5f00\u620f\u56ed\u5b50\uff0c\u90a3\u5e97\u4f19\u8ba1\u72b9\u81ea\u4f59\u5174\u672a\u5c3d\uff0c\u7838\u7740\u5634\u9053\uff1a\u201c\u5927\u7237\uff0c\u4eca\u665a\u6ca1\u767d\u6765\u5427\uff1f\u8fd9\u5728\u6211\u4eec\u592a \u539f\u57ce\uff0c\u662f\u4ece\u6765\u770b\u4e0d\u5230\u7684\uff0e\u201d \u3000\u3000\u738b\u5927\u5eb7\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u5b50\u5c0f\u5fc3\u770b\u7779\u4e86\u773c\u775b\uff01\u201d \u3000\u3000\u5e97\u4f19\u8ba1\u7b11\u9053\uff0c\u201c\u5982\u679c\u80fd\u591a\u770b\u51e0\u6b21\uff0c\u5c0f\u7684\u5c31\u662f\u771f\u778e\u4e86\u773c\u775b\uff0c\u4e5f\u5fc3\u7518\u60c5\u613f\u3002\u201d \u3000\u3000\u738b\u5927\u5eb7\u9ed8\u4e86\u9ed8\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u8bf4\u5728\u522b\u5904\u89c1\u8fc7\u5979\u4e48\uff1f\u7a76\u7adf\u5728\u54ea\u91cc\u89c1\u8fc7\uff1f\u201d \u3000\u3000\u5e97\u4f19\u8ba1\u795e\u79d8\u4e00\u7b11\u9053\uff1a\u201c\u4e0d\u7792\u5927\u7237\uff0c\u521a\u624d\u8131\u88e4\u5b50\u7684\u5973\u4eba\u548c\u5979\u90a3\u51e0\u4e2a\u540c\u4f19\uff0c\u5c31\u4f4f\u5728\u6211\u4eec\u9633\u66f2 \u5ba2\u6808\u91cc\u3002\u201d \u3000\u3000\u738b\u5927\u5eb7\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\uff0c\u5979\u4f4f\u5728\u90a3\u95f4\u4e0a\u623f\uff1f\u201d \u3000\u3000\u5e97\u4f19\u8ba1\u9053\uff1a\u201c\u4f4f\u5728\u53e6\u4e00\u5904\u8de8\u9662\u91cc\u3002\u201d \u3000\u3000\u201c\u4f60\u53ef\u77e5\u9053\u5979\u7684\u5e95\u7ec6\uff1f\u201d \u3000\u3000\u201c\u8fd9\u5973\u4eba\u59d3\u8bb8\uff0e\u53eb\u4ec0\u4e48\u5c0f\u65e6\u3002\u201d \u3000\u3000\u201c\u83ab\u975e\u5979\u662f\u5531\u5c0f\u65e6\u7684\uff1f\u201d \u3000\u3000\u201c\u5c0f\u7684\u53ea\u77e5\u9053\u5979\u53eb\u8bb8\u5c0f\u65e6\uff0c\u81f3\u4e8e\u662f\u4e0d\u662f\u5531\u5c0f\u65e6\u7684\uff0c\u90a3\u5c31\u4e0d\u5f97\u800c\u77e5\u4e86\uff0c\u53cd\u6b63\u4eca\u665a\u6ca1\u542c\u5979\u5531 \u8fc7\u3002\u201d \u3000\u3000\u201c\u597d\uff0c\u660e\u5929\u4f60\u5c31\u5e26\u4ffa\u53bb\u770b\u770b\u5979\u3002\u201d \u3000\u3000\u201c\u5927\u7237\u4e3a\u4ec0\u4e48\u8981\u89c1\u5979\uff1f\u201d \u3000\u3000\u201c\u770b\u770b\u5979\u89c1\u4e86\u4ffa\u4ee5\u540e\uff0c\u662f\u5426\u80af\u8131\u88e4\u5b50\uff01\u201d \u3000\u3000\u5e97\u4f19\u8ba1\u6447\u5934\u9053\uff1a\u201c\u5982\u679c\u5979\u5728\u5e97\u91cc\u80af\u8131\uff0c\u90a3\u5c31\u7528\u4e0d\u7740\u5230\u620f\u56ed\u6765\u5566\uff01\u201d \u3000\u3000\u738b\u5927\u5eb7\u9053\uff1a\u201c\u4e0d\u7ba1\u600e\u4e48\u8bf4\uff0c\u4eca\u665a\u4ffa\u603b\u7b97\u5f00\u4e86\u773c\u754c\uff0c\u4e16\u4e0a\u7adf\u6709\u5973\u4eba\u80af\u5f53\u4f17\u8131\u88e4\u5b50\u7684\uff0c\u771f\u662f \u602a\u4e8b\u5e74\u5e74\u6709\uff0c\u5f80\u5e74\u4e0d\u5982\u4eca\u5e74\u591a\uff01\u201d \u3000\u3000\u6b21\u65e5\u65e9\u996d\u65f6\u3002 \u3000\u3000\u82d7\u7d20\u82d3\u5728\u9ea6\u5c0f\u660e\u7684\u5f15\u89c1\u4e0b\uff0c\u4f1a\u6664\u4e86\u5d02\u5c71\u4e09\u96c4\u3002 \u3000\u3000\u738b\u5927\u5eb7\u672c\u6765\u6025\u7740\u8c08\u8c08\u6628\u665a\u8bb8\u5c0f\u65e6\u5f53\u4f17\u8131\u8863\u8868\u6f14\u7684\u4e8b\uff0c\u56e0\u788d\u4e8e\u82d7\u7d20\u82d3\u5728\u573a\uff0c\u59cb\u7ec8\u96be\u4ee5\u542f \u9f7f\u3002 \u3000\u3000\u597d\u5728\u82d7\u7d20\u82d3\u5403\u5b8c\u540e\u5148\u884c\u56de\u623f\u3002 \u3000\u3000\u738b\u5927\u5eb7\u968f\u5373\u563f\u563f\u7b11\u4e86\u4e00\u9635\u9053\uff1a\u201c\u4ed6\u5976\u5976\u7684\uff0c\u4e16\u4e0a\u4e8b\u65e0\u5947\u4e0d\u6709\uff0c\u5c45\u7136\u6709\u5973\u4eba\u5f53\u4f17\u8131\u88e4\u5b50 \u7684\uff01\u201d \u3000\u3000\u7b11\u9762\u4f5b\u9c8d\u8d85\u8138\u4e0a\u4e00\u70ed\u9053\uff1a\u201c\u8001\u4e8c\uff0c\u4f60\u5728\u80e1\u8bf4\u4e9b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u738b\u5927\u5eb7\u9053\uff1a\u201c\u4f60\u8001\u5927\u662f\u5426\u6ca1\u770b\u5230\uff1f\u201d \u3000\u3000\u9c8d\u8d85\u987f\u4e86\u987f\u9053\uff1a\u201c\u6211\u5f53\u7136\u6ca1\u770b\u5230\uff0c\u4f60\u600e\u4e48\u5ffd\u7136\u95ee\u8d77\u8fd9\u4e2a\u6765\uff1f\u201d \u3000\u3000\u738b\u5927\u5eb7\u89c1\u9c8d\u8d85\u4e00\u526f\u5047\u6b63\u7ecf\u7684\u6a21\u6837\uff0c\u65e2\u7136\u4ed6\u7231\u9762\u5b50\uff0c\u81ea\u5df1\u4e5f\u5c31\u4e0d\u4fbf\u5f53\u4f17\u63ed\u7a7f\u3002\u53c8\u563f\u563f\u7b11\u4e86 \u51e0\u58f0\u9053\uff1a\u201c\u6628\u665a\u4f60\u4eec\u4e0d\u662f\u542c\u638c\u67dc\u7684\u8bf4\u8fc7\uff0c\u6709\u4e2a\u5973\u4eba\u5728\u620f\u56ed\u91cc\u8df3\u821e\u4e48\uff1f\u201d \u3000\u3000\u9c8d\u8d85\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u638c\u67dc\u7684\u662f\u597d\u50cf\u8bf4\u8fc7\u3002\u201d \u3000\u3000\u738b\u5927\u5eb7\u9053\uff1a\u201c\u90a3\u5c31\u5bf9\u5566\uff01\u6628\u665a\u4ffa\u548c\u9ea6\u8001\u5f1f\u7279\u5730\u53bb\u770b\u4e86\u4e00\u573a\u3002\u201d \u3000\u3000\u6b64\u8bed\u4e00\u51fa\uff0c\u4f7f\u5f97\u9ea6\u5c0f\u660e\u987f\u65f6\u9762\u7ea2\u8033\u8d64\uff0c\u6697\u9a82\u738b\u5927\u5eb7\u5634\u4e0a\u7f3a\u5fb7\u3002 \u3000\u3000\u9c8d\u8d85\u63a5\u7740\u95ee\u9053\uff1a\u201c\u600e\u4e48\u6837\uff0c\u8131\u4e86\u6ca1\u6709\uff1f\u201d \u3000\u3000\u738b\u5927\u5eb7\u9053\uff1a\u201c\u5f53\u7136\u8131\u5566\uff01\u4e0d\u8131\u51ed\u4ec0\u4e48\u5356\u94b1\uff01\u201d \u3000\u3000\u9c8d\u8d85\u9053\uff1a\u201c\u4f60\u63d0\u8d77\u8fd9\u4e8b\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u738b\u5927\u5eb7\u9053\uff1a\u201c\u542c\u8bf4\u90a3\u5973\u4eba\u5c31\u4f4f\u5728\u8fd9\u5bb6\u5ba2\u6808\u7684\u53e6\u4e00\u8de8\u9662\u91cc\uff0c\u54b1\u4eec\u53bb\u770b\u770b\u600e\u6837\uff1f\u201d \u3000\u3000\u9c8d\u8d85\u8fde\u5fd9\u671b\u4e86\u9b3c\u8bf8\u845b\u4e00\u773c\u9053\uff1a\u201c\u8001\u4e8c\uff0c\u8981\u4e0d\u8981\u53bb\u770b\u770b\uff1f\u201d \u3000\u3000\u9b3c\u8bf8\u845b\u6d2a\u6cfd\u54a7\u5634\u6de1\u7136\u4e00\u7b11\u9053\uff1a\u201c\u53cd\u6b63\u95f2\u7740\u6ca1\u4e8b\uff0c\u770b\u770b\u5c31\u770b\u770b\u3002\u201d \u3000\u3000\u4e8e\u662f\uff0c\u5d02\u5c71\u4e09\u96c4\u548c\u9ea6\u5c0f\u660e\u4e00\u884c\u56db\u4eba\uff0c\u6765\u5230\u4e86\u53e6\u4e00\u8de8\u9662\u3002 \u3000\u3000\u8c01\u77e5\u8de8\u9662\u91cc\uff0e\u7adf\u7136\u89c1\u4e0d\u5230\u4e00\u4e2a\u4eba\u5f71\u3002 \u3000\u3000\u5d02\u5c71\u4e09\u96c4\u548c\u9ea6\u5c0f\u660e\u5fc3\u91cc\u90fd\u6709\u6570\uff0c\u90a3\u53eb\u8bb8\u5c0f\u65e6\u7684\u5973\u4eba\u5fc5\u5b9a\u4e0d\u4f1a\u5916\u51fa\uff0c\u53ef\u80fd\u4ecd\u5728\u7761\u61d2\u89c9\u3002 \u3000\u3000\u56e0\u4e4b\uff0c\u56db\u4eba\u51b3\u5b9a\u5c31\u5728\u8de8\u9662\u4e2d\u7a0d\u7b49\uff0e \u3000\u3000\u5927\u7ea6\u4e00\u76cf\u70ed\u8336\u5de5\u592b\u8fc7\u540e\uff0c\u7531\u4e00\u95f4\u4e0a\u623f\u5185\u8d70\u51fa\u4e2a\u50cf\u50f5\u5c38\u6a21\u6837\u7684\u5e74\u8f7b\u4eba\u3002 \u3000\u3000\u90a3\u5e74\u8f7b\u4eba\u51b2\u7740\u56db\u4eba\u95ee\u9053\uff1a\u201c\u4f60\u4eec\u51e0\u4f4d\u53ef\u662f\u6765\u627e\u4eba\u7684\uff1f\u201d \u3000\u3000\u738b\u5927\u5eb7\u9053\uff1a\u201c\u6709\u4e2a\u4e0d\u7a7f\u88e4\u5b50\u8df3\u821e\u7684\u5973\u4eba\uff0c\u53ef\u662f\u4f4f\u5728\u8fd9\u91cc\uff1f\u201d \u3000\u3000\u90a3\u4eba\u4e24\u773c\u4e00\u77aa\u9053\uff1a\u201c\u4f60\u8fd9\u4eba\u600e\u4e48\u8fd9\u6837\u8bf4\u8bdd\uff1f\u201d \u3000\u3000\u738b\u5927\u5eb7\u9053\uff1a\u201c\u8001\u5b50\u8fd9\u6837\u8bf4\u8bdd\uff0c\u6709\u4ec0\u4e48\u4e0d\u5bf9\uff1f\u90a3\u5973\u4eba\u660e\u660e\u6ca1\u7a7f\u88e4\u5b50\u5728\u53f0\u4e0a\u4e71\u8e66\u4e71\u8df3\uff0c\u8001\u5b50 \u6628\u665a\u770b\u8fc7\uff01\u201d \u3000\u3000\u90a3\u5e74\u8f7b\u4eba\u60f3\u800d\u72e0\uff0c\u4f46\u4ed6\u4e00\u89c1\u738b\u5927\u5eb7\u90a3\u526f\u8eab\u6750\uff0c\u58ee\u5f97\u50cf\u5ea7\u94c1\u5854\u822c\u7684\uff0c\u5374\u53c8\u4e0d\u6562\u968f\u4fbf\u53d1\u4f5c\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0e\u6b63\u9762\u4e0a\u623f\u5df2\u4f20\u51fa\u4e00\u4e2a\u5973\u5b50\u7684\u58f0\u97f3\u9053\uff1a\u201c\u522b\u963b\u62e6\uff0c\u4ed6\u4eec\u8981\u89c1\u6211\uff0c\u5c31\u8ba9\u4ed6\u4eec\u8fdb \u6765\uff01\u201d \u3000\u3000\u738b\u5927\u5eb7\u5f53\u5148\u8fc8\u6b65\u8fdb\u5165\u6b63\u9762\u4e0a\u623f\u3002 \u3000\u3000\u9c8d\u8d85\u3001\u6d2a\u6cfd\u3001\u9ea6\u5c0f\u660e\u4e5f\u968f\u540e\u800c\u5165\u3002 \u3000\u3000\u8fd9\u5973\u4eba\u5c45\u7136\u8fde\u623f\u95e8\u4e5f\u672b\u5173\uff0c\u738b\u5927\u5eb7\u7b49\u4eba\u8fdb\u53bb\u540e\uff0e\u5979\u4ecd\u7136\u8eba\u5728\u5e8a\u4e0a\u672a\u8d77\u3002 \u3000\u3000\u53ea\u89c1\u5979\u62e5\u7740\u9526\u88ab\uff0c\u7b11\u9762\u751f\u6625\u9053\uff1a\u201c\u56db\u4f4d\u8bf7\u5750\uff0e\u5f85\u6211\u7a7f\u597d\u540e\u518d\u62db\u5f85\u5404\u4f4d\uff01\u201d \u3000\u3000\u5982\u679c\u9762\u5bf9\u4e00\u822c\u826f\u5bb6\u5987\u5973\uff0c\u4eba\u5bb6\u8981\u7a7f\u8863\u8d77\u5e8a\uff0c\u738b\u5927\u5eb7\u7b49\u4eba\u5f53\u7136\u5fc5\u987b\u56de\u907f\uff0c\u4f46\u5bf9\u4e8e\u9762\u524d\u8fd9\u5973 \u4eba\uff0c\u5374\u6ca1\u6709\u5fcc\u8bb3\u7684\u5fc5\u8981\u3002 \u3000\u3000\u8bb8\u5c0f\u65e6\u4e5f\u5b9e\u5728\u591f\u5927\u65b9\uff0c\u7a7f\u8863\u65f6\u5c45\u7136\u5148\u628a\u88ab\u5b50\u6380\u5f00\uff0c\u50cf\u5728\u6545\u610f\u70ab\u8000\u5979\u90a3\u81ea\u4ee5\u4e3a\u6027\u611f\u8bf1\u4eba\u7684 \u8eab\u6750\u3002 \u3000\u3000\u5979\u5168\u8eab\u53ea\u7740\u4e86\u4e00\u6761\u77ed\u5f97\u4e0d\u80fd\u518d\u77ed\u7684\u5185\u88e4\u548c\u4e00\u4ef6\u53c8\u7a84\u53c8\u7d27\u7684\u809a\u515c\u3002 \u3000\u3000\u8fd9\u5973\u4eba\u7a7f\u8863\u670d\u548c\u5979\u5728\u821e\u53f0\u4e0a\u7684\u52a8\u4f5c\u9887\u4e3a\u76f8\u4f3c\uff0c\u6545\u610f\u6162\u6761\u65af\u7406\u7684\uff0c\u5374\u53c8\u663e\u5f97\u6781\u6709\u8282\u594f\u611f \u7684\uff0e \u3000\u3000\u5979\u597d\u4e0d\u5bb9\u6613\uff0c\u624d\u7b97\u628a\u8863\u670d\u7a7f\u597d\uff0e \u3000\u3000\u6240\u8c13\u7a7f\u597d\uff0c\u4e5f\u53ea\u6709\u5979\u81ea\u5df1\u8ba4\u4e3a\u5982\u6b64\u800c\u5df2\uff0c\u5176\u5b9e\u6700\u5916\u9762\u4e00\u5c42\uff0c\u4ecd\u662f\u4e00\u88ad\u8584\u6c99\uff0c\u5168\u8eab\u808c\u80a4\uff0c \u9664\u4e86\u90a3\u4e00\u5173\u4e8c\u5361\u4e4b\u5916\uff0c\u90fd\u9690\u7ea6\u53ef\u89c1\uff0e \u3000\u3000\u4e0d\u7528\u8bf4\uff0c\u8fd9\u65f6\u5d02\u5c71\u4e09\u96c4\u548c\u9ea6\u5c0f\u660e\u90fd\u65e9\u5df2\u770b\u6e05\u4e86\u5979\u7684\u957f\u76f8\u3002 \u3000\u3000\u9664\u4e86\u8eab\u6750\u8fd8\u4e0d\u9519\u5916\uff0c\u8bba\u59ff\u8272\u5b9e\u5728\u8bbe\u4ec0\u4e48\uff01 \u3000\u3000\u5979\u7684\u8bf1\u4eba\u4e4b\u5904\uff0c\u4e5f\u8bb8\u53ea\u5728\u5979\u90a3\u80a1\u6deb\u90aa\u7684\u9a9a\u6c14\uff0e \u3000\u3000\u5176\u5b9e\u5d02\u5c71\u4e09\u96c4\u4e2d\uff0c\u9664\u738b\u5927\u5eb7\u5916\uff0c\u9c8d\u8d85\u548c\u6d2a\u6cfd\u5bf9\u8892\u9732\u88f8\u4f53\u7684\u8bb8\u5c0f\u65e6\u90fd\u4e0d\u6562\u591a\u770b\uff0c\u9ea6\u5c0f\u660e\u66f4 \u662f\u5728\u4e00\u65c1\u80c0\u7ea2\u4e86\u8138\u3002 \u3000\u3000\u504f\u8bb8\u5c0f\u65e6\u5374\u6beb\u4e0d\u5728\u4e4e\uff0c\u7a7f\u597d\u8863\u670d\u540e\uff0c\u6d45\u7b11\u76c8\u76c8\u9053\uff1a\u201c\u56db\u4f4d\u73b0\u5728\u8be5\u8bf4\u660e\u6765\u610f\u4e86\u5427\uff1f\u201d \u3000\u3000\u738b\u5927\u5eb7\u5658\u5634\u7b11\u9053\uff1a\u201c\u6628\u665a\u4ffa\u4eec\u5230\u620f\u56ed\u5b50\u770b\u8fc7\u59d1\u5a18\u7684\u8df3\u821e\u2026\u2026\u201d \u3000\u3000\u8bb8\u5c0f\u65e6\u4e0d\u7b49\u738b\u5927\u5eb7\u8bf4\u5b8c\uff0c\u622a\u53e3\u9053\uff1a\u201c\u4f60\u4eec\u56db\u4f4d\u53ef\u662f\u90fd\u53bb\u6367\u8fc7\u6211\u7684\u573a\uff1f\u201d \u3000\u3000\u9c8d\u8d85\u62a2\u7740\u9053\uff1a\u201c\u53ea\u6709\u4ed6\u548c\u8fd9\u4f4d\u5c0f\u8001\u5f1f\u53bb\u8fc7\uff0c\u6211\u4eec\u4e24\u4eba\u6ca1\u53bb\u3002\u201d \u3000\u3000\u8bf4\u7740\uff0c\u5e76\u671b\u5411\u6d2a\u6cfd\u4e00\u773c\uff0c\u4ee5\u793a\u4ed6\u4eec\u4e24\u4eba\u8fd8\u7b97\u6e05\u9ad8\u3002 \u3000\u3000\u8bb8\u5c0f\u65e6\u628a\u79cb\u6ce2\u8f6c\u5230\u738b\u5927\u5eb7\u8138\u4e0a\u9053\uff1a\u201c\u5927\u7237\u770b\u8fc7\u540e\u6709\u4ec0\u4e48\u6279\u8bc4\uff1f\u201d \u3000\u3000\u738b\u5927\u5eb7\u5e72\u54b3\u4e86\u58f0\u9053\uff1a\u201c\u59d1\u5a18\u7684\u8fd9\u4efd\u52c7\u6c14\uff0c\u5b9e\u5728\u8ba9\u4ffa\u8001\u738b\u4f69\u670d\uff0c\u522b\u770b\u4ffa\u8001\u738b\u662f\u4e2a\u7537\u4eba\uff0e\u80c6 \u5b50\u4e5f\u7b97\u4e0d\u5c0f\uff0e\u5982\u679c\u53eb\u4ffa\u5728\u4eba\u9762\u524d\u8131\u88e4\u5b50\uff0c\u4ffa\u8fd8\u662f\u4e0d\u6562\uff0e\u201d \u3000\u3000\u8bb8\u5c0f\u65e6\u683c\u683c\u7b11\u9053\uff1a\u201c\u8fd9\u4f4d\u738b\u5927\u7237\u7684\u8bdd\u9519\u5566\uff01\u201d \u3000\u3000\u738b\u5927\u5eb7\u9053\uff1a\u201c\u9519\u5728\u90a3\u91cc\uff1f\u201d \u3000\u3000\u8bb8\u5c0f\u65e6\u9053\uff1a\u201c\u6211\u4eec\u5728\u53f0\u4e0a\u8131\u88e4\u5b50\u662f\u4e00\u79cd\u827a\u672f\uff0c\u4f60\u4eec\u4e0d\u80fd\u7528\u6709\u8272\u7684\u773c\u5149\u53bb\u770b\uff01\u201d \u3000\u3000\u738b\u5927\u5eb7\u9053\uff1a\u201c\u8fd9\u6837\u8bf4\u4ffa\u8001\u738b\u8fdb\u8305\u5751\u8131\u88e4\u5b50\uff0c\u4e5f\u662f\u4e00\u79cd\u827a\u672f\u5566\uff01\u201d \u3000\u3000\u8bb8\u5c0f\u65e6\u8e59\u4e86\u8e59\u7709\u9053\uff1a\u201c\u90a3\u591a\u810f\uff01\u201d \u3000\u3000\u738b\u5927\u5eb7\u7b11\u9053\uff1a\u201c\u96be\u9053\u59d1\u5a18\u662f\u559d\u897f\u5317\u98ce\u957f\u5927\u7684\uff0e\u4ece\u6765\u6ca1\u8fdb\u8fc7\u8305\u623f\uff1f\u201d \u3000\u3000\u201c\u738b\u5927\u7237\uff0c\u54b1\u4eec\u8c08\u70b9\u522b\u7684\u597d\u4e0d\u597d\uff1f\u201d \u3000\u3000\u738b\u5927\u5eb7\u9053\uff1a\u201c\u4ffa\u4eec\u8ddf\u59d1\u5a18\u7d20\u4e0d\u76f8\u8bc6\uff0c\u9664\u4e86\u8c08\u6628\u665a\u8131\u88e4\u5b50\u7684\u4e8b\uff0c\u8fd8\u6709\u4ec0\u4e48\u597d\u8c08\u7684\uff0e\u59d1 \u5a18\uff1f\u201d \u3000\u3000\u8bb8\u5c0f\u65e6\u7ec8\u4e8e\u53d1\u4e86\u5a07\u55d4\u9053\uff1a\u201c\u65e2\u6ca1\u6709\u597d\u8c08\u7684\uff0c\u4f60\u4eec\u56db\u4f4d\u5c31\u8bf7\u5427\uff01\u201d \u3000\u3000\u738b\u5927\u5eb7\u4e5f\u6765\u4e86\u7765\u6c14\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4f60\u4e0d\u8ddf\u4ffa\u4eec\u8c08\u4e5f\u6ca1\u5173\u7cfb\uff0c\u4ed6\u4eec\u4e09\u4f4d\u5374\u90fd\u6ca1\u60f9\u7740\u4e86\u4f60\uff01\u201d \u3000\u3000\u8bb8\u5c0f\u65e6\u89c6\u7ebf\u8f6c\u5411\u9c8d\u8d85\u3001\u6d2a\u6cfd\u548c\u9ea6\u5c0f\u660e\uff0c\u5374\u5355\u5355\u505c\u5728\u9ea6\u5c0f\u660e\u8138\u4e0a\u9053\uff1a\u201c\u8fd9\u4f4d\u5c0f\u5144\u5f1f\u6628\u665a\u4e5f \u53bb\u6367\u8fc7\u6211\u7684\u573a\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u51b7\u51b7\u4e00\u7b11\u9053\uff1a\u201c\u5728\u4e0b\u4e0d\u662f\u4f60\u7684\u5c0f\u5144\u5f1f\uff0c\u522b\u53eb\u5f97\u90a3\u4e48\u4eb2\u70ed\uff01\u201d \u3000\u3000\u8bb8\u5c0f\u65e6\u6123\u4e86\u6123\u9053\uff1a\u201c\u4f60\u8fd9\u5e74\u8f7b\u4eba\u597d\u5927\u7684\u7765\u6c14\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u5728\u6b63\u6b63\u5f53\u5f53\u7684\u5973\u4eba\u9762\u524d\uff0c\u5728\u4e0b\u6ca1\u6709\u813e\u6c14\uff0c\u5728\u4f60\u8fd9\u79cd\u5973\u4eba\u9762\u524d\uff0c\u5728\u4e0b\u53c8\u600e\u80fd \u6ca1\u7765\u6c14\uff01\u201d \u3000\u3000\u8bb8\u5c0f\u65e6\u4e07\u4e07\u6ca1\u6599\u5230\u8fd9\u5c0f\u4f19\u5b50\u6bd4\u738b\u5927\u5eb7\u66f4\u51f6\uff0e\u5374\u4e0d\u6562\u5f97\u7f6a\u3002 \u3000\u3000\u5ffd\u89c1\u738b\u5927\u5eb7\u7ad9\u8d77\u8eab\u6765\uff0c\u5927\u58f0\u9053\uff1a\u201c\u4ffa\u5165\u4ed6\u5976\u5976\u7684\uff0c\u5f53\u4f17\u8131\u88e4\u5b50\u5c45\u7136\u8fd8\u8bf4\u662f\u79cd\u827a\u672f\uff01\u4ffa\u867d \u7136\u4e0d\u61c2\u827a\u672f\uff0c\u4f46\u5374\u77e5\u9053\u50cf\u4f60\u8fd9\u79cd\u5973\u4eba\uff0c\u6839\u672c\u662f\u65e0\u803b\uff01\u4ffa\u73b0\u5728\u9650\u4f60\u8fd9\u9a9a\u5a18\u4eec\u9a6c\u4e0a\u79bb\u5f00\u4e86\u592a\u539f \u57ce\uff0c\u514d\u5f97\u628a\u592a\u539f\u57ce\u5f04\u80ae\u810f\u4e86\u3002\u5982\u679c\u4eca\u665a\u4ecd\u5728\u90a3\u5bb6\u620f\u56ed\u5b50\u8131\uff0c\u8001\u5b50\u5c31\u51b3\u5b9a\u53bb\u7838\u56ed\u5b50\uff01\u201d \u3000\u3000\u8bf4\u5b8c\u8bdd\uff0c\u738b\u5927\u5eb7\u5c31\u5927\u6b65\u5f80\u95e8\u5916\u8d70\u3002 \u3000\u3000\u9c8d\u8d85\u3001\u6d2a\u6cfd\u548c\u9ea6\u5c0f\u660e\u53ea\u597d\u4e5f\u8ddf\u7740\u8d77\u8eab\u51fa\u95e8\u3002 \u3000\u3000\u6765\u5230\u5929\u4e95\u3002 \u3000\u3000\u90a3\u50f5\u5c38\u6a21\u6837\u7684\u7537\u5b50\uff0c\u6b63\u7ad9\u5728\u53a2\u623f\u95e8\u53e3\uff0c\u867d\u7136\u4e0d\u6562\u8bf4\u4ec0\u4e48\uff0c\u5374\u77aa\u7740\u4e00\u5bf9\u9f20\u76ee\uff0c\u5411\u738b\u5927\u5eb7\u6012 \u76ee\u800c\u89c6\u3002 \u5367\u9f99\u751f\u300a\u5999\u7edd\u5929\u9999\u300b \u7b2c \u516b \u56de\u3000\u591c\u9014\u5de7\u9047 \u3000\u3000\u738b\u5927\u5eb7\u72e0\u9a82\u9053\uff1a\u201c\u738b\u516b\u86cb\uff01\u4f60\u662f\u5426\u4e0d\u670d\u6c14\uff1f\u8001\u5b50\u6700\u6068\u7684\u5c31\u662f\u4f60\u4eec\u8fd9\u4e9b\u5403\u8f6f\u996d\u7684\u72d7\u4e1c \u897f\uff01\u201d \u3000\u3000\u79bb\u5f00\u4e86\u8de8\u9662\u3002 \u3000\u3000\u9c8d\u8d85\u8f7b\u54b3\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u8001\u4e09\uff0c\u4f60\u662f\u600e\u4e48\u5566\uff1f\u201d \u3000\u3000\u738b\u5927\u5eb7\u9053\uff1a\u201c\u4ffa\u8fd8\u4e0d\u662f\u597d\u597d\u7684\uff01\u201d \u3000\u3000\u9c8d\u8d85\u9053\uff1a\u201c\u54b1\u4eec\u5230\u8fd9\u8fb9\u6765\uff0c\u4e0d\u8fc7\u662f\u597d\u5947\uff0c\u4e5f\u987a\u4fbf\u627e\u4e50\u5b50\uff0c\u4f60\u5374\u5ffd\u7136\u53d1\u4e86\u72d7\u718a\u813e\u6c14\uff0c\u4f55\u82e6 \u5462\uff1f\u201d \u3000\u3000\u738b\u5927\u5eb7\u54fc\u4e86\u58f0\u9053\uff1a\u201c\u4ffa\u65e9\u5c31\u770b\u4e0d\u60ef\u5566\uff01\u201d \u3000\u3000\u201c\u65e2\u7136\u770b\u4e0d\u60ef\uff0c\u6628\u665a\u4e3a\u4ec0\u4e48\u8fd8\u8981\u82b1\u94b1\u53bb\u770b\uff1f\u201d \u3000\u3000\u201c\u8ddf\u4f60\u4e00\u6837\uff0c\u8fd8\u4e0d\u662f\u4e3a\u4e86\u597d\u5947\u3002\u201d \u3000\u3000\u9c8d\u8d85\u4e5f\u5c31\u4e0d\u518d\u591a\u8bf4\u3002 \u3000\u3000\u56de\u5230\u6240\u4f4f\u7684\u8de8\u9662\u540e\uff0c\u7ecf\u8fc7\u4e00\u756a\u5546\u8bae\u3002 \u3000\u3000\u51b3\u5b9a\u4e0d\u5728\u592a\u539f\u505c\u7559\uff0c\u4ee5\u4fbf\u5c3d\u901f\u8d76\u5230\u5317\u5cb3\u8ff7\u8e2a\u8c37\u3002\u5d02\u5c71\u4e09\u96c4\u56e0\u4e3a\u8fd8\u8981\u5230\u592a\u539f\u5317\u65b9\u7684\u4e91\u4e2d\u5c71 \u987a\u4fbf\u8bbf\u53cb\uff0c\u9ea6\u5c0f\u660e\u548c\u82d7\u7d20\u82d3\u53ea\u597d\u548c\u4ed6\u4eec\u6682\u65f6\u5206\u624b\u3002 \u3000\u3000\u79bb\u5f00\u592a\u539f\uff0c\u9ea6\u5c0f\u660e\u548c\u82d7\u7d20\u82d3\u5f80\u5b9a\u8944\u65b9\u5411\u8fdb\u53d1\u3002 \u3000\u3000\u8fd9\u6bb5\u8def\u6709\u4e24\u767e\u91cc\u5de6\u53f3\u8def\u7a0b\uff0c\u5fc5\u987b\u7d27\u8d76\uff0c\u624d\u80fd\u4e0d\u803d\u8bef\u665a\u95f4\u5728\u5b9a\u8944\u6295\u5bbf\u3002 \u3000\u3000\u8fc7\u4e86\u884c\u5cad\u5173\uff0e\u592a\u9633\u5df2\u7ecf\u504f\u897f\u3002\u9ea6\u5c0f\u660e\u5ffd\u7136\u60f3\u8d77\u6709\u5173\u82d7\u7d20\u5170\u5df2\u505a\u4e86\u76df\u4e3b\u592b\u4eba\u4e4b\u4e8b\uff0e\u3000\u2014\u76f4 \u5c1a\u672a\u544a\u77e5\u82d7\u7d20\u82d3\uff0e\u5bf9\u82d7\u7d20\u82d3\u6765\u8bf4\uff0c\u8fd9\u662f\u4ef6\u5927\u4e8b\uff0c\u5fc5\u987b\u4e8b\u5148\u8ba9\u5979\u77e5\u9053\u3002 \u3000\u3000\u201c\u82d7\u59d1\u5a18\uff0e\u4f60\u53ef\u77e5\u9053\u4ee4\u59ca\u5728\u8ff7\u8e2a\u8c37\u76ee\u524d\u662f\u4ec0\u4e48\u8eab\u4efd\uff1f\u201d \u3000\u3000\u82d7\u7d20\u82d3\u89c1\u9ea6\u5c0f\u660e\u7a81\u5982\u5176\u6765\u6709\u6b64\u4e00\u95ee\uff0c\u867d\u6709\u4e9b\u60ca\u5947\uff0c\u5374\u5e76\u672a\u8fc7\u5206\u8be7\u5f02\uff0e\u987a\u53e3\u9053\uff1a\u201c\u542c\u8bf4\u6211 \u59d0\u59d0\u4ece\u524d\u662f\u8ddf\u968f\u80e1\u3001\u8c37\u4e24\u4f4d\u76df\u4e3b\u7684\uff0c\u80e1\u8c37\u4e24\u4f4d\u76df\u4e3b\u6b7b\u540e\uff0c\u5979\u4e00\u5b9a\u4ecd\u548c\u4f60\u4eec\u7ad9\u5728\u540c\u4e00\u65b9\u9762\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u6447\u6447\u5934\uff1a\u201c\u4f60\u731c\u9519\u5566\uff0c\u4ee4\u59ca\u73b0\u5728\u548c\u6211\u4eec\u6b63\u662f\u5bf9\u7acb\u7684\uff01\u201d \u3000\u3000\u82d7\u7d20\u82d3\u5403\u4e86\u4e00\u60ca\u9053\uff1a\u201c\u7a76\u7adf\u600e\u4e48\u4e00\u56de\u4e8b\uff1f\u4f60\u5feb\u8bf4\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u6211\u4eec\u8ff7\u8e2a\u8c37\u5206\u4e24\u6d3e\uff0c\u81ea\u4ece\u80e1\u3001\u8c37\u4e8c\u4f4d\u76df\u4e3b\u6b7b\u540e\uff0c\u4e24\u6d3e\u4e4b\u95f4\uff0c\u66f4\u662f\u58c1\u5792\u5206 \u660e\u3002\u201d \u3000\u3000\u82d7\u7d20\u82d3\u63a5\u9053\uff1a\u201c\u6211\u5df2\u542c\u4f60\u8bf4\u8fc7\uff0c\u4e00\u6d3e\u4ee5\u970d\u5143\u4f3d\u4e3a\u9996\u3001\u4ed6\u6b63\u662f\u76ee\u524d\u7684\u76df\u4e3b\uff0c\u3000\u2014\u6d3e\u4ee5\u949f\u4e00 \u8c6a\u4e3a\u9996\uff0c\u4e5f\u5c31\u662f\u4f60\u4eec\u8fd9\u4e00\u6d3e\u3002\u83ab\u975e\u6211\u59d0\u59d0\u76ee\u524d\u662f\u970d\u5143\u4f3d\u7684\u4e00\u6d3e\uff1f\u5979\u600e\u4f1a\u4e0d\u8ddf\u4f60\u4eec\u5728\u2014\u8d77 \u5462\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u51b7\u54fc\u4e00\u58f0\u9053\uff1a\u201c\u4ee4\u59ca\u76ee\u524d\u4e0d\u4f46\u662f\u970d\u5143\u4f3d\u90a3\u4e00\u6d3e\uff0c\u800c\u4e14\u8fd8\u505a\u4e86\u90a3\u8001\u738b\u516b\u86cb\u7684\u8001\u5a46\uff0c \u8bf4\u5f97\u597d\u542c\u4e00\u70b9\uff0c\u5979\u5df2\u7ecf\u662f\u4f4d\u538b\u585e\u592b\u4eba\u4e86\uff01\u201d \u3000\u3000\u82d7\u7d20\u82d3\u51e0\u4e4e\u4e0d\u6562\u76f8\u4fe1\u81ea\u5df1\u7684\u8033\u6735\uff0c\u554a\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u4f60\u600e\u4e48\u4e0d\u65e9\u8ddf\u6211\u8bf4\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u751f\u7c73\u5df2\u6210\u719f\u996d\uff0e\u6211\u518d\u65e9\u544a\u8bc9\u4f60\uff0c\u4f60\u4e5f\u6ca1\u529e\u6cd5\u8ba9\u4e8b\u60c5\u6539\u53d8\u8ba8\u6765\uff0e\u201d \u3000\u3000\u201c\u6709\u8fd9\u79cd\u4e8b\uff1f\u6211\u59d0\u59d0\u7edd\u4e0d\u53ef\u80fd\u662f\u8fd9\u79cd\u4eba\uff01\u201d \u3000\u3000\u201c\u6211\u4ece\u524d\u4e5f\u89c9\u5f97\u5979\u4e0d\u662f\u8fd9\u79cd\u4eba\uff0c\u4f46\u5374\u504f\u504f\u505a\u51fa\u8fd9\u79cd\u4e8b\uff01\u201d \u3000\u3000\u201c\u6211\u60f3\u5176\u4e2d\u4e00\u5b9a\u6709\u539f\u56e0\u3002\u201d \u3000\u3000\u201c\u5f53\u7136\u6709\u539f\u56e0\u3002\u201d \u3000\u3000\u201c\u4f60\u8bf4\u4ec0\u4e48\u539f\u56e0\uff1f\u201d \u3000\u3000\u201c\u90a3\u4e2a\u5973\u4eba\u4e0d\u559c\u6b22\u5f80\u9ad8\u679d\u4e0a\u722c\uff0c\u5979\u89c1\u80e1\u3001\u8c37\u4e24\u4f4d\u76df\u4e3b\u5df2\u6b7b\uff0c\u6211\u4eec\u8fd9\u4e00\u6d3e\u843d\u4e86\u52bf\uff0c\u5979\u5f53\u7136 \u8981\u6295\u5411\u970d\u5143\u4f3d\u3002\u4f55\u51b5\u8fd9\u6447\u8eab\u4e00\u53d8\u5c31\u662f\u76df\u4e3b\u592b\u4eba\uff0c\u8fd9\u79cd\u597d\u4e8b\u8c01\u4e0d\u60f3\u5e72\uff01\u201d \u3000\u3000\u201c\u53ef\u662f\u6211\u59d0\u86c6\u2026\u2026\u201d \u3000\u3000\u201c\u53c8\u8981\u8bf4\u5979\u5e76\u975e\u90a3\u79cd\u4eba\uff0c\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u82d7\u7d20\u82d3\u8e59\u7709\u7f04\u9ed8\u4e86\u534a\u664c\u9053\uff1a\u201c\u4e0d\u7ba1\u600e\u4e48\u8bf4\uff0c\u7b49\u89c1\u4e86\u5979\u4ee5\u540e\uff0e\u6211\u603b\u4f1a\u628a\u539f\u56e0\u5f04\u660e\u767d\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u90d1\u91cd\u5176\u4e8b\u7684\u9053\uff1a\u201c\u6211\u5fc5\u987b\u63d0\u9192\u4f60\uff0c\u5c06\u6765\u89c1\u4e86\u5979\uff0c\u4e00\u5b9a\u8981\u8c28\u614e\u884c\u4e8b\uff0c\u522b\u4ee5\u4e3a\u4eb2\u59ca\u59b9 \u53ef\u4ee5\u65e0\u8bdd\u4e0d\u8c08\uff0c\u4e00\u4e0d\u5c0f\u5fc3\u5f04\u51fa\u6bdb\u75c5\uff0c\u90a3\u5c31\u540e\u6094\u83ab\u53ca\u5566\uff01\u201d \u3000\u3000\u6b63\u8bf4\u5230\u8fd9\u91cc\uff0c\u5ffd\u542c\u8033\u8fb9\u54cd\u8d77\u4e00\u4e2a\u82cd\u8001\u6c89\u6d51\u7684\u58f0\u97f3\u9053\uff1a\u201c\u5c0f\u5a03\u513f\uff0c\u4f60\u600e\u4e48\u5230\u4e86\u8fd9\u91cc\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u731b\u5403\u4e00\u60ca\uff0c\u62ac\u5934\u770b\u53bb\uff0c\u4e0d\u77e5\u4ec0\u4e48\u65f6\u5019\uff0c\u9762\u524d\u4e08\u4f59\u4e4b\u5916\uff0e\u7adf\u51fa\u73b0\u4e86\u4e00\u540d\u4e71\u53d1\u62ab\u80a9\uff0c \u80f8\u5782\u957f\u9aef\u7684\u7070\u888d\u8001\u8005\u3002 \u3000\u3000\u8fd9\u4eba\u8d6b\u7136\u662f\u654c\u53cb\u96be\u5206\u7684\u5e9e\u58eb\u51b2\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u867d\u548c\u82d7\u7d20\u82d3\u4e00\u8def\u8c08\u7740\u8bdd\uff0c\u4f46\u5bf9\u5468\u8fd1\u4e8b\u7269\uff0c\u4ecd\u7136\u65f6\u65f6\u7559\u610f\u3002 \u3000\u3000\u800c\u5e9e\u58eb\u51b2\u5374\u80fd\u5728\u4ed6\u6beb\u65e0\u60ca\u89c9\u4e4b\u4e0b\uff0c\u65e0\u58f0\u65e0\u606f\u51fa\u73b0\u5728\u9762\u524d\uff0c\u5185\u5fc3\u53c8\u600e\u80fd\u4e0d\u6697\u751f\u51db\u9a87\uff0e \u3000\u3000\u4ed6\u5fc3\u91cc\u6709\u6570\uff0c\u8fd9\u4f4d\u8001\u602a\u7269\u7684\u6b66\u529f\uff0c\u5c24\u5728\u9634\u624b\u4e00\u9b54\u4e4b\u4e0a\u3002 \u3000\u3000\u5728\u4ed6\u9762\u524d\uff0c\u81ea\u5df1\u7edd\u4e0d\u80fd\u901e\u5f3a\uff0c\u53ef\u89c1\u9ea6\u5c0f\u660e\u73b0\u5728\u4e5f\u5f00\u59cb\u5b66\u4e56\u4e86\u3002 \u3000\u3000\u5f53\u4e0b\uff0c\u9ea6\u5c0f\u660e\u505c\u4e0b\u811a\u6b65\uff0c\u62b1\u62f3\u4e00\u793c\u9053\uff1a\u201c\u96be\u5f97\u53c8\u9047\u4e0a\u4e86\u5e9e\u8001\u524d\u8f88\uff0c\u8001\u524d\u8f88\u8981\u5230\u54ea\u91cc\u53bb \u554a\uff1f\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u5927\u523a\u523a\u5730\u77a5\u4e86\u9ea6\u5c0f\u660e\u4e00\u773c\u9053\uff1a\u201c\u8fd8\u6ca1\u56de\u7b54\u8001\u592b\u7684\u95ee\u8bdd\uff0c\u5374\u5148\u76d8\u95ee\u8d77\u8001\u592b\u6765\u4e86\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u7b11\u9053\uff1a\u201c\u90a3\u5c31\u5148\u7b54\u590d\u4f60\u7684\u95ee\u8bdd\uff0c\u665a\u8f88\u662f\u5949\u547d\u5230\u592a\u539f\u529e\u4e8b\uff0c\u529e\u4e8b\u5df2\u6bd5\uff0c\u6b63\u8981\u8d76\u56de\u8ff7 \u8e2a\u8c37\u3002\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u4f3c\u4e4e\u65e0\u5fc3\u8be2\u95ee\u9ea6\u5c0f\u660e\u6240\u529e\u4f55\u4e8b\uff0c\u54fc\u4e86\u58f0\u9053\uff1a\u201c\u4f60\u8981\u5bf9\u8001\u592b\u8bf4\u5b9e\u8bdd\uff0c\u82e5\u6709\u534a\u70b9\u4e0d \u5b9e\uff0c\u90a3\u5c31\u522b\u602a\u8001\u592b\u624b\u6bb5\u6bd2\u8fa3\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u8f6c\u4e86\u8f6c\u773c\u73e0\uff0c\u5fcd\u4e0d\u4f4f\u7b11\u9053\uff1a\u201c\u4f60\u597d\u50cf\u7cbe\u795e\u4e0d\u592a\u6b63\u5e38\u5427\uff1f\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u543c\u9053\uff1a\u201c\u4f60\u8bf4\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u6211\u521a\u624d\u8bf4\u7684\u672c\u6765\u5c31\u662f\u5b9e\u8bdd\uff0c\u5982\u679c\u4f60\u8fd8\u6709\u8bdd\u8981\u95ee\uff0c\u4e5f\u8be5\u7b49\u6211\u56de\u7b54\u4ee5\u540e\u65ad\u660e\u771f \u5047\u518d\u53d1\u813e\u6c14\uff0c\u4f60\u73b0\u5728\u4e00\u4e0a\u6765\u5c31\u2018\u5468\u5434\u90d1\u738b\u2019\u7684\u6765\u8fd9\u4e00\u5957\uff0c\u7b97\u4ec0\u4e48\u660e\u5802\uff1f\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u4e07\u6ca1\u6599\u5230\u7adf\u88ab\u2014\u4e2a\u540e\u751f\u665a\u8f88\u5012\u6253\u4e86\u2014\u8019\u3002 \u3000\u3000\u4ed6\u867d\u4e00\u5411\u6027\u60c5\u9ad8\u50b2\uff0c\u5374\u5e76\u975e\u4e0d\u8bb2\u7406\u4e4b\u4eba\uff0c\u987f\u4e86\u4e00\u987f\u9053\uff1a\u201c\u6211\u95ee\u4f60\uff0e\u4f60\u4eec\u628a\u8c37\u5bd2\u9999\u7684\u9057\u9ab8 \u5f04\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u6487\u6487\u5634\u9053\uff1a\u201c\u59d3\u5e9e\u7684\uff0e\u4f60\u8981\u522b\u4eba\u8bf4\u5b9e\u8bdd\uff0c\u81ea\u5df1\u5374\u662f\u4e2a\u8bf4\u8c0e\u5bb6\uff0c\u8fd9\u53c8\u7b97\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u795e\u8272\u2014\u7a92\u9053\uff1a\u201c\u4f60\u8fd9\u8bdd\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u8c37\u76df\u4e3b\u7684\u9057\u4f53\uff0c\u660e\u660e\u88ab\u4f60\u5e26\u8d70\uff0c\u4f60\u5374\u8981\u6765\u95ee\u6211\uff01\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u795e\u8272\u4e00\u53d8\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u90a3\u5929\u4f60\u4eec\u771f\u7684\u6ca1\u627e\u5230\u8c37\u5bd2\u9999\u7684\u5c38\u9996\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u8c37\u76df\u4e3b\u7684\u9057\u4f53\u88ab\u4f60\u85cf\u4e86\u8d77\u6765\uff0c\u6211\u4eec\u5f53\u7136\u627e\u4e0d\u5230\u3002\u201d \u3000\u3000\u201c\u8fd9\u8bdd\u53ef\u662f\u771f\u7684\uff1f\u201d \u3000\u3000\u201c\u96be\u9053\u8fd8\u903c\u6211\u53d1\u8a93\u4e0d\u6210\uff1f\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u5446\u4e86\u534a\u664c\uff0e\u731b\u5730\u4e00\u8dfa\u811a\u9053\uff1a\u201c\u8fd9\u6837\u8bf4\u6765\uff0c\u8001\u592b\u8fd8\u662f\u8981\u518d\u56de\u5929\u53f0\u4e07\u82b1\u5bab\u4e00\u8d9f\u4e86\uff01\u201d \u3000\u3000\u201c\u53bb\u627e\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u627e\u56de\u8c37\u5bd2\u9999\u7684\u9057\u4f53\uff0c\u8fd9\u662f\u5979\u4e34\u7ec8\u524d\u4ea4\u4ee3\u7684\uff0c\u8001\u592b\u4e0d\u80fd\u8f9c\u8d1f\u5979\u7684\u5631\u6258\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u89c1\u5e9e\u58eb\u51b2\u771f\u60c5\u6fc0\u52a8\uff0c\u4e0d\u50cf\u5728\u4f2a\u88c5\uff0c\u5401\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u65e2\u7136\u8c37\u76df\u4e3b\u7684\u9057\u4f53\u771f\u7684\u4e0d\u89c1 \u4e86\uff0c\u4f60\u56de\u4e07\u82b1\u5bab\u53c8\u6709\u4f55\u7528\uff1f\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u6ca1\u7528\uff1f\u201d \u3000\u3000\u201c\u4e8b\u60c5\u5df2\u7ecf\u8fc7\u4e86\u90a3\u4e48\u4e45\uff0c\u8c37\u76df\u4e3b\u7684\u9057\u4f53\u65e9\u5df2\u53d8\u6210\u4e00\u5806\u67af\u9aa8\uff0c\u7eb5\u7136\u4e0d\u662f\u88ab\u4eba\u76d7\u8d70\uff0c\u53c8\u5230\u54ea \u91cc\u53bb\u627e\uff1f\u201d \u3000\u3000\u201c\u8bf4\u7684\u4e5f\u662f\uff0c\u53ef\u662f\u2026\u2026\u201d \u3000\u3000\u201c\u53ef\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u90a3\u591c\u8001\u592b\u660e\u660e\u628a\u5979\u7684\u9057\u4f53\u653e\u5728\u8def\u65c1\u4e00\u5757\u5927\u9752\u77f3\u4e0a\uff0c\u4e0d\u5927\u4e00\u4f1a\u5de5\u592b\u5c31\u5df2\u4e0d\u89c1\uff0c\u660e\u660e\u662f\u88ab \u4eba\u76d7\u8d70\u3002\u201d \u3000\u3000\u201c\u5929\u53f0\u5c71\u6bd2\u86c7\u731b\u517d\u51fa\u6ca1\u65e0\u5e38\uff0c\u4e07\u4e00\u8c37\u76df\u4e3b\u7684\u9057\u4f53\u662f\u88ab\u731b\u864e\u91ce\u72fc\u53fc\u8d70\u4e86\u5462\uff1f\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u542c\u5230\u8fd9\u91cc\uff0c\u5446\u4e86\u4e00\u5446\uff0e\u9894\u9996\u9053\uff1a\u201c\u8bf4\u5f97\u6709\u7406\uff0e\u9664\u6b64\u4e4b\u5916\uff0c\u8001\u592b\u5b9e\u5728\u60f3\u4e0d\u51fa\u5176\u4ed6\u539f \u56e0\uff01\u201d \u3000\u3000\u5ffd\u89c1\u4ed6\u53cc\u988a\u4e00\u9635\u62bd\u6410\uff0c\u63a5\u7740\u987f\u8db3\u6376\u80f8\u9053\uff1a\u201c\u6211\u771f\u8be5\u6b7b\uff01\u6211\u771f\u8be5\u6b7b\uff01\u4e00\u65f6\u5927\u610f\uff0e\u7adf\u4f7f\u5f97\u5979 \u6b7b\u540e\u5c38\u9aa8\u4e0d\u5168\uff0c\u82e5\u5c06\u6765\u6709\u4e00\u5929\u5728\u9634\u66f9\u5730\u5e9c\u89c1\u4e86\u9762\uff0c\u8ba9\u6211\u62ff\u4ec0\u4e48\u5411\u5979\u4ea4\u4ee3\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u89c1\u4ed6\u8fd9\u526f\u6a21\u6837\uff0c\u6697\u89c9\u6709\u4e9b\u597d\u7b11\u3002 \u3000\u3000\u53ea\u597d\u5b89\u6170\u9053\uff1a\u201c\u4e8b\u60c5\u5df2\u7ecf\u53d1\u751f\u4e86\uff0c\u8001\u524d\u8f88\u81ea\u8d23\u53c8\u6709\u4f55\u7528\uff0c\u4f60\u73b0\u5728\u51c6\u5907\u5230\u54ea\u91cc\u53bb\uff1f\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u9053\uff1a\u201c\u8001\u592b\u56e0\u5bfb\u4e0d\u7740\u8c37\u5bd2\u660f\u9057\u4f53\uff0c\u5f53\u7136\u4e0d\u80fd\u56de\u957f\u767d\u5c71\uff0c\u4e00\u8def\u884c\u6765\uff0e\u76ee\u7684\u662f\u8981\u53bb\u8d9f \u5317\u5cb3\u8ff7\u8e2a\u8c37\u3002\u201d \u3000\u3000\u201c\u5230\u8ff7\u8e2a\u8c37\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u8001\u592b\u539f\u4ee5\u4e3a\u8c37\u5bd2\u9999\u7684\u9057\u4f53\u5728\u4f60\u4eec\u624b\u4e2d\uff0e\u6240\u4ee5\u624d\u8981\u5230\u8ff7\u8e2a\u8c37\uff0c\u73b0\u5728\u5df2\u660e\u771f\u76f8\uff0c\u90a3\u5c31\u7528\u4e0d \u7740\u53bb\u4e86\u3002\u201d \u3000\u3000\u201c\u600e\u4e48\u4e0d\u89c1\u7fce\u513f\uff1f\u201d \u3000\u3000\u201c\u8001\u592b\u56e0\u5e26\u7740\u4ed6\u884c\u52a8\u4e0d\u4fbf\uff0c\u5df2\u6682\u65f6\u4ea4\u4e0e\u2014\u4f4d\u597d\u53cb\u4ee3\u4e3a\u7167\u987e\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u5fc3\u4e2d\u2014\u52a8\uff0c\u6697\u9053\uff1a\u201c\u8fd9\u8001\u602a\u7269\u7684\u6b66\u529f\u9ad8\u4e0d\u53ef\u6d4b\uff0c\u6211\u4f55\u4e0d\u8bf4\u52a8\u4ed6\u540c\u5230\u8ff7\u8e2a\u8c37\uff0c\u85c9\u4ed6 \u4e4b\u529b\u628a\u970d\u5143\u4f3d\u9664\u53bb\uff0e\u5c82\u4e0d\u6bd4\u5f97\u5230\u2018\u5411\u5fc3\u9732\u2019\u89e3\u836f\u66f4\u597d\u2026\u2026\u201d \u3000\u3000\u4ed6\u5fc3\u5ff5\u8f6c\u52a8\uff0c\u7acb\u5373\u8f7b\u54b3\u4e86\u58f0\u9053\uff1a\u201c\u8001\u524d\u8f88\u65e2\u7136\u6682\u65f6\u4e0d\u56de\u957f\u767d\u5c71\uff0c\u4f55\u4e0d\u5c31\u5230\u8ff7\u8e2a\u8c37\u4e00 \u8d9f\u3002\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u9053\uff1a\u201c\u5c0f\u5a03\u513f\uff0c\u4f60\u53ef\u662f\u60f3\u628a\u8001\u592b\u8bf1\u5230\u8ff7\u8e2a\u8c37\uff0c\u518d\u60f3\u529e\u6cd5\u628a\u8001\u592b\u9664\u53bb\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u4e0d\u89c9\u557c\u7b11\u7686\u975e\uff0c\u9053\uff1a\u201c\u8001\u524d\u8f88\u60f3\u5230\u54ea\u91cc\u53bb\u4e86\uff0c\u8c01\u4e0d\u77e5\u9053\u4f60\u662f\u4f4d\u5927\u4e49\u51db\u7136\u7684\u524d\u8f88\u9ad8 \u4eba\u3002\u665a\u8f88\u60f3\u8bf7\u4f60\u5230\u8ff7\u8e2a\u8c37\u53bb\uff0c\u76ee\u7684\u4e0d\u5916\u662f\u5e0c\u671b\u4f60\u505a\u4e00\u4ef6\u9664\u6076\u9504\u5978\u7684\u4e8b\u3002\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u54fc\u4e86\u58f0\u9053\uff1a\u201c\u6709\u5c41\u5c31\u75db\u75db\u5feb\u5feb\u7684\u653e\uff0c\u5c11\u5728\u8001\u592b\u8138\u4e0a\u8d34\u91d1\uff01\u201d \u3000\u3000\u201c\u8001\u524d\u8f88\u53ef\u77e5\u9053\u76ee\u524d\u7684\u7eff\u6797\u76df\u4e3b\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u8001\u592b\u7ba1\u4ed6\u662f\u8c01\uff01\u201d \u3000\u3000\u201c\u4e5f\u8bb8\u5927\u5927\u51fa\u4e4e\u4f60\u7684\u610f\u5916\u5427\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u89c1\u5e9e\u58eb\u51b2\u6bcf\u53e5\u8bdd\u90fd\u786c\u5f97\u50cf\u5757\u7caa\u5751\u91cc\u7684\u77f3\u5934\u3002\u3000\u2014\u5411\u5014\u5f3a\u7684\u4ed6\uff0c\u4e0d\u5f97\u4e0d\u4ecd\u6781\u529b\u8010\u7740 \u6027\u5b50\u9053\uff1a\u201c\u8001\u524d\u8f88\uff0c\u6211\u82e5\u8bf4\u51fa\u6765\uff0c\u4f60\u4e00\u5b9a\u4e5f\u662f\u5fc3\u91cc\u4e0d\u670d\uff01\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u9053\uff1a\u201c\u4f60\u8981\u8bf4\u5c31\u8bf4\uff0c\u4f55\u5fc5\u565c\u55e6\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u73b0\u5728\u7684\u7eff\u6797\u76df\u4e3b\u662f\u970d\u5143\u4f3d\uff0c\u4f60\u670d\u4e48\uff1f\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u54fc\u4e86\u58f0\u9053\uff1a\u201c\u8001\u592b\u65e2\u4e0d\u662f\u7eff\u6797\u4e2d\u4eba\uff0c\u4e5f\u4e0d\u7ba1\u7eff\u6797\u76df\u4e3b\u4e2d\u4e8b\uff0c\u4f60\u4eec\u8c01\u5f53\u7eff\u6797\u76df\u4e3b\u90fd \u662f\u4e00\u6837\uff0c\u6211\u6709\u4ec0\u4e48\u670d\u4e0d\u670d\u7684\uff1f\u201d \u3000\u3000\u201c\u867d\u7136\u8001\u524d\u8f88\u4e0d\u662f\u7eff\u6797\u4e2d\u4eba\uff0c\u4f46\u7eff\u6797\u76df\u4e3b\u603b\u662f\u6709\u597d\u6709\u574f\uff0c\u5982\u679c\u771f\u6709\u4e00\u4f4d\u5fb7\u9ad8\u671b\u91cd\u7684\u7eff\u6797 \u76df\u4e3b\uff0c\u76f8\u4fe1\u8c01\u90fd\u5e94\u670d\u4ed6\u3002\u201d \u3000\u3000\u201c\u4f60\u4eec\u7eff\u6797\u4e4b\u4e2d\uff0c\u4ec0\u4e48\u65f6\u5019\u51fa\u73b0\u8fc7\u8fd9\u6837\u4e00\u4f4d\u76df\u4e3b\uff1f\u201d \u3000\u3000\u201c\u80e1\u3001\u8c37\u4e24\u4f4d\u5df2\u6545\u7684\u76df\u4e3b\uff0e\u5c31\u503c\u5f97\u4eba\u4eba\u94a6\u670d\uff01\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u4e0d\u89c9\u6380\u9aef\u5475\u5475\u5927\u7b11\u9053\uff1a\u201c\u80e1\u67cf\u9f84\u548c\u8c37\u5bd2\u9999\uff0c\u5728\u8001\u592b\u9762\u524d\uff0c\u8fd8\u4e0d\u7167\u6837\u662f\u4e24\u4e2a\u540e\u751f\u665a \u8f88\uff0c\u4e16\u4e0a\u53ef\u6709\u8001\u5b50\u670d\u513f\u5b50\u7684\u9053\u7406\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u6447\u6447\u5934\u9053\uff1a\u201c\u5176\u5b9e\u8fd9\u4e0d\u662f\u8001\u524d\u8f88\u670d\u4e0d\u670d\u7684\u95ee\u9898\uff0c\u800c\u662f\u665a\u8f88\u4eec\u5bf9\u970d\u5143\u4f3d\u4e0d\u670d\u3002\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u9053\uff1a\u201c\u90a3\u662f\u4f60\u4eec\u81ea\u5df1\u7684\u4e8b\uff0c\u4e0e\u8001\u592b\u4f55\u5e72\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u6b63\u8272\u9053\uff1a\u201c\u8001\u524d\u8f88\u8fd9\u6837\u8bf4\u8bdd\uff0c\u5c31\u5927\u5927\u4e0d\u8be5\u4e86\uff01\u201d \u3000\u3000\u201c\u8001\u592b\u7684\u8bdd\u6709\u4ec0\u4e48\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u201c\u8001\u524d\u8f88\u5fc5\u5b9a\u77e5\u9053\uff0c\u7eff\u6797\u4e2d\u4eba\uff0c\u4f55\u6b62\u5343\u5343\u4e07\u4e07\uff0c\u8ba9\u4e00\u4e2a\u5fc3\u672f\u4e0d\u6b63\u4e4b\u4eba\u4e3b\u6301\u5927\u5c40\uff0c\u52bf\u5fc5\u7978 \u5bb3\u65e0\u7a77\uff0c\u5c06\u6765\u53d7\u5bb3\u4e4b\u4eba\uff0c\u4e5f\u52bf\u5fc5\u5343\u5343\u4e07\u4e07\u3002\u201d \u3000\u3000\u201c\u5c0f\u5a03\u513f\uff0c\u8bf4\u4e86\u534a\u5929\uff0c\u4f60\u53ef\u662f\u5e0c\u671b\u85c9\u8001\u592b\u4e4b\u529b\uff0c\u628a\u970d\u5143\u4f3d\u8d76\u4e0b\u76df\u4e3b\u5b9d\u5ea7\uff1f\u201d \u3000\u3000\u201c\u8001\u524d\u8f88\u65e2\u5df2\u5f97\u665a\u8f88\u5fc3\u610f\uff0c\u665a\u8f88\u4e5f\u5c31\u7528\u4e0d\u7740\u518d\u591a\u89e3\u91ca\u4e86\u3002\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u8038\u4e86\u8038\u80a9\uff0c\u518d\u6253\u4e86\u4e2a\u54c8\u54c8\u9053\uff1a\u201c\u8001\u592b\u65b9\u624d\u5df2\u7ecf\u8bf4\u8fc7\uff0c\u65e9\u5df2\u4e0d\u518d\u8fc7\u95ee\u6b66\u6797\u4e2d\u4e8b\uff0c\u81f3 \u4e8e\u4f60\u4eec\u7eff\u6797\uff0c\u66f4\u4e0e\u8001\u592b\u65e0\u5173\u3002\u4f60\u8981\u8bf4\u52a8\u8001\u592b\u9664\u53bb\u970d\u5143\u4f3d\uff0c\u90a3\u662f\u5984\u60f3\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u5fd9\u9053\uff1a\u201c\u665a\u8f88\u662f\u6709\u6761\u4ef6\u7684\u3002\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u9053\uff1a\u201c\u4f60\u6709\u4ec0\u4e48\u6761\u4ef6\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u53ea\u8981\u8001\u524d\u8f88\u80fd\u9664\u53bb\u970d\u5143\u4f3d\uff0c\u665a\u8f88\u60c5\u613f\u62e5\u6234\u4f60\u4e3a\u7eff\u6797\u76df\u4e3b\u3002\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u5927\u7b11\u9053\uff1a\u201c\u5c82\u6709\u6b64\u7406\uff0c\u8fde\u80e1\u67cf\u9f84\u548c\u8c37\u5bd2\u9999\u90a3\u4e9b\u665a\u751f\u665a\u8f88\u90fd\u505a\u8fc7\u7eff\u6797\u76df\u4e3b\uff0c\u5374\u8981\u8001 \u592b\u7ee7\u4ed6\u4eec\u4e4b\u540e\u6765\u505a\uff0c\u82e5\u6309\u5929\u5b50\u4f20\u4f4d\u7684\u8f88\u4efd\u6765\u8bf4\uff0e\u8001\u592b\u5c82\u4e0d\u6210\u4e86\u4ed6\u4eec\u7684\u5b59\u5b50\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u8001\u524d\u8f88\u5373\u4f7f\u4e0d\u505a\u76df\u4e3b\uff0c\u4f46\u80fd\u4e3a\u7eff\u6797\u9664\u53bb\u4e00\u5bb3\uff0e\u4e5f\u7b97\u662f\u5927\u5927\u505a\u4e86\u4e00\u4ef6\u597d \u4e8b\uff01\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u9053\uff1a\u201c\u8001\u592b\u4ece\u4e0d\u66fe\u81ea\u8be9\u4e3a\u597d\u4eba\uff0c\u5f53\u7136\u4e5f\u4e0d\u60f3\u505a\u4ec0\u4e48\u597d\u4e8b\u3002\u201d \u3000\u3000\u201c\u8001\u524d\u8f88\u610f\u6b32\u4f55\u5f80\uff1f\u201d \u3000\u3000\u201c\u4e0d\u7ba1\u5982\u4f55\uff0c\u8001\u592b\u8fd8\u662f\u8981\u5230\u5929\u53f0\u4e07\u82b1\u5bab\u4e00\u8d9f\uff0c\u7eb5\u7136\u8c37\u5bd2\u9999\u5df2\u88ab\u864e\u72fc\u541e\u98df\uff0e\u603b\u8fd8\u80fd\u627e\u5230\u4e00 \u4e9b\u9aa8\u9ab8\u3002\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u521a\u8d70\u51e0\u6b65\uff0c\u5374\u53c8\u56de\u8eab\u9053\uff1a\u201c\u8001\u592b\u8fd8\u6ca1\u95ee\uff0c\u8fd9\u5973\u5a03\u513f\u662f\u8c01\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u5979\u662f\u82d7\u59d1\u5a18\uff0c\u8001\u524d\u8f88\u95ee\u5979\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u53cc\u76ee\u6e5b\u6e5b\u795e\u5149\uff0c\u51dd\u6ce8\u5728\u82d7\u7d20\u82d3\u8138\u4e0a\u751a\u4e45\u9053\uff1a\u201c\u8001\u592b\u597d\u50cf\u5728\u54ea\u91cc\u89c1\u8fc7\u5979\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u5c0f\u9519\uff0c\u5979\u5f88\u50cf\u8c37\u9999\u4e3b\u8eab\u8fb9\u7684\u53e6\u4e00\u4f4d\u82d7\u59d1\u5a18\u3002\u201d \u3000\u3000\u201c\u83ab\u975e\u5979\u4fe9\u662f\u59ca\u59b9\u3002\u201d \u3000\u3000\u201c\u5979\u662f\u90a3\u4f4d\u82d7\u59d1\u5a18\u7684\u59b9\u59b9\u3002\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u5ffd\u7136\u2014\u628a\u6293\u4f4f\u82d7\u7d20\u82d3\u7684\u9999\u80a9\u9053\uff1a\u201c\u5973\u5a03\u513f\uff0c\u968f\u8001\u592b\u8d70\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u4e00\u6123\u9053\uff1a\u201c\u4f60\u8981\u5e26\u5979\u5230\u54ea\u91cc\u53bb\uff1f\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u9053\uff1a\u201c\u8001\u592b\u672c\u6765\u5df2\u7ecf\u770b\u4e2d\u4e86\u8c37\u5bd2\u9999\u8eab\u8fb9\u90a3\u4f4d\u82d7\u59d1\u5a18\uff0e\u65e2\u7136\u5f97\u4e0d\u5230\u5979\uff0c\u73b0\u5728\u53ea\u597d\u628a \u8fd9\u5973\u5a03\u513f\u5e26\u8d70\u4e86\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u5927\u611f\u4e00\u6014\uff0c\u7acb\u5373\u6717\u58f0\u559d\u9053\uff1a\u201c\u59d3\u5e9e\u7684\uff0c\u539f\u6765\u4f60\u4e5f\u662f\u4e2a\u8001\u8272\u9b54\uff01\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u9053\uff1a\u201c\u4e0d\u7ba1\u4f60\u600e\u4e48\u8bf4\uff0c\u8001\u592b\u662f\u5e26\u5979\u5e26\u5b9a\u4e86\uff01\u201d \u3000\u3000\u4ed6\u8bdd\u672a\u8bf4\u5b8c\uff0c\u5df2\u4e00\u6307\u70b9\u4e86\u82d7\u7d20\u82d3\u7684\u7a74\u9053\uff0c\u987a\u624b\u631f\u4e8e\u814b\u4e0b\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u90a3\u987e\u4e00\u5207\uff0c\u7ffb\u8155\u62d4\u51fa\u5b9d\u5251\uff0c\u95ea\u7535\u822c\u76f4\u5411\u5e9e\u58eb\u51b2\u80f8\u524d\u5927\u7a74\u523a\u53bb\u3002 \u3000\u3000\u4ed6\u5728\u65e2\u60ca\u4e14\u6012\u4e4b\u4e0b\uff0c\u5251\u52bf\u4e4b\u5feb\uff0c\u5feb\u5f97\u4ee4\u4eba\u76ee\u4e0d\u6687\u63a5\u3002 \u3000\u3000\u5e9e\u58eb\u51b2\u672a\u6599\u5230\u9ea6\u5c0f\u660e\u8eab\u624b\u5982\u6b64\u51cc\u5389\uff0c\u4e00\u65f6\u5927\u610f\uff0c\u9669\u4e9b\u88ab\u4e00\u5251\u523a\u4e2d\uff0e \u3000\u3000\u9ea6\u5c0f\u660e\u4e00\u5251\u843d\u7a7a\uff0e\u8e0f\u524d\u4e00\u6b65\uff0c\u53c8\u662f\u4e00\u5251\u523a\u51fa\uff0e\u8e0f\u6b65\u51fa\u5251\uff0c\u52a8\u4f5c\u4e00\u6c14\u5475\u6210\uff0c\u5c31\u50cf\u65e9\u6709\u9884\u6599 \u4e00\u822c\uff0e \u3000\u3000\u8fd9\u65f6\u5e9e\u58eb\u51b2\u56e0\u5df2\u6709\u5907\uff0c\u4e0d\u95ea\u4e0d\u907f\uff0c\u53ea\u628a\u5927\u8896\u4e00\u62c2\uff0c\u4e00\u80a1\u72c2\u98ce\uff0c\u72b9\u5982\u6392\u5c71\u5012\u6d77\u5411\u9ea6\u5c0f\u660e\u649e \u6765\uff0c\u786c\u662f\u628a\u523a\u6765\u7684\u5251\u950b\u8361\u504f\u3002 \u3000\u3000\u4ed6\u62c2\u51fa\u4e00\u8896\u4e4b\u540e\uff0c\u8d81\u6536\u8896\u95f4\u5411\u540e\u5e26\u52a8\u4e4b\u529b\uff0c\u4eba\u5df2\u51cc\u7a7a\u800c\u8d77\u3002 \u3000\u3000\u5148\u662f\u201c\u4e00\u9e64\u51b2\u5929\u201d\uff0e\u76f4\u5347\u8d77\u4e86\u56db\u4e94\u4e08\uff0c\u63a5\u7740\u5411\u524d\u5e73\u5c04\u800c\u51fa\uff0c\u51cc\u865a\u5c65\u7a7a\uff0c\u5c45\u7136\u5386\u4e45\u4e0d\u5760\uff0e \u4e00\u53e3\u6c14\u76f4\u98de\u884c\u51fa\u4e8c\u4e09\u5341\u4e08\u3002 \u3000\u3000\u8fd9\u60c5\u666f\u53ea\u770b\u5f97\u9ea6\u5c0f\u660e\u7b80\u76f4\u76ee\u77aa\u53e3\u5446\uff0c\u8fd9\u79cd\u8f7b\u529f\u8eab\u6cd5\uff0c\u5206\u660e\u662f\u4f20\u8bf4\u4e2d\u7684\u2018\u51cc\u7a7a\u865a\u6e21\u2019\uff0c\u60f3 \u4e0d\u5230\u5e9e\u58eb\u51b2\u5728\u8f7b\u529f\u4e0a\u5df2\u5230\u8fbe\u8fd9\u7b49\u60ca\u4e16\u9a87\u4fd7\u4e4b\u5883\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u81ea\u77e5\u65e0\u6cd5\u8ffd\u4e0a\uff0c\u53ea\u597d\u773c\u770b\u5e9e\u58eb\u51b2\u548c\u82d7\u7d20\u82d3\u7684\u8eab\u5f71\uff0c\u5728\u5c71\u5ce6\u8d77\u4f0f\u4e2d\u6d88\u5931\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u5728\u539f\u5730\u6005\u60d8\u5446\u7acb\u4e86\u751a\u4e45\uff0c\u624d\u7ee7\u7eed\u5411\u524d\u8d76\u8def\u3002 \u3000\u3000\u4ed6\u548c\u82d7\u7d20\u82d3\u9003\u5bd3\u5415\u6881\u5c71\u2018\u4e5d\u5929\u7384\u9634\u6d1e\u5e9c\u2019\u624d\u4e0d\u8fc7\u4e09\u56db\u5929\uff0c\u82d7\u7d20\u82d3\u7adf\u7136\u4e24\u6b21\u88ab\u4eba\u5e26\u8d70\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u5fc3\u60c5\u5b9e\u5728\u5f88\u4e0d\u723d\u3002 \u3000\u3000\u4ed6\u4e00\u5411\u4ee5\u4e3a\u81ea\u5df1\u7684\u6b66\u529f\u867d\u7b97\u4e0d\u662f\u5929\u4e0b\u65e0\u654c\uff0c\u81f3\u5c11\u4e5f\u662f\u96be\u9022\u654c\u624b\u3002 \u3000\u3000\u6ca1\u60f3\u5230\u5982\u4eca\u5374\u5904\u5904\u5403\u4e8f\uff0c\u5b9e\u5728\u4ee4\u4ed6\u7070\u5fc3\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u7684\u5e08\u7236\uff0e\u8bf4\u8d77\u6765\u6709\u597d\u51e0\u4e2a\u3002 \u3000\u3000\u4ed6\u4e4b\u6240\u4ee5\u4f1a\u8fde\u62dc\u6570\u4e2a\u5e08\u7236\uff0c\u9664\u4e86\u6709\u4e9b\u5e08\u7236\u662f\u574f\u4eba\uff0c\u4e3a\u7684\u662f\u53e6\u62dc\u660e\u5e08\uff0c\u5f03\u6697\u6295\u660e\u5916\uff0c\u4e3b\u8981 \u7684\uff0c\u81ea\u7136\u4e3a\u7684\u662f\u5b66\u5f97\u4e00\u8eab\u83ab\u6d4b\u9ad8\u6df1\u7684\u6b66\u827a\uff0c\u597d\u80fd\u591f\u72ec\u9738\u5929\u4e0b\uff0e\u552f\u6211\u72ec\u5c0a\u3002 \u3000\u3000\u7531\u4e8e\u5e74\u7eaa\u8fd8\u5c0f\u5173\u7cfb\uff0c\u9ea6\u5c0f\u660e\u81ea\u662f\u521d\u751f\u4e4b\u728a\u4e0d\u754f\u864e\uff0c\u603b\u6709\u7740\u505a\u5929\u4e0b\u7b2c\u4e00\u4eba\u7684\u68a6\u60f3\u3002 \u3000\u3000\u5982\u4eca\u8fde\u9047\u632b\u6298\uff0c\u5012\u4f7f\u9ea6\u5c0f\u660e\u9a84\u50b2\u4e4b\u5fc3\uff0c\u6536\u655b\u4e0d\u5c11\u3002 \u3000\u3000\u4e00\u8def\u5f80\u5b9a\u8944\u8d76\u53bb\uff0c\u4ed6\u5fc3\u4e2d\u6ce2\u6d9b\u8d77\u4f0f\uff0e\u4e00\u76f4\u5c31\u6ca1\u5e73\u9759\u8fc7\u3002 \u3000\u3000\u6e10\u6e10\uff0c\u53ea\u89c9\u5929\u8272\u5df2\u665a\uff0c\u5374\u4ecd\u5728\u7fa4\u5c71\u4e4b\u4e2d\u3002 \u3000\u3000\u770b\u6765\uff0c\u662f\u8d76\u4e0d\u4e0a\u5230\u5b9a\u8944\u6295\u5bbf\u4e86\u3002 \u3000\u3000\u4eca\u591c\u5f97\u5728\u5c71\u4e2d\u968f\u4fbf\u627e\u4e2a\u5730\u65b9\u8fc7\u591c\uff0c\u8fde\u665a\u996d\u90fd\u6ca1\u6709\u7740\u843d\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u6709\u4e9b\u6101\u95f7\uff0c\u65b9\u60f3\u53f9\u6c14\uff0c\u5374\u53d1\u73b0\u8eab\u540e\u6709\u811a\u6b65\u58f0\u4f20\u6765\uff0c\u4ed6\u4e0d\u7531\u63d0\u9ad8\u8b66\u89c9\uff0c\u7acb\u5373\u8eb2\u5230\u8def \u65c1\u4e00\u68f5\u5927\u6811\u540e\u3002 \u3000\u3000\u6765\u4eba\u6e10\u6e10\u9760\u8fd1\uff0c\u9ea6\u5c0f\u660e\u5df2\u80fd\u770b\u6e05\u8eab\u5f71\uff0e \u3000\u3000\u5728\u8fd9\u5239\u90a3\uff01\u4ed6\u51e0\u4e4e\u4e0d\u6562\u76f8\u4fe1\u81ea\u5df1\u7684\u773c\u775b\uff0e\u6765\u4eba\u7684\u8eab\u5f71\uff0c\u7adf\u7136\u548c\u8c37\u5bd2\u9999\u5341\u5206\u76f8\u4f3c\u3002\u53ca\u81f3\u90a3 \u4eba\u5f71\u6765\u5230\u8fd1\u524d\uff0c\u5c82\u6b62\u76f8\u4f3c\uff0c\u7b80\u76f4\u5c31\u662f\u6d3b\u8131\u7684\u8c37\u5bd2\u9999\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u8ddf\u968f\u8c37\u5bd2\u9999\u8db3\u6709\u4e24\u4e09\u5e74\u4e4b\u4e45\uff0c\u5bf9\u5979\u7684\u4e00\u4e3e\u4e00\u52a8\uff0c\u65e0\u4e0d\u719f\u6089\u5f97\u4e0d\u80fd\u518d\u719f\u6089\uff0c\u4ed6\u5b9e\u5728 \u4e0d\u80fd\u76f8\u4fe1\u4e16\u4e0a\u6709\u5982\u6b64\u76f8\u4f3c\u4e4b\u4eba\u3002 \u3000\u3000\u4f46\uff0c\u8c37\u5bd2\u9999\u5374\u53c8\u660e\u660e\u65e9\u5df2\u4e0d\u5728\u4eba\u4e16\u3002 \u3000\u3000\u83ab\u975e\u8fd9\u9ed1\u5f71\u662f\u8c37\u5bd2\u9999\u7684\u9b3c\u9b42\u51fa\u73b0\uff1f \u3000\u3000\u4e0d\u53ef\u80fd\uff01\u7edd\u5bf9\u4e0d\u53ef\u80fd\uff0c\u5929\u624d\u521a\u9ed1\uff0c\u9b3c\u9b42\u4e0d\u53ef\u80fd\u51fa\u6765\u5f97\u8fd9\u4e48\u65e9\uff0e\u4f55\u51b5\u9ea6\u5c0f\u660e\u4e5f\u5e76\u4e0d\u76f8\u4fe1\u6709 \u9b3c\u3002 \u3000\u3000\u4e0d\u7b49\u4ed6\u4e0a\u524d\u67e5\u95ee\uff0c\u90a3\u9ed1\u8863\u8499\u9762\u5973\u4eba\u5df2\u81ea\u52a8\u505c\u4e0b\u811a\u6b65\u3002 \u3000\u3000\u663e\u7136\uff0c\u5979\u5df2\u53d1\u89c9\u8def\u65c1\u6811\u540e\u6709\u4eba\uff0e \u3000\u3000\u9ea6\u5c0f\u660e\u5728\u4e00\u9635\u6fc0\u52a8\u4e4b\u540e\uff0c\u8fde\u5fd9\u7531\u6811\u540e\u95ea\u8eab\u800c\u51fa\uff0c\u51b2\u4e0a\u524d\u53bb\uff0c\u62b1\u62f3\u6df1\u6df1\u4e00\u793c\u9053\uff1a\u201c\u5728\u4e0b\u9ea6 \u5c0f\u660e\uff0c\u5973\u4fa0\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u4ed6\u81ea\u52a8\u62a5\u4e0a\u59d3\u540d\uff0c\u4f3c\u4e4e\u662f\u79cd\u60c5\u4e0d\u81ea\u7981\u7684\u53cd\u5e94\uff0e\u56e0\u4e3a\u5bf9\u65b9\u592a\u50cf\u8c37\u5bd2\u9999\u4e86\u3002 \u3000\u3000\u9ed1\u8863\u8499\u9762\u5973\u5b50\u7531\u9ed1\u7eb1\u80cc\u540e\u900f\u51fa\u6709\u5982\u73e0\u6eda\u7389\u76d8\u822c\u7684\u58f0\u97f3\u9053\uff1a\u201c\u5929\u8272\u5df2\u665a\uff0e\u5c0f\u5144\u5f1f\u600e\u4e48\u4e00\u4e2a \u4eba\u6765\u5230\u8fd9\u91cc\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u4e0d\u89c9\u4e00\u5446\uff0c\u8fd9\u8bed\u8c03\u867d\u7136\u4ecd\u7136\u9177\u4f3c\u8c37\u5bd2\u9999\uff0e\u4f46\u58f0\u97f3\u5374\u53c8\u548c\u8c37\u5bd2\u9999\u5927\u5927\u4e0d\u540c\u3002 \u3000\u3000\u4ed6\u7565\u4e00\u8fdf\u7591\u9053\uff1a\u201c\u5728\u4e0b\u662f\u8ff7\u4e86\u8def\uff0c\u83ab\u975e\u5973\u4fa0\u4e5f\u8ff7\u4e86\u8def\uff1f\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u5973\u5b50\u6447\u6447\u5934\u9053\uff1a\u201c\u65e2\u662f\u8ff7\u8def\uff0c\u6709\u8fd9\u4e48\u597d\u7684\u6708\u8272\uff0c\u5c3d\u53ef\u5bfb\u8def\u800c\u884c\uff0c\u4f55\u5fc5\u8eb2\u5728\u6811 \u540e\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u5973\u4fa0\u65e2\u662f\u7531\u5357\u800c\u6765\uff0c\u5fc5\u5b9a\u4e0e\u5728\u4e0b\u540c\u8def\uff0c\u5728\u4e0b\u53ef\u5426\u968f\u5973\u4fa0\u4e00\u8def\u540c\u884c\uff1f\u201d \u3000\u3000\u4ed6\u63d0\u51fa\u6b64\u9879\u8981\u6c42\uff0c\u4e0d\u5916\u662f\u5e0c\u671b\u80fd\u548c\u5bf9\u65b9\u591a\u6709\u4e00\u6bb5\u76f8\u5904\u65f6\u95f4\uff0c\u4ee5\u4fbf\u67e5\u77e5\u5bf9\u65b9\u7684\u8eab\u4efd\u6765\u5386\u3002 \u3000\u3000\u9ed1\u8863\u8499\u9762\u5973\u5b50\u6447\u6447\u5934\u9053\uff1a\u201c\u524d\u9762\u5c94\u8def\u5f88\u591a\uff0c\u4f60\u600e\u77e5\u6211\u8d70\u7684\u5fc5\u5b9a\u548c\u4f60\u540c\u4e00\u6761\u8def\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u987f\u4e86\u987f\u9053\uff1a\u201c\u90a3\u4fbf\u8d70\u5230\u524d\u9762\u518d\u5206\u624b\uff0c\u4e5f\u8fd8\u4e0d\u8fdf\u3002\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u5973\u5b50\u4f3c\u662f\u7565\u6709\u72b9\u8c6b\uff0c\u9ed8\u4e86\u4e00\u9ed8\u9053\uff1a\u201c\u4e5f\u597d\uff01\u5c31\u968f\u6211\u4e00\u8d77\u8d70\u5427\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u5927\u559c\uff0c\u7acb\u5373\u968f\u5728\u5bf9\u65b9\u8eab\u540e\u524d\u8fdb\u3002 \u3000\u3000\u4ed6\u6545\u610f\u968f\u5728\u9ed1\u8863\u8499\u9762\u5973\u5b50\u8eab\u540e\uff0c\u76ee\u7684\u4e0d\u5916\u662f\u6697\u4e2d\u89c2\u5bdf\u5979\u7684\u4e3e\u6b62\u884c\u52a8\u3002 \u3000\u3000\u6b64\u523b\uff0e\u4ed6\u5df2\u548c\u5bf9\u65b9\u8fd1\u5728\u54ab\u5c3a\uff0c\u53ea\u89c9\u8eab\u524d\u7684\u9ed1\u591c\u8499\u9762\u5973\u5b50\uff0c\u4e0d\u4f46\u6b65\u5c65\u79fb\u52a8\u95f4\u548c\u8c37\u5bd2\u9999\u5b8c\u5168 \u6ca1\u6709\u4e24\u6837\uff0c\u8fde\u8eab\u9ad8\u548c\u4f53\u5f62\uff0c\u4e5f\u771f\u7684\u4e00\u822c\u65e0\u4e8c\uff0e \u3000\u3000\u4ed6\u7684\u5fc3\u4e0d\u4f4f\u6026\u6026\u76f4\u8df3\uff0c\u60c5\u4e0d\u81ea\u7981\u8f7b\u54b3\u4e86\u58f0\u9053\uff1a\u201c\u5973\u4fa0\u53ef\u662f\u5e38\u5728\u5916\u9762\u8d70\u52a8\uff1f\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u5973\u5b50\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\u4e3a\u4ec0\u4e48\u8981\u95ee\u8d77\u8fd9\u4e9b\uff1f\u4e3a\u4ec0\u4e48\u4e0d\u79f0\u6211\u4e00\u58f0\u5927\u59d0\u6216\u59d1\u5a18\uff0e\u5374\u504f\u504f \u5bf9\u6211\u4ee5\u5973\u4fa0\u76f8\u79f0\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u82b3\u9a7e\u8eab\u7740\u9ed1\u8863\u9762\u7f69\u9ed1\u7eb1\uff0c\u53c8\u4e00\u4e2a\u4eba\u591c\u95f4\u5728\u8352\u5c71\u5cfb\u5cad\u884c\u8d70\uff0c\u4e0d\u7528\u8bf4\u8be5\u662f\u4f4d\u6b66 \u6797\u4e2d\u4eba\u7269\u3002\u201d \u3000\u3000\u201c\u90a3\u4e48\u4f60\u5462\uff1f\u201d \u3000\u3000\u201c\u5728\u4e0b\u81ea\u7136\u4e5f\u662f\u6b66\u6797\u4e2d\u4eba\uff0e\u5973\u4fa0\u4e00\u5b9a\u770b\u5230\u5728\u4e0b\u8eab\u540e\u80cc\u7740\u4e00\u628a\u5b9d\u5251\uff1f\u201d \u3000\u3000\u201c\u4f60\u5c0f\u5c0f\u5e74\u7eaa\uff0e\u5c31\u2014\u4e2a\u4eba\u5728\u5916\u95ef\u8361\uff0c\u5012\u662f\u5341\u5206\u96be\u5f97\u3002\u201d \u3000\u3000\u201c\u8fd9\u662f\u6ca1\u529e\u6cd5\u7684\u4e8b\uff0c\u4eba\u5728\u6c5f\u6e56\uff0e\u8eab\u4e0d\u7531\u5df1\u561b\u3002\u201d \u3000\u3000\u201c\u4f60\u73b0\u5728\u51c6\u5907\u5230\u54ea\u91cc\u53bb\uff1f\u201d \u3000\u3000\u201c\u5728\u4e0b\u539f\u672c\u5e0c\u671b\u80fd\u5728\u5929\u665a\u4e4b\u524d\u8d76\u5230\u5b9a\u8944\u6295\u5bbf\uff0e\u60f3\u4e0d\u5230\u5374\u8d70\u9519\u4e86\u8def\uff0e\u73b0\u5728\u53ea\u60f3\u5c3d\u5feb\u8d76\u5230\u5b9a \u8944\uff0e\u201d \u3000\u3000\u201c\u6295\u5bbf\u4ee5\u540e\u5462\uff1f\u662f\u5426\u8fd8\u8981\u5230\u522b\u5904\u53bb\uff1f\u201d \u3000\u3000\u201c\u4e0d\u7792\u5973\u4fa0\uff0c\u5728\u4e0b\u662f\u5317\u5cb3\u8ff7\u8e2a\u8c37\u7684\u4eba\uff0e\u201d \u3000\u3000\u201c\u542c\u8bf4\u5317\u5cb3\u8ff7\u8e2a\u8c37\u662f\u5f53\u4eca\u7eff\u6797\u603b\u5be8\uff0c\u5c0f\u5144\u5f1f\u8be5\u662f\u7eff\u6797\u603b\u5be8\u7684\u4eba\u4e86\uff1f\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u5973\u4fa0\u53ef\u77e5\u9053\u76ee\u524d\u7684\u7eff\u6797\u76df\u4e3b\u662f\u8c01\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u4e3b\u52a8\u63d0\u51fa\u8be2\u95ee\uff0c\u76ee\u7684\u81ea\u7136\u662f\u8981\u770b\u770b\u5bf9\u65b9\u6709\u4f55\u53cd\u5e94\u3002 \u3000\u3000\u53ea\u542c\u9ed1\u8863\u8499\u9762\u5973\u5b50\u9053\uff1a\u201c\u6211\u867d\u7136\u4e5f\u4e60\u8fc7\u6b66\uff0c\u4f46\u5374\u5e76\u975e\u7eff\u6797\u4e2d\u4eba\uff0c\u5bf9\u4e8e\u4e0d\u76f8\u5e72\u7684\u4e8b\u4ece\u4e0d\u8fc7 \u95ee\uff0c\u4f55\u5fc5\u77e5\u9053\u7eff\u6797\u76df\u4e3b\u662f\u8c01\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u4e0d\u613f\u9519\u8fc7\u673a\u4f1a\uff0c\u5fd9\u9053\uff1a\u201c\u5973\u4fa0\u867d\u975e\u7eff\u6797\u4e2d\u4eba\uff0c\u4f46\u80fd\u77e5\u9053\u2014\u4e9b\u7eff\u6797\u4e2d\u4e8b\uff0c\u5374\u662f\u6709\u76ca \u65e0\u5bb3\u3002\u5728\u4e0b\u73b0\u5728\u5c31\u8c08\u8c08\u5317\u5cb3\u8ff7\u8e2a\u8c37\u7684\u4e8b\uff0c\u4e0d\u77e5\u5973\u4fa0\u613f\u4e0d\u613f\u610f\u542c\uff1f\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u5973\u5b50\u9053\uff1a\u201c\u65e2\u7136\u4f60\u60f3\u544a\u8bc9\u6211\uff0c\u6211\u5f53\u7136\u4e0d\u80fd\u4e0d\u542c\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u90a3\u4e48\u6211\u5c31\u544a\u8bc9\u4f60\u5427\u3002\u81ea\u4ece\u5317\u5cb3\u8ff7\u8e2a\u8c37\u6210\u4e3a\u5929\u4e0b\u7eff\u6797\u603b\u5be8\u4ee5\u6765\uff0c\u9996\u4efb\u76df\u4e3b\u53eb \u80e1\u67cf\u9f84\u2026\u2026\u201d \u3000\u3000\u4ed6\u53c8\u8bf4\u9053\uff1a\u201c\u80e1\u67cf\u9f84\u4e0d\u4f46\u6b66\u529f\u9ad8\u7edd\uff0c\u667a\u8c0b\u51fa\u4f17\uff0c\u66f4\u662f\u4f4d\u5927\u4ec1\u5927\u4e49\u7684\u5927\u82f1\u96c4\uff0e\u53ef\u60dc\u2026\u2026\u201d \u3000\u3000\u201c\u53ef\u60dc\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u53ef\u60dc\u4ed6\u5df2\u7ecf\u6b7b\u4e86\uff01\u201d \u3000\u3000\u201c\u540e\u6765\u5462\uff1f\u201d \u3000\u3000\u201c\u80e1\u76df\u4e3b\u6b7b\u540e\uff0c\u7ee7\u4efb\u7684\u662f\u4ed6\u7684\u592b\u4eba\u8c37\u5bd2\u9999\u3002\u201d \u3000\u3000\u201c\u5929\u4e0b\u7eff\u6797\uff0e\u7adf\u7531\u4e00\u4e2a\u5973\u5b50\u9886\u5bfc\uff0c\u884c\u4e48\uff1f\u201d \u3000\u3000\u201c\u8c37\u76df\u4e3b\u7684\u5a01\u671b\uff0c\u867d\u7136\u4e0d\u5982\u80e1\u76df\u4e3b\uff0c\u4f46\u7ee7\u4efb\u7684\u4eba\uff0c\u5374\u975e\u5979\u83ab\u5c5e\u3002\u201d \u3000\u3000\u201c\u5979\u7684\u6b66\u529f\u5982\u4f55\uff1f\u201d \u3000\u3000\u201c\u5979\u7684\u6b66\u529f\uff0e\u539f\u5148\u5e76\u4e0d\u7b97\u9ad8\uff0c\u540e\u6765\u8eab\u7ecf\u8fde\u756a\u5947\u9047\uff0c\u5f97\u5230\u597d\u51e0\u4f4d\u9041\u4e16\u5f02\u4eba\u4f20\u6388\uff0c\u6b66\u5b66\u8fdb\u5883 \u4e00\u65e5\u5343\u91cc\uff0c\u5230\u4e34\u6b7b\u524d\uff0c\u5df2\u662f\u9ad8\u4e0d\u53ef\u6d4b\u4e86\u3002\u5929\u53f0\u5c71\u4e07\u82b1\u5bab\u4e00\u6218\u3001\u6b66\u5f53\u7684\u7d2b\u9633\u3001\u91d1\u9633\u4ee5\u53ca\u5c11\u6797\u7684 \u5929\u660e\u548c\u5c1a\uff0c\u5168\u6b7b\u5728\u5979\u7684\u624b\u4e0b\u3002\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u5973\u5b50\u542c\u5230\u8fd9\u91cc\uff0c\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u539f\u6765\u8c37\u5bd2\u9999\u4e5f\u6b7b\u5566\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u957f\u957f\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u5982\u679c\u8c37\u76df\u4e3b\u8fd8\u6d3b\u7740\uff0c\u5317\u5cb3\u8ff7\u8e2a\u8c37\uff0c\u53c8\u600e\u4f1a\u5f04\u6210\u73b0\u5728\u8fd9\u6837 \u5b50\uff01\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u5973\u5b50\u9ed8\u4e86\u9ed8\u9053\uff1a\u201c\u83ab\u975e\u73b0\u5728\u7684\u8ff7\u8e2a\u8c37\uff0c\u5df2\u5927\u5927\u4e0d\u5982\u4ece\u524d\u4e86\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u65e0\u9650\u611f\u6168\u7684\u9053\uff1a\u201c\u5c82\u6b62\u5927\u4e0d\u5982\u524d\uff0e\u7b80\u76f4\u5df2\u7ecf\u4e0d\u80fd\u63d0\u5566\uff01\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u4f1a\u5f04\u5230\u8fd9\u79cd\u5730\u6b65\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u662f\u56e0\u4e3a\u5df2\u7ecf\u6362\u4e86\u76df\u4e3b\u7684\u7f18\u6545\u3002\u201d \u3000\u3000\u201c\u73b0\u5728\u7684\u76df\u4e3b\u662f\u8c01\uff1f\u542c\u4f60\u7684\u8bed\u6c14\uff0c\u4e00\u5b9a\u4e0d\u662f\u80fd\u5e72\u7684\u4eba\uff1f\u201d \u3000\u3000\u201c\u73b0\u5728\u7684\u76df\u4e3b\u53eb\u970d\u5143\u4f3d\uff0c\u4ed6\u80fd\u4e0d\u80fd\u5e72\u6ca1\u5173\u7cfb\uff0e\u201d \u3000\u3000\u201c\u5173\u7cfb\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u201c\u90a3\u8001\u5c0f\u5b50\u6839\u672c\u5c31\u662f\u9ed1\u9053\u51fa\u8eab\uff0e\u9634\u72e0\u9669\u8bc8\uff0e\u65e0\u6076\u4e0d\u505a\u3002\u5973\u4fa0\u8bf7\u60f3\u60f3\uff0c\u7531\u8fd9\u6837\u4e00\u4e2a\u4eba\u6765\u9886 \u5bfc\u5929\u4e0b\u7eff\u6797\uff0c\u4eca\u540e\u90a3\u8fd8\u4f1a\u6709\u597d\u65e5\u5b50\u8fc7\u3002\u201d \u3000\u3000\u201c\u65e2\u7136\u5982\u6b64\uff0e\u4f60\u4eec\u4e3a\u4ec0\u4e48\u8fd8\u8981\u62e5\u6234\u4ed6\uff1f\u201d \u3000\u3000\u201c\u6211\u4eec\u600e\u4f1a\u62e5\u6234\u4ed6\uff1f\u56e0\u4e3a\u4ed6\u624b\u4e0b\u4eba\u592a\u591a\uff0e\u52bf\u529b\u6bd4\u6211\u4eec\u5927\uff0e\u4e0d\u8ba9\u4ed6\u5e72\u4e5f\u4e0d\u6210\uff0c\u9664\u975e\u5973\u4fa0\u80af \u5e2e\u5fd9\uff0e\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u5973\u5b50\u4f3c\u662f\u4e00\u6014\u9053\uff1a\u201c\u6211\u80fd\u5e2e\u4e0a\u4ec0\u4e48\u5fd9\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u6545\u610f\u4e00\u5b57\u5b57\u7684\u9053\uff1a\u201c\u5982\u679c\u5973\u4fa0\u80af\u5230\u8ff7\u8e2a\u8c37\u53bb\uff0c\u6211\u4eec\u5c31\u62e5\u6234\u4f60\u505a\u7eff\u6797\u76df\u4e3b\uff0c\u8fd9\u6837\u5c31 \u7b49\u4e8e\u5e2e\u4e86\u6211\u4eec\u7684\u5fd9\uff01\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u5973\u5b50\u4e0d\u89c9\u5931\u7b11\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\u771f\u4f1a\u8bf4\u7b11\u8bdd\uff0c\u6211\u548c\u4f60\u4eec\u7eff\u6797\u6cbe\u4e0d\u4e0a\u534a\u70b9\u5173\u7cfb\uff0c\u600e\u53ef \u505a\u7eff\u6797\u76df\u4e3b\uff0c\u53c8\u6709\u8c01\u80af\u62e5\u6234\u6211\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u5927\u58f0\u9053\uff1a\u201c\u5728\u4e0b\u7b2c\u4e00\u4e2a\u62e5\u6234\u4f60\uff01\u800c\u4e14\u6211\u4fdd\u8bc1\u51e1\u662f\u53cd\u5bf9\u970d\u5143\u4f3d\u90a3\u8001\u738b\u516b\u86cb\u7684\u4eba\uff0e\u90fd \u4f1a\u6beb\u65e0\u5f02\u8bae\u7684\u4e00\u81f4\u8877\u5fc3\u8bda\u610f\u62e5\u6234\u4f60\uff01\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u5973\u5b50\u4f3c\u4e4e\u5927\u5927\u5403\u60ca\u9053\uff1a\u201c\u4e16\u4e0a\u54ea\u4f1a\u6709\u8fd9\u79cd\u4e8b\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u6211\u8fd9\u8bdd\u7edd\u5bf9\u662f\u771f\u7684\uff0e\u53ea\u8981\u62e5\u6234\u8c37\u76df\u4e3b\u7684\u4eba\uff0c\u4e00\u5b9a\u4f1a\u62e5\u6234\u4f60\uff01\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u5973\u5b50\u554a\u4e86\u58f0\u9053\uff1b\u201c\u5c0f\u5144\u5f1f\u8fd9\u8bdd\u4ee4\u6211\u8d8a\u53d1\u4e0d\u89e3\u4e86\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4e8b\u5230\u5982\u4eca\uff0c\u6211\u5c31\u4e0d\u5f97\u4e0d\u5b9e\u8bf4\u4e86\uff0c\u56e0\u4e3a\u5973\u4fa0\u592a\u50cf\u6211\u4eec\u90a3\u4f4d\u5df2\u7ecf\u53bb\u4e16\u7684\u8c37\u76df\u4e3b \u4e86\uff01\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u5973\u5b50\u4e0d\u89c9\u5931\u7b11\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\u7684\u60f3\u6cd5\u5b9e\u5728\u592a\u5929\u771f\uff0c\u6211\u7eb5\u7136\u957f\u5f97\u548c\u8c37\u5bd2\u9999\u6709\u4e9b\u76f8 \u4f3c\uff0c\u4f46\u5374\u7ec8\u7a76\u4e0d\u662f\u8c37\u5bd2\u9999\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1b\u201c\u5973\u4fa0\u5c82\u6b62\u548c\u8c37\u76df\u4e3b\u6709\u4e9b\u76f8\u4f3c\uff0c\u7b80\u76f4\u5c31\u662f\u4e00\u6a21\u2014\u6837\u3002\u201d \u3000\u3000\u201c\u4e0d\u77e5\u5c0f\u5144\u5f1f\u5bf9\u8c37\u5bd2\u9999\u8ba4\u8bc6\u591a\u5c11\uff1f\u201d \u3000\u3000\u201c\u6211\u8ddf\u968f\u8c37\u76df\u4e3b\u6709\u4e24\u4e09\u5e74\u4e4b\u4e45\uff0c\u5bf9\u5979\u7684\u4e00\u5207\uff0c\u53ef\u8bf4\u90fd\u6478\u5f97\u6e05\u6e05\u695a\u695a\u3002\u201d \u3000\u3000\u201c\u4e5f\u8bb8\u6211\u7684\u8eab\u6750\u50cf\u5979\uff0c\u4f46\u4f60\u5374\u5e76\u672b\u770b\u5230\u6211\u7684\u5bb9\u8c8c\u662f\u5426\u4e5f\u50cf\u5979\uff1f\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u2026\u2026\u5973\u4fa0\u662f\u5426\u5bb9\u5728\u4e0b\u4e00\u7779\u5bb9\u989c\uff1f\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u5973\u5b50\u7565\u4e00\u72b9\u8c6b\u9053\uff1a\u201c\u6211\u5982\u679c\u80af\u8ba9\u522b\u4eba\u770b\uff0c\u53c8\u4f55\u5fc5\u9762\u7f69\u9ed1\u7eb1\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u8fd9\u91cc\u53ea\u6709\u5728\u4e0b\u4e00\u4eba\uff0c\u5973\u4fa0\u4f55\u59a8\u7834\u4e00\u6b21\u4f8b\u3002\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u5973\u5b50\u5e76\u672a\u4f5c\u607c\uff0c\u9ed8\u4e86\u9ed8\u9053\uff1a\u201c\u4f60\u731c\u6211\u8be5\u662f\u4ec0\u4e48\u6837\u5b50\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u4e0d\u5047\u601d\u7d22\uff1a\u201c\u56fd\u8272\u5929\u9999\uff0c\u7edd\u4e16\u65e0\u53cc\uff0c\u548c\u6211\u4eec\u6545\u53bb\u7684\u8c37\u76df\u4e3b\u5b8c\u5168\u4e00\u6837\uff01\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u5973\u5b50\u7b11\u9053\uff1a\u201c\u8fd9\u6837\u8bf4\u6765\uff0c\u8c37\u5bd2\u9999\u4e00\u5b9a\u662f\u4f4d\u7edd\u4e16\u7f8e\u5973\u5566\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u5f53\u4e4b\u65e0\u6127\uff01\u201d \u3000\u3000\u201c\u5c0f\u5144\u5f1f\u5c0f\u5c0f\u5e74\u7eaa\uff0e\u5bf9\u7f8e\u4e11\u4e4b\u5206\uff0c\u662f\u5426\u6709\u771f\u6b63\u7684\u9274\u8d4f\u529b\uff0c\u5f88\u503c\u5f97\u6211\u6000\u7591\uff01\u201d \u3000\u3000\u201c\u5973\u4fa0\u8fd9\u8bdd\uff0e\u4ee4\u5728\u4e0b\u5f88\u4e0d\u670d\u6c14\uff01\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u5373\u4f7f\u662f\u4e2a\u50bb\u74dc\uff0c\u4e5f\u6ca1\u6709\u4e0d\u77e5\u9053\u7f8e\u4e11\uff0c\u4f55\u51b5\u5728\u4e0b\u5e76\u4e0d\u50bb\u3002\u201d \u3000\u3000\u201c\u6709\u4eba\u8bf4\u82f1\u96c4\u96be\u8fc7\u7f8e\u4eba\u5173\uff0e\u6211\u770b\u5c0f\u5144\u5f1f\u4e5f\u7b97\u662f\u4f4d\u5c11\u5e74\u82f1\u96c4\u4eba\u7269\uff0e\u662f\u5426\u4e5f\u66fe\u7ecf\u8fc7\u7f8e\u4eba\u5173 \u5462\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u4e07\u60f3\u4e0d\u5230\u5bf9\u65b9\u4f1a\u6709\u8fd9\u79cd\u95ee\u8bdd\u3002 \u3000\u3000\u8138\u4e0a\u4e00\u70ed\uff0c\u987f\u4e86\u987f\u9053\uff1a\u201c\u5728\u4e0b\u867d\u672b\u7ecf\u8fc7\uff0e\u4f46\u5982\u679c\u5c06\u6765\u771f\u6709\u90a3\u4e48\u4e00\u5929\uff0e\u5012\u5f88\u60f3\u8bd5\u8bd5\u7a76\u7adf\u662f \u4ec0\u4e48\u6ecb\u5473\u3002\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u5973\u5b50\u5401\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u6211\u529d\u4f60\u6700\u597d\u8fd8\u662f\u522b\u8bd5\uff0c\u4f5b\u5bb6\u66fe\u6709\u53e5\u2018\u8272\u5373\u662f\u7a7a\u2019\u7684\u8bdd\u3002\u5373 \u4ee5\u8c37\u5bd2\u9999\u6765\u8bf4\u5427\uff0c\u867d\u7136\u751f\u524d\u56fd\u8272\u5929\u9999\uff0e\u53ef\u80fd\u4e0d\u77e5\u6709\u591a\u5c11\u6b66\u6797\u4eba\u7269\u66fe\u4e3a\u5979\u503e\u5012\u8fc7\uff0e\u4f46\u73b0\u5728\u5979\u5374 \u53ea\u662f\u4e00\u5806\u67af\u9aa8\uff0c\u53c8\u7559\u4e0b\u4e86\u4ec0\u4e48\u5462\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4e0d\u7ba1\u5982\u4f55\uff0c\u5728\u4e0b\u73b0\u5728\u53ea\u60f3\u6709\u5e78\u77bb\u4ef0\u5973\u4fa0\u7684\u5e90\u5c71\u771f\u9762\u76ee\uff01\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u5973\u5b50\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\u7528\u4e0d\u7740\u591a\u8bf4\uff0c\u6211\u6ca1\u6709\u7406\u7531\u4e3a\u4f60\u7834\u4f8b\u3002\u201d \u3000\u3000\u8fd9\u65f6\u7684\u9ea6\u5c0f\u660e\uff0c\u771f\u6068\u4e0d\u5f97\u731d\u7136\u51fa\u624b\u3002 \u3000\u3000\u5c06\u9ed1\u8863\u8499\u9762\u5973\u5b50\u7684\u7f69\u9762\u9ed1\u7eb1\u626f\u4e0b\uff0e\u4ed6\u81ea\u4fe1\u51ed\u81ea\u5df1\u7684\u8eab\u624b\uff0c\u5373\u4f7f\u5bf9\u65b9\u6b66\u529f\u518d\u9ad8\uff0e\u4e5f\u672a\u5fc5\u80fd \u591f\u8eb2\u8fc7\u3002 \u3000\u3000\u4f46\u4ed6\u6700\u540e\u8fd8\u662f\u5f3a\u5fcd\u4e0b\u5fc3\u7406\u4e0a\u7684\u51b2\u52a8\uff0c\u56e0\u4e3a\u8fd9\u6837\u505a\u5b9e\u5728\u592a\u5510\u7a81\u3002 \u3000\u3000\u53c8\u524d\u8fdb\u4e86\u76cf\u8336\u5de5\u592b\uff0e\u524d\u9762\u51fa\u73b0\u2014\u6761\u5c94\u8def\u3002 \u3000\u3000\u9ed1\u8863\u8499\u9762\u5973\u5b50\u505c\u4e0b\u811a\u6b65\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\u5f80\u524d\u76f4\u8d70\uff0e\u4fbf\u662f\u901a\u5f80\u5b9a\u8944\u7684\u8def\uff0e\u54b1\u4eec\u73b0\u5728\u8be5\u5206\u624b \u5566\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u90a3\u4e48\u5973\u4fa0\u8981\u5f80\u54ea\u91cc\u53bb\uff1f\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u5973\u5b50\u6307\u7740\u5de6\u65b9\u53e6\u2014\u6761\u5c0f\u8def\u9053\uff1a\u201c\u6211\u8981\u5f80\u8fd9\u8fb9\u8d70\uff01\u201d \u3000\u3000\u201c\u5973\u4fa0\u662f\u5230\u54ea\u91cc\uff1f\u201d \u3000\u3000\u201c\u6211\u662f\u6765\u8bbf\u53cb\u7684\uff0c\u5c31\u5728\u524d\u9762\u4e0d\u8fdc\u3002\u201d \u3000\u3000\u201c\u5728\u4e0b\u662f\u5426\u53ef\u968f\u5973\u4fa0\u4e00\u8d77\u53bb\uff0c\u4e5f\u53ef\u987a\u4fbf\u5728\u90a3\u8fb9\u6295\u5bbf\uff1f\u201d \u3000\u3000\u201c\u6211\u662f\u5973\u4eba\uff0e\u8bbf\u7684\u4e5f\u662f\u5973\u4eba\uff0e\u90a3\u8fb9\u5b9e\u5728\u4e0d\u65b9\u4fbf\u62db\u5f85\u5c0f\u5144\u5f1f\u3002\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u5973\u5b50\u8bf4\u5b8c\u8bdd\uff0c\u8ff3\u81ea\u5f80\u5de6\u65b9\u5c94\u8def\u800c\u53bb\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u987f\u65f6\u5446\u5728\u5f53\u573a\uff0e\u5927\u6709\u4e0d\u77e5\u6240\u63aa\u4e4b\u611f\u3002 \u3000\u3000\u4f46\u4ed6\u5374\u53c8\u7edd\u5bf9\u4e0d\u613f\u5c31\u6b64\u548c\u9ed1\u8863\u8499\u9762\u5973\u5b50\u5206\u624b\u3002 \u3000\u3000\u597d\u5728\u4ed6\u5f88\u5feb\u4fbf\u6709\u4e86\u4e3b\u5f20\uff0c\u6697\u9053\uff1a\u201c\u6211\u4f55\u4e0d\u7b49\u5979\u8d70\u8fc7\u2014\u6bb5\u8ddd\u79bb\u968f\u540e\u8ddf\u8e2a\u3002\u201d \u3000\u3000\u8fd9\u6761\u8def\u5df2\u7ecf\u662f\u5c94\u8def\u4e86\uff0e\u4e0d\u53ef\u80fd\u518d\u6709\u5c94\u8def\uff0e\u5979\u7684\u53cb\u4eba\u65e2\u7136\u79bb\u8fd9\u91cc\u4e0d\u8fdc\uff0c\u6211\u4e5f\u5c31\u4e0d\u96be\u8ddf\u8e2a\u5230 \u5979\u3002\u201d \u3000\u3000\u5fc3\u5ff5\u65e2\u51b3\uff0c\u4fbf\u6545\u610f\u5728\u8def\u65c1\u5f98\u5f8a\u4e86\u4e00\u4f1a\u513f\uff0c\u7136\u540e\u653e\u8f7b\u811a\u6b65\u7531\u5de6\u65b9\u5c94\u8def\u8ddf\u53bb\u3002 \u3000\u3000\u8fd9\u6761\u8def\u6bd4\u5148\u524d\u7684\u66f4\u7a84\uff0c\u800c\u4e14\u4e24\u65c1\u6742\u6811\u4e1b\u751f\uff0c\u867d\u6709\u660e\u6708\u5f53\u7a7a\uff0c\u89c6\u754c\u5374\u5341\u5206\u4e0d\u826f\uff0c\u6839\u672c\u65e0\u6cd5 \u770b\u5230\u524d\u9762\u9ed1\u8863\u8499\u9762\u5973\u5b50\u4eba\u5728\u4f55\u5904\u3002 \u3000\u3000\u524d\u884c\u7ea6\u5341\u4f59\u4e08\uff0e\u524d\u9762\u7adf\u662f\u4e00\u7247\u9ed1\u9e26\u9e26\u7684\u677e\u6797\uff0c\u800c\u811a\u4e0b\u7684\u5c0f\u5f84\uff0c\u6b63\u5728\u677e\u6797\u5f53\u4e2d\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u660e\u77e5\u7eff\u6797\u4e2d\u6709\u53e5\u2018\u9022\u6797\u83ab\u5165\u2019\u7684\u8b66\u8bed\uff0c\u4e5f\u987e\u4e0d\u5f97\u90a3\u4e48\u591a\uff0e\u53ea\u80fd\u63a3\u51fa\u5b9d\u5251\uff0e\u6697\u4e2d\u7559 \u795e\u6212\u5907\uff0e \u3000\u3000\u8fd9\u7247\u677e\u6797\uff0c\u8db3\u8db3\u6709\u4e8c\u4e09\u5341\u4e08\u6df1\uff0c\u597d\u5728\u4e00\u8def\u4e0a\u5e76\u65e0\u4efb\u4f55\u52a8\u9759\u3002 \u3000\u3000\u5c82\u77e5\u521a\u521a\u7a7f\u51fa\u677e\u6797\uff0c\u9ea6\u5c0f\u660e\u4fbf\u60c5\u4e0d\u81ea\u7981\u8111\u5b50\u91cc\u2018\u55e1\u2019\u7684\u4e00\u58f0\u3002\u8fde\u6253\u4e24\u4e2a\u51b7\u98a4\uff0c\u8fde\u5934\u76ae\u4e5f \u6709\u4e9b\u53d1\u9ebb\u3002 \u3000\u3000\u539f\u6765\u677e\u6797\u5c3d\u5934\uff0c\u7adf\u662f\u4e00\u7247\u5893\u5730\u3002 \u3000\u3000\u83ab\u975e\u90a3\u9ed1\u4eba\u8499\u9762\u5973\u5b50\uff0c\u7adf\u662f\u8c37\u5bd2\u9999\u7684\u9b3c\u9b42\u51fa\u73b0\uff1f \u3000\u3000\u81f3\u5c11\u9ea6\u5c0f\u660e\u4f1a\u8fd9\u6837\u60f3\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u867d\u4e0d\u4fe1\u90aa\u3002 \u3000\u3000\u4f46\u6b64\u65f6\u6b64\u5730\uff0c\u6b64\u60c5\u6b64\u666f\uff0c\u5374\u4f7f\u5f97\u4ed6\u4e0d\u4fe1\u4e5f\u5f97\u4fe1\u3002 \u3000\u3000\u4ed6\u63e9\u8bd5\u4e86\u4e00\u4e0b\u989d\u89d2\u7684\u51b7\u6c57\u3002 \u3000\u3000\u5fc3\u60f3\u4e00\u5b9a\u662f\u6709\u4eba\u628a\u8c37\u5bd2\u9999\u7684\u9057\u9ab8\uff0c\u7531\u5929\u53f0\u5c71\u642c\u6765\u57cb\u846c\u5728\u8fd9\u91cc\u3002\u4f55\u4e0d\u5bdf\u770b\u2014\u4e0b\uff0c\u770b\u770b\u662f\u5426 \u6709\u8c37\u5bd2\u9999\u7684\u5893\u7891\u3002 \u3000\u3000\u8fd9\u7247\u5893\u5730\uff0c\u81f3\u5c11\u6709\u575f\u5893\u767e\u6765\u5ea7\uff0c\u65e7\u5806\u65b0\u5806\u90fd\u6709\uff0c\u4f46\u5374\u81f3\u5c11\u6709\u5c06\u8fd1\u4e00\u534a\u6ca1\u6709\u5893\u7891\uff0e \u3000\u3000\u4e0d\u7ba1\u5982\u4f55\uff0c\u9ea6\u5c0f\u660e\u8fd8\u662f\u628a\u6240\u6709\u5893\u7891\u4ed4\u7ec6\u770b\u5b8c\uff0e \u3000\u3000\u4ed6\u5931\u671b\u4e86\uff0c\u4e0d\u6d88\u8bf4\u6ca1\u6709\u4e00\u4e2a\u5893\u7891\u662f\u4e3a\u8c37\u5bd2\u9999\u800c\u7acb\u7684\uff0e \u3000\u3000\u4f46\u5374\u4e0d\u80fd\u56e0\u6b64\u800c\u65ad\u5b9a\u8c37\u5bd2\u9999\u4e0d\u662f\u57cb\u846c\u5728\u8fd9\u91cc\uff0e \u3000\u3000\u56e0\u4e3a\u6709\u4e0d\u5c11\u65b0\u5806\u662f\u6709\u5893\u65e0\u7891\u7684\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u6765\u5230\u5893\u5730\u65f6\uff0e\u672c\u6765\u5df2\u8fd1\u4e8c\u66f4\uff0c\u7ecf\u8fc7\u8fd9\u4e00\u756a\u6298\u817e\uff0c\u5df2\u662f\u5c06\u8fd1\u4e09\u66f4\u4e86\uff0e \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u524d\u9762\u4e0d\u8fdc\u7684\u5c71\u811a\u4e0b\uff0c\u95ea\u51fa\u4e00\u7ebf\u706f\u5149\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u5927\u559c\uff0c\u6025\u6025\u5bfb\u8def\u5411\u706f\u5149\u5904\u5954\u53bb\u3002 \u3000\u3000\u53ca\u81f3\u5230\u8fbe\u8fd1\u524d\uff0c\u539f\u6765\u5374\u662f\u4e00\u5c3c\u5eb5\u3002 \u3000\u3000\u5eb5\u95e8\u5f00\u7740\uff0c\u9ea6\u5c0f\u660e\u6beb\u4e0d\u72b9\u8c6b\u7684\u5411\u91cc\u8d70\u53bb\uff0e \u3000\u3000\u7a7f\u8fc7\u4e00\u9053\u6708\u5149\u6d1e\u95e8\uff0e\u8fce\u9762\u4fbf\u662f\u4e00\u5904\u4f5b\u5802\u3002 \u3000\u3000\u6b63\u6709\u4e00\u540d\u7f01\u8863\u8001\u5c3c\uff0c\u5728\u9999\u6848\u524d\u6572\u7740\u6728\u9c7c\u8bf5\u7ecf\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u5728\u4f5b\u5802\u5916\u7565\u7ad9\u4e86\u4e00\u4f1a\u513f\uff0c\u4fbf\u4e3e\u6b65\u8d70\u4e86\u8fdb\u53bb\uff0c\u606d\u606d\u656c\u656c\u7684\u6df1\u65bd\u4e00\u793c\u9053\uff1a\u201c\u8001\u5e08\u592a\u8bf7 \u4e86\uff01\u201d \u3000\u3000\u90a3\u8001\u5c3c\u4f3c\u662f\u5fae\u5fae\u4e00\u60ca\uff01\u671b\u4e86\u9ea6\u5c0f\u660e\u4e00\u773c\uff0c\u653e\u4e0b\u6728\u9c7c\u9053\uff1a\u201c\u591c\u9759\u66f4\u6df1\uff0c\u5c0f\u65bd\u4e3b\u600e\u4e48\u95ef\u5230\u8fd9 \u91cc\u6765\u4e86\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u53c8\u62f1\u4e86\u62f1\u624b\u9053\uff1a\u201c\u665a\u8f88\u672c\u6765\u662f\u7531\u592a\u539f\u8d76\u5f80\u5b9a\u8944\u7684\uff0c\u4e0d\u60f3\u591c\u665a\u8ff7\u4e86\u8def\uff0c\u4ee5\u81f4\u6765\u5230\u5b9d \u5eb5\u3002\u201d \u3000\u3000\u90a3\u8001\u5c3c\u8138\u4e0a\u770b\u4e0d\u51fa\u534a\u70b9\u8868\u60c5\uff0c\u6447\u4e86\u6447\u5934\u9053\uff1a\u201c\u539f\u6765\u5c0f\u65bd\u4e3b\u662f\u51c6\u5907\u6765\u655d\u5eb5\u6295\u5bbf\u7684\uff0c\u5f88\u62b1 \u6b49\uff0c\u655d\u5eb5\u4ece\u4e0d\u63a5\u5f85\u7537\u5bbe\uff0e\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u65e2\u7136\u8d35\u5eb5\u4e0d\u80af\u7559\u5bbf\uff0c\u665a\u8f88\u4e5f\u4e0d\u6562\u52c9\u5f3a\uff0c\u4e0d\u8fc7\u665a\u8f88\u6709\u51e0\u4ef6\u4e8b\u60f3\u8bf7\u95ee\u8001\u5e08\u592a\uff0c \u8fd8\u671b\u8001\u5e08\u592a\u4e0d\u541d\u8d50\u544a\uff01\u201d \u3000\u3000\u90a3\u8001\u5c3c\u53cc\u7738\u7728\u4e86\u51e0\u7728\u9053\uff1a\u201c\u4f60\u95ee\u5427\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u6709\u4f4d\u9ed1\u8863\u5973\u4fa0\uff0c\u662f\u5426\u6765\u5230\u4e86\u8d35\u5eb5\uff1f\u201d \u3000\u3000\u90a3\u8001\u5c3c\u84e6\u5730\u76ee\u4e2d\u795e\u5149\u4e00\u95ea\u5373\u655b\u9053\uff1a\u201c\u539f\u6765\u5c0f\u65bd\u4e3b\u662f\u6b66\u6797\u4e2d\u4eba\uff0c\u4e0d\u7136\u8981\u627e\u7684\u4e0d\u4f1a\u662f\u4f4d\u5973 \u4fa0\uff0e\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u4e0d\u89c9\u6697\u559c\uff0c\u542c\u8001\u5c3c\u7684\u8bed\u6c14\uff0c\u5f88\u53ef\u80fd\u9ed1\u8863\u8499\u9762\u5973\u5b50\u6765\u5230\u8fd9\u5ea7\u5c3c\u5eb5\u3002\u4fbf\u8fde\u5fd9\u70b9\u70b9\u5934 \u9053\uff1a\u201c\u665a\u8f88\u867d\u7b97\u4e0d\u5f97\u6b66\u6797\u4e2d\u4eba\uff0c\u4f46\u5374\u8ba4\u8bc6\u51e0\u4f4d\u6b66\u6797\u524d\u8f88\u3002\u201d \u3000\u3000\u90a3\u8001\u5c3c\u9053\uff1a\u201c\u90a3\u4f4d\u5973\u4fa0\u65e2\u662f\u5c0f\u65bd\u4e3b\u524d\u8f88\uff0c\u5fc5\u5b9a\u50cf\u8d2b\u5c3c\u4e00\u6837\u5e74\u7eaa\u4e86\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u5fd9\u9053\uff1a\u201c\u4e0d\uff0e\u90a3\u4f4d\u5973\u4fa0\u867d\u7136\u5e74\u7eaa\u6bd4\u665a\u8f88\u5927\uff0c\u4f46\u5374\u6bd4\u8001\u5e08\u592a\u5e74\u8f7b\u5f97\u591a\u3002\u201d \u3000\u3000\u90a3\u8001\u5c3c\u6447\u5934\u9053\uff1a\u201c\u6ca1\u6709\u8fd9\u6837\u7684\u5973\u4fa0\u5230\u655d\u5eb5\u6765\uff01\u201d \u3000\u3000\u201c\u542c\u5e08\u592a\u7684\u8bed\u6c14\uff0e\u83ab\u975e\u6709\u4e00\u4f4d\u5e74\u8001\u7684\u5973\u4fa0\u6765\u8fc7\uff1f\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u90a3\u662f\u8d2b\u5c3c\u7684\u5e08\u59d0\uff0c\u5e74\u7eaa\u6bd4\u8d2b\u5c3c\u8fd8\u8981\u5927\u5f97\u591a\u3002\u201d \u3000\u3000\u201c\u5979\u662f\u600e\u6837\u7684\u6253\u626e\uff1f\u201d \u3000\u3000\u201c\u5979\u8eab\u7a7f\u9ed1\u8863\uff0e\u8fd8\u7f69\u4e86\u4e00\u5757\u8499\u9762\u9ed1\u7eb1\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u53ea\u542c\u5f97\u5927\u6709\u557c\u7b11\u7686\u975e\u4e4b\u611f\u3002 \u3000\u3000\u4ed6\u65b9\u624d\u968f\u5728\u90a3\u9ed1\u8863\u8499\u9762\u5973\u5b50\u8eab\u540e\u8d70\u4e86\u6709\u597d\u51e0\u91cc\u8def\uff0c\u867d\u7136\u5e76\u672a\u4e00\u7779\u5979\u7684\u5e90\u5c71\u771f\u9762\u76ee\uff0c\u4f46\u7531 \u5979\u5a40\u5a1c\u591a\u59ff\u7684\u80cc\u5f71\u548c\u83b2\u6b65\u59d7\u59d7\u7684\u884c\u8fdb\u59ff\u6001\u770b\u6765\uff0e\u5374\u53ef\u65ad\u5b9a\u5fc5\u662f\u4f4d\u5e74\u8f7b\u5973\u5b50\u3002 \u3000\u3000\u90a3\u8001\u5c3c\u89c1\u9ea6\u5c0f\u660e\u4e45\u4e45\u4e0d\u8bed\uff0c\u4e0d\u7531\u8f7b\u54b3\u58f0\u9053\uff1a\u201c\u96be\u9053\u5c0f\u65bd\u4e3b\u4e0d\u76f8\u4fe1\u8d2b\u5c3c\u7684\u8bdd\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u665a\u8f88\u600e\u6562\u4e0d\u76f8\u4fe1\u8001\u5e08\u592a\u7684\u8bdd\uff0c\u5982\u679c\u8001\u5e08\u592a\u80af\u628a\u90a3\u4f4d\u5973\u4fa0\u8bf7\u51fa\u6765\u2014\u89c1\uff0c\u665a\u8f88 \u5c31\u66f4\u76f8\u4fe1\u4e86\u3002\u201d \u3000\u3000\u90a3\u8001\u5c3c\u8138\u8272\u5fae\u5fae\u2014\u6c89\u9053\uff1a\u201c\u5c82\u6709\u6b64\u7406\uff0c\u655d\u5e08\u59ca\u5df2\u7ecf\u5b89\u7761\u591a\u65f6\uff0c\u600e\u53ef\u518d\u6253\u6270\u5979\uff01\u201d \u3000\u3000\u201c\u90a3\u4e48\u665a\u8f88\u60f3\u518d\u95ee\u8001\u5e08\u592a\u4e00\u70b9\u522b\u7684\uff01\u201d \u3000\u3000\u201c\u5c0f\u65bd\u4e3b\u6709\u8bdd\u53ea\u7ba1\u95ee\uff0c\u7528\u4e0d\u7740\u518d\u5f81\u6c42\u8d2b\u5c3c\u7684\u610f\u89c1\uff0e\u201d \u3000\u3000\u201c\u8d35\u5eb5\u524d\u9762\u90a3\u7247\u5893\u5730\uff0c\u846c\u7684\u90fd\u662f\u4e9b\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u8bdd\u51fa\u53e3\u540e\uff0c\u624d\u89c9\u51fa\u8fd9\u79cd\u95ee\u6cd5\uff0c\u53ea\u6015\u4efb\u4f55\u4eba\u4e5f\u96be\u4ee5\u56de\u7b54\uff0e \u3000\u3000\u679c\u7136\uff0c\u90a3\u8001\u5c3c\u6447\u5934\u4e00\u7b11\u9053\uff1a\u201c\u8001\u5c3c\u4e0d\u662f\u8d1f\u8d23\u770b\u575f\u7684\uff0c\u600e\u77e5\u5893\u91cc\u846c\u7684\u90fd\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u201c\u665a\u8f88\u65b9\u624d\u89c1\u90a3\u91cc\u6709\u4e0d\u5c11\u65b0\u575f\uff0c\u90a3\u8be5\u662f\u65b0\u846c\u7684\u4eba\uff1f\u201d \u3000\u3000\u201c\u65b0\u575f\u5f53\u7136\u662f\u65b0\u846c\u7684\uff0c\u5c0f\u5144\u5f1f\u672a\u514d\u591a\u6b64\u4e00\u95ee\u3002\u201d \u3000\u3000\u201c\u53ef\u6709\u4e00\u4f4d\u5973\u4fa0\u662f\u65b0\u8fd1\u5b89\u846c\u7684\uff1f\u201d \u3000\u3000\u201c\u90a3\u5893\u5730\u867d\u7136\u79bb\u655d\u5eb5\u5f88\u8fd1\uff0c\u4f46\u8d2b\u5c3c\u5374\u751a\u5c11\u5230\u8fc7\uff0c\u6839\u672c\u4e0d\u77e5\u6709\u4ec0\u4e48\u5973\u4fa0\u846c\u90a3\u91cc\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u6005\u7136\u8f9e\u51fa\uff0e\u4e34\u8d70\u524d\uff0c\u8001\u5c3c\u5429\u5490\u4e0d\u51c6\u5bf9\u4efb\u4f55\u4eba\u900f\u9732\u4eca\u591c\u4e4b\u4e8b\u3002 \u3000\u3000\u6b21\u65e5\u6574\u4e2a\u4e0a\u5348\uff0e \u3000\u3000\u4ed6\u4ecd\u5728\u5c3c\u5eb5\u9644\u8fd1\u5f98\u5f8a\uff0c\u5e0c\u671b\u80fd\u89c1\u5230\u9ed1\u8863\u8499\u9762\u5973\u5b50\uff0c\u504f\u504f\u5c31\u8fde\u90a3\u8001\u5c3c\u4e5f\u4e0d\u66fe\u5728\u5c3c\u5eb5\u95e8\u5916\u51fa \u73b0\u3002 \u3000\u3000\u6700\u540e\uff0c\u9ea6\u5c0f\u660e\u53ea\u597d\u5bfb\u539f\u8def\u8d76\u5230\u5b9a\u8944\uff0e \u5367\u9f99\u751f\u300a\u5999\u7edd\u5929\u9999\u300b \u7b2c \u4e5d \u56de\u3000\u5927\u654c\u5f53\u524d \u3000\u3000\u9ea6\u5c0f\u660e\u56de\u5230\u5317\u5cb3\u8ff7\u8e2a\u8c37\uff0c\u5d02\u5c71\u4e09\u96c4\u5df2\u7ecf\u5148\u4ed6\u800c\u5230\uff0c\u8fd9\u662f\u56e0\u4e3a\u4ed6\u5728\u9014\u4e2d\u9047\u89c1\u90a3\u9ed1\u8863\u8499\u9762\u5973 \u5b50\u4ee5\u81f4\u591a\u803d\u8bef\u4e86\u2014\u5929\u4e4b\u6545\uff0e \u3000\u3000\u5176\u5b9e\uff0c\u5d02\u5c71\u4e09\u96c4\u53ea\u662f\u65e9\u5230\u4e86\u534a\u5929\uff0c\u56e0\u4e4b\uff0c\u949f\u4e00\u8c6a\u7b49\u4eba\u4e3a\u5d02\u5c71\u4e09\u96c4\u7684\u63a5\u98ce\u5bb4\u4e0a\uff0c\u9ea6\u5c0f\u660e\u4e5f \u8d76\u4e0a\u4e86\u53c2\u52a0\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u56e0\u4e3a\u6ca1\u5b8c\u6210\u4efb\u52a1\uff0c\u518d\u52a0\u82d7\u7d20\u82d3\u53c8\u88ab\u5e9e\u58eb\u51b2\u63b3\u8d70\uff0c\u5fc3\u4e2d\u96be\u514d\u4e00\u76f4\u95f7\u95f7\u4e0d\u4e50\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u548c\u4f59\u4ea6\u4e50\u7b49\u4eba\u95ee\u660e\u7ecf\u8fc7\u540e\uff0c\u514d\u4e0d\u4e86\u8981\u5b89\u6170\u4ed6\u51e0\u53e5\u3002 \u3000\u3000\u9152\u8fc7\u4e09\u5de1\uff0c\u83dc\u8fc7\u4e94\u5473\u3002 \u3000\u3000\u738b\u5927\u5eb7\u9996\u5148\u5f00\u8154\u9053\uff1a\u201c\u4ed6\u5976\u5976\u7684\uff0c\u970d\u5143\u4f3d\u90a3\u738b\u516b\u86cb\u5c45\u7136\u5f53\u4e0a\u4e86\u76df\u4e3b\uff0e\u4ffa\u8001\u738b\u771f\u662f\u4e0d\u670d \u6c14\uff01\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u4f4e\u58f0\u9053\uff1a\u201c\u738b\u5144\u8bf4\u8bdd\u6700\u597d\u5c0f\u58f0\u4e9b\uff01\u201d \u3000\u3000\u738b\u5927\u5eb7\u628a\u9152\u676f\u5f80\u684c\u4e0a\u4e00\u78d5\u9053\uff1a\u201c\u4f60\u4eec\u6015\u4ed6\uff0c\u4ffa\u8001\u738b\u53ef\u4e0d\u6015\u4ed6\uff0c\u5f85\u4f1a\u513f\u4ffa\u5c31\u53bb\u89c1\u4ed6\uff0c\u4ed6\u82e5 \u6562\u6446\u81ed\u67b6\u5b50\uff0c\u4ffa\u8001\u738b\u5f53\u573a\u5c31\u7ed9\u4ed6\u96be\u770b\uff01\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u76b1\u7709\u9053\uff1a\u201c\u8fd9\u4e0d\u662f\u6015\u4e0d\u6015\u7684\u95ee\u9898\uff0c\u65e2\u7136\u4eba\u5bb6\u5df2\u7ecf\u5f53\u4e0a\u4e86\u76df\u4e3b\uff0e\u54b1\u4eec\u5c31\u8be5\u542c\u4ed6 \u7684\u3002\u201d \u3000\u3000\u738b\u5927\u5eb7\u51b7\u7b11\u9053\uff1a\u201c\u4f59\u5148\u751f\u8fd9\u8bdd\uff0c\u771f\u53eb\u4ffa\u542c\u7740\u7a9d\u56ca\uff01\u201d \u3000\u3000\u9c8d\u8d85\u5fd9\u9053\uff1a\u201c\u8001\u4e09\u5c31\u5c11\u8bf4\u4e24\u53e5\u8bdd\u597d\u4e0d\u597d\uff0c\u4f59\u5148\u751f\u7684\u8bdd\u6ca1\u9519\uff0c\u54b1\u4eec\u521a\u521a\u6765\uff0e\u4ec0\u4e48\u60c5\u51b5\u90fd\u6ca1 \u5f04\u6e05\uff0e\u6700\u8981\u7d27\u7684\u5c31\u662f\u5148\u522b\u95f9\u4e8b\uff01\u201d \u3000\u3000\u6d2a\u6cfd\u4e5f\u63a5\u53e3\u9053\uff1a\u201c\u8001\u5927\u8bf4\u7684\u5bf9\uff0e\u8001\u4e09\u5343\u4e07\u5fcd\u8010\u4e9b\uff0c\u4f60\u82e5\u60f9\u4e86\u4e8b\uff0c\u54b1\u4eec\u4e09\u4e2a\u5728\u8ff7\u8e2a\u8c37\u6df7\u4e0d \u4e0b\u53bb\u4e8b\u5c0f\uff0c\u574f\u4e86\u949f\u526f\u76df\u4e3b\u548c\u4f59\u5148\u751f\u7684\u5927\u4e8b\uff0e\u90a3\u5c31\u4e0d\u662f\u54b1\u4eec\u4e09\u4e2a\u6240\u80fd\u62c5\u5f85\u7684\u4e86\u3002\u201d \u3000\u3000\u738b\u5927\u5eb7\u53ea\u597d\u4e0d\u518d\u5439\u80e1\u5b50\u77aa\u773c\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u642d\u8baa\u7740\u95ee\u9053\uff1a\u201c\u4e09\u4f4d\u5f53\u5bb6\u7684\u8fd8\u6ca1\u89c1\u8fc7\u970d\u5143\u4f3d\uff1f\u201d \u3000\u3000\u9c8d\u8d85\u9053\uff1a\u201c\u6211\u4eec\u5728\u8c37\u5916\u662f\u76f4\u63a5\u901a\u62a5\u949f\u526f\u76df\u4e3b\u7684\uff0c\u7531\u4f59\u5148\u751f\u628a\u6211\u4eec\u63a5\u4e86\u8fdb\u6765\uff0c\u521a\u6765\u4e0d\u5230\u4e24 \u4e2a\u65f6\u8fb0\uff0c\u4f60\u5c31\u6765\u4e86\uff0c\u8fd8\u6ca1\u6765\u5f97\u53ca\u53bb\u89c1\u970d\u76df\u4e3b\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u770b\u6837\u5b50\u4f60\u4eec\u4e09\u4f4d\u8fd8\u662f\u8981\u89c1\u4ed6\uff1f\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u5fd9\u62a2\u7740\u9053\uff1a\u201c\u5c0f\u660e\u5144\u5f1f\uff0c\u82e5\u4ed6\u4eec\u4e09\u4f4d\u4e0d\u5411\u970d\u5143\u4f3d\u62a5\u5230\uff0c\u5c06\u6765\u53c8\u5982\u4f55\u80fd\u5728\u8ff7\u8e2a\u8c37\u7acb \u8db3\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u54fc\u4e86\u58f0\u9053\uff1a\u201c\u89c1\u5c31\u89c1\u5427\uff01\u201d \u3000\u3000\u738b\u5927\u5eb7\u6700\u662f\u4e0d\u7518\u5bc2\u5bde\uff0c\u559d\u4e86\u53e3\u9152\u9053\uff1a\u201c\u526f\u76df\u4e3b\uff0c\u970d\u5143\u4f3d\u90a3\u8001\u738b\u516b\u86cb\u5f53\u4e0a\u76df\u4e3b\u540e\uff0c\u662f\u4e0d\u662f \u6210\u5929\u5230\u665a\u5728\u4f5c\u5a01\u4f5c\u798f\uff0c\u8e9c\u5f97\u50cf\u72d7\u718a\u4e00\u6837\uff1f\u201d \u3000\u3000\u949f\u4e00\u8c6a\u7b11\u9053\uff1a\u201c\u90a3\u5012\u4e0d\u4f1a\uff0c\u4ed6\u521d\u767b\u4e0a\u76df\u4e3b\u5b9d\u5ea7\uff0c\u5728\u57fa\u7840\u672a\u7a33\u4e4b\u524d\uff0c\u8fd8\u4e0d\u81f3\u4e8e\u592a\u8fc7\u5206\u3002\u201d \u3000\u3000\u201c\u8fd1\u6765\u8ff7\u8e2a\u8c37\u53ef\u53d1\u751f\u8fc7\u4ec0\u4e48\u5927\u4e8b\uff1f\u201d \u3000\u3000\u201c\u82e5\u8bf4\u8fd1\u6765\u8ff7\u8e2a\u8c37\u53d1\u751f\u8fc7\u7684\u5927\u4e8b\uff0c\u7b2c\u4e00\u4ef6\u4fbf\u662f\u970d\u5143\u4f3d\u5f53\u4e0a\u4e86\u76df\u4e3b\u3002\u201d \u3000\u3000\u738b\u5927\u5eb7\u5fcd\u4e0d\u4f4f\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u526f\u76df\u4e3b\u4e5f\u53d8\u5f97\u8fd9\u6837\u5e7d\u9ed8\uff01\u201d \u3000\u3000\u6b63\u8bf4\u8bdd\u95f4\uff0c\u5ffd\u542c\u7a97\u5916\u811a\u6b65\u58f0\u7531\u8fdc\u800c\u8fd1\uff0c\u63a5\u7740\u54cd\u8d77\u970d\u5143\u4f3d\u5475\u5475\u4e0d\u7edd\u7684\u7b11\u58f0\u3002 \u3000\u3000\u5e2d\u95f4\u6240\u6709\u7684\u4eba\u4e0d\u7531\u5168\u4e3a\u4e4b\u2014\u6014\u3002\u5404\u4eba\u4e92\u9012\u4e86\u4e2a\u773c\u8272\uff0e\u8c01\u90fd\u4e0d\u518d\u8a00\u8bed\u3002 \u3000\u3000\u53ea\u89c1\u7f57\u6d6e\u4e00\u53df\u970d\u5143\u4f3d\u5927\u6b65\u8d70\u8fdb\u996d\u5385\uff0c\u8eab\u540e\u7d27\u8ddf\u7740\u5cad\u5357\u4e8c\u5947\u7684\u641c\u9b42\u624b\u5df4\u5929\u4e49\u548c\u62d8\u9b42\u7d22\u5b8b\u5929 \u94ce\uff0e \u3000\u3000\u970d\u5143\u4f3d\u5b8c\u5168\u4e0d\u662f\u4e00\u5411\u7684\u9634\u6c89\u51b7\u6f20\u6a21\u6837\uff0c\u663e\u5f97\u5174\u9ad8\u91c7\u70c8\uff0c\u6ee1\u9762\u6625\u98ce\uff0c\u8fb9\u8d70\u8fb9\u5475\u5475\u7b11\u9053\uff1a \u201c\u771f\u662f\u5929\u5927\u7684\u597d\u6d88\u606f\uff0c\u5d02\u5c71\u7684\u4e09\u4f4d\u8001\u5f1f\u53c8\u56de\u5230\u4e86\u54b1\u4eec\u8ff7\u8e2a\u8c37\uff0c\u8001\u592b\u63a5\u9a7e\u6765\u8fdf\uff0c\u5343\u4e07\u6d77\u6db5\uff01\u201d \u3000\u3000\u5e2d\u95f4\u6240\u6709\u7684\u4eba\uff0c\u4e0d\u5f97\u4e0d\u8d77\u7acb\u76f8\u8fce\uff0c\u5c24\u5176\u5d02\u5c71\u4e09\u96c4\uff0c\u66f4\u663e\u5f97\u5341\u5206\u5c34\u5c2c\uff0e\u7b11\u9762\u4f5b\u9c8d\u8d85\u5e72\u54b3\u4e86 \u4e24\u58f0\u9053\uff1a\u201c\u5144\u5f1f\u88ab\u949f\u526f\u76df\u4e3b\u6d3e\u8fdb\u8c37\u6765\uff0c\u8fd8\u6ca1\u6765\u5f97\u53ca\u62dc\u89c1\u76df\u4e3b\uff0c\u4e0d\u60f3\u7adf\u6709\u52b3\u76df\u4e3b\u4eb2\u81ea\u524d\u6765\uff0c\u5b9e \u5728\u4e0d\u6562\u5f53\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u7b11\u9053\uff1a\u201c\u81ea\u5bb6\u5144\u5f1f\uff0c\u4f55\u5fc5\u5ba2\u6c14\uff0c\u5404\u4f4d\u5feb\u5feb\u8bf7\u5750\uff0c\u7ee7\u7eed\u559d\u9152\u7528\u83dc\uff01\u201d \u3000\u3000\u949f\u4e00\u8c6a\u7acb\u5373\u547d\u6587\u5929\u751f\u548c\u4e07\u6620\u971e\u518d\u642c\u6765\u4e09\u628a\u6905\u5b50\uff0c\u8bf7\u970d\u5143\u4f3d\u548c\u5cad\u5357\u4e8c\u5947\u5165\u5e2d\u3002 \u3000\u3000\u970d\u5143\u4f3d\u548c\u5cad\u5357\u4e8c\u5947\u4e5f\u5c31\u8001\u5b9e\u4e0d\u5ba2\u6c14\u7684\u5750\u4e0b\u3002 \u3000\u3000\u867d\u7136\u5982\u6b64\uff0c\u573a\u9762\u4ecd\u662f\u663e\u5f97\u5341\u5206\u5c34\u5c2c\uff0c\u5b8c\u5168\u5931\u53bb\u5148\u524d\u7684\u6b22\u4e50\u6c14\u6c1b\uff0e \u3000\u3000\u970d\u5143\u4f3d\u9996\u5148\u5411\u5d02\u5c71\u4e09\u96c4\u656c\u9152\uff0c\u4e00\u9762\u8bf4\u9053\uff1a\u201c\u4e09\u4f4d\u79bb\u5f00\u8ff7\u8e2a\u8c37\uff0c\u7b97\u6765\u5df2\u7ecf\u5feb\u4e09\u5e74\u5566\uff0c\u8fd9\u51e0 \u5e74\u5fc5\u5b9a\u8d8a\u53d1\u5f97\u610f\u4e86\u5427\uff1f\u201d \u3000\u3000\u738b\u5927\u5eb7\u62a2\u7740\u9053\uff1a\u201c\u518d\u5f97\u610f\u4e5f\u4e0d\u8fc7\u662f\u5728\u5d02\u5c71\u90a3\u533a\u533a\u4e4b\u5730\u5750\u5c71\u4e3a\u738b\uff0c\u6bd4\u8d77\u60a8\u8fd9\u4f4d\u7eff\u6797\u5927\u76df \u4e3b\uff0e\u90a3\u53ef\u662f\u5dee\u5f97\u8fdc\u5566\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u5c34\u5c2c\u4e00\u7b11\u9053\uff1a\u201c\u8001\u592b\u4f55\u5fb7\u4f55\u80fd\uff0c\u82e5\u4e0d\u662f\u949f\u526f\u76df\u4e3b\u548c\u5728\u573a\u5404\u4f4d\u540c\u9053\u8c2c\u8d5e\u63a8\u6234\uff0c\u90a3\u91cc \u4f1a\u6709\u4eca\u5929\u3002\u201d \u3000\u3000\u53ea\u542c\u9ea6\u5c0f\u660e\u54fc\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u4f60\u522b\u8bf4\u5f97\u597d\u542c\uff0c\u63a8\u6234\u4f60\u505a\u76df\u4e3b\u7684\u53ef\u6ca1\u6709\u6211\uff01\u201d \u3000\u3000\u5750\u5728\u4e00\u65c1\u7684\u4f59\u4ea6\u4e50\u7acb\u5373\u77aa\u4e86\u9ea6\u5c0f\u660e\u4e00\u773c\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u603b\u7b97\u89c1\u597d\u5c31\u6536\uff0c\u5e76\u6ca1\u518d\u8bf4\u4e0b\u53bb\uff0e \u3000\u3000\u5c82\u77e5\u738b\u5927\u5eb7\u5374\u63a5\u7740\u9053\uff1a\u201c\u8fd9\u76df\u4e3b\u7684\u5b9d\u5ea7\uff0c\u5750\u8d77\u6765\u4e00\u5b9a\u5f88\u8212\u670d\uff0c\u5982\u679c\u80fd\u8ba9\u4ffa\u8001\u738b\u5750\u4e0a\u53bb\u8fc7 \u8fc7\u763e\uff0c\u5373\u4f7f\u65e9\u4e0a\u5750\u4e0a\u53bb\u665a\u4e0a\u5c31\u6b7b\u4e5f\u5fc3\u7518\u60c5\u613f\u3002\u201d \u3000\u3000\u8001\u4e8c\u6d2a\u6cfd\u6025\u6025\u7528\u624b\u8098\u649e\u4e86\u738b\u5927\u5eb7\u4e00\u4e0b\uff0c\u53f1\u9053\uff1a\u201c\u6240\u4ee5\u4f60\u624d\u4e0d\u591f\u8d44\u683c\u5750\uff01\u201d \u3000\u3000\u5728\u573a\u6240\u6709\u7684\u4eba\uff0c\u672c\u4ee5\u4e3a\u970d\u5143\u4f3d\u5fc5\u5b9a\u607c\u7f9e\u6210\u6012\uff0c\u8bf4\u4e0d\u5b9a\u4f1a\u5f53\u573a\u53d1\u4f5c\uff0c\u8c01\u6599\u4ed6\u4f9d\u7136\u795e\u8272\u81ea \u82e5\uff0c\u5bf9\u9ea6\u5c0f\u660e\u548c\u738b\u5927\u5eb7\u7684\u8bdd\uff0c\u6839\u672c\u4e0d\u4e88\u7406\u4f1a\u3002 \u3000\u3000\u4e0d\u8fc7\u4ed6\u5e76\u672a\u5403\u5230\u6563\u5e2d\uff0c\u4fbf\u8d77\u8eab\u9053\uff1a\u201c\u4eca\u5929\u6765\u4e0d\u53ca\u5566\uff0c\u660e\u5929\u4e2d\u5348\uff0c\u8001\u592b\u518d\u5728\u805a\u4e49\u5385\u8bbe\u7b75\u4e3a \u5d02\u5c71\u4e09\u4f4d\u8001\u5f1f\u63a5\u98ce\u6d17\u5c18\uff0c\u5404\u4f4d\u8bf7\u7ee7\u7eed\u996e\u98df\u3002\u8001\u592b\u5931\u966a\u4e86\uff0e\u5148\u544a\u8f9e\u4e00\u6b65\uff01\u201d \u3000\u3000\u5cad\u5357\u4e8c\u5947\u4e5f\u8ddf\u7740\u7ad9\u8d77\u6765\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u4e3a\u4e86\u4e0d\u5931\u793c\u8c8c\uff0c\u4eb2\u81ea\u628a\u970d\u5143\u4f3d\u548c\u5cad\u5357\u4e8c\u5947\u9001\u51fa\u5927\u95e8\uff0c\u7136\u540e\u518d\u56de\u5e2d\u5165\u5ea7\u3002 \u3000\u3000\u53ea\u542c\u738b\u5927\u5eb7\u9053\uff1a\u201c\u4e24\u5e74\u591a\u4e0d\u89c1\uff0e\u8fd9\u8001\u5c0f\u5b50\u53d8\u5f97\u6bd4\u4ee5\u524d\u5ba2\u6c14\u591a\u5566\uff0c\u8fd8\u771f\u6709\u70b9\u76df\u4e3b\u67b6\u52bf\uff01\u201d \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u5168\u6028\u949f\u67d0\u987e\u8651\u4e0d\u5468\uff0e\u4ee5\u81f4\u6ca1\u966a\u540c\u4e09\u4f4d\u5148\u53bb\u89c1\u4ed6\uff0c\u5982\u6b64\u2014\u6765\uff0e\u96be\u514d\u8ba9\u4ed6\u5bf9\u54b1 \u4eec\u53c8\u591a\u4e86\u4e00\u5206\u731c\u5fcc\u3002\u201d \u3000\u3000\u738b\u5927\u5eb7\u4e24\u773c\u731b\u7728\u4e86\u51e0\u7728\u9053\uff1a\u201c\u526f\u76df\u4e3b\uff0e\u4e24\u5e74\u591a\u4e0d\u89c1\uff0c\u4f60\u4e5f\u53d8\u5566\uff01\u201d \u3000\u3000\u949f\u4e00\u8c6a\u82e6\u7b11\u9053\uff1a\u201c\u949f\u67d0\u4e0d\u662f\u9f50\u5929\u5927\u5723\u5b59\u609f\u7a7a\uff0c\u6709\u4ec0\u4e48\u53ef\u53d8\u7684\uff1f\u201d \u3000\u3000\u738b\u5927\u5eb7\u9053\uff1a\u201c\u4f60\u53d8\u5f97\u5931\u53bb\u4e86\u5f53\u5e74\u90a3\u79cd\u8c6a\u6c14\uff0c\u597d\u50cf\u771f\u7684\u5df2\u7ecf\u670d\u4e86\u59d3\u970d\u7684\u90a3\u8001\u5c0f\u5b50\u3002\u201d \u3000\u3000\u949f\u4e00\u8c6a\u6b63\u8272\u9053\uff1a\u201c\u65e2\u7136\u4ed6\u5df2\u505a\u4e86\u7eff\u6797\u76df\u4e3b\uff0c\u54b1\u4eec\u5c31\u8be5\u652f\u6301\u4ed6\u3002\u5982\u679c\u4ed6\u771f\u80fd\u9769\u9762\u6d17\u5fc3\u4e3a\u7eff \u6797\u505a\u4e00\u756a\u4e8b\uff0c\u54b1\u4eec\u62e5\u6234\u4ed6\u53c8\u6709\u4ec0\u4e48\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u738b\u5927\u5eb7\u6487\u4e86\u6487\u5634\u9053\uff1a\u201c\u6c5f\u5c71\u6613\u6539\uff0e\u672c\u6027\u96be\u79fb\u3002\u90a3\u8001\u5c0f\u5b50\u82e5\u80fd\u56de\u5934\u5411\u5584\uff0c\u9664\u975e\u65e5\u5934\u6253\u897f\u8fb9 \u51fa\u6765\uff01\u201d \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u5982\u679c\u4ed6\u771f\u6709\u4ec0\u4e48\u52a3\u8ff9\u51fa\u73b0\uff0c\u54b1\u4eec\u5f53\u7136\u8981\u89c1\u673a\u884c\u4e8b\uff0c\u81f3\u5c11\u4ed6\u4e0a\u4efb\u4ee5\u6765\u8fd9\u4e9b \u5929\uff0c\u8fd8\u6ca1\u505a\u8fc7\u4ec0\u4e48\u574f\u4e8b\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u51b7\u58f0\u9053\uff1a\u201c\u8c01\u8bf4\u90a3\u8001\u738b\u516b\u86cb\u6ca1\u505a\u8fc7\u574f\u4e8b\uff0c\u4ed6\u8981\u82d7\u59d1\u5a18\u505a\u5979\u7684\u538b\u5be8\u592b\u4eba\uff0e\u96be\u9053\u662f\u597d \u4e8b\u4e0d\u6210\uff1f\u201d \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u82d7\u59d1\u5a18\u7a76\u7adf\u662f\u600e\u6837\u8ddf\u4e86\u4ed6\uff0c\u54b1\u4eec\u5230\u73b0\u5728\u8fd8\u6ca1\u5f04\u6e05\u695a\uff0e\u5982\u679c\u662f\u82d7\u59d1\u5a18\u5fc3\u7518\u60c5 \u613f\u60f3\u8ddf\u4ed6\uff0c\u90a3\u5c31\u6028\u4e0d\u5f97\u970d\u5143\u4f3d\u4e86\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u8fd9\u4e0d\u662f\u53ef\u80fd\u7684\uff0c\u82d7\u59d1\u5a18\u600e\u4f1a\u770b\u4e0a\u90a3\u4e48\u4e2a\u8001\u5bb6\u4f19\uff1f\u201d \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u5973\u4eba\u770b\u4e0a\u4e86\u7537\u4eba\uff0c\u6709\u7684\u662f\u770b\u4e0a\u4e86\u4eba\uff0c\u6709\u7684\u662f\u770b\u4e0a\u52bf\uff0c\u4e5f\u6709\u7684\u662f\u770b\u4e0a\u4e86\u8d22\uff0e \u8c01\u6562\u4fdd\u82d7\u59d1\u5a18\u4e0d\u662f\u770b\u4e0a\u4e86\u4ed6\u7684\u8d22\u548c\u52bf\uff1f\u201d \u3000\u3000\u201c\u82d7\u59d1\u5a18\u4e0d\u53ef\u80fd\u662f\u90a3\u79cd\u4eba\uff01\u201d \u3000\u3000\u201c\u4f60\u5bf9\u82d7\u59d1\u5a18\u4e86\u89e3\u591a\u5c11\uff0c\u600e\u80fd\u65ad\u5b9a\u5979\u4e0d\u662f\u90a3\u79cd\u4eba\u5462\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u5bf9\u949f\u4e00\u8c6a\u8fd9\u756a\u8bdd\uff0c\u5f53\u7136\u4e0d\u670d\u6c14\uff0e \u3000\u3000\u6b63\u6b32\u518d\u8fa9\uff0c\u5374\u53c8\u88ab\u4f59\u4ea6\u4e50\u4ee5\u773c\u8272\u963b\u4f4f\u3002 \u3000\u3000\u4f59\u4ea6\u4e50\u5fc3\u91cc\u6709\u6570\uff0c\u65b9\u624d\u949f\uff0d\u8c6a\u7684\u8bdd\uff0c\u53ea\u662f\u5728\u963b\u6b62\u9ea6\u5c0f\u660e\u56e0\u6b64\u60f9\u7978\uff0c\u81f3\u4e8e\u4ed6\u5fc3\u91cc\u60f3\u7684\uff0c\u90a3 \u53c8\u662f\u53e6\u2014\u56de\u4e8b\u3002 \u3000\u3000\u5ffd\u542c\u738b\u5927\u5eb7\u9053\uff1a\u201c\u9ea6\u8001\u5f1f\uff0c\u548c\u4f60\u5728\u4e00\u8d77\u7684\u90a3\u4f4d\u82d7\u59d1\u5a18\u600e\u4e48\u4e0d\u89c1\u4e86\uff1f\u201d \u3000\u3000\u6b64\u8bed\u4e00\u51fa\uff0c\u4f17\u4eba\u96be\u514d\u90fd\u5927\u611f\u832b\u7136\u3002 \u3000\u3000\u4e00\u65f6\u4e4b\u95f4\uff0c\u4e03\u5634\u516b\u820c\u7684\u8be2\u95ee\u662f\u600e\u4e48\u56de\u4e8b\uff0e \u3000\u3000\u9ea6\u5c0f\u660e\u53ea\u5f97\u628a\u82d7\u7d20\u82d3\u548c\u81ea\u5df1\u5982\u4f55\u9003\u79bb\u9634\u624b\u4e00\u9b54\u9b54\u638c\u4ee5\u53ca\u534a\u8def\u53c8\u88ab\u5e9e\u58eb\u51b2\u63b3\u8d70\u7684\u7ecf\u8fc7\u5927\u7565 \u53d9\u8ff0\u4e86\u4e00\u904d\u3002 \u3000\u3000\u4f59\u4ea6\u4e50\u9053\uff1a\u201c\u5982\u679c\u90a3\u4f4d\u82d7\u7d20\u82d3\u59d1\u5a18\u80fd\u968f\u4f60\u540c\u6765\uff0c\u5bf9\u54b1\u4eec\u5012\u662f\u4ef6\u5927\u6709\u5e2e\u52a9\u7684\u4e8b\uff0c\u5979\u6ca1\u80fd\u6765 \u771f\u662f\u53ef\u60dc\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4f60\u8bf4\u5979\u6765\u4e86\u4ee5\u540e\uff0c\u5bf9\u54b1\u4eec\u6709\u4ec0\u4e48\u5e2e\u52a9\uff1f\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u9053\uff1a\u201c\u5979\u548c\u82d7\u7d20\u82d3\u662f\u59ca\u59b9\uff0c\u5982\u679c\u5979\u6765\u4e86\uff0c\u5c31\u4e0d\u96be\u67e5\u51fa\u82d7\u7d20\u5170\u5f03\u54b1\u4eec\u7684\u539f\u56e0\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u6211\u4f55\u5c1d\u4e0d\u662f\u8fd9\u4e48\u60f3\uff0c\u53ef\u60dc\u73b0\u5728\u4ec0\u4e48\u90fd\u522b\u8c08\u4e86\uff01\u201d \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u7167\u4f60\u8fd9\u4e48\u8bf4\uff0c\u8c37\u76df\u4e3b\u7684\u9057\u4f53\uff0c\u5e9e\u58eb\u51b2\u4e5f\u6ca1\u627e\u5230\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u770b\u6837\u5b50\u4ed6\u4e00\u5b9a\u4e5f\u6ca1\u627e\u5230\uff0c\u4e0d\u8fc7\u6211\u5012\u9047\u89c1\u4e86\u8c37\u76df\u4e3b\uff01\u201d \u3000\u3000\u4ed6\u8fd9\u6700\u540e\u4e00\u53e5\u8bdd\uff0c\u987f\u4f7f\u5168\u573a\u4e4b\u4eba\u4e3a\u4e4b\u4e00\u6014\u3002 \u3000\u3000\u4f59\u4ea6\u4e50\u5fd9\u9053\uff1a\u201c\u5f53\u7740\u8fd9\u4e48\u591a\u4eba\uff0c\u4f60\u5f00\u7684\u4ec0\u4e48\u73a9\u7b11\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u4e24\u773c\u4e00\u77aa\u9053\uff1a\u201c\u6211\u624d\u4e0d\u662f\u5f00\u73a9\u7b11\u5462\uff0c\u5979\u957f\u5f97\u82e5\u548c\u8c37\u76df\u4e3b\u6709\u534a\u70b9\u4e24\u6837\uff0c\u6211\u60c5\u613f\u5c06\u8111 \u888b\u5272\u4e0b\u6765\u4ea4\u7ed9\u4f60\u4eec\uff01\u201d \u3000\u3000\u201c\u542c\u4f60\u7684\u8bed\u6c14\uff0c\u662f\u5728\u8def\u4e0a\u9047\u5230\u4e00\u4e2a\u5973\u4eba\u5f88\u50cf\u8c37\u76df\u4e3b\uff1f\u201d \u3000\u3000\u201c\u5c82\u6b62\u5f88\u50cf\uff0c\u6839\u672c\u5c31\u548c\u8c37\u76df\u4e3b\u6d3b\u8131\u662f\u4e00\u4e2a\u4eba\uff01\u201d \u3000\u3000\u201c\u4f60\u4e0d\u59a8\u628a\u7ecf\u8fc7\u60c5\u5f62\u4ed4\u7ec6\u8bf4\u8bf4\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u968f\u5373\u53c8\u628a\u90a3\u665a\u7684\u60c5\u5f62\u4ed4\u7ec6\u63cf\u8ff0\u4e86\u2014\u756a\uff0e \u3000\u3000\u4f59\u4ea6\u4e50\u7b11\u9053\uff1a\u201c\u4eba\u7684\u5dee\u522b\uff0c\u4e3b\u8981\u662f\u5728\u8138\u4e0a\uff0c\u4f60\u6839\u672c\u6ca1\u770b\u5230\u5979\u7684\u5bb9\u8c8c\uff0e\u600e\u80fd\u8bf4\u548c\u8c37\u76df\u4e3b\u4e00 \u6a21\u4e00\u6837\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u5e76\u4e0d\u670d\u6c14\uff0e\u9053\uff1a\u201c\u4f60\u53ef\u770b\u5230\u4e16\u4e0a\u6709\u4e24\u4e2a\u4eba\u8eab\u6750\u548c\u4e3e\u52a8\u5b8c\u5168\u4e00\u6a21\u2014\u6837\u7684\uff1f\u54b1\u4eec\u4e24\u4e2a \u4e00\u6837\u4e0d\u4e00\u6837\uff1f\u5373\u4f7f\u53cc\u80de\u80ce\u5144\u5f1f\u59d0\u59b9\u4e5f\u6ca1\u89c1\u6709\u5b8c\u5168\u4e00\u6a21\u4e00\u6837\u7684\uff01\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u9053\uff1a\u201c\u5c31\u7b97\u5979\u7684\u9762\u8c8c\u4e5f\u50cf\u8c37\u76df\u4e3b\uff0e\u4f46\u58f0\u97f3\u5374\u53c8\u4e0d\u4e00\u6837\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4f60\u4e5f\u662f\u8d70\u904d\u5927\u6c5f\u5357\u5317\u9ec4\u6cb3\u4e24\u5cb8\u7684\u4eba\uff0c\u603b\u8be5\u77e5\u9053\u6b66\u6797\u4e2d\u6709\u4e00\u79cd\u53d8\u97f3\u836f\u53ef\u4ee5\u6539 \u53d8\u58f0\u97f3\u7684\u5427\uff1f\u201d \u3000\u3000\u201c\u5c31\u7b97\u5979\u4ec0\u4e48\u90fd\u50cf\u8c37\u76df\u4e3b\uff0c\u4f46\u5374\u7ec8\u7a76\u4e0d\u662f\u8c37\u76df\u4e3b\uff0c\u4eba\u6b7b\u5c82\u6709\u590d\u6d3b\u4e4b\u7406\uff1f\u201d \u3000\u3000\u201c\u5979\u4e0d\u662f\u771f\u6b63\u7684\u8c37\u76df\u4e3b\u4e5f\u6ca1\u5173\u7cfb\uff0c\u53ea\u8981\u54b1\u4eec\u80fd\u8bbe\u6cd5\u627e\u5230\u5979\u5c31\u597d\uff0e\u201d \u3000\u3000\u201c\u627e\u5230\u5979\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u628a\u5979\u8bf7\u5230\u8ff7\u8e2a\u8c37\u6765\u3002\u201d \u3000\u3000\u201c\u6765\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u54b1\u4eec\u8ddf\u5979\u5546\u8bae\u597d\uff0c\u8981\u5979\u81ea\u79f0\u662f\u8c37\u76df\u4e3b\u590d\u6d3b\uff0c\u8fd9\u6837\u4e00\u6765\uff0c\u970d\u5143\u4f3d\u90a3\u8001\u738b\u516b\u86cb\u5c82\u4e0d\u8981\u628a\u76df \u4e3b\u7684\u5b9d\u5ea7\u8ba9\u51fa\u6765\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u7684\u8fd9\u79cd\u60f3\u6cd5\uff0c\u867d\u7136\u8fd1\u4f3c\u8352\u5510\u3002\u4f46\u5374\u8d62\u5f97\u4e0d\u5c11\u4eba\u5fc3\u6709\u540c\u611f\uff0c\u81f3\u5c11\u8fd9\u662f\u4e00\u79cd\u7cbe\u795e\u4e0a\u7684 \u6170\u85c9\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u90a3\u4f4d\u5973\u4fa0\u770b\u6765\u5fc5\u5b9a\u4e5f\u662f\u6b66\u6797\u4eba\u7269\uff0c\u54b1\u4eec\u62e5\u6234\u5979\u767b\u4e0a\u76df\u4e3b\u5b9d\u5ea7\uff0c\u4e00\u5207 \u7528\u4e0d\u7740\u5979\u64cd\u5fc3\u3002\u53ea\u8981\u770b\u5230\u5979\uff0c\u5c31\u7b49\u4e8e\u770b\u5230\u8c37\u76df\u4e3b\uff0c\u5927\u5bb6\u60f3\u60f3\uff0c\u770b\u2014\u4e2a\u5927\u7f8e\u4eba\uff0c\u4e0d\u662f\u6bd4\u770b\u970d\u5143 \u4f3d\u90a3\u8001\u738b\u516b\u86cb\u8212\u670d\u591a\u4e86\u4e48\uff1f\u201d \u3000\u3000\u5728\u573a\u4f17\u4eba\uff0c\u7acb\u523b\u5f15\u8d77\u4e00\u9635\u54c4\u7b11\u3002 \u3000\u3000\u4f59\u4ea6\u4e50\u9053\uff1a\u201c\u4f60\u8bf4\u7684\u5927\u5bb6\u56fa\u7136\u9ad8\u5174\uff0e\u53ef\u60dc\u627e\u4e0d\u5230\u90a3\u4f4d\u5973\u4fa0\u4e5f\u662f\u6789\u7136\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u770b\u6765\u5012\u662f\u5341\u5206\u8ba4\u771f\uff0c\u7565\u2014\u6c89\u541f\u9053\uff1a\u201c\u90a3\u5ea7\u5c3c\u5eb5\u5c31\u5728\u592a\u539f\u4e0e\u5b9a\u8944\u4e4b\u95f4\u7684\u77f3\u5cad\u5173\u9644 \u8fd1\uff0c\u79bb\u54b1\u4eec\u8ff7\u8e2a\u8c37\u4e0d\u8fc7\u4e24\u5929\u884c\u7a0b\uff0c\u8fc7\u4e24\u5929\u6211\u60c5\u613f\u518d\u53bb\u4e00\u8d9f\u3002\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u9053\uff1a\u201c\u542c\u4f60\u65b9\u624d\u6240\u8bf4\uff0c\u90a3\u4f4d\u8001\u5c3c\u4f3c\u4e4e\u4e0d\u613f\u548c\u7537\u5ba2\u63a5\u89e6\uff0e\u4f60\u53bb\u53ea\u6015\u5f04\u4e0d\u51fa\u4ec0\u4e48\u7ed3 \u679c\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u5077\u5077\u671b\u4e86\u4e07\u6620\u971e\u4e00\u773c\u9053\uff1a\u201c\u54b1\u4eec\u8fd9\u91cc\uff0c\u73b0\u5728\u53ea\u6709\u4e07\u59d1\u5a18\u2014\u4e2a\u662f\u5973\u4eba\uff0c\u90a3\u5c31\u7531\u4e07\u59d1 \u5a18\u548c\u6211\u4e00\u9053\u53bb\u3002\u201d \u3000\u3000\u4e07\u6620\u9707\u671b\u4e86\u671b\u6587\u5929\u751f\uff0c\u7f9e\u602f\u602f\u5730\u4f4e\u4e0b\u4e86\u5934\u3002 \u3000\u3000\u539f\u6765\u4e07\u6620\u971e\u548c\u6587\u5929\u751f\u65e2\u662f\u5e08\u5144\u59b9\uff0c\u4e5f\u662f\u81ea\u5e7c\u9752\u6885\u7af9\u9a6c\u7684\u60c5\u4fa3\uff0c\u5979\u7684\u7236\u4eb2\u795e\u97ad\u98de\u68ad\u4e07\u6653\u5149 \u4e34\u7ec8\u524d\uff0c\u66f4\u66fe\u628a\u4e07\u6620\u971e\u7684\u7ec8\u8eab\u6258\u5490\u6587\u5929\u751f\u3002 \u3000\u3000\u6b64\u523b\u9ea6\u5c0f\u660e\u8981\u4e07\u6620\u971e\u540c\u5230\u90a3\u5ea7\u5c3c\u5eb5\uff0c\u5979\u5f53\u7136\u4e0d\u80fd\u4e0d\u5f81\u6c42\u6587\u5929\u751f\u7684\u540c\u610f\u3002 \u3000\u3000\u6587\u5929\u751f\u6b63\u5728\u4e3a\u96be\uff0c\u949f\u4e00\u8c6a\u5df2\u505a\u4e86\u51b3\u5b9a\u9053\uff1a\u201c\u90a3\u5c31\u7531\u6587\u8001\u5f1f\u548c\u4e07\u59d1\u5a18\u4e00\u8d77\u966a\u5c0f\u660e\u5144\u5f1f\u53bb\u597d \u4e86\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4f60\u51c6\u5907\u8981\u6211\u4eec\u4ec0\u4e48\u65f6\u5019\u52a8\u8eab\uff1f\u201d \u3000\u3000\u949f\u2014\u8c6a\u9053\uff1a\u201c\u8fd9\u662f\u4f60\u8981\u53bb\u7684\uff0e\u4e0d\u662f\u6211\u8981\u4f60\u53bb\uff0c\u6211\u7684\u610f\u601d\uff0c\u6700\u597d\u7b49\u660e\u5929\u970d\u76df\u4e3b\u4e3a\u4ed6\u4eec\u5d02\u5c71 \u4e09\u4f4d\u63a5\u8fc7\u98ce\u518d\u8d70\uff0e\u201d \u3000\u3000\u805a\u4e49\u5385\u5185\uff0c\u5e2d\u5f00\u4e09\u684c\uff0c\u8fd9\u662f\u7f57\u6d6e\u4e00\u53df\u970d\u5143\u4f3d\u4e3a\u5d02\u5c71\u4e09\u96c4\u8bbe\u4e0b\u7684\u63a5\u98ce\u5bb4\u3002 \u3000\u3000\u53c1\u4e0e\u76db\u5bb4\u7684\uff0c\u4e0d\u7528\u8bf4\u90fd\u662f\u8ff7\u8e2a\u8c37\u6709\u5934\u6709\u8138\u7684\u4eba\u7269\u3002\u5176\u4e2d\u6700\u4ee4\u4eba\u77a9\u76ee\u7684\uff0c\u8be5\u662f\u82d7\u7d20\u5170\u4ee5\u5973 \u4e3b\u4eba\u8eab\u4efd\u51fa\u573a\u4eae\u76f8\u3002 \u3000\u3000\u5176\u5b9e\u8bf4\u7a7f\u4e86\u4e5f\u7b97\u4e0d\u5f97\u4ec0\u4e48\uff0c\u5728\u8ff7\u8e2a\u8c37\u7684\u6570\u767e\u4eba\u4e2d\uff0c\u6709\u8d44\u683c\u643a\u7737\u53c2\u52a0\u7684\uff0c\u4e5f\u53ea\u6709\u970d\u5143\u4f3d\u4e00 \u4eba\u800c\u5df2\uff0e \u3000\u3000\u5176\u4f59\u4e0d\u662f\u6253\u7740\u738b\u8001\u4e94\u7684\u65d7\u53f7\uff0c\u5c31\u662f\u7737\u5c5e\u4e0d\u66fe\u968f\u6765\u8ff7\u8e2a\u8c37\u3002 \u3000\u3000\u82d7\u7d20\u5170\u663e\u5f97\u5341\u5206\u5927\u65b9\uff0c\u6beb\u65e0\u7f9e\u6da9\u4e4b\u6001\uff0c\u8fdb\u573a\u65f6\u548c\u6240\u6709\u5728\u573a\u7684\u4eba\u90fd\u70b9\u5934\u4e3a\u793c\u3002 \u3000\u3000\u53ea\u662f\u4e0d\u66fe\u5f00\u53e3\u8bf4\u8bdd\u3002 \u3000\u3000\u970d\u5143\u4f3d\u5374\u663e\u5f97\u795e\u8272\u9887\u4e3a\u51dd\u91cd\uff0e\u5f3a\u505a\u7b11\u989c\u4e3a\u5d02\u5c71\u4e09\u96c4\u656c\u8fc7\u9152\u540e\uff0c\u4e45\u4e45\u4e0d\u53d1\u2014\u8bed\u3002 \u3000\u3000\u8fd9\u60c5\u5f62\u53ea\u770b\u5f97\u53c2\u4e0e\u9152\u5bb4\u7684\u4eba\u90fd\u5927\u611f\u7eb3\u95f7\uff0c\u4f46\u5374\u65e0\u4eba\u5f00\u53e3\u8be2\u95ee\u3002 \u3000\u3000\u9152\u8fc7\u4e09\u5de1\u3002 \u3000\u3000\u641c\u9b42\u624b\u5df4\u5929\u4e49\u5e72\u54b3\u4e86\u58f0\u9053\uff1a\u201c\u76df\u4e3b\uff0e\u8fd9\u662f\u4ef6\u91cd\u8981\u5927\u4e8b\uff0c\u6538\u5173\u54b1\u4eec\u8ff7\u8e2a\u8c37\u7684\u5b89\u5371\u5b58\u4ea1\uff0c\u60a8 \u5c31\u8be5\u5f53\u4f17\u5ba3\u5e03\uff0c\u8ba9\u5927\u5bb6\u77e5\u9053\uff0c\u4ee5\u4fbf\u5171\u8c0b\u5bf9\u7b56\u624d\u6210\u3002\u201d \u3000\u3000\u8fd9\u51e0\u53e5\u8bdd\uff0c\u53ea\u542c\u5f97\u5728\u573a\u4f17\u4eba\u66f4\u662f\u4e2a\u4e2a\u9707\u60ca\uff0e \u3000\u3000\u518d\u770b\u970d\u5143\u4f3d\u65f6\uff0c\u7ec8\u4e8e\u7f13\u7f13\u7531\u5e2d\u4f4d\u4e0a\u7ad9\u4e86\u8d77\u6765\u3002\u8bed\u6c14\u6c89\u51dd\u7684\u9053\uff1a\u201c\u8bf8\u4f4d\u53ef\u542c\u8bf4\u8fc7\u9ed1\u9053\u4e2d\u6709 \u4e2a\u53eb\u6bd2\u706b\u6210\u5168\u7684\u4eba\u4e48\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u7684\u8fd9\u4e00\u95ee\uff0c\u95ee\u5f97\u7b80\u76f4\u6709\u4e9b\u6ca1\u5934\u6ca1\u8111\u3002 \u3000\u3000\u4e0d\u8fc7\u5728\u573a\u7684\u4eba\uff0c\u51e1\u662f\u5728\u6c5f\u6e56\u8d70\u52a8\u8fc7\u51e0\u5e74\u7684\uff0c\u5374\u6ca1\u4e00\u4e2a\u4e0d\u77e5\u9053\u9ed1\u9053\u4e0a\u6709\u8fd9\u4e48\u4e00\u53f7\u54cd\u5f53\u5f53\u7684 \u4eba\u7269\u53eb\u6bd2\u706b\u6210\u5168\uff0e \u3000\u3000\u63d0\u8d77\u6210\u5168\uff0c\u582a\u79f0\u662f\u5f53\u4eca\u6b66\u6797\u7b2c\u4e00\u6697\u5668\u9ad8\u624b\uff0c\u6b66\u529f\u4e5f\u9ad8\u4e0d\u53ef\u6d4b\uff0c\u4e3a\u4eba\u4e5f\u6700\u662f\u9634\u9669\u72e0\u6bd2\uff0e \u3000\u3000\u4ed6\u672c\u6765\u5df2\u4e8c\u5341\u5e74\u672a\u5728\u6c5f\u6e56\u51fa\u73b0\uff0c\u76f4\u5230\u4e24\u5e74\u524d\u624d\u548c\u53e6\u5916\u51e0\u4e2a\u9ed1\u9053\u9b54\u5934\u8054\u624b\u51fa\u51fb\uff0c\u80e1\u67cf\u9f84\u4e4b \u6b7b\uff0c\u548c\u4ed6\u4e5f\u626f\u5f97\u4e0a\u5173\u7cfb\u3002 \u3000\u3000\u56e0\u4e4b\uff0c\u63d0\u8d77\u6bd2\u706b\u6210\u5168\uff0c\u51e0\u4e4e\u4eba\u4eba\u95fb\u4e4b\u8272\u53d8\u3002 \u3000\u3000\u738b\u5927\u5eb7\u6539\u4e0d\u4e86\u4ed6\u90a3\u6025\u6027\u5b50\uff0c\u89c1\u4f17\u4eba\u90fd\u6ca1\u6709\u505a\u58f0\uff0c\u4fbf\u62a2\u7740\u95ee\u9053\uff1a\u201c\u970d\u76df\u4e3b\u4e3a\u4ec0\u4e48\u5ffd\u7136\u63d0\u8d77 \u6bd2\u706b\u6210\u5168\u90a3\u8001\u5c0f\u5b50\u6765\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u5e76\u672a\u56de\u7b54\uff0c\u518d\u95ee\u9053\uff1a\u201c\u8fd8\u6709\u4e00\u4e2a\u53eb\u9ed1\u9b54\u65f6\u5bc5\u7684\uff0c\u8bf8\u4f4d\u4e5f\u8be5\u542c\u8bf4\u8fc7\u5427\uff1f\u201d \u3000\u3000\u53ea\u542c\u5e2d\u4e2d\u4e00\u4eba\u9053\uff1a\u201c\u76df\u4e3b\u8bf4\u7684\u8fd9\u4eba\uff0c\u53ef\u662f\u8001\u9ed1\u9b54\u65f6\u4f5b\u4e4b\u5b50\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u9894\u9996\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u65f6\u5bc5\u6b63\u662f\u65f6\u4f5b\u4e4b\u5b50\uff0c\u56db\u5341\u5e74\u524d\uff0e\u8001\u9ed1\u9b54\u65f6\u4f5b\u4ee5\u9ed1\u7802\u638c\u72ec\u6b65\u5929 \u4e0b\uff0c\u66fe\u5728\u9ec4\u6cb3\u6e21\u53e3\uff0c\u4e00\u4e2a\u65f6\u8fb0\u4e4b\u5185\uff0c\u8fde\u6bd9\u6b66\u5f53\u6d3e\u5341\u4e94\u540d\u9ad8\u624b\uff0e \u3000\u3000\u5f53\u65f6\u8fd9\u4ef6\u4e8b\u9707\u60ca\u4e86\u6574\u4e2a\u6b66\u6797\uff0c\u73b0\u5728\u65f6\u4f5b\u867d\u7136\u5df2\u7ecf\u6545\u53bb\u591a\u5e74\uff0c\u4f46\u4ed6\u7684\u513f\u5b50\u65f6\u5bc5\u5374\u5c3d\u5f97\u4e43\u7236 \u771f\u4f20\uff0c\u9ed1\u7802\u638c\u4e0a\u7684\u9020\u8be3\uff0c\u534a\u70b9\u4e0d\u8f93\u65f6\u4f5b\uff0e\u201d \u3000\u3000\u65b9\u624d\u53d1\u8bdd\u90a3\u4eba\u9053\uff1a\u201c\u76df\u4e3b\u5ffd\u7136\u90d1\u91cd\u5176\u4e8b\u7684\u63d0\u5230\u6bd2\u706b\u6210\u5168\u548c\u9ed1\u9b54\u65f6\u5bc5\u8fd9\u4e24\u540d\u9ed1\u9053\u9b54\u5934\uff0c\u53ef \u662f\u6709\u4ec0\u4e48\u7528\u610f\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u8138\u8272\u6108\u89c1\u51dd\u91cd\uff0e \u3000\u3000\u2014\u5b57\u2014\u5b57\u7684\u9053\uff1a\u201c\u4e0d\u7792\u8bf8\u4f4d\uff0c\u8001\u592b\u6628\u5929\u6df1\u591c\u5ffd\u7136\u63a5\u5230\u4ed6\u4eec\u5177\u540d\u7684\u4e00\u5c01\u67ec\u5e16\u3002\u201d \u3000\u3000\u201c\u67ec\u5e16\u662f\u4ec0\u4e48\u4eba\u9001\u6765\u7684\uff1f\u4e0a\u9762\u8bf4\u4e9b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u67ec\u5e16\u662f\u8001\u592b\u7761\u68a6\u4e2d\u88ab\u4eba\u6295\u8fdb\u5ba4\u5185\u7684\uff0c\u54b1\u4eec\u8ff7\u8e2a\u8c37\u7684\u5404\u5904\u5173\u5361\u548c\u8001\u592b\u5ba4\u5916\u7684\u5de1\u903b\u5144\u5f1f\u5c45 \u7136\u4e0d\u66fe\u53d1\u89c9\u3002 \u3000\u3000\u81f3\u4e8e\u6295\u5e16\u7684\u7528\u610f\uff0e\u662f\u4ed6\u4eec\u8981\u5728\u4e09\u65e5\u4e4b\u5185\uff0c\u524d\u6765\u8ff7\u8e2a\u8c37\u62dc\u8bbf\u8001\u592b\u3002\u201d \u3000\u3000\u8fd9\u6d88\u606f\u679c\u7136\u4f7f\u5f97\u5168\u573a\u6240\u6709\u7684\u4eba\u90fd\u5927\u611f\u9707\u60ca\uff0c\u3000\u4e00\u65f6\u95f4\u4ea4\u5934\u63a5\u8033\uff0c\u8bae\u8bba\u7eb7\u7eb7\u3002 \u3000\u3000\u970d\u5143\u4f3d\u63a5\u7740\u53c8\u9053\uff1a\u201c\u5728\u67ec\u5e16\u5de5\u5177\u540d\u7684\u4e00\u5171\u4e09\u4eba\uff0c\u53e6\u4e00\u4e2a\u53eb\u725b\u5954\u3002\u201d \u3000\u3000\u738b\u5927\u5eb7\u8fde\u5fd9\u95ee\u9053\uff1a\u201c\u8fd9\u540d\u5b57\u542c\u6765\u602a\u602a\u7684\uff0c\u662f\u4e2a\u4ec0\u4e48\u6837\u7684\u4eba\u7269\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u8001\u592b\u5bf9\u65f6\u4e0b\u6b66\u6797\u4eba\u7269\uff0c\u4e0d\u8bba\u9ed1\u767d\u4e24\u9053\uff0c\u53ea\u8981\u662f\u6709\u5934\u6709\u8138\u7684\uff0c\u5373\u4f7f\u6ca1\u89c1\u8fc7\uff0c \u4e5f\u5fc5\u542c\u8bf4\u8fc7\uff0c\u504f\u504f\u4e5f\u4e0d\u6e05\u695a\u725b\u5954\u8fd9\u53f7\u4eba\u7269\uff0c\u4ed6\u65e2\u7136\u80fd\u548c\u6bd2\u706b\u6210\u5168\u3001\u9ed1\u9b54\u65f6\u5bc5\u540d\u5b57\u5e76\u5217\u53d1\u51fa\u62dc \u5e16\uff0c\u53ef\u89c1\u4e5f\u7edd\u4e0d\u662f\u7b49\u95f2\u4eba\u7269\u3002\u201d \u3000\u3000\u738b\u5927\u5eb7\u987f\u4e86\u4e00\u987f\u9053\uff1a\u201c\u4ed6\u4eec\u4e09\u4eba\u65e2\u7136\u8054\u540d\u6295\u5e16\u8981\u6765\u8ff7\u8e2a\u8c37\u62dc\u8bbf\u970d\u76df\u4e3b\uff0c\u6b63\u662f\u970d\u76df\u4e3b\u7684\u5149 \u5f69\uff0c\u4f60\u8fd8\u6709\u4ec0\u4e48\u4e0d\u9ad8\u5174\u7684\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u82e6\u7b11\u9053\uff1a\u201c\u738b\u8001\u5f1f\u5e94\u8be5\u6599\u60f3\u5f97\u5230\uff0c\u8fd9\u4e09\u4eba\u6765\u5230\u8ff7\u8e2a\u8c37\uff0c\u81ea\u7136\u4e0d\u6000\u597d\u610f\uff0c\u8bf4\u4e0d\u5b9a\u4f1a \u628a\u8ff7\u8e2a\u8c37\u95f9\u4e2a\u5929\u7ffb\u5730\u8986\uff0c\u5230\u90a3\u65f6\u80e1\u524d\u76df\u4e3b\u5728\u8ff7\u8e2a\u8c37\u4e00\u624b\u521b\u4e0b\u7684\u57fa\u4e1a\uff0c\u53ea\u6015\u5c31\u8361\u7136\u65e0\u5b58\u4e86\uff01\u201d \u3000\u3000\u738b\u5927\u5eb7\u4e0d\u52a8\u58f0\u8272\u9053\uff1a\u201c\u770b\u6765\u970d\u76df\u4e3b\u5fc5\u5b9a\u662f\u62c5\u5fc3\u8fd9\u76df\u4e3b\u5b9d\u5ea7\u4f1a\u88ab\u4ed6\u4eec\u62a2\u53bb\uff0c\u5bf9\u4e48\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u4e24\u592a\u9633\u7a74\u62bd\u52a8\u4e86\u51e0\u4e0b\u9053\uff1a\u201c\u5e76\u975e\u8001\u592b\u604b\u8fd9\u76df\u4e3b\u5b9d\u5ea7\u4e4b\u4f4d\uff0c\u8bf8\u4f4d\u5fc3\u91cc\u90fd\u8be5\u6709\u6570\uff0c\u82e5 \u8ba9\u4ed6\u4eec\u8fd9\u822c\u4eba\u5f53\u4e0a\u7eff\u6797\u76df\u4e3b\uff0c\u4eca\u540e\u7eff\u6797\u5c06\u4f1a\u53d8\u6210\u4ec0\u4e48\u6837\u5b50\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u8fd9\u8bdd\u8bf4\u5f97\u7684\u786e\u4e0d\u9519\u3002 \u3000\u3000\u82e5\u8bf4\u6bd2\u706b\u6210\u5168\u6216\u9ed1\u9b54\u65f6\u5bc5\u8fd9\u7b49\u9ed1\u9053\u715e\u661f\u51fa\u800c\u9886\u5bfc\u5929\u4e0b\u7eff\u6797\uff0c\u8fd8\u4e0d\u5982\u7531\u970d\u5143\u4f3d\u7ee7\u7eed\u9886\u5bfc\u3002 \u3000\u3000\u56e0\u4e3a\u6709\u949f\u4e00\u8c6a\u3001\u4f59\u4ea6\u4e50\u7b49\u4eba\u4ece\u65c1\u76d1\u7763\uff0c\u970d\u5143\u4f3d\u7edd\u4e0d\u53ef\u80fd\u505a\u51fa\u592a\u5927\u6076\u4e8b\uff0e\u4fbf\u82e5\u6362\u4e0a\u6bd2\u706b\u6210 \u5168\u6216\u9ed1\u9b54\u65f6\u5bc5\uff0c\u90a3\u4ed6\u4eec\u6beb\u65e0\u7591\u95ee\u7684\u5fc5\u5b9a\u4f1a\u4e3a\u6240\u6b32\u4e3a\u3002 \u3000\u3000\u53ea\u542c\u62d8\u9b44\u7d22\u5b8b\u5929\u94ce\u9053\uff1a\u201c\u76df\u4e3b\u65e2\u6709\u987e\u8651\uff0c\u5c31\u8be5\u62d2\u7edd\u4ed6\u4eec\u8fdb\u5165\u8ff7\u8e2a\u8c37\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u90a3\u6295\u5e16\u4e4b\u4eba\uff0c\u6839\u672c\u4e0d\u66fe\u4e0e\u8001\u592b\u89c1\u9762\uff0c\u8001\u592b\u53c8\u54ea\u6709\u673a\u4f1a\u8868\u793a\u610f\u89c1\u3002\u4f3a\u51b5\u5802\u5802 \u5929\u4e0b\u7eff\u6797\u603b\u5be8\u7684\u8ff7\u8e2a\u8c37\uff0c\u82e5\u516c\u7136\u62d2\u7edd\u4ed6\u4eec\u8fdb\u8c37\uff0e\u5c82\u4e0d\u7b49\u4e8e\u81ea\u884c\u627f\u8ba4\u6015\u4e86\u4ed6\u4eec\uff0e\u4e00\u65e6\u4f20\u626c\u51fa \u53bb\uff0c\u5fc5\u5b9a\u4f1a\u8d3b\u4eba\u7b11\u67c4\u3002\u201d \u3000\u3000\u5b8b\u5929\u94ce\u9634\u68ee\u68ee\u7b11\u4e86\u51e0\u58f0\u9053\uff1a\u201c\u54b1\u4eec\u8ff7\u8e2a\u8c37\u6709\u8fd9\u4e48\u591a\u5404\u8def\u82f1\u96c4\u597d\u6c49\uff0e\u96be\u9053\u8fd8\u6015\u4e86\u4ed6\u4eec\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u54b1\u4eec\u4eba\u6570\u867d\u591a\uff0c\u4f46\u82e5\u8bba\u5355\u6253\u72ec\u6597\uff0c\u6050\u6015\u8fd8\u6ca1\u6709\u4e00\u4eba\u80fd\u80dc\u8fc7\u4ed6\u4eec\u7684\u3002\u201d \u3000\u3000\u5b8b\u5929\u94ce\u54fc\u4e86\u58f0\u9053\uff1a\u201c\u4ed6\u4eec\u82e5\u4e0d\u8bb2\u6c5f\u6e56\u9053\u4e49\uff0c\u54b1\u4eec\u5f53\u7136\u4e5f\u7528\u4e0d\u7740\u987e\u8651\u4ec0\u4e48\uff0c\u5c31\u6765\u4e2a\u7fa4\u8d77\u56f4 \u653b\uff0c\u4e0d\u6101\u4e0d\u80fd\u628a\u4ed6\u4eec\u6d88\u706d\u5728\u8ff7\u8e2a\u8c37\u5185\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u8fd9\u79cd\u505a\u6cd5\u8001\u592b\u4f55\u5c1d\u6ca1\u8003\u8651\uff0c\u4f46\u4ed6\u4eec\u4e09\u4eba\u4e2d\uff0c\u6700\u9632\u4e0d\u80dc\u9632\u7684\u8981\u6570\u6bd2\u706b\u6210\u5168\uff0c \u4ed6\u7684\u6dec\u6bd2\u6697\u5668\uff0c\u652f\u652f\u89c1\u8840\u5c01\u5589\uff0e\u4e00\u51fa\u624b\u8db3\u53ef\u8fde\u4f24\u6570\u5341\u4eba\u6027\u547d\uff0c\u4e00\u65e6\u51b2\u7a81\u8d77\u6765\uff0c\u54b1\u4eec\u7684\u4f24\u4ea1\u4ee3 \u4ef7\u672a\u514d\u5c31\u592a\u5927\u4e86\u3002\u82e5\u4f60\u5b8b\u8001\u5f1f\u9996\u5f53\u5176\u51b2\uff0c\u53ea\u6015\u4e5f\u5f53\u573a\u96be\u9003\u6d3b\u547d\uff01\u201d \u3000\u3000\u5b8b\u5929\u94ce\u987f\u4e86\u987f\u9053\uff1a\u201c\u53ef\u662f\u76df\u4e3b\u603b\u8be5\u6709\u4e2a\u5e94\u654c\u4e4b\u7b56\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u62c2\u9aef\u7f04\u9ed8\u4e86\u534a\u664c\u9053\uff1a\u201c\u5bf9\u65b9\u6295\u5e16\u4e0a\u8a00\u660e\u4e09\u65e5\u4e4b\u5185\u524d\u6765\u8ff7\u8e2a\u8c37\uff0c\u53ef\u89c1\u5728\u4e09\u65e5\u4e4b\u5185\uff0e \u4ed6\u4eec\u968f\u65f6\u90fd\u53ef\u5230\u6765\u3002\u6b64\u523b\u6700\u8981\u7d27\u7684\u4e8b\uff0c\u5c31\u662f\u4ece\u73b0\u5728\u8d77\uff0c\u7531\u8c37\u5185\u5230\u8c37\u5916\u5404\u5904\u5173\u5361\uff0c\u5fc5\u987b\u591a\u6d3e\u4eba \u529b\u52a0\u5f3a\u8b66\u6212\u3002\u5426\u5219\uff0e\u82e5\u8ba9\u4ed6\u4eec\u6df7\u8fdb\u8c37\u5185\uff0e\u54b1\u4eec\u5c1a\u4e0d\u77e5\u60c5\uff0e\u90a3\u5c31\u662f\u5929\u5927\u7684\u7b11\u8bdd\u4e86\uff01\u201d \u3000\u3000\u5b8b\u5929\u94ce\u9053\uff1a\u201c\u8fd9\u65b9\u9762\u76df\u4e3b\u5c3d\u7ba1\u653e\u5fc3\uff0e\u5c5e\u4e0b\u4eec\u6563\u5e2d\u540e\u5c31\u5f00\u59cb\u628a\u5404\u5904\u5173\u5361\u91cd\u65b0\u5e03\u7f6e\uff0e\u5c5e\u4e0b\u672c \u4eba\u4ece\u73b0\u5728\u8d77\uff0c\u4e5f\u60c5\u613f\u81ea\u52a8\u8d1f\u8d23\u5de1\u591c\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u9894\u9053\uff1a\u201c\u90a3\u5c31\u6709\u52b3\u8bf8\u4f4d\u8f9b\u82e6\u4e86\uff0c\u81f3\u4e8e\u4ed6\u4eec\u4e09\u4eba\u5230\u8fd9\u540e\uff0c\u54b1\u4eec\u518d\u89c1\u673a\u884c\u4e8b\u3002\u201d \u3000\u3000\u6563\u5e2d\u540e\uff0c\u949f\u2014\u8c6a\u7b49\u4eba\u56de\u5230\u4e86\u67cf\u9f84\u9662\u3002 \u3000\u3000\u539f\u6765\u8fd9\u5904\u8ddd\u805a\u4e49\u5385\u4e0d\u8fdc\u7eff\u67f3\u836b\u4e2d\u7684\u72ec\u7acb\u9662\u843d\uff0c\u81ea\u8ff7\u8e2a\u8c37\u5efa\u7acb\u57fa\u4e1a\u4e4b\u540e\uff0c\u4fbf\u662f\u80e1\u67cf\u9f84\u548c\u8c37 \u5bd2\u9999\u7684\u5c45\u6240\u3002 \u3000\u3000\u76ee\u524d\u7fa4\u8c6a\u7531\u5929\u53f0\u5c71\u4e07\u82b1\u5bab\u8fd4\u56de\u540e\uff0c\u6700\u521d\u662f\u7531\u82d7\u7d20\u5170\u548c\u4e07\u6620\u971e\u5c45\u4f4f\uff0c\u8c01\u60f3\u5f53\u591c\u82d7\u7d20\u5170\u4fbf\u88ab \u970d\u5143\u4f3d\u63b3\u8d70\uff0c\u5269\u4e0b\u4e07\u6620\u971e\u2014\u4eba\u4e0d\u6562\u518d\u4f4f\u4e0b\u53bb\u3002 \u3000\u3000\u4e8e\u662f\u949f\u4e00\u8c6a\u4fbf\u51b3\u5b9a\u6240\u6709\u4eba\u90fd\u642c\u8fdb\u6765\u4f4f\uff0c\u5e76\u628a\u8fd9\u6240\u5ead\u9662\u53d6\u540d\u67cf\u9f84\u9662\uff0c\u4ee5\u793a\u5bf9\u524d\u4efb\u76df\u4e3b\u80e1\u67cf \u9f84\u7eaa\u5ff5\u4e4b\u610f\u3002 \u3000\u3000\u7fa4\u8c6a\u5e76\u672a\u4f11\u606f\uff0c\u56de\u5230\u67cf\u9f84\u9662\u540e\uff0c\u4fbf\u53c8\u805a\u96c6\u5728\u5ba2\u5385\u8bae\u4e8b\u3002 \u3000\u3000\u8fd9\u95f4\u5ba2\u5385\uff0c\u5728\u949f\u4e00\u8c6a\u7b49\u4eba\u6765\u8bf4\uff0c\u5c31\u7b97\u662f\u67cf\u9f84\u9662\u5185\u4e00\u95f4\u5c0f\u578b\u8bae\u4e8b\u5385\uff0c\u7fa4\u8c6a\u4eec\u7ecf\u5e38\u5728\u8fd9\u91cc\u805a \u4f1a\uff0c\u8fde\u5f00\u996d\u4e5f\u5728\u91cc\u3002 \u3000\u3000\u5750\u4e0b\u540e\uff0c\u949f\u4e00\u8c6a\u9996\u5148\u9053\uff1a\u201c\u5c0f\u660e\u5144\u5f1f\u524d\u5f80\u5c3c\u5eb5\u4e4b\u4e8b\uff0c\u53ef\u5426\u6682\u7f13\u51e0\u5929\uff1f\u201d \u3000\u3000\u4ed6\u56e0\u7d20\u77e5\u9ea6\u5c0f\u660e\u6027\u5b50\u4e0d\u597d\uff0c\u51e1\u4e8b\u4e0d\u5f97\u4e0d\u8ba9\u4ed6\u4e09\u5206\uff0c\u56e0\u4e4b\uff0c\u867d\u4ee5\u526f\u76df\u4e3b\u4e4b\u5c0a\uff0c\u5bf9\u9ea6\u5c0f\u660e\u8bf4 \u8bdd\uff0c\u5374\u4ece\u4e0d\u4f7f\u51fa\u547d\u4ee4\u8bed\u6c14\u3002 \u3000\u3000\u522b\u770b\u9ea6\u5c0f\u660e\u6027\u5b50\u4e0d\u597d\uff0c\u5374\u9887\u8bc6\u5927\u793c\uff0c\u7565\u4e00\u6c89\u541f\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u7684\u610f\u601d\uff0c\u8fd9\u51e0\u5929\u662f\u91cd\u8981\u65f6 \u523b\uff0c\u60c5\u51b5\u4e0d\u540c\uff0e\u54b1\u4eec\u8c01\u90fd\u4e0d\u8be5\u968f\u4fbf\u79bb\u5f00\uff0c\u5bf9\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u4f60\u65e2\u7136\u660e\u767d\uff0c\u90a3\u662f\u6700\u597d\u4e0d\u8fc7\uff0c\u54b1\u4eec\u7684\u4eba\uff0c\u529b\u91cf\u672c\u6765\u5c31\u5f88\u5355\u8584\uff0c\u4f60\u5728\u54b1\u4eec\u8fd9 \u4e9b\u4eba\u4e2d\u6b66\u529f\u6700\u9ad8\uff0c\u6240\u4ee5\u5728\u6bd2\u706b\u6210\u5168\u548c\u9ed1\u9b54\u65f6\u5bc5\u8fd9\u51e0\u4eba\u8981\u6765\u8ff7\u8e2a\u8c37\u7684\u8fd9\u6bb5\u65f6\u671f\uff0c\u4f60\u4e0d\u80fd\u4e0d\u5728 \u573a\uff0e\u201d \u3000\u3000\u8fd9\u51e0\u53e5\u8bdd\uff0c\u7b49\u4e8e\u7ed9\u9ea6\u5c0f\u660e\u6234\u4e0a\u4e00\u9876\u9ad8\u5e3d\u5b50\uff0c\u504f\u504f\u9ea6\u5c0f\u660e\u4e5f\u6700\u559c\u6b22\u6234\u9ad8\u5e3d\u5b50\uff0c\u95fb\u8a00\u7b11\u563b\u563b \u7684\u9053\uff1a\u201c\u6211\u672c\u6765\u5c31\u7b54\u5e94\u6682\u65f6\u4e0d\u5230\u5c3c\u5eb5\u53bb\u561b\uff0c\u54ea\u8fd8\u7528\u4f60\u518d\u89e3\u91ca\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u7684\u662f\u5fc3\u91cc\u8bdd\uff0c\u9ea6\u5c0f\u660e\u5e74\u5c11\u597d\u80dc\uff0c\u5373\u4f7f\u949f\u4e00\u8c6a\u4e0d\u63d0\u51fa\u8981\u6c42\uff0c\u4ed6\u4e5f\u6709\u610f\u7559\u4e0b\u6765\u770b\u573a\u70ed \u95f9\u3002 \u3000\u3000\u53ea\u542c\u738b\u5927\u5eb7\u9053\uff1a\u201c\u526f\u76df\u4e3b\uff0e\u4f60\u597d\u50cf\u771f\u60f3\u5e2e\u970d\u5143\u4f3d\u90a3\u8001\u5c0f\u5b50\uff0e\u54b1\u4eec\u6765\u4e2a\u8896\u624b\u65c1\u89c2\u5750\u5c71\u89c2\u864e \u6597\u4e0d\u662f\u5f88\u597d\u4e48\uff1f\u201d \u3000\u3000\u949f\u4e00\u8c6a\u6447\u5934\u9053\uff1a\u201c\u8bdd\u4e0d\u80fd\u8fd9\u4e48\u8bf4\uff0c\u54b1\u4eec\u65e2\u662f\u8ff7\u8e2a\u8c37\u7684\u4eba\uff0c\u5c31\u4e0d\u80fd\u770b\u8ff7\u8e2a\u8c37\u7684\u7b11\u8bdd\u3002 \u3000\u3000\u970d\u5143\u4f3d\u597d\u6b79\u603b\u662f\u54b1\u4eec\u7684\u76df\u4e3b\uff0c\u82e5\u54b1\u4eec\u4e0d\u60f3\u62e5\u6234\u4ed6\uff0c\u5c31\u8be5\u79bb\u5f00\u8ff7\u8e2a\u8c37\uff0c\u66f4\u4f55\u51b5\u82e5\u4e00\u65e6\u8ba9\u6210 \u5168\u3001\u65f6\u5bc5\u90a3\u822c\u4eba\u53d6\u970d\u5143\u4f3d\u800c\u4ee3\u4e4b\uff0c\u54b1\u4eec\u5728\u8ff7\u8e2a\u8c37\u5c31\u8fde\u7acb\u8db3\u4e4b\u5730\u4e5f\u6ca1\u6709\u4e86\uff01\u201d \u3000\u3000\u738b\u5927\u5eb7\u89c1\u949f\u4e00\u8c6a\u8bcd\u610f\u6073\u5207\uff0c\u8bf4\u7684\u53c8\u662f\u6b63\u7406\uff0c\u53ea\u5978\u4f4e\u4e0b\u5934\u4e0d\u518d\u8a00\u8bed\u3002 \u3000\u3000\u5ffd\u542c\u9b3c\u8bf8\u845b\u6d2a\u6cfd\u9053\uff1a\u201c\u54b1\u4eec\u662f\u5426\u53ef\u60f3\u4e2a\u529e\u6cd5\u628a\u6210\u5168\u3001\u65f6\u5bc5\u8fd9\u822c\u4eba\u6d88\u706d\u5728\u8c37\u5916\uff1f\u201d \u3000\u3000\u949f\u4e00\u8c6a\u76b1\u7709\u82e6\u7b11\u9053\uff1a\u201c\u6050\u6015\u4e0d\u662f\u90a3\u4e48\u9f50\u6613\uff0c\u8981\u60f3\u5728\u8c37\u5916\u6d88\u706d\u4ed6\u4eec\uff0e\u52bf\u5fc5\u4e34\u65f6\u5e03\u4e0b\u673a\u5173\u57cb \u4f0f\uff0e\u504f\u504f\u65f6\u5bc5\u548c\u6210\u5168\u7b49\u4eba\u53c8\u662f\u64c5\u957f\u673a\u5173\u57cb\u4f0f\u4e4b\u4eba\uff0c\u5982\u4f55\u80fd\u9a97\u5f97\u8fc7\u4ed6\u4eec\uff0e\u4e00\u65e6\u88ab\u4ed6\u4eec\u8bc6\u7834\uff0c\u5c82 \u4e0d\u5f04\u5de7\u6210\u62d9\u3002\u201d \u3000\u3000\u201c\u8ff7\u8e2a\u8c37\u5185\uff0c\u6709\u5f88\u591a\u5730\u65b9\uff0c\u5df2\u65e9\u6709\u673a\u5173\u7981\u5236\uff0c\u65e2\u7136\u8c37\u5916\u4e4b\u8ba1\u96be\u9042\uff0e\u628a\u4ed6\u4eec\u6d88\u706d\u5728\u8c37\u5185\u4e5f \u662f\u4e00\u6837\u3002\u201d\u9b3c\u8bf8\u845b\u6fc0\u52a8\u9053\u3002 \u3000\u3000\u201c\u8c37\u5185\u65e2\u8bbe\u7684\u673a\u5173\u5e03\u7f6e\uff0c\u4e5f\u4ec5\u662f\u51e0\u5904\u91cd\u8981\u5173\u5361\u624d\u6709\uff0c\u4f9d\u6211\u9884\u6599\uff0c\u4e5f\u7edd\u5bf9\u65e0\u6cd5\u7792\u8fc7\u4ed6\u4eec\u7684 \u773c\u775b\u3002\u201d \u3000\u3000\u9b3c\u8bf8\u845b\u4e5f\u4f4e\u4e0b\u5934\u4e0d\u518d\u8a00\u8bed\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u73af\u626b\u4e86\u4f17\u4eba\u4e00\u773c\u9053\uff1a\u201c\u5404\u4f4d\u8fd9\u5c31\u56de\u53bb\u4f11\u606f\u5427\uff0c\u82e5\u6709\u4e8b\u6545\u53d1\u751f\uff0c\u6211\u4f1a\u968f\u65f6\u901a\u77e5\u4f60 \u4eec\uff01\u201d \u3000\u3000\u4e00\u8fde\u4e24\u5929\u8fc7\u53bb\uff0c\u8ff7\u8e2a\u8c37\u5185\u5e76\u672a\u53d1\u751f\u4efb\u4f55\u4e8b\u6545\uff0e\u4f46\u6bcf\u4e2a\u4eba\u7684\u5185\u5fc3\uff0e\u5374\u662f\u8d8a\u6765\u8d8a\u7d27\u5f20\uff0c\u83ab\u975e \u90a3\u5c01\u67ec\u5e16\u662f\u522b\u4eba\u5047\u5192\u6210\u5168\u3001\u65f6\u5bc5\u3001\u725b\u5954\u4e4b\u540d\u800c\u6545\u610f\u9a9a\u6270\u8ff7\u8e2a\u8c37\u7684\uff1f\u6216\u662f\u5728\u6545\u5f04\u7384\u865a\u3002 \u3000\u3000\u5c31\u5728\u7b2c\u4e09\u5929\u7684\u4e0a\u5348\uff0c\u949f\u4e00\u8c6a\u7b49\u4eba\u53c8\u96c6\u5408\u5728\u67cf\u9f84\u9662\u7684\u5c0f\u578b\u8bae\u4e8b\u5385\u8bae\u4e8b\uff0c\u4e00\u540d\u5949\u970d\u5143\u4f3d\u4e4b\u547d \u7684\u5934\u76ee\u5306\u5306\u524d\u6765\u901a\u62a5\uff0c\u8bf4\u662f\u6bd2\u706b\u6210\u5168\u7b49\u4eba\u5df2\u5230\u8fbe\u8c37\u5916\uff0c\u8981\u7fa4\u8c6a\u5373\u523b\u524d\u5f80\u805a\u4e49\u5385\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u7b49\u2014\u4f19\u4eba\u968f\u5373\u5404\u81ea\u4f69\u597d\u5175\u5203\uff0c\u6765\u5230\u805a\u4e49\u5385\u5927\u95e8\u524d\u3002 \u3000\u3000\u805a\u4e49\u5385\u5916\u6b64\u523b\u5df2\u7ad9\u6ee1\u4e86\u4e0d\u5c11\u4eba\uff0c\u970d\u5143\u4f3d\u4e5f\u5728\u5176\u4e2d\uff0e \u3000\u3000\u5947\u602a\u7684\u662f\u5728\u8fd9\u79cd\u7d27\u5f20\u65f6\u523b\uff0c\u970d\u5143\u4f3d\u53cd\u800c\u663e\u5f97\u795e\u8272\u5341\u5206\u5e73\u9759\u3002 \u3000\u3000\u4f3c\u662f\u4e00\u526f\u80f8\u6709\u6210\u7af9\u6a21\u6837\uff0c\u548c\u4e09\u5929\u524d\u5728\u805a\u4e49\u5385\u7684\u8868\u73b0\u5927\u4e0d\u76f8\u540c\uff0c\u4ee4\u4eba\u5b9e\u5728\u5f04\u4e0d\u6e05\u4ed6\u846b\u82a6\u91cc \u5356\u7684\u4ec0\u4e48\u836f\u3002 \u3000\u3000\u4e00\u89c1\u949f\u4e00\u8c6a\u7b49\u4eba\u5230\u6765\uff0c\u970d\u5143\u4f3d\u7acb\u5373\u8d8b\u524d\u5bf9\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u65b9\u624d\u8c37\u5916\u5de1\u903b\u5f1f\u5144\u56de\u62a5\uff0e\u4ed6\u4eec\u5df2 \u7ecf\u5230\u8fbe\uff0c\u8001\u592b\u51b3\u5b9a\u5c31\u5728\u805a\u4e49\u5385\u5916\u7b49\u5019\u4ed6\u4eec\u3002\u201d \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u8fd9\u6837\u770b\u6765\uff0c\u4ed6\u4eec\u662f\u4f9d\u793c\u8fdb\u8c37\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u7b11\u9053\uff1a\u201c\u5728\u4e8b\u60c5\u6ca1\u6f14\u53d8\u5230\u6700\u540e\uff0c\u4e00\u5207\u90fd\u5f88\u96be\u9884\u6599\u3002\u201d \u3000\u3000\u201c\u53ef\u66fe\u6d3e\u4eba\u5230\u8c37\u5916\u8fce\u63a5\uff1f\u201d \u3000\u3000\u201c\u4ed6\u4eec\u65e2\u7136\u6309\u7167\u6c5f\u6e56\u793c\u6570\u8fdb\u8c37\uff0c\u54b1\u4eec\u5f53\u7136\u4e5f\u8981\u4f9d\u793c\u76f8\u5f85\u3002\u8001\u592b\u5df2\u6d3e\u51fa\u5df4\u5929\u4e49\u548c\u5b8b\u5929\u94ce\u5230 \u8c37\u53e3\u8fce\u63a5\u4ed6\u4eec\uff0e\u4e0d\u4e45\u4fbf\u53ef\u5230\u8fbe\u3002\u201d \u3000\u3000\u6b63\u8bf4\u8bdd\u95f4\uff0c\u641c\u9b42\u624b\u5df4\u5929\u4e49\u5df2\u966a\u7740\u6bd2\u706b\u6210\u5168\u7b49\u4eba\u7531\u805a\u4e49\u5385\u5bf9\u9762\u7684\u8c37\u53e3\u7f13\u7f13\u800c\u6765\u3002 \u3000\u3000\u6bd2\u706b\u6210\u5168\u8d70\u5728\u6700\u524d\uff0c\u6b64\u4eba\u751f\u5f97\u4e11\u964b\u65e0\u6bd4\uff0e\u4e00\u5f20\u9a6c\u8138\uff0c\u534a\u8fb9\u767d\uff0e\u534a\u8fb9\u7ea2\uff0e\u767d\u7684\u6beb\u65e0\u8840\u8272\uff0c \u7ea2\u7684\u9c9c\u8273\u593a\u76ee\u3002 \u3000\u3000\u7531\u4e8e\u4ed6\u8fd9\u5f20\u4e16\u6240\u7f55\u89c1\u7684\u602a\u8138\uff0c\u4ee4\u4eba\u770b\u4e0d\u51fa\u4ed6\u7684\u5e74\u7eaa\uff0c\u4e0d\u8fc7\u636e\u8bf4\u6b64\u4eba\u56db\u5341\u5e74\u524d\u4fbf\u5df2\u6210\u540d\u6c5f \u6e56\uff0c\u6210\u4e3a\u9ed1\u9053\u5de8\u64d8\uff0c\u7b97\u6765\u81f3\u5c11\u4e5f\u8be5\u516d\u5341\u4ee5\u4e0a\u4e86\uff0e \u3000\u3000\u8d70\u5728\u7b2c\u4e8c\u7684\uff0e\u662f\u9ed1\u9b54\u65f6\u5bc5\u3002 \u3000\u3000\u6b64\u4eba\u4e94\u77ed\u8eab\u6750\uff0c\u7626\u9aa8\u5d99\u5ccb\uff0c\u76ae\u80a4\u9edd\u9ed1\uff0c\u4f46\u5374\u53cc\u81c2\u7279\u957f\uff0c\u8fdc\u8fdc\u770b\u53bb\uff0c\u5b9b\u5982\u4e00\u53ea\u957f\u81c2\u733f\uff0e \u3000\u3000\u6700\u540e\u4e00\u4eba\uff0c\u4e0d\u7528\u8bf4\u662f\u725b\u5954\u4e86\u3002 \u3000\u3000\u8fd9\u4eba\u8eab\u6750\u77ee\u80d6\uff0c\u9762\u5706\u65e0\u987b\uff0c\u5934\u5927\u809a\u5927\uff0c\u53cc\u817f\u5947\u77ed\uff0c\u8d70\u8d77\u8def\u6765\uff0c\u4e00\u7f35\u4e00\u7f35\uff0e\u65e2\u50cf\u9e2d\u5b50\uff0c\u53c8 \u50cf\u4f01\u9e45\uff0e \u3000\u3000\u641c\u9b42\u624b\u5df4\u5929\u4e49\u5728\u65c1\u7d27\u7d27\u966a\u968f\uff0e \u3000\u3000\u4f46\u5374\u4e0d\u89c1\u53e6\u4e00\u524d\u53bb\u63a5\u5ba2\u7684\u62d8\u9b44\u7d22\u5b8b\u5929\u94ce\u3002 \u3000\u3000\u6bd2\u706b\u6210\u5168\u7b49\u4eba\u7684\u8fd9\u526f\u5f62\u76f8\uff0c\u79f0\u5f97\u4e0a\u662f\u540d\u526f\u5176\u5b9e\u7684\u4e09\u4e2a\u602a\u7269\u3002 \u3000\u3000\u597d\u5728\u8fd9\u65f6\u805a\u4e49\u5385\u5916\u7684\u6c14\u6c1b\u51dd\u8083\u3002 \u3000\u3000\u5373\u4f7f\u6709\u4eba\u60f3\u7b11\uff0c\u4e5f\u53ea\u80fd\u7b11\u5728\u5fc3\u91cc\u3002 \u3000\u3000\u970d\u5143\u4f3d\u8fde\u5fd9\u5927\u6b65\u8fce\u4e0a\u524d\u53bb\uff0c\u53cc\u624b\u9ad8\u62f1\u8fc7\u5934\uff0c\u6717\u58f0\u9053\uff1a\u201c\u96be\u5f97\u4e09\u4f4d\u5927\u9a7e\u8fb1\u4e34\u8ff7\u8e2a\u8c37\uff0c\u970d\u67d0 \u6709\u5931\u8fdc\u8fce\uff0c\u8fd8\u671b\u591a\u591a\u6d77\u6db5\uff01\u201d \u3000\u3000\u6bd2\u706b\u6210\u5168\u53d1\u51fa\u9634\u9633\u602a\u6c14\u7684\u7b11\u58f0\u9053\uff1a\u201c\u970d\u8001\u5f1f\uff0c\u606d\u559c\u4f60\u8363\u767b\u7eff\u6797\u76df\u4e3b\u5b9d\u5ea7\uff0c\u6211\u4eec\u4e09\u4e2a\u4eca\u5929 \u6765\u5230\u8d35\u5b9d\u5730\uff0c\u8981\u5411\u4f60\u9053\u8d3a\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u53c8\u662f\u2014\u62b1\u62f3\uff0c\u7b11\u9053\uff1a\u201c\u4e0d\u6562\u5f53\uff01\u4e0d\u6562\u5f53\uff0c\u4e09\u4f4d\u8bf7\uff01\u201d \u3000\u3000\u8fdb\u5165\u5927\u5385\uff0e\u970d\u5143\u4f3d\u9996\u5148\u4e3a\u6bd2\u706b\u6210\u5168\u7b49\u5f15\u89c1\u949f\u4e00\u8c6a\u4ee5\u4e0b\u51e0\u540d\u91cd\u8981\u5934\u76ee\uff0e \u3000\u3000\u7136\u540e\u628a\u4e8c\u4eba\u8ba9\u4e0a\u5ba2\u4f4d\u5c31\u5750\u3002 \u3000\u3000\u6bd2\u706b\u6210\u5168\u4e5f\u5f53\u9762\u4ecb\u7ecd\u725b\u5954\u9053\uff1a\u201c\u4ed6\u4eec\u4e24\u4f4d\uff0c\u65f6\u8001\u5f1f\u540d\u6ee1\u4e2d\u539f\u6b66\u6797\uff0e\u7528\u4e0d\u7740\u8001\u592b\u591a\u8bf4\uff0c\u53ea \u662f\u8fd9\u4f4d\u725b\u4eb2\u6b63\uff0c\u53ef\u80fd\u8bf8\u4f4d\u8fd8\u4e0d\u8ba4\u8bc6\u3002\u201d \u3000\u3000\u5728\u573a\u7fa4\u96c4\uff0c\u4e00\u542c\u6bd2\u706b\u6210\u5168\u5c45\u7136\u628a\u725b\u5954\u79f0\u4e3a\u725b\u4eb2\u738b\uff0c\u90fd\u4e0d\u7981\u5927\u5927\u4e00\u6123\u3002 \u3000\u3000\u83ab\u975e\u6b64\u4eba\u662f\u5f53\u4eca\u7687\u4e0a\u7684\u7687\u65cf\uff1f\u4f46\u8fd9\u53c8\u7edd\u4e0d\u53ef\u80fd\u3002 \u3000\u3000\u56e0\u4e3a\u5f53\u4eca\u7687\u4e0a\u5e76\u4e0d\u59d3\u725b\uff0c\u800c\u4e14\u725b\u5954\u7684\u4e00\u8eab\u5947\u88c5\u5f02\u670d\uff0c\u4e5f\u7edd\u4e0d\u4f3c\u7687\u5bb6\u6253\u626e\u3002 \u3000\u3000\u66f4\u4f55\u51b5\u7687\u4eb2\u540c\u621a\u4e00\u7c7b\u8d35\u65cf\u4eba\u7269\uff0c\u4e5f\u7edd\u4e0d\u53ef\u80fd\u5230\u7eff\u6797\u603b\u5be8\u8ff7\u8e2a\u8c37\u8fd9\u79cd\u5730\u65b9\u6765\uff0c\u800c\u4e14\u6bd2\u706b\u6210 \u5168\u4e5f\u6ca1\u6709\u548c\u7687\u4eb2\u56fd\u621a\u6500\u4e0a\u4ea4\u60c5\u7684\u7406\u7531\u3002 \u3000\u3000\u6bd2\u706b\u6210\u5168\u987f\u4e86\u4e00\u987f\uff0c\u624d\u53c8\u8fdb\u4e00\u6b65\u89e3\u91ca\u9053\uff1a\u201c\u8bf8\u4f4d\u522b\u8bef\u4f1a\uff0c\u725b\u4eb2\u738b\u662f\u897f\u57df\u7684\u4eb2\u738b\uff0c\u4ed6\u662f\u5f53 \u4eca\u897f\u57df\u5bc6\u5b97\u7ea2\u6559\u91d1\u8f6e\u6cd5\u738b\u7684\u80de\u5f1f\u3002\u201d \u3000\u3000\u8fd9\u6837\u89e3\u91ca\uff0c\u7167\u6837\u4e5f\u4ee4\u5728\u573a\u7fa4\u96c4\u5927\u6709\u557c\u7b11\u7686\u975e\u4e4b\u611f\u3002 \u3000\u3000\u897f\u57df\u7684\u7ea2\u6559\u91d1\u8f6e\u6cd5\u738b\uff0c\u56fa\u7136\u662f\u7687\u5bb6\u5fa1\u5c01\uff0c\u4f46\u6cd5\u738b\u7684\u5144\u5f1f\uff0c\u5374\u7edd\u4e0d\u591f\u8d44\u683c\u79f0\u4e3a\u4eb2\u738b\u3002 \u3000\u3000\u679c\u771f\u5982\u6b64\uff0c\u6cd5\u738b\u4e4b\u4f4d\uff0c\u5c82\u4e0d\u662f\u4e0e\u5f53\u4eca\u7687\u4e0a\u65e0\u5f02\u3002\u56e0\u4e4b\uff0c\u7fa4\u96c4\u5305\u62ec\u970d\u5143\u4f3d\u5728\u5185\uff0c\u4e00\u65f6\u4e4b \u95f4\uff0c\u8c01\u90fd\u63d2\u4e0d\u4e0a\u5634\u53bb\u3002 \u3000\u3000\u6bd2\u706b\u6210\u5168\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u8bf4\u6765\u725b\u4eb2\u738b\u4e0e\u8001\u592b\u76f8\u8bc6\uff0c\u4e5f\u5e76\u975e\u5076\u7136\uff0c\u8001\u592b\u6570\u5e74\u524d\u66fe\u5230\u8fc7\u4e00\u6b21\u897f \u57df\uff0c\u8499\u91d1\u8f6e\u6cd5\u738b\u70ed\u60c5\u63a5\u5f85\uff0c\u5f53\u65f6\u4e5f\u5c31\u8ba4\u8bc6\u4e86\u725b\u4eb2\u738b\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u4e0d\u80fd\u8001\u4e0d\u642d\u8154\uff0c\u8f7b\u54b3\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u8fd9\u6837\u8bf4\u725b\u4eb2\u738b\u662f\u7531\u897f\u57df\u8fe2\u8fe2\u6570\u5343\u91cc\u524d\u6765\u7684 \u4e86\uff1f\u201d \u3000\u3000\u4ed6\u867d\u660e\u77e5\u4ee5\u725b\u4eb2\u738b\u4e09\u5b57\u6765\u79f0\u547c\u725b\u5954\u6709\u4e9b\u4e0d\u4f26\u4e0d\u7c7b\uff0c\u4f46\u5f53\u7740\u6210\u5168\u548c\u725b\u5954\u7684\u9762\uff0c\u5374\u53c8\u4e0d\u5f97\u4e0d \u5982\u6b64\u79f0\u547c\u3002 \u3000\u3000\u6bd2\u706b\u6210\u5168\u9053\uff1a\u201c\u771f\u662f\u5de7\u5f97\u5f88\uff0c\u534a\u6708\u524d\u725b\u4eb2\u738b\u7531\u897f\u57df\u6765\u8bbf\u8001\u592b\uff0c\u540c\u65f6\u4e5f\u5e0c\u671b\u6765\u4e00\u8d9f\u8ff7\u8e2a \u8c37\uff0e\u56e0\u4e4b\u8001\u592b\u5c31\u4e0e\u725b\u4eb2\u738b\u4ee5\u53ca\u65f6\u8001\u5f1f\u8054\u540d\u6295\u5e16\uff0c\u524d\u6765\u8d35\u5b9d\u5730\uff0e\u5176\u5b9e\u8fd9\u6b21\u524d\u6765\uff0c\u8be5\u662f\u4ee5\u725b\u4eb2\u738b \u4e3a\u4e3b\uff0c\u8001\u592b\u548c\u65f6\u8001\u5f1f\u53ea\u80fd\u7b97\u966a\u5ba2\u7684\u3002\u201d \u3000\u3000\u7fa4\u96c4\u4eec\u53ea\u542c\u5f97\u53c8\u662f\u4e00\u9635\u60ca\u8be7\uff0c\u725b\u5954\u627e\u5230\u8ff7\u8e2a\u8c37\u6765\uff0e\u7a76\u7adf\u4e3a\u4e86\u4f55\u4e8b\uff1f \u3000\u3000\u970d\u5143\u4f3d\u521a\u8981\u95ee\u660e\u725b\u5954\u524d\u6765\u8ff7\u8e2a\u8c37\u7684\u539f\u56e0\uff0c\u6bd2\u706b\u6210\u5168\u53c8\u9053\uff1a\u201c\u7b49\u4eba\u5230\u9f50\u4e86\uff0c\u54b1\u4eec\u518d\u8c08\u6b63 \u4e8b\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u8138\u8272\u4e00\u53d8\u9053\uff1a\u201c\u8fd8\u6709\u4ec0\u4e48\u4eba\u8981\u6765\uff1f\u201d \u3000\u3000\u5df4\u5929\u4e49\u9053\uff1a\u201c\u542c\u6210\u8001\u82f1\u96c4\u8bf4\uff0c\u662f\u4f4d\u7531\u82d7\u7586\u6765\u7684\u5cd2\u4e3b\u3002\u201d \u3000\u3000\u6bd2\u706b\u6210\u5168\u63a5\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u4eba\u59d3\u706b\u540d\u83bd\uff0c\u662f\u82d7\u7586\u706b\u4e91\u6d1e\u7684\u603b\u5cd2\u4e3b\uff0e\u4e5f\u662f\u8001\u592b\u7684\u6545\u53cb\u3002\u201d \u3000\u3000\u4e00\u76f4\u672a\u5f00\u53e3\u7684\u9ed1\u9e70\u65f6\u5bc5\uff0c\u5230\u8fd9\u65f6\u624d\u9053\uff1a\u201c\u6210\u5144\u5c31\u8be5\u628a\u706b\u603b\u5cd2\u4e3b\u7684\u6765\u5386\u5bf9\u970d\u76df\u4e3b\u4ed4\u7ec6\u8bf4 \u8bf4\uff01\u201d \u3000\u3000\u6bd2\u706b\u6210\u5168\u563f\u563f\u4e00\u7b11\u9053\uff1a\u201c\u4ed6\u59d3\u706b\u540d\u83bd\uff0c\u662f\u82d7\u7586\u706b\u4e91\u6d1e\u7684\u603b\u5cd2\u4e3b\u5df2\u7ecf\u8bf4\u8fc7\u5566\uff0c\u73b0\u5728\u8be5\u4ecb\u7ecd \u4ecb\u7ecd\u4ed6\u7684\u4e00\u8eab\u672c\u9886\u3002\u706b\u603b\u5cd2\u4e3b\u4ee5\u64c5\u4e8e\u65bd\u866b\u9a70\u540d\u82d7\u7586\uff0c\u66fe\u5f97\u4e86\u4e2a\u866b\u795e\u7684\u5916\u53f7\uff0c\u7531\u6b64\u4e0d\u96be\u5f97\u77e5\u4ed6 \u7684\u65bd\u866b\u6cd5\u672f\u6709\u591a\u9ad8\u660e\uff0e\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u7fa4\u96c4\u4eec\u4e0d\u5c11\u4eba\u90fd\u5df2\u542c\u5f97\u5927\u60ca\u5931\u8272\u3002 \u3000\u3000\u4ed6\u4eec\u867d\u672a\u4eb2\u773c\u89c1\u8fc7\u6709\u4eba\u65bd\u653e\u6bd2\u866b\uff0e\u4f46\u5374\u542c\u8bf4\u8fc7\u866b\u6bd2\u7684\u5389\u5bb3\uff0c\u4f20\u8bf4\u4e2d\u6bd2\u866b\u7684\u7e41\u6b96\u529b\u5f3a\u5f97\u4e0d \u80fd\u518d\u5f3a\u3002 \u3000\u3000\u5982\u679c\u6709\u4eba\u628a\u51e0\u5341\u53ea\u6bd2\u866b\u653e\u8fdb\u8ff7\u8e2a\u8c37\uff0e\u4e0d\u51fa\u534a\u6708\uff0c\u8db3\u53ef\u4f7f\u6574\u4e2a\u8ff7\u8e2a\u8c37\u5bf8\u8349\u4e0d\u751f\uff0c\u4eba\u755c\u7686 \u4ea1\uff0e\u5176\u5389\u5bb3\u53ef\u60f3\u800c\u77e5\uff0e \u3000\u3000\u970d\u5143\u4f3d\u8868\u9762\u867d\u8fd8\u80fd\u4fdd\u6301\u9547\u5b9a\uff0c\u9aa8\u5b50\u91cc\u96be\u514d\u4e5f\u80c6\u98a4\u5fc3\u60ca\u3002 \u3000\u3000\u6210\u5168\u628a\u706b\u83bd\u5e26\u6765\u8ff7\u8e2a\u8c37\uff0c\u5206\u660e\u662f\u5728\u5411\u7eff\u6797\u603b\u5be8\u793a\u5a01\uff0c\u5c24\u5176\u725b\u5954\u7684\u6765\u610f\u672a\u660e\uff0e\u66f4\u4ee4\u4ed6\u5185\u5fc3 \u5600\u5495\u4e0d\u5df2\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u9ed1\u9b54\u65f6\u5bc5\u89c6\u7ebf\u8f6c\u5411\u5927\u5385\u95e8\u5916\u53eb\u9053\uff1a\u201c\u6765\u5566\uff01\u706b\u603b\u5cd2\u4e3b\u6765\u5566\uff01\u201d \u3000\u3000\u7fa4\u96c4\u6025\u6025\u8f6c\u5934\u5411\u5927\u5385\u770b\u53bb\uff0c\u53ea\u89c1\u62d8\u9b44\u7d22\u5b8b\u5929\u94ce\u8fd9\u65f6\u6b63\u966a\u7740\u4e00\u540d\u602a\u4eba\u7f13\u7f13\u5411\u5927\u5385\u884c\u6765\u3002 \u3000\u3000\u8fd9\u4eba\u7684\u957f\u76f8\uff0c\u6bd4\u6210\u5168\u3001\u65f6\u5bc5\u3001\u725b\u5954\u4e09\u4eba\u66f4\u602a\uff0c\u5934\u9876\u79c3\u5f97\u95ea\u95ea\u53d1\u5149\uff0c\u504f\u504f\u56db\u5468\u5374\u53c8\u4e1b\u751f\u7740 \u4e00\u5708\u7ea2\u53d1\uff0c\u8138\u5b54\u5947\u5927\uff0c\u5706\u5f97\u50cf\u53ea\u74e6\u76c6\uff0c\u4e24\u773c\u6709\u5982\u94dc\u94c3\uff0c\u77b3\u7738\u5947\u9ec4\uff0c\u9f3b\u6881\u584c\u9677\u3002\u4eba\u4e2d\u5374\u53c8\u77ed\u5f97 \u4e0d\u80fd\u518d\u77ed\uff0c\u9f3b\u5b50\u51e0\u4e4e\u548c\u5634\u5df4\u8fde\u5728\u4e00\u8d77\uff0e\u53cc\u8033\u5012\u7ad6\uff0c\u6709\u70b9\u50cf\u5154\u5b50\uff0c\u6ee1\u9762\u866c\u987b\uff0c\u989c\u8272\u9ec4\u4e2d\u6cdb\u7ea2\u3002 \u3000\u3000\u5728\u4ed6\u8eab\u540e\uff0c\u5e76\u6709\u4e24\u540d\u4f8d\u4ece\u3002 \u3000\u3000\u8fd9\u4e24\u540d\u4f8d\u4ece\u90fd\u4e2a\u5934\u4e0d\u9ad8\uff0c\u8eab\u8eaf\u80a5\u58ee\uff0c\u5934\u4e0a\u5168\u6263\u7740\u4e00\u4e2a\u91d1\u7b8d\uff0c\u957f\u53d1\u8986\u76d6\u5230\u80a9\u5934\uff0c\u4e0a\u8eab\u662f\u2014 \u6233\u77ed\u8884\uff0c\u88e4\u5b50\u957f\u5ea6\u4ec5\u53ca\u819d\u76d6\uff0c\u9732\u51fa\u4e24\u6761\u50cf\u9152\u74f6\u6930\u5b50\u822c\u7684\u5c0f\u817f\uff0c\u8fde\u978b\u5b50\u4e5f\u6ca1\u7a7f\u3002 \u3000\u3000\u4ed6\u4eec\u624b\u91cc\u5e76\u5404\u63d0\u7740\u4e00\u4e2a\u50cf\u9e1f\u7b3c\u822c\u7684\u65b9\u5323\uff0c\u65b9\u5323\u56db\u5468\u7f69\u7740\u9ed1\u5e03\uff0c\u4e0d\u77e5\u9053\u7684\uff0c\u8fd8\u4ee5\u4e3a\u662f\u4e24\u7b3c \u753b\u7709\u9e1f\u5462\uff0c\u4f46\u7fa4\u96c4\u5fc3\u91cc\u90fd\u6709\u6570\uff0e\u91cc\u9762\u88c5\u7684\u5fc5\u662f\u5404\u79cd\u6bd2\u866b\u3002 \u3000\u3000\u8fd9\u65f6\u6bd2\u706b\u6210\u5168\u3001\u9ed1\u9b54\u65f6\u5bc5\u4ee5\u53ca\u725b\u5954\u5df2\u8d77\u8eab\u8fce\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u970d\u5143\u4f3d\u4e5f\u4e0d\u5f97\u4e0d\u8d77\u8eab\u76f8\u8fce\u3002 \u3000\u3000\u53ea\u542c\u706b\u83bd\u76f4\u7740\u55d3\u5b50\uff0c\u53d1\u51fa\u6ee1\u53e3\u6df7\u6d4a\u4e4b\u97f3\u9053\uff1a\u201c\u6210\u5927\u4fa0\u3001\u65f6\u5927\u4fa0\uff0c\u52b3\u4f60\u4eec\u4e45\u7b49\u4e86\uff0c\u672c\u603b\u5cd2 \u4e3b\u56e0\u4e3a\u529e\u4e86\u70b9\u79c1\u4e8b\uff0c\u6765\u8fdf\u5566\uff0c\u62b1\u6b49\u62b1\u6b49\uff01\u201d \u3000\u3000\u8bf4\u706b\u83bd\u7684\u55d3\u5b50\u662f\u76f4\u7684\uff0c\u800c\u4e14\u4e00\u53e3\u6d4a\u97f3\uff0c\u4e00\u70b9\u90fd\u4e0d\u8fc7\u5206\u3002 \u3000\u3000\u4ed6\u7684\u8bdd\u7684\u786e\u662f\u6ee1\u53e3\u86ee\u8154\u53d8\u8c03\uff0c\u4e0d\u662f\u4e45\u8d70\u6c5f\u6e56\u7684\u4eba\uff0c\u6839\u672c\u65e0\u6cd5\u542c\u61c2\uff0c\u53ef\u80fd\u8fd8\u4ee5\u4e3a\u4ed6\u662f\u5728\u8bf4 \u5916\u56fd\u8bdd\u5462\u3002 \u3000\u3000\u4ed6\u53ea\u987e\u548c\u6210\u5168\u3001\u65f6\u5bc5\u5bd2\u6684\uff0c\u5374\u5e76\u672a\u7406\u776c\u5728\u4e00\u65c1\u76f8\u8fce\u7684\u4e3b\u4eba\u970d\u5143\u4f3d\u3002 \u3000\u3000\u970d\u5143\u4f3d\u672c\u662f\u5fc3\u80f8\u72ed\u7a84\u7684\u4eba\uff0c\u4e00\u6c14\u4e4b\u4e0b\uff0c\u5e72\u8106\u4e5f\u4e0d\u4e3b\u52a8\u548c\u706b\u83bd\u6253\u62db\u547c\u3002 \u3000\u3000\u6bd2\u706b\u6210\u5168\u4e0d\u613f\u4e00\u5f00\u59cb\u5c31\u95f9\u5f97\u4e0d\u6109\u5feb\uff0c\u5fd9\u9053\uff1a\u201c\u8001\u592b\u8be5\u5411\u706b\u603b\u5cd2\u4e3b\u5f15\u89c1\u4e00\u4e0b\u8fd9\u91cc\u7684\u4e3b\u4eba\uff0c \u8fd9\u4f4d\u5c31\u662f\uff01\u201d\u8fb9\u8bf4\u8fb9\u5411\u970d\u5143\u4f3d\u671b\u4e86\u2014\u773c\u3002 \u3000\u3000\u706b\u83bd\u8fd9\u624d\u62ac\u81c2\u505a\u4e86\u4e2a\u602a\u624b\u52bf\u9053\uff1a\u201c\u539f\u6765\u8fd9\u4f4d\u5c31\u662f\u95fb\u540d\u4e2d\u539f\u7eff\u6797\u7684\u970d\u5927\u76df\u4e3b\uff0c\u672c\u603b\u5cd2\u4e3b\u5931 \u656c\u5566\uff01\u201d \u3000\u3000\u63a5\u7740\u4fbf\u662f\u5404\u4eba\u91cd\u65b0\u5165\u5ea7\u3002 \u3000\u3000\u8fd9\u65f6\u65e9\u5df2\u6709\u4eba\u9001\u4e0a\u8336\u6765\uff0c\u5206\u522b\u5728\u6210\u5168\u3001\u65f6\u5bc5\u3001\u725b\u5954\u3001\u706b\u83bd\u56db\u4eba\u9762\u524d\u7684\u8336\u51e0\u4e0a\u5404\u653e\u4e86\u4e00 \u76cf\u3002 \u3000\u3000\u8c01\u77e5\u56db\u4eba\u53ea\u662f\u5404\u770b\u4e86\u4e00\u773c\uff0c\u5e76\u65e0\u4e00\u4eba\u52a8\u624b\u53d6\u8336\u3002 \u3000\u3000\u76f4\u7b49\u7247\u523b\u4e4b\u540e\uff0c\u624d\u89c1\u706b\u83bd\u54a7\u5f00\u8840\u76c6\u822c\u7684\u86e4\u87c6\u5634\u7b11\u9053\uff1a\u201c\u672c\u603b\u5cd2\u4e3b\u6000\u91cc\u7684\u5b9d\u8d1d\u5fc5\u5b9a\u4e5f\u53e3\u6e34 \u5566\uff0c\u8ba9\u5b83\u5148\u559d\u53e3\u8336\u89e3\u89e3\u6e34\u518d\u8bf4\u3002\u201d \u3000\u3000\u4ed6\u8fb9\u8bf4\u8fb9\u63a2\u624b\u5f80\u6000\u91cc\u6478\u53bb\u3002 \u3000\u3000\u4f46\u89c1\u4ed6\u62bd\u51fa\u624b\u6765\uff0c\u4e94\u6307\u4e00\u677e\uff0c\u7adf\u6709\u4e00\u53ea\u6709\u5982\u82cd\u8747\u7684\u5c0f\u866b\uff0e\u5e26\u7740\u55e1\u55e1\u4e4b\u58f0\uff0c\u5c55\u7fc5\u98de\u5230\u76cf\u53e3 \u4e0a\uff0c\u8111\u888b\u5f80\u8336\u6c34\u91cc\u4e00\u94bb\uff0c\u63a5\u7740\u518d\u98de\u5230\u7b2c\u4e8c\u53ea\u8336\u76cf\u53e3\u4e0a\uff0c\u5c55\u5f00\u540c\u6837\u52a8\u4f5c\uff0c\u4e0d\u5927\u4e00\u4f1a\uff0c\u4fbf\u628a\u56db\u76cf \u8336\u6c34\u5168\u884c\u8bd5\u8fc7\u3002 \u3000\u3000\u7136\u540e\u518d\u98de\u56de\u706b\u83bd\u638c\u4e2d\u3002 \u3000\u3000\u706b\u83bd\u4e00\u8fb9\u628a\u90a3\u957f\u6709\u7fc5\u8180\u7684\u5c0f\u866b\u9001\u56de\u6000\u4e2d\uff0c\u4e00\u9762\u5527\u5527\u5471\u5471\u7684\u7b11\u9053\uff1a\u201c\u90fd\u8bd5\u8fc7\u5566\uff0c\u6ca1\u4e8b\uff0c\u4e09 \u4f4d\u8bf7\u653e\u5fc3\u7528\u8336\uff01\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u5927\u5385\u5185\u65e9\u5df2\u9e26\u96c0\u65e0\u58f0\uff0c\u6240\u6709\u7684\u4eba\u51e0\u4e4e\u5168\u4e3a\u706b\u83bd\u65b9\u624d\u73a9\u7684\u90a3\u5957\u628a\u620f\uff0c\u5f04\u5f97\u76ee\u77aa\u53e3 \u5446\u3002 \u3000\u3000\u4e00\u4e2a\u82cd\u8747\u822c\u5927\u5c0f\u7684\u5c0f\u866b\uff0c\u5c45\u7136\u5982\u6b64\u901a\u7075\uff0c\u5e76\u4e14\u4e3a\u706b\u83bd\u5982\u6b64\u968f\u5fc3\u6240\u6b32\u7684\u9a71\u4f7f\uff0c\u5b9e\u5728\u662f\u4ef6\u524d \u6240\u672a\u89c1\u7684\u4e8b\uff0e \u3000\u3000\u5f53\u7136\uff0c\u7fa4\u96c4\u8c01\u90fd\u77e5\u9053\u90a3\u5c0f\u866b\u5fc5\u662f\u4e00\u79cd\u866b\u7269\u3002 \u3000\u3000\u4f46\u5374\u6ca1\u60f3\u5230\u866b\u7269\u7684\u7075\u6027\u7adf\u662f\u5982\u6b64\u4e4b\u5927\uff0c\u5982\u679c\u8fd9\u79cd\u866b\u957f\u5f97\u50cf\u4eba\u4e00\u6837\u5927\uff0c\u5c82\u4e0d\u6210\u4e3a\u6c5f\u6e56\u4e0a\u7528 \u6bd2\u9ad8\u624b\uff1f \u3000\u3000\u53ea\u89c1\u6bd2\u706b\u6210\u5168\u559d\u4e86\u53e3\u8336\uff0c\u5f97\u610f\u626c\u626c\u7684\u7b11\u9053\uff1a\u201c\u706b\u603b\u5cd2\u4e3b\uff0c\u8fd9\u79cd\u73a9\u827a\u513f\uff0c\u5728\u54b1\u4eec\u4e2d\u539f\u4e00 \u5e26\uff0c\u5f88\u5c11\u89c1\u5230\uff0c\u76f8\u4fe1\u5728\u573a\u7684\u5404\u4f4d\u7eff\u6797\u597d\u6c49\uff0c\u770b\u5230\u7684\u4e5f\u5fc5\u5b9a\u4e0d\u591a\uff0c\u4f60\u4f55\u4e0d\u518d\u73a9\u4e00\u6b21\u66f4\u7cbe\u5f69\u7684\uff0c \u8ba9\u5927\u5bb6\u90fd\u5f00\u5f00\u773c\u754c\uff01\u201d \u3000\u3000\u706b\u83bd\u86e4\u87c6\u5634\u4e00\u54a7\uff0c\u53c8\u5527\u5527\u5471\u5471\u7b11\u9053\uff1a\u201c\u597d\u5427\uff0c\u672c\u603b\u5cd2\u4e3b\u5c31\u51b3\u5b9a\u732e\u4e11\u5566\uff0c\u4e0d\u8fc7\u2026\u201d \u3000\u3000\u6bd2\u706b\u6210\u5168\u9053\uff1a\u201c\u4e0d\u8fc7\u4ec0\u4e48\uff1f\u83ab\u975e\u8fd8\u6709\u4ec0\u4e48\u7981\u5fcc\uff1f\u201d \u3000\u3000\u706b\u83bd\u4e24\u8033\u6247\u4e86\u51e0\u6247\u9053\uff1a\u201c\u5f85\u4f1a\u513f\u672c\u603b\u5cd2\u4e3b\u7684\u795e\u866b\u51fa\u6765\u4ee5\u540e\uff0c\u5927\u5bb6\u5343\u4e07\u4e0d\u80fd\u8ba9\u5b83\u78b0\u5230\u3002\u201d \u3000\u3000\u201c\u78b0\u5230\u4e86\u4f1a\u6709\u4ec0\u4e48\u540e\u679c\uff1f\u201d \u3000\u3000\u201c\u78b0\u5230\u4e4b\u5904\uff0e\u7acb\u5373\u7ea2\u80bf\u6e83\u70c2\uff0c\u4e24\u4e2a\u65f6\u8fb0\u4e4b\u5185\uff0c\u4fbf\u4f1a\u70c2\u5230\u5185\u8151\uff0c\u6bd2\u53d1\u8eab\u6b7b\u3002\u201d \u3000\u3000\u201c\u6709\u8fd9\u4e48\u4e25\u91cd\uff1f\u201d \u3000\u3000\u201c\u8c01\u4e0d\u76f8\u4fe1\uff0c\u8c01\u5c31\u4e0d\u59a8\u8bd5\u8bd5\u2026\u2026\u201d \u3000\u3000\u201c\u4e07\u4e00\u5f85\u4f1a\u513f\u6709\u4eba\u8eb2\u4e0d\u8fc7\u5462\uff1f\u201d \u3000\u3000\u201c\u4e0d\u59a8\u4e8b\uff0c\u672c\u603b\u5cd2\u4e3b\u53ef\u4ee5\u4e8b\u5148\u544a\u8beb\u6240\u6709\u795e\u866b\uff0e\u4eba\u4e0d\u4f24\u6211\uff0e\u6211\u4e0d\u4f24\u4eba\uff0c\u53ea\u8981\u5728\u573a\u5404\u4f4d\u522b\u62db \u60f9\u5b83\uff0e\u5b83\u5c31\u7edd\u4e0d\u4e3b\u52a8\u4f24\u4eba\uff0c\u8fd9\u6837\u5927\u5bb6\u603b\u8be5\u653e\u5fc3\u4e86\u5427\uff01\u201d \u3000\u3000\u706b\u83bd\u548c\u6210\u5168\u7684\u8fd9\u4e00\u756a\u95ee\u7b54\uff0c\u5206\u660e\u53c8\u662f\u5411\u5728\u573a\u7fa4\u96c4\u793a\u5a01\u3002 \u3000\u3000\u6b64\u523b\u5927\u5385\u5185\u7684\u7fa4\u96c4\uff0c\u66f4\u662f\u83ab\u6d4b\u9ad8\u6df1\uff0c\u6839\u672c\u5f04\u4e0d\u6e05\u706b\u83bd\u53c8\u8981\u5f04\u4ec0\u4e48\u7384\u865a\u3002 \u3000\u3000\u706b\u83bd\u5c06\u53f3\u624b\u7f13\u7f13\u63a2\u5165\u6000\u91cc\uff0c\u5f88\u5feb\u4fbf\u6478\u51fa\u4e00\u4e2a\u50cf\u70df\u888b\u8377\u5305\u5927\u5c0f\u7684\u9ed1\u8272\u5e03\u888b\uff0c\u4e00\u54a7\u5634\u7b11\u9053\uff1a \u201c\u8fd9\u888b\u91cc\u2014\u5171\u6709\u4e09\u5341\u516d\u53ea\u91d1\u8695\u866b\uff0c\u653e\u51fa\u4e4b\u540e\uff0c\u8ba9\u5b83\u4eec\u5728\u5927\u5385\u4e0a\u81ea\u7531\u98de\u65cb\u3002\u8fd8\u662f\u90a3\u53e5\u8001\u8bdd\uff0c\u5927 \u5bb6\u53ea\u80fd\u770b\uff0c\u5343\u4e07\u522b\u53bb\u6251\u6253\uff0c\u5426\u5219\uff0c\u82e5\u95f9\u51fa\u610f\u5916\uff0c\u672c\u603b\u5cd2\u4e3b\u6982\u4e0d\u8d1f\u8d23\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u7740\uff0c\u5148\u662f\u53e3\u4e2d\u5ff5\u5ff5\u6709\u8bcd\uff0c\u8c01\u4e5f\u6ca1\u542c\u6e05\u4ed6\u5600\u5495\u4e86\u4e9b\u4ec0\u4e48\uff0c\u63a5\u7740\u628a\u888b\u53e3\u6253\u5f00\u4e00\u6296\uff0c\u987f \u65f6\uff0c\u6570\u5341\u53ea\u91d1\u8695\u866b\uff0c\u50cf\u8fde\u9a7d\u7bad\u53d1\u5c04\u4e86\u51fa\u6765\u3002 \u3000\u3000\u8fd9\u4e9b\u91d1\u8695\u866b\uff0c\u5168\u90fd\u901a\u4f53\u706b\u7ea2\uff0c\u8868\u76ae\u53d1\u51fa\u95ea\u95ea\u91d1\u5149\u3002 \u3000\u3000\u5176\u5b9e\u8fd9\u4e9b\u6bd2\u7269\u867d\u8bf4\u662f\u91d1\u8695\uff0c\u4f46\u5934\u90e8\u5374\u6781\u4f3c\u6bd2\u86c7\uff0c\u4e00\u6761\u6761\u6602\u9996\u5410\u4fe1\uff0c\u5c24\u5176\u90a3\u7ea2\u820c\u4f38\u51fa\u53bb\u8db3 \u6709\u597d\u51e0\u5bf8\u957f\uff0c\u6bd4\u8eab\u5b50\u8fd8\u8981\u957f\u51fa\u6570\u500d\u3002 \u3000\u3000\u4e09\u5341\u516d\u6761\u91d1\u8695\u866b\u4e0d\u505c\u7684\u5410\u7740\u7ea2\u4fe1\uff0c\u4ea4\u7ec7\u6210\u4e00\u7247\u707f\u70c2\u800c\u53c8\u6050\u6016\u7684\u753b\u9762\uff0c\u770b\u7684\u4eba\u4eba\u5fc3\u91cc\u53d1 \u6bdb\u3002 \u3000\u3000\u53ea\u542c\u706b\u83bd\u559d\u9053\uff1a\u201c\u6563\u5f00\uff0c\u5feb\u98de\uff01\u201d \u3000\u3000\u90a3\u4e09\u5341\u516d\u6761\u91d1\u8695\uff0c\u679c\u7136\u5c31\u50cf\u901a\u6653\u4eba\u8bed\u4e00\u822c\u3002 \u3000\u3000\u5728\u4e3b\u4eba\u7684\u4e00\u58f0\u559d\u4ee4\u4e0b\uff0c\u7acb\u65f6\u9f50\u6563\u5f00\u6765\uff0c\u5411\u5927\u5385\u4e0a\u7a7a\u56db\u5468\u98de\u53bb\u3002\u4e00\u65f6\u4e4b\u95f4\uff0c\u5927\u5385\u5185\u5168\u662f\u4e00 \u7247\u55e1\u55e1\u632f\u7fc5\u4e4b\u58f0\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u7fa4\u96c4\u4eec\u51e0\u4e4e\u5168\u90fd\u9a87\u7136\u5931\u8272\u3002 \u3000\u3000\u4e00\u4e2a\u4e2a\u60c5\u4e0d\u81ea\u7981\u4ef0\u8138\u4e0a\u671b\uff0c\u4f46\u8eab\u5b50\u548c\u8111\u888b\u5374\u53c8\u5411\u4e0b\u76f4\u7f29\uff0c\u751f\u6015\u91d1\u8695\u98de\u843d\u5230\u81ea\u5df1\u5934\u4e0a\u3002 \u3000\u3000\u5ffd\u89c1\u706b\u83bd\u4f38\u51fa\u4e00\u6839\u6307\u5934\u671d\u5411\u62d8\u9b44\u7d22\u5b8b\u5929\u94ce\u5934\u4e0a\u4e00\u6307\uff0c\u4e00\u6761\u91d1\u8695\u970e\u65f6\u4fbf\u843d\u5728\u5b8b\u5929\u94ce\u4e0a\u3002 \u3000\u3000\u5b8b\u5929\u94ce\u4e00\u58f0\u60ca\u547c\u9053\uff1a\u201c\u706b\u603b\u5cd2\u4e3b\uff0c\u4f60\u8fd9\u662f\u4f55\u610f\uff1f\u201d \u3000\u3000\u706b\u83bd\u7b11\u9053\uff1a\u201c\u5b8b\u5f53\u5bb6\u7684\u653e\u5fc3\uff0c\u5b83\u53ea\u662f\u5728\u4f60\u5934\u4e0a\u4f11\u606f\u4f11\u606f\uff0c\u53ea\u8981\u4f60\u4e0d\u52a8\u5b83\uff0c\u7edd\u5bf9\u6ca1\u4e8b\u3002\u201d \u3000\u3000\u5b8b\u5929\u94ce\u9f87\u7259\u54a7\u5634\u9053\uff1a\u201c\u5b83\u4e3a\u4ec0\u4e48\u504f\u8981\u5728\u5b8b\u67d0\u5934\u535c\u4f11\u606f\uff1f\u201d \u3000\u3000\u706b\u83bd\u9053\uff1a\u201c\u5b83\u6b63\u98de\u5230\u5b8b\u5f53\u5bb6\u7684\u4e0a\u7a7a\uff0c\u81ea\u7136\u505c\u5728\u4f60\u5934\u4e0a\u6700\u65b9\u4fbf\u3002\u201d \u3000\u3000\u201c\u771f\u7684\u6ca1\u5173\u7cfb\uff1f\u201d \u3000\u3000\u201c\u672c\u603b\u5cd2\u4e3b\u8bf4\u6ca1\u5173\u7cfb\u5c31\u6ca1\u5173\u7cfb\u3002\u201d \u3000\u3000\u201c\u5b83\u597d\u50cf\u8fd8\u5728\u52a8\uff1f\u201d \u3000\u3000\u201c\u90a3\u662f\u5b83\u5728\u6d3b\u52a8\u8eaf\u722a\u7ec3\u7ec3\u62f3\u811a\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\uff0e\u5b83\u8fd8\u4f1a\u5730\u5802\u529f\uff1f\u201d \u3000\u3000\u201c\u5c82\u6b62\u5730\u5802\u529f\uff0c\u8fde\u592a\u6781\u62f3\u90fd\u4f1a\u6253\u3002\u5b8b\u5f53\u5bb6\u7684\u5934\u9876\u6bd4\u8f83\u6241\u5e73\uff0c\u6b63\u597d\u53ef\u505a\u5b83\u7684\u7ec3\u6b66\u573a\u3002\u201d \u3000\u3000\u201c\u706b\u603b\u5cd2\u4e3b\u53ef\u5343\u4e07\u522b\u5f00\u73a9\u7b11\uff01\u201d \u3000\u3000\u201c\u4f60\u65b9\u624d\u66fe\u966a\u672c\u603b\u5cd2\u4e3b\u8fdb\u8c37\uff0e\u54b1\u4eec\u8be5\u662f\u597d\u670b\u53cb\uff0c\u672c\u603b\u5cd2\u4e3b\u600e\u4f1a\u62ff\u4f60\u5f00\u73a9\u7b11\u3002\u201d \u3000\u3000\u4e00\u65c1\u770b\u5f97\u7709\u5f00\u773c\u7b11\u7684\u6bd2\u706b\u6210\u5168\u9053\uff1a\u201c\u4ed6\u4eec\u597d\u50cf\u90fd\u6015\u5f97\u5f88\uff0c\u706b\u603b\u5cd2\u4e3b\u5c31\u8bf7\u6536\u4e0b\u91d1\u8695\u5927\u9635 \u5427\uff01\u201d \u3000\u3000\u706b\u83bd\u70b9\u4e86\u70b9\u5934\u9053\uff1a\u201c\u8bf4\u7684\u4e5f\u662f\u3002\u201d \u3000\u3000\u63a5\u7740\u5148\u5f80\u7a7a\u6253\u4e86\u4e2a\u624b\u52bf\u3002 \u3000\u3000\u7136\u540e\u559d\u9053\uff1a\u201c\u7edf\u7edf\u6536\u5175\u56de\u7b3c\uff01\u201d\u4e00\u8fb9\u53cc\u624b\u5f20\u5f00\u888b\u53e3\u3002 \u3000\u3000\u4e09\u5341\u516d\u6761\u91d1\u8695\u7acb\u5373\u632f\u7fc5\u56de\u98de\uff0c\u4e0d\u5927\u2014\u4f1a\u4fbf\u50cf\u5bd2\u9e26\u5f52\u5de2\u822c\u5168\u90e8\u6d8c\u5411\u90a3\u9ed1\u8272\u5e03\u888b\u3002 \u3000\u3000\u706b\u83bd\u5c06\u5e03\u888b\u91cd\u65b0\u63e3\u5165\u6000\u4e2d\uff0c\u4e00\u9762\u54a7\u5634\u7b11\u9053\uff1a\u201c\u65b9\u624d\u73a9\u7684\u53ea\u662f\u5c0f\u628a\u620f\uff0c\u7b97\u4e0d\u4e86\u4ec0\u4e48\u3002\u73b0 \u5728\uff0c\u672c\u603b\u5cd2\u4e3b\u60f3\u518d\u5e03\u4e00\u4e2a\u795e\u866b\u5927\u9635\u7ed9\u5927\u5bb6\u77a7\u77a7\uff01\u201d \u3000\u3000\u8bf4\u7740\uff0c\u62ac\u624b\u5f80\u5927\u5385\u95e8\u5916\u62db\u4e86\u2014\u62db\u3002 \u3000\u3000\u90a3\u4e24\u540d\u65b9\u624d\u968f\u4ed6\u540c\u6765\u7684\u5cd2\u4eba\uff0c\u6b63\u7ad9\u5728\u5927\u5385\u95e8\u5916\uff0c\u95fb\u8a00\u968f\u5373\u63d0\u8d77\u653e\u5728\u5730\u4e0a\u7684\u9ed1\u7bb1\uff0c\u51c6\u5907\u5954 \u8fdb\u5927\u5385\u3002 \u3000\u3000\u5374\u542c\u6bd2\u706b\u6210\u5168\u9053\uff1a\u201c\u706b\u603b\u5cd2\u4e3b\uff0c\u65e2\u7136\u4ed6\u4eec\u5404\u4f4d\u7eff\u6797\u597d\u6c49\u5df2\u77e5\u795e\u866b\u7684\u5389\u5bb3\uff0c\u5c31\u4e0d\u5fc5\u518d\u8868\u6f14 \u4e86\uff0c\u54b1\u4eec\u8fd8\u662f\u548c\u970d\u76df\u4e3b\u8c08\u6b63\u4e8b\u8981\u7d27\u3002\u201d \u3000\u3000\u706b\u83bd\u5bf9\u6bd2\u706b\u6210\u5168\u5341\u5206\u987a\u4ece\uff0c\u9a6c\u4e0a\u5411\u90a3\u4e24\u540d\u5cd2\u4eba\u6446\u4e86\u6446\u624b\u9053\uff1a\u201c\u6682\u65f6\u4e0d\u5fc5\u8fdb\u6765\uff0c\u4f60\u4eec\u5c31\u5728 \u95e8\u5916\u5f85\u547d\uff01\u201d \u3000\u3000\u6bd2\u706b\u6210\u5168\u9053\uff1a\u201c\u706b\u603b\u5cd2\u4e3b\u65b9\u624d\u7684\u91d1\u8695\u98de\u5929\u9635\u786e\u5b9e\u7cbe\u5f69\uff0c\u6210\u67d0\u4f69\u670d\uff01\u201d \u3000\u3000\u706b\u83bd\u5482\u4e86\u5482\u86e4\u87c6\u5634\u9053\uff1a\u201c\u4e45\u95fb\u6210\u8001\u82f1\u96c4\u7684\u6697\u5668\u795e\u6280\uff0c\u4eba\u79f0\u4e2d\u539f\u6b66\u6797\u7b2c\u4e00\u9ad8\u624b\uff0c\u53ef\u5426\u4e5f\u8ba9 \u672c\u603b\u5cd2\u4e3b\u5f00\u5f00\u773c\u754c\uff1f\u201d \u3000\u3000\u6bd2\u706b\u6210\u5168\u9634\u68ee\u68ee\u4e00\u7b11\u9053\uff1a\u201c\u4e0d\u6562\u5f53\uff0c\u96d5\u866b\u5c0f\u6280\uff0c\u6bd4\u8d77\u706b\u603b\u5cd2\u4e3b\u7684\u653e\u866b\u795e\u672f\uff0c\u90a3\u662f\u5c0f\u5deb\u89c1 \u5927\u5deb\u4e86\uff01\u201d \u3000\u3000\u201c\u6210\u8001\u82f1\u96c4\u4f55\u5fc5\u5ba2\u6c14\uff01\u201d \u3000\u3000\u201c\u53ef\u60dc\u5927\u5385\u4e4b\u5185\uff0c\u4e0d\u4fbf\u65bd\u5c55\uff0e\u201d \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0e\u5927\u5385\u5de6\u65b9\u7684\u5899\u58c1\u4e0a\uff0c\u5ffd\u7136\u722c\u51fa\u4e09\u53ea\u58c1\u864e\uff0e \u3000\u3000\u4e09\u53ea\u58c1\u864e\u4e00\u524d\u4e24\u540e\uff0e\u6b63\u597d\u5f62\u6210\u4e00\u4e2a\u4e09\u89d2\u5f62\u3002 \u3000\u3000\u4f46\u89c1\u6bd2\u706b\u6210\u5168\u63a2\u624b\u5165\u6000\uff0c\u63a5\u7740\u6296\u624b\u5f80\u5de6\u65b9\u7529\u53bb\u3002 \u5367\u9f99\u751f\u300a\u5999\u7edd\u5929\u9999\u300b \u7b2c \u5341 \u56de\u3000\u7fa4\u8c6a\u9677\u56f0 \u3000\u3000\u7fa4\u96c4\u6839\u672c\u6ca1\u770b\u6e05\u6210\u5168\u7529\u51fa\u53bb\u7684\u662f\u4ec0\u4e48\uff0c\u90a3\u4e09\u53ea\u58c1\u864e\u5df2\u9f50\u9f50\u5e94\u624b\u6389\u843d\u5730\u4e0a\uff0c\u800c\u4e14\u7acb\u5373\u4f38\u817f \u77aa\u773c\uff0c\u50f5\u76f4\u4e0d\u52a8\u3002 \u3000\u3000\u4e00\u4e9b\u9760\u8fd1\u5de6\u58c1\u7684\u7eff\u6797\u597d\u6c49\uff0e\u8fd9\u624d\u770b\u6e05\u90a3\u4e09\u53ea\u58c1\u864e\uff0c\u5934\u4e0a\u5404\u81ea\u5d4c\u8fdb\u4e00\u6839\u84dd\u6c6a\u6c6a\u7ec6\u5982\u67d4\u4e1d\u7684 \u874e\u5c3e\u9488\u3002 \u3000\u3000\u8fd9\u5de6\u8fb9\u5899\u58c1\u8ddd\u6210\u5168\u8db3\u6709\u4e24\u4e09\u4e08\u8fdc\uff0c\u7fa4\u96c4\u9664\u4e86\u9707\u60ca\u4e8e\u6210\u5168\u7684\u65bd\u653e\u6697\u5668\u795e\u6280\uff0c\u66f4\u9a87\u4eba\u7684\u8be5\u662f \u90a3\u6dec\u6bd2\u874e\u5c3e\u9488\u7684\u6bd2\u6027\u4e4b\u70c8\u3002 \u3000\u3000\u706b\u83bd\u559d\u4e86\u2014\u58f0\u5f69\u9053\uff1a\u201c\u679c\u7136\u4e86\u4e0d\u8d77\uff0c\u672c\u603b\u5cd2\u4e3b\u96be\u5f97\u4e5f\u5f00\u4e86\u4e00\u6b21\u773c\u754c\uff01\u201d \u3000\u3000\u6bd2\u706b\u6210\u5168\u563f\u563f\u7b11\u9053\uff1a\u201c\u7b97\u4e0d\u5f97\u4ec0\u4e48\uff0c\u54b1\u4eec\u8fd8\u662f\u8be5\u548c\u970d\u76df\u4e3b\u8c08\u6b63\u4e8b\u8981\u7d27\u3002\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u5927\u5385\u5185\u65e9\u5df2\u81ea\u52a8\u9759\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u970d\u5143\u4f3d\u89c1\u6bd2\u706b\u6210\u5168\u2014\u518d\u63d0\u8d77\u8981\u8c08\u6b63\u4e8b\uff0c\u5374\u53c8\u5f04\u4e0d\u6e05\u5bf9\u65b9\u6240\u8bf4\u7684\u6b63\u4e8b\u7a76\u7adf\u662f\u4ec0\u4e48\uff0c\u8f7b\u54b3\u4e86 \u2014\u58f0\u9053\uff1a\u201c\u6210\u8001\u82f1\u96c4\u6709\u8bdd\u8bf7\u8bb2\uff0c\u970d\u67d0\u6d17\u8033\u606d\u542c\uff01\u201d \u3000\u3000\u5374\u542c\u9ed1\u9b54\u65f6\u5bc5\u51b7\u68ee\u68ee\u7b11\u9053\uff1a\u201c\u970d\u76df\u4e3b\u53ea\u8981\u542c\u6e05\u695a\u4e86\u5c31\u6210\uff0c\u8033\u6735\u6d17\u4e0d\u6d17\u5012\u662f\u6ca1\u6709\u5173\u7cfb\uff01\u201d \u3000\u3000\u6bd2\u706b\u6210\u5168\u4e5f\u563f\u563f\u7b11\u9053\uff1a\u201c\u65f6\u8001\u5f1f\u8bf4\u5f97\u5bf9\uff0c\u8001\u592b\u73b0\u5728\u5c31\u5f00\u59cb\u8c08\u6b63\u4e8b\u5566\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u62f1\u4e86\u62f1\u624b\u9053\uff1a\u201c\u8bf7\u8bb2\uff01\u201d \u3000\u3000\u6bd2\u706b\u6210\u5168\u6162\u541e\u4e54\u7684\u9053\uff1a\u201c\u8fd9\u8ff7\u8e2a\u8c37\u7684\u5929\u4e0b\u7eff\u6797\u603b\u5be8\uff0c\u81ea\u80e1\u67cf\u9f84\u521b\u7acb\u8fc4\u4eca\uff0c\u5c11\u8bf4\u4e5f\u6709\u4e09\u5e74 \u591a\uff0c\u60f3\u6765\u91d1\u94f6\u7cae\u9977\uff0c\u5fc5\u5b9a\u805a\u96c6\u4e0d\u5c11\u5427\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u95fb\u8a00\u4e0d\u89c9\u4e00\u6014\uff0c\u6697\u9053\uff1a\u201c\u8fd9\u8001\u9b54\u5934\u83ab\u975e\u8981\u5411\u8ff7\u8e2a\u8c37\u52d2\u7d22\u6572\u8bc8\uff1f\u2026\u2026\u201d \u3000\u3000\u5fc3\u91cc\u60f3\u7740\uff0c\u9762\u4e0a\u5374\u662f\u6ee1\u542b\u7b11\u5bb9\u9053\uff1a\u201c\u6210\u8001\u82f1\u96c4\u4e3a\u4ec0\u4e48\u5ffd\u7136\u95ee\u8d77\u8fd9\u4e8b\u6765\uff1f\u201d \u3000\u3000\u6bd2\u706b\u6210\u5168\u6de1\u6de1\u4e00\u7b11\u9053\uff1a\u201c\u970d\u76df\u4e3b\u8bf7\u5148\u56de\u7b54\u8001\u592b\u7684\u95ee\u8bdd\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u82e5\u8bf4\u91d1\u94f6\u7cae\u9977\uff0c\u53ea\u80fd\u8bf4\u76ee\u524d\u8fd8\u7b97\u591f\u7528\uff0c\u8ff7\u8e2a\u8c37\u517b\u7740\u4e0a\u5343\u4eba\uff0c\u5f00\u652f\u6d69\u5927\uff0c\u4e3a \u4f7f\u8fd9\u7eff\u6797\u603b\u5be8\u80fd\u7ee7\u7eed\u751f\u5b58\u53d1\u5c55\u4e0b\u53bb\uff0c\u970d\u67d0\u514d\u4e0d\u4e86\u8981\u65f6\u65f6\u53d1\u6101\u3002\u201d \u3000\u3000\u6bd2\u706b\u6210\u5168\u51b7\u51b7\u7b11\u9053\uff1a\u201c\u970d\u76df\u4e3b\u8bf4\u8fd9\u79cd\u8bdd\uff0c\u672a\u514d\u5c31\u5ba2\u6c14\u5f97\u592a\u8fc7\u4efd\u4e86\uff0c\u4e5f\u592a\u89c1\u5916\u4e86\u3002\u8ff7\u8e2a\u8c37 \u662f\u5929\u4e0b\u7eff\u6797\u603b\u5be8\uff0c\u7528\u4e0d\u7740\u70e7\u6740\u63b3\u63a0\uff0c\u4ec5\u662f\u5404\u5904\u5206\u5be8\u652f\u8235\u6bcf\u6b21\u89e3\u8fdb\u7684\u91d1\u94f6\u8d22\u5b9d\uff0c\u4e09\u5e74\u591a\u6765\u8bf4\u4e0d \u5b9a\u65e9\u5c31\u5806\u79ef\u5982\u5c71\uff0c\u8001\u592b\u7684\u8bdd\u4e0d\u4f1a\u5047\u5427\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u52c3\u7136\u53d8\u8272\u9053\uff1a\u201c\u6210\u8001\u82f1\u96c4\u660e\u767d\uff0c\u8ff7\u8e2a\u8c37\u867d\u7136\u5df2\u7ecf\u521b\u7acb\u4e09\u5e74\u591a\uff0c\u4f46\u5374\u81f3\u4eca\u5e76\u672b\u5efa\u7acb \u5206\u5be8\u652f\u8235\uff0c\u4e5f\u4ece\u672a\u5411\u5929\u4e0b\u7eff\u6797\u540c\u9053\u52df\u96c6\u91d1\u94f6\u3001\u4f60\u8bf4\u8ff7\u8e2a\u8c37\u8d22\u5b9d\u5806\u79ef\u5982\u5c71\uff0c\u5c82\u4e0d\u662f\u5927\u5927\u7b11 \u8bdd\uff01\u201d \u3000\u3000\u6bd2\u706b\u6210\u5168\u4f3c\u4e4e\u5df2\u4e0d\u8010\u70e6\uff0c\u4e3e\u8d77\u624b\u81c2\u8fde\u6446\u4e86\u51e0\u4e0b\u9053\uff1a\u201c\u4e0d\u5fc5\u8bf4\u90a3\u4e9b\u4e86\uff0c\u8001\u592b\u4eca\u5929\u7684\u6765\u610f\uff0c \u4e0d\u5916\u662f\u966a\u540c\u725b\u4eb2\u738b\u6765\u5411\u8d35\u5be8\u52df\u96c6\u4e00\u70b9\u8d44\u91d1\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u90a3\u5c31\u4e0d\u59a8\u8bf4\u51fa\u4e2a\u6570\u76ee\u6765\uff0c\u82e5\u970d\u67d0\u529e\u5f97\u5230\uff0c\u4e00\u5b9a\u5c3d\u529b\u5e2e\u5fd9\u3002\u4e0d\u8fc7\uff0c\u970d\u67d0\u5fc5\u987b \u77e5\u9053\u8fd9\u7b14\u94b1\u662f\u505a\u4ec0\u4e48\u7528\u7684\u3002\u201d \u3000\u3000\u6bd2\u706b\u6210\u5168\u671b\u4e86\u725b\u5954\u4e00\u773c\u9053\uff1a\u201c\u8001\u592b\u4eca\u5929\u662f\u966a\u540c\u725b\u4eb2\u738b\u6765\u52df\u6350\u7684\uff0c\u8fd8\u662f\u7531\u4ed6\u6765\u8bf4\u660e\u7684\u597d\u3002 \u725b\u4eb2\u738b\uff0c\u4f60\u8bf7\u544a\u8bc9\u970d\u76df\u4e3b\uff0c\u52df\u96c6\u8fd9\u7b14\u94b1\u7684\u7528\u9014\uff1f\u201d \u3000\u3000\u725b\u5954\u81ea\u8fdb\u5165\u5927\u5385\uff0c\u2014\u76f4\u4e0d\u66fe\u5f00\u53e3\uff0c\u5230\u8fd9\u65f6\u624d\u54a7\u4e86\u54a7\u5634\u5927\u58f0\u9053\uff1a\u201c\u560e\u5e3d\uff01\u201d \u3000\u3000\u8fd9\u2014\u58f0\u201c\u560e\u5e3d\u201d\uff0c\u4e0d\u4f46\u970d\u5143\u4f3d\u542c\u5f97\u83ab\u540d\u5176\u5999\uff0c\u8fde\u5176\u4ed6\u8ff7\u8e2a\u8c37\u6240\u6709\u7684\u4eba\uff0c\u4e5f\u90fd\u5f20\u53e3\u7ed3\u820c\uff0c \u6123\u5728\u5f53\u573a\u3002 \u3000\u3000\u6bd2\u706b\u6210\u5168\u7b11\u9053\uff1a\u201c\u8001\u592b\u77e5\u9053\u8bf8\u4f4d\u542c\u4e0d\u61c2\u725b\u4eb2\u738b\u7684\u8bdd\uff0c\u8fd8\u662f\u7531\u8001\u592b\u6765\u505a\u505a\u7ffb\u8bd1\u5427\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u6210\u8001\u82f1\u96c4\u600e\u80fd\u542c\u61c2\u4ed6\u7684\u8bdd\uff1f\u201d \u3000\u3000\u6bd2\u706b\u6210\u5168\u9053\uff1a\u201c\u8001\u592b\u5f53\u5e74\u5728\u91d1\u8f6e\u738b\u5e9c\u4f4f\u8fc7\u4e0d\u5c11\u4e00\u6bb5\u65f6\u95f4\uff0c\u5bf9\u4ed6\u4eec\u7684\u8bed\u8a00\uff0c\u5f53\u7136\u53ef\u4ee5\u542c \u61c2\uff0c\u4ed6\u8bf4\u7684\u2018\u560e\u5e3d\u2019\uff0c\u5c31\u662f\u76d6\u5e99\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u679c\u7136\u8bed\u97f3\u52c9\u5f3a\u6709\u70b9\u76f8\u8fd1\uff0c\u91d1\u8f6e\u6cd5\u738b\u4e3a\u4ec0\u4e48\u8fd8\u8981\u76d6\u5e99\uff1f\u201d \u3000\u3000\u6bd2\u706b\u6210\u5168\u9053\uff1a\u201c\u91d1\u8f6e\u6cd5\u738b\u662f\u5bc6\u5b97\u7ea2\u6559\u6559\u5b97\uff0c\u4fe1\u5f92\u662f\u65e0\u53ef\u8ba1\u6570\uff0c\u5f53\u7136\u8981\u6709\u5e99\u4f9b\u4fe1\u5f92\u671d \u62dc\u3002\u201d \u3000\u3000\u201c\u6210\u8001\u82f1\u96c4\u8bf7\u518d\u95ee\u95ee\u4ed6\uff0c\u51c6\u5907\u76d6\u4e00\u5ea7\u4ec0\u4e48\u6837\u7684\u5e99\uff1f\u201d \u3000\u3000\u201c\u4e0d\u5fc5\u95ee\uff0c\u4ed6\u65e9\u5df2\u5bf9\u8001\u592b\u63d0\u8fc7\uff0c\u636e\u6cd5\u738b\u7684\u610f\u601d\uff0e\u8981\u76d6\u4e00\u6240\u5360\u5730\u6570\u5341\u4ea9\u53ef\u4f9b\u597d\u51e0\u4e07\u4eba\u671d\u62dc \u7684\u5927\u5e99\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u4e0d\u52a8\u58f0\u8272\u9053\uff1a\u201c\u76d6\u8fd9\u6837\u2014\u5ea7\u5927\u5e99\uff0c\u4e0d\u77e5\u9700\u8981\u591a\u5c11\u8d44\u91d1\uff1f\u201d \u3000\u3000\u6bd2\u706b\u6210\u5168\u9053\uff1a\u201c\u9664\u4e86\u6cd5\u738b\u7684\u81ea\u5907\u6b3e\u5916\uff0c\u9700\u52df\u96c6\u7684\u5f53\u7136\u4e5f\u4e0d\u5728\u5c11\u6570\uff0c\u4e0d\u8fc7\u8001\u592b\u8fd8\u662f\u8981\u518d\u95ee \u4e00\u904d\u725b\u4eb2\u738b\u7684\u597d\u3002\u201d \u3000\u3000\u8bf4\u7740\uff0c\u89c6\u7ebf\u8f6c\u5411\u725b\u5954\u3002 \u3000\u3000\u725b\u5954\u628a\u4e24\u624b\u98df\u6307\u4ea4\u53c9\u4e86\u2014\u4e0b\uff0e\u53c8\u5927\u58f0\u9053\uff1a\u201c\u7838\u7897\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u76b1\u4e86\u76b1\u7709\u9053\uff1a\u201c\u970d\u67d0\u5df2\u4ea4\u4ee3\u8bbe\u7b75\u62db\u5f85\u5404\u4f4d\uff0c\u73b0\u5728\u9152\u83dc\u5c1a\u672a\u7aef\u6765\uff0c\u725b\u4eb2\u738b\u4e3a\u4ec0\u4e48\u5c31 \u8981\u7838\u7897\uff1f\u201d \u3000\u3000\u6bd2\u706b\u6210\u5168\u7b11\u9053\uff1a\u201c\u725b\u4eb2\u738b\u8bf4\u7684\u8fd9\u7838\u7897\u4e24\u5b57\uff0c\u7ffb\u8bd1\u8fc7\u6765\u5c31\u662f\u5341\u4e07\u3002\u201d \u3000\u3000\u201c\u5341\u4e07\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u662f\u5341\u4e07\u4e24\u9ec4\u91d1\u3002\u201d \u3000\u3000\u7fa4\u96c4\u4eec\u4e00\u542c\u5bf9\u65b9\u5c45\u7136\u8981\u52d2\u7d22\u5341\u4e07\u4e24\u9ec4\u91d1\uff0c\u90fd\u4e0d\u514d\u5927\u5403\u4e00\u60ca\uff0e\u9f50\u9f50\u628a\u76ee\u5149\u96c6\u4e2d\u5728\u970d\u5143\u4f3d\u8138 \u4e0a\uff0e\u770b\u4ed6\u5982\u4f55\u56de\u7b54\u3002 \u3000\u3000\u8c01\u90fd\u5fc3\u91cc\u6709\u6570\uff0c\u8ff7\u8e2a\u8c37\u867d\u7136\u4e0d\u7f3a\u7cae\u9977\uff0c\u4f46\u5374\u7edd\u5bf9\u7b79\u4e0d\u51fa\u5341\u4e07\u4e24\u9ec4\u91d1\uff0c\u8fd9\u6570\u76ee\u5b9e\u5728\u5927\u5f97\u5413 \u4eba\u3002 \u3000\u3000\u5c82\u6599\u8fd9\u65f6\u7684\u970d\u5143\u4f3d\uff0c\u53cd\u800c\u663e\u5f97\u5341\u5206\u4ece\u5bb9\uff0c\u563f\u563f\u7b11\u4e86\u51e0\u58f0\u9053\uff1a\u201c\u5341\u4e07\u4e24\u9ec4\u91d1\uff0c\u7b97\u4e0d\u5f97\u4ec0\u4e48 \u5927\u6570\u76ee\uff0c\u8fd9\u70b9\u94b1\u970d\u67d0\u8fd8\u6709\u529e\u6cd5\u7b79\u5f97\u51fa\u3002\u201d \u3000\u3000\u8fd9\u51e0\u53e5\u8bdd\uff0c\u53cd\u800c\u4f7f\u5f97\u6bd2\u706b\u6210\u5168\u5927\u51fa\u610f\u5916\uff0c\u4ed6\u672c\u6765\u9884\u6599\u970d\u5143\u4f3d\u7684\u53cd\u5e94\uff0c\u81f3\u5c11\u6709\u4e24\u79cd\u53ef\u80fd\uff0c \u7b2c\u4e00\u662f\u5f53\u573a\u7ffb\u8138\uff0c\u7b2c\u4e8c\u662f\u8bbe\u6cd5\u6740\u4ef7\u3002\u5728\u4ed6\u7684\u539f\u610f\uff0c\u5373\u4f7f\u970d\u5143\u4f3d\u53ea\u51fa\u4e00\u4e07\u4e24\u9ec4\u91d1\uff0c\u4e5f\u662f\u767d\u8d5a\uff0c \u5c82\u77e5\u5bf9\u65b9\u5c45\u7136\u4e00\u53e3\u7b54\u5e94\uff0c\u53cd\u800c\u4ee4\u4ed6\u6709\u4e9b\u83ab\u6d4b\u9ad8\u6df1\u3002 \u3000\u3000\u53ea\u542c\u970d\u5143\u4f3d\u53c8\u9053\uff1a\u201c\u56db\u4f4d\u4eca\u5929\u8fb1\u4e34\u8ff7\u8e2a\u8c37\uff0c\u4e3a\u970d\u67d0\u8fd9\u7eff\u6797\u603b\u5be8\u589e\u52a0\u4e86\u4e0d\u5c11\u5149\u5f69\uff0c\u970d\u67d0\u9664 \u4e86\u8bbe\u7b75\u63a5\u98ce\u5916\uff0c\u8fd8\u51c6\u5907\u4e86\u4e00\u70b9\u8282\u76ee\u4f9b\u8d35\u5bbe\u89c2\u8d4f\uff0c\u4ee5\u52a9\u96c5\u5174\uff01\u201d \u3000\u3000\u6bd2\u706b\u6210\u5168\u54a7\u4e86\u54a7\u5634\u9053\uff1a\u201c\u8001\u592b\u8fd8\u6ca1\u542c\u8bf4\u8fc7\uff0c\u4ec0\u4e48\u53eb\u8282\u76ee\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u8fd9\u540d\u8bcd\u970d\u67d0\u4e5f\u662f\u65b0\u542c\u6765\u7684\uff0c\u81f3\u4e8e\u4ec0\u4e48\u610f\u601d\uff0c\u970d\u67d0\u4e5f\u65e0\u6cd5\u89e3\u91ca\uff0c\u53ea\u77e5\u9053\u8fd9\u73a9 \u827a\u513f\u8868\u6f14\u8d77\u6765\u5f88\u597d\u770b\u3002\u201d \u3000\u3000\u6bd2\u706b\u6210\u5168\u679c\u7136\u88ab\u5f15\u8d77\u5174\u8da3\uff0c\u5fd9\u9053\uff1a\u201c\u90a3\u5c31\u8bf7\u970d\u76df\u4e3b\u5feb\u4e0a\u8282\u76ee\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u5ffd\u7136\u76ee\u6ce8\u7fa4\u96c4\uff0c\u63d0\u9ad8\u55d3\u95e8\u9053\uff1a\u201c\u8fd9\u8282\u76ee\u4e3b\u8981\u662f\u62db\u5f85\u56db\u4f4d\u8d35\u5bbe\u7684\uff0c\u54b1\u4eec\u81ea\u5df1\u4eba\u4ee5\u540e \u770b\u7684\u673a\u4f1a\u591a\u5f97\u5f88\uff0c\u73b0\u5728\u5c31\u8bf7\u5c3d\u91cf\u5f80\u540e\u7ad9\uff0c\u7a7a\u51fa\u573a\u5730\u6765\uff0c\u4ee5\u4fbf\u80fd\u8ba9\u56db\u4f4d\u8d35\u5bbe\u5728\u524d\u9762\u4ed4\u7ec6\u6b23 \u8d4f\uff01\u201d \u3000\u3000\u6bd2\u706b\u6210\u5168\u563f\u563f\u7b11\u9053\uff1a\u201c\u770b\u8282\u76ee\u4e3a\u4ec0\u4e48\u4e00\u5b9a\u8981\u5728\u524d\u9762\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u8fd9\u8282\u76ee\u4e00\u5b9a\u8981\u9760\u8fd1\u770b\u624d\u6709\u610f\u601d\uff0c\u5f85\u4f1a\u513f\u6210\u8001\u82f1\u96c4\u5c31\u660e\u767d\u5566\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u7740\uff0c\u56de\u8eab\u671b\u5411\u5927\u5385\u5de6\u4fa7\u5c4f\u98ce\u8fb9\u7684\u4fbf\u95e8\u53eb\u9053\uff1a\u201c\u8bb8\u4ed9\u5b50\u8bf7\u8fdb\u6765\uff01\u201d \u3000\u3000\u53ea\u542c\u5c4f\u98ce\u540e\u4e00\u9635\u8f7b\u5fae\u7684\u811a\u6b65\u58f0\u54cd\uff0c\u63a5\u7740\u8d70\u8fdb\u4e00\u4e2a\u8eab\u62ab\u4e00\u88ad\u9ed1\u8272\u6597\u7bf7\u6253\u626e\u5f97\u8273\u5149\u7167\u4eba\u7684\u5973 \u5b50\u6765\u3002 \u3000\u3000\u5728\u8fd9\u5239\u90a3\uff0c\u7fa4\u96c4\u4e2d\u7684\u9ea6\u5c0f\u660e\u548c\u5d02\u5c71\u4e09\u96c4\u5dee\u70b9\u513f\u5931\u58f0\u53eb\u4e86\u51fa\u6765\u3002 \u3000\u3000\u8eab\u62ab\u9ed1\u8272\u6597\u7bf7\u7684\u5973\u5b50\uff0c\u8d6b\u7136\u662f\u5728\u592a\u539f\u8868\u6f14\u8131\u88e4\u7684\u8bb8\u5c0f\u65e6\u3002 \u3000\u3000\u81f3\u4e8e\u5176\u4ed6\u7684\u4eba\uff0c\u5f04\u4e0d\u6e05\u662f\u600e\u4e48\u56de\u4e8b\uff0c\u603b\u7b97\u4ecd\u80fd\u4fdd\u6301\u9547\u5b9a\uff0c\u4e0d\u8fc7\u4ed6\u4eec\u5374\u4e0d\u80fd\u4e0d\u5947\u602a\u8ff7\u8e2a\u8c37 \u4ec0\u4e48\u65f6\u5019\u6765\u4e86\u8fd9\u4e48\u2014\u4e2a\u5973\u4eba\u3002 \u3000\u3000\u8bb8\u5c0f\u65e6\u867d\u7136\u4eba\u957f\u5f97\u4e0d\u7f8e\uff0c\u4f46\u5374\u6709\u526f\u4e0d\u9519\u7684\u8eab\u6750\uff0c\u800c\u4e14\u5979\u53c8\u64c5\u4e8e\u8868\u73b0\u81ea\u5df1\uff0c\u8d70\u8d77\u8def\u6765\uff0c\u6545 \u610f\u626d\u8170\u6446\u81c0\uff0c\u663e\u5f97\u8d8a\u53d1\u5a40\u5a1c\u591a\u59ff\u3002\u5c24\u5176\u8138\u4e0a\u90a3\u80a1\u5145\u6ee1\u6deb\u90aa\u7684\u5996\u6c14\u4e0e\u9a9a\u6c14\uff0c\u5bf9\u7537\u4eba\u6765\u8bf4\uff0c\u8fd8\u771f \u6709\u79cd\u52fe\u9b42\u6444\u9b44\u7684\u9b45\u529b\u3002 \u3000\u3000\u5979\u7f13\u7f13\u8d70\u5230\u6210\u5168\u3001\u65f6\u5bc5\uff0e\u725b\u5954\u3001\u706b\u83bd\u56db\u4eba\u8eab\u524d\uff0c\u79cb\u6ce2\u6d41\u8f6c\uff0c\u5404\u81ea\u63d6\u4e86\u2014\u798f\uff0e \u3000\u3000\u7531\u4e8e\u5979\u6709\u821e\u53f0\u7ecf\u9a8c\uff0c\u64c5\u957f\u8868\u6f14\uff0c\u8fde\u8fd9\u79cd\u88e3\u793c\u7684\u5c0f\u52a8\u4f5c\uff0c\u4f3c\u4e4e\u4e5f\u5bf9\u7537\u4eba\u6709\u80a1\u6311\u9017\u529b\u91cf\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u6210\u5168\u3001\u65f6\u5bc5\u3001\u725b\u5954\u3001\u706b\u83bd\u65e9\u5df2\u6709\u4e9b\u795e\u9b42\u8361\u6f3e\uff0c\u98d8\u98d8\u6b32\u4ed9\u7684\u611f\u89c9\u3002 \u3000\u3000\u539f\u6765\uff0c\u6bd2\u706b\u6210\u5168\u548c\u9ed1\u9b54\u65f6\u5bc5\uff0c\u672c\u5c31\u5c5e\u4e8e\u8272\u4e2d\u997f\u9b3c\u3001\u82b1\u91cc\u9b54\u738b\u4e00\u7c7b\u7684\u9ed1\u9053\u4eba\u7269\uff0c\u6240\u4ee5\u7f57\u6d6e \u4e00\u53df\u970d\u5143\u4f3d\u624d\u4f1a\u6295\u5176\u6240\u597d\u5bf9\u75c7\u4e0b\u836f\uff0e \u3000\u3000\u81f3\u4e8e\u725b\u5954\u548c\u706b\u83bd\uff0c\u4e00\u4e2a\u662f\u897f\u620e\uff0c\u4e00\u4e2a\u662f\u5357\u86ee\uff0c\u56e0\u4e3a\u4e0d\u53ca\u4e2d\u539f\u5f00\u5316\uff0c\u5f53\u7136\u517d\u6027\u66f4\u662f\u8d85\u8fc7\u4eba \u6027\uff0e \u3000\u3000\u8bb8\u5c0f\u65e6\u5728\u5411\u56db\u4eba\u65bd\u8fc7\u793c\u540e\uff0c\u4fbf\u7ad9\u5728\u90a3\u91cc\u4e0d\u52a8\uff0c\u4f46\u90a3\u2014\u53cc\u52fe\u9b42\u6444\u9b44\u7684\u5a9a\u773c\uff0c\u5374\u77ac\u606f\u4e0d\u505c\u5730 \u76f4\u5728\u6210\u5168\u7b49\u56db\u4eba\u8138\u4e0a\u7784\u6765\u7784\u53bb\u3002 \u3000\u3000\u6bd2\u706b\u6210\u5168\u8fd9\u65f6\u5df2\u60c5\u4e0d\u81ea\u7981\u6d8e\u6c34\u76f4\u6d41\uff0c\u6068\u4e0d\u5f97\u4e00\u53e3\u628a\u9762\u524d\u8fd9\u9a9a\u5973\u4eba\u541e\u4e0b\u809a\u53bb\uff0c\u4ed6\u8fde\u8fde\u5e72\u54b3 \u4e86\u51e0\u58f0\u9053\uff1a\u201c\u970d\u76df\u4e3b\uff0c\u8fd9\u4f4d\u59d1\u5a18\u662f\u4ece\u54ea\u91cc\u6765\u7684\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u4ed6\u81ea\u79f0\u662f\u4ece\u4e1c\u6d77\u6765\u7684\uff0c\u4eba\u5bb6\u90fd\u53eb\u4ed6\u4e1c\u6d77\u4ed9\u5b50\u3002\u201d \u3000\u3000\u6bd2\u706b\u6210\u5168\u70b9\u5934\u9053\uff1a\u201c\u8fd9\u5c31\u96be\u602a\u4e86\uff0c\u5982\u679c\u4e0d\u662f\u4e1c\u6d77\uff0c\u54ea\u91cc\u4f1a\u51fa\u8fd9\u6837\u4e00\u4f4d\u4ed9\u5b50\uff0c\u5979\u7684\u82b3\u540d\u53eb \u4ec0\u4e48\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u970d\u67d0\u53ea\u77e5\u5979\u59d3\u8bb8\uff0c\u81f3\u4e8e\u82b3\u540d\uff0c\u5979\u4e00\u76f4\u4e0d\u80af\u900f\u9732\u3002\u201d \u3000\u3000\u201c\u5979\u4e3a\u4ec0\u4e48\u4e0d\u8bf4\u8bdd\uff1f\u201d \u3000\u3000\u201c\u5979\u56e0\u4e3a\u662f\u4ed9\u5b50\uff0c\u628a\u54b1\u4eec\u90fd\u770b\u6210\u51e1\u4eba\uff0c\u6240\u4ee5\u4e0d\u80af\u8f7b\u6613\u8bb2\u8bdd\u3002\u201d \u3000\u3000\u201c\u4f60\u521a\u624d\u8bf4\u7684\u8282\u76ee\uff0c\u83ab\u975e\u4e1c\u6d77\u8bb8\u4ed9\u5b50\u7ad9\u5728\u8fd9\u91cc\u5c31\u662f\u8282\u76ee\uff1f\u201d \u3000\u3000\u201c\u8fd9\u53ea\u662f\u8bb8\u4ed9\u5b50\u51fa\u573a\uff0c\u8282\u76ee\u8fd8\u6ca1\u5f00\u59cb\u5462\u3002\u201d \u3000\u3000\u201c\u90a3\u5c31\u5f00\u59cb\u5427\uff0c\u522b\u8ba9\u8001\u592b\u548c\u4ed6\u4eec\u4e09\u4f4d\u7b49\u5f97\u5fc3\u6025\u2026\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u6bd2\u706b\u6210\u5168\u56db\u4eba\u548c\u7ad9\u5728\u8f83\u8fdc\u5904\u7684\u7fa4\u96c4\uff0c\u90fd\u628a\u773c\u775b\u7741\u5f97\u2014\u77ac\u4e0d\u77ac\uff0c\u8c01\u90fd\u4e0d\u518d\u53d1\u51fa\u58f0 \u97f3\uff0c\u4ee5\u4fbf\u4ed4\u7ec6\u770b\u770b\u8fd9\u5973\u4eba\u8981\u8868\u6f14\u4ec0\u4e48\u3002 \u3000\u3000\u5176\u5b9e\u5c31\u8fde\u201c\u8868\u6f14\u201d\u8fd9\u4e00\u540d\u8bcd\uff0c\u5728\u573a\u4f17\u4eba\u4e5f\u591a\u534a\u662f\u7b2c\u4e00\u6b21\u542c\u5230\u3002 \u3000\u3000\u53ea\u89c1\u8bb8\u5c0f\u65e6\u53c8\u5f00\u59cb\u626d\u8170\u6446\u81c0\u8d77\u6765\u3002 \u3000\u3000\u6447\u7740\u6447\u7740\uff0c\u4e24\u817f\u4e5f\u968f\u5373\u79fb\u52a8\u8d77\u6765\uff0c\u8fde\u53cc\u81c2\u4e5f\u4f38\u5c06\u51fa\u6765\uff0c\u5de6\u53f3\u4e0a\u4e0b\u7684\u865a\u7a7a\u4e71\u6293\u4e00\u901a\uff0c\u867d\u7136 \u8c01\u90fd\u4e0d\u77e5\u5979\u5728\u73a9\u7684\u4ec0\u4e48\u628a\u620f\uff0c\u4f46\u65e0\u53ef\u5426\u8ba4\u7684\uff0c\u4e00\u4e2a\u5973\u4eba\u5f53\u4f17\u8868\u6f14\u8fd9\u79cd\u52a8\u4f5c\uff0e\u5374\u662f\u4ed6\u4eec\u6709\u751f\u4ee5 \u6765\u9996\u6b21\u770b\u5230\u3002\u5f53\u7136\uff0c\u9ea6\u5c0f\u660e\u548c\u5d02\u5c71\u4e09\u96c4\u7b97\u662f\u4f8b\u5916\u3002 \u3000\u3000\u6bd2\u706b\u6210\u5168\u8fb9\u770b\u8fb9\u4f4e\u58f0\u9053\uff1a\u201c\u970d\u76df\u4e3b\uff0c\u662f\u4e0d\u662f\u5c31\u53ea\u6709\u8fd9\u4e00\u5957\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u7b11\u9053\uff1a\u201c\u8001\u9f20\u62c9\u5927\u677f\u8f66\u2014\u2014\u5927\u7684\u5728\u540e\u9762\u3002\u8bb8\u4ed9\u5b50\u7684\u8868\u6f14\uff0c\u4e5f\u662f\u597d\u7684\u5728\u540e\u9762\u3002\u201d \u3000\u3000\u6bd2\u706b\u6210\u5168\u9053\uff1a\u201c\u90a3\u5c31\u8bf7\u5979\u5feb\u6362\u8282\u76ee\u3002\u201d \u3000\u3000\u4ed6\u7684\u8bdd\u521a\u521a\u8bf4\u5b8c\uff0c\u7a81\u89c9\u773c\u775b\u4e00\u4eae\uff0c\u4e0d\u77e5\u4ec0\u4e48\u65f6\u5019\uff0e\u8bb8\u5c0f\u65e6\u7684\u9ed1\u8272\u6597\u7bf7\u5df2\u7ecf\u8131\u6389\uff0c\u9732\u51fa\u7684 \u662f\u4e00\u88ad\u8584\u8584\u7684\u767d\u7eb1\u3002 \u3000\u3000\u8fd9\u88ad\u8584\u7eb1\u5b9e\u5728\u592a\u8584\u4e86\uff0c\u51e0\u4e4e\u662f\u5168\u900f\u660e\u7684\uff0c\u6e05\u6670\u53ef\u89c1\u88f9\u5728\u5979\u8eab\u4e0a\u7684\u53ea\u662f\u4e00\u6761\u5185\u88e4\u548c\u809a\u513f\u3002 \u800c\u4e14\u88f9\u5f97\u5947\u7d27\uff0c\u4f7f\u5f97\u6574\u4e2a\u80f4\u4f53\u7684\u66f2\u7ebf\u5b8c\u5168\u66b4\u9732\u5728\u5916\u3002 \u3000\u3000\u5728\u90a3\u79cd\u5e74\u4ee3\uff0c\u5973\u4eba\u7684\u7a7f\u7740\u5982\u6b64\u66b4\u9732\uff0c\u7b80\u76f4\u662f\u95fb\u6240\u672a\u95fb\uff0c\u89c1\u6240\u672a\u89c1\uff0c\u8bf4\u5b83\u662f\u4e00\u79cd\u5929\u4e0b\u5947 \u89c2\uff0c\u4e5f\u7edd\u4e0d\u8fc7\u4efd\u3002 \u3000\u3000\u6b64\u523b\uff0c\u4e0d\u5c11\u4eba\u90fd\u770b\u5f97\u4e24\u773c\u53d1\u76f4\uff0c\u51e0\u4e4e\u8981\u6682\u65f6\u505c\u6b62\u547c\u5438\u3002 \u3000\u3000\u6bd2\u706b\u6210\u5168\u7b49\u56db\u4eba\u66f4\u662f\u76ee\u77aa\u53e3\u5446\uff0c\u5982\u9189\u4f3c\u51dd\uff0e \u3000\u3000\u8bb8\u5c0f\u65e6\u8fd8\u662f\u5728\u4e0d\u505c\u7684\u8df3\uff0c\u597d\u50cf\u811a\u5e95\u88c5\u4e86\u5f39\u7c27\uff0c\u4e24\u624b\u4e5f\u4e0d\u505c\u7684\u865a\u7a7a\u4e71\u6447\u3002 \u3000\u3000\u84e6\u5730\uff0c\u6bd2\u706b\u6210\u5168\u7b49\u56db\u4eba\u53ea\u89c9\u773c\u524d\u767d\u5149\u4e00\u95ea\uff0c\u63a5\u7740\u662f\u8f7b\u98ce\u62c2\u9762\u3002 \u3000\u3000\u539f\u6765\u8bb8\u5c0f\u65e6\u5df2\u628a\u767d\u7eb1\u892a\u6389\uff0c\u62ff\u5728\u624b\u4e2d\uff0c\u6545\u610f\u5728\u6bd2\u706b\u6210\u5168\u7b49\u56db\u4eba\u9762\u524d\u7529\u52a8\u4e86\u4e00\u4e0b\uff0c\u2014\u9762\u8fd8 \u4e0d\u4f4f\u5730\u659c\u7740\u5a9a\u773c\uff0c\u53cc\u7738\u4e71\u8f6c\u5730\u79cb\u6ce2\u9012\u9001\u3002 \u3000\u3000\u5728\u8fd9\u5239\u90a3\uff0c\u6bd2\u706b\u6210\u5168\u51e0\u4e4e\u8fde\u9aa8\u5934\u4e5f\u9165\u4e86\uff0c\u5634\u91cc\u4e0d\u7531\u81ea\u4e3b\u5730\u76f4\u5f80\u809a\u91cc\u541e\u53e3\u6c34\u3002 \u3000\u3000\u5f53\u6bd2\u706b\u6210\u5168\u518d\u5b9a\u775b\u770b\u65f6\uff0c\u8bb8\u5c0f\u65e6\u5df2\u505c\u6b62\u8df3\u52a8\uff0c\u6b63\u7ad9\u5728\u4ed6\u8eab\u524d\u4e0d\u8db3\u4e00\u5c3a\u5904\uff0c\u90a3\u809a\u513f\u4e0a\u65b9\u7684 \u4e24\u5ea7\u9ad8\u8038\u7684\u7389\u5cf0\u5dee\u70b9\u513f\u5c31\u8981\u89e6\u5230\u4ed6\u7684\u9f3b\u5b50\u4e0a\u3002 \u3000\u3000\u82e5\u975e\u5f53\u573a\u4f17\u76ee\u777d\u777d\uff0c\u6bd2\u706b\u6210\u5168\u771f\u6068\u4e0d\u5f97\u4f38\u51fa\u820c\u5934\u8214\u4e0a\u4e00\u8214\u3002 \u3000\u3000\u518d\u5f80\u4e0b\u770b\uff0c\u90a3\u6ed1\u5982\u817b\u8102\u7684\u5c0f\u8179\uff0e\u4fee\u957f\u7c89\u5ae9\u7684\u53cc\u817f\uff0c\u4e5f\u90fd\u89e6\u624b\u53ef\u53ca\u3002 \u3000\u3000\u504f\u504f\u8bb8\u5c0f\u65e6\u4ecd\u7ad9\u5728\u90a3\u91cc\u5bf9\u4ed6\u5a9a\u7b11\u3002 \u3000\u3000\u6bd2\u706b\u6210\u5168\u53ea\u89c9\u8fde\u5589\u5499\u4e5f\u5f00\u59cb\u53d1\u5e72\uff0c\u6296\u7740\u58f0\u97f3\u9053\uff1a\u201c\u970d\u76df\u4e3b\uff0e\u4ed9\u5b50\u8001\u7ad9\u5728\u8001\u592b\u9762\u524d\u505a\u4ec0 \u4e48\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u5979\u4e45\u4ef0\u6210\u8001\u82f1\u96c4\u5728\u6b66\u6797\u4e2d\u7684\u5927\u540d\uff0c\u7ad9\u5728\u90a3\u91cc\u4e0d\u52a8\uff0e\u662f\u8868\u793a\u656c\u5ba2\u3002\u201d \u3000\u3000\u6bd2\u706b\u6210\u5168\u6478\u4e86\u6478\u5634\u5df4\u9053\uff1a\u201c\u8001\u592b\u867d\u7136\u5728\u6b66\u6797\u4e2d\u6709\u70b9\u5c0f\u540d\u6c14\uff0c\u4f46\u5728\u8bb8\u4ed9\u5b50\u9762\u524d\uff0c\u5374\u663e\u5f97\u5341 \u5206\u6e3a\u5c0f\uff0c\u54ea\u91cc\u53ca\u5f97\u4ed9\u5b50\u8fd9\u822c\u5149\u8292\u56db\u5c04\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u81ea\u53e4\u7f8e\u4eba\u7231\u82f1\u96c4\uff0c\u4e5f\u8bb8\u5979\u7231\u4e0a\u4e86\u6210\u8001\u82f1\u96c4\uff0c\u6240\u4ee5\u624d\u7ad9\u5728\u90a3\u91cc\u4e0d\u8d70\u3002\u201d \u3000\u3000\u6bd2\u706b\u6210\u5168\u563b\u563b\u7b11\u9053\uff1a\u201c\u970d\u76df\u4e3b\u4e0d\u662f\u5f00\u73a9\u7b11\u5427\uff1f\u201d \u3000\u3000\u971c\u5143\u4f3d\u9053\uff1a\u201c\u5982\u679c\u6210\u8005\u82f1\u96c4\u4e0d\u4fe1\uff0c\u4e0d\u59a8\u5f53\u573a\u67e5\u8bc1\uff01\u201d \u3000\u3000\u8bf4\u7740\u671b\u5411\u8bb8\u5c0f\u65e6\u9053\uff1a\u201c\u4ed9\u5b50\uff0c\u4f60\u662f\u5426\u559c\u6b22\u6210\u8001\u82f1\u96c4\uff1f\u201d \u3000\u3000\u8c01\u77e5\u8bb8\u5c0f\u65e6\u7adf\u771f\u7684\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u970d\u5143\u4f3d\u6717\u6717\u7b11\u9053\uff1a\u201c\u600e\u4e48\u6837\uff0c\u970d\u67d0\u731c\u5f97\u4e0d\u9519\u5427\uff1f\u201d \u3000\u3000\u6bd2\u706b\u6210\u5168\u5e72\u54b3\u4e86\u4e24\u58f0\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u8001\u592b\u5c31\u2026\u2026\u201d \u3000\u3000\u201c\u6210\u8001\u82f1\u96c4\u5c31\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u201c\u5f53\u7740\u8fd9\u4e48\u591a\u4eba\u7684\u9762\uff0c\u8001\u592b\u4e5f\u4e0d\u4fbf\u592a\u8fc7\u5206\uff0c\u73b0\u5728\u8001\u592b\u53ea\u60f3\u2026\u2026\u201d \u3000\u3000\u201c\u6210\u8001\u82f1\u96c4\u60f3\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u201c\u60f3\u6478\u4e0a\u4e00\u6478\uff01\u201d \u3000\u3000\u201c\u6210\u8001\u82f1\u96c4\u8981\u600e\u6837\u6478\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u53ea\u662f\u6478\u6478\u5979\u7684\u8eab\u4e0a\uff0c\u4e0d\u53ef\u80fd\u6478\u5230\u91cc\u9762\u53bb\u3002\u201d \u3000\u3000\u201c\u8fd9\u79cd\u4e8b\u60c5\uff0c\u5fc5\u987b\u5148\u5f81\u6c42\u4e00\u4e0b\u4ed9\u5b50\u7684\u610f\u89c1\uff0c\u770b\u5979\u80af\u4e0d\u80af\u3002\u201d \u3000\u3000\u201c\u5c31\u8bf7\u970d\u76df\u4e3b\u4ee3\u8001\u592b\u95ee\u5979\u4e00\u4e0b\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u7acb\u5373\u6717\u58f0\u9053\uff1a\u201c\u6210\u8001\u82f1\u96c4\u7684\u8981\u6c42\uff0c\u4ed9\u5b50\u5fc5\u5b9a\u542c\u5230\u4e86\uff0c\u4f60\u7b54\u4e0d\u7b54\u5e94\uff1f\u201d \u3000\u3000\u8bb8\u5c0f\u65e6\u8ff7\u773c\u53d1\u51fa\u5a9a\u7b11\uff0e\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u6bd2\u706b\u6210\u5168\u987f\u65f6\u6655\u9676\u9676\u7684\u6709\u5982\u817e\u4e91\u9a7e\u96fe\u822c\uff0c\u7531\u4e8e\u592a\u8fc7\u5174\u594b\u6fc0\u52a8\uff0c\u4f38\u51fa\u7684\u4e00\u53ea\u53f3\u624b\uff0c\u4e5f\u5728\u4e0d \u4f4f\u98a4\u6296\u3002 \u3000\u3000\u90a3\u53ea\u624b\u4e94\u6307\u597d\u4f3c\u8783\u87f9\u63a7\u722a\uff0c\u5148\u642d\u4e0a\u8bb8\u5c0f\u65e6\u7684\u809a\u513f\uff0c\u987a\u7740\u4e0a\u65b9\u7684\u4e73\u6c9f\u6162\u6162\u4e0b\u6ed1\uff0e\u8d8a\u8fc7\u4e24\u5ea7 \u9ad8\u5cf0\uff0c\u518d\u5f9c\u5f89\u4e8e\u4e00\u7247\u5149\u6ed1\u7684\u5e73\u5766\u5730\u4e0a\uff0c\u518d\u79fb\u5411\u5c0f\u8179\u4e0b\uff0c\u7136\u540e\u5230\u8fbe\u4e00\u53cc\u7389\u817f\uff0c\u8fb9\u6478\u5634\u91cc\u8fd8\u8fb9\u5567 \u5567\u6709\u58f0\u3002 \u3000\u3000\u8bb8\u5c0f\u65e6\u5219\u5b9e\u5728\u5927\u65b9\u5f97\u4e0d\u80fd\u518d\u5927\u65b9\uff0c\u4e0d\u4f46\u52a8\u4e5f\u4e0d\u52a8\uff0c\u8138\u4e0a\u8fd8\u4e00\u76f4\u6d41\u9732\u7740\u5a07\u5a9a\u8ff7\u4eba\u7684\u7b11\u5bb9\u3002 \u3000\u3000\u5982\u6b64\u4e0a\u4e0a\u4e0b\u4e0b\u5927\u7ea6\u4e00\u76f4\u6478\u4e86\u4e09\u904d\uff0c\u624d\u4f9d\u4f9d\u4e0d\u820d\u7684\u7f29\u56de\u624b\u6765\u3002 \u3000\u3000\u5ffd\u542c\u9ed1\u9b54\u65f6\u5bc5\u8214\u5634\u7b11\u9053\uff1a\u201c\u6210\u5144\uff0e\u597d\u4e1c\u897f\u7531\u4f60\u4e00\u4e2a\u4eab\u53d7\uff0e\u672a\u514d\u592a\u4e0d\u516c\u5e73\u4e86\u5427\uff1f\u201d \u3000\u3000\u6bd2\u706b\u6210\u5168\u54a7\u4e86\u54a7\u5634\u9053\uff1a\u201c\u8fd9\u662f\u4ed9\u5b50\u7b54\u5e94\u8001\u592b\u7684\uff0c\u6709\u4ec0\u4e48\u4e0d\u516c\u5e73\uff1f\u201d \u3000\u3000\u9ed1\u9b54\u65f6\u5bc5\u987f\u4e86\u987f\u9053\uff1a\u201c\u90a3\u4e48\u65f6\u67d0\u4e5f\u60f3\u6478\u6478\uff0c\u6210\u4e0d\u6210\uff1f\u201d \u3000\u3000\u6bd2\u706b\u6210\u5168\u9053\uff1a\u201c\u4f60\u53c8\u4e0d\u662f\u6478\u8001\u592b\uff0e\u95ee\u8001\u592b\u6709\u4ec0\u4e48\u7528\uff1f\u201d \u3000\u3000\u9ed1\u9b54\u65f6\u5bc5\u8f6c\u5934\u9053\uff1a\u201c\u970d\u76df\u4e3b\uff0c\u53ef\u4e0d\u53ef\u4ee5\u5e2e\u65f6\u67d0\u5f81\u6c42\u4e00\u4e0b\u4ed9\u5b50\u7684\u610f\u89c1\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u8fd8\u6ca1\u6765\u5f97\u53ca\u95ee\uff0c\u8bb8\u5c0f\u65e6\u5df2\u81ea\u52a8\u8d70\u5230\u9ed1\u9b54\u65f6\u5bc5\u9762\u524d\uff0c\u800c\u4e14\u76ee\u6ce8\u65f6\u5bc5\u7728\u773c\u5a9a\u7b11\uff0e \u3000\u3000\u5c82\u77e5\u5979\u8fd9\u4e3e\u52a8\u7adf\u4f7f\u6bd2\u706b\u6210\u5168\u6253\u5fc3\u5e95\u6cdb\u8d77\u4e00\u79cd\u65e0\u540d\u5992\u610f\uff0c\u53ea\u662f\u4e0d\u4fbf\u5f62\u4e4b\u4e8e\u5916\u800c\u5df2\uff0e \u3000\u3000\u9ed1\u9b54\u65f6\u5bc5\u987f\u65f6\u7709\u5f00\u773c\u7b11\uff0c\u4f38\u51fa\u7684\u90a3\u53ea\u624b\uff0c\u6296\u5f97\u6bd4\u6bd2\u706b\u6210\u5168\u66f4\u5389\u5bb3\uff0e \u3000\u3000\u4ed6\u6478\u4eba\u7684\u65b9\u5f0f\u548c\u6bd2\u706b\u6210\u5168\u5b8c\u5168\u4e00\u6837\uff0c\u4e5f\u662f\u5148\u7531\u4e0a\u800c\u4e0b\uff0c\u518d\u7531\u4e0b\u800c\u4e0a\uff0c\u4e00\u8fde\u4e09\u904d\uff0c\u624d\u7f29\u56de \u624b\u53bb\uff0e \u3000\u3000\u8fd9\u65f6\uff0c\u5e76\u6392\u800c\u5750\u7684\u725b\u5954\u548c\u706b\u83bd\uff0c\u65e9\u5df2\u4e0d\u4f4f\u5728\u6293\u8033\u6320\u816e\uff0c\u4f3c\u4e4e\u5df2\u6025\u5f97\u50cf\u70ed\u9505\u4e0a\u7684\u8682\u8681\uff0e \u3000\u3000\u8bb8\u5c0f\u65e6\u521a\u8981\u8f6c\u8eab\u79bb\u53bb\uff0c\u725b\u5954\u7ec8\u4e8e\u5fcd\u4e0d\u4f4f\u543c\u9053\uff1a\u201c\u6e7e\u6f0f\u86d9\uff01\u201d \u3000\u3000\u6b64\u4eba\u770b\u6765\u9887\u80fd\u4e88\u4eba\u65b9\u4fbf\uff0c\u4ed6\u81ea\u77e5\u81ea\u5df1\u7684\u8bdd\u522b\u4eba\u4e0d\u6613\u542c\u61c2\uff0c\u56e0\u4e4b\u4e00\u76f4\u6781\u5c11\u5f00\u53e3\uff0c\u5728\u975e\u8bf4\u8bdd \u4e0d\u53ef\u65f6\uff0c\u4e5f\u4e00\u6b21\u53ea\u8bf4\u4e00\u53e5\uff0c\u800c\u4e14\u4e0d\u8fc7\u4e24\u4e09\u4e2a\u5b57\uff0e \u3000\u3000\u970d\u5143\u4f3d\u4e0d\u77e5\u725b\u5954\u5728\u56b7\u4ec0\u4e48\uff0c\u5fd9\u95ee\u6bd2\u706b\u6210\u5168\u9053\uff1a\u201c\u725b\u4eb2\u738b\u597d\u50cf\u5728\u53d1\u7765\u6c14\uff0c\u662f\u5426\u62b1\u6028\u970d\u67d0\u62db \u5f85\u4e0d\u5468\uff1f\u201d \u3000\u3000\u6bd2\u706b\u6210\u5168\u9053\uff1a\u201c\u725b\u4eb2\u738b\u4e5f\u60f3\u6478\uff0c\u4ed6\u521a\u624d\u90a3\u53e5\u2018\u6e7e\u6f0f\u86d9\u2019\uff0c\u7ffb\u8fc7\u6765\u5c31\u662f\u2018\u8fd8\u6709\u6211\u2019\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u7acb\u5373\u671b\u7740\u725b\u5954\u62f1\u62f1\u624b\u9053\uff1a\u201c\u62b1\u6b49\u62b1\u6b49\uff01\u201d \u3000\u3000\u8fd9\u6b21\u4ed6\u4e0d\u518d\u5f81\u6c42\u8bb8\u5c0f\u65e6\u7684\u610f\u89c1\uff0c\u53eb\u9053\uff1a\u201c\u725b\u4eb2\u738b\u662f\u8fdc\u6765\u7684\u8d35\u5ba2\uff0c\u6709\u670b\u81ea\u8fdc\u65b9\u6765\uff0c\u4e0d\u4ea6\u4e50 \u4e4e\uff0c\u8bb8\u4ed9\u5b50\u5c31\u7ed9\u4ed6\u6478\u6478\u5427\uff01\u201d \u3000\u3000\u8bb8\u5c0f\u65e6\u975e\u5e38\u542c\u8bdd\uff0c\u70b9\u4e86\u70b9\u5934\uff0c\u542b\u7b11\u518d\u6765\u5230\u725b\u5954\u9762\u524d\u3002 \u3000\u3000\u725b\u5954\u5e76\u975e\u4e2d\u571f\u4eba\u7269\uff0c\u52a8\u4f5c\u8f83\u6210\u5168\u548c\u65f6\u5bc5\u66f4\u4e3a\u7c97\u91ce\uff0c\u4e0d\u4f46\u6478\u7684\u624b\u6cd5\u6781\u91cd\uff0c\u800c\u4e14\u66f4\u6478\u5230\u809a\u513f \u548c\u5185\u88e4\u91cc\u9762\u53bb\u3002 \u3000\u3000\u8fd9\u4e00\u6765\u6478\u5f97\u8bb8\u5c0f\u65e6\u518d\u4e5f\u7b11\u4e0d\u51fa\uff0c\u53ea\u80fd\u54ac\u7259\u82e6\u6491\u3002 \u3000\u3000\u725b\u5954\u521a\u521a\u64a4\u56de\u7984\u5c71\u4e4b\u722a\uff0c\u53ea\u542c\u970d\u5143\u4f3d\u9053\uff1a\u201c\u706b\u603b\u5cd2\u4e3b\u4e5f\u662f\u8d35\u5ba2\uff0c\u54b1\u4eec\u4e0d\u80fd\u539a\u6b64\u8584\u5f7c\uff0c\u4ed9 \u5b50\u4e5f\u8bf7\u4e00\u89c6\u540c\u4ec1\uff0e\u4e0d\u53ef\u51b7\u843d\u4e86\u4ed6\u3002\u201d \u3000\u3000\u8bb8\u5c0f\u65e6\u518d\u6765\u5230\u706b\u83bd\u8eab\u524d\u3002 \u3000\u3000\u706b\u83bd\u4e00\u54a7\u5634\uff0c\u9732\u51fa\u6ee1\u53e3\u9ec4\u677f\u7259\uff0c\u7acb\u5373\u6478\u4e86\u4e0a\u53bb\uff0e \u3000\u3000\u8fd9\u4f4d\u5c71\u80de\u66f4\u91ce\uff0c\u5c45\u7136\u662f\u53cc\u624b\u4e00\u8d77\u52a8\u4f5c\uff0c\u800c\u4e14\u4e94\u6307\u7528\u529b\uff0c\u51e0\u4e4e\u8981\u6309\u8fdb\u8089\u91cc\u53bb\uff0c\u75db\u5f97\u8bb8\u5c0f\u65e6 \u4e0d\u4f4f\u76b1\u7709\u54a7\u5634\u3002 \u3000\u3000\u597d\u4e0d\u5bb9\u6613\u6253\u4e86\u2014\u6b21\u901a\u5173\uff0c\u8bb8\u5c0f\u65e6\u5728\u95ef\u8fc7\u56db\u5173\u4e4b\u540e\uff0c\u5df2\u662f\u9999\u6c57\u6dcb\u6f13\u3002 \u3000\u3000\u970d\u5143\u4f3d\u5475\u5475\u7b11\u9053\uff1a\u201c\u4ed9\u5b50\u8f9b\u82e6\u4e86\uff0c\u5c31\u8bf7\u56de\u623f\u4f11\u606f\u5427\uff01\u201d \u3000\u3000\u6bd2\u706b\u6210\u5168\u4e24\u8ddf\u76f4\u76f4\u5730\u671b\u7740\u8bb8\u5c0f\u65e6\u7684\u80cc\u5f71\u5728\u5c4f\u98ce\u540e\u6d88\u5931\uff0c\u624d\u8f6c\u5934\u9053\uff1a\u201c\u970d\u76df\u4e3b\uff0c\u4f60\u65b9\u624d\u8bf4 \u8fd9\u4f4d\u4ed9\u5b50\u662f\u4ece\u54ea\u91cc\u6765\u7684\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u7b11\u9053\uff1a\u201c\u6210\u8001\u82f1\u96c4\u771f\u662f\u8d35\u4eba\u591a\u5fd8\u4e8b\uff0c\u8bb8\u4ed9\u5b50\u662f\u4ece\u4e1c\u6d77\u6765\u7684\uff0e\u201d \u3000\u3000\u201c\u4e1c\u6d77\u80fd\u51fa\u8fd9\u6837\u7684\u7f8e\u5973\u5417\uff1f\u201d \u3000\u3000\u201c\u4e1c\u6d77\u6b63\u662f\u51fa\u7f8e\u5973\u7684\u5730\u65b9\uff0c\u800c\u4e14\u542c\u8bf4\u4e2a\u4e2a\u50cf\u4ed9\u5b50\u4e00\u6837\u7684\u5927\u65b9\uff0e\u201d \u3000\u3000\u201c\u5979\u5728\u8ff7\u8e2a\u8c37\u8981\u4f4f\u591a\u4e45\uff1f\u201d \u3000\u3000\u201c\u5f88\u96be\u8bf4\uff0c\u5982\u679c\u4ed9\u5b50\u8ba4\u4e3a\u8fd9\u91cc\u5f88\u8212\u670d\uff0c\u8bf4\u4e0d\u5b9a\u5c31\u4e0d\u8d70\u4e86\u3002\u201d \u3000\u3000\u201c\u8fd9\u6837\u8bf4\u8001\u592b\u4e5f\u4e0d\u60f3\u8d70\u4e86\u3002\u201d \u3000\u3000\u201c\u6210\u8001\u82f1\u96c4\u4e5f\u770b\u597d\u4e86\u8ff7\u8e2a\u8c37\uff1f\u201d \u3000\u3000\u201c\u8001\u592b\u662f\u60f3\u6709\u673a\u4f1a\u5e38\u5e38\u770b\u5230\u4ed9\u5b50\uff0c\u5982\u679c\u2026\u2026\u201d \u3000\u3000\u970d\u5143\u4f3d\u6de1\u6de1\u4e00\u7b11\u9053\uff1a\u3000\u201c\u6210\u8001\u82f1\u96c4\u7528\u4e0d\u7740\u5ba2\u6c14\uff0c\u6709\u8bdd\u8bf7\u76f4\u8bf4\u2026\u2026\u201d \u3000\u3000\u6bd2\u706b\u6210\u5168\u8fde\u54b3\u4e86\u4e24\u58f0\u9053\uff1a\u201c\u5982\u679c\u970d\u76df\u4e3b\u80af\u5e2e\u5fd9\u8bf7\u4ed9\u5b50\u968f\u8001\u592b\u8d70\uff0c\u8001\u592b\u60c5\u613f\u4e5f\u5e2e\u4f60\u4e00\u6b21 \u5fd9\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u6545\u505a\u4e00\u6123\u9053\uff1a\u201c\u6210\u8001\u82f1\u96c4\u5e2e\u970d\u67d0\u4ec0\u4e48\u5fd9\uff1f\u201d \u3000\u3000\u6bd2\u706b\u6210\u5168\u9053\uff1a\u201c\u8001\u592b\u53ef\u4ee5\u8981\u6c42\u725b\u4eb2\u738b\uff0c\u628a\u5341\u4e07\u4e24\u9ec4\u91d1\u6253\u4e2a\u5bf9\u6298\uff0c\u51cf\u4e3a\u4e94\u4e07\u4e24\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u6beb\u4e0d\u5728\u610f\u7684\u7b11\u9053\uff1a\u201c\u76d6\u5e99\u662f\u4ef6\u597d\u4e8b\uff0c\u970d\u67d0\u6350\u51fa\u5341\u4e07\u4e24\u9ec4\u91d1\u76d6\u5e99\uff0c\u6b63\u662f\u79ef\u4e0b\u4e00\u4ef6\u9634 \u529f\uff0c\u5fc3\u5b89\u7406\u5f97\uff0c\u4f55\u5fc5\u518d\u6253\u6298\u6263\uff1f\u201d \u3000\u3000\u201c\u90a3\u4e48\u4ed9\u5b50\u2026\u2026\u201d \u3000\u3000\u201c\u6210\u8001\u82f1\u96c4\u653e\u5fc3\uff0c\u5f85\u4f1a\u513f\u9152\u7b75\u6446\u597d\u65f6\uff0c\u970d\u67d0\u5fc5\u5b9a\u8bf7\u4ed9\u5b50\u524d\u6765\u4f5c\u966a\uff0c\u800c\u4e14\u8981\u5979\u5c31\u5750\u5728\u6210\u8001 \u82f1\u96c4\u8eab\u65c1\u3002\u201d \u3000\u3000\u6bd2\u706b\u6210\u5168\u7acb\u5373\u7b11\u5f97\u5408\u4e0d\u62e2\u5634\uff0c\u8fde\u58f0\u9053\uff1a\u201c\u90a3\u592a\u597d\u5566\uff01\u53ef\u662f\u6709\u2014\u4ef6\u2026\u2026\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u54ea\u4e00\u4ef6\uff1f\u201d \u3000\u3000\u6bd2\u706b\u6210\u5168\u987f\u4e86\u987f\u9053\uff1a\u201c\u8981\u5979\u4e5f\u662f\u65b9\u624d\u90a3\u79cd\u6253\u626e\uff0c\u53ea\u7a7f\u4e00\u6761\u77ed\u88e4\u4e00\u4ef6\u809a\u515c\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u7b11\u9053\uff1a\u201c\u5c0f\u4e8b\u4e00\u4ef6\uff0c\u636e\u8bf4\u6700\u8fd1\u6c5f\u6e56\u4e0a\u6d41\u884c\u4e00\u79cd\u53eb\u2018\u8131\u8863\u966a\u9152\u2019\u7684\u660e\u5802\uff0c\u5f85\u4f1a\u513f\u5e72 \u8106\u8bf7\u4ed9\u5b50\u628a\u809a\u515c\u548c\u77ed\u88e4\u4e5f\u8131\u4e86\uff0c\u4e0d\u662f\u66f4\u597d\u5417\uff1f\u201d \u3000\u3000\u6bd2\u706b\u6210\u5168\u4e00\u62cd\u5927\u817f\u9053\uff1a\u201c\u90a3\u592a\u597d\u5566\uff01\u8fd9\u53ef\u662f\u4f60\u8bf4\u7684\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u970d\u67d0\u5411\u6765\u8bf4\u8bdd\u7b97\u8bdd\uff0c\u82e5\u5979\u4e0d\u8131\uff0c\u6210\u8001\u82f1\u96c4\u53ea\u7ba1\u552f\u970d\u67d0\u662f\u95ee\uff01\u201d \u3000\u3000\u6bd2\u706b\u6210\u5168\u4e50\u5f97\u51e0\u4e4e\u8981\u624b\u821e\u8db3\u8e48\uff0c\u5927\u58f0\u9053\uff1a\u201c\u552f\u6709\u970d\u76df\u4e3b\u8fd9\u6837\u7684\u5feb\u4eba\uff0c\u624d\u80fd\u505a\u51fa\u8fd9\u6837\u7684\u5feb \u4e8b\u3002\u8001\u592b\u6d3b\u4e86\u5927\u534a\u8f88\u5b50\uff0c\u4eca\u5929\u603b\u7b97\u9047\u5230\u4e86\u771f\u6b63\u7684\u77e5\u5df1\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u6210\u8001\u82f1\u96c4\u8fc7\u5956\u4e86\uff01\u201d \u3000\u3000\u6bd2\u706b\u6210\u5168\u9053\uff1a\u201c\u4e8b\u4e0d\u5b9c\u8fdf\uff0c\u970d\u76df\u4e3b\u8be5\u5429\u5490\u4e0a\u83dc\u4e86\uff0e\u201d \u3000\u3000\u970d\u5143\u4f3d\u7acb\u5373\u671b\u5411\u8fdc\u5904\u7684\u641c\u9b42\u624b\u5df4\u5929\u4e49\u9053\uff1a\u201c\u5230\u53a8\u623f\u770b\u770b\u51c6\u5907\u597d\u4e86\u6ca1\u6709\uff1f\u201d \u3000\u3000\u5df4\u5929\u4e49\u5e94\u58f0\u800c\u53bb\u3002 \u3000\u3000\u8db3\u8db3\u8fc7\u4e86\u76cf\u8336\u5de5\u592b\uff0c\u624d\u89c1\u5df4\u5929\u4e49\u56de\u6765\u9053\uff1a\u201c\u9152\u83dc\u5df2\u7ecf\u51c6\u5907\u597d\uff0c\u9a6c\u4e0a\u5c31\u53ef\u9001\u8fdb\u5927\u5385\uff01\u201d \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u7a81\u542c\u6bd2\u706b\u6210\u5168\u53d1\u51fa\u4e00\u58f0\u5c16\u53eb\u9053\uff1a\u201c\u8001\u2026\u2026\u8001\u592b\u7684\u624b\uff01\u201d \u3000\u3000\u4ed6\u8fb9\u8bf4\u8fb9\u628a\u53f3\u624b\u4f38\u4e86\u51fa\u6765\uff0e \u3000\u3000\u53ea\u89c1\u6bd2\u706b\u6210\u5168\u90a3\u53ea\u53f3\u624b\u7adf\u7136\u53d8\u5f97\u4e00\u7247\u7ea2\u80bf\uff0c\u8fde\u5c0f\u81c2\u4e5f\u51e0\u4e4e\u80bf\u4e86\u4e00\u500d\u3002 \u3000\u3000\u4e0d\u6d88\u8bf4\uff0c\u8fd9\u4e09\u4eba\u4e5f\u548c\u6bd2\u706b\u6210\u5168\u4e00\u6837\uff0c\u624b\u81c2\u5168\u5728\u4e0d\u77e5\u4e0d\u89c9\u95f4\u80bf\u80c0\u8d77\u6765\uff0e\u5c24\u5176\u866b\u9b54\u706b\u83bd\uff0c\u56e0 \u4e3a\u6478\u4eba\u65f6\u662f\u4e24\u624b\u5e76\u7528\uff0c\u800c\u4e14\u53c8\u6478\u5f97\u7528\u529b\uff0c\u4ee5\u81f4\u80bf\u80c0\u5f97\u66f4\u662f\u5389\u5bb3\u3002 \u3000\u3000\u539f\u6765\uff0c\u8bb8\u5c0f\u65e6\u7684\u8eab\u4e0a\uff0c\u5df2\u4e8b\u5148\u6d82\u597d\u4e00\u5c42\u65e0\u8272\u65e0\u5473\u7684\u6bd2\u7c89\uff0c\u8fd9\u79cd\u6bd2\u7c89\u89e6\u5230\u4eba\u7684\u76ae\u80a4\u540e\uff0c\u5f53 \u65f6\u5e76\u65e0\u4efb\u4f55\u4e0d\u9002\u611f\u89c9\uff0c\u5374\u80fd\u6e17\u5165\u808c\u80a4\u5185\uff0c\u4fc3\u4f7f\u7a74\u9053\u9ebb\u75f9\uff0c\u8840\u6db2\u6d41\u52a8\u51cf\u7f13\uff0c\u800c\u5374\u5e76\u4e0d\u75bc\u75db\uff0c\u8fd9 \u4e5f\u5c31\u662f\u6bd2\u706b\u6210\u5168\u7b49\u4eba\u76f4\u5230\u73b0\u5728\u624d\u53d1\u89c9\u75c7\u72b6\u7684\u539f\u56e0\uff0e \u3000\u3000\u81f3\u4e8e\u8bb8\u5c0f\u65e6\uff0c\u56e0\u5728\u8eab\u4e0a\u5df2\u5148\u6d82\u597d\u2014\u5c42\u62b5\u6297\u8fd9\u79cd\u6bd2\u7c89\u7684\u53e6\u2014\u79cd\u836f\u7269\uff0c\u56de\u53bb\u4ee5\u540e\u53ea\u8981\u5f7b\u5e95\u6d17 \u4e00\u6b21\u6fa1\uff0c\u4fbf\u4e0d\u81f4\u6709\u4efb\u4f55\u4f24\u5bb3\u3002 \u3000\u3000\u970d\u5143\u4f3d\u5728\u63a5\u5f85\u6bd2\u706b\u6210\u5168\u7b49\u4eba\u4e4b\u524d\uff0c\u80fd\u80f8\u6709\u6210\u7af9\u822c\u663e\u5f97\u5f02\u5e38\u4ece\u5bb9\uff0c\u4e5f\u662f\u65e9\u5df2\u6709\u5907\u4e4b\u6545\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u970d\u5143\u4f3d\u65e9\u5df2\u79bb\u5ea7\u800c\u8d77\uff0c\u9762\u5bf9\u6bd2\u706b\u6210\u5168\u7b49\u4eba\uff0c\u5634\u89d2\u6cdb\u8d77\u4e00\u62b9\u9634\u7b11\u3002 \u3000\u3000\u6bd2\u706b\u6210\u5168\u970d\u7136\u800c\u8d77\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u970d\u5143\u4f3d\uff0e\u4f60\u7a76\u7adf\u73a9\u7684\u4ec0\u4e48\u628a\u620f\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u563f\u563f\u7b11\u9053\uff1a\u201c\u8001\u5c0f\u5b50\uff0c\u4f60\u73b0\u5728\u624d\u660e\u767d\uff0c\u5df2\u7ecf\u665a\u5566\uff01\u201d \u3000\u3000\u6bd2\u706b\u6210\u5168\u6c14\u6781\u4e4b\u4e0b\uff0c\u63a2\u624b\u4fbf\u60f3\u638f\u53d6\u6697\u5668\uff0e \u3000\u3000\u5c82\u77e5\u4ed6\u90a3\u4e00\u6761\u53f3\u81c2\uff0c\u65e9\u5df2\u4e0d\u80fd\u7075\u6d3b\u8fd0\u7528\uff0c\u8fde\u6574\u4e2a\u4e0a\u534a\u8eab\u90fd\u50cf\u4e2d\u4e86\u98ce\u5df2\u7ecf\u534a\u8eab\u4e0d\u9042\u4e86\u3002 \u3000\u3000\u9ed1\u9b54\u65f6\u5bc5\u4e5f\u65e0\u6cd5\u65bd\u51fa\u9ed1\u7802\u638c\u529b\u3002 \u3000\u3000\u81f3\u4e8e\u725b\u5954\uff0c\u4e5f\u5168\u8eab\u762b\u75ea\u65e0\u529b\u3002 \u3000\u3000\u706b\u83bd\u7684\u60c5\u5f62\u66f4\u60e8\uff0e\u6b64\u523b\u53cc\u624b\u624b\u6307\u5df2\u80bf\u80c0\u5f97\u7c97\u4e86\u4e00\u500d\uff0c\u6839\u672c\u65e0\u6cd5\u5f2f\u66f2\uff0c\u5f53\u7136\u4e5f\u5c31\u5931\u53bb\u65bd\u653e \u6bd2\u866b\u4e4b\u529b\u3002 \u3000\u3000\u970d\u5143\u4f3d\u62c2\u987b\u5475\u5475\u5927\u7b11\u9053\uff1a\u201c\u6211\u628a\u4f60\u4eec\u8fd9\u56db\u4e2a\u72d7\u5a18\u517b\u7684\uff0c\u5c45\u7136\u4e32\u901a\u4e00\u6c14\uff0c\u6253\u79cb\u98ce\u6253\u5230\u8c1c\u8e2a \u8c37\u6765\u4e86\uff0c\u4e5f\u4e0d\u6253\u542c\u6253\u542c\u6211\u970d\u5143\u4f3d\u662f\u4ec0\u4e48\u4eba\uff0c\u5802\u5802\u5929\u4e0b\u6b66\u6797\u603b\u5be8\uff0c\u96be\u9053\u4f1a\u683d\u5728\u4f60\u4eec\u8fd9\u4e9b\u6c5f\u6e56\u8d25 \u7c7b\u624b\u91cc\uff01\u201d \u3000\u3000\u6bd2\u706b\u6210\u5168\u54ac\u7259\u5207\u9f7f\u9053\uff1a\u201c\u970d\u5143\u4f3d\uff0c\u4f60\u597d\u5351\u9119\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u7b11\u9053\uff1a\u201c\u6bd4\u8d77\u4f60\u8fd9\u8001\u5c0f\u5b50\u6765\uff0c\u970d\u67d0\u8fd8\u7b97\u4e0d\u5f97\u5351\u9119\u3002\u4f55\u51b5\u5bf9\u4ed8\u4f60\u4eec\u8fd9\u79cd\u4eba\uff0c\u5351\u9119\u4e5f \u662f\u9ad8\u660e\u3002\u201d \u3000\u3000\u201c\u4f60\u51c6\u5907\u628a\u8001\u592b\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u201c\u82e5\u73b0\u5728\u8ba9\u4f60\u4eec\u6b7b\uff0c\u90a3\u672a\u514d\u592a\u4fbf\u5b9c\u4f60\u4eec\uff0c\u5148\u8ba9\u4f60\u4eec\u53d7\u70b9\u6d3b\u7f6a\u518d\u8bf4\uff01\u201d \u3000\u3000\u6bd2\u706b\u6210\u5168\u671b\u5411\u725b\u5954\u3001\u706b\u83bd\u9053\uff1a\u201c\u725b\u4eb2\u738b\u3001\u706b\u603b\u5cd2\u4e3b\uff0c\u771f\u662f\u5bf9\u4e0d\u8d77\uff01\u8fde\u7d2f\u4f60\u4eec\u4e5f\u8ddf\u7740\u5012 \u9709\uff01\u201d \u3000\u3000\u725b\u5954\u4e00\u8fb9\u9f87\u7259\u54a7\u5634\uff0c\u4e00\u8fb9\u5527\u5527\u54c7\u54c7\u7684\u53eb\u9a82\uff0c\u597d\u5728\u6ca1\u6709\u4eba\u542c\u5f97\u61c2\u4ed6\u9a82\u4e86\u4e9b\u4ec0\u4e48\u3002 \u3000\u3000\u970d\u5143\u4f3d\u54fc\u4e86\u58f0\u9053\uff1a\u201c\u8001\u5c0f\u5b50\uff0c\u7528\u4e0d\u7740\u7740\u6025\uff0e\u4f60\u65e2\u53eb\u6210\u5168\uff0c\u970d\u67d0\u4e00\u5b9a\u60f3\u529e\u6cd5\u6210\u5168\u4f60\uff01\u201d \u3000\u3000\u63a5\u7740\u56de\u5934\u9053\uff1a\u201c\u5df4\u8001\u5f1f\uff0c\u5c31\u7531\u4f60\u628a\u4ed6\u4eec\u56db\u4e2a\u5148\u62bc\u8fdb\u6c34\u7262\uff01\u201d \u3000\u3000\u5ffd\u542c\u9ea6\u5c0f\u660e\u9ad8\u53eb\u9053\uff1a\u201c\u522b\u5fd9\uff01\u6211\u5148\u7ed9\u8fd9\u8001\u5c0f\u5b50\u4e00\u70b9\u5389\u5bb3\u5c1d\u5c1d\u3002\u201d \u3000\u3000\u539f\u6765\uff0c\u9ea6\u5c0f\u660e\u60f3\u8d77\u5f53\u5e74\u80e1\u67cf\u9f84\u4e4b\u6b7b\uff0c\u4e0e\u6bd2\u706b\u6210\u5168\u4e0d\u65e0\u5173\u8054\uff0c\u4e5f\u53ef\u8d81\u8fd9\u673a\u4f1a\uff0c\u4e00\u6cc4\u80f8\u4e2d\u4e4b \u6124\u3002 \u3000\u3000\u4ed6\u8bdd\u58f0\u672a\u6bd5\uff0c\u4eba\u5df2\u8dc3\u843d\u5728\u8ddf\u524d\uff0c\u624b\u6307\u6bd2\u706b\u6210\u5168\u9053\uff1a\u201c\u8001\u738b\u516b\u86cb\uff0c\u4f60\u662f\u60f3\u6b7b\u8fd8\u662f\u60f3\u6d3b\uff1f\u201d \u3000\u3000\u6bd2\u706b\u6210\u5168\u4e24\u773c\u76f4\u7728\u7684\u95ee\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9a82\u9053\uff1a\u201c\u778e\u4e86\u72d7\u773c\uff01\u7adf\u8fde\u4f60\u7956\u5b97\u90fd\u4e0d\u8ba4\u8bc6\u3002\u201d \u3000\u3000\u201c\u4f60\u2026\u2026\u4f60\u6562\u9a82\u8001\u592b\uff1f\u201d \u3000\u3000\u201c\u9a82\u4f60\u662f\u5ba2\u6c14\uff0c\u63cd\u4f60\u8fd9\u8001\u5c0f\u5b50\u624d\u662f\u771f\u7684\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u8bf4\u7740\uff0c\u5907\u81c2\u4e00\u638c\uff0c\u76f4\u5411\u6bd2\u706b\u6210\u5168\u9762\u988a\u63b4\u53bb\uff0e \u3000\u3000\u6bd2\u706b\u6210\u5168\u65e9\u5df2\u6d3b\u52a8\u4e0d\u7075\uff0c\u529f\u529b\u65e0\u6cd5\u53d1\u6325\uff0c\u88ab\u4e00\u638c\u6253\u5f97\u8e09\u8dc4\u6454\u51fa\u4e94\u516d\u6b65\uff0e\u8fd8\u662f\u7ad9\u811a\u4e0d\u4f4f\uff0c \u76f4\u6454\u4e86\u4e2a\u72d7\u5403\u5c4e\u3002 \u3000\u3000\u5f85\u4ed6\u6323\u624e\u7740\u722c\u8d77\u8eab\u6765\uff0c\u4e0d\u4f46\u53e3\u89d2\u9c9c\u8840\u76f4\u6d41\uff0c\u8fde\u88ab\u63b4\u7684\u9762\u988a\u4e5f\u53d8\u4e86\u5f62\uff0e \u3000\u3000\u8fd9\u8001\u9b54\u5934\u6a2a\u884c\u9ed1\u9053\u5927\u534a\u8f88\u5b50\uff0c\u4ee5\u5f80\u4eba\u4eba\u5bf9\u4ed6\u95fb\u4e4b\u8272\u53d8\uff0c\u53ef\u80fd\u4ed6\u505a\u68a6\u4e5f\u8bbe\u60f3\u5230\uff0c\u5982\u4eca\u7adf\u88ab \u4e00\u4e2a\u4e73\u81ed\u672a\u5e72\u7684\u540e\u751f\u665a\u8f88\u6574\u5f97\u5982\u6b64\u72fc\u72c8\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u5e76\u672a\u5c31\u6b64\u7f62\u624b\uff0c\u8ddf\u8fc7\u53bb\u98de\u8d77\u4e00\u811a\uff0c\u518d\u8e22\u5411\u6bd2\u706b\u6210\u5168\u524d\u80f8\u3002 \u3000\u3000\u6bd2\u706b\u6210\u5168\u4e00\u58f0\u95f7\u54fc\uff0c\u5f53\u573a\u53c8\u662f\u4e00\u4e2a\u7b4b\u6597\u683d\u53bb\u3002 \u3000\u3000\u970d\u5143\u4f3d\u6015\u9ea6\u5c0f\u660e\u4e00\u65f6\u6027\u8d77\u628a\u6bd2\u706b\u6210\u5168\u6574\u6b7b\uff0c\u5fd9\u9053\uff1a\u201c\u9ea6\u5c0f\u5144\u5f1f\u6700\u597d\u6682\u65f6\u9976\u4ed6\u4e00\u6761\u72d7\u547d\uff0c \u660e\u5929\u8001\u592b\u518d\u5904\u7f6e\u4ed6\u4e0d\u8fdf\u3002\u201d \u3000\u3000\u522b\u770b\u970d\u5143\u4f3d\u8eab\u4e3a\u8ff7\u8e2a\u8c37\u76df\u4e3b\uff0c\u4f46\u5bf9\u9ea6\u5c0f\u660e\u5374\u4e0d\u5f97\u4e0d\u5c3d\u91cf\u5ba2\u6c14\u3002\u56e0\u4ed6\u65e9\u77e5\u9ea6\u5c0f\u660e\u6027\u60c5\u706b \u66b4\uff0c\u6b66\u529f\u53c8\u9ad8\u5f97\u51fa\u5947\uff0c\u82e5\u628a\u4ed6\u60f9\u607c\uff0c\u8bf4\u4e0d\u5b9a\u7ed9\u81ea\u5df1\u5f53\u573a\u4e0b\u4e0d\u4e86\u53f0\u9636\u3002\u9ea6\u5c0f\u660e\u5f53\u7740\u7fa4\u96c4\uff0c\u603b\u7b97 \u7ed9\u970d\u5143\u4f3d\u9762\u5b50\uff0c\u653e\u4e0b\u6bd2\u706b\u6210\u5168\uff0c\u518d\u4e00\u638c\u5411\u9ed1\u9b54\u65f6\u5bc5\u8138\u4e0a\u63b4\u53bb\u3002 \u3000\u3000\u9ed1\u9b54\u65f6\u5bc5\u4e00\u58f0\u60e8\u547c\uff0c\u4e5f\u5012\u5730\u8fde\u6eda\u4e03\u516b\u5c3a\u8fdc\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u53cd\u624b\u4e00\u638c\uff0c\u518d\u63b4\u5411\u725b\u5954\u3002 \u3000\u3000\u725b\u5954\u65e2\u77ee\u4e14\u80a5\uff0c\u672c\u6765\u5c31\u50cf\u4e2a\u8089\u7403\uff0c\u5012\u5730\u4e4b\u540e\u6eda\u5f97\u66f4\u8fdc\uff0e \u3000\u3000\u9ea6\u5c0f\u660e\u6b63\u8981\u8ddf\u8fc7\u53bb\u518d\u8865\u51e0\u811a\uff0c\u53ea\u542c\u725b\u5954\u5927\u53eb\u9053\uff1a\u201c\u6bcd\u6746\u9f20\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9a82\u9053\uff1a\u201c\u738b\u516b\u86cb\uff01\u8fde\u9f20\u7adf\u8fd8\u6709\u516c\u6746\u6bcd\u6746\u7684\uff0c\u4f60\u8bb2\u7684\u7a76\u7adf\u662f\u54ea\u4e00\u56fd\u8bdd\uff1f\u201d \u3000\u3000\u8fd9\u65f6\u725b\u5954\u5df2\u7a33\u4f4f\u6eda\u52a8\u4e4b\u52bf\uff0c\u6025\u6025\u6447\u624b\u9053\uff1a\u201c\u6bcd\u7ec6\u9f20\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u559d\u9053\uff1a\u201c\u7ba1\u4f60\u6bcd\u4eb2\u662f\u7c97\u662f\u7ec6\uff0e\u8fd9\u79cd\u4e8b\u4f55\u5fc5\u544a\u8bc9\u5916\u4eba\uff01\u201d \u3000\u3000\u725b\u5954\u77e5\u9053\u9ea6\u5c0f\u660e\u542c\u4e0d\u61c2\u81ea\u5df1\u7684\u8bdd\uff0c\u518d\u8bf4\u4e0b\u53bb\u66f4\u6613\u5f15\u8d77\u8bef\u4f1a\uff0c\u6025\u5f97\u53ea\u80fd\u8fde\u8fde\u6447\u624b\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u8fd9\u624d\u60f3\u8d77\u8be5\u627e\u4e2a\u7ffb\u8bd1\uff0c\u51b7\u51b7\u77a5\u4e86\u762b\u5750\u5728\u5730\u4e0a\u7684\u6bd2\u706b\u6210\u5168\u4e00\u773c\u9053\uff1a\u201c\u8001\u738b\u516b\u86cb\uff0c\u725b \u5954\u521a\u624d\u90a3\u4e24\u53e5\u8bdd\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u5982\u679c\u7ffb\u8bd1\u4e0d\u51fa\u6765\uff0c\u6211\u5c31\u8981\u4e86\u4f60\u7684\u72d7\u547d\uff01\u201d \u3000\u3000\u6bd2\u706b\u6210\u5168\u5df2\u5c1d\u5230\u5389\u5bb3\uff0c\u54ea\u6562\u4e0d\u542c\uff0c\u8fde\u5fd9\u9f80\u7259\u54a7\u5634\u5730\u9053\uff1a\u201c\u4ed6\u2026\u2026\u4ed6\u6ca1\u9a82\u4f60\uff01\u201d \u3000\u3000\u201c\u6211\u8981\u4f60\u7ffb\u8bd1\uff0c\u4e0d\u662f\u8981\u4f60\u5e2e\u4ed6\u8bb2\u8bdd\u3002\u201d \u3000\u3000\u201c\u4ed6\u8bf4\u2018\u6bcd\u6746\u9f20\u2019\uff0c\u5c31\u662f\u2018\u4e0d\u6562\u5566\u2019\uff01\u201d \u3000\u3000\u201c\u90a3\u4e48\u2018\u6bcd\u7ec6\u9f20\u2019\u5462\uff1f\u201d \u3000\u3000\u201c\u5c31\u662f\u2018\u4e0d\u662f\u5566\u2019\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u4e0d\u89c9\u6697\u81ea\u597d\u7b11\uff0c\u56de\u8eab\u53c8\u662f\u4e00\u638c\uff0c\u518d\u63b4\u5411\u866b\u9b54\u706b\u83bd\uff0c\u4ee5\u793a\u516c\u5e73\u3002 \u3000\u3000\u970d\u5143\u4f3d\u89c1\u9ea6\u5c0f\u660e\u5df2\u6253\u4e86\u4e2a\u901a\u5173\uff0c\u563f\u563f\u7b11\u9053\uff1a\u201c\u9ea6\u5c0f\u5144\u5f1f\u6253\u591f\u4e86\u5427\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4eca\u5929\u4e0d\u6253\u5566\uff01\u201d \u3000\u3000\u641c\u9b42\u624b\u5df4\u5929\u4e49\u968f\u5373\u5e26\u7740\u51e0\u540d\u55bd\u7f57\uff0c\u628a\u6210\u5168\u3001\u65f6\u5bc5\u3001\u725b\u5954\u3001\u706b\u83bd\u56db\u4eba\u62bc\u51fa\u5927\u5385\u3002 \u3000\u3000\u970d\u5143\u4f3d\u8fd9\u624d\u9ad8\u58f0\u9053\uff1a\u201c\u5404\u4f4d\u5c31\u8bf7\u5404\u81ea\u56de\u5be8\u4f11\u606f\uff0c\u4eca\u665a\u8001\u592b\u51b3\u5b9a\u8bbe\u7b75\u5e86\u529f\uff0c\u51e1\u662f\u5728\u573a\u8bf8 \u4f4d\uff0e\u4e00\u8d77\u53c2\u52a0\uff01\u201d \u3000\u3000\u5f53\u5929\u508d\u665a\uff0c\u805a\u4e49\u5385\u5185\u679c\u7136\u706f\u706b\u9ad8\u60ac\uff0c\u7167\u8000\u5f97\u5982\u540c\u767d\u663c\u3002 \u3000\u3000\u5927\u5385\u5185\u8bbe\u4e0b\u5341\u51e0\u684c\u76db\u7b75\uff0c\u663e\u5f97\u4e00\u7247\u559c\u6c14\u6d0b\u6d0b\uff0e \u3000\u3000\u7fa4\u96c4\u4eec\u5f88\u5feb\u4fbf\u5df2\u5230\u9f50\u3002 \u3000\u3000\u5173\u4e8e\u4e0a\u5348\u5236\u670d\u6bd2\u706b\u6210\u5168\u7b49\u56db\u4eba\u90a3\u4e00\u5e55\uff0c\u6b64\u523b\u4f9d\u7136\u5728\u7fa4\u96c4\u4eec\u8111\u6d77\u91cc\u6325\u4e4b\u4e0d\u53bb\u3002 \u3000\u3000\u7fa4\u96c4\u4eec\u5bf9\u4e8e\u970d\u5143\u4f3d\u5229\u7528\u5973\u8272\u8bf1\u4f7f\u6bd2\u706b\u6210\u5168\u56db\u4eba\u4e0a\u5f53\u7684\u624b\u6bb5\uff0c\u867d\u7136\u4e0d\u5c11\u4eba\u8ba4\u4e3a\u5728\u505a\u6cd5\u4e0a\u8fd1 \u4e4e\u5351\u9119\u4f4e\u52a3\u3002\u4f46\u60f3\u5230\u970d\u5143\u4f3d\u4e3a\u4e86\u633d\u6551\u8ff7\u8e2a\u8c37\u547d\u8fd0\u800c\u8fd9\u6837\u505a\uff0c\u4e5f\u672a\u59cb\u4e0d\u662f\u4e00\u79cd\u9ad8\u660e\u624b\u6bb5\u3002\u56e0 \u6b64\uff0c\u4ed6\u4eec\u7edd\u5927\u591a\u6570\u90fd\u5bf9\u970d\u5143\u4f3d\u9887\u4e3a\u5fc3\u670d\uff0c\u4e5f\u4f7f\u970d\u5143\u4f3d\u5728\u8ff7\u8e2a\u8c37\u7684\u58f0\u671b\u63d0\u9ad8\u4e0d\u5c11\uff0e \u3000\u3000\u53ea\u662f\u4ee4\u7edd\u5927\u591a\u6570\u4eba\u7eb3\u95f7\u7684\uff0c\u8ff7\u8e2a\u8c37\u7a76\u7adf\u4ec0\u4e48\u65f6\u5019\u6765\u4e86\u8fd9\u4e48\u4e00\u4f4d\u4e1c\u6d77\u8bb8\u4ed9\u5b50\uff0c\u800c\u8fd9\u4f4d\u4e1c\u8bf2 \u8bb8\u4ed9\u5b50\u7684\u5927\u80c6\u4f5c\u98ce\uff0e\u66f4\u4ee4\u4ed6\u4eec\u5230\u73b0\u5728\u8fd8\u4e3a\u4e4b\u548b\u820c\u3002 \u3000\u3000\u970d\u5143\u4f3d\u6700\u540e\u5230\u8fbe\uff0c\u800c\u4e14\u8fd8\u662f\u643a\u7737\u53c2\u52a0\uff0c\u5e26\u6765\u4ed6\u7684\u65b0\u5a5a\u592b\u4eba\u82d7\u7d20\u5170\u3002 \u3000\u3000\u4ed6\u4eec\u592b\u5987\u521a\u521a\u5750\u4e0b\uff0c\u738b\u5927\u5eb7\u5c31\u62a2\u7740\u9053\uff1a\u201c\u76df\u4e3b\u4e3a\u4ec0\u4e48\u4e0d\u628a\u90a3\u4f4d\u8bb8\u4ed9\u5b50\u4e5f\u5e26\u6765\uff1f\u201d \u3000\u3000\u971c\u5143\u4f3d\u5475\u5475\u7b11\u9053\uff1a\u201c\u8fd9\u662f\u54b1\u4eec\u7684\u5e86\u529f\u5bb4\uff0c\u4f55\u5fc5\u5e26\u5979\u6765\uff01\u201d \u3000\u3000\u738b\u5927\u5eb7\u9053\uff1a\u201c\u5c82\u6709\u6b64\u7406\uff0c\u82e5\u8bf4\u529f\u52b3\u5927\uff0e\u90a3\u4f4d\u8bb8\u4ed9\u5b50\u8be5\u5c45\u7b2c\u4e00\uff0e\u82e5\u6ca1\u6709\u5979\uff0e\u4eca\u665a\u7684\u5e86\u529f\u5bb4 \u53ea\u6015\u5c31\u8be5\u7531\u6bd2\u706b\u6210\u5168\u90a3\u8001\u5c0f\u5b50\u6765\u4e3b\u6301\u4e86\uff0e\u201d \u3000\u3000\u970d\u5143\u4f3d\u8138\u4e0a\u4e00\u70ed\u9053\uff1a\u201c\u5979\u5f88\u7d2f\uff0c\u800c\u4e14\u8eab\u4e0a\u7684\u6bd2\u7c89\u8fd8\u6ca1\u5f7b\u5e95\u6d17\u5e72\u51c0\uff0c\u6240\u4ee5\u4e0d\u65b9\u4fbf\u6765\uff0e\u201d \u3000\u3000\u738b\u5927\u5eb7\u54fc\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u54ea\u91cc\u662f\u5979\u4e0d\u65b9\u4fbf\uff0c\u5206\u660e\u662f\u4f60\u8fd9\u76df\u4e3b\u6709\u79c1\u5fc3\uff0e\u201d \u3000\u3000\u971c\u5143\u4f3d\u8138\u8272\u4e00\u53d8\u9053\uff1a\u201c\u738b\u8001\u5f1f\u8fd9\u8bdd\u2026\u2026\u201d \u3000\u3000\u738b\u5927\u5eb7\u9053\uff1a\u201c\u5979\u8131\u5149\u4e86\uff0c\u4f60\u60f3\u7559\u7740\u81ea\u5df1\u770b\uff0c\u5bf9\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u7ec8\u4e8e\u6309\u637a\u4e0d\u4f4f\uff0c\u53cc\u76ee\u7cbe\u5149\u95ea\u5c04\uff0c\u53f1\u9053\uff1a\u201c\u738b\u8001\u5f1f\uff0c\u4f60\u662f\u5426\u5b58\u5fc3\u8981\u6765\u6405\u573a\uff1f\u201d \u3000\u3000\u738b\u5927\u5eb7\u5927\u58f0\u9053\uff1a\u201c\u4ffa\u8bf4\u7684\u672c\u6765\u662f\u6b63\u7406\uff0c\u4f60\u80fd\u8ba9\u90a3\u5973\u4eba\u8131\u5149\u4e86\u7ed9\u5916\u4eba\u6478\uff0c\u5374\u4e0d\u80af\u7ed9\u81ea\u5df1\u4eba \u770b\uff0c\u96be\u9053\u54b1\u4eec\u81ea\u5df1\u5f1f\u5144\u8fd8\u4e0d\u5982\u5916\u4eba\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u88ab\u5f04\u5f97\u7b80\u76f4\u557c\u7b11\u7686\u975e\uff0c\u5f53\u7740\u8fd9\u4e48\u591a\u4eba\u7684\u9762\uff0c\u53c8\u5728\u5e86\u529f\u5bb4\u4e0a\uff0c\u5982\u679c\u53d1\u4f5c\u8d77\u6765\uff0c\u5f04\u5f97 \u4e0d\u6b22\u800c\u6563\uff0e\u5c82\u4e0d\u4e22\u5c3d\u9762\u5b50\uff1f \u3000\u3000\u597d\u5728\u4ed6\u672c\u5c31\u8001\u5978\u5de8\u733e\uff0c\u53ea\u597d\u6781\u529b\u9690\u5fcd\u4e0d\u53d1\u3002 \u3000\u3000\u5750\u5728\u970d\u5143\u4f3d\u65c1\u8fb9\u7684\u82d7\u7d20\u5170\uff0c\u66f4\u662f\u80c0\u7ea2\u4e86\u8138\u3002 \u3000\u3000\u7fa4\u96c4\u4eec\u65e2\u540c\u60c5\u970d\u5143\u4f3d\uff0c\u53c8\u89c9\u5f97\u738b\u5927\u5eb7\u7684\u8bdd\u6709\u7406\uff0c\u56e0\u4e3a\u4ed6\u4eec\u4e5f\u60f3\u518d\u6b23\u8d4f\u2014\u6b21\u8bb8\u5c0f\u65e6\u7684\u8131\u8863 \u98ce\u5149\uff0e \u3000\u3000\u5d02\u5c71\u4e09\u96c4\u4e2d\u7684\u8001\u5927\u7b11\u9762\u4f5b\u9c8d\u8d85\u548c\u8001\u4e8c\u9b3c\u8bf8\u845b\u6d2a\u6cfd\u5219\u5411\u738b\u5927\u5eb7\u76f4\u4f7f\u773c\u8272\uff0c\u738b\u5927\u5eb7\u4e5f\u89c9\u51fa\u81ea \u5df1\u672a\u514d\u65e0\u7406\u53d6\u95f9\uff0c\u603b\u7b97\u6ca1\u518d\u8bf4\u4ec0\u4e48\u3002 \u3000\u3000\u970d\u5143\u4f3d\u89c1\u573a\u9762\u6709\u4e9b\u5c34\u5c2c\uff0c\u8fde\u5fd9\u8d77\u8eab\u656c\u9152\u3002 \u3000\u3000\u9152\u8fc7\u4e09\u5de1\uff0c\u573a\u9762\u624d\u6e10\u6e10\u53c8\u70ed\u95f9\u8d77\u6765\uff0e \u3000\u3000\u8fd9\u4e00\u987f\u5e86\u529f\u5bb4\uff0c\u76f4\u5403\u5230\u5c06\u8fd1\u4e09\u66f4\uff0c\u56e0\u4e3a\u970d\u5143\u4f3d\u5174\u81f4\u4ecd\u6d53\uff0c\u56e0\u4e4b\u5e76\u65e0\u4e00\u4eba\u79bb\u5e2d\u3002 \u3000\u3000\u5c31\u662f\u8fd9\u65f6\uff0c\u5ffd\u89c1\u4e00\u540d\u8d1f\u8d23\u5185\u5be8\u5de1\u903b\u7684\u5934\u76ee\uff0c\u9762\u65e0\u4eba\u8272\u614c\u614c\u5f20\u5f20\u7684\u5954\u4e86\u8fdb\u6765\uff0c\u6765\u5230\u970d\u5143\u4f3d \u5e2d\u524d\u884c\u4e86\u4e00\u793c\u9053\uff1a\u201c\u7980\u76df\u4e3b\uff0c\u4e0d\u2026\u2026\u4e0d\u597d\u5566\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u4e5f\u5403\u4e86\u2014\u60ca\uff0c\u6025\u6025\u95ee\u9053\uff1a\u201c\u53d1\u751f\u4e86\u4ec0\u4e48\u4e8b\uff1f\u5feb\u62a5\u4e0a\u6765\uff01\u201d \u3000\u3000\u90a3\u5934\u76ee\u9053\uff1a\u201c\u5c5e\u4e0b\u65b9\u624d\u5de1\u903b\u5230\u6c34\u7262\u9644\u8fd1\uff0e\u89c1\u6c34\u7262\u5916\u8d1f\u8d23\u76d1\u5b88\u7684\u4e24\u540d\u5f1f\u5144\u5168\u88ab\u4eba\u70b9\u4e86\u7a74 \u9053\uff0c\u660f\u5012\u5728\u5730\u4e0a\uff0e\u201d \u3000\u3000\u201c\u4f60\u53ef\u66fe\u8fdb\u5165\u6c34\u7262\u67e5\u770b\uff1f\u201d \u3000\u3000\u201c\u5c5e\u4e0b\u7acb\u5373\u51b2\u8fdb\u6c34\u7262\uff0c\u53ea\u89c1\u2026\u2026\u201d \u3000\u3000\u201c\u600e\u4e48\u6837\uff1f\u5feb\u8bf4\uff01\u201d \u3000\u3000\u201c\u6bd2\u2026\u2026\u6bd2\u706b\u6210\u5168\u548c\u53e6\u5916\u90a3\u4e09\u4e2a\u88ab\u56da\u7684\u5168\u4e0d\u89c1\u4e86\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u987f\u65f6\u4e3a\u4e4b\u4e00\u5446\u3002 \u3000\u3000\u5927\u5385\u5185\u6240\u6709\u7684\u4eba\uff0c\u5728\u8fd9\u5239\u90a3\uff0c\u4e5f\u51e0\u4e4e\u5168\u6123\u5728\u5f53\u573a\u3002 \u3000\u3000\u53ea\u542c\u970d\u5143\u4f3d\u9ad8\u53eb\u9053\uff1a\u201c\u5404\u4f4d\u5feb\u56de\u53bb\u53d6\u597d\u5175\u5203\uff0c\u9a6c\u4e0a\u518d\u56de\u5927\u5385\u524d\u51c6\u5907\u8fce\u6218\uff01\u201d \u3000\u3000\u4e00\u9635\u684c\u6905\u9910\u76d8\u78b0\u649e\u58f0\u4e2d\uff0c\u7fa4\u96c4\u6b63\u8981\u8d77\u8eab\u79bb\u53bb\uff0c\u4fbf\u542c\u5927\u95e8\u5916\u4f20\u6765\u4e00\u9635\u51b7\u68ee\u68ee\u6709\u5982\u9b3c\u57df\u522e\u8d77 \u9634\u98ce\u822c\u7684\u7b11\u58f0\u9053\uff1a\u201c\u4e0d\u5fc5\u56de\u53bb\u5566\uff0c\u8001\u592b\u5c31\u5728\u8fd9\u5927\u5385\u5185\u4e00\u4e2a\u4e2a\u628a\u4f60\u4eec\u70b9\u540d\u9001\u4e0a\u897f\u5929\uff01\u201d \u3000\u3000\u8bdd\u58f0\u672b\u6bd5\uff0c\u4e00\u4e2a\u80f8\u5782\u767d\u9aef\uff0c\u8eab\u62ab\u9ed1\u888d\uff0c\u53d1\u633d\u9053\u9afb\uff0c\u624b\u6267\u62c2\u5c18\uff0e\u8138\u957f\u5982\u9a6c\uff0c\u53cc\u98a7\u9ad8\u7a81\uff0c\u9762 \u5982\u6b7b\u7070\u7684\u7626\u9ad8\u4eba\u5f71\uff0c\u5df2\u5835\u5728\u5927\u5385\u95e8\u53e3\u3002 \u3000\u3000\u8fd9\u4eba\u8d6b\u7136\u662f\u76d8\u8e1e\u5415\u6881\u5c71\u201c\u4e5d\u5929\u7384\u9634\u6d1e\u5e9c\u201d\u7684\u9ed1\u9053\u715e\u661f\u9634\u624b\u4e00\u9b54\u3002 \u3000\u3000\u7d27\u63a5\u53c8\u6709\u4e8c\u6761\u4eba\u5f71\u6765\u5230\u9634\u624b\u4e00\u9b54\u8eab\u540e\uff0c\u6b63\u662f\u6bd2\u706b\u6210\u5168\u3001\u9ed1\u9b54\u65f6\u5bc5\u548c\u725b\u5954\uff0c\u4f46\u5374\u4e0d\u89c1\u866b\u9b54 \u706b\u83bd\u3002 \u3000\u3000\u9634\u624b\u4e00\u9b54\u563f\u563f\u7b11\u9053\uff1a\u201c\u706b\u603b\u5cd2\u4e3b\u5b88\u5728\u5927\u5385\u4fa7\u95e8\u5916\uff0c\u4ed6\u624b\u91cc\u7d27\u634f\u7740\u90a3\u888b\u91d1\u8695\u795e\u866b\uff0c\u4f60\u4eec\u60f3 \u4ece\u4fa7\u95e8\u9003\u751f\uff0c\u90a3\u662f\u5984\u60f3\uff01\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u4efb\u51ed\u970d\u5143\u4f3d\u5982\u4f55\u9634\u6c89\u8001\u8fa3\uff0c\u4e5f\u5927\u611f\u5fc3\u614c\uff0c\u4ed6\u771f\u540e\u6094\u4e3a\u4ec0\u4e48\u4e0d\u5728\u4e0a\u5348\u628a\u6bd2\u706b\u6210\u5168\u7b49 \u56db\u4eba\u5f53\u573a\u5904\u6b7b\u3002\u6240\u5e78\u90a3\u4e24\u7b3c\u6bd2\u866b\uff0c\u5df2\u88ab\u4ed6\u711a\u70e7\u6389\uff0c\u4f46\u5374\u5fd8\u8bb0\u628a\u706b\u83bd\u85cf\u5728\u8eab\u4e0a\u7684\u6bd2\u866b\u70e7\u6bc1\u3002 \u3000\u3000\u81f3\u4e8e\u7fa4\u96c4\u4eec\uff0c\u6b64\u523b\u4e5f\u90fd\u6beb\u65e0\u7528\u6b66\u4e4b\u5730\uff0c\u56e0\u4e3a\u4ed6\u4eec\u662f\u53c2\u52a0\u5e86\u529f\u5bb4\u800c\u6765\uff0e\u8c01\u90fd\u4e0d\u66fe\u643a\u5e26\u5175 \u5203\uff0c\u800c\u4e14\u88ab\u56f0\u5728\u5927\u5385\u4e4b\u5185\uff0c\u5373\u4f7f\u6709\u6b66\u5668\uff0c\u4e5f\u6ca1\u6709\u8db3\u591f\u7684\u7a7a\u95f4\u65bd\u5c55\u3002 \u3000\u3000\u6bd2\u706b\u6210\u5168\u8fd9\u65f6\u53cc\u624b\u4e5f\u7d27\u6263\u6697\u5668\uff0c\u51b7\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u54ea\u4e2a\u4e0d\u6015\u6b7b\u7684\uff0c\u53ea\u7ba1\u5f80\u5916\u51b2\uff0c\u8001\u592b\u5de6 \u624b\u662f\u4e09\u5341\u516d\u679a\u6885\u82b1\u9488\uff0c\u53f3\u624b\u662f\u56db\u5341\u516b\u679a\u874e\u5c3e\u9488\uff0e\u800c\u4e14\u90fd\u5df2\u7ecf\u6dec\u8fc7\u6bd2\uff0c\u8db3\u591f\u7ed9\u4f60\u4eec\u4e00\u4e2a\u4e2a\u70b9\u540d \u7528\u7684\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u548c\u7fa4\u96c4\u5fc3\u91cc\u90fd\u6709\u6570\uff0c\u6bd2\u706b\u6210\u5168\u8fd9\u8bdd\u7edd\u975e\u84c4\u610f\u6050\u5413\uff0c\u82e5\u8d38\u7136\u5411\u5916\u786c\u95ef\uff0c\u52bf\u5fc5\u8ba8\u4e0d\u4e86 \u597d\u5904\uff0c\u6b64\u523b\u4ed6\u4eec\u552f\u4e00\u7684\u529e\u6cd5\uff0c\u4fbf\u662f\u5fc5\u987b\u4fdd\u6301\u51b7\u9759\u6c89\u7740\uff0c\u8bbe\u6cd5\u62d6\u5ef6\u65f6\u95f4\uff0e\u4f3a\u673a\u518d\u91c7\u53d6\u884c\u52a8\u3002 \u3000\u3000\u6bd2\u706b\u6210\u5168\u4f3c\u4e4e\u5df2\u7ecf\u7b49\u4e0d\u5f97\uff0c\u8de8\u524d\u4e00\u6b65\u9053\uff1a\u201c\u9634\u624b\u5144\uff0c\u8fd8\u7b49\u4ec0\u4e48\uff0c\u5144\u5f1f\u7684\u6697\u5668\u5c31\u51b3\u5b9a\u9a6c\u4e0a \u51fa\u624b\u4e86\uff01\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u9053\uff1a\u201c\u4f60\u6025\u4ec0\u4e48\uff0c\u4ed6\u4eec\u73b0\u5728\u5df2\u662f\u74ee\u4e2d\u4e4b\u9cd6\uff0c\u8981\u6740\u4e5f\u4e0d\u5fc5\u6025\u5728\u4e00\u65f6\u3002\u201d \u3000\u3000\u6bd2\u706b\u6210\u5168\u56e0\u81ea\u5df1\u548c\u5176\u4ed6\u4e09\u4eba\u7cfb\u9634\u624b\u2014\u9b54\u6240\u6551\uff0c\u57fa\u4e8e\u611f\u6069\u5fc3\u7406\uff0c\u53ea\u597d\u6682\u65f6\u5f3a\u5fcd\u4e0b\u6765\u3002 \u3000\u3000\u5ffd\u89c1\u9634\u624b\u4e00\u9b54\u89c6\u7ebf\u6295\u5411\u82d7\u7d20\u5170\uff0c\u559d\u9053\uff1a\u201c\u597d\u4e00\u4e2a\u53db\u5e08\u706d\u7956\u7684\u8d31\u5a62\uff0c\u8fd8\u4e0d\u5feb\u5feb\u8fc7\u6765\u53d7 \u6b7b\uff01\u201d \u3000\u3000\u82d7\u7d20\u5170\u65e9\u5df2\u5413\u5f97\u9762\u65e0\u4eba\u8272\uff0c\u98a4\u58f0\u53eb\u9053\uff1a\u201c\u5e08\u7236\uff0c\u53ea\u8981\u4f60\u7b54\u5e94\u4e0d\u4f24\u5bb3\u5927\u5385\u5185\u6240\u6709\u7684\u4eba\uff0c\u5f1f \u5b50\u60c5\u613f\u73b0\u5728\u5c31\u6b7b\u5728\u60a8\u9762\u524d\u3002\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u51b7\u7b11\u9053\uff1a\u201c\u8001\u592b\u73b0\u5728\u8fd8\u4e0d\u60f3\u8981\u4f60\u6b7b\uff0c\u6211\u8981\u628a\u4f60\u5e26\u56de\u53bb\u6162\u6162\u6298\u78e8\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u7075\u673a\u4e00\u52a8\uff0c\u7acb\u5373\u6293\u4f4f\u82d7\u7d20\u5170\u624b\u81c2\uff0c\u9ad8\u58f0\u9053\uff1a\u201c\u82d7\u59d1\u5a18\u65e9\u5df2\u662f\u8ff7\u8e2a\u8c37\u7684\u4eba\uff0c\u5c0a\u9a7e\u82e5 \u60f3\u5e26\u8d70\u5979\uff0c\u90a3\u5c31\u5fc5\u987b\u7b54\u5e94\u970d\u67d0\u4e00\u4e2a\u6761\u4ef6\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u6761\u4ef6\uff1f\u201d \u3000\u3000\u201c\u5c31\u662f\u65b9\u624d\u82d7\u59d1\u5a18\u6240\u8bf4\u7684\uff0c\u4e0d\u80fd\u4f24\u5bb3\u4efb\u4f55\u4e00\u4e2a\u8ff7\u8e2a\u8c37\u7684\u4eba\u3002\u201d \u3000\u3000\u201c\u8001\u592b\u5982\u679c\u4e0d\u7b54\u5e94\u5462\uff1f\u201d \u3000\u3000\u201c\u970d\u67d0\u5c31\u5148\u6740\u4e86\u82d7\u59d1\u5a18\uff0c\u8ba9\u4f60\u65e0\u6cd5\u5f97\u5230\u5979\uff0e\u201d \u3000\u3000\u201c\u4f60\u53ef\u77e5\u9053\u90a3\u65f6\u5019\u4f60\u5c06\u5f97\u5230\u4ec0\u4e48\u540e\u679c\uff1f\u201d \u3000\u3000\u201c\u53ea\u8981\u80fd\u8ba9\u4f60\u5f97\u4e0d\u5230\u82d7\u59d1\u5a18\uff0c\u970d\u67d0\u5c31\u662f\u2014\u6b7b\u4e5f\u65e0\u6028\uff01\u201d \u3000\u3000\u8fd9\u51e0\u53e5\u8bdd\uff0c\u679c\u771f\u4f7f\u9634\u624b\u4e00\u9b54\u8d77\u4e86\u6212\u5fc3\uff0c\u4ed6\u7684\u76ee\u7684\u662f\u60f3\u628a\u82d7\u7d20\u5170\u5e26\u56de\u5415\u6881\u5c71\uff0c\u7136\u540e\u518d\u8bf1\u56de \u82d7\u7d20\u82d3\uff0c\u4ee5\u4fbf\u2014\u8d77\u5904\u7f6e\u3002 \u3000\u3000\u82e5\u82d7\u7d20\u5170\u6b64\u523b\u6b7b\u5728\u970d\u5143\u4f3d\u624b\u4e2d\uff0c\u5c82\u4e0d\u4f7f\u539f\u5b9a\u8ba1\u5212\u6210\u7a7a\uff1f \u3000\u3000\u56e0\u4e4b\uff0c\u4ed6\u5fc5\u987b\u5148\u7a33\u4f4f\u970d\u5143\u4f3d\uff0c\u4e0d\u6562\u64cd\u4e4b\u8fc7\u6025\uff0c\u563f\u563f\u7b11\u4e86\u51e0\u58f0\u9053\uff1a\u201c\u597d\u5427\uff01\u8001\u592b\u5c31\u7b54\u5e94\u4f60 \u7684\u8981\u6c42\uff0c\u8ba9\u90a3\u8d31\u5a62\u9a6c\u4e0a\u8fc7\u6765\uff01\u201d \u3000\u3000\u201c\u9634\u624b\u8001\u82f1\u96c4\u8bf4\u8bdd\u53ef\u8981\u7b97\u8bdd\uff1f\u201d \u3000\u3000\u201c\u541b\u5b50\u4e00\u8a00\uff0c\u9a77\u9a6c\u96be\u8ffd\uff0e\u201d \u3000\u3000\u970d\u5143\u4f3d\u8fd9\u624d\u677e\u5f00\u6293\u4f4f\u82d7\u7d20\u5170\u7684\u624b\u3002 \u3000\u3000\u82d7\u7d20\u5170\u65e0\u9650\u6028\u5fff\u7684\u6012\u89c6\u4e86\u970d\u5143\u4f3d\u2014\u773c\u9053\uff1a\u201c\u539f\u6765\u4f60\u662f\u8fd9\u6837\u7684\u65e0\u60c5\u65e0\u4e49\uff0c\u8fde\u81ea\u5df1\u7684\u59bb\u5b50\u4e5f \u80af\u4ea4\u7ed9\u522b\u4eba\u6740\u5bb3\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u6b49\u7136\u966a\u7b11\u9053\uff1a\u201c\u4e3a\u4e86\u633d\u6551\u5927\u5c40\uff0c\u592b\u4eba\u7eb5\u7136\u4e00\u6b7b\uff0c\u4e5f\u662f\u5176\u6b7b\u91cd\u4e8e\u6cf0\u5c71\uff0e\u8fd8\u671b\u592b\u4eba\u6210 \u5168\uff01\u201d \u3000\u3000\u53ea\u542c\u9634\u624b\u2014\u9b54\u51b7\u7b11\u9053\uff1a\u201c\u970d\u5143\u4f3d\uff0c\u539f\u6765\u4f60\u548c\u8fd9\u8d31\u5a62\u2026\u2026\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u4e0d\u9519\uff01\u82d7\u59d1\u5a18\u5df2\u7136\u5728\u534a\u6708\u524d\u505a\u4e86\u970d\u67d0\u7684\u538b\u5be8\u592b\u4eba\uff0c\u4f60\u7684\u5f1f\u5b50\u80fd\u6df7\u5230\u5929\u4e0b\u6b66 \u6797\u603b\u5be8\u7b2c\u4e00\u592b\u4eba\u7684\u8eab\u4efd\u5730\u4f4d\uff0c\u603b\u7b97\u4e0d\u66fe\u8fb1\u6ca1\u4e86\u9634\u624b\u8001\u82f1\u96c4\u5427\uff01\u201d \u3000\u3000\u9634\u624b\u2014\u9b54\u54fc\u58f0\u9053\uff1a\u201c\u96be\u602a\u5979\u80cc\u53db\u8001\u592b\u8dd1\u5230\u8ff7\u8e2a\u8c37\u6765\uff0c\u539f\u6765\u662f\u53d7\u4f60\u7684\u5f15\u8bf1\uff01\u201d \u3000\u3000\u201c\u5c0a\u9a7e\u8a00\u91cd\u4e86\uff0c\u5979\u5f53\u5e74\u662f\u968f\u80e1\u524d\u76df\u4e3b\u6765\u7684\u3002\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u5982\u679c\u6709\u80e1\u67cf\u9f84\u5728\uff0c\u54ea\u91cc\u8fd8\u4f1a\u8f6e\u5230\u4f60\uff01\u201d \u3000\u3000\u201c\u4e5f\u8bb8\u8fd9\u5c31\u662f\u6240\u8c13\u7f18\u4efd\u5427\uff01\u201d \u3000\u3000\u201c\u5c11\u8d2b\u5634\uff01\u5feb\u53eb\u5979\u8fc7\u6765\u3002\u201d \u3000\u3000\u53ea\u89c1\u82d7\u7d20\u5170\u8138\u8272\u4e00\u7247\u8083\u7a46\uff0e\u9ad8\u53eb\u9053\uff1a\u201c\u73b0\u5728\u5f1f\u5b50\u53ea\u8981\u6c42\u4e00\u4ef6\u4e8b\uff0c\u5e0c\u671b\u4f60\u8001\u4eba\u5bb6\u8a00\u800c\u6709 \u4fe1\uff01\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u5012\u4e09\u89d2\u773c\u91cc\u767d\u591a\u9ed1\u5c11\u7684\u773c\u73e0\u659c\u7784\u4e86\u51e0\u7784\u9053\uff1a\u201c\u4f60\u6307\u7684\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u82d7\u7d20\u5170\u9053\uff1a\u201c\u5f1f\u5b50\u5411\u4f60\u62a5\u5230\u540e\uff0c\u4f60\u4e0d\u53ef\u518d\u5bf9\u8fd9\u91cc\u7684\u4efb\u4f55\u2014\u4eba\u4e0b\u624b\u3002\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u6253\u4e2a\u54c8\u54c8\u9053\uff1a\u201c\u8fd9\u8bdd\u8001\u592b\u65b9\u624d\u5df2\u7ecf\u7b54\u5e94\u8fc7\u970d\u5143\u4f3d\uff0c\u4f60\u7684\u8033\u6735\u54ea\u91cc\u53bb\u4e86\uff1f\u2019 \u3000\u3000\u82d7\u7d20\u5170\u7565\u4e00\u72b9\u8c6b\uff0c\u7acb\u5373\u4e3e\u6b65\u5411\u9634\u624b\u4e00\u9b54\u8eab\u524d\u8d70\u53bb\u3002 \u3000\u3000\u9634\u624b\u4e00\u9b54\u8138\u4e0a\u770b\u4e0d\u51fa\u4efb\u4f55\u8868\u60c5\uff0c\u7a81\u7136\uff0c\u63a2\u624b\u6293\u4f4f\u82d7\u7d20\u5170\u7684\u7693\u8155\u9053\uff1a\u201c\u8d31\u5a62\uff0c\u4f60\u662f\u60f3\u6d3b\u8fd8 \u662f\u60f3\u6b7b\uff1f\u201d \u3000\u3000\u8fd9\u65f6\u7684\u82d7\u7d20\u5170\u53cd\u800c\u663e\u5f97\u5341\u5206\u9547\u5b9a\uff0c\u5927\u6709\u89c6\u6b7b\u5982\u5f52\u4e4b\u6168\uff0c\u6b63\u8272\u9053\uff1a\u201c\u5f1f\u5b50\u4e00\u4eba\u80fd\u6362\u5f97\u5927\u5385 \u5185\u6240\u6709\u4eba\u547d\uff0c\u6b7b\u4e86\u4e5f\u503c\u5f97\uff01\u201d \u3000\u3000\u9634\u624b\u2014\u9b54\u53ea\u6c14\u5f97\u8138\u8272\u53d1\u9752\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u60f3\u7684\u5012\u4e0d\u9519\uff0c\u8001\u592b\u8981\u628a\u4f60\u5e26\u56de\u4e5d\u5929\u7384\u9634\u6d1e\u5e9c\u5189\u6162 \u6162\u5904\u7f6e\uff0c\u8ba9\u4f60\u597d\u597d\u5c1d\u5c1d\u6d3b\u4e0d\u4e86\u6b7b\u4e0d\u6210\u7684\u6ecb\u5473\uff01\u201d \u3000\u3000\u8bf4\u8bdd\u95f4\u4e3e\u624b\u4fbf\u70b9\u4e86\u82d7\u7d20\u5170\u7684\u7a74\u9053\uff0c\u7136\u540e\u628a\u4eba\u6454\u51fa\u5927\u95e8\u5916\u7684\u5730\u4e0a\u3002 \u3000\u3000\u53ea\u542c\u970d\u5143\u4f3d\u9ad8\u53eb\u9053\uff1a\u201c\u9634\u624b\u8001\u82f1\u96c4\u65e2\u7136\u5df2\u7ecf\u627f\u8bfa\u4e0d\u4f24\u5bb3\u5927\u5385\u5185\u6240\u6709\u7684\u4eba\uff0e\u73b0\u5728\u5c31\u8be5\u9000\u51fa \u8ff7\u8e2a\u8c37\u53bb\uff01\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u51b7\u68ee\u68ee\u7b11\u9053\uff1a\u201c\u8001\u592b\u867d\u7136\u7b54\u5e94\u4e0d\u51fa\u624b\u4f24\u4eba\uff0c\u4f46\u6bd2\u706b\u6210\u5144\u548c\u4ed6\u4eec\u53e6\u5916\u4e09\u4f4d\u5374\u53e3\u53e3 \u58f0\u58f0\u4e0d\u653e\u8fc7\u4f60\u4eec\uff0c\u8fd9\u5c31\u4e0d\u662f\u8001\u592b\u80fd\u7ba1\u7684\u4e86\uff01\u201d \u3000\u3000\u970d\u5143\u5496\u5fc3\u5934\u4e00\u9707\u9053\uff1a\u201c\u8ff7\u8e2a\u8c37\u548c\u4ed6\u4eec\u56db\u4f4d\u5f80\u65e5\u65e0\u6028\uff0c\u8fd1\u65e5\u65e0\u4ec7\uff0c\u4ed6\u4eec\u6709\u4ec0\u4e48\u7406\u7531\u548c\u8ff7\u8e2a \u8c37\u505a\u5bf9\uff1f\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u7b11\u9053\uff1a\u201c\u970d\u76df\u4e3b\u8bf4\u5f97\u592a\u8f7b\u677e\u4e86\uff0c\u4eca\u5929\u4e0a\u5348\u662f\u8c01\u628a\u4ed6\u4eec\u6253\u8fdb\u6c34\u7262\uff1f\u4ed6\u4eec\u53c8\u662f\u600e\u6837 \u4e2d\u6bd2\u7684\uff1f\u82e5\u4e0d\u662f\u8001\u592b\u6765\u5f97\u5de7\u6551\u4e86\u4ed6\u4eec\uff0e\u4ed6\u4eec\u8fd8\u80fd\u6d3b\u547d\u5417\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u4ed6\u4eec\u65e0\u7f18\u65e0\u6545\u8fdb\u5165\u8ff7\u8e2a\u8c37\u6572\u8bc8\u52d2\u7d22\uff0c\u53c8\u5728\u4f17\u76ee\u777d\u777d\u4e4b\u4e0b\uff0c\u5bf9\u5973\u4eba\u516c\u7136\u7325 \u4eb5\uff0e\u6b63\u662f\u548e\u7531\u81ea\u53d6\uff0c\u600e\u80fd\u6028\u5f97\u970d\u67d0\uff1f\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u987f\u4e86\u987f\u9053\uff1a\u201c\u8fd9\u662f\u4f60\u4eec\u53cc\u65b9\u7684\u4e8b\uff0c\u5fc5\u987b\u7531\u4f60\u4eec\u81ea\u5df1\u89e3\u51b3\u3002\u201d \u3000\u3000\u8c01\u77e5\u4ed6\u8bf4\u5b8c\u8bdd\uff0c\u5374\u8f6c\u5934\u6012\u58f0\u9053\uff1a\u201c\u6210\u5144\uff0c\u8fd9\u5c31\u662f\u4f60\u7684\u4e0d\u5bf9\u4e86\uff01\u201d \u3000\u3000\u6bd2\u706b\u6210\u5168\u4e00\u6123\u9053\uff1a\u201c\u6210\u67d0\u6709\u4ec0\u4e48\u4e0d\u597d\uff1f\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u9053\uff1a\u201c\u4f60\u4e0d\u8be5\u5bf9\u8001\u592b\u7684\u5973\u5f92\uff0c\u505a\u51fa\u975e\u793c\u7684\u4e3e\u52a8\uff0c\u597d\u6b79\u4f60\u4e5f\u662f\u5979\u7684\u957f\u8f88\uff0c\u884c\u4e8b\u600e \u53ef\u5982\u6b64\u6ca1\u6709\u5206\u5bf8\uff1f\u201d \u3000\u3000\u6bd2\u706b\u6210\u5168\u9053\uff1a\u201c\u9634\u624b\u5144\u4ee5\u4e3a\u90a3\u5973\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u624b\u6307\u95e8\u5916\u5730\u4e0a\u7684\u82d7\u7d20\u5170\u9053\uff1a\u201c\u9664\u4e86\u8001\u592b\u8fd9\u540d\u53db\u5e08\u706d\u7956\u7684\u5973\u5f92\u8fd8\u6709\u8c01\uff1f\u201d \u3000\u3000\u6bd2\u706b\u6210\u5168\u563b\u563b\u7b11\u9053\uff1a\u201c\u539f\u6765\u9634\u624b\u5144\u8bef\u4f1a\u5566\uff01\u6210\u67d0\u6478\u8fc7\u7684\u90a3\u5973\u4eba\u5e76\u975e\u8d35\u5973\u5f92\uff0e\u201d \u3000\u3000\u201c\u4f60\u8bf4\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u662f\u53e6\u5916\u4e00\u4e2a\u5973\u4eba\uff0c\u636e\u8bf4\u662f\u4ece\u4e1c\u6d77\u6765\u7684\uff0e\u53eb\u8bb8\u4ed9\u5b50\u3002\u201d \u3000\u3000\u201c\u80fd\u8131\u5149\u8863\u670d\u5f53\u4f17\u7ed9\u4eba\u6478\uff0c\u8fd9\u79cd\u65e0\u803b\u7684\u5973\u4eba\uff0c\u8fde\u7a91\u59d0\u513f\u90fd\u4e0d\u5982\uff0c\u8fd8\u79f0\u7684\u4ec0\u4e48\u4ed9\u5b50\uff1f\u201d \u3000\u3000\u201c\u4e0d\u7ba1\u5979\u591f\u4e0d\u591f\u683c\u79f0\u4ed9\u5b50\uff0c\u53cd\u6b63\u90a3\u5973\u4eba\u5bf9\u7537\u4eba\u5f88\u6709\u4e00\u5957\u3002\u201d \u3000\u3000\u201c\u6210\u67d0\u786e\u5b9e\u5bf9\u5979\u9887\u6709\u597d\u611f\uff0c\u6240\u4ee5\u2026\u2026\u201d \u3000\u3000\u201c\u6240\u4ee5\u8981\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u201c\u6210\u67d0\u51b3\u5b9a\u5148\u628a\u90a3\u5973\u4eba\u5f04\u5230\u624b\u540e\uff0c\u518d\u5904\u6b7b\u970d\u5143\u4f3d\u8fd9\u4e00\u4f19\u4eba\u3002\u201d \u3000\u3000\u201c\u597d\u5427\uff01\u4f60\u5c31\u5148\u8ba9\u970d\u5143\u4f3d\u628a\u90a3\u5973\u4eba\u732e\u51fa\u6765\uff0c\u4e5f\u8ba9\u8001\u592b\u770b\u770b\u5979\u5230\u5e95\u597d\u5728\u54ea\u91cc\u3002\u201d \u3000\u3000\u6bd2\u706b\u6210\u5168\u89c6\u7ebf\u8f6c\u5411\u970d\u5143\u4f3d\u9053\uff1a\u201c\u59d3\u970d\u7684\uff0c\u8bb8\u4ed9\u5b50\u54ea\u91cc\u53bb\u4e86\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u51b7\u51b7\u4e00\u7b11\u9053\uff1a\u201c\u970d\u67d0\u5df2\u628a\u90a3\u5973\u4eba\u85cf\u5728\u4e00\u95f4\u5bc6\u5ba4\u91cc\u3002\u201d \u3000\u3000\u201c\u73b0\u5728\u8001\u592b\u547d\u4ee4\u4f60\u628a\u5979\u732e\u51fa\u6765\uff01\u201d \u3000\u3000\u201c\u628a\u5979\u4ea4\u51fa\u6765\u53ef\u4ee5\uff0c\u4f46\u4f60\u5fc5\u987b\u5148\u653e\u970d\u67d0\u51fa\u53bb\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u90a3\u5bc6\u5ba4\u7684\u5f00\u542f\u4e4b\u6cd5\uff0c\u53ea\u6709\u970d\u67d0\u2014\u4eba\u77e5\u9053\uff0c\u82e5\u4e0d\u653e\u970d\u67d0\u51fa\u53bb\uff0c\u90a3\u5973\u4eba\u5c31\u51fa\u4e0d\u6765\u3002\u201d \u3000\u3000\u6bd2\u706b\u6210\u5168\u5c82\u80af\u4e0a\u5f53\uff0c\u563f\u563f\u7b11\u9053\uff1a\u201c\u90a3\u5c31\u4e0d\u59a8\u6682\u7b49\u4e00\u4e0b\uff0c\u8001\u592b\u73b0\u5728\u8981\u4f60\u5148\u4ea4\u51fa\u7b2c\u4e8c\u4e2a\u4eba \u6765\uff0e\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u5c0a\u9a7e\u60f3\u8981\u7684\u7b2c\u4e8c\u4e2a\u4eba\u53c8\u662f\u8c01\uff1f\u201d \u3000\u3000\u6bd2\u706b\u6210\u5168\u9053\uff1a\u201c\u90a3\u4e2a\u6253\u8fc7\u8001\u592b\u4eec\u56db\u4eba\u7684\u5c0f\u5b50\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u5f53\u7136\u77e5\u9053\u6bd2\u706b\u6210\u5168\u6307\u7684\u662f\u9ea6\u5c0f\u660e\uff0e \u3000\u3000\u4ed6\u672c\u6765\u65e9\u5df2\u628a\u9ea6\u5c0f\u660e\u89c6\u4e3a\u773c\u4e2d\u4e4b\u9489\uff0c\u65e9\u5c31\u60f3\u8bbe\u6cd5\u628a\u4ed6\u9664\u53bb\uff0c\u4f46\u53c8\u987e\u8651\u5230\u949f\u4e00\u8c6a\u4e00\u4f19\u4eba\u56e0 \u800c\u53db\u79bb\u3002\u82e5\u949f\u4e00\u8c6a\u4e00\u4f19\u4eba\u660e\u76ee\u5f20\u80c6\u7684\u8d77\u800c\u4f5c\u5bf9\uff0c\u81ea\u5df1\u8fd9\u76df\u4e3b\u5b9d\u5ea7\u8bf4\u4e0d\u5b9a\u5c31\u96be\u4ee5\u4fdd\u5168\uff0e\u5728\u8fd9\u79cd \u60c5\u5f62\u4e0b\uff0c\u4ed6\u5fc5\u987b\u5728\u8868\u9762\u4e0a\u52a0\u4ee5\u7b3c\u7edc\uff0c\u540c\u65f6\u66f4\u5e0c\u671b\u80fd\u628a\u9ea6\u5c0f\u660e\u6536\u5356\u8fc7\u6765\u4ee5\u5f52\u5df1\u7528\u3002 \u3000\u3000\u970d\u5143\u4f3d\u5fc3\u5ff5\u53ca\u6b64\uff0c\u968f\u5373\u6447\u4e86\u6447\u5934\u7b11\u9053\uff1a\u201c\u5c0a\u9a7e\u8981\u7684\u90a3\u4f4d\u8001\u5f1f\u53eb\u9ea6\u5c0f\u660e\uff0c\u53ef\u60dc\u4ed6\u5e76\u4e0d\u5728\u5927 \u5385\u4e4b\u5185\u3002\u201d \u3000\u3000\u4ed6\u7684\u8fd9\u51e0\u53e5\u8bdd\uff0c\u7fa4\u96c4\u4eec\u8c01\u90fd\u53ef\u4ee5\u542c\u5f97\u51fa\uff0e\u4e0d\u5916\u662f\u8981\u4fdd\u5168\u9ea6\u5c0f\u660e\uff0e\u56e0\u4e3a\u7fa4\u96c4\u4eec\u4e0d\u4e0b\u516b\u4e5d\u5341 \u4eba\u4e4b\u591a\uff0c\u6b64\u65f6\u90fd\u7d27\u9760\u5728\u5927\u5385\u4e00\u89d2\uff0c\u9ea6\u5c0f\u660e\u53c8\u7ad9\u5728\u4eba\u7fa4\u540e\u5385\uff0c\u7ad9\u5728\u5927\u95e8\u53e3\u7684\u6bd2\u706b\u6210\u5168\u7b49\u4eba\uff0c\u6839 \u672c\u65e0\u4ece\u53d1\u73b0\u3002 \u3000\u3000\u66f4\u52a0\u7fa4\u96c4\u4eec\u4e0d\u5c11\u8eab\u8eaf\u9ad8\u5927\u7684\u5728\u95fb\u53ca\u970d\u5143\u4f3d\u4e4b\u8a00\u540e\uff0e\u90fd\u6545\u610f\u62a2\u7740\u6321\u5728\u9ea6\u5c0f\u660e\u524d\u65b9\uff0c\u8fd9\u6837\u2014 \u6765\uff0c\u6bd2\u706b\u6210\u5168\u5c31\u66f4\u96be\u770b\u5230\u9ea6\u5c0f\u660e\u5728\u90a3\u91cc\u3002 \u3000\u3000\u6bd2\u706b\u6210\u5168\u5411\u4eba\u7fa4\u4e2d\u8fde\u7784\u4e86\u51e0\u7784\u9053\uff1a\u201c\u90a3\u5c0f\u5b50\u4eba\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u970d\u67d0\u56e0\u4ed6\u4e0a\u5348\u5f97\u7f6a\u4e86\u4f60\u4eec\u56db\u4f4d\uff0e\u6240\u4ee5\u4e0d\u51c6\u4ed6\u6765\u53c2\u52a0\u5bb4\u4f1a\uff0e\u81f3\u4e8e\u4ed6\u73b0\u5728\u4eba\u5728 \u54ea\u91cc\uff0c\u5927\u6168\u8fd8\u4e0d\u81f3\u4e8e\u79bb\u5f00\u8ff7\u8e2a\u8c37\u5427\u3002\u201d \u3000\u3000\u6bd2\u706b\u6210\u5168\u54ac\u7259\u5207\u9f7f\u9053\uff1a\u201c\u5f85\u4f1a\u513f\u8001\u592b\u6349\u5230\u4ed6\u540e\uff0c\u5b9a\u8981\u628a\u4ed6\u788e\u5c38\u4e07\u6bb5\uff01\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u9ed1\u9b54\u65f6\u5bc5\u548c\u725b\u5954\u867d\u672a\u5f00\u53e3\u8bf4\u8bdd\uff0c\u5374\u90fd\u5df2\u6c14\u5f97\u5589\u7ba1\u91cc\u54af\u54af\u4f5c\u54cd\u3002 \u3000\u3000\u9634\u624b\u4e00\u9b54\u76b1\u4e86\u76b1\u7709\u9053\uff1a\u201c\u6210\u5144\u597d\u50cf\u5bf9\u90a3\u5c0f\u5b50\u6068\u5f97\u4e0d\u5f97\u4e86\u3002\u201d \u3000\u3000\u6bd2\u706b\u6210\u5168\u54ac\u7259\u6068\u58f0\u9053\uff1a\u201c\u9634\u624b\u5144\u6709\u6240\u4e0d\u77e5\uff0c\u90a3\u5c0f\u5b50\u628a\u6211\u4eec\u56db\u4eba\u6253\u4e86\u4e2a\u901a\u5173\uff0e\u6210\u67d0\u8fd9\u9762\u988a \u5230\u73b0\u5728\u8fd8\u6ca1\u5b8c\u5168\u6d88\u80bf\uff0c\u4f60\u8bf4\u54b1\u4eec\u5728\u6c5f\u6e56\u6df7\u4e86\u5927\u534a\u8f88\u5b50\uff0c\u4ec0\u4e48\u65f6\u5019\u683d\u8fc7\u8fd9\u79cd\u8ddf\u5934\uff1f\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u56de\u9996\u77a5\u4e86\u9ed1\u9b54\u65f6\u5bc5\u4e00\u773c\u9053\uff1a\u201c\u65f6\u5144\u4e5f\u6ca1\u5e78\u514d\u5427\uff1f\u201d \u3000\u3000\u9ed1\u9b54\u65f6\u5bc5\u54ac\u5f97\u7259\u9f7f\u201c\u683c\u5d29\u683c\u5d29\u201d\u4f5c\u54cd\uff0c\u543c\u9053\uff1a\u201c\u4ed6\u5976\u5976\u7684\uff0c\u6211\u4eec\u56db\u4e2a\u54ea\u4e2a\u4e0d\u662f\u88ab\u90a3\u5c0f\u5b50 \u6253\u5f97\u6eda\u7684\u6eda\u3001\u722c\u7684\u722c\uff0c\u7b80\u76f4\u662f\u738b\u516b\u5543\u897f\u74dc\uff01\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u90fd\u662f\u9f0e\u9f0e\u5927\u540d\u7684\u4e86\u4e0d\u8d77\u4eba\u7269\uff0c\u600e\u4f1a\u9634\u6c9f\u91cc\u7ffb\u8239\u683d\u5728\u4e00\u4e2a\u540e\u751f\u665a\u8f88 \u624b\u91cc\u5462\uff1f\u201d \u3000\u3000\u9ed1\u9b54\u65f6\u5bc5\u54a7\u4e86\u54a7\u5634\u9053\uff1a\u201c\u90a3\u65f6\u6211\u4eec\u5df2\u7ecf\u4e2d\u4e86\u6bd2\uff0c\u624b\u811a\u96be\u4ee5\u52a8\u5f39\uff0c\u9664\u4e86\u6328\u6253\uff0c\u54ea\u91cc\u6765\u7684\u8fd8 \u624b\u4e4b\u529b\uff1f\u201d \u3000\u3000\u9634\u624b\u2014\u9b54\u9894\u9996\u9053\uff1a\u201c\u597d\uff01\u201d \u3000\u3000\u9ed1\u9b54\u65f6\u5bc5\u6123\u4e86\u6123\u9053\uff1a\u201c\u6211\u4eec\u88ab\u6253\u4e86\uff01\u4f60\u8fd8\u8bf4\u597d\uff1f\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u9053\uff1a\u201c\u8001\u592b\u8fd9\u597d\u7684\u610f\u601d\uff0c\u662f\u8bf4\u90a3\u5c0f\u5b50\u5b9e\u5728\u592a\u53ef\u6076\uff0c\u65f6\u5144\u5343\u4e07\u522b\u8bef\u4f1a\u3002\u201d \u3000\u3000\u9ed1\u9b54\u65f6\u5bc5\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u83ab\u975e\u9634\u624b\u5144\u4e5f\u8ba4\u8bc6\u90a3\u5c0f\u5b50\uff1f\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u54fc\u4e86\u58f0\u9053\uff1a\u201c\u8001\u592b\u8ba4\u8bc6\u4ed6\uff0c\u5728\u4f60\u4eec\u4e4b\u5148\uff0e\u90a3\u5c0f\u5b50\u4e0d\u4f46\u62db\u60f9\u8fc7\u8001\u592b\uff0c\u8fd8\u628a\u8001\u592b\u7684 \u5973\u5f1f\u5b50\u4e5f\u5e26\u8d70\u4e00\u4e2a\uff0c\u4f60\u4eec\u8bf4\u8fd9\u5c0f\u5b50\u53ef\u4e0d\u53ef\u6076\uff1f\u201d \u5367\u9f99\u751f\u300a\u5999\u7edd\u5929\u9999\u300b \u7b2c\u5341\u4e00\u56de\u3000\u72ec\u9000\u7fa4\u9b54 \u3000\u3000\u8fd9\u51e0\u53e5\u8bdd\uff0c\u53ea\u542c\u5f97\u970d\u5143\u4f3d\u65b9\u9762\u7684\u4eba\u5927\u611f\u832b\u7136\uff0c\u56e0\u4e3a\u4ed6\u4eec\u6839\u672c\u4e0d\u6e05\u695a\u9ea6\u5c0f\u660e\u6700\u8fd1\u66fe\u53bb\u8fc7\u5415 \u6881\u5c71\u7684\u4e8b\u3002 \u3000\u3000\u5176\u5b9e\u9634\u624b\u4e00\u9b54\u5e76\u975e\u65e0\u7f18\u65e0\u6545\u524d\u6765\u8c1c\u8e2a\u8c37\u7684\uff0c\u4ed6\u7684\u76ee\u7684\uff0c\u4e0d\u5916\u662f\u628a\u82d7\u7d20\u82d3\u6349\u56de\u53bb\u6cbb\u7f6a\uff0c\u5f53 \u7136\uff0c\u80fd\u628a\u82d7\u7d20\u5170\u4e5f\u5e26\u56de\u53bb\u90a3\u5c31\u66f4\u597d\u3002 \u3000\u3000\u6bd2\u706b\u6210\u5168\u7b49\u5f97\u5df2\u6709\u4e9b\u4e0d\u8010\uff0c\u543c\u9053\uff1a\u201c\u59d3\u970d\u7684\uff0e\u8fd8\u4e0d\u628a\u90a3\u5c0f\u5b50\u4ea4\u51fa\u6765\uff1f\u201d \u3000\u3000\u970d\u5143\u5496\u9053\uff1a\u201c\u4ed6\u4e0d\u5728\u8fd9\u91cc\uff0c\u4f60\u8ba9\u970d\u67d0\u600e\u4e48\u4ea4\uff0c\u9664\u975e\u4f60\u80fd\u51c6\u8bb8\u970d\u67d0\u5e26\u51e0\u4e2a\u4eba\u51fa\u53bb\u628a\u4ed6\u627e\u56de \u6765\u3002\u201d \u3000\u3000\u6bd2\u706b\u6210\u5168\u7565\u4e00\u72b9\u8c6b\u9053\uff1a\u201c\u9634\u624b\u5144\uff0c\u4f60\u770b\u8981\u4e0d\u8981\u653e\u4ed6\u51fa\u53bb\u627e\u4eba\uff1f\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u51b7\u7b11\u9053\uff1a\u201c\u8001\u592b\u770b\u4f60\u4f3c\u4e4e\u8d8a\u6765\u8d8a\u7cca\u6d82\u4e86\uff0c\u628a\u970d\u5143\u4f3d\u653e\u5c71\u53bb\uff0c\u4f60\u4eec\u5c31\u53c8\u79bb\u5012\u9709\u4e0d \u8fdc\u4e86\uff01\u201d \u3000\u3000\u201c\u4f60\u8bf4\u8be5\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u201c\u59d3\u9ea6\u7684\u90a3\u5c0f\u5b50\u5fc5\u5728\u5927\u5385\u91cc\u3002\u201d \u3000\u3000\u201c\u4f60\u600e\u4e48\u77e5\u9053\uff1f\u201d \u3000\u3000\u201c\u90a3\u5c0f\u5b50\u5e74\u7eaa\u867d\u5c0f\uff0c\u6b66\u529f\u5374\u9ad8\u5f97\u51fa\u5947\uff0c\u970d\u5143\u4f3d\u8bbe\u7b75\u62db\u5f85\u4f17\u5934\u76ee\uff0c\u600e\u4f1a\u6ca1\u6709\u4ed6\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u54b1\u4eec\u82e5\u8fdb\u53bb\u67e5\u770b\uff0c\u5f88\u53ef\u80fd\u5403\u4ed6\u4eec\u7684\u4e8f\u3002\u201d \u3000\u3000\u201c\u6210\u5144\uff0c\u6211\u770b\u4f60\u662f\u8d8a\u6d3b\u8d8a\u56de\u53bb\u5566\uff0c\u96be\u9053\u6d3b\u4eba\u4f1a\u7ed9\u5c3f\u618b\u6b7b\uff1f\u201d \u3000\u3000\u201c\u4f60\u6709\u4ec0\u4e48\u9ad8\u660e\u529e\u6cd5\uff1f\u201d \u3000\u3000\u201c\u8ba9\u4ed6\u4eec\u8fd9\u51e0\u5341\u4eba\u4e00\u4e2a\u4e2a\u7531\u5927\u5385\u897f\u8fb9\u8d70\u5230\u4e1c\u8fb9\uff0c\u8fd8\u4f1a\u67e5\u4e0d\u51fa\u90a3\u5c0f\u5b50\uff1f\u201d \u3000\u3000\u201c\u597d\uff0c\u5c31\u8fd9\u4e48\u529e\u3002\u201d \u3000\u3000\u8bf4\u7740\u51b7\u58f0\u9ad8\u53eb\u9053\uff1a\u201c\u970d\u5143\u4f3d\uff0c\u9634\u624b\u5144\u7684\u8bdd\uff0c\u542c\u5230\u6ca1\u6709\uff1f\u73b0\u5728\u9a6c\u4e0a\u8981\u4ed6\u4eec\u4e00\u4e2a\u4e2a\u8d70\u5230\u53e6\u4e00 \u5934\u3002\u54ea\u2014\u4e2a\u82e5\u6562\u4e0d\u9075\uff0c\u8001\u5927\u53cc\u624b\u7684\u6697\u5668\uff0c\u7acb\u523b\u5c31\u53d1\u5c04\u51fa\u53bb\uff0c\u5230\u90a3\u65f6\u522b\u57cb\u6028\u8001\u592b\u5fc3\u72e0\u624b\u8fa3\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u6b63\u72b9\u8c6b\u95f4\uff0c\u53ea\u542c\u7fa4\u96c4\u4e2d\u4e00\u4eba\u90ce\u58f0\u9ad8\u53eb\u9053\uff1a\u201c\u7528\u4e0d\u7740\u90a3\u4e48\u9ebb\u70e6\uff0c\u5728\u4e0b\u73b0\u5728\u5c31\u51fa\u6765\u7ed9 \u4f60\u4eec\u77bb\u4ef0\u77bb\u4ef0\uff01\u201d \u3000\u3000\u8bf4\u8bdd\u7684\u6b63\u662f\u9ea6\u5c0f\u660e\u3002 \u3000\u3000\u4ed6\u4eba\u968f\u58f0\u5230\uff0c\u7531\u4eba\u4e1b\u4e2d\u4e00\u63a0\u800c\u51fa\uff0c\u6602\u7136\u6765\u5230\u9634\u624b\u4e00\u9b54\u548c\u6bd2\u706b\u6210\u5168\u8eab\u524d\u3002 \u3000\u3000\u4ed6\u8fd9\u4e3e\u52a8\uff0c\u5f53\u771f\u5927\u51fa\u970d\u5143\u4f3d\u548c\u7fa4\u96c4\u610f\u5916\uff0c\u6240\u6709\u7684\u4eba\u5168\u4e3a\u4ed6\u5927\u5927\u634f\u4e00\u628a\u6c57\uff0c\u540c\u65f6\u4e5f\u83ab\u4e0d\u5bf9 \u4ed6\u8fd9\u4efd\u80c6\u6c14\u4e3a\u4e4b\u5fc3\u670d\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u5728\u9634\u624b\u2014\u9b54\u548c\u6bd2\u706b\u6210\u5168\u8eab\u524d\u4e03\u5c3a\u5916\u505c\u4e0b\u811a\u6b65\uff0c\u50b2\u7136\u8bf4\u9053\uff1a\u201c\u5728\u4e0b\u6765\u5566\uff0c\u4f60\u4eec\u8981\u600e \u4e48\u6837\uff1f\u770b\u7740\u529e\u5427\uff01\u201d \u3000\u3000\u4ed6\u8fd9\u50b2\u7136\u4e0d\u60e7\u7684\u4e3e\u52a8\uff0c\u7167\u6837\u4e5f\u4f7f\u5f97\u9634\u624b\u4e00\u9b54\u548c\u6bd2\u706b\u6210\u5168\u5927\u611f\u610f\u5916\uff0e \u3000\u3000\u6bd2\u706b\u6210\u5168\u65e9\u5df2\u628a\u9ea6\u5c0f\u660e\u6068\u5f97\u7259\u6839\u53d1\u75d2\uff0c\u521a\u8981\u6254\u51fa\u6697\u5668\uff0c\u5374\u88ab\u9634\u624b\u4e00\u9b54\u4e00\u628a\u6293\u4f4f\u624b\u81c2\u9053\uff1a \u201c\u522b\u5fd9\uff0c\u53cd\u6b63\u4ed6\u5df2\u662f\u6b7b\u8def\u4e00\u6761\uff0c\u8001\u592b\u8fd8\u6709\u8bdd\u95ee\u4ed6\u3002\u201d \u3000\u3000\u6bd2\u706b\u6210\u5168\u53ea\u597d\u5f3a\u5fcd\u6124\u6012\uff0c\u6682\u65f6\u7f29\u56de\u53bb\u3002 \u3000\u3000\u9634\u624b\u4e00\u9b54\u53cc\u76ee\u53d1\u5c04\u7740\u51f6\u5149\u9053\uff1a\u201c\u5c0f\u5b50\uff0c\u4f60\u628a\u82d7\u7d20\u82d3\u90a3\u8d31\u5a62\u5e26\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u6c14\u5b9a\u795e\u95f2\uff0c\u51b7\u51b7\u4e00\u7b11\u9053\uff1a\u201c\u5979\u6ca1\u6765\u8ff7\u8e2a\u8c37\uff0e\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u8f6c\u52a8\u7740\u767d\u591a\u9ed1\u5c11\u7684\u773c\u73e0\u9053\uff1a\u201c\u5979\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u5728\u4e0b\u628a\u5979\u6682\u653e\u5728\u4e00\u5904\u6c11\u5bb6\u91cc\uff0e\u201d \u3000\u3000\u201c\u90a3\u6c11\u5bb6\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u201c\u73b0\u5728\u4e0d\u80fd\u544a\u8bc9\u4f60\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u4e0d\u80af\u8bb2\uff1f\u201d \u3000\u3000\u201c\u6211\u6709\u6761\u4ef6\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u6761\u4ef6\uff1f\u201d \u3000\u3000\u201c\u4f60\u628a\u6240\u6709\u7684\u4eba\u90fd\u653e\u51fa\u5927\u5385\u6211\u518d\u8bb2\u3002\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u987f\u65f6\u8138\u4e0a\u9752\u7b4b\u66b4\u8d77\uff0c\u543c\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff01\u6b7b\u5230\u4e34\u5934\uff0c\u8fd8\u8ddf\u8001\u592b\u8c08\u6761\u4ef6\uff0c\u4f60\u2026\u2026\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u6de1\u6de1\u4e00\u7b11\uff1a\u201c\u8981\u6740\u8981\u5250\uff0c\u4efb\u51ed\u4e8e\u4f60\u3002\u4e0d\u8fc7\u4f60\u8be5\u660e\u767d\uff0e\u5982\u679c\u5728\u4e0b\u73b0\u5728\u6b7b\u4e86\uff0c\u4f60\u5c31\u6c38 \u8fdc\u522b\u60f3\u5f97\u5230\u82d7\u7d20\u82d3\uff01\u201d \u3000\u3000\u5ffd\u542c\u5b88\u5728\u5927\u5385\u4fa7\u95e8\u7684\u866b\u9b54\u706b\u83bd\u5927\u58f0\u9053\uff1a\u201c\u518d\u4e0d\u5904\u6b7b\u59d3\u9ea6\u7684\u90a3\u5c0f\u5b50\uff0c\u672c\u603b\u5cd2\u4e3b\u5c31\u8981\u653e\u866b \u4e86\uff01\u201d \u3000\u3000\u539f\u6765\uff0e\u866b\u9b54\u706b\u83bd\u89c1\u9634\u624b\u4e00\u9b54\u8fdf\u8fdf\u4e0d\u80af\u52a8\u624b\uff0c\u65e9\u5df2\u7b49\u5f97\u4e0d\u8010\uff0c\u5c24\u5176\u4ed6\u628a\u9ea6\u5c0f\u660e\u6068\u4e4b\u5165\u9aa8\uff0c \u5fc5\u6b32\u4eb2\u624b\u6740\u4e4b\u800c\u540e\u5feb\u3002 \u3000\u3000\u9634\u624b\u4e00\u9b54\u53eb\u9053\uff1a\u201c\u706b\u603b\u5cd2\u4e3b\u8bf7\u5fcd\u8010\u7247\u523b\uff0c\u8001\u592b\u518d\u95ee\u4ed6\u4e09\u4e94\u53e5\u8bdd\u5c31\u5f53\u573a\u628a\u4ed6\u5904\u6b7b\u3002\u201d \u3000\u3000\u5c82\u77e5\u866b\u9b54\u706b\u83bd\u5df2\u662f\u5fcd\u65e0\u53ef\u5fcd\uff0c\u63a2\u624b\u5165\u6000\uff0c\u638f\u51fa\u5e03\u888b\uff0c\u7adf\u628a\u4e09\u5341\u516d\u53ea\u91d1\u8695\u6bd2\u866b\uff0e\u6296\u624b\u5c31\u653e \u4e86\u51fa\u6765\u3002 \u3000\u3000\u90a3\u4e09\u5341\u516d\u53ea\u91d1\u8695\u6bd2\u866b\u8fdb\u5165\u5927\u5385\u4e4b\u540e\uff0c\u76f4\u5411\u4eba\u7fa4\u805a\u96c6\u4e4b\u5904\u98de\u53bb\uff0c\u6602\u9996\u5410\u4fe1\uff0e\u51f6\u6027\u5927\u53d1\uff0c\u5239 \u65f6\u55e1\u55e1\u4e4b\u58f0\u54cd\u5f7b\u6574\u5ea7\u5927\u5385\u3002 \u3000\u3000\u7fa4\u96c4\u4eec\u5728\u8fd9\u5239\u90a3\u83ab\u4e0d\u5927\u60ca\u5931\u8272\uff0c\u7eb7\u7eb7\u4e00\u9762\u8d70\u907f\uff0c\u4e00\u9762\u5288\u51fa\u638c\u529b\u8fce\u51fb\u3002\u4e00\u4e9b\u529f\u529b\u8f83\u5dee\u8d70\u907f \u8f83\u8fdf\u7684\uff0c\u4e0d\u5c11\u4eba\u5df2\u88ab\u91d1\u8695\u866b\u7684\u6bd2\u4fe1\u523a\u4e2a\u6b63\u7740\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u60ca\u547c\u60e8\u53eb\u4e4b\u58f0\u4e0d\u7edd\u4e8e\u8033\u3002 \u3000\u3000\u6bd2\u706b\u6210\u5168\u8d81\u65f6\u4e5f\u7acb\u5373\u5c06\u53f3\u624b\u7684\u6dec\u6bd2\u874e\u5c3e\u9488\u629b\u5c04\u51fa\u53bb\uff0c\u3000\u4e00\u7247\u84dd\u6c6a\u6c6a\u7684\u5149\u8292\uff0c\u88ad\u5411\u9ea6\u5c0f \u660e\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u60c5\u6025\u4e4b\u4e0b\uff0c\u5c31\u5730\u4e00\u4e2a\u7ffb\u6eda\uff0c\u6eda\u5230\u4e00\u5f20\u516b\u4ed9\u684c\u4e0b\uff0e \u3000\u3000\u6bd2\u706b\u6210\u5168\u4e07\u6ca1\u6599\u5230\u4e00\u628a\u874e\u5c3e\u6bd2\u9488\u7adf\u7136\u843d\u7a7a\uff0c\u7d27\u8ddf\u7740\u518d\u628a\u5de6\u624b\u7684\u6dec\u6bd2\u6885\u82b1\u9488\u518d\u63b7\u51fa\u53bb\u3002 \u3000\u3000\u8fd9\u628a\u6dec\u6bd2\u6885\u82b1\u9488\u6709\u4e09\u56db\u5341\u679a\uff0c\u51fa\u624b\u4e4b\u540e\uff0c\u8db3\u53ef\u7b3c\u7f69\u65b9\u5706\u4e03\u516b\u5c3a\u7684\u8303\u56f4\uff0c\u5728\u8fd9\u4e03\u516b\u5c3a\u7684\u65b9 \u5706\u4e4b\u5185\uff0c\u5373\u4f7f\u662f\u4e00\u53ea\u868a\u5b50\uff0c\u4e5f\u96be\u4ee5\u8131\u9003\uff0c\u66f4\u4f55\u51b5\u662f\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u5c82\u77e5\u9ea6\u5c0f\u660e\u6025\u4e2d\u751f\u667a\uff0c\u7adf\u62a2\u5728\u6bd2\u706b\u6210\u5168\u51fa\u624b\u4e4b\u524d\uff0c\u5c06\u90a3\u516b\u4ed9\u684c\u7684\u684c\u9762\u503e\u4fa7\u8fc7\u6765\uff0c\u81ea\u5df1\u5219 \u7f29\u8eab\u5728\u684c\u9762\u4e4b\u540e\u3002\u90a3\u6dec\u6bd2\u6885\u82b1\u9488\u7ec6\u5982\u725b\u6bdb\uff0c\u65e0\u6cd5\u7a7f\u8fc7\u684c\u9762\uff0e\u5f53\u7136\u4e5f\u5c31\u4f24\u4e0d\u5230\u9ea6\u5c0f\u660e\u3002 \u3000\u3000\u6bd2\u706b\u6210\u5168\u6b63\u6b32\u518d\u63a2\u624b\u5165\u6000\u638f\u51fa\u6697\u5668\u65f6\uff0c\u7a81\u542c\u8eab\u540e\u7684\u725b\u5954\u53d1\u51fa\u4e00\u58f0\u95f7\u54fc\uff0c\u63a5\u7740\u81ea\u5df1\u53f3\u81c2\u4e5f \u88ab\u4e00\u7f15\u521a\u52b2\u65e0\u6bd4\u7684\u6307\u98ce\u88ad\u4e2d\uff0c\u4f7f\u5f97\u4ed6\u534a\u8fb9\u8eab\u5b50\u987f\u65f6\u9ebb\u6728\u8d77\u6765\u3002 \u3000\u3000\u4ed6\u54ac\u7259\u54a7\u5634\u53eb\u9053\uff1a\u201c\u9634\u624b\u5144\uff0c\u4f60\u600e\u4e48\u53cd\u800c\u5bf9\u81ea\u5df1\u4eba\u4e0b\u624b\uff1f\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u6123\u4e86\u6123\u9053\uff1a\u201c\u8fd9\u662f\u4ec0\u4e48\u8bdd\uff1f\u8001\u592b\u6839\u672c\u5c31\u5728\u8fd9\u91cc\u6ca1\u52a8\uff0c\u4f60\u600e\u4e48\u5566\uff1f\u201d \u3000\u3000\u6bd2\u706b\u6210\u5168\u6323\u624e\u7740\u56de\u8fc7\u8eab\u53bb\uff0c\u53ea\u89c1\u725b\u5954\u5df2\u8eba\u5728\u5730\u4e0a\u4e0d\u52a8\uff0c\u5206\u660e\u662f\u88ab\u4eba\u70b9\u4e86\u7a74\u9053\uff0c\u4f46\u53c8\u770b\u4e0d \u5230\u70b9\u7a74\u4e4b\u4eba\u7a76\u7adf\u5728\u4f55\u5904\u3002 \u3000\u3000\u540c\u65f6\u4ed6\u4e5f\u660e\u767d\u4e86\u65b9\u624d\u7edd\u975e\u9634\u624b\u2014\u9b54\u6697\u4e2d\u65bd\u88ad\uff0e\u56e0\u4e3a\u9634\u624b\u4e00\u9b54\u660e\u660e\u7ad9\u5728\u4ed6\u548c\u725b\u5954\u8eab\u524d\uff0c\u7eb5 \u7136\u51fa\u624b\uff0e\u4e5f\u7edd\u4e0d\u53ef\u80fd\u88ad\u51fb\u5230\u4e8c\u4eba\u7684\u80cc\u540e\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u9634\u624b\u4e00\u9b54\u4e5f\u56de\u8fc7\u5934\u6765\uff0c\u5403\u4e86\u4e00\u60ca\u9053\uff1a\u201c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d \u3000\u3000\u6bd2\u706b\u6210\u5168\u5fcd\u7740\u75db\u9053\uff1a\u201c\u65b9\u624d\u725b\u4eb2\u738b\u88ab\u4eba\u70b9\u4e86\u7a74\u9053\uff0c\u6210\u67d0\u7684\u53f3\u81c2\u4e5f\u88ab\u4eba\u4ece\u80cc\u540e\u70b9\u4e86\u4e00 \u6307\u3002\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u8bb6\u7136\u9053\uff1a\u201c\u6709\u8fd9\u79cd\u4e8b\uff1f\u5bf9\u65b9\u7684\u4eba\u5462\uff1f\u201d \u3000\u3000\u6bd2\u706b\u6210\u5168\u9053\uff1a\u201c\u5c31\u662f\u56e0\u4e3a\u6ca1\u53d1\u73b0\u6765\u4eba\uff0c\u5144\u5f1f\u4e5f\u83ab\u540d\u5947\u5999\uff0c\u83ab\u975e\u6709\u9b3c\uff1f\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u5927\u5385\u5185\u7684\u7fa4\u96c4\uff0c\u4e5f\u90fd\u5f04\u4e0d\u6e05\u662f\u600e\u4e48\u56de\u4e8b\u3002\u4e0d\u8fc7\u4ed6\u4eec\u5374\u90fd\u4e2a\u4e2a\u6697\u559c\uff0c\u56e0\u4e3a\u6765\u4eba\u5206\u660e \u662f\u5728\u6697\u52a9\u8ff7\u8e2a\u8c37\uff0c\u662f\u4ee5\u5185\u5fc3\u5168\u5347\u8d77\u4e00\u80a1\u6b7b\u91cc\u6c42\u751f\u4e4b\u671b\u3002 \u3000\u3000\u9634\u624b\u2014\u9b54\u7565\u4e00\u6c89\u541f\u9053\uff1a\u201c\u6210\u5144\u8fd0\u6c14\u8bd5\u8bd5\uff0e\u662f\u5426\u8fd8\u6709\u65bd\u653e\u6697\u5668\u4e4b\u80fd\uff1f\u201d \u3000\u3000\u6bd2\u706b\u6210\u5168\u9053\uff1a\u201c\u6210\u67d0\u7684\u5de6\u624b\u52c9\u5f3a\u8fd8\u53ef\u4ee5\uff0e\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u9053\uff1a\u201c\u90a3\u5c31\u7531\u4f60\u5de6\u624b\u7d27\u6263\u6697\u5668\uff0c\u4ecd\u7136\u5b88\u4f4f\u95e8\u53e3\uff0c\u975e\u5230\u5fc5\u8981\uff0c\u6697\u5668\u4e0d\u53ef\u51fa\u624b\uff01\u201d \u3000\u3000\u6bd2\u706b\u6210\u5168\u4f9d\u8a00\u5de6\u624b\u53c8\u63e1\u4e86\u2014\u628a\u6dec\u6bd2\u6885\u82b1\u9488\uff0c\u5728\u8fd9\u79cd\u60c5\u5f62\u4e0b\uff0c\u5373\u4f7f\u9634\u624b\u4e00\u9b54\u4e0d\u4ea4\u4ee3\uff0c\u4ed6\u4e5f \u4e0d\u80af\u8f7b\u6613\u51fa\u624b\u3002 \u3000\u3000\u5426\u5219\uff0c\u5f53\u6697\u5668\u63b7\u51fa\uff0c\u7eb5\u7136\u80fd\u51fb\u6bd9\u5bf9\u65b9\u4e0d\u5c11\u4eba\uff0c\u5176\u4ed6\u7684\u4eba\u4e5f\u5fc5\u4e00\u62e5\u800c\u51fa\uff0c\u5230\u90a3\u65f6\u60c5\u52bf\u5c31\u96be \u4ee5\u63a7\u5236\u4e86\u3002 \u3000\u3000\u4ed6\u4e00\u9762\u76d1\u89c6\u7740\u7fa4\u96c4\u7684\u52a8\u9759\uff0e\u3000\u4e00\u9762\u95ee\u9053\uff1a\u201c\u9634\u624b\u5144\u8981\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u9053\uff1a\u201c\u4f60\u53ea\u7ba1\u5b88\u4f4f\u5927\u95e8\uff0c\u7531\u8001\u592b\u6765\u5bf9\u4ed8\u6765\u4eba\uff01\u201d \u3000\u3000\u6b64\u523b\uff0c\u7fa4\u96c4\u4ecd\u805a\u96c6\u5728\u5927\u5385\u4e00\u89d2\uff0c\u9664\u4e86\u5c11\u6570\u4eba\u4e0d\u4f4f\u53d1\u51fa\u547b\u541f\u4e4b\u58f0\uff0c\u5176\u4f59\u4ecd\u80fd\u4fdd\u6301\u9547\u5b9a\u3002 \u3000\u3000\u90a3\u4e09\u5341\u516d\u53ea\u91d1\u8695\u6bd2\u866b\u5168\u5df2\u88ab\u7fa4\u8c6a\u51fb\u6bd9\uff0c\u5c11\u6570\u88ab\u91d1\u8695\u6bd2\u866b\u54ac\u4e2d\u7684\uff0c\u5168\u9759\u9759\u7684\u8eba\u5728\u5730\u4e0a\uff0c\u6709 \u7684\u54ac\u7259\u5f3a\u5fcd\u75db\u82e6\uff0c\u6709\u7684\u5728\u4e0d\u4f4f\u547b\u541f\uff0c\u5176\u4f59\u672a\u53d7\u4f24\u7684\uff0c\u5219\u84c4\u52bf\u6212\u5907\uff0c\u79e9\u5e8f\u4ecd\u7136\u6781\u4e3a\u826f\u597d\uff0c\u663e\u5f97 \u4e00\u4e1d\u4e0d\u4e71\u3002 \u3000\u3000\u81f3\u4e8e\u53e6\u4e00\u4fa7\u95e8\uff0c\u866b\u9b54\u706b\u83bd\u4f9d\u7136\u5b88\u5728\u90a3\u91cc\uff0c\u7fa4\u96c4\u6599\u60f3\u4ed6\u8eab\u4e0a\u4ecd\u85cf\u6709\u6bd2\u866b\uff0c\u662f\u4ee5\u4e5f\u65e0\u4eba\u6562\u7531 \u4fa7\u95e8\u51b2\u51fa\u3002 \u3000\u3000\u53ea\u542c\u9634\u624b\u4e00\u9b54\u91cd\u91cd\u54b3\u4e86\u4e24\u58f0\u9053\uff1a\u201c\u7528\u4e0d\u7740\u85cf\u85cf\u8eb2\u8eb2\uff0c\u7a76\u7adf\u662f\u4f55\u65b9\u9ad8\u4eba\uff0c\u5c31\u8bf7\u73b0\u8eab\u51fa\u6765\uff0c \u4e5f\u597d\u8ba9\u8001\u592b\u89c1\u8bc6\u89c1\u8bc6\uff0e\u201d \u3000\u3000\u4e00\u8bed\u672a\u6bd5\uff0c\u8f7b\u98ce\u98d2\u7136\uff0c\u7531\u5927\u5385\u95e8\u5916\u5e7f\u573a\u5c3d\u5934\u5c71\u4e0a\u7684\u4e00\u68f5\u5de8\u677e\u4e0a\u98d8\u4e0b\u4e00\u4e2a\u9ed1\u8272\u4eba\u5f71\uff0e \u3000\u3000\u90a3\u4eba\u5f71\u8eab\u6750\u7626\u5c0f\uff0c\u4f46\u5374\u7a7f\u7740\u4e00\u8eab\u5bbd\u5927\u7684\u7f01\u8863\uff0c\u7f01\u8863\u4e0b\u6446\u5728\u591c\u98ce\u4e2d\u5fae\u5fae\u98d8\u52a8\uff0c\u4f46\u8eab\u8eaf\u5374\u53c8 \u7eb9\u98ce\u4e0d\u52a8\uff0c\u663e\u5f97\u5b89\u8be6\u800c\u53c8\u5e84\u4e25\u3002 \u3000\u3000\u805a\u96c6\u5728\u5927\u5385\u4e00\u89d2\u7684\u7fa4\u96c4\uff0c\u8fd9\u65f6\u90fd\u6d8c\u81f3\u4e00\u5904\u7a97\u53e3\u4e0b\uff0c\u51dd\u795e\u7aa5\u89c6\u5927\u5385\u5916\u52a8\u9759\uff0c\u53ea\u56e0\u662f\u591c\u95f4\uff0c \u867d\u80fd\u770b\u5230\u7626\u5c0f\u7684\u9ed1\u8863\u4eba\u5f71\uff0c\u5374\u65e0\u6cd5\u770b\u6e05\u5176\u9762\u76ee\uff0e \u3000\u3000\u90a3\u68f5\u5de8\u677e\u8ddd\u5927\u5385\u95e8\u53e3\u8db3\u6709\u4e03\u516b\u4e08\u8fdc\uff0c\u771f\u53ef\u8bf4\u662f\u60ca\u4e16\u9a87\u4fd7\u4e86\uff0e \u3000\u3000\u5982\u679c\u4ed6\u662f\u6b3a\u8fd1\u5927\u5385\u95e8\u53e3\u51fa\u624b\u7684\uff0c\u4ee5\u9634\u624b\u2014\u9b54\u548c\u6bd2\u706b\u6210\u5168\u90a3\u7b49\u4eba\u7269\u7adf\u6beb\u65e0\u60ca\u89c9\uff0c\u7167\u6837\u4e5f\u662f \u4ef6\u4e0d\u53ef\u601d\u8bae\u7684\u4e8b\u3002 \u3000\u3000\u9634\u624b\u4e00\u9b54\u51b7\u51b7\u4e00\u7b11\u9053\uff1a\u201c\u65b9\u624d\u5077\u88ad\u4ed6\u4eec\u4e8c\u4eba\u7684\u53ef\u662f\u5c0a\u9a7e\uff1f\u201d \u3000\u3000\u90a3\u7f01\u8863\u4eba\u6717\u58f0\u9053\uff1a\u201c\u51fa\u624b\u7684\u81ea\u7136\u662f\u6211\uff0c\u4f46\u5374\u7b97\u4e0d\u5f97\u5077\u88ad\u3002\u201d \u3000\u3000\u7f01\u8863\u4eba\u58f0\u97f3\u6717\u6717\u800c\u6e05\u8106\uff0c\u5206\u660e\u662f\u4e2a\u5973\u5b50\u3002 \u3000\u3000\u5728\u8fd9\u5239\u90a3\uff0c\u5927\u5385\u5185\u7684\u9ea6\u5c0f\u660e\u7a81\u611f\u5fc3\u795e\u4e00\u9707\uff0c\u8fd9\u58f0\u97f3\u542c\u6765\u7adf\u662f\u5341\u5206\u719f\u6089\uff0c\u4f46\u4e00\u65f6\u4e4b\u95f4\u5374\u60f3 \u4e0d\u8d77\u662f\u8c01\u3002 \u3000\u3000\u9634\u624b\u4e00\u9b54\u987f\u4e86\u2014\u987f\u9053\uff1a\u201c\u4e0d\u7ba1\u82b3\u9a7e\u662f\u5426\u5077\u88ad\uff0e\u82b3\u9a7e\u6b64\u65f6\u6b64\u5730\u51fa\u73b0\uff0c\u7a76\u7adf\u662f\u4f55\u7528\u5fc3\uff1f\u53ef\u5426 \u544a\u77e5\u6765\u610f\uff1f\u201d \u3000\u3000\u7f01\u8863\u4eba\u51b7\u51b7\u4e00\u7b11\u9053\uff1a\u201c\u90a3\u8981\u5148\u95ee\u95ee\u4f60\u4eec\u6765\u5230\u8ff7\u8e2a\u8c37\u662f\u4f55\u7528\u5fc3\uff1f\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u987f\u4e86\u987f\u9053\uff1a\u201c\u6bd2\u706b\u6210\u5144\u7b49\u56db\u4eba\u88ab\u970d\u5143\u4f3d\u65e0\u7f18\u65e0\u6545\u62d8\u8fdb\u8ff7\u8e2a\u8c37\uff0c\u8001\u592b\u53ea\u662f\u6765\u6551\u4ed6 \u4eec\u3002\u201d \u3000\u3000\u7f01\u8863\u4eba\u51b7\u7b11\u9053\uff1a\u201c\u8001\u5339\u592b\uff0c\u4f60\u8bf4\u5f97\u672a\u514d\u592a\u52a8\u542c\u4e86\u3002\u970d\u5143\u4f3d\u5c82\u4f1a\u65e0\u7f18\u65e0\u6545\u628a\u4ed6\u4eec\u62d8\u8fdb\u8ff7\u8e2a \u8c37\uff0e\u6211\u82e5\u5f04\u4e0d\u6e05\u539f\u56e0\uff0c\u4e5f\u4e0d\u4f1a\u524d\u6765\u4e86\u3002\u201d \u3000\u3000\u201c\u4f60\u7a76\u7adf\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u201c\u5728\u4f60\u4eec\u8fd9\u6279\u6c5f\u6e56\u8d25\u7c7b\u9762\u524d\uff0c\u6211\u8fd8\u6ca1\u6709\u5fc5\u8981\u900f\u9732\u8eab\u4efd\u6765\u5386\u3002\u201d \u3000\u3000\u201c\u4f60\u548c\u970d\u5143\u4f3d\u662f\u4ec0\u4e48\u5173\u7cfb\uff1f\u201d \u3000\u3000\u201c\u8c08\u4e0d\u4e0a\u4efb\u4f55\u5173\u7cfb\u3002\u201d \u3000\u3000\u201c\u65e2\u7136\u5982\u6b64\uff0c\u4f60\u5c31\u8be5\u77e5\u9053\u970d\u5143\u4f3d\u4e5f\u662f\u9ed1\u9053\u51fa\u8eab\uff0c\u4f60\u9a82\u8001\u592b\u7b49\u4eba\u662f\u6c5f\u6e56\u8d25\u7c7b\uff0c\u96be\u9053\u970d\u5143\u4f3d \u5c31\u4e0d\u662f\u6c5f\u6e56\u8d25\u7c7b\u3002\u201d \u3000\u3000\u201c\u6211\u662f\u51b2\u7740\u5929\u4e0b\u7eff\u6797\u603b\u5be8\u6765\u7684\uff0c\u8ff7\u8e2a\u8c37\u867d\u7136\u9f99\u86c7\u6df7\u6742\uff0c\u970d\u5143\u4f3d\u4e5f\u4e0d\u89c1\u5f97\u662f\u597d\u4eba\uff0c\u4f46\u8fd9\u5929 \u4e0b\u7eff\u6797\u603b\u5be8\u4e00\u65e6\u88ab\u4f60\u4eec\u6bc1\u706d\uff0c\u5fc5\u5c06\u5f15\u8d77\u7eff\u6797\u4e2d\u66f4\u5927\u7684\u6df7\u4e71\u3002\u4e3a\u4e86\u6d88\u5f2d\u7eff\u6797\u518d\u2014\u6b21\u7684\u6d69\u52ab\uff0c\u6211 \u5c31\u4e0d\u80fd\u773c\u770b\u4f60\u4eec\u80e1\u4f5c\u975e\u4e3a\u3002\u201d \u3000\u3000\u201c\u4f60\u81ea\u4fe1\u80fd\u6551\u5f97\u4e86\u4ed6\u4eec\uff1f\u201d \u3000\u3000\u201c\u5bf9\u4ed8\u4f60\u4eec\u8fd9\u51e0\u4e2a\u6c5f\u6e56\u8d25\u7c7b\uff0c\u8fd8\u7ef0\u7ef0\u6709\u4f59\uff01\u201d \u3000\u3000\u201c\u53ef\u60dc\u4ed6\u4eec\u56db\u4eba\u4e2d\uff0c\u5df2\u6709\u4e24\u4eba\u5931\u53bb\u529f\u529b\u3002\u201d \u3000\u3000\u7f01\u8863\u4eba\u6de1\u7136\u4e00\u7b11\u9053\uff1a\u201c\u73b0\u5728\u6211\u60c5\u613f\u89e3\u5f00\u4ed6\u4eec\u4e8c\u4eba\u7684\u7a74\u9053\uff0c\u8fd9\u6837\u4ed6\u4eec\u5c31\u65e0\u8bdd\u53ef\u8bf4\u4e86\u5427\uff01\u201d \u3000\u3000\u5979\u8bf4\u7740\uff0c\u53ea\u662f\u53f3\u81c2\u5fae\u5fae\u2014\u62ac\uff0c\u725b\u5954\u7acb\u5373\u7531\u5730\u4e0a\u7ad9\u4e86\u8d77\u6765\u3002 \u3000\u3000\u6bd2\u706b\u6210\u5168\u7684\u53f3\u81c2\u4e5f\u7acb\u5373\u8fd0\u8f6c\u81ea\u5982\u3002 \u3000\u3000\u7f01\u8863\u4eba\u7adf\u80fd\u5728\u4e03\u516b\u4e08\u5916\uff0c\u4e00\u62ac\u624b\u5373\u89e3\u5f00\u4e24\u4eba\u7a74\u9053\uff0c\u53ef\u89c1\u5979\u5148\u524d\u70b9\u4e2d\u725b\u5954\u548c\u6210\u5168\uff0c\u7edd\u975e\u8fd1 \u524d\u5077\u88ad\u3002 \u3000\u3000\u5728\u8fd9\u5239\u90a3\uff0c\u9634\u624b\u4e00\u9b54\u7b49\u4eba\u4ee5\u53ca\u5927\u5385\u5185\u7684\u7fa4\u96c4\uff0c\u65e0\u4e0d\u88ab\u7f01\u8863\u4eba\u7684\u8d85\u51e1\u5165\u5723\u795e\u529f\u60ca\u5f97\u76ee\u77aa\u53e3 \u5446\u3002 \u3000\u3000\u9634\u624b\u4e00\u9b54\u867d\u7136\u5fc3\u60ca\u80c6\u98a4\uff0c\u7136\u800c\u5df2\u6210\u9a91\u864e\u96be\u4e0b\u4e4b\u52bf\uff0c\u4e0d\u5f97\u4e0d\u4e0d\u8ba1\u540e\u679c\u7684\u786c\u6491\u4e0b\u53bb\u3002 \u3000\u3000\u53ea\u542c\u7f01\u8863\u4eba\u51b7\u7136\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u662f\u8054\u624b\u800c\u4e0a\u8fd8\u662f\u4e00\u4e2a\u4e00\u4e2a\u7684\u4e0a\uff1f\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u8fd8\u6ca1\u6765\u5f97\u53ca\u7b54\u8bdd\uff0c\u6bd2\u706b\u6210\u5168\u5df2\u7d27\u6263\u4e24\u628a\u6697\u5668\u8d70\u4e0b\u5927\u5385\u95e8\u5916\u53f0\u9636\u9053\uff1a\u201c\u9634\u624b\u5144\uff0c \u8fd9\u7b2c\u4e00\u9635\u5c31\u8ba9\u6210\u67d0\u6765\u8bd5\u8bd5\u3002\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u6b63\u662f\u6c42\u4e4b\u4e0d\u5f97\uff0c\u540c\u65f6\u4ed6\u4e5f\u660e\u767d\u6bd2\u706b\u6210\u5168\u7684\u7528\u610f\uff0c\u56e0\u6bd2\u706b\u6210\u5168\u6700\u64c5\u957f\u7684\u6697\u5668\uff0c\u6839 \u672c\u4e0d\u987b\u8fd1\u8eab\u62fc\u640f\u3002 \u3000\u3000\u7f01\u8863\u4eba\u6b66\u529f\u518d\u9ad8\uff0c\u4e5f\u96be\u4ee5\u8eb2\u8fc7\u4e24\u628a\u6697\u5668\u6570\u5341\u679a\u6885\u82b1\u9488\u53ca\u874e\u5c3e\u9488\u7684\u9aa4\u53d1\u65bd\u88ad\u3002 \u3000\u3000\u6bd2\u706b\u6210\u5168\u76f4\u8d70\u5230\u8ddd\u7f01\u8863\u4eba\u4e0d\u8db3\u4e24\u4e08\u5904\u624d\u6b62\u4f4f\u811a\u6b65\u3002 \u3000\u3000\u4ed6\u7684\u7528\u5fc3\u663e\u7136\u662f\u8981\u5728\u8fd1\u8ddd\u79bb\u5185\u51fa\u624b\uff0c\u8fd9\u6837\u5bf9\u65b9\u5c31\u8d8a\u53d1\u96be\u9003\u5929\u7f57\u5730\u7f51\u822c\u7684\u65bd\u88ad\u4e86\u3002 \u3000\u3000\u7f01\u8863\u4eba\u4f9d\u7136\u7eb9\u98ce\u4e0d\u52a8\uff0c\u51b7\u51b7\u95ee\u9053\uff1a\u201c\u4f60\u5c31\u662f\u6bd2\u706b\u6210\u5168\uff1f\u201d \u3000\u3000\u6bd2\u706b\u6210\u5168\u9053\uff1a\u201c\u9053\u4e0a\u7684\u670b\u53cb\u7ed9\u6210\u67d0\u8fd9\u6837\u4e00\u4e2a\u7ef0\u53f7\uff0c\u6210\u67d0\u4ece\u4e0d\u8ba1\u8f83\uff0c\u5c31\u7b97\u4f60\u8bf4\u5bf9\u4e86\u5427\uff01\u201d \u3000\u3000\u7f01\u8863\u4eba\u9053\uff1a\u201c\u597d\u4e00\u4e2a\u6c5f\u6e56\u8d25\u7c7b\uff0c\u679c\u7136\u5176\u8c8c\u4e0d\u626c\uff01\u201d \u3000\u3000\u6bd2\u706b\u6210\u5168\u672c\u5c31\u9762\u76ee\u53ef\u618e\uff0c\u5947\u4e11\u65e0\u6bd4\uff0c\u88ab\u5bf9\u65b9\u9a82\u53e5\u5176\u8c8c\u4e0d\u626c\uff0c\u6839\u672c\u4e0d\u4e88\u8ba1\u8f83\uff0c\u563f\u563f\u4e00\u7b11 \u9053\uff1a\u201c\u82b3\u9a7e\u6700\u597d\u522b\u4ee5\u8c8c\u53d6\u4eba\uff0e\u8f83\u6280\u8bba\u827a\uff0c\u7eb5\u7136\u662f\u5c0f\u767d\u8138\uff0c\u4e5f\u7167\u6837\u5360\u4e0d\u5230\u4fbf\u5b9c\u3002\u201d \u3000\u3000\u7f01\u8863\u4eba\u9053\uff1a\u201c\u8fd9\u6837\u8bf4\u4f60\u5df2\u7ecf\u5f88\u6ee1\u610f\u81ea\u5df1\u8fd9\u526f\u957f\u76f8\u4e86\uff1f\u201d \u3000\u3000\u6bd2\u706b\u6210\u5168\u7ec8\u4e8e\u88ab\u6fc0\u6012\uff0c\u543c\u9053\uff1a\u201c\u600e\u6837\u6bd4\u6cd5\uff0e\u5c31\u5feb\u5212\u51fa\u9053\u513f\u6765\uff0c\u7528\u4e0d\u7740\u591a\u8bf4\u5e9f\u8bdd\uff01\u201d \u3000\u3000\u7f01\u8863\u4eba\u9053\uff1a\u201c\u4f60\u65e2\u88ab\u4eba\u79f0\u6bd2\u706b\uff0c\u624b\u91cc\u53c8\u6263\u7740\u4e24\u628a\u6697\u5668\uff0c\u81ea\u7136\u662f\u4ee5\u6697\u5668\u6700\u64c5\u80dc\u573a\uff0c\u6211\u73b0\u5728 \u5c31\u8bd5\u8bd5\u4f60\u7684\u6697\u5668\u624b\u6cd5\u7a76\u7adf\u9ad8\u660e\u5230\u4ec0\u4e48\u5883\u754c\uff01\u201d \u3000\u3000\u201c\u4f60\u81ea\u4fe1\u80fd\u8eb2\u8fc7\u8001\u592b\u7684\u6697\u5668\uff1f\u201d \u3000\u3000\u201c\u5728\u4f60\u6ca1\u51fa\u624b\u4e4b\u524d\uff0e\u8c08\u81ea\u4fe1\u672a\u514d\u8a00\u4e4b\u8fc7\u65e9\u3002\u201d \u3000\u3000\u201c\u90a3\u4e48\u4f60\u662f\u51c6\u5907\u7528\u4ec0\u4e48\u529e\u6cd5\u6765\u8eb2\u907f\u8001\u592b\u7684\u6697\u5668\uff1f\u201d \u3000\u3000\u201c\u4f60\u95ee\u5f97\u672a\u514d\u592a\u591a\u4e86\uff0e\u4e0d\u8fc7\u6211\u53ef\u4ee5\u544a\u8bc9\u4f60\uff0c\u4e0d\u7ba1\u4f60\u7528\u4ec0\u4e48\u624b\u6cd5\u65bd\u88ad\uff0e\u6211\u811a\u4e0b\u7edd\u4e0d\u79fb\u52a8\u534a \u6b65\u3002\u201d \u3000\u3000\u8fd9\u51e0\u53e5\u8bdd\uff0c\u53cd\u800c\u4f7f\u5f97\u6bd2\u706b\u6210\u5168\u6709\u4e9b\u83ab\u6d4b\u9ad8\u6df1\u3002 \u3000\u3000\u7f01\u8863\u4eba\u50ac\u4fc3\u7740\u9053\uff1a\u201c\u4e0d\u5fc5\u72b9\u8c6b\uff0c\u73b0\u5728\u5c31\u51fa\u624b\u5427\uff01\u201d \u3000\u3000\u6bd2\u706b\u6210\u5168\u5de6\u624b\u2014\u7529\uff0c\u4e8c\u5341\u51e0\u652f\u874e\u5c3e\u9488\u7b3c\u7f69\u4e86\u6570\u5c3a\u65b9\u5706\uff0c\u631f\u7740\u54bb\u54bb\u5578\u58f0\uff0e\u76f4\u5411\u7f01\u8863\u4eba\u5168\u8eab \u5c04\u53bb\u3002 \u3000\u3000\u4ed6\u4e3a\u4e86\u52a0\u91cd\u706b\u529b\uff0c\u7d27\u63a5\u7740\u53f3\u624b\u7684\u2014\u6279\u6885\u82b1\u9488\u53c8\u7529\u6492\u51fa\u53bb\u3002 \u3000\u3000\u6885\u82b1\u9488\u56e0\u6bd4\u874e\u5c3e\u9488\u66f4\u4e3a\u7ec6\u5c0f\uff0c\u8fd9\u4e00\u628a\u8db3\u6709\u56db\u4e94\u5341\u652f\uff0c\u5728\u4e0d\u8db3\u4e24\u4e08\u7684\u8ddd\u79bb\u5185\uff0e\u653e\u773c\u5f53\u4eca\u6b66 \u6797\uff0c\u4efb\u4f55\u9ad8\u4eba\u4e5f\u96be\u4ee5\u8eb2\u8fc7\u3002 \u3000\u3000\u5c82\u77e5\u7f01\u8863\u4eba\u679c\u7136\u811a\u4e0b\u6beb\u672b\u79fb\u52a8\uff0c\u53cc\u81c2\u4e00\u6296\uff0c\u8863\u8896\u7adf\u7a81\u7136\u66b4\u80c0\u5230\u4e03\u516b\u5c3a\u957f\uff0c\u800c\u4e14\u8db3\u6709\u4e24\u5c3a \u5bbd\uff0e\u4e24\u8896\u4ea4\u4e92\u4e00\u76d8\u2014\u65cb\uff0c\u7adf\u7136\u628a\u8eab\u524d\u4ece\u5934\u5230\u811a\uff0c\u6321\u5f97\u98ce\u96e8\u4e0d\u900f\uff0e\u5c24\u5176\u53cc\u8896\u5706\u81a8\u5982\u9f13\uff0c\u90a3\u6570\u5341 \u679a\u6885\u82b1\u9488\u548c\u874e\u5c3e\u9488\u649e\u5230\u8863\u8896\u4e4b\u540e\uff0c\u5c45\u7136\u53d1\u51fa\u201c\u5bdf\u5bdf\u201d\u7684\u58f0\u97f3\u6765\uff0c\u5c31\u50cf\u649e\u4e0a\u94dc\u5899\u94c1\u58c1\u4e00\u822c\u3002 \u3000\u3000\u8fd9\u2014\u6765\uff0e\u4f7f\u5f97\u6bd2\u706b\u6210\u5168\u5446\u6123\u5f53\u573a\uff0c\u51e0\u4e4e\u4e0d\u76f8\u4fe1\u8fd9\u662f\u4e8b\u5b9e\u3002 \u3000\u3000\u9634\u624b\u2014\u9b54\u548c\u5927\u5385\u5185\u7684\u7fa4\u96c4\u4e5f\u90fd\u770b\u50bb\u4e86\u773c\uff0e \u3000\u3000\u6bd2\u706b\u6210\u5168\u6b64\u65f6\u6697\u5668\u5df2\u5c3d\uff0c\u521a\u8981\u6d51\u6c34\u6478\u9c7c\u51c6\u5907\u9000\u56de\u53bb\uff0c\u53ea\u542c\u7f01\u8863\u4eba\u559d\u9053\uff1a\u201c\u4f60\u51c6\u5907\u5230\u54ea\u91cc \u53bb\uff1f\u201d \u3000\u3000\u6bd2\u706b\u6210\u5168\u5e72\u54b3\u4e86\u58f0\u9053\uff1a\u201c\u5df2\u7ecf\u6bd4\u8fc7\u4e86\uff0c\u8fd8\u6709\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u7f01\u8863\u4eba\u9053\uff1a\u201c\u4f60\u5df2\u7ecf\u7528\u6697\u5668\u653b\u8fc7\u6211\uff0c\u6211\u5e76\u672a\u8fd8\u624b\uff0c\u8fd9\u6837\u662f\u5426\u516c\u5e73\uff1f\u201d \u3000\u3000\u6bd2\u706b\u6210\u5168\u6253\u4e86\u4e2a\u54c6\u55e6\u9053\uff1a\u201c\u4f60\u2026\u2026\u51c6\u5907\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u7f01\u8863\u4eba\u9053\uff1a\u201c\u73b0\u5728\u5e94\u8be5\u7531\u6211\u51fa\u624b\u4e86\uff0c\u4e0d\u8fc7\u6211\u4e0d\u60f3\u9650\u5236\u4f60\u7684\u884c\u52a8\uff0e\u4f60\u53ef\u4ee5\u51fa\u624b\u8fd8\u51fb\uff0c\u5c0f\u5fc3 \u4e86\uff01\u201d \u3000\u3000\u5979\u6700\u540e\u2014\u4e2a\u5b57\u97f3\u521a\u521a\u51fa\u53e3\uff0c\u53f3\u624b\u5fae\u62ac\uff0c\u3000\u4e00\u7f15\u6307\u98ce\u76f4\u5411\u6bd2\u706b\u6210\u5168\u524d\u80f8\u201c\u5c06\u53f0\u7a74\u201d\u88ad\u53bb\u3002 \u3000\u3000\u6bd2\u706b\u6210\u5168\u6839\u672c\u6765\u4e0d\u53ca\u95ea\u8eb2\uff0c\u2014\u58f0\u95f7\u54fc\uff0c\u5f53\u573a\u5012\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u7f01\u8863\u4eba\u51b7\u51b7\u4e00\u7b11\u9053\uff1a\u201c\u9634\u624b\u4e00\u9b54\uff0c\u73b0\u5728\u8be5\u4f60\u51fa\u573a\u5566\uff01\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u662f\u4ee5\u201c\u9634\u98ce\u638c\u201d\u9a70\u540d\u9ed1\u767d\u4e24\u9053\uff0c\u8bba\u6b66\u529f\u7eb5\u7136\u6bd4\u6bd2\u706b\u6210\u5168\u9ad8\u4e9b\uff0c\u4e5f\u9ad8\u4e0d\u4e86\u591a\u5c11\u3002 \u4ed6\u773c\u89c1\u7f01\u8863\u4eba\u7684\u76d6\u4e16\u795e\u529f\uff0c\u81ea\u5fd6\u4e07\u96be\u662f\u5bf9\u65b9\u654c\u624b\u3002 \u3000\u3000\u82e5\u8d38\u7136\u51fa\u9635\uff0c\u514d\u4e0d\u4e86\u4e5f\u8981\u4e22\u4eba\u73b0\u773c\uff0c\u4ed6\u4e00\u5411\u8001\u5978\u5de8\u733e\uff0c\u66f4\u80dc\u6bd2\u706b\u6210\u5168\uff0c\u5f53\u4e0b\u7565\u4e00\u6c89\u541f \u9053\uff1a\u201c\u8001\u592b\u5df2\u88ab\u4ed6\u4eec\u56db\u4f4d\u63a8\u4e3e\u4e3a\u4e3b\u4e8b\u4e4b\u4eba\uff0c\u8981\u6bd4\u4e5f\u8be5\u6bd4\u6700\u540e\u4e00\u9635\u3002\u201d \u3000\u3000\u7f01\u8863\u4eba\u4e0d\u52a8\u58f0\u8272\u9053\uff1a\u201c\u53ef\u4ee5\uff0e\u4f60\u65e2\u7136\u662f\u4ed6\u4eec\u7684\u4e3b\u4e8b\u4e4b\u4eba\uff0c\u90a3\u5c31\u518d\u6d3e\u51fa\u4e00\u4e2a\u6765\uff01\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u7acb\u5373\u671b\u5411\u9ed1\u9e70\u65f6\u5bc5\u9053\uff1a\u201c\u65f6\u5144\u8bf7\u5230\u5927\u5385\u4fa7\u95e8\u628a\u706b\u603b\u5cd2\u4e3b\u6362\u4e0b\uff0c\u8981\u4ed6\u5230\u8fd9\u8fb9 \u6765\uff01\u201d \u3000\u3000\u9ed1\u9b54\u89c1\u81ea\u5df1\u80fd\u8131\u8fc7\u548c\u7f01\u8863\u4eba\u4ea4\u624b\u8fd9\u4e00\u5173\uff0c\u5f53\u7136\u9ad8\u5174\uff0c\u968f\u5373\u5e94\u58f0\u800c\u53bb\u3002 \u3000\u3000\u4e0d\u4e00\u4f1a\u513f\uff0c\u866b\u9b54\u706b\u83bd\u4fbf\u7531\u5927\u5385\u4e00\u89d2\u8f6c\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u539f\u6765\uff0c\u9634\u624b\u4e00\u9b54\u60f3\u5230\u4ee5\u706b\u83bd\u6765\u5bf9\u4ed8\u7f01\u8863\u4eba\uff0c\u4e0d\u5916\u662f\u8981\u5229\u7528\u4ed6\u7684\u6bd2\u866b\u3002 \u3000\u3000\u6bd2\u706b\u6210\u5168\u7684\u6697\u5668\u88ab\u7f01\u8863\u4eba\u7684\u53cc\u8896\u6321\u843d\uff0c\u90a3\u662f\u56e0\u4e3a\u6697\u5668\u662f\u76f4\u7ebf\u65bd\u88ad\u800c\u5165\uff0c\u800c\u6bd2\u866b\u662f\u6d3b\u7269\uff0c \u80fd\u591f\u76d8\u65cb\u98de\u7fd4\uff0c\u8d81\u673a\u653e\u5c04\u6bd2\u6c41\uff0c\u53ea\u8981\u706b\u83bd\u653e\u51fa\u866b\u53bb\uff0c\u7f01\u8863\u4eba\u7684\u53cc\u8896\u5fc5\u5c06\u5931\u6548\u3002 \u3000\u3000\u706b\u83bd\u5e76\u4e0d\u6e05\u695a\u65b9\u624d\u6210\u5168\u548c\u7f01\u8863\u4eba\u4ea4\u624b\u7684\u60c5\u5f62\uff0c\u6765\u5230\u9634\u624b\u4e00\u9b54\u8ddf\u524d\u9053\uff1a\u201c\u9634\u624b\u8001\u82f1\u96c4\u6709\u4ec0 \u4e48\u5429\u5490\uff1f\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u9053\uff1a\u201c\u8fd9\u5973\u4eba\u60f3\u8bd5\u8bd5\u4f60\u795e\u866b\u7684\u5a01\u529b\uff0c\u4f60\u8eab\u4e0a\u8fd8\u6709\u591a\u5c11\u795e\u866b\uff1f\u201d \u3000\u3000\u706b\u83bd\u6c14\u72e0\u72e0\u5730\u9053\uff1a\u201c\u65b9\u624d\u90a3\u4e09\u5341\u516d\u53ea\u91d1\u8695\u866b\u5168\u5df2\u6536\u4e0d\u56de\u6765\u5566\uff0c\u4e0d\u8fc7\u6ca1\u5173\u7cfb\uff0c\u672c\u603b\u5cd2\u4e3b\u6000 \u91cc\u4ecd\u6709\u4e24\u4e2a\u53e3\u888b\uff0c\u4e00\u53e3\u888b\u662f\u6ce5\u9cc5\u866b\uff0c\u4e00\u53e3\u888b\u662f\u86c7\u866b\uff0c\u4e00\u5171\u5927\u7ea6\u6709\u4e0a\u767e\u53ea\u3002\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u9053\uff1a\u201c\u90a3\u4f60\u5c31\u628a\u866b\u5f80\u5979\u8eab\u4e0a\u653e\u597d\u5566\uff01\u201d \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u5927\u5385\u5185\u7684\u7fa4\u96c4\u5df2\u8d77\u4e86\u5267\u70c8\u53d8\u52a8\u3002 \u3000\u3000\u4ed6\u4eec\u56e0\u89c1\u706b\u83bd\u5df2\u79bb\u5f00\u4fa7\u95e8\uff0c\u6362\u7531\u9ed1\u9b54\u65f6\u5bc5\u628a\u5b88\uff0e\u9ed1\u9b54\u51ed\u6043\u7684\u53ea\u662f\u9ed1\u7802\u638c\uff0c\u7fa4\u96c4\u5c31\u4e0d\u518d\u653e \u5728\u5fc3\u4e0a\u3002 \u3000\u3000\u5f53\u4e0b\u7531\u970d\u5143\u4f3d\u3001\u949f\u4e00\u8c6a\u3001\u9ea6\u5c0f\u660e\u5e26\u5934\uff0c\u4e09\u4eba\u8054\u624b\u4ee5\u638c\u529b\u5c06\u9ed1\u9b54\u65f6\u5bc5\u51fb\u9000\uff0c\u76f4\u5411\u5927\u5385\u524d\u5e7f \u573a\u51b2\u51fa\uff0c\u628a\u9634\u624b\u2014\u9b54\u7b49\u4eba\u56e2\u56e2\u56f4\u4f4f\u3002 \u3000\u3000\u4e0d\u8fc7\u7fa4\u96c4\u4ecd\u4e0d\u6562\u7f29\u7d27\u5305\u56f4\u5708\uff0c\u62c5\u5fc3\u706b\u83bd\u731d\u7136\u53d1\u96be\u65bd\u653e\u6bd2\u866b\u88ad\u51fb\u3002 \u3000\u3000\u7f01\u8863\u4eba\u73af\u626b\u4e86\u7fa4\u96c4\u4e00\u773c\u9053\uff1a\u201c\u4f60\u4eec\u90fd\u8ba9\u8fdc\u4e9b\uff0c\u6211\u65e2\u5df2\u7ecf\u63d2\u624b\u7ba1\u4e0a\u8fd9\u4ef6\u4e8b\uff0c\u5c31\u4e00\u5b9a\u4f1a\u7ba1\u5230 \u5e95\uff0c\u8fd9\u51e0\u4e2a\u6c5f\u6e56\u8d25\u7c7b\uff0c\u6211\u4e00\u4eba\u5c31\u8db3\u53ef\u6536\u62fe\uff0c\u4f60\u4eec\u53ea\u5728\u4e00\u65c1\u770b\u7740\u4fbf\u4e86\u3002\u201d \u3000\u3000\u7fa4\u96c4\u6b64\u523b\u624b\u4e2d\u4ecd\u65e0\u5175\u5203\uff0c\u53c8\u89c1\u7f01\u8863\u4eba\u6b66\u529f\u5df2\u8fbe\u8d85\u51e1\u5165\u5723\u4e4b\u5883\uff0c\u4e5f\u5c31\u4e50\u5f97\u5728\u65c1\u770b\u770b\u70ed\u95f9\u5f00 \u5f00\u773c\u754c\u3002 \u3000\u3000\u53ea\u542c\u706b\u83bd\u563f\u563f\u7b11\u9053\uff1a\u201c\u672c\u603b\u5cd2\u4e3b\u7684\u5404\u79cd\u795e\u866b\uff0c\u4f60\u81ea\u4fe1\u80fd\u8eb2\u5f97\u8fc7\uff1f\u201d \u3000\u3000\u7f01\u8863\u4eba\u51b7\u7136\u9053\uff1a\u201c\u4f60\u53ea\u7ba1\u65bd\u653e\uff0c\u6211\u811a\u4e0b\u7edd\u4e0d\u79fb\u52a8\u534a\u6b65\u3002\u201d \u3000\u3000\u706b\u83bd\u6014\u4e86\u4e00\u6014\u9053\uff1a\u201c\u4f60\u8eab\u4e0a\u53ef\u662f\u85cf\u6709\u706b\u5668\uff0c\u51c6\u5907\u4ee5\u706b\u5668\u70e7\u9000\u795e\u866b\uff1f\u201d \u3000\u3000\u7f01\u8863\u4eba\u9053\uff1a\u201c\u6211\u8eab\u4e0a\u4ec0\u4e48\u90fd\u6ca1\u5e26\uff0c\u8fde\u5175\u5203\u90fd\u4e0d\u66fe\u5e26\u6765\u3002\u201d \u3000\u3000\u201c\u90a3\u4f60\u4e0d\u662f\u627e\u6b7b\u5417\uff1f\u201d \u3000\u3000\u201c\u8fd9\u662f\u6211\u81ea\u5df1\u7684\u4e8b\uff0c\u7528\u4e0d\u7740\u4f60\u62c5\u5fc3\u3002\u201d \u3000\u3000\u706b\u83bd\u65e9\u5df2\u628a\u4e24\u4e2a\u866b\u888b\u7d27\u7d27\u634f\u5728\u624b\u4e2d\uff0c\u4e00\u8fb9\u662f\u86c7\u866b\uff0c\u4e00\u8fb9\u662f\u6ce5\u9cc5\u866b\u3002 \u3000\u3000\u8fd9\u79cd\u86c7\u866b\u548c\u6ce5\u9cc5\u866b\uff0c\u786e\u662f\u771f\u86c7\u548c\u771f\u6ce5\u9cc5\uff0c\u56e0\u4e3a\u4e00\u751f\u4e0b\u6765\u5c31\u4ee5\u7279\u5236\u7684\u836f\u6c34\u6d78\u5582\uff0c\u5168\u8eab\u5145\u6ee1 \u866b\u6bd2\uff0c\u6240\u4ee5\u65e0\u6cd5\u957f\u5927\u3002 \u3000\u3000\u540c\u65f6\u8eab\u4e0a\u7adf\u80fd\u751f\u51fa\u7fc5\u8180\uff0c\u53ef\u4ee5\u5728\u7a7a\u4e2d\u5bf9\u4eba\u7c7b\u6216\u5176\u4ed6\u52a8\u7269\u98de\u6251\u88ad\u51fb\uff0c\u800c\u4e14\u66f4\u80fd\u542c\u547d\u4e8e\u4e3b\u4eba \u9a71\u4f7f\u3002 \u3000\u3000\u6240\u8c13\u4e3b\u4eba\uff0c\u6307\u7684\u5c31\u662f\u8bad\u7ec3\u866b\u7c7b\u4e4b\u4eba\uff0e\u706b\u83bd\u6700\u64c5\u8bad\u866b\uff0c\u56e0\u4e4b\u624d\u88ab\u79f0\u4e3a\u866b\u9b54\uff0c\u81f3\u4e8e\u6bd2\u706b\u6210\u5168 \u79f0\u4ed6\u866b\u795e\uff0e\u90a3\u81ea\u7136\u662f\u5f53\u9762\u5949\u627f\u4e4b\u8bcd\uff0c\u53eb\u7740\u597d\u542c\u7f62\u4e86\u3002 \u3000\u3000\u53ea\u542c\u7f01\u8863\u4eba\u9053\uff1a\u201c\u4e0d\u5fc5\u72b9\u8c6b\uff0c\u5c3d\u7ba1\u628a\u4f60\u7684\u6bd2\u866b\u653e\u8fc7\u6765\uff01\u201d \u3000\u3000\u706b\u83bd\u9634\u68ee\u68ee\u4e00\u7b11\uff0c\u7acb\u5373\u53e3\u4e2d\u5ff5\u5ff5\u6709\u8bcd\uff0c\u628a\u4e24\u624b\u7684\u888b\u53e3\u4e00\u677e\uff0e\u987f\u65f6\uff0c\u6570\u5341\u6761\u50cf\u874c\u86aa\u822c\u7684\u5947 \u5f62\u52a8\u7269\uff0e\u9ed1\u538b\u538b\u2014\u7247\uff0e\u632f\u7fc5\u5411\u7f01\u8863\u4eba\u5168\u8eab\u4e0a\u4e0b\u98de\u6251\u8fc7\u53bb\u3002 \u3000\u3000\u5728\u8fd9\u5239\u90a3\uff0e\u5728\u573a\u56f4\u89c2\u7684\u7fa4\u96c4\uff0c\u65e0\u4e0d\u5012\u62bd\u51b7\u6c14\uff0e\u731b\u6253\u5bd2\u5664\u3002 \u3000\u3000\u5374\u89c1\u7f01\u8863\u4eba\u7684\u5168\u8eab\u8863\u670d\uff0c\u4e0d\u77e5\u4ec0\u4e48\u65f6\u5019\uff0c\u7adf\u53c8\u81a8\u80c0\u800c\u8d77\uff0c\u5c31\u50cf\u6709\u4e00\u80a1\u72c2\u98d9\u3001\u53d1\u81ea\u5979\u7684\u4f53 \u5185\uff0c\u632f\u52a8\u5f97\u5168\u8eab\u8863\u670d\u730e\u730e\u4f5c\u54cd\u3002 \u3000\u3000\u5f53\u90a3\u6570\u5341\u53ea\u6bd2\u866b\u6251\u8fd1\u7f01\u8863\u4eba\u8eab\u4f53\u5468\u8fd1\u534a\u5c3a\u5de6\u53f3\u65f6\uff0c\u7adf\u53ea\u80fd\u5728\u539f\u5904\u6253\u8f6c\uff0c\u6839\u672c\u65e0\u6cd5\u518d\u524d \u8fdb\uff0c\u6709\u7684\u4e14\u88ab\u53cd\u5f39\u56de\u6765\u3002 \u3000\u3000\u800c\u7f01\u8863\u4eba\u53ea\u662f\u9759\u9759\u7684\u7ad9\u5728\u90a3\u91cc\uff0c\u4e1d\u6beb\u4e0d\u89c1\u4f5c\u52bf\uff0c\u66f4\u65e0\u6251\u51fb\u52a8\u4f5c\u3002 \u3000\u3000\u6570\u5341\u53ea\u6bd2\u866b\u76f4\u5728\u7f01\u8863\u4eba\u5168\u8eab\u4e0a\u4e0b\u534a\u5c3a\u5916\u76d8\u98de\u4e86\u5c06\u8fd1\u534a\u76cf\u8336\u5149\u666f\uff0c\u4f9d\u7136\u65e0\u6cd5\u51b2\u8fd1\uff0c\u6709\u4e0d\u5c11 \u4e14\u5df2\u5f53\u573a\u649e\u6b7b\uff0c\u866b\u5c38\u843d\u4e86\u4e00\u5730\u3002 \u3000\u3000\u8fd9\u65f6\uff0e\u706b\u83bd\u65e9\u5df2\u60ca\u5f97\u4e0d\u77e5\u6240\u63aa\uff0c\u4ed6\u505a\u68a6\u4e5f\u6ca1\u60f3\u5230\u4f1a\u53d1\u751f\u8fd9\u79cd\u602a\u4e8b\uff0c\u53ea\u597d\u6025\u6025\u6536\u56de\u5c1a\u6d3b\u7740 \u7684\u6bd2\u866b\uff0c\u5426\u5219\u90a3\u4e9b\u6bd2\u866b\uff0e\u52bf\u5fc5\u5168\u90e8\u649e\u6b7b\uff0e\u767d\u767d\u727a\u7272\u3002 \u3000\u3000\u81f3\u4e8e\u5728\u573a\u7fa4\u96c4\uff0c\u4e5f\u90fd\u88ab\u7f01\u8863\u4eba\u2014\u8eab\u4e0d\u53ef\u601d\u4e49\u7684\u6b66\u529f\uff0c\u60ca\u5f97\u900f\u4e0d\u8fc7\u6c14\u6765\u3002 \u3000\u3000\u7f01\u8863\u4eba\u6536\u56de\u66b4\u80c0\u7684\u8863\u886b\uff0c\u51b7\u51b7\u7b11\u9053\uff1a\u201c\u8fd8\u6709\u4ec0\u4e48\u672c\u9886\uff0c\u5c3d\u7ba1\u4f7f\u51fa\u6765\uff01\u201d \u3000\u3000\u706b\u83bd\u4e00\u8138\u72fc\u72c8\u4e0d\u582a\u795e\u8272\uff0c\u54ea\u91cc\u8fd8\u80fd\u8bf4\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u7f01\u8863\u4eba\u89c6\u7ebf\u8f6c\u5411\u9634\u624b\u4e00\u9b54\u9053\uff1a\u201c\u4e0b\u9762\u8be5\u8c01\u51fa\u573a\uff0c\u5feb\u70b9\u7ed9\u6211\u51fa\u6765\uff01\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u81ea\u5fd6\u7edd\u975e\u7f01\u8863\u4eba\u654c\u624b\uff0c\u9ed1\u9b54\u65f6\u5bc5\u53c8\u88ab\u970d\u5143\u4f3d\u3001\u949f\u4e00\u8c6a\u3001\u9ea6\u5c0f\u660e\u4e09\u4eba\u51fb\u4f24\uff0c\u81f3\u4e8e \u725b\u5954\uff0c\u53ea\u6015\u4ec5\u662f\u4e2a\u8349\u5305\u89d2\u8272\u3002 \u3000\u3000\u5982\u6b64\u770b\u6765\uff0c\u8fd8\u662f\u85cf\u62d9\u7684\u597d\uff0c\u4e5f\u514d\u5f97\u56e0\u800c\u6bc1\u4e86\u4e00\u4e16\u201c\u82f1\u540d\u201d\u3002 \u3000\u3000\u60f3\u5230\u8fd9\u91cc\uff0c\u5e72\u54b3\u4e86\u4e24\u58f0\u9053\uff1a\u201c\u65e2\u7136\u82b3\u9a7e\u81ea\u5fd6\u795e\u52c7\u65e0\u654c\uff0c\u90a3\u5c31\u514d\u5f97\u518d\u4f24\u548c\u6c14\u3002\u4f9d\u8001\u592b\u4e4b \u610f\uff0e\u5c31\u7528\u4e0d\u7740\u518d\u6bd4\u4e86\u3002\u201d \u3000\u3000\u7f01\u8863\u4eba\u4f3c\u4e4e\u4e5f\u4e0d\u613f\u518d\u7ed9\u9634\u624b\u4e00\u9b54\u96be\u770b\uff0c\u9ed8\u4e86\u4e00\u9ed8\u9053\uff1a\u201c\u597d\u5427\uff01\u8fd9\u6b21\u6211\u5c31\u53d1\u53d1\u6148\u60b2\uff0c\u653e\u4f60 \u4eec\u4e00\u6761\u751f\u8def\uff0c\u4f60\u4eec\u8d70\u5427\uff01\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u7b49\u4eba\u53ea\u89c9\u559c\u51fa\u671b\u5916\uff0c\u6ca1\u60f3\u5230\u7f01\u8863\u4eba\u5c45\u7136\u80af\u7ed9\u4ed6\u4eec\u2014\u6761\u751f\u8def\u3002 \u3000\u3000\u7f01\u8863\u4eba\u8bf4\u5b8c\u8bdd\uff0c\u968f\u5373\u53c8\u6325\u51fa\u624b\u89e3\u5f00\u4e86\u6bd2\u706b\u6210\u5168\u7684\u7a74\u9053\u3002 \u3000\u3000\u9634\u624b\u2014\u9b54\u5374\u53c8\u56de\u5230\u5927\u95e8\u524d\u2014\u628a\u6293\u8d77\u4ecd\u5728\u660f\u8ff7\u4e2d\u7684\u82d7\u7d20\u5170\u3002 \u3000\u3000\u7f01\u8863\u4eba\u559d\u9053\uff1a\u201c\u4f60\u8981\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u9634\u624b\u2014\u9b54\u9053\uff1a\u201c\u8fd9\u8d31\u5a62\u662f\u8001\u4eba\u7684\u95e8\u4e0b\u5f1f\u5b50\uff0c\u8001\u592b\u8981\u628a\u5979\u5e26\u8d70\u3002\u201d \u3000\u3000\u7f01\u8863\u4eba\u53f1\u9053\uff1a\u201c\u5979\u65e2\u5df2\u5728\u4e09\u5e74\u524d\u4fbf\u6295\u5f52\u8ff7\u8e2a\u8c37\uff0c\u4fbf\u662f\u8ff7\u8e2a\u8c37\u7684\u4eba\uff0c\u4f60\u51ed\u4ec0\u4e48\u628a\u5979\u5e26 \u8d70\uff1f\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u970d\u5143\u4f3d\u548c\u9ea6\u5c0f\u660e\u4e5f\u65e9\u5df2\u8dc3\u8fd1\u524d\u6765\uff0c\u5927\u6709\u7acb\u5373\u5411\u9634\u624b\u4e00\u9b54\u51fa\u624b\u4e4b\u52bf\u3002 \u3000\u3000\u9634\u624b\u4e00\u9b54\u51ed\u85c9\u5df2\u5931\uff0c\u5373\u4f7f\u6ca1\u6709\u7f01\u8863\u4eba\u5728\u65c1\uff0c\u4ed6\u4e5f\u4e0d\u6562\u518d\u5728\u7fa4\u96c4\u9762\u524d\u901e\u5f3a\uff0c\u53ea\u597d\u653e\u4e0b\u82d7\u7d20 \u5170\u9053\uff1a\u201c\u4e0d\u51c6\u5e26\u54b1\u5c31\u4e0d\u5e26\u3002\u201d \u3000\u3000\u63a5\u7740\u62db\u547c\u6bd2\u706b\u6210\u5168\u7b49\u56db\u4eba\u9053\uff1a\u201c\u4f60\u4eec\u4e0d\u8d70\u8fd8\u7b49\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u7a81\u89c1\u970d\u5143\u4f3d\u51b2\u4e0a\u524d\u6765\u9053\uff1a\u201c\u8ff7\u8e2a\u8c37\u4e0d\u662f\u8bf4\u8fdb\u5c31\u8fdb\uff0c\u8bf4\u8d70\u5c31\u8d70\u7684\u5730\u65b9\uff0c\u73b0\u5728\u970d\u67d0\u8be5\u548c\u4f60\u4eec \u7b97\u6e05\u8fd9\u7b14\u8d26\u4e86\uff01\u201d \u3000\u3000\u7f01\u8863\u4eba\u51b7\u58f0\u9053\uff1a\u201c\u6211\u5df2\u7ecf\u7b54\u5e94\u653e\u4ed6\u4eec\u8d70\uff0e\u4efb\u4f55\u4eba\u4e0d\u5f97\u62e6\u963b\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u62b1\u62f3\u4e00\u793c\u9053\uff1a\u201c\u5973\u795e\u4ed9\uff0c\u4ed6\u4eec\u65e0\u7f18\u65e0\u6545\u95ef\u8fdb\u8ff7\u8e2a\u8c37\uff0c\u8ff7\u8e2a\u8c37\u9669\u4e9b\u6bc1\u4e8e\u4e00\u65e6\uff0c\u600e\u53ef \u8f7b\u6613\u628a\u4ed6\u4eec\u653e\u8d70\uff01\u201d \u3000\u3000\u7f01\u8863\u4eba\u9053\uff1a\u201c\u7528\u4e0d\u7740\u591a\u8bf4\uff0c\u6211\u8bf4\u653e\u4ed6\u4eec\u8d70\uff0c\u5c31\u653e\u4ed6\u4eec\u8d70\uff01\u201d \u3000\u3000\u5979\u7684\u8bed\u8c03\u867d\u7136\u5341\u5206\u5e73\u548c\uff0c\u4f46\u5374\u9690\u542b\u7740\u903c\u4eba\u7684\u5a01\u4eea\u3002 \u3000\u3000\u970d\u5143\u4f3d\u54ea\u6562\u8fdd\u6297\uff0c\u8fde\u6574\u4e2a\u8ff7\u8e2a\u8c37\u7684\u547d\u8fd0\u90fd\u662f\u7f01\u8863\u4eba\u633d\u6551\u7684\uff0c\u5979\u65e2\u51b3\u5fc3\u628a\u9634\u624b\u2014\u9b54\u653e\u8d70\uff0c \u81ea\u5df1\u81ea\u7136\u4e5f\u4e0d\u6562\u518d\u8bf4\u4ec0\u4e48\u3002 \u3000\u3000\u7fa4\u96c4\u4eec\u773c\u89c1\u9634\u624b\u4e00\u9b54\u4e94\u4eba\u53bb\u8fdc\uff0c\u624d\u9f50\u9f50\u56f4\u62e2\u4e0a\u6765\u5411\u7f01\u8863\u4eba\u79f0\u8c22\u3002 \u3000\u3000\u6b64\u65f6\u7684\u7fa4\u96c4\uff0c\u5185\u5fc3\u4e2d\u5df2\u771f\u7684\u628a\u7f01\u8863\u4eba\u89c6\u4e3a\u5973\u795e\u4ed9\uff0c\u5bf9\u5979\u83ab\u4e0d\u65e2\u754f\u4e14\u656c\uff0c\u53c8\u5145\u6ee1\u611f\u6fc0\u4e4b \u60c5\u3002 \u3000\u3000\u7f01\u8863\u4eba\u7f13\u7f13\u95ee\u9053\uff1a\u201c\u5148\u524d\u5171\u6709\u51e0\u4eba\u88ab\u91d1\u8695\u6bd2\u866b\u54ac\u4e2d\u7684\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u4e00\u5171\u516b\u4f4d\u3002\u4ed6\u4eec\u5168\u5728\u5927\u5385\u91cc\u4e0d\u80fd\u51fa\u6765\uff0e\u770b\u6837\u5b50\u60c5\u51b5\u975e\u5e38\u4e25\u91cd\u3002\u201d \u3000\u3000\u201c\u4f60\u4eec\u53ef\u6709\u7597\u6cbb\u4e4b\u6cd5\uff1f\u201d \u3000\u3000\u201c\u866b\u6bd2\u4e43\u82d7\u7586\u4e4b\u7269\uff0c\u6570\u5341\u5e74\u6765\u4ece\u672a\u5728\u4e2d\u539f\u7eff\u6797\u51fa\u73b0\uff0c\u8ff7\u8e2a\u8c37\u867d\u6709\u4e0d\u5c11\u89e3\u6bd2\u836f\u7269\uff0c\u5374\u72ec\u7f3a \u866b\u6bd2\u89e3\u836f\u3002\u201d \u3000\u3000\u7f01\u8863\u4eba\u7f13\u7f13\u4ece\u6000\u91cc\u638f\u51fa\u4e00\u53ea\u7eff\u8272\u7389\u74f6\u9053\uff1a\u201c\u6211\u8fd9\u4e00\u74f6\u4e39\u4e38\uff0c\u53ef\u89e3\u767e\u6bd2\uff0c\u76f8\u4fe1\u5bf9\u866b\u6bd2\u4e5f\u5fc5 \u6709\u6cbb\u7597\u4e4b\u6548\uff0c\u62ff\u53bb\u8bd5\u8bd5\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u8fde\u5fd9\u8eac\u8eab\u63a5\u8fc7\uff0e\u8f6c\u624b\u4ea4\u4e8e\u641c\u9b42\u624b\u5df4\u5929\u4e49\u9053\uff1a\u201c\u5feb\u5230\u5927\u5385\u5582\u4ed6\u4eec\u670d\u4e0b\uff01\u201d \u3000\u3000\u5df4\u5929\u4e49\u5e94\u58f0\u800c\u53bb\u3002 \u3000\u3000\u7f01\u8863\u4eba\u4ef0\u671b\u4e86\u4e00\u4e0b\u5929\u8272\uff0c\u957f\u957f\u51fa\u53e3\u6c14\u9053\uff1a\u201c\u6211\u8be5\u8d70\u4e86\uff01\u201d \u3000\u3000\u970d\u5143\u4f43\u5fd9\u9053\uff1a\u201c\u8fd8\u8bf7\u5973\u795e\u4ed9\u7559\u4e0b\u82b3\u540d\uff01\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u8981\u7559\u540d\u5462\uff1f\u201d \u3000\u3000\u201c\u970d\u67d0\u51c6\u5907\u628a\u5973\u795e\u4ed9\u7684\u957f\u751f\u813e\u4f4d\uff0c\u4f9b\u5949\u5728\u5927\u5385\u91cc\uff0c\u4ee5\u4fbf\u8ba9\u8ff7\u8e2a\u8c37\u6240\u6709\u7684\u4eba\u671d\u5915\u53c2\u62dc\u3002\u201d \u3000\u3000\u201c\u503c\u5f97\u8fd9\u4e48\u9686\u91cd\u5417\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u503c\u5f97\uff0c\u4eca\u665a\u82e5\u6ca1\u6709\u5973\u795e\u4ed9\u65bd\u4ee5\u63f4\u624b\uff0c\u970d\u67d0\u4ee5\u53ca\u5728\u573a\u6240\u6709\u7684\u4eba\uff0c\u53ea\u6015\u90fd\u96be\u9003\u6d3b\u547d\u3002 \u8fde\u8ff7\u8e2a\u8c37\u8fd9\u7eff\u6797\u603b\u5be8\u4e5f\u5c06\u4e0d\u518d\u5b58\u5728\u3002\u5927\u6069\u5927\u5fb7\uff0c\u970d\u67d0\u600e\u80fd\u4e0d\u6c38\u94ed\u4e94\u5185\uff0c\u4ee5\u671f\u4ed6\u65e5\u7b54\u62a5\uff01\u201d \u3000\u3000\u7f01\u8863\u4eba\u6de1\u6de1\u4e00\u7b11\u9053\uff1a\u201c\u6211\u5e76\u975e\u56fe\u62a5\u800c\u6765\uff0c\u970d\u76df\u4e3b\u6700\u597d\u522b\u628a\u8fd9\u4e8b\u653e\u5728\u5fc3\u4e0a\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u8083\u5bb9\u9053\uff1a\u201c\u970d\u67d0\u6073\u6c42\u5973\u795e\u4ed9\u52a1\u5fc5\u7559\u4e0b\u5927\u540d\uff0c\u6216\u8005\u544a\u77e5\u5c0a\u5740\u662f\u5728\u4f55\u5904\u4ed9\u5c71\uff0c\u82e5\u5927\u6069 \u4e0d\u62a5\uff0c\u5b9e\u5728\u95ee\u5fc3\u6709\u6127\u3002\u201d \u3000\u3000\u7f01\u8863\u4eba\u8bed\u6c14\u575a\u5b9a\u5730\u9053\uff1a\u201c\u4e0d\u5fc5\u591a\u8bf4\uff0c\u6211\u8d70\u4e86\uff01\u201d \u3000\u3000\u8bf4\u5b8c\u8bdd\uff0c\u8f6c\u8eab\u98d8\u7136\u800c\u53bb\uff0e \u3000\u3000\u970d\u5143\u4f3d\u5728\u540e\u7d27\u8ddf\u7740\u9053\uff1a\u201c\u970d\u67d0\u606d\u9001\u5973\u795e\u4ed9\u5230\u8c37\u5916\u3002\u201d \u3000\u3000\u7f01\u8863\u4eba\u5934\u4e5f\u4e0d\u56de\u9053\uff1a\u201c\u4e0d\u7528\u4e86\uff01\u201d \u3000\u3000\u5979\u8bdd\u58f0\u672a\u6bd5\uff0c\u7a81\u7136\u817e\u8eab\u800c\u8d77\uff0c\u76f4\u5347\u8d77\u4e03\u516b\u4e08\u9ad8\uff0c\u7136\u540e\u5e73\u5c04\u800c\u53bb\uff0e\u8f6c\u77ac\u95f4\u4fbf\u5df2\u5728\u591c\u7a7a\u4e2d\u6d88 \u901d\u3002 \u3000\u3000\u4ee5\u949f\u4e00\u8c6a\u4e3a\u9996\u7684\u4e00\u4f19\u4eba\u56de\u5230\u201c\u67cf\u9f84\u9662\u201d\uff0c\u5e76\u672a\u7acb\u5373\u5c31\u5bdd\uff0e\u81ea\u52a8\u805a\u96c6\u5728\u5ba2\u5385\u91cc\u8c08\u8bba\u53d1\u751f\u7684 \u4e8b\u3002 \u3000\u3000\u4ed6\u4eec\u83ab\u4e0d\u4e3a\u7f01\u8863\u4eba\u7684\u76d6\u4e16\u795e\u529f\u6240\u60ca\uff0c\u5f53\u7136\u4e5f\u628a\u8bdd\u9898\u96c6\u4e2d\u5728\u7f01\u8863\u4eba\u8eab\u4e0a\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u559d\u4e86\u4e00\u53e3\u8336\u9053\uff1a\u201c\u4f59\u5144\u4e00\u5411\u9605\u5386\u6700\u4e30\uff0c\u53ef\u77e5\u8fd9\u4f4d\u5973\u795e\u4ed9\u7a76\u7adf\u4ec0\u4e48\u8eab\u4efd\u6765\u5386\uff1f\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u6447\u5934\u9053\uff1a\u201c\u5144\u5f1f\u81ea\u4fe1\u5bf9\u5f53\u4ee3\u524d\u8f88\u9ad8\u4eba\uff0c\u89c1\u8bc6\u8fc7\u7684\u867d\u7136\u4e0d\u80fd\u7b97\u591a\uff0c\u4f46\u95fb\u542c\u8fc7\u7684\u5374\u4e0d \u5728\u5c11\u6570\u3002\u5374\u4ece\u6ca1\u542c\u8bf4\u8fc7\u6b66\u6797\u4e2d\u6709\u8fd9\u4e48\u4e00\u4f4d\u6b66\u529f\u60ca\u4e16\u9a87\u4fd7\u7684\u5973\u9ad8\u4eba\u3002\u201d \u3000\u3000\u5ffd\u89c1\u5d02\u5c71\u4e09\u96c4\u7684\u738b\u5927\u5eb7\u731b\u7136\u8dfa\u4e86\u4e00\u811a\uff0c\u63a5\u7740\u53c8\u5728\u81ea\u5df1\u8138\u4e0a\u91cd\u91cd\u7684\u63b4\u4e86\u2014\u5df4\u638c\uff0c\u7adf\u7136\u6253\u5f97 \u8fde\u5634\u89d2\u90fd\u6d41\u51fa\u8840\u6765\u3002 \u3000\u3000\u8fd9\u4e3e\u52a8\u5b9e\u5728\u6709\u4e9b\u7a81\u5140\uff0c\u7b80\u76f4\u50cf\u5ffd\u7136\u53d1\u4e86\u75af\uff0c\u53ea\u770b\u5f97\u5728\u573a\u7684\u4eba\u65e2\u60ca\u6115\u53c8\u83ab\u540d\u5176\u5999\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u8f7b\u54b3\u4e86\u58f0\u9053\uff1a\u201c\u738b\u5144\u8fd9\u662f\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u738b\u5927\u5eb7\u5598\u4e86\u53e3\u5927\u6c14\u9053\uff1a\u201c\u4ffa\u4ece\u524d\u53ea\u9053\u81ea\u5df1\u7684\u6b66\u529f\u5df2\u7ecf\u5dee\u4e0d\u591a\uff0c\u4eca\u665a\u770b\u5230\u5973\u795e\u4ed9\uff0c\u624d\u77e5\u9053 \u4eba\u5916\u6709\u4eba\uff0c\u5929\u5916\u6709\u5929\u3002\u770b\u6765\u7eff\u6797\u8fd9\u53e3\u996d\uff0c\u4ffa\u4e5f\u522b\u60f3\u5403\u5566\uff01\u5e72\u8106\u56de\u5d02\u5c71\u8001\u5bb6\u62b1\u5b69\u5b50\u5427\uff01\u201d \u3000\u3000\u4ed6\u7684\u8fd9\u756a\u8bdd\uff0c\u542c\u5f97\u5728\u573a\u7684\u4eba\u53c8\u597d\u7b11\u53c8\u611f\u6168\u4e0d\u5df2\u3002 \u3000\u3000\u7b11\u9762\u4f5b\u9c8d\u8d85\u7528\u624b\u5fd6\u8f7b\u649e\u4e86\u738b\u5927\u5eb7\u4e00\u4e0b\u9053\uff1a\u201c\u8001\u4e8c\uff0c\u4f60\u75af\u4e86\u5417\uff1f\u201d \u3000\u3000\u738b\u5927\u5eb7\u9053\uff1a\u201c\u4ffa\u5c31\u662f\u56e0\u4e3a\u6ca1\u75af\uff0c\u624d\u8bb2\u51fa\u8fd9\u79cd\u771f\u5fc3\u8bdd\u6765\uff01\u201d \u3000\u3000\u949f\u4e00\u8c6a\u62c5\u5fc3\u738b\u5927\u5eb7\u5f53\u771f\u4f1a\u85c9\u9152\u88c5\u75af\uff0c\u8fde\u5fd9\u8d77\u8eab\u9053\uff1a\u201c\u65f6\u95f4\u4e0d\u65e9\uff0e\u6015\u5df2\u662f\u4e09\u66f4\u5929\u4e86\u3002\u4eca\u665a \u5404\u4f4d\u90fd\u53d7\u4e86\u60ca\uff0e\u8fd8\u662f\u5feb\u5feb\u56de\u623f\u4f11\u606f\u5427\uff01\u201d \u3000\u3000\u4f17\u4eba\u521a\u8981\u8d77\u8eab\u56de\u623f\uff0c\u5374\u542c\u9ea6\u5c0f\u660e\u9ad8\u53eb\u9053\uff1a\u201c\u522b\u8d70\uff0c\u6211\u8fd8\u6709\u8bdd\u8bb2\uff01\u201d \u3000\u3000\u949f\u4e00\u8c6a\u2014\u6123\u9053\uff1a\u201c\u9ea6\u8001\u5f1f\u53c8\u8981\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4f60\u4eec\u4e0d\u662f\u60f3\u77e5\u9053\u5973\u795e\u4ed9\u7684\u8eab\u4efd\u6765\u5386\u5417\uff1f\u73b0\u5728\u6211\u5c31\u5411\u5927\u5bb6\u8bf4\u660e\u3002\u201d \u3000\u3000\u949f\u4e00\u8c6a\u76b1\u4e86\u76b1\u7709\u9053\uff1a\u201c\u65f6\u95f4\u5df2\u7ecf\u4e0d\u65e9\u4e86\uff0c\u4f60\u522b\u518d\u5f00\u73a9\u7b11\u597d\u4e0d\u597d\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u4e24\u773c\u4e00\u77aa\u9053\uff1a\u201c\u8c01\u8ddf\u4f60\u4eec\u5f00\u73a9\u7b11\uff1f\u201d \u3000\u3000\u949f\u4e00\u8c6a\u53ea\u597d\u8010\u7740\u6027\u5b50\u9053\uff1a\u201c\u4f60\u600e\u4e48\u4f1a\u77e5\u9053\u5973\u795e\u4ed9\u7684\u8eab\u4efd\u6765\u5386\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u7b11\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u6211\u6bd4\u4f60\u4eec\u89c1\u591a\u8bc6\u5e7f\u4e4b\u5904\u3002\u522b\u770b\u4f60\u4eec\u90fd\u6709\u6c5f\u6e56\u9605\u5386\uff0e\u53ef\u60dc\u9605\u5386\u7684\u4e0d \u662f\u5730\u65b9\uff0e\u6211\u867d\u7136\u6c5f\u6e56\u9605\u5386\u4e0d\u591a\uff0c\u4f46\u5374\u4e00\u9605\u5386\u5c31\u9605\u5386\u4e0a\u5566\uff0c\u8fd9\u5c31\u53eb\u8d76\u5f97\u65e9\u4e0d\u5982\u8d76\u5f97\u5de7\uff01\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u6b63\u8272\u9053\uff1a\u201c\u77e5\u4e4b\u4e3a\u77e5\u4e4b\uff0c\u4e0d\u77e5\u4e3a\u4e0d\u77e5\uff0c\u662f\u77e5\u4e5f\u3002\u9ea6\u5c0f\u5144\u5f1f\u4e5f\u522b\u5b58\u5fc3\u800d\u6211\u4eec\u5927 \u5bb6\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4f60\u4eec\u90fd\u662f\u81ea\u5df1\u4eba\uff0c\u53c8\u6709\u4ec0\u4e48\u597d\u800d\u7684\uff1f\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u4f60\u5c31\u8bb2\u51fa\u6765\u5927\u5bb6\u542c\u542c\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u671b\u4e86\u4f17\u4eba\u4e00\u773c\u9053\uff1a\u201c\u4f60\u4eec\u6709\u7684\u5df2\u7ecf\u79bb\u5e2d\uff0c\u6211\u8bb2\u7ed9\u8c01\u542c\uff0c\u4f60\u4eec\u8981\u542c\u5c31\u56de\u6765\u91cd\u65b0\u5750 \u597d\uff01\u201d \u3000\u3000\u4f17\u4eba\u90fd\u4e0d\u6562\u5f97\u7f6a\u9ea6\u5c0f\u660e\uff0e\u542c\u4ed6\u8fd9\u6837\u4e00\u8bf4\uff0c\u90a3\u4e9b\u79bb\u5f00\u5ea7\u4f4d\u7684\uff0e\u53ea\u5f97\u518d\u56de\u6765\u5750\u597d\u3002 \u3000\u3000\u8c01\u77e5\u9ea6\u5c0f\u660e\u7adf\u7aef\u4e86\u8d77\u6765\uff0c\u50cf\u82e5\u65e0\u5176\u4e8b\u822c\uff0c\u4e45\u4e45\u4e0d\u66fe\u5f00\u53e3\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u50ac\u4fc3\u7740\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u53c8\u4e0d\u8bb2\u4e86\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u522b\u6025\uff01\u8fd9\u91cc\u9762\u6709\u7f18\u770b\u5230\u5973\u795e\u4ed9\u7684\uff0c\u53ea\u6709\u4e07\u59d1\u5a18\u3002\u201d \u3000\u3000\u8fd9\u53e5\u6ca1\u5934\u6ca1\u8111\u7684\u8bdd\uff0e\u66f4\u662f\u5f04\u5f97\u6240\u6709\u7684\u4eba\u557c\u7b11\u7686\u975e\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u53c8\u671b\u4e86\u4f17\u4eba\u4e00\u773c\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u662f\u5426\u5df2\u88ab\u6211\u7684\u8bdd\u5f04\u8ff7\u7cca\u4e86\uff1f\u73b0\u5728\u6211\u5c31\u5f00\u59cb\u8bb2 \u5566\uff01\u201d \u3000\u3000\u4f17\u4eba\u4e0d\u7ea6\u800c\u540c\u628a\u76ee\u5149\u96c6\u4e2d\u5728\u9ea6\u5c0f\u660e\u8138\u4e0a\uff0c\u770b\u4ed6\u7a76\u7adf\u5728\u800d\u4ec0\u4e48\u82b1\u62db\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u4e00\u5b57\u2014\u5b57\u7684\u9053\uff1a\u201c\u8fd9\u4f4d\u5973\u795e\u4ed9\u5c31\u662f\u5728\u77f3\u5cad\u5173\u548c\u5b9a\u8944\u4e4b\u95f4\u5c71\u4e0b\u5c3c\u5eb5\u91cc\u7684\u90a3\u4f4d\u8001\u5c3c\uff0c \u524d\u51e0\u5929\u6211\u60f3\u8bf7\u4e07\u59d1\u5a18\u966a\u6211\u4e00\u540c\u524d\u53bb\uff0c\u662f\u4e0d\u662f\u4e07\u59d1\u5a18\u6709\u7f18\u89c1\u5230\u5979\u8001\u4eba\u5bb6\uff1f\u201d \u3000\u3000\u539f\u6765\uff0c\u9ea6\u5c0f\u660e\u65e9\u5c31\u770b\u51fa\u7f01\u8863\u4eba\u5c31\u662f\u90a3\u4f4d\u8001\u5c3c\uff0c\u6700\u521d\u5728\u5927\u5385\u5185\u4ed6\u5df2\u542c\u51fa\u58f0\u97f3\u5f88\u8033\u719f\uff0c\u53ca\u81f3 \u51fa\u4e86\u5927\u5385\uff0c\u867d\u7136\u591c\u8272\u6726\u80e7\uff0c\u4ee5\u4ed6\u8fc7\u4eba\u76ee\u529b\uff0c\u7acb\u5373\u5c31\u4f9d\u7a00\u8ba4\u51fa\u5bf9\u65b9\u662f\u8c01\uff0e\u53ea\u662f\u5f53\u65f6\u4e0d\u4fbf\u9053\u7834\u800c \u5df2\uff0e \u3000\u3000\u4ed6\u4e4b\u6240\u4ee5\u4e0d\u613f\u5f53\u573a\u9053\u7834\u7f01\u8863\u4eba\u8eab\u4efd\uff0c\u6b63\u662f\u4ed6\u7684\u673a\u8b66\u8fc7\u4eba\u4e4b\u5904\u3002 \u3000\u3000\u56e0\u4e3a\u4e00\u65e6\u5f53\u573a\u9053\u7834\uff0c\u970d\u5143\u4f3d\u65e5\u540e\u5fc5\u5b9a\u4f1a\u5230\u5c3c\u5eb5\u53bb\u627e\u5230\u5979\u3002\u8fd9\u5bf9\u81ea\u5df1\u548c\u949f\u4e00\u8c6a\u2014\u65b9\uff0c\u65e0\u7591 \u662f\u4ef6\u5927\u5927\u4e0d\u5229\u7684\u4e8b\u3002 \u3000\u3000\u53e6\u5916\uff0e\u4ed6\u4e0d\u80fd\u4e0d\u987e\u8651\u56e0\u800c\u60f9\u8d77\u7f01\u8863\u4eba\u7684\u53cd\u611f\uff0c\u90a3\u4e48\u5c06\u6765\u518d\u5230\u5c3c\u5eb5\u89c1\u5979\uff0c\u4e8b\u60c5\u5c31\u66f4\u96be\u529e \u4e86\u3002 \u3000\u3000\u5ba2\u5385\u5185\u7684\u6c14\u6c1b\u987f\u65f6\u9759\u8083\u8d77\u6765\uff0c\u6c89\u5bc2\u4e86\u5f88\u4e45\uff0c\u624d\u542c\u4f59\u4ea6\u4e50\u9053\uff1a\u201c\u4f60\u53ef\u770b\u6e05\u4e86\u662f\u5979\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u7edd\u5bf9\u6ca1\u9519\uff01\u5982\u679c\u9519\u4e86\uff0c\u6211\u5c31\u60c5\u613f\u8d4c\u4e0a\u8fd9\u9897\u8111\u888b\uff01\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u9053\uff1a\u201c\u8a00\u91cd\u4e86\uff0c\u770b\u51c6\u4e86\u5c31\u597d\uff0e\u201d \u3000\u3000\u63a5\u7740\u8f6c\u5934\u9053\uff1a\u201c\u526f\u76df\u4e3b\uff0c\u662f\u5426\u8bf7\u9ea6\u5c0f\u5144\u5f1f\u9a6c\u4e0a\u5230\u5c3c\u5eb5\u53bb\uff1f\u201d \u3000\u3000\u949f\u4e00\u8c6a\u7565\u4e00\u6c89\u541f\u9053\uff1a\u201c\u65e2\u7136\u770b\u51c6\u4e86\u662f\u5979\uff0c\u5f53\u7136\u8be5\u53bb\uff0c\u8fd9\u5bf9\u54b1\u4eec\u662f\u6709\u597d\u5904\u7684\uff0c\u4e0d\u8fc7\u6700\u597d\u7b49 \u8fc7\u4e86\u660e\u5929\u518d\u8d70\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u8981\u7b49\u8fc7\u4e86\u660e\u5929\u518d\u8d70\uff1f\u201d \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u8ff7\u8e2a\u8c37\u4eca\u665a\u7ecf\u8fc7\u8fd9\u6b21\u5927\u53d8\uff0c\u660e\u5929\u970d\u5143\u4f3d\u5fc5\u5b9a\u6709\u91cd\u5927\u5b89\u6392\uff0c\u4f60\u5fc5\u987b\u5728\u573a\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u6211\u5728\u4e0d\u5728\u573a\uff0c\u6709\u4ec0\u4e48\u5173\u7cfb\uff1f\u201d \u3000\u3000\u949f\u2014\u8c6a\u9053\uff1a\u201c\u4f60\u73b0\u5728\u662f\u950b\u5934\u4e0a\u7684\u4eba\u7269\uff0c\u5728\u970d\u5143\u4f3d\u773c\u4e2d\uff0c\u8fde\u6211\u8fd9\u526f\u76df\u4e3b\u4e5f\u6ca1\u4f60\u91cd\u8981\u3002\u82e5\u4f60 \u4e0d\u5728\uff0c\u52bf\u5fc5\u5f15\u8d77\u4ed6\u7684\u7591\u5fc3\uff0c\u8fd9\u5bf9\u6211\u4eec\u662f\u4ef6\u5927\u5927\u4e0d\u5229\u7684\u4e8b\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u60f3\u4e86\u60f3\u9053\uff1a\u201c\u4f60\u8bf4\u5f97\u679c\u7136\u6709\u7406\uff0c\u6211\u660e\u5929\u4e0d\u8d70\u5c31\u662f\u5566\uff01\u201d \u3000\u3000\u949f\u4e00\u8c6a\u968f\u5373\u5ba3\u5e03\u5404\u4eba\u56de\u623f\u4f11\u606f\u3002 \u3000\u3000\u6b21\u65e5\uff0c\u970d\u5143\u4f3d\u679c\u7136\u53c8\u628a\u7fa4\u96c4\u53ec\u96c6\u5728\u805a\u4e49\u5385\u8bae\u4e8b\uff0c\u5e76\u91cd\u65b0\u89c4\u5b9a\u4e86\u5f88\u591a\u4e8b\u60c5\u3002 \u3000\u3000\u5c31\u5728\u5f53\u65e5\u665a\u95f4\uff0c\u9ea6\u5c0f\u660e\u548c\u4e07\u6620\u971e\u6084\u6084\u79bb\u5f00\u4e86\u8ff7\u8e2a\u8c37\uff0c\u6587\u5929\u751f\u5219\u56e0\u6545\u88ab\u949f\u4e00\u8c6a\u7559\u4e0b\u3002 \u3000\u3000\u4e3a\u4e86\u4e0d\u60ca\u52a8\u970d\u5143\u4f3d\u7684\u8033\u76ee\uff0c\u949f\u4e00\u8c6a\u7b49\u4eba\u90fd\u53ea\u9001\u5230\u201c\u67cf\u9f84\u9662\u201d\u95e8\u53e3\uff0e\u552f\u6709\u6587\u5929\u751f\u76f4\u628a\u4e24\u4eba \u9001\u51fa\u8c37\u5916\u3002 \u3000\u3000\u6587\u5929\u751f\u7684\u7528\u610f\uff0c\u9ea6\u5c0f\u660e\u5f53\u7136\u660e\u767d\u3002 \u3000\u3000\u6587\u5929\u751f\u81ea\u5e7c\u548c\u4e07\u6620\u971e\u9752\u6885\u7af9\u9a6c\u4e00\u8d77\u957f\u5927\uff0c\u4e07\u6620\u971e\u7684\u7236\u4eb2\u4e07\u6653\u5149\u4e34\u7ec8\u65f6\u53c8\u628a\u5973\u513f\u7684\u7ec8\u8eab\u6258 \u4ed8\u7ed9\u4ed6\u3002 \u3000\u3000\u4ed6\u5355\u72ec\u628a\u4e24\u4eba\u9001\u5230\u8c37\u5916\uff0c\u9664\u4e86\u662f\u5bf9\u4e07\u6620\u971e\u8868\u793a\u5fc3\u610f\u5916\uff0c\u4fbf\u662f\u6697\u793a\u9ea6\u5c0f\u660e\uff0c\u5e0c\u671b\u5c0f\u660e\u4e0d\u53ef \u505a\u51fa\u6697\u5ba4\u6b3a\u5fc3\u4e4b\u4e8b\u3002 \u3000\u3000\u6587\u5929\u751f\u56de\u8c37\u540e\uff0c\u9ea6\u5c0f\u660e\u4e8c\u4eba\u5f53\u665a\u4fbf\u5bbf\u5728\u5c71\u4e0b\u4e00\u5904\u6c11\u5bb6\uff0c\u6c11\u5bb6\u53ea\u6709\u4e00\u95f4\u7a7a\u5c4b\uff0c\u4e24\u4eba\u867d\u7136\u540c \u5c45\u2014\u5ba4\uff0c\u5374\u4ecd\u7136\u4fdd\u6301\u7740\u76f8\u5f53\u8ddd\u79bb\u3002 \u5367\u9f99\u751f\u300a\u5999\u7edd\u5929\u9999\u300b \u7b2c\u5341\u4e8c\u56de\u3000\u72ed\u8def\u76f8\u9022 \u3000\u3000\u5176\u5b9e\uff0c\u4e07\u6620\u971e\u6b64\u6b21\u968f\u540c\u9ea6\u5c0f\u660e\u51fa\u8c37\uff0e\u5b9e\u5728\u662f\u4ef6\u8feb\u4e0d\u5f97\u5df2\u7684\u4e8b\u3002\u5979\u867d\u7136\u5e74\u7eaa\u6bd4\u9ea6\u5c0f\u660e\u5927 \u4e9b\uff0c\u4f46\u6b66\u529f\u5374\u76f8\u5dee\u751a\u8fdc\u3002\u82e5\u8def\u4e0a\u9ea6\u5c0f\u660e\u8981\u5bf9\u5979\u505a\u51fa\u975e\u793c\u4e4b\u4e8b\uff0e\u5979\u6839\u672c\u65e0\u6cd5\u6297\u62d2\u3002 \u3000\u3000\u7b2c\u4e8c\u5927\u2014\u65e9\u8d77\u8eab\u7ee7\u7eed\u5357\u884c\uff0c\u4e0d\u6570\u65e5\u4fbf\u5230\u8fbe\u5b9a\u8944\u3002 \u3000\u3000\u5728\u8fd9\u51e0\u5929\u91cc\uff0e\u4e24\u4eba\u867d\u7136\u540c\u884c\u540c\u5bbf\uff0e\u9ea6\u5c0f\u660e\u5bf9\u4e07\u6620\u971e\u59cb\u7ec8\u8868\u73b0\u5f97\u89c4\u89c4\u77e9\u77e9\uff0c\u8fd9\u4f7f\u4e07\u6620\u971e\u53cd \u800c\u5bf9\u9ea6\u5c0f\u660e\u8083\u7136\u8d77\u656c\u3002 \u3000\u3000\u5979\u6bd4\u9ea6\u5c0f\u660e\u53ea\u5927\u4e00\u4e8c\u5c81\uff0c\u82e5\u4e0d\u662f\u6709\u4e2a\u81ea\u5e7c\u9752\u6885\u7af9\u9a6c\u7684\u6587\u5929\u751f\u5728\uff0e\u8bf4\u4e0d\u5b9a\u7531\u8fd9\u51e0\u5929\u7684\u671d\u5915 \u76f8\u5904\uff0c\u5979\u4f1a\u5bf9\u9ea6\u5c0f\u660e\u503e\u5fc3\u8981\u7231\u3002 \u3000\u3000\u56e0\u4e3a\u9ea6\u5c0f\u660e\u672c\u5c31\u751f\u5f97\u7709\u6e05\u76ee\u79c0\uff0c\u77eb\u77eb\u4e0d\u7fa4\uff0c\u76ee\u524d\u65e5\u6e10\u957f\u5927\u6210\u4eba\uff0c\u9664\u4e86\u6027\u60c5\u8fd8\u662f\u6840\u9a9b\u96be\u8bad \u5916\uff0c\u6ca6\u4eea\u8868\u79f0\u5f97\u4e0a\u662f\u4e30\u795e\u4fca\u9038\uff0c\u82f1\u6c14\u52c3\u52c3\uff0e\u6b63\u662f\u2014\u822c\u5c11\u5973\u4eec\u79c1\u5fc3\u7231\u6155\u7684\u5bf9\u8c61\u3002 \u3000\u3000\u7531\u5b9a\u8944\u5411\u5357\u5230\u90a3\u5ea7\u5c3c\u5eb5\uff0c\u4f30\u8ba1\u7740\u53ea\u4e8c\u4e09\u5341\u91cc\u7684\u8def\u7a0b\uff0c\u4e0d\u8fc7\u5374\u5927\u534a\u90fd\u662f\u5c71\u8def\u3002 \u3000\u3000\u6b63\u8d70\u5230\u2014\u5904\u5c71\u9e93\u5373\u5c06\u8f6c\u89d2\u5904\uff0c\u4e07\u6620\u971e\u5ffd\u7136\u4e00\u628a\u6293\u4f4f\u9ea6\u5c0f\u660e\u7684\u8863\u8896\u9053\uff1a\u201c\u5feb\u8eb2\uff01\u201d \u3000\u3000\u5979\u4e0d\u7b49\u9ea6\u5c0f\u660e\u53cd\u5e94\uff0c\u786c\u662f\u628a\u4ed6\u62c9\u5230\u8def\u65c1\u6797\u4e2d\u4e00\u68f5\u5927\u6811\u540e\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u6123\u6123\u5730\u95ee\u9053\uff1a\u201c\u8981\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u8fd9\u8bdd\u8a00\u8005\u65e0\u5fc3\uff0e\u542c\u6765\u5374\u6709\u70b9\u4e00\u6d6f\u53cc\u5173\uff0c\u4e07\u6620\u971e\u7c89\u8138\u4e00\u70ed\u9053\uff1a\u201c\u4f60\u770b\u90a3\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u672c\u6765\u4e00\u5411\u673a\u8b66\u8fc7\u4eba\uff0c\u53ea\u56e0\u65b9\u624d\u5fc3\u91cc\u5728\u60f3\u4e8b\u60c5\uff0c\u6240\u4ee5\u672a\u6ce8\u610f\u56db\u5468\u60c5\u5f62\u3002\u8fd9\u65f6\u624d\u53d1\u73b0 \u524d\u9762\u4e03\u516b\u4e08\u5916\u7684\u8f6c\u89d2\u5904\uff0c\u8fce\u9762\u6765\u4e86\u2014\u4eba\uff0c\u8d6b\u7136\u662f\u9634\u9b42\u4e0d\u6563\u7684\u9634\u624b\u4e00\u9b54\uff0e \u3000\u3000\u8fd9\u9b54\u5934\u56de\u5415\u6881\u5c71\uff0e\u591a\u534a\u4e5f\u8981\u8d70\u8fd9\u6761\u8def\uff0c\u4e0d\u8fc7\u4ed6\u662f\u5148\u4e00\u5929\u79bb\u5f00\u8ff7\u8e2a\u8c37\uff0c\u4e3a\u4ec0\u4e48\u4e5f\u624d\u8d70\u5230\u8fd9 \u91cc\uff1f \u3000\u3000\u5c24\u5176\u4ed6\u73b0\u5728\u662f\u56de\u5934\u8d70\uff0c\u66f4\u662f\u4ee4\u4eba\u4e0d\u89e3\u3002 \u3000\u3000\u9634\u624b\u4e00\u9b54\u8d70\u5f97\u6781\u6162\uff0c\u5b8c\u5168\u662f\u4e00\u526f\u6e38\u5c71\u73a9\u6c34\u6a21\u6837\uff0c\u60f3\u8d77\u4ed6\u90a3\u665a\u7684\u72fc\u72c8\uff0c\u771f\u4e0d\u77e5\u54ea\u91cc\u6765\u7684\u8fd9 \u756a\u95f2\u60c5\u9038\u81f4\u3002 \u3000\u3000\u4e07\u6620\u971e\u8fd9\u65f6\u5df2\u5fc3\u5982\u9e7f\u8df3\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u4e07\u4e00\u4ed6\u53d1\u73b0\u4e86\u54b1\u4eec\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u5982\u679c\u8eb2\u4e0d\u6389\uff0c\u90a3\u5c31\u548c\u4ed6\u786c\u62fc\u4e00\u573a\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u54b1\u4eec\u773c\u89c1\u8981\u5403\u4e8f\u7684\uff0c\u4f60\u867d\u7136\u6b66\u529f\u9ad8\uff0c\u5374\u7edd\u6597\u4e0d\u8fc7\u4ed6\u3002\u201d \u3000\u3000\u201c\u90a3\u4e5f\u4e0d\u89c1\u5f97\uff0c\u5373\u4f7f\u8f93\u7ed9\u4ed6\uff0c\u81f3\u5c11\u8fd8\u53ef\u4ee5\u81ea\u4fdd\u3002\u201d \u3000\u3000\u201c\u5982\u679c\u4ed6\u628a\u6211\u6349\u4f4f\u5462\uff1f\u201d \u3000\u3000\u201c\u8fd9\u2026\u2026\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u679c\u7136\u5f00\u59cb\u6709\u70b9\u7d27\u5f20\uff0c\u8fd9\u6b21\u4e07\u6620\u971e\u548c\u81ea\u5df1\u540c\u884c\uff0c\u662f\u4ed6\u63d0\u51fa\u7684\u8981\u6c42\uff0e\u82e5\u5728\u8def\u4e0a\u51fa\u5dee \u9519\uff0c\u4e0d\u4f46\u5bf9\u949f\u4e00\u8c6a\u3001\u6587\u5929\u751f\u7b49\u4eba\u65e0\u6cd5\u4ea4\u4ee3\uff0e\u66f4\u65e0\u6cd5\u5411\u81ea\u5df1\u7684\u826f\u5fc3\u4ea4\u4ee3\u3002 \u3000\u3000\u4f46\u504f\u504f\u5df2\u548c\u9634\u624b\u4e00\u9b54\u72ed\u8def\u76f8\u9022\uff0e\u6b64\u523b\u53ea\u80fd\u5e0c\u671b\u4e0d\u88ab\u5bf9\u65b9\u53d1\u73b0\u3002 \u3000\u3000\u5c82\u77e5\u9634\u624b\u2014\u9b54\u8d70\u8fd1\u5927\u6811\u65f6\uff0c\u5374\u5728\u8def\u4e0a\u505c\u4e86\u4e0b\u6765\uff0c\u9634\u68ee\u68ee\u7b11\u4e86\u51e0\u58f0\u9053\uff1a\u201c\u5c0f\u5b50\uff0e\u8eb2\u5c31\u8eb2\u5f97 \u4e86\u5417\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u6beb\u4e0d\u754f\u602f\uff0e\u51b7\u7b11\u4e00\u58f0\uff0c\u6602\u7136\u6765\u5230\u5927\u6811\u524d\u3002 \u3000\u3000\u9634\u624b\u4e00\u9b54\u76ae\u7b11\u8089\u4e0d\u7b11\u9053\uff1a\u201c\u771f\u5de7\uff01\u54b1\u4eec\u53c8\u9047\u4e0a\u4e86\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u7b97\u6211\u5012\u9709\uff0e\u53c8\u78b0\u4e0a\u4f60\u8fd9\u8001\u738b\u516b\u86cb\uff01\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u53cc\u988a\u731b\u5730\u62bd\u6410\u4e86\u51e0\u4e0b\u9053\uff1a\u201c\u6b7b\u5230\u4e34\u5934\uff0c\u8fd8\u6562\u9a82\u8001\u592b\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u80fd\u6328\u5728\u4e0b\u9a82\u7b97\u662f\u4f60\u7956\u4e0a\u79ef\u5fb7\uff01\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u5927\u559d\u9053\uff1a\u201c\u627e\u6b7b\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u6602\u7136\u9053\uff1a\u201c\u90a3\u4f60\u5c31\u8be5\u6253\u7740\u706f\u7b3c\uff01\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u543c\u9053\uff1a\u201c\u6df7\u86cb\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4f60\u77e5\u9053\u81ea\u5df1\u662f\u6df7\u86cb\u5c31\u597d\u3002\u201d \u3000\u3000\u9634\u624b\u2014\u9b54\u7b80\u76f4\u6c14\u70b8\u4e86\u80ba\uff0c\u518d\u543c\u9053\uff1a\u201c\u53ef\u6076\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4f60\u5c82\u6b62\u53ef\u6076\uff01\u201d \u3000\u3000\u9634\u624b\u2014\u9b54\u53ea\u6c14\u5f97\u6d51\u8eab\u53d1\u6296\uff0e\u987f\u4e86\u987f\u9053\uff1a\u201c\u628a\u90a3\u5973\u5a03\u513f\u2014\u8d77\u53eb\u51fa\u6765\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u672c\u4ee5\u4e3a\u4e07\u6620\u971e\u6ca1\u88ab\u53d1\u73b0\uff0e\u542c\u9634\u624b\u4e00\u9b54\u4e00\u53eb\uff0c\u53ea\u597d\u56de\u5934\u9053\uff1a\u201c\u4e07\u59d1\u5a18\u7528\u4e0d\u7740\u6015\uff0c\u53ea \u7ba1\u51fa\u6765\uff01\u201d \u3000\u3000\u4e07\u6620\u971e\u81ea\u77e5\u8eb2\u85cf\u4e0d\u4e86\uff0c\u53ea\u597d\u786c\u7740\u5934\u76ae\u8d70\u4e86\u51fa\u6765\u3002 \u3000\u3000\u9634\u624b\u2014\u9b54\u628a\u4e07\u6620\u971e\u4ed4\u7ec6\u7aef\u8be6\u4e86\u4e00\u9635\u9053\uff1a\u201c\u8fd9\u5973\u5a03\u513f\u8fd8\u771f\u4e0d\u9519\uff0c\u5934\u662f\u5934\uff0c\u811a\u662f\u811a\uff0c\u8001\u592b\u770b \u4e86\u6253\u5fc3\u773c\u91cc\u8212\u670d\u3002\u5973\u5a03\u513f\uff0c\u7528\u4e0d\u7740\u6015\uff0c\u53ea\u8981\u80af\u968f\u8001\u592b\u5230\u5415\u7cb1\u5c71\u4e5d\u5929\u7384\u9634\u6d1e\u5e9c\uff0c\u5c31\u6709\u4eab\u4e0d\u5c3d\u7684 \u8363\u534e\u5bcc\u8d35\uff0c\u6bd4\u5728\u8ff7\u8e2a\u8c37\u90a3\u79cd\u9b3c\u5730\u65b9\u597d\u5f97\u592a\u591a\u5566\uff01\u201d \u3000\u3000\u4e07\u6620\u971e\u65e2\u7f9e\u53c8\u6015\uff0c\u80c0\u5f97\u8138\u5e9e\u8840\u7ea2\uff0c\u6b63\u8272\u9053\uff1a\u201c\u8001\u82f1\u96c4\uff0c\u4f60\u662f\u524d\u8f88\uff0c\u8bb2\u8bdd\u6700\u597d\u89c4\u77e9\u4e9b\u3002\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u772f\u8d77\u90a3\u5bf9\u8272\u8ff7\u8ff7\u7684\u4e09\u89d2\u773c\uff0c\u563f\u563f\u7b11\u9053\uff1a\u201c\u8001\u592b\u6709\u4ec0\u4e48\u4e0d\u89c4\u77e9\u7684\uff0c\u80fd\u8ddf\u7740\u8001\u592b\uff0c \u7b97\u662f\u4f60\u7684\u798f\u6c14\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u55d4\u76ee\u559d\u9053\uff1a\u201c\u8001\u6df7\u86cb\uff01\u60f3\u4e0d\u5230\u4f60\u7adf\u4f1a\u5bf9\u4e00\u4f4d\u5e74\u8f7b\u59d1\u5a18\u5b58\u4e0b\u975e\u4efd\u4e4b\u5fc3\uff0c\u7b80\u76f4\u65e0\u803b\u900f \u9876\uff0c\u4f60\u53ef\u77e5\u9053\u8fd9\u4f4d\u59d1\u5a18\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u5979\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u5979\u662f\u4e00\u4ee3\u5927\u4fa0\u795e\u98de\u68ad\u4e07\u6653\u5149\u524d\u8f88\u7684\u5343\u91d1\uff0c\u5c82\u662f\u968f\u4fbf\u53d7\u4eba\u6b3a\u4fae\u7684\uff1f\u201d \u3000\u3000\u201c\u539f\u6765\u662f\u4e07\u6653\u5149\u7684\u5973\u513f\uff0c\u8001\u592b\u548c\u4e07\u6653\u5149\u6b63\u662f\u591a\u5e74\u8001\u53cb\uff0c\u4e07\u59d1\u5a18\u73b0\u5728\u65e2\u662f\u4e00\u540d\u5b64\u5973\uff0c\u8001\u592b \u6b63\u8be5\u591a\u7167\u5e94\u7167\u5e94\u5979\uff0c\u4e5f\u7b97\u5bf9\u5979\u7236\u4eb2\u5c3d\u4e86\u2014\u4efd\u6545\u53cb\u4e4b\u60c5\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u53f1\u9053\uff1a\u201c\u653e\u4f60\u5a18\u7684\u72d7\u5c41\uff0c\u8fd9\u8bdd\u6709\u8c01\u76f8\u4fe1\uff01\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u9661\u73b0\u6740\u673a\uff0c\u5927\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u5b50\u4e3a\u4ec0\u4e48\u53e3\u53e3\u58f0\u58f0\u4e00\u76f4\u5728\u8fb1\u9a82\u8001\u592b\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u96be\u9053\u4f60\u8fd8\u4e0d\u503c\u5f97\u9a82\uff1f\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u5df2\u4e0d\u613f\u518d\u548c\u9ea6\u5c0f\u660e\u6597\u5634\uff0c\u53ea\u89c1\u4ed6\u7a33\u7ad9\u5f53\u5730\uff0c\u80f8\u90e8\u4e00\u8d77\u4e00\u4f0f\uff0c\u9762\u8272\u4e5f\u8d8a\u6765\u8d8a\u9ed1\uff0c \u5c24\u5176\u5de6\u624b\u624b\u638c\u4e0d\u4f46\u53d1\u9ed1\uff0c\u8fd8\u5192\u51fa\u7f15\u7f15\u9ed1\u6c14\u3002 \u3000\u3000\u81f3\u4e8e\u53f3\u624b\uff0c\u56e0\u62ff\u7740\u62c2\u5c18\uff0c\u7f29\u5728\u8896\u5185\uff0e\u8fd8\u65e0\u6cd5\u770b\u51fa\u5f02\u6837\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u770b\u51fa\u5bf9\u65b9\u6b63\u5728\u51dd\u805a\u771f\u529b\uff0e\u4ee5\u4fbf\u4ee5\u9634\u98ce\u638c\u65bd\u88ad\uff0c\u968f\u5373\u4e5f\u62d4\u51fa\u957f\u5251\uff0c\u4e00\u9762\u53eb\u9053\uff1a\u201c\u4e07 \u59d1\u5a18\u5feb\u8d70\uff0e\u5f85\u4f1a\u513f\u5728\u5b9a\u8944\u6628\u665a\u6295\u5bbf\u7684\u90a3\u5bb6\u5ba2\u6808\u76f8\u4f1a\u3002\u201d \u3000\u3000\u4e07\u6620\u971e\u53f3\u624b\u6267\u5251\uff0c\u5de6\u624b\u7d27\u6263\u7740\u2014\u652f\u71d5\u5c3e\u94f6\u68ad\u9053\uff1a\u201c\u4e0d\uff01\u6211\u4e0d\u80fd\u8d70\uff0c\u81f3\u5c11\u6211\u8fd8\u53ef\u4ee5\u5728\u8fd9\u91cc \u4e3a\u4f60\u52a9\u9635\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4f60\u5feb\u8d70\uff01\u6211\u7eb5\u7136\u6253\u4e0d\u8fc7\u4ed6\uff0c\u81f3\u5c11\u8fd8\u53ef\u4ee5\u8131\u8eab\uff01\u201d \u3000\u3000\u4e07\u6620\u971e\u8bed\u6c14\u575a\u5b9a\u9053\uff1a\u201c\u6211\u4e0d\u80fd\u628a\u4f60\u7559\u4e0b\u81ea\u5df1\u8d70\uff01\u201d \u3000\u3000\u53ea\u542c\u9634\u624b\u4e00\u9b54\u53d1\u51fa\u4e00\u9635\u9634\u7b11\u58f0\u9053\uff1a\u201c\u59a4\u54c7\uff01\u539f\u6765\u4f60\u4eec\u8fd9\u5bf9\u72d7\u7537\u5973\u65e9\u5df2\u52fe\u642d\u6210\u5978\u4e86\uff0e\u96be\u602a \u5973\u5a03\u513f\u4e0d\u80af\u968f\u8001\u592b\u5230\u5415\u6881\u5c71\u4e5d\u5929\u7384\u9634\u6d1e\u5e9c\u53bb\uff0c\u5c0f\u767d\u8138\u5f53\u7136\u6bd4\u8001\u592b\u597d\u770b\u5f97\u591a\uff01\u201d \u3000\u3000\u8fd9\u4e00\u6765\u7ec8\u4e8e\u6fc0\u8d77\u4e86\u9ea6\u5c0f\u660e\u7684\u771f\u706b\uff0c\u4e07\u6620\u971e\u66f4\u662f\u7f9e\u6124\u96be\u5f53\uff0c\u3000\u2014\u54ac\u7259\uff0c\u71d5\u5c3e\u94f6\u68ad\u95ea\u7535\u822c\u5411 \u9634\u624b\u4e00\u9b54\u524d\u80f8\u63b7\u53bb\u3002 \u3000\u3000\u9634\u624b\u4e00\u9b54\u51b7\u51b7\u4e00\u7b11\uff0c\u53f3\u624b\u62c2\u5c18\u4e00\u6325\uff0c\u7acb\u5373\u628a\u71d5\u5c3e\u94f6\u68ad\u626b\u843d\uff0c\u63a5\u7740\u5de6\u624b\u53d1\u51fa\u4e00\u8bb0\u9634\u98ce\u638c\uff0e \u4e00\u80a1\u6392\u5c71\u5012\u6d77\u822c\u7684\u72c2\u98d9\u88ad\u5411\u9ea6\u5c0f\u660e\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u65e9\u5df2\u6709\u5907\uff0c\u5728\u5bf9\u65b9\u53d1\u638c\u7684\u540c\u65f6\uff0c\u4fbf\u5df2\u4fa7\u8eab\u6025\u95ea\u3002 \u3000\u3000\u867d\u662f\u5982\u6b64\uff0c\u4f46\u56e0\u5bf9\u65b9\u7684\u638c\u98ce\u7b3c\u7f69\u8303\u56f4\u592a\u5927\uff0c\u8eab\u5b50\u5de6\u4fa7\u4ecd\u88ab\u638c\u98ce\u88ad\u4e2d\uff0e \u3000\u3000\u9ea6\u5c0f\u660e\u53ea\u611f\u90a3\u638c\u98ce\u5bd2\u6f88\u808c\u9aa8\uff0c\u4e0d\u7531\u81ea\u4e3b\u8fde\u6253\u4e24\u4e2a\u5bd2\u5664\u3002 \u3000\u3000\u4e07\u6620\u971e\u867d\u7ad9\u5f97\u8f83\u8fdc\uff0c\u4e5f\u88ab\u649e\u9000\u4e94\u516d\u6b65\uff0c\u7ad9\u7a33\u4e4b\u540e\uff0c\u4ecd\u51bb\u5f97\u5a07\u9762\u53d1\u9752\uff0e \u3000\u3000\u9ea6\u5c0f\u660e\u7565\u4e00\u505c\u987f\u8fc5\u5373\u62a1\u5251\u5411\u9634\u624b\u4e00\u9b54\u653b\u53bb\u3002 \u3000\u3000\u9634\u624b\u2014\u9b54\u56e0\u518d\u5ea6\u8fd0\u96c6\u9634\u98ce\u638c\u529b\u4e0d\u6613\uff0c\u6682\u65f6\u53ea\u597d\u5c55\u5f00\u62c2\u5c18\u6325\u51fb\u3002 \u3000\u3000\u522b\u770b\u4ed6\u8fd9\u62c2\u5c18\u53ea\u662f\u968f\u8eab\u5e94\u7528\u4e4b\u7269\uff0c\u4f46\u5374\u66f4\u80dc\u4e00\u822c\u5175\u5203\uff0c\u6325\u52a8\u8d77\u6765\uff0e\u53d8\u5316\u83ab\u6d4b\uff0c\u8be1\u5f02\u591a \u7aef\uff0c\u903c\u5f97\u9ea6\u5c0f\u660e\u4e0d\u4f46\u65e0\u6cd5\u63a5\u8fd1\uff0c\u800c\u4e14\u773c\u82b1\u64a9\u4e71\uff0c\u9632\u4e0d\u80dc\u9632\u3002 \u3000\u3000\u9634\u624b\u4e00\u9b54\u9634\u7b11\u58f0\u4e2d\uff0e\u653b\u52bf\u4e5f\u8d8a\u6765\u8d8a\u5feb\uff0e \u3000\u3000\u5728\u4ed6\u9884\u6599\uff0c\u4e0d\u51fa\u4e09\u5341\u62db\uff0c\u7eb5\u4e0d\u80fd\u628a\u9ea6\u5c0f\u660e\u7f6e\u4e8e\u6b7b\u5730\uff0c\u4e5f\u5fc5\u53ef\u628a\u5bf9\u65b9\u957f\u5251\u6325\u843d\uff0c\u53ea\u8981\u9ea6\u5c0f \u660e\u5931\u53bb\u5175\u5203\uff0c\u518d\u6536\u62fe\u4ed6\u5c31\u6613\u5982\u53cd\u638c\u4e86\u3002 \u3000\u3000\u5c82\u77e5\u9ea6\u5c0f\u660e\u7684\u6b66\u529f\uff0c\u8fd9\u4e9b\u5929\u53c8\u6709\u8fdb\u5883\u3002 \u3000\u3000\u867d\u7136\u7a0d\u7a0d\u843d\u5165\u4e0b\u98ce\uff0c\u5374\u4f9d\u7136\u80fd\u4fdd\u6301\u6b7b\u7f20\u72e0\u62fc\u7684\u7cbe\u795e\uff0c\u800c\u4e14\u51fa\u624b\u72e0\u8fa3\uff0c\u5947\u62db\u8fde\u8fde\uff0c\u8feb\u5f97\u9634 \u624b\u4e00\u9b54\u4e1d\u6beb\u4e0d\u6562\u5927\u610f\u3002 \u3000\u3000\u9634\u624b\u4e00\u9b54\u8fb9\u6253\u8fb9\u6697\u6697\u5fc3\u60ca\uff0c\u5fd6\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u5c0f\u5c0f\u5e74\u7eaa\u5c31\u5982\u6b64\u96be\u7f20\uff0c\u82e5\u518d\u5047\u4ee5\u65f6\u65e5\uff0c\u53ea\u6015 \u5c31\u66f4\u96be\u5bf9\u4ed8\u4e86\u3002\u201d \u3000\u3000\u610f\u5ff5\u8f6c\u52a8\u95f4\uff0c\u8d8a\u53d1\u589e\u5f3a\u4e86\u4ed6\u5fc5\u6b32\u6740\u6b7b\u9ea6\u5c0f\u660e\u7684\u51b3\u5fc3\uff0c\u5426\u5219\u7eb5\u864e\u5f52\u5c71\uff0c\u5c06\u6765\u5c82\u4e0d\u5bf9\u81ea\u5df1\u5a01 \u80c1\u66f4\u5927\u3002 \u3000\u3000\u4ed6\u5fc3\u5ff5\u65e2\u5b9a\uff0c\u653b\u52bf\u6108\u89c1\u51cc\u5389\uff0c\u540c\u65f6\u5de6\u81c2\u54af\u54af\u4f5c\u54cd\uff0c\u5239\u65f6\u7c97\u4e86\u4e00\u500d\uff0e\u53c8\u5f00\u59cb\u76f4\u5192\u9ed1\u6c14\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u5fc3\u77e5\u4ed6\u53c8\u8981\u4ee5\u9634\u98ce\u638c\u65bd\u88ad\uff0c\u8fd1\u8eab\u76f8\u640f\uff0c\u5bf9\u65b9\u638c\u529b\u731d\u53d1\uff0c\u5b9e\u5728\u4e0d\u6613\u8eb2\u8fc7\uff0e\u4f46\u53c8\u4e0d\u80fd \u602f\u9635\u5f00\u6e9c\u3002 \u3000\u3000\u4ed6\u4e4b\u6240\u4ee5\u5982\u6b64\uff0c\u4e0d\u5916\u662f\u62c5\u5fc3\u4e07\u6620\u971e\u7684\u5b89\u5168\uff0c\u4ee5\u4ed6\u7684\u8f7b\u529f\u9020\u8be3\uff0c\u60f3\u8131\u79bb\u9634\u540e\u4e00\u9b54\u7684\u9634\u98ce\u638c \u65bd\u88ad\uff0c\u7edd\u975e\u96be\u4e8b\u3002 \u3000\u3000\u4f46\u82e5\u81ea\u5df1\u5c31\u6b64\u5f00\u6e9c\uff0c\u4e07\u6620\u971e\u52bf\u5fc5\u88ab\u5bf9\u65b9\u63b3\u8d70\uff0c\u4ed6\u5c82\u80fd\u8ba9\u4e07\u6620\u971e\u843d\u5165\u9634\u624b\u4e00\u9b54\u7684\u9b54\u638c\u3002 \u3000\u3000\u8fd9\u60c5\u666f\u4f7f\u5f97\u4e00\u65c1\u89c2\u6218\u7684\u4e07\u6620\u971e\uff0c\u8d8a\u770b\u82b3\u5fc3\u4e2d\u4e5f\u8d8a\u5192\u51b7\u6c14\u3002 \u3000\u3000\u6b64\u65f6\uff0c\u5979\u53f3\u624b\u65e9\u53c8\u6263\u4e86\u4e00\u679a\u6697\u5668\uff0e\u5374\u56e0\u9634\u624b\u4e00\u9b54\u548c\u9ea6\u5c0f\u660e\u53ae\u6740\u5f97\u592a\u8fc7\u6fc0\u70c8\uff0c\u5979\u6015\u5931\u53bb\u51c6 \u5934\u4f24\u4e86\u81ea\u5df1\u4eba\uff0c\u662f\u4ee5\u4e00\u76f4\u8fdf\u8fdf\u4e0d\u6562\u51fa\u624b\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u4e3a\u9632\u5bf9\u65b9\u731d\u53d1\u9634\u98ce\u638c\uff0e\u8fd9\u65f6\u6539\u53d8\u4e3a\u6e38\u6597\u65b9\u5f0f\uff0c\u811a\u8e0f\u8ff7\u8e2a\u4e03\u661f\u6b65\uff0c\u4e0d\u505c\u7684\u6ed1\u6b65\u65cb \u8eab\u3002 \u3000\u3000\u4ed6\u7684\u8f7b\u529f\u9020\u8be3\uff0e\u5df2\u5728\u9634\u624b\u4e00\u9b54\u4e4b\u4e0a\u3002\u56e0\u4e4b\uff0c\u6e10\u6e10\u53cd\u800c\u4f7f\u5f97\u9634\u624b\u4e00\u9b54\u5f00\u59cb\u773c\u82b1\u64a9\u4e71\uff0c\u4ee5\u81f4 \u9634\u98ce\u638c\u867d\u5df2\u8fd0\u8db3\u529f\u529b\uff0e\u5374\u4e0d\u6562\u968f\u4fbf\u65bd\u88ad\uff0c\u4ee5\u514d\u865a\u53d1\u635f\u8017\u5185\u529b\u3002 \u3000\u3000\u4e0d\u8fc7\u5982\u6b64\u4e00\u6765\uff0e\u9ea6\u5c0f\u660e\u56e0\u4f53\u529b\u6d88\u8017\u52a0\u901f\uff0c\u4e5f\u6e10\u6e10\u6709\u4e9b\u540e\u529b\u4e0d\u7ee7\u3002 \u3000\u3000\u53c8\u62fc\u6597\u4e86\u56db\u4e94\u5341\u56de\u5408\uff0c\u9ea6\u5c0f\u660e\u5728\u4f53\u529b\u4e0d\u652f\u4e0b\uff0e\u60c5\u6025\u95f4\u53eb\u9053\uff1a\u201c\u4e07\u59d1\u5a18\u5feb\u9003\uff01\u201d \u3000\u3000\u4ed6\u7684\u76ee\u7684\uff0c\u4e0d\u5916\u662f\u7b49\u4e07\u6620\u971e\u8d70\u8fdc\u540e\uff0c\u81ea\u5df1\u518d\u8bbe\u6cd5\u8131\u8eab\u3002 \u3000\u3000\u5c82\u77e5\u4e07\u6620\u971e\u5df2\u770b\u51fa\u9ea6\u5c0f\u660e\u5373\u5c06\u843d\u8d25\uff0c\u4e0d\u4f46\u4e0d\u8d70\uff0c\u53cd\u800c\u7eb5\u8eab\u6740\u4e86\u8fdb\u6765\uff0e\u4e3a\u9ea6\u5c0f\u660e\u52a9\u653b\u3002 \u3000\u3000\u9634\u624b\u2014\u9b54\u4e00\u58f0\u66b4\u543c\uff0c\u5de6\u624b\u968f\u5373\u8f9f\u51fa\u9634\u98ce\u638c\u3002 \u3000\u3000\u8fd9\u4e00\u638c\u6bd4\u5148\u524d\u7684\u90a3\u4e00\u638c\u5a01\u529b\u66f4\u731b\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u56e0\u65e9\u5df2\u6709\u5907\uff0c\u53ea\u662f\u88ab\u9634\u98ce\u638c\u7684\u504f\u98ce\u626b\u4e2d\uff0c\u5e76\u65e0\u5927\u788d\uff0c\u4f46\u4e07\u6620\u971e\u56e0\u521a\u521a\u7531\u6b63\u9762\u51b2 \u5165\uff0c\u5374\u6b63\u597d\u88ab\u638c\u98ce\u51fb\u4e2d\u3002 \u3000\u3000\u53ea\u542c\u4e07\u6620\u971e\u2014\u58f0\u60e8\u547c\uff0c\u5f53\u5373\u5012\u5728\u5730\u4e0a\u660f\u53a5\u8fc7\u53bb\u3002 \u3000\u3000\u8fd9\u2014\u6765\uff0c\u9ea6\u5c0f\u660e\u4e3a\u4e86\u6551\u4e07\u6620\u971e\uff0c\u5c31\u662f\u80fd\u5f00\u6e9c\u4e5f\u65e0\u6cd5\u5f00\u6e9c\u3002 \u3000\u3000\u6240\u5e78\u9634\u624b\u4e00\u9b54\u56e0\u65bd\u51fa\u638c\u529b\u4e4b\u540e\uff0c\u771f\u6c14\u6d88\u8017\u4e0d\u5c11\uff0c\u653b\u52bf\u4e5f\u968f\u4e4b\u51cf\u7f13\uff0e\u9ea6\u5c0f\u660e\u4e5f\u56e0\u800c\u7a0d\u7a0d\u83b7 \u5f97\u5598\u606f\u673a\u4f1a\u3002 \u3000\u3000\u9634\u624b\u4e00\u9b54\u62fc\u529b\u653b\u51fa\u51e0\u62db\uff0c\u628a\u9ea6\u5c0f\u660e\u903c\u9000\u6570\u6b65\uff0c\u505c\u4e0b\u624b\u6765\u9053\uff1a\u201c\u5c0f\u5b50\uff0c\u4f60\u5fc3\u91cc\u5e94\u8be5\u6709\u6570\uff0c \u7ee7\u7eed\u6253\u4e0b\u53bb\uff0c\u4f60\u7edd\u96be\u518d\u652f\u6301\u4e94\u5341\u62db\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u51b7\u7b11\u9053\uff1a\u201c\u5728\u4e0b\u65e2\u4f7f\u518d\u652f\u6301\u4e00\u62db\uff0c\u4e5f\u975e\u6253\u4e0d\u53ef\uff01\u201d \u3000\u3000\u201c\u54b1\u4eec\u8c08\u8c08\u6761\u4ef6\u5982\u4f55\uff1f\u201d \u3000\u3000\u201c\u4ec0\u4e48\u6761\u4ef6\uff1f\u4f60\u8bf4\uff01\u201d \u3000\u3000\u201c\u53ea\u8981\u4f60\u80af\u8ba9\u8001\u592b\u5c06\u8fd9\u5973\u5a03\u513f\u5e26\u8d70\uff0c\u8001\u592b\u5c31\u60c5\u613f\u653e\u8fc7\u4f60\u3002\u201d \u3000\u3000\u201c\u7b11\u8bdd\uff01\u53ea\u8981\u5728\u4e0b\u6709\u4e00\u53e3\u6c14\u5728\uff0c\u4f60\u5c31\u522b\u60f3\u6253\u8fd9\u79cd\u5ff5\u5934\uff01\u201d \u3000\u3000\u201c\u4f60\u8ddf\u8fd9\u5973\u5a03\u513f\u771f\u7684\u5df2\u7ecf\u604b\u5978\u60c5\u70ed\uff1f\u201d \u3000\u3000\u201c\u653e\u4f60\u5988\u7684\u72d7\u5c41\uff01\u201d \u3000\u3000\u539f\u6765\u9634\u624b\u4e00\u9b54\u4e5f\u89c9\u51fa\u60f3\u5236\u670d\u9ea6\u5c0f\u660e\u4e0d\u662f\u4e00\u4ef6\u6613\u4e8b\uff0c\u6709\u9ea6\u5c0f\u660e\u5728\uff0c\u5c31\u5f88\u96be\u5e26\u8d70\u4e07\u6620\u971e\u3002 \u3000\u3000\u4ed6\u9634\u6c89\u6c89\u5fd6\u601d\u4e86\u534a\u54cd\u9053\uff1a\u201c\u8fd9\u6837\u5427\uff0c\u8001\u592b\u90a3\u4e5d\u5929\u7384\u9634\u6d1e\u591a\u7684\u662f\u7f8e\u5973\uff0c\u4f60\u4e0a\u6b21\u4e5f\u66fe\u89c1\u8fc7\uff0e \u53ea\u8981\u4f60\u80af\u8ba9\u8001\u592b\u628a\u8fd9\u5973\u5a03\u513f\u5e26\u8d70\uff0c\u8001\u592b\u60c5\u613f\u628a\u624b\u4e0b\u7684\u7f8e\u5973\u8d50\u7ed9\u4f60\u4e00\u4e2a\u4ea4\u6362\uff0e\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u53f1\u9053\uff1a\u201c\u4eba\u4e0d\u662f\u8d27\u7269\uff0c\u600e\u53ef\u968f\u4fbf\u4ea4\u6362\uff0c\u5982\u679c\u5728\u4e0b\u627e\u4e00\u4e2a\u5973\u4eba\u4ea4\u6362\u4f60\u5a18\uff0c\u4f60\u8fd9\u8001\u6df7 \u86cb\u80af\u4e0d\u80af\uff1f\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u53ea\u6c14\u5f97\u4e03\u7a8d\u751f\u70df\uff0c\u4e0d\u518d\u7b54\u8bdd\uff0c\u6325\u52a8\u62c2\u5c18\uff0c\u4fbf\u53c8\u653b\u4e86\u4e0a\u6765\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u7acb\u5373\u53c8\u62fc\u529b\u8fce\u51fb\u8fc7\u53bb\u3002 \u3000\u3000\u4ed6\u65b9\u624d\u8bf4\u7684\u662f\u771f\u8bdd\uff0c\u53ea\u8981\u6709\u4e00\u53e3\u6c14\u5728\uff0c\u4fbf\u5fc5\u987b\u548c\u5bf9\u65b9\u62fc\u6218\u5230\u5e95\uff0c\u56e0\u4e3a\u4ed6\u5fc5\u987b\u62a4\u4f4f\u4e07\u6620\u971e \u4e0d\u88ab\u63b3\u8d70\uff0e\u5426\u5219\u8fd8\u6709\u4f55\u9762\u76ee\u518d\u56de\u8ff7\u8e2a\u8c37\u3002 \u3000\u3000\u8f6c\u77ac\u95f4\u4e24\u4eba\u53c8\u5bf9\u62c6\u4e86\u767e\u62db\u4ee5\u4e0a\uff0c\u9ea6\u5c0f\u660e\u8eab\u624b\u867d\u9ad8\uff0c\u5185\u529b\u603b\u662f\u4e0d\u5982\u5bf9\u65b9\u6df1\u539a\uff01\u767e\u62db\u4e4b\u540e\u53c8 \u6e10\u6e10\u843d\u5165\u4e0b\u98ce\u3002 \u3000\u3000\u4ed6\u6697\u5fd6\u9053\uff1a\u201c\u82e5\u8fd9\u6837\u4e45\u6218\u4e0b\u53bb\uff0c\u6700\u540e\u4e0d\u652f\u7684\u5fc5\u5b9a\u662f\u6211\uff0c\u82e5\u4e0d\u80fd\u8bbe\u6cd5\u51fa\u5947\u81f4\u80dc\uff0c\u770b\u6765\u6700\u540e \u8fd8\u662f\u8981\u7cdf\u3002\u201d \u3000\u3000\u5c82\u77e5\u5728\u8fd9\u65f6\uff0c\u9634\u540e\u4e00\u9b54\u7adf\u81ea\u52a8\u5411\u540e\u8dc3\u9000\u4e08\u4f59\uff0c\u505c\u4e0b\u624b\u6765\u3002 \u3000\u3000\u8fd9\u7b49\u4e8e\u53c8\u7ed9\u4e86\u9ea6\u5c0f\u660e\u4e00\u6b21\u5598\u606f\u673a\u4f1a\uff0c\u4e0d\u8fc7\u4ed6\u5f04\u4e0d\u6e05\u5bf9\u65b9\u4e3a\u4ec0\u4e48\u53cd\u800c\u4e0d\u4e58\u80dc\u88ad\u51fb\u3002 \u3000\u3000\u539f\u6765\uff0c\u9634\u624b\u4e00\u9b54\u4e5f\u56e0\u5185\u529b\u6d88\u8017\u8fc7\u591a\uff0c\u518d\u6253\u4e0b\u53bb\uff0c\u8fde\u4ed6\u81ea\u5df1\u4e5f\u6ca1\u6709\u5fc5\u80dc\u628a\u63e1\uff0e \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u53c8\u4e0d\u6253\u4e86\uff1f\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u9053\uff1a\u201c\u8001\u592b\u60f3\u548c\u4f60\u518d\u8c08\u5224\u4e00\u6b21\u3002\u201d \u3000\u3000\u201c\u4f60\u6211\u5df2\u662f\u52bf\u4e0d\u4e24\u7acb\uff0c\u6839\u672c\u6ca1\u4ec0\u4e48\u53ef\u8c08\u7684\uff01\u201d \u3000\u3000\u201c\u4f60\u522b\u8bef\u4f1a\uff0c\u8001\u592b\u5148\u95ee\u95ee\u4f60\uff0c\u4f60\u53ef\u662f\u771f\u7684\u559c\u6b22\u8fd9\u5973\u5a03\u513f\uff1f\u201d \u3000\u3000\u201c\u81f3\u5c11\uff0e\u5728\u4e0b\u4e0d\u80fd\u8ba9\u5979\u843d\u5728\u4f60\u624b\u4e2d\uff0e\u201d \u3000\u3000\u201c\u4f60\u53ef\u662f\u60f3\u8981\u4e00\u4e2a\u6b7b\u4eba\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u5446\u4e86\u5446\u9053\uff1a\u201c\u8fd9\u8bdd\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u4ef0\u8138\u563f\u563f\u7b11\u4e86\u4e00\u9635\u9053\uff1a\u201c\u5979\u5df2\u4e2d\u4e86\u8001\u592b\u7684\u9634\u98ce\u638c\uff0c\u53ea\u6709\u8001\u592b\u624d\u80fd\u6551\u5f97\u4e86\u5979\uff0c\u5426 \u5219\u5979\u5728\u4e24\u4e2a\u65f6\u8fb0\u4e4b\u5185\uff0c\u4fbf\u4f1a\u9634\u6bd2\u653b\u5fc3\uff0c\u6c14\u7edd\u8eab\u4ea1\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u5fc3\u5934\u6697\u51db\uff0c\u4f46\u8868\u9762\u4ecd\u80fd\u4fdd\u6301\u9547\u5b9a\uff0c\u9053\uff1a\u201c\u96be\u9053\u5728\u4e0b\u5c31\u6551\u4e0d\u597d\u5979\uff1f\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u9053\uff1a\u201c\u5f53\u4eca\u6b66\u6797\uff0e\u7ec3\u5c31\u9634\u98ce\u638c\u7684\uff0c\u53ea\u6709\u8001\u592b\u4e00\u4eba\uff0c\u8fd9\u5c31\u662f\u6240\u8c13\u72ec\u95e8\u6b66\u529f\uff0e\u8981\u60f3 \u6551\u8fd9\u5973\u5a03\u513f\uff0c\u5f53\u7136\u4e5f\u5f97\u72ec\u95e8\u624b\u6cd5\u3002\u4f60\u82e5\u80fd\u6551\u5979\uff0c\u4e0d\u59a8\u8bd5\u8bd5\u770b\uff0c\u53ea\u8981\u80fd\u628a\u5979\u6551\u9192\uff0c\u8001\u592b\u62d4\u817f\u5c31 \u8d70\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u867d\u4e0d\u80fd\u65ad\u5b9a\u9634\u624b\u4e00\u9b54\u8bf4\u7684\u662f\u5426\u771f\u8bdd\uff0c\u4f46\u5185\u5fc3\u5374\u96be\u514d\u6709\u2014\u4e9b\u7740\u614c\uff0c\u4e07\u4e00\u6551\u4e0d\u6d3b\u4e07\u6620 \u971e\uff0e\u90a3\u53c8\u8be5\u600e\u4e48\u529e\uff1f \u3000\u3000\u65e0\u5948\u4e4b\u4e0b\uff0c\u987f\u4e86\u987f\u9053\uff1a\u201c\u4f60\u7684\u610f\u601d\u8981\u60f3\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u9053\uff1a\u201c\u5f88\u7b80\u5355\uff0c\u53ea\u8981\u4f60\u8ba9\u8001\u592b\u628a\u5979\u5e26\u56de\u5415\u6881\u5c71\uff0c\u8001\u592b\u4fdd\u8bc1\u5728\u4e24\u4e2a\u6708\u4e4b\u5185\u518d\u628a\u5979 \u9001\u56de\u8ff7\u8e2a\u8c37\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u4e0d\u7981\u6012\u706b\u4e2d\u70e7\uff0c\u5927\u559d\u9053\uff1a\u201c\u8001\u738b\u516b\u86cb\uff0c\u6211\u8981\u4f60\u73b0\u5728\u5c31\u628a\u5979\u6551\u6d3b\uff01\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u7ec8\u4e8e\u607c\u7f9e\u6210\u6012\uff0c\u62c2\u5c18\u4e00\u6325\uff0e\u53c8\u653b\u4e86\u4e0a\u6765\uff0c\u2014\u9762\u543c\u9053\uff1a\u201c\u5c0f\u5b50\uff0c\u4f60\u662f\u656c\u9152\u4e0d\u5403\u5403 \u7f5a\u9152\uff0c\u8001\u592b\u8fd9\u6b21\u82e5\u5236\u670d\u4f60\u4e0d\u4e86\uff0c\u8a93\u4e0d\u4e3a\u4eba\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u53ea\u597d\u6296\u64de\u7cbe\u795e\uff0e\u518d\u5ea6\u594b\u529b\u8fce\u6218\u3002 \u3000\u3000\u8fd9\u6b21\u9634\u624b\u4e00\u9b54\u679c\u7136\u51fa\u62db\u8d8a\u53d1\u51cc\u5389\uff0c\u8feb\u5f97\u9ea6\u5c0f\u660e\u5f88\u5feb\u4fbf\u5168\u8eab\u6c57\u6c34\u76f4\u5192\uff0e\u4ee5\u81f4\u6709\u4e9b\u624b\u5fd9\u811a \u4e71\uff0c\u4f46\u4ed6\u5374\u5fc5\u987b\u786c\u6491\u4e0b\u53bb\u3002 \u3000\u3000\u6b63\u5728\u9ea6\u5c0f\u660e\u5373\u5c06\u4e0d\u652f\u4e4b\u9645\uff0c\u5ffd\u89c1\u524d\u9762\u5c71\u9e93\u8f6c\u89d2\u5904\uff0c\u4e00\u7537\u4e00\u5973\u4e24\u6761\u4eba\u5f71\uff0e\u98de\u4e5f\u4f3c\u5730\u63a0\u4e86\u8fc7 \u6765\u3002 \u3000\u3000\u90a3\u7537\u7684\u63a0\u81f3\u9634\u624b\u4e00\u9b54\u548c\u9ea6\u5c0f\u660e\u8eab\u4fa7\u4e24\u4e08\u5904\uff0e\u6b62\u6b65\u9ad8\u559d\u9053\uff1a\u201c\u4f4f\u624b\uff01\u201d \u3000\u3000\u867d\u7136\u53ea\u662f\u77ed\u77ed\u7684\u4e24\u4e2a\u5b57\uff0c\u4f46\u5374\u5165\u8033\u60ca\u5fc3\uff0e\u4f3c\u4e4e\u6709\u79cd\u6151\u4eba\u5fc3\u9b44\u7684\u529b\u91cf\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u548c\u9634\u624b\u4e00\u9b54\u60c5\u4e0d\u81ea\u7981\u5404\u81ea\u5411\u540e\u8dc3\u9000\u4e08\u8bb8\uff0c\u505c\u4e0b\u624b\u6765\u3002 \u3000\u3000\u4f46\u89c1\u8fd9\u4eba\u4e71\u53d1\u62ab\u80a9\uff0e\u80f8\u5782\u957f\u9aef\uff0c\u8eab\u7a7f\u4e00\u88ad\u5bbd\u5927\u7684\u7070\u888d\uff0c\u795e\u6001\u8131\u4fd7\uff0c\u8d6b\u7136\u662f\u957f\u767d\u795e\u53df\u5e9e\u58eb \u51b2\u3002 \u3000\u3000\u968f\u4ed6\u800c\u6765\u7684\u90a3\u5973\u5b50\uff0c\u8f7b\u76c8\u6d3b\u6cfc\uff0c\u98ce\u59ff\u7ef0\u7ea6\uff0c\u6b63\u662f\u82d7\u7d20\u82d3\u3002 \u3000\u3000\u82d7\u7d20\u82d3\u4e00\u89c1\u9634\u624b\u4e00\u9b54\uff0c\u7acb\u523b\u82b1\u5bb9\u5931\u8272\uff0c\u4e0d\u6562\u7ee7\u7eed\u524d\u8fdb\u3002 \u3000\u3000\u5e9e\u58eb\u51b2\u8fde\u5fd9\u4e00\u62db\u624b\u9053\uff1a\u201c\u522b\u6015\uff01\u6709\u8001\u592b\u5728\uff0c\u8c01\u4e5f\u4e0d\u6562\u628a\u4f60\u600e\u4e48\u6837\u3002\u201d \u3000\u3000\u82d7\u7d20\u82d3\u8fd9\u624d\u795e\u8272\u7565\u52a0\u6062\u590d\uff0c\u7d27\u7d27\u968f\u5728\u5e9e\u58eb\u51b2\u8eab\u540e\u3002 \u3000\u3000\u9634\u624b\u2014\u9b54\u89c6\u7ebf\u7d27\u76ef\u82d7\u7d20\u82d3\uff0c\u53cc\u988a\u4e0d\u4f4f\u62bd\u6410\uff0c\u543c\u9053\uff1a\u201c\u8d31\u5a62\uff0c\u539f\u6765\u4f60\u5df2\u8ddf\u968f\u4e86\u5e9e\u8001\u602a\uff1f\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u62c2\u9aef\u4e00\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff01\u8001\u592b\u5df2\u628a\u5979\u6536\u5165\u95e8\u5899\uff0c\u5979\u73b0\u5728\u5df2\u4e0d\u662f\u4f60\u4e5d\u5929\u7384\u9634\u6d1e\u5e9c\u7684\u4eba \u4e86\u3002\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u5486\u54ee\u9053\uff1a\u201c\u5e9e\u8001\u602a\uff0c\u4f60\u4e3a\u4ec0\u4e48\u62a2\u593a\u8001\u592b\u4e5d\u5929\u7384\u9634\u6d1e\u5e9c\u7684\u4eba\uff1f\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u6de1\u6de1\u4e00\u7b11\uff0c\u624b\u6307\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u9634\u624b\u5144\u8a00\u91cd\u4e86\uff0c\u82d3\u513f\u662f\u8fd9\u4f4d\u5c0f\u5144\u5f1f\u4ea4\u4e88\u8001\u592b\u7684\uff0c\u8001 \u592b\u51e0\u66fe\u4e0e\u4f60\u6253\u8fc7\u4ea4\u9053\uff1f\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u795e\u8272\u4e00\u7a92\uff0c\u63a5\u7740\u8f6c\u5934\u559d\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u539f\u6765\u662f\u4f60\u628a\u8fd9\u8d31\u5a62\u4ea4\u7ed9\u4e86\u5e9e\u8001\u602a\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u5728\u8fd9\u79cd\u60c5\u5f62\u4e0b\uff0c\u5f53\u7136\u4e0d\u6562\u518d\u5f97\u7f6a\u5e9e\u58eb\u51b2\uff0c\u867d\u7136\u5f53\u521d\u82d7\u7d20\u82d3\u662f\u88ab\u5e9e\u58eb\u51b2\u63b3\u8d70\u7684\uff0c\u5374 \u4e5f\u53ea\u597d\u70b9\u4e86\u70b9\u5934\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u662f\u5728\u4e0b\u628a\u82d7\u59d1\u5a18\u4ea4\u7ed9\u4e86\u5e9e\u8001\u524d\u8f88\uff01\u201d \u3000\u3000\u201c\u4f60\u4e3a\u4ec0\u4e48\u8981\u8fd9\u6837\u505a\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u4fe1\u53e3\u9053\uff1a\u201c\u56e0\u4e3a\u5e9e\u8001\u524d\u8f88\u8981\u6536\u5979\u505a\u5f1f\u5b50\uff0c\u5728\u4e0b\u5f53\u7136\u4e0d\u80fd\u803d\u8bef\u4e86\u82d7\u59d1\u5a18\u7684\u673a\u9047\u3002\u82d7 \u59d1\u5a18\u80fd\u8ddf\u968f\u5e9e\u8001\u524d\u8f88\uff0c\u603b\u6bd4\u8ddf\u7740\u4f60\u8fd9\u8001\u5c0f\u5b50\u597d\u5f97\u591a\uff01\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u4f3c\u662f\u542c\u5f97\u5927\u4e3a\u5f97\u610f\uff0c\u6478\u4e86\u6478\u5634\u5df4\u9053\uff1a\u201c\u9634\u624b\u5144\uff0c\u542c\u5230\u4e86\u6ca1\u6709\uff1f\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u543c\u9053\uff1a\u201c\u8001\u592b\u8033\u6735\u4e0d\u804b\uff0c\u5f53\u7136\u542c\u5f97\u5230\u4ed6\u8fd9\u756a\u9b3c\u8bdd\uff01\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u7b11\u9053\uff1a\u201c\u82e5\u8bba\u9b3c\u8bdd\uff0c\u5e94\u8be5\u51fa\u81ea\u4f60\u7684\u53e3\u4e2d\u624d\u5bf9\uff0c\u9ea6\u5c0f\u5144\u65e2\u4e0d\u9634\u53c8\u4e0d\u9b54\uff0e\u54ea\u91cc\u6765\u7684\u9b3c \u8bdd\uff1f\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u54ac\u4e86\u54ac\u7259\uff0c\u6b63\u8981\u53d1\u4f5c\uff0c\u4e0d\u77e5\u600e\u7684\uff0c\u5374\u53c8\u5fcd\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u5e9e\u58eb\u51b2\u671b\u4e86\u660f\u53a5\u5728\u5730\u7684\u4e07\u6620\u971e\u4e00\u773c\uff0c\u95ee\u9053\uff1a\u201c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u62c5\u5fc3\u9ea6\u5c0f\u660e\u8bf4\u51fa\u5bf9\u81ea\u5df1\u4e0d\u5229\u7684\u8bdd\uff0c\u5fd9\u9053\uff1a\u201c\u8fd9\u5973\u5a03\u513f\u548c\u90a3\u5c0f\u5b50\u604b\u5978\u60c5\u70ed\uff0c\u5728\u5c71 \u91ce\u95f4\u516c\u7136\u7325\u4eb5\uff0c\u8001\u592b\u770b\u4e0d\u8fc7\u53bb\uff0c\u624d\u51fa\u624b\u628a\u5979\u51fb\u660f\uff0e\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u7b11\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u628a\u9ea6\u5c0f\u5144\u5f1f\u4e5f\u4e00\u8d77\u51fb\u660f\uff0c\u4e13\u627e\u5e74\u8f7b\u5973\u4eba\u9ebb\u70e6\uff0c\u8fd8\u79f0\u5f97\u4ec0\u4e48\u82f1 \u96c4\u597d\u6c49\uff01\u201d \u3000\u3000\u201c\u96be\u9053\u4f60\u4e0d\u76f8\u4fe1\u8001\u592b\u7684\u8bdd\uff1f\u201d \u3000\u3000\u201c\u4f60\u9634\u624b\u5144\u7684\u8bdd\uff0c\u5c82\u6b62\u6211\u5e9e\u58eb\u51b2\u4e0d\u4fe1\uff0c\u53ea\u6015\u666e\u5929\u4e4b\u4e0b\u627e\u4e0d\u51fa\u4e00\u4eba\u4f1a\u76f8\u4fe1\u7684\u3002\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u54ac\u7259\u5207\u9f7f\u9053\uff1a\u201c\u597d\u54c7\uff01\u5e9e\u8001\u602a\uff0c\u4f60\u6562\u4fae\u8fb1\u8001\u592b\uff01\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u7b11\u9053\uff1a\u201c\u8001\u592b\u5bf9\u4f60\u8fd9\u6837\u8bb2\u8bdd\u5df2\u7ecf\u591f\u5ba2\u6c14\u4e86\uff0c\u54b1\u4eec\u4e24\u4e2a\u6682\u4e14\u514d\u8c08\uff0c\u8ba9\u6211\u5148\u95ee\u95ee\u8fd9\u4f4d \u5c0f\u5144\u5f1f\uff0e\u628a\u4e8b\u60c5\u5f04\u660e\u767d\u518d\u8bf4\u3002\u9ea6\u5c0f\u5144\u5f1f\uff0c\u4f60\u8bf4\u8bf4\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u968f\u5373\u628a\u65b9\u624d\u7684\u7ecf\u8fc7\u8bb2\u4e86\u4e00\u904d\uff0c\u53ea\u662f\u628a\u8981\u5230\u5c3c\u5eb5\u6c42\u89c1\u7f01\u8863\u8001\u5c3c\u4e4b\u4e8b\u9690\u53bb\uff0c\u4ee5\u514d\u6cc4\u9732 \u673a\u5bc6\u53c8\u751f\u679d\u8282\u3002 \u3000\u3000\u5e9e\u58eb\u51b2\u518d\u671b\u4e86\u5730\u4e0a\u7684\u4e07\u6620\u971e\u2014\u773c\u9053\uff1a\u201c\u8fd9\u59d1\u5a18\u53eb\u4ec0\u4e48\u540d\u5b57\uff1f\u4e5f\u662f\u8ff7\u8e2a\u8c37\u7684\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u8fd9\u4f4d\u59d1\u5a18\u53eb\u4e07\u6620\u971e\uff0c\u662f\u5df2\u6545\u524d\u8f88\u795e\u97ad\u98de\u68ad\u4e07\u6653\u5149\u7684\u5343\u91d1\uff0e\u4e07\u5927\u4fa0\u56e0\u548c\u80e1\u524d \u76df\u4e3b\u662f\u7ed3\u62dc\u4e4b\u4ea4\uff0c\u5728\u4e34\u7ec8\u524d\u628a\u4e07\u59d1\u5a18\u6258\u5490\u4e86\u80e1\u524d\u76df\u4e3b\uff0e\u56e0\u4e4b\u4e07\u59d1\u5a18\u624d\u662f\u8ff7\u8e2a\u8c37\u7684\u4eba\u3002\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u9ed8\u4e86\u4e00\u9ed8\u9053\uff1a\u201c\u4f60\u548c\u4e07\u59d1\u5a18\u662f\u5426\u771f\u7684\u5728\u70ed\u604b\u4e4b\u4e2d\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u6025\u5f97\u731b\u4e00\u8dfa\u811a\u9053\uff1a\u201c\u8001\u524d\u8f88\u522b\u542c\u9634\u624b\u4e00\u9b54\u90a3\u8001\u738b\u516b\u86cb\u80e1\u8bf4\u516b\u9053\uff0c\u665a\u8f88\u662f\u80e1\u524d\u76df\u4e3b \u7684\u5e08\u5f1f\uff0c\u4e07\u59d1\u5a18\u662f\u80e1\u524d\u76df\u4e3b\u7684\u4f84\u5973\uff0e\u665a\u8f88\u82e5\u548c\u5979\u6709\u4e86\u7537\u5973\u5173\u7cfb\uff0c\u90a3\u5c82\u4e0d\u6210\u4e86\u4e71\u4f26\u3002\u66f4\u4f55 \u51b5\u2026\u2026\u201d \u3000\u3000\u201c\u4f55\u51b5\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u4e07\u59d1\u5a18\u65e9\u6709\u4e00\u4f4d\u9752\u6885\u7af9\u9a6c\u7684\u4f34\u4fa3\uff0c\u8fd9\u4eba\u53eb\u6587\u5929\u751f\uff0c\u4e5f\u5728\u8ff7\u8e2a\u8c37\uff0c\u5373\u4f7f\u4e07\u59d1\u5a18\u548c\u665a\u8f88\u540c \u8f88\uff0c\u665a\u8f88\u4e5f\u4e0d\u8be5\u505a\u51fa\u6a2a\u5200\u593a\u7231\u4e4b\u4e8b\u3002\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u5fae\u5fae\u9894\u9996\uff0c\u8f6c\u8fc7\u5934\u53bb\u9053\uff1a\u201c\u9634\u624b\u5144\uff0e\u4f60\u4e5f\u4e00\u5927\u628a\u5e74\u7eaa\u4e86\uff0c\u5b9e\u5728\u4e0d\u8be5\u8bf4\u51fa\u65b9\u624d\u90a3\u79cd \u8bdd\uff0c\u8fd9\u662f\u6709\u635f\u9634\u5fb7\u7684\uff0c\u4f60\u8bf4\u4ed6\u4eec\u604b\u5978\u60c5\u70ed\uff0c\u53ef\u62ff\u5f97\u51fa\u8bc1\u636e\u6765\uff1f\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u53ea\u618b\u5f97\u9762\u8272\u6709\u5982\u732a\u809d\uff0c\u5e72\u54b3\u4e86\u4e24\u58f0\u9053\uff1a\u201c\u2014\u5bf9\u5c11\u5e74\u7537\u5973\u8d70\u5728\u4e00\u8d77\uff0c\u5e72\u67f4\u70c8\u706b\uff0c \u54ea\u80fd\u529e\u51fa\u597d\u4e8b\uff1f\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u7b11\u9053\uff1a\u201c\u636e\u8bf4\u9634\u624b\u5144\u7684\u4e5d\u5929\u7384\u9634\u6d1e\u5e9c\u517b\u4e86\u4e0d\u5c11\u7f8e\u5973\uff0c\u4f60\u8ddf\u5979\u4eec\u4e0d\u4f46\u8d70\u5728\u2014\u8d77\uff0c\u8fd8 \u4f4f\u5728\u2014\u8d77\uff0c\u671d\u5915\u76f8\u5904\uff0c\u662f\u5426\u4e5f\u6ca1\u529e\u51fa\u597d\u4e8b\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u4e5f\u7d27\u8ddf\u7740\u9ad8\u58f0\u9053\uff1a\u201c\u8001\u6df7\u86cb\uff0e\u4f60\u5a18\u4f60\u59b9\u5b50\u4e5f\u662f\u5973\u4eba\uff0c\u6211\u8ddf\u5979\u4eec\u8d70\u5728\u4e00\u8d77\uff0c\u53ef\u80fd\u4e5f \u6ca1\u529e\u51fa\u597d\u4e8b\u5427\uff01\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u6c14\u5f97\u5934\u8111\u53d1\u660f\uff0c\u53cc\u76ee\u51f6\u5149\u66b4\u5c04\uff0c\u62a1\u8d77\u62c2\u5c18\uff0c\u53c8\u5411\u9ea6\u5c0f\u660e\u51b2\u53bb\u3002 \u3000\u3000\u8c01\u77e5\u5e9e\u58eb\u51b2\u5374\u6a2a\u8de8\u4e24\u6b65\uff0c\u62e6\u5728\u9634\u624b\u4e00\u9b54\u8eab\u524d\u9053\uff1a\u201c\u9634\u624b\u5144\uff0c\u4f60\u60f3\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u543c\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u7adf\u6562\u8fb1\u9a82\u8001\u592b\uff0c\u8001\u592b\u975e\u8981\u4e86\u4ed6\u7684\u72d7\u547d\u4e0d\u53ef\uff01\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u54fc\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u6709\u8001\u592b\u5728\uff0c\u4f60\u5c31\u522b\u60f3\u52a8\u4ed6\u4e00\u6839\u6c57\u6bdb\uff01\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u5446\u4e86\u4e00\u5446\u9053\uff1a\u201c\u83ab\u975e\u4f60\u8fd9\u8001\u602a\u7269\u60f3\u63d2\u624b\u7ba1\u8fd9\u6863\u5b50\u95f2\u4e8b\uff1f\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u9053\uff1a\u201c\u8001\u592b\u548c\u59d3\u9ea6\u7684\u8fd9\u5a03\u513f\u5173\u7cfb\u4e0d\u540c\uff0c\u5f53\u7136\u8981\u62a4\u7740\u4ed6\u3002\u201d \u3000\u3000\u201c\u4f60\u8ddf\u4ed6\u662f\u4ec0\u4e48\u5173\u7cfb\uff1f\u201d \u3000\u3000\u201c\u8001\u592b\u662f\u8c37\u5bd2\u9999\u7684\u517b\u7236\uff0c\u4ed6\u53eb\u8c37\u5bd2\u9999\u662f\u5e08\u5ac2\uff0c\u4f60\u8bf4\u8bf4\u770b\uff0c\u8fd9\u5173\u7cfb\u662f\u5426\u4e0d\u540c\uff1f\u201d \u3000\u3000\u201c\u7b11\u8bdd\uff01\u8c37\u5bd2\u9999\u4ec0\u4e48\u65f6\u5019\u66fe\u628a\u4f60\u8fd9\u8001\u602a\u7269\u8ba4\u4f5c\u517b\u7236\uff1f\u8001\u592b\u600e\u4e48\u6ca1\u542c\u8bf4\u8fc7\uff1f\u201d \u3000\u3000\u201c\u4e0a\u6b21\u5929\u53f0\u5c71\u4e07\u82b1\u5bab\u90a3\u6b21\u8840\u6218\uff0c\u4f60\u8fd9\u8001\u9b54\u4e5f\u66fe\u5728\u573a\uff0c\u8c37\u5bd2\u9999\u4e34\u7ec8\u524d\u4eb2\u53e3\u4ea4\u4ee3\u8001\u592b\u5e26\u8d70\u5979 \u7684\u9057\u9ab8\uff0c\u53c8\u628a\u5979\u90a3\u517b\u5b50\u7fce\u513f\u4ea4\u4e88\u8001\u592b\uff0c\u8fd9\u8bdd\u4f60\u603b\u8be5\u542c\u5230\u4e86\u5427\uff1f\u201d \u3000\u3000\u201c\u8fd9\u5c31\u80fd\u65ad\u5b9a\u8c37\u5bd2\u9999\u662f\u4f60\u7684\u517b\u5973\u5417\uff1f\u201d \u3000\u3000\u201c\u8001\u592b\u7d22\u6027\u518d\u5bf9\u4f60\u5b9e\u8bf4\u5427\uff01\u8c37\u5bd2\u9999\u629a\u517b\u7684\u90a3\u4e2a\u5b64\u513f\u7fce\u513f\uff0c\u6b63\u662f\u8001\u592b\u7684\u4eb2\u5916\u5b59\uff0c\u8c37\u5bd2\u9999\u65e2 \u662f\u7fce\u513f\u7684\u517b\u6bcd\uff0c\u5f53\u7136\u5c31\u662f\u8001\u592b\u7684\u517b\u5973\uff0c\u8fd9\u662f\u5929\u7ecf\u5730\u4e49\u7684\u4e8b\uff0c\u54ea\u4e2a\u6df7\u5e10\u4e1c\u897f\u6562\u8bf4\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u8fd9\u8bdd\u867d\u7136\u6709\u70b9\u5f3a\u8bcd\u593a\u7406\uff0e\u5374\u4f7f\u5f97\u9634\u624b\u4e00\u9b54\u96be\u4ee5\u53cd\u9a73\uff0c\u987f\u4e86\u4e00\u987f\u9053\uff1a\u201c\u8001\u602a\u7269\uff0c\u4f60 \u771f\u8981\u7ba1\u8fd9\u6321\u5b50\u95f2\u4e8b\uff1f\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u51b7\u7b11\u9053\uff1a\u201c\u8fd9\u6839\u672c\u4e0d\u662f\u95f2\u4e8b\uff0c\u8001\u592b\u5f53\u7136\u8981\u7ba1\uff0e\u5373\u4f7f\u59d3\u9ea6\u7684\u5c0f\u5a03\u513f\u4e0e\u8001\u592b\u6ca1\u6709\u534a\u70b9 \u5173\u7cfb\uff0c\u8001\u592b\u7167\u6837\u4e5f\u8981\u7ba1\u3002\u201d \u3000\u3000\u201c\u4f60\u4e3a\u4ec0\u4e48\u8981\u8fd9\u6837\u591a\u4e8b\uff1f\u201d \u3000\u3000\u201c\u8001\u592b\u8981\u5148\u95ee\u95ee\u4f60\uff0c\u4e3a\u4ec0\u4e48\u8981\u65e0\u7f18\u65e0\u6545\u62e6\u8def\u4f24\u4eba\uff1f\u201d \u3000\u3000\u201c\u59d3\u9ea6\u7684\u5c0f\u5b50\u534a\u6708\u524d\u66fe\u65e0\u7f18\u65e0\u6545\u63b3\u8d70\u4e86\u8001\u592b\u7684\u4eba\uff01\u201d \u3000\u3000\u201c\u4f60\u6307\u7684\u4e00\u5b9a\u662f\u8001\u592b\u65b0\u6536\u7684\u5f1f\u5b50\u82d3\u513f\u4e86\uff0c\u82d3\u513f\u73b0\u5728\u5df2\u662f\u8001\u592b\u7684\u4eba\uff0c\u4f60\u60f3\u8981\u4eba\u53ea\u7ba1\u5411\u8001\u592b \u8981\uff01\u201d \u3000\u3000\u201c\u5f88\u597d\uff01\u90a3\u4f60\u5c31\u628a\u5979\u4ea4\u7ed9\u8001\u592b\u5e26\u8d70\uff01\u201d \u3000\u3000\u201c\u56db\u4e24\u68c9\u82b1\u4e00\u514d\u8c08\uff08\u5f39\uff09\uff0e\u82d3\u513f\u5df2\u662f\u8001\u592b\u7684\u4eba\uff0c\u4f60\u51ed\u4ec0\u4e48\u5e26\u5979\u8d70\uff1f\u201d \u3000\u3000\u201c\u5982\u679c\u8001\u592b\u5f3a\u8981\u5462\uff1f\u201d \u3000\u3000\u201c\u90a3\u5c31\u8981\u770b\u4f60\u7684\u9634\u98ce\u638c\u80fd\u5426\u80dc\u8fc7\u8001\u592b\uff1f\u201d \u3000\u3000\u8fd9\u2014\u6765\uff0c\u9634\u624b\u4e00\u9b54\u867d\u88ab\u6fc0\u6012\uff0c\u5374\u53c8\u4e0d\u6562\u8f7b\u4e3e\u52a8\uff0c\u4ed6\u65e9\u5c31\u542c\u8bf4\u5e9e\u58eb\u51b2\u7684\u6b66\u529f\u5df2\u8fbe\u767b\u5cf0\u9020\u6781 \u4e4b\u5883\uff0c\u5373\u4f7f\u8bba\u540d\u5934\u7684\u54cd\u4eae\uff0c\u4e5f\u5728\u81ea\u5df1\u4e4b\u4e0a\u3002 \u3000\u3000\u82e5\u52a8\u8d77\u624b\u6765\uff0c\u52bf\u5fc5\u8ba8\u4e0d\u4e86\u597d\u5904\uff0c\u5f53\u4e0b\u51b7\u7b11\u4e86\u51e0\u58f0\u9053\uff1a\u201c\u8001\u592b\u7528\u4e0d\u7740\u8ddf\u4f60\u4f24\u548c\u6c14\uff0c\u65e2\u7136\u4e0d \u80af\u628a\u90a3\u8d31\u5a62\u4ea4\u4e88\u8001\u592b\uff0c\u54b1\u4eec\u540e\u4f1a\u6709\u671f\uff0c\u518d\u89c1\u5566\uff01\u201d \u3000\u3000\u8bf4\u7f62\u8f6c\u8eab\u4fbf\u8d70\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u53eb\u9053\uff1a\u201c\u5e9e\u8001\u4f2f\u522b\u653e\u4ed6\u8d70\uff01\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u5b50\u771f\u4f1a\u89c1\u98ce\u8f6c\u8235\uff0c\u542c\u8bf4\u8001\u592b\u662f\u8c37\u5bd2\u9999\u7684\u517b\u7236\uff0c\u5c31\u6539\u53e3\u79f0\u8001\u592b\u4e3a\u8001 \u4f2f\u4e86\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u62b1\u62f3\u4e00\u793c\u9053\uff1a\u201c\u8f88\u4efd\u6538\u5173\uff0c\u665a\u8f88\u65e2\u7136\u77e5\u9053\u60a8\u662f\u8c37\u5e08\u5ac2\u7684\u517b\u7236\uff0c\u5f53\u7136\u8981\u6539\u53e3\u76f8 \u79f0\u3002\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u5f97\u610f\u5730\u62c2\u9aef\u4e00\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u5b50\u79f0\u5f97\u4e0a\u662f\u62cd\u9a6c\u5c41\u4e13\u5bb6\uff0c\u4f60\u4e3a\u4ec0\u4e48\u8981\u6c42\u8001\u592b\u522b\u8ba9\u9634 \u624b\u2014\u9b54\u8d70\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4e07\u59d1\u5a18\u662f\u88ab\u4ed6\u7684\u9634\u98ce\u638c\u51fb\u660f\uff0c\u53ea\u6709\u4ed6\u624d\u80fd\u628a\u5979\u6551\u9192\u3002\u201d \u3000\u3000\u201c\u8c01\u8bf4\u7684\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u662f\u90a3\u8001\u9b54\u5934\u81ea\u5df1\u8bf4\u7684\u3002\u201d \u3000\u3000\u201c\u72d7\u5c41\uff01\u8ba9\u8001\u592b\u8bd5\u8bd5\u770b\u3002\u4ed6\u6709\u72ec\u95e8\u624b\u6cd5\uff0c\u96be\u9053\u8001\u592b\u5c31\u6ca1\u6709\uff1f\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u8bf4\u7740\u6765\u5230\u4e07\u6620\u971e\u8eab\u524d\uff0c\u8e72\u4e0b\u6765\u89c2\u5bdf\u4e86\u534a\u664c\u9053\uff1a\u201c\u628a\u5979\u6276\u5750\u8d77\u6765\uff0e\u8001\u592b\u8fd9\u5c31\u8bd5 \u8bd5\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u4e3a\u4e86\u907f\u5acc\uff0e\u5fd9\u9053\uff1a\u201c\u8fd8\u662f\u8bf7\u82d7\u59d1\u5a18\u5e2e\u5fd9\u4e00\u4e0b\u5427\uff01\u201d \u3000\u3000\u82d7\u7d20\u82d3\u8fde\u5fd9\u4e0a\u524d\u628a\u4e07\u6620\u971e\u6276\u5750\u8d77\u6765\u3002 \u3000\u3000\u5e9e\u58eb\u51b2\u968f\u5373\u628a\u53f3\u638c\u62b5\u5728\u4e07\u6620\u971e\u540e\u80cc\u7684\u201c\u547d\u95e8\u7a74\u201d\u4e0a\uff0c\u8fd0\u6c14\u4e3a\u5979\u4e00\u9762\u7597\u4f24\uff0c\u4e00\u9762\u89e3\u7a74\u3002 \u3000\u3000\u5927\u7ea6\u534a\u76cf\u8336\u65f6\u5149\u8fc7\u53bb\uff0c\u53ea\u89c1\u4e07\u6620\u971e\u5168\u8eab\u90fd\u5192\u51fa\u84b8\u84b8\u70ed\u6c14\uff0c\u5e9e\u58eb\u51b2\u989d\u89d2\u4e0a\u4e5f\u6e17\u51fa\u8c46\u5927\u7684\u6c57 \u73e0\uff0e \u3000\u3000\u53c8\u8fc7\u4e86\u534a\u664c\uff0c\u5e9e\u58eb\u51b2\u624d\u6536\u56de\u624b\u6765\uff0c\u4ea4\u4ee3\u9053\uff1a\u201c\u518d\u653e\u5979\u4ef0\u5367\u4e0b\u53bb\uff01\u201d \u3000\u3000\u63a5\u7740\u518d\u7531\u6000\u91cc\u638f\u51fa\u4e00\u7c92\u7eff\u8272\u4e39\u4e38\uff0c\u4e3a\u4e07\u6620\u971e\u670d\u4e0b\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u5e26\u70b9\u7d27\u5f20\u5730\u95ee\u9053\uff1a\u201c\u5e9e\u8001\u4f2f\u6709\u628a\u63e1\u5979\u4e00\u5b9a\u80fd\u9192\u8fc7\u6765\uff1f\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u9053\uff1a\u201c\u9a6c\u4e0a\u5979\u5c31\u4f1a\u9192\u8fc7\u6765\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u5982\u91ca\u91cd\u8d1f\uff0c\u603b\u7b97\u653e\u4e0b\u5fc3\u6765\u3002 \u3000\u3000\u4ed6\u4e0d\u4f46\u5bf9\u4e07\u6620\u971e\u653e\u4e0b\u4e86\u5fc3\uff0c\u540c\u65f6\u4e5f\u770b\u51fa\u82d7\u7d20\u82d3\u786e\u5b9e\u672a\u53d7\u5e9e\u58eb\u51b2\u8650\u5f85\uff0c\u770b\u6765\u5e9e\u58eb\u51b2\u8fd9\u4f4d\u7279 \u7acb\u72ec\u884c\u7684\u602a\u4eba\uff0c\u7edd\u975e\u9634\u624b\u4e00\u9b54\uff0e\u6bd2\u706b\u6210\u5168\u90a3\u7b49\u9ed1\u9053\u4eba\u7269\u53ef\u6bd4\u3002 \u3000\u3000\u5e9e\u58eb\u51b2\u671b\u671b\u9ea6\u5c0f\u660e\uff0e\u518d\u671b\u671b\u82d7\u7d20\u82d3\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4e2a\u5206\u522b\u591a\u65e5\uff0c\u89c1\u4e86\u9762\u6b63\u8be5\u591a\u8c08\u8c08\uff0e\u600e\u4e48 \u53cd\u800c\u663e\u5f97\u964c\u751f\u8d77\u6765\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u53ea\u8981\u5e9e\u8001\u4f2f\u80fd\u5584\u5f85\u82d7\u59d1\u5a18\uff0c\u665a\u8f88\u4e5f\u5c31\u653e\u5fc3\u4e86\uff0e\u4e0a\u6b21\u5e9e\u8001\u4f2f\u628a\u82d7\u59d1\u5a18\u5e26\u8d70\uff0c \u665a\u8f88\u9669\u4e9b\u8bef\u4f1a\u4e86\u4f60\u8001\u4eba\u5bb6\uff0e\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u5475\u5475\u7b11\u9053\uff1a\u201c\u8001\u592b\u8fd9\u2014\u751f\uff0c\u4ece\u672a\u6536\u8fc7\u5f1f\u5b50\uff0c\u80fd\u628a\u5979\u6536\u5f52\u95e8\u4e0b\uff0c\u8be5\u662f\u5979\u7684\u798f\u7f18\u624d \u5bf9\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u90a3\u4e48\u665a\u8f88\u4e5f\u8be5\u8c22\u8c22\u5e9e\u8001\u4f2f\u4e86\uff01\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u4e24\u773c\u4e00\u772f\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u5b50\u771f\u4f1a\u8bf4\u597d\u542c\u7684\uff0c\u8001\u592b\u6536\u4e86\u5979\uff0e\u4f60\u6709\u4ec0\u4e48\u53ef\u8c22\u7684\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u6b63\u8272\u9053\uff1a\u201c\u5f53\u521d\u665a\u8f88\u9677\u8eab\u5728\u9634\u624b\u4e00\u9b54\u7684\u9b3c\u6d1e\u5e9c\uff0c\u662f\u82d7\u59d1\u5a18\u6551\u4e86\u6211\uff0c\u5982\u4eca\u82d7\u59d1\u5a18\u80fd \u5217\u5165\u60a8\u7684\u95e8\u5899\u53d7\u827a\uff0c\u4ee5\u60a8\u7684\u7edd\u4e16\u6b66\u529f\u6559\u5bfc\u4e8e\u5979\uff0e\u5979\u5c06\u6765\u4e5f\u5fc5\u80fd\u6210\u4e3a\u4e00\u540d\u6b66\u529f\u9ad8\u4e0d\u53ef\u6d4b\u7684\u5973 \u4fa0\uff0c\u665a\u8f88\u600e\u80fd\u4e0d\u4e3a\u82d7\u59d1\u5a18\u9ad8\u5174\uff01\u201d \u3000\u3000\u8fd9\u65f6\u7684\u82d7\u7d20\u82d3\u65e9\u5df2\u5a07\u7f9e\u6ee1\u9762\uff0c\u82b3\u5fc3\u4e2d\u5bf9\u9ea6\u5c0f\u660e\u771f\u6709\u4e00\u79cd\u8bf4\u4e0d\u51fa\u7684\u611f\u6fc0\uff0c\u53ea\u662f\u5f53\u7740\u5e08\u7236\u7684 \u9762\u4e0d\u4fbf\u8868\u793a\u800c\u5df2\u3002 \u3000\u3000\u5e9e\u58eb\u51b2\u4e0d\u52a8\u58f0\u8272\u9053\uff1a\u201c\u8001\u592b\u65e9\u770b\u51fa\u4f60\u5bf9\u82d3\u513f\u5341\u5206\u5173\u5fc3\uff0c\u82d3\u513f\u4e5f\u65f6\u65f6\u60e6\u8bb0\u7740\u4f60\u3002\u65e2\u7136\u5982 \u6b64\uff0c\u90a3\u5c31\u8ba9\u4f60\u4eec\u6682\u65f6\u76f8\u805a\u4e00\u6bb5\u65f6\u95f4\u5427\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u5fc3\u4e2d\u4e00\u559c\uff0c\u5374\u53c8\u832b\u7136\u9053\uff1a\u201c\u83ab\u975e\u5e9e\u8001\u4f2f\u4e5f\u8981\u548c\u665a\u8f88\u4e00\u8def\u540c\u884c\uff1f\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u5475\u5475\u7b11\u9053\uff1a\u201c\u8001\u592b\u54ea\u6709\u95f2\u5de5\u592b\u966a\u4f60\u540c\u884c\uff0c\u6211\u662f\u8ba9\u82d3\u513f\u968f\u4f60\u540c\u884c\uff0e\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u90a3\u4e48\u665a\u8f88\u8fc7\u51e0\u5929\u56de\u8ff7\u8e2a\u8c37\uff0c\u82d7\u59d1\u5a18\u662f\u5426\u4e5f\u8981\u8ddf\u53bb\uff1f\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u9053\uff1a\u201c\u5f53\u7136\u5979\u4e5f\u8981\u968f\u4f60\u5230\u8ff7\u8e2a\u8c37\u53bb\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u559c\u9053\uff1a\u201c\u90a3\u592a\u597d\u5566\uff01\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u9053\uff1a\u201c\u4f60\u522b\u9ad8\u5174\uff0c\u8001\u592b\u51c6\u5979\u968f\u4f60\u5230\u8ff7\u8e2a\u8c37\uff0c\u9664\u4e86\u8ba9\u4f60\u4eec\u591a\u4e00\u70b9\u65f6\u95f4\u76f8\u805a\u5916\uff0c\u6700\u4e3b \u8981\u7684\uff0c\u662f\u8981\u5979\u89c1\u89c1\u5979\u7684\u59d0\u59d0\u3002\u201d \u3000\u3000\u201c\u5e9e\u8001\u4f2f\u4e5f\u77e5\u9053\u82d7\u59d1\u5a18\u7684\u59d0\u59d0\u5728\u8ff7\u8e2a\u8c37\uff1f\u201d \u3000\u3000\u201c\u8fd9\u4e8b\u82d3\u513f\u5f53\u7136\u65e9\u5c31\u5bf9\u8001\u592b\u8bb2\u4e86\uff0c\u5979\u8bf4\u5979\u59d0\u59d0\u82d7\u7d20\u5170\u5c45\u7136\u505a\u4e86\u970d\u5143\u4f3d\u90a3\u72d7\u4e1c\u897f\u7684\u538b\u5be8\u592b \u4eba\u3002\u8fd9\u4e8b\u8fde\u8001\u592b\u90fd\u900f\u7740\u4e0d\u89e3\uff0c\u5c0f\u5b50\uff0c\u4f60\u4e00\u5b9a\u77e5\u9053\u539f\u56e0\u5427\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u82e6\u7b11\u4e86\u4e00\u4e0b\uff0c\u6447\u6447\u5934\u9053\uff1a\u201c\u8fd9\u4e8b\u665a\u8f88\u4e5f\u83ab\u540d\u5176\u5999\uff0c\u90a3\u4f4d\u82d7\u59d1\u5a18\u539f\u662f\u8ddf\u7740\u80e1\u524d\u76df\u4e3b \u8fdb\u5165\u8ff7\u8e2a\u8c37\u7684\uff0c\u5bf9\u80e1\u524d\u76df\u4e3b\u53ef\u8bf4\u5fe0\u5fc3\u4e0d\u4e8c\uff0c\u80e1\u524d\u76df\u4e3b\u5f52\u5929\u540e\uff0c\u5979\u548c\u8c37\u76df\u4e3b\u66f4\u662f\u60c5\u540c\u59d0\u59b9\uff0c\u771f \u4e0d\u77e5\u5979\u4e3a\u4ec0\u4e48\u4f1a\u53d8\u5fc3\u6295\u5411\u4e86\u970d\u5143\u4f3d\uff1f\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u9ed8\u4e86\u4e00\u9ed8\u9053\uff1a\u201c\u6240\u4ee5\u8001\u592b\u624d\u60f3\u5230\u8981\u67e5\u660e\u539f\u56e0\uff0c\u975e\u5f97\u82d3\u513f\u4e0d\u53ef\u3002\u5979\u4eec\u662f\u4eb2\u59d0\u59b9\uff0c\u90a3 \u4f4d\u82d7\u59d1\u5a18\u4e00\u5b9a\u53bb\u5bf9\u82d3\u513f\u8bf4\u51fa\u5fc3\u91cc\u7684\u8bdd\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u96be\u5f97\u8001\u4f2f\u80af\u4e3a\u8fd9\u4e8b\u653e\u82d3\u59d1\u5a18\u5230\u8ff7\u8e2a\u8c37\u53bb\uff0c\u4f60\u65b9\u624d\u8bf4\u53ea\u662f\u8ba9\u82d3\u59d1\u5a18\u6682\u65f6\u79bb\u5f00 \u60a8\uff0c\u90a3\u4e48\u5979\u4ee5\u540e\u53c8\u5230\u4ec0\u4e48\u5730\u65b9\u627e\u60a8\u5462\uff1f\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u9053\uff1a\u201c\u65f6\u95f4\u4e00\u5230\uff0c\u8001\u592b\u81ea\u4f1a\u8bbe\u6cd5\u901a\u77e5\u5979\u5230\u4ec0\u4e48\u5730\u65b9\u627e\u6211\uff0c\u7528\u4e0d\u7740\u4f60\u64cd\u5fc3\u3002\u201d \u3000\u3000\u6b63\u8bf4\u5230\u8fd9\u91cc\uff0c\u8eba\u5728\u4e00\u65c1\u7684\u4e07\u6620\u971e\u5df2\u60a0\u60a0\u9192\u6765\u3002 \u3000\u3000\u5f53\u5979\u770b\u5230\u8eab\u8fb9\u591a\u4e86\u4e00\u540d\u602a\u8001\u4eba\u548c\u4e00\u540d\u7f8e\u4e3d\u5c11\u5973\uff0c\u4e0d\u7531\u5fae\u5fae\u4e00\u6123\u3002\u4f46\u5374\u6ca1\u8bf4\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u5f88\u5feb\u5979\u5df2\u8ba4\u51fa\u8fd9\u540d\u602a\u8001\u4eba\u662f\u957f\u767d\u795e\u53df\u5e9e\u58eb\u51b2\uff0c\u56e0\u4e3a\u6570\u6708\u524d\u5979\u4e5f\u66fe\u53c2\u52a0\u8fc7\u5929\u53f0\u5c71\u4e07 \u82b1\u5bab\u90a3\u6b21\u8840\u6218\uff0c\u5f53\u7136\u4e5f\u89c1\u8fc7\u5e9e\u58eb\u51b2\u3002 \u3000\u3000\u82d7\u7d20\u82d3\u8fde\u5fd9\u4eb2\u5207\u5730\u628a\u4e07\u6620\u971e\u6276\u8d77\u3002 \u3000\u3000\u4e07\u6620\u971e\u56db\u4e0b\u5f20\u671b\u4e86\u4e00\u9635\uff0c\u4f3c\u662f\u5fc3\u6709\u4f59\u60b8\u5730\u95ee\u9053\uff1a\u201c\u9634\u624b\u4e00\u9b54\u90a3\u8001\u9b54\u5934\u5462\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u88ab\u5e9e\u8001\u4f2f\u6253\u8dd1\u5566\uff0e\u4f60\u4e5f\u662f\u5e9e\u8001\u4f2f\u6551\u7684\uff0c\u8fd8\u4e0d\u5feb\u62dc\u8c22\u4ed6\u8001\u4eba\u5bb6\uff01\u201d \u3000\u3000\u4e07\u6620\u971e\u8fde\u5fd9\u65e0\u9650\u611f\u6fc0\u7684\u5411\u5e9e\u58eb\u51b2\u62dc\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u5e9e\u58eb\u51b2\u9053\uff1a\u201c\u7528\u4e0d\u7740\u5ba2\u6c14\uff0c\u82d3\u513f\u5feb\u5feb\u6276\u4e07\u59d1\u5a18\u8d77\u6765\uff01\u201d \u3000\u3000\u4e07\u6620\u971e\u8d77\u8eab\u540e\uff0c\u671b\u7740\u82d7\u7d20\u82d3\u9053\uff1a\u201c\u8fd9\u4f4d\u59d1\u5a18\u662f\u8c01\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u5979\u5c31\u662f\u6211\u5bf9\u4f60\u8bf4\u8fc7\u7684\u82d7\u7d20\u82d3\u59d1\u5a18\uff0e\u4e5f\u5c31\u662f\u54b1\u4eec\u8ff7\u8e2a\u8c37\u90a3\u4f4d\u538b\u5be8\u592b\u4eba\u7684\u4eb2\u59b9 \u59b9\u3002\u201d \u3000\u3000\u4e07\u6620\u971e\u54e6\u4e86\u4e00\u58f0\uff0c\u6025\u6025\u88e3\u887d\u65bd\u793c\u9053\uff1a\u201c\u539f\u6765\u662f\u82d7\u59d1\u5a18\uff0c\u5931\u656c\u4e86\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u82d7\u59d1\u5a18\u73b0\u5728\u5df2\u662f\u5e9e\u8001\u4f2f\u7684\u9ad8\u8db3\uff0e\u5e9e\u8001\u4f2f\u5df2\u51b3\u5b9a\u8ba9\u5979\u548c\u6211\u4eec\u4e00\u8d77\u56de\u8ff7\u8e2a\u8c37 \u53bb\u3002\u201d \u3000\u3000\u4e07\u6620\u971e\u9053\uff1a\u201c\u90a3\u592a\u597d\u5566\uff01\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u89c1\u4e07\u6620\u971e\u5df2\u5b8c\u5168\u6062\u590d\uff0c\u968f\u5373\u5429\u5490\u82d7\u7d20\u82d3\u9053\uff1a\u201c\u82d3\u513f\uff0c\u4f60\u5c31\u968f\u4ed6\u4eec\u8d70\u5427\uff01\u65f6\u95f4\u4e00 \u5230\uff0c\u4e3a\u5e08\u81ea\u4f1a\u8bbe\u6cd5\u63a5\u4f60\u56de\u6765\uff0c\u6211\u8d70\u5566\uff01\u201d \u3000\u3000\u773c\u770b\u5e9e\u58eb\u51b2\u7684\u4eba\u5f71\u6d88\u901d\u4e0d\u89c1\uff0c\u82d7\u7d20\u82d3\u624d\u95ee\u9ea6\u5c0f\u660e\u548c\u4e07\u6620\u971e\u4e3a\u4ec0\u4e48\u4e0d\u5728\u8ff7\u8e2a\u8c37\uff0c\u5374\u6765\u5230\u8fd9 \u91cc\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u56e0\u82d7\u7d20\u82d3\u5df2\u5c06\u8ddf\u968f\u81ea\u5df1\u884c\u52a8\uff0c\u81ea\u7136\u4e0d\u80fd\u9690\u7792\uff0c\u5f53\u5373\u628a\u8981\u5230\u5c3c\u5eb5\u7684\u4e8b\u8bf4\u4e86\u4e00\u904d\u3002 \u3000\u3000\u7531\u8fd9\u91cc\u5230\u90a3\u5c3c\u5eb5\uff0e\u5927\u7ea6\u53ea\u6709\u4e03\u516b\u91cc\u8def\uff0c\u9ea6\u5c0f\u660e\u4f9d\u7a00\u4ecd\u80fd\u8bb0\u5f97\u9014\u5f84\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u8fb9\u8d70\u8fb9\u95ee\u9053\uff1a\u201c\u82d7\u59d1\u5a18\u8fd9\u4e9b\u5929\u548c\u5e9e\u8001\u4f2f\u90fd\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u201d \u3000\u3000\u82d7\u7d20\u82d3\u9053\uff1a\u201c\u8fd9\u534a\u4e2a\u591a\u6708\uff0c\u968f\u5e08\u7236\u8d70\u4e86\u5f88\u591a\u5730\u65b9\uff0c\u81f3\u4e8e\u662f\u4ec0\u4e48\u5730\u540d\uff0c\u6211\u4e5f\u8bb0\u4e0d\u6e05\u695a\u3002\u603b \u4e4b\uff0c\u5e08\u7236\u548c\u6211\u2014\u76f4\u662f\u9a6c\u4e0d\u505c\u8e44\u5730\u5728\u8d76\u8def\uff0e\u201d \u3000\u3000\u201c\u5e9e\u8001\u4f2f\u4e00\u5b9a\u8bf4\u8fc7\u4e1c\u5954\u897f\u8dd1\u7684\u539f\u56e0\u5427\uff1f\u201d \u3000\u3000\u201c\u4ed6\u8001\u4eba\u5bb6\u662f\u8981\u627e\u8ff7\u8e2a\u8c37\u8c37\u524d\u76df\u4e3b\u7684\u9057\u9ab8\u3002\u201d \u3000\u3000\u201c\u8c37\u524d\u76df\u4e3b\u7684\u9057\u9ab8\u662f\u5728\u5929\u53f0\u5c71\u88ab\u4eba\u76d7\u8d70\u7684\uff0c\u79bb\u8fd9\u91cc\u9065\u9065\u6570\u5343\u91cc\uff0c\u5728\u5c71\u897f\u5730\u754c\u600e\u4f1a\u627e\u5230 \u5462\uff1f\u201d \u3000\u3000\u201c\u90a3\u6211\u5c31\u4e0d\u660e\u767d\u4e86\uff0e\u201d \u3000\u3000\u201c\u6211\u4e0a\u6b21\u548c\u4f60\u5206\u624b\u540e\uff0e\u66fe\u9047\u5230\u4e00\u4f4d\u8499\u9762\u9ed1\u8863\u5973\u5b50\uff0c\u90a3\u5973\u5b50\u7684\u8eab\u6750\u4e3e\u6b62\uff0c\u548c\u8c37\u524d\u76df\u4e3b\u5b8c\u5168 \u4e00\u6a21\u4e00\u6837\u3002\u6240\u4ee5\u6211\u6000\u7591\u8c37\u524d\u76df\u4e3b\u5e76\u6ca1\u6b7b\u3002\u8fd9\u6b21\u548c\u4e07\u59d1\u5a18\u5230\u90a3\u5904\u5c3c\u5eb5\uff0c\u76ee\u7684\u4e0d\u5916\u662f\u5e0c\u671b\u80fd\u627e\u5230 \u8c37\u524d\u76df\u4e3b\u3002\u201d \u3000\u3000\u82d7\u7d20\u82d3\u53ea\u542c\u5f97\u5927\u611f\u8ff7\u60d1\u9053\uff1a\u201c\u4eba\u6b7b\u4e0d\u80fd\u590d\u6d3b\uff0c\u4f60\u4eec\u4e0d\u662f\u660e\u660e\u770b\u5230\u8c37\u524d\u76df\u4e3b\u6b7b\u5728\u5929\u53f0\u4e07\u82b1 \u8c37\u5417\uff1f\u800c\u4e14\u5979\u7684\u9057\u4f53\u8fd8\u88ab\u5e08\u7236\u5e26\u7740\u8d70\u4e86\u5341\u51e0\u91cc\u8def\u3002\u82e5\u5979\u8fd8\u6d3b\u7740\uff0c\u5e08\u7236\u600e\u4f1a\u770b\u4e0d\u51fa\u6765\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u5401\u4e86\u4e00\u53e3\u6c14\u9053\uff1a\u201c\u4f60\u8bf4\u7684\u56fa\u7136\u6709\u7406\uff0c\u4f46\u90a3\u665a\u6211\u6240\u89c1\u7684\u9ed1\u8863\u8499\u9762\u5973\u5b50\uff0c\u5374\u592a\u50cf\u8c37\u524d \u76df\u4e3b\u4e86\uff0c\u6211\u5fc5\u987b\u89e3\u5f00\u8fd9\u4e2a\u8c1c\u3002\u201d \u3000\u3000\u82d7\u7d20\u82d3\u4f3c\u662f\u4e5f\u88ab\u5f15\u8d77\u597d\u5947\uff0c\u9053\uff1a\u201c\u542c\u8bf4\u8c37\u524d\u76df\u4e3b\u56fd\u8272\u5929\u9999\uff0c\u7f8e\u8273\u5929\u4e0b\u65e0\u53cc\uff0c\u6211\u65e9\u5c31\u5e0c\u671b \u80fd\u4e00\u7779\u5979\u7684\u7edd\u4e16\u5bb9\u989c\u3002\u5982\u679c\u90a3\u4f4d\u9ed1\u8863\u8499\u9762\u5973\u5b50\u771f\u548c\u5979\u4e00\u6a21\u4e00\u6837\uff0c\u89c1\u4e86\u5979\u4e5f\u5c31\u7b49\u4e8e\u770b\u5230\u8c37\u524d\u76df \u4e3b\u4e86\uff01\u201d \u3000\u3000\u8bf4\u8bdd\u95f4\u4e0d\u77e5\u4e0d\u89c9\u8f6c\u8fc7\u4e24\u4e2a\u5c71\u811a\uff0e\u518d\u5f80\u524d\u4fbf\u662f\u901a\u5f80\u90a3\u5904\u5c3c\u5eb5\u7684\u5c94\u8def\u3002 \u3000\u3000\u5ffd\u542c\u4e07\u6620\u971e\u5931\u58f0\u9053\uff1a\u201c\u4f60\u770b\u524d\u9762\u5c71\u811a\u4e0b\u4e0d\u6b63\u6709\u4f4d\u9ed1\u8863\u8499\u9762\u5973\u5b50\uff1f\u4e00\u70b9\u4e0d\u5047\uff0c\u770b\u5979\u7684\u80cc\u5f71 \u548c\u8d70\u8def\u59ff\u6001\uff0c\u8fd8\u771f\u50cf\u8c37\u524d\u76df\u4e3b\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u6025\u6025\u5411\u524d\u65b9\u8fdc\u5904\u671b\u53bb\uff0c\u679c\u7136\u90a3\u8499\u9762\u5973\u5b50\u6b63\u662f\u4e0a\u6b21\u591c\u95f4\u6240\u9047\u9177\u4f3c\u8c37\u5bd2\u9999\u7684\u5973\u4eba\uff0c\u5f53 \u4e0b\u7acb\u5373\u52a0\u5feb\u811a\u6b65\u9053\uff1a\u201c\u54b1\u4eec\u5feb\u8ffd\uff01\u201d \u3000\u3000\u8fd9\u65f6\uff0e\u90a3\u9ed1\u8863\u8499\u9762\u5973\u5b50\u4e0e\u9ea6\u5c0f\u660e\u7b49\u4eba\u76f8\u8ddd\u5927\u7ea6\u5341\u51e0\u4e08\u8fdc\uff0c\u82e5\u65bd\u5c55\u8f7b\u529f\uff0c\u51e0\u4e2a\u7eb5\u8dc3\u4fbf\u53ef\u8ffd \u4e0a\u3002 \u3000\u3000\u4f46\u56e0\u62c5\u5fc3\u60ca\u52a8\u4e86\u5bf9\u65b9\uff0c\u53cd\u800c\u4f1a\u5f04\u5de7\u6210\u62d9\uff0c\u56e0\u4e4b\uff0c\u4e09\u4eba\u53ea\u80fd\u52a0\u5feb\u811a\u6b65\uff0c\u5374\u4e0d\u4fbf\u6025\u8d77\u76f4\u8ffd\uff0e \u3000\u3000\u773c\u770b\u90a3\u9ed1\u8863\u8499\u9762\u5973\u5b50\u8f6c\u5165\u5c94\u8def\uff0c\u800c\u90a3\u5c94\u8def\u53c8\u662f\u901a\u5f80\u5c3c\u5eb5\u7684\u5fc5\u7ecf\u4e4b\u8def\uff0c\u9ea6\u5c0f\u660e\u4e0d\u89c9\u6697\u559c\u3002 \u56e0\u4e3a\uff0c\u56e0\u4e3a\u7531\u6b64\u4e0d\u96be\u9884\u6599\u5f85\u4f1a\u513f\u5230\u4e86\u5c3c\u5eb5\u5fc5\u6709\u6240\u83b7\uff0e\u603b\u7b97\u4e0d\u865a\u6b64\u884c\u3002 \u3000\u3000\u5c82\u77e5\u9ea6\u5c0f\u660e\u7b49\u4e09\u4eba\u8d76\u5230\u90a3\u6761\u5c94\u8def\u53e3\u65f6\uff0c\u9ed1\u8863\u8499\u9762\u5973\u5b50\u5df2\u7ecf\u5931\u53bb\u8e2a\u5f71\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u9ea6\u5c0f\u660e\u5e76\u672b\u56e0\u6b64\u5931\u671b\uff0c\u56e0\u4e3a\u8fd9\u6761\u5c94\u8def\u4e24\u65c1\u90fd\u662f\u4e1b\u6797\uff0c\u800c\u4e14\u53c8\u66f2\u66f2\u6298\u6298\uff0c\u8def\u5728\u4e1b\u6797 \u5f53\u4e2d\uff0c\u53cc\u65b9\u53ea\u8981\u7a0d\u6709\u4e00\u70b9\u8ddd\u79bb\uff0c\u5c31\u4e0d\u6613\u88ab\u53d1\u73b0\u3002 \u3000\u3000\u4e8e\u662f\uff0c\u4ed6\u653e\u7f13\u811a\u6b65\u9053\uff1a\u201c\u4e0d\u5fc5\u8ffd\u4e86\uff01\u5979\u2014\u5b9a\u5230\u4e86\u5c3c\u5eb5\u3002\u54b1\u4eec\u53ea\u8981\u627e\u5230\u5c3c\u5eb5\uff0c\u4fbf\u53ef\u627e\u5230 \u5979\u3002\u201d \u3000\u3000\u4e07\u6620\u971e\u9053\uff1a\u201c\u60a8\u600e\u80fd\u65ad\u5b9a\u5979\u4e00\u5b9a\u4f1a\u5230\u5c3c\u5eb5\u53bb\u5462\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u6211\u4e0a\u6b21\u6765\u65f6\u4ed4\u7ec6\u89c2\u5bdf\u8fc7\uff0c\u8fd9\u9644\u8fd1\u9664\u4e86\u90a3\u5904\u5c3c\u5eb5\uff0c\u5e76\u65e0\u5176\u4ed6\u4eba\u5bb6\uff0c\u6beb\u65e0\u7591\u95ee \u5979\u662f\u5f80\u5c3c\u5eb5\u53bb\u4e86\uff0e\u201d \u3000\u3000\u51fa\u4e86\u4e1b\u6797\uff0c\u524d\u9762\u4fbf\u662f\u4e00\u7247\u5893\u5730\uff0c\u9ea6\u5c0f\u660e\u60f3\u8d77\u90a3\u665a\u4e4b\u4e8b\uff0c\u7b11\u9053\uff1a\u201c\u4e0a\u6b21\u6211\u6765\u65f6\u662f\u591c\u91cc\uff0c\u8fd8 \u7279\u5730\u505c\u4e0b\u6765\u5bdf\u770b\u4e86\u6240\u6709\u5893\u7891\u3002\u201d \u3000\u3000\u4e07\u6620\u971e\u4e00\u6123\u9053\uff1a\u201c\u5bdf\u770b\u5893\u7891\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u6211\u5f53\u65f6\u6709\u4e2a\u5947\u7279\u60f3\u6cd5\uff0c\u7adf\u8ba4\u4e3a\u90a3\u9ed1\u8863\u8499\u9762\u5973\u5b50\u662f\u8c37\u524d\u76df\u4e3b\u7684\u7075\u9b42\u51fa\u73b0\u3002\u770b \u5230\u8fd9\u7247\u5893\u5730\uff0c\u5fc3\u60f3\u5979\u4e00\u5b9a\u662f\u88ab\u4eba\u628a\u9057\u9ab8\u5e26\u6765\u846c\u5728\u8fd9\u91cc\u3002\u6b63\u597d\u90a3\u665a\u6709\u6708\u4eae\uff0c\u4fbf\u628a\u6240\u6709\u7684\u5893\u7891\u4ed4 \u7ec6\u5bdf\u770b\uff0c\u770b\u662f\u5426\u6709\u8c37\u524d\u76df\u4e3b\u7684\u540d\u5b57\u3002\u201d \u3000\u3000\u4ed6\u8fd9\u756a\u8bdd\u8bf4\u6765\u8f7b\u677e\uff0c\u4f46\u542c\u5728\u4e07\u6620\u971e\u548c\u82d7\u7d20\u82d3\u8033\u91cc\uff0e\u5374\u90fd\u6709\u6bdb\u9aa8\u609a\u7136\u4e4b\u611f\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u89c1\u4e8c\u4eba\u8138\u8272\u6709\u4e9b\u4e0d\u5bf9\uff0e\u8fde\u5fd9\u53c8\u9053\uff1a\u201c\u90a3\u662f\u56e0\u4e3a\u5f53\u65f6\u591c\u91cc\uff0c\u800c\u4e14\u90a3\u9ed1\u8863\u8499\u9762\u5973\u5b50\u7684 \u4eba\u5f71\u53c8\u662f\u5728\u9644\u8fd1\u6d88\u5931\u7684\uff0c\u6240\u4ee5\u624d\u4f1a\u60f3\u5230\u8fd9\u65b9\u9762\u7684\u3002\u5982\u679c\u662f\u5927\u767d\u5929\uff0c\u5c31\u4e0d\u53ef\u80fd\u6709\u8fd9\u79cd\u60f3\u6cd5\u3002\u201d \u3000\u3000\u8d8a\u8fc7\u5893\u5730\uff0c\u518d\u8d70\u4e86\u534a\u91cc\u5de6\u53f3\uff0c\u524d\u9762\u5c71\u811a\u4e0b\u4fbf\u662f\u90a3\u5904\u5c3c\u5eb5\u3002 \u3000\u3000\u4e0a\u6b21\u56e0\u5728\u591c\u95f4\uff0c\u9ea6\u5c0f\u660e\u5e76\u672a\u770b\u6e05\u5c3c\u5eb5\u5168\u8c8c\uff0e\u8fd9\u6b21\u7ec8\u4e8e\u770b\u6e05\u8fd9\u5ea7\u5c3c\u5eb5\u89c4\u6a21\u867d\u4e0d\u7b97\u5927\uff0c\u4f46\u5374 \u53e4\u8272\u53e4\u9999\uff0c\u96c5\u81f4\u975e\u5e38\uff0c\u5eb5\u524d\u5e7d\u7bc1\u4fee\u7af9\uff0c\u6797\u6728\u63a9\u6620\uff0c\u5927\u6709\u8d85\u5c18\u51fa\u4fd7\u4e4b\u6168\uff0e \u3000\u3000\u5eb5\u95e8\u4e0a\u65b9\u7684\u533e\u989d\u4e0a\u5199\u7740\u201c\u5999\u5999\u5eb5\u201d\u4e09\u4e2a\u4eff\u5b8b\u5927\u5b57\uff0c\u4ec5\u770b\u8fd9\u5eb5\u540d\uff0c\u4fbf\u663e\u5f97\u4e0e\u4f17\u4e0d\u540c\uff0e \u3000\u3000\u9ea6\u5c0f\u660e\u7b49\u4eba\u521a\u8981\u4e3e\u6b65\u8de8\u8fdb\u5eb5\u95e8\uff0c\u7a81\u89c1\u4e00\u540d\u5341\u4e03\u516b\u5c81\u7684\u5c0f\u5c3c\u51b2\u4e86\u51fa\u6765\uff0c\u53f1\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\u4e0d \u7ecf\u901a\u62a5\u4fbf\u8981\u5f80\u5eb5\u91cc\u4e71\u95ef\uff1f\u201d \u3000\u3000\u51fa\u5bb6\u4eba\u7adf\u7136\u5982\u6b64\u51f6\u608d\uff0c\u5b9e\u5728\u51fa\u4eba\u610f\u6599\uff0c\u5c24\u5176\u8fd9\u5c0f\u5c3c\u5507\u7ea2\u9f7f\u767d\uff0c\u770b\u6765\u5341\u5206\u5a07\u4fcf\uff0c\u7b80\u76f4\u4ee4\u4eba \u4e0d\u76f8\u4fe1\u5bf9\u4eba\u7adf\u662f\u8fd9\u6837\u75be\u8a00\u5389\u8272\u3002 \u3000\u3000\u82e5\u4f9d\u9ea6\u5c0f\u660e\u7684\u6027\u5b50\uff0c\u65e9\u5c31\u76f4\u51b2\u8fdb\u53bb\uff0c\u8bf4\u4e0d\u5b9a\u8fd8\u8981\u53cd\u9a82\u90a3\u5c0f\u5c3c\u51e0\u53e5\uff0c\u4f46\u73b0\u5728\u4ed6\u5374\u4e0d\u5f97\u4e0d\u8c28 \u614e\u5c0f\u5fc3\u3002 \u3000\u3000\u4e00\u6765\u662f\u4ed6\u6b64\u6765\u6709\u6c42\u4e8e\u4eba\uff0c\u4e8c\u6765\u4ed6\u60f3\u5230\u90a3\u7f01\u8863\u8001\u5c3c\u7684\u76d6\u4e16\u6b66\u529f\uff0c\u82e5\u5435\u95f9\u8d77\u6765\uff0c\u90a3\u5c31\u5403\u4e0d\u4e86 \u515c\u7740\u8d70\u4e86\u3002 \u3000\u3000\u60f3\u5230\u8fd9\u91cc\uff0c\u8fde\u5fd9\u62b1\u62f3\u2014\u793c\u9053\uff1a\u201c\u6211\u4eec\u662f\u6765\u6c42\u89c1\u5eb5\u4e3b\u7684\uff0c\u56e0\u4e3a\u627e\u4e0d\u5230\u4eba\u901a\u62a5\uff0c\u4e0d\u5f97\u4e0d\u76f4\u63a5 \u8fdb\u6765\u3002\u201d \u3000\u3000\u90a3\u5c0f\u5c3c\u51b7\u7740\u9762\u5b54\u9053\uff1a\u201c\u6211\u4eec\u5eb5\u4e3b\u4ece\u4e0d\u89c1\u5ba2\uff0c\u901a\u62a5\u4e5f\u662f\u6789\u7136\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u966a\u7740\u5c0f\u5fc3\u9053\uff1a\u201c\u6211\u4eec\u5b9e\u5728\u662f\u6709\u8981\u7d27\u7684\u4e8b\uff0c\u8d76\u4e86\u597d\u51e0\u5929\u8def\u624d\u6765\u5230\u8d35\u5eb5\uff0e\u52a1\u8bf7\u5c0f\u5e08\u592a \u4ee3\u6211\u4eec\u901a\u62a5\u4e00\u58f0\uff01\u201d \u3000\u3000\u90a3\u5c0f\u5c3c\u54fc\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u4f60\u8bf4\u5f97\u867d\u7136\u597d\u542c\uff0e\u53ef\u60dc\u4e0d\u5bf9\u52b2\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u832b\u7136\u9053\uff1a\u201c\u4ec0\u4e48\u53eb\u4e0d\u5bf9\u52b2\uff1f\u201d \u3000\u3000\u90a3\u5c0f\u5c3c\u9053\uff1a\u201c\u6211\u4eec\u5eb5\u4e3b\u7acb\u4e0b\u7684\u89c4\u77e9\uff0c\u53ea\u51c6\u5973\u4eba\u8fdb\u5eb5\uff0e\u7537\u4eba\u4e0d\u5f97\u5165\u5185\uff0c\u4f60\u662f\u7537\u4eba\u4e0d\u662f\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u76b1\u4e86\u76b1\u7709\u9053\uff1a\u201c\u5c0f\u5e08\u592a\u597d\u773c\u529b\uff0c\u5c45\u7136\u770b\u4e0d\u51fa\u5728\u4e0b\u662f\u7537\u662f\u5973\uff0e\u5982\u679c\u4f60\u4ee5\u4e3a\u5728\u4e0b\u662f\u5973 \u7684\uff0e\u5c31\u8be5\u653e\u6211\u8fdb\u53bb\u3002\u201d \u3000\u3000\u90a3\u5c0f\u5c3c\u8138\u4e0a\u7ec8\u4e8e\u6709\u4e86\u7b11\u5bb9\uff0c\u9053\uff1a\u201c\u65e2\u7136\u4f60\u4e5f\u662f\u5973\u7684\uff0c\u90a3\u6211\u5c31\u8fdb\u53bb\u901a\u62a5\u4e00\u58f0\uff0c\u4e0d\u8fc7\u4f60\u4eec\u5148 \u8981\u544a\u8bc9\u6211\u662f\u4e3a\u4ec0\u4e48\u4e8b\u800c\u6765\uff1f\u201d \u3000\u3000\u201c\u6211\u4eec\u89c1\u4e86\u5eb5\u4e3b\u540e\uff0c\u81ea\u7136\u4f1a\u4e3b\u52a8\u8bb2\u660e\u6765\u610f\u3002\u201d \u3000\u3000\u201c\u4e0d\u6210\uff01\u5eb5\u4e3b\u4f1a\u5148\u95ee\u6211\uff0c\u6211\u4e0d\u80fd\u56de\u7b54\u4e0d\u4e0a\u6765\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u987a\u53e3\u9053\uff1a\u201c\u4f60\u5c31\u8bf4\u6211\u4eec\u548c\u5eb5\u4e3b\u662f\u65e7\u8bc6\uff0e\u201d \u3000\u3000\u90a3\u5c0f\u5c3c\u9053\uff1a\u201c\u90a3\u662f\u6545\u4eba\u6765\u8bbf\u4e86\uff0c\u4f60\u4eec\u4e3a\u4ec0\u4e48\u4e0d\u65e9\u8bf4\uff1f\u7b49\u7740\uff0c\u6211\u9a6c\u4e0a\u8fdb\u53bb\u901a\u62a5\u3002\u201d \u3000\u3000\u5927\u7ea6\u8fc7\u4e86\u987f\u996d\u5de5\u592b\uff0c\u90a3\u5c0f\u5c3c\u624d\u8d70\u56de\u6765\u9053\uff1a\u201c\u968f\u6211\u5230\u7cbe\u820d\u6765\u5427\uff01\u201d \u3000\u3000\u8fdb\u5165\u5eb5\u95e8\uff0c\u53c8\u7a7f\u8fc7\u4e24\u9053\u95e8\uff0c\u90a3\u5c0f\u5c3c\u624d\u628a\u4e09\u4eba\u5f15\u5165\u4e00\u95f4\u7985\u5802\u9053\uff1a\u201c\u8fd9\u662f\u5eb5\u91cc\u7684\u5f85\u5ba2\u7cbe\u820d\uff0c \u5eb5\u4e3b\u901a\u5e38\u90fd\u5728\u8fd9\u91cc\u63a5\u5f85\u5ba2\u4eba\u3002\u4f60\u4eec\u7a0d\u5019\u4e00\u4e0b\uff0c\u5eb5\u4e3b\u9a6c\u4e0a\u5c31\u5230\u3002\u201d \u3000\u3000\u6b64\u523b\u7684\u5c0f\u5c3c\u5df2\u663e\u5f97\u4eb2\u5207\u591a\u4e86\uff0c\u4e34\u8d70\u65f6\u8fd8\u7279\u5730\u6ce1\u4e86\u4e09\u676f\u8336\u653e\u5728\u8336\u51e0\u4e0a\u3002 \u3000\u3000\u8fd9\u95f4\u7cbe\u820d\u7a97\u660e\u51e0\u51c0\uff0c\u5e03\u7f6e\u9ad8\u96c5\uff0c\u679c\u7136\u662f\u4f5b\u95e8\u6e05\u51c0\u4e4b\u5730\uff0e \u3000\u3000\u9ea6\u5c0f\u660e\u4f4e\u58f0\u4ea4\u4ee3\u82d7\u7d20\u82d3\u9053\uff1a\u201c\u5f85\u4f1a\u513f\u5eb5\u4e3b\u6765\u4e86\uff0e\u5343\u4e07\u8981\u4fdd\u6301\u793c\u8c8c\uff0c\u8868\u73b0\u5f97\u8d8a\u606d\u656c\u8d8a \u597d\uff01\u201d \u3000\u3000\u82d7\u7d20\u82d3\u5e26\u4e9b\u4e0d\u89e3\u9053\uff1a\u201c\u4f60\u4e00\u5411\u662f\u4e2a\u5929\u4e0d\u6015\u5730\u4e0d\u6015\u7684\u4eba\uff0c\u4e3a\u4ec0\u4e48\u534a\u4e2a\u591a\u6708\u4e0d\u89c1\uff0c\u5c31\u53d8\u5f97\u8fd9 \u4e48\u8c28\u614e\u5c0f\u5fc3\u8d77\u6765\u4e86\u5462\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4f60\u95ee\u4e07\u59d1\u5a18\u5c31\u77e5\u9053\u5566\uff0e\u8fd9\u4f4d\u5eb5\u4e3b\u524d\u4e9b\u5929\u5230\u8ff7\u8e2a\u8c37\uff0e\u72ec\u529b\u6740\u9000\u4e86\u9634\u624b\u2014\u9b54\u7b49 \u4e94\u4eba\uff0c\u633d\u6551\u4e86\u8ff7\u8e2a\u8c37\u4e00\u573a\u5927\u52ab\u3002 \u3000\u3000\u5979\u7684\u6b66\u529f\u4e4b\u9ad8\uff0c\u79f0\u5f97\u4e0a\u662f\u524d\u65e0\u53e4\u4eba\uff0c\u540e\u65e0\u6765\u8005\u3002\u522b\u770b\u6211\u5e74\u7eaa\u8f7b\uff0c\u5374\u89c1\u8fc7\u4e0d\u5c11\u5f53\u4ee3\u9ad8\u4eba\uff0e \u8fde\u521a\u6b7b\u4e0d\u4e45\u7684\u5929\u53f0\u4e07\u82b1\u5bab\u4e3b\u4f5f\u516c\u5e38\u6b66\u529f\u4e5f\u6bd4\u4e0d\u4e0a\u5979\u3002\u8fd9\u6837\u4e00\u4f4d\u9ad8\u4eba\uff0e\u54b1\u4eec\u600e\u80fd\u4e0d\u5bf9\u5979\u5c0a \u656c\u3002\u201d \u3000\u3000\u82d7\u7d20\u82d3\u4e5f\u542c\u5f97\u8083\u7136\u52a8\u5bb9\uff0c\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\uff0c\u8fd9\u771f\u662f\u6211\u7684\u8363\u5e78\uff0c\u4eca\u5929\u7adf\u6709\u673a\u4f1a\u77bb\u4ef0\u5230\u8fd9\u6837 \u4e00\u4f4d\u7edd\u4e16\u9ad8\u4eba\u3002\u201d \u3000\u3000\u6b63\u8c08\u8bdd\u95f4\uff0c\u90a3\u5c0f\u5c3c\u5df2\u966a\u7740\u4e00\u4f4d\u4e2d\u5e74\u5973\u5c3c\u7f13\u7f13\u8d70\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u7b49\u4eba\u89c1\u8fdb\u6765\u7684\u867d\u4e0d\u662f\u90a3\u4f4d\u7f01\u8863\u8001\u5c3c\uff0c\u4e5f\u4e00\u8d77\u7ad9\u8d77\u8eab\u6765\u3002 \u3000\u3000\u90a3\u4e2d\u5e74\u5973\u5c3c\u795e\u6001\u5341\u5206\u4e25\u8083\uff0c\u3000\u4e00\u770b\u4fbf\u77e5\u5728\u5c3c\u5eb5\u4e2d\u5fc5\u5b9a\u4e5f\u5f88\u6709\u8eab\u4efd\u5730\u4f4d\u3002\u5750\u5b9a\u4e4b\u540e\u5355\u638c\u7acb \u80f8\uff0c\u6253\u4e86\u4e2a\u95ee\u8baf\u9053\uff1a\u201c\u4e8c\u4f4d\u53ef\u662f\u8981\u89c1\u8d2b\u5c3c\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u966a\u7b11\u9053\uff1a\u201c\u665a\u8f88\u6c42\u89c1\u7684\uff0c\u662f\u8d35\u5eb5\u7684\u5eb5\u4e3b\uff01\u201d \u3000\u3000\u4e2d\u5e74\u5973\u5c3c\u4e0d\u52a8\u58f0\u9053\uff1a\u201c\u8d2b\u5c3c\u4fbf\u662f\u8fd9\u91cc\u7684\u5eb5\u4e3b\u3002\u201d \u3000\u3000\u8fd9\u4f7f\u9ea6\u5c0f\u660e\u7b49\u4e09\u4eba\u90fd\u4e0d\u514d\u4e3a\u4e4b\u9519\u6115\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u4e0d\u77e5\u8be5\u8bf4\u4ec0\u4e48\u624d\u597d\u3002 \u5367\u9f99\u751f\u300a\u5999\u7edd\u5929\u9999\u300b \u7b2c\u5341\u4e09\u56de\u3000\u62dc\u5e08\u5999\u5c3c \u3000\u3000\u4e2d\u5e74\u5973\u5c3c\u9053\uff1a\u201c\u4e09\u4f4d\u5c0f\u65bd\u4e3b\u65e2\u7136\u8981\u89c1\u8d2b\u5c3c\uff0c\u8d2b\u5c3c\u6765\u4e86\uff0c\u4e3a\u4ec0\u4e48\u53c8\u4e0d\u8bf4\u8bdd\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u8f7b\u54b3\u4e86\u2014\u58f0\u9053\uff1a\u201c\u665a\u8f88\u4eec\u6c42\u89c1\u7684\u597d\u50cf\u4e0d\u662f\u5eb5\u4e3b\u3002\u201d \u3000\u3000\u4ed6\u8bdd\u51fa\u53e3\u540e\uff0c\u624d\u89c9\u51fa\u63aa\u8bcd\u6709\u4e9b\u53ef\u7b11\u3002 \u3000\u3000\u679c\u7136\uff0c\u4e2d\u5e74\u5973\u5c3c\u6de1\u6de1\u4e00\u7b11\u9053\uff1a\u201c\u5c0f\u65bd\u4e3b\u771f\u4f1a\u5f00\u73a9\u7b11\uff0c\u8d2b\u5c3c\u4e3b\u6301\u8fd9\u5ea7\u5999\u5999\u5eb5\u5df2\u7ecf\u597d\u591a\u5e74 \u4e86\u3002\u5373\u4f7f\u4e0d\u50cf\uff0c\u8fd8\u662f\u8981\u505a\u4e0b\u53bb\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u8138\u4e0a\u4e00\u70ed\u9053\uff1a\u201c\u665a\u8f88\u6c42\u89c1\u7684\u90a3\u4f4d\u5e08\u592a\uff0c\u5e74\u7eaa\u6bd4\u5eb5\u4e3b\u8981\u5927\u4e9b\u3002\u201d \u3000\u3000\u4e2d\u5e74\u5973\u5c3c\u7b11\u9053\uff1a\u201c\u8d2b\u5c3c\u5728\u5999\u5999\u5eb5\uff0c\u5e74\u7eaa\u5df2\u7ecf\u7b97\u4e0d\u8f7b\u4e86\uff0c\u4eca\u5e74\u7b97\u6765\u5df2\u7ecf\u4e03\u5341\u6709\u516b\uff0c\u5e74\u7eaa\u518d \u5927\u4e9b\u90a3\u8be5\u662f\u8c01\u5462\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u3001\u4e07\u6620\u971e\u3001\u82d7\u7d20\u82d3\u4e0d\u89c9\u5168\u4e3a\u4e4b\u4e00\u6014\u3002 \u3000\u3000\u8fd9\u5eb5\u4e3b\u770b\u6765\u53ea\u662f\u4e2a\u56db\u5341\u5de6\u53f3\u7684\u4e2d\u5e74\u5973\u5b50\uff0c\u800c\u4e14\u4f9d\u7136\u9762\u76ee\u59e3\u597d\uff0c\u98ce\u59ff\u695a\u695a\uff0c\u600e\u4f1a\u5df2\u7ecf\u662f\u4e03 \u5341\u516b\u9ad8\u9f84\u7684\u4eba\uff1f \u3000\u3000\u4f46\u5bf9\u65b9\u65e2\u662f\u8fd9\u4e48\u8bf4\uff0c\u9ea6\u5c0f\u660e\u7b49\u4eba\u53c8\u4e0d\u4fbf\u5f53\u9762\u8868\u793a\u5f02\u8bae\u3002 \u3000\u3000\u4e2d\u5e74\u5973\u5c3c\u518d\u9053\uff1a\u201c\u4e09\u4f4d\u5c0f\u65bd\u4e3b\u53ef\u80fd\u662f\u8ba4\u9519\u4eba\u4e86\uff0c\u65e2\u7136\u5982\u6b64\uff0c\u90a3\u5c31\u8bf7\u5427\uff01\u201d \u3000\u3000\u63a5\u7740\u5429\u5490\u90a3\u5c0f\u5c3c\u9053\uff1a\u201c\u4e91\u5fc3\uff0c\u628a\u8fd9\u4e09\u4f4d\u5c0f\u65bd\u4e3b\u9001\u51fa\u5eb5\u53bb\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u5fc3\u5934\u5927\u6025\uff0c\u4ed6\u4e0d\u80fd\u767d\u6765\u4e00\u8d9f\uff0c\u5fd9\u9053\uff1a\u201c\u5eb5\u4e3b\u8bf7\u6682\u7f13\u4e0b\u9010\u5ba2\u4ee4\uff0c\u665a\u8f88\u534a\u6708\u524d\u4e00\u4e2a\u591c \u665a\uff0c\u66fe\u6765\u8fc7\u8d35\u5eb5\uff0c\u660e\u660e\u89c1\u5230\u4e00\u4f4d\u8001\u5e08\u592a\u5728\u4f5b\u5802\u8bf5\u7ecf\u3002\u800c\u4e14\u8fd8\u548c\u665a\u8f88\u8bb2\u8fc7\u4e0d\u5c11\u8bdd\uff0c\u665a\u8f88\u6c42\u89c1\u7684 \u662f\u5979\u8001\u4eba\u5bb6\u3002\u201d \u3000\u3000\u4e2d\u5e74\u5973\u5c3c\u6447\u4e86\u6447\u5934\u9053\uff1a\u201c\u5999\u5999\u5eb5\u6ca1\u6709\u8fd9\u6837\u4e00\u4e2a\u4eba\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u6b63\u8272\u9053\uff1a\u201c\u665a\u8f88\u660e\u660e\u5728\u8d35\u5eb5\u89c1\u5230\u90a3\u4f4d\u8001\u5e08\u592a\uff0c\u5eb5\u4e3b\u4f55\u5fc5\u575a\u62d2\u665a\u8f88\u4eec\u7684\u6c42\u89c1\uff01\u201d \u3000\u3000\u4e2d\u5e74\u5973\u5c3c\u8138\u8272\u4e00\u53d8\uff0c\u521a\u8981\u53d1\u4f5c\uff0c\u5ffd\u542c\u7a97\u5916\u54cd\u8d77\u4e00\u4e2a\u719f\u6089\u7684\u58f0\u97f3\u9053\uff1a\u201c\u9759\u6708\u4e0d\u5fc5\u96be\u4e3a\u4ed6 \u4eec\uff0c\u4e3a\u5e08\u7684\u89c1\u89c1\u4ed6\u4eec\u5c31\u662f\u4e86\uff0e\u201d \u3000\u3000\u542c\u8fd9\u58f0\u97f3\uff0c\u6b63\u662f\u90a3\u7f01\u8863\u8001\u5c3c\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u4e0d\u89c9\u559c\u51fa\u671b\u5916\u3002 \u3000\u3000\u4e2d\u5348\u5973\u5c3c\u5728\u8fd9\u5239\u90a3\uff0c\u4e5f\u7acb\u5373\u9762\u73b0\u8083\u5bb9\uff0c\u6025\u6025\u8d77\u5ea7\u606d\u8fce\u3002 \u3000\u3000\u7f01\u8863\u8001\u5c3c\u7f13\u7f13\u8fdb\u5165\u5f85\u5ba2\u7cbe\u820d\uff0c\u5728\u4e3b\u4f4d\u4e0a\u5750\u4e0b\uff0e \u3000\u3000\u4e2d\u5e74\u5973\u5c3c\u8fde\u5fd9\u4e0a\u524d\u65bd\u793c\u95ee\u5b89\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u548c\u4e07\u6620\u971e\u4e5f\u968f\u5373\u4e0a\u524d\u606d\u8c28\u65e0\u6bd4\u7684\u884c\u793c\u81f4\u656c\u3002 \u3000\u3000\u82d7\u7d20\u82d3\u867d\u662f\u7b2c\u4e00\u6b21\u770b\u5230\u7f01\u8863\u8001\u5c3c\uff0c\u4e5f\u968f\u7740\u9ea6\u5c0f\u660e\u548c\u4e07\u6620\u971e\u4e00\u8d77\u4e0a\u524d\u62dc\u89c1\u3002 \u3000\u3000\u7f01\u8863\u8001\u5c3c\u6e5b\u6e5b\u773c\u795e\uff0c\u626b\u89c6\u4e86\u9ea6\u5c0f\u660e\u4e09\u4eba\u4e00\u9635\u9053\uff1a\u201c\u4f60\u4eec\u53ef\u662f\u7531\u8ff7\u8e2a\u8c37\u6765\u7684\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u8eac\u8eab\u9053\uff1a\u201c\u665a\u8f88\u4eec\u6b63\u662f\u7531\u8ff7\u8e2a\u8c37\u6765\u7684\u3002\u201d \u3000\u3000\u4ed6\u56e0\u82d7\u7d20\u82d3\u662f\u968f\u540c\u800c\u6765\uff0c\u56e0\u4e4b\u5e76\u672a\u505a\u4e2a\u522b\u5f15\u89c1\u3002 \u3000\u3000\u7f01\u8863\u8001\u5c3c\u4e0d\u52a8\u58f0\u8272\u9053\uff1a\u201c\u4e09\u4f4d\u65bd\u4e3b\u8001\u8fdc\u6765\u5230\u5999\u5999\u5eb5\uff0c\u4e0d\u77e5\u4e3a\u4e86\u4f55\u4e8b\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4e0a\u6b21\u8001\u5e08\u592a\u633d\u6551\u4e86\u8ff7\u8e2a\u8c37\u4e00\u573a\u5927\u52ab\uff0c\u665a\u8f88\u4eec\u7279\u5730\u6765\u5411\u8001\u5e08\u592a\u81f4\u8c22\uff01\u201d \u3000\u3000\u7f01\u8863\u8001\u5c3c\u8138\u8272\u4e00\u53d8\u9053\uff1a\u201c\u53ef\u662f\u7eff\u6797\u76df\u4e3b\u970d\u5143\u4f3d\u8981\u4f60\u4eec\u6765\u7684\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4e0d\uff01\u662f\u665a\u8f88\u4eec\u4e09\u4eba\u81ea\u5df1\u6765\u7684\u3002\u665a\u8f88\u77e5\u9053\u8001\u5e08\u592a\u4e0d\u613f\u5916\u4eba\u6765\u6253\u6270\u6e05\u4fee\uff0c\u6240\u4ee5 \u4e0d\u66fe\u544a\u8bc9\u4efb\u4f55\u4eba\u4f60\u8001\u4eba\u5bb6\u4f4f\u5728\u5999\u5999\u5eb5\u3002\u201d \u3000\u3000\u7f01\u8863\u8001\u5c3c\u51b7\u7b11\u9053\uff1a\u201c\u53ef\u662f\u5979\u4eec\u4e24\u4f4d\u59d1\u5a18\u5c31\u77e5\u9053\u4e86\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u665a\u8f88\u53ef\u4ee5\u4fdd\u8bc1\uff0c\u5979\u4eec\u4e24\u4f4d\u4e0d\u4f1a\u518d\u544a\u8bc9\u522b\u4eba\u3002\u201d \u3000\u3000\u7f01\u8863\u8001\u5c3c\u54fc\u4e86\u58f0\u9053\uff1a\u201c\u4e0a\u6b21\u4f60\u6765\u65f6\uff0c\u8d2b\u5c3c\u660e\u660e\u4ea4\u4ee3\u8fc7\u4e0d\u51c6\u5bf9\u4efb\u4f55\u4eba\u900f\u9732\u5999\u5999\u5eb5\u7684\u4e8b\uff0c\u90a3 \u65f6\u53ea\u6709\u4f60\u4e00\u4eba\u77e5\u9053\uff0c\u73b0\u5728\u5374\u5df2\u53d8\u6210\u4e86\u4e09\u4eba\u77e5\u9053\uff0c\u4f60\u8fd8\u4fdd\u8bc1\u4e2a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u4e0d\u89c9\u7f9e\u6127\u6ee1\u9762\uff0c\u987f\u4e86\u987f\u9053\uff1a\u201c\u665a\u8f88\u662f\u56e0\u4e0a\u6b21\u8001\u5e08\u592a\u66fe\u8bf4\u8fc7\u5999\u5999\u5eb5\u4e0d\u51c6\u7537\u4eba\u8fdb\u6765\uff0e \u6240\u4ee5\u624d\u60f3\u5230\u5e26\u5979\u4eec\u4e24\u4f4d\u59d1\u5a18\u540c\u884c\u3002\u201d \u3000\u3000\u7f01\u8863\u8001\u5c3c\u53f1\u9053\uff1a\u201c\u5f3a\u8bcd\u593a\u7406\uff0c\u90a3\u4f60\u4e3a\u4ec0\u4e48\u8981\u5e26\u4e24\u4e2a\u6765\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u665a\u8f88\u4e0d\u6562\u6b3a\u7792\uff0c\u8fd9\u4f4d\u82d7\u59d1\u5a18\u662f\u5728\u8def\u4e0a\u4e0e\u665a\u8f88\u76f8\u9047\uff0c\u665a\u8f88\u4e0d\u80fd\u4e22\u4e0b\u5979\u4e0d \u7ba1\u3002\u201d \u3000\u3000\u201c\u5978\u5427\uff01\u7b97\u4f60\u6709\u7406\uff0c\u65e2\u7136\u662f\u4e3a\u8c22\u6211\u800c\u6765\uff0c\u73b0\u5728\u4f60\u4eec\u5df2\u7ecf\u5f53\u9762\u8c22\u8fc7\u4e86\uff0c\u5c31\u8be5\u8d70\u4e86\u3002\u201d \u3000\u3000\u201c\u665a\u8f88\u8fd8\u53e6\u6709\u4e00\u4ef6\u4e8b\u76f8\u6c42\u8001\u5e08\u592a\u3002\u201d \u3000\u3000\u201c\u53c8\u6709\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u201c\u4ecd\u662f\u4e0a\u6b21\u524d\u6765\u8d35\u5eb5\u7684\u4e8b\uff0c\u90a3\u4f4d\u9ed1\u8863\u8499\u9762\u5973\u5b50\u7a76\u7adf\u662f\u8c01\uff1f\u201d \u3000\u3000\u7f01\u8863\u8001\u5c3c\u77ac\u606f\u95f4\u8138\u8272\u53d8\u4e86\u51e0\u53d8\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u8001\u662f\u8981\u95ee\u8fd9\u4ef6\u4e8b\uff1f\u5999\u5999\u5eb5\u6839\u672c\u5c31\u6ca1\u6709\u8fd9\u6837 \u4e00\u4e2a\u4eba\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u665a\u8f88\u65b9\u624d\u6765\u65f6\uff0c\u5728\u8def\u4e0a\u53c8\u66fe\u9047\u89c1\u5979\u3002\u201d \u3000\u3000\u82d7\u7d20\u82d3\u548c\u4e07\u6620\u971e\u4e5f\u540c\u58f0\u9053\uff1a\u201c\u665a\u8f88\u4eec\u65b9\u624d\u4e5f\u4eb2\u773c\u89c1\u5230\uff01\u201d \u3000\u3000\u7f01\u8863\u8001\u5c3c\u51b7\u51b7\u7b11\u9053\uff1a\u201c\u5c31\u7b97\u4f60\u4eec\u9047\u89c1\u8fd9\u6837\u2014\u4e2a\u4eba\uff0c\u53c8\u600e\u80fd\u65ad\u5b9a\u5979\u5fc5\u5728\u5999\u5999\u5eb5\uff0c\u4f60\u4eec\u53ef\u66fe \u4eb2\u773c\u89c1\u5230\u5979\u8fdb\u5165\u5999\u5999\u5eb5\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u88ab\u95ee\u5f97\u6709\u4e9b\u96be\u4ee5\u7b54\u8bdd\uff0c\u987f\u9053\uff1a\u201c\u8fd9\u9644\u8fd1\u56db\u4e0b\u5e76\u65e0\u4eba\u5bb6\uff0c\u9664\u4e86\u5999\u5999\u5eb5\uff0c\u5979\u4e0d\u53ef\u80fd\u5230 \u522b\u5904\u53bb\u3002\u201d \u3000\u3000\u7f01\u8863\u8001\u5c3c\u9053\uff1a\u201c\u53c8\u5f3a\u8bcd\u593a\u7406\u3002\u5979\u4e3a\u4ec0\u4e48\u4e00\u5b9a\u8981\u8fdb\u5165\u5999\u5999\u5eb5\u6765\uff1f\u96be\u9053\u5979\u5c31\u4e0d\u80fd\u662f\u5230\u6df1\u5c71\u91cc \u53bb\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u5979\u5230\u6df1\u5c71\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u7f01\u8863\u8001\u5c3c\u9053\uff1a\u201c\u5b64\u9b42\u91ce\u9b3c\uff0c\u5f53\u7136\u8981\u9041\u8ff9\u6df1\u5c71\uff0c\u5982\u679c\u8d2b\u5c3c\u6240\u6599\u4e0d\u5dee\uff0c\u5979\u5f88\u53ef\u80fd\u5c31\u5728\u524d\u9762\u90a3 \u7247\u5893\u5730\u91cc\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u53ea\u89c9\u5f97\u8111\u5b50\u91cc\u201c\u55e1\u201d\u7684\u4e00\u58f0\uff0c\u4f3c\u4e4e\u8fde\u5934\u4e5f\u5927\u4e86\u5f88\u591a\uff0c\u4e24\u773c\u76f4\u76ef\u7740\u7f01\u8863\u8001\u5c3c\u9053\uff1a \u201c\u8001\u5e08\u592a\u8bf4\u8fd9\u8bdd\u4ee4\u665a\u8f88\u4e0d\u89e3\u3002\u201d \u3000\u3000\u7f01\u8863\u8005\u5c3c\u9053\uff1a\u201c\u5b9e\u5bf9\u4f60\u4eec\u8bf4\u5427\uff01\u8d2b\u5c3c\u8fd1\u6708\u4ee5\u6765\uff0c\u4e5f\u66fe\u9047\u5230\u8fd9\u6837\u4e00\u4e2a\u5973\u4eba\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u8feb\u4e0d\u53ca\u5f85\u95ee\u9053\uff1a\u201c\u771f\u7684\uff1f\u8001\u5e08\u592a\u53ef\u66fe\u4e0e\u5979\u8bb2\u8fc7\u8bdd\uff1f\u201d \u3000\u3000\u7f01\u8863\u8001\u5c3c\u6447\u5934\u9053\uff1a\u201c\u5979\u7684\u88c5\u626e\u5f02\u4e0e\u5e38\u4eba\uff0c\u8d2b\u5c3c\u5f53\u7136\u5e0c\u671b\u80fd\u67e5\u660e\u5979\u7684\u5e95\u7ec6\uff0c\u53ef\u60dc\u5f53\u8d2b\u5c3c\u6bcf \u6b21\u8d70\u8fd1\u5979\u65f6\uff0e\u5979\u5c31\u5ffd\u7136\u6d88\u5931\u4e0d\u89c1\u3002\u201d \u3000\u3000\u201c\u8001\u5e08\u592a\u90fd\u5728\u4ec0\u4e48\u5730\u65b9\u89c1\u5230\u5979\uff1f\u201d \u3000\u3000\u201c\u524d\u9762\u90a3\u7247\u5893\u5730\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u4e0d\u7981\u6253\u4e86\u4e2a\u5bd2\u5664\u9053\uff1a\u201c\u83ab\u975e\u90a3\u9ed1\u8863\u5973\u5b50\u679c\u771f\u662f\u9b3c\uff1f\u201d \u3000\u3000\u7f01\u8863\u8001\u5c3c\u9053\uff1a\u201c\u6709\u8fd9\u79cd\u53ef\u80fd\uff0c\u56e0\u4e3a\u636e\u8bf4\u90a3\u5893\u5730\u91cc\u65b0\u8fd1\u57cb\u846c\u4e86\u2014\u4e2a\u5973\u4eba\u3002\u201d \u3000\u3000\u201c\u90a3\u5973\u4eba\u662f\u5426\u5c31\u662f\u8ff7\u8e2a\u8c37\u7684\u8c37\u524d\u76df\u4e3b\uff1f\u201d \u3000\u3000\u201c\u8d2b\u5c3c\u5e76\u4e0d\u6e05\u695a\u5979\u662f\u8c01\u3002\u201d \u3000\u3000\u201c\u90a3\u4e00\u5b9a\u662f\u8c37\u524d\u76df\u4e3b\uff0c\u8001\u5e08\u592a\u53ef\u77e5\u9053\u662f\u54ea\u5ea7\u65b0\u575f\uff1f\u201d \u3000\u3000\u201c\u90a3\u7247\u5893\u5730\u91cc\u65b0\u575f\u5f88\u591a\uff0c\u8d2b\u5c3c\u5c31\u662f\u8981\u95ee\uff0c\u4e5f\u65e0\u4ece\u95ee\u8d77\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u5446\u4e86\u534a\u54cd\uff0c\u5374\u53c8\u81ea\u8a00\u81ea\u8bed\u9053\uff1a\u201c\u4e0d\u53ef\u80fd\uff01\u90a3\u9ed1\u8863\u8499\u9762\u5973\u5b50\u4e0d\u53ef\u80fd\u662f\u9b3c\uff01\u201d \u3000\u3000\u7f01\u8863\u8001\u5c3c\u9053\uff1a\u201c\u4f60\u600e\u77e5\u5979\u4e0d\u662f\u9b3c\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u9b3c\u90fd\u662f\u5728\u591c\u95f4\u51fa\u73b0\uff0e\u53ef\u662f\u665a\u8f88\u4eec\u65b9\u624d\u89c1\u5230\u5979\u5374\u662f\u767d\u5929\u3002\u66f4\u4f55\u51b5\u4e0a\u6b21\u5979\u8fd8\u548c \u665a\u8f88\u8bb2\u4e86\u4e0d\u5c11\u8bdd\uff0c\u9b3c\u54ea\u6709\u548c\u4eba\u8bb2\u8bdd\u7684\u9053\u7406\uff1f\u201d \u3000\u3000\u7f01\u8863\u8001\u5c3c\u795e\u6001\u5341\u5206\u5b89\u6837\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u8fd9\u5c31\u5f88\u96be\u8bb2\u4e86\u3002\u4e3a\u4e86\u8ba9\u4f60\u4eec\u76f8\u4fe1\uff0c\u8d2b\u5c3c\u51b3\u5b9a\u628a\u4f60\u4eec \u7559\u5728\u8fd9\u91cc\u4f4f\u4e0a\u4e00\u665a\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u832b\u7136\u9053\uff1a\u201c\u8001\u5e08\u592a\u51c6\u5907\u8ba9\u665a\u8f88\u4eec\u76f8\u4fe1\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u7f01\u8863\u8001\u5c3c\u9053\uff1a\u201c\u90a3\u9ed1\u8863\u8499\u9762\u5973\u5b50\u51e0\u4e4e\u6bcf\u665a\u5fc5\u5728\u5893\u5730\u51fa\u73b0\uff0c\u4eca\u665a\u8d2b\u5c3c\u51b3\u5b9a\u4eb2\u81ea\u5e26\u4f60\u4eec\u53bb\u770b \u770b\uff0c\u4e0d\u8fc7\u4f60\u4eec\u5fc5\u987b\u9075\u7167\u8d2b\u5c3c\u7684\u89c4\u5b9a\u884c\u4e8b\u3002\u201d \u3000\u3000\u201c\u8001\u5e08\u592a\u6709\u4ec0\u4e48\u89c4\u5b9a\uff1f\u201d \u3000\u3000\u201c\u5728\u8d2b\u5c3c\u672a\u5e26\u4f60\u4eec\u5230\u5893\u5730\u4e4b\u524d\uff0c\u4f60\u4eec\u4e0d\u53ef\u64c5\u81ea\u524d\u53bb\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u8981\u8fd9\u6837\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u4f60\u4eec\u767d\u5929\u524d\u53bb\u6253\u6270\uff0c\u665a\u4e0a\u5979\u53ef\u80fd\u5c31\u4e0d\u51fa\u6765\u4e86\uff0e\u201d \u3000\u3000\u201c\u665a\u8f88\u4eec\u4e00\u5b9a\u9075\u547d\uff01\u201d \u3000\u3000\u7f01\u8863\u8001\u5c3c\u8d77\u8eab\u79bb\u5ea7\uff0c\u5429\u5490\u90a3\u4e2d\u5e74\u5973\u5c3c\u9759\u6708\u9053\uff1a\u201c\u628a\u524d\u9762\u7684\u5f85\u5ba2\u7cbe\u820d\u6574\u7406\u51fa\u4e24\u95f4\uff0c\u62db\u5f85\u4ed6 \u4eec\u4e09\u4f4d\uff0c\u6211\u8981\u56de\u623f\u53bb\u4e86\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u7b49\u4e09\u4eba\u76ee\u9001\u7f01\u8863\u8001\u5c3c\u51fa\u4e86\u8fd9\u95f4\u7cbe\u820d\uff0c\u624d\u542c\u9759\u6708\u9053\uff1a\u201c\u4e09\u4f4d\u5c0f\u65bd\u4e3b\u968f\u6211\u6765\uff01\u201d \u3000\u3000\u8fc7\u4e86\u4e24\u9053\u6d1e\u95e8\uff0c\u53c8\u56de\u5230\u524d\u9762\u5eb5\u95e8\u9644\u8fd1\u3002 \u3000\u3000\u5c31\u5728\u5eb5\u95e8\u5185\u4fa7\u7684\u5de6\u65b9\uff0c\u6709\u4e00\u680b\u4e09\u95f4\u7cbe\u820d\uff0c\u4e2d\u95f4\u4e00\u95f4\u662f\u5c0f\u5ba2\u5385\uff0c\u5de6\u53f3\u4e24\u95f4\u662f\u5367\u5ba4\u3002 \u3000\u3000\u9759\u6708\u628a\u4e09\u4eba\u9886\u8fdb\u5c0f\u5ba2\u5385\uff0c\u4ea4\u4ee3\u968f\u540c\u524d\u6765\u7684\u90a3\u5c0f\u5c3c\u9053\uff1a\u201c\u4e91\u5fc3\uff0c\u8fd9\u4e09\u4f4d\u5c0f\u65bd\u4e3b\u5c31\u7531\u4f60\u8d1f\u8d23 \u62db\u5f85\uff0c\u6211\u8d70\u4e86\uff01\u201d \u3000\u3000\u5979\u8d70\u51fa\u95e8\u53bb\uff0c\u5374\u53c8\u6b62\u6b65\u56de\u8eab\u9053\uff1a\u201c\u8d2b\u5c3c\u4e5f\u6709\u4e00\u4e2a\u89c4\u5b9a\uff0c\u4e09\u4f4d\u5c0f\u65bd\u4e3b\u52a1\u5fc5\u9075\u5b88\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u5eb5\u4e3b\u6709\u8bdd\u53ea\u7ba1\u5429\u5490\u3002\u201d \u3000\u3000\u9759\u6708\u9053\uff1a\u201c\u5728\u5bb6\u5e08\u672a\u5e26\u4e09\u4f4d\u5230\u5893\u5730\u524d\uff0c\u4e09\u4f4d\u4e0d\u5f97\u64c5\u81ea\u51fa\u5eb5\u95e8\uff0c\u5f53\u7136\u66f4\u4e0d\u5f97\u5f80\u5eb5\u5185\u5176\u4ed6\u5730 \u65b9\u4e71\u95ef\u3002\u5426\u5219\u82e5\u95ef\u51fa\u7978\u6765\uff0c\u8fde\u8d2b\u5c3c\u4e5f\u96be\u4ee5\u62c5\u5f85\u3002\u201d \u3000\u3000\u9759\u6708\u5eb5\u4e3b\u8d70\u540e\uff0c\u4e91\u5fc3\u5c0f\u5c3c\u968f\u5373\u6ce1\u4e0a\u8336\u6765\uff0c\u7136\u540e\u4e5f\u5f84\u81ea\u79bb\u53bb\u3002 \u3000\u3000\u8fd9\u65f6\u5c1a\u4e0d\u5230\u6b63\u5348\uff0c\u79bb\u5929\u9ed1\u8fd8\u6709\u5927\u534a\u65e5\u65f6\u95f4\uff0c\u9ea6\u5c0f\u660e\u7b49\u4e09\u4eba\u56e0\u5728\u7cbe\u820d\u5185\u4e0d\u51c6\u51fa\u53bb\uff0c\u5b9e\u5728\u662f \u4ef6\u4e0d\u8212\u670d\u7684\u4e8b\uff01\u90a3\u6ecb\u5473\u7b80\u76f4\u548c\u8fdb\u56da\u7262\u4e00\u6837\u3002 \u3000\u3000\u4e07\u6620\u971e\u9053\uff1a\u201c\u6211\u770b\u8fd9\u5ea7\u5999\u5999\u5eb5\u91cc\uff0c\u5fc5\u662f\u6709\u4ec0\u4e48\u79d8\u5bc6\uff0c\u5426\u5219\u4e0d\u4f1a\u6709\u8fd9\u4e48\u591a\u7981\u5fcc\u3002\u201d \u3000\u3000\u82d7\u7d20\u82d3\u9053\uff1a\u201c\u662f\u554a\uff0c\u53ef\u662f\u5979\u4eec\u7684\u89c4\u5b9a\uff0c\u54b1\u4eec\u53c8\u4e0d\u80fd\u4e0d\u542c\uff0e\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u8fd9\u65f6\u53cd\u800c\u5341\u5206\u6c89\u7740\uff0c\u5750\u5728\u90a3\u91cc\u53ea\u987e\u559d\u8336\uff0c\u5e76\u672a\u5f00\u53e3\u8bf4\u8bdd\u3002 \u3000\u3000\u53ea\u542c\u4e07\u6620\u971e\u9053\uff1a\u201c\u9ea6\u5c0f\u4fa0\u548c\u82d7\u59d1\u5a18\u53ef\u76f8\u4fe1\u4e16\u4e0a\u6709\u9b3c\uff1f\u201d \u3000\u3000\u82d7\u7d20\u82d3\u9053\uff1a\u201c\u6211\u867d\u7136\u6ca1\u89c1\u8fc7\u9b3c\uff0c\u4f46\u5374\u77e5\u9053\u6709\u626e\u795e\u5f04\u9b3c\u7684\u3002\u201d \u3000\u3000\u4e07\u6620\u971e\u9053\uff1a\u201c\u4eca\u665a\u8001\u5e08\u592a\u5e26\u54b1\u4eec\u5230\u5893\u5730\u53bb\uff0c\u53ef\u4e0d\u53ef\u80fd\u4e5f\u662f\u4e8b\u5148\u5e03\u7f6e\u597d\uff0c\u6709\u4eba\u5728\u90a3\u91cc\u626e\u795e \u5f04\u9b3c\uff1f\u201d \u3000\u3000\u82d7\u7d20\u82d3\u76b1\u7709\u9053\uff1a\u201c\u8fd9\u5c31\u5f88\u96be\u8bf4\u4e86\uff0c\u53ea\u8981\u770b\u5f97\u51c6\uff0c\u5373\u4f7f\u6709\u4eba\u626e\u795e\u5f04\u9b3c\uff0c\u4e5f\u7792\u4e0d\u8fc7\u54b1\u4eec\u7684\u773c \u775b\u3002\u201d \u3000\u3000\u4e24\u4eba\u53c8\u8c08\u4e86\u4e00\u9635\uff0c\u4e91\u5fc3\u5c0f\u5c3c\u5df2\u63d0\u7740\u98df\u76d2\u8d70\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u867d\u7136\u662f\u98df\u76d2\uff0c\u5404\u79cd\u83dc\u80b4\u5374\u5341\u5206\u4e30\u5bcc\u7cbe\u81f4\uff0c\u4e91\u5fc3\u628a\u996d\u83dc\u5728\u684c\u4e0a\u6446\u597d\u9053\uff1a\u201c\u4e09\u4f4d\u5fc5\u5b9a\u997f\u4e86\uff0c \u5feb\u5feb\u8bf7\u7528\u996d\u5427\uff01\u201d \u3000\u3000\u4e09\u4eba\u5954\u6ce2\u4e86\u4e00\u4e0a\u5348\uff0e\u5c24\u5176\u9ea6\u5c0f\u660e\u548c\u4e07\u6620\u971e\uff0c\u66f4\u548c\u9634\u624b\u4e00\u9b54\u7ecf\u8fc7\u4e86\u4e00\u756a\u6fc0\u70c8\u62fc\u6218\uff0c\u5f53\u7136\u65e9 \u5c31\u9965\u80a0\u8f98\u8f98\uff0c\u968f\u5373\u5165\u5ea7\u5403\u4e86\u8d77\u6765\u3002 \u3000\u3000\u4e91\u5fc3\u5c0f\u5c3c\u5219\u7ad9\u5728\u4e00\u65c1\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u642d\u8baa\u7740\u95ee\uff1a\u201c\u5c0f\u5e08\u592a\uff0c\u8d35\u5eb5\u6709\u4e9b\u4e8b\u5728\u4e0b\u89c9\u5f97\u5f88\u5947\u602a\u3002\u201d \u3000\u3000\u4e91\u5fc3\u5c0f\u5c3c\u9053\uff1a\u201c\u5c0f\u65bd\u4e3b\u6709\u4ec0\u4e48\u5947\u602a\u7684\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u8001\u5e08\u592a\u5728\u8d35\u5eb5\u8eab\u4efd\u5730\u4f4d\u6700\u9ad8\uff0c\u4e3a\u4ec0\u4e48\u5eb5\u4e3b\u5374\u4e0d\u662f\u5979\uff1f\u201d \u3000\u3000\u4e91\u5fc3\u5c0f\u5c3c\u9053\uff1a\u201c\u8fd9\u6709\u4ec0\u4e48\u5947\u602a\u7684\uff1f\u90a3\u662f\u5e08\u7956\u628a\u5eb5\u4e3b\u8ba9\u7ed9\u4e86\u5e08\u7236\uff0c\u5e08\u7236\u867d\u7136\u662f\u5eb5\u4e3b\uff0c\u4f46\u6bcf \u9022\u6709\u91cd\u5927\u4e8b\u60c5\uff0c\u8fd8\u5fc5\u987b\u8bf7\u793a\u5e08\u7956\u3002\u201d \u3000\u3000\u201c\u8001\u5e08\u592a\u4e3a\u4ec0\u4e48\u8981\u628a\u5eb5\u4e3b\u8ba9\u7ed9\u4ee4\u5e08\u5462\uff1f\u201d \u3000\u3000\u201c\u7406\u7531\u5f88\u7b80\u5355\uff0c\u5e08\u7956\u4e0d\u505a\u5eb5\u4e3b\uff0c\u7528\u4e0d\u7740\u5904\u7406\u7410\u4e8b\uff0c\u4fbf\u53ef\u4e13\u5fc3\u4e00\u5fd7\u7684\u6e05\u4fee\uff0c\u8fd9\u4e0d\u662f\u5f88\u597d \u5417\uff1f\u201d \u3000\u3000\u201c\u8001\u5e08\u592a\u662f\u4ec0\u4e48\u65f6\u5019\u628a\u5eb5\u4e3b\u8ba9\u7ed9\u4ee4\u5e08\u7684\uff1f\u201d \u3000\u3000\u201c\u4ece\u6211\u6765\u5230\u5999\u5999\u5eb5\u540e\uff0c\u5eb5\u4e3b\u5c31\u662f\u5bb6\u5e08\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9ed8\u4e86\u4e00\u9ed8\u9053\uff1a\u201c\u5148\u524d\u5eb5\u4e3b\u8bf4\u5979\u5df2\u7ecf\u4e03\u5341\u516b\u5c81\uff0e\u5979\u4e3a\u4ec0\u5e7a\u8981\u9a97\u6211\u4eec\uff1f\u201d \u3000\u3000\u4e91\u5fc3\u5c0f\u5c3c\u6b63\u8272\u9053\uff1a\u201c\u5eb5\u4e3b\u660e\u660e\u5df2\u7ecf\u4e03\u5341\u516b\u5c81\uff0c\u5979\u6839\u672c\u6ca1\u9a97\u4f60\u4eec\uff01\u201d \u3000\u3000\u201c\u4f60\u600e\u4e48\u77e5\u9053\u5979\u8bf4\u7684\u662f\u771f\u8bdd\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u5979\u5bf9\u6211\u4e5f\u662f\u8fd9\u6837\u8bf4\u3002\u4e3a\u4e86\u8bc1\u660e\u5979\u7684\u8bdd\u4e0d\u5047\uff0c\u6211\u53ef\u4ee5\u544a\u8bc9\u4f60\u4eec\u4e00\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u201c\u6211\u4ece\u4e94\u5c81\u8fdb\u5165\u5999\u5999\u5eb5\uff0c\u81f3\u4eca\u5df2\u7ecf\u5341\u4e8c\u4e09\u5e74\uff0e\u4ece\u6211\u8bb0\u4e8b\u5230\u73b0\u5728\uff0e\u5bb6\u5e08\u7684\u6a21\u6837\u2014\u76f4\u672a\u53d8\uff0e \u6240\u4ee5\u6211\u624d\u76f8\u4fe1\u5979\u6709\u4e03\u5341\u516b\u5c81\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u53ea\u80fd\u695e\u6123\u5730\u542c\u7740\uff0c\u5374\u65e0\u6cd5\u518d\u8bf4\u4ec0\u4e48\uff0e \u3000\u3000\u4e00\u987f\u996d\u5306\u5306\u7528\u8fc7\uff0c\u4e91\u5fc3\u5c0f\u5c3c\u63d0\u8d70\u98df\u76d2\u4e4b\u540e\uff0c\u6574\u4e2a\u4e0b\u5348\u672a\u518d\u6765\u8fc7\uff0e \u3000\u3000\u9ea6\u5c0f\u660e\u3001\u82d7\u7d20\u82d3\u548c\u4e07\u6620\u971e\u4e5f\u8c28\u9075\u9759\u6708\u5eb5\u4e3b\u7684\u4ea4\u4ee3\uff0c\u5e76\u672a\u79bb\u5f00\u8fd9\u95f4\u7cbe\u820d\uff0c\u534a\u5929\u7684\u65f6\u95f4\uff0c\u5168 \u95f7\u5750\u5728\u5c0f\u5ba2\u5385\u91cc\u5ea6\u8fc7\u3002 \u3000\u3000\u76f4\u5230\u5929\u665a\uff0c\u4e91\u5fc3\u5c0f\u5c3c\u624d\u53c8\u6765\u9001\u4e0a\u665a\u996d\uff0e \u3000\u3000\u4e91\u5fc3\u5c0f\u5c3c\u4e34\u8d70\u65f6\u4ea4\u4ee3\uff0c\u8981\u7b49\u4e8c\u66f4\u8fc7\u540e\u624d\u80fd\u5230\u5893\u5730\u53bb\uff0c\u5e0c\u671b\u4ed6\u4eec\u80fd\u63d0\u524d\u5c31\u5bdd\u3002\u65f6\u95f4\u4e00\u5230\uff0e \u5979\u4f1a\u6765\u8d1f\u8d23\u62db\u547c\u8d77\u5e8a\u3002 \u3000\u3000\u6b63\u597d\u5de6\u53f3\u4e24\u95f4\u5367\u623f\uff0c\u9ea6\u5c0f\u660e\u4e00\u95f4\uff0c\u82d7\u7d20\u82d3\u548c\u4e07\u6620\u971e\u5408\u4f4f\u4e00\u95f4\u3002 \u3000\u3000\u5367\u623f\u5185\u5404\u79cd\u5bdd\u5177\u90fd\u51c6\u5907\u5f97\u597d\u597d\u7684\uff0c\u4e09\u4eba\u4e3a\u4e86\u517b\u8db3\u7cbe\u795e\uff0c\u665a\u996d\u540e\u7acb\u5373\u4e0a\u5e8a\u4f11\u606f\uff0c\u4e0d\u8fc7\u672a\u7b49 \u4e91\u5fc3\u5c0f\u5c3c\u6765\u53eb\uff0c\u4ed6\u4eec\u5728\u4e8c\u66f4\u5de6\u53f3\u4fbf\u81ea\u52a8\u8d77\u5e8a\uff0c\u9f50\u96c6\u5728\u5c0f\u5ba2\u5385\u7b49\u4faf\u3002 \u3000\u3000\u679c\u7136\uff0c\u8fc7\u4e86\u4e00\u4f1a\u513f\uff0c\u4e91\u5fc3\u5c0f\u5c3c\u4fbf\u6765\u4e86\uff0c\u5979\u54e6\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u539f\u6765\u4f60\u4eec\u4e09\u4f4d\u6ca1\u553e\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u6211\u4eec\u521a\u8d77\u5e8a\uff0c\u8001\u5e08\u592a\u8d77\u6765\u4e86\u5427\uff1f\u201d \u3000\u3000\u4e91\u5fc3\u5c0f\u5c3c\u9053\uff1a\u201c\u5e08\u7956\u5df2\u5728\u5eb5\u95e8\u5916\u7b49\u4faf\u4e09\u4f4d\uff0e\u4f60\u4eec\u8fd9\u5c31\u53bb\u5427\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u987a\u53e3\u95ee\u9053\uff1a\u201c\u5c0f\u5e08\u592a\u8981\u4e0d\u8981\u8ddf\u6211\u4eec\u4e00\u8d77\u53bb\uff1f\u201d \u3000\u3000\u4e91\u5fc3\u5c0f\u5c3c\u9053\uff1a\u201c\u6211\u6015\u9b3c\uff0c\u4e0d\u53bb\u5566\uff01\u201d \u3000\u3000\u4e09\u4eba\u7565\u7565\u6574\u4e86\u6574\u88c5\uff0c\u968f\u5373\u8d76\u5230\u5eb5\u95e8\u5916\uff0e\u53ea\u89c1\u7f01\u8863\u8001\u5c3c\u679c\u7136\u5df2\u5728\u90a3\u91cc\u7b49\u5019\u3002 \u3000\u3000\u4e09\u4eba\u9f50\u9f50\u5411\u7f01\u8863\u8001\u5c3c\u884c\u4e86\u4e00\u793c\uff0c\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u6253\u6270\u8001\u5e08\u592a\u6e05\u4fee\uff0c\u665a\u8f88\u4eec\u5b9e\u5728\u62b1\u6b49\uff01\u201d \u3000\u3000\u7f01\u8863\u8001\u5c3c\u9053\uff1a\u201c\u4e3a\u4e86\u8bc1\u5b9e\u8d2b\u5c3c\u6240\u8a00\u4e0d\u865a\uff0c\u4e5f\u5c31\u53ea\u597d\u966a\u4f60\u4eec\u4e00\u9053\u4e86\u3002\u201d \u3000\u3000\u8fd9\u65f6\u6b63\u662f\u4e0b\u65ec\uff0c\u521a\u597d\u6708\u4eae\u5df2\u7ecf\u5347\u8d77\uff0e\u4ee5\u4ed6\u4eec\u51e0\u4eba\u7684\u76ee\u529b\uff0c\u8db3\u53ef\u770b\u51fa\u6570\u5341\u4e08\u5916\u3002 \u3000\u3000\u4e00\u884c\u56db\u4eba\uff0c\u7acb\u5373\u5f80\u5893\u5730\u51fa\u53d1\u3002 \u3000\u3000\u591c\u6df1\u4eba\u9759\uff0c\u4e07\u7c41\u4ff1\u5bc2\uff0c\u4ed6\u4eec\u90fd\u81ea\u52a8\u653e\u8f7b\u811a\u6b65\uff0c\u4ee5\u514d\u60ca\u52a8\u4e86\u90a3\u9ed1\u8863\u8499\u9762\u5973\u5b50\u3002 \u3000\u3000\u4e00\u91cc\u5de6\u53f3\u7684\u5c71\u8def\uff0c\u5f88\u5feb\u4fbf\u5230\uff0e\u7f01\u8863\u8001\u5c3c\u6307\u7740\u5893\u5730\u524d\u7f18\u7684\u4e00\u9053\u571f\u5c97\u9053\uff1a\u201c\u9690\u8eab\u5728\u571f\u5c97\u4e4b \u540e\uff0c\u5bf9\u5893\u5730\u9644\u8fd1\u53ef\u4ee5\u4e00\u89c8\u65e0\u9057\uff0c\u54b1\u4eec\u5c31\u9690\u8eab\u5728\u571f\u5c97\u4e4b\u540e\u5427\uff01\u201d\u56db\u4eba\u6765\u5230\u571f\u5c97\u4e4b\u540e\uff0c\u53ea\u9732\u51fa\u534a \u4e2a\u5934\u6765\uff0c\u679c\u7136\u5bf9\u6574\u4e2a\u5893\u5730\uff0e\u5168\u80fd\u770b\u5230\u3002 \u3000\u3000\u8fd9\u5757\u5893\u5730\u6781\u5927\uff0c\u6709\u767e\u4f59\u5ea7\u575f\u5893\uff0c\u8db3\u8db3\u5360\u4e86\u597d\u51e0\u4ea9\u5730\u8303\u56f4\uff0c\u5fc5\u987b\u8f6c\u52a8\u89c6\u7ebf\uff0c\u624d\u80fd\u7531\u8fd9\u5934\u770b \u5230\u90a3\u5934\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u4f4e\u58f0\u9053\uff1a\u201c\u54b1\u4eec\u4e09\u4eba\u6700\u597d\u5206\u533a\u76d1\u89c6\uff0c\u82d7\u59d1\u5a18\u8bf7\u76d1\u89c6\u5de6\u8fb9\uff0c\u4e07\u59d1\u5a18\u8bf7\u76d1\u89c6\u53f3\u8fb9\uff0c\u4e2d \u95f4\u90e8\u5206\u7531\u6211\u8d1f\u8d23\uff0e\u201d \u3000\u3000\u4f46\u4e09\u4eba\u770b\u4e86\u534a\u664c\uff0c\u5374\u4e1d\u6beb\u4e0d\u89c1\u52a8\u9759\u3002 \u3000\u3000\u4e07\u6620\u971e\u6709\u4e9b\u7740\u6025\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u8001\u5e08\u592a\uff0c\u5979\u600e\u4e48\u8fd8\u4e0d\u6765\uff1f\u201d \u3000\u3000\u7f01\u8863\u8001\u5c3c\u7b11\u9053\uff1a\u201c\u5979\u6709\u5979\u7684\u884c\u52a8\uff0c\u95ee\u6211\u6709\u4ec0\u4e48\u7528\uff1f\u4e5f\u53ef\u80fd\u73b0\u5728\u65f6\u95f4\u672a\u5230\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u65f6\u95f4\u5df2\u7ecf\u4e0d\u65e9\u5566\uff01\u201d \u3000\u3000\u201c\u867d\u7136\u65f6\u95f4\u4e0d\u65e9\uff0c\u8fd8\u4e0d\u5230\u4e09\u66f4\uff0c\u4f60\u4e00\u5b9a\u542c\u8bf4\u8fc7\uff0c\u9b3c\u9b42\u51fa\u73b0\uff0c\u591a\u534a\u662f\u5728\u4e09\u66f4\uff0e\u201d \u3000\u3000\u7f01\u8863\u8001\u5c3c\u8fd9\u51e0\u53e5\u8bdd\u8bf4\u6765\u8f7b\u677e\uff0c\u4f46\u542c\u5f97\u4e07\u6620\u971e\u5374\u6709\u70b9\u6bdb\u9aa8\u609a\u7136\uff0c\u597d\u5728\u4eba\u591a\uff0c\u5982\u679c\u8981\u5979\u81ea\u5df1 \u7559\u5728\u73b0\u573a\uff0c\u867d\u6709\u4e00\u8eab\u6b66\u529f\uff0c\u4e5f\u5fc5\u80c6\u602f\u3002 \u3000\u3000\u51dd\u795e\u5411\u5893\u5730\u671b\u53bb\uff0c\u90a3\u4e00\u5ea7\u4e00\u5ea7\u7684\u575f\u5893\uff0c\u5728\u6708\u5149\u6620\u7167\u4e0b\uff0c\u8fd8\u771f\u6709\u4e9b\u9b3c\u5f71\u5e62\u5e62\u4e4b\u611f\uff0c\u5c24\u5176\u866b \u58f0\u557e\u557e\uff0c\u9b3c\u706b\u78f7\u78f7\uff0c\u8d8a\u53d1\u589e\u52a0\u4e86\u6050\u6016\u6c14\u6c1b\uff0e \u3000\u3000\u4e07\u6620\u971e\u642d\u8baa\u7740\u518d\u95ee\u9053\uff1a\u201c\u5982\u679c\u5979\u4eca\u665a\u4e0d\u51fa\u73b0\uff0c\u665a\u8f88\u4eec\u5c82\u4e0d\u662f\u767d\u6765\u4e86\uff1f\u201d \u3000\u3000\u7f01\u8863\u8001\u5c3c\u51b7\u58f0\u9053\uff1a\u201c\u6211\u53ea\u662f\u5e26\u4f60\u4eec\u6765\u78b0\u78b0\u8fd0\u6c14\uff0c\u5e76\u4e0d\u62c5\u4fdd\u5979\u4e00\u5b9a\u6765\uff0e\u8fde\u6211\u767d\u6765\u4e00\u8d9f\u90fd\u4e0d \u5728\u4e4e\uff0c\u4f60\u8fd8\u62b1\u6028\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4e07\u6620\u971e\u81ea\u77e5\u65b9\u624d\u7684\u8bdd\u5bf9\u7f01\u8863\u8001\u5c3c\u5927\u4e0d\u793c\u8c8c\uff0c\u4e00\u9635\u7f9e\u60ed\uff0c\u4e0d\u518d\u542d\u58f0\u3002 \u3000\u3000\u82d7\u7d20\u82d3\u5374\u5fcd\u4e0d\u4f4f\u7b11\u9053\uff1a\u201c\u8001\u5e08\u592a\uff0c\u4f60\u8001\u4eba\u5bb6\u65b9\u624d\u7684\u8bdd\u771f\u6709\u610f\u601d\u3002\u201d \u3000\u3000\u7f01\u8863\u8001\u5c3c\u9053\uff1a\u201c\u8d2b\u5c3c\u7684\u8bdd\u54ea\u91cc\u6709\u610f\u601d\uff1f\u201d \u3000\u3000\u82d7\u7d20\u82d3\u9053\uff1b\u201c\u665a\u8f88\u542c\u4eba\u5bb6\u8bf4\u9047\u89c1\u9b3c\u662f\u8fd0\u6c14\u4e0d\u597d\uff0c\u60a8\u521a\u624d\u8bf4\u662f\u5e26\u6211\u4eec\u6765\u78b0\u8fd0\u6c14\uff0c\u5c82\u4e0d\u6210\u4e86 \u9047\u89c1\u9b3c\u53cd\u800c\u662f\u597d\u8fd0\u6c14\uff1f\u201d \u3000\u3000\u7f01\u8863\u8001\u5c3c\u6de1\u6de1\u4e00\u7b11\u9053\uff1a\u201c\u8fd9\u8981\u770b\u4f60\u4eec\u7684\u5e0c\u671b\u662f\u4ec0\u4e48\uff0c\u4e00\u822c\u8c01\u90fd\u4e0d\u5e0c\u671b\u9047\u89c1\u9b3c\u3002\u6240\u4ee5\u770b\u5230 \u9b3c\u662f\u8fd0\u6c14\u80cc\u3002\u800c\u4f60\u4eec\u4eca\u665a\u662f\u5e0c\u671b\u9047\u89c1\u9b3c\uff0c\u5f53\u7136\u770b\u5230\u9b3c\u4fbf\u662f\u597d\u8fd0\u3002\u6211\u65b9\u624d\u7684\u8bdd\u6709\u4ec0\u4e48\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u82d7\u7d20\u82d3\u4e5f\u88ab\u8bf4\u5f97\u4e0d\u518d\u542d\u58f0\u3002 \u3000\u3000\u5ffd\u542c\u4e07\u6620\u971e\u60ca\u53eb\u9053\uff1a\u201c\u6765\u5566\uff01\u6765\u5566\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u548c\u82d7\u7d20\u82d3\u9f50\u9f50\u5411\u4e07\u6620\u971e\u76d1\u89c6\u7684\u533a\u57df\u770b\u53bb\uff0c\u679c\u7136\u5728\u5893\u5730\u53f3\u65b9\u51fa\u73b0\u4e86\u4e00\u4e2a\u4eba\u5f71\uff0c\u800c\u4e14 \u9ed1\u8863\u8499\u9762\uff0c\u6b63\u662f\u9177\u4f3c\u8c37\u5bd2\u9999\u7684\u90a3\u540d\u5973\u5b50\uff0c\u53ea\u56e0\u9694\u5f97\u592a\u8fdc\uff0c\u5374\u53c8\u65e0\u6cd5\u786e\u8ba4\u662f\u5426\u540c\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u8fd9\u65f6\u9ea6\u5c0f\u660e\u3001\u82d7\u7d20\u82d3\uff0e\u4e07\u6620\u971e\u90fd\u611f\u5168\u8eab\u8840\u8109\u52a0\u901f\uff0c\u6709\u7740\u65e0\u6bd4\u7684\u6fc0\u52a8\uff0e\u5df2\u5b8c\u5168\u5931\u4e86\u6050\u60e7\uff0c \u5413\u7684\u53ea\u662f\u60ca\u5947\uff0e \u3000\u3000\u7f01\u8863\u8001\u5c3c\u671b\u7740\u4eba\u5f71\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u4ece\u73b0\u5728\u8d77\uff0c\u4f60\u4eec\u6700\u597d\u522b\u8bf4\u8bdd\uff0c\u591a\u6ce8\u610f\u770b\uff0c\u5426\u5219\u4f60\u4eec\u5f88 \u53ef\u80fd\u4f1a\u4e0d\u77e5\u9053\u5f85\u4f1a\u513f\u5979\u662f\u600e\u6837\u6d88\u5931\u53bb\uff0e\u201d \u3000\u3000\u90a3\u9ed1\u8863\u5973\u5b50\u9759\u9759\u7684\u7ad9\u5728\u2014\u5757\u5893\u7891\u524d\uff0c\u4ef0\u8138\u671d\u5411\u4e1c\u65b9\uff0e\u4f3c\u5728\u89c2\u8d4f\u6708\u8272\u3002 \u3000\u3000\u8bb8\u4e45\uff0c\u624d\u89c1\u5979\u7f13\u7f13\u79fb\u52a8\u811a\u6b65\uff0c\u8d70\u8fd1\u5893\u7891\u540e\u7684\u90a3\u5ea7\u5893\uff0c\u5c31\u5728\u5893\u6cbf\u5750\u4e0b\uff0e \u3000\u3000\u8fd9\u4e00\u5750\u4e0d\u6253\u7d27\uff0c\u8db3\u8db3\u987f\u996d\u5de5\u592b\uff0c\u7adf\u7136\u4e00\u76f4\u672a\u52a8\uff0c\u5c31\u50cf\u6728\u96d5\u6ce5\u5851\u4e00\u822c\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u7ec8\u4e8e\u60c5\u4e0d\u81ea\u7981\u7684\u9053\uff1a\u201c\u8001\u5e08\u592a\uff0c\u5982\u679c\u5979\u4e00\u76f4\u4e0d\u52a8\uff0c\u54b1\u4eec\u4e5f\u2014\u76f4\u5b88\u5728\u8fd9\u91cc\u4e0d\u52a8 \u5417\uff1f\u201d \u3000\u3000\u7f01\u8863\u8005\u5c3c\u9053\uff1a\u201c\u4f60\u51c6\u5907\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u665a\u8f88\u60f3\u5411\u5979\u5077\u5077\u63a5\u8fd1\uff0c\u53ea\u8981\u80fd\u902e\u5230\u5979\uff0c\u5c31\u4e0d\u865a\u6b64\u884c\u4e86\u3002\u201d \u3000\u3000\u7f01\u8863\u8001\u5c3c\u9053\uff1a\u201c\u4f60\u5982\u679c\u60f3\u591a\u770b\u770b\u5979\uff0c\u5c31\u522b\u8f7b\u4e3e\u5984\u52a8\u3002\u201d \u3000\u3000\u201c\u90a3\u4e48\u665a\u8f88\u5077\u5077\u8d70\u8fc7\u53bb\u79bb\u5979\u8fd1\u4e9b\uff0c\u81f3\u5c11\u53ef\u4ee5\u770b\u5f97\u771f\u5207\u4e00\u70b9\u3002\u201d \u3000\u3000\u201c\u9b3c\u7684\u8b66\u89c9\u6027\u6700\u9ad8\uff0c\u53ea\u6015\u4f60\u8fd8\u6ca1\u6709\u8d70\u8fd1\u5979\uff0c\u5979\u5c31\u6d88\u5931\u5f97\u65e0\u5f71\u65e0\u8e2a\u4e86\u3002\u201d \u3000\u3000\u201c\u4e0d\u7ba1\u5982\u4f55\uff0c\u665a\u8f88\u8fd8\u662f\u60f3\u53bb\u8bd5\u8bd5\uff0c\u5426\u5219\u8001\u7b49\u5728\u8fd9\u91cc\uff0c\u5b9e\u5728\u4e0d\u662f\u529e\u6cd5\u3002\u201d \u3000\u3000\u201c\u65e2\u7136\u4f60\u6709\u8fd9\u79cd\u60f3\u6cd5\uff0c\u5c31\u53ea\u7ba1\u524d\u53bb\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u7acb\u5373\u4ea4\u5f85\u82d7\u7d20\u82d3\u548c\u4e07\u6620\u971e\u9053\uff1a\u201c\u4e24\u4f4d\u59d1\u5a18\u8bf7\u5b88\u5728\u8fd9\u91cc\uff0c\u6211\u8fc7\u53bb\u770b\u770b\uff01\u201d \u3000\u3000\u82d7\u7d20\u82d3\u96be\u7981\u597d\u5947\uff0c\u5fd9\u9053\uff1a\u201c\u6211\u4e5f\u8981\u53bb\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4eba\u591a\u4e86\uff0c\u53cd\u800c\u5bb9\u6613\u88ab\u5979\u53d1\u89c9\uff0c\u82d7\u59d1\u5a18\u8fd8\u662f\u522b\u53bb\u7684\u597d\u3002\u201d \u3000\u3000\u82d7\u7d20\u82d3\u4e5f\u5c31\u4e0d\u518d\u575a\u6301\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u4e3a\u4e86\u884c\u52a8\u9690\u79d8\uff0c\u4fbf\u8dc3\u4e0b\u571f\u5c97\uff0c\u4ee5\u571f\u5c97\u505a\u9690\u853d\uff0c\u518d\u5c4f\u606f\u524d\u8fdb\uff0c\u5e76\u63d0\u6c14\u7eb5\u8eab\uff0c\u5c3d\u91cf\u4e0d \u4f7f\u811a\u4e0b\u53d1\u51fa\u58f0\u97f3\u3002\u8fd9\u9053\u571f\u5c97\u6781\u957f\uff0c\u9884\u8ba1\u8d70\u5230\u5c3d\u5934\u65f6\uff0c\u5fc5\u5b9a\u5df2\u8ddd\u9ed1\u8863\u8499\u9762\u5973\u5b50\u6240\u5728\u575f\u5893\u4e0d\u8fdc\u3002 \u3000\u3000\u679c\u7136\uff0c\u5f53\u8d70\u5230\u571f\u5c97\u5c3d\u5934\uff0c\u53c8\u80fd\u770b\u5230\u5893\u5730\u65f6\uff0c\u548c\u90a3\u9ed1\u8863\u5973\u5b50\uff0c\u76f8\u8ddd\u53ea\u4e0d\u8fc7\u56db\u4e94\u4e08\u8fdc\u3002 \u3000\u3000\u8fd9\u6837\u7684\u8fdc\u8fd1\uff0e\u4ee5\u9ea6\u5c0f\u660e\u8f7b\u529f\u800c\u8a00\uff0c\u53ea\u8981\u5438\u6c14\u7eb5\u8dc3\uff0c\u4fbf\u5230\u8fbe\u8ddf\u524d\u3002 \u3000\u3000\u4f46\u4ed6\u5374\u4e0d\u6562\u8fd9\u6837\u505a\uff0c\u56e0\u4e3a\u4ed6\u60f3\u5230\uff0c\u5982\u6b64\u4e0d\u4f46\u5c06\u4f7f\u5bf9\u65b9\u53d7\u60ca\uff0c\u4e5f\u5fc5\u6fc0\u6012\u5bf9\u65b9\uff0e\u5982\u679c\u5bf9\u65b9\u771f \u662f\u8c37\u5bd2\u9999\uff0c\u90a3\u66f4\u662f\u4e00\u79cd\u5927\u5927\u4e0d\u793c\u8c8c\u7684\u4e3e\u52a8\u3002 \u3000\u3000\u5018\u82e5\u5bf9\u65b9\u5e76\u975e\u8c37\u5bd2\u9999\uff0c\u4e5f\u5fc5\u56e0\u88ab\u6fc0\u6012\u800c\u4e0d\u8868\u9732\u8eab\u4efd\uff0c\u66f4\u4f55\u51b5\u5bf9\u65b9\u6709\u53ef\u80fd\u771f\u7684\u662f\u9b3c\uff0c\u90a3\u5c31 \u66f4\u4e0d\u80fd\u5192\u5931\u4ece\u4e8b\u4e86\u3002 \u3000\u3000\u4e8e\u662f\uff0c\u4ed6\u51b3\u5b9a\u5148\u5728\u571f\u5c97\u5c3d\u5934\u7684\u9690\u853d\u5904\u89c2\u5bdf\u4ed4\u7ec6\u518d\u8bf4\u3002 \u3000\u3000\u7531\u4e8e\u8ddd\u79bb\u5df2\u8fd1\uff0c\u53c8\u6709\u6708\u5149\u7167\u5c04\uff0c\u6b64\u65f6\u5df2\u770b\u5f97\u5341\u5206\u6e05\u695a\uff0c\u8fd9\u5973\u4eba\u679c\u7136\u5c31\u662f\u4e0a\u6b21\u6240\u89c1\u7684\u9ed1\u8863 \u8499\u9762\u5973\u5b50\u3002 \u3000\u3000\u4e0d\u6d88\u8bf4\uff0c\u90a3\u795e\u6001\u4e5f\u5b8c\u5168\u548c\u8c37\u5bd2\u9999\u751f\u524d\u9177\u4f3c\u3002 \u3000\u3000\u6b64\u523b\uff0c\u5979\u4ecd\u7136\u5750\u90a3\u91cc\u52a8\u4e5f\u4e0d\u52a8\uff0e\u4e00\u4e2a\u4eba\u80fd\u51dd\u5750\u5982\u6b64\u957f\u4e45\u7684\u65f6\u95f4\uff0c\u5168\u8eab\u6beb\u4e0d\u6d3b\u52a8\u5b9e\u5728\u9887\u4e3a \u7f55\u89c1\u3002 \u3000\u3000\u66f4\u4f55\u51b5\u662f\u534a\u591c\u4e09\u66f4\uff0c\u5904\u5728\u56db\u65e0\u4eba\u70df\u8352\u51c9\u7684\u5893\u5730\u91cc\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u5c4f\u606f\u89c2\u5bdf\u4e86\u8db3\u6709\u76cf\u8336\u5de5\u592b\uff0c\u4fbf\u4e0d\u518d\u72b9\u8c6b\uff0e\u968f\u5373\u8e51\u624b\u8e51\u811a\u5411\u90a3\u9ed1\u8863\u8499\u9762\u5973\u5b50\u63a5\u8fd1\u3002 \u3000\u3000\u6b63\u597d\u5bf9\u65b9\u662f\u80cc\u5bf9\u9ea6\u5c0f\u660e\uff0c\u9ea6\u5c0f\u660e\u53ea\u8981\u811a\u4e0b\u4e0d\u53d1\u51fa\u58f0\u606f\uff0c\u4fbf\u4e0d\u81f4\u88ab\u5bf9\u65b9\u5bdf\u89c9\u3002 \u3000\u3000\u53cc\u65b9\u8ddd\u79bb\u8d8a\u6765\u8d8a\u8fd1\uff0c\u5f88\u5feb\u4fbf\u5df2\u5269\u4e0b\u4e24\u4e08\u5de6\u53f3\u7684\u8ddd\u79bb\u3002 \u3000\u3000\u6b64\u523b\u9ea6\u5c0f\u660e\u5185\u5fc3\u5df2\u6709\u7edd\u5bf9\u628a\u63e1\uff0c\u5bf9\u65b9\u6839\u672c\u4e0d\u53ef\u80fd\u9003\u8131\uff0c\u9000\u4e00\u6b65\u8bf4\uff0e\u5373\u4f7f\u5979\u771f\u662f\u9b3c\u9b42\u51fa \u73b0\uff0c\u4e5f\u5fc5\u53ef\u770b\u6e05\u5979\u9690\u6ca1\u4e4b\u5904\u3002 \u3000\u3000\u5982\u679c\u9690\u6ca1\u5728\u5979\u6240\u5750\u7684\u90a3\u5ea7\u575f\u5893\u91cc\uff0e\u90a3\u4e48\u8fd9\u5ea7\u575f\u5893\uff0c\u5c31\u5fc5\u5b9a\u662f\u5979\u7684\u57cb\u846c\u4e4b\u6240\uff0c \u3000\u3000\u5728\u8fd9\u5239\u90a3\u95f4\uff0c\u9ea6\u5c0f\u660e\u9690\u9690\u611f\u5230\uff0c\u4f3c\u4e4e\u5168\u8eab\u7684\u6bcf\u4e00\u4e2a\u7ec6\u80de\uff0e\u90fd\u5728\u5f00\u59cb\u7d27\u7f29\uff0e\u4f46\u4ed6\u5374\u4e0d\u4fbf\u4e00 \u6251\u800c\u4e0a\u3002 \u3000\u3000\u53ef\u4ee5\u60f3\u89c1\uff0c\u8fdc\u5904\u571f\u5c97\u540e\u7684\u4e07\u6620\u971e\u548c\u82d7\u7d20\u82d3\uff0c\u8fd9\u65f6\u5185\u5fc3\u5fc5\u5b9a\u4e5f\u6709\u7740\u540c\u6837\u65e0\u6bd4\u7684\u7d27\u5f20\uff0e \u3000\u3000\u9ea6\u5c0f\u660e\u4ecd\u7ee7\u7eed\u5c4f\u606f\u6162\u6162\u63a5\u8fd1\u3002 \u3000\u3000\u8c01\u77e5\u5c31\u5728\u8fd9\u65f6\uff0c\u5fae\u4e00\u7728\u773c\u4e4b\u95f4\uff0c\u90a3\u9ed1\u8863\u8499\u9762\u5973\u5b50\u7adf\u500f\u5ffd\u95f4\u5931\u53bb\u8e2a\u5f71\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u7acb\u5373\u60ca\u547c\u5931\u58f0\uff0c\u56e0\u4e3a\u4ed6\u7adf\u771f\u7684\u672a\u770b\u6e05\u90a3\u5973\u5b50\u7a76\u7adf\u662f\u600e\u4e48\u6837\u6d88\u5931\u7684\u3002\u5929\u4e0b\u7adf\u771f\u7684\u6709 \u8fd9\u7b49\u89c1\u6240\u672a\u89c1\u95fb\u6240\u672a\u95fb\u7684\u602a\u4e8b\uff0c\u504f\u504f\u5c31\u88ab\u4ed6\u9047\u4e0a\u4e86\uff0e \u3000\u3000\u4ed6\u6025\u6025\u8dc3\u5230\u5893\u524d\uff0c\u770b\u90a3\u5893\u7891\u65f6\uff0c\u53ea\u89c1\u4e0a\u9762\u5199\u7740\u663e\u8003\u67d0\u516c\u4e4b\u5893\uff0c\u663e\u7136\u4e0e\u90a3\u9ed1\u8863\u8499\u9762\u5973\u5b50\u65e0 \u5173\u3002 \u3000\u3000\u8fd9\u7a76\u7adf\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u5f53\u4ed6\u6014\u6014\u5730\u56de\u5230\u539f\u5904\uff0c\u7f01\u8863\u8001\u5c3c\u548c\u82d7\u7d20\u82d3\u3001\u4e07\u6620\u971e\u4ecd\u5728\u571f\u5c97\u540e\u7684\u659c \u5761\u4e0a\u3002 \u3000\u3000\u7ec6\u8863\u8001\u5c3c\u51b7\u51b7\u7b11\u9053\uff1a\u201c\u65b9\u624d\u7684\u7ecf\u8fc7\uff0c\u6211\u548c\u5979\u4eec\u4e24\u4f4d\u59d1\u5a18\u90fd\u5df2\u770b\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u4f60\u5c31\u4e0d\u5fc5\u518d \u8bf4\u4e86\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u5401\u4e86\u53e3\u6c14\uff1a\u201c\u83ab\u975e\u5979\u771f\u662f\u9b3c\uff1f\u201d \u3000\u3000\u7f01\u8863\u8001\u5c3c\u9053\uff1a\u201c\u4f60\u79bb\u5979\u90a3\u4e48\u8fd1\u90fd\u6ca1\u770b\u6e05\u5979\u662f\u4eba\u662f\u9b3c\uff0c\u8d2b\u5c3c\u53c8\u600e\u77e5\u5979\u662f\u4ec0\u4e48\uff0e\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u9b3c\u9b42\u51fa\u73b0\u65f6\uff0c\u672c\u6765\u548c\u4eba\u6ca1\u6709\u5206\u522b\uff0c\u665a\u8f88\u53c8\u600e\u80fd\u5224\u5b9a\u5979\u662f\u4eba\u662f\u9b3c\uff1f\u201d \u3000\u3000\u7f01\u8863\u8001\u5c3c\u9053\uff1a\u201c\u5982\u679c\u662f\u4eba\uff0c\u5979\u53c8\u600e\u4f1a\u5728\u5239\u90a3\u4e4b\u95f4\u6d88\u5931\u5f97\u65e0\u5f71\u65e0\u8e2a\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u8fd9\u6837\u8bf4\u8001\u5e08\u592a\u4e00\u5b9a\u8ba4\u4e3a\u5979\u771f\u662f\u9b3c\u4e86\uff1f\u201d \u3000\u3000\u7f01\u8863\u8001\u5c3c\u7b11\u9053\uff1a\u201c\u6211\u5728\u8fdc\u5904\uff0c\u54ea\u91cc\u6709\u4f60\u770b\u5f97\u771f\u5207\uff0c\u5e94\u5f53\u4f60\u6bd4\u6211\u66f4\u660e\u767d\uff0e\u4e3a\u4ec0\u4e48\u53cd\u800c\u95ee\u8d77 \u6211\u6765\uff1f\u201d \u3000\u3000\u82d7\u7d20\u82d3\u95ee\u9053\uff1a\u201c\u5979\u4f1a\u4e0d\u4f1a\u518d\u51fa\u73b0\uff1f\u201d \u3000\u3000\u7f01\u8863\u8001\u5c3c\u7ad9\u8d77\u8eab\u9053\uff1a\u201c\u8d70\u5427\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u3001\u82d7\u7d20\u82d3\u3001\u4e07\u6620\u971e\u4e09\u4eba\u968f\u7f01\u8863\u8001\u5c3c\u56de\u5230\u5999\u5999\u5eb5\uff0c\u5df2\u5c06\u8fd1\u56db\u66f4\u3002\u4f46\u4ed6\u4eec\u56e0\u5bf9\u7f01\u8863\u8001 \u5c3c\u6709\u79cd\u96be\u8a00\u7684\u656c\u754f\uff0c\u4e0d\u6562\u8fdf\u8d77\uff0e\u5929\u521a\u4eae\u4fbf\u90fd\u81ea\u52a8\u8d77\u5e8a\u3002 \u3000\u3000\u4e0d\u4e45\u4e91\u5fc3\u5c0f\u5c3c\u9001\u6765\u65e9\u996d\uff0c\u4e34\u8d70\u65f6\u9053\uff1a\u201c\u4e09\u4f4d\u5c0f\u65bd\u4e3b\u996d\u540e\u8bf7\u5230\u6628\u5929\u5f85\u8336\u7684\u7cbe\u820d\uff0c\u6211\u4eec\u5e08\u7956 \u5728\u90a3\u91cc\u7b49\u4f60\u4eec\uff0e\u201d \u3000\u3000\u4e09\u4eba\u4e0d\u6562\u6020\u6162\uff0c\u5306\u5306\u7528\u8fc7\u65e9\u996d\uff0c\u7acb\u5373\u8d76\u5230\u6628\u65e5\u53bb\u8fc7\u7684\u5f85\u5ba2\u7cbe\u820d\uff0e \u3000\u3000\u7f01\u8863\u8001\u5c3c\u679c\u7136\u5df2\u5148\u7b49\u5728\u90a3\u91cc\uff0c\u5979\u7684\u8eab\u65c1\uff0c\u8fd8\u7ad9\u7740\u5eb5\u4e3b\u9759\u6708\u548c\u4e91\u5fc3\u5c0f\u5c3c\u3002 \u3000\u3000\u4e09\u4eba\u8fde\u5fd9\u606d\u656c\u4e0a\u524d\u62dc\u89c1\uff0e \u3000\u3000\u7f01\u8863\u8001\u5c3c\u9053\uff1a\u201c\u5999\u5999\u5eb5\u4e8c\u5341\u5e74\u6765\uff0c\u4ece\u672a\u7559\u5916\u4eba\u4f4f\u5bbf\uff0c\u5bf9\u4f60\u4eec\u7b97\u662f\u7834\u4f8b\uff0c\u73b0\u5728\u4f60\u4eec\u8be5\u8d70 \u4e86\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u8eac\u8eab\u7b54\u9053\uff1a\u201c\u665a\u8f88\u4eec\u672c\u6765\u5c31\u51c6\u5907\u6765\u5411\u8001\u5e08\u592a\u8f9e\u522b\u7684\u3002\u201d \u3000\u3000\u7f01\u8863\u8001\u5c3c\u53cc\u76ee\u6e5b\u6e5b\u795e\u5149\uff0c\u626b\u89c6\u4e86\u4e09\u4eba\u4e00\u773c\u9053\uff1a\u201c\u8d2b\u5c3c\u60f3\u7559\u4e0b\u4f60\u4eec\u5176\u4e2d\u4e00\u4eba\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u4e0d\u77e5\u5bf9\u65b9\u662f\u4f55\u7528\u610f\uff0c\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u665a\u8f88\u4eec\u5e76\u6ca1\u72af\u4ec0\u4e48\u9519\uff0c\u8001\u5e08\u592a\u4e3a\u4f55\u8981\u7559\u4e0b\u6211\u4eec \u7684\u4eba\uff1f\u201d \u3000\u3000\u7f01\u8863\u8001\u5c3c\u7b11\u9053\uff1a\u201c\u5c0f\u65bd\u4e3b\u591a\u5fc3\u4e86\u3002\u8d2b\u5c3c\u7559\u4e0b\u4e00\u4eba\uff0e\u6559\u4ed6\u51e0\u62db\u6b66\u529f\uff0c\u96be\u9053\u4e0d\u597d\u5417\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u8fde\u5fd9\u6df1\u65bd\u4e00\u793c\u9053\uff1a\u201c\u65e2\u662f\u5982\u6b64\uff0c\u665a\u8f88\u613f\u610f\u7559\u4e0b\uff01\u201d \u3000\u3000\u7f01\u8863\u8001\u5c3c\u6447\u5934\u9053\uff1a\u201c\u4e0d\u6210\uff01\u4f60\u82e5\u7559\u5728\u8fd9\u91cc\uff0c\u5fc5\u5b9a\u5f15\u8d77\u970d\u5143\u4f3d\u7684\u7591\u5fc3\uff0c\u4e07\u2014\u4ed6\u627e\u5230\u8fd9\u91cc\u5c82 \u4e0d\u6270\u4e71\u4e86\u5999\u5999\u5eb5\u7684\u6e05\u9759\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u987f\u89c9\u4e00\u9635\u61ca\u4e27\u3002 \u3000\u3000\u6df1\u77e5\u4e60\u6b66\u4e4b\u4eba\uff0c\u6240\u671f\u76fc\u7684\u5c31\u662f\u6b66\u529f\u80fd\u66f4\u4e0a\u5c42\u697c\uff0e\u5c24\u5176\u9ea6\u5c0f\u660e\u56e0\u597d\u80dc\u5fc3\u5f3a\uff0c\u66f4\u662f\u4e0d\u613f\u5931\u53bb \u8fd9\u6b21\u5e73\u751f\u96be\u9022\u7684\u673a\u4f1a\u3002 \u3000\u3000\u53ea\u542c\u82d7\u7d20\u82d3\u62a2\u7740\u9053\uff1a\u201c\u90a3\u4e48\u8001\u5e08\u592a\u5c31\u8bf7\u628a\u665a\u8f88\u7559\u4e0b\u5427\uff01\u201d \u3000\u3000\u5979\u56e0\u5df2\u542c\u9ea6\u5c0f\u660e\u548c\u4e07\u6620\u971e\u8ff0\u8bf4\u7f01\u8863\u8001\u5c3c\u795e\u529f\u76d6\u4e16\uff0c\u6240\u4ee5\u624d\u6709\u6b64\u8981\u6c42\u3002 \u3000\u3000\u7f01\u8863\u8001\u5c3c\u53c8\u6447\u5934\u9053\uff1a\u201c\u8d2b\u5c3c\u53ea\u51c6\u5907\u7559\u4e0b\u4e00\u4eba\uff0c\u4f46\u5374\u4e0d\u80fd\u7559\u4f60\uff01\u201d \u3000\u3000\u82d7\u7d20\u82d3\u6005\u7136\u95ee\u9053\uff1b\u201c\u8001\u5e08\u592a\u53ef\u662f\u770b\u4e0d\u8d77\u665a\u8f88\uff1f\u201d \u3000\u3000\u7f01\u8863\u8001\u5c3c\u9053\uff1a\u201c\u4f60\u522b\u8bef\u4f1a\uff0c\u6211\u5df2\u542c\u8bf4\u4f60\u539f\u662f\u9634\u624b\u4e00\u9b54\u7684\u95e8\u4e0b\uff0c\u53c8\u88ab\u5c11\u6797\u638c\u95e8\u66ff\u5ce8\u5d4b\u638c\u95e8 \u6536\u4e3a\u8bb0\u540d\u5f1f\u5b50\uff0c\u540e\u6765\u957f\u767d\u795e\u53df\u5e9e\u58eb\u51b2\u53c8\u628a\u4f60\u5217\u5165\u95e8\u5899\u3002\u53ef\u89c1\u4f60\u7684\u6b66\u529f\u5fc5\u5b9a\u5df2\u7ecf\u4e0d\u51e1\uff0c\u4f55\u51b5\u636e \u8bf4\u4f60\u5230\u8ff7\u8e2a\u8c37\uff0c\u662f\u8981\u89c1\u4f60\u90a3\u59d0\u59d0\u82d7\u7d20\u5170\u3002\u56e0\u4e4b\uff0c\u8d2b\u5c3c\u4e0d\u4fbf\u803d\u8bef\u4e86\u4f60\u4eec\u59d0\u59b9\u7684\u4e45\u522b\u91cd\u9022\u3002\u201d \u3000\u3000\u4e07\u6620\u971e\u542c\u5230\u8fd9\u91cc\uff0c\u6025\u6025\u8d8b\u524d\u4e24\u6b65\uff0c\u62dc\u4f0f\u5728\u5730\u9053\uff1a\u201c\u5f1f\u5b50\u4e07\u6620\u971e\u53e9\u89c1\u5e08\u7236\uff01\u201d \u3000\u3000\u7f01\u8863\u8001\u5c3c\u62ac\u8d77\u53f3\u624b\uff0c\u865a\u7a7a\u4e00\u6258\u9053\uff1b\u201c\u8d77\u6765\u3002\u4e0d\u51c6\u79f0\u6211\u5e08\u7236\uff0c\u56e0\u4e3a\u4f60\u6211\u4e4b\u95f4\uff0c\u8f88\u4efd\u4e0d \u5bf9\u3002\u201d \u3000\u3000\u5728\u8fd9\u5239\u90a3\uff0c\u4e07\u6620\u971e\u53ea\u611f\u5230\u6709\u80a1\u5de8\u5927\u800c\u53c8\u67d4\u548c\u7684\u6697\u52b2\uff0c\u6258\u5f97\u5979\u4e0d\u60f3\u8d77\u6765\u4e5f\u975e\u8d77\u6765\u4e0d\u53ef\u3002 \u3000\u3000\u7f01\u8863\u8001\u5c3c\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u4f46\u4e0d\u62dc\u5e08\u7236\u4e0d\u4fbf\u6388\u827a\uff0c\u6240\u4ee5\u6211\u547d\u4f60\u62dc\u5728\u9759\u6708\u95e8\u4e0b\u3002\u9759\u6708\u968f\u6211\u53d7\u827a \u591a\u5e74\uff0c\u8bba\u6b66\u529f\u4e0d\u5728\u4f60\u6240\u89c1\u8fc7\u7684\u9ad8\u4eba\u4e4b\u4e0b\u3002\u5982\u679c\u8d2b\u5c3c\u6709\u7a7a\uff0c\u4e5f\u8bb8\u4f1a\u987a\u4fbf\u4f20\u4f60\u51e0\u62db\u3002\u201d \u3000\u3000\u5200\u6620\u971e\u8fde\u5fd9\u518d\u5ea6\u8dea\u62dc\u4e0b\u53bb\uff0c\u4e0d\u8fc7\u8fd9\u6b21\u5979\u5df2\u6539\u79f0\u201c\u5e08\u7956\u201d\uff0e \u3000\u3000\u63a5\u7740\u518d\u5411\u5e08\u7236\u9759\u6708\u62dc\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u9759\u6708\u4eb2\u81ea\u6276\u8d77\u4e07\u6620\u971e\u9053\uff1a\u201c\u6709\u5e08\u7956\u5728\uff0c\u7528\u4e0d\u7740\u518d\u5411\u6211\u884c\u6b64\u5927\u793c\u3002\u201d \u3000\u3000\u7f01\u8863\u8001\u5c3c\u9ed8\u4e86\u4e00\u9ed8\uff0e\u518d\u9053\uff1a\u201c\u971e\u513f\uff0c\u4f60\u53ef\u77e5\u9053\u6211\u5355\u5355\u628a\u4f60\u6536\u7559\u5728\u5999\u5999\u5eb5\u7684\u539f\u56e0\u5417\uff1f\u201d \u3000\u3000\u4e07\u6620\u971e\u8c28\u58f0\u9053\uff1a\u201c\u5f92\u5b59\u4e0d\u77e5\u3002\u201d \u3000\u3000\u7f01\u8863\u8001\u5c3c\u53f9\u606f\u4e00\u58f0\u9053\uff1a\u201c\u6211\u867d\u7136\u548c\u4f60\u7236\u4eb2\u4e07\u6653\u5149\u5e76\u4e0d\u8ba4\u8bc6\uff0c\u4f46\u5374\u77e5\u9053\u4ed6\u662f\u4e00\u4e2a\u597d\u4eba\uff0c\u53c8 \u77e5\u9053\u4ed6\u6b7b\u5f97\u6781\u60e8\u3002\u4f60\u8eab\u4e3a\u5973\u513f\uff0c\u5fc5\u5b9a\u65e9\u6709\u4e3a\u7236\u62a5\u4ec7\u4e4b\u5fd7\u3002\u6211\u547d\u4f60\u7559\u5728\u5999\u5999\u5eb5\uff0c\u4e0d\u5916\u662f\u80fd\u8ba9\u4f60 \u5c06\u6765\u5b8c\u6210\u4e3a\u7236\u62a5\u4ec7\u7684\u5fc3\u613f\u3002\u201d \u3000\u3000\u60f3\u8d77\u4e09\u5e74\u524d\u7236\u4eb2\u4e4b\u6b7b\uff0c\u4e07\u6620\u971e\u4e0d\u7981\u6cea\u6d41\u6ee1\u9762\uff0c\u6fc0\u52a8\u65e0\u6bd4\u5730\u518d\u5ea6\u62dc\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u7f01\u8863\u8001\u5c3c\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4eba\u5148\u56de\u6628\u665a\u4f4f\u5bbf\u4e4b\u5904\u5c0f\u5750\u4e00\u4f1a\u513f\uff0c\u6211\u8981\u628a\u971e\u513f\u7559\u5728\u8fd9\u91cc\u4ea4\u4ee3\u5979\u51e0 \u53e5\u8bdd\uff0c\u5f85\u4f1a\u513f\u518d\u547d\u5979\u9001\u4f60\u4eec\u4e00\u7a0b\uff0e\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u548c\u82d7\u7d20\u82d3\u56de\u5230\u6628\u665a\u6240\u4f4f\u7684\u7cbe\u820d\u4e0d\u4e45\uff0e\u4e07\u6620\u971e\u4e5f\u6765\u4e86\u3002\u5979\u8bf4\uff1a\u201c\u8d70\u5427\uff01\u6211\u9001\u4f60\u4eec\u4e24 \u4f4d\u3002\u201d \u3000\u3000\u63a5\u7740\u53c8\u9053\uff1a\u201c\u5e08\u7956\u548c\u5e08\u7236\u90fd\u5df2\u4ea4\u4ee3\u8fc7\uff0c\u4e0d\u5fc5\u518d\u53bb\u89c1\u5979\u4eec\u4e86\u3002\u201d \u3000\u3000\u51fa\u4e86\u5eb5\u95e8\uff0c\u6682\u65f6\u8c01\u90fd\u6ca1\u8bdd\u3002\u4e0d\u6d88\u8bf4\uff0c\u6b64\u523b\u7684\u9ea6\u5c0f\u660e\u548c\u82d7\u7d20\u82d3\uff0c\u90fd\u96be\u514d\u6697\u81ea\u7fa1\u6155\u4e07\u6620\u971e\u7684 \u9645\u9047\u3002 \u3000\u3000\u8d70\u4e86\u8bb8\u4e45\uff0c\u9ea6\u5c0f\u660e\u624d\u5401\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u4e07\u59d1\u5a18\uff0c\u8001\u5e08\u592a\u53ef\u66fe\u8bf4\u8fc7\u8981\u628a\u4f60\u7559\u5728\u8fd9\u91cc\u591a\u4e45\uff1f\u201d \u3000\u3000\u4e07\u6620\u971e\u9053\uff1a\u201c\u5e08\u7956\u548c\u5e08\u7236\u90fd\u4e0d\u66fe\u900f\u9732\u8fc7\uff0e\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4f60\u80fd\u88ab\u8001\u5e08\u592a\u548c\u9759\u6708\u5eb5\u4e3b\u7559\u4e0b\u6765\u6388\u827a\uff0c\u5728\u4f60\u6765\u8bf4\uff0c\u5f53\u7136\u662f\u4ef6\u597d\u4e8b\uff0c\u4f46\u5374\u7ed9 \u6211\u6dfb\u4e86\u5f88\u591a\u4e3a\u96be\u4e4b\u5904\u3002\u201d \u3000\u3000\u4e07\u6620\u971e\u98a6\u8d77\u9edb\u7709\u9053\uff1a\u201c\u6211\u660e\u767d\u4f60\u7684\u610f\u601d\uff0c\u4f60\u662f\u8bf4\u56de\u53bb\u4ee5\u540e\u4e0d\u597d\u5411\u949f\u526f\u76df\u4e3b\u4ea4\u4ee3\uff0e\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u82e6\u7b11\u9053\uff1a\u201c\u5bf9\u949f\u526f\u76df\u4e3b\u5012\u6ca1\u4ec0\u4e48\u4e0d\u597d\u4ea4\u4ee3\u7684\uff0c\u96be\u4ee5\u4ea4\u4ee3\u7684\u8be5\u662f\u4f60\u90a3\u5e08\u5144\u6587\u5929\u751f\u3002 \u4f60\u8ddf\u4ed6\u5173\u7cfb\u4e0d\u540c\u3002\u5f53\u521d\u6211\u8bf7\u6c42\u4f60\u4e00\u8d77\u6765\uff0e\u73b0\u5728\u5374\u53c8\u4e0d\u80fd\u5e26\u4f60\u4e00\u8d77\u56de\u53bb\uff0c\u56de\u5230\u8ff7\u8e2a\u8c37\u8ba9\u6211\u5bf9\u4ed6 \u8bf4\u4ec0\u4e48\u597d\uff1f\u201d \u3000\u3000\u4e07\u6620\u971e\u4e0d\u89c9\u5a07\u7f9e\u6ee1\u9762\uff0c\u4f4e\u4e0b\u5934\u9053\uff1a\u201c\u4e3a\u7236\u62a5\u4ec7\uff0c\u6bd4\u548c\u4ed6\u5728\u4e00\u8d77\u5e94\u8be5\u66f4\u91cd\u8981\uff0e\u5148\u7236\u5f53\u5e74\u5f85 \u4ed6\u6069\u91cd\u5982\u5c71\uff0c\u6211\u80fd\u7559\u5728\u5999\u5999\u5eb5\u4e60\u827a\uff0c\u5982\u679c\u4ed6\u662f\u4e2a\u8bc6\u5927\u4f53\u660e\u5927\u4e49\u7684\u4eba\uff0c\u5e94\u5f53\u4e3a\u6211\u9ad8\u5174\u624d\u5bf9\u3002\u201d \u3000\u3000\u201c\u4f60\u8ba4\u4e3a\u6587\u5929\u751f\u6709\u8fd9\u79cd\u5ea6\u91cf\u5417\uff1f\u201d \u3000\u3000\u201c\u6211\u60f3\u4ed6\u5e94\u8be5\u6709\u7684\u3002\u201d \u3000\u3000\u201c\u90a3\u6211\u5c31\u653e\u5fc3\u5566\uff0c\u8001\u5e08\u592a\u53ef\u8fd8\u6709\u4ec0\u4e48\u4ea4\u4ee3\u7684\uff1f\u201d \u3000\u3000\u201c\u5e08\u7956\u548c\u5e08\u7236\u90fd\u4ea4\u4ee3\u8fc7\uff0c\u8ff7\u8e2a\u8c37\u4e0d\u80fd\u518d\u6709\u4efb\u4f55\u4eba\u5230\u5999\u5999\u5eb5\u6765\uff0e\u5c24\u5176\u970d\u5143\u4f3d\u65b9\u9762\u7684\u4eba\uff0c\u8fde \u5999\u5999\u5eb5\u5728\u4ec0\u4e48\u5730\u65b9\u90fd\u4e0d\u80fd\u8ba9\u4ed6\u4eec\u77e5\u9053\uff0c\u66f4\u4e0d\u80fd\u8ba9\u4ed6\u4eec\u77e5\u9053\u5e08\u7956\u662f\u4f4f\u5728\u5999\u5999\u5eb5\u91cc\u3002\u201d \u3000\u3000\u201c\u5982\u679c\u6211\u6765\u5462\uff1f\u201d \u3000\u3000\u201c\u4f60\u6765\u4e5f\u8bb8\u4f1a\u4f8b\u5916\u3002\u201d \u3000\u3000\u4e07\u6620\u971e\u8bf4\u7740\uff0c\u4ece\u6000\u91cc\u638f\u51fa\u4e00\u672c\u8584\u518c\u9053\uff1a\u201c\u8fd9\u662f\u5e08\u7956\u8981\u6211\u4ea4\u7ed9\u4f60\u7684\uff0c\u8bf7\u6536\u4e0b\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u832b\u7136\u63a5\u8fc7\uff0e\u53ea\u89c1\u90a3\u8584\u518c\u5199\u7740\u201c\u5999\u5999\u5251\u8bc0\u201d\u56db\u4e2a\u9887\u4e3a\u82cd\u52b2\u800c\u53c8\u5a1f\u79c0\u7684\u5b57\u3002 \u3000\u3000\u4ed6\u4e0d\u89c9\u559c\u51fa\u671b\u5916\u9053\uff1a\u201c\u83ab\u975e\u662f\u8001\u5e08\u592a\u8d50\u7ed9\u6211\u7684\u6b66\u5b66\u79d8\u7b08\uff1f\u201d \u3000\u3000\u4e07\u6620\u971e\u9053\uff1a\u201c\u867d\u662f\u7edd\u5999\u6b66\u5b66\uff0c\u5374\u7b97\u4e0d\u5f97\u79d8\u7b08\u3002\u4f60\u6ca1\u89c1\u58a8\u6c41\u521a\u5e72\uff0c\u8fd9\u662f\u5e08\u7956\u6628\u665a\u7531\u5893\u5730\u56de \u6765\u540e\u624d\u4e34\u65f6\u5199\u6210\u7684\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u8001\u5e08\u592a\u4e3a\u4ec0\u4e48\u7adf\u80af\u8d50\u6211\u8fd9\u518c\u6b66\u529f\u7edd\u5b66\uff1f\u201d \u3000\u3000\u4e07\u6620\u971e\u9053\uff1a\u201c\u5b9e\u5bf9\u4f60\u8bf4\uff0c\u5e08\u7956\u4e00\u76f4\u79c1\u4e0b\u8d5e\u7f8e\u4f60\u7684\u8d44\u8d28\u4e0d\u51e1\uff0e\u662f\u6b66\u6797\u4e2d\u96be\u5f97\u5947\u624d\uff0e\u5979\u539f\u60f3 \u628a\u4f60\u6536\u4e0b\uff0c\u53c8\u62c5\u5fc3\u8ff7\u8e2a\u8c37\u5931\u53bb\u4f60\uff0e\u5f88\u53ef\u80fd\u4f7f\u949f\u526f\u76df\u4e3b\u5c06\u65e0\u6cd5\u518d\u548c\u970d\u5143\u4f3d\u7ee7\u7eed\u5468\u65cb\uff0e\u6240\u4ee5\uff0c\u624d \u8fde\u591c\u628a\u5979\u6240\u4f53\u4f1a\u5230\u7684\u5251\u672f\u7cbe\u534e\u5199\u6210\u4e00\u672c\u8584\u518c\uff0c\u53d6\u540d\u5999\u5999\u5251\u8bc0\uff0c\u8981\u4f60\u56de\u5230\u8ff7\u8e2a\u8c37\u540e\uff0c\u81ea\u884c\u7ec3 \u4e60\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u53ea\u542c\u5f97\u5927\u4e3a\u611f\u52a8\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u8001\u5e08\u592a\u4f1a\u8fd9\u6837\u8d4f\u8bc6\u6211\uff0c\u6211\u5c06\u6765\u4e00\u5b9a\u4e0d\u8d1f\u5979\u7684\u671f \u671b\u3002\u201d \u3000\u3000\u4e07\u6620\u971e\u9053\uff1a\u201c\u5e08\u7956\u8fd8\u7279\u522b\u4ea4\u4ee3\u6211\u8f6c\u544a\u4f60\uff0c\u8fd9\u672c\u8584\u518c\u4e0d\u53ef\u8ba9\u5916\u4eba\u770b\u5230\uff0c\u5373\u4f7f\u5728\u4e60\u7ec3\u7684\u65f6 \u5019\uff0c\u4e5f\u8981\u627e\u4e2a\u9690\u5bc6\u5730\u65b9\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u8bf7\u8f6c\u544a\u8001\u5e08\u592a\uff0c\u8bf7\u5979\u8001\u4eba\u5bb6\u653e\u5fc3\uff0c\u6211\u4e00\u5b9a\u4f1a\u8c28\u9075\u5979\u7684\u5429\u5490\u3002\u201d \u3000\u3000\u53ea\u542c\u82d7\u7d20\u82d3\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4f4d\u90fd\u6709\u4e00\u756a\u5947\u9047\uff0c\u79f0\u5f97\u4e0a\u662f\u4e0d\u865a\u6b64\u884c\uff0c\u552f\u6709\u6211\u4ec0\u4e48\u4e5f\u6ca1\u5f97 \u5230\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4f60\u53ef\u662f\u5728\u5ac9\u5992\u6211\u4eec\uff1f\u5176\u5b9e\u4f60\u7684\u5947\u9047\u6bd4\u6211\u4eec\u66f4\u591a\u3002\u4e00\u6708\u4e0d\u5230\uff0c\u4fbf\u5f97\u5230\u4e24\u4f4d\u7edd \u9876\u9ad8\u4eba\u6536\u4e3a\u95e8\u4e0b\uff0c\u96be\u9053\u8fd8\u4e0d\u591f\u5417\uff1f\u201d \u3000\u3000\u8bf4\u8bdd\u95f4\u5df2\u5230\u8fbe\u90a3\u7247\u5893\u5730\uff0e \u3000\u3000\u82d7\u7d20\u82d3\u9053\uff1a\u201c\u9ea6\u5c0f\u4fa0\u8ba4\u4e3a\u6628\u665a\u90a3\u9ed1\u8863\u8499\u9762\u5973\u4eba\u5230\u5e95\u662f\u4eba\u662f\u9b3c\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u6211\u4e5f\u88ab\u5f04\u7cca\u6d82\u4e86\uff0e\u6211\u672c\u6765\u4e00\u5411\u4e0d\u76f8\u4fe1\u6709\u9b3c\uff0c\u4f46\u6628\u665a\u7684\u4e8b\uff0c\u5374\u53c8\u4f7f\u6211\u4e0d\u5f97\u4e0d \u4fe1\u3002\u5f85\u4f1a\u513f\u54b1\u4eec\u5c31\u518d\u5230\u90a3\u575f\u5893\u9644\u8fd1\u4ed4\u7ec6\u770b\u770b\uff0e\u201d \u3000\u3000\u201c\u770b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u770b\u770b\u9644\u8fd1\u662f\u5426\u6709\u4ec0\u4e48\u673a\u5173\u5e03\u7f6e\uff0c\u5982\u679c\u53d1\u73b0\u53ef\u7591\u4e4b\u5904\uff0c\u90a3\u4e48\uff0e\u90a3\u9ed1\u8863\u8499\u9762\u5973\u5b50\u5fc5\u5b9a\u4ecd\u7136 \u662f\u4eba\uff0e\u201d \u3000\u3000\u6765\u5230\u6628\u665a\u9ed1\u8863\u8499\u9762\u5973\u5b50\u6240\u5750\u8fc7\u7684\u90a3\u5ea7\u575f\u5893\uff0e\u4e09\u4eba\u7acb\u5373\u5c55\u5f00\u5bf9\u9644\u8fd1\u5730\u5f62\u7684\u8be6\u7ec6\u89c2\u5bdf\uff0c\u5374\u4e1d \u6beb\u770b\u4e0d\u51fa\u6709\u4efb\u4f55\u53ef\u7591\u4e4b\u5904\uff0e \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u770b\u6765\u8fd9\u4e8b\u53ea\u597d\u62dc\u6258\u4e07\u59d1\u5a18\u4e86\uff0e\u201d \u3000\u3000\u4e07\u6620\u971e\u9053\uff1a\u201c\u83ab\u975e\u6211\u8fd8\u80fd\u5e2e\u5f97\u4e0a\u5fd9\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4f60\u7559\u5728\u5999\u5999\u5eb5\uff0c\u6bcf\u665a\u90fd\u53ef\u4ee5\u6765\u770b\uff0c\u6162\u6162\u5fc5\u80fd\u67e5\u51fa\u771f\u76f8\uff0e\u201d \u3000\u3000\u4e07\u6620\u971e\u4e0d\u7981\u6253\u5fc3\u5e95\u5347\u8d77\u4e00\u80a1\u5bd2\u610f\uff0c\u9053\uff1a\u201c\u5982\u679c\u6df1\u66f4\u534a\u591c\u6211\u4e00\u4e2a\u4eba\u6765\uff0e\u6211\u8fd8\u771f\u6709\u70b9\u80c6\u602f \u5462\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u827a\u9ad8\u4eba\u80c6\u5927\u3002\u4f60\u80fd\u5f97\u5230\u8fd9\u4e24\u4f4d\u540d\u5e08\u6388\u827a\uff0c\u4eca\u540e\u6b66\u529f\u5fc5\u5b9a\u7a81\u98de\u731b\u8fdb\uff0c\u4e00\u65e5\u5343 \u91cc\uff0c\u5230\u90a3\u65f6\u8fd8\u6709\u4ec0\u4e48\u53ef\u6015\u7684\uff0e\u201d \u3000\u3000\u4e09\u4eba\u79bb\u5f00\u5893\u5730\uff0c\u7ee7\u7eed\u8d76\u8def\uff0c\u7a7f\u8fc7\u90a3\u7247\u6811\u6797\uff0c\u6765\u5230\u5c94\u8def\u53e3\uff0c\u4e07\u6620\u971e\u624d\u9053\u522b\u800c\u53bb\u3002 \u3000\u3000\u6570\u65e5\u540e\uff0c\u9ea6\u5c0f\u660e\u548c\u82d7\u7d20\u82d3\u5df2\u5230\u8fbe\u5317\u5cb3\u8ff7\u8e2a\u8c37\u3002 \u3000\u3000\u4e3a\u4e86\u4e0d\u4f7f\u970d\u5143\u4f3d\u65b9\u9762\u7684\u4eba\u53d1\u89c9\u82d7\u7d20\u82d3\uff0c\u5728\u5165\u8c37\u524d\uff0c\u9ea6\u5c0f\u660e\u7279\u522b\u8981\u6c42\u82d7\u7d20\u82d3\u6362\u4e0a\u4e00\u8eab\u7537 \u88c5\uff0c\u5e76\u628a\u5165\u8c37\u7684\u65f6\u95f4\u9009\u62e9\u5728\u5165\u591c\u4e4b\u540e\u3002 \u3000\u3000\u8fdb\u5165\u201c\u67cf\u9f84\u9662\u201d\uff0c\u949f\u4e00\u8c6a\u7b49\u4eba\u5c1a\u672a\u5c31\u5bdd\uff0c\u4ed6\u4eec\u89c1\u9ea6\u5c0f\u660e\u5e26\u4e86\u4e00\u4f4d\u9177\u4f3c\u82d7\u7d20\u5170\u7684\u5c11\u5e74\u4eba \u6765\uff0c\u90fd\u4e0d\u514d\u7a83\u7a83\u8bae\u8bba\u3002 \u3000\u3000\u76f4\u5230\u9ea6\u5c0f\u660e\u8bf4\u660e\u540e\uff0c\u624d\u77e5\u82d7\u7d20\u82d3\u662f\u5973\u626e\u7537\u88c5\u7684\u3002 \u3000\u3000\u4e8e\u662f\u4f17\u4eba\u53c8\u9f50\u96c6\u5728\u5ba2\u5385\u91cc\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u9996\u5148\u4e3a\u82d7\u7d20\u82d3\u5f15\u89c1\u5728\u573a\u7684\u6bcf\u4e00\u4e2a\u4eba\uff0c\u63a5\u7740\u518d\u8be6\u8ff0\u524d\u5f80\u5999\u5999\u5eb5\u7684\u7ecf\u8fc7\u3002 \u3000\u3000\u5f53\u4f17\u4eba\u542c\u5230\u4e07\u6620\u971e\u88ab\u7559\u5728\u5999\u5999\u5eb5\u7684\u6d88\u606f\uff0c\u83ab\u4e0d\u4e3a\u4e07\u6620\u971e\u6709\u6b64\u5947\u9047\u800c\u9ad8\u5174\u3002\u552f\u6709\u6587\u5929\u751f\uff0c \u76b1\u7709\u4f4e\u5934\uff0c\u663e\u51fa\u4e00\u526f\u60d8\u7136\u82e5\u5931\u6a21\u6837\uff0c\u53ea\u662f\u5f53\u7740\u4f17\u4eba\u7684\u9762\uff0c\u4e0d\u4fbf\u8bf4\u4ec0\u4e48\u7f62\u4e86\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u4ee5\u526f\u76df\u4e3b\u7684\u8eab\u4efd\u9053\uff1a\u201c\u82d7\u59d1\u5a18\u5c31\u8bf7\u4f4f\u5728\u4e07\u59d1\u5a18\u7684\u623f\u95f4\u3002\u6700\u8fd1\u51e0\u5929\u6700\u597d\u522b\u5916\u51fa\uff0c\u4e3a \u4e0d\u4f7f\u5916\u4eba\u53d1\u73b0\u53ef\u7591\uff0c\u4e0d\u59a8\u6682\u65f6\u4ecd\u7740\u7537\u88c5\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u95ee\u9053\uff1a\u201c\u8fd9\u4e9b\u5929\u8c37\u91cc\u662f\u5426\u53c8\u53d1\u751f\u8fc7\u4e8b\u60c5\uff1f\u201d \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u8fd9\u4e9b\u5929\u8c37\u91cc\u5012\u662f\u9759\u5f97\u5f88\uff0c\u4f46\u970d\u5143\u4f3d\u5374\u663e\u5f97\u6709\u4e9b\u53cd\u5e38\u3002\u201d \u3000\u3000\u201c\u600e\u4e48\u53cd\u5e38\uff1f\u201d \u3000\u3000\u201c\u4ed6\u5bf9\u54b1\u4eec\u8fd9\u4f19\u4eba\u4f3c\u4e4e\u5904\u5904\u62c9\u62e2\uff0c\u80cc\u540e\u7a76\u7adf\u662f\u4ec0\u4e48\u9634\u8c0b\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u5f88\u96be\u9884\u6d4b\uff0e\u603b\u4e4b\uff0c \u54b1\u4eec\u5728\u573a\u6240\u6709\u7684\u4eba\uff0c\u5fc5\u987b\u683c\u5916\u5c0f\u5fc3\u3002\u201d \u3000\u3000\u6b21\u65e5\u65e9\u996d\u540e\uff0c\u970d\u5143\u4f3d\u6d3e\u6c5f\u5357\u56db\u602a\u4e2d\u7684\u8001\u5927\u5c71\u9b48\u6bdb\u8d8a\u524d\u6765\u4f20\u8bdd\uff0e\u544a\u77e5\u949f\u4e00\u8c6a\u970d\u5143\u4f3d\u4e2d\u5348\u8981 \u5728\u805a\u4e49\u5385\u8bbe\u5bb4\uff0c\u67cf\u9f84\u9662\u7684\u7fa4\u96c4\u52a1\u5fc5\u4e00\u8d77\u8d74\u5bb4\uff0c\u552f\u4e00\u7684\u89c4\u5b9a\uff0c\u662f\u5973\u6027\u4e0d\u5f97\u53c2\u52a0\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u8be2\u95ee\u4e3a\u4f55\u5973\u6027\u4e0d\u5f97\u53c2\u52a0\u3002 \u3000\u3000\u5c71\u9b48\u6bdb\u8d8a\u9053\uff1a\u201c\u5c5e\u4e0b\u4e5f\u4e0d\u660e\u767d\u76df\u4e3b\u4e3a\u4f55\u6709\u8fd9\u79cd\u89c4\u5b9a\uff1f\u201d \u3000\u3000\u5176\u5b9e\u67cf\u9f84\u9662\u5728\u539f\u6709\u7684\u4eba\u4e2d\uff0c\u5973\u6027\u4e5f\u53ea\u6709\u4e07\u6620\u971e\u4e00\u4eba\u800c\u5df2\u3002\u5373\u4ee5\u6574\u4e2a\u8ff7\u8e2a\u8c37\u800c\u8a00\uff0c\u5176\u4f59\u80fd \u4e0a\u5f97\u53f0\u9762\u7684\u5973\u4eba\uff0c\u9664\u4e86\u82d7\u7d20\u5170\uff0c\u5269\u4e0b\u7684\u4e5f\u53ea\u6709\u4e00\u4e2a\u8eab\u4efd\u6210\u8ff7\u7684\u8bb8\u5c0f\u65e6\u3002 \u3000\u3000\u8fd9\u9879\u89c4\u5b9a\u53ef\u8bf4\u5bf9\u67cf\u9f84\u9662\u6beb\u65e0\u5f71\u54cd\u3002 \u3000\u3000\u4e07\u6620\u971e\u672c\u6765\u5df2\u7ecf\u4e0d\u5728\uff0c\u6628\u665a\u65b0\u5230\u7684\u82d7\u7d20\u82d3\uff0c\u4e3a\u4e86\u6682\u65f6\u9690\u85cf\u8eab\u4efd\uff0c\u5c31\u662f\u51c6\u53c2\u52a0\uff0c\u949f\u4e00\u8c6a\u4e5f \u4e0d\u6253\u7b97\u8ba9\u5979\u516c\u5f00\u4eae\u76f8\u3002 \u3000\u3000\u5f88\u5feb\u4e2d\u5348\u4fbf\u5230\uff0c\u67cf\u9f84\u9662\u7684\u4eba\u9664\u82d7\u7d20\u82d3\u5916\uff0c\u5728\u949f\u4e00\u8c6a\u7684\u7387\u9886\u4e0b\uff0c\u9c7c\u8d2f\u8fdb\u5165\u805a\u4e49\u5385\u3002 \u3000\u3000\u805a\u4e49\u5385\u5e2d\u5f00\u4e03\u516b\u684c\uff0c\u53c2\u52a0\u7684\u5f53\u7136\u90fd\u662f\u8ff7\u8e2a\u8c37\u6709\u5934\u6709\u8138\u7684\u4eba\u7269\uff0c\u81f3\u4e8e\u55bd\u7f57\u4eec\u7684\u5e2d\u4f4d\uff0c\u5219\u90fd \u5b89\u6392\u5728\u5927\u5385\u5916\u7684\u5e7f\u573a\u4e0a\u3002 \u3000\u3000\u8fd9\u65f6\u7fa4\u96c4\u4eec\u90fd\u5df2\u5230\u9f50\uff0c\u53ea\u6709\u76df\u4e3b\u970d\u5143\u4f3d\u5c1a\u672a\u8fdb\u573a\u3002 \u3000\u3000\u5927\u5385\u6b63\u4e2d\u7684\u9996\u5e2d\u4e00\u684c\uff0c\u5171\u6709\u516b\u4e2a\u5ea7\u4f4d\u3002\u6bcf\u4e2a\u5ea7\u4f4d\u4e0a\uff0c\u90fd\u653e\u7740\u4e00\u4e2a\u540d\u724c\u3002\u67cf\u9f84\u9662\u65b9\u9762\uff0c\u5728 \u9996\u5e2d\u4e0a\u5171\u5206\u914d\u4e86\u4e09\u4e2a\u5e2d\u4f4d\u3002 \u3000\u3000\u9664\u949f\u4e00\u8c6a\u56e0\u4e3a\u662f\u526f\u76df\u4e3b\u5f53\u7136\u5e94\u5728\u9996\u5e2d\u5916\uff0c\u53e6\u6709\u4f59\u4ea6\u4e50\u548c\u9ea6\u5c0f\u660e\uff0e \u3000\u3000\u4f59\u4ea6\u4e50\u5728\u8ff7\u8e2a\u8c37\u76f8\u5f53\u4e8e\u519b\u5e08\u8eab\u4efd\uff0c\u5730\u4f4d\u5d07\u9ad8\uff0c\u5750\u9996\u5e2d\u4e5f\u662f\u7406\u6240\u5f53\u7136\u3002\u81f3\u4e8e\u9ea6\u5c0f\u660e\u80fd\u8000\u5750 \u9996\u5e2d\uff0c\u8db3\u89c1\u970d\u5143\u4f3d\u662f\u5bf9\u4ed6\u6709\u610f\u7b3c\u7edc\u3002 \u3000\u3000\u81f3\u4e8e\u970d\u5143\u4f3d\u7684\u5fc3\u8179\u624b\u4e0b\u4e5f\u5750\u5728\u9996\u5e2d\u7684\uff0c\u662f\u5cad\u5357\u4e8c\u5947\u548c\u6c5f\u5357\u56db\u602a\u7684\u8001\u5927\u5c71\u9b48\u6bdb\u8d8a\u3002 \u3000\u3000\u8fd8\u6709\u4e24\u4e2a\u7a7a\u4f4d\uff0c\u9664\u970d\u5143\u4f3d\u5916\uff0c\u53e6\u4e00\u5ea7\u4f4d\u4e0d\u77e5\u7559\u7ed9\u4f55\u4eba\u3002\u56e0\u4e3a\u970d\u5143\u4f3d\u89c4\u5b9a\u5973\u6027\u4e0d\u51c6\u53c2\u52a0\uff0c \u538b\u5be8\u592b\u4eba\u82d7\u7d20\u5170\u5f53\u7136\u4e5f\u4e0d\u5e94\u4f8b\u5916\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u4f3c\u662f\u6709\u610f\u5356\u4e56\uff0c\u6545\u610f\u626b\u89c6\u4e86\u5168\u684c\u4e00\u773c\u9053\uff1a\u201c\u8fd9\u4e00\u684c\u662f\u9996\u5e2d\uff0c\u4f60\u4eec\u5927\u5bb6\u90fd\u662f\u6709\u5934\u6709 \u8138\u7684\uff0c\u5750\u9996\u5e2d\u7406\u6240\u5f53\u7136\u3002\u6211\u9ea6\u5c0f\u660e\u7b97\u4ec0\u4e48\uff0e\u4e5f\u5750\u5230\u9996\u5e2d\u4e0a\u6765\uff0c\u53ea\u6015\u8981\u7ed9\u4f60\u4eec\u5927\u5bb6\u4e22\u4eba\uff01\u201d \u3000\u3000\u641c\u9b42\u624b\u5df4\u5929\u4e49\u563f\u563f\u7b11\u9053\uff1a\u201c\u9ea6\u5c0f\u5144\u5f1f\u662f\u672c\u8c37\u6700\u4e86\u4e0d\u8d77\u7684\u4eba\u624d\uff0c\u5750\u9996\u5e2d\u662f\u5e94\u8be5\u7684\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u4e24\u773c\u7728\u4e86\u51e0\u7728\u9053\uff1a\u201c\u6211\u6709\u4ec0\u4e48\u4e86\u4e0d\u8d77\uff0c\u4f60\u8bf4\u8bf4\u770b\uff01\u201d \u3000\u3000\u5df4\u5929\u4e49\u9053\uff1a\u201c\u4e0a\u6b21\u4f60\u5bf9\u4ed8\u6bd2\u706b\u6210\u5168\uff0c\u90a3\u79cd\u6beb\u65e0\u6240\u60e7\uff0c\u4e34\u5371\u4e0d\u4e71\u7684\u7cbe\u795e\uff0c\u4e0d\u4f46\u5df4\u67d0\u66ff\u4f60\u559d \u5f69\uff0c\u5728\u573a\u4e0a\u767e\u4eba\u4e5f\u6ca1\u6709\u4e0d\u4f69\u670d\u7684\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u7b11\u9053\uff1a\u201c\u5df4\u5f53\u5bb6\u7684\u8fc7\u5956\u5566\uff0c\u4f60\u5f88\u591f\u8d44\u683c\u5f53\u4e00\u4f4d\u62cd\u9a6c\u5c41\u4e13\u5bb6\uff0e\u201d \u3000\u3000\u5df4\u5929\u4e49\u7acb\u523b\u6709\u4e9b\u9762\u7ea2\u8033\u8d64\uff0c\u4ed6\u5728\u8ff7\u8e2a\u8c37\u8eab\u4efd\u5730\u4f4d\u6781\u9ad8\uff0c\u5f53\u5e74\u5728\u9ed1\u9053\u4e0a\u66f4\u662f\u4f4d\u65e0\u4eba\u4e0d\u77e5\u7684 \u4eba\u7269\uff0c\u4ece\u65e0\u4eba\u6562\u5728\u4ed6\u9762\u524d\u5982\u6b64\u65e0\u793c\uff0c\u4f46\u5728\u4eca\u5929\u8fd9\u79cd\u573a\u5408\uff0e\u4ed6\u8fd8\u662f\u5fcd\u4e86\u3002 \u3000\u3000\u5750\u5728\u4e00\u65c1\u7684\u4f59\u4ea6\u4e50\u62c5\u5fc3\u9ea6\u5c0f\u660e\u95f9\u4e8b\uff0c\u8f7b\u8f7b\u7528\u811a\u5c16\u8e22\u4e86\u9ea6\u5c0f\u660e\u4e00\u4e0b\u9053\uff1a\u201c\u5df4\u5f53\u5bb6\u7684\u662f\u4e00\u756a \u597d\u610f\uff0c\u9ea6\u8001\u5f1f\u5343\u4e07\u522b\u8bef\u4f1a\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u7b11\u5475\u5475\u5730\u9053\uff1a\u201c\u627e\u5f53\u7136\u77e5\u9053\u4ed6\u662f\u597d\u610f\uff0e\u4f46\u6211\u90a3\u53e5\u8bdd\u4e5f\u5e76\u975e\u6076\u610f\uff0e\u4e0d\u662f\u62cd\u9a6c\u5c41\u662f\u4ec0 \u4e48\u5462\uff1f\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u9053\uff1a\u201c\u7167\u8fd9\u6837\u8bf4\uff0c\u4f60\u662f\u559c\u6b22\u542c\u522b\u4eba\u8bf4\u4f60\u7684\u574f\u8bdd\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4e0d\u9519\uff01\u4f60\u4eec\u54ea\u4e00\u4f4d\u8c01\u6765\u9a82\u6211\uff0e\u6211\u4e00\u5b9a\u8bf4\u4f60\u4eec\u597d\u3002\u5df4\u5f53\u5bb6\u7684\uff0e\u4f60\u9a82\u9a82\u6211 \u770b\u3002\u201d \u3000\u3000\u5df4\u5929\u4e49\u5e72\u54b3\u4e24\u58f0\u9053\uff1a\u201c\u9ea6\u5c0f\u5144\u5f1f\u771f\u96be\u4f3a\u5019\uff0c\u4f60\u6ca1\u9519\u5df4\u67d0\u62ff\u4ec0\u4e48\u9a82\u4f60\u3002\u201d \u3000\u3000\u5c71\u9b48\u6bdb\u8d8a\u4f3c\u4e4e\u542c\u4e0d\u8fc7\u53bb\uff0c\u51b7\u68ee\u68ee\u4e00\u7b11\u9053\uff1a\u201c\u6bdb\u67d0\u5bf9\u9ea6\u5c0f\u5144\u5f1f\u4e5f\u662f\u4f69\u670d\u81f3\u6781\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u96be\u5f97\u96be\u5f97\uff01\u6211\u9ea6\u5c0f\u660e\u5c45\u7136\u4f1a\u88ab\u6c5f\u5357\u56db\u602a\u4e2d\u7684\u8001\u5927\u4f69\u670d\u3002\u4e0d\u8fc7\u6211\u6709\u4e00 \u4e8b\u4e0d\u660e\uff0c\u4f60\u4eec\u6c5f\u5357\u56db\u602a\u90fd\u602a\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u6bdb\u8d8a\u987f\u65f6\u8bed\u6c14\u4e00\u7a92\uff0c\u6709\u4e9b\u7b54\u4e0d\u4e0a\u8bdd\uff0c\u60f3\u53d1\u4f5c\u5374\u53c8\u5fc3\u5b58\u754f\u60e7\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u5f97\u7406\u4e0d\u8ba9\u4eba\uff0c\u518d\u9053\uff1a\u201c\u6211\u611f\u5230\u5947\u602a\uff0c\u6c5f\u5357\u98ce\u5149\u660e\u5a9a\uff0c\u7269\u4ea7\u5bcc\u70e7\uff0c\u51fa\u4ec0\u4e48\u4e0d\u597d\uff0c\u4e3a \u4ec0\u4e48\u504f\u504f\u51fa\u4e86\u56db\u602a\uff1f\u201d \u3000\u3000\u5df4\u5929\u4e49\u5fd9\u63a5\u53e3\u9053\uff1a\u201c\u9ea6\u5c0f\u5144\u5f1f\u5fc5\u5b9a\u542c\u8bf4\u8fc7\u2018\u5357\u4e03\u5317\u516d\u2019\u8fd9\u53e5\u8bdd\u5427\uff0e\u8fde\u626c\u5dde\u53ea\u662f\u2014\u5e9c\u4e4b\u5730 \u90fd\u51fa\u8fc7\u6240\u8c13\u2018\u626c\u5dde\u516b\u602a\u2019\uff0c\u6c5f\u5357\u90a3\u4e48\u5927\u7684\u5730\u65b9\uff0c\u51fa\u4e86\u56db\u602a\u53c8\u6709\u4ec0\u4e48\u7a00\u5947\uff1f\u201d \u5367\u9f99\u751f\u300a\u5999\u7edd\u5929\u9999\u300b \u7b2c\u5341\u56db\u56de\u3000\u80e1\u4f5c\u975e\u4e3a \u3000\u3000\u6b63\u8bf4\u5230\u8fd9\u91cc\uff0c\u53ea\u542c\u90bb\u5ea7\u6709\u4eba\u4f4e\u58f0\u558a\u9053\uff1a\u201c\u76df\u4e3b\u8fdb\u573a\u4e86\uff01\u201d \u3000\u3000\u679c\u7136\uff0c\u8fd9\u65f6\u7f57\u6d6e\u4e00\u53df\u970d\u5143\u4f3d\u5df2\u7531\u5c4f\u98ce\u540e\u51fa\u73b0\uff0c\u5f15\u5165\u6ce8\u76ee\u7684\u662f\uff0c\u4ed6\u8eab\u65c1\u8fd8\u7d27\u8ddf\u7740\u2014\u4e2a\u5973 \u4eba\u3002 \u3000\u3000\u8fd9\u5973\u4eba\u662f\u8bb8\u5c0f\u65e6\u3002 \u3000\u3000\u8bb8\u5c0f\u65e6\u5c45\u7136\u5728\u5927\u5ead\u5e7f\u4f17\u4e4b\u4e0b\uff0c\u7adf\u628a\u624b\u642d\u5728\u970d\u5143\u4f3d\u7684\u81c2\u5f2f\u91cc\uff0c\u5728\u90a3\u79cd\u65f6\u4ee3\uff0c\u8fd9\u4e3e\u52a8\u5341\u8db3\u591f \u5f97\u4e0a\u4ee4\u4eba\u77a0\u76ee\u7ed3\u820c\u3002 \u3000\u3000\u4eca\u5929\u8bb8\u5c0f\u65e6\u6253\u626e\u5f97\u66f4\u7279\u522b\uff0c\u5934\u7ed1\u7eff\u5e26\u7ea2\u82b1\uff0c\u7a7f\u7740\u4e24\u622a\u5f0f\u7ea2\u8272\u82b1\u5e03\u88d9\uff0c\u8170\u95f4\u9732\u51fa\u4e00\u622a\u767d \u8089\uff0e\u5916\u7f69\u9ed1\u8272\u8584\u7eb1\uff0c\u5168\u8eab\u808c\u80a4\uff0c\u82e5\u9690\u82e5\u73b0\u3002 \u3000\u3000\u5927\u5385\u5185\u7acb\u5373\u6380\u8d77\u4e00\u9635\u9f13\u566a\uff0c\u4e0d\u8fc7\u56e0\u4e3a\u6709\u76df\u4e3b\u970d\u5143\u4f3d\u5728\uff0c\u53ea\u662f\u2014\u9635\u5c0f\u5c0f\u9f13\u566a\uff0c\u79e9\u5e8f\u5e76\u672a\u5927 \u4e71\u3002 \u3000\u3000\u970d\u5143\u4f3d\u7f13\u7f13\u6765\u5230\u9996\u5e2d\u6b63\u4f4d\u5750\u4e0b\uff0c\u4e00\u9762\u4ea4\u4ee3\u8bb8\u5c0f\u65e6\u5c31\u5ea7\u3002 \u3000\u3000\u8bb8\u5c0f\u65e6\u5230\u8fd9\u65f6\u624d\u677e\u5f00\u633d\u5728\u970d\u5143\u4f3d\u81c2\u5f2f\u91cc\u7684\u624b\uff0c\u9762\u542b\u5fae\u7b11\u5730\u5750\u4e86\u4e0b\u6765\uff0c\u504f\u504f\u5c31\u5750\u5728\u9ea6\u5c0f\u660e \u7684\u8eab\u65c1\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u8fde\u5fd9\u7ad9\u8d77\u6765\u62cd\u4e86\u4f59\u4ea6\u4e50\u4e00\u628a\u9053\uff1a\u201c\u54ea\u91cc\u6765\u7684\u8fd9\u4e48\u4e00\u80a1\u9a9a\u6c14\uff0c\u54b1\u4eec\u6362\u6362\u4f4d\u7f6e\u597d\u4e0d \u597d\uff1f\u201d \u3000\u3000\u4ed6\u4e5f\u4e0d\u7ba1\u5bf9\u65b9\u662f\u5426\u540c\u610f\uff0c\u62c9\u8fc7\u4f59\u4ea6\u4e50\u4fbf\u6362\u4e86\u4f4d\u7f6e\u3002 \u3000\u3000\u53ea\u89c1\u970d\u5143\u4f3d\u91cd\u53c8\u7ad9\u8d77\u8eab\u6765\uff0c\u9ad8\u58f0\u9053\uff1a\u201c\u4e3a\u4e86\u589e\u52a0\u4eca\u5929\u7684\u5bb4\u4f1a\u6c14\u6c1b\uff0c\u672c\u76df\u4e3b\u7279\u5730\u628a\u8bb8\u4ed9\u5b50 \u8bf7\u6765\u4e0e\u5927\u5bb6\u540c\u4e50\uff0e \u3000\u3000\u8bb8\u4ed9\u5b50\u9152\u91cf\u6781\u5927\uff0c\u5982\u8bf8\u4f4d\u6709\u5174\uff0c\u5f85\u4f1a\u513f\u8bf7\u8e0a\u8dc3\u7ed9\u5979\u656c\u9152\uff0e\u5979\u662f\u6765\u8005\u4e0d\u62d2\uff0e\u201d \u3000\u3000\u8fd9\u4e00\u6765\u7fa4\u96c4\u4eec\u591a\u6570\u90fd\u5fc3\u4e0b\u5927\u4e50\uff0c\u4ed6\u4eec\u6b63\u60f3\u627e\u673a\u4f1a\u4e0e\u8bb8\u5c0f\u65e6\u4eb2\u8fd1\uff0c\u53ea\u56e0\u9152\u5bb4\u5c1a\u672a\u5f00\u59cb\uff0c\u4e0d \u4fbf\u9a6c\u4e0a\u884c\u52a8\u3002 \u3000\u3000\u970d\u5143\u4f3d\u63a5\u7740\u5ba3\u5e03\u9152\u5bb4\u5f00\u59cb\uff0e\u5148\u662f\u540c\u5e2d\u7684\u5411\u8bb8\u5c0f\u65e6\u656c\u9152\uff0c\u5cad\u5357\u4e8c\u5947\u548c\u5c71\u9b48\u6bdb\u8d8a\u90fd\u656c\u4e86\uff0c\u949f \u4e00\u8c6a\u548c\u4f59\u4ea6\u4e50\u4e5f\u4e0d\u5f97\u4e0d\u5165\u4e61\u968f\u4fd7\u3002 \u3000\u3000\u552f\u6709\u9ea6\u5c0f\u660e\uff0c\u786c\u662f\u4e0d\u7406\u4f1a\u3002 \u3000\u3000\u9152\u8fc7\u4e09\u5de1\uff0c\u7fa4\u96c4\u4eec\u4fbf\u7531\u5404\u684c\u8702\u6d8c\u800c\u6765\uff0c\u4e89\u7740\u5411\u8bb8\u5c0f\u65e6\u656c\u9152\uff0c\u6709\u7684\u8fd8\u8d81\u673a\u201c\u63e9\u6cb9\u201d\uff0e \u3000\u3000\u8bb8\u5c0f\u65e6\u59cb\u7ec8\u9762\u542b\u5a9a\u7b11\uff0c\u679c\u7136\u6765\u8005\u4e0d\u62d2\u3002 \u3000\u3000\u5927\u5385\u5185\u9a9a\u4e71\u4e86\u597d\u4e00\u9635\uff0c\u624d\u9759\u6b62\u4e0b\u6765\u3002 \u3000\u3000\u970d\u5143\u4f3d\u89c1\u7fa4\u96c4\u4eec\u90fd\u5df2\u5f52\u5ea7\uff0c\u5ffd\u7136\u9ad8\u58f0\u9053\uff1a\u201c\u8bb8\u4ed9\u5b50\u65b9\u624d\u5df2\u7ecf\u8868\u793a\u8fc7\uff0c\u4e3a\u4e86\u6ee1\u8db3\u5927\u5bb6\u7684\u9152 \u5174\uff0c\u5979\u613f\u610f\u5f53\u573a\u8868\u6f14\u4e00\u4e0b\uff01\u201d \u3000\u3000\u5927\u5385\u5185\u7acb\u65f6\u54cd\u8d77\u2014\u7247\u53eb\u597d\u4e4b\u58f0\u3002 \u3000\u3000\u970d\u5143\u4f3d\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u8bb8\u4ed9\u5b50\u7684\u8868\u6f14\u5f88\u7b80\u5355\uff0c\u4e0d\u8fc7\u5bf9\u5404\u4f4d\u6765\u8bf4\uff0c\u5374\u662f\u5f00\u4e86\u4e00\u6b21\u773c\u754c\u3002\u201d \u3000\u3000\u7fa4\u96c4\u4eec\u8c01\u4e5f\u5f04\u4e0d\u6e05\u8bb8\u5c0f\u65e6\u8981\u8868\u6f14\u4ec0\u4e48\uff0c\u4f46\u6240\u6709\u7684\u76ee\u5149\uff0c\u5374\u4e0d\u7ea6\u800c\u540c\u9f50\u9f50\u6295\u5c04\u8fc7\u6765\u3002 \u3000\u3000\u5374\u89c1\u8bb8\u5c0f\u65e6\u7f13\u7f13\u7ad9\u4e86\u8d77\u6765\u9053\uff1a\u201c\u6211\u8fd9\u4eba\u6700\u80af\u4e0e\u4eba\u65b9\u4fbf\uff0c\u6211\u77e5\u9053\u4f60\u4eec\u60f3\u770b\u7684\u662f\u4ec0\u4e48\uff0c\u73b0\u5728 \u5c31\u7ed9\u4eba\u5bb6\u770b\u3002\u201d \u3000\u3000\u8bf4\u7740\uff0c\u62ac\u624b\u5f80\u80f8\u8863\u4e0a\u4e00\u626f\uff0c\u7adf\u7136\u628a\u5de6\u4e73\u623f\u9732\u4e86\u51fa\u6765\uff0e \u3000\u3000\u5979\u4e3a\u4e86\u56db\u9762\u5747\u80fd\u7167\u987e\u5230\uff0c\u8fd8\u7279\u5730\u628a\u8eab\u5b50\u8f6c\u5708\uff0c\u5e76\u5728\u56db\u4e2a\u65b9\u5411\u5404\u505c\u987f\u4e86\u4e00\u4e0b\uff0c\u4ee5\u4fbf\u8ba9\u6bcf\u4e00 \u65b9\u5411\u7684\u4eba\u90fd\u80fd\u770b\u5f97\u4ed4\u7ec6\uff0c\u5e73\u5747\u5206\u914d\uff0c\u8c01\u4e5f\u4e0d\u5403\u4e8f\u3002 \u3000\u3000\u5ffd\u542c\u6709\u4eba\u53eb\u9053\uff1a\u201c\u8fd8\u6709\u4e00\u8fb9\u3002\u201d \u3000\u3000\u53e6\u4e00\u4eba\u53eb\u9053\uff1a\u201c\u5979\u82e5\u4e0d\u9732\u51fa\u90a3\u4e00\u8fb9\u7ed9\u5927\u5bb6\u770b\uff0c\u5c31\u8868\u793a\u90a3\u4e00\u8fb9\u6709\u6bdb\u75c5\uff01\u201d \u3000\u3000\u8bb8\u5c0f\u65e6\u2014\u6765\u5c31\u4e0d\u5728\u4e4e\uff0c\u4e8c\u6765\u53c8\u89c9\u5f97\u5982\u679c\u4e0d\u9732\u51fa\u53e6\u5916\u4e00\u8fb9\uff0c\u7fa4\u96c4\u5fc5\u5b9a\u4ee5\u4e3a\u5979\u5f53\u771f\u6709\u6bdb\u75c5\uff0c \u4e3a\u4e86\u663e\u793a\u672c\u94b1\u5145\u8db3\uff0c\u6beb\u4e0d\u72b9\u8c6b\u7684\u4fbf\u628a\u53e6\u4e00\u8fb9\u7684\u80f8\u8863\u62c9\u5f00\uff0c\u9732\u51fa\u5979\u90a3\u53f3\u8fb9\u7684\u201c\u5355\u67a3\u9992\u5934\u201d\u3002 \u3000\u3000\u7fa4\u96c4\u4eec\u987f\u65f6\u6709\u7684\u9f13\u8d77\u638c\u6765\uff0c\u6709\u7684\u8f6c\u8fc7\u5934\u4e0d\u5fcd\u5352\u7779\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u53c8\u6709\u4eba\u53eb\u9053\uff1a\u201c\u9694\u5f97\u592a\u8fdc\u5566\uff0c\u8bb8\u4ed9\u5b50\u6700\u597d\u8bf7\u5230\u6bcf\u4e00\u684c\u524d\u6643\u4e00\u6643\uff01\u201d \u3000\u3000\u8c01\u77e5\uff0c\u8bb8\u5c0f\u65e6\u7adf\u7136\u5f53\u771f\u7b54\u5e94\u4e86\u8981\u6c42\uff0e\u5f53\u573a\u79bb\u5f00\u5ea7\u4f4d\uff0c\u626d\u8170\u6446\u81c0\u5728\u5404\u684c\u7fa4\u96c4\u8ddf\u524d\u5c55\u5f00\u201c\u732e \u5b9d\u201d\u6d3b\u52a8\u3002 \u3000\u3000\u5979\u6bcf\u5230\u4e00\u684c\uff0c\u8fd8\u6545\u610f\u628a\u80f8\u90e8\u6643\u52a8\u4e00\u4e0b\uff0c\u5e26\u52a8\u5f97\u4e24\u4e73\u50cf\u8d27\u90ce\u9f13\u822c\u6447\u52a8\u3002 \u3000\u3000\u6b64\u523b\u5927\u5385\u5185\u7684\u79e9\u5e8f\u66f4\u4e3a\u6df7\u4e71\uff0c\u4f46\u5374\u628a\u6c14\u6c1b\u5e26\u52a8\u5f97\u8fdb\u5165\u6700\u9ad8\u6f6e\uff0c\u4e0d\u5c11\u4eba\u90fd\u5982\u75f4\u5982\u72c2\uff0c\u4e11\u6001 \u6bd5\u73b0\u3002 \u3000\u3000\u6709\u4e0d\u5c11\u4eba\u5728\u60c5\u4e0d\u81ea\u7981\u4e4b\u4e0b\uff0c\u7adf\u7136\u63a2\u624b\u53bb\u6478\uff0c\u4f46\u8bb8\u5c0f\u65e6\u5374\u4e0d\u6beb\u4e0d\u5728\u610f\uff0c\u4e0d\u4f46\u4e0d\u607c\uff0c\u53cd\u800c\u7b11 \u5f97\u66f4\u9a9a\u3002 \u3000\u3000\u597d\u4e00\u9635\u5de5\u592b\uff0c\u8bb8\u5c0f\u65e6\u624d\u56de\u5230\u539f\u5ea7\uff0e \u3000\u3000\u53ea\u542c\u970d\u5143\u4f3d\u95ee\u9053\uff1a\u201c\u5404\u4f4d\u90fd\u770b\u6e05\u695a\u4e86\u6ca1\u6709\uff1f\u201d \u3000\u3000\u987f\u65f6\u6709\u4e0d\u5c11\u4eba\u5e94\u9053\uff1a\u201c\u770b\u6e05\u695a\u5566\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u5176\u5b9e\u8bb8\u4ed9\u5b50\u90a3\u4e24\u4e2a\u4e1c\u897f\uff0c\u957f\u5f97\u548c\u522b\u7684\u5973\u4eba\u4e5f\u6ca1\u6709\u4ec0\u4e48\u4e24\u4f6f\uff0c\u5728\u573a\u5404\u4f4d\uff0c\u5373 \u4f7f\u6ca1\u770b\u8fc7\u5973\u4eba\u5976\u5b50\uff0c\u5c0f\u65f6\u5019\u4e5f\u5403\u8fc7\u5976\uff0c\u5b9e\u5728\u6ca1\u4ec0\u4e48\u5e0c\u5947\u7684\uff0e\u6240\u4ee5\u4eca\u5929\u7684\u4e8b\uff0c\u6839\u672c\u4e0d\u503c\u5f97\u5927\u60ca \u5c0f\u602a\u3002\u201d \u3000\u3000\u7fa4\u96c4\u4e2d\u4e00\u4eba\u53eb\u9053\uff1a\u201c\u5c5e\u4e0b\u4ece\u524d\u770b\u5973\u4eba\u5976\u5b50\uff0e\u53ea\u662f\u5728\u623f\u91cc\u5173\u8d77\u95e8\u6765\u4e00\u4e2a\u4eba\u770b\uff0c\u50cf\u8bb8\u4ed9\u5b50\u8fd9 \u6837\u5f53\u4f17\u9732\u80f8\u7684\uff0c\u5374\u8c01\u90fd\u6ca1\u89c1\u8fc7\uff0c\u5f53\u7136\u89c9\u5f97\u5e0c\u5947\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u7b11\u9053\uff1a\u201c\u8fd9\u662f\u65f6\u4ee3\u7684\u5173\u7cfb\uff0c\u65f6\u4ee3\u4e00\u5929\u5929\u6f14\u8fdb\uff0c\u4eba\u4e5f\u2014\u5929\u5929\u4e0d\u540c\u3002\u82e5\u518d\u8fc7\u51e0\u767e\u5e74\uff0c \u8bb8\u4ed9\u5b50\u5c31\u66f4\u4e0d\u5728\u4e4e\u4e86\uff0e\u201d \u3000\u3000\u201c\u53ef\u662f\u8bb8\u4ed9\u5b50\u4e0d\u53ef\u80fd\u518d\u6d3b\u51e0\u767e\u5e74\uff0e\u201d \u3000\u3000\u201c\u4f60\u600e\u77e5\u4e0d\u80fd\uff1f\u5979\u65e2\u7136\u662f\u4ed9\u5b50\uff0c\u90a3\u5c31\u6c38\u8fdc\u4e0d\u4f1a\u6b7b\u3002\u201d \u3000\u3000\u90a3\u4eba\u521a\u8981\u518d\u5f00\u53e3\uff0c\u970d\u5143\u4f3d\u5ffd\u7136\u6574\u4e86\u6574\u8138\u8272\uff0c\u6b63\u7ecf\u516b\u767e\u7684\u95ee\u9053\uff1a\u201c\u521a\u624d\u6709\u54ea\u51e0\u4f4d\u6478\u8fc7\u8bb8\u4ed9 \u5b50\u7684\u4e73\u623f\uff0c\u6700\u597d\u81ea\u52a8\u7ad9\u51fa\u6765\uff01\u201d \u3000\u3000\u4ed6\u8fde\u95ee\u4e86\u597d\u51e0\u904d\uff0c\u7fa4\u96c4\u4e2d\u7adf\u6ca1\u4e00\u4eba\u7ad9\u51fa\u6765\uff0c\u53ef\u89c1\u4ed6\u4eec\u7684\u9762\u76ae\uff0c\u8fd8\u662f\u8981\u6bd4\u8bb8\u5c0f\u65e6\u8584\u4e9b\uff0e \u3000\u3000\u970d\u5143\u4f3d\u4e0d\u52a8\u58f0\u8272\u9053\uff1a\u201c\u8c01\u7ad9\u51fa\u6765\u5c31\u8868\u793a\u8c01\u8bda\u5b9e\uff0e\u8bda\u5b9e\u7684\u4eba\u4e00\u5b9a\u4f1a\u5f97\u5230\u597d\u5904\u3002\u4e0d\u7ad9\u51fa\u6765\u5c31 \u662f\u4e0d\u8bda\u5b9e\uff0c\u4e0d\u8bda\u5b9e\u7684\u4eba\u4e0d\u4f46\u8981\u5403\u4e8f\uff0e\u800c\u4e14\u66f4\u8981\u5012\u5927\u9709\uff01\u201d \u3000\u3000\u7fa4\u96c4\u4e2d\u7acb\u5373\u6709\u4eba\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u76df\u4e3b\u8bf7\u628a\u8fd9\u8bdd\u5bf9\u5c5e\u4e0b\u4eec\u8bf4\u660e\u767d\u4e9b\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u7b11\u9053\uff1a\u201c\u90a3\u665a\u6bd2\u706b\u6210\u5168\u7b49\u56db\u4eba\u56e0\u4e3a\u6478\u8bb8\u4ed9\u5b50\u800c\u4e2d\u6bd2\u7684\u4e8b\u4ef6\uff0c\u4f60\u4eec\u603b\u8be5\u8fd8\u8bb0\u5f97 \u5427\uff1f\u201d \u3000\u3000\u90a3\u4e9b\u6478\u8fc7\u8bb8\u5c0f\u65e6\u7684\u4eba\uff0e\u95fb\u8a00\u7acb\u5373\u8138\u8272\u5927\u53d8\uff0c\u5176\u4e2d\u4e00\u4eba\u9053\uff1a\u201c\u76df\u4e3b\uff0c\u83ab\u975e\u5c5e\u4e0b\u4e5f\u4e2d\u4e86 \u6bd2\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u9894\u9996\u9053\uff1a\u201c\u4e0d\u9519\uff01\u4f60\u4eec\u660e\u767d\u4e86\u5c31\u597d\u3002\u201d \u3000\u3000\u5148\u524d\u90a3\u4eba\u6296\u7740\u58f0\u97f3\u53eb\u9053\uff1a\u201c\u76df\u4e3b\uff0c\u4f60\u4e0d\u8be5\u8fd9\u6837\u505a\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u54fc\u4e86\u58f0\u9053\uff1a\u201c\u6211\u4e3a\u4ec0\u4e48\u4e0d\u8be5\uff1f\u201d \u3000\u3000\u90a3\u4eba\u9053\uff1a\u201c\u6bd2\u706b\u6210\u5168\u4ed6\u4eec\u56db\u4e2a\u662f\u8ddf\u54b1\u4eec\u505a\u5bf9\u7684\uff0c\u8ba9\u4ed6\u4eec\u4e2d\u6bd2\u81ea\u7136\u5e94\u8be5\u3002\u4f46\u5c5e\u4e0b\u90fd\u662f\u60a8\u7684 \u5fc3\u8179\u90e8\u5c5e\uff0c\u60a8\u4e0d\u8be5\u8fde\u81ea\u5df1\u4eba\u4e5f\u4e0b\u6bd2\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u8138\u8272\u4e00\u6c89\u9053\uff1a\u201c\u6211\u8ba9\u8bb8\u4ed9\u5b50\u9732\u51fa\u8eab\u4e0a\u5230\u5404\u684c\uff0e\u53ea\u662f\u7ed9\u4f60\u4eec\u770b\u7684\uff0c\u5e76\u6ca1\u58f0\u660e\u53ef\u4ee5 \u6478\uff0c\u4f60\u4eec\u81ea\u5df1\u592a\u8fc7\u653e\u8086\uff0e\u8fd8\u80fd\u6028\u8c01\uff1f\u201d \u3000\u3000\u90a3\u4eba\u987f\u4e86\u987f\u9053\uff1a\u201c\u53ef\u662f\u5c5e\u4e0b\u4eec\u5df2\u7ecf\u6478\u4e86\uff0c\u4f60\u8001\u4eba\u5bb6\u603b\u8be5\u7ed9\u6211\u4eec\u4e00\u4e2a\u6539\u8fc7\u81ea\u65b0\u7684\u673a\u4f1a\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u8001\u592b\u5df2\u7ecf\u7ed9\u4e86\u4f60\u4eec\u673a\u4f1a\u3002\u201d \u3000\u3000\u201c\u76df\u4e3b\u8fd9\u8bdd\uff1f\u201d \u3000\u3000\u201c\u8001\u592b\u65b9\u624d\u95ee\u4f60\u4eec\u54ea\u4e2a\u6478\u4e86\uff0c\u5c31\u662f\u51c6\u5907\u7ed9\u4f60\u4eec\u89e3\u836f\uff0c\u504f\u504f\u4f60\u4eec\u8c01\u90fd\u4e0d\u80af\u627f\u8ba4\uff0c\u8fd9\u8ba9\u8001\u592b \u628a\u89e3\u836f\u7ed9\u8c01\u5462\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u7684\u8bdd\u521a\u521a\u8bf4\u5b8c\uff0c\u5927\u5385\u5185\u4fbf\u662f\u4e00\u9635\u684c\u6905\u54cd\u52a8\uff0c\u63a5\u7740\u8db3\u6709\u516b\u4e5d\u4e2a\u4eba\u9f50\u9f50\u6324\u5230\u970d\u5143\u4f3d\u8eab \u524d\uff0c\u4e0d\u7ea6\u800c\u540c\u4f4e\u5934\u8dea\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u970d\u5143\u4f3d\u626b\u4e86\u8fd9\u4e9b\u4eba\u4e00\u773c\u9053\uff1a\u201c\u4f60\u4eec\u90fd\u662f\u6478\u8fc7\u8bb8\u4ed9\u5b50\u7684\uff1f\u201d \u3000\u3000\u4f17\u4eba\u5168\u70b9\u4e86\u70b9\u5934\uff0e \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u65b9\u624d\u4e3a\u4ec0\u4e48\u4e0d\u80af\u7ad9\u51fa\u6765\u5462\uff1f\u201d \u3000\u3000\u4f17\u4eba\u7684\u5934\u5782\u5f97\u66f4\u4f4e\uff0c\u4ecd\u6ca1\u4e00\u4eba\u542d\u58f0\u3002 \u3000\u3000\u970d\u5143\u4f3d\u51b7\u7b11\u9053\uff1a\u201c\u54b1\u4eec\u7eff\u6797\u4e2d\u4eba\uff0c\u6700\u91cd\u8981\u7684\u5c31\u662f\u8bda\u5b9e\uff0c\u5c24\u5176\u8eab\u5728\u8ff7\u8e2a\u8c37\u7eff\u6797\u603b\u5be8\uff0c\u4e0d\u8bda \u5b9e\u5982\u4f55\u80fd\u505a\u5929\u4e0b\u7eff\u6797\u8868\u7387\uff1f\u201d \u3000\u3000\u4f17\u4eba\u9f50\u58f0\u9053\uff1a\u201c\u5c5e\u4e0b\u4eec\u77e5\u7f6a\u4e86\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u8fd9\u624d\u62db\u624b\u865a\u7a7a\u4e00\u6258\u9053\uff1a\u201c\u8d77\u6765\uff01\u81ea\u5bb6\u5144\u5f1f\uff0c\u4f55\u5fc5\u5982\u6b64\uff1f\u201d \u3000\u3000\u5148\u524d\u8bf4\u8bdd\u90a3\u4eba\u731b\u58ee\u80c6\u5b50\u9053\uff1a\u201c\u53ef\u662f\u76df\u4e3b\u8fd8\u6ca1\u8d50\u5c5e\u4e0b\u4eec\u89e3\u836f\uff01\u201d \u3000\u3000\u970d\u5143\u5496\u563f\u563f\u7b11\u4e86\u4e00\u9635\uff1a\u201c\u5b9e\u4e0d\u76f8\u7792\uff0c\u4f60\u4eec\u5e76\u6ca1\u4e2d\u6bd2\uff0e\u201d \u3000\u3000\u90a3\u4eba\u6123\u4e86\u6123\u9053\uff1a\u201c\u90a3\u4e48\u76df\u4e3b\u2026\u2026\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u7b11\u9053\uff1a\u201c\u81ea\u5bb6\u5144\u5f1f\uff0c\u8001\u592b\u600e\u80af\u5bf9\u4f60\u4eec\u4e0b\u6bd2\uff0c\u6211\u521a\u624d\u90a3\u6837\u505a\uff0e\u76ee\u7684\u4e0d\u5916\u662f\u60f3\u8bd5\u8bd5\u5404 \u4f4d\u662f\u5426\u8bda\u5b9e\uff0c\u4eba\u5fc5\u6709\u8bda\uff0c\u7136\u540e\u624d\u80fd\u8868\u73b0\u5fe0\u5fc3\uff0e\u201d \u3000\u3000\u4f17\u4eba\u6253\u4e86\u4e2a\u54c6\u55e6\uff0c\u9f50\u58f0\u9053\uff1a\u201c\u5c5e\u4e0b\u4eec\u8be5\u6b7b\uff0c\u4eca\u540e\u5bf9\u76df\u4e3b\u5982\u6709\u4e0d\u5fe0\uff0c\u5373\u4f7f\u6b7b\u540e\u505a\u9b3c\uff0c\u4e5f\u8981 \u788e\u5c38\u4e07\u6bb5\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u5475\u5475\u7b11\u9053\uff1a\u201c\u5404\u4f4d\u8a00\u91cd\u4e86\uff0e\u4f60\u4eec\u65b9\u624d\u5df2\u7ecf\u8868\u73b0\u5f88\u8bda\u5b9e\uff0c\u8001\u592b\u5f53\u7136\u76f8\u4fe1\u5c06\u6765\u5fc5\u5b9a\u5fe0 \u5fc3\u4e0d\u4e8c\uff0c\u7528\u4e0d\u7740\u7ad9\u5728\u8fd9\u91cc\uff0e\u5feb\u56de\u5ea7\u5427\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u800d\u51fa\u8fd9\u4e00\u62db\u624b\u6bb5\uff0c\u5bf9\u5927\u591a\u6570\u5728\u573a\u4eba\u6765\u8bf4\uff0c\u56fa\u7136\u6536\u5230\u4e86\u9884\u671f\u6548\u679c\uff0c\u4e5f\u5de9\u56fa\u4e86\u4ed6\u7684 \u201c\u9886\u5bfc\u4e2d\u5fc3\u201d\u3002 \u3000\u3000\u4f46\u5374\u66f4\u6fc0\u8d77\u4ee5\u949f\u4e00\u8c6a\u4e3a\u9996\u7684\u4e00\u4f19\u4eba\u7684\u53cd\u611f\uff0c\u970d\u5143\u4f3d\u5982\u679c\u662f\u806a\u660e\u4eba\uff0e\u5bf9\u8fd9\u4e00\u62db\u624b\u6bb5\u7684\u540e\u9057 \u75c7\uff0e\u5fc5\u5b9a\u8be5\u5fc3\u91cc\u6709\u6570\uff0e \u3000\u3000\u63a5\u7740\u7fa4\u8c6a\u53c8\u7ee7\u7eed\u996e\u5bb4\u8d77\u6765\u3002 \u3000\u3000\u8bb8\u5c0f\u65e6\u4e3a\u4e86\u5bf9\u9996\u5e2d\u6240\u6709\u7684\u4eba\u8ba8\u201c\u597d\u201d\uff0c\u90a3\u4e00\u5bf9\u4e73\u623f\uff0c\u4f9d\u7136\u66b4\u9732\u5728\u5916\uff0c\u903c\u5f97\u949f\u4e00\u8c6a\u3001\u4f59\u4ea6 \u4e50\u3001\u9ea6\u5c0f\u660e\u4e09\u4eba\u90fd\u53ea\u597d\u6781\u529b\u907f\u5f00\u89c6\u7ebf\uff0c\u4ee5\u514d\u773c\u775b\u53d7\u5230\u201c\u6c61\u67d3\u201d\u3002 \u3000\u3000\u970d\u5143\u4f3d\u7b11\u9053\uff1a\u201c\u8bb8\u4ed9\u5b50\uff0c\u4f60\u90a3\u4e00\u5bf9\u4e1c\u897f\u597d\u50cf\u4e5f\u6ca1\u4ec0\u4e48\u7279\u522b\uff0c\u4e3a\u4ec0\u4e48\u8001\u559c\u6b22\u9732\u5728\u5916\u9762\uff1f\u201d \u3000\u3000\u8bb8\u5c0f\u65e6\u9053\uff1a\u201c\u76df\u4e3b\u867d\u7136\u4e0d\u559c\u6b22\u770b\uff0c\u53ef\u662f\u6709\u4eba\u559c\u6b22\u770b\uff0c\u65e2\u7136\u4ed6\u4eec\u60f3\u770b\uff0c\u5c31\u7ed9\u4ed6\u4eec\u770b\u4e2a \u591f\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u70b9\u5934\u9053\uff1a\u201c\u679c\u7136\u662f\u83e9\u8428\u5fc3\u80a0\uff0c\u529f\u5fb7\u65e0\u91cf\uff0c\u5148\u524d\u8001\u592b\u5bf9\u4f60\u8981\u6c42\u7684\u4e8b\u60c5\uff0c\u4f60\u7a76\u7adf\u80af\u4e0d \u80af\u7b54\u5e94\uff1f\u201d \u3000\u3000\u8bb8\u5c0f\u65e6\u9887\u4e3a\u723d\u5feb\u7684\u9053\uff1a\u201c\u76df\u4e3b\u8981\u6c42\u7684\u4e8b\u60c5\uff0c\u6211\u4ec0\u4e48\u65f6\u5019\u6ca1\u6709\u7b54\u5e94\u8fc7\uff1f\u4e3a\u4e86\u7b54\u8c22\u8ff7\u8e2a\u8c37\u82f1 \u96c4\u597d\u6c49\u4eec\u7684\u597d\u610f\uff0e\u5373\u4f7f\u4f60\u8001\u4eba\u5bb6\u4e0d\u8981\u6c42\uff0c\u6211\u4e5f\u4f1a\u81ea\u544a\u594b\u52c7\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u563f\u563f\u7b11\u9053\uff1a\u201c\u65e2\u7136\u4ed9\u5b50\u80af\u5e03\u65bd\u96e8\u9732\uff0c\u8001\u592b\u5c31\u8981\u5f53\u573a\u5ba3\u5e03\u4e86\uff0c\u4e5f\u597d\u7ed9\u4ed6\u4eec\u4e00\u4e2a\u610f\u5916 \u60ca\u559c\u3002\u201d \u3000\u3000\u8bb8\u5c0f\u65e6\u6beb\u4e0d\u5728\u610f\u9053\uff1a\u201c\u76df\u4e3b\u5c31\u8bf7\u5ba3\u5e03\u597d\u5566\uff01\u201d \u3000\u3000\u4e24\u4eba\u8fd9\u756a\u5bf9\u8bdd\u58f0\u97f3\u5f88\u4f4e\uff0c\u53ea\u6709\u540c\u684c\u7684\u5176\u4ed6\u516d\u4eba\u542c\u5230\u3002 \u3000\u3000\u4f46\u8fd9\u516d\u4eba\u5374\u4e0d\u6e05\u695a\u8fd9\u4e00\u7537\u4e00\u5973\u53c8\u8981\u73a9\u4ec0\u4e48\u82b1\u6837\u3002 \u3000\u3000\u53ea\u89c1\u970d\u5143\u4f3d\u559c\u6c14\u6ee1\u9762\u7684\u7ad9\u8d77\u8eab\u6765\u9053\uff1a\u201c\u73b0\u5728\u8001\u592b\u8981\u5bf9\u5927\u5bb6\u5ba3\u5e03\u4e00\u4ef6\u610f\u5916\u7684\u597d\u6d88\u606f\u2026\u2026\u201d \u4ed6\u8bf4\u5230\u8fd9\u91cc\uff0c\u6545\u610f\u628a\u4e0b\u9762\u7684\u8bdd\u6253\u4f4f\uff0c\u5206\u660e\u662f\u8981\u540a\u4e00\u4e0b\u6240\u6709\u4eba\u7684\u80c3\u53e3\u3002 \u3000\u3000\u679c\u7136\uff0c\u7fa4\u96c4\u90fd\u7ad6\u8d77\u8033\u6735\uff0c\u6025\u76fc\u7740\u542c\u542c\u970d\u5143\u4f3d\u4e0b\u9762\u8981\u8bf4\u4ec0\u4e48\u3002 \u3000\u3000\u970d\u5143\u4f3d\u7b49\u628a\u6240\u6709\u7684\u4eba\u80c3\u53e3\u540a\u8db3\uff0c\u624d\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u5404\u4f4d\u79bb\u4e61\u80cc\u4e95\uff0c\u522b\u7236\u6bcd\u3001\u629b\u59bb\u5b50\u6765\u5230\u8ff7 \u8e2a\u8c37\uff0c\u867d\u7136\u662f\u80f8\u6000\u5927\u5fd7\u60f3\u5728\u7eff\u6797\u4e2d\u5efa\u7acb\u4e00\u756a\u4e8b\u4e1a\uff0c\u4f46\u5728\u4e07\u5c71\u4e1b\u4e2d\u7684\u8ff7\u8e2a\u8c37\uff0c\u751f\u6d3b\u4e0a\u603b\u662f\u5341\u5206 \u5bc2\u5bde\u3002 \u3000\u3000\u8fd9\u662f\u8c01\u90fd\u4e0d\u80fd\u4e0d\u627f\u8ba4\u7684\u4e8b\u5b9e\uff0c\u6240\u4ee5\uff0c\u8001\u592b\u5728\u63a5\u4efb\u76df\u4e3b\u4e4b\u540e\uff0c\u4e00\u76f4\u5e0c\u671b\u80fd\u5728\u8fd9\u65b9\u9762\u7ed9\u5927\u5bb6 \u8c03\u5242\u8c03\u5242\u3002\u201d \u3000\u3000\u53ea\u542c\u90bb\u684c\u7684\u738b\u5927\u5eb7\u9053\uff1a\u201c\u970d\u76df\u4e3b\u53c8\u4e0d\u662f\u6cbb\u75c5\u7684\u5927\u592b\uff0e\u4f60\u51ed\u4ec0\u4e48\u7ed9\u4eba\u5bb6\u8c03\u5242\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u738b\u8001\u5f1f\u8fd9\u8bdd\u95ee\u5f97\u597d\uff0c\u8001\u592b\u65e2\u4e0d\u662f\u5927\u592b\uff0c\u53c8\u4e0d\u662f\u5973\u4eba\uff0c\u5f53\u7136\u672c\u8eab\u4e0d\u80fd\u7ed9\u5927\u5bb6 \u8c03\u5242\uff0c\u4f46\u6211\u53ef\u4ee5\u60f3\u529e\u6cd5\u3002\u201d \u3000\u3000\u201c\u4f60\u6709\u4ec0\u4e48\u529e\u6cd5\u5c31\u5feb\u8bf4\u51fa\u6765\uff0c\u4f55\u5fc5\u5356\u5173\u5b50\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u6307\u4e86\u6307\u8bb8\u5c0f\u65e6\u9053\uff1a\u201c\u5979\u662f\u4e2a\u5973\u4eba\uff0c\u529e\u6cd5\u5c31\u5728\u5979\u8eab\u4e0a\u3002\u201d \u3000\u3000\u738b\u5927\u5eb7\u54fc\u4e86\u58f0\u9053\uff1a\u201c\u4f60\u60f3\u62ff\u5973\u4eba\u6765\u6536\u5356\u4eba\u5fc3\uff01\u7b97\u5566\uff01\u4e0d\u8bf4\u4e5f\u7f62\u3002\u201d \u3000\u3000\u5ffd\u542c\u4e00\u540d\u53eb\u94c1\u5934\u6731\u6b63\u7684\u5934\u76ee\u9053\uff1a\u201c\u76df\u4e3b\u522b\u542c\u738b\u5927\u5eb7\u7684\uff0c\u8981\u8bf4\u5c31\u5feb\u8bf4\u51fa\u6765\u5427\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u597d\u5427\uff01\u8001\u592b\u73b0\u5728\u5c31\u90d1\u91cd\u5ba3\u5e03\uff0c\u8bb8\u4ed9\u5b50\u4e3a\u4e86\u6170\u52b3\u5404\u4f4d\u82f1\u96c4\u597d\u6c49\uff0c\u60c5\u613f\u6c38\u8fdc\u7559 \u5728\u8ff7\u8e2a\u8c37\uff0c\u966a\u5927\u5bb6\u7761\u89c9\uff01\u201d \u3000\u3000\u6b64\u8bed\u4e00\u51fa\uff0e\u4e0d\u5c11\u4eba\u90fd\u51e0\u4e4e\u4e0d\u6562\u76f8\u4fe1\u81ea\u5df1\u7684\u8033\u6735\uff0c\u8fd8\u4ee5\u4e3a\u970d\u5143\u4f3d\u5728\u5f00\u73a9\u7b11\uff0c\u4e0d\u8fc7\u8eab\u4e3a\u7eff\u6797 \u76df\u4e3b\uff0c\u8fd9\u73a9\u7b11\u672a\u514d\u5f00\u5f97\u592a\u5927\u4e86\u3002 \u3000\u3000\u679c\u7136\uff0c\u94c1\u5934\u6731\u6b63\u62a2\u7740\u9053\uff1a\u201c\u76df\u4e3b\u4e0d\u662f\u5f00\u73a9\u7b11\u5427\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u6574\u4e86\u6574\u8138\u8272\u9053\uff1a\u201c\u6211\u5373\u7136\u662f\u76df\u4e3b\uff0c\u53c8\u600e\u80fd\u5f53\u7740\u8fd9\u4e48\u591a\u4eba\u968f\u4fbf\u4e69\u8bb2\u8bdd\u3002\u5982\u679c\u4f60\u4eec\u4e0d \u4fe1\uff0c\u6211\u5c31\u8bf7\u5f53\u4e8b\u4eba\u8868\u660e\u6001\u5ea6\u3002\u201d \u3000\u3000\u8bb8\u5c0f\u65e6\u968f\u5373\u6643\u52a8\u7740\u53cc\u4e73\u7ad9\u8d77\u6765\u9053\uff1a\u201c\u8bf8\u4f4d\u82f1\u96c4\u597d\u6c49\u4e0d\u5fc5\u6000\u7591\uff0c\u8fd9\u662f\u6211\u81ea\u613f\uff0e\u201d \u3000\u3000\u94c1\u5934\u6731\u6b63\u9053\uff1a\u201c\u4ed9\u5b50\u4e3a\u4ec0\u4e48\u80af\u505a\u8fd9\u4e48\u5927\u7684\u727a\u7272\uff1f\u201d \u3000\u3000\u8bb8\u5c0f\u65e6\u7b11\u9053\uff1a\u201c\u8fd9\u4e0d\u662f\u727a\u7272\uff0c\u5e94\u5f53\u662f\u4e00\u79cd\u6536\u83b7\u3002\u8bf8\u4f4d\u90fd\u662f\u5404\u5730\u6765\u7684\u82f1\u96c4\u597d\u6c49\uff0c\u672c\u4ed9\u5b50\u80fd \u6709\u673a\u4f1a\u6170\u52b3\u4f60\u4eec\uff1f\u4eb2\u8fd1\u8fd9\u4e48\u591a\u7684\u82f1\u96c4\u597d\u6c49\uff0c\u6b63\u662f\u6211\u7956\u4e0a\u6709\u5fb7\uff0c\u4e09\u751f\u6709\u5e78\uff0c\u5c06\u6765\u56de\u5230\u4e1c\u6d77\uff0c\u4e5f \u53ef\u8363\u8000\u95e8\u5ead\u3002\u201d \u3000\u3000\u94c1\u5934\u6731\u6b63\u987f\u4e86\u987f\u9053\uff1a\u201c\u54b1\u4eec\u8ff7\u8e2a\u8c37\u6709\u4e0a\u5343\u4eba\u4f17\uff0c\u5982\u679c\u6bcf\u4e2a\u4eba\u90fd\u60f3\u8ddf\u4ed9\u5b50\u7761\u89c9\uff0c\u4f60\u53d7\u5f97\u4e86 \u5417\uff1f\u201d \u3000\u3000\u8bb8\u5c0f\u65e6\u6251\u54e7\u4e00\u7b11\u9053\uff1a\u201c\u672c\u4ed9\u5b50\u867d\u7136\u51b3\u5b9a\u4e3a\u5927\u5bb6\u5f00\u5927\u95e8\uff0c\u4f46\u4e00\u4e0b\u5b50\u4e5f\u4e0d\u80fd\u6d8c\u8fdb\u90a3\u4e48\u591a\u82f1\u96c4 \u597d\u6c49\uff0c\u4f60\u4eec\u603b\u8981\u4e00\u4e2a\u2014\u4e2a\u7684\u6765\uff0e\u201d \u3000\u3000\u94c1\u5934\u6731\u6b63\u9053\uff1a\u201c\u4ed9\u5b50\u7684\u610f\u601d\uff0c\u662f\u8981\u5927\u5bb6\u6392\u961f\uff1f\u53ea\u6015\u8fd9\u961f\u4e0d\u5bb9\u6613\u6392\uff0c\u4e00\u5b9a\u4f1a\u6709\u4eba\u62a2\u7740\u63d2 \u961f\uff0e\u770b\u6765\u975e\u6253\u7834\u8111\u888b\u4e0d\u53ef\u3002\u201d \u3000\u3000\u8bb8\u5c0f\u65e6\u773c\u6ce2\u6d41\u8f6c\u5730\u9053\uff1a\u201c\u672c\u4ed9\u5b50\u53ea\u8d1f\u8d23\u5728\u623f\u95f4\u91cc\u62db\u5f85\uff0e\u81f3\u4e8e\u600e\u6837\u7ef4\u6301\u79e9\u5e8f\uff0c\u90a3\u662f\u970d\u76df\u4e3b \u7684\u4e8b\u3002\u201d \u3000\u3000\u94c1\u5934\u6731\u6b63\u9a6c\u4e0a\u628a\u76ee\u5149\u8f6c\u5411\u970d\u5143\u4f3d\u9053\uff1a\u201c\u62a5\u544a\u76df\u4e3b\uff0c\u4f60\u8001\u4eba\u5bb6\u4e00\u5b9a\u8981\u5148\u8ba1\u5212\u597d\uff0c\u514d\u5f97\u5927\u5bb6 \u4f24\u4e86\u548c\u6c14\uff0c\u95f9\u51fa\u4e8b\u6765\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u54fc\u4e86\u58f0\u9053\uff1a\u201c\u636e\u8001\u592b\u6240\u77e5\uff0e\u4f60\u662f\u6700\u559c\u6b22\u95f9\u4e8b\u7684\u4eba\uff0c\u7ecf\u5e38\u5728\u805a\u4e49\u5385\u91cc\u8e22\u684c\u7838\u677f\u51f3\uff0c \u6709\u65f6\u66f4\u5e26\u7740\u5144\u5f1f\u80e1\u95f9\uff0e\u5982\u679c\u4e3a\u4e89\u593a\u5148\u540e\u800c\u95f9\u4e8b\uff0c\u4f60\u662f\u7b2c\u4e00\u4e2a\u4eba\uff01\u201d \u3000\u3000\u94c1\u5934\u6731\u6b63\u5e72\u54b3\u4e86\u4e24\u58f0\u9053\uff1a\u201c\u53ef\u662f\u76df\u4e3b\u4e8b\u5148\u4e0d\u505a\u5b89\u6392\uff0c\u53ea\u6015\u4f1a\u8d8a\u95f9\u8d8a\u5927\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u7528\u4e0d\u7740\u4f60\u8fd9\u94c1\u5934\u66ff\u8001\u592b\u64cd\u5fc3\uff0c\u8001\u592b\u82e5\u4e8b\u5148\u6ca1\u6709\u8179\u6848\uff0c\u8fd8\u5f53\u7684\u4ec0\u4e48\u76df\u4e3b\u3002\u201d \u3000\u3000\u94c1\u5934\u6731\u6b63\u9053\uff1a\u201c\u90a3\u60a8\u5c31\u8bf4\u8bf4\u5427\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u8ff7\u8e2a\u8c37\u867d\u6709\u4e0a\u5343\u4e4b\u4f17\uff0e\u4f46\u8bb8\u4ed9\u5b50\u5374\u7528\u4e0d\u7740\u62db\u5f85\u90a3\u4e48\u591a\u3002\u201d \u3000\u3000\u201c\u62db\u5f85\u591a\u5c11\uff1f\u201d\u3000\uff0c \u3000\u3000\u201c\u6700\u591a\u4e5f\u4e0d\u8fc7\u5728\u573a\u7684\u5404\u4f4d\u3002\u8fd9\u4e48\u2014\u6765\uff0c\u5c31\u53ea\u5269\u4e0b\u51e0\u5341\u4eba\u4e86\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u8bb8\u4ed9\u5b50\u90a3\u6247\u95e8\uff0c\u4e5f\u4e0d\u80fd\u4e00\u4e0b\u6324\u8fdb\u51e0\u5341\u4eba\u53bb\u3002\u201d \u3000\u3000\u201c\u8bb8\u4ed9\u5b50\u54ea\u2014\u665a\u8be5\u62db\u5f85\u8c01\u3002\u7531\u8001\u592b\u51b3\u5b9a\u3002\u201d \u3000\u3000\u201c\u76df\u4e3b\u600e\u6837\u51b3\u5b9a\uff1f\u54b1\u4eec\u62bd\u7b7e\u597d\u4e0d\u597d\uff1f\u201d \u3000\u3000\u201c\u53bb\u4f60\u7684\u72d7\u86cb\uff0c\u8bb8\u4ed9\u5b50\u65e2\u7136\u81ea\u79f0\u662f\u6170\u52b3\u5927\u5bb6\uff0c\u5f53\u7136\u662f\u62db\u5f85\u6709\u529f\u7684\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u54b1\u4eec\u8ff7\u8e2a\u8c37\u597d\u50cf\u5e76\u6ca1\u5efa\u7acb\u529f\u52b3\u7c3f\u3002\u201d \u3000\u3000\u201c\u4f60\u4eec\u8c01\u6709\u529f\u8c01\u6709\u8fc7\u90fd\u5728\u8001\u592b\u5fc3\u91cc\uff0c\u6240\u4ee5\u8bb8\u4ed9\u5b50\u8be5\u62db\u5f85\u8c01\uff0c\u8c01\u5c31\u4f1a\u5728\u5934\u4e00\u5929\u6216\u5f53\u65e5\u5f97\u5230 \u8001\u592b\u7684\u901a\u77e5\uff0c\u51ed\u901a\u77e5\u4ee4\u724c\u624d\u80fd\u8fdb\u8bb8\u4ed9\u5b50\u7684\u95e8\u3002\u6ca1\u6709\u901a\u77e5\u4ee4\u724c\u7684\uff0c\u8c01\u90fd\u522b\u60f3\u3002\u201d \u3000\u3000\u7fa4\u96c4\u4eec\u8fd9\u624d\u660e\u767d\uff0c\u539f\u6765\u8fd9\u662f\u970d\u5143\u4f3d\u53c8\u4e00\u79cd\u63a7\u5236\u6240\u5c5e\u7684\u624b\u6bb5\u3002 \u3000\u3000\u522b\u770b\u8fd9\u624b\u6bb5\u6709\u70b9\u5351\u9119\u9f8c\u9f8a\uff0c\u4f46\u5374\u6781\u4e3a\u6709\u6548\uff0c\u4e16\u4e0a\u4e0d\u77e5\u6709\u591a\u5c11\u4eba\uff0c\u4e00\u751f\u4e2d\u90fd\u88ab\u8fd9\u79cd\u624b\u6bb5\u63a7 \u5236\u7740\uff0c\u800c\u4e14\u5bf9\u63a7\u5236\u8005\u8fd8\u611f\u6069\u975e\u6d45\u5462\u3002 \u3000\u3000\u5ffd\u542c\u53c8\u6709\u4eba\u53eb\u9053\uff1a\u201c\u8bf7\u95ee\u76df\u4e3b\uff0e\u8fd9\u4e8b\u4ec0\u4e48\u65f6\u5019\u5f00\u59cb\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u8001\u592b\u8fd8\u8981\u548c\u8bb8\u4ed9\u5b50\u505a\u6700\u540e\u6c9f\u901a\uff0c\u5927\u5bb6\u5fc5\u987b\u518d\u8010\u5fc3\u7b49\u5f85\u51e0\u5929\u3002\u201d \u3000\u3000\u90a3\u4eba\u6025\u547c\u547c\u5730\u9053\uff1a\u201c\u8981\u6c9f\u901a\u4e00\u53e5\u8bdd\u5c31\u597d\uff0c\u4e3a\u4ec0\u4e48\u8fd8\u8981\u7b49\u597d\u51e0\u5929\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u4f60\u77e5\u9053\u4ec0\u4e48\uff1f\u53eb\u4f60\u7b49\u5c31\u7b49\uff0c\u7f57\u55e6\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u8bb8\u5c0f\u65e6\u6b64\u65f6\u7a81\u7136\u5a9a\u773c\u7b11\u9053\uff1a\u201c\u5c31\u8ba9\u6211\u6765\u4e3a\u5927\u5bb6\u8df3\u4e2a\u821e\u5427\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1b\u201c\u4e0d\u5fc5\u8131\u5566\uff01\u5c31\u8fd9\u6837\u8df3\u6700\u597d\u3002\u201d \u3000\u3000\u8c01\u77e5\u8bb8\u5c0f\u65e6\u5374\u6447\u5934\u9053\uff1a\u201c\u4e0d\u8131\u4e0d\u8212\u670d\uff0c\u800c\u4e14\u7a7f\u7740\u8863\u670d\u8df3\uff0e\u4ed6\u4eec\u4e5f\u4e0d\u8fc7\u763e\u3002\u201d \u3000\u3000\u53ea\u542c\u94c1\u5934\u6731\u6b63\u53eb\u9053\uff1a\u201c\u76df\u4e3b\uff0c\u8fd9\u5c31\u662f\u4f60\u7684\u4e0d\u5bf9\u4e86\uff0e\u4eba\u5bb6\u8bb8\u4ed9\u5b50\u8981\u8131\uff0c\u60a8\u5374\u4e0d\u51c6\u5979\u8131\uff0c\u592a \u8ba9\u6211\u4eec\u5931\u671b\u5566\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u4f60\u61c2\u4ec0\u4e48\uff01\u8001\u592b\u662f\u4e3a\u4f60\u4eec\u597d\uff0c\u82e5\u4f60\u4eec\u770b\u4e86\uff0e\u5c06\u6765\u5230\u4e86\u8bb8\u4ed9\u5b50\u623f\u91cc\uff0c\u8fd8\u6709\u4ec0 \u4e48\u770b\u5934\uff01\u201d \u3000\u3000\u94c1\u5934\u6731\u6b63\u7ec8\u4e8e\u52c9\u5f3a\u4e0d\u518d\u542d\u58f0\u3002 \u3000\u3000\u8bb8\u5c0f\u65e6\u968f\u5373\u79bb\u5ea7\u51fa\u573a\uff0c\u5f00\u59cb\u8868\u6f14\u3002 \u3000\u3000\u5176\u5b9e\u8c01\u4e5f\u4e0d\u77e5\u5979\u8df3\u4e86\u4ec0\u4e48\uff0c\u53cd\u6b63\u6709\u80f3\u818a\u6709\u817f\u7684\u90fd\u80fd\u8df3\u5f97\u51fa\u6765\u3002 \u3000\u3000\u8fd9\u6b21\u5bb4\u4f1a\uff0c\u5c31\u5728\u8bb8\u5c0f\u65e6\u8df3\u5b8c\u4e4b\u540e\u6563\u5e2d\u3002 \u3000\u3000\u970d\u5143\u4f3d\u679c\u7136\u8a00\u51fa\u5fc5\u8df5\uff0c\u5c31\u5728\u7b2c\u4e8c\u5929\uff0c\u4fbf\u817e\u51fa\u4e00\u95f4\u72ec\u7acb\u5ead\u9662\uff0e\u8981\u8bb8\u5c0f\u65e6\u642c\u4e86\u8fdb\u53bb\u3002 \u3000\u3000\u8fd9\u5904\u72ec\u7acb\u5ead\u9662\uff0c\u56db\u9762\u90fd\u6709\u56f4\u5899\uff0c\u8fdb\u95e8\u4e4b\u540e\uff0c\u662f\u4e00\u4e2a\u4e0d\u5927\u7684\u5929\u4e95\uff0c\u4e2d\u95f4\u662f\u2014\u5ea7\u767d\u77f3\u780c\u6210\u7684 \u5c0f\u697c\u3002 \u3000\u3000\u970d\u5143\u4f3d\u56e0\u4e3a\u8bb8\u5c0f\u65e6\u81ea\u79f0\u662f\u4ece\u4e1c\u6d77\u6765\u7684\uff0e\u4fbf\u628a\u8fd9\u5ead\u9662\u547d\u540d\u201c\u4e1c\u6d77\u9662\u201d\u3002 \u3000\u3000\u5bf9\u5979\u771f\u662f\u591f\u4f18\u5f85\u7684\u3002 \u3000\u3000\u96be\u602a\u5979\u4f1a\u4e0d\u60f3\u8d70\u4e86\u3002 \u3000\u3000\u970d\u5143\u4f3d\u4e00\u76f4\u5e0c\u671b\u80fd\u4e3a\u8bb8\u5c0f\u65e6\u627e\u4e24\u540d\u670d\u4f8d\u7684\u4e2b\u73af\uff0c\u53ef\u60dc\u5728\u8ff7\u8e2a\u8c37\u627e\u4e0d\u5230\uff0c\u53ea\u5f97\u5148\u6d3e\u4e86\u4e24\u540d \u55bd\u7f57\u8d1f\u8d23\u9001\u996d\u548c\u6574\u7406\u73af\u5883\u3002 \u3000\u3000\u800c\u4e14\u4ed6\u5c45\u7136\u628a\u4e24\u540d\u55bd\u7f57\u7684\u201c\u5b9d\u8d1d\u201d\u9609\u6389\uff0c\u4f7f\u5f97\u8ff7\u8e2a\u8c37\u5c45\u7136\u6709\u4e86\u201c\u592a\u76d1\u201d\u3002 \u3000\u3000\u53e6\u5916\uff0e\u4e1c\u6d77\u9662\u7684\u95e8\u53e3\uff0c\u4e5f\u6d3e\u51fa\u8b66\u536b\uff0c\u8d1f\u8d23\u9a8c\u8bc1\u8fc7\u884c\uff0e\u4ee5\u514d\u6709\u4eba\u8fdb\u53bb\u201c\u6d51\u6c34\u6478\u9c7c\u201d\uff0c\u56e0\u4e3a \u636e\u8bf4\u8bb8\u4ed9\u5b50\u4e0d\u4f1a\u6b66\u529f\uff0c\u82e5\u6709\u4eba\u95ef\u8fdb\u53bb\u9738\u738b\u786c\u4e0a\uff0c\u5979\u6839\u672c\u65e0\u80fd\u62b5\u6297\u3002 \u3000\u3000\u5c31\u5728\u8bb8\u5c0f\u65e6\u642c\u8fdb\u4e1c\u6d77\u9662\u7684\u7b2c\u4e94\u5929\uff0c\u2018\u751f\u610f\u2019\u4fbf\u5f00\u5f20\u4e86\uff0c\u7b2c\u2014\u5f20\u6170\u52b3\u4ee4\u724c\uff0c\u5c45\u7136\u53d1\u7ed9\u4e86\u949f \u4e00\u8c6a\u3002 \u3000\u3000\u5176\u5b9e\u8fd9\u4e8b\u5e76\u4e0d\u592a\u8fc7\u610f\u5916\uff0c\u56e0\u4e3a\u949f\u2014\u8c6a\u662f\u526f\u76df\u4e3b\uff0c\u5728\u2018\u6392\u884c\u699c\u2019\u3000\u4e0a\u7684\u6392\u540d\u5f53\u7136\u5728\u5148\u3002 \u3000\u3000\u4f46\u949f\u4e00\u8c6a\u5e76\u6ca1\u53bb\uff0c\u4e5f\u672a\u628a\u4ee4\u724c\u7acb\u5373\u9001\u8fd8\u970d\u5143\u4f3d\u3002\u76f4\u5230\u7b2c\u4e8c\u5929\uff0c\u624d\u6d3e\u4eba\u4e0a\u7f34\u4e86\u4ee4\u724c\uff0c\u8fd9\u5728 \u970d\u5143\u4f3d\u6765\u8bf4\uff0c\u8fd8\u4ee5\u4e3a\u949f\u4e00\u8c6a\u5df2\u7ecf\u201c\u900d\u9065\u201d\u8fc7\u4e86\u5462\u3002 \u3000\u3000\u970d\u5143\u4f3d\u4e3a\u4e86\u62e2\u7edc\u67cf\u9f84\u9662\u65b9\u9762\u7684\u4eba\uff0c\u7b2c\u4e8c\u6b21\u8fdb\u5165\u4e1c\u6d77\u9662\u7684\u4ee4\u724c\uff0e\u7ade\u53c8\u662f\u67cf\u9f84\u9662\u7684\u4eba\uff0e\u5f97\u5230 \u4ee4\u724c\u7684\u4eba\u662f\u4f59\u4ea6\u4e50\u3002 \u3000\u3000\u4f59\u4ea6\u4e50\u4e5f\u548c\u949f\u4e00\u8c6a\u4e00\u6837\uff0e\u4eba\u672a\u5230\uff0e\u6b21\u65e5\u628a\u4ee4\u724c\u7f34\u8fd8\u970d\u5143\u4f3d\u3002 \u3000\u3000\u67cf\u9f84\u9662\u91cc\u7684\u4eba\u5fc3\u91cc\u90fd\u6709\u6570\uff0c\u82e5\u518d\u6765\u4ee4\u724c\uff0c\u5fc5\u5b9a\u843d\u5728\u9ea6\u5c0f\u660e\u8eab\u4e0a\u3002\u56e0\u4e4b\uff0c\u90fd\u8d81\u673a\u548c\u9ea6\u5c0f\u660e \u5f00\u73a9\u7b11\uff0e\u95ee\u4ed6\u63a5\u5230\u4ee4\u724c\u4ee5\u540e\u600e\u4e48\u529e\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u7b11\u9053\uff1a\u201c\u949f\u526f\u76df\u4e3b\u548c\u4f59\u5148\u751f\u770b\u4f3c\u806a\u660e\uff0c\u5176\u5b9e\u662f\u4e24\u4e2a\u4eba\u50bb\u74dc\uff01\u201d \u3000\u3000\u738b\u5927\u5eb7\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u9ea6\u5c0f\u4fa0\u63a5\u5230\u4ee4\u724c\u4ee5\u540e\u4e00\u5b9a\u53bb\u4e86\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u53cd\u95ee\u9053\uff1a\u201c\u5982\u679c\u6709\u4eba\u8bf7\u5ba2\uff0c\u4f60\u53bb\u4e0d\u53bb\uff1f\u201d \u3000\u3000\u738b\u5927\u5eb7\u9053\uff1a\u201c\u90a3\u8981\u770b\u8bf7\u5ba2\u7684\u662f\u4ec0\u4e48\u4eba\u548c\u6709\u6ca1\u6709\u8bda\u610f\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4eba\u5bb6\u8bf7\u4f60\u53bb\u7761\u89c9\uff0e\u8fde\u60c5\u4e49\u90fd\u6709\uff0c\u600e\u4f1a\u6ca1\u6709\u8bda\u610f\u3002\u201d \u3000\u3000\u738b\u5927\u5eb7\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u770b\u6765\u9ea6\u5c0f\u4fa0\u662f\u60f3\u53bb\u4e86\uff0c\u8fd9\u4e5f\u96be\u602a\u3002\u5982\u679c\u4ffa\u731c\u5f97\u4e0d\u5dee\uff0c\u4f60\u53ef\u80fd\u8fd8\u6ca1\u8ddf \u5973\u4eba\u7761\u8fc7\u89c9\uff0c\u6709\u4e86\u673a\u4f1a\uff0c\u5f53\u7136\u4e0d\u60f3\u653e\u5f03\u3002\u201d \u3000\u3000\u8c01\u60f3\u7b2c\u4e09\u6b21\u7684\u4ee4\u724c\uff0c\u7adf\u6ca1\u9001\u5230\u67cf\u9f84\u9662\u6765\u3002 \u3000\u3000\u971c\u5143\u4f3d\u8fde\u53d1\u51fa\u4e24\u6b21\u6170\u52b3\u4ee4\u724c\uff0c\u5f88\u60f3\u4e86\u89e3\u4e00\u4e0b\u8bb8\u5c0f\u65e6\u7684\u201c\u63a5\u5ba2\u201d\u60c5\u5f62\u3002\u95f2\u7740\u6ca1\u4e8b\uff0c\u4fbf\u6084\u6084 \u6765\u5230\u4e1c\u6d77\u9662\u3002 \u3000\u3000\u6765\u5230\u4e1c\u6d77\u9662\uff0c\u5929\u4e95\u5185\u9759\u6084\u6084\u7684\u5e76\u65e0\u4e00\u4eba\uff0e \u3000\u3000\u8fd9\u4e5f\u96be\u602a\uff0c\u6574\u5ea7\u4e1c\u6d77\u9662\uff0c\u53ea\u6709\u4e09\u4eba\u3002 \u3000\u3000\u4e00\u4e2a\u662f\u4e3b\u89d2\u8bb8\u5c0f\u65e6\uff0c\u4e24\u4e2a\u662f\u88ab\u9609\u6389\u201c\u5b9d\u8d1d\u201d\u7684\u55bd\u7f57\uff0c\u9664\u4e86\u62ff\u996d\u3001\u626b\u5730\u3001\u6574\u7406\u574f\u5883\u5916\uff0c\u6839 \u672c\u65e0\u4e8b\u53ef\u505a\uff0e \u3000\u3000\u53c8\u56e0\u4ed6\u4eec\u521a\u88ab\u9609\u6389\u4e0d\u4e45\uff0c\u4f24\u52bf\u5c1a\u672a\u75ca\u6108\uff0c\u9664\u4e86\u5f85\u5728\u95e8\u5916\uff0e\u5b9e\u5728\u61d2\u5f97\u6d3b\u52a8\u3002 \u3000\u3000\u970d\u5143\u4f3d\u53ea\u89c1\u8bb8\u5c0f\u65e6\u7684\u5367\u5ba4\u623f\u95e8\u865a\u63a9\uff0c\u91cc\u9762\u4e5f\u65e0\u534a\u70b9\u58f0\u606f\u3002 \u3000\u3000\u4ed6\u63ed\u5e18\u800c\u5165\uff0c\u539f\u6765\u8bb8\u5c0f\u65e6\u5374\u8eba\u5728\u5e8a\u4e0a\u672a\u8d77\uff0c\u4e00\u526f\u61d2\u6d0b\u6d0b\u65e0\u7cbe\u6253\u91c7\u6a21\u6837\u3002 \u3000\u3000\u8bb8\u5c0f\u65e6\u89c1\u6765\u4e86\u970d\u5143\u4f3d\u3001\u52c9\u5f3a\u5750\u8d77\u8eab\u3002 \u3000\u3000\u970d\u5143\u4f3d\u6123\u4e86\u4e0b\u9053\uff1a\u201c\u4ed9\u5b50\u83ab\u975e\u751f\u75c5\u4e86\uff1f\u201d \u3000\u3000\u8bb8\u5c0f\u65e6\u5a07\u61d2\u65e0\u529b\u9053\uff1a\u201c\u597d\u7aef\u7aef\u7684\uff0c\u600e\u4e48\u4f1a\u751f\u75c5\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u5fc3\u6709\u6240\u609f\u7684\u54e6\u4e86\u4e00\u58f0\uff0c\u63a5\u7740\u5475\u5475\u7b11\u9053\uff1a\u201c\u8001\u592b\u660e\u767d\u4e86\uff01\u201d \u3000\u3000\u8bb8\u5c0f\u65e6\u4e5f\u8ddf\u7740\u4e00\u6123\u9053\uff1a\u201c\u76df\u4e3b\u660e\u767d\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u5634\u89d2\u6cdb\u7740\u7b11\u9053\uff1a\u201c\u4e00\u5b9a\u662f\u949f\u526f\u76df\u4e3b\u548c\u4f59\u4ea6\u4e50\u7684\u52a8\u52b2\u592a\u5927\uff0c\u8ba9\u4f60\u53d7\u4e0d\u4e86\uff0c\u6240\u4ee5\u624d\u7d2f \u5f97\u8d77\u4e0d\u4e86\u5e8a\u3002\u201d \u3000\u3000\u8bb8\u5c0f\u65e6\u8d8a\u53d1\u4e0d\u89e3\u9053\uff1a\u201c\u76df\u4e3b\u8bf4\u4e9b\u4ec0\u4e48\uff1f\u4e3a\u4ec0\u4e48\u5ffd\u7136\u63d0\u8d77\u949f\u526f\u76df\u4e3b\u548c\u4f59\u5148\u751f\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u7728\u7740\u4e00\u5bf9\u4e09\u89d2\u773c\u9053\uff1a\u201c\u4f60\u88c5\u7684\u4ec0\u4e48\u849c\uff1f\u524d\u5929\u548c\u6628\u5929\u4e24\u4e2a\u665a\u4e0a\u4e0d\u6b63\u662f\u4ed6\u4eec\u4e24\u4e2a\u5728\u4e1c \u6d77\u9662\u4f5c\u5ba2\u5417\uff1f\u201d \u3000\u3000\u8bb8\u5c0f\u65e6\u832b\u7136\u9053\uff1a\u201c\u76df\u4e3b\u771f\u4f1a\u5f00\u73a9\u7b11\uff0c\u8fd9\u51e0\u5929\u6839\u672c\u6ca1\u5ba2\u4eba\u6765\u8fc7\uff0c\u6211\u6b63\u8981\u53bb\u95ee\u95ee\u4f60\uff0c\u4e3a\u4ec0\u4e48 \u8fd8\u4e0d\u5f00\u5f20\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u4e0d\u89c9\u4e3a\u4e4b\u9519\u6115\uff0c\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u5f00\u73a9\u7b11\u5427\uff1f\u201d \u3000\u3000\u8bb8\u5c0f\u65e6\u9053\uff1a\u201c\u8fd9\u662f\u6b63\u7ecf\u5927\u4e8b\uff0c\u600e\u80fd\u968f\u4fbf\u5f00\u73a9\u7b11\uff0e\u201d \u3000\u3000\u201c\u8001\u592b\u5728\u524d\u5929\u4fbf\u53d1\u51fa\u4ee4\u724c\uff0c\u7b2c\u4e00\u4f4d\u6170\u52b3\u7684\u662f\u949f\u526f\u76df\u4e3b\uff0c\u6628\u5929\u53c8\u53d1\u4ee4\u724c\u7ed9\u4f59\u4ea6\u4e50\u3002\u201d \u3000\u3000\u201c\u4ed6\u4eec\u6839\u672c\u6ca1\u6765\uff01\u201d \u3000\u3000\u201c\u8fd9\u5012\u5947\u602a\u4e86\uff0c\u83ab\u975e\u4ed6\u4eec\u751f\u75c5\u4e86\uff1f\u4e0d\u5bf9\uff0c\u8001\u592b\u4eca\u5929\u65e9\u4e0a\u8fd8\u89c1\u8fc7\u4ed6\u4eec\uff0e\u4e24\u4e2a\u90fd\u597d\u597d\u7684\uff0c\u4e3a \u4ec0\u4e48\u6ca1\u6765\u5462\uff1f\u201d \u3000\u3000\u201c\u76df\u4e3b\u4e3a\u4ec0\u4e48\u4e0d\u95ee\u95ee\u4ed6\u4eec\uff1f\u201d \u3000\u3000\u201c\u5f53\u65f6\u8001\u592b\u4e0d\u77e5\u9053\u4ed6\u4eec\u6ca1\u6765\uff0c\u81ea\u7136\u4e0d\u9700\u8981\u95ee\uff0c\u66f4\u4f55\u51b5\u8fd9\u79cd\u4e8b\u4e5f\u4e0d\u65b9\u4fbf\u95ee\u3002\u201d \u3000\u3000\u201c\u6211\u5012\u660e\u767d\u4e86\uff0e\u201d \u3000\u3000\u201c\u4f60\u660e\u767d\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u4e5f\u8bb8\u4ed6\u4eec\u6709\u6bdb\u75c5\uff1f\u201d \u3000\u3000\u201c\u5927\u6709\u53ef\u80fd\uff0c\u4e0d\u7136\u50cf\u4f60\u8fd9\u6837\u7684\u5973\u4eba\uff0c\u7537\u4eba\u54ea\u6709\u81ea\u52a8\u653e\u5f03\u7684\uff1f\u5176\u5b9e\u8fd9\u6837\u4e5f\u597d\uff0c\u81f3\u5c11\u4f60\u80fd\u843d \u4e2a\u8f7b\u677e\u3002\u201d \u3000\u3000\u8c01\u77e5\u8bb8\u5c0f\u65e6\u53cd\u800c\u663e\u5f97\u6709\u4e9b\u4e0d\u5feb\uff0c\u6447\u6447\u5934\u9053\uff1a\u201c\u4f60\u9519\u5566\uff01\u6211\u95f2\u7740\u4e5f\u662f\u95f2\u7740\uff0c\u80fd\u591a\u63a5\u4e24\u4e2a\u5ba2 \u4eba\uff0c\u624d\u662f\u6211\u7684\u5149\u8363\u3002\u53cd\u6b63\u90a3\u4e1c\u897f\u4e5f\u5c11\u4e0d\u4e86\u2014\u5757\uff0c\u5229\u4eba\u5229\u5df2\u3002\u82e5\u5f03\u800c\u4e0d\u7528\uff0c\u5c31\u7b49\u4e8e\u2014\u5757\u597d\u5730\u6ca1 \u79cd\u5e84\u7a3c\uff0c\u53cd\u800c\u4f1a\u6742\u8349\u4e1b\u751f\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u4e0d\u7531\u6253\u4e2a\u54c8\u54c8\u9053\uff1a\u201c\u539f\u6765\u4f60\u6709\u8fd9\u79cd\u60f3\u6cd5\uff0c\u771f\u662f\u4e86\u4e0d\u8d77\uff0e\u65e9\u77e5\u5982\u6b64\uff0c\u6211\u5c31\u8be5\u628a\u4e1c\u6d77 \u9662\u53e6\u8ba2\u2014\u5957\u5f00\u653e\u529e\u6cd5\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u53e6\u4e00\u5957\u529e\u6cd5\uff1f\u201d \u3000\u3000\u201c\u8ba9\u4e1c\u6d77\u9662\u65e5\u591c\u5f00\u653e\uff0c\u665a\u4e0a\u8fd8\u662f\u53ea\u63a5\u5f85\u4e00\u4e2a\uff0c\u767d\u5929\u63a5\u5f85\u51e0\u4e2a\uff0c\u7531\u4f60\u51b3\u5b9a\uff0c\u4f60\u81ea\u5df1\u8bf4\u8bf4\u770b \u767d\u5929\u80fd\u63a5\u5f85\u51e0\u4e2a\uff1f\u201d \u3000\u3000\u201c\u90a3\u8981\u770b\u60c5\u5f62\u5566\uff0c\u5982\u679c\u6211\u9ad8\u5174\u4e86\uff0c\u5373\u4f7f\u63a5\u5f85\u4e09\u5341\u4e2a\u56db\u5341\u4e2a\u4e5f\u6ca1\u95ee\u9898\u3002\u201d \u3000\u3000\u201c\u90a3\u672a\u514d\u592a\u591a\u4e86\uff0c\u4f9d\u8001\u592b\u7684\u610f\u601d\uff0c\u767d\u5929\u5c31\u63a5\u5f85\u5341\u4e2a\u5427\u3002\u8fd9\u6837\u7b97\u6765\uff0c\u4e0d\u8981\u56db\u4e2a\u6708\uff0c\u8ff7\u8e2a\u8c37 \u6240\u6709\u7684\u4eba\uff0c\u4fbf\u90fd\u6709\u673a\u4f1a\u6765\u4e1c\u6d77\u9662\u4f5c\u5ba2\u3002\u5728\u8001\u592b\u6765\u8bf4\uff0e\u4e5f\u7b97\u5bf9\u4ed6\u4eec\u5c3d\u4e86\u6700\u5927\u7684\u5fc3\u610f\uff0c\u8001\u592b\u8fd9\u7eff \u6797\u76df\u4e3b\u7684\u5b9d\u5ea7\uff0c\u4e5f\u5fc5\u5b9a\u4e00\u5929\u5929\u66f4\u52a0\u7a33\u56fa\u3002\u81f3\u4e8e\u4f60\uff0c\u5f53\u7136\u5c31\u662f\u8001\u592b\u6700\u5927\u7684\u529f\u81e3\u3002\u201d \u3000\u3000\u8bb8\u5c0f\u65e6\u9762\u6709\u96be\u8272\u9053\uff1a\u201c\u6211\u4e0d\u60f3\u4eba\u4eba\u90fd\u63a5\uff0c\u53ea\u5e0c\u671b\u80fd\u63a5\u90a3\u5929\u5728\u5927\u5385\u5403\u996d\u7684\u90a3\u51e0\u5341\u4e2a\u4eba\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u7b11\u9053\uff1a\u201c\u4ed6\u4eec\u90fd\u662f\u8ff7\u8e2a\u8c37\u7684\u5934\u76ee\uff0c\u53ef\u89c1\u4f60\u7684\u773c\u5149\u4e5f\u5f88\u9ad8\uff0e\u5176\u5b9e\u90a3\u4e9b\u55bd\u7f57\u4eec\uff0c\u8bba\u6b66 \u529f\u663e\u7136\u4e0d\u6d4e\uff0c\u4e0a\u4e86\u5e8a\u5374\u4e0d\u89c1\u5f97\u8f93\u7ed9\u90a3\u4e9b\u5934\u76ee\u3002\u201d \u3000\u3000\u4ed6\u9ed8\u4e86\u4e00\u9ed8\uff0c\u53c8\u9053\uff1a\u201c\u90a3\u5c31\u6682\u65f6\u7ef4\u6301\u539f\u72b6\uff0c\u8fd8\u662f\u767d\u5929\u4f11\u606f\uff0c\u665a\u4e0a\u53ea\u63a5\u4e00\u4e2a\uff0c\u7b49\u8fc7\u4e9b\u5929\u60c5 \u5f62\u518d\u51b3\u5b9a\u767d\u5929\u662f\u5426\u5f00\u653e\u3002\u201d \u3000\u3000\u8bb8\u5c0f\u65e6\u7ea2\u7740\u8138\u9053\uff1a\u201c\u53ef\u662f\u4f60\u53d1\u4ee4\u724c\uff0c\u522b\u518d\u53d1\u7ed9\u4e0d\u80af\u6765\u7684\u3002\u8fd9\u4e24\u5929\u665a\u4e0a\uff0c\u6211\u4e00\u76f4\u5728\u7b49\uff0c\u5de6 \u7b49\u4e0d\u6765\uff0e\u53f3\u7b49\u4e0d\u6765\uff0c\u771f\u6ca1\u9762\u5b50\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u7565\u4e00\u6c89\u541f\u9053\uff1a\u201c\u8001\u592b\u624b\u4e0b\u7684\u5934\u76ee\uff0c\u4f60\u5dee\u4e0d\u591a\u90fd\u89c1\u8fc7\uff0c\u4e0d\u59a8\u7531\u4f60\u6307\u5b9a\uff0c\u60f3\u8981\u54ea\u4e00 \u4e2a\uff0e\u8001\u592b\u5c31\u53d1\u4ee4\u724c\u7ed9\u4ed6\u3002\u201d \u3000\u3000\u8bb8\u5c0f\u65e6\u8f6c\u52a8\u7740\u773c\u73e0\u60f3\u4e86\u534a\u664c\u9053\uff1a\u201c\u6700\u597d\u662f\u6f02\u4eae\u53c8\u5e74\u8f7b\u7684\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u8001\u592b\u624b\u4e0b\u7684\u4eba\uff0c\u82f1\u4fca\u6f47\u6d12\u7684\u4e0d\u5728\u5c11\u6570\uff0c\u5e74\u7eaa\u8f7b\u7684\u4e5f\u5f88\u591a\uff0c\u4f60\u60f3\u8981\u54ea\u4e2a\uff1f\u201d \u3000\u3000\u8bb8\u5c0f\u65e6\u987f\u4e86\u987f\u9053\uff1a\u201c\u90a3\u5929\u540c\u684c\u6709\u4e2a\u5e74\u8f7b\u7684\uff0c\u597d\u50cf\u59d3\u9ea6\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u7b11\u9053\uff1a\u201c\u90a3\u5c0f\u5b50\u53eb\u9ea6\u5c0f\u660e\uff0c\u4f60\u600e\u4e48\u5ffd\u7136\u770b\u4e0a\u4ed6\u4e86\uff1f\u201d \u3000\u3000\u8bb8\u5c0f\u65e6\u9053\uff1a\u201c\u8fd8\u8981\u95ee\uff0e\u56e0\u4e3a\u4ed6\u5e74\u8f7b\uff0c\u957f\u5f97\u53c8\u5e05\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u6447\u5934\u9053\uff1a\u201c\u4e3a\u4e86\u7b2c\u4e00\u6b21\u5f00\u5f20\u8ba8\u4e2a\u5409\u5229\uff0c\u6211\u529d\u4f60\u6700\u597d\u522b\u8981\u4ed6\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u7406\u7531\u5f88\u591a\uff0e\u7b2c\u4e00\uff0c\u90a3\u5c0f\u5b50\u636e\u8bf4\u4eca\u5e74\u53ea\u6709\u5341\u516b\u5c81\uff0e\u800c\u4f60\u5df2\u662f\u4e09\u5341\u5de6\u53f3\u7684\u4eba\u4e86\uff0c\u7761\u5728\u4e00 \u8d77\uff0c\u50cf\u6bcd\u4eb2\u6402\u7740\u513f\u5b50\uff0e\u5b9e\u5728\u4e0d\u592a\u76f8\u914d\uff0c\u81f3\u5c11\u4f60\u662f\u5403\u4e8f\u7684\u3002\u201d \u3000\u3000\u201c\u8fd8\u6709\u7b2c\u4e8c\u6ca1\u6709\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u6709\uff0c\u90a3\u5c0f\u5b50\u6027\u60c5\u706b\u7206\uff0c\u8fde\u8001\u592b\u6709\u65f6\u90fd\u4e0d\u653e\u5728\u773c\u91cc\uff0c\u5bf9\u4f60\u7edd\u5bf9\u4e0d\u4f1a\u6e29\u67d4\u4f53\u8d34\uff0c\u82e5\u628a \u4ed6\u60f9\u7ffb\u4e86\uff0c\u8bf4\u4e0d\u5b9a\u4f1a\u63cd\u4f60\u4e00\u987f\u3002\u201d \u3000\u3000\u201c\u4f60\u53ef\u77e5\u9053\u67d4\u80fd\u514b\u521a\uff0c\u6211\u81ea\u6709\u529e\u6cd5\u8bad\u670d\u4ed6\uff0c\u4e16\u4e0a\u4ec0\u4e48\u7537\u4eba\u6211\u90fd\u89c1\u8fc7\uff0c\u8fd8\u6ca1\u6709\u770b\u4e00\u4e2a\u82f1\u96c4 \u80fd\u8fc7\u7f8e\u4eba\u5173\u7684\u3002\u201d \u3000\u3000\u201c\u4f60\u7684\u610f\u601d\u662f\u60f3\u8001\u725b\u5403\u5ae9\u8349\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u8bdd\uff1f\u6211\u867d\u7136\u5e74\u7eaa\u6bd4\u4ed6\u5927\uff0c\u5374\u4e5f\u4e0d\u80fd\u7b97\u8001\u3002\u76df\u4e3b\uff0c\u4f60\u770b\u6211\u8001\u4e0d\u8001\uff1f\u201d \u3000\u3000\u201c\u5728\u8001\u592b\u773c\u4e2d\uff0c\u4f60\u53ea\u662f\u5c0f\u59d1\u5a18\uff0c\u518d\u8fc7\u5341\u5e74\u4e5f\u4e0d\u80fd\u7b97\u8001\u3002\u201d \u3000\u3000\u201c\u90a3\u8fd8\u6709\u4ec0\u4e48\u53ef\u8bf4\u7684\uff0e\u53ea\u7ba1\u628a\u4ee4\u724c\u53d1\u7ed9\u90a3\u59d3\u9ea6\u7684\u5e74\u8f7b\u4eba\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u72b9\u8c6b\u4e86\u4e00\u9635\u9053\uff1a\u201c\u8fd9\u6837\u5427\uff0c\u4f60\u65e2\u7136\u559c\u6b22\u90a3\u59d3\u9ea6\u7684\u5c0f\u5b50\uff0c\u8fc7\u51e0\u5929\u6211\u81ea\u7136\u4f1a\u628a\u4ee4\u724c\u53d1 \u7ed9\u4ed6\u3002\u4e0d\u8fc7\u4eca\u665a\u6211\u60f3\u627e\u53e6\u5916\u4e00\u4e2a\u4eba\u6765\u966a\u4f60\u3002\u201d \u3000\u3000\u8bb8\u5c0f\u65e6\u98a6\u8d77\u67f3\u7709\u9053\uff1a\u201c\u8c01\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u5f53\u7136\u4e5f\u662f\u4f60\u559c\u6b22\u7684\u3002\u201d \u3000\u3000\u8bb8\u5c0f\u65e6\u9053\uff1a\u201c\u4f60\u77e5\u9053\u6211\u559c\u6b22\u4ec0\u4e48\u6837\u7684\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u563f\u563f\u7b11\u9053\uff1a\u201c\u542c\u4f60\u65b9\u624d\u7684\u8bed\u6c14\uff0c\u4e0d\u7528\u8bf4\u662f\u559c\u6b22\u80fd\u5e72\u7684\u7537\u4eba\uff0e\u82e5\u8bba\u80fd\u5e72\uff0c\u8fd9\u4eba\u79f0\u5f97 \u4e0a\u662f\u5458\u731b\u5c06\uff0c\u4e00\u5b9a\u80fd\u628a\u4f60\u4f3a\u4faf\u5f97\u8212\u8212\u670d\u670d\uff0e\u8db3\u53ef\u8865\u507f\u8fd9\u4e24\u665a\u4f60\u6240\u53d7\u7684\u635f\u5931\u3002\u201d \u3000\u3000\u201c\u4f60\u8bf4\u7684\u5230\u5e95\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u4ed6\u6765\u4e86\u4ee5\u540e\uff0c\u4f60\u81ea\u7136\u660e\u767d\uff0e\u73b0\u5728\u8bf4\u51fa\u6765\u5c31\u6ca1\u610f\u601d\u5566\uff01\u201d \u3000\u3000\u201c\u597d\u5427\uff0c\u6211\u4eca\u665a\u7b49\u7740\u4ed6\u6765\uff01\u201d \u3000\u3000\u201c\u4f60\u767d\u5929\u6700\u597d\u8fd8\u662f\u591a\u7761\u7761\uff0e\u517b\u8db3\u4e86\u7cbe\u795e\uff0c\u591c\u91cc\u624d\u5e94\u4ed8\u5f97\u4e86\u3002\u201d \u3000\u3000\u201c\u4f60\u653e\u5fc3\uff0c\u6211\u4e0d\u662f\u6ca1\u89c1\u8fc7\u4e16\u9762\u7684\u4eba\uff0e\u4e0d\u8bba\u4ed6\u591a\u4e48\u80fd\u5e72\uff0c\u6211\u4e5f\u6015\u4e0d\u4e86\u4ed6\u3002\u201d \u3000\u3000\u5c31\u5728\u5f53\u5929\u665a\u996d\u540e\uff0c\u4e00\u9762\u6170\u52b3\u4ee4\u724c\uff0c\u9001\u5230\u4e86\u5d02\u5c71\u4e09\u96c4\u4e4b\u4e00\u7684\u52c7\u91d1\u521a\u738b\u5927\u5eb7\u624b\u91cc\u3002 \u3000\u3000\u738b\u5927\u5eb7\u7a81\u63a5\u4ee4\u724c\uff0c\u53cd\u800c\u6709\u4e9b\u4e0d\u77e5\u6240\u63aa\uff0e \u3000\u3000\u67cf\u9f84\u9662\u6240\u6709\u7684\u4eba\uff0c\u9664\u82d7\u7d20\u82d3\u5916\uff0c\u90fd\u6d8c\u5230\u5c0f\u5ba2\u5385\u6765\u770b\u738b\u5927\u5eb7\u5982\u4f55\u5916\u7f6e\u3002 \u3000\u3000\u738b\u5927\u5eb7\u628a\u4ee4\u724c\u5f80\u684c\u4e0a\u2014\u6254\u9053\uff1a\u201c\u4ed6\u5976\u5976\u7684\uff01\u7f8a\u5708\u91cc\u8dd1\u51fa\u4e2a\u9a74\u6765\uff0c\u970d\u5143\u4f3d\u90a3\u8001\u5c0f\u5b50\uff0c\u600e\u4f1a \u628a\u4ee4\u724c\u5355\u5355\u53d1\u5728\u4ffa\u5934\u4e0a\uff1f\u201d \u3000\u3000\u6c5f\u5317\u56db\u9f99\u7684\u8001\u4e09\u98de\u5929\u9f99\u4f55\u5b97\u8f89\u7b11\u9053\uff1a\u201c\u8fd9\u662f\u970d\u5143\u4f3d\u770b\u5f97\u8d77\u4f60\uff0c\u4f60\u8fd8\u53d1\u4ec0\u4e48\u7262\u9a9a\uff1f\u201d \u3000\u3000\u738b\u5927\u5eb7\u9053\uff1a\u201c\u8fd9\u4e8b\u900f\u7740\u5947\u602a\uff0e\u5c31\u662f\u7167\u6b21\u5e8f\u8f6e\uff0c\u4e5f\u8f6e\u4e0d\u5230\u4ffa\u5934\u4e0a\u3002\u201d \u3000\u3000\u4f55\u5b97\u8f89\u9053\uff1a\u201c\u4eba\u5bb6\u949f\u526f\u76df\u4e3b\u548c\u4f59\u5148\u751f\u4e0d\u662f\u5df2\u7ecf\u8f6e\u8fc7\u4e86\u5417\uff1f\u201d \u3000\u3000\u738b\u4eba\u5eb7\u9053\uff1a\u201c\u4ed6\u4eec\u4e24\u4f4d\u8f6e\u8fc7\u4e86\uff0e\u8fd8\u6709\u5cad\u5357\u4e8c\u5947\u7684\u5df4\u5929\u4e49\u548c\u5b8b\u5929\u94ce\uff0c\u4ed6\u4eec\u5728\u8ff7\u8e2a\u8c37\u7684\u8eab\u4efd \u5730\u4f4d\u90fd\u6bd4\u4ffa\u9ad8\uff0c\u66f4\u662f\u970d\u5143\u4f3d\u7684\u5fc3\u8179\uff0c\u600e\u4f1a\u8f6e\u5230\u4ffa\u5934\u4e0a\uff1f\u201d \u3000\u3000\u4f55\u5b97\u8f89\u9053\uff1a\u201c\u90a3\u53ef\u80fd\u662f\u4ed6\u5bf9\u4f60\u4eec\u5d02\u5c71\u4e09\u96c4\u7684\u4eba\u7279\u522b\u8d4f\u8bc6\u3002\u201d \u3000\u3000\u738b\u5927\u5eb7\u54fc\u4e86\u58f0\u9053\uff1a\u201c\u53bb\u4f60\u7684\uff01\u4ffa\u738b\u5927\u5eb7\u662f\u4e2a\u7c97\u4eba\uff0e\u4e5f\u77e5\u9053\u957f\u5e7c\u6709\u5e8f\uff0c\u5c31\u7b97\u970d\u5143\u4f3d\u770b\u4e0a\u5d02 \u5c71\u4e09\u96c4\uff0c\u4e5f\u8be5\u5148\u628a\u4ee4\u724c\u53d1\u7ed9\u8001\u5927\uff0c\u518d\u53d1\u7ed9\u8001\u4e8c\uff0e\u6700\u540e\u624d\u80fd\u8f6e\u5230\u4ffa\u738b\u5927\u5eb7\u5934\u4e0a\uff0e\u5bf9\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u522b\u8bb2\u90a3\u4e48\u591a\u7406\u7531\uff0e\u73b0\u5728\u53ea\u95ee\u4f60\u51c6\u5907\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u738b\u5927\u5eb7\u9053\uff1a\u201c\u65e2\u7136\u4f60\u548c\u949f\u526f\u76df\u4e3b\u90fd\u4e0d\u53bb\uff0c\u4ffa\u5f53\u7136\u4e5f\u4e0d\u60f3\u53bb\u3002\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u9053\uff1a\u201c\u6211\u529d\u4f60\u8fd8\u662f\u53bb\u5f97\u597d\u3002\u201d \u3000\u3000\u738b\u5927\u5eb7\u4e24\u773c\u4e00\u77aa\u9053\uff1a\u201c\u4f60\u4eec\u4e0d\u53bb\uff0e\u4e3a\u4ec0\u4e48\u504f\u504f\u8981\u4ffa\u53bb\uff1f\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u6b63\u8272\u9053\uff1a\u201c\u949f\u526f\u76df\u4e3b\u548c\u6211\u4e0d\u53bb\uff0c\u5f88\u53ef\u80fd\u5df2\u5f15\u8d77\u970d\u5143\u4f3d\u7684\u7591\u5fc3\uff0c\u5982\u679c\u4f60\u518d\u4e0d\u53bb\uff0c\u5c06 \u6765\u5bf9\u54b1\u4eec\u5fc5\u5b9a\u5927\u5927\u4e0d\u5229\uff0e\u201d \u3000\u3000\u7b11\u9762\u4f5b\u9c8d\u8d85\u4e5f\u5e2e\u8154\u9053\uff1a\u201c\u8001\u4e09\u5c31\u53bb\u4e00\u8d9f\uff01\u90a3\u79cd\u5730\u65b9\u5f88\u591a\u4eba\u60f3\u53bb\u90fd\u53bb\u4e0d\u6210\uff0e\u4f55\u51b5\u53c8\u4e0d\u9700\u54b1 \u4eec\u638f\u8170\u5305\u82b1\u94b1\u3002\u201d \u3000\u3000\u738b\u5927\u5eb7\u773c\u73e0\u5b50\u8f6c\u4e86\u51e0\u8f6c\u9053\uff1a\u201c\u4ffa\u62a2\u5728\u4f60\u8001\u5927\u524d\u9762\uff0e\u4f60\u4e0d\u5403\u918b\uff1f\u201d \u3000\u3000\u9c8d\u8d85\u7b11\u9053\uff1a\u201c\u81ea\u5bb6\u4eba\u5403\u4ec0\u4e48\u918b\uff1f\u201d \u3000\u3000\u738b\u5927\u5eb7\u7ec8\u4e8e\u88ab\u8bf4\u52a8\u4e86\uff0c\u7ad9\u8d77\u8eab\u6536\u8d77\u4ee4\u724c\u9053\uff1a\u201c\u65e2\u7136\u4f60\u4eec\u5927\u5bb6\u90fd\u8fd9\u4e48\u8bf4\uff0c\u4ffa\u5c31\u53bb\u8bd5\u8bd5 \u770b\uff01\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u9053\uff1a\u201c\u6211\u8fd8\u8981\u63d0\u9192\u4f60\u4e00\u53e5\uff0c\u970d\u5143\u4f3d\u8be1\u8ba1\u591a\u7aef\uff0c\u90a3\u5973\u4eba\u4e5f\u662f\u2014\u809a\u5b50\u574f\u6c34\uff0e\u6700\u597d\u8981\u63d0 \u9ad8\u8b66\u89c9\uff0e\u514d\u5f97\u5403\u4e8f\u4e0a\u5f53\u3002\u201d \u3000\u3000\u738b\u5927\u5eb7\u54a7\u54a7\u5634\u9053\uff1a\u201c\u4ed6\u5976\u5976\u7684\uff0c\u4ffa\u4e5f\u4e0d\u662f\u4e09\u4e24\u5c81\u5b69\u5b50\uff0c\u5927\u98ce\u5927\u6d6a\u89c1\u591a\u4e86\uff0c\u4e0d\u4fe1\u4f1a\u683d\u5728\u4e2a \u81ed\u5a18\u4eec\u624b\u91cc\uff01\u201d \u3000\u3000\u9b3c\u8bf8\u845b\u4e5f\u9053\uff1a\u201c\u4e0d\u7ba1\u5982\u4f55\uff0c\u8001\u4e09\u8981\u89c1\u673a\u884c\u4e8b\u3002\u201d \u3000\u3000\u738b\u5927\u5eb7\u88ab\u5f04\u5f97\u6709\u4e9b\u4e0d\u8010\u70e6\uff0c\u543c\u9053\uff1a\u201c\u4f60\u4eec\u82e5\u518d\u4e03\u5634\u516b\u820c\uff0e\u4ffa\u4e0d\u53bb\u5566\uff01\u201d \u3000\u3000\u7fa4\u96c4\u679c\u7136\u8c01\u90fd\u4e0d\u518d\u8bf4\u8bdd\u3002 \u3000\u3000\u738b\u5927\u5eb7\u628a\u4ee4\u724c\u5f80\u6000\u91cc\u4e00\u63e3\uff0c\u5583\u5583\u7740\u9053\uff1a\u201c\u4ed6\u5976\u5976\u7684\uff01\u970d\u5143\u4f3d\u90a3\u8001\u5c0f\u5b50\u66fe\u8bf4\u8fc7\uff0c\u4ee4\u724c\u662f\u5934 \u4e00\u5929\u6216\u5f53\u5929\u4e0a\u5348\u9001\u6765\uff0c\u8fd9\u6b21\u7ed9\u4ffa\u8001\u738b\uff0c\u5374\u662f\u5403\u8fc7\u665a\u996d\u624d\u9001\u6765\uff0e\u7b49\u4e8e\u53eb\u4ffa\u5c4e\u9876\u5230\u809b\u95e8\u624d\u8fdb\u8305 \u623f\u3002\u201d \u3000\u3000\u9c8d\u8d85\u7b11\u9053\uff1a\u201c\u8fd9\u6837\u6700\u597d\u5566\uff01\u82e5\u65e9\u4e0a\u63a5\u5230\u4ee4\u724c\uff0c\u8981\u7b49\u5230\u5929\u665a\u624d\u80fd\u8fdb\u53bb\uff0c\u5c82\u4e0d\u7b49\u4e8e\u540a\u4eba\u80c3 \u53e3\u3002\u4f60\u5c31\u662f\u8981\u6d17\u6d17\u6fa1\u6362\u5957\u65b0\u8863\u670d\u518d\u53bb\uff0c\u73b0\u5728\u4e5f\u6765\u5f97\u53ca\u3002\u201d \u3000\u3000\u738b\u5927\u5eb7\u51b7\u54fc\u58f0\u9053\uff1a\u201c\u53bb\u89c1\u90a3\u79cd\u8d31\u5973\u4eba\uff0e\u8eab\u4e0a\u8d8a\u810f\u8d8a\u597d\uff0c\u8d8a\u81ed\u8d8a\u76f8\u914d\u3002\u201d \u3000\u3000\u4ed6\u521a\u8d70\u51fa\u4e24\u6b65\uff0c\u53ea\u542c\u4f55\u5b97\u8f89\u9053\uff1a\u201c\u90a3\u4ee4\u724c\u62ff\u5728\u624b\u4e0a\u6ca1\u6709\uff1f\u201d \u3000\u3000\u738b\u5927\u5eb7\u9053\uff1a\u201c\u4ffa\u63e3\u5728\u6000\u91cc\u8ddf\u62ff\u5728\u624b\u4e0a\u6709\u4ec0\u4e48\u4e24\u6837\uff0e\u4e16\u4e0a\u602a\u4e8b\u771f\u591a\uff01\u201d \u3000\u3000\u4f17\u4eba\u542c\u4e0d\u61c2\u4ed6\u6700\u540e\u4e00\u53e5\u8bdd\u662f\u4ec0\u4e48\u610f\u601d\uff0c\u6b63\u8981\u8be2\u95ee\uff0c\u738b\u5927\u5eb7\u5df2\u81ea\u5df1\u63a5\u9053\uff1a\u201c\u54b1\u53ea\u542c\u8bf4\u4ee4\u724c \u662f\u8c03\u5175\u9063\u5c06\u7684\uff0c\u73b0\u5728\u4ee4\u724c\u53d8\u6210\u4e86\u5ad6\u5973\u4eba\u7684\uff0c\u4f60\u4eec\u8bf4\u662f\u4e0d\u662f\u602a\u4e8b\uff1f\u9664\u4e86\u970d\u5143\u4f3d\u90a3\u8001\u5c0f\u5b50\uff0c\u8c01\u80fd \u60f3\u51fa\u8fd9\u79cd\u6b6a\u70b9\u5b50\uff01\u201d \u3000\u3000\u738b\u5927\u5eb7\u6447\u6447\u6446\u6446\u7684\u6765\u5230\u4e1c\u6d77\u9662\u95e8\u53e3\uff0c\u8fdc\u8fdc\u5c31\u53d1\u73b0\u95e8\u95e8\u8fd8\u6709\u4e2a\u7ad9\u5c97\u7684\u55bd\u5175\uff0c\u4e0d\u89c9\u6697\u6697\u9a82 \u9053\uff1a\u201c\u4ed6\u5976\u5976\u7684\uff01\u7a91\u5b50\u95e8\u53e3\u8fd8\u8981\u7ad9\u5c97\u653e\u54e8\uff0e\u53c8\u662f\u602a\u4e8b\u4e00\u4ef6\uff01\u201d \u3000\u3000\u90a3\u55bd\u5175\u624b\u62ff\u4e00\u67c4\u5355\u5200\uff0e\u795e\u6c14\u6d3b\u73b0\uff0c\u5728\u8fd9\u79cd\u5730\u65b9\u653e\u54e8\u8fd8\u8000\u6b66\u626c\u5a01\uff0c\u7167\u6837\u4e5f\u662f\u4ef6\u602a\u4e8b\u3002 \u3000\u3000\u738b\u5927\u5eb7\u770b\u4e5f\u4e0d\u770b\u90a3\u55bd\u5175\uff0e\u6602\u9996\u9614\u6b65\u4fbf\u5f80\u91cc\u8d70\u3002 \u3000\u3000\u90a3\u55bd\u5175\u5012\u9887\u8d1f\u8d23\u5c3d\u804c\uff0c\u8fde\u5fd9\u62e6\u4e86\u8fc7\u6765\u62b1\u5200\u4e00\u793c\u9053\uff1a\u201c\u738b\u5934\u9886\u8981\u5230\u54ea\u91cc\u53bb\uff1f\u201d \u3000\u3000\u738b\u5927\u5eb7\u94dc\u94c3\u822c\u7684\u4e24\u773c\u4e00\u77aa\u9053\uff1a\u201c\u8fd9\u91cc\u662f\u4e0d\u662f\u4e1c\u6d77\u9662\uff1f\u201d \u3000\u3000\u90a3\u55bd\u5175\u62ac\u624b\u6307\u4e86\u6307\u5927\u95e8\u4e0a\u65b9\u7684\u533e\u989d\u9053\uff1a\u201c\u90a3\u4e0d\u660e\u660e\u5199\u5728\u4e0a\u9762\uff0c\u738b\u5934\u9886\u4f55\u5fc5\u518d\u95ee\uff1f\u201d \u3000\u3000\u738b\u5927\u5eb7\u54a6\u4e86\u58f0\u9053\uff1a\u201c\u738b\u516b\u86cb\uff01\u4f60\u6562\u60c5\u4ee5\u4e3a\u4ffa\u8001\u738b\u4e0d\u8bc6\u5b57\uff1f\u201d \u3000\u3000\u90a3\u55bd\u5175\u6253\u4e2a\u54c6\u55e6\u9053\uff1a\u201c\u5c0f\u7684\u4e0d\u6562\uff01\u201d \u3000\u3000\u738b\u5927\u5eb7\u9a82\u9053\uff1a\u201c\u4f60\u660e\u660e\u770b\u5230\u4ffa\u8001\u738b\u8981\u5f80\u91cc\u9762\u8d70\uff0c\u5374\u504f\u8981\u62e6\u963b\uff0e\u53ef\u662f\u6545\u610f\u8981\u627e\u9ebb\u70e6\uff1f\u201d \u3000\u3000\u90a3\u55bd\u5175\u867d\u5df2\u5934\u76ae\u53d1\u9ebb\uff0e\u8fd8\u662f\u4e0d\u80fd\u8be5\u7ba1\u7684\u4e0d\u7ba1\uff0c\u55eb\u5685\u7740\u9053\uff1a\u201c\u738b\u5934\u9886\u65e2\u7136\u8981\u8fdb\u53bb\uff0c\u8bf7\u62ff\u51fa \u4ee4\u724c\u6765\u7ed9\u5c0f\u7684\u67e5\u9a8c\u4e00\u4e0b\u3002\u201d \u3000\u3000\u738b\u5927\u5eb7\u672c\u6765\u4e0d\u60f3\u53d6\u51fa\u4ee4\u724c\uff0c\u4f46\u53c8\u61d2\u5f97\u548c\u5bf9\u65b9\u7f57\u55e6\uff0c\u53ea\u597d\u63a2\u624b\u5165\u6000\uff0c\u53d6\u51fa\u4ee4\u724c\u9053\uff1a\u201c\u738b\u516b \u86cb\uff0e\u778e\u4e86\u4f60\u7684\u72d7\u773c\uff01\u8fd9\u4e0d\u662f\u4ee4\u724c\u96be\u9053\u8fd8\u662f\u4f60\u5bb6\u7684\u7956\u5b97\u724c\u4f4d\uff1f\u201d \u3000\u3000\u90a3\u55bd\u5175\u88ab\u9a82\u5f97\u867d\u5df2\u706b\u5192\u4e09\u4e08\uff0c\u8fd8\u662f\u4e0d\u5f97\u4e0d\u5f3a\u5fcd\u7740\u6027\u5b50\u9053\uff1a\u201c\u65e2\u7136\u6709\u4ee4\u724c\uff0c\u5c31\u8be5\u65e9\u4eae\u51fa\u6765 \u7ed9\u5c0f\u7684\u770b\uff0c\u738b\u5934\u9886\u8bf7\u5427\uff01\u201d \u3000\u3000\u738b\u5927\u5eb7\u9053\uff1a\u201c\u5e9f\u8bdd\uff01\u8001\u5b50\u662f\u970d\u76df\u4e3b\u8bf7\u7684\uff0c\u4f60\u591f\u4ec0\u4e48\u8d44\u683c\u8bf7\uff01\u201d \u3000\u3000\u8fdb\u5165\u5927\u95e8\uff0c\u4fbf\u662f\u2014\u4e2a\u8303\u56f4\u4e0d\u5c0f\u7684\u5929\u4e95\uff0e\u5899\u8fb9\u8fd8\u6446\u7740\u4e0d\u5c11\u76c6\u666f\uff0e\u90a3\u4e24\u540d\u88ab\u9609\u8fc7\u7684\u55bd\u5175\uff0c\u6b63 \u5728\u6d47\u82b1\u9664\u8349\u3002 \u3000\u3000\u5176\u4e2d\u4e00\u540d\u53eb\u738b\u4e09\u7684\u5954\u8fc7\u6765\u65bd\u4e86\u4e00\u793c\u9053\uff1a\u201c\u738b\u5934\u9886\u53ef\u662f\u6765\u8fc7\u591c\u7684\uff1f\u201d \u3000\u3000\u8fd9\u540d\u738b\u4e09\u5012\u662f\u6001\u5ea6\u4eb2\u5207\uff0c\u51fa\u8bed\u548c\u6c14\uff0c\u5f88\u6709\u70b9\u201c\u592a\u76d1\u201d\u6a21\u6837\u3002 \u3000\u3000\u738b\u5927\u5eb7\u626b\u89c6\u4e86\u5929\u4e95\u5404\u5904\u4e00\u773c\u9053\uff1a\u201c\u4ffa\u8fd8\u4ee5\u4e3a\u8fd9\u91cc\u5f88\u810f\uff0c\u539f\u6765\u6574\u7406\u5f97\u86ee\u5e72\u51c0\u7684\u3002\u201d \u3000\u3000\u738b\u4e09\u966a\u7740\u7b11\u8138\u9053\uff1a\u201c\u4ed9\u5b50\u4f4f\u7684\u5730\u65b9\uff0c\u5f53\u7136\u8981\u6574\u7406\u5f97\u5e72\u5e72\u51c0\u51c0\u3002\u201d \u3000\u3000\u738b\u5927\u5eb7\u4e5f\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4e2a\u5728\u8fd9\u91cc\u5f53\u5dee\uff0c\u8f9b\u82e6\u5566\uff01\u201d \u3000\u3000\u738b\u4e09\u9053\uff1a\u201c\u6ca1\u4ec0\u4e48\uff0c\u8fd9\u91cc\u4e8b\u60c5\u4e0d\u591a\uff0c\u5c0f\u7684\u4eec\u90fd\u6e05\u95f2\u5f97\u5f88\u3002\u201d \u3000\u3000\u738b\u5927\u5eb7\u9053\uff1a\u201c\u4ffa\u662f\u8bf4\u4f60\u4eec\u4e0b\u9762\u8f9b\u82e6\u4e86\u3002\u201d \u3000\u3000\u738b\u4e09\u987f\u65f6\u9762\u5b54\u618b\u5f97\u6709\u5982\u732a\u809d\uff0e\u54a7\u54a7\u5634\u9053\uff1a\u201c\u6ca1\u5173\u7cfb\uff0c\u5c0f\u7684\u4eec\u4e24\u4e2a\u5bb6\u4e61\u90fd\u6709\u5144\u5f1f\uff0c\u603b\u7b97\u65ad \u4e0d\u4e86\u9999\u706b\u3002\u201d \u3000\u3000\u738b\u5927\u5eb7\u9053\uff1a\u201c\u59d1\u5a18\u5462\uff1f\u201d \u3000\u3000\u738b\u4e09\u5403\u4e86\u2014\u60ca\uff0c\u5fd9\u9053\uff1a\u201c\u738b\u5934\u9886\u5343\u4e07\u522b\u53eb\u59d1\u5a18\uff01\u201d \u3000\u3000\u201c\u4e0d\u53eb\u5979\u59d1\u5a18\u53eb\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u8981\u53eb\u4ed9\u5b50\u3002\u201d \u3000\u3000\u201c\u54ea\u6709\u966a\u7537\u4eba\u7761\u89c9\u7684\u4ed9\u5b50\uff0e\u201d \u3000\u3000\u201c\u90a3\u662f\u53e6\u4e00\u56de\u4e8b\uff1f\u201d \u3000\u3000\u738b\u5927\u5eb7\u968f\u5373\u4e3e\u6b65\u4e0a\u697c\u3002 \u3000\u3000\u738b\u4e09\u4e5f\u7acb\u523b\u8ddf\u5728\u540e\u9762\u3002 \u3000\u3000\u738b\u5927\u5eb7\u9053\uff1a\u201c\u4f60\u8ddf\u6765\u505a\u4ec0\u4e48\uff1f\u4e0b\u9762\u5df2\u7ecf\u6ca1\u6709\u4e86\uff0c\u6709\u4ec0\u4e48\u597d\u770b\u7684\uff1f\u201d \u3000\u3000\u738b\u4e09\u5e72\u54b3\u4e86\u58f0\u9053\uff1a\u201c\u5c0f\u7684\u4e0a\u53bb\u7ed9\u738b\u5934\u9886\u5012\u8336\u3002\u201d \u3000\u3000\u738b\u5927\u5eb7\u9053\uff1a\u201c\u4ed9\u5b50\u8fde\u89c9\u90fd\u80fd\u966a\u4ffa\u7761\uff0c\u96be\u9053\u5c31\u4e0d\u80fd\u7ed9\u4ffa\u5012\u8336\uff1f\u201d \u3000\u3000\u738b\u4e09\u9053\uff1a\u201c\u8fd9\u662f\u5c0f\u7684\u4efd\u5185\u4e8b\uff0c\u4e0d\u80fd\u52b3\u52a8\u4ed9\u5b50\u3002\u201d \u3000\u3000\u201c\u53bb\u4f60\u7684\uff0c\u4e0d\u5fc5\u4e0a\u6765\u3002\u5979\u4e0d\u5012\u4ffa\u5c31\u81ea\u5df1\u5012\uff0c\u5979\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u201c\u738b\u5934\u9886\u4e0a\u4e86\u697c\u5c31\u770b\u5230\u4e86\u3002\u201d \u3000\u3000\u738b\u5927\u5eb7\u767b\u4e0a\u697c\u68af\uff0c\u53ea\u89c1\u4e0a\u65b9\u9ad8\u60ac\u7740\u4e00\u76cf\u5927\u578b\u6d41\u82cf\u5bab\u706f\uff0c\u706f\u9762\u4e0a\u7ed8\u7684\u662f\u4e00\u9f99\u4e00\u51e4\uff0c\u53e6\u4e00\u9762 \u5219\u662f\u4e00\u5bf9\u72ee\u5b50\u5728\u6eda\u7ee3\u7403\u3002 \u3000\u3000\u623f\u95e8\u6709\u4e8c\u4e09\u5904\uff0e\u5de6\u8fb9\u623f\u95e8\u4e0a\u4e5f\u6302\u7740\u4e00\u76cf\u5bab\u706f\uff0c\u662f\u7528\u7eff\u7eb1\u7cca\u6210\uff0c\u6210\u4e3a\u540d\u7b26\u5176\u5b9e\u7684\u7eff\u706f\u6237\u3002 \u3000\u3000\u738b\u5927\u5eb7\u5e76\u4e0d\u6e05\u695a\u7eff\u706f\u4ee3\u8868\u4ec0\u4e48\u610f\u601d\uff0c\u56e0\u4e3a\u90a3\u65f6\u8fd8\u6ca1\u6709\u8fd9\u79cd\u89c4\u77e9\uff0e\u4e0d\u8fc7\u65e2\u7136\u53ea\u6709\u5de6\u8fb9\u623f\u95e8 \u6709\u706f\uff0c\u8bb8\u5c0f\u65e6\u5fc5\u7136\u81ea\u5728\u91cc\u9762\u3002"}),showOnHomepage:!0},{name:"\u7b2c\u5341\u4e94\u56de\u3000\u82f1\u96c4\u62d2\u8272",content:(0,t.jsx)(t.Fragment,{children:"\u63a8\u5f00\u95e8\uff0c\u679c\u7136\u91cc\u9762\u706f\u5149\u660e\u4eae\uff0c\u623f\u5185\u5e03\u7f6e\u5f97\u98ce\u5149\u65d6\u65ce\uff0c\u9887\u6709\u4e00\u756a\u8bf1\u4eba\u6c14\u606f\u3002 \u3000\u3000\u5730\u4e0a\u6ca1\u4eba\uff0e\u5f80\u5e8a\u4e0a\u770b\u53bb\uff0c\u8bb8\u5c0f\u65e6\u6b63\u62e5\u88ab\u4f9d\u58c1\u800c\u5750\u3002 \u3000\u3000\u8bb8\u5c0f\u65e6\u2014\u89c1\u6765\u7684\u662f\u4e2a\u8eab\u9ad8\u516b\u5c3a\uff0c\u8138\u8272\u8d64\u7ea2\uff0c\u6d53\u7709\u73af\u773c\uff0c\u77ed\u987b\u5982\u6208\u7684\u83bd\u6c49\uff0c\u5148\u662f\u6123\u4e86\u4e00 \u4e0b\uff0c\u4f46\u5f88\u5feb\u4fbf\u8ba4\u51fa\u6b64\u4eba\u662f\u8c01\u3002 \u3000\u3000\u5979\u5728\u592a\u539f\u9633\u66f2\u5ba2\u6808\u4fbf\u89c1\u8fc7\u738b\u5927\u5eb7\uff0c\u6765\u5230\u8ff7\u8e2a\u8c37\u540e\uff0c\u5728\u805a\u4e49\u5385\u4e5f\u66fe\u4e24\u5ea6\u89c1\u8fc7\uff0e\u5374\u6ca1\u6599\u5230\u970d \u5143\u4f3d\u4f1a\u628a\u4eca\u665a\u7684\u4ee4\u724c\u4ea4\u7ed9\u4ed6\u3002 \u3000\u3000\u738b\u5927\u5eb7\u563f\u563f\u7b11\u4e86\u51e0\u58f0\uff0e\u4e0d\u542d\u4e0d\u54c8\u7684\u5c31\u5728\u5e8a\u8fb9\u7684\u6905\u4e0a\u5750\u4e0b\u3002 \u3000\u3000\u8bb8\u5c0f\u65e6\u76b1\u7740\u67f3\u7709\u9053\uff1a\u201c\u600e\u4e48\u662f\u4f60\uff1f\u201d \u3000\u3000\u738b\u5927\u5eb7\u5658\u4e86\u5658\u5634\u9053\uff1a\u201c\u662f\u6211\u4e0d\u884c\u5417\uff1f\u970d\u76df\u4e3b\u53eb\u4ffa\u6765\uff0c\u4ffa\u4ee5\u670d\u4ece\u547d\u4ee4\u4e3a\u5929\u804c\uff0c\u600e\u6562\u4e0d \u6765\uff1f\u201d \u3000\u3000\u8bb8\u5c0f\u65e6\u53c8\u76b1\u4e86\u76b1\u7709\u9053\uff1a\u201c\u53ef\u6709\u4ee4\u724c\uff1f\u201d \u3000\u3000\u738b\u5927\u5eb7\u987a\u624b\u628a\u4ee4\u724c\u638f\u51fa\u6765\u5728\u706f\u4e0b\u4e00\u4eae\u9053\uff1a\u201c\u8fd9\u4ed6\u5988\u7684\u8fd8\u662f\u5047\u7684\uff1f\u201d \u3000\u3000\u8bb8\u5c0f\u65e6\u9053\uff1a\u201c\u4f60\u8bf4\u8bdd\u600e\u4e48\u8fd9\u4e48\u96be\u542c\uff1f\u201d \u3000\u3000\u738b\u5927\u5eb7\u9053\uff1a\u201c\u5acc\u96be\u542c\u5c31\u5835\u4e0a\u8033\u6735\uff01\u201d \u3000\u3000\u201c\u4f60\u662f\u505a\u4ec0\u4e48\u6765\u7684\uff1f\u201d \u3000\u3000\u201c\u5e9f\u8bdd\uff01\u5f53\u7136\u662f\u7761\u89c9\u6765\u7684\u3002\u201d \u3000\u3000\u201c\u90a3\u4f60\u5c31\u7761\u5427\uff01\u6211\u628a\u5e8a\u8ba9\u7ed9\u4f60\u3002\u201d \u3000\u3000\u201c\u4f60\u51c6\u5907\u7761\u54ea\u91cc\uff1f\u201d \u3000\u3000\u201c\u90a3\u662f\u6211\u7684\u4e8b\uff0c\u7528\u4e0d\u7740\u4f60\u7ba1\u3002\u201d \u3000\u3000\u201c\u5c82\u6709\u6b64\u7406\uff01\u4f60\u4eca\u665a\u5c31\u662f\u966a\u4ffa\u7684\u3002\u5982\u679c\u6ca1\u4f60\u966a\uff0c\u4ffa\u4f55\u5fc5\u5230\u8fd9\u79cd\u5730\u65b9\u6765\uff1f\u201d \u3000\u3000\u201c\u4f60\u628a\u8fd9\u91cc\u5f53\u6210\u4ec0\u4e48\u5730\u65b9\uff1f\u201d \u3000\u3000\u201c\u8fd8\u7528\u4ffa\u8bb2\u5417\uff1f\u201d \u3000\u3000\u201c\u4f60\u4e5f\u4e0d\u6492\u6ce1\u5c3f\u7167\u7167\uff01\u201d \u3000\u3000\u201c\u4ffa\u65e9\u5c31\u7167\u5566\uff01\u5c0f\u767d\u8138\u662f\u7ee3\u82b1\u6795\u5934\uff0c\u53ea\u6709\u4ffa\u8fd9\u6837\u7684\u624d\u7ba1\u7528\u3002\u201d \u3000\u3000\u738b\u5927\u5eb7\u5c31\u8fd9\u4e48\u4e00\u53e5\u8bdd\uff0e\u7adf\u7136\u771f\u7684\u52fe\u52a8\u4e86\u8bb8\u5c0f\u65e6\u7684\u5fc3\u3002 \u3000\u3000\u539f\u6765\u8bb8\u5c0f\u65e6\u662f\u4e2a\u591c\u591c\u79bb\u4e0d\u5f00\u7537\u4eba\u7684\u5973\u4eba\uff0c\u5979\u4e4b\u6240\u4ee5\u5fc3\u7518\u60c5\u613f\u5728\u4e1c\u6d77\u9662\u63a5\u5f85\u7fa4\u96c4\uff0e\u53e3\u5934\u4e0a \u8bb2\u7684\u662f\u727a\u7272\u5c0f\u6211\uff0c\u5b9e\u9645\u4e0a\u6b63\u662f\u6210\u5168\u5c0f\u6211\u3002 \u3000\u3000\u504f\u504f\u5979\u81ea\u4ece\u6765\u5230\u8ff7\u8e2a\u8c37\uff0c\u773c\u89c1\u6709\u90a3\u4e48\u591a\u96c4\u58ee\u5a01\u6b66\u7684\u7537\u4eba\uff0e\u5374\u4e00\u4e2a\u4e5f\u5f04\u4e0d\u5230\u624b\uff0c\u53ea\u6709\u970d\u5143 \u4f3d\u66fe\u5149\u987e\u8fc7\u5979\u4e24\u6b21\u3002 \u3000\u3000\u4f46\u90a3\u4e5f\u662f\u676f\u6c34\u8f66\u85aa\uff0c\u65e0\u6d4e\u4e8e\u4e8b\u3002\u597d\u4e0d\u5bb9\u6613\u60f3\u51fa\u4e2a\u201c\u51a0\u5195\u5802\u7687\u201d\u7684\u7406\u7531\u6210\u7acb\u4e86\u4e1c\u6d77\u9662\uff0e\u524d \u4e24\u665a\u7684\u201c\u5ba2\u6237\u201d\u7adf\u53c8\u6ca1\u6765\uff0c\u4f7f\u5f97\u5979\u72ec\u5b88\u7a7a\u95fa\uff0c\u591c\u4ee5\u8fbe\u65e6\uff0c\u597d\u4e0d\u96be\u8010\u3002 \u3000\u3000\u60f3\u5230\u8fd9\u91cc\uff0c\u53c8\u60f3\u8d77\u738b\u5927\u5eb7\u65b9\u624d\u90a3\u53e5\u201c\u5c0f\u767d\u8138\u662f\u7ee3\u82b1\u6795\u5934\uff0c\u53ea\u6709\u4ffa\u624d\u7ba1\u7528\u201d\u90a3\u53e5\u8bdd\uff0c\u4fbf\u5f88 \u5feb\u5bf9\u738b\u5927\u5eb7\u6765\u4e86\u5174\u8da3\u3002 \u3000\u3000\u53cd\u6b63\u4e0a\u5e8a\u540e\u5439\u7184\u4e86\u706f\uff0e\u7ba1\u4ed6\u4ec0\u4e48\u8138\uff0c\u53ea\u8981\u7ba1\u7528\u5c31\u597d\u3002 \u3000\u3000\u738b\u5927\u5eb7\u4e0d\u77e5\u8bb8\u5c0f\u65e6\u5728\u60f3\u4ec0\u4e48\uff0c\u4ecd\u662f\u5750\u5728\u90a3\u91cc\u4e0d\u52a8\u3002 \u3000\u3000\u8bb8\u5c0f\u65e6\u4e5f\u659c\u7740\u5a9a\u773c\u4e00\u7b11\uff0c\u968f\u5373\u63a8\u5f00\u88ab\u5b50\u4e0b\u4e86\u5e8a\uff0e \u3000\u3000\u738b\u5927\u5eb7\u9053\uff1a\u201c\u4f60\u8981\u505a\u4ec0\u4e48\uff0c\u662f\u4e0d\u662f\u8981\u8d76\u4ffa\u8d70\uff1f\u201d \u3000\u3000\u8bb8\u5c0f\u65e6\u54af\u54af\u7b11\u9053\uff1a\u201c\u65e2\u7136\u4f60\u6301\u6709\u970d\u76df\u4e3b\u7684\u4ee4\u724c\uff0c\u6211\u600e\u80fd\u8d76\u4f60\u8d70\u3002\u201d \u3000\u3000\u738b\u5927\u5eb7\u7b11\u9053\uff1a\u201c\u90a3\u662f\u4f60\u5df2\u7ecf\u770b\u4e0a\u4ffa\u8001\u738b\u4e86\uff1f\u201d \u3000\u3000\u8bb8\u5c0f\u65e6\u5a9a\u773c\u53c8\u662f\u4e00\u629b\u9053\uff1a\u201c\u4f60\u867d\u7136\u4e0d\u662f\u5c0f\u767d\u8138\uff0c\u603b\u8fd8\u662f\u6761\u82f1\u96c4\u597d\u6c49\uff0c\u6211\u656c\u7684\u5c31\u662f\u82f1\u96c4\u597d \u6c49\u3002\u201d \u3000\u3000\u5979\u8bf4\u7740\u63d0\u8d77\u8336\u58f6\uff0c\u5012\u4e86\u676f\u8336\uff0c\u53cc\u624b\u6367\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u738b\u5927\u5eb7\u63a5\u8fc7\u8336\uff0e\u53c8\u653e\u56de\u684c\u4e0a\u9053\uff1a\u201c\u4f60\u5bf9\u4ffa\u8001\u738b\u8fd8\u771f\u4e0d\u9519\uff01\u201d \u3000\u3000\u201c\u4f60\u600e\u4e48\u77e5\u9053\u6211\u5bf9\u4f60\u4e0d\u9519\uff1f\u201d \u3000\u3000\u201c\u65b9\u624d\u5728\u697c\u4e0b\uff0e\u90a3\u4e2a\u53eb\u738b\u4e09\u7684\u8bf4\u4f60\u4e0d\u80af\u7ed9\u5ba2\u4eba\u5012\u8336\uff0c\u4ed6\u62a2\u7740\u8981\u4e0a\u6765\u5012\u3002\u201d \u3000\u3000\u201c\u4ed6\u4e3a\u4ec0\u4e48\u6ca1\u6765\uff1f\u201d \u3000\u3000\u201c\u662f\u4ffa\u4e0d\u51c6\u4ed6\u6765\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u4e0d\u51c6\u4ed6\u6765\uff1f\u201d \u3000\u3000\u201c\u4ffa\u5bf9\u4ed6\u8bf4\uff1a\u2018\u4ed9\u5b50\u8fde\u89c9\u90fd\u80af\u966a\u4ffa\u7761\uff0c\u600e\u4f1a\u4e0d\u80af\u5012\u8336\uff1f\u2019\u73b0\u5728\u4f60\u679c\u7136\u4e3b\u52a8\u5012\u4e86\u8336\uff0c\u53ef\u89c1 \u4ffa\u7684\u8bdd\u4e0d\u5dee\u3002\u201d \u3000\u3000\u201c\u90a3\u4f60\u5c31\u559d\u8336\u5427\uff01\u201d \u3000\u3000\u201c\u5f85\u4f1a\u513f\u4e0a\u4e86\u5e8a\u518d\u559d\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u73b0\u5728\u4e0d\u559d\uff1f\u201d \u3000\u3000\u201c\u73b0\u5728\u4e0d\u7d2f\uff0c\u4e5f\u4e0d\u6e34\u3002\u5f85\u4f1a\u513f\u529e\u8fc7\u4e8b\uff0e\u90a3\u65f6\u624d\u771f\u6b63\u9700\u8981\u559d\u676f\u8336\u89e3\u6e34\u3002\u201d \u3000\u3000\u539f\u6765\u738b\u5927\u5eb7\u867d\u662f\u4e2a\u7c97\u4eba\uff0c\u5374\u7c97\u4e2d\u6709\u7ec6\uff0c\u5c24\u5176\u4e34\u6765\u65f6\u4e0d\u5c11\u4eba\u90fd\u8981\u4ed6\u63d0\u9ad8\u8b66\u89c9\uff0c\u4e07\u4e00\u8336\u6c34\u4e2d \u653e\u6709\u4ec0\u4e48\u836f\u7269\uff0c\u5c82\u4e0d\u4e0a\u5f53\uff1f\u6240\u4ee5\u4ed6\u624d\u51b3\u5b9a\u4e0d\u559d\u3002 \u3000\u3000\u6b64\u65f6\uff0c\u8bb8\u5c0f\u65e6\u65e9\u5df2\u6625\u5fc3\u8361\u6f3e\uff0c\u800c\u4e14\u751f\u7406\u4e0a\u67d0\u4e00\u90e8\u5206\u8d8a\u6765\u8d8a\u201c\u6e34\u201d\uff0c\u81ea\u52a8\u53c8\u56de\u5230\u5e8a\u4e0a\u9053\uff1a \u201c\u65f6\u95f4\u4e0d\u65e9\u5566\uff01\u5439\u7184\u706f\u4e0a\u5e8a\u6765\u5427\u3002\u201d \u3000\u3000\u738b\u5927\u5eb7\u6447\u5934\u9053\uff1a\u201c\u522b\u6025\uff0c\u4ffa\u8fd8\u8981\u5750\u4f1a\u513f\uff0c\u800c\u4e14\u706f\u4e5f\u4e0d\u80fd\u5439\u3002\u201d \u3000\u3000\u8bb8\u5c0f\u65e6\u9053\uff1a\u201c\u6211\u5c31\u6ca1\u89c1\u8fc7\u4f60\u8fd9\u79cd\u7537\u4eba\u3002\u201d \u3000\u3000\u201c\u4ffa\u8fd9\u79cd\u7537\u4eba\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u201c\u522b\u7684\u7537\u4eba\u2014\u8fdb\u623f\u95f4\uff0c\u5fc5\u5b9a\u6025\u7740\u4e0a\u5e8a\uff0e\u54ea\u6709\u5750\u7740\u4e0d\u52a8\u7684\uff01\u201d \u3000\u3000\u201c\u4ffa\u662f\u5728\u517b\u7cbe\u84c4\u9510\uff0c\u5f85\u4f1a\u513f\u4e0a\u4e86\u5e8a\u624d\u6709\u620f\u5934\uff0c\u4f60\u61c2\u4e86\u5427\uff1f\u201d \u3000\u3000\u201c\u65e2\u7136\u4f60\u60f3\u5750\u4f1a\u513f\uff0c\u5c31\u628a\u706f\u5439\u7184\u597d\u4e86\u3002\u201d \u3000\u3000\u201c\u4e0d\u6210\uff0c\u4ffa\u8fd8\u60f3\u770b\u770b\u4f60\u3002\u82e5\u5439\u4e86\u706f\uff0c\u4f60\u8ba9\u4ffa\u770b\u738b\u516b\u8fd8\u662f\u770b\u4e4c\u9f9f\uff1f\u201d \u3000\u3000\u8bb8\u5c0f\u65e6\u867d\u89c9\u5f97\u738b\u5927\u5eb7\u8bf4\u8bdd\u592a\u7c97\uff0c\u5374\u5e76\u672a\u7740\u607c\uff0c\u56e0\u4e3a\uff0c\u5979\u53ea\u5e0c\u671b\u5f85\u4f1a\u513f\u5728\u5e8a\u4e0a\u5f97\u5230\u8865\u507f\uff0e \u53ea\u8981\u5728\u90a3\u65b9\u9762\u80fd\u5f97\u5230\u6ee1\u8db3\uff0c\u88ab\u9a82\u51e0\u53e5\u53c8\u7b97\u4ec0\u4e48\uff1f \u3000\u3000\u5979\u9ed8\u4e86\u4e00\u9ed8\u9053\uff1a\u201c\u4f60\u8981\u770b\uff0c\u73b0\u5728\u5c31\u770b\u5427\uff01\u201d \u3000\u3000\u738b\u5927\u5eb7\u54a7\u4e86\u54a7\u5634\u9053\uff1a\u201c\u4f60\u8863\u670d\u7a7f\u7684\u7d27\u7d27\u5730\uff0c\u6709\u4ec0\u4e48\u597d\u770b\u7684\uff1f\u201d \u3000\u3000\u8bb8\u5c0f\u65e6\u9053\uff1a\u201c\u4f60\u53ef\u4ee5\u770b\u6211\u7684\u8138\uff01\u201d \u3000\u3000\u738b\u5927\u5eb7\u9053\uff1a\u201c\u4ffa\u82e5\u53ea\u662f\u60f3\u770b\u4f60\u7684\u8138\uff0c\u4f55\u5fc5\u5230\u8fd9\u91cc\u6765\u770b\uff1f\u201d \u3000\u3000\u201c\u4f60\u8981\u770b\u54ea\u91cc\uff1f\u8bf4\u5427\uff01\u201d \u3000\u3000\u201c\u4e0a\u6b21\u5728\u5927\u5385\u91cc\uff0e\u4f60\u66fe\u628a\u5976\u5b50\u9732\u51fa\u6765\uff0c\u90a3\u6b21\u4ffa\u773c\u775b\u6709\u6bdb\u75c5\uff0c\u6ca1\u770b\u6e05\u695a\uff0e\u53ef\u4e0d\u53ef\u4ee5\u518d\u9732\u4e00 \u4e0b\uff1f\u201d \u3000\u3000\u8bb8\u5c0f\u65e6\u6beb\u4e0d\u72b9\u8c6b\uff0c\u80f8\u8863\u2014\u626f\uff0c\u9732\u51fa\u4e86\u4e00\u8fb9\u4e73\u623f\u9053\uff1a\u201c\u4f60\u4ed4\u7ec6\u770b\u5427\uff01\u201d \u3000\u3000\u7684\u786e\u5f88\u7f8e\uff01 \u3000\u3000\u738b\u5927\u5eb7\u77a5\u4e86\u4e00\u773c\u9053\uff1a\u201c\u8fd8\u6709\u53e6\u4e00\u8fb9\uff01\u201d \u3000\u3000\u8bb8\u5c0f\u65e6\u53c8\u628a\u53e6\u4e00\u5ea7\u9ad8\u5cf0\u63ed\u5f00\u4e86\u5e55\uff0c\u9053\uff1a\u201c\u597d\u770b\u4e0d\u597d\u770b\uff1f\u201d \u3000\u3000\u738b\u5927\u5eb7\u6447\u5934\u9053\uff1a\u201c\u6ca1\u4ec0\u4e48\u597d\u770b\u7684\uff01\u201d \u3000\u3000\u8bb8\u5c0f\u65e6\u6123\u4e86\u6123\u9053\uff1a\u201c\u4f60\u8fd9\u4eba\u597d\u50cf\u662f\u5757\u6728\u5934\uff0c\u5b9e\u5bf9\u4f60\u8bf4\uff0c\u6211\u4ece\u524d\u4e5f\u5728\u5927\u5ead\u5e7f\u4f17\u8868\u6f14\u8fc7\uff0c\u53ea \u8981\u6211\u4e00\u9732\uff0c\u5c31\u6709\u4e0a\u5343\u5bf9\u773c\u775b\u76ef\u8fc7\u6765\uff0c\u5c31\u50cf\u82cd\u8747\u89c1\u4e86\u871c\u8702\u4e00\u6837\uff0c\u600e\u4e48\u8f6e\u5230\u4f60\u5c31\u6ca1\u6709\u770b\u5934\u4e86 \u5462\uff1f\u201d \u3000\u3000\u738b\u5927\u5eb7\u6b63\u7ecf\u516b\u767e\u5730\u9053\uff1a\u201c\u4ffa\u60f3\u770b\u7684\uff0c\u662f\u5e0c\u5947\u53e4\u602a\u7684\u73a9\u610f\u513f\u3002\u4f60\u90a3\u4e00\u5bf9\u4e1c\u897f\uff0c\u6839\u672c\u6ca1\u4ec0\u4e48 \u7279\u522b\u3002\u4f60\u6709\u7684\uff0e\u522b\u7684\u5973\u4eba\u4e5f\u6709\u3002\u4ffa\u65e9\u5c31\u770b\u8fc7\u4e86\uff0c\u5f53\u7136\u4e0d\u60f3\u518d\u770b\u3002\u201d \u3000\u3000\u8bb8\u5c0f\u65e6\u53ea\u6c14\u5f97\u7259\u6839\u53d1\u75d2\uff0c\u5d29\u7740\u8138\u9053\uff1a\u201c\u4f60\u5230\u5e95\u60f3\u770b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u738b\u5927\u5eb7\u53cd\u800c\u6c89\u4f4f\u4e86\u6c14\uff0c\u6162\u541e\u541e\u5730\u9053\uff1a\u201c\u4f60\u7ed9\u4ffa\u770b\u4ec0\u4e48\uff0c\u4ffa\u5c31\u770b\u4ec0\u4e48\uff0c\u53cd\u6b63\u4ffa\u53ea\u8981\u597d\u770b \u7684\uff0e\u201d \u3000\u3000\u53ea\u542c\u4e00\u9635\u8863\u670d\u626f\u5265\u7684\u58f0\u97f3\uff0c\u63a5\u7740\u8bb8\u5c0f\u65e6\u9053\uff1a\u201c\u7ed9\u4f60\u770b\uff01\u201d \u3000\u3000\u738b\u5927\u5eb7\u62ac\u8d77\u773c\u6765\uff0c\u89c1\u8fd9\u65f6\u8bb8\u5c0f\u65e6\u4e0a\u534a\u8eab\u5df2\u6574\u4e2a\u8d64\u88f8\uff0c\u4e0d\u89c9\u563f\u563f\u4e00\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u4e00\u8eab\u8089\uff0c \u5728\u5c60\u5bb0\u573a\u968f\u65f6\u90fd\u770b\u5f97\u5230\uff0c\u548c\u5265\u4e86\u76ae\u7684\u80a5\u7f8a\u5dee\u4e0d\u591a\uff0c\u4e5f\u6ca1\u4ec0\u4e48\u597d\u770b\u7684\u3002\u201d \u3000\u3000\u8bb8\u5c0f\u65e6\u7b80\u76f4\u6c14\u660f\u4e86\u5934\uff0c\u543c\u9053\uff1a\u201c\u4f60\u672a\u514d\u592a\u8fc7\u4efd\u5566\uff01\u201d \u3000\u3000\u738b\u5927\u5eb7\u9053\uff1a\u201c\u4ffa\u65e9\u5c31\u8bf4\u8fc7\uff0c\u8981\u770b\u70b9\u7279\u522b\u7684\u3002\u4f60\u8fd9\u526f\u8eab\u6750\u5373\u4f7f\u4e0d\u50cf\u5265\u4e86\u76ae\u7684\u7f8a\uff0c\u4e5f\u548c\u4e00\u822c \u5973\u4eba\u6ca1\u4ec0\u4e48\u4e24\u6837\uff0e\u65e2\u7136\u90fd\u4e00\u6837\uff0c\u8fd8\u6709\u4ec0\u4e48\u770b\u5934\u7684\u3002\u201d \u3000\u3000\u8bb8\u5c0f\u65e6\u54ac\u7259\u5207\u9f7f\u9053\uff1a\u201c\u5982\u679c\u6211\u8ddf\u522b\u7684\u5973\u4eba\u4e0d\u2014\u6837\uff0c\u90a3\u4e0d\u5c31\u6210\u602a\u7269\u4e86\uff1f\u201d\u201d \u3000\u3000\u738b\u5927\u5eb7\u9053\uff1a\u201c\u4ffa\u672c\u6765\u5c31\u662f\u60f3\u770b\u602a\u7269\u7684\u3002\u201d \u3000\u3000\u8bb8\u5c0f\u65e6\u5f3a\u5fcd\u7740\u6c14\u9053\uff1a\u201c\u90a3\u4f60\u662f\u4e0d\u60f3\u770b\u4e86\uff1f\u201d \u3000\u3000\u738b\u5927\u5eb7\u6709\u4e00\u642d\u6ca1\u4e00\u642d\u5730\u9053\uff1a\u201c\u4f60\u90a3\u4e0b\u9762\u4ffa\u8fd8\u6ca1\u770b\u8fc7\uff0c\u53ef\u4e0d\u53ef\u4ee5\u8131\u4e0b\u88e4\u5b50\u8ba9\u4ffa\u77a7\u77a7\uff1f\u201d \u3000\u3000\u201c\u4f60\u4e0d\u662f\u8bf4\u6ca1\u4ec0\u4e48\u597d\u770b\u7684\u5417\uff1f\u201d \u3000\u3000\u201c\u4ffa\u4f30\u8ba1\u7740\u90a3\u5730\u65b9\u53ef\u80fd\u6bd4\u8f83\u597d\u770b\u4e00\u4e9b\uff0c\u4e00\u5b9a\u8ddf\u522b\u4eba\u4e0d\u540c\u3002\u4e0d\u8fc7\u4ffa\u4e5f\u4e0d\u52c9\u5f3a\uff0e\u4f60\u9ad8\u5174\u8131\u5c31 \u8131\uff0c\u4e0d\u9ad8\u5174\u8131\u5c31\u62c9\u5012\u3002\u201d \u3000\u3000\u8bb8\u5c0f\u65e6\u628a\u5fc3\u4e00\u6a2a\u9053\uff1a\u201c\u8131\u5c31\u8131\uff0c\u8001\u5a18\u5728\u5927\u5ead\u5e7f\u4f17\u90fd\u4e0d\u5728\u4e4e\uff0c\u8fd8\u5728\u4e4e\u4f60\uff01\u201d \u3000\u3000\u201c\u8981\u8131\u5c31\u8131\uff0c\u7528\u4e0d\u7740\u591a\u8bf4\u5e9f\u6d3b\uff01\u201d \u3000\u3000\u8bb8\u5c0f\u65e6\u5bf9\u8131\u88e4\u5b50\u52a8\u4f5c\u719f\u7ec3\uff0c\u4e09\u628a\u4e24\u628a\uff0e\u4fbf\u628a\u4e0b\u8eab\u4e5f\u8131\u4e86\u4e2a\u7cbe\u5149\u3002 \u3000\u3000\u8fd8\u6446\u4e2a\u59ff\u52bf\uff0e \u3000\u3000\u5979\u53ef\u80fd\u4ee5\u4e3a\u7537\u4eba\u90fd\u662f\u63a2\u9669\u5bb6\uff0c\u738b\u5927\u5eb7\u5fc5\u4f1a\u6251\u5e8a\u53bb\u201c\u63a2\u5e7d\u8bbf\u80dc\u201d\u3002 \u3000\u3000\u5c82\u77e5\u738b\u5927\u5eb7\u53ea\u662f\u77a5\u4e86\u4e00\u773c\uff0c\u51b7\u51b7\u7b11\u9053\uff1a\u201c\u4ffa\u770b\u8ddf\u522b\u7684\u5973\u4eba\u4e5f\u5dee\u4e0d\u591a\uff0c\u5b9e\u5728\u6ca1\u4ec0\u4e48\u7279\u522b\uff0e \u65e2\u7136\u5982\u6b64\uff0c\u4ffa\u5c31\u8d70\u5566\uff01\u201d \u3000\u3000\u8fb9\u8bf4\u8fb9\u7ad9\u8d77\u8eab\u6765\uff0e \u3000\u3000\u8bb8\u5c0f\u65e6\u505a\u68a6\u4e5f\u6ca1\u60f3\u5230\u51ed\u7740\u81ea\u5df1\u7684\u4e00\u8eab\u201c\u672c\u94b1\u201d\uff0c\u5c45\u7136\u4f1a\u62db\u81f4\u8fd9\u6837\u7684\u540e\u679c\uff0c\u6c14\u5f97\u51e0\u5e73\u8fde\u8bdd \u4e5f\u8bf4\u4e0d\u51fa\u3002 \u3000\u3000\u597d\u5728\u4f3c\u4e4e\u53c8\u6709\u8f6c\u673a\uff0c\u738b\u5927\u5eb7\u521a\u8d70\u51fa\u623f\u95e8\uff0c\u5374\u53c8\u8f6c\u8eab\u56de\u6765\u3002 \u3000\u3000\u8bb8\u5c0f\u65e6\u6c14\u5f97\u5168\u8eab\u53d1\u6296\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u8fd8\u662f\u56de\u6765\u4e86\uff0c\u53ef\u89c1\u8001\u5a18\u8fd8\u662f\u6709\u5438\u5f15\u529b\uff01\u201d \u3000\u3000\u738b\u5927\u5eb7\u7b11\u9053\uff1a\u201c\u4ffa\u597d\u4e0d\u5bb9\u6613\u6765\u4e86\uff0e\u603b\u8be5\u7559\u4e2a\u7eaa\u5ff5\u3002\u201d \u3000\u3000\u8bb8\u5c0f\u65e6\u542c\u5bf9\u65b9\u8bed\u610f\u6696\u6627\uff0c\u53ea\u9053\u738b\u5927\u5eb7\u5df2\u56de\u5fc3\u8f6c\u610f\uff0c\u5fd9\u9053\uff1a\u201c\u90a3\u4f60\u5c31\u4e0a\u6765\uff01\u201d \u3000\u3000\u738b\u5927\u5eb7\u9053\uff1a\u201c\u8981\u7559\u70b9\u7eaa\u5ff5\uff0c\u4f55\u5fc5\u90a3\u4e48\u9ebb\u70e6\uff1f\u201d \u3000\u3000\u8bb8\u5c0f\u65e6\u66f4\u662f\u4e00\u5934\u96fe\u6c34\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u53ea\u89c1\u738b\u5927\u5eb7\u624b\u6307\u4e00\u5f39\uff0c\u7528\u624b\u634f\u6210\u7684\u7eb8\u4e38\uff0e\u6b63\u5f39\u5728\u90a3\u8bb8\u5c0f\u65e6\u4e24\u6761\u5927\u817f\u4e2d\u95f4\u3002\u63a5 \u7740\u51b7\u54fc\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u7eaa\u5ff5\uff0c\u4f60\u61c2\u4e86\u5427\uff01\u201d \u3000\u3000\u8bf4\u5b8c\u8bdd\uff0c\u5c06\u4ee4\u724c\u6254\u5728\u5e8a\u4e0a\uff0c\u5927\u6b65\u4e0b\u697c\u800c\u53bb\u3002 \u3000\u3000\u6b64\u523b\u7684\u8bb8\u5c0f\u65e6\u6c14\u5f97\u5c82\u6b62\u53d1\u660f\uff0c\u7b80\u76f4\u662f\u4e00\u4f5b\u51fa\u4e16\uff0c\u4e8c\u4f5b\u6d85\u78d0\uff0c\u660f\u5929\u9ed1\u5730\u4e86\u534a\u664c\uff0e\u624d\u5411\u7a97\u5916 \u5927\u58f0\u558a\u9053\uff1a\u201c\u738b\u4e09\u5feb\u6765\uff01\u201d \u3000\u3000\u4e00\u9635\u697c\u68af\u58f0\u54cd\uff0c\u738b\u4e09\u51fa\u73b0\u5728\u623f\u95e8\u5916\u9053\uff1a\u201c\u542f\u7980\u4ed9\u5b50\uff0c\u6709\u4ec0\u4e48\u5429\u5490\uff1f\u201d \u3000\u3000\u8bb8\u5c0f\u65e6\u72e0\u58f0\u9053\uff1a\u201c\u53bb\u628a\u76df\u4e3b\u8bf7\u6765\uff01\u201d \u3000\u3000\u738b\u4e09\u5e72\u54b3\u4e86\u4e24\u58f0\u9053\uff1a\u201c\u542f\u7980\u4ed9\u5b50\uff0c\u5c0f\u7684\u4e0d\u6562\u53bb\uff0e\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u4e0d\u6562\u53bb\uff1f\u201d \u3000\u3000\u201c\u76df\u4e3b\u7684\u8eab\u4efd\u5730\u4f4d\u4e0d\u540c\uff0e\u53ea\u80fd\u4ed6\u53eb\u522b\u4eba\u53bb\u89c1\u4ed6\uff0c\u522b\u4eba\u4e0d\u80fd\u53eb\u4ed6\u6765\u89c1\u3002\u201d \u3000\u3000\u201c\u6211\u662f\u4ed9\u5b50\uff0e\u8eab\u4efd\u5730\u4f4d\u4e5f\u4e0d\u540c\u3002\u201d \u3000\u3000\u201c\u4f60\u867d\u7136\u88ab\u79f0\u4e3a\u4ed9\u5b50\uff0c\u5b9e\u9645\u4e0a\u4e5f\u662f\u4eba\uff0c\u603b\u4e0d\u53ef\u80fd\u6bd4\u76df\u4e3b\u8fd8\u5927\u3002\u201d \u3000\u3000\u8bb8\u5c0f\u65e6\u53ea\u597d\u7a7f\u597d\u8863\u670d\u4e0b\u4e86\u5e8a\uff0c\u54fc\u4e86\u58f0\u9053\uff1a\u201c\u4f60\u4e0d\u53bb\u6211\u5c31\u81ea\u5df1\u53bb\u3002\u201d \u3000\u3000\u738b\u4e09\u9053\uff1a\u201c\u8981\u89c1\u76df\u4e3b\uff0e\u672c\u6765\u5c31\u8be5\u4ed9\u5b50\u81ea\u5df1\u53bb\u3002\u201d \u3000\u3000\u8bb8\u5c0f\u65e6\u5ffd\u7136\u95ee\u9053\uff1a\u201c\u4f60\u521a\u624d\u662f\u5426\u770b\u5230\u6211\u5149\u7740\u8eab\u5b50\uff1f\u201d \u3000\u3000\u738b\u4e09\u9053\uff1a\u201c\u770b\u89c1\u6709\u4ec0\u4e48\u7528\uff1f\u4ed9\u5b50\u4e5f\u4e0d\u662f\u4e0d\u77e5\u9053\uff0c\u5c0f\u7684\u90a3\u4e2a\u65e9\u5c31\u6ca1\u6709\u4e86\uff01\u201d \u3000\u3000\u8bb8\u5c0f\u65e6\u9053\uff1a\u201c\u65e9\u77e5\u5982\u6b64\uff0c\u76df\u4e3b\u5c31\u4e0d\u8be5\u7ed9\u4f60\u4eec\u4e24\u4e2a\u9609\u6389\uff0c\u4e0b\u697c\u53bb\u5427\uff01\u201d \u3000\u3000\u738b\u4e09\u542c\u4e0d\u61c2\u8bb8\u5c0f\u65e6\u8bdd\u4e2d\u4e4b\u610f\uff0c\u6123\u6123\u5730\u4e0b\u697c\u800c\u53bb\u3002 \u3000\u3000\u8bb8\u5c0f\u65e6\u2014\u5f84\u6765\u5230\u970d\u5143\u4f3d\u7684\u4f4f\u5904\uff0c\u7ecf\u8fc7\u95e8\u53e3\u8b66\u536b\u55bd\u5175\u7684\u901a\u62a5\uff0c\u5728\u4e00\u6240\u5e03\u7f6e\u8c6a\u534e\u7684\u623f\u95f4\u4f1a\u89c1 \u4e86\u970d\u5143\u4f3d\u3002 \u3000\u3000\u970d\u5143\u4f3d\u56e0\u987b\u5c65\u884c\u82d7\u7d20\u5170\u7684\u767e\u65e5\u4e4b\u7ea6\uff0c\u65e5\u524d\u4ecd\u662f\u5355\u4eba\u72ec\u623f\u3002 \u3000\u3000\u65f6\u95f4\u5df2\u662f\u4e00\u66f4\u8fc7\u540e\uff0c\u5373\u5c06\u4e09\u9f13\uff0c\u970d\u5143\u4f3d\u65e9\u5df2\u5c31\u5bdd\uff0c\u6b64\u523b\u6b63\u662f\u62ab\u7740\u8863\u670d\uff0c\u4ece\u5e8a\u4e0a\u722c\u8d77\u6765 \u7684\u3002 \u3000\u3000\u8bb8\u5c0f\u65e6\u4e00\u8fdb\u95e8\u5c31\u6ca1\u597d\u6c14\u7684\u9053\uff1a\u201c\u76df\u4e3b\uff0c\u4f60\u7a76\u7adf\u641e\u7684\u4ec0\u4e48\u660e\u5802\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u88ab\u5f04\u5f97\u72b9\u5982\u4e08\u4e8c\u91d1\u521a\u6478\u4e0d\u7740\u5934\u8111\uff0c\u7728\u7740\u4e09\u89d2\u773c\u9053\uff1a\u201c\u4f60\u8bf4\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u8bb8\u5c0f\u65e6\u9053\uff1a\u201c\u6211\u8bf4\u7684\u662f\u8bdd\uff0c\u4f60\u8fde\u8bdd\u4e5f\u542c\u4e0d\u61c2\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u5728\u5e8a\u524d\u6905\u4e0a\u5750\u4e0b\u9053\uff1a\u201c\u6709\u8bdd\u597d\u597d\u8bb2\uff0c\u4f55\u5fc5\u53d1\u813e\u6c14\uff1f\u8001\u592b\u4e5f\u4e0d\u662f\u4e0d\u8bb2\u7406\u7684\u4eba\uff0e\u201d \u3000\u3000\u201c\u4f60\u4eca\u665a\u628a\u4ee4\u724c\u53d1\u7ed9\u4e86\u54ea\u4e00\u4e2a\uff1f\u201d \u3000\u3000\u201c\u53d1\u7ed9\u4e86\u738b\u5927\u5eb7\uff0c\u96be\u9053\u4ed6\u4e5f\u6ca1\u53bb\uff1f\u201d \u3000\u3000\u201c\u4ed6\u53bb\u4e86\u3002\u201d \u3000\u3000\u201c\u4ed6\u53bb\u4e86\u5c31\u597d\uff0c\u4f60\u8fd8\u6c14\u547c\u547c\u7684\u6765\u627e\u8001\u592b\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u5c31\u662f\u56e0\u4e3a\u4ed6\u53bb\u4e86\uff0c\u6240\u4ee5\u6211\u624d\u6765\u627e\u4f60\uff01\u201d \u3000\u3000\u201c\u4f60\u5148\u524d\u8ddf\u8001\u592b\u8bf4\u8fc7\uff0c\u5e0c\u671b\u627e\u4e2a\u80fd\u5e72\u7684\uff0c\u8001\u592b\u6599\u60f3\u738b\u5927\u5eb7\u5728\u8fd9\u65b9\u9762\u5fc5\u5b9a\u5f88\u80fd\u5e72\uff0c\u6240\u4ee5\u624d \u51b3\u5b9a\u628a\u4ee4\u724c\u53d1\u7ed9\u4ed6\u3002\u662f\u5426\u4ed6\u52a8\u4f5c\u592a\u7c97\u66b4\uff0c\u8ba9\u4f60\u53d7\u4e0d\u4e86\uff0c\u624d\u6765\u627e\u8001\u592b\u51fa\u6c14\uff1f\u201d \u3000\u3000\u201c\u4ed6\u82e5\u5728\u529e\u4e8b\u7684\u65f6\u5019\u7c97\u66b4\uff0e\u672c\u4ed9\u5b50\u6b22\u8fce\u8fd8\u6765\u4e0d\u53ca\uff0c\u53ef\u60dc\u4ed6\u7c97\u66b4\u5f97\u4e0d\u662f\u5730\u65b9\u3002\u201d \u3000\u3000\u201c\u5230\u5e95\u600e\u4e48\u56de\u4e8b\uff1f\u4f60\u5148\u8bf4\u660e\u767d\u3002\u201d \u3000\u3000\u8bb8\u5c0f\u65e6\u968f\u5373\u628a\u65b9\u624d\u7684\u7ecf\u8fc7\uff0c\u6beb\u4e0d\u9690\u7792\u548c\u76d8\u6258\u4e86\u51fa\u6765\u3002 \u3000\u3000\u970d\u5143\u4f3d\u7d27\u76b1\u7709\u5934\u9053\uff1a\u201c\u6709\u8fd9\u79cd\u4e8b\uff1f\u738b\u5927\u5eb7\u8fd9\u6837\u505a\u7a76\u7adf\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u8bb8\u5c0f\u65e6\u6487\u5634\u9053\uff1a\u201c\u4f60\u95ee\u6211\uff0c\u6211\u95ee\u8c01\uff0e\u6211\u82e5\u77e5\u9053\u662f\u600e\u4e48\u56de\u4e8b\uff0c\u5c31\u4e0d\u6765\u627e\u4f60\u4e86\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u6478\u7740\u80e1\u5b50\u6c89\u541f\u4e86\u534a\u664c\u9053\uff1a\u201c\u4f60\u51c6\u5907\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u8bb8\u5c0f\u65e6\u54ac\u4e86\u54ac\u7259\u9053\uff1a\u201c\u5bb0\u4e86\u4ed6\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u6447\u5934\u9053\uff1a\u201c\u738b\u5927\u5eb7\u662f\u5d02\u5c71\u4e09\u96c4\u4e4b\u4e00\uff0c\u5728\u80f6\u4e1c\u4e00\u5e26\uff0c\u540d\u5934\u5341\u5206\u54cd\u4eae\uff0c\u5728\u8ff7\u8e2a\u8c37\u4e5f\u662f \u91cd\u8981\u5934\u76ee\uff0c\u4e0d\u662f\u60f3\u5bb0\u5c31\u5bb0\u5f97\u4e86\u7684\u3002\u201d \u3000\u3000\u201c\u4f60\u53ef\u4ee5\u627e\u4e2a\u7f6a\u540d\u529e\u4ed6\u3002\u201d \u3000\u3000\u201c\u4ed6\u53ea\u662f\u5bf9\u4f60\u65e0\u793c\uff0c\u8fd9\u79cd\u7f6a\u540d\uff0c\u53ea\u6015\u4e0d\u592a\u59a5\u5f53\u3002\u201d \u3000\u3000\u201c\u96be\u9053\u4f60\u5c31\u4e0d\u80fd\u683d\u4ed6\u4e00\u4e2a\u522b\u7684\u7f6a\u540d\uff0c\u6b32\u52a0\u4e4b\u7f6a\uff0c\u4f55\u60a3\u65e0\u8f9e\uff01\u201d \u3000\u3000\u201c\u4f60\u522b\u6025\uff0c\u8ba9\u8001\u592b\u6162\u6162\u5904\u7f6e\uff0c\u5c06\u6765\u603b\u4f1a\u7ed9\u4f60\u51fa\u8fd9\u4e00\u53e3\u6c14\uff0c\u65f6\u95f4\u4e0d\u65e9\uff0c\u4f60\u5c31\u56de\u53bb\u7761\u5427\uff01\u201d \u3000\u3000\u8bb8\u5c0f\u65e6\u4e00\u626d\u5c41\u80a1\u9053\uff1a\u201c\u4e0d\u6210\uff0c\u4f60\u8ddf\u6211\u8bb2\u597d\u4e86\u4eca\u5929\u6709\u7537\u7684\u5230\u4e1c\u6d77\u9662\u966a\u6211\u7761\u89c9\uff0c\u73b0\u5728\u53c8\u843d\u4e86 \u7a7a\uff0c\u8fd9\u4e00\u665a\u8ba9\u6211\u600e\u4e48\u71ac\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u563f\u563f\u7b11\u9053\uff1a\u201c\u90a3\u5c31\u7531\u8001\u592b\u966a\u4f60\u7761\u600e\u4e48\u6837\uff1f\u9a6c\u4e0a\u4e0a\u5e8a\u5427\uff01\u201d \u3000\u3000\u8bb8\u5c0f\u65e6\u53c8\u628a\u5c41\u80a1\u2014\u626d\u9053\uff1a\u201c\u4e5f\u4e0d\u6210\uff0c\u6211\u81ea\u5df1\u6709\u73b0\u6210\u7684\u4e1c\u6d77\u9662\uff0c\u4f55\u5fc5\u4e0a\u4f60\u7684\u5e8a\u3002\u201d \u3000\u3000\u971c\u5143\u4f43\u9053\uff1a\u201c\u6ca1\u5173\u7cfb\uff0c\u8001\u592b\u60c5\u613f\u5230\u4f60\u90a3\u91cc\u53bb\uff01\u201d \u3000\u3000\u8bb8\u5c0f\u65e6\u6447\u5934\u9053\uff1a\u201c\u8fd8\u662f\u4e0d\u6210\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u6123\u6123\u5730\u9053\uff1a\u201c\u8fd9\u6837\u4e0d\u6210\uff0c\u90a3\u6837\u4e5f\u4e0d\u6210\uff0c\u4f60\u7a76\u7adf\u6253\u7684\u4ec0\u4e48\u4e3b\u610f\uff1f\u201d \u3000\u3000\u8bb8\u5c0f\u65e6\u9053\uff1a\u201c\u54b1\u4eec\u4e24\u4e2a\u5df2\u7ecf\u6709\u8fc7\u597d\u51e0\u6b21\u4e34\u5e8a\u7ecf\u9a8c\uff0c\u6211\u8981\u6362\u6362\u53e3\u5473\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u5782\u5934\u4e27\u6c14\u5401\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u4f60\u522b\u95f9\uff0c\u660e\u5929\u8001\u592b\u4e00\u5b9a\u80fd\u627e\u4e2a\u771f\u6b63\u80fd\u5e72\u7684\u53d1\u4ee4\u724c\u7ed9\u4ed6\uff0c \u5982\u679c\u518d\u51fa\u95ee\u9898\uff0e\u8001\u592b\u60c5\u613f\u628a\u8111\u888b\u5272\u4e0b\u6765\u4ea4\u7ed9\u4f60\uff01\u201d \u3000\u3000\u8bb8\u5c0f\u65e6\u543c\u9053\uff1a\u201c\u4e0d\u6210\uff0c\u6211\u4eca\u5929\u665a\u4e0a\u5c31\u8981\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u5f3a\u6291\u7740\u6027\u5b50\u9053\uff1a\u201c\u73b0\u5728\u5df2\u7ecf\u5feb\u5230\u4e8c\u66f4\uff0c\u4f55\u5fc5\u4e3a\u8fd9\u4e8b\u53bb\u60ca\u52a8\u4eba\uff0e\u4f60\u5c31\u5fcd\u4e00\u5fcd\u5427\uff01\u201d \u3000\u3000\u8bb8\u5c0f\u4e14\u9053\uff1a\u201c\u6211\u5df2\u7ecf\u5fcd\u4e86\u597d\u51e0\u4e2a\u665a\u4e0a\uff0c\u65e9\u5c31\u5fcd\u4e0d\u4f4f\u5566\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u8fd9\u4e0d\u662f\u5b58\u5fc3\u7ed9\u8001\u592b\u4e3a\u96be\u5417\uff1f\u8001\u592b\u8981\u966a\u4f60\u4f60\u53c8\u4e0d\u80af\uff0c\u96be\u9053\u5c06\u5c31\u4e00\u665a\u4e0a\u4e5f\u4e0d\u53ef \u4ee5\uff1f\u201d \u3000\u3000\u201c\u8fd9\u79cd\u4e8b\u54ea\u80fd\u5c06\u5c31\uff1f\u201d \u3000\u3000\u201c\u597d\u5427\uff01\u4f60\u5148\u56de\u53bb\uff0c\u8001\u592b\u9a6c\u4e0a\u5c31\u6d3e\u4e00\u4e2a\u4eba\u53bb\u5411\u4f60\u62a5\u5230\u3002\u201d \u3000\u3000\u201c\u4e0d\u8981\u4f60\u6d3e\u7684\uff0c\u6211\u8981\u81ea\u5df1\u9009\u3002\u201d \u3000\u3000\u8bb8\u5c0f\u65e6\u4e00\u561f\u5634\uff0c\u53c8\u9053\uff1a\u201c\u6211\u8981\u90a3\u59d3\u9ea6\u7684\u5c0f\u5b50\u3002\u201d \u3000\u3000\u201c\u6211\u4e0d\u662f\u65e9\u5c31\u5bf9\u4f60\u8bf4\u8fc7\u5417\uff1f\u90a3\u5c0f\u5b50\u7684\u813e\u6c14\u738b\u5927\u5eb7\u8fd8\u574f\uff0c\u8fde\u738b\u5927\u5eb7\u4f60\u90fd\u53d7\u4e0d\u4e86\uff0c\u600e\u80fd\u53d7\u5f97 \u4e86\u4ed6\u3002\u201d \u3000\u3000\u201c\u8fd9\u662f\u6211\u7684\u4e8b\uff0c\u4e0d\u7528\u4f60\u7ba1\u3002\u201d \u3000\u3000\u201c\u5982\u679c\u518d\u95f9\u51fa\u4e8b\u6765\uff0c\u4f60\u53ef\u4e0d\u80fd\u627e\u6211\u3002\u201d \u3000\u3000\u201c\u6211\u81ea\u5df1\u9009\u7684\uff0e\u5f53\u7136\u4e0d\u627e\u4f60\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u76b1\u7709\u6447\u5934\u9053\uff1a\u201c\u597d\u5427\uff01\u8001\u592b\u5c31\u4f9d\u4f60\uff0c\u53ef\u662f\u4ee4\u724c\u8fd8\u5728\u738b\u5927\u5eb7\u8eab\u4e0a\u3002\u201d \u3000\u3000\u8bb8\u5c0f\u65e6\u638f\u51fa\u4ee4\u724c\u9053\uff1a\u201c\u90a3\u59d3\u738b\u7684\u4e34\u8d70\u65f6\u5df2\u628a\u4ee4\u724c\u6254\u5728\u5e8a\u4e0a\uff0c\u6211\u5e26\u6765\u4e86\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u63a5\u8fc7\u4ee4\u724c\uff0e\u5583\u5583\u81ea\u8bed\u9053\uff1a\u201c\u8001\u592b\u539f\u4ee5\u4e3a\u6210\u7acb\u4e1c\u6d77\u9662\u662f\u4ef6\u597d\u4e8b\uff0c\u54ea\u77e5\u521a\u5f00\u5f20\u5c31\u60f9\u6765 \u8fd9\u4e48\u591a\u9ebb\u70e6\uff0c\u4e09\u66f4\u534a\u591c\u53d1\u4ee4\u724c\uff0c\u50cf\u4ec0\u4e48\u8bdd\uff1f\u201d \u3000\u3000\u8bb8\u5c0f\u65e6\u50ac\u4fc3\u9053\uff1a\u201c\u4f60\u522b\u7f57\u55e6\uff0c\u5feb\u6d3e\u4eba\u628a\u4ee4\u724c\u9001\u7ed9\u90a3\u59d3\u9ea6\u7684\u5c0f\u4f19\u5b50\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u54fc\u4e86\u58f0\u9053\uff1a\u201c\u8001\u592b\u8fd9\u5c31\u6d3e\u4eba\u9001\uff0e\u4f60\u56de\u53bb\u7b49\u7740\u5427\u3002\u201d \u3000\u3000\u867d\u7136\u67cf\u9f84\u9662\u6240\u6709\u7684\u4eba\u5df2\u7ecf\u4e0a\u5e8a\u5c31\u5bdd\uff0e\u4f46\u738b\u5927\u5eb7\u7684\u56de\u6765\uff0c\u5374\u53c8\u628a\u4f17\u4eba\u60ca\u9192\uff0c\u4e3a\u4e86\u77e5\u9053\u662f\u600e \u4e48\u56de\u4e8b\uff0c\u518d\u5ea6\u9f50\u96c6\u5728\u5c0f\u5ba2\u5385\uff0e\u56f4\u7740\u738b\u5927\u5eb7\u95ee\u957f\u95ee\u77ed\u3002 \u3000\u3000\u53ea\u6709\u82d7\u7d20\u82d3\u56e0\u5df2\u98ce\u95fb\u4e0d\u662f\u4ec0\u4e48\u597d\u4e8b\uff0c\u3000\u4e00\u4e2a\u4eba\u4ecd\u8eb2\u5728\u623f\u95f4\u91cc\uff0e \u3000\u3000\u5f53\u4e0b\uff0e\u738b\u5927\u5eb7\u4e3b\u52a8\u628a\u7ecf\u8fc7\u8bf4\u4e86\u4e00\u904d\u3002 \u3000\u3000\u7b11\u9762\u4f5b\u9c8d\u8d85\u9053\uff1a\u201c\u8001\u4e09\uff0c\u4f60\u5e72\u561b\u73a9\u51fa\u8fd9\u4e48\u4e00\u624b\u660e\u5802\u6765\uff1f\u201d \u3000\u3000\u738b\u5927\u5eb7\u9053\uff1a\u201c\u82e5\u8bf4\u73a9\u5973\u4eba\uff0c\u4ffa\u4e5f\u4e0d\u662f\u6ca1\u73a9\u8fc7\uff0e\u4f55\u5fc5\u4e00\u5b9a\u627e\u5979\u3002\u201d \u3000\u3000\u9c8d\u8d85\u9053\uff1a\u201c\u90a3\u5973\u4eba\u7684\u8eab\u6750\u4e0d\u662f\u5f88\u597d\u5417\uff1f\u201d \u3000\u3000\u738b\u5927\u5eb7\u9053\uff1a\u201c\u90a3\u79cd\u8eab\u6750\u6ee1\u8857\u90fd\u662f\uff0c\u7537\u4eba\u627e\u5973\u4eba\uff0c\u81f3\u5c11\u8be5\u627e\u4e2a\u6709\u5973\u4eba\u5473\u7684\uff0c\u770b\u5979\u8131\u8863\u8868\u6f14 \u662f\u4e2a\u65b0\u9c9c\uff0c\u82e5\u548c\u8fd9\u79cd\u5973\u4eba\u4e0a\u4e86\u5e8a\uff0c\u4e0d\u5012\u80c3\u53e3\u624d\u602a\uff0e\u66f4\u4f55\u51b5\u4ffa\u8fd8\u6709\u987e\u8651\u3002\u201d \u3000\u3000\u9c8d\u8d85\u9053\uff1a\u201c\u987e\u8651\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u738b\u5927\u5eb7\u9053\uff1a\u201c\u5979\u8fde\u8eab\u4e0a\u90fd\u6709\u6bd2\uff0c\u90a3\u5730\u65b9\u5f53\u7136\u6bd2\u6c14\u66f4\u6df1\uff0c\u5982\u679c\u5f04\u6210\u4e00\u8eab\u70c2\u75ae\uff0c\u8ff7\u8e2a\u8c37\u54ea\u91cc \u627e\u5927\u592b\u53bb\uff0e\u201d \u3000\u3000\u9c8d\u8d85\u9894\u9996\u9053\uff1a\u201c\u5f88\u96be\u5f97\uff0c\u4f60\u8fd8\u987e\u8651\u5f97\u8fd9\u4e48\u5468\u5230\u3002\u201d \u3000\u3000\u738b\u5927\u5eb7\u53e3\u6cab\u6a2a\u98de\u9053\uff1a\u201c\u8fd9\u662f\u5207\u8eab\u5927\u4e8b\uff0c\u5f53\u7136\u4e0d\u80fd\u4e0d\u987e\u8651\uff0c\u8fd8\u6709\u5c31\u662f\u4ffa\u60f3\u8d77\u4e00\u53e5\u8bdd\u6765\u3002\u201d \u3000\u3000\u201c\u54ea\u53e5\u8bdd\uff1f\u201d \u3000\u3000\u201c\u5f00\u5934\u662f\u9f50\u9c81\u4e24\u4e2a\u5b57\uff0c\u4e0b\u9762\u662f\u2026\u2026\u201d \u3000\u3000\u738b\u5927\u5eb7\u8bf4\u7740\u671b\u5411\u8001\u4e8c\u9b3c\u8bf8\u845b\u6d2a\u6cfd\u9053\uff1a\u201c\u4f60\u7ed9\u4ffa\u63a5\u4e0b\u53bb\u5427\uff01\u201d \u3000\u3000\u6d2a\u6cfd\u9053\uff1a\u201c\u662f\u4e0d\u662f\u9f50\u9c81\u4e3a\u793c\u4e49\u4e4b\u90a6\u90a3\u53e5\u8bdd\uff1f\u201d \u3000\u3000\u738b\u5927\u5eb7\u731b\u4e00\u62cd\u624b\u9053\uff1a\u201c\u5bf9\uff01\u5c31\u662f\u8fd9\u53e5\u8bdd\uff0c\u4ffa\u867d\u7136\u8bf4\u4e0d\u4e0a\u6765\uff0c\u610f\u601d\u5374\u61c2\uff0c\u4ffa\u4eec\u90a3\u5730\u65b9\u51fa\u8fc7 \u5b54\u5723\u4eba\uff0c\u53c8\u51fa\u8fc7\u4e00\u4e2a\u5175\u5723\u5b59\u5b50\u3002\u5982\u679c\u4ffa\u5f97\u4e86\u90a3\u79cd\u75c5\uff0c\u88ab\u90a3\u79cd\u75c5\u6298\u78e8\u6b7b\uff0c\u600e\u80fd\u5bf9\u5f97\u4f4f\u6b7b\u53bb\u7684\u4e24 \u4f4d\u5723\u4eba\uff01\u201d \u3000\u3000\u5927\u4f19\u513f\u90fd\u89c9\u5f97\u597d\u7b11\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u67cf\u9f84\u9662\u95e8\u5916\u7684\u8b66\u536b\u55bd\u5175\u5954\u8fdb\u6765\u62a5\u9053\uff1a\u201c\u7980\u526f\u76df\u4e3b\uff0c\u76df\u4e3b\u8eab\u8fb9\u7684\u4e00\u4f4d\u97e9\u5934\u76ee\u8981 \u8fdb\u6765\uff0e\u5c0f\u7684\u4e0d\u6562\u4f5c\u4e3b\u3002\u201d \u3000\u3000\u949f\u4e00\u8c6a\u4e00\u6123\u9053\uff1a\u201c\u8fd9\u79cd\u65f6\u5019\uff0c\u4ed6\u6765\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u90a3\u55bd\u5175\u9053\uff1a\u201c\u5c0f\u7684\u95ee\u4ed6\uff0c\u4ed6\u4e0d\u80af\u8bb2\uff0e\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u9053\uff1a\u201c\u65e2\u662f\u76df\u4e3b\u6d3e\u4eba\u6765\uff0c\u90a3\u5c31\u653e\u4ed6\u8fdb\u6765\u5427\uff01\u201d \u3000\u3000\u90a3\u540d\u97e9\u5934\u76ee\u5f88\u5feb\u4fbf\u8d70\u4e86\u8fdb\u6765\uff0e\u5411\u949f\u4e00\u8c6a\u65bd\u4e86\u4e00\u793c\u540e\uff0e\u4e24\u773c\u5374\u6ef4\u6e9c\u6e9c\u7684\u5f80\u6bcf\u4eba\u8138\u4e0a\u4e71\u8f6c\uff0e \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u4f60\u662f\u505a\u4ec0\u4e48\u6765\u7684\uff1f\u4e3a\u4ec0\u4e48\u4e0d\u8bb2\u8bdd\uff1f\u201d \u3000\u3000\u539f\u6765\u970d\u5143\u4f3d\u60f3\u5230\u9ea6\u5c0f\u660e\u867d\u7136\u5929\u4e0d\u6015\u5730\u4e0d\u6015\uff0c\u4f46\u5bf9\u7537\u5973\u4e4b\u95f4\u7684\u4e8b\u5fc5\u5b9a\u8fd8\u662f\u9762\u76ae\u751a\u8584\uff0c\u552f\u6050 \u4ed6\u5f53\u4f17\u56e0\u7f9e\u6076\u800c\u62d2\u7edd\uff0c\u6240\u4ee5\u7279\u522b\u4ea4\u4ee3\u8fd9\u540d\u9001\u4ee4\u724c\u7684\u5fc3\u8179\u5c0f\u5934\u76ee\u97e9\u9f99\u8981\u5728\u4eba\u4e0d\u77e5\u9b3c\u4e0d\u89c9\u7684\u72b6\u51b5 \u4e0b\u628a\u4ee4\u724c\u4ea4\u7ed9\u9ea6\u5c0f\u660e\u3002 \u3000\u3000\u6b64\u523b\u97e9\u9f99\u89c1\u5ba2\u5385\u5185\u8fd9\u4e48\u591a\u4eba\u5728\u573a\uff0c\u81ea\u7136\u4e0d\u4fbf\u5f00\u53e3\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u89c1\u97e9\u9f99\u4ecd\u4e0d\u8bf4\u8bdd\uff0c\u559d\u9053\uff1a\u201c\u4f60\u53ef\u662f\u4e2a\u54d1\u5df4\uff01\u201d \u3000\u3000\u97e9\u9f99\u53ea\u5f97\u671f\u671f\u827e\u827e\u7684\u9053\uff1a\u201c\u5c0f\u7684\u60f3\u5355\u72ec\u8ddf\u9ea6\u5c0f\u4fa0\u8bb2\u51e0\u53e5\u8bdd\uff01\u201d \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u662f\u8c01\u6d3e\u4f60\u6765\u7684\uff1f\u4e3a\u4ec0\u4e48\u8981\u5355\u72ec\u89c1\u9ea6\u5c0f\u4fa0\uff1f\u201d \u3000\u3000\u97e9\u9f99\u9762\u8272\u618b\u5f97\u8d64\u7ea2\uff0e\u55eb\u5685\u7740\u9053\uff1a\u201c\u5c0f\u7684\u2026\u2026\u5c0f\u7684\u2026\u2026\u201d \u3000\u3000\u53ea\u89c1\u9ea6\u5c0f\u660e\u53cc\u7709\u4e00\u6311\uff0c\u53f1\u9053\uff1a\u201c\u6211\u9ea6\u5c0f\u660e\u548c\u970d\u76df\u4e3b\u4e4b\u95f4\u4ece\u6ca1\u4ec0\u4e48\u6697\u5730\u5f80\u6765\uff0c\u4f60\u5c0f\u5b50\u82e5\u73b0 \u5728\u4e0d\u80af\u8bb2\uff0c\u4e8e\u8106\u5c31\u6eda\u56de\u53bb\u5427\uff01\u201d \u3000\u3000\u97e9\u9f99\u89c1\u5df2\u6ca1\u529e\u6cd5\u5c06\u9ea6\u5c0f\u660e\u5f15\u51fa\uff0c\u53ea\u597d\u4ece\u6000\u91cc\u628a\u90a3\u5757\u4ee4\u724c\u638f\u51fa\uff0c\u53cc\u624b\u606d\u606d\u656c\u656c\u7684\u9001\u5230\u684c\u4e0a \u9053\uff1a\u201c\u76df\u4e3b\u8981\u5c0f\u7684\u628a\u8fd9\u4ee4\u724c\u4ea4\u7ed9\u9ea6\u5c0f\u4fa0\u3002\u201d \u3000\u3000\u987f\u65f6\uff0e\u5ba2\u5385\u5185\u6240\u6709\u7684\u4eba\u7686\u4e3a\u4e4b\u4e00\u5446\uff0e\u4e0d\u7ea6\u800c\u540c\u5c06\u89c6\u7ebf\u6295\u5411\u9ea6\u5c0f\u660e\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u8138\u4e0a\u4e00\u70ed\uff0e\u7acb\u5373\u628a\u4ee4\u724c\u5f80\u5730\u4e0a\u4e00\u63b7\uff0c\u9a82\u9053\uff1a\u201c\u738b\u516b\u86cb\uff01\u8fd9\u7b97\u4ec0\u4e48\u610f\u601d\uff1f\u5feb\u5e26\u56de\u53bb \u4ea4\u8fd8\u970d\u5143\u4f3d\uff01\u201d \u3000\u3000\u949f\u2014\u8c6a\u62c5\u5fc3\u56e0\u800c\u95f9\u51fa\u7978\u6765\uff0c\u8fde\u5fd9\u5429\u5490\u97e9\u9f99\u9053\uff1a\u201c\u4f60\u56de\u53bb\u5427\uff01\u4ee4\u724c\u5c31\u7559\u5728\u8fd9\u8fb9\u3002\u201d \u3000\u3000\u97e9\u9f99\u8d70\u51fa\u4e24\u6b65\uff0c\u53c8\u786c\u7740\u5934\u76ae\u56de\u5934\u9053\uff1a\u201c\u526f\u76df\u4e3b\u5e72\u4e07\u529d\u529d\u9ea6\u5c0f\u4fa0\uff0c\u8981\u4ed6\u4e00\u5b9a\u53bb\u3002\u201d \u3000\u3000\u97e9\u9f99\u8d70\u540e\uff0c\u949f\u4e00\u8c6a\u6361\u8d77\u4ee4\u724c\uff0e\u653e\u56de\u684c\u4e0a\u9053\uff1a\u201c\u9ea6\u8001\u5f1f\uff0c\u8fd9\u662f\u4f55\u82e6\uff1f\u53bb\u4e0d\u53bb\u5728\u4f60\uff0c\u7528\u4e0d\u7740 \u751f\u8fd9\u4e48\u5927\u7684\u6c14\u3002\u82e5\u97e9\u9f99\u56de\u53bb\u628a\u521a\u624d\u7684\u60c5\u5f62\u544a\u77e5\u970d\u5143\u4f3d\uff0e\u970d\u5143\u4f3d\u5fc3\u80f8\u72ed\u7a84\uff0c\u5c82\u4e0d\u7ed9\u54b1\u4eec\u60f9\u6765\u9ebb \u70e6\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u6124\u6124\u5730\u9053\uff1a\u201c\u597d\u6c49\u505a\u4e8b\u597d\u6c49\u5f53\uff0c\u7edd\u4e0d\u7275\u8fde\u4f17\u4f4d\u5144\u957f\u3002\u82e5\u660e\u5929\u970d\u5143\u4f3d\u627e\u4e0a\u6211\uff0c\u6211\u5c31 \u5927\u95f9\u2014\u573a\u7ed9\u4f60\u4eec\u77a7\u77a7\uff01\u201d \u3000\u3000\u738b\u5927\u5eb7\u7acb\u5373\u5728\u4e00\u65c1\u9644\u548c\u7740\uff1a\u201c\u597d\u4e5f\uff01\u6211\u8d5e\u6210\u3002\u201d \u3000\u3000\u6d2a\u6cfd\u77aa\u89c6\u7740\u738b\u5927\u5eb7\u9053\uff1a\u201c\u4f60\u5c11\u5728\u65c1\u52a9\u9635\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u4ecd\u662f\u4e00\u8138\u8981\u60f9\u4e8b\u7684\u6837\u5b50\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u9ea6\u8001\u5f1f\u5230\u5e95\u53bb\u662f\u4e0d\u53bb\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9a6c\u4e0a\u4e24\u773c\u4e00\u77aa\u9053\uff1a\u201c\u5f53\u7136\u4e0d\u53bb\uff01\u201d \u3000\u3000\u5374\u542c\u4f59\u4ea6\u4e50\u6162\u6761\u671f\u7406\u7684\u9053\uff1a\u201c\u4e3a\u4e86\u987e\u5168\u5927\u5c40\uff0c\u6211\u529d\u9ea6\u8001\u5f1f\u8fd8\u662f\u53bb\u7684\u597d\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u4e5f\u8fd9\u6837\u8bb2\u8bdd\uff1f\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u9053\uff1a\u201c\u5f53\u7136\u6211\u662f\u4e3a\u4e86\u5927\u4f19\u7740\u60f3\uff0c\u4f55\u5fc5\u4e00\u5b9a\u8981\u95f9\u51fa\u4e8b\u6765\uff1f\u201d \u3000\u3000\u201c\u4e3a\u4e86\u4e0d\u53bb\u5ad6\u5973\u4eba\u5c31\u95f9\u51fa\u4e8b\u6765\uff0c\u4f60\u672a\u514d\u592a\u5c0f\u9898\u5927\u4f5c\u4e86\u5427\uff01\u201d \u3000\u3000\u201c\u8bdd\u4e0d\u80fd\u8fd9\u6837\u8bf4\uff0c\u4e8b\u60c5\u53ef\u5927\u53ef\u5c0f\uff0c\u4f55\u5fc5\u60f9\u5f97\u970d\u5143\u4f3d\u4e0d\u9ad8\u5174\u3002\u201d \u3000\u3000\u201c\u90a3\u5c31\u6362\u2014\u4e2a\u4eba\u53bb\u600e\u6837\uff1f\u201d \u3000\u3000\u201c\u4e0d\u6210\uff0c\u4ee4\u724c\u662f\u53d1\u7ed9\u4f60\u7684\uff0e\u82e5\u970d\u5143\u4f3d\u67e5\u95ee\u8d77\u6765\u53bb\u7684\u4e0d\u662f\u4f60\uff0c\u5c82\u4e0d\u662f\u4e00\u4e2a\u7f6a\u540d\uff1f\u201d \u3000\u3000\u949f\u2014\u8c6a\u4e5f\u7d27\u8ddf\u7740\u9053\uff1a\u201c\u53bb\u4e86\u540e\u8981\u4e0d\u8981\u78b0\u90a3\u5973\u4eba\uff0c\u4e00\u5207\u7531\u4f60\uff0e\u53cd\u6b63\u970d\u5143\u4f3d\u53ea\u662f\u8981\u4f60\u5230\u4e1c\u6d77 \u9662\u8fc7\u4e00\u591c\uff0c\u53ea\u8981\u8fc7\u591c\uff0c\u5c31\u7b97\u4ea4\u4ee3\u4e86\u5dee\u4e8b\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u5728\u4e03\u5634\u516b\u820c\u7684\u529d\u8bf4\u4e0b\uff0c\u603b\u7b97\u540c\u610f\u4e86\u3002\u4ed6\u7ad9\u8d77\u8eab\u6765\u53d6\u8d77\u684c\u4e0a\u7684\u4ee4\u724c\u9053\uff1a\u201c\u6211\u53bb\u5566\uff01 \u4f60\u4eec\u90fd\u56de\u623f\u7761\u5427\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u6765\u5230\u4e1c\u6d77\u9662\u95e8\u524d\uff0c\u8d1f\u8d23\u8b66\u536b\u7684\u8fd8\u662f\u539f\u5148\u90a3\u540d\u55bd\u5175\u3002 \u3000\u3000\u4ed6\u773c\u89c1\u738b\u5927\u5eb7\u51fa\u53bb\uff0c\u53c8\u89c1\u9ea6\u5c0f\u660e\u8fdb\u6765\uff0c\u867d\u89c9\u5f97\u602a\u602a\u7684\uff0c\u5374\u4e5f\u4e0d\u6562\u518d\u76d8\u95ee\uff0e\u4e0d\u8fc7\u5fc3\u91cc\u5374\u5728 \u6697\u81ea\u5600\u5495\u9053\uff1a\u201c\u8fd9\u4f4d\u4ed9\u5b50\u771f\u662f\u4e86\u4e0d\u8d77\uff0c\u4e00\u665a\u4e0a\u7adf\u8981\u6362\u4e24\u4e2a\u7537\u4eba\u624d\u8fc7\u763e\uff01\u201d \u3000\u3000\u672c\u6765\u8fd9\u65f6\u5019\u4e1c\u6d77\u9662\u7684\u5927\u95e8\u5e94\u8be5\u662f\u5173\u7740\u7684\uff0e\u56e0\u8bb8\u5c0f\u65e6\u4e8b\u5148\u6709\u4ea4\u4ee3\uff0c\u56e0\u4e4b\u6b64\u523b\u53ea\u662f\u865a\u63a9\u7740\u3002 \u3000\u3000\u5728\u9ea6\u5c0f\u660e\u7684\u60f3\u6cd5\u91cc\uff0c\u8bb8\u5c0f\u65e6\u5fc5\u5b9a\u5df2\u7ecf\u7761\u7740\u4e86\u3002\u54ea\u77e5\u5f53\u4ed6\u63ed\u5e18\u5165\u5185\uff0c\u53ea\u89c1\u8fd9\u5973\u4eba\u6b63\u62ab\u7740\u4e00 \u88ad\u8584\u7eb1\uff0c\u62e5\u88ab\u5750\u5728\u90a3\u91cc\u3002 \u3000\u3000\u9aa4\u89c1\u8fdb\u6765\u7684\u662f\u9ea6\u5c0f\u660e\uff0e\u8fd9\u5973\u4eba\u7684\u53cc\u773c\u987f\u65f6\u4e00\u4eae\uff0c\u56e0\u4e3a\u5979\u7b49\u7684\u5c31\u662f\u8fd9\u4e00\u523b\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u4e5f\u548c\u738b\u5927\u5eb7\u4e00\u6837\uff0c\u3000\u4e00\u8fdb\u95e8\u5c31\u5728\u6905\u4e0a\u5750\u4e0b\u3002 \u3000\u3000\u8bb8\u5c0f\u65e6\u53cc\u7738\u79cb\u6ce2\u95ea\u8f6c\uff0c\u9762\u6cdb\u6843\u82b1\uff0c\u55f2\u58f0\u55f2\u6c14\u9053\uff1a\u201c\u4f60\u600e\u4e48\u73b0\u5728\u624d\u6765\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u4e0d\u52a8\u58f0\u8272\u9053\uff1a\u201c\u970d\u76df\u4e3b\u521a\u624d\u624d\u628a\u4ee4\u724c\u4ea4\u7ed9\u6211\uff0c\u6211\u4e0d\u73b0\u5728\u6765\u4ec0\u4e48\u65f6\u5019\u6765\uff1f\u201d \u3000\u3000\u8bb8\u5c0f\u65e6\u5a9a\u773c\u2014\u7b11\u9053\uff1a\u201c\u65f6\u95f4\u4e0d\u65e9\u5566\uff01\u5feb\u4e0a\u5e8a\u5427\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4f60\u603b\u8be5\u8ba9\u6211\u5148\u4f11\u606f\u4f11\u606f\u3002\u201d \u3000\u3000\u8bb8\u5c0f\u65e6\u9053\uff1a\u201c\u4e0a\u5e8a\u8eba\u4e0b\u4f11\u606f\u4e0d\u662f\u66f4\u597d\u5417\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u6447\u5934\u9053\uff1a\u201c\u4e0a\u5e8a\u4ee5\u540e\u5c31\u4e0d\u80fd\u4f11\u606f\u5566\uff01\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u4e0d\u77e5\u9ea6\u5c0f\u660e\u662f\u5426\u6709\u610f\u800c\u53d1\uff0c\u4f46\u542c\u5728\u8bb8\u5c0f\u65e6\u8033\u6735\u91cc\uff0c\u5374\u4e50\u5728\u5fc3\u91cc\uff0c\u4e3a\u4e86\u8ba9\u5bf9\u65b9\u80fd\u517b \u517b\u7cbe\u795e\uff0c\u53ea\u597d\u5f3a\u5fcd\u7740\u6253\u5fc3\u5e95\u5411\u4e0a\u76f4\u51b2\u7684\u6b32\u706b\uff0c\u6682\u65f6\u4fdd\u6301\u9547\u5b9a\u3002 \u3000\u3000\u5c82\u77e5\u9ea6\u5c0f\u660e\u4e0d\u4f46\u4e45\u5750\u4e0d\u8d77\uff0c\u8fde\u773c\u775b\u4e5f\u95ed\u4e86\u4e0a\u53bb\u3002 \u3000\u3000\u8bb8\u5c0f\u65e6\u518d\u4e5f\u5fcd\u4e0d\u4f4f\uff0c\u642d\u8baa\u7740\u95ee\u9053\uff1a\u201c\u4f60\u600e\u4e48\u4e5f\u4e0d\u7741\u5f00\u773c\u770b\u770b\u6211\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u4f9d\u7136\u4e0d\u5f00\u773c\u9053\uff1a\u201c\u95ed\u4e0a\u773c\u53bb\uff0c\u624d\u662f\u771f\u6b63\u7684\u4f11\u606f\u3002\u201d \u3000\u3000\u8bb8\u5c0f\u65e6\u53c8\u662f\u4e00\u559c\uff0c\u6697\u9053\uff1a\u201c\u8fd9\u51e0\u591c\u7684\u7a7a\u865a\uff0c\u5f85\u4f1a\u513f\u7741\u5f00\u773c\u6765\u770b\u770b\uff0c\u6211\u8fd9\u91cc\u6709\u597d\u770b\u7684\uff0c\u53ef \u4ee5\u66ff\u4f60\u517b\u773c\u3002\u201d \u3000\u3000\u5979\u672c\u6765\u53ea\u62ab\u4e86\u4e00\u88ad\u8584\u7eb1\uff0c\u8584\u7eb1\u4e4b\u5185\uff0c\u5b8c\u5168\u4e2d\u7a7a\uff0c\u8fd9\u65f6\u628a\u88ab\u5b50\u4e00\u63a8\uff0c\u518d\u80a9\u8180\u4e00\u6296\uff0c\u5df2\u8131\u843d \u4e0b\u6765\uff0c\u90a3\u6cb9\u5149\u5149\u7684\u8eab\u5b50\uff0c\u5df2\u5b8c\u5168\u4e00\u4e1d\u4e0d\u6302\u3002 \u3000\u3000\u53ef\u60dc\u8fd9\u822c\u65d6\u65ce\u98ce\u5149\uff0c\u9ea6\u5c0f\u660e\u8fd8\u662f\u6ca1\u7741\u5f00\u773c\u2014\u77a7\uff0e \u3000\u3000\u201c\u5c0f\u4f19\u5b50\uff0c\u4f60\u600e\u4e48\u8fd8\u4e0d\u7741\u773c\uff1f\u201d \u3000\u3000\u201c\u4f60\u53eb\u8c01\u5c0f\u4f19\u5b50\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u662f\u4f60\u3002\u201d \u3000\u3000\u201c\u6211\u8001\u5566\uff01\u8001\u592b\u4eca\u5e74\u5df2\u4e5d\u5341\u6709\u4e5d\u3002\u201d \u3000\u3000\u201c\u4f60\u5f00\u7684\u4ec0\u4e48\u73a9\u7b11\uff1f\u201d \u3000\u3000\u201c\u8c01\u8ddf\u4f60\u5f00\u73a9\u7b11\uff0c\u8001\u592b\u8fde\u773c\u90fd\u7741\u4e0d\u5f00\u5566\uff01\u8001\u6210\u8fd9\u6837\u5b50\uff0e\u8fd8\u662f\u56de\u53bb\u5427\uff01\u201d \u3000\u3000\u201c\u4f60\u2026\u2026\u4f60\u771f\u8981\u8d70\uff1f\u201d \u3000\u3000\u201c\u8001\u592b\u504c\u5927\u7684\u5e74\u7eaa\uff0e\u82e5\u5728\u4f60\u8fd9\u91cc\u7761\u4e00\u665a\uff0c\u975e\u7fd8\u8fab\u5b50\u4e0d\u53ef\uff0c\u6211\u867d\u7136\u8001\u4e86\uff0c\u8fd8\u4e0d\u60f3\u73b0\u5728\u5c31 \u6b7b\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u8bf4\u7f62\u8d77\u8eab\uff0c\u987a\u624b\u628a\u4ee4\u724c\u653e\u5728\u684c\u4e0a\uff0e\u76f4\u5230\u8d70\u51fa\u623f\u95e8\uff0c\u624d\u7741\u5f00\u773c\u6765\u3002 \u3000\u3000\u8bb8\u5c0f\u65e6\u56e0\u6ca1\u7a7f\u8863\u670d\uff0c\u4e0d\u6562\u8ffd\u4e0b\u697c\u53bb\uff0c\u6025\u5f97\u5927\u53eb\u9053\uff1a\u201c\u9ea6\u5c0f\u4fa0\uff0c\u4f60\u522b\u8d70\uff0e\u5feb\u56de\u6765\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u4e0b\u4e86\u697c\uff0c\u8de8\u8fc7\u5929\u4e95\uff0c\u63a8\u5f00\u95e8\uff0e\u4e0d\u89c9\u4e00\u6123\u3002 \u3000\u3000\u6708\u5149\u4e0b\uff0c\u53ea\u89c1\u6c5f\u5357\u56db\u602a\u7684\u5c71\u9b48\u6bdb\u8d8a\u3001\u6c34\u9b3c\u6d77\u767b\u3001\u5730\u864e\u5305\u901a\u3001\u6797\u9b54\u82d7\u5f3a\u7adf\u5728\u95e8\u5916\u4e00\u5b57\u6392 \u5f00\uff0c\u963b\u4f4f\u53bb\u8def\uff0e \u3000\u3000\u539f\u6765\uff0c\u6c5f\u5357\u56db\u602a\u4eca\u665a\u8f6e\u503c\u5de1\u591c\uff0c\u5f53\u5de1\u5230\u603b\u5be8\u7684\u970d\u5143\u4f3d\u4f4f\u5904\u9644\u8fd1\uff0c\u6b63\u8d76\u4e0a\u970d\u5143\u4f3d\u9001\u8d70\u8bb8\u5c0f \u65e6\u5c1a\u672a\u56de\u623f\u3002 \u3000\u3000\u970d\u5143\u4f3d\u56e0\u66fe\u5bf9\u8bb8\u5c0f\u65e6\u8bf4\u8fc7\u82e5\u4eca\u665a\u518d\u51fa\u610f\u5916\uff0c\u4ed6\u60c5\u613f\u8d54\u4e0a\u8111\u888b\uff0c\u4e3a\u7b56\u4e07\u5168\uff0c\u4fbf\u5429\u5490\u6c5f\u5357\u56db \u602a\u5230\u4e1c\u6d77\u9662\u95e8\u5916\u5b88\u7740\uff0e\u7edd\u5bf9\u4e0d\u80fd\u8ba9\u8bb8\u5c0f\u65e6\u4eca\u665a\u7684\u5ba2\u4eba\u6e9c\u6389\u3002 \u3000\u3000\u6c5f\u5357\u56db\u602a\u9886\u547d\u4e4b\u540e\uff0c\u54ea\u6562\u6020\u6162\uff0c\u8fc5\u901f\u6765\u5230\u4e1c\u6d77\u9662\u95e8\u5916\uff0c\u6682\u65f6\u9690\u8eab\u5728\u6697\u5904\u3002 \u3000\u3000\u5f53\u9ea6\u5c0f\u660e\u8fdb\u5165\u4e1c\u6d77\u9662\u65f6\uff0c\u4ed6\u4eec\u90fd\u5df2\u770b\u89c1\uff0c\u65b9\u624d\u542c\u89c1\u697c\u68af\u54cd\uff0c\u5fc3\u77e5\u9ea6\u5c0f\u660e\u53ef\u80fd\u8981\u8d70\uff0c\u4e8e\u662f \u8fc5\u901f\u5730\u5728\u95e8\u53e3\u963b\u4f4f\u53bb\u8def\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u7565\u4e00\u6c89\u541f\uff0c\u5fc3\u91cc\u5df2\u6709\u4e86\u6570\uff0c\u5f53\u4e0b\u5728\u95e8\u5916\u6b62\u4f4f\u811a\u6b65\uff0c\u4e0d\u52a8\u58f0\u8272\u9053\uff1a\u201c\u4f60\u4eec\u56db\u4f4d\u8fd9\u7b97 \u4f55\u610f\uff1f\u201d \u3000\u3000\u56db\u602a\u4e2d\u7684\u8001\u5927\u5c71\u9b48\u6bdb\u8d8a\u9053\uff1a\u201c\u5728\u4e0b\u5f1f\u5144\u56db\u4eba\uff0c\u5949\u547d\u5b88\u4f4f\u4e1c\u6d77\u9662\u95e8\u53e3\uff0c\u5728\u5929\u4eae\u4e4b\u524d\uff0c\u4e0d\u51c6 \u4efb\u4f55\u4eba\u51fa\u53bb\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4f60\u4eec\u662f\u5949\u4ec0\u4e48\u4eba\u4e4b\u547d\uff1f\u201d \u3000\u3000\u5c71\u9b48\u6bdb\u8d8a\u9053\uff1a\u201c\u5f53\u7136\u662f\u5949\u7684\u76df\u4e3b\u4ee4\u8c15\uff01\u201d \u3000\u3000\u201c\u628a\u4ee4\u724c\u62ff\u6765\u6211\u770b\uff01\u201d \u3000\u3000\u201c\u76df\u4e3b\u7684\u53e3\u8c15\uff0c\u54ea\u91cc\u6709\u4ec0\u4e48\u4ee4\u724c\uff1f\u201d \u3000\u3000\u201c\u5728\u4e0b\u4e5f\u6709\u76df\u4e3b\u7684\u53e3\u8c15\u3002\u201d \u3000\u3000\u201c\u9ea6\u5c0f\u4fa0\u5949\u7684\u4ec0\u4e48\u53e3\u55bb\uff1f\u201d \u3000\u3000\u201c\u4e1c\u6d77\u9662\u4eca\u665a\u968f\u610f\u8fdb\u51fa\uff0e\u201d \u3000\u3000\u201c\u6bdb\u67d0\u4e0d\u4fe1\uff01\u201d \u3000\u3000\u201c\u4e0d\u4fe1\u5c31\u53bb\u95ee\u76df\u4e3b\u53bb\u3002\u201d \u3000\u3000\u53ea\u542c\u6c34\u9b3c\u6d77\u767b\u9053\uff1a\u201c\u8001\u5927\u522b\u542c\u4ed6\u8fd9\u4e00\u5957\uff0c\u8fd9\u5bb6\u4f19\u6700\u4f1a\u800d\u6ed1\u5934\uff0c\u522b\u4eba\u88ab\u4ed6\u800d\u4e86\u662f\u522b\u4eba\u7684 \u4e8b\uff0e\u54b1\u4eec\u53ef\u4e0d\u5403\u4ed6\u8fd9\u4e00\u5957\uff01\u201d \u3000\u3000\u5730\u864e\u5305\u901a\u9053\uff1a\u201c\u8fd9\u5bb6\u4f19\u7684\u6b66\u529f\u5f88\u9ad8\u3002\u201d \u3000\u3000\u6c34\u9b3c\u6d77\u767b\u9053\uff1a\u201c\u4ed6\u6b66\u529f\u867d\u9ad8\uff0c\u96be\u9053\u54b1\u4eec\u56db\u4e2a\u8054\u8d77\u624b\u6765\u8fd8\u6015\u4e86\u4ed6\uff1f\u201d \u3000\u3000\u5730\u864e\u5305\u901a\u7acb\u523b\u8f6c\u5934\u9053\uff1a\u201c\u8001\u5927\uff0e\u4f60\u770b\u600e\u4e48\u6837\uff1f\u4e0d\u80fd\u653e\u8d70\u4ed6\u662f\u76df\u4e3b\u7684\u4ee4\u8c15\uff0c\u54b1\u4eec\u5fc5\u987b\u8fbe\u6210 \u4efb\u52a1\uff0c\u5426\u5219\u62ff\u4ec0\u4e48\u5411\u76df\u4e3b\u4ea4\u4ee3\uff1f\u201d \u3000\u3000\u8fd9\u6c5f\u5357\u56db\u602a\uff0c\u65e9\u5c31\u5bf9\u9ea6\u5c0f\u660e\u770b\u4e0d\u987a\u773c\u3002\u53ea\u56e0\u9ea6\u5c0f\u660e\u6b66\u529f\u592a\u9ad8\uff0c\u53ea\u80fd\u6068\u5728\u5fc3\u91cc\u3002\u6b64\u523b\u89c1\u5bf9 \u529b\u624b\u65e0\u5bf8\u94c1\uff0c\u800c\u81ea\u5df1\u65b9\u9762\u56db\u4eba\u90fd\u63e1\u6709\u5175\u5203\u3002 \u3000\u3000\u518d\u52a0\u81ea\u5df1\u4eba\u591a\uff0c\u53c8\u6709\u970d\u5143\u4f3d\u7684\u8c15\u4ee4\uff0e\u8d81\u6b64\u6740\u6740\u9ea6\u5c0f\u660e\u7684\u9510\u6c14\uff0c\u5012\u662f\u4e2a\u96be\u5f97\u7684\u673a\u4f1a\uff0c\u5373\u4f7f \u7ed3\u679c\u4e86\u9ea6\u5c0f\u660e\uff0c\u4e5f\u6beb\u65e0\u7f6a\u8fc7\u53ef\u8a00\u3002 \u3000\u3000\u5c71\u9b48\u6bdb\u8d8a\u60f3\u5230\u8fd9\u91cc\u65f6\uff0c\u51b7\u51b7\u4e00\u7b11\u9053\uff1a\u201c\u9ea6\u5c0f\u4fa0\uff0c\u6bdb\u67d0\u529d\u4f60\u8fd8\u662f\u4e56\u4e56\u56de\u5230\u697c\u4e0a\u53bb\u7684\u597d\uff0c\u514d \u5f97\u4f24\u4e86\u548c\u6c14\u3002\u201d \u3000\u3000\u5176\u5b9e\uff0c\u4ee5\u9ea6\u5c0f\u660e\u7684\u8f7b\u529f\uff0c\u6839\u672c\u4e0d\u987b\u4ea4\u624b\uff0c\u817e\u8eab\u7531\u6c5f\u5357\u56db\u602a\u5934\u9876\u98de\u8fc7\uff0e\u7edd\u975e\u96be\u4e8b\u3002 \u3000\u3000\u4f46\u4ed6\u5374\u4e0d\u80af\u8fd9\u6837\u505a\uff0c\u4e5f\u51b3\u5b9a\u8d81\u8fd9\u673a\u4f1a\u7ed9\u6c5f\u5357\u56db\u602a\u4e00\u4e2a\u6559\u8bad\u3002 \u3000\u3000\u5c71\u9b48\u6bdb\u8d8a\u89c1\u9ea6\u5c0f\u660e\u4ecd\u7ad9\u5728\u90a3\u91cc\u4e0d\u7406\u4e0d\u776c\uff0c\u4e0d\u7531\u6c89\u4e0b\u55d3\u95e8\u9053\uff1a\u201c\u9ea6\u5c0f\u4fa0\uff0c\u6bdb\u67d0\u7684\u8bdd\u4f60\u542c\u5230 \u6ca1\u6709\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u5728\u4e0b\u8033\u6735\u4e0d\u804b\uff0e\u4f60\u8bb2\u7684\u4e5f\u4e0d\u662f\u5916\u56fd\u8bdd\uff0c\u5f53\u7136\u542c\u5230\u3002\u201d \u3000\u3000\u201c\u542c\u5230\u4e86\u4e3a\u4ec0\u4e48\u8fd8\u4e0d\u56de\u53bb\uff1f\u201d \u3000\u3000\u201c\u5728\u4e0b\u7684\u4e24\u6761\u817f\u53ea\u80fd\u5411\u524d\u8d70\uff0e\u4e0d\u80fd\u5f80\u540e\u9000\u3002\u201d \u3000\u3000\u201c\u82e5\u518d\u4e0d\u7406\uff0e\u6bdb\u67d0\u5c31\u6ca1\u4ec0\u4e48\u5ba2\u6c14\u7684\u4e86\u3002\u201d \u3000\u3000\u201c\u672c\u6765\u5c31\u7528\u4e0d\u7740\u4f60\u4eec\u5ba2\u6c14\uff0e\u5728\u4e0b\u5012\u8981\u770b\u770b\u4f60\u4eec\u54ea\u2014\u4e2a\u62e6\u5f97\u4f4f\u6211\u3002\u201d \u3000\u3000\u201c\u4f60\u662f\u656c\u9152\u4e0d\u5403\u7f5a\u9152\uff01\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u5728\u4e0b\u5c31\u6709\u8fd9\u4e2a\u6bdb\u75c5\uff0c\u4f60\u4eec\u662f\u2014\u8d77\u4e0a\uff0c\u8fd8\u662f\u4e00\u4e2a\u4e00\u4e2a\u5355\u6311\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u7684\u8fd9\u53e5\u8bdd\uff0c\u53cd\u800c\u4f7f\u5f97\u56db\u602a\u4e0d\u4fbf\u8054\u624b\u5408\u640f\uff0c\u6c34\u9b3c\u6d77\u767b\u62a2\u7740\u9053\uff1a\u201c\u8001\u5927\uff0c\u8001\u4e09\u3001\u8001 \u56db\uff0c\u4f60\u4eec\u53ea\u7ba1\u6682\u65f6\u522b\u52a8\u624b\uff0c\u6211\u6765\u5148\u4f1a\u4ed6\u4e00\u9635\uff0c\u8bd5\u8bd5\u4ed6\u7684\u6b66\u529f\u7a76\u7adf\u6709\u591a\u9ad8\uff01\u201d \u3000\u3000\u6c34\u9b3c\u6d77\u767b\u8bf4\u5b8c\u8bdd\uff0c\u624b\u4e2d\u7684\u77ed\u67c4\u6708\u7259\u94f2\u65e9\u5df2\u4eae\u4e86\u51fa\u6765\u3002 \u3000\u3000\u56db\u602a\u4e0d\u4f46\u7ef0\u53f7\u602a\uff0c\u8fde\u4f7f\u7528\u7684\u5175\u5203\u4e5f\u602a\uff0c\u9664\u6c34\u9b3c\u6d77\u767b\u7528\u7684\u662f\u77ed\u67c4\u6708\u7259\u94f2\u5916\uff0e\u8001\u5927\u5c71\u9b48\u6bdb\u8d8a \u662f\u874e\u5b50\u94a9\uff0c\u8001\u4e09\u5730\u864e\u5305\u901a\u662f\u516b\u94a9\u9e21\u722a\u9570\uff0e\u8001\u56db\u6797\u9b54\u82d7\u5f3a\u662f\u72d0\u9f7f\u952f\uff0c\u79f0\u5f97\u4e0a\u662f\u602a\u4e0a\u52a0\u602a\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u7a33\u7ad9\u5f53\u5730\uff0e\u7eb9\u98ce\u4e0d\u52a8\uff0c\u6de1\u6de1\u4e00\u7b11\u9053\uff1a\u201c\u53ea\u7ba1\u8fdb\u62db\uff0c\u522b\u8001\u6123\u5728\u90a3\u91cc\u3002\u201d \u3000\u3000\u6c34\u9b3c\u6d77\u767b\u660e\u77e5\u9ea6\u5c0f\u660e\u672a\u5e26\u5175\u5203\uff0c\u5374\u6545\u610f\u9053\uff1a\u201c\u5c0f\u5b50\uff0c\u4f60\u4e5f\u4eae\u51fa\u5175\u5203\u6765\u5427\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u5728\u4e0b\u4eca\u665a\u662f\u4e1c\u6d77\u9662\u7684\u5ad6\u5ba2\uff0c\u5ad6\u5ba2\u54ea\u6709\u5e26\u5200\u5e26\u67aa\u6765\u7684\uff0c\u4f55\u51b5\u5bf9\u4ed8\u4f60\u4eec\u8fd9\u79cd\u732b \u5934\u72d7\u8033\uff0c\u4f55\u52b3\u52a8\u7528\u5bb6\u4f19\uff1f\u201d \u3000\u3000\u6c34\u9b3c\u6d77\u767b\u559d\u9053\uff1a\u201c\u653e\u8086\uff01\u4ed9\u5b50\u600e\u4e48\u662f\u53ef\u4ee5\u5ad6\u7684\uff1f\u5e94\u5f53\u8bf4\u662f\u966a\u9a7e\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u53cd\u800c\u8010\u4f4f\u4e86\u6027\u5b50\uff0c\u7b11\u9053\uff1a\u201c\u5ad6\u5979\u662f\u62ac\u4e3e\u5979\uff0c\u90a3\u79cd\u5973\u4eba\u6bd4\u5993\u5973\u8fd8\u8981\u8d31\u4e09\u7ea7\u3002\u201d \u3000\u3000\u201c\u5927\u80c6\uff01\u4ed9\u5b50\u5c82\u662f\u5bb9\u4f60\u4fae\u8fb1\u7684\uff1f\u51ed\u4ec0\u4e48\u8bf4\u5979\u6bd4\u5993\u5973\u8fd8\u8981\u8d31\u4e09\u7ea7\uff1f\u201d \u3000\u3000\u201c\u5993\u5973\u53ea\u662f\u5173\u4e0a\u623f\u95e8\u7ed9\u4e00\u4e2a\u4eba\u770b\u7684\uff0c\u90a3\u5973\u4eba\u5374\u6562\u5f53\u4f17\u8131\u88e4\u5b50\uff0c\u5979\u4f55\u5c1d\u8d76\u5f97\u4e0a\u4e00\u540d\u5993 \u5973\uff1f\u201d \u3000\u3000\u6c34\u9b3c\u6d77\u767b\u66b4\u559d\u4e00\u58f0\uff0c\u51cc\u8eab\u75be\u6251\u800c\u4e0a\uff0c\u77ed\u67c4\u6708\u7259\u94f2\u95ea\u7535\u822c\u7167\u51c6\u9ea6\u5c0f\u660e\u5fc3\u7a9d\u523a\u4e0b\u3002 \u3000\u3000\u8fd9\u6c5f\u5357\u56db\u602a\u8eab\u624b\u867d\u65e0\u6cd5\u548c\u5cad\u5357\u4e8c\u5947\u76f8\u6bd4\uff0c\u4f46\u5728\u6c5f\u5357\u7eff\u6797\u4e2d\uff0c\u4e5f\u662f\u53ee\u5f53\u54cd\u7684\u4eba\u7269\uff0c\u5c24\u5176\u51fa \u624b\u72e0\u8fa3\uff0c\u62db\u6570\u602a\u5f02\uff0c\u5bf9\u8d77\u9635\u6765\uff0c\u5f88\u4ee4\u5bf9\u65b9\u5934\u75db\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u4f9d\u7136\u7a33\u7ad9\u5f53\u5730\uff0c\u76f4\u5230\u6708\u7259\u94f2\u4e34\u8fd1\u80f8\u4e0d\u8db3\u534a\u5c3a\u65f6\uff0c\u624d\u84e6\u5730\u65cb\u8eab\u6ed1\u6b65\uff0c\u5288\u51fa\u4e00\u638c\u3002 \u3000\u3000\u8fd9\u2014\u638c\u5a01\u529b\u8db3\u53ef\u5f00\u7891\u88c2\u77f3\uff0c\u751f\u751f\u628a\u6708\u7259\u94f2\u8361\u504f\u4e00\u5c3a\u6709\u4f59\uff0c\u540c\u65f6\u638c\u98ce\u4f59\u5a01\uff0c\u4e5f\u626b\u4e2d\u6c34\u9b3c\u6d77 \u767b\u7684\u5de6\u534a\u8eab\u3002 \u3000\u3000\u6c34\u9b3c\u6d77\u767b\u95f7\u54fc\u2014\u58f0\uff0c\u5fcd\u75db\u5728\u5730\u4e0a\u6253\u4e2a\u8e09\u8dc4\u4e4b\u540e\uff0c\u7adf\u53c8\u659c\u8eab\u75be\u6251\u8fc7\u6765\uff0c\u6708\u7259\u94f2\u518d\u5ea6\u523a\u6765\uff0e \u800c\u4e14\u529b\u9053\u6bd4\u5148\u524d\u7adf\u7136\u4e1d\u6beb\u4e0d\u51cf\uff0e \u3000\u3000\u53ef\u89c1\u8fd9\u602a\u7269\u4e0d\u4f46\u8010\u529b\u5927\uff0c\u97e7\u6027\u4e5f\u9887\u60ca\u4eba\u3002 \u3000\u3000\u8fd9\u4e00\u6765\u7ec8\u4e8e\u628a\u9ea6\u5c0f\u660e\u6fc0\u51fa\u771f\u706b\uff0c\u811a\u4e0b\u672b\u52a8\uff0c\u4e0a\u8eab\u8fc5\u901f\u5411\u5de6\u65b9\u4e00\u4fa7\uff0c\u4fdf\u94f2\u950b\u7531\u80f8\u524d\u63a0\u8fc7\uff0c \u500f\u5ffd\u95f4\u5de6\u624b\u5411\u5bf9\u65b9\u63e1\u94f2\u53f3\u8155\u4e0a\u6263\u53bb\uff0c\u7d27\u8ddf\u7740\u53f3\u624b\u518d\u62cd\u51fa\u4e00\u638c\u3002 \u3000\u3000\u53ea\u542c\u6c34\u9b3c\u6d77\u767b\u4e00\u58f0\u60e8\u53eb\uff0c\u53f3\u81c2\u4e00\u9ebb\uff0c\u77ed\u67c4\u6708\u7259\u94f2\u5df2\u6389\u843d\u5730\u4e0a\uff0c\u63a5\u7740\u4e00\u58f0\u66b4\u54cd\uff0c\u5de6\u988a\u4e5f\u6328 \u4e86\u91cd\u91cd\u2014\u63b4\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u63b4\u51fa\u4e00\u638c\u540e\uff0c\u6263\u4f4f\u5bf9\u65b9\u53f3\u8155\u7684\u5de6\u624b\u4e94\u6307\u8d81\u52bf\u677e\u5f00\u4e4b\u524d\uff0c\u731b\u529b\u5411\u4e00\u4fa7\u7529\u53bb\uff0e \u3000\u3000\u6c34\u9b3c\u6d77\u767b\u7acb\u523b\u88ab\u629b\u8d77\u4e00\u4e08\u591a\u9ad8\uff0c\u843d\u5730\u4e4b\u540e\uff0c\u51b2\u52bf\u4ecd\u672a\u505c\u4f4f\uff0c\u8fde\u6253\u4e09\u56db\u4e2a\u6eda\u7ffb\uff0c\u624d\u7b97\u7a33\u4e86 \u4e0b\u6765\uff0e \u3000\u3000\u5f53\u4ed6\u6323\u624e\u7740\u52c9\u5f3a\u722c\u8d77\uff0c\u6ee1\u9762\u5df2\u88ab\u649e\u5f97\u9c9c\u8840\u76f4\u6d41\uff0c\u5c24\u5176\u56e0\u8116\u5b50\u626d\u5230\uff0c\u8111\u888b\u6b6a\u6b6a\u659c\u659c\u7684\u659c\u4fa7 \u5728\u4e00\u8fb9\uff0c\u5f53\u771f\u53d8\u6210\u4e86\u4e00\u4e2a\u602a\u7269\uff0e \u3000\u3000\u5c71\u9b48\u6bdb\u8d8a\u3001\u5730\u864e\u5305\u901a\u3001\u6797\u9b54\u82d7\u5f3a\u4e00\u89c1\u6c34\u9b3c\u6d77\u767b\u88ab\u6253\u5f97\u5982\u6b64\u72fc\u72c8\uff0c\u4e00\u58f0\u547c\u54e8\uff0c\u7acb\u523b\u4ece\u4e09\u4e2a \u4e0d\u540c\u65b9\u5411\u9f50\u9f50\u653b\u4e86\u4e0a\u6765\uff0e \u3000\u3000\u8fd9\u4e09\u4eba\u8054\u624b\u7684\u5a01\u529b\uff0e\u81ea\u662f\u975e\u540c\u5c0f\u53ef\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u94a9\u5f71\u95ea\u95ea\uff0c\u9570\u5149\u4ea4\u9519\uff0c\u952f\u9f7f\u7ffb\u6eda\uff0c\u5939\u6742 \u7740\u5636\u5636\u54bb\u54bb\u7684\u5229\u5203\u7834\u98ce\u4e4b\u58f0\uff0c\u628a\u4e08\u4f59\u65b9\u5706\u4e4b\u95f4\uff0c\u7b3c\u7f69\u5f97\u5bc6\u4e0d\u900f\u98ce\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u8d64\u624b\u7a7a\u62f3\uff0c\u5728\u8fd9\u79cd\u60c5\u5f62\u4e0b\uff0c\u4e0d\u80fd\u786c\u62fc\uff0c\u5bf9\u65b9\u5175\u5203\u672b\u5230\uff0c\u4ed6\u5df2\u51cc\u7a7a\u800c\u8d77\uff0c\u5934\u4e0a\u811a \u4e0b\uff0c\u53cc\u81c2\u4ea4\u6325\uff0c\u8fde\u73af\u5288\u51fa\u638c\u529b\u3002 \u3000\u3000\u4e09\u602a\u89c1\u6b64\u60c5\u5f62\uff0e\u9f50\u9f50\u4ef0\u9996\u5411\u4e0a\u653b\u51fb\u3002 \u3000\u3000\u5728\u4ed6\u4eec\u7684\u9884\u6599\uff0c\u9ea6\u5c0f\u660e\u5728\u7a7a\u4e2d\u4e0d\u8fc7\u652f\u6301\u9877\u523b\u5de5\u592b\uff0c\u53ea\u8981\u2014\u843d\u4e0b\uff0c\u5728\u4e09\u79cd\u5175\u5203\u7684\u4ea4\u5e26\u653b\u51fb \u4e0b\uff0c\u7eb5\u7136\u4e0d\u7c89\u8eab\u788e\u9aa8\uff0c\u4e5f\u5fc5\u96be\u9003\u6d3b\u547d\u3002\u4f46\u5947\u602a\u7684\u662f\uff0e\u9ea6\u5c0f\u660e\u5c45\u7136\u80fd\u5386\u4e45\u4e0d\u5760\u3002 \u3000\u3000\u5176\u5b9e\uff0c\u4e00\u4e2a\u4eba\u7eb5\u7136\u8f7b\u529f\u51fa\u795e\u5165\u5316\uff0c\u9ad8\u5f97\u4e0d\u80fd\u518d\u9ad8\uff0e\u4e5f\u7edd\u4e0d\u53ef\u80fd\u5c65\u7a7a\u8e48\u865a\u5386\u4e45\u4e0d\u5760\uff0e\u9ea6\u5c0f \u660e\u53ea\u4e0d\u8fc7\u662f\u85c9\u7740\u4e09\u602a\u5175\u5203\u5411\u4e0a\u653b\u51fb\u7684\u6f5c\u529b\u800c\u6258\u4f4f\u4e86\u8eab\u5b50\uff0c\u82e5\u4e09\u602a\u6b64\u65f6\u505c\u624b\u4e0d\u653b\uff0c\u4ed6\u7acb\u523b\u5c31\u4f1a \u7531\u7a7a\u4e2d\u5760\u4e0b\u3002 \u3000\u3000\u504f\u504f\u4e09\u602a\u867d\u7136\u65e0\u6bd4\u7075\u5149\uff0c\u5374\u6ca1\u60f3\u5230\u8fd9\u79cd\u9053\u7406\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\u9ea6\u5c0f\u660e\u5728\u7a7a\u4e2d\u4fef\u8eab\u4e0b\u51fb\uff0c\u7167\u6837\u4e5f\u653b\u52bf\u51cc\u5389\uff0c\u4e09\u602a\u7eb5\u7136\u60f3\u5230\u8fd9\u4e00\u5c42\uff0c\u4e5f\u4e0d\u6562\u7f62\u624b\u4e0d \u653b\u3002 \u3000\u3000\u6fc0\u6218\u4e2d\uff0c\u7a81\u95fb\u6797\u9b54\u82d7\u5f3a\u53d1\u51fa\u4e00\u58f0\u60e8\u547c\uff0c\u968f\u5373\u5012\u6454\u800c\u51fa\uff0c\u4ef0\u5367\u5730\u4e0a\uff0c\u534a\u664c\u722c\u4e0d\u8d77\u6765\u3002 \u3000\u3000\u539f\u6765\u6797\u9b54\u82d7\u5f3a\u5df2\u88ab\u9ea6\u5c0f\u660e\u4e00\u638c\u51fb\u4e2d\u80a9\u5934\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u89c1\u5df2\u53ea\u5269\u4e24\u540d\u5bf9\u624b\uff0c\u538b\u529b\u987f\u51cf\uff0e\u968f\u5373\u8dc3\u9000\u4e08\u4f59\uff0c\u843d\u4e0b\u5730\u6765\uff0c\u7136\u540e\u518d\u6251\u8eab\u653b\u53bb\u3002 \u3000\u3000\u5c71\u9b48\u6bdb\u8d8a\u548c\u5730\u864e\u5305\u901a\u6b64\u523b\u5df2\u6709\u4e9b\u5fc3\u60ca\u80c6\u98a4\uff0c\u5f00\u59cb\u624b\u60ca\u811a\u4e71\uff0c\u4f46\u5904\u6b64\u60c5\u5f62\u4e0b\uff0c\u4ecd\u5fc5\u987b\u5168\u529b \u8fce\u6218\u3002 \u3000\u3000\u5927\u7ea6\u4e03\u516b\u62db\u8fc7\u53bb\uff0e\u7a81\u89c1\u9ea6\u5c0f\u660e\u4e00\u4e2a\u5927\u65cb\u8eab\uff0c\u95ea\u7535\u822c\u98de\u8d77\u4e00\u811a\uff0c\u6b63\u8e22\u5728\u5730\u864e\u5305\u901a\u7684\u53f3\u80a9\u3002 \u3000\u3000\u5730\u864e\u5305\u901a\u4e00\u58f0\u95f7\u54fc\uff0c\u76f4\u6325\u51fa\u4e24\u4e09\u4e08\u8fdc\uff0c\u843d\u5730\u4e4b\u540e\uff0c\u52a8\u4e5f\u4e0d\u52a8\uff0e\u60f3\u5fc5\u95ed\u8fc7\u6c14\u53bb\u3002 \u3000\u3000\u5c71\u9b48\u6bdb\u8d8a\u4e00\u89c1\u4e0d\u5999\uff0c\u54ea\u91cc\u8fd8\u6562\u518d\u6218\uff0c\u8f6c\u8eab\u62d4\u817f\u4fbf\u5411\u603b\u575b\u65b9\u5411\u72c2\u5954\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u5c82\u80fd\u5bb9\u4ed6\u9003\u8131\uff0c\u521a\u8981\u817e\u8eab\u8ffd\u53bb\uff0c\u53ea\u542c\u8033\u9645\u54cd\u8d77\u4e00\u58f0\u51b7\u559d\u9053\uff1a\u201c\u5c82\u6709\u6b64\u7406\uff0c\u81ea\u5bb6\u4eba \u54ea\u91cc\u5bb9\u5f97\u4f60\u4eec\u80e1\u95f9\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u542c\u51fa\u662f\u970d\u5143\u4f3d\u7684\u58f0\u97f3\uff0c\u51dd\u76ee\u4e00\u671b\uff0c\u53ea\u89c1\u6570\u4e08\u5916\u7684\u4e00\u68f5\u5927\u6811\u4e0b\uff0c\u679c\u7136\u6b63\u7ad9\u7740\u970d\u5143 \u4f3d\u3002 \u3000\u3000\u5728\u970d\u5143\u4f3d\u8eab\u540e\uff0c\u66f4\u6709\u5cad\u5357\u4e8c\u5947\u641c\u9b42\u624b\u5df4\u5929\u4e49\u548c\u62d8\u9b44\u7d22\u5b8b\u5929\u94ce\u3002 \u3000\u3000\u5df4\u3001\u5b8b\u4e8c\u4eba\u8eab\u540e\uff0c\u4ecd\u6709\u51e0\u4e2a\u4eba\u5f71\uff0c\u53ea\u56e0\u6811\u836b\u751a\u6d53\uff0c\u6708\u5149\u65e0\u6cd5\u5c04\u8fdb\uff0e\u53c8\u8fdc\u5728\u6570\u4e08\u4e4b\u5916\uff0c\u96be \u4ee5\u8ba4\u51fa\u662f\u8c01\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u53ea\u597d\u4e0d\u518d\u8ffd\u88ad\u5c71\u9b48\u6bdb\u8d8a\uff0c\u7a33\u7ad9\u539f\u5730\uff0c\u4ee5\u5f85\u970d\u5143\u4f3d\u5982\u4f55\u5904\u7f6e\u3002 \u3000\u3000\u8fd9\u65f6\u970d\u5143\u4f3d\u5df2\u7f13\u7f13\u4e3e\u6b65\u800c\u6765\uff0c\u8eab\u540e\u7d27\u968f\u7740\u5df4\u5929\u4e49\u548c\u5b8b\u5929\u94ce\u3002 \u3000\u3000\u970d\u5143\u4f3d\u76f4\u8d70\u5230\u8ddd\u9ea6\u5c0f\u660e\u4e0d\u8db3\u4e00\u4e08\u8fdc\u624d\u505c\u4e0b\u811a\u6b65\u3002 \u3000\u3000\u4ed6\u672b\u5411\u9ea6\u5c0f\u660e\u95ee\u8bdd\uff0c\u5374\u56de\u5934\u9ad8\u58f0\u9053\uff1a\u201c\u6bdb\u8d8a\u8fc7\u6765\uff01\u201d \u3000\u3000\u5c71\u9b48\u6bdb\u8d8a\u6b64\u523b\u6709\u970d\u5143\u4f3d\u4f5c\u4e3b\uff0c\u5f53\u7136\u4e0d\u518d\u60e7\u6015\uff0c\u5e94\u58f0\u5954\u4e86\u8fc7\u53bb\uff0c\u5e76\u5411\u970d\u5143\u4f3d\u606d\u606d\u656c\u656c\u884c\u4e86 \u2014\u793c\u3002 \u3000\u3000\u970d\u5143\u4f3d\u4e0d\u52a8\u58f0\u8272\u95ee\u9053\uff1a\u201c\u65b9\u624d\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d \u3000\u3000\u5176\u5b9e\uff0c\u970d\u5143\u4f3d\u5bf9\u65b9\u624d\u7684\u60c5\u5f62\uff0c\u65e9\u5df2\u770b\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u8fde\u539f\u56e0\u4e5f\u5168\u6709\u6570\uff0e\u6b64\u523b\u660e\u77e5\u6545\u95ee\uff0c\u53ea \u662f\u8981\u6446\u51fa\u4ed6\u7eff\u6797\u76df\u4e3b\u7684\u6d3e\u5934\u800c\u5df2\u3002 \u3000\u3000\u5c71\u9b48\u6bdb\u8d8a\u8eac\u8eab\u7b54\u9053\uff1a\u201c\u5c5e\u4e0b\u56db\u4eba\u5949\u76df\u4e3b\u4e4b\u547d\uff0c\u4e0d\u51c6\u4efb\u4f55\u4eba\u8fdb\u51fa\u4e1c\u6d77\u9662\u3002\u504f\u504f\u59d3\u9ea6\u7684\u4e0d\u9075 \u76df\u4e3b\u4ee4\u8c15\uff0c\u975e\u8981\u95ef\u51fa\u53bb\u4e0d\u53ef\uff0c\u5c5e\u4e0b\u56db\u4eba\u88ab\u8feb\u4e0d\u5f97\u4e0d\u51fa\u624b\u62e6\u963b\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u628a\u4ed6\u62e6\u4e0b\u6ca1\u6709\uff1f\u201d \u3000\u3000\u5c71\u9b48\u6bdb\u8d8a\u7f9e\u7ea2\u6ee1\u9762\uff0c\u5e72\u54b3\u4e86\u58f0\u9053\uff1a\u201c\u5c5e\u4e0b\u4eec\u6b66\u529f\u4e0d\u6d4e\uff0c\u62e6\u4e0d\u4f4f\u4ed6\uff0c\u53ef\u662f\u4ed6\u4e0d\u8be5\u628a\u81ea\u5df1\u4eba \u6253\u6210\u8fd9\u6837\u3002\u5c5e\u4e0b\u4eec\u662f\u5949\u547d\u884c\u4e8b\uff0c\u5f04\u6210\u8fd9\u79cd\u540e\u679c\uff0c\u8fd8\u671b\u76df\u4e3b\u4f5c\u4e3b\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u53f1\u9053\uff1a\u201c\u6b66\u529f\u4e0d\u5982\u4eba\uff0c\u53ea\u6028\u4f60\u4eec\u5b66\u827a\u4e0d\u7cbe\uff0c\u8fd8\u6709\u8138\u5728\u8001\u592b\u9762\u524d\u7533\u51a4\uff0c\u7b80\u76f4\u5c82\u6709\u6b64 \u7406\u3002\u201d \u3000\u3000\u5c71\u9b48\u6bdb\u8d8a\u88ab\u9a82\u5f97\u54ea\u91cc\u8fd8\u6562\u5f00\u53e3\u3002 \u3000\u3000\u970d\u5143\u4f3d\u8fd9\u624d\u89c6\u7ebf\u8f6c\u5411\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u9ea6\u8001\u5f1f\u8f9b\u82e6\u4e86\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u51b7\u58f0\u9053\uff1a\u201c\u6536\u62fe\u8fd9\u56db\u540d\u602a\u7269\uff0c\u8fd8\u7b97\u4e0d\u5f97\u4ec0\u4e48\u8f9b\u82e6\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u5475\u5475\u7b11\u9053\uff1a\u201c\u9ea6\u8001\u5f1f\u4f1a\u9519\u610f\u4e86\uff0c\u8001\u592b\u662f\u8bf4\u4f60\u5728\u8bb8\u4ed9\u5b50\u90a3\u91cc\u8f9b\u82e6\u4e86\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u5bf9\u4ed8\u4e00\u4e2a\u5973\u4eba\uff0c\u66f4\u6ca1\u4ec0\u4e48\u8f9b\u82e6\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u6697\u559c\uff1a\u201c\u4f60\u771f\u5bf9\u4ed8\u8fc7\u4ed9\u5b50\u4e86\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u82e5\u6ca1\u5bf9\u4ed8\u5979\uff0c\u6211\u600e\u4f1a\u79bb\u5f00\u4e1c\u6d77\u9662\u3002\u201d \u3000\u3000\u5ffd\u542c\u4f20\u6765\u4e00\u4e2a\u55f2\u91cc\u55f2\u6c14\u7684\u58f0\u97f3\u9053\uff1a\u201c\u970d\u76df\u4e3b\u522b\u542c\u4ed6\u7684\uff0c\u4ed6\u6839\u672c\u4ec0\u4e48\u4e8b\u90fd\u6ca1\u505a\uff01\u201d \u3000\u3000\u539f\u6765\u4e0d\u77e5\u4ec0\u4e48\u65f6\u5019\uff0e\u8bb8\u5c0f\u65e6\u5df2\u62ab\u7740\u4e00\u88ad\u8584\u8584\u7684\u9ed1\u7eb1\uff0c\u7ad9\u5728\u4e1c\u6d77\u9662\u95e8\u53e3\u3002 \u3000\u3000\u970d\u5143\u4f3d\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u4ed6\u771f\u6ca1\u529e\u8fc7\u4e8b\uff1f\u201d \u3000\u3000\u8bb8\u5c0f\u65e6\u9053\uff1a\u201c\u6211\u4e3a\u4ec0\u4e48\u8981\u9a97\u4f60\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u563f\u563f\u7b11\u9053\uff1a\u201c\u9ea6\u8001\u5f1f\uff0c\u8bb8\u4ed9\u5b50\u5929\u751f\u4e3d\u8d28\uff0c\u522b\u7684\u7537\u4eba\u60f3\u90fd\u60f3\u4e0d\u5230\u624b\uff0c\u4f60\u4e3a\u4ec0\u4e48\u53cd\u800c \u8fdb\u95e8\u4e0d\u529e\u4e8b\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u5f04\u4e0d\u6e05\u4ec0\u4e48\u53eb\u201c\u529e\u4e8b\u201d\uff0c\u54fc\u4e86\u58f0\u9053\uff1a\u201c\u4f60\u522b\u542c\u5979\u80e1\u8bf4\uff01\u6211\u867d\u7136\u8d70\u4e86\u4e00\u8d9f\u4e1c\u6d77\u9662\uff0c \u5374\u5e76\u975e\u670d\u4f8d\u5979\u7684\u4eba\uff0c\u65e2\u7136\u4e0d\u662f\u670d\u4f8d\u5979\u7684\u4eba\uff0c\u8fd8\u66ff\u5979\u529e\u7684\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u7b11\u9053\uff1a\u201c\u90a3\u670d\u4f8d\u4ed9\u5b50\u7684\u4e24\u4e2a\u4eba\uff0c\u60f3\u529e\u4e8b\u4e5f\u529e\u4e0d\u6210\uff0c\u53ea\u6709\u50cf\u4f60\u8fd9\u6837\u7684\u4eba\uff0c\u624d\u529e\u5f97\u6210 \u4e8b\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u80fd\u4e0d\u80fd\u529e\u5f97\u6210\u4e8b\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u8138\u4e0a\u4e00\u70ed\u9053\uff1a\u201c\u8001\u592b\u5f53\u7136\u529e\u8fc7\u4e8b\uff0e\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u5728\u4e0b\u6ca1\u5b66\u8fc7\uff0c\u4ece\u6765\u4e0d\u4f1a\u529e\u4e8b\u3002\u4f60\u65e2\u7136\u4f1a\uff0c\u5c31\u7531\u4f60\u529e\u5427\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u53cc\u988a\u62bd\u6410\u4e86\u51e0\u4e0b\u9053\uff1a\u201c\u9ea6\u8001\u5f1f\uff0c\u8001\u592b\u662f\u770b\u5f97\u8d77\u4f60\uff0c\u8d4f\u8bc6\u4f60\u662f\u4f4d\u4eba\u624d\uff0c\u6240\u4ee5\u624d\u6170\u52b3 \u4f60\u5230\u4e1c\u6d77\u9662\u6765\u8fc7\u591c\u3002\u4f60\u600e\u4e48\u4e0d\u4f46\u4e0d\u77e5\u611f\u6069\u56fe\u62a5\uff0c\u53cd\u800c\u5bf9\u8001\u592b\u5fc3\u5b58\u6076\u610f\uff0c\u8bed\u5e26\u8bbd\u523a\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u51b7\u54fc\u4e00\u58f0\u9053\uff1a\u201c\u56e0\u4e3a\u4f60\u6839\u672c\u662f\u80e1\u641e\uff0c\u53eb\u6211\u600e\u4e48\u611f\u6fc0\u4f60\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u6123\u4e86\u4e00\u773c\u9053\uff1a\u201c\u8001\u592b\u600e\u4e48\u5728\u80e1\u641e\uff0c\u4f60\u7ed9\u6211\u8bf4\u6e05\u695a\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4f60\u65e2\u7136\u662f\u6170\u52b3\u6211\uff0c\u6211\u6765\u5230\u4e1c\u6d77\u9662\u540e\uff0c\u5c31\u8be5\u8212\u8212\u670d\u670d\uff0c\u4ec0\u4e48\u4e8b\u4e5f\u4e0d\u505a\uff0c\u4e3a\u4ec0 \u4e48\u5374\u53c8\u8981\u6211\u529e\u4e8b\uff1f\u529e\u4e8b\u5f53\u7136\u5f88\u8f9b\u82e6\uff0c\u4f60\u5047\u85c9\u6170\u52b3\u4e4b\u540d\uff0c\u5374\u8981\u903c\u6211\u529e\u4e8b\uff0e\u5c82\u4e0d\u662f\u80e1\u641e\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u5f04\u4e0d\u6e05\u695a\u9ea6\u5c0f\u660e\u7a76\u7adf\u662f\u6545\u610f\u6c14\u4ed6\u8fd8\u662f\u771f\u4e0d\u660e\u767d\u529e\u4e8b\u7684\u522b\u4e00\u7528\u610f\uff0e\u88ab\u95ee\u5f97\u4e00\u53e3\u6d53\u75f0 \u618b\u5728\u5589\u7ba1\u91cc\uff0c\u8fde\u54b3\u4e86\u51e0\u58f0\uff0c\u624d\u626b\u6e05\u55d3\u95e8\u9053\uff1a\u201c\u4f60\u5c31\u662f\u4e0d\u60f3\u529e\u4e8b\uff0c\u8001\u592b\u4e5f\u4e0d\u52c9\u5f3a\uff0c\u73b0\u5728\u53ea\u8981\u4f60 \u542c\u8001\u592b\u4e00\u53e5\u8bdd\uff0e\u201d \u3000\u3000\u201c\u4f60\u8981\u8bf4\u4ec0\u4e48\u5c31\u8bf4\u597d\u4e86\uff01\u201d \u3000\u3000\u201c\u9a6c\u4e0a\u518d\u56de\u4e1c\u6d77\u9662\u53bb\u966a\u8bb8\u4ed9\u5b50\u3002\u201d \u3000\u3000\u201c\u5e94\u8be5\u662f\u5979\u966a\u6211\uff0c\u4e3a\u4ec0\u4e48\u8981\u6211\u966a\u5979\uff1f\u201d \u3000\u3000\u201c\u4e0d\u7ba1\u8c01\u966a\u8c01\uff0c\u53ea\u8981\u4f60\u56de\u4e1c\u6d77\u9662\u5c31\u597d\u3002\u201d \u3000\u3000\u201c\u6211\u4e0d\u56de\u53bb\uff01\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u4e0d\u56de\u53bb\uff1f\u201d \u3000\u3000\u201c\u5927\u4e08\u592b\u53ea\u80fd\u524d\u8fdb\uff0c\u4e0d\u53ef\u56de\u5934\u3002\u6211\u65e2\u7136\u5df2\u7ecf\u51fa\u6765\u4e86\uff0c\u5c31\u7edd\u4e0d\u56de\u53bb\u3002\u201d \u3000\u3000\u201c\u96be\u9053\u4f60\u8fde\u8001\u592b\u7684\u8bdd\u90fd\u4e0d\u80af\u542c\uff1f\u201d \u3000\u3000\u201c\u6211\u5df2\u7ecf\u542c\u8fc7\u4f60\u7684\u8bdd\u4e86\uff0c\u4f60\u53d1\u4ee4\u724c\u8981\u6211\u5230\u4e1c\u6d77\u9662\u6765\uff0c\u6211\u534a\u591c\u4e00\u66f4\u722c\u8d77\u5e8a\u4ec0\u4e48\u8bdd\u90fd\u6ca1\u8bb2\u5c31 \u6765\u4e86\u3002\u81f3\u4e8e\u73b0\u5728\u79bb\u5f00\u4e1c\u6d77\u9662\uff0c\u90a3\u662f\u6211\u4e2a\u4eba\u7684\u4e8b\uff0e\u4f60\u867d\u7136\u662f\u76df\u4e3b\uff0c\u4e5f\u65e0\u6743\u8fc7\u95ee\uff01\u201d \u3000\u3000\u201c\u8001\u592b\u4e3a\u4ec0\u4e48\u65e0\u6743\u8fc7\u95ee\uff1f\u201d \u3000\u3000\u201c\u4f60\u53ea\u89c4\u5b9a\u6211\u6765\uff0c\u5e76\u6ca1\u89c4\u5b9a\u6211\u4ec0\u4e48\u65f6\u5019\u8d70\uff1f\u201d \u3000\u3000\u201c\u4f60\u662f\u4e00\u5b9a\u4e0d\u56de\u4e1c\u6d77\u9662\u4e86\uff1f\u201d \u3000\u3000\u201c\u4e0d\u56de\u53bb\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u518d\u4e5f\u96be\u6291\u6012\u706b\uff0c\u6b63\u8981\u53d1\u4f5c\uff0c\u5374\u542c\u8bb8\u5c0f\u65e6\u6c14\u547c\u547c\u5730\u53eb\u9053\uff1a\u201c\u970d\u76df\u4e3b\uff0c\u4f60\u8be5\u7ed9\u6211\u4e00\u4ef6 \u4e1c\u897f\u4e86\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u832b\u7136\u9053\uff1a\u201c\u4f60\u95ee\u8001\u592b\u8981\u7684\u4ec0\u4e48\u4e1c\u897f\uff1f\u201d \u3000\u3000\u8bb8\u5c0f\u65e6\u9053\uff1a\u201c\u4f60\u7684\u8111\u888b\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u6478\u4e86\u6478\u5934\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u201d \u3000\u3000\u8bb8\u5c0f\u65e6\u51b7\u58f0\u9053\uff1a\u201c\u522b\u8fd9\u4e2a\u90a3\u4e2a\u7684\u4e86\uff0c\u4f60\u5148\u524d\u8bf4\u8fc7\uff0c\u82e5\u4eca\u665a\u6ca1\u4eba\u6765\uff0c\u5c31\u8d4c\u4e0a\u4f60\u7684\u8111\u888b\uff0c\u8eab \u4e3a\u5929\u4e0b\u7eff\u6797\u76df\u4e3b\uff0c\u603b\u4e0d\u80fd\u8bf4\u8bdd\u4e0d\u7b97\u8bdd\u5427\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u4ed6\u4e0d\u662f\u5df2\u7ecf\u6765\u8fc7\u4e86\uff1f\u201d \u3000\u3000\u8bb8\u5c0f\u65e6\u9053\uff1a\u201c\u4ed6\u6765\u4e86\u6ca1\u529e\u4e8b\uff0c\u800c\u4e14\u9a6c\u4e0a\u5c31\u8d70\uff0c\u8fd9\u7b97\u4ec0\u4e48\u6765\u8fc7\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u518d\u8f6c\u5411\u9ea6\u5c0f\u660e\uff0c\u6c89\u4e0b\u8138\u8272\u9053\uff1a\u201c\u4f60\u5f53\u771f\u4e0d\u80af\u56de\u4e1c\u6d77\u9662\u53bb\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1b\u201c\u5728\u4f60\u76df\u4e3b\u4e4b\u524d\uff0c\u6211\u8fd8\u5f00\u7684\u4ec0\u4e48\u73a9\u7b11\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u6740\u673a\u9661\u751f\uff0e\u5de6\u53f3\u56de\u987e\u4e86\u641c\u9b42\u624b\u5df4\u5929\u4e49\u548c\u62d8\u9b44\u7d22\u5b8b\u5929\u94ce\u4e00\u773c\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u5982\u6b64\u76ee\u65e0 \u6cd5\u7eaa\uff0c\u7adf\u6562\u5728\u8001\u592b\u9762\u524d\u516c\u7136\u6297\u547d\uff0c\u628a\u4ed6\u62ff\u4e0b\uff01\u201d \u3000\u3000\u641c\u9b42\u624b\u5df4\u5929\u4e49\u5e76\u672a\u4eae\u51fa\u5175\u5203\uff0c\u5374\u5411\u8eab\u540e\u6570\u4e08\u7684\u90a3\u68f5\u6811\u4e0b\u62db\u4e86\u62db\u624b\u9053\uff1a\u201c\u76df\u4e3b\u6709\u4ee4\uff0c\u8fc7 \u6765\uff01\u201d \u3000\u3000\u7acb\u523b\u4e00\u6761\u4eba\u5f71\uff0c\u6709\u5982\u7535\u5149\u77f3\u706b\u822c\uff0c\u7531\u5927\u6811\u4e0b\u4e00\u63a0\u800c\u81f3\u3002 \u3000\u3000\u8fd9\u4eba\u8d6b\u7136\u662f\u5f20\u656c\u5b89\uff0c\u5de6\u53f3\u53cc\u624b\u5404\u6263\u7740\u4e00\u679a\u5149\u8000\u707f\u70c2\u7684\u91d1\u73af\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u5fc3\u5934\u6697\u51db\uff0c\u60f3\u4e0d\u5230\u970d\u5143\u4f3d\u5df2\u4e8b\u5148\u5b89\u6392\u4e0b\u5bf9\u4ed8\u81ea\u5df1\u7684\u6740\u624b\u3002 \u3000\u3000\u5f20\u656c\u5b89\u662f\u9ea6\u5c0f\u660e\u7684\u5e08\u5144\uff0c\u4e0a\u6b21\u970d\u5143\u4f3d\u4e89\u593a\u6b66\u6797\u76df\u4e3b\uff0c\u5c31\u662f\u4ee5\u5f20\u656c\u5b89\u6765\u5bf9\u4ed8\u9ea6\u5c0f\u660e\u7684\u3002 \u3000\u3000\u4f46\u9ea6\u5c0f\u660e\u5374\u4e00\u76f4\u5f04\u4e0d\u6e05\u5f20\u656c\u5b89\u4e3a\u4f55\u80fd\u88ab\u970d\u5143\u4f3d\u6240\u7528\uff0c\u8fde\u970d\u5143\u4f3d\u56e0\u4f55\u80fd\u5f04\u5230\u201c\u5411\u5fc3\u9732\u201d\uff0e \u4e5f\u6beb\u65e0\u6240\u77e5\u3002 \u3000\u3000\u5f20\u656c\u5b89\u56e0\u5df2\u670d\u4e0b\u201c\u5411\u5fc3\u9732\u201d\uff0c\u7406\u667a\u5c3d\u5931\uff0e\u5b8c\u5168\u4ee5\u970d\u5143\u4f3d\u4e4b\u547d\u662f\u4ece\uff0c\u6765\u5230\u9762\u524d\uff0c\u5446\u5446\u7684\u7ad9 \u5728\u90a3\u91cc\uff0e\u52a8\u4e5f\u4e0d\u52a8\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u7684\u6b66\u529f\uff0e\u672c\u6765\u5c31\u6bd4\u5f20\u656c\u5b89\u7a0d\u900a\u4e00\u7b79\uff0c\u6b64\u523b\u53c8\u672a\u643a\u5e26\u5175\u5203\uff0c\u82e5\u53cc\u65b9\u4ea4\u624b\uff0c\u8fde\u4ed6\u81ea\u5df1 \u4e5f\u77e5\u9053\u6beb\u65e0\u7591\u95ee\u5fc5\u5b9a\u5403\u4e8f\u3002 \u3000\u3000\u5f53\u4e0b\uff0c\u9ea6\u5c0f\u660e\u5f3a\u81ea\u9547\u5b9a\uff0c\u62b1\u62f3\u4e00\u793c\u9053\uff1a\u201c\u5f20\u5e08\u5144\uff0c\u53ef\u8fd8\u8ba4\u8bc6\u5c0f\u5f1f\u5427\uff1f\u201d \u3000\u3000\u5f20\u656c\u5b89\u4f9d\u7136\u4e24\u773c\u53d1\u76f4\uff0c\u6d51\u4f3c\u4e0d\u89c9\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u518d\u9053\uff1a\u201c\u6211\u662f\u9ea6\u5c0f\u660e\uff0c\u54b1\u4eec\u5e08\u5144\u5f1f\u2014\u5411\u611f\u60c5\u6700\u597d\uff0c\u5e08\u5144\u5343\u4e07\u522b\u53d7\u4eba\u5229\u7528\uff01\u201d \u3000\u3000\u53ea\u542c\u970d\u5143\u4f3d\u51b7\u7b11\u9053\uff1a\u201c\u9ea6\u5c0f\u660e\uff0c\u8001\u592b\u5bf9\u4f60\u5df2\u7b97\u4ec1\u81f3\u4e49\u5c3d\uff0c\u73b0\u5728\u4f60\u662f\u81ea\u5bfb\u6b7b\u8def\uff0c\u6028\u4e0d\u5f97\u8001 \u592b\u5fc3\u72e0\u624b\u8fa3\uff01\u201d \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u4e24\u6761\u4eba\u5f71\u7531\u53e6\u4e00\u65b9\u5411\u6025\u63a0\u800c\u81f3\uff0c\u7adf\u662f\u949f\u4e00\u8c6a\u548c\u4f59\u4ea6\u4e50\u3002 \u3000\u3000\u539f\u6765\u949f\u4e00\u8c6a\u548c\u4f59\u4ea6\u4e50\u5728\u9ea6\u5c0f\u660e\u8d70\u540e\uff0c\u56e0\u62c5\u5fc3\u4ed6\u5728\u4e1c\u6d77\u9662\u95f9\u4e8b\uff0c\u4e24\u4eba\u7ecf\u8fc7\u4e00\u756a\u5546\u8bae\uff0c\u4fbf\u968f \u540e\u800c\u6765\u3002 \u3000\u3000\u5f53\u4ed6\u4eec\u5230\u8fbe\u4e1c\u6d77\u9662\u9644\u8fd1\uff0c\u6b63\u8d76\u4e0a\u9ea6\u5c0f\u660e\u5236\u670d\u4e86\u6c5f\u5357\u56db\u602a\uff0c\u4e0d\u60f3\u5374\u53c8\u6765\u4e86\u970d\u5143\u4f3d\u3002 \u3000\u3000\u4e24\u4eba\u9690\u5411\u6697\u5904\uff0e\u539f\u4e0d\u6253\u7b97\u73b0\u8eab\uff0c\u6b64\u523b\u89c1\u970d\u5143\u4f3d\u5df2\u52a8\u6740\u673a\uff0c\u800c\u4e14\u53ec\u6765\u5f20\u656c\u5b89\uff0c\u4e3a\u62a4\u536b\u9ea6\u5c0f \u660e\uff0c\u4fbf\u53ea\u597d\u548c\u970d\u5143\u4f3d\u2014\u89c1\u3002 \u3000\u3000\u970d\u5143\u4f3d\u4e00\u89c1\u659c\u523a\u91cc\u5192\u51fa\u949f\u2014\u8c6a\u548c\u4f59\u4ea6\u4e50\uff0c\u51b7\u51b7\u4e00\u7b11\u9053\uff1a\u201c\u949f\u526f\u76df\u4e3b\u548c\u4f59\u5148\u751f\u6765\u4e86\u6b63\u597d\uff0c \u56fd\u6709\u56fd\u6cd5\uff0c\u5e2e\u6709\u5e2e\u89c4\uff0c\u54b1\u4eec\u7eff\u6797\u603b\u5be8\u5c82\u80fd\u6beb\u65e0\u6cd5\u7eaa\uff1f\u8001\u592b\u4eca\u665a\u5c31\u51b3\u5b9a\u628a\u8fd9\u53db\u9006\u4e4b\u5f92\u6309\u5f8b\u5904 \u7f6e\uff01\u201d \u3000\u3000\u949f\u4e00\u8c6a\u62b1\u4e86\u62b1\u62f3\uff0c\u6545\u4f5c\u832b\u7136\u9053\uff1a\u201c\u76df\u4e3b\u8bf4\u7684\u53ef\u662f\u9ea6\u5c0f\u660e\u9ea6\u8001\u5f1f\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u9762\u8272\u5982\u7f69\u971c\u9053\uff1a\u201c\u8ff7\u8e2a\u8c37\u5185\u4e0a\u5343\u5f1f\u5144\uff0c\u9664\u4e86\u4ed6\u8c01\u6562\u5982\u6b64\u76ee\u65e0\u6cd5\u7eaa\uff0c\u7b80\u76f4\u662f\u65e0\u6cd5\u65e0 \u5929\u4e86\uff01\u201d \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u4ed6\u5df2\u7ecf\u9075\u7167\u76df\u4e3b\u4ee4\u8c15\u5230\u8fc7\u4e1c\u6d77\u9662\uff0c\u4e0d\u77e5\u76df\u4e3b\u8ba4\u4e3a\u4ed6\u8fd8\u6709\u4ec0\u4e48\u4e0d\u5f53\u4e4b\u5904\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u4ed6\u63d0\u524d\u79bb\u5f00\u4e1c\u6d77\u9662\uff0c\u5c31\u5927\u5927\u4e0d\u8be5\u3002\u8001\u592b\u547d\u4ed6\u56de\u53bb\uff0c\u4ed6\u53c8\u6297\u547d\u4e0d\u9075\uff0c\u5c24\u5176\u548c \u8001\u592b\u8bb2\u8bdd\u7684\u8bed\u6c14\uff0c\u4e00\u6d3e\u86ee\u6a2a\u65e0\u793c\uff0c\u6839\u672c\u6ca1\u628a\u6211\u8fd9\u7eff\u6797\u76df\u4e3b\u653e\u5728\u773c\u91cc\uff0c\u8001\u592b\u662f\u5728\u5fcd\u65e0\u53ef\u5fcd\u4e4b \u4e0b\uff0c\u624d\u51b3\u5b9a\u628a\u4ed6\u62ff\u4e0b\u6cbb\u7f6a\u3002\u201d \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u90a3\u597d\u529e\uff0c\u5c5e\u4e0b\u53ef\u4ee5\u529d\u529d\u4ed6\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u6c89\u58f0\u9053\uff1a\u201c\u8fd9\u662f\u8001\u592b\u7684\u4ee4\u8c15\uff0c\u4f55\u5fc5\u7528\u529d\u7684\uff01\u201d \u3000\u3000\u949f\u4e00\u8c6a\u6765\u5230\u9ea6\u5c0f\u660e\u9762\u524d\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u9ea6\u8001\u5f1f\uff0c\u542c\u6211\u7684\u6d3b\uff0c\u65e2\u7136\u76df\u4e3b\u8981\u4f60\u518d\u56de\u4e1c\u6d77\u9662\u53bb\uff0c \u4f60\u56de\u53bb\u4e0d\u5c31\u4ec0\u4e48\u4e8b\u90fd\u6ca1\u6709\u4e86\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u8bed\u6c14\u65a9\u9489\u622a\u94c1\u5730\u9053\uff1a\u201c\u6211\u5df2\u51b3\u5b9a\u4e0d\u56de\u53bb\uff0c\u8c01\u903c\u6211\u56de\u53bb\u90fd\u6ca1\u6709\u7528\uff01\u201d \u3000\u3000\u949f\u4e00\u8c6a\u60c5\u8bcd\u6073\u5207\u9053\uff1a\u201c\u8fd9\u662f\u4f55\u82e6\uff0c\u4f55\u5fc5\u56e0\u4e00\u65f6\u610f\u6c14\u7528\u4e8b\u800c\u4f24\u4e86\u548c\u6c14\uff0c\u56de\u67cf\u9f84\u9662\u548c\u56de\u4e1c\u6d77 \u9662\u53c8\u6709\u4ec0\u4e48\u5206\u522b\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u51b7\u7b11\u9053\uff1a\u201c\u5206\u522b\u53ef\u5927\u4e86\uff0c\u4f4f\u5728\u81ea\u5df1\u5bb6\u91cc\u548c\u51fa\u5916\u5bbf\u5a3c\u72ce\u5993\u96be\u9053\u6ca1\u6709\u5206\u522b\uff1f\u201d \u3000\u3000\u201c\u4eba\u5bb6\u662f\u4ed9\u5b50\uff0c\u600e\u53ef\u62ff\u5a3c\u5993\u76f8\u6bd4\uff1f\u201d \u3000\u3000\u201c\u6211\u770b\u5979\u8fde\u5a3c\u5993\u8fd8\u4e0d\u5982\uff0c\u5a3c\u5993\u53ef\u6709\u4e0d\u8981\u8138\u5230\u5f53\u4eba\u8131\u8863\u7684\uff1f\u201d \u3000\u3000\u949f\u4e00\u8c6a\u5927\u611f\u4e3a\u96be\u9053\uff1a\u201c\u9ea6\u8001\u5f1f\uff0c\u65e0\u8bba\u5982\u4f55\uff0e\u542c\u6211\u8fd9\u6b21\u8bdd\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u4f3c\u662f\u6709\u4e9b\u4e0d\u8010\u70e6\uff0c\u6717\u58f0\u9053\uff1a\u201c\u949f\u526f\u76df\u4e3b\uff0c\u4f60\u8d70\u5f00\uff0e\u6211\u8981\u770b\u770b\u59d3\u970d\u7684\u8981\u600e\u6837\u5904\u7f6e \u6211\uff01\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u4e3a\u987e\u5168\u5927\u5c40\uff0c\u6b63\u8981\u8fc7\u53bb\u82e6\u529d\uff0e\u53ea\u542c\u970d\u5143\u4f3d\u559d\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4eba\u95ea\u5230\u4e00\u8fb9\uff0c\u5373\u4f7f\u73b0\u5728 \u4ed6\u56de\u5fc3\u8f6c\u610f\uff0c\u8001\u592b\u7167\u6837\u8fd8\u662f\u9976\u4e0d\u4e86\u4ed6\uff01\u201d \u3000\u3000\u949f\u4e00\u8c6a\u8138\u8272\u4e00\u53d8\u9053\uff1a\u201c\u8fd9\u8bdd\u4ee4\u4eba\u4e0d\u89e3\uff0c\u4ed6\u5982\u679c\u542c\u4ece\u529d\u544a\u80af\u56de\u4e1c\u6d77\u9662\uff0c\u76df\u4e3b\u51ed\u4ec0\u4e48\u8fd8\u8981\u60e9 \u5904\u4ed6\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u4e24\u592a\u9633\u7a74\u62bd\u52a8\u4e86\u51e0\u4e0b\u9053\uff1a\u201c\u4ed6\u5982\u679c\u542c\u4ece\u4f60\u7684\u8bdd\uff0e\u90a3\u8868\u793a\u8001\u592b\u8fd9\u76df\u4e3b\u5728\u4ed6\u773c\u4e2d\u8fd8\u4e0d \u5982\u4f60\u4eec\uff0c\u66f4\u4f55\u51b5\u4ed6\u5df2\u6297\u547d\u5728\u5148\u3002\u201d \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u76df\u4e3b\u771f\u7684\u8981\u4ee5\u5f20\u656c\u5b89\u6765\u5236\u670d\u4ed6\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u8001\u592b\u624b\u4e0b\u65e2\u6709\u5f20\u656c\u5b89\uff0c\u5f53\u7136\u7528\u4e0d\u7740\u81ea\u5df1\u52a8\u624b\u3002\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u89c1\u4e8b\u60c5\u5df2\u4e0d\u53ef\u8f6c\u5706\uff0c\u5fd9\u8f6c\u5934\u9053\uff1a\u201c\u9ea6\u8001\u5f1f\uff0c\u4f60\u5feb\u8d70\uff01\u79bb\u5f00\u8ff7\u8e2a\u8c37\uff0c\u9752\u5c71\u6c38\u5728\uff0c\u7eff \u6c34\u5e38\u5b58\uff0c\u54b1\u4eec\u540e\u4f1a\u6709\u671f\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u51b7\u7b11\u9053\uff1a\u201c\u4f59\u5148\u751f\u522b\u6789\u8d39\u5fc3\u673a\uff0c\u4ed6\u80fd\u8d70\u5f97\u4e86\u5417\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u4e5f\u51b7\u7b11\u9053\uff1a\u201c\u59d3\u970d\u7684\uff0c\u82e5\u5728\u4e0b\u73b0\u5728\u8d70\uff0c\u5c82\u4e0d\u8868\u793a\u6015\u4e86\u4f60\u3002\u5728\u4e0b\u5f53\u7136\u8981\u8d70\uff0c\u4f46\u5374\u4e0d \u662f\u73b0\u5728\uff01\u201d \u3000\u3000\u2014\u65c1\u7684\u949f\u2014\u8c6a\uff0c\u4e0d\u80fd\u773c\u89c1\u9ea6\u5c0f\u660e\u624b\u65e0\u5bf8\u94c1\u548c\u5f20\u656c\u5b89\u52a8\u624b\uff0c\u7acb\u5373\u62d4\u51fa\u81ea\u5df1\u7684\u4f69\u5251\u629b\u5411\u9ea6\u5c0f \u660e\u9053\uff1a\u201c\u63a5\u4f4f\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u63a5\u5251\u5728\u624b\uff0c\u53cc\u76ee\u5c04\u5149\uff0c\u6717\u58f0\u9053\uff1a\u201c\u59d3\u970d\u7684\uff0e\u4f60\u5c31\u5429\u5490\u5bf9\u4ed8\u6211\u7684\u4eba\u51fa\u624b\u5427\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u8f6c\u5934\u5411\u5f20\u656c\u5b89\u9053\uff1a\u201c\u628a\u8fd9\u5c0f\u5b50\u7ed9\u8001\u592b\u62ff\u4e0b\uff01\u201d \u3000\u3000\u5f20\u656c\u5b89\u4e00\u8a00\u4e0d\u53d1\uff0c\u51cc\u8eab\u5411\u9ea6\u5c0f\u660e\u75be\u6251\u800c\u4e0b\uff0c\u53cc\u624b\u91d1\u73af\u6709\u5982\u7535\u95ea\u96f7\u5954\uff0c\u631f\u7740\u6151\u4eba\u5fc3\u9b44\u7684\u7834 \u98ce\u4e4b\u58f0\uff0c\u7f69\u5411\u9ea6\u5c0f\u660e\u5168\u8eab\u5404\u5904\u5927\u7a74\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u6b66\u529f\u65e2\u7565\u900a\u4e8e\u5f20\u656c\u5b89\u3002\u53c8\u4e0d\u4fbf\u5c06\u5bf9\u65b9\u523a\u4f24\uff0c\u81ea\u7136\u6253\u5f97\u788d\u624b\u788d\u811a\uff0c\u4e09\u4e94\u62db\u8fc7\u540e\uff0c\u4fbf \u5df2\u88ab\u903c\u5f97\u8fde\u9000\u4e8c\u4e09\u6b65\u3002 \u3000\u3000\u5f20\u656c\u5b89\u7684\u4e00\u5bf9\u91d1\u73af\u867d\u662f\u77ed\u5175\u5668\uff0c\u4f46\u56e0\u65bd\u5c55\u5f00\u6765\u6709\u7740\u65e0\u6bd4\u7684\u5947\u8be1\u800c\u53c8\u7075\u6d3b\uff0c\u5c24\u5176\u91d1\u5149\u95ea \u95ea\uff0c\u98ce\u9a70\u7535\u63a3\uff0c\u4e0d\u8bba\u4ec0\u4e48\u4eba\uff0c\u53ea\u8981\u4e00\u4ea4\u4e0a\u624b\uff0c\u5fc5\u5b9a\u773c\u82b1\u7f2d\u4e71\uff0e\u751a\u81f3\u5934\u660f\u76ee\u7729\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u63a5\u8fde\u523a\u51fa\u51e0\u5251\uff0c\u90fd\u88ab\u90a3\u91d1\u73af\u8361\u504f\u3002 \u3000\u3000\u90a3\u5bf9\u91d1\u73af\uff0c\u4e0d\u77e5\u662f\u4f55\u7269\u6253\u9020\uff0c\u575a\u786c\u5982\u94c1\uff0c\u5373\u4f7f\u5bf9\u65b9\u7528\u7684\u662f\u795e\u5175\u5229\u5668\uff0c\u4e5f\u65e0\u6cd5\u635f\u5b83\u5206\u6beb\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u548c\u4f59\u4ea6\u4e50\u6b64\u523b\u8868\u9762\u867d\u5728\u5c4f\u606f\u9759\u73b0\uff0c\u5185\u5fc3\u5374\u4e3a\u9ea6\u5c0f\u660e\u634f\u4e00\u628a\u6c57\uff0c\u4e0d\u8fc7\u4ed6\u4eec\u5df2\u6709\u9ed8 \u5951\uff0c\u53ea\u8981\u9ea6\u5c0f\u660e\u4e00\u6709\u4e0d\u652f\u73b0\u8c61\uff0c\u4e24\u4eba\u4fbf\u7acb\u523b\u51fa\u624b\u76f8\u52a9\uff0c\u7eb5\u7136\u60f9\u607c\u4e86\u970d\u5143\u4f3d\uff0c\u4e5f\u5728\u6240\u4e0d\u60dc\u3002 \u3000\u3000\u8f6c\u77ac\u95f4\u9ea6\u5c0f\u660e\u548c\u5f20\u656c\u5b89\u5df2\u662f\u56db\u4e94\u5341\u62db\u8fc7\u53bb\uff0c\u9ea6\u5c0f\u660e\u867d\u7136\u7a0d\u7a0d\u843d\u5165\u4e0b\u98ce\uff0c\u4f46\u5251\u62db\u4f9d\u7136\u7ae0\u6cd5 \u4e0d\u4e71\uff0c\u9884\u8ba1\u5728\u767e\u62db\u4e4b\u5185\uff0c\u8db3\u53ef\u81ea\u4fdd\u3002 \u3000\u3000\u6b64\u523b\u7684\u970d\u5143\u4f3d\u548c\u5cad\u5357\u4e8c\u5947\uff0c\u4e5f\u5728\u4e00\u65c1\u805a\u7cbe\u4f1a\u795e\u7684\u89c2\u6218\u3002 \u3000\u3000\u4ed6\u4eec\u773c\u89c1\u9ea6\u5c0f\u660e\u5982\u6b64\u96be\u7f20\uff0c\u4f7f\u5f97\u5f20\u656c\u5b89\u65e0\u6cd5\u5728\u77ed\u65f6\u95f4\u5185\u53d6\u80dc\uff0c\u96be\u514d\u5341\u5206\u7740\u6025\u3002 \u3000\u3000\u53ea\u542c\u4e00\u9635\u201c\u6ce2\u6ce2\u201d\u4e4b\u58f0\uff0e\u9ea6\u5c0f\u660e\u7684\u5251\u5c16\u548c\u91d1\u73af\uff0c\u7728\u773c\u95f4\u4fbf\u649e\u51fb\u4e86\u5341\u51e0\u6b21\uff0c\u4f9d\u7136\u662f\u8c01\u90fd\u4f24 \u4e0d\u4e86\u8c01\u3002 \u3000\u3000\u970d\u5143\u4f3d\u7981\u4e0d\u4f4f\u5728\u65c1\u5927\u558a\u9053\uff1a\u201c\u5f20\u656c\u5b89\uff0c\u5feb\u653b\uff01\u201d \u3000\u3000\u5f20\u656c\u5b89\u679c\u7136\u50cf\u542c\u5f97\u61c2\u970d\u5143\u4f3d\u7684\u8bdd\uff0c\u987f\u65f6\u653b\u52bf\u8d8a\u53d1\u52a0\u5feb\u8d77\u6765\uff0e\u4e00\u53e3\u6c14\u628a\u9ea6\u5c0f\u660e\u903c\u9000\u4e94\u516d \u6b65\u3002 \u3000\u3000\u5c31\u5728\u949f\u4e00\u8c6a\u548c\u4f59\u4ea6\u4e50\u4e92\u9012\u4e00\u4e2a\u773c\u8272\uff0c\u51c6\u5907\u51fa\u624b\u76f8\u52a9\u65f6\uff0c\u7a81\u542c\u201c\u5f53\u201d\u7684\u4e00\u58f0\u9707\u54cd\uff0c\u5f20\u656c\u5b89 \u7684\u5de6\u624b\u91d1\u73af\uff0c\u7adf\u88ab\u9ea6\u5c0f\u660e\u4e00\u5251\u51fb\u98de\u51fa\u53bb\u3002 \u3000\u3000\u8fd9\u60c5\u5f62\u4e0d\u4f46\u89c2\u6218\u53cc\u65b9\u6240\u6709\u7684\u4eba\u5927\u611f\u610f\u5916\uff0c\u8fde\u9ea6\u5c0f\u660e\u81ea\u5df1\u4e5f\u51e0\u4e4e\u4e0d\u6562\u76f8\u4fe1\u8fd9\u662f\u4e8b\u5b9e\u3002 \u3000\u3000\u5f20\u656c\u5b89\u53ea\u5269\u4e0b\u4e00\u679a\u91d1\u73af\uff0c\u7b49\u4e8e\u5a01\u529b\u51cf\u53bb\u4e00\u534a\uff0c\u9ea6\u5c0f\u660e\u7684\u538b\u529b\u4e5f\u968f\u4e4b\u5927\u51cf\uff0c\u5f88\u5feb\u4fbf\u62a2\u5360\u4e86 \u4e0a\u98ce\uff0e\u53cd\u800c\u903c\u5f97\u5f20\u656c\u5b89\u6b65\u6b65\u540e\u9000\u3002 \u3000\u3000\u4f46\u9ea6\u5c0f\u660e\u5ff5\u5728\u5f7c\u6b64\u540c\u95e8\u4e4b\u8c0a\uff0c\u7a33\u4f4f\u4e4b\u540e\uff0c\u4ec5\u628a\u5f20\u656c\u5b89\u903c\u9000\u51e0\u6b65\uff0c\u4fbf\u4e0d\u518d\u62a2\u653b\u3002 \u3000\u3000\u867d\u7136\u5982\u6b64\uff0c\u5f20\u656c\u5b89\u53ea\u51ed\u4e00\u679a\u91d1\u73af\uff0c\u7167\u6837\u4ecd\u662f\u96be\u4ee5\u62db\u67b6\uff0c\u4ec5\u652f\u6301\u4e86\u5341\u51e0\u62db\uff0c\u53f3\u624b\u7684\u91d1\u73af\u4fbf \u53c8\u88ab\u9ea6\u5c0f\u660e\u7684\u5251\u62db\u51fb\u843d\u3002 \u3000\u3000\u5f20\u656c\u5b89\u4ec5\u4f59\u7684\u91d1\u73af\u88ab\u51fb\u843d\u540e\uff0c\u7adf\u662f\u52a8\u4e5f\u4e0d\u52a8\uff0c\u7ad9\u5728\u90a3\u91cc\uff0c\u6709\u5982\u6728\u96d5\u6ce5\u5851\u2014\u822c\u3002 \u3000\u3000\u60c5\u52bf\u53d8\u5316\u5230\u5982\u6b64\u7ed3\u5c40\uff0c\u8fde\u970d\u5143\u4f3d\u3001\u5cad\u5357\u4e8c\u5947\u3001\u949f\u2014\u8c6a\u3001\u4f59\u4ea6\u4e50\u4e5f\u90fd\u6123\u5728\u5f53\u5730\uff0c\u6240\u4e0d\u540c \u7684\uff0c\u662f\u949f\u4e00\u8c6a\u548c\u4f59\u4ea6\u4e50\u60ca\u4e2d\u5e26\u559c\u3002 \u3000\u3000\u539f\u6765\u9ea6\u5c0f\u660e\u6240\u4ee5\u80fd\u8f6c\u8d25\u4e3a\u80dc\uff0e\u7834\u4e86\u5f20\u656c\u5b89\u7684\u53cc\u73af\uff0c\u662f\u5f97\u529b\u4e8e\u7f01\u8863\u8001\u5c3c\u6240\u8d60\u201c\u5999\u5999\u5251\u8bc0\u201d \u4e0a\u7684\u62db\u672f\u3002 \u3000\u3000\u4ed6\u8fd9\u51e0\u5929\u7ecf\u5e38\u5173\u8d77\u623f\u95e8\u4e00\u4e2a\u4eba\u5728\u82e6\u7ec3\u201c\u5999\u5999\u5251\u8bc0\u201d\u4e0a\u7684\u5251\u62db\uff0c\u65b9\u624d\u60c5\u6025\u95f4\u65e0\u610f\u4e2d\u65bd\u5c55\u51fa \u6765\uff0c\u7adf\u80fd\u53d1\u6325\u51fa\u5982\u6b64\u5f3a\u5927\u7684\u5a01\u529b\uff0c\u53ea\u662f\u4ed6\u81ea\u5df1\u5230\u73b0\u5728\u4ecd\u4e0d\u81ea\u77e5\u800c\u5df2\u3002 \u3000\u3000\u6b64\u523b\u7684\u970d\u5143\u4f3d\uff0e\u518d\u6c89\u7740\u4e5f\u6709\u4e9b\u6c89\u4e0d\u4f4f\u6c14\uff0e\u4ed6\u5b9e\u5728\u601d\u89e3\u4e0d\u900f\uff0e\u5f20\u656c\u5b89\u4e0a\u6b21\u660e\u660e\u66fe\u51fb\u8d25\u8fc7\u9ea6 \u5c0f\u660e\uff0c\u4e8b\u9694\u4e0d\u8db3\u4e24\u6708\uff0c\u4e3a\u4ec0\u4e48\u7adf\u6709\u8fd9\u79cd\u7ed3\u5c40\u3002\u83ab\u975e\u5f20\u656c\u5b89\u7684\u6b66\u529f\uff0e\u56e0\u670d\u8fc7\u201c\u5411\u5fc3\u9732\u201d\u758f\u4e8e\u4e60 \u7ec3\uff0e\u5df2\u5728\u65e5\u6e10\u9000\u6b65\u2026\u2026\u201d \u3000\u3000\u949f\u2014\u8c6a\u548c\u4f59\u4ea6\u4e50\u8fde\u5fd9\u6765\u5230\u9ea6\u5c0f\u660e\u8ddf\u524d\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u9ea6\u8001\u5f1f\uff0c\u5343\u4e07\u522b\u518d\u95f9\u4e8b\uff0c\u73b0\u5728\u8be5\u56de\u53bb\u4e86\uff0e\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u54fc\u4e86\u58f0\u9053\uff1a\u201c\u56de\u5230\u54ea\u91cc\u53bb\uff1f\u201d \u3000\u3000\u949f\u4e00\u8c6a\u671b\u4e86\u970d\u5143\u4f3d\u4e00\u773c\uff0e\u9053\uff1a\u201c\u968f\u4f60\u7684\u4fbf\uff0c\u6211\u60f3\u4f60\u5373\u4f7f\u4e0d\u56de\u4e1c\u6d77\u9662\uff0c\u76df\u4e3b\u4e5f\u4e0d\u4f1a\u89c1\u4f60\u7684 \u602a\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u6447\u4e86\u6447\u5934\u9053\uff1a\u201c\u5728\u8ff7\u8e2a\u8c37\uff0c\u5df2\u7ecf\u6ca1\u6709\u6211\u9ea6\u5c0f\u660e\u7acb\u8db3\u4e4b\u5730\u4e86\u3002\u4f60\u4e0d\u59a8\u95ee\u95ee\u4ed6\uff0c\u8fd8\u60f3 \u600e\u6837\u5904\u7f6e\u6211\uff1f\u201d \u3000\u3000\u949f\u4e00\u8c6a\u53ea\u5f97\u56de\u8fc7\u5934\u6765\u9053\uff1a\u201c\u76df\u4e3b\uff0c\u4f60\u4e00\u5411\u5bbd\u5b8f\u5927\u91cf\uff0c\u9ea6\u5c0f\u660e\u65b9\u624d\u867d\u7136\u5bf9\u4f60\u6709\u6240\u6e0e\u72af\uff0c\u5ff5 \u5728\u4ed6\u6614\u65e5\u5728\u8ff7\u8e2a\u8c37\u4e0d\u65e0\u5fae\u52b3\uff0c\u5c06\u529f\u6298\u7f6a\uff0c\u603b\u4e0d\u5931\u662f\u76df\u4e3b\u7684\u5f85\u4eba\u5fa1\u4e0b\u4e4b\u9053\u3002\u201d \u5367\u9f99\u751f\u300a\u5999\u7edd\u5929\u9999\u300b \u7b2c\u5341\u516d\u56de\u3000\u5de7\u632b\u9634\u8ba1 \u3000\u3000\u970d\u5143\u4f3d\u624b\u62c2\u957f\u9aef\uff0e\u51b7\u51b7\u4e00\u7b11\u9053\uff1a\u201c\u90a3\u5c31\u8ba9\u4ed6\u6682\u65f6\u56de\u53bb\uff0c\u660e\u65e5\u8001\u592b\u518d\u4f5c\u5904\u7f6e\uff01\u201d \u3000\u3000\u949f\u4e00\u8c6a\u518d\u671b\u5411\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u9ea6\u8001\u5f1f\u5c31\u8bf7\u56de\u67cf\u9f84\u9662\u5427\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u4f9d\u7136\u7ad9\u5728\u539f\u5730\u672a\u52a8\u9053\uff1a\u201c\u4ed6\u8981\u5904\u7f6e\uff0c\u5c31\u8bf7\u73b0\u5728\u5904\u7f6e\uff0c\u6211\u9ea6\u5c0f\u660e\u8fd8\u4e0d\u60f3\u5403\u4ed6\u7684\u6697 \u4e8f\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u6b64\u65f6\u5df2\u6c14\u5f97\u8fde\u53d1\u68a2\u90fd\u51e0\u4e4e\u6839\u6839\u76f4\u7ad6\uff0c\u4ed6\u8eab\u4e3a\u7eff\u6797\u76df\u4e3b\uff0c\u7adf\u88ab\u4e00\u4e2a\u6bdb\u5934\u5b69\u5b50\u5982\u6b64\u65e0 \u793c\u5192\u72af\uff0c\u5982\u4f55\u80fd\u5bb9\u5fcd\u5f97\u4e0b\uff1f \u3000\u3000\u597d\u5728\u4e1c\u6d77\u9662\u9644\u8fd1\u5c11\u4eba\u6765\u5f80\uff0c\u53c8\u5728\u591c\u95f4\uff0c\u5426\u5219\u82e5\u5728\u5927\u5ead\u5e7f\u4f17\u4e4b\u4e0b\uff0c\u5f53\u771f\u662f\u96be\u4ee5\u4e0b\u53f0\u3002 \u3000\u3000\u5ffd\u542c\u62d8\u9b44\u7d22\u5b8b\u5929\u94ce\u543c\u9053\uff1a\u201c\u76df\u4e3b\uff0c\u8fd9\u5c0f\u5b50\u672a\u514d\u592a\u65e0\u6cd5\u65e0\u5929\u4e86\uff0c\u5c31\u7531\u5c5e\u4e0b\u4e24\u4eba\u628a\u4ed6\u62ff \u4e0b\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u597d\uff0c\u9a6c\u4e0a\u52a8\u624b\uff01\u201d \u3000\u3000\u5b8b\u5929\u94ce\u548c\u5df4\u5929\u4e49\u5728\u970d\u5143\u4f3d\u7684\u4e00\u58f0\u4ee4\u4e0b\uff0c\u7acb\u5373\u6325\u52a8\u5175\u5203\u5411\u9ea6\u5c0f\u660e\u653b\u53bb\u3002 \u3000\u3000\u5b8b\u5929\u94ce\u7528\u7684\u662f\u4e00\u6761\u53d6\u540d\u62d8\u9b44\u7d22\u7684\u5175\u5203\uff0c\u7d22\u957f\u4e00\u4e08\u4e8c\u5c3a\uff0c\u5bf9\u654c\u65f6\u957f\u77ed\u53ef\u968f\u610f\u53d8\u5316\u3002 \u3000\u3000\u5df4\u5929\u4e49\u7528\u7684\u662f\u4e00\u6761\u4e5d\u8282\u4e94\u7eeb\u641c\u9b42\u97ad\uff0c\u4e5f\u957f\u6709\u4e08\u4f59\uff0c\u821e\u52a8\u8d77\u6765\u3002\u53d1\u51fa\u201c\u5431\u5431\u5527\u5527\u201d\u4e4b\u58f0\uff0e \u6709\u5982\u9b3c\u54ed\u72fc\u568e\uff0c\u6151\u4eba\u5fc3\u9b44\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u53f7\u79f0\u5cad\u5357\u4e8c\u5947\uff0c\u662f\u970d\u5143\u4f3d\u6700\u4e3a\u501a\u91cd\u7684\u5de6\u53f3\u4e8c\u5c06\uff0c\u5728\u7eff\u6797\u4e2d\uff0c\u65e9\u5df2\u662f\u6210\u540d\u7684\u7edd\u9876\u9ad8 \u624b\uff0c\u7edd\u975e\u6c5f\u5357\u56db\u602a\u6240\u53ef\u6bd4\u62df\u3002 \u3000\u3000\u5982\u4eca\u8054\u8d77\u624b\u6765\uff0c\u5a01\u529b\u4e4b\u731b\uff0c\u53ef\u60f3\u800c\u77e5\uff0e\u5f88\u5feb\u4fbf\u628a\u9ea6\u5c0f\u660e\u56f4\u5728\u4e08\u4f59\u65b9\u5706\u7684\u7d22\u5149\u97ad\u5f71\u4e4b\u4e2d\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u4f53\u529b\u672c\u6765\u5df2\u7ecf\u6d88\u8017\u751a\u591a\uff0c\u6b64\u523b\u5728\u5cad\u5357\u4e8c\u5947\u7684\u8054\u624b\u4e00\u8f6c\u731b\u653b\u4e4b\u4e0b\u3002\u53ea\u652f\u6491\u4e86\u4e09\u4e94 \u62db\uff0c\u4fbf\u88ab\u8feb\u8282\u8282\u5411\u540e\u8d25\u9000\u3002 \u3000\u3000\u53ea\u56e0\u5df4\u3001\u5b8b\u4e8c\u4eba\u7528\u7684\u90fd\u662f\u65e2\u957f\u53c8\u97e7\u7684\u8f6f\u5175\u5668\uff0c\u4f7f\u5f97\u9ea6\u5c0f\u660e\u7684\u5251\u62db\u9887\u96be\u53d1\u6325\uff0e\u5373\u4f7f\u4f7f\u51fa \u201c\u5999\u5999\u5251\u8bc0\u201d\u3000\u4e0a\u7684\u62db\u672f\uff0c\u77ed\u65f6\u95f4\u4e5f\u96be\u53d1\u6325\u529f\u6548\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u548c\u4f59\u4ea6\u4e50\u89c1\u6b64\u60c5\u5f62\uff0c\u597d\u51e0\u6b21\u60f3\u51fa\u624b\u76f8\u52a9\uff0c\u4f46\u53c8\u62c5\u5fc3\u56e0\u800c\u628a\u4e8b\u60c5\u95f9\u5f97\u66f4\u7cdf\uff0c\u597d\u5728\u9ea6 \u5c0f\u660e\u867d\u8d25\u4ecd\u53ef\u81ea\u4fdd\u3002 \u3000\u3000\u5426\u5219\u4ed6\u4eec\u5b81\u53ef\u4e0d\u8ba1\u540e\u679c\uff0c\u4e5f\u8981\u4fdd\u5168\u9ea6\u5c0f\u660e\u3002 \u3000\u3000\u5c31\u5728\u5cad\u5357\u4e8c\u5947\u62db\u62db\u8fdb\u903c\uff0e\u9ea6\u5c0f\u660e\u6b65\u6b65\u540e\u9000\u9669\u8c61\u73af\u751f\u4e4b\u9645\uff0c\u9aa4\u542c\u8fdc\u5904\u53d1\u51fa\u4e00\u58f0\u51b7\u53f1\uff0c\u63a5\u7740 \u4e00\u6761\u4eba\u5f71\u659c\u523a\u91cc\u2014\u95ea\u800c\u81f3\uff0c\u6325\u52a8\u624b\u4e2d\u957f\u5251\uff0c\u52a0\u5165\u4e86\u6218\u5708\u3002 \u3000\u3000\u5728\u8fd9\u5239\u90a3\uff0c\u949f\u4e00\u8c6a\u3001\u4f59\u4ea6\u4e50\u548c\u970d\u5143\u4f3d\u90fd\u5f04\u4e0d\u6e05\u6765\u8005\u662f\u5e2e\u8c01\u7684\uff0c\u76f4\u5230\u770b\u6e05\u9ea6\u5c0f\u660e\u5df2\u7a33\u4f4f \u811a\uff0c\u5cad\u5357\u4e8c\u5947\u653b\u52bf\u51cf\u7f13\uff0c\u624d\u77e5\u9053\u6765\u4eba\u662f\u4e3a\u9ea6\u5c0f\u660e\u52a9\u9635\u3002 \u3000\u3000\u8fd9\u4eba\u8eab\u6750\u4e0d\u9ad8\uff0c\u4f53\u5f62\u7626\u5c0f\uff0e\u4f46\u624b\u4e2d\u7684\u4e00\u67c4\u5251\uff0c\u5374\u53d8\u5316\u591a\u7aef\uff0c\u8be1\u5947\u83ab\u6d4b\uff0c\u5f88\u5feb\u4fbf\u5c06\u5cad\u5357\u4e8c \u5947\u903c\u5f97\u4e5f\u6b65\u6b65\u540e\u9000\u3002 \u3000\u3000\u970d\u5143\u4f3d\u7acb\u5373\u51fa\u58f0\u5927\u559d\u9053\uff1a\u201c\u4f4f\u624b\uff01\u201d \u3000\u3000\u6765\u4eba\u5e76\u4e0d\u604b\u6218\uff0c\u95fb\u58f0\u4e4b\u540e\uff0c\u8fc5\u901f\u5730\u53c8\u653b\u51fa\u4e09\u62db\uff0c\u7136\u540e\u5411\u540e\u8dc3\u5f00\u4e08\u4f59\u3002 \u3000\u3000\u5cad\u5357\u4e8c\u5947\u4e5f\u968f\u5373\u5411\u540e\u8dc3\u5f00\u3002 \u3000\u3000\u5230\u8fd9\u65f6\uff0c\u4f17\u4eba\u624d\u770b\u6e05\u6765\u4eba\u8eab\u7a7f\u4e00\u88ad\u84dd\u8272\u957f\u886b\uff0c\u53ea\u56e0\u9ed1\u7eb1\u8499\u9762\uff0c\u65e0\u6cd5\u770b\u6e05\u4ed6\u7684\u9762\u8c8c\u3002 \u3000\u3000\u970d\u5143\u4f3d\u5e72\u54b3\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u5c0a\u9a7e\u662f\u4f55\u65b9\u9ad8\u4eba\uff0c\u53ef\u662f\u4e0e\u8001\u592b\u8fc7\u4e0d\u53bb\uff1f\u201d \u3000\u3000\u84dd\u886b\u8499\u9762\u4eba\u4ecd\u7136\u4ed7\u5251\u5728\u624b\uff0c\u51b7\u51b7\u4e00\u7b11\u9053\uff1a\u201c\u5728\u4e0b\u4e0e\u4f60\u4eec\u53cc\u65b9\u90fd\u7d20\u4e0d\u76f8\u8bc6\uff0c\u53ea\u662f\u770b\u4e0d\u60ef\u8fd9 \u79cd\u5408\u51fb\u7fa4\u6bb4\u7684\u6253\u6cd5\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u4e0d\u89c9\u5fc3\u5934\u4e00\u51db\uff0c\u6697\u9053\uff1a\u201c\u8ff7\u8e2a\u8c37\u6212\u5907\u4e25\u5bc6\uff0c\u5230\u5904\u90fd\u662f\u5173\u5361\uff0c\u6b64\u4eba\u65e2\u975e\u8ff7\u8e2a\u8c37\u4eba\uff0c \u7a76\u7adf\u662f\u600e\u4e48\u6df7\u8fdb\u6765\u7684\uff1f\u201d \u3000\u3000\u5fc3\u5ff5\u53ca\u6b64\uff0c\u53c8\u8f7b\u54b3\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u5c0a\u9a7e\u662f\u5982\u4f55\u8fdb\u5165\u8ff7\u8e2a\u8c37\u7684\uff1f\u201d \u3000\u3000\u84dd\u886b\u8499\u9762\u4eba\u9053\uff1a\u201c\u8ff7\u8e2a\u8c37\u7eb5\u7136\u662f\u9f99\u6f6d\u864e\u7a74\uff0c\u4e5f\u963b\u6321\u4e0d\u4e86\u5728\u4e0b\u7684\u53bb\u8def\u3002\u201d \u3000\u3000\u201c\u5c0a\u9a7e\u64c5\u95ef\u8ff7\u8e2a\u8c37\uff0c\u7a76\u7adf\u5fc3\u5b58\u4f55\u610f\uff1f\u201d \u3000\u3000\u201c\u96be\u9053\u5728\u4e0b\u5c31\u4e0d\u53ef\u4ee5\u8fdb\u6765\u89c2\u5149\u4e00\u756a\uff1f\u201d \u3000\u3000\u201c\u6b22\u8fce\u3002\u5982\u679c\u5c0a\u9a7e\u80fd\u52a0\u5165\u8001\u592b\u8fd9\u7eff\u6797\u603b\u5be8\uff0c\u8001\u592b\u6b22\u8fce\u81f3\u6781\u3002\u201d \u3000\u3000\u201c\u5728\u4e0b\u65e9\u5c31\u6709\u610f\u52a0\u5165\u8ff7\u8e2a\u8c37\u7684\u7eff\u6797\u603b\u5be8\uff0c\u4e0d\u8fc7\u5fc5\u987b\u6709\u4e2a\u6761\u4ef6\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u6761\u4ef6\uff1f\u201d \u3000\u3000\u201c\u6362\u4e00\u4f4d\u65b0\u7684\u7eff\u6797\u76df\u4e3b\u3002\u53ea\u8981\u4f60\u80af\u4e0b\u53f0\uff0c\u5728\u4e0b\u5c31\u4e00\u5b9a\u7559\u5728\u8ff7\u8e2a\u8c37\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u987f\u65f6\u6012\u706b\u5927\u70bd\uff0c\u559d\u9053\uff1a\u201c\u5c82\u6709\u6b64\u7406\uff01\u8001\u592b\u8fd9\u7eff\u6797\u76df\u4e3b\u6765\u4e4b\u4e0d\u6613\uff0c\u800c\u4e14\u6df1\u53d7\u8ff7\u8e2a\u8c37 \u62e5\u6234\uff0c\u5c0a\u9a7e\u80af\u4e0d\u80af\u52a0\u5165\u53ea\u662f\u5c0f\u4e8b\u4e00\u4ef6\uff0c\u600e\u53ef\u51fa\u6b64\u72c2\u8a00\uff01\u201d \u3000\u3000\u84dd\u886b\u8499\u9762\u4eba\u7b11\u9053\uff1a\u201c\u970d\u76df\u4e3b\uff0c\u4f60\u9519\u4e86\uff01\u201d \u3000\u3000\u201c\u8001\u592b\u9519\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u201c\u4f60\u7684\u8bdd\u8bf4\u9519\u4e86\uff0e\u4f60\u8bf4\u4f60\u6df1\u53d7\u8ff7\u8e2a\u8c37\u62e5\u6234\uff0c\u662f\u5426\u4e5f\u5305\u62ec\u8fd9\u4f4d\u5c0f\u5144\u5f1f\u5728\u5185\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u4ed6\u662f\u53db\u9006\uff0e\u5f53\u7136\u4e0d\u80fd\u4e00\u6982\u800c\u8bba\u3002\u201d \u3000\u3000\u84dd\u886b\u8499\u9762\u4eba\u9053\uff1a\u201c\u7167\u4f60\u6240\u8bf4\u7684\u8fd9\u79cd\u53db\u9006\uff0c\u8ff7\u8e2a\u8c37\u53ea\u6015\u4e0d\u5728\u5c11\u6570\u3002\u4f60\u82e5\u6709\u81ea\u77e5\u4e4b\u660e\uff0c\u7528\u4e0d \u7740\u5728\u4e0b\u76f8\u529d\uff0c\u5c31\u8be5\u81ea\u52a8\u8ba9\u4f4d\u624d\u662f\u660e\u667a\u4e4b\u4e3e\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u4e0d\u52a8\u58f0\u8272\u9053\uff1a\u201c\u5c0a\u9a7e\u5e0c\u671b\u8001\u592b\u8fd9\u76df\u4e3b\u4e4b\u4f4d\u8ba9\u7ed9\u54ea\u4e2a\uff1f\u201d \u3000\u3000\u84dd\u886b\u8499\u9762\u4eba\u7565\u4e00\u505c\u987f\u9053\uff1a\u201c\u4e0d\u59a8\u865a\u4f4d\u4ee5\u5f85\uff0c\u4e0d\u4e45\u4e4b\u540e\uff0c\u5fc5\u6709\u5fb7\u53ef\u670d\u4eba\u3001\u5a01\u80fd\u670d\u4f17\u4e4b\u4eba\u524d \u6765\u5c31\u4efb\u7eff\u6797\u76df\u4e3b\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u5fc3\u4e2d\u7830\u7136\u4e00\u52a8\u9053\uff1a\u201c\u5c0a\u9a7e\u8bf4\u7684\u8fd9\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u84dd\u886b\u8499\u9762\u4eba\u9053\uff1a\u201c\u5929\u673a\u4e0d\u53ef\u6cc4\u9732\uff0c\u8bf7\u6055\u5728\u4e0b\u4e0d\u4fbf\u8a00\u660e\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u9ed8\u4e86\u4e00\u9ed8\u9053\uff1a\u201c\u5c0a\u9a7e\u4eca\u665a\u662f\u5426\u8fd8\u8981\u7559\u5728\u8ff7\u8e2a\u8c37\uff1f\u201d \u3000\u3000\u201c\u970d\u76df\u4e3b\u95ee\u8fd9\u4e2a\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u5982\u679c\u5c0a\u9a7e\u4eca\u665a\u8981\u7559\u5728\u8ff7\u8e2a\u8c37\uff1f\u8fdc\u6765\u662f\u5ba2\uff0c\u8001\u592b\u8fd9\u5c31\u547d\u4eba\u51c6\u5907\u4f4f\u5bbf\u4e4b\u5904\uff0c\u660e\u65e5\u518d\u8bbe\u7b75\u6b3e \u5f85\u3002\u201d \u3000\u3000\u201c\u970d\u76df\u4e3b\u7684\u4e00\u756a\u76db\u610f\uff0e\u5728\u4e0b\u5fc3\u9886\u4e86\u3002\u5728\u4e0b\u51b3\u5b9a\u73b0\u5728\u5c31\u8d70\uff0c\u4e0d\u8fc7\u5728\u4e34\u8d70\u4e4b\u524d\uff0c\u60f3\u5949\u544a\u970d\u76df \u4e3b\u51e0\u53e5\u8bdd\uff0e\u4e0d\u77e5\u4f60\u662f\u5426\u542c\u5f97\u8fdb\u53bb\uff1f\u201d \u3000\u3000\u201c\u5c0a\u9a7e\u6709\u8bdd\u8bf7\u8bb2\uff01\u201d \u3000\u3000\u201c\u970d\u76df\u4e3b\u51c6\u5907\u628a\u8fd9\u4f4d\u5c0f\u5144\u5f1f\u600e\u6837\u5904\u7f6e\uff1f\u201d \u3000\u3000\u201c\u8fd9\u662f\u8001\u592b\u81ea\u5df1\u7684\u4e8b\uff0c\u8001\u592b\u81ea\u5f53\u79c9\u516c\u5904\u7406\u3002\u201d \u3000\u3000\u84dd\u886b\u8499\u9762\u4eba\u8bed\u6c14\u5ffd\u8f6c\u90d1\u91cd\u9053\uff1a\u201c\u970d\u76df\u4e3b\u5e94\u5f53\u6bd4\u5728\u4e0b\u66f4\u660e\u767d\uff0c\u8fd9\u4f4d\u5c0f\u5144\u5f1f\u7684\u8eab\u624b\uff0c\u5728\u8d35\u8c37 \u6765\u8bf4\uff0c\u53ea\u6015\u5f88\u5c11\u4eba\u80fd\u548c\u4ed6\u76f8\u6bd4\uff0e \u3000\u3000\u970d\u76df\u4e3b\u82e5\u80fd\u5584\u52a0\u76f8\u5f85\uff0c\u5bf9\u4f60\u5c06\u6765\u7684\u4e00\u756a\u7eff\u6797\u4e8b\u4e1a\uff0c\u5fc5\u6709\u6781\u5927\u5e2e\u52a9\uff0c\u8fd8\u671b\u970d\u76df\u4e3b\u4e09\u601d\u624d \u597d\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u53ef\u662f\u4ed6\u51cc\u72af\u8001\u592b\uff0c\u76ee\u65e0\u6cd5\u7eaa\uff0c\u96be\u9053\u8001\u592b\u5c31\u8fd9\u6837\u7b97\u4e86\uff1f\u201d \u3000\u3000\u84dd\u886b\u8499\u9762\u4eba\u9053\uff1a\u201c\u4f60\u8eab\u4e3a\u5929\u4e0b\u7eff\u6797\u76df\u4e3b\uff0c\u603b\u8be5\u6709\u4e9b\u5ea6\u91cf\uff0c\u6709\u5bb9\u4e43\u5927\u3002\u5426\u5219\u51e1\u4e8b\u65a4\u65a4\u8ba1 \u8f83\uff0c\u775a\u7726\u5fc5\u62a5\uff0e\u4f55\u4ee5\u670d\u4f17\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u4e0d\u52a8\u58f0\u8272\u9053\uff1a\u201c\u83ab\u975e\u5c0a\u9a7e\u8981\u63d2\u624b\u7ba1\u8fd9\u6863\u5b50\u95f2\u4e8b\uff1f\u201d \u3000\u3000\u84dd\u886b\u8499\u9762\u4eba\u9053\uff1a\u201c\u5728\u4e0b\u672c\u6765\u5c31\u5df2\u7ecf\u63d2\u624b\u8fd9\u4ef6\u4e8b\u4e86\uff0e\u4f55\u51b5\u8fd9\u4e8b\u5728\u8ff7\u8e2a\u8c37\u6765\u8bf4\u662f\u4ef6\u5927\u4e8b\uff0e\u7edd \u4e0d\u662f\u95f2\u4e8b\u3002\u201d \u3000\u3000\u201c\u4f60\u662f\u8981\u8001\u592b\u4e0d\u518d\u8ba1\u8f83\u4eca\u665a\u7684\u4e8b\uff1f\u201d \u3000\u3000\u201c\u4e0d\u9519\u3002\u5728\u4e0b\u5b8c\u5168\u662f\u4e3a\u4e86\u970d\u76df\u4e3b\u7684\u7eff\u6797\u6c5f\u5c71\u7740\u60f3\uff0c\u4e43\u662f\u4e00\u756a\u597d\u610f\u3002\u542c\u4e5f\u5728\u4f60\uff0e\u4e0d\u542c\u4e5f\u5728 \u4f60\u3002\u5982\u679c\u8fd9\u4f4d\u5c0f\u5144\u5f1f\u906d\u5230\u4e0d\u6d4b\u2026\u2026\u201d \u3000\u3000\u201c\u5c0a\u9a7e\u4fbf\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u201c\u5728\u4e0b\u81ea\u77e5\u51ed\u4e00\u4eba\u4e4b\u529b\uff0e\u4e0d\u80fd\u628a\u4f60\u8fd9\u4f4d\u7eff\u6797\u76df\u4e3b\u600e\u4e48\u6837\uff0e\u4f46\u5728\u4e0b\u8fd8\u8ba4\u8bc6\u51e0\u4f4d\u670b\u53cb\uff0c\u5728\u4e0b \u5c31\u7ea6\u540c\u4ed6\u4eec\u4e00\u8d77\u5230\u8d35\u8c37\uff0c\u5230\u90a3\u65f6\u518d\u8ba9\u4f60\u89c1\u8bc6\u89c1\u8bc6\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u8fd8\u6ca1\u6765\u5f97\u53ca\u518d\u5f00\u53e3\uff0c\u84dd\u886b\u8499\u9762\u4eba\u5df2\u817e\u8eab\u4e00\u8dc3\uff0c\u8fde\u63a0\u51e0\u63a0\uff0e\u8eab\u5f62\u5239\u65f6\u6d88\u901d\u5f97\u65e0\u5f71\u65e0 \u8e2a\u3002 \u3000\u3000\u949f\u2014\u8c6a\u968f\u5373\u62b1\u4e86\u62b1\u62f3\u9053\uff1a\u201c\u9ea6\u8001\u5f1f\u7684\u4e8b\uff0c\u7a76\u7adf\u505a\u4f55\u6253\u7b97\uff0e\u8fd8\u671b\u76df\u4e3b\u73b0\u5728\u5c31\u8bf4\u660e\u767d\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u54fc\u4e86\u58f0\u9053\uff1a\u201c\u949f\u526f\u76df\u4e3b\u4e3a\u4ec0\u4e48\u8981\u8fd9\u6837\u7d27\u8feb\u4e0d\u820d\u7684\u903c\u95ee\u8001\u592b\uff1f\u201d \u3000\u3000\u949f\u4e00\u8c6a\u6b63\u8272\u9053\uff1a\u201c\u5c5e\u4e0b\u8eab\u4e3a\u526f\u76df\u4e3b\uff0e\u7406\u5e94\u53c2\u4e0e\u8c37\u5185\u5927\u4e8b\u3002\u5373\u4fbf\u5c5e\u4e0b\u4e0d\u4e3b\u52a8\u63d0\u51fa\uff0c\u76df\u4e3b\u4e5f \u8be5\u548c\u5c5e\u4e0b\u5546\u8bae\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u5e76\u4e0d\u7b54\u8bdd\uff0c\u8f6c\u8eab\u671b\u4e86\u5cad\u5357\u4e8c\u5947\u4ee5\u53ca\u5f20\u656c\u5b89\u2014\u773c\u9053\uff1a\u201c\u54b1\u4eec\u8d70\uff01\u201d \u3000\u3000\u73b0\u573a\u53ea\u5269\u4e0b\u949f\u4e00\u8c6a\u3001\u4f59\u4ea6\u4e50\u3001\u9ea6\u5c0f\u660e\u4e09\u4eba\u3002 \u3000\u3000\u4e1c\u6d77\u9662\u95e8\u53e3\u7684\u8bb8\u5c0f\u65e6\u5df2\u56de\u623f\u53bb\u4e86\uff0c\u6c5f\u5357\u56db\u602a\u4e5f\u65e9\u5df2\u72fc\u72c8\u800c\u53bb\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u957f\u957f\u5401\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u9ea6\u8001\u5f1f\uff0c\u8fd9\u662f\u4f55\u82e6\uff1f\u4f60\u5c31\u8010\u7740\u6027\u5b50\u5728\u4e1c\u6d77\u9662\u5f85\u4e00\u591c\u53c8\u6709\u4f55 \u59a8\uff0e\u4f55\u5fc5\u5e73\u767d\u60f9\u8d77\u8fd9\u573a\u98ce\u6ce2\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u5bf9\u949f\u4e00\u8c6a\u548c\u4f59\u4ea6\u4e50\u7684\u820d\u547d\u62a4\u536b\u81ea\u5df1\uff0c\u5185\u5fc3\u81ea\u662f\u6709\u8bf4\u4e0d\u51fa\u7684\u611f\u6fc0\uff0c\u95fb\u8a00\u8038\u4e86\u8038\u80a9 \u9053\uff1a\u201c\u5b88\u7740\u90a3\u79cd\u65e0\u803b\u8d31\u8d27\uff0e\u6211\u662f\u4e00\u65f6\u4e00\u523b\u4e5f\u4e0d\u60f3\u7559\uff01\u201d \u3000\u3000\u949f\u4e00\u8c6a\u7f04\u9ed8\u4e86\u534a\u664c\u9053\uff1a\u201c\u4e8b\u60c5\u5df2\u7ecf\u53d1\u751f\u4e86\uff0e\u8ffd\u6094\u4e5f\u6ca1\u7528\u3002\u6211\u95ee\u4f60\uff0c\u8fd9\u4f4d\u524d\u6765\u76f8\u52a9\u7684\u84dd\u886b \u8499\u9762\u4eba\u4f60\u53ef\u8ba4\u8bc6\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u6447\u5934\u9053\uff1a\u201c\u6211\u4e5f\u4e00\u76f4\u5728\u5947\u602a\uff0c\u8fd9\u4eba\u65e0\u7f18\u65e0\u6545\u7684\u524d\u6765\u76f8\u52a9\u4e8e\u6211\uff0c\u7a76\u7adf\u662f\u4ec0\u4e48\u4eba \u5462\uff1f\u201d \u3000\u3000\u201c\u8fd9\u8981\u95ee\u4f60\u4e86\u3002\u4ed4\u7ec6\u60f3\u60f3\u770b\uff0c\u4ed6\u5230\u5e95\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u6211\u521a\u624d\u4e0d\u662f\u8bf4\u8fc7\u4e0d\u8ba4\u8bc6\u8fd9\u4eba\u5417\uff1f\u201d \u3000\u3000\u201c\u4f60\u5e94\u8be5\u4f1a\u8ba4\u8bc6\u7684\u3002\u201d \u3000\u3000\u201c\u8fd9\u8bdd\u6211\u4e0d\u61c2\u3002\u201d \u3000\u3000\u201c\u7406\u7531\u5f88\u7b80\u5355\uff0c\u5728\u8fd9\u671f\u95f4\uff0c\u5fc5\u5b9a\u7ed3\u4ea4\u8fc7\u4e0d\u5c11\u670b\u53cb\u3002\u8fd9\u4eba\u4e00\u5b9a\u662f\u4f60\u65b0\u8fd1\u5728\u5916\u8ba4\u8bc6\u7684\uff0e\u4e0d\u7136 \u600e\u4f1a\u524d\u6765\u76f8\u52a9\uff1f\u201d \u3000\u3000\u201c\u4f60\u8fd9\u6d3b\u56fa\u7136\u6709\u7406\uff0e\u4f46\u6211\u4e24\u6b21\u5916\u51fa\uff0c\u5374\u5e76\u6ca1\u8ba4\u8bc6\u4ec0\u4e48\u670b\u53cb\u3002\u201d \u3000\u3000\u201c\u518d\u4ed4\u7ec6\u60f3\u60f3\u3002\u201d \u3000\u3000\u201c\u624d\u51e0\u5929\u7684\u4e8b\uff0e\u96be\u9053\u6211\u8fd8\u60f3\u4e0d\u8d77\u6765\uff1f\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u2014\u65c1\u8f7b\u54b3\u2014\u58f0\u9053\uff1a\u201c\u4e0d\u5fc5\u518d\u8c08\u8bba\u8fd9\u4e8b\uff0c\u5148\u56de\u67cf\u9f84\u9662\uff0c\u4e00\u5207\u7b49\u5929\u4eae\u518d\u8bf4\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u6de1\u7136\u2014\u7b11\u9053\uff1a\u201c\u82e5\u7b49\u5230\u5929\u4eae\uff0c\u53ea\u6015\u5c0f\u5f1f\u5c31\u8d70\u4e0d\u6210\u4e86\uff01\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u548c\u949f\u4e00\u8c6a\u5403\u4e86\u4e00\u60ca\u3002 \u3000\u3000\u949f\u2014\u8c6a\u6025\u6025\u95ee\u9053\uff1a\u201c\u4f60\u8bf4\u4ec0\u4e48\uff0c\u53ef\u662f\u8981\u79bb\u5f00\u8ff7\u8e2a\u8c37\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u6211\u4e0d\u79bb\u5f00\u8ff7\u8e2a\u8c37\uff0c\u96be\u9053\u8fd8\u8981\u7b49\u7740\u88ab\u90a3\u8001\u738b\u516b\u86cb\u6574\u6b7b\uff1f\u201d \u3000\u3000\u949f\u4e00\u8c6a\u5fc3\u5934\u5927\u6025\u9053\uff1a\u201c\u9ea6\u8001\u5f1f\uff0c\u5343\u4e07\u8d70\u4e0d\u5f97\u3002\u4f60\u8be5\u77e5\u9053\uff0c\u67cf\u9f84\u9662\u6240\u6709\u7684\u4eba\uff0c\u5168\u628a\u5e0c\u671b\u5bc4 \u6258\u5728\u4f60\u4e00\u4eba\u8eab\u4e0a\uff0c\u4f60\u82e5\u4e00\u8d70\uff0c\u6211\u4eec\u5927\u5bb6\u5168\u5b8c\u5566\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u6447\u5934\u9053\uff1a\u201c\u54ea\u6709\u8fd9\u4e48\u4e25\u91cd\uff1f\u50cf\u6211\u8fd9\u79cd\u4eba\uff0c\u591a\u4e00\u4e2a\u5c11\u4e00\u4e2a\u6839\u672c\u7b97\u4e0d\u4e86\u4ec0\u4e48\u3002\u66f4\u4f55\u51b5 \u6211\u8fd8\u7ecf\u5e38\u7ed9\u4f60\u4eec\u60f9\u9ebb\u70e6\u3002\u6211\u8d70\u4e86\uff0c\u4f60\u4eec\u53cd\u800c\u5c11\u62c5\u4e00\u4efd\u5fc3\u4e8b\u3002\u201d \u3000\u3000\u949f\u4e00\u8c6a\u60c5\u8bcd\u6073\u5207\u5730\u9053\uff1a\u201c\u4e0d\uff01\u9ea6\u8001\u5f1f\uff0c\u4f60\u8be5\u5fc3\u91cc\u6709\u6570\uff0c\u54b1\u4eec\u67cf\u9f84\u9662\u4ee5\u4f60\u7684\u6b66\u529f\u6700\u9ad8\uff0c\u6709 \u4f60\u5728\uff0c\u970d\u5143\u4f3d\u624d\u4e0d\u6562\u628a\u67cf\u9f84\u9662\u7684\u4eba\u600e\u4e48\u6837\u3002\u82e5\u4f60\u2014\u8d70\uff0c\u67cf\u9f84\u9662\u6240\u6709\u7684\u4eba\u4e5f\u5c31\u5371\u9669\u4e86\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u526f\u76df\u4e3b\uff0c\u4f60\u592a\u628a\u5c0f\u5f1f\u770b\u5927\u4e86\uff0c\u8fd9\u756a\u8fc7\u5956\u4e4b\u8bcd\uff0c\u5c0f\u5f1f\u5b9e\u5728\u62c5\u5f85\u4e0d\u8d77\uff01\u201d \u3000\u3000\u201c\u6211\u8bf4\u7684\u5b8c\u5168\u662f\u5b9e\u8bdd\u3002\u4f60\u8be5\u4e3a\u5927\u5bb6\u7740\u60f3\uff0c\u5982\u679c\u771f\u7684\u8981\u8d70\uff0e\u90a3\u4e48\u6211\u4eec\u67cf\u9f84\u9662\u6240\u6709\u7684\u4eba\uff0c\u4e5f \u8ddf\u7740\u4f60\u4e00\u8d77\u8d70\uff01\u201d \u3000\u3000\u8c01\u77e5\u8fd9\u4e00\u6765\uff0c\u9ea6\u5c0f\u660e\u53cd\u9762\u7740\u4e86\u6025\uff0c\u5fd9\u9053\uff1a\u201c\u4f60\u4eec\u7edd\u5bf9\u8d70\u4e0d\u5f97\uff01\u201d \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u4f60\u81ea\u5df1\u8981\u8d70\uff0c\u5374\u529d\u6211\u4eec\u4e0d\u8d70\uff0c\u8fd9\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u7406\u7531\u5f88\u7b80\u5355\u3002\u8ff7\u8e2a\u8c37\u662f\u6211\u80e1\u5e08\u5144\u548c\u80e1\u5e08\u5ac2\u4eb2\u624b\u5efa\u7acb\u8d77\u6765\u7684\uff0c\u73b0\u5728\u867d\u7531\u970d\u5143 \u4f3d\u5f53\u4e86\u76df\u4e3b\uff0e\u4f46\u54b1\u4eec\u67cf\u9f84\u9662\u7684\u4eba\u9a6c\uff0c\u603b\u8fd8\u662f\u80e1\u5e08\u5144\u548c\u80e1\u5e08\u5ac2\u7684\u65e7\u90e8\u4e0b\u3002\u54b1\u4eec\u603b\u4e0d\u80fd\u628a\u8ff7\u8e2a\u8c37 \u62f1\u624b\u5b8c\u5168\u8ba9\u7ed9\u970d\u5143\u4f3d\uff0c\u90a3\u6837\u505a\uff0c\u5982\u4f55\u5bf9\u5f97\u4f4f\u80e1\u5e08\u5144\u592b\u5987\u7684\u5728\u5929\u4e4b\u7075\uff1f\u201d \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u4f60\u65e2\u7136\u80fd\u60f3\u5230\u8fd9\u2014\u5c42\uff0c\u5c31\u4e0d\u8be5\u6709\u79bb\u5f00\u8ff7\u8e2a\u8c37\u7684\u5ff5\u5934\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u6211\u662f\u8feb\u4e0d\u5f97\u5df2\uff0c\u4eca\u665a\u51fa\u4e86\u8fd9\u4e48\u5927\u7684\u7eb0\u6f0f\uff0e\u5982\u679c\u4e0d\u8d70\uff0e\u96be\u9053\u8981\u7b49\u7740\u90a3\u8001\u738b\u516b \u86cb\u5272\u4e0b\u6211\u7684\u8111\u888b\uff01\u201d \u3000\u3000\u949f\u4e00\u8c6a\u6df1\u6df1\u5438\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u4f60\u653e\u5fc3\uff0c\u8fd9\u4e8b\u5305\u5728\u6211\u548c\u4f59\u5148\u751f\u8eab\u4e0a\u3002\u5982\u679c\u970d\u5143\u4f3d\u8981\u5bf9\u4f60\u600e \u6837\uff0c\u6211\u4eec\u5c31\u548c\u4ed6\u51b3\u4e00\u6b7b\u6218\uff0c\u5373\u4f7f\u4ece\u6b64\u846c\u8eab\u8ff7\u8e2a\u8c37\u4e5f\u5728\u6240\u4e0d\u60dc\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u4f9d\u7136\u6447\u5934\u9053\uff1a\u201c\u4f60\u4eec\u4e3a\u6211\u9ea6\u5c0f\u660e\u4ed8\u51fa\u8fd9\u6837\u5927\u7684\u4ee3\u4ef7\uff0c\u90a3\u591a\u5212\u4e0d\u6765\uff0e\u522b\u8bb2\u5566\uff01\u56de\u5230 \u67cf\u9f84\u9662\u518d\u8bf4\uff0e\u201d \u3000\u3000\u949f\u4e00\u8c6a\u4e0d\u7531\u4e00\u559c\u9053\uff1a\u201c\u83ab\u975e\u4f60\u5df2\u56de\u5fc3\u8f6c\u610f\u4e86\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u6211\u603b\u8be5\u56de\u53bb\u6574\u7406\u6574\u7406\u884c\u56ca\uff0c\u628a\u5fc5\u8981\u7684\u4e1c\u897f\u5e26\u5728\u8eab\u4e0a\u518d\u8d70\u3002\u201d \u3000\u3000\u56de\u5230\u67cf\u9f84\u9662\uff0c\u9ea6\u5c0f\u660e\u679c\u7136\u5728\u623f\u95f4\u6574\u7406\u884c\u56ca\u3002 \u3000\u3000\u4ed6\u4e3a\u4e86\u4e0d\u4f7f\u949f\u4e00\u8c6a\u3001\u4f59\u4ea6\u4e50\u529d\u963b\u548c\u6253\u6270\uff0c\u8fde\u95e8\u4e5f\u5173\u4e86\u8d77\u6765\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u548c\u4f59\u4ea6\u4e50\u5f53\u771f\u6025\u5f97\u50cf\u70ed\u9505\u4e0a\u7684\u8682\u8681\uff0c\u949f\u4e00\u8c6a\u65b9\u624d\u6240\u8bf4\u7684\u5b8c\u5168\u662f\u771f\u5fc3\u5b9e\u8bdd\uff0c\u82e5\u8d70\u4e86 \u9ea6\u5c0f\u660e\uff0e\u4ed6\u4eec\u67cf\u9f84\u9662\u8fd9\u4e00\u4f19\u4eba\u5f80\u540e\u7684\u65e5\u5b50\uff0c\u5b9e\u5728\u5c31\u4e0d\u597d\u8fc7\u4e86\u3002 \u3000\u3000\u5c24\u5176\u9ea6\u5c0f\u660e\u662f\u80e1\u67cf\u9f84\u7684\u5e08\u5f1f\uff0c\u5373\u4f7f\u5bf9\u67cf\u9f84\u9662\u65e0\u635f\uff0c\u4ed6\u4eec\u4e5f\u4e0d\u80fd\u8ba9\u4ed6\u56e0\u800c\u79bb\u53bb\u3002 \u3000\u3000\u7531\u4e8e\u949f\u4e00\u8c6a\u548c\u4f59\u4ea6\u4e50\u8bf4\u8bdd\u7684\u58f0\u97f3\u751a\u5927\uff0c\u5f88\u5feb\u4fbf\u60ca\u52a8\u4e86\u67cf\u9f84\u9662\u6240\u6709\u7761\u68a6\u4e2d\u7684\u4eba\uff0e\u4ed6\u4eec\u4e00\u542c \u9ea6\u5c0f\u660e\u8981\u8d70\uff0e\u7acb\u5373\u614c\u5fd9\u62ab\u8863\u4e0b\u5e8a\uff0c\u9f50\u96c6\u5728\u9ea6\u5c0f\u660e\u623f\u95e8\u5916\uff0c\u5411\u949f\u4e00\u8c6a\u548c\u4f59\u4ea6\u4e50\u95ee\u957f\u95ee\u77ed\u3002 \u3000\u3000\u5176\u4e2d\u552f\u6709\u82d7\u7d20\u82d3\uff0c\u4e0d\u77e5\u662f\u5426\u7761\u5f97\u592a\u719f\uff0c\u5e76\u672a\u8d77\u5e8a\u524d\u6765\u3002 \u3000\u3000\u5f53\u949f\u4e00\u8c6a\u548c\u4f59\u4ea6\u4e50\u8bf4\u51fa\u65b9\u624d\u7684\u7ecf\u8fc7\u540e\uff0c\u7fa4\u96c4\u4e5f\u90fd\u5927\u4e3a\u7740\u6025\uff0c\u4ed6\u4eec\u8fd9\u4e9b\u5929\u6765\uff0c\u65e9\u5df2\u628a\u9ea6\u5c0f \u660e\u89c6\u4e3a\u67cf\u9f84\u9662\u7684\u7075\u9b42\u4eba\u7269\uff0c\u5c82\u80af\u8ba9\u4ed6\u79bb\u53bb\uff1f \u3000\u3000\u53ea\u542c\u738b\u5927\u5eb7\u56b7\u7740\u9053\uff1a\u201c\u5976\u5976\u7684\uff01\u4ffa\u738b\u5927\u5eb7\u4e00\u5411\u6700\u80fd\u60f9\u4e8b\uff0c\u4f46\u4e0a\u534a\u591c\u5230\u4e1c\u6d77\u9662\uff0e\u5374\u5e76\u6ca1\u60f9 \u51fa\u4ec0\u4e48\u4e8b\u6765\u3002\u4e3a\u4ec0\u4e48\u9ea6\u5c0f\u5144\u5f1f\u504f\u504f\u60f9\u51fa\u4e8b\u6765\u3002\u54b1\u4eec\u7edd\u5bf9\u4e0d\u80fd\u8ba9\u9ea6\u5c0f\u5144\u5f1f\u8d70\uff0c\u62e6\u4e5f\u8981\u62e6\u4f4f \u4ed6\uff01\u201d \u3000\u3000\u7b11\u9762\u4f5b\u9c8d\u8d85\u9053\uff1a\u201c\u5f53\u7136\u4e0d\u80fd\u8ba9\u4ed6\u8d70\uff0e\u4f60\u522b\u7a77\u56b7\uff01\u201d \u3000\u3000\u7fa4\u8c6a\u4eec\u5728\u95e8\u5916\u7b49\u4e86\u597d\u5927\u2014\u4f1a\u513f\uff0e\u624d\u89c1\u9ea6\u5c0f\u660e\u8eab\u80cc\u884c\u56ca\u5175\u5203\uff0c\u6253\u5f00\u95e8\u6765\u3002 \u3000\u3000\u4e8e\u662f\u7fa4\u96c4\u4eec\u4e00\u62e5\u800c\u4e0a\uff0c\u8d8a\u53d1\u628a\u623f\u95e8\u5916\u5835\u4e86\u4e2a\u6c34\u6cc4\u4e0d\u901a\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u89c1\u6b64\u60c5\u5f62\uff0e\u53c8\u662f\u7fa4\u8c6a\u4eec\u7684\u4e00\u756a\u597d\u610f\uff0c\u53ea\u5f97\u9000\u540e\u4e24\u6b65\uff0e\u62f1\u4e86\u62f1\u624b\u9053\uff1a\u201c\u8bf8\u4f4d\u7684\u5fc3 \u610f\uff0e\u5728\u4e0b\u5fc3\u9886\u4e86\u3002\u4f46\u6211\u53bb\u610f\u5df2\u51b3\uff0c\u8bf8\u4f4d\u5c31\u662f\u518d\u633d\u7559\uff0c\u4e5f\u65e0\u6cd5\u6253\u6d88\u6211\u7684\u53bb\u610f\uff0c\u8fd8\u8bf7\u8ba9\u5f00\u4e00\u6761\u9053 \u8def\u624d\u597d\u8d70\u8def\uff01\u201d \u3000\u3000\u738b\u5927\u5eb7\u5927\u58f0\u9053\uff1a\u201c\u4ffa\u4eec\u7edd\u5bf9\u4e0d\u80fd\u8ba9\u4f60\u8d70\uff0e\u9ea6\u5c0f\u5144\u5f1f\u603b\u8be5\u7ed9\u4ffa\u4eec\u70b9\u9762\u5b50\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u8fd9\u4e8b\u4e0e\u9762\u5b50\u6839\u672c\u626f\u4e0d\u4e0a\u5173\u7cfb\u3002\u201d \u3000\u3000\u738b\u5927\u5eb7\u9053\uff1a\u201c\u4ffa\u4eec\u5f1f\u5144\u4e09\u4e2a\u4e3a\u4e86\u4e0d\u8ba9\u8ff7\u8e2a\u8c37\u843d\u5165\u970d\u5143\u4f3d\u4e4b\u624b\uff0e\u8fde\u8001\u5bb6\u90fd\u4e0d\u8981\uff0e\u7531\u5d02\u5c71\u51e0 \u5343\u91cc\u8def\u8d76\u6765\u5317\u5cb3\uff0c\u800c\u4f60\u8001\u5f1f\u4eba\u5728\u8ff7\u8e2a\u8c37\uff0c\u5374\u53c8\u8981\u79bb\u5f00\uff0e\u8fd9\u7b97\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u5401\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u4f60\u4eec\u4e09\u4f4d\u867d\u7136\u5df2\u7ecf\u6765\u5230\u5317\u5cb3\uff0c\u53ef\u662f\u8ff7\u8e2a\u8c37\u8fd8\u662f\u843d\u5165\u970d\u5143\u4f3d\u4e4b \u624b\uff01\u201d \u3000\u3000\u738b\u5927\u5eb7\u6025\u5f97\u56b7\u9053\uff1a\u201c\u8fd9\u6837\u8bf4\u4ffa\u4eec\u4e5f\u8be5\u8d70\u5566\uff01\u8001\u5927\u3001\u8001\u4e8c\uff0e\u54b1\u4eec\u56de\u5d02\u5c71\u53bb\uff01\u201d \u3000\u3000\u9b3c\u8bf8\u845b\u6d2a\u6cfd\u77aa\u4e86\u738b\u5927\u5eb7\u4e00\u773c\u9053\uff1a\u201c\u4f60\u4e0d\u529d\u8fd8\u597d\uff0c\u8fd9\u4e00\u529d\u7b49\u4e8e\u7ed9\u9ea6\u5c0f\u5144\u5f1f\u706b\u4e0a\u52a0\u6cb9\u3002\u4f60\u5148 \u95ed\u5634\uff0e\u8ba9\u6211\u8ddf\u9ea6\u5c0f\u5144\u5f1f\u8bb2\u51e0\u53e5\u8bdd\uff01\u201d \u3000\u3000\u63a5\u7740\u671b\u5411\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\u79bb\u5f00\u8ff7\u8e2a\u8c37\u540e\uff0c\u51c6\u5907\u5230\u54ea\u91cc\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u5929\u4e0b\u4e4b\u5927\uff0c\u4f55\u5904\u4e0d\u53ef\u6816\u8eab\u3002\u96be\u9053\u4f60\u8fd8\u6015\u6211\u5728\u5916\u9762\u997f\u6b7b\uff1f\u201d \u3000\u3000\u6d2a\u6cfd\u5c34\u5c2c\u4e00\u7b11\u9053\uff1a\u201c\u4f60\u603b\u8be5\u8bf4\u51fa\u8981\u53bb\u7684\u5730\u65b9\uff0c\u5c06\u6765\u6211\u4eec\u4e5f\u597d\u8ddf\u4f60\u8fde\u7edc\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u4e0d\u5047\u601d\u7d22\u9053\uff1a\u201c\u6211\u53ef\u4ee5\u53bb\u627e\u627e\u5e08\u7236\u9093\u79cb\uff01\u201d \u3000\u3000\u6d2a\u6cfd\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u636e\u8bf4\u5f53\u5e74\u4f60\u7559\u5728\u8ff7\u8e2a\u8c37\uff0c\u5e76\u975e\u4ed6\u7684\u610f\u601d\uff0e\u8fd9\u7b49\u4e8e\u80cc\u53db\u4e86\u4ed6\uff0e\u518d\u53bb\u627e \u4ed6\uff0e\u53ea\u6015\u4e0d\u662f\u529e\u6cd5\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u7565\u4e00\u6c89\u541f\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u9053\u6211\u66fe\u6709\u597d\u51e0\u4e2a\u5e08\u7236\uff1f\u6700\u65e9\u6211\u662f\u5929\u53f0\u4e07\u82b1\u5bab\u7684\u4eba\uff0e\u6211\u53ef\u4ee5 \u518d\u56de\u5929\u53f0\u4e07\u82b1\u5bab\u53bb\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u5929\u53f0\u4e07\u82b1\u5bab\u5bab\u4e3b\u4f5f\u516c\u5e38\u5df2\u7ecf\u6b7b\u4e86\u3002\u201d \u3000\u3000\u201c\u4ed6\u867d\u7136\u6b7b\u4e86\uff0e\u4f46\u4e07\u82b1\u5bab\u5176\u4ed6\u6240\u6709\u7684\u4eba\u4ecd\u672a\u79bb\u53bb\uff0c\u76ee\u524d\u7531\u5305\u4e5d\u5cf0\u8001\u524d\u8f88\u5728\u603b\u7ba1\u4e00\u5207\u3002\u4ed6 \u8001\u4eba\u5bb6\u5f53\u5e74\u5f85\u6211\u5f88\u597d\uff0e\u66f4\u4f55\u51b5\u8c37\u524d\u76df\u4e3b\u662f\u6b7b\u5728\u4e07\u82b1\u5bab\u5916\uff0c\u8bf4\u4e0d\u5b9a\u6211\u53ef\u4ee5\u627e\u5230\u5979\u7684\u9057\u4f53\u3002\u201d \u3000\u3000\u201c\u8bf4\u6765\u8bf4\u53bb\uff0c\u90fd\u4e0d\u662f\u529e\u6cd5\u3002\u552f\u4e00\u7684\u529e\u6cd5\uff0c\u662f\u4f60\u4ecd\u7559\u5728\u8ff7\u8e2a\u8c37\uff0e\u6211\u76f8\u4fe1\u970d\u5143\u4f3d\u7edd\u4e0d\u4f1a\u628a\u4f60 \u600e\u6837\u3002\u201d \u3000\u3000\u201c\u4f60\u76f8\u4fe1\uff0c\u53ef\u662f\u6211\u4e0d\u76f8\u4fe1\uff01\u201d \u3000\u3000\u8fd9\u65f6\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u9ea6\u8001\u5f1f\uff0c\u6211\u4eec\u6240\u6709\u7684\u4eba\u7edd\u5bf9\u4e0d\u4f1a\u8ba9\u4f60\u8d70\u7684\u3002\u81f3\u4e8e\u660e\u5929\u7684\u4e8b\uff0c\u6211\u949f\u2014\u8c6a \u62fc\u7740\u8fd9\u9897\u8111\u888b\u4e0d\u8981\uff0e\u4e5f\u8981\u4e3a\u4f60\u62c5\u4fdd\uff0e\u5fc5\u8981\u65f6\u54b1\u4eec\u5c31\u548c\u970d\u5143\u4f3d\u62fc\u2014\u6b7b\u6218\uff0c\u7136\u540e\u5927\u5bb6\u518d\u2014\u8d77 \u8d70\uff01\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u4e5f\u8ddf\u7740\u9053\uff1a\u201c\u9ea6\u8001\u5f1f\u8bf7\u76f8\u4fe1\u949f\u526f\u76df\u4e3b\u7684\u8bdd\uff0c\u54b1\u4eec\u67cf\u9f84\u9662\u7684\u5b9e\u529b\u867d\u7136\u6bd4\u4e0d\u4e0a\u970d\u5143 \u4f3d\uff0c\u4f46\u970d\u5143\u4f3d\u5374\u4e0d\u6562\u8f7b\u6613\u6fc0\u8d77\u4f17\u6012\u3002 \u3000\u3000\u970d\u5143\u4f3d\u8001\u5978\u5de8\u733e\uff0e\u4ed6\u5e94\u5f53\u660e\u767d\uff0c\u60f9\u607c\u4e86\u67cf\u9f84\u9662\uff0e\u4e00\u65e6\u53cc\u65b9\u706b\u62fc\u8d77\u6765\uff0e\u5fc5\u5b9a\u4e24\u8d25\u4ff1\u4f24\uff0e\u800c \u4e14\u56e0\u800c\u52a8\u6447\u4e86\u8ff7\u8e2a\u8c37\u7684\u6839\u57fa\uff0c\u8fd9\u65b9\u9762\u4ed6\u5c82\u80fd\u4e0d\u987e\u8651\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u5728\u4f17\u4eba\u8f6e\u756a\u529d\u8bf4\u4e0b\uff0c\u65e0\u5948\u4f55\u53ea\u597d\u6682\u65f6\u7559\u4e0b\u4e0d\u8d70\u3002 \u3000\u3000\u970d\u5143\u4f3d\u7387\u9886\u5cad\u5357\u4e8c\u5947\u7b49\u4eba\u56de\u53bb\u540e\uff0c\u5e76\u672a\u7acb\u5373\u5c31\u5bdd\uff0c\u5c31\u5728\u4ed6\u5c45\u5904\u9694\u58c1\u7684\u5bc6\u5ba4\u91cc\uff0c\u5cad\u5357\u4e8c\u5947 \u5206\u5750\u5de6\u53f3\uff0c\u540c\u65f6\u4e5f\u628a\u6c5f\u5357\u56db\u602a\u558a\u6765\uff0e\u4ee5\u5907\u67e5\u8be2\uff0e \u3000\u3000\u4e0d\u8fc7\u6c5f\u5357\u56db\u602a\u56e0\u5f62\u76f8\u72fc\u72c8\uff0c\u4f7f\u5f97\u970d\u5143\u4f3d\u4e22\u5c3d\u9762\u5b50\uff0c\u56e0\u4e4b\u8fde\u4e2a\u5ea7\u4f4d\u4e5f\u6ca1\u6709\uff0c\u53ea\u80fd\u72fc\u72c8\u4e0d \u582a\u3001\u5782\u5934\u4e27\u6c14\u7684\u7ad9\u5728\u4e00\u65c1\u3002 \u3000\u3000\u5176\u5b9e\uff0c\u6c5f\u5357\u56db\u602a\u6b64\u523b\u4ecd\u4e2a\u4e2a\u5934\u660f\u773c\u82b1\uff0e\u9aa8\u8f6f\u7b4b\u9ebb\u3002 \u3000\u3000\u5168\u8eab\u4e14\u75bc\u75db\u96be\u5f53\u3002\u82e5\u4e0d\u662f\u51db\u4e8e\u970d\u5143\u4f3d\u7684\u5a01\u4e25\uff0c\u4e0d\u5f97\u4e0d\u52c9\u5f3a\u652f\u6491\u7740\uff0c\u53ea\u6015\u65e9\u5c31\u8eba\u4e0b\u4e86\u3002 \u3000\u3000\u5df4\u5929\u4e49\u77e5\u9053\u970d\u5143\u4f3d\u6b63\u5728\u6c14\u5934\u4e0a\uff0e\u4e0d\u6562\u5148\u884c\u5f00\u53e3\uff0c\u5374\u628a\u773c\u76f4\u770b\u7740\u5b8b\u5929\u94ce\u3002 \u3000\u3000\u5b8b\u5929\u94ce\u5e72\u54b3\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u76df\u4e3b\uff0c\u65b9\u624d\u90a3\u4e2a\u84dd\u886b\u8499\u9762\u7684\u4eba\uff0c\u54b1\u4eec\u5fc5\u987b\u8bbe\u6cd5\u67e5\u660e\u4ed6\u7684\u8eab\u4efd\u6765 \u5386\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u54fc\u4e86\u58f0\u9053\uff1a\u201c\u4eba\u5df2\u7ecf\u8d70\u4e86\uff0c\u5230\u54ea\u91cc\u67e5\u53bb\uff1f\u8001\u592b\u539f\u4ee5\u4e3a\u8ff7\u8e2a\u8c37\u5bf9\u5916\u754c\u6765\u8bf4\uff0c\u6709\u5982\u5929 \u7f57\u5730\u7f51\u4e00\u822c\uff0c\u60f3\u4e0d\u5230\u7adf\u80fd\u8ba9\u5916\u4eba\u8f7b\u6613\u5730\u95ef\u4e86\u8fdb\u6765\u3002\u201d \u3000\u3000\u4ed6\u9ed8\u4e86\u4e00\u9ed8\uff0c\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u4e0a\u6b21\u88ab\u9634\u624b\u4e00\u9b54\u548c\u6bd2\u706b\u6210\u5168\u7b49\u4eba\u95ef\u8fdb\uff0c\u8fd8\u60c5\u6709\u53ef\u539f\uff0e\u4eca\u665a\u8fd9 \u5c0f\u5b50\uff0e\u540d\u4e0d\u89c1\u7ecf\u4f20\uff0c\u4e5f\u88ab\u4ed6\u95ef\u8fdb\u6765\uff0c\u5b9e\u5728\u592a\u4e0d\u50cf\u8bdd\uff01\u201d \u3000\u3000\u5b8b\u5929\u94ce\u9053\uff1a\u201c\u8fd9\u4eba\u7a76\u7adf\u662f\u4ece\u8c37\u5185\u4f55\u5904\u8fdb\u51fa\u7684\uff0c\u5f85\u4f1a\u513f\u5404\u5904\u5c97\u54e8\u53ef\u80fd\u5c31\u6709\u56de\u62a5\uff0c\u4e0d\u8fc7\u5c5e\u4e0b \u62c5\u5fc3\u662f\u5426\u6709\u5f1f\u5144\u4f24\u4ea1\uff0e\u201d \u3000\u3000\u970d\u5143\u4f3d\u53f1\u9053\uff1a\u201c\u4e0d\u5fc5\u8bf4\u4e86\uff0c\u4f60\u4eec\u7684\u610f\u601d\uff0c\u660e\u5929\u5982\u4f55\u5904\u7f6e\u9ea6\u5c0f\u660e\u90a3\u5c0f\u5b50\uff1f\u201d \u3000\u3000\u5b8b\u5929\u94ce\u987f\u4e86\u987f\u9053\uff1a\u201c\u90a3\u5c0f\u5b50\u8bf4\u4e0d\u5b9a\u5df2\u7ecf\u8dd1\u4e86\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u4ee5\u90a3\u5c0f\u5b50\u7684\u4e2a\u6027\uff0c\u7edd\u5bf9\u8dd1\u4e0d\u4e86\uff0e\u5176\u5b9e\u8001\u592b\u8fd8\u771f\u5e0c\u671b\u4ed6\u81ea\u52a8\u79bb\u5f00\u8ff7\u8e2a\u8c37\uff01\u201d \u3000\u3000\u5df4\u5929\u4e49\u7981\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u76df\u4e3b\u600e\u4f1a\u6709\u8fd9\u79cd\u60f3\u6cd5\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u53cc\u988a\u62bd\u6410\u4e86\u51e0\u4e0b\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4f4d\u90fd\u5fc5\u5b9a\u770b\u5f97\u51fa\u90a3\u5c0f\u5b50\u7684\u6b66\u529f\uff0c\u2014\u5929\u6bd4\u4e00\u5929\u9ad8\u3002\u82e5 \u7559\u5728\u8ff7\u8e2a\u8c37\uff0c\u52bf\u5fc5\u6210\u4e3a\u54b1\u4eec\u7684\u5fc3\u8179\u5927\u60a3\u3002\u201d \u3000\u3000\u5df4\u5929\u4e49\u9634\u68ee\u68ee\u4e00\u7b11\u9053\uff1a\u201c\u8fd9\u2014\u6b21\u76df\u4e3b\u6b63\u53ef\u8d81\u673a\u628a\u4ed6\u9664\u53bb\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u8001\u592b\u65e9\u5c31\u60f3\u628a\u4ed6\u9664\u53bb\uff0c\u53ef\u60dc\u59cb\u7ec8\u627e\u4e0d\u5230\u4ed6\u7684\u7f6a\u540d\u3002\u201d \u3000\u3000\u201c\u8fd9\u6b21\u4ed6\u72af\u4e0b\u4e86\u6297\u547d\u72af\u4e0a\u4e4b\u7f6a\uff0c\u6b63\u8be5\u6309\u5f8b\u5904\u6b7b\uff0c\u6740\u4e4b\u4e0d\u8650\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u4f60\u4eec\u53ef\u60f3\u5230\u6740\u4e86\u4ed6\u7684\u540e\u679c\uff1f\u201d \u3000\u3000\u201c\u6700\u591a\u5f15\u8d77\u949f\u4e00\u8c6a\u7b49\u7684\u4e0d\u6ee1\uff0c\u4f46\u4ed6\u4eec\u5374\u5fc5\u6562\u6012\u800c\u4e0d\u6562\u8a00\u3002\u201d \u3000\u3000\u201c\u4f60\u4eec\u5343\u4e07\u4e0d\u80fd\u5ffd\u89c6\u4e86\u949f\u4e00\u8c6a\u7b49\u4eba\u7684\u8fd9\u80a1\u529b\u91cf\uff0c\u8ff7\u8e2a\u8c37\u5185\u82e5\u6ca1\u6709\u4e86\u4ed6\u4eec\uff0c\u52bf\u5fc5\u5b9e\u529b\u5927 \u51cf\u3002\u82e5\u4ed6\u4eec\u8d77\u800c\u53cd\u6297\uff0c\u540e\u679c\u66f4\u662f\u4e0d\u582a\u60f3\u8c61\u3002\u8fd9\u5c31\u662f\u8001\u592b\u8fd1\u4e24\u6708\u6765\u4e00\u76f4\u5728\u7b3c\u7edc\u4ed6\u4eec\u7684\u539f\u56e0\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u76df\u4e3b\u5e94\u8be5\u660e\u767d\u8fd9\u4e9b\u4eba\u7edd\u4e0d\u53ef\u80fd\u771f\u5fc3\u4e0e\u54b1\u4eec\u5408\u4f5c\u3002\u201d \u3000\u3000\u201c\u8001\u592b\u5fc3\u91cc\u5f53\u7136\u6709\u6570\uff0e\u6211\u7684\u7528\u610f\uff0e\u4e0d\u8fc7\u662f\u6682\u65f6\u7a33\u4f4f\u4ed6\u4eec\uff0c\u4e00\u65e6\u57fa\u7840\u7262\u56fa\uff0e\u8ff7\u8e2a\u8c37\u4e0d\u518d\u6709 \u540e\u987e\u4e4b\u5fe7\uff0e\u518d\u8bbe\u6cd5\u4e00\u7f51\u6253\u5c3d\u4ed6\u4eec\u3002\u201d \u3000\u3000\u201c\u96be\u9053\u9ea6\u5c0f\u660e\u90a3\u53db\u9006\u7684\u4e8b\u5c31\u8fd9\u6837\u7b97\u4e86\uff1f\u201d \u3000\u3000\u201c\u8001\u592b\u521a\u624d\u8bf4\u8fc7\uff0c\u82e5\u4ed6\u80fd\u81ea\u884c\u9003\u8d70\uff0c\u54b1\u4eec\u5c31\u7b49\u4e8e\u51cf\u5c11\u4e00\u4efd\u654c\u5bf9\u529b\u91cf\uff0c\u949f\u4e00\u8c6a\u90a3\u4f19\u4eba\u4eca\u540e \u4e5f\u5fc5\u4e0d\u6562\u518d\u8fc7\u4efd\u56a3\u5f20\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u4ed6\u82e5\u4e0d\u9003\u8d70\uff0c\u76df\u4e3b\u603b\u4e0d\u80fd\u5c31\u8fd9\u6837\u7b97\u4e86\u3002\u201d \u3000\u3000\u201c\u8001\u592b\u81ea\u6709\u4e3b\u5f20\uff0e\u4f60\u4eec\u56de\u53bb\u7761\u5427\uff01\u201d \u3000\u3000\u949f\u4e00\u8c6a\u56de\u5230\u623f\u95f4\uff0c\u4e3a\u9ea6\u5c0f\u660e\u7684\u4e8b\u5c55\u8f6c\u53cd\u4fa7\uff0c\u4e00\u76f4\u96be\u4ee5\u5165\u7720\u3002\u4ed6\u8eab\u4e3a\u526f\u76df\u4e3b\uff0c\u66f4\u662f\u67cf\u9f84\u9662 \u65b9\u9762\u7684\u9996\u8111\uff0c\u5bf9\u970d\u5143\u4f3d\u660e\u5929\u5c06\u8981\u91c7\u53d6\u4ec0\u4e48\u884c\u52a8\uff0c\u81ea\u662f\u6bd4\u522b\u4eba\u66f4\u4e3a\u5173\u5fc3\u3002 \u3000\u3000\u8fd9\u65f6\u5df2\u7ecf\u662f\u4e0b\u534a\u591c\u4e86\uff0c\u5927\u7ea6\u5230\u4e86\u56db\u66f4\u5de6\u53f3\uff0c\u5373\u5c06\u6726\u80e7\u7761\u53bb\u4e4b\u9645\uff0e\u5ffd\u7136\u4e00\u7c92\u77f3\u5b50\u7531\u7a97\u5916\u98de \u8fdb\u5ba4\u5185\uff0c\u663e\u7136\u662f\u6709\u4eba\u793a\u8b66\u3002 \u3000\u3000\u4ed6\u7acb\u5373\u62ab\u8863\u4e0b\u5e8a\uff0c\u5411\u7a97\u5916\u671b\u53bb\uff0c\u7a97\u5916\u662f\u56f4\u5899\uff0e\u4e0d\u89c1\u4efb\u4f55\u52a8\u9759\u3002 \u3000\u3000\u6b63\u5728\u8bb6\u7136\u83ab\u540d\u95f4\uff0c\u53c8\u4e00\u7c92\u77f3\u5b50\u63b7\u8fdb\u7a97\u5185\uff0e \u3000\u3000\u8fd9\u6b21\u949f\u4e00\u8c6a\u7ec8\u4e8e\u770b\u6e05\u90a3\u77f3\u5b50\u662f\u7531\u56f4\u5899\u5916\u6295\u5c04\u8fdb\u6765\u7684\uff0c\u4ed6\u6beb\u4e0d\u8fdf\u7591\u5730\u8f7b\u8f7b\u6253\u5f00\u7a97\uff0c\u98de\u8eab\u63a0 \u51fa\u8f6c\u5899\u3002 \u3000\u3000\u56f4\u5899\u5916\u662f\u4e00\u7247\u72ed\u7a84\u7684\u7a7a\u5730\uff0c\u7a7a\u5730\u4e0a\u6742\u6811\u4e1b\u751f\uff0c\u518d\u5411\u5916\u4fbf\u662f\u5c71\u58c1\u3002 \u3000\u3000\u5f53\u4ed6\u843d\u5730\u4e0d\u4e45\uff0c\u4fbf\u6709\u4e00\u4e2a\u5a40\u5a1c\u4eba\u5f71\u7f13\u7f13\u8d70\u8fd1\u524d\u6765\uff0c\u8d6b\u7136\u662f\u82d7\u7d20\u5170\u3002 \u3000\u3000\u82d7\u7d20\u5170\u81ea\u5929\u53f0\u4e07\u82b1\u5bab\u56de\u6765\u7684\u7b2c\u4e8c\u5929\u4fbf\u505a\u4e86\u970d\u5143\u4f3d\u7684\u538b\u5be8\u592b\u4eba\uff0c\u5728\u8fd9\u6bb5\u671f\u95f4\uff0c\u949f\u4e00\u8c6a\u53ea\u548c \u5979\u5728\u805a\u4e49\u5385\u7684\u9152\u5bb4\u4e0a\u89c1\u8fc7\u3002 \u3000\u3000\u79c1\u4e0b\u6709\u673a\u4f1a\u76f8\u89c1\uff0c\u8fd9\u8fd8\u662f\u7b2c\u4e00\u6b21\u3002 \u3000\u3000\u5f53\u7136\uff0c\u4ed6\u5bf9\u82d7\u7d20\u5170\u65e0\u7aef\u80cc\u5f03\u67cf\u9f84\u9662\u7684\u4eba\u611f\u5230\u4e0d\u89e3\uff0c\u540c\u65f6\u4e5f\u5bf9\u82d7\u7d20\u5170\u7684\u6240\u4e3a\u4e0d\u9f7f\u3002\u6b64\u65f6\u5374 \u4e0d\u4fbf\u5f62\u4e4b\u4e8e\u8272\uff0c\u62b1\u4e86\u62b1\u62f3\u9053\uff1a\u201c\u539f\u6765\u662f\u970d\u592b\u4eba\uff01\u8fd9\u822c\u65f6\u5019\u5524\u9192\u949f\u67d0\uff0c\u5fc5\u5b9a\u662f\u6709\u4e8b\u4e86\uff1f\u201d \u3000\u3000\u82d7\u7d20\u5170\u4e0d\u7b54\u53cd\u95ee\u9053\uff1a\u201c\u6628\u591c\u53d1\u751f\u5728\u9ea6\u5c0f\u660e\u8eab\u4e0a\u7684\u4e8b\uff0e\u949f\u526f\u76df\u4e3b\u4e00\u5b9a\u77e5\u9053\u4e86\u3002\u201d \u3000\u3000\u949f\u4e00\u8c6a\u51b7\u7136\u9053\uff1a\u201c\u949f\u67d0\u5f53\u7136\u77e5\u9053\uff0c\u970d\u592b\u4eba\u53ef\u4ee5\u4e0d\u5173\u5fc3\u4ed6\uff0c\u949f\u67d0\u4e0d\u80fd\u4e0d\u5173\u5fc3\u4ed6\uff01\u201d \u3000\u3000\u201c\u949f\u76df\u4e3b\u600e\u77e5\u6211\u4e0d\u5173\u5fc3\u4ed6\uff1f\u201d \u3000\u3000\u201c\u4f60\u82e5\u5173\u5fc3\u4ed6\uff0c\u5c31\u4e0d\u81f3\u4e8e\u79bb\u5f00\u67cf\u9f84\u9662\u98de\u4e0a\u9ad8\u679d\u4e86\u3002\u201d \u3000\u3000\u201c\u949f\u526f\u76df\u4e3b\u4e0d\u8c08\u8fc7\u53bb\u7684\u4e8b\u597d\u5417\uff1f\u6211\u73b0\u5728\u6b63\u662f\u4e3a\u4ed6\u6765\u7684\uff1f\u201d \u3000\u3000\u201c\u4ed6\u6709\u4ec0\u4e48\u503c\u5f97\u970d\u592b\u4eba\u5173\u5fc3\u7684\uff1f\u201d \u3000\u3000\u201c\u76df\u4e3b\u8981\u6740\u4ed6\uff0c\u6211\u6765\u5c31\u662f\u8981\u544a\u8bc9\u4f60\u8fd9\u6d88\u606f\uff0c\u8bf7\u4f60\u8f6c\u544a\u4ed6\u5feb\u5feb\u9003\u79bb\u8ff7\u8e2a\u8c37\uff0c\u4ee5\u514d\u60f9\u4e0b\u6740\u8eab \u4e4b\u7978\uff01\u201d \u3000\u3000\u949f\u4e00\u8c6a\u609a\u7136\u4e00\u9707\uff0c\u8138\u8272\u77ac\u606f\u95f4\u53d8\u4e86\u51e0\u53d8\uff0c\u9053\uff1a\u201c\u970d\u592b\u4eba\u4ece\u54ea\u91cc\u542c\u6765\u7684\u6d88\u606f\uff1f\u201d \u3000\u3000\u82d7\u7d20\u5170\u9053\uff1a\u201c\u4ee5\u6211\u548c\u76df\u4e3b\u7684\u5173\u7cfb\uff0c\u8fd9\u6d88\u606f\u96be\u9053\u949f\u526f\u76df\u4e3b\u8fd8\u4e0d\u76f8\u4fe1\uff1f\u201d \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u592b\u4eba\u5f97\u6765\u7684\u6d88\u606f\uff0c\u949f\u67d0\u5f53\u7136\u76f8\u4fe1\uff0c\u53ea\u662f\u2026\u2026\u201d \u3000\u3000\u201c\u949f\u526f\u76df\u4e3b\u8981\u8bb2\u4ec0\u4e48\uff0c\u8bf7\u5feb\u8bf4\uff01\u201d \u3000\u3000\u201c\u4f9d\u949f\u67d0\u539f\u5148\u7684\u9884\u6599\uff0c\u970d\u76df\u4e3b\u4f3c\u4e4e\u4e0d\u81f4\u505a\u5f97\u8fd9\u4e48\u7edd\u3002\u9ea6\u5c0f\u660e\u7eb5\u7136\u4f1a\u53d7\u5230\u60e9\u5904\uff0c\u540e\u679c\u4e5f\u4e0d \u81f4\u5982\u6b64\u4e25\u91cd\uff0e\u201d \u3000\u3000\u201c\u76df\u4e3b\u505a\u4e8b\uff0c\u5f88\u96be\u9884\u6d4b\u3002\u73b0\u5728\u6211\u53ea\u5e0c\u671b\u949f\u526f\u76df\u4e3b\u5feb\u5feb\u901a\u77e5\u9ea6\u5c0f\u660e\u9003\u8d70\u3002\u201d \u3000\u3000\u949f\u4e00\u8c6a\u6b63\u8d77\u795e\u8272\u9053\uff1a\u201c\u970d\u592b\u4eba\u73b0\u5728\u5df2\u4e0d\u662f\u67cf\u9f84\u9662\u7684\u4eba\uff0c\u4e3a\u4ec0\u4e48\u8fd8\u8981\u5173\u5fc3\u67cf\u9f84\u9662\u7684\u4e8b\uff1f\u201d \u3000\u3000\u82d7\u7d20\u5170\u9762\u6cdb\u4e0d\u60a6\u4e4b\u8272\uff1a\u201c\u96be\u9053\u949f\u526f\u76df\u4e3b\u771f\u628a\u6211\u82d7\u7d20\u5170\u5f53\u6210\u65e0\u60c5\u65e0\u4e49\u7684\u4eba\uff1f\u201d \u3000\u3000\u949f\u2014\u8c6a\u9053\uff1a\u201c\u592b\u4eba\u82e5\u771f\u6709\u60c5\u6709\u4e49\uff0c\u5c31\u4e0d\u8be5\u80cc\u5f03\u67cf\u9f84\u9662\uff0c\u505a\u4e86\u970d\u76df\u4e3b\u7684\u538b\u5be8\u592b\u4eba\u3002\u201d \u3000\u3000\u82d7\u7d20\u5170\u5bd2\u7740\u8138\u8272\u9053\uff1a\u201c\u8fd9\u662f\u4ec0\u4e48\u8bdd\uff01\u7537\u5927\u5f53\u5a5a\uff0e\u5973\u5927\u5f53\u5ac1\u3002\u6211\u82d7\u7d20\u5170\u5df2\u7ecf\u662f\u63a5\u8fd1\u4e09\u5341\u7684 \u5973\u4eba\uff0c\u96be\u9053\u5c31\u4e00\u8f88\u5b50\u4e0d\u5ac1\u4eba\uff1f\u201d \u3000\u3000\u201c\u4f60\u5c31\u662f\u8981\u5ac1\u4eba\uff0e\u4e5f\u4e0d\u8be5\u5ac1\u7ed9\u970d\u76df\u4e3b\uff01\u201d \u3000\u3000\u201c\u970d\u76df\u4e3b\u6709\u4ec0\u4e48\u4e0d\u597d\uff1f\u4ed6\u65e2\u6709\u8eab\u4efd\uff0c\u53c8\u6709\u5730\u4f4d\uff0c\u6211\u80fd\u505a\u4e3a\u5929\u4e0b\u7eff\u6797\u76df\u4e3b\u7684\u592b\u4eba\uff0c\u4e5f\u662f\u4ef6 \u5149\u8363\u7684\u4e8b\u3002\u201d \u3000\u3000\u949f\u4e00\u8c6a\u6012\u706b\u653b\u5fc3\uff0e\u771f\u6068\u4e0d\u5f97\u5f53\u573a\u628a\u82d7\u7d20\u5170\u63b4\u4e0a\u51e0\u8033\u5149\uff0c\u4f46\u4ed6\u4e3a\u987e\u5168\u5927\u5c40\uff0e\u8fd8\u662f\u5fcd\u4e86\u3002\u5f53 \u4e0b\u51b7\u51b7\u2014\u7b11\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u592b\u4eba\u5c31\u4e0d\u8be5\u6765\u544a\u8bc9\u8fd9\u6d88\u606f\uff0e\u9ea6\u5c0f\u660e\u88ab\u6740\u4e86\uff0c\u4f60\u8be5\u79f0\u5fc3\u5982\u610f\u624d \u662f\uff01\u201d \u3000\u3000\u82d7\u7d20\u5170\u6574\u4e86\u6574\u8138\u8272\u9053\uff1a\u201c\u8fd9\u662f\u4ec0\u4e48\u8bdd\uff1f\u6211\u4e0d\u4e3a\u4e86\u67cf\u9f84\u9662\uff0c\u603b\u8fd8\u5ff5\u5728\u5df2\u6545\u53bb\u7684\u80e1\uff0e\u8c37\u4e24\u4f4d \u524d\u4efb\u76df\u4e3b\u4e4b\u60c5\uff0c\u9ea6\u5c0f\u660e\u662f\u4ed6\u4eec\u7684\u5e08\u5f1f\uff0c\u6211\u4e0d\u80fd\u773c\u770b\u4ed6\u767d\u767d\u9001\u547d\uff01\u201d \u3000\u3000\u949f\u2014\u8c6a\u54fc\u4e86\u58f0\u9053\uff1a\u201c\u96be\u5f97\u970d\u592b\u4eba\u8fd8\u6ca1\u5fd8\u8bb0\u80e1\u3001\u8c37\u4e24\u4f4d\u524d\u76df\u4e3b\uff01\u201d \u3000\u3000\u82d7\u7d20\u5170\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6d88\u606f\u5df2\u7ecf\u4f20\u5230\u4e86\uff0c\u82e5\u518d\u8c08\u4e0b\u53bb\uff0c\u5fc5\u5b9a\u8d8a\u53d1\u6709\u4f24\u548c\u6c14\uff0c\u6211\u8d70\u4e86\uff01\u201d \u3000\u3000\u82d7\u7d20\u5170\u7684\u6d88\u606f\u5f53\u7136\u4e0d\u4f1a\u662f\u5047\uff0c\u4f7f\u5f97\u4e00\u5411\u5934\u8111\u51b7\u9759\u7684\u949f\u4e00\u8c6a\u4e5f\u6c89\u4e0d\u4f4f\u6c14\uff0c\u56de\u5230\u67cf\u9f84\u9662\uff0c\u4ec0 \u4e48\u4e5f\u987e\u4e0d\u5f97\uff0c\u4fbf\u5148\u884c\u6572\u5f00\u4e86\u4f59\u4ea6\u4e50\u7684\u623f\u95e8\u3002 \u3000\u3000\u4f59\u4ea6\u4e50\u6b63\u5728\u719f\u7761\u4e2d\uff0c\u5f53\u4ed6\u62ab\u8863\u6253\u5f00\u95e8\uff0e\u89c1\u662f\u949f\u4e00\u8c6a\uff0c\u4fbf\u77e5\u5fc5\u6709\u6025\u4e8b\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u8fdb\u5165\u623f\u4e2d\uff0c\u4e0d\u7b49\u5bf9\u65b9\u8be2\u95ee\uff0c\u4fbf\u628a\u65b9\u624d\u82d7\u7d20\u5170\u524d\u6765\u793a\u8b66\u7684\u4e8b\u8bf4\u4e86\u4e00\u904d\u3002 \u3000\u3000\u8fd9\u6d88\u606f\u8fde\u4f59\u4ea6\u4e50\u4e5f\u5927\u611f\u610f\u5916\uff0c\u5fd9\u7a7f\u597d\u8863\u670d\u9053\uff1a\u201c\u8d70\uff0c\u8d76\u7d27\u901a\u77e5\u9ea6\u8001\u5f1f\u53bb\uff01\u201d \u3000\u3000\u8c01\u77e5\u9ea6\u5c0f\u660e\u7684\u623f\u95e8\uff0c\u7adf\u4e45\u4e45\u65e0\u6cd5\u53eb\u5f00\u3002 \u3000\u3000\u539f\u56e0\u662f\u9ea6\u5c0f\u660e\u6628\u665a\u529b\u6218\u6c5f\u5357\u56db\u602a\u548c\u5cad\u5357\u4e8c\u5947\uff0c\u5df2\u7ecf\u5927\u611f\u75b2\u7d2f\uff0e\u867d\u7136\u4eba\u5728\u7761\u68a6\u4e2d\u88ab\u6572\u95e8\u58f0 \u60ca\u9192\uff0c\u5374\u61d2\u5f97\u8d77\u6765\u5f00\u95e8\uff0e \u3000\u3000\u949f\u4e00\u8c6a\u8fb9\u6572\u95e8\u8fb9\u53eb\u9053\uff1a\u201c\u9ea6\u8001\u5f1f\u5feb\u5feb\u5f00\u95e8\uff0c\u6211\u548c\u4f59\u5148\u751f\u6709\u8981\u7d27\u7684\u4e8b\u544a\u8bc9\u4f60\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u63c9\u4e86\u63c9\u4e24\u53ea\u60fa\u677e\u7684\u7738\u5b50\uff0c\u4f9d\u7136\u8eba\u5728\u5e8a\u4e0a\uff0c\u61d2\u6d0b\u6d0b\u7684\u9053\uff1a\u201c\u4eba\u5bb6\u6b63\u7761\u5230\u597d\u65f6\u5019\uff0c\u4f60 \u4eec\u53c8\u6765\u5435\u4eba\u3002\u6211\u5df2\u7ecf\u7b54\u5e94\u4f60\u4eec\u4e0d\u8d70\u4e86\uff0e\u96be\u9053\u8fd8\u4e0d\u653e\u5fc3\uff1f\u201d \u3000\u3000\u949f\u4e00\u8c6a\u6025\u9053\uff1a\u201c\u4f60\u5feb\u5f00\u95e8\uff0c\u6709\u8981\u7d27\u7684\u4e8b\u901a\u77e5\u4f60\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u6709\u4e8b\u5c31\u5728\u95e8\u5916\u8bb2\uff0e\u4e3a\u4ec0\u4e48\u2014\u5b9a\u8981\u5f00\u95e8\uff1f\u201d \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u518d\u4e0d\u5f00\u95e8\uff0c\u6211\u5c31\u8981\u628a\u95e8\u8e22\u5f00\u4e86\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u8fd9\u624d\u4e0b\u5e8a\u5f00\u4e86\u95e8\u9053\uff1a\u201c\u526f\u76df\u4e3b\uff0c\u6211\u4e00\u5411\u4f69\u670d\u4f60\u7684\u51b7\u9759\u6c89\u7740\uff0c\u4e3a\u4ec0\u4e48\u4eca\u5929\u504f\u504f\u4e3e\u52a8 \u53cd\u5e38\uff1f\u201d \u3000\u3000\u949f\u4e00\u8c6a\u8fdb\u5f97\u95e8\u6765\uff0c\u62cd\u7740\u9ea6\u5c0f\u660e\u7684\u80a9\u8180\u9053\uff1a\u201c\u9ea6\u8001\u5f1f\uff0c\u5feb\u8d70\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u4e0d\u89c9\u4e00\u6123\u9053\uff1a\u201c\u771f\u662f\u602a\u4e8b\uff01\u5148\u524d\u6211\u8981\u8d70\uff0c\u4f60\u4eec\u5927\u4f19\u5835\u4f4f\u623f\u95e8\u4e0d\u51c6\u6211\u8d70\u3002\u73b0\u5728\u6211\u5df2 \u7ecf\u51b3\u5b9a\u4e0d\u8d70\u4e86\uff0c\u4f60\u5374\u53c8\u6765\u53eb\u6211\u8d70\u3002\u662f\u4e0d\u662f\u4f60\u4eec\u8111\u7b4b\u6709\u95ee\u9898\uff1f\u201d \u3000\u3000\u949f\u4e00\u8c6a\u5927\u4e3a\u7740\u6025\u5730\u9053\uff1a\u201c\u73b0\u5728\u60c5\u52bf\u8f6c\u53d8\u4e86\uff0c\u6211\u548c\u4f59\u5148\u751f\u4e0d\u5f97\u4e0d\u6539\u53d8\u4e3b\u610f\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u7728\u4e86\u7728\u773c\u9053\uff1a\u201c\u4ec0\u4e48\u60c5\u52bf\u8f6c\u53d8\u4e86\uff1f\u83ab\u975e\u4f60\u4eec\u4e5f\u8981\u8d70\uff1f\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u62a2\u5148\u5c06\u65b9\u624d\u949f\u4e00\u8c6a\u5728\u56f4\u5899\u5916\u5f97\u5230\u82d7\u7d20\u5170\u7684\u6d88\u606f\u5927\u7565\u8bb2\u8ff0\u4e86\u4e00\u904d\u3002 \u3000\u3000\u5728\u949f\u4e00\u8c6a\u548c\u4f59\u4ea6\u4e50\u7684\u9884\u6599\uff0c\u9ea6\u5c0f\u660e\u542c\u5230\u8fd9\u6d88\u606f\uff0c\u4e00\u5b9a\u4f1a\u7acb\u523b\u6574\u7406\u597d\u884c\u56ca\u5c31\u8d70\uff0e\u5c82\u77e5\u9ea6\u5c0f \u660e\u53cd\u800c\u53c8\u8eba\u56de\u5e8a\u4e0a\uff0c\u50cf\u82e5\u65e0\u5176\u4e8b\u4e00\u822c\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u50ac\u4fc3\u9053\uff1a\u201c\u9ea6\u8001\u5f1f\uff0c\u4f60\u662f\u600e\u4e48\u5566\uff1f\u96be\u9053\u8981\u7b49\u6b7b\u4e0d\u6210\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u51b7\u7b11\u9053\uff1a\u201c\u6211\u82e5\u73b0\u5728\u8d70\u4e86\uff0c\u90a3\u5c31\u8868\u793a\u6015\u4e86\u90a3\u8001\u738b\u516b\u86cb\uff0c\u96be\u9053\u4f60\u4eec\u8ba4\u4e3a\u6211\u9ea6\u5c0f\u660e\u662f \u8d2a\u751f\u6015\u6b7b\u4e4b\u4eba\uff1f\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u76b1\u4e86\u76b1\u7709\u9053\uff1a\u201c\u9ea6\u8001\u5f1f\uff0c\u73b0\u5728\u662f\u4ec0\u4e48\u65f6\u5019\u4e86\uff0c\u4f60\u8fd8\u8981\u901e\u5f3a\uff1f\u7559\u5f97\u9752\u5c71\u5728\uff0c\u4f55\u6015\u6ca1 \u67f4\u70e7\uff1f\u5feb\u5feb\u9003\u547d\u8981\u7d27\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u5f53\u771f\u6c89\u4f4f\u4e86\u6c14\uff0c\u9053\uff1a\u201c\u6211\u5c31\u662f\u8981\u6b7b\uff0c\u4e5f\u5fc5\u987b\u6b7b\u5f97\u8f70\u8f70\u70c8\u70c8\u3002\u970d\u5143\u4f3d\u5728\u6740\u6211\u4e4b\u524d\uff0c \u53ea\u6015\u4e5f\u8981\u4ed8\u51fa\u76f8\u5f53\u7684\u4ee3\u4ef7\uff01\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u9053\uff1a\u201c\u4f60\u7eb5\u7136\u80fd\u6740\u6b7b\u4ed6\u4eec\u51e0\u4e2a\uff0c\u6700\u540e\u603b\u8fd8\u662f\u8d54\u4e0a\u81ea\u5df1\u4e00\u6761\u547d\uff0e\u503c\u5f97\u5417\uff1f\u201d \u3000\u3000\u201c\u4f60\u5c31\u8ba4\u4e3a\u6211\u2014\u5b9a\u975e\u6b7b\u4e0d\u53ef\uff1f\u201d \u3000\u3000\u201c\u970d\u5143\u4f3d\u624b\u4e0b\u9ad8\u624b\u5982\u4e91\uff0c\u4f60\u7eb5\u7136\u6b66\u529f\u518d\u9ad8\uff0c\u4e5f\u65e0\u6d4e\u4e8e\u4e8b\u3002\u8fde\u5f53\u5e74\u9738\u738b\u4e4b\u52c7\uff0c\u6700\u540e\u4e5f\u8981\u81ea \u520e\u4e4c\u6c5f\uff0c\u4f60\u8be5\u5fc3\u91cc\u6709\u6570\u3002\u201d \u3000\u3000\u201c\u6211\u6ca1\u6570\uff0c\u8fde\u2014\u4e8c\u4e09\u56db\u4e94\u90fd\u5f04\u4e0d\u6e05\u695a\uff01\u201d \u3000\u3000\u201c\u9ea6\u8001\u5f1f\uff0c\u8fd9\u662f\u6027\u547d\u6538\u5173\u7684\u5927\u4e8b\uff0c\u4f60\u600e\u4e48\u8fd8\u50cf\u5f00\u73a9\u7b11\u4e00\u822c\uff01\u201d \u3000\u3000\u201c\u6211\u5df2\u51b3\u5b9a\u4e0d\u8d70\u5c31\u4e0d\u8d70\uff0c\u82e5\u4e00\u4f1a\u513f\u5c31\u6539\u53d8\u4e00\u4e2a\u4e3b\u610f\uff0c\u90a3\u624d\u662f\u5f00\u73a9\u7b11\u5462\u3002\u4e24\u4f4d\u5feb\u8bf7\u56de\u53bb\uff0c \u518d\u529d\u6211\u4e5f\u6ca1\u6709\u7528\uff0c\u6211\u8fd8\u8981\u7ee7\u7eed\u7761\u89c9\u3002\u201d \u3000\u3000\u949f\u4e00\u8c6a\u543c\u9053\uff1a\u201c\u4f60\u518d\u4e0d\u8d70\u6211\u4eec\u62ac\u4e5f\u8981\u628a\u4f60\u62ac\u51fa\u53bb\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u6211\u4ece\u6ca1\u5750\u8fc7\u8f7f\uff0c\u4f60\u4eec\u82e5\u80fd\u627e\u9876\u8f7f\u5b50\u62ac\u6211\u8d70\uff0e\u6211\u6b63\u597d\u53ef\u4ee5\u8fc7\u8fc7\u763e\u3002\u201d \u3000\u3000\u949f\u4e00\u8c6a\u53c8\u6c14\u53c8\u6025\uff0e\u5374\u88ab\u4f59\u4ea6\u4e50\u4e00\u628a\u62c9\u51fa\u623f\u95e8\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u54b1\u4eec\u4e5f\u4e0d\u5fc5\u529d\u4e86\uff0c\u526f\u76df\u4e3b \u56de\u53bb\u5427\uff01\u201d \u3000\u3000\u949f\u2014\u8c6a\u88ab\u4f59\u4ea6\u4e50\u4e00\u53e3\u6c14\u62c9\u8fdb\u81ea\u5df1\u623f\u95f4\uff0c\u5750\u4e0b\u540e\u9053\uff1a\u201c\u4f59\u5144\u600e\u4e48\u5566\uff1f\u54b1\u4eec\u65e0\u8bba\u5982\u4f55\u8981\u628a\u4ed6 \u529d\u8d70\uff0e\u4f60\u600e\u4e48\u5374\u534a\u8def\u6253\u9000\u5802\u9f13\uff1f\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u9053\uff1a\u201c\u9ea6\u5c0f\u660e\u7684\u6027\u5b50\uff0c\u526f\u76df\u4e3b\u5e76\u975e\u4e0d\u77e5\u9053\uff0c\u6211\u628a\u4f60\u62c9\u56de\u6765\uff0c\u6b63\u662f\u8981\u53e6\u60f3\u529e\u6cd5\uff0e\u201d \u3000\u3000\u201c\u9664\u4e86\u903c\u4ed6\u8d70\u8fd8\u6709\u4ec0\u4e48\u529e\u6cd5\uff1f\u201d \u3000\u3000\u201c\u65b9\u624d\u82d7\u7d20\u5170\u4f20\u6765\u7684\u6d88\u606f\uff0c\u867d\u7136\u4e0d\u80fd\u8bf4\u4e0d\u53ef\u9760\uff0c\u4f46\u6211\u6000\u7591\u8fd9\u6b63\u662f\u970d\u5143\u4f3d\u4f7f\u51fa\u6765\u7684\u2014 \u8ba1\uff0e\u201d \u3000\u3000\u949f\u4e00\u8c6a\u54e6\u4e86\u58f0\uff0c\u4f3c\u6709\u6240\u609f\u9053\uff1a\u201c\u4f59\u5144\u6709\u4f55\u9ad8\u89c1\uff1f\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u5206\u6790\u9053\uff1a\u201c\u4f9d\u5144\u5f1f\u611a\u89c1\uff0c\u970d\u5143\u4f3d\u4e3a\u514d\u6fc0\u8d77\u67cf\u9f84\u9662\u7684\u4f17\u6012\uff0c\u5fc5\u4e0d\u6562\u6740\u9ea6\u5c0f\u660e\u3002\u201d \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u90a3\u4e48\u82d7\u7d20\u5170\u4e3a\u4ec0\u4e48\u4f1a\u4f20\u6765\u8fd9\u9879\u6d88\u606f\u5462\uff1f\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u9053\uff1a\u201c\u8fd9\u6b63\u662f\u970d\u5143\u4f3d\u7684\u4e00\u8ba1\u3002\u4ed6\u6545\u610f\u8981\u82d7\u7d20\u5170\u6765\u4f20\u9012\u8fd9\u4e2a\u6d88\u606f\uff0c\u4ee5\u4fbf\u8ba9\u9ea6\u5c0f\u660e\u6709 \u65f6\u95f4\u9003\u79bb\u8ff7\u8e2a\u8c37\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u4e00\u8d70\uff0c\u5bf9\u970d\u5143\u4f3d\u6765\u8bf4\uff0e\u65e2\u4e0d\u5f97\u7f6a\u54b1\u4eec\u67cf\u9f84\u9662\uff0c\u53c8\u53bb\u4e86\u2014\u4e2a\u5fc3\u8179\u5927\u60a3\u3002\u8fd9\u529e\u6cd5\u53c8\u9634 \u9669\u53c8\u9ad8\u660e\uff0c\u526f\u76df\u4e3b\u8ba4\u4e3a\u5144\u5f1f\u7684\u8bdd\u662f\u5426\u6709\u9053\u7406\uff1f\u201d \u3000\u3000\u949f\u4e00\u8c6a\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u4f59\u5144\u7684\u8bdd\uff0c\u679c\u7136\u6709\u7406\u3002\u4f46\u9ea6\u8001\u5f1f\u504f\u504f\u4e0d\u8d70\uff0c\u970d\u5143\u4f3d\u603b\u4e0d\u80fd\u5c31\u6b64\u7f62 \u4f11\uff0e\u4ed6\u8eab\u4e3a\u7eff\u6797\u76df\u4e3b\uff0c\u4e3a\u4e86\u7ef4\u62a4\u603b\u5be8\u7684\u5be8\u89c4\uff0c\u5fc5\u5b9a\u8fd8\u662f\u8981\u5904\u7f6e\u9ea6\u5c0f\u660e\uff0e\u5426\u5219\u5c82\u4e0d\u6709\u635f\u4ed6\u7684\u5a01 \u4e25\uff1f\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u9053\uff1a\u201c\u4ed6\u5373\u4f7f\u5904\u7f6e\u9ea6\u5c0f\u660e\uff0e\u4e5f\u5fc5\u5728\u65e9\u996d\u4e4b\u540e\u3002\u54b1\u4eec\u4e0d\u59a8\u5728\u5929\u4e00\u4eae\u5c31\u5148\u53bb\u89c1\u89c1\u4ed6\uff0c \u8bd5\u63a2\u8bd5\u63a2\u4ed6\u7684\u8bed\u6c14\uff0c\u518d\u505a\u4e0b\u4e00\u6b65\u7684\u6253\u7b97\u3002\u201d \u3000\u3000\u949f\u4e00\u8c6a\u9894\u9996\u9053\uff1a\u201c\u597d\uff0c\u5c31\u8fd9\u4e48\u529e\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u7684\u4f4f\u5904\uff0c\u79f0\u4e3a\u201c\u5929\u661f\u9662\u201d\u3002 \u3000\u3000\u5929\u661f\u9662\u5efa\u7b51\u5dcd\u5ce8\uff0c\u5360\u5730\u6781\u5e7f\u3002\u5f53\u5e74\u80e1\u67cf\u9f84\u4fee\u5efa\u5b8c\u6210\u540e\uff0c\u53d6\u540d\u201c\u8fce\u5bbe\u9986\u201d\uff0c\u539f\u662f\u63a5\u5f85\u5916\u754c \u8d35\u5bbe\u7528\u7684\uff0c\u8fde\u80e1\u67cf\u9f84\u90fd\u4e0d\u66fe\u642c\u8fdb\u53bb\u4f4f\u3002 \u3000\u3000\u4f46\u970d\u5143\u4f3d\u4e24\u6708\u524d\u63a5\u4efb\u76df\u4e3b\u540e\uff0c\u5374\u628a\u201c\u8fce\u5bbe\u9986\u201d\u6539\u4e3a\u201c\u5929\u661f\u9662\u201d\uff0e\u6210\u4e86\u4ed6\u8fd9\u7eff\u6797\u76df\u4e3b\u7684\u79c1 \u4eba\u5c45\u6240\u3002 \u3000\u3000\u4ed6\u628a\u8fd9\u91cc\u53d6\u540d\u201c\u5929\u661f\u9662\u201d\uff0c\u4e0d\u5916\u662f\u5728\u70ab\u8000\u81ea\u5df1\u7684\u8eab\u4efd\u5730\u4f4d\u3002 \u3000\u3000\u771f\u4e8f\u4ed6\u60f3\u5f97\u51fa\u6765\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u548c\u4f59\u4ea6\u4e50\u82e6\u5f85\u5230\u5929\u8272\u5fae\u660e\uff0c\u4fbf\u6025\u5306\u5306\u7684\u6765\u5230\u5929\u661f\u9662\u3002 \u3000\u3000\u8fd9\u65f6\u970d\u5143\u4f3d\u4e5f\u5df2\u8d77\u5e8a\uff0c\u7ecf\u8fc7\u5b88\u536b\u55bd\u5175\u901a\u62a5\u540e\uff0c\u5c31\u5728\u5bdd\u5ba4\u5916\u7684\u5ba2\u5385\u63a5\u89c1\u949f\u4e00\u8c6a\u548c\u4f59\u4ea6\u4e50\u3002 \u3000\u3000\u970d\u5143\u4f3d\u795e\u6001\u81ea\u82e5\uff0e\u4e1d\u6beb\u770b\u4e0d\u51fa\u5373\u5c06\u6709\u4ec0\u4e48\u5927\u4e8b\u53d1\u751f\u3002 \u3000\u3000\u201c\u4e24\u4f4d\u8fd9\u4e48\u65e9\u6765\u89c1\u8001\u592b\uff0e\u5fc5\u662f\u6709\u4ec0\u4e48\u91cd\u8981\u5927\u4e8b\uff1f\u201d \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u6628\u665a\u4e3a\u9ea6\u5c0f\u660e\u7684\u4e8b\uff0c\u5c5e\u4e0b\u4eec\u4e00\u76f4\u5750\u7acb\u96be\u5b89\uff0c\u5f88\u60f3\u77e5\u9053\u76df\u4e3b\u7a76\u7adf\u8981\u600e\u6837\u5904\u7f6e \u4ed6\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u795e\u8272\u4f9d\u7136\u9053\uff1a\u201c\u56fd\u6709\u56fd\u6cd5\uff0c\u5e2e\u6709\u5e2e\u89c4\uff0c\u9ea6\u5c0f\u660e\u6628\u665a\u7684\u6240\u4f5c\u6240\u4e3a\uff0e\u4e8c\u4f4d\u90fd\u5df2\u4eb2\u773c\u5f97 \u89c1\u3002\u5982\u679c\u949f\u526f\u76df\u4e3b\u662f\u8001\u592b\uff0c\u8bd5\u95ee\u8be5\u5982\u4f55\u5904\u7f6e\uff1f\u201d \u3000\u3000\u949f\u4e00\u8c6a\u60f3\u4e0d\u5230\u970d\u5143\u4f3d\u7adf\u53cd\u95ee\u81ea\u5df1\uff0c\u987f\u4e86\u2014\u987f\u9053\uff1a\u201c\u9ea6\u5c0f\u660e\u56fa\u7136\u51cc\u72af\u4e86\u76df\u4e3b\uff0c\u4f46\u5ff5\u5728\u4ed6\u662f \u539f\u6765\u524d\u76df\u4e3b\u7684\u5e08\u5f1f\uff0c\u800c\u4e14\u53c8\u662f\u4e2a\u4eba\u624d\uff0c\u5c5e\u4e0b\u4eec\u603b\u5e0c\u671b\u76df\u4e3b\u80fd\u4ece\u8f7b\u53d1\u843d\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u62c2\u9aef\u4e00\u7b11\u9053\uff1a\u201c\u5c31\u56e0\u4e3a\u4ed6\u662f\u80e1\u524d\u76df\u4e3b\u7684\u4eba\uff0c\u6240\u4ee5\u624d\u4e0d\u628a\u8001\u592b\u653e\u5728\u773c\u91cc\u3002\u201d \u3000\u3000\u949f\u4e00\u8c6a\u5fc3\u5934\u2014\u9707\u9053\uff1a\u201c\u770b\u6765\u76df\u4e3b\u975e\u60e9\u5904\u4ed6\u4e0d\u53ef\u4e86\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u4ed6\u4ee5\u4e0b\u72af\u4e0a\uff0c\u5f53\u4f17\u901e\u51f6\uff0e\u8bba\u5f8b\u5f53\u6b7b\uff0c\u8001\u592b\u82e5\u4e0d\u6309\u5f8b\u5904\u7f6e\uff0c\u4f55\u4ee5\u670d\u4f17\uff1f\u5982\u679c \u8ff7\u8e2a\u8c37\u5185\u4eba\u4eba\u90fd\u4e0e\u4ed6\u2014\u6837\uff0e\u8fd8\u6709\u4f55\u4f53\u7edf\u53ef\u8a00\uff1f\u949f\u526f\u76df\u4e3b\u548c\u4f59\u5148\u751f\u600e\u53ef\u4e3a\u8fd9\u79cd\u4eba\u8bb2\u60c5\uff1f\u201d \u3000\u3000\u949f\u4e00\u8c6a\u6b63\u60f3\u5f00\u53e3\uff0e\u5374\u89c1\u4f59\u4ea6\u4e50\u5728\u65c1\u76f4\u4f7f\u773c\u8272\uff0c\u53ea\u597d\u8d77\u8eab\u62f1\u624b\u9053\uff1a\u201c\u65e2\u7136\u76df\u4e3b\u575a\u6301\u4e0d\u80af\u9976 \u6055\u9ea6\u5c0f\u660e\uff0c\u5c5e\u4e0b\u4eec\u591a\u8bb2\u65e0\u76ca\uff0c\u544a\u8f9e\u4e86\uff01\u201d \u3000\u3000\u8f9e\u51fa\u5929\u661f\u9662\uff0c\u949f\u4e00\u8c6a\u4e00\u9897\u5fc3\u518d\u4e5f\u9759\u4e0d\u4e0b\u6765\uff0c\u8bed\u6c14\u6fc0\u52a8\u5730\u9053\uff1a\u201c\u970d\u5143\u4f3d\u679c\u7136\u8981\u8d81\u8fd9\u673a\u4f1a\u60e9 \u5904\u9ea6\u5c0f\u660e\uff0e\u4f59\u5144\u4e3a\u4ec0\u4e48\u4e0d\u51c6\u6211\u518d\u8bb2\u4e0b\u53bb\uff1f\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u9053\uff1a\u201c\u4ed6\u5206\u660e\u793a\u610f\u54b1\u4eec\u8981\u903c\u4f7f\u9ea6\u5c0f\u660e\u8d70\uff0c\u8fd9\u6837\u4e5f\u5c31\u7ed9\u4ed6\u7559\u4e86\u9762\u5b50\u3002\u73b0\u5728\u65f6\u95f4\u7d27 \u8feb\uff0c\u82e5\u54b1\u4eec\u4e0d\u8d76\u5feb\u56de\u67cf\u9f84\u9662\u901a\u77e5\u9ea6\u5c0f\u660e\uff0c\u53ea\u6015\u5c31\u6765\u4e0d\u53ca\u4e86\uff1f\u201d \u3000\u3000\u949f\u4e00\u8c6a\u7d27\u8e59\u53cc\u7709\uff0e\u5401\u53e3\u6c14\u9053\uff1a\u201c\u53ef\u662f\u9ea6\u5c0f\u660e\u82e5\u4ecd\u4e0d\u80af\u8d70\u5462\uff1f\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u9053\uff1a\u201c\u8fd9\u6b21\u7531\u4e0d\u5f97\u4ed6\u4e86\uff0c\u54b1\u4eec\u5c31\u662f\u62ac\u4e5f\u8981\u628a\u4ed6\u62ac\u8d70\u3002\u201d \u3000\u3000\u201c\u5c0f\u660e\u4e00\u8d70\uff0e\u54b1\u4eec\u67cf\u9f84\u9662\u4ece\u6b64\u5c31\u5931\u53bb\u4e00\u6839\u6709\u529b\u652f\u67f1\uff01\u201d \u3000\u3000\u201c\u73b0\u5728\u5c0f\u660e\u9003\u547d\u8981\u7d27\uff0e\u4ee5\u540e\u7684\u4e8b\uff0c\u6162\u6162\u518d\u60f3\u529e\u6cd5\u3002\u201d \u3000\u3000\u4e24\u4eba\u5306\u5306\u56de\u5230\u67cf\u9f84\u9662\uff0c\u76f4\u5954\u9ea6\u5c0f\u660e\u623f\u95f4\u3002 \u3000\u3000\u95e8\u662f\u865a\u63a9\u7740\u7684\uff0c\u63a8\u95e8\u8fdb\u53bb\uff0c\u53ea\u89c1\u88ab\u8925\u51cc\u4e71\uff0c\u9ea6\u5c0f\u660e\u5374\u5df2\u5931\u53bb\u8e2a\u5f71\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u548c\u4f59\u4ea6\u4e50\u90fd\u4e0d\u89c9\u4e00\u559c\uff0c\u5728\u4ed6\u4eec\u7684\u9884\u6599\uff0c\u60f3\u628a\u9ea6\u5c0f\u660e\u529d\u8d70\uff0c\u5b9e\u5728\u4e0d\u662f\u4ef6\u6613\u4e8b\uff0c\u60f3\u4e0d \u5230\u9ea6\u5c0f\u660e\u7adf\u81ea\u52a8\u8d70\u4e86\u3002 \u3000\u3000\u4e24\u4eba\u4ecd\u4e0d\u653e\u5fc3\uff0c\u4ee5\u4e3a\u9ea6\u5c0f\u660e\u4e5f\u8bb8\u662f\u53bb\u4e86\u8305\u623f\uff0c\u4fbf\u5728\u623f\u5185\u5750\u7740\u7b49\u5019\uff0c\u76f4\u5230\u53d1\u73b0\u9ea6\u5c0f\u660e\u7684\u968f \u8eab\u5175\u5203\u4e5f\u4e0d\u89c1\u4e86\uff0c\u624d\u786e\u5b9a\u4ed6\u679c\u7136\u8d70\u4e86\uff0e \u3000\u3000\u949f\u4e00\u8c6a\u7b11\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u5929\u4e0d\u6015\u5730\u4e0d\u6015\u7684\u9ea6\u5c0f\u660e\uff0c\u4e5f\u6709\u5bb3\u6015\u7684\u4e00\u5929\u3002\u201d \u3000\u3000\u4e0d\u4e45\u4e4b\u540e\uff0c\u7fa4\u96c4\u4e5f\u5df2\u7eb7\u7eb7\u8d77\u5e8a\uff0c \u3000\u3000\u5f53\u4ed6\u4eec\u5f97\u77e5\u970d\u5143\u4f3d\u679c\u771f\u8981\u6740\u9ea6\u5c0f\u660e\uff0c\u800c\u9ea6\u5c0f\u660e\u5df2\u7ecf\u9003\u8d70\uff0c\u867d\u7136\u4e3a\u4ed6\u4fdd\u4f4f\u4e00\u547d\u800c\u5e86\u5e78\uff0c\u5374 \u53c8\u4e3a\u4ed6\u7684\u79bb\u53bb\u611f\u5230\u65e0\u6bd4\u6005\u60d8\u3002 \u3000\u3000\u65e9\u996d\u521a\u8fc7\uff0c\u970d\u5143\u4f3d\u4fbf\u6d3e\u4eba\u524d\u6765\u901a\u77e5\u67cf\u9f84\u9662\u7684\u4eba\u5230\u805a\u4e49\u5385\u96c6\u5408\u3002 \u3000\u3000\u4f59\u4ea6\u4e50\u9053\uff1a\u201c\u5927\u5bb6\u8fd9\u5c31\u53bb\u5427\uff0c\u8fd9\u662f\u970d\u76df\u4e3b\u8981\u5f53\u4f17\u5bf9\u9ea6\u8001\u5f1f\u5904\u7f6a\u7684\u96c6\u4f1a\uff0c\u54b1\u4eec\u5fc5\u987b\u5168\u4f53\u53c2 \u52a0\uff0c\u5426\u5219\u5fc5\u5c06\u5f15\u8d77\u970d\u76df\u4e3b\u7684\u7591\u5fc3\u3002\u201d \u3000\u3000\u738b\u5927\u5eb7\u9a82\u9053\uff1a\u201c\u4ed6\u5976\u5976\u7684\uff0c\u4eba\u90fd\u8d70\u4e86\uff0c\u4ed6\u8fd8\u5904\u7f6e\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u9053\uff1a\u201c\u8fd9\u662f\u4e00\u4e2a\u5f62\u5f0f\uff0c\u5426\u5219\u970d\u76df\u4e3b\u5982\u4f55\u4e0b\u5f97\u4e86\u53f0\uff0c\u53c8\u5982\u4f55\u5411\u4ed6\u7684\u5c5e\u4e0b\u4ea4\u4ee3\uff1f\u201d \u3000\u3000\u738b\u5927\u5eb7\u9053\uff1a\u201c\u54b1\u4eec\u8fd8\u8981\u4e0d\u8981\u5e26\u5bb6\u4f19\u53bb\uff1f\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u9053\uff1a\u201c\u5982\u679c\u5c0f\u660e\u6ca1\u8d70\uff0c\u54b1\u4eec\u5f53\u7136\u8981\u643a\u5e26\u5175\u5203\uff0c\u5fc5\u8981\u65f6\u5c31\u706b\u62fc\u4e00\u573a\uff0e\u73b0\u5728\u5c0f\u660e\u5df2\u7ecf \u8d70\u4e86\uff0c\u54b1\u4eec\u53ea\u662f\u53bb\u770b\u4e2a\u5f62\u5f0f\uff0c\u54ea\u8fd8\u7528\u5f97\u7740\u518d\u5e26\u5175\u5203\uff1f\u201d \u3000\u3000\u738b\u5927\u5eb7\u9053\uff1a\u201c\u597d\u5566\uff01\u54b1\u4eec\u73b0\u5728\u5c31\u8d70\uff0c\u770b\u770b\u90a3\u8001\u5c0f\u5b50\u600e\u6837\u8868\u6f14\u3002\u201d \u3000\u3000\u4e8e\u662f\u7fa4\u8c6a\u79bb\u5f00\u67cf\u9f84\u9662\uff0c\u5f80\u805a\u4e49\u5385\u800c\u6765\u3002 \u3000\u3000\u81f3\u4e8e\u82d7\u7d20\u82d3\uff0c\u672c\u5c31\u4e0d\u4fbf\u53c2\u52a0\uff0c\u518d\u52a0\u4e0a\u65e9\u996d\u65f6\u5e76\u672a\u51fa\u623f\uff0c\u7fa4\u8c6a\u4eec\u56e0\u5fc3\u91cc\u6709\u4e8b\uff0c\u4e5f\u6ca1\u4eba\u53bb\u53eb \u5979\u3002 \u3000\u3000\u5f53\u7fa4\u8c6a\u5230\u8fbe\u805a\u4e49\u5385\uff0c\u970d\u5143\u4f3d\u7684\u5fc3\u8179\u624b\u4e0b\u4eec\u5df2\u7ecf\u5148\u5230\uff0c\u4f46\u4eba\u6570\u5e76\u4e0d\u591a\uff0c\u53ea\u662f\u5cad\u5357\u4e8c\u5947\u3001\u6c5f \u5357\u56db\u602a\u548c\u53e6\u5916\u51e0\u540d\u5934\u76ee\uff0c\u52a0\u8d77\u6765\u4e5f\u4e0d\u8fc7\u5341\u4f59\u4eba\u3002 \u3000\u3000\u4f46\u970d\u5143\u4f3d\u5374\u5c1a\u672a\u5230\u3002 \u3000\u3000\u805a\u4e49\u5385\u4eca\u5929\u663e\u5f97\u7279\u522b\u58ee\u4e25\u8083\u7a46\uff0c\u6b63\u4e2d\u5899\u58c1\u4e0a\u9ad8\u60ac\u7740\u4e00\u526f\u6b66\u5723\u5173\u516c\u50cf\uff0c\u5de6\u624b\u6367\u7740\u6625\u79cb\uff0c\u53f3 \u624b\u62c2\u7740\u7f8e\u9aef\uff0c\u5173\u5e73\u5468\u4ed3\u5206\u5f85\u4e24\u65c1\uff0c\u795e\u5723\u5a01\u6b66\u81f3\u6781\u3002 \u3000\u3000\u539f\u6765\uff0c\u7eff\u6797\u4eba\u7269\u4f9b\u5949\u7684\u4e5f\u662f\u6b66\u5723\u5173\u516c\uff0c\u82e5\u7531\u5f53\u5e74\u7684\u7eff\u6797\u76df\u4e3b\u67cf\u9f84\u4f9b\u5949\u8fd8\u8bf4\u5f97\u8fc7\u53bb\uff0c\u50cf\u970d \u5143\u4f3d\u8fd9\u79cd\u4eba\u7269\u4e5f\u4f9b\u5949\u5173\u516c\uff0e\u82e5\u5173\u8001\u7237\u5728\u5929\u6709\u7075\uff0c\u51c6\u4f1a\u6c14\u4e2a\u534a\u6b7b\u3002 \u3000\u3000\u8bf4\u4e0d\u5b9a\u5f85\u4f1a\u513f\u4f1a\u7528\u9752\u9f99\u5043\u6708\u5200\u5148\u628a\u970d\u5143\u4f3d\u7684\u8111\u888b\u5272\u4e0b\u3002 \u3000\u3000\u5728\u6b66\u5723\u5173\u516c\u50cf\u7684\u4e24\u65c1\uff0c\u8fd8\u7279\u522b\u659c\u5782\u7740\u4e24\u9053\u7ea2\u8272\u9526\u7f0e\u5e03\u5e54\uff0c\u628a\u6b66\u5723\u50cf\u886c\u6258\u5f97\u8d8a\u53d1\u5a01\u6b66\u5e84 \u4e25\u3002 \u3000\u3000\u6b66\u5723\u50cf\u524d\u65b9\u662f\u4e00\u5f20\u6761\u51e0\u5f62\u7684\u4f9b\u6848\uff0c\u4f9b\u6848\u4e0a\u9648\u5217\u7740\u732a\u5934\u4e09\u7272\u548c\u9c9c\u82b1\u7d20\u679c\uff0e\u4e24\u679d\u8721\u70db\u9ad8\u70e7\uff0c \u9999\u7089\u5185\u9999\u70df\u7f2d\u7ed5\u3002 \u3000\u3000\u5728\u4f9b\u6848\u4e00\u4fa7\uff0e\u6446\u4e86\u4e09\u4e2a\u5ea7\u4f4d\uff0c\u4e0d\u7528\u8bf4\u5176\u4e2d\u4e24\u4e2a\u5ea7\u4f4d\u662f\u76df\u4e3b\u526f\u76df\u4e3b\u5750\u7684\uff0c\u53e6\u4e00\u5ea7\u4f4d\u5219\u4e0d\u77e5 \u4e3a\u4f55\u4eba\u6240\u8bbe\u3002 \u3000\u3000\u970d\u5143\u4f3d\u603b\u7b97\u77e5\u793c\uff0c\u5ea7\u4f4d\u53ea\u80fd\u6446\u5728\u4f9b\u6848\u2014\u4fa7\uff0c\u90a3\u662f\u56db\u4e3a\u4e0a\u6709\u5173\u516c\uff0c\u4ed6\u662f\u51e1\u4eba\uff0c\u867d\u8d35\u4e3a\u7eff\u6797 \u76df\u4e3b\uff0c\u4e5f\u5fc5\u987b\u9760\u8fb9\u7ad9\u3002 \u3000\u3000\u5373\u4f7f\u7687\u5e1d\u8001\u513f\u5230\u4e86\uff0e\u7167\u6837\u4e5f\u4e0d\u6562\u5bf9\u6b66\u5723\u51cc\u72af\u3002 \u3000\u3000\u949f\u2014\u8c6a\u56e0\u970d\u5143\u4f3d\u672a\u5230\uff0e\u6682\u65f6\u4e5f\u53ea\u80fd\u548c\u67cf\u9f84\u9662\u65b9\u9762\u7684\u4eba\u7ad9\u5728\u4e00\u8fb9\u3002 \u3000\u3000\u738b\u5927\u5eb7\u5728\u4eba\u4ece\u4e2d\u4f4e\u58f0\u9a82\u9053\uff1a\u201c\u4ffa\u5165\u4ed6\u5976\u5976\u7684\uff01\u82b1\u90a3\u4e48\u591a\u7684\u94f6\u5b50\u5e03\u7f6e\u597d\u8fd9\u5ea7\u4f9b\u6848\uff0c\u6709\u4e2a\u9e1f \u7528\uff1f\u8fd8\u4e0d\u662f\u4e3a\u4e86\u9762\u5b50\u7a7a\u8868\u6f14\u4e00\u573a\uff01\u201d \u3000\u3000\u5ffd\u542c\u5c4f\u98ce\u540e\u4e00\u4eba\u9ad8\u9053\uff1a\u201c\u5168\u4f53\u8083\u7acb\uff01\u76df\u4e3b\u3001\u592b\u4eba\u9a7e\u5230\uff01\u201d \u3000\u3000\u4e00\u9635\u811a\u6b65\u58f0\u54cd\uff0c\u7531\u5c4f\u98ce\u540e\u8d70\u51fa\u970d\u5143\u4f3d\u548c\u82d7\u7d20\u5170\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u7b49\u4eba\u5230\u8fd9\u65f6\u624d\u660e\u767d\u53e6\u5916\u2014\u4e2a\u4f4d\u7f6e\u662f\u4e3a\u538b\u5be8\u592b\u4eba\u51c6\u5907\u7684\u3002 \u3000\u3000\u4e24\u4eba\u624b\u7275\u624b\u4e00\u540c\u6b65\u5165\u5927\u5385\u3002 \u3000\u3000\u82d7\u7d20\u5170\u4e00\u53cc\u773c\u5374\u5077\u5077\u5411\u949f\u4e00\u8c6a\u7b49\u4eba\u671b\u53bb\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u548c\u4f59\u4ea6\u4e50\u4e92\u770b\u2014\u8ddf\uff0c\u5f7c\u6b64\u5fc3\u4e2d\u6709\u6570\u3002 \u3000\u3000\u970d\u5143\u4f3d\u8fb9\u8d70\u8fb9\u5411\u4e24\u65c1\u7fa4\u96c4\u4e0d\u4f4f\u542b\u7b11\u70b9\u5934\u81f4\u610f\uff0c\u5e76\u62ac\u8d77\u4e00\u53cc\u624b\u6765\u4e0d\u4f4f\u6325\u52a8\uff0c\u8fd9\u7ed9\u540e\u4ee3\u7684\u5927 \u4eba\u7269\u7559\u4e0b\u4e00\u4e2a\u5178\u8303\u3002 \u3000\u3000\u4ed6\u633d\u7740\u82d7\u7d20\u5170\u5728\u5ea7\u4f4d\u5750\u4e0b\u540e\uff0c\u7acb\u5373\u5411\u949f\u4e00\u8c6a\u62db\u624b\u9053\uff1a\u201c\u949f\u526f\u76df\u4e3b\u8bf7\u8fc7\u6765\u5c31\u5ea7\uff01\u201d \u3000\u3000\u949f\u4e00\u8c6a\u4e00\u58f0\u4e0d\u542d\uff0c\u8d70\u8fc7\u53bb\u5750\u597d\u3002 \u3000\u3000\u970d\u5143\u4f3d\u8fd9\u624d\u53cc\u76ee\u7cbe\u5149\u6e5b\u6e5b\uff0c\u91cd\u91cd\u7684\u54b3\u4e86\u4e24\u58f0\u9053\uff1a\u201c\u770b\u5927\u5385\u7684\u5e03\u7f6e\uff0c\u5404\u4f4d\u5fc5\u5df2\u7ecf\u77e5\u9053\u662f\u600e \u4e48\u56de\u4e8b\u4e86\uff1f\u201d \u3000\u3000\u56db\u4e0b\u5e76\u65e0\u4eba\u56de\u5e94\u3002 \u3000\u3000\u970d\u5143\u4f3d\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u672c\u603b\u5be8\u7684\u9ea6\u5c0f\u660e\uff0c\u6628\u665a\u53db\u9006\u72af\u4e0a\uff0c\u72af\u4e0b\u4e86\u6309\u5f8b\u5904\u6b7b\u4e4b\u7f6a\uff0c\u4eca\u5929\u672c\u76df \u4e3b\u5f00\u9999\u5802\u8bbe\u4e0b\u795e\u6848\uff0c\u51b3\u5b9a\u6309\u603b\u5be8\u5be8\u89c4\u5c06\u9ea6\u5c0f\u660e\u5f53\u4f17\u5904\u51b3\uff0c\u4ee5\u6b63\u6cd5\u7edf\u800c\u656c\u6548\u5c24\uff01\u201d \u3000\u3000\u5728\u573a\u4e4b\u4eba\uff0c\u67cf\u9f84\u9662\u65b9\u9762\u7684\u4eba\uff0c\u65e9\u5df2\u77e5\u9053\u662f\u600e\u4e48\u56de\u4e8b\uff0e\u56e0\u9ea6\u5c0f\u660e\u5df2\u7ecf\u9003\u8d70\uff0c\u662f\u4ee5\u8c01\u90fd\u4e0d\u89c1 \u7d27\u5f20\u3002 \u3000\u3000\u4f46\u8868\u9762\u5374\u4ecd\u5fc5\u987b\u88c5\u51fa\u51db\u7136\u5fc3\u60ca\u6a21\u6837\uff0c\u5982\u6b64\u624d\u4e0d\u81f4\u9732\u51fa\u7834\u7efd\u3002 \u3000\u3000\u81f3\u4e8e\u970d\u5143\u4f3d\u65b9\u9762\u7684\u4eba\uff0e\u5cad\u5357\u4e8c\u5947\u548c\u6c5f\u5357\u56db\u602a\u53ef\u80fd\u4e5f\u5df2\u77e5\u60c5\uff0e\u56e0\u4e4b\uff0e\u53ea\u662f\u2014\u526f\u65e0\u52a8\u4e8e\u8877\u6a21 \u6837\u3002 \u3000\u3000\u5176\u4f59\u7684\u5219\u96be\u514d\u60ca\u5f02\u4e4b\u72b6\uff0c\u5f62\u4e4b\u4e8e\u8272\u3002 \u3000\u3000\u5ffd\u542c\u738b\u5927\u5eb7\u9ad8\u53eb\u9053\uff1a\u201c\u76df\u4e3b\uff0c\u9ea6\u5c0f\u660e\u7a76\u7adf\u72af\u4e86\u4ec0\u4e48\u5927\u9519\uff0e\u8bf7\u4f60\u6700\u597d\u8bb2\u660e\u767d\uff0c\u514d\u5f97\u8ba9\u4ed6\u6b7b \u5f97\u4e0d\u660e\u4e0d\u767d\uff0c\u4ed6\u4eec\u8fd9\u4e9b\u53c2\u89c2\u7684\u4eba\u4e5f\u770b\u5f97\u4e0d\u660e\u4e0d\u767d\uff01\u201d \u3000\u3000\u738b\u5927\u5eb7\u7c97\u4e2d\u6709\u7ec6\uff0c\u4ed6\u8bf4\u8fd9\u8bdd\u5206\u660e\u662f\u6545\u610f\u4f5c\u620f\u3002 \u3000\u3000\u970d\u5143\u4f3d\u51b7\u51b7\u4e00\u7b11\u9053\uff1a\u201c\u738b\u8001\u5f1f\u4e0d\u5fc5\u7740\u6025\uff0c\u8001\u592b\u5f53\u7136\u4f1a\u8ba9\u4ed6\u6b7b\u5f97\u660e\u660e\u767d\u767d\uff0c\u4e5f\u8981\u8ba9\u5728\u573a\u6240 \u6709\u7684\u4eba\u90fd\u660e\u767d\u4ed6\u72af\u7684\u662f\u4ec0\u4e48\u7f6a\u540d\u3002\u5426\u5219\u8001\u592b\u5c82\u4e0d\u843d\u4e2a\u65e0\u8f9c\u6740\u4eba\u4e4b\u540d\uff01\u201d \u3000\u3000\u4ed6\u8bf4\u7740\u56de\u5934\u559d\u9053\uff1a\u201c\u5ba3\u5e03\u7f6a\u72b6\u3002\u201d \u3000\u3000\u4f46\u89c1\u5c4f\u98ce\u540e\u5e94\u58f0\u8d70\u51fa\u4e00\u4e2a\u7350\u5934\u9f20\u76ee\u3001\u5c16\u5634\u7f29\u816e\u3001\u9894\u4e0b\u7559\u6709\u4e00\u64ae\u5c71\u7f8a\u80e1\u5b50\u7684\u8001\u8005\uff0c\u624b\u91cc\u6367 \u7740\u4e00\u5f20\u5199\u5f97\u5bc6\u5bc6\u9ebb\u9ebb\u5b57\u8ff9\u7684\u5b57\u6761\uff0c\u6765\u5230\u5927\u5385\u4e2d\u592e\u3002 \u3000\u3000\u970d\u5143\u4f3d\u5429\u5490\u9053\uff1a\u201c\u5ba3\u8bfb\u7f6a\u72b6\uff01\u201d \u3000\u3000\u90a3\u7350\u5934\u9f20\u76ee\u8001\u8005\u7acb\u5373\u7528\u8bfb\u796d\u6587\u7684\u58f0\u8c03\uff0c\u6447\u5934\u6643\u8111\u7684\u5f00\u59cb\u7167\u672c\u5ba3\u79d1\u3002 \u3000\u3000\u970d\u5143\u4f3d\u7684\u51c6\u5907\u5de5\u4f5c\u5b9e\u5728\u4e0b\u4e86\u4e00\u756a\u5de5\u592b\uff0c\u5c45\u7136\u628a\u9ea6\u5c0f\u660e\u5217\u4e3e\u4e86\u5341\u5927\u7f6a\u72b6\uff0c\u51e0\u4e4e\u72b6\u72b6\u90fd\u7f6a\u4e0d \u53ef\u8d66\uff0c\u6b7b\u6709\u4f59\u8f9c\u3002 \u3000\u3000\u7350\u5934\u9f20\u76ee\u8001\u8005\u8bfb\u5b8c\u4e4b\u540e\uff0c\u521a\u8981\u5411\u970d\u5143\u4f3d\u884c\u793c\u79bb\u53bb\uff0c\u5374\u542c\u738b\u5927\u5eb7\u9ad8\u558a\u9053\uff1a\u201c\u6162\u8d70\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u8138\u8272\u4e00\u53d8\u9053\uff1a\u201c\u738b\u8001\u5f1f\u53c8\u6709\u4ec0\u4e48\u8bdd\u8bb2\uff1f\u201d \u3000\u3000\u201c\u8bf7\u95ee\u76df\u4e3b\uff0c\u8fd9\u4f4d\u8bfb\u72b6\u8001\u5934\u662f\u4ece\u54ea\u91cc\u627e\u6765\u7684\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u8fd9\u4f4d\u4e01\u5e08\u7237\u6ee1\u809a\u5b50\u5b66\u95ee\uff0e\u8fde\u796d\u6587\u90fd\u8bfb\u8fc7\uff0c\u8fd8\u5ff5\u8fc7\u56db\u4e66\u4e94\u7ecf\uff0e\u4f60\u53ef\u662f\u8ba4\u4e3a\u4ed6 \u65b9\u624d\u8bfb\u5f97\u4e0d\u6e05\u695a\uff1f\u201d \u3000\u3000\u201c\u4ffa\u738b\u5927\u5eb7\u5c31\u662f\u653e\u51e0\u4e2a\u5c41\uff0e\u4e5f\u6bd4\u4ed6\u8bf4\u8bdd\u6e05\u695a\u3002\u201d \u3000\u3000\u4e01\u5e08\u7237\u88ab\u5f04\u5f97\u54ed\u7b11\u4e0d\u5f97\uff0c\u770b\u4e86\u738b\u5927\u5eb7\u90a3\u822c\u51f6\u795e\u6076\u715e\u6a21\u6837\uff0c\u6839\u672c\u4e0d\u6562\u518d\u56de\u5634\u3002 \u3000\u3000\u970d\u5143\u4f3d\u6325\u4e86\u6325\u624b\u9053\uff1a\u201c\u4e01\u5e08\u7237\u8bf7\u56de\u53bb\uff0c\u6ca1\u4f60\u7684\u4e8b\u4e86\u3002\u201d \u3000\u3000\u4e01\u5e08\u7237\u53ef\u80fd\u6c14\u5f97\u4e0d\u8f7b\uff0e\u4e34\u8d70\u65f6\u7adf\u53f9\u6c14\u58f0\u8fde\u8fde\u3002 \u3000\u3000\u949f\u2014\u8c6a\u7b49\u4eba\u66f4\u662f\u89c9\u5f97\u597d\u7b11\u3002 \u3000\u3000\u5374\u6ca1\u4eba\u6562\u7b11\u51fa\u58f0\u97f3\u6765\u3002 \u3000\u3000\u5ffd\u542c\u641c\u9b42\u624b\u5df4\u5929\u4e49\u9053\uff1a\u201c\u7980\u76df\u4e3b\uff0c\u9601\u4e0b\u6709\u4e2a\u4e0d\u60c5\u4e4b\u6c42\uff0c\u4e0d\u77e5\u8be5\u4e0d\u8be5\u8bb2\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u6709\u8bdd\u5c31\u8bb2\uff0c\u4f55\u5fc5\u7f57\u7f57\u55e6\u55e6\uff01\u201d \u3000\u3000\u5df4\u5929\u4e49\u4f3c\u662f\u7a0d\u5fae\u72b9\u8c6b\u4e86\u2014\u4e0b\uff0c\u624d\u9053\uff1a\u201c\u9ea6\u5c0f\u660e\u867d\u7136\u7f6a\u65e0\u53ef\u8d66\uff0c\u4f46\u5ff5\u5728\u4ed6\u603b\u662f\u80e1\uff0c\u8c37\u4e24\u4f4d \u5df2\u6545\u76df\u4e3b\u6700\u4e3a\u7231\u62a4\u7684\u65e7\u5c5e\uff0c\u6240\u4ee5\u2026\u2026\u201d \u3000\u3000\u970d\u5143\u4f3d\u8f7b\u54b3\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u4f60\u6562\u60c5\u8981\u66ff\u4ed6\u8bb2\u60c5\uff1f\u201d \u3000\u3000\u5df4\u5929\u4e49\u9053\uff1a\u201c\u5c5e\u4e0b\u4e0d\u6562\uff01\u5c5e\u4e0b\u53ea\u6c42\u4f60\u8001\u4eba\u5bb6\u80fd\u5728\u4ed6\u6b7b\u540e\u4e3a\u4ed6\u8d85\u5ea6\u8d85\u5ea6\u4ea1\u9b42\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u6de1\u7b11\u9053\uff1a\u201c\u8fd8\u7528\u4f60\u63d0\u9192\uff0e\u8001\u592b\u505a\u4e8b\uff0c\u4e00\u5411\u516c\u79c1\u5206\u660e\u3002\u5c31\u50cf\u8bf8\u845b\u4eae\u6325\u6cea\u65a9\u9a6c\u8c21\u2014 \u6837\uff0c\u516c\u662f\u516c\uff0c\u79c1\u662f\u79c1\u3002\u5904\u6b7b\u9ea6\u5c0f\u660e\uff0c\u8fd9\u662f\u516c\u4e8b\uff1b\u5728\u79c1\u7684\u65b9\u9762\uff0c\u8001\u592b\u7167\u6837\u4e5f\u5fc3\u91cc\u96be\u8fc7\u3002\u5c24\u5176\u8001 \u592b\u4e0d\u80fd\u4e0d\u60f3\u5230\u4ed6\u662f\u80e1\uff0e\u8c37\u4e24\u4f4d\u5df2\u6545\u76df\u4e3b\u7684\u7231\u5c06\u3002\u201d \u3000\u3000\u201c\u8fd9\u6837\u8bf4\u76df\u4e3b\u662f\u540c\u610f\u5c06\u4ed6\u539a\u846c\uff0c\u518d\u6253\u91ae\u8d85\u5ea6\u4ea1\u9b42\u4e86\uff1f\u201d \u3000\u3000\u201c\u5c82\u6b62\u6253\u91ae\u8d85\u5ea6\u8d85\u5ea6\u4ed6\u7684\u4ea1\u9b42\uff0c\u8001\u592b\u5728\u628a\u4ed6\u5904\u6b7b\u4e4b\u540e\uff0c\u9a6c\u4e0a\u5c31\u6709\u8868\u793a\u3002\u201d \u3000\u3000\u201c\u76df\u4e3b\u8981\u600e\u6837\u8868\u793a\uff1f\u201d \u3000\u3000\u201c\u90a3\u5c31\u4e0d\u59a8\u5f53\u573a\u7ed9\u5927\u5bb6\u770b\u770b\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u8fb9\u8bf4\u8fb9\u7acb\u5373\u8f6c\u5934\u53eb\u9053\uff1a\u201c\u4e01\u5e08\u7237\u628a\u4ed6\u4eec\u4e94\u4e2a\u5e26\u8fc7\u6765\uff01\u201d \u3000\u3000\u5f88\u5feb\u7684\uff0e\u7350\u5934\u9f20\u76ee\u3001\u5c16\u5634\u7f29\u816e\u7684\u4e01\u5e08\u7237\u4fbf\u7531\u5c4f\u98ce\u540e\u5e26\u7740\u4e94\u4e2a\u4eba\u9c7c\u8d2f\u800c\u51fa\u3002 \u3000\u3000\u8fd9\u4e94\u4e2a\u4eba\u7684\u7a7f\u6234\u5728\u5927\u5385\u51fa\u73b0\uff0c\u4f7f\u5f97\u6240\u6709\u7684\u4eba\u90fd\u6709\u4e9b\u76ee\u77aa\u53e3\u5446\u3002 \u3000\u3000\u4ed6\u4eec\u90fd\u7a7f\u7740\u4e00\u5f0f\u7684\u5b5d\u670d\u5b5d\u5e3d\uff0c\u624b\u62c9\u54ed\u4e27\u68d2\uff0c\u4e00\u4e2a\u4e2a\u5782\u5934\u4e27\u6c14\u50cf\u6b7b\u4e86\u4e09\u5929\u6ca1\u57cb\u7684\u6837\u5b50\u3002 \u3000\u3000\u738b\u5927\u5eb7\u9996\u5148\u7981\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u8fd9\u4e94\u540d\u540a\u6b7b\u9b3c\u662f\u505a\u4ec0\u4e48\u7684\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u62c2\u9aef\u2014\u7b11\u9053\uff1a\u201c\u4ed6\u4eec\u662f\u5145\u5f53\u5b5d\u5b50\u54ed\u5893\u7684\uff0c\u5f85\u4f1a\u513f\u5c31\u7531\u4ed6\u4eec\u4f34\u9001\u7740\u9ea6\u5c0f\u660e\u7684\u5c38\u4f53\u5230 \u5893\u5730\u3002\u8fd9\u8fd8\u6709\u4e2a\u660e\u5802\u53eb\u505a\u2018\u4e94\u5b50\u54ed\u5893\u2019\u3002\u201d \u3000\u3000\u738b\u5927\u5eb7\u543c\u9053\uff1a\u201c\u7b80\u76f4\u5c82\u6709\u6b64\u7406\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u8138\u8272\u4e00\u6c89\u9053\uff1a\u201c\u4f60\u53ef\u662f\u89c9\u5f97\u6709\u4ec0\u4e48\u4e0d\u59a5\uff1f\u201d \u3000\u3000\u738b\u5927\u5eb7\u51b7\u7b11\u9053\uff1a\u201c\u5f53\u7136\u4e0d\u59a5\u3002\u9ea6\u5c0f\u5144\u5f1f\u8fde\u8001\u5a46\u90fd\u6ca1\u8ba8\uff0c\u54ea\u91cc\u6765\u7684\u4e94\u4e2a\u513f\u5b50\uff1f\u800c\u4e14\u8fd9\u4e94\u540d \u540a\u6b7b\u9b3c\uff0c\u81f3\u5c11\u5e74\u7eaa\u4e5f\u4e09\u5341\u5f00\u5916\uff0c\u9ea6\u5c0f\u5144\u5f1f\u5c31\u662f\u6709\u513f\u5b50\uff0c\u4e5f\u4e0d\u53ef\u80fd\u751f\u51fa\u6bd4\u81ea\u5df1\u5e74\u7eaa\u8fd8\u5927\u7684\u513f \u5b50\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u88ab\u6fc0\u5f97\u6709\u4e9b\u8bed\u585e\uff0c\u7ea2\u7740\u8138\u987f\u4e86\u4e00\u987f\u9053\uff1a\u201c\u54b1\u4eec\u4e2d\u56fd\u6709\u53e5\u8bdd\u8bf4\u2018\u4eba\u6b7b\u4e3a\u5927\u2019\u3002\u8001\u592b \u4e3a\u4ed6\u5b89\u6392\u4e0b\u8fd9\u4e94\u4e2a\u513f\u5b50\uff0c\u4e5f\u7b97\u5bf9\u4ed6\u5c3d\u4e86\u4e00\u756a\u5fc3\u610f\u3002\u201d \u3000\u3000\u738b\u5927\u5eb7\u72b9\u81ea\u4e0d\u80af\u7f62\u4f11\u9053\uff1a\u201c\u5f85\u4f1a\u513f\u4ed6\u4eec\u662f\u600e\u6837\u7684\u54ed\u6cd5\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u4f60\u653e\u5fc3\uff0e\u4ed6\u4eec\u4e00\u5b9a\u54ed\u5f97\u6bd4\u4eb2\u513f\u5b50\u66f4\u4e3a\u4f24\u5fc3\u3002\u201d \u3000\u3000\u738b\u5927\u5eb7\u9053\uff1a\u201c\u53ef\u4e0d\u53ef\u4ee5\u73b0\u5728\u5c31\u8868\u6f14\u4e00\u4e0b\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u5f53\u7136\u53ef\u4ee5\uff0c\u4f60\u4eec\u4e94\u4e2a\u9a6c\u4e0a\u54ed\u7ed9\u5927\u5bb6\u542c\u542c\u3002\u201d \u3000\u3000\u987f\u65f6\uff0c\u90a3\u4e94\u4e2a\u8eab\u7a7f\u5b5d\u670d\u5b5d\u5e3d\u7684\u4eba\uff0c\u53cc\u819d\u5f80\u5730\u4e0a\u2014\u8dea\uff0e\u69cc\u80f8\u987f\u8db3\uff0c\u54ed\u58f0\u5927\u4f5c\u3002 \u3000\u3000\u4ed6\u4eec\u54ed\u58f0\u4e4b\u5927\uff0c\u51e0\u4e4e\u8981\u6380\u5f00\u5927\u5385\u5c4b\u9876\uff0c\u800c\u4e14\u6709\u7684\u50cf\u6740\u9e21\u6740\u9e2d\uff0c\u6709\u7684\u50cf\u5bb0\u732a\u5bb0\u725b\uff0c\u5947\u8154\u602a \u8c03\u767e\u51fa\u3002 \u3000\u3000\u597d\u5728\u90a3\u65f6\u6ca1\u6709\u6269\u97f3\u5668\uff0c\u5426\u5219\u4e0d\u58f0\u95fb\u767e\u91cc\u624d\u602a\u3002 \u3000\u3000\u7fa4\u96c4\u4eec\u6709\u7684\u53d7\u4e0d\u4e86\u8fd9\u79cd\u5e72\u6270\uff0c\u90fd\u4e0d\u7981\u63a9\u8d77\u4e86\u8033\u6735\u3002 \u3000\u3000\u5ffd\u542c\u738b\u5927\u5eb7\u4e00\u58f0\u5927\u559d\u9053\uff1a\u201c\u5976\u5976\u7684\uff0c\u522b\u54ed\u5566\uff01\u201d \u3000\u3000\u738b\u5927\u5eb7\u7684\u543c\u58f0\u6bd4\u4e94\u5b50\u54ed\u5893\u8fd8\u5927\uff0c\u6709\u5982\u7126\u96f7\u9aa4\u53d1\uff0c\u4e94\u4eba\u7acb\u5373\u505c\u6b62\u54ed\u58f0\u3002 \u3000\u3000\u738b\u5927\u5eb7\u63a5\u7740\u53c8\u543c\u9053\uff1a\u201c\u4f60\u4eec\u4e94\u4e2a\u72d7\u5a18\u517b\u7684\uff0c\u76df\u4e3b\u8fd8\u6ca1\u6b7b\uff0c\u5c31\u54ed\u5f97\u8fd9\u4e48\u51f6\u3002\u82e5\u76df\u4e3b\u771f\u6b7b \u4e86\uff0c\u90a3\u8fd8\u5f97\u4e86\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u8138\u8272\u2014\u53d8\u9053\uff1a\u201c\u5927\u80c6\uff01\u4f60\u6562\u5492\u9a82\u8001\u592b\u3002\u201d \u3000\u3000\u738b\u5927\u5eb7\u9053\uff1a\u201c\u5c5e\u4e0b\u600e\u6562\u5492\u9a82\u76df\u4e3b\uff0c\u76df\u4e3b\u8fd9\u8bdd\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u8bed\u542b\u6740\u673a\u9053\uff1a\u201c\u8001\u592b\u5012\u8981\u95ee\u95ee\u4f60\u65b9\u624d\u7684\u8bdd\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u738b\u5927\u5eb7\u9053\uff1a\u201c\u65b9\u624d\u90a3\u4e94\u4e2a\u72d7\u5a18\u517b\u7684\uff0c\u9762\u5411\u76df\u4e3b\u8dea\u5728\u5730\u4e0a\u5927\u54ed\u5927\u53eb\uff0c\u8c01\u770b\u4e86\u4e5f\u4f1a\u8ba4\u4e3a\u4ed6\u662f \u54ed\u4f60\u8001\u4eba\u5bb6\u7684\u3002\u5c5e\u4e0b\u56e0\u4e3a\u770b\u4e0d\u8fc7\u53bb\uff0c\u624d\u7981\u6b62\u4ed6\u4eec\u518d\u80e1\u95f9\uff0c\u8fd9\u5b8c\u5168\u662f\u4e00\u756a\u597d\u610f\u3002\u76df\u4e3b\u600e\u4e48\u53cd\u800c \u602a\u8d77\u5c5e\u4e0b\u6765\u4e86\uff1f\u7b80\u76f4\u662f\u597d\u5fc3\u4e0d\u5f97\u597d\u62a5\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u660e\u77e5\u738b\u5927\u5eb7\u662f\u5728\u5f3a\u8bcd\u593a\u7406\uff0c\u5374\u4e5f\u53ea\u6709\u9ed8\u961f\uff0c\u671b\u7740\u4e01\u5e08\u7237\u6325\u4e86\u6325\u624b\u9053\uff1a\u201c\u628a\u4ed6\u4eec\u5e26 \u4e0b\u53bb\uff0c\u5f85\u4f1a\u513f\u9ea6\u5c0f\u660e\u6b63\u6cd5\u4e86\u518d\u6765\uff01\u201d \u3000\u3000\u738b\u5927\u5eb7\u72b9\u81ea\u4e0d\u80af\u89c1\u597d\u5c31\u6536\uff0e\u54fc\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u76df\u4e3b\uff0c\u4f60\u8001\u4eba\u5bb6\u4e3a\u9ea6\u8001\u5f1f\u7684\u540e\u4e8b\u51c6\u5907\u5f97\u8fd9\u6837 \u5468\u5230\uff0c\u4ffa\u67cf\u9f84\u9662\u7684\u4eba\u90fd\u5f88\u611f\u6fc0\u4f60\u3002\u53ea\u662f\u8bf4\u4e86\u534a\u5929\uff0c\u9ea6\u5c0f\u660e\u8fd8\u6ca1\u89c1\u4eba\u5f71\u5462\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u4e0d\u52a8\u58f0\u8272\u9053\uff1a\u201c\u738b\u8001\u5f1f\u53ef\u662f\u5e0c\u671b\u4ed6\u65e9\u4e00\u70b9\u6b7b\uff1f\u201d \u3000\u3000\u738b\u5927\u5eb7\u9053\uff1a\u201c\u53cd\u6b63\u4ed6\u72af\u7684\u662f\u6b7b\u7f6a\uff0c\u60f3\u6d3b\u4e5f\u6d3b\u4e0d\u4e86\u5566\u3002\u90a3\u5c31\u4e0d\u5982\u65e9\u4e00\u70b9\u513f\u8ba9\u4ed6\u5f52\u5929\uff0c\u4eba\u6b7b \u5165\u571f\u4e3a\u5b89\uff0c\u8fd9\u662f\u8c01\u90fd\u77e5\u9053\u7684\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u8fd9\u624d\u4fa7\u8138\u9053\uff1a\u201c\u949f\u526f\u76df\u4e3b\uff0c\u9ea6\u5c0f\u660e\u7684\u4eba\u5462\uff1f\u201d \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u5c5e\u4e0b\u65b9\u624d\u6765\u805a\u4e49\u5385\u65f6\uff0c\u4ed6\u8fd8\u7761\u5728\u623f\u95f4\u91cc\u6ca1\u8d77\u6765\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u4e0d\u628a\u4ed6\u62bc\u8fc7\u6765\uff1f\u201d \u3000\u3000\u201c\u5c5e\u4e0b\u53ea\u5949\u547d\u5230\u805a\u4e49\u5385\u6765\uff0c\u800c\u4e14\u5e76\u4e0d\u6e05\u695a\u5230\u805a\u4e49\u5385\u505a\u4ec0\u4e48\uff0c\u5e76\u672b\u5949\u547d\u62bc\u9ea6\u5c0f\u660e\u6765\u3002\u201d \u3000\u3000\u201c\u9ea6\u5c0f\u660e\u8bba\u5f8b\u5f53\u6b7b\uff0c\u4f60\u548c\u4f59\u5148\u751f\u90fd\u5df2\u4e8b\u5148\u77e5\u9053\uff0c\u4e0d\u8be5\u60f3\u4e0d\u5230\u628a\u4ed6\u62bc\u6765\u7684\u4e8b\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u5c5e\u4e0b\u4eec\u4e0d\u6e05\u695a\u76df\u4e3b\u8981\u4ec0\u4e48\u65f6\u5019\u5904\u7f6e\u4ed6\uff0e\u4ed6\u7684\u6b66\u529f\u751a\u9ad8\uff0c\u82e5\u4e8b\u5148\u6253\u8349\u60ca\u86c7\uff0c\u5c82\u4e0d\u66f4 \u4e3a\u4e0d\u5999\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u70b9\u4e86\u70b9\u5934\u9053\uff1a\u201c\u526f\u76df\u4e3b\u8bf4\u7684\u4e5f\u662f\uff0c\u90a3\u5c31\u8bf7\u4f60\u73b0\u5728\u56de\u67cf\u9f84\u9662\u628a\u4ed6\u62bc\u6765\u3002\u201d \u3000\u3000\u949f\u4e00\u8c6a\u6697\u9053\uff1a\u201c\u5c0f\u660e\u867d\u7136\u5df2\u7ecf\u9003\u8d70\u4e86\uff0c\u4f46\u6211\u56de\u67cf\u9f84\u9662\u865a\u6643\u2014\u4e0b\u518d\u5411\u4ed6\u56de\u62a5\uff0c\u4ed6\u53ef\u80fd\u54ac\u5b9a \u662f\u6211\u6545\u610f\u628a\u5c0f\u660e\u653e\u8d70\u7684\uff0c\u5c82\u4e0d\u7ed9\u4ed6\u7559\u4e0b\u628a\u67c4\u3002\u970d\u5143\u4f3d\u53cd\u590d\u65e0\u5e38\uff0c\u6211\u5c82\u80fd\u4e0a\u4ed6\u7684\u5f53\u2026\u2026\u201d \u3000\u3000\u60f3\u5230\u8fd9\u91cc\uff0e\u6447\u4e86\u6447\u5934\u9053\uff1a\u201c\u76df\u4e3b\u53ea\u6d3e\u5c5e\u4e0b\u4e00\u4eba\u53bb\u62bc\u4ed6\uff0c\u53ea\u6015\u4e0d\u59a5\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u5c82\u80fd\u4e0d\u77e5\u949f\u4e00\u8c6a\u7684\u7528\u610f\uff0e\u6545\u610f\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u6709\u4f55\u4e0d\u59a5\uff1f\u201d \u3000\u3000\u949f\u2014\u8c6a\u9053\uff1a\u201c\u9ea6\u5c0f\u660e\u7684\u6b66\u529f\u5728\u5c5e\u4e0b\u4e4b\u4e0a\uff0c\u5c5e\u4e0b\u4e00\u4eba\u600e\u80fd\u628a\u4ed6\u62bc\u6765\uff1f\u201d \u3000\u3000\u201c\u4f60\u662f\u526f\u76df\u4e3b\uff0e\u800c\u4e14\u636e\u8001\u592b\u6240\u77e5\uff0c\u4ed6\u4e00\u5411\u6700\u542c\u4f60\u7684\u8bdd\u3002\u201d \u3000\u3000\u201c\u76df\u4e3b\u8fd9\u8bdd\u672a\u514d\u8fd1\u4e8e\u53ef\u7b11\uff0c\u5e73\u5e38\u4ed6\u81ea\u7136\u80af\u542c\u5c5e\u4e0b\u7684\u8bdd\uff0c\u4f46\u73b0\u5728\u662f\u751f\u6b7b\u5173\u5934\uff0c\u53c8\u6709\u8c01\u80af\u675f \u624b\u5c31\u7f1a\uff1f\u201d \u3000\u3000\u201c\u65e2\u7136\u5982\u6b64\uff0c\u4f60\u5c31\u628a\u67cf\u9f84\u9662\u7684\u9ad8\u624b\uff0c\u591a\u5e26\u51e0\u4f4d\u56de\u53bb\uff0e\u201d \u3000\u3000\u201c\u4e5f\u4e0d\u59a5\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u67cf\u9f84\u9662\u7684\u4eba\uff0c\u90fd\u548c\u9ea6\u5c0f\u660e\u6709\u4ea4\u60c5\uff0c\u8c01\u4e5f\u4e0d\u4f1a\u62fc\u6b7b\u62fc\u6d3b\u53bb\u5bf9\u4ed8\u4ed6\u3002\u8fd9\u6837\u4e00\u6765\uff0c\u9ea6\u5c0f\u660e\u5c31 \u5f88\u53ef\u80fd\u8d70\u8131\u3002\u201d \u3000\u3000\u201c\u90a3\u4e48\u526f\u76df\u4e3b\u7684\u610f\u601d\u5462\uff1f\u201d \u3000\u3000\u201c\u6700\u597d\u7531\u5cad\u5357\u7684\u5df4\u5144\u548c\u5b8b\u5144\u53bb\u6267\u884c\u8fd9\u6b21\u4efb\u52a1\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u526f\u76df\u4e3b\u4e5f\u8981\u524d\u53bb\u624d\u6210\u3002\u201d \u3000\u3000\u201c\u5c5e\u4e0b\u5f53\u7136\u8981\u966a\u540c\u4ed6\u4eec\u4e24\u4f4d\uff0c\u5fc5\u8981\u65f6\u4e5f\u53ef\u534f\u52a9\u4ed6\u4eec\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u7565\u4e00\u72b9\u8c6b\u9053\uff1a\u201c\u4e5f\u597d\uff0c\u5c31\u7531\u5df4\u3001\u5b8b\u4e24\u4f4d\u8001\u5f1f\u968f\u540c\u526f\u76df\u4e3b\u5230\u67cf\u9f84\u9662\u4e00\u8d9f\uff0c\u6709\u4f60\u4eec\u4e09 \u4f4d\uff0e\u9ea6\u5c0f\u660e\u6b66\u529f\u518d\u9ad8\uff0e\u4e5f\u5fc5\u5b9a\u4f1a\u88ab\u4e56\u4e56\u62bc\u6765\u3002\u201d \u3000\u3000\u949f\u4e00\u8c6a\u3001\u5df4\u5929\u4e49\uff0c\u5b8b\u5929\u94ce\u8d70\u540e\uff0c\u67cf\u9f84\u9662\u65b9\u9762\u7684\u4eba\u90fd\u5728\u6697\u7b11\uff0c\u770b\u770b\u970d\u5143\u4f3d\u8fd9\u573a\u620f\u7a76\u7adf\u5982\u4f55 \u518d\u6f14\u4e0b\u53bb\uff0c\u53c8\u5982\u4f55\u7ed3\u5c40\u3002 \u3000\u3000\u5176\u5b9e\uff0c\u5df4\u5929\u4e49\u548c\u970d\u5143\u4f3d\u7684\u90a3\u756a\u5bf9\u8bdd\uff0c\u4e5f\u662f\u4e8b\u5148\u5546\u8bae\u597d\u7684\uff0c\u4e0d\u7136\u4e0d\u4f1a\u90a3\u6837\u903c\u771f\uff0c\u76ee\u7684\u53ea\u662f \u505a\u7ed9\u4e0d\u77e5\u60c5\u7684\u4eba\u770b\uff0c\u4ee5\u662d\u663e\u970d\u5143\u4f3d\u7684\u5927\u516c\u65e0\u79c1\uff0c\u6069\u5a01\u5e76\u6d4e\u3002 \u3000\u3000\u4e0d\u7ba1\u5982\u4f55\uff0c\u970d\u5143\u4f3d\u4e5f\u7b97\u715e\u8d39\u4e86\u4e00\u756a\u82e6\u5fc3\uff0e \u3000\u3000\u4e0d\u4f46\u5e03\u7f6e\u8d77\u9999\u5802\uff0c\u53c8\u51c6\u5907\u4e86\u7f6a\u72b6\u548c\u4e94\u5b50\u54ed\u5893\uff0c\u540c\u6837\u7684\uff0c\u4e5f\u8868\u793a\u9ea6\u5c0f\u660e\u4e0d\u662f\u4e2a\u7b80\u5355\u7684\u4eba \u7269\u3002 \u3000\u3000\u738b\u5927\u5eb7\u4e00\u5411\u4e0d\u7518\u5bc2\u5bde\uff0c\u642d\u8baa\u7740\u95ee\u9053\uff1a\u201c\u76df\u4e3b\uff0c\u4e07\u4e00\u62bc\u4e0d\u56de\u9ea6\u5c0f\u660e\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u54fc\u4e86\u58f0\u9053\uff1a\u201c\u8001\u592b\u4e0d\u4fe1\u8fd9\u4e09\u4f4d\u7edd\u9876\u9ad8\u624b\uff0c\u4f1a\u902e\u4e0d\u4f4f\u4ed6\uff01\u201d \u3000\u3000\u738b\u5927\u5eb7\u9053\uff1a\u201c\u4f60\u82e5\u65e9\u65e9\u4e0b\u4ee4\uff0c\u4ffa\u4eec\u67cf\u9f84\u9662\u7684\u4eba\uff0c\u5728\u4ed6\u7761\u68a6\u4e2d\u628a\u4ed6\u902e\u4f4f\uff0e\u4e0d\u8d39\u5439\u7070\u4e4b\u529b\uff0e \u4f55\u5fc5\u518d\u8d39\u8fd9\u4e48\u5927\u7684\u624b\u811a\uff1f\u65b9\u624d\u8bfb\u72b6\u548c\u4e94\u5b50\u54ed\u5893\u7684\u90a3\u4e00\u573a\uff0c\u4e5f\u80fd\u8ba9\u4ed6\u4eb2\u773c\u770b\u5230\uff0e\u90a3\u8be5\u591a\u597d\u3002\u201d \u3000\u3000\u201c\u4f60\u8bf4\u6709\u4ec0\u4e48\u597d\uff1f\u201d \u3000\u3000\u201c\u4ed6\u80fd\u5728\u6b7b\u524d\u770b\u5230\u4e27\u4e8b\u529e\u5f97\u8fd9\u4e48\u9686\u91cd\uff0c\u6b7b\u4e86\u4e5f\u53ef\u542b\u7b11\u4e5d\u6cc9\uff0c\u5f53\u7136\u597d\u5566\u3002\u201d \u3000\u3000\u5927\u7ea6\u76cf\u8336\u5de5\u592b\u4e4b\u540e\uff0e\u949f\u4e00\u8c6a\u3001\u5df4\u5929\u4e49\uff0e\u5b8b\u5929\u94ce\u90fd\u6025\u6025\u5954\u4e86\u56de\u6765\u3002 \u3000\u3000\u4e09\u4eba\u7684\u8138\u8272\uff0c\u51dd\u91cd\u4e2d\u53c8\u5e26\u7740\u7d27\u5f20\uff0e \u3000\u3000\u8fd9\u53c8\u662f\u5728\u6f14\u620f\u3002 \u3000\u3000\u970d\u5143\u4f3d\u5f53\u7136\u5fc3\u91cc\u6e05\u695a\uff0c\u56e0\u4e3a\u8fd9\u4ef6\u4e8b\u4e00\u76f4\u662f\u7531\u4ed6\u62c5\u4efb\u5bfc\u6f14\u3002 \u3000\u3000\u970d\u5143\u4f3d\u6545\u610f\u4e00\u6123\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u6ca1\u628a\u4eba\u62bc\u56de\u6765\uff1f\u201d \u3000\u3000\u5df4\u5929\u4e49\u4e5f\u6545\u610f\u614c\u614c\u5f20\u5f20\u5730\u9053\uff1a\u201c\u7980\u76df\u4e3b\uff0c\u90a3\u5c0f\u5b50\u4e0d\u89c1\u4e86\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u554a\u4e86\u58f0\u9053\uff1a\u201c\u5c82\u6709\u6b64\u7406\uff0c\u4ed4\u7ec6\u641c\u8fc7\u4e86\u6ca1\u6709\uff1f\u201d \u3000\u3000\u5df4\u5929\u4e49\u9053\uff1a\u201c\u67cf\u9f84\u9662\u4e0d\u8fc7\u5341\u51e0\u4e8c\u5341\u95f4\u623f\u95f4\uff0c\u5c5e\u4e0b\u548c\u949f\u526f\u76df\u4e3b\u90fd\u4ed4\u7ec6\u641c\u8fc7\u4e86\u3002\u201d \u3000\u3000\u201c\u4ed6\u662f\u5426\u85cf\u5728\u4ec0\u4e48\u5730\u65b9\uff1f\u201d \u3000\u3000\u201c\u4ed6\u5c31\u662f\u85cf\u8d77\u6765\uff0c\u4e5f\u4e0d\u53ef\u80fd\u8eb2\u85cf\u5728\u67cf\u9f84\u9662\u3002\u81f3\u4e8e\u6574\u4e2a\u8ff7\u8e2a\u8c37\uff0c\u8303\u56f4\u8fd9\u4e48\u5927\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c \u53c8\u5230\u54ea\u91cc\u641c\u67e5\uff1f\u201d \u3000\u3000\u201c\u5404\u5904\u5173\u5361\u6709\u6ca1\u6709\u56de\u62a5\u4ed6\u4ece\u54ea\u91cc\u9003\u8d70\u7684\uff1f\u201d \u3000\u3000\u201c\u5c5e\u4e0b\u5e76\u672a\u63a5\u5230\u4efb\u4f55\u6d88\u606f\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u731b\u5730\u4e00\u8dfa\u811a\u9053\uff1a\u201c\u9a6c\u4e0a\u4f20\u4ee4\u4e0b\u53bb\uff0c\u5404\u5904\u5173\u5361\u4e00\u5f8b\u4e25\u7981\u6709\u4eba\u51fa\u8c37\uff0c\u518d\u53ec\u96c6\u603b\u5be8\u6240\u6709 \u9ad8\u624b\uff0c\u5c55\u5f00\u641c\u67e5\uff01\u201d \u3000\u3000\u53ea\u542c\u82d7\u7d20\u5170\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u76df\u4e3b\uff0e\u4f9d\u59be\u8eab\u611a\u89c1\uff0c\u8fd8\u662f\u4e0d\u5fc5\u52b3\u5e08\u52a8\u4f17\u7684\u597d\uff0e\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u592b\u4eba\u6709\u4ec0\u4e48\u9ad8\u89c1\uff1f\u201d \u3000\u3000\u82d7\u7d20\u5170\u9053\uff1a\u201c\u4ee5\u9ea6\u5c0f\u660e\u7684\u6b66\u529f\uff0c\u5404\u5904\u5173\u5361\u5982\u4f55\u62e6\u963b\u5f97\u4e86\uff1f\u4ed6\u65e2\u7136\u8981\u8d70\uff0e\u5fc5\u5b9a\u662f\u8fdc\u8d70\u9ad8 \u98de\uff0c\u600e\u53ef\u80fd\u8fd8\u7559\u5728\u8ff7\u8e2a\u8c37\u91cc\u3002\u73b0\u5728\u5c55\u5f00\u884c\u52a8\uff0c\u5c82\u4e0d\u662f\u767d\u5fd9\u4e00\u573a\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u53c8\u4e00\u8dfa\u811a\u9053\uff1a\u201c\u65e9\u77e5\u5982\u6b64\uff0c\u8001\u592b\u6628\u665a\u5c31\u8be5\u628a\u4ed6\u64d2\u4f4f\uff0c\u4ed6\u8fd9\u4e00\u8d70\uff0c\u5f04\u5f97\u8001\u592b\u7b49\u4e8e\u767d \u5fd9\u4e86\u4e00\u573a\u3002\u201d \u3000\u3000\u4ed6\u5634\u91cc\u867d\u4e0d\u5f97\u4e0d\u5982\u6b64\u8bf4\uff0c\u5185\u5fc3\u5374\u611f\u5230\u5341\u5206\u6b23\u6170\u3002\u56e0\u4e3a\u8fd9\u573a\u620f\u603b\u7b97\u5706\u6ee1\u6f14\u5b8c\uff0c\u67cf\u9f84\u9662\u53bb\u4e86 \u9ea6\u5c0f\u660e\uff0c\u7b49\u4e8e\u4e3a\u4ed6\u6e05\u9664\u4e86\u5fc3\u8179\u5927\u60a3\uff0e \u3000\u3000\u82d7\u7d20\u5170\u9053\uff1a\u201c\u65e2\u7136\u9ea6\u5c0f\u660e\u5df2\u7ecf\u9003\u8d70\uff0c\u76df\u4e3b\u5c3d\u53ef\u6162\u6162\u518d\u8bbe\u6cd5\u628a\u4ed6\u902e\u56de\u6765\u3002\u5927\u5385\u91cc\u8fd9\u573a\u9762\u4e5f \u8be5\u64a4\u6536\u4e86\u5427\uff0c\u514d\u5f97\u628a\u4f17\u4f4d\u5934\u9886\u90fd\u8017\u5728\u8fd9\u91cc\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u521a\u8981\u5429\u5490\u6563\u573a\uff0c\u7a81\u542c\u5927\u5385\u5916\u6709\u4eba\u6717\u58f0\u9053\uff1a\u201c\u970d\u5143\u4f3d\uff0c\u4f60\u4e0d\u662f\u8981\u627e\u5728\u4e0b\u5417\uff1f\u5728\u4e0b\u5c31 \u5728\u8fd9\u91cc\uff01\u201d \u3000\u3000\u8fd9\u58f0\u97f3\u8d6b\u7136\u6b63\u662f\u9ea6\u5c0f\u660e\u3002 \u3000\u3000\u5728\u8fd9\u5239\u90a3\uff0e\u6240\u6709\u7684\u4eba\u51e0\u4e4e\u90fd\u4e0d\u6562\u76f8\u4fe1\u81ea\u5df1\u7684\u8033\u6735\uff0c\u9ea6\u5c0f\u660e\u600e\u4f1a\u81ea\u6295\u7f57\u7f51\uff0c\u4e3b\u52a8\u9001\u4e0a\u95e8 \u6765\uff1f \u3000\u3000\u970d\u5143\u4f3d\u95fb\u8a00\u8138\u8272\u4e00\u53d8\uff0c\u6025\u6025\u51b2\u5230\u5927\u5385\u95e8\u5916\uff0c\u3000\u4e00\u9762\u5927\u559d\u9053\uff1a\u201c\u5927\u5385\u5185\u7684\u4eba\u90fd\u51fa\u6765\uff0c\u628a\u8fd9 \u5c0f\u5b50\u7ed9\u8001\u592b\u56e2\u56e2\u56f4\u4f4f\uff01\u201d \u3000\u3000\u7fa4\u96c4\u95fb\u8a00\u8702\u6d8c\u800c\u51fa\u3002 \u3000\u3000\u5927\u5385\u5916\u7684\u7a7a\u5730\u4e0a\uff0c\u795e\u6001\u81ea\u82e5\u3001\u6e0a\u505c\u5cb3\u5cd9\u7684\u7ad9\u7acb\u7740\u4e00\u540d\u5c11\u5e74\uff0c\u679c\u7136\u6b63\u662f\u9ea6\u5c0f\u660e\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u624b\u6a2a\u957f\u5251\uff0c\u6602\u7136\u800c\u7acb\uff0c\u4e00\u526f\u795e\u5a01\u51db\u51db\u6a21\u6837\u3002 \u3000\u3000\u970d\u5143\u4f3d\u53c8\u51fa\u58f0\u559d\u9053\uff1a\u201c\u8fd8\u4e0d\u5feb\u5feb\u628a\u4ed6\u56f4\u4f4f\uff0c\u8fd9\u6b21\u7edd\u5bf9\u4e0d\u80fd\u8ba9\u4ed6\u8dd1\u6389\uff01\u201d \u3000\u3000\u949f\u2014\u8c6a\u5728\u60c5\u6025\u4e4b\u4e0b\uff0c\u8fc5\u901f\u5730\u5411\u67cf\u9f84\u9662\u7fa4\u8c6a\u6697\u65bd\u4e00\u4e2a\u773c\u8272\uff0c\u7136\u540e\u95ea\u7535\u822c\u63a0\u5230\u9ea6\u5c0f\u660e\u8eab\u540e \u53bb\u3002 \u3000\u3000\u63a5\u7740\u67cf\u9f84\u9662\u6240\u6709\u7684\u4eba\uff0c\u4e5f\u90fd\u5305\u56f4\u5728\u9ea6\u5c0f\u660e\u7684\u540e\u4fa7\u3002 \u3000\u3000\u5982\u6b64\u4e00\u6765\uff0c\u7b49\u4e8e\u4e3a\u9ea6\u5c0f\u660e\u89e3\u9664\u4e86\u540e\u987e\u4e4b\u5fe7\u3002\u5426\u5219\uff0c\u82e5\u970d\u5143\u4f3d\u7684\u624b\u4e0b\u8f6c\u62e2\u5728\u9ea6\u5c0f\u660e\u7684\u8eab \u540e\uff0c\u5bf9\u9ea6\u5c0f\u660e\u7684\u5a01\u80c1\uff0c\u4e5f\u5c31\u53ef\u60f3\u800c\u77e5\u3002 \u3000\u3000\u5cad\u5357\u4e8c\u5947\u3001\u6c5f\u5357\u56db\u602a\u7b49\u970d\u5143\u4f3d\u7684\u5fc3\u8179\u9ad8\u624b\uff0c\u89c1\u67cf\u9f84\u9662\u65b9\u9762\u7684\u4eba\u5df2\u62a2\u5148\u5954\u5411\u9ea6\u5c0f\u660e\u8eab\u540e\u53ca \u4e24\u4fa7\uff0c\u53ea\u597d\u56f4\u4f4f\u524d\u65b9\u53ca\u524d\u65b9\u4e24\u4fa7\u7684\u7a7a\u5904\u3002 \u3000\u3000\u6b64\u523b\u67cf\u9f84\u9662\u65b9\u9762\u7684\u7fa4\u8c6a\uff0c\u5bf9\u9ea6\u5c0f\u660e\u6b64\u4e3e\uff0c\u83ab\u4e0d\u53c8\u75db\u53c8\u6c14\u3002\u75db\u7684\u662f\u4ed6\u52bf\u5fc5\u5373\u5c06\u6e85\u8840\u6a2a\u6b7b\uff1b \u6c14\u7684\u662f\u4ed6\u65e2\u5df2\u9003\u8d70\uff0c\u4e3a\u4f55\u53c8\u524d\u6765\u9001\u6b7b\uff1f \u3000\u3000\u4e3a\u4e86\u901e\u4e00\u65f6\u4e4b\u5f3a\u800c\u9001\u4e0a\u4e00\u547d\uff0c\u90a3\u662f\u591a\u4e48\u4e0d\u503c\u800c\u53c8\u611a\u7b28\u7684\u4e00\u4ef6\u4e8b\uff01\u9ea6\u5c0f\u660e\u4e00\u5411\u806a\u660e\u8fc7\u4eba\uff0c \u4e3a\u4ec0\u4e48\u7adf\u505a\u51fa\u8fd9\u6837\u7684\u50bb\u4e8b\u6765\uff1f\u5b9e\u5728\u4ee4\u4ed6\u4eec\u96be\u4ee5\u7406\u89e3\u3002 \u3000\u3000\u4f46\u4e8b\u5df2\u81f3\u6b64\uff0c\u7fa4\u8c6a\u4eec\u5df1\u5bf9\u6551\u4ed6\u4e0d\u6b7b\u65e0\u80fd\u4e3a\u529b\uff0c\u73b0\u5728\u970d\u5143\u4f3d\u628a\u9ea6\u5c0f\u660e\u5904\u6b7b\uff0c\u53ef\u8bf4\u5df2\u662f\u540d\u6b63 \u8a00\u987a\u4e86\u3002 \u3000\u3000\u970d\u5143\u4f3d\u53cc\u988a\u4e0d\u4f4f\u62bd\u6410\uff0c\u543c\u9053\uff1a\u201c\u9ea6\u5c0f\u660e\uff0c\u53db\u9006\uff01\u4f60\u7684\u80c6\u5b50\u4e5f\u672a\u514d\u592a\u5927\u4e86\u3002\u4e0d\u8fc7\u6362\u53e5\u8bdd \u8bf4\uff0c\u4f60\u8fd9\u4efd\u52c7\u6c14\u5012\u9887\u4ee4\u8001\u592b\u4f69\u670d\uff0c\u4e16\u4e0a\u5c45\u7136\u8fd8\u6709\u50cf\u4f60\u8fd9\u6837\u4e0d\u6015\u6b7b\u7684\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u4f9d\u7136\u6c14\u5b9a\u795e\u95f2\uff0c\u6de1\u6de1\u4e00\u7b11\u9053\uff1a\u201c\u970d\u5143\u4f3d\uff0c\u591a\u8c22\u5566\uff01\u5728\u4e0b\u539f\u4ee5\u4e3a\u4f60\u8fd9\u4eba\u65e0\u6069\u65e0\u4e49\uff0c \u60f3\u4e0d\u5230\u4f1a\u5bf9\u6211\u9ea6\u5c0f\u660e\u8fd9\u4e48\u597d\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u53f1\u9053\uff1a\u201c\u4f60\u8fd9\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4f60\u65b9\u624d\u5728\u5927\u5385\u4e0d\u662f\u8bf4\u8fc7\u5417\uff1f\u8981\u4e3a\u6211\u539a\u846c\uff0c\u53c8\u51c6\u5907\u597d\u2018\u4e94\u5b50\u54ed\u5893\u2019\uff0c\u4f60\u7ed9\u6211 \u8fd9\u4efd\u8eab\u540e\u54c0\u8363\uff0c\u6211\u600e\u80fd\u4e0d\u611f\u6fc0\u4f60\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u53ea\u6c14\u5f97\u6012\u706b\u6025\u51b2\uff0e\u9053\uff1a\u201c\u542c\u4f60\u7684\u8bed\u6c14\uff0c\u662f\u8ba4\u4e3a\u6b7b\u6bd4\u6d3b\u7740\u597d\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff01\u6211\u6d3b\u7740\u7684\u65f6\u5019\uff0c\u54ea\u4f1a\u5f97\u5230\u8fd9\u79cd\u8363\u8000\uff0c\u6240\u4ee5\u6211\u662f\u81ea\u52a8\u524d\u6765\u9001\u6b7b \u7684\u3002\u201d \u3000\u3000\u201c\u597d\uff0c\u90a3\u4f60\u5c31\u8fdb\u5165\u5927\u5385\uff0c\u7b49\u5019\u884c\u5211\u3002\u201d \u3000\u3000\u201c\u65e2\u7136\u6b7b\u6bd4\u6d3b\u7740\u597d\uff0c\u5728\u4e0b\u4e3a\u4e86\u611f\u8c22\u4f60\u7684\u6069\u5fb7\uff0c\u8fd8\u4e0d\u60f3\u4e00\u4e2a\u4eba\u6b7b\u3002\u201d \u3000\u3000\u201c\u4f60\u60f3\u600e\u6837\uff1f\u201d \u3000\u3000\u201c\u60f3\u628a\u4f60\u559c\u6b22\u7684\u4eba\u4e5f\u5e26\u8d70\u51e0\u4e2a\u2014\u8d77\u6b7b\uff0c\u8fd9\u6837\u4e5f\u7b97\u5728\u4e0b\u5bf9\u4f60\u7684\u4e00\u756a\u56de\u62a5\uff01\u201d \u3000\u3000\u641c\u9b42\u624b\u5df4\u5929\u4e49\u518d\u4e5f\u8010\u4e0d\u4f4f\uff0e\u53eb\u9053\uff1a\u201c\u7980\u76df\u4e3b\uff0c\u7528\u4e0d\u7740\u548c\u4ed6\u591a\u8d39\u53e3\u820c\uff0c\u5c5e\u4e0b\u73b0\u5728\u5c31\u628a\u4ed6\u751f \u64d2\u6d3b\u6349\uff0c\u7136\u540e\u4ea4\u7ed9\u76df\u4e3b\u6b63\u6cd5\uff01\u201d \u3000\u3000\u62d8\u9b44\u7d22\u5b8b\u5929\u94ce\u77e5\u9053\u5df4\u5929\u4e49\u4e00\u4eba\u7edd\u975e\u9ea6\u5c0f\u660e\u5bf9\u624b\uff0c\u5fd9\u9053\uff1a\u201c\u5b8b\u67d0\u548c\u5df4\u5144\u4e00\u8d77\u8054\u624b\u64d2\u4ed6\uff01\u201d \u3000\u3000\u201c\u773c\u4e0b\u6709\u8fd9\u4e48\u591a\u4eba\uff0e\u8fd8\u7528\u4e0d\u7740\u4f60\u4eec\u52a8\u624b\uff01\u201d \u3000\u3000\u5df4\u5929\u4e49\u9053\uff1a\u201c\u76df\u4e3b\u53ef\u662f\u53c8\u8981\u6d3e\u90a3\u4e2a\u53eb\u5f20\u656c\u5b89\u7684\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u5411\u9ea6\u5c0f\u660e\u8eab\u540e\u626b\u4e86\u4e00\u773c\u9053\uff1a\u201c\u526f\u76df\u4e3b\uff0c\u67cf\u9f84\u9662\u7684\u9ad8\u624b\u5728\u573a\u4e0d\u5c11\uff0c\u9ea6\u5c0f\u660e\u5c31\u7531\u4f60\u4eec \u64d2\u4e0b\uff01\u201d \u3000\u3000\u949f\u4e00\u8c6a\u7acb\u5373\u53eb\u9053\uff1a\u201c\u7980\u76df\u4e3b\uff0c\u67cf\u9f84\u9662\u6240\u6709\u7684\u4eba\uff0e\u90fd\u4e0d\u66fe\u643a\u5e26\u5175\u5203\uff0c\u4ee5\u9ea6\u5c0f\u660e\u7684\u6b66\u529f\uff0c\u8c01 \u6562\u4e0a\u524d\u64d2\u4ed6\uff01\u201d \u3000\u3000\u201c\u4f60\u4eec\u4e3a\u4ec0\u4e48\u4e0d\u5e26\u5175\u5203\uff1f\u201d \u3000\u3000\u201c\u5c5e\u4e0b\u4eec\u53ea\u662f\u5949\u547d\u5230\u805a\u4e49\u5385\u6765\uff0c\u5f53\u65f6\u5e76\u4e0d\u77e5\u6765\u505a\u4ec0\u4e48\uff0c\u4f55\u5fc5\u643a\u5e26\u5175\u5203\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u89c1\u6b64\u60c5\u5f62\uff0c\u5f53\u7136\u4e0d\u80fd\u547d\u949f\u4e00\u8c6a\u7b49\u4eba\u56de\u53bb\u53d6\u5175\u5203\uff0c\u53ea\u597d\u56de\u5934\u53eb\u9053\uff1a\u201c\u5f20\u656c\u5b89\u51fa \u6765\uff01\u201d \u3000\u3000\u4e00\u6761\u4eba\u5f71\u7531\u5927\u5385\u5185\u63a0\u51fa\uff0c\u679c\u7136\u662f\u5f20\u656c\u5b89\uff0c\u663e\u7136\u6b64\u4eba\u4e5f\u662f\u85cf\u8eab\u5728\u5c4f\u98ce\u4e4b\u540e\uff0e \u3000\u3000\u5f20\u656c\u5b89\u53cc\u624b\u65e9\u5df2\u63e1\u7740\u5149\u8292\u8000\u773c\u7684\u4e24\u679a\u91d1\u73af\uff0e\u6765\u5230\u970d\u5143\u4f3d\u8eab\u4fa7\uff0c\u76ee\u5149\u5446\u6ede\u7684\u7ad9\u5728\u90a3\u91cc\u5f85 \u547d\u3002 \u3000\u3000\u539f\u6765\uff0c\u970d\u5143\u4f3d\u6628\u591c\u89c1\u5f20\u656c\u5b89\u8d25\u5728\u9ea6\u5c0f\u660e\u624b\u4e0b\uff0c\u4e00\u76f4\u4ee5\u4e3a\u662f\u2014\u65f6\u5931\u8bef\u548c\u5927\u610f\u6240\u81f4\uff0c\u56e0\u4e4b\uff0c \u4ed6\u51b3\u5b9a\u8ba9\u5f20\u656c\u5b89\u518d\u8bd5\u4e00\u6b21\u3002 \u3000\u3000\u5982\u679c\u4ecd\u7136\u8d25\u5317\uff0c\u518d\u91c7\u53d6\u5176\u4ed6\u884c\u52a8\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u67cf\u9f84\u9662\u6240\u6709\u7684\u4eba\u90fd\u8d8a\u53d1\u4e3a\u9ea6\u5c0f\u660e\u7740\u6025\uff0c\u949f\u4e00\u8c6a\u8ddd\u9ea6\u5c0f\u660e\u8eab\u540e\u6700\u8fd1\uff0c\u4ee5\u201c\u4f20\u97f3\u5165 \u5bc6\u201d\u4e4b\u672f\u53eb\u9053\uff1a\u201c\u9ea6\u8001\u5f1f\uff0e\u8fd9\u662f\u4f55\u82e6\uff1f\u4f60\u662f\u806a\u660e\u4eba\uff0c\u600e\u53ef\u4e3a\u901e\u4e00\u65f6\u4e4b\u6c14\u800c\u505a\u51fa\u50bb\u4e8b\uff1f\u201d \u5367\u9f99\u751f\u300a\u5999\u7edd\u5929\u9999\u300b \u7b2c\u5341\u4e03\u56de\u3000\u667a\u6218\u7fa4\u9b54 \u3000\u3000\u9ea6\u5c0f\u660e\u5e76\u672a\u56de\u5934\uff0c\u4e5f\u4ee5\u201c\u4f20\u97f3\u4e4b\u5bc6\u201d\u4e4b\u672f\u9053\uff1a\u201c\u949f\u5927\u54e5\u653e\u5fc3\uff0c\u5c0f\u5f1f\u6599\u60f3\u970d\u5143\u4f3d\u8fd8\u4e0d\u80fd\u628a \u6211\u600e\u6837\uff01\u201d \u3000\u3000\u949f\u4e00\u8c6a\u771f\u60f3\u628a\u9ea6\u5c0f\u660e\u81ed\u9a82\u4e00\u987f\uff0c\u53c8\u75db\u53c8\u6c14\u9053\uff1a\u201c\u970d\u5143\u4f3d\u624b\u4e0b\u5728\u573a\u7684\u5c31\u6709\u5341\u51e0\u540d\u9ad8\u624b\uff0c\u54b1 \u4eec\u67cf\u9f84\u9662\u7684\u4eba\u53c8\u5e2e\u4e0d\u4e0a\u4f60\u7684\u5fd9\uff0c\u4f60\u8fd8\u8bf4\u4e0d\u80fd\u628a\u4f60\u600e\u6837\uff0c\u4f60\u4e5f\u592a\u4e0d\u77e5\u5929\u9ad8\u5730\u539a\u4e86\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u8fde\u6211\u81ea\u5df1\u90fd\u4e0d\u7d27\u5f20\uff0c\u4f60\u4f55\u82e6\u8fd9\u6837\u7d27\u5f20\uff1f\u522b\u518d\u5206\u6211\u7684\u795e\uff0c\u6211\u73b0\u5728\u5fc5\u987b\u5168\u529b\u5bf9 \u4ed8\u5f20\u5e08\u5144\uff0c\u7b49\u665a\u4e0a\u6ca1\u4e8b\u65f6\uff0e\u518d\u966a\u4f60\u548c\u4f59\u5148\u751f\u597d\u597d\u559d\u51e0\u676f\u9152\uff01\u201d \u3000\u3000\u201c\u5c0f\u660e\uff0c\u4f60\u597d\u50cf\u75af\u5566\uff01\u4f60\u665a\u4e0a\u5f53\u7136\u8981\u559d\u9152\uff0c\u4e0d\u8fc7\u559d\u7684\u8be5\u662f\u796d\u5960\u4f60\u7684\u9152\uff01\u201d \u3000\u3000\u201c\u4e0d\u7ba1\u4ec0\u4e48\u9152\uff0c\u53ea\u8981\u6709\u9152\u559d\u5c31\u597d\u3002\u201d \u3000\u3000\u4e24\u4eba\u8fd9\u4e00\u9635\u5bf9\u8bdd\uff0c\u53ea\u6709\u4e24\u4eba\u77e5\u9053\uff0c\u5e76\u672a\u5f15\u8d77\u522b\u4eba\u6ce8\u610f\u3002 \u3000\u3000\u53ea\u89c1\u970d\u5143\u4f3d\u559d\u9053\uff1a\u201c\u5f20\u656c\u5b89\uff0c\u4f60\u6628\u665a\u4e3a\u4f55\u4e0d\u66fe\u8fbe\u6210\u4efb\u52a1\uff1f\u201d \u3000\u3000\u5f20\u656c\u5b89\u4e24\u773c\u5446\u89c6\u7740\u970d\u5143\u4f3d\uff0e\u4e00\u526f\u4f3c\u61c2\u975e\u61c2\u6a21\u6837\u3002 \u3000\u3000\u970d\u5143\u4f3d\u518d\u559d\u9053\uff1a\u201c\u73b0\u5728\u518d\u7ed9\u4f60\u4e00\u6b21\u673a\u4f1a\uff0c\u628a\u90a3\u5c0f\u5b50\u62ff\u4e0b\uff01\u201d \u3000\u3000\u5f20\u656c\u5b89\u4e24\u9053\u5446\u6ede\u7684\u76ee\u5149\u8f6c\u5411\u9ea6\u5c0f\u660e\uff0c\u63a5\u7740\u76f4\u6251\u800c\u4e0a\uff0c\u53cc\u624b\u4e24\u679a\u91d1\u73af\uff0c\u95ea\u7535\u822c\u4e00\u8d77\u51fb\u51fa\uff0c \u987f\u65f6\u5149\u8292\u4ea4\u9519\uff0c\u4ee4\u4eba\u773c\u82b1\u7f2d\u4e71\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u968f\u5373\u4e5f\u5251\u6c14\u5982\u8679\uff0e\u5bd2\u8292\u95ea\u70c1\uff0c\u5708\u5165\u4e86\u91d1\u5149\u4e4b\u4e2d\u3002 \u3000\u3000\u4e00\u9635\u201c\u5f53\u5f53\u201d\u7684\u91d1\u94c1\u4ea4\u51fb\u4e4b\u58f0\u8fc7\u540e\uff0c\u5f20\u656c\u5b89\u5f88\u5feb\u4fbf\u62a2\u5360\u4e86\u4e0a\u98ce\uff0c\u5728\u5341\u4f59\u62db\u8fc7\u540e\uff0c\u9ea6\u5c0f\u660e \u4fbf\u88ab\u8feb\u53cc\u811a\u4e0d\u4f4f\u5411\u540e\u79fb\u52a8\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u67cf\u9f84\u9662\u6240\u6709\u7684\u4eba\uff0c\u90fd\u4e0d\u514d\u770b\u5f97\u60ca\u5fc3\u52a8\u9b44\uff0c\u9ea6\u5c0f\u660e\u8fde\u4e00\u4e2a\u5f20\u656c\u5b89\u90fd\u5e94\u4ed8\u4e0d\u4e86\uff0c\u4f55\u51b5 \u970d\u5143\u4f3d\u624b\u4e0b\u66f4\u6709\u5341\u51e0\u540d\u9ad8\u624b\uff0e\u968f\u65f6\u90fd\u53ef\u4e0a\u524d\u52a9\u9635\uff0c\u5fc5\u8981\u65f6\u970d\u5143\u4f3d\u4e5f\u53ef\u80fd\u4eb2\u81ea\u4e0b\u573a\u3002 \u3000\u3000\u4f46\u970d\u5143\u4f3d\u5374\u770b\u5f97\u7709\u98de\u8272\u821e\uff0c\u5927\u4e3a\u5f97\u610f\uff0e \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u6218\u5c40\u5df2\u6709\u4e86\u663e\u8457\u53d8\u5316\uff0e\u9ea6\u5c0f\u660e\u4e0d\u4f46\u5df2\u7a33\u4f4f\u9635\u811a\uff0c\u53cd\u800c\u903c\u5f97\u5f20\u656c\u5b89\u5f00\u59cb\u624b\u5fd9\u811a \u4e71\u3002 \u3000\u3000\u5728\u573a\u53cc\u65b9\u7684\u4eba\u54ea\u91cc\u77e5\u9053\uff0c\u9ea6\u5c0f\u660e\u6b64\u523b\u5df2\u4f7f\u51fa\u201c\u5999\u5999\u5251\u8bc0\u201d\u4e0a\u7684\u5251\u6cd5\u3002 \u3000\u3000\u53ea\u542c\u201c\u5f53\u201d\u7684\u2014\u58f0\uff0c\u5f20\u656c\u5b89\u7684\u2014\u679a\u91d1\u73af\u5df2\u88ab\u51fb\u843d\u3002 \u3000\u3000\u5f20\u656c\u5b89\u521a\u8981\u5411\u540e\u8dc3\u9000\uff0c\u4e0d\u4f46\u53e6\u2014\u679a\u91d1\u73af\u4e5f\u51e0\u4e4e\u5728\u540c\u4e00\u65f6\u95f4\u88ab\u51fb\u98de\u51fa\u53bb\uff0c\u8fde\u9ea6\u5c0f\u660e\u7684\u5251\u950b\uff0c \u4e5f\u62b5\u4f4f\u4e86\u4ed6\u7684\u54bd\u5589\u3002 \u3000\u3000\u60c5\u52bf\u8f6c\u5f97\u5982\u6b64\u4e4b\u5feb\uff0e\u5b9e\u5728\u5927\u51fa\u6240\u6709\u4eba\u7684\u9884\u6599\uff0e \u3000\u3000\u5f20\u656c\u5b89\u53ea\u662f\u5446\u5728\u5f53\u5730\u3001\u77a0\u76ee\u7ed3\u820c\uff0c\u52a8\u4e5f\u4e0d\u52a8\u3002 \u3000\u3000\u5df4\u5929\u4e49\u548c\u5b8b\u5929\u94ce\u521a\u8981\u8054\u624b\u9f50\u4e0a\uff0c\u5374\u542c\u970d\u5143\u4f3d\u559d\u9053\uff1a\u201c\u6682\u65f6\u7528\u4e0d\u7740\u4f60\u4eec\u4e24\u4f4d\uff01\u201d \u3000\u3000\u5df4\u5929\u4e49\u548c\u5b8b\u5929\u9a7f\u53ea\u9053\u970d\u5143\u4f3d\u8981\u4eb2\u81ea\u51fa\u9635\uff0c\u53ea\u597d\u6682\u5728\u539f\u5730\u5f85\u547d\u3002 \u3000\u3000\u53ea\u89c1\u970d\u5143\u4f3d\u56de\u5934\u5411\u5927\u5385\u9ad8\u58f0\u9053\uff1a\u201c\u4f60\u4eec\u4e09\u4e2a\u4e00\u8d77\u51fa\u6765\uff01\u201d \u3000\u3000\u4ed6\u8bdd\u97f3\u521a\u843d\uff0c\u7acb\u5373\u6709\u4e09\u6761\u4eba\u5f71\u8fc5\u901f\u65e0\u6bd4\u7684\u63a0\u4e86\u51fa\u6765\u3002 \u3000\u3000\u4e09\u4eba\u6765\u5230\u970d\u5143\u4f3d\u8eab\u4fa7\uff0c\u81ea\u52a8\u4e00\u5b57\u6392\u597d\u3002 \u3000\u3000\u8fd9\u4e09\u4eba\u90fd\u5e74\u5728\u56db\u65ec\u4e0a\u4e0b\uff0c\u867d\u7136\u53cc\u76ee\u65e0\u795e\uff0c\u4f46\u5374\u6c14\u5ea6\u4e0d\u51e1\u3002 \u3000\u3000\u6700\u5de6\u4e00\u4eba\uff0c\u65b9\u9762\u5927\u8033\uff0c\u8eab\u8eaf\u9b41\u68a7\uff0c\u624b\u62c4\u4e00\u6761\u8db3\u6709\u4e03\u516b\u5c3a\u957f\u9e2d\u86cb\u7c97\u7ec6\u7684\u9554\u94c1\u62d0\u3002 \u3000\u3000\u4e2d\u95f4\u2014\u4eba\uff0e\u8eab\u6750\u7626\u524a\uff0e\u9a6c\u8138\u65e0\u987b\uff0c\u53f3\u624b\u7d27\u63e1\u4e00\u67c4\u5149\u8292\u95ea\u8000\u7684\u91d1\u5200\u3002 \u3000\u3000\u53f3\u9762\u4e00\u4eba\uff0c\u9762\u8c8c\u51b7\u5cfb\uff0c\u80a4\u8272\u5947\u767d\uff0c\u53cc\u624b\u5404\u63e1\u2014\u652f\u5929\u661f\u7b14\u3002 \u3000\u3000\u5728\u8fd9\u5239\u90a3\uff0c\u53cc\u65b9\u7fa4\u96c4\u56e0\u4e0d\u8ba4\u8bc6\u8fd9\u4e09\u4eba\u8eab\u4efd\u6765\u5386\uff0c\u53ea\u662f\u8be7\u5f02\u800c\u5df2\u3002\u4f46\u9ea6\u5c0f\u660e\u5374\u5df2\u5927\u60ca\u5931\u8272\uff0c \u51e0\u4e4e\u8981\u547c\u53eb\u51fa\u58f0\u3002 \u3000\u3000\u539f\u6765\u8fd9\u4e09\u4eba\u7adf\u5168\u662f\u9146\u79cb\u7684\u5f1f\u5b50\uff01 \u3000\u3000\u6700\u5de6\u4e00\u4eba\uff0c\u540d\u53eb\u7f57\u94ee\uff0c\u7ef0\u53f7\u94c1\u62d0\u8ffd\u9b42\u3002\u4e2d\u95f4\u2014\u4eba\u540d\u53eb\u4e01\u4e00\u9b42\uff0c\u7ef0\u53f7\u91d1\u5200\u9b3c\u68ad\u3002\u53f3\u8fb9\u4e00\u4eba \u540d\u53eb\u9ad8\u5d99\uff0e\u7ec5\u53f7\u51b7\u773c\u795e\u67ad\u3002 \u3000\u3000\u795e\u6756\u7fc1\u9146\u79cb\u539f\u662f\u8ff7\u8e2a\u8c37\u9996\u4efb\u76df\u4e3b\u80e1\u67cf\u9f84\u7684\u5e08\u53d4\uff0c\u6b64\u4eba\u4e00\u751f\u884c\u5f84\u4ecb\u4e8e\u6b63\u90aa\u4e4b\u95f4\uff0c\u4ed6\u6700\u5927\u7684 \u5174\u8da3\u4fbf\u662f\u5e7f\u6536\u5f1f\u5b50\u3002 \u3000\u3000\u56e0\u4e4b\u4e00\u5171\u6536\u4e86\u516d\u540d\u5f1f\u5b50\uff0e\u5927\u5f1f\u5b50\u65b9\u5929\u6f9c\uff0c\u3000\u4e0a\u6b21\u5728\u5929\u53f0\u4e07\u82b1\u5bab\u88ab\u957f\u767d\u795e\u53df\u5e9e\u58eb\u51b2\u51fb\u6bd9\uff0c \u4ee5\u4e0b\u4f9d\u5e8f\u4e8c\u5f1f\u5b50\u7f57\u94ee\u3001\u4e09\u5f1f\u5b50\u4e01\u4e00\u9b42\u3001\u56db\u5f1f\u5b50\u9ad8\u5d99\u3001\u4e94\u5f1f\u5b50\u5f20\u656c\u5b89\u3001\u516d\u5f1f\u5b50\u9ea6\u5c0f\u660e\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u518d\u4e5f\u60f3\u4e0d\u5230\uff0c\u4ed6\u7684\u4e8c\u5e08\u5144\u3001\u4e09\u5e08\u5144\u3001\u56db\u5e08\u5144\u4e5f\u5168\u843d\u5728\u970d\u5143\u4f3d\u624b\u4e2d\uff0c\u770b\u4ed6\u4eec\u773c\u795e\u5446 \u6ede\u7684\u6a21\u6837\uff0c\u6beb\u65e0\u7591\u95ee\u4e5f\u662f\u559d\u4e0b\u201c\u5411\u5fc3\u9732\u201d\u3002\u5931\u5374\u672c\u6027\uff0c\u4efb\u7531\u970d\u5143\u4f3d\u968f\u610f\u63a7\u5236\u3002 \u3000\u3000\u8fd9\u4e09\u4eba\u7684\u6b66\u529f\u6bd4\u8d77\u5f20\u656c\u5b89\uff0c\u90fd\u8981\u66f4\u9ad8\u4e00\u7b79\u3002 \u3000\u3000\u4ee5\u9ea6\u5c0f\u660e\u6765\u8bf4\uff0c\u5bf9\u4ed8\u4e00\u4e2a\u5f20\u656c\u5b89\u5df2\u4e0d\u8f7b\u677e\uff0c\u5982\u4eca\u4e09\u4f4d\u5e08\u5144\u4e00\u8d77\u5c71\u624b\uff0e\u540e\u679c\u5982\u4f55\uff0c\u53ef\u60f3\u800c \u77e5\u3002 \u3000\u3000\u4f46\u4ed6\u6b64\u523b\u5df2\u6210\u9a91\u864e\u96be\u4e0b\u4e4b\u52bf\uff0c\u53ea\u6709\u786c\u8d77\u5934\u76ae\u786c\u62fc\u4e86\uff0e \u3000\u3000\u5ffd\u542c\u8033\u8fb9\u53c8\u4f20\u6765\u949f\u4e00\u8c6a\u7684\u58f0\u97f3\u9053\uff1a\u201c\u5c0f\u660e\uff0c\u8fd9\u4e09\u4eba\u597d\u50cf\u90fd\u662f\u4f60\u7684\u5e08\u5144\uff0e\u4ed6\u4eec\u663e\u7136\u5df2\u88ab \u2018\u5411\u5fc3\u9732\u2019\u8ff7\u5931\u672c\u6027\uff0c\u8fd9\u4e0b\u4f60\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u6781\u529b\u4fdd\u6301\u7740\u9547\u5b9a\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u77e5\u9053\u600e\u4e48\u529e\uff0c\u6b7b\u5c31\u6b7b\u5427\uff0c\u53cd\u6b63\u5df2\u6709\u4e94\u4e2a\u513f\u5b50\u7ed9\u6211\u54ed \u5893\uff0c\u4e5f\u7b97\u503c\u5f97\u4e86\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u626b\u89c6\u4e86\u7f57\u94ee\u4e09\u4eba\u2014\u773c\u9053\uff1a\u201c\u4f60\u4eec\u662f\u4e00\u8d77\u51fa\u624b\uff0c\u8fd8\u662f\u548c\u4ed6\u5355\u6253\u72ec\u6597\uff1f\u201d \u3000\u3000\u7f57\u94ee\u4e09\u4eba\u90fd\u672a\u4f5c\u7b54\u3002 \u3000\u3000\u970d\u5143\u4f3d\u8fd9\u624d\u60f3\u8d77\u8fd9\u4e09\u4eba\u5df2\u7ecf\u5931\u53bb\u7406\u667a\uff0c\u65b9\u624d\u7684\u8bdd\u7b49\u4e8e\u5bf9\u725b\u5f39\u7434\u3002 \u3000\u3000\u4ed6\u6b63\u8981\u4e0b\u4ee4\u9f50\u4e0a\uff0c\u4ee5\u4fbf\u53ca\u65e9\u5c06\u9ea6\u5c0f\u660e\u6536\u62fe\uff0c\u5374\u5ffd\u542c\u82d7\u7d20\u5170\u9053\uff1a\u201c\u76df\u4e3b\uff0c\u6682\u7f13\u8ba9\u4ed6\u4eec\u4e09\u4eba \u51fa\u624b\uff0c\u5f85\u6211\u548c\u9ea6\u5c0f\u660e\u8fd9\u53db\u9006\u518d\u8bb2\u51e0\u53e5\u8bdd\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u832b\u7136\u9053\uff1a\u201c\u592b\u4eba\u548c\u4ed6\u6709\u4ec0\u4e48\u8bdd\u597d\u8bb2\uff1f\u201d \u3000\u3000\u82d7\u7d20\u5170\u9053\uff1a\u201c\u7b49\u6211\u903c\u4ed6\u653e\u4e86\u5f20\u656c\u5b89\uff0e\u4f60\u518d\u62ff\u4ed6\uff0c\u5426\u5219\u5c82\u4e0d\u767d\u767d\u9001\u4e86\u5f20\u656c\u5b89\u4e00\u547d\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u5f04\u4e0d\u6e05\u82d7\u7d20\u5170\u5c06\u7528\u4ec0\u4e48\u65b9\u6cd5\u903c\u9ea6\u5c0f\u660e\u653e\u56de\u5f20\u656c\u5b89\uff0c\u6b63\u8bb6\u7136\u4e0d\u89e3\u95f4\uff0c\u82d7\u7d20\u5170\u5df2\u8fc5\u901f \u7684\u5411\u9ea6\u5c0f\u660e\u8d70\u53bb\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u987f\u65f6\u609f\u51fa\u82d7\u7d20\u5170\u7684\u6765\u610f\uff0c\u539f\u6765\u5979\u662f\u6551\u81ea\u5df1\u6765\u7684\uff0c\u5c31\u5728\u82d7\u7d20\u5170\u8d70\u8fd1\u8eab\u524d\u4e0d\u8db3\u4e00\u4e08\u65f6\uff0c \u95ea\u7535\u822c\u51fa\u624b\u628a\u5f20\u656c\u5b89\u2014\u6307\u70b9\u5012\uff0c\u7d27\u63a5\u7740\u2014\u4e2a\u7bad\u6b65\u7a9c\u4e0a\u53bb\uff0c\u5251\u5c16\u53c8\u62b5\u4e0a\u4e86\u82d7\u7d20\u5170\u7684\u54bd\u5589\u3002 \u3000\u3000\u970d\u5143\u4f3d\u5927\u5403\u2014\u60ca\uff0c\u9a87\u7136\u53eb\u9053\uff1a\u201c\u592b\u4eba\uff01\u592b\u4eba\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u51b7\u7b11\u9053\uff1a\u201c\u970d\u5143\u4f3d\uff0c\u592b\u4eba\u7684\u6027\u547d\u5df2\u7ecf\u843d\u5728\u6211\u7684\u624b\u4e2d\uff0c\u4f60\u53eb\u5979\u8fd8\u4e0d\u5982\u53eb\u6211\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u987f\u65f6\u77a0\u76ee\u7ed3\u820c\uff0c\u5446\u5728\u5f53\u5730\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u4e00\u6761\u4eba\u5f71\u7531\u6570\u4e08\u5916\u7684\u2014\u68f5\u5927\u6811\u4e0b\uff0c\u52bf\u5982\u5f29\u7bad\u79bb\u5f26\uff0c\u5212\u7a7a\u75be\u63a0\u800c\u6765\uff0c\u8f7b\u98d8\u98d8\u65e0 \u58f0\u65e0\u606f\u7684\u964d\u843d\u5728\u9ea6\u5c0f\u660e\u8eab\u4fa7\u3002 \u3000\u3000\u4f46\u89c1\u8fd9\u4eba\u4e00\u88ad\u84dd\u886b\uff0c\u9762\u7f69\u9ed1\u7eb1\uff0c\u6b63\u662f\u6628\u665a\u76f8\u52a9\u9ea6\u5c0f\u660e\u7684\u90a3\u540d\u84dd\u886b\u8499\u9762\u4eba\u3002 \u3000\u3000\u949f\u2014\u8c6a\u548c\u4f59\u4ea6\u4e50\u987f\u65f6\u5fc3\u60c5\u5927\u4e3a\u653e\u677e\uff0e \u3000\u3000\u4e0d\u8fc7\uff0c\u90a3\u4e9b\u6628\u591c\u672b\u89c1\u6b64\u4eba\u7684\u53cc\u65b9\u7fa4\u96c4\uff0c\u5374\u4ecd\u6123\u5728\u539f\u5730\u3002 \u3000\u3000\u53ea\u542c\u84dd\u886b\u8499\u9762\u4eba\u9053\uff1a\u201c\u9ea6\u5c0f\u4fa0\u53ea\u7ba1\u5bf9\u4ed8\u8fd9\u5973\u4eba\uff0c\u5982\u679c\u6709\u4eba\u6562\u6765\u5077\u88ad\uff0e\u4e00\u5207\u6709\u5728\u4e0b\u5e94\u4ed8\u3002\u201d \u3000\u3000\u663e\u7136\uff0c\u84dd\u886b\u8499\u9762\u4eba\u548c\u9ea6\u5c0f\u660e\u65e9\u6709\u9ed8\u5951\uff0c\u9ea6\u5c0f\u660e\u53c8\u600e\u6837\u548c\u8fd9\u4eba\u8ba4\u8bc6\u7684\u5462\uff1f \u3000\u3000\u84dd\u886b\u8499\u9762\u4eba\u7684\u8eab\u624b\uff0c\u6628\u591c\u5df2\u6709\u4e0d\u5c11\u4eba\u89c1\u8fc7\uff0e\u5c24\u5176\u970d\u5143\u4f3d\uff0c\u66f4\u5bf9\u6b64\u4eba\u5370\u8c61\u6df1\u523b\u3002 \u3000\u3000\u5176\u5b9e\uff0c\u5373\u4f7f\u84dd\u886b\u8499\u9762\u4eba\u4e0d\u6765\u76f8\u52a9\u9ea6\u5c0f\u660e\uff0c\u970d\u5143\u4f3d\u4e5f\u4e0d\u6562\u518d\u547d\u624b\u4e0b\u4eba\u8f7b\u4e3e\u5984\u52a8\uff0c\u82d7\u7d20\u5170\u662f \u4ed6\u7684\u538b\u5be8\u592b\u4eba\uff0c\u5728\u4ed6\u7684\u5fc3\u4e2d\uff0c\u4fdd\u4f4f\u82d7\u7d20\u5170\u2014\u547d\uff0c\u4e0d\u6d88\u8bf4\u6bd4\u6740\u9ea6\u5c0f\u660e\u66f4\u4e3a\u91cd\u8981\u3002 \u3000\u3000\u5f53\u4e0b\uff0c\u970d\u5143\u4f3d\u6025\u5f97\u731b\u4e00\u8dfa\u811a\u9053\uff1a\u201c\u592b\u4eba\uff0c\u4f60\u8fd9\u662f\u4f55\u82e6\uff0e\u4e3a\u4e86\u8ddf\u4ed6\u8bb2\u51e0\u53e5\u8bdd\uff0c\u5374\u843d\u5165\u864e \u53e3\uff01\u201d \u3000\u3000\u82d7\u7d20\u5170\u9762\u8272\u60e8\u767d\u9053\uff1a\u201c\u59be\u8eab\u600e\u4f1a\u60f3\u5230\u4e8b\u60c5\u6f14\u53d8\u5230\u8fd9\u79cd\u5730\u6b65\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u4f60\u6709\u4ec0\u4e48\u8bdd\uff0c\u73b0\u5728\u5c31\u5bf9\u4ed6\u8bb2\u3002\u8001\u592b\u5012\u8981\u542c\u542c\uff0e\u5230\u5e95\u662f\u4ec0\u4e48\u91cd\u8981\u7684\u8bdd\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u62c5\u5fc3\u88ab\u970d\u5143\u4f3d\u8bc6\u51fa\u7834\u7efd\uff0c\u3000\u4e00\u628a\u6293\u4f4f\u82d7\u7d20\u5170\u80a9\u5934\uff0c\u5c06\u5251\u5c16\u6643\u4e86\u51e0\u6643\uff0c\u55d4\u76ee\u559d\u9053\uff1a \u201c\u4e0d\u51c6\u4f60\u8bb2\uff0c\u4f60\u5df2\u662f\u970d\u5143\u4f3d\u7684\u4eba\uff0e\u5728\u4e0b\u4e0d\u60f3\u542c\u4f60\u8bb2\u8bdd\uff01\u201d \u3000\u3000\u82d7\u7d20\u5170\u53eb\u9053\uff1a\u201c\u6211\u504f\u8981\u8bb2\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u543c\u9053\uff1a\u201c\u4e0d\u6015\u6b7b\u53ea\u7ba1\u8bb2\uff0c\u5148\u6740\u4e86\u4f60\uff0c\u5728\u4e0b\u6b7b\u4e5f\u503c\u5f97\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u5927\u53eb\u9053\uff1a\u201c\u592b\u4eba\uff0c\u4e0d\u5fc5\u8bb2\u4e86\uff0c\u4f60\u7684\u547d\u8981\u7d27\uff0c\u90a3\u5c0f\u5b50\u5728\u8fd9\u79cd\u5173\u5934\uff0e\u7edd\u4e0d\u4f1a\u5f00\u73a9\u7b11\u7684\uff0c \u4ed6\u662f\u8bf4\u6740\u5c31\u6740\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u77e5\u9053\u5c31\u597d\uff01\u201d \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u7a81\u542c\u84dd\u886b\u4eba\u4e00\u58f0\u51b7\u53f1\uff0c\u63a5\u7740\u5de6\u8155\u4e00\u626c\uff0c\u4e00\u9053\u767d\u5149\uff0c\u76f4\u5411\u6251\u6765\u2014\u4eba\u5c04\u53bb\u3002 \u3000\u3000\u90a3\u4eba\u60e8\u53eb\u4e00\u58f0\uff0c\u5e94\u624b\u5012\u5730\uff0c\u5374\u662f\u6c5f\u5357\u56db\u602a\u4e2d\u7684\u6797\u9b54\u82d7\u5f3a\u3002 \u3000\u3000\u539f\u6765\u82d7\u5f3a\u4e3a\u7acb\u529f\uff0e\u7adf\u5077\u5077\u7531\u4e00\u4fa7\u6251\u53bb\uff0c\u53ef\u60dc\u4ed6\u53ea\u79fb\u52a8\u4e86\u4e8c\u4e09\u6b65\uff0e\u4fbf\u88ab\u84dd\u886b\u8499\u9762\u4eba\u7684\u6697\u5668 \u51fb\u5012\u5728\u5730\uff0e \u3000\u3000\u6c5f\u5357\u56db\u602a\u7684\u53e6\u4e09\u602a\uff0c\u6d77\u767b\u3001\u5305\u901a\u4e00\u89c1\u82d7\u5f3a\u4e2d\u4e86\u6697\u5668\u88ab\u51fb\u5012\u5728\u5730\uff0c\u8fde\u5fd9\u5954\u4e0a\u524d\u53bb\u628a\u4ed6\u62ac\u4e86 \u56de\u6765\u3002 \u3000\u3000\u82d7\u5f3a\u88ab\u51fb\u4e2d\u201c\u671f\u95e8\u7a74\u201d\uff0c\u9664\u4e86\u5168\u8eab\u9ebb\u7656\uff0c\u5e76\u65e0\u5927\u788d\u3002 \u3000\u3000\u970d\u5143\u4f3d\u5386\u58f0\u53f1\u9053\uff1a\u201c\u4f60\u8fd9\u6df7\u86cb\u53ef\u662f\u60f3\u627e\u6b7b\uff1f\u201d \u3000\u3000\u82d7\u5f3a\u6709\u6c14\u65e0\u529b\u7684\u9053\uff1a\u201c\u7980\u76df\u4e3b\uff0c\u5c5e\u4e0b\u662f\u60f3\u6551\u592b\u4eba\u7684\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u6012\u9053\uff1a\u201c\u4f60\u6839\u672c\u5c31\u662f\u5bb3\u5979\uff0c\u4f60\u8fd9\u6761\u72d7\u547d\u90a3\u80fd\u62b5\u4e0a\u592b\u4eba\u4e00\u547d\u3002\u201d \u3000\u3000\u82d7\u5f3a\u54ea\u91cc\u8fd8\u6562\u542d\u58f0\u3002 \u3000\u3000\u970d\u5143\u4f3d\u89c9\u51fa\u65b9\u624d\u6709\u4e9b\u8fc7\u4efd\uff0c\u5429\u5490\u6bdb\u8d8a\u7b49\u4eba\u9053\uff1a\u201c\u6682\u65f6\u628a\u4ed6\u62ac\u56de\u53bb\uff0c\u62d4\u51fa\u6697\u5668\u4e3a\u4ed6\u7597\u4f24\u3002\u201d \u3000\u3000\u63a5\u7740\u626b\u89c6\u4e86\u5168\u573a\u4e00\u773c\u9053\uff1a\u201c\u8c01\u90fd\u4e0d\u51c6\u8f7b\u4e3e\u5984\u52a8\uff0c\u4fdd\u4f4f\u592b\u4eba\u7684\u547d\u8981\u7d27\uff01\u201d \u3000\u3000\u4f17\u4eba\u95fb\u8a00\uff0c\u81ea\u52a8\u5404\u81ea\u8eb2\u907f\u5411\u540e\u9000\u5f00\u4e08\u4f59\u3002 \u3000\u3000\u970d\u5143\u4f3d\u671b\u5411\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4f60\u51c6\u5907\u628a\u592b\u4eba\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u6de1\u7136\u4e00\u7b11\u9053\uff1a\u201c\u5728\u4e0b\u4eca\u5929\u65e2\u7136\u96be\u514d\u4e00\u6b7b\uff0c\u603b\u8981\u627e\u4e2a\u57ab\u80cc\u7684\uff0c\u5c11\u4e0d\u5f97\u8981\u628a\u5979\u5e26\u8d70\u3002\u201d \u3000\u3000\u201c\u5e26\u5230\u54ea\u91cc\u53bb\uff1f\u201d \u3000\u3000\u201c\u4e0d\u662f\u4e0a\u5929\u5802\uff0e\u5c31\u662f\u4e0b\u5730\u72f1\uff0c\u514d\u4e0d\u4e86\u540c\u5f52\u4e8e\u5c3d\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u8138\u8272\u4e00\u53d8\u9053\uff1a\u201c\u662f\u5426\u8fd8\u6709\u5546\u8bae\u4f59\u5730\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4f60\u53ef\u662f\u60f3\u8ddf\u5728\u4e0b\u8c08\u6761\u4ef6\uff1f\u201d \u3000\u3000\u201c\u4f60\u7684\u610f\u601d\u5462\uff1f\u201d \u3000\u3000\u201c\u65e2\u7136\u6709\u6761\u4ef6\u597d\u8c08\uff0c\u90a3\u5f88\u597d\uff0c\u5728\u4e0b\u5c31\u51b3\u5b9a\u4e0d\u6740\u5979\u3002\u201d \u3000\u3000\u201c\u73b0\u5728\u5c31\u8bf7\u4f60\u628a\u592b\u4eba\u653e\u56de\u6765\uff01\u201d \u3000\u3000\u201c\u6211\u53ea\u8bf4\u4e0d\u6740\u5979\uff0c\u5e76\u6ca1\u8bf4\u653e\u5979\u3002\u201d \u3000\u3000\u201c\u65e2\u7136\u4e0d\u6740\uff0c\u4e3a\u4ec0\u4e48\u53c8\u4e0d\u80af\u653e\uff1f\u201d \u3000\u3000\u201c\u6211\u662f\u8bf4\u4e0d\u7528\u5251\u6740\u5979\uff0c\u800c\u662f\u7528\u522b\u7684\u65b9\u6cd5\u8ba9\u5979\u6b7b\uff0c\u597d\u597d\u7ed9\u5979\u7559\u4e2a\u5168\u5c38\uff0c\u8fd9\u6837\u505a\u5f88\u5bf9\u5f97\u4f4f\u4f60 \u4e86\u3002\u201d \u3000\u3000\u201c\u8001\u592b\u662f\u8ddf\u4f60\u8c08\u6b63\u7ecf\u7684\u3002\u201d \u3000\u3000\u201c\u6b7b\u5f53\u7136\u662f\u6b63\u7ecf\u7684\uff0c\u4e5f\u662f\u4e25\u8083\u7684\uff0c\u54ea\u6709\u4eba\u6b7b\u4e4b\u524d\u8fd8\u5f00\u73a9\u7b11\u7684\u9053\u7406\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u8f7b\u54b3\u2014\u58f0\u9053\uff1a\u201c\u9ea6\u8001\u5f1f\uff0c\u8001\u592b\u4ece\u524d\u5e76\u672a\u4e8f\u5f85\u4e8e\u4f60\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u970d\u76df\u4e3b\uff0c\u4f60\u8fd9\u8bdd\u50cf\u662f\u4eba\u8bb2\u7684\u5417\uff1f\u201d \u3000\u3000\u201c\u4f60\u600e\u4e48\u53c8\u9a82\u8d77\u8001\u592b\u6765\u4e86\uff1f\u201d \u3000\u3000\u201c\u5728\u4e0b\u51e0\u66fe\u9a82\u4f60\uff0c\u662f\u4f60\u81ea\u5df1\u627e\u6328\u9a82\uff01\u201d \u3000\u3000\u201c\u4f60\u8bf4\u8fd9\u8bdd\u2026\u2026\u201d \u3000\u3000\u201c\u970d\u76df\u4e3b\uff0c\u4f60\u660e\u660e\u8981\u6740\u6211\uff0c\u8fde\u4e94\u5b50\u54ed\u5893\u90fd\u8bbe\u7f6e\u597d\u4e86\uff0c\u5728\u573a\u6240\u6709\u7684\u4eba\u90fd\u662f\u4eba\u8bc1\uff0c\u4f60\u5374\u504f\u8bf4 \u5f85\u6211\u5f88\u597d\uff0c\u8fd9\u4e0d\u662f\u7741\u7740\u773c\u775b\u8bf4\u778e\u8bdd\u5417\uff1f\u201d \u3000\u3000\u201c\u4e8b\u60c5\u5df2\u7ecf\u8fc7\u53bb\u4e86\uff0c\u5e0c\u671b\u9ea6\u8001\u5f1f\u522b\u518d\u8ba1\u8f83\uff0e\u201d \u3000\u3000\u201c\u4f60\u5dee\u2014\u70b9\u5c31\u8981\u4e86\u6211\u7684\u547d\uff0c\u5374\u8981\u6211\u4e0d\u8ba1\u8f83\uff0c\u7b80\u76f4\u5c82\u6709\u6b64\u7406\uff01\u201d \u3000\u3000\u201c\u53ea\u8981\u4f60\u80af\u653e\u4e86\u592b\u4eba\uff0c\u54b1\u4eec\u4ec0\u4e48\u6761\u4ef6\u90fd\u597d\u5546\u91cf\uff0e\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u73af\u626b\u5728\u573a\u6240\u6709\u4eba\u2014\u773c\uff0c\u9ad8\u58f0\u9053\uff1a\u201c\u5404\u4f4d\uff0c\u4f60\u4eec\u53ef\u77e5\u9053\u970d\u76df\u4e3b\u4e3a\u4ec0\u4e48\u8981\u6740\u6211\u5417\uff1f\u201d \u3000\u3000\u56db\u4e0b\u91cc\u9e26\u96c0\u65e0\u58f0\uff0c\u8c01\u90fd\u6ca1\u7b54\u8bdd\uff0c\u4e8b\u5b9e\u4e0a\u5728\u8fd9\u79cd\u60c5\u5f62\u4e0b\uff0c\u53c8\u6709\u8c01\u80af\u81ea\u627e\u9ebb\u70e6\uff1f \u3000\u3000\u9ea6\u5c0f\u660e\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u65e2\u7136\u4e0d\u8bb2\u8bdd\uff0c\u90a3\u5c31\u4e0d\u660e\u767d\u4e8b\u60c5\u539f\u56e0\uff0e\u73b0\u5728\u5728\u4e0b\u5c31\u516c\u5f00\u544a\u8bc9\u4f60\u4eec\uff0c\u514d \u5f97\u6211\u6b7b\u4e86\u6ca1\u4eba\u77e5\u9053\uff0e\u201d \u3000\u3000\u53ea\u542c\u949f\u2014\u8c6a\u53eb\u9053\uff1a\u201c\u9ea6\u8001\u5f1f\u4e0d\u5fc5\u8bf4\u4e86\uff0c\u514d\u5f97\u518d\u8ba9\u76df\u4e3b\u96be\u582a\u3002\u76df\u4e3b\u662f\u9886\u8896\u5929\u4e0b\u7eff\u6797\u7684\u4eba\uff0c \u54b1\u4eec\u505a\u90e8\u4e0b\u7684\uff0c\u603b\u8be5\u7ed9\u4ed6\u8001\u4eba\u5bb6\u7559\u70b9\u9762\u5b50\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4f60\u4eec\u662f\u4ed6\u7684\u90e8\u4e0b\uff0c\u6211\u73b0\u5728\u662f\u53db\u9006\uff0c\u65e9\u5df2\u4e0d\u662f\u4ed6\u7684\u90e8\u4e0b\u4e86\u3002\u4ed6\u8981\u6740\u6211\uff0c\u6211\u8fd8 \u7ed9\u4ed6\u7559\u9762\u5b50\uff0e\u5929\u4e0b\u6709\u8fd9\u79cd\u9053\u7406\u5417\uff1f\u201d \u3000\u3000\u4ed6\u8bed\u6c14\u7565\u987f\uff0c\u63a5\u7740\u63d0\u9ad8\u58f0\u97f3\u9053\uff1a\u201c\u73b0\u5728\u6211\u544a\u8bc9\u5927\u5bb6\uff0c\u4ed6\u662f\u4e3a\u4e86\u4e00\u4e2a\u5973\u4eba\u8981\u6740\u6211\uff0e\u5982\u679c\u8fd9 \u4e2a\u5973\u4eba\u662f\u4ed6\u7684\u538b\u5be8\u592b\u4eba\uff0e\u90a3\u8fd8\u60c5\u6709\u53ef\u539f\uff0c\u504f\u504f\u8fd9\u5973\u4eba\u5374\u662f\u4e2a\u6beb\u65e0\u5ec9\u803b\u7684\u81ed\u5a4a\u5b50\uff0e\u201d \u3000\u3000\u949f\u4e00\u8c6a\u6025\u9053\uff1a\u201c\u5c0f\u660e\uff0c\u4e0d\u5fc5\u518d\u8bf4\u4e86\u5427\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u6211\u4e3a\u4ec0\u4e48\u4e0d\u8bf4\uff1f\u8fd9\u4e2a\u5a4a\u5b50\u73b0\u5728\u5728\u4e1c\u6d77\u9662\u3002\u5927\u5bb6\u5f53\u7136\u77e5\u9053\u662f\u8c01\uff0c\u970d\u76df\u4e3b\u6628\u665a \u53d1\u4e0b\u4ee4\u724c\uff0c\u8981\u6211\u53bb\u5ad6\u90a3\u81ed\u5a4a\u5b50\uff0c\u6211\u4e0d\u5ad6\uff0c\u4ed6\u5c31\u8981\u6740\u6211\u3002 \u3000\u3000\u5927\u5bb6\u60f3\u60f3\u770b\uff0c\u54b1\u4eec\u8ff7\u8e2a\u8c37\u7eff\u6797\u603b\u5be8\u53ef\u6709\u8fd9\u79cd\u89c4\u77e9\uff0c\u82e5\u8fd9\u4e8b\u4e00\u65e6\u4f20\u626c\u51fa\u53bb\uff0c\u5c82\u4e0d\u88ab\u4eba\u7b11\u6389 \u5927\u7259\uff1f \u3000\u3000\u970d\u76df\u4e3b\u7684\u4ee4\u724c\uff0c\u5c45\u7136\u662f\u5ad6\u5a4a\u5b50\u7528\u7684\uff0c\u8fde\u4eba\u5bb6\u80af\u4e0d\u80af\u5ad6\uff0c\u4ed6\u4e5f\u8981\u7ba1\u3001\u7b80\u76f4\u662f\u6ed1\u5929\u4e0b\u4e4b\u5927 \u7a3d\uff01\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u970d\u5143\u4f3d\u65e9\u5df2\u9762\u8272\u618b\u5f97\u6709\u5982\u732a\u809d\uff0e\u8dfa\u4e86\u8dfa\u811a\u9053\uff1a\u201c\u9ea6\u8001\u5f1f\uff0c\u4f60\u5c31\u5c11\u8bf4\u4e24\u53e5\u597d\u4e0d\u597d\uff1f \u65e2\u7136\u8001\u592b\u5904\u7f6e\u4e0d\u5f53\uff0c\u5f53\u7136\u4e0d\u4f1a\u518d\u6740\u4f60\u3002\u73b0\u5728\u8be5\u662f\u54b1\u4eec\u597d\u597d\u5546\u91cf\u7684\u65f6\u95f4\u4e86\uff01\u201d \u3000\u3000\u201c\u8981\u600e\u4e48\u6837\uff0c\u4f60\u5148\u8bf4\u8bf4\u770b\uff01\u201d \u3000\u3000\u201c\u53ea\u8981\u4f60\u80af\u653e\u4e86\u592b\u4eba\uff0c\u8fc7\u53bb\u7684\u4e8b\uff0c\u4e00\u7b14\u52fe\u9500\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e48\u8bf4\u4f60\u662f\u4e0d\u6740\u6211\u4e86\uff0c\u90a3\u4e48\u4f60\u662f\u8981\u8d76\u8d70\u6211\uff1f\u201d \u3000\u3000\u201c\u4e00\u5207\u6089\u542c\u5c0a\u4fbf\uff0c\u4f60\u8981\u8d70\uff0c\u5c3d\u53ef\u79bb\u5f00\u8ff7\u8e2a\u8c37\uff1b\u5982\u679c\u4ecd\u8981\u7559\u5728\u8fd9\u91cc\uff0c\u8001\u592b\u4e5f\u81f3\u8868\u6b22\u8fce\u3002\u201d \u3000\u3000\u201c\u4f60\u7a7a\u53e3\u8bf4\u767d\u8bdd\u600e\u80fd\u7b97\u6570\uff1f\u201d \u3000\u3000\u201c\u8001\u592b\u521a\u624d\u7684\u8bdd\uff0e\u5728\u573a\u4e4b\u4eba\u7686\u5df2\u542c\u5230\uff0c\u600e\u80fd\u4e0d\u7b97\u6570\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u51b7\u51b7\u4e00\u7b11\u9053\uff1a\u201c\u8a00\u51fa\u5982\u98ce\uff0c\u7a7a\u53e3\u65e0\u51ed\u3002\u6211\u5fc5\u987b\u6709\u8bc1\u636e\u634f\u5728\u624b\u91cc\u624d\u884c\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u987f\u4e86\u2014\u987f\u9053\uff1a\u201c\u4f60\u8981\u4ec0\u4e48\u8bc1\u636e\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4f60\u81ea\u5df1\u80fd\u5199\u4e00\u5f20\u5b57\u636e\u7ed9\u6211\u6700\u597d\uff0c\u5426\u5219\u5c31\u7531\u65b9\u624d\u90a3\u4f4d\u8bfb\u72b6\u7684\u4e01\u7237\u5199\uff0c\u518d\u7531\u4f60 \u7b7e\u540d\u753b\u62bc\uff0c\u4ea4\u7ed9\u6211\uff0c\u5c31\u4eea\u5f0f\u5b8c\u6210\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u4e3a\u4e86\u82d7\u7d20\u5170\u7684\u5b89\u5168\uff0c\u4e0d\u6562\u591a\u803d\u8bef\u65f6\u95f4\uff0c\u5fd9\u547d\u5df4\u5929\u4e49\u9053\uff1a\u201c\u628a\u4e01\u5e08\u7236\u627e\u6765\uff01\u201d \u3000\u3000\u4e01\u5e08\u7236\u5f88\u5feb\u4fbf\u88ab\u5df4\u5929\u4e49\u5e26\u5230\u3002 \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u8001\u592b\u73b0\u5728\u8981\u4f60\u5199\u2014\u5f20\u5b57\u636e\u4ea4\u7ed9\u9ea6\u5934\u9886\u3002\u201d \u3000\u3000\u4e01\u5e08\u7237\u9053\uff1a\u201c\u65b9\u624d\u5c5e\u4e0b\u5df2\u7ecf\u5728\u5927\u5385\u5185\u542c\u6e05\u695a\u4e86\uff0c\u9a6c\u4e0a\u5c31\u9075\u7167\u4f60\u8001\u4eba\u5bb6\u7684\u610f\u601d\u5199\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u7740\u56de\u5230\u5927\u5385\uff0c\u76cf\u8336\u5de5\u592b\u4e4b\u540e\uff0e\u4fbf\u628a\u5199\u597d\u7684\u5b57\u636e\u4ea4\u7ed9\u970d\u5143\u4f3d\u9053\uff1a\u201c\u5c5e\u4e0b\u5df2\u5e26\u6765\u7b14\u781a\uff0c \u76df\u4e3b\u8ba4\u4e3a\u65e0\u8bef\u540e\u5c31\u8bf7\u7b7e\u540d\u753b\u62bc\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u5306\u5306\u770b\u8fc7\u540e\uff0c\u518d\u63a5\u8fc7\u7b14\uff0e\u7b7e\u4e86\u540d\u753b\u4e86\u62bc\u9053\uff1a\u201c\u9001\u8fc7\u53bb\u4ea4\u9ea6\u5934\u9886\u6536\u6267\u3002\u201d \u3000\u3000\u5374\u542c\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u522b\u5fd9\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u4e00\u6123\u9053\uff1a\u201c\u83ab\u975e\u9ea6\u8001\u5f1f\u53c8\u8981\u53d8\u5366\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u970d\u76df\u4e3b\u592a\u591a\u5fc3\u4e86\uff0c\u6211\u662f\u51c6\u5907\u8bf7\u4f60\u5429\u5490\u4e01\u5e08\u7237\u628a\u5b57\u636e\u5185\u5bb9\u5f53\u4f17\u5ba3\u8bb2\u4e00\u904d\uff0c\u4ee5 \u4fbf\u8ba9\u5927\u5bb6\u90fd\u542c\u660e\u767d\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u5fd9\u9053\uff1a\u201c\u4e01\u5e08\u7237\u9a6c\u4e0a\u5ba3\u8bfb\u3002\u201d \u3000\u3000\u8c01\u77e5\u9ea6\u5c0f\u660e\u5374\u53c8\u53eb\u9053\uff1a\u201c\u522b\u5fd9\uff0c\u4f60\u8ba9\u4ed6\u600e\u6837\u5ba3\u8bfb\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u5f53\u7136\u662f\u7167\u7740\u4e0a\u9762\u5199\u7684\uff0c\u3000\u4e00\u4e2a\u5b57\u2014\u4e2a\u5b57\u7684\u5ff5\u3002\u201d \u3000\u3000\u201c\u5e9f\u8bdd\uff0c\u8c01\u80fd\u540c\u65f6\u5ff5\u51fa\u4e24\u4e2a\u5b57\u6765\uff01\u201d \u3000\u3000\u201c\u4f60\u7a76\u7adf\u8981\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u201c\u8981\u4ed6\u7528\u5b98\u8bdd\u5ff5\uff0c\u81f3\u5c11\u6bcf\u53e5\u6bcf\u4e2a\u4eba\u90fd\u542c\u6e05\u695a\uff0c\u522b\u518d\u8bfb\u2018\u796d\u6587\u2019\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u8e59\u4e86\u8e59\u7709\uff0c\u4fa7\u8138\u95ee\u9053\uff1a\u201c\u4e01\u5e08\u7236\uff0e\u4f60\u5230\u5e95\u4f1a\u4e0d\u4f1a\u8bb2\u5b98\u8bdd\uff1f\u201d \u3000\u3000\u4e01\u5e08\u7237\u54a7\u4e86\u54a7\u5634\u89d2\u9053\uff1a\u201c\u5c5e\u4e0b\u5728\u5916\u95ef\u8361\u8fd9\u4e48\u591a\u5e74\uff0c\u5f53\u7136\u4f1a\u8bb2\u3002\u201d \u3000\u3000\u201c\u4f1a\u8bb2\u4e3a\u4ec0\u4e48\u521a\u624d\u4e0d\u80af\u8bb2\uff1f\u201d \u3000\u3000\u201c\u8bfb\u7f6a\u72b6\u548c\u796d\u6587\u4e00\u6837\uff0c\u5fc5\u987b\u8ba9\u4eba\u5e26\u70b9\u542c\u4e0d\u61c2\uff0c\u624d\u663e\u5f97\u6709\u5b66\u95ee\uff0e\u591f\u610f\u601d\u3002\u201d \u3000\u3000\u5ffd\u542c\u738b\u5927\u5eb7\u543c\u9053\uff1a\u201c\u53bb\u4f60\u5976\u5976\u7684\u718a\uff01\u5f85\u4f1a\u513f\u4ffa\u628a\u5200\u6309\u5728\u4f60\u8116\u5b50\u4e0a\uff0c\u770b\u4f60\u8fd9\u72d7\u5a18\u517b\u7684\u6c42\u9976 \u65f6\u8fd8\u662f\u5426\u50cf\u8bfb\u796d\u6587\u4e00\u6837\uff01\u201d \u3000\u3000\u4e01\u5e08\u7237\u54ea\u6562\u56de\u5634\uff0c\u7acb\u5373\u5c55\u5f00\u5b57\u636e\uff0c\u9ad8\u58f0\u6717\u8bf5\u8d77\u6765\u3002 \u3000\u3000\u8fd9\u6b21\u4ed6\u8bfb\u8d77\u6765\u679c\u7136\u6e05\u695a\u5f97\u591a\uff0c\u867d\u7136\u8fd8\u5e26\u70b9\u571f\u8154\u571f\u8c03\uff0c\u81f3\u5c11\u5df2\u8ba9\u4eba\u542c\u61c2\u3002 \u3000\u3000\u4e01\u5e08\u7236\u8bfb\u5b8c\u540e\uff0c\u786c\u7740\u5934\u76ae\u6218\u6218\u5162\u5162\u7684\u6765\u5230\u9ea6\u5c0f\u660e\u8ddf\u524d\uff0e\u5c06\u5b57\u636e\u53cc\u624b\u9012\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u63a2\u624b\u63a5\u8fc7\uff0c\u5306\u5306\u770b\u4e86\u4e00\u904d\uff0e\u7136\u540e\u6298\u8d77\u63e3\u8fdb\u6000\u91cc\u3002 \u3000\u3000\u970d\u5143\u4f3d\u50ac\u4fc3\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u8fd8\u4e0d\u628a\u592b\u4eba\u653e\u56de\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4f60\u653e\u4e00\u767e\u4e8c\u5341\u4e2a\u5fc3\u3002\u970d\u592b\u4eba\uff0c\u8bf7\u56de\u5427\uff01\u201d \u3000\u3000\u8bf4\u7740\u6536\u8d77\u4e86\u5251\u3002 \u3000\u3000\u5f53\u82d7\u7d20\u5170\u5c1a\u672a\u8d70\u56de\u970d\u5143\u4f3d\u8ddf\u524d\uff0c\u7a81\u89c1\u9ea6\u5c0f\u660e\u548c\u84dd\u886b\u8499\u9762\u4eba\u9f50\u9f50\u7eb5\u8eab\u800c\u8d77\uff0c\u63a0\u8fc7\u949f\u4e00\u8c6a\u7b49 \u4eba\u5934\u9876\u4e0a\u65b9\u540e\uff0e\u5f88\u5feb\u4fbf\u5728\u8fdc\u5904\u6d88\u901d\u4e0d\u89c1\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u548c\u4f59\u4ea6\u4e50\u3001\u738b\u5927\u5eb7\u7b49\u4eba\u60f3\u53eb\u5524\uff0c\u5374\u5df2\u6765\u4e0d\u53ca\u4e86\u3002 \u3000\u3000\u8fd9\u53d8\u5316\u970d\u5143\u4f3d\u4e5f\u6709\u4e9b\u610f\u5916\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u9ad8\u58f0\u9053\uff1a\u201c\u76df\u4e3b\uff0c\u4f60\u53ef\u662f\u4e0d\u51c6\u9ea6\u5c0f\u660e\u7559\u5728\u8c37\u91cc\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u5c82\u6709\u6b64\u7406\uff01\u521a\u624d\u53cc\u65b9\u6240\u8bf4\u7684\u8bdd\uff0e\u5927\u5bb6\u90fd\u542c\u5f97\u6e05\u6e05\u695a\u695a\uff0e\u8001\u592b\u5e76\u6ca1\u4e0d\u51c6\u4ed6\u7559 \u5728\u8c37\u91cc\u3002\u201d \u3000\u3000\u201c\u90a3\u4ed6\u4e3a\u4ec0\u4e48\u8981\u8d70\uff1f\u201d \u3000\u3000\u201c\u4ed6\u8981\u8d70\u662f\u4ed6\u7684\u4e8b\uff0c\u8001\u592b\u54ea\u91cc\u6e05\u695a\u3002\u201d \u3000\u3000\u201c\u5982\u679c\u6709\u4e00\u5929\u5c5e\u4e0b\u628a\u4ed6\u627e\u56de\u6765\u5462\uff1f\u201d \u3000\u3000\u201c\u8001\u592b\u6b63\u5e0c\u671b\u80fd\u628a\u4ed6\u627e\u56de\u6765\uff0c\u9ea6\u5c0f\u660e\u6b66\u529f\u51fa\u4f17\uff0c\u4ed6\u8d70\u4e86\u6b63\u662f\u8ff7\u8e2a\u8c37\u7684\u635f\u5931\u3002\u201d \u3000\u3000\u7fa4\u96c4\u5728\u5927\u5385\u5916\u6563\u53bb\u4e0d\u4e45\uff0c\u5929\u661f\u9662\u7684\u5bc6\u5ba4\u91cc\uff0c\u4fbf\u5f00\u59cb\u4e86\u2014\u9879\u79d8\u5bc6\u4f1a\u8bae\u3002 \u3000\u3000\u4e3b\u6301\u4eba\u81ea\u7136\u662f\u970d\u5143\u4f3d\uff0e\u53c2\u4e0e\u7684\u4eba\u53ea\u6709\u5cad\u5357\u4e8c\u5947\u5df4\u5929\u4e49\u548c\u5b8b\u5929\u94ce\u4e8c\u4eba\uff0e\u8fde\u82d7\u7d20\u5170\u90fd\u4e0d\u5728\u573a\u3002 \u3000\u3000\u970d\u5143\u4f3d\u731b\u4e00\u8dfa\u811a\u9053\uff1a\u201c\u8001\u592b\u5728\u9ed1\u767d\u4e24\u9053\u95ef\u8361\u4e86\u51e0\u5341\u5e74\uff0c\u4eca\u5929\u8fd9\u8ddf\u6597\u683d\u5f97\u5b9e\u5728\u592a\u5927\uff0c\u771f\u4e0d \u77e5\u4eca\u540e\u8be5\u5982\u4f55\u9886\u5bfc\u8ff7\u8e2a\u8c37\u6240\u6709\u5f1f\u5144\uff01\u201d \u3000\u3000\u5df4\u5929\u4e49\u966a\u7740\u8c04\u7b11\u9053\uff1a\u201c\u4e8b\u60c5\u5df2\u7ecf\u8fc7\u53bb\u4e86\uff0c\u76df\u4e3b\u518d\u70e6\u607c\u6709\u4f55\u7528\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u597d\u5728\u90a3\u5c0f\u5b50\u5df2\u7ecf\u8d70\u4e86\uff0e\u603b\u7b97\u53bb\u4e86\u4e00\u5bb3\u3002\u201d \u3000\u3000\u5df4\u5929\u4e49\u8138\u8272\u9634\u6c89\u4e86\u534a\u54cd\uff0c\u5ffd\u7136\u5e72\u54b3\u4e24\u58f0\uff0e\u4f4e\u58f0\u9053\uff1a\u201c\u76df\u4e3b\uff0c\u5c5e\u4e0b\u6709\u4ef6\u4e8b\uff0c\u4e0d\u77e5\u8be5\u4e0d\u8be5 \u8bb2\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u7709\u5934\u4e00\u76b1\u9053\uff1a\u201c\u8001\u592b\u8bf7\u4f60\u4eec\u4e24\u4f4d\u6765\uff0c\u5c31\u662f\u8981\u5546\u8bae\u4e8b\u60c5\uff0c\u6709\u4e8b\u4f55\u5fc5\u541e\u541e\u5410\u5410\uff1f\u201d \u3000\u3000\u5df4\u5929\u4e49\u987f\u4e86\u2014\u987f\u9053\uff1a\u201c\u6709\u9053\u662f\u2018\u758f\u4e0d\u95f4\u4eb2\u2019\uff0c\u5c5e\u4e0b\u60f3\u8bb2\u7684\u8bdd\u4e5f\u8bb8\u4e0d\u59a5\uff0c\u8fd8\u662f\u4e0d\u8bb2\u597d\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u4e24\u773c\u8f6c\u4e86\u51e0\u8f6c\u9053\uff1a\u201c\u5c82\u6709\u6b64\u7406\uff01\u8001\u592b\u5bf9\u4f60\uff0e\u4ec0\u4e48\u65f6\u5019\u89c1\u5916\u8fc7\uff01\u201d \u3000\u3000\u5df4\u5929\u4e49\u8fd9\u624d\u6b32\u8a00\u53c8\u6b62\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0e\u5c5e\u4e0b\u4fbf\u6597\u80c6\u8bb2\u4e86\u3002\u4eca\u5929\u7684\u4e8b\u60c5\uff0e\u5c5e\u4e0b\u5f88\u6000\u7591\u6bdb\u75c5 \u51fa\u5728\u592b\u4eba\u8eab\u4e0a\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u4f60\u662f\u8bf4\uff1f\u2026\u2026\u201d \u3000\u3000\u5df4\u5929\u4e49\u9053\uff1a\u201c\u4e5f\u8bb8\u662f\u5c5e\u4e0b\u591a\u5fc3\u4e86\uff0c\u4ee4\u5929\u7684\u4e8b\u60c5\u6240\u4ee5\u4f1a\u5f04\u5230\u8fd9\u79cd\u5730\u6b65\uff0e\u4e0d\u5916\u662f\u592b\u4eba\u843d\u5165\u9ea6 \u5c0f\u660e\u624b\u4e2d\u3002\u76df\u4e3b\u53ef\u66fe\u60f3\u5230\u592b\u4eba\u4e3a\u4ec0\u4e48\u4f1a\u843d\u5728\u4ed6\u7684\u624b\u4e2d\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u4f3c\u6709\u6240\u609f\u9053\uff1a\u201c\u83ab\u975e\u4f60\u6000\u7591\u592b\u4eba\u662f\u6545\u610f\u8fd9\u6837\u505a\u7684\uff1f\u201d \u3000\u3000\u5df4\u5929\u4e49\u9053\uff1a\u201c\u5e76\u975e\u5c5e\u4e0b\u591a\u5fc3\uff0e\u76df\u4e3b\u5e94\u8be5\u60f3\u5230\u592b\u4eba\u548c\u67cf\u9f84\u9662\u65b9\u9762\u7684\u5173\u7cfb\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u6c89\u9ed8\u4e86\u597d\u4e45\uff0c\u7ec8\u4e8e\u70b9\u4e86\u70b9\u5934\u9053\uff1a\u201c\u4f60\u8bf4\u7684\u6709\u7406\uff0e\u4f46\u4e3a\u4e86\u548c\u5979\u662f\u592b\u59bb\u5173\u7cfb\uff0c\u8fd9\u4e8b\u8001 \u592b\u53c8\u4e0d\u4fbf\u8ffd\u95ee\u3002\u201d \u3000\u3000\u5df4\u5929\u4e49\u9053\uff1a\u201c\u4ee5\u540e\u8bf7\u76df\u4e3b\u591a\u591a\u7559\u610f\uff0c\u51e1\u662f\u548c\u67cf\u9f84\u9662\u65b9\u9762\u626f\u4e0a\u5173\u7cfb\u7684\u673a\u5bc6\u5927\u4e8b\uff0c\u6700\u597d\u5c11\u8ba9 \u592b\u4eba\u53c2\u4e0e\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u8001\u592b\u4e0d\u4f1a\u518d\u5403\u7b2c\u4e8c\u6b21\u95f7\u4e8f\u3002\u201d \u3000\u3000\u201c\u5173\u4e8e\u5bf9\u4ed8\u67cf\u9f84\u9662\u65b9\u9762\uff0c\u76df\u4e3b\u53ef\u6709\u4ec0\u4e48\u65b0\u7684\u6253\u7b97\uff1f\u201d \u3000\u3000\u201c\u67cf\u9f84\u9662\u53bb\u4e86\u9ea6\u5c0f\u660e\uff0c\u5df2\u5bf9\u54b1\u4eec\u7684\u5a01\u80c1\u5927\u51cf\uff0c\u949f\u4e00\u8c6a\u90a3\u822c\u4eba\uff0c\u4eca\u540e\u53ef\u80fd\u4e0d\u6562\u518d\u56a3\u5f20\u4e86\u3002\u201d \u3000\u3000\u5df4\u5929\u4e49\u6447\u6447\u5934\uff0c\u9634\u68ee\u68ee\u4e00\u7b11\u9053\uff1a\u201c\u4e0d\u7ba1\u4ee5\u540e\u4ed6\u4eec\u6562\u4e0d\u6562\u518d\u56a3\u5f20\uff0c\u4f46\u76df\u4e3b\u82e5\u60f3\u628a\u90a3\u4e9b\u4eba\u5f15 \u4e3a\u5fc3\u8179\uff0c\u53ea\u6015\u8fd8\u5927\u4e0d\u5bb9\u6613\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u563f\u563f\u7b11\u9053\uff1a\u201c\u73b0\u5728\u6b63\u5728\u7528\u4eba\u4e4b\u9645\uff0c\u4e3a\u4e86\u5185\u90e8\u56e2\u7ed3\uff0c\u8001\u592b\u53ea\u662f\u6682\u65f6\u548c\u4ed6\u4eec\u59a5\u534f\uff0c\u65f6 \u673a\u4e00\u5230\uff0e\u5f53\u7136\u4f1a\u628a\u4ed6\u4eec\u96c6\u4f53\u526a\u9664\u3002\u201d \u3000\u3000\u4e00\u76f4\u672b\u5f00\u53e3\u7684\u62d8\u9b42\u7d22\u5b8b\u5929\u94ce\u5ffd\u7136\u8f7b\u54b3\u4e86\u58f0\u9053\uff1a\u201c\u5c5e\u4e0b\u4e5f\u5e0c\u671b\u8fdb\u8a00\u2014\u4ef6\u4e8b\uff0c\u4e5f\u662f\u4e0d\u77e5\u5f53\u8bb2 \u4e0d\u5f53\u8bb2\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u4f60\u600e\u4e48\u4e5f\u6765\u8fd9\u4e00\u5957\uff0c\u6709\u8bdd\u5c31\u8bb2\uff0c\u4f55\u5fc5\u518d\u95ee\uff1f\u201d \u3000\u3000\u5b8b\u5929\u94ce\u987f\u4e86\u4e00\u987f\uff0c\u624d\u9053\uff1a\u201c\u5c5e\u4e0b\u8bf4\u7684\u662f\u5173\u4e8e\u8bb8\u4ed9\u5b50\u2026\u2026\u201d \u3000\u3000\u970d\u5143\u4f3d\u5fd9\u622a\u4f4f\u5b8b\u5929\u94ce\u672a\u5b8c\u4e4b\u8a00\uff0c\u6b49\u7136\u7b11\u9053\uff1a\u201c\u8fd9\u4e8b\u5c31\u662f\u4f60\u4e0d\u63d0\uff0c\u8001\u592b\u4e5f\u89c9\u5f97\u5bf9\u4f60\u4e24\u4f4d\u5f88 \u62b1\u6b49\u3002\u201d \u3000\u3000\u5b8b\u5929\u94ce\u6014\u4e86\u6014\u9053\uff1a\u201c\u76df\u4e3b\u5bf9\u5c5e\u4e0b\u4eec\u62b1\u7684\u4ec0\u4e48\u6b49\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u4e1c\u6d77\u9662\u81ea\u6210\u7acb\u4ee5\u6765\uff0e\u8001\u592b\u5171\u53d1\u51fa\u56db\u6b21\u6170\u52b3\u4ee4\u724c\uff0e\u5bf9\u8c61\u90fd\u662f\u67cf\u9f84\u9662\u7684\u4eba\uff0c\u4e24 \u4f4d\u5230\u73b0\u5728\u8fd8\u6ca1\u8f6e\u5230\uff0c\u8001\u592b\u600e\u80fd\u4e0d\u62b1\u6b49\u3002\u5176\u5b9e\u8001\u592b\u662f\u53e6\u6709\u7528\u610f\uff0c\u4f60\u4eec\u4e8c\u4f4d\u5343\u4e07\u522b\u4ecb\u610f\u3002\u201d \u3000\u3000\u5b8b\u5929\u94ce\u5e72\u54b3\u4e86\u51e0\u58f0\u9053\uff1a\u201c\u76df\u4e3b\u8bef\u4f1a\u4e86\uff0c\u5c5e\u4e0b\u5e76\u975e\u8fd9\u610f\u601d\u3002\u201d \u3000\u3000\u201c\u4f60\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u201c\u5c5e\u4e0b\u662f\u8bf4\u4eca\u5929\u8fd9\u4e8b\u7684\u7978\u6839\uff0c\u5b8c\u5168\u662f\u7531\u8bb8\u4ed9\u5b50\u8eab\u4e0a\u800c\u8d77\uff0c\u5982\u679c\u4e0d\u8bbe\u7acb\u4e1c\u6d77\u9662\uff0c\u6839\u672c\u4e0d\u53ef \u80fd\u53d1\u751f\u9ea6\u5c0f\u660e\u4e8b\u4ef6\u3002\u201d \u3000\u3000\u201c\u8bf4\u7684\u4e5f\u662f\uff0c\u5b8b\u8001\u5f1f\u7684\u610f\u601d\u8be5\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u201c\u5c5e\u4e0b\u6597\u80c6\u8fdb\u8a00\uff0c\u6700\u597d\u628a\u4e1c\u6d77\u9662\u5173\u95ed\uff0c\u50cf\u8bb8\u4ed9\u5b50\u90a3\u79cd\u5973\u4eba\u672c\u6765\u5c31\u662f\u7978\u6c34\uff0c\u5982\u679c\u76df\u4e3b\u540c\u610f\uff0c \u5c31\u628a\u5979\u9010\u51fa\u8c37\u53bb\u6216\u8005\u628a\u5979\u6d3b\u57cb\u6389\u3002\u201d \u3000\u3000\u201c\u4e1c\u6d77\u9662\u6682\u65f6\u5173\u95ed\u6211\u540c\u610f\uff0e\u81f3\u4e8e\u628a\u5979\u9010\u51fa\u8c37\u53bb\uff0e\u90a3\u5012\u4e0d\u5fc5\u3002\u56e0\u4e3a\u5979\u8fd8\u6709\u5229\u7528\u4ef7\u503c\uff0c\u4e5f\u8bb8 \u4f60\u4eec\u8fd8\u4e0d\u77e5\u9053\uff0c\u5979\u73b0\u5728\u5df2\u5b8c\u5168\u5728\u8001\u592b\u7684\u63a7\u5236\u4e4b\u4e0b\u3002\u201d \u3000\u3000\u201c\u76df\u4e3b\u662f\u600e\u6837\u63a7\u5236\u5979\u7684\uff1f\u201d \u3000\u3000\u201c\u8001\u592b\u5df2\u6697\u4e2d\u7ed9\u5979\u670d\u4e0b\u4e00\u79cd\u836f\u7269\uff0c\u82e5\u4e03\u65e5\u4e4b\u5185\u4e0d\u670d\u89e3\u836f\uff0c\u5c31\u4f1a\u8179\u75db\u96be\u5fcd\uff0c\u65e0\u6cd5\u884c\u52a8\uff0e\u201d \u3000\u3000\u201c\u8fd9\u4e8b\u8bb8\u4ed9\u5b50\u77e5\u9053\u4e0d\u77e5\u9053\uff1f\u201d \u3000\u3000\u201c\u8001\u592b\u5f53\u7136\u4e0d\u4f1a\u8ba9\u5979\u77e5\u9053\u3002\u201d \u3000\u3000\u201c\u90a3\u4e48\u5979\u6bd2\u53d1\u65f6\u53c8\u600e\u4f1a\u4e0d\u77e5\u9053\uff1f\u201d \u3000\u3000\u201c\u8001\u592b\u8ba1\u7b97\u65f6\u95f4\uff0e\u6bcf\u6b21\u90fd\u5728\u5979\u6bd2\u53d1\u524d\uff0c\u6d3e\u4eba\u6697\u4e2d\u5728\u5979\u8336\u4e2d\u653e\u8fdb\u89e3\u836f\uff0c\u56e0\u4e4b\u5979\u624d\u81f3\u4eca\u6beb\u4e0d \u77e5\u60c5\u3002\u201d \u3000\u3000\u4e09\u4eba\u53c8\u5bc6\u8c0b\u4e86\u534a\u54cd\uff0c\u624d\u5404\u81ea\u6563\u53bb\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u7b49\u4eba\u4e5f\u5728\u970d\u5143\u4f3d\u56de\u5230\u5929\u661f\u9662\u4e0d\u4e45\uff0e\u56de\u5230\u67cf\u9f84\u9662\u3002 \u3000\u3000\u8def\u4e0a\uff0c\u4ed6\u4eec\u867d\u4e3a\u9ea6\u5c0f\u660e\u9003\u8fc7\u5927\u52ab\u800c\u6df1\u611f\u5e86\u5e78\uff0c\u4f46\u53e6\u4e00\u65b9\u9762\uff0c\u4e5f\u4e3a\u9ea6\u5c0f\u660e\u7684\u79bb\u53bb\u800c\u6005\u60d8\u4e0d \u5df2\u3002\u4e0d\u4f46\u662f\u611f\u60c5\u65b9\u9762\u7684\u5931\u843d\uff0c\u540c\u65f6\u4e5f\u7b49\u4e8e\u5931\u53bb\u4e86\u51ed\u6043\u3002 \u3000\u3000\u4f59\u4ea6\u4e50\u5401\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u8bf8\u4f4d\u53ef\u770b\u51fa\u4eca\u5929\u662f\u82d7\u59d1\u5a18\u6551\u4e86\u9ea6\u5c0f\u5144\u5f1f\uff1f\u201d \u3000\u3000\u738b\u5927\u5eb7\u9053\uff1a\u201c\u4ffa\u662f\u4e2a\u7c97\u4eba\uff0c\u4e5f\u770b\u51fa\u4e86\u95e8\u9053\u3002\u4ece\u524d\u4ffa\u8fd8\u4ee5\u4e3a\u82d7\u59d1\u5a18\u771f\u662f\u53d8\u4e86\u5fc3\uff0c\u73b0\u5728\u624d\u660e \u767d\uff0c\u5979\u8ddf\u4e86\u970d\u5143\u4f3d\uff0c\u4e00\u5b9a\u6709\u4ec0\u4e48\u82e6\u2026\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u63a5\u9053\uff1a\u201c\u4f60\u53ef\u662f\u8bf4\u6709\u4ec0\u4e48\u82e6\u8877\uff1f\u201d \u3000\u3000\u738b\u5927\u5eb7\u9053\uff1a\u201c\u4e0d\u9519\uff0e\u5c31\u662f\u8fd9\u4e24\u4e2a\u5b57\u3002\u4ffa\u867d\u7136\u6ca1\u8bfb\u8fc7\u51e0\u5e74\u4e66\uff0c\u6709\u4e9b\u6587\u7ec9\u7ec9\u7684\u8bdd\u8bf4\u4e0d\u4e0a\u6765\uff0c \u4f46\u5374\u61c2\u5f97\u610f\u601d\u3002\u201d \u3000\u3000\u4ed6\u63a5\u7740\u53c8\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u4ffa\u4eca\u5929\u4e0d\u660e\u767d\u7684\uff0c\u90a3\u84dd\u886b\u8499\u9762\u4eba\u53c8\u662f\u8c01\u5462\uff1f\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u9053\uff1a\u201c\u8fd9\u4eba\u6628\u665a\u4e5f\u66fe\u51fa\u73b0\u8fc7\uff0e\u4e5f\u5e2e\u8fc7\u9ea6\u8001\u5f1f\u6740\u9000\u5df4\u5929\u4e49\u548c\u5b8b\u5929\u94ce\uff0e\u504f\u504f\u8fde\u9ea6\u8001\u5f1f \u4e5f\u4e0d\u77e5\u9053\u4ed6\u662f\u8c01\u3002\u201d \u5367\u9f99\u751f\u300a\u5999\u7edd\u5929\u9999\u300b \u7b2c\u5341\u516b\u56de\u3000\u6c34\u706b\u4e0d\u5bb9 \u3000\u3000\u8fdb\u5165\u9662\u95e8\uff0c\u4e0d\u7531\u6240\u6709\u7684\u4eba\u90fd\u662f\u4e00\u9635\u60ca\u559c\uff0c\u53ea\u89c1\u5ba2\u5385\u91cc\u5750\u7740\u4e00\u4e2a\u4eba\uff0c\u8d6b\u7136\u662f\u9ea6\u5c0f\u660e\uff0e \u3000\u3000\u7fa4\u8c6a\u5728\u559c\u51fa\u671b\u5916\u4e4b\u4e0b\uff0c\u7acb\u5373\u5feb\u6b65\u62e5\u8fdb\u4e86\u5ba2\u5385\u3002 \u3000\u3000\u8fd8\u662f\u738b\u5927\u5eb7\u7b2c\u2014\u4e2a\u5f00\u53e3\u53eb\u9053\uff1a\u201c\u539f\u6765\u9ea6\u5c0f\u5144\u5f1f\u6ca1\u8d70\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u90fd\u5728\uff0c\u6211\u4e3a\u4ec0\u4e48\u8981\u8d70\u3002\u201d \u3000\u3000\u738b\u5927\u5eb7\u54a7\u5634\u7b11\u9053\uff1a\u201c\u90a3\u592a\u597d\u5566\uff01\u4ffa\u4eec\u65b9\u624d\u5728\u8c08\u8bba\u7740\u4f60\uff0c\u4e0d\u77e5\u4f60\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u6211\u8170\u91cc\u88c5\u7740\u970d\u5143\u4f3d\u7684\u5b57\u636e\uff0c\u8fd8\u6015\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u949f\u4e00\u8c6a\u6b63\u8272\u9053\uff1a\u201c\u4e0d\u7ba1\u5982\u4f55\uff0c\u9ea6\u8001\u5f1f\u4ee5\u540e\u8fd8\u662f\u8981\u8c28\u614e\u5c0f\u5fc3\uff0c\u7edd\u5bf9\u4e0d\u53ef\u518d\u95f9\u4e8b\u3002\u970d\u5143\u4f3d\u9634 \u9669\u8be1\u8bc8\uff0c\u54b1\u4eec\u4e0d\u5f97\u4e0d\u9632\uff0e\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u6211\u4ec0\u4e48\u65f6\u5019\u95f9\u4e8b\u6765\uff1f\u4ee5\u4eca\u5929\u7684\u4e8b\u6765\u8bf4\uff0c\u96be\u9053\u6211\u8fde\u5ad6\u5973\u4eba\u7684\u81ea\u7531\u90fd\u6ca1\u6709\uff1f\u4f60 \u4eec\u8bc4\u8bc4\u7406\uff0c\u5230\u5e95\u662f\u8c01\u627e\u8c01\u7684\u9ebb\u70e6\uff1f\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u9053\uff1a\u201c\u522b\u8bf4\u8fd9\u4e48\u591a\u4e86\uff0c\u73b0\u5728\u5927\u5bb6\u53ea\u60f3\u77e5\u9053\u90a3\u4f4d\u4e3a\u4f60\u52a9\u9635\u7684\u8499\u9762\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u795e\u79d8\u4e00\u7b11\u9053\uff1a\u201c\u63d0\u8d77\u6b64\u4eba\uff0c\u5176\u5b9e\u6211\u65e9\u5c31\u8ba4\u8bc6\u3002\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u4e0d\u60a6\u9053\uff1a\u201c\u65e2\u5df2\u8ba4\u8bc6\uff0c\u4e3a\u4ec0\u4e48\u6628\u665a\u4e0d\u8bb2\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u56e0\u4e3a\u6628\u665a\u5e76\u6ca1\u8ba4\u51fa\u662f\u8c01\u3002\u201d \u3000\u3000\u201c\u90a3\u4e48\u4f60\u662f\u4ec0\u4e48\u65f6\u5019\u8ba4\u51fa\u4ed6\u7684\uff1f\u201d \u3000\u3000\u201c\u4eca\u6668\u4e94\u66f4\u5de6\u53f3\uff0c\u5979\u6765\u6572\u627e\u7684\u7a97\uff0c\u6211\u7a7f\u7a97\u800c\u51fa\u4e00\u770b\uff0c\u624d\u77e5\u9053\u662f\u5979\u3002\u201d \u3000\u3000\u201c\u4ee5\u540e\u5462\uff1f\u201d \u3000\u3000\u201c\u7ecf\u8fc7\u4e24\u4eba\u4e00\u5546\u8bae\uff0c\u6211\u4eec\u5c31\u8eb2\u8d77\u6765\uff0c\u76f4\u7b49\u970d\u5143\u4f3d\u5728\u805a\u4e49\u5385\u51c6\u5907\u5c31\u7eea\uff0c\u6211\u624d\u51b3\u5b9a\u73b0\u8eab\u3002\u81f3 \u4e8e\u5979\uff0c\u6682\u65f6\u85cf\u5728\u5927\u6811\u4e0a\uff0c\u5fc5\u8981\u65f6\u4fbf\u6765\u4e3a\u6211\u52a9\u9635\u3002\u201d \u3000\u3000\u201c\u8fd9\u4eba\u5230\u5e95\u8c01\uff1f\u201d \u3000\u3000\u201c\u4f60\u4eec\u4e5f\u89c1\u8fc7\uff0c\u800c\u4e14\u90fd\u8ba4\u8bc6\u3002\u201d \u3000\u3000\u4f17\u4eba\u53ea\u542c\u5f97\u5982\u5760\u4e94\u91cc\u96fe\u4e2d\uff0e \u3000\u3000\u9ea6\u5c0f\u660e\u8fd9\u624d\u9ad8\u58f0\u9053\uff1a\u201c\u82d7\u59d1\u5a18\u8bf7\u51fa\u6765\uff01\u201d \u3000\u3000\u82d7\u7d20\u82d3\u5f88\u5feb\u4fbf\u7531\u623f\u5185\u8d70\u51fa\u6765\u3002 \u3000\u3000\u8fd9\u65f6\u5979\u4ecd\u7740\u7537\u88c5\uff0c\u5374\u5df2\u4e0d\u662f\u5148\u524d\u90a3\u5957\u84dd\u886b\u3002 \u3000\u3000\u539f\u6765\uff0c\u82d7\u7d20\u82d3\u6240\u4f4f\u7684\u623f\u95f4\uff0c\u548c\u9ea6\u5c0f\u660e\u623f\u95f4\u76f8\u9694\u4e0d\u8fdc\uff0c\u867d\u7136\u5979\u4e0d\u66fe\u8fc7\u95ee\u9ea6\u5c0f\u660e\u6628\u665a\u5230\u4e1c\u6d77 \u9662\u4e4b\u4e8b\uff0c\u5374\u5df2\u79c1\u4e0b\u542c\u5230\u4f17\u4eba\u5728\u8bae\u8bba\u6b64\u4e8b\u3002 \u3000\u3000\u56e0\u4e4b\uff0c\u5728\u9ea6\u5c0f\u660e\u6628\u665a\u8d70\u540e\uff0c\u4fbf\u4e5f\u643a\u5e26\u5175\u5203\uff0c\u7a7f\u7a97\u800c\u51fa\uff0c\u5e76\u627e\u4e86\u4e00\u5757\u9ed1\u7eb1\u5c06\u9762\u90e8\u7f69\u8d77\uff0c\u6084 \u6084\u6765\u5230\u4e1c\u6d77\u9662\u5916\uff0e \u3000\u3000\u5979\u5230\u8fbe\u4e1c\u6d77\u9662\u65f6\uff0c\u6b63\u503c\u9ea6\u5c0f\u660e\u548c\u6c5f\u5357\u56db\u602a\u4ea4\u4e0a\u4e86\u624b\uff0c\u56e0\u89c1\u6c5f\u5357\u56db\u602a\u4e0d\u662f\u9ea6\u5c0f\u660e\u5bf9\u624b\uff0c\u56e0 \u6b64\u4fbf\u9690\u8eab\u6697\u5904\uff0e\u5e76\u672a\u91c7\u53d6\u4efb\u4f55\u884c\u52a8\uff0c\u76f4\u5230\u5cad\u5357\u4e8c\u5947\u4e0a\u9635\u540e\uff0c\u624d\u73b0\u8eab\u76f8\u52a9\uff0c\u7136\u540e\u53c8\u8d81\u673a\u6e9c\u8d70\u3002 \u3000\u3000\u5979\u56de\u5230\u67cf\u9f84\u9662\uff0c\u4e00\u76f4\u4e3a\u9ea6\u5c0f\u660e\u62c5\u5fc3\uff0c\u65e0\u6cd5\u6210\u7720\u3002\u56db\u66f4\u8fc7\u540e\u53c8\u542c\u5230\u949f\u4e00\u8c6a\u548c\u4f59\u4ea6\u4e50\u53bb\u529d\u9ea6 \u5c0f\u660e\u9003\u8d70\uff0c\u4e8e\u662f\u5c31\u5728\u949f\u3001\u4f59\u4e8c\u4eba\u8d70\u540e\uff0c\u53c8\u7a7f\u7a97\u800c\u51fa\uff0c\u6765\u5230\u9ea6\u5c0f\u660e\u7a97\u5916\u6572\u7a97\u3002 \u3000\u3000\u4e24\u4eba\u76f8\u89c1\u540e\uff0c\u9ea6\u5c0f\u660e\u624d\u77e5\u9053\u6628\u665a\u76f8\u52a9\u81ea\u5df1\u7684\u84dd\u886b\u8499\u9762\u4eba\u7adf\u662f\u82d7\u7d20\u82d3\uff0c\u7ecf\u8fc7\u4e00\u756a\u5546\u8bae\uff0c\u624d \u51b3\u5b9a\u4e86\u5148\u524d\u7684\u90a3\u756a\u884c\u52a8\u3002 \u3000\u3000\u4ed6\u4eec\u7684\u8ba1\u5212\uff0c\u4e5f\u662f\u5fc5\u8981\u65f6\u5148\u64d2\u4f4f\u82d7\u7d20\u5170\uff0c\u4f7f\u970d\u5143\u4f3d\u6295\u9f20\u5fcc\u5668\uff0c\u4e0d\u6562\u8f7b\u4e3e\u5984\u52a8\uff0c\u540c\u65f6\u82d7\u7d20 \u82d3\u4e5f\u53ef\u8d81\u4e4b\u673a\u4f1a\u770b\u770b\u59d0\u59d0\u82d7\u7d20\u5170\uff0e \u3000\u3000\u9ea6\u5c0f\u660e\u8d77\u521d\u5f88\u9707\u60ca\u4e8e\u82d7\u7d20\u82d3\u8eab\u6cd5\u4e3a\u4f55\u5982\u6b64\u4e4b\u9ad8\uff0c\u56e0\u4e3a\u4ed6\u6628\u591c\u5df2\u89c1\u8fc7\u5979\u7684\u6b66\u529f\uff0c\u6bd4\u5979\u7684\u59d0 \u59d0\u82d7\u7d20\u5170\u4ee5\u53ca\u4e07\u6620\u971e\u90fd\u9ad8\u51fa\u8bb8\u591a\u3002\u4f46\u7ecf\u8fc7\u82d7\u7d20\u82d3\u7684\u4e00\u756a\u89e3\u8bf4\uff0c\u4e5f\u5c31\u4e0d\u4ee5\u4e3a\u602a\u4e86\u3002 \u3000\u3000\u539f\u6765\uff0c\u82d7\u7d20\u82d3\u5728\u5415\u6881\u5c71\u4e5d\u5929\u7384\u6d1e\u5e9c\u65f6\uff0c\u6700\u53d7\u9634\u624b\u4e00\u9b54\u7684\u559c\u7231\uff0e\u56e0\u4e4b\uff0e\u6559\u5979\u7684\u6b66\u529f\u4e5f\u6700\u591a\u3002 \u3000\u3000\u540e\u6765\uff0c\u957f\u767d\u5c71\u795e\u53df\u5e9e\u58eb\u51b2\u53c8\u4f20\u8fc7\u5979\u4e0d\u5c11\u6b66\u529f\uff0c\u4ee5\u81f4\u5979\u7684\u6b66\u529f\u5f97\u80fd\u66f4\u4e0a\u4e00\u5c42\u697c\uff0c\u5b9e\u5728\u662f\u5f88 \u81ea\u7136\u7684\u4e8b\u3002 \u3000\u3000\u5f53\u4e0b\uff0c\u949f\u2014\u8c6a\u7b49\u4eba\uff0c\u7ec8\u4e8e\u660e\u767d\u4e86\u662f\u600e\u4e48\u56de\u4e8b\uff0c\u4ed6\u4eec\u89c1\u67cf\u9f84\u9662\u53c8\u6dfb\u4e86\u8fd9\u4e48\u4e00\u4f4d\u751f\u529b\u519b\uff0e\u5185 \u5fc3\u90fd\u9ad8\u5174\u4e0d\u5df2\u3002 \u3000\u3000\u738b\u5927\u5eb7\u5e26\u70b9\u4e0d\u89e3\u7684\u95ee\u9053\uff1a\u201c\u82d7\u59d1\u5a18\u6253\u626e\u6210\u7537\u88c5\uff0c\u53c8\u9762\u7f69\u9ed1\u7eb1\uff0c\u4ffa\u4eec\u5f53\u7136\u8ba4\u4e0d\u51fa\u4f60\u662f\u8c01\uff0c \u53ef\u662f\u4e3a\u4ec0\u4e48\u5148\u524d\u8fde\u58f0\u97f3\u4e5f\u50cf\u7537\u7684\u5462\uff1f\u201d \u3000\u3000\u82d7\u7d20\u82d3\u7b11\u9053\uff1a\u201c\u5bb6\u5e08\u9634\u624b\u4e00\u9b54\uff0c\u4ed6\u8fde\u2018\u5411\u5fc3\u9732\u2019\u90fd\u9020\u5f97\u51fa\u6765\uff0c\u96be\u9053\u8fd8\u9020\u4e0d\u51fa\u2018\u53d8\u97f3\u836f\u2019\uff1f \u665a\u8f88\u79bb\u5f00\u4e5d\u5929\u7384\u6d1e\u65f6\uff0c\u987a\u624b\u5728\u836f\u623f\u5077\u6765\u4e00\u74f6\uff0e\u60f3\u4e0d\u5230\u6765\u5230\u8ff7\u8e2a\u8c37\u5c31\u6d3e\u4e0a\u4e86\u7528\u573a\u3002\u201d \u3000\u3000\u738b\u5927\u5eb7\u9053\uff1a\u201c\u771f\u4e86\u4e0d\u8d77\uff0c\u4f60\u548c\u9ea6\u5c0f\u5144\u5f1f\u79f0\u5f97\u4e0a\u662f\u5929\u751f\u4e00\u5bf9\uff0c\u5730\u914d\u4e00\u53cc\uff01\u201d \u3000\u3000\u4ed6\u8fd9\u2014\u53e5\u65e0\u5fc3\u4e4b\u8a00\u4e0d\u6253\u7d27\uff0c\u5374\u628a\u82d7\u7d20\u82d3\u7f9e\u5f97\u5239\u65f6\u7c89\u8138\u6cdb\u7ea2\uff0c\u4f4e\u4e0b\u4e86\u5934\u3002 \u3000\u3000\u8fde\u9ea6\u5c0f\u660e\u4e5f\u6709\u4e9b\u96be\u5f97\u2014\u89c1\u7684\u817c\u8146\u3002 \u3000\u3000\u53ea\u542c\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u73b0\u5728\u54b1\u4eec\u5927\u5bb6\u8be5\u5546\u8bae\u7684\u7b2c\u4e00\u4ef6\u4e8b\uff0c\u662f\u5c0f\u660e\u8001\u5f1f\u5982\u4f55\u548c\u970d\u5143\u4f3d\u89c1\u9762\u3002\u201d \u3000\u3000\u738b\u5927\u5eb7\u9053\uff1a\u201c\u9ea6\u5c0f\u5144\u5f1f\u624b\u91cc\u6709\u90a3\u8001\u5c0f\u5b50\u7b7e\u540d\u753b\u62bc\u7684\u5b57\u636e\uff0c\u8fd8\u6015\u4ed6\u4f5c\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u73b0\u5728\u4e0d\u662f\u6015\u4e0d\u6015\u7684\u95ee\u9898\uff0c\u800c\u662f\u9ea6\u8001\u5f1f\u548c\u4ed6\u89c1\u9762\u65f6\uff0c\u53cc\u65b9\u5fc5\u5b9a\u5f88\u5c34\u5c2c\uff0c\u54b1\u4eec \u603b\u8be5\u60f3\u529e\u6cd5\u5316\u89e3\u8fd9\u5c42\u5c34\u5c2c\u6c14\u6c1b\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u5bf9\u81ea\u5df1\u7684\u4e8b\uff0c\u53cd\u800c\u9887\u4e0d\u5728\u610f\u5730\u9053\uff1a\u201c\u970d\u5143\u4f3d\u548c\u6211\u5df2\u6210\u51a4\u5bb6\u5bf9\u5934\uff0c\u518d\u5316\u89e3\u4e5f\u6ca1\u6709\u7528\u3002 \u5012\u4e0d\u5982\u4e0d\u53bb\u7ba1\u5b83\uff0c\u73b0\u5728\u6700\u91cd\u8981\u7684\u4e00\u4ef6\u4e8b\uff0c\u5e94\u8be5\u662f\u5982\u4f55\u8ba9\u82d7\u59d1\u5a18\u65e9\u65e9\u4e0e\u5979\u59d0\u59d0\u89c1\u9762\u3002\u201d \u3000\u3000\u949f\u2014\u8c6a\u9053\uff1a\u201c\u8fd9\u7684\u786e\u662f\u4ef6\u8981\u7d27\u7684\u4e8b\uff0c\u4e8b\u5b9e\u8bc1\u660e\uff0c\u82d7\u5927\u59d1\u5a18\u5e76\u672a\u53d8\u5fc3\uff0c\u4f46\u54b1\u4eec\u548c\u5979\u5374\u5f88\u96be \u6709\u79c1\u4e0b\u63a5\u89e6\u7684\u673a\u4f1a\u3002\u5982\u679c\u80fd\u8ba9\u5979\u59d0\u59b9\u65e9\u4e9b\u89c1\u9762\uff0e\u54b1\u4eec\u5c31\u4e0d\u96be\u5f97\u77e5\u970d\u5143\u4f3d\u4e00\u4e9b\u4e0d\u66fe\u516c\u5f00\u7684\u6d88 \u606f\u3002\u201d \u3000\u3000\u738b\u5927\u5eb7\u9053\uff1a\u201c\u4eca\u5929\u82d7\u59d1\u5a18\u548c\u82d7\u5927\u59d1\u5a18\u4e0d\u662f\u5df2\u7ecf\u89c1\u8fc7\u4e86\u5417\uff1f\u201d \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u82d7\u59d1\u5a18\u4e0d\u8fc7\u662f\u770b\u5230\u4e86\u5979\u59d0\u59d0\u800c\u5df2\uff0c\u53cc\u65b9\u8fde\u53e5\u8bdd\u90fd\u4e0d\u80fd\u4ea4\u8c08\uff0c\u89c1\u8fd9\u79cd\u9762\u6709\u4ec0 \u4e48\u7528\uff1f\u201d \u3000\u3000\u738b\u5927\u5eb7\u5c34\u5c2c\u4e00\u7b11\u9053\uff1a\u201c\u526f\u76df\u4e3b\u8bf4\u7684\u4e5f\u662f\uff0c\u4e0d\u8fc7\u89c1\u7740\u603b\u6bd4\u6ca1\u770b\u5230\u597d\u3002\u201d \u3000\u3000\u6d2a\u6cfd\u7784\u4e86\u738b\u5927\u5eb7\u4e00\u773c\u9053\uff1a\u201c\u4f60\u53c8\u5728\u8bf4\u5e9f\u8bdd\u4e86\uff01\u201d \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u82d7\u59d1\u5a18\u8bf7\u8010\u5fc3\u7b49\u5f85\uff0e\u949f\u67d0\u2014\u5b9a\u8bbe\u6cd5\u8ba9\u4f60\u4eec\u59d0\u59b9\u5c3d\u65e9\u89c1\u9762\u3002\u201d \u3000\u3000\u4e2d\u5348\uff0e\u5f00\u996d\u65f6\u95f4\u5df2\u5230\uff0c\u67cf\u9f84\u9662\u7fa4\u8c6a\u521a\u8fdb\u5165\u9910\u5385\uff0c\u4fbf\u542c\u5b88\u95e8\u7684\u8fdb\u6765\u901a\u62a5\u9053\uff1a\u201c\u7980\u526f\u76df\u4e3b\uff0c \u76df\u4e3b\u5e26\u7740\u5df4\u5934\u9886\u548c\u5b8b\u5934\u9886\u6b63\u5411\u8fd9\u8fb9\u800c\u6765\uff0c\u53ef\u80fd\u662f\u8981\u5230\u54b1\u4eec\u67cf\u9f84\u9662\u3002\u201d \u3000\u3000\u8fd9\u5b88\u95e8\u55bd\u5175\u662f\u949f\u4e00\u8c6a\u7684\u5fc3\u8179\u624b\u4e0b\uff0c\u6545\u800c\u624d\u4f1a\u4e3b\u52a8\u4e8b\u5148\u901a\u62a5\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u5fd9\u4ea4\u4ee3\u9053\uff1a\u201c\u82d7\u59d1\u5a18\u8bf7\u6682\u56de\u623f\u8eb2\u4e00\u8eb2\uff01\u201d \u3000\u3000\u738b\u5927\u5eb7\u9053\uff1a\u201c\u9ea6\u5c0f\u5144\u5f1f\u662f\u5426\u4e5f\u8be5\u6682\u65f6\u907f\u2014\u907f\uff1f\u201d \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u8fd9\u6b63\u662f\u9ea6\u8001\u5f1f\u548c\u4ed6\u89c1\u9762\u7684\u597d\u673a\u4f1a\uff0c\u4e5f\u53ef\u514d\u9664\u5c06\u6765\u5728\u805a\u4e49\u5385\u4e4d\u89c1\u7684\u5c34\u5c2c\u3002\u201d \u3000\u3000\u679c\u7136\uff0c\u970d\u5143\u4f3d\u5f88\u5feb\u4fbf\u5e26\u9886\u5cad\u5357\u4e8c\u5947\u8d70\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u7b49\u4eba\u968f\u5373\u8d77\u7acb\u76f8\u8fce\uff0c\u9ea6\u5c0f\u660e\u4e5f\u4e0d\u5f97\u4e0d\u8ddf\u7740\u7ad9\u8d77\u3002 \u3000\u3000\u970d\u5143\u4f3d\u4e00\u773c\u5c31\u53d1\u73b0\u9ea6\u5c0f\u660e\uff0c\u5148\u662f\u8138\u8272\u4e00\u53d8\uff0e\u63a5\u7740\u53c8\u563f\u563f\u7b11\u9053\uff1a\u201c\u539f\u6765\u9ea6\u8001\u5f1f\u6ca1\u8d70\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u6211\u662f\u8ff7\u8e2a\u8c37\u7684\u4eba\uff0c\u662f\u4f60\u970d\u76df\u4e3b\u7684\u5c5e\u4e0b\uff0c\u4f60\u5e76\u6ca1\u8d76\u6211\u8d70\uff0c\u6211\u4e3a\u4ec0\u4e48\u8981\u8d70\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u5c34\u5c2c\u4e00\u7b11\u9053\uff1a\u201c\u4f60\u662f\u8ff7\u8e2a\u8c37\u7684\u4e00\u5458\u52c7\u5c06\uff0c\u8001\u592b\u672c\u6765\u5c31\u820d\u4e0d\u5f97\u4f60\u8d70\u3002\u73b0\u5728\u6765\uff0c\u5c31\u662f \u8981\u770b\u770b\u4f60\u5728\u4e0d\u5728\uff0c\u771f\u53eb\u8001\u592b\u9ad8\u5174\uff0c\u4f60\u679c\u7136\u5728\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u6211\u662f\u2014\u4e2a\u4e13\u7ed9\u4f60\u60f9\u9ebb\u70e6\u7684\u4eba\uff0c\u4f60\u771f\u7684\u5e0c\u671b\u6211\u5728\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u62cd\u4e86\u9ea6\u5c0f\u660e\u2014\u4e0b\u80a9\u8180\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u50cf\u4f60\u8fd9\u6837\u4e00\u4f4d\u96be\u5f97\u7684\u4eba\uff0e\u7eb5\u7136\u60f9\u70b9\u9ebb\u70e6\uff0c\u8001\u592b \u4e5f\u9ad8\u5174\uff01\u201d \u3000\u3000\u201c\u4f60\u8bf4\u7684\u53ef\u662f\u771f\u5fc3\u8bdd\uff1f\u201d \u3000\u3000\u201c\u4f60\u600e\u4e48\u8fde\u8001\u592b\u7684\u8bdd\u4e5f\u4e0d\u76f8\u4fe1\uff1f\u201d \u3000\u3000\u201c\u4f60\u8bf4\u8981\u6740\u6211\uff0c\u540e\u6765\u53c8\u4e0d\u6740\u6211\uff0c\u8fd9\u662f\u4e0d\u662f\u8bf4\u8bdd\u4e0d\u7b97\u8bdd\u5417\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u88ab\u5f04\u5f97\u8138\u4e0a\u4e00\u70ed\uff0c\u6709\u4e9b\u96be\u4ee5\u63aa\u8bcd\u3002 \u3000\u3000\u4f59\u4ea6\u4e50\u89c1\u72b6\uff0c\u62c5\u5fc3\u970d\u5143\u4f3d\u96be\u4ee5\u4e0b\u53f0\uff0c\u5fd9\u9053\uff1a\u201c\u9ea6\u8001\u5f1f\uff0c\u4f60\u597d\u50cf\u53c8\u72af\u4e86\u6bdb\u75c5\uff0c\u76df\u4e3b\u662f\u5bf9\u4f60 \u7684\u4e00\u756a\u597d\u610f\uff0c\u4f60\u5e94\u5f53\u611f\u8c22\u4ed6\u8001\u4eba\u5bb6\u624d\u5bf9\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u6211\u5f53\u7136\u660e\u767d\u76df\u4e3b\u5bf9\u6211\u597d\uff0e\u4e0d\u7136\u6211\u4f55\u5fc5\u7559\u5728\u8ff7\u8e2a\u8c37\uff1f\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u9053\uff1a\u201c\u65e2\u7136\u660e\u767d\u76df\u4e3b\u5bf9\u4f60\u597d\uff0c\u521a\u624d\u5c31\u4e0d\u5e94\u8be5\u518d\u8bf4\u90a3\u79cd\u8bdd\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u7b11\u9053\uff1a\u201c\u6211\u4e0d\u8fc7\u5f00\u5f00\u73a9\u7b11\uff0e\u76df\u4e3b\u867d\u7136\u8eab\u4efd\u5730\u4f4d\u9ad8\uff0c\u603b\u4e5f\u662f\u4e2a\u4eba\uff0c\u4e0d\u53ef\u80fd\u6ca1\u5f00\u8fc7\u73a9 \u7b11\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u5f00\u73a9\u7b11\u8981\u770b\u573a\u5408\u3002\u201d \u3000\u3000\u201c\u73b0\u5728\u8fd9\u573a\u5408\u6700\u6070\u5f53\uff0c\u76df\u4e3b\u4ece\u6628\u591c\u5230\u73b0\u5728\u4e00\u76f4\u5728\u751f\u6211\u7684\u6c14\uff0c\u8bf4\u4e0d\u5b9a\u5df2\u7ecf\u6c14\u660f\u4e86\u5934\uff0c\u6211\u5f00 \u5f00\u73a9\u7b11\uff0c\u6b63\u662f\u8bbe\u6cd5\u8ba9\u4ed6\u6d88\u6c14\uff0c\u6709\u4ec0\u4e48\u4e0d\u597d\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u89c9\u51fa\u67cf\u9f84\u9662\u4e0d\u80fd\u591a\u5f85\uff0e\u7acb\u5373\u62f1\u4e86\u62f1\u624b\u9053\uff1a\u201c\u8bf8\u4f4d\u8bf7\u7528\u996d\u5427\uff0c\u8001\u592b\u8be5\u8d70\u4e86\uff01\u201d \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u76df\u4e3b\u4f55\u4e0d\u7559\u5728\u8fd9\u91cc\u4e00\u8d77\u7528\u996d\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u8001\u592b\u90a3\u8fb9\u8fd8\u6709\u70b9\u4e8b\u7b49\u7740\u8981\u529e\uff0c\u665a\u4e0a\u518d\u8bbe\u5bb4\u62db\u5f85\u9ea6\u8001\u5f1f\u548c\u5404\u4f4d\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u8d70\u540e\uff0c\u738b\u5927\u5eb7\u9053\uff1a\u201c\u8fd9\u8001\u5c0f\u5b50\u53c8\u8981\u8bf7\u5ba2\uff0c\u53ea\u6050\u5bb4\u65e0\u597d\u5bb4\uff0c\u4f1a\u65e0\u597d\u4f1a\uff0c\u54b1\u4eec\u53bb\u662f\u4e0d \u53bb\uff1f\u201d \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u4ed6\u662f\u76df\u4e3b\uff0c\u8981\u8bbe\u5bb4\u62db\u5f85\uff0c\u54b1\u4eec\u5f53\u7136\u8981\u53bb\u3002\u201d \u3000\u3000\u738b\u5927\u5eb7\u76b1\u7709\u9053\uff1a\u201c\u4e07\u2014\u4ed6\u5728\u9152\u4e2d\u4e0b\u6bd2\u5462\uff1f\u201d \u3000\u3000\u5ffd\u89c1\u82d7\u7d20\u82d3\u7531\u623f\u5185\u8d70\u4e86\u51fa\u6765\u9053\uff1a\u201c\u53ef\u60dc\u6211\u4e0d\u80fd\u53c2\u52a0\uff0c\u5982\u679c\u6709\u6211\u5728\u573a\uff0c\u4e0d\u8bba\u4ed6\u4e0b\u4ec0\u4e48\u6bd2\uff0c \u90fd\u7792\u4e0d\u8fc7\u6211\u3002\u201d \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201d\u82d7\u59d1\u5a18\u5728\u8fd9\u65b9\u9762\u600e\u4f1a\u5982\u6b64\u9ad8\u660e\uff1f\u201d \u3000\u3000\u82d7\u7d20\u82d3\u9053\uff1a\u201c\u9ad8\u660e\u8c08\u4e0d\u4e0a\uff0c\u526f\u76df\u4e3b\u522b\u5fd8\u4e86\u6211\u5e08\u7236\u662f\u8c01\u3002\u8ddf\u4e86\u4ed6\u90a3\u4e48\u591a\u5e74\uff0c\u5b66\u4e5f\u5b66\u4e86\u4e0d\u5c11\uff0e \u770b\u4e5f\u770b\u4e86\u5f88\u591a\u3002\u5c31\u8fde\u6211\u59d0\u59d0\uff0c\u53ef\u80fd\u4e5f\u6bd4\u4e0d\u4e0a\u627e\u3002\u201d \u3000\u3000\u738b\u5927\u5eb7\u7981\u4e0d\u4f4f\u9053\uff1a\u201c\u90a3\u5c31\u8ba9\u82d7\u59d1\u5a18\u4e5f\u4e00\u8d77\u53c2\u52a0\u3002\u201d \u3000\u3000\u949f\u4e00\u8c6a\u6447\u5934\u9053\uff1a\u201c\u4e0d\u6210\uff01\u82d7\u59d1\u5a18\u548c\u4ed6\u6b63\u5f0f\u89c1\u9762\uff0e\u5fc5\u987b\u627e\u4e2a\u9002\u5f53\u65f6\u673a\uff0c\u4e0d\u7136\u4ed6\u5c06\u5bf9\u54b1\u4eec\u7591 \u5fc3\u66f4\u5927\u3002\u201d \u3000\u3000\u5374\u542c\u4f59\u4ea6\u4e50\u9053\uff1a\u201c\u5927\u5bb6\u653e\u5fc3\uff0c\u4f59\u67d0\u5728\u65bd\u6bd2\u9a8c\u6bd2\u65b9\u9762\uff0c\u4e5f\u61c2\u5f97\u4e00\u70b9\u76ae\u6bdb\uff0c\u5230\u65f6\u5019\u5927\u5bb6\u770b\u6211 \u7684\u8138\u8272\u884c\u52a8\uff0c\u6211\u52a8\u8fc7\u7b77\u5b50\u7684\u83dc\uff0e\u5927\u5bb6\u518d\u8ddf\u7740\u7528\u3002\u201d \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u4e5f\u597d\uff0c\u53ea\u6709\u8fd9\u4e48\u529e\u4e86\u3002\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u63a5\u7740\u53c8\u9053\uff1a\u201c\u8fd8\u6709\uff0c\u5982\u679c\u6211\u6240\u6599\u4e0d\u5dee\uff0e\u82d7\u5927\u59d1\u5a18\u4e5f\u5fc5\u5b9a\u5728\u573a\u3002\u5979\u65e2\u7136\u4ecd\u62a4\u7740\u6211\u4eec\uff0c \u5982\u679c\u996d\u83dc\u91cc\u6709\u6bd2\uff0c\u5979\u5fc5\u5b9a\u77e5\u9053\uff0c\u9ed8\u5bdf\u5979\u7684\u773c\u8272\uff0c\u4e5f\u662f\u4e2a\u529e\u6cd5\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u679c\u76f4\u5728\u5f53\u665a\u8bbe\u4e0b\u4e86\u9152\u5bb4\u3002 \u3000\u3000\u9152\u98df\u53ea\u6709\u4e24\u684c\uff0c\u67cf\u9f84\u9662\u7684\u7fa4\u8c6a\u5168\u5728\u88ab\u9080\u4e4b\u5217\uff0c\u53cd\u800c\u662f\u970d\u5143\u4f3d\u7684\u5fc3\u8179\u624b\u4e0b\uff0c\u53ea\u5230\u4e86\u5cad\u5357\u4e8c \u5947\u7b49\u5c11\u6570\u51e0\u4eba\u3002\u5728\u8868\u9762\u4e0a\u770b\uff0c\u81ea\u7136\u662f\u970d\u5143\u4f3d\u5bf9\u67cf\u9f84\u9662\u65b9\u9762\u7684\u91cd\u89c6\u3002 \u3000\u3000\u4e3a\u4e86\u4e0d\u5f20\u626c\uff0c\u9152\u5bb4\u662f\u8bbe\u5728\u5929\u661f\u970d\u5143\u4f3d\u79c1\u7528\u7684\u5ba2\u5385\u91cc\u3002 \u3000\u3000\u82d7\u7d20\u5170\u679c\u7136\u4e5f\u51fa\u73b0\u5728\u573a\uff0e \u3000\u3000\u8fd9\u987f\u9152\u996d\u4e3b\u8981\u62db\u5f85\u7684\u5bf9\u8c61\u8db3\u9ea6\u5c0f\u660e\u3002\u56e0\u4e4b\uff0c\u9ea6\u5c0f\u660e\u7684\u5ea7\u4f4d\u548c\u949f\u4e00\u8c6a\u5e76\u6392\u5728\u4e00\u8d77\uff0c\u6bd4\u4f59\u4ea6 \u4e50\u8fd8\u8981\u4e0a\u4f4d\u3002 \u3000\u3000\u970d\u5143\u4f3d\u7684\u6001\u5ea6\uff0e\u8868\u73b0\u5f97\u5f02\u5e38\u6bb7\u52e4\u4eb2\u5207\uff0c\u9996\u5148\u5411\u9ea6\u5c0f\u660e\u656c\u9152\u3002 \u3000\u3000\u82d7\u7d20\u5170\u5219\u795e\u8272\u6f20\u7136\uff0c\u5f88\u5c11\u5f00\u53e3\u8bb2\u8bdd\u3002 \u3000\u3000\u67cf\u9f84\u9662\u7684\u7fa4\u8c6a\uff0c\u90fd\u6697\u4e2d\u7559\u610f\u4f59\u4ea6\u4e50\u548c\u82d7\u7d20\u5170\u7684\u795e\u8272\uff0c\u6bcf\u9053\u83dc\u7b49\u4ed6\u4eec\u52a8\u8fc7\u7b77\u5b50\u518d\u52a8\u7b77\u5b50\u3002 \u3000\u3000\u5ffd\u542c\u970d\u5143\u4f3d\u95ee\u9053\uff1a\u201c\u4e07\u59d1\u5a18\u4e3a\u4ec0\u4e48\u6ca1\u5230\uff1f\u201d \u3000\u3000\u949f\u4e00\u8c6a\u65e9\u6709\u51c6\u5907\uff0e\u5fd9\u9053\uff1a\u201c\u4e07\u59d1\u5a18\u8fd9\u51e0\u5929\u8eab\u4e0a\u4e00\u76f4\u4e0d\u8212\u670d\uff0e\u8fde\u5e73\u5e38\u5403\u996d\uff0c\u90fd\u662f\u9001\u5230\u5979\u623f \u91cc\u4e00\u4e2a\u4eba\u7528\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u8f7b\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u53ef\u66fe\u8bf7\u5927\u592b\u770b\u8fc7\uff1f\u54b1\u4eec\u8ff7\u8e2a\u8c37\u4e5f\u6709\u4f4d\u540d\u533b\u3002\u201d \u3000\u3000\u949f\u2014\u8c6a\u6697\u611f\u5fc3\u5934\u4e00\u9707\u9053\uff1a\u201c\u5c5e\u4e0b\u600e\u4e48\u6ca1\u542c\u8bf4\u8fc7\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u5c31\u662f\u4eca\u5929\u5927\u5bb6\u6240\u89c1\u8fc7\u7684\u4e01\u5e05\u7236\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u54fc\u4e86\u58f0\u9053\uff1a\u201c\u90a3\u8001\u5c0f\u5b50\u8fd8\u4f1a\u770b\u75c5\uff1f\u5012\u770b\u4e0d\u51fa\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u4eba\u4e0d\u53ef\u8c8c\u76f8\uff0e\u6c34\u4e0d\u53ef\u6597\u91cf\u3002\u4e01\u5e08\u6587\u662f\u4f4d\u79c0\u624d\uff0c\u636e\u8bf4\u5728\u5bb6\u4e61\u53bf\u8859\u95e8\u91cc\u4e5f\u5f53\u8fc7 \u5e08\u7237\uff0c\u53c8\u5b66\u8fc7\u533b\u672f\uff0c\u4e13\u6d3d\u7591\u96be\u6742\u75c7\uff0e\u50cf\u4e07\u59d1\u5a18\u8fd9\u79cd\u75c5\uff0c\u4e00\u770b\u5c31\u597d\u3002\u201d \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u8fd8\u662f\u4e0d\u5fc5\u9ebb\u70e6\u4e01\u5e08\u7237\u7684\u597d\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u4ec0\u4e48\u8bdd\uff1f\u6709\u75c5\u54ea\u6709\u4e0d\u6cbb\u7684\uff0c\u660e\u5929\u8001\u592b\u5c31\u6d3e\u4e01\u5e08\u7237\u5230\u67cf\u9f84\u9662\u4e3a\u4e07\u59d1\u5a18\u770b\u75c5\uff0c \u4fdd\u8bc1\u836f\u5230\u75c5\u9664\u3002\u201d \u3000\u3000\u8fd9\u2014\u6765\uff0c\u4e0d\u4f46\u949f\u4e00\u805a\u5185\u5fc3\u8d77\u4e86\u6050\u614c\uff0c\u67cf\u9f84\u9662\u5176\u4ed6\u7684\u4eba\u4e5f\u6709\u4e9b\u6c89\u4e0d\u4f4f\u6c14\uff0c\u4f46\u970d\u5143\u4f3d\u8bdd\u5df2\u51fa \u53e3\u53c8\u6709\u8c01\u80fd\u8ba9\u4e01\u5e08\u7237\u4e0d\u5230\u67cf\u9f84\u9662\u53bb\u3002 \u3000\u3000\u67cf\u9f84\u9662\u7684\u7fa4\u8c6a\u56de\u53bb\u540e\uff0e\u949f\u2014\u8c6a\u4e3a\u660e\u65e5\u4e01\u5e08\u7237\u8981\u6765\u7ed9\u4e07\u59d1\u5a18\u770b\u75c5\u7684\u4e8b\u5927\u611f\u70e6\u607c\uff0e\u4f46\u56e0\u65f6\u95f4 \u5df2\u665a\uff0c\u5927\u5bb6\u90fd\u6709\u9189\u610f\uff0e\u65e0\u4ece\u5546\u8bae\u89e3\u51b3\u529e\u6cd5\uff0c\u53ea\u6709\u7b49\u660e\u5929\u518d\u8bf4\u4e86\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u56de\u5230\u623f\u91cc\uff0c\u4e5f\u7761\u4e0d\u7740\u89c9\u3002 \u3000\u3000\u4ed6\u60f3\u5230\u82d7\u7d20\u82d3\u5bf9\u81ea\u5df1\u4e24\u6b21\u820d\u547d\u76f8\u52a9\uff0e\u81ea\u5df1\u603b\u8be5\u8bbe\u6cd5\u8ba9\u5979\u548c\u82d7\u7d20\u5170\u65e9\u65e5\u76f8\u4f1a\uff0c\u5426\u5219\u5c31\u672a\u514d \u592a\u6127\u5bf9\u4eba\u5bb6\u4e86\u3002 \u3000\u3000\u4ed6\u672c\u6765\u5df2\u7ecf\u8eba\u4e0b\uff0e\u60f3\u5230\u8fd9\u91cc\uff0c\u7acb\u5373\u53c8\u8d77\u8eab\u7a7f\u597d\u88c5\uff0e\u51fa\u4e86\u67cf\u9f84\u9662\u3002 \u3000\u3000\u4ed6\u8981\u8d81\u591c\u53bb\u89c1\u82d7\u7d20\u5170\u3002 \u3000\u3000\u82d7\u7d20\u5170\u548c\u970d\u5143\u4f3d\u76ee\u524d\u5e76\u4e0d\u4f4f\u5728\u540c\u4e00\u623f\u95f4\uff0c\u4ed6\u5df2\u542c\u8bf4\uff0c\u540c\u65f6\u4e5f\u6253\u542c\u51fa\u82d7\u7d20\u5170\u5728\u5929\u661f\u9662\u7684\u4f4f \u5904\u6240\u5728\u3002 \u3000\u3000\u5929\u661f\u9662\u7684\u5927\u95e8\u5916\u6709\u8b66\u536b\uff0c\u5de1\u591c\u7684\u4e5f\u5f80\u6765\u9891\u7e41\uff0c\u9ea6\u5c0f\u660e\u53ea\u597d\u76f4\u63a5\u6f5c\u884c\u5230\u5929\u661f\u9662\u540e\u4fa7\uff0e\u8eb2\u5728 \u6697\u5904\u770b\u770b\u56db\u4e0b\u65e0\u4eba\uff0c\u624d\u8dc3\u8eab\u8fdb\u4e86\u9662\u5899\u3002 \u3000\u3000\u8fdb\u5165\u9662\u5899\uff0c\u5df2\u662f\u9662\u5185\uff0c\u9662\u5185\u8303\u56f4\u867d\u4e0d\u5c0f\uff0c\u4f46\u5df2\u5e76\u65e0\u8b66\u6212\u53ca\u5de1\u903b\u4eba\u5458\uff0e\u4e0d\u8fc7\u4ed6\u8fd8\u662f\u8e51\u8e2a\u5c4f \u606f\u800c\u884c\uff0c\u6162\u6162\u627e\u5230\u82d7\u7d20\u5170\u7684\u7a97\u5916\u3002 \u3000\u3000\u5176\u5b9e\u8fd9\u91cc\u662f\u5426\u771f\u662f\u82d7\u7d20\u5170\u7a97\u5916\uff0c\u4ed6\u5e76\u4e0d\u6562\u5341\u5206\u786e\u5b9a\uff0c\u800c\u5ba4\u5185\u5df2\u7184\u53bb\u706f\u706b\uff0c\u53ef\u80fd\u4eba\u5df2\u5165\u5bdd \u4e86\uff0e\u6839\u672c\u65e0\u6cd5\u5bdf\u770b\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u9690\u8eab\u7a97\u5916\u6697\u5904\uff0c\u8db3\u8db3\u7b49\u4e86\u76cf\u8336\u5de5\u592b\uff0c\u4ecd\u65e0\u52a8\u9759\uff0e\u65e0\u5948\u4e4b\u4e0b\uff0c\u53ea\u597d\u5728\u7a97\u68c2\u4e0a\u8f7b\u8f7b\u62cd \u4e86\u51e0\u62cd\u3002 \u3000\u3000\u7acb\u523b\uff0c\u91cc\u9762\u53d1\u51fa\u4e86\u60ca\u95ee\u58f0\uff1a\u201c\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u8fd9\u58f0\u97f3\u6b63\u662f\u82d7\u7d20\u5170\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u6697\u5fd6\u7740\u5982\u679c\u628a\u82d7\u7d20\u5170\u5f15\u51fa\u6237\u5916\uff0c\u52bf\u5fc5\u60ca\u52a8\u8033\u76ee\uff0e\u5fc3\u610f\u65e2\u5b9a\uff0e\u53c8\u62ac\u624b\u8fde\u62cd\u7a97\u68c2\u4e09\u4e0b\u3002 \u3000\u3000\u8fd9\u65f6\u82d7\u7d20\u5170\u5df2\u62ab\u597d\u8863\u670d\uff0c\u4e0b\u5e8a\u6765\u5230\u7a97\u524d\uff0c\u521a\u521a\u6253\u5f00\u7a97\u6237\uff0c\u8fd8\u6ca1\u6765\u5f97\u53ca\u95ee\u8bdd\uff0c\u9ea6\u5c0f\u660e\u8fc5\u901f \u7684\u8dc3\u8eab\u7a7f\u7a97\u76f4\u5165\u5ba4\u5185\u3002 \u3000\u3000\u82d7\u7d20\u5170\u5927\u5403\u4e00\u60ca\uff0c\u53eb\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\u597d\u5927\u80c6\uff0c\u7adf\u6562\u95ef\u5230\u76df\u4e3b\u592b\u4eba\u623f\u95f4\u6765\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u6025\u6025\u8bf4\u9053\uff1a\u201c\u5c0f\u58f0\u4e9b\uff0c\u662f\u6211\uff01\u201d \u3000\u3000\u201c\u4f60\u2026\u2026\u4f60\u662f\u5c0f\u660e\u2026\u2026\uff1f\u201d \u3000\u3000\u82d7\u7d20\u5170\u72b9\u81ea\u60ca\u9b42\u4e0d\u5b9a\uff0c\u76f4\u9000\u5230\u5e8a\u524d\u9053\uff1a\u201c\u5c0f\u660e\uff0c\u4f60\u53ef\u5343\u4e07\u4e0d\u80fd\u505a\u51fa\u50bb\u4e8b\u6765\u3002\u6211\u73b0\u5728\u5df2\u662f \u76df\u4e3b\u7684\u4eba\uff0c\u5373\u4f7f\u4e0d\u662f\uff0c\u4f60\u4e5f\u4e0d\u8be5\u5bf9\u6211\u6709\u8fc7\u4efd\u7684\u4e3e\u52a8\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u77e5\u9053\u5bf9\u65b9\u8bef\u4f1a\u4e86\u81ea\u5df1\uff0c\u5fd9\u9053\uff1a\u201c\u82d7\u59d0\u59d0\u522b\u8bef\u4f1a\uff0c\u6211\u662f\u6709\u4e8b\u6765\u627e\u4f60\u7684\u3002\u201d \u3000\u3000\u82d7\u7d20\u5170\u4f9d\u7136\u51b7\u9053\uff1a\u201c\u6709\u4e8b\u627e\u6211\uff0c\u5c31\u8be5\u7980\u660e\u76df\u4e3b\uff0c\u767d\u5929\u5802\u5802\u6b63\u6b63\u7684\u6765\uff0c\u6df1\u66f4\u534a\u591c\u95ef\u5230\u5c4b\u5b50 \u91cc\u6765\uff0e\u53c8\u7b97\u4f55\u610f\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u6025\u5f97\u8f7b\u8f7b\u4e00\u8dfa\u811a\u9053\uff1a\u201c\u5c0f\u5f1f\u8fd8\u8981\u6765\u5411\u4f60\u9053\u8c22\uff01\u201d \u3000\u3000\u201c\u4f60\u5411\u6211\u9053\u7684\u4ec0\u4e48\u8c22\uff1f\u201d \u3000\u3000\u201c\u4eca\u5929\u767d\u5929\u4f60\u6551\u8fc7\u6211\uff0c\u5f53\u7136\u8981\u5411\u4f60\u9053\u8c22\u3002\u201d \u3000\u3000\u201c\u4f60\u7684\u8bdd\u6211\u5b9e\u5728\u542c\u4e0d\u61c2\uff0c\u6211\u4ec0\u4e48\u65f6\u5019\u6551\u8fc7\u4f60\uff1f\u201d \u3000\u3000\u201c\u82d7\u59d0\uff0c\u4e0d\u5fc5\u518d\u88c5\u4f5c\u4e86\uff0c\u4ece\u524d\u67cf\u9f84\u9662\u7684\u4eba\uff0e\u90fd\u5bf9\u4f60\u53d8\u5fc3\u800c\u53bb\u65e2\u6000\u7591\u800c\u53c8\u4e0d\u8c05\u89e3\u3002\u770b\u4e86\u4eca \u5929\u4f60\u6551\u6211\uff0c\u624d\u77e5\u9053\u4f60\u5e76\u6ca1\u53d8\u5fc3\u3002\u4f60\u505a\u4e86\u970d\u5143\u4f3d\u7684\u538b\u5be8\u592b\u4eba\uff0c\u5fc5\u5b9a\u6709\u8bf4\u4e0d\u51fa\u6765\u7684\u82e6\u8877\u3002\u201d \u3000\u3000\u201c\u4f60\u5230\u5e95\u80e1\u8bf4\u4e9b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u5c0f\u5f1f\u6ca1\u80e1\u8bf4\uff0c\u53cd\u800c\u662f\u4f60\u6000\u7591\u5c0f\u5f1f\u7684\u6765\u610f\u3002\u201d \u3000\u3000\u201c\u4f60\u6df1\u66f4\u534a\u591c\u95ef\u5230\u6211\u623f\u95f4\uff0c\u672c\u6765\u5c31\u662f\u9b3c\u9b3c\u795f\u795f\u4e0d\u6b63\u7ecf\uff0e\u96be\u9053\u6211\u8fd8\u4f1a\u6b22\u8fce\u4f60\u6765\uff1f\u201d \u3000\u3000\u201c\u6211\u65b9\u624d\u8bf4\u8fc7\uff0c\u662f\u6709\u8981\u7d27\u4e8b\u89c1\u4f60\u3002\u201d \u3000\u3000\u201c\u6211\u65b9\u624d\u4e5f\u8bf4\u8fc7\uff0e\u4f60\u8be5\u5728\u767d\u5929\u6b63\u5927\u5149\u660e\u7684\u548c\u6211\u76f8\u89c1\uff0e\u201d \u3000\u3000\u201c\u6211\u82e5\u767d\u5929\u89c1\u4f60\uff0e\u5fc5\u5b9a\u4f1a\u8ba9\u970d\u5143\u4f3d\u77e5\u9053\u3002\u201d \u3000\u3000\u201c\u4ed6\u662f\u76df\u4e3b\uff0c\u4e5f\u662f\u6211\u7684\u4e08\u592b\uff0c\u5f53\u7136\u8be5\u77e5\u9053\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u6781\u529b\u6291\u5236\u7740\u5185\u5fc3\u7684\u6fc0\u52a8\uff0c\u5401\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u82d7\u59d0\uff0c\u4f60\u82e5\u8001\u662f\u8fd9\u6837\u8bb2\u8bdd\uff0c\u5c31\u592a\u8f9c\u8d1f\u5c0f \u5f1f\u7684\u4e00\u756a\u5fc3\u610f\u4e86\u3002\u201d \u3000\u3000\u82d7\u7d20\u5170\u4f9d\u7136\u8bed\u6c14\u51b7\u5cfb\u9053\uff1a\u201c\u6211\u4f55\u987b\u77e5\u9053\u4f60\u7684\u5fc3\u610f\uff0c\u4f60\u82e5\u77e5\u8da3\uff0c\u5c31\u9a6c\u4e0a\u8d70\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u4e07\u60f3\u4e0d\u5230\u4f1a\u5f04\u51fa\u8fd9\u6837\u7684\u540e\u679c\uff0c\u540c\u65f6\u4e5f\u5f00\u59cb\u6000\u7591\u82d7\u7d20\u5170\u5fc5\u5df2\u771f\u5fc3\u5f52\u9644\u4e86\u970d\u5143\u4f3d\uff0c\u4e0d \u7531\u4e00\u80a1\u6012\u706b\u76f4\u51b2\u800c\u8d77\u9053\uff1a\u201c\u82d7\u7d20\u5170\uff0c\u7528\u4e0d\u7740\u8fd9\u6837\u3002\u65e2\u4e0d\u9886\u6211\u7684\u60c5\uff0c\u6211\u5f53\u7136\u4e5f\u4e0d\u4f1a\u518d\u9886\u4f60\u7684\u60c5\uff0c \u6211\u4e5f\u4e0d\u518d\u6253\u6270\uff0c\u9a6c\u4e0a\u8d70\uff01\u201d \u3000\u3000\u4ed6\u521a\u8981\u7a7f\u7a97\u800c\u51fa\uff0c\u5374\u7a81\u542c\u95e8\u5916\u4f20\u6765\u811a\u6b65\u58f0\u3002 \u3000\u3000\u53ea\u6615\u82d7\u7d20\u5170\u4f4e\u58f0\u53eb\u9053\uff1a\u201c\u5feb\u5feb\u8eb2\u5230\u5e8a\u4e0b\uff0c\u542c\u8fd9\u811a\u6b65\u58f0\uff0c\u597d\u50cf\u662f\u76df\u4e3b\u6765\u4e86\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u6599\u60f3\u82e5\u7a7f\u7a97\u800c\u51fa\uff0c\u6df1\u591c\u95f4\u96be\u514d\u53d1\u51fa\u58f0\u97f3\uff0c\u52bf\u5fc5\u60ca\u52a8\u95e8\u5916\u7684\u4eba\u3002\u5982\u679c\u6765\u4eba\u771f\u662f\u970d\u5143 \u4f3d\uff0c\u8eab\u65c1\u5f88\u53ef\u80fd\u6709\u968f\u4ece\uff0c\u8bf4\u4e0d\u5b9a\u5df2\u6709\u4eba\u5728\u7a97\u5916\u76d1\u89c6\u3002\u4e3a\u9632\u4e07\u4e00\uff0c\u53ea\u597d\u6682\u65f6\u59d4\u5c48\u81ea\u5df1\uff0c\u4f9d\u8a00\u94bb \u5165\u5e8a\u4e0b\u3002 \u3000\u3000\u82d7\u7d20\u5170\u8fc5\u901f\u628a\u7a97\u5173\u597d\uff0c\u53c8\u56de\u5230\u5e8a\u4e0a\uff0c\u62e5\u88ab\u800c\u7720\u3002 \u3000\u3000\u811a\u6b65\u58f0\u5728\u95e8\u5916\u505c\u4f4f\uff0c\u63a5\u7740\u679c\u7136\u54cd\u8d77\u970d\u5143\u4f3d\u7684\u58f0\u97f3\u9053\uff1a\u201c\u592b\u4eba\u5f00\u95e8\uff01\u201d \u3000\u3000\u82d7\u7d20\u5170\u5e76\u4e0d\u7b54\u5e94\uff0c\u88c5\u4f5c\u7761\u53bb\u3002 \u3000\u3000\u76f4\u7b49\u970d\u5143\u4f3d\u53c8\u53eb\u4e86\u51e0\u58f0\uff0e\u5979\u624d\u53d1\u51fa\u61d2\u6d0b\u6d0b\u5982\u68a6\u521d\u9192\u7684\u58f0\u97f3\u9053\uff1a\u201c\u662f\u76df\u4e3b\u5427\uff1f\u600e\u4e48\u8fd9\u65f6\u5019 \u6765\u53eb\u95e8\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u6211\u60f3\u8fdb\u6765\u4e00\u4e0b\uff0c\u592b\u4eba\u8bf7\u5f00\u95e8\u3002\u201d \u3000\u3000\u82d7\u7d20\u5170\u9053\uff1a\u201c\u54b1\u4eec\u6709\u7ea6\u5728\u5148\uff0c\u767e\u65e5\u4e4b\u5185\uff0c\u5404\u81ea\u5206\u623f\uff0c\u76df\u4e3b\u8fd9\u65f6\u5019\u6765\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u5e72\u54b3\u4e86\u58f0\u9053\uff1a\u201c\u867d\u7136\u6709\u7ea6\u6709\u5148\uff0c\u4f46\u5e76\u6ca1\u89c4\u5b9a\u4f60\u7684\u623f\u95f4\u6211\u4e0d\u51c6\u8fdb\u6765\uff0c\u96be\u9053\u6211\u8fdb\u53bb\u5750 \u5750\u4e5f\u4e0d\u6210\uff1f\u201d \u3000\u3000\u201c\u53ef\u662f\u8fd9\u4e48\u665a\u4e86\uff0e\u4f60\u6211\u90fd\u8be5\u597d\u597d\u7761\u89c9\uff0c\u5982\u679c\u771f\u8981\u6765\uff0c\u5c31\u8be5\u660e\u5929\u518d\u6765\u3002\u201d \u3000\u3000\u201c\u5c31\u662f\u56e0\u4e3a\u7761\u4e0d\u7740\uff0c\u6211\u624d\u60f3\u6765\u8ddf\u4f60\u968f\u4fbf\u804a\u804a\uff0c\u4f60\u628a\u6211\u62d2\u4e4b\u95e8\u5916\uff0c\u6210\u4ec0\u4e48\u4f53\u7edf\uff1f\u201d \u3000\u3000\u82d7\u7d20\u5170\u65e0\u5948\u4e4b\u535c\uff0c\u53ea\u597d\u62ab\u8863\u8d77\u8eab\u6253\u5f00\u623f\u95e8\u3002 \u3000\u3000\u970d\u5143\u4f3d\u8fdb\u5165\u623f\u5185\uff0c\u5f84\u81ea\u5728\u5e8a\u6cbf\u5750\u4e0b\u3002 \u3000\u3000\u8fd9\u65f6\u4ed6\u7684\u4e24\u6761\u817f\uff0e\u6b63\u597d\u5728\u9ea6\u5c0f\u660e\u7684\u8eab\u524d\uff0c\u9ea6\u5c0f\u660e\u4e3a\u9632\u4e0d\u6d4b\uff0c\u53f3\u624b\u5df2\u7d27\u7d27\u63e1\u7740\u957f\u5251\uff0c\u53ea\u8981 \u4ed6\u62fc\u529b\u2014\u6325\uff0c\u8db3\u53ef\u628a\u970d\u5143\u4f3d\u7684\u53cc\u817f\u9f50\u6839\u524a\u65ad\u3002 \u3000\u3000\u4f46\u4ed6\u4e0d\u80fd\u8fd9\u6837\u505a\uff0c\u4ee5\u514d\u95ef\u51fa\u7978\u6765\uff0c\u56e0\u5c0f\u5931\u5927\u3002 \u3000\u3000\u53ea\u542c\u970d\u5143\u4f3d\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u4e0d\u70b9\u4e0a\u706f\uff1f\u201d \u3000\u3000\u82d7\u7d20\u5170\u9053\uff1a\u201c\u4f60\u6765\u4e0d\u8fc7\u662f\u8ddf\u6211\u804a\u804a\uff0c\u804a\u5929\u4f55\u5fc5\u70b9\u706f\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u9ed8\u4e86\u4e00\u9ed8\u9053\uff1a\u201c\u521a\u624d\u6211\u597d\u50cf\u542c\u5230\u4f60\u5728\u5173\u7a97\uff0c\u4e3a\u4ec0\u4e48\u8fd9\u65f6\u5019\u624d\u5173\u7a97\uff1f\u201d \u3000\u3000\u82d7\u7d20\u5170\u9053\uff1a\u201c\u4eca\u665a\u9152\u559d\u5f97\u592a\u591a\uff0c\u5934\u6709\u70b9\u660f\uff0c\u6211\u662f\u6253\u5f00\u7a97\u5b50\u900f\u900f\u6c14\uff0c\u5728\u7761\u89c9\u4e4b\u524d\uff0c\u5f53\u7136\u8981 \u628a\u7a97\u5b50\u5173\u597d\uff0e\u201d \u3000\u3000\u201c\u8fd9\u6837\u8bf4\u4f60\u662f\u521a\u624d\u624d\u8eba\u4e0b\u4e86\uff1f\u201d \u3000\u3000\u201c\u4f60\u5462\uff1f\u201d \u3000\u3000\u201c\u6211\u4e5f\u662f\u9152\u559d\u591a\u4e86\u7761\u4e0d\u7740\u3002\u201d \u3000\u3000\u201c\u90a3\u5c31\u73b0\u5728\u56de\u53bb\u7761\u5427\uff0c\u65f6\u95f4\u5df2\u7ecf\u4e0d\u65e9\u5566\uff0c\u4f60\u8eab\u4e3a\u76df\u4e3b\uff0e\u660e\u5929\u8fd8\u6709\u5f88\u591a\u4e8b\u60c5\u8981\u505a\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u84e6\u5730\u2014\u628a\u5c06\u82d7\u7d20\u5170\u6402\u8fdb\u6000\u4e2d\u9053\uff1a\u201c\u592b\u4eba\uff0c\u6211\u8981\u8ddf\u4f60\u4e00\u8d77\u7761\uff01\u201d \u3000\u3000\u82d7\u7d20\u5170\u6025\u5f97\u4e00\u9635\u731b\u529b\u6323\u624e\uff0c\u600e\u5948\u970d\u5143\u4f3d\u5929\u751f\u795e\u529b\uff0c\u518d\u600e\u4e48\u6323\u624e\u4e5f\u6323\u624e\u4e0d\u5f00\uff0c\u4e0d\u7531\u6c14\u6025\u8d25 \u574f\u7684\u53eb\u9053\uff1a\u201c\u76df\u4e3b\uff0c\u4f60\u672a\u514d\u5927\u8fc7\u4efd\u4e86\uff0c\u522b\u5fd8\u8bb0\u54b1\u4eec\u6709\u7ea6\u5728\u5148\u3002\u4f60\u8eab\u4e3a\u76df\u4e3b\uff0c\u600e\u53ef\u4e0d\u9075\u5b88\u8bfa \u8a00\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u4e5f\u6c14\u5598\u5401\u5401\u7684\u9053\uff1a\u201c\u592b\u4eba\uff0c\u4f60\u53ef\u77e5\u9053\u8001\u592b\u60f3\u4f60\u60f3\u4e86\u591a\u4e45\uff1f\u201d \u3000\u3000\u201c\u6700\u591a\u4e5f\u4e0d\u8d85\u8fc7\u4e24\u4e2a\u6708\uff01\u201d \u3000\u3000\u201c\u4f60\u9519\u5566\uff01\u4ece\u5f53\u5e74\u4f60\u968f\u80e1\u67cf\u9f84\u8fdb\u8c37\u65f6\uff0c\u8001\u592b\u5c31\u770b\u4e0a\u4e86\u4f60\uff0c\u53ea\u662f\u90a3\u65f6\u65e0\u6cd5\u8868\u793a\u800c\u5df3\uff0e\u201d \u3000\u3000\u201c\u73b0\u5728\u6211\u5df2\u662f\u4f60\u7684\u4eba\u4e86\uff0c\u8fd8\u6709\u4ec0\u4e48\u53ef\u60f3\u7684\uff1f\u201d \u3000\u3000\u201d\u4f60\u867d\u7136\u5728\u540d\u4e49\u4e0a\u5df2\u662f\u8001\u592b\u7684\u4eba\uff0c\u4f46\u8001\u592b\u5230\u73b0\u5728\u5374\u8fd8\u6ca1\u6709\u771f\u6b63\u5f97\u5230\u4f60\u3002\u201d \u3000\u3000\u201c\u90a3\u662f\u56e0\u4e3a\u5f7c\u6b64\u6709\u7ea6\u5728\u5148\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u6240\u6709\u8ff7\u8e2a\u8c37\u7684\u4eba\uff0c\u90fd\u4ee5\u4e3a\u54b1\u4eec\u4e24\u4e2a\u65e9\u5c31\u2026\u2026\u201d \u3000\u3000\u201c\u8fd9\u662f\u6211\u4eec\u81ea\u5df1\u7684\u4e8b\uff0c\u4f55\u5fc5\u63d0\u522b\u4eba\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u628a\u82d7\u7d20\u5170\u6402\u5f97\u66f4\u7d27\uff0c\u63a5\u7740\u5df2\u628a\u4eba\u538b\u5230\u5e8a\u4e0a\uff0c\u900f\u7740\u6ee1\u53e3\u9152\u6c14\u9053\uff1a\u201c\u592b\u4eba\uff0c\u4f60\u4e0d\u80fd\u518d \u62d2\u7edd\u8001\u592b\uff0c\u8001\u592b\u591c\u665a\u5b9e\u5728\u518d\u4e5f\u65e0\u6cd5\u5fcd\u53d7\uff01\u201d \u3000\u3000\u82d7\u7d20\u5170\u4e00\u9762\u7ffb\u6eda\u4e00\u9762\u9053\uff1a\u201c\u4f60\u82e5\u975e\u8fd9\u6837\u4e0d\u53ef\uff0c\u6211\u81ea\u4fe1\u4e5f\u96be\u4ee5\u62d2\u7edd\u3002\u53ef\u662f\u4f60\u8be5\u8ba9\u6211\u8d77\u6765\u628a \u8863\u670d\u8131\u6389\uff0c\u8fd9\u6837\u5b50\u54ea\u80fd\u529e\u4ec0\u4e48\u4e8b\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u559c\u51fa\u671b\u5916\uff0c\u4f9d\u8a00\u677e\u5f00\u4e86\u624b\u3002 \u3000\u3000\u82d7\u7d20\u5170\u5750\u8d77\u8eab\u6765\uff0c\u53f3\u624b\u5f80\u6795\u4e0b\u2014\u63a2\uff0c\u7a81\u7136\u4e00\u58f0\u51b7\u53f1\u9053\uff1a\u201c\u76df\u4e3b\uff0c\u4f60\u5982\u679c\u5e0c\u671b\u6211\u6b7b\uff0e\u5c31\u53ea \u7ba1\u6765\u903c\u6211\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u5927\u5403\u4e00\u60ca\uff0c\u867d\u7136\u5ba4\u5185\u5e76\u672a\u70b9\u706f\uff0c\u4ed6\u5374\u5df2\u770b\u51fa\u82d7\u7d20\u5170\u8fd9\u65f6\u624b\u91cc\u6b63\u63e1\u7740\u4e00\u628a\u660e\u6643\u6643\u7684 \u5315\u9996\uff0c\u800c\u4e14\u9876\u5728\u5979\u81ea\u5df1\u7684\u5fc3\u7a9d\u4e0a\u3002 \u3000\u3000\u53ea\u542c\u82d7\u7d20\u5170\u53c8\u9053\uff1a\u201c\u4f60\u5982\u679c\u518d\u6562\u903c\u6211\uff0c\u6211\u5c31\u6b7b\u7ed9\u4f60\u770b\u3002\u201d \u3000\u3000\u96f7\u5143\u4f3d\u60ca\u51fa\u4e00\u8eab\u51b7\u6c57\u9053\uff1a\u201c\u4e0d\u80af\u5c31\u4e0d\u80af\uff0c\u4f55\u5fc5\u5982\u6b64\uff1f\u201d \u3000\u3000\u82d7\u7d20\u5170\u9053\uff1a\u201c\u4f60\u82e5\u4e0d\u5982\u6b64\u903c\u6211\uff0e\u6211\u600e\u4f1a\u51fa\u6b64\u4e0b\u7b56\uff1f\u6709\u8bdd\u5230\u5e8a\u4e0b\u5750\u7740\u8bb2\uff0e\u4e0d\u51c6\u9760\u8fd1\u6211\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u53ea\u597d\u7531\u5e8a\u6cbf\u6539\u5750\u5230\u5e8a\u4e0b\u6905\u5b50\uff0c\u6447\u4e86\u6447\u5934\u9053\uff1a\u201c\u8fd9\u662f\u4f55\u82e6\uff01\u201d \u3000\u3000\u82d7\u7d20\u5170\u6c14\u54bb\u54bb\u7684\u9053\uff1a\u201c\u5e94\u5f53\u5148\u95ee\u95ee\u4f60\u521a\u624d\u662f\u4f55\u82e6\uff01\u201d \u3000\u3000\u201c\u8001\u592b\u5b9e\u5728\u662f\u5fcd\u53d7\u4e0d\u4e86\u4e86\uff0c\u8003\u8651\u4e86\u5f88\u4e45\uff0c\u624d\u539a\u7740\u9762\u76ae\u5230\u4f60\u623f\u4e2d\u6765\u3002\u5176\u5b9e\u8fd9\u4e5f\u662f\u4eba\u4e4b\u5e38\u60c5\uff0c \u56e0\u4e3a\u4f60\u603b\u662f\u6211\u7684\u592b\u4eba\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u4f60\u6211\u7684\u767e\u65e5\u4e4b\u7ea6\u66f4\u91cd\u8981\uff01\u66f4\u4f55\u51b5\u8ff7\u8e2a\u8c37\u9664\u4e86\u6211\u4e5f\u4e0d\u662f\u6ca1\u6709\u522b\u4eba\u3002\u201d \u3000\u3000\u201c\u4f60\u8bf4\u7684\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u4e0d\u662f\u6709\u4e2a\u4ec0\u4e48\u8bb8\u4ed9\u5b50\u5417\uff1f\u4f60\u65e2\u80fd\u628a\u5979\u5b89\u6392\u4e1c\u6d77\u9662\u4f9b\u522b\u4eba\u73a9\u4e50\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u7559\u7740\u81ea\u5df1\u6d88 \u9063\uff1f\u201d \u3000\u3000\u201c\u592b\u4eba\u53ef\u662f\u5403\u918b\u4e86\uff1f\u201d \u3000\u3000\u201c\u6211\u4ec0\u4e48\u918b\u4e0d\u597d\u5403\uff0c\u5403\u90a3\u79cd\u5973\u4eba\u7684\u918b\u3002\u201d \u3000\u3000\u201c\u4f60\u65e2\u77e5\u9053\u90a3\u662f\u4e2a\u8d31\u5973\u4eba\uff0c\u5c31\u4e0d\u8be5\u8bf4\u521a\u624d\u90a3\u79cd\u8bdd\u3002\u201d \u3000\u3000\u201c\u5979\u65e2\u7136\u662f\u8d31\u5973\u4eba\uff0c\u4f60\u4e3a\u4ec0\u4e48\u53c8\u628a\u5979\u79f0\u4e3a\u4ed9\u5b50\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u8038\u4e86\u8038\u80a9\uff0c\u563f\u563f\u7b11\u9053\uff1a\u201c\u5f53\u7740\u5979\u7684\u9762\uff0c\u5f53\u7136\u8981\u8bf4\u5979\u559c\u6b22\u542c\u7684\uff0c\u56e0\u4e3a\u8001\u592b\u8981\u5229\u7528 \u5979\u3002\u201d \u3000\u3000\u82d7\u7d20\u5170\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u5229\u7528\u5979\u627e\u8ddf\u5934\u683d\uff0c\u662f\u5417\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u4e00\u6123\u9053\uff1a\u201c\u592b\u4eba\u8fd9\u8bdd\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u82d7\u7d20\u5170\u9053\uff1a\u201c\u522b\u5fd8\u4e86\u9ea6\u5c0f\u660e\u4e8b\u4ef6\uff0c\u8fd8\u4e0d\u662f\u5168\u7531\u90a3\u5973\u4eba\u8eab\u4e0a\u800c\u8d77\u3002\u4f60\u8eab\u4e3a\u7eff\u6797\u76df\u4e3b\uff0c\u88ab\u5f53 \u4f17\u5f04\u5f97\u7070\u5934\u571f\u8138\uff0c\u96be\u9053\u8fd9\u8ddf\u5934\u683d\u5f97\u8fd8\u4e0d\u591f\u5927\u5417\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u5e72\u54b3\u4e86\u58f0\u9053\uff1a\u201c\u8fd9\u4e8b\u8001\u592b\u6b63\u8981\u95ee\u4f60\u3002\u201d \u3000\u3000\u82d7\u7d20\u5170\u9053\uff1a\u201c\u4f60\u95ee\u6211\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u9ea6\u5c0f\u660e\u672c\u6765\u5df2\u96be\u9003\u4e00\u6b7b\uff0c\u504f\u504f\u4f60\u5374\u8fc7\u53bb\u8981\u548c\u4ed6\u8bb2\u4ec0\u4e48\u8bdd\uff0c\u4ee5\u81f4\u843d\u5728\u4ed6\u624b\u4e2d\uff0e\u6211\u4e3a\u4e86\u4f60 \u4e0d\u53d7\u5230\u4f24\u5bb3\uff0c\u4e0d\u5f97\u4e0d\u59d4\u5c48\u6c42\u5168\u7b54\u5e94\u4ed6\u7684\u8981\u6c42\uff0c\u8fd9\u4e8b\u600e\u80fd\u6028\u6211\uff1f\u6211\u5012\u8981\u95ee\u95ee\u4f60\u4e3a\u4ec0\u4e48\u8981\u8fc7\u53bb\u8ddf \u4ed6\u8bb2\u8bdd\u3002\u201d \u3000\u3000\u201c\u4f60\u61c2\u4ec0\u4e48\uff1f\u6211\u4e5f\u662f\u4e3a\u4e86\u4f60\u3002\u201d \u3000\u3000\u201c\u8fd9\u8bdd\u8001\u592b\u4e0d\u61c2\u3002\u201d \u3000\u3000\u201c\u6211\u89c1\u4ed6\u8eab\u540e\u5168\u662f\u67cf\u9f84\u9662\u7684\u4eba\uff0c\u82e5\u4ed6\u9003\u8dd1\uff0c\u67cf\u9f84\u9662\u7684\u4eba\u5fc5\u5b9a\u4e0d\u80af\u53bb\u8ffd\u3002\u800c\u4f60\u624b\u4e0b\u7684\u4eba\uff0c \u56e0\u4e3a\u9694\u5f97\u8fdc\uff0c\u9ea6\u5c0f\u660e\u7684\u8f7b\u529f\u53c8\u9ad8\uff0c\u600e\u4f1a\u8ffd\u5f97\u4e0a\u4ed6\u3002\u201d \u3000\u3000\u201c\u6240\u4ee5\u4f60\u5c31\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u9519\uff01\u6211\u5047\u88c5\u8981\u8fc7\u53bb\u548c\u4ed6\u8bb2\u51e0\u53e5\u8bdd\uff0c\u53ea\u5e0c\u671b\u80fd\u5728\u8fd1\u8ddd\u79bb\u8d81\u4ed6\u4e0d\u6ce8\u610f\u65f6\u4e00\u6307\u5c06\u4ed6\u70b9\u5012\uff0e \u54ea\u77e5\u53cd\u800c\u5f04\u5de7\u6210\u62d9\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u731b\u4e00\u8dfa\u811a\u9053\uff1a\u201c\u4f60\u771f\u7cca\u6d82\uff0c\u4ee5\u9ea6\u5c0f\u660e\u7684\u6b66\u529f\uff0c\u4f60\u5982\u4f55\u80fd\u5236\u5f97\u4f4f\u4ed6\uff1f\u201d \u3000\u3000\u82d7\u7d20\u5170\u9053\uff1a\u201c\u6211\u5f53\u7136\u77e5\u9053\u4ed6\u6b66\u529f\u9ad8\uff0c\u4f46\u4ed6\u82e5\u771f\u5728\u6beb\u65e0\u6212\u5907\u7684\u72b6\u51b5\u4e0b\u654c\u4eba\u731d\u7136\u65bd\u88ad\uff0c\u6b66\u529f \u518d\u9ad8\u53c8\u6709\u4f55\u7528\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u8fde\u8fde\u6447\u4e86\u6447\u624b\u9053\uff1a\u201c\u7b97\u4e86\u7b97\u4e86\uff01\u4e8b\u60c5\u5df2\u7ecf\u8fc7\u53bb\uff0c\u8001\u592b\u518d\u8d23\u5907\u4f60\u4e5f\u6ca1\u7528\uff0e\u53ea\u6709\u81ea\u8ba4 \u5012\u9709\u4e86\uff01\u201d \u3000\u3000\u82d7\u7d20\u5170\u4f3c\u662f\u5df2\u6709\u4e9b\u4e0d\u8010\u70e6\uff1a\u201c\u4f60\u8fd8\u6709\u4ec0\u4e48\u8c08\u7684\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u8001\u592b\u53ea\u6c42\u4eca\u591c\u80fd\u8ddf\u4f60\u6b63\u5f0f\u56e2\u5706\uff0c\u65e2\u7136\u4f60\u4e0d\u80af\uff0c\u4ec0\u4e48\u4e5f\u6ca1\u8c08\u7684\u4e86\uff01\u201d \u3000\u3000\u82d7\u7d20\u5170\u9053\uff1a\u201c\u90a3\u5c31\u8bf7\u56de\u5230\u4f60\u81ea\u5df1\u7684\u623f\u91cc\u4f11\u606f\u5427\uff01\u800c\u4e14\u6211\u4e5f\u8981\u7761\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u7ad9\u8d77\u8eab\u6765\uff0c\u957f\u957f\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u6709\u4e86\u538b\u5be8\u592b\u4eba\u6709\u4ec0\u4e48\u7528\uff0c\u5230\u73b0\u5728\u8fd8\u662f\u4e00\u5bf9\u6302\u540d\u5927 \u59bb\uff0c\u53ea\u80fd\u53eb\u8001\u592b\u5e72\u7740\u6025\uff01\u201d \u3000\u3000\u82d7\u7d20\u5170\u9053\uff1a\u201c\u6211\u5df2\u7ecf\u5f88\u5bf9\u5f97\u4f4f\u4f60\u4e86\uff0e\u5728\u516c\u5f00\u573a\u6240\uff0c\u5904\u5904\u987a\u7740\u4f60\uff0c\u8fd8\u8981\u6211\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u522b\u8bf4\u4e86\uff0c\u8001\u592b\u5f88\u77e5\u8da3\uff0c\u73b0\u5728\u5c31\u8d70\uff0e\u201d \u3000\u3000\u970d\u5143\u4f3d\u8d70\u540e\uff0c\u82d7\u7d20\u5170\u4e0b\u5e8a\u628a\u623f\u95e8\u5173\u4e0a\uff0c\u7136\u540e\u53c8\u56de\u5230\u5e8a\u4e0a\uff0c\u5947\u602a\u7684\u662f\u7adf\u6ca1\u53eb\u9ea6\u5c0f\u660e\u51fa\u6765\uff0c \u597d\u50cf\u5fd8\u8bb0\u5e8a\u4e0b\u8fd8\u85cf\u7740\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u65b9\u624d\u970d\u5143\u4f3d\u548c\u82d7\u7d20\u5170\u7684\u5bf9\u8bdd\uff0c\u9ea6\u5c0f\u660e\u81ea\u662f\u542c\u5f97\u6e05\u6e05\u695a\u695a\uff0e \u3000\u3000\u4ed6\u672c\u6765\u8ba4\u4e3a\u82d7\u7d20\u5170\u4ecd\u7136\u5fc3\u5411\u67cf\u9f84\u9662\uff0c\u800c\u4e14\u662f\u6709\u610f\u6551\u81ea\u5df1\u3002\u6b64\u523b\u8fd9\u60f3\u6cd5\u5374\u53c8\u5f00\u59cb\u52a8\u6447\uff0c\u4e00 \u5207\u90fd\u9677\u5165\u6251\u6714\u8ff7\u79bb\u4e4b\u4e2d\uff0c\u4f3c\u4e4e\u5f04\u4e0d\u6e05\u65b9\u624d\u5979\u5bf9\u970d\u5143\u4f3d\u6240\u8bf4\u7684\u8bdd\u7a76\u7adf\u662f\u771f\u662f\u5047\u3002 \u3000\u3000\u4e0d\u8fc7\u552f\u4e00\u4ee4\u4ed6\u89c9\u5f97\u82d7\u7d20\u5170\u4ecd\u6709\u53ef\u53d6\u4e4b\u5904\u7684\uff0c\u662f\u5979\u548c\u970d\u5143\u4f3d\u4e4b\u95f4\u81f3\u4eca\u5c1a\u4e0d\u53ca\u4e8e\u4e71\u3002\u81f3\u4e8e\u767e \u65e5\u4e4b\u7ea6\u662f\u600e\u4e48\u56de\u4e8b\uff0c\u90a3\u5c31\u4e0d\u5f97\u800c\u77e5\u4e86\u3002 \u3000\u3000\u4ed6\u89c1\u82d7\u7d20\u5170\u4e00\u76f4\u4e0d\u80af\u7406\u4f1a\u81ea\u5df1\uff0c\u53ea\u597d\u81ea\u5df1\u94bb\u4e86\u51fa\u6765\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u53eb\u6211\u51fa\u6765\uff1f\u201d \u3000\u3000\u82d7\u7d20\u5170\u58f0\u97f3\u51b7\u51b7\u7684\u9053\uff1a\u201c\u4f60\u660e\u660e\u77e5\u9053\u4ed6\u8d70\u4e86\uff0c\u8fd8\u7528\u6211\u53eb\u5417\uff1f\u201d \u3000\u3000\u201c\u5728\u4eba\u60c5\u4e0a\uff0c\u4f60\u5e94\u8be5\u5148\u53eb\u6211\u2014\u58f0\u3002\u201d \u3000\u3000\u201c\u4f60\u6df1\u591c\u95ef\u5230\u6211\u623f\u91cc\u6765\uff0c\u8fd8\u6709\u4ec0\u4e48\u4eba\u60c5\u53ef\u8a00\uff1f\u201d \u3000\u3000\u6b64\u523b\u7684\u9ea6\u5c0f\u660e\uff0c\u771f\u6068\u4e0d\u5f97\u63b4\u82d7\u7d20\u5170\u51e0\u638c\u3002 \u3000\u3000\u4ed6\u4ece\u524d\u5bf9\u82d7\u7d20\u5170\uff0c\u672c\u6765\u79c1\u4e0b\u91cc\u5341\u5206\u7231\u6155\uff0c\u53ea\u56e0\u5e74\u9f84\u76f8\u5dee\u5f88\u591a\uff0c\u56e0\u4e4b\u59cb\u7ec8\u4e0d\u6562\u8868\u8fbe\u3002\u800c\u73b0 \u5728\u5462\uff0c\u4ed6\u5df2\u5f04\u4e0d\u6e05\u5bf9\u65b9\u7a76\u7adf\u662f\u4e2a\u4ec0\u4e48\u6837\u7684\u5973\u4eba\u3002 \u3000\u3000\u5728\u5fcd\u65e0\u53ef\u5fcd\u4e4b\u4e0b\uff0c\u4ed6\u51b3\u5b9a\u5c31\u6b64\u79bb\u5f00\u3002\u6765\u5230\u7a97\u524d\uff0e\u8f7b\u8f7b\u6253\u5f00\u7a97\u9053\uff1a\u201c\u6211\u7684\u786e\u662f\u6709\u91cd\u8981\u7684\u4e8b \u6765\u89c1\u4f60\uff0c\u65e2\u7136\u4f60\u4ee5\u8fd9\u79cd\u6001\u5ea6\u5bf9\u6211\uff0c\u6211\u5c31\u51b3\u5b9a\u4e0d\u8bf4\u4e86\uff0e\u970d\u592b\u4eba\uff0c\u518d\u89c1\uff01\u201d \u3000\u3000\u8c01\u77e5\u4ed6\u521a\u8981\u7a7f\u7a97\u800c\u51fa\uff0c\u82d7\u7d20\u5170\u5374\u53c8\u4f4e\u559d\u9053\uff1a\u201c\u6162\u7740\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u83ab\u975e\u4f60\u53c8\u60f3\u542c\u4e86\uff1f\u201d \u3000\u3000\u201c\u4f60\u4e0d\u8bf4\u51fa\u6765\uff0c\u8ba9\u6211\u542c\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u4f60\u771f\u7684\u60f3\u542c\uff1f\u201d \u3000\u3000\u201c\u5982\u679c\u662f\u548c\u6211\u6709\u5173\u7684\u4e8b\uff0c\u5f53\u7136\u60f3\u542c\u3002\u201d \u3000\u3000\u201c\u6b63\u662f\u548c\u4f60\u6709\u5173\uff0c\u4e0d\u7136\u53c8\u4f55\u5fc5\u95ef\u8fdb\u8fd9\u91cc\u5bf9\u4f60\u8bb2\u3002\u201d \u3000\u3000\u201c\u4f60\u8bf4\u3002\u201d \u3000\u3000\u201c\u4f60\u53ef\u6709\u4e00\u4f4d\u59b9\u59b9\u53eb\u82d7\u7d22\u82d3\uff1f\u201d \u3000\u3000\u82d7\u7d20\u5170\u679c\u7136\u7559\u4e0a\u4e86\u610f\uff0c\u8bb6\u7136\u95ee\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u5ffd\u7136\u63d0\u8d77\u5979\u6765\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u89c1\u8fc7\u5979\u3002\u201d \u3000\u3000\u201c\u5979\u5728\u5415\u7cb1\u5c71\u4e5d\u5929\u7384\u6d1e\u5e9c\uff0c\u4f60\u600e\u4f1a\u89c1\u5230\u5979\uff1f\u201d \u3000\u3000\u201c\u53cd\u6b63\u6211\u5df2\u7ecf\u89c1\u7740\u5979\u4e86\uff0c\u81f3\u4e8e\u600e\u6837\u89c1\u5230\u5979\u7684\uff0c\u8bf7\u6055\u4e0d\u4fbf\u660e\u544a\uff0e\u201d \u3000\u3000\u201c\u5176\u5b9e\u6211\u5df2\u731c\u5f97\u51fa\uff0c\u4e0a\u6b21\u5728\u8c37\u5916\u9047\u5230\u4f60\uff0c\u4f60\u5f88\u53ef\u80fd\u5c31\u662f\u5230\u5415\u6881\u5c71\u5411\u5bb6\u5e08\u6c42\u53d6\u2018\u5411\u5fc3\u9732\u2019 \u7684\u89e3\u836f\uff0e\u770b\u5230\u6211\u59b9\u59b9\u5e76\u4e0d\u8db3\u5947\u3002\u5979\u5bf9\u4f60\u6709\u4ec0\u4e48\u4ea4\u4ee3\uff1f\u201d \u3000\u3000\u201c\u5979\u5f88\u6000\u5ff5\u4f60\uff0c\u53ef\u60dc\u4f60\u7adf\u662f\u5982\u6b64\u7684\u65e0\u60c5\u65e0\u4e49\uff01\u201d \u3000\u3000\u201c\u4f4f\u5634\uff01\u4f60\u600e\u77e5\u6211\u65e0\u60c5\u65e0\u4e49\uff1f\u201d \u3000\u3000\u201c\u4f60\u82e5\u6709\u60c5\u6709\u4e49\uff0c\u5c31\u4e0d\u8be5\u8fd9\u6837\u5bf9\u5f85\u6211\uff01\u201d \u3000\u3000\u201c\u6211\u8ddf\u4f60\u54ea\u91cc\u6765\u7684\u60c5\u4e49\uff1f\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u679c\u7136\u628a\u9ea6\u5c0f\u660e\u95ee\u5f97\u6709\u4e9b\u8bed\u585e\uff0c\u987f\u4e86\u4e00\u987f\u9053\uff1a\u201c\u4f60\u6211\u4e4b\u95f4\u867d\u7136\u6ca1\u6709\uff0c\u4f46\u4f60\u548c\u82d7\u7d20\u82d3 \u59d0\u59b9\u4e4b\u95f4\uff0c\u603b\u8be5\u6709\u60c5\u4e49\u5427\uff1f\u201d \u3000\u3000\u82d7\u7d20\u5170\u9053\uff1a\u201c\u90a3\u8fd8\u7528\u4f60\u8bf4\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u5401\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u90a3\u5c31\u597d\uff0c\u4e5f\u8bb8\u6211\u591c\u665a\u4e0d\u66fe\u767d\u6765\u3002\u73b0\u5728\u6211\u90d1\u91cd\u544a\u8bc9\u4f60\uff0c\u4f60\u59b9\u59b9\u8981\u89c1 \u4f60\uff0c\u4e3a\u4e86\u987e\u8651\u4e0d\u8ba9\u970d\u5143\u4f3d\u77e5\u9053\uff0c\u5e0c\u671b\u4f60\u80fd\u4e3b\u52a8\u5b89\u6392\u4e00\u4e2a\u4e0e\u5979\u79d8\u5bc6\u76f8\u89c1\u7684\u673a\u4f1a\u3002\u201d \u3000\u3000\u82d7\u7d20\u5170\u4f3c\u662f\u96be\u4ee5\u76f8\u4fe1\uff0c\u51b7\u51b7\u4e00\u7b11\u9053\uff1a\u201c\u5c0f\u660e\uff0c\u4f60\u600e\u4e48\u5728\u6211\u9762\u524d\u7741\u7740\u773c\u76f4\u8bf4\u778e\u8bdd\uff0e\u96be\u9053\u6211 \u662f\u4e09\u5c81\u4e24\u5c81\u7684\u5c0f\u5b69\uff0c\u90a3\u4e48\u5bb9\u6613\u6b3a\u9a97\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u5b9e\u5bf9\u4f60\u8bf4\uff0c\u4e0a\u6b21\u6211\u5230\u5415\u6881\u5c71\u4e5d\u5929\u7384\u9634\u6d1e\u5e9c\uff0c\u5df2\u628a\u4ee4\u59b9\u5e26\u4e86\u51fa\u6765\u3002\u4e0a\u6b21\u4ee4\u5e08 \u6765\u8ff7\u8e2a\u8c37\u65f6\uff0c\u66fe\u5411\u6211\u8981\u4eba\uff0c\u5f53\u65f6\u4f60\u4e5f\u5728\u573a\u3002\u96be\u9053\u6ca1\u542c\u89c1\uff1f\u201d \u3000\u3000\u82d7\u7d20\u5170\u9053\uff1a\u201c\u6211\u5f53\u65f6\u5df2\u88ab\u4ed6\u70b9\u4e86\u7a74\u9053\uff0c\u660f\u53a5\u5728\u5927\u5385\u95e8\u5916\uff0c\u5982\u4f55\u542c\u5f97\u89c1\u4ed6\u66fe\u548c\u4f60\u8bb2\u8fc7\u4ec0 \u4e48\uff1f\u201d \u3000\u3000\u201c\u90a3\u6211\u73b0\u5728\u5c31\u518d\u544a\u8bc9\u4f60\uff0c\u4ee4\u59b9\u5df2\u5728\u591a\u5929\u524d\u968f\u6211\u6df7\u8fdb\u4e86\u8ff7\u8e2a\u8c37\u3002\u201d \u3000\u3000\u82d7\u7d20\u5170\u987f\u65f6\u663e\u5f97\u5927\u4e3a\u6fc0\u52a8\u9053\uff1a\u201c\u771f\u7684\uff1f\u5979\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u5979\u5728\u54ea\u91cc\uff0c\u76ee\u524d\u53ea\u6709\u6211\u4e00\u4eba\u77e5\u9053\uff0c\u9664\u975e\u4f60\u63d0\u51fa\u4fdd\u8bc1\uff0e\u6211\u624d\u4f1a\u544a\u8bc9\u4f60\u3002\u201d \u3000\u3000\u201c\u4f60\u8981\u6211\u63d0\u51fa\u4ec0\u4e48\u4fdd\u8bc1\uff1f\u201d \u3000\u3000\u201c\u4e0d\u80fd\u8ba9\u970d\u5143\u4f3d\u77e5\u9053\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e48\u7b80\u5355\u7684\u4e8b\uff0c\u4e5f\u8981\u6211\u4fdd\u8bc1\uff0e\u201d \u3000\u3000\u201c\u522b\u5fd8\u4e86\u4f60\u73b0\u5728\u5df2\u662f\u4ed6\u7684\u538b\u5be8\u592b\u4eba\u3002\u201d \u3000\u3000\u201c\u6211\u548c\u4ed6\u53ea\u662f\u540d\u4e49\u4e0a\u7684\u592b\u5987\uff0c\u96be\u9053\u4f60\u521a\u624d\u5728\u5e8a\u4e0b\u6ca1\u542c\u5230\uff1f\u201d \u3000\u3000\u201c\u597d\uff0c\u90a3\u6211\u5c31\u544a\u8bc9\u4f60\uff0c\u5979\u76ee\u524d\u5728\u67cf\u9f84\u9662\u3002\u201d \u3000\u3000\u82d7\u7d20\u5170\u5f3a\u5fcd\u7740\u6fc0\u52a8\uff0c\u9ed8\u4e86\u4e00\u9ed8\u9053\uff1a\u201c\u660e\u5929\u2014\u66f4\u5165\u591c\u540e\uff0c\u8981\u5979\u5728\u4e1c\u6d77\u9662\u540e\u4fa7\u7684\u6797\u836b\u91cc\u7b49\u6211\u3002 \u90a3\u91cc\u5c11\u6709\u4eba\u53bb\uff0e\u53c8\u662f\u4e00\u7247\u6811\u6797\uff0c\u4e0d\u592a\u53ef\u80fd\u6709\u4eba\u770b\u5230\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u5c31\u8fd9\u6837\u529e\uff0c\u6211\u8be5\u8d70\u4e86\uff0c\u96be\u9053\u4f60\u8fde\u4e00\u53e5\u5411\u6211\u9053\u8c22\u7684\u8bdd\u4e5f\u6ca1\u6709\uff1f\u201d \u3000\u3000\u82d7\u7d20\u5170\u9053\uff1a\u201c\u6211\u5f53\u7136\u8be5\u8c22\u8c22\u4f60\uff0c\u4e0d\u8fc7\u4f60\u534a\u591c\u4e09\u66f4\u95ef\u5230\u6211\u623f\u4e2d\u6765\uff0c\u5b9e\u5728\u662f\u4ef6\u5192\u5931\u53c8\u5192\u9669\u7684 \u4e8b\uff0c\u4ee5\u540e\u5343\u4e07\u4e0d\u53ef\u518d\u8fd9\u6837\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u8038\u8038\u80a9\u9053\uff1a\u201c\u6211\u53c8\u4f55\u5c1d\u613f\u610f\u8fd9\u6837\u505a\uff0e\u53ea\u662f\u4f60\u73b0\u5728\u5df2\u505a\u4e86\u5929\u4e0b\u7eff\u6797\u76df\u4e3b\u7684\u538b\u5be8\u592b\u4eba\uff0c \u6211\u4e0d\u8fc7\u662f\u4e00\u4e2a\u5c0f\u5934\u76ee\uff0c\u4e0d\u8fd9\u6837\u53c8\u600e\u80fd\u5355\u72ec\u89c1\u5230\u4f60\u3002\u201d \u3000\u3000\u82d7\u7d20\u5170\u8f6c\u8fc7\u8138\u53bb\u9053\uff1a\u201c\u4f60\u8d70\u5427\uff01\u8def\u4e0a\u8981\u7559\u610f\u4e00\u70b9\u3002\u201d \u3000\u3000\u6b21\u65e5\u65e9\u996d\u540e\u4e0d\u4e45\uff0c\u3000\u4e00\u4e2a\u7350\u5934\u9f20\u76ee\u3001\u5c16\u5634\u7f29\u816e\uff0e\u7559\u7740\u4e00\u64ae\u5c71\u7f8a\u80e1\uff0c\u8eab\u7a7f\u7070\u8272\u957f\u886b\u7684\u4eba\uff0c \u6447\u6447\u6446\u6446\u5927\u6a21\u5927\u6837\u7684\u6765\u5230\u67cf\u9f84\u9662\uff0e \u3000\u3000\u8fd9\u4eba\u7684\u8eab\u540e\u8ddf\u7740\u4e00\u4e2a\u968f\u4ece\u55bd\u5175\uff0c\u624b\u91cc\u63d0\u7740\u4e00\u53ea\u836f\u7bb1\u3002 \u3000\u3000\u539f\u6765\u8fd9\u4eba\u662f\u4e01\u5e08\u7237\uff0c\u4e0d\u7528\u8bf4\u662f\u5949\u970d\u5143\u4f3d\u4e4b\u547d\uff0c\u6765\u4e3a\u4e07\u6620\u971e\u8bca\u75c5\u7684\u3002 \u3000\u3000\u67cf\u9f84\u9662\u7684\u7fa4\u8c6a\uff0c\u90fd\u5728\u5ba2\u5385\u91cc\u3002 \u3000\u3000\u542c\u8bf4\u4e01\u5e08\u7237\u5230\u4e86\uff0c\u949f\u4e00\u8c6a\u548c\u4f59\u4ea6\u4e50\u9887\u4e3a\u6bb7\u52e4\u7684\u8fce\u51fa\u5ba2\u5385\u95e8\u5916\u3002 \u3000\u3000\u5982\u679c\u628a\u8ff7\u8e2a\u8c37\u6bd4\u4f5c\u7687\u57ce\uff0c\u73b0\u5728\u7684\u4e01\u5e08\u7237\uff0c\u8be5\u662f\u5fa1\u533b\u4e86\uff0c\u949f\u4e00\u8c6a\u548c\u4f59\u4ea6\u4e50\u5f53\u7136\u4e0d\u80fd\u6020\u6162\u3002 \u3000\u3000\u4e24\u4eba\u628a\u4e01\u5e08\u7237\u8fce\u8fdb\u5ba2\u5385\u5750\u4e0b\uff0c\u4f59\u4ea6\u4e50\u8fd8\u4eb2\u81ea\u6ce1\u4e0a\u2014\u676f\u8336\u3002 \u3000\u3000\u4e01\u5e08\u7237\u6628\u5929\u5728\u805a\u4e49\u5385\u66fe\u88ab\u738b\u5927\u5eb7\u548c\u9ea6\u5c0f\u660e\u7b49\u4eba\u8fb1\u9a82\u8fc7\uff0c\u672c\u4ee5\u4e3a\u6765\u5230\u67cf\u9f84\u9662\u5fc5\u5b9a\u53c8\u53d7\u5230\u51b7 \u5632\u70ed\u8bbd\uff0c\u56e0\u4e4b\u5728\u8def\u4e0a\u5fc3\u91cc\u4e00\u76f4\u5600\u5495\uff0e \u3000\u3000\u6b64\u523b\u89c1\u8fde\u8eab\u4e3a\u526f\u76df\u4e3b\u7684\u949f\u2014\u8c6a\u548c\u4e00\u5411\u53d7\u4eba\u5c0a\u91cd\u7684\u4f59\u4ea6\u4e50\u90fd\u793c\u8c8c\u76f8\u5f85\uff0c\u603b\u7b97\u653e\u4e0b\u5fc3\u91cc\u2014\u5757 \u77f3\u5934\u3002 \u3000\u3000\u4ed6\u559d\u4e86\u53e3\u8336\u9053\uff1a\u201c\u4e0d\u77e5\u4e07\u59d1\u5a18\u7684\u75c5\uff0c\u4eca\u5929\u6709\u4e9b\u8d77\u8272\u6ca1\u6709\uff1f\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u6447\u6447\u5934\uff0c\u53c8\u76b1\u76b1\u7709\u9053\uff1a\u201c\u4e0d\u7792\u4e01\u5e08\u7237\uff0c\u4e07\u59d1\u5a18\u7684\u75c5\uff0c\u7adf\u662f\u8d8a\u6765\u8d8a\u91cd\u4e86\u3002\u76ee\u524d\u4e0d\u4f46 \u7c73\u6c34\u672a\u8fdb\uff0c\u8fde\u8bdd\u90fd\u65e0\u6cd5\u8bb2\u51fa\uff0c\u771f\u4e0d\u77e5\u8be5\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u4e01\u5e08\u7237\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u4f1a\u75c5\u5f97\u8fd9\u6837\u91cd\uff0c\u4e0d\u8fc7\u6ca1\u5173\u7cfb\u2026\u2026\u201d \u3000\u3000\u7ad9\u5728\u4e00\u65c1\u7684\u738b\u5927\u5eb7\uff0c\u4e0d\u7b49\u4ed6\u8bf4\u5b8c\uff0e\u4fbf\u4e24\u773c\u4e00\u77aa\u9053\uff1a\u201c\u4f60\u8fd9\u8001\u5c0f\u5b50\u662f\u8bb2\u8bdd\u8fd8\u662f\u653e\u5c41\uff1f\u201d \u3000\u3000\u4e01\u5e08\u7237\u5446\u4e86\u4e00\u5446\u9053\uff1a\u201c\u738b\u5934\u9886\u600e\u4e48\u5f00\u53e3\u5c31\u9a82\u4eba\uff1f\u201d \u3000\u3000\u738b\u5927\u5eb7\u543c\u9053\uff1a\u201c\u4e07\u59d1\u5a18\u75c5\u5f97\u5df2\u7ecf\u5feb\u6b7b\u4e86\uff0c\u4f60\u8fd8\u8bf4\u6ca1\u5173\u7cfb\uff0c\u8fd9\u90a3\u91cc\u50cf\u4eba\u8bdd\uff01\u4e0d\u662f\u653e\u5c41\u662f\u4ec0 \u4e48\u3002\u201d \u3000\u3000\u4e01\u5e08\u7237\u54bd\u4e0b\u4e00\u53e3\u553e\u6cab\u9053\uff1a\u201c\u539f\u6765\u738b\u5934\u9886\u8bef\u4f1a\u5566\uff01\u8001\u673d\u7684\u610f\u601d\uff0c\u662f\u8bf4\u4e07\u59d1\u5a18\u867d\u7136\u75c5\u5f97\u5f88\u91cd\uff0c \u5f97\u7ecf\u8fc7\u8001\u673d\u8bca\u65ad\u4e4b\u540e\uff0c\u5fc5\u5b9a\u53ef\u4ee5\u5999\u624b\u56de\u6625\u3002\u201d \u3000\u3000\u738b\u5927\u5eb7\u51b7\u7b11\u9053\uff1a\u201c\u5c31\u51ed\u4f60\u90a3\u53cc\u624b\uff0c\u50cf\u7caa\u53c9\u5b50\u4e00\u6837\uff0c\u4e5f\u914d\u79f0\u5999\u624b\uff01\u201d \u3000\u3000\u4e01\u5e08\u7237\u82e6\u7b11\u9053\uff1a\u201c\u5999\u624b\u4e0d\u5999\u624b\uff0c\u4e0d\u5728\u4e8e\u957f\u5f97\u597d\u770b\u4e0d\u597d\u770b\u3002\u738b\u7fb2\u4e4b\u662f\u4e66\u5723\uff0c\u5434\u9053\u5b50\u662f\u753b\u5723\uff0c \u4ed6\u4eec\u7684\u624b\u4e5f\u4e0d\u89c1\u5f97\u597d\u770b\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u62a2\u7740\u9053\uff1a\u201c\u4e01\u5e08\u7237\u591a\u5927\u5e74\u7eaa\u5566\uff1f\u201d \u3000\u3000\u4e01\u5e08\u7237\u4e0d\u89c9\u4e00\u6123\u9053\uff1a\u201c\u9ea6\u5c0f\u4fa0\u4e3a\u4ec0\u4e48\u5ffd\u7136\u95ee\u8d77\u8001\u673d\u7684\u5e74\u9f84\u6765\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4f60\u522b\u95ee\u8fd9\u4e2a\uff0c\u53ea\u7ba1\u8bf4\u51fa\u591a\u5927\u5e74\u7eaa\u6765\u3002\u201d \u3000\u3000\u4e01\u5e08\u7237\u634b\u4e86\u634b\u5c71\u7f8a\u80e1\u5b50\uff0c\u5e26\u70b9\u501a\u8001\u5356\u8001\u7684\u610f\u5473\u9053\uff1a\u201c\u82e5\u95ee\u8d77\u8001\u673d\u7684\u5e74\u7eaa\uff0c\u867d\u7136\u6bd4\u8d77\u767e\u9f84 \u9ad8\u5bff\u7684\u524d\u8f88\u8fd8\u5dee\u4e86\u4e00\u5927\u622a\uff0c\u4f46\u5728\u8ff7\u8e2a\u8c37\u5185\uff0c\u5374\u5f88\u5c11\u6709\u4eba\u6562\u6bd4\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u7528\u4e0d\u7740\u5356\u8001\u8d44\u683c\uff0c\u5230\u5e95\u591a\u5927\uff1f\u201d \u3000\u3000\u4e01\u5e08\u7237\u563f\u563f\u7b11\u9053\uff1a\u201c\u8001\u673d\u5df2\u865a\u5ea6\u4e86\u516d\u5341\u6709\u516b\u6625\u79cb\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u51b7\u51b7\u4e00\u7b11\u9053\uff1a\u201c\u539f\u6765\u4f60\u624d\u4e0d\u8fc7\u516d\u5341\u516b\u5c81\uff0c\u738b\u7fb2\u4e4b\u548c\u5434\u9053\u5b50\uff0c\u2014\u4f4d\u6b7b\u4e86\u4e0a\u5343\u5e74\uff0c\u2014 \u4f4d\u6b7b\u4e86\u51e0\u767e\u5e74\uff0c\u4ed6\u4eec\u7684\u624b\u662f\u4ec0\u4e48\u6837\u5b50\uff0c\u4f60\u600e\u4e48\u770b\u5230\u7684\uff1f\u201d \u3000\u3000\u4e01\u5e08\u7237\u4e0d\u89c9\u795e\u8272\u4e3a\u4e4b\u4e00\u7a92\uff0c\u987f\u4e86\u4e00\u987f\u9053\uff1a\u201c\u8fd9\u4e0d\u8fc7\u662f\u60f3\u5f53\u7136\u5c14\u3002\u8b6c\u5982\u5b54\u592b\u5b50\u548c\u5173\u592b\u5b50\uff0c \u73b0\u5728\u6709\u8c01\u89c1\u8fc7\u4ed6\u4eec\uff0e\u4f46\u4ed6\u4eec\u5374\u65e2\u6709\u753b\u50cf\u53c8\u6709\u5851\u50cf\uff0c\u4f60\u80fd\u8bf4\u4ed6\u4eec\u7684\u50cf\u5851\u5f97\u4e0d\u5bf9\u5417\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4f60\u522b\u626f\u5f97\u592a\u8fdc\u3002\u5b54\u592b\u5b50\u548c\u5173\u592b\u5b50\u7684\u753b\u50cf\u5851\u50cf\uff0c\u4e0d\u662f\u73b0\u5728\u624d\u6709\u3002\u5f53\u521d\u5fc5\u5b9a\u6709 \u4eba\u770b\u5230\u4ed6\u4eec\u3002\u4f60\u8bf4\u738b\u7fb2\u4e4b\u548c\u5434\u9053\u5b50\u7684\u624b\u957f\u5f97\u4e0d\u597d\u770b\uff0c\u5374\u4ece\u6765\u6ca1\u4eba\u8fd9\u6837\u8bf4\u8fc7\uff0c\u9664\u975e\u4f60\u627e\u51fa\u8bc1\u636e \u6765\uff1f\u201d \u3000\u3000\u4e01\u5e08\u7237\u82e6\u7b11\u9053\uff1a\u201c\u8fd9\u79cd\u4e8b\u60c5\u54ea\u91cc\u80fd\u627e\u51fa\u8bc1\u636e\u6765\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u65e2\u627e\u4e0d\u51fa\u8bc1\u636e\u6765\uff0c\u90a3\u5c31\u8bc1\u660e\u4f60\u5728\u80e1\u626f\u3002\u5982\u679c\u6211\u6765\u80e1\u626f\u4e00\u756a\uff0c\u4f60\u4fe1\u4e0d\u4fe1\uff1f\u201d \u3000\u3000\u4e01\u5e08\u7237\u52a8\u7740\u4e00\u5bf9\u6b7b\u9c7c\u773c\u9053\uff1a\u201c\u9ea6\u5c0f\u4fa0\u51c6\u5907\u80e1\u626f\u4e9b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u6211\u8bf4\u4f60\u90a3\u7236\u6bcd\u957f\u5f97\u50cf\u4e4c\u9f9f\u738b\u516b\uff0c\u4f60\u540c\u610f\u4e0d\u540c\u610f\uff1f\u201d \u3000\u3000\u4e01\u5e08\u7237\u7acb\u523b\u9762\u5b54\u618b\u5f97\u72b9\u5982\u732a\u809d\uff0c\u6c14\u5f97\u5168\u8eab\u53d1\u6296\u9053\uff1a\u201c\u9ea6\u5c0f\u4fa0\u600e\u53ef\u51fa\u53e3\u4f24\u4eba\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u7b11\u9053\uff1a\u201c\u6709\u5176\u7236\u5fc5\u6709\u5176\u5b50\uff0c\u770b\u5230\u5c0a\u9a7e\u8fd9\u526f\u5c0a\u5bb9\uff0c\u5f53\u7136\u4f1a\u60f3\u5230\u4f60\u7684\u4e0a\u4e00\u4ee3\u662f\u4ec0\u4e48\u957f \u76f8\uff0e\u8fd9\u4e5f\u662f\u60f3\u5f53\u7136\u5c14\u7684\u4e8b\u3002\u4f60\u80fd\u60f3\u5f53\u7136\u5c14\uff0c\u96be\u9053\u6211\u5c31\u4e0d\u80fd\u60f3\u5f53\u7136\u5c14\uff1f\u201d \u3000\u3000\u4e01\u5e08\u7237\u53ea\u6c14\u5f97\u4e00\u8fde\u545b\u54b3\u4e86\u597d\u51e0\u58f0\uff0c\u51e0\u4e4e\u9f3b\u6d95\u773c\u6cea\u4ea4\u6d41\uff0c\u534a\u54cd\uff0c\u624d\u624b\u52bf\u8fde\u6447\uff0c\u5927\u58f0\u9053\uff1a \u201c\u7b97\u4e86\u7b97\u4e86\uff01\u8001\u673d\u4eca\u5929\u662f\u770b\u75c5\u6765\u7684\uff0c\u54b1\u4eec\u4e0d\u8c08\u8fd9\u4e9b\uff01\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u5728\u65c1\u966a\u7740\u7b11\u9053\uff1a\u201c\u4e01\u5e08\u7237\u6700\u597d\u73b0\u5728\u80fd\u5148\u8ba9\u5fc3\u60c5\u5e73\u9759\u4e00\u4e0b\uff0c\u5426\u5219\u5f85\u4f1a\u513f\u5fc5\u5b9a\u5f71\u54cd\u8bca \u65ad\u3002\u201d \u3000\u3000\u4e01\u5e08\u7237\u5598\u4e86\u4e00\u53e3\u5927\u6c14\u9053\uff1a\u201c\u90a3\u662f\u81ea\u7136\u3002\u8bca\u75c5\u8bb2\u7a76\u7684\u662f\u2018\u671b\u95fb\u95ee\u5207\u2019\uff0c\u82e5\u5fc3\u60c5\u4e0d\u597d\uff0c\u8bca\u65ad \u7684\u7ed3\u679c\u5fc5\u5b9a\u4e0d\u6b63\u786e\u3002\u4e00\u65e6\u4e0d\u80fd\u5bf9\u75c7\u4e0b\u836f\uff0c\u90a3\u53ef\u5c31\u4e0d\u662f\u95f9\u7740\u73a9\u7684\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u6700\u591a\u662f\u836f\u5230\u547d\u9664\uff0c\u5eb8\u533b\u5bb3\u4eba\u7684\uff0c\u4e0d\u53ea\u4f60\u4e01\u5e08\u7237\u4e00\u4e2a\u3002\u201d \u3000\u3000\u4e01\u5e08\u7237\u521a\u8981\u53d1\u4f5c\uff0c\u4f59\u4ea6\u4e50\u6b63\u8272\u9053\uff1a\u201c\u5927\u5bb6\u5c31\u8bf7\u522b\u518d\u6253\u6270\u4e01\u5e08\u7237\uff0e\u4e0d\u7136\u4ed6\u8001\u4eba\u5bb6\u600e\u80fd\u4e3a\u4e07 \u59d1\u5a18\u770b\u75c5\uff1f\u201d \u3000\u3000\u5ba2\u5385\u91cc\u603b\u7b97\u9759\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u4e01\u5e08\u7237\u8db3\u8db3\u559d\u4e86\u4e24\u676f\u8336\uff0c\u76f4\u7b49\u5df2\u7ecf\u5fc3\u5e73\u6c14\u548c\uff0e\u624d\u7ad9\u8d77\u8eab\u9053\uff1a\u201c\u597d\u4e86\uff0c\u8be5\u7ed9\u4e07\u59d1\u5a18\u770b\u75c5 \u4e86\uff01\u201d \u3000\u3000\u949f\u4e00\u8c6a\u548c\u4f59\u4ea6\u4e50\u8d77\u8eab\u5e26\u8def\uff0c\u9ea6\u5c0f\u660e\u4e5f\u8ddf\u5728\u540e\u9762\u3002 \u3000\u3000\u4e01\u5e08\u7237\u867d\u4e0d\u613f\u9ea6\u5c0f\u660e\u8ddf\u53bb\uff0c\u5374\u53c8\u4e0d\u4fbf\u8bf4\u4ec0\u4e48\u3002 \u3000\u3000\u8fdb\u5165\u623f\u95f4\uff0c\u53ea\u89c1\u7a97\u6237\u7d27\u95ed\uff0c\u5ba4\u5185\u975e\u5e38\u5e7d\u6697\u3002 \u3000\u3000\u9760\u58c1\u8fb9\u662f\u4e00\u5f20\u5367\u69bb\uff0c\u9ad8\u60ac\u7740\u2014\u9876\u7c89\u7ea2\u8272\u7f57\u5e10\uff0c\u9690\u7ea6\u53ef\u89c1\u7f57\u5e10\u5185\u6709\u4eba\u8eba\u5728\u69bb\u4e0a\u3002 \u3000\u3000\u4e01\u5e08\u7237\u5f20\u671b\u4e86\u2014\u4e0b\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u8fde\u706f\u4e5f\u4e0d\u70b9\uff1f\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u9053\uff1a\u201c\u4e07\u59d1\u5a18\u81ea\u4ece\u75c5\u4e86\u4ee5\u540e\uff0c\u6700\u6015\u706f\u5149\uff0c\u6240\u4ee5\u624d\u6ca1\u70b9\u3002\u201d \u5367\u9f99\u751f\u300a\u5999\u7edd\u5929\u9999\u300b \u7b2c\u5341\u4e5d\u56de\u3000\u591c\u9664\u4e09\u602a \u3000\u3000\u4e01\u5e08\u7237\u76b1\u7709\u9053\uff1a\u201c\u90a3\u4e48\u7a97\u5b50\u5c31\u8be5\u6253\u5f00\uff0c\u75c5\u4eba\u6700\u9700\u8981\u7684\u5c31\u662f\u65b0\u9c9c\u7a7a\u6c14\u3002\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u9053\uff1a\u201c\u4e01\u5e08\u7237\u522b\u5fd8\u4e86\u75c5\u4eba\u6700\u6015\u98ce\uff0e\u201d \u3000\u3000\u4e01\u5e08\u7237\u5e26\u70b9\u65e0\u5948\u7684\u8bed\u6c14\u9053\uff1a\u201c\u53ef\u4e0d\u53ef\u4ee5\u628a\u706f\u70b9\u4e0a\uff1f\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u9053\uff1a\u201c\u65b9\u624d\u4e0d\u662f\u8bf4\u8fc7\u5417\uff0e\u4e07\u59d1\u5a18\u75c5\u540e\u6700\u6015\u706f\u5149\uff0c\u4f55\u51b5\u73b0\u5728\u662f\u767d\u5929\uff0c\u672c\u6765\u5c31\u7528\u4e0d\u7740 \u70b9\u706f\uff0e\u201d \u3000\u3000\u201c\u867d\u7136\u662f\u767d\u5929\uff0c\u4f46\u56e0\u7a97\u6237\u6ca1\u6253\u5f00\uff0c\u5c4b\u5b50\u91cc\u5b9e\u5728\u592a\u6697\u3002\u201d \u3000\u3000\u201c\u53ef\u4e01\u5e08\u7237\u53ea\u987b\u628a\u8109\u5c31\u6210\uff0e\u6697\u4e00\u70b9\u53c8\u6709\u4f55\u59a8\u788d\uff1f\u201d \u3000\u3000\u201c\u2018\u671b\u95fb\u95ee\u5207\u2019\uff0c\u5bf9\u75c5\u4eba\u7b2c\u4e00\u4e2a\u8bca\u65ad\u8981\u51b3\u4fbf\u662f\u5bdf\u989c\u89c2\u8272\uff0c\u5982\u679c\u5ba4\u5185\u592a\u6697\uff0c\u8fd8\u2018\u671b\u2019\u4ec0 \u4e48\uff1f\u201d \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u70b9\u706f\u5c31\u70b9\u706f\u5427\uff0c\u53ea\u8981\u628a\u706f\u653e\u8fdc\u4e9b\uff0c\u4e5f\u8bb8\u4e07\u59d1\u5a18\u8fd8\u53d7\u5f97\u4e86\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u7740\uff0c\u968f\u5373\u5c06\u706f\u70b9\u4e0a\uff0e\u6545\u610f\u653e\u5728\u684c\u5b50\u8ddd\u5e8a\u69bb\u8f83\u8fdc\u7684\u2014\u7aef\u3002 \u3000\u3000\u4f59\u4ea6\u4e50\u642c\u6765\u4e00\u628a\u6905\u5b50\u653e\u5728\u5e8a\u524d\u9053\uff1a\u201c\u4e01\u5e08\u7237\u53ef\u662f\u8981\u5148\u628a\u8109\uff1f\u201d \u3000\u3000\u4e01\u5e08\u7237\u5750\u4e0b\u9053\uff1a\u201c\u90a3\u5f53\u7136\uff0c\u5c31\u8bf7\u4e07\u59d1\u5a18\u628a\u624b\u4f38\u51fa\u6765\u3002\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u53eb\u9053\uff1a\u201c\u4e07\u59d1\u5a18\uff0c\u76df\u4e3b\u6d3e\u4e01\u5e08\u7237\u6765\u7ed9\u4f60\u770b\u75c5\uff0e\u8bf7\u628a\u624b\u4f38\u51fa\u5e10\u5916\uff0c\u4ee5\u4fbf\u4e01\u5e08\u7237\u628a \u8109\uff01\u201d \u3000\u3000\u4ed6\u8fde\u53eb\u4e86\u597d\u51e0\u58f0\uff0c\u624d\u89c1\u7531\u7f57\u5e10\u5185\u7f13\u7f13\u4f38\u51fa\u4e00\u53ea\u624b\u81c2\u6765\u3002 \u3000\u3000\u8fd9\u53ea\u624b\u81c2\u80a4\u5982\u51dd\u8102\uff0e\u5149\u6d01\u6676\u83b9\uff0c\u5341\u6307\u5c16\u5c16\uff0c\u6709\u82e5\u67d4\u8351\uff0c\u53ea\u770b\u5f97\u516d\u5341\u516b\u5c81\u7684\u4e01\u5e08\u7237\uff0c\u4f3c\u4e4e \u6709\u4e9b\u628a\u6301\u4e0d\u4f4f\uff0c\u6697\u9053\uff1a\u201c\u597d\u7f8e\u597d\u67d4\u7684\u624b\uff0e\u8001\u673d\u6709\u673a\u4f1a\u63e1\u4e0a\u4e00\u63e1\uff0c\u4e5f\u8273\u798f\u4e0d\u6d45\u2026\u2026\u201d \u3000\u3000\u4e0d\u8fc7\uff0e\u4ed6\u540c\u65f6\u4e5f\u611f\u5230\u4e00\u9635\u832b\u7136\u4e0d\u89e3\uff0c\u5982\u679c\u7531\u8fd9\u53ea\u624b\u7684\u80a4\u8272\u6765\u770b\uff0c\u4e07\u6620\u971e\u53c8\u7edd\u4e0d\u53ef\u80fd\u662f\u75c5 \u4eba\u3002\u56e0\u4e3a\u4eba\u2014\u751f\u75c5\uff0c\u76ae\u80a4\u5fc5\u4f1a\u5931\u53bb\u5149\u6cfd\uff0c\u800c\u4e14\u4e5f\u968f\u4e4b\u6d88\u7626\u3002 \u3000\u3000\u4f46\u9732\u51fa\u5e10\u5916\u8fd9\u53ea\u624b\uff0c\u5374\u5b8c\u5168\u6ca1\u6709\u8fd9\u79cd\u7279\u5f81\uff0c\u5c82\u975e\u602a\u4e8b\u3002 \u3000\u3000\u4e01\u5e08\u7237\u4e0d\u51c6\u5907\u60f3\u90a3\u4e48\u591a\uff0c\u5148\u63e1\u4e0a\u8fd9\u53ea\u624b\u8fc7\u8fc7\u763e\u518d\u8bf4\u3002 \u3000\u3000\u867d\u7136\u4ed6\u5185\u5fc3\u4e50\u4e0d\u53ef\u652f\uff0c\u4f46\u8868\u9762\u4e0a\u5374\u4e0d\u4f4f\u8e59\u7709\u6447\u5934\u9053\uff1a\u201c\u5947\u602a\uff01\u5947\u602a\uff01\u771f\u5947\u602a\uff01\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u9053\uff1a\u201c\u4e01\u5e08\u7237\u54ea\u6765\u8fd9\u4e48\u591a\u5947\u602a\uff1f\u201d \u3000\u3000\u4e01\u5e08\u7237\u9053\uff1a\u201c\u4f9d\u4e07\u59d1\u5a18\u7684\u8109\u640f\u8df3\u52a8\u800c\u8bba\uff0c\u4f3c\u4e4e\u4e00\u70b9\u6bdb\u75c5\u90fd\u6ca1\u6709\uff0e\u600e\u4f1a\u75c5\u5f97\u8fd9\u4e48\u91cd\u5462\uff1f\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u9053\uff1a\u201c\u8bf7\u6055\u5931\u8a00\uff0c\u4e01\u5e08\u7237\u662f\u5426\u628a\u8109\u4e0d\u51c6\uff1f\u201d \u3000\u3000\u4e01\u5e08\u7237\u4e0d\u60a6\u9053\uff1a\u201c\u8001\u673d\u82e5\u8fde\u628a\u8109\u90fd\u6ca1\u628a\u63e1\uff0c\u8fd8\u770b\u7684\u4ec0\u4e48\u75c5\uff1f\u201d \u3000\u3000\u5374\u542c\u9ea6\u5c0f\u660e\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u7684\u6bdb\u75c5\u6211\u770b\u51fa\u6765\u4e86\uff01\u201d \u3000\u3000\u4e01\u5e08\u7237\u4e00\u6123\u9053\uff1a\u201c\u9ea6\u5c0f\u4fa0\u770b\u51fa\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1b\u201c\u4f60\u7684\u624b\u5728\u53d1\u6296\uff0c\u4e07\u59d1\u5a18\u7684\u624b\u5728\u4f60\u624b\u91cc\uff0c\u5f53\u7136\u4e5f\u8ddf\u7740\u53d1\u6296\uff0c\u8fd9\u6837\u628a\u8109\uff0c\u53c8\u600e \u80fd\u628a\u5f97\u51c6\u5462\uff1f\u201d \u3000\u3000\u4e01\u5e08\u7237\u7751\u4e0a\u4e00\u70ed\uff0c\u53ea\u597d\u653e\u4e0b\u624b\u9053\uff1a\u201c\u4e0d\u4f1a\u9519\uff01\u4e07\u59d1\u5a18\u7684\u8109\u818a\u5f88\u6b63\u5e38\u3002\u73b0\u5728\u8be5\u770b\u770b\u5979\u8138\u4e0a \u7684\u6c14\u8272\u3002\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u9053\uff1a\u201c\u4e07\u59d1\u5a18\u6015\u98ce\uff0c\u5343\u4e07\u4e0d\u80fd\u6380\u52a8\u5979\u7684\u7f57\u5e10\u3002\u201d \u3000\u3000\u4e01\u5e08\u7237\u53c8\u8e59\u7709\u9053\uff1a\u201c\u4e0d\u6380\u5f00\u5e10\u5b50\uff0c\u600e\u80fd\u770b\u5230\u5979\u7684\u6c14\u8272\uff1f\u201d \u3000\u3000\u201c\u4e0d\u59a8\u9694\u7740\u5e10\u5b50\u5f80\u91cc\u770b\u3002\u201d \u3000\u3000\u201c\u8fd9\u5c4b\u5b50\u672c\u6765\u5c31\u6697\uff0c\u53c8\u9694\u7740\u5e10\u5b50\uff0c\u8001\u673d\u53c8\u662f\u8001\u5316\u773c\uff0c\u600e\u80fd\u770b\u5f97\u5230\uff1f\u201d \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u6380\u5f00\u5c31\u6380\u5f00\u5427\uff0c\u53ea\u662f\u8981\u6162\u6162\u7684\u6380\uff01\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u53ea\u597d\u63a2\u8eab\u6380\u5f00\u7f57\u5e10\u3002 \u3000\u3000\u53ea\u89c1\u75c5\u4eba\u88ab\u4e00\u5e8a\u9526\u88ab\u7d27\u7d27\u7684\u88f9\u7740\uff0c\u9876\u7aef\u53ea\u9732\u51fa\u2014\u4e2a\u989d\u5934\uff0e\u4ee5\u4e0b\u5168\u65e0\u6cd5\u770b\u5230\u3002 \u3000\u3000\u4e01\u5e08\u7237\u9053\uff1a\u201c\u8bf7\u628a\u88ab\u5934\u7a0d\u7a0d\u6380\u4e00\u6380\uff0c\u81f3\u5c11\u8981\u9732\u51fa\u8138\u6765\u3002\u201d \u3000\u3000\u5374\u542c\u88ab\u5b50\u91cc\u53d1\u51fa\u6709\u6c14\u65e0\u529b\u7684\u5477\u541f\u58f0\u9053\uff1a\u201c\u4e0d\u2026\u2026\u4e0d\u80fd\u6380\u2026\u2026\u5343\u4e07\u2026\u2026\u4e0d\u80fd\u6380\u2026\u2026\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u9053\uff1a\u201c\u4e01\u5e08\u7237\u542c\u5230\u6ca1\u6709\uff0c\u4e07\u59d1\u5a18\u5df2\u7ecf\u8bf4\u8bdd\u4e86\uff0c\u5979\u7684\u989d\u5934\u9732\u5728\u5916\u9762\uff0c\u96be\u9053\u989d\u5934\u8fd8\u4e0d \u6210\uff1f\u201d \u3000\u3000\u4e01\u5e08\u7237\u4e0d\u4fbf\u575a\u6301\uff0c\u51dd\u76ee\u671b\u53bb\uff0c\u4e0d\u89c9\u5927\u5403\u4e00\u60ca\u9053\uff1a\u201c\u679c\u7136\u75c5\u5f97\u5389\u5bb3\uff01\u4e0d\u5f97\u4e86\uff01\u592a\u5389\u5bb3\u5566\uff01\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u5fcd\u7740\u7b11\u9053\uff1a\u201c\u521a\u624d\u4e0d\u662f\u8bf4\u5979\u6ca1\u75c5\u5417\uff1f\u600e\u4e48\u53c8\u5389\u5bb3\u8d77\u6765\u4e86\uff1f\u201d \u3000\u3000\u4e01\u5e08\u7237\u8fde\u8fde\u6447\u5934\u9053\uff1a\u201c\u602a\u5c31\u602a\u5728\u8fd9\u91cc\uff0c\u770b\u4e07\u59d1\u5a18\u7684\u624b\uff0c\u4e00\u70b9\u75c5\u8c61\u90fd\u6ca1\u6709\uff0c\u600e\u4e48\u5979\u7684\u989d\u5934 \u7adf\u7136\u8fd9\u6837\u5e72\u9ec4\uff0c\u4e0d\u4f46\u5e72\u9ec4\uff0e\u800c\u4e14\u9ec4\u4e2d\u5e26\u9752\uff0c\u4f3c\u4e4e\u662f\u75c5\u5165\u818f\u76f2\uff0c\u5f88\u53ef\u80fd\u547d\u5728\u65e6\u5915\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u6c14\u6124\u6124\u7684\u9053\uff1a\u201c\u4f60\u600e\u4e48\u80fd\u5728\u75c5\u4eba\u9762\u524d\u8bf4\u8fd9\u79cd\u4e0d\u5409\u7965\u7684\u8bdd\uff01\u201d \u3000\u3000\u4e86\u5e08\u7237\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u8001\u673d\u770b\u8fc7\u7684\u75c5\u4eba\u592a\u591a\u5566\uff0c\u82e5\u80a4\u8272\u662f\u8fd9\u79cd\u73b0\u8c61\uff0c\u3000\u5341\u6709\u516b\u4e5d\u5fc5\u662f\u51f6 \u591a\u5409\u5c11\uff01\u201d \u3000\u3000\u4ed6\u8bf4\u7740\uff0c\u5ffd\u7136\u7ad9\u4e86\u8d77\u6765\uff0c\u5f2f\u4e0b\u8eab\u5b50\uff0c\u628a\u989d\u5934\u8d34\u5230\u75c5\u4eba\u989d\u5934\u4e0a\uff0c\u5982\u679c\u4e0d\u662f\u75c5\u4eba\u7684\u773c\u775b\u4ee5\u4e0b \u88ab\u88ab\u5b50\u906e\u4f4f\uff0c\u7b80\u76f4\u50cf\u662f\u5728\u63a5\u543b\u7684\u59ff\u52bf\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u7acb\u5373\u559d\u9053\uff1a\u201c\u8001\u5c0f\u5b50\uff0c\u4f60\u8981\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4e01\u5e08\u7237\u5e76\u4e0d\u7406\u4f1a\uff0c\u5f88\u4e45\u4e4b\u540e\uff0c\u624d\u53c8\u5750\u56de\u539f\u4f4d\uff0c\u5634\u91cc\u5600\u5495\u7740\u5148\u524d\u628a\u8109\u7684\u90a3\u5957\u8001\u8bcd\u9053\uff1a\u201c\u5947 \u602a\uff01\u5947\u602a\uff01\u771f\u5947\u602a\uff01\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u9053\uff1a\u201c\u4e01\u5e08\u7237\u53c8\u5947\u602a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4e01\u5e08\u7237\u9053\uff1a\u201c\u8001\u673d\u65b9\u624d\u662f\u5728\u5b9e\u65bd\u2018\u671b\u95fb\u95ee\u5207\u2019\u4e2d\u7684\u2018\u95fb\u2019\u5b57\u8bc0\uff0c\u51e1\u75c5\u91cd\u7684\u4eba\uff0c\u8eab\u4e0a\u53d1\u51fa \u7684\u6c14\u5473\uff0c\u4e5f\u4e0e\u5e73\u5e38\u4eba\u4e0d\u540c\u3002\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u4e07\u59d1\u5a18\u7684\u6c14\u5473\u53ef\u6709\u4ec0\u4e48\u4e0d\u540c\uff1f\u201d \u3000\u3000\u4e01\u5e08\u7237\u9053\uff1a\u201c\u602a\u5c31\u602a\u5728\u8fd9\u91cc\uff0c\u4e07\u59d1\u5a18\u8eab\u4e0a\u6c14\u5473\u548c\u597d\u4eba\u2014\u822c\u65e0\u4e8c\uff0e\u7edd\u4e0d\u50cf\u6709\u75c5\u7684\u4eba\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u54fc\u58f0\u9053\uff1a\u201c\u4e07\u59d1\u5a18\u672c\u6765\u5c31\u662f\u597d\u4eba\uff0c\u96be\u9053\u4f60\u8ba4\u4e3a\u5979\u662f\u574f\u4eba\uff1f\u201d \u3000\u3000\u4e01\u5e08\u7237\u82e6\u7b11\u9053\uff1a\u201c\u8001\u673d\u8bf4\u7684\u597d\u4eba\uff0c\u662f\u6307\u7684\u6ca1\u75c5\u7684\u4eba\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u8fd9\u5012\u662f\u4ef6\u602a\u4e8b\uff0e\u660e\u660e\u662f\u597d\u4eba\uff0c\u751f\u4e86\u75c5\u5c31\u53d8\u6210\u574f\u4eba\u3002\u4eba\u5341\u6709\u516b\u4e5d\u662f\u751f\u75c5\u7684\uff0c \u8fd9\u6837\u770b\u6765\uff0c\u4eba\u6b7b\u540e\u5168\u662f\u574f\u4eba\u4e86\u3002\u201d \u3000\u3000\u4e01\u5e08\u7237\u9053\uff1a\u201c\u4e0d\u7ba1\u5982\u4f55\uff0c\u4e07\u59d1\u5a18\u5f97\u7684\u662f\u4e00\u79cd\u602a\u75c5\u3002\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u9053\uff1a\u201c\u602a\u75c5\u987b\u5f97\u602a\u836f\u533b\uff0c\u3000\u4e01\u5e08\u7237\u662f\u5426\u51c6\u5907\u7684\u6709\u602a\u836f\uff1f\u201d \u3000\u3000\u4e01\u5e08\u7237\u5e72\u7b11\u9053\uff1a\u201c\u4f59\u5148\u751f\u53d6\u7b11\u4e86\uff0c\u53ea\u6709\u602a\u75c5\uff0c\u54ea\u6765\u7684\u602a\u836f\uff0c\u73b0\u5728\u8001\u673d\u60f3\u95ee\u4e07\u59d1\u5a18\u51e0\u53e5 \u8bdd\u3002\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u6447\u5934\u9053\uff1a\u201c\u4e07\u59d1\u5a18\u75c5\u5f97\u8fd9\u4e48\u5389\u5bb3\uff0c\u54ea\u91cc\u8fd8\u80fd\u7b54\u8bdd\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u2018\u671b\u3001\u95fb\u3001\u95ee\u3001\u5207\u2019\u56db\u5b57\u8bc0\u4e2d\uff0c\u53ea\u5dee\u8fd9\u2018\u95ee\u2019\u4e86\u3002\u6b63\u56e0\u4e3a\u8001\u673d\u8bca\u65ad\u51fa\u5979\u662f\u4e00\u79cd\u602a \u75c5\uff0c\u5c31\u66f4\u987b\u95ee\u95ee\uff0c\u8bf4\u4e0d\u5b9a\u4f1a\u95ee\u51fa\u4e00\u4e2a\u660e\u5802\u6765\u3002\u201d \u3000\u3000\u201c\u90a3\u5c31\u95ee\u526f\u76df\u4e3b\u548c\u6211\u597d\u4e86\u3002\u4e07\u59d1\u5a18\u7684\u75c5\u60c5\uff0c\u6211\u4eec\u4e24\u4eba\u90fd\u6e05\u695a\uff0e\u53ef\u4ee5\u4ee3\u7b54\u3002\u201d \u3000\u3000\u201c\u8981\u95ee\u7684\u5b9e\u5728\u5927\u591a\uff0c\u4e0d\u95ee\u4e5f\u7f62\uff0c\u8001\u673d\u73b0\u5728\u5c31\u5f00\u836f\u65b9\u5427\uff01\u201d \u3000\u3000\u4e8e\u662f\u949f\u4e00\u8c6a\u7b49\u53c8\u966a\u7740\u4e01\u5e08\u7237\u56de\u5230\u4e86\u5ba2\u5385\uff0e \u3000\u3000\u4e01\u5e08\u7237\u6253\u5f00\u836f\u7bb1\uff0c\u91cc\u9762\u8fde\u6587\u623f\u56db\u5b9d\u90fd\u6709\uff0c\u63d0\u8d77\u7b14\u6765\u5199\u9053\uff1a\u201c\u5f53\u5f52\u3001\u5ddd\u828e\u5404\u4e03\u5206\uff1b\u8d64\u828d\u3001 \u77e5\u6bcd\u3001\u9ea6\u51ac\u3001\u5730\u9aa8\u76ae\u5404\u4e00\u94b1\uff1b\u7518\u8349\u4e94\u5206\uff0c\u6c34\u714e\uff0c\u7a7a\u5fc3\u670d\uff0c\u4ee5\u4e0a\u836f\u5242\u5747\u98df\u670d\u3001\u517c\u670d\u4e09\u8865\u4e38\u548c \u4e4b\uff0e\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u5728\u65c1\u8fb9\u770b\u8fb9\u9053\uff1a\u201c\u4e07\u59d1\u5a18\u76ee\u524d\u5df2\u7ecf\u7c92\u7c73\u4e0d\u8fdb\uff0c\u54ea\u91cc\u6765\u7684\u98df\u524d\u98df\u540e\uff1f\u201d \u3000\u3000\u4e01\u5e08\u7236\u7565\u4e00\u6c89\u541f\u9053\uff1a\u201c\u65e2\u4e0d\u80fd\u8fdb\u996e\u98df\uff0c\u53ef\u4e8e\u6bcf\u65e5\u6668\u3001\u5348\u3001\u665a\u670d\u4e4b\u3002\u201d \u3000\u3000\u63a5\u7740\u53c8\u63d0\u7b14\u5f00\u7b2c\u4e8c\u5242\u836f\u65b9\u4e8e\u5de6\uff1a\u201c\u53f0\u515a\u3001\u767d\u672f\u3001\u4f0f\u82d3\u3001\u7099\u8349\uff0c\u5f53\u5f52\uff0c\u5ddd\u828e\u3001\u7099\u7740\u5404\u4e00\u94b1\uff1b \u8089\u6842\u4e94\u5206\uff1b\u7528\u516d\u541b\u5b50\u52a0\u5f52\u828e\u6c64\uff0c\u518d\u52a0\u7ae5\u5c3f\u6d78\u4e00\u591c\u548c\u4ee5\u5341\u5168\u5927\u8865\u6c64\u670d\u4e4b\u3002\u7b2c\u4e00\u5242\u670d\u4e09\u65e5\u518d\u4ee5\u6b64 \u5242\u670d\u4e4b\uff0c\u65f6\u95f4\u4ecd\u4e3a\u6bcf\u65e5\u6668\u3001\u5348\uff0e\u665a\u3002\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u76b1\u7709\u9053\uff1a\u201c\u8ff7\u8e2a\u8c37\u54ea\u91cc\u6765\u7684\u7ae5\u5b50\u5c3f\uff1f\u201d \u3000\u3000\u4e01\u5e08\u7237\u671b\u4e86\u671b\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4ee5\u9ea6\u5c0f\u4fa0\u7684\u5e74\u7eaa\uff0c\u800c\u4e14\u5c1a\u672a\u6210\u5a5a\uff0c\u53ef\u4ee5\u79f0\u5f97\u7ae5\u5b50\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u7b11\u9053\uff1a\u201c\u6211\u5df2\u7ecf\u5341\u516b\u4e5d\u5c81\u4e86\uff0c\u6bd4\u4f60\u4e01\u5e08\u7237\u8fd8\u9ad8\u534a\u4e2a\u5934\uff0e\u54ea\u91cc\u6709\u8fd9\u4e48\u5927\u7684\u7ae5\u5b50\uff1f\u201d \u3000\u3000\u4e01\u5e08\u7237\u9053\uff1a\u201c\u51e1\u5e74\u672a\u6ee1\u5f31\u51a0\uff0e\u800c\u4e14\u80fd\u4ecd\u4fdd\u7ae5\u8eab\uff0c\u5c31\u7b97\u7ae5\u5b50\u3002\u201d \u3000\u3000\u201c\u53ef\u60dc\u6211\u5df2\u7ecf\u4e0d\u662f\u7ae5\u5b50\u8eab\u5566\uff01\u201d \u3000\u3000\u4e01\u5e08\u7237\u4e00\u5bf9\u8001\u9f20\u773c\u7728\u4e86\u534a\u664c\u9053\uff1a\u201c\u9ea6\u5c0f\u4fa0\u4ec0\u4e48\u65f6\u5019\u7834\u7684\u8eab\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u8fd8\u4e0d\u662f\u4f60\u4eec\u76df\u4e3b\u505a\u7684\u597d\u4e8b\uff0c\u53d1\u4e0b\u4ee4\u724c\u8981\u6211\u5230\u4e1c\u6d77\u9662\u53bb\uff0e\u5982\u679c\u4e0d\u7834\u8eab\u5c31\u662f\u6297 \u547d\uff0c\u4f60\u60f3\u6211\u600e\u6562\u6297\u547d\uff1f\u201d \u3000\u3000\u4e01\u5e08\u7237\u9ed8\u4e86\u534a\u54cd\u9053\uff1a\u201c\u90a3\u662f\u524d\u5929\u665a\u4e0a\u7684\u4e8b\uff0c\u53ea\u8981\u7834\u8eab\u672a\u8fc7\u4e09\u65e5\uff0c\u9a6c\u9a6c\u864e\u864e\u8fd8\u8bf4\u5f97\u8fc7\u53bb\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u8ba9\u4e07\u59d1\u5a18\u559d\u6211\u7684\u5c3f\u2026\u2026\u5b9e\u5728\u4e0d\u597d\u606f\u601d\u3002\u201d \u3000\u3000\u201c\u8fd9\u662f\u6551\u4eba\uff0c\u5c06\u6765\u4e07\u59d1\u5a18\u75c5\u597d\u4e86\uff0c\u8fd8\u8be5\u611f\u8c22\u4f60\u3002\u201d \u3000\u3000\u4e01\u5e08\u7237\u63a5\u7740\u4ece\u836f\u7bb1\u91cc\u6293\u836f\uff0c\u6293\u4e86\u534a\u5929\uff0c\u5374\u53c8\u8e59\u7709\u9053\uff1a\u201c\u7b2c\u4e00\u5242\u836f\u836f\u7bb1\u91cc\u5168\u6709\uff0c\u504f\u504f\u7b2c\u4e8c \u5242\u836f\u8fd8\u7f3a\u7099\u80cc\u3001\u53f0\u515a\u4e24\u6837\u3002\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u9053\uff1a\u201c\u90a3\u8be5\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u4e01\u5e08\u7237\u60f3\u4e86\u60f3\u9053\uff1a\u201c\u53ea\u6709\u9ebb\u70e6\u5404\u4f4d\u5230\u6d51\u6e90\u53bf\u57ce\u53bb\u4e70\u4e86\u3002\u597d\u5728\u662f\u7b2c\u4e8c\u5242\uff0e\u4e09\u65e5\u540e\u624d\u7528\uff0c\u65f6 \u95f4\u8fd8\u6765\u5f97\u53ca\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u5b8c\uff0c\u6536\u62fe\u8d77\u6587\u623f\u56db\u5b9d\uff0c\u628a\u836f\u65b9\u7559\u5728\u684c\u4e0a\uff0c\u968f\u5373\u5429\u5490\u90a3\u968f\u4ece\u55bd\u5175\u63d0\u8d77\u836f\u7bb1\uff0c\u8d77\u8eab\u544a\u8f9e\u3002 \u3000\u3000\u949f\u2014\u8c6a\u548c\u4f59\u4ea6\u4e50\u4e3a\u4e86\u8868\u793a\u793c\u8c8c\u548c\u611f\u6fc0\uff0c\u628a\u4ed6\u9001\u51fa\u67cf\u9f84\u9662\u5927\u95e8\u5916\u3002 \u3000\u3000\u5f53\u949f\u4e00\u8c6a\u548c\u4f59\u4ea6\u4e50\u8fd4\u56de\u5ba2\u5385\u4e0d\u4e45\uff0c\u82d7\u7d20\u82d3\u4fbf\u7531\u623f\u5185\u51fa\u6765\uff0e \u3000\u3000\u539f\u6765\u65b9\u624d\u8eba\u5728\u623f\u91cc\u4f2a\u88c5\u75c5\u4eba\u7684\uff0c\u6b63\u662f\u82d7\u7d20\u82d3\u3002 \u3000\u3000\u82d7\u7d20\u82d3\u4e3a\u4e86\u4f7f\u88c5\u5f97\u903c\u771f\uff0c\u7279\u5730\u628a\u8138\u90e8\u64e6\u4e0a\u4e00\u5c42\u836f\u6c34\uff0c\u56e0\u4e4b\u989d\u5934\u624d\u663e\u5f97\u5e72\u9ec4\u800c\u53c8\u900f\u7740\u4e4c\u9752\uff0c \u504f\u504f\u767e\u5bc6\u4e00\u758f\uff0e\u53cc\u624b\u5e76\u672a\u64e6\u4e0a\u836f\u6c34\uff0c\u56e0\u4e4b\u624d\u4f7f\u4e01\u5e08\u7237\u83ab\u6d4b\u9ad8\u6df1\uff0e \u3000\u3000\u5c31\u5728\u5f53\u5929\u5929\u9ed1\u540e\uff0c\u9ea6\u5c0f\u660e\u548c\u82d7\u7d20\u82d3\u6084\u6084\u6765\u5230\u4e1c\u6d77\u9662\u540e\u4fa7\u7684\u6811\u6797\u4e2d\u3002 \u3000\u3000\u82d7\u7d20\u82d3\u4ecd\u662f\u7537\u88c5\u6253\u626e\uff0c\u53ea\u662f\u5df2\u4e0d\u518d\u9ed1\u7eb1\u8499\u9762\u3002 \u3000\u3000\u4e24\u4eba\u9690\u8eab\u5728\u6797\u5185\u7684\u4e00\u68f5\u6811\u53f6\u8302\u5bc6\u7684\u5927\u6811\u4e0a\uff0c\u6b63\u548c\u4e1c\u6d77\u9662\u7684\u540e\u7a97\u9065\u9065\u76f8\u5bf9\u3002 \u3000\u3000\u4e1c\u6d77\u9662\u9644\u8fd1\u672c\u6765\u5c31\u5341\u5206\u50fb\u9759\uff0c\u6b64\u65f6\u53c8\u5df2\u5165\u591c\uff0c\u66f4\u662f\u4e07\u7c41\u65e0\u58f0\u3002 \u3000\u3000\u8db3\u8db3\u7b49\u4e86\u987f\u996d\u5de5\u592b\uff0c\u5947\u602a\u7684\u662f\u82d7\u7d20\u5170\u4e00\u76f4\u4e0d\u66fe\u5230\u6765\uff0e \u3000\u3000\u4f46\u4ed6\u4eec\u5374\u5fc5\u987b\u82e6\u7b49\u4e0b\u53bb\u3002 \u3000\u3000\u82d7\u7d20\u82d3\u6025\u76fc\u7740\u80fd\u89c1\u5230\u59d0\u59d0\uff0c\u5df2\u6709\u4e9b\u6c89\u4e0d\u4f4f\u6c14\uff0e\u4f4e\u58f0\u9053\uff1a\u201c\u53ef\u4e0d\u53ef\u80fd\u662f\u6211\u59d0\u59d0\u90a3\u8fb9\u51fa\u4e86\u4e8b \u60c5\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u636e\u6211\u6240\u77e5\uff0c\u970d\u5143\u4f3d\u5bf9\u4ee4\u59d0\u5341\u5206\u5ba0\u7231\uff0c\u4e0d\u5927\u53ef\u80fd\u6709\u4ec0\u4e48\u610f\u5916\u53d1\u751f\u3002\u201d \u3000\u3000\u82d7\u7d20\u82d3\u987f\u4e86\u987f\u9053\uff1a\u201c\u6211\u59d0\u59d0\u771f\u7684\u4e0d\u66fe\u548c\u4ed6\u540c\u623f\uff1f\u201d \u3000\u3000\u4e0d\u96be\u60f3\u89c1\uff0c\u82d7\u7d20\u82d3\u8bf4\u8fd9\u8bdd\u65f6\uff0c\u5fc5\u5b9a\u7f9e\u7ea2\u6ee1\u9762\uff0c\u53ea\u56e0\u662f\u591c\u665a\uff0c\u522b\u4eba\u65e0\u6cd5\u770b\u51fa\u5979\u7684\u8868\u60c5\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u636e\u6211\u6628\u665a\u542c\u5230\u7684\uff0e\u4ed6\u4eec\u4e4b\u95f4\uff0c\u6709\u4ec0\u4e48\u767e\u65e5\u4e4b\u7ea6\uff0e\u53ef\u60dc\u8be6\u7ec6\u60c5\u5f62\u4e0d\u5f97\u800c\u77e5\u3002\u201d \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0e\u53ea\u542c\u201c\u5431\u5440\u201d\u4e00\u58f0\uff0c\u4e1c\u6d77\u9662\u697c\u4e0a\u7684\u540e\u7a97\u7adf\u7136\u6253\u4e86\u5f00\u6765\u3002 \u3000\u3000\u4e24\u4eba\u60c5\u4e0d\u76ee\u7981\u5411\u7a97\u5185\u671b\u53bb\u3002 \u3000\u3000\u8fd9\u2014\u671b\u4e0d\u6253\u7d27\uff0c\u82d7\u7d20\u82d3\u4e00\u58f0\u60ca\u547c\uff0c\u7acb\u523b\u8f6c\u8fc7\u5934\u53bb\u3002 \u3000\u3000\u4f46\u89c1\u8bb8\u5c0f\u65e6\u6b63\u5750\u5728\u5e8a\u4e0a\u5168\u8eab\u7adf\u662f\u4e00\u4e1d\u4e0d\u6302\uff0c\u867d\u7136\u76f8\u8ddd\u8db3\u6709\u597d\u51e0\u4e08\u8fdc\uff0e\u53ea\u56e0\u5ba4\u5185\u706f\u706b\u660e\u4eae\uff0c \u4f9d\u7136\u770b\u5f97\u5341\u5206\u6e05\u695a\uff0e \u3000\u3000\u5c31\u8fd9\u4e48\u51d1\u5de7\uff0c\u9ea6\u5c0f\u660e\u548c\u82d7\u7d20\u82d3\u6b64\u523b\u662f\u5728\u6811\u4e0a\uff0c\u4f4d\u7f6e\u6bd4\u90a3\u4e2a\u697c\u4e0a\u7684\u540e\u7a97\u8fd8\u8981\u7a0d\u9ad8\u4e00\u4e9b\uff0c\u6545 \u800c\u624d\u80fd\u770b\u5f97\u5982\u6b64\u6e05\u695a\u3002\u5982\u679c\u4eba\u5728\u5730\u4e0a\uff0e\u90a3\u5c31\u65e0\u6cd5\u5f97\u7aa5\u5982\u6b64\u65d6\u65ce\u98ce\u5149\u4e86\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u56e0\u4e3a\u5df2\u7ecf\u770b\u8fc7\u591a\u6b21\uff0c\u4e5f\u5c31\u4e0d\u62ff\u8fd9\u5f53\u56de\u4e8b\uff0c\u53cd\u800c\u8981\u7559\u5fc3\u770b\u770b\u8bb8\u5c0f\u65e6\u623f\u5185\u662f\u5426\u6709\u4eba\u3002 \u3000\u3000\u8bb8\u5c0f\u65e6\u867d\u7136\u5750\u5728\u5e8a\u4e0a\uff0c\u5374\u5e76\u4e0d\u5b89\u9759\u3002 \u3000\u3000\u53cc\u624b\u5728\u80f8\u524d\u4ee5\u53ca\u5c0f\u8179\u4e0b\u4e71\u6293\u4e71\u6414\uff0c\u5927\u6709\u5947\u75d2\u96be\u71ac\u4e4b\u6168\uff0c\u4e0d\u5927\u4e00\u4f1a\uff0c\u7adf\u53c8\u4ece\u5e8a\u4e0a\u6eda\u8d77\u6765\uff0c \u800c\u4e14\u6000\u91cc\u8fd8\u62b1\u7740\u4e00\u4e2a\u6795\u5934\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u5f53\u7136\u660e\u767d\u662f\u600e\u4e48\u56de\u4e8b\uff0c\u5e76\u4e14\u5df2\u65ad\u5b9a\u623f\u5185\u9664\u4e86\u5979\uff0c\u5e76\u6ca1\u6709\u7537\u4eba\u5728\uff0c\u5426\u5219\u5979\u62b1\u7684\u4fbf\u4e0d \u662f\u6795\u5934\u4e86\u3002 \u3000\u3000\u53ea\u542c\u82d7\u7d20\u82d3\u58f0\u97f3\u51b7\u51b7\u7684\u9053\uff1a\u201c\u6709\u4ec0\u4e48\u597d\u770b\u7684\uff1f\u4f60\u8fd8\u8001\u770b\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9669\u4e0a\u4e00\u70ed\u9053\uff1a\u201c\u6211\u65e9\u5df2\u7ecf\u4e0d\u770b\u4e86\uff0e\u201d \u3000\u3000\u201c\u4f60\u8bf4\uff0c\u662f\u4e0d\u662f\u548c\u5979\u5df2\u7ecf\u2026\u2026\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u4e00\u6123\u9053\uff1a\u201c\u82d7\u59d1\u5a18\u600e\u4e48\u8bf4\u51fa\u8fd9\u79cd\u8bdd\u6765\uff1f\u201d \u3000\u3000\u201c\u662f\u4f60\u81ea\u5df1\u8bf4\u7684\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u8fd9\u624d\u60f3\u8d77\u4e0a\u5348\u548c\u4e01\u5e08\u7237\u8bb2\u8fc7\u7684\u8bdd\uff0c\u4e0d\u89c9\u7a98\u7136\u9053\uff1a\u201c\u4f60\u5343\u4e07\u522b\u5f53\u771f\uff0c\u6211\u4e0d\u8fc7\u662f\u9a97\u59d3 \u4e01\u7684\u3002\u5982\u679c\u548c\u5979\u771f\u53d1\u751f\u8fc7\u90a3\u79cd\u4e8b\uff0c\u4f55\u81f3\u4e8e\u79bb\u5f00\u4e1c\u6d77\u9662\uff0c\u60f9\u607c\u4e86\u970d\u5143\u4f3d\uff0e\u201d \u3000\u3000\u5ffd\u542c\u82d7\u7d20\u82d3\u5931\u58f0\u9053\uff1a\u201c\u90a3\u8fb9\u6811\u4e0a\u6709\u4eba\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u7acb\u65f6\u4e5f\u609a\u7136\u4e00\u60ca\uff0c\u6e38\u76ee\u77a5\u53bb\uff0c\u679c\u7136\uff0c\u5728\u4e8c\u4e09\u4e08\u5916\u7684\u5927\u6811\u679d\u53f6\u6df1\u5904\uff0e\u85cf\u7740\u4e00\u4e2a\u4eba\u5f71\u3002 \u3000\u3000\u201c\u4f60\u4e0d\u8981\u4e0b\u53bb\uff0c\u7ee7\u7eed\u5728\u6811\u4e0a\u76d1\u89c6\uff0c\u7531\u6211\u6765\u5904\u7f6e\u4ed6\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u8bf4\u8bdd\u95f4\u9065\u7a7a\u4e00\u6307\uff0c\u76f4\u5411\u90a3\u4eba\u5c04\u51fa\u4e00\u7f15\u6307\u98ce\uff0c\u7d27\u8ddf\u7740\u4eba\u5df2\u8dc3\u4e0b\u5730\u53bb\u3002 \u3000\u3000\u53ea\u542c\u90a3\u4eba\u4e00\u58f0\u95f7\u54fc\uff0c\u4fbf\u7531\u6811\u4e0a\u6454\u4e86\u4e0b\u53bb\uff0c\u5f53\u4ed6\u6b63\u60f3\u6323\u624e\u722c\u8d77\uff0c\u9ea6\u5c0f\u660e\u65e9\u5df3\u63a0\u5230\uff0c\u5251\u5c16\u4e5f \u65e9\u5df2\u9876\u4e0a\u90a3\u4eba\u524d\u80f8\uff0e \u3000\u3000\u867d\u5728\u591c\u95f4\uff0e\u9ea6\u5c0f\u660e\u4ecd\u4f9d\u7a00\u8ba4\u5f97\u51fa\u6b64\u4eba\u9762\u8c8c\uff0c\u4ed6\u628a\u5251\u5c16\u5fae\u5fae\u5411\u524d\u2014\u63a8\u9053\uff1a\u201c\u4f60\u53ef\u662f\u6731\u6b63\uff1f\u201d \u3000\u3000\u90a3\u4eba\u679c\u7136\u662f\u6731\u6b63\uff0c\u5413\u5f97\u4ed6\u5f20\u53e3\u7ed3\u820c\u7684\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\u662f\uff0c\u9ea6\u5c0f\u4fa0\u9976\u547d\uff01\u201d \u3000\u3000\u539f\u6765\u8fd9\u6731\u6b63\u867d\u7136\u8eab\u4efd\u4e0d\u9ad8\uff0c\u53ea\u662f\u4e2a\u5c0f\u5934\u76ee\uff0e\u5374\u662f\u971c\u5143\u4f3d\u7684\u5fc3\u8179\u624b\u4e0b\uff0c\u4ed6\u6709\u4e00\u4e2a\u5f1f\u5f1f\u6731\u9ad8\uff0c \u662f\u8ff7\u8e2a\u8c37\u5173\u5361\u4e0a\u7684\u4e00\u540d\u54e8\u5175\u3002 \u3000\u3000\u6570\u6708\u524d\u67cf\u9f84\u9662\u7fa4\u8c6a\u7531\u6d59\u4e1c\u5929\u53f0\u4e07\u82b1\u5bab\u56de\u8c1c\u8e2a\u8c37\u65f6\u88ab\u9ea6\u5c0f\u660e\u6740\u6b7b\uff0c\u6ca1\u60f3\u5230\u8fd9\u5f1f\u5144\u4e8c\u4eba\uff0c\u7adf \u7136\u9047\u5230\u540c\u4e00\u4e2a\u547d\u4e2d\u514b\u661f\uff0e \u3000\u3000\u5f53\u4e0b\uff0c\u9ea6\u5c0f\u660e\u51b7\u51b7\u4e00\u7b11\u9053\uff1a\u201c\u4f60\u591c\u665a\u4e4b\u95f4\uff0c\u722c\u5230\u6811\u4e0a\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6731\u6b63\u54bd\u4e0b\u2014\u53e3\u553e\u6cab\u9053\uff1a\u201c\u5c0f\u7684\u662f\u5949\u547d\u5de1\u591c\uff0c\u6240\u4ee5\u624d\u5230\u4e86\u8fd9\u91cc\u3002\u201d \u3000\u3000\u201c\u5de1\u591c\u8be5\u5728\u5730\u4e0a\u5404\u5904\u6e38\u52a8\uff0c\u4eba\u5728\u6811\u4e0a\uff0c\u52a8\u5f39\u4e0d\u5f97\uff0c\u600e\u80fd\u79f0\u4e3a\u5de1\u591c\uff1f\u201d \u3000\u3000\u201c\u5c0f\u7684\u662f\u60f3\u2026\u2026\u201d \u3000\u3000\u201c\u60f3\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u60f3\u5077\u770b\u8bb8\u4ed9\u5b50\u3002\u201d \u3000\u3000\u201c\u65b9\u624d\u4f60\u5df2\u7ecf\u770b\u5230\u4e86\uff0c\u597d\u4e0d\u597d\u770b\uff1f\u201d \u3000\u3000\u201c\u597d\u2026\u2026\u597d\u770b\uff01\u771f\u2026\u2026\u771f\u597d\u770b\uff01\u201d \u3000\u3000\u201c\u539f\u6765\u4f60\u8fd9\u5c0f\u5b50\u662f\u4e2a****\u72c2\uff0c\u8fd8\u60f3\u4e0d\u60f3\u770b\uff1f\u201d \u3000\u3000\u201c\u5c0f\u7684\u4e0d\u2026\u2026\u4e0d\u6562\u518d\u770b\u4e86\uff01\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u6015\u665a\u4e0a\u7761\u4e0d\u7740\u89c9\u3002\u201d \u3000\u3000\u201c\u4f60\u8fd8\u60f3\u56de\u53bb\u7761\u89c9\u5417\uff1f\u201d \u3000\u3000\u6731\u6b63\u4e0d\u89c9\u8138\u8272\u5927\u53d8\uff0e\u521a\u8981\u547c\u53eb\u51fa\u58f0\uff0c\u9ea6\u5c0f\u660e\u5251\u5c16\u65e9\u5df2\u7a7f\u80f8\u800c\u8fc7\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u8d77\u521d\u4e0d\u60f3\u6740\u6731\u6b63\uff0c\u4f46\u60f3\u5230\u81ea\u5df1\u548c\u82d7\u7d20\u82d3\u7684\u884c\u8e2a\u5df2\u88ab\u4ed6\u53d1\u73b0\uff0c\u4ed6\u52bf\u5fc5\u8f6c\u62a5\u970d\u5143\u4f3d\uff0e \u5230\u90a3\u65f6\u5c82\u4e0d\u53c8\u8981\u6380\u8d77\u4e00\u573a\u98ce\u6ce2\uff1f \u3000\u3000\u4ed6\u628a\u5b8b\u6b63\u7684\u5c38\u4f53\u629b\u8fdb\u8349\u4e1b\uff0c\u53c8\u56de\u5230\u539f\u6765\u7684\u6811\u4e0a\u3002 \u3000\u3000\u521a\u624d\u8fd9\u756a\u6740\u4eba\u7ecf\u8fc7\uff0e\u4e0d\u5fc5\u9ea6\u5c0f\u660e\u89e3\u8bf4\uff0c\u82d7\u7d20\u82d3\u5df2\u770b\u5f97\u6e05\u6e05\u695a\u695a\u3002 \u3000\u3000\u5c31\u5728\u4ed6\u56de\u6811\u4e0a\u4e0d\u4e45\uff0e\u8fdc\u5904\u4e00\u6761\u4eba\u5f71\u7f13\u7f13\u800c\u6765\uff0c\u867d\u7136\u65e0\u6cd5\u8ba4\u6e05\u662f\u8c01\uff0c\u731c\u60f3\u4e2d\u5374\u5341\u6709\u516b\u4e5d\u5fc5 \u662f\u82d7\u7d20\u5170\u3002 \u3000\u3000\u90a3\u4eba\u5f71\u6e10\u6e10\u63a5\u8fd1\uff0c\u4f9d\u7a00\u8ba4\u51fa\u679c\u7136\u662f\u82d7\u7d20\u5170\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u56e0\u82d7\u7d20\u82d3\u5df2\u6539\u626e\u7537\u88c5\uff0c\u82e5\u7531\u5979\u5355\u72ec\u4e0a\u524d\u76f8\u8ba4\uff0e\u8bf4\u4e0d\u5b9a\u4f1a\u5f04\u51fa\u610f\u5916\uff0c\u8fde\u5fd9\u4f4e\u58f0\u9053\uff1a \u201c\u82d7\u59d1\u5a18\u8bf7\u4ecd\u5728\u6811\u4e0a\u522b\u52a8\uff0e\u7531\u6211\u4e0b\u53bb\u5bf9\u4ee4\u59d0\u8bf4\u660e\u3002\u201d \u3000\u3000\u4ed6\u8bdd\u521a\u8bf4\u5b8c\uff0c\u4eba\u5df2\u98d8\u82b1\u843d\u53f6\u822c\u63a0\u4e0b\u6811\u53bb\uff0c\u679c\u7136\u8f7b\u8eab\u529f\u592b\u9ad8\u7edd\uff0c\u5c45\u7136\u70b9\u5c18\u4e0d\u60ca\u3002 \u3000\u3000\u63a5\u7740\u8fc5\u901f\u5730\u5411\u82d7\u7d20\u5170\u8fce\u53bb\u3002 \u3000\u3000\u82d7\u7d20\u5170\u8ba4\u51fa\u662f\u9ea6\u5c0f\u660e\uff0c\u968f\u5373\u505c\u4e0b\u811a\u6b65\uff0c\u4f4e\u58f0\u95ee\u9053\uff1a\u201c\u5979\u600e\u4e48\u6ca1\u6765\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u5c0f\u5f1f\u8981\u5148\u544a\u8bc9\u4f60\uff0e\u5979\u662f\u5973\u626e\u7537\u88c5\uff0c\u514d\u5f97\u5f85\u4f1a\u4f60\u89c1\u4e86\u5979\u5403\u60ca\uff0c\u82e5\u60ca\u547c\u51fa\u58f0\uff0c \u60ca\u52a8\u4e86\u5de1\u591c\u7684\uff0c\u5c82\u4e0d\u7cdf\u7cd5\uff1f\u201d \u3000\u3000\u82d7\u7d20\u5170\u9053\uff1a\u201c\u6211\u77e5\u9053\u4e86\uff0c\u4f60\u9a6c\u4e0a\u901a\u77e5\u5979\u6765\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u8f6c\u8eab\u5411\u540e\u62db\u4e86\u62db\u624b\u3002 \u3000\u3000\u4e00\u6761\u4eba\u5f71\uff0c\u7531\u5927\u6811\u4e0a\u4e00\u8dc3\u800c\u4e0b\u3002 \u3000\u3000\u5c31\u5728\u82d7\u7d20\u82d3\u521a\u521a\u6765\u5230\u8ddf\u524d\uff0c\u7a81\u542c\u82d7\u7d20\u5170\u5931\u58f0\u53eb\u9053\uff1a\u201c\u6811\u6797\u91cc\u6709\u4eba\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u4e5f\u609a\u7136\u5403\u60ca\uff0c\u6025\u6025\u4e00\u62e6\u82d7\u7d20\u82d3\u9053\uff1a\u201c\u5feb\u5feb\u5148\u628a\u6811\u6797\u91cc\u7684\u8033\u76ee\u6e05\u9664\u518d\u8bf4\uff01\u201d \u3000\u3000\u82d7\u7d20\u82d3\u6599\u77e5\u4e8b\u6001\u4e25\u91cd\uff0c\u53ea\u5f97\u6682\u65f6\u6487\u4e0b\u59d0\u59d0\uff0c\u62d4\u5251\u968f\u9ea6\u5c0f\u660e\u8f6c\u8eab\u518d\u5954\u56de\u6811\u6797\u3002 \u3000\u3000\u7531\u4e8e\u591c\u8272\u592a\u6697\uff0c\u53c8\u5728\u6811\u6797\u91cc\uff0c\u4e24\u4eba\u867d\u6781\u76ee\u641c\u7d22\uff0c\u5374\u5e76\u6ca1\u53d1\u73b0\u4eba\u8e2a\u3002 \u3000\u3000\u60f3\u518d\u95ee\u82d7\u7d20\u5170\u53d1\u73b0\u4eba\u5f71\u5728\u4f55\u5904\u65f6\uff0c\u82d7\u7d20\u5170\u53c8\u5df2\u5931\u53bb\u6240\u5728\u3002 \u3000\u3000\u5982\u679c\u6811\u6797\u5185\u9664\u4e86\u5df2\u6b7b\u4e86\u7684\u6731\u6b63\u5916\uff0c\u5f53\u771f\u8fd8\u6709\u4eba\uff0c\u4e0d\u96be\u60f3\u89c1\u5fc5\u662f\u970d\u5143\u4f3d\u6d3e\u51fa\u7684\u5fc3\u8179\u3002\u5982\u6b64 \u770b\u6765\uff0e\u8bf4\u4e0d\u5b9a\u4eca\u665a\u82d7\u7d20\u5170\u5916\u51fa\u4e0e\u82d7\u7d20\u82d3\u76f8\u4f1a\u4e4b\u4e8b\uff0e\u5df2\u88ab\u970d\u5143\u4f3d\u5f97\u77e5\u3002\u6240\u4ee5\u4e00\u5411\u6e05\u9759\u7684\u4e1c\u6d77\u9662 \u9644\u8fd1\uff0c\u624d\u4f1a\u5982\u6b64\u8b66\u6212\u68ee\u4e25\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u4f4e\u58f0\u9053\uff1a\u201c\u82d7\u59d1\u5a18\uff0c\u82e5\u5f53\u771f\u6811\u6797\u5185\u6709\u4eba\uff0c\u7edd\u5bf9\u4e0d\u80fd\u8ba9\u4ed6\u4eec\u8d70\u8131\u534a\u4e2a\uff0c\u5fc5\u987b\u4e00\u7f51\u6253 \u5c3d\uff0e\u5426\u5219\u540e\u679c\u592a\u4e25\u91cd\u4e86\u3002\u8fde\u4ee4\u59d0\u7684\u5904\u5883\uff0e\u4e5f\u4e0d\u582a\u60f3\u8c61\u3002\u201d \u3000\u3000\u82d7\u7d20\u82d3\u9053\uff1a\u201c\u53ef\u662f\u9664\u4e86\u65b9\u624d\u88ab\u4f60\u6740\u6b7b\u7684\u90a3\u4e00\u4e2a\uff0c\u54b1\u4eec\u5e76\u672a\u53d1\u73b0\u53e6\u5916\u6709\u4eba\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u8fd9\u7247\u6811\u6797\u8303\u56f4\u5e76\u4e0d\u7b97\u5927\uff0c\u6797\u5916\u4e24\u9762\u53c8\u662f\u60ac\u5d16\u5ced\u58c1\uff0c\u4ed6\u4eec\u60f3\u9003\uff0c\u53ea\u6709\u4e24\u4e2a\u65b9 \u5411\u597d\u8d70\uff0c\u5982\u679c\u4f60\u6211\u5404\u81ea\u5b88\u4f4f\u4e00\u4e2a\u65b9\u5411\uff0c\u5c31\u7edd\u5bf9\u8dd1\u4e0d\u4e86\u4ed6\u4eec\u3002\u201d \u3000\u3000\u201c\u5982\u679c\u4ed6\u4eec\u4e00\u76f4\u9690\u8eab\u6697\u5904\u4e0d\u52a8\u5462\uff1f\u201d \u3000\u3000\u201c\u90a3\u9664\u975e\u4ed6\u4eec\u4e00\u76f4\u8eb2\u85cf\u5230\u5929\u4eae\uff0c\u89c6\u754c\u5e7f\u9614\uff0c\u4ed6\u4eec\u5c31\u65e0\u5904\u53ef\u8eb2\u3002\u201d \u3000\u3000\u201c\u6211\u62c5\u5fc3\u6709\u7edd\u9876\u7684\u9ad8\u624b\u5728\u5185\uff0e\u82e5\u5bf9\u65b9\u4eba\u591a\uff0c\u54b1\u4eec\u5c31\u662f\u53d1\u73b0\u4ed6\u4eec\u53c8\u6709\u4f55\u7528\uff1f\u201d \u3000\u3000\u201c\u4f60\u653e\u5fc3\u5cad\u5357\u4e8c\u5947\u4e0d\u4f1a\u88ab\u6d3e\u51fa\u6765\u5e72\u8fd9\u79cd\u4e8b\u60c5\uff0c\u6700\u591a\u662f\u6c5f\u5357\u56db\u602a\u4e4b\u6d41\uff0e\u82e5\u8bf4\u8ff7\u8e2a\u8c37\u5185\u7684\u7edd \u9876\u9ad8\u624b\uff0c\u59d1\u5a18\u548c\u6211\u4e5f\u8be5\u5f53\u4e4b\u65e0\u6127\u4e86\u3002\u201d \u3000\u3000\u82d7\u7d20\u82d3\u70b9\u70b9\u5934\u9053\uff1a\u201c\u4f60\u8bf4\u5f97\u5bf9\uff0c\u6211\u5230\u53e6\u4e00\u9762\u51fa\u53e3\u53bb\u5b88\u7740\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u53c8\u53ee\u549b\u9053\uff1a\u201c\u6700\u597d\u662f\u5b88\u682a\u5f85\u5154\uff0c\u82e5\u5230\u5904\u641c\u5bfb\uff0c\u5bf9\u65b9\u53cd\u800c\u5bb9\u6613\u5728\u4f60\u4e0d\u6ce8\u610f\u4e4b\u65f6\u5077\u5077 \u6e9c\u8d70\u3002\u201d \u3000\u3000\u82d7\u7d20\u82d3\u8d70\u540e\uff0c\u9ea6\u5c0f\u660e\u5c31\u5728\u6811\u6797\u524d\u7f18\u5b88\u4f4f\u53e6\u4e00\u8fb9\u3002 \u3000\u3000\u548c\u4ed6\u65b9\u624d\u53ee\u549b\u82d7\u7d20\u82d3\u7684\u8bdd\u4e00\u6837\uff0c\u4ed6\u53ea\u805a\u7cbe\u4f1a\u795e\u7684\u76d1\u89c6\u7740\uff0e\u7edd\u4e0d\u5230\u6797\u5185\u8d70\u52a8\u641c\u7d22\uff0e\u5426\u5219\u591c \u95f4\u89c6\u7ebf\u4e0d\u660e\uff0c\u4e0d\u4f46\u5f92\u52b3\u65e0\u529f\uff0c\u53cd\u800c\u6613\u4e2d\u5bf9\u65b9\u6697\u7b97\u3002 \u3000\u3000\u4ed6\u5e0c\u671b\u80fd\u53d1\u73b0\u82d7\u7d20\u5170\u8eb2\u5728\u4f55\u5904\uff0e\u7136\u800c\u4e00\u76f4\u65e0\u6cd5\u627e\u5230\u3002\u8bf4\u4e0d\u5b9a\u5df2\u6e9c\u56de\u4e86\u5929\u661f\u9662\uff0c\u81ea\u5df1\u597d\u4e0d \u5bb9\u6613\u5b89\u6392\u4e86\u5979\u4eec\u59d0\u59b9\u76f8\u89c1\u7684\u673a\u4f1a\uff0c\u5374\u53c8\u843d\u4e86\u4e00\u573a\u7a7a\u3002 \u3000\u3000\u8db3\u8db3\u987f\u996d\u5de5\u592b\u8fc7\u53bb\uff0c\u56db\u4e0b\u4ecd\u6ca1\u6709\u534a\u70b9\u52a8\u9759\uff0c\u53c8\u65e0\u6cd5\u4e0e\u82d7\u7d20\u82d3\u53d6\u5f97\u8054\u7cfb\uff0c\u53ea\u6709\u82e6\u7b49\u4e0b\u53bb\u3002 \u3000\u3000\u4e0d\u8fc7\u4ed6\u5e76\u672a\u5931\u671b\uff0c\u53ea\u8981\u6797\u5185\u85cf\u7740\u6709\u4eba\uff0c\u4ed6\u6709\u628a\u63e1\u5fc5\u5b9a\u8ba9\u4ed6\u4eec\u96be\u4ee5\u9003\u8131\uff0c\u9664\u975e\u65b9\u624d\u82d7\u7d20\u5170 \u662f\u770b\u8d70\u4e86\u773c\u3002 \u3000\u3000\u53c8\u8fc7\u4e86\u76cf\u8336\u5de5\u592b\uff0c\u9ea6\u5c0f\u660e\u7ec8\u4e8e\u53d1\u73b0\u4e86\u53ef\u7591\uff0c\u53ea\u89c1\u56db\u4e94\u4e08\u8fdc\u5904\u7684\u4e00\u5806\u4e1b\u8349\uff0c\u5c45\u7136\u5728\u5fae\u5fae\u6643 \u52a8\u3002 \u3000\u3000\u4e1b\u8349\u65e0\u98ce\u81ea\u52a8\uff0c\u663e\u7136\u662f\u6709\u4eba\u5728\u8349\u5185\u79fb\u52a8\uff0e\u6216\u8005\u5728\u62e8\u8349\u5411\u5916\u63a2\u89c6\u3002 \u3000\u3000\u5728\u8fd9\u6bb5\u4ec5\u4ec5\u56db\u4e94\u4e08\u6781\u77ed\u7684\u8def\u9014\u4e2d\uff0c\u4ed6\u7684\u89c6\u7ebf\u4e00\u76f4\u6ca1\u79bb\u5f00\u90a3\u5806\u4e1b\u8349\uff0c\u5df2\u53ef\u65ad\u5b9a\u91cc\u9762\u6709\u4eba\u65e0 \u7591\u3002 \u3000\u3000\u4e8e\u662f\uff0c\u5728\u4ed6\u5927\u7ea6\u79bb\u90a3\u8349\u4e1b\u4e24\u4e08\u5de6\u53f3\u65f6\uff0c\u4fbf\u817e\u8eab\u76f4\u8d77\uff0c\u4e00\u6251\u800c\u4e0b\uff0c\u540c\u65f6\u624b\u4e2d\u957f\u5251\u4e5f\u62a1\u4e86\u51fa \u53bb\u3002 \u3000\u3000\u679c\u7136\uff0c\u5c31\u5728\u4ed6\u51cc\u8eab\u6309\u6b65\u5c31\u73ed\u5251\u950b\u523a\u51fa\u65f6\uff0e\u4e24\u6761\u4eba\u5f71\u8fc5\u901f\u65e0\u6bd4\u7684\u7531\u8349\u4ece\u4e2d\u5192\u4e86\u51fa\u6765\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u5c82\u80fd\u8ba9\u4ed6\u4eec\u8d70\u8131\uff0c\u5251\u52bf\u5982\u8679\uff0c\u987f\u65f6\u628a\u90a3\u4e24\u4eba\u7f69\u5728\u5251\u5e55\u4e4b\u5185\u3002 \u3000\u3000\u51e0\u4e4e\u5728\u540c\u4e00\u65f6\u95f4\uff0c\u6797\u5185\u8fdc\u5904\u4e5f\u4f20\u6765\u5175\u5203\u4ea4\u51fb\u4e4b\u58f0\uff0c\u90a3\u65b9\u5411\u6b63\u662f\u82d7\u7d20\u82d3\u628a\u5b88\u7684\uff0c\u53ef\u4ee5\u60f3\u89c1 \u82d7\u7d20\u82d3\u4e5f\u548c\u4eba\u4ea4\u4e0a\u4e86\u624b\u3002 \u3000\u3000\u88ab\u9ea6\u5c0f\u660e\u5251\u52bf\u5708\u4f4f\u7684\u4e8c\u4eba\uff0c\u672c\u60f3\u5404\u81ea\u9003\u7a9c\uff0c\u4f46\u56e0\u9ea6\u5c0f\u660e\u5251\u52bf\u592a\u7d27\uff0c\u4f7f\u4ed6\u4eec\u627e\u4e0d\u5230\u8131\u8eab\u673a \u4f1a\u3002 \u3000\u3000\u56e0\u4e4b\uff0c\u4e8c\u4eba\u4e0d\u5f97\u4e0d\u62fc\u547d\u53cd\u51fb\uff0c\u5c45\u7136\u8feb\u5f97\u9ea6\u5c0f\u660e\u5728\u77ed\u65f6\u95f4\u5185\u65e0\u6cd5\u4f24\u5230\u4ed6\u4eec\uff0c\u53ef\u89c1\u8fd9\u4e8c\u4eba\u679c \u7136\u662f\u8eab\u624b\u6781\u9ad8\uff0c\u7edd\u975e\u6cdb\u6cdb\u4e4b\u8f88\uff0e \u3000\u3000\u8fd9\u4e24\u4eba\u7684\u5175\u5203\uff0c\u3000\u2014\u4e2a\u662f\u77ed\u67c4\u6708\u7259\u94f2\uff0c\u4e00\u4e2a\u662f\u516b\u94a9\u9e21\u722a\u9570\uff0c\u9ea6\u5c0f\u660e\u5f88\u5feb\u4fbf\u8ba4\u51fa\u524d\u8005\u662f\u6c34 \u9b3c\u6d77\u767b\uff0e\u540e\u8005\u662f\u5730\u864e\u5305\u901a\u3002 \u3000\u3000\u5982\u6b64\u770b\u6765\uff0c\u90a3\u8fb9\u548c\u82d7\u7d20\u82d3\u6fc0\u6218\uff0c\u8be5\u662f\u6c5f\u5357\u56db\u602a\u4e2d\u7684\u53e6\u4e24\u602a\u4e86\u3002 \u3000\u3000\u6b63\u6240\u8c13\u4eba\u6025\u62fc\u547d\uff0c\u72d7\u6025\u8df3\u5899\u3002\u82e5\u5728\u5e73\u65f6\uff0c\u6d77\u767b\u548c\u5305\u901a\u7edd\u5bf9\u96be\u62b5\u9ea6\u5c0f\u660e\u5341\u62db\uff0c\u4f46\u6b64\u523b\u4ed6\u4eec \u56e0\u5df2\u4e0d\u987e\u4e00\u5207\u3002\u518d\u52a0\u4f7f\u7528\u7684\u5e76\u975e\u4e00\u822c\u5175\u5203\uff0c\u591c\u95f4\u66f4\u5bb9\u6613\u53d1\u6325\u5a01\u529b\uff0c\u662f\u4ee5\u624d\u80fd\u7ee7\u7eed\u652f\u6301\u4e0b\u53bb\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u4e5f\u8ddf\u7740\u53d1\u4e86\u5a01\uff0e\u4e00\u9762\u8fce\u6218\uff0c\u4e00\u9762\u6697\u8fd0\u5185\u529b\uff0c\u547c\u7684\u4e00\u638c\uff0c\u731d\u7136\u95f4\u5288\u51fa\u53bb\u3002 \u3000\u3000\u8fd9\u4e00\u638c\u8db3\u6709\u5f00\u7891\u88c2\u77f3\u4e4b\u529f\uff0e\u4e0d\u504f\u4e0d\u501a\uff0c\u521a\u597d\u5288\u4e0a\u4e86\u5730\u864e\u5305\u901a\u524d\u80f8\uff0e \u3000\u3000\u5305\u901a\u2014\u58f0\u60e8\u547c\uff0c\u53e3\u4e2d\u9c9c\u8840\u76f4\u55b7\uff0c\u5f53\u573a\u5012\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u5269\u4e0b\u6c34\u9b3c\u6d77\u767b\u5728\u5927\u611f\u60ca\u9a87\u4e4b\u4e0b\uff0c\u8fd8\u6ca1\u6765\u5f97\u53ca\u62bd\u8eab\uff0c\u4fbf\u88ab\u9ea6\u5c0f\u660e\u9f50\u80a9\u5c06\u53f3\u81c2\u65a9\u4e0b\uff0c\u968f\u5373\u5012 \u5728\u8840\u6cca\u4e2d\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u62c5\u5fc3\u82d7\u7d20\u82d3\u90a3\u8fb9\u6709\u4eba\u8d70\u8131\uff0c\u7acb\u5373\u5411\u6811\u6797\u53e6\u4e00\u8fb9\u63a0\u53bb\u3002 \u3000\u3000\u8fd9\u65f6\u82d7\u7d20\u82d3\u628a\u5b88\u7684\u53e6\u4e00\u65b9\uff0c\u5df2\u542c\u4e0d\u5230\u5175\u5203\u4ea4\u51fb\u4e4b\u58f0\uff0c\u6b63\u5954\u884c\u95f4\uff0c\u7a81\u7136\u659c\u91cc\u95ea\u7535\u822c\u4e00\u5251\u88ad \u6765\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u5403\u4e86\u4e00\u60ca\uff0c\u6025\u6025\u62a1\u5251\u683c\u53bb\u3002 \u3000\u3000\u53cc\u65b9\u5175\u5203\u4e00\u63a5\uff0c\u5374\u542c\u82d7\u7d20\u82d3\u7684\u58f0\u97f3\u9053\uff1a\u201c\u539f\u6765\u662f\u4f60\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u6536\u56de\u5251\u6765\u9053\uff1a\u201c\u597d\u9669\uff01\u6211\u662f\u6765\u770b\u770b\u4f60\u8fd9\u8fb9\u600e\u6837\u4e86\uff1f\u201d \u3000\u3000\u82d7\u7d20\u82d3\u7528\u5251\u5c16\u5f80\u6811\u4e0b\u4e00\u6307\u9053\uff1a\u201c\u5c31\u5728\u90a3\u91cc\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u770b\u53bb\uff0c\u679c\u7136\u6811\u4e0b\u6b63\u8eba\u7740\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u201c\u4f60\u628a\u4ed6\u6740\u6b7b\u4e86\uff1f\u201d \u3000\u3000\u201c\u6211\u60f3\u4f60\u53ef\u80fd\u8981\u5411\u4ed6\u95ee\u8bdd\uff0c\u53ea\u70b9\u4e86\u4ed6\u7684\u7a74\u9053\u3002\u201d \u3000\u3000\u201c\u65b9\u624d\u4f60\u53ea\u5bf9\u4ed8\u7684\u4ed6\u4e00\u4e2a\uff1f\u201d \u3000\u3000\u201c\u6211\u8fd9\u8fb9\u53ea\u6709\u4e00\u4e2a\uff0c\u4f60\u90a3\u8fb9\u5462\uff1f\u201d \u3000\u3000\u201c\u4e24\u4e2a\u3002\u201d \u3000\u3000\u201c\u6740\u6b7b\u6ca1\u6709\uff1f\u201d \u3000\u3000\u201c\u53ef\u80fd\u8fd8\u6d3b\u7740\u3002\u201d \u3000\u3000\u867d\u7136\u8fd9\u4e8c\u4eba\u90fd\u5df2\u65e0\u6cd5\u9003\u8131\uff0c\u9ea6\u5c0f\u660e\u5374\u9887\u611f\u4e0d\u59a5\uff0c\u82d7\u7d20\u82d3\u5360\u5012\u7684\u4e5f\u662f\u6c5f\u5357\u56db\u602a\u4e2d\u7684\u2014\u602a\uff0c \u90a3\u4e48\u53e6\u2014\u602a\u5c82\u4e0d\u5df2\u88ab\u4ed6\u9003\u8d70\uff1f \u3000\u3000\u4ed6\u6765\u5230\u6811\u4e0b\uff0c\u4fef\u8eab\u770b\u53bb\uff0c\u4e00\u70b9\u4e0d\u9519\uff0c\u6b64\u4eba\u6b63\u662f\u56db\u602a\u4e2d\u7684\u6797\u9b54\u82d7\u5f3a\u3002 \u3000\u3000\u82d7\u7d20\u82d3\u9053\uff1a\u201c\u8fd9\u4eba\u6b66\u529f\u4e0d\u5f31\uff0c\u5fc5\u662f\u4e00\u4e2a\u5934\u76ee\uff0c\u53ef\u8ba4\u51fa\u4ed6\u662f\u8c01\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u662f\u6c5f\u5357\u56db\u602a\u4e4b\u4e00\uff0c\u88ab\u6211\u5236\u670d\u7684\u4e5f\u662f\u4e24\u602a\uff0c\u5374\u5355\u5355\u4e0d\u89c1\u5927\u602a\u5c71\u9b48\u6bdb\u8d8a\uff0c\u8fd9\u4e00 \u6765\u6050\u6015\u8981\u7cdf\u3002\u82e5\u4ed6\u56de\u53bb\u7980\u62a5\u970d\u5143\u4f3d\uff0c\u8fd9\u6b21\u6211\u662f\u771f\u6b63\u4e0d\u80fd\u5728\u8ff7\u8e2a\u8c37\u5446\u4e0b\u53bb\u4e86\uff01\u201d \u3000\u3000\u82d7\u7d20\u82d3\u4e0d\u89c9\u4e5f\u7740\u4e86\u614c\uff0c\u9053\uff1a\u201c\u54b1\u4eec\u9a6c\u4e0a\u5728\u6811\u6797\u91cc\u5c55\u5f00\u641c\u7d22\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u6447\u5934\u9053\uff1a\u201c\u4e0d\u5fc5\u5566\uff01\u5982\u679c\u6811\u6797\u91cc\u9664\u4e86\u4ed6\u4eec\u4e09\u4e2a\u4ee5\u5916\u8fd8\u6709\u4eba\uff0c\u65e9\u5c31\u8dd1\u4e86\uff0c\u9664\u975e\u662f\u6b7b \u4eba\u3002\u201d \u3000\u3000\u201c\u90a3\u5c31\u5148\u5904\u7f6e\u4e86\u4ed6\u4eec\u518d\u8bf4\u3002\u201d \u3000\u3000\u201c\u4e5f\u597d\uff0c\u4f60\u8bf7\u6682\u65f6\u5b88\u5728\u8fd9\u91cc\uff0e\u6211\u5148\u8fc7\u53bb\u5904\u7f6e\u4e86\u90a3\u4e24\u4e2a\u9a6c\u4e0a\u56de\u6765\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u56de\u5230\u539f\u5904\uff0c\u88ab\u4e00\u638c\u5288\u4e2d\u524d\u80f8\u7684\u5730\u864e\u5305\u901a\u4ecd\u8eba\u5728\u5730\u4e0a\u4e0d\u52a8\uff0c\u88ab\u65a9\u53bb\u53f3\u81c2\u7684\u6c34\u9b3c\u6d77\u767b \u5219\u5728\u5730\u4e0a\u4e0d\u4f4f\u60e8\u547c\u6253\u6eda\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u6beb\u4e0d\u72b9\u8c6b\uff0e\u5148\u4e00\u5251\u5411\u6d77\u767b\u7684\u524d\u80f8\u6233\u53bb\uff0c\u63a5\u7740\u518d\u5c06\u5305\u901a\u8111\u888b\u5272\u4e0b\u3002 \u3000\u3000\u5239\u65f6\u4e4b\u95f4\uff0e\u6d77\u767b\u548c\u5305\u901a\u5df2\u4e00\u547d\u545c\u547c\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u518d\u56de\u5230\u82d7\u7d20\u82d3\u5904\u3002 \u3000\u3000\u201c\u90a3\u8fb9\u600e\u4e48\u6837\u4e86\uff1f\u201d\u82d7\u7d20\u82d3\u95ee\u9053\u3002 \u3000\u3000\u201c\u90a3\u8fb9\u4e24\u4eba\u5168\u5df2\u88ab\u6211\u6740\u6b7b\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u5462\uff1f\u201d \u3000\u3000\u201c\u89e3\u5f00\u4ed6\u7684\u7a74\u9053\uff0e\u95ee\u8fc7\u8bdd\u518d\u5904\u7f6e\u3002\u201d \u3000\u3000\u82d7\u7d20\u82d3\u63a2\u624b\u4e00\u6307\uff0c\u89e3\u5f00\u4e86\u6797\u9b54\u82d7\u5f3a\u7684\u7a74\u9053\uff0e \u3000\u3000\u5f53\u82d7\u5f3a\u7741\u5f00\u773c\u6765\uff0c\u770b\u5230\u524d\u9762\u53c8\u591a\u4e86\u4e00\u4eba\uff0c\u800c\u4e14\u4f9d\u7a00\u8ba4\u51fa\u662f\u9ea6\u5c0f\u660e\uff0e\u4e0d\u89c9\u9a87\u7136\u53eb\u9053\uff1a\u201c\u9ea6 \u5c0f\u4fa0\u9976\u547d\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u7b11\u9053\uff1a\u201c\u6211\u672c\u6765\u6ca1\u51c6\u5907\u6740\u4f60\uff0c\u4f55\u5fc5\u53eb\u9976\u547d\u3002\u201d \u3000\u3000\u82d7\u5f3a\u603b\u7b97\u7a0d\u7a0d\u7a33\u4e0b\u5fc3\u6765\uff0c\u7740\u9053\uff1a\u201c\u90a3\u4e48\u9ea6\u5c0f\u4fa0\u5c31\u8be5\u653e\u6211\u8d70\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u4e0d\u52a8\u58f0\u8272\u9053\uff1a\u201c\u6211\u60f3\u95ee\u4f60\u51e0\u53e5\u8bdd\uff0c\u53ea\u8981\u4f60\u80af\u4ece\u5b9e\u56de\u7b54\uff0e\u95ee\u8fc7\u8bdd\u81ea\u7136\u653e\u4f60\u8d70\uff01\u201d \u3000\u3000\u201c\u9ea6\u5c0f\u4fa0\u8bf7\u5feb\u95ee\u3002\u201d \u3000\u3000\u201c\u6211\u8fd8\u4e0d\u6025\uff0e\u4f60\u6025\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u6211\u2026\u2026\u201d \u3000\u3000\u201c\u597d\uff0c\u73b0\u5728\u5c31\u95ee\uff0e\u4f60\u4e3a\u4ec0\u4e48\u4eca\u665a\u8eab\u5728\u8fd9\u7247\u6811\u6797\u91cc\uff1f\u201d \u3000\u3000\u201c\u4eca\u665a\u6211\u2026\u2026\u6211\u8f6e\u503c\u5de1\u591c\u3002\u201d \u3000\u3000\u201c\u636e\u6211\u6240\u77e5\uff0c\u5de1\u591c\u7684\u5f88\u5c11\u5230\u8fd9\u9644\u8fd1\u6765\uff0e\u4f60\u4e0d\u4f46\u5de1\u5230\u8fd9\u91cc\u6765\uff0c\u800c\u4e14\u8fd8\u6f5c\u4f0f\u5728\u6811\u6797\u91cc\u4e0d\u52a8\uff0e \u53ef\u662f\u6709\u4ec0\u4e48\u7279\u6b8a\u4efb\u52a1\uff1f\u201d \u3000\u3000\u201c\u4e0d\u7792\u9ea6\u5c0f\u4fa0\uff0e\u6211\u662f\u5949\u76df\u4e3b\u4e4b\u547d\uff0c\u76d1\u89c6\u662f\u5426\u6709\u4eba\u5077\u5077\u8fdb\u5165\u4e1c\u6d77\u9662\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u77e5\u4ed6\u8bf4\u7684\u7edd\u975e\u5b9e\u8bdd\uff0e\u7b11\u4e86\u7b11\u9053\uff1a\u201c\u8981\u60f3\u8fdb\u5165\u4e1c\u6d77\u9662\uff0e\u5fc5\u6709\u76df\u4e3b\u4ee4\u724c\uff0c\u800c\u4e14\u4e1c\u6d77\u9662 \u95e8\u5916\u6709\u4eba\u628a\u5b88\uff0e\u4f55\u5fc5\u4f60\u6765\u76d1\u89c6\uff1f\u201d \u3000\u3000\u82d7\u5f3a\u987f\u4e86\u987f\u9053\uff1a\u201c\u9ea6\u5c0f\u4fa0\u6709\u6240\u4e0d\u77e5\uff0e\u4e1c\u6d77\u9662\u76ee\u524d\u5df2\u7ecf\u5173\u95ed\uff0c\u76df\u4e3b\u5df2\u4e0d\u518d\u53d1\u51fa\u4ee4\u724c\uff0c\u4ed6\u8001 \u4eba\u5bb6\u62c5\u5fc3\u6709\u4eba\u5077\u5077\u5f80\u91cc\u786c\u95ef\uff0c\u6240\u4ee5\u624d\u6d3e\u6211\u5728\u9644\u8fd1\u76d1\u89c6\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u6de1\u6de1\u2014\u7b11\u9053\uff1a\u201c\u5c31\u7b97\u4f60\u8bf4\u7684\u4e0d\u9519\uff01\u76d1\u89c6\u4e00\u4e2a\u4e1c\u6d77\u9662\uff0c\u4e5f\u7528\u4e0d\u7740\u90a3\u4e48\u591a\u4eba\u3002\u201d \u3000\u3000\u82d7\u5f3a\u6296\u7740\u58f0\u97f3\u9053\uff1a\u201c\u9ea6\u5c0f\u4fa0\u662f\u8bf4\u4ed6\u4eec\uff1f\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u9519\uff01\u4f60\u4eec\u56db\u602a\u5168\u5728\u8fd9\u6811\u6797\u91cc\uff0c\u96be\u9053\u8fd8\u7792\u5f97\u4f4f\u6211\uff1f\u201d \u3000\u3000\u201c\u4e0d\uff01\u6211\u4eec\u8001\u5927\u6ca1\u6765\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u8fd9\u624d\u653e\u4e0b\u4e86\u5fc3\uff0c\u53c8\u7b11\u4e86\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u8001\u5927\u6bdb\u8d8a\u4e3a\u4ec0\u4e48\u6ca1\u6765\uff1f\u201d \u3000\u3000\u82d7\u5f3a\u9053\uff1b\u201c\u4ed6\u8fd8\u6709\u522b\u7684\u4e8b\uff0e\u9ea6\u5c0f\u4fa0\uff0c\u6211\u5df2\u8bf4\u4e86\u5b9e\u8bdd\uff0c\u8be5\u653e\u6211\u8d70\u4e86\u5427\uff1f\u201d \u3000\u3000\u201c\u6d77\u767b\u548c\u5305\u901a\u5df2\u88ab\u6211\u6349\u4f4f\uff0e\u4f60\u53ef\u77e5\u9053\u4ed6\u4eec\u662f\u4ec0\u4e48\u4e0b\u573a\uff1f\u201d \u3000\u3000\u201c\u4e0d\u2026\u2026\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u201c\u6211\u5df2\u7ecf\u653e\u4e86\u4ed6\u4eec\u3002\u201d \u3000\u3000\u201c\u90a3\u2026\u2026\u5c31\u8be5\u4e5f\u653e\u6211\uff0e\u201d \u3000\u3000\u201c\u522b\u5fd9\uff0c\u6211\u662f\u653e\u4ed6\u4eec\u56de\u8001\u5bb6\u53bb\u4e86\u3002\u201d \u3000\u3000\u82d7\u5f3a\u521a\u8981\u60ca\u547c\uff0c\u65e9\u88ab\u9ea6\u5c0f\u660e\u624b\u8d77\u5251\u843d\uff0c\u780d\u6389\u4e86\u8111\u888b\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u8fd8\u5251\u5165\u9798\u9053\uff1a\u201c\u5bf9\u4e0d\u8d77\uff0c\u6211\u7ed9\u4f60\u51cf\u5c11\u4e86\u4e00\u4f4d\u672c\u5bb6\u3002\u201d \u3000\u3000\u82d7\u7d20\u82d3\u832b\u7136\u95ee\u9053\uff1a\u201c\u4f60\u8bf4\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4ed6\u4e5f\u59d3\u82d7\u3002\u201d \u3000\u3000\u82d7\u7d20\u82d3\u5401\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u4f60\u597d\u50cf\u62ff\u6740\u4eba\u4e0d\u5f53\u56de\u4e8b\uff0e\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u4e5f\u6df1\u6df1\u2014\u53f9\u9053\uff1a\u201c\u6211\u4f55\u5c1d\u559c\u6b22\u6740\u4eba\uff0c\u4eca\u665a\u8fd9\u56db\u4e2a\u5168\u4e0d\u662f\u597d\u4eba\uff0c\u672c\u6765\u5c31\u8be5\u6740\u3002\u591a\u6740 \u4e00\u4e2a\u574f\u4eba\uff0c\u597d\u4eba\u5c31\u591a\u5f97\u4e00\u4efd\u4fdd\u969c\uff0e\u66f4\u4f55\u51b5\u82e5\u7559\u4e0b\u4ed6\u4eec\u6d3b\u53e3\uff0c\u54b1\u4eec\u67cf\u9f84\u9662\u5fc5\u5b9a\u906d\u6b83\u3002\u201d \u3000\u3000\u82d7\u7d20\u82d3\u7565\u2014\u6c89\u541f\u9053\uff1a\u201c\u4f60\u73b0\u5728\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u5982\u679c\u4eca\u665a\u6709\u4eba\u9003\u8131\uff0c\u6211\u5c31\u975e\u79bb\u5f00\u8ff7\u8e2a\u8c37\u4e0d\u53ef\uff0c\u73b0\u5df2\u7ecf\u7528\u4e0d\u7740\u62c5\u5fc3\u4e86\uff0e\u201d \u3000\u3000\u201c\u53ef\u662f\u4f60\u4eca\u665a\u6740\u7684\u56db\u4e2a\u4eba\uff0c\u5168\u662f\u8ff7\u8e2a\u8c37\u7684\u5934\u76ee\uff0c\u660e\u5929\u5fc5\u5b9a\u4ecd\u4f1a\u60ca\u52a8\u6574\u4e2a\u8ff7\u8e2a\u8c37\u3002\u201d \u3000\u3000\u201c\u5f53\u7136\u4f1a\u60ca\u52a8\u8ff7\u8e2a\u8c37\uff0e\u4f46\u970d\u5143\u4f3d\u5374\u96be\u67e5\u51fa\u8c01\u6740\u7684\u3002\u201d \u3000\u3000\u201c\u4e0d\u7ba1\u5982\u4f55\uff0e\u4f60\u603b\u8981\u63d0\u9ad8\u8b66\u89c9\uff0e\u201d \u3000\u3000\u201c\u6211\u5c82\u6b62\u660e\u5929\u8981\u63d0\u9ad8\u8b66\u89c9\uff0c\u65e9\u5c31\u63d0\u9ad8\u8b66\u89c9\u4e86\u3002\u95f2\u8bdd\u5c11\u8bf4\uff0c\u54b1\u4eec\u56de\u53bb\u5427\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u548c\u82d7\u7d20\u82d3\u56de\u5230\u67cf\u9f84\u9662\u540e\uff0c\u5e76\u672a\u60ca\u52a8\u4efb\u4f55\u4eba\uff0c\u5404\u81ea\u56de\u623f\u5b89\u5bdd\u3002 \u3000\u3000\u6b21\u65e5\u5929\u4eae\u540e\uff0c\u5947\u602a\u7684\u662f\u6628\u665a\u7684\u4e8b\uff0c\u4e5f\u5e76\u5f15\u8d77\u8f70\u52a8\uff0c\u4e0d\u4f46\u67cf\u9f84\u9662\u672a\u4f20\u6765\u6d88\u606f\uff0c\u8fde\u6574\u4e2a\u8ff7\u8e2a \u8c37\uff0c\u4e5f\u548c\u4ee5\u5f80\u4e00\u6837\u5e73\u9759\uff0e \u3000\u3000\u9ea6\u5c0f\u660e\u53ea\u9053\u5c1a\u65e0\u4eba\u53d1\u73b0\u6811\u6797\u4e2d\u7684\u56db\u5177\u5c38\u4f53\uff0c\u76f4\u5230\u4e0b\u5348\uff0c\u4ecd\u4e0d\u89c1\u6709\u4eba\u8bf4\u8d77\u6c5f\u5357\u4e09\u602a\u548c\u6731\u6b63 \u5728\u6811\u6797\u60e8\u6b7b\u7684\u4e8b\u3002 \u3000\u3000\u8fd9\u663e\u5f97\u592a\u4e0d\u5bfb\u5e38\u4e86\u3002\u56e0\u4e3a\u6b7b\u8005\u56db\u4eba\uff0c\u5728\u8ff7\u8e2a\u8c37\u90fd\u4e0d\u662f\u4e00\u822c\u7684\u4eba\u7269\uff0c\u5373\u4f7f\u56db\u540d\u55bd\u5175\u88ab\u4eba\u6740 \u6b7b\uff0e\u4e5f\u4e0d\u53ef\u80fd\u6d88\u606f\u4e0d\u4f20\u5230\u67cf\u9f84\u9662\u3002 \u3000\u3000\u4ed6\u54ea\u91cc\u77e5\u9053\uff0c\u8fd9\u6b63\u662f\u970d\u5143\u4f3d\u91c7\u53d6\u7684\u4e0d\u4f7f\u6d88\u606f\u5916\u6cc4\u7684\u624b\u6bb5\u3002 \u3000\u3000\u970d\u5143\u4f3d\u5728\u5929\u4e0d\u4eae\u5c31\u63a5\u5230\u5de1\u591c\u5934\u76ee\u7684\u62a5\u544a\uff0c\u53ea\u5e26\u4e86\u51e0\u540d\u5fc3\u8179\u8d76\u5230\u73b0\u573a\uff0c\u7ecf\u8fc7\u67e5\u9a8c\u540e\uff0c\u5f53\u573a \u628a\u56db\u5177\u5c38\u4f53\u5c31\u5730\u63a9\u57cb\u3002 \u3000\u3000\u5e76\u544a\u8beb\u5728\u573a\u7684\u4eba\u4e25\u5bc6\u5c01\u9501\u6d88\u606f\uff0c\u76f4\u5230\u5348\u996d\u540e\uff0c\u4ed6\u624d\u53ec\u96c6\u5cad\u5357\u4e8c\u5947\u548c\u6c5f\u5357\u56db\u602a\u4e2d\u4ec5\u4f59\u7684\u8001 \u5927\u5c71\u9b48\u6bdb\u8d8a\u5728\u5929\u661f\u9662\u5c55\u5f00\u5bc6\u8bae\u3002 \u3000\u3000\u8fd9\u65f6\u970d\u5143\u4f3d\u548c\u5cad\u5357\u4e8c\u5947\u8fd8\u7b97\u6c89\u5f97\u4f4f\u6c14\uff0e\u552f\u6709\u5c71\u9b48\u6bdb\u8d8a\u51e0\u4e4e\u8981\u547c\u5929\u62a2\u5730\u3002 \u3000\u3000\u8fd9\u4e5f\u96be\u602a\uff0c\u4ed6\u4eec\u56db\u4e2a\u7ed3\u4ea4\u591a\u5e74\uff0c\u81ed\u5473\u76f8\u6295\uff0c\u60c5\u903e\u624b\u8db3\uff0c\u4e00\u5411\u7edd\u5c11\u5206\u79bb\u8fc7\uff0c\u5982\u4eca\u4e00\u4e0b\u5b50\u6b7b \u4e86\u4e09\u4e2a\uff0c\u600e\u80fd\u4e0d\u4ee4\u4ed6\u4f24\u5fc3\u6b32\u7edd\u3002 \u3000\u3000\u53ea\u89c1\u5c71\u9b48\u6bdb\u8d8a\u8dea\u5728\u970d\u5143\u4f3d\u9762\u524d\uff0c\u4e00\u628a\u773c\u6cea\u4e00\u628a\u9f3b\u6d95\u7684\u54ed\u568e\u9053\uff1a\u201c\u76df\u4e3b\uff0c\u4f60\u8001\u4eba\u5bb6\u2014\u5b9a\u8981 \u4e3a\u5c5e\u4e0b\u7684\u4e09\u540d\u5f1f\u5144\u62a5\u4ec7\u3002\u8fd9\u51f6\u624b\u5b9e\u5728\u592a\u6b8b\u5fcd\u4e86\uff0c\u2014\u6740\u7adf\u6740\u4e86\u6211\u4eec\u56db\u4eba\u4e2d\u7684\u4e09\u4e2a\uff01\u5c5e\u4e0b\u5f1f\u5144\u8a93 \u540c\u751f\u6b7b\uff0c\u4ed6\u4eec\u4e09\u4e2a\u6b7b\u4e86\uff0c\u5c5e\u4e0b\u4e5f\u6d3b\u4e0d\u4e0b\u53bb\u4e86\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u54fc\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u8d77\u6765\uff01\u5149\u54ed\u6709\u4ec0\u4e48\u7528\uff1f\u8001\u592b\u603b\u8981\u5148\u67e5\u660e\u51f6\u624b\u662f\u8c01\uff0c\u7136\u540e\u624d\u80fd\u5c06\u4ed6 \u7ef3\u4e4b\u4ee5\u6cd5\uff0e\u201d \u3000\u3000\u641c\u9b42\u624b\u5df4\u5929\u4e49\u4e5f\u7d27\u63a5\u7740\u9053\uff1a\u201c\u4eba\u5df2\u7ecf\u6b7b\u4e86\uff0c\u6bdb\u8001\u5f1f\u7a7a\u4f24\u5fc3\u53c8\u6709\u4f55\u7528\uff1f\u76df\u4e3b\u628a\u54b1\u4eec\u53ec\u96c6\u6765 \u5546\u8bae\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u9a6c\u4e0a\u5c31\u4f1a\u7406\u4e2a\u5934\u7eea\u6765\uff0e\u4f60\u5148\u522b\u54ed\u95f9\uff0c\u5f85\u4f1a\u513f\u4e00\u5b9a\u6709\u4f60\u8bf4\u8bdd\u7684\u673a\u4f1a\u3002\u201d \u3000\u3000\u6bdb\u8d8a\u53ea\u597d\u7ad9\u8d77\u6765\uff0c\u4f46\u8fd8\u662f\u54a7\u7740\u5634\u4e0d\u4f4f\u62ed\u6cea\u3002\u4e0d\u7ba1\u5982\u4f55\uff0e\u4ed6\u7684\u8fd9\u4efd\u4e49\u6c14\uff0c\u786e\u662f\u611f\u4eba\u7684\u3002 \u3000\u3000\u62d8\u9b44\u7d22\u5b8b\u5929\u94ce\u54b3\u4e86\u58f0\u9053\uff1a\u201c\u76df\u4e3b\uff01\u4e1c\u6d77\u9662\u9644\u8fd1\uff0c\u4e00\u5411\u751a\u5c11\u4eba\u524d\u53bb\u5de1\u903b\uff0c\u4e3a\u4ec0\u4e48\u6628\u591c\u4e00\u4e0b \u5b50\u5c31\u53bb\u4e86\u56db\u4e2a\uff0e\u800c\u4e14\u8fd8\u9690\u5728\u6811\u6797\u91cc\u76d1\u89c6\uff0e\u4ed6\u4eec\u76d1\u89c6\u4ec0\u4e48\uff0c\u83ab\u975e\u662f\u4f60\u8001\u4eba\u5bb6\u4eb2\u81ea\u6d3e\u51fa\u53bb\u7684\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u9762\u5e26\u82e6\u7b11\u9053\uff1a\u201c\u8fd9\u4e8b\u8be5\u6253\u592b\u4eba\u8eab\u4e0a\u8bf4\u8d77\u3002\u201d \u3000\u3000\u5b8b\u5929\u94ce\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u8fd9\u4e8b\u600e\u4e48\u4f1a\u4e0e\u592b\u4eba\u6709\u5173\u7cfb\uff1f\u201d \u3000\u3000\u201c\u8bf4\u8d77\u6765\u4f60\u4eec\u90fd\u522b\u89c1\u7b11\uff0c\u8001\u592b\u867d\u548c\u592b\u4eba\u5df2\u6709\u4e24\u4e2a\u6708\u7684\u592b\u59bb\u4e4b\u540d\uff0c\u5374\u5230\u73b0\u5728\u5e76\u65e0\u592b\u59bb\u4e4b \u5b9e\u3002\u201d \u3000\u3000\u201c\u8fd9\u662f\u4ec0\u4e48\u539f\u56e0\uff1f\u201d \u3000\u3000\u201c\u592b\u4eba\u53d1\u8a93\u7531\u5929\u53f0\u4e07\u82b1\u5bab\u8fd4\u56de\u8ff7\u8e2a\u8c37\u540e\uff0c\u8981\u4e3a\u8c37\u524d\u76df\u4e3b\u5b88\u5b5d\u767e\u65e5\uff0c\u767e\u65e5\u4e4b\u5185\uff0c\u5fc5\u987b\u4e0e\u8001 \u592b\u5206\u623f\u3002\u201d \u3000\u3000\u201c\u90a3\u4e48\u6628\u665a\u7684\u4e8b\u2026\u201d \u3000\u3000\u201c\u4f60\u542c\u6211\u8bf4\uff0e\u8001\u592b\u524d\u665a\u8bbe\u5bb4\u62db\u5f85\u9ea6\u5c0f\u660e\uff0c\u5bb4\u7f62\u4e4b\u540e\uff0c\u56de\u623f\u4e00\u76f4\u7761\u4e0d\u7740\uff0e\u4fbf\u60c5\u4e0d\u81ea\u7981\u5230\u4e86 \u592b\u4eba\u623f\u95f4\uff0c\u51fa\u4eba\u610f\u6599\u7684\u5c45\u7136\u53d1\u73b0\u4e86\u53ef\u7591\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u53ef\u7591\uff1f\u201d \u3000\u3000\u201c\u8001\u592b\u5728\u592b\u4eba\u623f\u95e8\u5916\u65f6\uff0c\u7adf\u542c\u5230\u4e86\u5173\u7a97\u58f0\u97f3\uff0c\u800c\u8fdb\u5165\u623f\u95f4\uff0e\u5979\u5df2\u7761\u5728\u5e8a\u4e0a\uff0c\u96be\u9053\u8fd9\u8fd8\u4e0d \u53ef\u7591\uff1f\u201d \u3000\u3000\u201c\u76df\u4e3b\u5c31\u8be5\u5728\u592b\u4eba\u623f\u5185\u641c\u67e5\u641c\u67e5\uff1f\u201d \u3000\u3000\u201c\u8001\u592b\u786e\u66fe\u8fd9\u6837\u60f3\u8fc7\uff0c\u4f46\u5df2\u987e\u8651\u5230\u4e07\u2014\u67e5\u4e0d\u51fa\u53ef\u7591\uff0c\u5c82\u4e0d\u6709\u4f24\u548c\u6c14\uff0c\u82e5\u592b\u59bb\u4e4b\u95f4\u5f04\u5f97\u8c8c \u5408\u795e\u79bb\uff0e\u76f8\u4e92\u731c\u5fcc\uff0c\u5c06\u6765\u53c8\u5982\u4f55\u6069\u7231\u76f8\u5904\uff1f\u201d \u3000\u3000\u201c\u4ee5\u540e\u5462\uff1f\u201d \u3000\u3000\u201c\u8001\u592b\u5728\u592b\u4eba\u623f\u5185\u53ea\u5c0f\u5750\u4e86\u4e00\u4f1a\uff0e\u4fbf\u8d77\u8eab\u79bb\u53bb\u3002\u8c01\u77e5\u8d70\u51fa\u95e8\u540e\u4e0d\u4e45\uff0e\u53c8\u542c\u5230\u592b\u4eba\u623f\u5185\u6709 \u5f00\u7a97\u5173\u7a97\u7684\u58f0\u97f3\u3002\u201d \u3000\u3000\u201c\u76df\u4e3b\u53ef\u662f\u6000\u7591\u592b\u4eba\u623f\u5185\u85cf\u6709\u4eba\uff1f\u201d \u3000\u3000\u201c\u8001\u592b\u5f53\u7136\u6709\u8fd9\u79cd\u6000\u7591\uff0c\u4f46\u5728\u6293\u4e0d\u5230\u786e\u5b9e\u8bc1\u636e\u4e4b\u4e0b\uff0c\u8fd8\u662f\u4e0d\u4fbf\u76f4\u63a5\u5411\u592b\u4eba\u67e5\u8bc1\u3002\u201d \u3000\u3000\u5df4\u5929\u4e49\u542c\u5230\u8fd9\u91cc\uff0c\u63d2\u8a00\u9053\uff1a\u201c\u76df\u4e3b\u8bf4\u4e86\u8fd9\u4e48\u591a\uff0c\u5c5e\u4e0b\u8fd8\u542c\u4e0d\u51fa\u8fd9\u4e8b\u4e0e\u6628\u591c\u56db\u4eba\u88ab\u6740\u7684\u4e8b \u6709\u4ec0\u4e48\u5173\u7cfb\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u53cc\u988a\u62bd\u6410\u4e86\u51e0\u4e0b\u9053\uff1a\u201c\u8001\u592b\u6000\u7591\u592b\u4eba\u53ef\u80fd\u4e0e\u4ec0\u4e48\u4eba\u5e7d\u4f1a\uff0c\u800c\u8fd9\u4eba\u7b2c\u4e8c\u5929\u53c8\u7edd\u5bf9\u4e0d \u6562\u518d\u5230\u623f\u4e2d\u6765\uff0c\u8fd9\u624d\u60f3\u5230\u4e1c\u6d77\u9662\u9644\u8fd1\u7684\u90a3\u7247\u6811\u6797\u3002\u90a3\u91cc\u591c\u91cc\u6700\u5e7d\u9759\uff0c\u5de1\u903b\u7684\u4eba\u4e5f\u5f88\u5c11\u53bb\uff0c\u5982 \u679c\u5bf9\u65b9\u548c\u592b\u4eba\u6709\u7ea6\uff0c\u5341\u6709\u516b\u4e5d\u4f1a\u5728\u90a3\u7247\u6811\u6797\u76f8\u4f1a\uff0e\u201d \u3000\u3000\u5df4\u5929\u4e49\u9894\u9996\u9053\uff1a\u201c\u76df\u4e3b\u6599\u4e8b\u679c\u7136\u5468\u5230\uff0c\u5c31\u8fd9\u6837\u624d\u6d3e\u51fa\u4ed6\u4eec\u56db\u4eba\u53bb\u76d1\u89c6\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u8d77\u5148\u8001\u592b\u53ea\u6d3e\u51fa\u6731\u6b63\u4e00\u4eba\uff0c\u8981\u4ed6\u8eb2\u5728\u6811\u6797\u6697\u5904\u8d1f\u8d23\u76d1\u89c6\uff0e\u540e\u6765\u6b63\u597d\u9047\u5230\u6d77 \u767b\u3001\u5305\u901a\u3001\u82d7\u5f3a\u4e09\u4eba\u5de1\u591c\uff0c\u4fbf\u53c8\u628a\u4ed6\u4eec\u4e5f\u4e00\u8d77\u6d3e\u4e86\u53bb\uff0c\u60f3\u4e0d\u5230\u8fd9\u4e00\u6765\u7adf\u4f7f\u4ed6\u4eec\u4e09\u4eba\u5168\u6789\u9001\u4e00 \u547d\uff01\u201d \u3000\u3000\u5df4\u5929\u4e49\u77a5\u4e86\u6bdb\u8d8a\u2014\u773c\uff0c\u563f\u563f\u7b11\u9053\uff1a\u201c\u6bdb\u8001\u5f1f\u542c\u5230\u6ca1\u6709\uff0e\u4f60\u8be5\u9ad8\u5174\u624d\u5bf9\uff0c\u82e5\u6628\u665a\u4f60\u4e5f\u540c\u53bb\uff0c \u53ea\u6015\u73b0\u5728\u5c31\u6ca1\u673a\u4f1a\u7ad9\u5728\u8fd9\u91cc\u4e86\uff01\u201d \u3000\u3000\u6bdb\u8d8a\u82e6\u7b11\u9053\uff1a\u201c\u5df4\u524d\u8f88\u4f55\u5fc5\u518d\u62ff\u5c5e\u4e0b\u5f00\u5fc3\uff01\u201d \u3000\u3000\u5df4\u5929\u4e49\u76ae\u7b11\u8089\u4e0d\u7b11\u9053\uff1a\u201c\u4e0d\u7ba1\u5982\u4f55\uff0c\u6d3b\u7740\u603b\u6bd4\u6b7b\u4e86\u597d\u3002\u201d \u3000\u3000\u5b8b\u5929\u94ce\u9053\uff1a\u201c\u8fd9\u6837\u770b\u6765\uff0e\u76df\u4e3b\u53ea\u662f\u5bf9\u592b\u4eba\u6000\u7591\uff0e\u5374\u4ecd\u7136\u65e0\u6cd5\u67e5\u51fa\u51f6\u624b\u662f\u8c01\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4f4d\u4e5f\u53ef\u4ee5\u6599\u60f3\u5f97\u5230\uff0c\u51f6\u624b\u9664\u4e86\u67cf\u9f84\u9662\u7684\uff0c\u4e0d\u53ef\u80fd\u662f\u522b\u4eba\uff0e\u201d \u3000\u3000\u5b8b\u5929\u94ce\u9053\uff1a\u201c\u90a3\u5c31\u8be5\u5148\u5230\u67cf\u9f84\u9662\u53bb\u67e5\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u6447\u5934\u9053\uff1a\u201c\u8fd9\u6837\u505a\u5c82\u975e\u6253\u8349\u60ca\u86c7\uff1f\u800c\u4e14\u54b1\u4eec\u73b0\u5728\u4e5f\u5b9e\u5728\u4e0d\u80fd\u5f97\u7f6a\u67cf\u9f84\u9662\u7684\u4eba\u3002\u201d \u3000\u3000\u201c\u83ab\u975e\u76df\u4e3b\u5bf9\u4ed6\u4eec\u8fd8\u6709\u4ec0\u4e48\u987e\u5fcc\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u987e\u5fcc\u3002\u4f60\u4e0d\u89c1\u8fd9\u4e24\u4e2a\u6708\u6765\uff0c\u8001\u592b\u2014\u76f4\u5728\u8bbe\u6cd5\u7b3c\u7edc\u4ed6\u4eec\u5417\uff1f\u201d \u3000\u3000\u201c\u76df\u4e3b\u8bf7\u8bf4\u660e\u539f\u56e0\u3002\u201d \u3000\u3000\u201c\u7b2c\u4e00\uff0c\u67cf\u9f84\u9662\u7684\u5b9e\u529b\u867d\u6bd4\u4e0d\u4e0a\u54b1\u4eec\uff0c\u4f46\u5728\u8ff7\u8e2a\u8c37\u5185\uff0c\u5374\u4e5f\u4e3e\u8db3\u8f7b\u91cd\uff0c\u82e5\u53cc\u65b9\u4e00\u65e6\u95f9\u7ffb\uff0c \u540e\u679c\u5982\u4f55\uff0c\u53ef\u60f3\u800c\u77e5\u3002\u201d \u3000\u3000\u201c\u7b2c\u4e8c\u5462\uff1f\u201d \u3000\u3000\u201c\u67cf\u9f84\u9662\u7684\u4e00\u4f19\u4eba\uff0c\u90fd\u662f\u80e1\u67cf\u9f84\u548c\u8c37\u5bd2\u9999\u7684\u65e7\u90e8\uff0c\u80e1\u3001\u8c37\u4e8c\u4eba\u751f\u524d\uff0c\u6df1\u83b7\u5c11\u6797\u3001\u6b66\u5f53\u7b49 \u81ea\u8be9\u4e3a\u6b63\u5927\u95e8\u6d3e\u7684\u597d\u611f\uff0c\u6709\u4ed6\u4eec\u5728\u8ff7\u8e2a\u8c37\uff0c\u65e0\u5f62\u4e2d\u51cf\u5c11\u4e86\u54b1\u4eec\u7684\u5916\u60a3\u3002 \u3000\u3000\u82e5\u4e00\u65e6\u628a\u67cf\u9f84\u9662\u7684\u9a71\u51fa\u7eff\u6797\u603b\u5be8\uff0c\u54b1\u4eec\u5c31\u5fc5\u8d8a\u53d1\u53d7\u5230\u5c11\u6797\uff0e\u6b66\u5f53\u90a3\u4e9b\u81ea\u8be9\u6b63\u5927\u95e8\u6d3e\u7684\u654c \u89c6\uff0c\u4e07\u4e00\u5bf9\u54b1\u4eec\u91c7\u53d6\u4e86\u4e0d\u5229\u884c\u52a8\uff0c\u90a3\u54b1\u4eec\u5c31\u5c8c\u53d1\u53ef\u5371\u4e86\u3002\u8fd9\u4e5f\u5c31\u662f\u8001\u592b\u4e0d\u5f97\u4e0d\u5bf9\u67cf\u9f84\u9662\u59d4\u5c48 \u6c42\u5168\u7684\u82e6\u8877\uff0e\u201d \u3000\u3000\u970d\u5143\u4f3d\u7684\u8fd9\u4e00\u756a\u5206\u6790\uff0c\u679c\u7136\u4f7f\u5f97\u5cad\u5357\u4e8c\u5947\u5927\u4e3a\u5fc3\u670d\uff0c\u53ef\u662f\u6bdb\u8d8a\u5374\u6b7b\u6c14\u618b\u88c2\u7684\u9053\uff1a\u201c\u7167\u76df \u4e3b\u8fd9\u6837\u8bf4\uff0c\u5c5e\u4e0b\u7684\u4e09\u4f4d\u5144\u5f1f\u5c31\u542b\u51a4\u83ab\u8fa9\u4e86\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u5b89\u6170\u9053\uff1a\u201c\u4f60\u522b\u6025\u3002\u9a6c\u4e0a\u5c31\u6709\u4eba\u6765\uff0c\u8001\u592b\u5b9a\u80fd\u4ece\u4ed6\u4eec\u8eab\u4e0a\u67e5\u51fa\u6d77\u767b\u3001\u5305\u901a\uff0e\u82d7\u5f3a \u548c\u6731\u6b63\u7684\u6b7b\u56e0\u3002\u201d \u3000\u3000\u5df4\u5929\u4e49\u9053\uff1a\u201c\u76df\u4e3b\u8981\u628a\u4ec0\u4e48\u4eba\u558a\u6765\u67e5\u8bc1\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u8001\u592b\u4e00\u5171\u901a\u77e5\u4e86\u56db\u4e2a\u4eba\u6765\uff0c\u4e00\u4e2a\u8bb8\u4ed9\u5b50\uff0c\u2014\u4e2a\u662f\u88ab\u9609\u6389\u5bb6\u4f19\u7684\u738b\u4e09\uff0c\u53e6\u4e24 \u4e2a\u662f\u4e1c\u6d77\u9662\u6628\u591c\u7684\u4e24\u540d\u5b88\u95e8\u8b66\u536b\u4fde\u4e09\u548c\u5c24\u6d51\u3002\u201d \u3000\u3000\u5df4\u5929\u4e49\u9053\uff1a\u201c\u8fd9\u5012\u662f\u4e2a\u597d\u529e\u6cd5\u3002\u201d \u3000\u3000\u8bf4\u8bdd\u95f4\uff0e\u4e8e\u4e09\u3001\u5c24\u6d51\u5df2\u7ecf\u7c07\u62e5\u7740\u8bb8\u5c0f\u65e6\u6765\u5230\u5bc6\u5ba4\u3002 \u3000\u3000\u970d\u5143\u4f3d\u5bf9\u8bb8\u5c0f\u65e6\u8fd8\u662f\u8868\u73b0\u5f97\u65e2\u4eb2\u5207\u53c8\u793c\u9047\uff0c\u8fde\u5fd9\u4eb2\u81ea\u642c\u4e86\u4e00\u628a\u6905\u5b50\u7b11\u8138\u76f8\u8fce\u9053\uff1a\u201c\u4ed9\u5b50 \u8bf7\u5750\uff01\u201d \u3000\u3000\u8bb8\u5c0f\u65e6\u4f9d\u7136\u8eab\u62ab\u2014\u88ad\u9ed1\u7eb1\uff0e\u8138\u4e0a\u6beb\u65e0\u8868\u60c5\uff0c\u8fde\u53e5\u8bdd\u90fd\u6ca1\u8bf4\u4fbf\u5f84\u81ea\u5750\u4e0b\u3002 \u3000\u3000\u738b\u4e09\u3001\u4fde\u4e09\u548c\u5c24\u6d51\u5219\u884c\u8fc7\u793c\u540e\uff0e\u6bd5\u606d\u6bd5\u656c\u7684\u7ad9\u7acb\u4e00\u65c1\u3002 \u3000\u3000\u970d\u5143\u4f3d\u559d\u4e86\u53e3\u8336\uff0c\u5148\u95ee\u4fde\u4e09\u548c\u5c24\u6d51\u4e8c\u4eba\u9053\uff1a\u201c\u6628\u665a\u53ef\u662f\u4f60\u4eec\u4e24\u4eba\u8d1f\u8d23\u4e1c\u6d77\u9662\u7684\u95e8\u524d\u8b66 \u536b\uff1f\u201d \u3000\u3000\u4e24\u4eba\u540c\u65f6\u5e94\u58f0\u9053\uff1a\u201c\u662f\u201d \u3000\u3000\u970d\u5143\u4f3d\u518d\u95ee\u9053\uff1a\u201c\u54ea\u4e2a\u4e0a\u534a\u591c\uff0c\u54ea\u4e2a\u4e0b\u534a\u591c\uff1f\u201d \u3000\u3000\u4fde\u4e09\u9053\uff1a\u201c\u5c0f\u7684\u4e0a\u534a\u591c\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u5982\u679c\u8001\u592b\u7684\u5224\u65ad\u4e0d\u5dee\uff0c\u4e8b\u60c5\u4e00\u5b9a\u53d1\u751f\u5728\u4e0a\u534a\u591c\u3002\u4fde\u4e09\uff0c\u4f60\u8f6e\u503c\u4e0a\u534a\u591c\uff0c\u8d23 \u4efb\u8be5\u5728\u4f60\u8eab\u4e0a\uff0e\u201d \u3000\u3000\u4fde\u4e09\u731b\u6253\u4e00\u54c6\u55e6\u9053\uff1a\u201c\u7980\u76df\u4e3b\uff0c\u5c0f\u7684\u53ea\u8d1f\u8d23\u8fdb\u51fa\u5927\u95e8\u7684\u76d8\u67e5\uff0e\u7ba1\u4e0d\u5230\u6811\u6797\u91cc\u7684\u90a3\u4e00\u6bb5\uff0e\u201d \u3000\u3000\u970d\u5143\u4f3d\u559d\u9053\uff1a\u201c\u6df7\u86cb\uff0c\u80e1\u8bf4\uff01\u96be\u9053\u4e1c\u6d77\u9662\u9644\u8fd1\u53d1\u751f\u4e86\u4e8b\u6545\uff0c\u4f60\u80fd\u4e0d\u7ba1\uff1f\u201d \u3000\u3000\u4fde\u4e09\u55eb\u5685\u7740\u9053\uff1a\u201c\u6811\u6797\u662f\u5728\u4e1c\u6d77\u9662\u7684\u540e\u9762\uff0c\u5c0f\u7684\u5b88\u5728\u524d\u95e8\uff0c\u600e\u80fd\u770b\u5230\u4e1c\u6d77\u9662\u7684\u540e\u9762\u53bb\uff1f\u201d \u3000\u3000\u201c\u8d8a\u53d1\u6df7\u86cb\uff01\u4f60\u7684\u773c\u775b\u770b\u4e0d\u89c1\uff0c\u96be\u9053\u8033\u6735\u4e5f\u542c\u4e0d\u89c1\uff1f\u201d \u3000\u3000\u201c\u5c0f\u7684\u867d\u7136\u542c\u5230\u540e\u9762\u6709\u6253\u6597\u58f0\u97f3\uff0c\u4f46\u56e0\u770b\u5b88\u524d\u95e8\u662f\u804c\u8d23\uff0c\u600e\u6562\u64c5\u79bb\u804c\u5b88\uff1f\u800c\u4e14\u5c0f\u7684\u6b66\u529f \u5e73\u5e38\uff0c\u4e5f\u5e2e\u4e0d\u4e0a\u5fd9\u53bb\uff0e\u201d \u3000\u3000\u201c\u90a3\u4e48\u4eca\u5929\u4e00\u65e9\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u5411\u8001\u592b\u6216\u5df4\u5934\u9886\u5b8b\u5934\u9886\u7980\u62a5\uff1f\u201d \u3000\u3000\u201c\u5c0f\u7684\u53ea\u4ee5\u4e3a\u4ed6\u4eec\u6253\u8fc7\u6ca1\u4e8b\u4e86\uff0c\u66f4\u4f55\u51b5\u5c0f\u7684\u4e5f\u4e0d\u6562\u76f4\u63a5\u5411\u76df\u4e3b\u548c\u5df4\u3001\u5b8b\u4e24\u4f4d\u5934\u9886\u7980\u62a5\u3002\u201d \u3000\u3000\u201c\u96be\u9053\u8fd9\u6837\u5c31\u7b97\u4e86\uff1f\u201d \u3000\u3000\u201c\u5c0f\u7684\u4eca\u5929\u4e00\u65e9\u5c31\u62a5\u544a\u8fc7\u9ad8\u55bd\u957f\uff0c\u542c\u8bf4\u4f60\u8001\u4eba\u5bb6\u4e5f\u662f\u63a5\u5230\u9ad8\u55bd\u957f\u7684\u7980\u62a5\u624d\u77e5\u9053\u8fd9\u4ef6\u4e8b \u7684\u3002\u201d \u3000\u3000\u5df4\u5929\u4e49\u62a2\u7740\u9053\uff1a\u201c\u5982\u679c\u662f\u8fd9\u6837\uff0c\u5c31\u6ca1\u6709\u4fde\u4e09\u548c\u5c24\u6d51\u7684\u4e8b\u4e86\uff0c\u76df\u4e3b\u653e\u4ed6\u4eec\u4e24\u4eba\u6682\u65f6\u56de\u53bb \u5427\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u53ea\u597d\u6325\u6325\u624b\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4e2a\u7ed9\u6211\u6eda\uff01\u201d \u3000\u3000\u4fde\u4e09\u548c\u5c24\u6d51\u62b1\u5934\u9f20\u7a9c\u800c\u53bb\u3002 \u3000\u3000\u970d\u5143\u4f3d\u518d\u95ee\u738b\u4e09\u9053\uff1a\u201c\u6628\u665a\u7684\u4e8b\uff0c\u4f60\u8be5\u77e5\u9053\u4e86\uff1f\u201d \u3000\u3000\u738b\u4e09\u5012\u5e76\u4e0d\u5bb3\u6015\uff0c\u6447\u4e86\u6447\u5934\u9053\uff1a\u201c\u5c0f\u7684\u53ea\u7ba1\u4e1c\u6d77\u9662\u5185\u90e8\u7684\u4e8b\uff0c\u8fde\u5927\u95e8\u5916\u7684\u4eba\u90fd\u4e0d\u6e05\u695a\uff0c \u5c0f\u7684\u53c8\u600e\u4f1a\u6e05\u695a\u5462\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u4f60\u4e0d\u59a8\u5230\u5916\u9762\u770b\u770b\uff01\u201d \u3000\u3000\u738b\u4e09\u9053\uff1a\u201c\u5c0f\u7684\u88ab\u76df\u4e3b\u9609\u53bb\u4e86\u5bb6\u4f19\uff0e\u5c31\u7b49\u4e8e\u5bab\u91cc\u7684\u592a\u76d1\uff0c\u4e1c\u6d77\u9662\u5c31\u7b49\u4e8e\u7687\u5bab\uff0c\u592a\u76d1\u79c1\u81ea \u51fa\u5bab\u662f\u72af\u6cd5\u7684\uff0e\u5c0f\u7684\u600e\u6562\u968f\u4fbf\u79bb\u5f00\u4e86\u4e1c\u6d77\u9662\uff1f\u66f4\u4f55\u51b5\u2026\u2026\u201d \u3000\u3000\u970d\u5143\u4f3d\u53f1\u9053\uff1a\u201c\u4f60\u8fd8\u6709\u4ec0\u4e48\u4f55\u51b5\u4e0d\u4f55\u51b5\uff1f\u201d \u3000\u3000\u738b\u4e09\u9053\uff1a\u201c\u5c0f\u7684\u662f\u8bf4\u90a3\u73a9\u610f\u513f\u4ece\u88ab\u9609\u53bb\u5230\u73b0\u5728\uff0c\u4f24\u52bf\u8fd8\u6ca1\u5168\u597d\u3002\u5c31\u662f\u53ef\u4ee5\u79bb\u5f00\u4e1c\u6d77\u9662\uff0e \u56e0\u884c\u52a8\u4e0d\u4fbf\uff0c\u4e5f\u4e0d\u53ef\u80fd\u8dd1\u5230\u6811\u6797\u91cc\u53bb\u5bdf\u770b\u3002\u201d \u3000\u3000\u201c\u90a3\u4e48\u4f60\u5728\u4e1c\u6d77\u9662\u4e2d\u8dd1\u4e0a\u8dd1\u4e0b\u670d\u4f8d\u8bb8\u4ed9\u5b50\uff0c\u662f\u600e\u4e48\u884c\u52a8\u7684\uff1f\u201d \u3000\u3000\u201c\u8bb8\u4ed9\u5b50\u7b49\u4e8e\u7687\u5bab\u91cc\u7684\u5a18\u5a18\uff0c\u5c0f\u7684\u4e0d\u6562\u4e0d\u670d\u4f8d\uff0c\u53ea\u597d\u54ac\u7259\u5fcd\u75db\u8dd1\u4e0a\u8dd1\u4e0b\uff0c\u81f3\u4e8e\u5916\u9762\u7684\u4e8b\uff0c \u5c0f\u7684\u5f53\u7136\u4e0d\u60f3\u518d\u54ac\u7259\u5fcd\u75db\u3002\u201d \u3000\u3000\u201c\u6628\u665a\u53ef\u66fe\u6709\u5916\u4eba\u8fdb\u5165\u4e1c\u6d77\u9662\uff1f\u201d \u3000\u3000\u201c\u4f60\u8001\u4eba\u5bb6\u5df2\u7ecf\u4e0d\u518d\u53d1\u51fa\u6170\u52b3\u4ee4\u724c\uff0c\u600e\u53ef\u80fd\u8fd8\u6709\u5916\u4eba\u8fdb\u5165\u4e1c\u6d77\u9662\uff1f\u201d \u3000\u3000\u738b\u4e09\u8bb2\u5f97\u5934\u5934\u662f\u9053\uff0c\u970d\u5143\u4f3d\u867d\u7136\u542c\u5f97\u6ee1\u809a\u5b50\u706b\u6c14\uff0c\u5374\u4e5f\u62ff\u4ed6\u6ca1\u529e\u6cd5\uff0c\u6b64\u4eba\u8fde\u5bb6\u4f19\u90fd\u88ab\u9609 \u6389\uff0e\u727a\u7272\u5df2\u7ecf\u591f\u5927\u5566\uff0e\u970d\u5143\u4f3d\u4e0d\u80fd\u4e0d\u5bf9\u4ed6\u5ba2\u6c14\u4e9b\u3002 \u5367\u9f99\u751f\u300a\u5999\u7edd\u5929\u9999\u300b \u7b2c\u4e8c\u5341\u56de\u3000\u591a\u884c\u4e0d\u4e49 \u3000\u3000\u53ea\u542c\u8bb8\u5c0f\u65e6\u9053\uff1a\u201c\u970d\u76df\u4e3b\uff0c\u4e3a\u4ec0\u4e48\u8fd8\u4e0d\u95ee\u6211\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u4f60\u4e0e\u4ed6\u4eec\u8eab\u4efd\u4e0d\u540c\uff0c\u5e94\u8be5\u6700\u540e\u95ee\uff0c\u73b0\u5728\u5c31\u8f6e\u5230\u4f60\u4e86\u3002\u201d \u3000\u3000\u8bb8\u5c0f\u65e6\u51b7\u7b11\u9053\uff1a\u201c\u90a3\u5c31\u8bf7\u4f60\u5148\u8ba9\u738b\u4e09\u56de\u53bb\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u8981\u738b\u4e09\u56de\u53bb\uff1f\u201d \u3000\u3000\u201c\u5f85\u4f1a\u513f\u6211\u8fd8\u6709\u53e6\u5916\u7684\u8bdd\u8bb2\uff0e\u738b\u4e09\u7559\u5728\u8fd9\u91cc\u4e0d\u65b9\u4fbf\u3002\u201d \u3000\u3000\u738b\u4e09\u4e00\u542c\uff0c\u6b63\u597d\u53ef\u4ee5\u8d81\u673a\u8d70\u5f00\uff0c\u5fd9\u5411\u970d\u5143\u4f3d\u65bd\u4e00\u793c\u9053\uff1a\u201c\u5c0f\u7684\u8fd9\u5c31\u9075\u547d\u56de\u53bb\u3002\u201d \u3000\u3000\u738b\u4e09\u8d70\u540e\uff0c\u970d\u5143\u4f3d\u9053\uff1a\u201c\u6628\u665a\u53d1\u751f\u4e8b\u60c5\u7684\u90a3\u7247\u6811\u6797\uff0c\u6b63\u5bf9\u8bb8\u4ed9\u5b50\u7684\u540e\u7a97\uff0c\u4ed9\u5b50\u5e94\u8be5\u542c\u5230 \u58f0\u97f3\uff0e\u7531\u540e\u7a97\u5411\u5916\u770b\uff0e\u5c45\u9ad8\u4e34\u4e0b\uff0c\u4e00\u5b9a\u770b\u5f97\u5f88\u6e05\u695a\uff0e\u5c31\u8bf7\u4ed9\u5b50\u628a\u5f53\u65f6\u7684\u60c5\u5f62\u8bf4\u8bf4\u3002\u201d \u3000\u3000\u8bb8\u5c0f\u65e6\u9053\uff1a\u201c\u6211\u5f53\u7136\u542c\u5230\u540e\u7a97\u5916\u6811\u6797\u91cc\u6709\u6253\u6597\u7684\u58f0\u97f3\u3002\u201d \u3000\u3000\u201c\u90a3\u4e00\u5b9a\u770b\u6e05\u695a\u4e86\uff1f\u201d \u3000\u3000\u201c\u53ef\u60dc\u6211\u623f\u91cc\u70b9\u7740\u706f\uff0c\u7531\u660e\u5904\u5f80\u6697\u5904\u770b\uff0c\u53c8\u600e\u80fd\u770b\u5f97\u89c1\u4ec0\u4e48\u5462\uff1f\u201d \u3000\u3000\u201c\u4ed6\u4eec\u6253\u6597\u524d\u540e\uff0e\u5fc5\u5b9a\u8bf4\u8fc7\u8bdd\uff0c\u4ed6\u4eec\u8bf4\u4e86\u4e9b\u4ec0\u4e48\uff0c\u4f60\u603b\u8be5\u542c\u5230\u4e86\uff1f\u201d \u3000\u3000\u201c\u6211\u6839\u672c\u8fa8\u8ba4\u4e0d\u6e05\u4ed6\u4eec\u8bf4\u8bdd\u7684\u58f0\u97f3\uff0e\u53c8\u600e\u77e5\u8bdd\u662f\u8c01\u8bf4\u7684\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u9893\u71c3\u5401\u53e3\u6c14\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u628a\u4f60\u4eec\u51e0\u4eba\u627e\u6765\uff0c\u4e5f\u662f\u7406\u4e0d\u51fa\u5934\u7eea\uff0e\u4ed9\u5b50\u53ef\u4ee5\u56de\u53bb\u4e86\uff0e\u201d \u3000\u3000\u8bb8\u5c0f\u65e6\u54fc\u4e86\u58f0\u9053\uff1a\u201c\u522b\u5fd8\u8bb0\u6211\u8fd8\u6709\u8bdd\u8981\u5bf9\u4f60\u8bb2\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u832b\u7136\u9053\uff1a\u201c\u4ed9\u5b50\u6709\u4ec0\u4e48\u8bdd\u5bf9\u8001\u592b\u8bb2\u5462\uff1f\u201d \u3000\u3000\u8c01\u77e5\u8bb8\u5c0f\u65e6\u5374\u53c8\u77a5\u4e86\u5cad\u5357\u4e8c\u5947\u548c\u6bdb\u8d8a\u4e00\u773c\u9053\uff1a\u201c\u4ed6\u4eec\u4e09\u4f4d\u4e5f\u8981\u51fa\u53bb\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u4ed6\u4eec\u4e09\u4f4d\u548c\u8001\u592b\u540c\u7518\u5171\u82e6\u591a\u5e74\uff0c\u8001\u592b\u2014\u76f4\u628a\u4ed6\u4eec\u89c6\u4e3a\u5de6\u53f3\u624b\uff0e\u4f60\u6709\u4ec0\u4e48\u8bdd \u53ea\u7ba1\u8bb2\uff01\u201d \u3000\u3000\u8bb8\u5c0f\u65e6\u6447\u5934\u9053\uff1a\u201c\u4e0d\uff01\u8fd9\u8bdd\u53ea\u80fd\u8ba9\u4f60\u4e00\u4eba\u542c\u5230\uff0c\u6211\u662f\u4e3a\u4f60\u7740\u60f3\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u53ea\u597d\u6b49\u7136\u4e00\u7b11\u9053\uff1a\u201c\u62b1\u6b49\uff01\u4e09\u4f4d\u5c31\u8bf7\u6682\u65f6\u5230\u5916\u9762\u5ba2\u5385\u5750\u5750\uff0c\u5f85\u4f1a\u513f\u8fdb\u6765\u3002\u201d \u3000\u3000\u5cad\u5357\u4e8c\u5947\u548c\u6bdb\u8d8a\u53ea\u597d\u8d77\u8eab\u544a\u8f9e\uff0e \u3000\u3000\u970d\u5143\u4f3d\u50ac\u4fc3\u9053\uff1a\u201c\u73b0\u5728\u603b\u8be5\u53ef\u4ee5\u8bb2\u4e86\u5427\uff01\u201d \u3000\u3000\u8bb8\u5c0f\u65e6\u84e6\u5730\u628a\u8138\u8272\u4e00\u6c89\u9053\uff1a\u201c\u970d\u76df\u4e3b\uff0c\u4f60\u5c31\u8fd9\u6837\u5bf9\u5f85\u6211\u5417\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u4e24\u773c\u7728\u4e86\u51e0\u7728\u9053\uff1a\u201c\u4ed9\u5b50\u597d\u50cf\u662f\u5728\u5bf9\u8001\u592b\u53d1\u7765\u6c14\uff0c\u8001\u592b\u662f\u4ec0\u4e48\u5730\u65b9\u5bf9\u4e0d\u4f4f\u4f60\uff1f\u201d \u3000\u3000\u8bb8\u5c0f\u65e6\u51b7\u7b11\u9053\uff1a\u201c\u8fd9\u4e9b\u5929\u6765\uff0c\u4f60\u4e00\u5207\u7684\u4e00\u5207\u90fd\u5bf9\u4e0d\u4f4f\u6211\uff0c\u4e0d\u4fe1\u4f60\u5fc3\u91cc\u4e0d\u660e\u767d\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u4e24\u624b\u4e00\u644a\u9053\uff1a\u201c\u4f60\u4e0d\u8bf4\u6211\u600e\u80fd\u660e\u767d\uff1f\u201d \u3000\u3000\u8bb8\u5c0f\u65e6\u72e0\u58f0\u9053\uff1a\u201c\u90a3\u6211\u5c31\u8bf4\uff0c\u4f60\u628a\u6211\u653e\u5728\u4e1c\u6d77\u9662\u91cc\u662f\u4ec0\u4e48\u7528\u610f\uff1f\u201d \u3000\u3000\u201c\u8fd9\u662f\u54b1\u4eec\u4e8b\u5148\u8bb2\u597d\u7ecf\u8fc7\u4f60\u540c\u610f\u7684\u3002\u201d \u3000\u3000\u201c\u6211\u540c\u610f\u8fc7\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u4f60\u81ea\u613f\u5728\u90a3\u91cc\u6170\u52b3\u672c\u8c37\u7684\u82f1\u96c4\u597d\u6c49\uff0c\u4e00\u665a\u4e0a\u4e00\u4e2a\uff0e\u201d \u3000\u3000\u201c\u4e0d\u9519\uff01\u662f\u6211\u81ea\u613f\u7684\u3002\u4e0d\u8fc7\u4f60\u624b\u4e0b\u7684\u82f1\u96c4\u597d\u6c49\u53bb\u8fc7\u6ca1\u6709\uff1f\u201d \u3000\u3000\u201c\u8001\u592b\u5df2\u7ecf\u53d1\u51fa\u56db\u6b21\u6170\u52b3\u4ee4\u724c\u3002\u201d \u3000\u3000\u201c\u4e5f\u4e0d\u9519\uff0c\u524d\u4e24\u540d\u6839\u672c\u8fde\u4eba\u5f71\u6ca1\u89c1\uff0e\u540e\u4e24\u540d\u4eba\u867d\u5230\uff0c\u5374\u4e0d\u5982\u4e0d\u5230\uff0c\u4f60\u8fd8\u6709\u4ec0\u4e48\u8bdd\u8bf4\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u5f53\u771f\u88ab\u95ee\u4f4f\u4e86\uff0c\u534a\u664c\u7b54\u4e0d\u4e0a\u8bdd\uff0e \u3000\u3000\u8bb8\u5c0f\u65e6\u8038\u7740\u67f3\u7709\uff0c\u6ee1\u9762\u51b0\u971c\uff0c\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u4f60\u8ba9\u672c\u4ed9\u5b50\u72ec\u5b88\u7a7a\u5e10\u597d\u51e0\u5929\u4e0d\u8bf4\uff0c\u73b0\u5728\u5c45\u7136 \u8fde\u6170\u52b3\u4ee4\u724c\u90fd\u4e0d\u53d1\uff0c\u5bb3\u5f97\u672c\u4ed9\u5b50\u5929\u5929\u665a\u4e0a\u7761\u4e0d\u7740\u89c9\uff0c\u7a76\u7adf\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u966a\u7b11\u9053\uff1a\u201c\u8ba9\u4ed9\u5b50\u591a\u4f11\u606f\u4f11\u606f\uff0c\u4e0d\u662f\u5f88\u597d\u5417\uff1f\u201d \u3000\u3000\u8bb8\u5c0f\u65e6\u5550\u9053\uff1b\u201c\u53bb\u4f60\u7684\uff01\u5982\u679c\u4e00\u4e2a\u4eba\u5929\u5929\u4e0d\u5403\u996d\u6210\u4e0d\u6210\uff1f\u672c\u4ed9\u5b50\u662f\u4e2a\u6709\u7528\u7684\u4eba\uff0c\u4e3a\u4ec0\u4e48 \u95f2\u7740\u4e0d\u7528\uff1f\u4e0d\u7528\u5c31\u662f\u6d6a\u8d39\uff0e\u4f60\u61c2\u4e0d\u61c2\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u5c34\u5c2c\u7b11\u9053\uff1a\u201c\u8001\u592b\u61c2\uff0e\u4e0d\u8fc7\u8001\u592b\u771f\u6ca1\u60f3\u5230\u4ed9\u5b50\u8fd9\u6837\u9700\u8981\u7537\u4eba\u3002\u201d \u3000\u3000\u8bb8\u5c0f\u65e6\u9053\uff1a\u201c\u672c\u4ed9\u5b50\u548c\u522b\u7684\u5973\u4eba\u4e0d\u540c\uff0c\u6bcf\u5929\u665a\u4e0a\u90fd\u4e0d\u80fd\u79bb\u5f00\u7537\u4eba\uff0c\u6709\u4ef6\u4e8b\u8bf4\u51fa\u6765\u80fd\u628a\u4f60 \u5413\u6b7b\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u8001\u592b\u8eab\u7ecf\u767e\u6218\uff0c\u4ec0\u4e48\u5927\u98ce\u5927\u6d6a\u90fd\u89c1\u8fc7\uff0e\u96be\u9053\u4f1a\u88ab\u4e00\u53e5\u8bdd\u5413\u6b7b\uff1f\u201d \u3000\u3000\u8bb8\u5c0f\u65e6\u9053\uff1b\u201c\u90a3\u6211\u5c31\u8bf4\u4e86\uff0c\u672c\u4ed9\u5b50\u6709\u8fc7\u4e00\u665a\u4e0a\u627e\u4e09\u5341\u4e2a\u7537\u4eba\u966a\u4f34\u7684\u8bb0\u5f55\uff0e\u4f60\u4fe1\u4e0d\u4fe1\uff1f\u201d \u3000\u3000\u971c\u5143\u4f3d\u731b\u5410\u4e00\u4e0b\u820c\u5934\uff0c\u54a7\u4e86\u54a7\u5634\u9053\uff1a\u201c\u4ed9\u5b50\u548c\u51e1\u4eba\u4e0d\u540c\uff0c\u8001\u592b\u4e0d\u4fe1\u4e5f\u5f97\u4fe1\uff01\u201d \u3000\u3000\u8bb8\u5c0f\u65e6\u9f3b\u7fc5\u4e00\u6380\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u4ee5\u540e\u6253\u7b97\u628a\u6211\u600e\u4e48\u529e\uff1f\u5feb\u8bf4\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u76b1\u4e86\u76b1\u7709\uff0c\u7565\u4e00\u72b9\u8c6b\u9053\uff1a\u201c\u4f60\u5148\u56de\u53bb\uff0c\u8001\u592b\u4e00\u5b9a\u4f1a\u7ed9\u4f60\u4e2a\u5706\u6ee1\u4ea4\u5f85\u3002\u201d \u3000\u3000\u8bb8\u5c0f\u65e6\u603b\u7b97\u6ca1\u518d\u7ee7\u7eed\u7ea0\u7f20\uff0c\u60bb\u60bb\u7136\u51fa\u623f\u800c\u53bb\uff0e \u3000\u3000\u5cad\u5357\u4e8c\u5947\u548c\u6bdb\u8d8a\u5f88\u5feb\u53c8\u8fdb\u5165\u5bc6\u5ba4\u3002 \u3000\u3000\u5df4\u5929\u4e49\u597d\u5947\u7684\u95ee\u9053\uff1a\u201c\u8bb8\u4ed9\u5b50\u7a76\u7adf\u6709\u4ec0\u4e48\u91cd\u8981\u7684\u4e8b\u548c\u76df\u4e3b\u8c08\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u5e26\u70b9\u65e0\u53ef\u5948\u4f55\u7684\u610f\u5473\u9053\uff1a\u201c\u8fd9\u5973\u4eba\u771f\u4e0d\u597d\u4f3a\u5019\uff0c\u54b1\u4eec\u73b0\u5728\u522b\u518d\u8c08\u5979\uff0e\u201d \u3000\u3000\u5df4\u5929\u4e49\u5728\u4fa7\u5ea7\u5750\u4e0b\u9053\uff1a\u201c\u5176\u5b9e\u76df\u4e3b\u7528\u4e0d\u7740\u67e5\uff0c\u6628\u665a\u7684\u4e8b\uff0c\u7edd\u5bf9\u662f\u67cf\u9f84\u9662\u7684\u4eba\u5e72\u7684\u3002\u5982\u679c \u6709\u9519\uff0c\u5c5e\u4e0b\u60c5\u613f\u628a\u8111\u888b\u8d4c\u4e0a\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u8001\u592b\u53c8\u4f55\u5c1d\u4e0d\u77e5\u9053\u662f\u4ed6\u4eec\u5e72\u7684\uff0e\u4f46\u5374\u603b\u8be5\u5148\u67e5\u51fa\u51f6\u624b\u662f\u8c01\uff1f\u201d \u3000\u3000\u5df4\u5929\u4e49\u563f\u563f\u7b11\u9053\uff1a\u201c\u4e0d\u7528\u67e5\u4e86\uff0c\u4ed6\u4eec\u4eba\u4eba\u90fd\u53ef\u80fd\u662f\u51f6\u624b\uff0c\u4eba\u4eba\u90fd\u662f\u548c\u54b1\u4eec\u4f5c\u5bf9\u7684\u3002\u76df\u4e3b \u6700\u591a\u53ea\u80fd\u5236\u4f4f\u4ed6\u4eec\u7684\u4eba\uff0c\u5374\u6c38\u8fdc\u4e0d\u80fd\u5236\u4f4f\u4ed6\u4eec\u7684\u5fc3\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u54e6\u4e86\u58f0\u8ffd\uff1a\u201c\u5df4\u8001\u5f1f\u8fd9\u8bdd\u53ef\u662f\u5bf9\u8001\u592b\u6709\u4ec0\u4e48\u6697\u793a\uff1f\u201d \u3000\u3000\u5df4\u5929\u4e49\u68ee\u68ee\u4e00\u7b11\u9053\uff1a\u201c\u4f9d\u5c5e\u4e0b\u4e4b\u610f\uff0c\u4e3a\u4e86\u4f7f\u8ff7\u8e2a\u8c37\u4e0d\u518d\u8d77\u98ce\u6ce2\uff0e\u514d\u9664\u540e\u987e\u4e4b\u5fe7\uff0c\u6700\u597d\u7684 \u529e\u6cd5\uff0c\u4fbf\u662f\u628a\u67cf\u9f84\u9662\u7684\u4eba\u7edf\u7edf\u9664\u53bb\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u6447\u5934\u9053\uff1a\u201c\u8001\u592b\u5148\u524d\u4e0d\u662f\u8bf4\u8fc7\u5417\uff1f\u5176\u4e2d\u6709\u4e24\u9879\u987e\u5fcc\u3002\u201d \u3000\u3000\u5df4\u5929\u4e49\u9053\uff1a\u201c\u76df\u4e3b\u6240\u4ee5\u6709\u6240\u987e\u5fcc\uff0c\u4e0d\u8fc7\u662f\u62c5\u5fc3\u4e0d\u80fd\u628a\u4ed6\u4eec\u4e00\u7f51\u6253\u5c3d\uff0c\u5982\u679c\u80fd\u628a\u4ed6\u4eec\u4e00\u7f51 \u6253\u5c3d\uff0c\u8fd8\u6709\u4ec0\u4e48\u597d\u987e\u5fcc\u7684\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u67cf\u9f84\u9662\u4e2a\u4e2a\u6b66\u529f\u9ad8\u5f3a\uff0e\u54b1\u4eec\u7684\u529b\u91cf\u867d\u7136\u80dc\u8fc7\u4ed6\u4eec\uff0c\u4f46\u60f3\u628a\u4ed6\u4eec\u4e00\u7f51\u6253\u5c3d\uff0e \u5374\u662f\u4e07\u4e07\u529e\u4e0d\u5230\u7684\u3002\u201d \u3000\u3000\u201c\u8fd9\u65b9\u9762\u7528\u4e0d\u7740\u76df\u4e3b\u64cd\u5fc3\uff0c\u5c5e\u4e0b\u81ea\u6709\u529e\u6cd5\u5bf9\u4ed8\u4ed6\u4eec\u3002\u201d \u3000\u3000\u201c\u4f60\u6709\u4ec0\u4e48\u529e\u6cd5\uff1f\u201d \u3000\u3000\u201c\u8fd9\u529e\u6cd5\u5175\u4e0d\u8840\u5203\uff0c\u6765\u4e2a\u706b\u711a\u67cf\u9f84\u9662\u3002\u5c31\u5728\u4eca\u665a\u5f85\u4ed6\u4eec\u719f\u7761\u4e4b\u65f6\uff0c\u5728\u67cf\u9f84\u9662\u56db\u5468\u5e03\u4e0b\u786b \u78f7\u706b\u836f\uff0c\u7eb5\u7136\u4ed6\u4eec\u8f7b\u529f\u518d\u597d\uff0c\u5728\u56db\u5468\u706b\u8d77\u4e4b\u4e0b\uff0c\u4ed3\u60f6\u4e2d\u4e5f\u7edd\u96be\u9003\u751f\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u67cf\u9f84\u9662\u95e8\u5916\u8b66\u536b\u662f\u4ed6\u4eec\u7684\u4eba\u3002\u201d \u3000\u3000\u201c\u5f53\u7136\u8981\u5148\u628a\u8b66\u536b\u9664\u53bb\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u6c89\u541f\u4e86\u4e00\u9635\u9053\uff1a\u201c\u8fd9\u662f\u4ef6\u5927\u4e8b\uff0e\u6210\u4e0e\u4e0d\u6210\uff0c\u5728\u6b64\u4e00\u4e3e\uff0c\u5fc5\u987b\u6709\u4e07\u5168\u628a\u63e1\uff0c\u624d\u53ef\u5c55 \u5f00\u884c\u52a8\u3002\u201d \u3000\u3000\u5df4\u5929\u4e49\u9053\uff1a\u201c\u76df\u4e3b\u653e\u5fc3\uff0c\u8fd9\u4e8b\u5305\u5728\u5c5e\u4e0b\u4e0e\u5b8b\u8001\u5f1f\u4e24\u4eba\u8eab\u4e0a\uff0c\u7edd\u5bf9\u4e07\u65e0\u4e00\u5931\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u53c8\u6c89\u541f\u4e86\u534a\u664c\u9053\uff1a\u201c\u53ea\u662f\u8ff7\u8e2a\u8c37\u53bb\u4e86\u67cf\u9f84\u9662\u7684\u4eba\uff0c\u5b9e\u529b\u5fc5\u5b9a\u5927\u51cf\u3002\u201d \u3000\u3000\u5df4\u5929\u4e49\u54a7\u5634\u4e00\u7b11\u9053\uff1a\u201c\u5c5e\u4e0b\u8fd8\u6ca1\u7980\u62a5\u76df\u4e3b\uff0c\u9a6c\u4e0a\u5c31\u6709\u751f\u529b\u519b\u5230\u4e86\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u559c\u9053\uff1a\u201c\u54ea\u91cc\u6765\u7684\u751f\u529b\u519b\uff1f\u201d \u3000\u3000\u5df4\u5929\u4e49\u9053\uff1a\u201c\u5d06\u5cd2\u4e94\u4e49\uff0e\u5927\u540d\u9f0e\u9f0e\uff0c\u4e0d\u5fc5\u5c5e\u4e0b\u591a\u8bf4\uff0c\u76df\u4e3b\u5fc5\u662f\u65e9\u5df2\u77e5\u9053\u4e86\u3002\u201d \u3000\u3000\u201c\u4f60\u8bf4\u7684\u53ef\u662f\u5d06\u5cd2\u4e94\u9b54\uff1f\u201d \u3000\u3000\u201c\u4e0d\u7ba1\u4e94\u4e49\u4e94\u9b54\uff0c\u53cd\u6b63\u4ed6\u4eec\u6765\u5230\u8fd9\u91cc\uff0e\u5c31\u662f\u76df\u4e3b\u7684\u4eba\u4e86\uff0e\u201d \u3000\u3000\u5b8b\u5929\u94ce\u9053\uff1a\u201c\u8fd9\u4e94\u4eba\u7684\u6b66\u529f\uff0c\u90fd\u4e0d\u5728\u5c5e\u4e0b\u548c\u5df4\u5144\u4e4b\u4e0b\uff0e\u6709\u4e86\u4ed6\u4eec\u4e94\u4f4d\u52a0\u5165\u8ff7\u8e2a\u8c37\uff0c\u53bb\u4e86 \u67cf\u9f84\u9662\u53c8\u7b97\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u6709\u4e9b\u4e0d\u4fe1\uff0c\u9ed8\u4e86\u9ed8\u9053\uff1a\u201c\u5d06\u5cd2\u4e94\u9b54\uff0c\u5728\u9ed1\u767d\u4e24\u9053\u53ef\u79f0\u72ec\u6811\u4e00\u5e1c\uff0c\u8fde\u5c11\u6797\u3001\u6b66\u5f53\u90fd \u5bf9\u4ed6\u4eec\u83ab\u53ef\u5948\u4f55\uff0c\u4ed6\u4eec\u600e\u4f1a\u6295\u5230\u8ff7\u8e2a\u8c37\u6765\u5462\uff1f\u201d \u3000\u3000\u5df4\u5929\u4e49\u9053\uff1a\u201c\u8fd9\u662f\u5c5e\u4e0b\u6258\u4e00\u4f4d\u597d\u53cb\u548c\u4ed6\u4eec\u8054\u7edc\u4e0a\u7684\uff0c\u6700\u8fd1\u90a3\u4f4d\u597d\u53cb\u6709\u4e66\u4fe1\u5230\u6765\uff0c\u4fe1\u4e0a\u8bf4 \u4ed6\u4eec\u4e45\u4ef0\u76df\u4e3b\u5927\u540d\uff0c\u53c8\u542c\u4f60\u8001\u4eba\u5bb6\u5df2\u505a\u4e86\u5929\u4e0b\u7eff\u6797\u76df\u4e3b\uff0c\u6240\u4ee5\u624d\u60c5\u613f\u6295\u5230\u9ebe\u4e0b\u6765\u3002\u201d \u3000\u3000\u201c\u4ed6\u4eec\u662f\u5426\u5df2\u7531\u5d06\u5cd2\u52a8\u8eab\uff1f\u201d \u3000\u3000\u201c\u636e\u4fe1\u4e0a\u8bf4\u3001\u5df2\u7ecf\u52a8\u8eab\u591a\u65f6\uff0c\u8bf4\u4e0d\u5b9a\u6700\u8fd1\u51e0\u5929\u4fbf\u53ef\u5230\u8fbe\u3002\u201d \u3000\u3000\u201c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u65e9\u5bf9\u8001\u592b\u63d0\u8d77\uff1f\u201d \u3000\u3000\u201c\u5c5e\u4e0b\u4e3a\u7684\u662f\u7ed9\u76df\u4e3b\u2014\u4e2a\u60ca\u559c\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u62cd\u62cd\u5df4\u5929\u4e49\u7684\u80a9\u8180\u9053\uff1a\u201c\u4f60\u7acb\u7684\u8fd9\u4e00\u529f\u53ef\u771f\u4e0d\u5c0f\uff0c\u8001\u592b\u771f\u4e0d\u77e5\u8be5\u600e\u6837\u5956\u8d4f\u4f60\u3002\u201d \u3000\u3000\u5df4\u5929\u4e49\u5f97\u610f\u7684\u2014\u7b11\u9053\uff1a\u201c\u76df\u4e3b\u5bf9\u5c5e\u4e0b\u4e00\u5411\u8a00\u542c\u8ba1\u4ece\uff0c\u8fd9\u756a\u77e5\u9047\u4e4b\u6069\uff0c\u5c5e\u4e0b\u5df2\u7ecf\u662f\u5f88\u611f\u6fc0 \u7684\u4e86\uff0c\u54ea\u91cc\u8fd8\u6562\u6c42\u8d4f\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1b\u201c\u5df4\u8001\u5f1f\u7528\u4e0d\u7740\u8ddf\u8001\u592b\u5ba2\u6c14\uff0c\u6b63\u597d\u9a6c\u4e0a\u5c31\u6709\u2014\u4e2a\u673a\u4f1a\uff0c\u5982\u679c\u706b\u70e7\u67cf\u9f84\u9662\u7684\u5927 \u4e8b\u6210\u529f\uff0c\u526f\u76df\u4e3b\u7684\u5b9d\u5ea7\u5c31\u8be5\u662f\u4f60\u7684\u4e86\uff01\u201d \u3000\u3000\u5df4\u5929\u4e49\u8fde\u5fd9\u8d77\u8eab\u606d\u8c28\u4e00\u793c\u9053\uff1a\u201c\u5168\u4ed7\u76df\u4e3b\u63d0\u62d4\uff01\u201d \u3000\u3000\u5f53\u665a\uff0c\u9ea6\u5c0f\u660e\u6b63\u5728\u5373\u5c06\u6726\u80e7\u5165\u7761\u4e4b\u9645\uff0c\u5ffd\u542c\u201c\u54bb\u201d\u7684\u4e00\u58f0\uff0e\u4f3c\u662f\u7531\u7a97\u5916\u5c04\u8fdb\u2014\u7c92\u77f3\u5b50\u3002 \u3000\u3000\u4ed6\u7acb\u5373\u7531\u5e8a\u4e0a\u2014\u8dc3\u800c\u8d77\uff0c\u6253\u5f00\u7a97\u6765\uff0c\u5374\u53c8\u4e0d\u89c1\u4efb\u4f55\u52a8\u9759\u3002 \u3000\u3000\u5f88\u5feb\u7684\uff0c\u4ed6\u4fbf\u60f3\u5230\uff0c\u83ab\u975e\u6709\u4eba\u793a\u8b66\u800c\u6765\uff1f \u3000\u3000\u71c3\u4e0a\u706f\uff0c\u597d\u4e0d\u5bb9\u6613\u5728\u5730\u4e0a\u627e\u5230\u90a3\u7c92\u77f3\u5b50\uff0c\u679c\u7136\uff0e\u77f3\u5b50\u5916\u9762\u5305\u7740\u4e00\u5c42\u7eb8\u3002 \u3000\u3000\u5c55\u5f00\u7eb8\u56e2\u5728\u706f\u4e0b\u770b\u53bb\uff0c\u4e0a\u9762\u8d6b\u7136\u5199\u7740\u4e00\u884c\u5b57\uff1a\u201c\u5c0f\u5fc3\u6709\u4eba\u7eb5\u706b\u711a\u70e7\u67cf\u9f84\u9662\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u609a\u7136\u9707\u60ca\uff0e\u7acb\u523b\u7531\u5899\u58c1\u4e0a\u53d6\u4e0b\u957f\u5251\uff0c\u7a7f\u597d\u8863\u670d\uff0c\u51fa\u4e86\u623f\u95f4\uff0c\u5206\u522b\u53eb\u9192\u949f\u4e00\u8c6a\uff0c\u4f59 \u4ea6\u4e50\u548c\u82d7\u7d20\u82d3\u3002 \u3000\u3000\u5f53\u949f\u4e00\u8c6a\u7b49\u4e09\u4eba\u542c\u7f62\u9ea6\u5c0f\u660e\u7684\u53d9\u8ff0\u540e\uff0c\u4e5f\u9707\u60ca\u4e0d\u5df2\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u73b0\u5728\u5c31\u7531\u54b1\u4eec\u56db\u4eba\u4e0a\u623f\u76d1\u89c6\uff0c\u4e1c\u3001\u897f\u3001\u5357\u3001\u5317\u56db\u65b9\uff0c\u6bcf\u4eba\u8d1f\u8d23\u4e00\u65b9\uff0c\u6682\u65f6 \u4e0d\u5fc5\u8f7b\u4e3e\u5984\u52a8\uff0c\u7b49\u5bf9\u529b\u6709\u4e86\u884c\u52a8\uff0c\u54b1\u4eec\u518d\u91c7\u53d6\u884c\u52a8\u3002\u201d \u3000\u3000\u82d7\u7d20\u82d3\u9053\uff1a\u201c\u8981\u4e0d\u8981\u628a\u6240\u6709\u7684\u4eba\u4e00\u8d77\u53eb\u8d77\u6765\uff1f\u201d \u3000\u3000\u949f\u2014\u8c6a\u5fd6\u4e86\u534a\u54cd\u9053\uff1a\u201c\u4e0d\u5fc5\uff0c\u5982\u679c\u5bf9\u65b9\u771f\u60f3\u706b\u711a\u67cf\u9f84\u9662\uff0c\u5fc5\u5b9a\u5148\u642c\u52a8\u6613\u71c3\u4e4b\u7269\uff0c\u54b1\u4eec\u65e2 \u5df2\u7ecf\u4e25\u5bc6\u76d1\u89c6\uff0c\u4ed6\u4eec\u5c31\u7edd\u5bf9\u96be\u4ee5\u5f97\u901e\u3002\u82e5\u628a\u6240\u6709\u7684\u4eba\u60ca\u52a8\u8d77\u6765\uff0c\u53cd\u800c\u4e0d\u5999\u3002\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u5f85\u4f1a\u513f\u82e5\u6349\u5230\u7eb5\u706b\u4e4b\u4eba\uff0c\u6700\u597d\u7559\u4e0b\u6d3b\u53e3\uff0c\u8fd9\u6837\u624d\u80fd\u67e5\u51fa\u5bf9\u65b9\u7684\u9996 \u8c0b\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u9996\u8c0b\u4e4b\u4eba\uff0c\u9664\u4e86\u970d\u5143\u4f3d\uff0c\u8fd8\u4f1a\u6709\u8c01\uff1f\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u9053\uff1a\u201c\u867d\u7136\u4e3b\u8c0b\u4e4b\u4eba\u5341\u6709\u516b\u4e5d\u662f\u970d\u5143\u4f3d\uff0c\u4f46\u603b\u8981\u7559\u4e0b\u6d3b\u53e3\u624d\u597d\u5bf9\u8d28\uff0c\u5426\u5219\u5c82\u4e0d\u53d8 \u6210\u65e0\u6545\u6740\u4eba\u3002\u201d \u3000\u3000\u949f\u4e00\u8c6a\u6c89\u541f\u4e86\u534a\u54cd\u9053\uff1a\u201c\u8fd9\u6295\u77f3\u793a\u8b66\u4e4b\u4eba\u53c8\u662f\u8c01\u5462\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u4e0d\u5047\u601d\u7d22\u7684\u9053\uff1a\u201c\u5728\u970d\u5143\u4f3d\u624b\u4e0b\uff0e\u8fd8\u6709\u5fc3\u5ff5\u67cf\u9f84\u9662\u7684\uff0c\u9664\u4e86\u82d7\u5927\u59d1\u5a18\u8fd8\u4f1a\u6709\u8c01\uff1f\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u9053\uff1a\u201c\u4f60\u524d\u665a\u95ef\u5165\u82d7\u5927\u59d1\u5a18\u623f\u95f4\uff0c\u5979\u4e0d\u662f\u5bf9\u4f60\u9887\u4e3a\u51b7\u6de1\u7684\u5417\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u5979\u7eb5\u7136\u4e0d\u4e3a\u54b1\u4eec\uff0c\u4e5f\u603b\u4f1a\u4e3a\u5979\u7684\u59b9\u59b9\u7740\u60f3\uff0c\u5982\u679c\u771f\u7684\u70e7\u4e86\u67cf\u9f84\u9662\uff0c\u82d7\u4e8c\u59d1 \u5a18\u4e5f\u8981\u8ddf\u7740\u906d\u6b83\uff1f\u201d \u3000\u3000\u949f\u4e00\u8c6a\u7acb\u5373\u5429\u5490\u9053\uff1a\u201c\u6682\u65f6\u4e0d\u5fc5\u8ba8\u8bba\u8fd9\u95ee\u9898\uff0c\u73b0\u5728\u5f00\u59cb\u56db\u9762\u76d1\u89c6\uff0c\u6211\u5b88\u4e1c\u4fa7\uff0c\u4f59\u5144\u8bf7\u5b88 \u897f\u4fa7\uff0c\u82d7\u59d1\u5a18\u8bf7\u5b88\u5317\u4fa7\uff0c\u5c0f\u660e\u5144\u5f1f\u8bf7\u5b88\u5357\u4fa7\u3002\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u7d27\u63a5\u7740\u9053\uff1a\u201c\u5404\u4f4d\u5148\u56de\u53bb\u628a\u623f\u4e2d\u706f\u706b\u7184\u706d\uff0c\u7136\u540e\u518d\u5404\u5c31\u5404\u4f4d\u3002\u201d \u3000\u3000\u56db\u4eba\u6563\u5f00\u540e\uff0e\u9ea6\u5c0f\u660e\u4f9d\u8a00\u5148\u56de\u623f\u7184\u4e86\u706f\uff0c\u7136\u540e\u8dc3\u4e0a\u5357\u9762\u5c4b\u9876\uff0e\u9690\u8eab\u5728\u5c4b\u810a\u5185\u4fa7\uff0c\u53ea\u9732\u51fa \u534a\u4e2a\u5934\u5411\u5916\u76d1\u89c6\u3002 \u3000\u3000\u67cf\u9f84\u9662\u4e5f\u7b97\u662f\u4e00\u5e62\u72ec\u7acb\u5ead\u9662\uff0c\u548c\u5176\u4ed6\u5c4b\u820d\u5e76\u4e0d\u76f8\u8fde\uff0e\u56db\u9762\u79cd\u4e86\u4e0d\u5c11\u67f3\u6811\uff0c\u867d\u7136\u4eba\u5728\u5c4b\u9876 \u4e0a\uff0c\u89c6\u754c\u4e5f\u5e76\u4e0d\u5341\u5206\u5e7f\u9614\u3002\u5bf9\u65b9\u91c7\u7528\u706b\u653b\uff0c\u5728\u884c\u52a8\u4e0a\u53ef\u8bf4\u662f\u5341\u5206\u65b9\u4fbf\uff0c\u53ea\u8981\u628a\u6613\u71c3\u4e4b\u7269\u85cf\u5728 \u6811\u840c\u4e0b\uff0c\u5f88\u5feb\u4fbf\u53ef\u8fd0\u9001\u5230\u67cf\u9f84\u9662\u56db\u5468\uff0e \u3000\u3000\u9ea6\u5c0f\u660e\u6240\u76d1\u89c6\u7684\u4e00\u9762\uff0c\u6b63\u662f\u67cf\u9f84\u9662\u7684\u524d\u9762\u5927\u95e8\u3002\u4ed6\u76ee\u529b\u7279\u5f3a\uff0c\u5f88\u6e05\u6670\u7684\u53ef\u4ee5\u770b\u5230\u5927\u95e8\u5916 \u7684\u8b66\u536b\u55bd\u5175\u5728\u9644\u8fd1\u6e38\u52a8\u5de1\u89c6\u3002 \u3000\u3000\u6b64\u65f6\u624d\u521a\u521a\u4e8c\u66f4\uff0c\u9ea6\u5c0f\u660e\u9884\u6599\u8ddd\u79bb\u5bf9\u65b9\u884c\u52a8\u7684\u65f6\u95f4\u4e0d\u65e9\uff0c\u800c\u4e14\u5927\u95e8\u5916\u53c8\u6709\u8b66\u536b\u55bd\u5175\uff0c\u4fbf \u51b3\u5b9a\u5148\u5230\u5176\u4ed6\u4e09\u5904\u770b\u770b\u3002 \u3000\u3000\u5176\u4ed6\u4e09\u5904\u7684\u949f\u4e00\u8c6a\u3001\u4f59\u4ea6\u4e50\u3001\u82d7\u7d20\u82d3\u90fd\u805a\u7cbe\u4f1a\u795e\u7684\u4e0a\u5c4b\u9876\u76d1\u89c6\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u5e76\u7279\u522b\u4ea4\u4ee3\u9ea6\u5c0f\u660e\uff0c\u8981\u4ed6\u8f6c\u544a\u4f59\u4ea6\u4e50\u548c\u82d7\u7d20\u82d3\uff0c\u82e5\u5bf9\u65b9\u5c1a\u672a\u628a\u6613\u71c3\u548c\u5f15\u706b\u4e4b\u7269\u79fb \u5230\u5899\u811a\u4e0b\uff0c\u7eb5\u7136\u53d1\u73b0\u6811\u836b\u4e0b\u6709\u4eba\uff0c\u4e5f\u6682\u65f6\u4e0d\u53ef\u91c7\u53d6\u884c\u52a8\u3002 \u3000\u3000\u76ee\u7684\u662f\u5e0c\u671b\u5bf9\u65b9\u628a\u8fd9\u4e9b\u5f15\u706b\u548c\u6613\u71c3\u4e4b\u7269\u7559\u5728\u73b0\u573a\uff0c\u4ee5\u4fbf\u5411\u970d\u5143\u4f3d\u5f53\u9762\u5bf9\u8d28\uff0e \u3000\u3000\u9ea6\u5c0f\u660e\u5411\u4f59\u4ea6\u4e50\u548c\u82d7\u7d20\u82d3\u4f20\u8fbe\u8fc7\u540e\uff0c\u518d\u56de\u5230\u524d\u9762\u5c4b\u9876\u65f6\uff0c\u4e0d\u7981\u5927\u5403\u4e00\u60ca\u3002 \u3000\u3000\u53ea\u89c1\u5927\u95e8\u5916\u7684\u90a3\u540d\u8b66\u536b\u55bd\u5175\uff0c\u5df2\u5012\u5367\u5730\u4e0a\uff0e\u52a8\u4e5f\u4e0d\u52a8\u3002 \u3000\u3000\u518d\u5411\u67f3\u6811\u4e1b\u4e2d\u770b\u53bb\uff0c\u8d6b\u7136\u5df2\u53d1\u73b0\u4e0d\u5c11\u4eba\u5f71\u5728\u8815\u8815\u6643\u52a8\uff0c\u8db3\u6709\u5341\u51e0\u540d\u4e4b\u591a\u3002 \u3000\u3000\u867d\u7136\u67f3\u6811\u4e1b\u4e2d\u4eba\u6570\u4e0d\u5c11\uff0c\u5374\u5e76\u672a\u53d1\u51fa\u534a\u70b9\u58f0\u606f\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u56e0\u5df2\u6709\u949f\u4e00\u8c6a\u7684\u4ea4\u4ee3\uff0c\u9887\u80fd\u6c89\u5f97\u4f4f\u6c14\uff0c\u51b3\u5b9a\u7b49\u5bf9\u65b9\u642c\u8fd0\u5f15\u706b\u4e4b\u7269\u65f6\uff0c\u518d\u5f00\u59cb\u622a\u51fb\u3002 \u3000\u3000\u5927\u7ea6\u53c8\u8fc7\u4e86\u76cf\u8336\u5de5\u592b\uff0c\u67f3\u836b\u4e0b\u7684\u4eba\uff0c\u5df2\u6162\u6162\u5411\u56f4\u5899\u5916\u63a5\u8fd1\uff0c\u4ed6\u4eec\u6709\u7684\u62b1\u7740\u5e72\u8349\uff0c\u6709\u7684\u63d0 \u7740\u6cb9\u6876\uff0c\u6709\u7684\u625b\u7740\u5e03\u888b\uff0c\u888b\u5b50\u91cc\u60f3\u5fc5\u88c5\u7740\u70df\u706b\u785d\u78f7\u836f\u7b49\u7269\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u773c\u89c1\u4ed6\u4eec\u628a\u8fd0\u6765\u4e4b\u7269\u653e\u5230\u5899\u5916\uff0c\u8fd8\u662f\u4e0d\u58f0\u4e0d\u54cd\uff0c\u56e0\u4e3a\u4ed6\u5df2\u770b\u51fa\u67f3\u836b\u5185\u5806\u79ef\u7684\u4e1c\u897f \u4e0d\u5c11\uff0c\u81f3\u5c11\u8fd8\u8981\u822c\u8fd0\u597d\u51e0\u6b21\u3002 \u3000\u3000\u76f4\u5230\u5bf9\u65b9\u642c\u8fd0\u5230\u7b2c\u4e09\u6b21\uff0c\u9ea6\u5c0f\u660e\u89c9\u51fa\u65f6\u673a\u5df2\u5230\uff0c\u5c31\u5728\u5c4b\u9876\u539f\u52bf\u4e00\u7eb5\uff0c\u4eba\u5df2\u95ea\u7535\u822c\u63a0\u51fa\u56f4 \u5899\u4e4b\u5916\uff0c\u63a5\u8fde\u4e24\u7f15\u6307\u98ce\u5f39\u51fa\uff0c\u4fbf\u6709\u4e24\u4eba\u5012\u4e0b\u5730\u53bb\u3002 \u3000\u3000\u5176\u4f59\u7684\u5728\u60ca\u53d8\u4e4b\u4f59\uff0e\u987f\u65f6\u56db\u6563\u9003\u7a9c\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u4e5f\u4e0d\u8ffd\u88ad\uff0c\u6709\u4e24\u540d\u6d3b\u53e3\u5728\uff0c\u5df2\u7ecf\u8db3\u591f\u4f5c\u4e3a\u51ed\u8bc1\u7684\u4e86\u3002 \u3000\u3000\u51e0\u4e4e\u5728\u540c\u4e00\u65f6\u95f4\uff0c\u53e6\u4e09\u9762\u7684\u949f\u2014\u8c6a\u3001\u4f59\u4ea6\u4e50\u548c\u82d7\u7d20\u82d3\u4e5f\u90fd\u5f00\u59cb\u4e86\u884c\u52a8\u3002 \u3000\u3000\u4e0d\u6d88\u8bf4\uff0e\u53e6\u4e09\u9762\u51c6\u5907\u653e\u706b\u4e4b\u4eba\uff0c\u4e5f\u90fd\u56db\u6563\u9003\u5954\u3002 \u3000\u3000\u8fd9\u2014\u6765\uff0e\u5f88\u5feb\u4fbf\u60ca\u52a8\u4e86\u67cf\u9f84\u9662\u5176\u4ed6\u6b63\u5728\u719f\u7761\u7684\u4eba\uff0c\u5d02\u5c71\u4e09\u96c4\u3001\u6c5f\u5317\u4e09\u9f99\u4ee5\u53ca\u6587\u5929\u751f\u7b49\u5168 \u64cd\u8d77\u5175\u5200\u8d76\u5230\u6237\u5916\u3002 \u3000\u3000\u4ed6\u4eec\u56e0\u4e3a\u90fd\u662f\u7531\u5927\u95e8\u51fa\u53bb\uff0c\u9996\u5148\u770b\u5230\u7684\u4fbf\u662f\u9ea6\u5c0f\u660e\u3002 \u3000\u3000\u738b\u5927\u5eb7\u832b\u7136\u95ee\u9053\uff1a\u201c\u9ea6\u8001\u5f1f\uff0e\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u7b80\u7565\u7684\u628a\u6709\u4eba\u51c6\u5907\u7eb5\u706b\u70e7\u67cf\u9f84\u9662\u7684\u4e8b\u8bf4\u4e86\u4e00\u904d\uff0c\u7136\u540e\u4ea4\u4ee3\u9053\uff1a\u201c\u8bf8\u4f4d\u8bf7\u5728\u8fd9\u91cc\u7a0d \u7b49\uff0c\u6211\u5148\u53bb\u548c\u949f\u526f\u76df\u4e3b\u5546\u8bae\u4e00\u4e0b\u4e0b\u4e00\u6b65\u8be5\u600e\u4e48\u529e\uff0c\u7136\u540e\u518d\u6765\u901a\u77e5\u5927\u5bb6\u3002\u201d \u3000\u3000\u4ed6\u7acb\u5373\u8d76\u5230\u67cf\u9f84\u9662\u56f4\u5899\u5916\u4e1c\u4fa7\uff0c\u53ea\u89c1\u949f\u4e00\u8c6a\u6b63\u7ad9\u5728\u90a3\u91cc\uff0c\u811a\u4e0b\u5e76\u8eba\u7740\u4e00\u4e2a\u4eba\uff0c\u5899\u8fb9\u4e5f\u653e \u7740\u597d\u51e0\u5806\u6d78\u8fc7\u6cb9\u7684\u5e72\u8349\u4ee5\u53ca\u6cb9\u6876\u785d\u78f7\u7b49\u7269\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u6025\u6025\u95ee\u9053\uff1a\u201c\u4f60\u90a3\u9762\u600e\u4e48\u6837\u4e86\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u8ddf\u4f60\u8fd9\u8fb9\u5dee\u4e0d\u591a\uff0c\u53e6\u5916\u5c0f\u5f1f\u6349\u5230\u4e86\u4e24\u4eba\u3002\u201d \u3000\u3000\u201c\u54b1\u4eec\u9662\u91cc\u7684\u4eba\u597d\u50cf\u90fd\u8d77\u6765\u4e86\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u89c1\u4eba\u5f71\uff1f\u201d \u3000\u3000\u201c\u4ed6\u4eec\u90fd\u5728\u524d\u9762\u5927\u95e8\u5916\u5f85\u547d\uff0c\u5c0f\u5f1f\u5c31\u662f\u6765\u95ee\u4f60\u8981\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u201c\u5c31\u9ebb\u70e6\u4f60\u4ea4\u4ee3\u4ed6\u4eec\u5206\u6210\u56db\u7ec4\uff0e\u5404\u81ea\u5b88\u4f4f\u4e1c\u3001\u897f\u3001\u5357\u3001\u5317\u56db\u9762\u73b0\u573a\uff0c\u7136\u540e\u518d\u901a\u77e5\u4f59\u5148\u751f \u5e26\u7740\u6349\u4f4f\u7684\u4eba\u5230\u524d\u95e8\u4f1a\u9762\uff0e\u201d \u3000\u3000\u201c\u4ed6\u4eec\u53ea\u6709\u4e03\u4e2a\u4eba\uff0c\u5982\u4f55\u5206\u6210\u56db\u7ec4\uff1f\u201d \u3000\u3000\u201c\u90a3\u4e48\u82d7\u59d1\u5a18\u5c31\u7559\u5728\u73b0\u573a\u597d\u4e86\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u4f20\u8fbe\u8fc7\u949f\u2014\u8c6a\u7684\u4ea4\u5f85\u540e\u4e0d\u4e45\uff0c\u949f\u4e00\u8c6a\u548c\u4f59\u4ea6\u4e50\u5df2\u5230\u8fbe\u524d\u95e8\uff0c\u4ed6\u4eec\u90fd\u5404\u6349\u4f4f\u4e00\u4eba\u3002 \u3000\u3000\u82d7\u7d20\u82d3\u4e5f\u6349\u4f4f\u4e00\u4eba\uff0c\u5979\u628a\u4eba\u9001\u5230\u524d\u95e8\u540e\uff0c\u624d\u518d\u56de\u5230\u56f4\u5899\u5317\u4fa7\u3002 \u3000\u3000\u88ab\u6349\u4f4f\u7684\u8fde\u9ea6\u5c0f\u660e\u7684\u4e24\u4e2a\u5728\u5185\u4e00\u5171\u4e94\u4eba\uff0e\u4e3a\u4e86\u5148\u505a\u4e00\u756a\u95ee\u8bdd\uff0c\u90fd\u628a\u4ed6\u4eec\u89e3\u4e86\u7a74\u9053\uff0c\u5e26\u81f3 \u67cf\u9f84\u9662\u5ba2\u5385\u3002 \u3000\u3000\u8fd9\u4e94\u4eba\u4e00\u4e2a\u53eb\u738b\u660e\uff0c\u4e00\u4e2a\u53eb\u4e01\u5b89\uff0c\u4e00\u4e2a\u53eb\u77f3\u5f6a\uff0c\u2014\u4e2a\u53eb\u5415\u5927\u96c4\uff0c\u2014\u4e2a\u53eb\u5434\u5b9d\u8d35\uff0c\u53ea\u6709\u738b \u660e\u662f\u4e2a\u5c0f\u5934\u76ee\u3002 \u3000\u3000\u6b64\u523b\u738b\u660e\u7b49\u4e94\u4eba\uff0e\u5168\u5df2\u5413\u5f97\u9762\u65e0\u4eba\u8272\uff0c\u5168\u8eab\u76f4\u6253\u54c6\u55e6\uff0c\u81ea\u52a8\u8dea\u5728\u5730\u4e0a\uff0c\u8fde\u5934\u4e5f\u4e0d\u6562\u62ac\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u4e0d\u7528\u6015\uff01\u4f60\u4eec\u90fd\u662f\u53d7\u4eba\u6307\u4f7f\u7684\uff0c\u53ea\u8981\u8bf4\u51fa\u662f\u4ec0\u4e48\u4eba\u4ea4\u4ee3\u4f60\u4eec\u8fd9\u6837\u505a\u7684\uff0c\u5c31 \u6ca1\u4f60\u4eec\u7684\u4e8b\u4e86\u3002\u201d \u3000\u3000\u738b\u660e\u6253\u7740\u51b7\u98a4\u9053\uff1a\u201c\u662f\u2026\u2026\u9ad8\u5934\u76ee\uff01\u201d \u3000\u3000\u949f\u4e00\u8c6a\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u9ad8\u5934\u76ee\u662f\u8c01\uff1f\u201d \u3000\u3000\u738b\u660e\u55eb\u5685\u7740\u9053\uff1a\u201c\u9ad8\u5934\u76ee\u53eb\u2026\u2026\u2026\u53eb\u9ad8\u6765\uff01\u201d \u3000\u3000\u949f\u4e00\u8c6a\u4f3c\u662f\u542c\u8bf4\u8fc7\u8ff7\u8e2a\u8c37\u6709\u4e2a\u53eb\u9ad8\u6765\u7684\uff0c\u4f46\u4e5f\u4e0d\u8fc7\u662f\u4e2a\u4e2d\u4e0b\u7ea7\u5934\u76ee\uff0c\u6bd4\u738b\u660e\u7684\u5730\u4f4d\u53ea\u9ad8 \u4e86\u4e00\u70b9\u3002\u5982\u679c\u8bf4\u662f\u9ad8\u6765\u4e0b\u4ee4\u711a\u70e7\u67cf\u9f84\u9662\u7684\uff0c\u663e\u7136\u4e0d\u662f\u5b9e\u8bdd\u3002 \u3000\u3000\u949f\u2014\u8c6a\u63a5\u7740\u518d\u95ee\u5176\u4ed6\u56db\u4eba\uff0c\u8c01\u77e5\u5176\u4ed6\u56db\u4eba\u4e5f\u5f02\u53e3\u540c\u58f0\u8bf4\u662f\u5949\u9ad8\u6765\u4e4b\u547d\u884c\u4e8b\u7684\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u5e76\u4e0d\u52a8\u6012\uff0c\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u9ad8\u6765\u53ea\u662f\u4e2a\u5c0f\u5934\u76ee\uff0c\u4ed6\u6709\u4ec0\u4e48\u8d44\u683c\u547d\u4ee4\u4f60\u4eec\u706b\u711a\u67cf\u9f84 \u9662\uff1f\u201d \u3000\u3000\u738b\u660e\u6296\u7740\u58f0\u97f3\u9053\uff1a\u201c\u9ad8\u6765\u867d\u7136\u8eab\u4efd\u4e0d\u9ad8\uff0c\u4f46\u4ed6\u5374\u662f\u5c5e\u4e0b\u7684\u9876\u5934\u4e0a\u53f8\uff0e\u4ed6\u7684\u547d\u4ee4\uff0c\u5c5e\u4e0b\u4e0d \u6562\u4e0d\u542c\u3002\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u9053\uff1a\u201c\u526f\u76df\u4e3b\uff0c\u4e0d\u5fc5\u95ee\u4e86\uff0c\u518d\u95ee\u4e5f\u95ee\u4e0d\u51fa\u5934\u7eea\u3002\u54b1\u4eec\u73b0\u5728\u5c31\u5e26\u7740\u4ed6\u4eec\u53bb\u89c1\u76df\u4e3b\uff0c \u8ba9\u76df\u4e3b\u7ed9\u54b1\u4eec\u2014\u4e2a\u4ea4\u4ee3\u3002\u201d \u3000\u3000\u949f\u2014\u8c6a\u9894\u9996\u9053\uff1a\u201c\u597d\uff0c\u73b0\u5728\u5c31\u8d70\uff01\u201d \u3000\u3000\u4e8e\u662f\uff0c\u949f\u4e00\u8c6a\u3001\u4f59\u4ea6\u4e50\u3001\u9ea6\u5c0f\u660e\u62bc\u7740\u738b\u660e\u7b49\u4e94\u4eba\uff0c\u76f4\u5f80\u5929\u661f\u9662\u800c\u6765\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u7b49\u4e09\u4eba\u672b\u5411\u91cc\u51b2\uff0c\u8fd8\u662f\u8010\u5fc3\u7684\u7b49\u5f85\u95e8\u5916\u8b66\u536b\u55bd\u5175\u8fdb\u5185\u901a\u62a5\u3002 \u3000\u3000\u597d\u5927\u4e00\u4f1a\u5de5\u592b\uff0c\u624d\u89c1\u970d\u5143\u4f3d\u4e24\u773c\u60fa\u5fea\u7684\u8d70\u51fa\u5927\u95e8\uff0c\u5728\u4ed6\u8eab\u540e\u968f\u4f8d\u7740\u5cad\u5357\u4e8c\u5947\u3002 \u3000\u3000\u4e0d\u96be\u60f3\u89c1\uff0c\u90a3\u4e9b\u7eb5\u706b\u4e0d\u6210\u7684\u55bd\u5175\uff0c\u5fc5\u5b9a\u628a\u4e8b\u60c5\u7ecf\u8fc7\u7980\u62a5\u4e86\u970d\u5143\u4f3d\u548c\u5cad\u5357\u4e8c\u5947\uff0c\u6b64\u523b\u970d\u5143 \u4f3d\u7684\u8868\u73b0\uff0c\u663e\u7136\u662f\u5728\u4f2a\u88c5\u6210\u6beb\u4e0d\u77e5\u60c5\u3002 \u3000\u3000\u679c\u7136\uff0e\u970d\u5143\u4f3d\u4e00\u51fa\u95e8\u5c31\u8bb6\u7136\u95ee\u9053\uff1a\u201c\u534a\u591c\u4e09\u66f4\uff0c\u4e09\u4f4d\u5230\u5929\u661f\u9662\u6765\u89c1\u8001\u592b\uff0e\u53ef\u662f\u53d1\u751f\u4e86\u4ec0 \u4e48\u91cd\u8981\u5927\u4e8b\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u51b7\u51b7\u4e00\u7b11\uff0c\u62a2\u5148\u8bf4\u9053\uff1a\u201c\u4e8b\u60c5\u4e0d\u5927\uff0e\u53ea\u662f\u6709\u4eba\u8981\u653e\u706b\u70e7\u6b7b\u67cf\u9f84\u9662\u6240\u6709\u7684\u4eba\u800c\u5df2\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u8138\u8272\u4e00\u53d8\u9053\uff1a\u201c\u6709\u8fd9\u79cd\u4e8b\uff0e\u600e\u4e48\u8001\u592b\u4e00\u70b9\u4e0d\u77e5\u9053\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4e0d\u7ba1\u4f60\u771f\u4e0d\u77e5\u9053\u8fd8\u662f\u5047\u4e0d\u77e5\u9053\uff0c\u6211\u4eec\u5df2\u7ecf\u902e\u4f4f\u4e94\u4e2a\u4eba\uff0c\u4f60\u95ee\u95ee\u4ed6\u4eec\u5c31\u77e5\u9053 \u4e86\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u6c89\u4e0b\u8138\u8272\u9053\uff1a\u201c\u9ea6\u8001\u5f1f\u600e\u53ef\u8fd9\u6837\u8bf4\u8bdd\uff1f\u67cf\u9f84\u9662\u4e5f\u662f\u8001\u592b\u81ea\u5df1\u7684\u4eba\uff0c\u96be\u9053\u8001\u592b\u4f1a\u706b \u70e7\u81ea\u5df1\u7684\u4eba\uff0c\u7b80\u76f4\u5c82\u6709\u6b64\u7406\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u5c82\u6709\u6b64\u7406\u7684\u4e8b\u592a\u591a\u4e86\uff0c\u6b63\u56e0\u4e3a\u4e16\u4e0a\u6709\u5c82\u6709\u6b64\u7406\u7684\u4eba\uff0c\u6240\u4ee5\u624d\u80fd\u505a\u51fa\u5c82\u6709\u6b64 \u7406\u7684\u4e8b\u3002\u201d \u3000\u3000\u949f\u4e00\u8c6a\u62c5\u5fc3\u628a\u4e8b\u60c5\u95f9\u50f5\uff0c\u5fd9\u5411\u9ea6\u5c0f\u660e\u4f7f\u4e2a\u773c\u8272\u9053\uff1a\u201c\u9ea6\u5c0f\u5144\u5f1f\u4e0d\u5fc5\u518d\u8bb2\uff0c\u76df\u4e3b\u603b\u4f1a\u7ed9\u54b1 \u4eec\u4e00\u4e2a\u4ea4\u4ee3\uff01\u201d \u3000\u3000\u949f\u4e00\u8c6a\u6240\u4ee5\u80fd\u5411\u9ea6\u5c0f\u660e\u4f7f\u773c\u8272\uff0c\u662f\u56e0\u4e3a\u5929\u661f\u9662\u5927\u95e8\u5916\u9ad8\u60ac\u7740\u4e00\u76cf\u6c14\u6b7b\u98ce\u706f\uff0c\u540c\u65f6\u4e5f\u53ef\u7167 \u89c1\u738b\u660e\u7b49\u4e94\u4eba\u3002 \u3000\u3000\u970d\u5143\u4f3d\u77a5\u4e86\u738b\u660e\u7b49\u4e94\u4eba\u4e00\u773c\u9053\uff1b\u201c\u4ed6\u4eec\u4e94\u4e2a\u662f\u505a\u4ec0\u4e48\u7684\uff1f\u201d \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u4ed6\u4eec\u5c31\u662f\u7eb5\u706b\u7684\uff0c\u5f53\u65f6\u4eba\u6570\u5f88\u591a\uff0c\u5c5e\u4e0b\u548c\u4f59\u5148\u751f\u53ea\u6349\u5230\u4ed6\u4eec\u4e94\u4e2a\uff0c\u5176\u5b9e\u5373 \u4f7f\u6349\u5230\u4e00\u4e2a\u4e5f\u591f\u4e86\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u5411\u738b\u660e\u7b49\u4e94\u4eba\u559d\u9053\uff1a\u201c\u8fd9\u4e8b\u771f\u662f\u4f60\u4eec\u51e0\u4eba\u5e72\u7684\uff1f\u201d \u3000\u3000\u738b\u660e\u7b49\u4eba\u5168\u6253\u7740\u54c6\u55e6\u4f4e\u4e0b\u5934\uff0c\u8c01\u90fd\u6ca1\u542d\u58f0\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u76df\u4e3b\u6682\u65f6\u522b\u6025\u7740\u95ee\u4ed6\u4eec\uff01\u5c5e\u4e0b\u60f3\u8bf7\u76df\u4e3b\u5230\u67cf\u9f84\u9662\u4e00\u8d9f\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u8bf7\u8001\u592b\u5230\u67cf\u9f84\u9662\uff1f\u201d \u3000\u3000\u201c\u738b\u660e\u7b49\u4e94\u4eba\u662f\u4eba\u8bc1\uff0c\u67cf\u9f84\u9662\u90a3\u8fb9\u8fd8\u6709\u7269\u8bc1\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u867d\u7136\u4e0d\u60f3\uff0e\u5374\u53c8\u63a8\u8131\u4e0d\u5f97\uff0c\u53ea\u597d\u5e26\u7740\u5cad\u5357\u4e8c\u5947\u968f\u949f\u2014\u8c6a\u7b49\u4ee5\u53ca\u738b\u660e\u4e00\u884c\u4eba\u5f80\u67cf\u9f84 \u9662\u800c\u6765\u3002 \u3000\u3000\u8fd9\u65f6\u4e0b\u5f26\u6708\u5df2\u7ecf\u5347\u8d77\uff0c\u5f53\u8d70\u5230\u67cf\u9f84\u9662\u5927\u95e8\u5916\u7684\u67f3\u836b\u65f6\uff0c\u4fbf\u5df2\u770b\u5230\u6811\u4e0b\u5806\u79ef\u7740\u5e72\u8349\u3001\u6cb9\u6876 \u7b49\u7269\u3002 \u3000\u3000\u6765\u5230\u5927\u95e8\u5916\u56f4\u5899\u8fb9\uff0c\u5f15\u706b\u4e4b\u7269\u5806\u79ef\u5f97\u66f4\u591a\u3002 \u3000\u3000\u5927\u95e8\u5916\u8d1f\u8d23\u770b\u5b88\u73b0\u573a\u7684\u662f\u5d02\u5c71\u4e09\u96c4\u4e2d\u7684\u6d2a\u6cfd\u548c\u738b\u5927\u5eb7\u3002 \u3000\u3000\u738b\u5927\u5eb7\u4e00\u89c1\u970d\u5143\u4f3d\u5230\u6765\uff0c\u4fbf\u563f\u563f\u7b11\u9053\uff1a\u201c\u76df\u4e3b\u8f9b\u82e6\u4e86\uff0c\u534a\u591c\u4e09\u66f4\u8fd8\u52b3\u52a8\u60a8\u8001\u4eba\u5bb6\u5230\u5904 \u8dd1\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u5e72\u54b3\u4e86\u58f0\u9053\uff1a\u201c\u8ff7\u8e2a\u8c37\u53d1\u751f\u4e86\u8fd9\u6837\u5927\u7684\u4e8b\uff0c\u8001\u592b\u600e\u80fd\u4e0d\u51fa\u6765\u770b\u770b\uff1f\u201d \u3000\u3000\u738b\u5927\u5eb7\u9053\uff1a\u201c\u76df\u4e3b\u53ef\u77e5\u9053\u5f53\u5e74\u80e1\u524d\u76df\u4e3b\u5efa\u67cf\u9f84\u9662\u65f6\uff0c\u52a8\u7528\u4e86\u591a\u5c11\u4eba\u5de5\uff1f\u8017\u8d39\u4e86\u591a\u5c11\u6750 \u6599\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1b\u201c\u738b\u8001\u5f1f\u5fc5\u5b9a\u6e05\u695a\u4e86\uff1f\u201d \u3000\u3000\u201c\u4ffa\u5c31\u662f\u56e0\u4e3a\u4e0d\u6e05\u695a\uff0c\u6240\u4ee5\u624d\u8981\u95ee\u95ee\u76df\u4e3b\u3002\u201d \u3000\u3000\u201c\u738b\u8001\u5f1f\u95ee\u8fd9\u4e2a\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u4ffa\u60f3\u77e5\u9053\u67cf\u9f84\u9662\u662f\u4e0d\u662f\u98ce\u6c34\u4e0d\u597d\uff0c\u4e0d\u7136\u4e3a\u4ec0\u4e48\u8981\u70e7\u6389\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u618b\u5f97\u8fde\u8fde\u54b3\u55fd\u4e86\u51e0\u58f0\uff0c\u5374\u6ca1\u7b54\u8154\u3002 \u3000\u3000\u738b\u5927\u5eb7\u518d\u9053\uff1a\u201c\u4ffa\u60f3\u8bf7\u76df\u4e3b\u6765\u5403\u4e00\u987f\u522b\u5177\u98ce\u5473\u7684\u5927\u83dc\uff0c\u53ef\u60dc\u6ca1\u529e\u6cd5\u8bf7\u4e86\uff01\u201d \u3000\u3000\u201c\u738b\u8001\u5f1f\u8fd9\u8bdd\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u201c\u542c\u8bf4\u4eba\u8089\u6700\u597d\u5403\uff0c\u82e5\u70e7\u4e86\u67cf\u9f84\u9662\uff0c\u4e00\u5b9a\u4f1a\u70e7\u51fa\u5f88\u591a\u4eba\u8089\u6765\uff0c\u5230\u90a3\u65f6\u5019\u5c82\u4e0d\u53ef\u4ee5\u8bf7\u4f60\u8001 \u4eba\u5bb6\u5403\u4e00\u987f\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u54ea\u91cc\u8fd8\u80fd\u518d\u8bf4\u4ec0\u4e48\uff0c\u5728\u949f\u4e00\u8c6a\u7684\u524d\u5bfc\u4e0b\uff0c\u518d\u8d70\u5411\u67cf\u9f84\u9662\u4e1c\u4fa7\u3002 \u3000\u3000\u4e1c\u4fa7\u7684\u60c5\u5f62\u4e5f\u662f\u4e00\u6837\u3002 \u3000\u3000\u63a5\u7740\u5230\u5317\u4fa7\uff0e\u8d1f\u8d23\u770b\u5b88\u73b0\u573a\u7684\u662f\u5d02\u5c71\u4e09\u96c4\u7684\u662f\u9c8d\u8d85\u548c\u82d7\u7d20\u82d3\u3002 \u3000\u3000\u82d7\u7d20\u82d3\u867d\u7136\u4ecd\u7740\u7537\u88c5\uff0c\u552f\u6050\u9732\u51fa\u7834\u7efd\uff0c\u4e0d\u5f97\u4e0d\u6545\u610f\u7ad9\u8fdc\u4e9b\u3002 \u3000\u3000\u5c82\u77e5\u970d\u5143\u4f3d\u773c\u5c16\uff0c\u8fd8\u662f\u6123\u4e00\u4e0b\u95ee\u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u8c01\uff1f\u201d \u3000\u3000\u9c8d\u8d85\u5fd9\u62a2\u7740\u7b54\u9053\uff1a\u201c\u8fd9\u4f4d\u5c0f\u5144\u5f1f\u662f\u67cf\u9f84\u9662\u7684\u4e00\u540d\u8b66\u536b\u55bd\u5175\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u4e24\u773c\u8f6c\u4e86\u51e0\u8f6c\u9053\uff1a\u201c\u8001\u592b\u597d\u50cf\u6ca1\u89c1\u8fc7\u3002\u201d \u3000\u3000\u9c8d\u8d85\u9053\uff1a\u201c\u8ff7\u8e2a\u8c37\u4eba\u6570\u4e0a\u5343\uff0c\u76df\u4e3b\u5c31\u662f\u89c1\u8fc7\uff0e\u4e5f\u7edd\u4e0d\u53ef\u80fd\u5168\u8ba4\u8bc6\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u4e3a\u4e86\u8868\u793a\u98ce\u5ea6\uff0c\u5e76\u672a\u7ee7\u7eed\u76d8\u95ee\uff0c\u518d\u5411\u897f\u8fb9\u8d70\u53bb\u3002 \u3000\u3000\u5f53\u518d\u56de\u5230\u524d\u95e8\uff0c\u970d\u5143\u4f3d\u9053\uff1a\u201c\u65f6\u95f4\u4e0d\u65e9\u4e86\uff0c\u526f\u76df\u4e3b\u548c\u4f59\u5148\u751f\u3001\u9ea6\u8001\u5f1f\u5c31\u8bf7\u5148\u8ba9\u5916\u9762\u7684\u4eba \u56de\u53bb\u5c31\u5bdd\uff0c\u738b\u660e\u7b49\u4eba\u7531\u8001\u592b\u5e26\u56de\u53bb\uff0c\u660e\u5929\u5fc5\u5b9a\u4f1a\u7ed9\u67cf\u9f84\u9662\u4e00\u4e2a\u4ea4\u4ee3\u3002\u201d \u3000\u3000\u949f\u2014\u8c6a\u9053\uff1a\u201c\u8fd9\u6837\u7684\u5927\u4e8b\uff0c\u600e\u53ef\u7b49\u5230\u660e\u5929\uff1f\u201d \u3000\u3000\u201c\u526f\u76df\u4e3b\u53ef\u6709\u4ec0\u4e48\u9ad8\u89c1\uff1f\u201d \u3000\u3000\u201c\u5e26\u7740\u4ed6\u4eec\u4e94\u4e2a\u518d\u5230\u76df\u4e3b\u90a3\u91cc\uff0c\u4eca\u665a\u975e\u67e5\u4e2a\u6c34\u843d\u77f3\u51fa\u4e0d\u53ef\uff01\u201d \u3000\u3000\u5728\u8fd9\u79cd\u60c5\u5f62\u4e0b\uff0c\u970d\u5143\u4f3d\u53c8\u600e\u80fd\u62d2\u7edd\uff0c\u53ea\u597d\u5e26\u7740\u4e00\u884c\u4eba\u4f17\uff0c\u53c8\u56de\u5230\u5929\u661f\u9662\u3002 \u3000\u3000\u8fdb\u5165\u5929\u661f\u9662\u5ba2\u5385\uff0c\u970d\u5143\u4f3d\u5927\u559d\u738b\u660e\u7b49\u4eba\u9053\uff1a\u201c\u4f60\u4eec\u8fd9\u4e9b\u6df7\u86cb\uff0e\u6628\u665a\u7684\u4e8b\uff0c\u7a76\u7adf\u662f\u8c01\u6307\u4f7f \u7684\uff1f\u201d \u3000\u3000\u738b\u660e\u7b49\u4e00\u8d77\u8dea\u5728\u5730\u4e0a\uff0c\u98a4\u58f0\u9053\uff1a\u201c\u662f\u2026\u2026\u662f\u9ad8\u6765\u9ad8\u5934\u76ee\u903c\u6211\u4eec\u5e72\u7684\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u731b\u4e00\u8dfa\u811a\uff0c\u6012\u4e0d\u53ef\u904f\u7684\u9053\uff1a\u201c\u9ad8\u6765\u8fd9\u6df7\u86cb\uff0c\u5c45\u7136\u505a\u51fa\u8fd9\u79cd\u4e8b\u6765\uff01\u201d \u3000\u3000\u949f\u4e00\u8c6a\u51b7\u7b11\u9053\uff1b\u201c\u96be\u9053\u76df\u4e3b\u4e5f\u8ba4\u4e3a\u662f\u9ad8\u6765\u5e72\u7684\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1b\u201c\u96be\u9053\u4ed6\u4eec\u8bf4\u7684\u8fd8\u4f1a\u6709\u9519\uff1f\u201d \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u53ea\u6015\u4e8b\u60c5\u4e0d\u662f\u8fd9\u4e48\u7b80\u5355\u7684\uff0e\u9ad8\u6765\u6709\u4ec0\u4e48\u6743\u5a01\u6307\u4f7f\u90a3\u4e48\u591a\u4eba\u7684\u884c\u52a8\uff1f\u518d\u8bf4\u4ed6 \u548c\u67cf\u9f84\u9662\u53c8\u54ea\u91cc\u6765\u7684\u8fd9\u4e48\u5927\u7684\u4ec7\u6068\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u53cd\u95ee\u9053\uff1a\u201c\u526f\u76df\u4e3b\u8ba4\u4e3a\u8c01\u548c\u67cf\u9f84\u9662\u6709\u8fd9\u4e48\u5927\u7684\u4ec7\u6068\u5462\uff1f\u201d \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u4e8b\u60c5\u662f\u8c01\u505a\u7684\uff0c\u8c01\u5e94\u5f53\u5fc3\u91cc\u6709\u6570\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u4e24\u592a\u9633\u7a74\u731b\u7136\u62bd\u6410\u4e86\u51e0\u4e0b\uff0c\u5374\u53c8\u7a33\u4f4f\uff0c\u63a5\u7740\u5429\u5490\u5df4\u5929\u4e49\u9053\uff1a\u201c\u6d3e\u4eba\u628a\u9ad8\u6765\u53eb\u6765\uff01\u201d \u3000\u3000\u5df4\u5929\u4e49\u8d77\u8eab\u800c\u53bb\u3002 \u3000\u3000\u970d\u5143\u4f3d\u559d\u4e86\u53e3\u8336\u9053\uff1a\u201c\u526f\u76df\u4e3b\u6700\u597d\u80fd\u628a\u5fc3\u60c5\u5e73\u9759\u4e0b\u6765\uff0c\u8001\u592b\u4e00\u5b9a\u4f1a\u628a\u4e8b\u60c5\u67e5\u4e2a\u6c34\u843d\u77f3 \u51fa\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u51b7\u7b11\u4e86\u51e0\u58f0\u9053\uff1a\u201c\u5982\u679c\u6c34\u843d\u77f3\u4e0d\u51fa\u53c8\u8be5\u600e\u4e48\u529e\u5462\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u9ea6\u5c0f\u5144\u5f1f\u7a0d\u5b89\u52ff\u8e81\uff0c\u5f85\u4f1a\u513f\u9ad8\u6765\u5230\u6765\uff0c\u5c31\u662f\u6c34\u843d\u77f3\u51fa\u7684\u65f6\u523b\u3002\u201d \u3000\u3000\u201c\u521a\u624d\u949f\u526f\u76df\u4e3b\u5df2\u7ecf\u8bf4\u8fc7\uff0c\u9ad8\u6765\u6ca1\u8d44\u683c\u505a\u8fd9\u79cd\u4e8b\uff0c\u4f60\u4e3a\u4ec0\u4e48\u628a\u8d23\u4efb\u63a8\u5728\u9ad8\u6765\u8eab\u4e0a\uff1f\u201d \u3000\u3000\u201c\u65e2\u7136\u738b\u660e\u8bf4\u662f\u9ad8\u6765\u53eb\u4ed6\u505a\u7684\uff0c\u8981\u628a\u9ad8\u6765\u627e\u6765\u67e5\u95ee\uff0e\u201d \u3000\u3000\u201c\u67e5\u95ee\u4e0d\u51fa\u5934\u7eea\u53c8\u8be5\u600e\u4e48\u529e\u5462\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u8981\u7ee7\u7eed\u5411\u4e0b\u8ffd\uff0e\u5982\u679c\u4e0d\u662f\u9ad8\u6765\uff0c\u4ed6\u5fc5\u5b9a\u4f1a\u54ac\u51fa\u53e6\u5916\u7684\u4eba\uff0c\u5982\u6b64\u4e00\u6ce2\u4e00\u6ce2\u8ffd\u4e0b\u53bb\uff0e \u6700\u540e\u603b\u80fd\u627e\u5230\u4e3b\u8c0b\u5143\u51f6\u3002\u201d \u3000\u3000\u76cf\u8336\u5de5\u592b\u4e4b\u540e\uff0c\u5df4\u5929\u4e49\u5df2\u4eb2\u81ea\u62bc\u7740\u9ad8\u6765\u8fdb\u5165\u5ba2\u5385\u3002 \u3000\u3000\u970d\u5143\u4f3d\u55d4\u76ee\u5927\u559d\u9053\uff1a\u201c\u9ad8\u6765\uff0c\u706b\u70e7\u67cf\u9f84\u9662\u7684\u4e8b\uff0c\u53ef\u662f\u4f60\u7684\u4e3b\u8c0b\uff1f\u201d \u3000\u3000\u5728\u949f\u4e00\u8c6a\u7b49\u4eba\u7684\u9884\u6599\uff0c\u9ad8\u6765\u5fc5\u4f1a\u5f53\u573a\u558a\u51a4\uff0c\u8c01\u77e5\u4ed6\u7adf\u6beb\u65e0\u754f\u60e7\u7684\u7b54\u9053\uff1a\u201c\u662f\u5c5e\u4e0b\u6d3e\u4eba\u5e72 \u7684\uff0c\u5982\u679c\u9519\u4e86\uff0c\u5c5e\u4e0b\u613f\u9886\u53d7\u7f6a\u540d\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u51b7\u7b11\u9053\uff1a\u201c\u6df7\u86cb\uff01\u706b\u70e7\u67cf\u9f84\u9662\uff0c\u4f60\u9519\u5f97\u5df2\u7ecf\u592a\u79bb\u8c31\uff0c\u8fd8\u8bf4\u5982\u679c\u9519\u4e86\uff0e\u4f60\u8fd9\u6df7\u86cb\u5b9e \u5728\u7f6a\u8be5\u4e07\u6b7b\uff0c\u4f60\u662f\u5426\u53d7\u4ed6\u4eba\u4e3b\u4f7f\uff1f\u201d \u3000\u3000\u9ad8\u6765\u987f\u4e86\u987f\u9053\uff1a\u201c\u4e8b\u60c5\u5b8c\u5168\u662f\u5c5e\u4e0b\u4e00\u4eba\u8d77\u610f\uff0c\u5e76\u6ca1\u5916\u4eba\u4e3b\u4f7f\u3002\u201d \u3000\u3000\u201c\u4f60\u4e3a\u4ec0\u4e48\u8981\u505a\u51fa\u8fd9\u6837\u80c6\u5927\u5305\u5929\u7684\u4e8b\uff1f\u201d \u3000\u3000\u201c\u5c5e\u4e0b\u770b\u4e0d\u60ef\u67cf\u9f84\u9662\u90a3\u6279\u4eba\uff0e\u201d \u3000\u3000\u201c\u80e1\u8bf4\uff01\u67cf\u9f84\u9662\u5168\u662f\u81ea\u5df1\u4eba\uff0c\u800c\u4e14\u8fd8\u6709\u949f\u526f\u76df\u4e3b\u5728\u5185\uff0c\u4f60\u5bf9\u81ea\u5df1\u4eba\u6709\u4ec0\u4e48\u770b\u4e0d\u60ef\uff1f\u201d \u3000\u3000\u201c\u4ed6\u4eec\u592a\u8dcb\u6248\uff0c\u6839\u672c\u6ca1\u628a\u76df\u4e3b\u77a7\u5728\u773c\u91cc\uff0c\u5c5e\u4e0b\u662f\u5fe0\u4e8e\u76df\u4e3b\u7684\u4eba\uff0c\u600e\u80fd\u770b\u5f97\u60ef\u3002\u201d \u3000\u3000\u201c\u4f60\u53ef\u77e5\u9053\u72af\u4e0b\u4e86\u4ec0\u4e48\u7f6a\u540d\uff1f\u201d \u3000\u3000\u201c\u5c5e\u4e0b\u6562\u505a\u6562\u5f53\uff0c\u95ee\u5fc3\u65e0\u6127\uff0e\u65e2\u7136\u5df2\u88ab\u67e5\u51fa\uff0c\u60c5\u613f\u4e00\u6b7b\uff01\u201d \u3000\u3000\u9ad8\u6765\u8bf4\u5b8c\u8bdd\u540e\uff0c\u7a81\u7136\u731b\u4e00\u54ac\u7259\uff0c\u5f20\u53e3\u55b7\u51fa\u4e00\u53e3\u9c9c\u8840\uff0c\u7adf\u7136\u5012\u5730\u6c14\u7edd\u3002 \u3000\u3000\u539f\u6765\u4ed6\u5df2\u54ac\u820c\u800c\u6b7b\u3002 \u3000\u3000\u8fd9\u5206\u660e\u662f\u4e8b\u5148\u5df2\u5b89\u6392\u597d\u7684\u2014\u5e55\u5267\u3002 \u3000\u3000\u970d\u5143\u4f3d\u54fc\u4e00\u58f0\u9053\uff1a\u201c\u8fd9\u6df7\u86cb\u6b7b\u6709\u4f59\u8f9c\uff01\u201d \u3000\u3000\u63a5\u7740\u671b\u5411\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u526f\u76df\u4e3b\uff0e\u4f60\u4eec\u4e09\u4f4d\u90fd\u5728\u573a\uff0c\u4e8b\u60c5\u603b\u7b97\u67e5\u660e\u767d\u4e86\uff0c\u6240\u5e78\u9ad8\u6765\u8fd9\u6df7\u86cb \u9884\u8c0b\u672a\u6210\uff0c\u73b0\u5728\u8be5\u56de\u53bb\u4f11\u606f\u4e86\uff0e\u201d \u3000\u3000\u949f\u4e00\u8c6a\u5c1a\u672a\u5f00\u53e3\uff0c\u9ea6\u5c0f\u660e\u5df2\u62a2\u7740\u8bf4\uff1a\u201c\u970d\u76df\u4e3b\uff0c\u96be\u9053\u8fd9\u6837\u5c31\u7b97\u5bf9\u6211\u4eec\u4ea4\u4ee3\u4e86\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u8138\u8272\u4e00\u53d8\u9053\uff1a\u201c\u9ad8\u6765\u5df2\u6b7b\uff0c\u5982\u679c\u9ea6\u8001\u5f1f\u8fd8\u6c14\u6124\u4e0d\u8fc7\uff0c\u53ea\u7ba1\u628a\u4ed6\u788e\u5c38\u4e07\u6bb5\uff0c\u4f60\u8ba9\u8001 \u592b\u8fd8\u8981\u600e\u6837\u4ea4\u4ee3\uff1f\u201d \u3000\u3000\u201c\u4e8b\u60c5\u6050\u6015\u4e0d\u662f\u8fd9\u4e48\u7b80\u5355\u7684\u5427\uff1f\u6211\u4eec\u5fc3\u91cc\u6709\u6570\uff0c\u4f60\u4e5f\u8be5\u5fc3\u91cc\u6709\u6570\u3002\u201d \u3000\u3000\u201c\u4f60\u8bf4\u8001\u592b\u6709\u4ec0\u4e48\u6570\uff1f\u201d \u3000\u3000\u201c\u9ad8\u6765\u4e0d\u8fc7\u662f\u4e2a\u66ff\u6b7b\u9b3c\uff0c\u4f60\u6839\u672c\u6ca1\u627e\u51fa\u771f\u6b63\u4e3b\u8c0b\u4e4b\u4eba\uff01\u201d \u3000\u3000\u201c\u5982\u679c\u9ea6\u8001\u5f1f\u8ba4\u4e3a\u9ad8\u6765\u4e0d\u662f\u4e3b\u8c0b\uff0c\u65f6\u95f4\u8fd8\u591a\u5f97\u5f88\uff0c\u8001\u592b\u5c31\u8bf7\u4f60\u6765\u8c03\u67e5\u5982\u4f55\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u521a\u8981\u62d4\u5251\u76f8\u5411\uff0c\u5374\u88ab\u4f59\u4ea6\u4e50\u62c9\u4f4f\u9053\uff1a\u201c\u9ea6\u5c0f\u5144\u5f1f\u7528\u4e0d\u7740\u548c\u76df\u4e3b\u4e89\u5435\uff0c\u76df\u4e3b\u8bf4\u5f97\u5bf9\uff0c \u65f6\u95f4\u8fd8\u591a\u5f97\u5f88\uff0c\u5373\u4f7f\u4e3b\u8c0b\u5e76\u975e\u9ad8\u6765\uff0c\u5c06\u6765\u603b\u4f1a\u67e5\u4e2a\u6c34\u843d\u77f3\u51fa\u3002\u201d \u3000\u3000\u949f\u2014\u8c6a\u4e5f\u7d27\u8ddf\u7740\u9053\uff1a\u201c\u54b1\u4eec\u8d70\uff01\u201d \u3000\u3000\u79bb\u5f00\u5929\u661f\u9662\uff0c\u9ea6\u5c0f\u660e\u6124\u6124\u7684\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4f4d\u4e3a\u4ec0\u4e48\u8fd9\u6837\u5e72\u8106\u5c31\u8d70\uff1f\u4e8b\u60c5\u660e\u660e\u53e6\u6709\u4e3b\u8c0b\uff0c \u96be\u9053\u5c31\u8fd9\u6837\u7b97\u4e86\uff1f\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u5401\u53e3\u6c14\u9053\uff1a\u201c\u5982\u679c\u4e3b\u8c0b\u662f\u970d\u5143\u4f3d\uff0c\u4f60\u53c8\u8be5\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u90a3\u5c31\u548c\u4ed6\u5f53\u573a\u62fc\u4e86\u3002\u4ee5\u65b9\u624d\u7684\u60c5\u52bf\u800c\u8bba\uff0c\u54b1\u4eec\u4e09\u4eba\u90fd\u4f69\u5e26\u7740\u5175\u5203\uff0c\u970d\u5143\u4f3d \u548c\u5df4\u5929\u4e49\u5b8b\u5929\u94ce\u90fd\u662f\u8d64\u624b\u7a7a\u62f3\uff0c\u82e5\u62fc\u6218\u8d77\u6765\uff0c\u5f53\u573a\u628a\u4ed6\u9664\u53bb\uff0c\u5e76\u975e\u96be\u4e8b\uff0c\u5931\u6389\u8fd9\u673a\u4f1a\uff0c\u5b9e\u5728 \u53ef\u60dc\uff01\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u4e0d\u4ee5\u4e3a\u7136\u9053\uff1a\u201c\u54b1\u4eec\u4e0d\u80fd\u8fd9\u6837\u83bd\u649e\uff0e\u51e1\u4e8b\u5fc5\u987b\u4ece\u8fdc\u5904\u770b\u3002\u201d \u3000\u3000\u201c\u4f60\u8fd9\u8bdd\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u201c\u82e5\u521a\u624d\u771f\u548c\u970d\u5143\u4f3d\u95f9\u7ffb\u62fc\u6218\u8d77\u6765\uff0c\u54b1\u4eec\u56fa\u7136\u80dc\u7b97\u5f88\u5927\uff0c\u4f46\u5374\u5e76\u975e\u7edd\u5bf9\u7684\uff0c\u4e07\u4e00\u2026\u2026\u201d \u3000\u3000\u201c\u54ea\u6709\u4ec0\u4e48\u4e07\u4e00\uff1f\u201d \u3000\u3000\u201c\u6211\u60f3\u4f60\u90a3\u56db\u4f4d\u670d\u8fc7\u2018\u5411\u5fc3\u9732\u2019\u88ab\u4ed6\u63a7\u5236\u7684\u5e08\u5144\uff0c\u5f88\u53ef\u80fd\u4e5f\u5728\u5929\u661f\u9662\u91cc\uff0c\u4e00\u65e6\u4ed6\u4eec\u51fa\u6765 \u52a9\u9635\uff0c\u54b1\u4eec\u5c82\u80fd\u5e94\u4ed8\u5f97\u4e86\uff1f\u518d\u8bf4\u8ff7\u8e2a\u8c37\u7684\u4e0a\u5343\u4eba\uff0c\u7edd\u5927\u591a\u6570\u90fd\u662f\u970d\u5143\u4f3d\u7684\u5fc3\u8179\u624b\u4e0b\uff0c\u54b1\u4eec\u7eb5 \u7136\u6740\u4e86\u970d\u5143\u4f3d\uff0c\u4e5f\u5f88\u96be\u5f97\u5230\u4ed6\u4eec\u7684\u62e5\u6234\uff0c\u5230\u90a3\u65f6\u5c82\u4e0d\u5f04\u5f97\u5929\u4e0b\u5927\u4e71\uff1f\u201d \u3000\u3000\u949f\u4e00\u8c6a\u4e5f\u9053\uff1a\u201c\u4f59\u5144\u8bf4\u5f97\u5bf9\uff0c\u54b1\u4eec\u867d\u7136\u5e0c\u671b\u80fd\u4ece\u970d\u5143\u4f3d\u624b\u91cc\u593a\u56de\u76df\u4e3b\u4e4b\u4f4d\uff0c\u4e5f\u8be5\u4ee5\u6b63\u5927 \u5149\u660e\u7684\u624b\u6bb5\u8fdb\u884c\uff0c\u8fd9\u624d\u80fd\u8ba9\u4eba\u5fc3\u670d\u3002\u82e5\u4e58\u4eba\u4e4b\u5371\u884c\u4e8b\uff0c\u53cd\u800c\u8981\u843d\u4e2a\u4e0d\u4ec1\u4e0d\u4e49\u4e0d\u9f7f\u4e4b\u540d\uff0c\u8fd9\u548c \u2018\u7be1\u4f4d\u2019\u6709\u4f55\u5206\u522b\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u89c1\u4ed6\u4eec\u4e24\u4eba\u8bf4\u7684\u5e76\u975e\u65e0\u7406\uff0c\u4e5f\u5c31\u4e0d\u518d\u8a00\u8bed\u3002 \u3000\u3000\u56de\u5230\u67cf\u9f84\u9662\uff0c\u6d3e\u51fa\u628a\u5b88\u73b0\u573a\u7684\u7fa4\u8c6a\uff0c\u4e5f\u90fd\u9646\u7eed\u56de\u6765\uff0c\u5927\u5bb6\u805a\u96c6\u5728\u5ba2\u5385\uff0c\u90fd\u5411\u949f\u4e00\u8c6a\u7b49\u4e09 \u4eba\u95ee\u957f\u95ee\u77ed\uff0e\u4ee5\u4fbf\u5f97\u77e5\u949f\u4e00\u8c6a\u7b49\u4e09\u4eba\u5728\u5929\u661f\u9662\u7684\u7ecf\u8fc7\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u5f53\u5373\u628a\u4e8b\u60c5\u8bf4\u4e86\u4e00\u904d\u3002 \u3000\u3000\u738b\u5927\u5eb7\u543c\u9053\uff1a\u201c\u4ed6\u5976\u5976\u7684\uff01\u4e8b\u60c5\u5206\u660e\u662f\u970d\u5143\u4f3d\u90a3\u8001\u738b\u516b\u86cb\u5e72\u7684\uff0c\u4ed6\u7adf\u628a\u8d23\u4efb\u5168\u63a8\u5728\u4e00\u4e2a \u5c0f\u5934\u76ee\u8eab\u4e0a\uff0c\u8d8b\u7740\u73b0\u5728\u591c\u6df1\u4eba\u9759\uff0c\u54b1\u4eec\u5e72\u8106\u6740\u8fdb\u5929\u661f\u9662\uff0c\u5148\u5bb0\u4e86\u90a3\u8001\u738b\u516b\u86cb\u518d\u8bf4\uff01\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u9053\uff1a\u201c\u738b\u5144\u6700\u597d\u7a0d\u5b89\u52ff\u8e81\uff0c\u65b9\u624d\u9ea6\u5c0f\u5144\u5f1f\u4f55\u5c1d\u6ca1\u6709\u8fd9\u79cd\u60f3\u6cd5\uff0c\u4f46\u8fd9\u662f\u4ef6\u5927\u4e8b\uff0c\u5b9e \u5728\u83bd\u649e\u4e0d\u5f97\uff0e\u201d \u3000\u3000\u738b\u5927\u5eb7\u4e0d\u670d\u9053\uff1a\u201c\u4f60\u4eec\u8bfb\u4e66\u4eba\uff0c\u505a\u4e8b\u603b\u662f\u5a46\u5a46\u5988\u5988\uff0c\u987e\u8651\u592a\u591a\uff0c\u6839\u672c\u6210\u4e0d\u4e86\u5927\u4e8b\uff01\u201d \u3000\u3000\u6d2a\u6cfd\u5fd9\u9053\uff1b\u201c\u8001\u4e09\u5c11\u8bf4\u4e24\u53e5\u597d\u4e0d\u597d\uff1f\u949f\u526f\u76df\u4e3b\u548c\u4f59\u5148\u751f\u96be\u9053\u8fd8\u8d76\u4e0d\u4e0a\u4f60\uff01\u201d \u3000\u3000\u738b\u5927\u5eb7\u8038\u8038\u80a9\u9053\uff1a\u201c\u597d\u4e86\u597d\u4e86\uff01\u4ffa\u4e0d\u8bf4\u5c31\u4e0d\u8bf4\u3002\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u5ffd\u7136\u6df1\u6df1\u4e00\u53f9\u9053\uff1a\u201c\u526f\u76df\u4e3b\uff0c\u4e8b\u60c5\u662f\u970d\u5143\u4f3d\u5e72\u7684\uff0c\u5df2\u65e0\u5eb8\u7f6e\u7591\u3002\u4ee5\u4f59\u67d0\u7684\u9884\u6599\uff0c \u7ecf\u8fc7\u8fd9\u756a\u53d8\u6545\u540e\uff0c\u970d\u5143\u4f3d\u4e0e\u54b1\u4eec\u4e4b\u95f4\u5df2\u662f\u6c34\u706b\u96be\u5bb9\u3002\u4ed6\u6b64\u8ba1\u4e0d\u6210\uff0c\u5fc5\u751f\u4e8c\u8ba1\u3002\u957f\u6b64\u4ee5\u5f80\uff0c\u54b1 \u4eec\u67cf\u9f84\u9662\u7ec8\u5fc5\u96be\u9003\u4e00\u52ab\u3002\u201d \u3000\u3000\u949f\u4e00\u8c6a\u4e5f\u9053\uff1a\u201c\u949f\u67d0\u4e5f\u65e9\u5c31\u6599\u5230\u4ee5\u540e\u7684\u65e5\u5b50\u5fc5\u5b9a\u8d8a\u6765\u8d8a\u4e0d\u597d\u8fc7\uff0c\u79f0\u5f97\u4e0a\u662f\u5371\u673a\u56db\u4f0f\uff0c\u671d \u4e0d\u4fdd\u5915\uff0e\u66fe\u591a\u6b21\u5fc3\u7070\u610f\u51b7\uff0c\u6709\u610f\u79bb\u5f00\u8ff7\u8e2a\u8c37\uff0e\u53e6\u8c0b\u4ed6\u5c31\uff0c\u4f46\u6700\u540e\u8fd8\u662f\u51b3\u5b9a\u575a\u5b88\u4e0b\u53bb\uff0e\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4f60\u53ef\u662f\u5929\u4e0b\u7eff\u6797\u603b\u5be8\u526f\u76df\u4e3b\u7684\u763e\u5934\u8fd8\u6ca1\u8fc7\u5b8c\uff1f\u201d \u3000\u3000\u949f\u4e00\u8c6a\u82e6\u7b11\u9053\uff1a\u201c\u9ea6\u5c0f\u5144\u5f1f\u60f3\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u90a3\u4f60\u4e3a\u4ec0\u4e48\u7559\u604b\u4e0d\u53bb\u5462\uff1f\u201d \u3000\u3000\u949f\u2014\u8c6a\u795e\u8272\u8f6c\u4e3a\u575a\u5b9a\u9053\uff1a\u201c\u8ff7\u8e2a\u8c37\u662f\u80e1\u3001\u8c37\u4e24\u4f4d\u524d\u76df\u4e3b\u4e00\u624b\u521b\u7acb\u7684\u57fa\u4e1a\uff0c\u54b1\u4eec\u867d\u7136\u65e0\u80fd \u53d1\u626c\u5149\u5927\uff0c\u81f3\u5c11\u5e94\u8be5\u7559\u5728\u8fd9\u91cc\uff0c\u575a\u5b88\u5c97\u4f4d\uff0c\u5982\u679c\u80e1\uff0e\u8c37\u4e24\u4f4d\u524d\u4efb\u76df\u4e3b\u5728\u5929\u6709\u7075\uff0c\u4e5f\u8be5\u8ba9\u4ed6\u4eec \u77e5\u9053\u8ff7\u8e2a\u8c37\u4ecd\u7136\u6709\u4ed6\u4eec\u7684\u65e7\u90e8\u3002\u5018\u82e5\u54b1\u4eec\u6492\u624b\u800c\u53bb\uff0c\u5982\u4f55\u5bf9\u5f97\u4f4f\u4e24\u4f4d\u76df\u4e3b\u5728\u5929\u4e4b\u7075\uff1f\u201d \u3000\u3000\u8fd9\u756a\u8bdd\u6653\u5f97\u60c5\u6073\u610f\u5207\uff0c\u4ee4\u5728\u573a\u6240\u6709\u7684\u4eba\uff0c\u90fd\u5927\u4e3a\u611f\u52a8\u3002 \u3000\u3000\u5ba2\u5385\u5185\u6c89\u5bc2\u4e86\u751a\u4e45\uff0c\u624d\u542c\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4eca\u665a\u7684\u4e8b\uff0c\u4f60\u4eec\u5927\u5bb6\u90fd\u8be5\u6028\u6211\u3002\u662f\u6211\u60f9\u4e0b\u7684\u7978\uff0c \u6211\u5f88\u62b1\u6b49\uff0c\u5bf9\u4e0d\u4f4f\u4f60\u4eec\u5927\u5bb6\uff01\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u76b1\u76b1\u7709\u5934\u9053\uff1a\u201c\u9ea6\u5c0f\u5144\u5f1f\u600e\u4e48\u5ffd\u7136\u8bf4\u51fa\u8fd9\u79cd\u8bdd\u6765\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u5982\u679c\u4e0d\u662f\u6628\u665a\u5728\u4e1c\u6d77\u9662\u540e\u9762\u6811\u6797\u91cc\u6740\u4e86\u6c5f\u5357\u4e09\u602a\u548c\u6731\u6b63\uff0c\u970d\u5143\u4f3d\u7edd\u5bf9\u4e0d\u4f1a \u6709\u4eca\u665a\u7684\u884c\u52a8\uff0c\u8fd9\u6837\u770b\u6765\uff0c\u6211\u5c82\u4e0d\u662f\u7f6a\u9b41\u7978\u9996\u3002\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u966a\u7740\u7b11\u9053\uff1a\u201c\u50cf\u6628\u665a\u7684\u60c5\u5f62\uff0c\u4e0d\u8bba\u662f\u8c01\u4e5f\u5fc5\u987b\u6740\u4e86\u4ed6\u4eec\u706d\u53e3\u3002\u4f60\u80fd\u628a\u4ed6\u4eec\u56db\u4e2a\u4e00 \u8d77\u6740\u6b7b\uff0c\u5e76\u6ca1\u6f0f\u7f51\u4e00\u4e2a\uff0c\u8be5\u662f\u4e00\u573a\u5927\u529f\u624d\u5bf9\u3002\u5426\u5219\uff0c\u970d\u5143\u4f3d\u5c3d\u53ef\u660e\u76ee\u5f20\u80c6\u6765\u5bf9\u4ed8\u54b1\u4eec\uff0c\u66f4\u4f55 \u51b5\u5f97\u5230\u793a\u8b66\u7684\u4e5f\u662f\u4f60\uff0c\u6240\u4ee5\u624d\u907f\u514d\u4e86\u8fd9\u573a\u5927\u7978\u3002\u5982\u6b64\u770b\u6765\uff0c\u67cf\u9f84\u9662\u6240\u6709\u7684\u4eba\uff0e\u8fd8\u5e94\u8be5\u611f\u6fc0\u4f60 \u624d\u662f\uff0c\u4f60\u600e\u4e48\u53cd\u800c\u81ea\u8d23\u8d77\u6765\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4f59\u5148\u751f\u771f\u4f1a\u8bb2\u8bdd\uff0c\u53cd\u800c\u79f0\u8d5e\u8d77\u6211\u6765\u4e86\uff01\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u9053\uff1a\u201c\u4e8b\u60c5\u672c\u6765\u5982\u6b64\uff0c\u6211\u4e0d\u8fc7\u662f\u85c9\u6b64\u673a\u4f1a\u8bf4\u660e\u7f62\u4e86\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9ed8\u4e86\u4e00\u9ed8\u9053\uff1a\u201c\u521a\u624d\u4f60\u8bf4\u8fc7\uff0c\u54b1\u4eec\u7559\u5728\u8ff7\u8e2a\u8c37\uff0c\u5c06\u6765\u603b\u6709\u4e00\u5929\u4f1a\u906d\u5230\u970d\u5143\u4f3d\u7684\u9634 \u8c0b\u6bd2\u8ba1\uff0c\u504f\u504f\u949f\u526f\u76df\u4e3b\u53c8\u4e0d\u80af\u8d70\uff0c\u4f60\u8bf4\u8be5\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u9053\uff1a\u201c\u6211\u73b0\u5728\u60f3\u51fa\u2014\u4e2a\u53d8\u901a\u7684\u529e\u6cd5\uff0c\u4e0d\u77e5\u526f\u76df\u4e3b\u540c\u4e0d\u540c\u610f\uff1f\u201d \u3000\u3000\u949f\u4e00\u8c6a\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u4f59\u5144\u6709\u4ec0\u4e48\u53d8\u901a\u529e\u6cd5\uff1f\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u9053\uff1a\u201c\u8c01\u90fd\u77e5\u9053\uff0c\u970d\u5143\u4f3d\u5bf9\u54b1\u4eec\u662f\u5fc5\u6b32\u9664\u4e4b\u800c\u540e\u5feb\u3002\u4e3a\u4e86\u81ea\u8eab\u5b89\u5168\uff0c\u54b1\u4eec\u8fd8\u662f\u4ee5 \u79bb\u5f00\u8ff7\u8e2a\u8c37\u4e3a\u5999\u3002\u201d \u3000\u3000\u949f\u4e00\u8c6a\u6447\u5934\u9053\uff1a\u201c\u8bf4\u6765\u8bf4\u53bb\uff0c\u4f60\u8fd8\u662f\u8981\u529d\u6211\u8d70\uff1f\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u9053\uff1a\u201c\u8d70\u867d\u8d70\uff0c\u4f46\u4e0d\u5fc5\u8d70\u5f97\u592a\u8fdc\u3002\u201d \u3000\u3000\u201c\u8bf7\u6055\u6211\u542c\u4e0d\u61c2\u4f59\u5144\u7684\u8bdd\u3002\u201d \u3000\u3000\u201c\u6709\u4e00\u4e2a\u5730\u65b9\uff0c\u79bb\u8ff7\u8e2a\u8c37\u4e0d\u8fc7\u5341\u91cc\u5de6\u53f3\uff0e\u5f53\u5e74\u80e1\u524d\u76df\u4e3b\u66fe\u5e26\u7740\u54b1\u4eec\u5148\u5728\u90a3\u91cc\u4f4f\u8fc7\u4e00\u6bb5\u65f6 \u95f4\uff0c\u7136\u540e\u624d\u5f00\u59cb\u5efa\u7acb\u8ff7\u8e2a\u8c37\u7684\uff0c\u54b1\u4eec\u4e3a\u4ec0\u4e48\u4e0d\u518d\u56de\u5230\u90a3\u91cc\u53bb\uff1f\u201d \u3000\u3000\u201c\u4f59\u5144\u8bf4\u7684\u53ef\u662f\u4e07\u6708\u5ce1\uff1f\u201d \u3000\u3000\u201c\u4e0d\u9519\uff01\u4e07\u6708\u5ce1\u662f\u62bb\u97ad\u4e07\u6653\u5149\u8001\u524d\u8f88\u7684\u9690\u5c45\u4e4b\u6240\uff0c\u4e07\u8001\u524d\u8f88\u662f\u80e1\u524d\u76df\u4e3b\u7684\u4e49\u5144\uff0c\u4ed6\u53bb\u4e16 \u5df2\u7ecf\u4e09\u5e74\uff0c\u54b1\u4eec\u603b\u8be5\u5230\u4ed6\u548c\u4ed6\u7684\u592b\u4eba\u5893\u524d\u796d\u62dc\u796d\u62dc\u3002\u800c\u4e14\u4f4f\u5728\u90a3\u91cc\uff0c\u4e5f\u662f\u540d\u6b63\u8a00\u987a\uff0c\u6b63\u597d\u53ef \u4ee5\u66ff\u4ed6\u770b\u5b88\u57fa\u4e1a\u3002\u201d \u3000\u3000\u53ea\u542c\u6587\u5929\u751f\u9053\uff1a\u201c\u4f59\u5148\u751f\u7684\u63d0\u9192\uff0c\u665a\u8f88\u8d5e\u6210\uff0e\u665a\u8f88\u65e9\u5c31\u6709\u8fd9\u79cd\u60f3\u6cd5\uff0c\u53ea\u662f\u5728\u8bf8\u4f4d\u524d\u8f88\u9762 \u524d\uff0c\u4e00\u76f4\u4e0d\u6562\u63d0\u8d77\u3002 \u3000\u3000\u539f\u6765\u6587\u5929\u751f\u6b63\u662f\u4e07\u6653\u5149\u7684\u7231\u5f92\uff0e\u4ed6\u539f\u6765\u662f\u4e2a\u5b64\u513f\uff0c\u81ea\u5c0f\u5c31\u88ab\u4e07\u6653\u5149\u6536\u517b\uff0c\u5e76\u6388\u4ed6\u6b66\u827a\u3002 \u540e\u6765\u89c1\u4ed6\u548c\u7231\u5973\u4e07\u6620\u971e\u9752\u6885\u7af9\u9a6c\uff0c\u4e24\u5c0f\u65e0\u731c\uff0c\u4e34\u7ec8\u65f6\u53c8\u628a\u7231\u5973\u7684\u7ec8\u8eab\uff0c\u6258\u4ed8\u4e86\u6587\u5929\u751f\uff0c\u6587\u5929 \u751f\u611f\u5e08\u6069\uff0c\u81ea\u7136\u5e0c\u671b\u80fd\u56de\u5230\u4ed6\u81ea\u5c0f\u957f\u5927\u7684\u4e07\u6708\u5ce1\uff0e \u3000\u3000\u949f\u4e00\u8c6a\u89c1\u6b64\u60c5\u5f62\uff0c\u4e5f\u89c9\u5f97\u79bb\u5f00\u8ff7\u8e2a\u8c37\u53bb\u4e07\u6708\u5ce1\uff0c\u4e0d\u5931\u4e3a\u4e0a\u4e0a\u4e4b\u7b56\u3002\u5fc3\u610f\u65e2\u52a8\uff0c\u5fd9\u95ee\u6587\u5929 \u751f\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u9053\u76ee\u524d\u4e07\u6708\u5ce1\u8fd8\u6709\u4e9b\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u6587\u5929\u751f\u9053\uff1a\u201c\u636e\u665a\u8f88\u6240\u77e5\uff0c\u5bb6\u5e08\u5f53\u65e5\u7684\u7ba1\u5bb6\u674e\u8302\u5927\u53d4\u4ecd\u4f4f\u5728\u90a3\u91cc\uff0c\u53e6\u5916\u8fd8\u6709\u5341\u51e0\u540d\u4ec6\u5987 \u4e2b\u73af\uff0c\u53ef\u80fd\u4e5f\u6ca1\u8d70\u3002\u54b1\u4eec\u53bb\u5230\u90a3\u91cc\uff0c\u4e0d\u8bba\u5403\u4f4f\uff0c\u90fd\u5f88\u65b9\u4fbf\u3002\u201d \u3000\u3000\u949f\u4e00\u8c6a\u7acb\u5373\u76ee\u89c6\u5168\u573a\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u949f\u67d0\u4e5f\u540c\u610f\u5230\u4e07\u6708\u5ce1\u53bb\uff0c\u5404\u4f4d\u662f\u5426\u8fd8\u6709\u4ec0\u4e48\u610f \u89c1\uff1f\u201d \u3000\u3000\u6240\u6709\u7684\u4eba\uff0c\u90fd\u5f02\u53e3\u540c\u58f0\u8d5e\u6210\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u65e2\u7136\u5927\u5bb6\u90fd\u540c\u610f\uff0c\u4e8b\u4e0d\u5b9c\u8fdf\uff0c\u54b1\u4eec\u6700\u597d\u5c31\u5728\u4eca\u665a\u5929\u4eae\u524d\u79bb\u5f00\u8ff7\u8e2a\u8c37\u3002\u73b0\u5728 \u5404\u4f4d\u5c31\u8bf7\u56de\u623f\u6574\u7406\u4e1c\u897f\uff0c\u8d35\u91cd\u4e4b\u7269\u8981\u5168\u90e8\u5e26\u8d70\uff0e\u534a\u4e2a\u65f6\u8fb0\u4e4b\u540e\uff0c\u5c31\u5728\u5929\u4e95\u4f1a\u9f50\u51fa\u53d1\uff01\u201d \u3000\u3000\u4e8e\u662f\u5404\u4eba\u4e00\u54c4\u800c\u6563\u3002 \u3000\u3000\u534a\u4e2a\u65f6\u8fb0\u4e4b\u540e\uff0c\u7fa4\u8c6a\u679c\u7136\u5df2\u6574\u7406\u5b8c\u6bd5\uff0c\u5404\u4eba\u8fde\u5e26\u8d70\u4e4b\u7269\u4e00\u8d77\u62ff\u5230\u5929\u4e95\u3002 \u3000\u3000\u8fd9\u4e9b\u4eba\u5728\u8ff7\u8e2a\u8c37\u4f4f\u4e86\u4e09\u5e74\uff0c\u81ea\u7136\u4e1c\u897f\u4e0d\u5c11\u3002 \u3000\u3000\u5d02\u5c71\u4e09\u96c4\u3001\u6c5f\u5317\u4e09\u9f99\u548c\u6587\u5929\u751f\u4ee5\u53ca\u51e0\u540d\u5fc3\u8179\u55bd\u5175\uff0c\u90fd\u60c5\u613f\u6311\u62c5\u5b50\uff0c\u7531\u949f\u4e00\u8c6a\uff0e\u4f59\u4ea6\u4e50\uff0c \u9ea6\u5c0f\u660e\u3001\u82d7\u7d20\u82d3\u56db\u540d\u6b66\u529f\u6700\u9ad8\u7684\u8d1f\u8d23\u5f00\u8def\u548c\u65ad\u540e\uff0e \u3000\u3000\u738b\u5927\u5eb7\u9053\uff1a\u201c\u54b1\u4eec\u5e26\u8d70\u8fd9\u4e48\u591a\u4e1c\u897f\uff0c\u5230\u4e86\u4e07\u6708\u5ce1\uff0c\u8db3\u591f\u5403\u7528\u4e09\u5e74\u4e94\u8f7d\u3002\u4f46\u4e07\u4e00\u8def\u4e0a\u906d\u5230 \u970d\u5143\u4f3d\u5e26\u9886\u9ad8\u624b\u622a\u6740\uff0c\u65e2\u8981\u7167\u987e\u4e1c\u897f\uff0c\u53c8\u8981\u548c\u5bf9\u65b9\u65af\u6740\uff0c\u6050\u6015\u5c31\u4e0d\u592a\u597d\u5e94\u4ed8\u4e86\uff01\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u9053\uff1a\u201c\u738b\u5144\u653e\u5fc3\uff0c\u54b1\u4eec\u8fd9\u4e48\u591a\u4eba\u884c\u52a8\uff0c\u53c8\u8981\u95ef\u5173\u8fc7\u5361\uff0c\u5f53\u7136\u7792\u4e0d\u8fc7\u970d\u5143\u4f3d\u3002\u4f46\u636e \u6211\u5224\u65ad\uff0c\u4ed6\u5374\u5fc5\u5b9a\u7741\u7740\u53cc\u773c\u653e\u54b1\u4eec\u8fc7\u53bb\uff0c\u7387\u9886\u9ad8\u624b\u8ffd\u88ad\u7684\u53ef\u80fd\u6027\u4e0d\u5927\u3002\u201d \u3000\u3000\u738b\u5927\u5eb7\u6709\u4e9b\u4e0d\u4fe1\u9053\uff1a\u201c\u4f59\u5148\u751f\u8fd9\u8bdd\u6709\u4ec0\u4e48\u6839\u636e\uff1f\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u9053\uff1a\u201c\u5f53\u7136\u6ca1\u6709\u6839\u636e\uff0c\u53ea\u662f\u9884\u6599\u800c\u5df2\uff0e\u738b\u5144\u5982\u679c\u4e0d\u4fe1\uff0c\u5f85\u4f1a\u513f\u4e8b\u5b9e\u4fbf\u53ef\u8bc1\u660e\u3002\u201d \u3000\u3000\u9b3c\u8bf8\u845b\u6d2a\u6cfd\u62cd\u4e86\u738b\u5927\u5eb7\u4e00\u4e0b\u80a9\u8180\u9053\uff1a\u201c\u8001\u4e09\uff0c\u4f60\u8fde\u4f59\u5148\u751f\u7684\u8bdd\u90fd\u4fe1\u4e0d\u8fc7\uff0e\u8fd8\u80fd\u4fe1\u8c01\uff1f\u201d \u3000\u3000\u738b\u5927\u5eb7\u54a7\u5634\u4e00\u7b11\u9053\uff1a\u201c\u4ffa\u4e0d\u8fc7\u8fd9\u6837\u95ee\u95ee\uff0e\u4f59\u5148\u751f\u662f\u5f53\u5e74\u80e1\uff0e\u8c37\u4e24\u4f4d\u76df\u4e3b\u7684\u519b\u5e08\uff0c\u7b49\u4e8e\u5f53 \u5e74\u7684\u8bf8\u845b\u4eae\uff0c\u4ed6\u7684\u8bdd\u5f53\u7136\u4e0d\u4f1a\u9519\uff01\u201d \u3000\u3000\u63a5\u7740\u53c8\u62cd\u62cd\u6d2a\u6cfd\u7684\u80a9\u8180\u9053\uff1a\u201c\u4e8c\u54e5\uff0c\u4f60\u4e5f\u88ab\u4eba\u79f0\u4e3a\u9b3c\u8bf8\u845b\uff0c\u4ffa\u5bf9\u4f60\u7684\u8bdd\u7167\u6837\u4e5f\u76f8\u4fe1\uff01\u201d \u3000\u3000\u6d2a\u6cfd\u5c34\u5c2c\u4e00\u7b11\u9053\uff1a\u201c\u4f59\u5148\u751f\u79f0\u5f97\u4e0a\u662f\u771f\u8bf8\u845b\uff0c\u6211\u8fd9\u9b3c\u8bf8\u845b\uff0c\u5982\u4f55\u80fd\u6bd4\u5f97\u4e0a\u771f\u8bf8\u845b\uff1f\u8981\u4e0d \u7136\u6211\u600e\u4f1a\u6311\u62c5\u5b50\u3002\u8bf8\u845b\u4eae\u4e00\u5411\u90fd\u662f\u5750\u56db\u8f6e\u8f66\u7684\uff0c\u4ec0\u4e48\u65f6\u4faf\u6311\u8fc7\u62c5\u5b50\uff1f\u201d \u3000\u3000\u738b\u5927\u5eb7\u7b11\u9053\uff1a\u201c\u4e8c\u54e5\u5403\u918b\u4e86\u662f\u4e0d\u662f\uff1f\u6ca1\u5173\u7cfb\uff0c\u4ffa\u7684\u529b\u6c14\u5927\uff0c\u4f60\u7684\u62c5\u5b50\u4ffa\u4e00\u8d77\u6311\uff0c\u4f60\u53ea\u7ba1 \u7a7a\u624b\u8d70\u8def\uff0c\u53ea\u662f\u8fd8\u627e\u4e0d\u5230\u8f66\u5b50\u7ed9\u4f60\u5750\u3002\u201d \u3000\u3000\u8fd9\u51e0\u53e5\u8bdd\uff0c\u542c\u5f97\u4e0d\u5c11\u4eba\u90fd\u4e0d\u7981\u6697\u7b11\u3002 \u3000\u3000\u8001\u5927\u9c8d\u8d85\u54fc\u4e86\u58f0\u9053\uff1a\u201c\u8001\u4e09\uff0e\u9a6c\u4e0a\u5c31\u8981\u51fa\u53d1\u4e86\uff0c\u4f60\u8fd8\u62ff\u8001\u4e8c\u6d88\u9063\uff01\u201d \u3000\u3000\u4e8e\u662f\u949f\u4e00\u8c6a\u4e0b\u4ee4\u9053\uff1a\u201c\u9a6c\u4e0a\u51fa\u53d1\uff01\u201d \u5367\u9f99\u751f\u300a\u5999\u7edd\u5929\u9999\u300b \u7b2c\u4e8c\u5341\u4e00\u56de\u3000\u5947\u9047\u6545\u4e3b \u3000\u3000\u79bb\u5f00\u67cf\u9f84\u9662\uff0c\u9762\u5bf9\u4f4f\u4e86\u4e09\u5e74\u7684\u8ff7\u8e2a\u8c37\uff0c\u7fa4\u8c6a\u4ecd\u96be\u514d\u6709\u4f9d\u4f9d\u4e0d\u820d\u4e4b\u611f\u3002\u5c24\u5176\u8fd9\u5730\u65b9\u662f\u5f53\u5e74 \u80e1\u67cf\u9f84\u4e00\u624b\u5efa\u7acb\u7684\uff0c\u4e00\u65e6\u8fdc\u79bb\uff0c\u53c8\u600e\u4e0d\u4ee4\u4ed6\u4eec\u9eef\u7136\u795e\u4f24\u3002 \u3000\u3000\u4e00\u8def\u4e0a\u679c\u7136\u5e76\u65e0\u4eba\u76d8\u67e5\uff0c\u5373\u4f7f\u7ecf\u8fc7\u5404\u5904\u5173\u5361\uff0c\u4ed6\u4eec\u4e00\u89c1\u662f\u526f\u76df\u4e3b\u949f\u4e00\u8c6a\u5728\u524d\u5f00\u8def\uff0e\u6839\u672c \u4e0d\u6562\u8fc7\u95ee\uff0e \u3000\u3000\u8fd9\u6761\u8c37\u9053\u8db3\u6709\u597d\u51e0\u91cc\u957f\uff0e\u51fa\u8c37\u4e4b\u540e\uff0c\u5df2\u8fd1\u4e94\u66f4\u3002 \u3000\u3000\u7ffb\u8fc7\u51e0\u5ea7\u5cf0\u9876\uff0c\u518d\u5f80\u524d\u8d70\uff0c\u9762\u524d\u51fa\u73b0\u4e00\u9053\u5ce1\u8c37\uff0e\u8fd9\u91cc\u4fbf\u662f\u4e07\u6708\u5ce1\u4e86\uff0e \u3000\u3000\u4e07\u6708\u5ce1\u7ecf\u8fc7\u795e\u97ad\u98de\u68ad\u4e07\u6653\u5149\u6570\u5341\u5e74\u7684\u82e6\u5fc3\u7ecf\u8425\uff0c\u666f\u8272\u79c0\u4e3d\uff0c\u6709\u5982\u4eba\u95f4\u4ed9\u5883\u3002\u7fa4\u8c6a\u76ee\u7779\u8fd9 \u7b49\u666f\u8272\uff0c\u90fd\u5fd8\u8bb0\u75b2\u7d2f\uff0c\u5fc3\u795e\u4e3a\u4e4b\u4e00\u632f\u3002 \u3000\u3000\u6b63\u884c\u8d70\u95f4\uff0c\u7a81\u542c\u9ea6\u5c0f\u660e\u5931\u58f0\u547c\u9053\uff1a\u201c\u4f60\u4eec\u770b\uff01\u524d\u9762\u5927\u9752\u77f3\u4e0a\u5750\u7740\u7684\u90a3\u4e2a\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u7fa4\u8c6a\u9f50\u9f50\u5411\u524d\u671b\u53bb\uff0c\u5728\u8fd9\u5239\u90a3\u95f4\u90fd\u4e0d\u7981\u4e3a\u8fd9\u2014\u6014\uff0c\u7686\u5f20\u53e3\u7ed3\u820c\uff0c\u8bf4\u4e0d\u51fa\u8bdd\u6765\uff0e \u3000\u3000\u4f46\u89c1\u524d\u65b9\u56db\u4e94\u4e08\u7684\u4e00\u5904\u7edd\u58c1\u4e0a\uff0c\u7a81\u51fa\u4e00\u5757\u5927\u9752\u77f3\uff0e\u5927\u9752\u77f3\u4e0a\u76d8\u5750\u7740\u4e00\u4eba\uff0c\u8fd9\u4eba\u5e74\u5728\u56db\u65ec \u5de6\u53f3\uff0c\u8eab\u6750\u4fee\u4f1f\uff0e\u7d2b\u8138\u3001\u73af\u76ee\u3001\u6ee1\u8138\u866c\u9aef\uff0c\u795e\u6001\u5a01\u6b66\u5e84\u4e25\uff0c\u4ee4\u4eba\u671b\u800c\u65e2\u754f\u4e14\u656c\uff0c\u8d6b\u7136\u662f\u80e1\u67cf \u9f84\uff01 \u3000\u3000\u4f46\u80e1\u67cf\u9f84\u5df2\u7ecf\u6b7b\u53bb\u4e09\u5e74\uff0c\u600e\u4f1a\u518d\u5728\u4eba\u95f4\u51fa\u73b0\uff1f \u3000\u3000\u82e5\u8bf4\u6b64\u4eba\u5e76\u975e\u80e1\u67cf\u9f84\uff0e\u5929\u4e0b\u53c8\u7edd\u4e0d\u53ef\u80fd\u6709\u4eba\u548c\u80e1\u67cf\u9f84\u795e\u6001\u5982\u6b64\u76f8\u4f3c\u3002 \u3000\u3000\u4e0d\u8fc7\u949f\u4e00\u8c6a\u7b49\u4eba\u5f88\u5feb\u4fbf\u60f3\u8d77\u4e00\u4ef6\u4e8b\uff0c\u90a3\u4fbf\u662f\u4e24\u6708\u524d\u4ed6\u4eec\u7531\u5929\u53f0\u4e07\u82b1\u5bab\u8fd4\u56de\u8ff7\u8e2a\u8c37\u5230\u96ea\u5cf0 \u80e1\u67cf\u9f84\u57cb\u5c38\u4e4b\u5904\u796d\u62dc\uff0c\u5f53\u6398\u5f00\u79ef\u96ea\u51c6\u5907\u91cd\u7779\u80e1\u67cf\u9f84\u9057\u5bb9\u65f6\uff0c\u80e1\u67cf\u9f84\u7684\u9057\u4f53\u7adf\u7136\u5df2\u7ecf\u4e0d\u5728\u3002\u7531 \u6b64\u5224\u65ad\uff0c\u80e1\u67cf\u9f84\u4ecd\u6d3b\u5728\u4eba\u95f4\uff0c\u4e5f\u5e76\u975e\u7edd\u4e0d\u53ef\u80fd\u3002 \u3000\u3000\u7fa4\u8c6a\u76f4\u5446\u7acb\u4e86\u5f88\u4e45\uff0c\u624d\u5728\u949f\u2014\u8c6a\u7684\u5429\u5490\u4e0b\uff0c\u628a\u62c5\u5b50\u653e\u5728\u5730\u4e0a\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u60c5\u7eea\u6fc0\u52a8\uff0c\u8d8a\u524d\u51e0\u6b65\uff0c\u53cc\u624b\u62b1\u62f3\uff0c\u9ad8\u4e3e\u8fc7\u5934\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u5c0a\u9a7e\u662f\u4ec0\u4e48\u4eba\uff1f\u5728\u4e0b \u949f\u4e00\u8c6a\uff0c\u53ef\u5426\u548c\u5c0a\u9a7e\u8bb2\u51e0\u5411\u8bdd\uff1f\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u90a3\u4eba\u7684\u795e\u8272\u4e5f\u663e\u5f97\u5341\u5206\u6fc0\u52a8\uff0c\u53eb\u9053\uff1b\u201c\u949f\u8d24\u5f1f\uff0c\u96be\u9053\u8fde\u6211\u80e1\u67cf\u9f84\u90fd\u4e0d\u8ba4\u8bc6\u4e86\uff1f\u9614 \u522b\u4e09\u5e74\uff0c\u4f60\u4eec\u90fd\u8fd8\u597d\u5417\uff1f\u201d \u3000\u3000\u8fd9\u4eba\u51fa\u8a00\u58f0\u5982\u6d2a\u949f\uff0c\u58f0\u9707\u5c71\u91ce\u3002\u4ec5\u542c\u8fd9\u58f0\u97f3\uff0c\u4fbf\u77e5\u7adf\u771f\u662f\u80e1\u67cf\u9f84\u3002 \u3000\u3000\u8fd9\u65f6\u4f59\u4ea6\u4e50\u3001\u9ea6\u5c0f\u660e\u7b49\u4eba\u90fd\u60c5\u4e0d\u81ea\u7981\u4e0d\u7ea6\u800c\u540c\u6324\u5230\u5927\u9752\u77f3\u4e0b\uff0c\u4e00\u4e2a\u4e2a\u51e0\u4e4e\u50cf\u5728\u505a\u68a6\u4e00\u822c\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u5446\u4e86\u5446\u9053\uff1a\u201c\u76df\u4e3b\uff0c\u771f\u7684\u662f\u4f60\uff01\u4f60\u4e0d\u662f\u4e09\u5e74\u524d\u2026\u2026\u201d \u3000\u3000\u80e1\u67cf\u9f84\u957f\u957f\u4e00\u53f9\u9053\uff1a\u201c\u4e09\u5e74\u524d\u6211\u867d\u7136\u6b7b\u4e86\uff0c\u5374\u4e3a\u4e00\u4f4d\u65f7\u4e16\u9ad8\u4eba\u6551\u6cbb\u3002\u201d \u3000\u3000\u949f\u4e00\u8c6a\u987f\u4e86\u987f\u9053\uff1a\u201c\u5f53\u65f6\u76df\u4e3b\u5f52\u5929\u540e\uff0c\u662f\u592b\u4eba\u548c\u5c5e\u4e0b\u628a\u4f60\u5b89\u846c\u5728\u96ea\u5cf0\u4e4b\u5dc5\u3002\u6709\u9053\u662f\u4eba\u6b7b \u4e0d\u80fd\u590d\u6d3b\uff0c\u4e16\u4e0a\u53ef\u6709\u8fd9\u79cd\u8d77\u6b7b\u56de\u751f\u7684\u9ad8\u4eba\uff1f\u201d \u3000\u3000\u5ffd\u542c\u80e1\u67cf\u9f84\u8eab\u540e\u4f20\u6765\u53e6\u5916\u4e00\u4eba\u7684\u8bdd\u58f0\u9053\uff1a\u201c\u8001\u592b\u5c31\u80fd\u8d77\u6b7b\u56de\u751f\uff0c\u9f84\u513f\u5c31\u662f\u8001\u592b\u6551\u7684\uff01\u201d \u3000\u3000\u8fd9\u58f0\u97f3\u867d\u7136\u4e0d\u5927\uff0c\u4f46\u5374\u5165\u8033\u60ca\u5fc3\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u7acb\u5373\u53c8\u53cc\u624b\u9ad8\u62f1\u8fc7\u989d\u9053\uff1a\u201c\u8001\u524d\u8f88\u53ef\u5426\u73b0\u8eab\u76f8\u89c1\uff0c\u4e5f\u597d\u8ba9\u665a\u8f88\u4e00\u7779\u4fa0\u989c\uff01\u201d \u3000\u3000\u8bdd\u58f0\u752b\u6bd5\uff0c\u5927\u77f3\u540e\u5df2\u95ea\u51fa\u4e2a\u767d\u53d1\u767d\u9aef\uff0c\u6ee1\u9762\u7ea2\u5149\u7684\u8001\u4eba\u3002 \u3000\u3000\u8fd9\u8001\u4eba\u5fae\u4e00\u505c\u987f\uff0c\u4fbf\u5728\u80e1\u67cf\u9f84\u8eab\u65c1\u5750\u4e0b\uff1a\u201c\u542c\u4f60\u7684\u8bed\u6c14\uff0c\u4f60\u4eec\u8fd9\u4e9b\u4eba\u90fd\u662f\u9f84\u513f\u5f53\u5e74\u4efb\u7eff \u6797\u76df\u4e3b\u65f6\u7684\u90e8\u4e0b\uff0c\u5bf9\u5417\uff1f\u201d \u3000\u3000\u949f\u2014\u8c6a\u8eac\u8eab\u7b54\u9053\uff1a\u201c\u665a\u8f88\u4eec\u6b63\u662f\u80e1\u76df\u4e3b\u5f53\u5e74\u7684\u90e8\u4e0b\u3002\u201d \u3000\u3000\u767d\u53d1\u767d\u9aef\u8001\u4eba\u7b54\u9053\uff1a\u201c\u4f60\u4eec\u548c\u9f84\u513f\u53ef\u6709\u4ec0\u4e48\u8bdd\u8bb2\uff1f\u201d \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u665a\u8f88\u4eec\u548c\u80e1\u76df\u4e3b\u8fdd\u522b\u4e09\u5e74\uff0c\u8981\u8bf4\u7684\u8bdd\uff0e\u53ea\u6015\u4e09\u5929\u4e09\u591c\u4e5f\u8bf4\u4e0d\u5b8c\u3002\u201d \u3000\u3000\u767d\u53d1\u767d\u9aef\u8001\u4eba\u9762\u9732\u4e0d\u60a6\u4e4b\u8272\u9053\uff1a\u201c\u8001\u592b\u7ed9\u4f60\u4eec\u76cf\u8336\u7684\u65f6\u95f4\uff0c\u8981\u957f\u8bdd\u77ed\u8bf4\uff01\u201d \u3000\u3000\u949f\u4e00\u8c6a\u6115\u7136\u9053\uff1a\u201c\u8001\u524d\u8f88\u4e3a\u4ec0\u4e48\u8981\u9650\u5236\u665a\u8f88\u4eec\u548c\u80e1\u76df\u4e3b\u7684\u8c08\u8bdd\u65f6\u95f4\uff1f\u201d \u3000\u3000\u767d\u53d1\u767d\u9aef\u8001\u4eba\u9762\u8272\u4e00\u6c89\u9053\uff1a\u201c\u597d\u5427\uff01\u7ed9\u4f60\u4eec\u5ef6\u957f\u5230\u987f\u996d\u65f6\u95f4\u3002\u201d \u3000\u3000\u53ea\u542c\u9ea6\u5c0f\u660e\u9ad8\u58f0\u9053\uff1a\u201c\u5927\u5e08\u4f2f\uff0c\u60a8\u597d\uff01\u201d \u3000\u3000\u767d\u53d1\u767d\u9aef\u8001\u4eba\u4e0d\u89c9\u4e00\u6123\u9053\uff1a\u201c\u4f60\u8fd9\u5a03\u513f\u600e\u4e48\u79f0\u8d77\u8001\u592b\u5927\u5e08\u4f2f\u6765\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u665a\u8f88\u662f\u80e1\u76df\u4e3b\u7684\u5e08\u5f1f\uff0c\u4f60\u5f53\u7136\u662f\u665a\u8f88\u7684\u5927\u5e08\u4f2f\uff0c\u8bf7\u6055\u5f1f\u5b50\u4e4b\u7f6a\uff0c\u4eca\u5929\u8fd8\u662f \u7b2c\u4e00\u6b21\u770b\u5230\u4f60\u8001\u4eba\u5bb6\uff01\u201d \u3000\u3000\u767d\u53d1\u767d\u9aef\u8001\u4eba\u4e0d\u52a8\u58f0\u8272\u9053\uff1a\u201c\u4f60\u7684\u5e08\u7236\u662f\u8c01\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u8bf7\u6055\u5f1f\u5b50\u6597\u80c6\uff0c\u53ea\u597d\u76f4\u547c\u5bb6\u5e08\u540d\u8bb3\u4e86\u3002\u5bb6\u5e08\u53eb\u9104\u79cb\uff0c\u4e5f\u662f\u60a8\u8001\u4eba\u5bb6\u7684\u5e08 \u5f1f\u3002\u201d \u3000\u3000\u767d\u53d1\u767d\u9aef\u8001\u4eba\u6de1\u6de1\u4e00\u7b11\u9053\uff1a\u201c\u8001\u592b\u66fe\u95fb\u9104\u79cb\u4e4b\u540d\uff0c\u5728\u8001\u592b\u773c\u4e2d\uff0c\u4ed6\u4e0d\u8fc7\u662f\u4e00\u540d\u540e\u751f\u665a\u8f88\uff0c \u5982\u4f55\u80fd\u76f8\u63d0\u5e76\u8bba\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u6014\u4e86\u6014\u9053\uff1a\u201c\u83ab\u975e\u4f60\u8001\u4eba\u5bb6\u2026\u2026\u201d \u3000\u3000\u767d\u53d1\u767d\u9aef\u8001\u4eba\u9053\uff1a\u201c\u9146\u79cb\u7684\u5e08\u5144\u53eb\u82df\u6d69\uff0c\u636e\u9f84\u513f\u8bf4\uff0c\u4ed6\u7684\u786e\u662f\u82df\u6d69\u7684\u5f1f\u5b50\u3002\u81f3\u4e8e\u8001\u592b\uff0c \u662f\u4e09\u5e74\u524d\u6551\u4e86\u9f84\u513f\u4e4b\u540e\uff0c\u624d\u628a\u4ed6\u6536\u5f52\u95e8\u4e0b\uff0c\u8001\u592b\u53c8\u600e\u80fd\u7b97\u662f\u4f60\u7684\u5927\u5e08\u4f2f\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u4e0d\u89c9\u7ea2\u7740\u8138\u4f4e\u4e0b\u5934\u3002 \u3000\u3000\u53ea\u542c\u80e1\u67cf\u9f84\u9053\uff1a\u201c\u949f\u8d24\u5f1f\uff0c\u4f60\u4eec\u73b0\u5728\u51c6\u5907\u5230\u54ea\u91cc\u53bb\uff1f\u201d \u3000\u3000\u949f\u4e00\u8c6a\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u5c5e\u4e0b\u4eec\u5728\u8ff7\u8e2a\u8c37\u65e0\u6cd5\u5bb9\u8eab\uff0c\u53ea\u597d\u6765\u5230\u4e07\u6708\u5ce1\u4e07\u6708\u5821\u6682\u4f4f\uff0e\u201d \u3000\u3000\u80e1\u67cf\u9f84\u5fae\u5fae\u4e00\u60ca\u9053\uff1a\u201c\u96be\u9053\u8ff7\u8e2a\u8c37\u7684\u7eff\u6797\u76df\u4e3b\u4e0d\u662f\u8d24\u5f1f\uff1f\u201d \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u539f\u6765\u76df\u4e3b\u5e76\u4e0d\u6e05\u695a\u8ff7\u8e2a\u8c37\u76ee\u524d\u60c5\u5f62\uff0c\u73b0\u5728\u7684\u76df\u4e3b\u662f\u970d\u5143\u4f3d\uff0e\u4ed6\u4e0e\u5c5e\u4e0b\u4eec\u6c34 \u706b\u4e0d\u5bb9\uff0c\u5c5e\u4e0b\u4eec\u88ab\u8feb\u4e0d\u5f97\u4e0d\u79bb\u5f00\u8ff7\u8e2a\u8c37\u3002\u201d \u3000\u3000\u80e1\u67cf\u9f84\u53c8\u5f00\u59cb\u6fc0\u52a8\uff0e\u6025\u6025\u95ee\u9053\uff1a\u201c\u5bd2\u9999\u5462\uff1f\u201d \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u96be\u9053\u76df\u4e3b\u4e0d\u77e5\u592b\u4eba\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u4e00\u76f4\u6ca1\u6709\u6d88\u606f\u3002\u201d \u3000\u3000\u201c\u592b\u4eba\u4e3a\u4e86\u66ff\u76df\u4e3b\u62a5\u4ec7\uff0c\u8d70\u904d\u4e09\u5c71\u4e94\u5cb3\uff0c\u5728\u6570\u6708\u524d\u2026\u2026\u201d \u3000\u3000\u201c\u600e\u4e48\u6837\uff1f\u4f60\u5feb\u8bf4\uff01\u201d \u3000\u3000\u201c\u5979\u2026\u2026\u5979\u5df2\u7ecf\u6b7b\u5728\u5929\u53f0\u4e07\u82b1\u5bab\u4e86\uff01\u201d \u3000\u3000\u80e1\u67cf\u9f84\u4e0d\u7531\u5f20\u5634\u5410\u51fa\u4e00\u53e3\u9c9c\u8840\uff0c\u8eab\u5b50\u6447\u6447\u6b32\u5760\uff0c\u51e0\u4e4e\u8981\u660f\u53a5\u8fc7\u53bb\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u8fde\u5fd9\u53eb\u9053\uff1a\u201c\u76df\u4e3b\u8282\u54c0\u4fdd\u91cd\uff0c\u4eba\u6b7b\u4e0d\u80fd\u590d\u6d3b\uff01\u201d \u3000\u3000\u80e1\u67cf\u9f84\u95ed\u4e0a\u773c\u53bb\uff0c\u4e0d\u518d\u8a00\u8bed\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u5fc3\u5934\u5927\u6025\uff0c\u4fbf\u51b3\u5b9a\u8dc3\u4e0a\u90a3\u5757\u5927\u9752\u77f3\u53bb\u3002\u90a3\u7a81\u51fa\u7edd\u5d16\u7684\u5927\u9752\u77f3\uff0e\u79bb\u5730\u5927\u7ea6\u4e09\u56db\u4e08\u9ad8\uff0c \u4ee5\u949f\u4e00\u8c6a\u7684\u8f7b\u529f\uff0c\u8db3\u53ef\u4e00\u8dc3\u800c\u4e0a\u3002 \u3000\u3000\u5728\u8fd9\u540c\u65f6\uff0c\u9ea6\u5c0f\u660e\u4e5f\u53eb\u9053\uff1a\u201c\u526f\u76df\u4e3b\uff0c\u54b1\u4eec\u4e0a\u53bb\u770b\u770b\u80e1\u5e08\u5144\u600e\u4e48\u5566\uff1f\u201d \u3000\u3000\u5374\u542c\u767d\u53d1\u767d\u9aef\u8001\u4eba\u53eb\u9053\uff1a\u201c\u8c01\u90fd\u4e0d\u51c6\u4e0a\u6765\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u4f3c\u4e4e\u6709\u4e9b\u4e0d\u670d\uff0c\u53eb\u9053\uff1a\u201c\u8001\u524d\u8f88\u4e3a\u4ec0\u4e48\u4e0d\u51c6\u665a\u8f88\u4eec\u4e0a\u53bb\uff1f\u201d \u3000\u3000\u767d\u53d1\u767d\u9aef\u8001\u4eba\u53eb\u9053\uff1a\u201c\u4e0d\u51c6\u5c31\u662f\u4e0d\u51c6\uff0c\u54ea\u6709\u4ec0\u4e48\u7406\u7531\u53ef\u8bb2\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u4e00\u62c9\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u4e0d\u7ba1\u4ed6\uff0c\u6211\u4eec\u975e\u4e0a\u53bb\u4e0d\u53ef\uff01\u201d \u3000\u3000\u949f\u2014\u8c6a\u4e5f\u51b3\u5b9a\u91c7\u53d6\u786c\u95ef\u786c\u4e0a\uff0c\u548c\u9ea6\u5c0f\u660e\u4e92\u9012\u4e86\u4e00\u4e2a\u773c\u8272\uff0e\u968f\u5373\u817e\u8eab\u800c\u8d77\u3002 \u3000\u3000\u54ea\u77e5\u4e24\u4eba\u521a\u521a\u8dc3\u8d77\u79bb\u5730\u8fd8\u4e0d\u8db3\u4e00\u4e08\uff0c\u7a81\u611f\u4e00\u80a1\u5947\u5927\u65e0\u6bd4\u7684\u6697\u52b2\u8fce\u9762\u88ad\u6765\uff0c\u4f7f\u5f97\u4e24\u4eba\u7acb\u5373 \u5012\u649e\u56de\u6765\uff0c\u843d\u5730\u4e4b\u540e\u8fd8\u662f\u65e0\u6cd5\u7ad9\u7a33\uff0e\u4e0d\u7531\u81ea\u4e3b\u6454\u4e86\u4e2a\u201c\u5c41\u80a1\u5750\u5730\u201d\u3002 \u3000\u3000\u8fd9\u80a1\u5947\u5927\u65e0\u6bd4\u7684\u6697\u52b2\uff0c\u5206\u660e\u662f\u7531\u767d\u53d1\u767d\u9aef\u8001\u4eba\u53d1\u51fa\u3002\u4f46\u7fa4\u8c6a\u8c01\u90fd\u6ca1\u770b\u6e05\u4ed6\u662f\u5982\u4f55\u4f5c\u52bf\u3002 \u8fd9\u7b49\u7edd\u4e16\u795e\u529f\uff0c\u600e\u4e0d\u4ee4\u5728\u573a\u6240\u6709\u7684\u4eba\u7686\u4e3a\u4e4b\u77a0\u76ee\u7ed3\u820c\uff1f \u3000\u3000\u5982\u6b64\u4e00\u6765\uff0c\u949f\u2014\u8c6a\u548c\u9ea6\u5c0f\u660e\u5f53\u7136\u4e5f\u4e0d\u6562\u518d\u5f3a\u884c\u767b\u4e0a\u90a3\u5757\u5927\u9752\u77f3\u3002 \u3000\u3000\u53ea\u542c\u767d\u53d1\u767d\u9aef\u8001\u4eba\u9053\uff1a\u201c\u8fd8\u6709\u4ec0\u4e48\u8bdd\u548c\u9f84\u513f\u8bb2\uff0c\u4f60\u4eec\u5c31\u5feb\u5feb\u5f00\u53e3\uff01\u201d \u3000\u3000\u949f\u2014\u8c6a\u9053\uff1a\u201c\u76df\u4e3b\uff0c\u8fd8\u6709\u4e00\u4ef6\u4e8b\uff0c\u5c5e\u4e0b\u5bf9\u4e0d\u4f4f\u4f60\uff01\u201d \u3000\u3000\u80e1\u67cf\u9f84\u8fd9\u65f6\u60c5\u7eea\u5df2\u7a0d\u7a0d\u7a33\u5b9a\uff0c\u7741\u5f00\u53cc\u773c\u9053\uff1a\u201c\u949f\u8d24\u5f1f\uff0e\u8be5\u662f\u6211\u5bf9\u4e0d\u4f4f\u4f60\u4eec\uff0c\u4e09\u5e74\u6765\u4e0d\u66fe \u548c\u4f60\u4eec\u540c\u7518\u5171\u82e6\uff0c\u4f60\u4eec\u600e\u53ef\u80fd\u6709\u5bf9\u4e0d\u4f4f\u6211\u7684\u4e8b\uff1f\u201d \u3000\u3000\u949f\u4e00\u8c6a\u795e\u8272\u60b2\u621a\u7684\u9053\uff1a\u201c\u5c5e\u4e0b\u8bf4\u7684\u662f\u592b\u4eba\u7684\u8eab\u540e\u4e4b\u4e8b\u3002\u201d \u3000\u3000\u201c\u83ab\u975e\u5979\u4e34\u7ec8\u524d\u6709\u4ec0\u4e48\u4ea4\u4ee3\uff1f\u800c\u4f60\u4eec\u4e0d\u66fe\u505a\u5230\uff1f\u201d \u3000\u3000\u201c\u4e0d\uff0c\u5c5e\u4e0b\u8bf4\u7684\u662f\u5979\u7684\u9057\u4f53\uff0c\u7adf\u6ca1\u80fd\u597d\u597d\u5b89\u846c\u3002\u201d \u3000\u3000\u201c\u6211\u6b63\u8981\u95ee\u95ee\u5979\u846c\u5728\u54ea\u91cc\u3002\u201d \u3000\u3000\u201c\u5c5e\u4e0b\u4eec\u8fde\u5979\u4eba\u9057\u4f53\u90fd\u672a\u627e\u5230\uff0c\u53c8\u5982\u4f55\u5b89\u846c\uff1f\u201d \u3000\u3000\u80e1\u67cf\u9f84\u8138\u8272\u4e00\u53d8\u9053\uff1a\u201c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u5979\u672c\u6765\u4ea4\u4ee3\u8fc7\u5e9e\u58eb\u51b2\uff0c\u8981\u5e9e\u58eb\u51b2\u628a\u5979\u7684\u9057\u4f53\u5e26\u8d70\uff0c\u5e9e\u58eb\u51b2\u5c1a\u672a\u8d70\u51fa\u5929\u53f0\u5c71\uff0c \u592b\u4eba\u7684\u9057\u4f53\u4fbf\u795e\u79d8\u5931\u8e2a\u4e86\u3002\u201d \u3000\u3000\u80e1\u67cf\u9f84\u6115\u7136\u9053\uff1a\u201c\u5e9e\u58eb\u51b2\uff1f\u53ef\u662f\u4eba\u79f0\u957f\u767d\u795e\u53df\uff0c\u53c8\u540d\u5929\u6c60\u8001\u602a\u7684\u90a3\u4f4d\u8001\u524d\u8f88\uff1f\u201d \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u6b63\u662f\u4ed6\uff01\u201d \u3000\u3000\u201c\u636e\u6211\u6240\u77e5\uff0c\u5bd2\u9999\u5e76\u4e0d\u8ba4\u8bc6\u4ed6\uff0c\u4e3a\u4ec0\u4e48\u4f1a\u9057\u8a00\u8981\u4ed6\u628a\u5c38\u4f53\u5e26\u8d70\uff1f\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u5c5e\u4e0b\u4e5f\u4e0d\u6e05\u695a\uff0c\u4e0d\u8fc7\u6709\u4ef6\u4e8b\u8fd8\u8981\u7980\u660e\u76df\u4e3b\uff0c\u5c31\u662f\u4f60\u548c\u592b\u4eba\u6536\u517b\u7684\u4e49\u5b50\u7fce\u513f\uff0c\u4e5f\u88ab \u5e9e\u58eb\u51b2\u5e26\u8d70\u3002\u201d \u3000\u3000\u201c\u96be\u9053\u8fd9\u4e5f\u662f\u5bd2\u9999\u4e34\u7ec8\u524d\u4ea4\u4ee3\u7684\uff1f\u201d \u3000\u3000\u201c\u592b\u4eba\u786e\u66fe\u4ea4\u4ee3\u8fc7\u3002\u201d \u3000\u3000\u201c\u53ef\u77e5\u9053\u539f\u56e0\uff1f\u201d \u3000\u3000\u201c\u636e\u8bf4\u7fce\u513f\u7684\u751f\u8eab\u6bcd\u4eb2\uff0c\u6b63\u662f\u5e9e\u58eb\u51b2\u7684\u5973\u513f\uff0c\u4ed6\u4ee5\u5916\u516c\u8eab\u4efd\u5e26\u8d70\u5916\u5b59\uff0c\u5c5e\u4e0b\u4eec\u867d\u4e0d\u613f\u610f\uff0c \u5374\u65e0\u6cd5\u62e6\u963b\u3002\u201d \u3000\u3000\u80e1\u67cf\u9f84\u53cc\u988a\u63a5\u8fde\u62bd\u6410\u4e86\u51e0\u4e0b\uff0c\u591a\u65f6\uff0c\u624d\u53c8\u95ee\u9053\uff1a\u201c\u4f60\u4eec\u53ef\u66fe\u518d\u89c1\u8fc7\u5e9e\u58eb\u51b2\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u62a2\u7740\u9053\uff1a\u201c\u5c0f\u5f1f\u89c1\u8fc7\uff0c\u800c\u4e14\u89c1\u8fc7\u4e0d\u6b62\u4e00\u6b21\u3002\u201d \u3000\u3000\u201c\u5728\u4ec0\u4e48\u5730\u65b9\uff1f\u201d \u3000\u3000\u201c\u5b9a\u8944\u9644\u8fd1\u7684\u5c71\u8fb9\uff0e\u201d \u3000\u3000\u201c\u4f60\u53ef\u95ee\u8fc7\u4ed6\u5bd2\u9999\u7684\u9057\u4f53\uff1f\u201d \u3000\u3000\u201c\u4ed6\u4e5f\u6b63\u5728\u5230\u5904\u5bfb\u627e\uff0e\u201d \u3000\u3000\u201c\u4ed6\u53ef\u66fe\u5e26\u7740\u7fce\u513f\uff1f\u201d \u3000\u3000\u201c\u4ed6\u8bf4\u5df2\u628a\u7fce\u513f\u5bc4\u6258\u5728\u53cb\u4eba\u90a3\u91cc\uff0e\u201d \u3000\u3000\u8fd9\u65f6\u949f\u4e00\u8c6a\u60c5\u4e0d\u81ea\u7981\u7684\u53eb\u9053\uff1a\u201c\u76df\u4e3b\uff0c\u4f60\u65e2\u7136\u4ecd\u6d3b\u5728\u4e16\u4e0a\uff0c\u800c\u4e14\u6b64\u5904\u53c8\u8ddd\u8ff7\u8e2a\u8c37\u4e0d\u8fdc\uff0c \u4e3a\u4ec0\u4e48\u4e0d\u56de\u53bb\u518d\u590d\u638c\u76df\u4e3b\u4e4b\u4f4d\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u4e5f\u7d27\u8ddf\u7740\u9053\uff1a\u201c\u80e1\u5e08\u5144\uff0c\u4f60\u53ef\u77e5\u9053\u81ea\u4ece\u970d\u5143\u4f3d\u5f53\u4e86\u76df\u4e3b\u540e\uff0c\u6211\u4eec\u7684\u65e5\u5b50\u662f\u591a\u4e48\u4e0d \u597d\u8fc7\uff0e\u90a3\u8001\u5c0f\u5b50\u8868\u9762\u4e0a\u5bf9\u6211\u4eec\u5f88\u5ba2\u6c14\uff0c\u9aa8\u5b50\u91cc\u5374\u65e0\u65f6\u65e0\u523b\u4e0d\u5728\u8bbe\u6cd5\u628a\u6211\u4eec\u9664\u6389\uff0e\u6628\u5929\u665a\u4e0a\uff0e \u6211\u4eec\u8fd9\u4e9b\u4eba\u5dee\u4e00\u70b9\u6ca1\u88ab\u4ed6\u6d3b\u6d3b\u70e7\u6b7b\uff0c\u5c31\u56e0\u4e3a\u8ff7\u8e2a\u8c37\u5f85\u4e0d\u4e0b\u53bb\u4e86\uff0c\u624d\u4e0d\u5f97\u4e0d\u6765\u5230\u4e07\u6708\u5ce1\u6816\u8eab\uff01\u201d \u3000\u3000\u80e1\u67cf\u9f84\u4f3c\u6709\u96be\u8a00\u4e4b\u9690\uff0c\u6447\u4e86\u6447\u5934\u9053\uff1a\u201c\u8ff7\u8e2a\u8c37\u662f\u6211\u4e00\u624b\u5efa\u7acb\u7684\uff0c\u6211\u53c8\u600e\u4e48\u4e0d\u60f3\u56de\u53bb\uff1f\u53ef \u662f\u2026\u2026\u53ef\u662f\u2026\u2026\u53ef\u662f\u2026\u2026\u201d \u3000\u3000\u53ea\u542c\u767d\u53d1\u767d\u9aef\u8001\u4eba\u63a5\u9053\uff1a\u201c\u662f\u8001\u592b\u4e0d\u51c6\u4ed6\u56de\u53bb\uff0c\u4f60\u4eec\u61c2\u4e86\u5417\uff1f\u201d \u3000\u3000\u949f\u4e00\u8c6a\u8083\u5bb9\u6b63\u8272\u9053\uff1a\u201c\u8001\u524d\u8f88\u4e3a\u4ec0\u4e48\u8981\u8fd9\u6837\u505a\uff1f\u60f3\u6765\u5176\u4e2d\u5fc5\u6709\u539f\u56e0\u3002\u201d \u3000\u3000\u767d\u53d1\u767d\u9aef\u8001\u4eba\u8138\u8272\u4e00\u6c89\u9053\uff1a\u201c\u4e0d\u51c6\u5c31\u662f\u4e0d\u51c6\uff0e\u5373\u4f7f\u6709\u539f\u56e0\uff0c\u4e5f\u6ca1\u6709\u5fc5\u8981\u544a\u8bc9\u4f60\u4eec\uff01\u201d \u3000\u3000\u949f\u4e00\u8c6a\u6df1\u6df1\u4e00\u793c\u9053\uff1a\u201c\u8001\u524d\u8f88\u9519\u4e86\uff01\u201d \u3000\u3000\u767d\u53d1\u767d\u9aef\u6012\u9053\uff1a\u201c\u8001\u592b\u9519\u5728\u54ea\u91cc\uff1f\u201c \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u80e1\u76df\u4e3b\u7684\u4fa0\u540d\uff0c\u8a89\u6ee1\u7eff\u6797\u3002\u7eff\u6797\u4e2d\u6b63\u9700\u8981\u4ed6\u8fd9\u6837\u4e00\u4f4d\u5fb7\u5a01\u8db3\u53ef\u670d\u4f17\u7684\u4eba\u6765 \u4e3b\u6301\u5927\u5c40\u3002\u5426\u5219\u5fc5\u5b9a\u7978\u60a3\u65e0\u7a77\uff0c\u8fd9\u662f\u4ef6\u9020\u798f\u5929\u4e0b\u7eff\u6797\u7684\u5927\u4e8b\uff0e\u8001\u524d\u8f88\u53cd\u800c\u4e0d\u51c6\uff0c\u5c82\u4e0d\u662f\u9519 \u4e86\uff1f\u201d \u3000\u3000\u767d\u53d1\u767d\u9aef\u8001\u4eba\u559d\u9053\uff1a\u201c\u540e\u751f\u665a\u8f88\uff0c\u7adf\u6562\u4ee5\u8fd9\u79cd\u8bed\u6c14\u5bf9\u8001\u592b\u8bb2\u8bdd\uff01\u5982\u679c\u4e09\u5e74\u524d\u8001\u592b\u4e0d\u4ee5\u8d77 \u6b7b\u56de\u751f\u4e4b\u672f\u6551\u6cbb\u9f84\u513f\uff0e\u73b0\u5728\u4f60\u4eec\u8fde\u4ed6\u7684\u9762\u90fd\u89c1\u4e0d\u5230\uff0e\u4f60\u4eec\u5c45\u7136\u5f97\u5bf8\u8fdb\u5c3a\uff0c\u7adf\u6562\u518d\u5411\u8001\u592b\u8fdb\u4e00 \u6b65\u63d0\u51fa\u8981\u6c42\uff0c\u7b80\u76f4\u5c82\u6709\u6b64\u7406\uff01\u201d \u3000\u3000\u949f\u4e00\u8c6a\u88ab\u4e00\u987f\u8bad\u65a5\u4e4b\u540e\uff0c\u81ea\u7136\u4e0d\u6562\u518d\u8bf4\u4ec0\u4e48\uff0e\u4f46\u5185\u5fc3\u5374\u7981\u4e0d\u4f4f\u6697\u9053\uff1a\u201c\u8fd9\u8001\u5934\u600e\u4e48\u8fd9\u6837 \u987d\u56fa\uff1f\u65e2\u4e0d\u5403\u8f6f\u53c8\u4e0d\u5403\u786c\uff0c\u4f46\u53c8\u62ff\u4ed6\u6ca1\u529e\u6cd5\uff01\u201d \u3000\u3000\u767d\u53d1\u767d\u9aef\u8001\u4eba\u63a5\u7740\u9053\uff1a\u201c\u8001\u592b\u4eca\u5929\u7b97\u662f\u5bf9\u4f60\u4eec\u5ba2\u6c14\uff0c\u8ba9\u4f60\u4eec\u548c\u9f84\u513f\u8c08\u4e86\u8fd9\u4e48\u4e45\uff0c\u8fd8\u6709\u8bdd \u8981\u8bb2\u6ca1\u6709\uff1f\u8d8a\u5feb\u8d8a\u597d\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9ad8\u58f0\u9041\uff1a\u201c\u8001\u524d\u8f88\u65e2\u4e0d\u7b54\u5e94\u6211\u4eec\u7684\u8981\u6c42\uff0c\u665a\u8f88\u4eec\u8fd8\u6709\u4ec0\u4e48\u53ef\u8bf4\u7684\uff0c\u4e0d\u8fc7\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u8fc7\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u665a\u8f88\u5e0c\u671b\u8001\u524d\u8f88\u51c6\u8bb8\u80e1\u5e08\u5144\u5728\u8fd9\u91cc\u591a\u5750\u4e00\u4f1a\u513f\uff0c\u8ba9\u5927\u5bb6\u591a\u770b\u4ed6\u51e0\u773c\u4e5f\u662f\u597d\u7684\uff0e\u201d \u3000\u3000\u767d\u53d1\u767d\u9aef\u8001\u4eba\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u53c8\u8981\u5f97\u5bf8\u8fdb\u5c3a\uff0c\u9f84\u513f\u6709\u4ec0\u4e48\u597d\u770b\u7684\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u9614\u522b\u4e09\u5e74\uff0c\u665a\u8f88\u4eec\u5f53\u7136\u60f3\u591a\u770b\u770b\u4ed6\uff01\u201d \u3000\u3000\u767d\u53d1\u767d\u9aef\u8001\u4eba\u8f6c\u5934\u9053\uff1a\u201c\u9f84\u513f\uff0e\u968f\u4e3a\u5e08\u8d70\uff01\u201d \u3000\u3000\u8bdd\u58f0\u672b\u6bd5\uff0c\u767d\u53d1\u767d\u9aef\u8001\u4eba\u548c\u80e1\u67cf\u9f84\u5df2\u7136\u5931\u53bb\u6240\u5728\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u548c\u9ea6\u5c0f\u660e\u79bb\u5927\u9752\u77f3\u6700\u8fd1\uff0c\u4e24\u4eba\u4e0d\u7ea6\u800c\u540c\u53cc\u80a9\u4e00\u6296\uff0c\u817e\u8eab\u75be\u5411\u5927\u9752\u77f3\u8dc3\u53bb\u3002 \u3000\u3000\u5728\u4e24\u4eba\u9884\u6599\uff0c\u5927\u9752\u77f3\u4e0a\u4fbf\u662f\u4e00\u7247\u7edd\u58c1\uff0c\u767d\u53d1\u767d\u9aef\u8001\u4eba\u548c\u80e1\u67cf\u9f84\u5fc5\u662f\u8eb2\u5728\u5927\u9752\u77f3\u540e\u65b9\uff0c\u53ea \u662f\u4e0b\u9762\u7684\u4eba\u65e0\u6cd5\u770b\u5230\u800c\u5df2\u3002 \u3000\u3000\u5728\u767b\u4e0a\u5927\u9752\u77f3\u540e\uff0c\u5bf9\u65b9\u4e24\u4eba\u7adf\u5df2\u771f\u7684\u8e2a\u5f71\u5168\u65e0\u3002 \u3000\u3000\u8fd9\u7b80\u76f4\u662f\u4ef6\u4e0d\u53ef\u601d\u8bae\u7684\u4e8b\uff0c\u4e00\u4e2a\u4eba\u7eb5\u7136\u8f7b\u529f\u518d\u9ad8\uff0c\u4e5f\u4e0d\u53ef\u80fd\u5728\u4f17\u76ee\u777d\u777d\u4e4b\u4e0b\uff0c\u7adf\u5ffd\u7136\u5931 \u53bb\u6240\u5728\uff0c\u9664\u975e\u5df2\u4e60\u5f97\u4e86\u9690\u8eab\u672f\u3002 \u3000\u3000\u4f46\u9690\u8eab\u672f\u53ea\u662f\u4e00\u79cd\u4f20\u95fb\uff0c\u6839\u672c\u4e0d\u53ef\u80fd\u771f\u6709\u4eba\u4e60\u5f97\u8fd9\u79cd\u672c\u9886\u3002 \u3000\u3000\u4e8c\u4eba\u5446\u4e86\u534a\u664c\uff0c\u9ea6\u5c0f\u660e\u624d\u9053\uff1b\u201c\u662f\u5426\u9644\u8fd1\u5d16\u58c1\u4e0a\u6709\u6d1e\u7a74\uff0c\u54b1\u4eec\u8981\u4ed4\u7ec6\u627e\u627e\u3002\u201d \u3000\u3000\u949f\u4e00\u8c6a\u88ab\u4e00\u8bed\u63d0\u9192\uff0c\u4e8e\u662f\u4e8c\u4eba\u5728\u9644\u8fd1\u5d16\u58c1\u4e0a\u4ed4\u7ec6\u89c2\u5bdf\uff0c\u8c01\u77e5\u8fd8\u662f\u6beb\u65e0\u6240\u83b7\u3002 \u3000\u3000\u5f53\u4e24\u4eba\u8dc3\u4e0b\u5927\u9752\u77f3\uff0c\u738b\u5927\u5eb7\u6025\u6025\u95ee\u9053\uff1a\u201c\u90a3\u8001\u5934\u513f\u548c\u80e1\u76df\u4e3b\u5230\u5e95\u54ea\u91cc\u53bb\u4e86\uff1f\u201d \u3000\u3000\u949f\u4e00\u8c6a\u60ca\u5f02\u672a\u5b9a\u7684\u6447\u6447\u5934\u9053\uff1a\u201c\u592a\u4e0d\u53ef\u601d\u8bae\u4e86\uff0c\u7adf\u65e0\u6cd5\u627e\u5230\u4ed6\u4eec\uff01\u201d \u3000\u3000\u4f17\u4eba\u6014\u5fe1\u4e86\u591a\u65f6\uff0c\u4f59\u4ea6\u4e50\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u6240\u8c13\u5947\u4eba\u5947\u4e8b\uff0c\u5927\u5bb6\u6682\u4e14\u4e0d\u5fc5\u8ffd\u7a76\uff0c\u8fd8\u662f\u7ee7\u7eed\u8d76 \u8def\u8981\u7d27\uff01\u201d \u3000\u3000\u949f\u4e00\u8c6a\u957f\u957f\u5401\u53e3\u6c14\u9053\uff1a\u201c\u90a3\u8001\u524d\u8f88\u7a76\u7adf\u4e3a\u4ec0\u4e48\u4e0d\u51c6\u80e1\u76df\u4e3b\u518d\u56de\u5230\u8ff7\u8e2a\u8c37\uff0c\u5b9e\u5728\u8ba9\u4eba\u601d\u89e3 \u4e0d\u900f\uff01\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u9053\uff1a\u201c\u54b1\u4eec\u4e0d\u4e86\u89e3\u5176\u4e2d\u539f\u56e0\uff0c\u53c8\u5982\u4f55\u601d\u89e3\u5f97\u900f\uff1f\u201d \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u770b\u6765\u80e1\u76df\u4e3b\u7684\u884c\u52a8\uff0c\u5df2\u63a7\u5236\u5728\u90a3\u4f4d\u8001\u524d\u8f88\u7684\u638c\u63e1\u4e4b\u4e2d\u3002\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u9053\uff1a\u201c\u90a3\u4f4d\u8001\u524d\u8f88\u628a\u80e1\u76df\u4e3b\u7531\u6b7b\u4e2d\u6551\u6d3b\uff0c\u5bf9\u80e1\u76df\u4e3b\u6709\u518d\u9020\u4e4b\u6069\uff0c\u80e1\u76df\u4e3b\u53c8\u600e\u80fd\u4e0d \u542c\u4ed6\u7684\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u5927\u5bb6\u4e0d\u5fc5\u8ba8\u8bba\u4e86\uff0c\u8fd9\u4ef6\u4e8b\u8d8a\u8ba8\u8bba\u8d8a\u7cca\u6d82\uff0e\u65e2\u7136\u80e1\u5e08\u5144\u548c\u90a3\u8001\u524d\u8f88\u5728\u8fd9\u91cc\u51fa \u73b0\uff0c\u5f88\u53ef\u80fd\u4e5f\u5728\u4e07\u6708\u5ce1\uff0c\u5f85\u4f1a\u513f\u89c1\u4e86\u674e\u7ba1\u5bb6\uff0c\u95ee\u95ee\u4ed6\u4e0d\u5c31\u7ed3\u4e86\uff0e\u201d \u3000\u3000\u4e8e\u662f\u4f17\u4eba\u6311\u8d77\u62c5\u5b50\uff0c\u7ee7\u7eed\u524d\u884c\uff0e \u3000\u3000\u8fd9\u4e07\u6708\u5ce1\u4e5f\u662f\u6781\u4e3a\u6df1\u9083\uff0c\u4ece\u5165\u53e3\u5230\u5c3d\u5934\uff0e\u8db3\u6709\u597d\u51e0\u91cc\u957f\uff0c\u89c4\u6a21\u6bd4\u8ff7\u8e2a\u8c37\u5dee\u4e0d\u4e86\u591a\u5c11\uff0c\u6240 \u4e0d\u540c\u7684\uff0c\u662f\u8ff7\u8e2a\u8c37\u5185\u8c37\u9053\u7eb5\u6a2a\u4ea4\u9519\u3002\u8fdb\u5165\u4e4b\u540e\uff0c\u5f88\u5bb9\u6613\u8ff7\u5931\u65b9\u5411\u3002\u4e07\u6708\u5ce1\u53ea\u662f\u4e00\u9053\u5ce1\u8c37\uff0c\u82e5 \u8bba\u666f\u8272\u5e7d\u7f8e\uff0e\u5219\u6bd4\u8ff7\u8e2a\u8c37\u72b9\u6709\u8fc7\u4e4b\u3002 \u3000\u3000\u5927\u7ea6\u524d\u8fdb\u4e86\u4e8c\u91cc\u5de6\u53f3\uff0c\u4fbf\u8fdc\u8fdc\u671b\u89c1\u6709\u56db\u4e94\u540d\u5927\u6c49\uff0c\u8fc5\u901f\u8fce\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u4e3a\u9996\u7684\u6b63\u662f\u4e07\u6708\u5821\u7684\u7ba1\u5bb6\u674e\u8302\uff0c\u5176\u4f59\u51e0\u4eba\u4e0d\u6d88\u8bf4\u90fd\u662f\u4e07\u6708\u5821\u7684\u7537\u4ec6\u4e86\u3002 \u3000\u3000\u674e\u8302\u5f53\u5e74\u662f\u4e07\u6708\u5821\u4e3b\u4eba\u795e\u97ad\u98de\u68ad\u4e07\u6653\u5149\u7684\u5f97\u529b\u5c5e\u4e0b\uff0c\u4e3a\u4eba\u5341\u5206\u7cbe\u660e\u80fd\u5e72\uff0c\u6b66\u529f\u4e5f\u4e0d\u5f31\uff0c \u4ed6\u7528\u7684\u662f\u4e00\u67c4\u5355\u5200\uff0c\u4eba\u79f0\u5feb\u5200\u674e\u8302\u3002 \u3000\u3000\u81ea\u4ece\u4e07\u6653\u5149\u6b7b\u540e\uff0c\u674e\u8302\u65e0\u5f62\u4e2d\u6210\u4e86\u4e07\u6708\u5821\u7684\u4e3b\u4eba\uff0c\u5821\u4e2d\u539f\u6709\u7684\u4ec6\u5987\u4e2b\u9b1f\uff0c\u4e5f\u90fd\u4e0d\u66fe\u79bb\u53bb\uff0e \u3000\u3000\u7ecf\u4ed6\u7684\u82e6\u5fc3\u652f\u6491\uff0c\u76ee\u524d\u7684\u4e07\u6708\u5821\uff0c\u8f83\u4e4b\u4e07\u6653\u5149\u751f\u524d\uff0c\u5e76\u4e0d\u900a\u8272\uff0e \u3000\u3000\u4e07\u6708\u5821\u9664\u4e86\u5f53\u5e74\u4e07\u6653\u5149\u7559\u4e0b\u7684\u79ef\u84c4\u5916\uff0c\u6240\u6709\u7684\u4eba\u4e5f\u90fd\u80fd\u81ea\u98df\u5176\u529b\uff0c\u5ce1\u4e2d\u5c71\u5761\u4e0a\u5f00\u8f9f\u4e86\u4e0d \u5c11\u826f\u7530\u548c\u679c\u56ed\uff0c\u5e76\u9972\u517b\u4e86\u4e0d\u5c11\u732a\u725b\u7f8a\u9e21\uff0e\u4e00\u5207\u90fd\u80fd\u81ea\u7ed9\u81ea\u8db3\uff0c\u4e0d\u987b\u5916\u6c42\u3002 \u3000\u3000\u674e\u8302\u662f\u95fb\u542c\u6709\u4e0d\u5c11\u4eba\u8fdb\u4e86\u5ce1\u8c37\uff0c\u4fbf\u7acb\u5373\u7387\u9886\u51e0\u540d\u5065\u4ec6\u51fa\u6765\u67e5\u770b\u7a76\u7adf\u3002 \u3000\u3000\u5f53\u4ed6\u8ba4\u51fa\u662f\u949f\u4e00\u8c6a\u4e00\u4f19\u4eba\uff0c\u603b\u7b97\u653e\u4e0b\u4e86\u5fc3\uff0c\u4e0d\u8fc7\u5bf9\u8fd9\u4e9b\u4eba\u7684\u6765\u610f\uff0e\u5374\u96be\u514d\u6709\u4e9b\u832b\u7136\u3002 \u3000\u3000\u6587\u5929\u751f\u548c\u674e\u8302\u7b49\u4eba\u6700\u719f\uff0c\u5fd9\u6311\u7740\u62c5\u5b50\u62a2\u4e0a\u524d\u9053\uff1a\u201c\u674e\u5927\u53d4\uff0c\u8fd9\u51e0\u5e74\u8fd8\u597d\u5427\uff1f\u201d \u3000\u3000\u674e\u8302\u554a\u4e86\u58f0\u9053\uff1a\u201c\u539f\u6765\u6587\u5c11\u7237\u4e5f\u6765\u4e86\uff01\u5230\u5e95\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d \u3000\u3000\u4e0d\u7b49\u5429\u5490\uff0e\u4fbf\u6709\u4e00\u540d\u7537\u4ec6\u62a2\u7740\u63a5\u4e0b\u6587\u5929\u751f\u7684\u62c5\u5b50\u3002 \u3000\u3000\u5176\u4f59\u51e0\u540d\u7537\u4ec6\uff0c\u4e5f\u4e3b\u52a8\u8fce\u4e0a\u6765\u63a5\u4e0b\u51e0\u526f\u62c5\u5b50\uff0e \u3000\u3000\u8fd9\u65f6\u674e\u8302\u53c8\u5fd9\u8fc7\u6765\u548c\u7fa4\u8c6a\u6253\u62db\u547c\uff0c\u8fd9\u4e9b\u4eba\u9664\u9ea6\u5c0f\u660e\u548c\u82d7\u7d20\u82d3\u5916\uff0c\u4e09\u5e74\u524d\u5168\u5728\u4e07\u6708\u5821\u8bf4\u4e00 \u4e0d\u4e8c\uff0c\u674e\u8302\u5f53\u7136\u90fd\u8ba4\u8bc6\uff0e \u3000\u3000\u949f\u4e00\u8c6a\u968f\u5373\u628a\u6765\u610f\u5411\u674e\u8302\u8bf4\u660e\u3002 \u3000\u3000\u4e07\u6708\u5821\u5ffd\u7136\u6765\u4e86\u8fd9\u4e48\u591a\u9ad8\u624b\uff0c\u5bf9\u674e\u8302\u6765\u8bf4\uff0c\u5b9e\u5728\u9ad8\u5174\uff0e \u3000\u3000\u5f53\u5e74\u4e07\u6653\u5149\u5728\u4e16\u65f6\uff0e\u4e07\u6708\u5821\u7ecf\u5e38\u8bbf\u5ba2\u4e0d\u65ad\uff0c\u800c\u4e14\u6765\u7684\u90fd\u662f\u6b66\u6797\u4e2d\u5f53\u4ee3\u77e5\u540d\u4e4b\u58eb\u3002\u81ea\u4e07\u6653 \u5149\u6b7b\u540e\uff0c\u4e07\u6708\u5821\u5df2\u96be\u5f97\u6709\u8fd9\u79cd\u666f\u8c61\u4e86\uff0c\u674e\u8302\u53c8\u600e\u80fd\u4e0d\u6253\u5fc3\u5e95\u5174\u594b\u3002 \u3000\u3000\u5f88\u5feb\u4fbf\u8fdb\u5165\u4e07\u6708\u5821\u3002 \u3000\u3000\u674e\u8302\u5fd9\u7740\u547d\u4eba\u6574\u7406\u623f\u95f4\u3002 \u3000\u3000\u7a7a\u623f\u751a\u591a\uff0e\u4ec5\u4ec5\u5f85\u5ba2\u4e4b\u7528\u7684\uff0c\u4fbf\u6709\u5341\u51e0\u95f4\u3002 \u3000\u3000\u628a\u7fa4\u8c6a\u5b89\u987f\u597d\uff0c\u4e2d\u5348\uff0c\u674e\u8302\u53c8\u8bbe\u4e0b\u4e30\u76db\u9152\u5bb4\uff0c\u4e3a\u7fa4\u8c6a\u63a5\u98ce\uff0c\u62db\u5f85\u5f97\u5f02\u5e38\u4eb2\u5207\u3002 \u3000\u3000\u9152\u5bb4\u6563\u540e\uff0c\u53ea\u6709\u949f\u4e00\u8c6a\u3001\u4f59\u4ea6\u4e50\u3001\u9ea6\u5c0f\u660e\u4e09\u4eba\u7559\u5728\u5927\u5385\u3002 \u3000\u3000\u674e\u8302\u4e5f\u7559\u5728\u5927\u5385\u76f8\u966a\uff0c\u5e76\u4eb2\u81ea\u6ce1\u4e0a\u8336\uff0e \u3000\u3000\u949f\u4e00\u8c6a\u89c1\u5927\u5385\u5185\u5df2\u65e0\u5916\u4eba\uff0c\u8fd9\u624d\u95ee\u9053\uff1a\u201c\u6211\u6709\u4e00\u4e8b\uff0c\u60f3\u95ee\u95ee\u674e\u5144\u3002\u201d \u3000\u3000\u674e\u8302\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u949f\u5927\u4fa0\u6709\u8bdd\u8bf7\u8bb2\uff01\u201d \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u8ff7\u8e2a\u8c37\u7684\u80e1\u76df\u4e3b\u5df2\u4e8e\u4e09\u5e74\u524d\u4e0d\u5e78\u6218\u6b7b\uff0c\u8fd9\u4ef6\u4e8b\u674e\u5144\u4e00\u5b9a\u77e5\u9053\u7684\u4e86\uff1f\u201d \u3000\u3000\u674e\u8302\u9053\uff1a\u201c\u8fd9\u6837\u7684\u5927\u4e8b\uff0c\u6211\u600e\u4f1a\u4e0d\u77e5\u9053\uff01\u201d \u3000\u3000\u949f\u2014\u8c6a\u9053\uff1a\u201c\u4e8b\u60c5\u602a\u5c31\u602a\u5728\u8fd9\u91cc\uff0c\u6211\u4eec\u521a\u624d\u8fdb\u5ce1\u65f6\uff0e\u7adf\u7136\u9047\u89c1\u4e86\u80e1\u76df\u4e3b\uff01\u201d \u3000\u3000\u674e\u8302\u4f3c\u662f\u5e76\u672a\u5403\u60ca\uff0c\u9053\uff1a\u201c\u662f\u5426\u80e1\u5927\u4fa0\u8eab\u8fb9\u8fd8\u6709\u4e00\u767d\u53d1\u8001\u4eba\uff1f\u201d \u3000\u3000\u949f\u4e00\u8c6a\u4e0d\u89c9\u4e00\u559c\u9053\uff1a\u201c\u539f\u6765\u674e\u5144\u4e5f\u9047\u89c1\u8fc7\u80e1\u76df\u4e3b\uff0c\u8fd9\u6837\u8bf4\u80e1\u76df\u4e3b\u4e5f\u662f\u4f4f\u5728\u4e07\u6708\u5821\u4e86\uff1f\u201d \u3000\u3000\u674e\u8302\u4e0d\u52a8\u58f0\u8272\u9053\uff1a\u201c\u949f\u5927\u4fa0\u8bf7\u5148\u628a\u521a\u624d\u7684\u7ecf\u8fc7\u8bf4\u4e00\u904d\uff01\u201d \u3000\u3000\u949f\u4e00\u8c6a\u867d\u7136\u5fc3\u91cc\u7740\u6025\uff0c\u4e5f\u53ea\u597d\u628a\u5f97\u9047\u80e1\u67cf\u9f84\u548c\u767d\u53d1\u8001\u4eba\u7684\u7ecf\u8fc7\u5927\u7565\u8bb2\u4e86\u51fa\u6765\uff0e \u3000\u3000\u674e\u8302\u542c\u7f62\u4e4b\u540e\uff0c\u5401\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u4e0d\u7792\u949f\u5927\u4fa0\uff0c\u6211\u4e5f\u9047\u5230\u8fc7\u80e1\u5927\u4fa0\u591a\u6b21\uff0c\u800c\u4e14\u6bcf\u6b21\u90fd\u6709\u90a3 \u767d\u53d1\u8001\u4eba\u5728\u4ed6\u8eab\u65c1\u3002\u201d \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u674e\u5144\u53ef\u548c\u4ed6\u8bb2\u8fc7\u8bdd\uff1f\u201d \u3000\u3000\u674e\u8302\u6447\u5934\u9053\uff1a\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u4e0d\u8ddf\u4ed6\u8bb2\u8bdd\uff1f\u201d \u3000\u3000\u201c\u4e00\u6765\u662f\u76f8\u9694\u8ddd\u79bb\u8f83\u8fdc\uff0c\u4e8c\u6765\u597d\u50cf\u662f\u80e1\u5927\u4fa0\u7684\u884c\u52a8\u5df2\u88ab\u767d\u53d1\u8001\u4eba\u63a7\u5236\uff0c\u80e1\u5927\u4fa0\u4f3c\u662f\u8981\u56de \u7b54\u6211\u7684\u8bdd\uff0c\u5374\u53c8\u4e0d\u6562\u3002\u201d \u3000\u3000\u201c\u674e\u5144\u4e3a\u4ec0\u4e48\u4e0d\u5c3d\u91cf\u4e0e\u4ed6\u63a5\u8fd1\uff0c\u8d70\u5230\u4ed6\u8ddf\u524d\u3002\u201d \u3000\u3000\u201c\u6211\u4f55\u5c1d\u6ca1\u6253\u7b97\u8d70\u8fd1\uff0c\u4f46\u6bcf\u8981\u8d70\u8fd1\uff0e\u90a3\u767d\u53d1\u8001\u4eba\u548c\u80e1\u5927\u4fa0\u5374\u8d70\u5f97\u66f4\u5feb\uff0c\u6211\u6839\u672c\u8ffd\u4e0d\u4e0a\u3002\u201d \u3000\u3000\u201c\u4ed6\u4eec\u65e2\u7136\u5e38\u5728\u4e07\u6708\u5ce1\u51fa\u73b0\uff0c\u5fc5\u5b9a\u4e5f\u4f4f\u5728\u9644\u8fd1\uff0c\u674e\u5144\u5fc5\u5b9a\u77e5\u9053\u4ed6\u4eec\u4f4f\u5728\u54ea\u91cc\u4e86\uff1f\u201d \u3000\u3000\u674e\u8302\u795e\u8272\u9887\u4e3a\u5c34\u5c2c\u7684\u9053\uff1a\u201c\u6211\u4f55\u5c1d\u4e0d\u662f\u8fd9\u4e48\u60f3\uff0e\u4f46\u4e00\u76f4\u5230\u73b0\u5728\uff0c\u5374\u59cb\u7ec8\u67e5\u4e0d\u5230\u4ed6\u4eec\u7684\u4f4f \u5904\u3002\u201d \u3000\u3000\u949f\u4e00\u8c6a\u5e26\u4e9b\u6005\u60d8\u7684\u9ed8\u4e86\u4e00\u9ed8\u9053\uff1a\u201c\u674e\u5144\u5bf9\u4e07\u6708\u5ce1\u9644\u8fd1\u5e94\u5f53\u5f88\u719f\u6089\u624d\u5bf9\uff0e\u201d \u3000\u3000\u4ed6\u8bdd\u51fa\u53e3\u540e\uff0c\u624d\u89c9\u51fa\u8fd9\u6837\u8bf4\u8bdd\u6709\u6b20\u793c\u8c8c\u3002 \u3000\u3000\u597d\u5728\u674e\u8302\u5e76\u4e0d\u4ecb\u610f\uff0c\u5c34\u5c2c\u4e00\u7b11\u9053\uff1a\u201c\u674e\u67d0\u8ffd\u968f\u4e07\u8001\u7237\u5b50\u4e8c\u4e09\u5341\u5e74\uff0c\u5728\u4e07\u6708\u5ce1\u4e5f\u4f4f\u4e86\u4e8c\u4e09 \u5341\u5e74\uff0c\u8fd9\u5ce1\u5185\u7684\u2014\u8349\u4e00\u6728\u90fd\u8ba4\u8bc6\uff0e\u5ce1\u5185\u6240\u6709\u7684\u53ef\u8d44\u85cf\u8eab\u4e4b\u5904\uff0c\u4e5f\u90fd\u4e86\u82e5\u6307\u638c\u3002\u82e5\u5f53\u771f\u80e1\u5927\u4fa0 \u548c\u90a3\u767d\u53d1\u8001\u4eba\u5c31\u4f4f\u5728\u4e07\u6708\u86f1\uff0c\u600e\u4f1a\u6709\u67e5\u4e0d\u51fa\u6765\u7684\u9053\u7406\uff1f\u201d \u3000\u3000\u949f\u4e00\u8c6a\u89c1\u6b64\u60c5\u5f62\uff0c\u53ea\u597d\u4e0d\u518d\u8c08\u80e1\u67cf\u9f84\u7684\u4e8b\u3002 \u3000\u3000\u4e0b\u5348\uff0c\u7fa4\u8c6a\u53c8\u9f50\u96c6\u5927\u5385\uff0c\u7531\u949f\u2014\u8c6a\u89c4\u5b9a\u4e8b\u60c5\u3002 \u3000\u3000\u4e07\u6708\u5821\u5728\u4e07\u6653\u5149\u53bb\u4e16\u540e\uff0c\u767d\u5929\u5e76\u65e0\u4efb\u4f55\u8b66\u6212\uff0c\u53ea\u6709\u591c\u95f4\u6d3e\u51fa\u4e00\u540d\u5728\u5e84\u9662\u5de1\u903b\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u4e3a\u9632\u4e07\u4e00\uff0e\u89c4\u5b9a\u767d\u5929\u7531\u7fa4\u8c6a\u4e2d\u6d3e\u51fa\u2014\u4eba\uff0e\u5728\u5ce1\u8c37\u5165\u53e3\u5904\u76d1\u89c6\uff0c\u9047\u5230\u5c0f\u4e8b\u81ea\u884c\u5904\u7f6e\uff0c \u5927\u4e8b\u5219\u8fc5\u901f\u56de\u5821\u4e2d\u901a\u62a5\uff0e \u3000\u3000\u81f3\u4e8e\u591c\u95f4\uff0c\u5219\u5c06\u5ce1\u8c37\u5165\u53e3\u7684\u8b66\u6212\u64a4\u56de\uff0c\u53e6\u6d3e\u51fa\u4e00\u4eba\uff0c\u4e0e\u4e07\u6708\u5821\u539f\u6709\u7684\u5de1\u903b\u5408\u6210\u4e00\u7ec4\u3002\u5728 \u5e84\u9662\u9644\u8fd1\u52a0\u5f3a\u8b66\u6212\u3002 \u3000\u3000\u8fd9\u9879\u89c4\u5b9a\uff0c\u7fa4\u8c6a\u81ea\u662f\u5e76\u65e0\u5f02\u8bae\u3002 \u3000\u3000\u53e6\u5916\uff0c\u7fa4\u8c6a\u5728\u767d\u5929\u9664\u7ec3\u529f\u5916\uff0c\u4e5f\u8981\u5e2e\u7740\u6d47\u6c34\u79cd\u83dc\uff0c\u9972\u9e21\u5582\u9e2d\u3002\u603b\u4e4b\uff0c\u501f\u4f4f\u5728\u4e07\u6708\u5821\uff0c\u5fc5 \u987b\u5e2e\u5fd9\u505a\u4e8b\uff0c\u8c01\u90fd\u4e0d\u80fd\u767d\u5403\u767d\u559d\u3002 \u3000\u3000\u4efb\u52a1\u4ea4\u4ee3\u5b8c\u6bd5\u540e\uff0c\u53c8\u51c6\u5907\u4e86\u7eb8\u7b94\u548c\u9999\u82b1\u9c9c\u679c\uff0c\u7531\u674e\u8302\u548c\u6587\u5929\u751f\u5e26\u8def\uff0c\u5411\u4e07\u6653\u5149\u548c\u4e07\u592b\u4eba \u7684\u9057\u7075\u796d\u62dc\u3002 \u3000\u3000\u8fd9\u662f\u56e0\u4e3a\u4ed6\u4eec\u592b\u5987\u90fd\u88ab\u5b89\u7f6e\u5728\u96ea\u7a9f\u91cc\uff0c\u5e76\u65e0\u9675\u5893\uff0c\u4ee5\u4fbf\u6c38\u77bb\u9057\u5bb9\u3002 \u3000\u3000\u6574\u6574\u5fd9\u4e86\u4e00\u4e0b\u5348\uff0c\u7fa4\u8c6a\u624d\u5f00\u59cb\u4f11\u606f\uff0c \u3000\u3000\u4e00\u8fde\u51e0\u5929\u8fc7\u53bb\uff0c\u4e07\u6708\u5821\u4e00\u76f4\u76f8\u5b89\u65e0\u4e8b\u3002 \u3000\u3000\u4e0d\u8fc7\u7fa4\u8c6a\u4eec\u5fc3\u91cc\u90fd\u6709\u6570\uff0c\u970d\u5143\u4f3d\u8fdf\u65e9\u603b\u4f1a\u6d3e\u4eba\u524d\u6765\u7684\uff0c\u4ed6\u7edd\u4e0d\u53ef\u80fd\u5bf9\u7fa4\u8c6a\u7684\u79bb\u5f00\u8ff7\u8e2a\u8c37 \u4e0d\u95fb\u4e0d\u95ee\u3002\u540c\u65f6\uff0c\u7fa4\u8c6a\u4eec\u76ee\u524d\u4eba\u5728\u4f55\u5904\uff0c\u4e5f\u7edd\u5bf9\u8131\u79bb\u4e0d\u4e86\u4ed6\u7684\u76d1\u89c6\u3002 \u3000\u3000\u5927\u7ea6\u5728\u7fa4\u8c6a\u6765\u5230\u4e07\u6708\u5ce1\u7684\u5341\u5929\u540e\u7684\u4e00\u4e2a\u4e0a\u5348\uff0c\u5ffd\u89c1\u6d3e\u5728\u5ce1\u53e3\u5916\u7684\u6c5f\u5317\u56db\u9f99\u8001\u56db\u591a\u722a\u9f99\u674e \u6770\uff0c\u5306\u5306\u5954\u56de\u6765\u901a\u62a5\uff0c\u8bf4\u662f\u6709\u4e8c\u4e09\u5341\u4e2a\u4eba\uff0c\u6b63\u5411\u5ce1\u8c37\u5165\u53e3\u5904\u5954\u6765\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u95ee\u9053\uff1a\u201c\u53ef\u770b\u6e05\u695a\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u674e\u6770\u9053\uff1a\u201c\u4e3a\u9996\u7684\u662f\u970d\u5143\u4f3d\uff0c\u4ed6\u8eab\u65c1\u6709\u5cad\u5357\u4e8c\u5947\uff0c\u56e0\u4e3a\u9694\u5f97\u5c1a\u8fdc\uff0c\u518d\u540e\u9762\u7684\u4eba\u5c31\u8ba4\u4e0d\u6e05 \u4e86\uff0c\u5947\u602a\u7684\u662f\u597d\u50cf\u8fd8\u6709\u4e00\u7fa4\u548c\u5c1a\u5728\u5185\u3002\u201d \u3000\u3000\u949f\u2014\u8c6a\u6115\u9053\uff1a\u201c\u6709\u548c\u5c1a\u5728\u5185\uff1f\u8ff7\u8e2a\u8c37\u54ea\u91cc\u6765\u7684\u50e7\u4eba\uff1f\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u9053\uff1a\u201c\u4e0d\u7ba1\u5982\u4f55\uff0c\u970d\u5143\u4f3d\u5fc5\u662f\u6765\u8005\u4e0d\u5584\uff0c\u5584\u8005\u4e0d\u6765\u3002\u5927\u5bb6\u8bf7\u5feb\u63e3\u597d\u5175\u5203\uff0c\u5728\u5927\u5385 \u540e\u9762\u96c6\u5408\uff01\u201d \u3000\u3000\u738b\u5927\u5eb7\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u8981\u96c6\u5408\u5728\u5927\u5385\u540e\u9762\uff1f\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u9053\uff1b\u201c\u54b1\u4eec\u5148\u4ee5\u793c\u76f8\u5f85\uff0c\u7531\u526f\u76df\u4e3b\u548c\u6211\u51fa\u9762\u63a5\u5f85\u4ed6\u4eec\uff0c\u5404\u4f4d\u6682\u65f6\u4e0d\u8981\u73b0\u8eab\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u6709\u4e9b\u4e0d\u9ad8\u5174\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u4e0d\u8981\u6211\u4e5f\u73b0\u8eab\uff1f\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u9053\uff1a\u201c\u4e5f\u597d\uff01\u9ea6\u5c0f\u5144\u5f1f\u4e5f\u8bf7\u51fa\u9762\uff0c\u8fd8\u6709\u5c31\u662f\u674e\u7ba1\u5bb6\uff0c\u4ed6\u662f\u4e07\u6708\u5821\u7684\u4e3b\u4eba\uff0c\u4e0d\u80fd\u4e0d \u5728\u573a\u3002\u201d \u3000\u3000\u5206\u6d3e\u65e2\u6bd5\uff0c\u949f\u2014\u8c6a\u3001\u4f59\u4ea6\u4e50\u3001\u9ea6\u5c0f\u660e\u968f\u5373\u8eab\u4f69\u5175\u5203\uff0c\u6765\u5230\u5927\u5385\u3002 \u3000\u3000\u53c8\u8fc7\u4e86\u76cf\u8336\u5de5\u592b\uff0c\u679c\u7136\u6709\u4e8c\u4e09\u5341\u4eba\uff0c\u5728\u970d\u5143\u4f3d\u7684\u7387\u9886\u4e0b\uff0c\u6d69\u6d69\u8361\u8361\u800c\u6765\u3002 \u3000\u3000\u949f\u2014\u8c6a\u3001\u4f59\u4ea6\u4e50\u3001\u9ea6\u5c0f\u660e\u3001\u674e\u8302\u7acb\u5373\u8fce\u51fa\u5927\u5385\u4e4b\u5916\u3002 \u3000\u3000\u5f53\u771f\u5947\u602a\uff0c\u6765\u4eba\u9664\u4e86\u970d\u5143\u4f3d\u548c\u5cad\u5357\u4e8c\u5947\u4e4b\u5916\uff0c\u949f\u4e00\u8c6a\u7b49\u5bf9\u5176\u4f59\u7684\u51e0\u4e4e\u5168\u4e0d\u8ba4\u8bc6\uff0e \u3000\u3000\u5176\u4e2d\u4e94\u4eba\uff0c\u4e2a\u4e2a\u76f8\u8c8c\u602a\u5f02\uff0c\u795e\u6001\u4e3e\u6b62\u4e5f\u663e\u5f97\u4e0e\u5e38\u4eba\u5927\u5927\u4e0d\u540c\uff0c\u8fde\u4f69\u5e26\u7684\u5175\u5203\uff0c\u4e5f\u5947\u5f62\u602a \u72b6\uff0e \u3000\u3000\u679c\u7136\u4e0d\u9519\uff0c\u91cc\u9762\u5c45\u7136\u6709\u5341\u540d\u8eab\u7a7f\u9ec4\u886b\u9ec4\u88e4\u8db3\u767b\u8292\u978b\u7684\u548c\u5c1a\uff0c\u4ed6\u4eec\u90fd\u8170\u4f69\u6212\u5200\uff0c\u624b\u91cc\u5404\u6a2a \u7740\u4e00\u6761\u8db3\u6709\u81c2\u7c97\u7ec6\u7684\u7985\u6756\u3002 \u3000\u3000\u548c\u5c1a\u4e0d\u7a7f\u8888\u88df\uff0c\u53ea\u7a7f\u9ec4\u886b\u9ec4\u88e4\uff0c\u5b8c\u5168\u4e00\u526f\u77ed\u6253\u626e\uff0c\u4e5f\u9887\u4e3a\u7f55\u89c1\u3002 \u3000\u3000\u4ee4\u4eba\u66f4\u4e3a\u5403\u60ca\u7684\u662f\uff0c\u4ed6\u4eec\u90fd\u8eab\u6750\u9ad8\u58ee\u9b41\u609f\uff0c\u809a\u5927\u8170\u5706\uff0c\u81c2\u7c97\u817f\u957f\uff0c\u5c31\u50cf\u94dc\u91d1\u521a\u3001\u94c1\u7f57\u6c49 \u4e00\u822c\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u5f53\u5148\u8fce\u4e86\u4e0a\u53bb\uff0c\u62b1\u62f3\u4e00\u793c\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u7adf\u662f\u970d\u76df\u4e3b\u5927\u9a7e\u5149\u4e34\uff0c\u949f\u67d0\u5931\u8fce\u4e86\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u4e0d\u52a8\u58f0\u8272\u9053\uff1a\u201c\u8001\u592b\u53ef\u5426\u5230\u5ba2\u5385\u548c\u949f\u526f\u76df\u4e3b\u4e00\u53d9\uff01\u201d \u3000\u3000\u949f\u4e00\u8c6a\u6de1\u7136\u7b11\u9053\uff1a\u201c\u949f\u67d0\u6b22\u8fce\u8fd8\u6765\u4e0d\u53ca\uff0c\u970d\u76df\u4e3b\u4f55\u51fa\u6b64\u8a00\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u968f\u5373\u5429\u5490\u968f\u6765\u7684\u4eba\u5728\u5927\u5385\u5916\u5e7f\u573a\u6682\u884c\u5f85\u547d\uff0c\u53ea\u5e26\u4e86\u5cad\u5357\u4e8c\u5947\u6b65\u8fdb\u5165\u5927\u5385\u3002 \u3000\u3000\u674e\u8302\u56e0\u662f\u5730\u4e3b\uff0c\u4eb2\u81ea\u4e3a\u5404\u4eba\u6ce1\u4e0a\u8336\uff0c\u7136\u540e\u5206\u5bbe\u4e3b\u5750\u4e0b\u3002 \u3000\u3000\u970d\u5143\u4f3d\u559d\u4e86\u53e3\u8336\u9053\uff1a\u201c\u8001\u592b\u4e00\u76f4\u601d\u89e3\u4e0d\u900f\uff0c\u949f\u526f\u76df\u4e3b\u4e3a\u4ec0\u4e48\u7387\u9886\u67cf\u9f84\u9662\u5404\u5934\u9886\u4e0d\u8f9e\u800c\u522b\uff1f \u8001\u592b\u8fd9\u6837\u95ee\u8bdd\uff0c\u4e5f\u8bb8\u5192\u6627\u4e86\u4e9b\uff0c\u8fd8\u8bf7\u949f\u526f\u76df\u4e3b\u3001\u4f59\u5148\u751f\u3001\u9ea6\u8001\u5f1f\u5343\u4e07\u4e0d\u8981\u4ecb\u610f\u3002\u201d \u3000\u3000\u949f\u4e00\u8c6a\u6b49\u7136\u4e00\u7b11\u9053\uff1a\u201c\u949f\u67d0\u4e00\u5411\u6027\u60c5\u61d2\u6563\uff0c\u81ea\u5fd6\u4e0d\u9002\u5408\u5728\u7eff\u6797\u4e2d\u8ba8\u751f\u6d3b\uff0c\u65e9\u5c31\u6709\u5fc3\u5f52\u9690 \u6797\u6cc9\uff0c\u6b63\u597d\u67cf\u9f84\u9662\u7684\u5404\u4f4d\u6709\u5fd7\u4e00\u540c\uff0c\u56e0\u4e4b\u624d\u9000\u51fa\u8ff7\u8e2a\u8c37\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u4e0d\u7ba1\u5982\u4f55\uff0c\u949f\u526f\u76df\u4e3b\u4e0d\u8be5\u4e0d\u8f9e\u800c\u522b\uff0c\u5bb3\u5f97\u8001\u592b\u8bbf\u67e5\u591a\u65e5\uff0c\u624d\u77e5\u9053\u539f\u6765\u5927\u5bb6 \u5df2\u6765\u5230\u4e07\u6708\u5821\u3002\u201d \u3000\u3000\u949f\u4e00\u8c6a\u53c8\u662f\u6b49\u7136\u4e00\u7b11\u9053\uff1a\u201c\u949f\u67d0\u7b49\u4eba\u5e76\u975e\u4e0d\u60f3\u5411\u76df\u4e3b\u62dc\u8f9e\uff0c\u4f46\u53c8\u62c5\u5fc3\u76df\u4e3b\u575a\u7559\uff0c\u65e0\u6cd5\u6210 \u884c\uff0c\u4e0d\u606d\u4e4b\u5904\uff0c\u8fd8\u8bf7\u591a\u591a\u539f\u8c05\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u84e6\u5730\u795e\u8272\u51dd\u91cd\uff0c\u6447\u5934\u9053\uff1a\u201c\u949f\u526f\u76df\u4e3b\u8fd9\u8bdd\u9519\u4e86\uff0c\u8ff7\u8e2a\u8c37\u867d\u7136\u521b\u7acb\u5df2\u7ecf\u4e09\u5e74\u6709\u4f59\uff0c \u4f46\u81f3\u4eca\u5374\u4ecd\u611f\u4eba\u624b\u4e0d\u8db3\u3002\u5f7c\u6b64\u6b63\u8be5\u622e\u529b\u540c\u5fc3\uff0c\u4e3a\u5b8c\u6210\u80e1\u3001\u8c37\u4e24\u4f4d\u6545\u53bb\u76df\u4e3b\u7684\u9057\u5fd7\u800c\u5171\u540c\u594b\u6597\uff0c \u5c24\u5176\u526f\u76df\u4e3b\u548c\u67cf\u9f84\u9662\u7684\u5404\u4f4d\u5934\u9886\uff0c\u6b63\u662f\u54b1\u4eec\u603b\u5be8\u7684\u6700\u5927\u652f\u67f1\u3002\u5982\u4eca\u8fd9\u6837\u4e00\u8d70\uff0e\u5982\u4f55\u5bf9\u5f97\u4f4f\u80e1\u3001 \u8c37\u4e24\u4f4d\u524d\u4efb\u76df\u4e3b\u7684\u5728\u5929\u4e4b\u7075\uff01\u201d \u3000\u3000\u53ea\u542c\u9ea6\u5c0f\u660e\u54fc\u4e86\u58f0\u9053\uff1a\u201c\u970d\u76df\u4e3b\u8bf4\u5f97\u5b9e\u5728\u51a0\u5195\u5802\u7687\uff0c\u6211\u4eec\u5982\u679c\u4e0d\u8d70\uff0c\u96be\u9053\u8fd8\u8981\u7b49\u7740\u88ab\u6d3b \u6d3b\u70e7\u6b7b\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u8138\u8272\u2014\u53d8\u9053\uff1a\u201c\u9ea6\u8001\u5f1f\u600e\u53ef\u8fd9\u6837\u8bf4\u8bdd\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u4e00\u633a\u80f8\u9053\uff1a\u201c\u6211\u8bf4\u7684\u6709\u4ec0\u4e48\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u711a\u70e7\u67cf\u9f84\u9662\uff0c\u53ea\u662f\u6709\u4eba\u9884\u8c0b\uff0c\u5e76\u672a\u917f\u6210\u5de8\u7978\uff0c\u9ea6\u8001\u5f1f\u8fd9\u6837\u8bf4\u8bdd\uff0c\u5c82\u4e0d\u662f\u8a00 \u91cd\u4e86\uff1f\u201d \u3000\u3000\u201c\u6709\u4e00\u5fc5\u6709\u4e8c\uff0c\u6709\u4e8c\u5fc5\u6709\u4e09\uff0c\u4e00\u8ba1\u4e0d\u6210\uff0c\u5fc5\u5b9a\u518d\u751f\u4e8c\u8ba1\uff0e\u6211\u4eec\u82e5\u4ecd\u7559\u5728\u67cf\u9f84\u9662\uff0c\u8fdf\u65e9\u603b \u4f1a\u88ab\u70e7\u6b7b\u7684\uff0c\u8fd9\u8bdd\u5e94\u8be5\u4e0d\u5047\u5427\uff1f\u201d \u3000\u3000\u201c\u8001\u592b\u53ef\u4ee5\u62c5\u4fdd\uff0c\u4eca\u540e\u82e5\u518d\u6709\u4eba\u62c5\u6562\u5411\u67cf\u9f84\u9662\u7eb5\u706b\uff0c\u5404\u4f4d\u5c31\u552f\u8001\u592b\u662f\u95ee\uff01\u201d \u3000\u3000\u201c\u4e0a\u6b21\u6211\u4eec\u8fd8\u4e0d\u662f\u95ee\u8fc7\u4f60\u6765\uff0c\u53ef\u60dc\u4f60\u5e76\u6ca1\u6709\u5411\u6211\u4eec\u6709\u6240\u4ea4\u4ee3\u3002\u201d \u3000\u3000\u201c\u96be\u9053\u9ad8\u6765\u4e3a\u8fd9\u4ef6\u4e8b\u800c\u6b7b\uff0c\u8fd8\u4e0d\u591f\u4e48\uff1f\u201d \u3000\u3000\u201c\u9ad8\u6765\u53ea\u662f\u66ff\u4e3b\u8c0b\u4e4b\u4eba\u4ee3\u6b7b\uff0e\u970d\u76df\u4e3b\uff0c\u5927\u5bb6\u90fd\u4e0d\u662f\u4e09\u5c81\u4e8c\u5c81\u5b69\u5b50\uff0c\u8fd9\u79cd\u4e8b\u8c01\u4e5f\u552c\u4e0d\u4e86 \u8c01\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u4e24\u592a\u9633\u7a74\u731b\u5730\u62bd\u52a8\u4e86\u51e0\u4e0b\uff0c\u521a\u8981\u53d1\u4f5c\uff0c\u9ea6\u5c0f\u660e\u53c8\u9053\uff1a\u201c\u970d\u76df\u4e3b\uff0c\u4e8b\u60c5\u5df2\u7ecf\u8fc7\u53bb\u4e86\uff0c \u6211\u4eec\u4e5f\u4e0d\u60f3\u518d\u8ffd\u7a76\u3002\u4f60\u8fdc\u6765\u662f\u5ba2\uff0c\u6211\u4eec\u51b3\u5b9a\u5bf9\u4f60\u4ee5\u5ba2\u793c\u76f8\u5f85\uff0c\u8bf7\u4f60\u4e0d\u8981\u518d\u63d0\u8981\u6211\u4eec\u91cd\u56de\u8ff7\u8e2a \u8c37\u4e4b\u4e8b\uff0c\u597d\u5417\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u5e72\u54b3\u4e86\u58f0\u9053\uff1a\u201c\u5404\u4f4d\u771f\u7684\u8fd9\u6837\u575a\u51b3\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u5e76\u975e\u6211\u4eec\u575a\u51b3\uff0c\u800c\u662f\u5df2\u7ecf\u51b3\u5b9a\u4e4b\u4e8b\uff0c\u5c31\u4e0d\u80fd\u66f4\u6539\uff0e\u6211\u4eec\u5f53\u521d\u8fdb\u5165\u8ff7\u8e2a\u8c37\uff0e \u5e76\u672a\u5356\u8eab\u7ed9\u4efb\u4f55\u4eba\uff0c\u53bb\u7559\u5f53\u7136\u8981\u7531\u81ea\u5df1\u51b3\u5b9a\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u987f\u4e86\u987f\u9053\uff1a\u201c\u8001\u592b\u53ea\u662f\u8bda\u5fc3\u8bda\u610f\u524d\u6765\u633d\u7559\uff0c\u603b\u5e0c\u671b\u5404\u4f4d\u80fd\u5171\u4f53\u65f6\u8270\uff0c\u4e3a\u5b8c\u6210\u80e1\u3001 \u8c37\u4e24\u4f4d\u524d\u4efb\u76df\u4e3b\u672a\u7adf\u5fd7\u4e1a\u800c\u7ee7\u7eed\u5c3d\u529b\uff0e\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u7b11\u9053\uff1a\u201c\u4f60\u8bf4\u5f97\u53ef\u771f\u597d\u542c\uff0c\u5904\u5904\u628a\u80e1\u3001\u8c37\u4e24\u4f4d\u524d\u4efb\u76df\u4e3b\u642c\u51fa\u6765\u538b\u6211\u4eec\uff0e\u4f46\u4e0d\u77e5\u80e1\u3001 \u8c37\u4e24\u4f4d\u76df\u4e3b\u751f\u524d\uff0c\u4f60\u4e3a\u4ed6\u4eec\u51fa\u8fc7\u591a\u5c11\u529b\uff1f\u201d \u3000\u3000\u201c\u9ea6\u8001\u5f1f\uff0c\u4f60\u600e\u4e48\u8001\u8fd9\u6837\u8bf4\u8bdd\uff1f\u201d \u3000\u3000\u201c\u4f60\u8981\u6211\u600e\u6837\u8bf4\u8bdd\uff1f\u201d \u3000\u3000\u201c\u4f60\u2026\u2026\u201d \u3000\u3000\u201c\u4f60\u5e94\u5f53\u653e\u660e\u767d\u70b9\uff0c\u73b0\u5728\u6211\u5df2\u4e0d\u662f\u4f60\u7684\u5c5e\u4e0b\uff0c\u4f60\u4e5f\u7528\u4e0d\u7740\u518d\u53eb\u949f\u4e00\u8c6a\u526f\u76df\u4e3b\uff0c\u4e07\u6708\u5821\u5e76 \u975e\u7eff\u6797\u603b\u5be8\uff0c\u54ea\u91cc\u6765\u7684\u526f\u76df\u4e3b\uff1f\u201d \u3000\u3000\u201c\u8bf8\u4f4d\u5f53\u771f\u4e0d\u60f3\u56de\u53bb\uff1f\u201d \u3000\u3000\u201c\u662f\u5426\u4f60\u7528\u5f3a\u8feb\u7684\uff1f\u6211\u770b\u4f60\u4eca\u5929\u5e26\u6765\u7684\u4eba\u5f88\u591a\uff0e\u8981\u60f3\u600e\u4e48\u6837\uff0c\u5c31\u76f4\u63a5\u8bf4\u51fa\u6765\u5427\uff01\u201d \u3000\u3000\u949f\u4e00\u8c6a\u548c\u4f59\u4ea6\u4e50\u62c5\u5fc3\u4e8b\u60c5\u9a6c\u4e0a\u95f9\u7ffb\uff0c\u8fde\u5fd9\u4ee5\u773c\u8272\u5411\u9ea6\u5c0f\u660e\u963b\u6b62\u3002 \u3000\u3000\u970d\u5143\u4f3d\u51b7\u51b7\u4e00\u7b11\u9053\uff1a\u201c\u949f\u526f\u76df\u4e3b\uff0c\u4f59\u5148\u751f\uff0c\u4f60\u4eec\u4e24\u4f4d\u662f\u5426\u8fd8\u8981\u518d\u8868\u793a\u4e00\u4e0b\u610f\u89c1\uff1f\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u5411\u5927\u5385\u5916\u63a0\u4e86\u4e00\u773c\uff0c\u4e0d\u52a8\u58f0\u8272\u9053\uff1a\u201c\u76df\u4e3b\u65b9\u624d\u66fe\u4e00\u518d\u63d0\u8d77\u8ff7\u8e2a\u8c37\u5185\u4eba\u624b\u4e0d\u8db3\uff0c\u95e8 \u5916\u8fd9\u4e9b\u4eba\uff0c\u4f59\u67d0\u5168\u4e0d\u8ba4\u8bc6\uff0c\u4ed6\u4eec\u662f\u5426\u65b0\u8fd1\u5230\u8ff7\u8e2a\u8c37\u5165\u4f19\u7684\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u62c2\u987b\u6de1\u6de1\u7b11\u9053\uff1a\u201c\u4f59\u5148\u751f\u597d\u773c\u529b\uff0c\u4ed6\u4eec\u786e\u5b9e\u591a\u6570\u662f\u65b0\u8fd1\u52a0\u5165\u8ff7\u8e2a\u8c37\u7684\uff0c\u53ea\u662f\u8ff7\u8e2a \u8c37\u662f\u5929\u4e0b\u7eff\u6797\u603b\u5be8\uff0c\u867d\u6709\u4ed6\u4eec\u52a0\u5165\uff0c\u529b\u91cf\u8fd8\u662f\u5355\u8584\u5f97\u5f88\u3002\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u9ed8\u4e86\u4e00\u9ed8\u9053\uff1a\u201c\u76df\u4e3b\u53ef\u5426\u628a\u8fd9\u4e9b\u4eba\u7684\u6765\u5386\u8bf4\u660e\u4e00\u4e0b\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u4f3c\u662f\u5341\u5206\u5f97\u610f\uff0c\u624b\u6307\u90a3\u4e94\u540d\u76f8\u8c8c\u602a\u5f02\u7684\u4eba\u9053\uff1a\u201c\u4f59\u5148\u751f\u4e00\u5411\u89c1\u591a\u8bc6\u5e7f\uff0c\u60f3\u5fc5\u542c\u8bf4 \u8fc7\u2018\u5d06\u5cd2\u4e94\u4e49\u2019\u5427\uff1f\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u4e0d\u89c9\u5fc3\u5934\u2014\u9707\u9053\uff1a\u201c\u83ab\u975e\u4ed6\u4eec\u662f\u2018\u5d06\u5cd2\u4e94\u9b54\u2019\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u563f\u563f\u7b11\u9053\uff1a\u201c\u4ed6\u4eec\u867d\u7136\u662f\u2018\u5d06\u5cd2\u4e94\u9b54\u2019\uff0c\u4f46\u5728\u6295\u5165\u54b1\u4eec\u8ff7\u8e2a\u8c37\u540e\uff0c\u4e5f\u8be5\u6539\u79f0\u2018\u5d06 \u5cd2\u4e94\u4e49\u2019\u4e86\uff01\u201d \u3000\u3000\u8fd9\u65f6\u4e0d\u4f46\u4f59\u4ea6\u4e50\u5403\u60ca\uff0c\u949f\u4e00\u8c6a\u4e5f\u6697\u81ea\u6115\u9a87\u4e0d\u5df2\u3002 \u3000\u3000\u4ed6\u4eec\u4ece\u524d\u867d\u672a\u89c1\u8fc7\u201c\u5d06\u5cd2\u4e94\u9b54\u201d\uff0c\u4f46\u5374\u6df1\u77e5\u8fd9\u4e94\u4eba\u4e2a\u4e2a\u5fc3\u72e0\u624b\u8fa3\uff0e\u5728\u6c5f\u6e56\u4e0a\u6076\u540d\u662d\u5f70\uff0c \u4e0d\u8bba\u9ed1\u767d\u4e24\u9053\uff0c\u4eba\u4eba\u4e3a\u4e4b\u4fa7\u76ee\u3002 \u3000\u3000\u970d\u5143\u4f3d\u7a76\u7adf\u7528\u7684\u4ec0\u4e48\u65b9\u6cd5\uff0e\u7adf\u628a\u8fd9\u4e94\u4eba\u4e5f\u5f15\u8fdb\u8ff7\u8e2a\u8c37\u5185\uff1f \u3000\u3000\u4f59\u4ea6\u4e50\u6781\u529b\u4fdd\u6301\u7740\u9547\u5b9a\uff0c\u518d\u9053\uff1a\u201c\u8ff7\u8e2a\u8c37\u600e\u4f1a\u53c8\u6709\u8fd9\u4e48\u591a\u51fa\u5bb6\u4eba\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u563f\u563f\u7b11\u9053\uff1a\u201c\u949f\u526f\u76df\u4e3b\u3001\u4f59\u5148\u751f\uff0c\u54b1\u4eec\u8ff7\u8e2a\u8c37\u603b\u7b97\u51fa\u4e86\u4e00\u53e3\u6c14\uff01\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u8fd8\u8bf7\u76df\u4e3b\u628a\u8bdd\u8bf4\u660e\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1b\u201c\u54b1\u4eec\u8ff7\u8e2a\u8c37\u7eff\u6797\u5927\u603b\u5be8\uff0c\u4e00\u5411\u88ab\u81ea\u8be9\u4e3a\u6b63\u5927\u95e8\u6d3e\u7684\u5c11\u6797\u3001\u6b66\u5f53\u55bb\u4e3a\u90aa\u6076\u7ec4 \u7ec7\uff0c\u4ed6\u4eec\u54ea\u4f1a\u60f3\u5230\u5982\u4eca\u4e5f\u6709\u6240\u8c13\u6b63\u5927\u95e8\u6d3e\u7684\u4eba\u52a0\u5165\u54b1\u4eec\u8ff7\u8e2a\u8c37\u6765\uff01\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u5403\u60ca\u9053\uff1a\u201c\u83ab\u975e\u4ed6\u4eec\u662f\u4ece\u5c11\u6797\u6765\u7684\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u5f97\u610f\u4e00\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff01\u4ed6\u4eec\u6b63\u662f\u7531\u5c11\u6797\u6539\u6295\u54b1\u4eec\u8ff7\u8e2a\u8c37\u7684\u3002\u201d \u3000\u3000\u201c\u5c11\u6797\u7684\u50e7\u5165\uff0c\u600e\u4f1a\u6539\u6295\u5230\u8ff7\u8e2a\u8c37\u6765\uff0e\u83ab\u975e\u662f\u5c11\u6797\u7684\u53db\u5f92\uff1f\u201d \u3000\u3000\u201c\u5728\u5c11\u6797\u6765\u8bf4\uff0c\u5f53\u7136\u4f1a\u79f0\u4ed6\u4eec\u53db\u5f92\uff0c\u4f59\u5148\u751f\u53ef\u542c\u8bf4\u8fc7\u5c11\u6797\u5341\u516b\u94dc\u4eba\u9635\uff1f\u201d \u3000\u3000\u201c\u4f59\u67d0\u867d\u7136\u5b64\u964b\u5be1\u95fb\uff0c\u4f46\u5c11\u6797\u5341\u516b\u94dc\u4eba\u9635\u8fd9\u6837\u9f0e\u9f0e\u5927\u540d\uff0c\u5374\u603b\u4e0d\u80fd\u6ca1\u542c\u8bf4\u8fc7\u3002\u76df\u4e3b\u4e3a\u4ec0 \u4e48\u5ffd\u7136\u63d0\u8d77\u5c11\u6797\u5341\u516b\u94dc\u4eba\u9635\u6765\uff1f\u201d \u3000\u3000\u201c\u4ed6\u4eec\u5c31\u662f\u5c11\u6797\u5341\u516b\u94dc\u4eba\uff0e\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u6709\u4e9b\u51e0\u4e4e\u4e0d\u6562\u76f8\u4fe1\u81ea\u5df1\u7684\u8033\u6735\uff0e\u60c5\u4e0d\u81ea\u7981\u53c8\u5411\u5916\u671b\u4e86\u2014\u773c\u9053\uff1a\u201c\u4ed6\u4eec\u597d\u50cf\u53ea\u6709\u5341 \u4eba\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u9894\u9996\u9053\uff1a\u201c\u4e0d\u9519\uff01\u4ed6\u4eec\u53ea\u662f\u5341\u516b\u94dc\u4eba\u7684\u4e00\u534a\uff0c\u90a3\u4e00\u4e2a\u8863\u670d\u7565\u5e26\u6697\u7ea2\u8272\u7684\uff0c\u662f\u5341\u516b \u94dc\u4eba\u7684\u9886\u961f\uff0c\u6cd5\u53f7\u609f\u660e\uff0c\u5c11\u6797\u7684\u5341\u516b\u94dc\u4eba\uff0c\u90fd\u662f\u7531\u4ed6\u8d1f\u8d23\u6307\u6325\u3002\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u9053\uff1a\u201c\u53e6\u5916\u4e5d\u540d\u94dc\u4eba\u5462\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u50b2\u7136\u9053\uff1a\u201c\u6709\u4e5d\u540d\u5c11\u6797\u94dc\u4eba\uff0c\u5df2\u7ecf\u591f\u66ff\u54b1\u4eec\u8ff7\u8e2a\u8c37\u4e89\u5149\u4e86\uff01\u201d \u3000\u3000\u201c\u4ed6\u4eec\u7a76\u7adf\u600e\u4f1a\u6295\u5230\u8ff7\u8e2a\u8c37\u6765\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u662f\u9886\u961f\u609f\u660e\u72af\u4e86\u5c11\u6797\u7684\u6212\u89c4\uff0c\u5c11\u6797\u638c\u95e8\u8981\u5904\u5206\u4ed6\uff0c\u609f\u660e\u4e3a\u4e86\u514d\u53d7\u5904\u5206\uff0c\u4fbf\u51b3\u5b9a\u5e26 \u9886\u5341\u516b\u94dc\u4eba\u5077\u5077\u9003\u51fa\u5c11\u6797\u3002\u4f46\u56e0\u5176\u4ed6\u4e5d\u4eba\u4e0d\u80af\uff0c\u4fbf\u53ea\u6709\u5e26\u7740\u8fd9\u4e5d\u540d\u94dc\u4eba\uff0c\u8fde\u591c\u51b2\u51fa\u5d69\u5c71\uff0c\u6295 \u5230\u54b1\u4eec\u8ff7\u8e2a\u8c37\u6765\u3002\u201d \u3000\u3000\u201c\u4ed6\u4eec\u4e3a\u4ec0\u4e48\u9009\u62e9\u4e86\u8ff7\u8e2a\u8c37\uff1f\u201d \u3000\u3000\u201c\u7406\u7531\u5f88\u7b80\u5355\uff0c\u56e0\u4e3a\u4ed6\u4eec\u82e5\u6295\u5176\u4ed6\u95e8\u6d3e\uff0c\u8bf4\u4e0d\u5b9a\u5176\u4ed6\u95e8\u6d3e\u4e0d\u4f46\u4e0d\u6562\u6536\u7559\uff0c\u8fd8\u4f1a\u79d8\u5bc6\u901a\u77e5 \u5c11\u6797\u628a\u4ed6\u4eec\u62bc\u56de\u5d69\u5c71\u3002\u6295\u5230\u8ff7\u8e2a\u8c37\uff0c\u81ea\u7136\u6700\u4e3a\u5b89\u5168\u3002\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u4e0d\u518d\u8a00\u8bed\u3002 \u3000\u3000\u970d\u5143\u4f3d\u671b\u4e86\u949f\u4e00\u8c6a\u548c\u4f59\u4ea6\u4e50\u4e00\u773c\uff0c\u8bed\u6c14\u90d1\u91cd\u7684\u9053\uff1a\u201c\u4e8c\u4f4d\u5230\u5e95\u80af\u4e0d\u80af\u56de\u53bb\uff0c\u76ee\u524d\u54b1\u4eec\u8ff7 \u8e2a\u8c37\uff0c\u786e\u5b9e\u5df2\u7ecf\u5b9e\u529b\u5927\u589e\uff0c\u8001\u592b\u6b63\u76fc\u7740\u67cf\u9f84\u9662\u7684\u5404\u4f4d\u5934\u9886\u56de\u53bb\uff0c\u5171\u8944\u5927\u4e8b\u3002\u201d \u3000\u3000\u949f\u4e00\u8c6a\u4e0d\u52a8\u58f0\u8272\u9053\uff1a\u201c\u65e2\u7136\u8ff7\u8e2a\u8c37\u5df2\u6709\u5d06\u5cd2\u4e94\u4e49\u548c\u5c11\u6797\u7684\u4e5d\u540d\u94dc\u4eba\u52a0\u5165\uff0c\u970d\u76df\u4e3b\u5df2\u8db3\u53ef \u50b2\u89c6\u7fa4\u96c4\uff0c\u949f\u67d0\u7b49\u4eba\u56de\u4e0d\u56de\u53bb\uff0c\u5b9e\u5728\u5df2\u7ecf\u4e0d\u91cd\u8981\u4e86\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u7ec8\u4e8e\u8138\u8272\u5fae\u5fae\u4e00\u53d8\u9053\uff1a\u201c\u8001\u592b\u529d\u949f\u526f\u76df\u4e3b\u8fd8\u662f\u56de\u53bb\u7684\u597d\u3002\u201d \u3000\u3000\u53ea\u542c\u9ea6\u5c0f\u660e\u9ad8\u58f0\u9053\uff1a\u201c\u970d\u76df\u4e3b\uff0c\u4f60\u53ef\u662f\u8981\u7528\u5f3a\u8feb\u7684\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u9762\u8272\u4e00\u6c89\uff0c\u559d\u9053\uff1a\u201c\u4f60\u600e\u77e5\u8001\u592b\u662f\u8981\u7528\u5f3a\u8feb\u7684\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1b\u201c\u4f60\u65b9\u624d\u6781\u529b\u70ab\u8000\u4ec0\u4e48\u5d06\u86d4\u4e94\u9b54\u548c\u5c11\u6797\u94dc\u4eba\uff0c\u5206\u660e\u662f\u5728\u5411\u6211\u4eec\u793a\u5a01\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u970d\u7136\u8d77\u8eab\u6012\u9053\uff1a\u201c\u4e0d\u7ba1\u662f\u8bf7\u4f60\u4eec\u56de\u53bb\u4e5f\u7f62\uff0c\u5f3a\u8feb\u4f60\u4eec\u56de\u53bb\u4e5f\u7f62\uff0c\u8001\u592b\u4eca\u5929\u65e2\u7136\u6765 \u4e86\uff0c\u5c31\u7edd\u4e0d\u80fd\u7a7a\u624b\u56de\u53bb\uff01\u201d \u3000\u3000\u4ed6\u8bf4\u7740\u5429\u5490\u5df4\u5929\u4e49\u9053\uff1a\u201c\u51fa\u53bb\u4ea4\u4ee3\u609f\u660e\uff0c\u9a6c\u4e0a\u6446\u4e0b\u94dc\u4eba\u9635\uff0c\u5176\u4f59\u7684\u4eba\u4e00\u8d77\u5f85\u547d\u8fce\u6218\uff01\u201d \u3000\u3000\u5df4\u5929\u4e49\u5306\u5306\u5954\u51fa\u5927\u5385\u5916\u3002 \u3000\u3000\u970d\u5143\u4f3d\u518d\u671b\u5411\u5b8b\u5929\u94ce\u9053\uff1a\u201c\u54b1\u4eec\u51fa\u53bb\uff01\u201d \u3000\u3000\u5927\u5385\u5185\u53ea\u5269\u4e0b\u949f\u2014\u8c6a\u3001\u4f59\u4ea6\u4e50\u3001\u9ea6\u5c0f\u660e\uff0c\u674e\u8302\u56db\u4eba\uff0e \u3000\u3000\u949f\u4e00\u8c6a\u6ee1\u9762\u6b49\u610f\uff0c\u5411\u674e\u8302\u62f1\u4e86\u62f1\u624b\u9053\uff1a\u201c\u4e07\u6708\u5821\u2014\u5411\u76f8\u5b89\u65e0\u4e8b\uff0c\u60f3\u4e0d\u5230\u949f\u67d0\u7b49\u4eba\u6765\u5230\u4e0d \u4e45\uff0c\u5c31\u7ed9\u5144\u5f1f\u5e26\u6765\u8fd9\u4e48\u591a\u707e\u7978\uff0c\u5b9e\u5728\u62b1\u6b49\u5f97\u5f88\uff01\u201d \u3000\u3000\u674e\u8302\u8bed\u6c14\u51dd\u91cd\u9053\uff1a\u201c\u4e8b\u5df2\u5982\u6b64\uff0c\u949f\u5927\u4fa0\u7528\u4e0d\u7740\u518d\u8bf4\u4ec0\u4e48\uff0e\u603b\u4e4b\uff0c\u674e\u67d0\u5e0c\u671b\u4e0d\u8981\u628a\u4e8b\u60c5\u95f9 \u5f97\u592a\u5927\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4f60\u4eec\u662f\u4e0d\u662f\u6028\u6211\uff0c\u56e0\u4e3a\u4e8b\u60c5\u95f9\u5230\u8fd9\u79cd\u5730\u6b65\uff0c\u90fd\u662f\u7531\u6211\u6311\u8d77\u6765\u7684\u3002\u201d \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u9ea6\u5c0f\u5144\u5f1f\u7528\u4e0d\u7740\u57cb\u6028\u81ea\u5df1\uff0c\u4e8b\u60c5\u603b\u662f\u8981\u89e3\u51b3\u7684\uff0c\u53ea\u4e0d\u8fc7\u6765\u5f97\u592a\u65e9\u4e86\u4e00\u70b9\u800c \u5df2\uff0c\u8bf7\u4f60\u901a\u77e5\u5927\u5385\u540e\u7684\u4eba\uff0e\u8981\u4ed6\u4eec\u5230\u5927\u5385\u5916\u8fce\u6218\uff01\u201d \u3000\u3000\u5f88\u5feb\u7684\uff0c\u53cc\u65b9\u4eba\u9a6c\u4fbf\u5df2\u5217\u9635\u76f8\u5f85\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u5239\u90a3\uff0c\u5927\u5385\u5916\u6c14\u6c1b\u7684\u51dd\u8083\uff0c\u51e0\u4e4e\u5df2\u5230\u4e86\u6781\u70b9\u3002 \u3000\u3000\u8c01\u90fd\u77e5\u9053\uff0c\u8fd9\u53ea\u662f\u66b4\u98ce\u96e8\u524d\u7684\u5b81\u9759\uff0c\u7247\u523b\u4e4b\u540e\uff0c\u4fbf\u5c06\u51fa\u73b0\u8165\u98ce\u8840\u96e8\u7684\u65e0\u60c5\u573a\u9762\u3002 \u3000\u3000\u971c\u5143\u4f3d\u7f13\u7f13\u626b\u63a0\u949f\u4e00\u8c6a\u7b49\u4eba\u4e00\u773c\u9053\uff1a\u201c\u8001\u592b\u4eca\u5929\u5bf9\u5404\u4f4d\uff0c\u53ef\u8bf4\u4ec1\u81f3\u4e49\u5c3d\uff0c\u73b0\u5728\u5200\u5175\u76f8\u89c1\uff0c \u4e5f\u662f\u8feb\u4e0d\u5f97\u5df2\u4e4b\u4e8b\uff0e \u3000\u3000\u771f\u662f\u60f3\u4e0d\u5230\uff0c\u5f7c\u6b64\u539f\u662f\u4e00\u5bb6\u4eba\uff0c\u5982\u4eca\u7adf\u7136\u8981\u81ea\u76f8\u6b8b\u6740\uff0c\u771f\u53ef\u8c13\u662f\u4ef6\u5927\u5927\u4e0d\u5e78\u4e4b\u4e8b\u3002\u73b0\u5728 \u8001\u592b\u60c5\u613f\u518d\u7ed9\u5404\u4f4d\u4e00\u70b9\u65f6\u95f4\u8003\u8651\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u53eb\u9053\uff1a\u201c\u970d\u5143\u4f3d\uff0c\u8fde\u9635\u5f0f\u90fd\u6446\u597d\u4e86\uff0c\u8fd8\u6709\u4ec0\u4e48\u53ef\u8003\u8651\u7684\uff1f\u201d \u3000\u3000\u53ea\u542c\u5df4\u5929\u4e49\u5927\u559d\u9053\uff1a\u201c\u597d\u4e00\u4e2a\u5927\u80c6\u53db\u9006\uff0c\u7adf\u6562\u76f4\u547c\u76df\u4e3b\u540d\u8bb3\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u53cc\u76ee\u5c04\u5149\uff0c\u53f1\u9053\uff1a\u201c\u5df4\u5929\u4e49\uff0c\u4f60\u4e2a\u4ec0\u4e48\u4e1c\u897f\uff1f\u606c\u4e0d\u77e5\u803b\uff01\u201d \u3000\u3000\u5df4\u5929\u4e49\u53ea\u6c14\u5f97\u8eab\u5b50\u4e00\u98a4\uff0c\u53f1\u9053\uff1a\u201c\u4f60\u6562\u9a82\u6211\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u51b7\u7b11\u9053\uff1a\u201c\u5c82\u6b62\u9a82\u4f60\uff0e\u672c\u5c11\u7237\u8fd8\u60f3\u63cd\u4f60\u8fd9\u738b\u516b\u86cb\u54e9\uff0c\u6709\u79cd\u5c31\u7ed9\u6211\u7ad9\u51fa\u6765\uff01\u201d \u3000\u3000\u5df4\u5929\u4e49\u5f53\u5e74\u5728\u6c5f\u6e56\u9053\u4e0a\uff0c\u672c\u662f\u4f4d\u540d\u5934\u53ee\u5f53\u54cd\u7684\u4eba\u7269\uff0c\u5373\u4f7f\u5728\u8ff7\u8e2a\u8c37\u7eff\u6797\u603b\u5be8\uff0c\u8bba\u8eab\u4efd\u5730 \u4f4d\uff0c\u4e5f\u4ec5\u6b21\u4e8e\u76df\u4e3b\u526f\u76df\u4e3b\u3002 \u3000\u3000\u4f17\u76ee\u777d\u777d\u4e4b\u4e0b\uff0c\u5c82\u80fd\u53d7\u6b64\u4fae\u8fb1\uff1f\u4ed6\u867d\u660e\u77e5\u4e0d\u662f\u9ea6\u5c0f\u660e\u7684\u5bf9\u624b\uff0c\u5374\u4e5f\u4e0d\u5f97\u4e0d\u51fa\u800c\u8fce\u6218\u3002 \u3000\u3000\u5f53\u4e0b\uff0c\u3000\u4e00\u677e\u8170\u95f4\u6263\u628a\uff0c\u4e00\u67c4\u901a\u4f53\u4e4c\u9ed1\uff0c\u540d\u53eb\u201c\u4e5d\u8282\u4e94\u7eeb\u641c\u9b42\u97ad\u201d\u7684\u5947\u5f62\u5175\u5203\u4fbf\u5012\u63d0\u5728 \u624b\u4e2d\u3002 \u3000\u3000\u4ed6\u8fd9\u201c\u4e5d\u8282\u4e94\u7eeb\u641c\u9b42\u97ad\u201d\u65bd\u5c55\u5f00\u6765\uff0c\u62db\u672f\u5341\u5206\u5947\u8be1\uff0c\u5f53\u5e74\u6709\u4e0d\u5c11\u6b66\u6797\u9ad8\u624b\uff0c\u90fd\u4e27\u547d\u5728\u4ed6 \u97ad\u4e0b\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u6839\u672c\u6ca1\u628a\u5df4\u5929\u4e49\u653e\u5728\u773c\u91cc\uff0c\u968f\u5373\u4e5f\u4e0d\u614c\u4e0d\u5fd9\u8d70\u4e86\u51fa\u6765\u3002 \u3000\u3000\u4ed6\u539f\u6709\u4e24\u79cd\u5175\u5203\uff0c\u3000\u4e00\u79cd\u662f\u5251\uff0c\u522b\u4e00\u79cd\u662f\u4e00\u6761\u516b\u4e5d\u5c3a\u957f\uff0c\u9ed1\u5149\u95ea\u95ea\uff0c\u5927\u6307\u7c97\u7ec6\u5f62\u5982\u8f6f\u97ad \u7684\u5175\u5203\u3002 \u3000\u3000\u4e3a\u4e86\u5bf9\u4ed8\u5bf9\u65b9\u7684\u602a\u97ad\uff0c\u8fd9\u6b21\u4ed6\u52a8\u7528\u4e86\u8f6f\u97ad\uff0c\u90a3\u53e3\u5251\u5219\u63d2\u5728\u80a9\u5934\u3002 \u3000\u3000\u53ea\u542c\u970d\u5143\u4f3d\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u7684\u6b66\u529f\u8def\u6570\u5e9e\u6742\u800c\u53c8\u5947\u8be1\uff0c\u5df4\u8001\u5f1f\u5c0f\u5fc3\u4e86\uff01\u201d \u3000\u3000\u5df4\u5929\u4e49\u9053\uff1a\u201c\u76df\u4e3b\u653e\u5fc3\uff0e\u5c5e\u4e0b\u81ea\u6709\u8fce\u654c\u4e4b\u9053\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4f60\u8fd9\u8001\u5c0f\u5b50\u522b\u53ea\u987e\u8bb2\u5e9f\u8bdd\uff0c\u5feb\u5feb\u51fa\u62db\uff0c\u672c\u5c11\u7237\u7b49\u7740\u4f60\uff01\u201d \u3000\u3000\u5df4\u5929\u4e49\u53ea\u6c14\u5f97\u4f53\u5185\u8840\u6c14\u76f4\u51b2\uff0c\u7eb5\u8eab\u76f4\u6251\uff0c\u641c\u9b42\u97ad\u4e00\u62db\u201c\u5377\u4e91\u98de\u96ea\u201d\uff0c\u95ea\u7535\u822c\u76f4\u5411\u9ea6\u5c0f\u660e \u5f53\u5934\u659c\u626b\u800c\u4e0b\u3002\u51fa\u62db\u4e4b\u5feb\uff0c\u4ee4\u4eba\u76ee\u4e0d\u6687\u63a5\u3002 \u3000\u3000\u5c82\u77e5\u9ea6\u5c0f\u660e\u5e76\u4e0d\u8fce\u51fb\uff0c\u53cd\u800c\u632b\u8eab\u5411\u540e\u8fde\u9000\u4e09\u6b65\u3002 \u3000\u3000\u5df4\u5929\u4e49\u4e00\u843d\u7a7a\uff0c\u7d27\u8ddf\u7740\u7eb5\u8eab\u75be\u8fdb\uff0c\u201c\u5237\u5237\u5237\u201d\uff0c\u3000\u2014\u8fde\u53c8\u662f\u4e09\u62db\u626b\u51fa\u3002 \u3000\u3000\u8fd9\u4e09\u62db\u867d\u662f\u8fde\u5288\u51fa\u624b\uff0c\u4f46\u52bf\u9053\u5374\u5404\u81ea\u4e0d\u540c\uff0c\u5176\u4e2d\u6709\u5288\u6709\u70b9\u6709\u6233\uff0c\u679c\u7136\u53d8\u5316\u591a\u7aef\uff0c\u8be1\u5f02\u83ab \u6d4b\uff0c\u770b\u5f97\u4eba\u773c\u82b1\u7f2d\u4e71\uff0e \u3000\u3000\u9ea6\u5c0f\u660e\u8fd8\u662f\u4e0d\u51fa\u624b\uff0c\u53ea\u662f\u2014\u5473\u95ea\u8eb2\u3002 \u3000\u3000\u4e0d\u8fc7\u8fd9\u65f6\u5df2\u5e76\u975e\u8dc3\u9000\uff0c\u800c\u662f\u4ee5\u6458\u661f\u6b65\u5de6\u53f3\u817e\u632a\u3002 \u3000\u3000\u4ed6\u4e00\u76f4\u4e0d\u80af\u8fd8\u624b\uff0c\u4e0d\u4f46\u53cc\u65b9\u89c2\u6218\u4e4b\u4eba\u770b\u5f97\u6709\u4e9b\u83ab\u540d\u5176\u5999\uff0c\u8fde\u5df4\u5929\u4e49\u4e5f\u611f\u5230\u4e0d\u53ef\u601d\u4e49\u3002 \u3000\u3000\u5df4\u5929\u4e49\u7684\u653b\u52bf\u8d8a\u6765\u8d8a\u731b\uff0c\u641c\u9b42\u97ad\u53d8\u5316\u65e0\u7a7a\u7684\u62db\u672f\uff0c\u51e0\u4e4e\u662f\u4e00\u6c14\u5475\u6210\u3002 \u3000\u3000\u4f46\u4e0d\u8bba\u4ed6\u51fa\u624b\u79fb\u4f4d\u5982\u4f55\u8fc5\u6377\uff0c\u5374\u59cb\u7ec8\u4f24\u4e0d\u5230\u5bf9\u65b9\u5206\u6beb\uff0c\u8fde\u9ea6\u5c0f\u660e\u7684\u8863\u670d\u90fd\u4e0d\u66fe\u89e6\u5230\u3002 \u3000\u3000\u89c2\u6218\u7684\u738b\u5927\u5eb7\u770b\u5f97\u6709\u4e9b\u4e0d\u8010\uff0c\u5927\u53eb\u9053\uff1a\u201c\u9ea6\u8001\u5f1f\uff0c\u4f60\u662f\u600e\u4e48\u5566\uff1f\u201d \u3000\u3000\u5c31\u5728\u738b\u5927\u5eb7\u7684\u8bdd\u58f0\u5c1a\u672a\u843d\u97f3\uff0c\u7a81\u89c1\u9ea6\u5c0f\u660e\u624b\u4e2d\u7684\u8f6f\u97ad\uff0c\u5df2\u50cf\u4e00\u6761\u957f\u86c7\u822c\u98de\u63a0\u51fa\u53bb\uff0c\u63a5\u7740 \u201c\u5427\u201d\u7684\u4e00\u58f0\uff0c\u5df2\u628a\u5df4\u5929\u4e49\u7684\u641c\u9b42\u97ad\u5377\u98de\u51fa\u53bb\u4e24\u4e08\u591a\u8fdc\uff0e \u3000\u3000\u5df4\u5929\u4e49\u5728\u4e0d\u77e5\u4e0d\u89c9\u4e2d\u5931\u53bb\u5175\u5203\uff0c\u5927\u9a87\u4e4b\u4e0b\uff0c\u521a\u8981\u64a4\u8eab\u540e\u9000\uff0c\u4f46\u95fb\u201c\u4e52\u4e53\u201d\u4e24\u58f0\u66b4\u54cd\uff0c\u800c \u988a\u4e0a\u7adf\u53c8\u4e2d\u4e86\u4e24\u6b21\u91cd\u63b4\u3002 \u3000\u3000\u8fd9\u4e24\u8bb0\u8033\u5149\u786e\u5b9e\u4efd\u91cf\u5947\u91cd\uff0c\u53ea\u63b4\u5f97\u5df4\u5929\u4e49\u53cc\u988a\u9c9c\u8840\u76f4\u6d41\uff0c\u4eba\u4e5f\u5934\u660f\u773c\u82b1\u3002 \u3000\u3000\u4f46\u5728\u573a\u4f17\u4eba\uff0e\u5374\u65e0\u4e00\u4eba\u770b\u6e05\u9ea6\u5c0f\u660e\u662f\u5982\u4f55\u51fa\u624b\u7684\u3002 \u3000\u3000\u518d\u770b\u9ea6\u5c0f\u670b\u65f6\uff0c\u6b64\u523b\u4e0d\u4f46\u5df2\u6536\u4e86\u97ad\uff0c\u4eba\u4e5f\u7ad9\u5728\u4e24\u4e08\u5f00\u5916\uff0c\u5c31\u50cf\u521a\u624d\u7684\u4e00\u5e55\u62fc\u6218\uff0c\u5e76\u975e\u53d1 \u751f\u5728\u4ed6\u8eab\u4e0a\u4e00\u6837\u3002 \u3000\u3000\u8fd9\u60c5\u666f\u4f7f\u5f97\u970d\u5143\u4f3d\u8138\u4e0a\u65e9\u5df2\u6302\u4e0d\u4f4f\uff0c\u56de\u5934\u559d\u9053\uff1a\u201c\u94dc\u4eba\u9635\u8fce\u6218\uff01\u201d \u3000\u3000\u7a81\u542c\u4eba\u4e1b\u4e2d\u53d1\u51fa\u4e00\u4e2a\u9634\u9669\u602a\u6c14\u7684\u58f0\u97f3\u9053\uff1a\u201c\u76df\u4e3b\uff0c\u6740\u9e21\u7109\u7528\u725b\u5200\uff0c\u5bf9\u4ed8\u8fd9\u6837\u2014\u4e2a\u540e\u751f\u665a \u8f88\uff0c\u54ea\u91cc\u8fd8\u7528\u5f97\u7740\u6446\u51fa\u94dc\u4eba\u9635\uff0c\u5c31\u628a\u8fd9\u5c0f\u5b50\u4ea4\u7ed9\u9634\u67d0\u597d\u4e86\uff01\u201d \u3000\u3000\u949f\u4e00\u8c6a\u7b49\u4eba\u5faa\u58f0\u671b\u53bb\uff0c\u53ea\u89c1\u8fd9\u4eba\u5e74\u7ea6\u4e94\u5341\u4e0a\u4e0b\uff0c\u751f\u4e86\u4e00\u5f20\u5de6\u8fb9\u767d\u53f3\u8fb9\u9ed1\u7684\u9634\u9633\u8138\uff0c\u4e0b\u5df4 \u7559\u7740\u7a00\u7a00\u843d\u843d\u51e0\u6839\u9ec4\u80e1\u5b50\uff0c\u957f\u76f8\u602a\u5f02\u4e2d\u53c8\u5e26\u7740\u51e0\u5206\u9634\u68ee\u6050\u6016\uff0c\u624b\u91cc\u5012\u63d0\u7740\u4e00\u6761\u5149\u8292\u95ea\u95ea\u7684\u91d1 \u86c7\u97ad\u3002 \u3000\u3000\u539f\u6765\u8fd9\u4eba\u662f\u5d06\u5cd2\u4e94\u9b54\u7684\u8001\u5927\u9634\u5929\u7433\uff0c\u6700\u64c5\u7528\u6bd2\uff0c\u56e0\u4e4b\u5f97\u4e86\u4e2a\u201c\u6bd2\u9b54\u201d\u7684\u7ef0\u53f7\u3002 \u3000\u3000\u970d\u5143\u4f3d\u9000\u540e\u4e24\u6b65\u9053\uff1a\u201c\u53cc\u53cc\u521a\u521a\u4ea4\u624b\uff0c\u9634\u5144\u4e43\u4e94\u4e49\u4e2d\u7684\u9996\u5ea7\uff0c\u4f55\u4e0d\u7559\u5f85\u6700\u540e\u6536\u62fe\u6218\u573a\uff01\u201d \u3000\u3000\u6bd2\u9b54\u9634\u5929\u7433\u9053\uff1a\u201c\u6211\u4eec\u4e94\u5144\u5f1f\u521d\u6295\u8ff7\u8e2a\u8c37\uff0c\u5bf8\u529f\u672a\u7acb\uff0c\u4eca\u5929\u80fd\u62fe\u4e2a\u5934\u529f\uff0c\u4e5f\u662f\u5e94\u8be5\u7684\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u9634\u5144\u5fc5\u987b\u5c0f\u5fc3\uff0c\u8fd9\u5c0f\u5b50\u5e74\u7eaa\u867d\u8f7b\uff0c\u8eab\u624b\u5374\u9ad8\u5f97\u51fa\u5947\uff0c\u5728\u8ff7\u8e2a\u8c37\u65f6\uff0c\u66fe\u662f\u8001 \u592b\u7684\u4e00\u4e2a\u5934\u75db\u4eba\u7269\uff0e\u201d \u3000\u3000\u6bd2\u9b54\u9634\u5929\u7433\u54a7\u5634\u68ee\u68ee\u4e00\u7b11\u9053\uff1a\u201c\u5934\u75db\u987b\u5f97\u5934\u836f\u533b\uff0c\u9634\u67d0\u4e0d\u624d\uff0c\u6700\u4f1a\u533b\u6cbb\u5934\u75c5\uff0e\u201d \u3000\u3000\u63a5\u7740\u4e24\u773c\u659c\u659c\u7684\u671b\u5411\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u5c0f\u5b50\uff0c\u4f11\u5f97\u901e\u80fd\uff0c\u4eca\u5929\u9047\u7740\u9634\u67d0\uff0c\u7b97\u662f\u4f60\u7684\u6b7b\u671f\u5230\u4e86\uff0c \u9634\u67d0\u7edd\u4e0d\u4ee5\u5927\u6b3a\u5c0f\uff0c\u8ba9\u4f60\u5148\u51fa\u624b\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u65e9\u5df2\u6c14\u5f97\u6012\u706b\u76f4\u51b2\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u8001\u5c0f\u5b50\uff0c\u4f60\u7a76\u7adf\u662f\u54ea\u4e2a\u738b\u516b\u86cb\uff0c\u5d06\u5cd2\u4e94\u9b54\u4e2d\u4f60\u7b97 \u54ea\u4e00\u9b54\uff1f\u672c\u5c11\u7237\u624b\u4e0b\u4e0d\u6740\u65e0\u540d\u4e4b\u9b3c\uff0c\u5feb\u62a5\u4e0a\u72d7\u540d\u6765\uff01\u201d \u3000\u3000\u6bd2\u9b54\u9634\u5929\u7433\u4e24\u773c\u95ea\u5149\u66b4\u95ea\uff0e\u8fd8\u6ca1\u6765\u5f97\u53ca\u559d\u9a82\uff0c\u970d\u5143\u4f3d\u5df2\u563f\u563f\u7b11\u7740\u63a5\u53e3\u9053\uff1b\u201c\u5c0f\u5b50\uff0c\u90a3\u5c31 \u8ba9\u4f60\u6b7b\u5f97\u660e\u767d\u4e9b\u3002\u8fd9\u4f4d\u82f1\u96c4\u597d\u6c49\uff0c\u662f\u5d06\u5cd2\u4e94\u4e49\u4e2d\u7684\u8001\u5927\u9634\u5929\u7433\uff0c\u4f60\u73b0\u5728\u77e5\u9053\u4e86\u5427\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u51b7\u7b11\u9053\uff1a\u201c\u4ec0\u4e48\u4e94\u4e49\u4e94\u9b54\u7684\uff1f\u5728\u672c\u5c11\u7237\u773c\u4e2d\uff0c\u8fd8\u4e0d\u5982\u4e94\u6761\u72d7\uff01\u201d \u3000\u3000\u8fd9\u4e00\u6765\uff0c\u8fde\u5176\u4ed6\u56db\u9b54\u4e5f\u60f9\u607c\u4e86\uff0c\u9f50\u9f50\u8dc3\u51fa\u5f53\u573a\uff0c\u5404\u81ea\u4eae\u51fa\u5175\u5203\uff0c\u5411\u9ea6\u5c0f\u660e\u6012\u76ee\u800c\u89c6\uff0c\u5927 \u6709\u4e00\u6d8c\u800c\u4e0a\u4e4b\u52bf\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u4e0d\u52a8\u58f0\u8272\u9053\uff1a\u201c\u4f60\u4eec\u4e94\u6761\u72d7\u662f\u5426\u6709\u610f\u8054\u624b\u51fa\u51fb\uff1f\u6ca1\u5173\u7cfb\uff0c\u5373\u4f7f\u518d\u5341\u6761\uff0e\u4e5f\u6ca1\u653e\u5728 \u672c\u5c11\u7237\u7684\u82f1\u96c4\u773c\u91cc\uff01\u201d \u3000\u3000\u4e94\u9b54\u66f4\u662f\u6012\u706b\u72c2\u70bd\uff0c\u4e0d\u8fc7\u7ecf\u9ea6\u5c0f\u660e\u8fd9\u4e00\u6fc0\uff0c\u4ed6\u4eec\u53cd\u800c\u4e0d\u4fbf\u9f50\u4e0a\uff0c\u4ee5\u514d\u843d\u4eba\u53e3\u5b9e\u3002 \u3000\u3000\u679c\u7136\uff0c\u8001\u5927\u6bd2\u9b54\u9634\u5929\u7433\u7acb\u5373\u53eb\u9053\uff1a\u201c\u4f60\u4eec\u56db\u4f4d\u8001\u5f1f\u9000\u4e0b\uff0c\u4e0d\u51fa\u5341\u62db\uff0c\u9634\u67d0\u5c31\u628a\u8fd9\u5c0f\u5b50\u9001 \u4e0a\u897f\u5929\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u8001\u5c0f\u53ef\u79f0\u662f\u4e2a\u5b5d\u5b50\uff0c\u5c45\u7136\u6709\u5b5d\u5fc3\u628a\u4f60\u8001\u5b50\u9001\u4e0a\u897f\u5929\u3002\u201d \u3000\u3000\u9634\u5929\u7433\u6c14\u5f97\u53d1\u660f\uff0c\u4f46\u56e0\u6709\u8a00\u5728\u5148\uff0c\u8981\u8ba9\u9ea6\u5c0f\u660e\u5148\u51fa\u624b\uff0c\u56e0\u4e4b\uff0c\u867d\u6068\u4e0d\u5f97\u4e00\u97ad\u5c06\u5bf9\u65b9\u62e6\u8170 \u626b\u65ad\uff0c\u5374\u8fd8\u662f\u5f3a\u5fcd\u7740\u4e0d\u66fe\u51fa\u62db\u3002 \u3000\u3000\u5ffd\u542c\u949f\u4e00\u8c6a\u559d\u53eb\u9053\uff1a\u201c\u9ea6\u5c0f\u5144\u5f1f\u9000\u4e0b\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u4e0d\u89c9\u4e00\u6123\uff0c\u56de\u5934\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u53c8\u8981\u6211\u9000\u4e0b\uff1f\u201d \u3000\u3000\u949f\u4e00\u8c6a\u5e76\u672a\u56de\u7b54\u3002 \u3000\u3000\u4f46\u4f59\u4ea6\u4e50\u5374\u6df1\u6df1\u5411\u9ea6\u5c0f\u660e\u4f7f\u4e86\u4e2a\u773c\u8272\u3002 \u3000\u3000\u539f\u6765\u949f\u4e00\u8c6a\u548c\u4f59\u4ea6\u4e50\u65e9\u95fb\u9634\u5929\u7433\u6700\u64c5\u65bd\u6bd2\uff0c\u800c\u9ea6\u5c0f\u660e\u53c8\u662f\u4ed6\u4eec\u4e4b\u4e2d\u7684\u9996\u53f7\u6218\u5c06\uff0c\u4e07\u4e00\u56e0 \u4e2d\u6bd2\u800c\u906d\u4e0d\u6d4b\uff0c\u4eca\u5929\u5c82\u4e0d\u540e\u679c\u4e0d\u582a\u8bbe\u60f3\uff0e \u3000\u3000\u56e0\u4e4b\uff0c\u4e24\u4eba\u51b3\u5b9a\u627e\u4eba\u628a\u9ea6\u5c0f\u660e\u6362\u4e0b\uff0c\u7b49\u770b\u6e05\u5bf9\u65b9\u7684\u7528\u6bd2\u624b\u6cd5\u540e\uff0c\u518d\u5bfb\u627e\u7834\u89e3\u4e4b\u9053\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u609f\u51fa\u949f\u4f59\u4e8c\u4eba\u7684\u7528\u5fc3\uff0c\u4f9d\u8a00\u9000\u56de\u9635\u53bb\u3002 \u3000\u3000\u6bd2\u9b54\u9634\u5929\u7433\u559d\u9053\uff1a\u201c\u5c0f\u5b50\uff0c\u4f60\u8000\u6b66\u6768\u5a01\u4e86\u534a\u5929\uff0c\u600e\u4e48\u53c8\u56de\u53bb\u4e86\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u770b\u4f60\u957f\u5f97\u4e09\u5206\u4e0d\u50cf\u4eba\uff0c\u4e03\u5206\u5012\u50cf\u9b3c\uff0c\u5c11\u7237\u61d2\u5f97\u8ddf\u4f60\u52a8\u624b\u3002\u201d \u3000\u3000\u949f\u4e00\u8c6a\u7acb\u5373\u5411\u5df1\u65b9\u9635\u4e2d\u95ee\u9053\uff1a\u201c\u8fd9\u4e00\u4ed7\u4e0d\u77e5\u54ea\u4f4d\u6709\u610f\u51fa\u6218\uff1f\u201d \u3000\u3000\u53ea\u542c\u4e00\u58f0\u5982\u96f7\u5927\u543c\u9053\uff1a\u201c\u4ffa\u738b\u5927\u5eb7\u53bb\u5bb0\u4e86\u8fd9\u72d7\u5a18\u517b\u7684\uff01\u201d \u3000\u3000\u738b\u5927\u5eb7\u81ea\u8fdb\u5165\u7eff\u6797\u603b\u5be8\uff0c\u867d\u7136\u4e00\u5411\u55b3\u55b3\u547c\u547c\uff0c\u4f46\u5374\u751a\u5c11\u4e0e\u4eba\u52a8\u624b\u8fc7\uff0e \u3000\u3000\u4ed6\u8eab\u6750\u9b41\u609f\u58ee\u7855\u800c\u53c8\u4eba\u9ad8\u9a6c\u5927\uff0c\u5929\u751f\u795e\u529b\uff0c\u624b\u91cc\u90a3\u67c4\u91d1\u80cc\u5f00\u5c71\u5200\uff0c\u8db3\u6709\u4e03\u516b\u5341\u65a4\uff0c\u4ec5\u51ed \u4ed6\u8fd9\u6a21\u6837\uff0c\u5c31\u8db3\u5413\u6b7b\u4eba\u4e0d\u507f\u547d\u7684\u3002 \u3000\u3000\u4ed6\u8fd9\u4e00\u51fa\u573a\uff0e\u4f7f\u5f97\u4e24\u8fb9\u6240\u6709\u89c2\u6218\u7684\uff0c\u83ab\u4e0d\u7ea6\u800c\u540c\u96c6\u4e2d\u5728\u4ed6\u8eab\u4e0a\uff0e \u3000\u3000\u9996\u5f53\u5176\u51b2\u7684\u6bd2\u9b54\u9634\u5929\u7433\uff0c\u96be\u514d\u4e5f\u6709\u4e9b\u80c6\u602f\uff0c\u6123\u6123\u7684\u95ee\u9053\uff1a\u201c\u5c0a\u9a7e\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u738b\u5927\u5eb8\u4e24\u773c\u4e00\u77aa\u9053\uff1a\u201c\u4ffa\u5165\u4f60\u5976\u5976\uff0c\u7adf\u8fde\u8001\u5b50\u90fd\u4e0d\u8ba4\u8bc6\uff0c\u53ef\u89c1\u4f60\u8fd9\u72d7\u5a18\u517b\u7684\u5fc5\u5b9a\u5fe4\u9006\u4e0d \u5b5d\uff01\u201d \u3000\u3000\u6bd2\u9b54\u9634\u5929\u7433\u88ab\u9a82\u5f97\u6709\u5982\u72d7\u8840\u55b7\u5934\uff0c\u559d\u9053\uff1a\u201c\u4f60\u6562\u9a82\u4eba\uff01\u201d \u3000\u3000\u738b\u5927\u5eb7\u543c\u9053\uff1a\u201c\u4ffa\u8fde\u63cd\u4eba\u90fd\u4e0d\u62ff\u7740\u5f53\u56de\u4e8b\uff0c\u9a82\u4f60\u8fd9\u72d7\u5a18\u517b\u7684\u53c8\u7b97\u4ec0\u4e48\uff01\u201d \u3000\u3000\u6bd2\u9b54\u9634\u5929\u7433\u4e0d\u518d\u7b54\u8bdd\uff0c\u4e00\u54ac\u7259\uff0c\u91d1\u86c7\u97ad\u6492\u51fa\u6f2b\u5929\u97ad\u5f71\uff0c\u76f4\u5411\u738b\u5927\u5eb7\u5f53\u5934\u7f69\u4e0b\u3002 \u5367\u9f99\u751f\u300a\u5999\u7edd\u5929\u9999\u300b \u7b2c\u4e8c\u5341\u4e8c\u56de\u3000\u5175\u6208\u76f8\u89c1 \u3000\u3000\u738b\u5927\u5eb7\u66b4\u543c\u4e00\u58f0\uff0c\u8de8\u6b65\u6b3a\u8eab\uff0c\u4e0d\u4f46\u4e0d\u8eb2\uff0c\u53cd\u5411\u97ad\u5f71\u4e2d\u8fce\u53bb\uff0c\u624b\u4e2d\u91d1\u80cc\u5f00\u5c71\u5200\uff0c\u864e\u864e\u751f\u98ce\uff0c \u6a2a\u626b\u76f4\u5288\uff0c\u731b\u51b2\u75be\u8fdb\u3002 \u3000\u3000\u8fd9\u79cd\u95e8\u623f\u5927\u5f00\uff0c\u53ea\u653b\u4e0d\u9632\uff0c\u53ea\u8fdb\u4e0d\u9000\u7684\u6253\u6cd5\uff0c\u679c\u7136\uff0c\u7adf\u7136\u628a\u6bd2\u9b54\u9634\u5929\u7433\u903c\u5f97\u8fde\u8fde\u540e\u9000\uff0e \u3000\u3000\u539f\u6765\u6bd2\u9b54\u9634\u5929\u7433\u53ea\u662f\u4ee5\u64c5\u957f\u65bd\u6bd2\u624b\u6cd5\u95fb\u540d\uff0c\u82e5\u8bba\u6b66\u529f\uff0c\u4e0e\u5176\u4ed6\u56db\u9b54\u76f8\u8f83\uff0c\u5e76\u4e0d\u51fa\u8272\u3002 \u3000\u3000\u738b\u5927\u5eb7\u4f9d\u7136\u662f\u731b\u6253\u731b\u51b2\uff0c\u5b8c\u5168\u662f\u7a0b\u54ac\u91d1\u201c\u4e09\u65a7\u5934\u201d\u7684\u67b6\u5f0f\u3002 \u3000\u3000\u9634\u5929\u7433\u53ea\u89c9\u4e0d\u80fd\u518d\u9000\uff0c\u4fbf\u5728\u4e24\u4e08\u65b9\u5706\u4e4b\u5185\u8f6c\u8d77\u5708\u5b50\u6765\uff0e \u3000\u3000\u4ed6\u97ad\u52bf\u7ef5\u7ef5\uff0c\u7ae0\u6cd5\u4e0d\u4e71\uff0c\u6709\u597d\u51e0\u6b21\u5c45\u7136\u626b\u4e2d\u4e86\u738b\u5927\u5eb7\u80a9\u8180\u3002 \u3000\u3000\u5982\u679c\u6362\u4e86\u2014\u822c\u5bf9\u624b\uff0c\u4e2d\u97ad\u4e4b\u540e\uff0c\u5373\u4f7f\u4e0d\u88ab\u5f53\u573a\u626b\u5012\uff0c\u4e5f\u5fc5\u60e8\u547c\u8fde\u8fde\uff0c\u5c82\u77e5\u738b\u5927\u5eb7\u90a3\u91d1\u80cc \u5f00\u5c71\u5200\u8fd8\u662f\u7167\u5288\u4e0d\u8bef\uff0c\u4e00\u8fb9\u9a82\u9053\uff1a \u3000\u3000\u201c\u4ffa\u5165\u4f60\u5976\u5976\u7684\uff0c\u4ffa\u8eab\u4e0a\u6ca1\u8671\u5b50\uff0e\u4e5f\u6ca1\u81ed\u866b\uff0e\u8fd8\u8981\u7528\u4f60\u8fd9\u72d7\u5a18\u517b\u7684\u6414\u75d2\uff01\u201d \u3000\u3000\u8c01\u77e5\u5c31\u5728\u4ed6\u9a82\u58f0\u752b\u6bd5\uff0c\u7a81\u89c1\u9634\u5929\u7433\u5de6\u624b\u2014\u626c\uff0c\u4e00\u62b9\u9ed1\u96fe\uff0c\u76f4\u5411\u738b\u5927\u5eb7\u98d8\u53bb\u3002 \u3000\u3000\u738b\u5927\u5eb7\u7acb\u5373\u8eab\u5b50\u5f00\u59cb\u6447\u6447\u6643\u6643\uff0c\u50cf\u5403\u9189\u4e86\u9152\u822c\u6a21\u6837\u3002 \u3000\u3000\u5d02\u5c71\u4e09\u96c4\u4e2d\u7684\u8001\u5927\u7b11\u9762\u4f5b\u9c8d\u8d85\u548c\u8001\u4e8c\u9b3c\u8bf8\u845b\u6d2a\u6cfd\u4e00\u89c1\u4e0d\u5999\uff0e\u6025\u6025\u8e7f\u4e86\u51fa\u6765\uff0c\u4e00\u4eba\u62b5\u4f4f\u9634 \u5929\u7433\u7684\u8fdb\u88ad\uff0c\u4e00\u4eba\u628a\u738b\u5927\u5eb7\u6276\u4e86\u56de\u53bb\uff0e \u3000\u3000\u8fd9\u6b21\u738b\u5927\u5eb7\u5de6\u80a9\u7ec8\u4e8e\u6328\u4e86\u91cd\u91cd\u4e00\u97ad\uff0c\u4e0d\u4f46\u8863\u670d\u88ab\u626b\u7834\uff0c\u80a9\u5934\u4e5f\u9c9c\u8840\u76f4\u6d41\u3002 \u3000\u3000\u62b5\u62c4\u9634\u5929\u7433\u7684\u6d2a\u6cfd\uff0c\u4ed6\u62c5\u5fc3\u5bf9\u65b9\u518d\u653e\u6bd2\u96fe\uff0c\u89c1\u738b\u5927\u5eb7\u5df2\u88ab\u8001\u5927\u6551\u56de\uff0c\u7acb\u5373\u4e5f\u62bd\u8eab\u540e\u9000\u3002 \u3000\u3000\u9634\u5927\u7433\u5f97\u610f\u626c\u626c\uff0c\u9634\u68ee\u68ee\u7b11\u4e86\u51e0\u58f0\u9053\uff1a\u201c\u4f60\u8fd9\u6df7\u5c0f\u5b50\u5c31\u662f\u518d\u5389\u5bb3\uff0c\u603b\u5389\u5bb3\u4e0d\u8fc7\u9634\u67d0\u7684 \u2018\u8840\u96fe\u6563\u2019\u5427\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u4e5f\u9762\u9732\u5f97\u610f\u4e4b\u8272\u9053\uff1a\u201c\u9634\u5144\u4f55\u4e0d\u628a\u2018\u8840\u96fe\u6563\u2019\u591a\u65bd\u653e\u4e9b\uff0e\u5982\u679c\u80fd\u628a\u4ed6\u4eec\u5168\u6570\u6bd2\u5012\uff0c \u90a3\u5c31\u7528\u4e0d\u7740\u518d\u8d39\u624b\u811a\u4e86\u3002\u201d \u3000\u3000\u9634\u5929\u7433\u9053\uff1a\u201c\u9634\u67d0\u8fd9\u2018\u8840\u96fe\u6563\u2019\uff0e\u6269\u6563\u8303\u56f4\u4e0d\u5927\uff0c\u53ea\u80fd\u5bf9\u4ed8\u5f53\u9762\u5bf9\u654c\u7684\u2014\u4e8c\u4eba\uff0c\u800c\u4e14\u8fd8 \u8981\u914d\u5408\u98ce\u5411\u624d\u80fd\u51fa\u624b\u3002\u201d \u3000\u3000\u4ed6\u8fd9\u4e00\u5f53\u573a\u8bf4\u660e\uff0c\u65e0\u7591\u8ba9\u949f\u4e00\u8c6a\u7b49\u4eba\u7a0d\u7a0d\u653e\u4e0b\u4e86\u5fc3\u3002 \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u9634\u5144\u8981\u4e0d\u8981\u518d\u6bd2\u5012\u4ed6\u4eec\u51e0\u4e2a\uff1f\u201d \u3000\u3000\u9634\u5929\u7433\u54a7\u5634\u7b11\u9053\uff1a\u201c\u9634\u67d0\u662f\u6765\u8005\u4e0d\u62d2\uff0e\u82e5\u4ed6\u4eec\u8fd8\u6709\u4eba\u60f3\u5c1d\u5c1d\u2018\u8840\u96fe\u6563\u2019\u7684\u6ecb\u5473\uff0c\u53ea\u7ba1\u4e0a \u6765\uff01\u201d \u3000\u3000\u53ea\u89c1\u4e00\u540d\u82f1\u96c4\u6f47\u6d12\u7684\u5e74\u8f7b\u4eba\uff0c\u65e0\u58f0\u65e0\u606f\u7684\u63a0\u5411\u573a\u4e2d\u9053\uff1a\u201c\u5728\u4e0b\u5012\u8981\u8bd5\u8bd5\u5c0a\u9a7e\u90a3\u2018\u8840\u96fe\u6563\u2019 \u7a76\u7adf\u5389\u5bb3\u5230\u4ec0\u4e48\u7a0b\u5ea6\uff01\u201d \u3000\u3000\u8fd9\u4eba\u8d6b\u7136\u662f\u6587\u5929\u751f\u3002 \u3000\u3000\u6587\u5929\u751f\u56e0\u8fd9\u4e07\u6708\u5821\u662f\u5e08\u7236\u795e\u97ad\u98de\u68ad\u4e07\u6653\u5149\u4e00\u624b\u6240\u521b\uff0c\u81ea\u5df1\u4e5f\u7b97\u534a\u4e2a\u4e3b\u4eba\uff0c\u4e0d\u80fd\u8896\u624b\u65c1\u89c2\uff0e \u660e\u77e5\u4e0d\u4e00\u5b9a\u80fd\u591f\u53d6\u80dc\uff0c\u4e5f\u5fc5\u987b\u51fa\u9762\u5e94\u6218\u3002 \u3000\u3000\u9634\u5929\u7433\u89c1\u6587\u5929\u751f\u5e74\u7eaa\u751a\u8f7b\uff0c\u8fd8\u5e26\u4e9b\u6587\u8d28\u5f6c\u5f6c\u7684\u6a21\u6837\uff0c\u5f53\u7136\u4e0d\u628a\u4ed6\u653e\u5728\u773c\u91cc\uff0c\u51b7\u7b11\u4e86\u51e0\u58f0 \u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u5b50\u4e5f\u60f3\u6765\u9001\u6b7b\uff1f\u201d \u3000\u3000\u6587\u5929\u751f\u4e00\u8138\u575a\u6bc5\u795e\u8272\uff0c\u53f1\u9053\uff1a\u201c\u4f60\u522b\u5356\u72c2\uff0e\u6bd2\u96fe\u4f24\u4eba\uff0c\u7b97\u4e0d\u4e86\u771f\u6b63\u672c\u4e8b\uff0e\u201d \u3000\u3000\u9634\u5929\u7433\u9053\uff1a\u201c\u80fd\u4f24\u4eba\u5c31\u662f\u672c\u4e8b\uff0c\u4f60\u8fd9\u5c0f\u5b50\u662f\u5426\u8fd8\u6709\u4e9b\u4e0d\u670d\uff1f\u201d \u3000\u3000\u6587\u5929\u751f\u51b7\u7b11\u9053\uff1a\u201c\u5f53\u7136\u4e0d\u670d\uff01\u201d \u3000\u3000\u201c\u90a3\u5f88\u597d\uff01\u9634\u67d0\u51b3\u5b9a\u8ba9\u4f60\u5148\u51fa\u62db\u3002\u201d \u3000\u3000\u6587\u5929\u751f\u56e0\u662f\u795e\u97ad\u98de\u68ad\u4e07\u6653\u5149\u7684\u5f1f\u5b50\uff0c\u5e08\u7236\u7528\u97ad\uff0e\u81ea\u5df1\u5f53\u7136\u4e5f\u662f\u7528\u97ad\u3002 \u3000\u3000\u4ed6\u8fd9\u97ad\u53eb\u9f99\u5934\u8f6f\u97ad\uff0c\u548c\u9634\u5929\u7433\u7684\u91d1\u86c7\u97ad\u5927\u540c\u5c0f\u5f02\uff0e\u97ad\u5bf9\u97ad\uff0c\u6b63\u662f\u5404\u51ed\u529f\u529b\uff0c\u4efb\u8c01\u4e5f\u65e0\u6cd5 \u6295\u673a\u53d6\u5de7\u3002 \u3000\u3000\u5f53\u4e0b\uff0c\u6587\u5929\u751f\u5c55\u5f00\u653b\u52bf\uff0e\u6325\u97ad\u76f4\u653b\u800c\u4e0a\u3002 \u3000\u3000\u9634\u5929\u7433\u7acb\u5373\u4e3e\u97ad\u76f8\u8fce\u3002 \u3000\u3000\u6587\u5929\u751f\u5c11\u5e74\u6c14\u76db\uff0c\u4e3a\u4e86\u5728\u4f17\u4eba\u9762\u524d\u663e\u793a\u8eab\u624b\uff0e\u4e00\u51fa\u624b\u5c31\u91c7\u53d6\u731b\u6253\u731b\u653b\uff0c\u97ad\u58f0\u54bb\u54bb\uff0c\u97ad\u5f71 \u6eda\u6eda\uff0c\u5f88\u5feb\u4fbf\u628a\u9634\u5929\u7433\u7f69\u8fdb\u4e00\u7247\u5149\u5f71\u4e4b\u4e2d\u3002 \u3000\u3000\u82e5\u8bba\u6b66\u529f\uff0c\u9634\u5929\u7433\u672c\u4e0d\u900a\u4e8e\u6587\u5929\u751f\uff0c\u4f46\u56e0\u4ed6\u7684\u76ee\u7684\u5728\u770b\u51c6\u98ce\u5411\u65bd\u653e\u6bd2\u96fe\uff0c\u6ce8\u610f\u529b\u5206\u6563\uff0c \u96be\u514d\u663e\u5f97\u788d\u624b\u788d\u811a\uff0c\u4ee5\u81f4\u4ea4\u624b\u4e0d\u4e45\uff0c\u4fbf\u843d\u5165\u4e0b\u98ce\u3002 \u3000\u3000\u6587\u5929\u751f\u5219\u662f\u5f97\u7406\u4e0d\u8ba9\u4eba\uff0c\u97ad\u52bf\u8d8a\u6765\u8d8a\u5feb\uff0c\u5feb\u5f97\u4ee4\u4eba\u76ee\u4e0d\u6687\u7ed9\u3002 \u3000\u3000\u7a81\u89c1\u9634\u5929\u7433\u5de6\u8155\u4e00\u626c\uff0c\u4e00\u9053\u8ff7\u6726\u9ed1\u96fe\uff0c\u76f4\u5411\u6587\u5929\u751f\u9762\u524d\u6563\u53bb\u3002 \u3000\u3000\u5c82\u77e5\u6587\u5929\u751f\u7adf\u7136\u6beb\u65e0\u534a\u70b9\u5f02\u6837\uff0c\u4e0d\u4f46\u4e0d\u66fe\u660f\u5012\uff0c\u53cd\u800c\u653b\u52bf\u6bd4\u5148\u524d\u66f4\u5feb\u3002 \u3000\u3000\u8fd9\u4f7f\u9634\u5929\u7433\u5927\u611f\u60ca\u7591\uff0c\u7d27\u63a5\u7740\u53c8\u6492\u51fa\u4e00\u56e2\u9ed1\u96fe\u3002 \u3000\u3000\u504f\u504f\u6587\u5929\u751f\u8fd8\u662f\u534a\u70b9\u672a\u53d7\u5f71\u54cd\u3002 \u3000\u3000\u9634\u5929\u7433\u7ec8\u4e8e\u660e\u767d\u201c\u8840\u96fe\u6563\u201d\u5df2\u5bf9\u6587\u5929\u751f\u5931\u6548\uff0c\u8fd9\u624d\u5168\u529b\u4ee5\u91d1\u86c7\u97ad\u8fce\u6218\u3002 \u3000\u3000\u5982\u6b64\u4e00\u6765\uff0c\u6587\u5929\u751f\u5df2\u65e0\u6cd5\u518d\u62a2\u5230\u4e0a\u98ce\uff0c\u5f62\u6210\u4e8c\u4eba\u5e73\u5206\u79cb\u8272\u4e4b\u5c40\u3002 \u3000\u3000\u5c31\u8fd9\u6837\u4e8c\u4eba\u8db3\u8db3\u5bf9\u62fc\u4e86\u4e03\u516b\u5341\u62db\uff0c\u8fd8\u662f\u4e2a\u4e0d\u80dc\u4e0d\u8d25\u96be\u5206\u4e0a\u4e0b\u4e4b\u5c40\u3002 \u3000\u3000\u6fc0\u6218\u4e2d\u5ffd\u542c\u4e00\u4eba\u543c\u9053\uff1a\u201c\u8001\u5927\u9000\u4e0b\uff0c\u7559\u7ed9\u5144\u5f1f\u6765\u6536\u62fe\u8fd9\u4e2a\u5c0f\u5b50\uff01\u201d \u3000\u3000\u9634\u5929\u7433\u56e0\u81ea\u77e5\u65e0\u6cd5\u5c06\u5bf9\u65b9\u5236\u670d\uff0c\u4f9d\u8a00\u9000\u4e0b\u9635\u53bb\uff0e \u3000\u3000\u53d1\u8bdd\u8fd9\u4eba\u662f\u5d06\u5cd2\u4e94\u9b54\u7684\u8001\u4e8c\uff0c\u7ef0\u53f7\u9b3c\u9b54\uff0c\u59d3\u5deb\u540d\u9053\u5168\u3002 \u3000\u3000\u6b64\u4eba\u5e74\u7eaa\u5728\u4e94\u65ec\u4e0a\u4e0b\uff0c\u5de5\u4e8e\u5fc3\u8ba1\uff0c\u516b\u5b57\u7709\uff0c\u540a\u89d2\u773c\uff0c\u4e00\u5f20\u9634\u68ee\u68ee\u7684\u9a6c\u8138\uff0c\u4e0b\u9894\u7559\u7740\u4e00\u64ae \u5c71\u7f8a\u80e1\uff0c\u624b\u6267\u4e00\u628a\u94c1\u62c2\u5c18\uff0c\u4e4d\u770b\u6709\u70b9\u50cf\u9634\u624b\u4e00\u9b54\uff0e \u3000\u3000\u5deb\u9053\u5168\u7684\u6b66\u529f\uff0c\u8981\u9ad8\u51fa\u9634\u5929\u7433\u751a\u591a\uff0c\u6240\u5dee\u7684\u53ea\u662f\u4e0d\u64c5\u65bd\u6bd2\u800c\u5df2\u3002 \u3000\u3000\u6587\u5929\u751f\u89c1\u5deb\u9053\u5168\u7f13\u7f13\u8d70\u4e86\u51fa\u6765\uff0c\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u8001\u5c0f\u5b50\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u5deb\u9053\u5168\u6478\u7740\u5c71\u7f8a\u80e1\u563f\u563f\u7b11\u9053\uff1a\u201c\u522b\u77a7\u4e0d\u8d77\u6211\u8fd9\u8001\u5c0f\u5b50\uff0c\u505a\u4f60\u7684\u8001\u5b50\u7ef0\u7ef0\u6709\u4f59\uff01\u201d \u3000\u3000\u6587\u5929\u751f\u6c14\u5f80\u4e0a\u51b2\uff0c\u3000\u4e00\u54ac\u7259\uff0c\u51cc\u8eab\u75be\u6251\uff0c\u9f99\u5934\u8f6f\u97ad\u4e00\u62db\u201c\u5929\u5916\u6765\u9e3f\u201d\uff0c\u7167\u51c6\u5deb\u9053\u5168\u9762\u95e8 \u626b\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u5c82\u77e5\u5deb\u9053\u5168\u4e24\u811a\u624e\u6869\u5982\u5c71\uff0c\u4e0d\u4f46\u4e0d\u8eb2\uff0c\u8fde\u94c1\u62c2\u5c18\u4e5f\u4e0d\u8fce\u51fb\uff0c\u5374\u626c\u8d77\u5de6\u638c\uff0e\u4e00\u638c\u5288\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u6587\u5929\u751f\u8eab\u5b50\u5c1a\u672a\u6b3a\u8fd1\u5deb\u9053\u5168\uff0c\u524d\u80f8\u5df2\u88ab\u638c\u98ce\u5288\u4e2d\u3002 \u3000\u3000\u8fd9\u4e00\u638c\u662f\u5deb\u9053\u5168\u5168\u529b\u65bd\u4e3a\uff0c\u53ea\u542c\u201c\u7830\u201d\u7684\u4e00\u58f0\uff0c\u76f4\u628a\u6587\u5929\u751f\u5411\u524d\u76f4\u51b2\u7684\u8eab\u5b50\uff0c\u649e\u5f97\u5012\u98de \u51fa\u53bb\u3002 \u3000\u3000\u5f53\u6587\u5929\u751f\u4ef0\u6454\u51fa\u4e24\u4e08\u4e4b\u5916\u5c1a\u672a\u7ad9\u4f4f\u811a\uff0c\u6b63\u55b7\u51fa\u4e00\u53e3\u9c9c\u8840\u4e4b\u9645\uff0e\u5deb\u9053\u5168\u5df2\u5982\u5f71\u968f\u5f62\u822c\u65e0\u58f0 \u65e0\u606f\u7684\u8ffd\u88ad\u800c\u6765\uff0c\u94c1\u62c2\u5c18\u201c\u5b54\u96c0\u5f00\u5c4f\u201d\u76d6\u5934\u800c\u4e0b\u3002 \u3000\u3000\u8c01\u90fd\u4e0d\u96be\u60f3\u8c61\uff0c\u90a3\u94c1\u62c2\u5c18\u5982\u679c\u626b\u4e2d\u6587\u5929\u751f\u4e0a\u76d8\uff0c\u6587\u5929\u751f\u52bf\u5fc5\u4eba\u5934\u5f00\u82b1\u3002 \u3000\u3000\u5c31\u5728\u949f\u4e00\u8c6a\u7b49\u4eba\u5931\u58f0\u60ca\u547c\u7684\u5343\u94a7\u4e00\u53d1\u4e4b\u9645\uff0c\u7a81\u89c1\u4e00\u9053\u767d\u5149\uff0c\u6709\u5982\u5929\u5916\u98de\u8679\u4e00\u95ea\u800c\u81f3\uff0c\u751f \u751f\u628a\u90a3\u5373\u5c06\u626b\u5230\u7684\u94c1\u62c2\u5c18\u6321\u4e86\u56de\u53bb\u3002 \u3000\u3000\u5f53\u90a3\u767d\u5149\u4e00\u655b\uff0c\u4f17\u4eba\u624d\u770b\u6e05\u539f\u6765\u662f\u9ea6\u5c0f\u660e\u53ca\u65f6\u817e\u8eab\u51fa\u624b\uff0c\u624d\u633d\u56de\u6587\u5929\u751f\u4e00\u547d\u3002 \u3000\u3000\u5deb\u9053\u5168\u76f4\u88ab\u4e00\u5251\u5e26\u52a8\u4e4b\u529b\u6454\u9000\u4e86\u597d\u51e0\u6b65\uff0c\u624d\u7ad9\u7a33\u811a\u8ddf\uff0e \u3000\u3000\u4ed6\u5148\u524d\u5df2\u89c1\u8fc7\u9ea6\u5c0f\u660e\u51fb\u8d25\u5df4\u5929\u4e49\u7684\u8eab\u624b\uff0c\u5374\u8ba4\u4e3a\u662f\u5df4\u5929\u4e49\u7684\u5931\u624b\u6240\u81f4\uff0c\u6b64\u523b\u603b\u7b97\u4eb2\u81ea\u9886 \u6559\u4e86\u5bf9\u65b9\u7684\u5389\u5bb3\u3002 \u3000\u3000\u53ea\u542c\u970d\u5143\u4f3d\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u6b66\u529f\u8def\u6570\u602a\u5f02\uff0c\u5341\u5206\u96be\u7f20\uff0c\u5deb\u8001\u5f1f\u4e0d\u53ef\u5927\u610f\uff01\u201d \u3000\u3000\u5deb\u9053\u5168\u9ed8\u4e86\u9ed8\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u4ece\u524d\u4e5f\u662f\u8ff7\u8e2a\u8c37\u7684\u5417\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u5e72\u54b3\u4e86\u4e24\u58f0\u9053\uff1a\u201c\u7528\u4e0d\u7740\u95ee\uff0e\u4ed6\u4eec\u5728\u573a\u7684\u5168\u662f\u8ff7\u8e2a\u8c37\u7684\u53db\u5f92\uff01\u201d \u3000\u3000\u5deb\u9053\u5168\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u5728\u8ff7\u8e2a\u8c37\u4e5f\u662f\u8fd9\u4e48\u7316\u72c2\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u5e72\u54b3\u4e86\u4e24\u58f0\u9053\uff1a\u201c\u90a3\u8fd8\u7528\u4f60\u8bf4\uff0c\u4ed6\u6839\u672c\u6ca1\u628a\u8001\u592b\u653e\u5728\u773c\u91cc\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u55d4\u76ee\u559d\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4e2a\u738b\u516b\u86cb\u522b\u5c3d\u8bb2\u5e9f\u8bdd\uff0c\u5982\u679c\u6015\u4e86\uff0c\u5c31\u8d81\u65e9\u6eda\u56de\u8ff7\u8e2a\u8c37\u53bb\uff01\u201d \u3000\u3000\u5deb\u9053\u5168\u88ab\u9a82\u5f97\u4e03\u7a8d\u751f\u70df\uff0c\u62a1\u52a8\u94c1\u62c2\u5c18\u4fbf\u5411\u9ea6\u5c0f\u660e\u6253\u53bb\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u4e0d\u614c\u4e0d\u5fd9\uff0c\u5251\u52bf\u82e5\u70b9\u82e5\u5288\uff0c\u76f4\u8fce\u5411\u94c1\u62c2\u5c18\u3002 \u3000\u3000\u4f46\u95fb\u4e00\u9635\u6c99\u6c99\u9707\u54cd\uff0c\u63a5\u7740\u5730\u4e0a\u7adf\u591a\u4e86\u6570\u6839\u94c1\u7ebf\uff0c\u800c\u4e14\u94c1\u7ebf\u8d8a\u843d\u8d8a\u591a\u3002 \u3000\u3000\u5728\u8fd9\u540c\u65f6\uff0c\u5deb\u9053\u5168\u4e5f\u89c9\u51fa\u94c1\u62c2\u5c18\u7adf\u8d8a\u6765\u8d8a\u77ed\uff0c\u8fd9\u624d\u77e5\u9053\u4e0d\u5999\u3002\u5206\u660e\u94c1\u62c2\u5c18\u5df2\u88ab\u8282\u8282\u524a\u65ad\uff0c \u6b64\u523b\u624b\u4e2d\u62ff\u7684\uff0c\u4f3c\u4e4e\u53ea\u5269\u4e0b\u534a\u622a\u94c1\u626b\u5e1a\u3002 \u3000\u3000\u5deb\u9053\u5168\u5927\u9a87\u4e4b\u4e0b\uff0c\u6025\u6025\u64a4\u8eab\u540e\u9000\u3002 \u3000\u3000\u5c82\u77e5\u8fd9\u65f6\u4ed6\u5df3\u88ab\u5708\u5728\u4e00\u56e2\u5149\u5f69\u4e4b\u4e2d\uff0c\u6839\u672c\u65e0\u6cd5\u8131\u8eab\u3002 \u3000\u3000\u84e6\u89c1\u4e24\u6761\u4eba\u5f71\u95ea\u7535\u822c\u6025\u63a0\u800c\u81f3\uff0c\u4e00\u5de6\u4e00\u53f3\uff0c\u5411\u9ea6\u5c0f\u660e\u5c55\u5f00\u4e86\u5939\u51fb\u5f0f\u7684\u8054\u624b\u5408\u653b\u3002 \u3000\u3000\u5deb\u9053\u5168\u867d\u88ab\u6551\u4e0b\uff0e\u4f46\u5374\u88ab\u9ea6\u5c0f\u660e\u4e00\u5251\u65a9\u65ad\u4e86\u5de6\u81c2\u3002 \u3000\u3000\u539f\u6765\u8fd9\u4e8c\u4eba\u4e00\u4e2a\u662f\u5d06\u5cd2\u4e94\u9b54\u4e2d\u7684\u8001\u4e09\u5200\u9b54\u8c37\u2014\u5cf0\uff0c\u2014\u4e2a\u662f\u8001\u56db\u5251\u9b54\u53f8\u592a\u5e73\u3002 \u3000\u3000\u8fd9\u4e8c\u4eba\u7684\u5200\u6cd5\u548c\u5251\u6cd5\uff0e\u5728\u9ed1\u9053\u4e0a\u65e9\u5c31\u95fb\u540d\u9050\u8fe9\uff0c\u4eba\u4eba\u4e3a\u4e4b\u8c08\u864e\u8272\u53d8\uff0c\u5200\u5251\u8054\u624b\uff0c\u66f4\u662f\u9b3c \u795e\u7686\u60ca\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u2014\u4eba\u72ec\u6218\u5200\u5251\u53cc\u9b54\uff0c\u4e00\u4e0a\u624b\u7adf\u88ab\u8fde\u8fde\u903c\u9000\u51e0\u6b65\u3002 \u3000\u3000\u76f4\u5230\u5341\u4f59\u62db\u8fc7\u540e\uff0c\u624d\u6e10\u6e10\u7a33\u4e86\u9635\u811a\u3002 \u3000\u3000\u4f46\u4ed6\u5fc3\u91cc\u6709\u6570\uff0c\u8981\u60f3\u51fb\u9000\u4e8c\u9b54\uff0c\u5374\u662f\u5927\u5927\u4e0d\u6613\u3002 \u3000\u3000\u53c8\u662f\u5341\u4f59\u62db\u8fc7\u540e\uff0c\u9ea6\u5c0f\u660e\u7ec8\u4e8e\u6e10\u6e10\u62a2\u56de\u4e86\u4e0a\u98ce\u3002 \u3000\u3000\u970d\u5143\u4f3d\u89c1\u5200\u5251\u53cc\u9b54\u5df2\u80fd\u7f20\u4f4f\u9ea6\u5c0f\u660e\uff0c\u3000\u7acb\u5373\u4e00\u58f0\u4ee4\u4e0b\uff0c\u559d\u4ee4\u6240\u6709\u7684\u4eba\u4e00\u8d77\u51b2\u6740\u8fc7\u6765\u3002 \u3000\u3000\u987f\u65f6\uff0c\u5927\u5385\u5916\u5f62\u6210\u4e00\u9635\u6df7\u4e71\u573a\u9762\uff0e\u5404\u79cd\u5175\u5203\u91d1\u94c1\u4ea4\u51fb\u4e4b\u58f0\uff0c\u4e0d\u7edd\u4e8e\u8033\uff0c\u4f46\u89c1\u98de\u6c99\u8d70\u77f3\uff0c \u5f81\u5c18\u6eda\u6eda\uff0c\u5927\u6709\u5929\u660f\u5730\u6697\u4e4b\u6168\u3002 \u3000\u3000\u8fd9\u671f\u95f4\uff0e\u970d\u5143\u4f3d\u5bf9\u4e0a\u4e86\u949f\u4e00\u8c6a\uff0c\u5b8b\u5929\u94ce\u5bf9\u4e0a\u4e86\u4f59\u4ea6\u4e50\u3002\u5df4\u5929\u4e49\u3001\u4e94\u9b54\u4e2d\u7684\u8001\u4e94\u8272\u9b54\u82b1\u79c0 \u4ee5\u53ca\u4e5d\u540d\u94dc\u4eba\uff0c\u5916\u52a0\u94dc\u4eba\u9886\u961f\u609f\u660e\uff0e\u5219\u4e0e\u82d7\u7d20\u82d3\u3001\u6587\u5929\u751f\u3001\u6d2a\u6cfd\u3001\u4ee5\u53ca\u6c5f\u5317\u4e09\u9f99\u5c55\u5f00\u53ae\u6740\u3002 \u3000\u3000\u738b\u5927\u5eb7\u56e0\u4ecd\u660f\u8ff7\u4e0d\u9192\uff0c\u5df2\u88ab\u9c8d\u8d85\u5e26\u5f80\u5185\u9662\u8eb2\u907f\uff0c\u65e0\u5f62\u4e2d\u67cf\u9f84\u9662\u65b9\u9762\u7684\u4eba\u5df2\u5b9e\u529b\u5927\u51cf\u3002 \u3000\u3000\u5176\u5b9e\uff0c\u5373\u4f7f\u6709\u9c8d\u8d85\u3001\u738b\u5927\u5eb7\u5728\u573a\uff0c\u56e0\u53cc\u65b9\u4eba\u624b\u76f8\u5dee\u60ac\u6b8a\uff0e\u67cf\u9f84\u9662\u65b9\u9762\u4e5f\u5fc5\u96be\u9003\u5384\u8fd0\uff0c\u970d \u5143\u4f3d\u4ec5\u51ed\u90a3\u4e5d\u540d\u94dc\u4eba\uff0c\u5c31\u8db3\u53ef\u5e94\u4ed8\u5168\u5c40\u3002 \u3000\u3000\u4e0d\u5927\u4e00\u4f1a\u5de5\u592b\uff0c\u6c5f\u5317\u4e09\u9f99\u5c31\u5df2\u5206\u522b\u88ab\u4e5d\u540d\u94dc\u4eba\u64d2\u4f4f\u3002 \u3000\u3000\u90a3\u4e5d\u540d\u94dc\u4eba\uff0c\u53ef\u80fd\u662f\u56e0\u4e3a\u51fa\u5bb6\u4eba\u5173\u7cfb\uff0e\u867d\u7136\u5f03\u660e\u6295\u6697\u5f52\u987a\u4e86\u970d\u5143\u4f3d\uff0c\u4ecd\u80fd\u9075\u5b88\u6212\u6740\u4e4b\u5f8b\uff0c \u53ea\u6349\u6d3b\u7684\uff0c\u5e76\u4e0d\u4f24\u4eba\u3002 \u3000\u3000\u5426\u5219\u6c5f\u5317\u4e09\u9f99\u54ea\u91cc\u8fd8\u6709\u6d3b\u547d\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u949f\u4e00\u8c6a\uff0c\u4f59\u4ea6\u4e50\u3001\u6587\u5929\u751f\u3001\u82d7\u7d20\u82d3\uff0c\u6d2a\u6cfd\u7b49\u4eba\u4e0d\u6d88\u8bf4\u4e5f\u5728\u8282\u8282\u540e\u9000\uff0c\u53ea\u5dee\u4e0d\u66fe\u88ab \u751f\u64d2\u6d3b\u6349\u3002 \u3000\u3000\u4f46\u4ed6\u4eec\u867d\u7136\u8d25\u4e86\u9635\uff0c\u5374\u4ecd\u80fd\u4e2a\u4e2a\u82e6\u6491\uff0c\u5e76\u65e0\u2014\u4eba\u8d81\u673a\u9003\u9038\uff0c\u9ea6\u5c0f\u660e\u672c\u5df2\u53ef\u64cd\u80dc\u7b97\uff0c\u4f46\u56e0 \u53c8\u6709\u4e8c\u540d\u94dc\u4eba\u52a9\u4e0a\u9635\u6765\uff0c\u5f88\u5feb\u4fbf\u5df2\u9669\u8c61\u73af\u751f\uff0c\u88ab\u5e26\u903c\u5f97\u4e0d\u5f97\u4e0d\u5f80\u540e\u9000\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u7a81\u542c\u2014\u58f0\u6c89\u559d\u9053\uff1a\u201c\u4f4f\u624b\uff01\u201d \u3000\u3000\u8fd9\u58f0\u97f3\u867d\u7136\u6ca1\u6709\u738b\u5927\u5eb7\u6765\u5f97\u5927\uff0c\u5374\u6709\u79cd\u9707\u6151\u4eba\u5fc3\u7684\u529b\u91cf\uff0c\u6df7\u6218\u4e2d\u7684\u53cc\u65b9\uff0c\u90fd\u4e0d\u7ea6\u800c\u540c\u505c \u4e0b\u624b\u6765\uff0c\u800c\u4e14\u5404\u81ea\u9000\u5230\u4e00\u8fb9\uff0e \u3000\u3000\u53ea\u89c1\u4e0d\u77e5\u4ec0\u4e48\u65f6\u5019\uff0c\u5927\u5385\u5de6\u4fa7\u5df2\u7ad9\u5b9a\u4e00\u4e2a\u4e71\u53d1\u62ab\u80a9\uff0e\u80f8\u5782\u957f\u9aef\u7684\u7070\u888d\u8001\u8005\u3002 \u3000\u3000\u82d7\u7d20\u82d3\u60ca\u559c\u7684\u547c\u53eb\u4e00\u58f0\uff0c\u6025\u6025\u5954\u5230\u90a3\u8001\u8005\u8ddf\u524d\u53eb\u9053\uff1a\u201c\u5e08\u7236\uff0e\u4f60\u8001\u4eba\u5bb6\u6765\u4e86\uff01\u201d \u3000\u3000\u539f\u6765\u8fd9\u4eba\u7adf\u662f\u957f\u767d\u795e\u53df\u5e9e\u58eb\u51b2\u3002 \u3000\u3000\u5728\u8fd9\u5239\u90a3\uff0c\u9ea6\u5c0f\u660e\u4e5f\u611f\u5230\u65e0\u5c3d\u60ca\u559c\uff0c\u4ed6\u548c\u5e9e\u58eb\u51b2\u76f8\u89c1\u591a\u6b21\uff0c\u4e5f\u591a\u6b21\u5f97\u8fc7\u5e9e\u58eb\u51b2\u7684\u6551\u63f4\uff0e \u6df1\u77e5\u4ed6\u6b64\u523b\u5230\u6765\uff0c\u5fc5\u5b9a\u4e5f\u662f\u5e2e\u7740\u5df1\u65b9\u7684\u3002 \u3000\u3000\u81f3\u4e8e\u970d\u5143\u4f3d\u7b49\u4eba\uff0c\u867d\u7136\u4e0a\u6b21\u5728\u5929\u53f0\u4e07\u82b1\u5bab\u66fe\u89c1\u8fc7\u5e9e\u58eb\u51b2\uff0e\u4e5f\u4e45\u95fb\u4ed6\u7684\u5927\u540d\uff0c\u4f46\u53cc\u65b9\u5374\u8c08 \u4e0d\u5230\u534a\u70b9\u4ea4\u60c5\u3002 \u3000\u3000\u5e9e\u58eb\u51b2\u4e4d\u89c1\u4e00\u4e2a\u7709\u6e05\u76ee\u79c0\uff0c\u5507\u7ea2\u9f7f\u767d\u7684\u5c11\u5e74\u5954\u8fc7\u6765\u558a\u81ea\u5df1\u5e08\u7236\uff0c\u5148\u662f\u2014\u6123\uff0c\u4f46\u5f88\u5feb\u4fbf\u8ba4 \u51fa\u662f\u82d7\u7d20\u82d3\u3002 \u3000\u3000\u4ed6\u6599\u60f3\u82d7\u7d20\u82d3\u5973\u626e\u7537\u88c5\uff0c\u5fc5\u6709\u7528\u610f\uff0c\u4e5f\u5c31\u89c1\u602a\u4e0d\u602a\u4e86\u3002\u56db\u4e0b\u671b\u4e86\u4e00\u773c\uff0c\u95ee\u9053\uff1a\u201c\u8fd9\u662f\u600e \u4e48\u56de\u4e8b\uff0c\u4f60\u4eec\u4e0d\u662f\u90fd\u5728\u8ff7\u8e2a\u8c37\u5417\uff1f\u4e3a\u4ec0\u4e48\u81ea\u76f8\u6b8b\u6740\u8d77\u6765\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u4e0d\u613f\u8ba9\u970d\u5143\u4f3d\u8bc6\u7834\u82d7\u7d20\u82d3\u7684\u8eab\u4efd\uff0c\u8fde\u5fd9\u5954\u4e86\u8fc7\u53bb\uff0c\u62b1\u62f3\u6df1\u6df1\u4e00\u793c\uff0c\u62a2\u7740\u7b54\u9053\uff1a \u201c\u5e9e\u8001\u4f2f\uff0e\u665a\u8f88\u4eec\u8fd9\u8fb9\u7684\u4eba\uff0c\u5df2\u5728\u5341\u5929\u524d\u79bb\u5f00\u8ff7\u8e2a\u8c37\u4e86\uff01\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u4f60\u4eec\u4e3a\u4ec0\u4e48\u8981\u79bb\u5f00\u8ff7\u8e2a\u8c37\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1b\u201c\u970d\u5143\u4f3d\u5bb9\u4e0d\u4e0b\u6211\u4eec\uff0c\u5dee\u70b9\u628a\u6211\u4eec\u6d3b\u6d3b\u70e7\u6b7b\uff0c\u6211\u4eec\u662f\u88ab\u8feb\u79bb\u5f00\u7684\u3002\u201d \u3000\u3000\u53ea\u542c\u970d\u5143\u4f3d\u559d\u9053\uff1a\u201c\u80e1\u8bf4\uff01\u8001\u592b\u4eca\u5929\u6765\uff0e\u5c31\u662f\u8981\u529d\u4f60\u4eec\u56de\u53bb\u7684\uff0c\u4f60\u4eec\u53cd\u8bf4\u662f\u8001\u592b\u5bb9\u4e0d\u4e0b \u4f60\u4eec\uff0c\u7b80\u76f4\u5c82\u6709\u6b64\u7406\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u970d\u5143\u4f3d\uff0c\u4f60\u8bf7\u6211\u4eec\u56de\u53bb\uff0c\u53ef\u662f\u8fd9\u79cd\u8bf7\u6cd5\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u8001\u592b\u5148\u524d\u597d\u8bdd\u8bf4\u5c3d\uff0c\u662f\u4f60\u4eec\u6267\u610f\u4e0d\u542c\uff0c\u6700\u540e\u8feb\u4e0d\u5f97\u5df2\uff0c\u624d\u4e0d\u5f97\u4e0d\u8fd9\u6837\u5bf9\u4ed8 \u4f60\u4eec\u3002\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u7f13\u6b65\u6765\u5230\u53cc\u65b9\u4e2d\u592e\uff0c\u624b\u62c2\u957f\u9aef\u9053\uff1a\u201c\u4f60\u4eec\u53cc\u65b9\u53e3\u5934\u8fd9\u6837\u4e89\u6267\u4e0b\u53bb\uff0e\u6c38\u8fdc\u662f\u6ca1\u5b8c\u6ca1 \u4e86\uff0c\u5012\u4e0d\u5982\u7531\u8001\u592b\u81ea\u884c\u95ee\u95ee\uff0c\u6765\u5f97\u6070\u5f53\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u4e0d\u613f\u5e9e\u58eb\u51b2\u591a\u7ba1\u95f2\u4e8b\uff0c\u4f46\u6151\u4e8e\u4ed6\u6709\u4e00\u8eab\u65e0\u654c\u795e\u529f\uff0c\u5374\u4e5f\u4e0d\u6562\u5f97\u7f6a\uff0c\u7acb\u5373\u62f1\u4e86\u62f1\u624b \u9053\uff1a\u201c\u5e9e\u8001\u524d\u8f88\u6709\u8bdd\u8bf7\u8bb2\uff01\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u5475\u5475\u4e00\u7b11\u9053\uff1a\u201c\u4f60\u53e3\u79f0\u8001\u592b\u524d\u8f88\uff0c\u5b9e\u5728\u4e0d\u6562\u5f53\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u5c0a\u9a7e\u672c\u6765\u5c31\u662f\u524d\u8f88\u9ad8\u4eba\uff0c\u8bba\u5e74\u7eaa\u6bd4\u970d\u67d0\u5927\u4e86\u4e0d\u5c11\uff0e\u4f55\u5fc5\u5ba2\u6c14\u3002\u201d \u3000\u3000\u5e9e\u5341\u51b2\u9894\u9996\u7b11\u9053\uff1a\u201c\u65e2\u7136\u970d\u76df\u4e3b\u8fd8\u77e5\u9053\u656c\u8001\u5c0a\u8d24\uff0c\u8001\u592b\u5c31\u60f3\u8ddf\u4f60\u8c08\u8c08\uff0c\u4f60\u4eec\u53cc\u65b9\u8fd9\u573a\u62fc \u6597\u7684\u539f\u56e0\uff0c\u65b9\u624d\u5df2\u7ecf\u8bf4\u660e\u767d\u4e86\uff0c\u6682\u4e14\u4e0d\u53bb\u7ba1\u5b83\u2026\u2026\u201d \u3000\u3000\u970d\u5143\u4f3d\u622a\u4f4f\u672a\u5b8c\u4e4b\u8a00\u9053\uff1a\u201c\u5e9e\u524d\u8f88\u522b\u542c\u4e00\u9762\u4e4b\u8bcd\uff0c\u90a3\u5c0f\u5b50\u5b8c\u5168\u662f\u80e1\u8bf4\u516b\u9053\uff01\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u9053\uff1a\u201c\u65e2\u7136\u970d\u76df\u4e3b\u65b9\u624d\u4e5f\u8bf4\u8fc7\u8bdd\uff0c\u5c31\u7b97\u4e0d\u5f97\u4e00\u9762\u4e4b\u8bcd\u3002\u603b\u4e4b\uff0c\u4e00\u65b9\u9762\u8981\u8d70\uff0c\u4e00\u65b9 \u9762\u4e0d\u51c6\u4ed6\u4eec\u8d70\uff0c\u5bf9\u5417\uff1f\u201d \u3000\u3000\u201c\u5e9e\u524d\u8f88\u8bf4\u7684\u4e0d\u9519\uff0c\u970d\u67d0\u4eca\u5929\u6765\uff0c\u5c31\u662f\u8981\u529d\u8bf7\u4ed6\u4eec\u56de\u53bb\u7684\u3002\u201d \u3000\u3000\u201c\u8fd9\u662f\u970d\u76df\u4e3b\u7684\u4e00\u756a\u597d\u610f\uff0c\u4f46\u4e3a\u4ec0\u4e48\u53c8\u52a8\u8d77\u6b66\u6765\uff1f\u201d \u3000\u3000\u201c\u4ed6\u4eec\u6267\u610f\u4e0d\u80af\u56de\u53bb\uff0c\u800c\u4e14\u9ea6\u5c0f\u660e\u51fa\u8a00\u4e0d\u900a\u3002\u201d \u3000\u3000\u201c\u9ea6\u5c0f\u660e\u51fa\u8a00\u4e0d\u900a\uff0c\u662f\u9ea6\u5c0f\u660e\u4e00\u4e2a\u4eba\u7684\u4e8b\uff0c\u4e0e\u5176\u4ed6\u7684\u4eba\u4f55\u5e72\uff1f\u201d \u3000\u3000\u201c\u4ed6\u4eec\u672c\u6765\u662f\u4e00\u4f19\u7684\uff0e\u201d \u3000\u3000\u201c\u970d\u76df\u4e3b\uff0c\u4f60\u9519\u4e86\uff01\u201d \u3000\u3000\u201c\u970d\u67d0\u9519\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u201c\u4ed6\u4eec\u79bb\u5f00\u8ff7\u8e2a\u8c37\uff0c\u53ef\u66fe\u72af\u4e0b\u7eff\u6797\u603b\u5be8\u7684\u89c4\u5f8b\uff1f\u201d \u3000\u3000\u201c\u4e0d\u66fe\u3002\u201d \u3000\u3000\u201c\u8fd9\u5c31\u5bf9\u4e86\uff0c\u4ed6\u4eec\u5e76\u975e\u5356\u7ed9\u4e86\u8ff7\u8e2a\u8c37\uff0c\u81ea\u7136\u6709\u53bb\u7559\u7684\u81ea\u7531\uff0c\u4f60\u53ef\u4ee5\u529d\u4ed6\u4eec\u7559\u4e0b\uff0e\u4f46\u4ed6\u4eec \u4e5f\u6709\u4e0d\u7559\u4e0b\u7684\u6743\u5229\uff0e\u8fde\u5728\u671d\u5c45\u5b98\u90fd\u53ef\u4ee5\u544a\u8001\u8fd8\u4e61\uff0c\u53c8\u4f55\u51b5\u4f60\u8fd9\u7eff\u6797\u603b\u5be8\uff0e\u5982\u4eca\u4ed6\u4eec\u4e0d\u80af\u56de\u53bb\uff0c \u4f60\u5c31\u5200\u5175\u76f8\u5411\uff0c\u8fd9\u7b97\u54ea\u95e8\u5b50\u89c4\u77e9\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u7ec8\u4e8e\u88ab\u6fc0\u5f97\u5fcd\u65e0\u53ef\u5fcd\uff0c\u6012\u9053\uff1a\u201c\u83ab\u975e\u5e9e\u524d\u8f88\u8981\u63d2\u624b\u8fd9\u6863\u5b50\u95f2\u4e8b\uff1f\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u54fc\u4e86\u58f0\u9053\uff1a\u201c\u8fd9\u54ea\u91cc\u662f\u95f2\u4e8b\uff0c\u6839\u672c\u5c31\u662f\u4e00\u4ef6\u5927\u4e8b\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u4e0d\u52a8\u58f0\u8272\u9053\uff1a\u201c\u5e9e\u524d\u8f88\u6253\u7b97\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u9053\uff1a\u201c\u4f60\u5148\u628a\u6349\u53bb\u7684\u4ed6\u4eec\u7684\u4e09\u4e2a\u4eba\u653e\u56de\u6765\uff0c\u8001\u592b\u518d\u548c\u4f60\u8c08\u522b\u7684\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u6b63\u5728\u72b9\u8c6b\uff0c\u641c\u9b42\u624b\u5df4\u5929\u4e49\u6765\u5230\u8ddf\u524d\u9053\uff1a\u201c\u7980\u76df\u4e3b\uff0c\u90a3\u4e09\u4e2a\u5343\u4e07\u4e0d\u80fd\u653e\uff0c\u552f\u6709\u628a\u8fd9 \u4e09\u4eba\u5e26\u56de\u505a\u4eba\u8d28\uff0c\u949f\u4e00\u8c6a\u7b49\u4eba\u624d\u4f1a\u4e56\u4e56\u7684\u56de\u5230\u8ff7\u8e2a\u8c37\uff01\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u626b\u63a0\u5df4\u5929\u4e49\u4e00\u773c\u9053\uff1b\u201c\u970d\u76df\u4e3b\uff0c\u8fd9\u4f4d\u662f\u4f60\u624b\u4e0b\u7684\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u50b2\u7136\u4e00\u7b11\u9053\uff1a\u201c\u5e9e\u524d\u8f88\u5fc5\u5b9a\u542c\u8bf4\u8fc7\u5cad\u5357\u4e8c\u5947\u5427\uff1f\u8fd9\u4f4d\u5c31\u662f\u4e8c\u5947\u4e2d\u7684\u8001\u5927\u5df4\u5929\u4e49\u3002\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u7b11\u9053\uff1a\u201c\u5cad\u5357\u4e8c\u5947\uff0c\u5927\u540d\u9f0e\u9f0e\uff0c\u8001\u592b\u81ea\u662f\u4e45\u4ef0\uff0c\u53ef\u5426\u8ba9\u4ed6\u8fc7\u6765\uff0c\u8001\u592b\u8981\u8ddf\u4ed6\u8bb2\u51e0 \u53e5\u8bdd\u3002\u201d \u3000\u3000\u5df4\u5929\u4e49\u4e00\u633a\u80f8\uff0c\u76f4\u5411\u5e9e\u58eb\u51b2\u8d70\u6765\u3002 \u3000\u3000\u7a81\u89c1\u5e9e\u58eb\u51b2\u5bbd\u5927\u7684\u8896\u888d\u4e00\u6296\uff0c\u90a3\u888d\u8896\u7adf\u7136\u66b4\u80c0\u4e94\u516d\u5c3a\u957f\uff0c\u751f\u751f\u628a\u5df4\u5929\u4e49\u62e6\u8170\u5377\u4f4f\u3002 \u3000\u3000\u63a5\u7740\u53ea\u628a\u53f3\u81c2\u53c8\u662f\u4e00\u6296\uff0c\u5df2\u628a\u5df4\u5929\u4e49\u629b\u5411\u7a7a\u4e2d\uff0c\u800c\u4e14\u662f\u5411\u949f\u4e00\u8c6a\u7b49\u4eba\u7684\u65b9\u5411\u6454\u53bb\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u79bb\u5f97\u6700\u8fd1\uff0c\u5df4\u5929\u4e49\u4eba\u672a\u843d\u5730\uff0c\u4fbf\u51fa\u624b\u70b9\u4e86\u4ed6\u7684\u5bd0\u7a74\u3002\u5f85\u6454\u4e0b\u5730\u6765\uff0c\u5df2\u662f\u65e0\u6cd5\u52a8\u5f39\u3002 \u3000\u3000\u970d\u5143\u4f3d\u695e\u4e86\u4e00\u6123\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u5e9e\u524d\u8f88\u8fd9\u7b97\u4f55\u610f\uff1f\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u9053\uff1a\u201c\u81ea\u7136\u4e5f\u662f\u628a\u4ed6\u7559\u4e0b\u505a\u4eba\u8d28\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u559d\u9053\uff1a\u201c\u4f60\u53ef\u662f\u5b58\u5fc3\u8981\u548c\u970d\u67d0\u8fc7\u4e0d\u53bb\uff1f\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u9053\uff1a\u201c\u8001\u592b\u4e00\u751f\uff0c\u4ece\u672a\u4e0e\u4efb\u4f55\u4eba\u8fc7\u4e0d\u53bb\uff0c\u602a\u53ea\u602a\u4f60\u591a\u884c\u4e0d\u4e49\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u53cd\u800c\u5e73\u9759\u4e0b\u6765\uff0c\u6de1\u6de1\u53c8\u9053\uff1a\u201c\u5e9e\u524d\u8f88\u8fd9\u8bdd\u6709\u4ec0\u4e48\u6839\u636e\uff1f\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u77a5\u4e86\u5d06\u5cd2\u4e94\u9b54\u4e00\u773c\u9053\uff1a\u201c\u4f60\u8fde\u8fd9\u4e94\u540d\u4e3a\u6076\u7d2f\u7d2f\u7684\u6c5f\u6e56\u8d25\u7c7b\u90fd\u63a5\u7eb3\u5230\u8ff7\u8e2a\u8c37\u53bb\uff0c\u96be \u9053\u8fd8\u79f0\u4e0d\u4e0a\u591a\u884c\u4e0d\u4e49\uff1f\u201d \u3000\u3000\u201c\u4f60\u5e94\u8be5\u518d\u770b\u770b\u90a3\u5341\u540d\u9ad8\u50e7\uff0c\u4ed6\u4eec\u80af\u7531\u5c11\u6797\u6295\u8eab\u5230\u8ff7\u8e2a\u8c37\uff0c\u53ef\u89c1\u8ff7\u8e2a\u8c37\u5fc5\u5b9a\u6bd4\u81ea\u8be9\u4e3a\u6b63 \u5927\u95e8\u6d3e\u7684\u5c11\u6797\u66f4\u4e3a\u5149\u660e\u6b63\u5927\u3002\u201d \u3000\u3000\u201c\u4e3a\u9996\u50e7\u4eba\uff0c\u6beb\u65e0\u7591\u95ee\u5fc5\u662f\u5c11\u6797\u53db\u5f92\uff0c\u4f60\u80fd\u6536\u7559\u8fd9\u6837\u53db\u5e08\u706d\u7956\u7684\u51fa\u5bb6\u4eba\uff0c\u53c8\u54ea\u91cc\u6765\u7684\u5149 \u660e\u6b63\u5927\uff1f\u201d \u3000\u3000\u5c82\u77e5\u4ed6\u8fd9\u51e0\u53e5\u8bdd\uff0c\u90a3\u609f\u660e\u548c\u4e5d\u540d\u94dc\u4eba\uff0c\u56e0\u662f\u51fa\u5bb6\u4eba\uff0c\u542c\u4e86\u8fd8\u80fd\u52c9\u5f3a\u9690\u5fcd\u4e0d\u53d1\uff0c\u4f46\u5d06\u5cd2\u4e94 \u9b54\u5374\u5df2\u6309\u637a\u4e0d\u5f80\uff0c\u5200\u3001\u5251\u53cc\u9b54\u7acb\u5373\u7531\u9635\u4e2d\u5404\u4ed7\u5175\u5203\u51b2\u4e86\u51fa\u6765\u3002 \u3000\u3000\u8fd9\u4e8c\u4eba\u65e9\u542c\u8bf4\u8fc7\u957f\u767d\u795e\u53df\u5e9e\u58eb\u51b2\u7684\u5927\u540d\uff0c\u771f\u6b63\u89c1\u9762\u8fd8\u662f\u7b2c\u4e00\u6b21\uff0c\u867d\u7136\u65b9\u624d\u5e9e\u58eb\u51b2\u8896\u5377\u5df4 \u5929\u4e49\u7684\u90a3\u624b\u7edd\u62db\u4e5f\u4ee4\u4ed6\u4eec\u6697\u611f\u5fc3\u60ca\uff0c\u4f46\u56e0\u4e3a\u4ed6\u4eec\u89c1\u5e9e\u58eb\u51b2\u5e76\u672a\u643a\u5e26\u5175\u5203\uff0c\u6240\u4ee5\u4ecd\u7136\u6709\u6043\u65e0\u6050\uff0e \u3000\u3000\u5e9e\u58eb\u51b2\u76ee\u6ce8\u53cc\u9b54\uff0c\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4e2a\u6df7\u8d26\u4e1c\u897f\uff0c\u62a5\u4e0a\u540d\u6765\uff01\u201d \u3000\u3000\u5200\u9b54\u51b7\u54fc\u9053\uff1a\u201c\u5728\u4e0b\u8c37\u4e00\u5cf0\uff01\u201d \u3000\u3000\u5251\u9b54\u4e5f\u51b7\u54fc\u9053\uff1a\u201c\u5728\u4e0b\u53f8\u592a\u5e73\uff01\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u7b11\u9053\uff1a\u201c\u6709\u4e86\u4f60\u4eec\u8fd9\u51e0\u4e2a\u6df7\u5e10\uff0c\u5929\u4e0b\u54ea\u91cc\u8fd8\u80fd\u592a\u5e73\u3002\u201d \u3000\u3000\u53f8\u592a\u5e73\u559d\u9053\uff1a\u201c\u8001\u5bb6\u4f19\uff0c\u7528\u4e0d\u7740\u501a\u8001\u5356\u8001\uff0c\u522b\u4eba\u628a\u4f60\u653e\u5728\u773c\u91cc\uff0c\u5d06\u5cd2\u4e94\u4e49\u5374\u4e0d\u5403\u4f60\u8fd9\u4e00 \u5957\uff0c\u65e2\u7136\u8981\u627e\u6b7b\uff0c\u90a3\u5c31\u4eae\u51fa\u5bb6\u4f19\u6765\uff0c\u4e5f\u597d\u8ba9\u4f60\u6b7b\u5f97\u5fc3\u670d\u53e3\u670d\u3002\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u62c2\u4e86\u62c2\u957f\u9aef\u9053\uff1a\u201c\u5bf9\u4ed8\u4f60\u4eec\u8fd9\u4e24\u540d\u9f20\u8f88\uff0c\u8001\u592b\u53ea\u8981\u2014\u53cc\u624b\u5c31\u591f\u4e86\uff01\u201d \u3000\u3000\u5200\u5251\u53cc\u9b54\u54ea\u91cc\u5fcd\u5f97\u4e0b\u8fd9\u53e3\u6c14\uff0c\u7acb\u5373\u4f7f\u51fa\u7edd\u62db\uff0c\u51cc\u5389\u65e0\u6bd4\u7684\u5411\u5e9e\u58eb\u51b2\u653b\u6765\u3002 \u3000\u3000\u4e00\u65f6\u4e4b\u95f4\uff0c\u5200\u5149\u5251\u5f71\uff0c\u72b9\u5982\u7535\u95ea\u96f7\u5954\uff0c\u770b\u7684\u4eba\u65e0\u4e0d\u60ca\u5fc3\u52a8\u9b44\u3002 \u3000\u3000\u5c82\u77e5\u5e9e\u58eb\u51b2\u53cc\u811a\u534a\u70b9\u4e0d\u89c1\u79fb\u52a8\uff0c\u8fde\u5de6\u624b\u7adf\u7136\u4e5f\u76f4\u5782\u4e0d\u7528\uff0c\u53ea\u51ed\u53f3\u81c2\u4e00\u6761\u888d\u8896\u5728\u8fce\u7740\u5200\u5251 \u6325\u821e\uff0e \u3000\u3000\u53cc\u65b9\u89c2\u6218\u4e4b\u4eba\uff0c\u53ea\u80fd\u8bf4\u4ed6\u7684\u888d\u8896\u662f\u5728\u6325\u821e\uff0c\u4f46\u90a3\u888d\u8896\u5374\u50cf\u5145\u6ee1\u4e86\u6c14\uff0c\u81a8\u9686\u800c\u8d77\uff0c\u8fdc\u770b\u6709 \u5982\u4e00\u53ea\u94c1\u6876\uff0c\u7531\u94c1\u6876\u5185\u6d8c\u51fa\u6765\u7684\u4e00\u80a1\u6f5c\u529b\uff0c\u7adf\u4f3c\u60ca\u6d9b\u9a87\u6d6a\u4e2d\u7684\u6012\u6f6e\u4e00\u822c\uff0c\u6ce2\u6ce2\u4e0d\u7edd\u3002 \u3000\u3000\u90a3\u5200\u9b54\u8c37\u4e00\u5cf0\u548c\u5251\u9b54\u53f8\u592a\u5e73\uff0c\u867d\u7136\u653b\u52bf\u51cc\u5389\uff0c\u4f46\u5728\u5e9e\u58eb\u51b2\u888d\u8896\u5185\u90a3\u80a1\u5de8\u5927\u7684\u6697\u52b2\u51b2\u903c\u4e4b \u4e0b\uff0c\u7adf\u7136\u59cb\u7ec8\u4e0d\u80fd\u8fd1\u8eab\uff0e \u3000\u3000\u4ee5\u81f4\u53cc\u65b9\u59cb\u7ec8\u4fdd\u6301\u7740\u4e00\u6bb5\u8ddd\u79bb\uff0e\u770b\u8d77\u6765\u72b9\u5982\u5404\u81ea\u5728\u8868\u6f14\u7edd\u6280\uff0c\u770b\u4e86\u53cd\u800c\u89c9\u5f97\u6709\u70b9\u53ef\u7b11\u3002 \u3000\u3000\u5c31\u8fd9\u6837\u5200\u5251\u53cc\u9b54\u8db3\u8db3\u8fdb\u653b\u4e86\u4e09\u56db\u5341\u62db\uff0c\u2014\u76f4\u65e0\u6cd5\u653b\u8fdb\u5e9e\u58eb\u51b2\u8eab\u524d\u4e09\u5c3a\u4e4b\u5185\u3002 \u3000\u3000\u6e10\u6e10\uff0c\u5200\u5251\u53cc\u9b54\u53ef\u80fd\u6709\u4e9b\u7b4b\u75b2\u529b\u5c3d\uff0c\u653b\u52bf\u5f88\u5feb\u4fbf\u7f13\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u7a81\u89c1\u5e9e\u58eb\u51b2\u53f3\u8896\u53c8\u66b4\u80c0\u6570\u5c3a\uff0c\u76f4\u51fb\u4e2d\u5200\u9b54\u8c37\u4e00\u5cf0\u524d\u80f8\u3002 \u3000\u3000\u8c37\u4e00\u5cf0\u2014\u58f0\u95f7\u54fc\uff0c\u5f53\u573a\u88ab\u51fb\u98de\u8d77\u4e24\u4e08\u591a\u9ad8\uff0c\u53c8\u5728\u7a7a\u4e2d\u659c\u6454\u51fa\u4e09\u56db\u4e08\u5916\uff0c\u624d\u6389\u843d\u5730\u4e0a\u3002 \u3000\u3000\u5251\u9b54\u53f8\u592a\u5e73\u5927\u9a87\u4e4b\u4e0b\uff0c\u521a\u8981\u5411\u540e\u8dc3\u9000\uff0c\u4e5f\u88ab\u5e9e\u58eb\u51b2\u53c8\u2014\u8896\u626b\u4e2d\u3002 \u3000\u3000\u53ef\u80fd\u5e9e\u58eb\u51b2\u6240\u7528\u7684\u529b\u9053\u4e0d\u540c\uff0c\u53f8\u592a\u5e73\u5e76\u672a\u5347\u7a7a\uff0c\u5374\u662f\u5728\u5730\u4e0a\u4e0d\u4f4f\u7ffb\u6eda\uff0c\u76f4\u6eda\u51fa\u597d\u51e0\u4e08\u8fdc\uff0c \u624d\u88ab\u4e94\u9b54\u4e2d\u7684\u8272\u9b54\u82b1\u79c0\u53ca\u65f6\u62e6\u4f4f\u3002 \u3000\u3000\u4f46\u82b1\u79c0\u4e5f\u6454\u4e86\u4e2a\u8ddf\u5934\u3002 \u3000\u3000\u970d\u5143\u4f3d\u7acb\u5373\u559d\u4ee4\u609f\u660e\u6446\u4e0b\u94dc\u4eba\u9635\u3002 \u3000\u3000\u8fd9\u94dc\u4eba\u9635\uff0c\u4e5f\u5c31\u662f\u7f57\u6c49\u9635\u3002 \u3000\u3000\u5c11\u6797\u6d3e\u7684\u7f57\u6c49\u9635\uff0e\u5929\u4e0b\u95fb\u540d\uff0e \u3000\u3000\u7f57\u6c49\u9635\u6709\u5927\u5c0f\u4e4b\u5206\uff0c\u5927\u7f57\u6c49\u9635\u4e3a\u4e5d\u4e5d\u516b\u5341\u4e00\u4eba\uff0c\u5c0f\u7f57\u6c49\u9635\u4e3a\u5341\u516b\u4eba\u3002 \u3000\u3000\u94dc\u4eba\u9635\u81ea\u7136\u662f\u5c0f\u7f57\u6c49\u9635\uff0c\u5374\u56e0\u5341\u516b\u94dc\u4eba\u7686\u662f\u7edd\u9876\u9ad8\u624b\uff0c\u66f4\u56e0\u6bcf\u65e5\u64cd\u7ec3\u6b64\u9635\uff0c\u6bcf\u4eba\u95f4\u90fd\u6709 \u58f0\u6c14\u76f8\u901a\u7684\u9ed8\u5951\u3002\u56e0\u4e4b\uff0c\u5a01\u52bf\u4e4b\u731b\u4e4b\u5927\uff0c\u81ea\u662f\u8981\u80dc\u8fc7\u4e00\u822c\u5c0f\u7f57\u6c49\u9635\u591a\u591a\u3002 \u3000\u3000\u53ef\u60dc\u7684\u662f\uff0c\u609f\u660e\u53ea\u5e26\u51fa\u4e5d\u540d\u94dc\u4eba\uff0c\u529b\u91cf\u51cf\u4e86\u2014\u534a\uff0c\u9635\u5f0f\u4e5f\u4e0d\u5f97\u4e0d\u968f\u4e4b\u6539\u53d8\u3002 \u3000\u3000\u53ea\u89c1\u90a3\u4e5d\u540d\u94dc\u4eba\uff0c\u5728\u609f\u660e\u7684\u6307\u6325\u4e4b\u4e0b\uff0c\u8f6c\u77ac\u95f4\u4fbf\u5404\u53d6\u65b9\u4f4d\uff0c\u6446\u6210\u9635\u5f0f\uff0e \u3000\u3000\u5341\u516b\u94dc\u4eba\u9635\u539f\u662f\u5206\u4f5c\u4e09\u6392\uff0c\u6bcf\u6392\u516d\u4eba\uff0c\u6b64\u523b\u867d\u7136\u4ecd\u662f\u4e09\u6392\uff0c\u5374\u6bcf\u6392\u53ea\u6709\u4e09\u4eba\uff0c\u5f62\u6210\u2014\u4e2a \u65b9\u9635\u3002 \u3000\u3000\u5e9e\u58eb\u51b2\u62c2\u9aef\u8fde\u7b11\u51e0\u58f0\u9053\uff1a\u201c\u970d\u76df\u4e3b\uff0c\u4f60\u53ef\u662f\u8981\u4ee5\u94dc\u4eba\u7f57\u6c49\u9635\u6765\u5413\u9000\u8001\u592b\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u795e\u8272\u51b7\u51dd\uff0c\u54fc\u4e00\u58f0\u9053\uff1a\u201c\u5c0a\u9a7e\u60f3\u5fc5\u81ea\u4fe1\u80fd\u51b2\u7834\u94dc\u4eba\u9635\u4e86\uff01\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u9053\uff1a\u201c\u8001\u592b\u2014\u751f\uff0c\u5e76\u4e0d\u66fe\u4e0e\u5c11\u6797\u4e3a\u654c\uff0c\u5c11\u6797\u81ea\u7136\u4e5f\u4e0d\u66fe\u4e3a\u8001\u592b\u8bbe\u4e0b\u7f57\u6c49\u9635\uff0c\u4e0d \u8fc7\u2026\u2026\u201d \u3000\u3000\u201c\u5c0a\u9a7e\u4e0d\u8fc7\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u4e0d\u8fc7\u970d\u76df\u4e3b\u8fd9\u7f57\u6c49\u9635\u5df2\u7ecf\u6b8b\u7f3a\u4e0d\u5168\uff0e\u8001\u592b\u81ea\u4fe1\u8fd8\u6709\u80fd\u529b\u5c06\u6b64\u9635\u51b2\u7834\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u4f3c\u662f\u9887\u6709\u81ea\u4fe1\uff0c\u51b7\u51b7\u4e00\u7b11\u9053\uff1a\u201c\u90a3\u5c31\u4e0d\u59a8\u8bd5\u8bd5\uff01\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u7b11\u9053\uff1a\u201c\u8fd9\u94dc\u4eba\u9635\uff0c\u7b97\u662f\u4f60\u7684\u6700\u540e\u51ed\u85c9\u4e86\uff0c\u5982\u679c\u4e0d\u5e78\u88ab\u8001\u592b\u51b2\u7834\uff0c\u4f60\u53c8\u8be5\u5982\u4f55\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u970d\u67d0\u60c5\u613f\u653e\u56de\u4ed6\u4eec\u6c5f\u5317\u4e09\u9f99\u3002\u201d \u3000\u3000\u5374\u89c1\u5e9e\u58eb\u51b2\u5411\u540e\u9000\u4e86\u51e0\u6b65\uff0c\u671b\u4e86\u7ad9\u5728\u4e00\u65c1\u7684\u9ea6\u5c0f\u660e\u4e00\u773c\u9053\uff1a\u201c\u5c0f\u5a03\u513f\uff0c\u8fd9\u573a\u529f\u52b3\u8ba9\u7ed9 \u4f60\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u5e9e\u8001\u4f2f\u53ef\u662f\u8981\u665a\u8f88\u7834\u9635\uff1f\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u9053\uff1a\u201c\u770b\u4f60\u521a\u624d\u51fa\u6218\u7684\u8eab\u624b\uff0c\u597d\u50cf\u6b66\u529f\u53c8\u6709\u8fdb\u5883\uff0c\u53ea\u7ba1\u5927\u80c6\u653b\u9635\uff0c\u5982\u679c\u653b\u4e0d\u7834\uff0c \u8001\u592b\u518d\u52a9\u4f60\u4e00\u81c2\u4e4b\u529b\uff0e\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u7ffb\u8155\u62d4\u51fa\u957f\u5251\u9053\uff1a\u201c\u5c11\u6797\u7684\u7f57\u6c49\u9635\uff0c\u665a\u8f88\u4ee5\u524d\u8fde\u89c1\u90fd\u6ca1\u89c1\u8fc7\uff0c\u8be5\u5982\u4f55\u7834\u6cd5\uff0c\u8001\u524d \u8f88\u662f\u5426\u53ef\u4ee5\u6307\u70b9\u4e00\u4e8c\uff1f\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u7b11\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u5403\u996d\u548c\u5927\u5c0f\u4fbf\u8981\u4e0d\u8981\u8001\u592b\u6559\u4f60\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u8138\u4e0a\u4e00\u70ed\uff0c\u7acb\u5373\u8dc3\u8eab\u6765\u5230\u94dc\u4eba\u9635\u524d\uff0e \u3000\u3000\u53ea\u89c1\u90a3\u4e5d\u540d\u94dc\u4eba\uff0c\u4e2a\u4e2a\u76ee\u5149\u4f3c\u7535\uff0c\u6bcf\u4eba\u624b\u91cc\u90fd\u7d27\u63e1\u7740\u4e00\u6761\u513f\u81c2\u7c97\u7ec6\u7684\u94c1\u7985\u6756\uff0c\u4f46\u90a3\u94c1\u7985 \u6756\u5374\u6709\u6a2a\u6709\u7ad6\uff0c\u6709\u659c\u6709\u9877\uff0c\u5728\u94dc\u4eba\u4eec\u7684\u8eab\u524d\u90e8\u4f4d\u5404\u81ea\u4e0d\u540c\u3002 \u3000\u3000\u6700\u5413\u4eba\u7684\uff0e\u662f\u4ed6\u4eec\u4e2a\u4e2a\u4eba\u9ad8\u9a6c\u5927\uff0c\u8180\u5927\u8170\u5706\uff0c\u679c\u771f\u5982\u94dc\u6d47\u7f57\u6c49\uff0e\u94c1\u6253\u91d1\u521a\u4e00\u822c\uff0c\u8d8a\u662f\u7ad9 \u5728\u90a3\u91cc\u624e\u6869\u5982\u5c71\u7684\u4e0d\u52a8\uff0c\u8d8a\u591f\u5413\u4eba\u7684\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u9762\u5bf9\u7740\u8fd9\u4e5d\u540d\u94dc\u4eba\uff0e\u5f53\u771f\u6709\u4e9b\u80c6\u98a4\u5fc3\u60ca\uff0e\u6253\u5fc3\u5e95\u5192\u4e0a\u4e00\u80a1\u5bd2\u610f\u3002 \u3000\u3000\u770b\u970d\u5143\u4f3d\u65f6\uff0c\u9762\u6cdb\u9634\u7b11\uff0c\u4e00\u526f\u6d0b\u6d0b\u5f97\u610f\u6a21\u6837\uff0e \u3000\u3000\u53ea\u542c\u5e9e\u58eb\u51b2\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u5b50\u8fd8\u6123\u5728\u90a3\u91cc\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u731b\u7136\u8dc3\u8d77\uff0c\u62a1\u5251\u4fbf\u5411\u524d\u5217\u6700\u53f3\u4e00\u540d\u94dc\u4eba\u653b\u53bb\u3002 \u3000\u3000\u5c82\u77e5\u51e0\u4e4e\u5728\u540c\u4e00\u65f6\u95f4\uff0c\u524d\u5217\u4e09\u540d\u94dc\u4eba\u7684\u94c1\u7985\u6756\u7adf\u9f50\u9f50\u8fce\u4e86\u4e0a\u6765\u3002 \u3000\u3000\u2014\u6761\u94c1\u7985\u6756\u626b\u51fa\u7684\u5a01\u529b\uff0c\u5df2\u8db3\u53ef\u5f00\u7891\u88c2\u77f3\uff0c\u4e09\u6761\u94c1\u7985\u6756\u9f50\u529b\u5408\u51fb\uff0c\u90a3\u529b\u9053\u4f55\u6b62\u5343\u94a7\uff0c\u9ea6 \u5c0f\u660e\u4eba\u5728\u7a7a\u4e2d\uff0c\u95ea\u8eb2\u4e0d\u53ca\uff0c\u76f4\u88ab\u9707\u98de\u8d77\u56db\u4e94\u4e08\u9ad8\uff0c\u60ac\u7a7a\u8fde\u6253\u4e24\u4e2a\u6eda\u7ffb\uff0c\u624d\u7a33\u4f4f\u4e0b\u843d\u4e4b\u52bf\u3002 \u3000\u3000\u4ed6\u867d\u7136\u843d\u4e0b\u5730\u540e\u5e76\u672a\u683d\u5012\uff0c\u4f46\u53f3\u81c2\u5374\u9178\u9ebb\u4e0d\u5df2\uff0c\u6240\u5e78\u4ed6\u65b9\u624d\u62fc\u6b7b\u63e1\u4f4f\u957f\u5251\uff0c\u5175\u5668\u5e76\u672b\u8131 \u624b\uff0c\u53ea\u542c\u5e9e\u58eb\u51b2\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u88ab\u9707\u5f97\u4e0d\u8f7b\u5427\uff1f\u6ca1\u5173\u7cfb\uff0c\u8981\u518d\u63a5\u518d\u52b1\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u7565\u4e00\u5598\u606f\uff0c\u679c\u7136\u53c8\u8dc3\u5230\u94dc\u4eba\u9635\u524d\u3002 \u3000\u3000\u8fd9\u65f6\u4e24\u65c1\u89c2\u6218\u4e4b\u4eba\uff0c\u65e0\u4e0d\u805a\u7cbe\u4f1a\u795e\uff0e\u5c24\u5176\u949f\u4e00\u8c6a\u7b49\uff0c\u66f4\u4e3a\u9ea6\u5c0f\u660e\u634f\u2014\u628a\u6c57\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u8fd9\u6b21\u5e76\u4e0d\u8dc3\u8d77\u8fdb\u653b\uff0c\u7adf\u662f\u624b\u6a2a\u957f\u5251\uff0c\u7f13\u7f13\u5411\u524d\u903c\u8fd1\u3002 \u3000\u3000\u5f53\u8ddd\u79bb\u524d\u5217\u90a3\u6700\u53f3\u4e00\u540d\u94dc\u4eba\u8eab\u4fa7\u4e0d\u8db3\u4e00\u4e08\u65f6\uff0e\u7a81\u89c1\u4ed6\u5de6\u624b\u5e76\u8d77\u98df\u4e2d\u4e8c\u6307\uff0c\u4e00\u7f15\u6307\u98ce\uff0c\u76f4 \u5411\u90a3\u94dc\u4eba\u53f3\u80a9\u5c04\u53bb\uff0c\u7d27\u8ddf\u7740\u6b3a\u8eab\u75be\u8fdb\uff0c\u95ea\u7535\u822c\u5288\u51fa\u2014\u5251\u3002 \u3000\u3000\u4ed6\u5251\u52bf\u9012\u51fa\uff0c\u7acb\u5373\u518d\u95ea\u7535\u822c\u9000\u4e86\u56de\u6765\u3002 \u3000\u3000\u8fd9\u6b21\u679c\u7136\u4e00\u51fb\u594f\u6548\uff0c\u53ea\u542c\u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c\u524d\u6392\u6700\u53f3\u4e00\u540d\u94dc\u4eba\u7684\u94c1\u7985\u6756\u5df2\u7ecf\u843d\u4e86\u5730\uff0e \u3000\u3000\u539f\u6765\u90a3\u94dc\u4eba\u53f3\u80a9\u5148\u88ab\u6307\u98ce\u88ad\u4e2d\uff0c\u3000\u2014\u9635\u5267\u75db\uff0c\u52b2\u9053\u5168\u5931\uff0c\u518d\u88ab\u5bf9\u65b9\u5288\u51fa\u4e00\u5251\uff0c\u5f53\u7136\u5df2\u65e0 \u529b\u62b5\u6321\uff0e\u80fd\u4e0d\u53d7\u4f24\uff0c\u5df2\u662f\u4e07\u5e78\u3002 \u3000\u3000\u81f3\u4e8e\u524d\u5217\u53e6\u4e24\u540d\u94dc\u4eba\uff0c\u867d\u7136\u4e5f\u540c\u65f6\u6325\u52a8\u94c1\u7985\u6756\u8fce\u51fb\u8fc7\u6765\uff0c\u4f46\u56e0\u9ea6\u5c0f\u660e\u9000\u5f97\u592a\u5feb\uff0e\u81ea\u7136\u4e5f \u5168\u843d\u7a7a\uff0c\u597d\u5728\u4ed6\u4eec\u80fd\u53ca\u65f6\u6536\u4f4f\uff0c\u5426\u5219\u8bf4\u4e0d\u5b9a\u5c31\u8981\u51fb\u4e2d\u6700\u53f3\u2014\u540d\u94dc\u4eba\u3002 \u3000\u3000\u5c31\u5728\u94dc\u4eba\u9635\u4e00\u9635\u6df7\u4e71\u4e4b\u9645\uff0c\u7a81\u542c\u6700\u540e\u4e00\u5217\u4e09\u540d\u94dc\u4eba\u4e2d\u7684\u4e00\u540d\uff0e\u7adf\u7136\u60e8\u53eb\u4e00\u58f0\uff0c\u968f\u5373\u5012\u5730 \u660f\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u539f\u6765\u9ea6\u5c0f\u660e\u8d81\u516b\u540d\u94dc\u4eba\u7684\u6ce8\u610f\u529b\u90fd\u96c6\u4e2d\u5728\u94c1\u7985\u6756\u843d\u5730\u7684\u90a3\u540d\u50e7\u4eba\u8eab\u4e0a\u65f6\uff0c\u7acb\u523b\u4ee5\u5feb\u5f97\u4e0d \u80fd\u518d\u5feb\u7684\u6458\u661f\u6b65\uff0c\u7ed5\u95ea\u5230\u94dc\u4eba\u9635\u540e\u65b9\uff0c\u5077\u5077\u7684\u2014\u6307\u70b9\u4e2d\u2014\u540d\u94dc\u4eba\u7684\u540e\u8111\u201c\u7389\u6795\u7a74\u201d\u3001\u90a3\u94dc\u4eba \u5c82\u80fd\u4e0d\u660f\u53a5\u8fc7\u53bb\u3002 \u3000\u3000\u4e5d\u540d\u94dc\u4eba\u5217\u6210\u7684\u9635\u5f0f\uff0c\u672c\u6765\u5c31\u5a01\u529b\u5927\u51cf\uff0c\u5982\u4eca\u4f24\u4e86\u4e24\u4eba\uff0c\u8d8a\u53d1\u96be\u4ee5\u914d\u5408\uff0e \u3000\u3000\u94dc\u4eba\u9635\u6307\u6325\u609f\u660e\u4e00\u89c1\u4e0d\u5999\uff0c\u53ea\u597d\u4e5f\u52a0\u5165\u9635\u4e2d\u51d1\u6570\u3002 \u3000\u3000\u522b\u770b\u4ed6\u662f\u5341\u516b\u94dc\u4eba\u9886\u961f\uff0c\u82e5\u8bba\u529f\u529b\u4e4b\u7cbe\u7edd\uff0c\u4ed6\u5374\u8fdc\u6bd4\u4e0d\u4e0a\u4efb\u4f55\u4e00\u540d\u94dc\u4eba\uff0c\u8fd9\u5c31\u50cf\u519b\u961f\u91cc \u6d41\u884c\u7684\u4e00\u53e5\u8bdd\u201c\u5b98\u6015\u5165\u5217\uff0c\u5175\u6015\u51fa\u5217\u201d\u4e00\u6837\uff0c\u5728\u9635\u5916\u6307\u6325\u5934\u5934\u662f\u9053\uff0c\u4e00\u65e6\u5165\u4e86\u9635\uff0c\u53cd\u800c\u614c\u4e86 \u624b\u811a\uff0c\u65e0\u6240\u9002\u4ece\u3002 \u3000\u3000\u6b63\u56e0\u5982\u6b64\uff0c\u6b64\u523b\u7684\u94dc\u4eba\u9635\uff0c\u65e0\u5f62\u4e2d\u5df2\u9635\u811a\u5927\u4e71\u3002 \u3000\u3000\u4f46\u89c1\u9ea6\u5c0f\u660e\u817e\u8eab\u800c\u8d77\uff0c\u7adf\u7136\u8dc3\u5230\u94dc\u4eba\u9635\u7684\u4e0a\u7a7a\u3002 \u3000\u3000\u9635\u4e2d\u516b\u4eba\u89c1\u6b64\u60c5\u5f62\uff0c\u9f50\u9f50\u62a1\u8d77\u94c1\u7985\u6756\u5411\u4e0a\u65b9\u8fce\u51fb\u3002 \u3000\u3000\u54ea\u77e5\u8fd9\u4e00\u51fb\u7531\u4e8e\u76ee\u6807\u90fd\u96c6\u4e2d\u5728\u9ea6\u5c0f\u660e\u2014\u4eba\u8eab\u4e0a\uff0c\u53ea\u542c\u4e00\u9635\u9707\u8033\u6b32\u804b\u7684\u91d1\u94c1\u4ea4\u9e23\u4e4b\u58f0\uff0c\u7adf \u7136\u5f62\u6210\u516b\u652f\u94c1\u7985\u6756\u81ea\u884c\u7ea0\u7f20\u649e\u51fb\u8d77\u6765\uff0e \u3000\u3000\u9ea6\u5c0f\u660e\u5c31\u8d81\u8fd9\u65f6\u523b\u53c8\u70b9\u5012\u4e86\u2014\u4e2a\uff0c\u7136\u540e\u4e00\u4e2a\u5012\u7eb5\uff0c\u98d8\u843d\u9635\u5916\u3002 \u3000\u3000\u504f\u504f\u8fd9\u88ab\u70b9\u5012\u7684\u7adf\u662f\u94dc\u4eba\u6307\u6325\u609f\u660e\u3002 \u3000\u3000\u609f\u660e\u5931\u53bb\u6307\u6325\u80fd\u529b\uff0c\u90a3\u4e03\u540d\u94dc\u4eba\u5df2\u662f\u7fa4\u9f99\u65e0\u9996\uff0c\u9635\u5f0f\u8d8a\u53d1\u6742\u4e71\u65e0\u7ae0\u3002 \u3000\u3000\u539f\u6765\u5c11\u6797\u5bfa\u7684\u5341\u516b\u94dc\u4eba\uff0e\u867d\u7136\u4e2a\u4e2a\u8eab\u6750\u58ee\u731b\uff0c\u5929\u751f\u795e\u529b\uff0c\u6756\u6cd5\u5df2\u8fbe\u5230\u7089\u706b\u7eaf\u9752\u4e4b\u5883\uff0c\u4f46 \u5374\u53ea\u662f\u56db\u80a2\u53d1\u8fbe\uff0c\u5934\u8111\u7b80\u5355\u3002 \u3000\u3000\u4ed6\u4eec\u53ea\u662f\u751f\u5c31\u2014\u526f\u7ec3\u6b66\u7684\u597d\u6750\u6599\uff0c\u5374\u6ca1\u6709\u591a\u5927\u667a\u6167\uff0c\u5728\u5931\u53bb\u9886\u961f\u7684\u8c03\u5ea6\u6307\u6325\u540e\uff0c\u5f53\u7136\u4e5f \u5c31\u53d1\u6325\u4e0d\u51fa\u539f\u6709\u5a01\u529b\u3002 \u3000\u3000\u5176\u5b9e\u970d\u5143\u4f3d\u8fd9\u6b21\u4e5f\u5934\u8111\u4e0d\u591f\u7075\u5149\uff0c\u65e2\u7136\u94dc\u4eba\u5df2\u65e0\u6cd5\u6446\u6210\u9635\u5f0f\u3002\u5c31\u8be5\u547d\u4ee4\u94dc\u4eba\u81ea\u7531\u884c\u52a8\uff0c \u7136\u540e\u518d\u4e3b\u52a8\u8054\u624b\u5411\u9ea6\u5c0f\u660e\u8fdb\u653b\uff0c\u9ea6\u5c0f\u660e\u7eb5\u7136\u6b66\u529f\u518d\u9ad8\uff0c\u4e5f\u5fc5\u5b9a\u96be\u4ee5\u62db\u67b6\u3002 \u3000\u3000\u504f\u504f\u4ed6\u5374\u8001\u8ba9\u94dc\u4eba\u5904\u4e8e\u6328\u6253\u5730\u4f4d\uff0c\u4ee5\u81f4\u88ab\u9ea6\u5c0f\u660e\u5404\u4e2a\u51fb\u7834\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u6b63\u8981\u8bbe\u6cd5\u518d\u653b\uff0c\u5374\u542c\u5e9e\u58eb\u51b2\u53eb\u9053\uff1a\u201c\u5c0f\u5b50\uff0c\u6682\u65f6\u9000\u56de\u6765\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u56de\u5230\u5e9e\u58eb\u51b2\u8eab\u524d\u9053\uff1a\u201c\u665a\u8f88\u7834\u9635\u5df2\u7ecf\u7834\u5f97\u5dee\u4e0d\u591a\u4e86\uff0c\u5e9e\u8001\u4f2f\u4e3a\u4ec0\u4e48\u53c8\u628a\u6211\u558a\u56de \u6765\uff1f\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u5b50\u679c\u771f\u6709\u4e24\u4e0b\u5b50\uff0e\u5f85\u8001\u592b\u548c\u970d\u5143\u4f3d\u518d\u8bb2\u51e0\u53e5\u8bdd\u518d\u8bf4\u3002\u201d \u3000\u3000\u8bf4\u7740\u8f6c\u8fc7\u8eab\u9053\uff1a\u201c\u970d\u76df\u4e3b\uff0c\u94dc\u4eba\u9635\u4e00\u5411\u5929\u4e0b\u65e0\u654c\uff0c\u8001\u592b\u5b9e\u4e0d\u5fcd\u89c1\u5b83\u7834\u5728\u50cf\u9ea6\u5c0f\u660e\u8fd9\u6837\u4e00 \u4e2a\u540e\u751f\u665a\u8f88\u624b\u91cc\u3002\u662f\u5426\u7531\u9ea6\u5c0f\u660e\u7ee7\u7eed\u653b\u9635\uff0c\u4e0d\u59a8\u7531\u4f60\u81ea\u5df1\u6765\u51b3\u5b9a\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u65e9\u5df2\u9762\u8272\u94c1\u9752\uff0c\u54fc\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u597d\u5427\uff01\u970d\u67d0\u4eca\u5929\u7b97\u767d\u6765\u4e00\u8d9f\u3002\u201d \u3000\u3000\u201c\u90a3\u5c31\u8bf7\u628a\u6c5f\u5317\u7684\u4e09\u4f4d\u670b\u53cb\u653e\u56de\u6765\uff01 \u3000\u3000\u201c\u53ef\u662f\u4ed6\u4eec\u624b\u91cc\u8fd8\u6709\u970d\u67d0\u7684\u4eba\u3002\u201d \u3000\u3000\u201c\u5f53\u7136\u8981\u8d70\u9a6c\u6362\u5c06\u3002\u201d \u3000\u3000\u201c\u4ed6\u4eec\u4e00\u4e2a\u6362\u4e09\u4e2a\uff0c\u672a\u514d\u592a\u5360\u4fbf\u5b9c\uff0e\u201d \u3000\u3000\u201c\u4f60\u662f\u5426\u51c6\u5907\u628a\u8001\u592b\u4e5f\u6362\u56de\u53bb\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u795e\u8272\u4e00\u7a92\uff0c\u4ed6\u5e72\u54b3\u4e24\u58f0\uff0c\u7acb\u523b\u5429\u5490\u9053\uff1a\u201c\u628a\u4ed6\u4eec\u4e09\u4eba\u653e\u56de\u53bb\uff01\u201d \u3000\u3000\u5f53\u6c5f\u5317\u4e09\u9f99\u56de\u6765\u540e\uff0c\u949f\u4e00\u8c6a\u65e9\u5df2\u4e3b\u52a8\u628a\u5df4\u5929\u4e49\u4e5f\u653e\u4e86\u8fc7\u53bb\uff0e \u3000\u3000\u970d\u5143\u4f3d\u5411\u5e26\u6765\u7684\u4eba\u6325\u4e86\u6325\u624b\u9053\uff1a\u201c\u54b1\u4eec\u8d70\uff01\u201d \u3000\u3000\u8fd9\u4e9b\u4eba\u53bb\u65f6\u6bd4\u6765\u65f6\u8981\u6162\u5f97\u591a\uff0c\u56e0\u4e3a\u5176\u4e2d\u6709\u4e0d\u5c11\u53d7\u4f24\u7684\uff0c\u5c24\u5176\u5d06\u86d4\u4e94\u9b54\u4e2d\u7684\u8001\u4e8c\u9b3c\u9b54\u5deb\u9053 \u5168\uff0c\u56e0\u65ad\u53bb\u4e00\u6761\u5de6\u81c2\uff0c\u5fc5\u987b\u6709\u4eba\u62ac\u7740\u624d\u80fd\u56de\u53bb\u3002 \u3000\u3000\u7fa4\u8c6a\u89c1\u970d\u5143\u4f3d\u7387\u4f17\u53bb\u8fdc\uff0c\u624d\u9f50\u9f50\u56f4\u62e2\u4e0a\u6765\u5411\u5e9e\u58eb\u51b2\u884c\u793c\u81f4\u8c22\u3002 \u3000\u3000\u8fd9\u4e9b\u4eba\u6570\u6708\u524d\u5728\u5929\u53f0\u5c71\u56e0\u8c37\u5bd2\u9999\u7684\u9057\u4f53\u4e0d\u89c1\uff0c\u5bf9\u5e9e\u58eb\u51b2\u96be\u514d\u6709\u731c\u5fcc\u4e4b\u5fc3\uff0c\u6b64\u523b\u4e0d\u4f46\u5bf9\u4ed6 \u8f6c\u53d8\u4e86\u5370\u8c61\uff0c\u800c\u4e14\u66f4\u5bf9\u4ed6\u611f\u6069\u4e0d\u5c3d\u3002 \u3000\u3000\u5f53\u4e0b\uff0c\u7fa4\u8c6a\u628a\u5e9e\u58eb\u51b2\u8fce\u81f3\u5927\u5385\u5f85\u8336\uff0e \u3000\u3000\u949f\u4e00\u8c6a\u53c8\u6d3e\u6587\u5929\u751f\u5230\u540e\u9762\u63a2\u89c6\u738b\u5927\u5eb7\u662f\u5426\u5df2\u7ecf\u9192\u6765\uff0e \u3000\u3000\u5e9e\u58eb\u51b2\u9053\uff1a\u201c\u4e0d\u9192\u4e5f\u8981\u628a\u4ed6\u62ac\u6765\uff0c\u8001\u592b\u8eab\u8fb9\u5e26\u7740\u51e0\u74f6\u89e3\u836f\uff0c\u4e5f\u8bb8\u80fd\u4e3a\u4ed6\u7597\u6bd2\u3002\u201d \u3000\u3000\u4e0d\u5927\u2014\u4f1a\u5de5\u592b\uff0c\u6587\u5929\u751f\u5df2\u548c\u9c8d\u8d85\u5408\u529b\u628a\u738b\u5927\u5eb7\u62ac\u8fdb\u5927\u5385\u3002 \u3000\u3000\u738b\u5927\u5eb7\u80a4\u8272\u9752\u4e2d\u6cdb\u7d2b\uff0c\u4e00\u76f4\u5728\u547b\u541f\u4e0d\u5df2\uff0c\u663e\u51fa\u4e00\u526f\u6781\u7aef\u75db\u82e6\u7684\u6a21\u6837\u3002 \u3000\u3000\u5e9e\u58eb\u51b2\u4fef\u8eab\u89c2\u5bdf\u4e86\u534a\u664c\uff0c\u4ece\u6000\u91cc\u638f\u51fa\u4e00\u53ea\u7eff\u8272\u7389\u74f6\uff0c\u4ea4\u4e88\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u8fd9\u74f6\u4e39\u836f\uff0e\u53ef\u89e3 \u2018\u8840\u96fe\u6563\u2019\u4e4b\u6bd2\uff0c\u5269\u4e0b\u7684\u5c31\u7531\u4f60\u4eec\u7559\u4e0b\u5907\u7528\u3002\u201d \u3000\u3000\u949f\u4e00\u8c6a\u5148\u5582\u738b\u5927\u5eb7\u670d\u4e0b\u4e00\u6599\uff0c\u5c06\u7389\u74f6\u63e3\u5165\u6000\u4e2d\uff0c\u53c8\u5411\u5e9e\u58eb\u51b2\u81f4\u8c22\u3002 \u3000\u3000\u539f\u6765\u5148\u524d\u7fa4\u8c6a\u4e2d\u53ea\u6709\u738b\u5927\u5eb7\u4e00\u4eba\u4e2d\u6bd2\uff0c\u81ea\u6587\u5929\u751f\u8fce\u6218\u9634\u5929\u7433\u540e\uff0c\u90a3\u201c\u8840\u96fe\u6563\u201d\u4fbf\u5931\u53bb\u6548 \u529b\u3002 \u3000\u3000\u8fd9\u662f\u56e0\u4e3a\u82d7\u7d20\u82d3\u8eab\u8fb9\u85cf\u6709\u4e00\u74f6\u76d7\u81ea\u201c\u4e5d\u5929\u7384\u9634\u6d1e\u5e9c\u201d\u7684\u7075\u836f\uff0c\u5979\u5077\u5077\u4ea4\u7ed9\u6587\u5929\u751f\u548c\u9ea6\u5c0f \u660e\u5404\u4e00\u7c92\uff0c\u56e0\u4e4b\u4e24\u4eba\u4fbf\u4e0d\u518d\u4e3a\u201c\u8840\u96fe\u6563\u201d\u6240\u4fb5\u3002 \u3000\u3000\u738b\u5927\u5eb7\u5f88\u5feb\u4fbf\u80a4\u8272\u6062\u590d\uff0e\u4eba\u4e5f\u5b8c\u5168\u6e05\u9192\u8fc7\u6765\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u8fd9\u662f\u5e9e\u8001\u524d\u8f88\u6551\u4e86\u738b\u5144\u7684\uff0c\u738b\u5144\u8fd8\u4e0d\u5feb\u5feb\u81f4\u8c22\uff01\u201d \u3000\u3000\u738b\u5927\u5eb7\u8fde\u5fd9\u8d77\u8eab\u5411\u5e9e\u58eb\u51b2\u62dc\u8c22\u3002 \u3000\u3000\u4ed6\u867d\u56e0\u6570\u6708\u524d\u672a\u968f\u8c37\u5bd2\u9999\u5230\u8fc7\u5929\u53f0\u4e07\u82b1\u5bab\uff0c\u4ee5\u81f4\u672a\u89c1\u8fc7\u5e9e\u58eb\u51b2\uff0c\u4f46\u56e0\u9ea6\u5c0f\u660e\u7ecf\u5e38\u63d0\u8d77\uff0c \u56e0\u4e4b\u5bf9\u8fd9\u4f4d\u6551\u547d\u6069\u4eba\u65e9\u5c31\u542c\u8bf4\u8fc7\u3002 \u3000\u3000\u5e9e\u58eb\u51b2\u5c06\u8336\u559d\u5b8c\uff0c\u8d77\u8eab\u9053\uff1a\u201c\u8001\u592b\u8be5\u8d70\u4e86\uff01\u201d \u3000\u3000\u949f\u4e00\u8c6a\u8fde\u5fd9\u62e6\u4f4f\u9053\uff1a\u201c\u8001\u524d\u8f88\u4e0d\u80fd\u8d70\uff0c\u665a\u8f88\u8fd8\u6709\u91cd\u8981\u5927\u4e8b\u5949\u544a\u3002\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u6123\u4e86\u4e00\u6123\uff0c\u53ea\u5f97\u53c8\u5750\u4e86\u4e0b\u6765\u3002 \u5367\u9f99\u751f\u300a\u5999\u7edd\u5929\u9999\u300b \u7b2c\u4e8c\u5341\u4e09\u56de\u3000\u7ea2\u7c89\u77e5\u5df1 \u3000\u3000\u8fd9\u65f6\uff0c\u7fa4\u8c6a\u65e9\u53c8\u628a\u5e9e\u58eb\u51b2\u56f4\u62e2\u8d77\u6765\u3002 \u3000\u3000\u5e9e\u58eb\u51b2\u9053\uff1a\u201c\u6709\u4ec0\u4e48\u91cd\u8981\u5927\u4e8b\uff1f\u4f60\u4eec\u8bf4\u5427\uff01\u201d \u3000\u3000\u949f\u4e00\u8c6a\u6b63\u8272\u9053\uff1a\u201c\u8001\u524d\u8f88\u53ef\u76f8\u4fe1\u4eba\u6b7b\u6709\u590d\u6d3b\u7684\u9053\u7406\uff1f\u201d \u3000\u3000\u8fd9\u53e5\u6ca1\u5934\u6ca1\u8111\u7684\u8bdd\uff0c\u5e9e\u58eb\u51b2\u88ab\u95ee\u5f97\u5927\u611f\u832b\u7136\uff0c\u4e24\u9053\u767d\u7709\u4e00\u76b1\u9053\uff1a\u201c\u4f60\u53ef\u662f\u78b0\u4e0a\u4e86\u4ec0\u4e48\u602a \u4e8b\uff1f\u201d \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u8001\u524d\u8f88\u8bf7\u5148\u7b54\u665a\u8f88\u63d0\u51fa\u7684\u95ee\u9898\uff0c\u4e16\u4e0a\u7a76\u7adf\u6709\u6ca1\u6709\u8d77\u6b7b\u56de\u751f\u4e4b\u672f\uff1f\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u542c\u4f60\u8fd9\u53e3\u6c14\uff0c\u7eb5\u7136\u6ca1\u4e2d\u90aa\uff0c\u4e5f\u6709\u70b9\u4e0d\u6b63\u5e38\u3002\u8c01\u90fd\u77e5\u9053\u4eba\u6b7b\u4e0d\u80fd\u590d \u6d3b\uff0c\u6240\u8c13\u8d77\u6b7b\u56de\u751f\uff0c\u90a3\u6839\u672c\u5c31\u662f\u9a97\u4eba\u7684\u8bdd\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u665a\u8f88\u4eec\u660e\u660e\u770b\u5230\u4e00\u4e2a\u4eba\u662f\u6b7b\u800c\u590d\u751f\u7684\u3002\u201d \u3000\u3000\u201c\u80e1\u8bf4\uff01\u8001\u592b\u770b\u4f60\u4eec\u662f\u6d3b\u89c1\u9b3c\u3002\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u6240\u6709\u7684\u4eba\u51e0\u4e4e\u90fd\u4e0d\u7ea6\u800c\u540c\u9f50\u58f0\u9053\uff1a\u201c\u8001\u524d\u8f88\uff0c\u949f\u526f\u76df\u4e3b\u4e0d\u662f\u80e1\u8bf4\uff0c\u4e0d\u4f46\u4ed6\u770b\u5230\u4e86\uff0c \u6211\u4eec\u4e5f\u770b\u5230\u4e86\uff01\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u4e0d\u52a8\u58f0\u8272\u9053\uff1a\u201c\u4f60\u4eec\u7a76\u7adf\u770b\u5230\u4e86\u8c01\uff1f\u201d \u3000\u3000\u7fa4\u8c6a\u53c8\u9f50\u58f0\u9053\uff1a\u201c\u6211\u4eec\u770b\u5230\u4e86\u80e1\u524d\u76df\u4e3b\uff01\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u6709\u8fd9\u79cd\u4e8b\uff0c\u4f60\u4eec\u662f\u5426\u56e0\u4e3a\u9694\u5f97\u5f88\u8fdc\uff0c\u770b\u5230\u4e00\u4eba\u5f88\u50cf\u80e1\u67cf\u9f84\uff1f\u201d \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u4e0d\uff01\u771f\u662f\u80e1\u76df\u4e3b\uff0c\u665a\u8f88\u8fd8\u548c\u4ed6\u8bb2\u8fc7\u4e0d\u5c11\u8bdd\uff01\u201d \u3000\u3000\u201c\u8bb2\u8fc7\u8bdd\u4e5f\u4e0d\u4e00\u5b9a\u80fd\u65ad\u5b9a\u662f\u4ed6\u3002\u201d \u3000\u3000\u201c\u4ed6\u786e\u5b9e\u662f\u80e1\u76df\u4e3b\uff0e\u4e0d\u8bba\u76f8\u8c8c\u548c\u58f0\u97f3\uff0c\u5b8c\u5168\u4e0d\u5dee\u3002\u63d0\u8d77\u4ece\u524d\u7684\u4e8b\uff0c\u4e0d\u8bba\u516c\u4e8b\u79c1\u4e8b\uff0c\u4ed6\u90fd \u4e86\u82e5\u6307\u638c\uff0c\u600e\u4f1a\u9519\u5f97\u4e86\uff1f\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u8138\u8272\u5fae\u53d8\uff0c\u7565\u4e00\u6c89\u541f\u9053\uff1a\u201c\u4f60\u4eec\u4e3a\u4ec0\u4e48\u4e0d\u7559\u4f4f\u4ed6\uff1f\u6709\u4ed6\u5728\uff0c\u53c8\u4f55\u6101\u970d\u5143\u4f3d\u4e0d\u8ba9 \u4f4d\uff1f\u201d \u3000\u3000\u949f\u4e00\u8c6a\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u504f\u504f\u6709\u4f4d\u8001\u524d\u8f88\u4ece\u4e2d\u4f5c\u6897\u3002\u201d \u3000\u3000\u201c\u539f\u6765\u8fd8\u53e6\u5916\u6709\u4eba\u548c\u4ed6\u5728\u4e00\u8d77\uff0c\u90a3\u8001\u5934\u513f\u600e\u80fd\u62e6\u5f97\u4f4f\u4ed6\uff1f\u201d \u3000\u3000\u201c\u90a3\u4f4d\u8001\u524d\u8f88\u5c31\u662f\u628a\u80e1\u524d\u76df\u4e3b\u6551\u6d3b\u7684\u4eba\uff0c\u800c\u4e14\u505a\u4e86\u80e1\u76df\u4e3b\u7684\u5e08\u7236\uff0c\u4ed6\u4e0d\u51c6\u80e1\u76df\u4e3b\u518d\u56de\u8ff7 \u8e2a\u8c37\uff0c\u80e1\u76df\u4e3b\u600e\u80fd\u4e0d\u542c\u4ed6\u7684\uff1f\u201d \u3000\u3000\u201c\u90a3\u8001\u5934\u513f\u4ec0\u4e48\u6837\u5b50\uff1f\u201d \u3000\u3000\u949f\u2014\u8c6a\u968f\u5373\u628a\u767d\u53d1\u767d\u9aef\u8001\u8005\u7684\u5f62\u76f8\u5f62\u5bb9\u4e86\u4e00\u904d\uff0c\u7fa4\u8c6a\u5e76\u6709\u4e0d\u5c11\u4eba\u5728\u65c1\u52a0\u4ee5\u8865\u5145\u3002 \u3000\u3000\u5e9e\u58eb\u51b2\u95fb\u8a00\u8138\u8272\u5927\u53d8\u9053\uff1a\u3000\u201c\u83ab\u975e\u8fd9\u4eba\u662f\u4f20\u8bf4\u4e2d\u7684\u56db\u7edd\u8001\u4eba\uff1f\u2026\u2026\u201d \u3000\u3000\u7fa4\u8c6a\u4e5f\u4f3c\u662f\u88ab\u5e9e\u58eb\u51b2\u7684\u795e\u8272\u611f\u67d3\uff0c\u6014\u4e86\u534a\u664c\uff0c\u624d\u542c\u949f\u4e00\u8c6a\u95ee\u9053\uff1a\u201c\u56db\u7edd\u8001\u4eba\u2026\u2026\u83ab\u975e\u5e9e \u524d\u8f88\u77e5\u9053\u8fd9\u4f4d\u7edd\u4e16\u9ad8\u4eba\u7684\u8eab\u4efd\u6765\u5386\uff1f\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u957f\u957f\u5401\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u8001\u592b\u4e5f\u662f\u7531\u4f20\u95fb\u4e2d\u542c\u6765\u7684\u3002\u63d0\u8d77\u8fd9\u4f4d\u524d\u8f88\u9ad8\u4eba\uff0c\u8bba\u5e74\u7eaa\u65e9\u5728 \u767e\u9f84\u4ee5\u4e0a\uff0c\u5f53\u8001\u592b\u51fa\u9053\u65f6\uff0c\u4ed6\u65e9\u5df2\u9041\u8ff9\u5c71\u6797\u3002\u4f55\u51b5\u8001\u592b\u65e9\u5e74\u751a\u5c11\u79bb\u5f00\u957f\u767d\u5c71\uff0c\u96be\u5f97\u6765\u4e2d\u539f\u4e00 \u8d9f\uff0c\u7eb5\u7136\u56db\u7edd\u8001\u4eba\u5c1a\u672a\u5f52\u9690\uff0c\u4e5f\u4e0d\u6613\u89c1\u5230\u4ed6\u3002\u201d \u3000\u3000\u201c\u5e9e\u524d\u8f88\u600e\u77e5\u8fd9\u4f4d\u767e\u9f84\u4ee5\u4e0a\u7684\u7edd\u4e16\u9ad8\u4eba\u6d3b\u5728\u4e16\u4e0a\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u6b66\u6797\u4e2d\u4ece\u65e0\u4eba\u4f20\u8fc7\u4ed6\u7684\u6b7b\u8baf\u3002\u201d \u3000\u3000\u201c\u90a3\u4e48\u5e9e\u524d\u8f88\u53c8\u600e\u4f1a\u8ba4\u4e3a\u6551\u6d3b\u80e1\u76df\u4e3b\u7684\u90a3\u4f4d\u524d\u8f88\u5c31\u662f\u56db\u7edd\u8001\u4eba\uff1f\u201d \u3000\u3000\u201c\u9664\u4e86\u4ed6\u6ca1\u4eba\u6709\u8fd9\u79cd\u80fd\u8010\u3002\u201d \u3000\u3000\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u5e9e\u524d\u8f88\u5c82\u4e0d\u53c8\u627f\u8ba4\u4e86\u4eba\u6b7b\u53ef\u4ee5\u590d\u6d3b\u5417\uff1f\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u6447\u6447\u5934\u9053\uff1a\u201c\u80e1\u67cf\u9f84\u80fd\u6b7b\u800c\u590d\u6d3b\uff01\u5f53\u521d\u4ed6\u5fc5\u5b9a\u4e0d\u662f\u771f\u6b7b\uff0c\u81f3\u5c11\u4f53\u5185\u8fd8\u4fdd\u6709\u4e00\u4e9b\u4e39 \u7530\u771f\u6c14\uff0c\u6240\u4ee5\u624d\u80fd\u88ab\u4eba\u6551\u6d3b\u3002 \u3000\u3000\u5982\u679c\u786e\u5df2\u771f\u6b63\u6b7b\u53bb\uff0c\u5373\u4f7f\u5927\u7f57\u795e\u4ed9\u4e5f\u96be\u4ee5\u8d77\u6b7b\u56de\u751f\u3002\u7eb5\u7136\u5982\u6b64\uff0c\u90a3\u6551\u8d77\u80e1\u67cf\u9f84\u4e4b\u4eba\uff0c\u5728 \u5f53\u4eca\u4e4b\u4e16\uff0c\u9664\u4e86\u56db\u7edd\u8001\u4eba\uff0c\u53ea\u6015\u8fd8\u627e\u4e0d\u51fa\u7b2c\u4e8c\u4f4d\u3002\u201d \u3000\u3000\u7fa4\u8c6a\u90fd\u542c\u5f97\u5341\u5206\u5165\u795e\uff0c\u5927\u5385\u91cc\u6c89\u5bc2\u4e86\u751a\u4e45\uff0e\u624d\u542c\u9ea6\u5c0f\u660e\u95ee\u9053\uff1a\u201c\u6709\u5173\u56db\u7edd\u8001\u4eba\u7684\u5f80\u4e8b\uff0c \u5e9e\u8001\u4f2f\u662f\u5426\u53ef\u4ee5\u544a\u8bc9\u665a\u8f88\u4eec\u2014\u4e9b\uff1f\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u60f3\u4e86\u60f3\u9053\uff1a\u201c\u56db\u7edd\u8001\u4eba\u7684\u5f80\u4e8b\uff0c\u8001\u592b\u77e5\u9053\u7684\u4e0d\u591a\uff0e\u53ea\u542c\u8bf4\u4ed6\u540c\u95e8\u5e08\u5144\u59b9\u5171\u6709\u4e09\u4eba\uff0c \u4e09\u4eba\u7684\u6b66\u529f\uff0c\u90fd\u662f\u60ca\u4e16\u9a87\u4fd7\uff0c\u9ad8\u4e0d\u53ef\u6d4b\uff0c\u3000\u4e00\u76f4\u9886\u8896\u4e2d\u539f\u6b66\u6797\u8fbe\u56db\u4e94\u5341\u5e74\u4e4b\u4e45\u3002\u5947\u602a\u7684\u662f\uff0c \u4ed6\u4eec\u7adf\u7136\u540c\u65f6\u5f52\u9690\uff0c\u540c\u65f6\u5728\u6b66\u6797\u4e2d\u9500\u58f0\u533f\u8ff9\uff0e\u81f3\u4eca\u8fd1\u767e\u4f59\u5e74\u6765\uff0e\u4e5f\u4ece\u672a\u4f20\u51fa\u4ed6\u4eec\u7684\u6b7b\u8baf\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4ed6\u4eec\u540c\u95e8\u5e08\u5144\u59b9\u4e09\u4eba\uff0c\u9664\u4e86\u56db\u7edd\u8001\u4eba\uff0e\u53e6\u5916\u4e24\u4f4d\u662f\u8c01\uff1f\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u9053\uff1a\u201c\u636e\u8bf4\u56db\u7edd\u8001\u4eba\u662f\u5927\u5e08\u5144\uff0c\u53e6\u4e8c\u4eba\u2014\u4f4d\u662f\u4ed6\u7684\u4e8c\u5e08\u5f1f\u4e09\u5999\u4e66\u751f\uff0e\u3000\u4e00\u662f\u4ed6\u7684 \u4e09\u5e08\u59b9\u5999\u5999\u4ed9\u5b50\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u4e0d\u7531\u5fc3\u5934\u7830\u7136\u4e00\u52a8\uff0c\u6697\u9053\uff1b\u201c\u83ab\u975e\u5999\u5999\u5eb5\u7684\u90a3\u4f4d\u8001\u5e08\u592a\u5c31\u662f\u5999\u5999\u4ed9\u5b50\uff1f\u770b\u6765\u5927\u6709 \u53ef\u80fd\u3002\u201d \u3000\u3000\u4ed6\u56e0\u60f3\u8d77\u90a3\u4f4d\u8001\u5e08\u592a\u66fe\u53ee\u5631\u8fc7\u4e0d\u53ef\u6cc4\u9732\u5999\u5999\u5eb5\u4e4b\u4e8b\uff0c\u56e0\u4e4b\u4e5f\u5c31\u4e0d\u4fbf\u8bb2\u51fa\u3002 \u3000\u3000\u53ea\u542c\u5e9e\u58eb\u51b2\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u542c\u8bf4\u4ed6\u4eec\u5e08\u5144\u59b9\u4e09\u4eba\u4e4b\u95f4\uff0c\u66fe\u6709\u4e00\u6bb5\u7f20\u7ef5\u60b1\u607b\u7684\u60c5\u7231\u6545\u4e8b\uff0c\u6700 \u540e\u843d\u5f97\u6709\u60c5\u4eba\u96be\u6210\u7737\u5c5e\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u65e2\u7136\u4ed6\u4eec\u5e08\u5144\u59b9\u4e4b\u95f4\u6709\u6bb5\u611f\u4eba\u7684\u60c5\u7231\u6545\u4e8b\uff0c\u5e9e\u8001\u4f2f\u4f55\u4e0d\u8bf4\u51fa\u6765\u7ed9\u5927 \u5bb6\u542c\u542c\uff01\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u6447\u5934\u9053\uff1a\u201c\u8fd9\u4e0d\u8fc7\u662f\u4e00\u79cd\u4f20\u95fb\uff0c\u8001\u592b\u4e5f\u4e0d\u8fc7\u662f\u4eba\u4e91\u4ea6\u4e91\u800c\u5df2\uff0e\u82e5\u5bf9\u4f60\u4eec\u8bb2\u5f97\u4e0d\u5bf9\uff0c \u672a\u514d\u6709\u8bec\u524d\u8d24\uff0e\u53cd\u800c\u662f\u79cd\u7f6a\u8fc7\uff0c\u6240\u4ee5\u8fd8\u662f\u4e0d\u591a\u8bb2\u7684\u597d\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u867d\u4e0d\u80fd\u518d\u95ee\uff0c\u5374\u4e00\u76f4\u5728\u4e3a\u8fd9\u4ef6\u4e8b\u5fc3\u5ff5\u8f6c\u52a8\u4e0d\u5df2\u3002 \u3000\u3000\u5e9e\u58eb\u51b2\u672c\u6765\u8bb2\u5b8c\u8bdd\u5c31\u8981\u8d70\uff0c\u5728\u949f\u4e00\u8c6a\u7b49\u4eba\u7684\u82e6\u82e6\u633d\u7559\u4e0b\uff0c\u53ea\u597d\u5728\u4e07\u6708\u5821\u5403\u4e86\u5348\u996d\u518d\u8d70\uff0e \u3000\u3000\u4e2d\u5348\uff0c\u674e\u8302\u7279\u522b\u51c6\u5907\u4e86\u4e30\u76db\u7684\u9152\u5e2d\uff0c\u6b3e\u5f85\u5e9e\u58eb\u51b2\u3002 \u3000\u3000\u9152\u5bb4\u5c31\u8bbe\u5728\u5927\u5385\uff0e\u949f\u4e00\u8c6a\u7b49\u5341\u4f59\u4eba\u5168\u5728\u5ea7\uff0c\u674e\u8302\u4e5f\u4ee5\u5730\u4e3b\u8eab\u4efd\u53c2\u52a0\u3002 \u3000\u3000\u9152\u8fc7\u4e09\u5de1\uff0c\u949f\u4e00\u8c6a\u957f\u957f\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u665a\u8f88\u4eec\u672c\u60f3\u7531\u8ff7\u8e2a\u8c37\u64a4\u5230\u4e07\u6708\u5821\u518d\u4f5c\u6253\u7b97\uff0c\u770b\u6765 \u8fde\u4e07\u6708\u5821\u4e5f\u4e0d\u80fd\u518d\u4f4f\u4e0b\u53bb\u4e86\uff01\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u9053\uff1a\u201c\u949f\u8001\u5f1f\u4f55\u51fa\u6b64\u8a00\uff1f\u201d \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u8001\u524d\u8f88\u5df2\u7ecf\u4eb2\u773c\u770b\u5230\u4e86\uff0c\u665a\u8f88\u4eec\u4eba\u5355\u52bf\u8584\uff0c\u548c\u970d\u5143\u4f3d\u76f8\u6bd4\uff0c\u5b9e\u529b\u76f8\u5dee\u60ac\u6b8a\u3002 \u4eca\u5929\u82e5\u4e0d\u662f\u8001\u524d\u8f88\u53ca\u65f6\u8d76\u5230\u4ed7\u4e49\u76f8\u52a9\uff0c\u540e\u679c\u5982\u4f55\uff0c\u4e0d\u96be\u9884\u6599\u3002\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u9ed8\u4e86\u4e00\u9ed8\u9053\uff1a\u201c\u4f60\u8ba4\u4e3a\u970d\u5143\u4f3d\u4eca\u540e\u8fd8\u4f1a\u518d\u6765\uff1f\u201d \u3000\u3000\u949f\u2014\u8c6a\u82e6\u7b11\u9053\uff1a\u201c\u8fd9\u662f\u4e00\u5b9a\u7684\uff0c\u82e5\u4ed6\u4e0b\u6b21\u518d\u7387\u4f17\u524d\u6765\uff0c\u600e\u53ef\u80fd\u518d\u6709\u50cf\u8001\u524d\u8f88\u8fd9\u6837\u7684\u7edd\u4e16 \u9ad8\u4eba\u4ed7\u4e49\u76f8\u52a9\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u62a2\u7740\u9053\uff1a\u201c\u90a3\u5c31\u8bf7\u5e9e\u8001\u4f2f\u4e5f\u7559\u5728\u4e07\u6708\u5821\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u6447\u5934\u7b11\u9053\uff1a\u201c\u8001\u592b\u82e5\u80fd\u7559\u5728\u4e07\u6708\u5821\uff0e\u4f55\u4e0d\u7559\u5728\u957f\u767d\u5c71\uff1f\u4e0d\u8fc7\u8001\u592b\u867d\u65e0\u6cd5\u7559\u5728\u4e07\u6708 \u5821\uff0c\u5374\u4e0d\u5e0c\u671b\u4f60\u4eec\u79bb\u5f00\u4e07\u6708\u5821\uff0e\u201d \u3000\u3000\u949f\u4e00\u8c6a\u63a5\u9053\uff1a\u201c\u8001\u524d\u8f88\u4e3a\u4ec0\u4e48\u6709\u8fd9\u79cd\u60f3\u6cd5\uff1f\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u9053\uff1a\u201c\u8001\u592b\u95ee\u95ee\u4f60\u4eec\uff0c\u79bb\u5f00\u4e07\u6708\u5821\uff0c\u51c6\u5907\u5230\u54ea\u91cc\u53bb\uff1f\u53c8\u51c6\u5907\u505a\u4f55\u6253\u7b97\uff1f\u201d \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u6b64\u523b\u8fd8\u6ca1\u6709\u4e00\u5b9a\uff0c\u82e5\u5b9e\u5728\u6df7\u4e0d\u4e0b\u53bb\uff0c\u665a\u8f88\u4eec\u53ea\u597d\u5c31\u6b64\u89e3\u6563\uff0c\u5404\u5954\u524d\u7a0b\u4e86\uff01\u201d \u3000\u3000\u53ea\u542c\u738b\u5927\u5eb7\u53eb\u9053\uff1a\u201c\u949f\u526f\u76df\u4e3b\u522b\u8bb2\u8fd9\u79cd\u4e27\u6c14\u8bdd\uff0c\u82e5\u5b9e\u5728\u627e\u4e0d\u5230\u53bb\u5904\uff0c\u4e0d\u5982\u5927\u4f19\u4eec\u5230\u5d02\u5c71 \u53bb\uff0c\u54b1\u4eec\u4ee5\u5d02\u5c71\u505a\u6839\u636e\u5730\uff0c\u6162\u6162\u518d\u58ee\u5927\u529b\u91cf\uff0e\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u6447\u5934\u9053\uff1a\u201c\u8fd9\u4f4d\u8001\u5f1f\u7684\u60f3\u6cd5\uff0c\u8001\u592b\u4e0d\u540c\u610f\uff01\u201d \u3000\u3000\u738b\u5927\u5eb7\u9053\uff1a\u201c\u8001\u524d\u8f88\u4e3a\u4ec0\u4e48\u4e0d\u540c\u610f\uff1f\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u9053\uff1a\u201c\u8bf8\u4f4d\u88ab\u8feb\u60f3\u6682\u65f6\u79bb\u5f00\u4e07\u6708\u5821\uff0c\u8001\u592b\u4e0d\u4fbf\u7f6e\u5599\uff0c\u4f46\u5374\u4e0d\u5e0c\u671b\u4f60\u4eec\u79bb\u5f00\u8ff7\u8e2a\u8c37 \u592a\u8fdc\u3002\u201d \u3000\u3000\u949f\u4e00\u8c6a\u53c8\u63a5\u53e3\u9053\uff1a\u201c\u8001\u524d\u8f88\u53ef\u662f\u6709\u4ec0\u4e48\u7528\u610f\uff1f\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u9053\uff1a\u201c\u60f3\u90a3\u8ff7\u8e2a\u8c37\uff0e\u4e43\u662f\u80e1\u67cf\u9f84\u548c\u8001\u592b\u90a3\u5e72\u5973\u513f\u2014\u624b\u5efa\u7acb\uff0e\u600e\u53ef\u7531\u970d\u5143\u4f3d\u767d\u767d\u5360 \u636e\uff1f\u4f60\u4eec\u53ea\u6709\u4ecd\u9a7b\u624e\u5728\u8ff7\u8e2a\u8c37\u9644\u8fd1\uff0e\u624d\u6709\u5e0c\u671b\u4ecd\u628a\u8ff7\u8e2a\u8c37\u6536\u56de\uff0c\u800c\u4e14\u6709\u4f60\u4eec\u5728\uff0c\u4ecd\u53ef\u7275\u5236\u970d \u5143\u4f3d\u4e00\u90e8\u5206\u529b\u91cf\uff0c\u4f7f\u4ed6\u4e0d\u6562\u8086\u65e0\u5fcc\u60ee\u7684\u4e3a\u975e\u4f5c\u6b79\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u53c8\u63d2\u8a00\u9053\uff1a\u201c\u83ab\u975e\u8c37\u524d\u76df\u4e3b\u771f\u662f\u5e9e\u8001\u4f2f\u7684\u5e72\u5973\u513f\uff1f\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u9053\uff1a\u201c\u8c37\u5bd2\u9999\u662f\u8001\u592b\u5916\u5b59\u7684\u517b\u6bcd\uff0c\u5f53\u7136\u5c31\u662f\u8001\u592b\u7684\u5e72\u5973\u513f\uff01\u201d \u3000\u3000\u201c\u53ef\u662f\u8c37\u524d\u76df\u4e3b\u751f\u524d\uff0c\u5e76\u6ca1\u63d0\u8d77\u8fc7\u8fd9\u4ef6\u4e8b\uff01\u201d \u3000\u3000\u201c\u5979\u4f55\u5fc5\u5bf9\u4f60\u4eec\u63d0\uff0c\u5982\u679c\u4e0d\u662f\u6709\u8fd9\u5c42\u5173\u7cfb\uff0c\u5979\u4e3a\u4ec0\u4e48\u5728\u5929\u53f0\u4e07\u82b1\u5bab\u4e34\u7ec8\u524d\u628a\u5979\u7684\u4e49\u5b50\u7fce \u513f\u4ea4\u7ed9\u8001\u592b\uff1f\u4e3a\u4ec0\u4e48\u53c8\u8981\u8001\u592b\u628a\u5979\u7684\u9057\u4f53\u5e26\u8d70\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u548c\u7fa4\u8c6a\u660e\u77e5\u5e9e\u58eb\u51b2\u7684\u8bdd\u6709\u4e9b\u5f3a\u8bcd\u593a\u7406\uff0c\u4f46\u4e3a\u4e86\u5bf9\u4ed6\u8868\u793a\u5c0a\u656c\uff0c\u5374\u5e76\u65e0\u4eba\u63d0\u51fa\u7591\u95ee\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u5c94\u5f00\u8bdd\u9898\uff0e\u95ee\u9053\uff1a\u201c\u8fd9\u51e0\u6708\u8001\u524d\u8f88\u884c\u8e2a\u65e0\u5b9a\uff0c\u4e0d\u77e5\u7fce\u513f\u4eba\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u9053\uff1a\u201c\u8001\u592b\u5df2\u628a\u7fce\u513f\u5bc4\u6258\u5728\u2014\u4f4d\u53cb\u4eba\u5904\uff0c\u90a3\u4f4d\u53cb\u4eba\uff0c\u4e0d\u4f46\u6b66\u529f\u4e0d\u6bd4\u8001\u592b\u5dee\uff0c\u8bba\u5b66 \u95ee\u66f4\u662f\u624d\u9ad8\u516b\u6597\u3002\u7fce\u513f\u7531\u4ed6\u8c03\u6559\uff0c\u5c06\u6765\u5fc5\u5b9a\u6bd4\u4ed6\u8001\u5b50\u66f4\u6709\u51fa\u606f\uff0c\u8fd9\u65b9\u9762\u4f60\u4eec\u5c3d\u7ba1\u653e\u5fc3\u3002\u201d \u3000\u3000\u9152\u5bb4\u8fc7\u540e\uff0c\u5e9e\u58eb\u51b2\u4fbf\u8d77\u8eab\u544a\u8f9e\u3002 \u3000\u3000\u949f\u2014\u8c6a\u3001\u4f59\u4ea6\u4e50\u3001\u9ea6\u5c0f\u660e\u3001\u82d7\u7d20\u82d3\u4ee5\u53ca\u674e\u8302\uff0c\u4eb2\u81ea\u9001\u5230\u5ce1\u53e3\uff0e \u3000\u3000\u4e34\u522b\u65f6\uff0c\u82d7\u7d20\u82d3\u4f9d\u4f9d\u4e0d\u820d\u7684\u9053\uff1a\u201c\u4e0d\u77e5\u54ea\u4e00\u5929\u8fd8\u80fd\u548c\u5e08\u7236\u89c1\u9762\uff1f\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u7b11\u9053\uff1a\u201c\u4eba\u751f\u4f55\u5904\u4e0d\u76f8\u9022\uff0c\u5c24\u5176\u4f60\u4eec\u8fd9\u4e9b\u4eba\u73b0\u5728\u662f\u843d\u96be\u65f6\u671f\uff0c\u8001\u592b\u5f53\u7136\u4e5f\u6302\u5ff5\u7740 \u4f60\u4eec\uff0c\u4e5f\u8bb8\u968f\u65f6\u90fd\u4f1a\u6765\u770b\u671b\u770b\u671b\uff01\u201d \u3000\u3000\u9001\u8d70\u5e9e\u58eb\u51b2\uff0c\u56de\u5230\u5927\u5385\uff0c\u867d\u7136\u6b8b\u7fb9\u5269\u996d\u65e9\u5df2\u64a4\u53bb\uff0c\u4f46\u7fa4\u8c6a\u4eec\u5374\u4ecd\u5728\u90a3\u91cc\u7b49\u4faf\uff0c\u5e76\u65e0\u4e00\u4eba \u6563\u53bb\uff0e \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u6b63\u597d\u5927\u5bb6\u90fd\u5728\u8fd9\u91cc\uff0c\u54b1\u4eec\u5c31\u8d81\u8fd9\u673a\u4f1a\u5546\u8bae\u5546\u8bae\u4e0b\u4e00\u6b65\u7684\u884c\u52a8\u3002\u201d \u3000\u3000\u4e8e\u662f\u4f17\u4eba\u53c8\u90fd\u5750\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u738b\u5927\u5eb7\u9053\uff1a\u201c\u4ffa\u731c\u2014\u5b9a\u662f\u5546\u8bae\u79bb\u5f00\u4e07\u6708\u5ce1\u540e\u8be5\u5230\u54ea\u91cc\u53bb\uff0c\u65e2\u7136\u5927\u5bb6\u4e0d\u80fd\u5230\u5d02\u5c71\uff0c\u90a3\u5c31\u4f9d \u5e9e\u8001\u524d\u8f88\u7684\u8bdd\uff0c\u5728\u8ddd\u8ff7\u8e2a\u8c37\u4e0d\u8fdc\u7684\u5730\u65b9\u627e\u4e2a\u5b89\u7f6e\u4e4b\u5904\u3002\u201d \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u738b\u5144\u8ba4\u4e3a\u5230\u54ea\u91cc\u53bb\u8f83\u597d\uff1f\u201d \u3000\u3000\u738b\u5927\u5eb7\u6478\u6478\u8111\u888b\uff0c\u5c34\u5c2c\u7b11\u9053\uff1a\u201c\u4ffa\u4eec\u662f\u4ece\u5d02\u5c71\u6765\u7684\uff0c\u5bf9\u8fd9\u9644\u8fd1\u5e76\u4e0d\u719f\uff0c\u949f\u526f\u76df\u4e3b\u6700\u597d\u95ee \u95ee\u522b\u4eba\u3002\u201d \u3000\u3000\u949f\u4e00\u8c6a\u671b\u5411\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u9ea6\u5c0f\u5144\u5f1f\u8fd1\u51e0\u6708\u6765\uff0e\u8fde\u756a\u51fa\u8c37\u591a\u6b21\uff0c\u4e5f\u8bb8\u66fe\u770b\u5230\u5408\u9002\u7684\u5730\u65b9\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u7565\u4e00\u6c89\u541f\u9053\uff1a\u201c\u79bb\u8ff7\u8e2a\u8c37\u53e3\u5916\u5927\u7ea6\u5341\u51e0\u91cc\u8def\u7684\u5c71\u811a\u4e0b\uff0c\u6709\u5ea7\u5e99\u5b87\uff0c\u89c4\u6a21\u4e0d\u5c0f\uff0c\u4f46 \u56e0\u5e74\u4e45\u5931\u4fee\uff0c\u636e\u8bf4\u50e7\u4eba\u4e0d\u591a\uff0c\u54b1\u4eec\u662f\u5426\u53ef\u4ee5\u642c\u5230\u90a3\u91cc\u53bb\uff1f\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u5fd9\u9053\uff1a\u201c\u90a3\u5ea7\u5e99\u6211\u4e5f\u5230\u8fc7\uff0c\u540d\u53eb\u767d\u59d1\u5bfa\uff0c\u5bfa\u5185\u6709\u4e00\u5ea7\u767d\u59d1\u5854\u3002\u56e0\u4e3a\u5bfa\u5e99\u5468\u56f4\u662f\u4e00 \u7247\u4e1b\u6797\uff0c\u6240\u4ee5\u5730\u70b9\u5f88\u9690\u5bc6\uff0e\u5982\u679c\u4f4f\u5728\u90a3\u91cc\uff0c\u786e\u5b9e\u662f\u4e2a\u597d\u5730\u65b9\u3002\u201d \u3000\u3000\u949f\u4e00\u8c6a\u7acb\u5373\u4e0b\u4e86\u51b3\u5b9a\u9053\uff1a\u201c\u597d\uff01\u4e3a\u4e86\u9690\u5bc6\uff0c\u54b1\u4eec\u5165\u591c\u540e\u4fbf\u51fa\u53d1\u3002\u201d \u3000\u3000\u5c31\u5728\u5f53\u5929\u5929\u665a\uff0c\u7fa4\u8c6a\u4eec\u679c\u7136\u79bb\u5f00\u4e86\u4e07\u6708\u5ce1\u7684\u4e07\u6708\u5821\uff0e \u3000\u3000\u8fd9\u6b21\u4ed6\u4eec\u5e76\u6ca1\u5e26\u4e0a\u6b21\u90a3\u4e48\u591a\u4e1c\u897f\uff0c\u628a\u4e0a\u6b21\u5e26\u6765\u7684\u90a3\u4e9b\u4e1c\u897f\uff0c\u5927\u90e8\u5206\u7559\u5728\u4e07\u6708\u5821\uff0c\u7531\u674e\u8302 \u4ee3\u4e3a\u4fdd\u7ba1\uff0c\u4ed6\u4eec\u5f53\u7136\u5341\u5206\u653e\u5fc3\uff01 \u3000\u3000\u51fa\u4e86\u5ce1\u53e3\uff0c\u7531\u9ea6\u5c0f\u660e\u548c\u4f59\u4ea6\u4e50\u5e26\u8def\uff0c\u4e0d\u5230\u4e09\u66f4\uff0c\u4fbf\u5df2\u5230\u8fbe\u767d\u59d1\u5bfa\uff0e \u3000\u3000\u767d\u59d1\u5bfa\u7684\u4f4f\u6301\u50e7\u4eba\u6cd5\u540d\u609f\u5c18\uff0c\u5e74\u5728\u4e94\u65ec\u4e0a\u4e0b\u3002 \u3000\u3000\u8fd9\u91cc\u539f\u6709\u4e8c\u5341\u51e0\u540d\u50e7\u4eba\uff0c\u81ea\u4ece\u4e09\u5e74\u524d\u80e1\u67cf\u9f84\u5728\u8ff7\u8e2a\u8c37\u5efa\u7acb\u5929\u4e0b\u7eff\u6797\u603b\u5be8\u540e\uff0c\u56e0\u4e3a\u8fd9\u5ea7\u5bfa \u5e99\u79bb\u8ff7\u8e2a\u8c37\u8c37\u53e3\u53ea\u6709\u5341\u51e0\u91cc\u8def\uff0c\u5f53\u5730\u4ee5\u53ca\u9644\u8fd1\u5c45\u6c11\u4fbf\u591a\u6570\u4e0d\u6562\u518d\u5f80\u767d\u59d1\u5bfa\u70e7\u9999\u8fd8\u613f\uff0c\u56e0\u4e4b\uff0e \u5bfa\u91cc\u7684\u70df\u706b\u4fbf\u4e00\u8e76\u4e0d\u632f\u3002 \u3000\u3000\u518d\u52a0\u4e0a\u4e0d\u5c11\u50e7\u4eba\u56e0\u62c5\u5fc3\u88ab\u8ff7\u8e2a\u8c37\u7684\u7eff\u6797\u4eba\u7269\u52a0\u5bb3\uff0c\u4fbf\u7eb7\u7eb7\u6c42\u53bb\u3002\u5982\u4eca\uff0e\u767d\u59d1\u5bfa\u9664\u4f4f\u6301\u609f \u5c18\u5916\uff0c\u53ea\u5269\u4e0b\u4e09\u540d\u5f1f\u5b50\u3002 \u3000\u3000\u8fd9\u4e09\u4eba\u6cd5\u540d\u5206\u522b\u4e3a\u666e\u51e1\u3001\u666e\u6d4e\u3001\u666e\u6e21\uff0c\u662f\u4f4f\u6301\u609f\u5c18\u6559\u51fa\u6765\u7684\u5f1f\u5b50\uff0c\u56e0\u4e4b\uff0c\u624d\u4e0d\u5fcd\u79bb\u53bb\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u7b49\u5341\u4e00\u4eba\u5230\u8fbe\u65f6\uff0c\u666e\u6d4e\u548c\u666e\u6e21\u90fd\u7761\u89c9\u4e86\uff0c\u53ea\u6709\u4f4f\u6301\u609f\u5c18\u8fd8\u5e26\u7740\u5927\u5f1f\u5b50\u666e\u51e1\u5728\u4f5b\u6bbf \u8bf5\u7ecf\u3002 \u3000\u3000\u4ed6\u4eec\u4e00\u89c1\u6765\u4e86\u8fd9\u4e48\u591a\u52b2\u88c5\u75be\u670d\u4f69\u5200\u5e26\u5251\u7684\u4eba\u7269\uff0c\u90fd\u4e0d\u514d\u5403\u4e86\u2014\u60ca\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u8fde\u5fd9\u62b1\u62f3\u4e00\u793c\u9053\uff1a\u201c\u65b9\u4e08\u53d7\u60ca\u4e86\u3002\u6211\u4eec\u662f\u6253\u8ff7\u8e2a\u8c37\u6765\u7684\uff01\u201d \u3000\u3000\u5c82\u77e5\u4ed6\u4e0d\u8bf4\u662f\u4ece\u8ff7\u8e2a\u8c37\u6765\u7684\u8fd8\u597d\uff0c\u8fd9\u4e00\u8868\u660e\u8eab\u4efd\uff0c\u8d8a\u53d1\u628a\u609f\u5c18\u5e08\u5f92\u5413\u4e86\u4e00\u8df3\u3002 \u3000\u3000\u5f53\u4e0b\u609f\u5c18\u53cc\u624b\u5408\u5341\uff0c\u9ad8\u8bf5\u4e86\u4e00\u58f0\u4f5b\u53f7\u9053\uff1a\u201c\u5404\u4f4d\u82f1\u96c4\u65bd\u4e3b\uff0c\u655d\u5bfa\u4e00\u5411\u5e76\u672a\u60f9\u7740\u5404\u4f4d\uff0c\u5404 \u4f4d\u6df1\u66f4\u534a\u591c\u95ef\u8fdb\u655d\u5bfa\uff0c\u83ab\u975e\uff1f\u2026\u2026\u201d \u3000\u3000\u7fa4\u8c6a\u8fd9\u624d\u660e\u767d\u5fc5\u662f\u8ff7\u8e2a\u8c37\u4e00\u5411\u58f0\u540d\u4e0d\u4f73\uff0c\u7adf\u7136\u4f7f\u5f97\u8fde\u51fa\u5bb6\u4eba\u90fd\u6709\u4e9b\u60e7\u6015\u3002 \u3000\u3000\u5176\u5b9e\u4ed6\u4eec\u65e9\u8be5\u60f3\u5230\uff0c\u5728\u4e00\u822c\u4eba\u5fc3\u76ee\u4e2d\uff0c\u7eff\u6797\u4eba\u7269\u53ea\u662f\u4e9b\u5360\u5c71\u4e3a\u738b\u7684\u76d7\u532a\uff0c\u6839\u672c\u4e0d\u53ef\u80fd\u6709 \u597d\u4eba\u5728\u5185\uff0c\u5584\u826f\u767e\u59d3\u53c8\u6709\u8c01\u613f\u610f\u548c\u8fd9\u79cd\u4eba\u7269\u63a5\u8fd1\u5462\uff1f \u3000\u3000\u4f59\u4ea6\u4e50\u5411\u6765\u5f62\u76f8\u8f83\u4e3a\u65af\u6587\uff0c\u968f\u5373\u4e5f\u6df1\u6df1\u4e00\u793c\u9053\uff1a\u201c\u8d35\u65b9\u4e08\u53ef\u80fd\u8bef\u4f1a\u4e86\u6211\u4eec\uff0c\u6211\u4eec\u867d\u7136\u662f \u4ece\u8ff7\u8e2a\u8c37\u6765\u7684\uff0c\u4f46\u5374\u5e76\u975e\u574f\u4eba\u3002\u201d \u3000\u3000\u201c\u90a3\u4e48\u5404\u4f4d\u82f1\u96c4\u65bd\u4e3b\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u4eec\u662f\u60f3\u6682\u501f\u8d35\u5bfa\u4f4f\u4e0a\u51e0\u5929\uff0c\u7edd\u4e0d\u635f\u53ca\u8d35\u5bfa\u4e00\u8349\u4e00\u6728\u3002\u201d \u3000\u3000\u738b\u5927\u5eb7\u63a5\u7740\u9053\uff1a\u201c\u800c\u4e14\u65e5\u540e\u5fc5\u6709\u91cd\u8c22\uff0c\u5373\u4f7f\u8981\u6211\u4eec\u91cd\u4fee\u5e99\u5b87\uff0c\u4e5f\u65e0\u6240\u8c13\u3002\u201d \u3000\u3000\u609f\u5c18\u72b9\u8c6b\u4e86\u534a\u664c\uff0c\u53ea\u597d\u9894\u9996\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u8d2b\u50e7\u73b0\u5728\u5c31\u547d\u5f1f\u5b50\u6574\u7406\u623f\u820d\uff0c\u4ee5\u4fbf\u62db\u5f85\u5404 \u4f4d\u82f1\u96c4\u65bd\u4e3b\u4f4f\u5bbf\u3002\u201d \u3000\u3000\u8bf4\u7740\uff0c\u5c31\u8981\u5429\u5490\u666e\u51e1\u53bb\u53eb\u9192\u666e\u6d4e\u548c\u666e\u6e21\u5e2e\u5fd9\u6574\u7406\u623f\u95f4\u3002 \u3000\u3000\u949f\u2014\u8c6a\u9053\uff1a\u201c\u4e0d\u5fc5\u4e86\uff01\u73b0\u5728\u5df2\u662f\u4e09\u66f4\uff0c\u6211\u4eec\u5c31\u51d1\u5408\u7740\u5728\u5927\u6bbf\u4f11\u606f\u5230\u5929\u4eae\uff0c\u5929\u4eae\u540e\u518d\u81ea\u5df1 \u52a8\u624b\u6574\u7406\u3002\u201d \u3000\u3000\u8fd9\u4f7f\u609f\u5c18\u53cd\u800c\u6709\u4e9b\u8fc7\u610f\u4e0d\u53bb\u9053\uff1a\u201c\u90a3\u4e0d\u592a\u59d4\u5c48\u5404\u4f4d\u82f1\u96c4\u65bd\u4e3b\u4e86\uff1f\u201d \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u8fd9\u6837\u7684\u751f\u6d3b\uff0c\u6211\u4eec\u5df2\u7ecf\u8fc7\u60ef\u4e86\uff0c\u65b9\u4e08\u7528\u4e0d\u7740\u64cd\u5fc3\u3002\u201d \u3000\u3000\u4e8e\u662f\u7fa4\u8c6a\u4eec\u4fbf\u8fdb\u5165\u5927\u6bbf\u4f11\u606f\u3002 \u3000\u3000\u609f\u5c18\u6253\u53d1\u666e\u51e1\uff0c\u56de\u623f\u7761\u89c9\u540e\uff0c\u81ea\u5df1\u4e5f\u7559\u5728\u5927\u6bbf\u76f8\u966a\u3002 \u3000\u3000\u4ed6\u7684\u76ee\u7684\uff0c\u4e0d\u5916\u662f\u60f3\u63a2\u542c\u4e00\u4e9b\u8ff7\u8e2a\u8c37\u7684\u6d88\u606f\uff0c\u5728\u5916\u754c\u4eba\u7684\u773c\u4e2d\uff0c\u8ff7\u8e2a\u8c37\u65e0\u7591\u662f\u4e00\u5904\u9887\u4e3a \u795e\u79d8\u7684\u5730\u65b9\u3002 \u3000\u3000\u5176\u5b9e\uff0c\u7fa4\u8c6a\u867d\u7136\u8fdb\u5165\u5927\u6bbf\uff0c\u5374\u6ca1\u4e00\u4e2a\u60f3\u7761\u7684\u3002\u3000\u4e00\u6765\u5927\u6bbf\u4f9b\u7684\u662f\u795e\u660e\uff0c\u5728\u795e\u660e\u9762\u524d\u6a2a\u4e03 \u7ad6\u516b\u7684\u7761\u89c9\uff0c\u8c01\u90fd\u77e5\u9053\u662f\u4ef6\u4e0d\u793c\u8c8c\u7684\u4e8b\uff1b\u4e8c\u6765\u6709\u609f\u5c18\u966a\u7740\uff0c\u4ed6\u4eec\u7167\u6837\u4e5f\u662f\u60f3\u4e86\u89e3\u4e00\u4e0b\u767d\u59d1\u5bfa \u7684\u60c5\u5f62\u3002 \u3000\u3000\u53cc\u65b9\u8c08\u4e86\u4e00\u9635\uff0c\u609f\u5c18\u5df2\u6e10\u6e10\u660e\u767d\u8ff7\u8e2a\u8c37\u5185\u7adf\u662f\u5206\u4e3a\u4e24\u5927\u6d3e\u7cfb\uff0c\u4e5f\u77e5\u9053\u4e86\u949f\u2014\u8c6a\u7b49\u4eba\u662f\u56e0 \u4e3a\u4e0d\u80fd\u89c1\u5bb9\u4e8e\u970d\u5143\u4f3d\u624d\u79bb\u5f00\u8ff7\u8e2a\u8c37\u7684\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u5ffd\u7136\u5fc3\u4e2d\u4e00\u52a8\u9053\uff1a\u201c\u65b9\u4e08\u6cd5\u540d\u609f\u5c18\uff0c\u53ef\u8ba4\u8bc6\u2014\u4f4d\u6cd5\u540d\u609f\u660e\u7684\u5e08\u7236\uff01\u201d \u3000\u3000\u609f\u5c18\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u949f\u65bd\u4e3b\u600e\u4f1a\u77e5\u9053\u609f\u660e\uff1f\u609f\u660e\u6b63\u662f\u8d2b\u50e7\u7684\u5e08\u5f1f\uff01\u201d \u3000\u3000\u609f\u5c18\u56e0\u548c\u7fa4\u8c6a\u8c08\u4e86\u751a\u4e45\uff0c\u7ecf\u949f\u4e00\u8c6a\u7684\u4ecb\u7ecd\uff0c\u5bf9\u7fa4\u8c6a\u7684\u59d3\u540d\u90fd\u5df2\u5f04\u5f97\u5f88\u6e05\u695a\uff0e \u3000\u3000\u949f\u4e00\u8c6a\u5fd9\u9053\uff1a\u201c\u8fd9\u6837\u8bf4\u65b9\u4e08\u4e5f\u662f\u5c11\u6797\u51fa\u8eab\u4e86\uff1f\u201d \u3000\u3000\u609f\u5c18\u9894\u9996\u9053\uff1a\u201c\u4e0d\u9519\u3002\u8d2b\u50e7\u5f53\u5e74\u4e5f\u662f\u7531\u5c11\u6797\u51fa\u6765\u7684\u3002\u949f\u65bd\u4e3b\u4e3a\u4ec0\u4e48\u5ffd\u7136\u63d0\u8d77\u8d2b\u50e7\u7684\u5e08\u5f1f \u609f\u660e\uff0c\u83ab\u975e\u548c\u4ed6\u8ba4\u8bc6\u3002\u201d \u3000\u3000\u949f\u2014\u8c6a\u4e0d\u7b54\u53cd\u95ee\u9053\uff1a\u201c\u65b9\u4e08\u65e2\u662f\u51fa\u8eab\u5c11\u6797\uff0c\u5fc5\u5b9a\u77e5\u9053\u5c11\u6797\u6709\u5341\u516b\u94dc\u4eba\uff0c\u800c\u5341\u516b\u94dc\u4eba\u4e4b\u5916\uff0c \u66f4\u6709\u4e00\u4eba\u8d1f\u8d23\u9886\u961f\uff0c\u7b49\u4e8e\u662f\u5341\u516b\u94dc\u4eba\u7684\u6307\u6325\u3002\u201d \u3000\u3000\u609f\u5c18\u9053\uff1a\u201c\u5c11\u6797\u7684\u5341\u516b\u94dc\u4eba\u95fb\u540d\u5929\u4e0b\uff0c\u8d2b\u50e7\u51fa\u8eab\u5c11\u6797\uff0c\u5c82\u6709\u4e0d\u77e5\u4e4b\u7406\uff0c\u542c\u8bf4\u76ee\u524d\u7684\u5341\u516b \u94dc\u4eba\u6307\u6325\uff0c\u6b63\u662f\u655d\u5e08\u5f1f\u609f\u660e\uff0e\u201d \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6b63\u662f\u4ed6\u3002\u53ef\u60dc\u73b0\u5728\u7684\u5341\u516b\u4eba\uff0c\u5df2\u7ecf\u6709\u4e00\u534a\u4e0d\u5728\u5d69\u5c71\u5c11\u6797\u672c\u5bfa\u4e86\uff01\u201d \u3000\u3000\u609f\u5c18\u5403\u60ca\u9053\uff1a\u201c\u4ed6\u4eec\u5230\u4e86\u54ea\u91cc\uff1f\u636e\u8d2b\u50e7\u6240\u77e5\uff0e\u5341\u516b\u94dc\u4eba\u662f\u4ece\u6765\u4e0d\u79bb\u5f00\u5d69\u5c71\u672c\u9662\u7684\uff0c\u5373\u4f7f \u79bb\u5f00\uff0c\u4e5f\u7edd\u4e0d\u53ef\u80fd\u53ea\u79bb\u5f00\u2014\u534a\u3002\u201d \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u4ed6\u4eec\u786e\u5b9e\u662f\u8d70\u4e86\u4e00\u534a\uff0c\u800c\u4e14\u662f\u7531\u8d35\u5e08\u5f1f\u609f\u660e\u5e26\u8d70\u7684\u3002\u201d \u3000\u3000\u609f\u5c18\u8d8a\u53d1\u5403\u60ca\u9053\uff1a\u201c\u771f\u6709\u8fd9\u79cd\u4e8b\uff1f\u949f\u65bd\u4e3b\u662f\u542c\u8c01\u8bf4\u7684\uff1f\u201d \u3000\u3000\u201c\u4e0d\u662f\u542c\u8bf4\u7684\uff0c\u662f\u4eb2\u773c\u770b\u5230\u7684\u3002\u5728\u573a\u7684\u5404\u4f4d\uff0c\u4e5f\u90fd\u4eb2\u773c\u770b\u5230\u7684\u3002\u201d \u3000\u3000\u201c\u609f\u660e\u5e26\u7740\u4e5d\u540d\u94dc\u4eba\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u201d \u3000\u3000\u201c\u8ff7\u8e2a\u8c37\uff01\u201d \u3000\u3000\u201c\u771f\u7684\uff1f\u949f\u65bd\u4e3b\u4e0d\u662f\u5f00\u73a9\u7b11\u5427\uff1f\u201d \u3000\u3000\u201c\u8fd9\u6837\u7684\u5927\u4e8b\uff0c\u600e\u53ef\u4e0e\u65b9\u4e08\u5f00\u73a9\u7b11\u3002\u5b9e\u4e0d\u76f8\u7792\uff0c\u6211\u4eec\u6b63\u662f\u88ab\u90a3\u4e5d\u540d\u94dc\u4eba\u8d76\u5230\u8fd9\u91cc\u6765\u7684\u3002\u201d \u3000\u3000\u949f\u2014\u8c6a\u968f\u5373\u628a\u4eca\u5929\u5728\u4e07\u6708\u5ce1\u7684\u7ecf\u8fc7\u53d9\u8ff0\u4e86\u4e00\u904d\uff0c\u4f46\u4ed6\u5e76\u672a\u63d0\u8d77\u5e9e\u58eb\u51b2\u3002 \u3000\u3000\u609f\u5c18\u5446\u4e86\u534a\u54cd\uff0c\u624d\u5583\u5583\u81ea\u8bed\u7684\u9053\uff1a\u201c\u609f\u660e\u600e\u4f1a\u505a\u51fa\u8fd9\u7b49\u6b3a\u5e08\u706d\u7956\u5927\u9006\u4e0d\u9053\u7684\u4e8b\u6765\uff1f\u201d \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u90a3\u81ea\u7136\u662f\u72af\u4e0b\u4e86\u5c11\u6797\u7684\u89c4\u6212\uff0c\u4ed6\u624d\u4e0d\u987e\u4e00\u5207\u7684\u8fd9\u6837\u505a\uff01\u201d \u3000\u3000\u609f\u5c18\u76b1\u7709\u53c8\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u609f\u660e\u5c31\u662f\u8981\u9003\u8dd1\uff0c\u4e5f\u8be5\u4e00\u4e2a\u4eba\u8d70\uff0c\u5c45\u7136\u5e26\u8d70\u4e86\u4e5d\u540d\u94dc\u4eba\uff0c\u8fd9\u4e8b \u82e5\u4f20\u626c\u51fa\u53bb\uff0c\u5c82\u4e0d\u4e22\u5c3d\u5c11\u6797\u5728\u6b66\u6797\u4e2d\u7684\u989c\u9762\uff0e\u609f\u660e\u8fd9\u79cd\u505a\u6cd5\uff0c\u5b9e\u5728\u592a\u4e0d\u5e94\u8be5\uff0c\u8d2b\u50e7\u8eab\u4e3a\u5c11\u6797 \u5f1f\u5b50\uff0c\u4e49\u4e0d\u5bb9\u8f9e\u3001\u8d23\u65e0\u65c1\u8d37\uff0c\u73b0\u5728\u6b63\u662f\u4e3a\u5e08\u95e8\u6548\u529b\u7684\u65f6\u5019\uff01\u201d \u3000\u3000\u949f\u2014\u8c6a\u6115\u7136\u9053\uff1a\u201c\u65b9\u4e08\u53ef\u662f\u6709\u4ec0\u4e48\u6253\u7b97\uff1f\u201d \u3000\u3000\u609f\u5c18\u8fd9\u65f6\u795e\u8272\u663e\u5f97\u5f02\u5e38\u8083\u7a46\uff0c\u3000\u4e00\u5b57\u4e00\u5b57\u7684\u9053\uff1a\u201c\u8d2b\u50e7\u8981\u5192\u6b7b\u8fdb\u5165\u8ff7\u8e2a\u8c37\uff0c\u5411\u609f\u660e\u6653\u4ee5 \u5927\u4e49\uff01\u201d \u3000\u3000\u949f\u4e00\u8c6a\u6025\u6025\u8bf4\u9053\uff1a\u201c\u65b9\u4e08\u5343\u4e07\u4f7f\u4e0d\u5f97\uff0c\u4f60\u82e5\u8fdb\u5165\u8ff7\u8e2a\u8c37\uff0c\u7b49\u4e8e\u7f8a\u5165\u864e\u53e3\uff0c\u767d\u767d\u9001\u4e0a\u4e00\u547d\uff0c \u5373\u4f7f\u609f\u660e\u4e0d\u628a\u4f60\u600e\u6837\uff0e\u970d\u5143\u4f3d\u4e5f\u7edd\u5bf9\u653e\u4e0d\u8fc7\u4f60\uff01\u201d \u3000\u3000\u738b\u5927\u5eb7\u4e5f\u8ddf\u7740\u529d\u963b\u9053\uff1a\u201c\u949f\u526f\u76df\u4e3b\u8bf4\u5f97\u5bf9\uff0c\u65b9\u4e08\u5343\u4e07\u4e0d\u80fd\u5192\u9669\uff0e\u660e\u77e5\u53bb\u4e86\u975e\u6b7b\u4e0d\u53ef\uff0c\u4e3a \u4ec0\u4e48\u504f\u504f\u8981\u53bb\u9001\u6b7b\u5462\uff1f\u201d \u3000\u3000\u609f\u5c18\u9887\u4e3a\u6fc0\u52a8\u7684\u9053\uff1a\u201c\u655d\u5bfa\u79bb\u8ff7\u8e2a\u8c37\u6700\u8fd1\uff0c\u82e5\u8d2b\u50e7\u4e0d\u505a\u8fd9\u4ef6\u4e8b\uff0c\u53c8\u6709\u8c01\u53bb\u505a\u5462\uff1f\u8d2b\u50e7\u4e0d \u5165\u5730\u72f1\uff0e\u53c8\u8ba9\u8c01\u5165\u5730\u72f1\u5462\uff1f\u201d \u3000\u3000\u949f\u4e00\u8c6a\u6b63\u8272\u9053\uff1a\u201c\u5982\u679c\u5165\u5730\u72f1\u5c31\u80fd\u5b8c\u6210\u5f97\u4e86\u5fc3\u613f\uff0c\u90a3\u5f53\u7136\u5165\u5730\u72f1\u4e5f\u65e0\u4ec0\u4e48\u4e0d\u53ef\uff0c\u4f46\u5165\u5730 \u72f1\u53ea\u662f\u767d\u767d\u9001\u4e0a\u4e00\u547d\uff0c\u90a3\u53c8\u4f55\u82e6\uff1f\u201d \u3000\u3000\u53ea\u542c\u4f59\u4ea6\u4e50\u9053\uff1a\u201c\u65b9\u4e08\u80fd\u6709\u8fd9\u756a\u5fc3\u610f\uff0c\u5df2\u7ecf\u4e0d\u6127\u662f\u5c11\u6797\u5f1f\u5b50\u4e86\u3002\u5728\u4e0b\u5012\u60f3\u51fa\u4e00\u4e2a\u529e\u6cd5\uff0c \u4e5f\u4e0d\u5931\u4e3a\u4e24\u5168\u4e4b\u8ba1\u3002\u201d \u3000\u3000\u609f\u5c18\u9053\uff1a\u201c\u4f59\u65bd\u4e3b\u6709\u4ec0\u4e48\u9ad8\u89c1\uff1f\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u9053\uff1a\u201c\u4f60\u4f55\u4e0d\u6d3e\u4e00\u540d\u5f1f\u5b50\u5230\u5c11\u6797\u901a\u62a5\uff0c\u5c11\u6797\u5f97\u5230\u6d88\u606f\uff0c\u5fc5\u5b9a\u4f1a\u6d3e\u5927\u6279\u9ad8\u624b\u524d\u6765\uff0c \u5230\u90a3\u65f6\u4f55\u6101\u4e0d\u80fd\u628a\u609f\u660e\u548c\u4e5d\u540d\u94dc\u4eba\u62bc\u89e3\u56de\u53bb\u3002\u201d \u3000\u3000\u609f\u5c18\u8fde\u58f0\u9053\uff1a\u201c\u4f59\u65bd\u4e3b\u8bf4\u5f97\u5bf9\uff01\u4f59\u65bd\u4e3b\u8bf4\u5f97\u5bf9\uff01\u8d2b\u50e7\u5934\u8111\u5b9e\u5728\u4e0d\u7075\u5149\uff0c\u7adf\u7136\u6ca1\u60f3\u5230\u8fd9\u4e00 \u6b65\uff0e\u201d \u3000\u3000\u6b21\u65e5\u5929\u4eae\u540e\uff0c\u609f\u5c18\u4eb2\u7b14\u5199\u4e86\u4e00\u5c01\u4fe1\uff0c\u5f53\u5373\u6d3e\u9063\u5927\u5f1f\u5b50\u666e\u51e1\u4e13\u7a0b\u524d\u5f80\u5d69\u5c71\u5c11\u6797\uff0c\u5e76\u8981\u4ed6\u628a \u4fe1\u5448\u4e0e\u5c11\u6797\u73b0\u4efb\u638c\u95e8\u5929\u7985\u5927\u5e08\uff0e \u3000\u3000\u949f\u4e00\u8c6a\u7b49\u4eba\u5728\u7528\u8fc7\u65e9\u996d\u540e\uff0c\u4fbf\u7531\u609f\u5c18\u966a\u540c\uff0c\u5728\u5bfa\u5185\u5404\u5904\u8d70\u4e86\u4e00\u904d\u3002 \u3000\u3000\u8fd9\u6240\u767d\u59d1\u5bfa\uff0c\u56e0\u4ece\u524d\u7559\u6709\u8fc7\u50e7\u5f92\u4e8c\u5341\u4f59\u4eba\uff0c\u89c4\u6a21\u81ea\u7136\u5e76\u4e0d\u7b97\u5c0f\uff0e\u4e0d\u8fc7\u6b64\u65f6\u5df2\u662f\u4e00\u7247\u8d25\u843d \u666f\u8c61\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u7b49\u4eba\u968f\u8eab\u5e26\u7684\u94f6\u4e24\u4e0d\u5c11\uff0c\u968f\u5373\u6350\u4e86\u4e00\u7b14\u9999\u706b\u94b1\uff0e\u5f53\u7136\u5566\uff0c\u4ed6\u4eec\u4e00\u884c\u5341\u4e00\u4eba\u4f4f\u5728\u8fd9 \u91cc\uff0c\u4e5f\u4e0d\u80fd\u8001\u662f\u767d\u5403\u767d\u559d\uff0c\u6350\u51fa\u8fd9\u7b14\u94b1\uff0c\u6709\u4e00\u534a\u4e5f\u662f\u4e3a\u4e86\u81ea\u5df1\u3002 \u3000\u3000\u5bfa\u5185\u7a7a\u623f\u5f88\u591a\uff0c\u949f\u4e00\u8c6a\u7b49\u4eba\u5374\u770b\u4e2d\u4e86\u5bfa\u5185\u7684\u4e00\u5ea7\u5b9d\u5854\u3002 \u3000\u3000\u8fd9\u5ea7\u5b9d\u5854\u540d\u66f0\u767d\u59d1\u5854\uff0c\u5171\u5206\u4e03\u7ea7\uff0c\u77d7\u7acb\u4e8e\u5bfa\u5185\u5927\u6bbf\u540e\u4fa7\uff0c\u9664\u4e86\u5e95\u5c42\u636e\u8bf4\u4f9b\u4e86\u4e00\u5c0a\u86c7\u795e\u5916\uff0c \u4e0a\u9762\u516d\u5c42\u90fd\u662f\u7a7a\u7740\u7684\u3002 \u3000\u3000\u6709\u4e86\u4e0a\u9762\u516d\u5c42\uff0c\u8db3\u53ef\u4f9b\u949f\u4e00\u8c6a\u7b49\u5341\u4e00\u4eba\u5c45\u4f4f\u3002 \u3000\u3000\u4ed6\u4eec\u9009\u62e9\u4f4f\u5728\u8fd9\u5ea7\u5b9d\u5854\u5185\u7684\u539f\u56e0\uff0c\u9664\u4e86\u5f7c\u6b64\u8054\u7cfb\u7167\u987e\u65b9\u4fbf\u5916\uff0c\u6700\u4e3b\u8981\u7684\u8fd8\u662f\u4fbf\u4e8e\u4e86\u671b\u76d1 \u89c6\u3002\u5b9d\u5854\u6709\u4e03\u5c42\u697c\u9ad8\uff0c\u867d\u7136\u5bfa\u5e99\u56db\u5468\u90fd\u6709\u6811\u6797\uff0c\u5374\u906e\u4e0d\u4f4f\u5b9d\u5854\u6700\u4e0a\u4e24\u5c42\uff0c\u4eba\u5728\u5b9d\u5854\u6700\u9876\uff0c\u89c6 \u754c\u8fbd\u9614\uff0c\u9644\u8fd1\u6709\u4f55\u52a8\u9759\uff1f\u53ef\u4ee5\u4e00\u89c8\u65e0\u9057\u3002 \u3000\u3000\u4e8e\u662f\u949f\u4e00\u8c6a\u5206\u914d\u5404\u4eba\u4f4f\u5904\uff0c\u7531\u949f\u4e00\u8c6a\u548c\u4f59\u4ea6\u4e50\u4f4f\u7b2c\u4e8c\u5c42\uff0c\u5d02\u5c71\u4e09\u96c4\u4f4f\u7b2c\u4e09\u5c42\uff0c\u6c5f\u5317\u4e09\u9f99 \u4f4f\u7b2c\u56db\u5c42\uff0c\u9ea6\u5c0f\u660e\u548c\u6587\u5929\u751f\u4f4f\u7b2c\u4e94\u5c42\uff0c\u82d7\u7d20\u82d3\u4f4f\u7b2c\u516d\u5c42\u3002 \u3000\u3000\u6700\u4e0a\u9762\u7684\u4e00\u5c42\uff0c\u4f5c\u4e3a\u4e86\u671b\u54e8\u6240\uff0c\u65e5\u591c\u8f6e\u6d41\u6d3e\u51fa\u4e00\u4eba\u5728\u6700\u4e0a\u4e00\u5c42\u8d1f\u8d23\u4e86\u671b\u76d1\u89c6\u3002 \u3000\u3000\u5206\u914d\u65e2\u6bd5\uff0c\u7fa4\u8c6a\u7acb\u5373\u642c\u4e86\u8fdb\u53bb\uff0c\u609f\u5c18\u5e76\u547d\u4e24\u540d\u5f1f\u5b50\u9001\u4e0a\u94fa\u76d6\u3002\u7531\u4e8e\u5854\u5185\u65e0\u5e8a\uff0c\u7fa4\u8c6a\u53ea\u80fd \u6253\u5730\u94fa\uff0c\u597d\u5728\u697c\u4e0a\u5730\u9762\u5e76\u4e0d\u6f6e\u6e7f\uff0c\u6253\u5730\u94fa\u548c\u7761\u5e8a\u69bb\u4e5f\u5dee\u4e0d\u4e86\u591a\u5c11\u3002 \u3000\u3000\u4e00\u8fde\u6570\u65e5\u8fc7\u53bb\uff0e\u5e76\u65e0\u4efb\u4f55\u52a8\u9759\uff0e \u3000\u3000\u4f46\u7fa4\u8c6a\u4eec\u4f4f\u5728\u8fd9\u91cc\uff0c\u5374\u5e76\u975e\u5b8c\u5168\u8eb2\u707e\u907f\u7978\uff0c\u4ed6\u4eec\u5fc5\u987b\u523a\u63a2\u2014\u4e9b\u8ff7\u8e2a\u8c37\u7684\u6d88\u606f\uff0c\u4ee5\u4fbf\u5019\u673a \u91c7\u53d6\u884c\u52a8\u3002 \u3000\u3000\u5c31\u5728\u949f\u4e00\u8c6a\u8981\u6d3e\u4eba\u51fa\u53bb\u523a\u63a2\u6d88\u606f\u7684\u2014\u4e2a\u508d\u665a\uff0c\u5374\u6765\u4e86\u4e07\u6708\u5821\u7684\u7ba1\u5bb6\u674e\u8302\u3002 \u3000\u3000\u674e\u8302\u65e9\u5df2\u77e5\u9053\u949f\u4e00\u8c6a\u7b49\u4eba\u4f4f\u5728\u767d\u59d1\u5bfa\uff0c\u6240\u4ee5\u624d\u80fd\u627e\u5230\u8fd9\u91cc\u6765\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u548c\u4f59\u4ea6\u4e50\u5f53\u5373\u628a\u674e\u8302\u62db\u5f85\u5728\u81ea\u5df1\u6240\u4f4f\u7684\u7b2c\u4e8c\u5c42\u5854\u5185\u3002 \u3000\u3000\u5176\u4f59\u5404\u5c42\u7684\u4eba\uff0c\u95fb\u542c\u674e\u8302\u5230\u6765\uff0c\u4e5f\u90fd\u4e0b\u6765\u548c\u674e\u8302\u76f8\u89c1\u3002 \u3000\u3000\u674e\u8302\u795e\u8272\u614c\u5f20\uff0c\u3000\u4e00\u770b\u4fbf\u77e5\u5fc5\u662f\u6709\u4ec0\u4e48\u91cd\u5927\u4e8b\u60c5\u524d\u6765\u901a\u62a5\uff0c\u4f46\u4f3c\u4e4e\u53c8\u96be\u4e8e\u542f\u9f7f\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u4e5f\u611f\u5230\u4e0d\u5b89\uff0c\u50ac\u4fc3\u7740\u9053\uff1a\u201c\u674e\u5144\u6709\u8bdd\u5feb\u8bb2\uff01\u201d \u3000\u3000\u674e\u8302\u957f\u957f\u5401\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u771f\u4e0d\u77e5\u9053\u5982\u4f55\u8bf4\u8d77\uff0c\u4e07\u6708\u5821\u88ab\u970d\u5143\u4f3d\u5e26\u4eba\u6d17\u52ab\u4e86\uff01\u201d \u3000\u3000\u949f\u4e00\u8c6a\u554a\u4e86\u58f0\u9053\uff1a\u201c\u90fd\u6d17\u52ab\u4e86\u4e9b\u4ec0\u4e48\u4e1c\u897f\u53bb\uff1f\u8d35\u5821\u6709\u6ca1\u6709\u4eba\u4f24\u4ea1\uff1f\u201d \u3000\u3000\u674e\u8302\u53c8\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u5821\u91cc\u7684\u8d35\u91cd\u4e1c\u897f\u4ee5\u53ca\u5404\u4f4d\u6ca1\u5e26\u8d70\u7684\u4e1c\u897f\uff0c\u5168\u88ab\u6d17\u52ab\u2014\u7a7a\uff0c\u6240\u5e78\u4ed6 \u4eec\u5e76\u672a\u4f24\u4eba\u3002\u201d \u3000\u3000\u201c\u4e8b\u60c5\u662f\u4ec0\u4e48\u65f6\u5019\u53d1\u751f\u7684\uff1f\u201d \u3000\u3000\u201c\u5c31\u5728\u6628\u5929\u591c\u91cc\uff0e\u201d \u3000\u3000\u201c\u4ed6\u4eec\u53ef\u66fe\u95ee\u8fc7\u6211\u4eec\uff1f\u201d \u3000\u3000\u201c\u95ee\u8fc7\u3002\u201d \u3000\u3000\u201c\u674e\u5144\u600e\u4e48\u56de\u7b54\u4ed6\u4eec\u7684\uff1f\u201d \u3000\u3000\u201c\u6211\u53ea\u8bf4\u4f60\u4eec\u5df2\u7ecf\u65e9\u5728\u6570\u65e5\u524d\u79bb\u5f00\u4e07\u6708\u5ce1\uff0c\u5e76\u4e0d\u6e05\u695a\u53bb\u4e86\u4ec0\u4e48\u5730\u65b9\u3002\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u949f\u4e00\u8c6a\u548c\u7fa4\u8c6a\u90fd\u611f\u5230\u4e00\u9635\u5185\u759a\uff0c\u4ed6\u4eec\u5e76\u4e0d\u75db\u60dc\u81ea\u5df1\u7559\u5728\u4e07\u6708\u5821\u7684\u4e1c\u897f\u88ab\u52ab\uff0c\u800c\u662f \u60f3\u5230\u4e07\u6708\u5821\u5185\u7684\u88ab\u52ab\uff0c\u663e\u7136\u662f\u81ea\u5df1\u2014\u4f19\u4eba\u5e26\u53bb\u7684\u707e\u96be\uff0c\u53c8\u5982\u4f55\u80fd\u4e0d\u6b49\u759a\u96be\u5b89\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u6b49\u7136\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u970d\u5143\u4f3d\u80fd\u505a\u51fa\u8fd9\u79cd\u4e8b\u6765\uff0c\u8bf4\u8d77\u6765\u8fd9\u4ef6\u4e8b\u5b8c\u5168\u662f\u56e0\u6211\u4eec\u800c\u8d77\uff0c\u949f \u67d0\u5b9e\u5728\u60ed\u6127\uff01\u201d \u3000\u3000\u674e\u8302\u5e26\u7740\u65e0\u5948\u795e\u8272\uff0c\u6447\u4e86\u6447\u5934\u9053\uff1a\u201c\u949f\u5927\u4fa0\u7528\u4e0d\u7740\u81ea\u8d23\uff0c\u597d\u5728\u5821\u5185\u8fd8\u6709\u90e8\u5206\u8d35\u91cd\u4e1c\u897f\uff0c \u4e0d\u66fe\u88ab\u4ed6\u4eec\u641c\u53bb\uff0c\u52c9\u5f3a\u652f\u6491\u4e0b\u53bb\uff0c\u8fd8\u4e0d\u81f4\u6709\u591a\u5927\u95ee\u9898\u3002\u201d \u3000\u3000\u201c\u674e\u5144\u53ef\u8fd8\u6709\u522b\u7684\u4ea4\u4ee3\uff1f\u201d \u3000\u3000\u674e\u8302\u9762\u8272\u8d8a\u53d1\u51dd\u91cd\uff0c\u7f13\u7f13\u4ece\u6000\u91cc\u638f\u51fa\u2014\u5c01\u4fe1\u6765\u9053\uff1a\u201c\u949f\u5927\u4fa0\u8bf7\u770b\u770b\u8fd9\u5c01\u4fe1\uff01\u201d \u3000\u3000\u949f\u4e00\u8c6a\u8bb6\u7136\u9053\uff1a\u201c\u662f\u8c01\u7684\u4fe1\uff1f\u201d \u3000\u3000\u674e\u8302\u9053\uff1a\u201c\u662f\u82d7\u59d1\u5a18\u6258\u4eba\u5e26\u6765\u7684\u3002\u201d \u3000\u3000\u949f\u4e00\u8c6a\u5306\u5306\u5c06\u4fe1\u63a5\u8fc7\uff0c\u4fe1\u5c01\u4e0a\u5199\u7740\u201c\u5448\u949f\u5927\u4fa0\u4eb2\u542f\u201d\uff0c\u5b57\u8ff9\u5a1f\u79c0\u4e2d\u53c8\u6709\u4e9b\u51cc\u4e71\uff0c\u663e\u7136\u662f \u4ed3\u4fc3\u614c\u5fd9\u95f4\u5199\u7684\u3002 \u3000\u3000\u6025\u6025\u62c6\u5c01\u62bd\u51fa\u4fe1\u7b3a\uff0c\u91cc\u9762\u53ea\u662f\u8349\u8349\u7684\u4e24\u884c\u5b87\uff0c\u4e0a\u5199\u7740\u5979\u5df2\u88ab\u970d\u5143\u4f3d\u6253\u8fdb\u5730\u7262\uff0c\u76ee\u524d\u662f\u6c42 \u751f\u4e0d\u5f97\uff0c\u6c42\u6b7b\u4e0d\u80fd\uff0c\u8981\u949f\u4e00\u8c6a\u77e5\u9053\u6b64\u4e8b\u4fbf\u53ef\uff0c\u4e0d\u5fc5\u5192\u9669\u53bb\u6551\u5979\uff0c\u4ee5\u514d\u6709\u53bb\u65e0\u56de\uff0c\u767d\u767d\u727a\u7272\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u5fc3\u5982\u5200\u7ede\uff0c\u770b\u8fc7\u4e4b\u540e\uff0e\u7acb\u5373\u628a\u4fe1\u4ea4\u4e0e\u82d7\u7d20\u82d3\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u81ea\u7136\u4e5f\u8ddf\u7740\u89c2\u770b\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u8feb\u4e0d\u53ca\u5f85\u95ee\u9053\uff1a\u201c\u8fd9\u5c01\u4fe1\u662f\u600e\u4e48\u5230\u8fbe\u674e\u5144\u624b\u4e2d\u7684\uff1f\u201d \u3000\u3000\u674e\u8302\u9053\uff1a\u201c\u949f\u5927\u4fa0\u53ef\u77e5\u9053\u6709\u4e2a\u53eb\u674e\u6797\u7684\uff1f\u201d \u3000\u3000\u201c\u8bb0\u5f97\uff0c\u636e\u8bf4\u674e\u6797\u539f\u662f\u4e07\u6708\u5821\u7684\u4eba\uff0c\u540e\u6765\u88ab\u80e1\u76df\u4e3b\u5e26\u5230\u8ff7\u8e2a\u8c37\u53bb\uff0c\u4e0d\u8fc7\u81ea\u80e1\u76df\u4e3b\u79bb\u5f00\u8ff7 \u8e2a\u8c37\u540e\uff0c\u4ed6\u5df2\u5f88\u5c11\u8ddf\u6211\u4eec\u8fd9\u4f19\u4eba\u6765\u5f80\u3002\u201d \u3000\u3000\u201c\u674e\u6797\u539f\u662f\u6211\u7684\u4e00\u4f4d\u5802\u5f1f\uff0c\u4ed6\u6700\u8fd1\u4e24\u5e74\u4e0d\u5e38\u548c\u949f\u5927\u4fa0\u7b49\u4eba\u6765\u5f80\uff0c\u76ee\u7684\u662f\u4e0d\u4f7f\u970d\u5143\u4f3d\u7591\u5fc3\u3002 \u8fd9\u5c01\u4fe1\u6b63\u662f\u82d7\u59d1\u5a18\u6258\u4ed6\u4e58\u591c\u79d8\u5bc6\u9001\u5230\u4e07\u6708\u5821\u7684\u3002\u201d \u3000\u3000\u201c\u674e\u6797\u662f\u4ec0\u4e48\u65f6\u5019\u5230\u4e07\u6708\u5821\u7684\uff1f\u201d \u3000\u3000\u201c\u4e5f\u662f\u6628\u591c\u3002\u201d \u3000\u3000\u201c\u8fd9\u6837\u5c82\u4e0d\u88ab\u970d\u5143\u4f3d\u6d3e\u51fa\u6d17\u52ab\u8d35\u5821\u7684\u4eba\u770b\u5230\uff1f\u201d \u3000\u3000\u201c\u674e\u6797\u4e5f\u662f\u53c2\u4e0e\u6d17\u52ab\u7684\u4eba\uff0c\u4ed6\u662f\u627e\u673a\u4f1a\u548c\u6211\u79c1\u4e0b\u89c1\u4e86\u4e00\u9762\uff0c\u987a\u4fbf\u628a\u4fe1\u4ea4\u7ed9\u4e86\u6211\u3002\u201d \u3000\u3000\u201c\u4ed6\u8bf4\u8fc7\u4ec0\u4e48\uff1f\u662f\u5426\u63d0\u8d77\u8fc7\u82d7\u59d1\u5a18\u88ab\u6253\u5165\u5730\u7262\u7684\u539f\u56e0\u3002\u201d \u3000\u3000\u201c\u4ed6\u8bf4\u82d7\u59d1\u5a18\u505a\u4e86\u4e00\u4ef6\u4e8b\uff0c\u88ab\u970d\u5143\u4f3d\u67e5\u5230\u4e86\u3002\u201d \u3000\u3000\u201c\u82d7\u59d1\u5a18\u505a\u4e86\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u636e\u8bf4\u970d\u5143\u4f3d\u51c6\u5907\u706b\u711a\u67cf\u9f84\u9662\u7684\u90a3\u4e00\u591c\uff0c\u662f\u82d7\u59d1\u5a18\u5411\u9ea6\u5c0f\u4fa0\u793a\u8b66\u7684\u3002\u970d\u5143\u4f3d\u67e5\u660e\u8fd9\u4ef6\u4e8b\uff0c \u5728\u5fcd\u65e0\u53ef\u5fcd\u7684\u60c5\u5f62\u4e0b\uff0e\u624d\u628a\u82d7\u59d1\u5a18\u6253\u5165\u5730\u7262\u3002\u201d \u3000\u3000\u7fa4\u8c6a\u5230\u8fd9\u65f6\uff0c\u7ec8\u4e8e\u660e\u767d\u539f\u6765\u662f\u82d7\u7d20\u5170\u6551\u4e86\u4ed6\u4eec\u4e00\u547d\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u9ed8\u9053\uff1a\u201c\u674e\u6797\u53ef\u8bf4\u8fc7\u82d7\u59d1\u5a18\u662f\u5426\u6709\u5371\u9669\uff1f\u201d \u3000\u3000\u674e\u8302\u9053\uff1a\u201c\u5f88\u96be\u8bb2\u3002\u970d\u5143\u4f3d\u9634\u9669\u72e1\u8bc8\uff0c\u624b\u6bb5\u6bd2\u8fa3\uff0c\u4ed6\u5728\u5f97\u77e5\u82d7\u59d1\u5a18\u5bf9\u4ed6\u4e0d\u662f\u771f\u5fc3\u4e4b\u540e\uff0c \u82d7\u59d1\u5a18\u7684\u540e\u679c\u5982\u4f55\uff0c\u4e5f\u5c31\u53ef\u60f3\u800c\u77e5\u4e86\u3002\u201d \u3000\u3000\u8fd9\u51e0\u53e5\u8bdd\uff0c\u53ea\u542c\u5f97\u7fa4\u8c6a\u65e0\u4e0d\u5927\u8d77\u6050\u614c\u3002 \u3000\u3000\u674e\u8302\u53c8\u8c08\u4e86\u51e0\u53e5\uff0c\u4fbf\u544a\u8f9e\u800c\u53bb\u3002 \u3000\u3000\u674e\u8302\u8d70\u540e\uff0c\u82d7\u7d20\u82d3\u518d\u4e5f\u96be\u7981\u5fc3\u4e2d\u7684\u60b2\u75db\uff0c\u5f53\u573a\u54ed\u4e86\u8d77\u6765\u3002 \u3000\u3000\u4f17\u4eba\u53ea\u5f97\u4e0a\u524d\u529d\u6170\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u4e8b\u5df2\u5982\u6b64\uff0c\u60b2\u4f24\u4e5f\u662f\u65e0\u7528\u3002\u636e\u6211\u6240\u6599\uff0e\u4ee4\u59d0\u4e00\u5b9a\u6709\u60ca\u65e0\u9669\u3002\u201d \u3000\u3000\u738b\u5927\u5eb7\u62a2\u7740\u95ee\u9053\uff1a\u201c\u526f\u76df\u4e3b\u600e\u80fd\u65ad\u5b9a\u6709\u60ca\u65e0\u9669\uff1f\u201d \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u82d7\u59d1\u5a18\u65e2\u7136\u505a\u970d\u76df\u4e3b\u7684\u538b\u5be8\u592b\u4eba\uff0e\u970d\u5143\u4f3d\u5fc5\u4e0d\u53ef\u80fd\u72e0\u4e0b\u5fc3\u6765\u5904\u7f6e\u5979\u3002\u201d \u3000\u3000\u738b\u5927\u5eb7\u54fc\u4e86\u58f0\u9053\uff1a\u201c\u8fde\u4eba\u90fd\u88ab\u6253\u8fdb\u5730\u7262\u91cc\u4e86\uff0e\u8fd8\u8bf4\u72e0\u4e0d\u4e0b\u5fc3\u6765\u5904\u7f6e\u5979\uff1f\u970d\u5143\u4f3d\u5fc3\u72e0\u624b\u8fa3\uff0c \u516d\u4eb2\u4e0d\u8ba4\uff0c\u8981\u662f\u4ed6\u60f3\u5904\u7f6e\u4e00\u4e2a\u4eba\uff0c\u522b\u8bf4\u662f\u4ed6\u8001\u5a46\uff0c\u5373\u4f7f\u4ed6\u7684\u7239\u5a18\uff0c\u4ed6\u7167\u6837\u72e0\u5f97\u4e0b\u5fc3\u6765\u3002\u201d \u3000\u3000\u949f\u2014\u8c6a\u5e26\u4e9b\u65e0\u5948\u7684\u9053\uff1a\u201c\u6211\u53c8\u4f55\u5c1d\u6ca1\u60f3\u5230\u8fd9\u4e00\u6b65\uff0c\u4f46\u4ee5\u54b1\u4eec\u76ee\u524d\u7684\u529b\u91cf\uff0c\u5374\u5b9e\u5728\u6551\u4e0d\u4e86 \u82d7\u5927\u59d1\u5a18\uff0c\u82e5\u8d38\u7136\u884c\u52a8\uff0c\u6253\u8349\u60ca\u86c7\uff0c\u53cd\u800c\u5f04\u5de7\u6210\u62d9\u3002\u201d \u3000\u3000\u738b\u5927\u5eb7\u9053\uff1a\u201c\u96be\u9053\u54b1\u4eec\u5c31\u80fd\u773c\u770b\u7740\u82d7\u5927\u59d1\u5a18\u88ab\u970d\u5143\u4f3d\u6d3b\u6d3b\u5904\u6b7b\uff1f\u603b\u8981\u60f3\u4e2a\u529e\u6cd5\u624d\u884c\uff01\u201d \u3000\u3000\u949f\u4e00\u8c6a\u7565\u4e00\u72b9\u8c6b\u9053\uff1a\u201c\u5404\u4f4d\u5148\u8bf7\u56de\u5230\u81ea\u5df1\u4f4f\u5904\uff0c\u8ba9\u6211\u548c\u4f59\u5148\u751f\u597d\u597d\u5546\u8bae\u5546\u8bae\u8be5\u600e\u4e48\u529e\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u56de\u5230\u81ea\u5df1\u6240\u4f4f\u7684\u7b2c\u4e94\u5c42\u5854\uff0c\u8f97\u8f6c\u53cd\u4fa7\uff0c\u518d\u4e5f\u7761\u4e0d\u7740\u3002 \u3000\u3000\u5230\u73b0\u5728\u4ed6\u624d\u771f\u6b63\u660e\u767d\uff0c\u539f\u6765\u82d7\u7d20\u5170\u6295\u5f52\u970d\u5143\u4f3d\uff0c\u76ee\u7684\u662f\u201c\u727a\u7272\u201d\u81ea\u5df1\uff0c\u4ee5\u4fbf\u6697\u5730\u5e2e\u52a9\u67cf \u9f84\u9662\u6240\u6709\u7684\u4eba\u3002 \u3000\u3000\u5982\u4eca\u82d7\u7d20\u5170\u8eab\u6709\u5927\u96be\uff0c\u4ed6\u53c8\u600e\u80fd\u5750\u89c6\u4e0d\u6551\u3002 \u3000\u3000\u4ed6\u60f3\u4e0b\u5854\u518d\u548c\u949f\u4e00\u8c6a\u3001\u4f59\u4ea6\u4e50\u8c08\u8c08\uff0c\u4f46\u53c8\u9884\u6599\u4e00\u5b9a\u8c08\u4e0d\u51fa\u4ec0\u4e48\u5934\u7eea\uff0c\u56e0\u4e3a\u8fd9\u4e24\u4eba\u662f\u7fa4\u8c6a \u4e2d\u7684\u9996\u8111\uff0c\u4e3a\u987e\u5168\u5927\u5c40\uff0c\u7edd\u4e0d\u4f1a\u91c7\u53d6\u4efb\u4f55\u5192\u9669\u884c\u52a8\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u51b3\u5b9a\u5148\u627e\u82d7\u7d20\u82d3\uff0c\u63a2\u6d4b\u4e00\u4e0b\u5979\u7684\u5fc3\u610f\u3002 \u3000\u3000\u82d7\u7d20\u82d3\u4f4f\u5728\u5b9d\u5854\u7684\u7b2c\u516d\u5c42\uff0c\u548c\u9ea6\u5c0f\u660e\u9694\u5f97\u6700\u8fd1\uff0c\u770b\u770b\u540c\u5c42\u7684\u6587\u5929\u751f\u5df2\u7ecf\u7761\u7740\uff0e\u4fbf\u4e00\u4e2a\u4eba \u6084\u6084\u7684\u6765\u5230\u7b2c\u516d\u5c42\u5854\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u65e9\u6709\u89c4\u5b9a\uff0c\u5165\u591c\u4e4b\u540e\uff0c\u9664\u4e86\u4f9b\u5949\u86c7\u795e\u7684\u7b2c\u4e00\u5c42\u5854\u5916\uff0c\u5176\u4f59\u5404\u5c42\u90fd\u4e0d\u51c6\u70b9\u706f\uff0c\u5c24\u5176 \u4e0a\u9762\u51e0\u5c42\uff0c\u89c4\u5b9a\u5f97\u66f4\u4e3a\u4e25\u683c\u3002 \u3000\u3000\u5f53\u7136\uff0c\u8fd9\u662f\u4e3a\u4e86\u4fdd\u6301\u9690\u5bc6\uff0c\u4ee5\u514d\u66b4\u5149\u7684\u539f\u56e0\u3002 \u3000\u3000\u4e0d\u96be\u60f3\u89c1\uff0c\u82d7\u7d20\u82d3\u4e5f\u4e0d\u66fe\u7761\u7740\u3002 \u3000\u3000\u5979\u89c1\u4e00\u4e2a\u4eba\u5f71\u7531\u4e0b\u5c42\u8e51\u624b\u8e51\u811a\u6e9c\u4e86\u4e0a\u6765\uff0c\u7acb\u5373\u4f4e\u58f0\u559d\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u4e5f\u4f4e\u58f0\u7b54\u9053\uff1a\u201c\u662f\u6211\uff0c\u9ea6\u5c0f\u660e\u3002\u201d \u3000\u3000\u82d7\u7d20\u82d3\u8fde\u5fd9\u7531\u94fa\u4e0a\u5750\u8d77\u9053\uff1a\u201c\u9ea6\u516c\u5b50\u6709\u4e8b\u5417\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u5982\u679c\u6ca1\u4e8b\uff0c\u6211\u600e\u4f1a\u8fd9\u65f6\u5019\u6765\u627e\u4f60\u3002\u201d \u3000\u3000\u201c\u8bf7\u5750\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u968f\u5373\u4e5f\u5728\u5730\u94fa\u4e0a\u5750\u4e0b\u3002 \u3000\u3000\u201c\u4f60\u53ef\u662f\u4e3a\u6211\u59d0\u59d0\u7684\u4e8b\u800c\u6765\uff1f\u201d \u3000\u3000\u201c\u4ee4\u59d0\u4e3a\u4e86\u6211\u4eec\u5927\u5bb6\u800c\u88ab\u6253\u8fdb\u5730\u7262\uff0c\u6211\u5f53\u7136\u4e0d\u80fd\u5750\u89c6\u4e0d\u7ba1\u3002\u201d \u3000\u3000\u201c\u4f60\u662f\u5426\u53c8\u89c1\u8fc7\u949f\u526f\u76df\u4e3b\u548c\u4f59\u5148\u751f\uff1f\u201d \u3000\u3000\u201c\u6211\u82e5\u53bb\u89c1\u4ed6\u4eec\uff0c\u4f55\u5fc5\u518d\u6765\u627e\u4f60\uff01\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u4e0d\u5148\u53bb\u89c1\u89c1\u4ed6\u4eec\uff1f\u201d \u3000\u3000\u201c\u4ed6\u4eec\u987e\u8651\u7684\u592a\u591a\uff0c\u82e5\u89c1\u4e86\u4ed6\u4eec\uff0c\u54b1\u4eec\u5c31\u65e0\u6cd5\u5355\u72ec\u884c\u52a8\u4e86\uff01\u201d \u3000\u3000\u201c\u4f60\u53ef\u662f\u8981\u4e0d\u987e\u5371\u9669\u53bb\u6551\u6211\u59d0\u59d0\uff1f\u201d \u3000\u3000\u201c\u4ee4\u59d0\u4e0d\u987e\u81ea\u8eab\u5b89\u5371\u6551\u4e86\u6211\u4eec\uff0c\u6211\u73b0\u5728\u5192\u9669\u6551\u5979\uff0c\u6b63\u662f\u5e94\u8be5\u7684\u3002\u201d \u3000\u3000\u82d7\u7d20\u82d3\u4f3c\u662f\u9887\u4e3a\u6fc0\u52a8\uff0e\u9ed8\u4e86\u4e00\u9ed8\u9053\uff1a\u201c\u5c31\u4f60\u4e00\u4e2a\u4eba\u53bb\uff0c\u90a3\u5b9e\u5728\u592a\u5371\u9669\u4e86\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u82e5\u53ea\u987e\u6015\u5371\u9669\u3002\u90a3\u8fd8\u505a\u5f97\u6210\u4ec0\u4e48\u4e8b\uff1f\u6211\u6765\u627e\u4f60\uff0c\u5c31\u662f\u8981\u770b\u770b\u4f60\u7684\u610f\u601d\u3002\u201d \u3000\u3000\u82d7\u7d20\u82d3\u4e0d\u89c9\u611f\u52a8\u5f97\u6d41\u51fa\u4e86\u773c\u6cea\uff0c\u771f\u6068\u4e0d\u5f97\u4e0a\u524d\u62e5\u62b1\u9ea6\u5c0f\u660e\uff0c\u4ee5\u8868\u8fbe\u5185\u5fc3\u7684\u60c5\u610f\u3002 \u3000\u3000\u53ea\u542c\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4f60\u600e\u4e48\u4e0d\u8bf4\u8bdd\uff1f\u201d \u3000\u3000\u82d7\u7d20\u82d3\u5f3a\u81ea\u6291\u5236\u7740\u6fc0\u52a8\u9053\uff1a\u201c\u8fde\u4f60\u90fd\u80af\u4e0d\u987e\u4e00\u5207\u7684\u6551\u6211\u59d0\u59d0\uff0c\u6211\u662f\u5979\u7684\u4eb2\u59b9\u59b9\uff0c\u5f53\u7136\u4e5f \u8981\u53bb\uff0c\u4e0d\u8fc7\u2026\u2026\u201d \u3000\u3000\u201c\u82d7\u59d1\u5a18\u8fd8\u6709\u4ec0\u4e48\u987e\u8651\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u6709\u987e\u8651\uff0c\u6211\u662f\u62c5\u5fc3\u6b64\u53bb\u4e0d\u4f46\u6551\u4e0d\u4e86\u6211\u59d0\u59d0\uff0c\u8fde\u6211\u4eec\u4e24\u4eba\u4e5f\u8981\u81ea\u9677\u7f57\u7f51\u3002\u201d \u3000\u3000\u201c\u6211\u65b9\u624d\u8bf4\u8fc7\uff0c\u987e\u8651\u592a\u591a\uff0c\u53cd\u800c\u505a\u4e0d\u4e86\u4e8b\u3002\u660e\u77e5\u5c71\u6709\u864e\uff0c\u504f\u5411\u864e\u5c71\u884c\u3002\u96be\u9053\u6211\u4eec\u5c31\u4e0d\u80fd \u7edd\u5904\u9022\u751f\uff1f\u201d \u3000\u3000\u201c\u597d\uff01\u6211\u51b3\u5b9a\u968f\u4f60\u540c\u53bb\uff0c\u4f60\u6253\u7b97\u4ec0\u4e48\u65f6\u5019\u884c\u52a8\u3002\u201d \u3000\u3000\u201c\u6551\u4eba\u5982\u6551\u706b\uff0c\u4e8b\u4e0d\u5b9c\u8fdf\uff0c\u54b1\u4eec\u73b0\u5728\u5c31\u8d70\uff0c\u6211\u7684\u5251\u548c\u8f6f\u97ad\u90fd\u5df2\u7ecf\u5e26\u5728\u8eab\u4e0a\u4e86\uff01\u201d \u3000\u3000\u201c\u53ef\u662f\u54b1\u4eec\u4e0b\u5854\u65f6\uff0c\u5f88\u5bb9\u6613\u88ab\u4ed6\u4eec\u53d1\u89c9\u3002\u201d \u3000\u3000\u201c\u7528\u4e0d\u7740\u2014\u5c42\u4e00\u5c42\u7684\u4e0b\u53bb\uff0c\u6bcf\u7ea7\u5854\u90fd\u6709\u4e00\u4e2a\u5854\u7a97\uff0c\u54b1\u4eec\u5c3d\u53ef\u7531\u5854\u7a97\u8dc3\u4e0b\u5730\u9762\uff0c\u795e\u4e0d\u77e5\u9b3c \u4e0d\u89c9\uff0c\u5982\u679c\u884c\u52a8\u987a\u5229\uff0c\u8bf4\u4e0d\u5b9a\u5929\u4eae\u524d\u5c31\u628a\u4ee4\u59d0\u6551\u56de\u6765\u4e86\u3002\u201d \u3000\u3000\u82d7\u7d20\u82d3\u6478\u9ed1\u7740\u597d\u88c5\uff0c\u53d6\u51fa\u5175\u5203\u9053\uff1a\u201c\u8fd9\u5c31\u8d70\u5427\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u5854\u7a97\u5f88\u5c0f\uff0c\u65e0\u6cd5\u4e24\u4eba\u540c\u65f6\u8dc3\u4e0b\uff0c\u5f85\u6211\u5148\u4e0b\u53bb\u4ee5\u540e\uff0c\u4f60\u518d\u4e0b\u6765\uff01\u201d \u3000\u3000\u4ed6\u8bf4\u7740\uff0c\u722c\u5230\u7a97\u53e3\u5934\u4e0b\u811a\u4e0a\uff0c\u53cc\u80a9\u4e00\u6296\uff0c\u4eba\u5df2\u8f7b\u98d8\u98d8\u7684\u843d\u4e0b\u5730\u53bb\uff0c\u8fde\u534a\u70b9\u58f0\u97f3\u90fd\u4e0d\u66fe\u53d1 \u51fa\u3002 \u3000\u3000\u5f53\u4ed6\u4fa7\u843d\u4e0b\u5730\u6765\u4e0d\u4e45\uff0c\u4fbf\u542c\u4e00\u9635\u8863\u8882\u98d8\u98ce\uff0c\u82d7\u7d20\u82d3\u4e5f\u843d\u4e0b\u5730\u6765\uff0c\u800c\u4e14\u7740\u5730\u65f6\u4e5f\u662f\u65e0\u58f0\u65e0 \u606f\u3002 \u3000\u3000\u8fd9\u4f7f\u9ea6\u5c0f\u660e\u72b9\u5982\u670d\u4e0b\u2014\u9897\u5b9a\u5fc3\u4e38\uff0c\u56e0\u4e3a\u8fd9\u6b21\u884c\u52a8\uff0c\u4e3a\u4e86\u8131\u56f4\u548c\u4f7f\u654c\u4eba\u96be\u4ee5\u8ffd\u88ad\uff0c\u8f7b\u529f\u6bd4 \u6b66\u529f\u66f4\u4e3a\u91cd\u8981\u3002 \u3000\u3000\u82d7\u7d20\u82d3\u7684\u6b66\u529f\uff0c\u5df2\u662f\u4e00\u6d41\u9ad8\u624b\uff0c\u96be\u5f97\u8fde\u8f7b\u529f\u4e5f\u8d85\u51e1\u8131\u4fd7\u3002\u6b64\u756a\u7eb5\u7136\u8425\u6551\u82d7\u7d20\u5170\u4e0d\u6210\uff0c\u81f3 \u5c11\u4e0d\u81f4\u88ab\u5bf9\u65b9\u751f\u64d2\u6d3b\u6349\u3002 \u3000\u3000\u4e24\u4eba\u5f88\u5feb\u4fbf\u7a7f\u51fa\u6811\u6797\uff0c\u6765\u5230\u90ca\u91ce\u3002 \u3000\u3000\u8fd9\u65f6\u5c1a\u4e0d\u5230\u4e8c\u66f4\uff0c\u5929\u4e0a\u5e76\u65e0\u6708\u4eae\uff0c\u5bf9\u4ed6\u4eec\u6765\u8bf4\uff0e\u53cd\u800c\u5bb9\u6613\u4fdd\u6301\u884c\u52a8\u7684\u9690\u5bc6\u3002 \u3000\u3000\u867d\u7136\u5929\u4e0a\u65e0\u6708\uff0c\u4f46\u4ee5\u4ed6\u4eec\u5f02\u4e8e\u5e38\u4eba\u7684\u89c6\u529b\uff0c\u7167\u6837\u53ef\u4ee5\u770b\u51fa\u5341\u4f59\u4e08\u5916\u3002 \u3000\u3000\u201c\u9ea6\u516c\u5b50\uff0c\u4f60\u5bf9\u8def\u5f84\u5f88\u719f\u5427\uff1f\u201d \u3000\u3000\u201c\u6211\u5728\u8ff7\u8e2a\u8c37\u8db3\u8db3\u5f85\u4e86\u4e09\u5e74\uff0c\u5f53\u7136\u8def\u5f84\u5f88\u719f\u3002\u201d \u3000\u3000\u201c\u6211\u662f\u6307\u8ff7\u8e2a\u8c37\u5185\u7684\u5404\u5904\u5173\u5361\u3002\u201d \u3000\u3000\u201c\u8ff0\u8e2a\u8c37\u5185\u7684\u5404\u5904\u5173\u5361\u548c\u7981\u5236\u5730\u533a\uff0c\u6211\u90fd\u4e86\u5982\u6307\u638c\uff0c\u4e0d\u8fc7\u4e3a\u4e86\u884c\u52a8\u987a\u5229\u548c\u9690\u79d8\uff0e\u8fd8\u662f\u8981 \u627e\u62e6\u963b\u8f83\u5c11\u7684\u5730\u70b9\u901a\u8fc7\u3002\u201d \u3000\u3000\u82d7\u7d20\u82d3\u53c8\u5f00\u59cb\u6fc0\u52a8\uff0c\u8f7b\u54b3\u4e86\u58f0\u9053\uff1a\u201c\u9ea6\u516c\u5b50\uff0e\u6211\u771f\u4e0d\u77e5\u8be5\u600e\u6837\u611f\u8c22\u4f60\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u4e5f\u89c9\u51fa\u82d7\u7d20\u82d3\u7684\u58f0\u97f3\u6709\u5f02\uff0e\u8038\u4e86\u8038\u80a9\u9053\uff1a\u201c\u73b0\u5728\u522b\u8bf4\u8fd9\u4e9b\uff0c\u7b49\u6551\u51fa\u4ee4\u59d0\u4ee5\u540e\u518d \u8bf4\uff01\u201d \u3000\u3000\u82d7\u7d20\u82d3\u9f13\u8db3\u52c7\u6c14\u9053\uff1a\u201c\u5230\u90a3\u65f6\u5019\u4f60\u8981\u6211\u600e\u6837\u8c22\u4f60\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u987f\u4e86\u987f\u9053\uff1a\u201c\u4f60\u53ea\u8981\u5411\u6211\u8bf4\u58f0\u8c22\u8c22\u5c31\u6210\u4e86\uff0c\u6211\u8fd8\u80fd\u8981\u4f60\u600e\u6837\u8c22\u5462\uff1f\u201d \u3000\u3000\u201c\u96be\u9053\uff1f\u2026\u2026\u201d \u3000\u3000\u201c\u82d7\u59d1\u5a18\u8981\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u82d7\u7d20\u82d3\u7a81\u7136\u7d27\u8ddf\u51e0\u6b65\uff0c\u4e00\u628a\u5c06\u9ea6\u5c0f\u660e\u7d27\u7d27\u62b1\u4f4f\u9053\uff1a\u201c\u9ea6\u516c\u5b50\uff0c\u6211\u2026\u2026\u559c\u6b22\u4f60\uff01\u201d \u3000\u3000\u8fd9\u7a81\u7136\u7684\u4e3e\u52a8\uff0c\u53cd\u800c\u4f7f\u5f97\u9ea6\u5c0f\u660e\u6709\u4e9b\u4e0d\u77e5\u6240\u63aa\uff0c\u5374\u60c5\u4e0d\u81ea\u7981\u4e5f\u628a\u82d7\u7d20\u82d3\u7d27\u7d27\u6402\u4f4f\u3002 \u3000\u3000\u8bf4\u8d77\u6765\u9ea6\u5c0f\u660e\u4e5f\u7b97\u662f\u4e2a\u591a\u60c5\u79cd\u5b50\u3002\u4ece\u524d\uff0c\u4ed6\u5bf9\u8c37\u5bd2\u9999\u4e00\u76f4\u5fc3\u5b58\u7231\u6155\uff0c\u53ea\u662f\u5bf9\u65b9\u662f\u81ea\u5df1\u7684 \u5e08\u5ac2\uff0c\u800c\u81ea\u5df1\u5e74\u7eaa\u53c8\u592a\u5c0f\uff0c\u5f53\u7136\u4e0d\u6562\u6709\u4efb\u4f55\u8868\u793a\u3002\u53e6\u5916\uff0c\u4ed6\u53c8\u6697\u6697\u7231\u6155\u7740\u82d7\u7d20\u5170\uff0c\u4e5f\u662f\u56e0\u4e3a \u5f7c\u6b64\u5e74\u9f84\u76f8\u5dee\u592a\u591a\uff0e\u53ea\u80fd\u628a\u8fd9\u4efd\u76db\u610f\u85cf\u5728\u5fc3\u5e95\u3002 \u3000\u3000\u81f3\u4e8e\u82d7\u7d20\u82d3\uff0c\u56e0\u5bb9\u8c8c\u548c\u82d7\u7d20\u5170\u9887\u4e3a\u76f8\u4f3c\uff0c\u867d\u7136\u5e74\u9f84\u4e5f\u6bd4\u81ea\u5df1\u5927\uff0c\u5374\u5927\u4e0d\u4e86\u591a\u5c11\uff0c\u5982\u4eca\u5979 \u7adf\u4e3b\u52a8\u6295\u6000\u9001\u62b1\uff0c\u9ea6\u5c0f\u660e\u5c82\u6709\u4e0d\u52a8\u60c5\u4e4b\u7406\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u9ea6\u5c0f\u660e\u548c\u4e00\u822c\u98ce\u6d41\u6210\u6027\u7684\u7ea8\u7ed4\u5b50\u5f1f\u4e0d\u4e00\u6837\u3002\u5176\u4ed6\u65b9\u9762\uff0c\u4ed6\u4e00\u5411\u5929\u4e0d\u6015\u5730\u4e0d\u6015\uff0c\u6562 \u505a\u6562\u4e3a\uff0c\u552f\u72ec\u5bf9\u7537\u5973\u95f4\u4e4b\u4e8b\uff0e\u53cd\u800c\u5341\u5206\u62d8\u8c28\u542b\u84c4\uff0c\u8fd9\u4e5f\u6b63\u662f\u4ed6\u4ee4\u7fa4\u8c6a\u91cd\u89c6\u548c\u8d4f\u8bc6\u4e4b\u5904\u3002 \u3000\u3000\u8fd9\u662f\u4ed6\u6709\u751f\u4ee5\u6765\u7b2c\u4e00\u6b21\u548c\u5f02\u6027\u53d1\u751f\u5982\u6b64\u4eb2\u5bc6\u7684\u4e3e\u52a8\uff0c\u81ea\u662f\u6709\u79cd\u8bf4\u4e0d\u51fa\u7684\u611f\u53d7\u3002 \u3000\u3000\u6e10\u6e10\uff0c\u56db\u7247\u706b\u70ed\u7684\u5634\u5507\uff0c\u4e5f\u80f6\u5728\u4e00\u8d77\u3002 \u3000\u3000\u8bb8\u4e45\u8bb8\u4e45\uff0c\u4ed6\u624d\u8f7b\u8f7b\u63a8\u5f00\u82d7\u7d20\u82d3\u9053\uff1a\u201c\u6211\u4eec\u8fd8\u662f\u7ee7\u7eed\u8d76\u8def\u5427\uff01\u201d \u3000\u3000\u82d7\u7d20\u82d3\u4e5f\u662f\u7b2c\u2014\u6b21\u548c\u5f02\u6027\u6709\u7740\u5982\u6b64\u4eb2\u5bc6\u7684\u63a5\u89e6\uff0c\u8d77\u521d\u662f\u60c5\u4e0d\u81ea\u7981\uff0c\u5230\u8fd9\u65f6\u624d\u89c9\u51fa\u5a07\u7f9e\u96be \u5f53\u3002 \u3000\u3000\u597d\u5728\u662f\u591c\u95f4\uff0c\u82e5\u6362\u4e86\u767d\u5929\uff0c\u5979\u771f\u4f1a\u65e0\u5730\u81ea\u5bb9\u3002 \u3000\u3000\u6b63\u56e0\u5982\u6b64\uff0c\u5f53\u4ed6\u548c\u9ea6\u5c0f\u660e\u4e0d\u518d\u62e5\u62b1\u540e\uff0c\u7adf\u5446\u5728\u539f\u5730\uff0c\u4e45\u4e45\u4e0d\u52a8\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u50ac\u4fc3\u9053\uff1a\u201c\u82d7\u59d1\u5a18\uff0c\u600e\u4e48\u8fd8\u4e0d\u8d70\uff1f\u201d \u3000\u3000\u82d7\u7d20\u82d3\u5982\u68a6\u521d\u9192\uff0e\u4f46\u5374\u50cf\u5583\u5583\u81ea\u8bed\u7684\u9053\uff1a\u201c\u4f60\u4e0d\u8be5\u518d\u53eb\u6211\u82d7\u59d1\u5a18\u4e86\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u5fc3\u91cc\u4e00\u52a8\u9053\uff1a\u201c\u4f60\u60f3\u8981\u6211\u53eb\u4f60\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u4f60\u81ea\u5df1\u5fc3\u91cc\u6709\u6570\uff0e\u8fd8\u8981\u95ee\u6211\u5417\uff1f\u201d \u3000\u3000\u201c\u90a3\u5c31\u53eb\u4f60\u82d7\u59d0\u5427\uff01\u4e0d\u8fc7\u5728\u4eba\u591a\u7684\u5730\u65b9\uff0c\u8fd8\u662f\u79f0\u547c\u82d7\u59d1\u5a18\u7684\u597d\u3002\u201d \u3000\u3000\u201c\u7531\u4f60\u5427\uff01\u73b0\u5728\u6211\u5c31\u8ddf\u4f60\u8d70\u3002\u201d \u3000\u3000\u987b\u77e5\u82d7\u7d20\u82d3\u81ea\u5e7c\u4fbf\u88ab\u9634\u624b\u4e00\u9b54\u63a0\u8fdb\u5415\u6881\u5c71\u4e5d\u5929\u7384\u9634\u6d1e\u5e9c\uff0c\u6700\u521d\u8fd8\u6709\u59d0\u59d0\u7167\u5e94\uff0c\u81ea\u4ece\u82d7\u7d20 \u5170\u79bb\u5f00\u4e5d\u5929\u7384\u9634\u6d1e\u5e9c\u540e\uff0c\u8eab\u8fb9\u4fbf\u518d\u4e5f\u6ca1\u6709\u4eb2\u4eba\u3002\u867d\u7136\u9634\u624b\u4e00\u9b54\u4e5f\u5bf9\u5979\u5341\u5206\u7231\u62a4\uff0c\u4f46\u5374\u5bf9\u5979\u4e0d \u5b58\u597d\u5fc3\u3002 \u3000\u3000\u53e6\u5916\uff0e\u82d7\u7d20\u82d3\u81ea\u5c0f\u5230\u5927\uff0e\u4e0d\u4f46\u7edd\u5c11\u548c\u5f02\u6027\u63a5\u89e6\uff0c\u8fde\u770b\u5230\u7684\u7537\u4eba\uff0c\u4e5f\u51e0\u4e4e\u53ea\u6709\u9634\u624b\u2014\u9b54\u4e00 \u4e2a\u3002\u800c\u7b2c\u4e00\u4e2a\u63a5\u89e6\u7684\u5f02\u6027\u9ea6\u5c0f\u660e\uff0c\u4fbf\u662f\u90a3\u4e48\u82f1\u4fca\u6d12\u8131\u800c\u53c8\u6b66\u529f\u51fa\u4f17\uff0c\u5979\u53c8\u5982\u4f55\u80fd\u4e0d\u5fc3\u751f\u60c5\u612b\uff01 \u3000\u3000\u53ea\u542c\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u82d7\u59d0\uff0c\u4f60\u662f\u60f3\u6740\u4eba\u8fd8\u662f\u4e0d\u60f3\u6740\u4eba\uff1f\u201d \u3000\u3000\u82d7\u7d20\u82d3\u6697\u9053\uff1a\u201c\u7537\u4eba\u7684\u5fc3\u80a0\u597d\u72e0\uff0c\u521a\u4eb2\u70ed\u8fc7\u4e86\u5c31\u8981\u6740\u4eba\u2026\u2026\u201d \u3000\u3000\u5979\u8f7b\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u4f60\u8981\u6740\u8c01\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u5f53\u7136\u6307\u7684\u662f\u970d\u5143\u4f3d\u624b\u4e0b\u7684\u4eba\uff0c\u5982\u679c\u8981\u6740\uff0c\u4e00\u8def\u5173\u5361\u4e0a\u4fbf\u53ef\u6740\u4e0d\u5c11\u3002\u201d \u3000\u3000\u201c\u90a3\u5c31\u7531\u4f60\u4e86\u3002\u201d \u3000\u3000\u201c\u6211\u5f53\u7136\u60f3\u591a\u6740\u51e0\u4e2a\uff0e\u4f46\u5374\u5fc5\u987b\u5c3d\u91cf\u907f\u514d\u6740\u4eba\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u6211\u62c5\u5fc3\u4e07\u4e00\u6551\u4e0d\u51fa\u5927\u59d1\u5a18\uff0e\u6740\u4e86\u4ed6\u4eec\u7684\u4eba\uff0c\u5fc5\u5bf9\u5927\u59d1\u5a18\u4e0d\u5229\u3002\u201d \u3000\u3000\u201c\u8fd9\u8bdd\u6709\u7406\uff0c\u8fd8\u662f\u522b\u6740\u7684\u597d\u3002\u201d \u3000\u3000\u201c\u90a3\u5c31\u8981\u7ed5\u9053\u4ece\u8c37\u540e\u9053\u8def\u8fdb\u53bb\uff0e\u90a3\u8fb9\u7684\u5173\u5361\u8f83\u5c11\u3002\u201d \u3000\u3000\u201c\u597d\uff0c\u4f60\u600e\u6837\u8d70\uff0c\u6211\u5c31\u8ddf\u4f60\u600e\u6837\u8d70\u3002\u201d \u3000\u3000\u539f\u6765\u8fdb\u5165\u8ff7\u8e2a\u8c37\uff0c\u540e\u5c71\u53e6\u6709\u901a\u8def\uff0c\u90a3\u91cc\u56e0\u4e3a\u5168\u662f\u60ac\u5d16\u7edd\u9876\uff0c\u4eba\u8ff9\u7f55\u81f3\uff0e\u5916\u4eba\u7edd\u5c11\u77e5\u9053\u90a3 \u6761\u8def\uff0c\u56e0\u4e4b\uff0c\u4e00\u5411\u6212\u5907\u8f83\u4e3a\u677e\u6563\uff0c\u53ea\u662f\u4e24\u4eba\u82e5\u7531\u540e\u5c71\u8fdb\u8c37\uff0c\u81f3\u5c11\u8981\u591a\u7ed5\u5341\u51e0\u91cc\u8def\u3002 \u3000\u3000\u51b3\u5b9a\u8def\u7ebf\u4e4b\u540e\uff0e\u4e24\u4eba\u968f\u5373\u65bd\u5c55\u8f7b\u529f\uff0c\u8fc5\u901f\u65e0\u6bd4\u7684\u7ed5\u5f80\u540e\u5c71\u3002 \u3000\u3000\u6240\u8c13\u540e\u5c71\u53ea\u662f\u5728\u8ff7\u8e2a\u8c37\u7684\u540e\u65b9\u800c\u5df3\uff0c\u5176\u5b9e\u540e\u5c71\u4e4b\u540e\uff0c\u7167\u6837\u5c42\u5ce6\u53e0\u5d82\u4e0d\u65ad\uff0c\u82e5\u975e\u9ea6\u5c0f\u660e\u4ece \u524d\u662f\u8ff7\u8e2a\u8c37\u7684\u4eba\uff0c\u6839\u672c\u96be\u4ee5\u627e\u5230\u8def\u5f84\u3002 \u3000\u3000\u5230\u8fbe\u540e\u5c71\u5165\u53e3\u9644\u8fd1\uff0c\u82d7\u7d20\u82d3\u5ffd\u7136\u6307\u7740\u6570\u91cc\u5916\u7684\u4e00\u5ea7\u76f4\u7acb\u5ced\u62d4\u7684\u9ad8\u8702\u9053\uff1a\u201c\u90a3\u5ea7\u5c71\u597d\u9669\uff0c \u53ea\u6015\u6ca1\u4eba\u80fd\u6500\u767b\u5230\u5cf0\u9876\u5427\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u90a3\u91cc\u53eb\u96ea\u5cf0\uff0c\u5f53\u5e74\u80e1\u76df\u4e3b\u6b7b\u540e\uff0c\u5c31\u662f\u7531\u8c37\u76df\u4e3b\u548c\u949f\u526f\u76df\u4e3b\u628a\u4ed6\u7684\u9057\u672c\u80cc\u5230 \u5cf0\u9876\u57cb\u846c\u5728\u96ea\u5806\u91cc\u7684\u3002\u201d \u3000\u3000\u82d7\u7d20\u82d3\u60ca\u8d5e\u9053\uff1a\u201c\u597d\u4e86\u4e0d\u8d77\uff01\u8c37\u524d\u76df\u4e3b\u548c\u949f\u526f\u76df\u4e3b\u5c45\u7136\u80fd\u628a\u4e00\u4e2a\u4eba\u80cc\u5230\u5cf0\u9876\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u5176\u5b9e\u8c37\u524d\u76df\u4e3b\u90a3\u65f6\u5019\u7684\u6b66\u529f\u5e76\u4e0d\u5f88\u9ad8\uff0c\u5979\u662f\u56e0\u4e3a\u54c0\u75db\u4e08\u592b\u4e4b\u6b7b\uff0c\u4f24\u5fc3\u5230\u4e86 \u6781\u70b9\uff0c\u56e0\u4e4b\u4e5f\u5c31\u6fc0\u53d1\u51fa\u4e00\u79cd\u4e0d\u53ef\u601d\u8bae\u7684\u6f5c\u529b\u3002\u82e5\u5728\u5e73\u65f6\uff0c\u8fd9\u662f\u6839\u672c\u529e\u4e0d\u5230\u7684\u4e8b\u60c5\u3002\u201d \u3000\u3000\u201c\u4ed6\u4eec\u592b\u5987\u7684\u611f\u60c5\uff0c\u771f\u662f\u90a3\u4e48\u597d\u5417\uff1f\u201d \u3000\u3000\u201c\u6211\u4ece\u672a\u89c1\u8fc7\u592b\u5987\u611f\u60c5\u6709\u90a3\u4e48\u597d\u7684\u3002\u201d \u3000\u3000\u201c\u8fd9\u6837\u8bf4\u6765\u3002\u8c37\u524d\u76df\u4e3b\u5728\u4e08\u592b\u6b7b\u540e\uff0c\u53c8\u600e\u80fd\u6d3b\u5f97\u4e0b\u53bb\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u5979\u8981\u4e3a\u4e08\u592b\u62a5\u4ec7\uff0c\u5c31\u51ed\u8fd9\u80a1\u575a\u5f3a\u7684\u610f\u5ff5\uff0e\u624d\u7ef4\u6301\u4f4f\u5979\u7684\u751f\u547d\u3002\u7b49\u5230\u6570\u6708\u524d\u5979\u4e3a\u4e08 \u592b\u62a5\u8fc7\u5927\u4ec7\u540e\uff0c\u4fbf\u4ee5\u8eab\u76f8\u6b89\u3002\u201d \u3000\u3000\u201c\u4ee5\u8eab\u76f8\u6b89\uff1f\u8c37\u524d\u76df\u4e3b\u4e0d\u4e5f\u662f\u5728\u5c11\u6797\u3001\u6b66\u5f53\u4e24\u6d3e\u7684\u7fa4\u653b\u4e4b\u4e0b\u800c\u88ab\u6740\u6b7b\u7684\u5417\uff1f\u201d \u3000\u3000\u201c\u4e0d\uff01\u5979\u5f53\u65f6\u53ea\u662f\u8eab\u53d7\u91cd\u4f24\uff0c\u800c\u4e14\u5c11\u6797\u3001\u6b66\u5f53\u4e24\u6d3e\u4e5f\u90fd\u505c\u624b\u4e0d\u653b\uff0c\u5979\u672c\u6765\u8fd8\u53ef\u4ee5\u7597\u4f24\u7ee7 \u7eed\u6d3b\u4e0b\u53bb\uff0c\u4f46\u5979\u5374\u81ea\u523a\u5fc3\u80f8\u800c\u6b7b\uff0e\u201d \u3000\u3000\u82d7\u7d20\u82d3\u53ea\u542c\u5f97\u5927\u4e3a\u52a8\u5bb9\uff0c\u4e00\u9897\u82b3\u5fc3\u6fc0\u8361\u4e0d\u5df2\uff0c\u5e7d\u5e7d\u53f9\u606f\u4e00\u58f0\u9053\uff1a\u201c\u592a\u51c4\u60e8\u4e86\uff01\u5982\u679c\u8c37\u524d \u76df\u4e3b\u80fd\u6d3b\u5230\u73b0\u5728\uff0c\u8fd9\u5bf9\u6069\u7231\u592b\u59bb\uff0c\u5c82\u4e0d\u53c8\u53ef\u91cd\u65b0\u56e2\u805a\uff01\u201d \u3000\u3000\u6b63\u8bf4\u5230\u8fd9\u91cc\uff0c\u5ffd\u542c\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u5664\u58f0\uff01\u90a3\u8fb9\u597d\u50cf\u6709\u4eba\u3002\u201d \u5367\u9f99\u751f\u300a\u5999\u7edd\u5929\u9999\u300b \u7b2c\u4e8c\u5341\u56db\u56de\u3000\u8d74\u6c64\u8e48\u706b \u3000\u3000\u82d7\u7d20\u82d3\u51dd\u76ee\u671b\u53bb\uff0c\u679c\u7136\u6570\u4e08\u5916\u4e00\u682a\u5927\u6811\u4e0b\uff0e\u51fa\u73b0\u4e24\u4e2a\u4eba\u5f71\uff0c\u7acb\u5373\u4e5f\u4f4e\u58f0\u9053\uff1a\u201c\u54b1\u4eec\u4e5f\u8be5 \u4f2a\u88c5\u8d77\u6765\u5566\uff01\u201d \u3000\u3000\u539f\u6765\u82d7\u7d20\u82d3\u4ecd\u7740\u7537\u88c5\uff0c\u53ea\u662f\u5728\u672a\u53d1\u73b0\u60c5\u51b5\u524d\uff0e\u5e76\u672a\u7f69\u4e0a\u9762\u7eb1\u800c\u5df2\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u4e3a\u9632\u5bf9\u65b9\u8ba4\u51fa\uff0c\u4e5f\u51c6\u5907\u4e86\u4e00\u5757\u9ed1\u7eb1\u3002 \u3000\u3000\u4e8e\u662f\u4e24\u4eba\u8fde\u5fd9\u628a\u9ed1\u7eb1\u7f69\u4e0a\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4f60\u5148\u522b\u8fc7\u53bb\uff0c\u7b49\u6211\u5236\u670d\u4e86\u4ed6\u4eec\uff0c\u518d\u56de\u6765\u53eb\u4f60\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u7740\uff0c\u7ed5\u5411\u4e00\u4fa7\uff0c\u6084\u6084\u5411\u90a3\u5927\u6811\u63a5\u8fd1\u3002 \u3000\u3000\u5c82\u77e5\u4ed6\u4eec\u4e8c\u4eba\u5148\u524d\u7684\u8c08\u8bdd\u58f0\uff0c\u65e9\u88ab\u5bf9\u65b9\u542c\u89c1\uff0c\u8fd8\u6ca1\u8d70\u8fd1\uff0c\u6811\u4e0b\u4fbf\u6709\u4e00\u4eba\u559d\u9053\uff1a\u201c\u4ec0\u4e48 \u4eba\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u8fb9\u8d70\u8fb9\u7b54\u9053\uff1a\u201c\u81ea\u5df1\u4eba\uff01\u201d \u3000\u3000\u90a3\u4eba\u521a\u8981\u518d\u95ee\uff0c\u65e9\u88ab\u9ea6\u5c0f\u660e\u4e00\u6307\u70b9\u660f\u8fc7\u53bb\u3002 \u3000\u3000\u53e6\u4e00\u4eba\u4e00\u89c1\u4e0d\u5999\uff0c\u521a\u8981\u5f00\u6e9c\uff0e\u4e5f\u88ab\u9ea6\u5c0f\u660e\u4e00\u6307\u70b9\u5012\uff0c\u4e0d\u8fc7\u4ed6\u70b9\u7684\u53ea\u662f\u9ebb\u7a74\uff0c\u90a3\u55bd\u5175\u4ecd\u80fd \u5f00\u53e3\u8bf4\u8bdd\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u4e00\u4e2a\u7bad\u6b65\u8ddf\u8fc7\u53bb\uff0c\u957f\u5251\u76f4\u6307\u5230\u90a3\u55bd\u5175\u80f8\u53e3\u9053\uff1a\u201c\u4f60\u5982\u679c\u8fd8\u60f3\u6d3b\uff0c\u5c31\u522b\u53eb\u558a\u4e86\uff01\u201d \u3000\u3000\u90a3\u55bd\u5175\u5413\u5f97\u76f4\u6253\u54c6\u55e6\u9053\uff1a\u201c\u82f1\u96c4\u9976\u547d\uff0e\u5c0f\u7684\u7edd\u4e0d\u558a\u53eb\u3002\u201d \u3000\u3000\u201c\u6211\u73b0\u5728\u5f00\u59cb\u95ee\u8bdd\uff0c\u4f60\u8981\u4ece\u5b9e\u7b54\u590d\u3002\u201d \u3000\u3000\u201c\u82f1\u96c4\u53ea\u7ba1\u95ee\u3002\u201d \u3000\u3000\u201c\u7531\u8fd9\u91cc\u5230\u540e\u5be8\uff0c\u8fd8\u6709\u591a\u5c11\u5c97\u54e8\uff1f\u6709\u6ca1\u6709\u5de1\u591c\u7684\uff1f\u201d \u3000\u3000\u201c\u53ea\u6709\u5165\u8c37\u5904\u6709\u4e00\u9053\u5c97\u54e8\uff0c\u81f3\u4e8e\u5de1\u591c\u7684\uff0e\u4e0d\u2014\u5b9a\u6709\uff0e\u4e5f\u8bb8\u6709\uff0e\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u4e3a\u4e86\u4e89\u53d6\u65f6\u95f4\uff0c\u4e0d\u613f\u548c\u90a3\u55bd\u5175\u591a\u7f57\u55e6\uff0c\u4f4e\u559d\u9053\uff1a\u201c\u73b0\u5728\u7531\u4f60\u5728\u524d\u9762\u5e26\u8def\u8fdb\u8c37\uff0c\u82e5 \u9047\u5230\u5c97\u54e8\uff0c\u7531\u4f60\u62db\u547c\uff0c\u53ea\u8981\u5230\u8fbe\u5730\u7262\u9644\u8fd1\uff0c\u5c31\u6ca1\u4f60\u7684\u4e8b\u4e86\u3002\u201d \u3000\u3000\u90a3\u55bd\u5175\u8fde\u58f0\u9053\uff1a\u201c\u5c0f\u7684\u9075\u547d\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u968f\u5373\u5411\u540e\u62db\u4e86\u62db\u624b\uff0c\u793a\u610f\u82d7\u7d20\u82d3\u8fc7\u6765\u3002 \u3000\u3000\u5f85\u82d7\u7d20\u82d3\u5230\u8fbe\u540e\uff0c\u9ea6\u5c0f\u660e\u547d\u4ee4\u90a3\u55bd\u5175\u9053\uff1a\u201c\u73b0\u5728\u5c31\u8d70\uff0e\u8def\u4e0a\u4e0d\u51c6\u56de\u5934\u770b\uff0c\u5426\u5219\u5c0f\u5fc3\u4f60\u7684 \u72d7\u547d\uff01\u201d \u3000\u3000\u90a3\u55bd\u5175\u9053\uff1a\u201c\u53ef\u662f\u5c0f\u7684\u8d70\u4e0d\u52a8\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u63a2\u624b\u89e3\u5f00\u4ed6\u7684\u9ebb\u7a74\u3002 \u3000\u3000\u4e8e\u662f\u90a3\u55bd\u5175\u5728\u524d\uff0c\u9ea6\u5c0f\u660e\u548c\u82d7\u7d20\u82d3\u5728\u540e\uff0c\u5f00\u59cb\u5411\u8c37\u540e\u524d\u8fdb\u3002\u4e00\u8def\u4e0a\u679c\u7136\u9047\u5230\u4e24\u5904\u5c97\u54e8\uff0c \u7531\u4e8e\u90a3\u55bd\u5175\u719f\u6089\u53e3\u4ee4\u548c\u8054\u7edc\u4fe1\u53f7\uff0c\u5f88\u5bb9\u6613\u7684\u4fbf\u5e94\u4ed8\u8fc7\u53bb\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u548c\u82d7\u7d20\u82d3\u4e3a\u6050\u5bf9\u65b9\u751f\u7591\uff0c\u6bcf\u9047\u5230\u5c97\u54e8\u65f6\uff0c\u4fbf\u4e8b\u5148\u628a\u7f69\u9762\u9ed1\u7eb1\u9664\u53bb\uff0c\u56e0\u4e3a\u662f\u591c\u95f4\uff0c \u53cc\u65b9\u76f8\u9694\u53c8\u6709\u4e00\u6bb5\u8ddd\u79bb\uff0c\u5c97\u54e8\u4e0a\u7684\u55bd\u5175\uff0c\u81ea\u7136\u8ba4\u4e0d\u6e05\u9762\u76ee\u3002 \u3000\u3000\u5730\u7262\u6b63\u5728\u540e\u8c37\uff0e\u6765\u5230\u5730\u7262\u9644\u8fd1\uff0c\u9ea6\u5c0f\u660e\u628a\u90a3\u5e26\u8def\u7684\u55bd\u5175\u70b9\u660f\u8fc7\u53bb\uff0c\u5e76\u628a\u4ed6\u62d6\u5230\u9690\u5bc6\u5904\uff0c \u4ee5\u514d\u88ab\u5de1\u591c\u7684\u53d1\u73b0\u3002 \u3000\u3000\u201c\u516c\u5b50\u53ef\u719f\u6089\u5730\u7262\u5185\u60c5\u51b5\uff1f\u201d \u3000\u3000\u201c\u6211\u53ea\u77e5\u9053\u4f4d\u7f6e\uff0c\u5e76\u6ca1\u8fdb\u5165\u5730\u7262\u8fc7\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u65e9\u77e5\u9053\u5730\u7262\u5916\u4ecd\u6709\u4eba\u628a\u5b88\uff0c\u8bf4\u5b8c\u8bdd\uff0c\u4fbf\u6cbf\u7740\u5c71\u58c1\u8f7b\u8f7b\u5411\u524d\u8d70\u53bb\u3002 \u3000\u3000\u679c\u7136\uff0c\u5c31\u5728\u5730\u7262\u5165\u53e3\u5de6\u4fa7\uff0e\u6709\u2014\u540d\u55bd\u5175\u6b63\u5750\u5728\u5730\u4e0a\u6253\u76f9\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u8ddf\u8fc7\u53bb\u53c8\u628a\u90a3\u55bd\u5175\u70b9\u4e86\u7a74\u9053\uff0c\u968f\u624b\u518d\u62d6\u5230\u4e00\u89d2\u3002 \u3000\u3000\u8fd9\u65f6\u82d7\u7d20\u82d3\u5df2\u8ddf\u4e86\u4e0a\u6765\u3002 \u3000\u3000\u539f\u6765\u8fd9\u5730\u7262\u662f\u7531\u5c71\u58c1\u5f00\u51ff\u800c\u6210\uff0c\u6574\u4e2a\u5730\u7262\uff0c\u5168\u5728\u5c71\u58c1\u95f4\u3002 \u3000\u3000\u8fdb\u53e3\u5904\u662f\u4e00\u9053\u77f3\u95e8\uff0c\u8fdb\u5165\u4e4b\u540e\uff0c\u4fbf\u662f\u7531\u4e0a\u800c\u4e0b\u7684\u77f3\u7ea7\uff0c\u7531\u4e8e\u6697\u5f97\u4f38\u624b\u4e0d\u89c1\u4e94\u6307\uff0c\u4ece\u672a\u5230 \u8fc7\u7684\u4eba\uff0e\u8c01\u4e5f\u5f04\u4e0d\u6e05\u7a76\u7adf\u6709\u591a\u6df1\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u65bd\u5c55\u4f20\u97f3\u5165\u5bc6\u5185\u529f\u9053\uff1a\u201c\u8fd8\u662f\u7531\u6211\u8d70\u5728\u524d\u9762\uff0c\u82d7\u59d0\u8981\u968f\u65f6\u6ce8\u610f\u60c5\u51b5\u7a81\u53d8\uff01\u201d \u3000\u3000\u82d7\u7d20\u82d3\u9053\uff1a\u201c\u4f60\u4e5f\u8981\u5343\u4e07\u5c0f\u5fc3\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u4e00\u624b\u62a4\u80f8\uff0c\u2014\u624b\u4ed7\u5251\uff0c\u8e51\u8d77\u811a\u6b65\uff0c\u3000\u4e00\u7ea7\u4e00\u7ea7\u5411\u4e0b\u8d70\u53bb\u3002 \u3000\u3000\u8fd9\u6761\u77f3\u7ea7\u8def\u679c\u7136\u5f88\u957f\uff0c\u76f4\u8d70\u4e86\u767e\u4f59\u7ea7\u624d\u5230\u8fbe\u5e73\u5730\uff0e\u4e0d\u6d88\u8bf4\uff0c\u5230\u4e86\u5e73\u5730\uff0c\u4e5f\u5c31\u662f\u5730\u7262\u7684\u6240 \u5728\u4e86\u3002 \u3000\u3000\u4f30\u8ba1\u7740\u8fd9\u5730\u7262\u81f3\u5c11\u6df1\u5165\u5730\u4e0b\u4e94\u516d\u4e08\u6df1\uff0c\u5730\u9762\u6e7f\u6dcb\u6dcb\u7684\uff0c\u7b80\u76f4\u8981\u53d8\u6210\u6c34\u7262\u4e86\u3002 \u3000\u3000\u6b64\u523b\u82d7\u7d20\u82d3\u65e9\u5df2\u5f00\u59cb\u6fc0\u52a8\uff0e\u59d0\u59d0\u88ab\u56da\u7981\u5728\u8fd9\u79cd\u9634\u6697\u6f6e\u6e7f\u7684\u5730\u65b9\uff0c\u5982\u4f55\u80fd\u4e0d\u8ba9\u5979\u4f24\u5fc3\u3002 \u3000\u3000\u5730\u7262\u91cc\u5df2\u6709\u706f\u5149\uff0c\u7167\u89c1\u4e00\u540d\u55bd\u5175\u5750\u5728\u4e00\u5904\u77f3\u53f0\u4e0a\uff0c\u4e5f\u5728\u660f\u660f\u6b32\u7761\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u4e00\u773c\u5c31\u770b\u51fa\u8fd9\u540d\u55bd\u5175\u662f\u9609\u6389\u201c\u5b9d\u8d1d\u201d\u7684\u738b\u4e09\u3002 \u3000\u3000\u738b\u4e09\u539f\u662f\u88ab\u6d3e\u5728\u4e1c\u6d77\u9662\u7167\u987e\u8bb8\u5c0f\u65e6\u7684\uff0c\u5982\u4eca\u8c03\u4ed6\u6765\u770b\u5b88\u5730\u7262\uff0e\u8c01\u90fd\u60f3\u5f97\u5230\u970d\u5143\u4f3d\u7684\u7528\u610f\u3002 \u3000\u3000\u7531\u4e8e\u9ea6\u5c0f\u660e\u884c\u52a8\u65e0\u58f0\u65e0\u606f\uff0c\u8fdb\u5165\u5730\u7262\u4e4b\u540e\uff01\u738b\u4e09\u5c45\u7136\u4e0d\u66fe\u53d1\u89c9\u3002 \u3000\u3000\u76f4\u81f3\u9ea6\u5c0f\u660e\u6765\u5230\u8ddf\u524d\uff0c\u738b\u4e09\u521a\u8981\u558a\u53eb\uff0c\u9ea6\u5c0f\u660e\u7684\u5251\u5c16\u65e9\u62b5\u4f4f\u4e86\u4ed6\u7684\u54bd\u5589\uff0c \u3000\u3000\u6b64\u523b\u82d7\u7d20\u82d3\u548c\u9ea6\u5c0f\u660e\u65e9\u5df2\u7f69\u4e0a\u4e86\u8499\u9762\u9ed1\u7eb1\uff0c\u738b\u4e09\u53ea\u5413\u5f97\u8138\u8272\u53d1\u7eff\uff0e\u98a4\u58f0\u53eb\u9053\uff1a\u201c\u597d\u6c49\u9976 \u547d\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u8bf4\u8bdd\u4f4e\u58f0\u70b9\uff01\u592b\u4eba\u5728\u54ea\u91cc\uff1f\u5feb\u8bf4\uff01\u201d \u3000\u3000\u201c\u592b\u4eba\u5728\u2026\u2026\u5728\u94c1\u6805\u680f\u91cc\u9762\uff01\u201d \u3000\u3000\u8fd9\u5730\u7262\u751a\u662f\u72e1\u957f\uff0c\u3000\u4e00\u5171\u7528\u94c1\u6805\u680f\u9694\u6210\u56db\u4e94\u95f4\uff0c\u90a3\u94c1\u6805\u8db3\u6709\u62c7\u6307\u7c97\u7ec6\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u5411\u94c1\u6805\u671b\u53bb\uff0c\u53ea\u6709\u4e00\u95f4\u91cc\u9762\u6709\u4eba\u3002 \u3000\u3000\u53ea\u56e0\u706f\u5728\u5916\u9762\uff0c\u6545\u800c\u94c1\u6805\u5185\u751a\u662f\u660f\u6697\uff0c\u9690\u7ea6\u6216\u89c1\u9760\u58c1\u5904\u6709\u5f20\u77f3\u5e8a\uff0c\u77f3\u5e8a\u4e0a\u76f4\u633a\u633a\u7684\u8eba\u7740 \u4e00\u4e2a\u4eba\uff0c\u4f46\u5374\u65e0\u6cd5\u770b\u6e05\u9762\u76ee\uff0e \u3000\u3000\u82d7\u7d20\u82d3\u89c1\u6b64\u60c5\u5f62\uff0c\u518d\u4e5f\u7981\u4e0d\u4f4f\u5931\u58f0\u53eb\u9053\uff1a\u201c\u59d0\u59d0\u3001\u59d0\u59d0\u2026\u2026\u201d \u3000\u3000\u8c01\u77e5\u5979\u8fde\u53eb\u6570\u58f0\uff0c\u7adf\u7136\u6beb\u65e0\u53cd\u5e94\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u559d\u9053\uff1a\u201c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u83ab\u975e\u592b\u4eba\u5df2\u7ecf\u2026\u2026\u201d \u3000\u3000\u738b\u4e09\u6253\u7740\u51b7\u98a4\u9053\uff1a\u201c\u597d\u2026\u2026\u597d\u6c49\u522b\u6025\uff0c\u592b\u4eba\u662f\u88ab\u70b9\u4e86\u7a74\u9053\uff01\u201d \u3000\u3000\u201c\u662f\u4ec0\u4e48\u4eba\u70b9\u5979\u7684\u7a74\u9053\u7684\uff1f\u201d \u3000\u3000\u201c\u662f\u2026\u2026\u662f\u76df\u4e3b\u508d\u665a\u4eb2\u81ea\u6765\u8fc7\u3002\u201d \u3000\u3000\u8fd9\u65f6\u82d7\u7d20\u82d3\u5df2\u8feb\u4e0d\u53ca\u5f85\u5728\u5f00\u542f\u6805\u95e8\uff0c\u4f46\u5374\u59cb\u7ec8\u65e0\u6cd5\u6253\u5f00\u3002 \u3000\u3000\u738b\u4e09\u53eb\u9053\uff1a\u201c\u95e8\u5df2\u4e0a\u9501\uff0c\u94a5\u5319\u5728\u8fd9\u91cc\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u62a2\u8fc7\u94a5\u5319\uff0e\u987a\u624b\u628a\u738b\u4e09\u70b9\u4e86\u7a74\u3002 \u3000\u3000\u6253\u5f00\u94c1\u6805\uff0c\u9ea6\u5c0f\u660e\u518d\u4e3a\u82d7\u7d20\u5170\u89e3\u5f00\u7a74\u9053\u3002 \u3000\u3000\u82d7\u7d20\u5170\u4e4d\u89c1\u9762\u524d\u7ad9\u7740\u4e24\u4e2a\u8499\u9762\u4eba\uff0c\u4e0d\u89c9\u4e5f\u60ca\u53eb\u8d77\u6765\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u548c\u82d7\u7d20\u82d3\u8fd9\u624d\u60f3\u8d77\u9762\u7f69\u9ed1\u7eb1\uff0c\u8fde\u5fd9\u5404\u81ea\u53d6\u4e0b\u3002 \u3000\u3000\u82d7\u7d20\u5170\u5b9a\u4e86\u5b9a\u795e\uff0c\u4e0d\u89c9\u60ca\u559c\u9053\uff1a\u201c\u539f\u6765\u662f\u5c0f\u660e\u5144\u5f1f\uff0c\u90a3\u4e00\u4f4d\u662f\u8c01\uff1f\u201d \u3000\u3000\u82d7\u7d20\u82d3\u7acb\u5373\u6251\u5230\u82d7\u7d22\u5170\u8eab\u4e0a\u9053\uff1a\u201c\u59d0\u59d0\uff0c\u662f\u6211\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u62c5\u5fc3\u5979\u8ba4\u4e0d\u51fa\uff0c\u7d27\u8ddf\u7740\u9053\uff1a\u201c\u5979\u662f\u7d20\u82d3\u59d1\u5a18\uff0e\u5973\u626e\u7537\u88c5\uff01\u201d \u3000\u3000\u82d7\u7d20\u5170\u554a\u4e86\u4e00\u58f0\uff0c\u6025\u6025\u628a\u59b9\u59b9\u62e5\u8fdb\u6000\u91cc\uff0c\u4e24\u4eba\u4e45\u4e45\u8bf4\u4e0d\u51fa\u8bdd\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u50ac\u4fc3\u9053\uff1a\u201c\u8d35\u59d0\u59b9\u6709\u8bdd\u79bb\u5f00\u8ff7\u8e2a\u8c37\u518d\u53d9\u8c08\uff0c\u65f6\u95f4\u5b9d\u8d35\uff0e\u54b1\u4eec\u5fc5\u987b\u5feb\u8d70\uff01\u201d \u3000\u3000\u82d7\u7d20\u82d3\u677e\u5f00\u624b\u6765\u9053\uff1a\u201c\u59d0\u59d0\uff0c\u8fd8\u6709\u4ec0\u4e48\u8981\u5e26\u8d70\u7684\u4e1c\u897f\u6ca1\u6709\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4e0d\u5fc5\u95ee\u4e86\uff0c\u82d7\u5927\u59d0\u7eb5\u6709\u8d35\u91cd\u4e1c\u897f\uff0c\u970d\u5143\u4f3d\u4e5f\u4e0d\u53ef\u80fd\u8ba9\u5979\u5e26\u5230\u5730\u7262\u6765\u3002\u201d \u3000\u3000\u82d7\u7d20\u5170\u4e0b\u4e86\u5e8a\u9053\uff1a\u201c\u4f60\u4eec\u662f\u600e\u4e48\u6765\u7684\uff1f\u53ef\u662f\u5df2\u7ecf\u63a5\u5230\u6211\u7684\u4fe1\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4e0d\u9519\uff01\u4fe1\u662f\u674e\u8302\u8fde\u591c\u9001\u7ed9\u949f\u526f\u76df\u4e3b\u7684\u3002\u201d \u3000\u3000\u201c\u4f60\u4eec\u79bb\u5f00\u4e07\u6708\u5ce1\u53c8\u5230\u4e86\u54ea\u91cc\uff1f\u201d \u3000\u3000\u201c\u56de\u53bb\u4ee5\u540e\u4f60\u5c31\u77e5\u9053\u4e86\uff0c\u54b1\u4eec\u8d70\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u63a5\u7740\u4ea4\u5f85\u82d7\u7d20\u82d3\u9053\uff1a\u201c\u51fa\u4e86\u5730\u7262\u540e\u53ef\u80fd\u6709\u60c5\u51b5\uff0c\u4e8c\u59d1\u5a18\u8bf7\u4fdd\u62a4\u7740\u5927\u59d1\u5a18\uff0c\u7531\u6211\u5728 \u524d\u9762\u5f00\u8def\u3002\u201d \u3000\u3000\u4e09\u4eba\u5f00\u59cb\u7b49\u4e0a\u77f3\u7ea7\u3002 \u3000\u3000\u82d7\u7d20\u5170\u548c\u82d7\u7d20\u82d3\u5206\u522b\u6570\u5e74\uff0e\u5982\u4eca\u5f97\u80fd\u91cd\u9022\uff0c\u8c01\u90fd\u6709\u6ee1\u8154\u7684\u8bdd\u8981\u8bf4\uff0e\u4f46\u6b64\u65f6\u6b64\u5730\uff0c\u5374\u53c8\u5fc5 \u987b\u5f3a\u5fcd\u7740\u4e0d\u80fd\u5f00\u53e3\u3002 \u3000\u3000\u51fa\u4e86\u5730\u7262\uff0c\u5916\u9762\u5e76\u65e0\u4efb\u4f55\u52a8\u9759\uff0c\u9ea6\u5c0f\u660e\u548c\u82d7\u6c0f\u59d0\u59b9\u603b\u7b97\u7a0d\u7a0d\u653e\u4e0b\u5fc3\u6765\u3002 \u3000\u3000\u8c01\u77e5\u521a\u8d70\u51fa\u51e0\u6b65\uff0c\u5728\u524d\u65b9\u7684\u6811\u6797\u5185\uff0c\u7adf\u7136\u95ea\u51fa\u4e09\u6761\u4eba\u5f71\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u7b49\u4e09\u4eba\u987f\u65f6\u5927\u611f\u5931\u8272\uff0e\u56e0\u4e3a\u4ed6\u4eec\u5df3\u770b\u6e05\u8fd9\u4e09\u4eba\u8d6b\u7136\u662f\u970d\u5143\u4f3d\u548c\u5cad\u5357\u4e8c\u5947\u3002 \u3000\u3000\u597d\u5728\u6b64\u523b\u9ea6\u5c0f\u660e\u548c\u82d7\u7d20\u82d3\u53c8\u5df2\u7f69\u4e0a\u4e86\u9762\u7eb1\uff0c\u5bf9\u65b9\u65e0\u6cd5\u8ba4\u51fa\u8eab\u4efd\u3002 \u3000\u3000\u53e6\u5916\uff0c\u9ea6\u5c0f\u660e\u4e5f\u670d\u8fc7\u82d7\u7d20\u82d3\u6240\u8d60\u7684\u53d8\u97f3\u836f\uff0c\u5373\u4f7f\u5f00\u53e3\u8bf4\u8bdd\uff0e\u970d\u5143\u4f3d\u548c\u5cad\u5357\u4e8c\u5947\u4e5f\u4e0d\u53ef\u80fd \u542c\u51fa\u662f\u8c01\u3002 \u3000\u3000\u82e5\u4ee5\u9ea6\u5c0f\u660e\u548c\u82d7\u7d20\u82d3\u7684\u8f7b\u529f\uff0c\u970d\u5143\u4f3d\u548c\u5cad\u5357\u4e8c\u5947\u5f53\u7136\u56f0\u4e0d\u4f4f\u4ed6\u4eec\uff0e\u7136\u800c\u73b0\u5728\u5e26\u4e86\u4e00\u4e2a\u82d7 \u7d20\u5170\uff0c\u800c\u82d7\u7d20\u5170\u7684\u6b66\u529f\u672c\u6765\u5c31\u4e0d\u5982\u9ea6\u5c0f\u660e\u3001\u82d7\u7d20\u82d3\u4e8c\u4eba\u3002 \u3000\u3000\u518d\u52a0\u5979\u5728\u5730\u7262\u56da\u7981\u591a\u65e5\uff0c\u5df2\u75c5\u4f53\u6079\u6079\uff0c\u5982\u6b64\u2014\u6765\uff0c\u53c8\u600e\u80fd\u4e0d\u4f7f\u9ea6\u5c0f\u660e\u548c\u82d7\u7d20\u82d3\u5927\u8d77\u6050\u614c\u3002 \u3000\u3000\u81f3\u4e8e\u82d7\u7d20\u5170\uff0c\u66f4\u662f\u9a87\u6015\u83ab\u540d\uff0c\u5979\u81ea\u5fd6\u82e5\u4e00\u76f4\u88ab\u56da\u7981\u5728\u5730\u7262\uff0e\u8fd8\u4e0d\u4e00\u5b9a\u5fc5\u6b7b\u3002\u800c\u9003\u8131\u4e0d\u6210 \u518d\u88ab\u6349\u56de\u53bb\uff0c\u90a3\u5c31\u51f6\u591a\u5409\u5c11\u4e86\u3002\u8fd9\u6837\u770b\u6765\uff0e\u624e\u674e\u6797\u8f6c\u4fe1\u5411\u949f\u2014\u8c6a\u6c42\u6551\uff0c\u53cd\u800c\u662f\u5bb3\u4e86\u81ea\u5df1\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u548c\u82d7\u7d20\u82d3\u6599\u5b9a\u5e26\u7740\u82d7\u7d20\u5170\u96be\u4ee5\u7a81\u56f4\uff0e\u53ea\u597d\u505c\u4e0b\u811a\u6b65\u3002 \u3000\u3000\u8fd9\u65f6\u970d\u5143\u4f3d\u65e9\u5df2\u7387\u9886\u5cad\u5357\u4e8c\u5947\u903c\u8fd1\u8fc7\u6765\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u548c\u82d7\u7d20\u82d3\u8fd9\u65f6\u4e5f\u53d1\u89c9\uff0c\u9644\u8fd1\u6811\u6797\u5185\uff0c\u4f3c\u4e4e\u4ecd\u6709\u4e0d\u5c11\u4eba\u9690\u8eab\u5728\u6697\u5904\u3002 \u3000\u3000\u970d\u5143\u4f3d\u5927\u559d\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4e2a\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u4e0d\u52a8\u58f0\u8272\u9053\uff1a\u201c\u4f60\u53ef\u662f\u7eff\u6797\u76df\u4e3b\u970d\u5143\u4f3d\uff0c\u5728\u4e0b\u60f3\u5148\u95ee\u4f60\u4e00\u4ef6\u4e8b\uff1f\u201d \u3000\u3000\u201c\u4f60\u60f3\u95ee\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u8fd9\u5973\u5b50\u8bf4\u662f\u4f60\u7684\u538b\u5be8\u592b\u4eba\uff0e\u540d\u53eb\u82d7\u7d20\u5170\uff0c\u662f\u5417\uff1f\u201d \u3000\u3000\u201c\u83ab\u975e\u4f60\u8ba4\u8bc6\u5979\uff1f\u201d \u3000\u3000\u201c\u4e0d\u8ba4\u8bc6\u3002\u201d \u3000\u3000\u201c\u65e2\u7136\u4e0d\u8ba4\u8bc6\uff0c\u4e3a\u4ec0\u4e48\u8981\u628a\u5979\u52ab\u8d70\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u5728\u4e0b\u5f88\u540c\u60c5\u5979\u7684\u4e0d\u5e78\u906d\u9047\uff0e\u201d \u3000\u3000\u201c\u80e1\u8bf4\uff01\u8001\u592b\u7684\u592b\u4eba\uff0c\u8fd8\u7528\u4f60\u6765\u540c\u60c5\uff1f\u201d \u3000\u3000\u201c\u5c0a\u9a7e\u65e2\u7136\u627f\u8ba4\u5979\u662f\u4f60\u7684\u592b\u4eba\uff0c\u7231\u62a4\u8fd8\u6765\u4e0d\u53ca\uff0c\u4e3a\u4ec0\u4e48\u53cd\u800c\u628a\u5979\u6253\u8fdb\u5730\u7262\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u4e07\u60f3\u4e0d\u5230\u5bf9\u65b9\u4e0d\u4f46\u6765\u52ab\u7262\uff0c\u53cd\u800c\u66f4\u7406\u76f4\u6c14\u58ee\u8d28\u95ee\u8d77\u81ea\u5df1\u6765\uff0e\u8fd9\u7b49\u4e8e\u732a\u516b\u6212\u4e0b\u5c71\u4e00 \u5012\u6253\u4e00\u8019\uff0c\u4e0d\u89c9\u543c\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u4f60\u8fd8\u5c45\u7136\u8ba4\u4e3a\u81ea\u5df1\u6709\u7406\uff0c\u53cd\u800c\u8d28\u95ee\u8d77\u8001\u592b\u6765\u4e86\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u5728\u4e0b\u662f\u56e0\u4e3a\u770b\u4e0d\u8fc7\u53bb\uff0c\u6240\u4ee5\u624d\u6253\u5305\u4e0d\u5e73\u3002\u4e0d\u8fc7\u4f60\u653e\u5fc3\uff0c\u6211\u4eec\u628a\u5c0a\u592b\u4eba\u52ab\u51fa \u5730\u7262\u4ee5\u540e\uff0e\u4e00\u5b9a\u4f1a\u597d\u597d\u7167\u987e\u5979\uff0c\u76f4\u5230\u4f60\u77e5\u9053\u5fcf\u6094\u613f\u610f\u63a5\u7eb3\u5979\u65f6\uff0c\u518d\u628a\u5979\u9001\u56de\u6765\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u53ea\u6c14\u5f97\u8138\u4e0a\u9752\u7b4b\u66b4\u8d77\uff0c\u559d\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0e\u4e00\u6d3e\u80e1\u8a00\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4f60\u65e2\u7136\u79f0\u547c\u5728\u4e0b\u597d\u5c0f\u5b50\uff0c\u4e00\u5b9a\u662f\u8ba4\u4e3a\u5728\u4e0b\u662f\u4e2a\u597d\u4eba\u4e86\uff0c\u65e2\u7136\u6211\u4eec\u662f\u597d\u4eba\uff0c \u5c0a\u9a7e\u53c8\u4f55\u5fc5\u53d1\u8fd9\u4e48\u5927\u813e\u6c14\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u88ab\u6c14\u5f97\u8fde\u54b3\u4e86\u51e0\u58f0\uff0c\u8f6c\u8fc7\u5934\u9053\uff1a\u201c\u5df4\u8001\u5f1f\u3001\u5b8b\u8001\u5f1f\uff0c\u8fd9\u4e24\u4e2a\u5c0f\u5b50\u4e0d\u50cf\u67cf\u9f84\u9662\u7684\u4eba\uff0c \u4f60\u4eec\u53ef\u542c\u51fa\u4ed6\u7684\u58f0\u97f3\u50cf\u8c01\uff1f\u201d \u3000\u3000\u5df4\u5929\u4e49\u9053\uff1a\u201c\u4ed6\u4eec\u65e2\u7136\u6765\u52ab\u7262\uff0c\u5fc5\u5b9a\u8ddf\u592b\u4eba\u8ba4\u8bc6\uff0c\u4f55\u4e0d\u95ee\u95ee\u592b\u4eba\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u89c6\u7ebf\u518d\u8f6c\u5411\u88ab\u82d7\u7d20\u82d3\u7d27\u7d27\u633d\u4f4f\u7684\u82d7\u7d20\u5170\u9053\uff1a\u201c\u8d31\u5973\u4eba\uff0e\u4ed6\u4eec\u662f\u8c01\uff1f\u8bf4\uff01\u201d \u3000\u3000\u82d7\u7d20\u5170\u98a4\u58f0\u9053\uff1a\u201c\u59be\u8eab\u5e76\u4e0d\u8ba4\u8bc6\u4ed6\u4eec\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u6562\u9a97\u6211\uff1f\u5982\u679c\u4e0d\u8ba4\u8bc6\uff0c\u4ed6\u4eec\u4e3a\u4ec0\u4e48\u4f1a\u6765\u52ab\u7262\uff1f\u201d \u3000\u3000\u201c\u59be\u2026\u2026\u59be\u8eab\u4e0d\u77e5\u9053\uff01\u201d \u3000\u3000\u201c\u8fd8\u6562\u5634\u786c\uff01\u53cd\u6b63\u4f60\u5df2\u7ecf\u8dd1\u4e0d\u6389\uff0c\u5f85\u4f1a\u8001\u592b\u5bb0\u4e86\u8fd9\u4e24\u4e2a\u5c0f\u5b50\u4e4b\u540e\uff0c\u4e0d\u6015\u4f60\u4e0d\u8bf4\u5b9e\u8bdd\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u62c5\u5fc3\u82d7\u7d20\u5170\u8eab\u906d\u4e0d\u6d4b\uff0c\u3000\u4e00\u6025\u4e4b\u4e0b\uff0c\u8131\u53e3\u53eb\u9053\uff1a\u201c\u970d\u5143\u4f3d\uff0c\u5982\u679c\u6211\u4eec\u662f\u6765\u52ab\u8272 \u5462\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u5fc3\u5934\u4e00\u9707\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u4eec\u2026\u2026\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4e0d\u9519\uff01\u50cf\u5c0a\u592b\u4eba\u8fd9\u6837\u6f02\u4eae\u7684\u5973\u4eba\uff0c\u4f60\u65e2\u7136\u4e0d\u559c\u6b22\uff0e\u5c31\u522b\u602a\u6211\u4eec\u8981\u559c\u6b22\u5979\u3002\u201d \u3000\u3000\u53ea\u542c\u82d7\u7d20\u5170\u53f1\u9053\uff1a\u201c\u539f\u6765\u4f60\u4eec\u4e24\u4e2a\u662f\u5b58\u4e0b\u8fd9\u79cd\u5fc3\u6765\u52ab\u7262\u7684\uff0c\u5feb\u5feb\u653e\u5f00\u6211\uff0c\u6211\u60c5\u613f\u518d\u56de\u5230 \u5730\u7262\u53bb\uff0c\u4e5f\u4e0d\u80fd\u53d7\u4f60\u4eec\u4fae\u8fb1\uff0e\u201d \u3000\u3000\u970d\u5143\u4f3d\u4e5f\u8ddf\u7740\u559d\u9053\uff1a\u201c\u653e\u5f00\u5979\uff01\u201d \u3000\u3000\u8fd9\u65f6\u970d\u5143\u4f3d\u548c\u5cad\u5357\u4e8c\u5947\u65e9\u5df2\u624b\u6a2a\u5175\u5203\uff0e\u6b65\u6b65\u903c\u8fd1\u3002 \u3000\u3000\u84e6\u89c1\u82d7\u7d20\u82d3\u4e00\u5251\u62b5\u4f4f\u82d7\u7d20\u5170\u5fc3\u7a9d\uff0c\u51b7\u559d\u9053\uff1a\u201c\u4f60\u4eec\u54ea\u4e2a\u6562\u518d\u5411\u524d\u4e00\u6b65\uff0c\u5728\u4e0b\u5c31\u4e00\u5251\u628a\u8fd9 \u5973\u4eba\u523a\u6b7b\uff01\u201d \u3000\u3000\u82d7\u7d20\u82d3\u7684\u8fd9\u4e00\u5047\u610f\u884c\u52a8\uff0c\u679c\u7136\u6548\u529b\u5f88\u5927\uff0c\u903c\u5f97\u970d\u5143\u4f3d\u548c\u5cad\u5357\u4e8c\u5947\u8fd8\u771f\u7684\u4e0d\u6562\u8f7b\u4e3e\u5984\u52a8\u3002 \u3000\u3000\u5728\u8fd9\u671f\u95f4\uff0c\u9ea6\u5c0f\u660e\u82d7\u7d20\u82d3\u90fd\u5fc3\u5ff5\u7535\u8f6c\uff0c\u4ed6\u4eec\u90fd\u4e0d\u96be\u9876\u6599\uff0c\u4e8b\u5230\u5982\u4eca\uff0c\u60f3\u628a\u82d7\u7d20\u5170\u5e26\u8d70\uff0c \u6839\u672c\u5df2\u662f\u4e0d\u53ef\u80fd\u7684\u4e8b\u3002 \u3000\u3000\u6b64\u523b\u6240\u8981\u505a\u7684\uff0c\u4e0d\u5916\u5982\u4f55\u4e0d\u4f7f\u82d7\u7d20\u5170\u62c5\u8d1f\u4efb\u4f55\u8d23\u4efb\uff0c\u4e5f\u5c31\u662f\u8bf4\uff0c\u4e0d\u4f7f\u970d\u5143\u4f3d\u6000\u7591\u4e8e\u5979\uff0c \u4ee5\u4fbf\u4eca\u540e\u518d\u7ee7\u7eed\u8bbe\u6cd5\u8425\u6551\u3002 \u3000\u3000\u53ea\u542c\u970d\u5143\u4f3d\u9053\uff1a\u201c\u4f60\u4eec\u5230\u5e95\u653e\u4e0d\u653e\u5f00\u5979\uff1f\u201d \u3000\u3000\u82d7\u7d20\u5170\u4e5f\u968f\u5373\u55d4\u53f1\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4e2a\u4e0b\u6d41\u4e1c\u897f\uff0c\u5feb\u653e\u5f00\u6211\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u54fc\u4e86\u58f0\u9053\uff1a\u201c\u592b\u4eba\uff0e\u970d\u5143\u4f3d\u8fd9\u6837\u5bf9\u5f85\u4f60\uff0c\u96be\u9053\u4f60\u8fd8\u60f3\u518d\u56de\u5230\u4ed6\u7684\u8eab\u8fb9\uff1f\u201d \u3000\u3000\u82d7\u7d20\u5170\u5927\u58f0\u9053\uff1a\u201c\u4e0d\u7ba1\u4ed6\u5f85\u6211\u600e\u6837\uff0c\u65e2\u7136\u6211\u5df2\u7ecf\u505a\u4e86\u4ed6\u7684\u59bb\u5b50\uff0e\u5c31\u8be5\u4ece\u4e00\u800c\u7ec8\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u7acb\u5373\u4ee5\u4f20\u97f3\u5165\u5bc6\u4e4b\u672f\u9053\uff1a\u201c\u82d7\u5927\u59d0\uff0c\u770b\u6765\u5c0f\u5f1f\u548c\u4ee4\u59b9\u662f\u65e0\u6cd5\u6551\u4f60\u51fa\u9669\u4e86\uff0c\u4f60\u8981\u591a \u591a\u4fdd\u91cd\uff0c\u6211\u4eec\u4e00\u5b9a\u4f1a\u518d\u8bbe\u6cd5\u6551\u4f60\u7684\u3002\u201d \u3000\u3000\u82d7\u7d20\u5170\u4e5f\u4ee5\u4f20\u97f3\u4e4b\u672f\u9053\uff1a\u201c\u4f60\u4eec\u5feb\u5feb\u8bbe\u6cd5\u8131\u8eab\u8981\u7d27\uff0e\u770b\u6765\u970d\u5143\u4f3d\u77ed\u65f6\u5185\u8fd8\u4e0d\u81f4\u628a\u6211\u600e\u4f6f\uff0c \u4f60\u4eec\u5feb\u8d70\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u63a5\u7740\u9ad8\u58f0\u9053\uff1a\u201c\u970d\u5143\u4f3d\uff0c\u5728\u4e0b\u4eec\u8fd9\u6b21\u52ab\u7f8e\u4e0d\u6210\uff0c\u7b97\u4f60\u8d70\u72d7\u8fd0\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u543c\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0e\u4f60\u8fd8\u6562\u9a82\u8001\u592b\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4f60\u6709\u4e86\u5a07\u59bb\u4e0d\u77e5\u7231\u62a4\uff0c\u53cd\u628a\u5979\u6253\u8fdb\u5730\u7262\u3001\u6211\u5f53\u7136\u8981\u9a82\u4f60\u3002\u201d \u3000\u3000\u201c\u5feb\u653e\u5f00\u5979\uff01\u201d \u3000\u3000\u201c\u53ea\u8981\u4f60\u4eec\u7ad9\u8fdc\u4e9b\uff0c\u5728\u4e0b\u5c31\u653e\u4eba\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u53ea\u597d\u548c\u5cad\u5357\u4e8c\u5947\u9000\u51fa\u4e09\u4e08\u5f00\u5916\uff0e \u3000\u3000\u9ea6\u5c0f\u660e\u7acb\u5373\u5411\u82d7\u7d20\u82d3\u4f20\u97f3\u9053\uff1a\u201c\u4e8c\u59d1\u5a18\uff0e\u73b0\u5728\u5c31\u8d70\uff01\u201d \u3000\u3000\u82d7\u7d20\u82d3\u867d\u820d\u4e0d\u5f97\u653e\u5f00\u59d0\u59d0\uff0c\u5728\u76ee\u524d\u7684\u60c5\u5f62\u4e0b\uff0c\u5374\u53c8\u4e0d\u5f97\u4e0d\u653e\u3002 \u3000\u3000\u4ee5\u9ea6\u5c0f\u660e\u548c\u82d7\u7d20\u82d3\u7684\u8f7b\u529f\uff0c\u653e\u5f00\u82d7\u7d20\u5170\u540e\uff0c\u4e8c\u4e09\u4e2a\u7eb5\u8dc3\uff0e\u4fbf\u5df1\u63a0\u51fa\u5341\u51e0\u4e08\u8fdc\u3002 \u3000\u3000\u970d\u5143\u4f3d\u548c\u5cad\u5357\u4e8c\u5947\u54ea\u91cc\u8fd8\u8ffd\u8d76\u5f97\u4e0a\u3002 \u3000\u3000\u90a3\u4e9b\u57cb\u4f0f\u5728\u6811\u6797\u91cc\u7684\uff0c\u66f4\u662f\u671b\u5c18\u83ab\u53ca\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u548c\u82d7\u7d20\u82d3\u4e00\u53e3\u6c14\u7eb5\u51fa\u540e\u8c37\u4e4b\u5916\uff0c\u624d\u653e\u7f13\u811a\u6b65\u3002 \u3000\u3000\u82d7\u7d20\u82d3\u60f3\u8d77\u59d0\u59d0\uff0c\u59d0\u59b9\u60c5\u6df1\uff0c\u4e0d\u89c9\u53c8\u4f4e\u58f0\u996e\u6ce3\u8d77\u6765\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u529d\u6170\u9053\uff1a\u201c\u82d7\u59d0\u4e0d\u5fc5\u4f24\u5fc3\uff0c\u5927\u59d1\u5a18\u9664\u4e86\u53d7\u4e9b\u6d3b\u7f6a\uff0c\u4e00\u5b9a\u6ca1\u4e8b\u7684\u3002\u201d \u3000\u3000\u201c\u4f60\u600e\u4e48\u77e5\u9053\u6211\u59d0\u59d0\u4e00\u5b9a\u4f1a\u6ca1\u4e8b\uff1f\u201d \u3000\u3000\u201c\u970d\u5143\u4f3d\u5982\u679c\u8981\u6740\u5979\uff0e\u65e9\u5c31\u6740\u4e86\uff0c\u4f55\u5fc5\u6253\u8fdb\u5730\u7262\uff0c\u800c\u4e14\u4f9d\u521a\u624d\u7684\u60c5\u5f62\uff0c\u53ef\u4ee5\u770b\u51fa\u970d\u5143\u4f3d \u5bf9\u5927\u59d1\u5a18\u4f9d\u4f9d\u4e0d\u820d\uff0e\u201d \u3000\u3000\u201c\u4f60\u8fd9\u8bdd\u6709\u4ec0\u4e48\u6839\u636e\uff1f\u201d \u3000\u3000\u201c\u4ed6\u89c1\u6211\u4eec\u5047\u610f\u8981\u6740\u5927\u59d1\u5a18\uff0c\u4fbf\u663e\u51fa\u5f88\u60ca\u614c\u7684\u6a21\u6837\uff0c\u4e3a\u4e86\u5927\u59d1\u5a18\u80fd\u6d3b\u547d\uff0c\u8981\u4ed6\u9000\u540e\u4ed6\u5c31 \u9000\u540e\uff0c\u96be\u9053\u8fd8\u4e0d\u80fd\u8bc1\u660e\u5417\uff1f\u8fd8\u6709\u2026\u2026\u201d \u3000\u3000\u201c\u8fd8\u6709\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u4ed6\u627e\u4e86\u90a3\u6837\u4e00\u4e2a\u4eba\u770b\u5b88\u5730\u7262\uff0c\u4fbf\u662f\u62c5\u5fc3\u770b\u5b88\u5730\u7262\u7684\u4eba\u4f1a\u505a\u51fa\u975e\u4efd\u4e4b\u4e8b\u6765\u3002\u201d \u3000\u3000\u82d7\u7d20\u82d3\u832b\u7136\u9053\uff1a\u201c\u4f60\u8bb2\u8fd9\u8bdd\u6211\u4e0d\u61c2\uff0c\u90a3\u770b\u5b88\u5730\u7262\u7684\u2026\u2026\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u4e0d\u89c9\u8138\u4e0a\u4e00\u70ed\uff0c\u4eba\u5bb6\u662f\u4f4d\u59d1\u5a18\uff0c\u81ea\u5df1\u600e\u597d\u660e\u8bb2\uff0c\u987f\u4e86\u4e00\u987f\u9053\uff1a\u201c\u6211\u662f\u8bf4\u90a3\u770b\u5b88\u5730 \u7262\u7684\u53eb\u738b\u4e09\uff0c\u4ed6\u6700\u5927\u7684\u957f\u5904\u5c31\u662f\u4e0d\u597d\u8272\u3002\u201d \u3000\u3000\u82d7\u7d20\u82d3\u8d8a\u611f\u4e0d\u89e3\u9053\uff1a\u201c\u90a3\u4eba\u4e0d\u8fc7\u662f\u4e2a\u55bd\u5175\uff0c\u4ed6\u597d\u8272\u4e0d\u597d\u8272\uff0e\u4f60\u53c8\u600e\u4e48\u77e5\u9053\u7684\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u5e72\u54b3\u4e86\u58f0\u9053\uff1a\u201c\u54b1\u4eec\u4e0d\u8c08\u8fd9\u4e9b\uff0c\u603b\u4e4b\uff0c\u82d7\u59d0\u59d0\u653e\u5fc3\uff0e\u5927\u59d1\u5a18\u662f\u7edd\u5bf9\u4e0d\u4f1a\u6709\u4e8b\u7684\uff01\u201d \u3000\u3000\u4e24\u4eba\u8d70\u4e86\u5f88\u4e45\uff0c\u9ea6\u5c0f\u660e\u89c1\u82d7\u7d20\u82d3\u4e00\u76f4\u4e0d\u4e3b\u52a8\u5f00\u53e3\u8bb2\u8bdd\uff0e\u77e5\u5979\u8fd8\u5728\u60f3\u4e0d\u5f00\uff0c\u4fbf\u642d\u8baa\u9053\uff1a \u201c\u5176\u5b9e\u54b1\u4eec\u65b9\u624d\u5c3d\u53ef\u548c\u4ed6\u4eec\u5927\u5e72\u4e00\u573a\uff0c\u4f46\u5374\u53c8\u4e0d\u5f97\u4e0d\u5c3d\u91cf\u907f\u514d\u52a8\u624b\u3002\u201d \u3000\u3000\u83b3\u7d20\u82d3\u9053\uff1a\u201c\u53c8\u662f\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u66fe\u548c\u970d\u5143\u4f3d\u4ea4\u8fc7\u624b\uff0c\u82e5\u6253\u8d77\u6765\uff0c\u5c82\u4e0d\u88ab\u4ed6\u5bdf\u89c9\u51fa\u6211\u662f\u8c01\u4e86\uff1f\u9664\u975e\u6211 \u80fd\u6740\u4e86\u4ed6\uff01\u201d \u3000\u3000\u201c\u4f60\u548c\u4ed6\u7684\u6b66\u529f\uff0c\u7a76\u7adf\u8c01\u9ad8\uff1f\u201d \u3000\u3000\u201c\u970d\u5143\u4f3d\u6210\u540d\u6570\u5341\u5e74\uff0c\u65e9\u5c31\u662f\u88ab\u6b66\u6797\u516c\u8ba4\u7684\u7edd\u9879\u9ad8\u624b\uff0c\u6211\u53ea\u80fd\u4ee5\u62db\u672f\u51fa\u5947\u80dc\u4ed6\uff0c\u82e5\u6218\u4e45 \u4e86\uff0c\u53ef\u80fd\u4e0d\u662f\u4ed6\u7684\u5bf9\u624b\uff0c\u81f3\u5c11\u90a3\u8001\u5c0f\u5b50\u7684\u5185\u529f\u6bd4\u6211\u6df1\u5f97\u591a\u3002\u201d \u3000\u3000\u201c\u5f53\u521d\u4ed6\u8fd9\u76df\u4e3b\u662f\u600e\u4e48\u6765\u7684\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u662f\u8f83\u91cf\u6b66\u529f\u9ad8\u4e0b\u800c\u6765\u7684\u3002\u201d \u3000\u3000\u201c\u949f\u526f\u76df\u4e3b\u6253\u4e0d\u8fc7\u4ed6\uff1f\u201d \u3000\u3000\u201c\u770b\u6837\u5b50\u7a0d\u900a\u4e00\u7b79\uff0e\u4f46\u4e5f\u5dee\u4e0d\u4e86\u591a\u5c11\u3002\u201d \u3000\u3000\u4e24\u4eba\u8fb9\u8c08\u8fb9\u8d70\uff0e\u82d7\u7d20\u82d3\u5fc3\u60c5\u679c\u7136\u5df2\u597d\u8f6c\u4e86\u8bb8\u591a\u3002 \u3000\u3000\u56e0\u4e3a\u662f\u7531\u540e\u8c37\u4e4b\u5916\u7ed5\u9053\u800c\u884c\uff0e\u56de\u5230\u767d\u59d1\u5bfa\uff0c\u5df3\u662f\u4e94\u9f13\u5929\u660e\u4e86\u3002 \u3000\u3000\u521a\u4e00\u8fdb\u5bfa\uff0e\u4fbf\u89c1\u949f\u4e00\u8c6a\u7b49\u4eba\u90fd\u805a\u96c6\u5728\u5854\u524d\u7684\u9662\u843d\u91cc\uff0e\u6b63\u5728\u614c\u614c\u5f20\u5f20\uff0c\u8bae\u8bba\u4e0d\u4f11\u3002 \u3000\u3000\u4e00\u89c1\u9ea6\u5c0f\u660e\u548c\u82d7\u7d20\u82d3\u56de\u6765\uff0e\u949f\u4e00\u8c6a\u7acb\u5373\u95ee\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4f4d\u7a76\u7adf\u54ea\u91cc\u53bb\u4e86\uff1f\u53ef\u662f\u5230\u8ff7\u8e2a\u8c37 \u53bb\u8fc7\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4f60\u4eec\u77e5\u9053\u4e86\u5c31\u597d\u3002\u201d \u3000\u3000\u949f\u4e00\u8c6a\u731b\u8dfa\u4e00\u811a\u9053\uff1a\u201c\u8981\u8d70\u4e5f\u8be5\u5927\u5bb6\u5148\u5546\u8bae\u5546\u8bae\uff0e\u53ef\u77e5\u6211\u4eec\u8fd9\u4e9b\u4eba\u7d27\u5f20\u4e86\u5927\u534a\u591c\uff0c\u8c01\u90fd \u6ca1\u7761\u89c9\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u8038\u8038\u80a9\u9053\uff1a\u201c\u8ddf\u4f60\u4eec\u5546\u8bae\u90a3\u5c31\u8d70\u4e0d\u6210\u4e86\uff0c\u8fde\u6211\u5230\u8ff7\u8e2a\u8c37\u7684\u4eba\u90fd\u6ca1\u7d27\u5f20\uff0c\u4f60\u4eec\u7d27\u5f20 \u4ec0\u4e48\uff1f\u201d \u3000\u3000\u949f\u4e00\u8c6a\u53ea\u6c14\u5f97\u8138\u8272\u53d1\u7eff\uff0c\u51e0\u4e4e\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u4f59\u4ea6\u4e50\u5fd9\u9053\uff1a\u201c\u597d\u4e86\u597d\u4e86\uff0c\u65e2\u7136\u6ca1\u51fa\u4e8b\uff0c\u56de\u6765\u5c31\u597d\u4e86\u3002\u4f60\u8be5\u628a\u5230\u8ff7\u8e2a\u8c37\u7684\u60c5\u5f62\uff0e\u8bf4\u7ed9\u5927 \u5bb6\u542c\u542c\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u8bf4\u8fc7\u4e4b\u540e\uff0c\u4f59\u4ea6\u4e50\u518d\u9053\uff1a\u201c\u80fd\u56de\u6765\u5c31\u662f\u4e07\u5e78\u4e86\uff0c\u51ed\u4f60\u4eec\u4e24\u4e2a\u4eba\uff0c\u60f3\u6551\u51fa\u82d7\u5927\u59d1\u5a18\uff0e \u6839\u672c\u662f\u4e0d\u53ef\u80fd\u7684\u4e8b\u3002\u201d \u3000\u3000\u949f\u4e00\u8c6a\u8fd9\u624d\u53c8\u9053\uff1a\u201c\u4f60\u4eec\u4e8c\u4f4d\u8f9b\u82e6\u4e86\u4e00\u591c\uff0e\u5feb\u5feb\u53bb\u4f11\u606f\u5427\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u662f\u6211\u548c\u82d7\u59d1\u5a18\u8fde\u7d2f\u4f60\u4eec\u6ca1\u7761\u89c9\uff0e\u8fd8\u662f\u4f60\u4eec\u53bb\u4f11\u606f\u5427\uff01\u6211\u5230\u7b2c\u4e03\u5c42\u5854\u4e0a\u53bb\u8d1f \u8d23\u76d1\u89c6\u3002\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u9053\uff1a\u201c\u5929\u5df2\u7ecf\u4eae\u4e86\uff0c\u8fd8\u6709\u4ec0\u4e48\u597d\u4f11\u606f\u7684\uff0c\u5403\u4e86\u65e9\u996d\u518d\u8bf4\u5427\uff01\u201d \u3000\u3000\u53c8\u662f\u4e00\u8fde\u4e09\u5929\u8fc7\u53bb\uff0c\u5e76\u672b\u53d1\u751f\u4efb\u4f55\u4e8b\u60c5\u3002 \u3000\u3000\u7b2c\u56db\u5929\u4e2d\u5348\uff0c\u7fa4\u8c6a\u548c\u4f4f\u6301\u609f\u5c18\u6b63\u5728\u5bfa\u9662\u95f2\u8c08\uff0c\u5ffd\u7136\u6d3e\u5f80\u5c11\u6797\u62a5\u4fe1\u7684\u666e\u51e1\u56de\u6765\u4e86\u3002 \u3000\u3000\u609f\u5c18\u4e0d\u89c9\u4e00\u6123\u9053\uff1a\u201c\u600e\u4e48\u8fd9\u6837\u5feb\u5c31\u56de\u6765\u4e86\uff0c\u83ab\u975e\u8def\u4e0a\u51fa\u4e86\u4e8b\u60c5\uff1f\u201d \u3000\u3000\u666e\u51e1\u9053\uff1a\u201c\u5f1f\u5b50\u5728\u8def\u4e0a\u5c31\u9047\u5230\u4e86\u5c11\u6797\u672c\u5bfa\u7684\u4eba\uff0c\u628a\u4e8b\u60c5\u544a\u8bc9\u4e86\u4ed6\u4eec\uff0c\u5c31\u7528\u4e0d\u7740\u518d\u5230\u5d69\u5c71 \u4e86\u3002\u201d \u3000\u3000\u609f\u5c18\u6025\u6025\u95ee\u9053\uff1a\u201c\u4f60\u9047\u5230\u4e86\u54ea\u4e9b\u4eba\uff1f\u201d \u3000\u3000\u666e\u51e1\u9053\uff1a\u201c\u6709\u4e24\u4f4d\u5e08\u53d4\u7956\uff0c\u2014\u4f4d\u662f\u5929\u673a\u5e08\u53d4\u7956\uff0c\u2014\u4f4d\u662f\u5929\u89c9\u5e08\u53d4\u7956\uff0c\u53e6\u5916\u4ed6\u4eec\u8fd8\u5e26\u4e86\u5341 \u51e0\u540d\u5f1f\u5b50\u3002\u201d \u3000\u3000\u609f\u5c18\u5927\u559c\u9053\uff1a\u201c\u8fd9\u5c31\u597d\u4e86\uff0c\u4ed6\u4eec\u4e00\u5b9a\u4e5f\u662f\u67e5\u8bbf\u609f\u660e\u548c\u90a3\u4e5d\u540d\u94dc\u4eba\u800c\u6765\uff0c\u4f60\u5728\u4ec0\u4e48\u5730\u65b9\u9047 \u5230\u4ed6\u4eec\u7684\uff1f\u201d \u3000\u3000\u666e\u51e1\u9053\uff1a\u201c\u5c31\u5728\u539f\u5e73\u9644\u8fd1\u3002\u201d \u3000\u3000\u201c\u4ed6\u4eec\u662f\u5426\u8981\u5148\u5230\u54b1\u4eec\u767d\u59d1\u5bfa\u6765\uff1f\u201d \u3000\u3000\u201c\u4e24\u4f4d\u5e08\u53d4\u7956\u6ca1\u8bb2\u3002\u201d \u3000\u3000\u201c\u4f60\u5148\u56de\u53bb\u4f11\u606f\u5427\uff01\u201d \u3000\u3000\u609f\u5c18\u63a5\u7740\u5411\u7fa4\u8c6a\u9053\uff1a\u201c\u8fd9\u5c31\u597d\u4e86\uff0c\u8d2b\u50e7\u7684\u4e24\u4f4d\u5e08\u53d4\u65e2\u7136\u5df2\u77e5\u609f\u660e\u548c\u4e5d\u4e2a\u94dc\u4eba\u9003\u81f3\u8ff7\u8e2a\u8c37\uff0c \u5fc5\u5b9a\u4f1a\u5148\u5230\u655d\u5bfa\u6b47\u811a\uff0e\u7136\u540e\u518d\u91c7\u53d6\u884c\u52a8\u3002\u7b49\u4ed6\u4eec\u5230\u8fbe\u65f6\uff0e\u8d2b\u50e7\u518d\u4e0e\u5404\u4f4d\u5f15\u89c1\u3002\u201d \u3000\u3000\u7fa4\u8c6a\u95fb\u542c\u8fd9\u6d88\u606f\uff0c\u4e5f\u5927\u4e3a\u5174\u594b\uff0e\u4ed6\u4eec\u6b63\u6101\u65e0\u6cd5\u4e0e\u8ff7\u8e2a\u8c37\u65b9\u9762\u6297\u8861\uff0c\u5982\u4eca\u6709\u4e86\u4e5d\u5927\u95e8\u6d3e\u4e4b \u9996\u7684\u5c11\u6797\u52a0\u5165\uff0c\u7b49\u4e8e\u4e3a\u81ea\u5df1\u589e\u5f3a\u4e86\u529b\u91cf\u3002 \u3000\u3000\u5c31\u5728\u5f53\u65e5\u665a\u996d\u540e\uff0c\u5929\u673a\u3001\u5929\u89c9\u4e24\u4f4d\u5c11\u6797\u9ad8\u50e7\u679c\u7136\u7387\u9886\u5341\u4f59\u540d\u5f1f\u5b50\u6765\u5230\u767d\u59d1\u5bfa\u3002 \u3000\u3000\u609f\u5c18\u95fb\u62a5\uff0c\u7acb\u5373\u5e26\u7740\u666e\u51e1\u3001\u666e\u6e21\u3001\u666e\u6d4e\u81f3\u5bfa\u95e8\u5916\u606d\u8fce\uff0c\u4e00\u9762\u901a\u77e5\u949f\u4e00\u8c6a\u7b49\u5230\u7985\u5802\u76f8\u4f1a\u3002 \u3000\u3000\u5929\u673a\u3001\u5929\u89c9\u4e24\u4f4d\u5927\u5e08\u4e43\u5f53\u4eca\u5c11\u6797\u638c\u95e8\u5929\u7985\u5927\u5e08\u7684\u5e08\u5f1f\uff0c\u8eab\u4efd\u5730\u4f4d\u6781\u9ad8\uff0c\u4e00\u5411\u671b\u91cd\u6b66\u6797\u3002 \u3000\u3000\u4ed6\u4eec\u6240\u5e26\u9886\u7684\u5341\u51e0\u540d\u5f1f\u5b50\uff0c\u4e5f\u90fd\u662f\u7ecf\u7531\u7cbe\u9009\u7684\u9ad8\u624b\u3002 \u3000\u3000\u609f\u5c18\u606d\u8bf7\u5929\u673a\u3001\u5929\u89c9\u4e24\u4f4d\u5927\u5e08\u5728\u7985\u5802\u5750\u4e0b\uff0c\u4e00\u9762\u91cd\u65b0\u89c1\u793c\uff0c\u4e00\u9762\u4eb2\u81ea\u9012\u4e0a\u9999\u8317\u3002 \u3000\u3000\u90a3\u5341\u51e0\u540d\u5f1f\u5b50\uff0c\u5219\u5728\u7985\u5802\u5916\u4f11\u606f\u3002 \u3000\u3000\u4e24\u4f4d\u9ad8\u50e7\u4e2d\u4ee5\u5929\u673a\u5e74\u957f\uff0c\u53ea\u89c1\u4ed6\u9762\u8272\u51dd\u91cd\uff0c\u957f\u957f\u53f9\u606f\u4e00\u58f0\u9053\uff1a\u201c\u672c\u95e8\u4e0d\u5e78\uff0c\u7adf\u7136\u51fa\u4e86\u8fd9 \u79cd\u4e8b\u60c5\u3002\u8fd8\u597d\uff0c\u73b0\u5728\u603b\u7b97\u67e5\u660e\u4e86\u609f\u660e\u90a3\u53db\u9006\u548c\u90a3\u4e5d\u540d\u94dc\u4eba\u7684\u4e0b\u843d\u3002\u201d \u3000\u3000\u609f\u5c18\u8083\u7acb\u76f8\u966a\uff0c\u8eac\u8eab\u9053\uff1a\u201c\u4e24\u4f4d\u5e08\u53d4\u53ef\u662f\u4e3a\u8fd9\u4ef6\u4e8b\u4e13\u7a0b\u524d\u6765\u7684\uff1f\u201d \u3000\u3000\u5929\u673a\u5927\u5e08\u9894\u9996\u9053\uff1a\u201c\u4e0d\u9519\uff01\u81ea\u4ece\u8fd9\u4e0d\u5e78\u4e8b\u4ef6\u53d1\u751f\u540e\uff0c\u638c\u95e8\u5e08\u5144\u65e5\u591c\u96be\u5b89\uff0c\u7acb\u5373\u5175\u5206\u56db\u8def\uff0c \u67e5\u8bbf\u609f\u660e\u548c\u4e5d\u540d\u94dc\u4eba\u4e0b\u843d\u3002 \u3000\u3000\u611a\u5e08\u53d4\u548c\u4f60\u5929\u89c9\u5e08\u53d4\u8d1f\u8d23\u5317\u8def\uff0c\u56e0\u4e3a\u6599\u60f3\u5230\u4ed6\u4eec\u53ef\u80fd\u9003\u5f80\u8ff7\u8e2a\u8c37\uff0c\u6240\u4ee5\u2014\u8def\u4fbf\u5f80\u5317\u5cb3\u65b9 \u5411\u8d76\u6765\u3002\u679c\u7136\u4e0d\u51fa\u6240\u6599\uff0c\u5c31\u5728\u539f\u5e73\u9644\u8fd1\u9047\u5230\u4e86\u4f60\u6d3e\u5f80\u5c11\u6797\u672c\u5bfa\u62a5\u4fe1\u7684\u666e\u51e1\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e8b\u5f1f\u5b50\u4e5f\u662f\u51e0\u5929\u524d\u624d\u77e5\u9053\u7684\u3002\u201d \u3000\u3000\u201c\u666e\u51e1\u5df2\u5bf9\u611a\u5e08\u53d4\u8bf4\u8fc7\uff0c\u76ee\u524d\u4f60\u8fd9\u91cc\u4f4f\u4e86\u4e0d\u5c11\u539f\u5c5e\u8ff7\u8e2a\u8c37\u7684\u4eba\uff1f\u201d \u3000\u3000\u201c\u4e0d\u9519\uff01\u4ed6\u4eec\u90fd\u662f\u8ff7\u8e2a\u8c37\u80e1\u524d\u76df\u4e3b\u7684\u4eba\uff0c\u56e0\u4e3a\u4e0d\u80fd\u89c1\u5bb9\u4e8e\u73b0\u4efb\u76df\u4e3b\u970d\u5143\u4f3d\uff0c\u4fbf\u79bb\u5f00\u8c37\u5230 \u4e86\u4e07\u6708\u5ce1\uff0c\u518d\u7531\u4e07\u6708\u5ce1\u6765\u5230\u8fd9\u91cc\u3002\u201d \u3000\u3000\u201c\u8fd9\u6837\u8bf4\u6765\u5c31\u8be5\u628a\u4ed6\u4eec\u8bf7\u6765\u4e00\u89c1\u3002\u201d \u3000\u3000\u201c\u5f1f\u5b50\u5df2\u901a\u77e5\u8fc7\u4ed6\u4eec\uff0c\u4ed6\u4eec\u9a6c\u4e0a\u5c31\u6765\u3002\u201d \u3000\u3000\u6b63\u8bf4\u8bdd\u95f4\uff0c\u949f\u4e00\u8c6a\u3001\u4f59\u4ea6\u4e50\u3001\u9ea6\u5c0f\u660e\u3001\u82d7\u7d20\u82d3\u5df2\u5306\u5306\u8d70\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u7b49\u9664\u4e86\u82d7\u7d20\u82d3\u5916\uff0c\u4ece\u524d\u5df2\u548c\u5929\u89c9\u5927\u5e08\u89c1\u8fc7\uff0c\u548c\u5929\u673a\u5927\u5e08\u5374\u662f\u7b2c\u2014\u6b21\u6709\u7f18\u76f8\u4f1a\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u3001\u4f59\u4ea6\u4e50\u7b49\u90fd\u662f\u62f1\u624b\u4e3a\u793c\u3002 \u3000\u3000\u82d7\u7d20\u82d3\u5219\u88e3\u887d\u4e3a\u793c\u3002 \u3000\u3000\u5929\u673a\u5927\u5e08\u5fae\u5fae\u4e00\u6014\u9053\uff1a\u201c\u8fd9\u5973\u5c0f\u65bd\u4e3b\u2026\u2026\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u62a2\u7740\u4ee3\u7b54\u9053\uff1a\u201c\u5979\u662f\u82d7\u59d1\u5a18\uff0c\u6570\u6708\u524d\u5728\u5415\u6881\u5c71\uff0c\u8d35\u6d3e\u638c\u95e8\u66ff\u5ce8\u5d4b\u638c\u95e8\u6536\u4e3a\u8bb0\u540d\u5f1f \u5b50\u3002\u201d \u3000\u3000\u5929\u673a\u5927\u5e08\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u4e0d\u9519\uff01\u672c\u6d3e\u638c\u95e8\u5e08\u5144\u5df2\u5bf9\u8001\u8872\u63d0\u8d77\u8fc7\uff0c\u8bf4\u5979\u539f\u662f\u9634\u624b\u4e00\u9b54\u7684\u95e8\u535c\uff0c \u5bf9\u5417\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u5979\u867d\u662f\u9634\u624b\u4e00\u9b54\u7684\u95e8\u4e0b\uff0c\u4f46\u5374\u65e9\u5df2\u5f03\u6697\u6295\u660e\u4e86\u3002\u201d \u3000\u3000\u5929\u673a\u5927\u5e08\u6b49\u7136\u4e00\u7b11\u9053\uff1a\u201c\u662f\u8001\u8872\u5931\u8a00\uff0c\u4e0d\u8be5\u518d\u63d0\u9634\u624b\u4e00\u9b54\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u518d\u9053\uff1a\u201c\u82d7\u59d1\u5a18\u6240\u4ee5\u8981\u6c42\u62dc\u89c1\u4e8c\u4f4d\u5927\u5e08\uff0c\u9664\u4e86\u4e24\u4f4d\u5927\u5e08\u4fa0\u540d\u8fdc\u64ad\u5916\uff0c\u4fbf\u662f\u5e0c\u671b\u4e24 \u4f4d\u5927\u5e08\u80fd\u5e26\u5979\u5230\u8ff7\u8e2a\u8c37\u53bb\u3002\u201d \u3000\u3000\u5929\u673a\u5927\u5e08\u53c8\u662f\u4e00\u6123\u9053\uff1a\u201c\u5979\u4e3a\u4ec0\u4e48\u53c8\u8981\u5230\u8ff7\u8e2a\u8c37\u53bb\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u5b9e\u5bf9\u4e24\u4f4d\u5927\u5e08\u8bf4\uff0c\u82d7\u59d1\u5a18\u6709\u4e2a\u59d0\u59d0\uff0c\u88ab\u970d\u5143\u4f3d\u6536\u505a\u538b\u5be8\u592b\u4eba\uff0c\u56e0\u5979\u5fc3\u5411\u6211 \u4eec\uff0c\u970d\u5143\u4f3d\u5df2\u628a\u5979\u6253\u5165\u5730\u7262\uff0e \u3000\u3000\u524d\u51e0\u5929\u591c\u91cc\u665a\u8f88\u66fe\u548c\u82d7\u59d1\u5a18\u4e00\u8d77\u53bb\u52ab\u7262\uff0e\u6ca1\u52ab\u6210\u3002\u73b0\u5728\u4e24\u4f4d\u5927\u5e08\u6765\u4e86\uff0c\u5fc5\u5b9a\u4f1a\u8fdb\u8ff7\u8e2a\u8c37 \u53bb\u6349\u56de\u8d35\u6d3e\u7684\u53db\u5f92\u609f\u660e\u548c\u4e5d\u540d\u94dc\u4eba\uff0c\u6240\u4ee5\u5979\u624d\u6253\u7b97\u968f\u4e24\u5927\u5e08\u5230\u8ff7\u8e2a\u8c37\u53bb\u3002\u201d \u3000\u3000\u949f\u4e00\u8c6a\u63a5\u7740\u4e5f\u628a\u7fa4\u8c6a\u56e0\u4f55\u9003\u51fa\u8ff7\u8e2a\u8c37\uff0c\u4ee5\u53ca\u970d\u5143\u4f3d\u7387\u4f17\u5230\u4e07\u6708\u5ce1\u7684\u7ecf\u8fc7\u8be6\u8ff0\u4e86\u4e00\u904d\u3002 \u3000\u3000\u5929\u673a\u5927\u5e08\u6c89\u541f\u4e86\u534a\u664c\u9053\uff1a\u201c\u8001\u8872\u548c\u5929\u89c9\u5e08\u5f1f\u8fdb\u8ff7\u8e2a\u8c37\u662f\u5fc5\u7136\u7684\uff0c\u82e5\u5e26\u7740\u4e00\u4e2a\u5973\u5a03\u513f\uff0c\u53ea \u6015\u6709\u4e9b\u4e0d\u65b9\u4fbf\uff0e\u201d \u3000\u3000\u5929\u89c9\u5927\u5e08\u63a5\u9053\uff1a\u201c\u8001\u8872\u4eec\u8fdb\u5165\u8ff7\u8e2a\u8c37\uff0c\u4e0d\u5916\u662f\u628a\u609f\u660e\u548c\u4e5d\u540d\u94dc\u4eba\u62bc\u56de\u5c11\u6797\u6309\u5f8b\u60e9\u5904\uff0c\u82e5 \u60f3\u628a\u8fd9\u5973\u5a03\u513f\u7684\u59d0\u59d0\u63a5\u51fa\u8ff7\u8e2a\u8c37\uff0c\u90a3\u5c31\u540d\u4e0d\u6b63\u8a00\u4e0d\u987a\u4e86\u3002\u201d \u3000\u3000\u5929\u673a\u5927\u5e08\u9053\uff1a\u201c\u5e08\u5f1f\u8bf4\u5f97\u662f\uff0c\u54b1\u4eec\u51fa\u5bb6\u4eba\u81ea\u662f\u4e0d\u8be5\u63d2\u624b\u672c\u8eab\u4ee5\u5916\u7684\u4e8b\u3002\u201d \u3000\u3000\u82d7\u7d20\u82d3\u5fc3\u5ff5\u59d0\u59d0\u5b89\u5371\uff0c\u82b3\u5fc3\u5927\u6025\uff0c\u7acb\u5373\u76c8\u76c8\u62dc\u5012\u9053\uff1a\u201c\u6c42\u4e8c\u4f4d\u5927\u5e08\u6210\u5168\uff0c\u5f1f\u5b50\u53ea\u6c42\u80fd\u8fdb \u8c37\u63a2\u542c\u5bb6\u59d0\u7684\u6d88\u606f\uff0c\u4e0d\u6562\u5962\u6c42\u522b\u7684\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u4e5f\u6b63\u5bb9\u9053\uff1a\u201c\u51fa\u5bb6\u4eba\u6148\u60b2\u4e3a\u6000\uff0e\u4f60\u4eec\u4e8c\u4f4d\u5927\u5e08\u5c31\u505a\u505a\u597d\u4e8b\u5427\uff01\u201d \u3000\u3000\u5929\u673a\u5927\u5e08\u679c\u7136\u4f3c\u662f\u4e8e\u5fc3\u4e0d\u5fcd\uff0c\u9ed8\u4e86\u4e00\u9ed8\uff0c\u62ac\u624b\u4e00\u62db\u9053\uff1a\u201c\u4f60\u8d77\u6765\uff0c\u5bb9\u8001\u8872\u659f\u914c\u659f\u914c\u518d \u8bf4\u3002\u201d \u3000\u3000\u5929\u89c9\u5927\u5e08\u5fd9\u9053\uff1a\u201c\u5e08\u5144\uff0c\u5e26\u4e00\u4e2a\u5973\u5a03\u513f\u8fdb\u8c37\uff0c\u603b\u662f\u4e0d\u5b9c\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff0c\u201c\u6ca1\u5173\u7cfb\uff0c\u5979\u53ef\u4ee5\u5973\u626e\u7537\u88c5\u3002\u201d \u3000\u3000\u5929\u89c9\u5927\u5e08\u76b1\u7709\u9053\uff1a\u201c\u53ef\u662f\u5979\u6709\u5934\u53d1\u2026\u2026\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u7b11\u9053\uff1a\u201c\u5927\u5e08\u600e\u4e48\u8fd9\u6837\u6b7b\u5fc3\u773c\uff0c\u96be\u9053\u5c11\u6797\u5c31\u6ca1\u6709\u4fd7\u5bb6\u5f1f\u5b50\uff1f\u201d \u3000\u3000\u5929\u89c9\u5927\u5e08\u9053\uff1a\u201c\u53ef\u662f\u970d\u5143\u4f3d\u539f\u5c31\u8ba4\u8bc6\u5979\uff0c\u6362\u4e86\u7537\u88c5\uff0c\u4e5f\u7792\u4e0d\u8fc7\u8ff7\u8e2a\u8c37\u7684\u4eba\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u5979\u8fdb\u5165\u8ff7\u8e2a\u8c37\u7684\u65f6\u95f4\u5f88\u77ed\uff0c\u800c\u4e14\u4ece\u672a\u516c\u5f00\u9732\u9762\u3002\u8ff7\u8e2a\u8c37\u7684\u4eba\u6839\u672c\u5c31\u4e0d\u77e5\u9053 \u5979\u662f\u8c01\uff0e\u4f55\u51b5\u2026\u2026\u201d \u3000\u3000\u201c\u5c0f\u65bd\u4e3b\u8fd8\u6709\u4ec0\u4e48\u4f55\u51b5\u7684\uff1f\u201d \u3000\u3000\u201c\u665a\u8f88\u662f\u8bf4\u5979\u7684\u6b66\u529f\u5f88\u9ad8\uff0c\u4e07\u4e00\u8d35\u6d3e\u5728\u8ff7\u8e2a\u8c37\u548c\u970d\u5143\u4f3d\u51b2\u7a81\u8d77\u6765\uff0c\u6709\u4e86\u5979\uff0c\u4e5f\u7b49\u4e8e\u589e\u52a0 \u4e86\u4e00\u4efd\u529b\u91cf\u3002\u201d \u3000\u3000\u5929\u89c9\u5927\u5e08\u4f3c\u662f\u4e5f\u88ab\u8bf4\u52a8\uff0c\u4e0d\u518d\u8a00\u8bed\u3002 \u3000\u3000\u7985\u5802\u5185\u6c89\u5bc2\u4e86\u534a\u664c\uff0c\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u4e24\u4f4d\u5927\u5e08\u51c6\u5907\u4ec0\u4e48\u65f6\u5019\u8fdb\u5165\u8ff7\u8e2a\u8c37\uff1f\u201d \u3000\u3000\u5929\u673a\u5927\u5e08\u9053\uff1a\u201c\u8001\u8872\u548c\u5929\u89c9\u5e08\u5fc5\u987b\u6b63\u5927\u5149\u660e\u7684\u8fdb\u5165\u8ff7\u8e2a\u8c37\u8981\u4eba\uff0c\u81ea\u7136\u65f6\u95f4\u8981\u9009\u5728\u767d\u5929\uff0e \u4eca\u5929\u5929\u8272\u5df1\u665a\uff0c\u90a3\u5c31\u660e\u5929\u4e00\u65e9\u51fa\u53d1\u5427\uff01\u201d \u3000\u3000\u949f\u2014\u8c6a\u9053\uff1a\u201c\u5982\u679c\u4e8c\u4f4d\u5927\u5e08\u7528\u5f97\u7740\u665a\u8f88\u4eec\u8fd9\u4e9b\u4eba\uff0e\u53ea\u7ba1\u5f00\u53e3\uff0c\u665a\u8f88\u4eec\u8d76\u6c64\u8e48\u706b\uff0e\u5728\u6240\u4e0d \u8f9e\uff01\u201d \u3000\u3000\u5929\u673a\u5927\u5e08\u5408\u638c\u8bf5\u4e86\u58f0\u4f5b\u53f7\u9053\uff1a\u201c\u949f\u65bd\u4e3b\u8fd9\u756a\u76db\u60c5\uff0c\u4ee4\u8001\u8872\u611f\u52a8\u3002\u53ea\u662f\u8001\u8872\u8fd9\u6b21\u884c\u52a8\uff0c\u662f \u5c11\u6797\u7684\u5bb6\u52a1\u4e8b\uff0c\u82e5\u6709\u5916\u4eba\u53c2\u4e0e\uff0c\u53cd\u800c\u5f04\u5de7\u6210\u62d9\uff0c\u8fd9\u662f\u8001\u8872\u4e0d\u5f97\u4e0d\u987e\u8651\u7684\u3002\u201d \u3000\u3000\u4f17\u4eba\u53c8\u8c08\u4e86\u4e00\u4f1a\u513f\uff0c\u4fbf\u5404\u81ea\u6563\u53bb\u3002 \u3000\u3000\u7fa4\u8c6a\u56de\u5230\u5854\u4e0a\uff0c\u5d02\u5c71\u4e09\u96c4\u3001\u6c5f\u5317\u4e09\u9f99\u3001\u6587\u5929\u751f\u7b49\u4eba\u6b63\u5728\u7b2c\u4e8c\u5c42\u5854\u7b49\u5019\u6d88\u606f\u3002 \u3000\u3000\u5f53\u4ed6\u4eec\u5f97\u77e5\u65b9\u624d\u4f1a\u8c08\u7684\u60c5\u5f62\u540e\uff0c\u738b\u5927\u5eb7\u9053\uff1a\u201c\u4ed6\u5976\u5976\u7684\uff0c\u4ffa\u4ee5\u4e3a\u54b1\u4eec\u53ef\u4ee5\u8ddf\u4ed6\u4eec\u8054\u5408\u8d77 \u6765\uff0c\u6765\u4e2a\u76f4\u6363\u8ff7\u8e2a\u8c37\uff0c\u8bf4\u4e0d\u5b9a\u4e00\u4e0b\u5b50\u5c31\u53ef\u628a\u970d\u5143\u4f3d\u90a3\u738b\u516b\u86cb\u6d88\u706d\uff0c\u8c01\u60f3\u4ed6\u4eec\u8fd9\u4e9b\u548c\u5c1a\u5374\u53c8\u987e \u524d\u987e\u540e\uff0c\u5b9e\u5728\u8981\u4e0d\u5f97\uff01\u201d \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u8bdd\u4e0d\u80fd\u8fd9\u4e48\u8bf4\uff0c\u8fd9\u662f\u4e24\u56de\u4e8b\uff0c\u600e\u53ef\u6df7\u4e3a\u4e00\u8c08\uff0c\u5c11\u6797\u662f\u5149\u660e\u6b63\u5927\u95e8\u6d3e\uff0c\u4eba\u5bb6 \u7edd\u4e0d\u80af\u80e1\u6765\uff0e\u201d \u3000\u3000\u738b\u5927\u5eb7\u9053\uff1a\u201c\u597d\u4e86\uff0c\u4ffa\u4e0d\u8bb2\u4e86\u3002\u4e0d\u8fc7\uff0c\u4ffa\u5012\u5e0c\u671b\u80fd\u8ddf\u82d7\u59d1\u5a18\u4e00\u6837\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u7b11\u9053\uff0c\u201c\u4f60\u738b\u5f53\u5bb6\u7684\u54ea\u4e00\u70b9\u80fd\u8ddf\u82d7\u59d1\u5a18\u4e00\u6837\uff1f\u8eab\u6750\u3001\u8138\u86cb\uff1f\u6839\u672c\u5b8c\u5168\u4e0d\u4e00\u6837\u561b\uff01\u201d \u3000\u3000\u738b\u5927\u5eb7\u5e72\u54b3\u4e86\u58f0\u9053\uff1a\u201c\u9ea6\u8001\u5f1f\u522b\u5f00\u73a9\u7b11\uff0c\u4ffa\u662f\u5e0c\u671b\u4e5f\u80fd\u6539\u626e\u4e00\u4e0b\u8ddf\u7740\u4ed6\u4eec\u6df7\u8fdb\u8c37\u53bb\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4eba\u5bb6\u82d7\u59d1\u5a18\u662f\u5973\u626e\u7537\u88c5\uff0c\u4f60\u662f\u51c6\u5907\u7537\u626e\u5973\u88c5\uff1f\u201d \u3000\u3000\u738b\u5927\u5eb7\u5e72\u7b11\u9053\uff1a\u201c\u4f60\u662f\u600e\u4e48\u5566\uff1f\u8001\u62ff\u4ffa\u5f00\u73a9\u7b11\uff0c\u4ffa\u82e5\u626e\u4e2a\u5973\u7684\uff0e\u4f60\u6562\u770b\u5417\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u6562\u770b\uff0c\u4f60\u73b0\u5728\u5c31\u626e\u8d77\u6765\u8ba9\u6211\u77a7\u77a7\u3002\u201d \u3000\u3000\u201c\u7b97\u5566\u7b97\u5566\uff01\u4ffa\u4e0d\u53bb\u5c31\u662f\u4e86\u3002\u201d \u3000\u3000\u6b21\u65e5\uff0c\u609f\u5c18\u547d\u4e09\u540d\u5f1f\u5b50\u63d0\u524d\u51c6\u5907\u4e86\u65e9\u996d\u3002 \u3000\u3000\u996d\u540e\uff0e\u5929\u673a\u3001\u5929\u89c9\u4e24\u4f4d\u5927\u5e08\u4fbf\u7387\u9886\u5341\u4e8c\u540d\u5f1f\u5b50\u548c\u4e24\u540d\u4fd7\u5bb6\u4eba\u51fa\u53d1\u3002 \u3000\u3000\u539f\u6765\u8fd9\u4e24\u540d\u4fd7\u5bb6\u4eba\uff0c\u9664\u4e86\u5973\u626e\u7537\u88c5\u7684\u82d7\u7d20\u82d3\u5916\uff0c\u66f4\u6709\u4e00\u4e2a\u6234\u4e86\u4eba\u76ae\u9762\u5177\u7684\u9ea6\u5c0f\u660e\u3002 \u3000\u3000\u81f3\u4e8e\u9ea6\u5c0f\u660e\u7684\u8fd9\u526f\u4eba\u76ae\u9762\u5177\uff0e\u5219\u662f\u5411\u6c5f\u5317\u4e09\u9f99\u501f\u7684\uff0c\u6c5f\u5317\u4e09\u9f99\u5bf9\u8fd9\u4e1c\u897f\u662f\u5907\u800c\u672a\u7528\uff0c\u501f \u7ed9\u9ea6\u5c0f\u660e\uff0c\u7b97\u662f\u7b2c\u4e00\u6b21\u4eae\u76f8\u3002 \u3000\u3000\u6b64\u523b\u7684\u9ea6\u5c0f\u660e\uff0c\u5df3\u53d8\u6210\u4e2a\u5e74\u5728\u4e09\u5341\u4e0a\u4e0b\u7684\u4eba\uff0e\u9762\u8c8c\u5e73\u5e38\uff0c\u751a\u81f3\u6709\u70b9\u50cf\u4e61\u4e0b\u4eba\u3002\u5176\u5b9e\u6234\u4e0a \u4eba\u76ae\u9762\u5177\uff0c\u5e0c\u671b\u7684\u4e5f\u662f\u9762\u8c8c\u5e73\u5e38\uff0c\u552f\u6709\u5982\u6b64\uff0c\u624d\u4e0d\u81f4\u5f15\u4eba\u6ce8\u610f\uff0e \u3000\u3000\u4ed6\u548c\u82d7\u7d20\u82d3\u8d77\u521d\u662f\u8d70\u5728\u540e\u9762\uff0c\u76ee\u7684\u662f\u65b9\u4fbf\u8c08\u8bdd\uff0c\u76f4\u5230\u63a5\u8fd1\u8ff7\u8e2a\u8c37\u5165\u53e3\u624d\u548c\u5929\u673a\u5929\u89c9\u8d70\u5728 \u4e00\u8d77\uff0c\u4ee5\u4fbf\u4e3a\u4ed6\u4eec\u5e26\u8def\u3002 \u3000\u3000\u5929\u673a\u5927\u5e08\u5df2\u542c\u949f\u2014\u8c6a\u8bf4\u8fc7\u9ea6\u5c0f\u660e\u5e74\u8f7b\u6c14\u76db\uff0c\u5bb9\u6613\u60f9\u4e8b\uff0c\u8fb9\u8d70\u8fb9\u53ee\u5631\u9053\uff1a\u201c\u5f85\u4f1a\u513f\u5165\u8c37\u4e4b \u540e\uff0c\u82e5\u6709\u5173\u5361\u62e6\u963b\uff0c\u9ea6\u5c0f\u65bd\u4e3b\u5fc5\u987b\u8010\u4e0b\u6027\u5b50\uff0c\u5343\u4e07\u4e0d\u53ef\u95f9\u51fa\u4e8b\u6765\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4e0d\u52b3\u5927\u5e08\u4ea4\u4ee3\uff0c\u665a\u8f88\u662f\u6765\u5e2e\u5fd9\u7684\uff0c\u5f53\u7136\u4e0d\u80fd\u7ed9\u4e24\u4f4d\u5927\u5e08\u5e2e\u5012\u5fd9\u3002\u201d \u3000\u3000\u5929\u89c9\u5927\u5e08\u5728\u65c1\u9053\uff1a\u201c\u5e08\u5144\uff0c\u4e07\u4e00\u4ed6\u4eec\u4e0d\u51c6\u54b1\u4eec\u8fdb\u8c37\u5462\uff1f\u201d \u3000\u3000\u5929\u673a\u5927\u5e08\u9053\uff1a\u201c\u4e0d\u53ef\u80fd\uff01\u54b1\u4eec\u662f\u6b63\u5927\u5149\u660e\u524d\u6765\u62dc\u8bbf\u970d\u5143\u4f3d\uff0c\u4ed6\u6ca1\u6709\u7406\u7531\u62d2\u7edd\u5165\u8c37\uff0c\u800c\u4e14 \u970d\u5143\u4f3d\u76ee\u524d\u7fbd\u6bdb\u672a\u4e30\uff0c\u5bf9\u54b1\u4eec\u5c11\u6797\uff0c\u5fc5\u5b9a\u4e0d\u6562\u5f97\u7f6a\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u4ed6\u4e3a\u4ec0\u4e48\u6562\u6536\u7559\u54b1\u4eec\u5c11\u6797\u7684\u4eba\uff1f\u201d \u3000\u3000\u201c\u90a3\u662f\u53e6\u5916\u7684\u4e8b\u3002\u4e0d\u7ba1\u5982\u4f55\uff0e\u4ed6\u603b\u4e0d\u80fd\u4e0d\u63a5\u89c1\u54b1\u4eec\u3002\u201d \u3000\u3000\u8fd9\u65f6\u5df2\u6765\u5230\u8c37\u53e3\u3002 \u3000\u3000\u8c37\u53e3\u56e0\u4e3a\u662f\u7b2c\u4e00\u9053\u5173\u5361\uff0c\u6700\u4e3a\u91cd\u8981\uff0c\u5171\u6709\u4e94\u4eba\u628a\u5b88\uff0c\u5176\u4e2d\u4e00\u540d\u662f\u5e26\u961f\u7684\u5c0f\u5934\u76ee\uff0e \u3000\u3000\u5728\u5929\u673a\u3001\u5929\u89c9\u5c1a\u79bb\u8c37\u53e3\u6570\u4e08\u5916\u65f6\uff0c\u90a3\u4e94\u4eba\u4fbf\u5404\u4ed7\u5175\u5203\uff0c\u5c06\u8c37\u53e3\u5835\u4f4f\u3002 \u3000\u3000\u5929\u673a\u5927\u5e08\u5f53\u5148\u6253\u4e86\u4e2a\u95ee\u8baf\u9053\uff1a\u201c\u6709\u52b3\u901a\u62a5\u8d35\u8c37\u970d\u76df\u4e3b\uff0c\u8d2b\u50e7\u7b49\u4eba\u662f\u4ece\u5c11\u6797\u6765\u7684\u3002\u201d \u3000\u3000\u90a3\u5c0f\u5934\u76ee\u677f\u7740\u9762\u5b54\u9053\uff1a\u201c\u4ece\u5c11\u6797\u6765\u7684\u8981\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5929\u673a\u5927\u5e08\u5355\u638c\u7acb\u80f8\u9053\uff1a\u201c\u8d2b\u50e7\u7b49\u4eba\u6709\u4e8b\u6c42\u89c1\u970d\u76df\u4e3b\uff01\u201d \u3000\u3000\u90a3\u5c0f\u5934\u76ee\u603b\u7b97\u7a0d\u901a\u4eba\u6027\uff0c\u53c8\u89c1\u50e7\u4f17\u4eec\u2014\u4e2a\u4e2a\u5168\u5e26\u7740\u624b\u81c2\u7c97\u7ec6\u7684\u94c1\u7985\u6756\uff0c\u8170\u95f4\u53c8\u60ac\u7740\u660e\u6643 \u6643\u7684\u7684\u6212\u5200\uff0e\u751f\u6015\u95f9\u7ffb\u4e86\u8ba8\u4e0d\u5230\u597d\u5904\uff0c\u53ea\u5f97\u5429\u5490\u4e00\u540d\u55bd\u5175\u8fdb\u8c37\u901a\u62a5\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u89c1\u90a3\u55bd\u5175\u8d70\u540e\uff0c\u4fbf\u62c9\u7740\u5929\u673a\u5927\u5e08\u7684\u8888\u88df\u8896\u5b50\u9053\uff1a\u201c\u5927\u5e08\uff0c\u54b1\u4eec\u5230\u90a3\u8fb9\u6811\u4e0b\u4f11\u606f\u4f11 \u606f\uff01\u201d \u3000\u3000\u5929\u673a\u5927\u5e08\u4f9d\u8a00\u7387\u4f17\u6765\u5230\u5341\u4f59\u4e08\u5916\u7684\u4e00\u68f5\u5927\u6811\u4e0b\uff0c\u5e2d\u5730\u800c\u5750\u4e0b\u6765\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4ece\u8c37\u53e3\u5230\u970d\u5143\u4f3d\u6240\u4f4f\u7684\u5929\u661f\u9662\u6709\u597d\u51e0\u91cc\u8fdc\uff0e\u6765\u56de\u8981\u534a\u4e2a\u65f6\u8fb0\uff0c\u5927\u6709\u5c3d\u53ef\u597d \u597d\u4f11\u606f\u4f11\u606f\uff0c\u800c\u4e14\u4ece\u524d\u6ca1\u5546\u8bae\u597d\u7684\u4e8b\uff0c\u4e5f\u53ef\u8d81\u8fd9\u673a\u4f1a\u5546\u8bae\u5546\u8bae\uff01\u201d \u3000\u3000\u5929\u673a\u5927\u5e08\u9053\uff1a\u201c\u8be5\u5546\u8bae\u7684\u65e9\u5c31\u5546\u8bae\u8fc7\u4e86\uff0c\u54ea\u4f1a\u7b49\u5230\u73b0\u5728\u3002\u201d \u3000\u3000\u65e2\u7136\u5929\u673a\u3001\u5929\u89c9\u5927\u5e08\u6ca1\u6709\u8bdd\u8bb2\uff0c\u90a3\u5341\u4e8c\u540d\u50e7\u4eba\u4e5f\u90fd\u95ed\u76ee\u6253\u5750\uff0c\u9ea6\u5c0f\u660e\u548c\u82d7\u7d20\u82d3\u6709\u8bdd\u8bb2\u5374 \u4e0d\u65b9\u4fbf\uff0c\u53ea\u597d\u4e5f\u966a\u7740\u4f17\u50e7\u67af\u5750\u3002 \u3000\u3000\u679c\u7136\u76f4\u7b49\u4e86\u5c06\u8fd1\u534a\u4e2a\u65f6\u8fb0\uff0c\u624d\u89c1\u641c\u9b42\u624b\u5df4\u5929\u4e49\u8fce\u51fa\u8c37\u6765\u3002 \u3000\u3000\u4e0d\u7528\u8bf4\uff0c\u5df4\u5929\u4e49\u662f\u5949\u970d\u5143\u4f3d\u4e4b\u547d\u800c\u6765\u3002 \u3000\u3000\u5df4\u5929\u4e49\u662f\u8001\u6c5f\u6e56\uff0c\u65e9\u5c31\u89c1\u8fc7\u5929\u673a\u548c\u5929\u89c9\u4e8c\u5927\u5e08\uff0c\u6765\u5230\u8c37\u53e3\uff0c\u62b1\u62f3\u4e3a\u793c\u9053\uff1a\u201c\u5931\u8fce\u5931\u8fce\uff01 \u96be\u5f97\u4e24\u4f4d\u8001\u7985\u5e08\u80fd\u9a7e\u4e34\u655d\u8c37\uff0c\u655d\u76df\u4e3b\u7279\u547d\u5df4\u67d0\u524d\u6765\u606d\u8fce\uff0c\u5931\u793c\u4e4b\u5904\uff0c\u8fd8\u8bf7\u591a\u591a\u539f\u8c05\uff01\u201d \u3000\u3000\u5929\u673a\u5927\u5e08\u4e5f\u5ba2\u6c14\u4e86\u51e0\u53e5\uff0c\u968f\u5373\u7387\u9886\u4f17\u4eba\u8ddf\u968f\u5df4\u5929\u4e49\u8fdb\u8c37\uff0c\u5e76\u2014\u8def\u9ed8\u5bdf\u5f62\u52bf\u3002 \u3000\u3000\u8def\u4e0a\uff0c\u53cc\u65b9\u90fd\u672a\u63d0\u5230\u5c11\u6797\u53db\u5f92\u609f\u660e\u548c\u4e5d\u540d\u94dc\u4eba\u4e4b\u4e8b\u3002 \u3000\u3000\u8fdb\u5165\u603b\u5be8\uff0c\u970d\u5143\u4f3d\u65e9\u5df2\u7b49\u4faf\u5728\u805a\u4e49\u5385\u95e8\u5916\u3002 \u3000\u3000\u970d\u5143\u4f3d\u8eab\u65c1\u53ea\u5e26\u4e86\u4e09\u4e94\u4eba\uff0c\u5176\u4e2d\u6709\u62d8\u9b44\u7d22\u5b8b\u5929\u94ce\uff0e\u8fde\u4e01\u5e08\u7237\u4e5f\u5728\u5185\u3002 \u3000\u3000\u8fdb\u5165\u5927\u5385\uff0c\u5206\u5bbe\u4e3b\u5750\u5b9a\uff0c\u5148\u8c08\u4e86\u51e0\u53e5\u5ba2\u5957\u8bdd\uff0c\u970d\u5143\u4f3d\u5bf9\u9ea6\u5c0f\u660e\u548c\u82d7\u7d20\u82d3\u770b\u5f97\u5341\u5206\u7559\u610f\uff0c \u663e\u7136\u4ed6\u5bf9\u8fd9\u4e24\u4eba\u6709\u4e9b\u773c\u719f\u3002 \u3000\u3000\u5929\u673a\u5927\u5e05\u62c5\u5fc3\u4ed6\u77a7\u51fa\u7834\u7efd\uff0e\u5fd9\u9053\uff1a\u201c\u4ed6\u4eec\u662f\u655d\u5bfa\u7684\u4e24\u540d\u4fd7\u5bb6\u5f1f\u5b50\uff0c\u83ab\u975e\u970d\u76df\u4e3b\u5728\u54ea\u91cc\u89c1 \u8fc7\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u5176\u4e2d\u4e00\u4f4d\u5c0f\u5144\u5f1f\uff0e\u597d\u50cf\u89c1\u8fc7\uff0c\u53e6\u4e00\u4f4d\u6ca1\u89c1\u8fc7\uff0e\u4f46\u8eab\u6750\u4e3e\u6b62\uff0c\u5374\u6709\u4e9b\u773c\u719f\u3002\u201d \u3000\u3000\u4ed6\u6240\u79f0\u597d\u50cf\u89c1\u8fc7\u7684\u2014\u4eba\uff0c\u5206\u660e\u662f\u82d7\u7d20\u82d3\uff0c\u56e0\u4e3a\u5728\u4e07\u6708\u5ce1\u7684\u90a3\u573a\u6fc0\u6218\uff0c\u82d7\u7d20\u82d3\u867d\u7740\u7537\u88c5\uff0c \u5374\u672a\u7f69\u9ed1\u7eb1\u3002 \u3000\u3000\u5929\u673a\u5927\u5e08\u7b11\u9053\uff1a\u201c\u4e5f\u8bb8\u662f\u4ed6\u4eec\u957f\u5f97\u548c\u970d\u76df\u4e3b\u89c1\u8fc7\u7684\u4eba\u6709\u4e9b\u76f8\u50cf\u5427\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u786e\u5b9e\u6709\u70b9\u50cf\u3002\u201d \u3000\u3000\u559d\u8fc7\u4e00\u76cf\u8336\u540e\uff0c\u5929\u673a\u5927\u5e08\u968f\u5373\u628a\u8bdd\u8f6c\u5165\u6b63\u9898\u9053\uff1a\u201c\u8001\u8872\u6b64\u6765\uff0c\u9664\u4e86\u62dc\u8bbf\u970d\u76df\u4e3b\u5916\uff0c\u53e6\u6709 \u4e00\u4e8b\uff0c\u8fd8\u671b\u970d\u76df\u4e3b\u80fd\u5e2e\u655d\u5bfa\u4e00\u6b21\u5fd9\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u8001\u7985\u5e08\u6709\u8bdd\u8bf7\u8bb2\uff0c\u53ea\u8981\u970d\u67d0\u80fd\u505a\u5f97\u5230\u7684\uff0c\u65e0\u4e0d\u5c3d\u529b\u6548\u52b3\u3002\u201d \u3000\u3000\u5929\u673a\u5927\u5e08\u7f13\u7f13\u9053\uff1a\u201c\u655d\u5bfa\u4e0d\u5e78\uff0c\u7adf\u7136\u51fa\u4e86\u53db\u5e08\u706d\u7956\u4e4b\u5f92\uff0e\u542c\u8bf4\u8fd9\u4e9b\u4eba\u5df2\u6295\u5230\u8d35\u603b\u5be8\uff0e\u53ef \u6709\u8fd9\u4ef6\u4e8b\u5427\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u672c\u60f3\u4e0d\u4e88\u627f\u8ba4\uff0c\u4f46\u4ed6\u5df2\u51b3\u5b9a\u65e5\u540e\u5c06\u4ee5\u90a3\u513f\u540d\u94dc\u4eba\u5bf9\u5916\u70ab\u8000\uff0e\u85c9\u4ee5\u63d0\u9ad8\u4ed6\u7684\u7eff\u6797\u76df \u4e3b\u58f0\u52bf\u5730\u4f4d\uff0c\u4e0d\u627f\u8ba4\u53cd\u800c\u4e0d\u5999\u3002\u968f\u5373\u4e0d\u52a8\u58f0\u8272\u9053\uff1a\u201c\u4e0d\u9519\uff01\u8d35\u5bfa\u7684\u5f1f\u5b50\u609f\u660e\u548c\u4e5d\u540d\u94dc\u4eba\uff0c\u773c \u4e0b\u90fd\u5728\u8ff7\u8e2a\u8c37\u603b\u5be8\u3002\u201d \u3000\u3000\u5929\u673a\u5927\u5e08\u9053\uff1a\u201c\u8001\u8872\u548c\u5929\u89c9\u5e08\u6b63\u662f\u5949\u638c\u95e8\u5e08\u5144\u6cd5\u8c15\uff0c\u524d\u6765\u8d35\u603b\u5be8\uff0e\u5e0c\u671b\u628a\u4ed6\u4eec\u62bc\u56de\u5c11\u6797 \u53bb\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u9ed8\u4e86\u9ed8\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\uff0c\u65e2\u7136\u662f\u62bc\u4ed6\u4eec\u56de\u53bb\uff0c\u8001\u7985\u5e08\u4f3c\u4e4e\u4e0d\u5fc5\u5e26\u8fd9\u4e48\u591a\u4eba\u6765\uff01\u201d \u3000\u3000\u5929\u673a\u5927\u5e08\u9053\uff1a\u201c\u90a3\u4e5d\u540d\u94dc\u4eba\uff0c\u4e2a\u4e2a\u6b66\u529f\u9ad8\u5f3a\uff0c\u82e5\u4e0d\u591a\u5e26\u51e0\u540d\u5f1f\u5b50\u6765\uff0c\u8fe2\u8fe2\u5343\u4f59\u91cc\uff0e\u5982\u4f55 \u80fd\u62bc\u89e3\u4ed6\u4eec\u56de\u53bb\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u563f\u563f\u7b11\u9053\uff1a\u201c\u5176\u5b9e\u970d\u67d0\u65e9\u5c31\u5bf9\u4ed6\u4eec\u6653\u4ee5\u5927\u4e49\uff0e\u4e00\u518d\u82e6\u53e3\u5a46\u5fc3\u529d\u4ed6\u4eec\u56de\u5c11\u6797\u53bb\uff0c\u5982 \u4eca\u4e24\u4f4d\u8001\u7985\u5e08\u6765\u4e86\uff0c\u90a3\u662f\u6700\u597d\u4e0d\u8fc7\u3002\u201d \u3000\u3000\u5929\u673a\u5927\u5e08\u4e0d\u89c9\u559c\u51fa\u671b\u5916\uff0c\u4ed6\u5b9e\u5728\u6ca1\u60f3\u5230\u970d\u5143\u4f3d\u4f1a\u7b54\u5e94\u5f97\u8fd9\u4e48\u5feb\uff0c\u8fde\u5fd9\u9ad8\u8bf5\u4e86\u58f0\u4f5b\u53f7\u9053\uff1a \u201c\u970d\u76df\u4e3b\u5982\u6b64\u4ed7\u4e49\u76f8\u52a9\uff0c\u5f85\u8001\u8872\u8fd4\u56de\u5c11\u6797\u7980\u660e\u638c\u95e8\u5e08\u5144\u540e\uff0c\u5fc5\u5f53\u524d\u6765\u91cd\u8c22\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u53c8\u662f\u563f\u563f\u4e00\u7b11\u9053\uff1a\u201c\u970d\u67d0\u65b9\u624d\u5df2\u7ecf\u8bf4\u8fc7\uff0c\u53ea\u8981\u8001\u7985\u5e08\u6709\u6240\u5429\u5490\uff0c\u65e0\u4e0d\u5c3d\u529b\u6548\u52b3\uff01\u201d \u3000\u3000\u63a5\u7740\u5429\u5490\u5b8b\u5929\u94ce\u9053\uff1a\u201c\u628a\u609f\u660e\u548c\u4e5d\u540d\u94dc\u4eba\u5e26\u5230\u5927\u5385\u6765\uff01\u201d \u3000\u3000\u5b8b\u5929\u94ce\u5e94\u58f0\u800c\u53bb\u3002 \u3000\u3000\u7ea6\u534a\u76cf\u8336\u5de5\u592b\uff0c\u679c\u89c1\u609f\u660e\u548c\u4e5d\u540d\u94dc\u4eba\u9c7c\u8d2f\u8fdb\u5165\u5927\u5385\u3002 \u3000\u3000\u5c82\u77e5\u4ed6\u4eec\u5bf9\u5929\u673a\u5929\u89c9\u4e24\u4f4d\u5927\u5e08\u4ee5\u53ca\u5176\u4ed6\u5c11\u6797\u5f1f\u5b50\u770b\u90fd\u4e0d\u770b\u4e00\u773c\uff0c\u5374\u606d\u606d\u656c\u656c\u7684\u53ea\u5411\u970d\u5143 \u4f3d\u65bd\u4e86\u2014\u793c\uff0c\u7136\u540e\u7ad9\u5728\u4e00\u65c1\u3002 \u3000\u3000\u9976\u662f\u5929\u53fd\u5929\u89c9\u4e24\u4f4d\u5927\u5e08\u5b9a\u529b\u6df1\u539a\uff0c\u5bb9\u5fcd\u4e4b\u5fc3\u8d85\u8fc7\u5e38\u4eba\uff0c\u89c1\u6b64\u60c5\u5f62\uff0e\u4e5f\u90fd\u6c14\u5f97\u9762\u8272\u94c1\u9752\uff0c \u53ea\u5dee\u5c1a\u672a\u51fa\u58f0\u559d\u65a5\u3002 \u3000\u3000\u970d\u5143\u4f3d\u9762\u5e26\u5fae\u7b11\uff0c\u6307\u4e86\u6307\u5929\u673a\u3001\u5929\u89c9\u4e24\u4f4d\u5927\u5e08\u9053\uff1a\u201c\u8d35\u5bfa\u7684\u4e24\u4f4d\u7985\u5e08\u5728\u6b64\uff0e\u4f60\u4eec\u4e3a\u4ec0\u4e48 \u4e0d\u4e0a\u524d\u89c1\u8fc7\u3002\u201d \u3000\u3000\u53ea\u542c\u609f\u660e\u51b7\u51b7\u4e00\u7b11\u9053\uff1a\u201c\u7980\u76df\u4e3b\uff0e\u5c5e\u4e0b\u4eec\u81ea\u6295\u5954\u7eff\u6797\u603b\u5be8\u540e\uff0e\u65e9\u5df2\u548c\u5c11\u6797\u65ad\u7edd\u5173\u7cfb\uff0c\u5373 \u4f7f\u5c11\u6797\u638c\u95e8\u524d\u6765\uff0e\u4e5f\u6ca1\u4ec0\u4e48\u597d\u89c1\u7684\u3002\u201d \u3000\u3000\u5929\u673a\u5927\u5e08\u7ec8\u4e8e\u6309\u637a\u4e0d\u4f4f\uff0e\u5927\u559d\u9053\uff1a\u201c\u597d\u4e00\u4e2a\u5927\u80c6\u7684\u5b7d\u755c\uff0c\u7adf\u6562\u5bf9\u5e08\u5c0a\u8fd9\u822c\u65e0\u793c\uff01\u201d \u3000\u3000\u6c14\u6781\u4e4b\u4e0b\uff0e\u8bf4\u5b8c\u573a\u624b\u4e00\u638c\uff0c\u76f4\u5411\u609f\u660e\u5288\u53bb\u3002 \u3000\u3000\u609f\u660e\u4f3c\u662f\u65e9\u5df2\u6709\u5907\uff0e\u638c\u52bf\u672b\u5230\uff0e\u4fbf\u5df2\u8fc5\u901f\u95ea\u5230\u970d\u5143\u4f3d\u8eab\u540e\uff0e\u4e00\u9762\u53eb\u9053\uff1a\u201c\u76df\u4e3b\u5feb\u6551\u5c5e \u4e0b\uff01\u201d \u3000\u3000\u8fd9\u4e00\u6765\uff0c\u8feb\u5f97\u5929\u673a\u5927\u5e08\u679c\u7136\u65e0\u6cd5\u518d\u5c71\u624b\u3002 \u3000\u3000\u970d\u5143\u4f3d\u76ee\u6ce8\u4e5d\u540d\u94dc\u4eba\uff0c\u62ac\u624b\u4e00\u6325\u9053\uff1a\u201c\u4f60\u4eec\u90fd\u9000\u5230\u5916\u9762\u53bb\uff01\u201d \u3000\u3000\u5929\u673a\u5927\u5e08\u8138\u8272\u4e00\u53d8\u9053\uff1a\u201c\u970d\u76df\u4e3b\u4e3a\u4ec0\u4e48\u8981\u4ed6\u4eec\u9000\u8d70\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u4ed6\u4eec\u90fd\u542c\u609f\u660e\u6307\u6325\uff0c\u65e0\u4e0d\u4ee5\u609f\u660e\u9a6c\u9996\u662f\u77bb\uff0c\u7559\u4e0b\u609f\u660e\u4e00\u4eba\u5728\u5c31\u591f\u4e86\u3002\u201d \u3000\u3000\u5929\u673a\u5927\u5e08\u53cc\u988a\u4e00\u9635\u62bd\u6410\u9053\uff1a\u201c\u609f\u660e\u8fd9\u755c\u751f\u592a\u53ef\u6076\u4e86\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u4e0d\u7ba1\u4ed6\u53ef\u6076\u4e0d\u53ef\u6076\uff0c\u8001\u7985\u5e08\u8981\u5e26\u8d70\u4e5d\u540d\u94dc\u4eba\uff0e\u603b\u8be5\u5148\u548c\u609f\u660e\u8c08\u8c08\u3002\u201d \u3000\u3000\u201c\u8001\u8872\u548c\u8fd9\u5b7d\u755c\u6709\u4ec0\u4e48\u597d\u8c08\u7684\uff0c\u53ea\u8981\u970d\u76df\u4e3b\u628a\u4ed6\u4ea4\u7ed9\u8001\u8872\uff0c\u8001\u8872\u81ea\u6709\u529e\u6cd5\u62bc\u4ed6\u4eec\u56de\u53bb\u3002\u201d \u3000\u3000\u201c\u8bdd\u4e0d\u80fd\u8fd9\u4e48\u8bf4\uff0c\u4ed6\u4eec\u65e2\u7136\u5df2\u7ecf\u6295\u5f52\u4e86\u655d\u603b\u5be8\uff0c\u8001\u7985\u5e08\u603b\u8be5\u542c\u542c\u4ed6\u4eec\u7684\u5fd7\u613f\uff0c\u82e5\u4efb\u7531\u8001 \u7985\u5e08\u628a\u4eba\u5e26\u8d70\uff0c\u4ee5\u540e\u8fd8\u6709\u8c01\u6562\u518d\u6295\u5954\u5230\u8ff7\u8e2a\u8c37\uff1f\u201d \u3000\u3000\u201c\u83ab\u975e\u970d\u76df\u4e3b\u53c8\u53d8\u5366\u4e86\uff1f\u201d \u3000\u3000\u201c\u970d\u67d0\u4e00\u5411\u6700\u91cd\u4fe1\u8bfa\uff0c\u8001\u7985\u5e08\u5e26\u4ed6\u4eec\u4e0d\u8d70\uff0e\u90a3\u53c8\u600e\u80fd\u57cb\u6028\u970d\u67d0\uff1f\u201d \u3000\u3000\u5ffd\u542c\u5df4\u5929\u4e49\u53eb\u9053\uff1a\u201c\u7cdf\u7cd5\uff01\u4ed6\u4eec\u5df2\u5728\u5927\u5385\u5916\u5e03\u6210\u9635\u5f0f\u4e86\uff01\u201d \u3000\u3000\u5929\u673a\u5927\u5e08\u7b49\u4eba\u5411\u5927\u5385\u5916\u671b\u53bb\uff0c\u679c\u7136\u90a3\u4e5d\u540d\u94dc\u4eba\u5df2\u5e03\u6210\u4e00\u4e2a\u65b9\u9635\u3002 \u3000\u3000\u8fd9\u4e9b\u5c11\u6797\u50e7\u4eba\uff0c\u4f5c\u68a6\u4e5f\u60f3\u4e0d\u5230\u4e8b\u60c5\u4f1a\u6f14\u53d8\u5230\u8fd9\u79cd\u5730\u6b65\uff0e \u3000\u3000\u53ea\u542c\u970d\u5143\u4f3d\u9053\uff1a\u201c\u4e8c\u4f4d\u5927\u5e08\u8fd8\u662f\u5230\u5916\u9762\u770b\u770b\u7684\u597d\u3002\u201d \u3000\u3000\u5929\u673a\uff0c\u5929\u89c9\u4e24\u4f4d\u5927\u5e08\u53ea\u5f97\u8d77\u5ea7\u6765\u5230\u5927\u5385\u95e8\u53e3\u3002 \u3000\u3000\u5c11\u6797\u5341\u4e8c\u540d\u5f1f\u5b50\u4ee5\u53ca\u9ea6\u5c0f\u660e\u548c\u82d7\u7d20\u82d3\uff0c\u4e5f\u90fd\u968f\u4e4b\u800c\u51fa\u3002 \u3000\u3000\u51fa\u5f97\u95e8\u6765\uff0c\u4f17\u4eba\u4e0d\u89c9\u53c8\u662f\u4e00\u60ca\uff0c\u4f46\u89c1\u94dc\u4eba\u9635\u4e4b\u540e\uff0c\u5de6\u53f3\u53c8\u5404\u7ad9\u7740\u56db\u540d\u624b\u6301\u5175\u5203\uff0c\u6012\u76ee\u6a2a \u7709\u7684\u4eba\u3002 \u3000\u3000\u5929\u673a\u548c\u5929\u89c9\u4e24\u4f4d\u5927\u5e08\u4e00\u773c\u5c31\u770b\u51fa\u5de6\u8fb9\u56db\u4eba\u662f\u5d06\u5cd2\u4e94\u9b54\u4e2d\u7684\u56db\u9b54\uff0c\u5176\u4e2d\u72ec\u7f3a\u9b3c\u9b54\u5deb\u9053\u5168\u3002 \u3000\u3000\u5f53\u7136\uff0c\u4ed6\u4eec\u5e76\u4e0d\u6e05\u695a\u9b3c\u9b54\u5deb\u9053\u5168\u5df2\u88ab\u9ea6\u5c0f\u660e\u5728\u4e07\u6708\u5ce1\u780d\u53bb\u4e00\u6761\u5de6\u81c2\uff0c\u76ee\u524d\u6b63\u5728\u517b\u4f24\u5f53\u4e2d\u3002 \u3000\u3000\u81f3\u4e8e\u53e6\u5916\u56db\u4eba\uff0c\u53ea\u6709\u9ea6\u5c0f\u660e\u8ba4\u8bc6\uff0c\u8d6b\u7136\u662f\u4ed6\u7684\u56db\u4f4d\u5e08\u5144\u94c1\u62d0\u8ffd\u9b42\u7f57\u94ee\u3001\u91d1\u9a6c\u9b3c\u68ad\u4e01\u4e00\u9b42\u3001 \u51b7\u773c\u795e\u67ad\u9ad8\u5d99\u548c\u5c1a\u65e0\u7ef0\u53f7\u7684\u5f20\u656c\u5b89\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u5fc3\u4e2d\u6709\u6570\uff0c\u4ec5\u4ec5\u4ee5\u8fd9\u516b\u4e2a\u4eba\u518d\u52a0\u4e0a\u94dc\u4eba\u9635\uff0c\u5c11\u6797\u65b9\u9762\u5c31\u96be\u4ee5\u5e94\u4ed8\uff0c\u82e5\u970d\u5143\u4f3d\u6d3e\u5176 \u4ed6\u624b\u4e0b\u52a9\u9635\uff0c\u5c11\u6797\u4e00\u65b9\u4eca\u5929\u5c31\u5f88\u53ef\u80fd\u5168\u519b\u8986\u6ca1\u3002 \u5367\u9f99\u751f\u300a\u5999\u7edd\u5929\u9999\u300b \u7b2c\u4e8c\u5341\u4e94\u56de\u3000\u94e9\u7fbd\u800c\u5f52 \u3000\u3000\u663e\u7136\uff0c\u8fd9\u5c40\u9762\u662f\u5728\u970d\u5143\u4f3d\u5f97\u5230\u5929\u673a\u5927\u5e08\u8fdb\u8c37\u7684\u6d88\u606f\u540e\u4fbf\u6709\u9884\u8c0b\u3002 \u3000\u3000\u6b64\u523b\u94dc\u4eba\u9635\uff0c\u56e0\u4e3a\u53ea\u5269\u4e0b\u4e5d\u540d\uff0c\u5373\u4f7f\u65e0\u4eba\u6307\u6325\uff0c\u4e5f\u7167\u6837\u53ef\u4ee5\u8fd0\u8f6c\u81ea\u5982\uff0c\u56e0\u4e4b\uff0c\u609f\u660e\u8fd8\u662f \u7d27\u7d27\u7684\u968f\u5728\u970d\u5143\u4f3d\u8eab\u65c1\u3002 \u3000\u3000\u5929\u673a\u5927\u5e08\u548c\u5929\u89c9\u5927\u5e08\uff0c\u6b64\u523b\u65e9\u5df2\u6c14\u5f97\u4e00\u4f5b\u51fa\u4e16\uff0e\u4e8c\u4f5b\u6d85\u78d0\uff0c\u51e0\u4e4e\u8fde\u8bdd\u90fd\u8bf4\u4e0d\u51fa\u3002 \u3000\u3000\u970d\u5143\u4f3d\u5475\u5475\u7b11\u9053\uff1a\u201c\u4e24\u4f4d\u8001\u7985\u5e08\uff0c\u8fd9\u5c40\u9762\u8fde\u970d\u67d0\u4e5f\u5927\u611f\u610f\u5916\uff0e\u8be5\u600e\u6837\u5904\u7f6e\uff0c\u4e5f\u53ea\u6709\u8bf7\u4e8c \u4f4d\u81ea\u884c\u51b3\u5b9a\u4e86\uff01\u201d \u3000\u3000\u5929\u673a\u5927\u5e08\u53cc\u76ee\u795e\u5149\u5982\u7535\uff0c\u6012\u626b\u4e86\u9635\u540e\u516b\u4eba\u4e00\u773c\u9053\uff1a\u201c\u4ed6\u4eec\u90a3\u4e9b\u4eba\u662f\u505a\u4ec0\u4e48\u7684\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u4ed6\u4eec\u90fd\u662f\u8ff7\u8e2a\u8c37\u65b0\u8fd1\u6765\u7684\u5ba2\u4eba\uff0c\u970d\u67d0\u5b9e\u5728\u662f\u65e0\u6743\u7ea6\u675f\u4ed6\u4eec\u3002\u201d \u3000\u3000\u5929\u673a\u5927\u5e08\u51b7\u7136\u9053\uff1a\u201c\u970d\u76df\u4e3b\u4e0d\u59a8\u95ee\u95ee\uff0c\u4ed6\u4eec\u7a76\u7adf\u51c6\u5907\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u7acb\u5373\u671b\u5411\u5d06\u5cd2\u56db\u9b54\u9053\uff1a\u201c\u56db\u4f4d\u4e3a\u4ec0\u4e48\u5ffd\u7136\u4eae\u51fa\u5175\u5203\uff0c\u7ad9\u5728\u8fd9\u91cc\uff1f\u201d \u3000\u3000\u8001\u5927\u6bd2\u9b54\u9634\u5929\u7433\u9053\uff1a\u201c\u96be\u9053\u76df\u4e3b\u8fd8\u8981\u95ee\u5417\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u6545\u4f5c\u832b\u7136\u9053\uff1a\u201c\u4e0d\u95ee\u600e\u4e48\u660e\u767d\uff1f\u9634\u5144\u8fd8\u662f\u5feb\u5feb\u8bb2\u51fa\u6765\u7684\u597d\uff01\u201d \u3000\u3000\u9634\u5929\u7433\u9053\uff1a\u201c\u7406\u7531\u5f88\u7b80\u5355\u3002\u5c11\u6797\u5bfa\u7684\u609f\u660e\u548c\u4e5d\u94dc\u4eba\u90fd\u662f\u6295\u5954\u8ff7\u8e2a\u8c37\u6765\u7684\uff0c\u6211\u4eec\u5219\u662f\u8ff7\u8e2a \u8c37\u7684\u5ba2\u4eba\uff0c\u5982\u4eca\u5c11\u6797\u6d3e\u4eba\u6765\u6349\u62ff\u609f\u660e\u548c\u4e5d\u5927\u94dc\u4eba\uff0c\u970d\u76df\u4e3b\u5c45\u7136\u4e0d\u7ba1\uff0c\u8fd9\u4e8b\u6211\u4eec\u5b9e\u5728\u770b\u4e0d\u8fc7\u53bb\uff0c \u53ea\u597d\u81ea\u5df1\u6765\u7ba1\u4e00\u7ba1\u4e86\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u6545\u610f\u76b1\u4e86\u76b1\u7709\u9053\uff1a\u201c\u9634\u5144\u8fd9\u6837\u505a\uff0e\u53ea\u6015\u970d\u67d0\u5bf9\u5c11\u6797\u4e24\u4f4d\u5927\u5e08\u65b9\u9762\u6ca1\u6cd5\u4ea4\u4ee3\u3002\u201d \u3000\u3000\u9634\u5929\u7433\u51b7\u7b11\u9053\uff1a\u201c\u90a3\u662f\u4f60\u81ea\u5df1\u7684\u4e8b\uff01\u970d\u76df\u4e3b\uff0c\u5b9e\u5bf9\u4f60\u8bf4\uff0c\u6211\u4eec\u5f1f\u5144\u4e94\u4eba\u4e5f\u662f\u6709\u610f\u6295\u5954\u8ff7 \u8e2a\u8c37\uff0c\u5982\u4f60\u4eca\u4e0d\u80fd\u4fdd\u62a4\u8bda\u610f\u6295\u5954\u8d35\u8c37\u7684\u609f\u660e\u5e08\u548c\u4e5d\u5927\u94dc\u4eba\uff0c\u6211\u4eec\u4e3a\u4ed6\u4eec\u58f0\u63f4\u53c8\u6709\u4ec0\u4e48\u4e0d\u53ef\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u8038\u4e86\u8038\u80a9\uff0c\u76ee\u5149\u626b\u8fc7\u5929\u673a\u3001\u5929\u89c9\u4e24\u4f4d\u5927\u5e08\u9053\uff1a\u201c\u4e8b\u60c5\u95f9\u5230\u8fd9\u79cd\u5730\u6b65\uff0e\u970d\u67d0\u4e5f\u5b9e\u5728 \u65e0\u80fd\u4e3a\u529b\u4e86\u3002\u201d \u3000\u3000\u8fd9\u65f6\u9ea6\u5c0f\u660e\u65e9\u5df2\u6012\u706b\u96be\u7981\uff0c\u671b\u7740\u5929\u673a\u5927\u5e05\u9ad8\u58f0\u9053\uff1a\u201c\u522b\u542c\u4ed6\u4eec\u7684\uff0c\u8fd9\u5d06\u5cd2\u56db\u9b54\u65e9\u5c31\u5f52\u964d \u4e86\u8c1c\u8e2a\u8c37\uff0e\u53e6\u6709\u4e00\u9b54\u88ab\u4eba\u524a\u53bb\u2014\u6761\u5de6\u81c2\uff0c\u6240\u4ee5\u4eca\u5929\u624d\u6ca1\u9732\u9762\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u4e24\u773c\u7728\u52a8\u4e86\u4e00\u9635\u9053\uff1a\u201c\u8fd9\u4f4d\u8001\u5f1f\u51ed\u4ec0\u4e48\u8fd9\u6837\u8bf4\u8bdd\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u54fc\u4e86\u58f0\u9053\uff1a\u201c\u5728\u4e0b\u8bf4\u7684\u6709\u4ec0\u4e48\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u4ed6\u5df2\u4e8b\u5148\u670d\u4e86\u53d8\u97f3\u836f\uff0c\u6240\u4ee5\u58f0\u97f3\u4e5f\u548c\u4ece\u524d\u5927\u5927\u4e0d\u76f8\u540c\u3002 \u3000\u3000\u8fd9\u4e00\u6765\uff0e\u970d\u5143\u4f3d\u8d8a\u8d77\u7591\u5fc3\uff0c\u9ea6\u5c0f\u660e\u58f0\u8a00\u5d06\u5cd2\u4e94\u9b54\u5df2\u5f52\u987a\u4e86\u8ff7\u8e2a\u8c37\uff0c\u4e5f\u8bb8\u662f\u731c\u60f3\u4e4b\u8bcd\uff0c\u4f46 \u4e94\u9b54\u4e2d\u6709\u4e00\u9b54\u88ab\u65a9\u65ad\u5de6\u81c2\u5bf9\u65b9\u53c8\u662f\u5982\u4f55\u77e5\u9053\u7684\u5462\uff1f \u3000\u3000\u970d\u5143\u4f3d\u60f3\u5230\u8fd9\u91cc\uff0e\u4e0d\u7531\u9762\u6cdb\u6740\u673a\uff0c\u559d\u9053\uff1a\u201c\u8fd9\u4f4d\u8001\u5f1f\u4fe1\u53e3\u5f00\u6cb3\uff0c\u5371\u8a00\u8038\u542c\uff0c\u4e0d\u77e5\u662f\u4f55\u5c45 \u5fc3\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u51b7\u51b7\u2014\u7b11\u9053\uff1a\u201c\u5982\u679c\u970d\u76df\u4e3b\u80fd\u628a\u9b3c\u9b54\u5deb\u9053\u5168\u627e\u6765\uff0c\u4ed6\u82e5\u5de6\u81c2\u8fd8\u5728\uff0c\u90a3\u65f6\u5728\u4e0b\u60c5\u613f \u4efb\u51ed\u970d\u76df\u4e3b\u5904\u7f6e\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u592a\u9633\u7a74\u5267\u70c8\u8038\u52a8\u4e86\u51e0\u4e0b\uff0c\u671b\u5411\u5929\u673a\u5927\u5e08\u9053\uff1a\u201c\u8fd9\u8001\u5f1f\u7a76\u7adf\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u5929\u673a\u5927\u5e05\u4e0d\u52a8\u58f0\u8272\u9053\uff1a\u201c\u8001\u8872\u5148\u524d\u5df2\u5bf9\u970d\u76df\u4e3b\u8bf4\u8fc7\uff0c\u4ed6\u4eec\u4e24\u4e2a\uff0e\u90fd\u662f\u655d\u5bfa\u7684\u4fd7\u5bb6\u5f1f\u5b50\uff0c \u970d\u76df\u4e3b\u53ef\u8ba4\u4e3a\u4ed6\u6709\u4ec0\u4e48\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u201c\u8fd9\u4eba\u80e1\u8a00\u4e71\u8bed\uff0e\u53e3\u51fa\u4e0d\u5b9e\u4e4b\u8a00\uff0c\u5206\u660e\u662f\u4e0d\u6000\u597d\u610f\uff01\u201d \u3000\u3000\u201c\u4ed6\u8bf4\u53e6\u6709\u4e00\u9b54\u5df2\u65ad\u53bb\u5de6\u81c2\uff0c\u970d\u76df\u4e3b\u5c31\u8be5\u628a\u53e6\u4e00\u9b54\u627e\u6765\uff0c\u53ea\u8981\u8bc1\u660e\u4ed6\u6240\u8a00\u4e0d\u5b9e\uff0c\u8001\u8872\u81ea \u5f53\u5f53\u4f17\u6309\u95e8\u89c4\u60e9\u6cbb\u4e8e\u4ed6\u3002\u201d \u3000\u3000\u201c\u4ed6\u4eec\u4e94\u4e49\u4e2d\u7684\u8001\u4e8c\uff0c\u56e0\u4e3a\u6709\u4e8b\uff0c\u6839\u672c\u5c31\u4e0d\u66fe\u5230\u8ff7\u8e2a\u8c37\uff01\u201d \u3000\u3000\u201c\u90a3\u5c31\u65e0\u6cd5\u65ad\u5b9a\u8001\u8872\u8fd9\u4f4d\u4fd7\u5bb6\u5f1f\u5b50\u6240\u8a00\u662f\u5426\u4e0d\u5b9e\uff01\u201d \u3000\u3000\u5ffd\u542c\u6bd2\u9b54\u9634\u5929\u7433\u543c\u9053\uff1a\u201c\u76df\u4e3b\u7528\u4e0d\u7740\u548c\u8fd9\u5c0f\u5b50\u7f57\u55e6\uff0c\u65e2\u7136\u4ed6\u662f\u51b2\u7740\u6211\u4eec\u4e94\u4e49\u6765\u7684\uff0c\u6700\u597d \u8fd9\u4e8b\u5c31\u8ba9\u6211\u4eec\u81ea\u5df1\u89e3\u51b3\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u6602\u7136\u6717\u58f0\u9053\uff1a\u201c\u90a3\u6700\u597d\u4e0d\u8fc7\uff0c\u4f60\u4eec\u662f\u60f3\u8054\u624b\u51fa\u51fb\uff0c\u8fd8\u662f\u8981\u8ddf\u5728\u4e0b\u5355\u6311\uff1f\u201d \u3000\u3000\u9634\u5929\u7433\u672c\u6765\u5c31\u6ca1\u628a\u9ea6\u5c0f\u660e\u653e\u5728\u773c\u91cc\uff0e\u82e5\u5bf9\u4ed8\u4e00\u4e2a\u5c11\u6797\u540d\u4e0d\u89c1\u7ecf\u4f20\u7684\u4fd7\u5bb6\u5f1f\u5b50\u4fbf\u8981\u8054\u624b\u51fa \u51fb\uff0c\u5c06\u7f6e\u201c\u4e94\u4e49\u201d\u989c\u9762\u4e8e\u4f55\u5730\uff0e \u3000\u3000\u5f53\u4e0b\u9634\u68ee\u68ee\u4e00\u7b11\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0e\u5c45\u7136\u53e3\u51fa\u72c2\u8a00\uff0e\u9634\u67d0\u5bf9\u4ed8\u4f60\u8fd9\u6837\u7684\u56db\u4e2a\u8fd8\u5dee\u4e0d\u591a\u3002\u201d \u3000\u3000\u9634\u5929\u7433\u521a\u8981\u8dc3\u8eab\u8fc7\u6765\uff0c\u5374\u542c\u8001\u56db\u5251\u9b54\u53f8\u592a\u5e73\u9053\uff1a\u201c\u8fd9\u70b9\u5c0f\u4e8b\uff0c\u4ea4\u7ed9\u5144\u5f1f\u4e86\uff0e\u4f60\u8001\u5927\u53ea\u7ba1 \u5728\u540e\u9762\u5750\u9547\u3002\u201d \u3000\u3000\u53f8\u592a\u5e73\u8bdd\u672a\u8bf4\u5b8c\uff0c\u4eba\u5df2\u5012\u63d0\u957f\u5251\uff0e\u6447\u6447\u6446\u6446\u7684\u8d70\u4e86\u51fa\u6765\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u53cd\u800c\u6c89\u4f4f\u4e86\u6c14\uff0c\u671b\u7740\u5929\u673a\u5927\u5e08\u9053\uff1a\u201c\u5e08\u7236\uff0e\u5c31\u8ba9\u5f1f\u5b50\u51fa\u53bb\u8bd5\u8bd5\u5982\u4f55\uff1f\u201d \u3000\u3000\u5929\u673a\u5927\u5e08\u65e9\u5df2\u542c\u8bf4\u9ea6\u5c0f\u660e\u6b66\u529f\u51fa\u4f17\uff0c\u5728\u949f\u4e00\u8c6a\u7b49\u4eba\u91cc\u9762\u662f\u7b2c\u4e00\u53f7\u6218\u5c06\uff0c\u4e3a\u4e86\u770b\u770b\u4ed6\u7684\u8eab \u624b\uff0c\u5f53\u7136\u4e5f\u5c31\u9894\u9996\u540c\u610f\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u968f\u5373\u4e5f\u624b\u63d0\u957f\u5251\uff0c\u7f13\u7f13\u8d70\u4e86\u51fa\u6765\u3002 \u3000\u3000\u5176\u5b9e\u53f8\u592a\u5e73\u6b63\u662f\u770b\u51c6\u4e86\u9ea6\u5c0f\u660e\u7528\u5251\uff0c\u624d\u62a2\u7740\u8981\u81ea\u5df1\u51fa\u9635\u3002 \u3000\u3000\u4ed6\u80fd\u88ab\u79f0\u4f5c\u5251\u9b54\uff0c\u81ea\u7136\u662f\u56e0\u5728\u5251\u6cd5\u4e0a\u9020\u8be3\u4e0d\u51e1\uff0e\u4ee5\u5251\u5236\u5251\uff0e\u6b63\u662f\u4ed6\u7684\u62ff\u624b\u7edd\u6d3b\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u53f8\u592a\u5e73\uff0c\u662f\u7531\u4f60\u5148\u51fa\u624b\uff0c\u8fd8\u662f\u7531\u5728\u4e0b\u5148\u51fa\u624b\uff1f\u201d \u3000\u3000\u53f8\u592a\u5e73\u7728\u52a8\u7740\u4e00\u5bf9\u9f20\u773c\u9053\uff1a\u201c\u4f60\u5c0f\u5b50\u600e\u77e5\u54b1\u53eb\u53f8\u592a\u5e73\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u6709\u9053\u662f\u597d\u4e8b\u4e0d\u51fa\u95e8\uff0c\u6076\u4e8b\u4f20\u5343\u91cc\u3002\u4f60\u8fd9\u8001\u738b\u516b\u86cb\u6076\u540d\u662d\u5f70\uff0c\u5728\u4e0b\u4e0d\u804b\u4e0d\u778e\uff0c \u53c8\u600e\u80fd\u4e0d\u77e5\u9053\uff1f\u201d \u3000\u3000\u53f8\u592a\u5e73\u2014\u542c\u5bf9\u65b9\u5c45\u7136\u79f0\u81ea\u5df1\u738b\u516b\u86cb\uff0c\u66b4\u543c\u2014\u58f0\u9053\uff1a\u201c\u8001\u5b50\u5c31\u8ba9\u4f60\u5148\u51fa\u62db\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u7b11\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0e\u4f60\u7237\u7237\u5c31\u4e0d\u5ba2\u6c14\u4e86\uff01\u201d \u3000\u3000\u4ed6\u8bdd\u58f0\u752b\u6bd5\uff0c\u7f13\u7f13\u5411\u524d\u53c8\u8d70\u4e86\u51e0\u6b65\uff0e\u4e00\u526f\u540a\u513f\u90ce\u5f53\u7684\u6a21\u6837\uff0c\u63a5\u7740\u957f\u5251\u4e00\u4e3e\uff0c\u659c\u659c\u7684\u5288\u4e86 \u4e0b\u6765\u3002 \u3000\u3000\u770b\u6765\u9ea6\u5c0f\u660e\u662f\u6545\u610f\u5982\u6b64\u3002 \u3000\u3000\u4ed6\u7684\u7528\u610f\uff0c\u4e0d\u5916\u662f\u5148\u677e\u540e\u7d27\uff0c\u82e5\u4e00\u5f00\u59cb\u5c31\u65bd\u51fa\u6740\u624b\u950f\uff0c\u5f88\u53ef\u80fd\u4f1a\u62db\u81f4\u5bf9\u5c11\u6797\u65b9\u9762\u4e0d\u5229\u7684 \u540e\u679c\uff0c\u4ed6\u4e0d\u80fd\u4e0d\u987e\u5168\u5927\u5c40\u3002 \u3000\u3000\u4e0a\u6b21\u4ed6\u5728\u4e07\u6708\u5ce1\uff0c\u66fe\u72ec\u6218\u5200\u3001\u5251\u53cc\u9b54\uff0c\u65e9\u77e5\u5251\u9b54\u529f\u592b\u9ad8\u4f4e\uff0c\u5982\u4eca\u53ea\u6218\u5251\u9b54\u4e00\u4eba\uff0e\u5f53\u7136\u6ca1 \u628a\u4ed6\u653e\u5728\u5fc3\u4e0a\u3002 \u3000\u3000\u53f8\u592a\u5e73\u4e00\u89c1\u9ea6\u5c0f\u660e\u597d\u50cf\u53ea\u662f\u4e09\u811a\u732b\u628a\u5f0f\uff0c\u51fa\u5251\u52bf\u9053\uff0c\u597d\u8c61\u4e61\u4e0b\u4eba\u9504\u5934\u9504\u5730\u4e00\u6837\uff0c\u4e0d\u7531\u5fc3 \u91cc\u6697\u7b11\uff0c\u56e0\u4e4b\uff0c\u4e5f\u4e0d\u7528\u51fa\u771f\u529f\u592b\uff0c\u53ea\u662f\u62a1\u5251\u5411\u6765\u5251\u4e0a\u683c\u53bb\u3002 \u3000\u3000\u53ea\u542c\u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c\u53cc\u5251\u4e00\u63a5\uff0c\u9ea6\u5c0f\u660e\u4e00\u58f0\u602a\u53eb\uff0c\u7adf\u88ab\u9707\u51fa\u597d\u51e0\u6b65\uff0c\u800c\u4e14\uff0c\u8fd8\u4e0d\u4f4f\u54ac\u7259 \u54a7\u5634\u3002 \u3000\u3000\u53f8\u592a\u5e73\u51b7\u7b11\u9053\uff1a\u201c\u5c0f\u5b50\uff0e\u521a\u624d\u4f60\u765e\u86e4\u87c6\u6253\u5475\u6b20\uff0c\u53e3\u6c14\u4e0d\u5c0f\uff0c\u4f60\u5230\u5e95\u5b66\u8fc7\u5251\u6cd5\u6ca1\u6709\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u5598\u7740\u5927\u6c14\u9053\uff1a\u201c\u4e0d\u7ba1\u5b66\u8fc7\u6ca1\u5b66\u8fc7\uff0c\u53cd\u6b63\u5728\u4e0b\u5df2\u7ecf\u63a5\u4e86\u4f60\u8fd9\u738b\u516b\u86cb\u4e00\u62db\uff01\u201d \u3000\u3000\u201c\u4f60\u8fd8\u6562\u9a82\u4eba\uff1f\u201d \u3000\u3000\u201c\u9a82\u4e86\u662f\u8d5a\u7684\u3002\u201d \u3000\u3000\u53f8\u592a\u5e73\u4e00\u54ac\u7259\uff0c\u7ec8\u4e8e\u4f7f\u51fa\u4e86\u4ed6\u7684\u5251\u6cd5\u7edd\u62db\u201c\u5929\u9b54\u5341\u4e8c\u5f0f\u201d\uff0c\u5239\u65f6\u95f4\u5bd2\u8292\u95ea\u70c1\uff0c\u5149\u5f71\u7ffb\u843d\uff0c \u628a\u9ea6\u5c0f\u660e\u5b8c\u5168\u7b3c\u7f69\u5728\u4e00\u56e2\u5251\u6c14\u4e4b\u4e2d\u3002 \u3000\u3000\u4e0d\u8fc7\u4ed6\u5251\u62db\u51fa\u624b\u4e4b\u540e\uff0c\u624d\u89c9\u5f97\u8fd9\u6837\u672a\u514d\u5c0f\u9898\u5927\u4f5c\uff0e\u5bf9\u4ed8\u4e00\u4e2a\u6709\u5982\u4e61\u4e0b\u4eba\u822c\u7684\u5e73\u5eb8\u4e4b\u8f88\uff0c \u4f55\u7528\u5982\u6b64\u94fa\u5f20\u3002 \u3000\u3000\u4f46\u89c1\u9ea6\u5c0f\u660e\u5de6\u95ea\u53f3\u8eb2\uff0c\u602a\u53eb\u8fde\u8fde\uff0c\u5b8c\u5168\u9677\u5165\u624b\u5fd9\u811a\u4e71\u4e4b\u5883\u3002 \u3000\u3000\u5947\u602a\u7684\u662f\u53f8\u592a\u5e73\u4e0d\u8bba\u5251\u52bf\u5982\u4f55\u7ef5\u5bc6\u51cc\u5386\uff0c\u5374\u59cb\u7ec8\u4f24\u4e0d\u5230\u4ed6\u3002 \u3000\u3000\u53f8\u592a\u5e73\u2014\u53e3\u6c14\u628a\u201c\u5929\u9b54\u5341\u4e8c\u5f0f\u201d\u65bd\u5c55\u5b8c\u6bd5\uff0e\u6014\u4e86\u6014\u9053\uff1a\u201c\u4f60\u5c0f\u5b50\u7a76\u7adf\u7528\u7684\u4ec0\u4e48\u8eab\u6cd5\uff1f\u5c45 \u7136\u6ca1\u4f24\u5728\u8001\u5b50\u7684\u5251\u4e0b\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u7b11\u9053\uff1a\u201c\u5b59\u5b50\u6253\u7237\u7237\uff0c\u82e5\u4f24\u4e86\u7237\u7237\uff0c\u90a3\u8fd8\u6210\u4ec0\u4e48\u4f53\u7edf\uff1f\u201d \u3000\u3000\u53f8\u592a\u5e73\u53ea\u6c14\u5f97\u7259\u6839\u53d1\u75d2\uff0c\u731b\u8d77\u2014\u5251\uff0c\u76f4\u5411\u9ea6\u5c0f\u660e\u5f53\u5934\u5288\u53bb\u3002 \u3000\u3000\u53cc\u5251\u4e00\u63a5\uff0c\u706b\u661f\u76f4\u5192\uff0c\u53f8\u592a\u5e73\u5f53\u573a\u5411\u540e\u6454\u51fa\u4e94\u516d\u6b65\uff0e\u51e0\u4e4e\u62ff\u6869\u4e0d\u7a33\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u5e76\u672a\u8ffd\u88ad\uff0e\u6a2a\u5251\u51b7\u51b7\u4e00\u7b11\u9053\uff1a\u201c\u738b\u516b\u86cb\uff0c\u4f60\u8fd8\u79f0\u7684\u4ec0\u4e48\u5251\u9b54\uff0c\u8fde\u5728\u4e0b\u8fd9\u4e61\u4e0b\u4eba\u90fd \u4e0d\u5982\uff0c\u5e72\u8106\u4e70\u5757\u8c46\u8150\u649e\u6b7b\u7b97\u4e86\uff01\u201d \u3000\u3000\u53f8\u592a\u5e73\u5b9e\u5728\u5fcd\u4e0d\u4e0b\u8fd9\u53e3\u6c14\uff0c\u7565\u4e00\u5598\u606f\uff0e\u4fbf\u53c8\u653b\u4e86\u4e0a\u6765\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u4e3a\u987e\u5168\u5927\u5c40\uff0c\u51b3\u5b9a\u6682\u65f6\u4e0d\u5f04\u51fa\u6d41\u8840\u573a\u9762\uff0c\u4f46\u5374\u5df2\u4e0d\u518d\u5ba2\u6c14\uff0c\u201c\u5237\u3001\u5237\u3001\u5237\u201d\u3000\u4e00 \u8fde\u4e09\u5251\uff0c\u53c8\u628a\u53f8\u592a\u5e73\u903c\u5f97\u4e0d\u4f4f\u540e\u9000\u3002 \u3000\u3000\u53f8\u592a\u5e73\u5728\u7f9e\u6124\u4ea4\u52a0\u4e4b\u4e0b\uff0c\u53ea\u5f97\u65bd\u51fa\u4ed6\u6700\u540e\u7684\u6740\u624b\u950f\uff0c\u7a81\u7136\u65bd\u51fa\u5929\u9b54\u5341\u4e8c\u5f0f\u4e2d\u7684\u6700\u540e\u4e00\u5f0f \u201c\u4e4c\u9f99\u7a7f\u5854\u201d\uff0c\u95ea\u7535\u822c\u4e00\u5251\u523a\u5411\u9ea6\u5c0f\u660e\u7684\u5fc3\u7a9d\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u5fae\u5fae\u4e00\u7b11\uff0c\u4e5f\u65bd\u51fa\u201c\u5999\u5999\u5251\u8bc0\u201d\u4e2d\u7684\u7b2c\u4e09\u62db\u201c\u4f5b\u6e38\u4e5d\u5929\u201d\uff0c\u5251\u950b\u6709\u5982\u5929\u5916\u98de\u8679\uff0c \u3000\u4e00\u9053\u767d\u5149\uff0c\u63a0\u5411\u90a3\u523a\u6765\u7684\u2014\u5251\u3002 \u3000\u3000\u53ea\u542c\u201c\u94ee\u201d\u7684\u4e00\u58f0\uff0c\u53f8\u592a\u5e73\u7684\u5251\uff0c\u7acb\u5373\u88ab\u9707\u98de\u51fa\u53bb\u3002 \u3000\u3000\u53f8\u592a\u5e73\u987f\u611f\u534a\u81c2\u9165\u9ebb\uff0c\u5927\u9a87\u4e4b\u4e0b\uff0c\u521a\u8981\u62fc\u529b\u540e\u8dc3\uff0c\u4e0d\u60f3\u5bf9\u65b9\u7684\u5251\u5c16\uff0c\u5374\u5df2\u62b5\u4e0a\u4e86\u54bd\u5589\u3002 \u8fd9\u53d8\u5316\u9664\u82d7\u7d20\u82d3\u5fc3\u91cc\u6709\u6570\u5916\uff0c\u5176\u4f59\u6240\u6709\u7684\u4eba\uff0c\u65e0\u4e0d\u611f\u5230\u7a81\u5982\u5176\u6765\u3002 \u3000\u3000\u5176\u4f59\u4e09\u9b54\u4e00\u89c1\u5251\u9b54\u53f8\u592a\u5e73\u843d\u5f97\u8fd9\u822c\u5149\u666f\uff0c\u5185\u5fc3\u867d\u53c8\u9a87\u53c8\u6025\uff0c\u5374\u8c01\u90fd\u4e0d\u6562\u8f7b\u4e3e\u5984\u52a8\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u559d\u9053\uff1a\u201c\u4f60\u4eec\u4e09\u4e2a\u738b\u516b\u86cb\u82e5\u6562\u8fc7\u6765\uff0c\u5728\u4e0b\u5c31\u4e00\u5251\u5148\u8981\u4e86\u4ed6\u7684\u72d7\u547d\uff01 \u3000\u3000\u63a5\u7740\u518d\u559d\u4ee4\u53f8\u592a\u5e73\u9053\uff1a\u201c\u8dea\u4e0b\uff01\u201d \u3000\u3000\u53f8\u592a\u5e73\u4e3a\u4e86\u6d3b\u547d\uff0e\u7adf\u771f\u7684\u4e00\u70b9\u9aa8\u6c14\u4e5f\u6ca1\u6709\uff0e\u5f53\u573a\u8dea\u4e86\u4e0b\u53bb\uff0c\u6b64\u65f6\u4ed6\u5df2\u65e0\u5175\u5203\uff0c\u8dea\u4e0b\u4e4b\u540e\uff0c \u56e0\u4e3a\u59ff\u52bf\u6240\u9650\uff0c\u6839\u672c\u65e0\u6cd5\u9003\u8dd1\u3002 \u3000\u3000\u53ea\u542c\u970d\u5143\u4f3d\u9053\uff1a\u201c\u4e24\u4f4d\u8001\u7985\u5e08\uff0e\u4f60\u4eec\u8fd9\u4f4d\u4fd7\u5bb6\u5f1f\u5b50\uff0c\u672a\u514d\u592a\u8fc7\u4efd\u4e86\u5427\uff01\u201d \u3000\u3000\u5929\u673a\u3001\u5927\u89c9\u4e24\u4f4d\u5927\u5e08\u5c1a\u672a\u6765\u5f97\u53ca\u7b54\u8bdd\uff0c\u82d7\u7d20\u82d3\u5df2\u6717\u58f0\u9053\uff1a\u201c\u56db\u9b54\u65e2\u662f\u8ff7\u8e2a\u8c37\u7684\u5ba2\u4eba\uff0c\u6211 \u4eec\u4e5f\u662f\u8ff7\u8e2a\u8c37\u7684\u5ba2\u4eba\uff0e\u5ba2\u4eba\u548c\u5ba2\u4eba\u4e4b\u95f4\u7684\u4e8b\uff0c\u4f60\u7ba1\u5f97\u7740\u5417\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u6c89\u58f0\u9053\uff1a\u201c\u4e0d\u7ba1\u4f60\u4eec\u53cc\u65b9\u8c01\u662f\u5ba2\u4eba\uff0c\u53ea\u8981\u4e8b\u60c5\u53d1\u751f\u5728\u8ff7\u8e2a\u8c37\uff0c\u8001\u592b\u6709\u6743\u8fc7\u95ee\u3002\u201d \u3000\u3000\u5929\u673a\u5927\u5e08\u7ec8\u7a76\u662f\u51fa\u5bb6\u4eba\uff0c\u4e5f\u6709\u4e9b\u770b\u4e0d\u8fc7\u53bb\uff0c\u8bf5\u4e86\u4e00\u58f0\u4f5b\u53f7\u9053\uff1a\u201c\u5f92\u513f\uff0e\u653e\u4ed6\u56de\u53bb\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u542c\u51fa\u8fd9\u58f0\u2018\u5f92\u513f\u2019\u53eb\u7684\u662f\u81ea\u5df1\uff0c\u5fd9\u9053\uff1a\u201c\u5e08\u7236\uff0c\u54b1\u4eec\u6765\u7684\u76ee\u7684\u662f\u4ec0\u4e48\uff1f\u597d\u4e0d\u5bb9\u6613 \u624d\u5236\u4f4f\u8fd9\u738b\u516b\u86cb\uff0c\u600e\u80fd\u653e\u4ed6\uff1f\u201d \u3000\u3000\u201c\u4f60\u53ea\u7ba1\u653e\u4e86\u4ed6\uff0c\u54b1\u4eec\u6765\u7684\u76ee\u7684\uff0c\u662f\u8981\u62bc\u89e3\u5c11\u6797\u53db\u5f92\u56de\u53bb\uff0e\u9664\u6b64\u4e4b\u5916\uff0c\u7528\u4e0d\u7740\u518d\u8ddf\u522b\u4eba \u4e3a\u96be\u3002\u201d \u3000\u3000\u201c\u5e08\u7236\u672a\u514d\u592a\u6148\u60b2\u4e3a\u6000\u4e86\u5427\uff1f\u5d06\u5cd2\u4e94\u9b54\u672c\u5c31\u662f\u6c5f\u6e56\u8d25\u7c7b\uff0c\u5bf9\u4ed6\u4eec\u6148\u60b2\uff0c\u53cd\u800c\u662f\u4ef6\u574f\u4e8b\uff01\u201d \u3000\u3000\u201c\u4e3a\u5e08\u7684\u8fd8\u662f\u8981\u4f60\u653e\u4e86\u4ed6\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u54ea\u91cc\u80af\u542c\uff0c\u63a2\u624b\u4e00\u6307\uff0c\u70b9\u4e86\u53f8\u592a\u5e73\u7a74\u9053\uff0c\u63a5\u7740\u4e00\u811a\u8e22\u53bb\u3002 \u3000\u3000\u539f\u6765\u4ed6\u8fd9\u811a\uff0c\u7adf\u628a\u53f8\u592a\u5e73\u8e22\u5411\u5c11\u6797\u90a3\u5341\u4e8c\u540d\u50e7\u4eba\u7684\u843d\u811a\u4e4b\u5904\uff0e \u3000\u3000\u4e00\u9762\u53eb\u9053\uff1a\u201c\u4f17\u4f4d\u5e08\u5144\u597d\u597d\u770b\u4f4f\u8fd9\u738b\u516b\u86cb\uff0c\u5c0f\u5f1f\u73b0\u5728\u8981\u72ec\u6597\u4e09\u9b54\u3002\u201d \u3000\u3000\u4ed6\u7684\u6700\u540e\u4e00\u4e2a\u5b57\u521a\u521a\u843d\u97f3\uff0c\u4eba\u5df2\u817e\u7a7a\u800c\u8d77\uff0c\u4e00\u63a0\u6570\u4e08\uff0e\u76f4\u5411\u4e09\u9b54\u6251\u53bb\uff0c\u4eba\u5728\u7a7a\u4e2d\uff0c\u957f\u5251 \u5df2\u81ea\u51fa\u624b\u3002 \u3000\u3000\u8fd9\u7a81\u53d1\u7684\u52a8\u4f5c\uff0c\u5b9e\u5728\u592a\u5feb\u4e86\uff0c\u8fde\u5f53\u4e8b\u8005\u7684\u4e09\u9b54\uff0c\u90fd\u6709\u4e9b\u63aa\u624b\u4e0d\u53ca\u3002 \u3000\u3000\u8001\u5927\u6bd2\u9b54\u9634\u5929\u7433\uff0c\u60c5\u6025\u95f4\u6025\u6025\u4e00\u6325\u624b\uff0c\u4e00\u628a\u201c\u8840\u96fe\u6563\u201d\u4fbf\u5411\u9ea6\u5c0f\u660e\u6ca1\u5934\u6ca1\u8111\u7684\u6492\u53bb\uff0c\u7d27 \u63a5\u7740\u91d1\u86c7\u97ad\u4e5f\u51fa\u4e86\u624b\u3002 \u3000\u3000\u8001\u4e09\u5200\u9b54\u8c37\u4e00\u5cf0\u548c\u8001\u4e94\u8272\u9b54\u82b1\u79c0\uff0c\u3000\u4e00\u4e2a\u633a\u7740\u593a\u547d\u8fde\u73af\u5200\uff0c\u4e00\u4e2a\u626c\u8d77\u94c1\u6247\uff0c\u9f50\u9f50\u5c55\u5f00\u53cd \u653b\u3002 \u3000\u3000\u539f\u6765\u9ea6\u5c0f\u660e\u8d77\u610f\u72ec\u6597\u4e09\u9b54\uff0c\u4e0d\u5916\u662f\u8981\u5148\u884c\u626b\u9664\u5c11\u6797\u65b9\u9762\u7684\u969c\u788d\uff0c\u4e0d\u4f7f\u5929\u673a\u5929\u89c9\u4e24\u4f4d\u5927\u5e08 \u6709\u4ed6\u987e\u4e4b\u5fe7\u3002 \u3000\u3000\u4f46\u4e09\u9b54\u4e5f\u786e\u4e0d\u662f\u6613\u4e0e\u4e4b\u8f88\uff0c\u8bba\u6b66\u529f\uff0c\u4ed6\u4eec\u4eba\u4eba\u90fd\u53ef\u72ec\u5f53\u4e00\u9762\uff0c\u5982\u4eca\u4e09\u9b54\u8054\u624b\uff0e\u5a01\u52bf\u5c82\u8db3 \u6cdb\u6cdb\u3002\u3000\u5341\u4f59\u62db\u8fc7\u53bb\uff0c\u679c\u7136\u5df2\u628a\u9ea6\u5c0f\u660e\u903c\u5165\u4e0b\u98ce\u3002 \u3000\u3000\u82d7\u7d20\u82d3\u4e00\u89c1\u9ea6\u5c0f\u660e\u6e10\u9732\u8d25\u8c61\uff0c\u7acb\u5373\u4fbf\u633a\u5251\u52a9\u653b\u4e0a\u53bb\u3002 \u3000\u3000\u5979\u7684\u6b66\u529f\uff0c\u5df2\u548c\u9ea6\u5c0f\u660e\u76f8\u5dee\u65e0\u51e0\uff0c\u8fd9\u4e00\u52a9\u653b\u4e0a\u53bb\uff0c\u5f88\u5feb\u4fbf\u626d\u8f6c\u5f62\u52bf\uff0e\u903c\u5f97\u4e09\u9b54\u624b\u5fd9\u811a\u4e71\uff0e \u8fde\u8fde\u540e\u9000\u3002 \u3000\u3000\u8001\u5927\u6bd2\u9b54\u9634\u5929\u7433\u614c\u5fd9\u95f4\u53c8\u6492\u51fa\u4e00\u628a\u201c\u8840\u9709\u6563\u201d\u3002 \u3000\u3000\u201c\u8840\u96fe\u6563\u201d\u6bd2\u6027\u867d\u91cd\uff0c\u504f\u504f\u5bf9\u9ea6\u5c0f\u660e\u548c\u82d7\u7d20\u82d3\u534a\u70b9\u4e0d\u53d1\u751f\u6548\u529b\u3002 \u3000\u3000\u8fd9\u662f\u56e0\u4e3a\u4ed6\u4eec\u4e8c\u4eba\u4e8b\u5148\u5df2\u670d\u4e0b\u82d7\u7d20\u82d3\u8eab\u85cf\u7684\u89e3\u6bd2\u7075\u836f\u3002 \u3000\u3000\u6fc0\u6218\u95f4\uff0c\u9ea6\u5c0f\u660e\u5ffd\u7136\u4e00\u7f15\u6307\u98ce\uff0c\u53c8\u628a\u5200\u9b54\u8c37\u4e00\u5cf0\u70b9\u5012\u3002 \u3000\u3000\u6bd2\u9b54\u9634\u5929\u7433\u548c\u8272\u9b54\u82b1\u79c0\u5927\u60ca\u4e4b\u4e0b\uff0c\u6025\u6025\u4e00\u4e2a\u5012\u7eb5\uff0e\u76f4\u8dc3\u9000\u5230\u970d\u5143\u4f3d\u8eab\u8fb9\u3002 \u3000\u3000\u8fd9\u65f6\u970d\u5143\u4f3d\u8138\u8272\u65e9\u5df2\u9634\u6697\u4e0d\u5b9a\uff0c\u9634\u607b\u607b\u7b11\u4e86\u51e0\u58f0\u9053\uff1a\u201c\u8d35\u6d3e\u8fd9\u4e24\u540d\u4fd7\u5bb6\u5f1f\u5b50\uff0c\u679c\u7136\u8eab\u624b \u51fa\u4f17\uff0c\u970d\u67d0\u6df1\u611f\u4f69\u670d\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u51b7\u7b11\u9053\uff1a\u201c\u5728\u4e0b\u7528\u4e0d\u7740\u4f60\u4f69\u670d\uff0c\u73b0\u5728\u653e\u4eba\u8981\u7d27\u3002\u201d \u3000\u3000\u201c\u4f60\u8981\u653e\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u662f\u53db\u5f92\u609f\u660e\u548c\u4e5d\u540d\u94dc\u4eba\u3002\u201d \u3000\u3000\u201c\u4ed6\u4eec\u5c31\u5728\u773c\u524d\uff0c\u53ea\u8981\u4f60\u4eec\u80fd\u628a\u4ed6\u4eec\u5e26\u8d70\uff0c\u5c3d\u7ba1\u5e26\u8d70\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u671b\u4e86\u671b\u5929\u673a\u5927\u5e08\u9053\uff1a\u201c\u5e08\u7236\uff0c\u60a8\u8001\u4eba\u5bb6\u51c6\u5907\u600e\u6837\u7834\u9635\uff1f\u201d \u3000\u3000\u8c01\u77e5\u5929\u673a\u5927\u5e08\u53cd\u800c\u8d77\u4e86\u72b9\u8c6b\u3002 \u3000\u3000\u4ed6\u660e\u77e5\u8981\u628a\u609f\u660e\u548c\u4e5d\u540d\u94dc\u4eba\u62bc\u56de\u5c11\u6797\uff0c\u5fc5\u987b\u5148\u7834\u94dc\u4eba\u9635\uff0c\u5374\u53c8\u4e0d\u613f\u91c7\u53d6\u884c\u52a8\u3002 \u3000\u3000\u7406\u7531\u5f88\u7b80\u5355\uff0c\u94dc\u4eba\u9635\u539f\u662f\u5c11\u6797\u5bf9\u654c\u6cd5\u5b9d\uff0c\u5c11\u6797\u6d3e\u597d\u4e0d\u5bb9\u6613\u57f9\u517b\u51fa\u5341\u516b\u94dc\u4eba\uff0c\u5982\u4eca\u5374\u8981\u5c11 \u6797\u81ea\u5df1\u7684\u4eba\u6765\u7834\u94dc\u4eba\u9635\uff0c\u4e00\u65e6\u4f20\u626c\u51fa\u53bb\uff0c\u5c06\u4f7f\u5c11\u6797\u989c\u9762\u4f55\u5b58\uff1f \u3000\u3000\u9ea6\u5c0f\u660e\u89c1\u5929\u673a\u5927\u5e08\u8fdf\u8fdf\u4e0d\u80af\u4e0b\u4ee4\uff0c\u4e0d\u7531\u50ac\u4fc3\u7740\u9053\uff1a\u201c\u5e08\u7236\uff0c\u4f60\u8001\u4eba\u5bb6\u6765\u65f6\u66fe\u8bf4\u8fc7\u8981\u65e9\u53bb \u65e9\u56de\uff0c\u4e3a\u4ec0\u4e48\u73b0\u5728\u53cd\u800c\u72b9\u8c6b\u4e0d\u51b3\u8d77\u6765\uff1f\u201d \u3000\u3000\u5929\u673a\u5927\u5e08\u987f\u4e86\u987f\u9053\uff1a\u201c\u5c31\u70e6\u4f60\u5148\u653b\u5934\u4e00\u9635\u5982\u4f55\uff1f\u201d \u3000\u3000\u5929\u673a\u5927\u5e08\u6240\u4ee5\u8981\u547d\u9ea6\u5c0f\u660e\u653b\u9635\uff0c\u4e0d\u5916\u56e0\u9ea6\u5c0f\u660e\u5e76\u975e\u5c11\u6797\u7684\u4eba\uff0c\u4f20\u626c\u51fa\u53bb\u4e0d\u81f4\u5f71\u5411\u5c11\u6797\u7684 \u58f0\u8a89\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u867d\u7136\u5fc3\u91cc\u4e0d\u9ad8\u5174\uff0c\u5374\u4e5f\u4e0d\u5f97\u4e0d\u542c\uff0c\u4e3a\u4e86\u5bf9\u4ed8\u94c1\u7985\u4ed7\uff0c\u4ed6\u51b3\u5b9a\u6539\u7528\u8f6f\u97ad\uff0c\u56e0\u4e3a\u4ed6\u90a3 \u8f6f\u97ad\u8db3\u6709\u4e03\u516b\u5c3a\u957f\uff0e\u4e0d\u5fc5\u8fd1\u8eab\uff0c\u4fbf\u53ef\u53d1\u6325\u6548\u529b\u3002 \u3000\u3000\u4e8e\u662f\u4e00\u677e\u8170\u95f4\u6263\u628a\uff0c\u63d0\u97ad\u4fbf\u5411\u94dc\u4eba\u9635\u524d\u65b9\u8d70\u53bb\u3002 \u3000\u3000\u6b64\u523b\u7684\u970d\u5143\u4f3d\uff0c\u7531\u4e8e\u5df2\u8ba4\u51fa\u9ea6\u5c0f\u660e\u7684\u5175\u5668\uff0c\u7acb\u5373\u559d\u9053\uff1a\u201c\u4e14\u6162\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u5728\u4e0b\u5949\u547d\u7834\u9635\uff0e\u4f60\u51ed\u4ec0\u4e48\u963b\u62e6\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u4e24\u773c\u7728\u52a8\u4e00\u9635\u9053\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u9ea6\u5c0f\u660e\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u6545\u4f5c\u4e00\u6123\u9053\uff1a\u201c\u83ab\u7126\u6885\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u8001\u592b\u95ee\u4f60\u662f\u4e0d\u662f\u9ea6\u5c0f\u660e\uff1f\u201d \u3000\u3000\u201c\u5728\u4e0b\u672c\u6765\u5c31\u6ca1\u56bc\u6885\uff0c\u56bc\u7cd6\u679c\u8fd8\u5dee\u4e0d\u591a\uff0c\u53ef\u60dc\u4f60\u4e5f\u6ca1\u6709\u7cd6\u679c\u9001\u6765\u3002\u201d \u3000\u3000\u201c\u8001\u592b\u95ee\u7684\u662f\u9ea6\u5c0f\u660e\uff1f\u201d \u3000\u3000\u201c\u5728\u4e0b\u662f\u8ddf\u968f\u5e08\u7236\u6765\u62bc\u89e3\u4eba\u7684\uff0c\u5356\u7684\u4ec0\u4e48\u7126\u7164\uff1f\u970d\u76df\u4e3b\uff0c\u4f60\u597d\u50cf\u7cbe\u795e\u6709\u70b9\u95ee\u9898\uff1f\u201d \u3000\u3000\u201c\u6df7\u5e10\uff01\u201d \u3000\u3000\u201c\u90a3\u662f\u4f60\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u53ea\u6c14\u5f97\u4e00\u53e3\u6d53\u75f0\u618b\u5728\u5589\u7ba1\u91cc\uff0c\u8fde\u8fde\u545b\u54b3\u4e0d\u5df2\uff0c\u53ea\u597d\u4e0d\u518d\u8a00\u8bed\uff0c\u8fc7\u4e86\u534a\u664c\uff0c\u624d\u671b\u7740 \u5929\u673a\u5927\u5e08\u51b7\u7b11\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u8d35\u6d3e\u7adf\u6709\u8fd9\u79cd\u5f1f\u5b50\uff01\u201d \u3000\u3000\u5929\u673a\u5927\u5e08\u53cc\u624b\u5408\u5341\u9053\uff1a\u201c\u970d\u76df\u4e3b\u4e07\u52ff\u89c1\u602a\uff0c\u5c31\u56e0\u5c0f\u5f92\u6027\u60c5\u987d\u52a3\uff0e\u6240\u4ee5\u81f3\u4eca\u4e0d\u80fd\u767b\u5802\u5165\u5ba4\uff0c \u53ea\u914d\u505a\u2014\u540d\u4fd7\u5bb6\u5f1f\u5b50\u3002\u201d \u3000\u3000\u201c\u4ed6\u53ef\u6709\u6cd5\u540d\uff1f\u201d \u3000\u3000\u201c\u65e2\u662f\u4fd7\u5bb6\u5f1f\u5b50\uff0c\u4f55\u6765\u6cd5\u540d\uff1f\u201d \u3000\u3000\u201c\u90a3\u4e48\u4ed6\u4fd7\u5bb6\u59d3\u751a\u540d\u8c01\uff1f\u201d \u3000\u3000\u201c\u970d\u76df\u4e3b\u8fd8\u662f\u95ee\u4ed6\u81ea\u5df1\u597d\u4e86\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u62c5\u5fc3\u771f\u88ab\u970d\u5143\u4f3d\u8bc6\u7834\u8eab\u4efd\uff0e\u7acb\u523b\u4e3b\u52a8\u8bf4\u9053\uff1a\u201c\u5728\u4e0b\u53eb\u9ea6\u5927\u660e\uff0e\u521a\u624d\u4e0d\u8fc7\u662f\u8ddf\u4f60\u970d \u76df\u4e3b\u5f00\u5f00\u73a9\u7b11\uff0c\u4f60\u522b\u5f53\u771f\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u9ea6\u5927\u660e\uff1f\u90a3\u4e48\u4f60\u603b\u8be5\u77e5\u9053\u9ea6\u5c0f\u660e\u5427\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u9ea6\u5c0f\u660e\u662f\u5728\u4e0b\u7684\u5f1f\u5f1f\uff0c\u542c\u8bf4\u4ed6\u5728\u4f60\u624b\u4e0b\u505a\u4e8b\uff0c\u5728\u4e0b\u6b63\u8981\u89c1\u89c1\u4ed6\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u5403\u4e86\u2014\u60ca\uff0e\u6697\u9053\uff1a\u201c\u539f\u6765\u8fd9\u5c0f\u5b50\u662f\u9ea6\u5c0f\u660e\u7684\u54e5\u54e5\uff0e\u96be\u602a\u8eab\u6750\u548c\u4f7f\u7528\u7684\u5175\u5668\u4ee5\u53ca\u51fa \u624b\u62db\u672f\u5341\u5206\u76f8\u50cf\uff0c\u4e00\u4e2a\u9ea6\u5c0f\u660e\u5c31\u628a\u8ff7\u8e2a\u8c37\u95f9\u7ffb\u4e86\u5929\uff0c\u5982\u4eca\u53c8\u51fa\u6765\u4e2a\u540c\u6837\u7684\uff0c\u8fd9\u8fd8\u4e86\u5f97\uff01\u201d \u3000\u3000\u53ea\u542c\u9ea6\u5c0f\u660e\u53c8\u9053\uff1a\u201c\u970d\u76df\u4e3b\uff0e\u4e3a\u4ec0\u4e48\u4e0d\u56de\u7b54\u5728\u4e0b\u521a\u624d\u7684\u8bdd\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u51b7\u7b11\u4e86\u51e0\u58f0\u9053\uff1a\u201c\u4f60\u8bf4\u8fc7\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u5728\u4e0b\u60f3\u89c1\u89c1\u820d\u5f1f\u9ea6\u5c0f\u660e\uff01\u201d \u3000\u3000\u201c\u4ed6\u2026\u2026\u201d \u3000\u3000\u201c\u4ed6\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u2018\u4ed6\u5df2\u88ab\u8001\u592b\u6d3e\u5230\u8c37\u5916\u529e\u4e8b\u53bb\u4e86\u3002\u201d \u3000\u3000\u201c\u771f\u4e0d\u5de7\uff01\u597d\u4e0d\u5bb9\u6613\u6765\u5230\u8ff7\u8e2a\u8c37\u4e00\u8d9f\uff0c\u5374\u65e0\u6cd5\u548c\u820d\u5f1f\u76f8\u4f1a\uff0c\u5728\u4e0b\u60f3\u95ee\u95ee\u4ed6\u5728\u8d35\u8c37\u8868\u73b0\u7684 \u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u201c\u5c0a\u9a7e\u770b\u770b\u81ea\u5df1\uff0c\u5c31\u77e5\u9053\u4ed6\u4e86\uff01\u201d \u3000\u3000\u201c\u90a3\u6562\u60c5\u5f88\u4e0d\u9519\uff0c\u8fd9\u6837\u5728\u4e0b\u5c31\u653e\u5fc3\u4e86\u3002\u4e0d\u8fc7\u4ed6\u5e74\u8f7b\uff0c\u8fd8\u671b\u970d\u76df\u4e3b\u591a\u591a\u6559\u5bfc\uff01\u201d \u3000\u3000\u201c\u4ed6\u82e5\u80af\u542c\u8001\u592b\u6559\u5bfc\uff0c\u90a3\u5c31\u4e00\u5207\u597d\u529e\u4e86\uff01\u201d \u3000\u3000\u2018\u4f60\u4e0d\u59a8\u62ff\u51fa\u76df\u4e3b\u7684\u6743\u5a01\u6765\uff0c\u8c05\u4ed6\u4e0d\u6562\u4e0d\u6015\uff0e\u201d \u3000\u3000\u201c\u4ed6\u662f\u5929\u4e0d\u6015\u5730\u4e0d\u6015\uff01\u201d \u3000\u3000\u201c\u7cdf\u7cd5\uff01\u820d\u5f1f\u600e\u4e48\u8fd8\u662f\u6027\u60c5\u4e0d\u6539\u3002\u4e0d\u7ba1\u5982\u4f55\uff0c\u5728\u4e0b\u5e0c\u671b\u970d\u76df\u4e3b\u80fd\u591a\u63d0\u62d4\u63d0\u62d4\u4ed6\uff01\u201d \u3000\u3000\u201c\u597d\u5427\uff01\u6709\u673a\u4f1a\u518d\u8bf4\u3002\u201d \u3000\u3000\u201c\u591a\u8c22\u5566\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u6765\u5230\u4e5d\u540d\u94dc\u4eba\u9635\u524d\uff0c\u4f46\u89c1\u4e5d\u540d\u94dc\u4eba\uff0c\u5341\u516b\u53ea\u773c\u775b\u77aa\u5f97\u72b9\u5982\u94dc\u73b2\uff0c\u89c6\u7ebf\u5168\u96c6\u4e2d\u5728\u81ea \u5df1\u8138\u4e0a\u3002 \u3000\u3000\u5947\u602a\u7684\u662f\u4e5d\u540d\u94dc\u4eba\uff0c\u81ea\u4e07\u6708\u5ce1\u76f8\u4f1a\u5230\u73b0\u5728\uff0c\u9ea6\u5c0f\u660e\u5e76\u672a\u542c\u5230\u4efb\u4f55\u2014\u4eba\u8bb2\u8fc7\u8bdd\uff0c\u5f88\u50cf\u662f\u4e00 \u7fa4\u54d1\u5df4\uff0e \u3000\u3000\u9ea6\u5c0f\u660e\u7ec8\u4e8e\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u4f60\u4eec\u4e3a\u4ec0\u4e48\u4e0d\u8bb2\u8bdd\uff1f\u662f\u4e0d\u662f\u970d\u76df\u4e3b\u7ed9\u4f60\u4eec\u5403\u4e86\u4ec0\u4e48\u836f\uff1f\u6ca1\u5173 \u7cfb\uff0c\u6211\u8fd9\u91cc\u6709\u73b0\u6210\u7684\u89e3\u836f\uff0c\u4f60\u4eec\u54ea\u4f4d\u8981\uff0e\u53ea\u7ba1\u6765\u62ff\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u7684\u8fd9\u4e00\u7740\uff0c\u5206\u660e\u662f\u8981\u5206\u6563\u4e5d\u540d\u94dc\u4eba\u7684\u6ce8\u610f\u529b\uff0c\u4ee5\u4fbf\u7701\u4e9b\u529b\u91cf\u653b\u9635\u3002 \u3000\u3000\u8c01\u77e5\u90a3\u4e5d\u540d\u94dc\u4eba\u6839\u672c\u4e0d\u7406\u4e0d\u776c\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u5f04\u4e0d\u6e05\u662f\u600e\u4e48\u56de\u4e8b\uff0c\u518d\u5411\u524d\u79fb\u52a8\u4e86\u4e24\u6b65\u9053\uff1a\u201c\u4f60\u4eec\u54d1\u5df4\u4e5f\u6ca1\u5173\u7cfb\uff0c\u5728\u4e0b\u73b0\u5728\u5c31\u8981 \u653b\u9635\u4e86\u3002\u201d \u3000\u3000\u8bf4\u7740\uff0c\u626c\u8155\u4e00\u6296\uff0c\u90a3\u8f6f\u97ad\u72b9\u5982\u4e00\u6761\u98de\u86c7\u76f4\u5411\u524d\u6392\u6700\u53f3\u4e00\u540d\u94dc\u4eba\u7684\u8116\u5b50\u4e0a\u7ed5\u53bb\uff0c\u4e0d\u4f46\u624b\u6cd5 \u5947\u51c6\uff0c\u800c\u4e14\u90a3\u8f6f\u97ad\u98de\u884c\u7684\u65b9\u5411\u4e5f\u5947\u5e7b\u83ab\u6d4b\u3002 \u3000\u3000\u90a3\u94dc\u4eba\u4f3c\u662f\u4e5f\u5403\u4e86\u4e00\u60ca\uff0e\u614c\u5fd9\u2014\u504f\u5934\uff0c\u94c1\u7985\u6756\u968f\u5373\u6a2a\u626b\u800c\u51fa\u3002\u90a3\u94c1\u7985\u6756\u867d\u7136\u4e5f\u6709\u4e03\u516b\u5c3a \u957f\uff0c\u4f46\u56e0\u987b\u53cc\u624b\u63e1\u6301\uff0c\u65e0\u5f62\u4e2d\u77ed\u4e86\u4e8c\u4e09\u5c3a\u957f\u5ea6\uff0c\u81ea\u662f\u65e0\u6cd5\u51fb\u5230\u9ea6\u5c0f\u660e\u3002 \u3000\u3000\u97ad\u3001\u6756\u4e00\u63a5\uff0c\u94c1\u7985\u6756\u7acb\u523b\u88ab\u97ad\u68a2\u7f20\u4f4f\u3002 \u3000\u3000\u5982\u6b64\u4e00\u6765\uff0e\u4e24\u4eba\u90fd\u4e0d\u5f97\u4e0d\u7528\u4e0a\u5168\u8eab\u4e4b\u529b\uff0c\u770b\u8c01\u80fd\u62c9\u52a8\u8c01\u3002 \u3000\u3000\u90a3\u94dc\u4eba\u672c\u5c31\u529b\u5927\u65e0\u7a77\uff0c\u518d\u52a0\u94c1\u7985\u6756\u8d77\u4e86\u6760\u6746\u4f5c\u7528\uff0c\u7adf\u7136\u4e00\u4e0b\u5b50\u628a\u9ea6\u5c0f\u660e\u7529\u4e0a\u4e86\u534a\u7a7a\u3002 \u3000\u3000\u987f\u65f6\uff0e\u73b0\u573a\u5f62\u6210\u4e86\u7a7a\u4e2d\u629b\u4eba\u7684\u5947\u666f\uff0c\u6240\u6709\u89c2\u6218\u4e4b\u4eba\uff0c\u8fde\u4e5d\u540d\u94dc\u4eba\u5728\u5185\uff0c\u90fd\u60c5\u4e0d\u81ea\u7981\u4ef0\u5934 \u5f80\u4e0a\u89c2\u770b\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u53ea\u542c\u94dc\u4eba\u9635\u540e\u6392\uff0c\u60f3\u8d77\u4e86\u4e24\u58f0\u95f7\u54fc\u3002 \u3000\u3000\u5f53\u4f17\u4eba\u8f6c\u79fb\u89c6\u7ebf\uff0c\u5411\u94dc\u4eba\u9635\u540e\u65b9\u770b\u65f6\uff0c\u7adf\u662f\u4e24\u540d\u94dc\u4eba\u5012\u5730\u4e0d\u8d77\u3002 \u3000\u3000\u539f\u6765\u82d7\u7d20\u82d3\u5df2\u770b\u51c6\u94dc\u4eba\u9635\u5e03\u6210\u4e4b\u540e\uff0c\u6240\u6709\u94dc\u4eba\u90fd\u662f\u987e\u524d\u4e0d\u987e\u540e\uff0c\u56e0\u4e4b\uff0c\u7adf\u7136\u6765\u4e86\u4e2a\u5077\u88ad\uff0c \u77ac\u606f\u95f4\u4ee5\u9694\u7a7a\u6253\u7a74\u624b\u6cd5\uff0e\u70b9\u4e2d\u4e86\u4e24\u540d\u94dc\u4eba\u7a74\u9053\u3002 \u3000\u3000\u800c\u6b64\u523b\u9ea6\u5c0f\u660e\u4e5f\u5df2\u8f7b\u98d8\u98d8\u7684\u843d\u4e0b\u5730\u6765\uff0c\u53c8\u5728\u94dc\u4eba\u9635\u524d\u65b9\u5c55\u5f00\u653b\u51fb\u3002 \u3000\u3000\u970d\u5143\u4f3d\u2014\u89c1\u94dc\u4eba\u9635\u88ab\u653b\u7834\uff0c\u60c5\u6025\u4e4b\u4e0b\uff0c\u53ea\u597d\u53d1\u51fa\u6697\u53f7\uff0c\u547d\u4ee4\u9ea6\u5c0f\u660e\u7684\u56db\u540d\u5e08\u5144\u7f57\u94ee\u3001\u4e01 \u4e00\u9b42\u3001\u9ad8\u5d99\u3001\u5f20\u656c\u5b89\u4e00\u8d77\u63a9\u6740\u8fc7\u6765\u3002 \u3000\u3000\u53e6\u4e00\u65b9\u9762\uff0c\u8c03\u606f\u5b8c\u6bd5\u7684\u5d06\u5cd2\u4e09\u9b54\u9634\u5929\u7433\u3001\u8c37\u4e00\u5cf0\u3001\u82b1\u79c0\u4e5f\u8ddf\u7740\u52a0\u5165\u6218\u6597\u3002 \u3000\u3000\u5230\u8fd9\u65f6\uff0c\u5929\u673a\u5929\u89c9\u5927\u5e08\u548c\u968f\u5e26\u7684\u5c11\u6797\u5341\u4e8c\u540d\u9ad8\u624b\uff0c\u624d\u4e0d\u5f97\u4e0d\u8d77\u800c\u8fce\u6218\u3002\u4e00\u65f6\u4e4b\u95f4\uff0c\u6709\u5982 \u5929\u5730\u53d8\u8272\uff0c\u5404\u79cd\u5175\u5203\u9f50\u98de\uff0c\u558a\u6740\u4e4b\u58f0\u4e0d\u7edd\u4e8e\u8033\u3002 \u3000\u3000\u800c\u90a3\u5269\u4e0b\u7684\u4e03\u540d\u94dc\u4eba\uff0c\u4e5f\u4e0d\u518d\u5e03\u9635\uff0c\u5404\u633a\u94c1\u7985\u6756\u5411\u5c11\u6797\u65b9\u9762\u51b2\u6740\u3002 \u3000\u3000\u5929\u673a\u5927\u5e08\u3001\u5929\u89c9\u5927\u5e08\u3001\u9ea6\u5c0f\u660e\u3001\u82d7\u7d20\u82d3\u6b66\u529f\u867d\u9ad8\uff0c\u504f\u504f\u7f57\u94ee\u3001\u4e01\u2014\u9b42\uff0c\u9ad8\u5d99\uff0e\u5f20\u656c\u5b89\u56db \u4eba\uff0c\u4e5f\u90fd\u662f\u7edd\u9876\u9ad8\u624b\uff0c\u5c24\u5176\u4ed6\u4eec\u7684\u608d\u4e0d\u754f\u6b7b\uff0c\u53ea\u8fdb\u4e0d\u9000\uff0c\u66f4\u662f\u4ee4\u4eba\u671b\u800c\u751f\u754f\u3002\u800c\u5929\u673a\u5929\u89c9\u4e0d \u613f\u8f7b\u6613\u5f00\u542f\u6740\u6212\uff0c\u9ea6\u5c0f\u660e\u4e5f\u4e0d\u4fbf\u4f24\u4e86\u56db\u4f4d\u5e08\u5144\uff0c\u5728\u8fd9\u79cd\u60c5\u5f62\u4e0b\uff0c\u53ea\u6709\u4e14\u6218\u4e14\u9000\u3002 \u3000\u3000\u4e00\u573a\u60ca\u5929\u52a8\u5730\u7684\u60e8\u70c8\u6fc0\u6218\u8fc7\u540e\uff0c\u5c11\u6797\u65b9\u9762\u53ea\u5269\u4e0b\u5929\u673a\u5927\u5e08\u2014\u4eba\u3002 \u3000\u3000\u52a0\u4e0a\u9ea6\u5c0f\u660e\u548c\u82d7\u7d20\u82d3\uff0c\u9003\u51fa\u8ff7\u8e2a\u8c37\u7684\uff0c\u4e5f\u4e0d\u8fc7\u4e09\u4eba\u800c\u5df2\u3002 \u3000\u3000\u539f\u6765\u5929\u89c9\u5927\u5e08\u548c\u5341\u4e8c\u540d\u5c11\u6797\u5f1f\u5b50\uff0c\u9664\u4e86\u62b5\u6321\u4e0d\u4f4f\u7f57\u94ee\u56db\u4eba\u4ee5\u53ca\u5d06\u5cd2\u4e09\u9b54\u548c\u94dc\u4eba\u4eec\u7684\u653b\u52bf \u5916\uff0c\u6700\u4e3b\u8981\u7684\u662f\u5403\u4e86\u9634\u5929\u7433\u7684\u201c\u8840\u96fe\u6563\u201d\u7684\u4e8f\uff0c\u5927\u90e8\u5206\u662f\u88ab\u201c\u8840\u96fe\u6563\u201d\u5f53\u573a\u6bd2\u5f97\u660f\u53a5\u8fc7\u53bb\u3002 \u3000\u3000\u53ea\u6709\u5929\u673a\u5927\u5e08\u4ed7\u7740\u5185\u529b\u6df1\u539a\uff0c\u867d\u7136\u4e5f\u53d7\u201c\u8840\u96fe\u6563\u201d\u6240\u4fb5\uff0c\u4ecd\u80fd\u5f3a\u81ea\u652f\u6301\u7740\u548c\u9ea6\u5c0f\u660e\u3001\u82d7 \u7d20\u82d3\u9003\u51fa\u8ff7\u8e2a\u8c37\u3002 \u5367\u9f99\u751f\u300a\u5999\u7edd\u5929\u9999\u300b \u7b2c\u4e8c\u5341\u516d\u56de\u3000\u76df\u4e3b\u590d\u51fa \u3000\u3000\u51fa\u4e86\u8ff7\u8e2a\u8c37\uff0c\u5929\u673a\u5927\u5e08\u4f3c\u4e4e\u4ecd\u6709\u4e9b\u5934\u6655\u76ee\u7729\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u957f\u957f\u5401\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u5927\u5e08\uff0c\u6211\u4eec\u5c31\u5728\u9644\u8fd1\u4f11\u606f\u5427\uff01\u201d \u3000\u3000\u5929\u673a\u5927\u5e08\u70b9\u4e86\u70b9\u5934\uff0c\u968f\u5373\u5728\u9644\u8fd1\u6811\u6797\u5185\u76d8\u819d\u5750\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u82d7\u7d20\u82d3\u4e5f\u76b1\u7709\u5e7d\u5e7d\u4e00\u53f9\u9053\uff1a\u201c\u65e9\u77e5\u5982\u6b64\uff0c\u5c31\u8be5\u628a\u89e3\u6bd2\u4e39\u836f\u5148\u7ed9\u4e24\u4f4d\u5927\u5e08\u548c\u4f17\u5e08\u7236\u670d\u4e0b\u3002 \u76f4\u5230\u53cc\u65b9\u62fc\u6218\u8d77\u6765\u540e\uff0c\u6211\u867d\u7136\u60f3\u5230\u8fd9\u2014\u5c42\uff0c\u5374\u5df2\u6765\u4e0d\u53ca\u4e86\uff01\u201d \u3000\u3000\u8bf4\u7740\uff0c\u4ece\u6000\u4e2d\u638f\u51fa\u7389\u74f6\uff0e\u5012\u4e86\u4e24\u9897\u4e39\u4e38\uff0e\u606d\u606d\u656c\u656c\u7684\u9012\u4e0e\u5929\u673a\u5927\u5e08\u3002 \u3000\u3000\u5929\u673a\u5927\u5e08\u63a5\u8fc7\u540e\u7acb\u5373\u670d\u4e0b\uff0c\u5374\u7d27\u95ed\u53cc\u76ee\uff0c\u5e76\u672a\u8a00\u8bed\uff0e \u3000\u3000\u9ea6\u5c0f\u660e\u548c\u82d7\u7d20\u82d3\u89c1\u6b64\u60c5\u5f62\uff0e\u4e5f\u5c31\u4e0d\u4fbf\u4ea4\u8c08\u3002 \u3000\u3000\u8db3\u8db3\u987f\u996d\u5de5\u592b\u4e4b\u540e\uff0e\u5929\u673a\u5927\u5e08\u624d\u7f13\u7f13\u7741\u5f00\u773c\u6765\uff0c\u9762\u8272\u51dd\u91cd\u7684\u9053\uff1a\u201c\u4e07\u60f3\u4e0d\u5230\uff0c\u8001\u8872\u548c\u5929 \u89c9\u5e08\u5f1f\u5e26\u7740\u5341\u4e8c\u540d\u5f1f\u5b50\u524d\u6765\uff0c\u5982\u4eca\u7adf\u53ea\u5269\u8001\u8872\u4e00\u4eba\uff0c\u8fd9\u8ba9\u8001\u8872\u5982\u4f55\u56de\u5c11\u6797\u5411\u638c\u95e8\u5e08\u5144\u590d\u547d\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u5b89\u6170\u7740\u9053\uff1a\u201c\u4e8b\u60c5\u5df2\u7ecf\u53d1\u751f\u4e86\uff0e\u5927\u5e08\u4f24\u611f\u53c8\u6709\u4f55\u7528\uff1f\u636e\u665a\u8f88\u9884\u6599\uff0c\u5929\u89c9\u5927\u5e08\u548c\u8d35 \u5bfa\u90a3\u5341\u4e8c\u5f1f\u5b50\uff0c\u591a\u6570\u662f\u4e2d\u6bd2\u88ab\u971c\u5143\u4f3d\u6d3b\u6349\uff0c\u5728\u76ee\u524d\u7684\u60c5\u51b5\u4e0b\uff0c\u970d\u5143\u4f3d\u5fc5\u4e0d\u4f1a\u6740\u5bb3\u4ed6\u4eec\u3002\u201d \u3000\u3000\u5929\u673a\u5927\u5e08\u6447\u4e86\u6447\u5934\u9053\uff1a\u201c\u970d\u5143\u4f3d\u7eb5\u7136\u4e0d\u6740\u5bb3\u4ed6\u4eec\uff0c\u4f46\u5802\u5802\u5c11\u6797\u5f1f\u5b50\uff0c\u843d\u5230\u5982\u6b64\u5730\u6b65\uff0c\u4e00 \u65e6\u4f20\u626c\u51fa\u53bb\uff0c\u6211\u5c11\u6797\u4e00\u6d3e\uff0c\u7167\u6837\u4e5f\u8981\u58f0\u8a89\u626b\u5730\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u6b63\u8272\u9053\uff1a\u201c\u81ea\u53e4\u90aa\u4e0d\u80dc\u6b63\uff0c\u5927\u5e08\u8fd8\u662f\u60f3\u5f00\u4e9b\u597d\u3002\u201d \u3000\u3000\u5929\u673a\u5927\u5e08\u7f13\u7f13\u7ad9\u8d77\u8eab\u6765\u9053\uff1a\u201c\u8fd9\u6b21\u8fdb\u5165\u8ff7\u8e2a\u8c37\uff0c\u591a\u627f\u5c0f\u65bd\u4e3b\u548c\u82d3\u513f\u5e2e\u5fd9\uff0c\u8001\u8872\u73b0\u5728\u5c31\u4ee3 \u638c\u95e8\u5e08\u5144\uff0c\u5411\u4e8c\u4f4d\u81f4\u5c04\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u8083\u5bb9\u9053\uff1a\u201c\u5927\u5e08\u8fd9\u6837\u8bf4\u8bdd\u5c31\u592a\u89c1\u5916\u4e86\u3002\u6570\u6708\u524d\u665a\u8f88\u5728\u5415\u6881\u5c71\u4e5d\u5929\u7384\u9634\u6d1e\u5e9c\u5916\uff0c\u773c \u89c1\u5c31\u8981\u5403\u9634\u624b\u4e00\u9b54\u7684\u4e8f\uff0e\u591a\u627f\u8d35\u6d3e\u638c\u95e8\u5927\u5e08\u4ed7\u4e49\u89e3\u56f4\u3002\u665a\u8f88\u4eca\u5929\u968f\u5927\u5e08\u5230\u8ff7\u8e2a\u8c37\u53bb\uff0c\u8fd9\u70b9\u5c0f \u4e8b\uff0c\u53c8\u7b97\u5f97\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u82d7\u7d20\u82d3\u4e5f\u9053\uff1a\u201c\u5927\u5e08\u4e0d\u5fc5\u5ba2\u6c14\u3002\u201d \u3000\u3000\u5929\u673a\u5927\u5e08\u51c4\u51c9\u4e00\u7b11\u9053\uff1a\u201c\u4e5f\u597d\uff0c\u5f7c\u6b64\u90fd\u7528\u4e0d\u7740\u518d\u5ba2\u5957\u4ec0\u4e48\uff0c\u54b1\u4eec\u73b0\u5728\u5c31\u8be5\u5206\u624b\u4e86\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u4e0d\u89c9\u4e00\u60ca\u9053\uff1a\u201c\u5927\u5e08\u8981\u5230\u54ea\u91cc\u53bb\uff1f\u201d \u3000\u3000\u5929\u673a\u5927\u5e08\u9053\uff1a\u201c\u8001\u8872\u5fc5\u987b\u9a6c\u4e0a\u56de\u5c11\u6797\u5411\u638c\u95e8\u5e08\u5144\u8bf7\u7f6a\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u5927\u5e08\u7eb5\u7136\u5fc5\u987b\u8fd4\u56de\u5c11\u6797\uff0c\u4e5f\u8be5\u5148\u5230\u767d\u59d1\u5bfa\u4e00\u8d9f\u3002\u201d \u3000\u3000\u201c\u6709\u5fc5\u8981\u5417\uff1f\u201d \u3000\u3000\u201c\u5927\u5e08\u603b\u8be5\u4eb2\u81ea\u5411\u609f\u5c18\u4f4f\u6301\u4ea4\u4ee3\u4e00\u4e0b\u3002\u201d \u3000\u3000\u5929\u673a\u5927\u5e08\u6c89\u541f\u4e86\u4e0b\uff0c\u7ec8\u4e8e\u9894\u9996\u9053\uff1a\u201c\u597d\u5427\uff01\u73b0\u5728\u5c31\u56de\u767d\u59d1\u5bfa\u53bb\u3002\u201d \u3000\u3000\u56de\u5230\u767d\u59d1\u5bfa\uff0c\u5df2\u662f\u5348\u540e\u3002\u5929\u673a\u5927\u5e08\u88ab\u609f\u5c18\u63a5\u8fdb\u7985\u5ba4\uff0c\u9ea6\u5c0f\u660e\u548c\u82d7\u7d20\u82d3\u5219\u5f84\u81ea\u8fd4\u5854\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u7b49\u4eba\u6b63\u7126\u6025\u7684\u7b49\u5f85\u6d88\u606f\uff0c\u4e00\u89c1\u4e8c\u4eba\u56de\u6765\uff0c\u90fd\u6025\u5207\u5207\u7684\u8be2\u95ee\u8fdb\u5165\u8ff7\u8e2a\u8c37\u7684\u7ecf\u8fc7\u3002 \u3000\u3000\u4ed6\u4eec\u542c\u8fc7\u9ea6\u5c0f\u660e\u548c\u82d7\u7d20\u82d3\u7684\u53d9\u8ff0\u540e\uff0e\u3000\u4e00\u65f6\u4e4b\u95f4\uff0c\u90fd\u9762\u8272\u51dd\u91cd\uff0c\u9eef\u7136\u65e0\u8bed\u3002 \u3000\u3000\u591a\u65f6\uff0c\u4f59\u4ea6\u4e50\u624d\u6253\u7834\u6c89\u5bc2\uff0c\u9053\uff1a\u201c\u770b\u6765\u54b1\u4eec\u5728\u767d\u59d1\u5bfa\u4e5f\u65e0\u6cd5\u4e45\u5f85\uff0e\u73b0\u5728\u5c31\u8be5\u5546\u8bae\u5546\u8bae\u4e0b \u4e00\u6b65\u8be5\u5230\u4ec0\u4e48\u5730\u65b9\u53bb\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u2014\u6123\u9053\uff1a\u201c\u8fd9\u5730\u65b9\u4e0d\u662f\u5f88\u597d\u5417\uff1f\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u9053\uff1a\u201c\u8ff7\u8e2a\u8c37\u5b9e\u529b\u65e5\u5927\uff0c\u970d\u5143\u4f3d\u8fdf\u65e9\u603b\u4f1a\u77e5\u9053\u54b1\u4eec\u7684\u85cf\u8eab\u4e4b\u5904\uff0c\u4ed6\u5c82\u80af\u8f7b\u6613\u653e \u8fc7\uff1f\u201d \u3000\u3000\u738b\u5927\u5eb7\u9053\uff1a\u201c\u4ffa\u65e9\u5c31\u8bf4\u8fc7\uff0c\u6700\u597d\u662f\u64a4\u5230\u5d02\u5c71\uff0c\u54b1\u4eec\u5c31\u53ef\u5b89\u5b89\u7a33\u7a33\u5f53\u5c71\u5927\u738b\u4e86\u3002\u4ffa\u5305\u7ba1\u4f60 \u4eec\u90fd\u80fd\u5403\u9999\u7684\u559d\u8fa3\u7684\uff0c\u4f55\u7528\u50cf\u73b0\u5728\u8fd9\u822c\u8eb2\u8eb2\u85cf\u85cf\u3002\u660e\u660e\u4e00\u4e2a\u4eba\uff0c\u5374\u53d8\u5f97\u50cf\u4e2a\u8001\u9f20\u4e00\u6837\u3002\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u82e6\u7b11\u9053\uff1a\u201c\u738b\u5144\u7684\u8bdd\u56fa\u7136\u6709\u7406\uff0e\u4f46\u8ff7\u8e2a\u8c37\u662f\u80e1\u524d\u76df\u4e3b\u521b\u4e0b\u7684\u57fa\u4e1a\uff0c\u54b1\u4eec\u4e0d\u80fd\u8f7b\u8a00 \u653e\u5f03\uff0e\u7eb5\u7136\u79bb\u5f00\u767d\u59d1\u5bfa\uff0c\u4e5f\u4e0d\u80fd\u8d70\u5f97\u592a\u8fdc\u3002\u201d \u3000\u3000\u738b\u5927\u5eb7\u9053\uff1a\u201c\u80e1\u524d\u76df\u4e3b\u5df2\u7ecf\u51fa\u73b0\u4e86\uff0c\u65e2\u7136\u8fd8\u8981\u6536\u590d\u8ff7\u8e2a\u8c37\uff0e\u4e3a\u4ec0\u4e48\u4e0d\u627e\u80e1\u524d\u76df\u4e3b\u53bb\uff1f\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u9887\u6709\u6fc0\u8361\u4eba\u5fc3\u7684\u4f5c\u7528\uff0c\u987f\u65f6\u5438\u5f15\u4e86\u5728\u573a\u6240\u6709\u4eba\u7684\u6ce8\u610f\u529b\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u9996\u5148\u8bf4\u9053\uff1a\u201c\u738b\u5144\u8bf4\u5f97\u5bf9\uff0c\u54b1\u4eec\u73b0\u5728\u7b2c\u4e00\u4ef6\u4e8b\uff0e\u5c31\u662f\u8981\u8bbe\u6cd5\u627e\u5230\u80e1\u524d\u76df\u4e3b\u3002\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u9053\uff1a\u201c\u6211\u53c8\u4f55\u5c1d\u4e0d\u662f\u8fd9\u4e48\u60f3\u3002\u4f46\u53c8\u5230\u54ea\u91cc\u53bb\u627e\u5462\uff1f\u201d \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u54b1\u4eec\u4e0a\u6b21\u662f\u5728\u4e07\u6708\u5ce1\u5165\u53e3\u5904\u89c1\u5230\u80e1\u524d\u76df\u4e3b\u7684\uff0e\u53ef\u4ee5\u6599\u60f3\u4ed6\u5fc5\u5b9a\u5728\u4e07\u6708\u5ce1\u9644 \u8fd1\u3002\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u6447\u5934\u9053\uff1a\u201c\u526f\u76df\u4e3b\u8fd9\u8bdd\u672a\u514d\u592a\u6b66\u65ad\uff0e\u8b6c\u5982\u5e9e\u58eb\u51b2\u8001\u524d\u8f88\uff0e\u6700\u8fd1\u51e0\u6708\u6765\uff0c\u51e0\u4e4e\u968f\u5904 \u90fd\u53ef\u4ee5\u770b\u5230\u4ed6\uff0c\u4f46\u53c8\u6709\u8c01\u77e5\u9053\u4ed6\u4f4f\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u949f\u2014\u8c6a\u6709\u4e9b\u8bed\u585e\uff0c\u987f\u4e86\u987f\u9053\uff1a\u201c\u53ef\u662f\u4e07\u6708\u5821\u7684\u674e\u7ba1\u5bb6\u4e5f\u66fe\u89c1\u8fc7\u4ed6\u3002\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u9053\uff1a\u201c\u7eb5\u7136\u674e\u7ba1\u5bb6\u89c1\u8fc7\u4e86\uff0e\u4e5f\u65e0\u6cd5\u65ad\u5b9a\u4ed6\u5728\u4e07\u6708\u5ce1\uff0c\u66f4\u4f55\u51b5\u80e1\u524d\u76df\u4e3b\u7684\u2014\u5207\u884c\u52a8\uff0c \u8fd8\u8981\u53d7\u90a3\u56db\u7edd\u8001\u4eba\u7684\u63a7\u5236\u3002\u201d \u3000\u3000\u201c\u8fd9\u6837\u8bf4\u54b1\u4eec\u2014\u5b9a\u8981\u79bb\u5f00\u8fd9\u91cc\u4e86\uff1f\u201d \u3000\u3000\u201c\u4f59\u5144\u662f\u5426\u5df2\u7ecf\u627e\u597d\u5730\u65b9\uff1f\u201d \u3000\u3000\u201c\u73b0\u5728\u6b63\u8be5\u7531\u5927\u5bb6\u5546\u8bae\u4e00\u4e0b\u3002\u201d \u3000\u3000\u949f\u4e00\u8c6a\u7ad9\u8d77\u8eab\u9053\uff1a\u201c\u9ea6\u5c0f\u5144\u5f1f\u548c\u82d7\u59d1\u5a18\u8f9b\u82e6\u4e86\u2014\u4e0a\u5348\uff0c\u521a\u56de\u6765\uff0c\u5c31\u8ba9\u4ed6\u4eec\u7528\u8fc7\u996d\u4f11\u606f\u4f11 \u606f\u518d\u8bf4\u5427\uff01\u201d \u3000\u3000\u5f53\u65e5\u4e0b\u5348\uff0e\u7fa4\u8c6a\u867d\u53c8\u805a\u96c6\u5728\u4e00\u8d77\u5546\u8bae\u4eca\u540e\u7684\u884c\u52a8\uff0c\u5374\u59cb\u7ec8\u5546\u8bae\u4e0d\u51fa\u7ed3\u679c\u3002 \u3000\u3000\u665a\u996d\u540e\uff0e\u5927\u7ea6\u4e8c\u66f4\u5de6\u53f3\uff0c\u9ea6\u5c0f\u660e\u6b63\u5df1\u4e0a\u5e8a\u5c31\u5bdd\u8fdb\u5165\u68a6\u4e61\u4e4b\u9645\uff0c\u5ffd\u7136\u6709\u4eba\u628a\u4ed6\u4ece\u68a6\u4e2d\u53eb\u9192\u3002 \u3000\u3000\u53ea\u542c\u738b\u5927\u5eb7\u58f0\u97f3\u6025\u4fc3\u4e2d\u53c8\u5145\u6ee1\u4e86\u5174\u594b\u7684\u8bed\u6c14\u9053\uff1a\u201c\u9ea6\u8001\u5f1f\uff0c\u5feb\u5230\u7b2c\u4e8c\u5c42\u5854\u91cc\u949f\u526f\u76df\u4e3b\u548c \u4f59\u5148\u751f\u90a3\u91cc\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u4e00\u9aa8\u788c\u722c\u8d77\u6765\u9053\uff1a\u201c\u5230\u5e95\u4ec0\u4e48\u4e8b\uff1f\u6df1\u66f4\u534a\u591c\u6765\u5435\u4eba\uff01\u201d \u3000\u3000\u738b\u5927\u5eb7\u50ac\u4fc3\u7740\u9053\uff1a\u201c\u5148\u522b\u95ee\uff0c\u4e0b\u53bb\u5c31\u77e5\u9053\u4e86\uff01\u201d \u3000\u3000\u8bf4\u5b8c\u8bdd\uff0c\u6025\u6025\u4e0b\u697c\u800c\u53bb\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u53ea\u5f97\u6478\u9ed1\u7a7f\u597d\u8863\u670d\uff08\u4ed6\u4f4f\u5728\u7b2c\u4e94\u5c42\uff0c\u591c\u95f4\u4e0d\u51c6\u70b9\u706f\uff09\uff0e \u3000\u3000\u6765\u5230\u7b2c\u4e8c\u5c42\uff0c\u53ea\u89c1\u9664\u82d7\u7d20\u82d3\u5916\uff0c\u6240\u6709\u7684\u4eba\u90fd\u96c6\u4e2d\u5728\u90a3\u91cc\uff0c\u800c\u4e14\u56e2\u56e2\u56f4\u7740\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u8fd9\u4eba\u8d6b\u7136\u662f\u80e1\u67cf\u9f84\uff01 \u3000\u3000\u9ea6\u5c0f\u660e\u60ca\u559c\u4e4b\u4e0b\uff0c\u8fde\u5fd9\u5206\u5f00\u4f17\u4eba\uff0c\u51b2\u4e0a\u524d\u53bb\uff0c\u53eb\u9053\uff1a\u201c\u80e1\u5e08\u5144\uff0c\u4f60\u600e\u4e48\u6765\u4e86\uff1f\u4ec0\u4e48\u65f6\u5019 \u6765\u7684\uff1f\u201d \u3000\u3000\u80e1\u67cf\u9f84\u4eb2\u5207\u65e0\u6bd4\u7684\u4e00\u628a\u62c9\u4f4f\u9ea6\u5c0f\u660e\u7684\u624b\u9053\uff1a\u201c\u5c0f\u660e\uff0e\u8fd9\u51e0\u5e74\u6765\uff0e\u5b9e\u5728\u8f9b\u82e6\u4f60\u4e86\uff01\u201d \u3000\u3000\u201c\u4f60\u8fd8\u6ca1\u8bf4\u662f\u4ec0\u4e48\u65f6\u5019\u6765\u7684\uff1f\u201d \u3000\u3000\u738b\u5927\u5eb7\u62a2\u7740\u9053\uff1a\u201c\u76df\u4e3b\u5f53\u7136\u521a\u5230\uff0e\u4ed6\u4e00\u5230\u6211\u5c31\u53bb\u558a\u4f60\u7684\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u5927\u5bb6\u90fd\u5230\u4e86\uff0e\u6211\u4e3a\u4ec0\u4e48\u6765\u5f97\u6700\u665a\uff1f\u201d \u3000\u3000\u201c\u90a3\u662f\u56e0\u4e3a\u4f60\u4f4f\u5f97\u6700\u9ad8\u2026\u2026\u201d \u3000\u3000\u738b\u5927\u5eb7\u8bf4\u5230\u8fd9\u91cc\uff0c\u731b\u4e00\u8dfa\u811a\u9053\uff1a\u201c\u7cdf\u7cd5\uff01\u8fd8\u662f\u5fd8\u8bb0\u53eb\u6587\u5929\u751f\uff0c\u9ea6\u8001\u5f1f\u4e3a\u4ec0\u4e48\u4e5f\u4e0d\u987a\u4fbf\u53eb \u4ed6\u2014\u58f0\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4f60\u53ea\u53eb\u6211\uff0c\u5e76\u6ca1\u8bf4\u8981\u6211\u53eb\u4ed6\u3002\u201d \u3000\u3000\u738b\u5927\u5eb7\u7acb\u5373\u53c8\u5954\u4e0a\u5854\u53bb\u3002 \u3000\u3000\u8fd9\u65f6\u949f\u4e00\u8c6a\u624d\u95ee\u9053\uff1a\u201c\u76df\u4e3b\u6765\u5230\u767d\u59d1\u5bfa\uff0c\u5728\u5c5e\u4e0b\u4eec\u6765\u8bf4\uff0e\u771f\u662f\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\u7684\u4e8b\uff0e\u4f60\u662f \u600e\u4e48\u77e5\u9053\u6211\u4eec\u5728\u8fd9\u91cc\u7684\uff1f\u201d \u3000\u3000\u80e1\u67cf\u9f84\u9053\uff1a\u201c\u6211\u662f\u5148\u627e\u5230\u4e07\u6708\u5821\u7684\u674e\u7ba1\u5bb6\uff0c\u7531\u674e\u8302\u544a\u8bc9\u6211\u7684\u3002\u201d \u3000\u3000\u949f\u4e00\u8c6a\u60ca\u559c\u9053\uff1a\u201c\u83ab\u975e\u76df\u4e3b\u7684\u884c\u52a8\u5df2\u7ecf\u81ea\u7531\u4e86\uff1f\u201d \u3000\u3000\u80e1\u67cf\u9f84\u70b9\u70b9\u5934\u9053\uff1a\u201c\u5f88\u96be\u5f97\uff0c\u5e08\u7236\u56e0\u4e3a\u8981\u8fdc\u884c\u8bbf\u53cb\uff0e\u7279\u5730\u7ed9\u4e86\u6211\u4e09\u4e2a\u6708\u5047\u671f\uff0c\u5728\u8fd9\u6bb5\u671f \u95f4\uff0c\u6211\u7684\u884c\u52a8\u7b97\u662f\u81ea\u7531\u4e86\u3002\u201d \u3000\u3000\u949f\u2014\u8c6a\u9053\uff1a\u201c\u76df\u4e3b\u53ef\u5426\u628a\u79bb\u5f00\u8ff7\u8e2a\u8c37\u8fd9\u4e8c\u4e09\u5e74\u6765\u7684\u60c5\u51b5\uff0c\u5bf9\u5c5e\u4e0b\u4eec\u8bf4\u4e00\u8bf4\u3002\u201d \u3000\u3000\u80e1\u67cf\u9f84\u9053\uff1a\u201c\u6211\u5012\u5e0c\u671b\u5148\u542c\u542c\u4f60\u4eec\u7684\u3002\u201d \u3000\u3000\u949f\u4e00\u8c6a\u968f\u5373\u8be6\u7ec6\u53d9\u8ff0\u4e86\u4e00\u904d\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u4e5f\u628a\u4e24\u6b21\u51fa\u8c37\u4ee5\u53ca\u4eca\u5929\u968f\u5929\u673a\u3001\u5929\u89c9\u4e24\u4f4d\u5927\u5e08\u4e0e\u970d\u5143\u4f3d\u4f1a\u9762\u94e9\u7fbd\u800c\u5f52\u7684\u60c5\u5f62\u6458\u8981 \u544a\u8bc9\u4e86\u80e1\u67cf\u9f84\u3002 \u3000\u3000\u4e0d\u8fc7\u4ed6\u6682\u65f6\u5e76\u672a\u63d0\u8d77\u66fe\u9047\u5230\u4e00\u4e2a\u9ed1\u8863\u8499\u9762\u5973\u5b50\u9887\u4f3c\u8c37\u5bd2\u9999\u7684\u4e8b\u3002 \u3000\u3000\u80e1\u67cf\u9f84\u542c\u5f97\u5341\u5206\u5165\u795e\uff0c\u65f6\u800c\u6fc0\u52a8\uff0c\u65f6\u800c\u53f9\u606f\uff0c\u6700\u540e\u6df1\u6df1\u5401\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u6ca1\u6599\u5230\u970d\u5143\u4f3d\u7adf \u662f\u5982\u6b64\u80e1\u4f5c\u975e\u4e3a\uff0c\u4e00\u610f\u5b64\u884c\uff0c\u5c45\u7136\u8fde\u4f60\u4eec\u8fd9\u4e9b\u4eba\u4e5f\u5bb9\u7eb3\u4e0d\u4e0b\u3002\u201d \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u597d\u5728\u4e00\u5207\u90fd\u5df2\u6210\u4e3a\u8fc7\u53bb\uff0c\u5982\u4eca\u76df\u4e3b\u590d\u51fa\uff0c\u4ed6\u53c8\u600e\u6562\u4e0d\u8ba9\u51fa\u76df\u4e3b\u4e4b\u4f4d\uff1f\u201d \u3000\u3000\u8fd9\u65f6\u738b\u5927\u5eb7\u548c\u6587\u5929\u751f\u4e5f\u5df2\u5230\u6765\uff0c\u738b\u5927\u5eb7\u53eb\u9053\uff1a\u201c\u4ffa\u73b0\u5728\u5c31\u95ee\u95ee\u76df\u4e3b\uff0c\u4ec0\u4e48\u65f6\u5019\u5e26\u4ffa\u4eec\u56de \u8ff7\u8e2a\u8c37\uff1f\u201d \u3000\u3000\u5c82\u77e5\u80e1\u67cf\u9f84\u7adf\u4f4e\u4e0b\u5934\u53bb\uff0e\u9ed8\u7136\u4e0d\u53d1\u4e00\u8bed\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u60c5\u4e0d\u81ea\u7981\u95ee\u9053\uff1a\u201c\u80e1\u5e08\u5144\uff0c\u4f60\u600e\u4e48\u4e0d\u8bf4\u8bdd\u4e86\uff1f\u201d \u3000\u3000\u80e1\u67cf\u9f84\u6df1\u6df1\u4e00\u53f9\u9053\uff1a\u201c\u8bf4\u51fa\u6765\u6050\u6015\u8ba9\u4f60\u4eec\u5931\u671b\uff0e\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u4e24\u773c\u76f4\u7728\u7684\u9053\uff1a\u201c\u83ab\u975e\u90a3\u4f4d\u8001\u524d\u8f88\u4e0d\u51c6\u518d\u56de\u8ff7\u8e2a\u8c37\u53bb\uff1f\u201d \u3000\u3000\u80e1\u67cf\u9f84\u900f\u7740\u9887\u4e3a\u65e0\u5948\u7684\u795e\u8272\u9053\uff1a\u201c\u4f60\u731c\u5bf9\u4e86\uff01\u5e08\u7236\u4e0d\u51c6\u6211\u518d\u6d89\u8db3\u7eff\u6797\u3002\u201d \u3000\u3000\u201c\u4f60\u4e3a\u4ec0\u4e48\u4e00\u5b9a\u8981\u542c\u4ed6\u7684\uff1f\u201d \u3000\u3000\u201c\u6211\u8fd9\u4e00\u6761\u547d\uff0c\u5b8c\u5168\u62dc\u4ed6\u6240\u8d50\uff0c\u53c8\u600e\u80fd\u4e0d\u542c\u4ed6\u7684\uff1f\u201d \u3000\u3000\u201c\u4ed6\u4e3a\u4ec0\u4e48\u4e0d\u51c6\u4f60\u6d89\u8db3\u7eff\u6797\uff1f\u603b\u8be5\u6709\u4e2a\u7406\u7531\u5427\uff1f\u201d \u3000\u3000\u201c\u4ed6\u8001\u4eba\u5bb6\u7684\u8bdd\u5fc5\u987b\u9075\u5b88\uff0c\u6211\u53c8\u600e\u597d\u95ee\u7406\u7531\u3002\u201d \u3000\u3000\u5ffd\u542c\u4f59\u4ea6\u4e50\u9053\uff1a\u201c\u76df\u4e3b\uff0c\u65e2\u7136\u4f60\u5fc5\u987b\u9075\u5b88\u90a3\u4f4d\u8001\u524d\u8f88\u7684\u5429\u5490\uff0e\u5c5e\u4e0b\u4eec\u81ea\u7136\u4e0d\u6562\u52c9\u5f3a\uff0c\u5c5e \u4e0b\u73b0\u5728\u53ea\u6c42\u4f60\u4e00\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u201c\u4f59\u5144\u6709\u8bdd\u8bf7\u8bb2\uff01\u201d \u3000\u3000\u201c\u76df\u4e3b\u65b9\u624d\u8bf4\u8fc7\uff0c\u90a3\u4f4d\u8001\u524d\u8f88\u7ed9\u4e86\u60a8\u4e09\u4e2a\u6708\u5047\u671f\uff0c\u60a8\u662f\u5426\u53ef\u4ee5\u5e26\u5c5e\u4e0b\u4eec\u5230\u8ff7\u8e2a\u8c37\u4e00\u8d9f\uff1f\u201d \u3000\u3000\u201c\u53bb\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u53ea\u8981\u60a8\u4e00\u73b0\u8eab\uff0c\u970d\u5143\u4f3d\u5fc5\u5b9a\u4f1a\u4e56\u4e56\u7684\u628a\u76df\u4e3b\u4e4b\u4f4d\u8ba9\u51fa\uff0c\u8ff7\u8e2a\u8c37\u662f\u60a8\u4e00\u624b\u5efa\u7acb\u7684\uff0e\u60a8\u603b \u4e0d\u80fd\u773c\u770b\u7740\u8ba9\u4ed6\u80e1\u4f5c\u975e\u4e3a\u5427\uff1f\u201d \u3000\u3000\u201c\u5c31\u7b97\u970d\u5143\u4f3d\u628a\u76df\u4e3b\u4e4b\u4f4d\u8ba9\u51fa\uff0c\u53c8\u6253\u7b97\u8ba9\u8c01\u6765\u505a\u5462\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u662f\u8be5\u76df\u4e3b\u91cd\u884c\u590d\u4f4d\uff0c\u4e07\u4e00\u60a8\u4e0d\u65b9\u4fbf\uff0c\u4ea4\u7ed0\u949f\u526f\u76df\u4e3b\u6682\u65f6\u4ee3\u7406\u4ea6\u53ef\uff0c\u53cd\u6b63\u6211\u4eec\u90fd\u662f \u60a8\u7684\u65e7\u5c5e\uff0c\u3000\u4e00\u5b9a\u4f1a\u5c3d\u529b\u5c06\u8ff7\u8e2a\u8c37\u65e5\u76ca\u53d1\u626c\u5149\u5927\u3002\u201d \u3000\u3000\u80e1\u67cf\u9f84\u6447\u5934\u82e6\u7b11\u9053\uff1a\u201c\u6211\u4f55\u5c1d\u4e0d\u662f\u8fd9\u6837\u60f3\uff0c\u4f46\u6211\u82e5\u8fdb\u5165\u8ff7\u8e2a\u8c37\uff0c\u5c82\u4e0d\u7b49\u4e8e\u8fdd\u72af\u4e86\u5e08\u7236\u7684 \u89c4\u6212\uff1f\u4f55\u51b5\u5e08\u7236\u4e34\u8d70\u65f6\uff0c\u8fd8\u4ea4\u4e0b\u4e00\u4ef6\u4e8b\u8981\u6211\u53bb\u5b8c\u6210\u3002\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u90a3\u4f4d\u8001\u524d\u8f88\u4ea4\u4ee3\u4e86\u76df\u4e3b\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u80e1\u67cf\u9f84\u9053\uff1a\u201c\u5e05\u7236\u8981\u6211\u53bb\u627e\u4ed6\u8001\u4eba\u5bb6\u7684\u4e00\u4f4d\u5e08\u5f1f\u3002\u201d \u3000\u3000\u201c\u90a3\u4f4d\u8001\u524d\u8f88\u7684\u5e08\u5f1f\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u201c\u636e\u5e08\u7236\u8bf4\uff0c\u6211\u7684\u8fd9\u4f4d\u5e08\u53d4\u53ef\u80fd\u5728\u5929\u53f0\u4e07\u82b1\u5bab\u3002\u8bf8\u4f4d\u60f3\u60f3\u770b\uff0c\u5929\u53f0\u5c71\u79bb\u8fd9\u91cc\u9065\u9065\u6570\u5343\u91cc\uff0c \u6765\u56de\u4e00\u8d9f\uff0c\u81f3\u5c11\u8981\u53bb\u6389\u4e24\u4e2a\u6708\u65f6\u95f4\uff0c\u66f4\u4f55\u51b5\u662f\u5426\u5728\u5929\u53f0\u4e07\u82b1\u5bab\uff0c\u76ee\u524d\u8fd8\u65e0\u6cd5\u786e\u5b9a\u3002\u201d \u3000\u3000\u201c\u76df\u4e3b\u8981\u627e\u7684\u8fd9\u4f4d\u524d\u8f88\u7684\u5927\u540d\uff0c\u76df\u4e3b\u5fc5\u5b9a\u6e05\u695a\u3002\u201d \u3000\u3000\u201c\u6211\u7684\u8fd9\u4f4d\u5e08\u53d4\u53eb\u4e09\u5999\u4e66\u751f\uff0c\u8bf8\u4f4d\u662f\u5426\u66fe\u542c\u8bf4\u8fc7\uff1f\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u5fc3\u5934\u7830\u7136\u2014\u52a8\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u76df\u4e3b\u73b0\u5728\u7684\u5e08\u7236\uff0c\u4e00\u5b9a\u662f\u56db\u7edd\u8001\u4eba\u4e86\uff1f\u201d \u3000\u3000\u80e1\u67cf\u9f84\u53cc\u76ee\u4e2d\u95ea\u51fa\u5f02\u91c7\uff0c\u9053\uff1b\u201c\u4f59\u5144\u662f\u600e\u4e48\u77e5\u9053\u7684\uff1f\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u9053\uff1a\u201c\u8fd9\u4e8b\u662f\u4e0a\u6b21\u5e9e\u58eb\u51b2\u8001\u524d\u8f88\u5728\u4e07\u6708\u5ce1\u8bb2\u8ff0\u51fa\u6765\u7684\u3002\u201d \u3000\u3000\u80e1\u67cf\u9f84\u6014\u4e86\u6014\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u5e9e\u58eb\u51b2\u7adf\u7136\u5982\u6b64\u535a\u77e5\u5e7f\u95fb\uff01\u201d \u3000\u3000\u8fd9\u65f6\u9ea6\u5c0f\u660e\u53c8\u8feb\u4e0d\u53ca\u5f85\u7684\u63d2\u8a00\u9053\uff1a\u201c\u65f6\u95f4\u5b9d\u8d35\uff0c\u7528\u4e0d\u7740\u518d\u8ba8\u8bba\u4ec0\u4e48\u56db\u7edd\u8001\u4eba\u4e09\u5999\u4e66\u751f\u7684 \u4e8b\uff0c\u73b0\u5728\u6211\u53ea\u60f3\u95ee\u95ee\u80e1\u5e08\u5144\u4ec0\u4e48\u65f6\u5019\u52a8\u8eab\u5230\u5929\u53f0\u4e07\u82b1\u5bab\u53bb\uff1f\u201d \u3000\u3000\u80e1\u67cf\u9f84\u9053\uff1a\u201c\u6211\u6253\u7b97\u660e\u5929\u4e00\u65e9\u5c31\u52a8\u8eab\uff0c\u6240\u4ee5\u624d\u5728\u52a8\u8eab\u4e4b\u524d\uff0c\u7279\u5730\u6765\u770b\u770b\u5927\u5bb6\u3002\u201d \u3000\u3000\u201c\u8fd8\u6709\u8c01\u548c\u5e08\u5144\u540c\u884c\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u53ea\u6709\u6211\u81ea\u5df1\u4e00\u4eba\u3002\u201d \u3000\u3000\u201c\u5e08\u5144\u8981\u4e0d\u8981\u627e\u4e2a\u5411\u5bfc\u5462\uff1f\u201d \u3000\u3000\u201c\u6682\u65f6\u7528\u4e0d\u7740\uff0c\u5230\u4e86\u5929\u53f0\u5c71\u518d\u8bf4\u5427\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u7acb\u5373\u6bdb\u9042\u81ea\u8350\u9053\uff1a\u201c\u5c31\u7531\u5c0f\u5f1f\u968f\u5e08\u5144\u53bb\u5427\uff0c\u6211\u662f\u6700\u597d\u4e0d\u8fc7\u7684\u5411\u5bfc\u4e86\u3002\u201d \u3000\u3000\u80e1\u67cf\u9f84\u54e6\u4e86\u58f0\u9053\uff1b\u201c\u83ab\u975e\u4f60\u5bf9\u5929\u53f0\u4e07\u82b1\u5bab\u5f88\u719f\u6089\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u5e08\u5144\u65e9\u8be5\u77e5\u9053\uff0c\u5c0f\u5f1f\u539f\u662f\u5929\u53f0\u4e07\u82b1\u5bab\u5bab\u4e3b\u4f5f\u516c\u5e38\u624b\u4e0b\u7684\u4e00\u540d\u4f7f\u8005\uff0e\u540e\u6765\u624d \u6295\u5f52\u5e08\u7236\u9093\u79cb\u95e8\u4e0b\u7684\u3002\u201d \u3000\u3000\u80e1\u67cf\u9f84\u559c\u9053\uff1a\u201c\u90a3\u6562\u60c5\u597d\uff01\u4e0d\u8fc7\u4f60\u82e5\u79bb\u5f00\u8fd9\u91cc\uff0e\u4e0d\u77e5\u949f\u526f\u76df\u4e3b\u548c\u4f59\u5144\u662f\u5426\u540c\u610f\uff1f\u201d \u3000\u3000\u539f\u6765\u80e1\u67cf\u9f84\u5df2\u77e5\u9ea6\u5c0f\u660e\u8fd9\u4e8c\u4e09\u5e74\u6765\uff0c\u6b66\u529f\u7a81\u98de\u731b\u8fdb\uff0c\u76ee\u524d\u5df2\u662f\u8fd9\u4f19\u4eba\u4e2d\u8eab\u624b\u6700\u9ad8\u7684\u4e00\u4e2a\u3002 \u4ed6\u82e5\u79bb\u5f00\uff0c\u5fc5\u5b9a\u5bf9\u949f\u4e00\u8c6a\u7b49\u4eba\u5927\u5927\u4e0d\u5229\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u5185\u5fc3\u867d\u4e5f\u4e0d\u613f\u9ea6\u5c0f\u660e\u79bb\u5f00\uff0c\u5374\u4e5f\u4e0d\u80fd\u4e0d\u540c\u610f\uff0c\u5fd9\u9053\uff1a\u201c\u65e2\u7136\u5c0f\u660e\u5144\u5f1f\u8981\u4e0e\u76df\u4e3b\u4f5c \u4f34\u524d\u5f80\u5929\u53f0\u5c71\uff0c\u90a3\u662f\u6700\u597d\u4e0d\u8fc7\u3002\u201d \u3000\u3000\u80e1\u67cf\u9f84\u5411\u5854\u7a97\u5916\u671b\u4e86\u671b\u5929\u8272\u9053\uff1a\u201c\u6211\u8be5\u8d70\u4e86\uff0c\u660e\u5929\u65e5\u51fa\u4e4b\u65f6\uff0c\u5c0f\u660e\u5e08\u5f1f\u8bf7\u5230\u4e07\u6708\u5ce1\u53e3\u548c \u6211\u76f8\u4f1a\uff01\u201d \u3000\u3000\u949f\u4e00\u8c6a\u548c\u4f59\u4ea6\u4e50\u51e0\u4e4e\u540c\u58f0\u9053\uff1a\u201c\u76df\u4e3b\u4e3a\u4ec0\u4e48\u4e0d\u7559\u5728\u8fd9\u91cc\u6682\u4f4f\u4e00\u5bbf\uff1f\u201d \u3000\u3000\u80e1\u67cf\u9f84\u9053\uff1b\u201c\u6211\u8fd8\u8981\u56de\u53bb\u6574\u7406\u6574\u7406\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u5b8c\u8bdd\uff0c\u4e0d\u987e\u4f17\u4eba\u633d\u7559\uff0e\u5f84\u81ea\u4e0b\u5854\u800c\u53bb\u3002 \u3000\u3000\u5f53\u4f17\u4eba\u9001\u4e0b\u5854\u6765\uff0c\u4fbf\u89c1\u80e1\u67cf\u9f84\u53cc\u80a9\u4e00\u6643\uff0c\u53bb\u52bf\u5982\u98ce\uff0c\u5f88\u5feb\u4fbf\u5728\u591c\u7a7a\u4e2d\u6d88\u901d\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u5e08\u5144\u5e94\u8be5\u4e0d\u4f1a\u723d\u7ea6\uff0c\u6211\u4e5f\u8be5\u56de\u53bb\u4f11\u606f\u4e86\u3002\u201d \u3000\u3000\u949f\u4e00\u8c6a\u9894\u9996\u9053\uff1a\u201c\u5927\u5bb6\u90fd\u6b47\u606f\u5427\uff01\u201d \u3000\u3000\u4f17\u4eba\u4fbf\u5404\u81ea\u56de\u623f\u4f11\u606f\u3002 \u3000\u3000\u82d7\u7d20\u82d3\u5728\u81ea\u4e2a\u513f\u623f\u95f4\uff0c\u5df2\u7136\u88ab\u4f17\u4eba\u7684\u8bdd\u58f0\u60ca\u9192\u3002 \u3000\u3000\u7531\u4e8e\u6ca1\u6709\u63a5\u5230\u901a\u77e5\uff0c\u4fbf\u4e5f\u4e0d\u597d\u51fa\u95e8\u63a2\u95ee\u3002 \u3000\u3000\u5979\u5728\u623f\u95f4\u5185\u601d\u7d22\u7740\uff0e \u3000\u3000\u81ea\u5df1\u79bb\u5f00\u9634\u624b\u4e00\u9b54\u540e\uff0c\u5df2\u6210\u81ea\u7531\u4e4b\u8eab\uff0e \u3000\u3000\u5f53\u7136\u5e94\u8be5\u8d81\u6b64\u673a\u4f1a\u589e\u957f\u70b9\u89c1\u8bc6\u3002 \u3000\u3000\u51b5\u4e14\uff0c\u81ea\u8ba4\u4e3a\u548c\u9ea6\u5c0f\u660e\u7684\u5173\u7cfb\u5df2\u4e0d\u5e73\u5e38\uff0e \u3000\u3000\u4e0d\u53ea\u662f\u666e\u901a\u670b\u53cb\u4e86\uff01 \u3000\u3000\u5979\u51b3\u5b9a\u5f85\u4f1a\u513f\u53bb\u6572\u9ea6\u5c0f\u660e\u623f\u95e8\uff0c\u82e5\u8981\u51fa\u95e8\uff0e\u5979\u4fbf\u2026\u2026\u3002 \u3000\u3000\u6b21\u65e5\u5929\u8fd8\u4e0d\u4eae\uff0c\u767d\u59d1\u5bfa\u5927\u95e8\u5185\u4fbf\u95ea\u51fa\u4e00\u7537\u2014\u5973\u4e8c\u4e2a\u4eba\u5f71\u3002 \u3000\u3000\u9664\u9ea6\u5c0f\u660e\u5916\uff0c\u53e6\u4e00\u4e2a\u662f\u82d7\u7d20\u82d3\uff0e \u3000\u3000\u539f\u6765\u6628\u665a\u82d7\u7d20\u82d3\u867d\u672a\u63a5\u83b7\u901a\u77e5\u53bb\u89c1\u80e1\u67cf\u9f84\uff0c\u5374\u5df2\u88ab\u60ca\u9192\u3002 \u3000\u3000\u56e0\u4e4b\uff0c\u5f53\u80e1\u67cf\u9f84\u8d70\u540e\uff0c\u9ea6\u5c0f\u660e\u8fd4\u56de\u7b2c\u4e94\u5c42\u5854\u65f6\uff0c\u5979\u4fbf\u6765\u5411\u9ea6\u5c0f\u660e\u67e5\u95ee\u521a\u624d\u53d1\u751f\u4e86\u4ec0\u4e48\u4e8b\u3002 \u3000\u3000\u5f53\u5979\u5f97\u77e5\u8be6\u60c5\u540e\uff0c\u4fbf\u51b3\u5b9a\u4e5f\u8981\u968f\u9ea6\u5c0f\u660e\u540c\u884c\uff0e \u3000\u3000\u5979\u7684\u7406\u7531\u4e0d\u5916\u662f\u8fde\u9ea6\u5c0f\u660e\u548c\u7fa4\u8c6a\u5728\u4e00\u8d77\u90fd\u65e0\u6cd5\u6551\u51fa\u59d0\u59d0\uff0c\u5982\u4eca\u9ea6\u5c0f\u660e\u4e00\u8d70\uff0c\u60f3\u6551\u51fa\u82d7\u7d20 \u5170\uff0e\u66f4\u662f\u5e0c\u671b\u6e3a\u832b\u3002 \u3000\u3000\u65e2\u7136\u5982\u6b64\uff0c\u5012\u4e0d\u5982\u968f\u80e1\u67cf\u9f84\u548c\u9ea6\u5c0f\u660e\u5230\u5916\u9762\u5386\u7ec3\u5386\u7ec3\uff0c\u81f3\u5c11\u5bf9\u81ea\u5df1\u662f\u6709\u597d\u5904\u7684\u3002 \u3000\u3000\u53e6\u4e00\u4e2a\u539f\u56e0\uff0c\u662f\u5979\u4e00\u76f4\u5bf9\u9ea6\u5c0f\u660e\u60c5\u6709\u72ec\u949f\uff0e\u5c24\u5176\u90a3\u665a\u4e00\u543b\u4e4b\u540e\uff0c\u66f4\u662f\u53d1\u8a93\u4ee5\u8eab\u76f8\u8bb8\uff0c\u53c8 \u600e\u820d\u5f97\u548c\u9ea6\u5c0f\u660e\u8fdc\u79bb\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u65e0\u6cd5\u63a8\u5374\u4e4b\u4e0b\uff0c\u4e5f\u53ea\u6709\u7b54\u5e94\u5979\u7684\u8981\u6c42\u3002 \u3000\u3000\u8def\u4e0a\uff0c\u4e24\u4eba\u8fb9\u8d70\u8fb9\u8c08\u3002 \u3000\u3000\u201c\u82d7\u59d0\uff0c\u73b0\u5728\u7ec8\u4e8e\u8bc1\u660e\u4e86\u4e00\u4ef6\u4e8b\uff0c\u6211\u90a3\u80e1\u5e08\u5144\u5fc5\u5b9a\u5c31\u4f4f\u5728\u4e07\u6708\u5ce1\u9644\u8fd1\uff0c\u5426\u5219\u4ed6\u600e\u4f1a\u8981\u6211 \u548c\u4ed6\u5728\u4e07\u6708\u5ce1\u53e3\u76f8\u4f1a\u3002\u201d \u3000\u3000\u201c\u5f85\u4f1a\u513f\u89c1\u4e86\u9762\uff0c\u4e0d\u59a8\u95ee\u95ee\u4ed6\u3002\u201d \u3000\u3000\u82d7\u7d20\u82d3\u63a5\u7740\u53c8\u9053\uff1a\u201c\u6211\u771f\u4e0d\u89e3\uff0c\u80e1\u76df\u4e3b\u4e3a\u4ec0\u4e48\u4e0d\u80af\u518d\u56de\u8ff7\u8e2a\u8c37\uff1f\u4ed6\u82e5\u56de\u53bb\uff0c\u9664\u4e86\u5fc5\u53ef\u91cd \u767b\u76df\u4e3b\u4e4b\u4f4d\uff0c\u540c\u65f6\u4e5f\u53ef\u89e3\u9664\u6211\u59d0\u59d0\u7684\u5371\u96be\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u6211\u4e0d\u662f\u5bf9\u4f60\u8bf4\u8fc7\u5417\uff1f\u8fd9\u662f\u56db\u7edd\u8001\u4eba\u7684\u89c4\u5b9a\uff0c\u4ed6\u53c8\u600e\u6562\u4e0d\u542c\uff1f\u201d \u3000\u3000\u201c\u56db\u7edd\u8001\u4eba\u5bf9\u4ed6\u5c31\u90a3\u6837\u6709\u5a01\u4e25\uff1f\u201d \u3000\u3000\u201c\u8fd9\u5e76\u975e\u5a01\u4e25\u4e0d\u5a01\u4e25\u7684\u95ee\u9898\uff0c\u56db\u7edd\u8001\u4eba\u5bf9\u4ed6\u6709\u518d\u9020\u4e4b\u6069\uff0e\u82e5\u6ca1\u6709\u56db\u7edd\u8001\u4eba\u5f53\u5e74\u7684\u65bd\u6551\uff0c \u73b0\u5728\u54ea\u91cc\u8fd8\u4f1a\u6709\u4ed6\uff1f\u8fd9\u5c31\u662f\u4ed6\u4e0d\u5f97\u4e0d\u5bf9\u56db\u7edd\u8001\u4eba\u5904\u5904\u606d\u987a\u7684\u7406\u7531\u3002\u201d \u3000\u3000\u5230\u8fbe\u4e07\u6708\u5ce1\u5ce1\u53e3\uff0c\u521a\u597d\u662f\u65e5\u51fa\u4e4b\u65f6\u3002 \u3000\u3000\u53ea\u89c1\u80e1\u67cf\u9f84\u65e9\u5df2\u7b49\u5728\u90a3\u91cc\uff0c\u800c\u4e14\u6b63\u5750\u5728\u4e0a\u6b21\u90a3\u5757\u4f4d\u5728\u5ced\u58c1\u7684\u5927\u9752\u77f3\u4e0a\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u4e0d\u89c9\u5fc3\u5934\u4e00\u9707\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u7cdf\u7cd5\uff0c\u83ab\u975e\u56db\u7edd\u8001\u4eba\u53c8\u5728\u4ed6\u8eab\u8fb9\uff1f\u201d \u3000\u3000\u82d7\u7d20\u82d3\u9053\uff1a\u201c\u8fd9\u6b21\u54b1\u4eec\u5c31\u6709\u7406\u7531\u4e0a\u53bb\u770b\u770b\u4e86\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u968f\u5373\u52a0\u5feb\u811a\u6b65\uff0c\u6765\u5230\u5927\u9752\u77f3\u4e0b\uff0c\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u5e08\u5144\uff0e\u6211\u4eec\u53ef\u4ee5\u4e0a\u53bb\u5417\uff1f\u201d \u3000\u3000\u80e1\u67cf\u9f84\u9053\uff1a\u201c\u5f53\u7136\u53ef\u4ee5\u4e0a\u6765\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u548c\u82d7\u7d20\u82d3\u4e00\u8dfa\u811a\uff0c\u4eba\u5df2\u76f4\u51b2\u800c\u8d77\uff0e\u7136\u540e\u8f7b\u98d8\u98d8\u7684\u843d\u4e0a\u90a3\u5757\u5927\u9752\u77f3\u3002 \u3000\u3000\u82d7\u7d20\u82d3\u8fde\u5fd9\u5411\u80e1\u67cf\u9f84\u6df1\u6df1\u88e3\u887d\u65bd\u4e86\u4e00\u793c\u3002 \u3000\u3000\u9762\u5bf9\u8fd9\u4f4d\u795e\u5a01\u51db\u51db\u7684\u7eff\u6797\u4e00\u4ee3\u5927\u8c6a\uff0c\u82d7\u7d20\u82d3\u4e5f\u4e0d\u671f\u7136\u7684\u8083\u7136\u8d77\u656c\u3002 \u3000\u3000\u80e1\u67cf\u9f84\u76ee\u6ce8\u82d7\u7d20\u82d3\u9053\uff1a\u201c\u8fd9\u4f4d\u59d1\u5a18\u662f\u8c01\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u62a2\u7740\u9053\uff1a\u201c\u5e08\u5144\u770b\u5979\u50cf\u8c01\uff1f\u201d \u3000\u3000\u80e1\u67cf\u9f84\u6536\u56de\u89c6\u7ebf\u9053\uff1a\u201c\u8fd9\u59d1\u5a18\u7684\u786e\u50cf\u4e00\u4e2a\u4eba\uff0c\u5f88\u50cf\u82d7\u7d20\u5170\u59d1\u5a18\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u7b11\u9053\uff1a\u201c\u5e08\u5144\u597d\u773c\u529b\uff0c\u5979\u6b63\u662f\u82d7\u59d1\u5a18\u7684\u59b9\u59b9\uff0c\u540d\u53eb\u82d7\u7d20\u82d3\u3002\u201d \u3000\u3000\u201c\u5979\u662f\u600e\u4e48\u6765\u7684\uff1f\u201d \u3000\u3000\u201c\u5979\u4e5f\u662f\u9634\u624b\u4e00\u9b54\u7684\u5f1f\u5b50\uff0c\u4e24\u6708\u524d\u9003\u51fa\u5415\u6881\u5c71\uff0e\u6765\u5230\u8ff7\u8e2a\u8c37\uff0c\u672c\u6765\u8981\u6295\u5954\u82d7\u5927\u59d1\u5a18\uff0c\u53ef \u60dc\u82d7\u5927\u59d1\u5a18\u76ee\u524d\u5df2\u88ab\u970d\u5143\u4f3d\u6253\u8fdb\u5730\u7262\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e9b\u7ecf\u8fc7\uff0c\u4e07\u6708\u5ce1\u7684\u674e\u7ba1\u5bb6\u5df2\u5bf9\u6211\u8bf4\u8fc7\u3002\u600e\u4e48\uff0e\u82d7\u4e8c\u59d1\u5a18\u4e5f\u8981\u968f\u6211\u4eec\u540c\u884c\uff1f\u201d \u3000\u3000\u201c\u5979\u548c\u82d7\u5927\u59d1\u5a18\u4e00\u6837\uff0c\u4e5f\u662f\u65e9\u5c31\u4ef0\u6155\u5e08\u5144\u7684\uff0c\u6240\u4ee5\u624d\u4e3b\u52a8\u8981\u8ddf\u968f\u60a8\u540c\u884c\u3002\u5979\u7684\u6b66\u529f\u5f88\u9ad8\uff0c \u8def\u4e0a\u7528\u4e0d\u7740\u5e08\u5144\u64cd\u5fc3\u3002\u201d \u3000\u3000\u80e1\u67cf\u9f84\u9894\u9996\u9053\uff1a\u201c\u4e5f\u597d\u3002\u53ea\u662f\u8fd9\u8d9f\u5929\u53f0\u5c71\u4e4b\u884c\uff0c\u672a\u514d\u592a\u82e6\u4e86\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u671b\u4e86\u82d7\u7d20\u82d3\u4e00\u773c\u9053\uff1a\u201c\u8fd8\u4e0d\u5feb\u8c22\u8fc7\u80e1\u5e08\u5144\uff01\u201d \u3000\u3000\u82d7\u7d20\u82d3\u53c8\u4e0a\u524d\u88e3\u887d\u4e00\u793c\u9053\uff1a\u201c\u591a\u8c22\u80e1\u76df\u4e3b\u6210\u5168\uff01\u201d \u3000\u3000\u80e1\u67cf\u9f84\u4e0d\u52a8\u58f0\u8272\u9053\uff1a\u201c\u6211\u73b0\u5728\u65e9\u5df2\u4e0d\u662f\u7eff\u6797\u76df\u4e3b\u4e86\uff0c\u7528\u4e0d\u7740\u518d\u79f0\u547c\u6211\u76df\u4e3b\u3002\u201d \u3000\u3000\u82d7\u7d20\u82d3\u6697\u9053\uff1a\u201c\u6211\u8be5\u79f0\u547c\u4ed6\u4ec0\u4e48\u597d\u5462\uff1f\u201d \u3000\u3000\u5979\u672c\u6765\u60f3\u6539\u79f0\u201c\u80e1\u524d\u8f88\u201d\u6216\u201c\u80e1\u53d4\u53d4\u201d\uff0c\u4f46\u53c8\u60f3\u5230\u6b64\u8eab\u5df2\u9ed8\u8bb8\u9ea6\u5c0f\u660e\uff0c\u800c\u9ea6\u5c0f\u660e\u5374\u79f0\u4ed6 \u201c\u80e1\u5e08\u5144\u201d\uff0e\u82e5\u81ea\u5df1\u79f0\u80e1\u67cf\u9f84\u201c\u524d\u8f88\u201d\u6216\u201c\u53d4\u53d4\u201d\uff0c\u5c06\u6765\u5c82\u4e0d\u4ecd\u8981\u6539\u53e3\u3002\u56e0\u4e4b\uff0e\u590d\u53c8\u6df1\u65bd\u2014 \u793c\u9053\uff1a\u201c\u591a\u8c22\u80e1\u5927\u4fa0\uff01\u201d \u3000\u3000\u8fd9\u65f6\u9ea6\u5c0f\u660e\u5df2\u5728\u6697\u4e2d\u7559\u610f\u5927\u9752\u77f3\u9644\u8fd1\uff0c\u5e76\u4e0d\u89c1\u56db\u7edd\u8001\u4eba\u7684\u5f71\u5b50\uff0c\u8fd9\u624d\u653e\u4e0b\u4e86\u5fc3\u3002 \u3000\u3000\u80e1\u67cf\u9f84\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u5c0f\u660e\uff0c\u4f60\u770b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u5c0f\u5f1f\u662f\u60f3\u770b\u770b\u8fd9\u5d16\u58c1\u9644\u8fd1\u6709\u65e0\u53ef\u4ee5\u85cf\u8eab\u4e4b\u5904\u3002\u201d \u3000\u3000\u201c\u83ab\u975e\u4f60\u4e5f\u51c6\u5907\u85cf\u8d77\u6765\uff1f\u201d \u3000\u3000\u201c\u4e0d\uff01\u4e0a\u6b21\u56db\u7edd\u8001\u4eba\u548c\u5e08\u5144\u5728\u8fd9\u5927\u9752\u77f3\u4e0a\uff0c\u4e00\u4e0b\u5b50\u5c31\u4e0d\u89c1\u4e86\uff0e\u7a76\u7adf\u662f\u8eb2\u5230\u54ea\u91cc\u53bb\u7684\uff1f\u201d \u3000\u3000\u201c\u8fd9\u662f\u79d8\u5bc6\uff0e\u6682\u65f6\u4e0d\u80fd\u5bf9\u4f60\u8bb2\u3002\u4e0d\u8fc7\u6211\u53ef\u4ee5\u544a\u8bc9\u4f60\uff0e\u8fd9\u9644\u8fd1\u5e76\u65e0\u53ef\u8d44\u85cf\u8eab\u4e4b\u5904\uff0c\u4e0d\u4fe1\u4f60 \u5c31\u4ed4\u7ec6\u5bdf\u770b\u5bdf\u770b\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u6599\u5b9a\u627e\u4e0d\u51fa\u4ec0\u4e48\u7834\u7efd\uff0e\u9ed8\u4e86\u2014\u9ed8\u9053\uff1a\u201c\u5e08\u5144\uff0c\u4ec0\u4e48\u65f6\u5019\u51fa\u53d1\uff1f\u201d \u3000\u3000\u80e1\u67cf\u9f84\u7ad9\u8d77\u8eab\u9053\uff1a\u201c\u73b0\u5728\u5c31\u8d70\uff01\u201d \u3000\u3000\u4e09\u5929\u540e\uff0c\u80e1\u67cf\u9f84\u7b49\u4e00\u884c\u4e09\u4eba\uff0c\u5df2\u5230\u8fbe\u5b9a\u8944\u3002 \u3000\u3000\u79bb\u5f00\u5b9a\u8944\uff0c\u9ea6\u5c0f\u660e\u5ffd\u7136\u60f3\u8d77\u4e86\u5999\u5999\u5eb5\uff0e\u4e5f\u60f3\u8d77\u4e86\u90a3\u4f4d\u9177\u4f3c\u8c37\u5bd2\u9999\u7684\u9ed1\u8863\u8499\u9762\u795e\u79d8\u5973\u5b50\u3002 \u3000\u3000\u4ed6\u642d\u8baa\u7740\u95ee\u9053\uff1a\u201c\u5e08\u5144\u5230\u5929\u53f0\u5c71\u89c1\u4e09\u5999\u4e66\u751f\u8001\u524d\u8f88\uff0e\u7a76\u7adf\u662f\u4e3a\u4e86\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u80e1\u67cf\u9f84\u9053\uff1a\u201c\u5e08\u7236\u53ea\u4ea4\u7ed9\u6211\u4e00\u5c01\u4fe1\uff0c\u8981\u6211\u628a\u8fd9\u5c01\u4fe1\u9762\u5448\u4e09\u5999\u5e08\u53d4\uff0c\u81f3\u4e8e\u4fe1\u91cc\u5199\u4e9b\u4ec0\u4e48\uff0c \u6211\u5e76\u4e0d\u6e05\u695a\u3002\u201d \u3000\u3000\u201c\u5e08\u5144\u53ef\u60f3\u5ff5\u5e08\u5ac2\uff1f\u201d \u3000\u3000\u201c\u4f60\u4f55\u5fc5\u591a\u6b64\u2014\u95ee\uff0e\u6211\u4e0e\u5bd2\u9999\u7684\u611f\u60c5\uff0e\u96be\u9053\u4f60\u8fd8\u4e0d\u6e05\u695a\u3002\u5979\u4e3a\u6211\u800c\u6b7b\uff0c\u73b0\u5728\u53cd\u800c\u662f\u6211\u6d3b \u5728\u4e16\u4e0a\uff0e\u4f7f\u6211\u6c38\u8fdc\u6127\u5bf9\u4e8e\u5979\uff01\u201d \u3000\u3000\u201c\u5e08\u5144\u4e0d\u5fc5\u96be\u8fc7\uff0c\u5e08\u5ac2\u6570\u6708\u524d\u5c31\u6b7b\u5728\u5929\u53f0\u4e07\u82b1\u5bab\uff0e\u6b64\u756a\u54b1\u4eec\u5230\u4e86\u90a3\u91cc\uff0c\u5c31\u5728\u5979\u7684\u6b7b\u5730\u796d \u4e0a\u2014\u796d\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u6211\u6700\u5927\u7684\u5fc3\u613f\uff0c\u662f\u80fd\u8bbe\u6cd5\u627e\u5230\u5979\u7684\u9057\u4f53\uff0c\u4ee5\u4fbf\u5e26\u56de\u6765\u5b89\u846c\u3002\u201d \u3000\u3000\u201c\u5e08\u5144\uff0c\u6709\u4ef6\u4e8b\u5c0f\u5f1f\u4e00\u76f4\u4e0d\u66fe\u5bf9\u60a8\u8bb2\uff0e\u73b0\u5728\u5b9e\u5728\u8be5\u662f\u544a\u8bc9\u60a8\u7684\u65f6\u5019\u4e86\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u4e8b\uff1f\u5feb\u544a\u8bc9\u6211\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u968f\u5373\u628a\u4f1a\u9047\u9ed1\u8863\u8499\u9762\u5973\u5b50\u7684\u4e8b\uff0c\u4ece\u5934\u5230\u5c3e\u8bf4\u4e86\u4e00\u904d\uff0c\u540c\u65f6\u4e5f\u4e0d\u5f97\u4e0d\u63d0\u8d77\u90a3\u7f01\u8863\u8001 \u5c3c\uff0c\u4ee5\u53ca\u7f01\u8863\u8001\u5c3c\u66fe\u5230\u8ff7\u8e2a\u8c37\u72ec\u529b\u51fb\u9000\u7fa4\u9b54\u7684\u7ecf\u8fc7\u3002 \u3000\u3000\u80e1\u67cf\u9f84\u53ea\u542c\u5f97\u5927\u4e3a\u6fc0\u52a8\u9053\uff1a\u201c\u6709\u8fd9\u79cd\u4e8b\uff1f\u90a3\u9ed1\u8863\u8499\u9762\u5973\u5b50\u5f53\u771f\u50cf\u4f60\u5e08\u5ac2\u5bd2\u9999\u5417\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u8fde\u767d\u5df1\u4e5f\u6fc0\u8d77\u6765\uff0c\u9053\uff1a\u201c\u5c0f\u5f1f\u4ece\u6ca1\u770b\u5230\u4e16\u4e0a\u6709\u4e24\u4e2a\u4eba\u5982\u6b64\u76f8\u50cf\u7684\uff01\u5979\u7684\u9ad8\u77ee\u3001\u8eab \u6750\u3001\u4e3e\u6b62\uff0c\u53ef\u8bf4\u548c\u5e08\u878b\u5b8c\u5168\u2014\u6a21\u4e00\u6837\u3002\u552f\u2014\u6240\u5dee\u7684\uff0c\u662f\u65e0\u6cd5\u770b\u5230\u5979\u7684\u9762\u8c8c\u3002\u201d \u3000\u3000\u80e1\u67cf\u9f84\u9053\uff1a\u201c\u4f60\u53bb\u627e\u8fc7\u5979\u7684\u90a3\u5904\u5c3c\u5eb5\uff0c\u5728\u4ec0\u4e48\u5730\u65b9\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u5c31\u5728\u524d\u9762\u4e0d\u8fdc\uff0c\u79bb\u8fd9\u91cc\u4e0d\u8fc7\u5341\u51e0\u91cc\u8def\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u90a3\u5c31\u7531\u4f60\u5e26\u8def\u53bb\u4e00\u8d9f\u3002\u201d \u3000\u3000\u201c\u5c31\u662f\u5e08\u5144\u4e0d\u8bf4\uff0c\u5c0f\u5f1f\u4e5f\u65e9\u5c31\u60f3\u53bb\u770b\u770b\uff0c\u56e0\u4e3a\u4e07\u6620\u971e\u59d1\u5a18\u4e5f\u5728\u90a3\u5ea7\u5c3c\u5eb5\u91cc\uff0c\u5979\u73b0\u5728\u7684\u5e08 \u7236\uff0c\u5c31\u662f\u90a3\u91cc\u7684\u5eb5\u4e3b\u9759\u6708\u5e08\u592a\u3002\u201d \u3000\u3000\u201c\u90a3\u5c31\u66f4\u8981\u53bb\u4e86\u3002\u971e\u513f\u7684\u7236\u4eb2\u662f\u6211\u7684\u4e49\u5144\uff0c\u4e49\u5144\u4e34\u7ec8\u524d\u628a\u971e\u513f\u6258\u4ed8\u7ed9\u6211\uff0c\u800c\u6211\u5374\u53c8\u4e2d\u9014 \u79bb\u5f00\uff0c\u5982\u4eca\u60f3\u6765\uff0e\u4e0d\u4f46\u6127\u5bf9\u6211\u90a3\u4e49\u5144\uff0e\u4e5f\u5bf9\u971e\u513f\u90a3\u4e2b\u5934\u6709\u4e9b\u62b1\u6b49\uff0c\u56e0\u4e3a\u6211\u5e76\u6ca1\u5c3d\u5230\u7167\u987e\u5979\u7684 \u8d23\u4efb\u3002\u201d \u3000\u3000\u201c\u5e08\u5144\u7528\u4e0d\u7740\u81ea\u8d23\uff0c\u4f60\u5927\u96be\u4e0d\u6b7b\uff0c\u5982\u4eca\u53c8\u5df2\u590d\u51fa\uff0c\u5c06\u6765\u8fd8\u4e0d\u7167\u6837\u53ef\u4ee5\u7ee7\u7eed\u7167\u987e\u5979\uff0c\u53ea\u662f \u5c0f\u5f1f\u6709\u4e00\u4e8b\u4e0d\u660e\u3002\u201d \u3000\u3000\u201c\u4f60\u597d\u50cf\u5f88\u7f57\u55e6\uff0c\u53c8\u6709\u4ec0\u4e48\u4e8b\u5462\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u987f\u4e86\u987f\u9053\uff1b\u201c\u5c31\u662f\u56db\u7edd\u8001\u4eba\u7a76\u7adf\u7528\u4ec0\u4e48\u65b9\u6cd5\u628a\u5e08\u5144\u6551\u6d3b\u7684\uff1f\u201d \u3000\u3000\u80e1\u67cf\u9f84\u4f3c\u662f\u4e0d\u613f\u591a\u8bf4\uff0c\u76b1\u4e86\u7709\u5934\uff0c\u53cd\u95ee\u9053\uff1a\u201c\u5f53\u5e74\u6211\u662f\u600e\u4e48\u6b7b\u7684\uff0c\u4f60\u53ef\u5728\u573a\u770b\u5230\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u5c0f\u5f1f\u867d\u6ca1\u770b\u5230\u5e08\u5144\u6b7b\u65f6\u7684\u60c5\u5f62\uff0c\u4f46\u5374\u77e5\u9053\u5e08\u5ac2\u548c\u949f\u4e00\u8c6a\u628a\u60a8\u846c\u5728\u96ea\u5cf0\u4e0a\u7684 \u7ecf\u8fc7\uff0c\u60a8\u82e5\u6ca1\u6b7b\uff0c\u4ed6\u4eec\u53c8\u600e\u53ef\u80fd\u628a\u60a8\u5b89\u846c\uff1f\u201d \u3000\u3000\u80e1\u67cf\u9f84\u6c89\u9ed8\u4e86\u534a\u54cd\uff0c\u518d\u95ee\u9053\uff1b\u201c\u4f60\u53ef\u542c\u8bf4\u8fc7\u6b66\u6797\u4e2d\u6709\u4eba\u7ec3\u8fc7\u2018\u9f9f\u606f\u672f\u2019\u7684\uff1f\u8fd9\u79cd\u2018\u9f9f\u606f \u672f\u2019\uff0c\u4e5f\u5c31\u662f\u4fd7\u8bf4\u7684\u2018\u95ed\u6c14\u6cd5\u2019\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u554a\u4e86\u58f0\u9053\uff1a\u201c\u83ab\u975e\u5e08\u5144\u7ec3\u8fc7\uff1f\u201d \u3000\u3000\u80e1\u67cf\u9f84\u9894\u9996\u9053\uff1a\u201c\u6211\u5f53\u5e74\u786e\u66fe\u7ec3\u8fc7\uff0c\u5374\u4e00\u76f4\u6ca1\u611f\u89c9\u51fa\u5ba2\u89c2\u5b58\u5728\u7684\u529f\u6548\uff0c\u4f46\u6211\u80fd\u591f\u590d\u6d3b\uff0c \u5374\u53c8\u5b8c\u5168\u662f\u2018\u9f9f\u606f\u672f\u2019\u4e4b\u529f\u3002\u201d \u3000\u3000\u201c\u8fd9\u6837\u8bf4\u6765\uff0e\u5f53\u5e74\u5e08\u5ac2\u548c\u949f\u2014\u8c6a\u5b89\u846c\u5e08\u5144\uff0c\u53cd\u800c\u662f\u5b89\u846c\u9519\u4e86\uff1f\u201d \u3000\u3000\u201c\u4ed6\u4eec\u6ca1\u6709\u9519\u3002\u201d \u3000\u3000\u201c\u5e08\u5144\u53ef\u4e0d\u53ef\u4ee5\u518d\u8bf4\u660e\u767d\u4e9b\uff1f\u201d \u3000\u3000\u201c\u5982\u679c\u6ca1\u6709\u5e08\u7236\u90a3\u79cd\u795e\u529f\u76d6\u4e16\u7684\u4eba\u65bd\u6551\uff0c\u6211\u6700\u540e\u8fd8\u662f\u8981\u6b7b\u53bb\u7684\u3002\u201d \u3000\u3000\u201c\u56db\u7edd\u8001\u4eba\u7528\u4ec0\u4e48\u65b9\u6cd5\u6551\u6d3b\u5e08\u5144\u7684\u3002\u201d \u3000\u3000\u201c\u636e\u5e08\u7236\u8bf4\uff0e\u5982\u679c\u4e00\u4e2a\u4eba\u81ea\u884c\u65bd\u884c\u2018\u9f9f\u606f\u672f\u2019\uff0c\u53ef\u4ee5\u505c\u6b62\u547c\u5438\u4e09\u663c\u591c\u3002\u4f46\u6211\u5f53\u65f6\u662f\u88ab\u5229 \u5251\u523a\u5165\u80f8\u819b\uff0c\u6240\u5e78\u6ca1\u523a\u4e2d\u5fc3\u810f\uff0e \u3000\u3000\u867d\u7136\u5982\u6b64\uff0c\u771f\u6c14\u4e5f\u5927\u534a\u6d88\u6563\uff0c\u5e08\u7236\u662f\u5728\u6211\u88ab\u57cb\u846c\u5728\u96ea\u5806\u91cc\u7684\u4e0b\u534a\u591c\u628a\u6211\u6398\u51fa\u65bd\u6551\u7684\u3002\u4ed6 \u5e26\u6211\u5230\u4e00\u5904\u5c71\u6d1e\u91cc\uff0c\u7ecf\u8fc7\u4e09\u5929\u4e09\u591c\uff0c\u624d\u628a\u6211\u6551\u9192\uff0e\u201d \u3000\u3000\u201c\u5c0f\u5f1f\u8fd8\u662f\u60f3\u77e5\u9053\u4ed6\u662f\u600e\u6837\u65bd\u6551\u7684\uff0e\u201d \u3000\u3000\u201c\u636e\u4ed6\u8001\u4eba\u5bb6\u8bf4\uff0c\u6211\u5f53\u65f6\u4f53\u5185\u5c1a\u6709\u4e00\u70b9\u771f\u6c14\u672a\u6563\uff0c\u5982\u679c\u771f\u6c14\u5df2\u5b8c\u5168\u6563\u53bb\uff0c\u5373\u4f7f\u5927\u795e\u4ed9\u4e5f \u96be\u4ee5\u76f8\u6551\u3002\u81f3\u4e8e\u4ed6\u662f\u7528\u4ec0\u4e48\u65b9\u6cd5\u65bd\u6551\u7684\uff0c\u90a3\u5c31\u4e0d\u662f\u6211\u6240\u80fd\u77e5\u9053\u7684\u4e86\u3002\u201d \u3000\u3000\u8fb9\u8c08\u8fb9\u8d70\uff0c\u7a7f\u8fc7\u5c94\u8def\uff0c\u5f88\u5feb\u4fbf\u5230\u8fbe\u5999\u5999\u5eb5\u524d\u3002 \u3000\u3000\u5f53\u80e1\u67cf\u9f84\u53d1\u73b0\u95e8\u5916\u201c\u5999\u5999\u5eb5\u201d\u4e09\u5b57\u65f6\uff0c\u4e0d\u89c9\u8138\u8272\u4e00\u53d8\uff0c\u6b62\u4f4f\u4e86\u811a\u6b65\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u6123\u4e86\u6123\u9053\uff1a\u201c\u5e08\u5144\u4e3a\u4ec0\u4e48\u4e0d\u8d70\uff1f\u201d \u3000\u3000\u80e1\u67cf\u9f84\u5374\u5446\u5446\u51fa\u795e\uff0e\u81ea\u8a00\u81ea\u8bed\u9053\uff1a\u201c\u5999\u5999\u5eb5\uff01\u5999\u5999\u5eb5\uff1f\u8fd9\u5999\u5999\u4e24\u5b57\u2026\u2026\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u5fc3\u4e2d\u4e00\u52a8\u9053\uff1a\u201c\u5e08\u5144\u662f\u5426\u60f3\u5230\u4e86\u5999\u5999\u4ed9\u5b50\uff1f\u201d \u3000\u3000\u80e1\u67cf\u9f84\u5982\u68a6\u65b9\u9192\uff0c\u6115\u7136\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\u5999\u5999\u4ed9\u5b50\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u662f\u5e9e\u58eb\u51b2\u8001\u524d\u8f88\u90a3\u5929\u5728\u4e07\u6708\u5ce1\u63d0\u8d77\u8fc7\u7684\uff0e\u201d \u3000\u3000\u201c\u4ed6\u4e3a\u4ec0\u4e48\u5ffd\u7136\u63d0\u8d77\u5999\u5999\u4ed9\u5b50\uff1f \u3000\u3000\u201c\u56e0\u4e3a\u6211\u4eec\u66fe\u544a\u8bc9\u4ed6\u5728\u5ce1\u53e3\u9047\u89c1\u8fc7\u5e08\u5144\uff0c\u5e76\u8bf4\u5e08\u5144\u8eab\u65c1\u6709\u4f4d\u8001\u4eba\uff0e\u5e08\u5144\u5bf9\u4ed6\u5341\u5206\u606d\u987a\u3002 \u3000\u3000\u5e9e\u8001\u524d\u8f88\u5f53\u65f6\u5c31\u8ba4\u4e3a\u90a3\u8001\u4eba\u5fc5\u5b9a\u662f\u56db\u7edd\u8001\u4eba\uff0c\u56e0\u800c\u53c8\u63d0\u8d77\u56db\u7edd\u8001\u4eba\u6709\u4f4d\u5e08\u5f1f\u53eb\u4e09\u5999\u4e66\u751f\uff0c \u6709\u4f4d\u5e08\u59b9\u53eb\u5999\u5999\u4ed9\u5b50\u3002\u201d \u3000\u3000\u80e1\u67cf\u9f84\u70b9\u4e86\u70b9\u5934\u9053\uff1a\u201c\u5e9e\u58eb\u51b2\u8001\u524d\u8f88\u679c\u7136\u77e5\u9053\u5f97\u5f88\u591a\uff0c\u6211\u6b63\u662f\u770b\u4e86\u90a3\u6241\u989d\u7684\u2018\u5999\u5999\u2019\u4e8c \u5b57\u89e6\u52a8\u4e86\u611f\u60f3\u3002\u201d \u3000\u3000\u201c\u83ab\u975e\u5e08\u5144\u5e76\u4e0d\u77e5\u9053\u5999\u5999\u4ed9\u5b50\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u201c\u4e0d\u4f46\u6211\u4e0d\u77e5\u9053\uff0c\u8fde\u5e08\u7236\u4ed6\u8001\u4eba\u5bb6\u4e5f\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u201c\u4ed6\u4eec\u5e08\u5144\u59b9\u4e3a\u4ec0\u4e48\u4f1a\u5931\u53bb\u8054\u7edc\uff1f\u201d \u3000\u3000\u201c\u4ed6\u4eec\u5931\u53bb\u8054\u7edc\u636e\u8bf4\u5df2\u7ecf\u4e0a\u767e\u5e74\u4e86\uff0e\u800c\u4e14\u5176\u4e2d\u53ef\u80fd\u7275\u6d89\u7740\u611f\u60c5\u7684\u95ee\u9898\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e48\u8bf4\u5e9e\u8001\u524d\u8f88\u8bb2\u5bf9\u4e86\u3002\u201d \u3000\u3000\u201c\u4ed6\u600e\u4e48\u8bb2\uff1f\u201d \u3000\u3000\u201c\u4ed6\u8bf4\u56db\u7edd\u8001\u4eba\u548c\u4e09\u5999\u4e66\u751f\u90fd\u6df1\u7231\u7740\u5999\u5999\u4ed9\u5b50\uff0c\u5999\u5999\u4ed9\u5b50\u5bf9\u8fd9\u4e24\u4f4d\u5e08\u5144\u4e5f\u90fd\u6df1\u6df1\u76f8\u7231\uff0c \u4e09\u4eba\u4e4b\u95f4\u7684\u8fd9\u6bb5\u60c5\u7231\u7ecf\u8fc7\uff0c\u79f0\u5f97\u4e0a\u662f\u54c0\u6028\u52a8\u4eba\uff0c\u7f20\u7ef5\u60b1\u607b\uff01\u201d \u3000\u3000\u80e1\u67cf\u9f84\u9eef\u7136\u53f9\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u539f\u6765\u4f60\u6bd4\u6211\u77e5\u9053\u5f97\u66f4\u6e05\u695a\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u5fae\u5fae\u2014\u6123\u9053\uff1a\u201c\u8fd9\u4e8c\u4e09\u5e74\u6765\uff0c\u5e08\u5144\u6bcf\u5929\u90fd\u5b88\u7740\u56db\u7edd\u8001\u4eba\uff0c\u5c0f\u5f1f\u600e\u4f1a\u77e5\u9053\u5f97\u6bd4\u60a8\u66f4 \u6e05\u695a\u5462\uff1f\u201d \u3000\u3000\u80e1\u67cf\u9f84\u82e6\u7b11\u9053\uff1a\u201c\u6211\u867d\u7136\u5929\u5929\u5b88\u7740\u4ed6\u8001\u4eba\u5bb6\uff0c\u4f46\u4ed6\u5374\u5bf9\u8fd9\u4e8b\u53ea\u5b57\u4e0d\u63d0\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u5e08\u5144\u53c8\u662f\u600e\u4e48\u77e5\u9053\u7684\u5462\uff1f\u201d \u3000\u3000\u80e1\u67cf\u9f84\u9053\uff1a\u201c\u6709\u4e00\u6b21\u4ed6\u8001\u4eba\u5bb6\u51fa\u95e8\u800c\u53bb\uff0c\u662f\u6211\u5728\u4ed6\u7684\u85cf\u4e66\u4e2d\u53d1\u73b0\u51e0\u5c01\u4e66\u4fe1\uff0c\u624d\u77e5\u9053\u4e86\u8fd9 \u4ef6\u4e8b\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u56db\u7edd\u8001\u4eba\u56de\u6765\u540e\uff0c\u5e08\u5144\u53ef\u66fe\u95ee\u8fc7\u4ed6\uff1f\u201d \u3000\u3000\u80e1\u67cf\u9f84\u82e6\u7b11\u9053\uff1a\u201c\u4e0d\u95ee\u8fd8\u597d\uff0c\u8fd9\u4e00\u95ee\uff0c\u4ed6\u8001\u4eba\u5bb6\u7adf\u53d1\u4e86\u96f7\u9706\uff0c\u8db3\u8db3\u4e09\u5929\uff0c\u6c14\u5f97\u8fde\u996d\u90fd\u6ca1 \u5403\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u6447\u4e86\u6447\u5934\u9053\uff1a\u201c\u90a3\u4e0d\u662f\u6c14\u7684\uff0c\u662f\u52fe\u8d77\u4e86\u4ed6\u7684\u5f80\u4e8b\uff0c\u52fe\u8d77\u4e86\u4ed6\u7684\u611f\u6168\uff0c\u60f3\u5403\u4e5f\u5403\u4e0d \u8fdb\u53bb\u3002\u201d \u3000\u3000\u80e1\u67cf\u9f84\u77aa\u4e86\u9ea6\u5c0f\u660e\u4e00\u773c\u9053\uff1a\u201c\u4f60\u80e1\u8bf4\u4e9b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u7b11\u9053\uff1a\u201c\u4e0d\u8bf4\u5c31\u4e0d\u8bf4\u3002\u5e08\u5144\uff0c\u73b0\u5728\u8fdb\u5eb5\u8981\u7d27\u3002\u201d \u3000\u3000\u80e1\u67cf\u9f84\u7a0d\u505a\u72b9\u8c6b\u9053\uff1a\u201c\u4f60\u4e0d\u59a8\u548c\u82d7\u59d1\u5a18\u5148\u8fdb\u53bb\uff0c\u6211\u6682\u65f6\u5728\u95e8\u5916\u7b49\u7740\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u89c9\u51fa\u80e1\u67cf\u9f84\u7684\u5fc3\u610f\uff0c\u4e5f\u4e0d\u52c9\u5f3a\uff0c\u62db\u547c\u82d7\u7d20\u82d3\u9053\uff1a\u201c\u54b1\u4eec\u8fdb\u53bb\uff01\u201d \u3000\u3000\u4ed6\u5fc3\u91cc\u6709\u6570\uff0c\u5982\u679c\u7f01\u8863\u8001\u5c3c\u771f\u662f\u5f53\u5e74\u7684\u5999\u5999\u4ed9\u5b50\uff0c\u5f85\u4f1a\u513f\u5fc5\u5c06\u51fa\u73b0\u4e00\u5e55\u611f\u4eba\u573a\u9762\uff0e \u3000\u3000\u5230\u8fbe\u4f5b\u5802\u95e8\u5916\uff0c\u4fbf\u770b\u5230\u9759\u6708\u5eb5\u4e3b\u6b63\u5728\u8bf5\u7ecf\uff0e\u5979\u8eab\u65c1\u7ad9\u7740\u4e00\u540d\u5c0f\u5c3c\uff0e\u4f46\u5374\u4e0d\u662f\u4e07\u6620\u971e\u3002 \u3000\u3000\u53ea\u542c\u90a3\u5c0f\u5c3c\u9053\uff1a\u201c\u5e08\u7236\uff0c\u6709\u4eba\u6765\u4e86\uff01\u201d \u3000\u3000\u9759\u6708\u5eb5\u4e3b\u505c\u4e0a\u8bf5\u7ecf\uff0c\u5411\u4f5b\u5802\u5916\u671b\u4e86\u773c\u9053\uff1a\u201c\u539f\u6765\u662f\u9ea6\u5c0f\u65bd\u4e3b\u548c\u82d7\u59d1\u5a18\uff0c\u5feb\u5feb\u5230\u91cc\u9762\u5949 \u8336\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u548c\u82d7\u7d20\u82d3\u8fde\u5fd9\u8fdb\u5165\u4f5b\u5802\u548c\u9759\u6708\u5eb5\u4e3b\u89c1\u793c\uff0e \u3000\u3000\u201c\u4e24\u4f4d\u5c0f\u65bd\u4e3b\u600e\u4e48\u6709\u65f6\u95f4\u5230\u655d\u5eb5\u6765\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u9664\u4e86\u8001\u5e08\u592a\u548c\u5eb5\u4e3b\uff0c\u4e5f\u8981\u6765\u770b\u770b\u4e07\u59d1\u5a18\u3002\u201d \u3000\u3000\u9759\u6708\u9053\uff1a\u201c\u5bb6\u5e08\u548c\u971e\u513f\u90fd\u5f88\u597d\uff0c\u4e0d\u52b3\u4e24\u4f4d\u5c0f\u65bd\u4e3b\u6302\u5ff5\u3002\u201d \u3000\u3000\u201c\u5979\u4eec\u90fd\u5728\u54ea\u91cc\uff1f\u665a\u8f88\u60f3\u53bb\u89c1\u89c1\u3002\u201d \u3000\u3000\u201c\u4e24\u4f4d\u5c0f\u65bd\u4e3b\u6765\u5f97\u4e0d\u51d1\u5de7\uff0c\u5bb6\u5e08\u5df2\u5728\u4e09\u65e5\u524d\u79bb\u5eb5\u5916\u51fa\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u65f6\u5019\u56de\u6765\uff1f\u201d \u3000\u3000\u201c\u4e0d\u4e00\u5b9a\u3002\u201d \u3000\u3000\u201c\u90a3\u4e48\u4e07\u59d1\u5a18\u5462\uff1f\u201d \u3000\u3000\u201c\u971e\u513f\u4e5f\u968f\u5bb6\u5e08\u4e00\u8d77\u53bb\u4e86\uff0e\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u4e0d\u89c9\u5927\u5931\u6240\u671b\uff0c\u4ed6\u660e\u77e5\u9759\u6708\u5eb5\u4e3b\u53ef\u80fd\u662f\u6709\u610f\u9a97\u4eba\uff0c\u5374\u53c8\u4e0d\u4fbf\u8fdb\u5165\u5404\u5904\u5bdf\u770b\u3002 \u3000\u3000\u4ed6\u7f04\u9ed8\u4e86\u534a\u54cd\u9053\uff1a\u201c\u771f\u662f\u4e0d\u5de7\uff0c\u4eca\u5929\u6709\u4f4d\u4e86\u4e0d\u8d77\u7684\u4eba\u7269\u6765\u770b\u8001\u5e08\u592a\uff0e\u504f\u504f\u8001\u5e08\u592a\u5374\u53c8\u5916 \u51fa\u3002\u201d \u3000\u3000\u9759\u6708\u5eb5\u4e3b\u5fae\u5fae\u4e00\u6014\u9053\uff1a\u201c\u9ea6\u5c0f\u65bd\u4e3b\u8bf4\u7684\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u6570\u6708\u524d\u8001\u5e08\u592a\u66fe\u8fdb\u5165\u8ff7\u8e2a\u8c37\uff0c\u5eb5\u4e3b\u5fc5\u5b9a\u77e5\u9053\u8ff7\u8e2a\u8c37\u7684\u4e3b\u4eba\u662f\u4ec0\u4e48\u4eba\u4e86\u3002\u201d \u3000\u3000\u201c\u8d2b\u5c3c\u5f53\u7136\u77e5\u9053\u3002\u8ff7\u8e2a\u8c37\u7684\u8c37\u4e3b\u5c31\u662f\u4f60\u4eec\u7eff\u6797\u76df\u4e3b\uff0c\u83ab\u975e\u970d\u76df\u4e3b\u970d\u5143\u4f3d\u6765\u4e86\uff1f\u201d \u3000\u3000\u201c\u4e0d\u662f\u4ed6\uff0c\u662f\u53e6\u4e00\u4f4d\u76df\u4e3b\u3002\u201d \u3000\u3000\u201c\u636e\u8bf4\u8ff7\u8e2a\u8c37\u81ea\u521b\u7acb\u5230\u73b0\u5728\uff0c\u53ea\u6709\u4e09\u4efb\u76df\u4e3b\uff0c\u524d\u4e8c\u4efb\u90fd\u6b7b\u4e86\uff0c\u54ea\u91cc\u6765\u7684\u53e6\u4e00\u4f4d\u76df\u4e3b\uff1f\u201d \u3000\u3000\u201c\u5eb5\u4e3b\u6709\u6240\u4e0d\u77e5\uff0c\u521b\u7acb\u8ff7\u8e2a\u8c37\u7684\u7b2c\u4e00\u4efb\u76df\u4e3b\u5e76\u6ca1\u6b7b\uff0c\u4eca\u5929\u6765\u8bbf\u8001\u5e08\u592a\u7684\uff0c\u6b63\u662f\u9996\u4efb\u76df\u4e3b \u80e1\u67cf\u9f84\uff0e\u201d \u3000\u3000\u201c\u9ea6\u5c0f\u65bd\u4e3b\u4e0d\u662f\u5f00\u73a9\u7b11\u5427\uff1f\u201d \u3000\u3000\u201c\u665a\u8f88\u600e\u6562\u548c\u5eb5\u4e3b\u5f00\u73a9\u7b11\uff0c\u80e1\u524d\u76df\u4e3b\u5c31\u5728\u8d35\u5eb5\u95e8\u5916\u3002\u201d \u3000\u3000\u201c\u90a3\u5c31\u5feb\u5feb\u6709\u8bf7\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u5306\u5306\u5954\u5230\u95e8\u5916\u9053\uff1a\u201c\u5e08\u5144\uff0c\u5eb5\u4e3b\u6709\u8bf7\uff01\u201d \u3000\u3000\u80e1\u67cf\u9f84\u9053\uff1a\u201c\u90a3\u4f4d\u8001\u5e08\u592a\u53ef\u5728\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u8001\u5e08\u592a\u548c\u4e07\u59d1\u5a18\u90fd\u4e0d\u5728\uff0e\u201d \u3000\u3000\u80e1\u67cf\u9f84\u82e5\u6709\u6240\u5931\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u6211\u53c8\u4f55\u5fc5\u8fdb\u53bb\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u8fdb\u53bb\u5148\u548c\u5eb5\u4e3b\u8ba4\u8bc6\u4e00\u4e0b\uff0c\u4ee5\u540e\u5e08\u5144\u518d\u6765\uff0c\u5c82\u4e0d\u8981\u65b9\u4fbf\u4e9b\uff0c\u201d \u3000\u3000\u80e1\u67cf\u9f84\u968f\u5373\u5728\u9ea6\u5c0f\u660e\u7684\u524d\u5bfc\u4e0b\uff0c\u8fdb\u5165\u5eb5\u5802\u3002 \u3000\u3000\u9759\u6708\u5eb5\u4e3b\u8fde\u5fd9\u8d77\u8eab\u76f8\u8fce\uff0c\u5979\u867d\u7136\u4ece\u672a\u89c1\u8fc7\u80e1\u67cf\u9f84\uff0c\u5374\u4e5f\u4e3a\u80e1\u67cf\u9f84\u7684\u795e\u5a01\u51db\u51db\u6240\u60ca\uff0c\u6697\u9053\uff1a \u201c\u679c\u7136\u662f\u4f4d\u4e86\u4e0d\u8d77\u7684\u82f1\u96c4\u8c6a\u6770\u4eba\u7269\uff0c\u602a\u4e0d\u5f97\u4ed6\u80fd\u5f53\u4e0a\u9996\u4efb\u7eff\u6797\u76df\u4e3b\uff01\u201d \u3000\u3000\u9759\u6708\u5eb5\u4e3b\u672c\u8981\u62db\u5f85\u80e1\u67cf\u9f84\u7b49\u4e09\u4eba\u5348\u996d\uff0c\u80e1\u67cf\u9f84\u4e3a\u4e86\u8d76\u8def\uff0c\u53ea\u597d\u8c26\u8f9e\uff0e\u53ea\u5728\u4f5b\u5802\u5c0f\u5750\u4e86\u4e00 \u4f1a\u513f\uff0c\u4fbf\u5e26\u7740\u9ea6\u5c0f\u660e\u548c\u82d7\u7d20\u82d3\u8f9e\u51fa\u3002 \u3000\u3000\u79bb\u5f00\u5999\u5999\u5eb5\uff0c\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u5e08\u5144\u53ef\u770b\u51fa\u8fd9\u4f4d\u5eb5\u4e3b\u662f\u4f4d\u4ec0\u4e48\u6837\u7684\u4eba\uff1f\u201d \u3000\u3000\u80e1\u67cf\u9f84\u9053\uff1a\u201c\u7cbe\u6c14\u655b\uff0c\u6df1\u85cf\u4e0d\u9732\uff0c\u770b\u6765\u5fc5\u662f\u4f4d\u5185\u529b\u7cbe\u7eaf\u3001\u6b66\u529f\u4e0d\u51e1\u7684\u7edd\u9876\u9ad8\u624b\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u5eb5\u4e3b\u7684\u8eab\u624b\u5982\u4f55\uff0c\u5c0f\u5f1f\u5e76\u672a\u89c1\u8fc7\uff0c\u4f46\u90a3\u4f4d\u8001\u5e08\u592a\u7684\u6b66\u529f\uff0c\u5374\u662f\u5c0f\u5f1f\u81f3\u4eca\u6240 \u89c1\u6700\u9ad8\u7684\u4e00\u4f4d\u3002\u5373\u4f7f\u5e9e\u58eb\u51b2\u8001\u524d\u8f88\uff0c\u4e5f\u53ef\u80fd\u6bd4\u4e0d\u4e0a\u5979\uff0e\u6240\u4ee5\u4f9d\u5c0f\u5f1f\u6240\u6599\uff0c\u5979\u5341\u6709\u516b\u4e5d\u5c31\u662f\u5f53 \u5e74\u7684\u5999\u5999\u4ed9\u5b50\u3002\u201d \u3000\u3000\u80e1\u67cf\u9f84\u9053\uff1a\u201c\u6700\u597d\u4e0d\u8981\u5984\u52a0\u731c\u6d4b\uff0e\u4e0d\u8fc7\u6709\u4ef6\u4e8b\uff0c\u5012\u4f7f\u6211\u9887\u611f\u5b89\u6170\u3002\u201d \u3000\u3000\u201c\u5e08\u5144\u8bf4\u7684\u662f\u54ea\u4ef6\u4e8b\uff1f\u201d \u3000\u3000\u201c\u6211\u662f\u8bf4\u971e\u513f\u80fd\u88ab\u8fd9\u4f4d\u5eb5\u4e3b\u6536\u4f5c\u5f1f\u5b50\uff0c\u53c8\u5f88\u53ef\u80fd\u5f97\u5230\u90a3\u4f4d\u8001\u5e08\u592a\u7684\u771f\u4f20\uff0c\u5c06\u6765\u6b66\u529f\u4e00\u5b9a \u4e0d\u53ef\u9650\u91cf\u3002\u6211\u90a3\u6b7b\u53bb\u7684\u4e49\u5144\u4e07\u6653\u5149\uff0c\u4e5f\u8be5\u7791\u76ee\u4e5d\u6cc9\u4e86\uff01\u201d \u3000\u3000\u80e1\u67cf\u9f84\u987f\u4e86\u4e00\u987f\uff0e\u53c8\u9053\uff1a\u201c\u53ea\u662f\u6211\u62c5\u5fc3\u4e00\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u832b\u7136\u9053\uff1a\u201c\u5e08\u5144\u62c5\u5fc3\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u80e1\u67cf\u9f84\u9053\uff1a\u201c\u971e\u513f\u867d\u7136\u5fc3\u5730\u5584\u826f\uff0e\u4f46\u5374\u591a\u5c11\u5e26\u70b9\u2018\u9a84\u6027\u2019\uff0c\u6211\u62c5\u5fc3\u82e5\u5979\u7684\u6b66\u529f\u5c06\u6765\u9ad8\u51fa \u6587\u5929\u751f\u592a\u591a\uff0c\u5f88\u53ef\u80fd\u59a8\u788d\u4e86\u4ed6\u4eec\u4e24\u4e2a\u7684\u5a5a\u4e8b\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u5fc3\u5934\u5fae\u5fae\u4e00\u9707\uff0c\u6697\u9053\uff1a\u201c\u80e1\u5e08\u5144\u6240\u89c1\u4e0d\u5dee\uff0c\u4e0a\u6b21\u6211\u548c\u5979\u5355\u72ec\u51fa\u8c37\uff0c\u5979\u4f3c\u662f\u5bf9\u6211\u9887 \u6709\u60c5\u610f\u2026\u2026\u201d \u3000\u3000\u60f3\u5230\u8fd9\u91cc\uff0c\u5fd9\u9053\uff1a\u201c\u4e07\u59d1\u5a18\u548c\u6587\u5929\u751f\u8bba\u5e74\u7eaa\u4e5f\u8be5\u4e8c\u5341\u51fa\u5934\u4e86\u5427\uff0c\u4e3a\u4ec0\u4e48\u5230\u73b0\u5728\u8fd8\u4e0d\u5b8c\u6210 \u5a5a\u4e8b\uff1f\u8fde\u522b\u4eba\u90fd\u66ff\u4ed6\u4eec\u7740\u6025\u3002\u201d \u3000\u3000\u80e1\u67cf\u9f84\u9053\uff1a\u201c\u8fd9\u4e0d\u80fd\u602a\u4ed6\u4eec\uff0c\u971e\u513f\u5728\u7236\u4e27\u540e\u5fc5\u987b\u5b88\u5b5d\u4e09\u5e74\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4e07\u5927\u4fa0\u4ece\u53bb\u4e16\u5230\u73b0\u5728\uff0e\u5e94\u8be5\u5df2\u6ee1\u4e09\u5e74\u4e86\u5427\uff1f\u201d \u3000\u3000\u80e1\u67cf\u9f84\u9053\uff1a\u201c\u867d\u7136\u5df2\u6ee1\u4e09\u5e74\uff0c\u4f46\u6700\u8fd1\u4f60\u4eec\u7684\u751f\u6d3b\uff0c\u4e00\u76f4\u52a8\u8361\u4e0d\u5b89\uff0c\u4ed6\u4eec\u53c8\u600e\u80fd\u987e\u5230\u5a5a \u4e8b\uff1f\u201d \u3000\u3000\u201c\u5982\u4eca\u5e08\u5144\u56de\u6765\uff0c\u6b63\u597d\u53ef\u4ee5\u4e3a\u4ed6\u4eec\u4e3b\u6301\u5a5a\u4e8b\u3002\u201d \u3000\u3000\u201c\u6211\u4e5f\u6709\u8fd9\u79cd\u6253\u7b97\u3002\u4f46\u971e\u513f\u76ee\u524d\u5df2\u88ab\u9759\u6708\u5eb5\u4e3b\u6536\u4e3a\u5f92\u5f1f\uff0c\u4eba\u5728\u5999\u5999\u5eb5\uff0c\u8fd9\u4e8b\u53c8\u4e0d\u5f97\u4e0d\u803d \u6401\u4e0b\u53bb\u4e86\u3002\u201d \u3000\u3000\u6653\u884c\u591c\u5bbf\u3002\u80e1\u67cf\u9f84\u7b49\u4e09\u4eba\u5927\u7ea6\u4e00\u6708\u5de6\u53f3\uff0c\u4fbf\u5230\u4e86\u6d59\u4e1c\u5929\u53f0\u5c71\u3002 \u3000\u3000\u5f53\u5230\u8fbe\u4e07\u82b1\u5bab\u5916\uff0c\u5df2\u662f\u4e0b\u5348\u65e5\u5df2\u504f\u897f\u4e4b\u65f6\u3002 \u3000\u3000\u4e07\u82b1\u5bab\u5916\u4e00\u7247\u4e18\u9675\u5730\uff0c\u6b63\u662f\u6570\u6708\u524d\u8c37\u5bd2\u9999\u5927\u6218\u5c11\u6797\u3001\u6b66\u5f53\u4e24\u5927\u95e8\u6d3e\u7684\u6218\u573a\uff0c\u867d\u7136\u65f6\u9694\u5df2 \u4e45\uff0c\u4fbf\u5728\u611f\u89c9\u4e0a\u5374\u4ecd\u5145\u6ee1\u7740\u4e00\u7247\u8083\u6740\u4e4b\u6c14\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u6307\u4e86\u6307\u4e00\u5904\u571f\u4e18\u9053\uff1a\u201c\u5e08\u5144\uff0c\u5e08\u5ac2\u5c31\u662f\u5728\u90a3\u91cc\u56e0\u4f24\u91cd\u81ea\u77e5\u65e0\u6cd5\u6d3b\u547d\uff0c\u81ea\u523a\u80f8\u5802\u800c \u6b7b\u7684\uff01\u201d \u3000\u3000\u80e1\u67cf\u9f84\u4ef0\u5929\u957f\u957f\u53f9\u606f\u4e00\u58f0\uff0c\u5728\u73b0\u573a\u51ed\u540a\u751a\u4e45\uff0c\u624d\u611f\u4f24\u65e0\u6bd4\u7684\u5429\u5490\u9053\uff1a\u201c\u6211\u4eec\u8fdb\u5bab\u5427\uff01\u201d \u3000\u3000\u7531\u5bab\u5916\u7684\u724c\u697c\u8d70\u5230\u5bab\u5185\uff0c\u6709\u4e00\u6bb5\u5f88\u957f\u7684\u77f3\u7ea7\u8def\u3002 \u3000\u3000\u5728\u4ece\u524d\uff0c\u5bab\u5916\u8c37\u9053\u4e24\u65c1\u7684\u6811\u4e0a\u6216\u4e0a\u7a7a\u3001\u7ecf\u5e38\u6709\u5f88\u591a\u4e0d\u77e5\u540d\u7684\u7ea2\u9e1f\u51fa\u73b0\uff0c\u800c\u4e14\u9e23\u58f0\u602a\u5f02\uff0c \u4f3c\u901a\u7075\u6027\u3002 \u3000\u3000\u5982\u4eca\uff0c\u5df2\u770b\u4e0d\u89c1\u8fd9\u4e9b\u7ea2\u9e1f\uff0c\u597d\u50cf\u4e5f\u968f\u7740\u4e07\u82b1\u5bab\u4e3b\u4f5f\u516c\u5e38\u7684\u6b7b\u53bb\u800c\u6d88\u901d\u65e0\u8e2a\u3002 \u3000\u3000\u80e1\u67cf\u9f84\u8fb9\u8d70\u8fb9\u95ee\u9053\uff1a\u201c\u76ee\u524d\u4e07\u82b1\u5bab\u4e2d\u8fd8\u6709\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u5f53\u65e5\u4e07\u82b1\u5bab\u4e3b\u5728\u4e16\u65f6\uff0c\u6709\u4e8c\u4e09\u5341\u540d\u4e0b\u4eba\uff0c\u5bab\u4e3b\u597d\u8272\uff0c\u6240\u4ee5\u8fd9\u4e9b\u4e0b\u4eba\u4e2d\u591a\u534a \u662f\u5e74\u8f7b\u8c8c\u7f8e\u7684\u4e2b\u73af\uff0e\u5176\u4e2d\u4e00\u540d\u7ba1\u4e8b\u7684\u53eb\u5305\u4e5d\u5cf0\uff0c\u54b1\u4eec\u73b0\u5728\u5fc5\u987b\u5148\u627e\u5230\u5305\u4e5d\u5cf0\u3002\u201d \u3000\u3000\u80e1\u67cf\u9f84\u54e6\u4e86\u4e00\u58f0\uff0c\u5931\u58f0\u9053\uff1a\u201c\u5305\u4e5d\u5cf0\uff1f\u8fd9\u540d\u5b57\u597d\u50cf\u542c\u8bf4\u8fc7\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u5e08\u5144\u53ef\u662f\u5728\u4ec0\u4e48\u5730\u65b9\u89c1\u8fc7\u4ed6\uff1f\u201d \u3000\u3000\u201c\u89c1\u5012\u6ca1\u89c1\u8fc7\uff0c\u53ea\u662f\u542c\u8bf4\u8fc7\u3002\u201d \u3000\u3000\u201c\u6211\u60f3\u5e08\u5144\u4e5f\u4e0d\u53ef\u80fd\u89c1\u8fc7\u4ed6\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u4ed6\u8db3\u8db3\u6709\u4e09\u5341\u51e0\u5e74\u6ca1\u79bb\u5f00\u8fc7\u4e07\u82b1\u5bab\u3002\u201d \u3000\u3000\u201c\u53ef\u77e5\u9053\u4ed6\u7684\u8eab\u4efd\u6765\u5386\uff1f\u201d \u3000\u3000\u201c\u8bf4\u8d77\u6765\u4ed6\u662f\u4e00\u4e2a\u975e\u5e38\u53ef\u601c\u800c\u503c\u5f97\u540c\u60c5\u7684\u4eba\uff0c\u4ed6\u539f\u662f\u4e07\u82b1\u5bab\u4e3b\u7684\u5e08\u5144\uff0c\u636e\u8bf4\u4e07\u82b1\u5bab\u4e3b\u7684 \u5f88\u591a\u6b66\u529f\uff0c\u90fd\u662f\u7531\u4ed6\u4f20\u6388\u7684\u3002\u201d \u3000\u3000\u201c\u65e2\u7136\u5982\u6b64\uff0c\u4ed6\u4e3a\u4ec0\u4e48\u53cd\u800c\u4e3a\u4e07\u82b1\u5bab\u4e3b\u6240\u5f79\u4f7f\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u4ed6\u7684\u820c\u5934\u5df2\u88ab\u4e07\u82b1\u5bab\u4e3b\u5272\u53bb\uff0c\u5e76\u4e14\u670d\u4e86\u4e07\u82b1\u5bab\u4e3b\u7684\u6bd2\u836f\uff0c\u5fc5\u987b\u6bcf\u534a\u5e74\u5411\u4e07\u82b1\u5bab\u4e3b \u8ba8\u2014\u6b21\u89e3\u836f\u3002\u4ed6\u867d\u542c\u547d\u4e07\u82b1\u5bab\u4e3b\u9a71\u4f7f\uff0c\u5b9e\u9645\u4e0a\u5bf9\u4ed6\u6068\u4e4b\u5165\u9aa8\u3002\u201d \u3000\u3000\u201c\u8fd9\u6837\u8bf4\u5305\u4e5d\u5cf0\u5df2\u4e0d\u4f1a\u8bf4\u8bdd\u4e86\uff0c\u6211\u4eec\u627e\u5230\u4ed6\u53c8\u6709\u4f55\u7528\uff1f\u201d \u3000\u3000\u201c\u4ed6\u4e0d\u4f1a\u8bf4\u8bdd\u6ca1\u5173\u7cfb\uff0c\u6211\u4eec\u53ef\u4ee5\u95ee\u522b\u4eba\u3002\u5e08\u5144\uff0c\u5c0f\u5f1f\u5012\u60f3\u8d77\u4e00\u4ef6\u4e8b\u6765\u4e86\u3002\u201d \u3000\u3000\u201c\u4f60\u60f3\u8d77\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u4e24\u773c\u7728\u52a8\u4e86\u51e0\u4e0b\u9053\uff1a\u201c\u5e08\u5144\u53ef\u77e5\u9053\u5e08\u5ac2\u4e34\u6b7b\u524d\uff0c\u5979\u7684\u6b66\u529f\u5df2\u7ecf\u9ad8\u4e0d\u53ef\u6d4b\u3002\u201d \u3000\u3000\u80e1\u67cf\u9f84\u7565\u4e00\u6c89\u541f\u9053\uff1a\u201c\u5979\u7684\u6b66\u529f\u4ece\u524d\u5e76\u4e0d\u9ad8\uff0c\u600e\u4f1a\u8fdb\u5c55\u5230\u8fd9\u79cd\u5883\u754c\u5462\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u9664\u4e86\u4e07\u82b1\u5bab\u4e3b\u6559\u8fc7\u5979\u6b66\u529f\u5916\uff0c\u53e6\u4e00\u4f4d\u6559\u5979\u6b66\u529f\u7684\uff0e\u5f88\u53ef\u80fd\u662f\u4e09\u5999\u4e66\u751f\u3002\u201d \u3000\u3000\u80e1\u67cf\u9f84\u542c\u5f97\u4e00\u695e\uff0c\u6025\u6025\u95ee\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u5e08\u5ac2\u5728\u548c\u5c11\u6797\u3001\u6b66\u5f53\u51b3\u6218\u524d\uff0c\u66fe\u5931\u8e2a\u4e86\u597d\u591a\u5929\uff0e\u540e\u6765\u624d\u5728\u2014\u5904\u5730\u4e0b\u627e\u5230\u5979\u3002 \u542c\u4eba\u8bf4\uff0c\u4e09\u5999\u4e66\u751f\u5f88\u53ef\u80fd\u5c31\u4f4f\u5728\u5730\u5e95\u4e0b\u3002\u201d \u3000\u3000\u80e1\u67cf\u9f84\u8d8a\u53d1\u5403\u60ca\u9053\uff1a\u201c\u90a3\u5904\u5730\u4e0b\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u5c0f\u5f1f\u4e5f\u8bb0\u4e0d\u6e05\u695a\u4e86\uff0c\u53cd\u6b63\u5c31\u5728\u4e07\u82b1\u5bab\u9644\u8fd1\uff0c\u800c\u4e14\u662f\u7531\u5f88\u591a\u4eba\u6398\u4e86\u597d\u51e0\u5929\u624d \u6398\u5f00\u7684\u3002\u201d \u3000\u3000\u201c\u65e2\u7136\u5df2\u7ecf\u6398\u5f00\uff0c\u90a3\u5c31\u5fc5\u5b9a\u770b\u5230\u4e09\u5999\u4e66\u751f\u4e86\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\u3002\u56e0\u4e3a\u53ea\u6398\u5f00\u4e00\u90e8\u5206\uff0c\u518d\u5f80\u91cc\u6398\uff0c\u660e\u77e5\u91cc\u9762\u5fc5\u6709\u6d1e\u5ba4\uff0c\u53ef\u5c31\u662f\u6398\u4e0d\u5f00\u3002\u201d \u3000\u3000\u8bf4\u8bdd\u95f4\u5df2\u5230\u8fbe\u5bab\u5899\u524d\uff0e\u3000\u4e00\u540d\u7d2b\u8863\u5c0f\u5a62\u8ba4\u51fa\u662f\u9ea6\u5c0f\u660e\uff0c\u8fde\u5fd9\u5954\u4e86\u8fc7\u6765\u9053\uff1a\u201c\u9ea6\u5c0f\u4fa0\uff0e\u597d \u51e0\u5e74\u4e0d\u89c1\u5566\uff0c\u96be\u5f97\u4f60\u53c8\u56de\u5230\u5bab\u91cc\u6765\uff0c\u6709\u4e8b\u5417\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u7b11\u9053\uff1a\u201c\u5305\u8001\u524d\u8f88\u53ef\u5728\u91cc\u9762\uff1f\u4ed6\u8001\u4eba\u5bb6\u76ee\u524d\u662f\u5426\u5df2\u505a\u4e86\u4e07\u82b1\u5bab\u4e3b\uff1f\u201d \u3000\u3000\u90a3\u5c0f\u5a62\u9053\uff1a\u201c\u4ed6\u8001\u4eba\u5bb6\u76ee\u524d\u867d\u7136\u5df2\u662f\u4e07\u82b1\u5bab\u7684\u4e3b\u4eba\uff0c\u4f46\u5374\u53ea\u51c6\u522b\u4eba\u4ecd\u79f0\u4ed6\u603b\u7ba1\u3002\u201d \u3000\u3000\u90a3\u5c0f\u5a62\u628a\u80e1\u67cf\u9f84\u7b49\u4e09\u4eba\u5f15\u8fdb\u5ba2\u5385\uff0c\u4eb2\u5207\u7684\u6ce1\u8336\uff0c\u63a5\u7740\u9053\uff1a\u201c\u4e09\u4f4d\u8bf7\u7a0d\u5f85\uff0e\u6211\u53bb\u8bf7\u5305\u603b\u7ba1 \u6765\u3002\u201d \u3000\u3000\u80e1\u67cf\u9f84\u7559\u610f\u5ba2\u5385\u5185\u7684\u5e03\u7f6e\uff0c\u7adf\u662f\u5341\u5206\u9ad8\u96c5\u3002 \u3000\u3000\u6765\u5230\u5ba2\u5385\u95e8\u524d\u671b\u53bb\uff0c\u56f4\u5899\u5185\u4ead\u53f0\u697c\u9601\uff0c\u5047\u5c71\u6c34\u69ad\uff0e\u666f\u8272\u7f8e\u4e0d\u80dc\u6536\u3002 \u3000\u3000\u4e0d\u5927\u2014\u4f1a\u513f\u3001\u4fbf\u89c1\u4e00\u4f4d\u9762\u8c8c\u6e05\u7626\u4f46\u795e\u6001\u9887\u4e3a\u6148\u7965\u7684\u9a7c\u80cc\u8001\u4eba\uff0c\u7531\u65b9\u624d\u90a3\u540d\u7d2b\u8863\u5c0f\u5a62\u966a\u4f8d \u7740\u7f13\u7f13\u800c\u6765\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u62a2\u5148\u8fce\u4e0a\u524d\u53bb\uff0c\u62b1\u62f3\u4e00\u793c\u9053\uff1a\u201c\u5305\u8001\u524d\u8f88\u522b\u6765\u53ef\u597d\uff0c\u8fd8\u8bb0\u5f97\u6211\u5417\uff1f\u201d \u3000\u3000\u4ed6\u7684\u8fd9\u51e0\u53e5\u8bdd\uff0c\u5f53\u7136\u4e0d\u6307\u671b\u5305\u4e5d\u5cf0\u56de\u7b54\uff0e\u56e0\u4e3a\u5305\u4e5d\u5cf0\u6839\u672c\u4e0d\u80fd\u8bf4\u8bdd\u3002 \u3000\u3000\u8c01\u77e5\u5305\u4e5d\u5cf0\u5374\u70b9\u4e86\u70b9\u5934\uff0c\u7b11\u5475\u5475\u9053\uff1a\u201c\u5c0f\u660e\uff0c\u4f60\u6765\u5566\uff1f\u771f\u4e0d\u5bb9\u6613\uff0c\u8001\u592b\u53c8\u770b\u5230\u4f60\u4e86\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u60ca\u559c\u9053\uff1a\u201c\u539f\u6765\u60a8\u8001\u4eba\u5bb6\u5df2\u7ecf\u4f1a\u2026\u2026\u201d \u3000\u3000\u4ed6\u672c\u6765\u8981\u8bf4\u201c\u539f\u6765\u60a8\u8001\u4eba\u5bb6\u5df2\u7ecf\u4f1a\u8bf4\u8bdd\u4e86\uff01\u201d \u3000\u3000\u5305\u4e5d\u5cf0\u4f9d\u7136\u5475\u5475\u7b11\u9053\uff1a\u201c\u4f60\u53ef\u662f\u8bf4\u8001\u592b\u4e3a\u4ec0\u4e48\u5df2\u7ecf\u4e0d\u54d1\u4e86\uff1f\u5f85\u4f1a\u513f\u518d\u544a\u8bc9\u4f60\uff0c\u5feb\u544a\u8bc9\u8001 \u592b\u53e6\u5916\u4e24\u4f4d\u5ba2\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u968f\u5373\u4e3a\u80e1\u67cf\u9f84\u548c\u82d7\u7d20\u82d3\u5f15\u89c1\uff0e \u3000\u3000\u5305\u4e5d\u5cf0\u6570\u5341\u5e74\u672a\u79bb\u5f00\u4e07\u82b1\u5bab\uff0c\u6839\u672c\u8fde\u80e1\u67cf\u9f84\u7684\u540d\u5b57\u90fd\u672a\u542c\u8bf4\u8fc7\uff0c\u4e0d\u8fc7\u65e2\u66fe\u505a\u8fc7\u5929\u4e0b\u7eff\u6797 \u76df\u4e3b\uff0c\u81ea\u7136\u4e0d\u662f\u4f4d\u7b80\u5355\u4eba\u7269\u3002\u5f53\u4e0b\uff0c\u62db\u5f85\u5f97\u5341\u5206\u4eb2\u5207\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u5bd2\u6684\u8fc7\u540e\uff0e\u4fbf\u5f00\u95e8\u89c1\u5c71\u7684\u9053\uff1a\u201c\u80e1\u76df\u4e3b\u6b64\u6765\uff0c\u662f\u8981\u62dc\u89c1\u4e00\u4f4d\u524d\u8f88\u9ad8\u4eba\uff0c\u4e5f\u8bb8\u5305\u8001 \u524d\u8f88\u77e5\u9053\u8fd9\u4f4d\u524d\u8f88\u9ad8\u4eba\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u5305\u4e5d\u5cf0\u76b1\u4e86\u76b1\u7709\u9053\uff1a\u201c\u4f60\u8bf4\u7684\u662f\u8c01\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4e09\u5999\u4e66\u751f\u3002\u201d \u3000\u3000\u5305\u4e5d\u5cf0\u7acb\u5373\u795e\u8272\u4e00\u53d8\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\u4e09\u5999\u4e66\u751f\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u5305\u8001\u524d\u8f88\u8fd8\u662f\u8bf7\u8bf4\u5b9e\u8bdd\u5427\u3002\u60a8\u4ee5\u4e3a\u665a\u8f88\u4e0d\u77e5\u9053\uff0c\u4e09\u5999\u4e66\u751f\u5c31\u4f4f\u5728\u4e07\u82b1\u5bab\u9644 \u8fd1\u7684\u5730\u4e0b\u5bab\u5ba4\u91cc\u5417\uff1f \u3000\u3000\u4e0a\u6b21\u592b\u4eba\u6740\u6b7b\u5bab\u4e3b\u540e\uff0c\u4fbf\u5230\u5730\u4e0b\u53bb\u4f1a\u89c1\u4e86\u4e09\u5999\u4e66\u751f\uff0e\u800c\u4e14\u548c\u4e09\u5999\u4e66\u751f\u5728\u2014\u8d77\u597d\u591a\u5929\uff0e\u8fd9 \u4e9b\u4e8b\u60a8\u5e94\u5f53\u6bd4\u665a\u8f88\u66f4\u6e05\u695a\u624d\u5bf9\u3002\u201d \u3000\u3000\u4ed6\u6240\u8bf4\u7684\u592b\u4eba\uff0c\u662f\u6307\u7684\u8c37\u5bd2\u9999\u3002 \u3000\u3000\u56e0\u4e3a\u8c37\u5bd2\u9999\u4e3a\u4e86\u4e60\u5f97\u4e07\u82b1\u5bab\u4e3b\u4f5f\u516c\u5e38\u7684\u6b66\u529f\uff0e\u4ee5\u4fbf\u66ff\u592b\u62a5\u4ec7\uff0c\u66fe\u5047\u610f\u8981\u5ac1\u7ed9\u4f5f\u516c\u5e38\uff0c\u5e76 \u8a00\u660e\u5b66\u8fc7\u6240\u6709\u6b66\u529f\u540e\u518d\u5b8c\u6210\u82b1\u70db\u3002 \u3000\u3000\u5c31\u5728\u82b1\u70db\u4e4b\u591c\uff0c\u5979\u523a\u6b7b\u4e86\u4f5f\u516c\u5e38\uff0c\u7136\u540e\u624d\u6709\u7f18\u5728\u5730\u4e0b\u4f1a\u89c1\u4e86\u4e09\u5999\u4e66\u751f\uff0c\u7ecf\u4e09\u5999\u4e66\u751f\u6536\u4e3a \u5f1f\u5b50\uff0c\u53c8\u4e60\u5f97\u4e86\u66f4\u4e0a\u5c42\u6402\u7684\u6b66\u529f\u3002 \u3000\u3000\u5305\u4e5d\u5cf0\u7ecf\u9ea6\u5c0f\u660e\u8fd9\u4e00\u552c\uff0e\u53ea\u597d\u987f\u4e86\u4e00\u987f\u9053\uff1a\u201c\u4e09\u5999\u8001\u524d\u8f88\u7684\u786e\u5c31\u5728\u4e07\u82b1\u5bab\u9644\u8fd1\u5730\u4e0b\uff0c\u4e0d \u77e5\u80e1\u76df\u4e3b\u89c1\u4ed6\u6709\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u80e1\u67cf\u9f84\u63a5\u8fc7\u8bdd\u9053\uff1a\u201c\u6709\u4eba\u6709\u5c01\u4fe1\u7ed9\u4e09\u5999\u8001\u524d\u8f88\uff0c\u5728\u4e0b\u5c31\u662f\u5e26\u4fe1\u7684\u4eba\uff0e\u201d \u3000\u3000\u5305\u4e5d\u5cf0\u4f3c\u662f\u6709\u4e9b\u4e0d\u4fe1\uff0c\u6123\u4e86\u6123\u9053\uff1a\u201c\u4e0d\u53ef\u80fd\u5427\uff1f\u4e09\u5999\u8001\u524d\u8f88\u4e0e\u4e16\u9694\u7edd\u81f3\u5c11\u5df2\u5c06\u8fd1\u767e\u5e74\uff0c \u5f53\u4eca\u4e4b\u4e16\uff0c\u7edd\u5c11\u6709\u4eba\u77e5\u9053\u4ed6\u8fd8\u6d3b\u5728\u4e16\u4e0a\uff0c\u600e\u4f1a\u6709\u4eba\u5199\u4fe1\u7ed9\u4ed6\u5462\uff1f\u201d \u3000\u3000\u80e1\u67cf\u9f84\u8083\u5bb9\u9053\uff1a\u201c\u5305\u524d\u8f88\u6709\u6240\u4e0d\u77e5\uff0c\u8fd9\u4f4d\u5199\u4fe1\u4e4b\u4eba\u662f\u4e09\u5999\u524d\u8f88\u7684\u540c\u95e8\u5e08\u5144\u3002\u201d \u3000\u3000\u80e1\u67cf\u9f84\u4ece\u6000\u4e2d\u638f\u51fa\u4fe1\uff0c\u606d\u606d\u656c\u656c\u7684\u653e\u5728\u684c\u4e0a\u9053\uff1a\u201c\u4e09\u5999\u524d\u8f88\u7684\u5e08\u5144\u662f\u56db\u7edd\u8001\u4eba\uff0e\u8fd9\u5c01\u4fe1 \u4e00\u70b9\u4e0d\u9519\u662f\u56db\u7edd\u8001\u4eba\u7684\u4eb2\u7b14\u3002\u201d \u3000\u3000\u5305\u4e5d\u5cf0\u5e76\u672a\u770b\u4fe1\uff0e\u56e0\u4e3a\u662f\u5426\u56db\u7edd\u8001\u4eba\u7684\u4eb2\u7b14\uff0c\u4ed6\u6839\u672c\u65e0\u6cd5\u8fa8\u8ba4\uff0c\u9ed8\u4e86\u4e00\u9ed8\u9053\uff1a\u201c\u80e1\u76df\u4e3b \u662f\u56db\u7edd\u8001\u4eba\u7684\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u80e1\u67cf\u9f84\u9053\uff1b\u201c\u56db\u7edd\u8001\u4eba\u6b63\u662f\u5bb6\u5e08\u3002\u201d \u3000\u3000\u5305\u4e5d\u5cf0\u9762\u6cdb\u4e0d\u60a6\u4e4b\u8272\u9053\uff1a\u201c\u80e1\u76df\u4e3b\u4f55\u5fc5\u8981\u6b3a\u9a97\u8001\u592b\uff1f\u201d \u3000\u3000\u201c\u5728\u4e0b\u8bf4\u7684\u662f\u5b9e\u8bdd\uff0c\u600e\u6562\u6b3a\u9a97\u5305\u524d\u8f88\u3002\u201d \u3000\u3000\u201c\u636e\u8001\u592b\u6240\u77e5\uff0c\u56db\u7edd\u8001\u4eba\u5df2\u9041\u4e16\u5c06\u8fd1\u767e\u5e74\uff0c\u4ee5\u80e1\u76df\u4e3b\u7684\u5e74\u7eaa\uff0c\u600e\u53ef\u80fd\u662f\u4ed6\u8001\u4eba\u5bb6\u7684\u5f1f \u5b50\uff1f\u201d \u3000\u3000\u80e1\u67cf\u9f84\u9053\uff1a\u201c\u5305\u524d\u8f88\u6709\u6240\u4e0d\u77e5\uff0c\u665a\u8f88\u662f\u4e24\u5e74\u524d\u624d\u88ab\u5bb6\u5e08\u6536\u5f52\u95e8\u4e0b\u7684\u3002\u201d \u3000\u3000\u5305\u4e5d\u5cf0\u6115\u7136\u9053\uff1a\u201c\u56db\u7edd\u8001\u4eba\u5f53\u771f\u8fd8\u6d3b\u5728\u4e16\u4e0a\uff1f\u8fd9\u4e8b\u53ea\u6015\u8fde\u4e09\u5999\u8001\u524d\u8f88\u4e5f\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u80e1\u67cf\u9f84\u89c1\u5305\u4e5d\u5cf0\u5e76\u672a\u770b\u4fe1\uff0c\u53ea\u597d\u6536\u4e0b\u8d77\u6765\u9053\uff1a\u201c\u90a3\u5c31\u6709\u52b3\u5305\u524d\u8f88\u5e26\u5728\u4e0b\u524d\u5f80\u8c12\u89c1\u4e09\u5999\u524d \u8f88\uff01\u201d \u3000\u3000\u5305\u4e5d\u5cf0\u9762\u6cdb\u4e3a\u96be\u4e4b\u8272\u9053\uff1a\u201c\u4e0d\u7792\u80e1\u76df\u4e3b\uff0c\u4e09\u5999\u524d\u8f88\u867d\u5728\u655d\u5bab\u9644\u8fd1\u5730\u4e0b\u4f4f\u4e86\u51e0\u5341\u5e74\uff0c\u4f46\u8001 \u592b\u4e0e\u6b7b\u53bb\u7684\u5bab\u4e3b\u5374\u4e00\u76f4\u6beb\u4e0d\u77e5\u60c5\u3002 \u3000\u3000\u4e00\u76f4\u5230\u6570\u6708\u524d\u5404\u5927\u95e8\u6d3e\u9f50\u96c6\u4e07\u82b1\u5bab\uff0c\u4e0e\u592b\u4eba\u5c55\u5f00\u4e00\u573a\u6fc0\u6218\uff0c\u8001\u592b\u624d\u53d1\u73b0\u90a3\u6761\u901a\u5f80\u5730\u4e0b\u79d8 \u9053\uff0c\u5176\u5b9e\u8fd9\u6761\u79d8\u9053\u4e5f\u662f\u4e0a\u6b21\u4e34\u65f6\u5f00\u6398\u51fa\u6765\u7684\u3002\u201d \u3000\u3000\u80e1\u67cf\u9f84\u9053\uff1a\u201c\u65e2\u7136\u6709\u8def\u8fdb\u5165\u5730\u4e0b\uff0c\u5305\u524d\u8f88\u5c31\u8be5\u4e3a\u5728\u4e0b\u505a\u4e00\u5f15\u8def\u4e4b\u4eba\u3002\u201d \u3000\u3000\u5305\u4e5d\u5cf0\u6447\u5934\u9053\uff1a\u201c\u53ef\u60dc\u8fd9\u6761\u79d8\u9053\u53c8\u5df2\u5c01\u4f4f\u4e86\u3002\u201d \u3000\u3000\u201c\u96be\u9053\u4e09\u5999\u524d\u8f88\u5c31\u6c38\u8fdc\u5c45\u4f4f\u5730\u4e0b\u4e0d\u51fa\u6765\uff1f\u201d \u3000\u3000\u201c\u786e\u662f\u5982\u6b64\u3002\u4ed6\u51e0\u5341\u5e74\u4ece\u4e0d\u66fe\u5230\u5730\u9762\u4e0a\u6765\u3002\u201d \u3000\u3000\u201c\u90a3\u4e48\u4ed6\u5403\u4ec0\u4e48\uff1f\u662f\u600e\u6837\u751f\u6d3b\u7684\uff1f\u201d \u3000\u3000\u201c\u636e\u8bf4\u5728\u4ed6\u7684\u6d1e\u5ba4\u5916\u9762\u751f\u6709\u4e0d\u5c11\u5343\u5e74\u96ea\u83b2\uff0c\u53ea\u8981\u670d\u4e0b\u90a3\u5343\u5e74\u96ea\u83b2\u7684\u2014\u679d\u4e00\u53f6\uff0c\u4fbf\u53ef\u6570\u6708 \u4e0d\u997f\u3002\u201d \u3000\u3000\u80e1\u67cf\u9f84\u4e0d\u89c9\u5927\u5931\u6240\u671b\u9053\uff1a\u201c\u8fd9\u6837\u8bf4\u5728\u4e0b\u662f\u65e0\u6cd5\u89c1\u5230\u4e09\u5999\u524d\u8f88\u4e86\uff1f\u201d \u3000\u3000\u5305\u4e5d\u5cf0\u72b9\u8c6b\u4e86\u534a\u664c\u9053\uff1a\u201c\u80e1\u76df\u4e3b\u60f3\u89c1\u4e09\u5999\u524d\u8f88\uff0e\u90a3\u662f\u4e0d\u53ef\u80fd\u7684\u4e8b\uff0c\u4e0d\u8fc7\u8001\u592b\u4e5f\u8bb8\u80fd\u628a\u4fe1 \u8f6c\u5448\u7ed9\u4ed6\u3002\u201d \u3000\u3000\u80e1\u67cf\u9f84\u8feb\u4e0d\u53ca\u5f85\u95ee\u9053\uff1a\u201c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d \u3000\u3000\u5305\u4e5d\u8702\u9053\uff1a\u201c\u56e0\u4e3a\u53e6\u6709\u4e00\u6761\u79d8\u9053\uff0c\u53ef\u4ee5\u901a\u5165\u5730\u4e0b\u3002\u201d \u3000\u3000\u80e1\u67cf\u9f84\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0e\u5305\u524d\u8f88\u5c3d\u53ef\u5e26\u5728\u4e0b\u7531\u79d8\u9053\u8fdb\u5165\u5730\u4e0b\uff0e\u201d \u3000\u3000\u5305\u4e5d\u5cf0\u6447\u5934\u9053\uff1a\u201c\u4e0d\u6210\uff01\u4e09\u5999\u524d\u8f88\u6709\u89c4\u5b9a\uff0c\u90a3\u6761\u79d8\u9053\u53ea\u53ef\u7531\u8001\u592b\u4e00\u4eba\u8fdb\u5165\uff0c\u800c\u4e14\u6bcf\u5929\u53ea \u80fd\u8fdb\u51fa\u4e00\u6b21\u3002\u201d \u3000\u3000\u201c\u80fd\u5426\u901a\u878d\u4e00\u4e0b\uff1f\u201d \u3000\u3000\u201c\u4e0d\u6210\uff01\u5426\u5219\u8fde\u8001\u592b\u4ee5\u540e\u4e5f\u522b\u60f3\u8fdb\u53bb\u4e86\u3002\u201d \u3000\u3000\u80e1\u67cf\u9f84\u65e0\u5948\uff0c\u4e0d\u5f97\u4e0d\u518d\u53d6\u51fa\u90a3\u5c01\u4fe1\u9053\uff1a\u201c\u90a3\u5c31\u6709\u52b3\u5305\u524d\u8f88\u628a\u4fe1\u8f6c\u5448\u4e09\u5999\u524d\u8f88\uff0c\u4e0d\u8fc7\u5728\u4e0b \u5e0c\u671b\u80fd\u770b\u5230\u4ed6\u7684\u56de\u4fe1\u3002\u201d \u3000\u3000\u5305\u4e5d\u5cf0\u671b\u4e86\u671b\u5929\u8272\u9053\uff1a\u201c\u770b\u6765\u4e09\u4f4d\u4eca\u665a\u662f\u65e0\u6cd5\u79bb\u5f00\u4e07\u82b1\u5bab\u4e86\uff0c\u8001\u592b\u9a6c\u4e0a\u547d\u4eba\u4e3a\u4e09\u4f4d\u6574\u7406 \u4f4f\u5904\uff0c\u665a\u4e0a\u8fd8\u8981\u8bbe\u5bb4\u4e3a\u4e09\u4f4d\u6d17\u5c18\u3002\u201d \u3000\u3000\u80e1\u67cf\u9f84\u3001\u9ea6\u5c0f\u660e\u3001\u82d7\u7d20\u82d3\u88ab\u62db\u5f85\u5728\u6c34\u69ad\u8fb9\u7684\u4e00\u5ea7\u5c0f\u697c\u91cc\u3002\u82d7\u7d20\u82d3\u4f4f\u697c\u4e0a\uff0c\u9ea6\u5c0f\u660e\u548c\u80e1\u67cf \u9f84\u4f4f\u697c\u4e0b\uff0e \u3000\u3000\u56e0\u4e3a\u665a\u996d\u524d\u672a\u518d\u89c1\u5230\u5305\u4e5d\u5cf0\uff0c\u9884\u6599\u4ed6\u4e00\u5b9a\u662f\u5e26\u7740\u4fe1\u7531\u79d8\u9053\u5f80\u5730\u4e0b\u89c1\u4e09\u5999\u4e66\u751f\u53bb\u4e86\u3002 \u3000\u3000\u8d1f\u8d23\u670d\u4f8d\u4e09\u4eba\u7684\uff0c\u662f\u4e00\u540d\u53eb\u6c34\u4ed9\u7684\u5c0f\u5a62\u3002 \u3000\u3000\u80e1\u67cf\u9f84\u7b49\u4e09\u4eba\u95f2\u6765\u65e0\u4e8b\uff0e\u4fbf\u5728\u697c\u4e0b\u7684\u5c0f\u5ba2\u5385\u91cc\u559d\u8336\u8c08\u5929\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u5ffd\u7136\u95ee\u6c34\u4ed9\u9053\uff1a\u201c\u5305\u603b\u7ba1\u73b0\u5728\u5df2\u7ecf\u4f1a\u8bf4\u8bdd\uff0c\u662f\u8c01\u628a\u4ed6\u6cbb\u597d\u7684\uff1f\u201d \u3000\u3000\u6c34\u4ed9\u9053\uff1a\u201c\u6211\u4eec\u4e5f\u4e0d\u6e05\u695a\uff0c\u597d\u50cf\u662f\u5728\u592b\u4eba\u6b7b\u540e\u4e0d\u4e45\u4ed6\u5c31\u80fd\u5f00\u53e3\u8bf4\u8bdd\u7684\u3002\u201d \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u5df2\u53e6\u6709\u4e00\u540d\u5c0f\u5a62\u6765\u53eb\u5f00\u996d\u3002 \u3000\u3000\u80e1\u67cf\u9f84\u4e09\u4eba\u5230\u8fbe\u65f6\uff0c\u5305\u4e5d\u5cf0\u5df2\u5148\u7b49\u5728\u90a3\u91cc\u3002 \u3000\u3000\u7b75\u5e2d\u5f88\u4e30\u76db\uff0c\u5c71\u73cd\u6d77\u5473\uff0c\u5e94\u6709\u5c3d\u6709\uff0e \u3000\u3000\u5206\u5bbe\u4e3b\u5750\u4e0b\u540e\uff0c\u80e1\u67cf\u9f84\u8fde\u5fd9\u95ee\u9053\uff1a\u201c\u5305\u524d\u8f88\u53ef\u4f1a\u89c1\u8fc7\u4e09\u5999\u524d\u8f88\u4e86\uff1f\u201d \u3000\u3000\u5305\u4e5d\u5cf0\u9053\uff1a\u201c\u89c1\u8fc7\u4e86\u3002\u4fe1\u4e5f\u9762\u5448\u4ed6\u8001\u4eba\u5bb6\u4e86\u3002\u201d \u3000\u3000\u201c\u4ed6\u53ef\u8bf4\u8fc7\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u4ed6\u5f53\u65f6\u6b63\u5728\u6253\u5750\uff0c\u5e76\u6ca1\u7acb\u5373\u770b\u4fe1\uff0c\u8001\u592b\u4e5f\u4e0d\u6562\u50ac\u4ed6\uff0c\u53c8\u4e0d\u4fbf\u4e45\u5019\uff0c\u5c31\u53ea\u597d\u56de\u6765\u4e86\uff0e\u201d \u3000\u3000\u201c\u8fd9\u6837\u4e00\u6765\uff0c\u5728\u4e0b\u5c82\u4e0d\u662f\u65e0\u6cd5\u53d6\u5f97\u56de\u4fe1\uff1f\u201d \u3000\u3000\u201c\u8001\u592b\u5df2\u7ecf\u63d0\u8d77\u8fc7\u80e1\u76df\u4e3b\u5e0c\u671b\u5f97\u5230\u56de\u4fe1\uff0c\u53ea\u597d\u660e\u5929\u518d\u53bb\u89c1\u4ed6\u4e00\u6b21\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u5ffd\u7136\u95ee\u9053\uff1a\u201c\u5305\u8001\u524d\u8f88\uff0c\u5730\u4e0b\u6d1e\u5e9c\u91cc\uff0c\u9664\u4e86\u4e09\u5999\u8001\u524d\u8f88\u5916\uff0c\u60a8\u8fd8\u770b\u5230\u522b\u7684\u4eba\u6ca1 \u6709\uff1f\u201d \u3000\u3000\u5305\u4e5d\u5cf0\u5927\u4e3a\u60ca\u8bb6\u9053\uff1a\u201c\u4e09\u5999\u524d\u8f88\u7684\u5730\u4e0b\u6d1e\u5e9c\u91cc\uff0c\u600e\u53ef\u80fd\u8fd8\u6709\u522b\u4eba\u5462\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u60a8\u8be5\u77e5\u9053\uff0c\u592b\u4eba\u5728\u5bab\u4e3b\u6b7b\u540e\uff0c\u66fe\u8fdb\u5165\u5730\u5e9c\u548c\u4e09\u5999\u524d\u8f88\u89c1\u8fc7\u9762\u3002\u201d \u3000\u3000\u5305\u4e5d\u5cf0\u9053\uff1a\u201c\u592b\u4eba\u5728\u5bab\u4e3b\u6b7b\u540e\u4fbf\u5df2\u5931\u8e2a\uff0c\u540e\u6765\u7684\u786e\u6709\u53ef\u80fd\u8fdb\u5165\u5730\u5e9c\u548c\u4e09\u5999\u524d\u8f88\u89c1\u8fc7\u9762\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u6240\u4ee5\u665a\u8f88\u624d\u6000\u7591\u592b\u4eba\u5f88\u53ef\u80fd\u4ecd\u5728\u5730\u5e9c\uff0e\u201d \u3000\u3000\u5305\u4e5d\u5cf0\u5931\u58f0\u7b11\u9053\uff1a\u201c\u5c82\u6709\u6b64\u7406\uff1f\u592b\u4eba\u660e\u660e\u5df2\u5728\u6570\u6708\u524d\u6218\u6b7b\uff0e\u600e\u4f1a\u7559\u5728\u4e09\u5999\u524d\u8f88\u7684\u5730\u5e9c \u91cc\uff1f\u201d \u3000\u3000\u201c\u592b\u4eba\u867d\u7136\u5df2\u7ecf\u6218\u6b7b\uff0c\u53ef\u662f\u5e76\u65e0\u4eba\u770b\u5230\u5979\u7684\u9057\u4f53\u3002\u201d \u3000\u3000\u201c\u5979\u4e0d\u662f\u5728\u4e34\u7ec8\u524d\u4ea4\u4ee3\u8fc7\u957f\u767d\u795e\u53df\u5e9e\u58eb\u51b2\u628a\u5979\u7684\u9057\u4f53\u5e26\u8d70\u5417\uff1f\u201d \u3000\u3000\u201c\u53ef\u662f\u5e9e\u8001\u524d\u8f88\u8fd8\u6ca1\u51fa\u5929\u53f0\u5c71\uff0c\u592b\u4eba\u7684\u9057\u4f53\u5c31\u4e0d\u89c1\u4e86\u3002\u201d \u3000\u3000\u201c\u6709\u8fd9\u79cd\u4e8b\uff1f\u8001\u592b\u4ece\u6ca1\u542c\u8bf4\u8fc7\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u6cdb\u51fa\u4e0d\u9ad8\u5174\u7684\u795e\u8272\u9053\uff1a\u201c\u5305\u8001\u524d\u8f88\uff0e\u8fd9\u4e8b\u662f\u60a8\u505a\u9519\u4e86\uff01\u201d \u3000\u3000\u5305\u4e5d\u5cf0\u6014\u4e86\u6014\u9053\uff1a\u201c\u8001\u592b\u9519\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u592b\u4eba\u8be5\u662f\u4e07\u82b1\u5bab\u7684\u4eba\uff0c\u60a8\u4e0d\u8be5\u8ba9\u5e9e\u8001\u524d\u8f88\u628a\u5979\u7684\u9057\u4f53\u5e26\u8d70\u3002\u201d \u3000\u3000\u5305\u4e5d\u5cf0\u82e6\u7b11\u9053\uff1a\u201c\u5c82\u6709\u6b64\u7406\uff01\u592b\u4eba\u548c\u5bab\u4e3b\u5e76\u672a\u6b63\u5f0f\u6210\u5a5a\uff0c\u600e\u80fd\u8bf4\u662f\u4e07\u82b1\u5bab\u7684\u4eba\uff1f\u66f4\u4f55\u51b5 \u90a3\u5929\u592b\u4eba\u548c\u5c11\u6797\u3001\u6b66\u5f53\u6fc0\u6218\u4e4b\u65f6\uff0e\u8001\u592b\u5e76\u4e0d\u5728\u73b0\u573a\uff0c\u5373\u4f7f\u60f3\u7559\u4e0b\u5979\u7684\u9057\u4f53\uff0e\u4e5f\u662f\u4e0d\u53ef\u80fd\u7684 \u4e8b\uff0e\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u987f\u4e86\u987f\u9053\uff1a\u201c\u665a\u8f88\u6000\u7591\u592b\u4eba\u4e5f\u8bb8\u8fd8\u6d3b\u5728\u4e16\u4e0a\uff0e\u201d \u3000\u3000\u5305\u4e5d\u5cf0\u8d8a\u53d1\u6115\u7136\u9053\uff1a\u201c\u4eba\u6b7b\u600e\u4f1a\u590d\u6d3b\uff0c\u54ea\u6709\u8fd9\u79cd\u53ef\u80fd\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u5979\u5f88\u53ef\u80fd\u662f\u88ab\u4e09\u5999\u8001\u524d\u8f88\u6551\u6d3b\u3002\u5b9e\u8bdd\u5bf9\u60a8\u8bf4\uff0c\u665a\u8f88\u7684\u8fd9\u4f4d\u5e08\u5144\u80e1\u76df\u4e3b\uff0c\u4e5f \u662f\u5728\u4e24\u5e74\u524d\u6b7b\u540e\u88ab\u4eba\u6551\u6d3b\u7684\u3002\u6551\u4ed6\u7684\u4eba\uff0c\u5c31\u662f\u4ed6\u73b0\u5728\u7684\u5e08\u7236\u56db\u7edd\u8001\u4eba\u3002\u8fd9\u6837\u770b\u6765\uff0c\u4e09\u5999\u4e66\u751f \u53c8\u4e3a\u4ec0\u4e48\u4e0d\u80fd\u6551\u6cbb\u592b\u4eba\uff1f\u4f55\u51b5\u592b\u4eba\u7684\u9057\u4f53\u5c31\u662f\u5728\u4e07\u82b1\u5bab\u9644\u8fd1\u5931\u8e2a\u7684\uff0e\u201d \u3000\u3000\u5305\u4e5d\u5cf0\u987f\u65f6\u7b54\u4e0d\u4e0a\u8bdd\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u518d\u9053\uff1a\u201c\u5305\u8001\u524d\u8f88\u672c\u6765\u88ab\u5bab\u4e3b\u6240\u5bb3\uff0c\u53e3\u4e0d\u80fd\u8a00\uff0c\u73b0\u5728\u5df2\u8bf4\u8bdd\u5982\u5e38\uff0c\u5982\u679c\u665a\u8f88\u6240\u6599 \u4e0d\u5dee\uff0c\u5fc5\u5b9a\u662f\u4e09\u5999\u8001\u524d\u8f88\u4e3a\u60a8\u7597\u6cbb\u6210\u529f\u7684\uff1f\u201d \u3000\u3000\u5305\u4e5d\u5cf0\u9894\u9996\u9053\uff1a\u201c\u4e0d\u9519\u3002\u8001\u592b\u73b0\u5728\u5df2\u4e0d\u518d\u54d1\uff0c\u5b8c\u5168\u662f\u62dc\u4e09\u5999\u524d\u8f88\u6240\u8d50\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u8fd9\u5c31\u5bf9\u4e86\uff0e\u4e09\u5999\u8001\u524d\u8f88\u65e2\u7136\u80fd\u6551\u60a8\uff0c\u5f53\u7136\u4e5f\u80fd\u6551\u592b\u4eba\u3002\u201d \u3000\u3000\u9152\u8fc7\u4e09\u5de1\uff0e\u80e1\u67cf\u9f84\u9053\uff1a\u201c\u5728\u4e0b\u8fd8\u60f3\u6073\u6c42\u5305\u524d\u8f88\u4e00\u4e8b\u3002\u201d \u3000\u3000\u5305\u4e5d\u5cf0\u9053\uff1a\u201c\u80e1\u76df\u4e3b\u6709\u8bdd\u8bf7\u8bb2\uff0c\u53ea\u8981\u8001\u592b\u80fd\u505a\u5f97\u5230\u7684\uff0c\u65e0\u4e0d\u5c3d\u529b\u3002\u201d \u3000\u3000\u80e1\u67cf\u9f84\u9053\uff1a\u201c\u660e\u5929\u5305\u524d\u8f88\u518d\u89c1\u4e09\u5999\u524d\u8f88\u4e4b\u65f6\uff0c\u8fd8\u8bf7\u6c42\u4ed6\u80fd\u5426\u5bb9\u5728\u4e0b\u4e00\u89c1\u3002\u201d \u3000\u3000\u5305\u4e5d\u5cf0\u7f04\u9ed8\u4e86\u2014\u9635\u9053\uff1a\u201c\u597d\u5427\uff01\u8001\u592b\u4e00\u5b9a\u5f53\u9762\u6c42\u4ed6\uff0c\u5c31\u7b97\u4ed6\u80fd\u7b54\u5e94\uff0c\u80e1\u76df\u4e3b\u4e5f\u5fc5\u987b\u540e\u5929 \u624d\u80fd\u89c1\u5230\u4ed6\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u8fd8\u8981\u591a\u7b49\u4e00\u5929\uff1f\u201d \u3000\u3000\u201c\u8001\u592b\u65e9\u5c31\u8bf4\u8fc7\uff0c\u4e09\u5999\u524d\u8f88\u53ea\u51c6\u6211\u4e00\u5929\u51fa\u5165\u5730\u5e9c\u4e00\u6b21\u3002\u201d \u3000\u3000\u80e1\u67cf\u9f84\u9053\uff1a\u201c\u53ea\u8981\u80fd\u89c1\u7740\u4e09\u5999\u8001\u524d\u8f88\uff0c\u5728\u4e0b\u5373\u4f7f\u591a\u7b49\u4e00\u5929\uff0c\u53c8\u6709\u4f55\u59a8\uff1f\u201d \u3000\u3000\u5305\u4e5d\u5cf0\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u80e1\u76df\u4e3b\u5c31\u8bf7\u660e\u5929\u7b49\u8001\u592b\u7684\u6d88\u606f\u5427\uff01\u201d \u5367\u9f99\u751f\u300a\u5999\u7edd\u5929\u9999\u300b \u7b2c\u4e8c\u5341\u4e03\u56de\u3000\u9b54\u6d1e\u64d2\u9b54 \u3000\u3000\u5f53\u665a\uff0e\u80e1\u67cf\u9f84\u7b49\u4e09\u4eba\uff0c\u5c31\u4f4f\u5728\u6c34\u69ad\u65c1\u90a3\u680b\u5c0f\u697c\u91cc\u3002 \u3000\u3000\u6b21\u65e5\u65e9\u996d\uff0c\u662f\u6c34\u4ed9\u9001\u8fc7\u6765\u7684\u3002 \u3000\u3000\u76f4\u5230\u5348\u996d\u65f6\uff0c\u624d\u53c8\u548c\u5305\u4e5d\u5cf0\u89c1\u4e86\u9762\u3002 \u3000\u3000\u5305\u4e5d\u5cf0\u5f53\u5373\u4ea4\u7ed9\u80e1\u67cf\u9f84\u4e00\u5c01\u4fe1\u9053\uff1a\u201c\u8fd9\u662f\u4e09\u5999\u524d\u8f88\u7684\u4eb2\u7b14\u56de\u4fe1\uff0c\u80e1\u76df\u4e3b\u8bf7\u597d\u597d\u6536\u7740\u3002\u201d \u3000\u3000\u80e1\u67cf\u9f84\u6025\u95ee\u9053\uff1a\u201c\u4e09\u5999\u8001\u524d\u8f88\u53ef\u63d0\u53ca\u53ec\u89c1\u5728\u4e0b\u4e4b\u4e8b\uff1f\u201d \u3000\u3000\u201c\u4ed6\u82e5\u80af\u63a5\u89c1\u80e1\u76df\u4e3b\uff0c\u4e5f\u8bb8\u5c31\u7528\u4e0d\u7740\u56de\u4fe1\u4e86\u3002\u8001\u592b\u4e3a\u8fd9\u4e8b\u66fe\u5411\u4ed6\u82e6\u82e6\u6073\u6c42\uff0c\u4f46\u4ed6\u5374\u2014\u53e3 \u56de\u7edd\u3002\u201d \u3000\u3000\u201c\u4ed6\u8001\u4eba\u5bb6\u4e3a\u4ec0\u4e48\u4e0d\u80af\uff1f\u603b\u8be5\u6709\u4e2a\u7406\u7531\uff0c\u5728\u4e0b\u8fe2\u8fe2\u6570\u5343\u91cc\u8d76\u6765\u5929\u53f0\u5c71\uff0c\u4e09\u5999\u8001\u524d\u8f88\u5c45\u7136 \u4e0d\u80af\u63a5\u89c1\uff0e\u8bf4\u8d77\u6765\u5728\u4e0b\u8fd8\u662f\u4ed6\u7684\u5e08\u4f84\uff0c\u4ed6\u8fd9\u6837\u505a\uff0c\u4e5f\u672a\u514d\u592a\u4e0d\u8fd1\u4eba\u60c5\u4e86\uff01\u201d \u3000\u3000\u80e1\u67cf\u9f84\u8bdd\u51fa\u53e3\u540e\uff0c\u624d\u89c9\u51fa\u63aa\u8bcd\u6709\u4e9b\u4e0d\u606d\u3002 \u3000\u3000\u5305\u4e5d\u5cf0\u6447\u4e86\u6447\u5934\u9053\uff1a\u201c\u602a\u53ea\u602a\u5148\u524d\u8001\u592b\u6ca1\u5bf9\u80e1\u76df\u4e3b\u8bf4\u6e05\u695a\uff0e\u5176\u5b9e\u4e09\u5999\u524d\u8f88\u4e5f\u6709\u4ed6\u7684\u82e6 \u8877\u3002\u201d \u3000\u3000\u80e1\u67cf\u9f84\u9053\uff1a\u201c\u4ed6\u8001\u4eba\u5bb6\u6709\u4ec0\u4e48\u82e6\u8877\uff1f\u201d \u3000\u3000\u5305\u4e5d\u5cf0\u9053\uff1a\u201c\u636e\u8bf4\u4e09\u5999\u524d\u8f88\u9ad8\u9f84\u5df2\u4e00\u767e\u516d\u5341\u6709\u4f59\uff0c\u65e9\u5c31\u662f\u534a\u4ed9\u4e4b\u4f53\uff0e\u56e0\u4e4b\uff0c\u4ed6\u5fc5\u987b\u5c11\u89c1 \u751f\u4eba\uff0c\u5426\u5219\u5c06\u5bf9\u4ed6\u7684\u8eab\u4f53\u6709\u5bb3\u3002\u201d \u3000\u3000\u8fd9\u51e0\u53e5\u8bdd\uff0c\u4f7f\u5f97\u80e1\u67cf\u9f84\u3001\u9ea6\u5c0f\u660e\u3001\u82d7\u7d20\u82d3\u90fd\u5403\u4e86\u4e00\u60ca\u3002 \u3000\u3000\u5c24\u5176\u80e1\u67cf\u9f84\uff0c\u53ea\u77e5\u5e05\u7236\u56db\u7edd\u8001\u4eba\u5df2\u662f\u767e\u5c81\u4e4b\u4e0a\uff0c\u5374\u4e07\u6ca1\u6599\u5230\u8fde\u4ed6\u7684\u5e08\u5f1f\u4e09\u5999\u4e66\u751f\u5e74\u5df2\u4e00 \u767e\u516d\u5341\u6709\u4f59\u3002 \u3000\u3000\u6b63\u5728\u8bb6\u7136\u95f4\uff0c\u53ea\u542c\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u5e08\u5144\u5c31\u7528\u4e0d\u7740\u575a\u6301\u975e\u89c1\u4e09\u5999\u524d\u8f88\u4e0d\u53ef\u4e86\u3002\u60a8 \u53ea\u6709\u4e09\u4e2a\u6708\u5047\u671f\uff0e\u8fd8\u662f\u5feb\u4e9b\u8fd4\u56de\u5317\u5cb3\u7684\u597d\u3002\u201d \u3000\u3000\u80e1\u67cf\u9f84\u4e5f\u5173\u5fc3\u949f\u4e00\u8c6a\u7b49\u4eba\u7684\u5b89\u5371\uff0c\u5c31\u5728\u5f53\u65e5\u4e0b\u5348\uff0e\u4fbf\u79bb\u5f00\u4e07\u82b1\u5bab\u5317\u8fd4\u3002 \u3000\u3000\u51fa\u4e86\u5929\u53f0\u5c71\uff0c\u9ea6\u5c0f\u660e\u63d0\u9192\u9053\uff1a\u201c\u4ee5\u54b1\u4eec\u7684\u811a\u7a0b\uff0c\u4e0d\u8db3\u4e00\u6708\uff0c\u4fbf\u53ef\u8fd4\u56de\u5317\u5cb3\u3002\u56de\u53bb\u540e\u5e08\u5144 \u8fd8\u6709\u2014\u6708\u5047\u671f\uff0c\u56de\u7a0b\u65f6\u54b1\u4eec\u4f55\u4e0d\u7a0d\u7a0d\u7ed5\u9053\u5230\u5d69\u5c71\u5c11\u6797\u2014\u8d9f\u3002\u201d \u3000\u3000\u80e1\u67cf\u9f84\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u8981\u5230\u5c11\u6797\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u5c11\u6797\u7684\u5929\u673a\u5927\u5e08\uff0e\u4e0a\u6b21\u5230\u8ff7\u8e2a\u8c37\u94e9\u7fbd\u800c\u5f52\uff0c\u6b64\u523b\u5c11\u6797\u53ef\u80fd\u53c8\u589e\u6d3e\u9ad8\u624b\u5230\u8ff7 \u8e2a\u8c37\u53bb\u4e86\u3002\u54b1\u4eec\u80fd\u5728\u5c11\u6797\u9884\u5148\u77e5\u9053\u4e00\u4e9b\u60c5\u51b5\uff0c\u4e0d\u662f\u5f88\u597d\u5417\uff1f\u201d \u3000\u3000\u82d7\u7d20\u82d3\u4e5f\u63a5\u7740\u9053\uff1a\u201c\u665a\u8f88\u4e5f\u60f3\u5230\u5c11\u6797\u4e00\u8d9f\u53bb\u89c1\u89c1\u5929\u7985\u5927\u5e08\u3002\u201d \u3000\u3000\u80e1\u67cf\u9f84\u5df2\u77e5\u5c11\u6797\u5f53\u4eca\u638c\u95e8\u548c\u82d7\u7d20\u82d3\u751a\u662f\u6295\u7f18\u3002 \u3000\u3000\u4fbf\u7b54\u5e94\u4e8c\u4eba\u8981\u6c42\u4e4b\u4e8b\u3002 \u3000\u3000\u534a\u6708\u4e4b\u540e\uff0c\u4e09\u4eba\u5df2\u5230\u8fbe\u8c6b\u7701\u767b\u5c01\u53bf\uff0c\u5f53\u665a\u5c31\u6295\u5bbf\u5728\u767b\u5c01\u53bf\u57ce\uff0c\u6b21\u65e5\u4e00\u65e9\uff0c\u4fbf\u5411\u5d69\u5c71\u8fdb\u53d1\u3002 \u3000\u3000\u5d69\u5c71\u5c31\u5728\u767b\u5c01\u53bf\u5883\u5185\uff0c\u53e4\u79f0\u5916\u65b9\u5c71\uff0c\u8bd7\u7ecf\u4e0a\u5373\u79f0\u8d5e\u5d69\u5c71\u4e91\uff1a\u201c\u5d69\u9ad8\u7ef4\u5cb3\uff0c\u5cfb\u6781\u4e8e\u5929\u3002\u201d \u3000\u3000\u5d69\u5c71\u5305\u62ec\u592a\u5ba4\u3001\u5c11\u5ba4\u4e24\u5c71\uff0c\u4e1c\u9762\u662f\u592a\u5ba4\u5c71\uff0c\u897f\u9762\u662f\u5c11\u5ba4\u5c71\u3002\u592a\u5ba4\u5468\u56f4\u4e00\u767e\u5eff\u91cc\uff0c\u5c11\u5ba4\u5468 \u56f4\u767e\u91cc\u3002 \u3000\u3000\u5c11\u6797\u5bfa\u4f4d\u4e8e\u5c11\u5ba4\u5c71\u9e93\uff0c\u662f\u9b4f\u6587\u5e1d\u4e8c\u5341\u5e74\uff0c\u897f\u57df\u9ad8\u50e7\u9640\u8dcb\u6240\u5efa\u3002 \u3000\u3000\u5230\u5357\u5317\u671d\u65f6\uff0c\u5929\u7afa\u9ad8\u50e7\u8fbe\u6469\u6765\u4e2d\u56fd\uff0c\u88ab\u6881\u6b66\u5e1d\u6d3e\u4eba\u8fce\u5f80\u91d1\u9675\uff0c\u8fbe\u6469\u9610\u91ca\u4f5b\u6559\u6559\u4e49\uff0c\u4e0d\u7acb \u6587\u5b57\uff0c\u4f46\u4e0d\u80fd\u4e3a\u6881\u6b66\u5e1d\u6240\u7406\u89e3\u3002\u4e8e\u662f\u8fbe\u6469\u4fbf\u6e21\u6c5f\u5317\u6765\uff08\u5373\u6709\u540d\u7684\u4e00\u82c7\u6e21\u6c5f\u6545\u4e8b\uff09\u3002\u5230\u8fbe\u5d69\u5c71 \u5c11\u6797\u5bfa\uff0e\u521b\u8bbe\u7985\u5b97\uff0c\u53c8\u9762\u58c1\u9759\u5750\u8fbe\u4e5d\u5e74\u4e4b\u4e45\uff0c\u4ece\u6b64\u5c11\u6797\u6b66\u5b66\u4fbf\u540d\u95fb\u5929\u4e0b\uff0c\u88ab\u6b66\u6797\u4e2d\u4e5d\u5927\u95e8\u6d3e \u8ba4\u4e3a\u6cf0\u5c71\u5317\u6597\u3002 \u3000\u3000\u5f53\u80e1\u67cf\u9f84\u7b49\u4e09\u4eba\u5230\u8fbe\u5c11\u6797\u5bfa\u5916\uff0c\u4fbf\u6709\u50e7\u4eba\u8fdb\u5185\u901a\u62a5\uff0c\u638c\u95e8\u5927\u5e08\u5929\u7985\u5f88\u5feb\u4fbf\u4eb2\u81ea\u8fce\u4e86\u51fa\u6765\u3002 \u3000\u3000\u4e4d\u89c1\u80e1\u67cf\u9f84\uff0c\u5929\u7985\u5927\u5e08\u4e0d\u7531\u5403\u4e86\u4e00\u60ca\u3002\u4ed6\u548c\u80e1\u67cf\u9f84\u66fe\u89c1\u8fc7\u51e0\u6b21\uff0c\u660e\u77e5\u4ed6\u4e24\u5e74\u524d\u5df2\u6218\u6b7b\u5728 \u8ff7\u8e2a\u8c37\u5916\u7684\u843d\u96c1\u8c37\uff0c\u5982\u4eca\u600e\u7684\u5374\u53c8\u6d3b\u4e86\u56de\u6765\uff1f \u3000\u3000\u4f46\u4ed6\u662f\u6709\u9053\u9ad8\u50e7\uff0c\u5b9a\u529b\u6df1\u539a\uff0c\u867d\u7136\u5185\u5fc3\u60ca\u5f02\uff0c\u5374\u4e0d\u5f62\u4e4b\u4e8e\u8272\u3002\u5929\u7985\u5927\u5e08\u628a\u80e1\u67cf\u9f84\u7b49\u4e09\u4eba \u8fce\u8fdb\u7985\u5ba4\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u548c\u5929\u7985\u5927\u5e08\u4e5f\u65e9\u5c31\u8ba4\u8bc6\uff0c\u4e0a\u6b21\u5728\u5415\u6881\u5c71\uff0e\u8fd8\u591a\u4e8f\u5929\u7985\u5927\u5e08\u51fa\u624b\u76f8\u52a9\uff0c\u624d\u51fb\u9000\u9634\u624b \u2014\u9b54\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u5929\u7985\u5927\u5e08\u5fc5\u5b9a\u8981\u95ee\u80e1\u67cf\u9f84\u4f55\u4ee5\u6b7b\u800c\u590d\u751f\uff0c\u4fbf\u4e3b\u52a8\u4ee3\u80e1\u67cf\u9f84\u628a\u5f97\u8499\u56db\u7edd\u8001\u4eba\u76f8\u6551\u7684 \u4e8b\u8bf4\u4e86\u4e00\u904d\uff0e \u3000\u3000\u5929\u7985\u5927\u5e08\u4f3c\u662f\u795e\u8272\u9887\u4e3a\u51dd\u91cd\uff0c\u5bd2\u55a7\u8fc7\u540e\u9053\uff1a\u201c\u542c\u9ea6\u5c0f\u65bd\u4e3b\u7684\u8bed\u6c14\uff0c\u5728\u6765\u5230\u5929\u53f0\u5c71\u4e4b\u524d\uff0c \u66fe\u4f4f\u5728\u5317\u5cb3\u8ff7\u8e2a\u8c37\u5916\u7684\u767d\u59d1\u5bfa\uff0c\u53ef\u66fe\u89c1\u8fc7\u8001\u8872\u7684\u5929\u673a\u5929\u89c9\u4e24\u4f4d\u5e08\u5f1f\u4ee5\u53ca\u4ed6\u4eec\u6240\u5e26\u9894\u7684\u5341\u4e8c\u540d \u5f1f\u5b50\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u5927\u611f\u4e00\u6123\u9053\uff1a\u201c\u83ab\u975e\u5929\u673a\u8001\u7985\u5e08\u8fd8\u4e0d\u66fe\u56de\u5230\u8d35\u5bfa\uff1f\u201d \u3000\u3000\u5929\u7985\u4e5f\u4e0d\u89c9\u4e00\u60ca\u9053\uff1a\u201c\u539f\u6765\u9ea6\u5c0f\u65bd\u4e3b\u5df2\u89c1\u8fc7\u4ed6\u4eec\uff0c\u53ef\u5426\u628a\u5f53\u65f6\u7ecf\u8fc7\u7684\u60c5\u5f62\u544a\u8bc9\u8001\u8872\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u968f\u5373\u628a\u81ea\u5df1\u548c\u82d7\u7d20\u82d3\u5df2\u968f\u5929\u673a\u3001\u5929\u89c9\u8fdb\u5165\u8ff7\u8e2a\u8c37\uff0e\u6700\u540e\u53ea\u5269\u4e0b\u5929\u673a\u5927\u5e08\u2014\u4eba\u9003\u51fa \u7684\u7ecf\u8fc7\uff0c\u8be6\u7ec6\u53d9\u8ff0\u4e86\u4e00\u904d\u3002 \u3000\u3000\u5929\u7985\u5927\u5e08\u7684\u795e\u8272\u8d8a\u6765\u8d8a\u6c89\u91cd\uff0c\u957f\u957f\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\uff0c\u96be\u602a\u8001\u8872\u2014\u76f4\u5f97\u4e0d\u5230\u6d88\u606f\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u53ef\u662f\u5929\u673a\u8001\u7985\u5e08\u603b\u8be5\u65e9\u5df2\u56de\u5230\u8d35\u5bfa\uff0c\u665a\u8f88\u548c\u4ed6\u8001\u4eba\u5bb6\u9003\u51fa\u8ff7\u8e2a\u8c37\u540e\uff0e\u66fe\u4e00 \u540c\u56de\u5230\u767d\u59d1\u5bfa\uff0c\u4ed6\u5f53\u65f6\u5f52\u5fc3\u4f3c\u7bad\uff0e\u4e3a\u4ec0\u4e48\u5230\u5728\u73b0\u8fd8\u672a\u56de\u6765\u5462\uff1f\u201d \u3000\u3000\u201c\u4f9d\u8001\u8872\u9884\u6599\uff0c\u5f88\u53ef\u80fd\u5728\u8def\u4e0a\u53c8\u51fa\u4e86\u4e8b\u60c5\uff01\u201d \u3000\u3000\u201c\u4ee5\u5929\u673a\u8001\u7985\u5e08\u7684\u7edd\u4e16\u6b66\u529f\uff0c\u65e2\u5df2\u79bb\u5f00\u8ff7\u8e2a\u8c37\uff0c\u53c8\u600e\u4f1a\u518d\u51fa\u4e8b\u60c5\uff1f\u201d \u3000\u3000\u5929\u7985\u5927\u5e08\u518d\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u8001\u8872\u4e5f\u4e3a\u8fd9\u4e8b\u4e0d\u89e3\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u8e59\u8d77\u4e24\u9053\u5251\u7709\u9053\uff1a\u201c\u5927\u5e08\u51c6\u5907\u600e\u4e48\u529e\u5462\uff1f\u201d \u3000\u3000\u5929\u7985\u5927\u5e08\u9053\uff1a\u201c\u5e78\u597d\u4eca\u5929\u4e09\u4f4d\u5230\u6765\uff0c\u8001\u8872\u624d\u77e5\u9053\u4e86\u5929\u673a\u3001\u5929\u89c9\u4e24\u4f4d\u5e08\u5f1f\u7387\u9886\u5341\u4e8c\u540d\u5f1f\u5b50 \u5230\u5317\u5cb3\u7684\u7ecf\u8fc7\uff0c\u4e5f\u8bc1\u5b9e\u4e86\u655d\u6d3e\u7684\u53db\u5f92\u609f\u660e\u786e\u5b9e\u662f\u5e26\u4e86\u4e5d\u540d\u94dc\u4eba\u6295\u9760\u4e86\u8ff7\u8e2a\u8c37\u7684\u970d\u5143\u4f3d\u3002\u201d \u3000\u3000\u201c\u96be\u9053\u5927\u5e08\u5c31\u7f62\u4e86\u4e0d\u6210\uff1f\u201d \u3000\u3000\u201c\u8fd9\u6837\u7684\u5927\u4e8b\uff0c\u5c82\u80fd\u7f62\u4e86\uff0c\u8001\u8872\u5f53\u7acb\u5373\u6311\u9009\u6d3e\u5bfa\u5185\u9ad8\u624b\uff0c\u9a6c\u4e0a\u518d\u8d76\u5f80\u5317\u5cb3\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u53ea\u6d3e\u51fa\u9ad8\u624b\u4e0d\u6210\uff0c\u867d\u9053\u8d35\u6d3e\u4e0a\u6b21\u6d3e\u51fa\u7684\u5929\u673a\u3001\u5929\u89c9\u4e24\u4f4d\u5927\u5e08\u8fd8\u4e0d\u662f\u9ad8\u624b\uff1f\u201d \u3000\u3000\u5929\u7985\u5927\u5e08\u7565\u4e00\u6c89\u5fd6\u9053\uff1a\u201c\u9ea6\u5c0f\u65bd\u4e3b\u6709\u4f55\u89c1\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4ece\u524d\u56fd\u5bb6\u9047\u5230\u5f3a\u654c\u5916\u4fae\uff0c\u662f\u65e9\u6709\u7687\u5e1d\u5fa1\u9a7e\u4eb2\u5f81\u4e4b\u4f8b\uff0e\u73b0\u5728\u8d35\u6d3e\u8981\u5bf9\u4ed8\u8ff7\u8e2a \u8c37\u7684\u970d\u5143\u4f3d\uff0c\u4e5f\u5fc5\u987b\u638c\u95e8\u5927\u5e08\u4eb2\u81ea\u7387\u4f17\u51fa\u9a6c\uff0c\u5426\u5219\u5f88\u53ef\u80fd\u518d\u8e48\u5929\u673a\u3001\u5929\u89c9\u4e24\u4f4d\u5927\u5e08\u7684\u8986\u8f99\uff0e\u201d \u3000\u3000\u5929\u7985\u5927\u5e08\u9894\u9996\u9053\uff1a\u201c\u9ea6\u5c0f\u65bd\u4e3b\u8bf4\u5f97\u5bf9\uff0c\u8001\u8872\u5c31\u51b3\u5b9a\u4eb2\u81ea\u53bb\u8d9f\u5317\u5cb3\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u8d81\u673a\u9053\uff1a\u201c\u4e0a\u6b21\u66fe\u89c1\u5927\u5e08\u548c\u6b66\u5f53\u638c\u95e8\u9752\u9633\u9053\u957f\u540c\u884c\uff0c\u5982\u679c\u518d\u80fd\u627e\u5230\u9752\u9633\u9053\u957f\u540c\u884c\uff0c \u90a3\u5c31\u66f4\u597d\u4e86\uff01\u201d \u3000\u3000\u5929\u7985\u5927\u5e08\u9053\uff1a\u201c\u609f\u660e\u7387\u4e5d\u540d\u94dc\u4eba\u53db\u51fa\u5c11\u6797\uff0c\u7eaf\u5c5e\u672c\u6d3e\u79c1\u4e8b\uff0c\u4e0d\u5e94\u518d\u9080\u6b66\u5f53\u63d2\u624b\uff0c\u5982\u679c\u80e1 \u76df\u4e3b\u548c\u9ea6\u5c0f\u65bd\u4e3b\u56de\u5230\u5317\u5cb3\u540e\u80af\u76f8\u52a9\u4e00\u81c2\u4e4b\u529b\uff0c\u90a3\u5012\u662f\u8001\u8872\u6240\u5e0c\u671b\u7684\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u5927\u5e08\u4f55\u5fc5\u5ba2\u6c14\uff1f\u6d88\u706d\u970d\u5143\u4f3d\uff0c\u4e5f\u53ef\u8bf4\u662f\u6211\u4eec\u7684\u4e8b\u3002\u4e0e\u5176\u8bf4\u662f\u6211\u4eec\u5e2e\u8d35\u6d3e\u7684 \u5fd9\uff0c\u4e0d\u5982\u8bf4\u662f\u8d35\u6d3e\u5e2e\u6211\u4eec\u7684\u5fd9\u3002\u201d \u3000\u3000\u5929\u7985\u5927\u5e08\u9053\uff1a\u201c\u90a3\u5c31\u592a\u597d\u4e86\uff0c\u8001\u8872\u5927\u7ea6\u6570\u65e5\u540e\u4fbf\u53ef\u7387\u9886\u5f1f\u5b50\u51fa\u53d1\uff0c\u53ea\u662f\u5230\u8fbe\u5317\u5cb3\u540e\uff0c\u6700 \u597d\u53cc\u65b9\u80fd\u7ea6\u4e00\u4e2a\u4f1a\u5408\u5730\u70b9\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u60f3\u4e86\u60f3\u9053\uff1a\u201c\u5c31\u5728\u767d\u59d1\u5bfa\u5982\u4f55\uff1f\u201d \u3000\u3000\u5929\u7985\u5927\u5e08\u9053\uff1a\u201c\u4e5f\u597d\uff0c\u4e00\u8a00\u4e3a\u5b9a\uff01\u201d \u3000\u3000\u5f53\u65e5\u4e2d\u5348\uff0c\u5929\u7985\u5927\u5e08\u8bbe\u5e2d\u62db\u5f85\u80e1\u67cf\u9f84\u7b49\u4e09\u4eba\u3002\u5348\u540e\uff0e\u5e76\u4eb2\u81ea\u628a\u4e09\u4eba\u9001\u4e0b\u5c71\u6765\u3002 \u3000\u3000\u548c\u5929\u7985\u5927\u5e08\u5206\u624b\u540e\uff0c\u9ea6\u5c0f\u660e\u4fbf\u8feb\u4e0d\u53ca\u5f85\u7684\u9053\uff1a\u201c\u771f\u662f\u60f3\u4e0d\u5230\uff0e\u5929\u673a\u8001\u7985\u5e08\u7adf\u7136\u6ca1\u56de\u6765\uff01\u201d \u3000\u3000\u80e1\u5e15\u9f84\u9053\uff1a\u201c\u770b\u6765\u2014\u5b9a\u662f\u51fa\u4e86\u610f\u5916\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u5927\u4e3a\u4e0d\u5b89\u7684\u9053\uff1a\u201c\u5982\u679c\u5929\u673a\u8001\u7985\u5e08\u51fa\u4e86\u610f\u5916\uff0e\u5fc5\u5b9a\u4ecd\u662f\u970d\u5143\u4f3d\u6240\u4e3a\uff0c\u5c0f\u5f1f\u626d\u5fc3\u949f \u526f\u76df\u4e3b\u4ed6\u4eec\u4e5f\u53ef\u80fd\u6709\u610f\u5916\u3002\u201d \u3000\u3000\u80e1\u67cf\u9f84\u9053\uff1a\u201c\u6211\u4e5f\u6709\u540c\u611f\uff0e\u636e\u949f\u4e00\u8c6a\u548c\u4f59\u4ea6\u4e50\u5bf9\u6211\u8bf4\uff0c\u4f60\u548c\u82d7\u59d1\u5a18\u662f\u4ed6\u4eec\u4e4b\u95f4\u7684\u4e24\u540d\u4e3b \u5c06\uff0c\u8fde\u4f60\u4eec\u548c\u4ed6\u4eec\u5728\u4e00\u8d77\u65f6\u90fd\u88ab\u970d\u5143\u4f3d\u8d76\u5f97\u4e1c\u5954\u897f\u8dd1\uff0e\u5982\u4eca\u4ed6\u4eec\u5c11\u4e86\u4f60\u4eec\uff0c\u60c5\u51b5\u5f53\u7136\u5c31\u66f4\u4e3a \u5371\u9669\u4e86\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u8d8a\u53d1\u6c89\u4e0d\u4f4f\u6c14\u9053\uff1a\u201c\u90a3\u54b1\u4eec\u5c31\u8be5\u52a0\u7d27\u811a\u7a0b\uff0c\u8d8a\u5feb\u8d76\u56de\u53bb\u8d8a\u597d\u3002\u201d \u3000\u3000\u80e1\u67cf\u9f84\u9053\uff1a\u201c\u5982\u679c\u4ed6\u4eec\u771f\u51fa\u4e86\u610f\u5916\uff0c\u54b1\u4eec\u56de\u53bb\u5f97\u518d\u65e9\uff0c\u4e5f\u6765\u4e0d\u53ca\u4e86\uff0e\u4e3a\u4eca\u4e4b\u8ba1\uff0c\u5fc5\u987b\u56de \u53bb\u7b49\u5019\u548c\u5c11\u6797\u65b9\u9762\u4f1a\u5408\uff0c\u624d\u80fd\u6b63\u5f0f\u5bf9\u8ff7\u8e2a\u8c37\u91c7\u53d6\u884c\u52a8\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u4e0d\u89c9\u5927\u559c\u9053\uff1b\u201c\u83ab\u975e\u5e08\u5144\u4e5f\u8981\u53c2\u52a0\u884c\u52a8\uff1f\u201d \u3000\u3000\u80e1\u67cf\u9f84\u9053\uff1a\u201c\u6211\u6709\u5e08\u547d\u5728\u8eab\uff0c\u89c4\u5b9a\u6211\u4e0d\u5f97\u8fdb\u5165\u8ff7\u8e2a\u8c37\u4e00\u6b65\uff0c\u5982\u679c\u53cc\u65b9\u662f\u5728\u8c37\u5916\u6709\u884c\u52a8\uff0c \u6211\u4e5f\u8bb8\u53ef\u4ee5\u548c\u970d\u5143\u4f3d\u4f1a\u4f1a\uff0e\u867d\u7136\u5982\u6b64\uff0c\u6211\u4ecd\u62c5\u5fc3\u5c06\u6765\u4f1a\u53d7\u5230\u5e08\u7236\u7684\u8d23\u7f5a\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u6c89\u541f\u4e86\u534a\u664c\u9053\uff1a\u201c\u5e08\u5144\uff0c\u5c0f\u5f1f\u60f3\u8d77\u4e00\u4ef6\u4e8b\uff0c\u53c8\u8be5\u548c\u60a8\u5546\u8bae\u5546\u8bae\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u4e8b\uff1f\u4f60\u8bf4\u3002\u201d \u3000\u3000\u201c\u970d\u5143\u4f3d\u76ee\u524d\u5bf9\u5916\u6700\u5927\u7684\u6cd5\u5b9d\uff0c\u4fbf\u662f\u5c0f\u5f1f\u7684\u90a3\u56db\u4f4d\u5e08\u5144\uff0e\u4ed6\u4eec\u670d\u4e86\u2018\u5411\u5fc3\u9732\u2019\uff0e\u4e00\u5207\u542c \u547d\u4e8e\u970d\u5143\u4f3d\u9a71\u4f7f\uff0e\u4e0d\u4f46\u6b66\u529f\u4e0d\u51cf\uff0c\u4e34\u9635\u53cd\u800c\u8d8a\u53d1\u52c7\u5f80\u76f4\u524d\u3002\u5c0f\u5f1f\u548c\u949f\u526f\u76df\u4e3b\u7b49\u4eba\uff0c\u51e0\u4e4e\u6bcf\u6b21 \u90fd\u5403\u4e86\u4ed6\u4eec\u56db\u4eba\u7684\u4e8f\u3002\u201d \u3000\u3000\u201c\u4f60\u7684\u610f\u601d\uff0c\u76ee\u524d\u6700\u91cd\u8981\u7684\u4e00\u4ef6\u4e8b\uff0c\u4fbf\u662f\u80fd\u627e\u5230\u2018\u5411\u5fc3\u9732\u2019\u7684\u89e3\u836f\uff0c\u662f\u5417\uff1f\u201d \u3000\u3000\u201c\u5c0f\u5f1f\u6b63\u662f\u8fd9\u610f\u601d\u3002\u201d \u3000\u3000\u201c\u4f60\u53ef\u6709\u4ec0\u4e48\u6253\u7b97\uff1f\u201d \u3000\u3000\u201c\u2018\u5411\u5fc3\u9732\u2019\u662f\u9634\u624b\u2014\u9b54\u7684\u72ec\u95e8\u836f\u7269\uff0c\u5c0f\u5f1f\u4e0a\u6b21\u5230\u5415\u6881\u5c71\uff0c\u76ee\u7684\u5c31\u662f\u5411\u9634\u624b\u4e00\u9b54\u6c42\u53d6\u89e3 \u836f\uff0c\u4e0d\u60f3\u9669\u4e9b\u9001\u4e86\u4e00\u547d\u3002 \u3000\u3000\u8fd8\u591a\u4e8f\u82d7\u59d1\u5a18\u76f8\u52a9\u8131\u9003\uff0c\u6240\u4ee5\u6211\u60f3\u56de\u7a0b\u65f6\u518d\u5230\u5415\u6881\u5c71\u4e5d\u5929\u7384\u9634\u6d1e\u5e9c\u4e00\u8d9f\uff0c\u53ea\u8981\u80fd\u8ba8\u5230\u89e3 \u836f\u8ba9\u5c0f\u5f1f\u7684\u56db\u4f4d\u5e08\u5144\u6e05\u9192\uff0c\u970d\u5143\u4f3d\u7684\u529b\u91cf\u5c31\u7b49\u4e8e\u74e6\u89e3\u4e86\u4e00\u534a\u3002\u201d \u3000\u3000\u201c\u4f60\u6709\u628a\u63e1\u80fd\u8ba8\u5230\u89e3\u836f\uff1f\u201d \u3000\u3000\u201c\u4e0a\u6b21\u5c0f\u5f1f\u662f\u5355\u4eba\u72ec\u9a6c\uff0c\u8fd9\u6b21\u6709\u4e86\u5e08\u5144\uff0c\u54b1\u4eec\u6765\u4e2a\u5148\u793c\u540e\u5175\uff0e\u4e0d\u6015\u4ed6\u4e0d\u4ea4\u51fa\u89e3\u836f\u3002\u201d \u3000\u3000\u80e1\u67cf\u9f84\u8f6c\u95ee\u82d7\u7d20\u82d3\u9053\uff1a\u201c\u82d7\u59d1\u5a18\u539f\u662f\u9634\u624b\u4e00\u9b54\u7684\u4eba\uff0c\u53ef\u77e5\u9053\u4ed6\u7684\u89e3\u836f\u85cf\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u82d7\u7d20\u82d3\u9053\uff1a\u201c\u5bb6\u5e08\u6709\u5904\u836f\u5ba4\uff0c\u91cc\u9762\u51e0\u4e4e\u4ec0\u4e48\u836f\u7269\u90fd\u6709\uff0c\u53ea\u8981\u80fd\u6253\u5f00\u836f\u5ba4\uff0c\u665a\u8f88\u5c31\u6709\u529e\u6cd5 \u627e\u5230\u2018\u5411\u5fc3\u9732\u2019\u89e3\u836f\u3002\u201d \u3000\u3000\u80e1\u67cf\u9f84\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u54b1\u4eec\u53bb\u4e00\u8d9f\u5415\u6881\u5c71\u4e5f\u597d\u3002\u201d \u3000\u3000\u8c01\u77e5\u82d7\u7d20\u82d3\u5374\u5413\u5f97\u8138\u8272\u9aa4\u53d8\u9053\uff1a\u201c\u53ef\u662f\u665a\u8f88\u600e\u6562\u548c\u4ed6\u89c1\u9762\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u7b11\u9053\uff1a\u201c\u6709\u80e1\u5e08\u5144\u548c\u6211\uff0c\u8fd8\u6015\u4ec0\u4e48\uff1f\u8c05\u4ed6\u4e5f\u4e0d\u6562\u52a8\u4f60\u4e00\u6839\u6c57\u6bdb\u3002\u201d \u3000\u3000\u82d7\u7d20\u82d3\u5fc3\u6709\u4f59\u60b8\u9053\uff1a\u201c\u53ef\u662f\u8981\u5bf9\u4ed8\u4ed6\uff0c\u8fdb\u53bb\u4e4b\u540e\uff0c\u5f88\u5bb9\u6613\u843d\u5165\u4ed6\u7684\u5708\u5957\uff0c\u9664\u975e\u80fd\u628a\u4ed6\u5f15 \u51fa\u6d1e\u5916\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u60f3\u4e86\u60f3\u9053\uff1a\u201c\u6211\u6709\u529e\u6cd5\u628a\u4ed6\u5f15\u51fa\u6765\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u529e\u6cd5\uff1f\u201d \u3000\u3000\u201c\u4e0d\u5fc5\u73b0\u5728\u8bb2\uff0c\u5230\u65f6\u5019\u518d\u8bf4\u5427\uff01\u201d \u3000\u3000\u80e1\u67cf\u9f84\u9053\uff1a\u201c\u5c31\u8fd9\u4e48\u51b3\u5b9a\uff0c\u6211\u966a\u4f60\u4eec\u5230\u5415\u6881\u5c71\u4e00\u8d9f\u3002\u201d \u3000\u3000\u5415\u7cb1\u5c71\u4f4d\u4e8e\u5c71\u897f\u4e2d\u90e8\u504f\u56db\uff0c\u7eb5\u8d70\u4e8e\u9ec4\u3001\u6c7e\u4e24\u6cb3\u4e4b\u95f4\uff0c\u7531\u4e8e\u6709\u82d7\u7d20\u82d3\u5e26\u8def\uff0c\u5165\u5c71\u4e4b\u540e\uff0c\u5f88 \u5feb\u4fbf\u627e\u5230\u9634\u624b\u4e00\u9b54\u6240\u5c45\u7684\u4e5d\u5929\u7384\u9634\u6d1e\u5e9c\u3002 \u3000\u3000\u8fd9\u5904\u6d1e\u5e9c\uff0c\u5916\u8868\u770b\u6765\uff0c\u53ea\u662f\u5ce1\u8c37\u6df1\u5904\u7684\u2014\u4e2a\u5c71\u6d1e\uff0c\u4f46\u4e24\u65c1\u90fd\u5947\u5ca9\u63d2\u5929\uff0c\u5f62\u52bf\u5341\u5206\u9669\u6076\uff0c \u4e00\u822c\u4eba\u6839\u672c\u65e0\u6cd5\u5230\u8fbe\u3002 \u3000\u3000\u5373\u4f7f\u5c45\u4f4f\u5728\u9644\u8fd1\u5c71\u4e0b\u7684\u4eba\uff0c\u4e5f\u7edd\u5927\u591a\u6570\u4e0d\u77e5\u4e5d\u5929\u7384\u9634\u6d1e\u5e9c\u6240\u5728\uff0c\u5e73\u65f6\u66f4\u65e0\u4eba\u6562\u5230\u9644\u8fd1\u5f98 \u5f8a\uff0c\u56e0\u4e4b\uff0c\u4e5f\u5c31\u8d8a\u53d1\u517b\u6210\u9634\u624b\u4e00\u9b54\u7684\u5984\u81ea\u5c0a\u5927\u3002 \u3000\u3000\u6765\u5230\u6d1e\u53e3\uff0c\u5e76\u65e0\u4e00\u4eba\uff0c\u7531\u6d1e\u53e3\u5411\u91cc\u671b\u53bb\u4e00\u7247\u6f06\u9ed1\uff0c\u6df1\u4e0d\u89c1\u5e95\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4e0a\u6b21\u5c0f\u5f1f\u8d70\u8fc7\u8fd9\u5904\u5165\u53e3\u7684\u786e\u5f88\u6df1\uff0c\u5e08\u5144\uff0c\u8981\u4e0d\u8981\u73b0\u5728\u5c31\u76f4\u63a5\u8fdb\u53bb\uff1f\u201d \u3000\u3000\u80e1\u67cf\u9f84\u9053\uff1a\u201c\u54b1\u4eec\u65e2\u5df2\u51b3\u5b9a\u5148\u793c\u540e\u5175\uff0c\u5c31\u4e0d\u5e94\u76f4\u5f80\u91cc\u95ef\uff0c\u66f4\u4f55\u51b5\u91cc\u9762\u8fd8\u6709\u673a\u5173\u57cb\u4f0f\uff1f\u201d \u3000\u3000\u82d7\u7d20\u82d3\u9053\uff1a\u201c\u9ea6\u5c0f\u4fa0\u4e0d\u662f\u6709\u529e\u6cd5\u628a\u4ed6\u5f15\u51fa\u6765\u5417\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u968f\u5373\u8d70\u8fd1\u6d1e\u53e3\uff0e\u63d0\u9ad8\u55d3\u95e8\u5411\u91cc\u53eb\u9053\uff1a\u201c\u91cc\u9762\u6709\u4eba\u6ca1\u6709\uff1f\u5ba2\u4eba\u6765\u5566\uff01\u201d \u3000\u3000\u4ed6\u8fd9\u58f0\u97f3\u672c\u6765\u5c31\u5927\uff0c\u518d\u52a0\u6d1e\u5185\u4f20\u97f3\u529b\u5f3a\uff0c\u8fde\u7ad9\u5728\u8fdc\u5904\u7684\u80e1\u67cf\u9f84\u548c\u82d7\u7d20\u82d3\u90fd\u88ab\u56de\u97f3\u9707\u5f97\u8033 \u9f13\u4f5c\u54cd\uff0c\u4f20\u5165\u6d1e\u5185\u7684\u58f0\u97f3\uff0c\u4e5f\u5c31\u53ef\u60f3\u800c\u77e5\u3002 \u3000\u3000\u4e0d\u5927\u4e00\u4f1a\u5de5\u592b\uff0c\u679c\u7136\u6709\u4e00\u540d\u7eff\u8863\u5c0f\u5a62\u5954\u4e86\u51fa\u6765\u3002 \u3000\u3000\u82d7\u7d20\u82d3\u81ea\u7136\u8ba4\u8bc6\u8fd9\u540d\u7eff\u8863\u5c0f\u5a62\uff0c\u8fde\u5fd9\u53eb\u9053\uff1a\u201c\u7eff\u4e91\uff0c\u662f\u6211\uff01\u201d \u3000\u3000\u90a3\u5c0f\u5a62\u6115\u7136\u9053\uff1a\u201c\u539f\u6765\u662f\u82d3\u59d1\u5a18\uff0c\u4f60\u600e\u4e48\u6765\u4e86\uff01\u201d \u3000\u3000\u82d7\u7d20\u82d3\u9053\uff1a\u201c\u6211\u662f\u56de\u6765\u63a2\u671b\u5e08\u7236\u7684\u3002\u201d \u3000\u3000\u7eff\u4e91\u5403\u60ca\u9053\uff1a\u201c\u82d3\u59d1\u5a18\u597d\u5927\u80c6\uff0c\u6d1e\u4e3b\u6349\u4f60\u90fd\u6349\u4e0d\u5230\u624b\uff0c\u4f60\u5c45\u7136\u56de\u6765\u81ea\u6295\u7f57\u7f51\uff01\u201d \u3000\u3000\u82d7\u7d20\u82d3\u9053\uff1a\u201c\u4f60\u522b\u7ba1\u90a3\u4e48\u591a\uff0c\u5feb\u5feb\u8fdb\u5185\u901a\u62a5\u3002\u201d \u3000\u3000\u201c\u90a3\u4e00\u4f4d\u662f\u4e0d\u662f\u9ea6\u516c\u5b50\uff1f\u201d \u3000\u3000\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u201c\u8fd8\u6709\u4e00\u4f4d\u5462\uff1f\u201d \u3000\u3000\u201c\u4e0d\u5fc5\u95ee\u90a3\u4e48\u591a\uff0c\u5e08\u7236\u4ed6\u8001\u4eba\u5bb6\u89c1\u4e86\u9762\u4e00\u5b9a\u8ba4\u8bc6\u3002\u201d \u3000\u3000\u7eff\u4e91\u7565\u2014\u72b9\u8c6b\uff0c\u4fbf\u8fd4\u8eab\u5306\u5306\u8fdb\u6d1e\u800c\u53bb\u3002 \u3000\u3000\u82d7\u7d20\u82d3\u4ece\u6000\u4e2d\u638f\u51fa\u53ea\u7eff\u8272\u7389\u74f6\uff0c\u5012\u51fa\u4e09\u7c92\u836f\u4e38\uff0c\u5206\u9001\u80e1\u67cf\u9f84\u3001\u9ea6\u5c0f\u660e\u5404\u81ea\u4e00\u7c92\u9053\uff1a\u201c\u80e1 \u5927\u4fa0\u548c\u9ea6\u5c0f\u4fa0\u8bf7\u5373\u670d\u4e0b\u836f\u4e38\uff01\u201d \u3000\u3000\u80e1\u67cf\u9f84\u9053\uff1a\u201c\u8fd9\u662f\u505a\u4ec0\u4e48\u7528\u7684\uff1f\u201d \u3000\u3000\u82d7\u7d20\u82d3\u9053\uff1a\u201c\u8fd9\u662f\u89e3\u6bd2\u7075\u836f\uff0c\u9884\u5148\u670d\u4e0b\uff0e\u53ef\u4f7f\u767e\u6bd2\u4e0d\u4fb5\uff0c\u514d\u5f97\u5f85\u4f1a\u906d\u4e86\u5bb6\u5e08\u7684\u6697\u7b97\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u4e5f\u9053\uff1a\u201c\u5e08\u5144\u53ea\u7ba1\u670d\u4e0b\uff0c\u82d7\u59d1\u5a18\u8fd9\u836f\u7075\u5f97\u5f88\uff0c\u5d06\u5cd2\u4e94\u9b54\u7684\u8001\u5927\u9634\u5929\u7433\u6700\u64c5\u65bd\u6bd2\u3002 \u4e0a\u6b21\u5929\u673a\u3001\u5929\u89c9\u4e24\u4f4d\u5927\u5e08\u5728\u8ff7\u8e2a\u8c37\u51e0\u4e4e\u8d25\u5f97\u5168\u519b\u8986\u6ca1\uff0c\u4e3b\u8981\u4fbf\u662f\u4e2d\u4e86\u2018\u8840\u96fe\u6563\u2019\u7684\u6bd2\uff0c\u82d7\u59d1 \u5a18\u548c\u6211\u80fd\u5168\u8eab\u800c\u9000\uff0c\u5168\u662f\u6cbe\u4e86\u8fd9\u836f\u513f\u7684\u5149\uff0e\u201d \u3000\u3000\u80e1\u67cf\u9f84\u968f\u5373\u4e00\u53e3\u670d\u4e0b\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u548c\u82d7\u7d20\u82d3\u4e5f\u8ddf\u7740\u670d\u4e0b\u3002 \u3000\u3000\u5927\u7ea6\u534a\u76cf\u8336\u5de5\u592b\u4e4b\u540e\uff0c\u7eff\u4e91\u91cd\u53c8\u5728\u6d1e\u53e3\u51fa\u73b0\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u62a2\u7740\u95ee\u9053\uff1a\u201c\u4f60\u4eec\u6d1e\u4e3b\u662f\u5426\u8bf7\u6211\u4eec\u8fdb\u53bb\uff1f\u201d \u3000\u3000\u5c82\u77e5\u7eff\u4e91\u6beb\u65e0\u8868\u60c5\u7684\u9053\uff1a\u201c\u771f\u62b1\u6b49\uff01\u6d1e\u4e3b\u4e0d\u5728\u6d1e\u5e9c\uff0c\u6709\u52b3\u8bf8\u4f4d\u7a7a\u8dd1\u2014\u8d9f\u4e86\uff01\u201d \u3000\u3000\u201c\u4f60\u4e0d\u662f\u9a97\u6211\u4eec\u5427\uff1f\u201d \u3000\u3000\u201c\u4e09\u4f4d\u662f\u8d35\u5ba2\uff0c\u6211\u4eec\u600e\u6562\u968f\u4fbf\u6b3a\u9a97\uff01\u201d \u3000\u3000\u201c\u4ed6\u662f\u4ec0\u4e48\u65f6\u5019\u51fa\u6d1e\u7684\uff1f\u201d \u3000\u3000\u201c\u9ea6\u516c\u5b50\u5e94\u8be5\u8bf4\u6211\u4eec\u6d1e\u4e3b\u4ec0\u4e48\u65f6\u5019\u79bb\u5f00\u6d1e\u5e9c\u624d\u5bf9\uff01\u201d \u3000\u3000\u201c\u5978\u5427\uff01\u5c31\u7167\u4f60\u8fd9\u6837\u8bb2\uff0c\u4ed6\u662f\u4ec0\u4e48\u65f6\u5019\u79bb\u5f00\u6d1e\u5e9c\u7684\uff1f\u201d \u3000\u3000\u201c\u8be6\u7ec6\u65f6\u95f4\u6211\u4e5f\u4e0d\u6e05\u695a\uff0c\u53ef\u80fd\u662f\u6628\u665a\uff0c\u4e5f\u53ef\u80fd\u662f\u4eca\u5929\u4e00\u65e9\uff0c\u56e0\u4e3a\u6628\u5929\u8fd8\u89c1\u8fc7\u4ed6\u8001\u4eba\u5bb6\u3002\u201d \u3000\u3000\u7eff\u4e91\u8bf4\u5b8c\u8bdd\uff0c\u5f84\u81ea\u56de\u6d1e\u800c\u53bb\u3002 \u3000\u3000\u8fd9\u4e00\u6765\uff0c\u7b80\u76f4\u628a\u9ea6\u5c0f\u660e\u5f04\u50bb\u4e86\u773c\u3002 \u3000\u3000\u82d7\u7d20\u82d3\u671b\u4e86\u9ea6\u5c0f\u660e\u4e00\u773c\u9053\uff1a\u201c\u8fd9\u56de\u4f60\u53ef\u6ca1\u5492\u5ff5\u4e86\u5427\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u7d27\u76b1\u7709\u5934\u9053\uff1a\u201c\u4f60\u8ba4\u4e3a\u9634\u624b\u4e00\u9b54\u662f\u5426\u771f\u7684\u4e0d\u5728\u6d1e\u91cc\uff1f\u201d \u3000\u3000\u82d7\u7d20\u82d3\u6c89\u541f\u4e86\u534a\u664c\u9053\uff1a\u201c\u5f88\u96be\u8bb2\u3002\u4e0d\u8fc7\u6211\u60f3\u4ed6\u5341\u6709\u516b\u4e5d\u4ecd\u5728\u6d1e\u91cc\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u5982\u679c\u662f\u8fd9\u6837\uff0c\u90a3\u53cd\u800c\u4ee4\u4eba\u4e0d\u89e3\u4e86\u3002\u201d \u3000\u3000\u201c\u4f60\u60f3\u5230\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u4f60\u6211\u4e8c\u4eba\uff0c\u90fd\u662f\u4ed6\u6700\u6068\u7684\uff0c\u5982\u4eca\u54b1\u4eec\u81ea\u52a8\u9001\u4e0a\u95e8\u6765\uff0c\u4ed6\u6b63\u5e94\u8be5\u5229\u7528\u6d1e\u5185\u673a\u5173\u57cb\u4f0f\uff0c\u628a \u6211\u4eec\u902e\u4f4f\uff0c\u4e3a\u4ec0\u4e48\u53cd\u800c\u4e0d\u5e0c\u671b\u6211\u4eec\u5165\u6d1e\u5462\uff1f\u201d \u3000\u3000\u201c\u4ed6\u7684\u7591\u5fc3\u6700\u5927\uff0c\u4e5f\u8bb8\u4ed6\u6000\u7591\u54b1\u4eec\u4e5f\u6709\u57cb\u4f0f\uff0c\u6240\u4ee5\u624d\u4e0d\u6562\u8f7b\u6613\u653e\u54b1\u4eec\u8fdb\u53bb\u3002\u53e6\u5916\uff0c\u4ed6\u77e5 \u9053\u6d1e\u5185\u7684\u673a\u5173\u57cb\u4f0f\u6211\u90fd\u6e05\u695a\uff0c\u66f4\u4e0d\u80fd\u4e0d\u6709\u6240\u987e\u5fcc\u3002\u201d \u3000\u3000\u201c\u96be\u9053\u4ed6\u5c31\u8fd9\u6837\u7f62\u4e86\u4e0d\u6210\uff1f\u201d \u3000\u3000\u201c\u5982\u679c\u6211\u6240\u6599\u4e0d\u5dee\uff0c\u4ed6\u73b0\u5728\u4e00\u5b9a\u4f1a\u6d3e\u51fa\u773c\u7ebf\uff0c\u5728\u6697\u4e2d\u76d1\u89c6\u6211\u4eec\u3002\u201d \u3000\u3000\u201d\u4ed6\u6d3e\u51fa\u7684\u773c\u7ebf\u53c8\u4ece\u54ea\u91cc\u51fa\u6d1e\u5462\uff1f\u201d \u3000\u3000\u201c\u636e\u6211\u6240\u77e5\uff0c\u53e6\u5916\u8fd8\u6709\u51fa\u53e3\uff0c\u53ea\u662f\u5f88\u9690\u79d8\u7f62\u4e86\uff0e\u8bf4\u4e0d\u5b9a\u73b0\u5728\u5c71\u8c37\u4e0a\u65b9\u6216\u8005\u9644\u8fd1\u5ced\u58c1\u5c31\u5df2 \u7ecf\u6709\u4eba\u5728\u5bf9\u54b1\u4eec\u6697\u4e2d\u76d1\u89c6\u4e86\uff0e\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u4e0d\u89c9\u5fc3\u5934\u609a\u7136\u4e00\u9707\uff0c\u62ac\u5934\u7559\u610f\u5411\u9644\u8fd1\u671b\u53bb\uff0c\u5374\u770b\u4e0d\u51fa\u4ec0\u4e48\u53ef\u7591\u3002 \u3000\u3000\u82d7\u7d20\u82d3\u9053\uff1a\u201c\u8fd9\u9644\u8fd1\u5ced\u58c1\u4e0a\u6742\u6811\u4e1b\u751f\uff0c\u5230\u5904\u90fd\u662f\u7a81\u51fa\u7684\u5ca9\u77f3\uff0c\u4efb\u4f55\u5730\u65b9\u90fd\u53ef\u85cf\u4f4f\u4e00\u4e8c\u4e2a \u4eba\uff0c\u6839\u672c\u4e0d\u6613\u53d1\u73b0\u4ed6\u4eec\u7684\u8e2a\u8ff9\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1b\u201c\u4e0d\u7ba1\u5982\u4f55\uff0c\u8fd9\u6d1e\u53e3\u662f\u4ed6\u4eec\u8fdb\u51fa\u7684\u6b63\u95e8\uff0c\u6211\u4e00\u5b9a\u8981\u903c\u4ed6\u4eec\u73b0\u8eab\u4e0d\u53ef\u3002\u201d \u3000\u3000\u82d7\u7d20\u82d3\u6447\u4e86\u6447\u5934\u9053\uff1a\u201c\u4f60\u6709\u4ec0\u4e48\u529e\u6cd5\u5462\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9887\u6709\u628a\u63e1\u7684\u9053\uff1a\u201c\u4f60\u770b\u6211\u7684\uff0c\u4e0d\u8fc7\u8fd8\u8981\u8bf7\u4f60\u5e2e\u5e2e\u5fd9\uff01\u201d \u3000\u3000\u4ed6\u8bf4\u7740\uff0c\u7acb\u5373\u5728\u9644\u8fd1\u627e\u5bfb\u5e72\u679d\u67af\u53f6\uff0c\u7136\u540e\u4e00\u5927\u62b1\u2014\u5927\u62b1\u7684\u5f80\u6d1e\u53e3\u642c\u8fd0\u3002 \u3000\u3000\u82d7\u7d20\u82d3\u660e\u767d\u4ed6\u7684\u7528\u610f\uff0c\u81ea\u7136\u4e5f\u5e2e\u8d77\u5fd9\u6765\u3002 \u3000\u3000\u4e0d\u5927\u4e00\u4f1a\u513f\uff0c\u90a3\u6d1e\u53e3\u4fbf\u51e0\u4e4e\u88ab\u5e72\u679d\u67af\u8349\u585e\u6ee1\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u53d6\u51fa\u706b\u71a0\uff0c\u968f\u5373\u70b9\u71c3\u8d77\u6765\u3002 \u3000\u3000\u6b63\u597d\u6d1e\u5916\u6709\u98ce\uff0c\u800c\u4e14\u6b63\u662f\u5439\u5411\u6d1e\u53e3\u5185\u7684\uff0c\u5f88\u5feb\u4fbf\u567c\u567c\u556a\u556a\u5f62\u6210\u4e00\u56e2\u718a\u718a\u70c8\u706b\uff0c\u867d\u7136\u706b\u70e7 \u4e0d\u5230\u6d1e\u5185\u53bb\uff0c\u4f46\u90a3\u6d53\u70df\u88ab\u5439\u8fdb\u6d1e\u5185\uff0c\u6d1e\u5185\u7684\u60c5\u5f62\u81ea\u662f\u4e0d\u96be\u60f3\u89c1\uff0c\u8bf4\u4e0d\u5b9a\u6709\u4eba\u4f1a\u88ab\u6d53\u70df\u545b\u6b7b\u3002 \u3000\u3000\u4e5d\u5929\u7384\u9634\u6d1e\u5e9c\u5c45\u7136\u4f1a\u906d\u5230\u706b\u653b\uff0c\u53ea\u6015\u662f\u9634\u624b\u4e00\u9b54\u518d\u4e5f\u60f3\u4e0d\u5230\u7684\u4e8b\u3002 \u3000\u3000\u8fd9\u80a1\u6d53\u70df\u70c8\u706b\uff0c\u76f4\u70e7\u4e86\u987f\u996d\u5de5\u592b\u624d\u6e10\u6e10\u7184\u706d\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u5fc3\u91cc\u6709\u6570\uff0e\u6b64\u523b\u8fd8\u4e0d\u53ef\u80fd\u6709\u4eba\u51fa\u6765\u3002 \u3000\u3000\u82d7\u7d20\u82d3\u9053\uff1a\u201c\u5982\u679c\u91cc\u9762\u8fd8\u662f\u6ca1\u4eba\u51fa\u6765\uff0c\u4f60\u6253\u7b97\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4ed6\u4eec\u53ef\u80fd\u5df2\u7ecf\u88ab\u6d53\u70df\u545b\u660f\uff0c\u82e5\u4ecd\u6ca1\u4eba\u51fa\u6765\uff0c\u54b1\u4eec\u5c3d\u53ef\u653e\u5927\u80c6\u8fdb\u53bb\uff0c\u8fdb\u5165\u836f \u5ba4\u627e\u5230\u89e3\u836f\u5c31\u51fa\u6765\uff0c\u7528\u4e0d\u7740\u518d\u89c1\u9634\u624b\u2014\u9b54\u3002\u201d \u3000\u3000\u8c01\u77e5\u4ed6\u7684\u8bdd\u521a\u521a\u8bf4\u5b8c\uff0c\u8033\u9645\u7adf\u4f20\u6765\u9634\u624b\u4e00\u9b54\u563f\u563f\u7684\u51b7\u7b11\u58f0\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff01\u7adf\u6562\u653e\u706b\u70e7\u8001 \u592b\u7684\u4e5d\u5929\u7384\u9634\u6d1e\u5e9c\uff0c\u5f53\u771f\u662f\u6d3b\u5f97\u4e0d\u8010\u70e6\u4e86\uff0c\u7528\u4e0d\u7740\u8fdb\u53bb\u627e\u8001\u592b\uff0c\u8001\u592b\u73b0\u5728\u5c31\u5728\u6d1e\u5916\u4e0e\u4f60\u4eec\u4f1a \u4f1a\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u6115\u7136\u4e3e\u76ee\u56db\u4e0b\u671b\u53bb\uff0c\u5374\u770b\u4e0d\u5230\u9634\u624b\u4e00\u9b54\u7684\u8e2a\u5f71\u3002 \u3000\u3000\u4ed6\u672c\u6765\u8981\u5f00\u53e3\u5927\u9a82\u51e0\u53e5\uff01\u4f46\u53c8\u60f3\u5230\u5148\u793c\u540e\u5175\uff0c\u53ea\u597d\u5fcd\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u5176\u5b9e\u4ed6\u65e9\u8be5\u60f3\u5230\uff0c\u653e\u706b\u70e7\u6d1e\u5df2\u7ecf\u5931\u53bb\u5148\u793c\u540e\u5175\u7684\u7acb\u573a\u4e86\u3002 \u3000\u3000\u53ea\u542c\u9634\u624b\u4e00\u9b54\u53c8\u9053\uff1a\u201c\u8d31\u5a62\uff0c\u539f\u6765\u4f60\u8fd9\u53db\u9006\u4e5f\u6765\u4e86\uff0c\u90a3\u5012\u7701\u4e86\u8001\u592b\u7684\u4e8b\u5566\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u542c\u97f3\u8fa8\u4f4d\uff0c\u5df2\u7ecf\u786e\u5b9a\u5bf9\u65b9\u58f0\u97f3\u53d1\u81ea\u5de6\u524d\u65b9\u5ced\u58c1\u4e0a\u4e00\u5757\u5927\u77f3\u540e\uff0c\u7acb\u5373\u9ad8\u53eb\u9053\uff1a\u201c\u9634 \u624b\u4e00\u9b54\uff0c\u7528\u4e0d\u7740\u85cf\u85cf\u8eb2\u8eb2\uff0c\u8fd8\u662f\u65e9\u4e9b\u73b0\u8eab\u51fa\u6765\u7684\u597d\u3002\u201d \u3000\u3000\u4e00\u8bed\u752b\u6bd5\uff0c\u679c\u7136\u7531\u5ced\u58c1\u4e0a\u7684\u5927\u77f3\u540e\uff0c\u8f6c\u51fa\u4e86\u9634\u624b\u4e00\u9b54\u3002 \u3000\u3000\u4ed6\u4e00\u73b0\u8eab\u5c31\u7531\u4e09\u56db\u4e08\u9ad8\u7684\u5927\u77f3\u4e0a\uff0c\u50cf\u9b45\u5f71\u822c\u65e0\u58f0\u65e0\u606f\u7684\u98d8\u4e0b\u5730\u6765\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u4e0d\u7531\u6697\u81ea\u63d0\u9ad8\u8b66\u89c9\uff0c\u9634\u624b\u4e00\u9b54\u72ec\u81ea\u4e00\u4eba\u9762\u5bf9\u9762\u7684\u62e6\u4f4f\u53bb\u8def\uff0c\u5fc5\u662f\u6709\u6043\u65e0\u6050\uff0c\u8bf4\u4e0d \u5b9a\u5728\u5468\u56f4\u53e6\u6709\u57cb\u4f0f\u3002\u56e0\u4e4b\uff0c\u5728\u672a\u5f97\u5230\u80e1\u67cf\u9f84\u7684\u793a\u610f\u4e0b\uff0c\u81ea\u5df1\u4e5f\u4e0d\u6562\u8f7b\u4e3e\u5984\u52a8\u3002 \u3000\u3000\u80e1\u67cf\u9f84\u5e76\u672a\u9762\u5bf9\u9634\u624b\u4e00\u9b54\uff0c\u56e0\u4e4b\uff0c\u9634\u624b\u4e00\u9b54\u4e5f\u672a\u770b\u6e05\u662f\u8c01\u3002 \u3000\u3000\u53ea\u542c\u9634\u624b\u4e00\u9b54\u58f0\u97f3\u51b7\u5f97\u50cf\u9b3c\u57df\u5439\u6765\u4e00\u9635\u9634\u98ce\u822c\u95ee\u9053\uff1a\u201c\u90a3\u4e00\u4e2a\u662f\u8c01\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u5df2\u4e0d\u518d\u5148\u793c\u540e\u5175\uff0c\u51b7\u51b7\u4e00\u7b11\u9053\uff1a\u201c\u82e5\u4f60\u770b\u6e05\u4ed6\u662f\u8c01\uff0c\u53ea\u6015\u4f1a\u5413\u7834\u72d7\u80c6\uff01\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u4e0d\u4f46\u4e0d\u6012\uff0c\u53cd\u800c\u563f\u563f\u7b11\u9053\uff1a\u201c\u8001\u592b\u80c6\u5b50\u54ea\u4f1a\u8fd9\u4e48\u5c0f\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4f60\u7684\u80c6\u5b50\u518d\u5927\uff0c\u7167\u6837\u4e5f\u4f1a\u5c41\u6eda\u5c3f\u6d41\u3002\u201d \u3000\u3000\u7a81\u89c1\u80e1\u67cf\u9f84\u8f6c\u8fc7\u8eab\u6765\uff0c\u62b1\u62f3\u4e00\u793c\u9053\uff1a\u201c\u9634\u624b\u524d\u8f88\u522b\u6765\u65e0\u6059\uff01\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u4e0d\u89c9\u8138\u8272\u4e00\u53d8\u9053\uff1a\u201c\u5c0a\u9a7e\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u80e1\u67cf\u9f84\u9053\uff1a\u201c\u9634\u624b\u524d\u8f88\u5f53\u771f\u5065\u5fd8\uff0c\u54b1\u4eec\u4e09\u5e74\u524d\u5728\u5357\u660c\u57ce\u836f\u738b\u5e99\u8fd8\u66fe\u4f1a\u8fc7\uff0c\u5e94\u8be5\u8fd8\u8bb0\u5f97\u80e1 \u67d0\u5427\uff1f\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u53cc\u988a\u6025\u5267\u7684\u6410\u4e86\u51e0\u4e0b\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u4e0d\u662f\u5df2\u7ecf\u2026\u2026\u201d \u3000\u3000\u80e1\u67cf\u9f84\u6602\u7136\u7b11\u9053\uff1a\u201c\u6c5f\u6e56\u4f20\u8a00\uff0c\u4f55\u8db3\u51ed\u4fe1\uff01\u80e1\u67d0\u82e5\u6b7b\u4e86\uff0c\u600e\u4f1a\u53c8\u5728\u9634\u624b\u524d\u8f88\u9762\u524d\u51fa\u73b0\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u7d27\u8ddf\u7740\u9053\uff1a\u201c\u8001\u5c0f\u5b50\uff0c\u82e5\u628a\u6211\u4eec\u80e1\u76df\u4e3b\u5f53\u6210\u9634\u9b42\u51fa\u73b0\uff0c\u4f60\u4e5f\u5c31\u778e\u5b50\u95fb\u81ed\u4e00\u79bb\u6b7b \uff08\u5c4e\uff09\u4e0d\u8fdc\u4e86\uff01\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u4e0d\u4fbf\u8ffd\u6839\u7a76\u5e95\uff0c\u5f80\u4e0a\u4e00\u62db\u624b\u9053\uff1a\u201c\u56db\u4f4d\u8bf7\u4eae\u4eae\u76f8\uff0e\u4e5f\u597d\u8ba9\u4ed6\u4eec\u89c1\u8bc6\u89c1\u8bc6\uff01\u201d \u3000\u3000\u84e6\u5730\uff0c\u7531\u5148\u524d\u5ced\u58c1\u4e0a\u90a3\u5757\u5927\u77f3\u540e\uff0c\u5192\u51fa\u4e86\u56db\u4e2a\u5947\u5f62\u602a\u72b6\u7684\u4eba\u5f71\u3002 \u3000\u3000\u8fd9\u56db\u4eba\u8d6b\u7136\u662f\u6570\u6708\u524d\u51e0\u4e4e\u628a\u8ff7\u8e2a\u8c37\u95f9\u5f97\u5929\u7ffb\u5730\u8986\u7684\u6bd2\u706b\u6210\u5168\uff0e\u9ed1\u9b54\u65f6\u5bc5\u3001\u82d7\u7586\u706b\u4e91\u6d1e\u603b \u5cd2\u4e3b\u706b\u83bd\u548c\u53f7\u79f0\u897f\u57df\u5bc6\u5b97\u7ea2\u6559\u4eb2\u738b\u7684\u725b\u5954\u3002 \u3000\u3000\u8fd9\u56db\u4eba\u56e0\u6570\u6708\u524d\u66fe\u5728\u8ff7\u8e2a\u8c37\u5927\u95f9\u8fc7\uff0c\u9ea6\u5c0f\u660e\u81ea\u7136\u5168\u8ba4\u8bc6\u3002\u80e1\u67cf\u9f84\u867d\u662f\u8d70\u904d\u9ec4\u6cb3\u4e24\u5cb8\u3001\u5927 \u6c5f\u5357\u5317\u7684\u4eba\u7269\uff0c\u5374\u53ea\u80fd\u8ba4\u51fa\u6bd2\u706b\u6210\u5168\u548c\u9ed1\u9b54\u65f6\u5bc5\u3002 \u3000\u3000\u81f3\u4e8e\u82d7\u7d20\u82d3\uff0c\u8fde\u4e00\u4e2a\u4e5f\u8ba4\u4e0d\u5f97\u3002 \u3000\u3000\u80e1\u67cf\u9f84\u7b49\u4eba\uff0c\u867d\u65e9\u5c31\u9884\u6599\u9634\u624b\u4e00\u9b54\u5fc5\u6709\u57cb\u4f0f\uff0c\u5374\u4e07\u672a\u6599\u5230\u7adf\u662f\u6bd2\u706b\u6210\u5168\u548c\u9ed1\u9b54\u65f6\u5bc5\u7b49\u4eba \u4e3a\u9634\u624b\u4e00\u9b54\u52a9\u9635\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u4f4e\u58f0\u9053\uff1a\u201c\u5e08\u5144\uff0c\u53e6\u4e24\u4eba\u4e5f\u8bb8\u60a8\u4e0d\u8ba4\u8bc6\uff0c\u90a3\u79c3\u9876\u7ea2\u53d1\u662f\u82d7\u7586\u706b\u4e91\u6d1e\u7684\u5cd2\u4e3b\uff0c\u540d\u53eb \u706b\u83bd\u3002 \u3000\u3000\u53e6\u4e00\u4e2a\u9762\u5706\u65e0\u9aef\u3001\u53cc\u773c\u5947\u77ed\u7684\u897f\u57df\u5bc6\u5b97\u7ea2\u6559\u7684\uff0c\u540d\u53eb\u725b\u5954\u3002\u6570\u6708\u524d\u4ed6\u4eec\u66fe\u8fdb\u5165\u8ff7\u8e2a\u8c37\u95f9 \u4e8b\uff0e\u591a\u4e8f\u5999\u5999\u5eb5\u90a3\u4f4d\u8001\u5e08\u592a\u628a\u4ed6\u4eec\u5236\u4f0f\u9a71\u8d70\u3002\u201d \u3000\u3000\u80e1\u67cf\u9f84\u5de6\u624b\u6284\u8d77\u94c1\u62d0\uff0c\u53f3\u624b\u63a3\u51fa\u957f\u5251\u9053\uff1a\u201c\u77e5\u9053\u4e86\uff0c\u4f60\u548c\u82d7\u59d1\u5a18\u8981\u5343\u4e07\u5c0f\u5fc3\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u8868\u9762\u4e0a\u867d\u7136\u4ecd\u80fd\u4fdd\u6301\u9547\u5b9a\uff0c\u5185\u5fc3\u4e5f\u662f\u96be\u514d\u6709\u4e9b\u6050\u614c\uff0c\u6bd2\u706b\u6210\u5168\u7b49\u56db\u4eba\u518d\u52a0\u4e0a\u9634\u624b \u4e00\u9b54\uff0c\u4e0a\u6b21\u66fe\u5728\u8ff7\u8e2a\u8c37\u5927\u5385\u5236\u4f4f\u7eff\u6797\u603b\u5be8\u7684\u6570\u5341\u540d\u9ad8\u624b\u3002 \u3000\u3000\u8fde\u8001\u5978\u5de8\u733e\u7684\u970d\u5143\u4f3d\u90fd\u51e0\u4e4e\u683d\u4e86\u8ddf\u5934\uff0c\u5982\u4eca\u81ea\u5df1\u4e00\u65b9\u53ea\u6709\u4e09\u4eba\uff0c\u7eb5\u7136\u80e1\u67cf\u9f84\u795e\u52c7\u76d6\u4e16\uff0c \u770b\u6765\u4e5f\u51f6\u591a\u5360\u5c11\u3002 \u3000\u3000\u8fd9\u65f6\u4ed6\u771f\u540e\u6094\u4e0d\u8be5\u5230\u5415\u6881\u5c71\u4e5d\u5929\u7384\u9634\u6d1e\u5e9c\u6765\uff0c\u56e0\u4e3a\u8fd9\u4e8b\u662f\u7531\u4ed6\u63d0\u8bae\u7684\u3002 \u3000\u3000\u53ea\u542c\u6bd2\u706b\u6210\u5168\u9053\uff1a\u201c\u9634\u624b\u5144\uff0c\u80e1\u67cf\u9f84\u4e0d\u662f\u6b7b\u4e86\u5417\uff1f\u600e\u4e48\u53c8\u51fa\u6765\u5566\uff1f\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u9053\uff1a\u201c\u542c\u8bf4\u5f53\u5e74\u80e1\u67cf\u9f84\u88ab\u523a\u7684\u65f6\u5019\u4f60\u66fe\u5728\u573a\uff0c\u4f60\u8be5\u77e5\u9053\u7684\u6bd4\u8001\u592b\u6e05\u695a\u3002\u201d \u3000\u3000\u6bd2\u706b\u6210\u5168\u9053\uff1a\u201c\u6210\u67d0\u660e\u660e\u770b\u5230\u4ed6\u88ab\u8c6b\u5357\u8303\u7389\u6606\u4e00\u5251\u523a\u6b7b\uff0c\u7136\u540e\u5c38\u4f53\u88ab\u8c37\u5bd2\u9999\u62d6\u8d70\uff0c\u4ed6\u53c8 \u600e\u80fd\u518d\u6d3b\u56de\u6765\u4e86\uff1f\u201d \u3000\u3000\u201c\u90a3\u4e48\u9762\u524d\u8fd9\u4eba\u5374\u548c\u4ed6\u4e00\u822c\u65e0\u4e8c\uff0c\u53c8\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d \u3000\u3000\u201c\u8fd9\u4eba\u4e00\u5b9a\u662f\u5047\u7684\u3002\u201d \u3000\u3000\u201c\u4eba\u600e\u4e48\u4f1a\u6709\u5047\u7684\uff1f\u201d \u3000\u3000\u201c\u76ee\u524d\u6b66\u6797\u4e2d\u7684\u6613\u5bb9\u672f\uff0e\u6bd4\u4ece\u524d\u53c8\u4e0d\u77e5\u8fdb\u6b65\u4e86\u591a\u5c11\uff0e\u53ea\u8981\u8eab\u6750\u76f8\u4eff\u4f5b\uff0c\u6613\u5bb9\u51fa\u6765\uff0c\u6839\u672c \u6beb\u65e0\u7834\u7efd\u53ef\u5bfb\u3002\u4ed6\u82e5\u771f\u662f\u80e1\u67cf\u9f84\uff0c\u600e\u4f1a\u4e0d\u56de\u8ff7\u8e2a\u8c37\u518d\u505a\u4ed6\u7684\u7eff\u6797\u76df\u4e3b\uff1f\u201d \u3000\u3000\u8fd9\u51e0\u53e5\u8bdd\uff0c\u7ec8\u4e8e\u4f7f\u9634\u624b\u4e00\u9b54\u5b8c\u5168\u76f8\u4fe1\u9762\u524d\u7684\u80e1\u67cf\u9f84\u662f\u53e6\u6709\u4eba\u4f2a\u88c5\u6539\u626e\u7684\uff0e\u6b63\u56e0\u5982\u6b64\uff0c\u4e5f \u4f7f\u4ed6\u5927\u5927\u653e\u4e0b\u5fc3\u6765\u3002 \u3000\u3000\u9634\u624b\u2014\u9b54\u5411\u4e0a\u62db\u4e86\u62db\u624b\u9053\uff1a\u201c\u4f60\u4eec\u4e3a\u4ec0\u4e48\u8fd8\u4e0d\u4e0b\u6765\uff1f\u201d \u3000\u3000\u6bd2\u706b\u6210\u5168\u548c\u9ed1\u9b54\u65f6\u5bc5\u7acb\u5373\u5e94\u58f0\u8f7b\u98d8\u74e2\u7684\u843d\u5728\u9634\u624b\u4e00\u9b54\u8eab\u4fa7\u3002 \u3000\u3000\u9634\u624b\u4e00\u9b54\u671b\u7740\u725b\u5954\u548c\u706b\u83bd\u9053\uff1a\u201c\u4f60\u4eec\u4e8c\u4f4d\u8fd8\u5728\u4e0a\u9762\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u706b\u83bd\u9053\uff1a\u201c\u672c\u603b\u5cd2\u4e3b\u5c45\u9ad8\u4e34\u4e0b\u653e\u866b\u6bd4\u8f83\u65b9\u4fbf\u4e9b\u3002\u201d \u3000\u3000\u725b\u5954\u9053\uff1a\u201c\u672c\u4eb2\u738b\u5728\u4e0a\u9762\u8d1f\u8d23\u76d1\u89c6\uff0c\u4e5f\u6bd4\u8f83\u65b9\u4fbf\u4e9b\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u5411\u4e0a\u53f1\u9053\uff1a\u201c\u725b\u5954\uff0c\u4f60\u8fd9\u4e2a\u738b\u516b\u86cb\uff01\u4e0a\u6b21\u5230\u8ff7\u8e2a\u8c37\uff0e\u88c5\u6210\u4e0d\u4f1a\u8bf4\u4eba\u8bdd\uff0c\u73b0\u5728\u600e\u4e48 \u4f1a\u8bf4\u8bdd\u4e86\uff1f\u201d \u3000\u3000\u725b\u5954\u563f\u563f\u7b11\u9053\uff1a\u201c\u672c\u4eb2\u738b\u5728\u4e2d\u539f\u4f4f\u4e86\u8fd9\u4e48\u4e45\uff0c\u5b66\u4e5f\u5b66\u4f1a\u4e86\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4f60\u8fd9\u738b\u516b\u86cb\u65e2\u7136\u6709\u8fd9\u4e48\u597d\u7684\u8bed\u8a00\u5929\u624d\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u53bb\u8bf4\u4e66\uff1f\u201d \u3000\u3000\u725b\u5954\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u6b7b\u5230\u4e34\u5934\uff0c\u8fd8\u6562\u8ddf\u672c\u4eb2\u738b\u95f9\u7b11\uff01\u201d \u3000\u3000\u53ea\u542c\u80e1\u67cf\u9f84\u9053\uff1b\u201c\u5c0f\u660e\uff0c\u8fce\u654c\u8981\u7d27\uff0c\u7528\u4e0d\u7740\u548c\u4ed6\u7f57\u55e6\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u671b\u5411\u9634\u624b\u4e00\u9b54\u9053\uff1a\u201c\u8001\u5c0f\u5b50\uff0c\u4f60\u6709\u672c\u4e8b\uff0c\u73b0\u5728\u5c31\u4f7f\u5427\uff01\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u4e0d\u52a8\u58f0\u8272\u9053\uff1a\u201c\u8001\u592b\u60f3\u5148\u95ee\u95ee\u4f60\u4eec\u4eca\u5929\u662f\u505a\u4ec0\u4e48\u6765\u7684\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u5f88\u7b80\u5355\uff0c\u60f3\u8ddf\u4f60\u8ba8\u4e9b\u2018\u5411\u5fc3\u9732\u2019\u89e3\u836f\u3002\u201d \u3000\u3000\u201c\u7b11\u6d3b\uff01\u8001\u592b\u72ec\u95e8\u7279\u5236\u7684\u89e3\u836f\uff0c\u5c82\u80fd\u65e0\u7f18\u65e0\u6545\u9001\u7ed9\u4f60\u4eec\u3002\u201d \u3000\u3000\u201c\u5982\u679c\u4e0d\u7ed9\uff0e\u6211\u4eec\u5c31\u53ea\u6709\u786c\u8981\uff01\u201d \u3000\u3000\u201c\u765e\u86e4\u87c6\u6253\u5475\u6b20\u2014\u2014\u597d\u5927\u7684\u53e3\u6c14\uff01\u4f60\u4eec\u81ea\u4fe1\u4eca\u5929\u8fd8\u80fd\u8dd1\u5f97\u4e86\u5417\uff1f\u201d \u3000\u3000\u201c\u6211\u4eec\u672c\u6765\u5c31\u6ca1\u60f3\u8dd1\u7684\u3002\u201d \u3000\u3000\u53ea\u542c\u9ed1\u9b54\u65f6\u5bc5\u9053\uff1a\u201c\u9634\u624b\u5144\uff0c\u8ddf\u8fd9\u5c0f\u5b50\u8fd8\u6597\u7684\u4ec0\u4e48\u5634\uff0c\u65e9\u70b9\u628a\u4ed6\u4eec\u89e3\u51b3\u4e86\u518d\u8bf4\u3002\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u9053\uff1a\u201c\u5c31\u8bf7\u65f6\u5144\u628a\u8fd9\u5c0f\u5b50\u62ff\u4e0b\uff0c\u6700\u597d\u62ff\u6d3b\u7684\uff0c\u8001\u592b\u8fd8\u8981\u95ee\u4ed6\u53e3\u4f9b\u3002\u201d \u3000\u3000\u9ed1\u9b54\u65f6\u5bc5\u9053\uff1a\u201c\u542c\u8bf4\u8fd9\u5c0f\u5b50\u5f88\u624e\u624b\u3002\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u867d\u7136\u624e\u624b\uff0c\u51ed\u4f60\u65f6\u5144\u7684\u9ed1\u7802\u638c\uff0c\u4f55\u6101\u5236\u4e0d\u4f4f\u4ed6\uff01\u201d \u3000\u3000\u9ed1\u9b54\u65f6\u5bc5\u968f\u5373\u5411\u524d\u8d70\u51fa\u51e0\u6b65\u3002 \u3000\u3000\u53ea\u89c1\u4ed6\u731b\u4e00\u5438\u6c14\uff0c\u987f\u65f6\u4e0a\u8179\u66b4\u80c0\u5982\u9f13\uff0e\u63a5\u7740\u53f3\u81c2\u4e5f\u7c97\u4e86\u4e00\u500d\u6709\u4f59\uff0e\u5c24\u5176\u53f3\u638c\u4e0d\u4f46\u5239\u65f6\u989c \u8272\u53d8\u5f97\u6709\u5982\u9ed1\u70ad\uff0c\u800c\u4e14\u8fd8\u5192\u51fa\u84b8\u9ed1\u6c14\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u53ea\u770b\u5f97\u5927\u611f\u5403\u60ca\u3002 \u3000\u3000\u80e1\u67cf\u9f84\u7acb\u5373\u4ee5\u4f20\u97f3\u5165\u5bc6\u4e4b\u672f\u9053\uff1a\u201c\u7528\u8f6f\u97ad\u5bf9\u4ed8\u4ed6\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u660e\u767d\uff0c\u8f6f\u97ad\u662f\u957f\u5175\u5668\uff0c\u53ef\u907f\u514d\u548c\u5bf9\u65b9\u8fd1\u8eab\u63a5\u89e6\uff0c\u53ea\u8981\u4e0d\u88ab\u5bf9\u65b9\u638c\u529b\u6240\u53ca\uff0c\u7eb5\u7136\u53d7 \u4f24\uff0c\u4e5f\u4e0d\u81f3\u4e8e\u592a\u4e25\u91cd\u3002 \u3000\u3000\u4ed6\u4f9d\u8a00\u677e\u5f00\u8170\u95f4\u6263\u628a\uff0c\u63e1\u97ad\u4e5f\u5411\u524d\u8d70\u51fa\u4e24\u6b65\u9053\uff1a\u201c\u9ed1\u8001\u5c0f\uff0c\u7528\u4e0d\u7740\u803d\u8bef\u65f6\u95f4\uff0c\u5728\u4e0b\u7b49\u7740 \u4f60\u53d1\u638c\uff01\u201d \u3000\u3000\u9ed1\u9b54\u65f6\u5bc5\u5e76\u4e0d\u7b54\u8bdd\uff0c\u56e0\u4e3a\u8fd9\u65f6\u4ed6\u6839\u672c\u4e0d\u80fd\u5f00\u53e3\uff0c\u3000\u4e00\u5f00\u53e3\u5fc5\u5b9a\u6cc4\u6c14\uff0e \u3000\u3000\u9ea6\u5c0f\u660e\u5012\u662f\u5f88\u5927\u65b9\uff0c\u5e76\u4e0d\u8d81\u673a\u65bd\u88ad\uff0c\u53ea\u662f\u9759\u9759\u7684\u7b49\u5019\u5bf9\u65b9\u53d1\u62db\u3002 \u3000\u3000\u7a81\u89c1\u65f6\u5bc5\u5410\u6c14\u5f00\u58f0\uff0c\u500f\u5ffd\u95f4\u75be\u8fdb\u4e09\u6b65\uff0c\u731b\u7136\u4e00\u638c\uff0c\u76f4\u5411\u9ea6\u5c0f\u660e\u5f53\u5934\u5288\u4e0b\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u867d\u65e9\u5df2\u6709\u5907\uff0c\u5374\u4e5f\u5927\u5403\u4e86\u4e00\u60ca\uff0c\u6025\u6025\u5411\u540e\u8dc3\u9000\u51e0\u6b65\uff0c\u624b\u4e2d\u8f6f\u97ad\uff0c\u201c\u5530\u201d\u7684\u4e00\u58f0\u63a0 \u4e86\u51fa\u53bb\u3002 \u3000\u3000\u65f6\u5bc5\u7684\u8fd9\u4e00\u638c\uff0c\u5a01\u52bf\u4e0d\u4e9a\u4e8e\u6392\u5c71\u5012\u6d77\uff0c\u6fc0\u8d77\u5730\u9762\u4e00\u7247\u98de\u6c99\u8d70\u77f3\uff0c\u8fde\u7ad9\u5728\u6570\u4e08\u5916\u7684\u80e1\u67cf\u9f84 \u548c\u82d7\u7d20\u82d3\uff0c\u4e5f\u90fd\u88ab\u638c\u98ce\u6fc0\u8361\u5f97\u8863\u8882\u98d8\u98d8\u3002 \u3000\u3000\u5c24\u5176\u4ed6\u8fd9\u638c\u529b\uff0c\u66f4\u631f\u7740\u4e00\u80a1\u5947\u5bd2\u65e0\u6bd4\u7684\u9634\u98ce\u51b7\u6c14\uff0e\u9ea6\u5c0f\u660e\u867d\u672a\u88ab\u638c\u529b\u51fb\u4e2d\uff0c\u5374\u8fde\u6253\u4e24\u4e2a \u5bd2\u5664\uff0c\u8fde\u624b\u811a\u90fd\u51bb\u5f97\u6709\u4e9b\u50f5\u786c\u3002 \u3000\u3000\u4f46\u65f6\u5bc5\u5374\u5e76\u4e0d\u6ee1\u610f\uff0c\u5728\u4ed6\u9884\u6599\uff0c\u8fd9\u4e00\u638c\u8db3\u53ef\u628a\u9ea6\u5c0f\u660e\u51fb\u6bd9\u638c\u4e0b\uff0c\u5728\u65e2\u607c\u53c8\u6c14\u4e4b\u4e0b\u7d27\u63a5\u7740 \u53c8\u53d1\u51fa\u4e00\u638c\u3002 \u3000\u3000\u53ea\u542c\u80e1\u67cf\u9f84\u53c8\u4f20\u97f3\u9053\uff1a\u201c\u6682\u65f6\u522b\u8fd8\u51fb\uff0e\u5c3d\u91cf\u4ee5\u8ff7\u8e2a\u8c37\u6b65\u6cd5\u95ea\u907f\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u4e00\u8fde\u907f\u5f00\u65f6\u5bc5\u4e03\u516b\u638c\uff0c\u80e1\u67cf\u9f84\u53c8\u9053\uff1a\u201c\u73b0\u5728\u53ef\u4ee5\u51fa\u97ad\u4e86\uff0c\u7528\u97ad\u68a2\u7f20\u4f4f\u4ed6\u7684\u53f3\u8155\u3002\u201d \u3000\u3000\u539f\u6765\u65f6\u5bc5\u7684\u9ed1\u7802\u638c\u529b\uff0c\u56fa\u662f\u8fd0\u6c14\u84c4\u52bf\u800c\u53d1\uff0e\u6545\u800c\u7b2c\u2014\u638c\u6700\u731b\uff0c\u4ee5\u540e\u7684\u5a01\u529b\u5219\u968f\u4e4b\u9012\u51cf\u3002 \u6b64\u523b\u5df2\u8fde\u53d1\u4e03\u516b\u638c\uff0c\u53ea\u8981\u4e0d\u88ab\u638c\u529b\u76f4\u63a5\u51fb\u4e2d\uff0c\u9ea6\u5c0f\u660e\u5df2\u53ef\u62b5\u53d7\u5f97\u4f4f\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u628a\u63e1\u65f6\u673a\uff0c\u770b\u51c6\u90e8\u4f4d\uff0c\u9aa4\u8d77\u4e00\u97ad\uff0c\u2014\u62db\u201c\u91d1\u86c7\u6447\u5c3e\u201d\u76f4\u5411\u65f6\u5bc5\u53f3\u81c2\u62bd\u53bb\u3002 \u3000\u3000\u8fd9\u97ad\u52bf\u75be\u4f3c\u95ea\u7535\uff0c\u5947\u51c6\u65e0\u6bd4\u7684\u6b63\u597d\u7f20\u4e0a\u65f6\u5bc5\u53f3\u8155\u3002 \u3000\u3000\u65f6\u5bc5\u5927\u60ca\u4e4b\u4e0b\uff0c\u731b\u529b\u5411\u540e\u5e26\u53bb\u3002 \u3000\u3000\u5c82\u77e5\u9ea6\u5c0f\u660e\u4e5f\u540c\u65f6\u5c06\u8f6f\u97ad\u731b\u529b\u540e\u5e26\u3002 \u3000\u3000\u82e5\u8bba\u5185\u529b\uff0c\u65f6\u5bc5\u5fc5\u5b9a\u80dc\u8fc7\u9ea6\u5c0f\u660e\uff0c\u4f46\u56e0\u4e00\u4e2a\u662f\u7528\u624b\u5e26\uff0c\u4e00\u4e2a\u662f\u7528\u97ad\u5e26\uff0c\u624b\u81c2\u6ca1\u6709\u97ad\u957f\uff0c \u65f6\u5bc5\u5f53\u7136\u8981\u5927\u5927\u5403\u4e8f\u3002 \u3000\u3000\u7adf\u88ab\u5f53\u573a\u5e26\u52a8\u5f97\u8e09\u8dc4\u5411\u524d\u6454\u51fa\u597d\u51e0\u6b65\uff0c\u5f53\u4ed6\u521a\u521a\u7ad9\u7a33\uff0c\u4e0d\u60f3\u9ea6\u5c0f\u660e\u5df2\u5728\u8fd9\u65f6\uff0c\u8fc5\u5feb\u7684\u63a3 \u51fa\u957f\u5251\uff0c\u5251\u5c16\u5df2\u62b5\u4f4f\u4e86\u4ed6\u7684\u80f8\u53e3\u3002 \u3000\u3000\u6b64\u523b\u65f6\u5bc5\u5c31\u662f\u60f3\u518d\u53d1\u638c\uff0c\u4e5f\u65e0\u80fd\u4e3a\u529b\u3002\u4e00\u6765\u88ab\u4eba\u5251\u62b5\u524d\u80f8\uff0c\u4e0d\u6562\u8f7b\u4e3e\u5984\u52a8\uff1b\u4e8c\u6765\u4ed6\u7684\u53f3 \u8155\u5df2\u88ab\u8f6f\u97ad\u7f20\u62c9\u5f97\u76ae\u7834\u8840\u6d41\uff0c\u5373\u4f7f\u60f3\u8fd0\u6c14\u4e5f\u65e0\u6cd5\u5c06\u5185\u529b\u8d2f\u7a7f\u5230\u53f3\u638c\u4e0a\u53bb\uff0e \u3000\u3000\u9ea6\u5c0f\u660e\u51b7\u51b7\u4e00\u7b11\u9053\uff1a\u201c\u9ed1\u8001\u5c0f\u5b50\uff0c\u6ca1\u60f3\u5230\u4f60\u4e5f\u6709\u4eca\u5929\u5427\uff1f\u201d \u3000\u3000\u65f6\u5bc5\u7684\u786e\u505a\u68a6\u4e5f\u6ca1\u60f3\u5230\uff0c\u7eb5\u6a2a\u5927\u6c5f\u5357\u5317\u6570\u5341\u5e74\u7684\u4e00\u4ee3\u9ed1\u9b54\uff0c\u7adf\u7136\u683d\u5728\u4e00\u4e2a\u540d\u4e0d\u89c1\u7ecf\u4f20\u7684 \u6bdb\u5934\u5c0f\u5b50\u624b\u91cc\u3002\u9664\u4e86\u9762\u8272\u618b\u5f97\u6709\u5982\u732a\u809d\uff0c\u54ea\u91cc\u8fd8\u80fd\u8bf4\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u5f97\u7406\u4e0d\u8ba9\u4eba\uff0c\u518d\u9053\uff1a\u201c\u9ed1\u8001\u5c0f\u5b50\uff0c\u600e\u4e48\u4e0d\u8bb2\u8bdd\uff1f\u4ec0\u4e48\u65f6\u5019\u53d8\u54d1\u5df4\u4e86\uff1f\u201d \u3000\u3000\u4ed6\u77e5\u9053\u65f6\u5bc5\u5fc5\u5b9a\u4ecd\u4e0d\u5f00\u53e3\uff01\uff0c\u8bf4\u5b8c\u8bdd\u731b\u7136\u4e00\u6307\uff0c\u70b9\u4e0a\u5bf9\u65b9\u7684\u53f3\u8098\u201c\u66f2\u6c60\u7a74\u201d\uff0c\u518d\u4e00\u6307\u52a0 \u70b9\u201c\u5c06\u53f0\u7a74\u201d\u3002 \u3000\u3000\u8fd9\u4e00\u6765\uff0e\u7b49\u4e8e\u7ed9\u65f6\u5bc5\u52a0\u4e0a\u4e86\u4e8c\u9053\u67b7\u9501\uff0c\u65f6\u5bc5\u6643\u4e86\u51e0\u6643\uff0c\u5f53\u573a\u5012\u4e0b\u5730\u53bb\u3002 \u3000\u3000\u53ea\u542c\u6bd2\u706b\u6210\u5168\u51b7\u7b11\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u8ba9\u8001\u592b\u6765\u6536\u62fe\u4f60\uff01\u201d \u3000\u3000\u8bf4\u8bdd\u95f4\u5927\u6447\u5927\u6446\u7684\u8d70\u4e86\u51fa\u6765\u3002 \u3000\u3000\u6bd2\u706b\u6210\u5168\u7684\u957f\u76f8\uff0c\u770b\u6765\u5c31\u4ee4\u4eba\u65e2\u6050\u6016\u53c8\u6076\u5fc3\uff0e\u8138\u8272\u534a\u8fb9\u767d\u534a\u8fb9\u7ea2\uff0c\u7ea2\u7684\u9c9c\u8273\u593a\u76ee\uff0c\u767d\u7684 \u6beb\u65e0\u8840\u8272\u3002 \u3000\u3000\u6b64\u4eba\u53f7\u79f0\u6b66\u6797\u7b2c\u4e00\u6697\u5668\u9ad8\u624b\uff0c\u5c24\u5176\u5584\u4e8e\u65bd\u653e\u706b\u836f\uff0e\u4ed6\u51fa\u6218\u65f6\u770b\u4f3c\u53cc\u624b\u7a7a\u65e0\u4e00\u7269\u3001\u5176\u5b9e\u7cfb \u5728\u8170\u95f4\u7684\u9769\u56ca\u6709\u5982\u8702\u7a9d\u4e00\u822c\uff0e\u88c5\u6ee1\u4e86\u51e0\u5341\u79cd\u4e0d\u540c\u7684\u6697\u5668\u706b\u836f\uff0c\u968f\u65f6\u90fd\u53ef\u51fa\u624b\u4f24\u4eba\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u521a\u8981\u8fce\u6218\uff0c\u5df2\u542c\u80e1\u67cf\u9f84\u9053\uff1a\u201c\u5c0f\u660e\u548c\u82d7\u59d1\u5a18\u9000\u4e0b\uff0e\u7ad9\u8fdc\u4e9b\uff01\u8fd9\u59d3\u6210\u7684\u4ea4\u7ed9\u6211\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u548c\u82d7\u7d20\u82d3\u4f9d\u8a00\u5411\u540e\u9000\u4e86\u597d\u51e0\u4e08\uff0c\u4f46\u8fd8\u662f\u84c4\u52bf\u4ee5\u5f85\u3002 \u3000\u3000\u80e1\u67cf\u9f84\u53f3\u5251\u5de6\u62d0\uff0c\u7f13\u7f13\u5411\u524d\u8d70\u51fa\u51e0\u6b65\uff0c\u51b7\u51b7\u7b11\u9053\uff1a\u201c\u6210\u5168\uff0c\u5ff5\u5728\u4f60\u548c\u80e1\u67d0\u7684\u9093\u5e08\u53d4\u662f\u591a \u5e74\u8001\u53cb\uff0c\u5728\u4e0b\u60c5\u613f\u8ba9\u4f60\u5148\u884c\u51fa\u624b\u3002\u201d \u3000\u3000\u6bd2\u706b\u6210\u5168\u4e0d\u5c51\u7684\u77a5\u8fc7\u6765\u4e00\u773c\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u4f60\u5c31\u662f\u5047\u5192\u80e1\u67cf\u9f84\uff0c\u4e5f\u5413\u4e0d\u5012\u8001\u592b\uff01\u201d \u3000\u3000\u80e1\u67cf\u9f84\u9053\uff1a\u201c\u52a8\u624b\u8fc7\u62db\uff0c\u5404\u51ed\u771f\u5b9e\u672c\u9886\u53d6\u80dc\uff0c\u5728\u4e0b\u662f\u4e0d\u662f\u80e1\u67cf\u9f84\uff0e\u5e76\u4e0d\u91cd\u8981\u3002\u201d \u3000\u3000\u6bd2\u706b\u6210\u5168\u9053\uff1a\u201c\u4f60\u7684\u786e\u88c5\u5f97\u5f88\u50cf\uff0c\u8fde\u5175\u5668\u4e5f\u5b8c\u5168\u4e00\u6837\uff0c\u8001\u592b\u73b0\u5728\u5c31\u60f3\u770b\u770b\u4f60\u628a\u80e1\u67cf\u9f84 \u201c\u62d0\u5185\u5957\u5251\u201d\u7684\u7edd\u62db\uff0c\u5b66\u5230\u4e86\u51e0\u5206\uff01\u201d \u3000\u3000\u80e1\u67cf\u9f84\u4e0d\u52a8\u58f0\u8272\u9053\uff1a\u201c\u7528\u4e0d\u7740\u591a\u8bf4\u5e9f\u8bdd\uff0c\u6709\u591a\u5c11\u6697\u5668\u548c\u706b\u836f\uff0c\u53ea\u7ba1\u65bd\u5c55\u51fa\u6765\uff01\u201d \u3000\u3000\u8fd9\u65f6\u7ad9\u5728\u540e\u9762\u8fdc\u5904\u7684\u9ea6\u5c0f\u660e\u548c\u82d7\u7d20\u82d3\uff0c\u90fd\u4e3a\u80e1\u67cf\u9f84\u634f\u4e00\u628a\u6c57\uff0c\u4e00\u65e6\u80e1\u67cf\u9f84\u88ab\u6697\u5668\u6240\u4f24\uff0e \u4ed6\u4eec\u4e8c\u4eba\u4e5f\u5fc5\u96be\u9003\u5384\u8fd0\u3002 \u3000\u3000\u7a81\u89c1\u6bd2\u706b\u6210\u5168\u5de6\u624b\u4e00\u626c\uff0c\u4e00\u56e2\u84dd\u6c6a\u6c6a\u7684\u5149\u8292\uff0c\u76f4\u5411\u80e1\u67cf\u9f84\u76d6\u6d12\u800c\u4e0b\u3002 \u3000\u3000\u8c01\u90fd\u77e5\u9053\uff0e\u8fd9\u662f\u4e00\u84ec\u4f53\u79ef\u6781\u5c0f\u7684\u6dec\u6bd2\u6697\u5668\u3002 \u3000\u3000\u6bd2\u706b\u6210\u5168\u65bd\u653e\u6697\u5668\u7684\u624b\u6cd5\uff0c\u679c\u7136\u4e0e\u4f17\u4e0d\u540c\uff0c\u51fa\u624b\u521d\u65f6\u6709\u5982\u4e00\u9053\u94f6\u67f1\uff0c\u51e0\u5341\u679a\u6697\u5668\u51dd\u805a\u4e0d \u6563\uff0e\u76f4\u5230\u4e34\u8fd1\u5bf9\u65b9\u4e0a\u7a7a\u4e09\u5c3a\u5de6\u53f3\uff0e\u84e6\u5730\u5316\u4f5c\u4e00\u56e2\u84dd\u661f\uff0c\u76f4\u7b3c\u7f69\u4e86\u6570\u5c3a\u65b9\u5706\uff0e\u50cf\u6c34\u94f6\u6cc4\u5730\u822c\uff0c \u5954\u5411\u4eba\u8eab\u5404\u5904\u5927\u7a74\u3002 \u3000\u3000\u4f46\u89c1\u80e1\u67cf\u9f84\u5251\u62d0\u4ea4\u6325\uff0c\u62d0\u4e2d\u5957\u5251\uff0c\u5251\u5916\u7f69\u62d0\uff0c\u5f62\u6210\u4e00\u9053\u98ce\u96e8\u96be\u4fb5\u7684\u5149\u5e55\uff0c\u4e00\u9635\u7c0c\u7c0c\u556a\u556a \u4e4b\u58f0\u8fc7\u540e\uff0c\u90a3\u6253\u6765\u7684\u6c41\u6bd2\u6885\u82b1\u9488\u548c\u6b47\u5c3e\u7ec6\u9488\u5168\u7eb7\u7eb7\u843d\u5728\u5730\u4e0a\uff0c\u7adf\u7136\u8fde\u80e1\u67cf\u9f84\u7684\u8863\u670d\u90fd\u6ca1\u6cbe\u5230\u3002 \u3000\u3000\u8fd9\u4f7f\u6bd2\u706b\u6210\u5168\u7b80\u76f4\u6709\u4e9b\u76ee\u77aa\u53e3\u5446\uff0c\u6697\u9053\uff1a\u201c\u8fd9\u5192\u724c\u80e1\u67cf\u9f84\uff0c\u4f3c\u4e4e\u6bd4\u771f\u7684\u4fbf\u8fd8\u8981\u5389\u5bb3\u4e9b\uff0e \u8001\u592b\u7684\u6697\u5668\uff0e\u5c45\u7136\u4f24\u4e0d\u5230\u4ed6\uff01\u201d \u3000\u3000\u4ed6\u54ea\u91cc\u77e5\u9053\uff0c\u6b64\u523b\u7684\u80e1\u67cf\u9f84\uff0c\u56e0\u53c8\u968f\u56db\u7edd\u8001\u4eba\u4e60\u5f97\u4e0d\u5c11\u6b66\u529f\uff0e\u8eab\u624b\u6bd4\u4e09\u5e74\u524d\u53c8\u4e0d\u77e5\u8fdb\u6b65 \u4e86\u591a\u5c11\u3002 \u3000\u3000\u6bd2\u706b\u6210\u5168\u5c82\u80fd\u5c31\u6b64\u5e72\u4f11\uff0c\u53f3\u624b\u4e00\u626c\uff0c\u4e00\u9897\u9e21\u86cb\u5927\u5c0f\u7684\u91d1\u7403\uff0e\u76f4\u5411\u80e1\u67cf\u9f84\u95ea\u7535\u822c\u7838\u53bb\u3002 \u3000\u3000\u80e1\u67cf\u9f84\u53cd\u624b\u4e00\u62d0\uff0e\u53ea\u542c\u201c\u94ee\u201d\u7684\u4e00\u58f0\uff0c\u6b63\u597d\u51fb\u4e2d\u91d1\u7403\u3002 \u3000\u3000\u90a3\u91d1\u7403\u5403\u94c1\u62d0\u4e00\u51fb\uff0c\u5f53\u573a\u659c\u98de\u51fa\u4e09\u56db\u4e08\u8fdc\uff0c\u843d\u5730\u4e4b\u540e\uff0c\u987f\u65f6\u7206\u5217\u6210\u4e00\u56e2\u8000\u773c\u7684\u706b\u7130\uff0c\u7adf \u628a\u9644\u8fd1\u7684\u8349\u5730\u70e7\u4e86\u4e08\u4f59\u65b9\u5706\u7684\u4e00\u5927\u7247\u3002 \u3000\u3000\u6bd2\u706b\u6210\u5168\u8d8a\u53d1\u5403\u60ca\uff0c\u559d\u9053\uff1a\u201c\u4f60\u5230\u5e95\u662f\u8c01\uff1f\u201d \u3000\u3000\u80e1\u67cf\u9f84\u9053\uff1a\u201c\u5728\u4e0b\u73b0\u5728\u53ea\u662f\u548c\u4f60\u5bf9\u654c\uff0c\u4f55\u5fc5\u95ee\u5728\u4e0b\u662f\u8c01\uff1f\u201d \u3000\u3000\u8c01\u77e5\u6bd2\u706b\u6210\u5168\u7adf\u4e00\u58f0\u4e0d\u54cd\u9000\u56de\u9634\u624b\u4e00\u9b54\u8eab\u8fb9\u3002 \u3000\u3000\u9634\u624b\u4e00\u9b54\u6123\u4e86\u6123\u9053\uff1a\u201c\u6210\u5144\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u6253\u4e86\uff1f\u201d \u3000\u3000\u6bd2\u706b\u6210\u5168\u9053\uff1a\u201c\u9634\u624b\u5144\u521a\u624d\u4e00\u5b9a\u770b\u5230\u4e86\uff0e\u8fd9\u5bb6\u4f19\u7684\u6b66\u529f\uff0c\u6bd4\u771f\u7684\u80e1\u67cf\u9f84\u8fd8\u8981\u9ad8\u51fa\u8bb8\u591a\uff0c \u518d\u6253\u53ea\u6015\u4e5f\u6253\u4e0d\u51fa\u4ec0\u4e48\u660e\u5802\u6765\u3002\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u4e24\u773c\u8f6c\u4e86\u51e0\u8f6c\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u4ed6\u53c8\u4f55\u5fc5\u5047\u5192\u80e1\u67cf\u9f84\uff1f\u201d \u3000\u3000\u201c\u90a3\u5c31\u4e0d\u662f\u6210\u67d0\u6240\u80fd\u77e5\u9053\u7684\u4e86\uff1f\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u5012\u63d0\u62c2\u5c18\uff0c\u5411\u524d\u8d70\u51fa\u51e0\u6b65\u9053\uff1a\u201c\u5f85\u8001\u592b\u548c\u4ed6\u62fc\u51e0\u62db\u8bd5\u8bd5\uff01\u201d \u3000\u3000\u80e1\u67cf\u9f84\u4f9d\u7136\u5de6\u62d0\u53f3\u5251\uff0c\u84c4\u52bf\u4ee5\u5f85\u3002 \u3000\u3000\u9634\u624b\u4e00\u9b54\u6709\u5982\u6b7b\u7070\u822c\u7684\u53cc\u988a\uff0c\u62bd\u6410\u4e86\u51e0\u4e0b\u9053\uff1a\u201c\u4f60\u65e2\u7136\u6b66\u529f\u4e0d\u5728\u80e1\u67cf\u9f84\u4e4b\u4e0b\uff0c\u5c31\u5b9e\u5728\u72af \u4e0d\u7740\u518d\u5192\u4ed6\u4eba\u4e4b\u540d\uff0e\u5148\u63a5\u8001\u592b\u4e09\u62db\u8bd5\u8bd5\uff01\u201d \u3000\u3000\u8bf4\u7740\u62c2\u5c18\u4e00\u6296\uff0c\u5148\u662f\u6a2a\u626b\uff0c\u534a\u8def\u53c8\u8f6c\u4e3a\u76f4\u51fb\uff0c\u631f\u7740\u9510\u98ce\u6012\u5578\uff0c\u5f53\u5934\u7f69\u4e0b\u3002 \u3000\u3000\u80e1\u67cf\u9f84\u94c1\u62d0\u4e00\u62a1\uff0c\u65bd\u51fa\u4e00\u62db\u201c\u4e07\u8702\u5bfb\u7a74\u201d\uff0c\u8fce\u4e86\u4e0a\u53bb\u3002 \u3000\u3000\u5c82\u77e5\u9634\u624b\u4e00\u9b54\u53ea\u662f\u865a\u62db\uff0e\u90a3\u62c2\u5c18\u62db\u672f\u5e76\u672a\u4f7f\u6ee1\uff0c\u75be\u6536\u518d\u51fa\uff0c\u500f\u5ffd\u95f4\u7adf\u7136\u626b\u5411\u80e1\u67cf\u9f84\u4e0b\u76d8\u3002 \u3000\u3000\u80e1\u67cf\u9f84\u94c1\u62d0\u5df2\u51fa\uff0c\u6765\u4e0d\u53ca\u53d8\u62db\uff0e\u957f\u5251\u6025\u6025\u65bd\u51fa\u4e00\u62db\u201c\u6d6a\u5377\u6d41\u7802\u201d\uff0e\u75be\u5411\u62c2\u5c18\u8fce\u53bb\u3002 \u3000\u3000\u957f\u5251\u548c\u62c2\u5c18\u4e00\u63a5\uff0c\u867d\u672a\u53d1\u51fa\u58f0\u97f3\uff0c\u4f46\u80e1\u67cf\u9f84\u5374\u7a81\u611f\u53f3\u81c2\u6709\u4e0d\u5c11\u5904\u5947\u75db\u5f7b\u9aa8\u3002\u5f88\u663e\u7136\u7684\uff0e \u4ed6\u7684\u53f3\u81c2\u5df2\u88ab\u62c2\u5c18\u626b\u4e2d\u591a\u5904\u3002 \u3000\u3000\u9634\u624b\u4e00\u9b54\u6b63\u6b32\u518d\u653b\uff0c\u5374\u5728\u5927\u60ca\u5931\u8272\u4e4b\u4e0b\uff0c\u53cd\u800c\u6025\u6025\u5411\u540e\u8dc3\u9000\u3002 \u3000\u3000\u539f\u6765\u4ed6\u90a3\u62c2\u5c18\u5df2\u88ab\u80e1\u67cf\u9f84\u7684\u957f\u5251\u62e6\u8170\u523a\u65ad\uff0c\u53ea\u5269\u4e0b\u534a\u622a\u3002\u800c\u80e1\u67cf\u9f84\u88ab\u626b\u4e2d\u53f3\u81c2\uff0e\u53ea\u662f\u62c2 \u5c18\u88ab\u524a\u65ad\u540e\u7684\u4f59\u529b\u53cd\u5f39\uff0c\u5426\u5219\u5c82\u6b62\u662f\u5947\u75db\u5f7b\u9aa8\u800c\u5df2\u3002 \u3000\u3000\u9634\u624b\u4e00\u9b54\u6700\u62ff\u624b\u7684\u662f\u201c\u9634\u98ce\u638c\u201d\uff0c\u4e0d\u8fc7\u4ed6\u51b3\u5b9a\u628a\u7edd\u62db\u7559\u5728\u6700\u540e\u624d\u65bd\u51fa\uff0c\u56e0\u4e4b\uff0c\u8f6c\u5934\u671b\u5411 \u4e0a\u65b9\u9053\uff1a\u201c\u706b\u603b\u5cd2\u4e3b\uff0c\u5feb\u5feb\u65bd\u653e\u795e\u866b\u653b\u51fb\uff01\u201d \u3000\u3000\u5c82\u77e5\u8fd9\u65f6\u706b\u83bd\u548c\u725b\u5954\u90fd\u5df2\u5931\u53bb\u6240\u5728\u3002 \u3000\u3000\u9634\u624b\u4e00\u9b54\u4e0d\u89c9\u5927\u611f\u60ca\u5f02\uff0c\u7acb\u5373\u9ad8\u58f0\u9053\uff1a\u201c\u706b\u603b\u5cd2\u4e3b\u3001\u725b\u4eb2\u738b\uff0c\u4f60\u4eec\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u201d \u3000\u3000\u53ea\u542c\u4e00\u4e2a\u82cd\u8001\u800c\u53c8\u6c89\u6d51\u7684\u58f0\u97f3\u9053\uff1a\u201c\u4ed6\u4eec\u90fd\u5728\u8fd9\u91cc\uff0c\u73b0\u5728\u5c31\u8ba9\u4f60\u770b\u770b\uff01\u201d \u3000\u3000\u8bf4\u8bdd\u95f4\uff0c\u7531\u5ced\u58c1\u4e0a\u90a3\u5757\u5927\u77f3\u540e\u63a2\u51fa\u4e24\u4e2a\u5934\u6765\uff0c\u6b63\u662f\u706b\u83bd\u548c\u725b\u5954\u3002 \u3000\u3000\u9634\u624b\u2014\u9b54\u8138\u8272\u4e00\u53d8\uff0c\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4f4d\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d \u3000\u3000\u706b\u83bd\u76f4\u7740\u55d3\u95e8\u53eb\u9053\uff1a\u201c\u6709\u4f4d\u8001\u82f1\u96c4\u628a\u672c\u603b\u5cd2\u4e3b\u548c\u725b\u4eb2\u738b\u5236\u4f4f\u4e86\uff01\u201d \u3000\u3000\u201c\u4ed6\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u672c\u603b\u5cd2\u4e3b\u548c\u725b\u4eb2\u738b\u90fd\u4e0d\u8ba4\u8bc6\u3002\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u8d8a\u53d1\u9a87\u5f02\uff0c\u6b64\u4eba\u80fd\u65e0\u58f0\u65e0\u606f\u5236\u4f4f\u725b\u5954\u548c\u706b\u83bd\uff0c\u6b66\u529f\u4e4b\u9ad8\uff0e\u4e0d\u96be\u60f3\u89c1\u3002\u5c24\u5176\u6b64\u4eba \u662f\u5982\u4f55\u767b\u4e0a\u5ced\u58c1\u7684\uff0c\u7adf\u7136\u65e0\u4eba\u5bdf\u89c9\uff0c\u5c31\u66f4\u4e0d\u53ef\u601d\u8bae\u4e86\u3002 \u3000\u3000\u4ed6\u5e72\u54b3\u4e86\u4e24\u58f0\u9053\uff1a\u201c\u7a76\u7adf\u4f55\u65b9\u9ad8\u4eba\uff0c\u8fd8\u8bf7\u73b0\u8eab\u76f8\u89c1\uff01\u201d \u3000\u3000\u5ced\u58c1\u4e0a\u7684\u5927\u77f3\u540e\uff0c\u679c\u7136\u53c8\u63a2\u51fa\u4e00\u4e2a\u5934\u6765\uff0c\u8d6b\u7136\u662f\u957f\u767d\u795e\u53df\u5e9e\u58eb\u51b2\u3002 \u3000\u3000\u8fd9\u65f6\u6700\u611f\u60ca\u559c\u7684\u83ab\u8fc7\u4e8e\u9ea6\u5c0f\u660e\u548c\u82d7\u7d20\u82d3\uff0c\u4f46\u4e8c\u4eba\u5374\u5e76\u672a\u8868\u793a\u4ec0\u4e48\u3002 \u3000\u3000\u9634\u624b\u4e00\u9b54\u5446\u4e86\u5446\u9053\uff1a\u201c\u5e9e\u5144\uff0c\u4f60\u8fd9\u7b97\u4f55\u610f\uff1f\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u51b7\u54fc\u4e00\u58f0\u9053\uff1a\u201c\u5c11\u6765\u8fd9\u5957\uff01\u8001\u592b\u4ec0\u4e48\u65f6\u5019\u66fe\u548c\u4f60\u8fd9\u79cd\u4eba\u79f0\u5144\u9053\u5f1f\u8fc7\uff1f\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u987f\u4e86\u987f\u9053\uff1a\u201c\u597d\u5427\uff01\u65e2\u7136\u5982\u6b64\uff0c\u8001\u592b\u4e5f\u4e0d\u60f3\u9ad8\u6500\uff0e\u4f60\u628a\u4ed6\u4eec\u4e8c\u4eba\u5236\u4f4f\uff0c\u5fc5\u662f\u51b2 \u7740\u8001\u592b\u6765\u7684\uff01\u201d \u3000\u3000\u201c\u4f60\u4e5f\u4e0d\u6492\u6ce1\u5c3f\u7167\u7167\uff0c\u591f\u4e0d\u591f\u8d44\u683c\u8ba9\u8001\u592b\u51b2\u7740\u4f60\u6765\uff01\u201d \u3000\u3000\u201c\u90a3\u4e48\u5c0a\u9a7e\u662f\u51b2\u7740\u8c01\u6765\u7684\uff1f\u201d \u3000\u3000\u201c\u8001\u592b\u770b\u4e0d\u60ef\u8fd9\u4e24\u4e2a\u72d7\u4e1c\u897f\uff0c\u5c45\u7136\u6765\u5230\u4e2d\u539f\u4e3a\u975e\u4f5c\u6b79\uff0c\u5174\u98ce\u4f5c\u6d6a\uff01\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u8bf4\u8bdd\u95f4\u5df2\u628a\u706b\u83bd\u3001\u725b\u5954\u70b9\u4e86\u7a74\u9053\uff0c\u4eba\u4e5f\u8ddf\u7740\u843d\u4e0b\u5730\u6765\u3002 \u3000\u3000\u9634\u624b\u4e00\u9b54\u2014\u89c1\u4e0d\u5999\uff0c\u7acb\u5373\u5411\u6bd2\u706b\u6210\u5168\u4f7f\u4e00\u4e2a\u773c\u8272\uff0c\u4e24\u4eba\u5239\u65f6\u5954\u8fdb\u6d1e\u4e2d\u3002 \u3000\u3000\u80e1\u67cf\u9f84\u548c\u9ea6\u5c0f\u660e\u968f\u5373\u5411\u524d\u89c1\u793c\u3002 \u3000\u3000\u82d7\u7d20\u82d3\u4e5f\u6765\u62dc\u89c1\u5e08\u7236\u3002 \u3000\u3000\u5e9e\u58eb\u51b2\u5230\u8fd9\u65f6\u624d\u5403\u60ca\u7684\u95ee\u9053\uff1a\u201c\u5c0a\u9a7e\u771f\u662f\u80e1\u76df\u4e3b\u5417\uff1f\u7a76\u7adf\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u62a2\u7740\u628a\u80e1\u67cf\u9f84\u4e3a\u56db\u7edd\u8001\u4eba\u6240\u6551\u7684\u7ecf\u8fc7\uff0c\u6458\u8981\u8bf4\u4e86\u4e00\u904d\u3002 \u3000\u3000\u5e9e\u58eb\u51b2\u53ea\u542c\u5f97\u4e5f\u5927\u611f\u6115\u7136\uff0c\u591a\u65f6\uff0c\u624d\u50cf\u81ea\u8a00\u81ea\u8bed\u822c\u9053\uff1a\u201c\u539f\u6765\u56db\u7edd\u8001\u4eba\u7adf\u7136\u771f\u6d3b\u5728\u4e16 \u4e0a\uff1f\u2026\u2026\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u5fd9\u9053\uff1a\u201d\u5e9e\u8001\u4f2f\u53ef\u66fe\u89c1\u8fc7\u56db\u7edd\u8001\u4eba\uff1f\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u9053\uff1a\u201c\u8001\u592b\u4e5f\u53ea\u662f\u7531\u4f20\u95fb\u4e2d\u5f97\u77e5\u6b66\u6797\u4e2d\u767e\u5e74\u524d\u6709\u8fd9\u6837\u4e00\u4f4d\u76d6\u4e16\u9ad8\u4eba\uff0c\u8001\u592b\u867d\u7136\u5e74 \u7eaa\u4e0d\u8f7b\uff0c\u4f46\u5f53\u5e74\u5728\u884c\u8d70\u6b66\u6797\u65f6\uff0c\u8fd9\u4f4d\u524d\u8f88\u5374\u65e9\u5df2\u4e0d\u5728\u6b66\u6797\u51fa\u73b0\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u5e9e\u8001\u4f2f\u600e\u4f1a\u5230\u8fd9\u91cc\u6765\uff1f\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u9053\uff1a\u201c\u8fd8\u4e0d\u662f\u4e3a\u597d\u5947\u800c\u6765\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u4e8b\u5f15\u8d77\u8001\u524d\u8f88\u7684\u597d\u5947\uff1f\u201d \u3000\u3000\u201c\u8001\u592b\u4eca\u5929\u4e00\u65e9\u5c31\u5728\u5c71\u4e0b\u53d1\u73b0\u4f60\u548c\u82d3\u513f\u968f\u540c\u80e1\u76df\u4e3b\u5165\u5c71\u800c\u6765\uff0c\u5f53\u65f6\u5fc3\u60f3\u80e1\u76df\u4e3b\u5df2\u6b7b\uff0e\u600e \u4f1a\u53c8\u51fa\u73b0\u4e00\u4e2a\u76f8\u540c\u7684\u4eba\uff0e\u5185\u5fc3\u96be\u514d\u8d77\u4e86\u6000\u7591\uff0e\u4e8e\u662f\u5c31\u8ddf\u8e2a\u4f60\u4eec\u800c\u6765\uff0c\u6b63\u597d\u5c31\u9047\u4e0a\u4e86\u8fd9\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u80e1\u67cf\u9f84\u8fde\u5fd9\u62b1\u62f3\u4e00\u793c\u9053\uff1a\u201c\u4eca\u5929\u82e5\u4e0d\u662f\u8001\u524d\u8f88\u65bd\u4ee5\u63f4\u624b\uff0e\u540e\u679c\u5982\u4f55\uff0e\u5b9e\u5728\u96be\u4ee5\u9884\u6599\uff0e\u76f8 \u63f4\u4e4b\u60c5\uff0c\u665a\u8f88\u4eec\u65e5\u540e\u5fc5\u5f53\u91cd\u8c22\uff01\u201d \u5367\u9f99\u751f\u300a\u5999\u7edd\u5929\u9999\u300b \u7b2c\u4e8c\u5341\u516b\u56de\u3000\u518d\u8e48\u8ff7\u8c37 \u3000\u3000\u5e9e\u58eb\u51b2\u5475\u5475\u7b11\u9053\uff1a\u201c\u80e1\u76df\u4e3b\u4f55\u5fc5\u8fc7\u8c26\uff0c\u5c0a\u592b\u4eba\u8c37\u5bd2\u9999\u662f\u8001\u592b\u7684\u5e72\u5973\u513f\uff0c\u7b97\u6765\u4f60\u4e5f\u4e0d\u662f\u5916 \u4eba\uff0c\u81ea\u5df1\u4eba\u54ea\u7528\u5ba2\u6c14\uff01\u201d \u3000\u3000\u80e1\u67cf\u9f84\u65e9\u5df2\u542c\u9ea6\u5c0f\u660e\u63d0\u8fc7\u6b64\u4e8b\uff0c\u53c8\u62b1\u62f3\u4e00\u793c\u9053\uff1a\u201c\u524d\u8f88\u5bf9\u8d31\u5185\u8bf8\u591a\u7231\u62a4\u4e0e\u7167\u987e\uff0c\u665a\u8f88\u611f \u6fc0\u975e\u6d45\uff01\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u957f\u957f\u53f9\u606f\u4e00\u58f0\u9053\uff1a\u201c\u53ef\u60dc\u9999\u513f\u5df2\u7ecf\u5728\u5929\u53f0\u4e07\u82b1\u5bab\u9999\u6d88\u7389\u6b92\u4e86\u3002\u8001\u592b\u662f\u5728\u5979\u6b7b\u540e\u624d \u628a\u5979\u6536\u4f5c\u5e72\u5973\u513f\u7684\u3002\u5728\u5979\u6d3b\u7740\u7684\u65f6\u5019\uff0c\u5e76\u6ca1\u542c\u5230\u5979\u53eb\u8001\u592b\u4e00\u58f0\u2018\u5e72\u7239\u2019\u2026\u2026\u201d \u3000\u3000\u80e1\u67cf\u9f84\u6b63\u8272\u9053\uff1a\u201c\u4e0d\u7ba1\u5982\u4f55\uff0c\u665a\u8f88\u662f\u6c38\u8fdc\u611f\u6fc0\u524d\u8f88\u7684\u3002\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u53c8\u53f9\u606f\u2014\u58f0\u9053\uff1a\u201c\u4f60\u8d8a\u8fd9\u6837\u8bf4\u8bdd\uff0c\u8001\u592b\u5c31\u8d8a\u611f\u5230\u60ed\u6127\u3002\u201d \u3000\u3000\u201c\u8001\u524d\u8f88\u4f55\u51fa\u6b64\u8a00\uff1f\u201d \u3000\u3000\u201c\u9999\u513f\u4e34\u7ec8\u524d\uff0c\u66fe\u8981\u6c42\u8001\u592b\u5c06\u5979\u7684\u9057\u4f53\u5e26\u8d70\uff0c\u8001\u592b\u5e76\u6ca1\u5b8c\u6210\u5979\u7684\u9057\u5fd7\uff0e\u96be\u9053\u8fd8\u4e0d\u8be5\u611f\u5230 \u60ed\u6127\uff1f\u201d \u3000\u3000\u201c\u665a\u8f88\u4e5f\u5bf9\u6b64\u4e8b\u611f\u5230\u5947\u602a\uff0c\u53ef\u60dc\u884c\u52a8\u53d7\u5230\u5bb6\u5e08\u9650\u5236\uff0c\u65e0\u6cd5\u67e5\u660e\u539f\u56e0\u3002\u524d\u8f88\uff0c\u7fce\u513f\u76ee\u524d\u7684 \u60c5\u51b5\u5982\u4f55\uff1f\u201d \u3000\u3000\u201c\u7fce\u513f\u7531\u8001\u592b\u5bc4\u6258\u5728\u4e00\u4f4d\u53cb\u4eba\u5bb6\u91cc\uff0c\u7167\u987e\u5f97\u5f88\u5468\u5230\uff0c\u4f60\u8981\u4e0d\u8981\u53bb\u770b\u770b\uff1f\u53ea\u662f\u8def\u7a0b\u7a0d\u8fdc\u4e86 \u70b9\uff0c\u5f80\u8fd4\u81f3\u5c11\u9700\u4e09\u4e94\u5929\u65f6\u95f4\u3002\u201d \u3000\u3000\u80e1\u67cf\u9f84\u62c5\u5fc3\u8bef\u4e86\u5c11\u6797\u638c\u95e8\u5929\u7985\u5927\u5e08\u7684\u4f1a\u5408\u4e4b\u671f\uff0e\u53c8\u8bb0\u6302\u7740\u949f\u2014\u8c6a\u7b49\u4eba\u7684\u5b89\u5371\uff0e\u53ea\u597d\u8bf4\u9053\uff1a \u201c\u65e2\u7136\u8001\u524d\u8f88\u7684\u53cb\u4eba\u5bf9\u7fce\u513f\u5df2\u6709\u59a5\u5584\u7167\u987e\uff0e\u665a\u8f88\u6682\u65f6\u5c31\u7528\u4e0d\u7740\u53bb\u770b\u4ed6\u4e86\uff0e\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u7565\u4e00\u6c89\u541f\u9053\uff1a\u201c\u542c\u4f60\u4eec\u65b9\u624d\u7684\u8bed\u6c14\uff0e\u662f\u6765\u5411\u9634\u624b\u4e00\u9b54\u8ba8\u53d6\u89e3\u836f\uff0e\u4f60\u4eec\u5230\u5e95\u60f3\u8981\u4ec0 \u4e48\u89e3\u836f\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u662f\u2018\u5411\u5fc3\u9732\u2019\u89e3\u836f\u3002\u56e0\u4e3a\u665a\u8f88\u7684\u56db\u4f4d\u5e08\u5144\uff0c\u76ee\u524d\u90fd\u88ab\u970d\u5143\u4f3d\u4ee5\u2018\u5411\u5fc3\u9732\u2019 \u63a7\u5236\u5728\u8ff7\u8e2a\u8c37\u3002\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u671b\u4e86\u671b\u4e5d\u5929\u7384\u9634\u6d1e\u5e9c\u6d1e\u53e3\u9053\uff1a\u201c\u9634\u624b\u4e00\u9b54\u5df2\u7ecf\u9003\u8fdb\u6d1e\u53bb\u4e86\uff0c\u53ea\u6015\u5f88\u96be\u518d\u8ba8\u53d6\u5230\u89e3 \u836f\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u665a\u8f88\u4eec\u53ef\u4ee5\u51b2\u6740\u8fdb\u53bb\uff0c\u73b0\u5728\u6d1e\u5185\u53ea\u5269\u4e0b\u9634\u624b\u4e00\u9b54\u548c\u6bd2\u706b\u6210\u5168\uff0c\u8fd8\u6709\u4ec0\u4e48\u53ef \u6015\u7684\uff1f\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u6447\u5934\u9053\uff1a\u201c\u8001\u592b\u529d\u4f60\u4eec\u8fd8\u662f\u522b\u8fdb\u53bb\u7684\u597d\u3002\u201d \u3000\u3000\u201c\u96be\u9053\u8fd8\u6709\u4ec0\u4e48\u987e\u8651\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u6709\u987e\u8651\uff0c\u636e\u8001\u592b\u6240\u77e5\uff0c\u6d1e\u5185\u673a\u5173\u57cb\u4f0f\u5f88\u591a\uff0c\u800c\u4e14\u654c\u6697\u6211\u660e\uff0c\u4f60\u4eec\u76f2\u76ee\u8fdb\u53bb\uff0c\u5982\u4f55 \u9632\u5f97\u4e86\u4ed6\u4eec\u7684\u6697\u4e2d\u65bd\u88ad\uff1f\u5c24\u5176\u6210\u5168\u7684\u6697\u5668\uff0c\u66f4\u662f\u9632\u4e0d\u80dc\u9632\uff0e\u201d \u3000\u3000\u201c\u53ef\u662f\u4e0d\u8fdb\u53bb\u53c8\u5982\u4f55\u80fd\u5f97\u5230\u89e3\u836f\uff1f\u201d \u3000\u3000\u201c\u5982\u679c\u8fdb\u53bb\u53cd\u800c\u8981\u6789\u9001\u2014\u547d\uff0c\u53c8\u8be5\u600e\u4e48\u8bf4\uff1f\u201d \u3000\u3000\u80e1\u67cf\u9f84\u4e5f\u9053\uff1a\u201c\u8001\u524d\u8f88\u8bf4\u5f97\u5bf9\uff0c\u54b1\u4eec\u8fd8\u662f\u522b\u8fdb\u53bb\u7684\u597d\u3002\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u6c89\u541f\u4e86\u534a\u664c\u9053\uff1a\u201c\u4f60\u4eec\u4e0d\u7528\u7740\u6025\uff0c\u4e5f\u8bb8\u8001\u592b\u6709\u529e\u6cd5\u627e\u5230\u2018\u5411\u5fc3\u9732\u2019\u7684\u89e3\u836f\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u559c\u9053\uff1a\u201c\u8001\u524d\u8f88\u5230\u54ea\u91cc\u627e\uff1f\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u9053\uff1a\u201c\u5c31\u662f\u8d1f\u8d23\u7167\u987e\u7fce\u513f\u7684\u90a3\u4f4d\u53cb\u4eba\uff0c\u8fd9\u4eba\u7cbe\u4e8e\u533b\u9053\uff0c\u66fe\u7814\u5236\u4e86\u4e0d\u5c11\u72ec\u95e8\u836f\u7269\uff0c \u8bf4\u4e0d\u5b9a\u4ed6\u90a3\u91cc\u5c31\u6709\u89e3\u836f\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u665a\u8f88\u4eec\u6025\u987b\u5f97\u5230\u89e3\u836f\uff0c\u8fd8\u671b\u8001\u524d\u8f88\u80fd\u65e9\u65e5\u62ff\u5230\u3002\u201d \u3000\u3000\u201c\u5982\u679c\u8001\u592b\u62ff\u5230\u4e86\uff0c\u600e\u6837\u9001\u7ed9\u4f60\u4eec\u5462\uff1f\u201d \u3000\u3000\u201c\u8ff7\u8e2a\u8c37\u5916\u6709\u4e2a\u767d\u59d1\u5bfa\uff0c\u8001\u524d\u8f88\u5c31\u5230\u90a3\u91cc\u597d\u4e86\uff0c\u518d\u4e0d\u7136\u9001\u5230\u4e07\u6708\u5ce1\u4e5f\u53ef\u3002\u201d \u3000\u3000\u201c\u597d\uff0c\u5c31\u8fd9\u4e48\u529e\uff01\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u8bf4\u5b8c\u8bdd\uff0c\u98de\u8eab\u8dc3\u4e0a\u5ced\u58c1\u4e0a\u90a3\u5757\u5927\u77f3\uff0e\u7136\u540e\u4e00\u624b\u63d0\u7740\u706b\u83bd\uff0c\u4e00\u624b\u63d0\u7740\u725b\u5954\uff0c\u518d\u843d\u4e0b \u5730\u6765\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u8fd8\u6709\u4e00\u4e2a\u9ed1\u9b54\u65f6\u5bc5\uff0c\u5e72\u8106\u5c31\u4e00\u8d77\u5904\u7f6e\u3002\u201d \u3000\u3000\u8bf4\u7740\u628a\u660f\u8ff7\u4e00\u65c1\u7684\u9ed1\u9b54\u65f6\u5bc5\u4e5f\u63d0\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u5e9e\u58eb\u51b2\u5c06\u706b\u83bd\u3001\u725b\u5954\u6254\u5230\u5730\u4e0a\u9053\uff1a\u201c\u4f60\u4eec\u60f3\u600e\u6837\u5904\u7f6e\u4ed6\u4eec\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4e00\u8d77\u5bb0\u6389\uff0c\u6700\u5e72\u8106\u4e0d\u8fc7\u4e86\u3002\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u6447\u5934\u9053\uff1a\u201c\u4e0a\u5929\u6709\u597d\u751f\u4e4b\u5fb7\uff0c\u4ed6\u4eec\u867d\u7136\u5e76\u975e\u5584\u7c7b\uff0c\u4f46\u8fd8\u7f6a\u4e0d\u81f3\u6b7b\uff0c\u4f9d\u8001\u592b\u4e4b\u610f\u8fd8 \u662f\u653e\u4e86\u4ed6\u4eec\u7684\u597d\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u4e0d\u4fbf\u575a\u6301\uff0c\u9ed8\u4e86\u9ed8\u9053\uff1a\u201c\u53ef\u662f\u8fd9\u4e2a\u4ece\u82d7\u7586\u6765\u7684\uff0c\u7ecf\u5e38\u4ee5\u866b\u6bd2\u5bb3\u4eba\uff0c\u653e\u4e86\u4ed6\u53c8\u4e0d\u77e5 \u6709\u591a\u5c11\u4eba\u53d7\u5bb3\u3002\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u9053\uff1a\u201c\u4ed6\u7684\u6bd2\u86ca\uff0c\u636e\u8bf4\u4e0a\u6b21\u5728\u8ff7\u8e2a\u8c37\u5df2\u5927\u534a\u8017\u635f\uff0c\u6b64\u523b\u6240\u5269\u65e0\u51e0\uff0c\u8001\u592b\u65b9\u624d\u5df2\u628a \u4ed6\u5269\u4e0b\u7684\u5b8c\u5168\u6d88\u706d\u4e86\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u7ec8\u4e8e\u653e\u4e0b\u5fc3\uff0c\u63a5\u7740\u4e3a\u65f6\u5bc5\u3001\u706b\u83bd\u3001\u725b\u5954\u4e09\u4eba\u89e3\u5f00\u7a74\u9053\u3002 \u3000\u3000\u4ed6\u4eec\u4e00\u89c1\u5e9e\u58eb\u51b2\u3001\u80e1\u67cf\u9f84\u3001\u9ea6\u5c0f\u660e\u3001\u82d7\u7d20\u82d3\u5168\u5728\u9762\u524d\uff0c\u53ea\u6709\u4f4e\u7740\u5934\u4e0d\u6562\u542d\u58f0\u3002 \u3000\u3000\u5e9e\u58eb\u51b2\u9053\uff1a\u201c\u4f60\u4eec\u73b0\u5728\u8fd8\u60f3\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u4e09\u4eba\u4f9d\u7136\u4f4e\u5934\u4e0d\u8bed\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u4e24\u773c\u4e00\u77aa\uff0e\u9a82\u9053\uff1a\u201c\u4f60\u4eec\u4e09\u4e2a\u738b\u516b\u86cb\uff0c\u82e5\u4e0d\u662f\u5e9e\u524d\u8f88\u5f00\u6069\uff0c\u4eca\u5929\u54ea\u6709\u4f60\u4eec\u7684\u72d7\u547d \u597d\u6d3b\uff01\u73b0\u5728\u7531\u6211\u6253\u4e00\u4e2a\u8d70\u4e00\u4e2a\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u7740\uff0c\u626c\u624b\u4fbf\u5411\u9ed1\u9b54\u65f6\u5bc5\u9762\u988a\u4e0a\u4e00\u638c\u63b4\u53bb\u3002 \u3000\u3000\u9ed1\u9b54\u65f6\u5bc5\u51b7\u4e0d\u9632\u88ab\u6253\u4e86\u4e00\u4e2a\u8ddf\u5934\uff0c\u4ee5\u4ed6\u5728\u9ed1\u9053\u4e0a\u7684\u8eab\u4efd\u5730\u4f4d\uff0c\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\u4f1a\u906d\u5230\u8fd9\u6837 \u7f9e\u8fb1\uff0c\u4f46\u5374\u53c8\u4e0d\u6562\u53cd\u6297\uff0c\u53ea\u6709\u6342\u7740\u9762\u988a\u8e09\u8dc4\u5954\u53bb\u3002 \u3000\u3000\u706b\u83bd\u7ad9\u5728\u7b2c\u4e8c\u540d\u3001\u4e00\u89c1\u9ea6\u5c0f\u660e\u51fa\u624b\u8f9b\u8fa3\uff0c\u4ed6\u662f\u82d7\u7586\u6765\u7684\uff0c\u867d\u7136\u8fdc\u79bb\u5bb6\u4e61\u4e22\u4eba\u4e22\u5728\u8fdc\u65b9\u65e0 \u6240\u8c13\uff0c\u4f46\u5374\u6015\u53d7\u76ae\u8089\u4e4b\u82e6\uff0c\u6025\u6025\u54a7\u4e86\u54a7\u86e4\u87c6\u5634\u9053\uff1a\u201c\u5c0f\u5b50\uff0c\u3000\u5343\u4e07\u624b\u4e0b\u7559\u60c5\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u53f1\u9053\uff1a\u201c\u7559\u4f60\u5976\u5976\u7684\u72d7\u718a\uff01\u201d \u3000\u3000\u8fb9\u8bf4\u8fb9\u731b\u8d77\u4e00\u638c\u63b4\u53bb\u3002 \u3000\u3000\u706b\u83bd\u867d\u8eab\u4e3a\u82d7\u7586\u706b\u4e91\u6d1e\u603b\u5cd2\u4e3b\uff0c\u9664\u4e86\u7cbe\u4e8e\u5e03\u866b\u653e\u866b\u5916\uff0c\u5e76\u65e0\u591a\u5927\u6b66\u529f\u57fa\u7840\uff0c\u88ab\u2014\u638c\u6253\u5f97 \u7adf\u7136\u6454\u4e86\u4e2a\u72d7\u5403\u5c4e\uff0c\u5f85\u722c\u8d77\u6765\u65f6\uff0c\u5df2\u662f\u6ee1\u5634\u9c9c\u8840\u76f4\u6d41\u3002 \u3000\u3000\u8fd9\u65f6\u7684\u725b\u5954\u65e9\u5df2\u5413\u5f97\u5c41\u6eda\u5c3f\u6d41\uff0c\u7adf\u7136\u201c\u5657\u901a\u201d\u4e00\u58f0\uff0c\u53cc\u819d\u8dea\u4e0b\uff0c\u6253\u7740\u54c6\u55e6\u9053\uff1a\u201c\u5c0f\u4fa0\u9976 \u547d\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u7b11\u9053\uff1a\u201c\u4e0d\u6562\u5f53\uff01\u4f60\u662f\u4eb2\u738b\uff0c\u6211\u4e0d\u8fc7\u4e00\u4ecb\u5e73\u6c11\uff0c\u54ea\u6709\u738b\u7237\u5411\u8001\u767e\u59d3\u4e0b\u8dea\u7684\uff1f\u201d \u3000\u3000\u725b\u5954\u53e9\u7740\u5934\u9053\uff1a\u201c\u90a3\u662f\u9a97\u4eba\u7684\u3002\u5c0f\u7684\u867d\u7136\u5230\u8fc7\u897f\u57df\uff0e\u5374\u4e0d\u662f\u897f\u57df\u7684\u4eb2\u738b\uff0c\u800c\u4e14\u897f\u57df\u597d\u50cf \u4e5f\u6ca1\u6709\u4eb2\u738b\uff0e\u201d \u3000\u3000\u201c\u90a3\u4f60\u4e3a\u4ec0\u4e48\u8981\u9a97\u4eba\uff1f\u201d \u3000\u3000\u201c\u8fd9\u5e74\u5934\u513f\u552f\u6709\u8fd9\u6837\u4eba\u5bb6\u624d\u77a7\u5f97\u8d77\uff0c\u4e5f\u624d\u80fd\u5403\u5f97\u5f00\u3002\u770b\u90a3\u4e9b\u6709\u5934\u6709\u8138\u6709\u529e\u6cd5\u7684\uff0c\u54ea\u4e00\u4e2a \u4e0d\u662f\u8fd9\u6837\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9a82\u9053\uff1a\u201c\u6eda\u5427\uff01\u201d \u3000\u3000\u725b\u5954\u8dd1\u4e86\u51e0\u6b65\uff0c\u53c8\u56de\u5934\u9053\uff1a\u201c\u4f60\u770b\u770b\uff0c\u5c0f\u7684\u8fd9\u4e0d\u5c31\u8d5a\u4fbf\u5b9c\u4e86\u5417\uff1f\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u9053\uff1a\u201c\u8001\u592b\u4e5f\u8981\u8d70\u4e86\uff0e\u8981\u4e0d\u8981\u4e5f\u6328\u4f60\u4e00\u8033\u5149\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u5c34\u5c2c\u7b11\u9053\uff1a\u201c\u5e9e\u8001\u4f2f\u600e\u4e48\u8ddf\u665a\u8f88\u5f00\u8fd9\u4e48\u5927\u7684\u73a9\u7b11\uff1f\u60a8\u5982\u679c\u624b\u75d2\uff0c\u665a\u8f88\u60c5\u613f\u6328\u4f60\u4e00 \u5bb6\u4f19\uff01\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u5b50\u8fd8\u771f\u6709\u4e00\u5957\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u9664\u4e86\u8eab\u4e0a\u4e00\u5957\uff0c\u8fd8\u6709\u51e0\u5957\uff0e\u53ef\u60dc\u6ca1\u5e26\u51fa\u6765\u3002\u201d \u3000\u3000\u80e1\u67cf\u9f84\u89c1\u8fd9\u4e00\u8001\u4e00\u5c11\uff0c\u5c45\u7136\u6ca1\u5927\u6ca1\u5c0f\u7684\u80e1\u626f\u8d77\u6765\uff0c\u5fd9\u9053\uff1a\u201c\u6b63\u597d\u987a\u8def\uff0c\u54b1\u4eec\u5c31\u9001\u8001\u524d\u8f88 \u2014\u7a0b\u3002\u201d \u3000\u3000\u80e1\u67cf\u9f84\u7b49\u4e09\u4eba\uff0c\u76f4\u5230\u51fa\u4e86\u5415\u6881\u5c71\uff0c\u624d\u548c\u5e9e\u58eb\u51b2\u5206\u624b\u3002 \u3000\u3000\u4e24\u5929\u540e\uff0c\u8fc7\u4e86\u592a\u539f\uff0c\u5f80\u5b9a\u8944\u65b9\u5411\u8fdb\u53d1\uff0c\u6765\u5230\u4e00\u5904\u5c71\u5761\u4e0a\uff0c\u5ffd\u542c\u9ea6\u5c0f\u660e\u5931\u58f0\u53eb\u9053\uff1a\u201c\u5e08\u5144\uff0c \u524d\u9762\u2014\u4eba\uff0c\u5f88\u50cf\u5999\u5999\u5eb5\u7684\u90a3\u4f4d\u8001\u5e08\u592a\uff01\u8fd8\u6709\uff0c\u90a3\u4f4d\u5f88\u50cf\u5e08\u5ac2\u7684\u9ed1\u8863\u8499\u9762\u5973\u5b50\u4e5f\u6765\u4e86\uff01\u201d \u3000\u3000\u80e1\u67cf\u9f84\u4e3e\u76ee\u671b\u53bb\uff0c\u679c\u7136\u5728\u524d\u9762\u5341\u4f59\u4e08\u5916\uff0c\u6765\u4e86\u4e24\u4e2a\u5973\u4eba\u3002\u524d\u9762\u4e00\u4eba\uff0c\u662f\u4f4d\u7f01\u8863\u8001\u5c3c\uff1b\u540e \u9762\u2014\u4eba\uff0e\u8eab\u7a7f\u9ed1\u8863\uff0c\u9ed1\u7eb1\u8499\u9762\uff0c\u4e0d\u8bba\u8eab\u6750\u4e3e\u6b62\uff0c\u679c\u7136\u9177\u4f3c\u8c37\u5bd2\u9999\u3002 \u3000\u3000\u987f\u65f6\uff0c\u80e1\u67cf\u9f84\u60c5\u4e0d\u81ea\u7981\u5185\u5fc3\u5927\u4e3a\u6fc0\u52a8\u8d77\u6765\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u518d\u9053\uff1a\u201c\u4ed6\u4eec\u597d\u50cf\u662f\u8fce\u9762\u800c\u6765\uff0e\u54b1\u4eec\u53ea\u7ba1\u6162\u6162\u8d70\uff0c\u5f88\u5feb\u4fbf\u4f1a\u78b0\u4e0a\u3002\u201d \u3000\u3000\u5c82\u77e5\u4ed6\u7684\u8bdd\u521a\u521a\u8bf4\u5b8c\uff0c\u90a3\u7f01\u8863\u8001\u5c3c\u548c\u9ed1\u8863\u8499\u9762\u5973\u5b50\u5df2\u8f6c\u5165\u53e6\u5916\u4e00\u6761\u5c94\u8def\u3002 \u3000\u3000\u80e1\u67cf\u9f84\u7acb\u5373\u52a0\u5feb\u811a\u6b65\u9053\uff1a\u201c\u54b1\u4eec\u5feb\u8ffd\uff01\u201d \u3000\u3000\u4e3a\u907f\u514d\u65bd\u5c55\u8f7b\u529f\u60ca\u52a8\u5bf9\u65b9\uff0c\u4e09\u4eba\u53ea\u80fd\u628a\u6b65\u4f10\u52a0\u5feb\uff0c\u5341\u51e0\u4e08\u7684\u8ddd\u79bb\uff0c\u5f88\u5feb\u4fbf\u5df2\u8ffd\u4e0a\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u53eb\u9053\uff1a\u201c\u8001\u5e08\u592a\u6162\u8d70\uff01\u201d \u3000\u3000\u7f01\u8863\u8001\u5c3c\u95fb\u58f0\u6b62\u6b65\uff0c\u8f6c\u8fc7\u8eab\u6765\u3002 \u3000\u3000\u90a3\u9ed1\u8863\u8499\u9762\u5973\u5b50\uff0c\u5219\u7d27\u7d27\u504e\u5728\u7f01\u8863\u8001\u5c3c\u8eab\u540e\u3002 \u3000\u3000\u5728\u8fd9\u5239\u90a3\uff0c\u80e1\u67cf\u9f84\u5df2\u5b8c\u5168\u770b\u6e05\uff0c\u90a3\u9ed1\u8863\u8499\u9762\u5973\u5b50\uff0c\u9664\u9762\u8c8c\u88ab\u9ed1\u7eb1\u7f69\u4f4f\u5916\uff0c\u8eab\u6750\u4e3e\u6b62\u7684\u786e \u7adf\u4e0e\u8c37\u5bd2\u9999\u4e0d\u5dee\u5206\u6beb\u3002 \u3000\u3000\u4ed6\u51e0\u6b21\u60f3\u51b2\u4e0a\u524d\u53bb\u63ed\u5f00\u5979\u7684\u9762\u7eb1\uff0c\u4fbf\u56e0\u788d\u4e8e\u7f01\u8863\u8001\u5c3c\u5728\u65c1\uff0c\u4e0d\u5f97\u5f3a\u81ea\u6291\u5236\u7740\u81ea\u5df1\u7684\u4e3e\u52a8\u3002 \u3000\u3000\u53ea\u89c1\u7f01\u8863\u8001\u5c3c\u53cc\u7738\u795e\u5149\u6e5b\u6e5b\uff0c\u7f13\u7f13\u63a0\u8fc7\u80e1\u67cf\u9f84\u4e09\u4eba\u4e00\u773c\u9053\uff1a\u201c\u9ea6\u5c0f\u65bd\u4e3b\u4e0d\u5728\u8ff7\u8e2a\u8c37\uff0c\u600e \u4e48\u6765\u5230\u4e86\u8fd9\u91cc\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u8eac\u8eab\u65bd\u4e00\u793c\u9053\uff1a\u201c\u7980\u8001\u5e08\u592a\uff0c\u665a\u8f88\u4eec\u73b0\u5728\u5df2\u7ecf\u4e0d\u5728\u8ff7\u8e2a\u8c37\u3002\u201d \u3000\u3000\u7f01\u8863\u8001\u5c3c\u6115\u7136\u9053\uff1a\u201c\u83ab\u975e\u4f60\u4eec\u7684\u7eff\u6797\u603b\u5be8\u5df2\u7ecf\u8f6c\u79fb\u5230\u522b\u5904\u53bb\u4e86\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u665a\u8f88\u4eec\u662f\u88ab\u970d\u5143\u4f3d\u8d76\u8d70\u4e86\u3002\u201d \u3000\u3000\u201c\u4f60\u4eec\u88ab\u8d76\u5230\u4ec0\u4e48\u5730\u65b9\uff1f\u201d \u3000\u3000\u201c\u5148\u662f\u4e07\u6708\u5ce1\uff0c\u518d\u5230\u767d\u59d1\u5bfa\uff0c\u81f3\u4e8e\u73b0\u5728\u5728\u54ea\u91cc\uff0e\u8fde\u665a\u8f88\u4e5f\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u4e0d\u77e5\u9053\uff1f\u201d \u3000\u3000\u201c\u665a\u8f88\u4eec\u53bb\u4e86\u4e00\u8d9f\u6c5f\u5357\uff0c\u73b0\u5728\u6b63\u662f\u7531\u6c5f\u5357\u8d76\u56de\u6765\uff0c\u6839\u672c\u5f04\u4e0d\u6e05\u949f\u526f\u76df\u4e3b\u4ed6\u4eec\u76ee\u524d\u662f\u5426\u4ecd \u5728\u767d\u59d1\u5bfa\u3002\u201d \u3000\u3000\u201c\u539f\u6765\u4f60\u4eec\u662f\u7531\u6c5f\u5357\u56de\u6765\uff0c\u90a3\u5c31\u8be5\u5feb\u5feb\u5230\u767d\u59d1\u5bfa\u548c\u4ed6\u4eec\u4f1a\u5408\uff0c\u4f60\u4eec\u5feb\u4e9b\u8d70\u5427\uff01\u201d \u3000\u3000\u201c\u4e0d\uff0c\u665a\u8f88\u8fd8\u6709\u4e8b\u60c5\u3002\u201d \u3000\u3000\u7f01\u8863\u8001\u5c3c\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u4f60\u6709\u4ec0\u4e48\u4e8b\u60c5\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u6b63\u8272\u9053\uff1a\u201c\u665a\u8f88\u4e0a\u6b21\u5230\u5999\u5999\u5eb5\uff0c\u4e3a\u7684\u5c31\u662f\u8981\u627e\u4f60\u8001\u4eba\u5bb6\u8eab\u540e\u90a3\u4f4d\u5973\u4fa0\uff0c\u5f53\u65f6\u4f60\u8001 \u4eba\u5bb6\u2014\u76f4\u4e0d\u627f\u8ba4\u5979\u5728\u5999\u5999\u5eb5\uff0c\u73b0\u5728\u6211\u4eec\u7ec8\u4e8e\u53c8\u9047\u89c1\u4e86\uff01\u201d \u3000\u3000\u7f01\u8863\u8001\u5c3c\u4e0d\u52a8\u58f0\u8272\u9053\uff1a\u201c\u4f60\u9047\u89c1\u5979\u4fbf\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u665a\u8f88\u5e0c\u671b\u5979\u80fd\u63ed\u5f00\u7f69\u9762\u9ed1\u7eb1\u3002\u201d \u3000\u3000\u7f01\u8863\u8001\u5c3c\u51b7\u7b11\u9053\uff1a\u201c\u5c82\u6709\u6b64\u7406\uff01\u5973\u4eba\u5bb6\u8499\u9762\u7f69\u9ed1\u7eb1\uff0c\u5fc5\u6709\u5979\u7684\u7528\u610f\u3002\u5982\u679c\u80fd\u63ed\u5f00\u8ba9\u522b\u4eba \u770b\uff0c\u5979\u53c8\u4f55\u5fc5\u591a\u6b64\u4e00\u4e3e\uff1f\u201d \u3000\u3000\u201c\u53ef\u662f\u8001\u5e08\u592a\u6709\u6240\u4e0d\u77e5\uff0c\u665a\u8f88\u6000\u7591\u5979\u5c31\u662f\u665a\u8f88\u7684\u80e1\u5e08\u5ac2\u8c37\u5bd2\u9999\u3002\u201d \u3000\u3000\u201c\u8c37\u5bd2\u9999\u4e0d\u662f\u5df2\u7ecf\u6b7b\u4e86\u5417\uff1f\u4e5f\u8bb8\u5979\u771f\u7684\u6709\u70b9\u50cf\uff0c\u4fbf\u4f60\u603b\u8be5\u77e5\u9053\u4eba\u6b7b\u662f\u4e0d\u80fd\u590d\u6d3b\u7684\u3002\u201d \u3000\u3000\u201c\u4e0d\u7ba1\u5979\u662f\u4e0d\u662f\uff0c\u665a\u8f88\u53ea\u60f3\u770b\u770b\u5979\u7684\u5e90\u5c71\u771f\u9762\u65e5\uff01\u201d \u3000\u3000\u201c\u6709\u8d2b\u5c3c\u5728\u6b64\uff0c\u8c01\u90fd\u522b\u60f3\u8ba9\u5979\u53d6\u4e0b\u7f69\u9762\u9ed1\u7eb1\u6765\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u60c5\u4e0d\u81ea\u7981\u5927\u58f0\u9053\uff1a\u201c\u8001\u5e08\u592a\uff0c\u4f60\u53ef\u77e5\u9053\u4eca\u5929\u662f\u4ec0\u4e48\u4eba\u6765\u4e86\uff1f\u201d \u3000\u3000\u7f01\u8863\u8001\u5c3c\u5fae\u4e00\u9519\u6115\u9053\uff1a\u201c\u4f60\u6562\u5728\u8d2b\u5c3c\u9762\u524d\u653e\u8086\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u665a\u8f88\u5c31\u662f\u501f\u5929\u505a\u80c6\u5b50\uff0c\u4e5f\u4e0d\u6562\u5728\u60a8\u9762\u524d\u65e0\u7406\u53d6\u95f9\uff0c\u665a\u8f88\u6307\u7684\u662f\u8fd9\u4e00\u4f4d\uff01\u201d \u3000\u3000\u4ed6\u8bf4\u7740\u56de\u8eab\u6307\u4e86\u6307\u80e1\u67cf\u9f84\u3002 \u3000\u3000\u80e1\u67cf\u9f84\u8fde\u5fd9\u4e5f\u5411\u7f01\u8863\u8001\u5c3c\u8eac\u8eab\u4e00\u793c\u3002 \u3000\u3000\u7f01\u8863\u8001\u5c3c\u671b\u5411\u80e1\u67cf\u9f84\u9053\uff1a\u201c\u4ed6\u662f\u8c01\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4ed6\u5c31\u662f\u624b\u521b\u8ff7\u8e2a\u8c37\u7684\u80e1\u524d\u76df\u4e3b\uff0c\u4e5f\u662f\u665a\u8f88\u7684\u5e08\u5144\u3002\u201d \u3000\u3000\u7f01\u8863\u8001\u5c3c\u8138\u4e0a\u770b\u4e0d\u51fa\u4efb\u4f55\u8868\u60c5\uff0c\u9ed8\u4e86\u4e00\u9ed8\u9053\uff1a\u201c\u80e1\u67cf\u9f84\u4e0d\u662f\u65e9\u5df2\u7ecf\u6b7b\u4e86\u5417\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4ed6\u88ab\u4e00\u4f4d\u524d\u8f88\u9ad8\u4eba\u6240\u6551\uff0c\u6240\u4ee5\u624d\u80fd\u6b7b\u800c\u590d\u751f\u3002\u201d \u3000\u3000\u7f01\u8863\u8001\u5c3c\u5148\u662f\u8138\u8272\u4e00\u53d8\uff0c\u4f46\u77ac\u5373\u53c8\u5e73\u9759\u4e0b\u6765\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u5c31\u7b97\u4ed6\u771f\u662f\u80e1\u67cf\u9f84\uff0c\u4f60\u4e5f\u6ca1 \u6709\u5728\u8d2b\u5c3c\u9762\u524d\u63d0\u4ed6\u7684\u5fc5\u8981\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u53ef\u662f\u4ed6\u60f3\u770b\u770b\u8fd9\u4f4d\u9ed1\u8863\u8499\u9762\u5973\u4fa0\u7684\u5e90\u5c71\u771f\u9762\u76ee\uff0c\u56e0\u4e3a\u8c37\u5bd2\u9999\u5c31\u662f\u4ed6\u7684\u592b\u4eba\uff0c \u522b\u4eba\u53ef\u4ee5\u4e0d\u5173\u5fc3\uff0e\u4ed6\u4e0d\u80fd\u4e0d\u5173\u5fc3\uff01\u201d \u3000\u3000\u7f01\u8863\u8001\u5c3c\u84e6\u5730\u6012\u8038\u53cc\u7709\uff0c\u559d\u9053\uff1a\u201c\u80e1\u8bf4\uff01\u80e1\u67cf\u9f84\u601d\u5ff5\u4ed6\u7684\u59bb\u5b50\uff0c\u662f\u4eba\u4e4b\u5e38\u60c5\uff0c\u4f46\u4ed6\u5374\u6ca1 \u6709\u7406\u7531\u628a\u522b\u7684\u5973\u4eba\u5f53\u6210\u81ea\u5df1\u7684\u59bb\u5b50\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u8bed\u6c14\u6073\u5207\u7684\u9053\uff1a\u201c\u8001\u5e08\u592a\u7684\u8bdd\u56fa\u7136\u6709\u7406\uff0c\u4f46\u80e1\u76df\u4e3b\u53ea\u662f\u60f3\u770b\u770b\u5979\uff0c\u5e76\u6ca1\u6709\u5176\u4ed6\u4e0d \u793c\u8c8c\u7684\u4e3e\u52a8\uff0c\u96be\u9053\u8001\u5e08\u592a\u8fde\u8fd9\u4e48\u4e00\u70b9\u8981\u6c42\u90fd\u4e0d\u80fd\u7b54\u5e94\uff1f\u201d \u3000\u3000\u201c\u8fd9\u662f\u5979\u7684\u4e8b\uff0c\u8d2b\u5c3c\u600e\u53ef\u968f\u4fbf\u4ee3\u4ed6\u4eba\u4f5c\u4e3b\uff01\u201d \u3000\u3000\u201c\u90a3\u5c31\u8ba9\u665a\u8f88\u82f4\u63a5\u95ee\u5979\u5982\u4f55\uff1f\u201d \u3000\u3000\u201c\u4f60\u5c31\u95ee\u5979\u5427\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u671b\u7740\u9ed1\u8863\u5973\u5b50\u62b1\u4e86\u62b1\u62f3\u9053\uff1a\u201c\u5973\u4fa0\uff0c\u5728\u4e0b\u65b9\u624d\u548c\u8001\u5e08\u592a\u6240\u8bf4\u7684\u8bdd\uff0c\u4f60\u5fc5\u5b9a\u5df2\u7ecf\u542c \u5f97\u660e\u660e\u767d\u767d\uff0c\u73b0\u5728\u53ea\u8bf7\u4f60\u628a\u8499\u9762\u9ed1\u7eb1\u53d6\u4e0b\u6765\uff0c\u4e3e\u624b\u4e4b\u52b3\uff0c\u4f60\u662f\u4e0d\u4f1a\u62d2\u7edd\u7684\u5427\uff1f\u201d \u3000\u3000\u8c01\u77e5\u5bf9\u65b9\u7adf\u7136\u6beb\u65e0\u53cd\u5e94\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u5fc3\u5934\u5927\u6025\uff0c\u53c8\u95ee\u4e86\u51e0\u58f0\uff0c\u8fbd\u662f\u4e0d\u89c1\u52a8\u9759\u3002 \u3000\u3000\u53ea\u542c\u7f01\u8863\u8001\u5c3c\u9053\uff1a\u201c\u65e2\u7136\u8fde\u5979\u81ea\u5df1\u90fd\u4e0d\u7b54\u5e94\uff0e\u4f60\u8fd8\u6709\u4ec0\u4e48\u518d\u8bb2\u7684\uff1f\u5feb\u5feb\u8d76\u4f60\u4eec\u7684\u8def\u5427\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u8001\u5e08\u592a\uff0c\u665a\u8f88\u4eec\u597d\u4e0d\u5bb9\u6613\u624d\u518d\u5ea6\u9047\u5230\u8fd9\u4f4d\u5973\u4fa0\uff0c\u4f60\u8001\u4eba\u5bb6\u5c31\u2026\u2026\u201d \u3000\u3000\u4f46\u89c1\u7f01\u8863\u8001\u5c3c\u5bbd\u5927\u7684\u888d\u8896\u4e00\u6325\uff0c\u987f\u65f6\u4e00\u80a1\u5947\u5927\u65e0\u6bd4\u7684\u6697\u52b2\uff0c\u6709\u5982\u6392\u5c71\u5012\u6d77\u822c\u6d8c\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u3001\u80e1\u67cf\u9f84\u3001\u82d7\u7d20\u82d3\u7ad9\u811a\u4e0d\u4f4f\uff0e\u5168\u88ab\u8fd9\u80a1\u6697\u52ab\u649e\u5f97\u8e09\u8dc4\u540e\u6454\uff0c\u76f4\u9000\u51fa\u4e8c\u4e09\u4e08\u5916\uff0c\u624d \u7ad9\u4f4f\u811a\u8ddf\u3002 \u3000\u3000\u5f53\u4ed6\u4eec\u518d\u6b32\u5411\u524d\u65f6\uff0c\u7f01\u8863\u8001\u5c3c\u548c\u9ed1\u8863\u8499\u9762\u5973\u5b50\u65e9\u5df2\u5931\u53bb\u8e2a\u5f71\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u731b\u4e00\u8dfa\u811a\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u8fd9\u4e48\u597d\u7684\u4e00\u6b21\u673a\u4f1a\uff0c\u7adf\u7136\u53c8\u5931\u53bb\u4e86\uff01\u201d \u3000\u3000\u80e1\u67cf\u9f84\u6de1\u6de1\u4e00\u7b11\u9053\uff1a\u201c\u522b\u628a\u8fd9\u4e8b\u653e\u5728\u5fc3\u4e0a\uff0c\u5feb\u5feb\u8d76\u8def\u8981\u7d27\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u4e0d\u89c9\u4e00\u6123\u9053\uff1a\u201c\u5e08\u5144\uff0c\u4f60\u600e\u4e48\u5566\uff1f\u201d \u3000\u3000\u80e1\u67cf\u9f84\u9053\uff1b\u201c\u6211\u5e76\u6ca1\u6709\u600e\u6837\uff0c\u5012\u662f\u4f60\u6709\u70b9\u53cd\u5e38\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u91cd\u91cd\u7684\u54b3\u4e86\u58f0\u9053\uff1a\u201c\u5979\u660e\u660e\u662f\u5e08\u5ac2\uff0c\u5f53\u5e74\u60a8\u548c\u5979\u7684\u6069\u7231\uff0c\u7eff\u6797\u4e2d\u8c01\u4e0d\u7fa1\u6155\u3002\u4f46\u73b0 \u5728\u60a8\u5bf9\u5979\u597d\u50cf\u6beb\u4e0d\u5173\u5fc3\uff0c\u7a76\u7adf\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d \u3000\u3000\u80e1\u67cf\u9f84\u9053\uff1a\u201c\u5979\u82e5\u771f\u662f\u5e08\u5ac2\uff0c\u65e9\u5c31\u8be5\u81ea\u52a8\u6380\u5f00\u9762\u7eb1\uff0c\u62a2\u4e0a\u524d\u548c\u6211\u76f8\u8ba4\u3002\u54ea\u4f1a\u50cf\u521a\u624d\u4e00\u526f \u6f20\u4e0d\u5173\u5fc3\u7684\u6a21\u6837\uff1f\u4f55\u51b5\u6570\u6708\u524d\u4f60\u4eec\u660e\u660e\u770b\u5230\u5979\u6b7b\u5728\u5929\u53f0\u4e07\u82b1\u5bab\uff0c\u53c8\u600e\u53ef\u80fd\u6d3b\u56de\u6765\u5462\uff1f\u201d \u3000\u3000\u201c\u53ef\u662f\u5e08\u5144\u60a8\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u662f\u9047\u4e0a\u4e86\u5bb6\u5e08\u56db\u7edd\u8001\u4eba\uff0c\u5979\u53c8\u600e\u53ef\u80fd\u5f97\u5230\u50cf\u6211\u2014\u6837\u7684\u673a\u7f18\u548c\u673a\u9047\uff1f\u201d \u3000\u3000\u201c\u53ef\u662f\u5e08\u5144\u522b\u5fd8\u4e86\u5929\u53f0\u4e07\u82b1\u5bab\u5730\u5e9c\u91cc\u6709\u4f4d\u4e09\u5999\u4e66\u751f\u3002\u201d \u3000\u3000\u201c\u5982\u679c\u5bd2\u9999\u771f\u662f\u88ab\u4e09\u5999\u4e66\u751f\u6240\u6551\uff0c\u5979\u5c31\u8be5\u4e5f\u5728\u5929\u53f0\u4e07\u82b1\u5bab\u5730\u5e9c\u624d\u662f\uff0c\u4e3a\u4ec0\u4e48\u5230\u4e86\u8fd9\u91cc \u6765\uff1f\u201d \u3000\u3000\u201c\u54b1\u4eec\u4e0d\u662f\u2014\u76f4\u6000\u7591\u8fd9\u4f4d\u8001\u5e08\u592a\u5c31\u662f\u5f53\u5e74\u7684\u5999\u5999\u4ed9\u5b50\u5417\uff1f\u679c\u771f\u5982\u6b64\uff0c\u5e08\u5ac2\u5949\u4e09\u5999\u4e66\u751f\u4e4b \u547d\uff0c\u6765\u627e\u5979\u7684\u5e08\u53d4\u5999\u5999\u4ed9\u5b50\uff0c\u53c8\u6709\u4ec0\u5e7a\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u80e1\u67cf\u9f84\u88ab\u95ee\u5f97\u4f3c\u4e4e\u7b54\u4e0d\u4e0a\u8bdd\uff0c\u7f04\u9ed8\u4e86\u534a\u664c\uff0c\u5401\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u5979\u82e5\u771f\u662f\u5bd2\u9999\uff0c\u603b\u8be5\u5230\u8ff7\u8e2a \u8c37\u53bb\u770b\u770b\u4f60\u4eec\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u5e08\u5144\u7684\u884c\u52a8\u53d7\u56db\u7edd\u8001\u4eba\u7684\u9650\u5236\uff0e\u53c8\u600e\u77e5\u5979\u7684\u884c\u52a8\u4e0d\u53d7\u4e09\u5999\u4e66\u751f\u548c\u8fd9\u4f4d\u8001\u5e08 \u592a\u7684\u9650\u5236\u5462\uff1f\u201d \u3000\u3000\u80e1\u67cf\u9f84\u6447\u4e86\u6447\u5934\u9053\uff0c\u201c\u522b\u8bf4\u4e86\uff0c\u8d76\u8def\u8981\u7d27\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u524d\u9762\u5f88\u5feb\u4fbf\u662f\u5999\u5999\u5eb5\uff0c\u54b1\u4eec\u518d\u8fdb\u53bb\u770b\u770b\u5982\u4f55\uff1f\u201d \u3000\u3000\u80e1\u67cf\u9f84\u9053\uff1a\u201c\u8fd9\u5012\u4f7f\u5f97\u3002\u201d \u3000\u3000\u6765\u5230\u5999\u5999\u5eb5\uff0c\u8fdc\u8fdc\u5c31\u671b\u89c1\u4e07\u6620\u971e\u6b63\u5728\u5eb5\u95e8\u5916\u6d47\u82b1\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u5feb\u6b65\u5148\u884c\u5954\u5b50\u8fc7\u53bb\uff0c\u53eb\u9053\uff1a\u201c\u4e07\u59d1\u5a18\uff01\u4f60\u770b\u8c01\u6765\u4e86\uff1f\u201d \u3000\u3000\u4e07\u6620\u971e\u653e\u4e0b\u6d47\u82b1\u6c34\u74e2\uff0c\u60ca\u559c\u9053\uff1a\u201c\u539f\u6c42\u662f\u9ea6\u5c0f\u4fa0\uff0e\u90a3\u4e00\u4f4d\u2026\u2026\u90a3\u4e00\u4f4d\u600e\u4e48\u597d\u50cf\u80e1\u53d4\u53d4\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4ed6\u6b63\u662f\u4f60\u7684\u80e1\u53d4\u53d4\uff01\u201d \u3000\u3000\u4e07\u6620\u971e\u6014\u4e86\u6014\u9053\uff1a\u201c\u80e1\u53d4\u53d4\u600e\u4e48\u6d3b\u7740\uff1f\u201d \u3000\u3000\u5f53\u9ea6\u5c0f\u660e\u7b80\u8981\u8bf4\u660e\u7ecf\u8fc7\u540e\uff0c\u80e1\u67cf\u9f84\u548c\u82d7\u7d20\u82d3\u5df2\u6765\u5230\u8ddf\u524d\u3002 \u3000\u3000\u4e07\u6620\u971e\u8fde\u5fd9\u4e0a\u524d\u89c1\u793c\uff0e\u80e1\u67cf\u9f84\u662f\u5979\u7236\u4eb2\u795e\u97ad\u98de\u68f1\u4e07\u6653\u5149\u7684\u4e49\u5f1f\uff0c\u81ea\u4e07\u6653\u5149\u6b7b\u540e\uff0c\u7b97\u662f\u5979 \u552f\u4e00\u7684\u4eb2\u4eba\u3002\u5982\u4eca\u89c1\u80e1\u67cf\u9f84\u6d3b\u751f\u751f\u7684\u6765\u5230\u9762\u524d\uff0c\u6b23\u559c\u4e4b\u60c5\uff0c\u4e0d\u8a00\u53ef\u55bb\u3002 \u3000\u3000\u80e1\u67cf\u9f84\u6b64\u523b\u89c1\u5230\u8fd9\u4f4d\u4e49\u4f84\u5973\uff0c\u4e5f\u662f\u500d\u611f\u4eb2\u5207\uff0c\u4e8c\u4eba\u7d27\u7d27\u76f8\u62e5\uff0c\u6fc0\u52a8\u5f97\u4e45\u4e45\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4e07\u59d1\u5a18\uff0c\u5eb5\u4e3b\u5728\u5eb5\u91cc\u5417\uff1f\u201d \u3000\u3000\u4e07\u6620\u971e\u9053\uff1a\u201c\u5e08\u7236\u5f88\u5c11\u79bb\u5eb5\uff0c\u73b0\u5728\u6b63\u5728\u4f5b\u7ae0\u8bf5\u7ecf\u3002\u201d \u3000\u3000\u201c\u8001\u5e08\u592a\u53ef\u66fe\u56de\u5eb5\uff1f\u201d \u3000\u3000\u201c\u9ea6\u5c0f\u4fa0\u600e\u77e5\u5e08\u7956\u5df2\u7ecf\u5916\u51fa\uff1f\u201d \u3000\u3000\u201c\u5b9e\u4e0d\u76f8\u7792\uff0c\u6211\u4eec\u66fe\u5728\u8def\u4e0a\u9047\u5230\u8fc7\u5979\u3002\u201d \u3000\u3000\u201c\u5e08\u7956\u7ecf\u5e38\u4e0d\u5728\u5eb5\u5c0f\uff0c\u5eb5\u91cc\u7684\u4e8b\uff0c\u591a\u534a\u7531\u5e08\u7236\u4e3b\u6301\uff0c\u73b0\u5728\u5c31\u8bf7\u5230\u91cc\u9762\u89c1\u89c1\u5e08\u7236\u5427\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u671b\u5411\u80e1\u67cf\u9f84\u9053\uff1a\u201c\u5e08\u5144\uff0c\u8981\u4e0d\u8981\u8fdb\u53bb\uff1f\u201d \u3000\u3000\u80e1\u67cf\u9f84\u9053\uff1b\u201c\u65e2\u7136\u5df1\u89c1\u7740\u971e\u513f\uff0c\u95ee\u95ee\u971e\u513f\u4fbf\u53ef\uff0c\u7528\u4e0d\u7740\u518d\u8fdb\u53bb\u4e86\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u70b9\u70b9\u5934\u9053\uff1a\u201c\u5e08\u5144\u8bf4\u5f97\u5bf9\uff0c\u53ea\u6709\u4e07\u59d1\u5a18\u624d\u4f1a\u5bf9\u54b1\u4eec\u8bf4\u5b9e\u8bdd\u3002\u201d \u3000\u3000\u4e07\u6620\u9707\u5fd9\u9053\uff1a\u201c\u80e1\u53d4\u53d4\u548c\u9ea6\u5c0f\u4fa0\u95ee\u7684\u662f\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u90a3\u4f4d\u9ed1\u8863\u8364\u9762\u5973\u4fa0\u7684\u4e8b\uff0e\u4f60\u5df3\u7ecf\u77e5\u9053\u4e86\uff0e\u6211\u4eec\u5728\u8def\u4e0a\u53c8\u9047\u5230\u8fc7\u5979\u3002\u201d \u3000\u3000\u4e07\u6620\u971e\u8138\u8272\u5fae\u5fae\u4e00\u53d8\uff0e\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u5979\u8eab\u65c1\u8fd8\u6709\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u5979\u548c\u8001\u5e08\u592a\u5728\u4e00\u8d77\uff0c\u6211\u4e0d\u660e\u767d\u8001\u5e08\u592a\u4e3a\u4ec0\u4e48\u8981\u6b3a\u9a97\u6211\u4eec\uff1f\u201d \u3000\u3000\u201c\u5979\u6b3a\u9a97\u8fc7\u9ea6\u5c0f\u4fa0\u4ec0\u4e48\uff01\u201d \u3000\u3000\u201c\u4e0a\u6b21\u4f60\u6211\u548c\u82d7\u576b\u5a18\u4e00\u8d77\u6765\u65f6\uff0c\u8001\u5e08\u592a\u66fe\u8bf4\u8fc7\u90a3\u9ed1\u8863\u8499\u9762\u5973\u4fa0\u662f\u9b3c\uff0c\u8fd8\u7279\u5730\u4e09\u66f4\u534a\u591c\u628a \u54b1\u4eec\u5f15\u5230\u5893\u5730\u53bb\u770b\u3002\u800c\u73b0\u5728\u90a3\u9ed1\u8863\u8499\u9762\u5973\u4fa0\u5df2\u968f\u5728\u5979\u8eab\u8fb9\uff0c\u96be\u9053\u8fd9\u4e0d\u662f\u6b3a\u9a97\u5417\uff1f\u201d \u3000\u3000\u201c\u53ef\u80fd\u5e08\u7956\u5f53\u65f6\u771f\u7684\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u201c\u90a3\u4e48\u73b0\u5728\u5979\u8be5\u77e5\u9053\u4e86\uff0c\u5374\u53c8\u504f\u504f\u4e0d\u51c6\u6211\u4eec\u770b\u3002\u201d \u3000\u3000\u201c\u770b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u81ea\u7136\u662f\u770b\u770b\u90a3\u5973\u4fa0\u662f\u4e0d\u662f\u80e1\u5e08\u5ac2\u3002\u4e07\u59d1\u5a18\uff0c\u4f60\u4e00\u5b9a\u89c1\u8fc7\u5979\uff0c\u73b0\u5728\u5c31\u5bf9\u6211\u4eec\u8bf4\u5b9e\u8bdd\u5427\uff01\u201d \u3000\u3000\u8c01\u77e5\u4e07\u6620\u971e\u5374\u6447\u5934\u9053\uff1a\u201c\u6211\u867d\u7136\u4e0d\u65f6\u89c1\u8fc7\u5979\uff0e\u4f46\u4e5f\u4e0d\u4f1a\u770b\u5230\u5979\u7684\u9762\u8c8c\uff0c\u800c\u4e14\u4ece\u672a\u4ea4\u8c08 \u8fc7\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u76b1\u4e86\u76b1\u7709\u5934\u9053\uff1a\u201c\u5979\u662f\u5426\u4f4f\u5728\u8fd9\u5999\u5999\u5eb5\u91cc\uff1f\u201d \u3000\u3000\u4e07\u6620\u971e\u9053\uff1a\u201c\u5979\u53ea\u662f\u6709\u65f6\u5019\u6765\uff0c\u800c\u4e14\u5979\u4f4f\u7684\u90a3\u95f4\u7cbe\u820d\uff0c\u5e08\u7956\u548c\u5e08\u592a\u90fd\u4e0d\u51c6\u6211\u63a5\u8fd1\uff0c\u5403\u996d \u4e5f\u4ece\u6765\u4e0d\u66fe\u548c\u5979\u540c\u684c\u8fc7\uff0c\u6240\u4ee5\u6211\u60f3\u5979\u7edd\u4e0d\u53ef\u80fd\u662f\u80e1\u5a76\u5a76\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4f60\u662f\u600e\u6837\u65ad\u5b9a\u7684\uff1f\u201d \u3000\u3000\u4e07\u6620\u971e\u9053\uff1a\u201c\u5979\u82e5\u771f\u662f\u80e1\u5a76\u5a76\uff0c\u53c8\u600e\u4f1a\u89c1\u4e86\u9762\u4e0d\u8ddf\u6211\u8bb2\u8bdd\uff1f\u201d \u3000\u3000\u8fd9\u65f6\u9ea6\u5c0f\u660e\u5b9e\u5728\u5df2\u6709\u4e9b\u95ee\u4e0d\u4e0b\u53bb\u3002 \u3000\u3000\u80e1\u67cf\u9f84\u9053\uff1a\u201c\u4e0d\u5fc5\u518d\u95ee\u4e86\uff0c\u54b1\u4eec\u8fd8\u662f\u8d70\u5427\uff01\u201d \u3000\u3000\u4e07\u6620\u971e\u9053\uff1a\u201c\u53d4\u53d4\u548c\u9ea6\u5c0f\u4fa0\u3001\u82d7\u59d1\u5a18\u4e5f\u8be5\u5230\u5eb5\u91cc\u559d\u676f\u8336\uff01\u201d \u3000\u3000\u80e1\u67cf\u9f84\u9053\uff1a\u201c\u4e0d\u5fc5\u4e86\uff0c\u5f85\u4f1a\u513f\u89c1\u4e86\u5eb5\u4e3b\uff0c\u4e0d\u5fc5\u63d0\u8d77\u6211\u4eec\u6765\u8fc7\u4e4b\u4e8b\u3002\u201d \u3000\u3000\u4e07\u6620\u971e\u53ea\u597d\u4f9d\u4f9d\u4e0d\u820d\u7684\u628a\u4e09\u4eba\u9001\u4e86\u4e00\u7a0b\uff0c\u624d\u56de\u5eb5\u800c\u53bb\u3002 \u3000\u3000\u5f53\u665a\uff0c\u4e09\u4eba\u5c31\u5728\u5b9a\u8944\u4f4f\u4e0b\uff0c\u6b21\u65e5\u7ee7\u7eed\u8d76\u8def\uff0e \u3000\u3000\u53c8\u8fc7\u4e86\u4e8c\u65e5\uff0e\u5df2\u5230\u5317\u5cb3\u3002 \u3000\u3000\u4ed6\u4eec\u81ea\u7136\u662f\u5148\u8d76\u5230\u767d\u59d1\u5bfa\u3002 \u3000\u3000\u5148\u8fdb\u5165\u767d\u59d1\u5854\uff0c\u624d\u53d1\u89c9\u5404\u5c42\u90fd\u5df2\u4eba\u53bb\u5854\u7a7a\u3002 \u3000\u3000\u5f53\u4ed6\u4eec\u7531\u5854\u4e0a\u4e0b\u6765\uff0e\u53ea\u89c1\u4f4f\u6301\u609f\u5c18\u6b63\u5e26\u7740\u4e09\u540d\u5f1f\u5b50\u5728\u5927\u6bbf\u8bf5\u7ecf\uff0c\u8fd9\u65f6\u4ed6\u4eec\u7ec8\u4e8e\u7a0d\u7a0d\u653e\u4e0b \u5fc3\u6765\u3002 \u3000\u3000\u609f\u5c18\u77a5\u89c1\u80e1\u67cf\u9f84\u7b49\u4e09\u4eba\uff0c\u8fde\u5fd9\u8fce\u4e86\u51fa\u6765\uff0c\u5e76\u628a\u4e09\u4eba\u5f15\u8fdb\u5f85\u5ba2\u7985\u623f\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u8feb\u4e0d\u53ca\u5f85\u7684\u95ee\uff1a\u201c\u949f\u526f\u76df\u4e3b\u4ed6\u4eec\u90fd\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u201d \u3000\u3000\u609f\u5c18\u957f\u957f\u5401\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u81ea\u4ece\u4e09\u4f4d\u8d70\u540e\u4e0d\u4e45\uff0c\u949f\u65bd\u4e3b\u4eec\u4fbf\u906d\u5230\u4e00\u6b21\u8ff7\u8e2a\u8c37\u7684\u88ad\u51fb\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u795e\u8272\u4e00\u7d27\u9053\uff1a\u201c\u7ed3\u679c\u600e\u6837\uff1f\u201d \u3000\u3000\u609f\u5c18\u9053\uff1a\u201c\u53cc\u65b9\u90fd\u6709\u4eba\u8d1f\u4f24\uff0c\u597d\u5728\u949f\u65bd\u4e3b\u65b9\u9762\u7684\u4eba\u6709\u4f24\u65e0\u4ea1\u3002\u201d \u3000\u3000\u201c\u4ed6\u4eec\u662f\u4ec0\u4e48\u65f6\u5019\u642c\u8d70\u7684\uff1f\u201d \u3000\u3000\u201c\u949f\u65bd\u4e3b\u548c\u4f59\u65bd\u4e3b\u62c5\u5fc3\u8ff7\u8e2a\u8c37\u7ee7\u7eed\u6765\u4eba\u65bd\u88ad\uff0c\u5be1\u4e0d\u654c\u4f17\uff0c\u4fbf\u5728\u906d\u5230\u88ad\u51fb\u7684\u7b2c\u4e8c\u5929\u591c\u665a\uff0c \u6084\u6084\u64a4\u51fa\u4e86\u655d\u5bfa\u3002\u201d \u3000\u3000\u201c\u53ef\u77e5\u9053\u4ed6\u4eec\u642c\u5230\u4ec0\u4e48\u5730\u65b9\u53bb\u4e86\uff1f\u201d \u3000\u3000\u201c\u4ed6\u4eec\u642c\u8d70\u7684\u5f53\u65f6\uff0c\u8fd8\u4e0d\u66fe\u51b3\u5b9a\u8981\u642c\u5f80\u4f55\u5904\u3002\u201d \u3000\u3000\u201c\u4ed6\u4eec\u64a4\u8d70\u4ee5\u540e\uff0c\u4e5f\u4e0d\u66fe\u518d\u6709\u4eba\u6765\u8fc7\uff1f\u201d \u3000\u3000\u201c\u6ca1\u4eba\u6765\u8fc7\uff0c\u8d2b\u50e7\u548c\u4e09\u540d\u5f1f\u5b50\u4e5f\u4e0d\u66fe\u89c1\u8fc7\u4ed6\u4eec\uff0c\u66f4\u6ca1\u6709\u4ed6\u4eec\u7684\u6d88\u606f\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u987f\u65f6\u5fc3\u5934\u8499\u4e0a\u4e00\u9635\u9634\u5f71\uff0c\u6c89\u5bc2\u4e86\u534a\u664c\u9053\uff1a\u201c\u8d35\u5e08\u95e8\u7684\u5929\u673a\u8001\u7985\u5e08\u4e0d\u77e5\u54ea\u91cc\u53bb\u4e86\uff1f\u201d \u3000\u3000\u609f\u5c18\u9053\uff1a\u201c\u655e\u5e08\u53d4\u5728\u4e09\u4f4d\u8d70\u540e\u7684\u6b21\u65e5\uff0c\u5c31\u56de\u5d69\u5c71\u5c11\u6797\u672c\u9662\u53bb\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u773c\u671b\u80e1\u67cf\u9f84\uff0c\u8138\u8272\u4e00\u53d8\u9053\uff1a\u201c\u7cdf\u7cd5\uff01\u5929\u673a\u8001\u7985\u5e08\u679c\u7136\u51fa\u4e86\u610f\u5916\uff01\u201d \u3000\u3000\u609f\u5c18\u6115\u7136\u95ee\u9053\uff1a\u201c\u655d\u5e08\u53d4\u51fa\u4e86\u4ec0\u4e48\u610f\u5916\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u6211\u4eec\u8fd9\u6b21\u7531\u5929\u53f0\u5c71\u5317\u8fd4\uff0c\u66fe\u7279\u5730\u5230\u5d69\u5c71\u5c11\u6797\u4e00\u8d9f\uff0c\u627f\u8499\u5929\u7985\u638c\u95e8\u5927\u5e08\u4eb2\u81ea \u63a5\u5f85\uff0c\u636e\u8bf4\u5929\u673a\u8001\u7985\u5e08\u5e76\u6ca1\u56de\u53bb\u3002\u8fd9\u6837\u770b\u6765\uff0e\u5c82\u4e0d\u662f\u51fa\u4e86\u610f\u5916\uff1f\u201d \u3000\u3000\u609f\u5c18\u4e5f\u987f\u65f6\u9a87\u7136\u5931\u8272\u9053\uff1a\u201c\u655d\u6d3e\u638c\u95e8\u5e08\u5c0a\u53ef\u66fe\u8bf4\u8fc7\u6253\u7b97\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u5929\u7985\u5927\u5e08\u5f53\u573a\u51b3\u5b9a\uff0e\u8981\u4eb2\u7387\u6d3e\u5bfa\u5185\u9ad8\u624b\u5230\u5317\u5cb3\u6765\uff0c\u9664\u4e86\u8981\u628a\u90a3\u4e5d\u540d\u94dc\u4eba\u62bc \u89e3\u56de\u53bb\uff0e\u66f4\u91cd\u8981\u7684\uff0c\u662f\u8981\u6551\u51fa\u5929\u89c9\u8001\u7985\u5e08\u7b49\u4eba\u3002\u201d \u3000\u3000\u201c\u655d\u6d3e\u638c\u95e8\u5e08\u5c0a\u6765\u5317\u5cb3\u4ee5\u540e\uff0c\u662f\u5426\u76f4\u63a5\u8fdb\u5165\u8ff7\u8e2a\u8c37\uff1f\u201d \u3000\u3000\u201c\u5929\u7985\u5927\u5e08\u5df2\u548c\u6211\u4eec\u7ea6\u5b9a\uff0c\u8981\u5148\u6765\u8d35\u5bfa\u4f1a\u5408\uff0e\u7136\u540e\u518d\u91c7\u53d6\u884c\u52a8\uff0e\u53ef\u60dc\u949f\u526f\u76df\u4e3b\u7b49\u4eba\u53c8\u5931 \u53bb\u4e0b\u843d\u3002\u201d \u3000\u3000\u8fd9\u65f6\u609f\u5c18\u662f\u53c8\u559c\u53c8\u7d27\u5f20\u3002 \u3000\u3000\u559c\u7684\u662f\u638c\u95e8\u5927\u5e08\u5c45\u7136\u80af\u5149\u4e34\u4ed6\u8fd9\u5c0f\u5c0f\u7684\u767d\u59d1\u5bfa\u3002 \u3000\u3000\u7d27\u5f20\u7684\u662f\u5929\u7985\u5927\u5e08\u5230\u8fbe\u65f6\uff0c\u5fc5\u987b\u90d1\u91cd\u63a5\u5f85\uff0c\u8fd9\u5de5\u4f5c\u5b9e\u5728\u591f\u4ed6\u548c\u4e09\u540d\u5f1f\u5b50\u5fd9\u7684\u3002 \u3000\u3000\u4ed6\u8fde\u5fd9\u518d\u95ee\u9053\uff1a\u201c\u4e0d\u77e5\u655d\u6d3e\u638c\u95e8\u5e08\u5c0a\u4ec0\u4e48\u65f6\u5019\u53ef\u4ee5\u5230\u8fbe\u655d\u5bfa\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u6211\u4eec\u5728\u8def\u4e0a\u66fe\u56e0\u6545\u803d\u8bef\u4e86\u4e00\u4e9b\u884c\u7a0b\uff0c\u8ba1\u7b97\u65f6\u95f4\uff0c\u5929\u7985\u5927\u5e08\u5c31\u5728\u8fd9\u4e00\u4e8c\u5929\u4fbf \u53ef\u5230\u8fbe\uff0c\u8bf4\u4e0d\u5b9a\u5c31\u5728\u4eca\u665a\u3002\u201d \u3000\u3000\u8fd9\u4e00\u6765\u609f\u5c18\u8d8a\u53d1\u7d27\u5f20\u8d77\u6765\u3002 \u3000\u3000\u80e1\u67cf\u9f84\u8d77\u8eab\u9053\uff1a\u201c\u5c0f\u660e\uff0c\u82d7\u59d1\u5a18\uff0c\u54b1\u4eec\u8be5\u8d70\u4e86\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u4e00\u6123\u9053\uff1a\u201c\u5230\u54ea\u91cc\u53bb\uff1f\u201d \u3000\u3000\u80e1\u67cf\u9f84\u9053\uff1b\u201c\u4e07\u6708\u5ce1\uff0c\u4e5f\u8bb8\u949f\u526f\u76df\u4e3b\u7b49\u4eba\u5728\u4e07\u6708\u5ce1\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u7edd\u5bf9\u4e0d\u53ef\u80fd\uff01\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u5f53\u521d\u6211\u4eec\u672c\u6765\u5c31\u7531\u8ff7\u8e2a\u8c37\u5230\u4e07\u6708\u5ce1\u7684\uff0c\u56e0\u4e3a\u90a3\u91cc\u4e0d\u5b89\u5168\uff0c\u624d\u8fc1\u5230\u8fd9\u91cc\u6765\u7684\uff0c\u4ed6\u4eec\u600e\u53ef \u80fd\u518d\u56de\u53bb\u5462\uff1f\u201d \u3000\u3000\u201c\u4f60\u53ea\u77e5\u5176\u4e00\uff0c\u4e0d\u77e5\u5176\u4e8c\u3002\u4e07\u6708\u5ce1\u5730\u65b9\u5927\uff0c\u8303\u56f4\u5e7f\uff0e\u968f\u4fbf\u627e\u4e2a\u9690\u79d8\u4e4b\u5904\u90fd\u53ef\u85cf\u8eab\u3002\u81f3\u4e8e \u8fd9\u767d\u59d1\u5bfa\uff0c\u53ea\u8981\u88ab\u4eba\u5305\u56f4\u4f4f\uff0c\u4fbf\u7b49\u4e8e\u9677\u5165\u5371\u5883\u3002\u4f60\u8ba4\u4e3a\u6211\u8fd9\u8bf4\u6cd5\u662f\u5426\u6709\u9053\u7406\uff1f\u201d \u3000\u3000\u201c\u5e08\u5144\u8bf4\u5f97\u6709\u7406\uff0c\u54b1\u4eec\u73b0\u5728\u5c31\u8d70\u3002\u201d \u3000\u3000\u609f\u5c18\u5fd9\u9053\uff1a\u201c\u6211\u770b\u4e09\u4f4d\u65bd\u571f\u8fd8\u662f\u5728\u655d\u5bfa\u7528\u8fc7\u665a\u996d\u518d\u8d70\u7684\u597d\u3002\u201d \u3000\u3000\u80e1\u67cf\u9f84\u4e00\u60f3\uff0c\u8fd9\u8bdd\u4e5f\u5bf9\uff0c\u5982\u679c\u767d\u5929\u884c\u8d70\uff0c\u4e07\u4e00\u88ab\u8ff7\u8e2a\u8c37\u7684\u773c\u7ebf\u53d1\u73b0\uff0c\u5c82\u4e0d\u5927\u4e3a\u4e0d\u5999\u3002 \u3000\u3000\u4e8e\u662f\uff0e\u4fbf\u5728\u767d\u59d1\u5bfa\u76f4\u7b49\u5230\u665a\u996d\u540e\uff0c\u624d\u5411\u609f\u5c18\u9053\u522b\u3002 \u3000\u3000\u609f\u5c18\u9887\u4e3a\u5173\u5fc3\u7684\u95ee\u9053\uff1a\u201c\u655d\u6d3e\u638c\u95e8\u5e08\u5c0a\u5230\u8fbe\u655d\u5bfa\u540e\uff0c\u8981\u600e\u6837\u548c\u80e1\u76df\u4e3b\u8fde\u7edc\uff0c\u8fd8\u8bf7\u793a\u4e0b\uff01\u201d \u3000\u3000\u80e1\u67cf\u9f84\u9053\uff1a\u201c\u90a3\u5c31\u8bf7\u6d3e\u4e00\u540d\u5f1f\u5b50\u5230\u4e07\u6708\u5ce1\u901a\u77e5\u80e1\u67d0\uff0c\u4e5f\u8bb8\u6211\u4eec\u4f1a\u4e3b\u52a8\u56de\u5230\u8fd9\u91cc\u6765\uff0e\u201d \u3000\u3000\u80e1\u67cf\u9f84\u3001\u9ea6\u5c0f\u660e\u3001\u82d7\u7d20\u82d3\u8e0f\u7740\u6708\u8272\uff0c\u5f80\u4e07\u6708\u5ce1\u8fdb\u53d1\u3002 \u3000\u3000\u4ed6\u4eec\u7ed5\u8fc7\u8ff7\u8e2a\u8c37\u53e3\u800c\u884c\uff0c\u56e0\u4e4b\u5e76\u672a\u53d1\u73b0\u8ff7\u8e2a\u8c37\u773c\u7ebf\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u642d\u8baa\u7740\u9053\uff1a\u201c\u5e08\u5144\uff0c\u8ff7\u8e2a\u8c37\u662f\u4f60\u2014\u624b\u6240\u521b\uff0c\u96be\u9053\u60a8\u771f\u7684\u4e0d\u60f3\u56de\u53bb\u4e86\uff1f\u201d \u3000\u3000\u80e1\u67cf\u9f84\u8038\u80a9\u9053\uff1a\u201c\u6211\u5f53\u7136\u4e0d\u80fd\u65e0\u52a8\u4e8e\u8877\uff0c\u600e\u5948\u5e08\u7236\u4e00\u518d\u544a\u8beb\uff0e\u4e25\u7981\u6211\u8e0f\u8fdb\u8ff7\u8e2a\u8c37\u4e00\u6b65\u3002 \u5e08\u547d\u5927\u5982\u5929\uff0e\u600e\u53ef\u4e0d\u9075\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u8d81\u7740\u4ed6\u8001\u4eba\u5bb6\u6ca1\u56de\u6765\uff0c\u8fdb\u8ff7\u8e2a\u8c37\u53c8\u6709\u4f55\u59a8\uff1f\u201d \u3000\u3000\u80e1\u67cf\u9f84\u6447\u5934\u9053\uff1a\u201c\u4e3a\u4eba\u5f1f\u5b50\uff0c\u5c82\u80fd\u9633\u5949\u9634\u8fdd\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u65e0\u5948\u7684\u9053\uff1a\u201c\u5e08\u5144\uff0c\u4f60\u597d\u50cf\u53d8\u4e86\u3002\u201d \u3000\u3000\u201c\u6211\u53d8\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u201c\u60a8\u4ee5\u524d\u8c6a\u6c14\u5e72\u4e91\uff0c\u51e1\u4e8b\u4ece\u4e0d\u754f\u7f29\uff0c\u73b0\u5728\u4ec0\u4e48\u4e8b\u90fd\u987e\u524d\u987e\u540e\uff0c\u8fd9\u4e0d\u662f\u53d8\u4e86\u5417\uff1f\u201d \u3000\u3000\u201c\u5c0f\u660e\uff0c\u4f60\u8be5\u77e5\u9053\uff0c\u6211\u8fd9\u6761\u547d\u662f\u600e\u4e48\u6765\u7684\uff1f\u82e5\u6ca1\u6709\u5bb6\u5e08\uff0c\u54ea\u91cc\u8fd8\u6709\u6211\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u89c1\u80e1\u67cf\u9f84\u8bf4\u51fa\u6b64\u8bdd\uff0c\u53ea\u597d\u4e0d\u518d\u8a00\u8bed\u3002 \u3000\u3000\u5230\u8fbe\u4e07\u6708\u5ce1\u4e07\u6708\u5821\u65f6\uff0c\u5df3\u8fd1\u4e8c\u66f4\uff0c\u603b\u7ba1\u674e\u8302\u95fb\u62a5\u614c\u5fd9\u51fa\u6765\u63a5\u5f85\uff0c\u628a\u80e1\u67cf\u9f84\u7b49\u4eba\u8fce\u5165\u5927\u5385\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u7acb\u5373\u628a\u524d\u5f80\u5929\u53f0\u4e07\u82b1\u5bab\u8fd4\u56de\u4ee5\u53ca\u5148\u5230\u8fc7\u767d\u59d1\u5bfa\u7684\u7ecf\u8fc7\u8bf4\u4e86\u4e00\u904d\uff0c\u63a5\u7740\u518d\u95ee\u8d77\u949f\u4e00 \u8c6a\u7b49\u4eba\u7684\u6d88\u606f\u3002 \u3000\u3000\u674e\u8302\u9053\uff1a\u201c\u949f\u526f\u76df\u4e3b\u4ed6\u4eec\u8fd1\u4e24\u6708\u6765\uff0c\u5e76\u65e0\u4e00\u4eba\u518d\u5230\u4e07\u6708\u5ce1\u6765\uff0c\u6211\u4e5f\u6ca1\u6709\u4ed6\u4eec\u7684\u4efb\u4f55\u6d88 \u606f\uff01\u201d \u3000\u3000\u8fd9\u51e0\u53e5\u8bdd\u53ea\u542c\u5f97\u80e1\u67cf\u9f84\u3001\u9ea6\u5c0f\u660e\u3001\u82d7\u7d20\u82d3\u4e09\u4eba\u6709\u5982\u5f53\u5934\u53d7\u4e86\u91cd\u91cd\u4e00\u51fb\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u7d27\u76b1\u7709\u5934\uff0c\u671b\u4e86\u80e1\u67cf\u9f84\u4e00\u773c\u9053\uff1a\u201c\u5e08\u5144\uff0c\u770b\u6765\u949f\u526f\u76df\u4e3b\u7b49\u4eba\uff0c\u771f\u662f\u51f6\u591a\u5409\u5c11\u4e86\u3002\u201d \u3000\u3000\u80e1\u67cf\u9f84\u4e5f\u795e\u8272\u9eef\u7136\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u518d\u9053\uff1a\u201c\u8fde\u5929\u673a\u8001\u7985\u5e08\u90fd\u53ef\u80fd\u5df2\u9677\u5165\u8ff7\u8e2a\u8c37\uff0c\u949f\u526f\u76df\u4e3b\u7b49\u4eba\u53c8\u600e\u80fd\u514d\u4e8e\u8eab\u906d\u4e0d \u6d4b\u3002\u201d \u3000\u3000\u674e\u8302\u53ea\u5f97\u5b89\u6170\u7740\u9053\uff1a\u201c\u9ea6\u5c0f\u4fa0\u4e0d\u5fc5\u60f3\u5f97\u592a\u591a\uff0c\u65f6\u95f4\u4e0d\u65e9\uff0c\u8fd8\u662f\u5148\u5b89\u6b47\u4e86\uff0c\u522b\u7684\u4e8b\u660e\u5929\u518d \u8bf4\u5427\uff01\u201d \u3000\u3000\u674e\u8302\u521a\u8981\u628a\u80e1\u67cf\u9f84\u4e09\u4eba\u5e26\u5230\u5ba2\u623f\u4f11\u606f\uff0c\u53ea\u89c1\u4e00\u540d\u58ee\u5e74\u50e7\u4eba\u5306\u5306\u5954\u8fdb\u6765\uff0c\u5374\u662f\u767d\u59d1\u5bfa\u4f4f\u6301 \u609f\u5c18\u5927\u5e08\u7684\u5927\u5f1f\u5b50\u666e\u51e1\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4f60\u600e\u4e48\u8fd9\u65f6\u5019\u6765\u4e86\uff1f\u201d \u3000\u3000\u666e\u51e1\u53ef\u80fd\u662f\u4e00\u8def\u6025\u6025\u5954\u884c\uff0c\u64e6\u4e86\u64e6\u989d\u89d2\u7684\u6c57\u6c34\u9053\uff1a\u201c\u655d\u6d3e\u638c\u95e8\u5e08\u7956\u5230\u4e86\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u4ec0\u4e48\u65f6\u5019\u5230\u7684\uff1f\u201d \u3000\u3000\u666e\u51e1\u9053\uff1a\u201c\u5c31\u5728\u4e09\u4f4d\u79bb\u5f00\u655d\u5bfa\u4e0d\u4e45\uff0c\u53ef\u80fd\u53cc\u65b9\u8def\u7ebf\u4e0d\u540c\uff0c\u6240\u4ee5\u624d\u6ca1\u5728\u8def\u4e0a\u9047\u4e0a\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u518d\u671b\u5411\u80e1\u67cf\u9f84\u9053\uff1a\u201c\u5e08\u5144\uff0c\u54b1\u4eec\u662f\u660e\u5929\u8d70\uff0c\u8fd8\u662f\u73b0\u5728\u5c31\u8d70\uff1f\u201d \u3000\u3000\u674e\u8302\u62a2\u7740\u9053\uff1a\u201c\u5df2\u7ecf\u8fd9\u4e48\u665a\u4e86\uff0c\u6211\u770b\u8fd8\u662f\u660e\u5929\u8d70\u597d\u5566\u3002\u8fd9\u4f4d\u5e08\u7236\u4e5f\u8bf7\u7559\u5728\u5821\u91cc\u6682\u4f4f\u4e00 \u591c\u3002\u201d \u3000\u3000\u80e1\u67cf\u9f84\u7ad9\u8d77\u8eab\u9053\uff1a\u201c\u8fd8\u662f\u73b0\u5728\u5c31\u56de\u767d\u59d1\u5bfa\u8f83\u597d\uff0c\u5982\u679c\u660e\u5929\u767d\u5929\u8d70\uff0c\u52bf\u5fc5\u62db\u60f9\u8ff7\u8e2a\u8c37\u7684\u8033 \u76ee\u3002\u201d \u3000\u3000\u674e\u8302\u89c1\u80e1\u67cf\u9f84\u8bf4\u5f97\u6709\u7406\uff0c\u4e5f\u5c31\u4e0d\u518d\u5f3a\u7559\u3002 \u3000\u3000\u4e8e\u662f\uff0c\u80e1\u67cf\u9f84\u3001\u9ea6\u5c0f\u660e\u3001\u82d7\u7d20\u82d3\u3001\u666e\u51e1\u7b49\u56db\u4eba\uff0c\u53c8\u8fde\u591c\u8fd4\u56de\u767d\u59d1\u5bfa\u3002 \u3000\u3000\u5f53\u80e1\u67cf\u9f84\u7b49\u4eba\u8fd4\u56de\u767d\u59d1\u5bfa\u65f6\uff0c\u5929\u7985\u5927\u5e08\u548c\u5e26\u9886\u7684\u5c11\u6797\u50e7\u4eba\u5df2\u7ecf\u5165\u7761\u3002 \u3000\u3000\u76f4\u5230\u6b21\u65e5\u65e9\u996d\u540e\uff0c\u624d\u7531\u609f\u5c18\u4eb2\u6765\u5f15\u5bfc\u5728\u7985\u5802\u548c\u5929\u7985\u5927\u5e08\u89c1\u4e86\u9762\u3002 \u3000\u3000\u8fd9\u6b21\u5929\u7985\u5927\u5e08\u5e26\u7740\u4ed6\u7684\u53e6\u4e00\u4f4d\u5e08\u5f1f\u5929\u671b\u5927\u5e08\u4e00\u8d77\u6765\u7684\uff0c\u53e6\u6709\u4e8c\u5341\u56db\u540d\u5f1f\u5b50\uff0c\u5176\u4e2d\u5305\u62ec\u609f \u5b57\u8f88\u7684\u56db\u5927\u91d1\u521a\u5728\u5185\u3002 \u3000\u3000\u8bba\u5b9e\u529b\uff0c\u6bd4\u4e0a\u6b21\u5929\u673a\u3001\u5929\u89c9\u5927\u5e08\u524d\u6765\u65f6\u8db3\u8db3\u589e\u52a0\u4e86\u4e00\u500d\u3002 \u3000\u3000\u53cc\u65b9\u5206\u5bbe\u4e3b\u5750\u5b9a\uff0c\u80e1\u67cf\u9f84\u5f00\u95e8\u89c1\u5c71\u7684\u95ee\u9053\uff1a\u201c\u638c\u95e8\u5927\u5e08\u51c6\u5907\u4ec0\u4e48\u65f6\u5019\u91c7\u53d6\u884c\u52a8\uff1f\u201d \u3000\u3000\u5929\u7985\u5927\u5e08\u9053\uff1a\u201c\u8001\u8872\u62c5\u5fc3\u5929\u673a\u3001\u5929\u89c9\u4e24\u4f4d\u5e08\u5f1f\u548c\u5341\u4e8c\u540d\u5f1f\u5b50\u7684\u5b89\u5371\uff0c\u81ea\u662f\u5e0c\u671b\u8d8a\u5feb\u8d8a\u597d\uff0c \u4f46\u56e0\u6b64\u6b21\u5e26\u6765\u7684\u4e8c\u5341\u56db\u540d\u5f1f\u5b50\u957f\u9014\u8dcb\u6d89\u52b3\u7d2f\uff0c\u5fc5\u987b\u7a0d\u4e8b\u4f11\u517b\uff0c\u6240\u4ee5\u521a\u624d\u548c\u5929\u671b\u5e08\u5f1f\u5546\u8bae\u7684\u7ed3 \u679c\uff0c\u51b3\u5b9a\u660e\u5929\u884c\u52a8\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u81ea\u544a\u594b\u52c7\u9053\uff1a\u201c\u548c\u4e0a\u6b21\u2014\u6837\uff0c\u665a\u8f88\u51b3\u5b9a\u968f\u540c\u638c\u95e8\u8001\u7985\u5e08\u4e00\u8d77\u8fdb\u8c37\u3002\u201d \u3000\u3000\u82d7\u7d20\u82d3\u4e5f\u63a5\u7740\u9053\uff1a\u201c\u6211\u4e5f\u8981\u53bb\u3002\u201d \u3000\u3000\u5929\u7985\u5927\u5e08\u9894\u9996\u9053\uff1a\u201c\u8001\u8872\u4e0d\u5e0c\u671b\u4f60\u4eec\u4e8c\u4eba\u4e00\u8d77\u53bb\uff0c\u4e0d\u8fc7\u2026\u2026\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u8001\u7985\u5e08\u653e\u5fc3\uff0c\u884c\u52a8\u65f6\u665a\u8f88\u4f1a\u6234\u4e0a\u4eba\u76ae\u9762\u5177\uff0c\u82d7\u59d1\u5a18\u4e5f\u4f1a\u6539\u626e\u7537\u88c5\uff0c\u4fdd\u8bc1\u4e0d \u4f1a\u88ab\u970d\u5143\u4f3d\u8ba4\u51fa\u6765\uff0c\u81ea\u7136\u5c31\u4e0d\u4f1a\u7ed9\u60a8\u6dfb\u9ebb\u70e6\u3002\u201d \u3000\u3000\u5929\u7985\u968f\u5373\u5c06\u89c6\u7ebf\u7f13\u7f13\u79fb\u5411\u80e1\u67cf\u9f84\u3002 \u3000\u3000\u80e1\u67cf\u9f84\u5c82\u80fd\u4e0d\u77e5\u5929\u7985\u5927\u5e08\u7528\u610f\uff0c\u4e0d\u89c9\u6df1\u6df1\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u80e1\u67d0\u786e\u6709\u4e0d\u4fbf\uff0e\u8fd8\u671b\u638c\u95e8\u5927\u5e08 \u89c1\u8c05\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u56db\u7edd\u8001\u524d\u8f88\u6ca1\u56de\u6765\uff0c\u60a8\u7684\u4e09\u4e2a\u6708\u5047\u671f\u4e5f\u672a\u5230\uff0c\u600e\u4f1a\u6709\u4e0d\u4fbf\u5462\uff1f\u201d \u3000\u3000\u80e1\u67cf\u9f84\u9053\uff1a\u201c\u4f60\u61c2\u4ec0\u4e48\uff1f\u7eff\u6797\u4e2d\u8c01\u4eba\u4e0d\u77e5\u6211\u662f\u8ff7\u8e2a\u8c37\u9996\u4efb\u7eff\u6797\u76df\u4e3b\uff0c\u6211\u82e5\u516c\u7136\u5728\u8ff7\u8e2a\u8c37 \u51fa\u73b0\uff0c\u6beb\u65e0\u7591\u95ee\u662f\u4ef6\u8f70\u52a8\u7eff\u6797\u7684\u5927\u4e8b\u3002\u5bb6\u5e08\u56de\u6765\u4ee5\u540e\uff0c\u53c8\u600e\u80fd\u4e0d\u77e5\u9053\u5462\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u90a3\u4e48\u5e08\u5144\u4f55\u4e0d\u4e5f\u5b66\u7740\u5c0f\u5f1f\u4e00\u6837\u6539\u5934\u6362\u9762\u524d\u53bb\uff1f\u201d \u3000\u3000\u80e1\u67cf\u9f84\u82e6\u7b11\u9053\uff1a\u201c\u6211\u82e5\u80fd\u548c\u4f60\u4e00\u6837\uff0c\u90a3\u5c31\u597d\u4e86\uff01\u201d \u3000\u3000\u5929\u7985\u5927\u5e08\u4e5f\u5fd9\u8bf4\u9053\uff1a\u201c\u9ea6\u5c0f\u65bd\u4e3b\u4e0d\u5fc5\u529d\u4e86\uff0c\u4ee5\u80e1\u65bd\u4e3b\u7684\u8eab\u4efd\u5730\u4f4d\uff0c\u8981\u53bb\u5c31\u8be5\u5802\u5802\u6b63\u6b63\u7684 \u53bb\uff0e\u4e54\u88c5\u6539\u626e\u6df7\u53bb\uff0c\u7684\u786e\u4e0d\u662f\u529e\u6cd5\uff0e\u8fd9\u662f\u8c01\u90fd\u4e0d\u8be5\u52c9\u5f3a\u4ed6\u7684\u3002\u201d \u3000\u3000\u80e1\u67cf\u9f84\u4e5f\u9053\uff1a\u201c\u660e\u5929\u6211\u5c31\u7559\u5728\u767d\u59d1\u5bfa\uff0c\u5982\u679c\u970d\u5143\u4f3d\u80c6\u6562\u8ffd\u6740\u5230\u767d\u59d1\u5bfa\u6765\uff0c\u5c4a\u65f6\u6211\u4e00\u5b9a\u4f1a \u51fa\u9762\u7684\u3002\u201d \u3000\u3000\u5929\u7985\u3001\u5929\u671b\u4e24\u5927\u5e08\u548c\u968f\u5e26\u7684\u4e8c\u5341\u56db\u540d\u5f1f\u5b50\uff0e\u679c\u7136\u5728\u767d\u59d1\u5bfa\u5c06\u517b\u4f11\u606f\u4e86\u4e00\u5929\uff0e\u5230\u7b2c\u4e09\u5929\u4fbf \u5f80\u8ff7\u8e2a\u8c37\u8fdb\u53d1\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u548c\u82d7\u7d20\u82d3\u4ecd\u6539\u4efd\u5f97\u548c\u4e0a\u6b21\u4e00\u6837\u3002 \u3000\u3000\u5176\u5b9e\u5728\u5929\u7985\u5927\u5e08\u773c\u4e2d\uff0c\u53ea\u5e0c\u671b\u4ed6\u4eec\u4e8c\u4eba\u505a\u505a\u5411\u5bfc\u800c\u5df2\u3002 \u3000\u3000\u8fd9\u4e5f\u8bf4\u660e\u4e86\u5929\u7985\u5927\u5e08\u6b64\u6b21\u884c\u52a8\uff0e\u4ecd\u4e0d\u613f\u610f\u5984\u52a8\u5e72\u6208\u3002 \u3000\u3000\u6765\u5230\u8ff7\u8e2a\u8c37\u53e3\uff0c\u5947\u602a\u7684\u662f\u5e76\u65e0\u4eba\u62e6\u963b\u6216\u76d8\u67e5\uff0c\u90a3\u4e9b\u628a\u5b88\u5173\u5361\u7684\u55bd\u5175\uff0e\u7adf\u81ea\u52a8\u8ba9\u5f00\u8def\uff0c\u4efb \u7531\u5929\u7985\u5927\u5e08\u6240\u7387\u9886\u7684\u50e7\u4f17\u901a\u884c\u3002 \u3000\u3000\u4f46\u8d8a\u662f\u5982\u6b64\uff0c\u8d8a\u4f7f\u5929\u7985\u5927\u5e08\u7b49\u4eba\u5fc5\u987b\u63d0\u9ad8\u8b66\u89c9\u3002 \u3000\u3000\u4e00\u8def\u6beb\u65e0\u963b\u62e6\u7684\u76f4\u6765\u5230\u603b\u5be8\u5185\u90e8\u7684\u805a\u4e49\u5385\u524d\uff0c\u8fdc\u8fdc\u4fbf\u671b\u89c1\u970d\u5143\u4f3d\u548c\u5cad\u5357\u4e8c\u5947\u6b63\u7ad9\u5728\u5927\u5385 \u95e8\u53e3\uff0c\u63a5\u7740\u4fbf\u5feb\u6b65\u8fce\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u663e\u7136\uff0c\u970d\u5143\u4f3d\u5bf9\u5929\u7985\u5927\u5e08\u7387\u4f17\u524d\u6765\uff0c\u65e9\u5df2\u6709\u5907\u3002 \u3000\u3000\u8fd9\u5bf9\u8ff7\u8e2a\u8c37\u6765\u8bf4\uff0c\u6beb\u65e0\u7591\u95ee\u662f\u4ef6\u7834\u5929\u8352\u7684\u5927\u4e8b\u3002 \u3000\u3000\u5802\u5802\u4e00\u4ee3\u6b66\u6797\u5927\u5b97\u5e08\u7684\u5c11\u6797\u6d3e\u638c\u95e8\u4eba\uff0e\u5c45\u7136\u80af\u9a7e\u4e34\u4e00\u5411\u88ab\u6b66\u6797\u6b63\u5927\u95e8\u6d3e\u89c6\u4e3a\u90aa\u6076\u7ec4\u7ec7\u7684 \u7eff\u6797\u603b\u5be8\uff0c\u5373\u4f7f\u5f53\u5e74\u80e1\u67cf\u9f84\u62c5\u4efb\u76df\u4e3b\u65f6\uff0c\u4e5f\u4ece\u672a\u6709\u8fc7\u3002 \u3000\u3000\u5ef6\u5165\u5927\u5385\uff0c\u970d\u5143\u4f3d\u63a5\u5f85\u5f97\u5341\u5206\u6bb7\u52e4\u4eb2\u5207\u3002 \u3000\u3000\u5929\u7985\u5927\u5e08\u4e3a\u4f7f\u6c14\u6c1b\u878d\u6d3d\uff0c\u53ea\u5e26\u4e86\u5929\u671b\u5927\u5e08\u3001\u9ea6\u5c0f\u660e\u3001\u82d7\u7d20\u82d3\u8fdb\u5165\u5927\u5385\uff0c\u5176\u4f59\u7684\u4e8c\u5341\u56db\u540d \u50e7\u4f17\uff0c\u90fd\u7559\u5728\u5927\u5385\u5916\u5e7f\u573a\u4e0a\u3002 \u3000\u3000\u5c11\u6797\u679c\u7136\u4e0d\u6127\u662f\u6b63\u5927\u95e8\u6d3e\uff0c\u6212\u5f8b\u68ee\u4e25\uff0c\u90a3\u4e8c\u5341\u56db\u540d\u50e7\u4fa3\uff0c\u867d\u7136\u5728\u5927\u5385\u5916\u7684\u6811\u4e0b\u4f11\u606f\uff0e\u4e2a \u4e2a\u4f9d\u6756\u6253\u5750\uff0c\u4ecd\u662f\u6392\u5217\u5f97\u4e95\u7136\u6709\u5e8f\u3002 \u3000\u3000\u970d\u5143\u4f3d\u548c\u5929\u7985\u5927\u5e08\u5bd2\u55a7\u4e86\u51e0\u53e5\u3001\u63a5\u7740\u671b\u4e86\u671b\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u8fd9\u4f4d\u9ea6\u5c0f\u5144\u5f1f\u5b9a\u662f\u638c\u95e8\u5927\u5e08\u7684 \u4fd7\u5bb6\u5f1f\u5b50\u4e86\uff1f\u201d \u3000\u3000\u5929\u7985\u5927\u5e08\u5fc3\u5934\u4e00\u9707\uff0c\u6697\u9053\uff1a\u201c\u83ab\u975e\u970d\u5143\u4f3d\u5df2\u8ba4\u51fa\u4ed6\u7684\u8eab\u4efd\uff0c\u4e0d\u7136\u600e\u4f1a\u77e5\u9053\u4ed6\u59d3\u9ea6\uff1f\u2026\u2026\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u5fc3\u77e5\u5929\u7985\u5927\u5e08\u5fc5\u5b9a\u7b54\u4e0d\u4e0a\u8bdd\uff0c\u5fd9\u62b1\u4e86\u62b1\u62f3\u9053\uff1a\u201c\u970d\u76df\u4e3b\u771f\u662f\u8d35\u4eba\u591a\u5fd8\u4e8b\uff0c\u5728\u4e0b\u82e5 \u975e\u5c11\u6797\u4fd7\u5bb6\u5f1f\u5b50\uff0c\u600e\u4f1a\u524d\u540e\u4e24\u6b21\u6765\u5230\u8d35\u8c37\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u563f\u563f\u7b11\u9053\uff1a\u201c\u8001\u592b\u5f53\u7136\u5fd8\u4e0d\u4e86\uff0c\u4e0a\u6b21\u5c0f\u5144\u5f1f\u968f\u5929\u673a\u3001\u5929\u89c9\u4e24\u4f4d\u8001\u7985\u5e08\u6765\uff0c\u4e0d\u4f46\u4f24 \u4e86\u5d06\u5cd2\u56db\u4e49\uff0c\u66f4\u9669\u4e9b\u7834\u4e86\u8001\u592b\u7684\u94dc\u4eba\u9635\uff0c\u771f\u4e0d\u6127\u662f\u4f4d\u5c11\u5e74\u82f1\u96c4\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u51b7\u58f0\u9053\uff1a\u201c\u970d\u76df\u4e3b\u4e0d\u5fc5\u8bf4\u9898\u5916\u8bdd\uff0c\u5728\u4e0b\u7684\u5f1f\u5f1f\u9ea6\u5c0f\u660e\u56de\u6765\u6ca1\u6709\uff1f\u8fd9\u6b21\u5728\u4e0b\u603b\u8be5\u80fd \u89c1\u7740\u4ed6\u4e86\uff01\u201d \u3000\u3000\u5929\u7985\u5927\u5e08\u7ec8\u4e8e\u660e\u767d\u970d\u5143\u4f3d\u5e76\u672a\u771f\u6b63\u8bc6\u7834\u9ea6\u5c0f\u660e\u7684\u8eab\u4efd\u3002 \u3000\u3000\u970d\u5143\u4f3d\u62c2\u4e86\u62c2\u957f\u9aef\u9053\uff1a\u201c\u9ea6\u5c0f\u5144\u5f1f\u653e\u5fc3\uff0c\u5f85\u4f1a\u513f\u8001\u592b\u4e00\u5b9a\u8ba9\u4f60\u4eec\u5144\u5f1f\u76f8\u4f1a\u3002\u201d \u3000\u3000\u5929\u7985\u5927\u5e08\u8fd9\u624d\u91cd\u91cd\u54b3\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u8001\u8872\u6b64\u6765\u76ee\u7684\uff0c\u970d\u76df\u4e3b\u60f3\u5fc5\u5df2\u7ecf\u660e\u767d\uff0c\u7528\u4e0d\u7740\u518d\u591a\u8bf4 \u4e86\u5427\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u970d\u67d0\u5f53\u7136\u660e\u767d\uff0c\u8001\u7985\u5e08\u662f\u8981\u62bc\u56de\u8d35\u6d3e\u5f1f\u5b50\u609f\u660e\u548c\u90a3\u51e0\u540d\u94dc\u4eba\u3002\u201d \u3000\u3000\u201c\u970d\u76df\u4e3b\u65e2\u7136\u660e\u767d\uff0c\u5c31\u8bf7\u628a\u4ed6\u4eec\u4ea4\u7ed9\u8001\u8872\u5e26\u8d70\u3002\u201d \u3000\u3000\u201c\u5f53\u7136\u53ef\u4ee5\uff0c\u4e0a\u6b21\u8d35\u6d3e\u7684\u5929\u673a\u5929\u89c9\u4e24\u4f4d\u8001\u7985\u5e08\u6765\u65f6\uff0c\u970d\u67d0\u4e5f\u4e0d\u66fe\u62d2\u7edd\u4ed6\u4eec\u7684\u8981\u6c42\uff0e\u53ef\u60dc \u609f\u660e\u548c\u4e5d\u540d\u94dc\u4eba\u4e0d\u613f\u518d\u56de\u5c11\u6797\uff0c\u970d\u67d0\u4e5f\u4e0d\u613f\u52c9\u5f3a\u4ed6\u4eec\u3002\u201d \u3000\u3000\u201c\u970d\u76df\u4e3b\u53ef\u5bb9\u8001\u8872\u548c\u4ed6\u4eec\u4e00\u89c1\uff1f\u201d \u3000\u3000\u201c\u638c\u95e8\u8001\u7985\u5e08\u6709\u547d\uff0c\u970d\u67d0\u5c82\u6562\u4e0d\u9075\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u7acb\u5373\u4ea4\u4ee3\u5927\u5385\u5185\u4e00\u540d\u624b\u4e0b\u9053\uff1a\u201c\u628a\u4ed6\u4eec\u5e26\u8fdb\u5927\u5385\u6765\uff01\u201d \u3000\u3000\u4e0d\u5230\u76cf\u8336\u5de5\u592b\uff0c\u609f\u660e\u679c\u7136\u7387\u9886\u4e5d\u540d\u94dc\u4eba\u6765\u5230\u5927\u5385\uff0c\u5728\u5c4f\u98ce\u8fb9\u7ad9\u4f4f\u3002 \u3000\u3000\u970d\u5143\u4f3d\u559d\u9053\uff1a\u201c\u8d35\u6d3e\u638c\u95e8\u4eba\u5230\u4e86\uff0c\u4f60\u4eec\u8fd8\u4e0d\u4e0a\u524d\u89c1\u793c\uff1f\u201d \u3000\u3000\u5c82\u77e5\u609f\u660e\u548c\u4e5d\u540d\u94dc\u4eba\u5374\u7ad9\u5728\u90a3\u91cc\u4e0d\u52a8\uff0c\u800c\u4e14\u8fde\u6b63\u773c\u4e5f\u6ca1\u770b\u5929\u7985\u5927\u5e08\u4e00\u4e0b\u3002 \u3000\u3000\u970d\u5143\u4f3d\u518d\u559d\u9053\uff1a\u201c\u609f\u660e\uff0c\u8001\u592b\u7684\u8bdd\u542c\u5230\u6ca1\u6709\uff1f\u201d \u3000\u3000\u609f\u660e\u8eac\u8eab\u7b54\u9053\uff1a\u201c\u5c5e\u4e0b\u4eec\u65e2\u5df2\u6295\u5954\u8ff7\u8e2a\u8c37\uff0e\u4fbf\u4e0d\u518d\u662f\u5c11\u6797\u4e2d\u4eba\uff0c\u4f55\u5fc5\u518d\u548c\u5c11\u6797\u638c\u95e8\u89c1 \u793c\u3002\u201d \u3000\u3000\u5929\u7985\u5927\u5e08\u5185\u529f\u518d\u6df1\uff0e\u6db5\u517b\u518d\u597d\uff0c\u8fd9\u65f6\u4e5f\u6c14\u5f97\u5168\u8eab\u53d1\u6296\u3002 \u3000\u3000\u970d\u5143\u4f3d\u7b11\u9053\uff1a\u201c\u638c\u95e8\u8001\u7985\u5e08\uff0c\u609f\u660e\u65e2\u7136\u8bf4\u51fa\u8fd9\u79cd\u8bdd\uff0c\u8fde\u8001\u592b\u4e5f\u65e0\u53ef\u5948\u4f55\u4e86\uff01\u201d \u3000\u3000\u5929\u7985\u5927\u5e08\u5f3a\u81ea\u6291\u5236\u7740\u6012\u706b\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u5c31\u8981\u4ed6\u4eec\u9000\u4e0b\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u8f6c\u8fc7\u5934\u53bb\u9053\uff1a\u201c\u4f60\u4eec\u9000\u5230\u5927\u5385\u540e\u5f85\u547d\uff01\u201d \u3000\u3000\u5929\u7985\u5927\u5e08\u7f04\u9ed8\u4e86\u8bb8\u4e45\uff0c\u624d\u53c8\u95ee\u9053\uff1a\u201c\u4e0a\u6b21\u8001\u8872\u7684\u5929\u89c9\u5e08\u5f1f\u548c\u5341\u4e8c\u540d\u672c\u6d3e\u5f1f\u5b50\uff0c\u4ed6\u4eec\u53ef\u8fd8 \u7559\u5728\u8d35\u8c37\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u4e0d\u52a8\u58f0\u8272\u9053\uff1a\u201c\u4ed6\u4eec\u90fd\u5728\uff0e\u970d\u67d0\u5bf9\u4ed6\u4eec\u4e00\u76f4\u59a5\u5584\u62db\u5f85\uff0e\u5176\u4e2d\u6709\u51e0\u4f4d\u53d7\u8fc7\u4f24\u7684\uff0e\u76ee \u524d\u4e5f\u90fd\u5b8c\u5168\u5eb7\u590d\u3002\u201d \u3000\u3000\u201c\u53ef\u5426\u5bb9\u4ed6\u4eec\u4e5f\u6765\u548c\u8001\u8872\u4e00\u89c1\uff1f\u201d \u3000\u3000\u201c\u638c\u95e8\u8001\u7985\u5e08\u8fdc\u9053\u800c\u6765\uff0c\u970d\u67d0\u5c82\u80fd\u4e0d\u5bb9\u4e00\u89c1\uff1f\u201d \u3000\u3000\u4e00\u65c1\u7684\u641c\u9b42\u624b\u5df4\u5929\u4e49\u9053\uff1a\u201c\u5728\u4e0b\u9a6c\u4e0a\u5c31\u628a\u4ed6\u4eec\u8bf7\u6765\uff01\u201d \u3000\u3000\u8fd9\u6b21\u6765\u5f97\u6bd4\u609f\u660e\u548c\u4e5d\u540d\u94dc\u4eba\u66f4\u5feb\uff0c\u8fde\u5929\u673a\u5927\u5e08\u4e5f\u8d6b\u7136\u5728\u5185\uff0e \u3000\u3000\u5929\u673a\u5927\u5e08\u3001\u5929\u89c9\u5927\u5e08\u548c\u5341\u4e8c\u540d\u5c11\u6797\u5f1f\u5b50\uff0c\u770b\u6765\u679c\u7136\u8eab\u4f53\u5f88\u597d\uff0c\u4f46\u773c\u795e\u5374\u6709\u4e9b\u5446\u6ede\u3002 \u3000\u3000\u90a3\u5341\u4e8c\u540d\u5c11\u6797\u5f1f\u5b50\u4e5f\u5728\u5c4f\u98ce\u524d\u7ad9\u4f4f\u3002 \u3000\u3000\u53ea\u6709\u5929\u673a\u5927\u5e08\u548c\u5929\u89c9\u5927\u5e08\u7f13\u7f13\u76f4\u8d70\u8fc7\u6765\uff0c\u4f46\u8fc7\u6765\u540e\u5374\u7ad9\u5728\u970d\u5143\u4f3d\u8eab\u65c1\u3002 \u3000\u3000\u5929\u7985\u5927\u5e08\u4e00\u9635\u6fc0\u52a8\uff0c\u7ad9\u8d77\u8eab\u6765\u53eb\u9053\uff1a\u201c\u4e8c\u4f4d\u5e08\u5f1f\uff0c\u4f60\u4eec\u2026\u2026\u201d \u3000\u3000\u5929\u673a\u5927\u5e08\u53cc\u624b\u5408\u5341\uff0e\u8bf5\u4e86\u58f0\u4f5b\u53f7\u9053\uff1a\u201c\u638c\u95e8\u5e08\u5144\uff0e\u4f60\u600e\u4e48\u4e5f\u5230\u8ff7\u8e2a\u8c37\u7c73\u4e86\uff1f\u83ab\u975e\u4e5f\u662f\u60f3 \u5f52\u987a\u970d\u76df\u4e3b\uff1f\u201d \u3000\u3000\u5929\u7985\u5927\u5e08\u5927\u611f\u5403\u60ca\uff0e\u51e0\u4e4e\u96be\u4ee5\u76f8\u4fe1\u81ea\u5df1\u7684\u8033\u6735\uff0c\u5931\u58f0\u9053\uff1a\u201c\u5929\u673a\u5e08\u5f1f\uff0c\u4f60\u600e\u4f1a\u53d8\u6210\u8fd9 \u6837\uff1f\u201d \u3000\u3000\u5929\u673a\u5927\u5e08\u7b11\u9053\uff1a\u201c\u6211\u5f88\u597d\uff0c\u638c\u95e8\u5e08\u5144\u8fd9\u8bdd\u2026\u2026\u201d \u3000\u3000\u5929\u7985\u5927\u5e08\u5927\u58f0\u9053\uff1a\u201c\u4f60\u54ea\u91cc\u662f\u5f88\u597d\uff0c\u5206\u660e\u5df2\u7ecf\u795e\u667a\u4e0d\u6e05\uff01\u201d \u3000\u3000\u5929\u673a\u5927\u5e08\u6447\u5934\u9053\uff1a\u201c\u5e08\u5144\u8bf4\u54ea\u91cc\u8bdd\uff1f\u96be\u9053\u5c0f\u5f1f\u8fd8\u6709\u4ec0\u4e48\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u5929\u7985\u5927\u5e08\u521a\u8981\u518d\u8bf4\u6d3b\uff0c\u9ea6\u5c0f\u660e\u5df2\u62a2\u7740\u9053\uff1a\u201c\u638c\u95e8\u4eba\u4e0d\u5fc5\u518d\u95ee\uff0c\u73b0\u5728\u5f1f\u5b50\u6253\u8bdd\u8981\u95ee\u95ee\u970d\u76df \u4e3b\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u9ea6\u5c0f\u5144\u5f1f\u6709\u4ec0\u4e48\u8bdd\u8981\u95ee\u8001\u592b\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u53cc\u76ee\u5c04\u5149\uff0e\u89c6\u7ebf\u76f4\u903c\u5230\u970d\u5143\u4f3d\u8138\u4e0a\u9053\uff1a\u201c\u4f60\u53ef\u662f\u7ed9\u4ed6\u4eec\u670d\u4e86\u2018\u5411\u5fc3\u9732\u2019\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u8138\u8272\u4e00\u53d8\u9053\uff1a\u201c\u8001\u592b\u4e0d\u61c2\u4f60\u8bf4\u4e9b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u4f60\u771f\u4e0d\u61c2\u8fd8\u662f\u5047\u4e0d\u61c2\uff1f\u201d \u3000\u3000\u201c\u8001\u592b\u66fe\u542c\u8bf4\u8fc7\u2018\u5411\u5fc3\u9732\u2019\u662f\u9634\u624b\u4e00\u9b54\u7684\u72ec\u95e8\u836f\u7269\uff0c\u4f46\u9634\u624b\u4e00\u9b54\u4e00\u5411\u5bf9\u8ff7\u8e2a\u8c37\u4e0d\u6000\u597d\u610f\uff0c \u8001\u592b\u53c8\u5230\u54ea\u91cc\u5f04\u6765\u2018\u5411\u5fc3\u9732\u2019\uff1f\u201d \u5367\u9f99\u751f\u300a\u5999\u7edd\u5929\u9999\u300b \u7b2c\u4e8c\u5341\u4e5d\u56de\u3000\u5c3d\u91ca\u524d\u5acc \u3000\u3000\u201c\u5728\u4e0b\u95ee\u7684\u53ea\u662f\u4f60\u662f\u5426\u5582\u4ed6\u4eec\u670d\u4e86\u2018\u5411\u5fc3\u9732\u2019\uff0c\u81f3\u4e8e\u4f60\u90a3\u2018\u5411\u5fc3\u9732\u2019\u662f\u600e\u4e48\u6765\u7684\uff0c\u90a3\u662f \u4f60\u7684\u4e8b\uff0c\u7528\u4e0d\u7740\u5728\u4e0b\u77e5\u9053\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u795e\u8272\u4e2d\u6740\u673a\u7a81\u663e\uff0c\u671b\u4e86\u671b\u5929\u7985\u5927\u5e08\u9053\uff1a\u201c\u8d35\u6d3e\u8fd9\u540d\u4fd7\u5bb6\u5f1f\u5b50\uff0c\u8bed\u6c14\u5982\u6b64\u903c\u4eba\uff0c\u5982 \u679c\u638c\u95e8\u8001\u7985\u5e08\u4e0d\u7ba1\u6559\uff0c\u8001\u592b\u5012\u60f3\u66ff\u8d35\u6d3e\u4ee3\u7ba1\u4e00\u7ba1\uff01\u201d \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0e\u9ea6\u5c0f\u660e\u62ac\u624b\u5f80\u8138\u4e0a\u4e00\u62b9\uff0e\u5df2\u63ed\u4e0b\u4eba\u76ae\u9762\u5177\uff0c\u9732\u51fa\u672c\u6765\u9762\u76ee\uff0c\u4e00\u9762\u6717\u58f0\u9053\uff1a \u201c\u970d\u5143\u4f3d\uff0e\u4f60\u73b0\u5728\u8be5\u77e5\u9053\u6211\u662f\u8c01\u4e86\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u8fd9\u79cd\u7a81\u5982\u5176\u6765\u7684\u4e3e\u52a8\uff0c\u4e0d\u4f46\u5929\u7985\u548c\u5929\u671b\u5927\u5e08\u88ab\u5f04\u5f97\u4e0d\u77e5\u6240\u63aa\uff0e\u8fde\u82d7\u7d20\u82d3\u4e5f\u5927\u611f\u610f \u5916\u3002 \u3000\u3000\u53ea\u542c\u970d\u5143\u4f3d\u51b7\u68ee\u68ee\u7b11\u9053\uff1a\u201c\u8001\u592b\u65e9\u5c31\u6599\u5230\u4f60\u5fc5\u662f\u9ea6\u5c0f\u660e\uff0c\u53ea\u662f\u5f53\u7740\u5929\u7985\u5927\u5e08\u7684\u9762\uff0c\u4e0d\u4fbf \u63ed\u7834\u800c\u5df2\u3002\u73b0\u5728\u4f60\u81ea\u8d25\u5f62\u9057\u8ff9\uff0c\u90a3\u5c31\u522b\u602a\u8001\u592b\u624b\u4e0b\u65e0\u60c5\u4e86\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u53f1\u9053\uff1a\u201c\u4f60\u60f3\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u8001\u592b\u65e9\u5c31\u60f3\u628a\u4f60\u788e\u5c38\u4e07\u65ad\uff0e\u4ee5\u89e3\u5fc3\u5934\u4e4b\u6068\u3002\u53ef\u60dc\u59cb\u7ec8\u6ca1\u6cd5\u902e\u5230\u4f60\uff0c\u73b0\u5728\u4f60 \u662f\u81ea\u6295\u7f57\u7f51\uff0c\u6028\u4e0d\u5230\u54ea\u4e2a\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u84e6\u5730\u62d4\u51fa\u957f\u5251\uff0c\u5927\u559d\u9053\uff1a\u201c\u970d\u5143\u4f3d\uff01\u4f60\u82e5\u4e0d\u670d\u6c14\uff0c\u53ea\u7ba1\u4e0a\u524d\u548c\u5728\u4e0b\u6bd4\u8bd5\u6bd4\u8bd5\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u77e5\u5bf9\u65b9\u6b66\u529f\u4e0d\u5728\u81ea\u5df1\u4e4b\u4e0b\uff0e\u6b64\u65f6\u6b64\u5730\uff0c\u4ed6\u5f53\u7136\u4e0d\u613f\u81ea\u5df1\u5c71\u624b\uff0e \u3000\u3000\u5f53\u4e0b\uff0c\u563f\u563f\u7b11\u4e86\u51e0\u58f0\u9053\uff1a\u201c\u8001\u592b\u6b63\u5728\u5927\u5385\u62db\u5f85\u5c11\u6797\u6765\u7684\u8d35\u5ba2\uff0e\u4f60\u65e2\u7136\u60f3\u5728\u4e34\u6b7b\u4e4b\u524d\u8868\u6f14 \u8868\u6f14\u8eab\u624b\uff0c\u4e0d\u59a8\u73b0\u5728\u5c31\u5230\u5927\u5385\u5916\u5e7f\u573a\u53bb\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u6b63\u6b32\u8d81\u6b64\u673a\u4f1a\u903c\u95ee\u949f\u4e00\u8c6a\u7b49\u4eba\u7684\u4e0b\u843d\uff0c\u51b7\u51b7\u4e00\u7b11\u9053\uff1a\u201c\u4e0d\u8bba\u5728\u4ec0\u4e48\u5730\u65b9\uff0c\u5728\u4e0b\u8fd8 \u6015\u4e0d\u4e86\u4f60\uff0c\u4e0d\u8fc7\u5728\u4e0b\u60f3\u95ee\u4f60\u51e0\u53e5\u8bdd\u3002\u201d \u3000\u3000\u201c\u4f60\u60f3\u95ee\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u949f\u526f\u76df\u4e3b\u4ed6\u4eec\u90fd\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u201d \u3000\u3000\u201c\u5c82\u6709\u6b64\u7406\uff01\u4f60\u548c\u4ed6\u4eec\u5728\u2014\u8d77\uff0c\u7adf\u7136\u4e0d\u77e5\u4ed6\u4eec\u4e0b\u843d\uff0c\u53cd\u6765\u95ee\u8001\u592b\uff0c\u5f53\u771f\u662f\u5929\u5927\u7b11\u8bdd\uff01\u201d \u3000\u3000\u201c\u4f60\u8981\u8bf4\u5b9e\u8bdd\u3002\u4ed6\u4eec\u90fd\u4e0d\u89c1\u4e86\uff0c\u662f\u5426\u4e5f\u88ab\u4f60\u63b3\u8fdb\u8c37\u6765\uff1f\u201d \u3000\u3000\u201c\u4f60\u660e\u767d\u5c31\u597d\u3002\u201d \u3000\u3000\u201c\u4ed6\u4eec\u76ee\u524d\u4eba\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u201c\u53ea\u8981\u5f85\u4f1a\u513f\u4f60\u4e0d\u6b7b\uff0c\u81ea\u7136\u770b\u5f97\u5230\u4ed6\u4eec\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u4e0d\u518d\u95ee\u8bdd\uff0c\u7eb5\u8eab\u75be\u8fdb\uff0c\u957f\u5251\u4e00\u63a0\uff0c\u95ea\u7535\u822c\u75be\u5411\u970d\u5143\u4f3d\u5f53\u80f8\u523a\u53bb\u3002 \u3000\u3000\u53ea\u542c\u4e00\u58f0\u91d1\u94c1\u5927\u9707\uff0c\u957f\u5251\u5df2\u88ab\u970d\u5143\u4f3d\u7684\u9752\u9f99\u593a\u8361\u504f\u3002 \u3000\u3000\u970d\u5143\u4f3d\u63a5\u4e0b\u4e00\u62db\uff0c\u7acb\u5373\u4e00\u4e2a\u5012\u7eb5\uff0c\u8dc3\u5230\u5927\u5385\u95e8\u5916\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u521a\u8981\u8ddf\u8fc7\u53bb\uff0c\u5374\u88ab\u641c\u9b42\u624b\u5df4\u5929\u4e49\u548c\u62d8\u9b44\u7d22\u5b8b\u5929\u94ce\u4ea4\u4e92\u62e6\u4f4f\u3002 \u3000\u3000\u5f53\u9ea6\u5c0f\u660e\u51fb\u9000\u5df4\u5929\u4e49\u548c\u5b8b\u5929\u94ce\uff0c\u970d\u5143\u4f3d\u65e9\u5df2\u559d\u4ee4\u624b\u4e0b\u4eba\u5c06\u5927\u5385\u95e8\u53e3\u91cd\u91cd\u56f4\u4f4f\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u6765\u5230\u95e8\u53e3\uff0e\u53ea\u89c1\u6570\u4e08\u4e4b\u5916\uff0c\u8ff7\u8e2a\u8c37\u7684\u9ad8\u624b\u5df2\u56f4\u62e2\u4e86\u4e09\u56db\u5c42\uff0c\u8db3\u8db3\u6709\u56db\u4e94\u5341\u4eba\u4e4b\u591a\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u5929\u7985\uff0c\u5929\u671b\u4e24\u5927\u5e08\u548c\u82d7\u7d20\u82d3\u4e5f\u5168\u8ddf\u5230\u4e86\u5927\u5385\u95e8\u53e3\u3002 \u3000\u3000\u5ffd\u542c\u4eba\u7fa4\u4e2d\u53d1\u51fa\u4e2a\u51b7\u68ee\u68ee\u7684\u58f0\u97f3\u9053\uff1a\u201c\u970d\u76df\u4e3b\uff0c\u628a\u8fd9\u5c0f\u5b50\u7ed9\u8001\u592b\u5427\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u609a\u7136\u4e00\u60ca\uff0c\u5faa\u53d1\u8bdd\u4e4b\u58f0\u671b\u53bb\uff0c\u6b64\u4eba\u8d6b\u7136\u662f\u9634\u624b\u4e00\u9b54\uff01 \u3000\u3000\u5c24\u5176\u4ee4\u4ed6\u5927\u611f\u610f\u5916\u7684\uff0c\u8fde\u6bd2\u706b\u5168\u6210\u3001\u9ed1\u9b54\u65f6\u5bc5\u4ee5\u53ca\u706b\u83bd\u3001\u725b\u5954\u4e5f\u5728\u5176\u4e2d\u3002 \u3000\u3000\u7b80\u76f4\u4ee4\u4eba\u96be\u4ee5\u76f8\u4fe1\uff0c\u8fd9\u51e0\u4eba\u5c45\u7136\u4e5f\u6295\u5954\u4e86\u8ff7\u8e2a\u8c37\u3002 \u3000\u3000\u4f46\u89c1\u9634\u624b\u4e00\u9b54\u6392\u5f00\u4ece\u4eba\uff0e\u7f13\u7f13\u6765\u5230\u5305\u56f4\u5708\u4e4b\u5185\uff0c\u563f\u563f\u7b11\u9053\uff1a \u3000\u3000\u201c\u5c0f\u5b50\uff0e\u4eca\u5929\u4f60\u8fd8\u5f80\u54ea\u91cc\u8dd1\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9a82\u9053\uff1a\u201c\u597d\u4e00\u4e2a\u65e0\u803b\u7684\u8001\u738b\u516b\u86cb\uff0c\u7adf\u7136\u4e5f\u5f52\u987a\u4e86\u65e0\u6076\u4e0d\u505a\u7684\u970d\u5143\u4f3d\uff01\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u5e72\u7b11\u4e86\u51e0\u58f0\u9053\uff1a\u201c\u8bc6\u65f6\u52a1\u8005\u4e3a\u4fca\u6770\uff0c\u8001\u592b\u548c\u970d\u76df\u4e3b\u5316\u654c\u4e3a\u53cb\uff0c\u53c8\u6709\u4ec0\u4e48\u4e0d\u53ef\uff1f \u90a3\u4e2a\u5047\u80e1\u67cf\u9f84\u54ea\u91cc\u53bb\u4e86\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u6602\u7136\u9053\uff1a\u201c\u6536\u62fe\u4f60\u8fd9\u9634\u624b\u8001\u6df7\u86cb\uff0c\u4f55\u987b\u80e1\u76df\u4e3b\u4eb2\u81ea\u524d\u6765\uff01\u201d \u3000\u3000\u201c\u4f60\u5f53\u771f\u8fd8\u6562\u4e0e\u8001\u592b\u52a8\u624b\uff1f\u201d \u3000\u3000\u201c\u53ea\u95ee\u4f60\u6562\u4e0d\u6562\u548c\u5728\u4e0b\u52a8\u624b\uff1f\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u624b\u4e2d\u62c2\u5c18\u4e00\u6325\u9053\uff1a\u201c\u90a3\u5c31\u522b\u602a\u8001\u592b\u5fc3\u72e0\u624b\u8fa3\u4e86\uff01\u201d \u3000\u3000\u4ed6\u7684\u90a3\u67c4\u62c2\u5c18\uff0c\u672c\u6765\u5df2\u88ab\u80e1\u67cf\u9f84\u62e6\u8170\u524a\u65ad\uff0c\u6b64\u523b\u663e\u7136\u662f\u6362\u4e86\u4e00\u628a\u65b0\u7684\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u4e0d\u518d\u7b54\u8bdd\uff0c\u5355\u8db3\u8f7b\u8f7b\u4e00\u70b9\uff0c\u4eba\u5df2\u8dc3\u8d77\uff0c\u957f\u5251\u2014\u62db\u201c\u63b7\u4e91\u98de\u96ea\u201d\uff0c\u7f69\u5411\u9634\u624b\u4e00\u9b54\u4e0a \u76d8\uff0c\u51fa\u624b\u4e4b\u5feb\uff0c\u76f4\u5982\u7535\u5149\u77f3\u706b\u3002 \u3000\u3000\u9634\u624b\u4e00\u9b54\u65e9\u77e5\u9053\u9ea6\u5c0f\u660e\u8eab\u624b\u975e\u51e1\uff0c\u4f46\u4ed6\u81ea\u6043\u8eab\u4efd\uff0c\u4e0d\u80af\u95ea\u907f\uff0c\u4e00\u62db\u201c\u6325\u5c18\u6e05\u8c08\u201d\u8fce\u4e86\u4e0a \u53bb\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u5728\u534a\u7a7a\uff0e\u65e0\u6cd5\u501f\u529b\uff0c\u88ab\u62c2\u5c18\u4e00\u626b\uff0c\u5f53\u573a\u88ab\u626b\u5f97\u7a7a\u6454\u51fa\u4e94\u516d\u5c3a\uff0c\u624d\u843d\u4e0b\u5730\u6765\u3002 \u3000\u3000\u9634\u624b\u4e00\u9b54\u4e00\u51fa\u624b\u5373\u5360\u4e86\u4e0a\u98ce\uff0c\u5c82\u80af\u7f62\u4f11\uff1f\u8ddf\u4e0a\u53bb\u53c8\u662f\u2014\u62c2\u5c18\u626b\u4e0b\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u65bd\u51fa\u6458\u661f\u6b65\uff0c\u811aF\u8f7b\u8f7b\u4e00\u6ed1\uff0c\u8eab\u5b50\u5df2\u65cb\u5230\u9634\u624b\u4e00\u9b54\u5de6\u4fa7\uff0e\u957f\u5251\u75be\u523a\u5de6\u808b\u3002 \u3000\u3000\u9634\u624b\u4e00\u9b54\u5927\u5403\u4e00\u60ca\uff0c\u614c\u5fd9\u95f4\u62c2\u5c18\u5373\u6536\uff0e\u518d\u53cd\u624b\u6325\u51fa\u3002 \u3000\u3000\u8fd9\u4e00\u62db\u867d\u628a\u5251\u52bf\u5c01\u67b6\u5f00\u53bb\uff0c\u4f46\u62c2\u5c18\u5374\u88ab\u524a\u53bb\u4e00\u5927\u534a\uff0c\u7adf\u548c\u65e5\u524d\u5728\u4e5d\u5929\u7384\u9634\u6d1e\u5e9c\u524d\u548c\u80e1\u67cf \u9f84\u4ea4\u624b\u7684\u60c5\u5f62\u5b8c\u5168\u4e00\u6837\u3002 \u3000\u3000\u9634\u624b\u4e00\u9b54\u5927\u9a87\u4e4b\u4e0b\uff0c\u6025\u6025\u540e\u9000\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u5e76\u672a\u8d81\u673a\u8ffd\u88ad\uff0e\u56e0\u4e3a\u4ed6\u62c5\u5fc3\u6b64\u4e3e\u5c06\u62db\u60f9\u5bf9\u65b9\u7684\u7fa4\u8d77\u5408\u653b\uff0c\u53cd\u4e0d\u5982\u6682\u65f6\u8ba9\u9634\u624b\u4e00\u9b54 \u7a33\u4f4f\uff0c\u7136\u540e\u518d\u4f3a\u673a\u884c\u52a8\u3002 \u3000\u3000\u5728\u8fd9\u5239\u90a3\uff0c\u9634\u624b\u4e00\u9b54\u5df1\u6123\u5728\u5f53\u5730\uff0c\u4ed6\u548c\u9ea6\u5c0f\u660e\u524d\u540e\u4ea4\u624b\u4e09\u6b21\uff0e\u81ea\u89c9\u8fd9\u4e73\u81ed\u672b\u5e72\u7684\u5c0f\u5b50\u8eab \u624b\u7adf\u662f\u4e00\u6b21\u6bd4\u4e00\u6b21\u5f3a\uff0c\u4ee5\u76ee\u524d\u7684\u60c5\u5f62\u800c\u8bba\uff0c\u663e\u7136\u5df2\u9ad8\u8fc7\u81ea\u5df1\u3002 \u3000\u3000\u4ed6\u5728\u4f17\u76ee\u777d\u777d\u4e4b\u4e0b\u5f53\u7136\u4e0d\u80af\u8ba4\u8f93\uff0c\u51b3\u5b9a\u4ee5\u4ed6\u6700\u62ff\u624b\u7684\u9634\u98ce\u638c\u8fce\u654c\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u89c1\u9634\u624b\u4e00\u9b54\u51dd\u7acb\u4e0d\u52a8\uff0c\u53f3\u81c2\u5374\u4e0d\u4f4f\u9707\u6296\uff0c\u6de1\u6de1\u4e00\u7b11\u9053\uff1a\u201c\u8001\u5c0f\u5b50\uff01\u770b\u6837\u5b50\u4f60\u597d\u50cf \u72af\u4e0b\u4ec0\u4e48\u6bdb\u75c5\uff1f\u83ab\u975e\u53d1\u4e86\u7f8a\u766b\u75af\uff1f\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u56e0\u4e3a\u6b63\u8fd0\u6c14\uff0c\u65e0\u6cd5\u5f00\u53e3\uff0c\u53ea\u80fd\u767d\u767d\u6328\u9a82\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u4e3a\u4e86\u6c14\u4ed6\u4e00\u6c14\uff0c\u518d\u9a82\u9053\uff1a\u201c\u8001\u738b\u516b\u86cb\uff0c\u4f60\u4ec0\u4e48\u65f6\u5019\u53d8\u6210\u54d1\u5df4\u4e86\uff1f\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u5728\u5fcd\u65e0\u53ef\u5fcd\u4e4b\u4e0b\uff0c\u731b\u4e00\u54ac\u7259\uff0c\u594b\u529b\u62cd\u51fa\u2014\u638c\u3002 \u3000\u3000\u8fd9\u4e00\u638c\u662f\u4ed6\u594b\u52bf\u800c\u53d1\uff0c\u4f46\u56e0\u88ab\u9ea6\u5c0f\u660e\u9a82\u5f97\u4e39\u7530\u4e4b\u6c14\u65e0\u6cd5\u96c6\u4e2d\uff0e\u529b\u9053\u7adf\u7136\u5927\u4e0d\u5982\u524d\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u62c5\u5fc3\u88ab\u9634\u98ce\u88ad\u4e2d\uff0c\u2014\u7f15\u5f3a\u52b2\u7684\u6307\u98ce\u8fce\u7740\u638c\u98ce\u5c04\u53bb\u4e4b\u540e\uff0e\u7acb\u5373\u5411\u2014\u4fa7\u907f\u5f00\u4e94\u5c3a\u3002 \u3000\u3000\u53ea\u542c\u9634\u624b\u4e00\u9b54\u4e00\u58f0\u95f7\u54fc\uff0c\u5f53\u573a\u5411\u540e\u5012\u53bb\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u6b63\u6b32\u8fd1\u524d\u65bd\u88ad\uff0e\u6bd2\u706b\u6210\u5168\u548c\u9ed1\u9b54\u65f6\u5bc5\u5374\u53ca\u65f6\u53cc\u53cc\u653b\u4e86\u4e0a\u6765\u3002 \u3000\u3000\u8fd9\u4e8c\u4eba\u4e00\u4e2a\u662f\u4ee5\u6697\u5668\u548c\u706b\u5668\u4ee4\u9ed1\u767d\u4e24\u9053\u95fb\u540d\uff0d\u4e27\u80c6\uff0c\u4e00\u4e2a\u662f\u4ee5\u9ed1\u7802\u638c\u72ec\u6b65\u5927\u6c5f\u5357\u5317\uff0c\u4f46 \u56e0\u56db\u5468\u5e03\u6ee1\u4e86\u8ff7\u8e2a\u8c37\u7684\u4eba\uff0c\u8feb\u5f97\u4e8c\u4eba\u90fd\u4e0d\u6562\u65bd\u5c55\u6240\u957f\u3002 \u3000\u3000\u8fd9\u4e00\u6765\uff0e\u9ea6\u5c0f\u660e\u72ec\u6218\u6210\u5168\u548c\u65f6\u5bc5\uff0c\u4f9d\u7136\u6e38\u5203\u6709\u4f59\uff0c\u6beb\u65e0\u602f\u610f\uff0c\u4f46\u82e5\u60f3\u51fb\u9000\u4e8c\u4eba\uff0e\u5374\u4e5f\u5927 \u4e0d\u5bb9\u6613\u3002 \u3000\u3000\u84e6\u5730\uff0c\u9ea6\u5c0f\u660e\u5251\u62db\u52a0\u5feb\uff0c\u4f46\u89c1\u6f2b\u5929\u5149\u5f71\u7ffb\u6eda\u6643\u52a8\uff0e\u7adf\u7136\u5239\u65f6\u628a\u4e24\u540d\u9ed1\u9053\u5de8\u64d8\u5b8c\u5168\u7b3c\u7f69\u5728 \u68ee\u68ee\u5251\u6c14\u4e4b\u4e2d\u3002 \u3000\u3000\u539f\u6765\u4ed6\u5df2\u65bd\u51fa\u4e86\u201c\u5999\u5999\u5251\u51b3\u201d\u4e2d\u7684\u5251\u62db\u3002\u5a01\u529b\u4e4b\u731b\uff0e\u8fde\u4ed6\u81ea\u5df2\u4e5f\u96be\u4ee5\u60f3\u8c61\u3002 \u3000\u3000\u8fd9\u65f6\u6240\u6709\u89c2\u6218\u7684\u4eba\uff0e\u83ab\u4e0d\u4e3a\u9ea6\u5c0f\u660e\u7684\u5982\u6b64\u795e\u52c7\u800c\u5927\u611f\u60ca\u5f02\uff0c\u51e0\u4e4e\u4e2a\u4e2a\u770b\u5f97\u80c6\u98a4\u5fc3\u60ca\uff0c\u76ee \u8e6c\u53e3\u5446\u3002\u5c24\u5176\u970d\u5143\u4f3d\uff0e\u66f4\u662f\u611f\u5230\u4e0d\u53ef\u601d\u8bae\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u201c\u5530\u5530\u201d\u4e24\u58f0\u4e4b\u540e\uff0c\u6bd2\u706b\u6210\u5168\u548c\u9ed1\u9b54\u65f6\u5bc5\u5168\u5df2\u8840\u67d3\u6ee1\u8eab\u3002 \u3000\u3000\u970d\u5143\u4f3d\u7acb\u5373\u5927\u53eb\u9053\uff1a\u201c\u4e00\u8d77\u4e0a\uff01\u5343\u4e07\u522b\u8ba9\u8fd9\u5c0f\u5b50\u8d70\u8131\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u4e3a\u4e86\u8131\u8eab\uff0c\u5df2\u4e0d\u518d\u901e\u5f3a\uff0c\u80a9\u5934\u4e00\u6643\uff0c\u5c31\u5728\u4f17\u4eba\u5c1a\u672a\u56f4\u653b\u8fc7\u6765\u7684\u77ac\u95f4\uff0c\u4eba\u5df2\u817e\u7a7a\u800c \u8d77\uff0c\u201c\u4e00\u9e64\u51b2\u5929\u201d\uff0c\u76f4\u62d4\u8d77\u56db\u4e94\u4e08\u9ad8\u3002 \u3000\u3000\u7136\u540e\u7531\u91cd\u91cd\u5305\u56f4\u8005\u7684\u5934\u9876\u6a2a\u60ca\u800c\u8fc7\uff0c\u5f85\u843d\u4e0b\u5730\u65f6\uff0c\u5df2\u79bb\u5f00\u4eba\u7fa4\u4e03\u516b\u4e08\u8fdc\uff0e \u3000\u3000\u970d\u5143\u4f3d\u81ea\u77e5\u624b\u4e0b\u65e0\u4eba\u80fd\u591f\u8ffd\u4e0a\uff0c\u9664\u4e86\u8dfa\u811a\u53d1\u72e0\uff0e\u62a5\u672c\u6ca1\u6709\u522b\u7684\u529e\u6cd5\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u5927\u53eb\u9053\uff1a\u201c\u638c\u95e8\u8001\u7985\u5e08\uff0c\u7528\u4e0d\u7740\u518d\u5411\u970d\u5143\u4f3d\u8981\u4eba\uff0c\u5feb\u5feb\u51fa\u8c37\u8981\u7d27\uff01\u201d \u3000\u3000\u8bf4\u5b8c\u8bdd\uff0c\u65bd\u8d77\u8f7b\u529f\uff0c\u76f4\u5411\u8c37\u5916\u5954\u53bb\uff0e \u3000\u3000\u4e00\u8def\u4e4b\u4e0a\uff0c\u90a3\u4e9b\u628a\u5b88\u5173\u5361\u7684\u55bd\u5175\uff0c\u6709\u5c11\u6570\u51e0\u4eba\u963b\u6321\u7684\uff0c\u5168\u6b7b\u5728\u9ea6\u5c0f\u660e\u5251\u4e0b\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u4e00\u53e3\u6c14\u5954\u51fa\u8c37\u5916\uff0c\u624d\u627e\u4e86\u4e00\u5904\u6811\u4e0b\u4f11\u606f\u3002 \u3000\u3000\u4ed6\u867d\u7136\u5df2\u7ecf\u8131\u9669\uff0c\u5374\u4e0d\u80fd\u4e0d\u62c5\u5fc3\u5c11\u6797\u65b9\u9762\u4ee5\u53ca\u82d7\u7d20\u82d3\u7684\u5b89\u5371\uff0c\u4e8e\u662f\u5c31\u5728\u8c37\u53e3\u5916\u7b49\u5019\uff0c\u4e07 \u4e00\u5929\u7985\u5927\u5e08\u548c\u82d7\u7d20\u82d3\u7b49\u4eba\u4e0d\u89c1\u51fa\u8c37\uff0c\u4fbf\u51b3\u5b9a\u4e0d\u987e\u4e00\u5207\u7684\u518d\u6740\u56de\u8c37\u53bb\u3002 \u3000\u3000\u5c31\u5728\u4ed6\u521a\u5750\u4e0b\u4e0d\u4e45\uff0c\u7a81\u89c1\u4e00\u540d\u5168\u8eab\u94f6\u8272\u52b2\u88c5\uff0c\u5251\u7709\u661f\u76ee\uff0c\u733f\u81c2\u8702\u8170\uff0c\u80cc\u63d2\u5b9d\u5251\uff0c\u82f1\u6c14\u52c3 \u52c3\u7684\u5e74\u8f7b\u4eba\u5feb\u6b65\u51b2\u7740\u81ea\u5df1\u800c\u6765\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u4e00\u89c1\u6b64\u4eba\uff0c\u987f\u65f6\u4e00\u80a1\u6c14\u8840\u76f4\u51b2\uff0c\u8fc5\u901f\u65e0\u6bd4\u7684\u7ad9\u8eab\u800c\u8d77\uff0e\u62a2\u5251\u5411\u8fd9\u4eba\u5fc3\u80f8\u523a\u53bb\u3002 \u3000\u3000\u90a3\u4eba\u614c\u8feb\u4e4b\u95f4\uff0c\u3000\u4e00\u9762\u95ea\u907f\uff0c\u3000\u4e00\u9762\u6025\u6025\u53eb\u9052\uff0c\u201c\u9ea6\u5c0f\u4fa0\uff0c\u5728\u4e0b\u662f\u8303\u7389\u6606\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u559d\u9053\uff1a\u201c\u6211\u65e9\u5c31\u770b\u51fa\u4f60\u662f\u8303\u7389\u6606\uff0c\u6240\u4ee5\u624d\u8981\u6740\u4f60\uff01\u201d \u3000\u3000\u8bf4\u7740\u53c8\u662f\u4e00\u5251\u523a\u53bb\u3002 \u3000\u3000\u8303\u7389\u6606\u5c31\u52bf\u4e00\u4e2a\u56de\u65cb\uff0c\u6717\u58f0\u9053\uff1a\u201c\u9ea6\u5c0f\u4fa0\uff0c\u542c\u5728\u4e0b\u89e3\u91ca\u8fc7\u540e\u518d\u52a8\u624b\u5982\u4f55\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u89c1\u5bf9\u65b9\u4e0d\u66fe\u8fd8\u624b\uff0e\u8fde\u5251\u4e5f\u6ca1\u62d4\uff0c\u53ea\u597d\u505c\u4e0b\u624b\u6765\u9053\uff1a\u201c\u6709\u4ec0\u4e48\u8bdd\u5feb\u8bf4\uff0e\u7b49\u8bf4\u5b8c\u4e86\u6211 \u518d\u6740\u4f60\uff01\u201d \u3000\u3000\u8303\u7389\u6606\u968f\u5373\u5c31\u5730\u5750\u4e0b\u9053\uff1a\u201c\u8bf4\u6765\u8bdd\u957f\uff0c\u9ea6\u5c0f\u4fa0\u4e5f\u8bf7\u5750\u4e0b\u597d\u5417\uff1f\u201d \u3000\u3000\u4ed6\u8981\u6c42\u9ea6\u5c0f\u660e\u5750\u4e0b\u6765\uff0c\u4e0d\u5916\u662f\u8ba9\u4ed6\u5b89\u9759\uff0e\u514d\u52a8\u5e72\u6208\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u89c1\u5bf9\u65b9\u5982\u6b64\u6beb\u4e0d\u8bbe\u9632\uff0c\u53cd\u800c\u89c9\u5f97\u81ea\u5df1\u592a\u83bd\u649e\uff0c\u4fbf\u4f9d\u8a00\u4e5f\u5728\u5bf9\u9762\u5750\u4e0b\u3002 \u3000\u3000\u539f\u6765\u8fd9\u8303\u7389\u6606\u662f\u8c6b\u5357\u8303\u5bb6\u5821\u7684\u5c11\u5821\u4e3b\uff0e\u4ed6\u7684\u7236\u4eb2\u8303\u94dc\u5c71\uff0c\u5341\u5e74\u524d\u66fe\u603b\u9886\u5927\u6c5f\u5357\u5317\u4fa0\u4e49\u9053\uff0c \u662f\u4f4d\u5a01\u540d\u8fdc\u64ad\u53d7\u6b66\u6797\u4e2d\u4eba\u4eba\u656c\u4ef0\u7684\u82f1\u96c4\u4eba\u7269\u3002 \u3000\u3000\u4e09\u5e74\u524d\uff0c\u8303\u7389\u6606\u968f\u7236\u4eb2\u8303\u94dc\u5c71\u5317\u6765\u6052\u5c71\uff0c\u5728\u8ff7\u8e2a\u8c37\u9644\u8fd1\u7684\u843d\u96c1\u8c37\uff0c\u8303\u94dc\u5c71\u6b7b\u5728\u795e\u6756\u7fc1\u9146 \u79cb\u3001\u6bd2\u706b\u6210\u5168\u3001\u9b3c\u8001\u6c34\u5bd2\u3001\u4eba\u9b54\u4f0d\u72ec\u7b49\u4eba\u624b\u4e2d\uff0c\u800c\u8303\u7389\u6606\u5374\u53c8\u8bef\u6740\u4e86\u80e1\u67cf\u9f84\u3002 \u3000\u3000\u8bf4\u8d77\u6765\u4e09\u5e74\u524d\u843d\u96c1\u8c37\u90a3\u4e00\u6218\uff0c\u5185\u60c5\u5b9e\u5728\u9519\u7efc\u590d\u6742\uff0c\u4ee5\u81f4\u6069\u4ec7\u96be\u8fa8\uff0c\u800c\u9ea6\u5c0f\u660e\u81f3\u4eca\u4ecd\u8ba4\u4e3a \u8303\u7389\u6606\u662f\u6740\u6b7b\u80e1\u67cf\u9f84\u7684\u552f\u2014\u51f6\u624b\u3002 \u3000\u3000\u5f53\u65f6\uff0c\u662f\u795e\u6756\u7fc1\u9146\u79cb\u3001\u6bd2\u706b\u6210\u5168\u3001\u9b3c\u8001\u6c34\u5bd2\u3001\u4eba\u9b54\u4f0d\u72ec\u56db\u5927\u9ed1\u9053\u9b54\u5934\u79d8\u5bc6\u6765\u5230\u843d\u96c1\u8c37\uff0c \u610f\u6b32\u6d88\u706d\u4ee5\u5929\u660e\u5927\u5e08\u548c\u7d2b\u9633\u9053\u957f\u4e3a\u9996\u7684\u5c11\u6797\u3001\u6b66\u5f53\u4e8c\u6d3e\u7cbe\u82f1\u4ee5\u53ca\u5317\u6765\u7684\u8303\u94dc\u5c71\u7236\u5b50\u3002 \u3000\u3000\u80e1\u67cf\u9f84\u5f53\u65f6\u6b63\u662f\u8ff7\u8e2a\u8c37\u7684\u7eff\u6797\u76df\u4e3b\uff0e\u56e0\u4e3a\u4ed6\u662f\u9146\u79cb\u7684\u5e08\u4f84\uff0c\u4fbf\u88ab\u9146\u79cb\u9080\u5230\u8ff7\u8e2a\u8c37\u5916\u7684\u843d \u96c1\u8c37\u8981\u4ed6\u76f8\u52a9\u2014\u81c2\u4e4b\u529b\uff0c\u5171\u540c\u5bf9\u4ed8\u5c11\u6797\u3001\u6b66\u5f53\u4e8c\u6d3e\u548c\u8303\u94dc\u5c71\u7236\u5b50\u3002 \u3000\u3000\u80e1\u67cf\u9f84\u5f97\u6089\u8be6\u60c5\u540e\uff0c\u66fe\u529b\u529d\u9146\u79cb\u7b49\u4eba\uff0e\u4e0d\u53ef\u4e0e\u5c11\u6797\uff0c\u6b66\u5f53\u7ed3\u6028\u3002 \u3000\u3000\u4f46\u9146\u79cb\u7b49\u4eba\u5374\u504f\u504f\u4e0d\u542c\uff0c\u4ed6\u4eec\u597d\u4e0d\u5bb9\u6613\u5b8c\u6210\u628a\u5c11\u6797\u3001\u6b66\u5f53\u4ee5\u53ca\u8303\u94dc\u5c71\u7236\u5b50\u8bf1\u6765\u843d\u96c1\u8c37\u7684 \u8ba1\u5212\uff0e\u5c82\u80af\u8f7b\u8a00\u653e\u5f03\uff1f\u4ecd\u6253\u7b97\u4ee5\u706b\u836f\u70b8\u6b7b\u8fd9\u4e9b\u4eba\u3002 \u3000\u3000\u4e8e\u662f\uff0c\u5728\u4e0d\u4e45\u4e4b\u540e\uff0c\u53cc\u65b9\u679c\u7136\u5728\u843d\u96c1\u8c37\u5c55\u5f00\u4e00\u573a\u60e8\u70c8\u65e0\u6bd4\u7684\u6fc0\u6218\u3002 \u3000\u3000\u5c11\u6797\u3001\u6b66\u5f53\u548c\u8303\u5bb6\u5821\u65b9\u9762\u8bef\u4ee5\u4e3a\u80e1\u67cf\u9f84\u662f\u9146\u79cb\u7b49\u4eba\u7684\u5e2e\u51f6\uff0c\u800c\u9146\u79cb\u7b49\u4eba\u53c8\u8ba4\u4e3a\u80e1\u67cf\u9f84\u8981 \u6697\u52a9\u5c11\u6797\u3001\u6b66\u5f53\u548c\u8303\u5bb6\u5821\u3002 \u3000\u3000\u56e0\u4e4b\uff0c\u5728\u4e24\u9762\u5939\u51fb\u4e4b\u4e0b\uff0c\u80e1\u67cf\u9f84\u4fbf\u58ee\u70c8\u727a\u7272\u3002 \u3000\u3000\u4ed6\u5148\u88ab\u8303\u7389\u6606\u7531\u540e\u523a\u5165\u4e00\u5251\uff0e\u53c8\u88ab\u6b66\u5f53\u5f53\u65f6\u7684\u638c\u95e8\u7d2b\u9633\u9053\u957f\u4e00\u5251\u523a\u8fdb\u524d\u80f8\uff0c\u6700\u540e\u4ecd\u4ee5\u9c9c \u8840\u6d47\u706d\u5f15\u7ebf\uff0c\u4f7f\u5c11\u6797\u3001\u6b66\u5f53\u4e0d\u5c11\u4eba\u5e78\u514d\u4e8e\u96be\u3002 \u3000\u3000\u8fd9\u6bb5\u5f80\u4e8b\uff0c\u5f53\u65f6\u9ea6\u5c0f\u660e\u867d\u7136\u4e5f\u5728\u73b0\u573a\uff0c\u800c\u4e14\u4e5f\u548c\u8303\u7389\u6606\u4ea4\u8fc7\u624b\uff0c\u5374\u81f3\u4eca\u4ecd\u5f04\u4e0d\u6e05\u5176\u4e2d\u9519 \u7efc\u590d\u6742\u7684\u539f\u56e0\uff0c\u4e5f\u96be\u602a\u4ed6\u628a\u8303\u7389\u6606\u6068\u4e4b\u5165\u9aa8\u3002 \u3000\u3000\u8303\u7389\u6606\u957f\u957f\u5401\u4e86\u2014\u53e3\u6c14\uff0c\u53cc\u76ee\u4e2d\u5374\u95ea\u70c1\u7740\u76c8\u76c8\u6c68\u5149\uff0c\u9053\uff1a\u201c\u9ea6\u5c0f\u4fa0\uff0c\u8bf4\u8d77\u6765\u4e09\u5e74\u524d\u843d\u96c1 \u8c37\u4e00\u6218\uff0c\u6700\u5927\u7684\u53d7\u5bb3\u8005\uff0e\u5e94\u8be5\u662f\u6211\u4eec\u8303\u5bb6\u5821\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u4e0d\u52a8\u58f0\u8272\u9053\uff1a\u201c\u8fd9\u8bdd\u600e\u4e48\u8bf4\u5462\uff1f\u201d \u3000\u3000\u8303\u7389\u6606\u9053\uff1a\u201c\u90a3\u4e00\u6218\uff0e\u5bb6\u7236\u548c\u5bb6\u5144\u8303\u6587\u6770\uff0c\u5168\u6b7b\u5728\u4ee4\u5e08\u9146\u79cb\u7b49\u4eba\u624b\u4e2d\uff0c\u5728\u4e0b\u4e5f\u8eab\u8d1f\u91cd\u4f24\uff0c \u9669\u4e9b\u4e27\u547d\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u54fc\u4e86\u58f0\u9053\uff1a\u201c\u6211\u4e0d\u5e0c\u671b\u4f60\u8bf4\u8fd9\u4e9b\uff0c\u4f60\u6740\u6b7b\u6211\u80e1\u5e08\u5144\u3001\u96be\u9053\u8fd8\u4e0d\u591f\u5417\uff1f\u201d \u3000\u3000\u8303\u7389\u6606\u4f4e\u4e0b\u5934\uff0c\u6ee1\u9762\u6127\u6094\u4e4b\u8272\uff0c\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u5728\u4e0b\u65e5\u591c\u96be\u5b89\u7684\uff0c\u6b63\u662f\u4e3a\u4e86\u8fd9\u4ef6\u4e8b\uff0c\u56e0\u4e3a \u6211\u8bef\u6740\u4e86\u80e1\u76df\u4e3b\u3002\u201d \u3000\u3000\u201c\u90a3\u4f60\u5c31\u628a\u5f53\u65f6\u7684\u60c5\u5f62\u518d\u8bf4\u4e00\u904d\u3002\u201d \u3000\u3000\u201c\u5f53\u65f6\u6bd2\u706b\u6210\u5168\u5df2\u5e03\u4e0b\u4e00\u9053\u836f\u5f15\uff0c\u51c6\u5907\u5f15\u7206\u4e8b\u5148\u57cb\u597d\u7684\u706b\u836f\u3002\u82e5\u706b\u836f\u4e00\u88ab\u5f15\u7206\uff0c\u5c11\u6797\u3001 \u6b66\u5f53\u548c\u6211\u4eec\u8303\u5bb6\u5821\u7684\u4f24\u4ea1\u4e0d\u96be\u60f3\u89c1\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u90a3\u706b\u836f\u5e76\u672a\u5f15\u7206\u3002\u201d \u3000\u3000\u201c\u8fd9\u6b63\u662f\u80e1\u76df\u4e3b\u820d\u8eab\u6551\u4eba\u7684\u8868\u73b0\u3002\u5f53\u65f6\u5bb6\u7236\u6b63\u7ad9\u5728\u706b\u836f\u8fd1\u65c1\uff0c\u80e1\u76df\u4e3b\u4e3a\u4e86\u6551\u5bb6\u7236\uff0c\u53c8\u62c5 \u5fc3\u5f97\u7f6a\u9146\u79cb\u7b49\u4eba\uff0e\u4fbf\u6084\u6084\u8d70\u8fd1\u5bb6\u7236\u8eab\u540e\uff0c\u5728\u4e0b\u8bef\u4ee5\u4e3a\u80e1\u76df\u4e3b\u662f\u8981\u5bf9\u5bb6\u7236\u4e0b\u6bd2\u624b\u3002\u56e0\u4e4b\u624d\u5728\u80e1 \u76df\u4e3b\u80cc\u540e\u523a\u4e86\u4e00\u5251\uff0c\u63a5\u7740\u80e1\u76df\u4e3b\u53c8\u88ab\u5f53\u65f6\u7684\u6b66\u5f53\u638c\u95e8\u7d2b\u9633\u9053\u957f\u4e00\u5251\u523a\u8fdb\u524d\u80f8\uff0c\u800c\u80e1\u76df\u4e3b\u4e3a\u4e86 \u6551\u4eba\uff0c\u7adf\u5fcd\u7740\u6700\u540e\u4e00\u53e3\u6c14\uff0c\u7528\u8eab\u4f53\u6251\u5411\u6b63\u5728\u71c3\u70e7\u4e2d\u7684\u5f15\u7ebf\uff0c\u3000\u76f4\u5230\u9c9c\u8840\u5c06\u5f15\u7ebf\u7184\u706d\uff0c\u624d\u58ee\u70c8 \u6b7b\u53bb\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u542c\u5230\u8fd9\u91cc\uff0e\u56de\u601d\u524d\u60c5\uff0c\u4e5f\u4e0d\u89c9\u8ff8\u51fa\u4e86\u773c\u6cea\uff0e\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u603b\u7b97\u4f60\u8fd8\u6709\u826f\u5fc3\uff0c\u53ef\u662f \u4f60\u5df2\u94f8\u6210\u5927\u9519\uff0c\u5373\u4f7f\u60f3\u8d4e\u7f6a\uff0c\u4e5f\u6765\u4e0d\u53ca\u4e86\u3002\u4f60\u7531\u8c6b\u5357\u8303\u5bb6\u5821\u53c8\u6765\u5230\u5317\u5cb3\uff0c\u53c8\u6709\u4ec0\u4e48\u7528\u610f\uff1f\u201d \u3000\u3000\u8303\u7389\u6606\u9053\uff1a\u201c\u5728\u4e0b\u60f3\u67e5\u660e\u4e00\u4ef6\u4e8b\uff0c\u80e1\u76df\u4e3b\u5230\u5e95\u662f\u771f\u6b7b\uff0e\u8fd8\u662f\u4ecd\u65e7\u6d3b\u7740\uff1f\u201d \u3000\u3000\u201c\u4eba\u6b7b\u5c82\u80fd\u590d\u6d3b\uff1f\u4f60\u4e0d\u662f\u660e\u660e\u770b\u5230\u627e\u80e1\u5e08\u5144\u5df2\u7ecf\u6b7b\u4e86\u5417\uff1f\u201d \u3000\u3000\u201c\u5728\u4e0b\u5f53\u7136\u770b\u5230\uff0c\u53ef\u662f\u6700\u8fd1\u53c8\u6709\u4eba\u770b\u5230\u80e1\u76df\u4e3b\u51fa\u73b0\u8fc7\u3002\u201d \u3000\u3000\u201c\u662f\u8c01\u770b\u5230\u7684\uff1f\u5728\u54ea\u91cc\u770b\u5230\u7684\uff1f\u201d \u3000\u3000\u201c\u4e8b\u60c5\u5c31\u5728\u6700\u8fd1\uff0c\u6709\u4eba\u4eb2\u773c\u770b\u5230\u80e1\u76df\u4e3b\u7531\u8c6b\u5357\u7ecf\u8fc7\uff0c\u800c\u4e14\u597d\u50cf\u53c8\u5230\u8fc7\u5c11\u6797\uff0c\u5f53\u65f6\u5728\u4e0b\u6b63 \u5728\u6d1b\u9633\uff0c\u542c\u5230\u8fd9\u6d88\u606f\u4fbf\u8d76\u6765\u5317\u5cb3\u3002\u201d \u3000\u3000\u201c\u4f60\u76f8\u4fe1\u8fd9\u4e8b\u662f\u771f\u7684\uff1f\u201d \u3000\u3000\u201c\u636e\u76ee\u7779\u4e4b\u4eba\u8bf4\uff0c\u90a3\u4eba\u7edd\u5bf9\u662f\u80e1\u76df\u4e3b\uff0c\u800c\u4e14\u2026\u2026\u201d \u3000\u3000\u201c\u800c\u4e14\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u9ea6\u5c0f\u660e\u4fbf\u53ef\u8bc1\u660e\u3002\u5e78\u559c\u5728\u4e0b\u80fd\u9047\u4e0a\u4e86\u4f60\u3002\u201d \u3000\u3000\u201c\u6211\u600e\u80fd\u8bc1\u660e\u5462\uff1f\u201d \u3000\u3000\u201c\u9ea6\u5c0f\u4fa0\u8bf7\u8bf4\u5b9e\u8bdd\uff0e\u636e\u76ee\u7779\u4e4b\u4eba\u8bf4\uff0c\u968f\u80e1\u76df\u4e3b\u540c\u884c\u7684\uff0c\u9ea6\u5c0f\u4fa0\u4fbf\u662f\u5176\u4e2d\u4e4b\u4e00\uff0c\u53e6\u5916\u8fd8\u6709 \u4e00\u4f4d\u59d1\u5a18\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u53ea\u597d\u70b9\u4e86\u70b9\u5934\u9053\uff1a\u201c\u4e0d\u9519\uff01\u6709\u8fd9\u4e48\u2014\u56de\u4e8b\u3002\u201d \u3000\u3000\u8303\u7389\u6606\u8feb\u4e0d\u53ca\u5f85\u7684\u95ee\u9053\uff1a\u201c\u9ea6\u5c0f\u4fa0\u5feb\u8bf7\u8bf4\u660e\uff0c\u80e1\u76df\u4e3b\u662f\u600e\u4e48\u6d3b\u56de\u6765\u7684\uff1f\u8fd9\u4e09\u5e74\u6765\uff0c\u4ed6\u7684 \u4eba\u53c8\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u7565\u4e00\u6c89\u541f\u9053\uff1a\u201c\u4f60\u95ee\u8fd9\u4e9b\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u8303\u7389\u6606\u9053\uff1a\u201c\u5982\u679c\u80e1\u76df\u4e3b\u771f\u8fd8\u6d3b\u7740\uff0c\u5728\u4e0b\u8981\u5411\u4ed6\u8c22\u7f6a\uff0c\u7136\u540e\u518d\u8bbe\u6cd5\u8d4e\u7f6a\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u5f53\u7136\u8be5\u5411\u4ed6\u8c22\u7f6a\uff0c\u4f46\u53c8\u5982\u4f55\u8d4e\u7f6a\uff1f\u201d \u3000\u3000\u8303\u7389\u6606\u6b63\u8272\u9053\uff1a\u201c\u5728\u4e0b\u60c5\u613f\u4e00\u751f\u8ffd\u968f\u4ed6\uff0c\u8d74\u6c64\u8e48\u706b\uff0c\u5728\u6240\u4e0d\u8f9e\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u6697\u9053\uff1b\u201c\u770b\u4ed6\u8fd9\u6a21\u6837\uff0c\u7684\u786e\u662f\u8c22\u7f6a\u8d4e\u7f6a\u6765\u7684\uff0c\u5c06\u6765\u5bf9\u4ed8\u970d\u5143\u4f3d\uff0c\u6b63\u6101\u4eba\u624b\u4e0d\u8db3\uff0c \u6b64\u4eba\u7684\u6b66\u529f\uff0c\u5f53\u5e74\u4e0e\u6211\u4e0d\u76f8\u4e0a\u4e0b\u3002\u82e5\u80fd\u6709\u4ed6\u76f8\u52a9\uff0e\u5b9e\u662f\u6c42\u4e4b\u4e0d\u5f97\u3002\u201d \u3000\u3000\u60f3\u5230\u8fd9\u91cc\uff0c\u654c\u610f\u5168\u6d88\uff0c\u70b9\u4e86\u70b9\u5934\u9053\uff1a\u201c\u7b97\u4f60\u8d70\u8fd0\uff0c\u5c1a\u672a\u94f8\u6210\u5927\u9519\u3002\u6211\u80e1\u5e08\u5144\u5927\u96be\u4e0d\u6b7b\uff0c \u5f97\u9047\u9ad8\u4eba\u76f8\u6551\uff0c\u770b\u6765\u4f60\u524d\u6765\u5317\u5cb3\uff0c\u4e0d\u66fe\u767d\u8dd1\u3002\u201d \u3000\u3000\u8303\u7389\u6606\u559c\u51fa\u671b\u5916\u9053\uff1a\u201c\u80e1\u76df\u4e3b\u771f\u7684\u6ca1\u6b7b\uff0e\u5728\u4e0b\u8be5\u8c22\u5929\u8c22\u5730\u4e86\uff01\u201d \u3000\u3000\u4ed6\u8bf4\u7740\uff0c\u5f53\u771f\u7531\u5750\u53d8\u8dea\uff0c\u606d\u606d\u656c\u656c\u7684\u5411\u8ff7\u8e2a\u8c37\u65b9\u5411\u8fde\u78d5\u4e86\u4e09\u4e2a\u5934\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u8ff7\u8e2a\u8c37\u7684\u4e3b\u4eba\uff0c\u73b0\u5728\u65e9\u5df2\u4e0d\u662f\u6211\u80e1\u5e08\u5144\u4e86\uff0c\u4f60\u5e72\u561b\u671d\u90a3\u8fb9\u78d5\u5934\uff1f\u201d \u3000\u3000\u8303\u7389\u6606\u9053\uff1a\u201c\u5728\u4e0b\u5f53\u7136\u77e5\u9053\u76ee\u524d\u7684\u7eff\u6797\u76df\u4e3b\u662f\u970d\u5143\u4f3d\uff0e\u4f46\u80e1\u76df\u4e3b\u4eba\u5728\u54ea\u91cc\uff1f\u8fd8\u8bf7\u9ea6\u5c0f\u4fa0 \u5feb\u5feb\u544a\u77e5\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u6211\u80e1\u5e08\u5144\u5728\u767d\u59d1\u5bfa\u3002\u201d \u3000\u3000\u8303\u7389\u6606\u56db\u4e0b\u671b\u4e86\u671b\u9053\uff1a\u201c\u767d\u59d1\u5bfa\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u7528\u624b\u4e00\u6307\u9053\uff1a\u201c\u5c31\u5728\u90a3\u91cc\uff01\u201d \u3000\u3000\u8303\u7389\u6606\u7acb\u523b\u53c8\u5411\u9ea6\u5c0f\u660e\u6240\u6307\u7684\u65b9\u5411\u53e9\u4e86\u4e09\u4e2a\u5934\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u7b97\u5566\uff01\u4f60\u5728\u8fd9\u91cc\u53e9\u5934\uff0c\u6211\u5e08\u5144\u600e\u80fd\u770b\u5f97\u5230\uff1f\u201d \u3000\u3000\u201c\u5c31\u8bf7\u9ea6\u5c0f\u4fa0\u9a6c\u4e0a\u5e26\u5728\u4e0b\u5230\u767d\u59d1\u5bfa\u53bb\u3002\u201d \u3000\u3000\u201c\u522b\u5fd9\uff01\u6211\u8fd9\u91cc\u8fd8\u6709\u8981\u7d27\u7684\u4e8b\u3002\u201d \u3000\u3000\u201c\u9ea6\u5c0f\u4fa0\u6709\u4ec0\u4e48\u4e8b\uff0e\u8981\u4e0d\u8981\u5728\u4e0b\u5e2e\u5fd9\uff1f\u201d \u3000\u3000\u201c\u5179\u4e8b\u4f53\u5927\uff0c\u4f60\u73b0\u5728\u60f3\u5e2e\u5fd9\u4e5f\u5e2e\u4e0d\u4e0a\u3002\u201d \u3000\u3000\u201c\u8fd8\u8bf7\u9ea6\u5c0f\u4fa0\u544a\u77e5\u662f\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u6b64\u65f6\u5df2\u5bf9\u8303\u7389\u6606\u9887\u5177\u597d\u611f\uff0c\u4fbf\u628a\u968f\u540c\u5929\u7985\u5927\u5e08\u8fdb\u8c37\uff0c\u72ec\u81ea\u4e00\u4eba\u9003\u51fa\u7684\u4e8b\u8bf4\u4e86\u4e00\u904d\u3002 \u3000\u3000\u8303\u7389\u6606\u542c\u5f97\u5927\u4e3a\u6fc0\u52a8\uff1a\u201c\u9ea6\u5c0f\u4fa0\u53ef\u662f\u8981\u7b49\u5019\u4ed6\u4eec\u7684\u6d88\u606f\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u5f53\u7136\u8981\u7b49\u5019\u4ed6\u4eec\u7684\u6d88\u606f\u3002\u201d \u3000\u3000\u8303\u7389\u6606\u9053\uff1a\u201c\u5728\u4e0b\u5bf8\u529f\u672a\u7acb\uff0c\u5982\u679c\u6ca1\u6709\u6d88\u606f\uff0c\u5728\u4e0b\u60c5\u613f\u968f\u9ea6\u5c0f\u4fa0\u6740\u8fdb\u8c37\u53bb\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u6447\u5934\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u7684\u6b66\u529f\u5f88\u9ad8\uff0c\u81f3\u4e8e\u6211\uff0e\u8eab\u624b\u4e5f\u4e0d\u5dee\u4f46\u4ee5\u54b1\u4eec\u4e8c\u4eba\u4e4b\u529b\uff0c\u6740\u8fdb \u53bb\u6839\u672c\u65e0\u6d4e\u4e8e\u4e8b\u3002\u201d \u3000\u3000\u6b63\u8bf4\u5230\u8fd9\u91cc\uff0c\u7a81\u89c1\u82d7\u7d20\u82d3\u7531\u8c37\u5185\u5954\u4e86\u51fa\u6765\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u6025\u6025\u8d77\u8eab\u8fce\u4e0a\u524d\u53bb\u9053\uff1a\u201c\u600e\u4e48\u6837\uff1f\u5929\u7985\u8001\u7985\u5e08\u4ed6\u4eec\u5462\uff1f\u201d \u3000\u3000\u82d7\u7d20\u82d3\u9053\uff1a\u201c\u5e08\u7236\u548c\u4ed6\u4eec\u5c1a\u672a\u52a8\u624b\uff0c\u4e5f\u8bb8\u4f1a\u5b89\u7136\u51fa\u8c37\u3002\u201d \u3000\u3000\u201c\u4f60\u662f\u600e\u4e48\u5148\u51fa\u6765\u7684\uff1f\u201d \u3000\u3000\u201c\u6211\u5df2\u88ab\u970d\u5143\u4f3d\u770b\u51fa\u7834\u7efd\uff0c\u970d\u5143\u4f3d\u4e0b\u4ee4\u56f4\u6355\u6211\uff0c\u6240\u5e78\u6211\u7684\u8f7b\u529f\u80dc\u8fc7\u4ed6\u4eec\uff0c\u7ec8\u4e8e\u7a81\u56f4\u800c \u51fa\u3002\u201d \u3000\u3000\u201c\u53ef\u66fe\u548c\u4ed6\u4eec\u6253\u6597\uff1f\u201d \u3000\u3000\u201c\u4f60\u770b\uff0c\u6211\u7684\u8863\u670d\u5df2\u7ecf\u7834\u4e86\u597d\u51e0\u5904\uff0c\u6240\u5e78\u5e76\u672a\u53d7\u4f24\u3002\u201d \u3000\u3000\u8fd9\u65f6\u8303\u7389\u6606\u5df2\u8ddf\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u82d7\u7d20\u82d3\u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u8c01\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4ed6\u53eb\u8303\u7389\u6606\uff0c\u5f53\u5e74\u4e00\u5251\u523a\u6b7b\u6211\u80e1\u5e08\u5144\u7684\u5c31\u662f\u4ed6\uff01\u201d \u3000\u3000\u82d7\u7d20\u82d3\u554a\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u65e2\u7136\u4ed6\u662f\u6740\u80e1\u76df\u4e3b\u7684\u51f6\u624b\uff0c\u4f60\u600e\u4e48\u8fd8\u548c\u4ed6\u5728\u4e00\u8d77\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u7b11\u9053\uff1a\u201c\u4ed6\u5df2\u7ecf\u5411\u6211\u5fcf\u6094\uff0c\u800c\u4e14\u662f\u5411\u6211\u80e1\u5e08\u5144\u8c22\u7f6a\u8d4e\u7f6a\u6765\u7684\u3002\u53cd\u6b63\u80e1\u5e08\u5144\u6b7b\u4e86\u53c8 \u6d3b\u56de\u6765\uff0c\u54b1\u4eec\u5c31\u8be5\u539f\u8c05\u4ed6\u3002\u201d \u3000\u3000\u82d7\u7d20\u82d3\u9053\uff1a\u201c\u4ed6\u89c1\u8fc7\u80e1\u76df\u4e3b\u6ca1\u6709\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u8fd8\u6ca1\u6709\u3002\u201d \u3000\u3000\u201c\u90a3\u5c31\u5feb\u5e26\u4ed6\u89c1\u80e1\u76df\u4e3b\u53bb\uff01\u201d \u3000\u3000\u201c\u53ef\u662f\u54b1\u4eec\u8fd8\u8981\u7b49\u5019\u5929\u7985\u8001\u7985\u5e08\u3002\u201d \u3000\u3000\u201c\u6211\u60f3\u4ed6\u4eec\u53ea\u8981\u4e0d\u52a8\u624b\uff0e\u970d\u5143\u4f3d\u8fd8\u4e0d\u81f3\u4e8e\u96be\u4e3a\u4ed6\u4eec\uff0c\u54b1\u4eec\u56de\u5230\u767d\u59d1\u5bfa\uff0c\u7b49\u4ed6\u4eec\u4e5f\u662f\u2014\u6837\uff0c \u5f85\u5728\u8fd9\u91cc\uff0e\u53cd\u800c\u5bb9\u6613\u88ab\u8ff7\u8e2a\u8c37\u7684\u773c\u7ebf\u53d1\u73b0\u884c\u8e2a\u3002\u201d \u3000\u3000\u201c\u597d\u5427\uff01\u54b1\u4eec\u8fd9\u5c31\u56de\u767d\u59d1\u5bfa\u53bb\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u63a5\u7740\u518d\u4e3a\u8303\u7389\u6606\u5f15\u89c1\u82d7\u7d20\u82d3\uff0c\u9053\uff1a\u201c\u5979\u53eb\u82d7\u7d20\u82d3\uff0c\u4e0a\u56de\u968f\u540c\u6211\u80e1\u5e05\u5144\u5230\u5c11\u6797\u7684\u90a3 \u4f4d\u59d1\u5a18\u5c31\u662f\u5979\u3002\u201d \u3000\u3000\u8303\u7389\u6606\u9053\uff1a\u201c\u82d7\u59d1\u5a18\u4e3a\u4ec0\u4e48\u8981\u6539\u626e\u7537\u88c5\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4e3a\u7684\u662f\u4fdd\u5bc6\u8eab\u4efd\uff0c\u4e0d\u7136\u600e\u80fd\u968f\u540c\u5929\u7985\u8001\u7985\u5e08\u8fdb\u8c37\uff1f\u201d \u3000\u3000\u82d7\u7d20\u82d3\u9053\uff1a\u201c\u53ef\u73b0\u5728\u5df2\u7ecf\u88ab\u970d\u5143\u4f3d\u8bc6\u7834\u4e86\uff01\u201d \u3000\u3000\u56de\u5230\u767d\u59d1\u5bfa\uff0c\u4f4f\u6301\u65b9\u4e08\u609f\u5c18\u6b63\u7126\u6025\u7684\u7b49\u5019\u5728\u5bfa\u95e8\u5916\uff0c\u4e00\u89c1\u9ea6\u5c0f\u660e\u7b49\u56de\u6765\uff0c\u4fbf\u6025\u6025\u95ee\u9053\uff1a \u201c\u655d\u6d3e\u638c\u95e8\u5e08\u5c0a\u4ed6\u4eec\u600e\u4e48\u6ca1\u56de\u6765\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4ed6\u4eec\u5f85\u4f1a\u513f\u5c31\u4f1a\u56de\u6765\uff0c\u80e1\u76df\u4e3b\u53ef\u5728\u5bfa\u4e2d\uff1f\u201d \u3000\u3000\u609f\u5c18\u9053\uff1a\u201c\u80e1\u76df\u4e3b\u6b63\u5728\u5b9d\u5854\u7684\u4e8c\u5c42\u4f11\u606f\uff0c\u4ed6\u4e5f\u7740\u6025\u5f97\u5f88\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u6211\u4eec\u5148\u53bb\u770b\u770b\u80e1\u76df\u4e3b\u3002\u201d \u3000\u3000\u767b\u4e0a\u767d\u59d1\u5854\u7b2c\u4e8c\u5c42\uff0c\u80e1\u67cf\u9f84\u6b63\u5750\u5728\u90a3\u91cc\u6c89\u601d\u3002 \u3000\u3000\u8303\u7389\u6606\u8fde\u5fd9\u5954\u8fc7\u53bb\uff0c\u4ee5\u819d\u8dea\u5012\u9053\uff1a\u201c\u7f6a\u4eba\u8303\u7389\u6606\uff0c\u53e9\u89c1\u80e1\u76df\u4e3b\uff01\u201d \u3000\u3000\u80e1\u67cf\u9f84\u987f\u611f\u4e00\u6123\uff0c\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u662f\u8303\u5c11\u5821\u4e3b\uff1f\u201d \u3000\u3000\u8303\u7389\u6606\u8fde\u8fde\u53e9\u5934\u9053\uff1a\u201c\u665a\u8f88\u6b63\u662f\u8303\u7389\u6606\uff01\u201d \u3000\u3000\u80e1\u67cf\u9f84\u5446\u4e86\u4e00\u5446\u9053\uff1a\u201c\u4f60\u600e\u4e48\u6765\u4e86\uff1f\u6765\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u8303\u7389\u6606\u8bed\u6c14\u65e0\u6bd4\u6fc0\u52a8\u9053\uff1a\u201c\u665a\u8f88\u662f\u5411\u80e1\u76df\u4e3b\u8c22\u7f6a\u8d4e\u7f6a\u6765\u7684\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u62a2\u7740\u9053\uff1a\u201c\u4ed6\u65b9\u624d\u5df2\u7ecf\u5bf9\u5c0f\u5f1f\u8bf4\u5f97\u5f88\u660e\u767d\uff0e\u5f53\u5e74\u8bef\u4f24\u4e86\u5e08\u5144\uff0c\u5e08\u5144\u8bf7\u539f\u8c05\u4ed6\u5427\uff01\u201d \u3000\u3000\u80e1\u67cf\u9f84\u8fde\u5fd9\u63a2\u624b\u62c9\u8d77\u8303\u7389\u6606\u9053\uff1a\u201c\u8303\u8001\u5f1f\uff0c\u5feb\u522b\u5982\u6b64\uff0e\u8fd9\u4e09\u5e74\u6765\uff0c\u80e1\u67d0\u4ece\u672a\u602a\u7f6a\u4e8e\u4f60\u3002 \u5f53\u65f6\u4f60\u4e3a\u4e86\u6551\u4ee4\u5c0a\u60c5\u6025\u95f4\u523a\u6211\u2014\u5251\uff0c\u4f60\u505a\u5f97\u5f88\u5bf9\uff0c\u6211\u53c8\u600e\u80fd\u602a\u4f60\u3002\u201d \u3000\u3000\u8fd9\u51e0\u53e5\u8bdd\uff0c\u53ea\u542c\u5f97\u8303\u7389\u6606\u771f\u662f\u611f\u6fc0\u6d95\u96f6\uff0c\u4e45\u4e45\u8bf4\u4e0d\u51fa\u8bdd\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u5e08\u5144\uff0c\u8303\u5c11\u5821\u4e3b\u8bf4\u8fc7\uff0c\u4ece\u4eca\u4ee5\u540e\u8981\u6c38\u8fdc\u8ffd\u968f\u5728\u60a8\u8eab\u8fb9\uff0e\u8d74\u6c64\u8e48\u706b\uff0c\u5728\u6240\u4e0d \u8f9e\uff01\u201d \u3000\u3000\u80e1\u67cf\u9f84\u82e6\u7b11\u9053\uff1a\u201c\u53ef\u60dc\u6211\u4e0d\u5f97\u4e0d\u8f9c\u8d1f\u4ed6\u8fd9\u756a\u5fc3\u610f\u3002\u201d \u3000\u3000\u201c\u60a8\u4e3a\u4ec0\u4e48\u8981\u8f9c\u8d1f\u4eba\u5bb6\uff1f\u201d \u3000\u3000\u201c\u8fc7\u51e0\u5929\u5e08\u7236\u56de\u6765\u4ee5\u540e\uff0c\u8303\u5c11\u5821\u4e3b\u53c8\u600e\u80fd\u548c\u6211\u5728\u2014\u8d77\uff1f\u201d \u3000\u3000\u201c\u8bf4\u7684\u4e5f\u662f\u3002\u4e0d\u8fc7\u6ca1\u5173\u7cfb\uff0c\u53cd\u6b63\u5c0f\u5f1f\u4eec\u8981\u7ee7\u7eed\u5bf9\u4ed8\u970d\u5143\u4f3d\uff0c\u6709\u8303\u5c11\u5821\u4e3b\u5e2e\u5fd9\uff0c\u4e5f\u7b49\u4e8e\u4e3a \u60a8\u6548\u52b3\u3002\u201d \u3000\u3000\u201c\u6682\u65f6\u4e0d\u8c08\u8fd9\u4e2a\uff0e\u4f60\u8fd8\u6ca1\u544a\u8bc9\u6211\u968f\u5929\u7985\u5927\u5e08\u8fdb\u8c37\u7684\u7ecf\u8fc7\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u521a\u8ff0\u8bf4\u4e86\u2014\u534a\uff0c\u53ea\u89c1\u666e\u51e1\u5306\u5306\u5954\u4e86\u8fdb\u6765\u544a\u77e5\u5929\u7985\u5927\u5e08\u5df2\u7ecf\u56de\u6765\u4e86\u3002 \u3000\u3000\u80e1\u67cf\u9f84\u8fde\u5fd9\u5e26\u7740\u9ea6\u5c0f\u660e\u3001\u82d7\u7d20\u82d3\u3001\u8303\u7389\u6606\u4e0b\u5854\u6765\u5230\u540e\u9762\u7985\u5ba4\u3002 \u3000\u3000\u8fd9\u65f6\u609f\u5c18\u6b63\u5728\u63a5\u5f85\u5929\u7985\u548c\u5929\u671b\u5927\u5e08\u3002 \u3000\u3000\u90a3\u5c11\u6797\u7684\u4e8c\u5341\u56db\u540d\u5f1f\u5b50\u4e5f\u5168\u56de\u6765\u4e86\uff0c\u4ed6\u4eec\u6b63\u5728\u5eca\u4e0b\u4f11\u606f\u3002 \u3000\u3000\u8303\u7389\u6606\u9996\u5148\u4e0a\u524d\u548c\u5929\u7985\u3001\u5929\u671b\u4e24\u4f4d\u5927\u5e08\u89c1\u793c\uff0c\u4ed6\u4eec\u5f7c\u6b64\u65e9\u5c31\u8ba4\u8bc6\uff0e \u3000\u3000\u80e1\u67cf\u9f84\u95ee\u53ca\u51fa\u8c37\u60c5\u5f62\uff0c\u5929\u7985\u5927\u5e08\u559f\u7136\u4e00\u53f9\u9053\uff1a\u201c\u8001\u8872\u5ea6\u91cf\u60c5\u52bf\uff0e\u82e5\u60f3\u628a\u609f\u660e\u548c\u4e5d\u540d\u94dc\u4eba \u62bc\u56de\uff0c\u52bf\u5fc5\u5927\u52a8\u5e72\u6208\u3002 \u3000\u3000\u2014\u65e6\u53cc\u65b9\u95f9\u7ffb\uff0c\u8ff7\u8e2a\u8c37\u65b9\u9762\u4eba\u591a\u52bf\u4f17\uff0c\u4e0d\u4f46\u8ba8\u4e0d\u4e86\u597d\u5904\uff0c\u53cd\u800c\u53ef\u80fd\u8981\u5168\u519b\u8986\u6ca1\u3002\u65e0\u5948\u4e4b \u4e0b\uff0c\u53ea\u597d\u548c\u5929\u671b\u5e08\u5f1f\u6682\u65f6\u7387\u9886\u5f1f\u5b50\u9000\u51fa\u8c37\u5916\u3002\u201d \u3000\u3000\u80e1\u67cf\u9f84\u6c89\u541f\u4e86\u534a\u664c\u9053\uff1a\u201c\u96be\u9053\u5927\u5e08\u5c31\u8fd9\u6837\u7b97\u4e86\uff1f\u201d \u3000\u3000\u5929\u7985\u5927\u5e08\u53c8\u53f9\u4e86\u4e00\u53e3\u6c14\u9053\uff1a\u201c\u8001\u8872\u5728\u5c11\u6797\u51fa\u53d1\u65f6\uff0c\u672c\u4ee5\u4e3a\u4eba\u624b\u5df2\u8db3\uff0c\u4e07\u60f3\u4e0d\u5230\u8ff7\u8e2a\u8c37\u65b9 \u9762\u76ee\u524d\u8d8a\u53d1\u5b9e\u529b\u5927\u589e\u3002\u201d \u3000\u3000\u80e1\u67cf\u9f84\u9053\uff1a\u201c\u4e0d\u7ba1\u5982\u4f55\uff0e\u5927\u5e08\u7eb5\u7136\u4e0d\u80fd\u628a\u4e5d\u540d\u94dc\u4eba\u62bc\u56de\uff0c\u4e5f\u8be5\u6551\u51fa\u5929\u673a\u3001\u5929\u89c9\u4e8c\u4f4d\u5927\u5e08 \u4ee5\u53ca\u8d35\u6d3e\u7684\u5341\u4e8c\u540d\u5f1f\u5b50\uff0e\u201d \u3000\u3000\u5929\u7985\u5927\u5e08\u53cc\u988a\u62bd\u6410\u4e86\u51e0\u4e0b\u9053\uff1a\u201c\u8001\u8872\u5c82\u80fd\u4e0d\u987e\u53ca\u4ed6\u4eec\u7684\u5b89\u5371\uff1f\u4e00\u8def\u4e0a\u548c\u5929\u671b\u5e08\u5f1f\u5546\u8bae\u7684 \u7ed3\u679c\uff0c\u51b3\u5b9a\u5148\u884c\u8fd4\u56de\u5c11\u6797\u3002 \u3000\u3000\u9664\u4e86\u518d\u52a0\u6d3e\u4eba\u624b\u5916\uff0c\u540c\u65f6\u518d\u8054\u7edc\u6b66\u5f53\u65b9\u9762\u6d3e\u51fa\u9ad8\u624b\u534f\u52a9\uff0c\u5982\u679c\u6b66\u5f53\u638c\u95e8\u9752\u9633\u9053\u5144\u80fd\u4eb2\u7387 \u5f1f\u5b50\u524d\u6765\u5317\u5cb3\uff0c\u90a3\u5c31\u66f4\u597d\u4e86\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u62a2\u7740\u9053\uff1a\u201c\u8001\u7985\u5e08\u7684\u4e3b\u610f\u867d\u597d\uff0c\u4f46\u665a\u8f88\u4e0d\u8d5e\u6210\uff01\u201d \u3000\u3000\u5929\u7985\u5927\u5e08\u9053\uff1a\u201c\u9ea6\u5c0f\u65bd\u4e3b\u6709\u4f55\u9ad8\u89c1\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u7531\u8fd9\u91cc\u5230\u8d35\u5bfa\u5c11\u6797\uff0c\u8def\u9014\u8db3\u6709\u5343\u91cc\u4e4b\u9065\uff0c\u7531\u5c11\u6797\u5230\u6b66\u5f53\uff0c\u53c8\u6709\u597d\u51e0\u767e\u91cc\uff0c \u518d\u52a0\u4e0a\u8054\u7edc\u65f6\u95f4\uff0c\u5f80\u8fd4\u81f3\u5c11\u8981\u4e24\u4e2a\u6708\uff0c\u5728\u65f6\u95f4\u4e0a\u5982\u4f55\u6765\u5f97\u53ca\uff1f\u201d \u3000\u3000\u5929\u63b8\u5927\u5e08\u9ed8\u4e86\u4e00\u9ed8\u9053\uff1a\u201c\u9ea6\u5c0f\u65bd\u4e3b\u679c\u7136\u8bf4\u5f97\u6709\u7406\uff0e\u4f60\u6709\u4ec0\u4e48\u53e6\u5916\u7684\u529e\u6cd5\uff0c\u8fd8\u8bf7\u8bf4\u51fa\u6765\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4f9d\u665a\u8f88\u611a\u89c1\uff0c\u8001\u7985\u5e08\u6700\u597d\u5728\u767d\u59d1\u5bfa\u4f4f\u4e0b\u6765\u6309\u5175\u4e0d\u52a8\u3002\u201d \u3000\u3000\u5929\u7985\u5927\u5e08\u8e59\u4e86\u4e24\u9053\u767d\u7709\u9053\uff1a\u201c\u6309\u5175\u4e0d\u52a8\u5982\u4f55\u80fd\u89e3\u51b3\u5f97\u4e86\u95ee\u9898\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u53ea\u8981\u665a\u8f88\u80fd\u548c\u949f\u526f\u76df\u4e3b\u7b49\u4eba\u8054\u7edc\u4e0a\uff0e\u53cc\u65b9\u4f1a\u5408\u4e00\u5904\uff0e\u5c31\u8db3\u53ef\u5927\u961f\u4eba\u9a6c\u5f00\u8fdb \u8ff7\u8e2a\u8c37\uff0c\u548c\u970d\u5143\u4f3d\u51b3\u4e00\u6b7b\u6218\uff01\u201d \u3000\u3000\u201c\u53ef\u662f\u9ea6\u5c0f\u65bd\u4e3b\u5982\u4f55\u80fd\u548c\u4ed6\u4eec\u8054\u7edc\uff1f\u201d \u3000\u3000\u201c\u4f9d\u665a\u8f88\u9884\u6599\uff0e\u4ed6\u4eec\u5fc5\u5b9a\u85cf\u5728\u8ff7\u8e2a\u8c37\u9644\u8fd1\u3002\u4ed6\u4eec\u7684\u4eba\u4e0d\u5728\u5c11\u6570\uff0c\u60f3\u627e\u5230\u4ed6\u4eec\uff0c\u5e76\u975e\u96be \u4e8b\u3002\u201d \u3000\u3000\u5929\u7985\u5927\u5e08\u6c89\u541f\u4e0d\u8bed\u3002 \u3000\u3000\u5929\u671b\u5927\u5e08\u9053\uff1a\u201c\u9ea6\u5c0f\u65bd\u4e3b\u8bf4\u5f97\u6709\u7406\uff0c\u54b1\u4eec\u4e0d\u59a8\u6682\u5728\u767d\u59d1\u5bfa\u4f4f\u4e0b\uff0e\u82e5\u5728\u4e03\u65e5\u5185\u4ecd\u65e0\u6cd5\u8054\u7edc \u5230\u949f\u526f\u76df\u4e3b\u7b49\u4eba\uff0c\u518d\u4f5c\u8ba1\u8f83\uff0c\u4e5f\u8fd8\u4e0d\u8fdf\u3002\u201d \u3000\u3000\u5929\u7985\u5927\u5e08\u9894\u9996\u9053\uff1a\u201c\u4e5f\u597d\uff0e\u5c31\u8fd9\u4e48\u51b3\u5b9a\uff01\u201d \u3000\u3000\u56de\u5230\u5854\u5185\uff0e\u80e1\u67cf\u9f84\u3001\u9ea6\u5c0f\u660e\u3001\u8303\u7389\u6606\u4f4f\u7b2c\u4e8c\u5c42\uff0c\u82d7\u7d20\u82d3\u4f4f\u7b2c\u4e09\u5c42\u3002\u767d\u59d1\u5bfa\u6709\u4e8c\u5341\u4f59\u9ad8\u624b \u5728\uff0c\u5b89\u5168\u5df2\u65e0\u987e\u8651\uff0e\u80e1\u67cf\u9f84\u4e5f\u5c31\u7528\u4e0d\u7740\u518d\u6d3e\u51fa\u8b66\u6212\u3002 \u3000\u3000\u665a\u996d\u540e\uff0c\u95f2\u6765\u65e0\u4e8b\uff0e\u80e1\u67cf\u9f84\u3001\u9ea6\u5c0f\u660e\u3001\u8303\u7389\u6606\u4e09\u4eba\u4fbf\u5728\u5854\u5185\u5546\u8bae\u4eca\u540e\u884c\u52a8\u3002 \u3000\u3000\u8303\u7389\u6606\u9053\uff1a\u201c\u80e1\u5927\u4fa0\uff0c\u8303\u5bb6\u5821\u5185\u76ee\u524d\u8fd8\u6709\u4e03\u516b\u540d\u9ad8\u624b\uff0e\u5982\u679c\u8fd9\u8fb9\u4eba\u624b\u4e0d\u591f\uff0c\u665a\u8f88\u60c5\u613f\u56de \u8c6b\u5357\u628a\u4ed6\u4eec\u5e26\u6765\u52a9\u9635\u3002\u201d \u3000\u3000\u80e1\u67cf\u9f84\u9053\uff1a\u201c\u4e0d\u5fc5\u4e86\uff0c\u8fdc\u6c34\u6551\u4e0d\u4e86\u8fd1\u706b\u3002\u201d \u3000\u3000\u5ffd\u542c\u9ea6\u5c0f\u660e\u95ee\u9053\uff1a\u201c\u5e08\u5144\uff0c\u60a8\u7684\u5047\u671f\u8fd8\u6709\u591a\u4e45\uff1f\u201d \u3000\u3000\u80e1\u67cf\u9f84\u9053\uff1a\u201c\u8bba\u65f6\u95f4\u8fd8\u8be5\u6709\u534a\u4e2a\u591a\u6708\uff0c\u4f46\u8fd9\u8981\u770b\u5e08\u7236\u4ec0\u4e48\u65f6\u5019\u56de\u6765\u3002\u5982\u679c\u4ed6\u8001\u4eba\u5bb6\u73b0\u5728 \u56de\u6765\uff0e\u6211\u5c31\u8981\u9a6c\u4e0a\u56de\u53bb\u966a\u4f8d\u4ed6\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4f46\u613f\u4ed6\u6c38\u8fdc\u522b\u56de\u6765\u3002\u201d \u3000\u3000\u80e1\u67cf\u9f84\u9053\uff1a\u201c\u5176\u5b9e\u5c31\u662f\u5e08\u7236\u4e0d\u56de\u6765\uff0c\u6211\u7167\u6837\u4e5f\u65e0\u6cd5\u53c2\u52a0\u4f60\u4eec\u7684\u884c\u52a8\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9762\u663e\u4e0d\u60a6\u4e4b\u8272\u9053\uff1a\u201c\u5e08\u5144\uff0c\u4f60\u9519\u4e86\uff01\u201d \u3000\u3000\u201c\u6211\u9519\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u201c\u5c0f\u5f1f\u8981\u8bb2\u7684\u8bdd\u4e5f\u8bb8\u51b2\u4e86\u4e00\u70b9\uff0e\u5e08\u5144\u5343\u4e07\u522b\u89c1\u602a\u3002\u8ff7\u8e2a\u8c37\u662f\u60a8\u4e00\u624b\u5efa\u7acb\u7684\uff0c\u949f\u526f\u76df\u4e3b\u7b49 \u4eba\uff0c\u4e5f\u662f\u60a8\u7684\u65e7\u90e8\u3002 \u3000\u3000\u800c\u73b0\u5728\u60a8\u5374\u7f6e\u8eab\u4e8b\u5916\uff0c\u5bf9\u4ed8\u970d\u5143\u4f3d\uff0c\u5e94\u8be5\u662f\u6211\u4eec\u5e2e\u60a8\u6253\u5929\u4e0b\uff0c\u60a8\u5374\u8bf4\u662f\u6211\u4eec\u7684\u884c\u52a8\uff0c\u81ea \u5df1\u53cd\u800c\u63a8\u5f97\u5e72\u5e72\u51c0\u51c0\u3002\u5e08\u5144\uff0c\u5c0f\u5f1f\u8ba4\u4e3a\u60a8\u8fd9\u8bdd\u5b9e\u5728\u8bf4\u4e0d\u8fc7\u53bb\u3002\u201d \u3000\u3000\u8fd9\u51e0\u53e5\u8bdd\uff0c\u8bed\u6c14\u4e0d\u80fd\u8bf4\u4e0d\u91cd\uff0e\u80e1\u67cf\u9f84\u7ec8\u4e8e\u663e\u51fa\u7f9e\u6127\u6a21\u6837\uff0c\u4f4e\u4e0b\u4e86\u5934\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u5e08\u5144\uff0c\u4f60\u8fd9\u6837\u505a\uff0c\u56fa\u7136\u662f\u5e08\u547d\u96be\u8fdd\uff0c\u4f46\u5bf9\u5929\u4e0b\u7eff\u6797\uff0c\u5374\u4e07\u4e07\u4ea4\u4ee3\u4e0d \u8fc7\u53bb\u3002\u5982\u679c\u4efb\u7531\u970d\u5143\u4f3d\u80e1\u4f5c\u975e\u4e3a\uff0c\u52bf\u5fc5\u9020\u6210\u7eff\u6797\u6d69\u52ab\u3002\u72ec\u5584\u5176\u8eab\uff0c\u53c8\u600e\u80fd\u6bd4\u5f97\u4e0a\u517c\u5584\u5929\u4e0b\u3002\u201d \u3000\u3000\u80e1\u67cf\u9f84\u957f\u957f\u5401\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u4f60\u7684\u610f\u601d\uff0c\u4f3c\u4e4e\u975e\u903c\u6211\u53c2\u52a0\u884c\u52a8\u4e0d\u53ef\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u5e08\u5144\u5fc3\u91cc\u5fc5\u6709\u6570\uff0c\u6211\u4eec\u7eb5\u6709\u5343\u767e\u9ad8\u624b\u6740\u8fdb\u8ff7\u8e2a\u8c37\uff0c\u5374\u6bd4\u4e0d\u4e0a\u60a8\u7684\u767b\u9ad8\u4e00 \u547c\u3002\u201d \u3000\u3000\u201c\u8fd9\u8bdd\u600e\u4e48\u8bb2\uff1f\u201d \u3000\u3000\u201c\u7406\u7531\u5f88\u7b80\u5355\u3002\u5c0f\u5f1f\u4eec\u8fdb\u5165\u8ff7\u8e2a\u8c37\uff0e\u5373\u4f7f\u62fc\u5f97\u8840\u8089\u6a2a\u98de\u3001\u5c38\u9ab8\u904d\u5730\uff0e\u4e5f\u4e0d\u89c1\u5f97\u80fd\u628a\u970d\u5140 \u4f3d\u8d76\u8d70\u3002 \u3000\u3000\u800c\u5e08\u5144\u82e5\u80af\u8fdb\u5165\u8ff7\u8e2a\u8c37\uff0c\u53ea\u8981\u4e00\u73b0\u8eab\uff0c\u5fc5\u53ef\u4e0d\u6218\u800c\u5c48\u4eba\u4e4b\u5175\u3002\u970d\u5143\u4f3d\u4e5f\u5fc5\u5b9a\u88ab\u8feb\u4e56\u4e56\u7684 \u8ba9\u51fa\u7eff\u6797\u76df\u4e3b\u4e4b\u4f4d\u3002\u201d \u3000\u3000\u201c\u4f60\u672a\u514d\u592a\u62ac\u4e3e\u6211\u4e86\uff01\u201d \u3000\u3000\u201c\u8fd9\u662f\u4eba\u4eba\u90fd\u53ef\u4ee5\u60f3\u5230\u7684\u4e8b\uff0c\u5c0f\u5f1f\u8bf4\u7684\u5b8c\u5168\u662f\u5b9e\u8bdd\u3002\u201d \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u5854\u4e0b\u811a\u6b65\u58f0\u54cd\uff0c\u63a5\u7740\u4e0a\u6765\u4e8c\u4eba\uff0e\u3000\u4e00\u4e2a\u662f\u4e07\u6708\u5821\u603b\u7ba1\u674e\u8302\uff0c\u3000\u2014\u4e2a\u8d6b\u7136\u662f\u8ff7 \u8e2a\u8c37\u7684\u5c0f\u5934\u76ee\u8d75\u798f\u3002 \u3000\u3000\u8d75\u798f\u539f\u662f\u80e1\u67cf\u9f84\u7684\u4eb2\u4fe1\uff0c\u80e1\u67cf\u9f84\u6b7b\u540e\uff0c\u53c8\u505a\u4e86\u970d\u5143\u4f3d\u7684\u5fc3\u8179\u3002 \u3000\u3000\u80e1\u67cf\u9f84\u5fae\u5fae\u4e00\u60ca\u9053\uff1a\u201c\u8d75\u798f\u3001\u4f60\u600e\u4e48\u6765\u4e86\uff1f\u201d \u3000\u3000\u8d75\u798f\u671b\u7740\u80e1\u67cf\u9f84\uff0c\u7ffb\u8eab\u4fbf\u62dc\u4e86\u4e0b\u53bb\uff0e\u3000\u4e00\u9762\u8bed\u6c14\u6fc0\u52a8\u7684\u9053\uff1a\u201c\u8fd8\u662f\u8bf7\u674e\u5927\u53d4\u5bf9\u76df\u4e3b\u8bf4 \u5427\uff01\u201d \u3000\u3000\u674e\u8302\u5fd9\u9053\uff1a\u201c\u8d75\u798f\u662f\u5077\u5077\u5230\u4e07\u6708\u5821\u4f20\u9012\u6d88\u606f\u7684\uff0c\u6211\u628a\u4ed6\u5077\u5077\u5e26\u4e86\u6765\u89c1\u80e1\u76df\u4e3b\uff0e\u201d \u3000\u3000\u80e1\u67cf\u9f84\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u8d75\u798f\u600e\u4e48\u77e5\u9053\u6211\u8fd8\u6d3b\u7740\uff1f\u201d \u3000\u3000\u674e\u8302\u9053\uff1a\u201c\u662f\u6211\u5728\u8def\u4e0a\u544a\u8bc9\u4ed6\u7684\u3002\u201d \u3000\u3000\u80e1\u67cf\u9f84\u9053\uff1a\u201c\u4f60\u4eec\u7ecf\u8fc7\u8ff7\u8e2a\u8c37\u65f6\uff0c\u5fc5\u5b9a\u5929\u8fd8\u6ca1\u9ed1\uff0c\u82e5\u88ab\u970d\u5143\u4f3d\u7684\u773c\u7ebf\u53d1\u73b0\uff0c\u53ea\u6015\u5bf9\u8d75\u798f \u4e0d\u5229\u3002\u201d \u3000\u3000\u674e\u8302\u9053\uff1a\u201c\u6211\u4eec\u662f\u7ed5\u9053\u800c\u6765\u7684\uff0c\u8def\u4e0a\u4e0d\u4f1a\u6709\u95ee\u9898\u3002\u201d \u3000\u3000\u80e1\u67cf\u9f84\u76ee\u6ce8\u8d75\u798f\u9053\uff1a\u201c\u6709\u4ec0\u4e48\u8bdd\u8981\u8bb2\uff0c\u8fd8\u662f\u7531\u4f60\u81ea\u5df1\u8bf4\u5427\uff01\u201d \u3000\u3000\u8d75\u798f\u8bed\u6c14\u7d27\u5f20\u7684\u9053\uff1a\u201c\u76df\u4e3b\uff0c\u60a8\u53ef\u77e5\u9053\u949f\u526f\u76df\u4e3b\u7b49\u4eba\u7684\u4e0b\u843d\uff1f\u201d \u3000\u3000\u80e1\u67cf\u9f84\u795e\u8272\u2014\u7d27\u9053\uff1a\u201c\u6211\u548c\u4ed6\u4eec\u5df2\u7ecf\u5931\u53bb\u8054\u7edc\uff0c\u83ab\u975e\u4ed6\u4eec\u5df2\u88ab\u970d\u5143\u4f3d\u63b3\u8fdb\u8ff7\u8e2a\u8c37\uff1f\u201d \u3000\u3000\u8d75\u798f\u70b9\u70b9\u5934\u9053\uff1a\u201c\u9664\u4e86\u949f\u526f\u76df\u4e3b\u3001\u4f59\u5148\u751f\u548c\u5d02\u5c71\u7684\u738b\u5927\u5eb7\u5934\u9886\u5916\uff0c\u5176\u4f59\u5168\u5df2\u88ab\u62bc\u5728\u8c1c\u8e2a \u8c37\u3002\u201d \u3000\u3000\u201c\u4ed6\u4eec\u662f\u4ec0\u4e48\u65f6\u5019\u88ab\u63b3\u8fdb\u8ff7\u8e2a\u8c37\u7684\uff1f\u201d \u3000\u3000\u201c\u5927\u7ea6\u5feb\u4e24\u4e2a\u6708\u4e86\uff01\u201d \u3000\u3000\u80e1\u67cf\u9f84\u6c41\u7b97\u65f6\u95f4\uff0c\u6b63\u662f\u81ea\u5df1\u548c\u9ea6\u5c0f\u660e\u3001\u82d7\u7d20\u82d3\u521a\u52a8\u8eab\u5230\u5929\u53f0\u4e07\u82b1\u5bab\u4e0d\u4e45\u3002 \u3000\u3000\u4ed6\u9ed8\u4e86\u4e00\u9ed8\u9053\uff1a\u201c\u53ef\u77e5\u9053\u4ed6\u4eec\u88ab\u62bc\u7684\u5730\u70b9\uff1f\u76ee\u524d\u60c5\u5f62\u5982\u4f55\uff1f\u201d \u3000\u3000\u8d75\u798f\u9053\uff1a\u201c\u4ed6\u4eec\u90fd\u88ab\u62bc\u5728\u6c34\u7262\u91cc\uff0c\u90a3\u79cd\u4e0d\u6b7b\u4e0d\u6d3b\u7684\u7f6a\uff0c\u76df\u4e3b\u4e0d\u96be\u60f3\u89c1\u3002\u201d \u3000\u3000\u201c\u4f60\u5c31\u662f\u4e3a\u8fd9\u4e8b\u5230\u4e07\u6708\u5ce1\u53bb\u7684\uff1f\u201d \u3000\u3000\u201c\u5c5e\u4e0b\u5fc3\u60f3\uff0c\u4e07\u6708\u5ce1\u7684\u674e\u5927\u53d4\u3000\u5b9a\u548c\u949f\u526f\u76df\u4e3b\u3001\u4f59\u5148\u751f\u7b49\u4eba\u6709\u8054\u7edc\u3002\u6240\u4ee5\u624d\u8d81\u5de1\u8c37\u4e4b\u4fbf\uff0c \u5077\u5077\u6f5c\u5f80\u4e07\u6708\u5821\u5411\u674e\u5927\u53d4\u901a\u62a5\uff0c\u60f3\u4e0d\u5230\u76df\u4e3b\u5c45\u7136\u8fd8\u5065\u5728\u3002\u201d \u3000\u3000\u201c\u4ed6\u4eec\u65e2\u7136\u88ab\u63b3\u8fdb\u8ff7\u8e2a\u8c37\u5c06\u8fd1\u4e24\u4e2a\u6708\uff0c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u65e9\u4e9b\u5411\u674e\u603b\u7ba1\u901a\u62a5\uff1f\u201d \u3000\u3000\u201c\u4e00\u6765\u627e\u4e0d\u5230\u51fa\u8c37\u7684\u673a\u4f1a\uff0c\u4e8c\u6765\u62bc\u5728\u6c34\u7262\u91cc\u7684\u5934\u9886\u4eec\u90a3\u65f6\u8fd8\u5f88\u5b89\u5168\u3002\u201d \u3000\u3000\u201c\u83ab\u975e\u73b0\u5728\u5df2\u7ecf\u4e0d\u5b89\u5168\u4e86\uff1f\u201d \u3000\u3000\u201c\u4ed6\u2026\u2026\u4ed6\u4eec\u53ef\u80fd\u9a6c\u4e0a\u5c31\u6709\u5371\u9669\uff01\u201d \u3000\u3000\u80e1\u67cf\u9f84\u5fc3\u5934\u731b\u611f\u4e00\u9707\uff0c\u6025\u6025\u95ee\u9053\uff1a\u201c\u4ed6\u4eec\u6709\u4ec0\u4e48\u5371\u9669\uff0e\u5feb\u8bf4\uff01\u201d \u3000\u3000\u8d75\u798f\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u5c5e\u4e0b\u542c\u6765\u7684\u53ef\u9760\u6d88\u606f\uff0c\u970d\u5143\u4f3d\u5f88\u53ef\u80fd\u5728\u4e09\u5929\u4e4b\u5185\u628a\u4ed6\u4eec\u5904\u51b3\u6389\uff01\u201d \u3000\u3000\u80e1\u67cf\u9f84\u3001\u9ea6\u5c0f\u660e\u731b\u95fb\u6b64\u8a00\uff0e\u5168\u90fd\u8138\u8272\u5927\u53d8\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u82d7\u7d20\u82d3\u4e5f\u95fb\u58f0\u5230\u6765\u3002 \u3000\u3000\u5854\u5ba4\u5185\u5f00\u59cb\u4e86\u4e00\u9635\u53ef\u6015\u7684\u6c89\u5bc2\uff0c\u591a\u65f6\uff0c\u9ea6\u5c0f\u660e\u624d\u9053\uff1a\u201c\u82d7\u59d1\u5a18\u5462\uff1f\u662f\u5426\u8fd8\u5728\u5730\u7262\u91cc\uff1f\u201d \u3000\u3000\u8d75\u798f\u9053\uff1a\u201c\u82d7\u59d1\u5a18\u636e\u8bf4\u4ecd\u5728\u5730\u7262\u91cc\u3002\u201d \u3000\u3000\u201c\u4f60\u53ef\u53bb\u8fc7\u5730\u7262\u3001\u6c34\u7262\uff1f\u201d \u3000\u3000\u201c\u5730\u7262\u6709\u4e13\u4eba\u628a\u5b88\uff0c\u6c34\u7262\u5012\u662f\u53bb\u8fc7\uff0c\u53ea\u662f\u6ca1\u673a\u4f1a\u548c\u4ed6\u4eec\u8bb2\u8bdd\u3002\u201d \u3000\u3000\u80e1\u67cf\u9f84\u9053\uff1a\u201c\u4f60\u8fd8\u6709\u522b\u7684\u6d88\u606f\u6ca1\u6709\uff1f\u201d \u3000\u3000\u8d75\u798f\u9053\uff1a\u201c\u5c5e\u4e0b\u77e5\u9053\u7684\u53ea\u662f\u8fd9\u4e9b\u3002\u201d \u3000\u3000\u80e1\u67cf\u9f84\u5145\u6ee1\u611f\u6fc0\u4e4b\u60c5\u9053\uff1a\u201c\u591a\u8c22\u4f60\u5192\u9669\u524d\u6765\u9001\u4fe1\uff0c\u6b64\u5904\u4e0d\u5b9c\u4e45\u7559\uff0e\u4f60\u73b0\u5728\u5c31\u56de\u53bb\u5427\uff01\u201d \u3000\u3000\u8d75\u798f\u9053\uff1a\u201c\u76df\u4e3b\u5343\u4e07\u8981\u8bbe\u6cd5\u6551\u4ed6\u4eec\uff0c\u5c5e\u4e0b\u544a\u8f9e\u4e86\uff01\u201d \u3000\u3000\u80e1\u67cf\u9f84\u9053\uff1a\u201c\u4f60\u653e\u5fc3\uff0c\u6211\u4f1a\u6709\u6240\u884c\u52a8\u7684\uff0e\u8def\u4e0a\u8981\u5c0f\u5fc3\uff01\u201d \u3000\u3000\u674e\u8302\u4e5f\u8d77\u8eab\u544a\u8f9f\u3002 \u3000\u3000\u80e1\u67cf\u9f84\u548c\u9ea6\u5c0f\u660e\u4eb2\u81ea\u628a\u4e8c\u4eba\u9001\u5230\u5854\u5916\u3002 \u3000\u3000\u56de\u5230\u7b2c\u4e8c\u5c42\u5854\u5ba4\uff0c\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u5e08\u5144\uff0c\u60a8\u521a\u624d\u5bf9\u8d75\u798f\u8868\u793a\u8981\u6709\u6240\u884c\u52a8\uff0e\u541b\u5b50\u4e00\u8a00\uff0c\u9a77\u9a6c \u96be\u8ffd\uff0c\u603b\u4e0d\u80fd\u518d\u53cd\u6094\u5427\uff1f\u201d \u3000\u3000\u80e1\u67cf\u9f84\u9053\uff1a\u201c\u6211\u53ea\u662f\u8981\u6551\u4ed6\u4eec\u51fa\u9669\uff0c\u5e76\u975e\u8981\u548c\u970d\u5143\u4f3d\u6b63\u9762\u51b2\u7a81\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u53ea\u8981\u80fd\u6551\u4ed6\u4eec\u51fa\u9669\u5c31\u597d\uff0c\u4ee5\u540e\u7684\u4e8b\u6162\u6162\u518d\u8c08\u3002\u60a8\u51c6\u5907\u600e\u6837\u884c\u52a8\uff1f\u201d \u3000\u3000\u80e1\u67cf\u9f84\u9053\uff1a\u201c\u4e3a\u4e86\u4e0d\u6b63\u5f0f\u51fa\u9762\uff0c\u884c\u52a8\u5f53\u7136\u8d8a\u79d8\u5bc6\u8d8a\u597d\u3002\u201d \u3000\u3000\u82d7\u7d20\u82d3\u62a2\u7740\u9053\uff1a\u201c\u5343\u4e07\u4e5f\u8981\u6551\u6211\u59d0\u59d0\uff01\u201d \u3000\u3000\u80e1\u67cf\u9f84\u9053\uff1a\u201c\u90a3\u662f\u81ea\u7136\uff0c\u4f60\u59d0\u59d0\u662f\u4e3a\u4e86\u8ddf\u968f\u6211\u624d\u80cc\u53db\u9634\u624b\u4e00\u9b54\uff0c\u8fdb\u5165\u8ff7\u8e2a\u8c37\u7684\uff0c\u5c24\u5176\u5979 \u548c\u5bd2\u9999\u60c5\u540c\u59d0\u59b9\uff0c\u6211\u53c8\u600e\u4f1a\u4e0d\u6551\u5979\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u542c\u5e08\u5144\u7684\u8bed\u6c14\uff0c\u662f\u8981\u52ab\u7262\u4e86\uff0c\u4f46\u4e0d\u77e5\u600e\u6837\u52ab\u6cd5\uff1f\u201d \u3000\u3000\u80e1\u67cf\u9f84\u7565\u4e00\u6c89\u541f\u9053\uff1a\u201c\u7531\u540e\u8c37\u8fdb\u53bb\uff0e\u5c3d\u91cf\u522b\u60ca\u52a8\u970d\u5143\u4f3d\u3002\u5982\u679c\u970d\u5143\u4f3d\u4e8b\u5148\u4e0d\u52a0\u6212\u5907\uff0c\u4ee5 \u6211\u4eec\u56db\u4e2a\u4eba\u7684\u529b\u91cf\uff0c\u8db3\u53ef\u9a6c\u5230\u6210\u529f\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4e0a\u6b21\u5c0f\u5f1f\u548c\u82d7\u59d1\u5a18\u53bb\u8425\u6551\u82d7\u5927\u59d1\u5a18\uff0c\u4e5f\u662f\u7531\u540e\u8c37\u79d8\u5bc6\u8fdb\u53bb\u7684\uff0e\u4f46\u5374\u529f\u4e8f\u4e00 \u7bd1\uff0c\u521a\u4e00\u51fa\u7262\u5c31\u88ab\u970d\u5143\u4f3d\u4eb2\u7387\u9ad8\u624b\u62e6\u622a\u3002\u4e3a\u4e86\u81ea\u4fdd\uff0c\u53ea\u597d\u653e\u5f03\u82d7\u5927\u59d1\u5a18\u3002\u201d \u3000\u3000\u82d7\u7d20\u82d3\u5fc3\u601d\u7ec6\u5bc6\uff0e\u5fd9\u9053\uff1a\u201c\u4ee5\u665a\u8f88\u770b\uff0e\u53ea\u6015\u8fd9\u6b21\u6210\u529f\u7684\u5e0c\u671b\u4e5f\u4e0d\u5927\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4f60\u600e\u4e48\u8fd8\u6ca1\u884c\u52a8\u5c31\u8bf4\u4e27\u6c14\u8bdd\uff1f\u201d \u3000\u3000\u82d7\u7d20\u82d3\u9053\uff1a\u201c\u6211\u5f53\u7136\u6709\u6211\u7684\u7406\u7531\u3002\u4e0a\u6b21\u54b1\u4eec\u4e24\u4e2a\u4eba\u6ca1\u6551\u51fa\u4e00\u4e2a\u4eba\uff0c\u8fd9\u6b21\u867d\u7136\u54b1\u4eec\u6709\u56db\u4e2a \u4eba\uff0c\u4f46\u8981\u6551\u7684\u5374\u662f\u4e03\u4e2a\u4eba\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u8ba1\u7b97\u7740\u82d7\u7d20\u5170\u52a0\u4e0a\u5d02\u5c71\u7684\u8001\u5927\u8001\u4e8c\u3001\u6c5f\u5317\u4e09\u9f99\u4ee5\u53ca\u6587\u5929\u751f\uff0c\u53ef\u4e0d\u6b63\u662f\u4e03\u4e2a\u4eba\u3002 \u3000\u3000\u82d7\u7d20\u82d3\u518d\u9053\uff1a\u201c\u6700\u5927\u7684\u96be\u5904\uff0c\u662f\u4ed6\u4eec\u8fd9\u4e03\u4f4d\u5728\u7262\u91cc\u7684\u4eba\uff0c\u4e0d\u4f46\u6b66\u529f\u96be\u4ee5\u65bd\u5c55\uff0c\u53ea\u6015\u8fde\u884c \u52a8\u4e5f\u8bf8\u591a\u4e0d\u4fbf\uff0e\u201d \u3000\u3000\u6211\u59d0\u59d0\u88ab\u5173\u8fdb\u5730\u7262\u7b97\u6765\u5df2\u7ecf\u4e09\u4e2a\u6708\u4e86\uff0c\u7cbe\u795e\u4f53\u529b\u65e9\u5df2\u6d88\u78e8\u5f97\u5dee\u4e0d\u591a\uff0e\u81f3\u4e8e\u5728\u6c34\u7262\u91cc\u7684\u516d \u4f4d\uff0c\u867d\u7136\u8fd8\u4e0d\u5230\u4e8c\u4e2a\u6708\uff0e\u4f46\u6574\u5929\u4e0b\u534a\u8eab\u6ce1\u5728\u6c34\u91cc\uff0c\u53ea\u6015\u8fde\u8d70\u8def\u90fd\u56f0\u96be\u4e86\u3002\u201d \u3000\u3000\u7ecf\u8fc7\u82d7\u7d20\u82d3\u8fd9\u6837\u4e00\u5206\u6790\uff0c\u80e1\u67cf\u9f84\u548c\u9ea6\u5c0f\u660e\u90fd\u50cf\u88ab\u6d47\u4e86\u4e00\u5934\u51b7\u6c34\uff0c\u4e45\u4e45\u9ed8\u9ed8\u4e0d\u8bed\u3002 \u3000\u3000\u534a\u664c\u4e4b\u540e\uff0c\u624d\u542c\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u949f\u4e00\u8c6a\u3001\u4f59\u4ea6\u6770\u3001\u738b\u5927\u5eb7\u4ed6\u4eec\u5230\u5e95\u54ea\u91cc\u53bb\u4e86\uff1f\u5982\u679c\u4ed6\u4eec\u5728\uff0c \u54b1\u4eec\u4e5f\u662f\u4e03\u4e2a\u4eba\uff0c\u90a3\u5c31\u597d\u5f97\u591a\u4e86\u3002\u201d \u3000\u3000\u80e1\u67cf\u9f84\u548c\u82d7\u7d20\u82d3\u90fd\u672a\u5f00\u53e3\u8bf4\u8bdd\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u6447\u4e86\u6447\u5934\u9053\uff1a\u201c\u5947\u602a\uff01\u201d \u3000\u3000\u80e1\u67cf\u9f84\u9053\uff1a\u201c\u4f60\u5947\u602a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u738b\u5927\u5eb7\u662f\u4e2a\u7c97\u4eba\uff0e\u4e3a\u4ec0\u4e48\u4ed6\u6ca1\u88ab\u63b3\u8d70\uff1f\u201d \u3000\u3000\u80e1\u67cf\u9f84\u9053\uff1a\u201c\u4f60\u522b\u5c0f\u770b\u4e86\u738b\u5927\u5eb7\uff0e\u4ed6\u867d\u662f\u4e2a\u7c97\u4eba\uff0c\u4f46\u6709\u65f6\u5019\u4e5f\u7c97\u4e2d\u6709\u7ec6\u3002\u201d \u3000\u3000\u6b63\u8bf4\u8bdd\u95f4\uff0c\u5ffd\u542c\u5854\u4e0b\u53c8\u4f20\u6765\u811a\u6b65\u58f0\u97f3\u3002 \u3000\u3000\u8fd9\u65f6\u5df2\u5c06\u8fd1\u4e8c\u66f4\uff0c\u8c01\u90fd\u53ef\u4ee5\u60f3\u5230\uff0c\u6765\u4eba\u5fc5\u662f\u609f\u5c18\u6216\u662f\u4ed6\u7684\u4e09\u540d\u5f1f\u5b50\u3002 \u3000\u3000\u8c01\u77e5\u767b\u4e0a\u5854\u6765\u7684\uff0c\u5374\u6b63\u662f\u949f\u4e00\u8c6a\uff0c\u4f59\u4ea6\u4e50\u548c\u738b\u5927\u5eb7\u3002 \u3000\u3000\u80e1\u67cf\u9f84\u7b49\u56db\u4eba\u987f\u65f6\u559c\u51fa\u671b\u5916\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u53eb\u9053\uff1a\u201c\u597d\u554a\uff01\u4f60\u4eec\u73b0\u5728\u624d\u6765\uff0c\u90fd\u628a\u6211\u4eec\u6025\u574f\u5566\uff01\u201d \u5367\u9f99\u751f\u300a\u5999\u7edd\u5929\u9999\u300b \u7b2c\u4e09\u5341\u56de \u603b\u5be8\u52ab\u7262 \u3000\u3000\u949f\u4e00\u8c6a\u7b49\u4eba\u5148\u5411\u80e1\u67cf\u9f84\u89c1\u793c\uff0c\u9ea6\u5c0f\u660e\u53c8\u62a2\u7740\u4e3a\u4ed6\u4eec\u5f15\u89c1\u8303\u7389\u6606\uff0c\u5e76\u7b80\u8981\u8bf4\u660e\u8303\u7389\u6606\u7684\u6765 \u610f\u3002 \u3000\u3000\u80e1\u67cf\u9f84\u8fd9\u624d\u95ee\u9053\uff1a\u201c\u8fd9\u4e9b\u5929\u4f60\u4eec\u4e09\u4f4d\u5230\u5e95\u5728\u54ea\u91cc\u85cf\u8eab\uff1f\u201d \u3000\u3000\u949f\u4e00\u8c6a\u9762\u8272\u9eef\u7136\u9053\uff1a\u201c\u5c5e\u4e0b\u4e09\u4eba\u8fd8\u4e0d\u662f\u4e1c\u85cf\u897f\u8eb2\uff0c\u4f46\u5374\u5e76\u672a\u8fdc\u79bb\u8ff7\u8e2a\u8c37\uff0c\u4e3a\u7684\u662f\u8981\u8bbe\u6cd5 \u6551\u4ed6\u4eec\u516d\u4f4d\u51fa\u9669\u3002\u201d \u3000\u3000\u80e1\u67cf\u9f84\u9053\uff1a\u201c\u521a\u624d\u8d75\u798f\u6765\u8fc7\uff0e\u5df2\u7ecf\u544a\u77e5\u4ed6\u4eec\u516d\u4f4d\u5728\u8ff7\u8e2a\u8c37\u7684\u60c5\u5f62\u3002\u201d \u3000\u3000\u949f\u4e00\u8c6a\u8feb\u4e0d\u53ca\u5f85\u95ee\u9053\uff1a\u201c\u8d75\u798f\u6765\u8fc7\uff1f\u4ed6\u4eec\u516d\u4f4d\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u80e1\u67cf\u9f84\u6df1\u6df1\u4e00\u53f9\u9053\uff1a\u201c\u4ed6\u4eec\u90fd\u88ab\u6253\u5728\u6c34\u7262\u91cc\uff0c\u800c\u4e14\u5c31\u5728\u6700\u8fd1\u4e09\u5929\uff0c\u970d\u5143\u4f3d\u53ef\u80fd\u8981\u5bf9\u4ed6\u4eec \u4e0b\u6bd2\u624b\uff01\u201d \u3000\u3000\u949f\u2014\u8c6a\u5927\u5403\u2014\u60ca\u9053\uff1a\u201c\u8fd9\u600e\u4e48\u529e\uff1f\u76df\u4e3b\u5c31\u8be5\u8bbe\u6cd5\u642d\u6551\u4ed6\u4eec\uff01\u201d \u3000\u3000\u80e1\u67cf\u9f84\u9053\uff1a\u201c\u6211\u548c\u4ed6\u4eec\u4e09\u4f4d\uff0c\u65b9\u624d\u5c31\u5728\u8ba1\u8bae\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u4f60\u4eec\u6765\u5f97\u6b63\u597d\u201d\u3002 \u3000\u3000\u201c\u76df\u4e3b\u51c6\u5907\u5982\u4f55\u884c\u52a8\uff1f\u201d \u3000\u3000\u201c\u4e8b\u4e0d\u5b9c\u8fdf\uff0c\u4eca\u665a\u5c31\u53bb\u52ab\u7262\u3002\u201d \u3000\u3000\u201c\u7531\u54ea\u6761\u8def\u8fdb\u8c37\uff1f\u201d \u3000\u3000\u201c\u4e3a\u4e86\u4e0d\u60ca\u52a8\u5bf9\u65b9\uff0c\u5f53\u7136\u4ee5\u8d70\u540e\u8c37\u4e3a\u5b9c\u3002\u201d \u3000\u3000\u201c\u76df\u4e3b\u8ba4\u4e3a\u8fd9\u6b21\u884c\u52a8\u662f\u5426\u6709\u7edd\u5bf9\u628a\u63e1\uff1f\u201d \u3000\u3000\u201c\u6b64\u4e3e\u53ea\u51c6\u6210\u529f\uff0e\u4e0d\u51c6\u5931\u8d25\u3002\u7406\u7531\u5f88\u7b80\u5355\uff0e\u82e5\u65e0\u6cd5\u6551\u51fa\u4ed6\u4eec\uff0c\u4ed6\u4eec\u5c31\u5fc5\u6b7b\u65e0\u7591\uff0c\u53cd\u800c\u7b49 \u4e8e\u662f\u6211\u4eec\u5bb3\u4e86\u4ed6\u4eec\u3002\u201d \u3000\u3000\u949f\u4e00\u8c6a\u8138\u8272\u51dd\u91cd\uff0c\u9ed8\u7136\u4f4e\u4e0b\u5934\u53bb\u3002 \u3000\u3000\u80e1\u67cf\u9f84\u671b\u4e86\u4f59\u4ea6\u4e50\u4e00\u773c\u9053\uff1a\u201c\u4f59\u5144\u6709\u4f55\u9ad8\u89c1\uff1f\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u4e5f\u4e45\u4e45\u7b54\u4e0d\u4e0a\u8bdd\uff0c\u56e0\u4e3a\u8fd9\u6b21\u884c\u52a8\uff0e\u8d23\u4efb\u5b9e\u5728\u592a\u8fc7\u91cd\u5927\uff0c\u4e07\u4e00\u7a0d\u6709\u5dee\u6c60\uff0c\u8c01\u4e5f\u627f\u62c5 \u4e0d\u8d77\u3002\u4f59\u4ea6\u4e50\u867d\u662f\u4f4d\u667a\u591a\u661f\uff0c\u5374\u4e5f\u4e0d\u80fd\u4e0d\u8c28\u614e\u5c0f\u5fc3\u3002 \u3000\u3000\u5ffd\u542c\u738b\u5927\u5eb7\u9053\uff1a\u201c\u4ffa\u5012\u6709\u4e2a\u529e\u6cd5\uff01\u201d \u3000\u3000\u80e1\u67cf\u9f84\u9053\uff1a\u201c\u738b\u5144\u5feb\u8bb2\u51fa\u6765\u8ba9\u5927\u5bb6\u542c\u542c\uff01\u201d \u3000\u3000\u738b\u5927\u5eb7\u9053\uff1a\u201c\u4ed6\u4eec\u6c34\u7262\u91cc\u516d\u4e2a\uff0c\u52a0\u4e0a\u82d7\u5927\u59d1\u5a18\uff0e\u4e00\u5171\u662f\u4e03\u4e2a\uff0c\u54b1\u4eec\u4e5f\u662f\u4e03\u4e2a\uff0c\u4e03\u4e2a\u6551\u4e03 \u4e2a\uff0c\u82e5\u4ed6\u4eec\u884c\u52a8\u65b9\u4fbf\uff0c\u5e76\u975e\u96be\u4e8b\u3002\u4f46\u82e5\u5fc5\u987b\u2014\u4e2a\u4eba\u80cc\u7740\u4e00\u4e2a\u4eba\uff0c\u53ea\u8981\u970d\u5143\u4f3d\u5e26\u9886\u9ad8\u624b\u51fa\u9762\u62e6 \u622a\uff0c\u90a3\u5c31\u5f88\u96be\u8131\u8eab\u4e86\uff01\u201d \u3000\u3000\u80e1\u67cf\u9f84\u9894\u9996\u9053\uff1a\u201c\u65b9\u624d\u82d7\u59d1\u5a18\u4e5f\u8fd9\u6837\u5206\u6790\u8fc7\uff0c\u83ab\u975e\u738b\u5144\u8fd8\u6709\u522b\u7684\u529e\u6cd5\uff1f\u201d \u3000\u3000\u738b\u5927\u5eb7\u633a\u4e86\u633a\u80f8\u9053\uff1a\u201c\u5f53\u7136\u6709\uff0c\u6d3b\u4eba\u4e0d\u80fd\u7ed9\u5c3f\u618b\u6b7b\uff0c\u54b1\u4eec\u8981\u60f3\u4e2a\u58f0\u4e1c\u51fb\u897f\u7684\u529e\u6cd5\uff0c\u628a\u970d \u5143\u4f3d\u548c\u4ed6\u624b\u4e0b\u7684\u9ad8\u624b\u5f15\u5230\u8fdc\u79bb\u6c34\u7262\u7684\u5730\u65b9\u4e0d\u5c31\u7ed3\u4e86\u3002\u201d \u3000\u3000\u201c\u738b\u5144\u51c6\u5907\u600e\u6837\u58f0\u4e1c\u51fb\u897f\uff1f\u201d \u3000\u3000\u201c\u5f88\u7b80\u5355\uff0c\u54b1\u4eec\u5206\u51fa\u4e00\u90e8\u5206\u5230\u524d\u8c37\u653e\u706b\u70e7\u5c71\uff0c\u8bf1\u4f7f\u970d\u5143\u4f3d\u548c\u8ff7\u8e2a\u8c37\u7684\u9ad8\u624b\u90fd\u8d76\u5f80\u524d\u8c37\uff0c \u540e\u8c37\u4e0d\u5c31\u7a7a\u865a\u4e86\u5417\uff1f\u90a3\u65f6\u52ab\u7262\u5c31\u4e0d\u4f1a\u518d\u6709\u9ad8\u624b\u62e6\u622a\uff0c\u7eb5\u7136\u8fd8\u6709\u5c11\u6570\u7684\uff0c\u54b1\u4eec\u8fd8\u6015\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u80e1\u67cf\u9f84\u9053\uff1a\u201c\u738b\u5144\u679c\u7136\u9ad8\u660e\uff0c\u521a\u624d\u6211\u8fd8\u8bf4\u8fc7\u4f60\u7c97\u4e2d\u6709\u7ec6\uff0e\u5f53\u771f\u9a6c\u4e0a\u5c31\u5151\u73b0\u4e86\u3002\u201d \u3000\u3000\u738b\u5927\u5eb7\u7b11\u9053\uff1a\u201c\u76df\u4e3b\u7528\u4e0d\u7740\u73b0\u5728\u5938\u5956\uff0c\u7b49\u4e8b\u60c5\u6210\u529f\u4e86\uff0c\u4ffa\u518d\u5411\u60a8\u9886\u8d4f\u3002\u201d \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u76df\u4e3b\uff0c\u8fd9\u524d\u8c37\u653e\u706b\u4e4b\u4e8b\uff0c\u5c31\u4ea4\u7ed9\u5c5e\u4e0b\u4e86\uff0c\u5c5e\u4e0b\u5bf9\u5185\u8c37\u7684\u5730\u5f62\uff0c\u6bd4\u4ed6\u4eec\u90fd\u719f\u3002 \u524d\u8c37\u5165\u53e3\u4e0d\u8fdc\uff0c\u6709\u4e24\u5904\u6728\u9020\u8425\u820d\uff0e\u4f9b\u5728\u524d\u8c37\u5de1\u591c\u653e\u54e8\u7684\u4f4f\u7528\u3002 \u3000\u3000\u653e\u706b\u70e7\u8d77\u6765\u5f88\u5bb9\u6613\uff0c\u4e24\u5904\u8425\u820d\uff0c\u5171\u6709\u4e8c\u4e09\u5341\u95f4\uff0c\u706b\u52bf\u4e00\u8d77\uff0e\u8db3\u53ef\u7167\u4eae\u534a\u8fb9\u5929\uff0c\u970d\u5143\u4f3d\u5fc5 \u5b9a\u9886\u9ad8\u624b\u8d76\u53bb\uff0c\u5230\u90a3\u65f6\u6b63\u5982\u738b\u5144\u6240\u8a00\uff0c\u540e\u8c37\u5fc5\u662f\u7a7a\u865a\u7684\u3002\u201d \u3000\u3000\u80e1\u67cf\u9f84\u7565\u2014\u601d\u5fd6\u9053\uff1a\u201c\u4f60\u4e00\u4e2a\u4eba\uff0c\u53ea\u6015\u4e0d\u591f\u3002\u201d \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u90a3\u5c31\u8bf7\u4f59\u5144\u548c\u5c5e\u4e0b\u2014\u8d77\u53bb\u3002\u201d \u3000\u3000\u738b\u5927\u5eb7\u62a2\u7740\u9053\uff1a\u201c\u4ffa\u4e5f\u8981\u53bb\uff0c\u6740\u4eba\u653e\u706b\u7684\u4e8b\uff0e\u4ffa\u6700\u62ff\u624b\uff01\u201d \u3000\u3000\u949f\u2014\u8c6a\u9053\uff1a\u201c\u6709\u6211\u548c\u4f59\u5144\u8db3\u591f\u4e86\uff0c\u76df\u4e3b\u90a3\u8fb9\u6700\u987b\u4eba\u624b\uff0e\u4f60\u5c31\u968f\u76df\u4e3b\u884c\u52a8\u597d\u4e86\uff0c\u66f4\u4f55\u51b5\u4f60 \u4eec\u5d02\u5c71\u7684\u8001\u5927\u8001\u4e8c\u90fd\u5728\u6c34\u7262\u91cc\uff0c\u4f60\u5fc5\u987b\u5b9e\u9645\u53c2\u52a0\u8425\u6551\u884c\u52a8\u3002\u201d \u3000\u3000\u738b\u5927\u5eb7\u9053\uff1a\u201c\u949f\u526f\u76df\u4e3b\u8bf4\u5f97\u5bf9\uff0c\u90a3\u4e48\u4ffa\u5c31\u4e0d\u7ba1\u653e\u706b\u7684\u4e8b\u4e86\u3002\u201d \u3000\u3000\u80e1\u67cf\u9f84\u671b\u4e86\u671b\u5854\u7a97\u5916\u7684\u5929\u8272\u9053\uff1a\u201c\u73b0\u5728\u4fbf\u53ef\u884c\u52a8\uff0c\u8d70\u540e\u8c37\u5fc5\u987b\u591a\u7ed5\u4e03\u516b\u91cc\u8def\uff0c\u4e88\u8ba1\u6211\u4eec \u52ab\u7262\u7684\uff0e\u5927\u7ea6\u4e09\u66f4\u5de6\u53f3\u624d\u53ef\u8fdb\u5165\u540e\u8c37\uff0c\u949f\u5144\u548c\u4f59\u5144\u5fc5\u987b\u5728\u4e09\u66f4\u524d\u653e\u8d77\u706b\u6765\u3002\u201d \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u5c5e\u4e0b\u9075\u547d\u3002\u4e0d\u8fc7\u4e3a\u4e86\u884c\u52a8\u786e\u5b9e\uff0c\u76df\u4e3b\u6700\u597d\u5148\u5728\u540e\u8c37\u7a33\u4f4f\u8eab\u5f62\uff0c\u7b49\u53d1\u73b0\u524d\u8c37 \u706b\u8d77\u540e\uff0c\u518d\u91c7\u53d6\u884c\u52a8\u3002\u201d \u3000\u3000\u82d7\u7d20\u82d3\u62a2\u7740\u9053\uff1a\u201c\u8ff7\u8e2a\u8c37\u5230\u5904\u90fd\u662f\u5c71\uff0c\u524d\u8c37\u706b\u8d77\uff0c\u540e\u8c37\u80fd\u770b\u5230\u5417\uff1f\u201d \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u51e0\u5341\u95f4\u8425\u820d\u70e7\u8d77\u6765\uff0c\u8db3\u53ef\u7167\u4eae\u534a\u8fb9\u5929\uff0c\u4eba\u5728\u540e\u8c37\uff0e\u7eb5\u7136\u770b\u4e0d\u5230\u706b\u82d7\uff0c\u4f46\u90a3 \u7247\u5149\u4eae\u5374\u4e00\u5b9a\u4f1a\u770b\u5230\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u5e08\u5144\uff0e\u8981\u4e0d\u8981\u901a\u77e5\u609f\u5c18\u65b9\u4e08\u548c\u5929\u7985\u8001\u7985\u5e08\uff1f\u201d \u3000\u3000\u80e1\u67cf\u9f84\u9053\uff1a\u201c\u4e0d\u5fc5\u4e86\uff01\u8fd9\u662f\u54b1\u4eec\u81ea\u5df1\u7684\u4e8b\uff0c\u7528\u4e0d\u7740\u4ed6\u4eec\u63d2\u624b\uff0e\u5426\u5219\uff0e\u8fd8\u5f88\u53ef\u80fd\u4f7f\u4ed6\u4eec\u53d7 \u5230\u7275\u8fde\u3002\u201d \u3000\u3000\u4e8e\u662f\uff0c\u5404\u4eba\u5f00\u59cb\u6574\u88c5\u53ca\u4f69\u5e26\u5175\u5200\u3002 \u3000\u3000\u56e0\u4e3a\u662f\u591c\u95f4\uff0c\u82d7\u7d20\u82d3\u4e5f\u4e0d\u518d\u6539\u626e\u7537\u88c5\uff0e \u3000\u3000\u4e03\u4e2a\u4eba\u6084\u6084\u79bb\u5f00\u767d\u59d1\u5bfa\uff0c\u5728\u8ddd\u8ff7\u8e2a\u8c37\u4e8c\u4e09\u91cc\u8def\u65f6\uff0c\u80e1\u67cf\u9f84\u7b49\u4eba\u4fbf\u548c\u949f\u4e00\u8c6a\uff0e\u4f59\u4ea6\u4e50\u5206\u4e86 \u9053\u3002 \u3000\u3000\u5230\u8fd9\u65f6\u9ea6\u5c0f\u660e\u624d\u95ee\u738b\u5927\u5eb7\u9053\uff1a\u201c\u4ed6\u4eec\u516d\u4e2a\u4eba\u7a76\u7adf\u662f\u600e\u6837\u88ab\u63b3\u8fdb\u8c37\u7684\uff1f\u201d \u3000\u3000\u738b\u5927\u5eb7\u9053\uff1a\u201c\u8fd8\u4e0d\u662f\u4e3a\u4e86\u6551\u82d7\u5927\u59d1\u5a18\uff0c\u90a3\u665a\u4ffa\u4eec\u4e5f\u662f\u7531\u540e\u8c37\u8fdb\u53bb\u7684\u3002\u201d \u3000\u3000\u201c\u8fdb\u53bb\u4ee5\u540e\u5c31\u4e2d\u4f0f\u4e86\uff0c\u5bf9\u5417\uff1f\u201d \u3000\u3000\u201c\u5dee\u4e0d\u591a\u5427\uff01\u8fd8\u597d\uff0c\u5e76\u6ca1\u88ab\u970d\u5143\u4f3d\u4e00\u7f51\u6253\u5c3d\u3002\u201d \u3000\u3000\u201c\u770b\u4e0d\u51fa\u4f60\u738b\u5f53\u5bb6\u7684\u8fd8\u5f88\u673a\u8b66\uff0c\u80fd\u591f\u5b89\u7136\u8131\u9669\u3002\u201d \u3000\u3000\u201c\u8fde\u76df\u4e3b\u90fd\u8bf4\u4ffa\u7c97\u4e2d\u6709\u7ec6\uff0c\u4ffa\u5f53\u7136\u5f88\u673a\u8b66\u3002\u5982\u679c\u4ffa\u88ab\u970d\u5143\u4f3d\u6349\u53bb\uff0c\u90a3\u5c31\u6bd4\u4ed6\u4eec\u516d\u4e2a\u66f4\u5012 \u9709\u5566\uff01\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u4ffa\u5bf9\u970d\u5143\u4f3d\u90a3\u8001\u5c0f\u5b50\uff0e\u4e00\u5411\u4e0d\u5ba2\u6c14\uff0c\u5728\u8ff7\u8e2a\u8c37\u65f6\uff0c\u7ecf\u5e38\u9876\u649e\u4ed6\uff0c\u5c31\u51ed\u8fd9\u4e00\u70b9\uff0c\u4ed6 \u5c82\u80af\u8f7b\u9976\u4e86\u4ffa\uff01\u201d \u3000\u3000\u201c\u5982\u679c\u6211\u88ab\u4ed6\u6349\u53bb\uff0c\u90a3\u4e0d\u6bd4\u4f60\u66f4\u60e8\uff01\u201d \u3000\u3000\u201c\u90a3\u4f60\u5c31\u522b\u60f3\u6d3b\u5566\uff01\u201d \u3000\u3000\u80e1\u67cf\u9f84\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4e2a\u522b\u591a\u8bb2\u8bdd\uff0c\u4fdd\u6301\u9759\u8083\u8981\u7d27\u3002 \u3000\u3000\u8fd9\u65f6\u5929\u7a7a\u5e76\u65e0\u6708\u8272\uff0c\u6b63\u597d\u5f85\u4f1a\u513f\u5bb9\u6613\u53d1\u73b0\u706b\u5149\u3002 \u3000\u3000\u5927\u7ea6\u4e0d\u5230\u4e09\u66f4\uff0c\u4e00\u884c\u4e94\u4eba\uff0e\u4fbf\u5df2\u5230\u8fbe\u8c37\u540e\u7684\u4e00\u4e2a\u5c71\u5934\uff0e \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u5e08\u5144\uff0c\u54b1\u4eec\u5c31\u5728\u8fd9\u91cc\u5750\u4e0b\u6765\u4f11\u606f\u5427\uff01\u8fd9\u91cc\u5730\u52bf\u9ad8\uff0c\u5f85\u4f1a\u513f\u53d1\u73b0\u706b\u5149\u6bd4\u8f83\u5bb9 \u6613\u3002\u201d \u3000\u3000\u80e1\u67cf\u9f84\u968f\u5373\u5429\u5490\u4f17\u4eba\u5c31\u5730\u5750\u4e0b\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u7981\u4e0d\u4f4f\u6307\u7740\u8eab\u4fa7\u8fdc\u5904\u4e00\u5ea7\u5ced\u62d4\u5982\u524a\u7684\u7edd\u5cf0\u9053\uff1a\u201c\u5e08\u5144\u53ef\u8fd8\u8bb0\u5f97\u90a3\u5ea7\u5c71\u5cf0\uff1f\u201d \u3000\u3000\u80e1\u67cf\u9f84\u671b\u4e86\u671b\u9053\uff1a\u201c\u90a3\u4e2a\u662f\u96ea\u5cf0\u5417\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u5e08\u5144\u53ef\u5230\u8fc7\u96ea\u5cf0\u4e4b\u5dc5\uff1f\u201d \u3000\u3000\u80e1\u67cf\u9f84\u6447\u5934\u9053\uff1a\u201c\u5f53\u5e74\u6211\u5728\u8ff7\u8e2a\u8c37\u65f6\uff0c\u4e00\u6765\u4e8b\u60c5\u592a\u5fd9\uff0c\u4e8c\u6765\u56e0\u96ea\u5cf0\u592a\u8fc7\u9669\u7edd\uff0e\u6500\u767b\u4e0d\u6613\uff0c \u4e5f\u5c31\u4e0d\u66fe\u767b\u4e0a\u5cf0\u5dc5\u8fc7\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u8bed\u6c14\u6fc0\u52a8\u7684\u9053\uff1a\u201c\u5176\u5b9e\u5e08\u5144\u5230\u8fc7\uff0c\u53ea\u662f\u4f60\u81ea\u5df1\u4e0d\u77e5\u9053\u7f62\u4e86\uff01\u201d \u3000\u3000\u80e1\u67cf\u9f84\u6115\u7136\u9053\uff1a\u201c\u4f60\u8fd9\u8bdd\u6211\u600e\u4e48\u542c\u4e0d\u61c2\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u5401\u53e3\u6c14\u9053\uff1a\u201c\u5f53\u5e74\u5e08\u5144\u6b7b\u540e\uff0c\u662f\u5e08\u5ac2\u4eb2\u624b\u628a\u60a8\u846c\u5728\u96ea\u5cf0\u9876\u4e0a\u7684\u3002\u201d \u3000\u3000\u201c\u662f\u4ec0\u4e48\u4eba\u628a\u6211\u62ac\u5230\u5cf0\u9876\u7684\uff1f\u201d \u3000\u3000\u201c\u662f\u5e08\u5ac2\u62b1\u7740\u5e08\u5144\u6500\u4e0a\u5cf0\u9876\uff0c\u53e6\u5916\u5e2e\u5fd9\u7684\u662f\u949f\u4e00\u8c6a\u3002\u201d \u3000\u3000\u80e1\u67cf\u9f84\u6ee1\u8138\u72d0\u7591\u7684\u95ee\u9053\uff1a\u201c\u5bd2\u9999\u4e00\u5411\u8eab\u5b50\u5a07\u5f31\uff0c\u5f53\u65f6\u6b66\u529f\u4e5f\u5e76\u4e0d\u9ad8\uff0c\u5979\u600e\u80fd\u628a\u6211\u62b1\u4e0a\u96ea \u5cf0\u4e4b\u5dc5\uff1f\u201d \u3000\u3000\u201c\u7cbe\u8bda\u6240\u81f3\uff0e\u91d1\u77f3\u4e3a\u5f00\u3002\u5f53\u65f6\u5e08\u5ac2\u628a\u2014\u8154\u60b2\u75db\uff0c\u5316\u4e3a\u529b\u91cf\uff0e\u8fd9\u5c31\u662f\u6240\u8c13\u8d85\u80fd\u529b\uff0e\u3000\u4e00\u79cd \u4e0d\u53ef\u601d\u4e49\u7684\u8d85\u80fd\u529b\u3002\u201d \u3000\u3000\u201c\u5979\u4e3a\u4ec0\u4e48\u8981\u628a\u6211\u846c\u5728\u96ea\u5cf0\uff1f\u201d \u3000\u3000\u201c\u5979\u8981\u8ba9\u60a8\u7684\u9057\u4f53\u6c38\u4fdd\u4e0d\u574f\uff0c\u4ee5\u540e\u53ef\u4ee5\u5e38\u5e38\u5230\u96ea\u5cf0\u6316\u5f00\u96ea\u5806\u77bb\u4ef0\u4f60\u7684\u9057\u5bb9\u3002\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u80e1\u67cf\u9f84\u65e9\u5df2\u96be\u7981\u6fc0\u52a8\uff0c\u6cea\u6c34\u6eda\u843d\u53cc\u988a\u3002 \u3000\u3000\u597d\u5728\u662f\u591c\u95f4\uff0c\u9ea6\u5c0f\u660e\u7b49\u51e0\u4eba\u5e76\u672a\u53d1\u73b0\u3002 \u3000\u3000\u5ffd\u542c\u82d7\u7d20\u82d3\u53eb\u9053\uff1a\u201c\u597d\u50cf\u5df2\u7ecf\u8d77\u706b\u4e86\uff01\u201d \u3000\u3000\u4f17\u4eba\u9f50\u9f50\u5f80\u524d\u8c37\u65b9\u5411\u671b\u53bb\uff0c\u679c\u7136\u5929\u8272\u6e10\u6e10\u53d8\u4eae\uff0e\u50cf\u662f\u65e5\u51fa\u524d\u521d\u73b0\u66d9\u5149\uff0e\u4e0d\u5927\u4e00\u4f1a\u5de5\u592b\u4fbf \u706b\u5149\u51b2\u5929\uff0e\u8fde\u558a\u53eb\u547c\u6551\u4e4b\u58f0\uff0c\u90fd\u9690\u9690\u53ef\u95fb\u3002 \u3000\u3000\u53c8\u8fc7\u4e86\u534a\u54cd\uff0c\u90a3\u706b\u5149\u5df2\u51e0\u4e4e\u70e7\u7ea2\u4e86\u534a\u8fb9\u5929\uff0c\u800c\u4e14\u5f88\u660e\u663e\u7684\u770b\u51fa\u53d1\u706b\u70b9\u6709\u4e8c\u5904\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u5e08\u5144\uff0c\u8be5\u884c\u52a8\u4e86\uff01\u201d \u3000\u3000\u80e1\u67cf\u9f84\u9053\uff1a\u201c\u518d\u7a0d\u7b49\u4e00\u4f1a\u513f\uff0c\u73b0\u5728\u970d\u5143\u4f3d\u5fc5\u5b9a\u521a\u521a\u95fb\u62a5\uff0c\u8fd8\u4e0d\u53ef\u80fd\u8d76\u5f80\u524d\u8c37\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9ed8\u4e86\u4e00\u9ed8\u9053\uff1a\u201c\u5f85\u4f1a\u513f\u901a\u8fc7\u5c97\u54e8\u6216\u9047\u5230\u5de1\u591c\u7684\u600e\u4e48\u5904\u7f6e\uff1f\u201d \u3000\u3000\u80e1\u67cf\u9f84\u9053\uff1a\u201c\u6700\u597d\u7559\u4ed6\u4eec\u4e00\u547d\uff01\u201d \u3000\u3000\u201c\u5e08\u5144\u5f53\u5e74\u4eba\u79f0\u51b7\u9762\u960e\u7f57\uff0c\u73b0\u5728\u597d\u50cf\u5df2\u7ecf\u6148\u60b2\u4e3a\u6000\u5566\uff01\u201d \u3000\u3000\u201c\u8bdd\u4e0d\u80fd\u8fd9\u6837\u8bb2\uff0c\u4ed6\u4eec\u90fd\u662f\u65e0\u8f9c\u7684\uff0c\u800c\u4e14\u4e5f\u662f\u6211\u6614\u65e5\u7684\u90e8\u4e0b\uff0c\u53ea\u8981\u4ed6\u4eec\u4e0d\u788d\u4e8b\uff0c\u5c31\u4e0d\u5e94 \u5984\u52a0\u6740\u5bb3\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u5230\u4e86\u90a3\u91cc\uff0c\u662f\u5206\u5934\u884c\u52a8\uff0c\u8fd8\u662f\u5927\u5bb6\u5728\u4e00\u8d77\u884c\u52a8\uff1f\u201d \u3000\u3000\u80e1\u67cf\u9f84\u65e9\u6709\u8179\u6848\uff0e\u9053\uff1a\u201c\u5206\u5934\u884c\u52a8\uff0e\u53ef\u4ee5\u8282\u7701\u65f6\u95f4\uff0e\u800c\u4e14\u6c34\u7262\u5730\u7262\u76f8\u9694\u4e0d\u8fdc\uff0c\u4ecd\u53ef\u76f8\u4e92 \u7167\u5e94\u3002\u201d \u3000\u3000\u201c\u5e08\u5144\u73b0\u5728\u5c31\u8bf7\u5206\u914d\u4efb\u52a1\uff01\u201d \u3000\u3000\u201c\u5c31\u7531\u4f60\u548c\u82d7\u59d1\u5a18\u53bb\u52ab\u5730\u7262\uff0e\u6211\u548c\u738b\u5144\u4ee5\u53ca\u8303\u5c11\u5821\u4e3b\u53bb\u52ab\u6c34\u7262\u3002\u81f3\u4e8e\u5f97\u624b\u540e\u4f1a\u5408\u5730\u70b9\uff0c \u7b49\u5230\u4e86\u73b0\u573a\u518d\u6307\u5b9a\u3002\u201d \u3000\u3000\u201c\u597d\uff0c\u7531\u5c0f\u5f1f\u5728\u524d\u9762\u5f00\u8def\uff0c\u4f60\u4eec\u6700\u597d\u79bb\u8fdc\u4e9b\uff0c\u514d\u5f97\u76ee\u6807\u592a\u5927\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u521a\u8d70\u51fa\u51e0\u4e08\uff0c\u82d7\u7d20\u82d3\u5df2\u968f\u540e\u8ddf\u4e86\u4e0a\u6765\u3002 \u3000\u3000\u4ed6\u4eec\u4e8c\u4eba\u4e0a\u6b21\u6709\u2014\u6b21\u52ab\u7262\u7ecf\u9a8c\uff0c\u800c\u4e14\u8fd9\u6b21\u8d70\u7684\u4e5f\u662f\u540c\u4e00\u8def\u7ebf\uff0c\u5728\u524d\u5f00\u8def\uff0c\u81ea\u662f\u6700\u9002\u5408\u4e0d \u8fc7\u3002 \u3000\u3000\u8fd9\u6b21\u6ca1\u78b0\u4e0a\u5de1\u591c\u7684\uff0c\u53ef\u80fd\u56e0\u524d\u8c37\u5931\u706b\uff0c\u5de1\u591c\u7684\u90fd\u8d81\u673a\u8dd1\u53bb\u770b\u70ed\u95f9\u4e86\u3002 \u3000\u3000\u81f3\u4e8e\u8def\u7ecf\u7684\u4e8c\u4e09\u5904\u5c97\u54e8\uff0c\u4e5f\u90fd\u56e0\u53ea\u987e\u8f6c\u5934\u5411\u524d\u8c37\u65b9\u5411\u671b\uff0c\u758f\u4e8e\u8b66\u89c9\uff0c\u4e5f\u5168\u88ab\u9ea6\u5c0f\u660e\u548c\u82d7 \u7d20\u82d3\u4ee5\u9694\u7a7a\u6253\u7a74\u65b9\u5f0f\u70b9\u5012\u3002 \u3000\u3000\u5f88\u5feb\u4fbf\u6478\u5230\u5730\u7262\u524d\u65b9\uff0c\u5f53\u9ea6\u5c0f\u660e\u548c\u82d7\u7d20\u82d3\u5c06\u5730\u7262\u5916\u7684\u8b66\u536b\u4e5f\u70b9\u5012\u540e\uff0c\u80e1\u67cf\u9f84\u7b49\u4e09\u4eba\u4e5f\u8ddf \u4e86\u4e0a\u6765\u3002 \u3000\u3000\u80e1\u67cf\u9f84\u6307\u7740\u9644\u8fd1\u4e00\u68f5\u5927\u6811\u9053\uff1a\u201c\u73b0\u5728\u5c31\u5206\u5934\u884c\u52a8\uff0c\u5f85\u4f1a\u5c31\u5728\u5927\u6811\u4e0b\u4f1a\u5408\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u5b8c\u8bdd\uff0c\u5e26\u7740\u738b\u5927\u5eb7\u548c\u8303\u7389\u6606\u5954\u4e0b\u6c34\u7262\u3002 \u3000\u3000\u6c34\u7262\u5c31\u5728\u5730\u7262\u4fa7\u65b9\u4e0d\u8db3\u767e\u6b65\u5904\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u89c1\u80e1\u67cf\u9f84\u7b49\u4eba\u5df2\u8d70\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u82d7\u59d1\u5a18\uff0c\u54b1\u4eec\u8fd9\u5c31\u8fdb\u5730\u7262\u5427\uff01\u201d \u3000\u3000\u8fd9\u6b21\u4ecd\u662f\u7531\u9ea6\u5c0f\u660e\u5728\u524d\uff0c\u4ed6\u4e00\u624b\u62a4\u80f8\uff0c\u4e00\u624b\u4ed7\u5251\uff0c\u6cbf\u7740\u77f3\u9636\u7ea7\u7ea7\u800c\u4e0b\u3002 \u3000\u3000\u5230\u8fbe\u7262\u5e95\uff0c\u5de7\u5f97\u5f88\uff0c\u4ecd\u662f\u9609\u6389\u2018\u5b9d\u8d1d\u2019\u7684\u738b\u4e09\u76d1\u5b88\u5728\u90a3\u91cc\u3002 \u3000\u3000\u738b\u4e09\u4e4d\u89c1\u9ea6\u5c0f\u660e\uff0c\u5927\u60ca\u5931\u8272\u9053\uff1a\u201c\u9ea6\u5c0f\u4fa0\u9976\u547d\uff01\u9976\u547d\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u53ea\u8981\u4f60\u80af\u5408\u4f5c\uff0c\u6211\u5c31\u4e0d\u6740\u4f60\u3002\u201d \u3000\u3000\u201c\u9ea6\u5c0f\u4fa0\u8981\u5c0f\u7684\u600e\u6837\u5408\u4f5c\uff1f\u201d \u3000\u3000\u201c\u6253\u5f00\u94c1\u6805\u95e8\uff01\u201d \u3000\u3000\u8fd9\u65f6\u94c1\u6805\u5185\u7684\u82d7\u7d20\u5170\u5df2\u7531\u7761\u68a6\u4e2d\u60ca\u9192\uff0c\u800c\u4e14\u542c\u51fa\u6765\u4eba\u662f\u9ea6\u5c0f\u660e\uff0c\u5fd9\u53eb\u9053\uff1a\u201c\u9ea6\u5c0f\u5144\u5f1f\uff0c \u4f60\u600e\u4e48\u53c8\u6765\u4e86\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4e0a\u6b21\u6ca1\u6551\u51fa\u4f60\uff0c\u8fd9\u6b21\u4f60\u653e\u5fc3\uff0c\u4e00\u5b9a\u80fd\u628a\u4f60\u6551\u51fa\u53bb\u3002\u201d \u3000\u3000\u82d7\u7d20\u5170\u9053\uff1a\u201c\u53ef\u662f\u6211\u884c\u52a8\u4e0d\u4fbf\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u6ca1\u5173\u7cfb\uff0c\u4ee4\u59b9\u53ef\u4ee5\u80cc\u4f60\uff01\u201d \u3000\u3000\u82d7\u7d20\u82d3\u8fde\u5fd9\u53eb\u9053\uff1a\u201c\u59d0\u59d0\uff0e\u6211\u4e5f\u6765\u4e86\uff01\u201d \u3000\u3000\u8fd9\u65f6\u738b\u4e09\u5df2\u628a\u7262\u95e8\u6253\u5f00\uff0c\u9ea6\u5c0f\u660e\u4e00\u6307\u628a\u738b\u4e09\u70b9\u660f\u8fc7\u53bb\uff0c\u4fbf\u51b2\u5230\u82d7\u7d20\u5170\u7684\u77f3\u5e8a\u524d\uff0e \u3000\u3000\u82d7\u7d20\u5170\u7f9e\u602f\u602f\u7684\u53eb\u9053\uff1a\u201c\u9ea6\u5c0f\u5144\u5f1f\u8bf7\u7b49\u4e00\u4e0b\uff0c\u6211\u7684\u8863\u670d\u8fd8\u6ca1\u7a7f\u597d\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u8138\u4e0a\u4e00\u70ed\uff0c\u5fd9\u9000\u5230\u94c1\u6805\u5916\u9053\uff1a\u201c\u4e8c\u59d1\u5a18\u5feb\u66ff\u4ee4\u59d0\u7a7f\u597d\u8863\u670d\uff0c\u6211\u5728\u94c1\u6805\u5916\u7b49\u4f60\u4eec\uff01\u201d \u3000\u3000\u82d7\u7d20\u82d3\u5f88\u5feb\u4fbf\u4e3a\u59d0\u59d0\u7a7f\u597d\u8863\u670d\uff0c\u5e76\u6276\u6301\u7740\u5979\u8d70\u51fa\u94c1\u6805\u3002 \u3000\u3000\u706f\u5f71\u4e0b\uff0e\u9ea6\u5c0f\u660e\u65e9\u770b\u51fa\u82d7\u7d20\u5170\u7684\u5bb9\u8272\u8d8a\u53d1\u6194\u60b4\uff0c\u884c\u52a8\u95f4\u4e5f\u50cf\u6709\u6c14\u65e0\u529b\u3002 \u3000\u3000\u4ed6\u6765\u4e0d\u53ca\u591a\u5bd2\u55a7\u4ec0\u4e48\uff0c\u968f\u5373\u8bf4\u9053\uff1a\u201c\u8fd8\u662f\u6211\u5728\u524d\u9762\uff0c\u4e8c\u59d1\u5a18\u5343\u4e07\u6276\u6301\u597d\u4ee4\u59d0\uff01\u201d \u3000\u3000\u82d7\u7d20\u82d3\u7d22\u6027\u628a\u59d0\u59d0\u62b1\u4e86\u8d77\u6765\u9053\uff1a\u201c\u4e0a\u77f3\u9636\u5427\uff01\u8d70\u6162\u4e9b\uff01\u201d \u3000\u3000\u6765\u5230\u7262\u5916\uff0c\u76f4\u5954\u80e1\u67cf\u9f84\u6307\u5b9a\u7684\u90a3\u68f5\u5927\u6811\u4e0b\u3002 \u3000\u3000\u82d7\u7d20\u82d3\u628a\u82d7\u7d20\u5170\u653e\u4e0b\u9053\uff1a\u201c\u59d0\u59d0\uff0c\u54b1\u4eec\u5c31\u5728\u8fd9\u91cc\u4f11\u606f\u2014\u4f1a\u513f\u5427\uff01\u201d \u3000\u3000\u82d7\u7d20\u5170\u4e0d\u89c9\u4e00\u6123\u9053\uff1a\u201c\u9a6c\u4e0a\u9003\u51fa\u8ff7\u8e2a\u8c37\u8981\u7d27\uff0c\u4e3a\u4ec0\u4e48\u8981\u4f11\u606f\uff1f\u970d\u5143\u4f3d\u8ffd\u6765\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u82d7\u7d20\u82d3\u9053\uff1a\u201c\u59d0\u59d0\u653e\u5fc3\uff0c\u970d\u5143\u4f3d\u7edd\u5bf9\u4e0d\u53ef\u80fd\u5230\u8fd9\u91cc\u6765\u3002\u201d \u3000\u3000\u201c\u4f60\u600e\u4e48\u77e5\u9053\uff1f\u201d \u3000\u3000\u201c\u949f\u526f\u76df\u4e3b\u548c\u4f59\u5148\u751f\u5728\u524d\u8c37\u653e\u706b\uff0c\u706b\u52bf\u5f88\u5927\uff0c\u970d\u5143\u4f3d\u4e00\u5b9a\u8d76\u5230\u524d\u8c37\u53bb\u4e86\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u4ed6\u4eec\u8fd8\u6709\u9ad8\u624b\u3002\u201d \u3000\u3000\u201c\u6709\u9ad8\u624b\u4e5f\u4e0d\u53ef\u80fd\u5230\u8fd9\u8fb9\u6765\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4e8c\u59d1\u5a18\u5c31\u5bf9\u4ee4\u59d0\u5b9e\u8bf4\u4e86\u5427\uff01\u201d \u3000\u3000\u82d7\u7d20\u82d3\u9053\uff1a\u201c\u59d0\u59d0\uff0e\u54b1\u4eec\u8981\u5728\u8fd9\u91cc\u7b49\u80e1\u76df\u4e3b\uff0c\u56e0\u4e3a\u80e1\u76df\u4e3b\u53bb\u6551\u6c34\u7262\u91cc\u7684\u4eba\u53bb\u4e86\u3002\u201d \u3000\u3000\u82d7\u7d20\u5170\u832b\u7136\u9053\uff1a\u201c\u54ea\u4e2a\u80e1\u76df\u4e3b\uff1f\u201d \u3000\u3000\u201c\u80e1\u76df\u4e3b\u600e\u4f1a\u6709\u4e24\u4e2a\uff1f\u5c31\u662f\u5f00\u521b\u8ff7\u8e2a\u8c37\u7684\u80e1\u76df\u4e3b\u80e1\u67cf\u9f84\uff01\u201d \u3000\u3000\u201c\u4f60\u8bf4\u7684\u4ec0\u4e48\u68a6\u8bdd\uff1f\u80e1\u76df\u4e3b\u4e0d\u662f\u65e9\u5c31\u6b7b\u4e86\u5417\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u5fd9\u7ad9\u8d77\u8eab\u6765\u9053\uff1a\u201c\u4e8c\u59d1\u5a18\u8bf7\u628a\u7ecf\u8fc7\u544a\u8bc9\u4ee4\u59d0\uff0c\u6211\u5230\u6c34\u7262\u90a3\u8fb9\u53bb\u770b\u770b\uff01\u201d \u3000\u3000\u6c34\u7262\u53ea\u662f\u4e00\u4e2a\u5927\u6c34\u5751\uff0c\u56db\u5468\u7b51\u7740\u9ad8\u9ad8\u7684\u56f4\u5899\uff0e\u6c34\u7262\u4e0a\u65b9\u6709\u4e2a\u7b80\u964b\u7684\u9876\u68da\u3002 \u3000\u3000\u4f53\u79ef\u4e0d\u5927\u3002 \u3000\u3000\u901a\u5f80\u6c34\u7262\u6709\u9053\u94c1\u95e8\uff0c\u6b64\u65f6\u94c1\u95e8\u65e9\u5df2\u6253\u5f00\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u4e00\u51b2\u8fdb\u94c1\u95e8\uff0c\u4fbf\u662f\u6c34\u7262\u5cb8\u8fb9\u3002 \u3000\u3000\u56e0\u4e3a\u91cc\u9762\u70b9\u7740\u706f\uff0c\u53ef\u4ee5\u770b\u5f97\u5f88\u6e05\u695a\uff0c\u53ea\u89c1\u80e1\u67cf\u9f84\u7b49\u4eba\u5df2\u5c06\u6c34\u7262\u91cc\u7684\u516d\u4e2a\u4eba\u62d6\u4e0a\u5cb8\uff0c\u6b63\u5728 \u53ee\u53ee\u5f53\u5f53\u7684\u6572\u811a\u9563\u3002 \u3000\u3000\u80e1\u67cf\u9f84\u4e00\u89c1\u9ea6\u5c0f\u660e\u4fbf\u9053\uff1a\u201c\u4f60\u600e\u4e48\u6765\u4e86\uff1f\u82d7\u59d1\u5a18\u5462\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u5c0f\u5f1f\u548c\u82d7\u59d1\u5a18\u5df2\u628a\u82d7\u5927\u59d1\u5a18\u6551\u51fa\uff0c\u5979\u4eec\u6b63\u5728\u6811\u4e0b\u4f11\u606f\uff0c\u5e08\u5144\u5728\u8fd9\u91cc\u6572\u4ec0 \u4e48\uff1f\u201d \u3000\u3000\u80e1\u67cf\u9f84\u9053\uff1a\u201c\u4ed6\u4eec\u811a\u4e0a\u90fd\u6709\u811a\u9563\uff0c\u4e0d\u6572\u5f00\u5f85\u4f1a\u513f\u600e\u4e48\u884c\u52a8\uff1f\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u4e0d\u7528\u94a5\u5319\u5f00\uff1f\u201d \u3000\u3000\u201c\u90a3\u770b\u7262\u7684\u8eab\u4e0a\u6ca1\u5e26\u94a5\u5319\u3002\u201d \u3000\u3000\u201c\u6572\u5f00\u51e0\u4e2a\u6ca1\u6709\uff1f\u201d \u3000\u3000\u201c\u5f88\u56f0\u96be\uff0c\u82e5\u7528\u5251\u5288\uff0c\u5f53\u7136\u53ef\u4ee5\u5288\u5f00\uff0c\u4f46\u4ed6\u4eec\u53c8\u600e\u80fd\u53d7\u5f97\u4e86\uff1f\u201d \u3000\u3000\u738b\u5927\u5eb7\u9053\uff1a\u201c\u82e5\u5b9e\u5728\u5f04\u4e0d\u5f00\uff0c\u5c31\u53ea\u597d\u80cc\u7740\u8d70\u4e86\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u4e00\u58f0\u4e0d\u54cd\u7684\u6765\u5230\u90a3\u5df2\u88ab\u70b9\u5012\u7684\u5b88\u7262\u55bd\u5175\u8eab\u65c1\uff0e\u4ed4\u7ec6\u7684\u5f80\u4ed6\u8eab\u4e0a\u6478\u53bb\uff0c\u610f\u5916\u7684\u7adf\u7136 \u6478\u5230\u94a5\u5319\u3002 \u3000\u3000\u6709\u4e86\u94a5\u5319\uff0e\u4e0d\u5927\u4e00\u4f1a\u5de5\u592b\uff0c\u4fbf\u628a\u5d02\u5c71\u4e8c\u96c4\u3001\u6c5f\u5317\u4e09\u9f99\u548c\u6587\u5929\u751f\u7684\u811a\u9563\u6253\u5f00\u3002 \u3000\u3000\u4e8e\u662f\uff0c\u6709\u7684\u6400\u6276\u4e00\u4e2a\uff0c\u6709\u7684\u6400\u6276\u4e24\u4e2a\uff0e\u51fa\u4e86\u6c34\u7262\uff0c\u6765\u5230\u5927\u6811\u4e0b\u3002 \u3000\u3000\u8fd9\u65f6\u82d7\u7d20\u5170\u5df2\u77e5\u80e1\u67cf\u9f84\u662f\u771f\u6b63\u590d\u6d3b\uff0c\u4e5f\u77e5\u9053\u4e86\u6709\u516d\u4eba\u88ab\u63b3\u8fdb\u8ff7\u8e2a\u8c37\u5173\u5165\u6c34\u7262\uff0c\u4fbf\u6323\u624e\u7740 \u8d77\u8eab\u5148\u5411\u80e1\u67cf\u9f84\u89c1\u793c\u3002 \u3000\u3000\u8bf4\u8d77\u6765\u82d7\u7d20\u5170\u4e5f\u7b97\u80e1\u67cf\u9f84\u7684\u7ea2\u7c89\u77e5\u5df1\uff0c\u5f53\u5e74\u5979\u5bf9\u80e1\u67cf\u9f84\u4e00\u89c1\u503e\u5fc3\uff0c\u4e0d\u60dc\u80cc\u53db\u9634\u624b\u4e00\u9b54\uff0c \u76f8\u968f\u5230\u4e86\u8ff7\u8e2a\u8c37\u3002 \u3000\u3000\u4f46\u5f53\u5979\u89c1\u4e86\u8c37\u5bd2\u9999\uff0c\u53ea\u89c9\u5bf9\u65b9\u7684\u7edd\u4e16\u59ff\u5bb9\u548c\u624d\u534e\u80dc\u8fc7\u81ea\u5df1\u591a\u591a\uff0c\u53ea\u597d\u628a\u4e00\u8154\u7231\u5fc3\u53d8\u4e3a\u5bf9 \u80e1\u67cf\u9f84\u7684\u6548\u5fe0\u3002 \u3000\u3000\u80e1\u67cf\u9f84\u548c\u82d7\u7d20\u5170\u4e45\u522b\u91cd\u9022\uff0e\u4e5f\u96be\u5fcd\u5185\u5fc3\u7684\u6fc0\u52a8\uff0e\u5374\u53c8\u4e0d\u4fbf\u8fc7\u5206\u8868\u793a\uff0c\u53ea\u662f\u6325\u6325\u624b\u9053\uff1a \u201c\u5f85\u4f1a\u513f\u51fa\u4e86\u8c37\u5927\u5bb6\u518d\u597d\u597d\u8c08\uff01\u201d \u3000\u3000\u6b64\u65f6\u6b64\u5730\uff0c\u552f\u4e00\u7684\u4e3b\u8981\u5728\u4e8b\u4fbf\u662f\u5982\u4f55\u5b89\u7136\u8131\u8eab\uff0c\u8c01\u90fd\u65e0\u6687\u591a\u8c08\u3002 \u3000\u3000\u5d02\u5c71\u4e8c\u96c4\u3001\u6c5f\u5317\u4e09\u9f99\u53ca\u6587\u5929\u751f\u516d\u4eba\uff0c\u5728\u6c34\u7262\u91cc\u6ce1\u4e86\u4e00\u4e2a\u591a\u6708\uff0c\u817f\u90e8\u4ee5\u4e0b\u65e2\u80bf\u4e14\u80c0\uff0c\u800c\u4e14 \u8fd8\u8d77\u4e86\u6c34\u6ce1\u3002\u867d\u53ef\u52c9\u5f3a\u884c\u8d70\uff0c\u4f46\u5374\u53ea\u80fd\u8e52\u8dda\u4e3e\u6b65\u3002 \u3000\u3000\u5982\u679c\u4e00\u4eba\u80cc\u4e00\u4e2a\uff0c\u8fd8\u5269\u4e0b\u4e8c\u4e2a\u3002 \u3000\u3000\u80e1\u67cf\u9f84\u968f\u5373\u5429\u5490\u4ee5\u633d\u6276\u529b\u5f0f\u6162\u6162\u524d\u8fdb\uff0c\u6709\u7684\u633d\u6276\u4e00\u4e2a\uff0c\u6709\u7684\u633d\u6276\u4e24\u4e2a\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u5e08\u5144\uff0e\u54b1\u4eec\u5fc5\u987b\u7559\u4e0b\u4e00\u4e2a\u4eba\u6765\u65ad\u540e\uff0c\u5426\u5219\u5982\u679c\u9ad8\u624b\u8ffd\u88ad\u8fc7\u6765\uff0c\u5c31\u5f88\u5371\u9669 \u4e86\uff01\u201d \u3000\u3000\u80e1\u67cf\u9f84\u7565\u4e00\u6c89\u541f\u9053\uff1a\u201c\u4ee5\u73b0\u5728\u7684\u60c5\u5f62\uff0c\u6700\u591a\u53ea\u80fd\u62bd\u51fa\u2014\u4eba\u65ad\u540e\uff0c\u53c8\u6709\u591a\u5927\u7528\u5904\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u7ffb\u8155\u62d4\u51fa\u957f\u5251\u9053\uff1a\u201c\u90a3\u5c31\u7531\u5c0f\u5f1f\u7559\u4e0b\u597d\u4e86\uff01\u201d \u3000\u3000\u80e1\u67cf\u9f84\u9053\uff1a\u201c\u4f60\u53ef\u5343\u4e07\u522b\u6545\u610f\u95f9\u4e8b\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u5e08\u5144\u653e\u5fc3\uff01\u4eba\u4e0d\u60f9\u6211\uff0c\u6211\u4e0d\u60f9\u4eba\uff0c\u4eba\u4e0d\u6740\u627e\uff0c\u6211\u4e0d\u6740\u4eba\u3002\u603b\u4e4b\uff0c\u6211\u8fd9\u4e00\u4f1a \u8bbe\u6cd5\u7a33\u4f4f\u8ffd\u5175\uff0c\u8ba9\u4f60\u4eec\u5b89\u7136\u8131\u9669\u3002\u201d \u3000\u3000\u80e1\u67cf\u9f84\u4e0d\u518d\u8bf4\u4ec0\u4e48\uff0e\u5e26\u9886\u7740\u4f17\u4eba\u76f4\u5f80\u6765\u65f6\u540e\u8c37\u7684\u539f\u8def\u800c\u53bb\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u6545\u610f\u5c31\u5728\u5730\u7262\u524d\u7684\u8349\u576a\u4e0a\uff0c\u6765\u56de\u8e31\u7740\u65b9\u6b65\uff0c\u4ed6\u7684\u76ee\u7684\u6b63\u662f\u8981\u5f15\u8d77\u8ff7\u8e2a\u8c37\u7684\u4eba\u6ce8\u610f\uff0e \u5982\u6b64\u5bf9\u65b9\u5c31\u4e0d\u81f4\u518d\u8ffd\u8d76\u80e1\u67cf\u9f84\u7b49\u4eba\u3002 \u3000\u3000\u5927\u7ea6\u76cf\u8336\u5de5\u592b\u8fc7\u53bb\uff0e\u679c\u7136\u8fdc\u5904\u4f20\u6765\u5306\u4fc3\u800c\u53c8\u614c\u4e71\u7684\u811a\u6b65\u58f0\uff0c\u4f30\u8ba1\u81f3\u5c11\u6709\u4e8c\u4e09\u5341\u4eba\u4e4b\u591a\u3002 \u3000\u3000\u8fd9\u65f6\u4e0b\u5f26\u6708\u5df2\u7ecf\u5347\u8d77\uff0e\u4e0d\u5927\u2014\u4f1a\u513f\uff0c\u679c\u7136\u4e00\u4f19\u4eba\u6d69\u6d69\u8361\u8361\u6025\u5954\u800c\u6765\u3002 \u3000\u3000\u53ea\u542c\u6709\u4eba\u53eb\u9053\uff1a\u201c\u52ab\u7262\u7684\u5c31\u662f\u9ea6\u5c0f\u660e\uff0c\u8fd9\u5c0f\u5b50\u8fd8\u6ca1\u8d70\uff01\u201d \u3000\u3000\u8bf4\u8bdd\u95f4\u9ea6\u5c0f\u660e\u5df2\u88ab\u56e2\u56e2\u56f4\u4f4f\u3002 \u3000\u3000\u867d\u7136\u56f4\u4f4f\uff0c\u4f46\u8fd9\u4e9b\u4eba\u5374\u90fd\u4e0d\u6562\u8fc7\u5206\u903c\u8fd1\uff0c\u56e0\u4e3a\u4ed6\u4eec\u90fd\u77e5\u9053\u9ea6\u5c0f\u660e\u7684\u5389\u5bb3\uff0c\u8c01\u9760\u8fd1\u4e86\u8c01\u5c31 \u8981\u5012\u9709\u3002 \u3000\u3000\u6708\u5149\u4e0b\uff0c\u9ea6\u5c0f\u660e\u5df2\u770b\u6e05\u4e3a\u9996\u7684\u662f\u9634\u624b\u4e00\u9b54\u3002 \u3000\u3000\u7ad9\u5728\u9634\u624b\u4e00\u9b54\u8eab\u65c1\u7684\uff0c\u662f\u706b\u83bd\u548c\u725b\u5954\uff0e\u53e6\u4e00\u4e2a\u5219\u662f\u6c5f\u5357\u56db\u602a\u4e2d\u4ec5\u4f59\u7684\u4e00\u602a\u8001\u5927\u5c71\u9b48\u6bdb\u8d8a\u3002 \u3000\u3000\u4e0d\u96be\u6599\u51fa\uff0c\u970d\u5143\u4f3d\u548c\u5cad\u5357\u4e8c\u5947\u7b49\u4eba\uff0e\u5fc5\u5b9a\u5df2\u53bb\u4e86\u524d\u8c37\uff0c\u9634\u624b\u4e00\u9b54\u548c\u725b\u5954\u3001\u706b\u83bd\u56e0\u4e3a\u662f\u8ff7 \u8e2a\u8c37\u7684\u8d35\u5ba2\uff0c\u7559\u5728\u603b\u5be8\uff0c\u624d\u88ab\u6bdb\u8d8a\u8bf7\u4e86\u6765\u3002 \u3000\u3000\u867d\u7136\u5bf9\u65b9\u4eba\u591a\u52bf\u4f17\uff0c\u9ea6\u5c0f\u660e\u53cd\u800c\u7a0d\u7a0d\u653e\u4e0b\u4e86\u5fc3\u3002 \u3000\u3000\u56e0\u4e3a\u6bd2\u706b\u6210\u5168\u548c\u9ed1\u9b54\u65f6\u5bc5\u5e76\u6ca1\u6765\uff0e\u5728\u9ea6\u5c0f\u660e\u6765\u8bf4\uff0c\u8fd9\u4e8c\u4eba\u624d\u662f\u6700\u96be\u5bf9\u4ed8\u7684\u3002 \u3000\u3000\u53ea\u542c\u5c71\u9b48\u6bdb\u8d8a\u54ac\u7259\u5207\u9f7f\u7684\u53eb\u9053\uff1a\u201c\u9634\u624b\u8001\u524d\u8f88\uff0e\u5feb\u628a\u8fd9\u5c0f\u5b50\u64d2\u4f4f\uff0c\u665a\u8f88\u8981\u628a\u4ed6\u788e\u5c38\u4e07 \u65ad\uff01\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u8f6c\u4e86\u8f6c\u4e00\u5bf9\u767d\u591a\u9ed1\u5c11\u7684\u773c\u7403\u9053\uff1a\u201c\u4f60\u600e\u4e48\u8fd9\u6837\u6068\u4ed6\uff1f\u201d \u3000\u3000\u6bdb\u8d8a\u54ac\u4e86\u54ac\u7259\u9053\uff1a\u201c\u9634\u624b\u8001\u524d\u8f88\u6709\u6240\u4e0d\u77e5\uff0c\u6211\u4eec\u6c5f\u5357\u56db\u6770\uff0e\u8001\u4e8c\u8001\u4e09\u8001\u56db\u5168\u6b7b\u5728\u8fd9\u5c0f\u5b50 \u624b\u4e2d\u3002\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u70b9\u4e86\u70b9\u5934\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u786e\u5b9e\u5fc3\u72e0\u624b\u8fa3\uff0c\u8fde\u8001\u592b\u90fd\u5403\u8fc7\u4ed6\u7684\u4e8f\uff0c\u4f55\u51b5\u4f60\u4eec\u6c5f\u5357\u56db \u602a\u3002\u4e0d\u8fc7\u73b0\u5728\u4f60\u53ef\u4ee5\u653e\u5fc3\uff0c\u4ed6\u5c31\u662f\u957f\u4e86\u7fc5\u8180\uff0c\u8fd9\u6b21\u4e5f\u96be\u9003\u8001\u592b\u4e4b\u624b\u3002\u201d \u3000\u3000\u8bf4\u7740\uff0c\u659c\u7784\u4e86\u9ea6\u5c0f\u660e\u4e00\u773c\u9053\uff1a\u201c\u5c0f\u5b50\uff0c\u770b\u4f60\u8fd8\u80fd\u5f80\u54ea\u91cc\u8dd1\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u5df2\u6709\u51b3\u5b9a\uff0e\u5c31\u662f\u6682\u4e0d\u52a8\u624b\uff0e\u5c3d\u91cf\u62d6\u5ef6\u65f6\u95f4\uff0c\u4ee5\u4fbf\u80fd\u8ba9\u80e1\u67cf\u9f84\u7b49\u4eba\u8d70\u8fdc\u4e9b\u3002 \u3000\u3000\u60f3\u5230\u8fd9\u91cc\uff0c\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u9634\u624b\u8001\u6df7\u86cb\uff01\u4f60\u522b\u8bf4\u5f97\u8fd9\u4e48\u8f7b\u677e\uff0e\u82e5\u4e0d\u8d76\u7d27\u9000\u56de\u53bb\uff0c\u5403\u4e8f\u7684 \u662f\u4f60\u4eec\u3002\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u9634\u68ee\u68ee\u7b11\u9053\uff1a\u201c\u4f60\u597d\u50cf\u6709\u6043\u65e0\u6050\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u5728\u4e0b\u5f53\u7136\u4e0d\u53ea\u4e00\u4e2a\u4eba\u6765\uff0c\u5f85\u4f1a\u88ab\u5305\u56f4\u7684\u5c0f\u5fc3\u662f\u4f60\u4eec\u3002\u201d \u3000\u3000\u53ea\u542c\u4e00\u540d\u55bd\u5175\u9053\uff1a\u201c\u7980\u8001\u524d\u8f88\uff0c\u5148\u524d\u662f\u5c0f\u7684\u5de1\u591c\uff0c\u53d1\u73b0\u4ed6\u4eec\u597d\u50cf\u6765\u4e86\u516d\u4e03\u4e2a\uff01\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u4e00\u5411\u8be1\u8ba1\u591a\u7aef\uff0c\u4e5f\u6599\u5b9a\u9ea6\u5c0f\u660e\u2014\u4eba\u5b88\u5728\u8fd9\u91cc\u4e0d\u8d70\uff0c\u5176\u4e2d\u5b9a\u6709\u539f\u56e0\uff0c\u56e0\u4e4b\uff0c\u4e5f\u4e0d \u4fbf\u8d38\u7136\u91c7\u53d6\u884c\u52a8\u3002\u5f53\u4e0b\u54fc\u4e86\u58f0\u9053\uff1a\u201c\u5c0f\u5b50\uff0c\u4ed6\u4eec\u90fd\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u4e0d\u52a8\u58f0\u8272\u9053\uff1a\u201c\u4f60\u60f3\u60f3\u770b\uff0c\u5728\u4e0b\u662f\u6765\u505a\u4ec0\u4e48\u7684\uff1f\u201d \u3000\u3000\u6bdb\u8d8a\u53eb\u9053\uff1a\u201c\u9634\u624b\u8001\u524d\u8f88\uff0c\u4ed6\u4eec\u662f\u6765\u52ab\u7262\u7684\uff0c\u5176\u4ed6\u7684\u4eba\uff0c\u53ef\u80fd\u90fd\u5728\u5730\u7262\u548c\u6c34\u7262\u91cc\u3002\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u9053\uff1a\u201c\u4f60\u6d3e\u4eba\u5230\u4e24\u5904\u7262\u91cc\u53bb\u67e5\u770b\u4e00\u4e0b\u3002\u201d \u3000\u3000\u6bdb\u8d8a\u7acb\u5373\u6d3e\u51fa\u56db\u4eba\uff0c\u4e8c\u4eba\u67e5\u770b\u6c34\u7262\uff0c\u4e8c\u5165\u67e5\u770b\u5730\u7262\u3002 \u3000\u3000\u67e5\u770b\u6c34\u7262\u7684\u4e8c\u540d\u55bd\u5175\u5e94\u58f0\u800c\u53bb\uff0c\u8c01\u77e5\u67e5\u770b\u5730\u7262\u7684\u4e8c\u540d\u55bd\u5175\u5374\u8fdf\u8fdf\u4e0d\u6562\u884c\u52a8\u3002 \u3000\u3000\u6bdb\u8d8a\u559d\u9053\uff1a\u201c\u4f60\u4eec\u4e3a\u4ec0\u4e48\u8fd8\u4e0d\u8d70\uff1f\u201d \u3000\u3000\u4e00\u540d\u55bd\u5175\u9053\uff1a\u201c\u7980\u6bdb\u5934\u9886\uff0c\u5730\u7262\u90a3\u4e48\u6df1\uff0c\u91cc\u9762\u53c8\u90fd\u662f\u9ad8\u624b\uff0c\u6211\u4eec\u8fdb\u53bb\u4e4b\u540e\uff0c\u9664\u4e86\u9001\u6b7b\uff0c \u6839\u672c\u51fa\u4e0d\u6765\u3002\u201d \u3000\u3000\u67e5\u770b\u6c34\u7262\u7684\u4e8c\u540d\u55bd\u5175\uff0c\u5f88\u5feb\u4fbf\u8dd1\u56de\u6765\uff0c\u4e00\u4eba\u53eb\u9053\uff1a\u201c\u7980\u6bdb\u5934\u9886\uff0c\u6c34\u7262\u91cc\u7684\u4eba\u5df2\u7ecf\u88ab\u52ab\u8d70 \u4e86\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u7acb\u5373\u8f6c\u5934\u671b\u4e86\u671b\u5730\u7262\u6d1e\u95e8\u4e00\u773c\uff0c\u9ad8\u53eb\u9053\uff1a\u201c\u4f60\u4eec\u8fd8\u4e0d\u5feb\u51fa\u6765\uff0c\u6211\u5df2\u7ecf\u88ab\u4ed6\u4eec\u5305\u56f4 \u5566\uff01\u201d \u3000\u3000\u8fd9\u4e00\u6765\uff0c\u679c\u7136\u5413\u5f97\u5bf9\u65b9\u90fd\u4e0d\u6562\u8f7b\u4e3e\u5984\u52a8\uff0c\u4e0d\u5c11\u55bd\u5175\u90fd\u60c5\u4e0d\u81ea\u7981\u5411\u540e\u9000\uff0c\u751f\u6015\u592a\u9760\u8fd1\u4e86\u5fc5 \u5b9a\u906d\u6b83\u3002 \u3000\u3000\u4f46\u9634\u624b\u4e00\u9b54\u5374\u4e0d\u662f\u8fd9\u4e48\u5bb9\u6613\u88ab\u5413\u4f4f\u7684\uff0c\u4e5f\u5927\u53eb\u9053\uff1a\u201c\u4f60\u4eec\u90fd\u522b\u6015\uff01\u522b\u4e2d\u4e86\u8fd9\u5c0f\u5b50\u7684\u7f13\u5175 \u4e4b\u8ba1\u3002\u5730\u7262\u91cc\u7eb5\u7136\u8fd8\u6709\u4ed6\u4eec\u7684\u4eba\uff0c\u4e5f\u4e0d\u53ef\u80fd\u5f88\u591a\uff0c\u8bf4\u4e0d\u5b9a\u4ed6\u4eec\u5df2\u7ecf\u9003\u8d70\uff0c\u53ea\u7559\u8fd9\u5c0f\u5b50\u8d1f\u8d23\u65ad \u540e\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u7b11\u9053\uff1a\u201c\u9634\u624b\u8001\u6df7\u86cb\uff0c\u8c22\u8c22\u4f60\u5566\uff01\u56e0\u4e3a\u4f60\u5f88\u770b\u5f97\u8d77\u5728\u4e0b\u3002\u201d \u3000\u3000\u201c\u4f60\u600e\u4e48\u8001\u9a82\u8001\u592b\uff1f\u201d \u3000\u3000\u201c\u4f60\u672c\u6765\u5c31\u662f\u4e2a\u8001\u6df7\u86cb\uff0c\u5728\u4e0b\u54ea\u9a82\u8fc7\u4f60\uff1f\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u54ac\u4e86\u54ac\u7259\uff0c\u8f6c\u5934\u9053\uff1a\u201c\u6bdb\u8001\u5f1f\uff0c\u65e2\u7136\u8fd9\u5c0f\u5b50\u6740\u4e86\u4f60\u7684\u4e09\u4f4d\u5f1f\u5144\uff0c\u73b0\u5728\u6b63\u662f\u4f60\u62a5 \u4ec7\u7684\u673a\u4f1a\u5230\u4e86\uff0c\u4e3a\u4ec0\u4e48\u8fd8\u4e0d\u52a8\u624b\uff1f\u201d \u3000\u3000\u6bdb\u8d8a\u4e0d\u89c9\u5934\u76ae\u4e00\u9ebb\uff0c\u7ed3\u7ed3\u5df4\u5df4\u7684\u9053\uff1a\u201c\u8001\u524d\u8f88\u522b\u5f00\u73a9\u7b11\uff0c\u665a\u8f88\u82e5\u80fd\u6740\u5f97\u4e86\u4ed6\uff0c\u65e9\u5c31\u62a5\u4ec7 \u4e86\uff01\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u9053\uff1a\u201c\u4f60\u6740\u4e0d\u4e86\u4ed6\u624d\u662f\u602a\u4e8b\uff01\u201d \u3000\u3000\u6bdb\u8d8a\u5446\u4e86\u5446\u9053\uff1a\u201c\u8001\u524d\u8f88\u8fd9\u8bdd\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u9053\uff1a\u201c\u8bba\u5e74\u7eaa\u4f60\u6bd4\u4ed6\u5927\uff0e\u54ea\u6709\u5927\u4eba\u6015\u5c0f\u5b69\u7684\uff1f\u8bba\u540d\u6c14\u4f60\u662f\u6c5f\u5357\u56db\u6770\u7684\u8001\u5927\uff0c\u800c \u4ed6\u5374\u53ea\u662f\u4e2a\u5c0f\u5175\u3002\u4f60\u6740\u4e0d\u4e86\u4ed6\uff0c\u5c82\u4e0d\u6210\u4e86\u7b11\u8bdd\uff1f\u201d \u3000\u3000\u8fd9\u51e0\u53e5\u8bdd\uff0c\u53ea\u542c\u5f97\u6bdb\u8d8a\u7b80\u76f4\u6709\u4e9b\u557c\u7b11\u7686\u975e\uff0c\u987f\u4e86\u987f\u9053\uff1a\u201c\u8001\u524d\u8f88\u6bd4\u665a\u8f88\u7684\u5e74\u7eaa\u66f4\u5927\uff0c\u8bba \u540d\u6c14\u4e5f\u6bd4\u665a\u8f88\u5927\u5f97\u591a\uff0c\u8fd8\u662f\u4f60\u8001\u4eba\u5bb6\u628a\u4ed6\u62ff\u4e0b\u624d\u662f\uff01\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u51b7\u7b11\u9053\uff1a\u201c\u8001\u592b\u662f\u4f60\u4eec\u8ff7\u8e2a\u8c37\u7684\u5ba2\u4eba\uff0c\u54ea\u6709\u4e3b\u4eba\u4e0d\u52a8\u624b\uff0e\u5374\u8ba9\u5ba2\u4eba\u52a8\u624b\u7684\u9053\u7406\uff1f \u518d\u8bf4\u4ed6\u8ddf\u8001\u592b\u867d\u7136\u6709\u4ec7\uff0c\u5374\u6ca1\u8ddf\u4f60\u7684\u4ec7\u6df1\uff0c\u5bf9\u5417\uff1f\u201d \u3000\u3000\u539f\u6765\u9634\u624b\u4e00\u9b54\u548c\u9ea6\u5c0f\u660e\u5728\u4ee5\u5f80\u7684\u4e09\u5ea6\u4ea4\u624b\u4e2d\uff0c\u53ea\u89c9\u5bf9\u65b9\u7684\u8eab\u624b\u4e00\u6b21\u6bd4\u4e00\u6b21\u9ad8\uff0c\u6700\u540e\u2014\u6b21\uff0c \u51e0\u4e4e\u5df2\u80dc\u8fc7\u81ea\u5df1\u3002\u56e0\u4e4b\uff0c\u5728\u4f17\u76ee\u777d\u777d\u4e0b\uff0c\u4ed6\u4e5f\u4e0d\u613f\u4eb2\u81ea\u4e0a\u9635\uff0c\u4e07\u4e00\u683d\u4e86\u8ddf\u5934\uff0e\u5c82\u4e0d\u58f0\u540d\u626b\u5730\u3002 \u3000\u3000\u800c\u9ea6\u5c0f\u660e\u5374\u6b63\u4e2d\u4e0b\u6000\uff0c\u4ed6\u7684\u76ee\u7684\uff0c\u5373\u5728\u62d6\u5ef6\u65f6\u95f4\uff0c\u6362\u53d6\u7a7a\u95f4\uff0e\u9634\u624b\u4e00\u9b54\u8fdf\u4e0d\u52a8\u624b\uff0e\u4ed6\u6b63 \u6c42\u4e4b\u4e0d\u5f97\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u8fdc\u5904\u4e00\u6761\u4eba\u5f71\u6025\u63a0\u800c\u6765\uff0c\u8d6b\u7136\u662f\u641c\u9b42\u624b\u5df4\u5929\u4e49\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u987f\u611f\u5fc3\u5934\u2014\u9707\uff0c\u4ee5\u76ee\u524d\u7684\u60c5\u52bf\uff0c\u82e5\u52a8\u8d77\u624b\u6765\uff0e\u6beb\u65e0\u7591\u95ee\u5fc5\u5b9a\u5403\u4e8f\uff0c\u6b64\u523b\u5bf9\u65b9\u53c8\u591a \u4e86\u5df4\u5929\u4e49\uff0c\u770b\u6765\u5b9e\u5728\u4e0d\u5999\u3002 \u3000\u3000\u4f46\u4ed6\u4ecd\u4e0d\u5f97\u4e0d\u6781\u529b\u4fdd\u6301\u9547\u5b9a\uff0c\u4ee5\u8fbe\u62d6\u5ef6\u65f6\u95f4\u7684\u76ee\u7684\u3002 \u3000\u3000\u5df4\u5929\u4e49\u655d\u4e86\u9ea6\u5c0f\u660e\u4e00\u773c\u9053\uff1a\u201c\u662f\u5426\u771f\u662f\u88ab\u52ab\u7262\u4e86\uff1f\u201d \u3000\u3000\u6bdb\u8d8a\u9053\uff1a\u201c\u6c34\u7262\u5df2\u7ecf\u88ab\u52ab\u4e86\uff0e\u73b0\u5728\u5730\u7262\u91cc\u53ef\u80fd\u8fd8\u6709\u4ed6\u4eec\u7684\u4eba\u3002\u201d \u3000\u3000\u5df4\u5929\u4e49\u5fd9\u9053\uff1a\u201c\u6bd2\u706b\u6210\u5168\u8001\u82f1\u96c4\u6765\u4e86\u6ca1\u6709\uff1f\u201d \u3000\u3000\u6bdb\u8d8a\u9053\uff1a\u201c\u6210\u8001\u82f1\u96c4\u6ca1\u6765\uff0c\u5df4\u524d\u8f88\u95ee\u4ed6\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5df4\u5929\u4e49\u9053\uff1a\u201c\u53ea\u8981\u6210\u8001\u82f1\u96c4\u53d1\u51fa\u706b\u5668\uff0c\u8db3\u53ef\u628a\u5730\u7262\u5927\u95e8\u5c01\u4f4f\uff0e\u4e0d\u8bba\u5730\u7262\u91cc\u6709\u4ed6\u4eec\u591a\u5c11\u4eba\uff0c \u4e00\u4e2a\u4e5f\u8dd1\u4e0d\u51fa\u6765\u3002\u201d \u3000\u3000\u6bdb\u8d8a\u7acb\u5373\u5429\u5490\u4e00\u540d\u55bd\u5175\u5230\u603b\u5be8\u8bf7\u6bd2\u706b\u6210\u5168\u3002 \u3000\u3000\u9634\u624b\u4e00\u9b54\u9053\uff1a\u201c\u5df4\u8001\u5f1f\uff0e\u524d\u8c37\u7684\u706b\u7184\u4e86\u6ca1\u6709\uff1f\u201d \u3000\u3000\u5df4\u5929\u4e49\u9053\uff1a\u201c\u5df4\u67d0\u6765\u65f6\u8fd8\u6ca1\u7184\u3002\u201d \u3000\u3000\u201c\u970d\u76df\u4e3b\u5462\uff1f\u201d \u3000\u3000\u201c\u76df\u4e3b\u4ecd\u5728\u524d\u8c37\u6307\u6325\u5f1f\u5144\u6551\u706b\u3002\u201d \u3000\u3000\u201c\u4f60\u662f\u600e\u4e48\u6765\u7684\uff1f\u201d \u3000\u3000\u201c\u6709\u4eba\u5230\u524d\u8c37\u5411\u76df\u4e3b\u7980\u62a5\uff0c\u8bf4\u662f\u53ef\u80fd\u6709\u4eba\u52ab\u7262\uff0e\u76df\u4e3b\u5c31\u4ea4\u4ee3\u5df4\u67d0\u901f\u6765\u540e\u8c37\u67e5\u770b\uff0c\u5927\u5bb6\u4e3a \u4ec0\u4e48\u8fd8\u4e0d\u52a8\u624b\uff1f\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u9053\uff1a\u201c\u7b49\u5f04\u6e05\u4e86\u5730\u7262\u91cc\u7a76\u7adf\u6709\u6ca1\u6709\u4ed6\u4eec\u7684\u4eba\u518d\u52a8\u624b\u4e5f\u4e0d\u8fdf\uff0e\u53cd\u6b63\u8fd9\u5c0f\u5b50\u5df2\u6210\u7fc1 \u4e2d\u4e4b\u9cd6\u3002\u201d \u3000\u3000\u5df4\u5929\u4e49\u9053\uff1a\u201c\u5730\u7262\u91cc\u6839\u672c\u4e0d\u53ef\u80fd\u6709\u4ed6\u4eec\u7684\u4eba\uff0c\u8fd9\u5c0f\u5b50\u5206\u660e\u662f\u7559\u5728\u8fd9\u91cc\u5ef6\u8fdf\u54b1\u4eec\u7684\u8ffd\u7f09\u884c \u52a8\uff0c\u82e5\u4e0d\u65e9\u4e9b\u628a\u4ed6\u62ff\u4e0b\uff0c\u5c82\u4e0d\u662f\u4e2d\u4e86\u4ed6\u4eec\u7684\u8ba1\uff1f\u201d \u3000\u3000\u8bf4\u7740\uff0c\u62a1\u8d77\u624b\u4e2d\u7684\u4e5d\u8282\u641c\u9b42\u97ad\uff0e\u76f4\u5411\u9ea6\u5c0f\u660e\u903c\u53bb\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u51b7\u7b11\u9053\uff1a\u201c\u5df4\u5929\u4e49\uff0c\u4f60\u4e0d\u8fc7\u662f\u5728\u4e0b\u7684\u624b\u4e0b\u8d25\u5c06\uff0c\u8fd9\u6b21\u662f\u5426\u8981\u81ea\u52a8\u9001\u6b7b\uff1f\u201d \u3000\u3000\u5df4\u5929\u4e49\u867d\u660e\u77e5\u4e0d\u662f\u9ea6\u5c0f\u660e\u5bf9\u624b\uff0c\u56e0\u4f9d\u4ed7\u4eba\u591a\uff0c\u53c8\u6709\u9634\u624b\u4e00\u9b54\u5728\u65c1\uff0c\u4ed6\u76f8\u4fe1\u81ea\u5df1\u82e5\u8d25\u4e0b\u9635 \u6765\uff0e\u9634\u624b\u4e00\u9b54\u5fc5\u5b9a\u51fa\u624b\u76f8\u52a9\uff0e\u6545\u800c\u624d\u80c6\u6562\u51fa\u9635\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u89c1\u5df4\u5929\u4e49\u4e0d\u7b54\u8bdd\uff0c\u518d\u9053\uff1a\u201c\u8fde\u9634\u624b\u2014\u9b54\u90a3\u8001\u738b\u516b\u86cb\u90fd\u4e0d\u6562\u8ddf\u5728\u4e0b\u52a8\u624b\uff0c\u4f60\u7684\u72d7\u80c6 \u4e5f\u672a\u514d\u592a\u5927\u4e86\u3002\u201d \u3000\u3000\u4ed6\u7684\u539f\u610f\uff0e\u65e8\u5728\u62d6\u5ef6\u65f6\u95f4\uff0c\u5c82\u77e5\u8fd9\u51e0\u53e5\u8bdd\uff0c\u53cd\u800c\u5f04\u5de7\u6210\u62d9\uff0c\u6fc0\u5f97\u9634\u624b\u4e00\u9b54\u975e\u52a8\u624b\u4e0d\u53ef\u4e86\u3002 \u3000\u3000\u9634\u624b\u4e00\u9b54\u4e00\u6296\u624b\u4e2d\u62c2\u5c18\uff0c\u7acb\u5373\u8ddf\u4e0a\u6765\u9053\uff1a\u201c\u5df4\u8001\u5f1f\uff0c\u4e3a\u4e86\u4e89\u53d6\u65f6\u95f4\uff0e\u901f\u6218\u901f\u51b3\uff0c\u54b1\u4eec\u5c31 \u8054\u624b\u6765\u5bf9\u4ed8\u4ed6\uff01\u201d \u3000\u3000\u4e00\u8bed\u672a\u4e86\uff0c\u3000\u4e8c\u4eba\u679c\u7136\u4ee5\u8fc5\u96f7\u4e0d\u53ca\u63a9\u8033\u4e4b\u52bf\uff0e\u9f50\u9f50\u5411\u9ea6\u5c0f\u660e\u653b\u4e0a\u6765\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u5168\u662f\u9876\u5c16\u9ad8\u624b\uff0c\u8fde\u624b\u51fa\u51fb\uff0c\u5a01\u529b\u975e\u540c\u5c0f\u53ef\u3002\u9ea6\u5c0f\u660e\u8eab\u624b\u867d\u9ad8\uff0c\u5728\u4ea4\u624b\u5341\u4f59\u62db\u540e\uff0c \u4e5f\u6e10\u6e10\u4e0d\u654c\u3002 \u3000\u3000\u4ed6\u4e3a\u4e86\u518d\u62d6\u5ef6\u4e00\u4e9b\u65f6\u95f4\uff0c\u53ea\u597d\u65cb\u5c55\u51fa\u6458\u661f\u6b65\u5c55\u5f00\u6e38\u6597\u3002 \u3000\u3000\u53c8\u652f\u6301\u4e86\u56db\u4e94\u5341\u56de\u5408\uff0e\u5b9e\u5728\u62b5\u6321\u4e0d\u4f4f\uff0c\u7ec8\u4e8e\u88ab\u8feb\u4e14\u6218\u4e14\u9000\u3002 \u3000\u3000\u5728\u4ed6\u4e88\u6599\uff0c\u6b64\u523b\u80e1\u67cf\u9f84\u7b49\u4eba\u81f3\u5c11\u5df2\u8be5\u8d70\u51fa\u4e8c\u4e09\u91cc\u8def\uff0c\u81ea\u5df1\u5c3d\u53ef\u65bd\u5c55\u8f7b\u529f\u5954\u884c\uff0c\u76f8\u4fe1\u4e0d\u51fa \u534a\u91cc\u8def\uff0c\u5fc5\u53ef\u5c06\u5bf9\u65b9\u629b\u4e0b\u3002 \u3000\u3000\u4e8e\u662f\uff0c\u4ed6\u5df2\u4e0d\u518d\u604b\u6218\uff0c\u817e\u8eab\u76f4\u5411\u8c37\u5916\u98de\u63a0\u3002 \u3000\u3000\u5df4\u5929\u4e49\u4e14\u8ffd\u4e14\u53eb\u9053\uff1a\u201c\u9634\u624b\u8001\u82f1\u96c4\uff0c\u7559\u7740\u8fd9\u5c0f\u5b50\u662f\u4e00\u5927\u5bb3\uff0e\u7edd\u5bf9\u4e0d\u80fd\u8ba9\u4ed6\u8dd1\u6389\uff01\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u9053\uff1a\u201c\u8001\u592b\u81ea\u4ece\u6765\u5230\u8ff7\u8e2a\u8c37\uff0e\u8499\u970d\u76df\u4e3b\u70ed\u60c5\u6b3e\u5f85\uff0e\u5f53\u7136\u4f1a\u5e2e\u4f60\u4eec\u7acb\u4e0b\u8fd9\u4e00\u529f\uff0c \u5feb\u8ffd\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u4e00\u53e3\u6c14\u5954\u51fa\u540e\u8c37\uff0c\u53c8\u5954\u51fa\u534a\u91cc\u5de6\u53f3\uff0e\u4fbf\u95ea\u8eab\u5230\u8def\u65c1\u4e00\u68f5\u5927\u6811\u540e\uff0c\u63a5\u7740\u518d\u8dc3\u4e0a\u6811\u5e72\uff0c \u4e00\u9762\u4f11\u606f\uff0c\u4e00\u9762\u89c2\u5bdf\u8eab\u540e\u8ffd\u5175\u3002 \u3000\u3000\u5c82\u77e5\u9634\u624b\u4e00\u9b54\u548c\u5df4\u5929\u4e49\u7684\u8f7b\u529f\u4e5f\u9ad8\u5f97\u51fa\u5947\uff0c\u5f53\u9ea6\u5c0f\u660e\u521a\u8dc3\u4e0a\u6811\u679d\uff0c\u4e8c\u4eba\u5df2\u8ffd\u4e86\u4e0a\u6765\u3002 \u3000\u3000\u4e0d\u8fc7\u5176\u4f59\u7684\u4eba\u5374\u5e76\u4e0d\u8ddf\u4e0a\u3002 \u3000\u3000\u9634\u624b\u4e00\u9b54\u548c\u5df4\u5929\u4e49\u663e\u7136\u6ca1\u53d1\u89c9\u9ea6\u5c0f\u660e\u5df2\u5728\u6811\u4e0a\uff0c\u7ee7\u7eed\u5411\u524d\u8ffd\u53bb\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u62c5\u5fc3\u88ab\u4ed6\u4eec\u8ffd\u5230\u524d\u9762\u7684\u4eba\uff0c\u7acb\u523b\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4e2a\u738b\u516b\u86cb\u8fd8\u5f80\u54ea\u91cc\u8d70\uff1f\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u548c\u5df4\u5929\u4e49\u95fb\u58f0\u6b62\u6b65\u56de\u8eab\uff0c\u5374\u770b\u4e0d\u5230\u9ea6\u5c0f\u660e\u4eba\u5728\u54ea\u91cc\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4f60\u4eec\u5c0f\u7956\u5b97\u5728\u6811\u4e0a\uff0c\u6709\u80c6\u91cf\u53ea\u7ba1\u8fc7\u6765\uff01\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u548c\u5df4\u5929\u4e49\u98de\u5feb\u7684\u6765\u5230\u6811\u4e0b\u3002 \u3000\u3000\u5df4\u5929\u4e49\u9053\uff1a\u201c\u8001\u82f1\u96c4\uff0e\u54b1\u4eec\u4e0a\u53bb\u6349\u4ed6\uff01\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u9053\uff1a\u201c\u4f60\u5148\u4e0a\u53bb\u8bd5\u8bd5\uff01\u201d \u3000\u3000\u5df4\u5929\u4e49\u867d\u6709\u4e9b\u80c6\u602f\uff0e\u4f46\u56e0\u6709\u9634\u624b\u4e00\u9b54\u5728\u6811\u4e0b\u63a5\u5e94\uff0c\u4e5f\u5c31\u4e0d\u518d\u5728\u4e4e\uff0c\u80a9\u5934\u4e00\u6643\uff0c\u817e\u8eab\u8dc3\u8d77\uff0e \u5f85\u8ddd\u79bb\u9ea6\u5c0f\u660e\u4e94\u516d\u5c3a\u65f6\uff0c\u731b\u7136\u53d1\u51fa\u4e00\u638c\uff0e\u9065\u7a7a\u5288\u53bb\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u65e9\u6599\u5230\u5bf9\u65b9\u5fc5\u6709\u6b64\u4e00\u7740\u3001\u8fce\u7740\u638c\u98ce\u4e5f\u5288\u51fa\u4e00\u638c\u3002 \u3000\u3000\u4e24\u9053\u6709\u5982\u72c2\u98d9\u822c\u7684\u638c\u98ce\u4e00\u63a5\uff0c\u7206\u8d77\u4e00\u58f0\u8f70\u7136\u5927\u9707\uff0c\u5df4\u5929\u4e49\u867d\u7136\u5185\u529b\u4e0d\u6bd4\u9ea6\u5c0f\u660e\u5dee\uff0c\u4f46\u56e0 \u7cfb\u51cc\u7a7a\u53d1\u638c\uff0c\u529b\u9053\u96be\u4ee5\u7528\u5b9e\uff0c\u5f53\u573a\u88ab\u9707\u5f97\u5931\u53bb\u63a7\u5236\uff0e\u6454\u4e0b\u5730\u53bb\u3002 \u3000\u3000\u5728\u6b64\u540c\u65f6\uff0c\u6811\u4e0a\u4e0d\u5c11\u65ad\u679d\u788e\u53f6\u4e5f\u7eb7\u7eb7\u843d\u5730\uff0c\u9ea6\u5c0f\u660e\u66f4\u662f\u6643\u4e86\u51e0\u6643\uff0c\u624d\u52c9\u5f3a\u7a33\u4f4f\u3002 \u3000\u3000\u5df4\u5929\u4e49\u88ab\u6454\u5f97\u9aa8\u75db\u7b4b\u9ebb\uff0c\u4e00\u9635\u9f87\u7259\u54a7\u5634\u9053\uff1a\u201c\u8be5\u8001\u82f1\u96c4\u4e0a\u6811\u4e86\uff01\u201d \u3000\u3000\u4ee5\u9634\u624b\u4e00\u9b54\u7684\u8001\u5978\u5de8\u733e\uff0c\u600e\u80af\u5403\u4e8f\u4e0a\u5f53\uff0c\u5f53\u4e0b\uff0c\u9ed8\u9ed8\u8fd0\u96c6\u529f\u529b\uff0c\u7136\u540e\u4ef0\u9762\u2014\u8bb0\u201c\u9634\u98ce\u638c\u201d \u5288\u5411\u9ea6\u5c0f\u660e\u505c\u8eab\u4e4b\u5904\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u968f\u5373\u4e5f\u4ee5\u4e00\u7f15\u6307\u98ce\u9065\u7a7a\u5411\u4e0b\u6233\u53bb\u3002 \u3000\u3000\u987f\u65f6\uff0e\u9ea6\u5c0f\u660e\u53ea\u89c9\u5168\u8eab\u782d\u9aa8\u751f\u5bd2\uff0e\u8fde\u6253\u4e24\u4e2a\u51b7\u98a4\uff0c\u9669\u4e9b\u6454\u4e0b\u5730\u53bb\u3002 \u3000\u3000\u9634\u624b\u4e00\u9b54\u4e5f\u88ab\u90a3\u8db3\u53ef\u7a7f\u91d1\u900f\u77f3\u7684\u9510\u5229\u6307\u98ce\u6233\u5230\u638c\u5fc3\uff0c\u75db\u5f97\u4ed6\u5dee\u4e00\u70b9\u53eb\u58f0\u6765\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u53eb\u9053\uff1a\u201c\u4f60\u4eec\u4e3a\u4ec0\u4e48\u4e0d\u4e0a\u6765\u4e86\uff1f\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u54fc\u4e86\u58f0\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u5b50\u4e0b\u6765\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4f60\u5c0f\u7956\u5b97\u5750\u5728\u4e0a\u9762\u8212\u8212\u670d\u670d\uff0c\u4f55\u5fc5\u4e0b\u53bb\u3002\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u521a\u8981\u518d\u91c7\u6536\u884c\u52a8\uff0c\u5ffd\u89c1\u4e24\u6761\u4eba\u5f71\u6709\u5982\u6025\u77e2\u822c\u75be\u63a0\u800c\u6765\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u5f88\u5feb\u4fbf\u8fa9\u8ba4\u51fa\u8fd9\u4e8c\u4eba\u7adf\u7136\u662f\u949f\u4e00\u8c6a\u548c\u4f59\u4ea6\u4e50\uff0e\u5927\u559c\u4e4b\u4e0b\uff0c\u8fde\u5fd9\u53eb\u9053\uff1a\u201c\u4f60\u4eec\u5feb\u5230 \u8fd9\u91cc\u6765\uff0c\u6811\u4e0b\u7684\u4e24\u4e2a\u738b\u516b\u86cb\uff0c\u4e00\u4e2a\u662f\u9634\u624b\u4e00\u9b54\uff0c\u4e00\u4e2a\u662f\u5df4\u5929\u4e49\uff01\u201d \u3000\u3000\u539f\u6765\u949f\u4e00\u8c6a\u548c\u4f59\u4ea6\u4e50\u5728\u653e\u8fc7\u706b\u540e\uff0e\u4fbf\u7ed5\u5230\u540e\u8c37\u6765\u4e3a\u80e1\u67cf\u9f84\u7b49\u4eba\u52a9\u9635\uff0e\u4ed6\u4eec\u5728\u524d\u9762\u4e0d\u8fdc\u9047 \u5230\u80e1\u67cf\u9f84\u7b49\u4eba\uff0e\u80e1\u67cf\u9f84\u62c5\u5fc3\u9ea6\u5c0f\u660e\u6709\u5931\uff0c\u4fbf\u8981\u4ed6\u4eec\u8d76\u5f80\u540e\u8c37\u589e\u63f4\u3002 \u3000\u3000\u4e8c\u4eba\u95fb\u542c\u9ea6\u5c0f\u660e\u5728\u6811\u4e0a\u7684\u62db\u547c\uff0e\u8fc5\u5feb\u65e0\u6bd4\u7684\u4fbf\u63a0\u5230\u6811\u4e0b\u628a\u9634\u624b\u4e00\u9b54\u524d\u540e\u5939\u4f4f\u3002 \u3000\u3000\u5df4\u5929\u4e49\u4e00\u89c1\u4e0d\u5999\uff0e\u6492\u817f\u4fbf\u843d\u8352\u800c\u9003\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u548c\u4f59\u4ea6\u4e50\u66f4\u4e0d\u7b54\u8bdd\uff0c\u5404\u4ed7\u5175\u5203\uff0c\u5411\u9634\u624b\u4e00\u9b54\u5c55\u5f00\u524d\u540e\u5939\u51fb\uff0e\u8feb\u5f97\u9634\u624b\u4e00\u9b54\u6839\u672c\u65e0 \u6cd5\u8131\u8eab\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u89c1\u673a\u4e0d\u53ef\u5931\uff0e\u4fef\u8eab\u7531\u6811\u4e0a\u76f4\u5411\u9634\u624b\u4e00\u9b54\u51b2\u53bb\uff0c\u63a5\u7740\u4ee5\u8fc5\u96f7\u4e0d\u53ca\u63a9\u8033\u4e4b\u52bf\uff0c\u3000\u4e00\u6307 \u70b9\u4e0b\u3002 \u3000\u3000\u9634\u624b\u4e00\u9b54\u53ea\u987e\u62db\u67b6\u949f\u4e00\u8c6a\u548c\u4f59\u4ea6\u4e50\uff0c\u54ea\u91cc\u6765\u5f97\u53ca\u5206\u795e\u5e94\u4ed8\u7b2c\u4e09\u8005\u7684\u7a81\u88ad\uff0c\u4fbf\u88ab\u4e00\u6307\u70b9\u4e0a \u4e86\u524d\u80f8\u201c\u7487\u7391\u7a74\u201d\uff0c\u5f53\u573a\u5012\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u949f\u2014\u8c6a\u95ee\u9053\uff1a\u201c\u4f60\u600e\u4e48\u5230\u6811\u4e0a\u53bb\u4e86\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u6211\u62c5\u5fc3\u88ab\u4ed6\u4eec\u8ffd\u4e0a\u80e1\u5e08\u5144\u4ed6\u4eec\u90a3\u4e00\u4f19\u4eba\uff0c\u4e8e\u662f\u628a\u4ed6\u4eec\u5f15\u5230\u8fd9\u91cc\uff0c\u4e3a\u7684\u662f\u8f6c \u79fb\u65b9\u5411\u3002\u201d \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u4f60\u5c31\u8be5\u8ba9\u4ed6\u4eec\u8ffd\u4e0a\uff0c\u6709\u80e1\u76df\u4e3b\u5728\uff0c\u8fd8\u6015\u6536\u62fe\u4e0d\u4e86\u4ed6\u4eec\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u8bdd\u4e0d\u80fd\u8fd9\u6837\u8bb2\uff0e\u6211\u662f\u62c5\u5fc3\u80e1\u5e08\u5144\u7b49\u4eba\u66b4\u9732\u4e86\u884c\u8ff9\uff0c\u5bf9\u54b1\u4eec\u5927\u5927\u4e0d\u5229\u3002\u201d \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u4e5f\u6709\u9053\u7406\u3002\u4f60\u51c6\u5907\u628a\u9634\u624b\u2014\u9b54\u600e\u6837\u5904\u7f6e\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u8981\u628a\u4ed6\u62bc\u56de\u767d\u59d1\u5bfa\u53bb\u3002\u201d \u3000\u3000\u201c\u90a3\u591a\u9ebb\u70e6\uff0e\u4e0d\u5982\u73b0\u5728\u5c31\u7ed3\u679c\u4e86\u4ed6\u3002\u201d \u3000\u3000\u201c\u4f60\u54ea\u91cc\u77e5\u9053\uff0c\u54b1\u4eec\u5fc5\u987b\u7559\u4e0b\u4ed6\u7684\u72d7\u547d\uff0c\u5c06\u6765\u624d\u6709\u529e\u6cd5\u5bf9\u4ed8\u970d\u5143\u4f3d\u3002\u201d \u3000\u3000\u201c\u8fd9\u8bdd\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u6709\u4e86\u4ed6\uff0c\u624d\u53ef\u5f97\u5230\u2018\u5411\u5fc3\u9732\u2019\u7684\u89e3\u836f\u3002\u8fd9\u6b21\u5c0f\u5f1f\u968f\u80e1\u5e08\u5144\u7531\u5929\u53f0\u5c71\u56de\u6765\uff0c\u66fe\u7279\u5730 \u5230\u8fc7\u5415\u6881\u5c71\u5411\u4ed6\u8ba8\u53d6\u89e3\u836f\u3002\u53ef\u60dc\u6ca1\u8ba8\u5230\uff0c\u73b0\u5728\u597d\u4e0d\u5bb9\u6613\u6293\u5230\u4ed6\uff0e\u6b63\u662f\u5f97\u5230\u89e3\u836f\u7684\u673a\u4f1a\u5230\u4e86\u3002\u201d \u3000\u3000\u201c\u539f\u6765\u5982\u6b64\uff0c\u90a3\u5f53\u7136\u8981\u628a\u4ed6\u5e26\u56de\u53bb\u3002\u201d \u3000\u3000\u949f\u4e00\u8c6a\u8bf4\u5b8c\u8bdd\uff0c\u81ea\u52a8\u80cc\u8d77\u9634\u624b\u4e00\u9b54\u9053\uff1a\u201c\u54b1\u4eec\u8d70\u5427\uff01\u82e5\u5feb\u4e9b\u8d76\uff0e\u4e0d\u51fa\u987f\u996d\u5de5\u592b\uff0c\u4fbf\u53ef\u8d76 \u4e0a\u80e1\u76df\u4e3b\u4ed6\u4eec\u3002\u201d \u3000\u3000\u679c\u7136\uff0c\u987f\u996d\u5de5\u592b\u4e4b\u540e\uff0c\u4ed6\u4eec\u5df2\u8d76\u4e0a\u4e86\u80e1\u67cf\u9f84\u4e00\u4f19\u4eba\u3002 \u3000\u3000\u80e1\u67cf\u9f84\u4e00\u89c1\u949f\u4e00\u8c6a\u80cc\u7740\u4e00\u4e2a\u4eba\uff0c\u832b\u7136\u95ee\u9053\uff1a\u201c\u8fd9\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u4ee3\u7b54\u9053\uff1a\u201c\u662f\u9634\u624b\u4e00\u9b54\u90a3\u8001\u6df7\u86cb\uff01\u201d \u3000\u3000\u80e1\u67cf\u9f84\u5927\u559c\u9053\uff1a\u201c\u6709\u4e86\u4ed6\u5c31\u4e0d\u6101\u89e3\u836f\u4e86\uff01\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u9053\uff1a\u201c\u54b1\u4eec\u5fc5\u987b\u8d70\u5feb\u4e9b\uff0c\u6700\u597d\u5728\u5929\u4eae\u524d\u8d76\u56de\u767d\u59d1\u5bfa\u3002\u201d \u3000\u3000\u597d\u5728\u73b0\u5728\u53c8\u591a\u4e86\u4f59\u4ea6\u4e50\u548c\u9ea6\u5c0f\u660e\u4e24\u4e2a\u7a7a\u624b\u7684\uff0c\u3000\u4e00\u884c\u4eba\u4f17\uff0c\u679c\u7136\u5728\u4e94\u66f4\u524d\u540e\uff0e\u4fbf\u56de\u5230\u4e86 \u767d\u59d1\u5bfa\u3002 \u3000\u3000\u521a\u8fdb\u5bfa\u95e8\uff0c\u4fbf\u89c1\u4f4f\u6301\u65b9\u4e08\u609f\u5c18\u8fce\u4e86\u4e0a\u6765\u3002 \u3000\u3000\u4e0d\u6d88\u8bf4\uff0c\u609f\u5c18\u5728\u6628\u665a\u80e1\u67cf\u9f84\u7b49\u4eba\u52a8\u8eab\u540e\uff0c\u5df2\u53d1\u89c9\u5854\u5185\u65e0\u4eba\uff0c\u5fc3\u77e5\u5fc5\u5b9a\u6709\u4ec0\u4e48\u884c\u52a8\uff0c\u4fbf\u5929 \u4e0d\u4eae\u5728\u5bfa\u95e8\u9644\u8fd1\u7b49\u5019\u3002 \u3000\u3000\u80e1\u67cf\u9f84\u56e0\u4e3a\u8fd8\u8981\u5b89\u7f6e\u52ab\u56de\u6765\u7684\u4eba\uff0e\u65e0\u6687\u548c\u609f\u5c18\u591a\u8c08\uff0c\u4fbf\u9053\uff1a\u201c\u7b49\u80e1\u67d0\u628a\u4ed6\u4eec\u5b89\u7f6e\u597d\u4e86\uff0c \u518d\u5230\u7985\u5ba4\u5411\u5929\u7985\u8001\u7985\u5e08\u9762\u62a5\u6628\u665a\u7ecf\u8fc7\u3002\u201d \u3000\u3000\u8fdb\u5165\u5854\u5185\uff0c\u80e1\u67cf\u9f84\u628a\u4eba\u5458\u91cd\u65b0\u5b89\u7f6e\u3002 \u3000\u3000\u4ed6\u548c\u949f\u4e00\u8c6a\u3001\u4f59\u4ea6\u4e50\u3001\u9ea6\u5c0f\u660e\u4f4f\u7b2c\u4e8c\u5c42\uff0c\u5d02\u5c71\u4e09\u96c4\u7b2c\u4e09\u5c42\uff0e\u6c5f\u5317\u4e09\u9f99\u7b2c\u56db\u5c42\uff0c\u82d7\u7d20\u5170\u59d0 \u59b9\u7b2c\u4e94\u5c42\u3002 \u3000\u3000\u7b2c\u516d\u5c42\u7a7a\u7740\uff0e\u628a\u9634\u624b\u2014\u9b54\u653e\u5728\u6700\u9876\u4e00\u5c42\uff0c\u5e76\u7531\u8303\u7389\u6606\u548c\u6587\u5929\u751f\u8d1f\u8d23\u770b\u5b88\u3002 \u3000\u3000\u65e9\u996d\u540e\uff0c\u80e1\u67cf\u9f84\u7387\u9886\u949f\u4e00\u8c6a\u3001\u4f59\u4ea6\u4e50\u3001\u9ea6\u5c0f\u660e\u6765\u5230\u5927\u6bbf\u4e00\u4fa7\u7684\u7985\u5ba4\u3002 \u3000\u3000\u8fd9\u65f6\u5929\u7985\u3001\u5929\u671b\u4e24\u4f4d\u5927\u5e08\u5df2\u7b49\u5728\u90a3\u91cc\uff0c\u609f\u5c18\u5219\u4f8d\u7acb\u5929\u7985\u5927\u5e08\u8eab\u4fa7\u3002 \u3000\u3000\u5929\u7985\u5927\u5e08\u9053\uff1a\u201c\u542c\u8bf4\u6628\u591c\u80e1\u76df\u4e3b\u5e26\u4eba\u8fdb\u5165\u8ff7\u8e2a\u8c37\uff0c\u6551\u51fa\u88ab\u56da\u5728\u5730\u7262\u6240\u6709\u7684\u4eba\uff0e\u96be\u5f97\u884c\u52a8 \u5982\u6b64\u987a\u5229\uff0c\u53ef\u559c\u53ef\u8d3a\uff01\u201d \u3000\u3000\u80e1\u67cf\u9f84\u9053\uff1a\u201c\u6628\u665a\u7684\u884c\u52a8\u662f\u4e34\u65f6\u51b3\u5b9a\u7684\uff0c\u6240\u4ee5\u4e8b\u5148\u6765\u4e0d\u53ca\u7980\u62a5\u638c\u95e8\u5927\u5e08\uff0c\u5931\u793c\u4e4b\u5904\uff0c\u8fd8 \u8bf7\u591a\u591a\u539f\u8c05\uff01\u201d \u3000\u3000\u949f\u4e00\u8c6a\u63a5\u9053\uff1a\u201c\u53e6\u5916\u80e1\u76df\u4e3b\u8fd8\u6709\u4e00\u4e2a\u987e\u8651\uff0c\u90a3\u5c31\u662f\u62c5\u5fc3\u8fde\u7d2f\u8d35\u6d3e\u3002\u56e0\u4e3a\u8d35\u6d3e\u8fd8\u6709\u4e0d\u5c11\u4eba \u5728\u8ff7\u8e2a\u8c37\uff0c\u3000\u4e00\u65e6\u5f97\u7f6a\u4e86\u970d\u5143\u4f3d\uff0c\u5fc5\u5c06\u5bf9\u8d35\u6d3e\u4e0d\u5229\u3002\u201d \u3000\u3000\u5929\u7985\u5927\u5e08\u9762\u6709\u6127\u8272\u9053\uff1a\u201c\u949f\u65bd\u4e3b\u8fd9\u6837\u8bf4\u8bdd\uff0c\u5b9e\u5728\u6559\u8001\u8872\u60ed\u6127\uff0e\u773c\u4e0b\u8d35\u6211\u53cc\u65b9\uff0e\u5fc5\u987b\u534f\u529b \u540c\u5fc3\uff0c\u4e00\u81f4\u5bf9\u4ed8\u8ff7\u8e2a\u8c37\u3002\u655d\u6d3e\u4e0d\u4fbf\u660e\u91cc\u76f8\u52a9\uff0c\u81f3\u5c11\u4e5f\u5e94\u6697\u4e2d\u5e2e\u5fd9\u3002\u201d \u3000\u3000\u5929\u671b\u5927\u5e08\u8ddf\u7740\u8bf4\u9053\uff1a\u201c\u5982\u679c\u80e1\u76df\u4e3b\u6628\u665a\u7684\u884c\u52a8\uff0c\u80fd\u4e8b\u5148\u901a\u77e5\u655d\u5e08\u5144\uff0c\u655d\u5e08\u5144\u81f3\u5c11\u53ef\u4ee5\u6d3e \u51fa\u51e0\u540d\u5f1f\u5b50\uff0c\u8d1f\u8d23\u80cc\u4eba\uff0c\u5bf9\u80e1\u76df\u4e3b\u7684\u884c\u52a8\uff0c\u5fc5\u662f\u65b9\u4fbf\u5f88\u591a\u3002\u201d \u3000\u3000\u5929\u7985\u5927\u5e08\u7f13\u7f13\u4ece\u8888\u88df\u5185\u638f\u51fa\u2014\u53ea\u7eff\u8272\u7389\u74f6\u9053\uff1a\u201c\u636e\u8bf4\u6c34\u7262\u91cc\u7684\u4eba\uff0c\u5728\u6c34\u91cc\u6ce1\u4e86\u4e00\u4e2a\u591a\u6708\uff0e \u7edd\u975e\u77ed\u65f6\u95f4\u53ef\u4ee5\u590d\u539f\u3002\u8001\u8872\u8fd9\u91cc\u6709\u74f6\u836f\u7c89\uff0c\u5bf9\u6c34\u4f24\u6e83\u80bf\u5177\u6709\u7279\u6548\uff0c\u5f85\u4f1a\u513f\u8bf7\u9a6c\u4e0a\u4e3a\u4ed6\u4eec\u6577\u5728 \u4f24\u5904\uff0c\u8f7b\u8005\u4e09\u65e5\uff0c\u91cd\u8005\u4e03\u65e5\u4fbf\u53ef\u6062\u590d\u5982\u5e38\u3002\u201d \u3000\u3000\u80e1\u67cf\u9f84\u8fde\u5fd9\u9053\u8c22\uff0c\u53cc\u624b\u63a5\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u5929\u7985\u5927\u5e08\u6c89\u541f\u4e86\u4e00\u9635\u9053\uff1a\u201c\u4e07\u4e00\u8fd9\u4e24\u5929\u970d\u5143\u4f3d\u5e26\u9886\u4eba\u9a6c\u8d76\u6765\u767d\u59d1\u5bfa\uff0c\u80e1\u76df\u4e3b\u53ef\u6709\u4ec0\u4e48\u6253 \u7b97\uff1f\u201d \u3000\u3000\u80e1\u67cf\u9f84\u4f3c\u4e4e\u6599\u5230\u8fd9\u4e00\u6b65\u9053\uff1a\u201c\u80e1\u67d0\u4e3a\u4e86\u4fdd\u62a4\u6c34\u7262\u5730\u7262\u6551\u51fa\u7684\u6614\u65e5\u5c5e\u4e0b\uff0c\u53ea\u6709\u4ee5\u73b0\u6709\u4eba\u624b \u4e0e\u970d\u5143\u4f3d\u62fc\u4e00\u6b7b\u6218\u4e86\uff01\u201d \u3000\u3000\u5929\u7985\u5927\u5e08\u6447\u4e86\u6447\u5934\u9053\uff1a\u201c\u80e1\u76df\u4e3b\u6b64\u523b\u5728\u767d\u59d1\u5bfa\u7684\u65e7\u90e8\uff0c\u534a\u6570\u4eba\u53ef\u8c13\u6beb\u65e0\u6218\u6597\u529b\uff0c\u82e5\u548c\u5bf9 \u65b9\u786c\u62fc\u8d77\u6765\uff0e\u53ea\u6015\u80dc\u7b97\u4e0d\u5927\u3002\u201d \u3000\u3000\u80e1\u67cf\u9f84\u4e24\u9053\u6d53\u7709\u6df1\u9501\u9053\uff1b\u201c\u83ab\u975e\u8001\u7985\u5e08\u6709\u4ec0\u4e48\u9ad8\u89c1\uff1f\u201d \u3000\u3000\u5929\u7985\u5927\u5e08\u9053\uff1a\u201c\u4f9d\u8001\u8872\u611a\u610f\uff0c\u80e1\u76df\u4e3b\u6700\u597d\u5e26\u7740\u65e7\u5c5e\u6682\u65f6\u8fdc\u79bb\u8ff7\u8e2a\u8c37\uff0c\u7b49\u4ed6\u4eec\u4f24\u52bf\u597d\u4e86\u4ee5 \u540e\uff0c\u518d\u4f5c\u8ba1\u8f83\u3002\u201d \u3000\u3000\u80e1\u67cf\u9f84\u60f3\u4e86\u60f3\u9053\uff1a\u201c\u8001\u7985\u5e08\u8fd9\u8bdd\u6709\u7406\uff0c\u80e1\u67d0\u51b3\u5b9a\u5c31\u5728\u4eca\u5929\u5165\u591c\u540e\u64a4\u51fa\u767d\u59d1\u5bfa\u3002\u201d \u3000\u3000\u8f9e\u522b\u5929\u7985\u5927\u5e08\u80e1\u67cf\u9f84\u7b49\u4eba\u518d\u56de\u5230\u5854\u5185\uff0c\u7b2c\u4e00\u4ef6\u4e8b\uff0c\u4fbf\u662f\u4e3a\u4ece\u7262\u91cc\u52ab\u56de\u7684\u4eba\u6577\u836f\u7597\u4f24\u3002\u6577 \u836f\u5b8c\u6bd5\u540e\uff0c\u80e1\u67cf\u9f84\u5429\u5490\u6240\u6709\u7684\u4eba\u6682\u65f6\u4f11\u606f\u2014\u756a\uff0e\u56e0\u4e3a\u6628\u591c\u8c01\u90fd\u4e0d\u66fe\u7761\u89c9\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u3001\u4f59\u4ea6\u4e50\u3001\u9ea6\u5c0f\u660e\u89c1\u80e1\u67cf\u9f84\u5e76\u672a\u4f11\u606f\uff0c\u4e5f\u90fd\u5728\u65c1\u76f8\u966a\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u5e08\u5144\uff0c\u54b1\u4eec\u771f\u8981\u64a4\u51fa\u767d\u59d1\u5bfa\u5417\uff1f\u201d \u3000\u3000\u80e1\u67cf\u9f84\u957f\u957f\u5401\u53e3\u6c14\u9053\uff1a\u201c\u4ee5\u76ee\u524d\u54b1\u4eec\u7684\u529b\u91cf\uff0c\u7684\u786e\u65e0\u6cd5\u4e0e\u8ff7\u8e2a\u8c37\u76f8\u6bd4\u3002\u5c24\u5176\u5176\u4e2d\u4e03\u4f4d\u521a \u6551\u56de\u6765\u7684\u884c\u52a8\u4e0d\u4fbf\u3002\u82e5\u970d\u5143\u4f3d\u5f53\u771f\u7387\u4f17\u524d\u6765\uff0c\u54b1\u4eec\u4e00\u65e6\u65e0\u6cd5\u62b5\u5fa1\uff0c\u53ea\u6015\u8fde\u64a4\u9000\u90fd\u65e0\u6cd5\u64a4\u51fa\u3002 \u53e6\u5916\uff0c\u54b1\u4eec\u7559\u5728\u767d\u59d1\u5bfa\uff0e\u4e5f\u5f88\u53ef\u80fd\u7275\u8fde\u4e86\u5c11\u6797\u65b9\u9762\u7684\u4eba\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u770b\u6765\u5e08\u5144\u5df2\u51b3\u5b9a\u5c31\u5728\u4eca\u5929\u665a\u4e0a\u884c\u52a8\u4e86\uff1f\u201d \u3000\u3000\u80e1\u67cf\u9f84\u9053\uff1a\u201c\u5fc5\u987b\u5728\u665a\u4e0a\u624d\u80fd\u884c\u52a8\u79d8\u5bc6\uff0e\u4e5f\u6b63\u53ef\u4ee5\u5229\u7528\u767d\u5929\u597d\u597d\u4f11\u606f\u4e00\u756a\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u5ffd\u7136\u95ee\u9053\uff1a\u201c\u5e08\u5144\u8981\u4e0d\u8981\u5230\u5854\u9876\u53bb\u770b\u770b\u9634\u624b\u4e00\u9b54\uff1f\u201d \u3000\u3000\u201c\u770b\u4ed6\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u662f\u5411\u4ed6\u8981\u2018\u5411\u5fc3\u9732\u2019\u89e3\u836f\uff01\u201d \u3000\u3000\u201c\u90a3\u5c31\u7531\u4f60\u5148\u53bb\u770b\u770b\u4ed6\uff0c\u7136\u540e\u518d\u628a\u60c5\u5f62\u544a\u8bc9\u6211\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u968f\u5373\u5f80\u5b9d\u5854\u9876\u5c42\u800c\u6765\u3002 \u3000\u3000\u8def\u7ecf\u7b2c\u4e94\u5c42\u5854\uff0c\u53ea\u89c1\u82d7\u7d20\u82d3\u6b63\u966a\u7740\u59d0\u59d0\u82d7\u7d20\u5170\u5728\u95f2\u8c08\u89e3\u95f7\u3002 \u3000\u3000\u82d7\u7d20\u82d3\u95ee\u9053\uff1a\u201c\u4f60\u8981\u5230\u54ea\u91cc\u53bb\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u5949\u4e86\u80e1\u5e08\u5144\u4e4b\u547d\uff0c\u5230\u4e0a\u9762\u53bb\u770b\u770b\u4f60\u4eec\u4e24\u4f4d\u7684\u6df7\u86cb\u5e08\u7236\u9634\u624b\u2014\u9b54\u3002\u201d \u3000\u3000\u82d7\u7d20\u82d3\u5a07\u9762\u6cdb\u51fa\u7ea2\u971e\u9053\uff1a\u201c\u4e0a\u9762\u5df2\u7ecf\u6709\u4eba\u770b\u5b88\u4ed6\uff0c\u4f60\u4f55\u5fc5\u518d\u53bb\u770b\u4ed6\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4f60\u89c1\u8fc7\u4ed6\u6ca1\u6709\uff1f\u201d \u3000\u3000\u82d7\u7d20\u82d3\u9053\uff1a\u201c\u597d\u6b79\u4ed6\u4ece\u524d\u66fe\u662f\u6211\u5e08\u7236\uff0c\u4ed6\u73b0\u5728\u90a3\u79cd\u6837\u5b50\uff0c\u6211\u600e\u597d\u610f\u601d\u518d\u53bb\u770b\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u54fc\u4e86\u58f0\u9053\uff1a\u201c\u4ed6\u662f\u4e2a\u8001\u8272\u9b54\uff0c\u636e\u8bf4\u6253\u65f6\u8fde\u5973\u5f1f\u5b50\u4e5f\u4e0d\u653e\u8fc7\uff0c\u8fd9\u79cd\u79bd\u517d\u4e0d\u5982\u7684\u4eba\uff0c \u6839\u672c\u5c31\u4e0d\u914d\u505a\u4f60\u5e08\u7236\uff0c\u6211\u770b\u4f60\u8fd8\u662f\u968f\u6211\u2014\u8d77\u4e0a\u53bb\u5427\uff01\u201d \u3000\u3000\u201c\u6211\u4e0d\u53bb\u3002\u201d \u3000\u3000\u201c\u89c1\u4e86\u4ed6\u5bf9\u4f60\u6709\u597d\u5904\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u597d\u5904\uff1f\u201d \u3000\u3000\u201c\u6709\u6028\u62a5\u6028\uff0c\u6709\u4ec7\u62a5\u4ec7\uff0c\u4f60\u4e5f\u8be5\u51fa\u51fa\u95f7\u6c14\uff01\u201d \u3000\u3000\u201c\u518d\u600e\u4e48\u8bf4\u6211\u4e5f\u4e0d\u8be5\u628a\u4ed6\u600e\u4e48\u6837\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u6599\u5b9a\u82d7\u7d20\u82d3\u4e0d\u4f1a\u4e0a\u53bb\uff0c\u53ea\u597d\u4e00\u4e2a\u4eba\u767b\u4e0a\u4e86\u7b2c\u4e03\u5c42\u5854\u3002 \u3000\u3000\u770b\u5b88\u9634\u624b\u4e00\u9b54\u7684\u662f\u8303\u7389\u6606\u548c\u6587\u5929\u751f\uff0c\u5176\u4e2d\u6587\u5929\u751f\u672c\u8eab\u5c31\u884c\u52a8\u4e0d\u4fbf\uff0c\u5b9e\u9645\u80fd\u8d1f\u8d77\u8d23\u4efb\u7684\uff0c \u53ea\u6709\u8303\u7389\u6606\u4e00\u4eba\u3002 \u3000\u3000\u6b64\u523b\u6587\u5929\u751f\u6b63\u9760\u5899\u6253\u76f9\uff0c\u8303\u7389\u6606\u4e5f\u5728\u95ed\u76ee\u517b\u795e\u3002 \u3000\u3000\u81f3\u4e8e\u9634\u624b\u4e00\u9b54\uff0c\u4e0d\u4f46\u88ab\u70b9\u4e86\u7a74\uff0e\u8fde\u53cc\u624b\u90fd\u53cd\u81c2\u7ed1\u7740\uff0c\u4e24\u811a\u4e5f\u88ab\u7f1a\u5728\u4e00\u8d77\u3002 \u3000\u3000\u8303\u7389\u6606\u542c\u5230\u811a\u6b65\u58f0\uff0c\u7741\u5f00\u773c\u6765\u9053\uff1a\u201c\u9ea6\u5c0f\u4fa0\u6709\u4e8b\u5417\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u6211\u60f3\u95ee\u4ed6\u51e0\u53e5\u8bdd\u3002\u201d \u3000\u3000\u8bf4\u7740\uff0e\u6325\u624b\u4e3a\u9634\u624b\u4e00\u9b54\u89e3\u5f00\u7a74\u9053\u3002 \u3000\u3000\u9634\u624b\u4e00\u9b54\u60a0\u60a0\u6e05\u9192\uff0e\u5f53\u4ed6\u53d1\u89c9\u53cc\u624b\u53cc\u811a\u88ab\u7f1a\uff0c\u987f\u65f6\u8138\u8272\u618b\u5f97\u6709\u5982\u732a\u809d\u3002 \u3000\u3000\u4ed6\u672c\u662f\u6210\u540d\u6570\u5341\u5e74\u7684\u4e00\u4ee3\u9ed1\u9053\u9b54\u5934\uff0c\u8d6b\u8d6b\u4e0d\u53ef\u2014\u4e16\uff0c\u5982\u4eca\u843d\u5f97\u5982\u6b64\u72fc\u72c8\uff0c\u771f\u6068\u4e0d\u5f97\u4e00\u5934 \u649e\u6b7b\uff0e\u53ef\u60dc\u73b0\u5728\u8fde\u649e\u6b7b\u7684\u673a\u4f1a\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u6de1\u6de1\u2014\u7b11\u9053\uff1a\u201c\u8001\u6df7\u86cb\uff0c\u6211\u60f3\u95ee\u4f60\u51e0\u53e5\u8bdd\uff0c\u4f60\u8981\u4ece\u5b9e\u56de\u7b54\uff01\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u53cc\u988a\u731b\u7136\u62bd\u6410\u626d\u66f2\u4e86\u51e0\u4e0b\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u5b50\u53eb\u8c01\u8001\u6df7\u86cb\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u5f53\u7136\u662f\u53eb\u4f60\uff01\u4f60\u672c\u6765\u5c31\u662f\u4e2a\u8001\u6df7\u86cb\uff0c\u6211\u53eb\u5f97\u6709\u4ec0\u4e48\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u543c\u9053\uff1a\u201c\u80e1\u8bf4\uff01\u8001\u592b\u5802\u5802\u2026\u2026\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u63a5\u9053\uff1a\u201c\u5802\u5802\u4e00\u9b54\uff0c\u5bf9\u5417\uff1f\u4e0d\u7ba1\u662f\u4e00\u9b54\u8fd8\u662f\u4e00\u6df7\u86cb\uff0e\u4e8b\u5230\u5982\u4eca\uff0c\u4e5f\u53ea\u597d\u59d4\u5c48\u4e00\u4e0b \u4e86\uff01\u201d \u3000\u3000\u201c\u6709\u8bdd\u5feb\u8bb2\uff0c\u6709\u5c41\u4f19\u653e\uff01\u201d \u3000\u3000\u201c\u4f60\u5fc3\u91cc\u5e94\u8be5\u6709\u6570\uff0e\u53ea\u8981\u80fd\u628a\u2018\u5411\u5fc3\u9732\u2019\u7684\u89e3\u836f\u4ea4\u51fa\u6765\uff0c\u6211\u4eec\u80e1\u76df\u4e3b\u4e5f\u8bb8\u80fd\u9976\u4e86\u4f60\u7684\u72d7 \u547d\u3002\u201d \u3000\u3000\u201c\u8001\u592b\u7684\u2018\u5411\u5fc3\u9732\u2019\u89e3\u836f\uff0c\u600e\u53ef\u80fd\u5e26\u5728\u8eab\u4e0a\uff1f\u201d \u3000\u3000\u201c\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u5728\u5415\u6881\u5c71\u4e5d\u5929\u7384\u9634\u6d1e\u5e9c\u91cc\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u6697\u9053\uff1a\u201c\u8fd9\u8001\u6df7\u86cb\u8bf4\u5f97\u4e0d\u5047\uff0c\u4ed6\u82e5\u628a\u89e3\u836f\u5e26\u5728\u8eab\u4e0a\uff0c\u73b0\u5728\u4e00\u5b9a\u641c\u5f97\u51fa\u6765\u3002\u201d \u3000\u3000\u4ed6\u671b\u4e86\u8303\u7389\u6606\u4e00\u773c\u9053\uff1a\u201c\u8303\u5c11\u5821\u4e3b\u641c\u8fc7\u4ed6\u7684\u8eab\u4e0a\u6ca1\u6709\uff1f\u201d \u3000\u3000\u8303\u7389\u6606\u9053\uff1a\u201c\u641c\u8fc7\uff0c\u4ec0\u4e48\u4e5f\u6ca1\u6709\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u8f6c\u5934\u9053\uff1a\u201c\u8001\u6df7\u86cb\uff0c\u8fd9\u6837\u770b\u4f60\u662f\u4ea4\u4e0d\u51fa\u89e3\u836f\u6765\u4e86\uff1f\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u9053\uff1a\u201c\u4f60\u4eec\u8981\u60f3\u5f97\u5230\u89e3\u836f\u5f88\u7b80\u5355\uff0e\u53ea\u8981\u628a\u8001\u592b\u653e\u4e86\uff0e\u8001\u592b\u60c5\u613f\u56de\u5415\u6881\u5c71\u4e5d\u5929\u7384 \u9634\u6d1e\u5e9c\u53d6\u89e3\u836f\u6765\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u6447\u5934\u9053\uff1a\u201c\u9b3c\u624d\u4fe1\u5f97\u8fc7\u4f60\u7684\u8bdd\uff01\u4f60\u82e5\u8dd1\u4e86\u5462\uff1f\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u9053\uff1a\u201c\u541b\u5b50\u4e00\u8a00\uff0c\u9a77\u9a6c\u96be\u8ffd\u3002\u8001\u592b\u4e00\u5411\u6700\u91cd\u8a00\u8bfa\uff0e\u5c82\u80fd\u6b3a\u9a97\u4f60\u8fd9\u79cd\u540e\u751f\u665a\u8f88\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u5e76\u4e0d\u52a8\u6012\uff0e\u7b11\u4e86\u7b11\u9053\uff1a\u201c\u8001\u6df7\u86cb\uff0e\u4e16\u4e0a\u6709\u4f60\u8fd9\u79cd\u541b\u5b50\uff0e\u90a3\u5c31\u6ca1\u6709\u5c0f\u4eba\u4e86\uff01\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u54ac\u4e86\u54ac\u7259\u9053\uff1a\u201c\u65e2\u7136\u4e0d\u80af\u653e\u8001\u592b\uff0e\u90a3\u5c31\u4f11\u60f3\u5f97\u5230\u89e3\u836f\u3002\u4e0d\u8fc7\u8001\u592b\u8fd8\u6709\u4e00\u4e2a\u529e \u6cd5\u3002\u201d \u3000\u3000\u201c\u8001\u6df7\u86cb\u6709\u4ec0\u4e48\u53e6\u5916\u7684\u529e\u6cd5\uff1f\u201d \u3000\u3000\u201c\u4f60\u4eec\u82e5\u4fe1\u4e0d\u8fc7\u8001\u592b\uff0e\u4e0d\u59a8\u628a\u8001\u592b\u62bc\u5230\u5415\u7cb1\u5c71\u4e5d\u5929\u7384\u9634\u6d1e\u5e9c\uff0e\u90a3\u65f6\u8001\u592b\u5c31\u627e\u51fa\u89e3\u836f\u4ea4\u7ed9 \u4f60\u4eec\uff0c\u8fd9\u6837\u603b\u53ef\u4ee5\u5427\uff1f\u201d \u3000\u3000\u201c\u4f60\u60f3\u5f97\u5012\u4e0d\u9519\uff0e\u82e5\u62bc\u7740\u4f60\u8fd9\u8001\u6df7\u86cb\u8d70\u8def\uff0e\u6211\u4eec\u81f3\u5c11\u8981\u6d3e\u51fa\u4e8c\u540d\u9ad8\u624b\u3002\u6211\u4eec\u73b0\u5728\u7684\u4eba\u624b \u5df2\u7ecf\u4e0d\u591f\u7528\uff0c\u600e\u53ef\u518d\u6d6a\u8d39\u4eba\u529b\uff1f\u66f4\u4f55\u51b5\u4f60\u4ea4\u51fa\u6765\u7684\u662f\u5426\u771f\u89e3\u836f\uff0c\u5f53\u65f6\u4e5f\u65e0\u4ece\u8fa8\u8bc6\u3002\u201d \u3000\u3000\u201c\u7167\u4f60\u8fd9\u6837\u8bf4\uff0c\u90a3\u5c31\u522b\u60f3\u5f97\u5230\u89e3\u836f\uff01\u201d \u3000\u3000\u201c\u5728\u4e0b\u4e5f\u6709\u4e2a\u529e\u6cd5\uff01\u201d \u3000\u3000\u201c\u4ec0\u4e48\u529e\u6cd5\uff1f\u201d \u3000\u3000\u201c\u4f60\u90a3\u5f03\u6697\u6295\u660e\u7684\u5f1f\u5b50\u82d7\u7d20\u82d3\u6b63\u5728\u6211\u4eec\u8fd9\u91cc\uff0c\u4f60\u5199\u5c01\u4eb2\u7b14\u4fe1\u4ea4\u7ed9\u5979\uff0c\u7531\u5979\u5230\u5415\u6881\u5c71\u4e5d\u5929 \u7384\u9634\u6d1e\u5e9c\u628a\u89e3\u836f\u53d6\u56de\uff0e\u7b49\u9a8c\u660e\u89e3\u836f\u662f\u771f\u540e\uff0c\u4fdd\u8bc1\u653e\u4f60\u8fd9\u8001\u6df7\u86cb\u6eda\u86cb\uff01\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u2014\u5bf9\u4e09\u89d2\u773c\u7728\u4e86\u51e0\u7728\u9053\uff1a\u201c\u8001\u592b\u6b63\u60f3\u89c1\u89c1\u8fd9\u4e2b\u5934\uff0c\u5feb\u5feb\u628a\u5979\u627e\u6765\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u53ea\u597d\u53c8\u5954\u4e0b\u7b2c\u4e94\u5c42\uff0e\u53eb\u9053\uff1a\u201c\u4e8c\u59d1\u5a18\uff0c\u8001\u6df7\u86cb\u627e\u4f60\uff01\u201d \u3000\u3000\u82d7\u7d20\u82d3\u6123\u4e86\u6123\u9053\uff1a\u201c\u8001\u6df7\u86cb\u662f\u8c01\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1b\u201c\u5c31\u662f\u4f60\u6015\u89c1\u7684\u90a3\u4e2a\u3002\u201d \u3000\u3000\u201c\u4ed6\u2026\u2026\u4ed6\u627e\u6211\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u6709\u5173\u2018\u5411\u5fc3\u9732\u2019\u89e3\u836f\u7684\u4e8b\u3002\u4f60\u65e0\u8bba\u5982\u4f55\u8981\u89c1\u89c1\u4ed6\u3002\u201d \u3000\u3000\u82d7\u7d20\u82d3\u4e0d\u89c9\u5927\u611f\u4e3a\u96be\u8d77\u6765\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u4e00\u672c\u6b63\u7ecf\u7684\u9053\uff1a\u201c\u4e8c\u59d1\u5a18\uff0c\u8fd9\u662f\u4ef6\u5927\u4e8b\uff0c\u4e3a\u4e86\u5927\u5c40\uff0c\u4f60\u4e0d\u5e94\u8be5\u62d2\u7edd\u3002\u201d \u3000\u3000\u82d7\u7d20\u82d3\u6ca1\u5948\u4f55\uff0c\u8f6c\u8fc7\u5934\u9053\uff1a\u201c\u59d0\u59d0\uff0c\u6211\u80fd\u89c1\u4ed6\u5417\uff1f\u201d \u3000\u3000\u82d7\u7d20\u5170\u9053\uff1a\u201c\u7528\u4e0d\u7740\u6015\uff0c\u4f60\u5c31\u53bb\u5427\uff01\u201d \u3000\u3000\u82d7\u7d20\u82d3\u53ea\u597d\u8ddf\u7740\u9ea6\u5c0f\u660e\u767b\u4e0a\u7b2c\u4e03\u5c42\u5854\u3002 \u3000\u3000\u4e4d\u89c1\u9634\u624b\u4e00\u9b54\u7684\u624b\u811a\u88ab\u7f1a\u7684\u72fc\u72c8\u6a21\u6837\uff0c\u82d7\u7d20\u82d3\u8d8a\u53d1\u4e0d\u6562\u5bf9\u9634\u624b\u4e00\u9b54\u6b63\u773c\u76f8\u770b\uff0c\u53ea\u80fd\u9ed8\u9ed8 \u7684\u5782\u4e0b\u5934\u7ad9\u5728\u9ea6\u5c0f\u660e\u8eab\u65c1\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u8001\u6df7\u86cb\uff0c\u4f60\u90a3\u5f03\u6697\u6295\u660e\u7684\u5973\u5f1f\u5b50\u6765\u4e86\uff0c\u6709\u5c41\u5c31\u5feb\u653e\u5427\uff01\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u54b3\u4e86\u4e24\u58f0\u9053\uff1a\u201c\u4e2b\u5934\uff0c\u8001\u592b\u597d\u6b79\u4e5f\u505a\u8fc7\u4f60\u5e08\u7236\uff0c\u628a\u4f60\u4ece\u5c0f\u6559\u517b\u5230\u5927\u3002\u5982\u4eca\u89c1\u4e86 \u8001\u592b\uff0c\u534a\u70b9\u5e94\u6709\u7684\u793c\u8c8c\u90fd\u6ca1\u6709\uff0c\u7adf\u7136\u5fd8\u6069\u8d1f\u4e49\u5230\u8fd9\u79cd\u5730\u6b65\uff0c\u5b9e\u5728\u8ba9\u8001\u592b\u5bd2\u5fc3\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u51b7\u7b11\u9053\uff1a\u201c\u8001\u6df7\u86cb\uff0c\u73b0\u5728\u4e0d\u662f\u4f60\u8bad\u4eba\u7684\u65f6\u5019\uff0c\u800c\u4e14\u4f60\u4e5f\u4e0d\u591f\u8d44\u683c\u8bb2\u8fd9\u79cd\u8bdd\uff0c\u771f\u662f \u606c\u4e0d\u77e5\u803b\uff01\u201d \u3000\u3000\u4f46\u82d7\u7d20\u82d3\u8fd8\u662f\u602f\u602f\u7684\u4e0a\u524d\u65bd\u4e86\u4e00\u793c\u9053\uff1a\u201c\u5f1f\u5b50\u53c2\u89c1\u5e08\u7236\uff01\u201d \u3000\u3000\u9634\u624b\u2014\u9b54\u5c34\u5c2c\u4e00\u7b11\u9053\uff1a\u201c\u8fd9\u8fd8\u5dee\u4e0d\u591a\uff0c\u4e0d\u6127\u662f\u8001\u592b\u2014\u624b\u51cb\u6559\u51fa\u6765\u7684\uff0c\u6bd4\u59d3\u9ea6\u7684\u5c0f\u5b50\u597d\u591a \u5566\uff01\u201d \u3000\u3000\u53ea\u542c\u201c\u556a\u201d\u7684\u4e00\u58f0\uff0c\u9634\u624b\u2014\u9b54\u5de6\u988a\u5df2\u7206\u8d77\u4e00\u58f0\u8106\u54cd\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u8fd9\u4e00\u638c\u6253\u5f97\u5e76\u4e0d\u91cd\uff0c\u53ea\u662f\u7565\u4f5c\u6559\u8bad\u800c\u5df2\u3002 \u3000\u3000\u9634\u624b\u4e00\u9b54\u53cc\u624b\u53cd\u7f1a\uff0e\u60f3\u6478\u6478\u9762\u988a\u90fd\u4e0d\u80fd\uff0c\u54a7\u4e86\u54a7\u5634\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u4f60\u8fd8\u771f\u6253\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u82e5\u4e0d\u662f\u82d7\u59d1\u5a18\u5728\u65c1\uff0c\u5728\u4e0b\u54ea\u4f1a\u8fd9\u6837\u5ba2\u6c14\uff1f\u4f60\u8be5\u611f\u8c22\u82d7\u59d1\u5a18\u624d\u5bf9\uff01\u201d \u5367\u9f99\u751f\u300a\u5999\u7edd\u5929\u9999\u300b \u7b2c\u4e09\u5341\u4e00\u56de\u3000\u5200\u5149\u5251\u5f71 \u3000\u3000\u82d7\u7d20\u82d3\u4e3a\u5c3d\u91cf\u51cf\u5c11\u9634\u624b\u4e00\u9b54\u7684\u96be\u582a\uff0c\u642d\u8baa\u9053\uff1a\u201c\u5e08\u7236\u6709\u4ec0\u4e48\u8bdd\u5c31\u6e05\u8bb2\u5427\uff01\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u9053\uff1a\u201c\u4e5d\u5929\u7384\u9634\u6d1e\u5e9c\u7684\u836f\u5ba4\u4f60\u662f\u77e5\u9053\u7684\uff0c\u5728\u5de6\u65b9\u6700\u4e0a\u5c42\u7684\u6728\u67b6\u4e0a\u6709\u4e2a\u7eff\u8272\u5c0f\u78c1 \u7f50\uff0c\u90a3\u91cc\u9762\u88c5\u7684\u2018\u5411\u5fc3\u9732\u2019\u89e3\u836f\uff0c\u4f60\u628a\u5b83\u53d6\u4e8c\u5341\u7c92\u6765\uff0c\u56de\u5230\u8fd9\u91cc\uff0c\u5c31\u7b49\u4e8e\u6551\u4e86\u5e08\u7236\u4e00\u547d\u3002\u201d \u3000\u3000\u82d7\u7d20\u82d3\u9762\u6709\u96be\u8272\u9053\uff1a\u201c\u90a3\u836f\u5ba4\u6709\u4e13\u4eba\u770b\u5b88\uff0c\u5f1f\u5b50\u6839\u672c\u4e0d\u80fd\u8fdb\u53bb\uff01\u201d \u3000\u3000\u201c\u4f60\u5c31\u8bf4\u662f\u5949\u4e86\u8001\u592b\u4e4b\u547d\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u7a7a\u53e3\u65e0\u51ed\uff0c\u5b88\u836f\u5ba4\u7684\u4eba\u600e\u4f1a\u76f8\u4fe1\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u5f88\u7b80\u5355\uff0c\u8001\u6df7\u86cb\uff01\u5c31\u7531\u4f60\u4eb2\u7b14\u5199\u5c01\u4fe1\u4ea4\u7ed9\u82d7\u59d1\u5a18\u5427\u3002\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u9053\uff1a\u201c\u53ef\u662f\u54ea\u6765\u7684\u7eb8\u7b14\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u5728\u4e0b\u9a6c\u4e0a\u627e\u7ed9\u4f60\uff0c\u4e0d\u77e5\u4f60\u4f1a\u4e0d\u4f1a\u5199\u5b57\uff1f\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u543c\u9053\uff1a\u201c\u80e1\u8bf4\uff01\u8001\u592b\u4e5f\u662f\u9971\u8bfb\u8bd7\u4e66\u4e4b\u4eba\uff0c\u600e\u80fd\u8fde\u5b57\u90fd\u4e0d\u4f1a\u5199\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u7b11\u9053\uff1a\u201c\u90a3\u4f60\u662f\u540d\u526f\u5176\u5b9e\u7684\u65af\u6587\u626b\u5730\uff0c\u8863\u51a0\u79bd\u517d\uff01\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u53c8\u9a82\u4eba\uff01\u201d \u3000\u3000\u201c\u6ca1\u7529\u4f60\u8033\u5149\u5c31\u7b97\u5ba2\u6c14\u5566\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u521a\u8981\u4e0b\u5854\u5411\u4f4f\u6301\u65b9\u4e08\u609f\u5c18\u501f\u7b14\u58a8\u7eb8\u781a\uff0c\u5ffd\u542c\u8303\u7389\u6606\u5c16\u58f0\u53eb\u9053\uff1a\u201c\u4e0d\u597d\uff01\u597d\u50cf\u6709\u5927 \u961f\u4eba\u9a6c\uff0e\u6b63\u5411\u54b1\u4eec\u8fd9\u91cc\u800c\u6765\uff01\u201d \u3000\u3000\u8fd9\u65f6\u8303\u7389\u6606\u6b63\u63a2\u9996\u5411\u7a97\u5916\u5f20\u671b\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u8fc5\u901f\u7684\u51d1\u8fc7\u8eab\u53bb\u5411\u5916\u4e00\u770b\uff0c\u679c\u7136\uff0e\u5927\u7ea6\u4e00\u91cc\u5916\u7684\u5c71\u811a\u4e0b\uff0c\u8db3\u6709\u4e0a\u767e\u4eba\uff0c\u9ed1\u538b\u538b\u7684 \u4e00\u7247\uff0c\u6b63\u6d69\u6d69\u8361\u8361\u5411\u767d\u59d1\u5bfa\u65b9\u5411\u800c\u6765\u3002 \u3000\u3000\u800c\u90a3\u5904\u5c71\u811a\u4e0b\u53c8\u6b63\u662f\u8ff7\u8e2a\u8c37\u51fa\u53e3\u4e4b\u5904\u7684\u5fc5\u7ecf\u4e4b\u8def\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u5fc3\u77e5\u4e0d\u5999\uff0c\u3000\u4e00\u6307\u5c06\u9634\u624b\u4e00\u9b54\u70b9\u660f\u9053\uff1a\u201c\u5c11\u5821\u4e3b\u548c\u82d7\u59d1\u5a18\u5feb\u53d6\u597d\u5175\u5203\u542c\u5019\u884c\u52a8\uff0c \u6211\u9a6c\u4e0a\u4e0b\u53bb\u544a\u77e5\u80e1\u5e08\u5144\uff01\u201d \u3000\u3000\u6765\u5230\u7b2c\u4e8c\u5c42\u5854\uff0c\u80e1\u67cf\u9f84\u4ecd\u548c\u949f\u4e00\u8c6a\u3001\u4f59\u4ea6\u4e50\u8ba1\u8bae\u6709\u5173\u884c\u52a8\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u53eb\u9053\uff1a\u201c\u5e08\u5144\uff0c\u5feb\u51c6\u5907\u8fce\u654c\uff0c\u970d\u5143\u4f3d\u5e26\u9886\u5927\u961f\u4eba\u9a6c\uff0c\u9a6c\u4e0a\u5c31\u5230\u4e86\uff01\u201d \u3000\u3000\u80e1\u67cf\u9f84\u4e0d\u89c9\u4e00\u60ca\u9053\uff1a\u201c\u8fd9\u6d88\u606f\u4ec0\u4e48\u4eba\u4f20\u6765\u7684\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u5c0f\u5f1f\u65b9\u624d\u5728\u5854\u9876\u4eb2\u773c\u770b\u5230\u7684\uff0c\u4ed6\u4eec\u79bb\u8fd9\u91cc\u5927\u7ea6\u8fd8\u6709\u4e00\u91cc\u8def\u5de6\u53f3\uff0e\u60a8\u8fd9\u91cc\u662f \u7b2c\u4e8c\u5c42\u5854\uff0c\u5bfa\u5916\u6709\u6811\u906e\u7740\uff0c\u5f53\u7136\u770b\u4e0d\u5230\u3002\u201d \u3000\u3000\u201c\u4e00\u91cc\u4e4b\u5916\u53c8\u600e\u80fd\u770b\u51fa\u662f\u970d\u5143\u4f3d\uff1f\u201d \u3000\u3000\u201c\u7531\u8ff7\u8e2a\u8c37\u65b9\u5411\u6765\u4e86\u4e00\u767e\u591a\u4eba\uff0c\u4e0d\u662f\u970d\u5143\u4f3d\u8fd8\u4f1a\u662f\u8c01\uff1f\u201d \u3000\u3000\u80e1\u67cf\u9f84\u6025\u6025\u5429\u5490\u9053\uff1a\u201c\u5c31\u70e6\u4f60\u518d\u4e0a\u5854\u901a\u77e5\u80fd\u884c\u52a8\u7684\u4eba\u9a6c\u4e0a\u51c6\u5907\uff0e\u5854\u9876\u6700\u597d\u4ecd\u7559\u2014\u4eba\u8d1f\u8d23 \u76d1\u89c6\u3002\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u9053\uff1a\u201c\u5c5e\u4e0b\u73b0\u5728\u5c31\u53bb\u901a\u77e5\u5c11\u6797\u65b9\u9762\u3002\u201d \u3000\u3000\u80e1\u67cf\u9f84\u9053\uff1a\u201c\u54b1\u4eec\u4e0d\u60f3\u7275\u7d2f\u5c11\u6797\u65b9\u9762\uff0e\u4f55\u7528\u901a\u77e5\u4ed6\u4eec\uff1f\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u9053\uff1a\u201c\u5c5e\u4e0b\u53ea\u662f\u8ba9\u4ed6\u4eec\u77e5\u9053\uff0c\u5e76\u975e\u6c42\u4ed6\u4eec\u5e2e\u5fd9\u3002\u201d \u3000\u3000\u8bf4\u7f62\u5f84\u81ea\u4e0b\u697c\u800c\u53bb\u3002 \u3000\u3000\u80e1\u67cf\u9f84\u4ea4\u4ee3\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u5f85\u4f1a\u513f\u6700\u597d\u7531\u949f\u8d24\u5f1f\u5148\u884c\u51fa\u9762\uff0c\u975e\u5230\u5fc5\u8981\uff0c\u6211\u4e0d\u5b9c\u73b0\u8eab\u3002\u201d \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u76df\u4e3b\u6682\u65f6\u8eb2\u5230\u4f55\u5904\uff1f\u201d \u3000\u3000\u80e1\u67cf\u9f84\u9053\uff1a\u201c\u6211\u5230\u5854\u9876\u89c2\u5bdf\u4e00\u4e0b\u3002\u201d \u3000\u3000\u80e1\u67cf\u9f84\u5306\u5306\u6765\u5230\u7b2c\u4e03\u5c42\u5854\uff0e\u8303\u7389\u6606\u5df2\u7ecf\u4e0b\u5854\u800c\u53bb\uff0c\u53ea\u6709\u6587\u5929\u751f\u4e00\u4eba\u63a2\u5934\u5854\u7a97\u8d1f\u8d23\u5b88\u671b\u3002 \u3000\u3000\u5411\u5916\u770b\u53bb\uff0c\u5bf9\u65b9\u7684\u4eba\u9a6c\uff0c\u79bb\u5bfa\u5df2\u7ecf\u4e0d\u8db3\u534a\u91cc\uff0c\u679c\u7136\u4eba\u6570\u4e0d\u4e0b\u767e\u540d\u4e4b\u591a\uff0c\u867d\u7136\u65e0\u6cd5\u8ba4\u6e05\u9762 \u76ee\uff0e\u4f46\u5404\u79cd\u5175\u5200\u5728\u65e5\u5149\u6620\u7167\u4e0b\u8000\u773c\u751f\u82b1\uff0c\u5f53\u771f\u4ee4\u4eba\u770b\u6765\u80c6\u98a4\u5fc3\u60ca\u3002 \u3000\u3000\u80e1\u67cf\u9f84\u770b\u7f62\u4e4b\u540e\uff0c\u4ea4\u4ee3\u6587\u5929\u751f\u7ee7\u7eed\u76d1\u89c6\uff0c\u81ea\u5df1\u53c8\u4e0b\u5854\u6765\u5230\u7b2c\u4e8c\u5c42\u3002 \u3000\u3000\u8fd9\u65f6\u949f\u4e00\u8c6a\u3001\u4f59\u4ea6\u4e50\u3001\u738b\u5927\u5eb7\u3001\u9ea6\u5c0f\u660e\u3001\u8303\u7389\u6606\u3001\u82d7\u7d20\u82d3\u5df2\u5168\u805a\u5728\u7b2c\u4e8c\u5c42\u5854\u3002 \u3000\u3000\u80e1\u67cf\u9f84\u9053\uff1a\u201c\u54b1\u4eec\u5e72\u8106\u5c31\u8fce\u5230\u6811\u6797\u5916\u9762\u53bb\uff01\u201d \u3000\u3000\u949f\u4e00\u8c6a\u7565\u4e00\u6c89\u541f\u9053\uff1a\u201c\u4e5f\u597d\uff01\u8fd9\u6837\u4e5f\u514d\u5f97\u4ed6\u4eec\u628a\u767d\u59d1\u5bfa\u91cd\u91cd\u5305\u56f4\uff0c\u54b1\u4eec\u53cd\u800c\u65bd\u5c55\u4e0d\u5f00\u3002\u201d \u3000\u3000\u5f53\u516d\u4eba\u6765\u5230\u6811\u6797\u4e4b\u5916\uff0c\u5bf9\u65b9\u7684\u4eba\u9a6c\u6b63\u597d\u5df2\u7ecf\u63a5\u8fd1\uff0c\u5f53\u5148\u4e00\u4eba\u6b63\u662f\u7f57\u6d6e\u4e00\u53df\u970d\u5143\u4f3d\u3002 \u3000\u3000\u5728\u970d\u5143\u4f3d\u8eab\u540e\uff0c\u4f9d\u7a00\u53ef\u4ee5\u8ba4\u51fa\u6709\u5cad\u5357\u4e8c\u5947\u3001\u6c5f\u5357\u56db\u602a\u7684\u8001\u5927\u5c71\u9b48\u6bdb\u8d8a\uff0c\u4ee5\u53ca\u5d06\u5cd2\u4e94\u9b54\u548c \u6bd2\u706b\u6210\u5168\u3001\u9ed1\u9b54\u65f6\u5bc5\u3001\u725b\u5954\u3001\u706b\u83bd\u7b49\u4eba\u3002 \u3000\u3000\u970d\u5143\u4f3d\u7684\u5982\u6b64\u9635\u5bb9\uff0c\u5df2\u7b49\u4e8e\u8ff7\u8e2a\u8c37\u7684\u7cbe\u9510\u5c3d\u51fa\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u3001\u4f59\u4ea6\u4e50\u3001\u9ea6\u5c0f\u660e\u3001\u738b\u5927\u5eb7\u3001\u8303\u7389\u6606\u3001\u82d7\u7d20\u82d3\u5219\u2014\u5b57\u6392\u5f00\uff0c\u963b\u4f4f\u53bb\u8def\u3002 \u3000\u3000\u970d\u5143\u4f3d\u53cc\u81c2\u865a\u7a7a\u5411\u4e0b\u4e00\u538b\uff0c\u793a\u610f\u968f\u5e26\u7684\u767e\u4f59\u4eba\u6446\u597d\u9635\u5f0f\u3002 \u3000\u3000\u90a3\u767e\u4f59\u4eba\u7acb\u5373\u8fc5\u901f\u65e0\u6bd4\u7684\u56f4\u6210\u4e00\u4e2a\u534a\u5706\uff0c\u76f8\u8ddd\u949f\u4e00\u8c6a\u7b49\u4ec5\u6709\u56db\u4e94\u4e08\u8ddd\u79bb\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u6717\u58f0\u9053\uff1a\u201c\u970d\u76df\u4e3b\uff0e\u4f60\u7387\u4f17\u56f4\u4f4f\u767d\u59d1\u5bfa\uff0c\u7a76\u7adf\u662f\u4f55\u7528\u5fc3\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u51b7\u7136\u7b11\u9053\uff1a\u201c\u5e94\u8be5\u662f\u8001\u592b\u5148\u95ee\u4f60\u624d\u5bf9\uff0c\u6628\u665a\u4f60\u4eec\u524d\u5f80\u8ff7\u8e2a\u8c37\u52ab\u7262\uff0c\u53c8\u6709\u4ec0\u4e48\u7528 \u610f\uff1f\u201d \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u6628\u665a\u8d35\u8c37\u6709\u8c01\u89c1\u5230\u8fc7\u949f\u67d0\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u62ac\u624b\u4e00\u6307\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u96be\u9053\u6709\u8fd9\u5c0f\u5b50\u8fd8\u4e0d\u7b97\u6570\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u4e00\u633a\u80f8\u9053\uff1a\u201c\u970d\u5143\u4f3d\uff0c\u8001\u738b\u516b\u86cb\uff01\u4e0d\u9519\uff0c\u5728\u4e0b\u66fe\u8fdb\u8ff7\u8e2a\u8c37\u52ab\u8fc7\u7262\uff0c\u96be\u9053\u8fd8\u6709\u4ec0\u4e48 \u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u201c\u4f60\u8fd9\u5c0f\u5b50\u51ed\u4ec0\u4e48\u52ab\u7262\uff1f\u201d \u3000\u3000\u201c\u4f60\u8fd9\u8001\u738b\u516b\u86cb\u53c8\u51ed\u4ec0\u4e48\u628a\u4ed6\u4eec\u5173\u5728\u7262\u91cc\uff1f\u201d \u3000\u3000\u201c\u4ed6\u4eec\u5b58\u5fc3\u548c\u8001\u592b\u4e3a\u654c\uff0c\u8001\u592b\u6ca1\u6740\u4ed6\u4eec\uff0c\u5df2\u7ecf\u662f\u5bbd\u5927\u4e3a\u6000\u4e86\uff01\u201d \u3000\u3000\u201c\u653e\u4f60\u5988\u7684\u72d7\u5c41\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u5c82\u80fd\u5fcd\u5f97\u4e0b\u8fd9\u53e3\u6c14\uff0e\u521a\u4e0b\u4ee4\u624b\u4e0b\u4eba\u51b2\u6740\u8fc7\u53bb\uff0c\u5374\u542c\u641c\u9b42\u624b\u5df4\u5929\u4e49\u9053\uff1a\u201c\u76df\u4e3b\uff0c\u5148 \u95ee\u95ee\u4ed6\u4eec\u4eba\u5728\u54ea\u91cc\u518d\u8bf4\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u5f3a\u81ea\u4fdd\u6301\u7740\u9547\u5b9a\u9053\uff1a\u201c\u9ea6\u5c0f\u660e\uff0c\u6628\u665a\u4f60\u4eec\u628a\u52ab\u6765\u7684\u4eba\u90fd\u85cf\u5728\u4ec0\u4e48\u5730\u65b9\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u85cf\u5728\u8ff7\u8e2a\u8c37\u5929\u661f\u9662\u91cc\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u4e0d\u7531\u8138\u4e0a\u9752\u7b4b\u66b4\u8d77\uff0c\u8f6c\u5934\u9053\uff1a\u201c\u4f60\u4eec\u54ea\u4e2a\u628a\u8fd9\u5c0f\u5b50\u62ff\u4e0b\uff01\u201d \u3000\u3000\u4f46\u89c1\u9ed1\u9b54\u65f6\u5bc5\u8d8a\u4f17\u800c\u51fa\u9053\uff1a\u201c\u76df\u4e3b\uff0c\u65f6\u67d0\u66fe\u5403\u8fc7\u8fd9\u5c0f\u5b50\u7684\u4e8f\uff0c\u4e00\u76f4\u96be\u51cf\u5fc3\u5934\u4e4b\u6068\uff0c\u5c31\u8ba9 \u65f6\u67d0\u6765\u6536\u62fe\u8fd9\u5c0f\u5b50\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u51b7\u7b11\u9053\uff1a\u201c\u65f6\u5bc5\uff0c\u5728\u4e0b\u8fde\u970d\u5143\u4f3d\u90a3\u8001\u738b\u516b\u86cb\u90fd\u4e0d\u5728\u4e4e\uff0c\u96be\u9053\u8fd8\u4f1a\u6015\u4e86\u4f60\uff01\u201d \u3000\u3000\u65f6\u5bc5\u56e0\u5df2\u5728\u8fd0\u96c6\u529f\u529b\uff0c\u65e0\u6cd5\u5f00\u53e3\u8bb2\u8bdd\uff0c\u53ea\u80fd\u6012\u76ee\u76f8\u89c6\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u7b11\u9053\uff1a\u201c\u770b\u4ec0\u4e48\uff01\u8fde\u4f60\u5c0f\u7956\u5b97\u90fd\u4e0d\u8ba4\u8bc6\uff0c\u53ef\u89c1\u4f60\u8fd9\u738b\u516b\u86cb\u771f\u4e0d\u662f\u4e1c\u897f\uff01\u201d \u3000\u3000\u65f6\u5bc5\u5df2\u7ecf\u8fd0\u597d\u638c\u529b\uff0c\u7acb\u5373\u903c\u8fd1\u8fc7\u6765\u3002 \u3000\u3000\u8c01\u90fd\u4e0d\u96be\u60f3\u5230\uff0c\u4ed6\u8fd9\u9ed1\u7802\u638c\u8ddd\u79bb\u8d8a\u8fd1\uff0c\u5a01\u529b\u8d8a\u5927\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u81ea\u662f\u4e0d\u613f\u786c\u78b0\u786c\u548c\u4ed6\u63a5\u638c\uff0c\u968f\u5373\u5fae\u4fa7\u8dc3\u9000\u3002 \u3000\u3000\u65f6\u5bc5\u5374\u5982\u5f71\u968f\u5f62\uff0c\u76f4\u5411\u9ea6\u5c0f\u660e\u8ffd\u53bb\uff0c\u8feb\u5f97\u9ea6\u5c0f\u660e\u4e0d\u5f97\u4e0d\u5c55\u5f00\u6458\u661f\u6b65\u8fce\u654c\u3002 \u3000\u3000\u4e8e\u662f\uff0c\u3000\u4e8c\u4eba\u5f62\u6210\u50cf\u8d70\u706f\u822c\u5728\u5927\u7ea6\u4e8c\u4e09\u4e08\u65b9\u5706\u8303\u56f4\u5185\u4e0d\u4f4f\u95ea\u8f6c\u56de\u65cb\u3002 \u3000\u3000\u5ffd\u89c1\u9ed1\u9b54\u65f6\u5bc5\u53f3\u638c\u4e00\u626c\uff0c\u8f70\u7136\u4e00\u58f0\u5de8\u54cd\uff0c\u4e00\u80a1\u72c2\u731b\u65e0\u6bd4\u7684\u9ed1\u98ce\uff0c\u76f4\u5411\u9ea6\u5c0f\u660e\u5f53\u5934\u6d8c\u53bb\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u6025\u6025\u6ed1\u6b65\u65cb\u8eab\uff0c\u90a3\u80a1\u9ed1\u98ce\u6b63\u597d\u7531\u8eab\u4fa7\u63a0\u8fc7\uff0c\u63a5\u7740\u4ee5\u8fc5\u96f7\u4e0d\u53ca\u63a9\u8033\u4e4b\u52bf\uff0c\u3000\u4e00\u5251\u5411 \u9ed1\u9b54\u65f6\u5bc5\u524d\u80f8\u523a\u53bb\u3002 \u3000\u3000\u65f6\u5bc5\u65e0\u6687\u77ed\u65f6\u95f4\u518d\u8fd0\u638c\u529b\uff0c\u614c\u9a87\u4e4b\u4e0b\uff0e\u53ea\u5f97\u4e5f\u6025\u6025\u5411\u4e00\u65c1\u95ea\u8eb2\u3002 \u3000\u3000\u9976\u4ed6\u52a8\u4f5c\u591f\u5feb\uff0c\u5de6\u808b\u5916\u7f18\u4ecd\u88ab\u5251\u5c16\u523a\u4e2d\uff0c\u987f\u65f6\u9c9c\u8840\u6d94\u6d94\u800c\u4e0b\u3002 \u3000\u3000\u4e8c\u4eba\u5404\u51fa\u7edd\u5b66\uff0c\u53cc\u65b9\u4e00\u8f6e\u5feb\u653b\uff0e\u3000\u5341\u4f59\u62db\u8fc7\u540e\uff0e\u65f6\u5bc5\u5df2\u6e10\u611f\u4e0d\u654c\u3002 \u3000\u3000\u800c\u9ea6\u5c0f\u660e\u5251\u62db\u5374\u8d8a\u6765\u8d8a\u5feb\uff0c\u8feb\u5f97\u65f6\u5bc5\u8d8a\u53d1\u624b\u5fd9\u811a\u4e71\u3002 \u3000\u3000\u53ea\u542c\u6bd2\u706b\u6210\u5168\u53eb\u9053\uff1a\u201c\u65f6\u5144\u5feb\u9000\u56de\u6765\uff0c\u7531\u6211\u6765\u5bf9\u4ed8\u4ed6\uff01\u201d \u3000\u3000\u65f6\u5bc5\u7acb\u5373\u5411\u540e\u8dc3\u9000\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u521a\u8981\u8ffd\u88ad\uff0c\u7a81\u89c1\u6bd2\u706b\u6210\u5168\u53f3\u81c2\u4e00\u626c\uff0c\u4e00\u9897\u9f99\u773c\u5927\u5c0f\u7684\u7d2b\u8272\u5706\u7403\u631f\u7740\u9510\u98ce\u6025\u63a0\u800c\u81f3\u3002 \u3000\u3000\u4ed6\u53ea\u9053\u8fd9\u662f\u6bd2\u706b\u6210\u5168\u53d1\u6765\u7684\u6697\u5668\uff0c\u8fde\u5fd9\u62a1\u5251\u5288\u53bb\u3002 \u3000\u3000\u4f46\u95fb\u201c\u94ee\u201d\u7136\u2014\u58f0\u9707\u54cd\uff0e\u90a3\u7d2b\u8272\u5706\u7403\u5c45\u7136\u7206\u70b8\u5f00\u6765\uff0e\u6d8c\u51fa\u4e00\u56e2\u706b\u5149\u3002\u6709\u5982\u6b63\u6708\u5341\u4e94\u65bd\u653e \u7684\u70df\u706b\u822c\uff0e\u76f4\u7b3c\u7f69\u4e86\u4e08\u4f59\u65b9\u5706\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u867d\u95ea\u907f\u5f97\u5feb\uff0c\u8863\u670d\u4e0a\u4e5f\u6709\u6570\u5904\u88ab\u7130\u706b\u70e7\u7834\uff0c\u6240\u5e78\u6ca1\u4f24\u53ca\u76ae\u8089\u3002 \u3000\u3000\u539f\u6765\u8fd9\u6b63\u662f\u6bd2\u706b\u6210\u5168\u706b\u5668\u4e2d\u7684\u78f7\u706b\u73e0\uff0c\u4f53\u79ef\u867d\u5c0f\uff0c\u5a01\u529b\u5374\u5927\u5f97\u60ca\u4eba\u3002 \u3000\u3000\u6bd2\u706b\u6210\u5168\u8863\u4e00\u679a\u78f7\u706b\u73e0\u6254\u51fa\u4e4b\u540e\uff0c\u63a5\u7740\u53c8\u662f\u4e09\u679a\uff0c\u201c\u54bb\u54bb\u54bb\u201d\u51e0\u4e4e\u540c\u65f6\u51fa\u624b\u3002 \u3000\u3000\u8fd9\u6b21\u4ed6\u5e76\u672b\u6254\u5411\u9ea6\u5c0f\u660e\u8eab\u4e0a\uff0c\u800c\u662f\u6254\u5411\u9ea6\u5c0f\u660e\u8eab\u524d\u5730\u4e0a\u4e24\u679a\uff0e\u8eab\u540e\u5730\u4e0a\u2014\u679a\u3002 \u3000\u3000\u663e\u7136\uff0c\u4ed6\u662f\u5b58\u5fc3\u4e0d\u8ba9\u9ea6\u5c0f\u660e\u9003\u51fa\u706b\u529b\u8303\u56f4\u4e4b\u5916\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u6599\u5b9a\u78f7\u706b\u73e0\u7206\u70b8\u4e4b\u540e\uff0c\u706b\u529b\u548c\u5f39\u7247\u5fc5\u5b9a\u5411\u4e0a\u65b9\u7206\u6563\uff0c\u82e5\u8dc3\u5411\u534a\u7a7a\uff0e\u975e\u88ab\u788e\u7247\u51fb\u4e2d \u4e0d\u53ef\u3002 \u3000\u3000\u53ea\u597d\u8fc5\u901f\u5730\u5c31\u5730\u2014\u5367\uff0c\u5f85\u78f7\u706b\u73e0\u7206\u5f00\u540e\uff0c\u518d\u5012\u7eb5\u5411\u540e\u8dc3\u9000\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u7559\u5728\u9ea6\u5c0f\u660e\u8eab\u540e\u7684\u949f\u2014\u8c6a\u7b49\u4e94\u4eba\uff0c\u4e5f\u88ab\u8feb\u7eb7\u7eb7\u540e\u9000\u3002 \u3000\u3000\u970d\u5143\u4f3d\u89c1\u673a\u4e0d\u53ef\u5931\uff0c\u7acb\u5373\u4e00\u626c\u624b\uff0c\u62db\u547c\u624b\u4e0b\u767e\u4f59\u4eba\u63a9\u6740\u8fc7\u6765\u3002 \u3000\u3000\u5f53\u9ea6\u5c0f\u660e\u3001\u949f\u4e00\u8c6a\u7b49\u4eba\u7a7f\u8fc7\u6811\u6797\uff0c\u9000\u5230\u5bfa\u95e8\u9644\u8fd1\uff0e\u53ea\u89c1\u5929\u7985\u3001\u5929\u671b\u4e24\u5927\u5e08\u5df2\u5404\u6301\u94c1\u7985\u6756\uff0c \u7ad9\u5728\u5bfa\u95e8\u524d\u3002 \u3000\u3000\u90a3\u4e8c\u5341\u56db\u540d\u5c11\u6797\u50e7\u4eba\uff0e\u4e5f\u90fd\u6392\u6210\u2014\u5217\uff0e\u8083\u7acb\u5728\u5929\u7985\u3001\u5929\u671b\u8eab\u540e\u3002 \u3000\u3000\u970d\u5143\u4f3d\u7387\u9886\u624b\u4e0b\u8ffd\u8fc7\u6811\u6797\uff0e\u4e00\u89c1\u6709\u5c11\u6797\u50e7\u4eba\u52a9\u9635\uff0c\u4fbf\u518d\u793a\u610f\u624b\u4e0b\u4eba\u5c31\u5730\u505c\u4f4f\uff0c\u4e00\u9762\u9ad8\u53eb \u9053\uff1a\u201c\u5929\u7985\u8001\u548c\u5c1a\uff0c\u539f\u6765\u4ed6\u4eec\u6709\u5c11\u6797\u65b9\u9762\u6491\u8170\uff0c\u624d\u6562\u8fd9\u6837\u80c6\u5927\u5984\u4e3a\uff01\u201d \u3000\u3000\u5929\u7985\u4eba\u5e08\u5355\u638c\u7acb\u80f8\uff0c\u8bf5\u4e86\u58f0\u4f5b\u53f7\u9053\uff1a\u201c\u970d\u76df\u4e3b\u4f1a\u9519\u610f\u4e86\uff0e\u4f60\u4eec\u53cc\u65b9\u7684\u4e8b\uff0c\u8001\u8872\u7edd\u4e0d\u63d2\u624b\uff0c \u5c11\u6797\u4e00\u5411\u5bf9\u7eff\u6797\u7eb7\u4e89\u4fdd\u6301\u4e2d\u7acb\uff0e\u8fd9\u6b21\u4e5f\u5e76\u4e0d\u4f8b\u5916\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0e\u8d35\u65b9\u4e3a\u4ec0\u4e48\u5217\u9635\u4ee5\u5f85\uff1f\u201d \u3000\u3000\u5929\u7985\u5927\u5e08\u9053\uff1a\u201c\u767d\u59d1\u5bfa\u4e43\u662f\u655d\u6d3e\u5c11\u6797\u672c\u9662\u7684\u2014\u5904\u5206\u652f\u5e99\u5b87\uff0e\u4efb\u4f55\u5916\u6765\u6b66\u529b\u4e0d\u5f97\u4fb5\u5165\u3002\u8001 \u8bb7\u73b0\u5728\u53ea\u662f\u7387\u9886\u5f1f\u5b50\u4fdd\u62a4\u767d\u59d1\u5bfa\uff0c\u5e76\u975e\u4e0e\u970d\u76df\u4e3b\u4e3a\u654c\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u51b7\u7b11\u9053\uff1a\u201c\u8fd9\u6837\u770b\u6765\uff0c\u4f60\u4eec\u662f\u4e0d\u51c6\u8001\u592b\u6740\u8fdb\u5bfa\u91cc\u53bb\u4e86\uff1f\u201d \u3000\u3000\u5929\u7985\u5927\u5e08\u9053\uff1a\u201c\u970d\u76df\u4e3b\u77e5\u9053\u5c31\u597d\uff0e\u201d \u3000\u3000\u201c\u5929\u7985\u8001\u548c\u5c1a\uff0e\u4ed6\u4eec\u4e3a\u4ec0\u4e48\u53ef\u4ee5\u8fdb\u5165\u767d\u59d1\u5bfa\uff1f\u201d \u3000\u3000\u201c\u4ed6\u4eec\u53ea\u662f\u5728\u767d\u59d1\u5bfa\u6682\u4f4f\u3001\u5e76\u672a\u95f9\u3002\u201d \u3000\u3000\u201c\u5982\u679c\u8001\u592b\u975e\u6740\u8fdb\u53bb\u4e0d\u53ef\u5462\uff1f\u201d \u3000\u3000\u201c\u8001\u8872\u4e3a\u4e86\u4fdd\u62a4\u767d\u59d1\u5bfa\uff0c\u53ea\u597d\u88ab\u8feb\u5f00\u6b21\u6740\u6212\u4e86\uff01\u201d \u3000\u3000\u201c\u4f60\u53ef\u77e5\u9053\u4ed6\u4eec\u90fd\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u201c\u4ed6\u4eec\u539f\u672c\u4e5f\u662f\u8ff7\u8e2a\u8c37\u7684\u4eba\uff0c\u53ea\u56e0\u970d\u76df\u4e3b\u5bb9\u4e0d\u4e0b\u4ed6\u4eec\uff0c\u6240\u4ee5\u624d\u4e0d\u5f97\u4e0d\u64a4\u51fa\u8ff7\u8e2a\u8c37\u3002\u201d \u3000\u3000\u201c\u4ed6\u4eec\u6839\u672c\u662f\u53db\u9006\uff01\u82e5\u975e\u53db\u9006\uff0c\u600e\u4f1a\u4e0d\u542c\u8001\u592b\u8282\u5236\uff0c\u53cd\u51fa\u8ff7\u8e2a\u8c37\uff1f\u201d \u3000\u3000\u201c\u90a3\u662f\u8d35\u8c37\u81ea\u5df1\u7684\u4e8b\uff0c\u8001\u8872\u4e0d\u4fbf\u7f6e\u5599\u3002\u201d \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u4e00\u679a\u78f7\u706b\u73e0\uff0c\u76f4\u5411\u5929\u7985\u5927\u5e08\u7838\u6765\u3002 \u3000\u3000\u5929\u7985\u5927\u5e08\u94c1\u7985\u6756\u731b\u7136\u4e00\u6325\uff0c\u76f4\u628a\u78f7\u706b\u73e0\u51fb\u5f97\u5012\u98de\u56de\u53bb\u3002 \u3000\u3000\u6bd2\u706b\u6210\u5168\u4e00\u89c1\u4e0d\u5999\uff0c\u5927\u53eb\u9053\uff1a\u201c\u5feb\u8eb2\uff01\u201d \u3000\u3000\u8bf4\u65f6\u8fdf\uff0e\u90a3\u65f6\u5feb\uff0c\u78f7\u706b\u73e0\u7206\u5f00\u4e4b\u540e\uff0c\u7adf\u70e7\u4f24\u4e86\u597d\u51e0\u540d\u55bd\u5175\u3002 \u3000\u3000\u8fd9\u4e00\u6765\uff0c\u6bd2\u706b\u6210\u5168\u53cd\u800c\u56e0\u8d2a\u529f\u62db\u4e86\u7978\uff0e\u4ee5\u81f4\u4e0d\u6562\u518d\u8f7b\u4e3e\u5984\u52a8\u3002 \u3000\u3000\u679c\u7136\uff0e\u8fde\u970d\u5143\u4f3d\u4e5f\u5bf9\u6bd2\u706b\u6210\u5168\u5927\u4e3a\u4e0d\u6ee1\uff0c\u51b7\u54fc\u4e00\u58f0\u9053\uff1a\u201c\u6ca1\u6709\u8001\u592b\u793a\u610f\uff0c\u6210\u5927\u4fa0\u6700\u597d\u522b \u64c5\u81ea\u51fa\u624b\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u4e0d\u613f\u5c11\u6797\u65b9\u9762\u53d7\u5230\u7275\u8fde\uff0c\u8fd9\u65f6\u53c8\u8d8a\u4f17\u800c\u51fa\u9053\uff1a\u201c\u970d\u5143\u4f3d\uff0c\u4f9d\u4ed7\u4eba\u591a\u52bf\u4f17\uff0c\u7b97\u4e0d\u5f97 \u672c\u9886\uff0c\u5728\u4e0b\u73b0\u5728\u5c31\u5355\u5355\u5411\u4f60\u6311\u6218\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u563f\u563f\u8fde\u7b11\u51e0\u58f0\u9053\uff1a\u201c\u8001\u592b\u8eab\u4e3a\u5929\u4e0b\u7eff\u6797\u76df\u4e3b\uff0c\u624b\u4e0b\u6218\u5c06\u5982\u4e91\uff0c\u5bf9\u4ed8\u4f60\u8fd9\u79cd\u540e\u751f\u665a \u8f88\uff0c\u4f55\u7528\u4eb2\u81ea\u51fa\u624b\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u5c31\u53ea\u7ba1\u6d3e\u51fa\u9ad8\u624b\uff0c\u53ea\u8981\u662f\u4e00\u5bf9\u4e00\u5355\u6253\u72ec\u6597\uff0c\u4efb\u4f55\u4eba\u90fd\u6ca1\u653e\u5728\u5728\u4e0b\u773c \u91cc\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u8f6c\u5934\u5429\u5490\u5df4\u5929\u4e49\u9053\uff1a\u201c\u628a\u8fd9\u5c0f\u5b50\u7684\u56db\u540d\u5e08\u5144\u5e26\u8fc7\u6765\u3002\u201d \u3000\u3000\u5df4\u5929\u4e49\u5411\u540e\u4e00\u62db\u624b\uff0c\u679c\u7136\uff0c\u5728\u540e\u9762\u4eba\u4e1b\u4e2d\u8d70\u51fa\u94c1\u62d0\u8feb\u9b42\u7f57\u94ee\u3001\u3000\u91d1\u5200\u9b3c\u68ad\u4e01\u4e00\u9b42\u3001\u51b7\u773c \u795e\u67ad\u9ad8\u78f7\u548c\u53cc\u624b\u5957\u7740\u91d1\u73af\u7684\u5f20\u656c\u5b89\u3002 \u3000\u3000\u539f\u6765\u970d\u5143\u4f3d\u628a\u8fd9\u56db\u4eba\u2014\u76f4\u9690\u85cf\u5728\u4f17\u4eba\u4e4b\u540e\uff0c\u4ed6\u4eec\u65e2\u662f\u4f0f\u5175\uff0c\u4e5f\u662f\u4e13\u95e8\u5bf9\u4ed8\u9ea6\u5c0f\u660e\u7684\u6cd5\u5b9d\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u987f\u65f6\u6697\u611f\u5fc3\u60ca\uff0c\u4ed6\u8fd9\u56db\u540d\u5e08\u5144\uff0c\u5f53\u5e74\u6b66\u529f\u90fd\u5728\u4ed6\u4e4b\u4e0a\uff0e\u867d\u7136\u76ee\u524d\u81ea\u5df1\u6b66\u529f\u5927\u8fdb\uff0c \u800c\u4e14\u4e0a\u6b21\u5df2\u51fb\u8d25\u5f20\u656c\u5b89\u3002 \u3000\u3000\u4f46\u662f\u5426\u80fd\u6218\u8fc7\u53e6\u5916\u4e09\u4eba\uff0c\u6b64\u523b\u5374\u96be\u4ee5\u9884\u6599\u3002\u5c24\u5176\u4ed6\u4eec\u56db\u4eba\u5168\u662f\u81ea\u5df1\u7684\u5e08\u5144\uff0c\u81ea\u5df1\u51fa\u624b\u65f6 \u5fc5\u987b\u6709\u6240\u987e\u5fcc\uff0c\u4e00\u65e6\u4f24\u4e86\u4ed6\u4eec\uff0c\u5b9e\u5728\u4e8e\u5fc3\u4e0d\u5fcd\u3002 \u3000\u3000\u53ea\u542c\u970d\u5143\u4f3d\u559d\u4ee4\u5f20\u656c\u5b89\u9053\uff1a\u201c\u7ed9\u8001\u592b\u628a\u8fd9\u5c0f\u5b50\u62ff\u4e0b\uff0c\u62ff\u4e0d\u4e0b\u6d3b\u7684\uff0c\u62ff\u6b7b\u7684\u4e5f\u662f\u4e00\u6837\uff01\u201d \u3000\u3000\u539f\u6765\u970d\u5143\u4f3d\u5728\u548c\u949f\u4e00\u8c6a\u4e89\u593a\u7eff\u6797\u76df\u4e3b\u65f6\uff0c\u66fe\u4ee5\u5f20\u656c\u5b89\u51fb\u8d25\u8fc7\u9ea6\u5c0f\u660e\uff0c\u6545\u800c\u540e\u6765\u5f20\u656c\u5b89\u8d25 \u5728\u9ea6\u5c0f\u660e\u624b\u4e0b\uff0c\u4ed6\u59cb\u7ec8\u611f\u5230\u6000\u7591\u3002 \u3000\u3000\u56e0\u4e4b\uff0c\u624d\u51b3\u5b9a\u5148\u8ba9\u5f20\u656c\u5b89\u51fa\u6218\uff0c\u4ee5\u4fbf\u770b\u770b\u4e8c\u4eba\u7a76\u7adf\u8c01\u9ad8\u8c01\u4e0b\u3002 \u3000\u3000\u5f53\u4e0b\uff0c\u5f20\u656c\u5b89\u7f13\u7f13\u8d70\u51fa\uff0c\u5728\u9ea6\u5c0f\u660e\u8eab\u524d\u516b\u5c3a\u5904\u505c\u4f4f\uff0c\u4e24\u773c\u5446\u76f4\u7684\u5411\u524d\u5e73\u89c6\uff0c\u4f3c\u4e4e\u5df2\u5728\u84c4 \u52bf\u4ee5\u5f85\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u8f7b\u54b3\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u5f20\u5e08\u5144\uff0c\u4f60\u5f53\u771f\u4e0d\u8ba4\u8bc6\u5c0f\u5f1f\u5417\uff1f\u201d \u3000\u3000\u5f20\u656c\u5b89\u53ea\u662f\u4e24\u773c\u7728\u4e86\u51e0\u7728\uff0c\u4f3c\u662f\u542c\u4e0d\u61c2\u9ea6\u5c0f\u660e\u5728\u8bf4\u4e9b\u4ec0\u4e48\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u518d\u9053\uff1a\u201c\u4f60\u73b0\u5728\u7518\u5fc3\u542c\u970d\u5143\u4f3d\u9a71\u4f7f\uff0c\u4e3a\u864e\u4f5c\u4f25\uff0c\u5b9e\u5728\u4e0d\u503c\u5f97\uff0c\u5c0f\u5f1f\u771f\u4e0d\u77e5\u7528\u4ec0\u4e48 \u529e\u6cd5\u624d\u80fd\u4f7f\u4f60\u6e05\u9192\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u559d\u9053\uff1a\u201c\u5f20\u656c\u5b89\uff0c\u7ad9\u5728\u90a3\u91cc\u7b49\u4ec0\u4e48\uff1f\u8fd8\u4e0d\u5feb\u5feb\u5c06\u4ed6\u62ff\u4e0b\uff01\u201d \u3000\u3000\u8bf4\u4e5f\u5947\u602a\uff0c\u5f20\u656c\u5b89\u5bf9\u970d\u5143\u4f3d\u7684\u8bdd\uff0c\u5374\u4f3c\u80fd\u5b8c\u5168\u542c\u61c2\uff0c\u6b3a\u8eab\u4e00\u7eb5\uff0e\u626c\u8d77\u4e24\u679a\u91d1\u73af\uff0c\u95ea\u7535\u822c \u76f4\u5411\u9ea6\u5c0f\u660e\u7838\u53bb\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u660e\u77e5\u4ed6\u51fa\u624b\u65f6\u62db\u5f0f\u6700\u4e3a\u9510\u5229\uff0c\u6025\u6025\u5411\u4e00\u4fa7\u95ea\u53bb\u3002 \u3000\u3000\u5f20\u656c\u5b89\u4e00\u62db\u843d\u7a7a\uff0c\u811a\u4e0b\u4e00\u65cb\u4e00\u6ed1\uff0c\u91d1\u73af\u518d\u5411\u9ea6\u5c0f\u660e\u5f53\u5934\u51fb\u4e0b\uff0c\u6765\u52bf\u5feb\u5f97\u4ee4\u4eba\u76ee\u4e0d\u6687\u63a5\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u60c5\u6025\u4e4b\u4e0b\uff0c\u5df2\u987e\u4e0d\u5f97\u662f\u5426\u4f24\u7740\u5bf9\u65b9\uff0c\u731b\u8d77\u4e00\u5251\uff0c\u5411\u5f20\u656c\u5b89\u53f3\u624b\u91d1\u73af\u5288\u53bb\uff0c\u540c\u65f6\u5de6 \u624b\u4e5f\u5f39\u51fa\u4e00\u7f15\u6307\u98ce\uff0c\u76f4\u88ad\u524d\u80f8\u3002 \u3000\u3000\u53ea\u542c\u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c\u5251\u3001\u73af\u4e00\u63a5\uff0c\u90a3\u91d1\u73af\u7acb\u523b\u88ab\u9707\u98de\u8d77\u4e8c\u4e08\u591a\u9ad8\uff0c\u7136\u540e\u659c\u629b\u5411\u8fdc\u5904\u5730\u4e0a\u3002 \u3000\u3000\u90a3\u7f15\u5f3a\u52b2\u7684\u6307\u98ce\uff0c\u4e5f\u88ad\u4e2d\u5f20\u656c\u5b89\u524d\u80f8\u201c\u4e2d\u5ead\u7a74\u201d\u3002 \u3000\u3000\u5f20\u656c\u5b89\u4ef0\u8eab\u5012\u5730\u4e4b\u540e\uff0c\u53f3\u624b\u4e5f\u6ee1\u662f\u9c9c\u8840\uff0c\u8fd9\u8840\u6c61\u5e76\u975e\u9ea6\u5c0f\u660e\u5251\u52bf\u6240\u4f24\uff0c\u800c\u662f\u91d1\u73af\u8131\u624b\u65f6 \u88ab\u91d1\u73af\u6240\u649e\u3002 \u3000\u3000\u970d\u5143\u4f3d\u4e00\u89c1\u5f20\u656c\u5b89\u5012\u5730\u4e0d\u8d77\uff0c\u9664\u547d\u4eba\u8dc3\u5165\u573a\u4e2d\u628a\u5f20\u656c\u5b89\u62a2\u56de\uff0c\u518d\u547d\u51b7\u773c\u795e\u67ad\u9ad8\u5d99\u51fa\u6218\u3002 \u3000\u3000\u51b7\u773c\u795e\u67ad\u9ad8\u5d99\u60ef\u7528\u7684\u5175\u5200\u662f\u4e00\u5bf9\u5929\u661f\u7b14\uff0e\u6b64\u4eba\u4e00\u5411\u795e\u6001\u51b7\u5cfb\uff0c\u8138\u4e0a\u4ece\u65e0\u7b11\u610f\uff0c\u80a4\u8272\u5947\u767d\uff0c \u76ee\u5149\u9510\u5229\u6151\u4eba\uff0e\u56e0\u4e4b\u624d\u5f97\u4e86\u4e2a\u51b7\u773c\u795e\u67ad\u7684\u7ef0\u53f7\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u660e\u77e5\u9ad8\u5d99\u4e5f\u670d\u8fc7\u201c\u5411\u5fc3\u9732\u201d\uff0c\u8fd8\u662f\u62b1\u5251\u4e00\u793c\uff0c\u8bd5\u63a2\u7740\u95ee\u9053\uff1a\u201c\u9ad8\u5e08\u5144\uff0c\u4f60\u4e00\u5411\u5fc3 \u601d\u654f\u9510\uff0c\u7cbe\u660e\u80fd\u5e72\uff0c\u96be\u9053\u4e5f\u7518\u5fc3\u53d7\u970d\u5143\u4f3d\u90a3\u8001\u738b\u516b\u86cb\u9a71\u4f7f\uff1f\u201d \u3000\u3000\u8c01\u77e5\u9ad8\u5d99\u7adf\u7136\u5f00\u4e86\u53e3\uff0c\u51b7\u51b7\u4e00\u7b11\u9053\uff1a\u201c\u970d\u76df\u4e3b\u5728\u7eff\u6797\u4e2d\u884c\u4fa0\u4ed7\u4e49\uff0c\u6211\u5bf9\u4ed6\u65e9\u5c31\u4ef0\u6155\u5d07\u62dc\uff0c \u4e3a\u4ed6\u6548\u547d\uff0c\u53c8\u6709\u4ec0\u4e48\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u5927\u4e3a\u60ca\u6115\u9053\uff1a\u201c\u83ab\u975e\u9ad8\u5e08\u5144\u5e76\u672a\u4e3a\u2018\u5411\u5fc3\u9732\u2019\u6240\u8ff7\uff1f\u201d \u3000\u3000\u9ad8\u5d99\u563f\u563f\u7b11\u9053\uff1a\u201c\u5c0f\u660e\uff0c\u4f60\u770b\u6211\u4e0d\u662f\u597d\u597d\u7684\u5417\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u4e0d\u89c9\u5fc3\u4e2d\u4e00\u80a1\u70ed\u8840\u76f4\u51b2\uff0e\u53f1\u9053\uff1a\u201c\u539f\u6765\u4f60\u662f\u7518\u5fc3\u8ba4\u8d3c\u4f5c\u7236\uff01\u201d \u3000\u3000\u9ad8\u5d99\u9053\uff1a\u201c\u5c0f\u660e\uff0c\u8bc6\u53eb\u52a1\u8005\u4e3a\u4fca\u6770\u3002\u53ea\u8981\u4f60\u80fd\u8bda\u5fc3\u8bda\u610f\u5f52\u987a\u76df\u4e3b\uff0c\u6211\u62c5\u4fdd\u970d\u76df\u4e3b\u7edd\u4e0d\u8ffd \u7a76\u4f60\u7684\u8fc7\u5f80\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u559d\u9053\uff1a\u201c\u4e00\u6d3e\u80e1\u8a00\uff01\u5728\u4e0b\u73b0\u5728\u5df2\u4e0d\u518d\u8ba4\u4f60\u8fd9\u540d\u5e08\u5144\u3002\u9ad8\u5d99\uff0c\u770b\u5728\u4f60\u5e74\u957f\uff0c\u5728\u4e0b\u8ba9 \u4f60\u5148\u8fdb\u62db\uff01\u201d \u3000\u3000\u9ad8\u5d99\u54fc\u4e86\u58f0\u9053\uff1a\u201c\u5927\u80c6\u53db\u9006\uff0c\u7adf\u7136\u6562\u548c\u6211\u52a8\u624b\uff1f\u4f60\u81ea\u4fe1\u80fd\u80dc\u5f97\u8fc7\u6211\u5417\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u80dc\u4e0d\u80dc\u5f97\u4e86\u4f60\u662f\u53e6\u5916\u4e00\u56de\u4e8b\uff0c\u4f46\u81f3\u5c11\u8fd8\u6015\u4e0d\u4e86\u4f60\uff01\u201d \u3000\u3000\u9ad8\u5d99\u9053\uff1a\u201c\u597d\u5427\uff01\u6211\u65e9\u5c31\u542c\u8bf4\u4f60\u8d8a\u6765\u8d8a\u72c2\uff0c\u4eca\u5929\u5c31\u597d\u597d\u6559\u8bad\u6559\u8bad\u4f60\u3002\u201d \u3000\u3000\u4ed6\u8bdd\u58f0\u672a\u843d\uff0c\u4e00\u5bf9\u5929\u661f\u7b14\u95ea\u7535\u60ca\u9e3f\u822c\u70b9\u4e86\u8fc7\u6765\uff0e\u4e0a\u70b9\u201c\u5929\u5e9c\u7a74\u201d\uff0c\u3000\u4e0b\u70b9\u201c\u6c14\u6d77\u7a74\u201d\uff0c \u2014\u53d1\u4e4b\u95f4\uff0c\u53cc\u7ba1\u9f50\u4e0b\uff0c\u4f3c\u662f\u4e00\u51fa\u624b\u5c31\u60f3\u7f6e\u9ea6\u5c0f\u660e\u4e8e\u6b7b\u5730\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u5438\u6c14\u5012\u9000\u4e09\u5c3a\uff0c\u53f3\u624b\u957f\u5251\u4e00\u62db\u201c\u4e91\u52fe\u5bd2\u6708\u201d\uff0e\u5de6\u624b\u4e5f\u8d81\u52bf\u89e3\u4e0b\u8170\u4e2d\u8f6f\u97ad\uff0c\u201c\u5530\u201d \u7684\u4e00\u58f0\uff0c\u201c\u5377\u4e91\u98de\u96ea\u201d\u5411\u9ad8\u5d99\u62e6\u8170\u626b\u53bb\u3002 \u3000\u3000\u53ea\u542c\u201c\u9535\u201d\u7684\u4e00\u58f0\uff0c\u9ad8\u5d99\u5de6\u624b\u7684\u5929\u661f\u7b14\u548c\u9ea6\u5c0f\u660e\u7684\u957f\u5251\u786c\u63a5\u4e86\u2014\u62db\uff0c\u4f46\u53f3\u624b\u7684\u5929\u661f\u7b14\u5374 \u88ab\u8f6f\u97ad\u7f20\u4f4f\u3002 \u3000\u3000\u8fd9\u4e00\u6765\uff0c\u4e24\u4eba\u5df2\u7f20\u5728\u4e00\u8d77\uff0c\u9664\u4e86\u4e00\u53cc\u624b\u62fc\u547d\u62c9\u626f\uff0c\u53e6\u4e00\u53cc\u624b\u7684\u957f\u5251\u548c\u5929\u661f\u7b14\u53ea\u80fd\u8fd1\u8eab\u62fc \u640f\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u7684\u5251\u867d\u8f83\u5929\u661f\u7b14\u957f\uff0c\u7531\u4e8e\u4e24\u4eba\u76f8\u8ddd\u592a\u8fd1\uff0c\u53cd\u800c\u51fa\u62db\u4e0d\u6613\uff0c\u53d8\u5f97\u788d\u624b\u788d\u811a\u3002 \u3000\u3000\u4e24\u4eba\u4e00\u624b\u62fc\u640f\uff0c\u4e00\u624b\u62c9\u626f\uff0c\u53ea\u80fd\u5728\u539f\u5730\u5c55\u5f00\u7f20\u6597\u3002 \u3000\u3000\u4e0d\u96be\u60f3\u89c1\uff0c\u8fd9\u79cd\u6253\u6cd5\u5bf9\u9ea6\u5c0f\u660e\u6765\u8bf4\uff0c\u663e\u7136\u5403\u4e8f\u3002 \u3000\u3000\u987b\u77e5\u9ea6\u5c0f\u660e\u7684\u8eab\u624b\uff0c\u6700\u64c5\u957f\u7684\u662f\u81ea\u7531\u53d1\u6325\uff0c\u4e00\u65e6\u5728\u884c\u52a8\u4e0a\u53d7\u4e86\u62d8\u675f\uff0c\u65e0\u5f62\u4e2d\u5a01\u529b\u5927\u51cf\u3002 \u3000\u3000\u9ad8\u5d99\u65e9\u770b\u900f\u8fd9\u4e00\u7740\uff0c\u53f3\u624b\u7684\u5929\u661f\u7b14\u731b\u529b\u5411\u6000\u91cc\u4e00\u5e26\uff0c\u7adf\u628a\u9ea6\u5c0f\u660e\u5e26\u52a8\u5f97\u7ad9\u811a\u4e0d\u4f4f\uff0c\u8e09\u8dc4 \u5411\u524d\u683d\u53bb\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u5343\u94a7\u4e00\u53d1\u4e4b\u9645\uff0c\u9ea6\u5c0f\u660e\u60c5\u6025\u95f4\u5de6\u624b\u4e00\u677e\uff0c\u63a5\u7740\u518d\u4ee5\u6458\u661f\u6b65\u5411\u4e00\u4fa7\u95ea\u53bb\u3002 \u3000\u3000\u9ad8\u5d99\u6ca1\u6599\u5230\u9ea6\u5c0f\u660e\u4f1a\u677e\u624b\u629b\u6389\u8f6f\u97ad\uff0c\u4e0d\u4f46\u6233\u4e0b\u7684\u5929\u661f\u7b14\u843d\u7a7a\uff0c\u540c\u65f6\u4e5f\u7ad9\u811a\u4e0d\u7a33\uff0c\u5411\u540e\u95ea \u4e86\u4e2a\u5927\u8ddf\u5934\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u867d\u9003\u8fc7\u4e00\u52ab\uff0c\u4f46\u5728\u4f17\u76ee\u777d\u777d\u4e4b\u4e0b\uff0c\u5374\u8138\u9762\u5c3d\u5931\uff0c\u56e0\u4e3a\u5728\u5bf9\u654c\u4e4b\u95f4\u88ab\u8feb\u629b\u6389\u5175\u5203\uff0c \u81ea\u7136\u7b97\u662f\u4ef6\u5947\u803b\u5927\u8fb1\u7684\u4e8b\u3002 \u3000\u3000\u9ad8\u5d99\u7565\u2014\u5598\u606f\uff0c\u590d\u53c8\u53cc\u624b\u6325\u52a8\u7740\u5929\u661f\u7b14\u653b\u4e86\u4e0a\u6765\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u7f9e\u607c\u4e4b\u4e0b\uff0c\u731b\u5438\u4e00\u53e3\u771f\u6c14\uff0c\u4e00\u63a5\u6218\u5c31\u65bd\u51fa\u4e86\u7f01\u8863\u8001\u5c3c\u201c\u5999\u5999\u5251\u8bc0\u201d\u4e0a\u7684\u5251\u6cd5\uff0c\u987f \u65f6\u628a\u9ad8\u5d99\u7f69\u5728\u950b\u8292\u95ea\u70c1\u7684\u5251\u6c14\u4e4b\u4e2d\uff0c\u201c\u5999\u5999\u5251\u8bc0\u201d\u7684\u5251\u6cd5\uff0c\u62db\u672f\u5b9e\u5728\u592a\u5feb\uff0c\u5251\u52bf\u4e00\u51fa\uff0c\u62db\u62db \u90fd\u5982\u95ea\u7535\u5954\u96f7\uff0c\u6240\u6709\u89c2\u6218\u7684\u4eba\uff0c\u5168\u611f\u773c\u82b1\u7f2d\u4e71\u3002 \u3000\u3000\u8eab\u4e34\u5176\u5883\u7684\u9ad8\u5d99\uff0c\u66f4\u662f\u4e71\u4e86\u624b\u811a\uff0c\u8fde\u60f3\u8131\u56f0\u800c\u51fa\u90fd\u96be\u4ee5\u627e\u5230\u673a\u4f1a\uff0c\u7b80\u76f4\u5c31\u50cf\u88ab\u7f69\u5165\u5929\u7f57 \u5730\u7f51\u4e00\u822c\u3002 \u3000\u3000\u5927\u7ea6\u4e03\u516b\u62db\u8fc7\u540e\uff0c\u53ea\u542c\u201c\u545b\u90ce\u201d\u3000\u2014\u58f0\uff0e\u9ad8\u5d99\u5de6\u624b\u5929\u661f\u7b14\u5df2\u88ab\u51fb\u98de\u3002 \u3000\u3000\u63a5\u7740\u53c8\u662f\u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c\u53e6\u4e00\u679d\u5929\u661f\u7b14\u4e5f\u88ab\u9707\u5f97\u8131\u624b\u800c\u51fa\u3002 \u3000\u3000\u9ad8\u5d99\u6025\u6025\u540e\u9000\uff0e\u9ea6\u5c0f\u660e\u7684\u5251\u5c16\u5df2\u62b5\u4e0a\u4ed6\u7684\u54bd\u5589\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u51b7\u51b7\u4e00\u7b11\u9053\uff1a\u201c\u9ad8\u5d99\uff0c\u539f\u6765\u53e6\u5916\u4e09\u4f4d\u5e08\u5144\u662f\u88ab\u4f60\u6240\u63a7\u5236\u7684\u3002\u201d \u3000\u3000\u9ad8\u5d99\u76ee\u77aa\u53e3\u5446\u9053\uff1a\u201c\u9ea6\u5e08\u5f1f\uff0c\u6211\u2026\u2026\u6211\u662f\u8feb\u4e0d\u5f97\u5df2\u2026\u2026\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4f60\u795e\u5fd7\u6e05\u9192\uff0c\u54ea\u91cc\u6765\u7684\u8feb\u4e0d\u5f97\u5df2\uff1f\u201d \u3000\u3000\u201c\u9ea6\u5e08\u5f1f\uff0c\u2026\u2026\u6211\u2026\u2026\u201d \u3000\u3000\u201c\u5c11\u6765\u8fd9\u4e00\u5957\uff0c\u6211\u9ea6\u5c0f\u660e\u6839\u672c\u6ca1\u6709\u4f60\u8fd9\u79cd\u5e08\u5144\uff01\u201d \u3000\u3000\u5ffd\u89c1\u9ad8\u5d99\u5f20\u5634\u5410\u4e86\u4e00\u56e2\u767d\u6cab\uff0c\u4e24\u773c\u4e00\u76f4\uff0c\u4ef0\u8eab\u5411\u540e\u5012\u53bb\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u4e0d\u7b49\u4ed6\u5012\u4e0b\uff0e\u4e00\u8bb0\u98de\u65cb\u811a\uff0c\u8e22\u4e0a\u4ed6\u7684\u4fa7\u8170\u9053\uff1a\u201c\u9ad8\u5d99\uff0c\u4f60\u88c5\u5f97\u5f88\u4e0d\u9519\uff0c\u6211\u9ea6\u5c0f\u660e \u5c82\u80fd\u4e0a\u4f60\u7684\u5f53\uff01\u201d \u3000\u3000\u8fd9\u4e00\u811a\u7684\u529b\u9053\u7528\u5f97\u5341\u5206\u5de7\u5999\uff0c\u7adf\u628a\u9ad8\u5d99\u8e22\u5411\u5bfa\u95e8\u65b9\u5411\u3002 \u3000\u3000\u738b\u5927\u5eb7\u63a2\u624b\u63a5\u4f4f\uff0e\u518d\u628a\u9ad8\u5d99\u8d81\u673a\u70b9\u4e86\u7a74\u9053\uff0c\u7136\u540e\u4e22\u5728\u8eab\u540e\u3002 \u3000\u3000\u970d\u5143\u4f3d\u4e07\u6ca1\u6599\u5230\u5f20\u656c\u5b89\u548c\u9ad8\u5d99\u7adf\u5168\u8d25\u5728\u9ea6\u5c0f\u660e\u624b\u4e0b\uff0e\u5c24\u5176\u9ad8\u5d99\u88ab\u63b3\uff0c\u5bf9\u4ed6\u5173\u7cfb\u6700\u4e3a\u91cd\u5927\u3002 \u3000\u3000\u539f\u6765\u5728\u7f57\u4e89\u3001\u4e01\u4e00\u9b42\u3001\u9ad8\u5d99\u3001\u5f20\u656c\u5b89\u56db\u4eba\u4e2d\uff0c\u4ed6\u65e9\u5df2\u628a\u9ad8\u5d99\u6536\u5356\uff0c\u56e0\u4e4b\uff0e\u9ad8\u5d99\u5e76\u672a\u670d\u4e0b \u201c\u5411\u5fc3\u9732\u201d\uff0c\u970d\u5143\u4f3d\u6b63\u662f\u5229\u7528\u9ad8\u5d99\u6765\u63a7\u5236\u53e6\u5916\u4e09\u4eba\u7684\u3002 \u3000\u3000\u970d\u5143\u4f3d\u672c\u60f3\u53d1\u52a8\u624b\u4e0b\u767e\u4f59\u4eba\u4e00\u8d77\u8fdb\u653b\uff0c\u4f46\u6b64\u65f6\u6b64\u5730\uff0c\u5374\u53c8\u4e0d\u613f\u610f\u60f9\u8d77\u5c11\u6797\u65b9\u9762\u7684\u53cd\u51fb\uff0e \u82e5\u5f53\u771f\u5c11\u6797\u65b9\u9762\u4e5f\u53c2\u4e0e\u6218\u6597\uff0c\u5728\u4ed6\u9884\u6599\uff0e\u52bf\u5fc5\u4e24\u8d25\u4ff1\u4f24\u3002 \u3000\u3000\u970d\u5143\u4f3d\u5f53\u7136\u4e0d\u60f3\u505a\u8fd9\u79cd\u4ed8\u51fa\u4ee3\u4ef7\u592a\u5927\u7684\u4e8b\u3002 \u3000\u3000\u60f3\u5230\u8fd9\u91cc\uff0c\u53ea\u597d\u518d\u547d\u91d1\u5200\u9b3c\u68ad\u4e01\u4e00\u9b42\u8fce\u6218\u9ea6\u5c0f\u660e\u3002 \u3000\u3000\u8fd9\u5728\u9ea6\u5c0f\u660e\u6765\u8bf4\uff0c\u96be\u514d\u4e5f\u6709\u4e9b\u80c6\u602f\u3002 \u3000\u3000\u4ed6\u5171\u6709\u4e94\u4f4d\u5e08\u5144\uff0c\u9664\u5927\u5e08\u5144\u65b9\u5929\u6f9c\u5728\u5929\u53f0\u4e07\u82b1\u5bab\u5730\u7a74\u5185\u88ab\u5e9e\u58eb\u51b2\u51fb\u6bd9\u5916\uff0c\u5176\u4f59\u56db\u4f4d\u5e08\u5144 \u4f9d\u5e8f\u662f\u7f57\u94ee\u3001\u4e01\u4e00\u9b42\u3001\u9ad8\u5d99\u3001\u5f20\u656c\u5b89\uff0e\u800c\u56db\u4eba\u7684\u6b66\u529f\u9ad8\u4e0b\uff0c\u4e5f\u662f\u4f9d\u6b64\u987a\u5e8f\u6392\u5217\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u6218\u7f62\u5f20\u656c\u5b89\u518d\u6218\u9ad8\u5d99\uff0c\u5bf9\u624b\u662f\u8d8a\u6765\u8d8a\u5f3a\uff0c\u800c\u81ea\u5df1\u53c8\u8d8a\u6765\u8d8a\u7d2f\uff0c\u4e5f\u5c31\u662f\u8bf4\u8d8a\u6765\u8d8a\u96be \u5e94\u4ed8\uff0c\u8d8a\u6765\u8d8a\u611f\u5403\u529b\u3002 \u3000\u3000\u5982\u4eca\u53c8\u6362\u4e0a\u4e86\u91d1\u5200\u9b3c\u68ad\u4e01\u4e00\u9b42\uff0c\u9ea6\u5c0f\u660e\u867d\u6709\u4e9b\u602f\u610f\uff0c\u5374\u4e0d\u5f97\u4e0d\u6218\u3002 \u3000\u3000\u4ed6\u54ea\u91cc\u77e5\u9053\uff0c\u970d\u5143\u4f3d\u6b63\u662f\u7528\u8fd9\u79cd\u8f66\u8f6e\u6218\u6cd5\u6765\u5bf9\u4ed8\u4ed6\uff0c\u7b49\u4ed6\u4f53\u529b\u4e0d\u652f\u65f6\u518d\u5c06\u4ed6\u62ff\u4e0b\u3002 \u3000\u3000\u4e01\u4e00\u9b42\u521a\u8d70\u51fa\u51e0\u6b65\uff0c\u51b7\u4e0d\u9632\u4e00\u679a\u4e4c\u5149\u95ea\u4eae\u7684\u9b3c\u68ad\u75be\u5411\u9ea6\u5c0f\u660e\u524d\u80f8\u63b7\u53bb\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u6ca1\u60f3\u5230\u4e01\u4e00\u9b42\u7adf\u6697\u653e\u51b7\u7bad\uff0c\u6025\u6025\u5411\u4e00\u4fa7\u95ea\u53bb\u3002 \u3000\u3000\u90a3\u9b3c\u68ad\u4f59\u529b\u672b\u606f\uff0c\u64e6\u8fc7\u738b\u5927\u5eb7\u5de6\u808b\uff0c\u76f4\u5c04\u8fdb\u5bfa\u95e8\u4e4b\u4e0a\u3002 \u3000\u3000\u738b\u5927\u5eb7\u4e24\u773c\u5706\u7741\uff0c\u5927\u559d\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u8fd9\u7b97\u54ea\u95e8\u5b50\u660e\u5802\uff01\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u4e01\u4e00\u9b42\u5df2\u903c\u8fd1\u9ea6\u5c0f\u660e\u8eab\u524d\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4e01\u5e08\u5144\uff0c\u4f60\u4e00\u5411\u4e3a\u4eba\u7cbe\u660e\uff0c\u4e0d\u8be5\u7518\u5fc3\u53d7\u4eba\u5974\u5f79\u9a71\u4f7f\uff01\u201d \u3000\u3000\u4e0d\u96be\u60f3\u89c1\uff0e\u8fd9\u8bdd\u95ee\u4e86\u7b49\u4e8e\u767d\u95ee\uff0e\u56e0\u4e3a\u4e01\u4e00\u9b42\u4e5f\u662f\u670d\u8fc7\u201c\u5411\u5fc3\u9732\u201d\u7684\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u518d\u9053\uff1a\u201c\u4e01\u5e08\u5144\uff0c\u4f60\u82e5\u5fc3\u91cc\u8fd8\u6e05\u695a\uff0c\u5c31\u8be5\u9a6c\u4e0a\u9000\u56de\u53bb\uff0c\u5426\u5219\u5c31\u6055\u5c0f\u5f1f\u65e0\u793c\u4e86\uff01\u201d \u3000\u3000\u4e01\u2014\u9b42\u91d1\u5200\u4e00\u62a1\uff0c\u91d1\u5149\u8000\u773c\u751f\u8f89\uff0c\u731b\u5411\u9ea6\u5c0f\u660e\u653b\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u4ed7\u5251\u76f8\u8fce\uff0e \u3000\u3000\u4e01\u4e00\u9b42\u7684\u8eab\u624b\u679c\u7136\u8f83\u9ad8\u5d99\u53c8\u9ad8\u51fa\u4e00\u7b79\uff0c\u91d1\u5200\u62db\u62db\u6709\u5982\u98ce\u9a70\u7535\u63a3\uff0c\u800c\u4e14\u4e00\u76f4\u4e0d\u79bb\u9ea6\u5c0f\u660e\u5168 \u8eab\u8981\u5bb3\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u4e45\u6218\u5df2\u901e\u75b2\u6001\uff0c\u53ea\u80fd\u54ac\u7259\u82e6\u6491\u3002 \u3000\u3000\u5927\u7ea6\u4e8c\u5341\u51e0\u62db\u8fc7\u540e\uff0c\u9ea6\u5c0f\u660e\u65bd\u5c55\u51fa\u201c\u5999\u5999\u5251\u8bc0\u201d\u4e2d\u7684\u5999\u5999\u4e09\u5f0f\uff0c\u603b\u7b97\u53c8\u6273\u56de\u4f18\u52bf\uff0c\u8fde\u6e10 \u5360\u5f97\u4e0a\u98ce\uff0e \u3000\u3000\u970d\u5143\u4f3d\u6025\u4e8e\u5c06\u9ea6\u5c0f\u660e\u5236\u670d\uff0c\u7acb\u5373\u53c8\u547d\u94c1\u62d0\u8ffd\u9b42\u7f57\u94ee\u52a9\u4e0a\u9635\u53bb\u3002 \u3000\u3000\u94c1\u62d0\u8ffd\u9b42\u7f57\u94ee\u7684\u6b66\u529f\uff0c\u66f4\u5728\u91d1\u5200\u9b3c\u68ad\u4e01\u4e00\u9b42\u4e4b\u4e0a\u3002\u8fd9\u4e00\u6765\uff0c\u9ea6\u5c0f\u660e\u518d\u4e5f\u62b5\u6321\u6b65\u4f4f\uff0c\u903c\u5f97 \u4ed6\u4e0d\u5f97\u4e0d\u4e14\u6218\u4e14\u9000\u3002 \u3000\u3000\u8303\u7389\u6606\u4e00\u89c1\u9ea6\u5c0f\u660e\u5feb\u9000\u5230\u5bfa\u95e8\u53e3\uff0e\u4e5f\u8fc5\u5feb\u7684\u633a\u5251\u52a9\u4e0a\u9635\u53bb\u3002 \u3000\u3000\u970d\u5143\u4f3d\u6740\u673a\u65e9\u8d77\uff0c\u7acb\u5373\u547d\u5d06\u5cd2\u56db\u9b54\u548c\u5df4\u5929\u4e49\u3001\u5b8b\u5929\u94ce\u4e5f\u51b2\u6740\u8fc7\u6765\uff0e \u3000\u3000\u53e6\u5916\u6bd2\u706b\u6210\u5168\uff0e\u9ed1\u9b54\u65f6\u5bc5\u4ee5\u53ca\u706b\u83bd\uff0e\u725b\u5954\u4e5f\u90fd\u84c4\u52bf\u4ee5\u5f85\uff0c\u53ea\u7b49\u970d\u5143\u4f3d\u4ee4\u4e0b\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u3001\u4f59\u4ea6\u4e50\u3001\u738b\u5927\u5eb7\u3001\u82d7\u7d20\u82d3\u89c1\u6b64\u60c5\u5f62\uff0c\u4e5f\u90fd\u8dc3\u5165\u573a\u4e2d\uff0c\u5c55\u5f00\u62fc\u640f\u3002\u4e00\u65f6\u4e4b\u95f4\uff0c\u5200 \u5149\u5251\u5f71\uff0c\u91d1\u94c1\u4ea4\u51fb\u4e4b\u58f0\u4e0d\u7edd\u4e8e\u8033\u3002\u5730\u4e0a\u5219\u4e00\u7247\u98de\u7802\u8d70\u77f3\uff0c\u5f81\u5c18\u6eda\u6eda\uff0c\u6839\u672c\u65e0\u6cd5\u5206\u6e05\u8c01\u662f\u8c01\u6765\u3002 \u3000\u3000\u6240\u5e78\u970d\u5143\u4f3d\u5c1a\u672a\u547d\u6240\u6709\u4eba\u4e00\u8d77\u51b2\u6740\u8fc7\u6765\uff0c\u5c11\u6797\u65b9\u9762\u4e5f\u4ecd\u80fd\u6682\u65f6\u4fdd\u6301\u4e2d\u7acb\u3002 \u3000\u3000\u53cc\u65b9\u6df7\u6218\u95f4\uff0c\u5ffd\u542c\u8033\u9645\u54cd\u8d77\u4e00\u58f0\u5982\u96f7\u5927\u559d\uff0c\u9707\u5f97\u6240\u6709\u7684\u4eba\u90fd\u8033\u9f13\u55e1\u55e1\u4f5c\u54cd\uff0c\u62fc\u6218\u53cc\u65b9\uff0c \u60c5\u4e0d\u81ea\u7981\u5168\u90fd\u505c\u4e0b\u624b\u6765\u3002 \u3000\u3000\u4f46\u89c1\u5bfa\u95e8\u53e3\u4e0d\u77e5\u4ec0\u4e48\u65f6\u5019\uff0c\u5df2\u7ad9\u5b9a\u2014\u4f4d\u7d2b\u8138\u73af\u76ee\uff0c\u6ee1\u816e\u866c\u987b\uff0c\u8eab\u6750\u4fee\u4f1f\uff0c\u795e\u5a01\u51db\u51db\u7684\u52b2 \u88c5\u5927\u6c49\uff0c\u8d6b\u7136\u662f\u51b7\u9762\u960e\u7f57\u80e1\u67cf\u9f84\uff01 \u3000\u3000\u5728\u8fd9\u5239\u90a3\uff0c\u970d\u5143\u4f3d\u7684\u624b\u4e0b\u767e\u4f59\u4eba\uff0c\u51e0\u4e4e\u5168\u90fd\u76ee\u77aa\u53e3\u5446\uff0c\u50f5\u5728\u5f53\u573a\u3002 \u3000\u3000\u62fc\u6218\u4e2d\u505c\u4e0b\u624b\u6765\u7684\u5cad\u5357\u4e8c\u5947\u548c\u5d06\u5cd2\u4e94\u9b54\u5728\u539f\u5730\u5446\u4e86\u534a\u664c\uff0e\u5168\u90fd\u9000\u56de\u9635\u4e0a\u3002 \u3000\u3000\u53ea\u6709\u7f57\u94ee\u548c\u4e01\u4e00\u9b42\uff0e\u56e0\u5df2\u5931\u53bb\u7406\u667a\uff0c\u4ecd\u5446\u5446\u7684\u7ad9\u5728\u539f\u5730\u4e0d\u52a8\u3002 \u3000\u3000\u970d\u5143\u4f3d\u695e\u4e86\u7247\u523b\uff0e\u624d\u6536\u56de\u89c6\u7ebf\u8f6c\u5934\u4f4e\u58f0\u95ee\u9ed1\u9b54\u65f6\u5bc5\u9053\uff1a\u201c\u65f6\u5144\u5728\u5415\u6881\u5c71\u4e5d\u5929\u7384\u9634\u6d1e\u5e9c\u524d \u9047\u5230\u7684\u53ef\u662f\u4ed6\uff1f\u201d \u3000\u3000\u65f6\u5bc5\u70b9\u5934\u9053\uff1a\u201c\u4e0d\u9519\uff01\u6b63\u662f\u4ed6\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u518d\u95ee\u9053\uff1a\u201c\u4ed6\u7a76\u7adf\u662f\u4e0d\u662f\u80e1\u67cf\u9f84\uff1f\u201d \u3000\u3000\u65f6\u5bc5\u9053\uff1a\u201c\u6309\u8bf4\u80e1\u67cf\u9f84\u5df2\u7ecf\u6b7b\u4e86\uff0c\u54ea\u6709\u4eba\u6b7b\u590d\u6d3b\u7684\u9053\u7406\uff1f\u4f46\u8fd9\u4eba\u7684\u5f62\u8c8c\uff0c\u5374\u53c8\u6d3b\u8131\u662f\u80e1 \u67cf\u9f84\uff0c\u5c24\u5176\u8eab\u624b\u4e4b\u9ad8\uff0c\u4f3c\u4e4e\u66f4\u80dc\u5f53\u5e74\u7684\u80e1\u67cf\u9f84\uff0e\u5b9e\u5728\u4ee4\u65f6\u67d0\u4e0d\u89e3\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u7565\u4e00\u6c89\u541f\uff0e\u5f53\u5373\u5927\u58f0\u9053\uff1a\u201c\u5c0a\u9a82\u7ecf\u8fc7\u6613\u5bb9\u4e4b\u540e\uff0c\u7684\u786e\u4e0e\u5f53\u5e74\u524d\u4efb\u7eff\u6797\u76df\u4e3b\u80e1\u67cf\u9f84 \u4e00\u822c\u65e0\u4e8c\uff0c\u5c0a\u9a7e\u65e2\u7136\u6b66\u529f\u4e0d\u5728\u80e1\u524d\u76df\u4e3b\u4e4b\u4e0b\uff0c\u4e3a\u4f55\u4e0d\u4ee5\u672c\u6765\u9762\u76ee\u76f8\u89c1\uff1f\u201d \u3000\u3000\u80e1\u67cf\u9f84\u8138\u4e0a\u6beb\u65e0\u8868\u60c5\uff0c\u4e5f\u5e76\u672a\u7b54\u8bdd\u3002 \u3000\u3000\u53ea\u542c\u5df4\u5929\u4e49\u4f4e\u58f0\u9053\uff1a\u201c\u76df\u4e3b\uff0c\u5c5e\u4e0b\u5012\u731c\u5f97\u51fa\u8fd9\u4eba\u6613\u5bb9\u6210\u80e1\u67cf\u9f84\u7684\u7528\u610f\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u4f60\u8ba4\u4e3a\u4ed6\u7528\u5fc3\u4f55\u5728\uff1f\u201d \u3000\u3000\u5df4\u5929\u4e49\u9053\uff1a\u201c\u8fd9\u5b8c\u5168\u662f\u949f\u4e00\u8c6a\u7684\u6388\u610f\uff0c\u5c06\u4ed6\u626e\u6210\u80e1\u67cf\u9f84\uff0c\u4ee5\u4fbf\u8feb\u4f7f\u60a8\u81ea\u52a8\u4ea4\u51fa\u7eff\u6797\u76df\u4e3b \u4e4b\u4f4d\u3002\u5176\u5b9e\u8fd9\u4eba\u9664\u4e86\u6b66\u529f\u4e4b\u5916\uff0c\u53ef\u80fd\u5e76\u65e0\u591a\u5927\u667a\u8c0b\uff0c\u5426\u5219\u4ed6\u4e3a\u4ec0\u4e48\u4e00\u76f4\u4e0d\u5f00\u53e3\u8bf4\u8bdd\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u9894\u9996\u9053\uff1a\u201c\u4f60\u5206\u6790\u5f97\u6709\u9053\u7406\u3002\u201d \u3000\u3000\u5df4\u5929\u4e49\u9053\uff1a\u201c\u76df\u4e3b\u5c31\u8be5\u518d\u548c\u4ed6\u8bb2\u51e0\u53e5\u8bdd\uff0c\u5f88\u5feb\u4fbf\u53ef\u77a7\u51fa\u4ed6\u7684\u7834\u7efd\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u89c6\u7ebf\u518d\u8f6c\u5411\u80e1\u67cf\u9f84\u9053\uff1a\u201c\u670b\u53cb\uff0c\u4f9d\u8001\u592b\u9884\u6599\uff0c\u4f60\u8be5\u662f\u4f4d\u5802\u5802\u7684\u82f1\u96c4\u597d\u6c49\uff0c\u4f55\u82e6\u53d7 \u4ed6\u4eec\u8fd9\u4e9b\u7eff\u6797\u8d25\u7c7b\u7684\u611a\u5f04\u9a71\u4f7f\uff0c\u82e5\u80af\u6295\u5230\u8001\u592b\u8fd9\u8fb9\u6765\uff0c\u8001\u592b\u5b9a\u4e0d\u4f1a\u4e8f\u5f85\u4f60\u3002\u201d \u3000\u3000\u80e1\u67cf\u9f84\u6717\u58f0\u9053\uff1a\u201c\u970d\u5143\u4f3d\uff0e\u4f60\u53ef\u662f\u5e0c\u671b\u80e1\u67d0\u518d\u56de\u8ff7\u8e2a\u8c37\uff1f\u8ff7\u8e2a\u8c37\u672c\u662f\u80e1\u67d0\u4e00\u624b\u5f00\u521b\u7684\uff0c \u80e1\u67d0\u968f\u65f6\u90fd\u53ef\u56de\u53bb\uff0c\u4f55\u5fc5\u4f60\u6765\u76f8\u8bf7\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u4e0d\u89c9\u8138\u8272\u4e00\u53d8\uff0c\u8f6c\u5934\u9053\uff1a\u201c\u5df4\u8001\u5f1f\uff0e\u8fd9\u4eba\u8fde\u58f0\u97f3\u4e5f\u50cf\u80e1\u67cf\u9f84\uff01\u201d \u3000\u3000\u5df4\u5929\u4e49\u9053\uff1a\u201c\u58f0\u97f3\u50cf\u4e0d\u8db3\u4e3a\u5947\uff0c\u4e5f\u8bb8\u4ed6\u662f\u670d\u4e86\u53d8\u97f3\u836f\u3002\u636e\u8bf4\u4e5d\u5bab\u5c71\u7684\u5723\u624b\u533b\u9690\u9b4f\u5929\u5316\u5df2 \u70bc\u6210\u51e0\u767e\u79cd\u53d8\u97f3\u836f\uff0e\u518d\u7ecf\u76f8\u4e92\u8c03\u914d\uff0c\u60f3\u53d8\u51fa\u4efb\u4f55\u4eba\u7684\u58f0\u97f3\u90fd\u6beb\u65e0\u56f0\u96be\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u4ed6\u4e0d\u80af\u900f\u9732\u8eab\u4efd\uff0c\u8fd9\u5012\u662f\u4ef6\u9ebb\u70e6\u4e8b\u3002\u201d \u3000\u3000\u201c\u76df\u4e3b\u4f55\u4e0d\u5c06\u4ed6\u751f\u64d2\u8fc7\u6765\uff0c\u4ed6\u58f0\u97f3\u548c\u5f62\u8c8c\u90fd\u50cf\u80e1\u67cf\u9f84\uff0c\u5982\u679c\u80fd\u628a\u4ed6\u5e26\u56de\u8ff7\u8e2a\u8c37\uff0c\u5012\u662f\u5927 \u5927\u6709\u7528\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u968f\u5373\u559d\u4ee4\u91d1\u5200\u9b3c\u68ad\u4e01\u4e00\u9b42\u9053\uff1a\u201c\u628a\u8fd9\u4eba\u62ff\u4e0b\uff0c\u8981\u64d2\u6d3b\u7684\uff01\u201d \u3000\u3000\u4ed6\u4e0d\u4ee4\u94c1\u62d0\u8ffd\u9b42\u7f57\u94ee\u4e00\u8d77\u51fa\u624b\uff0e\u4e0d\u5916\u662f\u60f3\u5148\u770b\u770b\u80e1\u67cf\u9f84\u7684\u8eab\u624b\uff0c\u7136\u540e\u518d\u51b3\u5b9a\u662f\u5426\u6d3e\u4eba\u52a9 \u9635\u3002 \u3000\u3000\u80e1\u67cf\u9f84\u7f13\u7f13\u5411\u524d\u8d70\u51fa\u51e0\u6b65\uff0c\u63a5\u7740\u7ffb\u8155\u62d4\u51fa\u957f\u5251\u3002 \u3000\u3000\u4ed6\u884c\u8d70\u5927\u6c5f\u5357\u5317\u3001\u9ec4\u6cb3\u4e24\u5cb8\u4e8c\u5341\u5e74\uff0e\u9ed1\u9053\u4eba\u7269\u83ab\u4e0d\u4e3a\u4ed6\u7684\u201c\u62d0\u5185\u5957\u5251\u201d\u7edd\u62db\u4e27\u80c6\u60ca\u9b42\uff0e \u4f46\u4ed6\u5bf9\u4ed8\u4e00\u822c\u9ad8\u624b\uff0c\u6700\u60ef\u5e38\u7684\u8fd8\u662f\u5355\u72ec\u7528\u5251\u3002\u53ea\u6709\u5728\u9047\u5230\u7edd\u9876\u9ad8\u624b\u65f6\uff0c\u624d\u5251\u3001\u62d0\u5e76\u7528\u3002 \u3000\u3000\u56e0\u4e4b\u3001\u6b64\u523b\u548c\u4e01\u4e00\u9b42\u4ea4\u624b\uff0c\u4ed6\u8fd8\u4e0d\u5c51\u4eae\u51fa\u94c1\u62d0\u3002 \u3000\u3000\u7a81\u89c1\u4e01\u4e00\u9b42\u5de6\u8155\u4e00\u626c\uff0c\u4e00\u679a\u9b3c\u68ad\u95ea\u7535\u822c\u76f4\u5411\u80e1\u67cf\u9f84\u54bd\u5589\u88ad\u53bb\u3002 \u3000\u3000\u80e1\u67cf\u9f84\u957f\u5251\u4e00\u6487\uff0e\u201c\u5f53\u201d\u7684\u2014\u58f0\uff0c\u5288\u4e2a\u6b63\u7740\uff0c\u90a3\u9b3c\u68f1\u88ab\u5288\u5f97\u53cd\u800c\u5012\u98de\u56de\u53bb\uff0c\u5dee\u70b9\u513f\u91cd\u4f24 \u5df4\u5929\u4e49\u3002 \u3000\u3000\u4e01\u4e00\u9b42\u6295\u51fa\u9b3c\u68ad\u540e\uff0c\u4eba\u4e5f\u6b3a\u8eab\u75be\u8fdb\uff0c\u91d1\u5200\u5149\u8292\u6eda\u52a8\uff0e\u6709\u5982\u6f2b\u5929\u82b1\u96e8\uff0c\u7f69\u5411\u80e1\u67cf\u9f84\u4e0a\u76d8\u3002 \u3000\u3000\u80e1\u67cf\u9f84\u7a33\u7ad9\u539f\u5730\uff0c\u811a\u4e0b\u534a\u70b9\u4e0d\u66fe\u79fb\u52a8\uff0c\u957f\u5251\u2014\u62db\u201c\u795e\u9f99\u4e09\u73b0\u201d\uff0c\u6709\u5982\u4e09\u67c4\u5251\u540c\u65f6\u51fa\u624b\uff0c \u53ea\u542c\u4e00\u9635\u5200\u5251\u4ea4\u51fb\u4e4b\u58f0\uff0c\u706b\u661f\u8ff8\u4e0b\uff0e\u4e01\u4e00\u9b42\u5df2\u88ab\u8feb\u5012\u9000\u4e09\u56db\u6b65\uff0c\u597d\u4e0d\u5bb9\u6613\u624d\u62ff\u6869\u7ad9\u7a33\u3002 \u3000\u3000\u80e1\u67cf\u9f84\u4e00\u6536\u957f\u5251\uff0c\u53f1\u9053\uff1a\u201c\u4e01\u4e00\u9b42\uff0c\u770b\u5728\u4f60\u662f\u5e08\u53d4\u95e8\u4e0b\uff0c\u80e1\u67d0\u4e0d\u5fcd\u4f24\u4f60\uff0c\u8fd8\u4e0d\u5feb\u5feb\u9000 \u4e0b\uff01\u201d \u3000\u3000\u4e01\u4e00\u9b42\u4e24\u773c\u5446\u76f4\u7684\u7728\u4e86\u51e0\u7728\uff0c\u770b\u4e0d\u51fa\u4efb\u4f55\u53cd\u5e94\u3002 \u3000\u3000\u970d\u5143\u4f3d\u559d\u9053\uff1a\u201c\u7ee7\u7eed\u8fdb\u653b\uff01\u201d \u3000\u3000\u4e01\u4e00\u9b42\u4f3c\u4e4e\u5bf9\u970d\u5143\u4f3d\u7684\u8bdd\u72ec\u80fd\u542c\u61c2\uff0c\u800c\u4e14\u552f\u547d\u662f\u4ece\uff0c\u7331\u8eab\u8d77\u6b65\uff0c\u518d\u5411\u80e1\u67cf\u9f84\u653b\u53bb\u3002\u91d1\u5200 \u8fc7\u5904\uff0c\u4f46\u89c1\u5251\u5f71\u4e00\u95ea\uff0c\u53c8\u662f\u4e00\u58f0\u9707\u54cd\uff0c\u4e01\u4e00\u9b42\u7684\u91d1\u5200\u7acb\u5373\u8131\u624b\u98de\u51fa\u3002 \u3000\u3000\u540c\u4e00\u65f6\u95f4\uff0c\u80e1\u67cf\u9f84\u4e00\u638c\u53c8\u51fb\u4e2d\u4e01\u4e00\u9b42\u524d\u80f8\u3002 \u3000\u3000\u867d\u7136\u80e1\u67cf\u9f84\u5e76\u672a\u8fc7\u4efd\u7528\u529b\uff0c\u4f46\u4e01\u4e00\u9b42\u5374\u88ab\u51fb\u98de\u4e86\u8d77\u6765\uff0c\u76f4\u5347\u4e00\u4e08\u591a\u9ad8\uff0c\u624d\u50cf\u5929\u5916\u98de\u77f3\u822c \u6454\u5411\u5730\u9762\u3002 \u3000\u3000\u843d\u5730\u4e4b\u540e\uff0e\u5f20\u53e3\u55b7\u51fa\u4e00\u80a1\u9c9c\u8840\uff0e\u7adf\u7136\u4eba\u4e8b\u4e0d\u77e5\u3002 \u3000\u3000\u970d\u5143\u4f3d\u5927\u60ca\u4e4b\u4e0b\uff0c\u518d\u4ee4\u94c1\u62d0\u8ffd\u9b42\u7f57\u94ee\u51fa\u6218\u3002 \u3000\u3000\u7f57\u94ee\u662f\u5728\u970d\u5143\u4f3d\u4ee5\u201c\u5411\u5fc3\u9732\u201d\u63a7\u5236\u4e0b\u6b66\u529f\u6700\u9ad8\u7684\u4e00\u4e2a\uff0e\u970d\u5143\u4f3d\u9884\u6599\u4ed6\u53ef\u80fd\u4ecd\u4e0d\u662f\u5bf9\u65b9\u5bf9 \u624b\uff0c\u968f\u5373\u6697\u793a\u6bd2\u706b\u6210\u5168\u51c6\u5907\u706b\u5668\uff0c\u4ee5\u5907\u5fc5\u8981\u65f6\u518d\u7528\u706b\u653b\u3002 \u3000\u3000\u8fd9\u6b21\u6bd2\u706b\u6210\u5168\u7adf\u62ff\u51fa\u78f7\u706b\u5f39\uff0c\u8fd9\u79cd\u706b\u5668\u7206\u70b8\u5f00\u6765\uff0c\u6740\u4f24\u529b\u53ef\u53ca\u4e8e\u6570\u4e08\u65b9\u5706\u4e4b\u5185\uff0c\u5728\u6b64\u8303 \u56f4\u5185\uff0c\u518d\u5feb\u7684\u8eab\u624b\u4e5f\u65e0\u6cd5\u95ea\u907f\u3002 \u3000\u3000\u80e1\u67cf\u9f84\u4ecd\u7a33\u7ad9\u539f\u5730\u672a\u52a8\uff0c\u53ea\u7b49\u94c1\u62d0\u8ffd\u9b42\u7f57\u94ee\u6765\u653b\u3002 \u3000\u3000\u7f57\u94ee\u867d\u662f\u80e1\u67cf\u9f84\u5e08\u53d4\u9146\u79cb\u7684\u5f1f\u5b50\uff0c\u4f46\u4e8c\u4eba\u5374\u7edd\u5c11\u89c1\u9762\uff0c\u76f4\u5230\u4e09\u5e74\u524d\u9146\u79cb\u5ffd\u7136\u5728\u843d\u96c1\u8c37\u51fa \u73b0\u65f6\uff0c\u80e1\u67cf\u9f84\u624d\u548c\u7f57\u94ee\u8ba4\u8bc6\u3002 \u3000\u3000\u56e0\u4e4b\uff0c\u5bf9\u7f57\u94ee\u7684\u6b66\u529f\u5982\u4f55\uff0c\u80e1\u67cf\u9f84\u5e76\u4e0d\u6e05\u695a\u3002 \u3000\u3000\u7f57\u94ee\u4eba\u79f0\u94c1\u62d0\u8ffd\u9b42\uff0c\u4e0d\u6d88\u8bf4\u60ef\u7528\u7684\u5175\u5203\u662f\u62d0\u3002 \u3000\u3000\u4ed6\u8fd9\u62d0\u662f\u7528\u9554\u94c1\u6253\u9020\u800c\u6210\uff0c\u8db3\u6709\u4e94\u516d\u5341\u65a4\uff0c\u53c8\u56e0\u4ed6\u8ddb\u4e86\u4e00\u8db3\uff0c\u56e0\u4e4b\uff0c\u3000\u4e00\u62d0\u6709\u4e8c\u79cd\u7528\u9014\u3002 \u3000\u3000\u5e73\u65f6\u548c\u6218\u65f6\u90fd\u4e0d\u79bb\u624b\u3002 \u3000\u3000\u7a81\u89c1\u7f57\u94ee\u9554\u94c1\u62d0\u5f80\u5730\u4e0a\u4e00\u70b9\uff0e\u4eba\u5df2\u51cc\u7a7a\u800c\u8d77\u3002 \u3000\u3000\u4ed6\u90a3\u62d0\u672c\u6765\u6491\u5728\u814b\u4e0b\uff0c\u4f46\u4eba\u65e2\u5347\u7a7a\u4ee5\u540e\uff0c\u9554\u94c1\u62d0\u5df2\u53d8\u6210\u53cc\u624b\u4e92\u63e1\uff0c\u3000\u4e00\u62db\u201c\u6cf0\u5c71\u538b\u9876\u201d \u75be\u5411\u80e1\u67cf\u9f84\u4e0a\u76d8\u731b\u51fb\u800c\u4e0b\u3002 \u3000\u3000\u8fd9\u4e00\u51fb\u529b\u9053\u4f55\u6b62\u5343\u94a7\uff0c\u5373\u4f7f\u80e1\u67cf\u9f84\u662f\u94dc\u6d47\u7f57\u6c49\uff0c\u94c1\u6253\u91d1\u521a\uff0c\u4e5f\u4f1a\u88ab\u4ece\u5934\u9876\u7838\u5230\u811a\u8ddf\u3002 \u3000\u3000\u504f\u504f\u80e1\u67cf\u9f84\u4ecd\u662f\u4e0d\u95ea\u4e0d\u8eb2\uff0c\u4e00\u54ac\u7259\uff0c\u62a1\u5251\u2014\u62db\u201c\u6a2a\u67b6\u91d1\u6881\u201d\u5411\u4e0a\u683c\u53bb\u3002 \u3000\u3000\u4e00\u58f0\u91d1\u94c1\u5927\u9707\uff0e\u80e1\u67cf\u9f84\u867d\u7136\u63a5\u4f4f\u8fd9\u521a\u731b\u65e0\u6bd4\u7684\u4e00\u51fb\uff0e\u4f46\u811a\u4e0b\u5374\u8db3\u8db3\u9677\u5165\u5730\u9762\u534a\u5c3a\uff0e\u4e4d\u770b \u8d77\u6765\u597d\u50cf\u4eba\u5ffd\u7136\u77ee\u4e86\u534a\u622a\u3002 \u3000\u3000\u770b\u7f57\u94ee\u65f6\uff0e\u4e5f\u88ab\u9707\u5f97\u8eab\u5b50\u8fde\u6643\u51e0\u4e0b\uff0c\u8fde\u989d\u89d2\u90fd\u5192\u51fa\u8c46\u5927\u7684\u6c57\u73e0\u3002 \u3000\u3000\u4ed6\u7565\u4e00\u5598\u606f\uff0c\u53c8\u662f\u4e00\u62d0\u5f53\u5934\u538b\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u8fd9\u4e00\u62d0\u7684\u529b\u91cf\uff0c\u4f3c\u4e4e\u8f83\u4e0a\u6b21\u66f4\u91cd\u3002 \u3000\u3000\u53c8\u662f\u4e00\u58f0\u91d1\u94c1\u5927\u9707\u3002 \u3000\u3000\u8fd9\u6b21\u662f\u94c1\u62d0\u5bf9\u4e0a\u4e86\u94c1\u62d0\u3002 \u3000\u3000\u56e0\u4e3a\u80e1\u67cf\u9f84\u65e9\u8d81\u7f57\u94ee\u65b9\u624d\u5598\u606f\u7684\u5239\u90a3\uff0e\u4e5f\u6539\u4ee5\u94c1\u62d0\u51fb\u53bb\u3002 \u3000\u3000\u53cc\u62d0\u2014\u63a5\uff0c\u9ad8\u4e0b\u7acb\u5206\uff0c\u7f57\u94ee\u7684\u9554\u94c1\u62d0\u987f\u88ab\u51fb\u5f97\u8131\u624b\u98de\u51fa\uff0c\u4eba\u4e5f\u88ab\u9707\u5f97\u5411\u540e\u8e09\u8dc4\u6454\u53bb\u3002 \u5367\u9f99\u751f\u300a\u5999\u7edd\u5929\u9999\u300b \u7b2c\u4e09\u5341\u4e8c\u56de\u3000\u4e45\u522b\u91cd\u9022 \u3000\u3000\u8fd9\u65f6\uff0c\u82e5\u80e1\u67cf\u9f84\u8ffd\u51fb\u8fc7\u53bb\uff0c\u5b9a\u53ef\u4e00\u62d0\u6216\u2014\u638c\u5c06\u7f57\u94ee\u51fb\u6bd9\u3002 \u3000\u3000\u4f46\u4ed6\u5e76\u672a\u8fd9\u6837\u505a\uff0e\u56e0\u5bf9\u65b9\u603b\u662f\u81ea\u5df1\u7684\u8fdc\u652f\u5e08\u5144\u5f1f\uff0c\u800c\u4e14\u53c8\u662f\u795e\u5fd7\u4e0d\u6e05\u4e4b\u4eba\u3002 \u3000\u3000\u970d\u5143\u4f3d\u672c\u6253\u7b97\u8981\u6bd2\u706b\u6210\u5168\u53d1\u51fa\u624b\u4e2d\u7684\u78f7\u706b\u5f39\uff0c\u56e0\u89c1\u80e1\u67cf\u9f84\u4f3c\u662f\u5e76\u65e0\u76f8\u72af\u4e4b\u610f\uff0e\u4e5f\u51b3\u5b9a\u6682 \u65f6\u6309\u5175\u4e0d\u52a8\uff0e \u3000\u3000\u53ea\u542c\u5df4\u5929\u4e49\u4f4e\u58f0\u9053\uff1a\u201c\u76df\u4e3b\uff0c\u8fd9\u4eba\u4e0d\u4f46\u5f62\u8c8c\u50cf\u3001\u58f0\u97f3\u50cf\uff0c\u8fde\u4f7f\u7528\u7684\u5175\u5203\u4ee5\u53ca\u6b66\u529f\u8def\u6570\uff0c \u4e5f\u50cf\u6781\u4e86\u80e1\u67cf\u9f84\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u8e59\u8d77\u53cc\u7709\u9053\uff1a\u201c\u8fd9\u6837\u8bf4\u6765\uff0c\u4ed6\u8be5\u771f\u662f\u80e1\u67cf\u9f84\u4e86\u3002\u4f46\u80e1\u67cf\u9f84\u660e\u660e\u5df2\u7ecf\u6b7b\u53bb\uff0e\u53c8\u600e\u53ef \u80fd\u6d3b\u56de\u6765\u5462\uff1f\u201d \u3000\u3000\u5df4\u5929\u4e49\u8038\u80a9\u9053\uff1a\u201c\u5c5e\u4e0b\u4e5f\u88ab\u5f04\u7cca\u6d82\u4e86\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u4ed6\u662f\u5426\u80e1\u67cf\u9f84\uff0c\u4e0d\u5fc5\u7ba1\u4ed6\u3002\u4e0d\u8fc7\uff0c\u6b64\u4eba\u5728\uff0c\u5bf9\u54b1\u4eec\u662f\u5927\u5927\u4e0d\u5229\uff0c\u53ea\u6015\u4eca\u5929 \u7684\u884c\u52a8\uff0e\u5f88\u96be\u5706\u6ee1\u5b8c\u6210\u3002\u201d \u3000\u3000\u5df4\u5929\u4e49\u4e0d\u4ee5\u4e3a\u7136\u9053\uff1a\u201c\u76df\u4e3b\u672a\u514d\u987e\u8651\u5f97\u592a\u591a\uff0c\u6709\u6210\u8001\u82f1\u96c4\u7684\u706b\u5668\u548c\u5d06\u5cd2\u8001\u5927\u7684\u2018\u8840\u96fe \u6563\u2019\uff0c\u5373\u4f7f\u60f3\u70e7\u6389\u8fd9\u5ea7\u767d\u59d1\u5bfa\u90fd\u5e76\u975e\u96be\u4e8b\uff0c\u8fd8\u6709\u4ec0\u4e48\u53ef\u6015\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u8001\u592b\u5e76\u975e\u6015\u4e8b\uff0c\u800c\u662f\u89c9\u5f97\u6b64\u65f6\u6b64\u5730\u4e0d\u5b9c\u60f9\u4e8b\u3002\u4e00\u65e6\u653b\u8fdb\u767d\u59d1\u5bfa\uff0c\u5c11\u6797\u65b9\u9762 \u5fc5\u4e0d\u81f4\u8896\u624b\u65c1\u89c2\uff0c\u5230\u90a3\u65f6\u9e7f\u6b7b\u8c01\u624b\uff0c\u5f88\u96be\u9884\u6599\u3002\u201d \u3000\u3000\u4ed6\u4eec\u4e8c\u4eba\u8fd9\u9635\u5bf9\u8c08\u90fd\u58f0\u97f3\u6781\u5c0f\uff0c\u80e1\u67cf\u9f84\u7b49\u4eba\u5e76\u672a\u542c\u6e05\u8bf4\u4e9b\u4ec0\u4e48\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u4f3c\u662f\u6709\u4e9b\u4e0d\u8010\uff0c\u53eb\u9053\uff1a\u201c\u5e08\u5144\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u4e0b\u4ee4\u5927\u5bb6\u51b2\u6740\u8fc7\u53bb\uff1f\u201d \u3000\u3000\u80e1\u67cf\u9f84\u9053\uff1a\u201c\u5c0f\u660e\u7a0d\u5b89\u52ff\u8e81\uff0e\u6ca1\u6709\u6211\u7684\u547d\u4ee4\uff0e\u8c01\u90fd\u4e0d\u53ef\u8f7b\u4e3e\u5984\u52a8\uff01\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u79bb\u9ea6\u5c0f\u660e\u6700\u8fd1\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u76df\u4e3b\u81ea\u6709\u9ad8\u89c1\uff0c\u54b1\u4eec\u4e00\u5207\u4f9d\u76df\u4e3b\u6307\u793a\u884c\u4e8b\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u5fcd\u4e0d\u4f4f\u53eb\u9053\uff1a\u201c\u53ea\u8981\u4f60\u4eec\u628a\u9ad8\u5d99\u4ea4\u51fa\uff0e\u8001\u592b\u60c5\u613f\u7387\u9886\u6240\u5c5e\u64a4\u51fa\u767d\u59d1\u5bfa\uff01\u201d \u3000\u3000\u80e1\u67cf\u9f84\u7acb\u5373\u5429\u5490\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u89e3\u5f00\u9ad8\u5d99\u7a74\u9053\uff0c\u8ba9\u4ed6\u56de\u53bb\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u5927\u4e0d\u60c5\u613f\uff0e\u53eb\u9053\uff1a\u201c\u5e08\u5144\uff0c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u5c0f\u5f1f\u597d\u4e0d\u5bb9\u6613\u5c06\u4ed6\u64d2\u5230\u624b\uff0c\u60a8\u5374\u8981\u628a \u4ed6\u653e\u8d70\uff1f\u201d \u3000\u3000\u201c\u522b\u8bb2\u7406\u7531\uff0c\u53ea\u7ba1\u653e\u4ed6\u56de\u53bb\uff01\u201d \u3000\u3000\u201c\u5e08\u5144\u8be5\u660e\u767d\uff0c\u5c0f\u5f1f\u53e6\u5916\u7684\u4e09\u4f4d\u5e08\u5144\uff0c\u90fd\u53d7\u4ed6\u63a7\u5236\uff0c\u82e5\u653e\u8d70\u4e86\u4ed6\uff0c\u5bf9\u54b1\u4eec\u5f71\u54cd\u592a\u5927\u4e86\uff01\u201d \u3000\u3000\u201c\u8981\u4f60\u653e\u4f60\u5c31\u653e\uff0e\u96be\u9053\u8fde\u6211\u7684\u8bdd\u4f60\u4e5f\u4e0d\u542c\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u4e00\u6c14\u4e4b\u4e0b\uff0c\u731b\u7136\u98de\u8d77\u4e00\u811a\u76f4\u5411\u8eba\u5728\u5730\u4e0a\u7684\u9ad8\u5d99\u8e22\u53bb\u3002 \u3000\u3000\u8fd9\u4e00\u811a\u52b2\u9053\u5947\u91cd\uff0c\u76f4\u628a\u9ad8\u5d99\u8e22\u8d77\u4e8c\u4e08\u591a\u9ad8\uff0e\u7136\u540e\u5411\u5bf9\u65b9\u9635\u4e2d\u843d\u53bb\u3002 \u3000\u3000\u5df4\u5929\u4e49\u773c\u660e\u624b\u5feb\uff0e\u8dc3\u8eab\u5c06\u4eba\u63a5\u4f4f\u3002\u5426\u5219\u9ad8\u5d99\u76f4\u6389\u5730\u4e0a\uff0c\u5f88\u53ef\u80fd\u4f1a\u5f53\u573a\u6454\u5f97\u6c14\u7edd\u8eab\u4ea1\u3002 \u3000\u3000\u970d\u5143\u4f3d\u8868\u73b0\u5f97\u5341\u5206\u723d\u5feb\uff0c\u89c1\u9ad8\u5d99\u5df2\u56de\uff0c\u968f\u5373\u4fbf\u4e0b\u4ee4\u64a4\u9000\u3002 \u3000\u3000\u672c\u6765\u8be5\u662f\u4e00\u573a\u8165\u98ce\u8840\u96e8\u7684\u60e8\u70c8\u62fc\u640f\uff0c\u7adf\u7136\u5982\u6b64\u620f\u5267\u5316\u7684\u6536\u573a\uff0c\u51e0\u4e4e\u6240\u6709\u7684\u4eba\u90fd\u6709\u7740\u610f\u5916 \u4e4b\u611f\u3002 \u3000\u3000\u80e1\u67cf\u9f84\u4e00\u58f0\u4e0d\u54cd\uff0c\u5f84\u81ea\u56de\u5854\u800c\u53bb\u3002 \u3000\u3000\u949f\u2014\u8c6a\u3001\u4f59\u4ea6\u4e50\u3001\u738b\u5927\u5eb7\u3001\u9ea6\u5c0f\u660e\u3001\u8303\u7389\u6606\u3001\u82d7\u7d20\u82d3\u90fd\u968f\u540e\u8ddf\u4e86\u4e0a\u6765\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u62a2\u7740\u9053\uff1a\u201c\u5e08\u5144\u5b9e\u5728\u4e0d\u8be5\u628a\u9ad8\u5d99\u653e\u56de\u53bb\uff0c\u5982\u679c\u628a\u4ed6\u7559\u4e0b\uff0c\u5c0f\u5f1f\u53e6\u5916\u90a3\u4e09\u4f4d\u5e08\u5144\u4e5f \u8bb8\u970d\u5143\u4f3d\u5c31\u65e0\u6cd5\u518d\u9a71\u4f7f\u4ed6\u4eec\uff0e\u201d \u3000\u3000\u80e1\u67cf\u9f84\u9762\u8272\u51dd\u91cd\uff0e\u53f9\u53e3\u6c14\u9053\uff0c\u201c\u6211\u4f55\u5c1d\u6ca1\u60f3\u5230\u8fd9\u4e00\u5c42\uff0c\u4f46\u82e5\u7559\u4e0b\u9ad8\u5d99\uff0c\u970d\u5143\u4f3d\u600e\u80fd\u5e72\u4f11\uff01 \u4e00\u573a\u8840\u6218\uff0c\u52bf\u5fc5\u96be\u4ee5\u907f\u514d\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u96be\u9053\u54b1\u4eec\u8fd8\u6015\u4e86\u4ed6\u4eec\uff1f\u201d \u3000\u3000\u80e1\u67cf\u9f84\u82e6\u7b11\u9053\uff1a\u201c\u5728\u6ca1\u6709\u7edd\u5bf9\u628a\u63e1\u4e4b\u4e0b\uff0e\u8fd9\u573a\u8840\u6218\uff0c\u8fd8\u662f\u4ee5\u5c3d\u91cf\u907f\u514d\u4e3a\u4f73\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u633a\u4e86\u633a\u80f8\u9053\uff1a\u201c\u4ee5\u5e08\u5144\u7684\u6b66\u529f\uff0c\u518d\u52a0\u4e0a\u6211\u4eec\u516d\u4e2a\uff0c\u600e\u80fd\u80dc\u4e0d\u4e86\u4ed6\u4eec\uff1f\u201d \u3000\u3000\u80e1\u67cf\u9f84\u6447\u5934\u9053\uff1a\u201c\u4f60\u53ea\u77e5\u5176\u4e00\uff0c\u4e0d\u77e5\u5176\u4e8c\uff0c\u82e5\u5355\u4ee5\u6b66\u529f\u800c\u8bba\uff0c\u6211\u4eec\u7684\u786e\u5e76\u4e0d\u5728\u4e4e\u4ed6\u4eec\uff0e \u4f46\u4f60\u53ef\u66fe\u60f3\u5230\uff0c\u53cc\u65b9\u4e00\u65e6\u6df7\u6218\u53ae\u6740\u8d77\u6765\uff0c\u52bf\u5fc5\u8fde\u7d2f\u5230\u767d\u59d1\u5bfa\u548c\u5c11\u6797\u65b9\u9762\u3002\u201d \u3000\u3000\u201c\u600e\u4e48\u4f1a\u5462\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u4f1a\uff01\u4ed6\u4eec\u65e2\u6709\u706b\u5668\uff0c\u53c8\u6709\u6bd2\u5242\uff0e\u8fd9\u4e24\u6837\u5e76\u975e\u7eaf\u4ee5\u6b66\u529f\u5e94\u4ed8\u5f97\u4e0b\u7684\u3002\u4e07\u4e00\u767d\u59d1\u5bfa\u88ab \u711a\uff0c\u6211\u4eec\u5982\u4f55\u5411\u609f\u5c18\u65b9\u4e08\u4ea4\u4ee3\u3002 \u3000\u3000\u53e6\u5916\uff0e\u5c11\u6797\u65b9\u9762\u7684\u5929\u7985\uff0c\u5929\u671b\u4e24\u4f4d\u5927\u5e08\u4ee5\u53ca\u4ed6\u4eec\u7684\u4e8c\u5341\u56db\u540d\u5f1f\u5b50\uff0e\u4e5f\u5fc5\u5b9a\u56e0\u8fd9\u4e8b\u800c\u53d7\u7275 \u8fde\uff0c\u4e00\u65e6\u4ed6\u4eec\u6709\u6240\u4f24\u4ea1\uff0c\u8fd9\u540e\u679c\u662f\u4e0d\u662f\u4ecd\u8981\u6211\u4eec\u6765\u8d1f\u8d23\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u7ec8\u4e8e\u4e0d\u518d\u8a00\u8bed\u3002 \u3000\u3000\u80e1\u67cf\u9f84\u7ee7\u7eed\u9053\uff1a\u201c\u5c24\u5176\u5854\u5185\u7684\u4ed6\u4eec\u4e03\u4f4d\uff0c\u90fd\u884c\u52a8\u4e0d\u4fbf\uff0e\u82e5\u5f53\u771f\u767d\u59d1\u5bfa\u88ab\u711a\uff0c\u4ed6\u4eec\u53ea\u6015\u8fde \u9003\u751f\u90fd\u6709\u56f0\u96be\uff0c\u8fd9\u65b9\u9762\u6211\u53c8\u600e\u80fd\u4e0d\u6709\u6240\u987e\u8651\uff1f\u201d \u3000\u3000\u4f17\u4eba\u7f04\u9ed8\u4e86\u751a\u4e45\uff0e\u4f59\u4ea6\u4e50\u624d\u95ee\u9053\uff1a\u201c\u76df\u4e3b\u6709\u4ec0\u4e48\u6253\u7b97\uff1f\u5982\u679c\u8001\u662f\u5b88\u5728\u8fd9\u91cc\uff0c\u4e5f\u4e0d\u662f\u529e \u6cd5\u3002\u201d \u3000\u3000\u80e1\u67cf\u9f84\u8bed\u6c14\u575a\u5b9a\u7684\u9053\uff1a\u201c\u8fd8\u662f\u7167\u539f\u5b9a\u8ba1\u5212\uff0c\u4eca\u665a\u64a4\u51fa\u767d\u59d1\u5bfa\u3002\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u9053\uff1a\u201c\u76df\u4e3b\u662f\u5426\u51b3\u5b9a\u4e86\u4e0b\u4e00\u6b65\u7684\u843d\u811a\u5730\u70b9\uff1f\u201d \u3000\u3000\u80e1\u67cf\u9f84\u4f3c\u5df2\u80f8\u6709\u6210\u7af9\u9053\uff1a\u201c\u5728\u4e94\u53f0\u5c71\u5357\u9e93\uff0e\u6709\u5904\u5cb3\u738b\u5e99\uff0c\u6211\u548c\u90a3\u91cc\u7684\u4f4f\u6301\u65b9\u4e08\u5f88\u719f\uff0e\u6211 \u5df2\u51b3\u5b9a\u64a4\u5230\u90a3\u91cc\u53bb\u3002\u90a3\u91cc\u8ddd\u8ff7\u8e2a\u8c37\u5c06\u8fd1\u4e8c\u767e\u91cc\u8def\u7a0b\uff0c\u8db3\u53ef\u907f\u514d\u970d\u5143\u4f3d\u518d\u7387\u4f17\u9a9a\u6270\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u4e0d\u89c9\u2014\u559c\uff0e\u9053\uff1a\u201c\u90a3\u592a\u597d\u4e86\uff01\u5c0f\u5f1f\u7b2c\u4e00\u4e2a\u8d5e\u6210\u3002\u201d \u3000\u3000\u80e1\u67cf\u9f84\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u8d5e\u6210\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u90a3\u91cc\u8ddd\u5b9a\u8944\u8f83\u8fd1\uff0c\u5230\u5999\u5999\u5eb5\u65b9\u4fbf\uff0c\u90a3\u4f4d\u8001\u5e08\u592a\u6b66\u529f\u76d6\u4e16\uff0e\u82e5\u80fd\u5f97\u5979\u76f8\u52a9\uff0c \u5c06\u6765\u6d88\u706d\u970d\u5143\u4f3d\u5c31\u66f4\u5bb9\u6613\u4e86\u3002\u8fd8\u6709\uff0c\u4e07\u59d1\u5a18\u4e5f\u662f\u54b1\u4eec\u7684\u4eba\u3002\u201d \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u5230\u4e86\u5cb3\u738b\u5e99\u4ee5\u540e\uff0c\u76df\u4e3b\u6253\u7b97\u5728\u90a3\u91cc\u4f4f\u591a\u4e45\uff1f\u201d \u3000\u3000\u80e1\u67cf\u9f84\u9053\uff1a\u201c\u5fc5\u987b\u7b49\u4ed6\u4eec\u4e03\u4f4d\u4f24\u52bf\u5168\u597d\u540e\uff0c\u624d\u80fd\u518d\u6253\u7b97\u4e0b\u4e00\u6b65\u884c\u52a8\u3002\u201d \u3000\u3000\u949f\u4e00\u8c6a\u795e\u8272\u4f3c\u662f\u5341\u5206\u51dd\u91cd\uff0c\u53f9\u4e86\u53e3\u6c14\uff0c\u6b32\u8a00\u53c8\u6b62\u3002 \u3000\u3000\u80e1\u67cf\u9f84\u76b1\u4e86\u76b1\u7709\u9053\uff1a\u201c\u949f\u8d24\u5f1f\u597d\u50cf\u6709\u4ec0\u4e48\u5fc3\u4e8b\uff1f\u201d \u3000\u3000\u949f\u4e00\u8c6a\u53c8\u662f\u9eef\u7136\u4e00\u53f9\u9053\uff1a\u201c\u65e2\u5df2\u88ab\u76df\u4e3b\u770b\u51fa\uff0c\u5c5e\u4e0b\u4e5f\u5c31\u4e0d\u5f97\u4e0d\u8bf4\u4e86\u3002\u5c5e\u4e0b\u62c5\u5fc3\u7684\u662f\u76df\u4e3b \u4e09\u6708\u5047\u671f\u5df2\u7ecf\u5feb\u6ee1\uff0e\u4e00\u65e6\u76df\u4e3b\u88ab\u56db\u7edd\u524d\u8f88\u53ec\u56de\uff0c\u5c5e\u4e0b\u4eec\u53c8\u5982\u4f55\u5bf9\u4ed8\u5f97\u4e86\u970d\u5143\u4f3d\uff1f\u201d \u3000\u3000\u80e1\u67cf\u9f84\u957f\u957f\u5401\u53e3\u6c14\u9053\uff1a\u201c\u4f60\u987e\u8651\u5f97\u56fa\u7136\u6781\u662f\uff0c\u4f46\u6211\u786e\u662f\u8eab\u4e0d\u7531\u5df1\uff0c\u4e5f\u53ea\u597d\u8d70\u4e00\u6b65\u7b97\u4e00\u6b65 \u4e86\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u7740\u7ad9\u8d77\u8eab\u6765\u9053\uff1a\u201c\u54b1\u4eec\u4e34\u8d70\u524d\u5fc5\u987b\u5bf9\u767d\u59d1\u5bfa\u65b9\u9762\u4ee5\u53ca\u5929\u7985\u5929\u671b\u4e24\u4f4d\u5927\u5e08\u505a\u4e00\u4ea4\u4ee3\uff0c \u73b0\u5728\u6211\u5c31\u8fc7\u53bb\u5411\u4ed6\u4eec\u8f9e\u522b\u4e00\u4e0b\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u5c0f\u5f1f\u4e5f\u8981\u8ddf\u968f\u5e08\u5144\u4e00\u8d77\u8fc7\u53bb\u3002\u201d \u3000\u3000\u80e1\u67cf\u9f84\u9053\uff1a\u201c\u949f\u8d24\u5f1f\u3001\u4f59\u8d24\u5f1f\u4e5f\u4e00\u8d77\u8fc7\u53bb\u597d\u4e86\uff01\u201d \u3000\u3000\u56db\u4eba\u4ece\u5854\u4e2d\u6765\u5230\u7985\u5ba4\uff0c\u6b63\u597d\u5929\u7985\u5927\u5e08\u3001\u5929\u671b\u5927\u5e08\u548c\u609f\u5c18\u90fd\u5728\u3002 \u3000\u3000\u53cc\u65b9\u89c1\u8fc7\u793c\u540e\uff0c\u80e1\u67cf\u9f84\u968f\u5373\u8bf4\u660e\u6765\u610f\u3002 \u3000\u3000\u5929\u7985\u5927\u5e08\u542c\u540e\u6709\u4e9b\u4e0d\u5b89\uff0c\u56e0\u4e3a\u80e1\u67cf\u9f84\u7387\u4eba\u4e00\u8d70\uff0c\u5c11\u6797\u65b9\u9762\u65e0\u5f62\u4e2d\u5df2\u663e\u5f97\u52bf\u5355\u529b\u8584\uff0c\u53ea\u662f \u4ed6\u8eab\u4e3a\u4e00\u4ee3\u6b66\u6797\u5b97\u5e08\u7684\u5c11\u6797\u638c\u95e8\uff0c\u4e0d\u4fbf\u8868\u660e\u800c\u5df2\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u770b\u51fa\u5929\u7985\u5927\u5e08\u7684\u5fc3\u610f\uff0c\u5fd9\u9053\uff1a\u201c\u8001\u7985\u5e08\u653e\u5fc3\uff0e\u6211\u4eec\u5f88\u5feb\u5c31\u4f1a\u56de\u6765\u7684\u3002\u201d \u3000\u3000\u5929\u7985\u5927\u5e08\u8bf5\u4e86\u58f0\u4f5b\u53f7\u9053\uff1a\u201c\u4f46\u613f\u4f17\u4f4d\u65bd\u4e3b\u65e9\u53bb\u65e9\u56de\uff0c\u5426\u5219\u8001\u8872\u5f88\u96be\u5355\u72ec\u5bf9\u8ff7\u8e2a\u8c37\u91c7\u53d6\u884c \u52a8\uff0c\u82e5\u518d\u56de\u5c11\u6797\u91cd\u65b0\u8c03\u96c6\u4eba\u624b\uff0c\u5728\u65f6\u95f4\u4e0a\u5b9e\u5728\u6765\u4e0d\u53ca\u3002\u201d \u3000\u3000\u8f9e\u522b\u5929\u7985\u5927\u5e08\u7b49\u4eba\uff0c\u518d\u56de\u5230\u5854\u5ba4\uff0c\u80e1\u67cf\u9f84\u9053\uff1a\u201c\u6211\u73b0\u5728\u79bb\u5f00\u767d\u59d1\u5bfa\u4e00\u4e0b\uff0c\u6709\u5173\u591c\u95f4\u884c\u52a8 \u7684\u4e8b\uff0c\u8bf8\u4f4d\u73b0\u5728\u5c31\u5f00\u59cb\u51c6\u5907\u3002\u201d \u3000\u3000\u949f\u4e00\u8c6a\u4e00\u6123\u9053\uff1a\u201c\u76df\u4e3b\u8981\u5230\u54ea\u91cc\u53bb\uff1f\u201d \u3000\u3000\u80e1\u67cf\u9f84\u9053\uff1a\u201c\u6211\u8be5\u56de\u4e07\u6708\u5ce1\u770b\u770b\uff0c\u5e08\u7236\u662f\u5426\u5df2\u7ecf\u56de\u6765\u3002\u201d \u3000\u3000\u949f\u4e00\u8c6a\u554a\u4e86\u58f0\u9053\uff1a\u201c\u76df\u4e3b\u548c\u56db\u7edd\u8001\u524d\u8f88\u5f53\u771f\u4f4f\u5728\u4e07\u6708\u5ce1\uff1f\u4e3a\u4ec0\u4e48\u4e07\u6708\u5821\u7684\u674e\u7ba1\u5bb6\u90fd\u4e0d\u77e5 \u9053\u5462\uff1f\u201d \u3000\u3000\u80e1\u67cf\u9f84\uff1a\u201c\u82e5\u88ab\u5916\u754c\u5f97\u77e5\u5bb6\u5e08\u4f4f\u5904\uff0c\u5bb6\u5e08\u53c8\u600e\u80fd\u7b97\u662f\u4e2a\u9041\u4e16\u4e4b\u4eba\uff1f\u201d \u3000\u3000\u80e1\u67cf\u9f84\u5728\u5b9d\u5854\u5404\u5c42\u5de1\u89c6\u4e86\u4e00\u904d\u4e4b\u540e\uff0c\u4fbf\u81ea\u884c\u79bb\u5bfa\u800c\u53bb\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u9001\u51fa\u5bfa\u95e8\u5916\u95ee\u9053\uff1a\u201c\u76df\u4e3b\u4e88\u5b9a\u4ec0\u4e48\u65f6\u5019\u56de\u6765\uff1f\u201d \u3000\u3000\u80e1\u67cf\u9f84\u9053\uff1a\u201c\u4e00\u66f4\u5de6\u53f3\u56de\u6765\uff0e\u6211\u56de\u6765\u540e\u5927\u5bb6\u7acb\u523b\u51fa\u53d1\u3002\u201d \u3000\u3000\u201c\u4e07\u4e00\u56db\u7edd\u8001\u524d\u8f88\u5df2\u7ecf\u56de\u6765\uff0c\u628a\u76df\u4e3b\u7559\u4e0b\u8be5\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u201c\u5047\u671f\u672b\u6ee1\uff0c\u6211\u4f1a\u5c3d\u91cf\u8bbe\u6cd5\u56de\u6765\u3002\u201d \u3000\u3000\u80e1\u67cf\u9f84\u8d70\u540e\uff0e\u949f\u4e00\u8c6a\u7b49\u4eba\u4fbf\u5fd9\u7740\u505a\u884c\u52a8\u524d\u51c6\u5907\u3002 \u3000\u3000\u5176\u5b9e\u4ed6\u4eec\u5e76\u6ca1\u6709\u4ec0\u4e48\u597d\u51c6\u5907\u7684\uff0c\u6700\u91cd\u8981\u7684\uff0c\u8be5\u662f\u5982\u4f55\u7167\u987e\u4e03\u540d\u884c\u52a8\u4e0d\u4fbf\u4e4b\u4eba\u3002 \u3000\u3000\u53e6\u5916\uff0c\u5e26\u7740\u9634\u624b\u4e00\u9b54\u4e5f\u5f62\u6210\u4e00\u79cd\u7d2f\u8d58\u3002 \u3000\u3000\u4e0d\u8fc7\u4ed6\u4eec\u6700\u62c5\u5fc3\u7684\uff0e\u8fd8\u662f\u80e1\u67cf\u9f84\u80fd\u5426\u5728\u9884\u5b9a\u65f6\u95f4\u56de\u6765\u3002 \u3000\u3000\u665a\u996d\u8fc7\u540e\uff0c\u5929\u8272\u6e10\u6697\uff0c\u80e1\u67cf\u9f84\u679c\u7136\u56de\u6765\u4e86\u3002 \u3000\u3000\u5f53\u4ed6\u4eec\u79bb\u5bfa\u65f6\uff0e\u5929\u7985\u5927\u5e08\u3001\u5929\u671b\u5927\u5e08\u548c\u609f\u5c18\u76f4\u9001\u51fa\u5bfa\u95e8\u4e4b\u5916\u3002 \u3000\u3000\u609f\u5c18\u8fd8\u7279\u522b\u51c6\u5907\u4e86\u2014\u888b\u5e72\u7cae\u4ea4\u7ed9\u4e86\u80e1\u67cf\u9f84\uff0c\u4ee5\u5907\u5728\u79bb\u5f00\u5c71\u533a\u524d\u98df\u7528\u3002 \u3000\u3000\u8fd9\u4e03\u540d\u884c\u52a8\u4e0d\u4fbf\u4e4b\u4eba\uff0c\u82d7\u7d20\u5170\u7ecf\u8fc7\u4e00\u5929\u7684\u8c03\u517b\uff0c\u5df2\u53ef\u4ee5\u52c9\u5f3a\u8d70\u8def\u3002 \u3000\u3000\u81f3\u4e8e\u5d02\u5c71\u4e8c\u96c4\u3001\u6c5f\u5317\u4e09\u9f99\u548c\u6587\u5929\u751f\u4ecd\u9700\u4eba\u6276\u6301\u3002 \u3000\u3000\u53e6\u5916\u9634\u624b\u4e00\u9b54\uff0c\u5219\u662f\u53cc\u624b\u53cd\u7f1a\uff0c\u53c8\u7528\u4e00\u5757\u9ed1\u5e03\u7f69\u4f4f\u4ed6\u7684\u53cc\u773c\uff0c\u88ab\u5939\u5728\u4f17\u4eba\u4e2d\u95f4\uff0c\u534a\u70b9\u4e5f \u65e0\u6cd5\u53cd\u6297\u3002 \u3000\u3000\u6653\u5bbf\u591c\u884c\uff0c\u4e09\u5929\u540e\uff01\u4ed6\u4eec\u5df2\u5230\u8fbe\u4e94\u53f0\u5c71\u5357\u9e93\uff0c\u4f4f\u8fdb\u4e86\u5cb3\u738b\u5e99\u3002 \u3000\u3000\u8fd9\u5ea7\u5cb3\u738b\u5e99\u89c4\u6a21\u4e0d\u5c0f\uff0c\u4f46\u50e7\u4eba\u5374\u6ca1\u6709\u51e0\u4e2a\u3002 \u3000\u3000\u4f4f\u6301\u65b9\u4e08\u6cd5\u540d\u9752\u98ce\uff0c\u4fd7\u5bb6\u548c\u80e1\u67cf\u9f84\u662f\u540c\u4e61\u2014\u95e8\u4eb2\u621a\uff0c\u8bf4\u8d77\u6765\u8be5\u662f\u80e1\u67cf\u9f84\u7684\u8868\u53d4\u3002 \u3000\u3000\u6570\u65e5\u540e\uff0c\u5d02\u5c71\u4e8c\u96c4\u3001\u6c5f\u5317\u4e09\u9f99\u3001\u82d7\u7d20\u5170\u3001\u6587\u5929\u751f\u4e03\u4eba\uff0c\u90fd\u5df2\u65e5\u6e10\u5eb7\u590d\uff0c\u9ea6\u5c0f\u660e\u4fbf\u5efa\u8bae\u80e1 \u67cf\u9f84\u5230\u5415\u6881\u5c71\u4e5d\u5929\u7384\u9634\u6d1e\u5e9c\u53d6\u201c\u5411\u5fc3\u9732\u201d\u89e3\u836f\u3002 \u3000\u3000\u4ed6\u4eec\u5728\u7ecf\u8fc7\u2014\u756a\u4e89\u8bae\u4e4b\u540e\uff0c\u51b3\u5b9a\u7531\u80e1\u67cf\u9f84\u3001\u9ea6\u5c0f\u660e\u4eb2\u81ea\u62bc\u89e3\u9634\u624b\u4e00\u9b54\u5230\u5415\u6881\u5c71\u3002 \u3000\u3000\u8fd9\u662f\u56e0\u4e3a\u9634\u624b\u4e00\u9b54\u8001\u5978\u5de8\u6ed1\uff0c\u5fc5\u987b\u6709\u80e1\u67cf\u9f84\u548c\u9ea6\u5c0f\u660e\u8fd9\u6837\u7684\u9ad8\u624b\u62bc\u89e3\uff0c\u8def\u4e0a\u624d\u4e0d\u4f1a\u51fa\u95ee \u9898\u3002 \u3000\u3000\u597d\u5728\u6b64\u523b\u5df2\u7528\u4e0d\u7740\u62c5\u5fc3\u970d\u5143\u4f3d\u518d\u6765\u88ad\u51fb\u3002 \u3000\u3000\u6b64\u523b\u7684\u9634\u624b\u4e00\u9b54\uff0c\u53ef\u8c13\u72fc\u72c8\u5230\u5bb6\uff0c\u53cc\u624b\u53cd\u7f1a\uff0c\u4e0a\u8eab\u8fd8\u88ab\u8fde\u70b9\u4e09\u5904\u9ebb\u7a74\uff0c\u4f46\u811a\u4e0b\u5374\u80fd\u7167\u6837 \u8d70\u8def\u3002 \u3000\u3000\u6240\u5e78\u4e00\u8def\u4e0a\u5e76\u672b\u78b0\u5230\u719f\u4eba\uff0c\u5426\u5219\uff0c\u8fd9\u8001\u9b54\u5934\u4e0d\u649e\u6b7b\u624d\u602a\u3002 \u3000\u3000\u6b21\u65e5\u5df2\u5230\u5b9a\u8944\u53bf\u57ce\uff0c\u7531\u5b9a\u8944\u5357\u884c\uff0c\u4e8c\u5341\u4f59\u91cc\u540e\u4fbf\u662f\u5999\u5999\u5eb5\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u5e08\u5144\uff0c\u54b1\u4eec\u8981\u4e0d\u8981\u518d\u5230\u5999\u5999\u5eb5\u4e00\u8d9f\uff1f\u201d \u3000\u3000\u80e1\u67cf\u9f84\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u53c8\u8981\u518d\u5230\u5999\u5999\u5eb5\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u53bb\u770b\u770b\u4e07\u59d1\u5a18\uff0c\u5982\u679c\u5979\u80fd\u56de\u6765\uff0c\u54b1\u4eec\u5c82\u4e0d\u53c8\u591a\u4e86\u4e00\u4e2a\u4eba\u624b\uff0c\u82e5\u80fd\u9047\u5230\u8001\u5e08 \u592a\uff0c\u90a3\u5c31\u66f4\u597d\u4e86\u3002\u201d \u3000\u3000\u80e1\u67cf\u9f84\u81ea\u7136\u4e5f\u60e6\u8bb0\u7740\u4e07\u6620\u971e\uff0c\u5979\u662f\u4e49\u5144\u4e07\u6653\u5149\u7684\u72ec\u751f\u5973\u513f\uff0c\u4e07\u6653\u5149\u5728\u4e34\u6b7b\u524d\uff0c\u66fe\u628a\u4e07\u6620 \u971e\u6258\u4ed8\u7ed9\u81ea\u5df1\uff0c\u5982\u4eca\u4e07\u6620\u971e\u5bc4\u8eab\u5c3c\u5eb5\uff0c\u4ed6\u53c8\u600e\u80fd\u4e0d\u5173\u5fc3\u5979\u7684\u8fd1\u51b5\u3002 \u3000\u3000\u53e6\u5916\uff0c\u4ed6\u4e5f\u786e\u60f3\u89c1\u90a3\u4f4d\u7f01\u8863\u8001\u5c3c\u3002 \u3000\u3000\u5982\u679c\u7f01\u8863\u8001\u5c3c\u771f\u662f\u5f53\u5e74\u5999\u5999\u4ed9\u5b50\uff0c\u90a3\u5c31\u8be5\u662f\u81ea\u5df1\u7684\u5e08\u53d4\uff0c\u4e0d\u8bba\u5728\u793c\u8c8c\u548c\u4eb2\u60c5\u4e0a\uff0c\u90fd\u5e94\u62dc \u89c1\u4e00\u756a\u3002 \u3000\u3000\u867d\u7136\u4e0a\u6b21\u4e5f\u66fe\u89c1\u8fc7\uff0c\u4f46\u5374\u7b97\u4e0d\u4e0a\u662f\u6b63\u5f0f\u76f8\u89c1\u3002 \u3000\u3000\u4e8e\u662f\u4e8c\u4eba\u62bc\u7740\u9634\u624b\u4e00\u9b54\u7531\u5c94\u8def\u8f6c\u5f80\u5999\u5999\u5eb5\u3002 \u3000\u3000\u6b64\u523b\u9634\u624b\u4e00\u9b54\u5df2\u4e0d\u518d\u9ed1\u5e03\u906e\u773c\uff0c\u50bb\u6123\u6123\u7684\u95ee\u9053\uff1a\u201c\u5230\u5415\u7cb1\u5c71\u4e0d\u662f\u8fd9\u6837\u8d70\u7684\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u600e\u6837\u8d70\u7531\u6211\u5e08\u5144\u51b3\u5b9a\uff0c\u4f60\u8fd9\u8001\u738b\u516b\u86cb\u7f57\u55e6\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u5e72\u54b3\u4e86\u4e24\u58f0\u9053\uff1a\u201c\u8001\u592b\u662f\u4e0d\u613f\u4f60\u4eec\u591a\u7ed5\u51a4\u6789\u8def\uff0c\u4f60\u8fd9\u5c0f\u5b50\u5012\u72d7\u54ac\u5415\u6d1e\u5bbe\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9a82\u9053\uff1a\u201c\u597d\u554a\uff01\u4f60\u5634\u91cc\u8fd8\u6562\u4e0d\u5e72\u4e0d\u51c0\uff0c\u4e0d\u7529\u4f60\u51e0\u8033\u5149\uff0c\u8c05\u4f60\u4e5f\u4e0d\u77e5\u9053\u6211\u7684\u5389\u5bb3\u3002\u201d \u3000\u3000\u80e1\u67cf\u9f84\u5fd9\u9053\uff1a\u201c\u7b97\u5566\uff01\u4ed6\u5df2\u7ecf\u591f\u4e22\u4eba\u7684\u5566\uff0c\u7528\u4e0d\u7740\u518d\u6574\u4ed6\u3002\u201d \u3000\u3000\u6765\u5230\u5999\u5999\u5eb5\u524d\uff0c\u8fdc\u8fdc\u53c8\u671b\u89c1\u4e07\u6620\u971e\u7ad9\u5728\u95e8\u53e3\u3002 \u3000\u3000\u4e07\u6620\u971e\u4e00\u89c1\u80e1\u67cf\u9f84\u548c\u9ea6\u5c0f\u660e\uff0e\u8fde\u5fd9\u8fce\u4e86\u4e0a\u6765\u89c1\u793c\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u62a2\u7740\u95ee\u9053\uff1a\u201c\u8001\u5e08\u592a\u548c\u5eb5\u4e3b\u90fd\u5728\u5417\uff1f\u201d \u3000\u3000\u4e07\u6620\u971e\u5e76\u672a\u56de\u7b54\uff0e\u5374\u6123\u6123\u7684\u671b\u7740\u9634\u624b\u4e00\u9b54\u9053\uff1a\u201c\u8fd9\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u7b11\u9053\uff1a\u201c\u4f60\u4ed4\u7ec6\u770b\u770b\uff0c\u50cf\u4e0d\u50cf\u9634\u624b\u4e00\u9b54\u90a3\u8001\u738b\u516b\u86cb\uff1f\u201d \u3000\u3000\u4e07\u6620\u971e\u7728\u52a8\u7740\u53cc\u7738\u9053\uff1a\u201c\u9634\u624b\u4e00\u9b54\u600e\u4e48\u843d\u5230\u8fd9\u79cd\u5730\u6b65\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4ed6\u65e9\u5c31\u8be5\u843d\u5230\u8fd9\u5730\u6b65\u4e86\uff0e\u4f60\u597d\u50cf\u5f88\u540c\u60c5\u4ed6\u3002\u201d \u3000\u3000\u80e1\u67cf\u9f84\u9053\uff1a\u201c\u5c0f\u660e\u522b\u548c\u971e\u513f\u8bf4\u4e9b\u5e9f\u8bdd\uff0e\u54b1\u4eec\u9a6c\u4e0a\u8fdb\u5eb5\u8981\u7d27\u3002\u201d \u3000\u3000\u5374\u89c1\u4e07\u6620\u971e\u9762\u6709\u96be\u8272\u9053\uff1a\u201c\u53d4\u53d4\u5982\u679c\u6709\u4e8b\uff0c\u6700\u597d\u7531\u4f84\u5973\u5148\u8fdb\u5185\u7980\u62a5\u3002\u201d \u3000\u3000\u80e1\u67cf\u9f84\u4e00\u6123\u9053\uff1a\u201c\u662f\u5426\u6211\u548c\u5c0f\u660e\u4e0d\u4fbf\u8fdb\u53bb\uff1f\u201d \u3000\u3000\u4e07\u6620\u971e\u6b49\u7136\u9053\uff1a\u201c\u4e0d\u7792\u53d4\u53d4\uff0c\u4f84\u5973\u6b63\u662f\u5949\u5e08\u7236\u4e4b\u547d\u628a\u5b88\u5eb5\u95e8\uff0c\u4efb\u4f55\u4eba\u4e0d\u5f97\u8fdb\u5165\u3002\u201d \u3000\u3000\u201c\u8fd9\u662f\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u4f84\u5973\u4e5f\u4e0d\u6e05\u695a\u3002\u201d \u3000\u3000\u201c\u90a3\u5c31\u7531\u4f60\u5148\u8fdb\u53bb\u5411\u5eb5\u4e3b\u901a\u62a5\uff0c\u5c31\u8bf4\u6211\u6709\u4e8b\u6c42\u89c1\uff01\u201d \u3000\u3000\u4e07\u6620\u971e\u8f6c\u8eab\u800c\u53bb\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u832b\u7136\u9053\uff1a\u201c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u628a\u7740\u95e8\u4e0d\u51c6\u4efb\u4f55\u4eba\u8fdb\u53bb\uff0c\u5176\u4e2d\u5fc5\u6709\u8e4a\u8df7\uff1f\u201d \u3000\u3000\u80e1\u67cf\u9f84\u9053\uff1a\u201c\u7b49\u971e\u513f\u51fa\u6765\u540e\u518d\u505a\u8ba1\u8f83\u3002\u201d \u3000\u3000\u4e0d\u5927\u4e00\u4f1a\u513f\uff0c\u4e07\u6620\u971e\u4fbf\u5306\u5306\u5fd9\u5fd9\u7684\u5954\u51fa\u6765\u9053\uff1a\u201c\u53d4\u53d4\uff0e\u5e08\u7236\u8981\u60a8\u8d76\u7d27\u56de\u5b9a\u8944\u53bf\u57ce\u53bb\uff01\u201d \u3000\u3000\u80e1\u67cf\u9f84\u8bb6\u7136\u9053\uff1a\u201c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u6211\u4eec\u4ece\u5b9a\u8944\u53bf\u57ce\u6765\uff0c\u4e3a\u4ec0\u4e48\u8981\u6211\u56de\u53bb\uff1f\u201d \u3000\u3000\u201c\u5e08\u7236\u8bf4\u6709\u4f4d\u8001\u524d\u8f88\u8981\u627e\u53d4\u53d4\u3002\u201d \u3000\u3000\u201c\u54ea\u4f4d\u8001\u524d\u8f88\uff1f\u201d \u3000\u3000\u201c\u5c31\u662f\u5e9e\u58eb\u51b2\u5e9e\u8001\u524d\u8f88\u3002\u201d \u3000\u3000\u201c\u4ed6\u2026\u2026\u4ed6\u627e\u6211\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u4f84\u5973\u4e0d\u6e05\u695a\u3002\u201d \u3000\u3000\u201c\u4ed6\u600e\u4e48\u4f1a\u5728\u5b9a\u8944\u53bf\u57ce\u627e\u6211\uff1f\u201d \u3000\u3000\u201c\u4ed6\u662f\u51c6\u5907\u5230\u4e07\u6708\u4fa0\u627e\u53d4\u53d4\u7684\uff0e\u636e\u8bf4\u4ed6\u8001\u4eba\u5bb6\u8981\u5728\u5b9a\u8944\u8bbf\u4e00\u4f4d\u53cb\u4eba\uff0c\u6240\u4ee5\u4eca\u665a\u4f4f\u5728\u5b9a\u8944 \u53bf\u57ce\u3002\u53d4\u53d4\u5230\u4e86\u5b9a\u8944\uff0c\u4e00\u5b9a\u53ef\u4ee5\u627e\u5230\u4ed6\u3002\u5426\u5219\u4ed6\u8d76\u5f80\u4e07\u6708\u5ce1\uff0c\u5c82\u4e0d\u6251\u4e86\u7a7a\u3002\u201d \u3000\u3000\u201c\u5b9a\u8944\u53bf\u57ce\u90a3\u4e48\u5927\uff0e\u6211\u5230\u54ea\u91cc\u627e\u4ed6\uff1f\u201d \u3000\u3000\u201c\u636e\u8bf4\uff0c\u4ed6\u4eca\u665a\u5c06\u4f4f\u5728\u5ba2\u5e97\u91cc\uff0c\u5b9a\u8944\u53bf\u57ce\u53ea\u6709\u4e8c\u4e09\u5bb6\u5ba2\u5e97\uff0e\u627e\u8d77\u6765\u5f88\u65b9\u4fbf\u3002\u201d \u3000\u3000\u80e1\u67cf\u9f84\u6c89\u5fd6\u4e86\u534a\u664c\uff0c\u671b\u9ea6\u5c0f\u660e\u2014\u773c\u9053\uff1a\u201c\u8d70\uff01\u54b1\u4eec\u56de\u5b9a\u8944\u53bb\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u54b1\u4eec\u8fd9\u6b21\u51fa\u6765\u7684\u76ee\u7684\uff0e\u662f\u8981\u5230\u5415\u6881\u5c71\u53d6\u89e3\u836f\uff0c\u8fd9\u6837\u4e00\u6765\uff0c\u5c82\u4e0d\u53c8\u8981\u803d\u8bef\u4e0d \u5c11\u65f6\u95f4\u3002\u201d \u3000\u3000\u80e1\u67cf\u5cad\u9053\uff1a\u201c\u5e9e\u8001\u524d\u8f88\u627e\u6211\uff0c\u5fc5\u5b9a\u6709\u5341\u5206\u8981\u7d27\u7684\u4e8b\uff0e\u600e\u597d\u8ba9\u4eba\u5bb6\u7a7a\u8dd1\u2014\u8d9f\u3002\u597d\u5728\u6b64\u5904\u79bb \u5b9a\u8944\u53bf\u57ce\u4e0d\u8fdc\uff0e\u6700\u591a\u4e0d\u8fc7\u803d\u8bef\u4e00\u5929\u65f6\u95f4\uff0e\u7b97\u4e0d\u4e86\u4ec0\u4e48\u3002\u201d \u3000\u3000\u201c\u5e08\u5144\u731c\u60f3\u8be5\u662f\u4ec0\u4e48\u8981\u7d27\u7684\u4e8b\uff1f\u201d \u3000\u3000\u201c\u6211\u548c\u5e9e\u8001\u524d\u8f88\u4ee5\u524d\u5f88\u5c11\u6765\u5f80\u3002\u751a\u81f3\u8fde\u89c1\u9762\u90fd\u6ca1\u51e0\u6b21\uff0e\u4ed6\u73b0\u5728\u627e\u6211\uff0c\u2014\u5b9a\u4e0e\u7fce\u513f\u6709\u5173\uff0e\u201d \u3000\u3000\u201c\u5e08\u5144\u6240\u6599\u4e0d\u5dee\uff0c\u4e00\u5b9a\u662f\u6709\u5173\u7fce\u513f\u7684\u4e8b\uff0e\u8fd9\u5012\u975e\u53bb\u89c1\u4ed6\u4e0d\u53ef\u4e86\u3002\u53e6\u5916\uff0c\u5e08\u5144\u548c\u4ed6\u8fd8\u6709\u4e00 \u5c42\u5173\u7cfb\uff0e\u201d \u3000\u3000\u201c\u4ec0\u4e48\u5173\u7cfb\uff1f\u201d \u3000\u3000\u201c\u4ed6\u53ea\u662f\u58f0\u58f0\u8bf4\u5e08\u5ac2\u662f\u4ed6\u7684\u4e49\u5973\uff0e\u5982\u6b64\u770b\u6765\uff0e\u4e5f\u7b49\u4e8e\u662f\u5e08\u5144\u7684\u4e49\u7236\u4e86\uff01\u201d \u3000\u3000\u201c\u8fd9\u53ea\u662f\u4ed6\u81ea\u5df1\u8bf4\u7684\uff0c\u636e\u6211\u6240\u77e5\uff0c\u4f60\u5e08\u5ac2\u4ece\u524d\u6839\u672c\u4e0d\u8ba4\u8bc6\u4ed6\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u7fce\u513f\u5374\u662f\u4ed6\u7684\u4eb2\u5916\u5b59\u3001\u800c\u5e08\u5144\u548c\u5e08\u5ac2\u5219\u662f\u7fce\u513f\u7684\u4e49\u7236\u6bcd\uff0c\u51ed\u7740\u8fd9\u5c42\u5173\u7cfb\uff0c\u5e08\u5ac2\u8ba4 \u4ed6\u4f5c\u4e49\u7236\uff0c\u5e76\u975e\u4e0d\u53ef\u80fd\u3002 \u3000\u3000\u5426\u5219\u5e08\u5ac2\u4e34\u7ec8\u524d\uff0c\u4e3a\u4ec0\u4e48\u4f1a\u5f53\u4f17\u58f0\u660e\u8981\u4ed6\u628a\u9057\u4f53\u5e26\u8d70\uff1f\u66f4\u4f55\u51b5\u4ece\u5929\u53f0\u4e07\u82b1\u5bab\u56de\u6765\u8fd9\u51e0\u6708 \u4e2d\uff0c\u5e9e\u8001\u524d\u8f88\u4e00\u76f4\u662f\u5e2e\u5fd9\u54b1\u4eec\u7684\u3002\u201d \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u5eb5\u5185\u5df2\u4f20\u6765\u9759\u6708\u5eb5\u4e3b\u547c\u5524\u4e07\u6620\u971e\u7684\u58f0\u97f3\u3002 \u3000\u3000\u80e1\u67cf\u5cad\u9053\uff1a\u201c\u971e\u513f\uff0c\u4f60\u56de\u5eb5\u53bb\u5427\uff0e\u8bf7\u4ee3\u6211\u5411\u4ee4\u5e08\u81f4\u8c22\uff01\u201d \u3000\u3000\u4e07\u6620\u971e\u5411\u80e1\u67cf\u9f84\u65bd\u4e86\u4e00\u793c\uff0c\u53c8\u5411\u9ea6\u5c0f\u660e\u6253\u8fc7\u62db\u547c\uff0c\u624d\u8fdb\u5165\u5eb5\u95e8\u3002 \u3000\u3000\u4e8e\u662f\uff0c\u9ea6\u5c0f\u660e\u548c\u80e1\u67cf\u9f84\u62bc\u7740\u9634\u624b\u2014\u9b54\uff0c\u518d\u56de\u5b9a\u8944\u53bf\u57ce\u3002 \u3000\u3000\u6b64\u523b\u9634\u624b\u4e00\u9b54\u5fc3\u91cc\u8d8a\u53d1\u4e0d\u662f\u5473\u9053\uff0c\u56e0\u4e3a\u89c1\u5230\u5e9e\u58eb\u51b2\u540e\uff0c\u53c8\u591a\u4e86\u4e00\u4e2a\u5bf9\u5934\uff0e\u540a\u7136\u5e9e\u58eb\u51b2\u4e0d \u4f1a\u628a\u4ed6\u600e\u4e48\u6837\uff0c\u4f46\u989c\u9762\u5374\u5fc5\u66f4\u8361\u7136\u65e0\u5b58\u3002 \u3000\u3000\u5b9a\u8944\u679c\u7136\u53ea\u6709\u4e8c\u4e09\u5bb6\u5ba2\u5e97\uff0c\u7531\u4e8e\u5e26\u7740\u9634\u624b\u4e00\u9b54\u5728\u8857\u4e0a\u884c\u8d70\u4e0d\u65b9\u4fbf\uff0c\u80e1\u67cf\u9f84\u4e00\u8fdb\u53bf\u57ce\u4fbf\u5148 \u627e\u4e86\u5bb6\u5ba2\u5e97\u4f4f\u4e0b\uff0c\u7136\u540e\u547d\u9ea6\u5c0f\u660e\u5230\u5176\u4ed6\u5ba2\u5e97\u67e5\u8bbf\u5e9e\u58eb\u51b2\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u5f88\u5feb\u4fbf\u8bbf\u904d\u5176\u4ed6\u5ba2\u5e97\uff0c\u5374\u59cb\u7ec8\u627e\u4e0d\u5230\u6709\u5e9e\u58eb\u51b2\u8fd9\u540d\u4f4f\u5ba2\u3002 \u3000\u3000\u518d\u56de\u5230\u6240\u4f4f\u5ba2\u5e97\uff0e\u5927\u4e3a\u5931\u671b\u7684\u5411\u80e1\u67cf\u9f84\u62a5\u544a\u67e5\u8bbf\u7ecf\u8fc7\u3002 \u3000\u3000\u80e1\u67cf\u9f84\u5012\u662f\u5f88\u6c89\u7740\uff0c\u9053\uff1a\u201c\u4e5f\u8bb8\u5e9e\u8001\u524d\u8f88\u592a\u665a\u4e4b\u540e\u624d\u6295\u5bbf\uff0c\u54b1\u4eec\u5c31\u8d81\u8fd9\u673a\u4f1a\u4f11\u606f\u4f11\u606f\u4e5f \u597d\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u5982\u679c\u5929\u665a\u4e4b\u540e\u8fd8\u662f\u627e\u4e0d\u5230\u5e9e\u8001\u524d\u8f88\u5462\uff1f\u201d \u3000\u3000\u80e1\u67cf\u9f84\u9053\uff1a\u201c\u4e0d\u7ba1\u5982\u4f55\uff0c\u54b1\u4eec\u5728\u5b9a\u8944\u53bf\u57ce\u4f4f\u4e0a\u4e00\u665a\uff0c\u82e5\u627e\u4e0d\u5230\u4eba\uff0c\u660e\u5929\u5c31\u7ee7\u7eed\u8d76\u5f80\u5415\u6881 \u5c71\u3002\u201d \u3000\u3000\u5348\u996d\u8fc7\u540e\uff0c\u9ea6\u5c0f\u660e\u5c0f\u7761\u4e86\u4e00\u4f1a\u513f\uff0c\u770b\u770b\u5929\u8272\u5df2\u665a\uff0c\u518d\u5230\u53e6\u5916\u5ba2\u5e97\u8bbf\u67e5\u3002 \u3000\u3000\u521a\u51fa\u4e86\u5ba2\u5e97\u95e8\uff0c\u8fce\u9762\u6765\u4e86\u4e00\u4f4d\u8001\u8005\uff0c\u9ea6\u5c0f\u660e\u4e0d\u89c9\u773c\u775b\u4e00\u4eae\uff0c\u6765\u4eba\u8d6b\u7136\u6b63\u662f\u5e9e\u58eb\u51b2\u3002\u9ea6\u5c0f \u660e\u4e00\u9635\u60ca\u559c\uff0c\u6025\u6025\u8fce\u4e0a\u524d\u53bb\u9053\uff1a\u201c\u5e9e\u8001\u4f2f\uff0e\u53ef\u627e\u5230\u4f60\u8001\u4eba\u5bb6\u4e86\uff01\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u4e00\u89c1\u9ea6\u5c0f\u660e\uff0e\u4e5f\u900f\u7740\u60ca\u559c\u9053\uff1a\u201c\u4f60\u600e\u4e48\u5230\u8fd9\u91cc\u6765\u4e86\uff1f\u201d \u3000\u3000\u201c\u665a\u8f88\u6b63\u662f\u8981\u627e\u5e9e\u8001\u4f2f\u7684\uff01\u201d \u3000\u3000\u201c\u4f60\u627e\u8001\u592b\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u665a\u8f88\u4e0a\u5348\u66fe\u5230\u8fc7\u5999\u5999\u5eb5\uff0c\u636e\u5eb5\u4e3b\u8bf4\uff0c\u5e9e\u8001\u4f2f\u5230\u4e07\u6708\u5ce1\u627e\u80e1\u76df\u4e3b\uff0c\u5e76\u8bf4\u5e9e\u8001\u4f2f\u4eca\u665a\u5c06\u6295 \u5bbf\u5b9a\u8944\u53bf\u57ce\uff0c\u665a\u8f88\u8fd9\u624d\u4e5f\u56de\u5230\u5b9a\u5bdd\u53bf\u57ce\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u8001\u592b\u8fd8\u662f\u8981\u5230\u4e07\u6708\u5ce1\u627e\u80e1\u76df\u4e3b\uff0c\u4f60\u82e5\u6709\u65f6\u95f4\uff0e\u5c31\u966a\u8001\u592b\u4e00\u8d77\u53bb\u4e5f\u597d\u3002\u201d \u3000\u3000\u201c\u4e0d\u5fc5\u4e86\uff0c\u80e1\u76df\u4e3b\u5c31\u4f4f\u5728\u8fd9\u5bb6\u5ba2\u5e97\u3002\u201d \u3000\u3000\u201c\u80e1\u76df\u4e3b\u600e\u4f1a\u4f4f\u5728\u8fd9\u5bb6\u5ba2\u5e97\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u5f53\u5373\u628a\u80e1\u67cf\u9f84\u7387\u4f17\u7531\u767d\u59d1\u5bfa\u6765\u5230\u4e94\u53f0\u5c71\u5357\u9e93\u5cb3\u738b\u5e99\uff0c\u53ca\u76ee\u524d\u6b63\u8981\u62bc\u89e3\u9634\u624b\u4e00\u9b54\u5230 \u5415\u6881\u5c71\u53d6\u89e3\u836f\u7684\u7ecf\u8fc7\u5927\u7565\u8bf4\u4e00\u4e86\u904d\u3002 \u3000\u3000\u5e9e\u58eb\u51b2\u559c\u9053\uff1a\u201c\u90a3\u592a\u597d\u4e86\uff0c\u5feb\u5e26\u8001\u592b\u8fdb\u5ba2\u5e97\u89c1\u80e1\u76df\u4e3b\u53bb\uff01\u201d \u3000\u3000\u4e8c\u4eba\u5306\u5306\u8fdb\u4e86\u5ba2\u5e97\uff0e\u8fd8\u6ca1\u8d70\u5230\u623f\u95e8\uff0c\u9ea6\u5c0f\u660e\u5c31\u558a\u9053\uff1a\u201c\u5e08\u5144\uff0e\u5e9e\u8001\u4f2f\u6765\u5566\uff01\u8fd8\u4e0d\u5feb\u5feb\u51fa \u6765\u8fce\u63a5\u3002\u201d \u3000\u3000\u4e00\u8bed\u672a\u6bd5\uff0c\u80e1\u67cf\u9f84\u65e9\u8083\u5bb9\u8fce\u4e86\u51fa\u6765\uff0c\u671b\u7740\u5e9e\u58eb\u51b2\u62f1\u624b\u8fc7\u989d\u9053\uff1a\u201c\u665a\u8f88\u80e1\u67cf\u9f84\uff0e\u62dc\u89c1\u5e9e\u8001 \u524d\u8f88\uff01\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u5fd9\u9053\uff1a\u201c\u90fd\u662f\u81ea\u5df1\u4eba\uff0c\u7528\u4e0d\u7740\u5ba2\u5957\uff01\u201d \u3000\u3000\u4ed6\u7684\u8fd9\u53e5\u8bdd\uff0c\u5206\u660e\u53c8\u662f\u501a\u8001\u5356\u8001\uff0c\u4ee5\u8c37\u5bd2\u9999\u7684\u4e49\u7236\u81ea\u5c45\u3002 \u3000\u3000\u80e1\u67cf\u9f84\u53ea\u597d\u53c8\u6539\u53e3\u76f8\u79f0\u9053\uff1a\u201c\u5e9e\u8001\u4f2f\uff01\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u5475\u5475\u7b11\u9053\uff1a\u201c\u5e94\u8be5\u7684\uff0c\u522b\u5fd8\u4e86\u8001\u592b\u662f\u5c0a\u592b\u4eba\u7684\u4e49\u7236\u3002\u201d \u3000\u3000\u8fdb\u5165\u4e0a\u623f\uff0c\u80e1\u67cf\u9f84\u628a\u5e9e\u58eb\u51b2\u8ba9\u81f3\u4e0a\u5ea7\uff0c\u5e76\u4eb2\u81ea\u732e\u4e0a\u8336\u3002 \u3000\u3000\u5e9e\u58eb\u51b2\u559d\u4e86\u53e3\u8336\u9053\uff1a\u201c\u8001\u592b\u672c\u6765\u6253\u7b97\u5230\u4e07\u6708\u5ce1\u627e\u4f60\uff0c\u80fd\u5728\u8fd9\u91cc\u9047\u4e0a\uff0c\u4e5f\u7701\u53bb\u4e00\u756a\u5954\u6ce2\uff0e\u201d \u3000\u3000\u80e1\u67cf\u9f84\u9053\uff1a\u201c\u5e9e\u8001\u4f2f\u600e\u77e5\u665a\u8f88\u4f4f\u5728\u4e07\u6708\u5ce1\uff1f\u66f4\u4f55\u51b5\u665a\u8f88\u5bb6\u5e08\u4f4f\u7684\u5730\u65b9\uff0c\u81f3\u4eca\u5e76\u65e0\u4efb\u4f55\u5916 \u4eba\u77e5\u9053\u3002\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u9053\uff1a\u201c\u8001\u592b\u6628\u65e5\u9047\u89c1\u4e86\u4ee4\u5e08\u56db\u7edd\u8001\u4eba\uff0c\u56db\u7edd\u8001\u4eba\u5df2\u5bf9\u8001\u592b\u8bb2\u8fc7\u4ed6\u6240\u4f4f\u7684\u5730\u65b9\u3002\u201d \u3000\u3000\u80e1\u67cf\u9f84\u731b\u5403\u2014\u60ca\u9053\uff1a\u201c\u5bb6\u5e08\u56de\u6765\u4e86\uff1f7\u539f\u6765\u5e9e\u8001\u4f2f\u548c\u5bb6\u5e05\u8ba4\u8bc6\uff1f\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u6447\u5934\u9053\uff1a\u201c\u8001\u592b\u5e74\u7eaa\u867d\u8001\uff0c\u4f46\u56db\u7edd\u8001\u4eba\u5374\u66f4\u662f\u8001\u592b\u7684\u524d\u8f88\u3002\u4ed6\u6bd4\u8001\u592b\u53ef\u80fd\u8981\u5e74\u957f \u767e\u5c81\u4ee5\u4e0a\u3002\u5f53\u8001\u592b\u5e7c\u5e74\u521d\u51fa\u9053\u65f6\uff0c\u4ed6\u65e9\u5df2\u606f\u9690\u6c5f\u6e56\uff0c\u8001\u592b\u4ece\u524d\u600e\u53ef\u80fd\u8ba4\u8bc6\u4ed6\uff1f\u201d \u3000\u3000\u201c\u5e9e\u8001\u4f2f\u5728\u4ec0\u4e48\u5730\u65b9\u9047\u89c1\u5bb6\u5e08\u7684\uff1f\u201d \u3000\u3000\u201c\u5999\u5999\u5eb5\u9644\u8fd1\u5c71\u4e0a\u3002\u201d \u3000\u3000\u201c\u90a3\u4e48\u5e9e\u8001\u4f2f\u5230\u4e07\u6708\u5ce1\u627e\u665a\u8f88\u53c8\u662f\u4e3a\u4e86\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u201c\u8001\u592b\u662f\u53d7\u4ee4\u5e08\u4e4b\u6258\uff0c\u56e0\u4e3a\u56db\u7edd\u8001\u4eba\u60f3\u77e5\u9053\u5728\u4ed6\u5916\u51fa\u4e09\u6708\u671f\u95f4\uff0c\u4f60\u90fd\u505a\u4e86\u4e9b\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u80e1\u67cf\u9f84\u4e0d\u89c9\u5fc3\u5934\u4e00\u51db\uff01 \u3000\u3000\u56db\u7edd\u8001\u4eba\u4e34\u8d70\u65f6\uff0c\u66fe\u2014\u518d\u5631\u5490\u4ed6\uff0e\u4e0d\u5f97\u518d\u6d89\u8db3\u8ff7\u8e2a\u8c37\uff0c\u504f\u504f\u4ed6\u672a\u66fe\u9075\u5e08\u547d\uff0e\u8fd9\u5982\u4f55\u8ba9\u4ed6 \u5411\u5e9e\u58eb\u51b2\u56de\u7b54\u3002 \u3000\u3000\u53e6\u4e00\u65b9\u9762\uff0e\u9ea6\u5c0f\u660e\u5185\u5fc3\u4e5f\u5927\u4e3a\u7d27\u5f20\u3002 \u3000\u3000\u56db\u7edd\u8001\u4eba\u4e00\u56de\u6765\uff0c\u80e1\u67cf\u9f84\u52bf\u5fc5\u4e0d\u80fd\u518d\u9886\u5bfc\u4ed6\u4eec\u5bf9\u4ed8\u970d\u5143\u4f3d\uff0c\u5982\u6b64\u4e00\u6765\uff0e\u5c82\u4e0d\u4e00\u5207\u5e0c\u671b\u90fd \u8981\u843d\u7a7a\u3002 \u3000\u3000\u5e9e\u58eb\u51b2\u89c1\u80e1\u67cf\u9f84\u6c89\u541f\u4e0d\u8bed\uff0e\u76b1\u4e86\u76b1\u7709\u9053\uff1a\u201c\u4f60\u600e\u4e48\u4e0d\u56de\u7b54\u8001\u592b\u7684\u8bdd\uff1f\u201d \u3000\u3000\u80e1\u67cf\u9f84\u9053\uff1a\u201c\u5173\u4e8e\u665a\u8f88\u5c06\u8fd1\u4e09\u4e2a\u6708\u6765\u7684\u884c\u8e2a\uff0e\u5c0f\u660e\u65b9\u624d\u662f\u5426\u5df2\u5bf9\u8001\u4f2f\u63d0\u8fc7\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u62a2\u7740\u9053\uff1a\u201c\u65b9\u624d\u6211\u5df1\u5728\u5ba2\u5e97\u95e8\u5916\u5bf9\u5e9e\u8001\u4f2f\u8bb2\u4e86\u3002\u201d \u3000\u3000\u80e1\u67cf\u9f84\u9053\uff1a\u201c\u4e5f\u5c31\u662f\u5982\u6b64\u800c\u5df2\u3002\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u9ed8\u4e86\u4e00\u9ed8\u9053\uff1a\u201c\u90a3\u4e48\u8001\u592b\u5c31\u8fd9\u6837\u56de\u590d\u4ee4\u5e08\u597d\u4e86\u3002\u201d \u3000\u3000\u201c\u5bb6\u5e08\u76ee\u524d\u5728\u4ec0\u4e48\u5730\u65b9\uff1f\u201d \u3000\u3000\u201c\u5999\u5999\u5eb5\u3002\u201d \u3000\u3000\u80e1\u67cf\u9f84\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u8fd9\u5c31\u5947\u602a\u4e86\uff0c\u665a\u8f88\u4e0a\u5348\u8fd8\u5230\u8fc7\u5999\u5999\u5eb5\uff0c\u504f\u504f\u5eb5\u4e3b\u4e0d\u51c6\u8fdb\u53bb\u3002\u65e2\u7136\u5bb6 \u5e08\u5728\u5999\u5999\u5eb5\uff0c\u4e3a\u4ec0\u4e48\u53c8\u4e0d\u51c6\u665a\u8f88\u4eb2\u81ea\u89c1\u4ed6\u8001\u4eba\u5bb6\uff1f\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u957f\u957f\u5401\u53e3\u6c14\u9053\uff1a\u201c\u5176\u4e2d\u539f\u56e0\uff0c\u8001\u592b\u4e5f\u770b\u51fa\u4e86\u4e9b\uff0c\u53ea\u662f\u5e76\u4e0d\u5341\u5206\u6e05\u695a\uff0c\u4f60\u53ef\u77e5\u9053\u5999 \u5999\u5eb5\u90a3\u4f4d\u8001\u5c3c\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u80e1\u67cf\u9f84\u9053\uff1a\u201c\u5982\u679c\u665a\u8f88\u6240\u6599\u4e0d\u5dee\uff0c\u5979\u5fc5\u662f\u4f20\u8bf4\u4e2d\u5f53\u5e74\u7684\u5999\u5999\u4ed9\u5b50\uff0c\u662f\u5bb6\u5e08\u7684\u5e08\u59b9\uff0c\u4e5f\u662f \u665a\u8f88\u7684\u5e08\u53d4\u3002\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u9894\u9996\u9053\uff1a\u201c\u534a\u70b9\u4e0d\u9519\uff0c\u5999\u5999\u5eb5\u7684\u90a3\u4f4d\u5999\u5999\u8001\u5c3c\uff0c\u6b63\u662f\u5f53\u5e74\u7684\u5999\u5999\u4ed9\u5b50\u3002\u201d \u3000\u3000\u80e1\u67cf\u9f84\u6025\u6025\u95ee\u9053\uff1a\u201c\u5979\u548c\u5bb6\u5e08\u53ef\u66fe\u89c1\u4e86\u9762\uff1f\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u9053\uff1a\u201c\u4ed6\u4eec\u5f53\u7136\u90fd\u89c1\u4e86\u9762\uff0c\u800c\u4e14\u76ee\u524d\u90fd\u5728\u5999\u5999\u5eb5\u3002\u201d \u3000\u3000\u80e1\u67cf\u9f84\u987f\u4e86\u987f\u9053\uff1a\u201c\u542c\u8bf4\u4ed6\u4eec\u5e08\u5144\u59b9\u4e4b\u95f4\u2026\u2026\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u4f3c\u662f\u4e5f\u6709\u7740\u65e0\u9650\u611f\u6168\u9053\uff1a\u201c\u4e0d\u9519\uff01\u5f53\u5e74\u4ed6\u4eec\u5e08\u5144\u59b9\u4e00\u5171\u4e09\u4eba\uff0e\u56db\u7edd\u8001\u4eba\u662f\u5927\u5e08\u5144\uff0c \u4e09\u5999\u4e66\u751f\u662f\u4e8c\u5e08\u5144\uff0c\u5999\u5999\u4ed9\u5b50\u662f\u5e08\u59b9\uff0e\u5e08\u5144\u59b9\u4e4b\u95f4\u53ef\u80fd\u5e74\u9f84\u76f8\u5dee\u4e0d\u5927\u3002\u4e8e\u662f\uff0c\u4e00\u6869\u7f20\u7ef5\u60b1\u607b \u7684\u7231\u60c5\u6545\u4e8b\uff0c\u5c31\u53d1\u751f\u5728\u4ed6\u4eec\u8eab\u4e0a\u3002\u201d \u3000\u3000\u201c\u540e\u6765\u5462\uff1f\u201d \u3000\u3000\u201c\u5999\u5999\u4ed9\u5b50\u65e2\u6df1\u7231\u7740\u5927\u5e08\u5144\uff0c\u53c8\u5bf9\u4e8c\u5e08\u5144\u4e0d\u80fd\u5fd8\u60c5\uff0c\u5728\u96be\u4ee5\u9009\u62e9\u4e4b\u4e0b\uff0c\u53ea\u597d\u5fcd\u6cea\u4ed6\u53bb\uff0c \u56db\u7edd\u8001\u4eba\u548c\u4e09\u5999\u4e66\u751f\u5bfb\u904d\u5929\u6daf\u6d77\u89d2\uff0c\u59cb\u7ec8\u65e0\u6cd5\u627e\u5230\u5979\u7684\u82b3\u8e2a\u3002\u4ece\u6b64\uff0c\u56db\u7edd\u8001\u4eba\u548c\u4e09\u5999\u4e66\u751f\u4e5f \u90fd\u8fdc\u907f\u5c18\u4e16\uff0e\u4e0e\u5916\u754c\u65ad\u7edd\u97f3\u4fe1\u3002\u201d \u3000\u3000\u201c\u636e\u665a\u8f88\u6240\u77e5\uff0e\u4e09\u5999\u5e08\u53d4\u6570\u5341\u5e74\u6765\uff0c\u4e00\u76f4\u4f4f\u5728\u5929\u53f0\u4e07\u82b1\u5bab\u5730\u5e9c\u4e4b\u5185\u3002\u201d \u3000\u3000\u201c\u8001\u592b\u542c\u56db\u7edd\u8001\u4eba\u548c\u5999\u5999\u8001\u5c3c\u7684\u8bed\u6c14\uff0e\u5999\u5999\u8001\u5c3c\u548c\u4e09\u5999\u4e66\u751f\u65e9\u6709\u8054\u7cfb\uff0c\u552f\u6709\u4ee4\u5e08\u56db\u7edd\u8001 \u4eba\uff0c\u662f\u6570\u6708\u524d\u624d\u5f97\u77e5\u4e09\u5999\u4e66\u751f\u7684\u9690\u5c45\u4e4b\u5904\u3002\u6240\u4ee5\u624d\u5199\u4e86\u4e00\u5c01\u4fe1\u547d\u4f60\u9001\u53bb\u3002\u81f3\u4e8e\u4eca\u5e08\u548c\u5999\u5999\u8001 \u5c3c\uff0c\u53ef\u80fd\u662f\u6570\u65e5\u524d\u624d\u5728\u4e00\u4e2a\u5076\u7136\u7684\u673a\u4f1a\u91cc\u4e0d\u671f\u800c\u9047\u7684\u3002\u201d \u3000\u3000\u201c\u4ed6\u4eec\u76f8\u604b\u767e\u4f59\u5e74\uff0c\u5982\u4eca\u89c1\u4e86\u9762\u2026\u2026\u201d \u3000\u3000\u201c\u4ed6\u4eec\u90fd\u662f\u4e00\u767e\u591a\u5c81\u5c06\u8fd1\u4e24\u767e\u5c81\u7684\u4eba\u4e86\uff0e\u6b64\u523b\u89c1\u4e86\u9762\uff0c\u9664\u4e86\u611f\u6168\u5f80\u4e8b\uff0c\u4e92\u53d9\u79bb\u8877\u5916\uff0c\u81ea \u7136\u5df2\u4e0d\u590d\u518d\u73b0\u5c0f\u513f\u5973\u79c1\u60c5\u3002\u201d \u3000\u3000\u80e1\u6015\u9f84\u671b\u4e86\u671b\u7a97\u5916\u5929\u8272\uff0e\u624d\u4e0d\u8fc7\u521a\u521a\u8d77\u66f4\uff0c\u5e26\u7740\u8feb\u4e0d\u53ca\u5f85\u7684\u795e\u8272\u9053\uff1a\u201c\u665a\u8f88\u51b3\u5b9a\u73b0\u5728\u5c31 \u518d\u8d76\u5f80\u5999\u5999\u5eb5\uff0c\u4ee5\u4fbf\u62dc\u89c1\u5bb6\u5e08\u548c\u5999\u5999\u5e08\u53d4\uff01\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u9053\uff1a\u201c\u4f60\u5df2\u77e5\u5999\u5999\u5eb5\u4e3b\u4e0d\u51c6\u4f60\u8fdb\u53bb\uff0c\u4f55\u5fc5\u518d\u53bb\uff1f\u201d \u3000\u3000\u80e1\u67cf\u9f84\u9053\uff1a\u201c\u73b0\u5728\u665a\u8f88\u5df2\u77e5\u4e0a\u60c5\uff0c\u5999\u5999\u5eb5\u4e3b\u5c31\u6ca1\u7406\u7531\u518d\u4e0d\u51c6\u665a\u8f88\u8fdb\u53bb\u3002\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u9053\uff1a\u201c\u636e\u8001\u592b\u6599\u60f3\uff0c\u5999\u5999\u5eb5\u4e3b\u4e0d\u51c6\u4f60\u8fdb\u53bb\uff0c\u3000\u4e00\u5b9a\u662f\u4ee4\u5e08\u7684\u6388\u610f\u3002\u201d \u3000\u3000\u201c\u5bb6\u5e08\u600e\u53ef\u80fd\u8fd9\u6837\u505a\u5462\uff1f\u201d \u3000\u3000\u201c\u7406\u7531\u5f88\u7b80\u5355\uff0c\u4ed6\u662f\u5e0c\u671b\u548c\u5999\u5999\u8001\u5c3c\u591a\u805a\u6664\u51e0\u65e5\uff0c\u4e0d\u613f\u4efb\u4f55\u4eba\u6253\u6270\u4ed6\u4eec\u7684\u6e05\u9759\u3002\u201d \u3000\u3000\u201c\u5bb6\u5e08\u4e5f\u8bb8\u5fd8\u4e86\uff0c\u665a\u8f88\u8fd9\u91cc\u8fd8\u6709\u4e09\u5999\u5e08\u53d4\u5199\u7ed9\u4ed6\u8001\u4eba\u5bb6\u7684\u4e00\u5c01\u4fe1\uff0c\u4ed6\u53c8\u600e\u4f1a\u4e0d\u6025\u7740\u770b \u4fe1\u3002\u201d \u3000\u3000\u201c\u65e2\u7136\u4f60\u6709\u4e09\u5999\u4e66\u751f\u7684\u4fe1\uff0c\u4e8b\u60c5\u5c31\u597d\u529e\u4e86\uff0e\u8d70\uff01\u8001\u592b\u966a\u4f60\u4e00\u8d77\u53bb\u3002\u201d \u3000\u3000\u4e09\u4eba\u51fa\u4e86\u5ba2\u623f\uff0c\u80e1\u67cf\u9f84\u4ea4\u4ee3\u9053\uff1a\u201c\u5c0f\u660e\u53bb\u628a\u9634\u624b\u4e00\u9b54\u7275\u51fa\u6765\uff01\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u2014\u6123\u9053\uff1a\u201c\u600e\u4e48\uff1f\u9634\u624b\u4e00\u9b54\u843d\u5728\u4f60\u4eec\u624b\u4e2d\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u5fd9\u9053\uff1a\u201c\u90a3\u8001\u5c0f\u5b50\u6295\u9760\u4e86\u970d\u5143\u4f3d\uff0c\u4e0a\u6b21\u665a\u8f88\u4eec\u52ab\u7262\u65f6\uff0e\u628a\u4ed6\u751f\u64d2\u6d3b\u6349\u4e86\u6765\u3002\u73b0\u5728 \u6b63\u662f\u8981\u62bc\u7740\u4ed6\u5230\u5415\u6881\u5c71\u53d6\u2018\u5411\u5fc3\u9732\u2019\u89e3\u836f\u3002\u201d \u3000\u3000\u201c\u539f\u6765\u5982\u6b64\uff0c\u8bb0\u5f97\u8001\u592b\u4e0a\u6b21\u66fe\u8bf4\u8fc7\u8981\u5e2e\u4f60\u4eec\u8bbe\u6cd5\u627e\u5230\u89e3\u836f\uff0c\u73b0\u5728\u8001\u592b\u5df2\u7ecf\u627e\u5230\u4e86\uff0c\u4f60\u4eec \u5c31\u7528\u4e0d\u7740\u518d\u9ebb\u70e6\u4e86\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u5927\u559c\u9053\uff1a\u201c\u5c31\u8bf7\u8001\u524d\u8f88\u518d\u56de\u623f\uff0c\u665a\u8f88\u628a\u9634\u624b\u4e00\u9b54\u5e26\u8fc7\u6765\uff0e\u8981\u4ed6\u8ba4\u4e00\u8ba4\u89e3\u836f\u5bf9\u4e0d \u5bf9\u3002\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u9053\uff1a\u201c\u8fd9\u5012\u4f7f\u5f97\u3002\u201d \u3000\u3000\u5f53\u5e9e\u58eb\u51b2\u548c\u80e1\u67cf\u9f84\u56de\u5230\u623f\u95f4\u4e0d\u4e45\uff0c\u9ea6\u5c0f\u660e\u4fbf\u4ece\u53e6\u2014\u623f\u95f4\u628a\u9634\u624b\u4e00\u9b54\u62bc\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u5e9e\u58eb\u51b2\u4e0d\u89c9\u563f\u563f\u7b11\u9053\uff1a\u201c\u9634\u624b\u8001\u9b3c\uff0c\u60f3\u4e0d\u5230\u4f60\u4e5f\u6709\u4eca\u5929\uff01\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u9762\u8272\u618b\u5f97\u6709\u5982\u732a\u809d\uff0c\u54ea\u91cc\u8fd8\u80fd\u8bb2\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u5e9e\u58eb\u51b2\u4ece\u6000\u91cc\u6478\u51fa\u4e00\u53ea\u7eff\u8272\u7389\u74f6\uff0c\u5012\u51fa\u4e8c\u7c92\u4e38\u9053\uff1a\u201c\u9634\u624b\u8001\u9b3c\uff0c\u8001\u592b\u5f04\u6765\u7684\u2018\u5411\u5fc3\u9732\u2019 \u89e3\u836f\uff0c\u4f60\u770b\u770b\u5bf9\u4e0d\u5bf9\uff1f\u5982\u679c\u4e0d\u5dee\uff0e\u8001\u592b\u5c31\u8bb2\u4e2a\u4eba\u60c5\uff0c\u73b0\u5728\u5c31\u653e\u4e86\u4f60\u3002\u201d \u3000\u3000\u9634\u624b\u4e00\u9b54\u770b\u4e86\u4e00\u770b\uff0c\u7acb\u523b\u8bf4\u9053\uff1a\u201c\u4e00\u70b9\u4e0d\u9519\uff0c\u6b63\u662f\u5411\u5fc3\u9732\u89e3\u836f\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u5fd9\u9053\uff1a\u201c\u5e9e\u8001\u4f2f\uff0c\u8fd9\u8001\u5c0f\u5b50\u4e3a\u4e86\u653e\u4ed6\uff0c\u60a8\u8fd9\u89e3\u836f\uff0c\u5373\u4f7f\u662f\u5047\u7684\uff0c\u4ed6\u4e5f\u5fc5\u8bf4\u662f\u771f\u7684\u3002 \u4ed6\u8bf4\u771f\u7684\uff0c\u600e\u80fd\u7b97\u6570\uff1f\u201d \u3000\u3000\u7a81\u89c1\u5e9e\u58eb\u51b2\u4e00\u62ac\u624b\uff0c\u4fbf\u628a\u9634\u624b\u4e00\u9b54\u70b9\u660f\uff0c\u63a5\u7740\u4ece\u6000\u91cc\u53c8\u638f\u51fa\u4e00\u7c92\u7ea2\u8272\u836f\u4e38\u9053\uff1a\u201c\u7528\u8336\u6c34 \u7ed9\u4ed6\u670d\u4e0b\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u6014\u6014\u7684\u9053\uff1a\u201c\u8fd9\u662f\u4ec0\u4e48\u836f\uff1f\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u9053\uff1a\u201c\u6bd4\u2018\u5411\u5fc3\u9732\u2019\u66f4\u5389\u5bb3\u7684\u4e00\u79cd\u836f\uff0c\u8fd9\u6837\u4e00\u6765\uff0c\u5c31\u53ef\u5b89\u5fc3\u628a\u4ed6\u653e\u8d70\u4e86\uff0c\u4ece\u4eca\u4e4b \u540e\uff0c\u8fd9\u8001\u5c0f\u5b50\u4e0d\u53ef\u80fd\u518d\u5174\u98ce\u4f5c\u6d6a\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u8001\u524d\u8f88\u7684\u89e3\u836f\u7a76\u7adf\u5bf9\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u201c\u4f60\u653e\u5fc3\uff0c\u8001\u592b\u8fd9\u4f4d\u597d\u53cb\uff0c\u4eba\u79f0\u533b\u9690\uff0c\u6b67\u9ec4\u4e4b\u672f\uff0c\u582a\u79f0\u72ec\u6b65\u5f53\u4e16\u3002\u636e\u8bf4\u8fde\u9634\u624b\u4e00\u9b54\u7814\u5236 \u7684\u2018\u5411\u5fc3\u9732\u2019\u90fd\u662f\u5077\u4ed6\u7684\u79d8\u65b9\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u4f9d\u8a00\u628a\u90a3\u7c92\u7ea2\u8272\u836f\u4e38\u5582\u9634\u624b\u2014\u9b54\u670d\u4e0b\u3002 \u3000\u3000\u5e9e\u58eb\u51b2\u9053\uff1a\u201c\u628a\u4ed6\u518d\u62d6\u56de\u539f\u6765\u623f\u95f4\uff0c\u5f53\u4ed6\u9192\u6765\u4e4b\u540e\uff0c\u5fc5\u5b9a\u795e\u667a\u5168\u5931\u3002\u201d \u3000\u3000\u80e1\u67cf\u9f84\u9053\uff1a\u201c\u5c0f\u660e\u628a\u9634\u624b\u4e00\u9b54\u623f\u95e8\u9501\u4e0a\uff0c\u73b0\u5728\u54b1\u4eec\u8be5\u52a8\u8eab\u5230\u5999\u5999\u5eb5\u4e86\u3002\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u9053\uff1a\u201c\u4f9d\u8001\u592b\u4e4b\u610f\uff0c\u4eca\u665a\u6700\u597d\u5c31\u5728\u5ba2\u5e97\u5bbf\u4e0b\uff0c\u6df1\u591c\u53bb\u6253\u6270\u4ee4\u5e08\u548c\u4ee4\u5e08\u53d4\uff0c\u5c82\u4e0d\u5e72 \u6270\u4e86\u4ed6\u4eec\u7684\u6e05\u9759\uff0c\u660e\u5929\u4e00\u65e9\u51fa\u53d1\u4e0d\u8fdf\u3002\u201d \u3000\u3000\u6b21\u65e5\u4e00\u65e9\uff0c\u5e9e\u58eb\u51b2\u3001\u80e1\u67cf\u9f84\u3001\u9ea6\u5c0f\u660e\u4fbf\u8d77\u6765\u7528\u65e9\u996d\u3002 \u3000\u3000\u996d\u540e\u4fbf\u88c5\u51fa\u53d1\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u9634\u624b\u8001\u5c0f\u5b50\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u9053\uff1a\u201c\u628a\u4ed6\u5e26\u5230\u91ce\u5916\u518d\u653e\u4eba\uff01\u201d \u3000\u3000\u7275\u51fa\u9634\u624b\u4e00\u9b54\uff0c\u679c\u7136\u4e24\u773c\u53c8\u76f4\u53c8\u5446\uff0c\u548c\u767d\u75f4\u4e00\u822c\u65e0\u4e8c\u3002 \u3000\u3000\u51fa\u4e86\u57ce\uff0c\u6765\u5230\u90ca\u5916\uff0c\u9ea6\u5c0f\u660e\u53c8\u95ee\u9053\uff1a\u201c\u5e9e\u8001\u4f2f\uff0c\u5982\u679c\u5c31\u8fd9\u6837\u628a\u4ed6\u653e\u8d70\uff0c\u4ed6\u5c82\u4e0d\u8981\u6d3b\u6d3b\u997f \u6b7b\uff1f\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u5b50\u600e\u4e48\u5ffd\u7136\u53d1\u8d77\u6148\u60b2\u6765\u4e86\uff1f\u8fd9\u79cd\u4eba\u997f\u6b7b\u8fd8\u6709\u4ec0\u4e48\u53ef\u60dc\u7684\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u7acb\u5373\u4e3a\u9634\u624b\u4e00\u9b54\u89e3\u5f00\u7d22\uff0e\u5728\u4ed6\u5c41\u80a1\u4e0a\u8e22\u4e86\u2014\u811a\u9053\uff1a\u201c\u8001\u5c0f\u5b50\uff0e\u6eda\u5427\uff01\u201d \u3000\u3000\u9634\u624b\u2014\u9b54\u534a\u70b9\u6ca1\u6709\u77e5\u89c9\uff0e\u53ea\u662f\u6643\u4e86\u4e00\u8ddf\u5934\uff0c\u7136\u540e\u6447\u6447\u6446\u6446\u7684\u8e09\u8dc4\u800c\u53bb\uff0c\u4e00\u4ee3\u9b54\u5934\u843d\u5f97\u5982 \u6b64\u4e0b\u573a\uff0e\u4e5f\u591f\u51c4\u60e8\u7684\u4e86\u3002 \u3000\u3000\u4e0d\u5230\u4e00\u4e2a\u65f6\u8fb0\uff0c\u4fbf\u5230\u8fbe\u5999\u5999\u5eb5\u524d\u3002 \u3000\u3000\u8fd9\u6b21\u95e8\u5916\u65e0\u4eba\uff0e\u5e9e\u58eb\u51b2\u9053\uff1a\u201c\u4e0d\u53ef\u592a\u83bd\u649e\uff0c\u5f85\u8001\u592b\u5148\u8fdb\u53bb\u770b\u770b\uff01\u201d \u3000\u3000\u5f88\u4e45\u4e4b\u540e\uff0c\u5e9e\u58eb\u51b2\u624d\u9762\u5e26\u7b11\u5bb9\uff0c\u8d70\u4e86\u51fa\u6765\u9053\uff1a\u201c\u8fdb\u6765\u5427\uff01\u56db\u7edd\u548c\u5999\u5999\u4e24\u4f4d\u524d\u8f88\u6b63\u5728\u4f5b\u5802 \u540e\u7684\u7cbe\u820d\u91cc\u7b49\u4f60\uff01\u201d \u3000\u3000\u80e1\u67cf\u9f84\u6574\u4e86\u6574\u8863\u51a0\uff0c\u5728\u5e9e\u58eb\u51b2\u7684\u524d\u5bfc\u4e0b\uff0e\u5e26\u7740\u9ea6\u5c0f\u660e\u8fdb\u95e8\u524d\u5f80\u4f5b\u5802\u540e\u800c\u6765\u3002 \u3000\u3000\u8d70\u5230\u7cbe\u820d\u95e8\u5916\uff0c\u53ea\u89c1\u56db\u7edd\u8001\u4eba\u548c\u5999\u5999\u8001\u5c3c\u6b63\u5e76\u80a9\u5750\u5728\u90a3\u91cc\u3002 \u3000\u3000\u80e1\u67cf\u9f84\u614c\u5fd9\u8083\u5bb9\u4e0a\u524d\u62dc\u89c1\uff0c\u62dc\u5b8c\u4e86\u56db\u7edd\u8001\u4eba\uff0c\u518d\u62dc\u5999\u5999\u8001\u5c3c\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u4e5f\u8ddf\u7740\u4e0a\u524d\u5927\u793c\u62dc\u89c1\u3002 \u3000\u3000\u56db\u7edd\u8001\u4eba\u62db\u547c\u5e9e\u58eb\u51b2\u5728\u4e00\u65c1\u5750\u4e0b\uff0c\u95ee\u80e1\u67cf\u9f84\u9053\uff1a\u201c\u539f\u6765\u4f60\u5df2\u8ba4\u8bc6\u4f60\u5e08\u53d4\uff1f\u201d \u3000\u3000\u80e1\u67cf\u9f84\u8eac\u8eab\u9053\uff1a\u201c\u5f1f\u5b50\u4ece\u5929\u53f0\u5c71\u8fd4\u56de\u65f6\uff0c\u5728\u5eb5\u5916\u5c71\u4e0a\u89c1\u8fc7\u5e08\u53d4\uff0c\u4f46\u90a3\u65f6\u5e08\u53d4\u5e76\u672a\u8868\u660e\u8eab \u4efd\u3002\u201d \u3000\u3000\u56db\u7edd\u8001\u4eba\u9053\uff1a\u201c\u96be\u5f97\u4eca\u5929\u4f60\u7ec8\u4e8e\u6b63\u5f0f\u89c1\u5230\u4f60\u5e08\u53d4\uff0c\u8fde\u4e3a\u5e08\u4e5f\u662f\u767e\u5e74\u6765\u76f4\u5230\u524d\u51e0\u5929\u624d\u6709\u673a \u4f1a\u548c\u4f60\u5e08\u53d4\u89c1\u9762\u3002\u5230\u5929\u53f0\u4e07\u82b1\u5bab\u7684\u4e8b\u600e\u4e48\u6837\u4e86\uff1f\u201d \u3000\u3000\u80e1\u67cf\u9f84\u8fde\u5fd9\u606d\u606d\u656c\u656c\u7684\u5c06\u4e09\u5999\u4e66\u751f\u7684\u4fe1\u5448\u4e0a\uff0c\u5e76\u6697\u4e2d\u7559\u610f\u56db\u7edd\u8001\u4eba\u770b\u4fe1\u7684\u795e\u60c5\u3002 \u3000\u3000\u56db\u7edd\u8001\u4eba\u5148\u662f\u6709\u4e9b\u6fc0\u52a8\uff0c\u4f46\u5f88\u5feb\u4fbf\u5e73\u9759\u4e0b\u6765\uff0e\u770b\u5b8c\u4fe1\uff0c\u8f6c\u5934\u9053\uff1a\u201c\u4e8c\u5e08\u5f1f\u672c\u8981\u6765\u5317\u5cb3\u770b \u6211\uff0e\u4f46\u53c8\u56e0\u6545\u4e0d\u4fbf\u8fdc\u884c\uff0c\u8fd8\u662f\u7531\u6211\u5230\u5929\u53f0\u5c71\u53bb\u770b\u4ed6\u5427\uff01\u5e08\u59b9\u8981\u4e0d\u8981\u4e00\u8d77\u524d\u53bb\uff1f\u201d \u3000\u3000\u5999\u5999\u8001\u5c3c\u7f04\u9ed8\u534a\u664c\u9053\uff1a\u201c\u8fd8\u662f\u5148\u628a\u8fd9\u91cc\u7684\u4e8b\u5904\u7406\u5b8c\u6bd5\u518d\u8bf4\u5427\uff01\u90a3\u65f6\u5c0f\u59b9\u5b9a\u4f1a\u966a\u540c\u5927\u5e08\u5144 \u524d\u53bb\u3002\u201d \u3000\u3000\u56db\u7edd\u8001\u4eba\u9894\u9996\u9053\uff1a\u201c\u4e5f\u597d\uff0c\u5e08\u59b9\u73b0\u5728\u5c31\u544a\u8bc9\u9f84\u513f\u771f\u76f8\u5427\uff01\u4e5f\u597d\u7ed9\u4ed6\u4e00\u4e2a\u60ca\u559c\u3002\u201d \u3000\u3000\u5999\u5999\u8001\u5c3c\u8f6c\u8fc7\u5934\u6765\uff0c\u89c6\u7ebf\u6295\u5411\u80e1\u67cf\u9f84\u9053\uff1a\u201c\u9f84\u513f\uff0c\u4f60\u8fd8\u60f3\u4e0d\u60f3\u5ff5\u4f60\u59bb\u9999\u513f\uff1f\u201d \u3000\u3000\u80e1\u67cf\u9f84\u5fc3\u7830\u7136\u4e00\u52a8\uff0e\u5e94\u9053\uff1a\u201c\u5f1f\u5b50\u5f53\u65e5\u4e0e\u5bd2\u9999\u8a93\u540c\u751f\u6b7b\uff0e\u5982\u4eca\u5f1f\u5b50\u82db\u6d3b\u4eba\u4e16\uff0e\u548c\u5979\u5929\u4eba \u6c38\u9694\uff0c\u5c82\u6709\u4e0d\u601d\u5ff5\u5979\u7684\u9053\u7406\uff01\u201d \u3000\u3000\u5999\u5999\u8001\u5c3c\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u90a3\u4e48\u5e08\u6743\u73b0\u5728\u5c31\u544a\u8bc9\u4f60\uff0c\u9999\u513f\u5e76\u6ca1\u6709\u6b7b\u3002\u201d \u3000\u3000\u80e1\u67cf\u9f84\u4e00\u9635\u60ca\u559c\u4e4b\u4e0b\uff0e\u53cd\u800c\u5446\u5728\u5f53\u5730\uff0c\u4e45\u4e45\u8bf4\u4e0d\u5c71\u8bdd\u6765\u3002 \u3000\u3000\u5e9e\u58eb\u51b2\u4e5f\u51e0\u4e4e\u96be\u4ee5\u76f8\u4fe1\u81ea\u5df1\u7684\u8033\u6735\u3002 \u3000\u3000\u552f\u6709\u9ea6\u5c0f\u660e\uff0c\u56e0\u65e9\u5c31\u8ba4\u5b9a\u90a3\u9ed1\u8863\u8499\u9762\u5973\u5b50\u5fc5\u662f\u8c37\u5bd2\u9999\uff0c\u56e0\u4e4b\uff0e\u53cd\u800c\u80fd\u4fdd\u6301\u9547\u5b9a\uff0e \u3000\u3000\u534a\u664c\u4e4b\u540e\uff0e\u80e1\u67cf\u9f84\u624d\u6fc0\u52a8\u65e0\u6bd4\u7684\u9053\uff1a\u201c\u5bd2\u9999\u7a76\u7adf\u662f\u600e\u4e48\u6d3b\u56de\u6765\u7684\uff1f\u5e08\u53d4\u5feb\u8bf7\u544a\u8bc9\u5f1f\u5b50\uff01\u201d \u3000\u3000\u5999\u5999\u8001\u5c3c\u9053\uff1a\u201c\u662f\u4f60\u4e09\u5999\u5e08\u53d4\u628a\u5979\u6551\u6d3b\u7684\u3002\u201d \u3000\u3000\u201c\u5979\u2026\u2026\u5979\u80fd\u6b7b\u800c\u590d\u6d3b\u5417\uff1f\u201d \u3000\u3000\u201c\u8fd9\u8bdd\u5b9e\u5728\u95ee\u5f97\u592a\u50bb\uff0e\u4f60\u672c\u8eab\u4e0d\u4e5f\u662f\u6b7b\u800c\u590d\u6d3b\u5417\uff1f\u4f60\u5e08\u7236\u80fd\u6551\u5f97\u4e86\u4f60\uff0e\u96be\u9053\u4f60\u4e8c\u5e08\u53d4\u5c31 \u6551\u4e0d\u4e86\u4f60\u7684\u59bb\u5b50\uff1f\u4f55\u51b5\u636e\u8bf4\u9999\u513f\u5f53\u65f6\u7684\u4f24\u52bf\uff0e\u6bd4\u4f60\u8fd8\u8f7b\u4e9b\u3002\u201d \u3000\u3000\u80e1\u67cf\u9f84\u4e0d\u6562\u518d\u591a\u95ee\u8fd9\u65b9\u9762\u7684\u4e8b\uff0e\u8feb\u4e0d\u53ca\u5f85\u7684\u9053\uff1a\u201c\u5bd2\u9999\u76ee\u524d\u5728\u54ea\u91cc\uff1f\u53ef\u5426\u5bb9\u5f1f\u5b50\u548c\u5979\u4e00 \u89c1\uff1f\u201d \u3000\u3000\u5999\u5999\u8001\u5c3c\u5429\u5490\u9ea6\u5c0f\u660e\u9053\uff1a\u201c\u4f60\u53bb\u901a\u77e5\u9759\u6708\uff0c\u8981\u5979\u628a\u9999\u513f\u5e26\u5230\u8fd9\u91cc\u6765\uff01\u201d \u3000\u3000\u4e0d\u5927\u4e00\u4f1a\u513f\uff0e\u8c37\u5bd2\u9999\u679c\u7136\u968f\u7740\u9759\u6708\uff0e\u5728\u9ea6\u5c0f\u660e\u7684\u966a\u540c\u4e0b\uff0c\u59d7\u59d7\u8fdb\u5165\u7cbe\u820d\u3002 \u5367\u9f99\u751f\u300a\u5999\u7edd\u5929\u9999\u300b \u7b2c\u4e09\u5341\u4e09\u56de\u3000\u590d\u7edf\u7eff\u6797 \u3000\u3000\u4e0d\u96be\u60f3\u89c1\uff0c\u5728\u8fd9\u5239\u90a3\uff0c\u80e1\u67cf\u9f84\u548c\u8c37\u5bd2\u9999\u592b\u59bb\u4e45\u522b\u91cd\u9022\uff0c\u53c8\u90fd\u662f\u5386\u7ecf\u6b7b\u52ab\u5f52\u6765\uff0e\u7b52\u76f4\u604d\u5982 \u8eab\u5728\u68a6\u4e2d\uff0e\u6068\u4e0d\u5f97\u76f8\u62e5\u75db\u54ed\u4e00\u573a\u3002\u4f46\u56e0\u6709\u56db\u7edd\u8001\u4eba\u3001\u5999\u5999\u8001\u5c3c\u4ee5\u53ca\u5e9e\u58eb\u51b2\u5728\uff0c\u4e5f\u53ea\u6709\u5f3a\u5fcd\u5185 \u5fc3\u7684\u6fc0\u52a8\u3002 \u3000\u3000\u8c37\u5bd2\u9999\u4f9d\u6b21\u5411\u56db\u7edd\u8001\u4eba\u3001\u5999\u5999\u8001\u5c3c\u3001\u5e9e\u58eb\u51b2\u89c1\u793c\uff0c\u7136\u540e\u5f85\u7acb\u5728\u5999\u5999\u8001\u5c3c\u8eab\u65c1\uff0e \u3000\u3000\u5ffd\u542c\u9ea6\u5c0f\u660e\u5927\u58f0\u9053\uff1a\u201c\u8001\u5e08\u592a\uff0e\u60a8\u592a\u4e0d\u5e94\u8be5\u4e86\u3002\u201d \u3000\u3000\u4ed6\u8fd9\u5927\u58f0\u4e00\u53eb\uff0c\u4f7f\u5f97\u5ba4\u5185\u6240\u6709\u7684\u4eba\uff0c\u90fd\u9f50\u9f50\u4e00\u60ca\u3002 \u3000\u3000\u4f46\u5999\u5999\u8001\u5c3c\u5374\u5e76\u672a\u52a8\u6012\uff0c\u7f13\u7f13\u95ee\u9053\uff1a\u201c\u4f60\u4e14\u8bf4\u8bf4\uff0c\u6211\u6709\u4ec0\u4e48\u4e0d\u8be5\uff1f\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u7406\u76f4\u6c14\u58ee\u7684\u9053\uff1a\u201c\u665a\u8f88\u4ece\u524d\u6240\u89c1\u7684\u90a3\u4f4d\u9ed1\u8863\u8499\u9762\u5973\u4fa0\uff0c\u5206\u660e\u5c31\u662f\u665a\u8f88\u7684\u5e08\u5ac2\uff0e\u4f60 \u8001\u4eba\u5bb6\u5374\u4e0d\u4f46\u4e00\u518d\u9690\u7792\uff0c\u800c\u4e14\u751a\u81f3\u5e26\u7740\u6211\u4eec\u5230\u5893\u5730\u53bb\u770b\u9b3c\u3002\u60a8\u8fd9\u6837\u505a\uff0c\u5c82\u4e0d\u662f\u5927\u5927\u4e0d\u8be5\uff1f\u201d \u3000\u3000\u5999\u5999\u8001\u5c3c\u6de1\u6de1\u4e00\u7b11\u9053\uff1a\u201c\u6211\u6709\u4e0d\u5f97\u5df2\u7684\u82e6\u8877\uff0c\u4e0d\u5f97\u4e0d\u8fd9\u6837\u505a\u3002\u201d \u3000\u3000\u201c\u4f60\u8001\u4eba\u5bb6\u6709\u4ec0\u4e48\u82e6\u8877\uff1f\u201d \u3000\u3000\u201c\u8fd9\u662f\u4e09\u5999\u4e8c\u5e08\u5144\u4ea4\u4ee3\u7684\uff0c\u4ed6\u6253\u53d1\u9999\u513f\u7531\u5929\u53f0\u5c71\u5230\u8fd9\u513f\u6765\u89c1\u6211\uff0c\u8981\u6211\u4ee3\u4e3a\u7167\u770b\u9999\u513f\uff0c\u6388 \u5979\u6b66\u529f\uff0c\u5e76\u5199\u6765\u4e00\u5c01\u4eb2\u7b14\u4fe1\uff0c\u8981\u6211\u544a\u8beb\u5979\u6682\u65f6\u4e0d\u53ef\u4e0e\u6614\u65e5\u4efb\u4f55\u4eb2\u4eba\u76f8\u8ba4\u3002\u201d \u3000\u3000\u201c\u8fd9\u662f\u4ec0\u4e48\u539f\u56e0\u5462\uff1f\u201d \u3000\u3000\u201c\u8fd9\u662f\u4e09\u7092\u4e8c\u5e08\u5144\u7684\u90d1\u91cd\u4ea4\u4ee3\uff0c\u8fde\u6211\u4e5f\u4e0d\u6e05\u695a\u662f\u4ec0\u4e48\u539f\u56e0\u3002\u8fd9\u6837\u505a\u5c82\u53ea\u4f60\u4eec\u73b0\u5728\u4e0d\u8c05\u89e3\uff0e \u8fde\u9999\u513f\u4e5f\u5927\u4e0d\u60c5\u613f\u3002\u4f46\u5e08\u547d\u5728\u8eab\uff0c\u5979\u53c8\u4e0d\u6562\u4e0d\u9075\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u81ea\u77e5\u9519\u53d1\u4e86\u6027\u5b50\uff0c\u53ea\u597d\u6b49\u7136\u7684\u8bf4\uff1a\u201c\u90a3\u4e48\u665a\u8f88\u7684\u5e08\u5ac2\u73b0\u5728\u5df2\u4ee5\u672c\u6765\u9762\u76ee\u51fa\u73b0\uff0e\u4f60 \u8001\u4eba\u5bb6\u603b\u8be5\u8ba9\u5979\u548c\u80e1\u5e08\u5144\u592b\u59bb\u56e2\u805a\u4e86\u5427\uff01\u201d \u3000\u3000\u5999\u5999\u8001\u5c3c\u7565\u4e00\u72b9\u8c6b\uff0c\u7ec8\u4e8e\u70b9\u4e86\u70b9\u5934\u9053\uff1a\u201c\u597d\u5427\uff01\u6211\u867d\u6709\u610f\u4ecd\u628a\u5979\u7559\u5728\u5eb5\u4e2d\uff0c\u4f46\u53c8\u4e0d\u5fcd\u62c6 \u6563\u4ed6\u4eec\u592b\u59bb\uff0c\u8fc7\u4e24\u5929\u6211\u5c31\u628a\u5979\u9001\u5230\u4e07\u6708\u5ce1\u53bb\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u5927\u559c\uff0c\u5fd9\u9053\uff1a\u201c\u665a\u8f88\u4eec\u73b0\u5728\u5df2\u7ecf\u642c\u5230\u4e94\u53f0\u5c71\u5357\u9e93\u7684\u5cb3\u738b\u5e99\uff01\u201d \u3000\u3000\u5999\u5999\u8001\u5c3c\u9053\uff1a\u201c\u90a3\u5c31\u66f4\u8fd1\u4e86\uff0c\u73b0\u5728\u4f60\u4eec\u53ef\u4ee5\u8d70\u5566\uff01\u4e09\u5929\u4e4b\u5185\uff0c\u6211\u4e00\u5b9a\u4f1a\u8981\u9999\u513f\u5230\u5cb3\u738b\u5e99 \u548c\u4f60\u4eec\u76f8\u4f1a\u3002\u201d \u3000\u3000\u53ea\u542c\u5e9e\u58eb\u51b2\u8f7b\u54b3\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u665a\u8f88\u6709\u51e0\u53e5\u8bdd\u60f3\u4e0e\u4e8c\u4f4d\u8001\u524d\u8f88\u8bb2\uff0c\u4e0d\u77e5\u4e24\u4f4d\u8001\u524d\u8f88\u662f\u5426\u80af \u5bb9\u665a\u8f88\u4e00\u8a00\uff01\u201d \u3000\u3000\u56db\u7edd\u8001\u4eba\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u5e9e\u8001\u5f1f\u6709\u8bdd\u8bf7\u8bb2\uff01\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u8083\u5bb9\u6b63\u8272\u9053\uff1a\u201c\u67cf\u9f84\u548c\u9999\u513f\u5f53\u5e74\u90fd\u66fe\u505a\u8fc7\u5929\u4e0b\u7eff\u6797\u76df\u4e3b\uff0c\u8001\u524d\u8f88\u53ef\u5426\u5f00\u6069\u8ba9\u4ed6\u4eec \u91cd\u8fd4\u8ff7\u8e2a\u8c37\uff0c\u518d\u4e3a\u5929\u4e0b\u7eff\u6797\u5c3d\u4e00\u4efd\u529b\u91cf\u3002\u201d \u3000\u3000\u56db\u7edd\u8001\u4eba\u9762\u73b0\u4e0d\u60a6\u4e4b\u8272\u9053\uff1a\u201c\u8fd9\u8bdd\u53ef\u662f\u9f84\u513f\u62dc\u6258\u5e9e\u8001\u5f1f\u8bb2\u7684\uff1f\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u8fde\u5fd9\u6447\u5934\u9053\uff1a\u201c\u8001\u524d\u8f88\u522b\u8bef\u4f1a\uff0c\u67cf\u9f84\u4ece\u6ca1\u5411\u665a\u8f88\u63d0\u8d77\u8fd9\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u201c\u65e2\u7136\u5982\u6b64\uff0c\u5e9e\u8001\u5f1f\u53c8\u4e3a\u4f55\u4ee3\u4ed6\u4eec\u8bb2\u8bdd\uff1f\u201d \u3000\u3000\u201c\u8001\u524d\u8f88\u60f3\u5fc5\u5df2\u7ecf\u77e5\u9053\uff0c\u76ee\u524d\u7684\u7eff\u6797\u76df\u4e3b\u970d\u5143\u4f3d\uff0e\u4e3a\u6076\u7d2f\u7d2f\uff0c\u5c45\u5fc3\u53f5\u6d4b\uff0c\u82e5\u4efb\u7531\u4ed6\u7316\u72c2 \u4e0b\u53bb\uff0c\u65e5\u540e\u7eff\u6797\u82cd\u751f\uff0c\u5fc5\u906d\u5927\u52ab\u3002\u665a\u8f88\u4e3a\u5929\u4e0b\u7eff\u6797\u7740\u60f3\uff0c\u624d\u4e0d\u5f97\u4e0d\u5411\u8001\u524d\u8f88\u8fdb\u8a00\uff01\u201d \u3000\u3000\u56db\u7edd\u8001\u4eba\u542c\u540e\u795e\u8272\u6df1\u6c89\uff0c\u4e0d\u518d\u8a00\u8bed\u3002 \u3000\u3000\u5999\u5999\u8001\u5c3c\u8f7b\u8f7b\u53f9\u606f\u4e00\u58f0\uff0c\u4fa7\u8138\u9053\uff1a\u201c\u5927\u5e08\u5144\uff0c\u4ed6\u8bf4\u5f97\u6709\u7406\uff0c\u4e3a\u633d\u6551\u5929\u4e0b\u7eff\u6797\u82cd\u751f\uff0c\u4f60\u5c31 \u7528\u4e0d\u7740\u518d\u575a\u6301\u4e86\uff01\u201d \u3000\u3000\u56db\u7edd\u8001\u4eba\u6c89\u541f\u4e86\u534a\u54cd\uff0c\u7ec8\u4e8e\u957f\u5401\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u8981\u4ed6\u4eec\u5148\u56de\u53bb\uff0c\u8ba9\u6211\u4ed4\u7ec6\u8003\u8651\uff01\u201d \u3000\u3000\u8f9e\u51fa\u5999\u5999\u5eb5\u7684\uff0c\u53ea\u6709\u80e1\u67cf\u9f84\u548c\u9ea6\u5c0f\u660e\uff0c\u5e9e\u58eb\u51b2\u51b3\u5b9a\u7559\u4e0b\u6765\u627e\u673a\u4f1a\u7ee7\u7eed\u8bf4\u52a8\u56db\u7edd\u8001\u4eba\u3002 \u3000\u3000\u5728\u56de\u7a0b\u7684\u8def\u4e0a\uff0e\u80e1\u67cf\u9f84\u548c\u9ea6\u5c0f\u660e\u5185\u5fc3\u90fd\u5145\u6ee1\u7740\u65e0\u5c3d\u5174\u594b\uff0e\u7eb5\u7136\u56db\u7edd\u8001\u4eba\u4e0d\u51c6\u4ed6\u4eec\u91cd\u8fd4\u8ff7 \u8e2a\u8c37\uff0e\u81f3\u5c11\u80e1\u67cf\u9f84\u548c\u8c37\u5bd2\u9999\u5fc5\u5f97\u56e2\u5706\uff0c\u7167\u6837\u4e5f\u662f\u4e00\u4ef6\u5929\u5927\u7684\u559c\u4e8b\u3002 \u3000\u3000\u4ed6\u4eec\u4e00\u8def\u6025\u8d76\uff0e\u8fde\u591c\u95f4\u90fd\u4e0d\u66fe\u6295\u5bbf\uff0c\u6b21\u65e5\u5348\u540e\uff0c\u4fbf\u5230\u8fbe\u4e94\u53f0\u5357\u9e93\u5cb3\u738b\u5e99\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u7b49\u4eba\u89c1\u80e1\u67cf\u9f84\u548c\u9ea6\u5c0f\u660e\u8fd9\u4e48\u5feb\u5c31\u56de\u6765\uff0c\u90fd\u6709\u4e9b\u832b\u7136\uff0e\u4e89\u7740\u4e0a\u524d\u95ee\u957f\u95ee\u77ed\u3002 \u3000\u3000\u5f53\u9ea6\u5c0f\u660e\u8bf4\u660e\u66fe\u5230\u5999\u5999\u5eb5\u4e0e\u8c37\u5bd2\u9999\u76f8\u4f1a\u7684\u7ecf\u8fc7\u540e\uff0e\u4f17\u4eba\u83ab\u4e0d\u6b22\u6b23\u9f13\u821e\uff0e\u96c0\u8dc3\u4e09\u4e08\uff0c\u53ea\u76fc \u8c37\u5bd2\u9999\u80fd\u65e9\u65e5\u6765\u4e0e\u4ed6\u4eec\u76f8\u805a\u3002 \u3000\u3000\u8fd9\u65f6\u7531\u7262\u91cc\u6551\u56de\u6765\u7684\u4e03\u4eba\uff0e\u90fd\u5df2\u6062\u590d\u5f97\u5dee\u4e0d\u591a\uff0e\u53ea\u8981\u56db\u7edd\u8001\u4eba\u5141\u51c6\u80e1\u67cf\u9f84\u548c\u8c37\u5bd2\u9999\u91cd\u8fd4 \u8ff7\u8e2a\u8c37\uff0e\u4ed6\u4eec\u90fd\u53ef\u53c2\u52a0\u6218\u6597\u884c\u5217\u3002 \u3000\u3000\u4e09\u5929\u540e\uff0c\u8c37\u5bd2\u9999\u679c\u771f\u6765\u5230\u5cb3\u738b\u5e99\uff0c\u534a\u5e74\u4e0d\u89c1\uff0c\u4f3c\u662f\u8d8a\u53d1\u56fd\u8272\u5929\u9999\uff0e\u5149\u8273\u7167\u4eba\u3002 \u3000\u3000\u966a\u540c\u8c37\u5bd2\u9999\u524d\u6765\u7684\uff0e\u9664\u5e9e\u58eb\u51b2\u5916\uff0c\u8fde\u4e07\u6620\u971e\u4e5f\u6765\u4e86\u3002 \u3000\u3000\u5e9e\u58eb\u51b2\u548c\u80e1\u67cf\u9f84\u7b49\u4eba\u2014\u89c1\u9762\u5c31\u5ba3\u5e03\u4e86\u4e2a\u597d\u6d88\u606f\uff0e\u90a3\u5c31\u662f\u56db\u7edd\u8001\u4eba\u5df2\u51c6\u8bb8\u80e1\u67cf\u9f84\u91cd\u8fd4\u8ff7\u8e2a \u8c37\u518d\u638c\u5929\u4e0b\u7eff\u6797\uff0c\u4e07\u6620\u971e\u5219\u662f\u5949\u5999\u5999\u8001\u5c3c\u4e4b\u547d\u524d\u6765\u52a9\u9635\u3002 \u3000\u3000\u4f17\u4eba\u65e0\u4e0d\u6b22\u5929\u559c\u5730\uff0c\u5f53\u665a\u5c31\u5728\u5cb3\u738b\u5e99\u7834\u4f8b\u5927\u5f00\u7b75\u5e2d\uff0e\u76f4\u559d\u5f97\u4e0d\u9189\u4e0d\u4f11\u3002 \u3000\u3000\u4f17\u4eba\u5546\u8bae\u7684\u7ed3\u679c\uff0c\u51b3\u5b9a\u6b21\u65e5\u4f7f\u51fa\u53d1\u518d\u56de\u767d\u59d1\u5bfa\uff0c\u7136\u540e\u5bf9\u8ff7\u8e2a\u8c37\u5c55\u5f00\u884c\u52a8\u3002 \u3000\u3000\u53e6\u5916\u2014\u4ef6\u4ee4\u4f17\u4eba\u9ad8\u5174\u7684\u4e8b\uff0c\u4fbf\u662f\u5e9e\u58eb\u51b2\u4e5f\u60c5\u613f\u4ed7\u4e49\u76f8\u808b\uff0c\u56e0\u4e3a\u4ed6\u5728\u62a4\u9001\u8c37\u5bd2\u9999\u524d\u6765\u7684\u8def \u4e0a\uff0c\u8c37\u5bd2\u9999\u5df2\u771f\u7684\u62dc\u4ed6\u4f5c\u4e49\u7236\u4e86\uff0c\u5f53\u7136\u8981\u5e2e\u5e72\u5973\u513f\u7684\u5fd9\uff0e \u3000\u3000\u6b21\u65e5\u4e00\u65e9\u51fa\u53d1\uff0e\u4e24\u65e5\u540e\u4fbf\u56de\u5230\u767d\u59d1\u5bfa\u3002 \u3000\u3000\u4ee4\u4ed6\u4eec\u5927\u5403\u4e00\u60ca\u7684\u662f\uff0c\u767d\u59d1\u5bfa\u4f4f\u6301\u609f\u5c18\u548c\u4ed6\u7684\u4e09\u540d\u5f1f\u5b50\u4ee5\u53ca\u5929\u7985\u3001\u5929\u671b\u5927\u5e08\u7b49\u4eba\u7adf\u7136\u4e0d \u89c1\u4e86\uff0e\u53ea\u5269\u4e0b\u4e00\u5ea7\u7a7a\u5bfa\u3002 \u3000\u3000\u4e0d\u7ba1\u5982\u4f55\uff0c\u4f17\u4eba\u8fd8\u662f\u5728\u767d\u59d1\u5bfa\u4f4f\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u5f53\u665a\uff0e\u80e1\u67cf\u9f84\u6d3e\u51fa\u4e07\u6620\u971e\u548c\u6587\u5929\u751f\u5230\u4e07\u6708\u5ce1\uff0c\u76ee\u7684\u662f\u5411\u674e\u8302\u67e5\u8be2\u662f\u5426\u77e5\u9053\u767d\u59d1\u5bfa\u53d1\u751f\u4e86 \u4ec0\u4e48\u4e8b\uff0e\u518d\u6d3e\u51fa\u6c5f\u5317\u4e09\u9f99\u5230\u9644\u8fd1\u5e02\u9547\u91c7\u8d2d\u98df\u7528\u4e4b\u7269\uff0e\u7136\u540e\u96c6\u5408\u6240\u6709\u4eba\u5728\u7985\u5802\u8bae\u4e8b\u3002 \u3000\u3000\u80e1\u67cf\u9f84\u662f\u4e3b\u4e8b\u4e4b\u4eba\uff0c\u4f46\u56e0\u5e9e\u58eb\u51b2\u662f\u524d\u8f88\u4eba\u7269\uff0c\u53c8\u662f\u8c37\u5bd2\u9999\u7684\u4e49\u7236\uff0c\u4fbf\u63a8\u8ba9\u5e9e\u58eb\u51b2\u4e3b\u6301\u5927 \u5c40\u3002 \u3000\u3000\u5e9e\u58eb\u51b2\u4e5f\u5c31\u8001\u5b9e\u4e0d\u5ba2\u6c14\u7684\u5750\u4e0a\u4e86\u4e3b\u4f4d\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u62a2\u5148\u9053\uff1a\u201c\u8981\u884c\u52a8\u5c31\u8be5\u8d8a\u5feb\u8d8a\u597d\uff0e\u5e9e\u8001\u4f2f\uff0e\u660e\u5929\u5c31\u52a8\u624b\u597d\u4e0d\u597d\uff1f\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u9053\uff1a\u201c\u8001\u592b\u4e5f\u662f\u8fd9\u610f\u601d\u3002\u4f9d\u8001\u592b\u5224\u65ad\uff0c\u767d\u59d1\u5bfa\u5185\u5c11\u6797\u65b9\u9762\u7684\u4eba\uff0c\u5fc5\u5b9a\u4e5f\u90fd\u9677\u5165\u8ff7 \u8e2a\u8c37\u3002\u4e0d\u8fc7\u8001\u592b\u5e0c\u671b\u80fd\u5148\u4e86\u89e3\u4e00\u4e0b\u8ff7\u8e2a\u8c37\u5185\u6700\u8fd1\u7684\u60c5\u51b5\uff0e\u7136\u540e\u518d\u91c7\u53d6\u884c\u52a8\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u62cd\u62cd\u80f8\u812f\u9053\uff1a\u201c\u90a3\u5f88\u7b80\u5355\uff0c\u665a\u8f88\u4eca\u665a\u5c31\u8fdb\u8c37\u8bbe\u6cd5\u8e29\u63a2\u4e00\u4e0b\u3002\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u6447\u5934\u9053\uff1a\u201c\u8c01\u90fd\u53bb\u5f97\uff0c\u5c31\u662f\u4f60\u53bb\u4e0d\u5f97\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u665a\u8f88\u53bb\u4e0d\u5f97\uff1f\u201d \u3000\u3000\u201c\u4f60\u662f\u970d\u5143\u4f3d\u7684\u773c\u4e2d\u9489\uff0c\u4e07\u2014\u6709\u4e86\u5dee\u9519\uff0e\u53ea\u6015\u5c31\u6709\u53bb\u65e0\u56de\u4e86\uff01\u201d \u3000\u3000\u201c\u665a\u8f88\u4e00\u5b9a\u6709\u529e\u6cd5\u8131\u8eab\u3002\u201d \u3000\u3000\u201c\u4e0d\u6210\uff01\u8001\u592b\u4e0d\u51c6\u4f60\u53bb\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u6b63\u8981\u518d\u8bb2\uff0c\u5ffd\u89c1\u4e00\u540d\u5927\u6c49\u5306\u5306\u5954\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u7acb\u523b\uff0c\u7985\u5802\u5185\u54cd\u8d77\u4e00\u9635\u60ca\u559c\u7684\u547c\u53eb\u58f0\u3002 \u3000\u3000\u539f\u6765\u5927\u6c49\u7adf\u662f\u5931\u8e2a\u534a\u5e74\u7684\u6c5f\u5317\u4e94\u9f99\u8001\u5927\u51fa\u4e91\u9f99\u59dc\u5b8f\u3002 \u3000\u3000\u59dc\u5b8f\u662f\u534a\u5e74\u524d\u548c\u949f\u4e00\u8c6a\u7b49\u4eba\u7531\u5929\u53f0\u5c71\u8fd4\u56de\u65f6\u5148\u9063\u56de\u8c37\uff0c\u4f46\u56de\u8c37\u540e\u5374\u2014\u76f4\u6ca1\u6709\u4ed6\u7684\u6d88\u606f\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u8feb\u4e0d\u53ca\u5f85\u7684\u95ee\u9053\uff1a\u201c\u59dc\u5144\u8fd9\u51e0\u6708\u7a76\u7adf\u4eba\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u59dc\u5b8f\u9996\u5148\u5411\u5e9e\u58eb\u51b2\u3001\u80e1\u67cf\u9f84\u3001\u8c37\u5bd2\u9999\u89c1\u4e0b\u793c\uff0c\u6765\u4e0d\u53ca\u591a\u95ee\u8c37\u5bd2\u9999\u662f\u600e\u4e48\u6d3b\u8fc7\u6765\u7684\uff0c\u4fbf\u9053\uff1a \u201c\u5144\u5f1f\u534a\u5e74\u524d\u521a\u7531\u5929\u53f0\u5c71\u56de\u8c37\uff0e\u5c31\u88ab\u970d\u5143\u4f3d\u56da\u7981\u5728\u4e00\u5904\u79d8\u6d1e\u91cc\u3002\u201d \u3000\u3000\u949f\u4e00\u8c6a\u4e00\u76b1\u7709\u5934\u9053\uff1a\u201c\u4f60\u53c8\u662f\u600e\u4e48\u8dd1\u51fa\u6765\u7684\uff1f\u201d \u3000\u3000\u59dc\u5b8f\u9053\uff1a\u201c\u591a\u4e8f\u4e00\u4e2a\u53eb\u8d75\u798f\u7684\u4e09\u5929\u524d\u5077\u5077\u628a\u5144\u5f1f\u653e\u51fa\u3002\u201d \u3000\u3000\u201c\u4f60\u600e\u77e5\u6211\u4eec\u5df2\u6765\u4e86\u767d\u59d1\u5bfa\uff1f\u201d \u3000\u3000\u201c\u5144\u5f1f\u5148\u8eb2\u85cf\u5728\u4e07\u6708\u5ce1\uff0e\u542c\u674e\u7ba1\u5bb6\u8bf4\uff0c\u80e1\u76df\u4e3b\u5df2\u7ecf\u91cd\u51fa\u6c5f\u6e56\uff0e\u76ee\u524d\u6b63\u5728\u767d\u59d1\u5bfa\uff0c\u56e0\u4e4b\u5144 \u5f1f\u5c31\u8d76\u6765\u4e86\uff0c\u6b63\u597d\u65b9\u624d\u5728\u8def\u4e0a\u9047\u5230\u4e86\u6587\u5929\u751f\u548c\u4e07\u59d1\u5a18\u3002\u201d \u3000\u3000\u201c\u5c11\u6797\u638c\u95e8\u5929\u7985\u5927\u5e08\u5e26\u9886\u5929\u671b\u5927\u5e08\u548c\u4e8c\u5341\u56db\u540d\u5f1f\u5b50\u524d\u4e9b\u5929\u4e5f\u4f4f\u5728\u767d\u59d1\u5bfa\uff0c\u73b0\u5728\u90fd\u4e0d\u89c1\u4e86\uff0c \u4f60\u53ef\u77e5\u9053\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d \u3000\u3000\u201c\u8d75\u798f\u5bf9\u5144\u5f1f\u8bb2\u8fc7\uff0c\u4ed6\u4eec\u5df2\u90fd\u88ab\u970d\u5143\u4f3d\u63b3\u8fdb\u8ff7\u8e2a\u8c37\u53bb\u4e86\uff01\u201d \u3000\u3000\u201c\u8ff7\u8e2a\u8c37\u76ee\u524d\u7684\u60c5\u5f62\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u201c\u8fd8\u662f\u8001\u6837\u5b50\uff0c\u53ea\u662f\u636e\u8bf4\u65b0\u8fd1\u53c8\u589e\u6dfb\u4e86\u4e0d\u5c11\u4eba\u9a6c\u3002\u201d \u3000\u3000\u59dc\u5b8f\u63a5\u7740\u628a\u6240\u77e5\u60c5\u5f62\u8be6\u7ec6\u8bf4\u4e86\u4e00\u904d\u3002 \u3000\u3000\u53ea\u542c\u5e9e\u58eb\u51b2\u9053\uff1a\u201c\u597d\u4e86\u597d\u4e86\uff01\u7528\u4e0d\u7740\u518d\u6d3e\u4eba\u8fdb\u8c37\u8e29\u63a2\u4e8b\u60c5\uff0c\u73b0\u5728\u5927\u5bb6\u5c31\u56de\u53bb\u4f11\u606f\uff0c\u591c\u665a \u6d3e\u51fa\u5fc5\u8981\u8b66\u6212\uff0c\u660e\u5929\u4e00\u65e9\u5c31\u5c55\u5f00\u884c\u52a8\u3002\u201d \u3000\u3000\u6b21\u65e5\u65e9\u996d\u540e\uff0c\u5e9e\u58eb\u51b2\u3001\u80e1\u67cf\u9f84\u3001\u8c37\u5bd2\u9999\u7b49\u4e00\u884c\u5c06\u8fd1\u4e8c\u5341\u4eba\uff0e\u7531\u767d\u59d1\u5bfa\u51fa\u53d1\uff0c\u5f00\u5f80\u8ff7\u8e2a\u8c37\u3002 \u3000\u3000\u4ed6\u4eec\u8981\u6b63\u5927\u5149\u660e\u7684\u8fdb\u8c37\uff0e\u6240\u4ee5\u65f6\u95f4\u9009\u5728\u767d\u5929\u3002 \u3000\u3000\u6765\u5230\u8c37\u53e3\uff0c\u5173\u5361\u4e0a\u7684\u55bd\u5175\uff0e\u4e00\u89c1\u6b7b\u53bb\u7684\u4e24\u4f4d\u524d\u4efb\u76df\u4e3b\u80e1\u67cf\u9f84\u548c\u8c37\u5bd2\u9999\u5c45\u7136\u7387\u961f\u51fa\u73b0\uff0c\u5168 \u60ca\u5f97\u76ee\u77aa\u53e3\u5446\uff0c\u534a\u54cd\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u80e1\u67cf\u9f84\u5e76\u4e0d\u96be\u4e3a\u4ed6\u4eec\uff0c\u8981\u4ed6\u4eec\u6d3e\u4eba\u901f\u901f\u8fdb\u8c37\u5411\u970d\u5143\u4f3d\u901a\u62a5\uff0c\u7136\u540e\u518d\u62db\u547c\u4f17\u4eba\u7f13\u6b65\u5411\u8c37\u5185 \u8fdb\u53d1\uff0c\u76ee\u7684\u662f\u8ba9\u970d\u5143\u4f3d\u6765\u5f97\u53ca\u51c6\u5907\u3002 \u3000\u3000\u5f53\u4ed6\u4eec\u5230\u8fbe\u7eff\u6797\u603b\u5be8\u805a\u4e49\u5385\u524d\u7684\u573a\u4e0a\uff0c\u970d\u5143\u4f3d\u65e9\u5df2\u5217\u9635\u7b49\u5f85\uff0c\u6240\u8c13\u5217\u9635\u7b49\u5f85\uff0c\u5e76\u975e\u5217\u9635 \u76f8\u8fce\uff0c\u800c\u662f\u5217\u597d\u4e86\u9635\u5f0f\uff0c\u5251\u62d4\u5f29\u5f20\uff0c\u4e00\u526f\u5175\u5203\u76f8\u89c1\u7684\u6c14\u52bf\u3002 \u3000\u3000\u7ad9\u5728\u970d\u5143\u4f3d\u8eab\u540e\u7684\uff0c\u4e0d\u4e0b\u6570\u5341\u4eba\u4e4b\u591a\uff0c\u4f9d\u7a00\u53ef\u8fa9\u7684\uff0c\u4fbf\u6709\u5cad\u5357\u4e8c\u5947\u3001\u5d06\u5cd2\u4e94\u9b54\u3001\u6bd2\u706b\u6210 \u5168\u3001\u9ed1\u9b54\u65f6\u5bc5\u3001\u725b\u5954\u3001\u706b\u83bd\u3001\u7f57\u94ee\u3001\u4e01\u2014\u9b42\u3001\u9ad8\u5d99\uff0c\u5f20\u656c\u5b89\u3001\u3001\u5929\u673a\u3001\u5929\u89c9\u3001\u609f\u660e\u4ee5\u53ca\u4e5d\u540d \u94dc\u4eba\u3002 \u3000\u3000\u7b49\u4e8e\u8ff7\u8e2a\u8c37\u7cbe\u9510\u5c3d\u51fa\u3002 \u3000\u3000\u66f4\u8ba9\u80e1\u67cf\u9f84\u7b49\u4eba\u60ca\u5f02\u7684\uff0e\u8fde\u5c11\u6797\u638c\u95e8\u5929\u7985\u5927\u5e08\u3001\u5929\u671b\u5927\u5e08\u4ee5\u53ca\u5c11\u6797\u4e8c\u5341\u56db\u540d\u50e7\u4eba\u4e5f\u5728\u5185\u3002 \u3000\u3000\u80e1\u67cf\u9f84\u7b49\u4eba\u7f13\u7f13\u6765\u5230\u970d\u5143\u4f3d\u8eab\u524d\u4e09\u4e08\u5916\uff0c\u4e5f\u505c\u4e0b\u811a\u6b65\u3002 \u3000\u3000\u970d\u5143\u4f3d\u7b49\u4eba\uff0c\u539f\u5df2\u5728\u767d\u59d1\u5bfa\u89c1\u8fc7\u80e1\u67cf\u9f84\uff0c\u6b64\u523b\u89c1\u8c37\u5bd2\u9999\u4e5f\u5df2\u51fa\u73b0\uff0c\u4e0d\u5c11\u4eba\u90fd\u4e0d\u514d\u5927\u60ca\u5931 \u8272\uff0e \u3000\u3000\u4f46\u970d\u5143\u4f3d\u5bf9\u8fd9\u4e8c\u4eba\u662f\u5426\u771f\u662f\u80e1\u67cf\u9f84\u548c\u8c37\u5bd2\u9999\uff0c\u4f3c\u4e4e\u4ecd\u5fc3\u5b58\u6000\u7591\uff0c\u5426\u5219\uff0c\u4ed6\u5fc5\u5b9a\u53e6\u6709\u4e00\u5957 \u5e94\u4ed8\u529e\u6cd5\u3002 \u3000\u3000\u5e9e\u58eb\u51b2\u9996\u5148\u53d1\u8bdd\u9053\uff1a\u201c\u970d\u5143\u4f3d\uff0c\u8c1c\u8e2a\u8c37\u524d\u540e\u4e24\u4efb\u76df\u4e3b\u90fd\u6765\u4e86\uff0e\u4f60\u76ee\u524d\u867d\u4e5f\u8d35\u4e3a\u7eff\u6797\u76df\u4e3b\uff0c \u4f46\u603b\u4e0d\u80fd\u4e0d\u627f\u8ba4\u66fe\u505a\u8fc7\u4ed6\u4eec\u4e8c\u4eba\u7684\u5c5e\u4e0b\uff0c\u5982\u4eca\u4e0d\u4f46\u4e0d\u5927\u793c\u76f8\u8fce\uff0c\u53cd\u800c\u5217\u9635\u4ee5\u5f85\uff0c\u6210\u4ec0\u4e48\u4f53 \u7edf\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u51b7\u7136\u7b11\u9053\uff1a\u201c\u5e9e\u58eb\u51b2\uff0c\u4f60\u4e0d\u5728\u957f\u767d\u5c71\u900d\u9065\uff0c\u4e3a\u4ec0\u4e48\u4e5f\u6765\u6dcc\u6d51\u6c34\uff1f\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u54fc\u4e86\u58f0\u9053\uff1a\u201c\u8001\u592b\u4e3a\u63f4\u7eff\u6797\u82cd\u751f\u800c\u6765\uff0e\u600e\u80fd\u8bf4\u6210\u662f\u6dcc\u6d51\u6c34\uff1f\u970d\u5143\u4f3d\uff0c\u4f60\u82e5\u77e5\u793c\u77e5 \u8da3\uff0c\u5c31\u8be5\u7acb\u5373\u8ba9\u51fa\u7eff\u6797\u76df\u4e3b\u4e4b\u4f4d\uff0c\u4e5f\u8bb8\u4ecd\u80fd\u4fdd\u4f4f\u4f60\u6614\u65e5\u526f\u76df\u4e3b\u4e4b\u804c\u3002\u5426\u5219\uff0c\u4eca\u5929\u5c31\u662f\u4f60\u7684\u6b7b \u671f\u5230\u4e86\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u8138\u8272\u9aa4\u53d8\uff0c\u559d\u9053\uff1a\u201c\u8ff7\u8e2a\u8c37\u5c82\u5bb9\u4f60\u8fd9\u8001\u5339\u592b\u7316\u72c2\uff1f\u54ea\u4e2a\u51fa\u53bb\u628a\u4ed6\u62ff\u4e0b\uff01\u201d \u3000\u3000\u4ed6\u8eab\u540e\u9ad8\u624b\u867d\u591a\uff0c\u4f46\u5374\u90fd\u51db\u4e8e\u5e9e\u58eb\u51b2\u7684\u5a01\u540d\uff0c\u8c01\u90fd\u4e0d\u6562\u81ea\u544a\u594b\u52c7\u51fa\u6218\u3002 \u3000\u3000\u970d\u5143\u4f3d\u8f6c\u5934\u671b\u4e86\u5df4\u5929\u4e49\u4e00\u773c\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u8981\u5929\u7985\u8001\u548c\u5c1a\u51fa\u6218\uff01\u201d \u3000\u3000\u53ea\u89c1\u5df4\u5929\u4e49\u6765\u5230\u8eab\u540e\u7684\u5929\u7985\u5927\u5e08\u65c1\uff0c\u5728\u4ed6\u8eab\u8fb9\u6bd4\u624b\u8bb2\u4e86\u51e0\u53e5\u3002 \u3000\u3000\u8bf4\u4e5f\u5947\u602a\uff0c\u5929\u7985\u5927\u5e08\u7adf\u7136\u5012\u63d0\u7740\u9f99\u5934\u7985\u6756\u8d70\u4e86\u51fa\u6765\u3002 \u3000\u3000\u8c01\u4e5f\u60f3\u4e0d\u5230\uff0c\u970d\u5143\u4f3d\u7adf\u7136\u9996\u5148\u6d3e\u5929\u7985\u5927\u5e08\u51fa\u573a\uff0c\u5206\u660e\u5728\u4e3a\u5e9e\u58eb\u51b2\u7b49\u4eba\u51fa\u96be\u9898\u3002 \u3000\u3000\u5e9e\u58eb\u51b2\u6b63\u5728\u4e3a\u96be\uff0c\u53ea\u89c1\u8c37\u5bd2\u9999\u5df2\u624b\u6a2a\u77ed\u5251\uff0c\u83b2\u6b65\u59d7\u59d7\u7684\u8d70\u4e86\u51fa\u6765\u3002 \u3000\u3000\u5e9e\u58eb\u51b2\u53eb\u9053\uff1a\u201c\u9999\u513f\u5c0f\u5fc3\uff0c\u8fd9\u8001\u548c\u5c1a\u6b63\u662f\u5f53\u4eca\u5c11\u6797\u638c\u95e8\u5929\u7985\uff01\u201d \u3000\u3000\u8c37\u5bd2\u9999\u4e0d\u52a8\u58f0\u8272\u9053\uff1a\u201c\u4e49\u7236\u653e\u5fc3\uff0c\u5973\u513f\u65e9\u5c31\u8ba4\u8bc6\u4ed6\uff01\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u9053\uff1a\u201c\u4ed6\u867d\u7136\u5df2\u7ecf\u8ff7\u5931\u672c\u6027\uff0c\u4f46\u6b66\u529f\u5374\u5728\uff0c\u4f60\u5343\u4e07\u4e0d\u53ef\u8f7b\u654c\uff0e\u800c\u4e14\u8fd8\u6700\u597d\u522b\u4f24\u4e86 \u4ed6\u3002\u201d \u3000\u3000\u8c37\u5bd2\u9999\u9053\uff1a\u201c\u5973\u513f\u77e5\u9053\u3002\u201d \u3000\u3000\u5979\u7684\u8bdd\u521a\u521a\u8bf4\u5b8c\uff0c\u5ffd\u89c1\u5929\u7985\u5927\u5e08\u9f99\u5934\u7985\u6756\u4e00\u62a1\uff0e\u201c\u547c\u201d\u7684\u4e00\u58f0\uff0e\u6709\u5982\u98ce\u9a70\u7535\u63a3\u822c\u62e6\u8170\u626b \u4e86\u8fc7\u6765\uff0c\u5a01\u52bf\u4e4b\u731b\uff0c\u771f\u53ef\u60ca\u5929\u52a8\u5730\u3002 \u3000\u3000\u5e9e\u58eb\u51b2\u3001\u80e1\u67cf\u9f84\u7b49\u4eba\u6b63\u5728\u4e3a\u8c37\u5bd2\u9999\u634f\u4e00\u628a\u51b7\u6c57\u4e4b\u9645\uff0e\u8c37\u5bd2\u9999\u5374\u5df2\u5931\u53bb\u6240\u5728\uff0c\u4f7f\u5f97\u5929\u7985\u5927 \u5e08\u7a7a\u53d1\u2014\u62db\uff0c\u53cd\u800c\u95ea\u4e86\u4e2a\u5927\u8ddf\u5934\u3002 \u3000\u3000\u539f\u6765\u8c37\u5bd2\u9999\u4e0d\u77e5\u7528\u7684\u4ec0\u4e48\u8eab\u6cd5\uff0c\u7adf\u7136\u5728\u77ac\u606f\u95f4\u95ea\u8fc7\u9f99\u5934\u6756\u4e00\u8bb0\u6a2a\u626b\uff0c\u76f4\u5230\u5929\u7985\u5927\u5e08\u6536\u52bf \u4e4b\u540e\uff0c\u53c8\u5728\u539f\u5730\u73b0\u8eab\uff0c\u5c31\u50cf\u662f\u4e00\u5177\u9b45\u5f71\uff0c\u5ffd\u800c\u6d88\u901d\u5ffd\u800c\u590d\u73b0\u2014\u6837\u3002 \u3000\u3000\u867d\u7136\u5979\u5e76\u672a\u51fa\u624b\uff0e\u5374\u770b\u5f97\u5168\u573a\u6240\u6709\u7684\u4eba\u65e0\u4e0d\u77a0\u76ee\u7ed3\u820c\uff0e \u3000\u3000\u5c24\u5176\u80e1\u67cf\u9f84\uff0c\u603b\u7b97\u4eb2\u773c\u76ee\u7779\u4e86\u7231\u59bb\u6b64\u523b\u7684\u8eab\u624b\u3002 \u3000\u3000\u970d\u5143\u4f3d\u559d\u9053\uff1a\u201c\u5929\u7985\u8001\u548c\u5c1a\uff0c\u7b49\u4ec0\u4e48\uff1f\u8fd8\u4e0d\u5feb\u5feb\u518d\u53d1\u62db\uff01\u201d \u3000\u3000\u8c01\u77e5\u5929\u7985\u5927\u5e08\u521a\u6b32\u53d1\u52a8\u7b2c\u4e8c\u62db\uff0c\u5374\u5ffd\u7136\u8eab\u5b50\u4e00\u4ef0\uff0c\u5411\u540e\u5012\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u5728\u573a\u4e2d\u4eba\uff0c\u51e0\u4e4e\u8c01\u90fd\u6ca1\u770b\u6e05\u5929\u7985\u5927\u5e08\u662f\u4e3a\u4ec0\u4e48\u5ffd\u7136\u5012\u4e0b\u53bb\u7684\uff0c\u4f46\u5374\u8c01\u90fd\u77e5\u9053\u4ed6\u662f\u4e2d\u4e86\u8c37 \u5bd2\u9999\u7684\u6307\u529b\u548c\u638c\u529b\u3002 \u3000\u3000\u770b\u8c37\u5bd2\u9999\u65f6\uff0c\u4ecd\u7ad9\u5728\u539f\u5730\u672a\u52a8\uff0c\u521a\u624d\u53d1\u751f\u7684\u4e8b\uff0c\u5c31\u50cf\u4e0e\u5979\u5b8c\u5168\u65e0\u5173\u4e00\u822c\u3002 \u3000\u3000\u970d\u5143\u4f3d\u5927\u60ca\u4e4b\u4e0b\uff0c\u56de\u5934\u77a5\u4e86\u5d06\u5cd2\u4e94\u9b54\u4e00\u773c\u9053\uff1a\u201c\u4f60\u4eec\u4e00\u8d77\u4e0a\uff01\u201d \u3000\u3000\u4e94\u9b54\u867d\u7136\u4e5f\u60ca\u9a87\u8c37\u5bd2\u9999\u9ad8\u4e0d\u53ef\u6d4b\u7684\u8eab\u624b\uff0c\u4f46\u4ed7\u7740\u4eba\u591a\uff0c\u53c8\u4e0d\u6562\u4e0d\u9075\u970d\u5143\u4f3d\u7684\u4ee4\u8c15\uff0e\u4e92\u9012 \u4e00\u4e2a\u773c\u8272\uff0c\u4e00\u62e5\u800c\u4e0a\uff0e\u9f50\u9f50\u5411\u8c37\u5bd2\u9999\u653b\u4e86\u4e0a\u6765\u3002 \u3000\u3000\u5d06\u5cd2\u4e94\u9b54\uff0c\u4e2a\u4e2a\u90fd\u79f0\u5f97\u4e0a\u662f\u9876\u5c16\u9ad8\u624b\uff0c\u5982\u4eca\u4e94\u4eba\u8054\u624b\uff0e\u7fa4\u56f4\u5408\u653b\uff0c\u5a01\u529b\u5c82\u540c\u5c0f\u53ef\u3002\u3000\u2014 \u65f6\u4e4b\u95f4\uff0e\u7adf\u628a\u8c37\u5bd2\u9999\u4e5f\u903c\u5f97\u624b\u5fd9\u811a\u4e71\u3002 \u3000\u3000\u8c37\u5bd2\u9999\u65b9\u624d\u51fa\u6218\u5929\u7985\u5927\u5e08\uff0e\u6240\u4ee5\u80fd\u5f97\u5fc3\u5e94\u624b\uff0c\u4e0d\u5916\u662f\u56e0\u5929\u7985\u5927\u5e08\u56e0\u5fc3\u795e\u4e27\u5931\uff0c\u4ee5\u81f4\u53cd\u5e94 \u8fdf\u949d\uff0c\u867d\u5177\u76d6\u4e16\u6b66\u529f\uff0e\u8c37\u5bd2\u9999\u4ecd\u6709\u673a\u53ef\u4e58\u3002 \u3000\u3000\u6b64\u523b\u4e00\u4eba\u8fce\u6218\u4e94\u9b54\uff0e\u81ea\u7136\u987e\u524d\u4e0d\u80fd\u987e\u540e\uff0e\u987e\u5de6\u4e0d\u80fd\u987e\u53f3\u3002\u7eb5\u7136\u5979\u7684\u6b66\u529f\u5df2\u8fbe\u767b\u5cf0\u9020\u6781\u4e4b \u5883\uff0c\u5374\u88ab\u7ea0\u7f20\u5f97\u96be\u4ee5\u5c3d\u60c5\u53d1\u6325\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u4e00\u89c1\u5e08\u5ac2\u9677\u8eab\u91cd\u56f4\uff0c\u4e0d\u7b49\u5e9e\u58eb\u51b2\u548c\u80e1\u67cf\u9f84\u53d1\u4ee4\uff0e\u7acb\u523b\u633a\u5251\u4e5f\u52a0\u5165\u6218\u5708\u3002 \u3000\u3000\u6709\u4e86\u9ea6\u5c0f\u660e\u52a0\u5165\uff0c\u60c5\u52bf\u968f\u4e4b\u5927\u53d8\uff0c\u53cd\u800c\u903c\u5f97\u4e94\u9b54\u6b65\u6b65\u540e\u9000\u3002 \u3000\u3000\u5ffd\u542c\u80e1\u67cf\u9f84\u5927\u559d\u9053\uff1a\u201c\u4f4f\u624b\uff01\u201d \u3000\u3000\u8c37\u5bd2\u9999\u548c\u9ea6\u5c0f\u660e\u95fb\u8a00\u53cc\u53cc\u5411\u540e\u8dc3\u9000\u3002 \u3000\u3000\u4e94\u9b54\u5df2\u6709\u4e9b\u62db\u67b6\u4e0d\u4f4f\uff0c\u81ea\u7136\u4e0d\u6562\u8ffd\u51fb\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u5374\u5fc3\u4e2d\u6709\u4e9b\u4e0d\u5feb\uff0e\u53eb\u9053\uff1a\u201c\u5e08\u5144\uff0e\u5c0f\u5f1f\u548c\u5e08\u5ac2\u9a6c\u4e0a\u5c31\u53ef\u628a\u8fd9\u4e94\u4e2a\u738b\u516b\u86cb\u7ed3\u679c\uff0c\u60a8 \u600e\u4e48\u5374\u5ffd\u7136\u53eb\u505c\uff1f\u201d \u3000\u3000\u80e1\u67cf\u9f84\u9053\uff1a\u201c\u4f60\u548c\u4f60\u5e08\u5ac2\u53ea\u7ba1\u9000\u4e0b\u53bb\uff0e\u73b0\u5728\u7531\u6211\u6765\u4eb2\u81ea\u548c\u4ed6\u4eec\u4f1a\u4f1a\u3002\u201d \u3000\u3000\u8c37\u5bd2\u9999\u548c\u9ea6\u5c0f\u660e\u53ea\u597d\u9000\u56de\u9635\u4e2d\u3002 \u3000\u3000\u80e1\u67cf\u9f84\u76ee\u6ce8\u970d\u5143\u4f3d\u9053\uff1a\u201c\u970d\u5143\u4f3d\uff0c\u4f60\u597d\u50cf\u59cb\u7ec8\u4e0d\u76f8\u4fe1\u6211\u662f\u771f\u6b63\u7684\u80e1\u67cf\u9f84\uff0e\u53ef\u6562\u4eb2\u81ea\u51fa\u624b \u548c\u6211\u4e00\u6218\u3002\u201d \u3000\u3000\u6b64\u523b\u7684\u970d\u5143\u4f3d\uff0e\u5df3\u6210\u9a91\u864e\u96be\u4e0b\u4e4b\u52bf\uff0c\u867d\u5df2\u6709\u4e9b\u76f8\u4fe1\u5bf9\u65b9\u662f\u80e1\u67cf\u9f84\uff0c\u5374\u53c8\u4e0d\u4fbf\u627f\u8ba4\u3002\u56e0\u4e3a \u4ed6\u4e00\u65e6\u627f\u8ba4\uff0c\u5c31\u5fc5\u987b\u8ba9\u5c71\u7eff\u6797\u76df\u4e3b\u5b9d\u5ea7\uff0c\u8fd9\u53c8\u5c82\u662f\u4ed6\u5fc3\u7518\u60c5\u613f\u7684\u4e8b\uff1f \u3000\u3000\u5f53\u4e0b\uff0c\u4ed6\u5f3a\u4f5c\u9547\u5b9a\uff0c\u9634\u68ee\u68ee\u4e00\u7b11\u9053\uff1a\u201c\u8001\u592b\u8eab\u4e3a\u5929\u4e0b\u7eff\u6797\u76df\u4e3b\uff0c\u624b\u4e0b\u6218\u5c06\u5982\u4e91\uff0c\u5bf9\u4ed8\u5c0a \u9a7e\uff0c\u4f55\u987b\u4eb2\u81ea\u51fa\u624b\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u518d\u559d\u4ee4\u5d06\u5cd2\u4e94\u9b54\u9053\uff1a\u201c\u8fd8\u662f\u7531\u4f60\u4eec\u4e94\u4eba\u4e00\u8d77\u4e0a\uff01\u201d \u3000\u3000\u5d06\u5cd2\u4e94\u9b54\u89c1\u53c8\u662f\u4ee5\u4e94\u5bf9\u4e00\uff0c\u800c\u4e14\u5f04\u4e0d\u6e05\u662f\u771f\u80e1\u67cf\u9f84\u8fd8\u662f\u5047\u80e1\u67cf\u9f84\uff0c\u81ea\u662f\u6beb\u65e0\u6240\u60e7\uff0c\u4e94\u4eba \u5206\u4e94\u4e2a\u65b9\u4f4d\uff0e\u4ee5\u8fc5\u96f7\u4e0d\u53ca\u63a9\u8033\u4e4b\u52bf\uff0e\u95ea\u7535\u822c\u56f4\u653b\u4e0a\u6765\u3002 \u3000\u3000\u80e1\u67cf\u9f84\u89c1\u4e94\u9b54\u6765\u52bf\u592a\u731b\uff0c\u4e5f\u4e0d\u6562\u8fc7\u5206\u5927\u610f\uff0e\u4e00\u51fa\u624b\u4fbf\u4e24\u79cd\u5175\u5203\u5e76\u7528\uff0c\u53f3\u5251\u5de6\u62d0\uff0c\u65cb\u8eab\u8fce \u6218\u3002 \u3000\u3000\u8fd9\u65f6\u7ad9\u5728\u524d\u9762\u7684\u8c37\u5bd2\u9999\u548c\u9ea6\u5c0f\u660e\uff0c\u90fd\u51dd\u795e\u84c4\u52bf\u4ee5\u5f85\uff0c\u51c6\u5907\u968f\u65f6\u51fa\u624b\u76f8\u52a9\u3002 \u3000\u3000\u53cc\u65b9\u4e00\u4ea4\u624b\u5c31\u5404\u51fa\u7edd\u62db\uff0c\u5f88\u5feb\u4fbf\u628a\u80e1\u67cf\u9f84\u56f4\u5728\u4e00\u7247\u6eda\u6eda\u6d41\u52a8\u7684\u5149\u5f71\u4e4b\u4e2d\uff0e\u6b64\u65f6\u5373\u4f7f\u6709\u4eba \u60f3\u4e0a\u524d\u52a9\u9635\uff0c\u4e5f\u4f3c\u4e4e\u96be\u4ee5\u63d2\u8fdb\u624b\u53bb\u3002 \u3000\u3000\u53cc\u65b9\u9ad8\u624b\u90fd\u5c4f\u606f\u4ee5\u5f85\uff0c\u4e2a\u4e2a\u770b\u5f97\u773c\u82b1\u7f2d\u4e71\uff0c\u800c\u80e1\u67cf\u9f84\u65b9\u9762\u7684\u4eba\uff0c\u9664\u773c\u82b1\u7f2d\u4e71\u5916\uff0c\u66f4\u52a0\u4e0a \u63d0\u5fc3\u540a\u80c6\u3002 \u3000\u3000\u8d77\u521d\uff0c\u4e94\u9b54\u4e00\u4e0b\u5b50\u5c31\u628a\u80e1\u67cf\u9f84\u5305\u56f4\u5f97\u5341\u5206\u7d27\u5bc6\uff0c\u4f3c\u4e4e\u6709\u610f\u8feb\u4f7f\u80e1\u67cf\u9f84\u65e0\u6cd5\u65bd\u5c55\u3002 \u3000\u3000\u7247\u523b\u4e4b\u540e\uff0c\u5305\u56f4\u5708\u5374\u53c8\u8d8a\u6765\u8d8a\u677e\uff0c\u663e\u7136\uff0c\u3000\u4e94\u9b54\u5df2\u662f\u88ab\u80e1\u67cf\u9f84\u5251\u3001\u62d0\u4ea4\u65bd\u7684\u5a01\u529b\u903c\u5f97\u4e0d \u6562\u8fc7\u4efd\u9760\u8fd1\u3002 \u3000\u3000\u5927\u9b54\u9634\u5929\u7433\u89c1\u65bd\u653e\u201c\u8840\u96fe\u6563\u201d\u7684\u65f6\u673a\u5df2\u5230\uff0c\u8d81\u7a7a\u63a2\u624b\u5165\u6000\uff0c\u7acb\u5373\u626c\u8155\u51c6\u5907\u5411\u80e1\u67cf\u9f84\u9762\u90e8 \u6492\u53bb\u3002 \u3000\u3000\u54ea\u77e5\u4ed6\u53f3\u8155\u521a\u521a\u626c\u8d77\uff0c\u7a81\u7136\u53f3\u80a9\u4e00\u51c9\uff0c\u6574\u6761\u624b\u81c2\uff0c\u7adf\u7136\u88ab\u80e1\u67cf\u9f84\u7684\u957f\u5251\u9f50\u80a9\u524a\u4e0b\uff0c\u5f53\u5373 \u5012\u5367\u5728\u8840\u6cca\u4e4b\u4e2d\u3002 \u3000\u3000\u5176\u4f59\u56db\u9b54\uff0c\u89c1\u5927\u9b54\u65ad\u81c2\u5012\u5730\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u60b2\u6124\u653b\u5fc3\uff0c\u4e0d\u4f46\u4e0d\u9000\uff0c\u53cd\u800c\u62db\u5f0f\u8fdb\u5f97\u66f4\u731b\u3002 \u3000\u3000\u5175\u5203\u4ea4\u9519\u9707\u54cd\u95f4\uff0e\u53ea\u542c\u201c\u7830\u201d\u7684\u2014\u58f0\uff0c\u80e1\u67cf\u9f84\u7adf\u7136\u4e00\u62d0\u53c8\u7838\u4e2d\u4e86\u4e8c\u9b54\u5deb\u9053\u5168\u5c16\u9876\uff0e\u522b\u8bf4 \u4e8c\u9b54\u5deb\u9053\u5168\u7684\u8111\u888b\u662f\u8089\u505a\u7684\uff0c\u5373\u4f7f\u662f\u4e2a\u94c1\u7403\uff0c\u7167\u6837\u4e5f\u4f1a\u88ab\u7838\u4e2a\u94c1\u5f40\u8ff8\u88c2\u3002 \u3000\u3000\u5deb\u9053\u5168\u8fde\u53eb\u90fd\u6ca1\u53eb\u51fa\u58f0\uff0c\u4fbf\u5f53\u573a\u505a\u4e86\u65e0\u5934\u4e4b\u9b3c\u3002 \u3000\u3000\u5269\u4e0b\u7684\u4e09\u9b54\u867d\u7136\u4e2a\u4e2a\u80c6\u98a4\u9b42\u98de\uff0c\u5728\u8fd9\u79cd\u60c5\u5f62\u4e0b\uff0c\u5374\u5e76\u65e0\u2014\u4e2a\u8d81\u673a\u9003\u9038\uff0c\u4f9d\u7136\u5168\u529b\u8054\u624b\u786c \u62fc\u3002 \u3000\u3000\u53ea\u542c\u53c8\u662f\u4e00\u58f0\u60e8\u547c\uff0c\u4e09\u9b54\u5200\u9b54\u8c37\u4e00\u5cf0\u7684\u8111\u888b\u7adf\u7136\u98de\u8d77\u4e86\u4e00\u4e08\u591a\u9ad8\u3002\u8111\u888b\u5c1a\u672a\u843d\u5730\uff0c\u8eab\u5b50 \u5df2\u5148\u5012\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u6b64\u4eba\u867d\u8eab\u9996\u5f02\u5904\uff0c\u4f46\u4e2d\u6c14\u4e4b\u8db3\uff0c\u5374\u4ee4\u4eba\u9707\u60ca\u3002 \u3000\u3000\u90a3\u4e00\u58f0\u60e8\u547c\uff0c\u4e0d\u6d88\u8bf4\u662f\u8111\u888b\u5728\u8116\u5b50\u4e0a\u65f6\u6240\u53d1\uff0c\u4f46\u8111\u888b\u79bb\u5f00\u8116\u5b50\u540e\uff0c\u5374\u4ecd\u4f59\u97f3\u4e0d\u7edd\uff0c\u5982\u975e \u5f53\u573a\u76ee\u7779\uff0c\u8c01\u4e5f\u4e0d\u4f1a\u76f8\u4fe1\u771f\u6709\u8fd9\u79cd\u602a\u4e8b\u53d1\u751f\u3002 \u3000\u3000\u6b64\u523b\u6700\u5fc3\u80c6\u4ff1\u88c2\u7684\uff0c\u8be5\u662f\u56db\u9b54\u5251\u9b54\u53f8\u592a\u5e73\uff0c\u56e0\u4e3a\u80e1\u67cf\u9f84\u6740\u4eba\u662f\u6309\u7167\u4e94\u9b54\u4f9d\u6b21\u6740\u7684\u3002\u5148\u6740 \u5927\u9b54\uff0c\u518d\u6740\u4e8c\u9b54\uff0c\u5982\u4eca\u662f\u6740\u4e86\u4e09\u9b54\uff0c\u4e0b\u4e00\u4e2a\u5fc5\u5b9a\u662f\u81ea\u5df1\u4e86\u3002 \u3000\u3000\u5fc3\u5ff5\u53ca\u6b64\uff0c\u54ea\u91cc\u8fd8\u6562\u518d\u6218\uff1f\u6b63\u6b32\u62bd\u8eab\u6025\u9000\uff0e\u4e0d\u60f3\u5bf9\u65b9\u7684\u94c1\u62d0\u5df2\u5230\uff0e\u7adf\u7136\u628a\u56db\u9b54\u53f8\u592a\u5e73\u62e6 \u8170\u65a9\u6210\u4e24\u6bb5\u3002 \u3000\u3000\u4e94\u9b54\u82b1\u79c0\u8fd9\u65f6\u65e9\u5df2\u4e24\u817f\u53d1\u8f6f\uff0c\u60f3\u8dd1\u4e5f\u8dd1\u4e0d\u52a8\uff0c\u88ab\u80e1\u67cf\u9f84\u632f\u8155\u4e00\u5251\uff0e\u8111\u888b\u4e5f\u642c\u4e86\u5bb6\u3002 \u3000\u3000\u80e1\u67cf\u9f84\u9877\u523b\u4e4b\u95f4\uff0e\u8fde\u65a9\u5d06\u5cd2\u4e94\u9b54\uff0c\u8fd9\u7b49\u795e\u52c7\uff0c\u5168\u573a\u6240\u6709\u7684\u4eba\uff0c\u5305\u62ec\u5e9e\u58eb\u51b2\u5728\u5185\uff01\u51e0\u66fe\u89c1 \u8fc7\uff0e\u4e00\u65f6\u4e4b\u95f4\uff0c\u51e0\u4e4e\u5168\u90fd\u5446\u5728\u5f53\u5730\uff0e \u3000\u3000\u5176\u5b9e\uff0c\u80e1\u67cf\u9f84\u81ea\u4e09\u5348\u524d\u590d\u51fa\u6c5f\u6e56\u5c31\u4efb\u5929\u4e0b\u7eff\u6797\u76df\u4e3b\u540e\uff0c\u5373\u4e0b\u5b9a\u51b3\u5fc3\u4e0d\u518d\u5984\u4e50\u65e0\u8f9c\u3002\u4f46\u5d06 \u5cd2\u4e94\u9b54\u5374\u5e76\u975e\u65e0\u8f9c\uff0c\u4e94\u4eba\u72fc\u72c8\u4e3a\u5978\uff0c\u6076\u8ff9\u7167\u5f70\uff0c\u628a\u4ed6\u4eec\u9664\u53bb\uff0e\u53cd\u800c\u662f\u505a\u4e86\u4e00\u4ef6\u9020\u798f\u7eff\u6797\u7684\u597d \u4e8b\u3002 \u3000\u3000\u80e1\u67cf\u9f84\u672c\u4ee5\u4e3a\u970d\u5143\u4f3d\u4f1a\u5c31\u6b64\u5c48\u670d\uff0c\u4e5f\u53ef\u514d\u53bb\u518d\u4e00\u6b21\u8165\u98ce\u8840\u96e8\u66f4\u4e3a\u60e8\u70c8\u7684\u6740\u622e\u5c40\u9762\uff0c\u5c82\u77e5 \u970d\u5143\u4f3d\u5728\u5fae\u4e00\u505c\u987f\u4e4b\u540e\uff0c\u7adf\u53d1\u52a8\u8eab\u540e\u7684\u767e\u4f59\u540d\u9ad8\u624b\uff0c\u50cf\u6d77\u6f6e\u822c\u4e00\u8d77\u5411\u80e1\u67cf\u9f84\u65b9\u9762\u51b2\u6740\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u8fd9\u4e00\u6765\uff0c\u80e1\u67cf\u9f84\u3001\u8c37\u5bd2\u9999\u3001\u5e9e\u58eb\u51b2\u65b9\u9762\u7684\u7fa4\u8c6a\uff0c\u4e5f\u90fd\u51b2\u6740\u8fc7\u53bb\uff0c\u3000\u4e00\u573a\u60ca\u5929\u52a8\u5730\u7684\u8840\u6218\uff0c \u7acb\u5373\u5c55\u5f00\u3002\u72b6\u51b5\u4e4b\u60e8\u70c8\uff0c\u7edd\u975e\u5c40\u5916\u4eba\u53ef\u4ee5\u60f3\u8c61\u3002 \u3000\u3000\u80e1\u67cf\u9f84\u3001\u8c37\u5bd2\u9999\u3001\u5e9e\u58eb\u51b2\u3001\u9ea6\u5c0f\u660e\u62c5\u5fc3\u635f\u4f24\u4e86\u81ea\u5df1\u7684\u4eba\uff0c\u90fd\u62a2\u5728\u7b2c\u4e00\u7ebf\u62fc\u529b\u53ae\u6740\uff0c\u4ee5\u56db \u4eba\u7684\u795e\u52c7\uff0c\u970d\u5143\u4f3d\u65b9\u9762\u7684\u9ad8\u624b\uff0c\u5e76\u65e0\u4e00\u4eba\u80fd\u62b5\u6321\u5f97\u4f4f\u3002 \u3000\u3000\u970d\u5143\u4f3d\u89c1\u5927\u52bf\u5df2\u53bb\uff0c\u6025\u6025\u7387\u9886\u5cad\u5357\u4e8c\u5947\u843d\u8352\u800c\u9003\u3002 \u3000\u3000\u80e1\u67cf\u9f84\u548c\u8c37\u5bd2\u9999\u5ff5\u5728\u8fd9\u4e8c\u4eba\u66fe\u662f\u81ea\u5df1\u5c5e\u4e0b\uff0c\u672c\u60f3\u653e\u4ed6\u4eec\u4e00\u9a6c\uff0e\u4f46\u5e9e\u58eb\u51b2\u3001\u9ea6\u5c0f\u660e\u3001\u949f\u4e00 \u8c6a\u3001\u4f59\u4ea6\u4e50\u7b49\u4eba\u5374\u4e0d\u80af\u820d\uff0c\u5f88\u5feb\u4fbf\u628a\u4ed6\u4eec\u5236\u670d\uff0c\u7136\u540e\u62bc\u56de\u805a\u4e49\u5385\u3002 \u3000\u3000\u5e9e\u58eb\u51b2\u6240\u4ee5\u4e0d\u5f53\u573a\u7ed3\u679c\u4ed6\u4eec\uff0c\u4e0d\u5916\u662f\u4e3a\u4e86\u5c0a\u91cd\u80e1\u67cf\u9f84\u548c\u8c37\u5bd2\u9999\uff0c\u4ee5\u4fbf\u4ea4\u7531\u4ed6\u4eec\u592b\u59bb\u4eb2\u81ea \u5904\u7f6e\u3002 \u3000\u3000\u8fd9\u65f6\u5927\u5385\u5916\u7684\u5e7f\u573a\uff0c\u65e9\u5df2\u5c38\u6a2a\u904d\u5730\u3002\u6bd2\u706b\u6210\u5168\u3001\u9ed1\u9b54\u65f6\u5bc5\u3001\u706b\u83bd\u3001\u725b\u5954\u4ee5\u53ca\u6c5f\u5357\u56db\u602a\u4e2d \u7684\u8001\u5927\u5c71\u9b51\u6bdb\u8d8a\uff0e\u5168\u5012\u5367\u5728\u8840\u6cca\u4e4b\u4e2d\u3002 \u3000\u3000\u81f3\u4e8e\u5c11\u6797\u7247\u9762\u7684\u5929\u7985\u3001\u5929\u671b\u3001\u5929\u673a\u3001\u5929\u89c9\u3001\u609f\u5c18\u4ee5\u53ca\u90a3\u6570\u5341\u540d\u50e7\u4eba\uff0e\u5374\u90fd\u5df2\u4e0d\u77e5\u53bb\u5411\u3002 \u5176\u4e2d\u6709\u5c11\u6570\u51e0\u540d\u50e7\u4eba\uff0c\u4e5f\u6b7b\u5728\u6df7\u6218\u4e4b\u4e2d\u3002 \u3000\u3000\u8c01\u77e5\u7247\u523b\u4e4b\u540e\uff0c\u5927\u5385\u5916\u7adf\u4e00\u4e0b\u5b50\u53c8\u6d8c\u6765\u597d\u51e0\u767e\u4eba\u3002 \u3000\u3000\u80e1\u67cf\u9f84\u7b49\u7fa4\u8c6a\u5148\u662f\u5927\u611f\u5403\u60ca\uff0c\u597d\u5728\u5f88\u5feb\u4fbf\u770b\u51fa\u539f\u6765\u4ed6\u4eec\u662f\u62e5\u6234\u6545\u4e3b\u8868\u660e\u6548\u5fe0\u800c\u6765\u7684\u3002 \u3000\u3000\u5f53\u4e0b\u80e1\u67cf\u9f84\u6717\u58f0\u544a\u8bc9\u4ed6\u4eec\u6682\u4e14\u5404\u56de\u5c97\u4f4d\uff0e\u7b49\u4e8b\u60c5\u5904\u7406\u5b8c\u6bd5\u540e\u518d\u2014\u2014\u63a5\u89c1\u6170\u95ee\u3002 \u3000\u3000\u4f17\u4eba\u8fd9\u624d\u6b22\u547c\u800c\u6563\u3002 \u3000\u3000\u80e1\u67cf\u9f84\u68c0\u89c6\u81ea\u5df1\u5e26\u6765\u7684\u7fa4\u8c6a\uff0c\u9664\u5c11\u6570\u51e0\u4eba\u6218\u4f24\u5916\uff0e\u5176\u4f59\u90fd\u5b8c\u597d\u65e0\u6059\u3002 \u3000\u3000\u5e9e\u58eb\u51b2\u5429\u5490\u6c5f\u5317\u56db\u9f99\u5b88\u4f4f\u5927\u5385\u95e8\u53e3\uff0c\u7ee7\u7eed\u76d1\u89c6\u6709\u4f55\u52a8\u9759\uff0e\u7136\u540e\u62db\u547c\u4f17\u4eba\u8fdb\u5165\u5927\u5385\u3002 \u3000\u3000\u970d\u5143\u4f3d\u3001\u5df4\u5929\u4e49\u3001\u5b8b\u5929\u94ce\u968f\u540e\u4e5f\u88ab\u9ea6\u5c0f\u660e\u3001\u949f\u4e00\u8c6a\u3001\u4f59\u4ea6\u4e50\u7b49\u4eba\u62bc\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u5e9e\u58eb\u51b2\u6599\u5b9a\u80e1\u67cf\u9f84\u5bf9\u8fd9\u4e09\u540d\u9996\u6076\u5206\u5b50\u4e0d\u5fcd\u5fc3\u4ece\u4e25\u5904\u7f6e\uff0e\u4e0d\u7b49\u80e1\u6015\u9f84\u5f00\u53e3\uff0c\u4fbf\u51b7\u7136\u7b11\u9053\uff1a \u201c\u4f60\u4eec\u4e09\u4e2a\u8fd8\u6709\u4ec0\u4e48\u8bdd\u8bf4\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u548c\u5cad\u5357\u4e8c\u5947\u5f53\u771f\u80fd\u5c48\u80fd\u4f38\uff0c\u7adf\u7136\u4e24\u817f\u4e00\u8f6f\uff0c\u9f50\u9f50\u8dea\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u5e9e\u58eb\u51b2\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u8fd9\u7b97\u4ec0\u4e48\uff0c\u8dea\u7740\u6709\u4ec0\u4e48\u7528\uff0c\u8bb2\u8bdd\u8981\u7d27\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u55eb\u5685\u7740\u9053\uff1a\u201c\u970d\u67d0\u4f46\u6c42\u4e0d\u6b7b\uff0c\u8fd8\u671b\u5e9e\u8001\u82f1\u96c4\u4ece\u8f7b\u53d1\u843d\uff01\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u7b11\u9053\uff1a\u201c\u4f60\u6c42\u8001\u592b\u6ca1\u7528\uff0e\u6740\u4e0d\u6740\u4f60\uff0e\u5168\u7531\u80e1\u76df\u4e3b\u51b3\u5b9a\uff0c\u8001\u592b\u662f\u5c40\u5916\u4eba\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u62ac\u5934\u671b\u4e86\u80e1\u67cf\u9f84\u4e00\u773c\u9053\uff1a\u201c\u4ed6\u771f\u662f\u80e1\u76df\u4e3b\u5417\uff1f\u201d \u3000\u3000\u53ea\u542c\u9ea6\u5c0f\u660e\u9a82\u9053\uff1a\u201c\u8001\u738b\u516b\u86cb\uff01\u778e\u4e86\u4f60\u7684\u72d7\u773c\uff0c\u96be\u9053\u80e1\u76df\u4e3b\u8fd8\u6709\u5047\u7684\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u7acb\u5373\u8f6c\u8dea\u5411\u80e1\u67cf\u9f84\u9053\uff1a\u201c\u5c5e\u4e0b\u6709\u773c\u65e0\u73e0\uff0c\u7f6a\u8be5\u4e07\u6b7b\uff0e\u82e5\u65e9\u77e5\u771f\u662f\u76df\u4e3b\uff0c\u5c5e\u4e0b\u4e5f\u65e9 \u5c31\u7387\u4f17\u606d\u8fce\u76df\u4e3b\u590d\u4f4d\u4e86\uff0c\u600e\u6562\u505a\u51fa\u8fd9\u7b49\u65e0\u6cd5\u65e0\u5929\u4e4b\u4e8b\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u53f1\u9053\uff1a\u201c\u4f60\u82e5\u4e0d\u662f\u843d\u5230\u73b0\u5728\u7684\u5730\u6b65\uff0c\u600e\u4f1a\u8bf4\u51fa\u8fd9\u79cd\u8bdd\u6765\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u5e72\u54b3\u4e86\u4e24\u58f0\u9053\uff1a\u201c\u9ea6\u5c0f\u5144\u5f1f\uff0c\u4f60\u5c31\u505a\u505a\u597d\u4e8b\uff0e\u5e2e\u6211\u8bb2\u51e0\u53e5\u597d\u8bdd\u5427\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u4e24\u773c\u4e00\u77aa\u9053\uff1a\u201c\u8c01\u662f\u4f60\u7684\u9ea6\u5c0f\u5144\u5f1f\uff0c\u4e5f\u4e0d\u6492\u6ce1\u5c3f\u7167\u7167\uff0c\u4f60\u591f\u8d44\u683c\u548c\u6211\u79f0\u5144\u9053\u5f1f \u5417\uff1f\u201d \u3000\u3000\u80e1\u67cf\u9f84\u8f6c\u53c8\u671b\u5411\u8c37\u5bd2\u9999\u9053\uff1a\u201c\u9999\u59b9\uff0c\u6211\u53bb\u540e\u4f60\u66fe\u7ee7\u4efb\u4e86\u4e24\u5e74\u76df\u4e3b\uff0e\u548c\u4ed6\u76f8\u5904\u7684\u65f6\u95f4\u6bd4\u6211 \u957f\uff0c\u5bf9\u4ed6\u6240\u4f5c\u6240\u4e3a\uff0c\u77e5\u9053\u7684\u6bd4\u6211\u53ef\u80fd\u8981\u591a\u4e9b\uff0e\u4f60\u8ba4\u4e3a\u8be5\u5982\u4f55\u53d1\u843d\u4ed6\uff1f\u201d \u3000\u3000\u8c37\u5bd2\u9999\u60f3\u4e86\u60f3\uff0c\u5e7d\u5e7d\u5401\u53e3\u6c14\u9053\uff1a\u201c\u8fd8\u662f\u7531\u5927\u54e5\u505a\u4e3b\u5427\uff01\u4e0d\u8bba\u4f60\u600e\u6837\u5904\u7f6e\u4ed6\uff0c\u6211\u90fd\u6ca1\u6709\u610f \u89c1\u3002\u201d \u3000\u3000\u80e1\u67cf\u9f84\u7565\u4e00\u6c89\u541f\uff0c\u671b\u5411\u970d\u5143\u4f3d\u9053\uff1a\u201c\u4f60\u662f\u5426\u8fd8\u5e0c\u671b\u518d\u4efb\u526f\u76df\u4e3b\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u51e0\u4e4e\u4e0d\u6562\u76f8\u4fe1\u81ea\u5df1\u7684\u8033\u6735\uff0c\u5fd9\u9053\uff1a\u201c\u5c5e\u4e0b\u53ea\u6c42\u4e0d\u6b7b\uff0c\u82e5\u76df\u4e3b\u80af\u8ba9\u5c5e\u4e0b\u518d\u4efb\u526f\u76df\u4e3b\uff0c \u5927\u6069\u5927\u5fb7\uff0c\u5c5e\u4e0b\u6ca1\u9f7f\u96be\u5fd8\u3002\u4ece\u4eca\u4ee5\u540e\uff0c\u7c89\u8eab\u788e\u9aa8\uff0c\u5728\u6240\u4e0d\u8f9e\uff01\u201d \u3000\u3000\u5374\u542c\u9ea6\u5c0f\u660e\u53eb\u9053\uff1a\u201c\u5e08\u5144\uff0c\u8fd9\u8001\u5c0f\u5b50\u4eba\u9762\u517d\u5fc3\uff0c\u82e5\u6570\u6708\u524d\u711a\u67cf\u9f84\u9662\u9042\u4e86\u4ed6\u7684\u610f\uff0c\u4eca\u5929\u8ddf \u968f\u60a8\u7684\u8fd9\u4f19\u4eba\uff0c\u4fbf\u5168\u90fd\u6ca1\u547d\u4e86\u3002\u4f60\u9976\u4ed6\u4e0d\u6b7b\u5c31\u5df2\u5927\u5927\u4e0d\u8be5\uff0c\u600e\u53ef\u518d\u6062\u590d\u4ed6\u7684\u65e7\u804c\uff1f\u201d \u3000\u3000\u80e1\u67cf\u9f84\u4e0d\u52a8\u58f0\u8272\u9053\uff1a\u201c\u4e0d\u7ba1\u5982\u4f55\uff0c\u4ed6\u603b\u662f\u6211\u5f53\u5e74\u624b\u521b\u8ff7\u8e2a\u8c37\u7684\u65e7\u5c5e\uff0e\u4ed6\u65e2\u7136\u6b64\u523b\u5df2\u7ecf\u77e5 \u9519\uff0c\u6211\u603b\u4e0d\u8be5\u4e8f\u5f85\u4e8e\u4ed6\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u6b63\u8981\u518d\u4e89\u8bba\uff0c\u5374\u542c\u8c37\u5bd2\u9999\u9053\uff1a\u201c\u5c0f\u660e\u4e0d\u5fc5\u518d\u8bf4\u4e86\uff0c\u542c\u5927\u54e5\u7684\u8bdd\uff0c\u4e0d\u4f1a\u9519\u7684\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u53ea\u597d\u6c14\u547c\u547c\u7684\u4e0d\u518d\u8a00\u8bed\u3002 \u3000\u3000\u80e1\u67cf\u9f84\u5429\u5490\u9053\uff1a\u201c\u4f60\u4eec\u4e09\u4e2a\u90fd\u8d77\u6765\uff0e\u6211\u8fd8\u6709\u5f88\u591a\u4e8b\u60c5\u8981\u4f60\u4eec\u505a\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u3001\u5df4\u5929\u4e49\u3001\u5b8b\u5929\u94ce\u6709\u5982\u6b7b\u56da\u5f97\u8499\u5927\u8d66\uff0c\u5c0f\u5fc3\u7684\u722c\u8d77\u8eab\u6765\uff0c\u51e0\u4e4e\u540c\u65f6\u8eac\u8eab\u5782\u624b\u9053\uff1a \u201c\u76df\u4e3b\u8981\u5c5e\u4e0b\u505a\u4ec0\u4e48\uff0c\u5feb\u8bf7\u5429\u5490\uff01\u201d \u3000\u3000\u80e1\u67cf\u9f84\u9053\uff1a\u201c\u5927\u5385\u5916\u7684\u5c38\u4f53\uff0c\u9a6c\u4e0a\u6e05\u7406\u57cb\u846c\uff0c\u4f59\u4e0b\u4e8b\u518d\u542c\u5019\u5429\u5490\uff01\u201d \u3000\u3000\u4e09\u4eba\u5e94\u58f0\u800c\u53bb\uff0e \u3000\u3000\u949f\u4e00\u8c6a\u62c5\u5fc3\u970d\u5143\u4f3d\u7b49\u4e09\u4eba\u9003\u8d70\uff0c\u5fd9\u9053\uff1a\u201c\u76df\u4e3b\uff0c\u8981\u4e0d\u8981\u518d\u6d3e\u4eba\u76d1\u89c6\u4ed6\u4eec\u7684\u884c\u52a8\uff1f\u201d \u3000\u3000\u201c\u4e0d\u5fc5\uff0c\u4ed6\u4eec\u5df2\u77e5\u4e0d\u6b7b\uff0c\u800c\u4e14\u4ecd\u53ef\u590d\u4efb\u539f\u804c\uff0c\u5c82\u6709\u9003\u8d70\u4e4b\u7406\u3002\u201d \u3000\u3000\u63a5\u7740\u53c8\u5429\u5490\u949f\u4e00\u8c6a\u3001\u4f59\u4ea6\u4e50\u3001\u8303\u7389\u6606\u3001\u6587\u5929\u751f\u3001\u9c8d\u8d85\u3001\u6d2a\u6cfd\u3001\u738b\u5927\u5eb7\u7b49\u4eba\u9053\uff1a\u201c\u5404\u4f4d\u73b0 \u5728\u5c31\u5230\u8c37\u5185\u8c37\u5916\u5206\u5934\u5bfb\u627e\u5c11\u6797\u65b9\u9762\u7684\u4eba\uff0e\u4ed6\u4eec\u90fd\u5df2\u670d\u8fc7\u2018\u5411\u5fc3\u9732\u2019\uff0c\u4e27\u5931\u795e\u667a\uff0e\u4e0d\u53ef\u80fd\u8d70\u5f97 \u592a\u8fdc\uff0c\u627e\u5230\u4e4b\u540e\uff0c\u5c31\u628a\u4ed6\u4eec\u5e26\u56de\u805a\u4e49\u5385\u6765\uff01\u201d \u3000\u3000\u4f17\u4eba\u968f\u5373\u9886\u547d\u800c\u53bb\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u80e1\u67cf\u9f84\u7684\u65e7\u5c5e\u5fc3\u8179\u66fe\u6570\u5ea6\u5192\u6b7b\u5230\u4e07\u6708\u5ce1\u548c\u767d\u59d1\u5bfa\u4f20\u9012\u6d88\u606f\u7684\u674e\u6797\u3001\u8d75\u798f\u4e5f\u6765\u5230\u5927 \u5385\u5411\u80e1\u67cf\u9f84\u3001\u8c37\u5bd2\u9999\u62dc\u89c1\uff0e\u5e76\u544a\u77e5\u8c37\u5185\u8be6\u60c5\u3002 \u3000\u3000\u5e9e\u58eb\u51b2\u9053\uff1a\u201c\u73b0\u5728\u5c31\u7531\u8001\u592b\u5728\u6b64\u5750\u9547\uff0c\u4f60\u4eec\u4e0d\u9632\u8d81\u8fd9\u673a\u4f1a\u5230\u8c37\u5185\u5404\u5904\u8d70\u8d70\u3002\u201d \u3000\u3000\u4e8e\u662f\u80e1\u67cf\u9f84\u3001\u8c37\u5bd2\u9999\u7387\u9886\u5728\u573a\u7684\u9ea6\u5c0f\u660e\u3001\u82d7\u7d20\u5170\u3001\u82d7\u7d20\u82d3\u3001\u4e07\u6620\u971e\uff0c\u5728\u725b\u6797\u548c\u8d75\u798f\u7684\u966a \u540c\u4e0b\uff0e\u5c55\u5f00\u5bf9\u8ff7\u8e2a\u8c37\u7684\u5de1\u89c6\u3002 \u3000\u3000\u4ed6\u4eec\u6bcf\u5230\u4e00\u5904\uff0c\u5c31\u6709\u4e00\u5904\u53d1\u51fa\u6b22\u547c\u58f0\u3002 \u3000\u3000\u56de\u7a0b\u65f6\uff0c\u6765\u5230\u4e86\u4e1c\u6d77\u9662\uff0c\u53ea\u89c1\u5927\u95e8\u6df1\u9501\uff0c\u5374\u6709\u4e00\u540d\u55bd\u5175\u5728\u8d1f\u8d23\u8b66\u536b\u3002 \u3000\u3000\u8fd9\u540d\u55bd\u5175\u540d\u53eb\u9ad8\u4e09\uff0c\u4e00\u89c1\u80e1\u67cf\u9f84\u7b49\u4eba\u5230\u8fbe\uff0c\u8fde\u5fd9\u8fc7\u6765\u884c\u793c\u3002 \u3000\u3000\u80e1\u67cf\u9f84\u5e76\u672a\u547d\u9ad8\u4e09\u5f00\u95e8\u8fdb\u53bb\u67e5\u770b\uff0e\u5374\u671b\u7740\u5927\u95e8\u4e0a\u65b9\u90a3\u201c\u4e1c\u6d77\u9662\u201d\u4e09\u4e2a\u5927\u5b57\u9053\uff1a\u201c\u8fd9\u5730\u65b9 \u662f\u4ec0\u4e48\u65f6\u5019\u53d6\u540d\u4e1c\u6d77\u9662\u7684\uff1f\u201d \u3000\u3000\u8c37\u5bd2\u9999\u8e59\u8d77\u53cc\u7709\u9053\uff1a\u201c\u5c0f\u59b9\u5728\u65f6\uff0c\u8fd9\u91cc\u4e5f\u4e0d\u53eb\u4e1c\u6d77\u9662\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u8fde\u5fd9\u89e3\u91ca\u9053\uff1a\u201c\u5e08\u5144\u548c\u5e08\u5ac2\u6709\u6240\u4e0d\u77e5\uff0c\u970d\u5143\u4f3d\u6536\u4e86\u2014\u4e2a\u5973\u7684\u53eb\u8bb8\u5c0f\u65e6\uff0c\u5c31\u628a\u5979\u5b89 \u7f6e\u5728\u8fd9\u91cc\u4f4f\u3002\u56e0\u90a3\u5973\u7684\u81ea\u79f0\u662f\u4e1c\u6d77\u6765\u7684\uff0e\u6240\u4ee5\u5c31\u628a\u8fd9\u91cc\u53d6\u540d\u4e1c\u6d77\u9662\u3002\u201d \u3000\u3000\u63a5\u7740\u95ee\u9ad8\u4e09\u9053\uff1a\u201c\u65e2\u7136\u6709\u4eba\u5728\u91cc\u9762\u4f4f\uff0c\u4e3a\u4ec0\u4e48\u5374\u53c8\u9501\u4e0a\u5927\u95e8\uff1f\u201d \u3000\u3000\u9ad8\u4e09\u9053\uff1a\u201c\u7980\u9ea6\u5934\u9886\uff0c\u73b0\u5728\u91cc\u9762\u5df2\u7ecf\u6ca1\u4eba\u4f4f\u4e86\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u90a3\u5973\u7684\u5462\uff1f\u201d \u3000\u3000\u9ad8\u4e09\u9053\uff1a\u201c\u6b7b\u5566\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u4e0d\u89c9\u4e00\u6123\u9053\uff1a\u201c\u662f\u600e\u4e48\u6b7b\u7684\uff1f\u201d \u3000\u3000\u9ad8\u4e09\u9053\uff1a\u201c\u4e0a\u540a\u6b7b\u7684\u3002\u201d \u3000\u3000\u201c\u5979\u4e3a\u4ec0\u4e48\u8981\u4e0a\u540a\uff1f\u201d \u3000\u3000\u201c\u8bb8\u4ed9\u5b50\u8001\u662f\u4e00\u4e2a\u4eba\u5728\u8fd9\u680b\u697c\u4e0a\uff0c\u970d\u76df\u4e3b\u53c8\u4e0d\u51c6\u7537\u4eba\u6765\uff0c\u8bb8\u4ed9\u5b50\u53d7\u4e0d\u4e86\u53d1\u4e86\u75af\uff0c\u6700\u540e\u4fbf \u60f3\u4e0d\u5f00\u4e0a\u540a\u6b7b\u4e86\uff01\u201d \u3000\u3000\u8fd9\u51e0\u53e5\u8bdd\u4e0d\u6253\u7d27\uff0c\u53ea\u542c\u5f97\u8c37\u5bd2\u9999\u3001\u82d7\u7d20\u5170\u3001\u82d7\u7d20\u82d3\u3001\u4e07\u6620\u971e\u90fd\u5df2\u5a07\u9762\u6cdb\u7ea2\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u5fd9\u6325\u6325\u624b\u9053\uff1a\u201c\u522b\u8bf4\u5f97\u8fd9\u4e48\u96be\u542c\uff0c\u65e2\u7136\u90a3\u5973\u7684\u6b7b\u4e86\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u53eb\u522b\u4eba\u6765\u4f4f\uff0c\u7a7a\u7740 \u591a\u53ef\u60dc\uff0c\u53c8\u4f55\u5fc5\u8fd8\u6d3e\u4eba\u5b88\u95e8\uff1f\u201d \u3000\u3000\u9ad8\u4e09\u9053\uff1a\u201c\u970d\u524d\u76df\u4e3b\u672c\u6765\u51c6\u5907\u6d3e\u4eba\u6765\u4f4f\uff0c\u4f46\u8bb8\u4ed9\u5b50\u6b7b\u540e\u9634\u9b42\u4e0d\u6563\uff0c\u5e38\u5e38\u95f9\u9b3c\uff0c\u8c01\u90fd\u4e0d\u6562 \u4f4f\uff0c\u53ea\u597d\u628a\u8fd9\u91cc\u505a\u4e3a\u4ed3\u5e93\uff0e\u5c0f\u7684\u73b0\u5728\u662f\u5728\u770b\u5b88\u4ed3\u5e93\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u7b11\u9053\uff1a\u201c\u5c0f\u5fc3\u591c\u665a\u88ab\u90a3\u5973\u9b3c\u6349\u8d70\u3002\u201d \u3000\u3000\u9ad8\u4e09\u9053\uff1a\u201c\u6ca1\u5173\u7cfb\uff0c\u665a\u4e0a\u662f\u53cc\u8b66\u536b\uff0c\u6709\u4f34\u5c31\u7528\u4e0d\u7740\u6015\u4e86\u3002\u201d \u3000\u3000\u5f53\u80e1\u67cf\u9f84\u3001\u8c37\u5bd2\u9999\u7387\u9886\u4f17\u4eba\u518d\u56de\u5230\u5927\u5385\u65f6\uff0c\u4e0d\u89c9\u90fd\u5413\u4e86\u4e00\u8df3\uff0c\u53ea\u89c1\u5927\u5385\u5730\u4e0a\u7adf\u8eba\u4e86\u51e0\u5341 \u4e2a\u4eba\u3002\u5e9e\u58eb\u51b2\uff0e\u949f\u4e00\u8c6a\u3001\u4f59\u4ea6\u4e50\u7b49\u4eba\u6b63\u5728\u6765\u56de\u5de1\u89c6\u3002 \u3000\u3000\u80e1\u67cf\u9f84\u6115\u7136\u9053\uff1a\u201c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u9053\uff1a\u201c\u4f60\u6240\u8981\u627e\u7684\u4eba\uff0c\u9664\u4e86\u5c11\u6570\u51e0\u4e2a\u6b7b\u7684\uff0c\u5168\u627e\u56de\u6765\u4e86\uff0c\u53ea\u6709\u5c11\u6797\u53db\u5f92\u548c\u4e00\u4e2a\u53eb \u9ad8\u5d99\u7684\u4e0d\u77e5\u53bb\u5411\u3002\u201d \u3000\u3000\u80e1\u67cf\u9f84\u7acb\u5373\u4e5f\u548c\u8c37\u5bd2\u9999\u3001\u9ea6\u5c0f\u660e\u5de1\u89c6\u4e86\u4e00\u904d\u3002 \u3000\u3000\u5929\u7985\u3001\u5929\u671b\u3001\u5929\u673a\u3001\u5929\u89c9\u56db\u4f4d\u5927\u5e08\u5168\u5728\u3002\u53e6\u5916\u4fbf\u662f\u609f\u5c18\u3001\u666e\u51e1\u3001\u666e\u6d4e\u3001\u666e\u6e21\u4ee5\u53ca\u7f57\u94ee\u3001 \u4e01\u4e00\u9b42\u3001\u5f20\u656c\u5b89\u548c\u4e8c\u4e09\u5341\u540d\u5c11\u6797\u5f1f\u5b50\uff0c\u8fde\u90a3\u4e5d\u540d\u94dc\u4eba\u4e5f\u5728\u5185\u3002 \u3000\u3000\u80e1\u67cf\u9f84\u770b\u4e86\u4e00\u9635\u9053\uff1a\u201c\u4ed6\u4eec\u600e\u4e48\u90fd\u8eba\u5728\u5730\u4e0a\uff0e\u597d\u50cf\u5df2\u7ecf\u4eba\u4e8b\u4e0d\u77e5\u3002\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u9053\uff1a\u201c\u8001\u592b\u5df2\u7ecf\u7ed9\u4ed6\u4eec\u6bcf\u4eba\u5582\u4e0b\u4e00\u7c92\u89e3\u836f\uff0c\u4ed6\u4eec\u90fd\u5fc5\u987b\u660f\u7761\u5341\u4e2a\u65f6\u8fb0\uff0e\u5927\u7ea6\u660e\u5929 \u65e9\u4e0a\u624d\u53ef\u9192\u6765\uff0e\u9192\u6765\u4e4b\u540e\u4fbf\u5b8c\u5168\u590d\u539f\u3002\u201d \u3000\u3000\u80e1\u67cf\u9f84\u4e00\u76b1\u7709\u5934\u9053\uff1a\u201c\u8fd9\u4e48\u4e45\u7684\u65f6\u95f4\uff0e\u8ba9\u4ed6\u4eec\u4e00\u76f4\u8eba\u5728\u5927\u5385\uff0e\u53ea\u6015\u4e0d\u592a\u59a5\u5f53\u3002\u201d \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u8fd9\u91cc\u79bb\u5929\u661f\u9662\u6700\u8fd1\uff0c\u5f85\u4f1a\u513f\u5c5e\u4e0b\u5c31\u547d\u4eba\u628a\u4ed6\u4eec\u62ac\u8fdb\u5929\u661f\u9662\u3002\u201d \u3000\u3000\u80e1\u67cf\u9f84\u9053\uff1a\u201c\u8fd9\u6837\u6700\u597d\uff0c\u949f\u8d24\u5f1f\u8bf7\u518d\u9a6c\u4e0a\u4ea4\u4ee3\u53a8\u623f\uff0e\u4eca\u665a\u5728\u5927\u5385\u5916\u5e7f\u573a\u8bbe\u5bb4\u5e86\u529f\uff0c\u6240\u6709 \u5144\u5f1f\uff0e\u5168\u4f53\u53c2\u52a0\u3002\u201d \u3000\u3000\u5f53\u665a\uff0c\u8ff7\u8e2a\u8c37\u805a\u4e49\u5385\u5916\u7684\u5e7f\u573a\u4e0a\uff0c\u5e2d\u5f00\u767e\u4f59\u684c\uff0c\u97ad\u70ae\u53ca\u6b22\u817e\u4e4b\u58f0\u4e0d\u7edd\uff0e\u54cd\u904d\u5c71\u8c37\uff0c\u76db\u51b5 \u7a7a\u524d\u3002 \u3000\u3000\u80e1\u67cf\u9f84\u5f53\u771f\u5bbd\u5b8f\u5927\u91cf\uff0c\u7adf\u628a\u970d\u5143\u4f3d\u3001\u5df4\u5929\u4e49\u3001\u5b8b\u5929\u94ce\u4e09\u4eba\u4e5f\u5b89\u6392\u5728\u9996\u5e2d\uff0e\u5e9e\u58eb\u51b2\u662f\u5f53\u7136 \u7684\u5750\u5728\u6700\u4e0a\u4f4d\u3002 \u3000\u3000\u53e6\u56db\u4eba\u4fbf\u662f\u80e1\u67cf\u9f84\u3001\u8c37\u5bd2\u9999\u3001\u4f59\u4ea6\u4e50\u548c\u949f\u4e00\u8c6a\uff0c\u8fde\u9ea6\u5c0f\u660e\u90fd\u6392\u9664\u5728\u9996\u5e2d\u4e4b\u5916\u3002 \u3000\u3000\u8fd9\u4e00\u987f\u9152\u7b75\uff0c\u76f4\u5403\u5230\u5c06\u8fd1\u4e8c\u66f4\uff0c\u624d\u4e2a\u4e2a\u9152\u9189\u996d\u9971\uff0e\u5c3d\u6b22\u800c\u6563\u3002 \u3000\u3000\u80e1\u67cf\u9f84\u7b49\u4eba\u4ecd\u4f4f\u5728\u67cf\u9f84\u9662\uff0c\u53ea\u6709\u5e9e\u58eb\u51b2\u56e0\u987b\u968f\u65f6\u68c0\u89c6\u670d\u4e0b\u89e3\u836f\u7684\u4eba\uff0e\u4fbf\u5e26\u7740\u9ea6\u5c0f\u660e\u4f4f\u5728 \u5929\u661f\u9662\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u548c\u5e9e\u58eb\u51b2\u8fdb\u5165\u5929\u661f\u9662\u521a\u7761\u4e0b\u4e0d\u4e45\uff0e\u4fbf\u542c\u6709\u4eba\u8f7b\u8f7b\u6572\u95e8\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u62ab\u8863\u4e0b\u5e8a\uff0c\u6253\u5f00\u95e8\u540e\uff0e\u8fdb\u6765\u7684\u8d6b\u7136\u662f\u949f\u4e00\u8c6a\u548c\u4f59\u4ea6\u4e50\uff0c\u4e8c\u4eba\u5168\u662f\u52b2\u88c5\u6253\u626e\uff0c\u800c\u4e14 \u8170\u4f69\u5175\u5203\uff0c\u4e0d\u89c9\u8bb6\u7136\u95ee\u9053\uff1a\u201c\u4f60\u4eec\u4e8c\u4f4d\u6765\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u949f\u4e00\u8c6a\u4f4e\u58f0\u9053\uff1a\u201c\u6709\u4e8b\u6765\u627e\u5e9e\u8001\u524d\u8f88\u548c\u4f60\u5546\u91cf\uff01\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u8fd9\u65f6\u4e5f\u7531\u5e8a\u4e0a\u5750\u8d77\uff0c\u6309\u53e3\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\u5feb\u8bf4\uff01\u201d \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u5e9e\u8001\u524d\u8f88\u53ef\u770b\u51fa\u970d\u5143\u4f3d\u548c\u5cad\u5357\u4e8c\u5947\u4ecd\u5fc3\u6000\u4e8c\u610f\uff1f\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u9894\u9996\u9053\uff1a\u201c\u8001\u592b\u5f53\u7136\u770b\u51fa\u4ed6\u4eec\u5e76\u975e\u76f4\u6b63\u7518\u4f0f\uff0c\u82e5\u628a\u8fd9\u4e09\u4eba\u7559\u5728\u8ff7\u8e2a\u8c37\uff0c\u603b\u662f\u4f60\u4eec \u7684\u540e\u60a3\uff01\u201d \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u4eca\u665a\u56e0\u5929\u661f\u9662\u591a\u4f4f\u8fdb\u51e0\u5341\u4eba\uff0c\u665a\u8f88\u5df2\u547d\u5cad\u5357\u4e8c\u5947\u548c\u970d\u5143\u4f3d\u5408\u4f4f\u4e00\u5ba4\uff0c\u4e09\u4e2a \u8fdb\u623f\u540e\uff0c\u5fc5\u5b9a\u7a83\u7a83\u79c1\u8bae\u3002 \u3000\u3000\u665a\u8f88\u548c\u4f59\u5148\u751f\u6765\uff0c\u662f\u60f3\u627e\u9ea6\u5c0f\u660e\u5144\u5f1f\u4e00\u8d77\u5230\u970d\u5143\u4f3d\u7a97\u5916\u7a83\u542c\u4ed6\u4eec\u8bf4\u4e9b\u4ec0\u4e48\u3002\u5fc5\u8981\u65f6\u4e5f\u597d \u628a\u4ed6\u4eec\u4e00\u4e3e\u6210\u64d2\uff0c\u514d\u9664\u540e\u60a3\uff01\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u7565\u2014\u6c89\u541f\u9053\uff1a\u201c\u591a\u4e8f\u4f60\u4eec\u60f3\u5f97\u5468\u5230\uff0c\u73b0\u5728\u4f60\u4eec\u5c31\u53bb\u3002\u82e5\u6709\u9700\u8981\u8001\u592b\u5e2e\u5fd9\u4e4b\u5904\uff0c\u968f \u65f6\u6765\u901a\u77e5\u6211\u3002\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u5306\u5306\u7740\u597d\u88c5\uff0c\u4f69\u4e0a\u957f\u5251\uff0c\u7acb\u5373\u968f\u540c\u949f\u4e00\u8c6a\u3001\u4f59\u4ea6\u4e50\u6084\u6084\u51fa\u4e86\u623f\u95e8\u3002 \u3000\u3000\u970d\u5143\u4f3d\u4f4f\u5728\u5929\u661f\u9662\u6700\u540e\u4e00\u8fdb\uff0e\u540e\u7a97\u5916\u4fbf\u662f\u56f4\u5899\uff0c\u4e2d\u95f4\u53ea\u7559\u4e86\u4e00\u6761\u5bbd\u4e0d\u8fc7\u4e94\u5c3a\u7684\u901a\u9053\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u7b49\u4eba\u662f\u7531\u5916\u9762\u8dc3\u8fdb\u56f4\u5899\uff0c\u518d\u6f5c\u5230\u970d\u5143\u4f3d\u7a97\u5916\u3002 \u3000\u3000\u91cc\u9762\u679c\u7136\u6709\u7a83\u7a83\u79c1\u8bed\u58f0\u4f20\u51fa\u3002 \u3000\u3000\u6b63\u56e0\u970d\u5143\u4f3d\u548c\u5cad\u5357\u4e8c\u5947\u6b63\u5728\u4ea4\u8c08\uff0c\u518d\u52a0\u4e0a\u9152\u559d\u5f97\u4e0d\u5c11\uff0e\u56e0\u4e4b\uff0e\u8b66\u89c9\u4e4b\u5fc3\u4e5f\u968f\u4e4b\u51cf\u4f4e\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u7b49\u4e09\u4eba\u5728\u7a97\u5916\u5c4f\u606f\u7a83\u542c\u3002 \u3000\u3000\u53ea\u542c\u970d\u5143\u4f3d\u9053\uff1a\u201c\u53ea\u8981\u628a\u67cf\u9f84\u9662\u7684\u4eba\u901a\u901a\u7ed3\u679c\uff0c\u8001\u592b\u4fbf\u53ef\u6062\u590d\u76df\u4e3b\u4e4b\u4f4d\uff0e\u80e1\u67cf\u9f84\u53ea\u4e0d\u8fc7 \u53c8\u505a\u4e86\u4e00\u5929\u76df\u4e3b\u800c\u5df2\uff01\u201d \u3000\u3000\u5df4\u5929\u4e49\u9053\uff1a\u201c\u4f9d\u5c5e\u4e0b\u4e4b\u89c1\uff0c\u8fde\u5c11\u6797\u65b9\u9762\u4e5f\u8981\u4e00\u8d77\u6740\u5149\uff0e\u7559\u7740\u4ed6\u4eec\uff0c\u52bf\u5fc5\u6210\u4e3a\u540e\u60a3\uff01\u201d \u3000\u3000\u970d\u5143\u4f3d\u987f\u4e86\u4e00\u987f\u9053\uff1a\u201c\u4f60\u7684\u610f\u601d\u8981\u600e\u6837\u6740\u6cd5\uff1f\u201d \u3000\u3000\u5df4\u5929\u4e49\u9053\uff1a\u201c\u73b0\u5728\u6740\u4ed6\u4eec\uff0c\u6700\u7b80\u5355\u4e0d\u8fc7\uff0c\u4ed6\u4eec\u4eca\u665a\u90fd\u5b89\u7f6e\u5728\u5929\u661f\u9662\uff0c\u800c\u4e14\u2014\u4e2a\u4e2a\u4eba\u4e8b\u4e0d \u77e5\uff0c\u5168\u6570\u6740\u4e86\u4ed6\u4eec\uff0c\u4e0d\u8d39\u5439\u7070\u4e4b\u529b\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u82e5\u4e0d\u662f\u4f60\u63d0\u8d77\uff0c\u8001\u592b\u8fd8\u771f\u6ca1\u60f3\u5230\u8fd9\u4ef6\u4e8b\u5462\uff01\u201d \u3000\u3000\u623f\u5185\u6c89\u5bc2\u4e86\u534a\u54cd\uff0c\u53c8\u542c\u5b8b\u5929\u94ce\u9053\uff1a\u201c\u76df\u4e3b\u771f\u6709\u628a\u63e1\u628a\u67cf\u9f84\u9662\u7684\u4eba\u6740\u5149\u5417\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u563f\u563f\u7b11\u9053\uff1a\u201c\u8001\u592b\u4ece\u6765\u4e0d\u505a\u6ca1\u628a\u63e1\u7684\u4e8b\uff0c\u5b9e\u5bf9\u4f60\u4eec\u8bf4\uff0e\u5d06\u5cd2\u8001\u5927\u9634\u5929\u7433\u7684\u2018\u8840\u96fe \u6563\u2019\uff0c\u6211\u5df2\u5f97\u5230\u4e0d\u5c11\u3002\u8d81\u4ed6\u4eec\u719f\u7761\u65f6\u7528\uff0c\u4ed6\u4eec\u5f88\u5feb\u4fbf\u4f1a\u4eba\u4e8b\u4e0d\u77e5\uff0c\u7eb5\u7136\u4ed6\u4eec\u4e2a\u4e2a\u90fd\u6709\u80e1\u67cf\u9f84 \u90a3\u6837\u9ad8\u4e0d\u53ef\u6d4b\u7684\u6b66\u529f\uff0c\u4e5f\u53d8\u6210\u548c\u6b7b\u4eba\u4e00\u6837\uff0e\u5728\u6beb\u65e0\u62b5\u6297\u7684\u60c5\u5f62\u4e0b\uff0e\u6740\u4ed6\u4eec\u53c8\u6709\u4f55\u96be\uff1f\u201d \u3000\u3000\u5b8b\u5929\u94ce\u9053\uff1a\u201c\u76df\u4e3b\u51c6\u5907\u4ec0\u4e48\u65f6\u5019\u52a8\u624b\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u9053\uff1a\u201c\u73b0\u5728\u52a8\u624b\u8fd8\u65e9\u4e86\u70b9\uff0c\u5fc5\u987b\u7b49\u4ed6\u4eec\u719f\u7761\u540e\u624d\u80fd\u884c\u52a8\uff0c\u4e0d\u8fc7\u8fd9\u91cc\u5929\u661f\u9662\u5c11\u6797\u65b9 \u9762\u7684\uff0c\u9a6c\u535c\u5c31\u53ef\u52a8\u624b\u4e86\uff01\u201d \u3000\u3000\u7a97\u5916\u7684\u949f\u4e00\u8c6a\u3001\u4f59\u4ea6\u4e50\u3001\u9ea6\u5c0f\u660e\u542c\u5230\u8fd9\u91cc\uff0c\u7acb\u523b\u6084\u6084\u9000\u81f3\u6570\u4e08\u5916\u7684\u79d8\u5904\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u679c\u7136\u4e0d\u51fa\u6240\u6599\uff0c\u82e5\u54b1\u4eec\u4eca\u591c\u4e0d\u6765\uff0c\u53ea\u6015\u540e\u679c\u5c31\u4e0d\u582a\u8bbe\u60f3\uff01\u201d \u3000\u3000\u4f59\u4ea6\u4e50\u9053\uff1a\u201c\u4e8b\u4e0d\u5b9c\u8fdf\uff0c\u8d81\u4ed6\u4eec\u8fd8\u6ca1\u51c6\u5907\uff0c\u54b1\u4eec\u73b0\u5728\u5c31\u53ef\u6740\u8fdb\u53bb\u4e86\uff01\u201d \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u8fd9\u4e09\u4eba\u90fd\u4e0d\u662f\u6613\u4e0e\u4e4b\u8f88\uff0c\u7edd\u4e0d\u80fd\u8ba9\u4ed6\u4eec\u6709\u4efb\u4f55\u2014\u4e2a\u9003\u8131\uff0e\u6211\u73b0\u5728\u5c31\u53bb\u628a\u5e9e \u8001\u524d\u8f88\u8bf7\u6765\uff0c\u5c0f\u660e\u8bf7\u5b88\u4f4f\u540e\u7a97\uff0c\u7b49\u542c\u5230\u5e9e\u8001\u524d\u8f88\u6765\u4e86\u4ee5\u540e\u518d\u4e00\u8d77\u52a8\u624b\uff01\u201d \u3000\u3000\u9ea6\u5c0f\u660e\u968f\u5373\u53c8\u6765\u5230\u540e\u7a97\u5916\u3002 \u3000\u3000\u4e0d\u5927\u4e00\u4f1a\u513f\uff0c\u949f\u4e00\u8c6a\u5df2\u628a\u5e9e\u58eb\u51b2\u8bf7\u6765\uff0c\u52a0\u4e0a\u4f59\u4ea6\u4e50\uff0c\u5171\u662f\u4e09\u4eba\uff0c\u6765\u5230\u970d\u5143\u4f3d\u4f4f\u5904\u7684\u524d\u95e8\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u5728\u8def\u4e0a\u65e9\u5df2\u548c\u5e9e\u58eb\u51b2\u5546\u91cf\u597d\uff0c\u6765\u5230\u95e8\u5916\uff0c\u5c31\u6309\u7167\u8ba1\u5212\u884c\u4e8b\u3002 \u3000\u3000\u53ea\u542c\u949f\u4e00\u8c6a\u6572\u95e8\u53eb\u9053\uff1a\u201c\u970d\u526f\u76df\u4e3b\u7761\u7740\u4e86\u6ca1\u6709\uff1f\u201d \u3000\u3000\u970d\u5143\u4f3d\u77bf\u7136\u4e00\u60ca\uff0c\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u5144\u5f1f\u949f\u4e00\u8c6a\uff0e\u5949\u76df\u4e3b\u4e4b\u547d\uff0e\u6709\u4e8b\u4e0e\u970d\u526f\u76df\u4e3b\u5546\u8bae\u3002\u201d \u3000\u3000\u970d\u5143\u4f3d\u6123\u4e86\u695e\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\u8fd9\u6837\u8981\u7d27\uff1f\u201d \u3000\u3000\u949f\u4e00\u8c6a\u9053\uff1a\u201c\u5144\u5f1f\u8fdb\u5165\u623f\u95f4\u540e\u81ea\u5f53\u5b9e\u8bb2\uff01\u201d \u3000\u3000\u623f\u95e8\u6253\u5f00\uff0c\u5f00\u95e8\u7684\u5374\u662f\u5b8b\u5929\u94ce\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u95ea\u7535\u822c\u4e00\u5251\u76f4\u5411\u5b8b\u5929\u94ce\u5f53\u80f8\u523a\u53bb\u3002 \u3000\u3000\u5b8b\u5929\u94ce\u4e00\u58f0\u60e8\u547c\uff0c\u4fbf\u5012\u5367\u8840\u6cca\u4e2d\u3002 \u3000\u3000\u970d\u5143\u4f3d\u548c\u5df4\u5929\u4e49\u5927\u60ca\u4e4b\u4e0b\uff0c\u731b\u89c1\u949f\u4e00\u8c6a\u8eab\u540e\u66f4\u6709\u5e9e\u58eb\u51b2\u548c\u4f59\u4ea6\u4e50\uff0c\u6765\u4e0d\u53ca\u53d6\u5175\u5203\uff0c\u4fbf\u614c \u5fd9\u7531\u540e\u5b99\u5411\u5916\u51b2\uff0e \u3000\u3000\u5df4\u5929\u4e49\u51b2\u5728\u524d\u9762\uff0e\u7a7f\u7a97\u800c\u51fa\u5c1a\u672a\u843d\u5730\uff0c\u4fbf\u88ab\u9ea6\u5c0f\u660e\u4e00\u5251\u524a\u53bb\u4e86\u8111\u888b\u3002 \u3000\u3000\u970d\u5143\u4f3d\u8eab\u5b50\u521a\u51fa\u7a97\u4e00\u534a\uff0c\u3000\u4e00\u89c1\u662f\u9ea6\u5c0f\u660e\u5b88\u5728\u5916\u9762\uff0c\u4fbf\u6025\u6025\u53c8\u5411\u540e\u5012\u8dc3\u3002 \u3000\u3000\u54ea\u77e5\u4ed6\u8eab\u5b50\u521a\u9000\u56de\uff0c\u949f\u4e00\u8c6a\u7684\u5251\u950b\u65e9\u5230\uff0e\u6b63\u597d\u628a\u4ed6\u62e6\u8170\u65a9\u65ad\u3002 \u3000\u3000\u72fc\u72c8\u4e3a\u5978\u7684\u970d\u5143\u4f3d\u548c\u5cad\u5357\u4e8c\u5947\uff0c\u5c31\u8fd9\u6837\u9877\u523b\u4e4b\u95f4\u5b8c\u7ed3\u4e86\u751f\u547d\u3002 \u3000\u3000\u949f\u4e00\u8c6a\u3001\u4f59\u4ea6\u4e50\u3001\u9ea6\u5c0f\u660e\u4e09\u4eba\u5e72\u4e0b\u4e86\u8fd9\u4ef6\u5927\u4e8b\uff0c\u9996\u5148\u5e76\u672a\u544a\u77e5\u80e1\u67cf\u9f84\uff0c\u62c5\u5fc3\u53d7\u8d23\uff0c\u4fbf\u592e \u6c42\u5e9e\u58eb\u51b2\u966a\u4ed6\u4eec\u4e00\u540c\u5230\u67cf\u9f84\u9662\u89c1\u80e1\u67cf\u9f84\u3002 \u3000\u3000\u80e1\u67cf\u9f84\u5f97\u77e5\u4e0a\u60c5\u540e\uff0c\u5f53\u7136\u4e5f\u4e0d\u80fd\u8d23\u5907\u4ed6\u4eec\uff0c\u53cd\u800c\u5609\u5956\u4ed6\u4eec\u7acb\u4e0b\u4e86\u5927\u529f\u3002 \u3000\u3000\u6b21\u65e5\u4e0a\u5348\uff0c\u5c11\u6797\u65b9\u9762\u7684\u4eba\u548c\u7f57\u94ee\u3001\u3000\u4e01\u4e00\u9b42\u3001\u5f20\u656c\u5b89\u90fd\u5df2\u6e05\u9192\u8fc7\u6765\u3002 \u3000\u3000\u4e2d\u5348\uff0c\u80e1\u67cf\u9f84\u8bbe\u4e0b\u7d20\u5bb4\uff0c\u6b3e\u5f85\u5929\u7985\u3001\u5929\u671b\u3001\u5929\u673a\u3001\u5929\u89c9\u3001\u609f\u5c18\u4ee5\u53ca\u4ed6\u4eec\u7684\u6570\u5341\u540d\u5f1f\u5b50\u3002 \u3000\u3000\u996d\u540e\uff0c\u5929\u7985\u5927\u5e08\u7387\u9886\u4f17\u5f1f\u5b50\uff0c\u62bc\u7740\u4e5d\u540d\u94dc\u4eba\u5411\u80e1\u67cf\u9f84\u3001\u8c37\u5bd2\u9999\u7b49\u4eba\u9053\u522b\u3002 \u3000\u3000\u81f3\u4e8e\u7f57\u94ee\u3001\u4e01\u4e00\u9b42\u3001\u5f20\u656c\u5b89\u4e09\u4eba\uff0c\u90fd\u51b3\u5b9a\u7559\u5728\u8ff7\u8e2a\u8c37\u4e3a\u80e1\u67cf\u9f84\u6548\u547d\u3002 \u3000\u3000\u80e1\u67cf\u9f84\u3001\u8c37\u5bd2\u9999\u4eb2\u7387\u949f\u4e00\u8c6a\u3001\u4f59\u4ea6\u4e50\u3001\u9ea6\u5c0f\u660e\u7b49\u4eba\u4e3a\u5929\u673a\u5927\u5e08\u7b49\u9001\u884c\u3002 \u3000\u3000\u5e9e\u571f\u51b2\u4e5f\u4e00\u8d77\u51fa\u8c37\uff0c\u4ed6\u51b3\u5b9a\u8981\u628a\u7fce\u513f\u5e26\u56de\u6765\u4ea4\u7ed9\u80e1\u67cf\u9f84\u548c\u8c37\u5bd2\u9999\uff0c\u7136\u540e\u81ea\u5df1\u4e5f\u51c6\u5907\u5728\u8ff7 \u8e2a\u8c37\u518d\u76d8\u6853\u4e9b\u65e5\u5b50\u3002 \u3000\u3000\u51fa\u4e86\u8c37\u53e3\u5ffd\u89c1\u8fdc\u5904\u6811\u836b\u4e0b\u6709\u4e24\u4e2a\u4eba\u5f71\uff0e\u4f3c\u662f\u6b63\u5411\u8fd9\u8fb9\u5f20\u671b\u3002 \u3000\u3000\u9ea6\u5c0f\u660e\u773c\u5c16\uff0c\u65e9\u770b\u51fa\u662f\u9ad8\u5d99\u548c\u609f\u660e\uff0c\u4fbf\u5411\u82d7\u7d20\u82d3\u6253\u4e00\u58f0\u62db\u547c\uff0e\u4e8c\u4eba\u7acb\u5373\u65bd\u5c55\u8f7b\u529f\uff0c\u75be\u5982 \u5212\u7a7a\u6025\u77e2\u822c\uff0c\u5411\u6811\u836b\u4e0b\u63a0\u53bb\u3002 \u3000\u3000\u9ad8\u5d99\u548c\u609f\u660e\u4e00\u89c1\u88ab\u5bf9\u65b9\u53d1\u89c9\uff0c\u6025\u6025\u6492\u817f\u72c2\u5954\u3002 \u3000\u3000\u4f46\u4ed6\u4eec\u7684\u8f7b\u529f\uff0c\u5374\u96be\u4ee5\u548c\u5bf9\u65b9\u76f8\u6bd4\uff0e\u5f88\u5feb\u4fbf\u88ab\u9ea6\u5c0f\u660e\u548c\u82d7\u7d20\u82d3\u8ffd\u4e0a\uff0c\u3000\u4e8e\u662f\u56db\u4eba\u5f53\u573a\u5c55 \u5f00\u4e00\u756a\u640f\u6740\uff0c\u9ea6\u5c0f\u660e\u62e6\u4f4f\u4e86\u9ad8\u5d99\uff0c\u82d7\u7d20\u82d3\u5219\u548c\u609f\u660e\u4ea4\u4e0a\u4e86\u624b\u3002 \u3000\u3000\u609f\u660e\u6b66\u529f\u672c\u6765\u5e76\u4e0d\u751a\u9ad8\uff0c\u4ea4\u624b\u4e0d\u5230\u5341\u62db\uff0c\u4fbf\u88ab\u82d7\u7d22\u82d3\u70b9\u4e2d\u4e86\u7a74\u9053\u3002 \u3000\u3000\u9ad8\u5d99\u56e0\u6b66\u529f\u9ad8\u5f3a\uff0e\u867d\u975e\u9ea6\u5c0f\u660e\u5bf9\u624b\uff0c\u4f46\u9ea6\u5c0f\u660e\u56e0\u4ed6\u662f\u81ea\u5df1\u5e08\u5144\uff0c\u5374\u4e0d\u4fbf\u9042\u4e0b\u6bd2\u624b\uff0c\u800c\u77ed \u65f6\u95f4\u60f3\u628a\u4ed6\u751f\u64d2\u6d3b\u6349\uff0c\u53c8\u5e76\u975e\u6613\u4e8b\u3002 \u3000\u3000\u5c31\u5728\u4e8c\u4eba\u7f20\u6597\u95f4\uff0c\u949f\u4e00\u8c6a\u5df2\u968f\u540e\u8d76\u81f3\uff0c\u3000\u4e00\u5200\u7531\u9ad8\u5d99\u540e\u80cc\u523a\u5165\uff0e\u9ad8\u5d99\u5f53\u573a\u2014\u547d\u545c\u547c\u3002 \u3000\u3000\u82d7\u7d20\u82d3\u3001\u9ea6\u5c0f\u660e\u3001\u949f\u4e00\u8c6a\u62bc\u7740\u609f\u660e\u56de\u6765\uff0c\u4ea4\u4e0e\u4e86\u5929\u7985\u5927\u5e08\u3002 \u3000\u3000\u5929\u7985\u5927\u5e08\u9ad8\u55a7\u4e86\u4e00\u58f0\u4f5b\u53f7\u9053\uff1a\u201c\u5584\u6076\u5230\u5934\u7ec8\u6709\u62a5\uff0c\u8fd9\u5b7d\u755c\u603b\u7b97\u5f97\u5230\u4e86\u62a5\u5e94\u4e86\uff01\u201d \u3000\u3000\u63a5\u7740\u53c8\u56de\u8eab\u9053\uff1a\u201c\u80e1\u76df\u4e3b\u548c\u80e1\u592b\u4eba\u4ee5\u53ca\u5404\u4f4d\u65bd\u4e3b\u4e0d\u5fc5\u8fdc\u9001\u4e86\uff0c\u6b64\u7ffb\u627f\u8499\u4ed7\u4e49\u76f8\u52a9\uff0c\u5c11\u6797 \u4e00\u95e8\uff0c\u6c38\u94ed\u4e0d\u5fd8\u3002\u4eca\u540e\u5982\u6709\u7528\u5f97\u7740\u4e4b\u5904\uff0e\u53ea\u8981\u6d3e\u4eba\u5230\u5c11\u6797\u62db\u547c\u4e00\u58f0\uff0c\u8d74\u6c64\u8e48\u706b\uff0c\u8001\u8872\u5728\u6240\u4e0d \u8f9e\uff01\u201d \u3000\u3000\u5e9e\u58eb\u51b2\u4e5f\u56de\u8eab\u9053\uff1a\u201c\u8001\u592b\u4e5f\u8bf7\u8bf8\u4f4d\u4e0d\u5fc5\u8fdc\u9001\uff0e\u5c71\u5be8\u521d\u521d\u6062\u590d\uff0c\u767e\u4e8b\u5f85\u7406\uff0c\u8fd8\u662f\u56de\u53bb\u5fd9\u4f60 \u4eec\u7684\u8981\u7d27\u3002\u8fc7\u51e0\u5929\u8001\u592b\u4fbf\u628a\u7fce\u513f\u9001\u6765\uff0e\u8ba9\u4f60\u4eec\u7684\u7eff\u6797\u603b\u5be8\uff0c\u518d\u6dfb\u4e00\u4efd\u559c\u5e86\uff01\u201d"}),showOnHomepage:!0}]}}]);