"use strict";(self.webpackChunkwebsite=self.webpackChunkwebsite||[]).push([[4784],{70028:(e,n,t)=>{t.r(n),t.d(n,{default:()=>o});var a=t(74848);const o=[{name:"\u4e00\u3000\u4e61\u4e0b\u4eba\u8fdb\u57ce",content:(0,a.jsx)(a.Fragment,{children:"\u6258\uff01\u6258\u6258\u6258\uff01\u6258\uff01\u6258\u6258\uff01 \u3000\u3000\u4e24\u67c4\u6728\u5251\u6325\u821e\u53d8\u6597\uff0c\u76f8\u4e92\u649e\u51fb\uff0c\u53d1\u51fa\u6258\u6258\u4e4b\u58f0\u3002\u6709\u65f6\u76f8\u9694\u826f\u4e45\u800c\u65e0\u58f0\u606f\uff0c\u6709\u65f6\u649e\u51fb\u4e4b\u58f0\u5bc6\u5982\u8054\u73e0\uff0c\u8fde\u7ef5\u4e0d\u7edd\u3002 \u3000\u3000\u90a3\u662f\u5728\u6e58\u897f\u6c85\u9675\u5357\u90ca\u7684\u9ebb\u6eaa\u94fa\u4e61\u4e0b\uff0c\u4e09\u95f4\u5c0f\u5c0f\u74e6\u5c4b\u4e4b\u524d\uff0c\u6652\u8c37\u573a\u4e0a\uff0c\u4e00\u5bf9\u9752\u5e74\u7537\u5973\u624b\u6301\u6728\u5251\u3002\u6b63\u5728\u6bd4\u8bd5\u3002 \u3000\u3000\u5c4b\u524d\u77ee\u51f3\u4e0a\u5750\u7740\u4e00\u4e2a\u8001\u5934\u513f\uff0c\u5634\u91cc\u54ac\u7740\u4e00\u6839\u77ed\u77ed\u7684\u65f1\u70df\u888b\uff0c\u624b\u4e2d\u6b63\u5728\u6253\u8349\u978b\uff0c\u5076\u5c14\u62ac\u8d77\u5934\u6765\uff0c\u5411\u8fd9\u5bf9\u9752\u5e74\u7537\u5973\u77a7\u4e0a\u4e00\u773c\uff0c\u5634\u89d2\u8fb9\u5fae\u5fae\u542b\u7b11\uff0c\u610f\u793a\u5609\u8bb8\u3002\u6de1\u6de1\u9633\u5149\u7a7f\u8fc7\u4ed6\u53e3\u4e2d\u55b7\u51fa\u6765\u7684\u4e00\u7f15\u7f15\u9752\u70df\uff0c\u7167\u5728\u4ed6\u6ee1\u5934\u767d\u53d1\u3001\u6ee1\u8138\u76b1\u7eb9\u4e4b\u4e0a\uff0c\u4f46\u4ed6\u5411\u541e\u5410\u4f38\u7f29\u7684\u4e24\u67c4\u6728\u5251\u77a5\u4e0a\u4e00\u773c\u4e4b\u65f6\uff0c\u773c\u4e2d\u795e\u5149\u70af\u7136\uff0c\u51db\u51db\u6709\u5a01\uff0c\u770b\u6765\u4ed6\u5e74\u7eaa\u5176\u5b9e\u4e5f\u5e76\u4e0d\u5f88\u8001\uff0c\u4f3c\u4e4e\u4e94\u5341\u5c81\u4e5f\u8fd8\u4e0d\u5230\u3002 \u3000\u3000\u90a3\u5c11\u5973\u5341\u4e03\u516b\u5c81\u5e74\u7eaa\uff0c\u5706\u5706\u7684\u8138\u86cb\uff0c\u4e00\u53cc\u5927\u773c\u9ed1\u6e9c\u6e9c\u5730\uff0c\u8fd9\u65f6\u7d2f\u5f97\u989d\u5934\u89c1\u6c41\uff0c\u5de6\u988a\u4e0a\u4e00\u6761\u6c57\u6c34\u6d41\u4e86\u4e0b\u6765\uff0c\u76f4\u6d41\u5230\u9888\u4e2d\u3002 \u3000\u3000\u5979\u4f38\u5de6\u624b\u8863\u8896\u64e6\u4e86\u64e6\uff0c\u8138\u4e0a\u7ea2\u5f97\u50cf\u5c4b\u6a90\u4e0b\u6302\u7740\u7684\u4e00\u4e32\u4e32\u7684\u7ea2\u8fa3\u6912\u3002\u90a3\u9752\u5e74\u6bd4\u5979\u5927\u7740\u4e24\u4e09\u5c81\uff0c\u957f\u8eab\u9edd\u9ed1\uff0c\u98a7\u9aa8\u5fae\u9ad8\uff0c\u7c97\u624b\u5927\u811a\uff0c\u90a3\u662f\u6e58\u897f\u4e61\u4e0b\u5e38\u89c1\u7684\u5e84\u7a3c\u5c11\u5e74\u6c49\u5b50\uff0c\u624b\u4e2d\u4e00\u67c4\u6728\u5251\u5012\u4f7f\u5f97\u9887\u4e3a\u7075\u52a8\u3002 \u3000\u3000\u7a81\u7136\u95f4\u90a3\u9752\u5e74\u624b\u4e2d\u6728\u5251\u81ea\u5de6\u4e0a\u65b9\u659c\u5288\u5411\u4e0b\uff0c\u8ddf\u7740\u5411\u540e\u633a\u5251\u523a\u51fa\uff0c\u66f4\u4e0d\u56de\u5934\u3002\u90a3\u5c11\u5973\u4f4e\u5934\u907f\u8fc7\uff0c\u6728\u5251\u8fde\u523a\uff0c\u6765\u52bf\u52b2\u6025\u3002 \u3000\u3000\u90a3\u9752\u5e74\u9000\u4e86\u4e24\u6b65\uff0c\u6728\u5251\u5927\u5f00\u5927\u9616\uff0c\u4e00\u58f0\u5406\u559d\uff0c\u6a2a\u524a\u4e09\u5251\u3002\u90a3\u5c11\u5973\u62b5\u6321\u4e0d\u4f4f\uff0c\u7a81\u7136\u6536\u5251\u7ad9\u4f4f\uff0c\u7adf\u4e0d\u62db\u67b6\uff0c\u5a07\u55d4\u9053\uff1a\u201c\u7b97\u4f60\u5389\u5bb3\uff0c\u6210\u4e0d\u6210\uff1f\u628a\u6211\u780d\u6b7b\u4e86\u7f62\uff01\u201d \u3000\u3000\u90a3\u9752\u5e74\u6ca1\u6599\u5230\u5979\u7adf\u4f1a\u7a81\u7136\u6536\u5251\u4e0d\u67b6\u3002\u8fd9\u7b2c\u4e09\u5251\u773c\u89c1\u4fbf\u8981\u524a\u4e0a\u5979\u8170\u95f4\uff0c\u4e00\u60ca\u4e4b\u4e0b\uff0c\u6025\u5fd9\u6536\u62db\uff0c\u53ea\u662f\u53bb\u52bf\u592a\u5f3a\uff0c\u5657\u7684\u4e00\u58f0\uff0c\u5251\u8eab\u7adf\u6253\u4e2d\u4e86\u81ea\u5df1\u5de6\u624b\u624b\u80cc\uff0c\u201c\u554a\u54df\u201d\u4e00\u58f0\uff0c\u53eb\u4e86\u51fa\u6765\u3002 \u3000\u3000\u90a3\u5c11\u5973\u62cd\u624b\u53eb\u597d\uff0c\u7b11\u9053\uff1a\u201c\u7f9e\u4e5f\u4e0d\u7f9e\uff1f\u4f60\u624b\u4e2d\u62ff\u7684\u82e5\u662f\u771f\u5251\uff0c\u8fd9\u53ea\u624b\u8fd8\u5728\u5417\uff1f\u201d \u3000\u3000\u90a3\u9752\u5e74\u4e00\u5f20\u9ed1\u8138\u9ed1\u91cc\u6cdb\u7ea2\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u6015\u524a\u5230\u4f60\u8eab\u4e0a\uff0c\u8fd9\u624d\u4e0d\u5c0f\u5fc3\u78b0\u5230\u81ea\u5df1\u3002\u82e5\u662f\u771f\u7684\u62da\u6597\uff0c\u4eba\u5bb6\u80af\u8ba9\u4f60\u4e48\uff1f\u5e08\u7236\uff0c\u4f60\u5012\u8bc4\u8bc4\u8fd9\u4e2a\u7406\u770b\u3002\u201d\u8bf4\u5230\u6700\u540e\u8fd9\u53e5\u8bdd\u65f6\uff0c\u9762\u5411\u8001\u8005\u3002 \u3000\u3000\u90a3\u8001\u8005\u63d0\u7740\u534a\u622a\u8349\u978b\uff0c\u7ad9\u8d77\u8eab\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4e24\u4e2a\u5148\u524d\u4e94\u5341\u51e0\u62db\u62c6\u5f97\u8fd8\u53ef\u4ee5\uff0c\u540e\u9762\u8fd9\u51e0\u62db\uff0c\u53ef\u7b80\u76f4\u4e0d\u6210\u8bdd\u4e86\u3002\u201d\u4ece\u5c11\u5973\u624b\u4e2d\u63a5\u8fc7\u6728\u5251\uff0c\u6325\u5251\u4f5c\u659c\u5288\u52bf\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4e00\u62db\u2018\u54e5\u7fc1\u558a\u4e0a\u6765\u2019\uff0c\u8ddf\u7740\u4e00\u62db\u2018\u662f\u6a2a\u4e0d\u6562\u8fc7\u2019\uff0c\u90a3\u5c31\u5e94\u5f53\u6a2a\u524a\uff0c\u4e0d\u53ef\u76f4\u523a\u3002\u963f\u82b3\uff0c\u4f60\u8fd9\u4e24\u62db\u662f\u2018\u5ffd\u542c\u55b7\u60ca\u98ce\uff0c\u8fde\u5c71\u82e5\u5e03\u9003\u2019\uff0c\u5251\u52bf\u8be5\u50cf\u4e00\u758b\u5e03\u90a3\u6837\u9003\u4e86\u5f00\u53bb\u3002\u963f\u4e91\u8fd9\u4e24\u62db\u2018\u843d\u6ce5\u62db\u5927\u59d0\uff0c\u9a6c\u547d\u98ce\u5c0f\u5c0f\u2019\u5012\u4f7f\u5f97\u4e0d\u9519\u3002\u4e0d\u8fc7\u62db\u6cd5\u65e2\u7136\u53eb\u505a\u2018\u98ce\u5c0f\u5c0f\u2019\uff0c\u4f60\u51fa\u529b\u7684\u4f7f\u5251\uff0c\u90a3\u5c31\u4e0d\u5bf9\u4e86\u3002\u54b1\u4eec\u8fd9\u4e00\u5957\u5251\u6cd5\uff0c\u662f\u6b66\u6797\u4e2d\u5927\u5927\u6709\u540d\u7684\u2018\u8eba\u5c38\u5251\u6cd5\u2019\uff0c\u6bcf\u4e00\u62db\u51fa\u53bb\uff0c\u90fd\u8981\u654c\u4eba\u8eba\u4e0b\u6210\u4e3a\u4e00\u5177\u6b7b\u5c38\u3002\u81ea\u5df1\u4eba\u6bd4\u5212\u5582\u62db\u867d\u4e0d\u80fd\u8fd9\u4e48\u5f53\u771f\uff0c\u4f46\u2018\u8eba\u5c38\u2019\u4e8c\u5b57\uff0c\u603b\u662f\u8981\u65f6\u65f6\u523b\u523b\u8bb0\u5728\u5fc3\u91cc\u7684\u3002\u201d \u3000\u3000\u90a3\u5c11\u5973\u9053\uff1a\u201c\u7239\u3002\u54b1\u4eec\u7684\u5251\u6cd5\u5f88\u597d\uff0c\u53ef\u662f\u8fd9\u540d\u5b57\u5b9e\u5728\u4e0d\u5927\u2026\u2026\u4e0d\u5927\u597d\u542c\uff0c\u8eba\u5c38\u5251\u6cd5\uff0c\u542c\u7740\u5c31\u53eb\u4eba\u5bb3\u6015\u3002\u201d \u3000\u3000\u90a3\u8001\u8005\u9053\uff1a\u201c\u542c\u7740\u53eb\u4eba\u5bb3\u6015\uff0c\u90a3\u624d\u5a01\u98ce\u54ea\u3002\u654c\u4eba\u8fd8\u6ca1\u52a8\u624b\uff0c\u5148\u5c31\u5fc3\u60ca\u80c6\u6218\uff0c\u4fbf\u5df2\u8f93\u4e86\u4e09\u5206\u3002\u201d\u4ed6\u624b\u6301\u6728\u5251\uff0c\u5c06\u9002\u624d\u8fd9\u516d\u62db\u91cd\u65b0\u6f14\u4e86\u4e00\u904d\u3002\u53ea\u89c1\u4ed6\u5251\u62db\u51dd\u91cd\uff0c\u8f6c\u91cd\u8fdb\u9000\uff0c\u4ff1\u662f\u72e0\u8fa3\u5f02\u5e38\uff0c\u90a3\u4e00\u53cc\u9752\u5e74\u7537\u5973\u77a7\u5f97\u5fc3\u4e0b\u4f69\u670d\uff0c\u62cd\u8d77\u624b\u6765\u3002\u90a3\u8001\u8005\u5c06\u6728\u5251\u8fd8\u7ed9\u5c11\u5973\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4e24\u4e2a\u518d\u7ec3\u4e00\u904d\u3002\u963f\u82b3\u522b\u95f9\u7740\u73a9\uff0c\u521a\u624d\u5e08\u54e5\u82e5\u4e0d\u662f\u8ba9\u4f60\uff0c\u4f60\u5c0f\u547d\u513f\u8fd8\u5728\u4e48\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5973\u4f38\u4e86\u4f38\u820c\u5934\uff0c\u7a81\u7136\u95f4\u4e00\u5251\u523a\u51fa\uff0c\u8fc5\u6377\u4e4b\u6781\u3002\u90a3\u9752\u5e74\u4e0d\u53ca\u9632\u5907\uff0c\u6025\u5fd9\u56de\u5251\u62db\u67b6\uff0c\u4f46\u88ab\u90a3\u5c11\u5973\u5360\u4e86\u673a\u5148\uff0c\u8fde\u8fde\u62a2\u653b\uff0c\u90a3\u9752\u5e74\u4e00\u65f6\u4e4b\u95f4\u7adf\u6ca1\u6cd5\u6273\u56de\u3002\u773c\u89c1\u8d25\u5c40\u5df2\u6210\uff0c\u5ffd\u7136\u4e1c\u5317\u89d2\u4e0a\u9a6c\u8e44\u58f0\u54cd\uff0c\u4e00\u4e58\u9a6c\u5feb\u5954\u800c\u6765\u3002 \u3000\u3000\u90a3\u9752\u5e74\u56de\u5934\u9053\uff1a\u201c\u662f\u8c01\u6765\u5566\uff1f\u201d\u90a3\u5c11\u5973\u559d\u9053\uff1a\u201c\u6253\u8d25\u4e86\uff0c\u522b\u8d56\u76ae\uff01\u8c01\u6765\u4e86\u8ddf\u4f60\u6709\u751a\u76f8\u5e72\uff1f\u201d\u5237\u5237\u5237\u53c8\u662f\u8fde\u653b\u4e09\u5251\u3002\u90a3\u9752\u5e74\u594b\u529b\u62b5\u6321\uff0c\u6012\u9053\uff1a\u201c\u4f60\u9053\u6211\u6015\u4e86\u4f60\u4e0d\u6210\uff1f\u201d\u90a3\u5c11\u5973\u7b11\u9053\uff1a\u201c\u4f60\u5634\u4e0a\u4e0d\u6015\u5fc3\u91cc\u6015\u3002\u201d\u5de6\u523a\u4e00\u5251\uff0c\u53f3\u523a\u4e00\u5251\uff0c\u4e24\u62db\u53bb\u52bf\u6781\u662f\u7075\u52a8\u3002 \u3000\u3000\u5176\u65f6\u9a6c\u4e0a\u4e58\u5ba2\u5df2\u52d2\u4f4f\u4e86\u9a6c\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u2018\u5929\u82b1\u843d\u4e0d\u5c3d\uff0c\u5904\u5904\u9e1f\u8854\u98de\uff01\u2019\u5999\u554a\uff01\u201d \u3000\u3000\u90a3\u5c11\u5973\u201c\u54a6\u201d\u7684\u4e00\u58f0\uff0c\u5411\u540e\u8df3\u5f00\u3002\u5411\u90a3\u4e58\u5ba2\u6253\u91cf\uff0c\u53ea\u89c1\u4ed6\u7ea6\u83ab\u4e8c\u5341\u4e09\u56db\u5c81\u5e74\u7eaa\uff0c\u670d\u9970\u8003\u7a76\uff0c\u662f\u57ce\u91cc\u6709\u94b1\u4eba\u5bb6\u5b50\u5f1f\u7684\u6253\u626e\uff0c\u4e0d\u7981\u8138\u4e0a\u4e00\u7ea2\uff0c\u8f7b\u58f0\u9053\uff1a\u201c\u7239\uff0c\u4ed6\u2026\u2026\u4ed6\u600e\u4e48\u77e5\u9053\uff1f\u201d \u3000\u3000\u90a3\u8001\u8005\u542c\u5f97\u9a6c\u4e0a\u4e58\u5ba2\u8bf4\u51fa\u5973\u513f\u8fd9\u4e24\u62db\u5251\u6cd5\u7684\u540d\u79f0\uff0c\u5fc3\u4e0b\u4e5f\u611f\u8be7\u5f02\uff0c\u6b63\u5f85\u76f8\u8be2\u3002\u90a3\u4e58\u5ba2\u5df2\u6eda\u978d\u4e0b\u9a6c\uff0c\u4e0a\u524d\u62b1\u62f3\u8bf4\u9053\uff1a\u201c\u8bf7\u95ee\u8001\u4e08\uff0c\u9ebb\u6eaa\u94fa\u6709\u4e00\u4f4d\u5251\u672f\u540d\u5bb6\uff0c\u2018\u94c1\u9501\u6a2a\u6c5f\u2019\u621a\u957f\u53d1\u621a\u8001\u7237\u5b50\uff0c\u4ed6\u4f4f\u5728\u54ea\u91cc\uff1f\u201d\u90a3\u8001\u8005\u9053\uff1a\u201c\u6211\u4fbf\u662f\u621a\u957f\u53d1\u3002\u4ec0\u4e48\u2018\u5251\u672f\u540d\u5bb6\u2019\uff0c\u90a3\u53ef\u662f\u4e07\u4e07\u4e0d\u6562\u5f53\u4e86\u3002\u5927\u7237\u5bfb\u6211\u4f5c\u751a\uff1f\u201d \u3000\u3000\u90a3\u9752\u5e74\u58ee\u58eb\u62dc\u5012\u5728\u5730\uff0c\u8bf4\u9053\uff1a\u201c\u665a\u8f88\u535c\u57a3\uff0c\u8ddf\u621a\u5e08\u53d4\u78d5\u5934\u3002 \u3000\u3000\u665a\u8f88\u5949\u5bb6\u5e08\u4e4b\u547d\uff0c\u7279\u6765\u53e9\u89c1\u3002\u201d\u621a\u957f\u53d1\u9053\uff1a\u201c\u4e0d\u6562\u5f53\uff0c\u4e0d\u6562\u5f53\uff01\u201d \u3000\u3000\u4f38\u624b\u6276\u8d77\uff0c\u53cc\u81c2\u5fae\u8fd0\u5185\u52b2\u3002\u535c\u57a3\u53ea\u611f\u534a\u8eab\u9178\u9ebb\uff0c\u8138\u4e0a\u4e00\u7ea2\uff0c\u9053\uff1a\u201c\u621a\u5e08\u53d4\u8003\u8f83\u665a\u8f88\u8d77\u6765\u5566\uff0c\u4e00\u89c1\u9762\u4fbf\u53eb\u665a\u8f88\u51fa\u4e11\u3002\u201d \u3000\u3000\u621a\u957f\u53d1\u7b11\u9053\uff1a\u201c\u4f60\u5185\u529f\u8fd8\u5dee\u70b9\u513f\uff0c\u4f60\u662f\u4e07\u5e08\u54e5\u7684\u7b2c\u51e0\u5f1f\u5b50\uff1f\u201d\u535c\u57a3\u8138\u4e0a\u53c8\u662f\u4e00\u7ea2\uff0c\u9053\uff1a\u201c\u665a\u8f88\u662f\u5e08\u7236\u7b2c\u4e94\u4e2a\u4e0d\u6210\u6750\u7684\u5f1f\u5b50\u3002\u5e08\u7236\u4ed6\u8001\u4eba\u5bb6\u65e5\u5e38\u79f0\u9053\u621a\u5e08\u53d4\u5185\u529f\u6df1\u539a\uff0c\u600e\u4e48\u62ff\u665a\u8f88\u5582\u8d77\u62db\u6765\u5566\uff01\u201d\u621a\u957f\u53d1\u54c8\u54c8\u5927\u7b11\uff0c\u9053\uff1a\u201c\u4e07\u5e08\u54e5\u597d\uff1f\u6211\u4eec\u8001\u5144\u5f1f\u5341\u51e0\u5e74\u4e0d\u89c1\u5566\u3002\u201d\u535c\u57a3\u9053\uff1a\u201c\u6258\u4f60\u8001\u4eba\u5bb6\u798f\uff0c\u5e08\u7236\u5b89\u597d\u3002\u8fd9\u4e24\u4f4d\u5e08\u54e5\u5e08\u59ca\u3002\u662f\u4f60\u8001\u4eba\u5bb6\u9ad8\u8db3\u7f62\uff1f\u5251\u6cd5\u771f\u9ad8\uff01\u201d \u3000\u3000\u621a\u957f\u53d1\u62db\u62db\u624b\u3002\u9053\uff1a\u201c\u963f\u82b3\uff0c\u963f\u82b3\uff0c\u8fc7\u6765\u89c1\u8fc7\u535c\u5e08\u54e5\uff0c\u8fd9\u662f\u6211\u7684\u5149\u6746\u513f\u5f92\u5f1f\u72c4\u4e91\uff0c\u8fd9\u662f\u6211\u7684\u5149\u6746\u513f\u5973\u513f\u963f\u82b3\u3002\u563f\uff0c\u4e61\u4e0b\u59d1\u5a18\uff0c\u4fbf\u8fd9\u4e48\u4e0d\u5927\u65b9\uff0c\u90fd\u662f\u81ea\u5df1\u4e00\u5bb6\u4eba\uff0c\u6015\u4ec0\u4e48\u4e11\u4e86\uff1f\u201d \u3000\u3000\u621a\u82b3\u8eb2\u5728\u72c4\u4e91\u80cc\u540e\uff0c\u4e5f\u4e0d\u89c1\u793c\u3002\u53ea\u70b9\u5934\u7b11\u4e86\u7b11\u3002\u72c4\u4e91\u9053\uff1a\u201c\u535c\u5e08\u5144\uff0c\u4f60\u7ec3\u7684\u5251\u6cd5\u8ddf\u6211\u4eec\u7684\u90fd\u662f\u4e00\u8def\uff0c\u662f\u5417\uff1f\u4e0d\u7136\u600e\u4e48\u4e00\u89c1\u4fbf\u8ba4\u51fa\u4e86\u5e08\u59b9\u5251\u62db\u3002\u201d \u3000\u3000\u621a\u957f\u53d1\u201c\u5478\u201d\u7684\u4e00\u58f0\uff0c\u5728\u5730\u4e0b\u5410\u4e86\u53e3\u75f0\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u5e08\u7236\u8ddf\u4ed6\u5e08\u7236\u540c\u95e8\u5b66\u827a\uff0c\u5b66\u7684\u81ea\u7136\u662f\u4e00\u8def\u5251\u6cd5\u4e86\uff0c\u90a3\u8fd8\u7528\u95ee\uff1f\u201d \u3000\u3000\u535c\u57a3\u6253\u5f00\u9a6c\u978d\u65c1\u7684\u5e03\u56ca\uff0c\u53d6\u51fa\u4e00\u4e2a\u5305\u88b1\uff0c\u53cc\u624b\u5949\u4e0a\uff0c\u8bf4\u9053\uff1a\u201c\u621a\u5e08\u53d4\uff0c\u5e08\u7236\u8bf4\u4e00\u70b9\u513f\u8584\u793c\uff0c\u8bf7\u5e08\u53d4\u8d4f\u9762\u6536\u4e0b\u3002\u201d\u621a\u957f\u53d1\u8c22\u4e86\u4e00\u58f0\uff0c\u4fbf\u53eb\u5973\u513f\u6536\u4e86\u3002 \u3000\u3000\u621a\u82b3\u62ff\u5230\u623f\u4e2d\uff0c\u6253\u5f00\u5305\u88b1\uff0c\u89c1\u662f\u4e00\u4ef6\u9526\u7f0e\u9762\u7f8a\u76ae\u888d\u5b50\uff0c\u4e00\u53ea\u6c49\u7389\u8155\u956f\uff0c\u4e00\u9876\u6be1\u5e3d\uff0c\u4e00\u4ef6\u9ed1\u5462\u9a6c\u8902\u3002\u621a\u82b3\u6367\u4e86\u51fa\u6765\uff0c\u7b11\u563b\u563b\u7684\u53eb\u9053\uff1a\u201c\u7239\uff0c\u7239\uff0c\u4f60\u4ece\u6765\u6ca1\u7a7f\u8fc7\u8fd9\u4e48\u6f02\u4eae\u7684\u8863\u886b\uff0c\u7a7f\u4e86\u8d77\u6765\uff0c\u54ea\u8fd8\u50cf\u4e2a\u5e84\u7a3c\u4eba\uff1f\u8fd9\u53ef\u4e0d\u662f\u53d1\u4e86\u8d22\u3001\u505a\u4e86\u5b98\u4e48\uff1f\u201d \u3000\u3000\u621a\u957f\u53d1\u4e00\u770b\uff0c\u4e5f\u4e0d\u7981\u6014\u4f4f\u4e86\uff0c\u9694\u4e86\u597d\u4e00\u4f1a\u3002\u624d\u5ff8\u5ff8\u6029\u6029\u7684\u9053\uff1a\u201c\u4e07\u5e08\u54e5\u2026\u2026\u8fd9\u4e2a\u2026\u2026\u563f\u563f\uff0c\u771f\u662f\u7684\u2026\u2026\u201d \u3000\u3000\u72c4\u4e91\u5230\u524d\u6751\u53bb\u6253\u4e86\u4e09\u65a4\u767d\u9152\u3002\u621a\u82b3\u6740\u4e86\u4e00\u53ea\u80a5\u9e21\uff0c\u6458\u4e86\u56ed\u4e2d\u7684\u5927\u767d\u83dc\u548c\u7a7a\u5fc3\u83dc\uff0c\u6ee1\u6ee1\u716e\u4e86\u4e00\u5927\u76d8\uff0c\u53e6\u6709\u4e00\u5927\u7897\u7ea2\u8fa3\u6912\u6d78\u5728\u76d0\u6c34\u4e4b\u4e2d\u3002\u56db\u4eba\u56e2\u56e2\u4e00\u684c\uff0c\u5750\u7740\u5403\u996d\u3002 \u3000\u3000\u5e2d\u4e0a\u621a\u957f\u53d1\u95ee\u8d77\u6765\u610f\u3002\u535c\u57a3\u8bf4\u9053\uff1a\u201c\u5e08\u7236\u8bf4\u8ddf\u5e08\u53d4\u5341\u591a\u5e74\u4e0d\u89c1\uff0c\u597d\u751f\u8bb0\u6302\uff0c\u65e9\u5c31\u60f3\u5230\u6e56\u5357\u6765\u63a2\u8bbf\uff0c\u53ea\u662f\u5e08\u7236\u4ed6\u8001\u4eba\u5bb6\u6bcf\u65e5\u91cc\u8981\u7ec3\u2018\u8fde\u57ce\u5251\u6cd5\u2019\uff0c\u6ca1\u6cd5\u8d70\u52a8\u2026\u2026\u201d\u621a\u957f\u53d1\u6b63\u7aef\u8d77\u9152\u7897\u653e\u5728\u5507\u8fb9\uff0c\u5c06\u521a\u559d\u8fdb\u5634\u7684\u4e00\u53e3\u9152\u5410\u56de\u7897\u91cc\uff0c\u5fd9\u95ee\uff1a\u201c\u4ec0\u4e48\uff1f\u4f60\u5e08\u7236\u5728\u7ec3\u2018\u8fde\u57ce\u5251\u6cd5\u2019\uff1f\u201d\u535c\u57a3\u795e\u60c5\u5f88\u662f\u5f97\u610f\uff0c\u9053\uff1a\u201c\u4e0a\u4e2a\u6708\u521d\u4e94\uff0c\u5e08\u7236\u5df2\u628a\u2018\u8fde\u57ce\u5251\u6cd5\u2019\u7ec3\u6210\u4e86\u3002\u201d \u3000\u3000\u621a\u957f\u53d1\u66f4\u662f\u4e00\u60ca\uff0c\u5c06\u9152\u7897\u91cd\u91cd\u5f80\u684c\u4e0a\u4e00\u653e\uff0c\u5c0f\u534a\u7897\u9152\u90fd\u6cfc\u4e86\u51fa\u6765\uff0c\u6e85\u5f97\u684c\u4e0a\u548c\u80f8\u524d\u8863\u895f\u90fd\u662f\u9152\u6c34\u3002\u4ed6\u5446\u4e86\u4e00\u9635\uff0c\u7a81\u7136\u54c8\u54c8\u5927\u7b11\uff0c\u4f38\u624b\u5728\u535c\u57a3\u7684\u80a9\u5934\u91cd\u91cd\u4e00\u62cd\uff0c\u8bf4\u9053\uff1a\u201c\u4ed6\u5988\u7684\uff0c\u597d\u5c0f\u5b50\uff0c\u4f60\u5e08\u7236\u4ece\u5c0f\u5c31\u7231\u5439\u725b\u3002\u8fd9\u2018\u8fde\u57ce\u5251\u6cd5\u2019\u8fde\u4f60\u5e08\u7956\u90fd\u6ca1\u7ec3\u6210\uff0c\u4f60\u5e08\u7236\u7684\u73a9\u827a\u513f\u53c8\u4e0d\u89c1\u5f97\u5982\u4f55\u9ad8\u660e\uff0c\u522b\u6765\u9a97\u4f60\u5e08\u53d4\u5566\uff0c\u559d\u9152\uff0c\u559d\u9152\u2026\u2026\u201d\u8bf4\u7740\u4ef0\u8116\u5b50\u628a\u534a\u7897\u767d\u9152\u90fd\u559d\u5e72\u4e86\uff0c\u5de6\u624b\u6293\u4e86\u4e00\u53ea\u7ea2\u8fa3\u6912\uff0c\u5927\u56bc\u8d77\u6765\u3002 \u3000\u3000\u535c\u5766\u8138\u4e0a\u5374\u6ca1\u4e1d\u6beb\u7b11\u610f\uff0c\u8bf4\u9053\uff1a\u201c\u5e08\u7236\u77e5\u9053\u5e08\u53d4\u5b9a\u662f\u4e0d\u4fe1\uff0c\u4e0b\u6708\u5341\u516d\uff0c\u662f\u5e08\u7236\u4ed6\u8001\u4eba\u5bb6\u4e94\u5341\u5c81\u5bff\u8fb0\uff0c\u8bf7\u5e08\u53d4\u5e26\u540c\u5e08\u5f1f\u5e08\u59b9\uff0c\u540c\u53bb\u8346\u5dde\u559d\u676f\u6c34\u9152\uff0c\u5e08\u7236\u547d\u665a\u8f88\u4e13\u8bda\u524d\u6765\u76f8\u9080\uff0c\u65e0\u8bba\u5982\u4f55\u8981\u8bf7\u5e08\u53d4\u5149\u4e34\u3002\u5e08\u7236\u8bf4\u9053\uff0c\u4ed6\u7684\u2018\u8fde\u57ce\u5251\u6cd5\u2019\u53ea\u6015\u8fd8\u6709\u7ec3\u5f97\u4e0d\u5230\u4e4b\u5904\uff0c\u8981\u8ddf\u5e08\u53d4\u4e00\u8d77\u6765\u7422\u78e8\u7422\u78e8\uff0c\u5e08\u7236\u5e38\u8bf4\u5e08\u53d4\u5251\u6cd5\u4e86\u5f97\uff0c\u6211\u4eec\u5e08\u5144\u5f1f\u5982\u5f97\u5e08\u53d4\u6307\u70b9\u51e0\u62db\uff0c\u5927\u4f19\u513f\u4e00\u5b9a\u5927\u6709\u8fdb\u76ca\u3002\u201d \u3000\u3000\u621a\u957f\u53d1\u9053\uff1a\u201c\u4f60\u90a3\u4e8c\u5e08\u53d4\u8a00\u8fbe\u5e73\uff0c\u5df2\u53bb\u8bf7\u8fc7\u4e86\u4e48\uff1f\u201d\u535c\u57a3\u9053\uff1a\u201c\u8a00\u4e8c\u5e08\u53d4\u884c\u8e2a\u65e0\u5b9a\uff0c\u5e08\u7236\u66fe\u6d3e\u4e8c\u5e08\u54e5\u3001\u4e09\u5e08\u54e5\u3001\u56db\u5e08\u54e5\u4e09\u4f4d\uff0c\u5206\u522b\u5230\u6cb3\u6714\u3001\u6c5f\u5357\u3001\u4e91\u8d35\u4e09\u5904\u5bfb\u8bbf\uff0c\u90fd\u8bf4\u627e\u4e0d\u5230\u3002\u621a\u5e08\u53d4\u53ef\u66fe\u542c\u5230\u8a00\u4e8c\u5e08\u53d4\u7684\u8baf\u606f\u4e48\uff1f\u201d \u3000\u3000\u621a\u957f\u53d1\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u5e08\u5144\u5f1f\u4e09\u4eba\u4e4b\u4e2d\uff0c\u4e8c\u5e08\u54e5\u6b66\u529f\u6700\u5f3a\uff0c\u82e5\u8bf4\u662f\u4ed6\u7ec3\u6210\u4e86\u2018\u8fde\u57ce\u5251\u6cd5\u2019\uff0c\u6211\u5012\u8fd8\u6709\u4e09\u5206\u76f8\u4fe1\u3002 \u3000\u3000\u4f60\u5e08\u7236\u561b\uff0c\u563f\u563f\uff0c\u6211\u4e0d\u4fe1\uff0c\u6211\u4e0d\u4fe1\uff01\u201d \u3000\u3000\u4ed6\u5de6\u624b\u6293\u4f4f\u9152\u58f6\uff0c\u6ee1\u6ee1\u5012\u4e86\u4e00\u7897\u9152\uff0c\u53f3\u624b\u62ff\u7740\u9152\u7897\uff0c\u5374\u4e0d\u4fbf\u559d\uff0c\u5ffd\u7136\u5927\u58f0\u9053\uff1a\u201c\u597d\uff01\u4e0b\u6708\u5341\u516d\u3002\u6211\u51c6\u5230\u8346\u5dde\uff0c\u7ed9\u4f60\u5e08\u7236\u62dc\u5bff\uff0c\u5012\u8981\u77a7\u77a7\u4ed6\u7684\u2018\u8fde\u57ce\u5251\u6cd5\u2019\u662f\u600e\u4e48\u7ec3\u6210\u7684\u3002\u201d \u3000\u3000\u4ed6\u5c06\u9152\u7897\u91cd\u91cd\u5728\u684c\u4e0a\u4e00\u987f\uff0c\u53c8\u662f\u534a\u7897\u9152\u6cfc\u4e86\u51fa\u6765\uff0c\u6e85\u5f97\u684c\u4e0a\u3001\u8863\u895f\u4e0a\u90fd\u662f\u9152\u6c34\u3002 \u3000\u3000\u201c\u7239\u7239\uff0c\u4f60\u628a\u5927\u9ec4\u62ff\u53bb\u5356\u4e86\uff0c\u6765\u5e74\u54b1\u4eec\u8015\u7530\u600e\u4e48\u7b97\u554a\uff1f\u201d \u3000\u3000\u201c\u6765\u5e74\u5230\u6765\u5e74\u518d\u8bf4\uff0c\u54ea\u7ba1\u5f97\u8fd9\u8bb8\u591a\uff1f\u201d \u3000\u3000\u201c\u7239\u7239\uff0c\u54b1\u4eec\u5728\u8fd9\u513f\u4e0d\u662f\u597d\u597d\u7684\u4e48\uff1f\u5230\u8346\u5dde\u53bb\u5e72\u4ec0\u4e48\uff1f\u4ec0\u4e48\u4e07\u5e08\u4f2f\u505a\u751f\u65e5\uff0c\u5356\u4e86\u5927\u9ec4\u505a\u76d8\u7f20\uff0c\u6211\u8bf4\u72af\u4e0d\u7740\u3002\u201d \u3000\u3000\u201c\u7239\u7239\u7b54\u5e94\u4e86\u535c\u57a3\u7684\uff0c\u4e00\u5b9a\u5f97\u53bb\u3002\u5927\u4e08\u592b\u4e00\u8a00\u65e2\u51fa\uff0c\u600e\u80fd\u53cd\u6094\uff1f\u5e26\u4e86\u4f60\u548c\u963f\u4e91\u5230\u5927\u5730\u65b9\u89c1\u89c1\u4e16\u9762\uff0c\u522b\u4e00\u8f88\u5b50\u505a\u4e61\u4e0b\u4eba\u3002\u201d \u3000\u3000\u201c\u505a\u4e61\u4e0b\u4eba\u6709\u751a\u4e48\u4e0d\u597d\uff1f\u6211\u4e0d\u8981\u89c1\u751a\u4e48\u4e16\u9762\u3002\u5927\u9ec4\u662f\u6211\u4ece\u5c0f\u517b\u5927\u7684\u3002\u6211\u5e26\u7740\u5b83\u53bb\u5403\u8349\uff0c\u5e26\u7740\u5b83\u56de\u5bb6\u3002\u7239\u7239\uff0c\u4f60\u77a7\u77a7\u5927\u9ec4\u5728\u6d41\u773c\u6cea\uff0c\u5b83\u4e0d\u80af\u53bb\u3002\u201d \u3000\u3000\u201c\u50bb\u59d1\u5a18\uff01\u725b\u662f\u755c\u751f\uff0c\u77e5\u9053\u4ec0\u4e48\uff1f\u5feb\u653e\u5f00\u624b\u3002\u201d \u3000\u3000\u201c\u6211\u4e0d\u653e\u624b\u3002\u4eba\u5bb6\u4e70\u4e86\u5927\u9ec4\u53bb\uff0c\u8981\u5bb0\u6765\u5403\u4e86\uff0c\u6211\u4e0d\u820d\u5f97\u3002\u201d \u3000\u3000\u201c\u4e0d\u4f1a\u5bb0\u7684\uff0c\u4eba\u5bb6\u4e70\u4e86\u53bb\u8015\u7530\u3002\u201d \u3000\u3000\u201c\u6628\u5929\u738b\u5c60\u6237\u6765\u8ddf\u4f60\u8bf4\u4ec0\u4e48\uff1f\u4e00\u5b9a\u662f\u4e70\u5927\u9ec4\u53bb\u6740\u4e86\u3002\u4f60\u9a97\u6211\uff0c\u4f60\u9a97\u6211\u3002\u4f60\u77a7\uff0c\u5927\u9ec4\u5728\u6d41\u773c\u6cea\u3002\u5927\u9ec4\uff0c\u5927\u9ec4\uff0c\u6211\u4e0d\u653e\u4f60\u53bb\u3002\u4e91\u54e5\uff0c\u4e91\u54e5\uff01\u5feb\u6765\uff0c\u7239\u7239\u8981\u5356\u4e86\u5927\u9ec4\u2026\u2026\u201d \u3000\u3000\u201c\u963f\u82b3\uff01\u7239\u7239\u4e5f\u820d\u4e0d\u5f97\u5927\u9ec4\u3002\u53ef\u662f\u54b1\u4eec\u7a7a\u624b\u4e0a\u4eba\u5bb6\u53bb\u62dc\u5bff\uff0c\u90a3\u6210\u4e48\uff1f\u54b1\u4eec\u4e09\u4e2a\u6ee1\u8eab\u7834\u7834\u70c2\u70c2\u7684\uff0c\u603b\u5f97\u7f1d\u4e09\u5957\u65b0\u8863\uff0c\u514d\u5f97\u8ba9\u4eba\u5bb6\u770b\u8f7b\u4e86\u3002\u201d \u3000\u3000\u201c\u4e07\u5e08\u4f2f\u4e0d\u662f\u9001\u4e86\u4f60\u65b0\u8863\u65b0\u5e3d\u4e48\uff1f\u7a7f\u8d77\u6765\u633a\u795e\u6c14\u7684\u3002\u201d \u3000\u3000\u201c\u5509\uff0c\u5929\u6c14\u8fd9\u4e48\u70ed\uff0c\u8001\u7f8a\u76ae\u888d\u5b50\u600e\u4e48\u80cc\u5f97\u4e0a\u8eab\uff1f\u518d\u8bf4\uff0c\u4f60\u5e08\u4f2f\u5938\u53e3\u8bf4\u7ec3\u6210\u4e86\u2018\u8fde\u57ce\u5251\u6cd5\u2019\uff0c\u6211\u5c31\u662f\u4e0d\u4fe1\uff0c\u975e\u5f97\u4eb2\u773c\u53bb\u77a7\u77a7\u4e0d\u53ef\u3002\u4e56\u5b69\u5b50\uff0c\u5feb\u653e\u5f00\u4e86\u624b\u3002\u201d \u3000\u3000\u201c\u5927\u9ec4\uff0c\u4eba\u5bb6\u8981\u5bb0\u4f60\uff0c\u4f60\u5c31\u7528\u89d2\u649e\u4ed6\uff0c\u81ea\u5df1\u9003\u56de\u6765\uff0c\u4e0d\uff01 \u3000\u3000\u4eba\u5bb6\u4f1a\u8ffd\u6765\u7684\uff0c\u4f60\u9003\u5f97\u8fdc\u8fdc\u7684\uff0c\u9003\u5230\u5c71\u91cc\u2026\u2026\u201d \u3000\u3000\u534a\u4e2a\u6708\u4e4b\u540e\u3002\u621a\u957f\u53d1\u5e26\u540c\u5f92\u513f\u72c4\u4e91\u3001\u5973\u513f\u621a\u82b3\uff0c\u6765\u5230\u4e86\u8346\u5dde\u3002\u4e09\u4eba\u51c6\u7a7f\u4e86\u65b0\u8863\uff0c\u521d\u6765\u5927\u57ce\uff0c\u571f\u5934\u571f\u8111\uff0c\u90fd\u6709\u70b9\u513f\u5fc3\u865a\u80c6\u602f\uff0c\u624b\u8db3\u65e0\u63aa\u3002\u6253\u542c\u201c\u4e94\u4e91\u624b\u201d\u4e07\u9707\u5c71\u7684\u4f4f\u5904\u3002\u9014\u4eba\u8bf4\u9053\uff1a\u201c\u4e07\u8001\u82f1\u96c4\u7684\u5bb6\u8fd8\u7528\u95ee\uff1f\u90a3\u8fb9\u6700\u5927\u7684\u5c4b\u5b50\u4fbf\u662f\u4e86\u3002\u201d \u3000\u3000\u72c4\u4e91\u548c\u621a\u82b3\u4e00\u8d70\u5230\u4e07\u5bb6\u5927\u5b85\u4e4b\u524d\uff0c\u77a7\u89c1\u90a3\u9ad8\u5899\u6731\u95e8\u3001\u6302\u706f\u7ed3\u5f69\u7684\u6c14\u6d3e\uff0c\u5fc3\u4e2d\u90e8\u662f\u6697\u81ea\u5600\u5495\u3002\u621a\u82b3\u7d27\u7d27\u62c9\u4f4f\u4e86\u7236\u4eb2\u7684\u8863\u8896\u3002\u621a\u957f\u53d1\u6b63\u5f85\u5411\u95e8\u516c\u8be2\u95ee\uff0c\u5ffd\u89c1\u535c\u57a3\u4ece\u95e8\u91cc\u51fa\u6765\uff0c\u5fc3\u4e2d\u4e00\u559c\uff0c\u53eb\u9053\uff1a\u201c\u535c\u8d24\u4f84\uff0c\u6211\u6765\u5566\u3002\u201d \u3000\u3000\u535c\u57a3\u5fd9\u8fce\u5c06\u51fa\u6765\uff0c\u559c\u9053\uff1a\u201c\u621a\u5e08\u53d4\u5230\u4e86\u3002\u72c4\u5e08\u5f1f\u597d\uff0c\u5e08\u59b9\u597d\u3002\u5e08\u7236\u6b63\u7275\u8bb0\u7740\u5e08\u53d4\u5462\u3002\u8fd9\u51e0\u5929\u8001\u662f\u8bf4\uff1a\u2018\u621a\u5e08\u5f1f\u600e\u4e48\u8fd8\u4e0d\u5230\uff1f\u2019\u8bf7\u7f62\uff01\u201d \u3000\u3000\u621a\u957f\u53d1\u7b49\u4e09\u4eba\u8d70\u8fdb\u5927\u95e8\uff0c\u9f13\u4e50\u624b\u5439\u8d77\u8fce\u5bbe\u7684\u4e50\u66f2\u3002\u5522\u5450\u7a81\u54cd\uff0c\u72c4\u4e91\u5403\u4e86\u4e00\u60ca\u3002 \u3000\u3000\u5927\u5385\u4e0a\u4e00\u4e2a\u8eab\u5f62\u9b41\u68a7\u7684\u8001\u8005\u6b63\u5728\u548c\u4f17\u5bbe\u5ba2\u5468\u65cb\u3002\u621a\u957f\u53d1\u53eb\u9053\uff1a\u201c\u5927\u5e08\u54e5\uff0c\u6211\u6765\u5566\uff01\u201d\u90a3\u8001\u8005\u4e00\u6014\uff0c\u4f3c\u4e4e\u8ba4\u4e0d\u51fa\u4ed6\uff0c\u5446\u4e86\u4e00\u5446\uff0c\u8fd9\u624d\u6ee1\u8138\u7b11\u5bb9\u7684\u62a2\u5c06\u51fa\u6765\uff0c\u5475\u5475\u7b11\u9053\uff1a\u201c\u8001\u4e09\uff0c\u4f60\u53ef\u8001\u5f97\u5f88\u4e86\uff0c\u6211\u51e0\u4e4e\u4e0d\u8ba4\u5f97\u4f60\u5566\uff01\u201d \u3000\u3000\u5e08\u5144\u5f1f\u6b63\u8981\u62c9\u624b\u53d9\u65e7\uff0c\u5ffd\u7136\u9f3b\u4e2d\u95fb\u5230\u4e00\u80a1\u5947\u81ed\uff0c\u63a5\u7740\u542c\u5f97\u4e00\u4e2a\u7834\u9523\u4f3c\u7684\u58f0\u97f3\u559d\u9053\uff1a\u201c\u4e07\u9707\u5c71\uff0c\u4f60\u5341\u5e74\u524d\u6b20\u4e86\u6211\u4e00\u6587\u94b1\uff0c\u4eca\u65e5\u8be5\u8fd8\u4e86\u7f62\uff1f\u201d\u621a\u957f\u53d1\u4e00\u8f6c\u5934\uff0c\u53ea\u89c1\u5385\u53e3\u4e00\u4eba\u63d0\u8d77\u4e00\u53ea\u6728\u6876\uff0c\u53cc\u624b\u4e00\u626c\uff0c\u6ee1\u6876\u7caa\u6c34\uff0c\u75be\u5411\u4ed6\u548c\u4e07\u9707\u5c71\u4e8c\u4eba\u6cfc\u5c06\u8fc7\u6765\u3002 \u3000\u3000\u621a\u957f\u53d1\u773c\u89c1\u5973\u513f\u548c\u5f92\u5f1f\u7ad9\u5728\u8eab\u540e\uff0c\u81ea\u5df1\u82e5\u662f\u4fa7\u8eab\u95ea\u907f\uff0c\u8fd9\u4e00\u6876\u7caa\u6c34\u52bf\u987b\u515c\u5934\u6cfc\u5728\u5973\u513f\u8eab\u4e0a\uff0c\u4ed6\u5e94\u53d8\u5947\u901f\uff0c\u53cc\u624b\u6293\u4f4f\u957f\u888d\uff0c\u8fd0\u52a8\u4e00\u5d29\uff0c\u62cd\u62cd\u62cd\u62cd\u4e00\u9635\u8fc5\u901f\u8f7b\u54cd\uff0c\u6263\u5b50\u5d29\u65ad\uff0c\u5de6\u624b\u6293\u4f4f\u8863\u895f\u5411\u5916\u4e00\u5d29\uff0c\u957f\u888d\u5df2\u7136\u79bb\u8eab\uff0c\u5185\u52b2\u8d2f\u5904\uff0c\u4e00\u4ef6\u957f\u888d\u4fbf\u5982\u8239\u5e06\u9f13\u98ce\uff0c\u5c06\u6cfc\u6765\u7684\u7caa\u6c34\u5c3d\u884c\u515c\u5728\u5176\u4e2d\u3002\u4ed6\u987a\u624b\u4e00\u9001\uff0c\u515c\u6ee1\u7caa\u6c34\u7684\u957f\u888d\u5411\u6765\u4eba\u75be\u98de\u8fc7\u53bb\u3002 \u3000\u3000\u90a3\u4eba\u63b7\u51fa\u7caa\u6876\uff0c\u4fbf\u5373\u8dc3\u5728\u4e00\u65c1\u3002\u7830\u84ec\uff0c\u62cd\u5566\uff0c\u7caa\u6876\u548c\u957f\u888d\u5148\u540e\u7740\u5730\uff0c\u6ee1\u5385\u81ed\u6c14\u5f25\u6f2b\u3002 \u3000\u3000\u53ea\u89c1\u90a3\u4eba\u6ee1\u816e\u866c\u9aef\uff0c\u8eab\u5f62\u9b41\u68a7\uff0c\u5a01\u98ce\u51db\u51db\u7684\u7ad9\u5728\u5f53\u5730\uff0c\u54c8\u54c8\u5927\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4e07\u9707\u5c71\uff0c\u5144\u5f1f\u5343\u91cc\u8fe2\u8fe2\u7684\u6765\u7ed9\u4f60\u62dc\u5bff\uff0c\u5c11\u4e86\u793c\u7269\uff0c\u9001\u4e0a\u9ec4\u91d1\u4e07\u4e24\uff0c\u606d\u559c\u4f60\u91d1\u7389\u6ee1\u5802\u554a\uff01\u201d \u3000\u3000\u4e07\u9707\u5c71\u7684\u516b\u540d\u5f1f\u5b50\u89c1\u6b64\u4eba\u5982\u6b64\u524d\u6765\u6363\u4e71\uff0c\u5c06\u4e00\u5ea7\u706f\u70db\u8f89\u714c\u7684\u5bff\u5802\u5f04\u5f97\u6c61\u79fd\u4e0d\u582a\uff0c\u65e0\u4e0d\u5927\u6012\u3002\u516b\u4e2a\u4eba\u4e00\u62e5\u800c\u4e0a\uff0c\u8981\u63ea\u4f4f\u4ed6\u6253\u4e2a\u534a\u6b7b\u3002 \u3000\u3000\u4e07\u9707\u5c71\u559d\u9053\uff1a\u201c\u90fd\u7ed9\u6211\u7ad9\u4f4f\u4e86\u3002\u201d\u516b\u540d\u5f1f\u5b50\u5f53\u5373\u505c\u6b65\u3002\u4e8c\u5f1f\u5b50\u5468\u573b\u5411\u90a3\u5927\u6c49\u7834\u53e3\u5927\u9a82\uff1a\u201c\u64cd\u4f60\u5976\u5976\u7684\u96c4\uff0c\u4f60\u662f\u751a\u4e48\u4e1c\u897f\uff1f \u3000\u3000\u4eca\u5929\u662f\u4e07\u8001\u7237\u7684\u597d\u65e5\u5b50\uff0c\u5374\u6765\u6405\u5c40\uff0c\u4e0d\u63cd\u4f60\u4e2a\u597d\u7684\uff0c\u4f60\u8fd9\u738b\u516b\u7f94\u5b50\uff0c\u4e5f\u4e0d\u77e5\u9053\u4e94\u4e91\u624b\u4e07\u5bb6\u7684\u5389\u5bb3\u3002\u201d \u3000\u3000\u4e07\u9707\u5c71\u5df2\u8ba4\u51fa\u8fd9\u866c\u9aef\u6c49\u5b50\u7684\u6765\u5386\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u9053\u662f\u8c01\uff0c\u539f\u6765\u662f\u592a\u884c\u5c71\u5415\u5927\u5be8\u4e3b\u5230\u4e86\u3002\u5415\u5927\u5be8\u4e3b\u8fd9\u51e0\u5e74\u53d1\u4e86\u5927\u8d22\u54ea\uff0c\u5bb6\u91cc\u5806\u6ee1\u4e86\u9ec4\u91d1\u4e07\u4e24\u4f7f\u4e0d\u5b8c\uff0c\u968f\u8eab\u8fd8\u5e26\u7740\u8fd9\u8bb8\u591a\u3002\u201d \u3000\u3000\u4f17\u5bbe\u5ba2\u542c\u5230\u201c\u592a\u884c\u5c71\u5415\u5927\u5be8\u4e3b\u201d\u8fd9\u4e03\u4e2a\u5b57\uff0c\u8bb8\u591a\u4eba\u7eb7\u7eb7\u4ea4\u5934\u63a5\u8033\u7684\u8bae\u8bba\uff1a\u201c\u539f\u6765\u662f\u592a\u884c\u5c71\u7684\u5415\u901a\uff0c\u4e0d\u77e5\u4ed6\u5982\u4f55\u8ddf\u4e07\u8001\u7237\u5b50\u7ed3\u4e0b\u4e86\u6881\u5b50\u3002\u201d\u201c\u8fd9\u5415\u901a\u662f\u5317\u4e94\u7701\u4e2d\u9ed1\u9053\u4e0a\u6781\u5389\u5bb3\u7684\u4eba\u7269\uff0c\u4e00\u624b\u516d\u5408\u5200\u516d\u5408\u62f3\uff0c\u9ec4\u6cb3\u5357\u5317\u53ef\u662f\u5927\u5927\u7684\u6709\u540d\u3002\u201d\u201c\u5584\u8005\u4e0d\u6765\uff0c\u6765\u8005\u4e0d\u5584\uff01\u4eca\u65e5\u6709\u4e00\u756a\u70ed\u95f9\u77a7\u7684\u4e86\u3002\u201d \u3000\u3000\u5415\u901a\u51b7\u7b11\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u5341\u5e74\u4e4b\u524d\uff0c\u6211\u5144\u5f1f\u5728\u592a\u539f\u5e9c\u505a\u6848\uff0c\u6697\u4e2d\u6709\u4eba\u901a\u98ce\u62a5\u4fe1\uff0c\u574f\u4e86\u6211\u4eec\u7684\u4e70\u5356\u3002\u90a3\u4e5f\u4e0d\u6253\u7d27\uff0c\u5374\u7d2f\u5f97\u6211\u5144\u5f1f\u5415\u5a01\u574f\u5728\u9e70\u722a\u5b50\u624b\u91cc\uff0c\u6b7b\u4e8e\u975e\u547d\u3002\u76f4\u5230\u4e09\u5e74\u4e4b\u524d\uff0c\u624d\u67e5\u5230\u539f\u6765\u662f\u4f60\u4e07\u9707\u5c71\u8fd9\u72d7\u8d3c\u5e72\u7684\u597d\u4e8b\u3002\u8fd9\u4ef6\u4e8b\u4f60\u8bf4\u600e\u4e48\u4e86\u7ed3\uff1f\u201d \u3000\u3000\u4e07\u9707\u5c71\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u90a3\u662f\u6211\u59d3\u4e07\u7684\u901a\u98ce\u62a5\u8baf\uff0c\u5728\u6c5f\u6e56\u4e0a\u5403\u996d\uff0c\u505a\u6ca1\u672c\u94b1\u4e70\u5356\uff0c\u90a3\u4e5f\u6ca1\u751a\u4e48\uff0c\u53ef\u662f\u4f60\u5144\u5f1f\u5415\u5a01\u5f3a\u5978\u4eba\u5bb6\u9ec4\u82b1\u95fa\u5973\uff0c\u8fde\u574f\u56db\u6761\u4eba\u547d\u3002\u8fd9\u7b49\u4f24\u5929\u5bb3\u7406\u4e4b\u4e8b\uff0c\u6211\u59d3\u4e07\u7684\u9047\u4e0a\u4e86\u53ef\u4e0d\u80fd\u4e0d\u7ba1\u3002\u201d \u3000\u3000\u4f17\u4eba\u4e00\u542c\uff0c\u90fd\u5927\u58f0\u53eb\u56b7\u8d77\u6765\uff1a\u201c\u8fd9\u79cd\u6076\u4e8b\u4e5f\u5e72\uff0c\u4e0d\u77e5\u7f9e\u803b\uff01\u201d \u3000\u3000\u201c\u8d3c\u5f3a\u76d7\uff0c\u7ed1\u4e86\u4ed6\u8d77\u6765\u9001\u5b98\u3002\u201d\u201c\u91c7\u82b1\u5927\u76d7\uff0c\u7adf\u6562\u5230\u6c5f\u9675\u5e9c\u6765\u6492\u91ce\uff01\u201d \u3000\u3000\u5415\u901a\u7a81\u7136\u4e00\u4e2a\u7bad\u6b65\uff0c\u4ece\u5ead\u9662\u4e2d\u7a9c\u5230\u5385\u524d\uff0c\u6a2a\u8fc7\u624b\u81c2\uff0c\u4fbf\u5411\u6979\u67f1\u4e0a\u51fb\u4e86\u8fc7\u53bb\u3002\u8fde\u51fb\u6570\u4e0b\uff0c\u53ea\u542c\u5f97\u5580\u5587\u5587\u4e00\u54cd\uff0c\u4e00\u6761\u7897\u53e3\u7c97\u7ec6\u7684\u6979\u67f1\u767b\u65f6\u65ad\u4e3a\u4e24\u622a\uff0c\u5c4b\u74e6\u7eb7\u7eb7\u5815\u4e0b\uff0c\u9662\u4e2d\u5385\u524d\uff0c\u4e00\u7247\u70df\u5c18\u5f25\u6f2b\u3002\u8bb8\u591a\u4eba\u9003\u51fa\u4e86\u5385\u5916\u3002\u4f17\u4eba\u89c1\u4ed6\u9732\u4e86\u8fd9\u624b\u94c1\u81c2\u529f\uff0c\u65e0\u4e0d\u51db\u7136\uff0c\u5747\u60f3\uff1a\u201c\u82e5\u662f\u8eab\u4e0a\u7ed9\u4ed6\u624b\u81c2\u8fd9\u4e48\u6a2a\u626b\u4e00\u8bb0\uff0c\u54ea\u91cc\u8fd8\u6709\u547d\u5728\uff1f\u201d \u3000\u3000\u5415\u901a\u53cd\u8eab\u8dc3\u56de\u5ead\u9662\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u4e07\u9707\u5c71\uff0c\u4f60\u5f53\u771f\u662f\u4fa0\u4e49\u9053\uff0c\u5c31\u8be5\u660e\u5200\u660e\u67aa\u7684\u51fa\u6765\u6253\u62b1\u4e0d\u5e73\uff0c\u6211\u5012\u670d\u4f60\u662f\u6761\u597d\u6c49\u3002\u4e3a\u4ec0\u4e48\u5077\u5077\u7684\u53bb\u5411\u5b98\u5e9c\u901a\u98ce\uff1f\u53c8\u4e3a\u4ec0\u4e48\u541e\u6ca1\u4e86\u6211\u5144\u5f1f\u5df2\u7ecf\u5230\u624b\u4e86\u7684\u516d\u5343\u4e24\u94f6\u5b50\uff1f\u4ed6\u5988\u7684\uff0c\u4f60\u5351\u9119\u65e0\u803b\uff01\u6709\u79cd\u7684\u5c31\u6765\u62da\u4e2a\u6b7b\u6d3b\uff01\u201d \u3000\u3000\u4e07\u9707\u5c71\u51b7\u7b11\u9053\uff1a\u201c\u5415\u5927\u5be8\u4e3b\uff0c\u5341\u5e74\u4e0d\u89c1\uff0c\u4f60\u529f\u592b\u679c\u7136\u5927\u5927\u957f\u8fdb\u4e86\u3002\u53ea\u53ef\u60dc\u4f3c\u4f60\u8fd9\u7b49\u4eba\u7269\uff0c\u6b66\u529f\u8d8a\u5f3a\uff0c\u5bb3\u4eba\u8d8a\u591a\u3002\u59d3\u4e07\u7684\u5e74\u7eaa\u867d\u8001\uff0c\u53ea\u5f97\u6765\u9886\u6559\u9886\u6559\u3002\u201d\u8bf4\u7740\u7f13\u6b65\u800c\u51fa\u3002 \u3000\u3000\u5ffd\u7136\u95f4\u4eba\u4e1b\u4e2d\u7a9c\u51fa\u4e00\u4e2a\u7c97\u7709\u5927\u773c\u7684\u5c11\u5e74\uff0c\u6084\u6ca1\u58f0\u7684\u6b3a\u8fd1\u8eab\u53bb\uff0c\u53cc\u81c2\u4e00\u7ffb\uff0c\u5df2\u52fe\u4f4f\u5415\u901a\u7684\u4e24\u6761\u624b\u81c2\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u4f60\u5f04\u810f\u4e86\u6211\u5e08\u7236\u7684\u65b0\u8863\u670d\uff0c\u5feb\u5feb\u8d54\u6765\uff01\u201d\u6b63\u662f\u621a\u957f\u53d1\u7684\u5f1f\u5b50\u72c4\u4e91\u3002 \u3000\u3000\u5415\u901a\u53cc\u81c2\u4e00\u9707\uff0c\u8981\u5c06\u8fd9\u5c11\u5e74\u9707\u5f00\uff0c\u4e0d\u6599\u624b\u81c2\u7ed9\u72c4\u4e91\u6b7b\u547d\u52fe\u4f4f\u4e86\uff0c\u65e0\u6cd5\u6323\u8131\u3002\u5415\u901a\u8fd9\u94c1\u81c2\u529f\u987b\u5f97\u6a2a\u626b\u76f4\u51fb\uff0c\u65b9\u80fd\u53d1\u6325\u5a01\u529b\uff0c\u51b7\u4e0d\u9632\u88ab\u4ed6\u52fe\u4f4f\u4e86\uff0c\u81c2\u4e0a\u52b2\u529b\u4f7f\u4e0d\u51fa\u6765\u3002\u4ed6\u5927\u6012\u4e4b\u4e0b\uff0c\u53f3\u819d\u4e00\u4e3e\uff0c\u649e\u5728\u72c4\u4e91\u7684\u5c0f\u8179\u4e4b\u4e0a\uff0c\u559d\u9053\uff1a\u201c\u5feb\u653e\u624b\uff01\u201d\u72c4\u4e91\u5403\u75db\uff0c\u81c2\u529b\u4e00\u677e\u3002\u5415\u901a\u4e00\u62db\u201c\u98ce\u4e91\u4e4d\u8d77\u201d\uff0c\u6323\u8131\u4e86\u4ed6\u53cc\u81c2\uff0c\u547c\u7684\u4e00\u62f3\u51fb\u51fa\uff0c\u6b63\u662f\u201c\u516d\u5408\u62f3\u201d\u4e2d\u7684\u4e00\u62db\uff0c\u201c\u4e4c\u9f99\u63a2\u6d77\u201d\u3002 \u3000\u3000\u72c4\u4e91\u6025\u7a9c\u8ba9\u5f00\uff0c\u53eb\u9053\uff1a\u201c\u6211\u4e0d\u8ddf\u4f60\u6253\u67b6\u3002\u6211\u5e08\u7236\u8fd9\u4ef6\u65b0\u888d\u5b50\uff0c\u82b1\u4e86\u4e09\u4e24\u94f6\u5b50\u7f1d\u7684\uff0c\u54b1\u4eec\u5356\u4e86\u5927\u726f\u725b\u5927\u9ec4\uff0c\u624d\u7f1d\u4e86\u4e09\u5957\u8863\u670d\uff0c\u4eca\u513f\u7b2c\u4e00\u6b21\u4e0a\u8eab\u2026\u2026\u201d\u5415\u901a\u6012\u9053\uff1a\u201c\u695e\u5c0f\u5b50\uff0c\u80e1\u8bf4\u516b\u9053\u751a\u4e48\uff1f\u201d\u72c4\u4e91\u51b2\u4e0a\u4e09\u6b65\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u5feb\u8d54\u6765\uff01\u201d\u4ed6\u662f\u519c\u5bb6\u5b50\u5f1f\uff0c\u6700\u662f\u7231\u60dc\u7269\u529b\uff0c\u773c\u89c1\u5e08\u7236\u5356\u53bb\u5fc3\u7231\u7684\u592a\u726f\u725b\u7f1d\u4e86\u4e09\u5957\u65b0\u8863\uff0c\u7b2c\u4e00\u6b21\u7a7f\u51fa\u6765\u4fbf\u8ba9\u4eba\u7ed9\u69fd\u8e4b\u4e86\uff0c\u6559\u4ed6\u5982\u4f55\u4e0d\u6df1\u611f\u75db\u60dc\uff1f\u4ed6\u4e5f\u4e0d\u7406\u5415\u901a\u8ddf\u4e07\u9707\u5c71\u4e4b\u95f4\u6709\u4ec0\u4e48\u6c5f\u6e56\u8fc7\u8282\uff0c\u5e08\u7236\u8fd9\u4ef6\u888d\u5b50\u603b\u4e4b\u662f\u975e\u8d54\u4e0d\u53ef\u3002 \u3000\u3000\u4e07\u9707\u5c71\u9053\uff1a\u201c\u72c4\u8d24\u4f84\u9000\u4e0b\uff0c\u4f60\u5e08\u7236\u7684\u888d\u5b50\u7531\u6211\u6765\u8d54\u4fbf\u662f\u3002\u201d \u3000\u3000\u72c4\u4e91\u9053\uff1a\u201c\u8981\u4ed6\u8d54\uff0c\u4ed6\u8981\u662f\u8d70\u4e86\uff0c\u4f60\u53c8\u4e0d\u8ba4\u5e10\uff0c\u90a3\u4fbf\u7cdf\u4e86\uff0c\u201d\u8bf4\u7740\u53c8\u53bb\u626d\u5415\u901a\u7684\u8863\u895f\u3002\u5415\u901a\u4e00\u95ea\uff0c\u7830\u7684\u4e00\u62f3\uff0c\u51fb\u5728\u72c4\u4e91\u80f8\u53e3\uff0c\u53ea\u6253\u5f97\u4ed6\u8eab\u5b50\u8fde\u6643\uff0c\u9669\u4e9b\u6454\u5012\u3002\u4e07\u9707\u5c71\u559d\u9053\uff1a\u201c\u72c4\u8d24\u4f84\u9000\u4e0b\uff01\u201d \u3000\u3000\u8bed\u6c14\u5df2\u9887\u4e25\u5cfb\u3002 \u3000\u3000\u72c4\u4e91\u7ea2\u4e86\u53cc\u773c\uff0c\u559d\u9053\uff1a\u201c\u4f60\u4e0d\u8d54\u8863\u670d\u8fd8\u6253\u4eba\uff0c\u4e0d\u8bb2\u7406\u4e48\uff1f\u201d \u3000\u3000\u5415\u901a\u7b11\u9053\uff1a\u201c\u6211\u6253\u4f60\u8fd9\u6d51\u5c0f\u5b50\u4fbf\u600e\u6837\uff1f\u201d\u72c4\u4e91\u9053\uff1a\u201c\u6211\u4e5f\u6253\u4f60\uff01\u201d \u3000\u3000\u8eab\u5f62\u4e00\u632b\uff0c\u5de6\u638c\u659c\u5288\uff0c\u53f3\u638c\u5df2\u4ece\u5de6\u638c\u5e95\u7a7f\u51fa\u3002\u5415\u901a\u4f7f\u62db\u201c\u6253\u864e\u5f0f\u201d\uff0c\u5de6\u817f\u865a\u5750\uff0c\u53f3\u62f3\u98de\u51fa\u53bb\u3002 \u3000\u3000\u4e24\u4eba\u8fd9\u4e00\u642d\u4e0a\u624b\uff0c\u970e\u65f6\u4e4b\u95f4\u62c6\u4e86\u5341\u4f59\u62db\uff0c\u72c4\u4e91\u81ea\u5e7c\u8ddf\u7740\u621a\u957f\u53d1\u7ec3\u6b66\uff0c\u4e0e\u5e08\u59b9\u8fc7\u62db\u6bd4\u5251\uff0c\u4ece\u6ca1\u4e00\u5929\u95f4\u65ad\u3002\u5415\u901a\u867d\u662f\u664b\u4e2d\u5927\u76d7\uff0c\u9ed1\u9053\u4e2d\u7684\u6210\u540d\u4eba\u7269\uff0c\u4e00\u65f6\u4e4b\u95f4\u5374\u4e5f\u6253\u4ed6\u4e0d\u5012\uff0c\u51e0\u6b21\u8981\u4f7f\u94c1\u81c2\u529f\u3002\u90fd\u88ab\u4ed6\u4e56\u5de7\u907f\u5f00\uff0c\u5728\u4ed6\u80a9\u5934\u6253\u4e2d\u4e86\u4e24\u62f3\uff0c\u72c4\u4e91\u8089\u539a\u9aa8\u58ee\uff0c\u4e5f\u6ca1\u53d7\u4f24\u3002 \u3000\u3000\u518d\u62c6\u6570\u62db\uff0c\u5415\u901a\u7126\u8e81\u8d77\u6765\uff0c\u7a81\u7136\u95f4\u62f3\u6cd5\u4e00\u53d8\uff0c\u81ea\u201c\u516d\u5408\u62f3\u201d\u53d8\u4e3a\u201c\u8d64\u5c3b\u8fde\u62f3\u201d\u3002\u8fd9\u5957\u62f3\u6cd5\u4ea6\u662f\u201c\u516d\u5408\u62f3\u201d\u4e2d\u4e00\u8def\uff0c\u53ea\u662f\u6742\u4ee5\u7334\u62f3\uff0c\u8bb2\u7a76\u6402\u3001\u6253\u3001\u817e\u3001\u5c01\u3001\u8e22\u3001\u6f6d\u3001\u626b\u3001\u6302\uff0c\u53c8\u52a0\u4e0a\u201c\u732b\u7a9c\u3001\u72d7\u95ea\u3001\u5154\u6eda\u3001\u9e70\u7ffb\u3001\u677e\u5b50\u7075\u3001\u7ec6\u80f8\u5de7\u3001\u9e5e\u5b50\u7ffb\u8eab\u3001\u8dfa\u5b50\u811a\u201d\u516b\u5f0f\uff0c\u5f0f\u4e2d\u5957\u5f0f\uff0c\u53d8\u5e7b\u591a\u7aef\u3002\u72c4\u4e91\u6ca1\u89c1\u8fc7\u8fd9\u8def\u62f3\u6cd5\uff0c\u5fc3\u4e2d\u4e00\u614c\uff0c\u5de6\u817f\u4e0a\u8fde\u63a5\u7ed9\u4ed6\u8e39\u4e86\u4e24\u811a\u3002 \u3000\u3000\u4e07\u9707\u5c71\u77a7\u51fa\u4ed6\u4e0d\u662f\u654c\u624b\uff0c\u559d\u9053\uff1a\u201c\u72c4\u8d24\u4f84\u9000\u4e0b\uff0c\u4f60\u6253\u4ed6\u4e0d\u8fc7\u3002\u201d \u3000\u3000\u72c4\u4e91\u53eb\u9053\uff1a\u201c\u6253\u4e0d\u8fc7\u4e5f\u8981\u6253\u3002\u201d\u7830\u7684\u4e00\u54cd\uff0c\u80f8\u53e3\u53c8\u88ab\u5415\u901a\u6253\u4e86\u4e00\u62f3\u3002 \u3000\u3000\u621a\u82b3\u5728\u65c1\u77a7\u7740\uff0c\u4e00\u76f4\u4e3a\u5e08\u54e5\u62c5\u5fc3\uff0c\u8fd9\u65f6\u5fcd\u4e0d\u4f4f\u4e5f\u53eb\uff1a\u201c\u5e08\u54e5\uff0c\u4e0d\u7528\u6253\u4e86\uff0c\u8ba9\u4e07\u5e08\u4f2f\u6253\u53d1\u4ed6\u3002\u201d\u4f46\u72c4\u4e91\u53cc\u81c2\u76f4\u4e0a\u76f4\u4e0b\uff0c\u4e0d\u987e\u6027\u547d\u7684\u524d\u51b2\uff0c\u4e0d\u4f4f\u5406\u559d\uff1a\u201c\u6211\u4e0d\u6015\u4f60\uff0c\u6211\u4e0d\u6015\u4f60\u3002\u201d\u7830\u7684\u4e00\u58f0\uff0c\u9f3b\u5b50\u53c8\u4e2d\u4e86\u4e00\u62f3\uff0c\u767b\u65f6\u9c9c\u8840\u6dcb\u6f13\u3002 \u3000\u3000\u4e07\u9707\u5c71\u76b1\u8d77\u4e86\u7709\u5934\uff0c\u5411\u621a\u957f\u53d1\u9053\uff1a\u201c\u5e08\u5f1f\uff0c\u4ed6\u4e0d\u542c\u6211\u8bdd\uff0c\u4f60\u53eb\u4ed6\u4e0b\u6765\u7f62\uff01\u201d\u621a\u957f\u53d1\u54fc\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u8ba9\u4ed6\u5403\u70b9\u513f\u82e6\u5934\uff0c\u5f85\u4f1a\u8ba9\u6211\u53bb\u6597\u6597\u8fd9\u91c7\u82b1\u5927\u76d7\u3002\u201d \u3000\u3000\u4fbf\u5728\u6b64\u65f6\uff0c\u5927\u95e8\u5916\u8d70\u8fdb\u4e00\u4e2a\u84ec\u5934\u57a2\u9762\u7684\u8001\u4e5e\u4e10\uff0c\u5de6\u624b\u62ff\u7740\u53ea\u7834\u7897\uff0c\u53f3\u624b\u62c4\u7740\u4e00\u6839\u7af9\u68d2\uff0c\u5636\u54d1\u7740\u55d3\u5b50\u53eb\u9053\uff1a\u201c\u8001\u7237\u5b50\u4eca\u65e5\u505a\u559c\u4e8b\uff0c\u65bd\u820d\u8001\u5316\u5b50\u4e00\u7897\u51b7\u996d\u3002\u201d \u3000\u3000\u4f17\u4eba\u90fd\u6b63\u5168\u795e\u8d2f\u6ce8\u7684\u77a7\u7740\u5415\u901a\u4e0e\u72c4\u4e91\u6253\u6597\uff0c\u8c01\u4e5f\u6ca1\u53bb\u7406\u4f1a\uff0c\u90a3\u4e5e\u4e10\u547b\u541f\u53eb\u5524\uff1a\u201c\u554a\u5537\uff0c\u997f\u6b7b\u4e86\uff0c\u997f\u6b7b\u4e86\u3002\u201d\u7a81\u7136\u5de6\u8db3\u8e0f\u5728\u5730\u4e0b\u7684\u7caa\u4fbf\u4e4b\u4e2d\uff0c\u811a\u4e0b\u4e00\u6ed1\uff0c\u4fef\u8eab\u6454\u5c06\u4e0b\u6765\uff0c\u5927\u53eb\u4e00\u58f0\uff1a \u3000\u3000\u201c\u554a\u54df\uff0c\u8dcc\u6b7b\u4e86\uff01\u201d\u624b\u4e2d\u7684\u7834\u7897\u548c\u7af9\u68d2\u540c\u65f6\u6454\u51fa\u3002\u8bf4\u4e5f\u771f\u5de7\u3002\u90a3\u7834\u7897\u6b63\u597d\u63b7\u5728\u5415\u901a\u540e\u80cc\u201c\u5fd7\u5802\u7a74\u201d\u4e0a\uff0c\u7af9\u68d2\u4e00\u7aef\u5374\u5728\u5415\u901a\u819d\u5f2f\u7684\u201c\u66f2\u6cc9\u7a74\u201d\u4e2d\u4e00\u78b0\u3002 \u3000\u3000\u5415\u901a\u819d\u95f4\u4e00\u8f6f\uff0c\u5de6\u8db3\u8dea\u5012\uff0c\u540c\u65f6\u5168\u8eab\u9178\u9ebb\uff0c\u4f3c\u4e4e\u7a81\u7136\u865a\u8131\u3002\u72c4\u4e91\u53cc\u62f3\u9f50\u51fa\uff0c\u7830\u7830\u4e24\u58f0\uff0c\u5c06\u5415\u901a\u5e9e\u5927\u7684\u8eab\u5b50\u6253\u5f97\u98de\u4e86\u8d77\u6765\uff0c\u62cd\u7684\u4e00\u54cd\uff0c\u81ed\u6c34\u56db\u6e85\uff0c\u6b63\u6454\u5728\u4ed6\u643a\u6765\u7684\u7caa\u4fbf\u4e4b\u4e2d\u3002 \u3000\u3000\u8fd9\u4e00\u4e0b\u53d8\u6545\u4eba\u4eba\u5927\u51fa\u610f\u6599\u4e4b\u5916\uff0c\u53ea\u89c1\u5415\u901a\u72fc\u72c8\u4e07\u72b6\u7684\u722c\u8d77\u8eab\u6765\uff0c\u62b1\u5934\u9f20\u7a9c\u800c\u51fa\u3002\u4f17\u8d3a\u5ba2\u54c8\u54c8\u5927\u7b11\uff0c\u9f50\u58f0\u547c\u559d\uff1a\u201c\u62ff\u4f4f\u4ed6\uff0c\u62ff\u4f4f\u4ed6\uff01\u201d\u201c\u522b\u8ba9\u8fd9\u8d3c\u5b50\u8dd1\u4e86\u3002\u201d \u3000\u3000\u72c4\u4e91\u5140\u81ea\u5927\u53eb\uff1a\u201c\u8d54\u6211\u5e08\u7236\u7684\u888d\u5b50\u3002\u201d\u5f85\u8981\u8d76\u51fa\uff0c\u7a81\u89c9\u5de6\u81c2\u88ab\u4eba\u63e1\u4f4f\uff0c\u52a8\u5f39\u4e0d\u5f97\uff0c\u4fa7\u5934\u4e00\u770b\uff0c\u6b63\u662f\u5e08\u7236\u3002\u621a\u957f\u53d1\u9053\uff1a\u201c\u4f60\u4fa5\u5e78\u5f97\u80dc\uff0c\u8fd8\u8ffd\u4ec0\u4e48\uff1f\u201d\u621a\u82b3\u62bd\u51fa\u624b\u5e15\uff0c\u7ed9\u72c4\u4e91\u64e6\u53bb\u8138\u4e0a\u9c9c\u8840\u3002\u72c4\u4e91\u4e00\u4f4e\u5934\uff0c\u53ea\u89c1\u81ea\u5df1\u65b0\u886b\u7684\u8863\u895f\u4e0a\u70b9\u70b9\u6ef4\u6ef4\u7684\u90fd\u662f\u9c9c\u8840\uff0c\u4e0d\u7981\u5927\u6025\uff0c\u9053\uff1a\u201c\u7cdf\u7cd5\uff0c\u7cdf\u7cd5\uff01\u6211\u2026\u2026\u6211\u8fd9\u4ef6\u65b0\u8863\u4e5f\u5f04\u810f\u4e86\u3002\u201d \u3000\u3000\u53ea\u89c1\u90a3\u8001\u4e5e\u4e10\u8e52\u8dda\u7740\u8d70\u51fa\u5927\u95e8\uff0c\u5583\u5583\u81ea\u8bed\uff1a\u201c\u996d\u6ca1\u8ba8\u7740\uff0c\u53cd\u8d54\u4e86\u4e00\u53ea\u996d\u7897\u3002\u201d\u72c4\u4e91\u77e5\u9053\u9002\u624d\u53d6\u80dc\uff0c\u5168\u9760\u8fd9\u4e5e\u4e10\u78b0\u5de7\u4e00\u8dcc\uff0c\u4ece\u6000\u91cc\u638f\u51fa\u4e8c\u5341\u679a\u5927\u94b1\uff0c\u90a3\u662f\u5e08\u7236\u7ed9\u4ed6\u6765\u57ce\u91cc\u96f6\u82b1\u7684\uff0c\u8ffd\u51fa\u53bb\u585e\u5728\u4ed6\u7684\u624b\u91cc\u3002\u90a3\u8001\u4e5e\u4e10\u8fde\u58f0\u9053\uff1a\u201c\u591a\u8c22\uff0c\u591a\u8c22\u3002\u201d \u3000\u3000\u5f53\u665a\u4e07\u9707\u5c71\u5927\u5f20\u7b75\u5e2d\uff0c\u6b3e\u5f85\u524d\u6765\u8d3a\u5bff\u7684\u8d3a\u5ba2\u3002\u4ed6\u662f\u8346\u5dde\u7684\u5927\u7ec5\u58eb\uff0c\u5bff\u5802\u4e2d\u60ac\u4e86\u8346\u5dde\u5e9c\u51cc\u77e5\u5e9c\u3001\u6c5f\u9675\u53bf\u5c1a\u77e5\u53bf\u9001\u7684\u5bff\u5e5b\uff0c\u91d1\u5149\u95ea\u95ea\uff0c\u597d\u4e0d\u98ce\u5149\u3002 \u3000\u3000\u5e2d\u4e0a\u81ea\u662f\u4eba\u4eba\u8c08\u8bba\u65e5\u95f4\u8fd9\u4e00\u4ef6\u8da3\u4e8b\uff0c\u5927\u5bb6\u90fd\u8bf4\u72c4\u4e91\u798f\u6c14\u597d\uff0c\u773c\u89c1\u4e0d\u654c\uff0c\u521a\u597d\u8fd9\u8001\u4e5e\u4e10\u8fdb\u6765\u6454\u4e86\u4e00\u4ea4\uff0c\u6270\u4e71\u4e86\u5415\u901a\u7684\u5fc3\u795e\u3002\u5927\u5bb6\u4e5f\u4e0d\u514d\u79f0\u8d5e\u72c4\u4e91\u5c0f\u5c0f\u5e74\u7eaa\uff0c\u5c45\u7136\u6709\u8fd9\u7b49\u80c6\u8bc6\uff0c\u548c\u8fd9\u9ed1\u9053\u4e0a\u7684\u6210\u540d\u4eba\u7269\u7f20\u6597\u5230\u6570\u5341\u62db\uff0c\u90a3\u4e5f\u5df2\u6781\u4e0d\u5bb9\u6613\u3002\u81ea\u7136\u4e5f\u6709\u4eba\u8bf4\u8fd9\u662f\u5bff\u661f\u516c\u6d2a\u798f\u9f50\u5929\uff0c\u5426\u5219\u90a3\u6709\u8fd9\u4e48\u5de7\uff0c\u8001\u4e5e\u4e10\u6454\u4e2a\u4ef0\u516b\u53c9\uff0c\u7adf\u7136\u5c31\u6b64\u9000\u4e86\u5f3a\u654c\uff0c\u82e5\u662f\u4e07\u9707\u5c71\u81ea\u5df1\u51fa\u624b\uff0c\u5f53\u7136\u4e24\u4e09\u4e0b\u4fbf\u6253\u53d1\u4e86\u8fd9\u6076\u5ba2\uff0c\u4e0d\u8fc7\u8981\u52b3\u52a8\u5bff\u661f\u516c\u7684\u5927\u9a7e\uff0c\u4fbf\u4e0d\u8fd9\u4e48\u6709\u8da3\u4e86\u3002 \u3000\u3000\u4f17\u5bbe\u5ba2\u8fd9\u4e48\u4e00\u79f0\u8d5e\u72c4\u4e91\uff0c\u4e07\u9707\u5c71\u624b\u4e0b\u7684\u516b\u540d\u5f1f\u5b50\u5747\u611f\u8138\u4e0a\u9eef\u7136\u65e0\u5149\u3002\u8fd9\u5415\u901a\u672c\u662f\u51b2\u7740\u4e07\u9707\u5c71\u800c\u6765\uff0c\u4e07\u95e8\u5f1f\u5b50\u4e0d\u51fa\u624b\uff0c\u5374\u6559\u5e08\u53d4\u4e00\u4e2a\u5446\u5934\u5446\u8111\u7684\u4e61\u4e0b\u5f1f\u5b50\u5f3a\u884c\u51fa\u5934\uff0c\u6253\u9000\u4e86\u654c\u4eba\u3002\u516b\u540d\u5f1f\u5b50\u4e2a\u4e2a\u5fc3\u4e2d\u6c14\u6124\uff0c\u53ef\u53c8\u4e0d\u4fbf\u53d1\u4f5c\u3002 \u3000\u3000\u4e07\u9707\u5c71\u4eb2\u81ea\u656c\u8fc7\u9152\u540e\uff0c\u5927\u5f1f\u5b50\u9c81\u5764\u3001\u4e8c\u5f1f\u5b50\u5468\u573b\u3001\u4e09\u5f1f\u5b50\u4e07\u572d\u3001\u56db\u5f1f\u5b50\u5b59\u5747\u3001\u4e94\u5f1f\u5b50\u535c\u57a3\u3001\u516d\u5f1f\u5b50\u5434\u574e\u3001\u4e03\u5f1f\u5b50\u51af\u5766\u3001\u516b\u5f1f\u5b50\u6c88\u57ce\u4e00\u5e2d\u5e2d\u8fc7\u6765\u656c\u9152\u3002\u4e07\u95e8\u516b\u5f1f\u5b50\u90fd\u4ee5\u201c\u571f\u201d\u5b57\u508d\u4e3a\u540d\uff0c\u5176\u4e2d\u7b2c\u4e09\u5f1f\u5b50\u4e07\u572d\u662f\u4e07\u9707\u5c71\u7684\u72ec\u5b50\uff0c\u4ed6\u957f\u8eab\u7389\u7acb\u3002\u8138\u5f62\u5fae\u89c1\u7626\u524a\uff0c\u4fca\u7f8e\u6f47\u6d12\uff0c\u5012\u50cf\u662f\u4e2a\u5bcc\u5bb6\u516c\u5b50\uff0c\u4e0d\u4f3c\u5927\u5e08\u5144\u9c81\u5764\u3001\u4e8c\u5e08\u5144\u5468\u573b\u90a3\u4e48\u8d73\u8d73\u6602\u6602\u3002 \u3000\u3000\u516b\u4eba\u5411\u6765\u5bbe\u4e2d\u6709\u529f\u540d\u7684\u4e3e\u4eba\u3001\u79c0\u624d\u3001\u6b66\u6797\u5c0a\u957f\u656c\u8fc7\u4e86\u9152\uff0c\u656c\u4e86\u5e08\u53d4\u621a\u957f\u53d1\u4e00\u676f\uff0c\u4fbf\u5411\u72c4\u4e91\u656c\u9152\u3002\u4e07\u572d\u8bf4\u9053\uff1a\u201c\u4eca\u65e5\u72c4\u5e08\u5144\u7ed9\u5bb6\u7236\u6323\u4e86\u597d\u5927\u7684\u9762\u5b50\uff0c\u6211\u4eec\u5e08\u5144\u5f1f\u516b\u4eba\uff0c\u6bcf\u4e2a\u90fd\u975e\u656c\u72c4\u5e08\u5144\u4e00\u5927\u676f\u4e0d\u53ef\u3002\u201d\u72c4\u4e91\u7d20\u6765\u4e0d\u4f1a\u559d\u9152\uff0c\u53cc\u624b\u4e71\u6447\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u4f1a\u559d\uff0c\u6211\u4e0d\u4f1a\u559d\u3002\u201d \u3000\u3000\u4e07\u572d\u9053\uff1a\u201c\u65e5\u95f4\u5bb6\u7236\u8fde\u53eb\u4e09\u6b21\uff0c\u8981\u72c4\u5e08\u5144\u9000\u4e0b\uff0c\u72c4\u5e08\u5144\u7f6e\u4e4b\u4e0d\u7406\uff0c\u628a\u5bb6\u7236\u7684\u8bdd\u5f53\u4f5c\u8033\u8fb9\u98ce\u4e00\u822c\u3002\u6211\u4eec\u6b64\u523b\u656c\u9152\uff0c\u72c4\u5e08\u5144\u53c8\u662f\u4e0d\u559d\uff0c\u90a3\u628a\u6211\u4eec\u4e07\u5bb6\u95e8\u53ef\u5fd2\u4e5f\u5c0f\u770b\u4e86\u3002\u201d\u72c4\u4e91\u6115\u7136\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\u6ca1\u6709\u554a\u3002\u201d \u3000\u3000\u621a\u957f\u53d1\u542c\u5f97\u4e07\u572d\u7684\u8bed\u6c14\u4e0d\u5bf9\uff0c\u8bf4\u9053\uff1a\u201c\u4e91\u513f\uff0c\u4f60\u559d\u4e86\u9152\u3002\u201d \u3000\u3000\u72c4\u4e91\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\u2026\u2026\u6211\u4e0d\u4f1a\u559d\u9152\u7684\u554a\u3002\u201d\u621a\u957f\u53d1\u6c89\u58f0\u9053\uff1a\u201c\u559d\u4e86\uff01\u201d\u2018\u72c4\u4e91\u65e0\u5948\uff0c\u53ea\u5f97\u4e00\u4eba\u4e00\u676f\uff0c\u63a5\u8fde\u559d\u4e86\u516b\u676f\u3002\u767b\u65f6\u6ee1\u8138\u901a\u7ea2\uff0c\u8033\u4e2d\u55e1\u55e1\u4f5c\u54cd\uff0c\u8111\u5b50\u91cc\u80e1\u91cc\u80e1\u6d82\u7684\u4e00\u56e2\u3002 \u3000\u3000\u8fd9\u4e00\u665a\u72c4\u4e91\u7761\u4e0a\u4e86\u5e8a\uff0c\u5fc3\u5934\u5140\u81ea\u8ff7\u7cca\uff0c\u53ea\u611f\u80f8\u95f4\u3001\u80a9\u5934\u3001\u817f\u4e0a\uff0c\u88ab\u5415\u901a\u62f3\u6253\u811a\u8e22\u8fc7\u4e4b\u5904\u90fd\u662f\u70ed\u8fa3\u8fa3\u7684\u75bc\u75db\u3002\u7761\u5230\u534a\u591c\uff0c\u7761\u68a6\u4e2d\u542c\u5f97\u7a97\u4e0a\u6709\u4eba\u4f38\u6307\u5f39\u51fb\uff0c\u6709\u4eba\u4e0d\u4f4f\u53eb\u5524\uff1a\u201c\u72c4\u5e08\u5144\u3001\u72c4\u4e91\uff0c\u72c4\u4e91\uff01\u201d\u72c4\u4e91\u4e00\u60ca\u800c\u9192\uff0c\u95ee\u9053\uff1a\u201c\u662f\u8c01\uff1f\u201d \u3000\u3000\u7a97\u5916\u90a3\u4eba\u8bf4\u9053\uff1a\u201c\u5c0f\u5f1f\u4e07\u572d\uff0c\u6709\u4e8b\u76f8\u5546\uff0c\u8bf7\u72c4\u5e08\u5144\u51fa\u6765\u3002\u201d \u3000\u3000\u72c4\u4e91\u4e00\u5446\uff0c\u4e0b\u5f97\u5e8a\u6765\uff0c\u62ab\u8863\u7a7f\u978b\uff0c\u63a8\u5f00\u7a97\u5b50\u3002\u53ea\u89c1\u7a97\u5916\u516b\u4e2a\u4eba\u4e00\u5b57\u6392\u5f00\uff0c\u6bcf\u4eba\u624b\u4e2d\u90fd\u6301\u4e00\u67c4\u957f\u5251\uff0c\u4fbf\u662f\u90a3\u4e07\u95e8\u516b\u5f1f\u5b50\u3002 \u3000\u3000\u72c4\u4e91\u5947\u9053\uff1a\u201c\u53eb\u6211\u5e72\u4ec0\u4e48\uff1f\u201d\u4e07\u572d\u9053\uff1a\u201c\u54b1\u4eec\u8981\u9886\u6559\u9886\u6559\u72c4\u5e08\u5144\u7684\u5251\u62db\u3002\u201d\u72c4\u4e91\u6447\u5934\u9053\uff1a\u201c\u5e08\u7236\u5429\u5490\u8fc7\u7684\uff0c\u4e0d\u53ef\u8ddf\u4e07\u5e08\u4f2f\u95e8\u4e0b\u7684\u5e08\u5144\u4eec\u6bd4\u8bd5\u6b66\u827a\u3002\u201d\u4e07\u572d\u51b7\u7b11\u9053\uff1a\u201c\u539f\u6765\u621a\u5e08\u53d4\u5012\u6709\u81ea\u77e5\u4e4b\u660e\u201d\u72c4\u4e91\u6012\u9053\uff1a\u201c\u4ec0\u4e48\u81ea\u77e5\u4e4b\u660e\uff1f\u201d\u7a81\u7136\u95f4\u55e4\u55e4\u4e09\u58f0\uff0c\u4e07\u572d\u9694\u7a97\u5411\u4ed6\u8fde\u523a\u4e09\u5251\uff0c\u5251\u5203\u90fd\u5728\u4ed6\u8138\u988a\u8fb9\u63a0\u8fc7\uff0c\u76f8\u5dee\u4e0d\u8fc7\u5bf8\u8bb8\u3002\u72c4\u4e91\u53ea\u611f\u8138\u988a\u8fb9\u51c9\u98d5\u98d5\u5730\uff0c\u5927\u5403\u4e00\u60ca\uff0c\u6025\u5fd9\u5012\u9000\uff0c\u5de6\u811a\u5728\u51f3\u4e0a\u4e00\u7eca\uff0c\u4e00\u4e2a\u8e09\u8dc4\uff0c\u5341\u5206\u72fc\u72c8\u3002\u4e07\u95e8\u516b\u5f1f\u5b50\u90fd\u5927\u58f0\u7b11\u4e86\u8d77\u6765\u3002 \u3000\u3000\u72c4\u4e91\u5927\u6012\uff0c\u8fd4\u8eab\u62bd\u51fa\u6795\u5934\u5e95\u4e0b\u7684\u957f\u5251\uff0c\u8dc3\u51fa\u7a97\u53bb\uff0c\u89c1\u4e07\u95e8\u516b\u5927\u5f1f\u5b50\u4eba\u4eba\u8138\u8272\u4e0d\u5584\uff0c\u4e0d\u7981\u5fc3\u4e0b\u6697\u81ea\u5600\u5495\uff0c\u867d\u662f\u6709\u6c14\uff0c\u4f46\u5ff5\u53ca\u5e08\u7236\u4e00\u518d\u53ee\u5631\uff0c\u5343\u4e07\u4e0d\u53ef\u548c\u5e08\u4f2f\u95e8\u4eba\u5931\u548c\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u8981\u600e\u6837\uff1f\u201d \u3000\u3000\u4e07\u572d\u957f\u5251\u865a\u51fb\uff0c\u5728\u7a7a\u4e2d\u55e1\u55e1\u4f5c\u54cd\uff0c\u8bf4\u9053\uff1a\u201c\u72c4\u5e08\u5144\uff0c\u4f60\u4eca\u65e5\u901e\u5f3a\u51fa\u5934\uff0c\u53ea\u9053\u6211\u8346\u5dde\u4e07\u5bb6\u95e8\u4e2d\u4eba\u4eba\u90fd\u6b7b\u5149\u4e86\uff0c\u662f\u4e0d\u662f\uff1f\u8fd8\u662f\u8bf4\u6211\u4e07\u95e8\u5bb6\u4e2d\uff0c\u6ca1\u4e00\u4e2a\u53ca\u5f97\u4e0a\u4f60\u72c4\u5927\u54e5\u7684\u8eab\u624b\uff1f\u201d \u3000\u3000\u72c4\u4e91\u6447\u5934\u9053\uff1a\u201c\u90a3\u4eba\u5f04\u810f\u4e86\u6211\u5e08\u7236\u7684\u8863\u670d\uff0c\u6211\u81ea\u7136\u8981\u4ed6\u8d54\uff0c\u8fd9\u5173\u4f60\u751a\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u4e07\u572d\u6447\u5934\u9053\uff1a\u201c\u4f60\u5728\u4f17\u4f4d\u5bbe\u5ba2\u4e4b\u524d\u6210\u540d\u7acb\u4e07\uff0c\u9732\u4e86\u597d\u5927\u7684\u8138\uff0c\u5374\u6559\u6211\u5e08\u5144\u5f1f\u516b\u4eba\u5168\u95f9\u5f97\u7070\u5934\u571f\u8138\u3002\u522b\u8bf4\u518d\u5230\u6c5f\u6e56\u4e0a\u6df7\uff0c\u4fbf\u662f\u8fd9\u8346\u5dde\u57ce\u4e2d\uff0c\u6211\u4eec\u5e08\u5144\u5f1f\u4e5f\u65e0\u7acb\u8db3\u4e4b\u5730\u4e86\u3002\u4f60\u4eca\u65e5\u7684\u6240\u4f5c\u6240\u4e3a\uff0c\u4e0d\u4e5f\u592a\u8fc7\u5206\u4e86\u4e48\uff1f\u201d\u72c4\u4e91\u6115\u7136\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\u4e0d\u77e5\u9053\u554a\u3002\u201d \u3000\u3000\u4e07\u95e8\u5927\u5f1f\u5b50\u9c81\u5764\u9053\uff1a\u201c\u4e09\u5e08\u5f1f\uff0c\u8fd9\u5c0f\u5b50\u88c5\u849c\uff0c\u8ddf\u4ed6\u591a\u8bf4\u4ec0\u4e48\uff1f\u62bb\u91cf\u62bb\u91cf\u4ed6\u3002\u201d \u3000\u3000\u4e07\u572d\u957f\u5251\u9012\u51fa\uff0c\u6307\u5411\u72c4\u4e91\u5de6\u80a9\u3002\u72c4\u4e91\u8bc6\u5f97\u8fd9\u4e00\u5251\u4e43\u662f\u865a\u62db\uff0c\u8eab\u5f62\u4e0d\u52a8\uff0c\u4ea6\u4e0d\u4f38\u5251\u6321\u67b6\u3002\u4e07\u572d\u659c\u5251\u6536\u56de\uff0c\u88ab\u4ed6\u8bc6\u7834\u5251\u62db\uff0c\u66f4\u662f\u7740\u607c\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u554a\uff0c\u4f60\u662f\u4e0d\u5c51\u8ddf\u6211\u52a8\u624b\uff01\u201d\u72c4\u4e91\u9053\uff1a\u201c\u5e08\u7236\u5429\u5490\u8fc7\u7684\uff0c\u5343\u4e07\u4e0d\u53ef\u8ddf\u5e08\u4f2f\u7684\u95e8\u4eba\u6bd4\u8bd5\u3002\u201d \u3000\u3000\u7a81\u7136\u95f4\u55e4\u7684\u4e00\u58f0\uff0c\u4e07\u572d\u957f\u5251\u523a\u51fa\uff0c\u5728\u4ed6\u53f3\u624b\u8863\u8896\u4e0a\u523a\u7834\u4e86\u4e00\u6761\u957f\u7f1d\u3002 \u3000\u3000\u72c4\u4e91\u5bf9\u8fd9\u4ef6\u65b0\u8863\u751a\u662f\u5b9d\u7231\uff0c\u5e73\u767d\u65e0\u7aef\u7684\u7ed9\u4ed6\u523a\u7834\uff0c\u518d\u4e5f\u5fcd\u8010\u4e0d\u4f4f\uff0c\u559d\u9053\uff1a\u201c\u4f60\u523a\u7834\u6211\u8863\u670d\uff0c\u8981\u4f60\u8d54\u3002\u201d\u4e07\u572d\u51b7\u51b7\u4e00\u7b11\uff0c\u633a\u5251\u53c8\u523a\u5411\u4ed6\u7684\u5de6\u8896\u3002\u72c4\u4e91\u56de\u5251\u659c\u524a\u3002\u5f53\u7684\u4e00\u58f0\uff0c\u683c\u5f00\u6765\u5251\uff0c\u4e58\u52bf\u8fd8\u51fb\u3002\u4e24\u4eba\u8fd9\u4e00\u4ea4\u4e0a\u624b\uff0c\u4fbf\u5373\u8d8a\u6597\u8d8a\u5feb\u3002\u4e24\u4eba\u6240\u5b66\u5251\u6cd5\u4e00\u8109\u76f8\u627f\uff0c\u6597\u5230\u5341\u4f59\u62db\u540e\uff0c\u72c4\u4e91\u5174\u53d1\uff0c\u4e00\u5251\u5251\u7adf\u5f80\u4e07\u572d\u8981\u5bb3\u523a\u53bb\u3002 \u3000\u3000\u5468\u573b\u53eb\u9053\uff1a\u201c\u563f\uff01\u8fd9\u5c0f\u5b50\u5f53\u771f\u8981\u4eba\u6027\u547d\u4e48\uff1f\u4e09\u5e08\u5f1f\uff0c\u624b\u4e0b\u522b\u5bb9\u60c5\u4e86\u3002\u201d \u3000\u3000\u72c4\u4e91\u4e00\u60ca\uff0c\u6697\u60f3\uff1a\u201c\u6211\u82e5\u662f\u4e00\u4e2a\u5931\u624b\uff0c\u771f\u7684\u523a\u4f24\u4e86\u4ed6\uff0c\u90a3\u53ef\u4e0d\u597d\u3002\u201d\u624b\u4e0a\u653b\u52bf\u767b\u7f13\u3002\u4e07\u572d\u8fd8\u9053\u4ed6\u5251\u6cd5\u4e0d\u53ca\u81ea\u5df1\uff0c\u5251\u62db\u7684\u7ef5\u7ef5\u4e0d\u7edd\uff0c\u6765\u52bf\u5176\u662f\u51cc\u5389\u3002\u72c4\u4e91\u8fde\u8fde\u5012\u9000\u3002\u559d\u9053\uff1a\u201c\u6211\u53c8\u4e0d\u8ddf\u4f60\u771f\u6253\u3002\u4f60\u8fd9\u662f\u5e72\u4ec0\u4e48\u4e86\uff1f\u201d\u4e07\u572d\u9053\uff1a\u201c\u5e72\u4ec0\u4e48\uff1f\u8981\u523a\u4f60\u51e0\u4e2a\u900f\u660e\u7a9f\u7abf\uff01\u201d\u55e4\u7684\u4e00\u5251\u3002\u8e0f\u4e2d\u5bab\u76f4\u523a\uff0c\u72c4\u4e91\u659c\u8eab\u95ea\u5728\u5de6\u4fa7\uff0c\u773c\u89c1\u4ed6\u53f3\u80a9\u5904\u9732\u51fa\u7834\u7efd\uff0c\u957f\u5251\u5012\u7ffb\u4e0a\u53bb\uff0c\u8fd9\u4e00\u5251\u82e5\u662f\u76f4\u524a\uff0c\u4e07\u572d\u80a9\u5934\u975e\u53d7\u91cd\u4f24\u4e0d\u53ef\uff0c\u72c4\u4e91\u624b\u8155\u7565\u7ffb\uff0c\u5251\u5203\u5e73\u8f6c\uff0c\u62cd\u7684\u4e00\u58f0\uff0c\u5728\u4ed6\u7684\u80a9\u4e0a\u62cd\u4e86\u4e00\u4e0b\u3002 \u3000\u3000\u4ed6\u53ea\u9053\u8fd9\u4e00\u6765\u80dc\u8d1f\u5df2\u5206\uff0c\u4e07\u572d\u8be5\u5f53\u77e5\u96be\u800c\u9000\uff0c\u4ed6\u5e73\u65e5\u548c\u5e08\u59b9\u6bd4\u5251\uff0c\u4e00\u5230\u8fd9\u4e2a\u5730\u6b65\u4fbf\u5373\u7f62\u624b\uff0c\u4e0d\u6599\u4e07\u572d\u4fca\u8138\u4e00\u7ea2\uff0c\u53cd\u800c\u633a\u5251\u76f4\u523a\u3002\u72c4\u4e91\u731d\u4e0d\u53ca\u9632\uff0c\u5de6\u817f\u4e0a\u4e00\u9635\u5267\u75db\uff0c\u5df2\u7136\u4e2d\u5251\u3002 \u3000\u3000\u9c81\u5764\u3001\u5468\u573b\u7b49\u62cd\u624b\u6b22\u547c\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u5b50\uff0c\u8eba\u4e0b\u7f62\uff01\u201d\u8ba4\u8f93\u4fbf\u9976\u4e86\u4f60\uff01\u201d\u201c\u621a\u5e08\u53d4\u8c03\u6559\u51fa\u6765\u7684\u4e61\u5df4\u4f6c\u95e8\u5f92\uff0c\u539f\u4e0d\u8fc7\u662f\u8fd9\u51e0\u4e0b\u4e09\u811a\u732b\u628a\u5f0f\u3002\u201d \u3000\u3000\u72c4\u4e91\u817f\u4e0a\u4e2d\u5251\u540e\u672c\u5df2\u5927\u6012\uff0c\u542c\u8fd9\u4e9b\u4eba\u51fa\u8a00\u8fb1\u53ca\u5e08\u7236\uff0c\u66f4\u662f\u6012\u53d1\u5982\u72c2\uff0c\u4e00\u54ac\u7259\uff0c\u957f\u5251\u5982\u75be\u98ce\u9aa4\u96e8\u822c\u653b\u4e86\u8fc7\u53bb\uff0c\u4e07\u572d\u89c1\u5bf9\u65b9\u52bf\u5982\u75af\u864e\uff0c\u4e0d\u7981\u5fc3\u6709\u602f\u610f\uff0c\u4ed6\u81ea\u5e7c\u5a07\u751f\u60ef\u517b\uff0c\u5251\u6cd5\u867d\u7ec3\u5f97\u4e0d\u9519\uff0c\u8fd9\u822c\u62da\u547d\u7684\u6076\u6597\u7a76\u7adf\u4ece\u672a\u7ecf\u5386\u8fc7\uff0c\u5fc3\u4e2d\u4e00\u6015\uff0c\u5251\u62db\u4fbf\u89c1\u6563\u4e71\u3002 \u3000\u3000\u535c\u57a3\u773c\u89c1\u4e09\u5e08\u5144\u582a\u582a\u8981\u8d25\uff0c\u62fe\u8d77\u4e00\u5757\u7816\u5934\uff0c\u7528\u529b\u6295\u5411\u72c4\u4e91\u540e\u5fc3\u3002 \u3000\u3000\u72c4\u4e91\u5168\u795e\u8d2f\u6ce8\u7684\u6b63\u548c\u4e07\u572d\u6597\u5251\uff0c\u7a81\u7136\u95f4\u80cc\u5fc3\u4e0a\u4e00\u75db\uff0c\u88ab\u7816\u5934\u91cd\u91cd\u63b7\u4e2d\u3002\u4ed6\u56de\u5934\u9a82\u9053\uff1a\u201c\u4e0d\u8981\u8138\uff0c\u4e24\u4e2a\u6253\u4e00\u4e2a\u4e48\uff1f\u201d\u535c\u57a3\u9053\uff1a\u201c\u751a\u4e48\uff0c\u4f60\u8bf4\u751a\u4e48\uff1f\u201d \u3000\u3000\u72c4\u4e91\u5fc3\u9053\uff1a\u201c\u4eca\u65e5\u4f60\u4eec\u4fbf\u662f\u516b\u4eba\u9f50\u4e0a\uff0c\u6211\u4e5f\u4e0d\u80fd\u4e22\u4e86\u5e08\u7236\u7684\u8138\u9762\u3002\u201d\u4e0d\u987e\u817f\u4e0a\u548c\u80cc\u5fc3\u7684\u75bc\u75db\u3002\u4e00\u5251\u5251\u5411\u4e07\u572d\u523a\u53bb\u3002\u8fd9\u65f6\u4ed6\u5251\u62db\u5df2\u4e0d\u6210\u7ae0\u6cd5\uff0c\u7834\u7efd\u767e\u51fa\uff0c\u4f46\u6f0f\u6d1e\u867d\u591a\uff0c\u6c14\u52bf\u5374\u76db\uff0c\u4e07\u572d\u72fc\u72c8\u95ea\u67b6\uff0c\u5df2\u4e0d\u6562\u8fdb\u653b\u3002 \u3000\u3000\u535c\u57a3\u5411\u516d\u5e08\u5f1f\u5434\u574e\u4f7f\u4e2a\u773c\u8272\uff0c\u8bf4\u9053\uff1a\u201c\u4e09\u5e08\u5144\u5251\u6cd5\u9ad8\u660e\uff0c\u8fd9\u5c0f\u5b50\u62db\u67b6\u4e0d\u4f4f\uff0c\u5018\u82e5\u4f24\u4e86\u4ed6\u6027\u547d\uff0c\u621a\u5e08\u53d4\u8138\u4e0a\u987b\u4e0d\u597d\u770b\uff0c\u54b1\u4fe9\u4e0a\u524d\u63a0\u63a0\u9635\u7f62\uff01\u201d\u5434\u574e\u4f1a\u610f\uff0c\u70b9\u5934\u9053\uff1a\u201c\u4e0d\u9519\u3002\u54b1\u54e5\u513f\u4fe9\u7559\u70b9\u513f\u795e\uff0c\u522b\u8ba9\u4e09\u5e08\u5144\u5251\u4e0b\u4f24\u4eba\u3002\u201d\u4e24\u4eba\u4e00\u5de6\u4e00\u53f3\uff0c\u98d5\u98d5\u4e24\u5251\uff0c\u9f50\u5f80\u72c4\u4e91\u80c1\u4e0b\u523a\u53bb\u3002 \u3000\u3000\u72c4\u4e91\u7684\u5251\u6cd5\u672c\u6765\u4e5f\u6ca1\u6bd4\u4e07\u572d\u9ad8\u660e\u591a\u5c11\uff0c\u5168\u4ed7\u4e00\u9f13\u4f5c\u6c14\u7684\u731b\u653b\uff0c\u8fd9\u624d\u5360\u5f97\u4e86\u4e0a\u98ce\u3002\u535c\u57a3\u548c\u5434\u574e\u4e0a\u524d\u4e00\u5939\u653b\uff0c\u4ed6\u4ee5\u4e00\u654c\u4e09\uff0c\u767b\u65f6\u624b\u5fd9\u8db3\u4e71\uff0c\u5237\u7684\u4e00\u58f0\uff0c\u5de6\u817f\u4e0a\u53c8\u5df2\u4e2d\u5251\u3002\u8fd9\u4e00\u5251\u4f24\u5f97\u4e0d\u8f7b\uff0c\u4ed6\u518d\u4e5f\u7ad9\u7acb\u4e0d\u5b9a\uff0c\u4e00\u4ea4\u5750\u5012\uff0c\u624b\u4e0a\u957f\u5251\u5374\u5e76\u4e0d\u6454\u8131\uff0c\u4ecd\u662f\u4e0d\u4f4f\u6321\u683c\u4e09\u4eba\u523a\u6765\u7684\u5251\u62db\u3002\u9c81\u5764\u51b7\u54fc\u4e00\u58f0\uff0c\u62a2\u4e0a\u6765\u53f3\u8db3\u98de\u51fa\uff0c\u8e22\u4e2d\u4ed6\u7684\u624b\u8155\uff0c\u72c4\u4e91\u62ff\u634f\u4e0d\u4f4f\uff0c\u957f\u5251\u8131\u624b\u98de\u51fa\uff0c\u8dcc\u5165\u6811\u4e1b\u4e4b\u4e2d\u3002\u4e07\u572d\u957f\u5251\u76f4\u51fa\uff0c\u5251\u5c16\u62b5\u4f4f\u4e86\u4ed6\u54bd\u5589\u3002\u535c\u57a3\u548c\u5434\u574e\u54c8\u54c8\u4e00\u7b11\uff0c\u8dc3\u540e\u9000\u5f00\u3002 \u3000\u3000\u4e07\u572d\u5f97\u610f\u6d0b\u6d0b\u7684\u7b11\u9053\uff1a\u201c\u4e61\u4e0b\u4f6c\uff0c\u670d\u4e86\u4e48\uff1f\u201d\u72c4\u4e91\u559d\u9053\uff1a\u201c\u670d\u4f60\u4e2a\u5c41\uff01\u4f60\u4eec\u56db\u4e2a\u6253\u6211\u4e00\u4e2a\uff0c\u7b97\u4ec0\u4e48\u597d\u6c49\u5b50\uff1f\u201d\u4e07\u572d\u5251\u5c16\u5fae\u5fae\u5411\u524d\u4e00\u9001\uff0c\u9677\u5165\u4ed6\u54bd\u5589\u7684\u8f6f\u8089\u6570\u5206\uff0c\u559d\u9053\uff1a\u201c\u4f60\u8fd8\u6562\u5634\u786c\uff01 \u3000\u3000\u6211\u518d\u4f7f\u4e00\u70b9\u529b\uff0c\u7acb\u65f6\u5272\u65ad\u4e86\u4f60\u5589\u7ba1\u3002\u201d\u72c4\u4e91\u9a82\u9053\uff1a\u201c\u4f60\u4f7f\u529b\u554a\uff0c\u4f60\u6709\u79cd\u4fbf\u5272\u65ad\u6211\u5589\u7ba1\u3002\u4e0d\u4f7f\u529b\u7684\u662f\u4e4c\u9f9f\u738b\u516b\u86cb\u3002\u201d\u4e07\u572d\u76ee\u9732\u51f6\u5149\uff0c\u5de6\u8db3\u75be\u51fa\uff0c\u5728\u4ed6\u809a\u5b50\u4e0a\u91cd\u91cd\u8e22\u4e86\u4e00\u811a\uff0c\u9a82\u9053\uff1a\u201c\u81ed\u8d3c\uff0c\u4f60\u5634\u5df4\u8fd8\u786c\u4e0d\u786c\uff1f\u201d \u3000\u3000\u8fd9\u4e00\u811a\u53ea\u8e22\u5f97\u72c4\u4e91\u4e94\u810f\u516d\u8151\u72b9\u5982\u5012\u8f6c\u4e86\u4e00\u822c\uff0c\u9669\u4e9b\u547b\u541f\u51fa\u58f0\uff0c\u4f46\u54ac\u7259\u5f3a\u81ea\u5fcd\u4f4f\uff0c\u9a82\u9053\uff1a\u201c\u81ed\u6742\u79cd\uff0c\u738b\u516b\u86cb\uff01\u201d\u4e07\u572d\u53c8\u662f\u4e00\u811a\uff0c\u8fd9\u4e00\u6b21\u8e22\u5728\u4ed6\u7684\u9762\u95e8\u3002\u72c4\u4e91\u4f46\u89c9\u773c\u524d\u91d1\u661f\u4e71\u5192\uff0c\u51e0\u6b32\u6655\u53bb\uff0c\u6b32\u5f85\u5f20\u53e3\u518d\u9a82\uff0c\u5374\u9a82\u4e0d\u51fa\u58f0\u4e86\u3002 \u3000\u3000\u4e07\u572d\u51b7\u7b11\u9053\uff1a\u201c\u4eca\u65e5\u4fbf\u9976\u4e86\u4f60\u3002\u4f60\u5feb\u5411\u5e08\u7236\u5e08\u59b9\u54ed\u8bc9\u53bb\uff0c\u8bf4\u6211\u4eec\u4eba\u591a\u52bf\u4f17\uff0c\u6253\u4e86\u4f60\u5566\uff01\u6599\u4f60\u8fd9\u8113\u5305\u8d27\u5b9a\u8981\u53bb\u54ed\u54ed\u557c\u557c\u3002\u201d \u3000\u3000\u72c4\u4e91\u6012\u9053\uff1a\u201c\u54ed\u8bc9\u751a\u4e48\uff1f\u5927\u4e08\u592b\u62a5\u4ec7\uff0c\u53ea\u81ea\u5df1\u4e00\u4e2a\u513f\u52a8\u624b\u3002\u201d\u4e07\u572d\u6b63\u8981\u4ed6\u8bf4\u8fd9\u4e00\u53e5\u8bdd\u3002\u66f4\u6fc0\u4ed6\u9053\uff1a\u201c\u7ed9\u4f60\u8138\u4e0a\u7559\u4e9b\u8bb0\u8ba4\uff0c\u597d\u6559\u4f60\u5e08\u7236\u5f00\u53e3\u6765\u95ee\u3002\u201d\u8bf4\u7740\u5728\u4ed6\u5de6\u773c\u53f3\u8138\u91cd\u91cd\u7684\u5404\u8e22\u4e00\u811a\u3002\u72c4\u4e91\u767b\u65f6\u534a\u8fb9\u8138\u80bf\u4e86\u8d77\u6765\uff0c\u5de6\u773c\u6cea\u6c34\u6a21\u7cca\u3002 \u3000\u3000\u535c\u57a3\u62cd\u624b\u7b11\u9053\uff1a\u201c\u563f\u563f\uff0c\u5927\u4e08\u592b\u54ed\u5566\uff01\u82f1\u96c4\u53d8\u6210\u72d7\u718a\u5566\uff01\u201d \u3000\u3000\u72c4\u4e91\u6c14\u5f97\u809a\u5b50\u771f\u8981\u70b8\u4e86\u5f00\u6765\uff0c\u5fc3\u60f3\u4f60\u5230\u6211\u5e08\u7236\u5bb6\u91cc\u6765\uff0c\u6211\u597d\u597d\u7684\u62db\u5f85\u4e8e\u4f60\uff0c\u4e70\u9152\u6740\u9e21\uff0c\u54ea\u4e00\u70b9\u5bf9\u4f60\u4e0d\u8d77\uff0c\u6b64\u523b\u5374\u5982\u6b64\u635f\u6211\u3002 \u3000\u3000\u4e07\u572d\u9053\uff1a\u201c\u4f60\u6253\u4e0d\u8fc7\u6211\uff0c\u4e0d\u59a8\u53bb\u5411\u6211\u7239\u7239\u54ed\u8bc9\uff0c\u8981\u6211\u7239\u7239\u8d23\u7f5a\u6211\uff0c\u4ee3\u4f60\u51fa\u4e86\u8fd9\u53e3\u9e1f\u6c14\u3002\u2018\u545c\u545c\u545c\uff0c\u4e07\u5e08\u4f2f\uff0c\u4f60\u7684\u516b\u4e2a\u5f1f\u5b50\uff0c\u6253\u5f97\u6211\u722c\u5728\u5730\u4e0b\u558a\u54ed\u6c42\u9976\u3002\u545c\u545c\u545c\uff0c\u4e07\u5e08\u4f2f\uff0c\u4f60\u4e0d\u4e3b\u6301\u516c\u9053\u5417\uff1f\u2019\u201d\u72c4\u4e91\u9053\uff1a\u201c\u4f60\u8fd9\u79cd\u6ca1\u9aa8\u5934\u7684\u80da\u5b50\uff0c\u624d\u5411\u5927\u4eba\u54ed\u8bc9\uff01\u201d \u3000\u3000\u4e07\u572d\u548c\u9c81\u5764\u3001\u535c\u5766\u76f8\u89c6\u4e00\u7b11\uff0c\u5fc3\u60f3\u4eca\u65e5\u7684\u95f7\u6c14\u5df2\u51fa\uff0c\u5f53\u5373\u56de\u5251\u5165\u9798\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff01\u4f60\u6709\u79cd\u7684\u660e\u5929\u518d\u6765\u6253\u8fc7\uff0c\u5c11\u7237\u53ef\u8981\u5931\u966a\u4e86\uff01\u201d\u516b\u4e2a\u4eba\u563b\u563b\u54c8\u54c8\u7684\u626c\u957f\u800c\u53bb\u3002 \u3000\u3000\u72c4\u4e91\u77a7\u7740\u8fd9\u516b\u4e2a\u4eba\u7684\u80cc\u5f71\uff0c\u5fc3\u4e2d\u53c8\u662f\u6c14\u607c\uff0c\u53c8\u662f\u4e0d\u89e3\uff0c\u81ea\u5fd6\uff1a\u201c\u6211\u65e2\u6ca1\u5f97\u7f6a\u4ed6\u4eec\uff0c\u66f4\u6ca1\u5f97\u7f6a\u4ed6\u4eec\u5e08\u7236\uff0c\u4e3a\u751a\u4e48\u5e73\u767d\u65e0\u7aef\u7684\u6765\u6253\u6211\u4e00\u987f\uff1f\u96be\u9053\u57ce\u91cc\u4eba\u90fd\u8fd9\u822c\u86ee\u4e0d\u8bb2\u7406\u4e48\uff1f\u201d\u52c9\u5f3a\u652f\u6491\u7740\u7ad9\u8d77\u8eab\u6765\uff0c\u5934\u8111\u4e00\u6655\uff0c\u53c8\u5750\u5012\u5728\u5730\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u8eab\u540e\u4e00\u4eba\u5509\u58f0\u53f9\u6c14\u7684\u8bf4\u9053\uff1a\u201c\u5509\uff0c\u6253\u4e0d\u8fc7\u4eba\u5bb6\uff0c\u5c31\u8be5\u78d5\u5934\u6c42\u9976\u554a\uff0c\u8fd9\u4e48\u767d\u767d\u5730\u6328\u4e86\u4e00\u987f\u63cd\uff0c\u8fd9\u4e0d\u51a4\u4e48\uff1f\u201d\u72c4\u4e91\u6012\u9053\uff1a\u201c\u5b81\u53ef\u7ed9\u4eba\u5bb6\u6253\u6b7b\uff0c\u4e5f\u4e0d\u78d5\u5934\uff01\u201d\u56de\u8fc7\u5934\u6765\uff0c\u53ea\u89c1\u4e00\u4eba\u5f13\u8eab\u66f2\u80cc\uff0c\u62d6\u7740\u978b\u76ae\uff0c\u6162\u541e\u541e\u7684\u8d70\u6765\uff0c\u4f46\u89c1\u4ed6\u84ec\u5934\u57a2\u9762\uff0c\u4fbf\u662f\u65e5\u95f4\u6240\u89c1\u7684\u90a3\u4e2a\u8001\u4e10\u3002 \u3000\u3000\u90a3\u8001\u4e10\u9053\uff1a\u201c\u5509\uff0c\u4eba\u8001\u4e86\uff0c\u80cc\u4e0a\u98ce\u6e7f\u75db\u5f97\u5389\u5bb3\u3002\u5c0f\u4f19\u5b50\uff0c\u4f60\u7ed9\u6211\u80cc\u4e0a\u6376\u6376\u3002\u201d\u72c4\u4e91\u6b63\u4e00\u809a\u5b50\u706b\uff0c\u54fc\u4e86\u4e00\u58f0\uff0c\u6ca1\u53bb\u7406\u4ed6\u3002 \u3000\u3000\u90a3\u8001\u4e10\u53f9\u9053\uff1a\u201c\u8c01\u6559\u6211\u7edd\u5b50\u7edd\u5b59\uff0c\u4eba\u5230\u8001\u6765\uff0c\u6ca1\u4e00\u4e2a\u4eb2\u4eba\u7167\u987e\uff0c\u54ce\u5537\uff0c\u54ce\u5537\u2026\u2026\u201d\u6491\u7740\u7af9\u68d2\uff0c\u4e00\u6b65\u6b65\u7684\u8d70\u8fdc\u3002 \u3000\u3000\u72c4\u4e91\u89c1\u90a3\u8001\u4e10\u80cc\u5f71\u98a4\u6296\u5f97\u5389\u5bb3\uff0c\u81ea\u5df1\u521a\u7ed9\u4eba\u72e0\u72e0\u6253\u4e86\u4e00\u987f\uff0c\u4e0d\u7531\u5f97\u8d77\u4e86\u540c\u75c5\u76f8\u601c\u4e4b\u5fc3\uff0c\u53eb\u9053\uff1a\u201c\u5582\uff0c\u6211\u8fd9\u91cc\u8fd8\u6709\u51e0\u5341\u6587\u94b1\uff0c\u4f60\u62ff\u53bb\u4e70\u9992\u5934\u5403\u7f62\uff01\u201d \u3000\u3000\u90a3\u8001\u4e10\u4e00\u6b65\u6b65\u7684\u6328\u4e86\u56de\u6765\u3002\u63a5\u8fc7\u94dc\u94b1\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u80cc\u4e0a\u98ce\u6e7f\u75db\u5f97\u5389\u5bb3\uff0c\u4f60\u7ed9\u6211\u6376\u6376\uff01\u201d\u72c4\u4e91\u9053\uff1a\u201c\u597d\uff0c\u6211\u5305\u4e86\u817f\u4e0a\u7684\u4f24\u53e3\u518d\u8bf4\u3002\u201d\u90a3\u8001\u4e10\u9053\uff1a\u201c\u4f60\u5c31\u53ea\u987e\u81ea\u5df1\uff0c\u4e0d\u987e\u4eba\u5bb6\uff0c\u7b97\u751a\u4e48\u82f1\u96c4\u597d\u6c49\uff1f\u201d\u72c4\u4e91\u7ed9\u4ed6\u4e00\u6fc0\uff0c\u4fbf\u9053\uff1a\u201c\u597d\uff01\u6211\u7ed9\u4f60\u6376\uff01\u201d\u5750\u5012\u5728\u5730\uff0c\u4f38\u62f3\u7ed9\u4ed6\u6376\u80cc\u3002 \u3000\u3000\u6376\u5f97\u4e24\u62f3\uff0c\u90a3\u8001\u4e10\u9053\uff1a\u201c\u597d\u8212\u670d\uff0c\u597d\u8212\u670d\uff0c\u518d\u7528\u529b\u4e9b\uff01\u201d\u72c4\u4e91\u52a0\u4e86\u4e9b\u529b\u9053\u3002\u90a3\u8001\u4e10\u9053\uff1a\u201c\u53ef\u60dc\u529b\u9053\u592a\u8f7b\u3002\u201d\u72c4\u4e91\u53c8\u52a0\u91cd\u4e86\u4e9b\u3002\u8001\u4e10\u9053\uff1a\u201c\u5509\uff0c\u4e0d\u4e2d\u7528\u7684\u5c0f\u4f19\u5b50\u554a\uff0c\u6328\u4e86\u4e00\u987f\u63cd\uff0c\u4fbf\u6b7b\u6837\u6d3b\u6c14\uff0c\u8fde\u7ed9\u8001\u4eba\u5bb6\u6376\u6376\u80cc\u7684\u529b\u9053\u4e5f\u6ca1\u6709\u4e86\u3002\u4f60\u8fd9\u79cd\u4eba\u6d3b\u5728\u4e16\u4e0a\u6709\u751a\u4e48\u7528\uff1f\u201d \u3000\u3000\u72c4\u4e91\u6012\u9053\uff1a\u201c\u6211\u4e00\u4f7f\u529b\u6c14\uff0c\u53ea\u6015\u6253\u65ad\u4e86\u4f60\u7684\u8001\u9aa8\u5934\u3002\u201d\u8001\u4e10\u7b11\u9053\uff1a\u201c\u4f60\u8981\u662f\u6253\u5f97\u65ad\u6211\u7684\u8001\u9aa8\u5934\uff0c\u5c31\u4e0d\u4f1a\u8eba\u5728\u5730\u4e0b\u53c8\u7ed9\u4eba\u5bb6\u8e22\u3001\u53c8\u7ed9\u4eba\u5bb6\u63cd\u4e86\u3002\u201d\u72c4\u4e91\u5927\u6012\uff0c\u624b\u4e0a\u52a0\u529b\uff0c\u90a3\u8001\u4e10\u9053\uff1a\u201c\u55ef\uff0c\u8fd9\u6837\u624d\u6709\u4e9b\u610f\u601d\uff0c\u4e0d\u8fc7\u8fd8\u662f\u592a\u8f7b\u3002\u201d\u72c4\u4e91\u7830\u7684\u4e00\u62f3\uff0c\u4f7f\u52b2\u51fb\u51fa\u3002\u8001\u4e10\u7b11\u9053\uff1a\u201c\u592a\u8f7b\uff0c\u592a\u8f7b\uff0c\u4e0d\u7ba1\u7528\u3002\u201d \u3000\u3000\u72c4\u4e91\u9053\uff1a\u201c\u8001\u5934\u513f\uff0c\u4f60\u522b\u5f00\u73a9\u7b11\uff0c\u6211\u53ef\u4e0d\u60f3\u6253\u4f24\u4f60\u3002\u201d\u90a3\u8001\u4e10\u51b7\u7b11\u9053\uff1a\u201c\u51ed\u4f60\u4e5f\u6253\u5f97\u4f24\u6211\uff1f\u4f60\u4f7f\u8db3\u5168\u529b\uff0c\u6253\u6211\u4e00\u62f3\u8bd5\u8bd5\u3002\u201d \u3000\u3000\u72c4\u4e91\u53f3\u81c2\u8fd0\u52a8\uff0c\u5f85\u8981\u6325\u62f3\u5f80\u4ed6\u80cc\u4e0a\u51fb\u53bb\uff0c\u6708\u5149\u4e0b\u89c1\u5230\u4ed6\u8001\u6001\u9f99\u949f\u7684\u6a21\u6837\uff0c\u5fc3\u4e2d\u4e00\u8f6f\uff0c\u653e\u677e\u4e86\u52b2\u529b\uff0c\u8bf4\u9053\uff1a\u201c\u8c01\u6765\u8ddf\u4f60\u4e00\u822c\u89c1\u8bc6\uff01\u201d\u8f7b\u8f7b\u5728\u4ed6\u80cc\u4e0a\u6376\u4e86\u4e00\u4e0b\u3002 \u3000\u3000\u7a81\u7136\u4e4b\u95f4\uff0c\u53ea\u89c9\u8170\u95f4\u7ed9\u4eba\u4e00\u6258\u4e00\u6454\uff0c\u8eab\u5b50\u4fbf\u5982\u817e\u4e91\u9a7e\u96fe\u822c\u98de\u4e86\u8d77\u6765\uff0c\u7830\u7684\u4e00\u58f0\uff0c\u6454\u5165\u8349\u4e1b\u4e4b\u4e2d\uff0c\u53ea\u8dcc\u5f97\u5934\u6655\u773c\u82b1\uff0c\u8001\u534a\u5929\u624d\u722c\u8d77\u8eab\u3002\u4ed6\u6162\u6162\u6323\u624e\u7740\u7ad9\u8d77\uff0c\u5e76\u4e0d\u53d1\u6012\uff0c\u53ea\u662f\u8bf4\u4e0d\u51fa\u7684\u60ca\u5947\uff0c\u6014\u6014\u7684\u77a7\u7740\u8001\u4e10\uff0c\u9053\uff1a\u201c\u662f\u4f60\u2026\u2026\u662f\u4f60\u6454\u6211\u7684\u4e48\uff1f\u201d \u3000\u3000\u90a3\u8001\u4e10\u9053\uff1a\u201c\u8fd9\u91cc\u8fd8\u6709\u522b\u4eba\u6ca1\u6709\uff1f\u4e0d\u662f\u6211\u8fd8\u6709\u8c01\uff1f\u201d\u72c4\u4e91\u9053\uff1a\u201c\u4f60\u7528\u751a\u4e48\u6cd5\u5b50\u6454\u6211\u7684\uff1f\u201d\u90a3\u8001\u4e10\u9053\uff1a\u201c\u4e3e\u5934\u671b\u660e\u6708\uff0c\u4f4e\u5934\u601d\u6545\u4e61\u3002\u201d\u72c4\u4e91\u5947\u9053\uff1a\u201c\u8fd9\u662f\u5e08\u7236\u6559\u6211\u7684\u5251\u6cd5\u554a\uff0c\u4f60\u2026\u2026\u4f60\u600e\u77e5\u9053\uff1f\u201d\u90a3\u8001\u4e10\u9053\uff1a\u201c\u62f3\u62db\u5251\u6cd5\uff0c\u90fd\u662f\u4e00\u6837\u3002\u518d\u8bf4\uff0c\u4f60\u5e08\u7236\u4e5f\u6ca1\u6559\u5bf9\u3002\u201d \u3000\u3000\u72c4\u4e91\u6012\u9053\uff1a\u201c\u6211\u5e08\u7236\u6559\u5f97\u600e\u4e48\u4e0d\u5bf9\u4e86\uff1f\u51ed\u4f60\u8fd9\u8001\u53eb\u5316\u4e5f\u6562\u8bf4\u6211\u5e08\u7236\u7684\u4e0d\u662f\uff1f\u201d\u90a3\u8001\u4e10\u9053\uff1a\u201c\u8981\u662f\u4f60\u5e08\u7236\u6559\u5f97\u5bf9\u4e86\uff0c\u4e3a\u751a\u4e48\u4f60\u6253\u4e0d\u8fc7\u4eba\u5bb6\uff1f\u201d\u72c4\u4e91\u9053\uff1a\u201c\u4ed6\u4eec\u4e09\u56db\u4e2a\u6253\u6211\u4e00\u4e2a\uff0c\u6211\u81ea\u7136\u6253\u4e0d\u8fc7\uff0c\u82e5\u662f\u4e00\u4e2a\u5bf9\u4e00\u4e2a\uff0c\u4f60\u77a7\u6211\u8f93\u4e0d\u8f93\uff1f\u201d\u90a3\u8001\u4e10\u7b11\u9053\uff1a\u201c\u54c8\u54c8\uff0c\u6253\u67b6\u561b\uff0c\u8bb2\u751a\u4e48\u4e00\u4e2a\u6253\u4e00\u4e2a\uff1f\u4f60\u8981\u5355\u6253\u72ec\u6597\uff0c\u4eba\u5bb6\u4e0d\u5e72\uff0c\u90a3\u600e\u4e48\u529e\uff1f\u8981\u4e0d\u662f\u8dea\u4e0b\u78d5\u5934\uff0c\u5c31\u5f97\u8ba4\u547d\u6328\u6253\u3002\u4e00\u4e2a\u4eba\u6253\u5f97\u8d62\u5341\u4e2a\u516b\u4e2a\uff0c\u90a3\u624d\u662f\u597d\u6c49\u5b50\u3002\u201d\u72c4\u4e91\u5fc3\u60f3\u8fd9\u8bdd\u5012\u4e5f\u4e0d\u9519\uff0c\u8bf4\u9053\uff1a\u201c\u4ed6\u4eec\u662f\u6211\u5e08\u4f2f\u7684\u5f1f\u5b50\uff0c\u5251\u6cd5\u8ddf\u6211\u5dee\u4e0d\u591a\uff0c\u6211\u4e00\u4e2a\u600e\u6597\u5f97\u8fc7\u4ed6\u4eec\u516b\u4e2a\uff1f\u201d \u3000\u3000\u90a3\u8001\u4e10\u9053\uff1a\u201c\u6211\u6559\u4f60\u51e0\u624b\u529f\u592b\uff0c\u8ba9\u4f60\u4e00\u4e2a\u6253\u8d62\u4ed6\u4eec\u516b\u4e2a\uff0c\u4f60\u5b66\u4e0d\u5b66\uff1f\u201d \u3000\u3000\u72c4\u4e91\u5927\u559c\uff0c\u9053\uff1a\u201c\u6211\u5b66\uff0c\u6211\u5b66\uff01\u201d\u4f46\u8f6c\u5ff5\u4e00\u60f3\uff0c\u4e16\u4e0a\u672a\u5fc5\u6709\u8fd9\u79cd\u672c\u9886\uff0c\u800c\u8fd9\u5e74\u7eaa\u8001\u8fc8\u7684\u4e5e\u4e10\u66f4\u52a0\u4e0d\u4f3c\u8eab\u6709\u4e0a\u4e58\u6b66\u529f\u4e4b\u4eba\uff0c\u6b63\u81ea\u8e0c\u8e87\u4e0d\u5b9a\uff0c\u7a81\u7136\u80cc\u5fc3\u7ed9\u4eba\u4e00\u6293\uff0c\u8eab\u5b50\u53c8\u98de\u4e86\u8d77\u6765\uff0c\u8fd9\u6b21\u5728\u7a7a\u4e2d\u8eab\u4e0d\u7531\u4e3b\u7684\u8fde\u7ffb\u4e86\u4e24\u4e2a\u7b4b\u6597\uff0c\u98de\u5f97\u9ad8\uff0c\u843d\u4e0b\u6765\u65f6\u8dcc\u5f97\u66f4\u91cd\uff0c\u624b\u81c2\u5728\u5730\u4e0b\u4e00\u6491\uff0c\u5173\u8282\u9669\u4e9b\u6298\u65ad\uff0c\u722c\u8d77\u8eab\u6765\u65f6\uff0c\u75db\u5f97\u8bdd\u4e5f\u8bf4\u4e0d\u51fa\u6765\uff0c\u5fc3\u4e2d\u5374\u662f\u6b22\u559c\u65e0\u6bd4\uff0c\u53eb\u9053\uff1a\u201c\u8001\u2026\u2026\u8001\u4f2f\u4f2f\uff0c\u6211\u2026\u2026\u6211\u8ddf\u4f60\u5b66\u3002\u201d \u3000\u3000\u90a3\u8001\u4e10\u9053\uff1a\u201c\u6211\u4eca\u5929\u6559\u4f60\u51e0\u62db\uff0c\u660e\u513f\u665a\u4e0a\uff0c\u4f60\u518d\u8ddf\u4ed6\u4eec\u5230\u8fd9\u91cc\u6765\u6253\u8fc7\uff0c\u4f60\u6562\u4e0d\u6562\uff1f\u201d \u3000\u3000\u72c4\u4e91\u5fc3\u60f3\uff1a\u201c\u4f60\u6b66\u529f\u867d\u9ad8\uff0c\u6211\u5728\u4e00\u5929\u4e4b\u5185\u53c8\u5982\u4f55\u5b66\u5f97\u4f1a\uff1f\u201d \u3000\u3000\u4f46\u60f3\u5230\u8981\u8ddf\u4e07\u572d\u3001\u9c81\u5764\u8fd9\u5e72\u4eba\u518d\u6253\uff0c\u4e0d\u7531\u5f97\u8c6a\u6c14\u52c3\u53d1\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u6562\uff01\u6700\u591a\u518d\u6328\u4e00\u987f\u63cd\uff0c\u6709\u751a\u4e48\u5927\u4e0d\u4e86\uff01\u201d \u3000\u3000\u90a3\u8001\u4e10\u5de6\u624b\u500f\u51fa\uff0c\u6293\u4f4f\u4ed6\u540e\u9888\uff0c\u5c06\u4ed6\u91cd\u91cd\u5f80\u5730\u4e0b\u4e00\u63b7\uff0c\u9a82\u9053\uff1a\u201c\u81ed\u5c0f\u5b50\uff0c\u6211\u65e2\u6559\u4e86\u4f60\u6b66\u529f\uff0c\u4f60\u600e\u4e48\u8fd8\u4f1a\u6328\u4ed6\u4eec\u7684\u63cd\uff1f\u4f60\u4fe1\u4e0d\u8fc7\u6211\u4e48\uff1f\u201d\u72c4\u4e91\u867d\u7136\u6454\u5f97\u751a\u75db\uff0c\u5fc3\u4e2d\u53ea\u6709\u66f4\u52a0\u6b22\u559c\uff0c\u5fd9\u9053\uff1a\u201c\u5bf9\uff0c\u5bf9\uff01\u662f\u6211\u8bf4\u9519\u4e86\uff0c\u8bf7\u4f60\u8001\u4eba\u5bb6\u5feb\u6559\u7f62\uff01\u201d \u3000\u3000\u90a3\u8001\u4e10\u9053\uff1a\u201c\u4f60\u628a\u5b66\u8fc7\u7684\u5251\u6cd5\u4f7f\u7ed9\u6211\u77a7\uff0c\u4e00\u9762\u4f7f\uff0c\u4e00\u9762\u5ff5\u5251\u62db\u7684\u540d\u79f0\uff01\u201d \u3000\u3000\u72c4\u4e91\u5e94\u9053\uff1a\u201c\u662f\uff01\u201d\u89c1\u817f\u4e0a\u4f24\u5904\u4e0d\u65ad\u6d41\u8840\uff0c\u4fbf\u8349\u8349\u88f9\u597d\u4f24\u53e3\uff0c\u5230\u8349\u4e1b\u4e2d\u627e\u5230\u81ea\u5df1\u957f\u5251\uff0c\u4f9d\u7740\u5e08\u7236\u6240\u6388\uff0c\u4e00\u62db\u62db\u7684\u4f7f\u52a8\uff0c\u53e3\u4e2d\u5ff5\u7740\u5251\u62db\u540d\u79f0\uff0c\u5230\u540e\u6765\u8d8a\u4f7f\u8d8a\u987a\uff0c\u5634\u91cc\u4e5f\u8d8a\u5ff5\u8d8a\u5feb\u3002 \u3000\u3000\u4ed6\u6b63\u7ec3\u5230\u9163\u5904\uff0c\u5ffd\u542c\u90a3\u8001\u4e10\u54c8\u54c8\u5927\u7b11\uff0c\u4e0d\u7981\u6115\u7136\u6536\u5251\uff0c\u95ee\u9053\uff1a\u201c\u6211\u7ec3\u5f97\u4e0d\u5bf9\u4e48\uff1f\u201d\u90a3\u8001\u4e10\u4e0d\u7b54\uff0c\u5140\u81ea\u6367\u4f4f\u809a\u5b50\uff0c\u7b11\u5f2f\u4e86\u8170\uff0c\u7ad9\u4e0d\u76f4\u8eab\u5b50\u3002\u72c4\u4e91\u5fae\u6709\u6012\u610f\uff0c\u9053\uff1a\u201c\u5c31\u7b97\u6211\u7ec3\u5f97\u4e0d\u5bf9\uff0c\u4e5f\u6ca1\u751a\u4e48\u597d\u7b11\u3002\u201d \u3000\u3000\u90a3\u8001\u4e10\u7a81\u7136\u6b62\u7b11\uff0c\u53f9\u9053\uff1a\u201c\u621a\u957f\u53d1\u554a\u621a\u957f\u53d1\uff0c\u4f60\u8fd9\u4e00\u756a\u72e0\u52b2\uff0c\u5f53\u771f\u4e86\u5f97\u3002\u201d\u6447\u4e86\u6447\u5934\uff0c\u9053\uff1a\u201c\u628a\u5251\u7ed9\u6211\u3002\u201d\u72c4\u4e91\u5012\u8f6c\u5251\u67c4\uff0c\u9012\u4e86\u8fc7\u53bb\u3002\u90a3\u8001\u4e10\u63a5\u8fc7\u957f\u5251\uff0c\u8f7b\u8f7b\u5ff5\u9053\uff1a\u201c\u5b64\u9e3f\u6d77\u4e0a\u6765\uff0c\u6c60\u6f62\u4e0d\u6562\u987e\u3002\u201d\u5c06\u957f\u5251\u821e\u4e86\u5f00\u6765\u3002\u4ed6\u4e00\u5251\u5728\u624b\uff0c\u970e\u65f6\u4e4b\u95f4\u4fbf\u5982\u6362\u4e86\u4e00\u4e2a\u4eba\u4e00\u822c\uff0c\u8eab\u5f62\u6c89\u7a33\uff0c\u5251\u52bf\u98d8\u9038\uff0c\u54ea\u91cc\u8fd8\u662f\u9002\u624d\u8fd9\u822c\u9f99\u949f\u59d4\u7410\uff1f \u3000\u3000\u72c4\u4e91\u770b\u4e86\u51e0\u62db\uff0c\u5ffd\u6709\u6240\u609f\uff0c\u8bf4\u9053\uff1a\u201c\u8001\u4f2f\uff0c\u65e5\u91cc\u6211\u8ddf\u90a3\u5415\u901a\u76f8\u6597\uff0c\u662f\u4f60\u6545\u610f\u63b7\u90a3\u996d\u7897\u5e2e\u6211\u7684\u4e48\uff1f\u201d\u90a3\u8001\u4e10\u6012\u9053\uff1a\u201c\u90a3\u8fd8\u7528\u8bf4\uff1f\u516d\u5408\u624b\u5415\u901a\u7684\u6b66\u529f\u6bd4\u4f60\u50bb\u5c0f\u5b50\u5f3a\u5f97\u592a\u591a\u3002\u51ed\u4f60\u8fd9\u70b9\u513f\u9053\u884c\uff0c\u771f\u80fd\u6253\u53d1\u4ed6\u4e86\uff1f\u201d \u3000\u3000\u4ed6\u4e00\u9762\u8bf4\uff0c\u4e00\u9762\u7ee7\u7eed\u4f7f\u5251\u3002\u72c4\u4e91\u542c\u4ed6\u6240\u5ff5\u53e3\u8bc0\u548c\u5e08\u7236\u6240\u6388\u5e76\u65e0\u5206\u522b\uff0c\u53ea\u5b57\u97f3\u5076\u6709\u5dee\u5f02\uff0c\u4f46\u5251\u62db\u5374\u5927\u4e0d\u76f8\u540c\uff0c\u8d8a\u770b\u8d8a\u611f\u5947\u602a\u3002 \u3000\u3000\u90a3\u8001\u4e10\u5de6\u624b\u634f\u4e2a\u5251\u8bc0\uff0c\u53f3\u624b\u957f\u5251\u9661\u7136\u9012\u51fa\uff0c\u731b\u5730\u91cc\u5251\u4ea4\u5de6\u624b\uff0c\u53f3\u624b\u53cd\u8fc7\u6765\u62cd\u7684\u4e00\u58f0\uff0c\u91cd\u91cd\u6253\u4e86\u4ed6\u4e00\u4e2a\u8033\u5149\u3002\u72c4\u4e91\u5413\u4e86\u4e00\u8df3\uff0c\u629a\u7740\u9762\u988a\u6012\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u4e3a\u751a\u4e48\u6253\u4eba\uff1f\u201d\u8001\u4e10\u7b11\u9053\uff1a\u201c\u6211\u6559\u4f60\u5251\u62db\uff0c\u4f60\u5374\u5728\u80e1\u601d\u4e71\u60f3\uff0c\u8fd9\u4e0d\u8be5\u6253\u4e48\uff1f\u201d \u3000\u3000\u72c4\u4e91\u5fc3\u60f3\u539f\u662f\u81ea\u5df1\u7684\u4e0d\u662f\uff0c\u5f53\u5373\u5fc3\u5e73\u6c14\u548c\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u662f\u6211\u4e0d\u597d\u3002\u6211\u77a7\u4f60\u8bf4\u7684\u62db\u6570\u8ddf\u6211\u5e08\u7236\u4e00\u6837\uff0c\u5251\u6cd5\u53ef\u5168\u7136\u4e0d\u540c\uff0c\u89c9\u5f97\u5f88\u662f\u5947\u602a\u3002\u201d \u3000\u3000\u90a3\u8001\u4e10\u95ee\u9053\uff1a\u201c\u662f\u4f60\u5e08\u7236\u6559\u7684\u597d\uff0c\u8fd8\u662f\u6211\u4f7f\u7684\u597d\uff1f\u201d\u72c4\u4e91\u6447\u5934\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u9053\u3002\u201d\u8001\u4e10\u5c06\u957f\u5251\u629b\u8fd8\u7ed9\u4ed6\uff0c\u9053\uff1a\u201c\u54b1\u4eec\u6bd4\u5212\u6bd4\u5212\u3002\u201d\u72c4\u4e91\u9053\uff1a\u201c\u6211\u672c\u4e8b\u8ddf\u4f60\u8001\u4eba\u5bb6\u5dee\u5f97\u592a\u8fdc\uff0c\u6bd4\u4f60\u4e0d\u8fc7\u3002\u201d \u3000\u3000\u8001\u4e10\u51b7\u7b11\u9053\uff1a\u201c\u563f\uff0c\u50bb\u5c0f\u5c0f\u5b50\u8fd8\u6ca1\u50bb\u5f97\u5230\u5bb6\u3002\u8fd9\u6837\u7f62\uff0c\u54b1\u4eec\u53ea\u6bd4\u62db\u5f0f\uff0c\u4e0d\u6bd4\u529f\u529b\u3002\u201d\u624b\u4e2d\u7af9\u68d2\u4e00\u6296\uff0c\u4ee5\u68d2\u4f5c\u5251\uff0c\u5411\u72c4\u4e91\u523a\u6765\u3002 \u3000\u3000\u72c4\u4e91\u6a2a\u5251\u6321\u683c\uff0c\u89c1\u8001\u4e10\u7af9\u68d2\u505c\u6ede\u4e0d\u524d\uff0c\u5f53\u5373\u632f\u5251\u53cd\u523a\u3002\u54ea\u77e5\u4ed6\u5251\u5c16\u53ea\u4e00\u6296\u95f4\uff0c\u8001\u4e10\u7684\u7af9\u68d2\u5982\u6bd2\u86c7\u66b4\u8d77\uff0c\u5411\u524d\u4e00\u63a2\uff0c\u5df2\u70b9\u4e2d\u4e86\u4ed6\u80a9\u5934\u3002 \u3000\u3000\u72c4\u4e91\u5fc3\u60a6\u8bda\u670d\uff0c\u5927\u53eb\uff1a\u201c\u5999\u6781\uff0c\u5999\u6781\u3002\u201d\u6a2a\u5251\u524d\u524a\u3002\u90a3\u8001\u4e10\u7ffb\u8fc7\u7af9\u68d2\uff0c\u5e73\u9760\u4ed6\u5251\u8eab\uff0c\u72c4\u4e91\u8fd0\u52b2\u53cd\u63a8\uff0c\u90a3\u8001\u4e10\u7684\u7af9\u68d2\u8fde\u8f6c\u51e0\u4e2a\u5708\u5b50\uff0c\u5c06\u4ed6\u52b2\u529b\u5168\u5f15\u5230\u4e86\u76f8\u53cd\u7684\u65b9\u5411\u3002\u72c4\u4e91\u62ff\u634f\u4e0d\u4f4f\uff0c\u957f\u5251\u8131\u624b\u98de\u51fa\u3002\u4ed6\u5446\u4e86\u4e00\u5446\uff0c\u8bf4\u9053\uff1a\u201c\u8001\u4f2f\uff0c\u4f60\u7684\u5251\u62db\u771f\u9ad8\u3002\u201d \u3000\u3000\u90a3\u8001\u4e10\u7af9\u68d2\u4e00\u4f38\uff0c\u642d\u4f4f\u7a7a\u4e2d\u843d\u4e0b\u7684\u957f\u5251\uff0c\u68d2\u7aef\u5982\u6709\u80f6\u6c34\uff0c\u7adf\u5c06\u957f\u5251\u7c98\u4e86\u56de\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u5e08\u7236\u4e00\u8eab\u597d\u6b66\u529f\uff0c\u5c31\u53ea\u6559\u4e86\u4f60\u8fd9\u4e9b\u5417\uff1f\u563f\u563f\uff0c\u5e0c\u5947\u53e4\u602a\u3002\u201d\u6447\u6447\u5934\u53c8\u9053\uff1a\u201c\u4f60\u95e8\u4e2d\u8fd9\u5957\u2018\u5510\u8bd7\u5251\u6cd5\u2019\uff0c\u6bcf\u4e00\u62db\u90fd\u662f\u4ece\u4e00\u53e5\u5510\u8bd7\u4e2d\u5316\u51fa\u6765\u7684\u2026\u2026\u201d \u3000\u3000\u72c4\u4e91\u9053\uff1a\u201c\u751a\u4e48\u2018\u5510\u8bd7\u5251\u6cd5\u2019\uff1f\u5e08\u7236\u8bf4\u662f\u2018\u8eba\u5c38\u5251\u6cd5\u2019\uff0c\u51e0\u5251\u51fa\u53bb\uff0c\u654c\u4eba\u4fbf\u8eba\u4e0b\u53d8\u6210\u4e86\u5c38\u9996\u3002\u201d \u3000\u3000\u90a3\u8001\u4e10\u563f\u563f\u7b11\u4e86\u51e0\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u662f\u2018\u5510\u8bd7\uff0c\u4e0d\u662f\u2018\u8eba\u5c38\u2019\uff01 \u3000\u3000\u4f60\u5e08\u7236\u8ddf\u4f60\u8bf4\u662f\u2018\u8eba\u5c38\u2019\u5417\uff1f\u53ef\u7b11\uff0c\u53ef\u7b11\uff01\u8fd9\u4e24\u62db\u2018\u5b64\u9e3f\u6d77\u4e0a\u6765\uff0c\u6c60\u6f62\u4e0d\u6562\u987e\u2019\uff0c\u662f\u8bf4\u4e00\u53ea\u5b64\u5b64\u5355\u5355\u7684\u9e3f\u9e1f\uff0c\u4ece\u6d77\u4e0a\u98de\u6765\uff0c\u89c1\u5230\u9646\u5730\u4e0a\u7684\u5c0f\u5c0f\u6c60\u6cbc\uff0c\u5e76\u4e0d\u6816\u606f\u3002\u8fd9\u4e24\u53e5\u8bd7\u662f\u5510\u671d\u7684\u5bb0\u76f8\u5f20\u4e5d\u9f84\u505a\u7684\uff0c\u4ed6\u6bd4\u62df\u81ea\u5df1\u8eab\u4efd\u6e05\u9ad8\uff0c\u4e0d\u559c\u8ddf\u4eba\u4e89\u6743\u593a\u5229\u3002\u5c06\u4e4b\u5316\u6210\u5251\u6cd5\uff0c\u987e\u76fc\u4e4b\u9645\u8981\u6709\u4e00\u80a1\u98d8\u9038\u81ea\u8c6a\u7684\u6c14\u606f\u3002\u4ed6\u6240\u8c13\u2018\u4e0d\u6562\u987e\u2019\uff0c\u662f\u2018\u4e0d\u5c51\u77a7\u5b83\u4e00\u773c\u2019\u7684\u610f\u601d\u3002\u4f60\u5e08\u7236\u5374\u6559\u4f60\u8bfb\u4f5c\u751a\u4e48\u2018\u54e5\u7fc1\u558a\u4e0a\u6765\uff0c\u662f\u6a2a\u4e0d\u6562\u8fc7\u2019\uff0c\u7ed3\u679c\u524d\u4e00\u53e5\u53d8\u6210\u5927\u58f0\u75be\u547c\uff0c\u540e\u4e00\u53e5\u6210\u4e3a\u754f\u9996\u754f\u5c3e\u3002\u5251\u6cd5\u7684\u539f\u610f\u662f\u8361\u7136\u65e0\u5b58\u4e86\u3002\u4f60\u5e08\u7236\u5f53\u771f\u4e86\u4e0d\u8d77\uff0c\u2018\u94c1\u9501\u6a2a\u6c5f\u2019\uff0c\u6559\u5f92\u5f1f\u8fd9\u6837\u6559\u6cd5\uff0c\u563f\u563f\uff0c\u5389\u5bb3\uff0c\u5389\u5bb3\uff01\u201d\u8bf4\u7740\u8fde\u8fde\u51b7\u7b11\u3002 \u3000\u3000\u72c4\u4e91\u6014\u6014\u7684\u542c\u7740\uff0c\u542c\u5f97\u4ed6\u8bdd\u4e2d\u54ac\u6587\u56bc\u5b57\uff0c\u867d\u7136\u4e0d\u5927\u61c2\uff0c\u5374\u4e5f\u77e5\u4ed6\u8bf4\u5f97\u5f88\u5bf9\uff0c\u72c4\u4e91\u5411\u6765\u656c\u7231\u5e08\u7236\uff0c\u542c\u4ed6\u5c06\u5e08\u7236\u8bf4\u5f97\u4e00\u65e0\u662f\u5904\uff0c\u5230\u540e\u6765\u66f4\u8086\u610f\u8ba5\u5632\uff0c\u5fc3\u4e0b\u96be\u8fc7\uff0c\u5ffd\u5730\u8f6c\u8eab\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u8981\u53bb\u7761\u4e86\uff01\u4e0d\u5b66\u4e86\u3002\u201d \u3000\u3000\u90a3\u8001\u4e10\u5947\u9053\uff1a\u201c\u4e3a\u751a\u4e48\uff1f\u6211\u8bf4\u5f97\u4e0d\u5bf9\u4e48\uff1f\u201d\u72c4\u4e91\u9053\uff1a\u201c\u4f60\u6216\u8bb8\u8bf4\u5f97\u5f88\u5bf9\u3002\u4f46\u4f60\u8bf4\u6211\u5e08\u7236\u7684\u4e0d\u662f\uff0c\u6211\u5b81\u53ef\u4e0d\u5b66\u3002\u6211\u5e08\u7236\u662f\u5e84\u7a3c\u4eba\uff0c\u4e0d\u8bc6\u5b57\uff0c\u4e0d\u61c2\u4f60\u8bf4\u7684\u90a3\u4e00\u5957\u4e5f\u662f\u6709\u7684\u2026\u2026\u201d\u90a3\u8001\u4e10\u7b11\u9053\uff1a\u201c\u4f60\u5e08\u7236\u4e0d\u8bc6\u5b57\uff1f\u54c8\u54c8\uff0c\u8fd9\u53ef\u5947\u4e86\u3002\u201d\u72c4\u4e91\u6c14\u6124\u6124\u7684\u9053\uff1a\u201c\u5e84\u7a3c\u4eba\u4e0d\u8bc6\u5b57\uff0c\u6709\u751a\u4e48\u597d\u7b11\uff1f\u201d\u90a3\u8001\u4e10\u54c8\u54c8\u4e00\u7b11\uff0c\u4f38\u624b\u629a\u4ed6\u5934\u9876\uff0c\u9053\uff1a\u201c\u5f88\u597d\uff0c\u5f88\u597d\uff01\u4f60\u8fd9\u5c0f\u5b50\u5fc3\u5730\u539a\u9053\uff0c\u6211\u5c31\u662f\u559c\u6b22\u4f60\u8fd9\u79cd\u4eba\u3002\u6211\u5411\u4f60\u8ba4\u9519\uff0c\u4ece\u6b64\u4e0d\u518d\u8bf4\u4f60\u5e08\u7236\u534a\u53e5\u4e0d\u662f\uff0c\u884c\u4e0d\u884c\uff1f\u201d \u3000\u3000\u72c4\u4e91\u8f6c\u6012\u4e3a\u559c\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u53ea\u8981\u4e0d\u8bf4\u6211\u5e08\u7236\uff0c\u6211\u5411\u4f60\u78d5\u5934\u4e5f\u6210\u3002\u201d \u3000\u3000\u8bf4\u7740\u8dea\u5012\u5728\u5730\uff0c\u549a\u549a\u549a\u7684\u78d5\u4e86\u51e0\u4e2a\u54cd\u5934\u3002 \u3000\u3000\u90a3\u8001\u4e10\u7b11\u541f\u541f\u7684\u53d7\u4e86\u4ed6\u8fd9\u51e0\u62dc\uff0c\u968f\u5373\u89e3\u91ca\u5251\u62db\uff0c\u5982\u4f55\u201c\u5ffd\u542c\u55b7\u60ca\u98ce\uff0c\u8fde\u5c71\u77f3\u5e03\u9003\u201d\uff0c\u5176\u5b9e\u662f\u201c\u4fef\u542c\u95fb\u60ca\u98ce\uff0c\u8fde\u5c71\u82e5\u6ce2\u6d9b\u201d\uff1b\u5982\u4f55\u201c\u843d\u6ce5\u62db\u5927\u59d0\uff0c\u9a6c\u547d\u98ce\u5c0f\u5c0f\u201d\uff0c\u4e43\u662f\u201c\u843d\u65e5\u7167\u5927\u65d7\uff0c\u9a6c\u9e23\u98ce\u8427\u8427\u201d\u3002\u5728\u6e58\u897f\u571f\u97f3\u4e2d\uff0c\u8fd9\u201c\u6ce5\u201d\u5b57\u548c\u201c\u65e5\u201d\u5b57\u5374\u4e5f\u5dee\u4e0d\u591a\u3002\u90a3\u8001\u4e10\u8a00\u8bed\u4e4b\u4e2d\uff0c\u5f53\u771f\u518d\u4e5f\u4e0d\u63d0\u621a\u957f\u53d1\u534a\u53e5\uff0c\u5355\u662f\u7ea0\u6b63\u72c4\u4e91\u5251\u6cd5\u4e2d\u7684\u9519\u5931\u3002 \u3000\u3000\u90a3\u8001\u4e10\u9053\uff1a\u201c\u4f60\u5251\u6cd5\u4e2d\u83ab\u540d\u5176\u5999\u7684\u4e1c\u897f\u592a\u591a\uff0c\u4e00\u65f6\u4e5f\u8bf4\u4e0d\u5b8c\u3002\u6211\u6559\u4f60\u4e09\u62db\u529f\u592b\uff0c\u660e\u513f\u4f60\u518d\u8ddf\u8fd9\u516b\u4e2a\u4e0d\u6210\u5668\u7684\u5c0f\u5b50\u6253\u8fc7\uff0c\u7528\u5fc3\u8bb0\u4f4f\u4e86\u3002\u201d \u3000\u3000\u72c4\u4e91\u7cbe\u795e\u4e00\u632f\uff0c\u7528\u5fc3\u77a7\u90a3\u8001\u4e10\u4f7f\u7af9\u68d2\u6bd4\u5212\u3002\u7b2c\u4e00\u62db\u662f\u201c\u523a\u80a9\u5f0f\u201d\uff0c\u654c\u4eba\u82e5\u662f\u4e00\u5473\u9632\u5b88\uff0c\u90a3\u662f\u6c38\u8fdc\u523a\u4ed6\u4e0d\u7740\uff0c\u4f46\u53ea\u8981\u4e00\u51fa\u5251\u76f8\u653b\uff0c\u7acb\u65f6\u4fbf\u53ef\u540e\u53d1\u5148\u81f3\uff0c\u523a\u4e2d\u4ed6\u7684\u7709\u5934\u3002\u7b2c\u4e8c\u62db\u201c\u8033\u5149\u5f0f\u201d\uff0c\u4fbf\u662f\u90a3\u8001\u4e10\u9002\u624d\u5251\u4ea4\u5de6\u624b\u3001\u53f3\u624b\u53cd\u6253\u4ed6\u8033\u5149\u7684\u8fd9\u4e00\u62db\u3002\u8fd9\u4e00\u62db\u53e4\u602a\u65e0\u6bd4\uff0c\u5c31\u7b97\u654c\u4eba\u660e\u77e5\u81ea\u5df1\u8981\u5251\u4ea4\u5de6\u624b\uff0c\u53cd\u624b\u6253\u4ed6\u8033\u5149\uff0c\u4f46\u95ea\u5de6\u6253\u5de6\uff0c\u95ea\u53f3\u6253\u53f3\uff0c\u8d8a\u662f\u95ea\u907f\uff0c\u8d8a\u6253\u5f97\u91cd\u3002 \u3000\u3000\u7b2c\u4e09\u62db\u662f\u201c\u53bb\u5251\u5f0f\u201d\uff0c\u9002\u624d\u8001\u4e10\u7528\u7af9\u68d2\u4ee4\u4ed6\u957f\u5251\u8131\u624b\uff0c\u4fbf\u662f\u8fd9\u4e00\u62db\u3002 \u3000\u3000\u8fd9\u4e09\u8bb0\u62db\u5f0f\uff0c\u90a3\u8001\u4e10\u90fd\u66fe\u5728\u72c4\u4e91\u8eab\u4e0a\u7528\u8fc7\uff0c\u672c\u6765\u5404\u6709\u4e00\u4e2a\u5178\u96c5\u7684\u5510\u8bd7\u540d\u79f0\u3002\u4f46\u90a3\u8001\u4e10\u77e5\u9053\u4ed6\u897f\u74dc\u5927\u7684\u5b57\u8bc6\u4e0d\u4e0a\u51e0\u62c5\uff0c\u6559\u4ed6\u8bd7\u53e5\uff0c\u5f92\u4e71\u5fc3\u795e\uff0c\u4e8e\u662f\u6539\u7528\u4e86\u4e09\u4e2a\u4e00\u542c\u4fbf\u61c2\u7684\u540d\u79f0\u3002 \u3000\u3000\u72c4\u4e91\u5e76\u4e0d\u5982\u4f55\u806a\u660e\uff0c\u6027\u5b50\u5374\u6781\u575a\u6bc5\u3002\u8fd9\u4e09\u62db\u8db3\u8db3\u5b66\u4e86\u4e00\u4e2a\u591a\u65f6\u8fb0\uff0c\u65b9\u59cb\u7eaf\u719f\u3002 \u3000\u3000\u90a3\u8001\u4e10\u7b11\u9053\uff1a\u201c\u597d\u5566\uff01\u4f60\u5f97\u7b54\u5e94\u6211\u4e00\u4ef6\u4e8b\uff0c\u4eca\u665a\u6211\u6559\u4f60\u5251\u6cd5\u4e4b\u4e8b\uff0c\u4e0d\u5f97\u8ddf\u8c01\u8bf4\u8d77\uff0c\u8fde\u4f60\u5e08\u7236\u548c\u5e08\u59b9\u4e5f\u4e0d\u80fd\u8bf4\u3002\u5426\u5219\u2026\u2026\u201d\u72c4\u4e91\u656c\u5e08\u5982\u7236\uff0c\u5bf9\u8fd9\u4f4d\u5a07\u61a8\u7f8e\u8c8c\u7684\u5e08\u59b9\u53c8\u662f\u79c1\u604b\u5df2\u4e45\uff0c\u8bf4\u6709\u751a\u4e48\u4e8b\u8981\u7792\u4f4f\u5e08\u7236\u3001\u5e08\u59b9\uff0c\u90a3\u53ef\u6bd4\u751a\u4e48\u90fd\u96be\uff0c\u4e00\u65f6\u8e0c\u8e87\u4e0d\u7b54\u3002 \u3000\u3000\u90a3\u8001\u4e10\u53f9\u9053\uff1a\u201c\u6b64\u4e2d\u7f18\u7531\uff0c\u4e00\u65f6\u4e0d\u4fbf\u7ec6\u8bf4\uff0c\u4f60\u82e5\u6cc4\u9732\u4e86\u4eca\u665a\u4e4b\u4e8b\uff0c\u6211\u6027\u547d\u96be\u4fdd\uff0c\u5b9a\u8981\u6b7b\u5728\u4e94\u4e91\u624b\u4e07\u9707\u5c71\u7684\u5251\u5e95\u3002\u201d\u72c4\u4e91\u5403\u4e86\u4e00\u60ca\uff0c\u5947\u9053\uff1a\u201c\u8001\u4f2f\u4f2f\uff0c\u4f60\u6b66\u529f\u8fd9\u4e48\u9ad8\u5f3a\uff0c\u600e\u4f1a\u6015\u6211\u5e08\u4f2f\uff1f\u201d \u3000\u3000\u90a3\u8001\u4e10\u4e0d\u7b54\uff0c\u626c\u957f\u4fbf\u53bb\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u662f\u5426\u6709\u5fc3\u5bb3\u6211\uff0c\u90a3\u5168\u77a7\u4f60\u81ea\u5df1\u4e86\u3002\u201d \u3000\u3000\u72c4\u4e91\u5fd9\u8ffd\u4e86\u4e0a\u53bb\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u591a\u8c22\u8001\u4f2f\u4f2f\u8fd8\u6765\u4e0d\u53ca\uff0c\u600e\u4f1a\u5bb3\u4f60\u6027\u547d\uff1f\u6211\u8981\u662f\u6cc4\u6f0f\u4e00\u5b57\u534a\u53e5\uff0c\u6559\u6211\u5929\u8bdb\u5730\u706d\u3002\u201d\u90a3\u8001\u4e10\u53f9\u4e86\u53e3\u6c14\uff0c\u8db3\u4e0d\u505c\u6b65\u7684\u8d70\u4e86\u3002 \u3000\u3000\u72c4\u4e91\u5446\u4e86\u4e00\u9635\uff0c\u5ffd\u7136\u60f3\u8d77\u6ca1\u95ee\u90a3\u8001\u4e10\u7684\u59d3\u540d\uff0c\u53eb\u9053\uff1a\u201c\u8001\u4f2f\u4f2f\uff0c\u8001\u4f2f\u4f2f\uff01\u201d\u4f46\u90a3\u8001\u4e10\u6ca1\u5165\u6811\u4e1b\u4e4b\u4e2d\uff0c\u5df2\u7136\u5f71\u8e2a\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u6b21\u65e5\u6e05\u6668\uff0c\u621a\u957f\u53d1\u89c1\u72c4\u4e91\u76ee\u9752\u9f3b\u80bf\uff0c\u597d\u751f\u5947\u602a\uff0c\u95ee\u9053\uff1a\u201c\u8ddf\u8c01\u6253\u67b6\u4e86\uff0c\u600e\u4e48\u4f24\u6210\u8fd9\u4e2a\u6837\u5b50\uff1f\u201d\u72c4\u4e91\u4e0d\u5584\u8bf4\u8c0e\uff0c\u652f\u543e\u96be\u7b54\u3002\u621a\u82b3\u7b11\u9053\uff1a\u201c\u8fd8\u4e0d\u662f\u6628\u5929\u7ed9\u90a3\u4e2a\u751a\u4e48\u5927\u76d7\u5415\u901a\u6253\u7684\u4e48\uff1f\u201d\u621a\u957f\u53d1\u51b3\u8ba1\u60f3\u4e0d\u5230\u6628\u665a\u4e4b\u4e8b\uff0c\u4e5f\u4e0d\u518d\u95ee\u3002 \u3000\u3000\u621a\u82b3\u62c9\u4e86\u62c9\u72c4\u4e91\u7684\u8863\u895f\uff0c\u4e24\u4eba\u4ece\u8fb9\u95e8\u51fa\u53bb\uff0c\u6765\u5230\u4e00\u53e3\u4e95\u8fb9\uff0c\u89c1\u56db\u4e0b\u65e0\u4eba\uff0c\u4fbf\u5728\u4e95\u680f\u5708\u4e0a\u5750\u4e86\u4e0b\u6765\u3002\u621a\u82b3\u95ee\u9053\uff1a\u201c\u5e08\u54e5\uff0c\u4f60\u6628\u665a\u8ddf\u8c01\u6253\u67b6\u4e86\uff1f\u201d\u72c4\u4e91\u55eb\u5685\u672a\u7b54\u3002\u621a\u82b3\u9053\uff1a\u201c\u4f60\u4e0d\u7528\u7792\u6211\uff0c\u6628\u5929\u4f60\u8ddf\u5415\u901a\u76f8\u6597\uff0c\u4ed6\u4e00\u62f3\u4e00\u811a\u6253\u5728\u4f60\u8eab\u4e0a\u751a\u4e48\u5730\u65b9\uff0c\u6211\u5168\u77a7\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u4ed6\u53ef\u6ca1\u6253\u4e2d\u4f60\u7684\u773c\u775b\u3002\u201d\u72c4\u4e91\u6599\u77e5\u7792\u5979\u4e0d\u8fc7\uff0c\u5fc3\u60f3\uff1a\u201c\u6211\u53ea\u8981\u4e0d\u8bf4\u90a3\u8001\u4f2f\u4f2f\u7684\u4e8b\uff0c\u5c31\u4e0d\u8981\u7d27\u3002\u201d\u4e8e\u662f\u5c06\u4e07\u95e8\u516b\u5f1f\u5b50\u5982\u4f55\u534a\u591c\u91cc\u524d\u6765\u5bfb\u8845\u3001\u5982\u4f55\u6bd4\u5251\u3001\u5982\u4f55\u843d\u8d25\u53d7\u8fb1\u7684\u4e8b\u4e00\u4e00\u90fd\u8bf4\u4e86\u3002 \u3000\u3000\u621a\u82b3\u8d8a\u542c\u8d8a\u6012\uff0c\u4e00\u5f20\u4fcf\u8138\u6da8\u5f97\u901a\u7ea2\uff0c\u6c14\u6124\u6124\u7684\u9053\uff1a\u201c\u4ed6\u4eec\u516b\u4e2a\u4eba\u6253\u4f60\u4e00\u4e2a\uff0c\u7b97\u751a\u4e48\u597d\u6c49\uff1f\u201d\u72c4\u4e91\u9053\uff1a\u201c\u5012\u4e0d\u662f\u516b\u4e2a\u4eba\u4e00\u9f50\u51fa\u624b\uff0c\u662f\u4e09\u56db\u4e2a\u6253\u6211\u4e00\u4e2a\u3002\u201d\u621a\u82b3\u6012\u9053\uff1a\u201c\u54fc\uff0c\u4ed6\u4eec\u4e09\u56db\u4e2a\u8054\u624b\u6253\u4f60\uff0c\u5df2\u7ecf\u8d62\u4e86\uff0c\u5176\u4f59\u7684\u5c31\u4e0d\u5fc5\u52a8\u624b\u3002\u5018\u82e5\u4e09\u56db\u4e2a\u6253\u4e0d\u8fc7\uff0c\u8fd8\u4e0d\u662f\u4e94\u516d\u4e2a\u3001\u4e03\u516b\u4e2a\u4e00\u8d77\u4e0b\u573a\u3002\u201d\u72c4\u4e91\u70b9\u5934\u9053\uff1a\u201c\u90a3\u591a\u534a\u4f1a\u8fd9\u6837\u3002\u201d \u3000\u3000\u621a\u82b3\u970d\u5730\u7ad9\u8d77\uff0c\u9053\uff1a\u201c\u54b1\u4eec\u8ddf\u7239\u7239\u8bf4\u53bb\uff0c\u6559\u4e07\u9707\u5c71\u8bc4\u8bc4\u8fd9\u4e2a\u7406\u770b\u3002\u201d\u5979\u76db\u6012\u4e4b\u4e0b\uff0c\u8fde\u201c\u4e07\u5e08\u4f2f\u201d\u4e5f\u4e0d\u79f0\u4e86\uff0c\u7adf\u662f\u76f4\u547c\u5176\u540d\u3002 \u3000\u3000\u72c4\u4e91\u5fd9\u9053\uff1a\u201c\u4e0d\uff0c\u6211\u6253\u67b6\u6253\u8f93\u4e86\uff0c\u5411\u5e08\u7236\u8bc9\u82e6\uff0c\u90a3\u4e0d\u662f\u6559\u4eba\u77a7\u4e0d\u8d77\u5417\uff1f\u201d \u3000\u3000\u621a\u82b3\u54fc\u4e86\u4e00\u58f0\uff0c\u89c1\u4ed6\u8863\u886b\u7834\u635f\u751a\u591a\uff0c\u5fc3\u4e0b\u75db\u60dc\uff0c\u4ece\u6000\u4e2d\u53d6\u51fa\u9488\u7ebf\u5305\uff0c\u5c31\u5728\u4ed6\u8eab\u4e0a\u7f1d\u8865\u3002\u5979\u5934\u53d1\u64e6\u5728\u72c4\u4e91\u4e0b\u5df4\uff0c\u72c4\u4e91\u53ea\u89c9\u75d2\u75d2\u7684\uff0c\u9f3b\u4e2d\u95fb\u5230\u5979\u5c11\u5973\u7684\u6de1\u6de1\u808c\u80a4\u4e4b\u9999\uff0c\u4e0d\u7531\u5f97\u5fc3\u795e\u8361\u6f3e\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5e08\u59b9\uff01\u201d\u621a\u82b3\u9053\uff1a\u201c\u7a7a\u5fc3\u83dc\uff0c\u522b\u8bf4\u8bdd\uff01\u522b\u8ba9\u4eba\u51a4\u6789\u4f60\u4f5c\u8d3c\u3002\u201d \u3000\u3000\u6c5f\u5357\u4e09\u6e58\u4e00\u5e26\u6c11\u95f4\u8ff7\u4fe1\uff0c\u7a7f\u7740\u8863\u886b\u8ba9\u4eba\u7f1d\u8865\u6216\u9489\u7f00\u94ae\u6263\u4e4b\u65f6\uff0c\u82e5\u662f\u8bf4\u4e86\u8bdd\uff0c\u5c31\u4f1a\u7ed9\u4eba\u51a4\u8d56\u5077\u4e1c\u897f\u3002\u201c\u7a7a\u5fc3\u83dc\u201d\u5374\u662f\u621a\u82b3\u7ed9\u72c4\u4e91\u53d6\u7684\u7ef0\u53f7\uff0c\u7b11\u4ed6\u76f4\u809a\u76f4\u80a0\uff0c\u6ca1\u534a\u70b9\u673a\u5fc3\u3002 \u3000\u3000\u8fd9\u65e5\u665a\u95f4\uff0c\u4e07\u9707\u5c71\u5728\u5385\u4e0a\u8bbe\u4e86\u7b75\u5e2d\u5bb4\u8bf7\u5e08\u5f1f\uff0c\u516b\u4e2a\u95e8\u4e0b\u5f1f\u5b50\u5728\u4e0b\u9996\u76f8\u966a\uff0c\u5341\u4e8c\u4eba\u56e2\u56e2\u5750\u4e86\u4e00\u5f20\u5706\u684c\u3002 \u3000\u3000\u9152\u8fc7\u4e09\u5de1\uff0c\u4e07\u9707\u5c71\u89c1\u72c4\u4e91\u5634\u5507\u9ad8\u9ad8\u80bf\u8d77\uff0c\u996e\u98df\u4e0d\u4fbf\uff0c\u8bf4\u9053\uff1a\u201c\u72c4\u8d24\u4f84\uff0c\u6628\u513f\u8f9b\u82e6\u4e86\u4f60\uff0c\u6765\u6765\u6765\uff0c\u591a\u5403\u4e00\u70b9\u3002\u201d\u631f\u4e86\u4e00\u53ea\u9e21\u817f\uff0c\u653e\u5728\u4ed6\u789f\u4e2d\u3002\u5468\u573b\u9f3b\u4e2d\u7a81\u7136\u54fc\u7684\u4e00\u58f0\u3002 \u3000\u3000\u621a\u82b3\u65e9\u5df2\u6ee1\u809a\u662f\u706b\uff0c\u8fd9\u65f6\u518d\u4e5f\u5fcd\u8010\u4e0d\u4f4f\uff0c\u5927\u58f0\u9053\uff1a\u201c\u4e07\u5e08\u4f2f\uff0c\u6211\u5e08\u54e5\u8fd9\u4e9b\u4f24\uff0c\u4e0d\u662f\u5415\u901a\u6253\u7684\uff0c\u662f\u4f60\u516b\u4e2a\u9ad8\u5f92\u8054\u624b\u6253\u7684\u3002\u201d \u3000\u3000\u4e07\u9707\u5c71\u548c\u621a\u957f\u53d1\u540c\u65f6\u5403\u4e86\u4e00\u60ca\uff0c\u95ee\u9053\uff1a\u201c\u751a\u4e48\uff1f\u201d \u3000\u3000\u4e07\u95e8\u7b2c\u516b\u5f1f\u5b50\u6c88\u57ce\u5e74\u7eaa\u6700\u5c0f\uff0c\u5374\u5341\u5206\u4f36\u7259\u4fd0\u9f7f\uff0c\u62a2\u7740\u8bf4\u9053\uff1a\u201c\u72c4\u5e08\u54e5\u6253\u8d62\u4e86\u5415\u901a\uff0c\u8bf4\u5e08\u7236\u4f60\u8001\u4eba\u5bb6\u80c6\u5c0f\u6015\u4e8b\uff0c\u4e0d\u6562\u548c\u5415\u901a\u52a8\u624b\uff0c\u5168\u9760\u4ed6\u72c4\u5e08\u54e5\u51fa\u9a6c\uff0c\u624d\u8d76\u8d70\u4e86\u4ed6\uff0c\u6ca1\u8ba9\u4f60\u8001\u4eba\u5bb6\u51fa\u4e11\u3002\u6211\u4eec\u6c14\u4e0d\u8fc7\u2026\u2026\u201d\u4e07\u9707\u5c71\u8138\u4e0a\u53d8\u8272\uff0c\u4f46\u968f\u5373\u7b11\u9053\uff1a\u201c\u662f\u554a\uff0c\u8fd9\u539f\u662f\u5168\u4ed7\u72c4\u8d24\u4f84\u66ff\u6211\u4eec\u633d\u56de\u4e86\u989c\u9762\u3002\u201d\u6c88\u57ce\u9053\uff1a\u201c\u4e07\u5e08\u54e5\u542c\u4ed6\u53e3\u51fa\u72c2\u8a00\uff0c\u5b9e\u5728\u6c14\u4e0d\u8fc7\uff0c\u8fd9\u624d\u7ea6\u72c4\u5e08\u54e5\u6bd4\u5251\uff0c\u597d\u50cf\u662f\u4e07\u5e08\u54e5\u5360\u4e86\u5148\u3002\u201d \u3000\u3000\u72c4\u4e91\u6012\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u80e1\u8bf4\u516b\u9053\u2026\u2026\u6211\u2026\u2026\u6211\u51e0\u65f6\u2026\u2026\u201d \u3000\u3000\u4ed6\u672c\u5c31\u4e0d\u5584\u8a00\u8f9e\uff0c\u542c\u5f97\u6c88\u57ce\u6492\u8c0e\u8bec\u8511\uff0c\u53c8\u6025\u53c8\u6012\u4e4b\u4e0b\uff0c\u66f4\u662f\u7ed3\u7ed3\u5df4\u5df4\u7684\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u4e07\u9707\u5c71\u9053\uff1a\u201c\u600e\u4e48\u662f\u572d\u513f\u50cf\u5360\u4e86\u5148\uff1f\u201d\u6c88\u57ce\u9053\uff1a\u201c\u6628\u665a\u4e07\u5e08\u54e5\u548c\u72c4\u5e08\u54e5\u600e\u4e48\u6bd4\u5251\uff0c\u6211\u4eec\u90fd\u6ca1\u77a7\u89c1\u3002\u4eca\u5929\u65e9\u6668\u4e07\u5e08\u54e5\u8ddf\u5927\u4f19\u8bf4\u8d77\uff0c\u597d\u50cf\u662f\u4e07\u5e08\u54e5\u7528\u4e00\u62db\u2026\u2026\u7528\u4e00\u62db\u2026\u2026\u201d\u4ed6\u8f6c\u5934\u95ee\u4e07\u572d\u9053\uff1a\u201c\u4e07\u5e08\u54e5\uff0c\u4f60\u7528\u4e00\u62db\u751a\u4e48\u62db\u6570\u80dc\u4e86\u72c4\u5e08\u54e5\u7684\uff1f\u201d\u4e07\u572d\u9053\uff1a\u201c\u662f\u2018\u957f\u5b89\u4e00\u7247\u6708\uff0c\u4e07\u6237\u64e3\u8863\u58f0\u2019\uff01\u201d\u4ed6\u4e8c\u4eba\u4e00\u642d\u4e00\u6321\uff0c\u5c06\u201c\u516b\u4eba\u8054\u624b\u201d\u4e4b\u4e8b\u63a8\u4e86\u4e2a\u4e00\u5e72\u4e8c\u51c0\u3002\u4e07\u572d\u600e\u6837\u80dc\u4e86\u72c4\u4e91\uff0c\u65c1\u4eba\u89c1\u90fd\u6ca1\u89c1\u5230\uff0c\u81ea\u7136\u8c08\u4e0d\u4e0a\u8054\u624b\u76f8\u653b\u4e86\u3002\u6c88\u57ce\u4e0d\u8fc7\u5341\u4e94\u516d\u5c81\u5e74\u7eaa\uff0c\u4e00\u526f\u5929\u771f\u70c2\u6f2b\u7684\u6837\u5b50\uff0c\u8c01\u90fd\u4e0d\u4fe1\u4ed6\u4f1a\u6492\u8c0e\u3002 \u3000\u3000\u4e07\u9707\u5c71\u70b9\u4e86\u70b9\u5934\uff0c\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\u3002\u201d \u3000\u3000\u621a\u957f\u53d1\u6c14\u5f97\u6ee1\u8138\u901a\u7ea2\uff0c\u4f38\u624b\u4e00\u62cd\u684c\u5b50\uff0c\u559d\u9053\uff1a\u201c\u4e91\u513f\uff0c\u6211\u5343\u53ee\u4e07\u5631\uff0c\u53eb\u4f60\u4e0d\u53ef\u548c\u4e07\u5e08\u4f2f\u95e8\u4e0b\u4f17\u5e08\u5144\u5931\u4e86\u548c\u6c14\uff0c\u600e\u5730\u6253\u8d77\u67b6\u6765\u4e86\u3002\u201d \u3000\u3000\u72c4\u4e91\u542c\u5f97\u8fde\u5e08\u7236\u4e5f\u4fe1\u4e86\u6c88\u57ce\u7684\u8bdd\uff0c\u53ea\u6c14\u5f97\u6d51\u8eab\u53d1\u6296\uff0c\u9053\uff1a\u201c\u5e08\u7236\u2026\u2026\u6211\u2026\u2026\u6211\u2026\u2026\u6211\u6ca1\u6709\u2026\u2026\u201d\u621a\u957f\u53d1\u5288\u5934\u5288\u8138\u4e00\u8bb0\u8033\u5149\u6253\u4e86\u8fc7\u53bb\uff0c\u559d\u9053\uff1a\u201c\u505a\u9519\u4e86\u4e8b\uff0c\u8fd8\u8981\u62b5\u8d56\uff01\u201d\u72c4\u4e91\u4e0d\u6562\u95ea\u907f\uff0c\u621a\u957f\u53d1\u8fd9\u4e00\u638c\u6253\u5f97\u597d\u91cd\uff0c\u72c4\u4e91\u8138\u988a\u672c\u5c31\u9752\u80bf\uff0c\u767b\u65f6\u80bf\u4e0a\u52a0\u80bf\u3002 \u3000\u3000\u621a\u82b3\u6025\u53eb\uff1a\u201c\u7239\uff0c\u4f60\u4e5f\u4e0d\u95ee\u95ee\u6e05\u695a\u3002\u201d \u3000\u3000\u72c4\u4e91\u72c2\u6012\u4e4b\u4e0b\uff0c\u725b\u813e\u6c14\u53d1\u4f5c\uff0c\u7a81\u7136\u7eb5\u8eab\u8df3\u8d77\uff0c\u62a2\u8fc7\u653e\u5728\u8eab\u540e\u51e0\u4e0a\u7684\u957f\u5251\uff0c\u62d4\u5251\u51fa\u9798\uff0c\u8dc3\u5728\u5385\u5fc3\uff0c\u53eb\u9053\uff1a\u201c\u5e08\u7236\uff0c\u8fd9\u4e07\u2026\u2026\u4e07\u572d\u8bf4\u6253\u8d25\u4e86\u6211\uff0c\u6559\u4ed6\u518d\u6253\u6253\u770b\u3002\u201d\u621a\u957f\u53d1\u5927\u6012\uff0c\u559d\u9053\uff1a\u201c\u4f60\u56de\u4e0d\u56de\u6765\uff1f\u201d\u79bb\u5ea7\u51fa\u53bb\uff0c\u53c8\u8981\u6325\u62f3\u6bb4\u51fb\u3002\u621a\u82b3\u4e00\u628a\u62c9\u4f4f\uff0c\u53eb\u9053\uff1a\u201c\u7239\u7239\uff01\u201d \u3000\u3000\u72c4\u4e91\u5927\u53eb\uff1a\u201c\u4f60\u4eec\u516b\u4e2a\u4eba\u518d\u6765\u6253\u6211\uff0c\u6709\u79cd\u7684\u5c31\u4e00\u9f50\u6765\u3002\u54ea\u4e00\u4e2a\u4e0d\u6765\uff0c\u4fbf\u662f\u4e4c\u9f9f\u513f\u5b50\u72d7\u6742\u79cd\u3002\u201d\u4ed6\u6025\u6012\u4e4b\u4e0b\uff0c\u53e3\u4e0d\u62e9\u8a00\uff0c\u4e71\u9a82\u8d77\u6765\u3002 \u3000\u3000\u4e07\u9707\u5c71\u7709\u5934\u4e00\u76b1\uff0c\u8bf4\u9053\uff1a\u201c\u65e2\u662f\u5982\u6b64\uff0c\u4f60\u4eec\u53bb\u9886\u6559\u9886\u6559\u72c4\u5e08\u54e5\u7684\u5251\u6cd5\u4e5f\u662f\u597d\u7684\u3002\u201d \u3000\u3000\u516b\u540d\u5f1f\u5b50\u5df4\u4e0d\u5f97\u5e08\u7236\u6709\u8fd9\u53e5\u8bdd\uff0c\u5404\u4eba\u63d0\u8d77\u957f\u5251\uff0c\u5206\u5360\u516b\u65b9\uff0c\u5c06\u72c4\u4e91\u56f4\u5728\u5793\u5fc3\u3002 \u3000\u3000\u72c4\u4e91\u5927\u58f0\u53eb\u9053\uff1a\u201c\u6628\u513f\u665a\u4e0a\u662f\u516b\u4e2a\u72d7\u6742\u79cd\u6253\u6211\u4e00\u4eba\uff0c\u4eca\u65e5\u53c8\u662f\u516b\u4e2a\u72d7\u6742\u79cd\u2026\u2026\u201d \u3000\u3000\u621a\u957f\u53d1\u559d\u9053\uff1a\u201c\u4e91\u513f\uff0c\u4f60\u80e1\u8bf4\u4e9b\u751a\u4e48\uff1f\u6bd4\u5251\u5c31\u6bd4\u5251\uff0c\u662f\u6bd4\u5634\u4e0a\u4f36\u4fd0\u4e48\uff1f\u201d \u3000\u3000\u4e07\u9707\u5c71\u542c\u4ed6\u5de6\u4e00\u53e5\u201c\u72d7\u6742\u79cd\u201d\uff0c\u53f3\u4e00\u53e5\u201c\u72d7\u6742\u79cd\u201d\uff0c\u5fc3\u4e0b\u4e5f\u52a8\u4e86\u771f\u6012\uff0c\u8fd9\u516b\u4eba\u4e2d\u7684\u4e07\u572d\u662f\u4ed6\u4eb2\u751f\u513f\u5b50\uff0c\u72c4\u4e91\u5982\u6b64\u4e71\u9a82\uff0c\u53e3\u53e3\u58f0\u58f0\u4fbf\u662f\u9a82\u5728\u4ed6\u7684\u5934\u4e0a\u3002\u4ed6\u89c1\u516b\u4e2a\u5f1f\u5b50\u5206\u7ad9\u516b\u65b9\uff0c\u9690\u7136\u6709\u5206\u8fdb\u5408\u51fb\u4e4b\u52bf\uff0c\u559d\u9053\uff1a\u201c\u72c4\u5e08\u5144\u77a7\u4e0d\u8d77\u54b1\u4eec\uff0c\u8981\u4ee5\u4e00\u4e2a\u6597\u516b\u4e2a\uff0c\u96be\u9053\u54b1\u4eec\u81ea\u5df1\u4e5f\u77a7\u4e0d\u8d77\u81ea\u5df1\uff1f\u201d \u3000\u3000\u5927\u5f1f\u5b50\u9c81\u5764\u9053\uff1a\u201c\u662f\uff0c\u4f17\u4f4d\u5e08\u5f1f\u9000\u5f00\uff0c\u8ba9\u6211\u5148\u9886\u6559\u72c4\u5e08\u54e5\u7684\u9ad8\u62db\u3002\u201d \u3000\u3000\u4e94\u5f1f\u5b50\u535c\u57a3\u6781\u5de5\u5fc3\u8ba1\uff0c\u6628\u665a\u89c1\u5230\u72c4\u4e91\u4e0e\u4e07\u572d\u52a8\u624b\uff0c\u8fd9\u4e61\u4e0b\u4f6c\u6b66\u529f\u4e0d\u5f31\uff0c\u8fd9\u65f6\u60c5\u6025\u62da\u547d\uff0c\u5927\u5e08\u5144\u672a\u5fc5\u80fd\u80dc\uff0c\u5982\u88ab\u4ed6\u5148\u8d62\u5f97\u4e00\u4ed7\uff0c\u7eb5\u7136\u518d\u6709\u4eba\u5c06\u4ed6\u6253\u8d25\uff0c\u4e5f\u5df2\u6298\u4e86\u4e07\u95e8\u9510\u6c14\uff0c\u540c\u95e8\u4e2d\u5251\u672f\u4ee5\u56db\u5e08\u5144\u5b59\u5747\u4e3a\u7b2c\u4e00\uff0c\u6700\u597d\u8ba9\u5b59\u5747\u4e00\u4e0a\u624b\u4fbf\u5c06\u4ed6\u6253\u8d25\uff0c\u4ee4\u4ed6\u518d\u4e5f\u8bf4\u5634\u4e0d\u5f97\uff0c\u4fbf\u9053\uff1a\u201c\u5927\u5e08\u54e5\u662f\u54b1\u4eec\u540c\u95e8\u8868\u7387\uff0c\u4f55\u5fc5\u4eb2\u81ea\u51fa\u9a6c\uff1f\u8ba9\u56db\u5e08\u54e5\u6559\u8bad\u6559\u8bad\u4ed6\u4e5f\u5c31\u662f\u4e86\u3002\u201d \u3000\u3000\u9c81\u5764\u4e00\u542c\uff0c\u5df2\u660e\u5176\u610f\uff0c\u5fae\u7b11\u9053\uff1a\u201c\u597d\uff0c\u56db\u5e08\u5f1f\uff0c\u54b1\u4eec\u77a7\u4f60\u7684\u4e86\u3002\u201d\u5de6\u624b\u4e00\u6325\uff0c\u4e03\u4eba\u4e00\u9f50\u9000\u5f00\uff0c\u53ea\u5269\u5b59\u5747\u4e00\u4eba\u548c\u72c4\u4e91\u76f8\u5bf9\u3002 \u3000\u3000\u5b59\u5747\u6c89\u9ed8\u5be1\u8a00\uff0c\u5e38\u5e38\u6574\u5929\u4e0d\u8bf4\u4e00\u53e5\u8bdd\uff0c\u662f\u4ee5\u80fd\u6f5c\u5fc3\u5411\u5b66\uff0c\u5251\u6cd5\u5728\u516b\u540c\u95e8\u4e2d\u6700\u5f3a\u3002\u4ed6\u89c1\u5e08\u5144\u5f1f\u63a8\u5df1\u51fa\u9a6c\uff0c\u5f53\u5373\u957f\u5251\u4e00\u7acb\uff0c\u4f4e\u5934\u8eac\u8eab\uff0c\u8fd9\u4e00\u62db\u53eb\u505a\u201c\u4e07\u56fd\u4ef0\u5b97\u5468\uff0c\u8863\u51a0\u62dc\u5195\u65d2\u201d\uff0c\u4e43\u662f\u6781\u5177\u793c\u656c\u7684\u8d77\u624b\u5251\u62db\u3002\u4f46\u5f53\u5e74\u621a\u957f\u53d1\u5411\u72c4\u4e91\u8bf4\u5251\u4e4b\u65f6\uff0c\u5374\u5c06\u8fd9\u62db\u7684\u540d\u79f0\u8bf4\u505a\u201c\u996d\u89d2\u8ba9\u7cbd\u81ed\uff0c\u4e00\u5b98\u62dc\u9a6c\u7334\u201d\u3002\u610f\u601d\u662f\u8bf4\uff1a\u201c\u6211\u662f\u597d\u597d\u7684\u5927\u7c73\u996d\uff0c\u4f60\u662f\u4e00\u53ea\u81ed\u7cbd\u5b50\uff0c\u5916\u8868\u4e0a\u8ba9\u4f60\u4e00\u4e0b\uff0c\u606d\u656c\u4f60\u4e00\u4e0b\uff0c\u6211\u5fc3\u91cc\u53ef\u5728\u9a82\u4f60\uff01\u6211\u662f\u5b98\uff0c\u4f60\u662f\u7334\u5b50\uff0c\u6211\u62dc\u4f60\uff0c\u662f\u5b98\u62dc\u755c\u751f\u3002\u201d\u72c4\u4e91\u89c1\u4ed6\u65bd\u51fa\u8fd9\u4e00\u62db\uff0c\u5fc3\u4e0b\u66f4\u6012\uff0c\u5f53\u4e0b\u4e5f\u662f\u957f\u5251\u4e00\u7acb\uff0c\u4f4e\u5934\u8eac\u8eab\uff0c\u8fd8\u4e86\u4ed6\u4e00\u62db\u201c\u996d\u89d2\u8ba9\u68d5\u81ed\uff0c\u4e00\u5b98\u62dc\u9a6c\u7334\u201d\uff0c\u9488\u950b\u76f8\u5bf9\uff0c\u6beb\u4e0d\u793a\u5f31\u3002 \u3000\u3000\u4ed6\u53ea\u8fd9\u4e48\u4e00\u8eac\u8eab\uff0c\u8eab\u5b50\u5c1a\u672a\u7ad9\u76f4\uff0c\u957f\u5251\u5251\u5c16\u5df2\u5411\u5b59\u5747\u5c0f\u8179\u4e0a\u523a\u4e86\u8fc7\u53bb\u3002\u4e07\u95e8\u7fa4\u5f1f\u5b50\u9f50\u58f0\u60ca\u547c\u3002\u5b59\u5747\u56de\u5251\u683c\u6321\uff0c\u94ee\u7684\u4e00\u58f0\uff0c\u53cc\u5251\u76f8\u51fb\uff0c\u4e24\u4eba\u624b\u81c2\u4e0a\u5404\u662f\u4e00\u9ebb\u3002 \u3000\u3000\u9c81\u5764\u9053\uff1a\u201c\u5e08\u7236\uff0c\u4f60\u77a7\u8fd9\u5c0f\u5b50\u4e0b\u624b\u72e0\u4e0d\u72e0\uff1f\u4ed6\u7b80\u76f4\u662f\u8981\u5b59\u5e08\u5f1f\u7684\u547d\u554a\u3002\u201d\u4e07\u9707\u5c71\u5fc3\u4e0b\u6697\u6697\u60ca\u5f02\uff1a\u201c\u8fd9\u4e61\u4e0b\u5c0f\u5b50\u5e72\u4e48\u5982\u6b64\u6124\u6fc0\uff0c\u4e00\u4e0a\u6765\u5c31\u662f\u62da\u547d\uff1f\u201d \u3000\u3000\u4f46\u542c\u5f97\u94ee\u94ee\u94ee\u94ee\u6570\u58f0\u8fde\u54cd\uff0c\u72c4\u4e91\u548c\u5b59\u5747\u5feb\u5251\u76f8\u640f\uff0c\u62c6\u5230\u5341\u4f59\u62db\u540e\uff0c\u5b59\u5747\u957f\u5251\u4e00\u659c\uff0c\u5c0f\u8179\u95f4\u9732\u51fa\u7834\u7efd\u3002\u72c4\u4e91\u5927\u559d\u4e00\u58f0\uff0c\u633a\u5251\u76f4\u8fdb\uff0c\u5b59\u5747\u56de\u8fc7\u957f\u5251\u3001\u5df2\u5c06\u4ed6\u957f\u5251\u538b\u4f4f\uff0c\u62cd\u7684\u4e00\u638c\uff0c\u6b63\u51fb\u5728\u4ed6\u80f8\u53e3\u3002\u4e07\u95e8\u7fa4\u5f1f\u5b50\u9f50\u58f0\u559d\u91c7\uff0c\u6709\u4eba\u53eb\u4e86\u8d77\u6765\uff1a\u201c\u4e00\u4e2a\u4e5f\u6253\u4e0d\u8fc7\uff0c\u8fd8\u5439\u5927\u6c14\u6253\u516b\u4e2a\u4e48\uff1f\u201d\u72c4\u4e91\u8eab\u5b50\u4e00\u6643\uff0c\u62bd\u8d77\u957f\u5251\uff0c\u72b9\u5982\u75be\u98ce\u9aa4\u96e8\u822c\u4e00\u9635\u731b\u653b\u3002\u5b59\u5747\u6321\u5f97\u51e0\u62db\uff0c\u53d1\u5251\u56de\u653b\uff0c\u72c4\u4e91\u7a81\u7136\u95f4\u957f\u5251\u6296\u52a8\uff0c\u5657\u7684\u4e00\u58f0\u8f7b\u54cd\uff0c\u5df2\u523a\u5165\u4e86\u5b59\u5747\u7684\u80a9\u5934\uff0c\u6b63\u662f\u90a3\u8001\u4e10\u6240\u6388\u7684\u201c\u523a\u80a9\u5f0f\u201d\u3002 \u3000\u3000\u8fd9\u4e00\u62db\u201c\u523a\u80a9\u5f0f\u201d\u7a81\u5982\u5176\u6765\uff0c\u8c01\u4e5f\u6599\u60f3\u4e0d\u5230\u3002\u4f46\u89c1\u5b59\u5747\u80a9\u5934\u9c9c\u8840\u957f\u6d41\uff0c\u8eab\u5b50\u6447\u6643\uff0c\u4e07\u95e8\u7fa4\u5f1f\u5b50\u9f50\u58f0\u547c\u559d\u3002\u9c81\u5764\u548c\u5468\u573b\u53cc\u5251\u9f50\u51fa\uff0c\u5411\u72c4\u4e91\u653b\u4e86\u4e0a\u53bb\u3002\u72c4\u4e91\u957f\u5251\u5de6\u4e00\u523a\uff0c\u53f3\u4e00\u6233\uff0c\u5657\u5657\u4e24\u58f0\uff0c\u9c81\u5764\u548c\u5468\u573b\u53f3\u80a9\u5206\u522b\u4e2d\u5251\uff0c\u624b\u4e2d\u957f\u5251\u5148\u540e\u843d\u5730\u3002 \u3000\u3000\u4e07\u9707\u5c71\u6c89\u7740\u8138\uff0c\u53eb\u4e86\u58f0\uff1a\u201c\u5f88\u597d\uff01\u201d \u3000\u3000\u4e07\u572d\u63d0\u8d77\u957f\u5251\uff0c\u51dd\u76ee\u77aa\u7740\u72c4\u4e91\uff0c\u7a81\u7136\u95f4\u4e00\u58f0\u66b4\u559d\uff0c\u98d5\u98d5\u98d5\u8fde\u523a\u4e09\u5251\u3002\u72c4\u4e91\u4e00\u4e00\u6321\u5f00\uff0c\u5251\u4ea4\u5de6\u624b\uff0c\u53f3\u624b\u53cd\u5c06\u8fc7\u6765\uff0c\u62cd\u7684\u4e00\u58f0\u54cd\uff0c\u91cd\u91cd\u6253\u4e86\u4ed6\u4e00\u8bb0\u8033\u5149\u3002\u8fd9\u4e00\u62db\u66f4\u662f\u6765\u5f97\u7a81\u7136\uff0c\u4e07\u572d\u4e00\u6014\u4e4b\u95f4\uff0c\u72c4\u4e91\u5df2\u98de\u8d77\u5de6\u817f\uff0c\u8e39\u5728\u4ed6\u80f8\u53e3\u3002\u4e07\u572d\u62b5\u53d7\u4e0d\u4f4f\uff0c\u5750\u5012\u5728\u5730\u3002\u535c\u57a3\u62a2\u4e0a\u76f8\u6276\uff0c\u72c4\u4e91\u4e0d\u8ba9\u4ed6\u8d70\u8fd1\uff0c\u633a\u5251\u523a\u51fa\uff0c\u535c\u57a3\u53ea\u5f97\u4e3e\u5251\u62db\u67b6\u3002 \u3000\u3000\u5434\u574e\u3001\u51af\u5766\u3001\u6c88\u57ce\u4e09\u4eba\u89c1\u72c4\u4e91\u5982\u6b64\u51f6\u731b\uff0c\u800c\u4e07\u572d\u5750\u5728\u5730\u4e0b\uff0c\u4e00\u65f6\u7ad9\u4e0d\u8d77\u8eab\uff0c\u60ca\u6012\u4e4b\u4e0b\uff0c\u5404\u64cd\u5175\u5203\u56f4\u4e86\u4e0a\u6765\u3002\u8fd9\u65f6\u4e07\u5bb6\u7684\u5bb6\u4e01\u5a62\u4ec6\u542c\u5f97\u5385\u4e0a\u5175\u5203\u76f8\u4ea4\u7684\u58f0\u97f3\uff0c\u7eb7\u7eb7\u5954\u6765\u89c2\u770b\u3002 \u3000\u3000\u621a\u957f\u53d1\u53cc\u76ee\u77aa\u89c6\uff0c\u8138\u8272\u832b\u7136\uff0c\u4e0d\u77e5\u5982\u4f55\u662f\u597d\u3002 \u3000\u3000\u621a\u82b3\u53eb\u9053\uff1a\u201c\u7239\u7239\uff0c\u4ed6\u4eec\u5927\u4f19\u513f\u6253\u5e08\u54e5\u4e00\u4eba\uff0c\u5feb\uff0c\u5feb\u6551\u4ed6\u554a\u3002\u201d"}),showOnHomepage:!0},{name:"\u4e8c\u3000\u7262\u72f1",content:(0,a.jsx)(a.Fragment,{children:"\u53ee\u53ee\u5f53\u5f53\u5175\u5203\u76f8\u4ea4\u58f0\u4e2d\uff0c\u767d\u5149\u95ea\u8000\uff0c\u4e00\u67c4\u67c4\u957f\u5251\u98de\u4e86\u8d77\u6765\u3002\u4e00\u67c4\u8dcc\u5165\u4e86\u4eba\u4e1b\uff0c\u4f17\u5a62\u4ec6\u767b\u65f6\u4e71\u4f5c\u4e00\u56e2\uff0c\u4e00\u67c4\u6454\u4e0a\u4e86\u5e2d\u9762\uff0c\u66f4\u6709\u4e00\u67c4\u76f4\u63d2\u5165\u5934\u9876\u6a2a\u6881\u4e4b\u4e2d\u3002\u9877\u523b\u4e4b\u95f4\uff0c\u535c\u57a3\u3001\u5434\u574e\u3001\u51af\u5766\u3001\u6c88\u57ce\u56db\u4eba\u624b\u4e2d\u7684\u957f\u5251\uff0c\u90fd\u88ab\u72c4\u4e91\u4ee5\u201c\u53bb\u5251\u5f0f\u201d\u7ede\u593a\u8131\u624b\u3002 \u3000\u3000\u4e07\u9707\u5c71\u53cc\u638c\u4e00\u51fb\uff0c\u7b11\u9053\uff1a\u201c\u5f88\u597d\uff0c\u5f88\u597d\uff01\u621a\u5e08\u5f1f\uff0c\u96be\u4e3a\u4f60\u7ec3\u6210\u4e86\u2018\u8fde\u57ce\u5251\u6cd5\u2019\uff01\u606d\u559c\uff0c\u606d\u559c\uff01\u201d\u58f0\u97f3\u4e2d\u5374\u6ee1\u662f\u51c4\u51c9\u4e4b\u610f\u3002 \u3000\u3000\u621a\u957f\u53d1\u4e00\u5446\uff0c\u95ee\u9053\uff1a\u201c\u751a\u4e48\u2018\u8fde\u57ce\u5251\u6cd5\u2019\uff1f\u201d \u3000\u3000\u4e07\u9707\u5c71\u9053\uff1a\u201c\u72c4\u4e16\u5144\u8fd9\u51e0\u62db\uff0c\u4e0d\u662f\u2018\u8fde\u57ce\u5251\u6cd5\u2019\u662f\u751a\u4e48\uff1f \u3000\u3000\u5764\u513f\u3001\u573b\u513f\u3001\u572d\u513f\uff0c\u5927\u4f19\u90fd\u56de\u6765\u3002\u4f60\u4eec\u72c4\u5e08\u5144\u5b66\u7684\u662f\u621a\u5e08\u53d4\u7684\u2018\u8fde\u57ce\u5251\u6cd5\u2019\uff0c\u4f60\u4eec\u5982\u4f55\u662f\u4ed6\u654c\u624b\uff1f\u201d\u53c8\u5411\u621a\u957f\u53d1\u51b7\u7b11\u9053\uff1a\u201c\u5e08\u5f1f\uff0c\u4f60\u88c5\u5f97\u771f\u50cf\uff0c\u5f53\u771f\u662f\u5927\u667a\u82e5\u611a\uff01\u2018\u94c1\u9501\u6a2a\u6c5f\u2019\uff0c\u59d4\u5b9e\u4e86\u4e0d\u8d77\u3002\u201d \u3000\u3000\u72c4\u4e91\u8fde\u4f7f\u201c\u523a\u80a9\u5f0f\u201d\u3001\u201c\u8033\u5149\u5f0f\u201d\u3001\u201c\u53bb\u5251\u5f0f\u201d\u4e09\u8def\u5251\u62db\uff0c\u7247\u523b\u4e4b\u95f4\u4fbf\u5c06\u4e07\u95e8\u516b\u5f1f\u5b50\u6253\u5f97\u5927\u8d25\u4e8f\u8f93\uff0c\u81ea\u662f\u5f97\u610f\uff0c\u53ea\u662f\u80dc\u6765\u5982\u6b64\u5bb9\u6613\uff0c\u5fc3\u4e2d\u53cd\u800c\u80e1\u6d82\u4e86\uff0c\u4e0d\u7531\u5f97\u624b\u8db3\u65e0\u63aa\uff0c\u77a7\u77a7\u5e08\u7236\uff0c\u77a7\u77a7\u5e08\u59b9\uff0c\u53c8\u77a7\u77a7\u5e08\u4f2f\uff0c\u4e0d\u77e5\u8bf4\u751a\u4e48\u8bdd\u624d\u597d\u3002 \u3000\u3000\u621a\u957f\u53d1\u8d70\u8fd1\u8eab\u53bb\uff0c\u63a5\u8fc7\u4ed6\u624b\u4e2d\u957f\u5251\uff0c\u7a81\u7136\u95f4\u5251\u5c16\u4e00\u6296\uff0c\u6307\u5411\u4ed6\u7684\u54bd\u5589\uff0c\u559d\u9053\uff1a\u201c\u8fd9\u4e9b\u5251\u62db\uff0c\u4f60\u662f\u8ddf\u8c01\u5b66\u7684\uff1f\u201d \u3000\u3000\u72c4\u4e91\u5927\u5403\u4e00\u60ca\uff0c\u4ed6\u672c\u6765\u51e1\u4e8b\u4e0d\u6562\u7792\u9a97\u5e08\u7236\uff0c\u4f46\u90a3\u8001\u4e10\u8bf4\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u5018\u82e5\u6cc4\u9732\u4e86\u4f20\u5251\u4e4b\u4e8b\uff0c\u5b9a\u8981\u9001\u4e86\u90a3\u8001\u4e10\u7684\u6027\u547d\uff0c\u81ea\u5df1\u56e0\u6b64\u800c\u7acb\u4e0b\u91cd\u8a93\uff0c\u51b3\u4e0d\u5410\u9732\u4e00\u5b57\u534a\u53e5\uff0c\u4fbf\u9053\uff1a\u201c\u5e08\u2026\u2026\u5e08\u7236\uff0c\u662f\u5f1f\u5b50\u2026\u2026\u5f1f\u5b50\u81ea\u5df1\u60f3\u51fa\u6765\u7684\u3002\u201d \u3000\u3000\u621a\u957f\u53d1\u559d\u9053\uff1a\u201c\u4f60\u81ea\u5df1\u60f3\u5f97\u51fa\u8fd9\u822c\u5de7\u5999\u7684\u5251\u62db\uff1f\u4f60\u2026\u2026\u4f60\u7adf\u80c6\u6562\u5bf9\u6211\u80e1\u8bf4\u516b\u9053\uff01\u518d\u4e0d\u5b9e\u8bf4\uff0c\u6211\u4e00\u5251\u8981\u4e86\u4f60\u7684\u5c0f\u547d\u3002\u201d\u624b\u8155\u5411\u524d\u7565\u9001\uff0c\u5251\u5c16\u523a\u5165\u4ed6\u54bd\u5589\u6570\u5206\uff0c\u5251\u5c16\u4e0a\u5df2\u6e17\u51fa\u9c9c\u8840\u3002 \u3000\u3000\u621a\u82b3\u5954\u4e86\u8fc7\u6765\uff0c\u62b1\u4f4f\u7236\u4eb2\u624b\u81c2\uff0c\u53eb\u9053\uff1a\u201c\u7239\uff01\u5e08\u54e5\u8ddf\u54b1\u4eec\u5bf8\u6b65\u4e0d\u79bb\uff0c\u53c8\u6709\u8c01\u80fd\u6559\u4ed6\u6b66\u529f\u4e86\uff1f\u8fd9\u4e9b\u5251\u62db\uff0c\u4e0d\u90fd\u662f\u4f60\u8001\u4eba\u5bb6\u6559\u4ed6\u7684\u4e48\uff1f\u201d \u3000\u3000\u4e07\u9707\u5c71\u51b7\u7b11\u9053\uff1a\u201c\u621a\u5e08\u5f1f\uff0c\u4f60\u4f55\u5fc5\u518d\u88c5\u8154\u4f5c\u52bf\uff1f\u4ee4\u5ad2\u90fd\u8bf4\u5f97\u660e\u660e\u767d\u767d\u4e86\u3002\u2018\u94c1\u9501\u6a2a\u6c5f\u2019\u7684\u9ad8\u660e\u624b\u6bb5\uff0c\u4e0d\u5fc5\u4f7f\u5728\u81ea\u5df1\u5e08\u54e5\u8eab\u4e0a\u3002\u6765\u6765\u6765\uff01\u8001\u54e5\u54e5\u8d3a\u4f60\u4e09\u676f\uff01\u201d\u8bf4\u7740\u6ee1\u6ee1\u659f\u4e86\u4e24\u676f\u9152\uff0c\u4ef0\u8116\u5b50\u5148\u559d\u4e86\u4e00\u676f\uff0c\u8bf4\u9053\uff1a\u201c\u505a\u54e5\u54e5\u7684\u5148\u5e72\u4e3a\u656c\uff01\u4f60\u4e0d\u80fd\u4e0d\u7ed9\u6211\u8fd9\u4e2a\u9762\u5b50\u3002\u201d \u3000\u3000\u621a\u957f\u53d1\u54fc\u7684\u4e00\u58f0\uff0c\u629b\u5251\u5728\u5730\uff0c\u56de\u8eab\u63a5\u8fc7\u9152\u676f\uff0c\u8fde\u559d\u4e86\u4e09\u676f\uff0c\u4fa7\u8fc7\u4e86\u5934\u6c89\u601d\uff0c\u6ee1\u8138\u7591\u4e91\uff0c\u5583\u5583\u8bf4\u9053\uff1a\u201c\u5947\u602a\uff0c\u5947\u602a\uff01\u201d \u3000\u3000\u4e07\u9707\u5c71\u9053\uff1a\u201c\u621a\u5e08\u5f1f\uff0c\u6211\u6709\u4e00\u4ef6\u4e8b\uff0c\u60f3\u8ddf\u4f60\u8c08\u8c08\uff0c\u54b1\u4eec\u5230\u4e66\u623f\u4e2d\u53bb\u8bf4\u3002\u201d\u621a\u957f\u53d1\u70b9\u4e86\u70b9\u5934\u3002\u4e07\u9707\u5c71\u643a\u7740\u4ed6\u624b\uff0c\u5e08\u5144\u5f1f\u4fe9\u5e76\u80a9\u8d70\u5411\u4e66\u623f\u3002 \u3000\u3000\u4e07\u95e8\u516b\u5f1f\u5b50\u9762\u9762\u76f8\u89d1\u3002\u6709\u7684\u8138\u8272\u94c1\u9752\uff0c\u6709\u7684\u5583\u5583\u5492\u9a82\u3002 \u3000\u3000\u6c88\u57ce\u9053\uff1a\u201c\u6211\u5c0f\u4fbf\u53bb\uff01\u7ed9\u72c4\u4e91\u8fd9\u5c0f\u5b50\u8fd9\u4e48\u4e00\u4e0b\u5b50\uff0c\u5413\u5f97\u6211\u5c4e\u5c3f\u9f50\u6d41\u3002\u201d\u9c81\u5764\u6c89\u8138\u559d\u9053\uff1a\u201c\u516b\u5e08\u5f1f\uff0c\u4f60\u4e22\u7684\u4e11\u8fd8\u4e0d\u591f\u4e48\uff1f\u201d \u3000\u3000\u6c88\u57ce\u4f38\u4e86\u4f38\u820c\u5934\uff0c\u5306\u5306\u79bb\u5e2d\u3002\u4ed6\u8d70\u51fa\u5385\u95e8\uff0c\u5230\u5395\u6240\u53bb\u8f6c\u4e86\u8f6c\uff0c\u8e51\u624b\u8e51\u811a\u7684\u4fbf\u8d70\u5230\u4e66\u623f\u95e8\u5916\uff0c\u4fa7\u8033\u503e\u542c\u3002 \u3000\u3000\u53ea\u542c\u5f97\u5e08\u7236\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u621a\u5e08\u5f1f\uff0c\u4e8c\u5341\u5e74\u6765\u63ed\u4e0d\u7834\u7684\u8c1c\uff0c\u5230\u4eca\u65e5\u624d\u7b97\u771f\u76f8\u5927\u767d\u3002\u201d \u3000\u3000\u542c\u5f97\u621a\u957f\u53d1\u7684\u58f0\u97f3\u9053\uff1a\u201c\u5c0f\u5f1f\u4e0d\u61c2\uff0c\u751a\u4e48\u53eb\u505a\u771f\u76f8\u5927\u767d\u3002\u201d \u3000\u3000\u201c\u90a3\u8fd8\u7528\u6211\u591a\u8bf4\u4e48\uff1f\u5e08\u7236\u4ed6\u8001\u4eba\u5bb6\u662f\u600e\u4e48\u6b7b\u7684\uff1f\u201d \u3000\u3000\u201c\u5e08\u7236\u5931\u843d\u4e86\u4e00\u672c\u7ec3\u6b66\u529f\u7684\u4e66\uff0c\u627e\u6765\u627e\u53bb\u627e\u4e0d\u5230\uff0c\u90c1\u90c1\u4e0d\u4e50\uff0c\u5c31\u6b64\u901d\u4e16\u3002\u4f60\u53c8\u4e0d\u662f\u4e0d\u77e5\u9053\uff0c\u4f55\u5fc5\u95ee\u6211\uff1f\u201d \u3000\u3000\u201c\u662f\u554a\u3002\u8fd9\u672c\u7ec3\u6b66\u7684\u4e66\uff0c\u53eb\u505a\u751a\u4e48\u540d\u5b57\uff1f\u201d \u3000\u3000\u201c\u6211\u600e\u4e48\u77e5\u9053\uff1f\u4f60\u95ee\u6211\u5e72\u751a\u4e48\uff1f\u201d \u3000\u3000\u201c\u6211\u5374\u542c\u5e08\u7236\u8bf4\u8fc7\uff0c\u53eb\u505a\u300a\u8fde\u57ce\u8bc0\u300b\u3002\u201d \u3000\u3000\u201c\u751a\u4e48\u7ec3\u6210\u3001\u7ec3\u4e0d\u6210\u7684\uff0c\u6211\u534a\u70b9\u4e5f\u4e0d\u61c2\u3002\u201d \u3000\u3000\u201c\u77e5\u4e4b\u8005\u4e0d\u5982\u597d\u4e4b\u8005\uff0c\u597d\u4e4b\u8005\u4e0d\u5982\u751a\u4e48\uff1f\u201d \u3000\u3000\u201c\u4e0d\u5982\u4e50\u4e4b\u8005\uff01\u201d \u3000\u3000\u201c\u563f\u563f\uff0c\u54c8\u54c8\uff0c\u5475\u5475\uff01\u201d \u3000\u3000\u201c\u6709\u751a\u4e48\u597d\u7b11\uff1f\u201d \u3000\u3000\u201c\u4f60\u660e\u660e\u6ee1\u8179\u8bd7\u4e66\uff0c\u5374\u88c5\u4f5c\u7c97\u9c81\u4e0d\u6587\u3002\u54b1\u4eec\u540c\u95e8\u5b66\u827a\u5341\u51e0\u5e74\uff0c\u8c01\u8fd8\u4e0d\u77e5\u9053\u8c01\u7684\u5e95\uff1f\u4f60\u4e0d\u61c2\u2018\u2019\u4e09\u5b57\uff0c\u53c8\u600e\u80cc\u5f97\u51fa\u300a\u8bba\u8bed\u300b\u3001\u300a\u5b5f\u5b50\u300b\uff1f\u201d \u3000\u3000\u201c\u4f60\u662f\u8003\u8f83\u6211\u6765\u4e86\uff0c\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u201c\u62ff\u6765\uff01\u201d \u3000\u3000\u201c\u62ff\u751a\u4e48\u6765\uff1f\u201d \u3000\u3000\u201c\u4f60\u81ea\u5df1\u77e5\u9053\uff0c\u8fd8\u88c5\u751a\u4e48\u849c\uff1f\u201d \u3000\u3000\u201c\u6211\u621a\u957f\u53d1\u5411\u6765\u5c31\u4e0d\u6015\u4f60\u3002\u201d \u3000\u3000\u6c88\u57ce\u542c\u5e08\u7236\u548c\u5e08\u53d4\u8d8a\u5435\u8d8a\u5927\u58f0\uff0c\u5fc3\u4e2d\u5bb3\u6015\u8d77\u6765\uff0c\u6025\u5954\u56de\u5385\uff0c\u8d70\u5230\u9c81\u5764\u8eab\u8fb9\u4f4e\u58f0\u9053\uff1a\u201c\u5927\u5e08\u5144\uff0c\u5e08\u7236\u8ddf\u5e08\u53d4\u5435\u4e86\u8d77\u6765\uff0c\u53ea\u6015\u8981\u6253\u67b6\uff01\u201d \u3000\u3000\u9c81\u5764\u4e00\u6014\uff0c\u7ad9\u8d77\u8eab\u6765\u9053\uff1a\u201c\u54b1\u4eec\u77a7\u77a7\u53bb\uff01\u201d\u5468\u573b\u3001\u4e07\u572d\u3001\u5b59\u5747\u7b49\u90fd\u6025\u6b65\u8ddf\u53bb\u3002 \u3000\u3000\u621a\u82b3\u62c9\u62c9\u72c4\u4e91\u7684\u8863\u8896\uff0c\u9053\uff1a\u201c\u54b1\u4eec\u4e5f\u53bb\uff01\u201d\u72c4\u4e91\u70b9\u70b9\u5934\uff0c\u521a\u8d70\u51fa\u4e24\u6b65\uff0c\u621a\u82b3\u5c06\u4e00\u67c4\u957f\u5251\u585e\u5728\u4ed6\u624b\u4e2d\u3002\u72c4\u4e91\u4e00\u56de\u5934\uff0c\u53ea\u89c1\u621a\u82b3\u5de6\u624b\u4e2d\u63d0\u7740\u4e24\u628a\u957f\u5251\u3002\u72c4\u4e91\u95ee\u9053\uff1a\u201c\u4e24\u628a\uff1f\u201d\u621a\u82b3\u9053\uff1a\u201c\u7239\u6ca1\u5e26\u5175\u5203\uff01\u201d \u3000\u3000\u4e07\u95e8\u516b\u5f1f\u5b50\u90fd\u662f\u8138\u8272\u6c89\u91cd\uff0c\u7ad9\u5728\u4e66\u623f\u95e8\u5916\u3002\u72c4\u4e91\u548c\u621a\u82b3\u7ad9\u5f97\u7a0d\u8fdc\u3002\u5341\u4e2a\u4eba\u5c4f\u606f\u51dd\u6c14\uff0c\u542c\u7740\u4e66\u623f\u4e2d\u4e24\u4eba\u7684\u4e89\u5435\u3002 \u3000\u3000\u201c\u621a\u5e08\u5f1f\uff0c\u5e08\u7236\u4ed6\u8001\u4eba\u5bb6\u7684\u6027\u547d\uff0c\u660e\u660e\u662f\u4f60\u5bb3\u6b7b\u7684\u3002\u201d\u90a3\u662f\u4e07\u9707\u5c71\u7684\u58f0\u97f3\u3002 \u3000\u3000\u201c\u653e\u5c41\uff0c\u653e\u4f60\u5988\u7684\u5c41\uff0c\u4e07\u5e08\u54e5\uff0c\u4f60\u8bdd\u8bf4\u5f97\u660e\u767d\u4e9b\uff0c\u5e08\u7236\u600e\u4e48\u4f1a\u662f\u6211\u5bb3\u6b7b\u7684\uff1f\u201d\u621a\u957f\u53d1\u76db\u6012\u4e4b\u4e0b\uff0c\u58f0\u97f3\u5927\u5f02\uff0c\u53d8\u5f97\u5341\u5206\u5636\u54d1\u3002 \u3000\u3000\u201c\u5e08\u7236\u4ed6\u90a3\u672c\u300a\u8fde\u57ce\u8bc0\u300b\uff0c\u96be\u9053\u4e0d\u662f\u4f60\u621a\u5e08\u5f1f\u5077\u53bb\u7684\uff1f\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\u751a\u4e48\u8fde\u4eba\u3001\u8fde\u9b3c\u7684\uff1f\u4e07\u5e08\u54e5\uff0c\u4f60\u60f3\u8bec\u8d56\u6211\u59d3\u621a\u7684\uff0c\u53ef\u6ca1\u8fd9\u4e48\u5bb9\u6613\u3002\u201d \u3000\u3000\u201c\u4f60\u5f92\u513f\u521a\u624d\u4f7f\u7684\u5251\u62db\uff0c\u96be\u9053\u4e0d\u662f\u8fde\u57ce\u5251\u6cd5\uff1f\u4e3a\u751a\u4e48\u8fd9\u822c\u8f7b\u7075\u5de7\u5999\uff1f\u201d \u3000\u3000\u201c\u6211\u5f92\u513f\u751f\u6765\u806a\u660e\uff0c\u662f\u4ed6\u81ea\u5df1\u609f\u51fa\u6765\u7684\uff0c\u8fde\u6211\u4e5f\u4e0d\u4f1a\u3002\u54ea\u91cc\u662f\u751a\u4e48\u8fde\u57ce\u5251\u6cd5\u4e86\uff1f\u4f60\u53eb\u535c\u57a3\u6765\u8bf7\u6211\uff0c\u8bf4\u4f60\u5df2\u7ec3\u6210\u4e86\u8fde\u57ce\u5251\u6cd5\uff0c\u4f60\u8bf4\u8fc7\u8fd9\u8bdd\u6ca1\u6709\uff1f\u54b1\u4eec\u53eb\u535c\u57a3\u6765\u5bf9\u8bc1\u554a\uff01\u201d \u3000\u3000\u95e8\u5916\u5404\u4eba\u7684\u773c\u5149\u4e00\u9f50\u5411\u535c\u57a3\u77a7\u53bb\uff0c\u53ea\u89c1\u4ed6\u795e\u8272\u6781\u662f\u96be\u770b\uff0c\u663e\u7136\u621a\u957f\u53d1\u7684\u8bdd\u4e0d\u5047\u3002\u72c4\u4e91\u548c\u621a\u82b3\u5bf9\u89c6\u4e00\u773c\uff0c\u90fd\u70b9\u4e86\u70b9\u5934\uff0c\u5fc3\u60f3\uff1a\u201c\u535c\u57a3\u8fd9\u8bdd\u6211\u4e5f\u542c\u89c1\u7684\uff0c\u8981\u60f3\u62b5\u8d56\u90a3\u53ef\u4e0d\u6210\u3002\u201d \u3000\u3000\u53ea\u542c\u4e07\u9707\u5c71\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6211\u81ea\u7136\u8bf4\u8fc7\u8fd9\u8bdd\u3002\u82e5\u4e0d\u662f\u8fd9\u4e48\u8bf4\uff0c\u5982\u4f55\u80fd\u9a97\u5f97\u4f60\u6765\u3002\u621a\u957f\u53d1\uff0c\u6211\u6765\u95ee\u4f60\uff0c\u4f60\u8bf4\u4ece\u6765\u6ca1\u542c\u89c1\u8fc7\u2018\u8fde\u57ce\u5251\u6cd5\u2019\u7684\u540d\u5b57\uff0c\u4e3a\u751a\u4e48\u535c\u57a3\u4e00\u8bf4\u6211\u5df2\u7ec3\u6210\u8fde\u57ce\u5251\u6cd5\uff0c\u4f60\u5c31\u5df4\u5df4\u7684\u8d76\u6765\uff1f\u4f60\u8fd8\u60f3\u8d56\u5417\uff1f\u201d \u3000\u3000\u201c\u554a\u54c8\uff0c\u59d3\u4e07\u7684\uff0c\u4f60\u662f\u8bd3\u6211\u5230\u8346\u5dde\u6765\u7684\uff1f\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u4f60\u5c06\u5251\u8bc0\u4ea4\u51fa\u6765\uff0c\u518d\u5230\u5e08\u7236\u575f\u4e0a\u78d5\u5934\u8c22\u7f6a\u3002\u201d \u3000\u3000\u201c\u4e3a\u751a\u4e48\u8981\u4ea4\u7ed9\u4f60\uff1f\u201d \u3000\u3000\u201c\u54fc\uff0c\u6211\u662f\u5927\u5e08\u5144\u3002\u201d \u3000\u3000\u623f\u4e2d\u6c89\u5bc2\u4e86\u534a\u664c\uff0c\u53ea\u542c\u621a\u957f\u53d1\u5636\u54d1\u7684\u58f0\u97f3\u9053\uff1a\u201c\u597d\uff0c\u6211\u4ea4\u7ed9\u4f60\u3002\u201d \u3000\u3000\u95e8\u5916\u4f17\u4eba\u4e00\u542c\u5230\u201c\u597d\uff0c\u6211\u4ea4\u7ed9\u4f60\u201d\u8fd9\u4e94\u4e2a\u5b57\uff0c\u90fd\u4e0d\u7531\u81ea\u4e3b\u7684\u5168\u8eab\u4e00\u9707\u3002\u72c4\u4e91\u548c\u621a\u82b3\u6068\u4e0d\u5f97\u6709\u4e2a\u5730\u6d1e\u53ef\u4ee5\u94bb\u5c06\u4e0b\u53bb\u3002\u9c81\u5764\u7b49\u516b\u4eba\u5411\u72c4\u621a\u4e8c\u4eba\u6295\u4ee5\u9119\u5937\u4e4b\u8272\u3002\u621a\u82b3\u53c8\u662f\u6c14\u607c\uff0c\u53c8\u611f\u5230\u4e07\u5206\u5c48\u8fb1\uff0c\u771f\u60f3\u4e0d\u5230\u7239\u7239\u7adf\u4f1a\u505a\u51fa\u8fd9\u7b49\u4e0d\u8981\u8138\u7684\u4e8b\u6765\u3002 \u3000\u3000\u7a81\u7136\u4e4b\u95f4\uff0c\u623f\u4e2d\u4f20\u51fa\u4e07\u9707\u5c71\u957f\u58f0\u60e8\u547c\uff0c\u6781\u662f\u51c4\u5389\u3002 \u3000\u3000\u4e07\u572d\u60ca\u53eb\uff1a\u201c\u7239\uff01\u201d\u98de\u817f\u8e22\u5f00\u623f\u95e8\uff0c\u62a2\u4e86\u8fdb\u53bb\u3002\u53ea\u89c1\u4e07\u9707\u5c71\u5012\u5728\u5730\u4e0b\uff0c\u80f8\u53e3\u63d2\u7740\u4e00\u67c4\u660e\u6643\u6643\u7684\u5315\u9996\uff0c\u8eab\u8fb9\u90fd\u662f\u9c9c\u8840\u3002 \u3000\u3000\u7a97\u5b50\u5927\u5f00\uff0c\u5140\u81ea\u6447\u6643\uff0c\u621a\u957f\u53d1\u5374\u5df2\u4e0d\u77e5\u53bb\u5411\u3002 \u3000\u3000\u4e07\u572d\u54ed\u53eb\uff1a\u201c\u7239\uff0c\u7239\uff01\u201d\u6251\u5230\u4e07\u9707\u5c71\u8eab\u8fb9\u3002 \u3000\u3000\u621a\u82b3\u53e3\u4e2d\u4f4e\u58f0\u4e5f\u53eb\uff1a\u201c\u7239\uff0c\u7239\uff01\u201d\u8eab\u5b50\u98a4\u6296\uff0c\u63e1\u4f4f\u4e86\u72c4\u4e91\u7684\u624b\u3002 \u3000\u3000\u9c81\u5764\u53eb\u9053\uff1a\u201c\u5feb\uff0c\u5feb\u8ffd\u51f6\u624b\uff01\u201d\u548c\u5468\u573b\u3001\u5b59\u5747\u8bf8\u5e08\u5f1f\u7eb7\u7eb7\u8dc3\u51fa\u7a97\u53bb\uff0c\u5927\u53eb\uff1a\u201c\u6349\u51f6\u624b\uff0c\u6349\u51f6\u624b\u554a\uff01\u201d \u3000\u3000\u72c4\u4e91\u89c1\u4e07\u95e8\u516b\u5f1f\u5b50\u7eb7\u7eb7\u51fa\u53bb\u8ffd\u8d76\u5e08\u7236\uff0c\u8fd9\u4e00\u4e0b\u53d8\u6545\uff0c\u5f53\u771f\u5413\u5f97\u4ed6\u516d\u795e\u65e0\u4e3b\uff0c\u4e0d\u77e5\u5982\u4f55\u624d\u597d\u3002\u621a\u82b3\u53c8\u53eb\u4e86\u4e00\u58f0\uff1a\u201c\u7239\u7239\uff01\u201d \u3000\u3000\u8eab\u5b50\u6643\u4e86\u4e24\u6643\uff0c\u7ad9\u7acb\u4e0d\u5b9a\u3002\u72c4\u4e91\u5fd9\u4f38\u624b\u6276\u4f4f\uff0c\u4e00\u4f4e\u5934\uff0c\u53ea\u89c1\u4e07\u9707\u5c71\u53cc\u76ee\u7d27\u95ed\uff0c\u8138\u4e0a\u795e\u60c5\u72f0\u72de\u53ef\u6016\uff0c\u60f3\u662f\u4e34\u6b7b\u65f6\u53d7\u5230\u6781\u5927\u75db\u82e6\u3002 \u3000\u3000\u72c4\u4e91\u4e0d\u6562\u518d\u770b\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5e08\u59b9\uff0c\u54b1\u4eec\u8d70\u4e0d\u8d70\uff1f\u201d\u621a\u82b3\u5c1a\u672a\u56de\u7b54\uff0c\u53ea\u542c\u5f97\u8eab\u540e\u4e00\u4e2a\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u662f\u8c0b\u6740\u6211\u5e08\u7236\u7684\u540c\u72af\uff0c\u53ef\u4e0d\u80fd\u8d70\uff01\u201d \u3000\u3000\u72c4\u4e91\u548c\u621a\u82b3\u56de\u8fc7\u5934\u6765\uff0c\u53ea\u89c1\u4e00\u67c4\u957f\u5251\u7684\u5251\u5c16\u6307\u7740\u621a\u82b3\u540e\u5fc3\uff0c\u5251\u67c4\u6293\u5728\u535c\u57a3\u7684\u624b\u91cc\u3002\u72c4\u4e91\u5927\u6012\uff0c\u5f85\u6b32\u53cd\u5507\u76f8\u8ba5\uff0c\u4f46\u8bdd\u5230\u53e3\u8fb9\uff0c\u60f3\u5230\u5e08\u7236\u624b\u5203\u5e08\u5144\uff0c\u90a3\u8fd8\u6709\u751a\u4e48\u8bdd\u53ef\u8bf4\uff1f\u4e0d\u7531\u5f97\u4f4e\u4e0b\u4e86\u5934\uff0c\u4e00\u8a00\u4e0d\u53d1\u3002 \u3000\u3000\u535c\u57a3\u51b7\u51b7\u7684\u9053\uff1a\u201c\u4e24\u4f4d\u8bf7\u56de\u5230\u81ea\u5df1\u623f\u53bb\uff0c\u5f85\u54b1\u4eec\u62ff\u5230\u621a\u957f\u53d1\u540e\uff0c\u4e00\u8d77\u9001\u5b98\u6cbb\u7f6a\u3002\u201d\u72c4\u4e91\u9053\uff1a\u201c\u6b64\u4e8b\u5168\u7531\u6211\u4e00\u4eba\u8eab\u4e0a\u800c\u8d77\uff0c\u8ddf\u5e08\u59b9\u6beb\u4e0d\u76f8\u5e72\u3002\u4f60\u4eec\u8981\u6740\u8981\u5250\uff0c\u627e\u6211\u4e00\u4eba\u4fbf\u4e86\u3002\u201d\u535c\u57a3\u731b\u529b\u63a8\u4ed6\u80cc\u5fc3\uff0c\u559d\u9053\uff1a\u201c\u8d70\u7f62\uff0c\u8fd9\u53ef\u4e0d\u662f\u4f60\u901e\u597d\u6c49\u7684\u65f6\u5019\u3002\u201d\u72c4\u4e91\u53ea\u542c\u5230\u5916\u9762\u2018\u6349\u51f6\u624b\u554a\uff0c\u6349\u51f6\u624b\u554a\uff01\u201d\u7684\u58f0\u97f3\uff0c\u8ddf\u7740\u8857\u4e0a\u5621\u3001\u5621\u3001\u5621\u7684\u9523\u58f0\u54cd\u4e86\u8d77\u6765\uff0c\u5954\u8d70\u547c\u53f7\u4e4b\u58f0\uff0c\u4e71\u6210\u4e00\u7247\uff0c\u5fc3\u4e0b\u5b9e\u662f\u8bf4\u4e0d\u51fa\u7684\u7f9e\u6127\u96be\u5f53\uff0c\u54ac\u4e86\u54ac\u7259\uff0c\u8d70\u5411\u81ea\u5df1\u623f\u53bb\u3002 \u3000\u3000\u621a\u82b3\u54ed\u9053\uff1a\u201c\u5e08\u54e5\uff0c\u90a3\u2026\u2026\u90a3\u600e\u4e48\u5f97\u4e86\uff1f\u201d\u72c4\u4e91\u54fd\u54bd\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\u4e0d\u77e5\u9053\u3002\u6211\u53bb\u8ddf\u5e08\u7236\u62b5\u7f6a\u597d\u4e86\u3002\u201d\u621a\u82b3\u54ed\u9053\uff1a\u201c\u7239\u7239\uff0c\u4ed6\u2026\u2026\u4ed6\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u201d \u3000\u3000\u72c4\u4e91\u5750\u5728\u623f\u4e2d\uff0c\u5176\u65f6\u8ddd\u4e07\u9707\u5c71\u88ab\u6740\u5df2\u6709\u4e24\u4e2a\u591a\u65f6\u8fb0\uff0c\u4ed6\u5140\u81ea\u5446\u5446\u5750\u5728\u684c\u524d\uff0c\u671b\u7740\u70e7\u5f97\u53ea\u5269\u534a\u5bf8\u7684\u6b8b\u70db\uff0c\u5fc3\u4e71\u5982\u9ebb\u3002 \u3000\u3000\u8fd9\u65f6\u8ffd\u8d76\u621a\u957f\u53d1\u7684\u4f17\u4eba\u90fd\u5df2\u56de\u6765\u4e86\u3002\u201c\u51f6\u624b\u9003\u51fa\u57ce\u53bb\u4e86\uff0c\u8ffd\u4e0d\u5230\u5566\uff01\u201d\u201c\u660e\u513f\u54b1\u4eec\u8ffd\u5230\u6e56\u5357\u53bb\uff0c\u65e0\u8bba\u5982\u4f55\u8981\u6349\u5230\u51f6\u624b\uff0c\u7ed9\u5e08\u7236\u62a5\u4ec7\uff01\u201d\u201c\u53ea\u6015\u51f6\u624b\u4ea1\u547d\u6c5f\u6e56\uff0c\u518d\u4e5f\u5bfb\u4ed6\u4e0d\u7740\u3002\u201d\u201c\u54fc\uff01\u4fbf\u662f\u8ffd\u5230\u5929\u6daf\u6d77\u89d2\uff0c\u4e5f\u8981\u6349\u5230\u4ed6\u788e\u5c38\u4e07\u6bb5\u3002\u201d\u201c\u660e\u65e5\u5927\u6492\u6c5f\u6e56\u5e16\u5b50\uff0c\u8981\u8bf7\u6b66\u6797\u82f1\u96c4\u4e3b\u6301\u516c\u9053\uff0c\u5171\u540c\u8ffd\u6740\u8fd9\u5351\u9119\u65e0\u803b\u7684\u51f6\u624b\u3002\u201d \u3000\u3000\u201c\u5bf9\uff0c\u5bf9\uff01\u54b1\u4eec\u628a\u51f6\u624b\u7684\u5973\u513f\u548c\u59d3\u72c4\u7684\u5c0f\u72d7\u5148\u5bb0\u4e86\uff0c\u7528\u6765\u796d\u62dc\u5e08\u7236\u7684\u82f1\u7075\u3002\u201d\u201c\u4e0d\uff01\u5f85\u660e\u5929\u53bf\u592a\u7237\u6765\u9a8c\u8fc7\u4e86\u5c38\u9996\u518d\u8bf4\u3002\u201d\u4e07\u95e8\u5bb6\u4eba\u5f1f\u5b50\u8fd9\u4e9b\u7eb7\u7eb7\u8bae\u8bba\uff0c\u4e5f\u65e9\u5df2\u505c\u606f\u4e86\u3002 \u3000\u3000\u72c4\u4e91\u60f3\u53eb\u5e08\u59b9\u72ec\u81ea\u9003\u8d70\uff0c\u4f46\u60f3\uff1a\u201c\u5979\u5e74\u7eaa\u8f7b\u8f7b\u4e00\u4e2a\u5973\u5b50\uff0c\u6d41\u843d\u6c5f\u6e56\uff0c\u6709\u8c01\u6765\u7167\u987e\uff1f\u6211\u5e26\u7740\u5979\u4e00\u540c\u9003\u8d70\u7f62\uff1f\u4e0d\uff0c\u4e0d\uff01\u8fd9\u4ef6\u7978\u4e8b\u90fd\u662f\u7531\u6211\u8eab\u4e0a\u800c\u8d77\uff0c\u82e5\u4e0d\u662f\u6211\u901e\u5f3a\u51fa\u5934\uff0c\u8ddf\u4e07\u5bb6\u4f17\u5e08\u5144\u6253\u67b6\u751f\u4e8b\uff0c\u4e07\u5e08\u4f2f\u600e\u4f1a\u7591\u5fc3\u6211\u5e08\u7236\u76d7\u4e86\u751a\u4e48\u2018\u8fde\u57ce\u5251\u2019\u7684\u5251\u8bc0\uff1f\u6211\u5e08\u7236\u662f\u4e2a\u6700\u8001\u5b9e\u4e0d\u8fc7\u7684\u597d\u4eba\uff0c\u600e\u4f1a\u53bb\u5077\u751a\u4e48\u5251\u8bc0\uff1f\u8fd9\u4e09\u62db\u5251\u6cd5\u662f\u90a3\u4e2a\u8001\u4e5e\u4e10\u6559\u6211\u7684\u554a\u3002\u53ef\u662f\u5e08\u7236\u5df2\u6740\u4e86\u4eba\uff0c\u6211\u8fd9\u65f6\u518d\u8bf4\u51fa\u6765\uff0c\u65c1\u4eba\u4e5f\u51b3\u4e0d\u76f8\u4fe1\uff0c\u5c31\u7b97\u76f8\u4fe1\u4e86\uff0c\u53c8\u6709\u751a\u4e48\u7528\uff1f\u6211\u5b9e\u5728\u7f6a\u5927\u6076\u6781\uff0c\u90fd\u662f\u6211\u4e00\u4e2a\u4eba\u4e0d\u597d\u3002\u6211\u660e\u5929\u8981\u5f53\u4f17\u8a00\u660e\uff0c\u4e3a\u5e08\u7236\u8fa9\u767d\u3002\u53ef\u662f\u2026\u2026\u53ef\u662f\u4e07\u5e08\u4f2f\u660e\u660e\u662f\u5e08\u7236\u6740\u7684\u3002\u5e08\u7236\u7684\u6076\u540d\u600e\u80fd\u6d17\u5237\u5f97\u4e86\uff1f\u4e0d\uff0c\u6211\u51b3\u4e0d\u80fd\u9003\u8d70\uff0c\u6211\u7559\u7740\u7ed9\u5e08\u7236\u62b5\u7f6a\uff0c\u8ba9\u4ed6\u4eec\u6740\u4e86\u6211\u597d\u4e86\uff01\u201d \u3000\u3000\u6b63\u81ea\u601d\u6f6e\u8d77\u4f0f\uff0c\u5ffd\u542c\u5f97\u5916\u9762\u5c4b\u9876\u4e0a\u5580\u5587\u4e00\u58f0\u8f7b\u54cd\uff0c\u4e00\u62ac\u5934\uff0c\u53ea\u89c1\u4e00\u6761\u9ed1\u5f71\u81ea\u897f\u800c\u4e1c\uff0c\u4ece\u5c4b\u9876\u4e0a\u7eb5\u8dc3\u800c\u8fc7\u3002\u4ed6\u9669\u4e9b\u53eb\u51fa\u201c\u5e08\u7236\u201d\u6765\uff0c\u4f46\u51dd\u76ee\u4e00\u770b\uff0c\u90a3\u4eba\u8eab\u5f62\u53c8\u9ad8\u53c8\u7626\uff0c\u51b3\u4e0d\u662f\u5e08\u7236\u3002\u8ddf\u7740\u53c8\u6709\u4e00\u4e2a\u4eba\u5f71\u7d27\u63a5\u7740\u8dc3\u8fc7\uff0c\u8fd9\u6b21\u66f4\u770b\u660e\u767d\u90a3\u4eba\u624b\u63e1\u5355\u5200\u3002 \u3000\u3000\u4ed6\u5fc3\u60f3\uff1a\u201c\u4ed6\u4eec\u662f\u5728\u641c\u5bfb\u5e08\u7236\u4e48\uff1f\u96be\u9053\u5e08\u7236\u8fd8\u5728\u9644\u8fd1\uff0c\u5e76\u672a\u8d70\u8fdc\uff1f\u201d\u6b63\u601d\u7591\u95f4\uff0c\u5ffd\u542c\u5f97\u4e1c\u8fb9\u5c4b\u4e2d\u4f20\u6765\u4e00\u58f0\u5973\u5b50\u7684\u60ca\u547c\u3002 \u3000\u3000\u4ed6\u5927\u5403\u4e00\u60ca\uff0c\u63e1\u4f4f\u5251\u67c4\uff0c\u4e00\u8dc3\u800c\u8d77\uff0c\u9996\u5148\u60f3\u5230\u7684\u4fbf\u662f\uff1a \u3000\u3000\u201c\u4ed6\u4eec\u5728\u6b3a\u4fae\u5e08\u59b9\uff1f\u201d\u8ddf\u7740\u53c8\u542c\u5f97\u4e00\u58f0\u5973\u5b50\u7684\u547c\u558a\uff1a\u201c\u6551\u547d\uff01\u201d \u3000\u3000\u8fd9\u58f0\u97f3\u4f3c\u4e4e\u5e76\u975e\u621a\u82b3\uff0c\u4f46\u4ed6\u5173\u5fc3\u592a\u5207\uff0c\u54ea\u7b49\u5f97\u53ca\u5206\u8fa8\u662f\u5426\u621a\u82b3\u9047\u9669\uff0c\u7eb5\u8eab\u4fbf\u4ece\u7a97\u53e3\u8dc3\u4e86\u51fa\u53bb\uff0c\u521a\u7ad9\u4e0a\u5c4b\u6a90\uff0c\u53c8\u542c\u5f97\u90a3\u5973\u5b50\u60ca\u53eb\uff1a\u201c\u6551\u547d\uff01\u6551\u547d\uff01\u201d \u3000\u3000\u4ed6\u5faa\u58f0\u5954\u53bb\uff0c\u53ea\u89c1\u4e1c\u8fb9\u697c\u4e0a\u900f\u51fa\u706f\u5149\uff0c\u4e00\u6247\u7a97\u5b50\u5140\u81ea\u6447\u52a8\u3002\u4ed6\u7eb5\u5230\u7a97\u8fb9\uff0c\u5f80\u91cc\u5f20\u53bb\uff0c\u53ea\u89c1\u4e00\u4e2a\u5973\u5b50\u624b\u8db3\u88ab\u7ed1\uff0c\u6a2a\u5367\u5728\u5e8a\uff0c\u4e24\u6761\u6c49\u5b50\u4f38\u51fa\u624b\u53bb\u6478\u5979\u8138\u988a\uff0c\u53e6\u4e00\u4e2a\u5374\u8981\u89e3\u5979\u8863\u886b\u3002\u72c4\u4e91\u4e0d\u8ba4\u5f97\u8fd9\u5973\u5b50\u662f\u8c01\uff0c\u4f46\u89c1\u5979\u5df2\u5413\u5f97\u8138\u65e0\u4eba\u8272\uff0c\u5728\u5e8a\u4e0a\u6eda\u52a8\u6323\u624e\uff0c\u5927\u58f0\u547c\u6551\u3002 \u3000\u3000\u4ed6\u81ea\u5df1\u867d\u5728\u96be\u4e2d\uff0c\u4f46\u89c1\u6b64\u60c5\u666f\uff0c\u4e0d\u80fd\u7f6e\u4e4b\u4e0d\u7406\uff0c\u5f53\u5373\u8fde\u5251\u5e26\u4eba\u4ece\u7a97\u4e2d\u6251\u5c06\u8fdb\u53bb\uff0c\u633a\u5251\u523a\u5411\u5de6\u8fb9\u90a3\u6c49\u5b50\u7684\u540e\u5fc3\u3002\u53f3\u8fb9\u7684\u6c49\u5b50\u4e3e\u8d77\u4e00\u5f20\u6905\u5b50\u4e00\u683c\uff0c\u5de6\u8fb9\u7684\u6c49\u5b50\u5df2\u62d4\u51fa\u5355\u5200\uff0c\u780d\u4e86\u8fc7\u6765\u3002\u72c4\u4e91\u89c1\u8fd9\u4e24\u4eba\u8138\u4e0a\u90fd\u8499\u4e86\u9ed1\u5e03\uff0c\u53ea\u9732\u51fa\u4e00\u5bf9\u773c\u775b\uff0c\u559d\u9053\uff1a\u201c\u5927\u80c6\u6076\u8d3c\uff0c\u7559\u4e0b\u547d\u6765\uff01\u201d\u5237\u5237\u5237\u8fde\u523a\u4e09\u5251\u3002 \u3000\u3000\u4e24\u6761\u6c49\u5b50\u4e0d\u58f0\u4e0d\u54cd\uff0c\u5404\u4f7f\u5355\u5200\u683c\u6253\u3002\u4e00\u540d\u6c49\u5b50\u53eb\u9053\uff1a\u201c\u5415\u5144\u5f1f\uff0c\u626f\u547c\uff01\u201d\u53e6\u4e00\u4eba\u9053\uff1a\u201c\u7b97\u4ed6\u4e07\u9707\u5c71\u8fd0\u6c14\uff0c\u4e0b\u6b21\u518d\u6765\u62a5\u4ec7\uff01\u201d \u3000\u3000\u53cc\u5200\u9f50\u4e3e\uff0c\u5f80\u72c4\u4e91\u5934\u4e0a\u780d\u5c06\u8fc7\u6765\u3002 \u3000\u3000\u72c4\u4e91\u89c1\u6765\u52bf\u51f6\u731b\uff0c\u95ea\u8eab\u907f\u8fc7\u3002\u4e00\u6761\u6c49\u5b50\u98de\u8db3\u8e22\u7ffb\u4e86\u684c\u5b50\uff0c\u70db\u53f0\u6454\u4e0b\uff0c\u623f\u4e2d\u767b\u65f6\u9ed1\u6f06\u4e00\u56e2\u3002\u53ea\u542c\u5f97\u547c\u547c\u58f0\u54cd\uff0c\u4e24\u4eba\u8dc3\u51fa\u7a97\u5b50\uff0c\u8ddf\u7740\u4e52\u4e53\u8fde\u54cd\uff0c\u51e0\u5757\u74e6\u7247\u63b7\u5c06\u8fc7\u6765\u3002\u9ed1\u6697\u4e2d\u72c4\u4e91\u770b\u4e0d\u6e05\u695a\uff0c\u800c\u8fd9\u9ad8\u6765\u9ad8\u53bb\u7684\u8f7b\u8eab\u529f\u592b\u4ed6\u539f\u4e5f\u4e0d\u64c5\u957f\uff0c\u4e0d\u6562\u8ffd\u51fa\u3002 \u3000\u3000\u4ed6\u5fc3\u60f3\uff1a\u201c\u5176\u4e2d\u4e00\u4e2a\u8d3c\u5b50\u59d3\u5415\uff0c\u591a\u534a\u662f\u5415\u901a\u7684\u4e00\u4f19\uff0c\u662f\u62a5\u4ec7\u6765\u4e86\uff0c\u4ed6\u4eec\u8fd8\u4e0d\u77e5\u4e07\u5e08\u4f2f\u5df2\u6b7b\u3002\u201d \u3000\u3000\u5ffd\u542c\u5e8a\u4e0a\u90a3\u5973\u5b50\u53eb\u9053\uff1a\u201c\u554a\u54df\uff0c\u75db\u6b7b\u4e86\uff0c\u6211\u80f8\u53e3\u6709\u4e00\u628a\u5c0f\u5200\uff0c\u5feb\u7ed9\u6211\u62d4\u51fa\u6765\u3002\u201d\u72c4\u4e91\u5403\u4e86\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u8d3c\u4eba\u523a\u4e2d\u4e86\u4f60\uff1f\u201d \u3000\u3000\u90a3\u5973\u5b50\u547b\u541f\u9053\uff1a\u201c\u523a\u4e2d\u4e86\uff01\u523a\u4e2d\u4e86\uff01\u201d \u3000\u3000\u72c4\u4e91\u9053\uff1a\u201c\u6211\u70b9\u4eae\u8721\u70db\u7ed9\u4f60\u77a7\u77a7\u3002\u201d\u90a3\u5973\u5b50\u9053\uff1a\u201c\u4f60\u8fc7\u6765\uff0c\u5feb\uff0c\u5feb\u8fc7\u6765\uff01\u201d\u72c4\u4e91\u542c\u5979\u8bf4\u5f97\u60ca\u614c\uff0c\u8d70\u8fd1\u4e00\u6b65\uff0c\u9053\uff1a\u201c\u751a\u4e48\uff1f\u201d \u3000\u3000\u7a81\u7136\u4e4b\u95f4\uff0c\u90a3\u5973\u5b50\u5f20\u5f00\u624b\u81c2\uff0c\u5c06\u4ed6\u62e6\u8170\u62b1\u4f4f\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u6551\u547d\u554a\uff0c\u6551\u547d\u554a\uff01\u201d \u3000\u3000\u72c4\u4e91\u8fd9\u4e00\u60ca\u6bd4\u9002\u624d\u66f4\u662f\u5389\u5bb3\uff0c\u660e\u660e\u89c1\u5979\u624b\u8db3\u90fd\u88ab\u7ed1\u4f4f\uff0c\u600e\u5730\u4f1a\u5c06\u81ea\u5df1\u62b1\u4f4f\uff1f\u5fd9\u4f38\u624b\u53bb\u63a8\uff0c\u60f3\u8131\u5f00\u5979\u7684\u6402\u62b1\uff0c\u4e0d\u6599\u8fd9\u5973\u5b50\u6b7b\u547d\u7684\u62b1\u4f4f\u4ed6\u8170\uff0c\u4e00\u65f6\u4e4b\u95f4\u7adf\u7136\u63a8\u5979\u4e0d\u5f00\u3002 \u3000\u3000\u5ffd\u7136\u95f4\u773c\u524d\u4e00\u4eae\uff0c\u7a97\u53e3\u4f38\u8fdb\u4e24\u4e2a\u706b\u628a\uff0c\u7167\u5f97\u623f\u4e2d\u660e\u5982\u767d\u663c\uff0c\u597d\u51e0\u4e2a\u4eba\u540c\u65f6\u95ee\u9053\uff1a\u201c\u751a\u4e48\u4e8b\uff1f\u751a\u4e48\u4e8b\uff1f\u201d\u90a3\u5973\u5b50\u53eb\u9053\uff1a\u201c\u91c7\u82b1\u8d3c\uff0c\u91c7\u82b1\u8d3c\uff01\u8c0b\u8d22\u5bb3\u547d\u554a\uff0c\u6551\u547d\uff0c\u6551\u547d\uff01\u201d \u3000\u3000\u72c4\u4e91\u5927\u6025\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u2026\u2026\u4f60\u600e\u4e48\u4e0d\u8bc6\u597d\u6b79\uff1f\u201d\u4f38\u624b\u5f80\u5979\u8eab\u4e0a\u4e71\u63a8\u3002\u90a3\u5973\u5b50\u672c\u6765\u62b1\u7740\u4ed6\u8170\uff0c\u8fd9\u65f6\u5374\u5168\u529b\u6491\u62d2\uff0c\u53eb\u9053\uff1a\u201c\u522b\u78b0\u6211\uff0c\u522b\u78b0\u6211\uff01\u201d \u3000\u3000\u72c4\u4e91\u6b63\u5f85\u9003\u5f00\uff0c\u5ffd\u89c9\u540e\u9888\u4e2d\u4e00\u9635\u51b0\u51b7\uff0c\u4e00\u67c4\u957f\u5251\u5df2\u67b6\u5728\u9888\u4e2d\u3002\u4ed6\u6b63\u5f85\u5206\u8fa9\uff0c\u84e6\u5730\u91cc\u767d\u5149\u4e00\u95ea\uff0c\u53ea\u89c9\u53f3\u638c\u4e00\u9635\u5267\u75db\uff0c\u5f53\u5577\u4e00\u58f0\uff0c\u81ea\u5df1\u624b\u4e2d\u7684\u957f\u5251\u8dcc\u5728\u5730\u677f\u4e4b\u4e0a\u3002\u4ed6\u4fef\u773c\u4e00\u770b\uff0c\u5413\u5f97\u51e0\u4e4e\u6655\u4e86\u8fc7\u53bb\uff0c\u53ea\u89c1\u81ea\u5df1\u53f3\u624b\u7684\u4e94\u6839\u624b\u6307\u5df2\u88ab\u4eba\u524a\u843d\uff0c\u9c9c\u8840\u5982\u6cc9\u6c34\u822c\u55b7\u5c06\u51fa\u6765\uff0c\u614c\u4e71\u4e2d\u659c\u773c\u770b\u65f6\uff0c\u4f46\u89c1\u5434\u574e\u624b\u6301\u5e26\u8840\u957f\u5251\uff0c\u7ad9\u5728\u4e00\u65c1\u3002 \u3000\u3000\u4ed6\u53ea\u8bf4\u5f97\u4e00\u58f0\uff1a\u201c\u4f60\uff01\u201d\u98de\u8d77\u53f3\u8db3\u4fbf\u5f80\u5434\u574e\u8e22\u53bb\uff0c\u7a81\u7136\u95f4\u540e\u5fc3\u88ab\u4eba\u731b\u529b\u4e00\u62f3\uff0c\u4e00\u4e2a\u8e09\u8dc4\uff0c\u6251\u8dcc\u5728\u90a3\u5973\u4eba\u8eab\u4e0a\u3002\u90a3\u5973\u4eba\u53c8\u53eb\uff1a\u201c\u6551\u547d\u554a\uff0c\u91c7\u82b1\u8d3c\u554a\uff01\u201d\u53ea\u542c\u5f97\u9c81\u5764\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u5c06\u8fd9\u5c0f\u8d3c\u7ed1\u4e86\uff01\u201d \u3000\u3000\u72c4\u4e91\u867d\u662f\u4e2a\u4ece\u6ca1\u89c1\u8fc7\u4e16\u9762\u7684\u4e61\u4e0b\u5c11\u5e74\uff0c\u6b64\u523b\u4e5f\u660e\u767d\u662f\u843d\u5165\u4e86\u4eba\u5bb6\u5e03\u7f6e\u7684\u9634\u6bd2\u9677\u9631\u4e4b\u4e2d\u3002\u4ed6\u6025\u8dc3\u800c\u8d77\uff0c\u7ffb\u8fc7\u8eab\u6765\uff0c\u6b63\u8981\u5411\u9c81\u5764\u6251\u53bb\uff0c\u5ffd\u7136\u89c1\u5230\u4e00\u5f20\u82cd\u767d\u7684\u8138\uff0c\u5374\u662f\u621a\u82b3\u3002 \u3000\u3000\u72c4\u4e91\u4e00\u5446\uff0c\u53ea\u89c1\u621a\u82b3\u8138\u4e0a\u7684\u795e\u8272\u53c8\u662f\u4f24\u5fc3\uff0c\u53c8\u662f\u9119\u5937\uff0c\u53c8\u662f\u6124\u6012\u3002\u4ed6\u53eb\u9053\uff1a\u201c\u5e08\u59b9\uff01\u201d\u621a\u82b3\u7a81\u7136\u6ee1\u8138\u6da8\u5f97\u901a\u7ea2\uff0c\u9053\uff1a\u201c\u4f60\u4e3a\u751a\u4e48\u2026\u2026\u4e3a\u751a\u4e48\u8fd9\u6837\uff1f\u201d\u72c4\u4e91\u6ee1\u8179\u51a4\u5c48\uff0c\u8fd9\u65f6\u5982\u4f55\u8bf4\u5f97\u51fa\u53e3\uff1f \u3000\u3000\u621a\u82b3\u201c\u554a\u201d\u7684\u4e00\u58f0\uff0c\u54ed\u4e86\u51fa\u6765\uff0c\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\u8fd8\u662f\u6b7b\u4e86\u7684\u597d\u3002\u201d\u89c1\u5230\u72c4\u4e91\u53f3\u624b\u4e94\u6307\u5168\u88ab\u524a\u843d\uff0c\u5fc3\u4e2d\u53c8\u662f\u4e00\u75db\uff0c\u54ac\u4e00\u54ac\u7259\uff0c\u6495\u4e0b\u81ea\u5df1\u5e03\u886b\u4e0a\u4e00\u5757\u8863\u895f\uff0c\u8d70\u8fd1\u8eab\u6765\uff0c\u66ff\u4ed6\u5305\u624e\u4f24\u53e3\u3002\u8fd9\u65f6\u5979\u8138\u8272\u5374\u53c8\u53d8\u5f97\u96ea\u767d\u3002 \u3000\u3000\u72c4\u4e91\u75db\u5f97\u51e0\u6b21\u4fbf\u6b32\u6655\u53bb\uff0c\u4f46\u5f3a\u81ea\u652f\u6301\u4e0d\u5012\uff0c\u53ea\u54ac\u5f97\u5634\u5507\u51fa\u8840\uff0c\u4e00\u53e5\u8bdd\u4e5f\u8bf4\u4e0d\u51fa\u6765\u4e86\u3002 \u3000\u3000\u9c81\u5764\u9053\uff1a\u201c\u5c0f\u5e08\u5a18\uff0c\u8fd9\u72d7\u8d3c\u80c6\u6562\u5bf9\u4f60\u65e0\u793c\uff0c\u54b1\u4eec\u5b9a\u7136\u5bb0\u4e86\u4ed6\u7ed9\u4f60\u51fa\u6c14\u3002\u201d\u539f\u6765\u8fd9\u5973\u5b50\u662f\u4e07\u9707\u5c71\u7684\u5c0f\u59be\u3002\u5979\u53cc\u624b\u63a9\u8138\uff0c\u545c\u545c\u54ed\u558a\uff0c\u8bf4\u9053\uff1a\u201c\u4ed6\u2026\u2026\u4ed6\u8bf4\u4e86\u597d\u591a\u4e0d\u4e09\u4e0d\u56db\u7684\u8bdd\u3002\u4ed6\u8bf4\u4f60\u4eec\u5e08\u7236\u5df2\u7ecf\u6b7b\u4e86\uff0c\u53eb\u6211\u8ddf\u4ece\u4ed6\u3002\u4ed6\u8bf4\u621a\u59d1\u5a18\u7684\u7236\u4eb2\u6740\u4e86\u4eba\uff0c\u8981\u8fde\u7d2f\u5230\u4ed6\u3002\u4ed6\u2026\u2026\u4ed6\u53c8\u8bf4\u5df2\u5f97\u4e86\u597d\u591a\u91d1\u94f6\u73e0\u5b9d\uff0c\u53d1\u4e86\u5927\u8d22\uff0c\u53eb\u6211\u7acb\u523b\u8ddf\u4ed6\u8fdc\u8d70\u9ad8\u98de\uff0c\u4e00\u751f\u5403\u7740\u4e0d\u5b8c\u2026\u2026\u201d \u3000\u3000\u72c4\u4e91\u8111\u6d77\u4e2d\u6df7\u4e71\u4e00\u7247\uff0c\u53ea\u662f\u5583\u5583\u7684\u9053\uff1a\u201c\u5047\u7684\u2026\u2026\u5047\u7684\u2026\u2026\u201d \u3000\u3000\u5468\u573b\u5927\u58f0\u9053\uff1a\u201c\u53bb\uff0c\u53bb\uff01\u53bb\u641c\u8fd9\u5c0f\u8d3c\u7684\u623f\uff01\u201d \u3000\u3000\u4f17\u4eba\u5c06\u72c4\u4e91\u63a8\u63a8\u62c9\u62c9\uff0c\u62e5\u5411\u4ed6\u7684\u623f\u4e2d\u3002\u621a\u82b3\u832b\u7136\u8ddf\u5728\u540e\u9762\u3002 \u3000\u3000\u4e07\u572d\u5374\u9053\uff1a\u201c\u5927\u5bb6\u4e0d\u53ef\u96be\u4e3a\u72c4\u5e08\u54e5\uff0c\u4e8b\u60c5\u6ca1\u5f04\u660e\u767d\uff0c\u53ef\u4e0d\u80fd\u51a4\u6789\u4e86\u597d\u4eba\uff01\u201d\u5468\u573b\u6012\u9053\uff1a\u201c\u8fd8\u6709\u751a\u4e48\u4e0d\u660e\u767d\u7684\uff1f\u8fd9\u5c0f\u5b50\u662f\u5c41\u597d\u4eba\uff01\u201d\u4e07\u572d\u9053\uff1a\u201c\u6211\u77a7\u4ed6\u5012\u4e0d\u662f\u4e3a\u975e\u4f5c\u6b79\u4e4b\u4eba\u3002\u201d\u5468\u573b\u9053\uff1a\u201c\u521a\u624d\u4f60\u6ca1\u4eb2\u8033\u542c\u89c1\u4e48\uff1f\u6ca1\u4eb2\u773c\u77a7\u89c1\u4e48\uff1f\u201d\u4e07\u572d\u9053\uff1a\u201c\u6211\u77a7\u4ed6\u662f\u591a\u996e\u4e86\u51e0\u676f\uff0c\u4e0d\u8fc7\u662f\u9152\u540e\u4e71\u6027\u3002\u201d \u3000\u3000\u8fd9\u8bb8\u591a\u4e8b\u7eb7\u81f3\u6c93\u6765\uff0c\u621a\u82b3\u65e9\u5df2\u6ca1\u4e86\u4e3b\u610f\uff0c\u542c\u4e07\u572d\u8fd9\u4e48\u66ff\u72c4\u4e91\u5206\u8fa9\uff0c\u5fc3\u4e0b\u6697\u6697\u611f\u6fc0\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u4e07\u5e08\u5144\uff0c\u6211\u5e08\u54e5\u2026\u2026\u7684\u786e\u4e0d\u662f\u90a3\u6837\u7684\u4eba\u3002\u201d \u3000\u3000\u4e07\u572d\u9053\uff1a\u201c\u662f\u554a\uff0c\u6211\u8bf4\u4ed6\u53ea\u662f\u559d\u9189\u4e86\u9152\uff0c\u5077\u94b1\u662f\u4e00\u5b9a\u4e0d\u4f1a\u7684\u3002\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u4f17\u4eba\u5df2\u63a8\u7740\u72c4\u4e91\uff0c\u6765\u5230\u4ed6\u623f\u4e2d\u3002\u6c88\u57ce\u53cc\u773c\u9aa8\u788c\u788c\u7684\u5728\u623f\u4e2d\u8f6c\u4e86\u8f6c\uff0c\u4e00\u77ee\u8eab\uff0c\u4f38\u624b\u5728\u5e8a\u5e95\u4e0b\u62c9\u51fa\u4e00\u4e2a\u91cd\u7538\u7538\u7684\u5305\u88f9\u6765\uff0c\u4f46\u542c\u5f97\u53ee\u53ee\u5f53\u5f53\uff0c\u91d1\u5c5e\u649e\u51fb\u4e4b\u58f0\u4e71\u54cd\u3002\u72c4\u4e91\u66f4\u52a0\u60ca\u5f97\u5446\u4e86\uff0c\u53ea\u89c1\u6c88\u57ce\u89e3\u5f00\u5305\u88f9\uff0c\u6ee1\u773c\u90fd\u662f\u538b\u6241\u4e86\u7684\u91d1\u5668\u94f6\u5668\u3001\u9152\u58f6\u9152\u676f\uff0c\u4e0d\u4e00\u800c\u8db3\uff0c\u90fd\u662f\u4e07\u5e9c\u4e2d\u9152\u7b75\u4e0a\u7684\u7269\u4e8b\u3002 \u3000\u3000\u621a\u82b3\u4e00\u58f0\u60ca\u547c\uff0c\u4f38\u624b\u6276\u4f4f\u4e86\u684c\u5b50\u3002 \u3000\u3000\u4e07\u572d\u5b89\u6170\u9053\uff1a\u201c\u621a\u5e08\u59b9\uff0c\u4f60\u522b\u60ca\u614c\uff0c\u54b1\u4eec\u6162\u6162\u60f3\u6cd5\u5b50\u3002\u201d \u3000\u3000\u51af\u5766\u63ed\u8d77\u88ab\u8925\uff0c\u53c8\u662f\u4e24\u4e2a\u5305\u88f9\u3002\u6c88\u57ce\u548c\u51af\u5766\u5206\u522b\u89e3\u5f00\uff0c\u4e00\u5305\u662f\u94f6\u952d\u5143\u5b9d\uff0c\u53e6\u4e00\u5305\u5374\u662f\u5973\u5b50\u7684\u9996\u9970\uff0c\u73e0\u82b1\u9879\u94fe\u3001\u91d1\u956f\u91d1\u6212\u7684\u4e00\u5927\u5806\u3002 \u3000\u3000\u621a\u82b3\u6b64\u65f6\u66f4\u65e0\u6000\u7591\uff0c\u6028\u6124\u6b32\u7edd\uff0c\u6068\u4e0d\u5f97\u7acb\u65f6\u4fbf\u6a2a\u5251\u81ea\u520e\u3002 \u3000\u3000\u5979\u81ea\u5e7c\u548c\u72c4\u4e91\u4e00\u540c\u957f\u5927\uff0c\u5fc3\u76ee\u4e2d\u65e9\u4fbf\u5f53\u4ed6\u662f\u65e5\u540e\u7684\u592b\u90ce\uff0c\u54ea\u6599\u5230\u8fd9\u4e2a\u81ea\u5df1\u4e00\u5411\u7231\u91cd\u7684\u60c5\u4fa3\uff0c\u7adf\u4f1a\u5728\u81ea\u5df1\u906d\u9022\u6a2a\u7978\u4e4b\u65f6\uff0c\u8981\u548c\u522b\u7684\u5973\u4eba\u8fdc\u8d70\u9ad8\u98de\u3002\u96be\u9053\u8fd9\u4e2a\u5996\u5996\u5a06\u5a06\u7684\u5973\u5b50\uff0c\u4fbf\u5f53\u771f\u8ff7\u4f4f\u4e86\u4ed6\u4e48\uff1f\u8fd8\u662f\u4ed6\u5bb3\u6015\u53d7\u7239\u7239\u8fde\u7d2f\uff0c\u60f3\u72ec\u81ea\u9003\u8d70\uff1f \u3000\u3000\u9c81\u5764\u5927\u58f0\u559d\u9a82\uff1a\u201c\u81ed\u5c0f\u8d3c\uff0c\u8d43\u7269\u4ff1\u5728\uff0c\u8fd8\u60f3\u62b5\u8d56\u4e48\uff1f\u201d\u5de6\u53f3\u5f00\u5f13\uff0c\u91cd\u91cd\u6253\u4e86\u72c4\u4e91\u4e24\u8bb0\u8033\u5149\u3002\u72c4\u4e91\u53cc\u81c2\u88ab\u5b59\u5747\u3001\u5434\u574e\u5206\u522b\u6293\u4f4f\u4e86\u3002\u65e0\u6cd5\u6321\u683c\uff0c\u4e24\u8fb9\u8138\u988a\u767b\u65f6\u9ad8\u9ad8\u80bf\u80c0\u8d77\u6765\u3002\u9c81\u5764\u6253\u53d1\u4e86\u6027\uff0c\u4e00\u62f3\u62f3\u51fb\u5411\u4ed6\u80f8\u53e3\u3002 \u3000\u3000\u621a\u82b3\u53eb\u9053\uff1a\u201c\u522b\u6253\uff0c\u522b\u6253\uff0c\u6709\u8bdd\u597d\u8bf4\u3002\u201d \u3000\u3000\u5468\u573b\u9053\uff1a\u201c\u6253\u6b7b\u8fd9\u5c0f\u8d3c\uff0c\u518d\u62a5\u5b98\uff01\u201d\u8bf4\u7740\u4e5f\u662f\u4e00\u62f3\u3002\u72c4\u4e91\u53e3\u4e00\u5f20\uff0c\u55b7\u51fa\u4e00\u5927\u53e3\u8840\u6765\u3002\u51af\u5766\u633a\u5251\u4e0a\u524d\uff0c\u9053\uff1a\u201c\u5c06\u4ed6\u5de6\u624b\u4e5f\u5272\u4e0b\u4e86\uff0c\u77a7\u4ed6\u80fd\u4e0d\u80fd\u518d\u5e72\u574f\u4e8b\uff1f\u201d\u5b59\u5747\u63d0\u8d77\u72c4\u4e91\u7684\u5de6\u81c2\uff0c\u51af\u5766\u4e3e\u5251\u4fbf\u8981\u780d\u4e0b\u3002\u621a\u82b3\u201c\u554a\u201d\u7684\u4e00\u58f0\u6025\u53eb\u3002\u4e07\u572d\u9053\uff1a\u201c\u5927\u4f19\u77a7\u6211\u9762\u4e0a\uff0c\u522b\u96be\u4e3a\u4ed6\u4e86\uff0c\u54b1\u4eec\u7acb\u523b\u5c31\u9001\u5b98\u3002\u201d \u3000\u3000\u621a\u82b3\u89c1\u51af\u5766\u7f13\u7f13\u6536\u5251\uff0c\u4e24\u884c\u73e0\u6cea\u987a\u7740\u8138\u988a\u6eda\u4e86\u4e0b\u6765\uff0c\u5411\u4e07\u572d\u671b\u4e86\u4e00\u773c\uff0c\u773c\u8272\u4e2d\u5145\u6ee1\u611f\u6fc0\u4e4b\u60c5\u3002 \u3000\u3000\u201c\u4e00\u4e94\uff0c\u4e00\u5341\uff0c\u5341\u4e94\uff0c\u4e8c\u5341\u2026\u2026\u201d \u3000\u3000\u5dee\u5f79\u53e3\u4e2d\u6570\u7740\uff0c\u677f\u5b50\u7740\u529b\u5f80\u72c4\u4e91\u7684\u540e\u817f\u4e0a\u6253\u53bb\u3002\u72c4\u4e91\u8eab\u5b50\u88ab\u53e6\u5916\u4e24\u4e2a\u5dee\u5f79\u6309\u7740\uff0c\u7af9\u677f\u5b50\u4e00\u4e0b\u53c8\u4e00\u4e0b\u7684\u843d\u4e0b\u6765\u3002\u548c\u4ed6\u5fc3\u4e2d\u75db\u695a\u76f8\u6bd4\uff0c\u8fd9\u4e9b\u51fb\u6253\u6839\u672c\u7b97\u4e0d\u4e86\u751a\u4e48\uff0c\u751a\u81f3\u4ed6\u53f3\u638c\u4e0a\u7684\u75db\u695a\u4e5f\u7b97\u4e0d\u4e86\u751a\u4e48\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u53ea\u662f\u60f3\uff1a\u201c\u8fde\u82b3\u59b9\u4e5f\u5f53\u6211\u662f\u8d3c\uff0c\u8fde\u5979\u4e5f\u5f53\u6211\u662f\u8d3c\u3002\u201d \u3000\u3000\u201c\u4e8c\u5341\u4e94\u2026\u2026\u4e09\u5341\u2026\u2026\u4e09\u5341\u4e94\u2026\u2026\u56db\u5341\u2026\u2026\u201d\u677f\u5b50\u5728\u843d\uff0c\u808c\u80a4\u80bf\u4e86\uff0c\u7834\u88c2\u4e86\uff0c\u9c9c\u8840\u6cbe\u5230\u4e86\u677f\u5b50\u4e0a\uff0c\u6e85\u5728\u56db\u5468\u5730\u4e0b\u3002 \u3000\u3000\u72c4\u4e91\u5728\u76d1\u72f1\u7684\u7262\u623f\u4e2d\u9192\u6765\u65f6\uff0c\u5140\u81ea\u660f\u660f\u6c89\u6c89\uff0c\u4e0d\u77e5\u81ea\u5df1\u8eab\u5904\u4f55\u5730\uff0c\u4e5f\u4e0d\u77e5\u65f6\u5019\u5df2\u8fc7\u4e86\u591a\u4e45\uff0c\u6e10\u6e10\u7684\uff0c\u4ed6\u611f\u5230\u4e86\u53f3\u624b\u4e94\u6839\u624b\u6307\u65ad\u622a\u5904\u7684\u75bc\u75db\uff0c\u53c8\u611f\u5230\u4e86\u80cc\u4e0a\u3001\u817f\u4e0a\u3001\u81c0\u4e0a\u88ab\u677f\u5b50\u7b1e\u6253\u5904\u7684\u75bc\u75db\u3002\u4ed6\u60f3\u7ffb\u8fc7\u8eab\u6765\uff0c\u597d\u8ba9\u521b\u75db\u5904\u4e0d\u538b\u5728\u5730\u4e0a\uff0c\u7a81\u7136\u4e4b\u95f4\uff0c\u4e24\u5904\u80a9\u5934\u4e00\u9635\u96be\u4ee5\u5f62\u5bb9\u7684\u5267\u70c8\u75bc\u75db\uff0c\u53c8\u4f7f\u4ed6\u6655\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u5f85\u5f97\u518d\u6b21\u9192\u6765\uff0c\u4ed6\u9996\u5148\u542c\u5230\u4e86\u81ea\u5df1\u58f0\u5636\u529b\u7aed\u7684\u547b\u541f\uff0c\u63a5\u7740\u611f\u5230\u5168\u8eab\u5404\u5904\u7684\u5267\u75db\u3002\u53ef\u662f\u4e3a\u751a\u4e48\u80a9\u5934\u5374\u75db\u5f97\u8fd9\u4e48\u5389\u5bb3\uff1f\u4e3a\u751a\u4e48\u8fd9\u75bc\u75db\u7adf\u662f\u5982\u6b64\u7684\u96be\u4ee5\u5fcd\u53d7\uff1f\u4ed6\u53ea\u611f\u5230\u8bf4\u4e0d\u51fa\u7684\u5bb3\u6015\uff0c\u826f\u4e45\u826f\u4e45\uff0c\u7adf\u4e0d\u6562\u4f4e\u4e0b\u5934\u53bb\u770b\u3002\u201c\u96be\u9053\u6211\u4e24\u4e2a\u80a9\u8180\u90fd\u7ed9\u4eba\u524a\u53bb\u4e86\u5417\uff1f\u201d \u3000\u3000\u9694\u4e86\u4e00\u9635\uff0c\u5ffd\u7136\u542c\u5230\u94c1\u5668\u7684\u8f7b\u8f7b\u649e\u51fb\u4e4b\u58f0\uff0c\u4e00\u4f4e\u5934\uff0c\u53ea\u89c1\u4e24\u6761\u94c1\u94fe\u4ece\u81ea\u5df1\u53cc\u80a9\u5782\u4e86\u4e0b\u6765\u3002\u4ed6\u60ca\u9a87\u4e4b\u4e0b\uff0c\u4fa7\u5934\u770b\u65f6\uff0c\u53ea\u5413\u5f97\u5168\u8eab\u53d1\u98a4\u3002 \u3000\u3000\u8fd9\u4e00\u98a4\u6296\uff0c\u4e24\u80a9\u5904\u66f4\u75db\u5f97\u51f6\u4e86\u3002\u539f\u6765\u8fd9\u4e24\u6761\u94c1\u94fe\u7adf\u662f\u4ece\u4ed6\u80a9\u80db\u7684\u7435\u7436\u9aa8\u5904\u7a7f\u8fc7\uff0c\u548c\u4ed6\u53cc\u624b\u7684\u94c1\u9563\uff0c\u811a\u8e1d\u4e0a\u7684\u94c1\u94fe\u9501\u5728\u4e00\u8d77\u3002\u7a7f\u7435\u7436\u9aa8\uff0c\u4ed6\u66fe\u542c\u5e08\u7236\u8bf4\u8fc7\u7684\uff0c\u90a3\u662f\u5b98\u5e9c\u5bf9\u4ed8\u6700\u51f6\u6076\u7684\u6c5f\u6d0b\u5927\u76d7\u7684\u6cd5\u5b50\uff0c\u4efb\u4f60\u6b66\u529f\u518d\u5f3a\uff0c\u7435\u7436\u9aa8\u88ab\u94c1\u94fe\u7a7f\u8fc7\uff0c\u534a\u70b9\u529f\u592b\u4e5f\u4f7f\u4e0d\u51fa\u6765\u4e86\u3002\u970e\u65f6\u4e4b\u95f4\uff0c\u5fc3\u4e2d\u8f6c\u8fc7\u4e86\u65e0\u6570\u5ff5\u5934\uff1a\u201c\u4e3a\u751a\u4e48\u8981\u8fd9\u6837\u5bf9\u4ed8\u6211\uff1f\u96be\u9053\u4ed6\u4eec\u771f\u7684\u4ee5\u4e3a\u6211\u662f\u5927\u76d7\uff1f\u6211\u8fd9\u6837\u53d7\u51a4\u6789\uff0c\u96be\u9053\u5b98\u8001\u7237\u67e5\u4e0d\u51fa\u4e48\uff1f\u201d \u3000\u3000\u5728\u77e5\u53bf\u7684\u5927\u5802\u4e4b\u4e0a\uff0c\u4ed6\u66fe\u65ad\u65ad\u7eed\u7eed\u7684\u8bc9\u8bf4\u7ecf\u8fc7\uff0c\u4f46\u4e07\u9707\u5c71\u7684\u5c0f\u59be\u6843\u7ea2\u4e00\u529b\u6307\u8bc1\uff0c\u610f\u56fe\u5f3a\u5978\u7684\u662f\u4ed6\u800c\u4e0d\u662f\u522b\u4eba\u3002\u4e07\u5bb6\u516b\u4e2a\u5f1f\u5b50\u548c\u8bb8\u591a\u5bb6\u4eba\u90fd\u8bc1\u5b9e\uff0c\u4eb2\u773c\u770b\u5230\u4ed6\u62b1\u4f4f\u4e86\u6843\u7ea2\uff0c\u770b\u5230\u90a3\u4e9b\u8d3c\u8d43\u4ece\u4ed6\u5e8a\u5e95\u4e0b\u3001\u88ab\u8925\u5e95\u4e0b\u641c\u51fa\u6765\u3002\u8859\u95e8\u91cc\u7684\u5dee\u5f79\u53c8\u90fd\u8bf4\uff0c\u8346\u5dde\u4e07\u5bb6\u5a01\u540d\u8fdc\u9707\uff0c\u54ea\u6709\u751a\u4e48\u76d7\u8d3c\u6562\u53bb\u6253\u4e3b\u610f\u3002 \u3000\u3000\u72c4\u4e91\u8bb0\u5f97\u77e5\u53bf\u76f8\u8c8c\u6e05\u79c0\uff0c\u9762\u76ee\u5f88\u662f\u6148\u7965\u3002\u4ed6\u60f3\u77e5\u53bf\u5927\u8001\u7237\u4e00\u65f6\u542c\u4fe1\u4eba\u8a00\uff0c\u51a4\u6789\u4e86\u597d\u4eba\uff0c\u4f46\u7ec8\u7a76\u4f1a\u67e5\u5f97\u51fa\u6765\u3002\u53ef\u662f\uff0c\u53f3\u624b\u4e94\u6839\u624b\u6307\u7ed9\u524a\u65ad\u4e86\uff0c\u4ee5\u540e\u600e\u4e48\u518d\u80fd\u4f7f\u5251\uff1f \u3000\u3000\u4ed6\u6ee1\u8154\u6124\u6012\uff0c\u6ee1\u8179\u60b2\u6068\uff0c\u4e0d\u987e\u75bc\u75db\u7684\u7ad9\u8d77\u8eab\u6765\uff0c\u5927\u58f0\u53eb\u558a\uff1a\u201c\u51a4\u6789\uff0c\u51a4\u6789\uff01\u201d\u5ffd\u7136\u817f\u4e0a\u4e00\u9635\u9178\u8f6f\uff0c\u4fef\u8eab\u5411\u5730\u76f4\u6454\u4e86\u4e0b\u53bb\u3002\u4ed6\u6323\u624e\u7740\u53c8\u60f3\u722c\u8d77\uff0c\u521a\u521a\u7ad9\u76f4\uff0c\u817f\u819d\u9178\u8f6f\uff0c\u53c8\u5411\u524d\u6454\u5012\u3002 \u3000\u3000\u4ed6\u722c\u5728\u5730\u4e0b\uff0c\u4ecd\u662f\u5927\u53eb\uff1a\u201c\u51a4\u6789\uff0c\u51a4\u6789\uff01\u201d \u3000\u3000\u5c4b\u89d2\u4e2d\u5ffd\u6709\u4e00\u4e2a\u58f0\u97f3\u51b7\u51b7\u7684\u8bf4\u9053\uff1a\u201c\u7ed9\u4eba\u7a7f\u4e86\u7435\u7436\u9aa8\uff0c\u4e00\u8eab\u529f\u592b\u90fd\u5e9f\u4e86\uff0c\u563f\u563f\uff0c\u563f\u563f\uff01\u4e0b\u7684\u672c\u94b1\u53ef\u771f\u4e0d\u5c0f\uff01\u201d\u72c4\u4e91\u4e5f\u4e0d\u7406\u4f1a\u8bf4\u8bdd\u7684\u662f\u8c01\uff0c\u66f4\u4e0d\u53bb\u7406\u4f1a\u8fd9\u51e0\u53e5\u8bdd\u662f\u751a\u4e48\u610f\u601d\uff0c\u4ecd\u662f\u5927\u53eb\uff1a\u201c\u51a4\u6789\uff0c\u51a4\u6789\uff01\u201d \u3000\u3000\u4e00\u540d\u72f1\u5352\u8d70\u4e86\u8fc7\u6765\uff0c\u559d\u9053\uff1a\u201c\u5927\u547c\u5c0f\u53eb\u7684\u5e72\u751a\u4e48\uff1f\u8fd8\u4e0d\u7ed9\u6211\u95ed\u5634\uff01\u201d\u72c4\u4e91\u53eb\u9053\uff1a\u201c\u51a4\u6789\uff0c\u51a4\u6789\uff01\u6211\u8981\u89c1\u77e5\u53bf\u5927\u8001\u7237\uff0c\u8981\u6c42\u4ed6\u7533\u51a4\u3002\u201d\u90a3\u72f1\u5352\u559d\u9053\uff1a\u201c\u4f60\u95ed\u4e0d\u95ed\u5634\uff1f\u201d\u72c4\u4e91\u53cd\u800c\u53eb\u5f97\u66f4\u54cd\u4e86\u3002 \u3000\u3000\u90a3\u72f1\u5352\u72de\u7b11\u4e00\u58f0\uff0c\u8f6c\u8eab\u63d0\u4e86\u4e00\u53ea\u6728\u6876\uff0c\u9694\u7740\u94c1\u680f\uff0c\u515c\u5934\u4fbf\u5c06\u6728\u6876\u5411\u4ed6\u8eab\u4e0a\u5012\u4e86\u4e0b\u53bb\u3002\u72c4\u4e91\u53ea\u611f\u4e00\u9635\u81ed\u6c14\u523a\u9f3b\uff0c\u5df2\u4e0d\u53ca\u95ea\u907f\uff0c\u5168\u8eab\u767b\u65f6\u6e7f\u900f\uff0c\u8fd9\u4e00\u6876\u7adf\u662f\u5c3f\u6c34\u3002\u5c3f\u6c34\u6dcb\u5728\u4ed6\u8eab\u4e0a\u5404\u5904\u7834\u635f\u7684\u521b\u53e3\uff0c\u75bc\u75db\u66f4\u662f\u52a0\u500d\u7684\u5389\u5bb3\u3002\u4ed6\u773c\u524d\u4e00\u9ed1\uff0c\u6655\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u4ed6\u8ff7\u8ff7\u7cca\u7cca\u7684\u53d1\u7740\u9ad8\u70e7\uff0c\u4e00\u65f6\u5524\u7740\uff1a\u201c\u5e08\u7236\uff0c\u5e08\u7236\uff01\u201d\u4e00\u65f6\u53c8\u53eb\uff1a\u201c\u5e08\u59b9\uff0c\u5e08\u59b9\uff01\u201d\u63a5\u8fde\u4e09\u5929\u4e4b\u4e2d\uff0c\u72f1\u5352\u9001\u4e86\u7cd9\u7c73\u996d\u6765\uff0c\u4ed6\u4e00\u76f4\u795e\u667a\u4e0d\u6e05\uff0c\u6ca1\u5403\u8fc7\u4e00\u53e3\u3002 \u3000\u3000\u5230\u5f97\u7b2c\u56db\u65e5\u4e0a\uff0c\u8eab\u4e0a\u7684\u70e7\u7ec8\u4e8e\u6e10\u6e10\u9000\u4e86\u3002\u5404\u5904\u521b\u53e3\u75db\u5f97\u9ebb\u6728\u4e86\uff0c\u5df2\u4e0d\u5982\u524d\u51e0\u65e5\u90a3\u4e48\u5267\u70c8\u96be\u5fcd\u3002\u4ed6\u8bb0\u8d77\u4e86\u81ea\u5df1\u7684\u51a4\u5c48\uff0c\u5f20\u53e3\u53c8\u53eb\uff1a\u201c\u51a4\u6789\uff01\u201d\u4f46\u8fd9\u65f6\u53eb\u51fa\u6765\u7684\u58f0\u97f3\u5fae\u5f31\u4e4b\u6781\uff0c\u53ea\u662f\u65ad\u65ad\u7eed\u7eed\u7684\u51e0\u4e0b\u547b\u541f\u3002 \u3000\u3000\u4ed6\u5750\u4e86\u4e00\u9635\uff0c\u832b\u7136\u6253\u91cf\u8fd9\u95f4\u7262\u623f\u3002\u90a3\u662f\u7ea6\u83ab\u4e24\u4e08\u89c1\u65b9\u7684\u4e00\u95f4\u5927\u77f3\u5c4b\uff0c\u5899\u58c1\u90fd\u662f\u4e00\u5757\u5757\u7c97\u7cd9\u7684\u5927\u77f3\u6240\u780c\uff0c\u5730\u4e0b\u4e5f\u662f\u5927\u77f3\u5757\u94fa\u6210\uff0c\u5899\u89d2\u843d\u91cc\u653e\u7740\u4e00\u53ea\u7caa\u6876\uff0c\u9f3b\u4e2d\u95fb\u5230\u7684\u5c3d\u662f\u81ed\u6c14\u548c\u9709\u6c14\u3002 \u3000\u3000\u4ed6\u7f13\u7f13\u8f6c\u8fc7\u5934\u6765\uff0c\u53ea\u89c1\u897f\u9996\u5c4b\u89d2\u4e4b\u4e2d\uff0c\u4e00\u5bf9\u773c\u775b\u72e0\u72e0\u7684\u77aa\u89c6\u7740\u4ed6\u3002\u72c4\u4e91\u8eab\u5b50\u4e00\u98a4\uff0c\u6ca1\u60f3\u5230\u8fd9\u7262\u623f\u4e2d\u5c45\u7136\u8fd8\u6709\u522b\u4eba\u3002\u53ea\u89c1\u8fd9\u4eba\u6ee1\u8138\u866c\u9aef\uff0c\u5934\u53d1\u957f\u957f\u7684\u76f4\u5782\u81f3\u9888\uff0c\u8863\u886b\u7834\u70c2\u4e0d\u582a\uff0c\u7b80\u76f4\u5982\u540c\u8352\u5c71\u4e2d\u7684\u91ce\u4eba\u3002\u4ed6\u624b\u4e0a\u624b\u94d0\uff0c\u8db3\u4e0a\u8db3\u9563\uff0c\u548c\u81ea\u5df1\u4e00\u6a21\u4e00\u6837\uff0c\u751a\u81f3\u7435\u7436\u9aa8\u4e2d\u4e5f\u7a7f\u7740\u4e24\u6761\u94c1\u94fe\u3002 \u3000\u3000\u72c4\u4e91\u5fc3\u4e2d\u7b2c\u4e00\u4e2a\u5ff5\u5934\u7adf\u662f\u6b22\u559c\uff0c\u5634\u89d2\u8fb9\u95ea\u8fc7\u4e86\u4e00\u4e1d\u5fae\u7b11\uff0c\u5fc3\u4e2d\u60f3\uff1a\u201c\u539f\u6765\u4e16\u754c\u4e0a\u8fd8\u6709\u5982\u6211\u4e00\u822c\u4e0d\u5e78\u7684\u4eba\u3002\u201d\u4f46\u968f\u5373\u8f6c\u5ff5\uff1a \u3000\u3000\u201c\u8fd9\u4eba\u5982\u6b64\u51f6\u6076\uff0c\u60f3\u5fc5\u771f\u662f\u4e2a\u6740\u4eba\u653e\u706b\u3001\u65e0\u6076\u4e0d\u4f5c\u7684\u6c5f\u6d0b\u5927\u76d7\u3002 \u3000\u3000\u4ed6\u662f\u7f6a\u6709\u5e94\u5f97\uff0c\u6211\u5374\u662f\u51a4\u6789\uff01\u201d\u60f3\u5230\u8fd9\u91cc\uff0c\u4e0d\u7981\u773c\u6cea\u4e00\u8fde\u4e32\u7684\u6389\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u4ed6\u53d7\u5ba1\u88ab\u7b1e\uff0c\u7405\u73f0\u5165\u72f1\uff0c\u867d\u7136\u5403\u5c3d\u4e86\u82e6\u695a\uff0c\u5374\u4e00\u76f4\u54ac\u7d27\u7259\u5173\u5f3a\u5fcd\uff0c\u4ece\u6ca1\u6d41\u8fc7\u534a\u6ef4\u773c\u6cea\uff0c\u5230\u8fd9\u65f6\u518d\u4e5f\u6291\u5236\u4e0d\u4f4f\uff0c\u7d22\u6027\u653e\u58f0\u5927\u54ed\u8d77\u6765\u3002 \u3000\u3000\u90a3\u866c\u9aef\u72af\u4eba\u51b7\u7b11\u9053\uff1a\u201c\u88c5\u5f97\u771f\u50cf\uff0c\u597d\u672c\u4e8b\uff01\u4f60\u662f\u4e2a\u620f\u5b50\u4e48\uff1f\u201d \u3000\u3000\u72c4\u4e91\u4e0d\u53bb\u7406\u4ed6\uff0c\u81ea\u7ba1\u81ea\u7684\u5927\u58f0\u54ed\u558a\u3002\u53ea\u542c\u5f97\u811a\u6b65\u58f0\u54cd\uff0c\u90a3\u72f1\u5352\u53c8\u63d0\u4e86\u4e00\u6876\u5c3f\u6c34\u8fc7\u6765\u3002\u72c4\u4e91\u6027\u5b50\u518d\u786c\uff0c\u5374\u4e5f\u4e0d\u6562\u8ddf\u4ed6\u9876\u649e\uff0c\u53ea\u5f97\u6162\u6162\u6536\u4f4f\u4e86\u54ed\u58f0\u3002\u90a3\u72f1\u5352\u4fa7\u5934\u5411\u4ed6\u6253\u91cf\uff0c\u5ffd\u7136\u8bf4\u9053\uff1a\u201c\u5c0f\u8d3c\uff0c\u6709\u4eba\u77a7\u4f60\u6765\u7740\u3002\u201d \u3000\u3000\u72c4\u4e91\u53c8\u60ca\u53c8\u559c\uff0c\u5fd9\u9053\uff1a\u201c\u662f\u2026\u2026\u662f\u8c01\uff1f\u201d\u90a3\u72f1\u5352\u53c8\u4fa7\u5934\u5411\u4ed6\u6253\u91cf\u4e86\u4e00\u4f1a\uff0c\u4ece\u8eab\u8fb9\u638f\u51fa\u4e00\u679a\u5927\u94c1\u5319\uff0c\u5f00\u4e86\u5916\u8fb9\u7684\u94c1\u95e8\u3002\u53ea\u542c\u5f97\u811a\u6b65\u58f0\u54cd\uff0c\u90a3\u72f1\u5352\u8d70\u8fc7\u4e00\u6761\u957f\u957f\u7684\u752c\u9053\uff0c\u53c8\u662f\u5f00\u94c1\u95e8\u7684\u58f0\u97f3\uff0c\u63a5\u7740\u662f\u5173\u94c1\u95e8\u3001\u9501\u94c1\u95e8\u7684\u58f0\u97f3\uff0c\u752c\u9053\u4e2d\u4e09\u4e2a\u4eba\u7684\u811a\u6b65\u58f0\u97f3\uff0c\u5411\u7740\u8fd9\u8fb9\u8d70\u6765\u3002 \u3000\u3000\u72c4\u4e91\u5927\u559c\uff0c\u5f53\u5373\u8dc3\u8d77\uff0c\u817f\u4e0a\u4e00\u8f6f\uff0c\u4fbf\u8981\u6454\u5012\uff0c\u5fd9\u9760\u4f4f\u8eab\u65c1\u7684\u5899\u58c1\uff0c\u8fd9\u4e00\u7275\u52a8\u80a9\u5934\u7684\u7435\u7436\u9aa8\uff0c\u53c8\u662f\u4e00\u9635\u5927\u75db\u3002\u4f46\u4ed6\u6ee1\u6000\u6b23\u559c\uff0c\u628a\u75bc\u75db\u5168\u90fd\u5fd8\u4e86\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u5e08\u7236\uff0c\u5e08\u59b9\uff01\u201d\u4ed6\u5728\u4e16\u4e0a\u53ea\u6709\u5e08\u7236\u548c\u5e08\u59b9\u4e24\u4e2a\u4eb2\u4eba\uff0c\u752c\u9053\u4e2d\u9664\u4e86\u72f1\u5352\u4e4b\u5916\u5c1a\u6709\u4e24\u4eba\uff0c\u81ea\u7136\u662f\u5e08\u7236\u548c\u5e08\u59b9\u4e86\u3002 \u3000\u3000\u7a81\u7136\u4e4b\u95f4\uff0c\u4ed6\u53e3\u4e2d\u558a\u51fa\u4e00\u4e2a\u201c\u5e08\u201d\u5b57\uff0c\u4e0b\u9762\u8fd9\u4e2a\u201c\u7236\u201d\u5b57\u5374\u7f29\u5728\u5589\u5934\uff0c\u5f20\u5927\u4e86\u5634\uff0c\u95ed\u4e0d\u62e2\u6765\u3002\u4ece\u94c1\u95e8\u4e2d\u8fdb\u6765\u7684\uff0c\u7b2c\u4e00\u4e2a\u662f\u72f1\u5352\uff0c\u7b2c\u4e8c\u4e2a\u662f\u4e2a\u8863\u9970\u534e\u4e3d\u7684\u82f1\u4fca\u5c11\u5e74\uff0c\u5374\u662f\u4e07\u572d\uff0c\u7b2c\u4e09\u4e2a\u4fbf\u662f\u621a\u82b3\u3002 \u3000\u3000\u5979\u5927\u53eb\uff1a\u201c\u5e08\u54e5\uff0c\u5e08\u54e5\uff01\u201d\u6251\u5230\u4e86\u94c1\u6805\u680f\u65c1\u3002 \u3000\u3000\u72c4\u4e91\u8d70\u4e0a\u4e00\u6b65\uff0c\u89c1\u5230\u5979\u4e00\u8eab\u7ef8\u886b\uff0c\u5e76\u4e0d\u662f\u4ece\u4e61\u95f4\u7a7f\u51fa\u6765\u7684\u90a3\u5957\u65b0\u8863\uff0c\u7b2c\u4e8c\u6b65\u4fbf\u4e0d\u518d\u8de8\u4e86\u51fa\u53bb\u3002\u4f46\u89c1\u5979\u53cc\u76ee\u7ea2\u80bf\uff0c\u53ea\u53eb\uff1a\u201c\u5e08\u54e5\uff0c\u5e08\u54e5\uff0c\u4f60\u2026\u2026\u4f60\u2026\u2026\u201d \u3000\u3000\u72c4\u4e91\u95ee\u9053\uff1a\u201c\u5e08\u7236\u5462\uff1f\u53ef\u2026\u2026\u53ef\u627e\u5230\u4e86\u4ed6\u8001\u4eba\u5bb6\u4e48\uff1f\u201d\u621a\u82b3\u6447\u4e86\u6447\u5934\uff0c\u773c\u6cea\u6251\u7c0c\u7c0c\u7684\u6389\u4e86\u4e0b\u6765\u3002\u72c4\u4e91\u53c8\u95ee\uff1a\u201c\u4f60\u2026\u2026\u4f60\u53ef\u597d\uff1f\u4f4f\u5728\u54ea\u91cc\uff1f\u201d\u621a\u82b3\u62bd\u62bd\u564e\u564e\u7684\u9053\uff1a\u201c\u6211\u6ca1\u5730\u65b9\u53bb\uff0c\u6682\u4e14\u4f4f\u5728\u4e07\u5e08\u54e5\u5bb6\u91cc\u2026\u2026\u201d\u72c4\u4e91\u5927\u58f0\u53eb\u9053\uff1a\u201c\u8fd9\u662f\u5bb3\u4eba\u7684\u5730\u65b9\uff0c\u5343\u4e07\u4f4f\u4e0d\u5f97\uff0c\u5feb\u2026\u2026\u5feb\u642c\u4e86\u51fa\u53bb\u3002\u201d\u621a\u82b3\u4f4e\u4e0b\u4e86\u5934\uff0c\u8f7b\u58f0\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\u53c8\u6ca1\u94b1\u3002\u4e07\u5e08\u54e5\u2026\u2026\u5f85\u6211\u5f88\u597d\uff0c\u4ed6\u8fd9\u51e0\u5929\u2026\u2026\u5929\u5929\u4e0a\u8859\u95e8\uff0c\u82b1\u94b1\u6253\u70b9\u2026\u2026\u642d\u6551\u4f60\u3002\u201d \u3000\u3000\u72c4\u4e91\u66f4\u662f\u607c\u6012\uff0c\u5927\u58f0\u9053\uff1a\u201c\u6211\u53c8\u6ca1\u72af\u7f6a\uff0c\u8981\u4ed6\u82b1\u751a\u4e48\u94b1\uff1f \u3000\u3000\u5c06\u6765\u54b1\u4eec\u600e\u751f\u8fd8\u4ed6\uff1f\u77e5\u53bf\u5927\u8001\u7237\u67e5\u660e\u4e86\u6211\u7684\u51a4\u6789\uff0c\u81ea\u4f1a\u653e\u6211\u51fa\u53bb\u3002\u201d \u3000\u3000\u621a\u82b3\u201c\u554a\u201d\u7684\u4e00\u58f0\uff0c\u53c8\u54ed\u4e86\u51fa\u6765\uff0c\u6068\u6068\u7684\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u4e3a\u751a\u4e48\u8981\u505a\u8fd9\u79cd\u4e8b\uff1f\u4e3a\u2026\u2026\u4e3a\u751a\u4e48\u8981\u6487\u4e0b\u6211\uff1f\u201d \u3000\u3000\u72c4\u4e91\u4e00\u6014\uff0c\u767b\u65f6\u660e\u767d\u4e86\uff0c\u5230\u8fd9\u65f6\u5019\uff0c\u5e08\u59b9\u8fd8\u662f\u4ee5\u4e3a\u6843\u7ea2\u7684\u8bdd\u662f\u771f\u7684\uff0c\u76f8\u4fe1\u8fd9\u51e0\u5305\u91d1\u94f6\u73e0\u5b9d\u786e\u662f\u81ea\u5df1\u5077\u7684\u3002\u4ed6\u4e00\u751f\u5bf9\u621a\u82b3\u53c8\u656c\u53c8\u7231\uff0c\u53c8\u601c\u53c8\u754f\uff0c\u751a\u4e48\u4e8b\u90fd\u8ddf\u5979\u8bf4\uff0c\u751a\u4e48\u4e8b\u90fd\u8ddf\u5979\u5546\u91cf\uff0c\u54ea\u77e5\u9053\u4e00\u9047\u4e0a\u8fd9\u7b49\u5927\u4e8b\uff0c\u5979\u7adf\u548c\u65c1\u4eba\u4e1d\u6beb\u6ca1\u6709\u5206\u522b\uff0c\u4e00\u822c\u7684\u4e5f\u8ba4\u4e3a\u81ea\u5df1\u53bb\u903c\u5978\u5973\u5b50\uff0c\u5077\u76d7\u91d1\u94f6\uff0c\u4ee5\u4e3a\u81ea\u5df1\u80fd\u505a\u8fd9\u79cd\u574f\u4e8b\u3002 \u3000\u3000\u8fd9\u77ac\u606f\u4e4b\u95f4\uff0c\u4ed6\u5fc3\u4e2d\u611f\u5230\u7684\u75db\u695a\uff0c\u6bd4\u4e4b\u8089\u4f53\u4e0a\u6240\u53d7\u79cd\u79cd\u75bc\u75db\u66f4\u80dc\u767e\u500d\u3002\u4ed6\u5f20\u53e3\u7ed3\u820c\uff0c\u6709\u5343\u8a00\u4e07\u8bed\u8981\u5411\u621a\u82b3\u8fa9\u767d\uff0c\u53ef\u662f\u5589\u5499\u5ffd\u7136\u54d1\u4e86\uff0c\u534a\u53e5\u8bdd\u4e5f\u8bf4\u4e0d\u51fa\u6765\u3002\u4ed6\u62da\u547d\u7528\u529b\uff0c\u6da8\u5f97\u9762\u7ea2\u8033\u8d64\uff0c\u4f46\u5589\u5499\u820c\u5934\u603b\u662f\u4e0d\u542c\u4f7f\u5524\uff0c\u53d1\u4e0d\u51fa\u4e1d\u6beb\u58f0\u97f3\u3002 \u3000\u3000\u621a\u82b3\u89c1\u5230\u4ed6\u8fd9\u7b49\u53ef\u6016\u7684\u795e\u60c5\uff0c\u5bb3\u6015\u8d77\u6765\uff0c\u8f6c\u8fc7\u4e86\u5934\u4e0d\u6562\u77a7\u4ed6\u3002 \u3000\u3000\u72c4\u4e91\u4f7f\u4e86\u534a\u5929\u52b2\uff0c\u59cb\u7ec8\u8bf4\u4e0d\u51fa\u4e00\u4e2a\u5b57\uff0c\u5ffd\u89c1\u621a\u82b3\u8f6c\u5934\u907f\u5f00\u81ea\u5df1\uff0c\u4e0d\u7531\u5f97\u5fc3\u4e2d\u5927\u6078\uff1a\u201c\u5979\u5728\u6068\u6211\uff0c\u6068\u6211\u629b\u5f03\u4e86\u5979\u53bb\u627e\u522b\u4e2a\u5973\u5b50\uff0c\u6068\u6211\u5077\u76d7\u522b\u4eba\u7684\u91d1\u94f6\u73e0\u5b9d\uff0c\u6068\u6211\u5728\u5e08\u95e8\u6709\u96be\u4e4b\u65f6\u60f3\u5077\u5077\u4e00\u4eba\u8fdc\u8d70\u9ad8\u98de\u3002\u5e08\u59b9\uff0c\u5e08\u59b9\uff0c\u4f60\u8fd9\u4e48\u4e0d\u76f8\u4fe1\u6211\uff0c\u53c8\u4f55\u5fc5\u6765\u770b\u6211\uff1f\u201d\u4ed6\u518d\u4e5f\u4e0d\u6562\u53bb\u77a7\u621a\u82b3\uff0c\u6162\u6162\u8f6c\u5934\u6765\uff0c\u5411\u7740\u5899\u58c1\u3002 \u3000\u3000\u621a\u82b3\u56de\u8fc7\u8138\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u5e08\u54e5\uff0c\u8fc7\u53bb\u7684\u4e8b\uff0c\u4e5f\u4e0d\u7528\u518d\u8bf4\u4e86\uff0c\u53ea\u76fc\u65e9\u65e5\u2026\u2026\u65e9\u65e5\u5f97\u5230\u7239\u7239\u8baf\u606f\u3002\u4e07\u5e08\u54e5\u4ed6\u2026\u2026\u4ed6\u5728\u60f3\u6cd5\u5b50\u4fdd\u4f60\u51fa\u53bb\u2026\u2026\u201d \u3000\u3000\u72c4\u4e91\u5fc3\u4e2d\u60f3\u8bf4\uff1a\u201c\u6211\u4e0d\u8981\u4ed6\u4fdd\u3002\u201d\u53c8\u60f3\u8bf4\uff1a\u201c\u4f60\u522b\u4f4f\u5728\u4ed6\u5bb6\u91cc\u3002\u201d\u4f46\u8d8a\u662f\u7528\u529b\uff0c\u5168\u8eab\u808c\u8089\u8d8a\u662f\u7d27\u5f20\u62bd\u6410\uff0c\u8bf4\u4e0d\u51fa\u4e00\u4e2a\u5b57\u6765\u3002 \u3000\u3000\u4ed6\u8eab\u5b50\u4e0d\u4f4f\u6296\u52a8\uff0c\u94c1\u94fe\u94ee\u94ee\u4f5c\u54cd\u3002 \u3000\u3000\u90a3\u72f1\u5352\u50ac\u9053\uff1a\u201c\u65f6\u5019\u5230\u5566\u3002\u8fd9\u662f\u6b7b\u56da\u7262\uff0c\u4e13\u56da\u6740\u4eba\u91cd\u72af\uff0c\u539f\u662f\u4e0d\u8bb8\u4eba\u63a2\u76d1\u7684\u3002\u4e0a\u9762\u8981\u662f\u77e5\u9053\u4e86\uff0c\u6211\u4eec\u53ef\u5403\u7f6a\u4e0d\u8d77\u3002\u59d1\u5a18\uff0c\u8fd9\u4eba\u4fbf\u6d3b\u7740\u51fa\u53bb\uff0c\u4e5f\u662f\u4e2a\u5e9f\u4eba\u3002\u4f60\u4e58\u65e9\u5fd8\u4e86\u4ed6\uff0c\u5ac1\u4e2a\u6709\u94b1\u7684\u6f02\u4eae\u5c11\u7237\u7f62\uff01\u201d\u8bf4\u7740\u5411\u4e07\u572d\u77a7\u4e86\u4e00\u773c\uff0c\u8272\u8ff7\u8ff7\u7684\u7b11\u4e86\u8d77\u6765\u3002 \u3000\u3000\u621a\u82b3\u6c42\u9053\uff1a\u201c\u5927\u53d4\uff0c\u6211\u8fd8\u6709\u51e0\u53e5\u8bdd\u8ddf\u6211\u5e08\u54e5\u8bf4\u3002\u201d\u4f38\u624b\u5230\u94c1\u6805\u680f\u5185\uff0c\u53bb\u62c9\u72c4\u4e91\u7684\u8863\u8896\uff0c\u67d4\u58f0\u8bf4\u9053\uff1a\u201c\u5e08\u54e5\uff0c\u4f60\u653e\u5fc3\u597d\u5566\uff0c\u6211\u4e00\u5b9a\u6c42\u4e07\u5e08\u54e5\u6551\u4f60\u51fa\u53bb\uff0c\u54b1\u4eec\u4e00\u5757\u53bb\u627e\u7239\u7239\u3002\u201d\u5c06\u4e00\u53ea\u5c0f\u7af9\u7bee\u9012\u4e86\u8fdb\u53bb\uff0c\u9053\uff1a\u201c\u90a3\u662f\u4e9b\u814a\u8089\u3001\u814a\u9c7c\u3001\u719f\u9e21\u86cb\uff0c\u8fd8\u6709\u4e8c\u4e24\u94f6\u5b50\u3002\u5e08\u54e5\uff0c\u6211\u660e\u5929\u518d\u6765\u77a7\u4f60\u2026\u2026\u201d \u3000\u3000\u90a3\u72f1\u5352\u4e0d\u8010\u70e6\u4e86\uff0c\u559d\u9053\uff1a\u201c\u5927\u59d1\u5a18\uff0c\u4f60\u518d\u4e0d\u8d70\uff0c\u6211\u53ef\u8981\u4e0d\u5ba2\u6c14\u5566\uff01\u201d \u3000\u3000\u4e07\u572d\u8fd9\u65f6\u624d\u5f00\u53e3\u9053\uff1a\u201c\u72c4\u5e08\u5144\uff0c\u4f60\u653e\u5fc3\u7f62\u3002\u4f60\u7684\u4e8b\u5c31\u662f\u6211\u7684\u4e8b\uff0c\u5c0f\u5f1f\u81ea\u4f1a\u5c3d\u529b\u5411\u53bf\u592a\u7237\u6c42\u60c5\uff0c\u5c06\u4f60\u7684\u7f6a\u5b9a\u5f97\u8d8a\u8f7b\u8d8a\u597d\u3002\u201d \u3000\u3000\u90a3\u72f1\u5352\u8fde\u58f0\u50ac\u4fc3\uff0c\u621a\u82b3\u65e0\u53ef\u5948\u4f55\uff0c\u53ea\u5f97\u59d4\u59d4\u5c48\u5c48\u7684\u8d70\u4e86\u51fa\u53bb\uff0c\u4e00\u6b65\u4e00\u56de\u5934\u7684\u77a7\u7740\u72c4\u4e91\uff0c\u4f46\u89c1\u4ed6\u4fbf\u5982\u4e00\u5c0a\u77f3\u50cf\u4e00\u822c\uff0c\u59cb\u7ec8\u4e00\u52a8\u4e0d\u52a8\u7684\u5411\u7740\u5899\u58c1\u3002 \u3000\u3000\u72c4\u4e91\u773c\u4e2d\u6240\u89c1\u7684\uff0c\u53ea\u662f\u77f3\u58c1\u4e0a\u7684\u51f9\u51f8\u8d77\u4f0f\uff0c\u4ed6\u771f\u60f3\u8f6c\u8fc7\u5934\u6765\uff0c\u671b\u4e00\u773c\u621a\u82b3\u7684\u80cc\u5f71\uff0c\u60f3\u53eb\u5979\u4e00\u58f0\u201c\u5e08\u59b9\u201d\uff0c\u53ef\u662f\u4e0d\u4f46\u53e3\u4e2d\u8bf4\u4e0d\u51fa\u8bdd\uff0c\u8fde\u5934\u9888\u4e5f\u50f5\u76f4\u4e86\u3002\u4ed6\u542c\u5230\u752c\u9053\u4e2d\u4e09\u4e2a\u4eba\u7684\u811a\u6b65\u58f0\u6e10\u6e10\u8fdc\u53bb\uff0c\u542c\u5230\u5f00\u9501\u3001\u5f00\u94c1\u95e8\u7684\u58f0\u97f3\uff0c\u542c\u5230\u752c\u9053\u4e2d\u72f1\u5352\u4e00\u4e2a\u4eba\u56de\u6765\u7684\u811a\u6b65\u58f0\uff0c\u5fc3\u60f3\uff1a\u201c\u5979\u8bf4\u660e\u5929\u518d\u6765\u770b\u6211\u3002\u5509\uff0c\u53ef\u5f97\u518d\u7b49\u957f\u957f\u7684\u4e00\u5929\uff0c\u6211\u624d\u80fd\u518d\u89c1\u5230\u5979\u3002\u201d \u3000\u3000\u4ed6\u4f38\u624b\u5230\u7af9\u7bee\u4e2d\u53bb\u53d6\u98df\u7269\u3002\u5ffd\u7136\u4e00\u53ea\u6bdb\u8338\u8338\u7684\u5927\u624b\u4f38\u5c06\u8fc7\u6765\uff0c\u5c06\u7af9\u7bee\u62a2\u4e86\u8fc7\u53bb\uff0c\u6b63\u662f\u90a3\u4e2a\u51f6\u6076\u7684\u72af\u4eba\u3002\u53ea\u89c1\u4ed6\u6293\u8d77\u7bee\u4e2d\u4e00\u5757\u814a\u8089\uff0c\u653e\u5165\u53e3\u4e2d\u56bc\u4e86\u8d77\u6765\u3002 \u3000\u3000\u72c4\u4e91\u6012\u9053\uff1a\u201c\u8fd9\u662f\u6211\u7684\uff01\u201d\u4ed6\u7a81\u7136\u80fd\u5f00\u53e3\u8bf4\u8bdd\u4e86\uff0c\u81ea\u5df1\u89c9\u5f97\u5341\u5206\u5947\u602a\u3002\u4ed6\u8d70\u4e0a\u4e00\u6b65\uff0c\u60f3\u53bb\u62a2\u593a\u3002\u90a3\u72af\u4eba\u4f38\u624b\u4e00\u63a8\uff0c\u72c4\u4e91\u7ad9\u7acb\u4e0d\u5b9a\uff0c\u4e00\u4ea4\u5411\u540e\u6454\u51fa\uff0c\u7830\u7684\u4e00\u58f0\uff0c\u540e\u8111\u649e\u5728\u77f3\u5899\u4e4b\u4e0a\u3002 \u3000\u3000\u8fd9\u65f6\u5019\u4ed6\u624d\u660e\u767d\u201c\u7a7f\u7435\u7436\u9aa8\uff0c\u6210\u4e86\u5e9f\u4eba\u201d\u7684\u771f\u6b63\u610f\u601d\u3002 \u3000\u3000\u7b2c\u4e8c\u5929\u621a\u82b3\u5374\u6ca1\u6765\u770b\u4ed6\u3002\u7b2c\u4e09\u5929\u6ca1\u6765\uff0c\u7b2c\u56db\u5929\u4e5f\u6ca1\u6709\u3002 \u3000\u3000\u72c4\u4e91\u4e00\u5929\u53c8\u4e00\u5929\u7684\u76fc\u671b\u3001\u5931\u671b\uff0c\u7b49\u5230\u7b2c\u5341\u5929\u4e0a\uff0c\u4ed6\u51e0\u4e4e\u8981\u53d1\u75af\u4e86\u3002\u4ed6\u53eb\u5524\uff0c\u5435\u95f9\uff0c\u5c06\u5934\u5728\u5899\u4e0a\u78b0\u649e\uff0c\u4f46\u621a\u82b3\u59cb\u7ec8\u6ca1\u6709\u6765\uff0c\u6362\u6765\u7684\u53ea\u6709\u72f1\u5352\u6dcb\u6765\u7684\u5c3f\u6c34\u3001\u90a3\u51f6\u5f92\u7684\u6bb4\u51fb\u3002 \u3000\u3000\u8fc7\u5f97\u534a\u4e2a\u6708\uff0c\u4ed6\u7ec8\u4e8e\u6e10\u6e10\u5b89\u9759\u4e0b\u6765\uff0c\u53d8\u6210\u4e00\u53e5\u8bdd\u4e5f\u4e0d\u8bf4\u3002 \u3000\u3000\u4e00\u5929\u665a\u4e0a\uff0c\u5ffd\u7136\u6709\u56db\u540d\u72f1\u5352\u8d70\u8fdb\u7262\u6765\uff0c\u624b\u4e2d\u90fd\u6267\u7740\u94a2\u5200\uff0c\u62bc\u4e86\u90a3\u51f6\u5f92\u51fa\u53bb\u3002 \u3000\u3000\u72c4\u4e91\u5fc3\u60f3\uff1a\u201c\u662f\u62bc\u4ed6\u51fa\u53bb\u5904\u51b3\u65a9\u9996\u7f62\uff1f\u90a3\u5bf9\u4ed6\u5012\u597d\uff0c\u4ee5\u540e\u4e0d\u7528\u518d\u6328\u8fd9\u79cd\u82e6\u65e5\u5b50\u4e86\uff0c\u6211\u4e5f\u4e0d\u7528\u518d\u53d7\u4ed6\u6b3a\u4fae\u3002\u201d \u3000\u3000\u4ed6\u6b63\u7761\u5f97\u6726\u6726\u80e7\u80e7\uff0c\u5ffd\u7136\u542c\u5f97\u94c1\u94fe\u66f3\u5730\u7684\u58f0\u97f3\uff0c\u56db\u540d\u72f1\u5352\u67b6\u4e86\u90a3\u51f6\u5f92\u56de\u6765\u3002\u72c4\u4e91\u7741\u5f00\u773c\u6765\uff0c\u53ea\u89c1\u90a3\u51f6\u5f92\u5168\u8eab\u90fd\u662f\u9c9c\u8840\uff0c\u663e\u7136\u662f\u7ed9\u4eba\u72e0\u72e0\u7684\u62f7\u6253\u4e86\u4e00\u987f\u3002 \u3000\u3000\u90a3\u56da\u5f92\u4e00\u5012\u5728\u5730\u4e0b\uff0c\u4fbf\u5373\u660f\u8ff7\u4e0d\u9192\u3002\u72c4\u4e91\u5f85\u56db\u4e2a\u72f1\u5352\u53bb\u540e\uff0c\u501f\u7740\u7167\u8fdb\u7262\u623f\u6765\u7684\u6708\u5149\uff0c\u6253\u91cf\u4ed6\u65f6\uff0c\u53ea\u89c1\u4ed6\u8138\u4e0a\u3001\u81c2\u4e0a\u3001\u817f\u4e0a\uff0c\u90fd\u662f\u9177\u906d\u97ad\u6253\u7684\u8840\u75d5\u3002\u72c4\u4e91\u867d\u7136\u8fde\u65e5\u53d7\u4ed6\u7684\u6b3a\u4fae\uff0c\u89c1\u4e86\u8fd9\u7b49\u60e8\u72b6\uff0c\u4e0d\u7531\u5f97\u5fc3\u6709\u4e0d\u5fcd\uff0c\u4ece\u6c34\u94b5\u4e2d\u5012\u4e86\u4e9b\u6c34\uff0c\u5582\u7740\u4ed6\u559d\u3002 \u3000\u3000\u90a3\u56da\u5f92\u7f13\u7f13\u9192\u8f6c\uff0c\u7741\u773c\u89c1\u662f\u72c4\u4e91\uff0c\u7a81\u7136\u4e3e\u8d77\u94c1\u94d0\uff0c\u731b\u529b\u5f80\u4ed6\u5934\u4e0a\u7838\u843d\u3002\u72c4\u4e91\u529b\u6c14\u867d\u5931\uff0c\u5e94\u53d8\u7684\u673a\u7075\u5c1a\u5728\uff0c\u6025\u5fd9\u95ea\u8eab\u76f8\u907f\uff0c\u4e0d\u6599\u90a3\u56da\u72af\u53cc\u624b\u529b\u9053\u5e76\u4e0d\u4f7f\u8db3\uff0c\u534a\u9014\u4e2d\u8fe5\u5c06\u8fc7\u6765\uff0c\u7830\u7684\u4e00\u58f0\uff0c\u91cd\u91cd\u7838\u5728\u4ed6\u8170\u95f4\u3002\u72c4\u4e91\u7acb\u8db3\u4e0d\u5b9a\uff0c\u5411\u5de6\u76f4\u8dcc\u51fa\u53bb\u3002\u4ed6\u624b\u8db3\u90fd\u6709\u94c1\u94fe\u4e0e\u7435\u7436\u9aa8\u76f8\u8fde\uff0c\u767b\u65f6\u5267\u75db\u96be\u5f53\uff0c\u4e0d\u7981\u53c8\u60ca\u53c8\u6012\uff0c\u9a82\u9053\uff1a\u201c\u75af\u5b50\uff01\u201d \u3000\u3000\u90a3\u56da\u5f92\u72c2\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u82e6\u8089\u8ba1\uff0c\u5982\u4f55\u7792\u5f97\u8fc7\u6211\uff0c\u4e58\u65e9\u522b\u6765\u6253\u6211\u7684\u4e3b\u610f\u3002\u201d \u3000\u3000\u72c4\u4e91\u53ea\u89c9\u80c1\u95f4\u808b\u9aa8\u51e0\u4e4e\u65ad\u6298\uff0c\u75db\u5f97\u8bdd\u4e5f\u8bf4\u4e0d\u51fa\u6765\uff0c\u8fc7\u5f97\u534a\u664c\uff0c\u624d\u9053\uff1a\u201c\u75af\u5b50\uff0c\u4f60\u81ea\u8eab\u96be\u4fdd\uff0c\u6709\u751a\u4e48\u4e3b\u610f\u7ed9\u4eba\u597d\u6253\uff1f\u201d \u3000\u3000\u90a3\u56da\u5f92\u4e00\u8dc3\u800c\u524d\uff0c\u5de6\u8db3\u8e0f\u4f4f\u72c4\u4e91\u80cc\u5fc3\uff0c\u53f3\u8db3\u5728\u4ed6\u8eab\u4e0a\u91cd\u91cd\u8e22\u4e86\u51e0\u811a\uff0c\u559d\u9053\uff1a\u201c\u6211\u770b\u4f60\u8fd9\u5c0f\u8d3c\u5e74\u7eaa\u8fd8\u8f7b\uff0c\u4f5c\u6076\u4e0d\u591a\uff0c\u4e0d\u8fc7\u662f\u53d7\u4eba\u6307\u4f7f\uff0c\u5426\u5219\u6211\u4e0d\u4e00\u811a\u8e22\u6b7b\u4f60\u624d\u602a\u3002\u201d \u3000\u3000\u72c4\u4e91\u6c14\u5f97\u8eab\u4e0a\u7684\u75db\u695a\u4e5f\u81ea\u5fd8\u4e86\uff0c\u5fc3\u60f3\u65e0\u8f9c\u53d7\u8fd9\u7262\u72f1\u4e4b\u707e\uff0c\u5df2\u662f\u4e0d\u5e78\uff0c\u800c\u4e0e\u8fd9\u4e0d\u53ef\u7406\u55bb\u7684\u75af\u6c49\u540c\u5904\u4e00\u5ba4\uff0c\u66f4\u662f\u4e0d\u5e78\u4e4b\u4e2d\u518d\u52a0\u4e0d\u5e78\u3002 \u3000\u3000\u5230\u4e86\u7b2c\u4e8c\u4e2a\u6708\u5706\u4e4b\u591c\uff0c\u90a3\u56da\u72af\u53c8\u88ab\u56db\u540d\u5e26\u5200\u72f1\u5352\u5e26\u4e86\u51fa\u53bb\uff0c\u62f7\u6253\u4e00\u987f\uff0c\u9001\u56de\u7262\u623f\u3002\u8fd9\u4e00\u6b21\u72c4\u4e91\u5b66\u4e86\u4e56\uff0c\u4efb\u4ed6\u6a21\u6837\u5982\u4f55\u60e8\u4e0d\u5fcd\u7779\uff0c\u59cb\u7ec8\u4e0d\u53bb\u7406\u4f1a\u3002\u4e0d\u6599\u4e0d\u7406\u4e5f\u662f\u4e0d\u6210\uff0c\u90a3\u56da\u5f92\u4e00\u53e3\u6c14\u6ca1\u5904\u51fa\uff0c\u5c3d\u7ba1\u904d\u4f53\u9cde\u4f24\uff0c\u8fd8\u662f\u6765\u627e\u4ed6\u7684\u6666\u6c14\uff0c\u4e0d\u4f4f\u5406\u559d\uff1a \u3000\u3000\u201c\u4f60\u5976\u5976\u7684\uff0c\u4f60\u518d\u5367\u5e95\u5341\u5e74\u516b\u5e74\uff0c\u8001\u5b50\u4e5f\u4e0d\u4e0a\u4f60\u7684\u5f53\u3002\u201d\u201c\u4eba\u5bb6\u6253\u4f60\u7956\u5b97\uff0c\u4f60\u7956\u5b97\u5c31\u6253\u4f60\u8fd9\u5b59\u5b50\uff01\u201d\u201c\u54b1\u4eec\u5c31\u662f\u8fd9\u4e48\u8017\u7740\uff0c\u77a7\u662f\u8c01\u53d7\u7684\u7f6a\u591a\u3002\u201d\u4f3c\u4e4e\u4ed6\u8eab\u53d7\u62f7\u6253\uff0c\u5168\u662f\u72c4\u4e91\u7684\u4e0d\u662f\uff0c\u53c8\u6253\u53c8\u8e22\uff0c\u95f9\u4e86\u534a\u5929\u3002 \u3000\u3000\u6b64\u540e\u6bcf\u5230\u6708\u4eae\u5c06\u5706\uff0c\u72c4\u4e91\u5c31\u6101\u7709\u4e0d\u5c55\uff0c\u77e5\u9053\u60e8\u53d7\u837c\u6bd2\u7684\u65e5\u5b50\u8fd1\u4e86\u3002\u679c\u7136\u6bcf\u6708\u5341\u4e94\uff0c\u90a3\u56da\u72af\u603b\u662f\u7ed9\u62c9\u51fa\u53bb\u7ecf\u53d7\u4e00\u987f\u62f7\u6253\uff0c\u56de\u6765\u540e\u5c31\u8f6c\u800c\u5bf9\u4ed8\u72c4\u4e91\u3002\u603b\u7b97\u72c4\u4e91\u5e74\u7eaa\u751a\u8f7b\uff0c\u8eab\u5f3a\u529b\u58ee\uff0c\u6bcf\u4e2a\u6708\u6328\u4e00\u987f\u6253\uff0c\u5012\u4e5f\u7ecf\u53d7\u5f97\u8d77\uff0c\u6709\u65f6\u4e0d\u514d\u5947\u602a\uff1a\u201c\u6211\u7435\u7436\u9aa8\u88ab\u94c1\u94fe\u7a7f\u540e\uff0c\u529b\u6c14\u5168\u65e0\u3002\u8fd9\u75af\u6c49\u4e00\u822c\u7684\u7ed9\u94c1\u94fe\u7a7f\u4e86\u7435\u7436\u9aa8\uff0c\u600e\u5730\u4ecd\u6709\u4e00\u8eab\u86ee\u529b\uff1f\u201d\u51e0\u6b21\u9f13\u8d77\u52c7\u6c14\u8be2\u95ee\uff0c\u4f46\u53ea\u987b\u4e00\u5f00\u53e3\uff0c\u90a3\u75af\u6c49\u4fbf\u62f3\u8db3\u4ea4\u52a0\uff0c\u6b64\u540e\u53ea\u597d\u534a\u53e5\u8bdd\u4e5f\u4e0d\u5411\u4ed6\u8bf4\u3002 \u3000\u3000\u5982\u6b64\u5306\u5306\u8fc7\u4e86\u6570\u6708\uff0c\u51ac\u5c3d\u6625\u6765\uff0c\u5c48\u6307\u5728\u72f1\u4e2d\u5c06\u8fd1\u4e00\u5e74\u3002\u72c4\u4e91\u6162\u6162\u60ef\u4e86\uff0c\u5fc3\u4e2d\u7684\u6028\u6124\u3001\u8eab\u4e0a\u7684\u75db\u695a\uff0c\u5012\u4e5f\u6e10\u6e10\u9ebb\u6728\u4e86\u3002\u8fd9\u4e9b\u65f6\u65e5\u4e4b\u4e2d\uff0c\u4ed6\u4e3a\u4e86\u907f\u5f00\u90a3\u75af\u6c49\u7684\u6bb4\u8fb1\uff0c\u59cb\u7ec8\u6b63\u773c\u4e5f\u4e0d\u77a7\u4ed6\u4e00\u4e0b\u3002\u53ea\u8981\u4e0d\u8ddf\u4ed6\u8bf4\u8bdd\uff0c\u76ee\u5149\u4e0d\u4e0e\u4ed6\u76f8\u5bf9\uff0c\u9664\u4e86\u6708\u5706\u4e4b\u5915\uff0c\u90a3\u75af\u6c49\u5e73\u65f6\u5012\u4e5f\u4e0d\u6765\u62db\u60f9\u3002 \u3000\u3000\u8fd9\u4e00\u65e5\u6e05\u6668\uff0c\u72c4\u4e91\u773c\u672a\u7741\u5f00\uff0c\u542c\u5f97\u7262\u623f\u5916\u71d5\u8bed\u5462\u5583\uff0c\u7a81\u7136\u95f4\u60f3\u8d77\u4ece\u524d\u5e38\u548c\u621a\u82b3\u5728\u4e00\u8d77\u89c2\u770b\u71d5\u5b50\u7b51\u5de2\u7684\u60c5\u666f\uff0c\u5fc3\u4e2d\u84e6\u5730\u4e00\u9178\uff0c\u5411\u71d5\u8bed\u5904\u671b\u53bb\uff0c\u53ea\u89c1\u4e00\u5bf9\u71d5\u5b50\u6e10\u98de\u6e10\u8fdc\uff0c\u4ece\u6570\u5341\u4e08\u5916\u9ad8\u697c\u7554\u7684\u7a97\u4e0b\u63a0\u8fc7\u3002\u4ed6\u957f\u65e5\u65e0\u804a\uff0c\u5e38\u81ea\u9065\u773a\u7eb1\u7a97\uff0c\u731c\u60f3\u8fd9\u697c\u4e2d\u6709\u4f55\u4eba\u5c45\u4f4f\uff0c\u4f46\u7a97\u5b50\u8001\u662f\u7d27\u7d27\u7684\u5173\u7740\uff0c\u7a97\u69db\u4e0a\u5374\u7ec8\u5e74\u4e0d\u65ad\u7684\u4f9b\u7740\u4e00\u76c6\u9c9c\u82b1\uff0c\u5176\u65f6\u6625\u5149\u70c2\u6f2b\uff0c\u7a97\u69db\u4e0a\u653e\u7684\u662f\u4e00\u76c6\u8309\u8389\u3002 \u3000\u3000\u6b63\u5728\u80e1\u601d\u4e71\u60f3\uff0c\u5ffd\u542c\u5f97\u90a3\u75af\u6c49\u8f7b\u8f7b\u4e00\u58f0\u53f9\u606f\u3002\u8fd9\u4e00\u5e74\u6765\uff0c\u90a3\u75af\u6c49\u4e0d\u662f\u72c2\u7b11\uff0c\u4fbf\u662f\u9a82\u4eba\uff0c\u4ece\u6765\u6ca1\u542c\u89c1\u4ed6\u53f9\u8fc7\u751a\u4e48\u6c14\uff0c\u4f55\u51b5\u8fd9\u58f0\u53f9\u606f\u4e4b\u4e2d\uff0c\u7adf\u9887\u6709\u5fe7\u4f24\u3001\u6e29\u67d4\u4e4b\u610f\u3002\u72c4\u4e91\u5fcd\u4e0d\u4f4f\u8f6c\u8fc7\u5934\u53bb\uff0c\u53ea\u89c1\u90a3\u75af\u6c49\u5634\u89d2\u8fb9\u5e26\u7740\u4e00\u4e1d\u5fae\u7b11\uff0c\u8138\u4e0a\u795e\u8272\u8bda\u631a\uff0c\u4e0d\u518d\u662f\u90a3\u526f\u51f6\u608d\u6076\u6bd2\u7684\u6a21\u6837\uff0c\u773c\u775b\u6b63\u671b\u7740\u90a3\u76c6\u8309\u8389\u3002\u72c4\u4e91\u552f\u6050\u4ed6\u89c9\u5bdf\u81ea\u5df1\u5728\u5077\u7aa5\u4ed6\u7684\u8138\u8272\uff0c\u5f53\u5373\u8f6c\u8fc7\u4e86\u5934\u4e0d\u6562\u518d\u770b\u3002 \u3000\u3000\u81ea\u4ece\u53d1\u89c1\u4e86\u8fd9\u79d8\u5bc6\u540e\uff0c\u72c4\u4e91\u6bcf\u5929\u65e9\u6668\u90fd\u5077\u770b\u8fd9\u75af\u6c49\u7684\u795e\u60c5\uff0c\u4f46\u89c1\u4ed6\u603b\u662f\u8138\u8272\u6e29\u67d4\u7684\u51dd\u671b\u7740\u90a3\u76c6\u9c9c\u82b1\uff0c\u4ece\u6625\u5929\u7684\u8309\u8389\u3001\u73ab\u7470\uff0c\u671b\u5230\u4e86\u590f\u5929\u7684\u4e01\u9999\u3001\u51e4\u4ed9\u3002\u8fd9\u534a\u5e74\u4e4b\u4e2d\uff0c\u4e24\u4e2a\u4eba\u51e0\u4e4e\u6ca1\u8bf4\u4e0a\u5341\u53e5\u8bdd\u3002\u6708\u5706\u4e4b\u591c\u7684\u6bb4\u6253\uff0c\u4e5f\u53d8\u6210\u4e86\u4e00\u4e2a\u95f7\u6253\uff0c\u4e00\u4e2a\u95f7\u6328\u3002\u72c4\u4e91\u65e9\u5df2\u89c9\u5bdf\u5230\uff0c\u53ea\u8981\u81ea\u5df1\u4e00\u53e5\u8bdd\u4e0d\u8bf4\uff0c\u8fd9\u75af\u6c49\u7684\u6012\u6c14\u5c31\u5c0f\u5f97\u591a\uff0c\u62f3\u811a\u843d\u4e0b\u65f6\u4e5f\u8f7b\u5f97\u591a\u3002\u4ed6\u5fc3\u60f3\uff1a\u201c\u518d\u8fc7\u5f97\u51e0\u5e74\uff0c\u6050\u6015\u6211\u8fde\u600e\u4e48\u8bf4\u8bdd\u4e5f\u8981\u5fd8\u4e86\u3002\u201d \u3000\u3000\u8fd9\u75af\u6c49\u867d\u7136\u86ee\u6a2a\u65e0\u7406\uff0c\u5374\u4e5f\u6709\u4e00\u6837\u597d\u5904\uff0c\u5413\u5f97\u72f1\u5352\u8f7b\u6613\u4e0d\u6562\u5230\u7262\u623f\u4e2d\u7f57\u55e6\u3002\u6709\u65f6\u72f1\u5352\u7ed9\u4ed6\u9a82\u5f97\u72e0\u4e86\uff0c\u4e0d\u9001\u996d\u7ed9\u4ed6\uff0c\u4ed6\u5c31\u593a\u72c4\u4e91\u7684\u996d\u5403\u3002\u82e5\u662f\u4e24\u4eba\u7684\u996d\u90fd\u4e0d\u9001\uff0c\u90a3\u75af\u6c49\u997f\u4e0a\u51e0\u5929\u4e5f\u6ee1\u4e0d\u5728\u4e4e\u3002 \u3000\u3000\u90a3\u4e00\u5e74\u5341\u4e00\u6708\u5341\u4e94\uff0c\u90a3\u75af\u6c49\u7ed9\u82e6\u6253\u4e00\u987f\u4e4b\u540e\uff0c\u5ffd\u7136\u53d1\u8d77\u70e7\u6765\uff0c\u660f\u8ff7\u4e2d\u5c3d\u8bf4\u80e1\u8bdd\uff0c\u524d\u8a00\u4e0d\u5bf9\u540e\u8bed\uff0c\u72c4\u4e91\u4f9d\u7a00\u53ea\u542c\u5f97\u4ed6\u5e38\u5e38\u547c\u5524\u7740\u4e24\u4e2a\u5b57\uff0c\u4f3c\u4e4e\u662f\u201c\u53cc\u82b1\u201d\uff0c\u53c8\u4f3c\u201c\u4f24\u6000\u201d\u3002 \u3000\u3000\u72c4\u4e91\u521d\u65f6\u4e0d\u6562\u7406\u4f1a\uff0c\u4f46\u5230\u5f97\u6b21\u65e5\u5348\u95f4\uff0c\u542c\u4ed6\u4e0d\u65ad\u547b\u541f\u7684\u8bf4\uff1a\u201c\u6c34\uff0c\u6c34\uff0c\u7ed9\u6211\u6c34\u559d\uff01\u201d\u5fcd\u4e0d\u4f4f\u5728\u74e6\u94b5\u4e2d\u5012\u4e86\u4e9b\u6c34\uff0c\u51d1\u5230\u4ed6\u5634\u8fb9\uff0c\u4e25\u795e\u6212\u5907\uff0c\u9632\u4ed6\u53c8\u53cc\u624b\u6bb4\u51fb\u8fc7\u6765\u3002\u5e78\u597d\u8fd9\u4e00\u6b21\u4ed6\u4e56\u4e56\u7684\u559d\u4e86\u6c34\uff0c\u4fbf\u5373\u7761\u5012\u3002 \u3000\u3000\u5f53\u5929\u665a\u4e0a\uff0c\u7adf\u7136\u53c8\u6765\u4e86\u56db\u4e2a\u72f1\u5352\uff0c\u67b6\u7740\u4ed6\u51fa\u53bb\u53c8\u62f7\u6253\u4e86\u4e00\u987f\u3002\u8fd9\u6b21\u56de\u6765\uff0c\u90a3\u75af\u6c49\u7684\u547b\u541f\u58f0\u5df2\u662f\u82e5\u65ad\u82e5\u7eed\u3002\u4e00\u540d\u72f1\u5352\u72e0\u72e0\u7684\u9053\uff1a\u201c\u4ed6\u5014\u5f3a\u4e0d\u8bf4\uff0c\u660e\u513f\u518d\u6253\u3002\u201d\u53e6\u4e00\u540d\u72f1\u5352\u9053\uff1a\u201c\u4e58\u7740\u4ed6\u795e\u667a\u4e0d\u6e05\uff0c\u54b1\u4eec\u8d76\u7d27\u5f97\u903c\u4ed6\u8bf4\u51fa\u6765\u3002\u8bf4\u4e0d\u5b9a\u4ed6\u8fd9\u4e00\u6b21\u8981\u89c1\u960e\u738b\uff0c\u90a3\u53ef\u4e0d\u7f8e\u3002\u201d \u3000\u3000\u72c4\u4e91\u548c\u4ed6\u5728\u72f1\u4e2d\u540c\u5904\u5df2\u4e45\uff0c\u867d\u82e6\u53d7\u4ed6\u6b3a\u51cc\u6298\u78e8\uff0c\u53ef\u4e5f\u771f\u4e0d\u613f\u4ed6\u8fd9\u4e48\u4fbf\u6b7b\u5728\u72f1\u5352\u7684\u624b\u4e0b\u3002\u5341\u4e03\u90a3\u4e00\u5929\uff0c\u72c4\u4e91\u670d\u4f8d\u4ed6\u559d\u4e86\u56db\u4e94\u6b21\u6c34\u3002\u6700\u540e\u4e00\u6b21\uff0c\u90a3\u75af\u6c49\u70b9\u4e86\u70b9\u5934\u793a\u8c22\u3002\u81ea\u4ece\u540c\u72f1\u4ee5\u6765\uff0c\u72c4\u4e91\u9996\u6b21\u89c1\u5230\u4ed6\u7684\u53cb\u5584\u4e4b\u610f\uff0c\u7a81\u7136\u4e4b\u95f4\uff0c\u5fc3\u4e2d\u611f\u5230\u4e86\u65e0\u6bd4\u7684\u6b22\u559c\u3002 \u3000\u3000\u8fd9\u5929\u4e8c\u66f4\u8fc7\u540e\uff0c\u90a3\u56db\u540d\u72f1\u5352\u679c\u7136\u53c8\u6765\u4e86\uff0c\u6253\u5f00\u4e86\u7262\u95e8\u3002\u72c4\u4e91\u5fc3\u60f3\u8fd9\u4e00\u6b21\u90a3\u75af\u6c49\u82e5\u518d\u7ecf\u62f7\u6253\uff0c\u90a3\u662f\u975e\u6b7b\u4e0d\u53ef\uff0c\u5ffd\u7136\u5c06\u5fc3\u4e00\u6a2a\uff0c\u8df3\u8d77\u6765\u62e6\u5728\u7262\u95e8\u524d\uff0c\u559d\u9053\uff1a\u201c\u4e0d\u8bb8\u8fdb\u6765\uff01\u201d\u4e00\u540d\u9ad8\u5927\u7684\u72f1\u5352\u8fc8\u6b65\u8fc7\u6765\uff0c\u9a82\u9053\uff1a\u201c\u8d3c\u56da\u72af\uff0c\u6eda\u5f00\u3002\u201d\u72c4\u4e91\u624b\u4e0a\u65e0\u529b\uff0c\u731b\u5730\u91cc\u4f4e\u5934\u4e00\u53e3\u54ac\u53bb\uff0c\u5c06\u4ed6\u53f3\u624b\u98df\u4e2d\u4e24\u6307\u54ac\u5f97\u9c9c\u8840\u6dcb\u6f13\uff0c\u7259\u9f7f\u6df1\u53ca\u6307\u9aa8\uff0c\u4e24\u6839\u624b\u6307\u51e0\u4e4e\u90fd\u54ac\u65ad\u4e86\u3002\u90a3\u72f1\u5352\u5927\u5403\u4e00\u60ca\uff0c\u53cd\u8eab\u8df3\u51fa\u7262\u623f\uff0c\u545b\u5577\u4e00\u58f0\uff0c\u4e00\u67c4\u5355\u5200\u6389\u5728\u5730\u4e0b\u3002 \u3000\u3000\u72c4\u4e91\u4fef\u8eab\u62a2\u8d77\uff0c\u547c\u547c\u547c\u8fde\u5288\u4e09\u5200\uff0c\u4ed6\u624b\u4e0a\u867d\u65e0\u52b2\u529b\uff0c\u4f46\u4ee5\u5200\u4ee3\u5251\uff0c\u62db\u6570\u4ecd\u662f\u9887\u4e3a\u7cbe\u5999\u3002\u4e00\u540d\u80a5\u80d6\u7684\u72f1\u5352\u4ed7\u5200\u76f4\u8fdb\uff0c\u72c4\u4e91\u8eab\u5b50\u4e00\u4fa7\uff0c\u4e00\u62db\u201c\u5927\u6bcd\u54e5\u76d0\u5931\uff0c\u957f\u9e45\u5364\u7ffc\u5706\u201d\uff08\u5176\u5b9e\u662f\u201c\u5927\u6f20\u5b64\u70df\u76f4\uff0c\u957f\u6cb3\u843d\u65e5\u5706\u201d\uff09\uff0c\u5355\u5200\u8f6c\u4e86\u4e2a\u5706\u5708\uff0c\u5237\u7684\u4e00\u5200\uff0c\u780d\u5728\u4ed6\u817f\u4e0a\u3002\u90a3\u72f1\u5352\u5413\u5f97\u8fde\u6eda\u5e26\u722c\u7684\u9000\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u8fd9\u4e00\u6765\u8840\u6e85\u7262\u95e8\uff0c\u56db\u540d\u72f1\u5352\u89c1\u4ed6\u52bf\u82e5\u75af\u864e\uff0c\u5f62\u540c\u62da\u547d\uff0c\u5012\u4e5f\u4e0d\u6562\u8f7b\u6613\u62a2\u8fdb\uff0c\u5728\u7262\u95e8\u5916\u5c06\u72c4\u4e91\u7684\u5341\u516b\u4ee3\u7956\u5b97\u90fd\u9a82\u4e86\u4e2a\u81ed\u6b7b\uff0c\u751a\u4e48\u6c61\u8a00\u79fd\u8bed\u90fd\u9a82\u4e86\u51fa\u6765\u3002\u72c4\u4e91\u4e00\u8a00\u4e0d\u53d1\uff0c\u53ea\u662f\u5b88\u4f4f\u72f1\u95e8\u3002\u90a3\u56db\u540d\u72f1\u5352\u5c45\u7136\u6ca1\u53bb\u642c\u6c42\u63f4\u519b\uff0c\u773c\u770b\u653b\u4e0d\u8fdb\u6765\uff0c\u9a82\u4e86\u4e00\u4f1a\uff0c\u4e5f\u5c31\u53bb\u4e86\u3002 \u3000\u3000\u63a5\u8fde\u56db\u5929\u4e4b\u4e2d\uff0c\u72f1\u5352\u65e2\u4e0d\u9001\u996d\uff0c\u4e5f\u4e0d\u9001\u6c34\u3002\u72c4\u4e91\u5230\u7b2c\u4e94\u5929\u65f6\uff0c\u6e34\u5f97\u518d\u4e5f\u96be\u4ee5\u5fcd\u8010\u3002\u90a3\u75af\u6c49\u66f4\u662f\u5634\u5507\u4e5f\u7126\u4e86\uff0c\u5ffd\u9053\uff1a\u201c\u4f60\u5047\u88c5\u8981\u780d\u6b7b\u6211\uff0c\u8fd9\u72d7\u5a18\u517b\u7684\u975e\u62ff\u6c34\u6765\u4e0d\u53ef\u3002\u201d\u72c4\u4e91\u4e0d\u660e\u5176\u7406\uff0c\u4f46\u60f3\uff1a\u201c\u4e0d\u7ba1\u6709\u6ca1\u6709\u7528\uff0c\u8bd5\u8bd5\u4e5f\u597d\uff01\u201d\u5f53\u4e0b\u5927\u58f0\u53eb\u9053\uff1a\u201c\u518d\u4e0d\u62ff\u6c34\u6765\uff0c\u6211\u5c06\u8fd9\u75af\u6c49\u5148\u780d\u6b7b\u518d\u8bf4\u3002\u201d\u53cd\u8fc7\u5200\u80cc\uff0c\u5728\u94c1\u6805\u680f\u4e0a\u78b0\u5f97\u5f53\u5f53\u5f53\u7684\u76f4\u54cd\u3002 \u3000\u3000\u53ea\u89c1\u90a3\u72f1\u5352\u5306\u5306\u8d76\u6765\uff0c\u5927\u58f0\u5403\u559d\uff1a\u201c\u4f60\u4f24\u4e86\u4ed6\u4e00\u6839\u6beb\u6bdb\uff0c\u8001\u5b50\u7528\u5200\u5c16\u5728\u4f60\u8eab\u4e0a\u622a\u4e00\u5343\u4e00\u4e07\u4e2a\u7a9f\u7abf\u3002\u201d\u8ddf\u7740\u4fbf\u62ff\u4e86\u6e05\u6c34\u548c\u51b7\u996d\u6765\u3002 \u3000\u3000\u72c4\u4e91\u5582\u7740\u90a3\u75af\u6c49\u5403\u559d\u5df2\u6bd5\uff0c\u95ee\u9053\uff1a\u201c\u4ed6\u8981\u6298\u78e8\u4f60\uff0c\u53ef\u53c8\u6015\u6211\u6740\u4e86\u4f60\uff0c\u90a3\u662f\u751a\u4e48\u9053\u7406\uff1f\u201d \u3000\u3000\u90a3\u75af\u6c49\u53cc\u76ee\u5706\u7741\uff0c\u4e3e\u8d77\u624b\u4e2d\u7684\u74e6\u94b5\uff0c\u5288\u5934\u5411\u4ed6\u7838\u53bb\uff0c\u9a82\u9053\uff1a\u201c\u4f60\u8fd9\u756a\u5047\u60fa\u60fa\u7684\u4e70\u597d\uff0c\u6211\u5c31\u4e0a\u4e86\u4f60\u7684\u5f53\u4e48\uff1f\u201d\u4e52\u4e53\u4e00\u58f0\uff0c\u74e6\u94b5\u7834\u788e\uff0c\u72c4\u4e91\u989d\u5934\u9c9c\u8840\u6d94\u6d94\u800c\u4e0b\u3002\u4ed6\u832b\u7136\u9000\u5f00\uff0c\u5fc3\u60f3\uff1a\u201c\u8fd9\u4eba\u72c2\u6027\u53c8\u53d1\u4f5c\u4e86\uff01\u201d \u3000\u3000\u4f46\u6b64\u540e\u9022\u5230\u6708\u5706\u4e4b\u540e\uff0c\u90a3\u4e9b\u72f1\u5352\u867d\u4e00\u822c\u7684\u5c06\u90a3\u75af\u6c49\u63d0\u51fa\u53bb\u62f7\u6253\uff0c\u4ed6\u56de\u6765\u5374\u4e0d\u518d\u5728\u72c4\u4e91\u8eab\u4e0a\u627e\u8865\u3002\u4e24\u4eba\u4ecd\u7136\u5e76\u4e0d\u4ea4\u8c08\uff0c\u72c4\u4e91\u8981\u662f\u5411\u4ed6\u591a\u77a7\u4e0a\u51e0\u773c\uff0c\u918b\u94b5\u5927\u7684\u62f3\u5934\u8fd8\u662f\u4e00\u822c\u62db\u547c\u8fc7\u6765\u3002 \u3000\u3000\u90a3\u75af\u6c49\u53ea\u6709\u5728\u671b\u7740\u5bf9\u9762\u9ad8\u697c\u7a97\u69db\u4e0a\u7684\u9c9c\u82b1\u4e4b\u65f6\uff0c\u8138\u4e0a\u76ee\u4e2d\uff0c\u624d\u9732\u51fa\u4e00\u4e1d\u6e29\u67d4\u7684\u795e\u8272\u3002 \u3000\u3000\u5230\u5f97\u7b2c\u56db\u5e74\u7684\u6625\u5929\uff0c\u72c4\u4e91\u5fc3\u4e2d\u5df2\u65e0\u51fa\u72f1\u4e4b\u5ff5\uff0c\u867d\u7136\u68a6\u9b42\u4e4b\u4e2d\uff0c\u4ecd\u662f\u4e0d\u65ad\u7684\u60f3\u5230\u5e08\u7236\u548c\u5e08\u59b9\uff0c\u4f46\u5e08\u7236\u7684\u5f71\u5b50\u7ec8\u4e8e\u6162\u6162\u6de1\u4e86\u3002\u5e08\u59b9\u90a3\u58ee\u5065\u5a40\u5a1c\u7684\u8eab\u5b50\uff0c\u7ea2\u7ea2\u7684\u8138\u86cb\uff0c\u9ed1\u6e9c\u6e9c\u7684\u5927\u773c\u775b\uff0c\u5728\u4ed6\u5fc3\u5e95\u5374\u4ecd\u548c\u4e09\u5e74\u591a\u524d\u4e00\u822c\u7684\u6e05\u6670\u3002 \u3000\u3000\u4ed6\u5df2\u4e0d\u6562\u76fc\u671b\u80fd\u51fa\u72f1\u53bb\u518d\u548c\u5e08\u59b9\u76f8\u4f1a\uff0c\u6bcf\u5929\u53ef\u603b\u5fd8\u4e0d\u4e86\u6697\u6697\u5411\u89c2\u4e16\u97f3\u83e9\u8428\u795d\u7977\uff0c\u53ea\u8981\u5e08\u59b9\u80fd\u518d\u5230\u72f1\u4e2d\u6765\u63a2\u671b\u4ed6\u4e00\u6b21\uff0c\u4fbf\u662f\u5929\u5929\u53d7\u90a3\u75af\u6c49\u7684\u6bb4\u6253\uff0c\u4e5f\u6240\u7518\u613f\u3002 \u3000\u3000\u621a\u82b3\u59cb\u7ec8\u6ca1\u6709\u6765\u3002 \u3000\u3000\u6709\u4e00\u5929\uff0c\u5374\u6709\u4e00\u4e2a\u4eba\u6765\u63a2\u671b\u4ed6\u3002\u90a3\u662f\u4e2a\u8eab\u7a7f\u7ef8\u9762\u76ae\u888d\u7684\u82f1\u4fca\u5c11\u5e74\uff0c\u7b11\u563b\u563b\u7684\u9053\uff1a\u201c\u72c4\u5e08\u5144\uff0c\u4f60\u8fd8\u8ba4\u5f97\u6211\u4e48\uff1f\u6211\u662f\u6c88\u57ce\u3002\u201d \u3000\u3000\u9694\u4e86\u4e09\u5e74\u591a\uff0c\u4ed6\u8eab\u6750\u5df2\u957f\u9ad8\uff0c\u72c4\u4e91\u51e0\u4e4e\u5df2\u8ba4\u4ed6\u4e0d\u51fa\u3002 \u3000\u3000\u72c4\u4e91\u5fc3\u4e2d\u6026\u6026\u4e71\u8df3\uff0c\u53ea\u76fc\u80fd\u542c\u5230\u5e08\u59b9\u7684\u4e00\u4e9b\u8baf\u606f\uff0c\u95ee\u9053\uff1a\u201c\u6211\u5e08\u59b9\u5462\uff1f\u201d \u3000\u3000\u6c88\u57ce\u9694\u7740\u6805\u680f\uff0c\u9012\u4e86\u4e00\u53ea\u7bee\u5b50\u8fdb\u6765\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u662f\u6211\u4e07\u5e08\u5ac2\u9001\u7ed9\u4f60\u7684\u3002\u4eba\u5bb6\u53ef\u6ca1\u5fd8\u4e86\u65e7\u76f8\u597d\uff0c\u5927\u559c\u7684\u65e5\u5b50\uff0c\u5df4\u5df4\u7684\u53eb\u6211\u9001\u4e24\u53ea\u9e21\u3001\u56db\u53ea\u732a\u8e44\u3001\u5341\u516d\u5757\u559c\u7cd5\u6765\u7ed9\u4f60\u3002\u201d \u3000\u3000\u72c4\u4e91\u832b\u7136\u95ee\u9053\uff1a\u201c\u54ea\u4e00\u4e2a\u4e07\u5e08\u5ac2\uff1f\u751a\u4e48\u5927\u559c\u7684\u65e5\u5b50\uff1f\u201d \u3000\u3000\u6c88\u57ce\u54c8\u54c8\u4e00\u7b11\uff0c\u6ee1\u8138\u72e1\u8c32\u7684\u795e\u8272\uff0c\u8bf4\u9053\uff1a\u201c\u4e07\u5e08\u5ac2\u561b\uff0c\u5c31\u662f\u4f60\u7684\u5e08\u59b9\u621a\u59d1\u5a18\u4e86\u3002\u4eca\u5929\u662f\u5979\u548c\u6211\u4e07\u5e08\u54e5\u62dc\u5802\u6210\u4eb2\u7684\u597d\u65e5\u5b50\u3002\u5979\u53eb\u6211\u9001\u559c\u7cd5\u9e21\u8089\u7ed9\u4f60\uff0c\u90a3\u4e0d\u662f\u633a\u591f\u4ea4\u60c5\u4e48\uff1f\u201d \u3000\u3000\u72c4\u4e91\u8eab\u5b50\u4e00\u6643\uff0c\u53cc\u624b\u6293\u4f4f\u94c1\u6805\uff0c\u98a4\u58f0\u6012\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u80e1\u8bf4\u516b\u9053\uff01\u6211\u5e08\u59b9\u600e\u80fd\u2026\u2026\u600e\u80fd\u5ac1\u7ed9\u90a3\u59d3\u4e07\u7684\uff1f\u201d \u3000\u3000\u6c88\u57ce\u7b11\u9053\uff1a\u201c\u6211\u6069\u5e08\u7ed9\u4f60\u5e08\u7236\u523a\u4e86\u4e00\u5200\uff0c\u5e78\u597d\u6ca1\u6b7b\uff0c\u540e\u6765\u517b\u597d\u4e86\u4f24\uff0c\u8fc7\u53bb\u7684\u4e8b\uff0c\u65e2\u5f80\u4e0d\u548e\u3002\u4f60\u5e08\u59b9\u4f4f\u5728\u6211\u4e07\u5e08\u54e5\u5bb6\u91cc\uff0c\u8fd9\u4e09\u5e74\u6765\u537f\u537f\u6211\u6211\uff0c\u8bf4\u4e0d\u5b9a\u2026\u2026\u8bf4\u4e0d\u5b9a\u2026\u2026\u54c8\u54c8\uff0c\u660e\u5e74\u62c5\u4fdd\u7ed9\u751f\u4e2a\u767d\u767d\u80d6\u80d6\u7684\u5a03\u5a03\u3002\u201d\u4ed6\u5e74\u7eaa\u5927\u4e86\uff0c\u8bf4\u8bdd\u66f4\u662f\u6cb9\u8154\u6ed1\u8c03\uff0c\u6d41\u6c14\u5341\u8db3\u3002 \u3000\u3000\u72c4\u4e91\u8033\u4e2d\u55e1\u55e1\u4f5c\u54cd\uff0c\u4f3c\u4e4e\u542c\u5230\u81ea\u5df1\u53e3\u4e2d\u95ee\u9053\uff1a\u201c\u6211\u5e08\u7236\u5462\uff1f\u201d\u4f3c\u4e4e\u542c\u5230\u6c88\u57ce\u7b11\u9053\uff1a\u201c\u8c01\u77e5\u9053\u5462\uff1f\u4ed6\u53ea\u9053\u81ea\u5df1\u6740\u4e86\u4eba\uff0c\u8fd8\u4e0d\u9ad8\u98de\u8fdc\u8d70\uff1f\u54ea\u91cc\u8fd8\u6562\u56de\u6765\uff1f\u201d\u53c8\u4f3c\u4e4e\u542c\u5230\u6c88\u57ce\u7b11\u9053\uff1a\u201c\u4e07\u5e08\u5ac2\u8bf4\u9053\uff1a\u4f60\u5728\u7262\u91cc\u5b89\u5fc3\u4f4f\u4e0b\u53bb\u7f62\uff0c\u5f85\u5979\u751f\u5f97\u4e09\u7537\u56db\u5973\uff0c\u8bf4\u4e0d\u5b9a\u4f1a\u6765\u77a7\u77a7\u4f60\u3002\u201d \u3000\u3000\u72c4\u4e91\u7a81\u7136\u5927\u543c\uff1a\u201c\u4f60\u80e1\u8bf4\uff0c\u80e1\u8bf4\uff01\u4f60\u2026\u2026\u4f60\u2026\u2026\u4f60\u653e\u751a\u4e48\u72d7\u5c41\u2026\u2026\u201d\u63d0\u8d77\u7bee\u5b50\u7528\u529b\u63b7\u51fa\uff0c\u559c\u7cd5\u3001\u732a\u8e44\u3001\u719f\u9e21\uff0c\u6eda\u4e86\u4e00\u5730\u3002\u4f46\u89c1\u6bcf\u4e00\u5757\u7c89\u7ea2\u8272\u7684\u559c\u7cd5\u4e0a\uff0c\u90fd\u5370\u7740\u201c\u4e07\u621a\u8054\u59fb\uff0c\u767e\u5e74\u597d\u5408\u201d\u516b\u4e2a\u6df1\u7ea2\u7684\u5c0f\u5b57\u3002 \u3000\u3000\u72c4\u4e91\u62da\u547d\u8981\u4e0d\u4fe1\u6c88\u57ce\u7684\u8bdd\uff0c\u53ef\u53c8\u600e\u80fd\u4e0d\u4fe1\uff1f\u8ff7\u8ff7\u7cca\u7cca\u4e2d\u53ea\u542c\u6c88\u57ce\u7b11\u9053\uff1a\u201c\u4e07\u5e08\u5ac2\u8bf4\uff0c\u53ef\u60dc\u4f60\u4e0d\u80fd\u53bb\u559d\u4e00\u676f\u559c\u9152\u2026\u2026\u201d \u3000\u3000\u72c4\u4e91\u53cc\u624b\u8fde\u7740\u94c1\u94d0\uff0c\u7a81\u7136\u4ece\u6805\u680f\u4e2d\u75be\u4f38\u51fa\u53bb\uff0c\u4e00\u628a\u634f\u4f4f\u6c88\u57ce\u7684\u8116\u5b50\u3002\u6c88\u57ce\u5927\u60ca\u60f3\u9003\u3002\u72c4\u4e91\u4e0d\u77e5\u4ece\u54ea\u91cc\u7a81\u7136\u751f\u51fa\u6765\u4e00\u80a1\u52b2\u529b\uff0c\u7adf\u8d8a\u634f\u8d8a\u7d27\u3002\u6c88\u57ce\u7684\u8138\u4ece\u7ea2\u53d8\u7d2b\uff0c\u53cc\u624b\u4e71\u821e\uff0c\u59cb\u7ec8\u6323\u624e\u4e0d\u8131\u3002 \u3000\u3000\u90a3\u72f1\u5352\u6025\u5fd9\u8d76\u6765\uff0c\u62b1\u7740\u6c88\u57ce\u7684\u8eab\u5b50\u731b\u62c9\uff0c\u8d39\u5c3d\u4e86\u529b\u6c14\uff0c\u624d\u6551\u4e86\u4ed6\u6027\u547d\u3002 \u3000\u3000\u72c4\u4e91\u5750\u5728\u5730\u4e0b\uff0c\u4e0d\u8a00\u4e0d\u52a8\u3002\u90a3\u72f1\u5352\u563b\u563b\u54c8\u54c8\u7684\u5c06\u9e21\u8089\u548c\u559c\u7cd5\u90fd\u6361\u4e86\u53bb\u3002\u72c4\u4e91\u77aa\u7740\u773c\u775b\uff0c\u53ef\u5c31\u5168\u6ca1\u77a7\u89c1\u3002 \u3000\u3000\u8fd9\u5929\u665a\u4e0a\u4e09\u66f4\u65f6\u5206\uff0c\u4ed6\u5c06\u8863\u886b\u6495\u6210\u4e86\u4e00\u6761\u6761\u5e03\u6761\uff0c\u6413\u6210\u4e86\u4e00\u6839\u7ef3\u5b50\uff0c\u6253\u4e00\u4e2a\u6d3b\u7ed3\uff0c\u4e24\u7aef\u7f1a\u5728\u94c1\u6805\u680f\u9ad8\u5904\u7684\u6a2a\u6863\u4e0a\uff0c\u5c06\u5934\u4f38\u8fdb\u6d3b\u7ed3\u4e4b\u4e2d\u3002 \u3000\u3000\u4ed6\u5e76\u4e0d\u60b2\u54c0\uff0c\u4e5f\u4e0d\u518d\u611f\u5230\u6124\u6068\u3002\u4eba\u4e16\u5df2\u65e0\u53ef\u604b\u4e4b\u5904\uff0c\u8fd9\u662f\u6700\u723d\u5feb\u7684\u89e3\u8131\u75db\u82e6\u7684\u6cd5\u5b50\u3002\u53ea\u89c9\u8116\u5b50\u4e2d\u7684\u7ef3\u7d22\u8d8a\u6765\u8d8a\u7d27\uff0c\u4e00\u4e1d\u4e1d\u7684\u6c14\u606f\u4e5f\u5438\u4e0d\u8fdb\u4e86\u3002\u8fc7\u5f97\u7247\u523b\uff0c\u751a\u4e48\u4e5f\u4e0d\u77e5\u9053\u4e86\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u7ec8\u4e8e\u6e10\u6e10\u6709\u4e86\u77e5\u89c9\uff0c\u597d\u50cf\u6709\u4e00\u53ea\u5927\u624b\u5728\u91cd\u91cd\u538b\u4ed6\u80f8\u53e3\uff0c\u90a3\u53ea\u624b\u4e00\u677e\u4e00\u538b\uff0c\u9f3b\u5b50\u4e2d\u5c31\u6709\u4e00\u9635\u9635\u51c9\u6c14\u900f\u4e86\u8fdb\u6765\u3002\u4e5f\u4e0d\u77e5\u9053\u8fc7\u4e86\u591a\u5c11\u65f6\u5019\uff0c\u4ed6\u624d\u6162\u6162\u7741\u5f00\u773c\u6765\u3002 \u3000\u3000\u773c\u524d\u662f\u4e00\u5f20\u6ee1\u816e\u866c\u9aef\u7684\u8138\uff0c\u90a3\u5f20\u8138\u54a7\u5f00\u4e86\u5634\u5728\u7b11\u3002 \u3000\u3000\u72c4\u4e91\u4e0d\u7531\u5f97\u6ee1\u8179\u6c14\u607c\uff0c\u5fc3\u9053\uff1a\u201c\u4f60\u4e8b\u4e8b\u8ddf\u6211\u4f5c\u5bf9\uff0c\u6211\u4fbf\u662f\u5bfb\u6b7b\uff0c\u4f60\u4e5f\u4e0d\u8bb8\u6211\u6b7b\u3002\u201d\u6709\u5fc3\u8981\u8d77\u6765\u548c\u4ed6\u53ae\u62da\uff0c\u5b9e\u662f\u592a\u8fc7\u8870\u5f31\uff0c\u529b\u4e0d\u4ece\u5fc3\u3002\u90a3\u75af\u6c49\u7b11\u9053\uff1a\u201c\u4f60\u5df2\u6c14\u7edd\u4e86\u5c0f\u534a\u4e2a\u65f6\u8fb0\uff0c\u82e5\u4e0d\u662f\u6211\u7528\u72ec\u95e8\u529f\u592b\u76f8\u6551\uff0c\u5929\u4e0b\u518d\u6ca1\u7b2c\u4e8c\u4e2a\u4eba\u6551\u5f97\u3002\u201d\u72c4\u4e91\u6012\u9053\uff1a\u201c\u8c01\u8981\u4f60\u6551\uff1f\u6211\u53c8\u4e0d\u60f3\u6d3b\u4e86\u3002\u201d\u90a3\u75af\u6c49\u5f97\u610f\u6d0b\u6d0b\u7684\u9053\uff1a\u201c\u6211\u4e0d\u8bb8\u4f60\u6b7b\uff0c\u4f60\u4fbf\u6b7b\u4e0d\u4e86\u3002\u201d \u3000\u3000\u90a3\u75af\u6c49\u53ea\u662f\u7b11\u541f\u541f\u7684\u77a7\u7740\u4ed6\uff0c\u8fc7\u4e86\u4e00\u4f1a\uff0c\u5ffd\u7136\u51d1\u5230\u4ed6\u7684\u8eab\u8fb9\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u6211\u8fd9\u95e8\u529f\u592b\u53eb\u4f5c\u2018\u795e\u7167\u7ecf\u2019\uff0c\u4f60\u542c\u89c1\u8fc7\u6ca1\u6709\uff1f\u201d \u3000\u3000\u72c4\u4e91\u6012\u9053\uff1a\u201c\u6211\u53ea\u77e5\u9053\u4f60\u6709\u795e\u7ecf\u75c5\uff0c\u751a\u4e48\u795e\u7167\u7ecf\u4e0d\u795e\u7167\u7ecf\uff0c\u4ece\u6765\u6ca1\u542c\u89c1\u8fc7\u3002\u201d \u3000\u3000\u8bf4\u4e5f\u5947\u602a\uff0c\u90a3\u75af\u6c49\u8fd9\u4e00\u6b21\u7adf\u4e1d\u6beb\u6ca1\u6709\u53d1\u6012\uff0c\u53cd\u800c\u8f7b\u8f7b\u7684\u54fc\u8d77\u5c0f\u66f2\u6765\uff0c\u4f38\u624b\u538b\u4f4f\u72c4\u4e91\u7684\u80f8\u53e3\uff0c\u4e00\u538b\u4e00\u653e\uff0c\u4fbf\u5982\u626f\u98ce\u7bb1\u4e00\u822c\uff0c\u5c06\u6c14\u606f\u538b\u5165\u4ed6\u80ba\u4e2d\uff0c\u4f4e\u58f0\u53c8\u9053\uff1a\u201c\u4e5f\u662f\u4f60\u547d\u5927\uff0c\u6211\u8fd9\u2018\u795e\u7167\u7ecf\u2019\u5df2\u7ec3\u4e86\u4e00\u5341\u4e8c\u5e74\uff0c\u76f4\u5230\u4e24\u4e2a\u6708\u524d\u65b9\u624d\u7ec3\u6210\u3002\u5018\u82e5\u4f60\u5728\u4e24\u4e2a\u6708\u4e4b\u524d\u5bfb\u6b7b\uff0c\u6211\u5c31\u6551\u4f60\u4e0d\u5f97\u4e86\u3002\u201d \u3000\u3000\u72c4\u4e91\u80f8\u53e3\u90c1\u95f7\u96be\u5f53\uff0c\u60f3\u8d77\u4e86\u621a\u82b3\u5ac1\u4e86\u4e07\u572d\uff0c\u771f\u89c9\u8fd8\u662f\u6b7b\u4e86\u7684\u5e72\u51c0\uff0c\u5411\u90a3\u75af\u6c49\u77aa\u4e86\u4e00\u773c\uff0c\u6068\u6068\u7684\u9053\uff1a\u201c\u6211\u524d\u751f\u4e0d\u77e5\u4f5c\u4e86\u751a\u4e48\u5b7d\uff0c\u4eca\u4e16\u8981\u649e\u5230\u4f60\u8fd9\u6076\u8d3c\u3002\u201d \u3000\u3000\u90a3\u75af\u6c49\u7b11\u9053\uff1a\u201c\u6211\u5f88\u5f00\u5fc3\uff0c\u5c0f\u5144\u5f1f\uff0c\u8fd9\u4e09\u5e74\u6765\u6211\u771f\u9519\u602a\u4e86\u4f60\u3002\u6211\u4e01\u5178\u5411\u4f60\u8d54\u4e0d\u662f\u5566\uff01\u201d\u8bf4\u7740\u722c\u5728\u5730\u4e0b\uff0c\u549a\u549a\u549a\u7684\u5411\u4ed6\u78d5\u4e86\u4e09\u4e2a\u54cd\u5934\u3002 \u3000\u3000\u72c4\u4e91\u53f9\u4e86\u53e3\u6c14\uff0c\u4f4e\u58f0\u8bf4\u4e86\u58f0\uff1a\u201c\u75af\u5b50\uff01\u201d\u4e5f\u5c31\u6ca1\u518d\u53bb\u7406\u4ed6\uff0c\u6162\u6162\u4fa7\u8fc7\u8eab\u6765\uff0c\u7a81\u7136\u60f3\u8d77\uff1a\u201c\u4ed6\u81ea\u79f0\u4e01\u5178\uff0c\u90a3\u662f\u59d3\u4e01\u540d\u5178\u4e48\uff1f \u3000\u3000\u6211\u548c\u4ed6\u5728\u72f1\u4e2d\u540c\u5904\u4e09\u5e74\uff0c\u4e00\u76f4\u4e0d\u77e5\u4ed6\u7684\u59d3\u540d\u3002\u201d\u597d\u5947\u5fc3\u8d77\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u53eb\u751a\u4e48\uff1f\u201d\u90a3\u75af\u6c49\u9053\uff1a\u201c\u6211\u59d3\u4e01\uff0c\u76ee\u4e0d\u8bc6\u4e01\u7684\u4e01\uff0c\u4e09\u575f\u4e94\u5178\u7684\u5178\u3002\u6211\u7591\u5fc3\u75c5\u592a\u91cd\uff0c\u4e00\u76f4\u5f53\u4f60\u662f\u6b79\u4eba\uff0c\u8fd9\u4e09\u5e74\u591a\u6765\u5f53\u771f\u5c06\u4f60\u5bb3\u5f97\u82e6\u4e86\uff0c\u5b9e\u5728\u592a\u5bf9\u4f60\u4e0d\u8d77\u3002\u201d\u72c4\u4e91\u89c9\u5f97\u4ed6\u8bf4\u8bdd\u6709\u6761\u6709\u7406\uff0c\u5e76\u65e0\u534a\u70b9\u75af\u6001\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u5230\u5e95\u662f\u4e0d\u662f\u75af\u5b50\uff1f\u201d \u3000\u3000\u4e01\u5178\u9eef\u7136\u4e0d\u8bed\uff0c\u9694\u5f97\u534a\u664c\uff0c\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5230\u5e95\u75af\u4e0d\u75af\uff0c\u90a3\u4e5f\u96be\u8bf4\u5f97\u5f88\u3002\u6211\u662f\u5728\u6c42\u5fc3\u4e4b\u6240\u5b89\uff0c\u65c1\u4eba\u770b\u6765\uff0c\u5374\u4e0d\u514d\u89c9\u5f97\u6211\u592a\u8fc7\u50bb\u5f97\u83ab\u540d\u5176\u5999\u3002\u201d\u8fc7\u4e86\u4e00\u4f1a\uff0c\u53c8\u5b89\u6170\u4ed6\u9053\uff1a\u201c\u72c4\u5144\u5f1f\uff0c\u4f60\u5fc3\u4e2d\u7684\u59d4\u5c48\uff0c\u6211\u5df2\u731c\u5230\u4e86\u5341\u4e4b\u516b\u4e5d\u3002\u4eba\u5bb6\u65e2\u7136\u5bf9\u4f60\u65e0\u60c5\u65e0\u4e49\uff0c\u4f60\u53c8\u4f55\u5fc5\u5c06\u8fd9\u5973\u5b50\u82e6\u82e6\u653e\u5728\u5fc3\u4e0a\uff1f\u5927\u4e08\u592b\u4f55\u60a3\u65e0\u59bb\uff1f\u5c06\u6765\u5a36\u4e00\u4e2a\u80dc\u4f60\u5e08\u59b9\u5341\u500d\u7684\u5973\u5b50\uff0c\u53c8\u6709\u4f55\u96be\uff1f\u201d \u3000\u3000\u72c4\u4e91\u542c\u4e86\u8fd9\u756a\u8bf4\u8bdd\uff0c\u4e09\u5e74\u591a\u6765\u90c1\u5728\u5fc3\u4e2d\u7684\u59d4\u5c48\uff0c\u5fcd\u4e0d\u4f4f\u4fbf\u5982\u5c71\u6d2a\u822c\u5954\u6cfb\u4e86\u51fa\u6765\uff0c\u4f46\u89c9\u80f8\u53e3\u4e00\u9178\uff0c\u6cea\u73e0\u6eda\u6eda\u800c\u4e0b\uff0c\u5230\u540e\u6765\uff0c\u4fbf\u4f0f\u5728\u4e01\u5178\u6000\u4e2d\u5927\u54ed\u8d77\u6765\u3002 \u3000\u3000\u4e01\u5178\u6402\u4f4f\u4ed6\u4e0a\u8eab\uff0c\u8f7b\u8f7b\u629a\u6478\u4ed6\u7684\u957f\u53d1\u3002 \u3000\u3000\u8fc7\u5f97\u4e09\u5929\uff0c\u72c4\u4e91\u7cbe\u795e\u7a0d\u632f\u3002\u4e01\u5178\u4f4e\u4f4e\u7684\u8ddf\u4ed6\u6709\u8bf4\u6709\u7b11\uff0c\u8bb2\u4e9b\u6c5f\u6e56\u4e0a\u7684\u638c\u6545\u8da3\u4e8b\uff0c\u8ddf\u4ed6\u89e3\u95f7\u3002\u4f46\u5f53\u72f1\u540f\u9001\u996d\u6765\u65f6\uff0c\u4e01\u5178\u5374\u4ecd\u5bf9\u72c4\u4e91\u5927\u58f0\u547c\u53f1\uff0c\u79fd\u8bed\u8fb1\u9a82\uff0c\u795e\u60c5\u4e0e\u524d\u6beb\u65e0\u5f02\u6837\u3002 \u3000\u3000\u4e00\u4e2a\u6298\u78e8\u5f97\u4ed6\u82e6\u607c\u4e0d\u582a\u7684\u5bf9\u5934\uff0c\u7a81\u7136\u95f4\u6210\u4e3a\u826f\u670b\u597d\u53cb\uff0c\u82e5\u4e0d\u662f\u621a\u82b3\u5ac1\u4e86\u4eba\u8fd9\u4ef6\u4e8b\u4e0d\u65ad\u50cf\u6bd2\u866b\u822c\u54ac\u566c\u4ed6\u7684\u5fc3\uff0c\u8fd9\u65f6\u7684\u72f1\u4e2d\u751f\u6daf\uff0c\u548c\u4e09\u5e74\u6765\u7684\u60c5\u5f62\u76f8\u6bd4\uff0c\u7b80\u76f4\u7b97\u5f97\u662f\u5929\u5802\u4e86\u3002 \u3000\u3000\u72c4\u4e91\u66fe\u5411\u4e01\u5178\u95ee\u8d77\uff0c\u4e3a\u751a\u4e48\u4ee5\u524d\u5f53\u4ed6\u662f\u6b79\u4eba\uff0c\u4e3a\u751a\u4e48\u7a81\u7136\u5bdf\u89c9\u4e86\u771f\u76f8\u3002\u4e01\u5178\u9053\uff1a\u201c\u4f60\u82e5\u771f\u662f\u6b79\u4eba\uff0c\u51b3\u4e0d\u4f1a\u4e0a\u540a\u81ea\u6740\u3002 \u3000\u3000\u6211\u7b49\u4f60\u6c14\u7edd\u597d\u4e45\uff0c\u6b7b\u5f97\u900f\u4e86\uff0c\u8eab\u5b50\u90fd\u5feb\u50f5\u4e86\uff0c\u8fd9\u624d\u65bd\u6551\u3002\u666e\u5929\u4e0b\u9664\u4e86\u6211\u81ea\u5df1\u4e4b\u5916\uff0c\u6ca1\u4eba\u77e5\u9053\u6211\u5df2\u7ec3\u6210\u2018\u795e\u7167\u7ecf\u2019\u7684\u4e0a\u4e58\u529f\u592b\u3002\u82e5\u4e0d\u662f\u6211\u4f1a\u5f97\u8fd9\u95e8\u529f\u592b\uff0c\u65e0\u8bba\u5982\u4f55\u6551\u4f60\u4e0d\u8f6c\u3002\u4f60\u81ea\u6740\u65e2\u662f\u771f\u7684\uff0c\u90a3\u4fbf\u4e0d\u662f\u5411\u6211\u65bd\u82e6\u8089\u8ba1\u7684\u6b79\u4eba\u4e86\u3002\u201d\u72c4\u4e91\u53c8\u95ee\uff1a \u3000\u3000\u201c\u4f60\u7591\u5fc3\u6211\u5411\u4f60\u65bd\u82e6\u8089\u8ba1\uff1f\u90a3\u4e3a\u751a\u4e48\uff1f\u201d\u4e01\u5178\u5fae\u7b11\u4e0d\u7b54\u3002 \u3000\u3000\u7b2c\u4e8c\u6b21\u72c4\u4e91\u53c8\u95ee\u5230\u8fd9\u4ef6\u4e8b\u65f6\uff0c\u4e01\u5178\u4ecd\u662f\u4e0d\u7b54\uff0c\u72c4\u4e91\u4fbf\u4e0d\u518d\u95ee\u4e86\u3002 \u3000\u3000\u4e00\u65e5\u665a\u4e0a\uff0c\u4e01\u5178\u5728\u4ed6\u8033\u8fb9\u4f4e\u58f0\u9053\uff1a\u201c\u6211\u8fd9\u2018\u795e\u7167\u7ecf\u2019\u529f\u592b\uff0c\u662f\u5929\u4e0b\u5185\u529f\u4e2d\u5a01\u529b\u6700\u5f3a\u3001\u6700\u5965\u5999\u7684\u6cd5\u95e8\u3002\u4eca\u65e5\u8d77\u6211\u4f20\u6388\u7ed9\u4f60\uff0c\u4f60\u5c0f\u5fc3\u8bb0\u4f4f\u4e86\u3002\u201d\u72c4\u4e91\u6447\u5934\u9053\uff1a\u201c\u6211\u4e0d\u5b66\u3002\u201d\u4e01\u5178\u5947\u9053\uff1a\u201c\u8fd9\u7b49\u673a\u7f18\u65f7\u4e16\u96be\u9022\uff0c\u4f60\u4e3a\u751a\u4e48\u4e0d\u8981\u5b66\uff1f\u201d\u72c4\u4e91\u9053\uff1a\u201c\u8fd9\u79cd\u65e5\u5b50\u751f\u4e0d\u5982\u6b7b\u3002\u54b1\u4e8c\u4eba\u6b64\u751f\u770b\u6765\u4e5f\u65e0\u51fa\u72f1\u7684\u65f6\u5019\uff0c\u518d\u9ad8\u5f3a\u7684\u6b66\u529f\u5b66\u4e86\u4e5f\u662f\u6beb\u65e0\u7528\u5904\u3002\u201d\u4e01\u5178\u7b11\u9053\uff1a\u201c\u8981\u51fa\u72f1\u53bb\uff0c\u90a3\u8fd8\u4e0d\u5bb9\u6613\uff1f\u6211\u5c06\u521d\u6b65\u53e3\u8bc0\u4f20\u4f60\uff0c\u4f60\u597d\u597d\u8bb0\u7740\u3002\u201d \u3000\u3000\u72c4\u4e91\u751a\u662f\u6267\u62d7\uff0c\u5bfb\u6b7b\u7684\u5ff5\u5934\u5140\u81ea\u672a\u6d88\uff0c\u8bf4\u751a\u4e48\u4e5f\u4e0d\u80af\u5b66\u3002 \u3000\u3000\u4e01\u5178\u53c8\u597d\u6c14\u53c8\u597d\u7b11\uff0c\u5374\u4e5f\u675f\u624b\u65e0\u7b56\uff0c\u6068\u4e0d\u5f97\u518d\u50cf\u4ece\u524d\u8fd9\u822c\u6253\u4ed6\u4e00\u987f\u3002 \u3000\u3000\u53c8\u8fc7\u6570\u65e5\uff0c\u6708\u4eae\u53c8\u8981\u5706\u4e86\u3002\u72c4\u4e91\u4e0d\u7981\u6697\u6697\u66ff\u4e01\u5178\u62c5\u5fc3\u3002\u4e01\u5178\u731c\u5230\u4ed6\u5fc3\u610f\uff0c\u8bf4\u9053\uff1a\u201c\u72c4\u5144\u5f1f\uff0c\u6211\u6bcf\u4e2a\u6708\u8be5\u5f53\u6709\u8fd9\u756a\u6298\u78e8\uff0c\u6211\u53d7\u4e86\u62f7\u6253\u540e\uff0c\u56de\u6765\u4ecd\u8981\u6253\u4f60\u51fa\u6c14\uff0c\u4f60\u6211\u5343\u4e07\u4e0d\u53ef\u663e\u5f97\u548c\u597d\uff0c\u5426\u5219\u4e8e\u4f60\u6211\u90fd\u662f\u5927\u5927\u7684\u4e0d\u5229\u3002\u201d\u72c4\u4e91\u95ee\u9053\uff1a\u201c\u90a3\u4e3a\u751a\u4e48\uff1f\u201d\u4e01\u5178\u9053\uff1a\u201c\u4ed6\u4eec\u5018\u82e5\u7591\u5fc3\u4f60\u6211\u4ea4\u4e86\u670b\u53cb\uff0c\u4fbf\u4f1a\u5bf9\u4f60\u4f7f\u7528\u6bd2\u5211\uff0c\u903c\u4f60\u5411\u6211\u5957\u95ee\u4e00\u4ef6\u4e8b\u3002\u6211\u6253\u4f60\u9a82\u4f60\uff0c\u5c31\u53ef\u514d\u5f97\u4f60\u8eab\u906d\u6076\u6bd2\u60e8\u9177\u7684\u5211\u7f5a\u3002\u201d \u3000\u3000\u72c4\u4e91\u70b9\u5934\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u4ef6\u4e8b\u65e2\u5982\u6b64\u91cd\u8981\uff0c\u4f60\u5343\u4e07\u4e0d\u53ef\u8bf4\u4e0e\u6211\u77e5\u9053\uff0c\u514d\u5f97\u6211\u4e00\u4e2a\u4e0d\u5c0f\u5fc3\uff0c\u8d70\u6f0f\u4e86\u98ce\u58f0\u3002\u4e01\u5927\u54e5\uff0c\u6211\u662f\u4e2a\u6beb\u65e0\u89c1\u8bc6\u7684\u4e61\u4e0b\u5c0f\u5b50\uff0c\u5018\u82e5\u80e1\u91cc\u80e1\u6d82\u8bef\u4e86\u4f60\u7684\u5927\u4e8b\uff0c\u5982\u4f55\u5bf9\u5f97\u8d77\u4f60\uff1f\u201d \u3000\u3000\u4e01\u5178\u9053\uff1a\u201c\u4ed6\u4eec\u628a\u4f60\u548c\u6211\u5173\u5728\u4e00\u8d77\uff0c\u521d\u65f6\u6211\u53ea\u9053\u4ed6\u4eec\u6d3e\u4f60\u524d\u6765\u5367\u5e95\uff0c\u5047\u610f\u8ba8\u597d\u4e8e\u6211\uff0c\u4ece\u4e2d\u8bbe\u6cd5\u5957\u95ee\u6211\u7684\u53e3\u98ce\uff0c\u56e0\u6b64\u6211\u5bf9\u4f60\u5341\u5206\u607c\u6012\uff0c\u5927\u52a0\u6298\u78e8\u3002\u73b0\u4e0b\u6211\u77e5\u9053\u4f60\u4e0d\u662f\u5367\u5e95\u7684\u5978\u7ec6\u4e86\uff0c\u53ef\u662f\u4ed6\u4eec\u5c06\u4f60\u548c\u6211\u5173\u5728\u4e00\u8d77\uff0c\u8fd9\u822c\u4e09\u5e74\u56db\u5e74\u7684\u4e0d\u653e\uff0c\u7528\u610f\u4ecd\u5728\u76fc\u4f60\u505a\u5978\u7ec6\u3002\u53ea\u671b\u4f60\u8ba8\u5f97\u6211\u7684\u6b22\u5fc3\uff0c\u6211\u5411\u4f60\u5410\u9732\u4e86\u673a\u5bc6\uff0c\u4ed6\u4eec\u4fbf\u53ef\u62f7\u6253\u903c\u95ee\u4e8e\u4f60\u3002\u4ed6\u4eec\u60c5\u77e5\u5bf9\u4ed8\u6211\u5f88\u96be\uff0c\u5bf9\u4ed8\u4f60\u8fd9\u4e2a\u5e74\u8f7b\u5c0f\u4f19\u5b50\uff0c\u90a3\u4fbf\u5bb9\u6613\u4e4b\u6781\u3002\u4f60\u662f\u77e5\u53bf\u8859\u95e8\u7684\u72af\u4eba\uff0c\u5374\u9001\u5230\u77e5\u5e9c\u8859\u95e8\u7684\u56da\u7262\u6765\u76d1\u7981\uff0c\u81ea\u7136\u4fbf\u662f\u8fd9\u4e2a\u7f18\u6545\u3002\u201d \u3000\u3000\u5341\u4e94\u665a\u4e0a\uff0c\u56db\u540d\u5e26\u5200\u72f1\u5352\u63d0\u4e86\u4e01\u5178\u51fa\u53bb\u3002\u72c4\u4e91\u5fc3\u7eea\u4e0d\u5b81\uff0c\u7b49\u5019\u4ed6\u56de\u8f6c\u3002\u5230\u5f97\u56db\u66f4\u5929\u65f6\uff0c\u4e01\u5178\u53c8\u662f\u76ee\u9752\u9f3b\u80bf\u3001\u6ee1\u8eab\u9c9c\u8840\u7684\u56de\u5230\u7262\u623f\u3002 \u3000\u3000\u5f85\u56db\u540d\u72f1\u5352\u8d70\u540e\uff0c\u4e01\u5178\u8138\u8272\u90d1\u91cd\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u72c4\u5144\u5f1f\uff0c\u4eca\u5929\u4e8b\u60c5\u5f88\u662f\u7cdf\u7cd5\uff0c\u5f53\u771f\u4e0d\u5de7\u4e4b\u6781\uff0c\u7ed9\u4ec7\u4eba\u8ba4\u51fa\u4e86\u6211\u3002\u201d\u72c4\u4e91\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u4e01\u5178\u9053\uff1a\u201c\u6bcf\u6708\u5341\u4e94\uff0c\u77e5\u5e9c\u63d0\u6211\u53bb\u62f7\u6253\u4e00\u987f\uff0c\u90a3\u662f\u4f8b\u884c\u516c\u4e8b\u3002\u53ef\u662f\u4eca\u5929\u6709\u4eba\u6765\u884c\u523a\u77e5\u5e9c\u3002\u773c\u89c1\u4ed6\u6027\u547d\u4e0d\u4fdd\uff0c\u6211\u4fbf\u51fa\u624b\u76f8\u6551\uff0c\u53ea\u56e0\u6211\u8eab\u6709\u94d0\u9563\uff0c\u56db\u540d\u523a\u5ba2\u4e2d\u53ea\u6740\u4e86\u4e09\u4e2a\uff0c\u7b2c\u56db\u4e2a\u7ed9\u4ed6\u8dd1\u4e86\uff0c\u8fd9\u53ef\u7559\u4e0b\u4e86\u7978\u80ce\u3002\u201d \u3000\u3000\u72c4\u4e91\u8d8a\u542c\u8d8a\u5947\u602a\uff0c\u8fde\u95ee\uff1a\u201c\u77e5\u5e9c\u5230\u5e95\u4e3a\u751a\u4e48\u8fd9\u822c\u62f7\u6253\u4f60\uff1f \u3000\u3000\u8fd9\u77e5\u5e9c\u8fd9\u7b49\u6b8b\u66b4\uff0c\u6709\u4eba\u884c\u523a\uff0c\u4f60\u53c8\u4f55\u5fc5\u6551\u4ed6\uff1f\u9003\u8d70\u7684\u5251\u5ba2\u662f\u8c01\uff1f\u201d\u4e01\u5178\u6447\u6447\u5934\uff0c\u53f9\u9053\uff1a\u201c\u4e00\u65f6\u4e5f\u8bf4\u4e0d\u6e05\u695a\u8fd9\u8bb8\u591a\u4e8b\u3002\u72c4\u5144\u5f1f\uff0c\u4f60\u6b66\u529f\u4e0d\u6d4e\uff0c\u53c8\u6ca1\u4e86\u529b\u6c14\uff0c\u4ee5\u540e\u4e0d\u8bba\u89c1\u5230\u751a\u4e48\u4e8b\uff0c\u5343\u4e07\u4e0d\u53ef\u51fa\u624b\u52a9\u6211\u3002\u201d \u3000\u3000\u72c4\u4e91\u5e76\u4e0d\u7b54\u8bdd\uff0c\u5fc3\u60f3\uff1a\u201c\u6211\u59d3\u72c4\u7684\u5c82\u662f\u8d2a\u751f\u6015\u6b7b\u4e4b\u5f92\uff1f\u4f60\u62ff\u6211\u5f53\u670b\u53cb\uff0c\u4f60\u82e5\u6709\u5371\u96be\uff0c\u6211\u600e\u80fd\u4e0d\u51fa\u624b\uff1f\u201d \u3000\u3000\u6b64\u540e\u6570\u65e5\u4e4b\u4e2d\uff0c\u4e01\u5178\u53ea\u662f\u9ed8\u9ed8\u6c89\u601d\uff0c\u9664\u4e86\u671b\u7740\u8fdc\u5904\u9ad8\u697c\u7a97\u69db\u4e0a\u7684\u82b1\u6735\uff0c\u8138\u4e0a\u5076\u5c14\u9732\u51fa\u4e00\u4e1d\u5fae\u7b11\u4e4b\u5916\uff0c\u6574\u65e5\u4ef0\u8d77\u4e86\u5934\u5446\u60f3\u3002 \u3000\u3000\u5230\u4e86\u5341\u4e5d\u90a3\u4e00\u5929\u6df1\u591c\uff0c\u72c4\u4e91\u7761\u5f97\u6b63\u719f\uff0c\u5ffd\u542c\u5f97\u5580\u5580\u4e24\u58f0\u3002 \u3000\u3000\u4ed6\u7741\u5f00\u773c\u6765\uff0c\u6708\u5149\u4e0b\u53ea\u89c1\u4e24\u540d\u52b2\u88c5\u5927\u6c49\u4f7f\u5229\u5668\u780d\u65ad\u4e86\u7262\u623f\u5916\u7684\u94c1\u6805\u680f\uff0c\u624b\u4e2d\u5404\u6267\u4e00\u67c4\u5355\u5200\uff0c\u8e0a\u8eab\u800c\u5165\u3002\u72c4\u4e91\u60ca\u5f97\u5446\u4e86\uff0c\u4e0d\u77e5\u5982\u4f55\u662f\u597d\uff0c\u4f46\u89c1\u4e01\u5178\u501a\u5899\u800c\u7acb\uff0c\u563f\u563f\u51b7\u7b11\u3002 \u3000\u3000\u90a3\u8eab\u6750\u8f83\u77ee\u7684\u5927\u6c49\u8bf4\u9053\uff1a\u201c\u59d3\u4e01\u7684\uff0c\u54b1\u5144\u5f1f\u4fe9\u8e0f\u904d\u4e86\u5929\u6daf\u6d77\u89d2\uff0c\u5230\u5904\u627e\u4f60\uff0c\u54ea\u60f3\u5f97\u5230\u4f60\u7adf\u662f\u8eb2\u5165\u8346\u5dde\u5e9c\u7684\u7262\u623f\uff0c\u505a\u90a3\u7f29\u5934\u4e4c\u9f9f\u3002\u603b\u7b97\u8001\u5929\u6709\u773c\uff0c\u5bfb\u5230\u4e86\u4f60\u3002\u201d\u53e6\u4e00\u540d\u5927\u6c49\u9053\uff1a\u201c\u54b1\u4eec\u771f\u4eba\u9762\u524d\u4e0d\u8bf4\u5047\u8bdd\uff0c\u4f60\u5c06\u90a3\u672c\u4e66\u53d6\u51fa\u6765\uff0c\u4e09\u4efd\u5bf9\u5206\uff0c\u54b1\u5144\u5f1f\u975e\u4f46\u4e0d\u4f1a\u96be\u4e3a\u4f60\uff0c\u8fd8\u7acb\u523b\u5c06\u4f60\u6551\u51fa\u7262\u72f1\u3002\u201d\u4e01\u5178\u6447\u5934\u9053\uff1a\u201c\u4e0d\u5728\u6211\u8fd9\u91cc\u3002\u5341\u4e09\u5e74\u524d\uff0c\u65e9\u5c31\u7ed9\u8a00\u8fbe\u5e73\u5077\u4e86\u53bb\u5566\u3002\u201d \u3000\u3000\u72c4\u4e91\u542c\u5230\u201c\u8a00\u8fbe\u5e73\u201d\u4e09\u5b57\uff0c\u5fc3\u4e2d\u4e00\u52a8\uff1a\u201c\u90a3\u662f\u6211\u4e8c\u5e08\u4f2f\u554a\uff0c\u600e\u5730\u8ddf\u6b64\u4e8b\u751f\u4e86\u5173\u8054\uff1f\u201d \u3000\u3000\u90a3\u77ee\u5927\u6c49\u559d\u9053\uff1a\u201c\u4f60\u6545\u5e03\u7591\u9635\uff0c\u4f11\u60f3\u7792\u5f97\u8fc7\u6211\u3002\u53bb\u4f60\u7684\u7f62\uff01\u201d \u3000\u3000\u6325\u5200\u4e0a\u524d\uff0c\u5200\u5c16\u523a\u5411\u4e01\u5178\u7684\u54bd\u5589\u3002\u4e01\u5178\u4e0d\u95ea\u4e0d\u907f\uff0c\u8ba9\u90a3\u5c16\u5200\u5c06\u53ca\u5589\u5934\u6570\u5bf8\u4e4b\u5904\uff0c\u7a81\u7136\u4e00\u77ee\u8eab\uff0c\u6b3a\u5411\u8eab\u6750\u8f83\u9ad8\u7684\u5927\u6c49\u5de6\u4fa7\uff0c\u624b\u8098\u649e\u5904\uff0c\u6b63\u4e2d\u4ed6\u5c0f\u8179\u3002\u90a3\u5927\u6c49\u4e00\u58f0\u6ca1\u54fc\uff0c\u4fbf\u5373\u59d4\u5012\u3002 \u3000\u3000\u90a3\u77ee\u5927\u6c49\u60ca\u6012\u4ea4\u96c6\uff0c\u547c\u547c\u4e24\u5200\uff0c\u5411\u4e01\u5178\u75be\u5288\u8fc7\u53bb\u3002\u4e01\u5178\u53cc\u81c2\u4e00\u4e3e\uff0c\u81c2\u95f4\u7684\u94c1\u94fe\u5c06\u5355\u5200\u67b6\u5f00\uff0c\u4fbf\u5728\u540c\u65f6\uff0c\u819d\u76d6\u731b\u5730\u4e0a\u633a\uff0c\u649e\u5728\u77ee\u5927\u6c49\u8eab\u4e0a\u3002\u90a3\u4eba\u731b\u55b7\u9c9c\u8840\uff0c\u5012\u6bd9\u4e8e\u5730\u3002 \u3000\u3000\u4e01\u5178\u970e\u606f\u95f4\u7a7a\u624b\u8fde\u6bd9\u4e8c\u4eba\uff0c\u72c4\u4e91\u4e0d\u7531\u5f97\u77a7\u5f97\u5446\u4e86\u3002\u4ed6\u6b66\u529f\u867d\u5931\uff0c\u773c\u5149\u5374\u5728\uff0c\u77e5\u9053\u81ea\u5df1\u7eb5\u7136\u529f\u529b\u5982\u65e7\uff0c\u957f\u5251\u5728\u624b\uff0c\u4e5f\u672a\u5fc5\u53ca\u5f97\u4e0a\u8fd9\u77ee\u6c49\u5b50\uff0c\u53e6\u5916\u90a3\u540d\u6c49\u5b50\u672a\u53ca\u51fa\u624b\uff0c\u4fbf\u5df2\u8eab\u4ea1\uff0c\u529f\u592b\u5982\u4f55\u867d\u77a7\u4e0d\u51fa\u7aef\u502a\uff0c\u4f46\u65e2\u4e0e\u90a3\u77ee\u6c49\u8054\u624b\uff0c\u60f3\u6765\u4e5f\u5fc5\u4e0d\u5f31\u3002\u4e01\u5178\u7435\u7436\u9aa8\u4e2d\u4ecd\u662f\u7a7f\u7740\u94c1\u94fe\uff0c\u7adf\u7136\u5728\u4e3e\u624b\u6295\u8db3\u4e4b\u95f4\u4fbf\u8fde\u6740\u4e24\u540d\u597d\u624b\uff0c\u5b9e\u4ee4\u4ed6\u60ca\u4f69\u65e0\u5df2\u3002 \u3000\u3000\u4e01\u5178\u5c06\u4e24\u5177\u5c38\u9996\u4ece\u94c1\u6805\u95f4\u63b7\u4e86\u51fa\u53bb\uff0c\u501a\u5899\u4fbf\u7761\u3002\u6b64\u523b\u94c1\u6805\u5df2\u65ad\uff0c\u4ed6\u4e8c\u4eba\u82e5\u8981\u8d8a\u72f1\uff0c\u5b9e\u662f\u5927\u6709\u673a\u4f1a\uff0c\u4f46\u4e01\u5178\u65e2\u4e00\u8a00\u4e0d\u53d1\uff0c\u72c4\u4e91\u4e5f\u4e0d\u89c9\u5f97\u5916\u9762\u7684\u4e16\u754c\u6bd4\u72f1\u4e2d\u66f4\u597d\u3002 \u3000\u3000\u7b2c\u4e8c\u65e5\u65e9\u6668\uff0c\u72f1\u5352\u8fdb\u6765\u89c1\u4e86\u4e24\u5177\u5c38\u4f53\uff0c\u767b\u65f6\u5927\u60ca\u5c0f\u602a\u7684\u5435\u56b7\u8d77\u6765\u3002\u4e01\u5178\u6012\u76ee\u76f8\u5411\uff0c\u72c4\u4e91\u542c\u800c\u4e0d\u95fb\u3002\u90a3\u72f1\u5352\u9664\u4e86\u5c06\u5c38\u9996\u642c\u53bb\uff0c\u4e00\u70b9\u4e5f\u95ee\u4e0d\u51fa\u751a\u4e48\u7f18\u6545\u6765\u3002 \u3000\u3000\u53c8\u8fc7\u4e24\u65e5\uff0c\u72c4\u4e91\u534a\u591c\u91cc\u53c8\u88ab\u5f02\u58f0\u60ca\u9192\u3002\u6726\u80e7\u4e4b\u4e2d\uff0c\u53ea\u89c1\u4e01\u5178\u53cc\u81c2\u5e73\u4e3e\uff0c\u6b63\u548c\u4e00\u540d\u9053\u4eba\u56db\u638c\u76f8\u62b5\u3002\u4e24\u4eba\u7ad9\u7740\u52a8\u4e5f\u4e0d\u52a8\u3002 \u3000\u3000\u8fd9\u9053\u4eba\u4f55\u65f6\u8fdb\u6765\uff0c\u5982\u4f55\u548c\u4e01\u5178\u6bd4\u62da\u5185\u529b\uff0c\u72c4\u4e91\u7adf\u7136\u534a\u70b9\u4e0d\u77e5\u3002 \u3000\u3000\u4ed6\u66fe\u542c\u5e08\u7236\u8bf4\u8fc7\uff0c\u6bd4\u6b66\u89d2\u6597\u4e4b\u4e2d\uff0c\u4ee5\u6bd4\u62da\u5185\u529b\u6700\u4e3a\u51f6\u9669\uff0c\u4e0d\u4f46\u6beb\u65e0\u65cb\u56de\u95ea\u907f\u7684\u4f59\u5730\uff0c\u800c\u4e14\u5f80\u5f80\u662f\u5fc5\u5206\u751f\u6b7b\uff0c\u8bf4\u4e0d\u4e0a\u751a\u4e48\u70b9\u5230\u4e3a\u6b62\u3002 \u3000\u3000\u661f\u6708\u5fae\u5149\u4e4b\u4e0b\uff0c\u4f46\u89c1\u90a3\u9053\u4eba\u6781\u7f13\u6781\u6162\u7684\u5411\u524d\u8de8\u4e86\u4e00\u6b65\uff0c\u4e01\u5178\u4e5f\u6162\u6162\u7684\u9000\u4e86\u4e00\u6b65\u3002\u8fc7\u4e86\u597d\u4e00\u4f1a\uff0c\u90a3\u9053\u4eba\u53c8\u8fc8\u51fa\u4e00\u6b65\uff0c\u4e01\u5178\u8ddf\u7740\u9000\u4e86\u4e00\u6b65\u3002 \u3000\u3000\u72c4\u4e91\u89c1\u90a3\u9053\u4eba\u6b65\u6b65\u8fdb\u903c\uff0c\u663e\u7136\u9887\u5360\u4e0a\u98ce\uff0c\u7126\u6025\u8d77\u6765\uff0c\u7a81\u7136\u62a2\u6b65\u4e0a\u524d\uff0c\u4e3e\u8d77\u624b\u4e0a\u94c1\u94d0\uff0c\u5f80\u90a3\u9053\u4eba\u5934\u9876\u51fb\u4e86\u4e0b\u53bb\u3002\u94c1\u94d0\u521a\u78b0\u5230\u9053\u4eba\u7684\u9876\u95e8\uff0c\u84e6\u5730\u91cc\u4e0d\u77e5\u4ece\u4f55\u5904\u6d8c\u6765\u4e00\u80a1\u6697\u52b2\uff0c\u731b\u529b\u5728\u4ed6\u8eab\u4e0a\u4e00\u63a8\u3002\u4ed6\u7ad9\u7acb\u4e0d\u5b9a\uff0c\u76f4\u6454\u4e86\u51fa\u53bb\uff0c\u7830\u7684\u4e00\u58f0\uff0c\u91cd\u91cd\u5728\u5899\u4e0a\u4e00\u649e\uff0c\u4e00\u5c41\u80a1\u5750\u5c06\u4e0b\u6765\uff0c\u4f38\u624b\u6491\u5730\u6b32\u8d77\uff0c\u9ed1\u6697\u4e2d\u5374\u6491\u5728\u4e00\u53ea\u74e6\u7897\u8fb9\u4e0a\uff0c\u5580\u7684\u4e00\u54cd\uff0c\u74e6\u7897\u88ab\u4ed6\u6309\u7834\u4e86\u4e00\u8fb9\uff0c\u4f46\u89c9\u6ee1\u624b\u662f\u6c34\u3002\u4ed6\u66f4\u4e0d\u591a\u60f3\uff0c\u6293\u8d77\u74e6\u7897\uff0c\u5c06\u534a\u7897\u51b7\u6c34\u5f84\u5f80\u90a3\u9053\u4eba\u540e\u8111\u6cfc\u53bb\u3002 \u3000\u3000\u4e01\u5178\u8fd9\u65f6\u7684\u5185\u529b\u5176\u5b9e\u65e9\u5df2\u8fdc\u5728\u90a3\u9053\u4eba\u4e4b\u4e0a\uff0c\u53ea\u662f\u8981\u8bd5\u8bd5\u81ea\u5df1\u65b0\u7ec3\u6210\u7684\u795e\u529f\uff0c\u6536\u53d1\u4e4b\u9645\u5230\u5e95\u6709\u4f55\u7b49\u5a01\u529b\uff0c\u624d\u5c06\u4ed6\u4f5c\u4e3a\u8bd5\u62db\u7684\u9776\u5b50\uff0c\u90a3\u9053\u4eba\u672c\u5df2\u7d2f\u5f97\u7b4b\u75b2\u529b\u7aed\uff0c\u6cb9\u5c3d\u706f\u67af\uff0c\u8fd9\u534a\u7897\u51b7\u6c34\u6cfc\u5230\u540e\u8111\uff0c\u4e00\u60ca\u4e4b\u4e0b\uff0c\u4f46\u89c9\u5bf9\u65b9\u7684\u5185\u52b2\u6c79\u6d8c\u800c\u81f3\uff0c\u683c\u683c\u683c\u683c\u7206\u58f0\u4e0d\u7edd\uff0c\u808b\u9aa8\u3001\u81c2\u9aa8\u3001\u817f\u9aa8\u5bf8\u5bf8\u65ad\u6298\u3002\u4ed6\u773c\u671b\u4e01\u5178\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u5df2\u7ec3\u6210\u4e86\u2018\u795e\u7167\u7ecf\u2019\u7684\u2026\u2026\u5927\u6cd5\u2026\u2026\u90a3\u2026\u2026\u662f\u2026\u2026\u5929\u4e0b\u2026\u2026\u5929\u4e0b\u2026\u2026\u65e0\u654c\u624b\u2026\u2026\u201d\u6162\u6162\u7f29\u6210\u4e00\u4e2a\u8089\u56e2\uff0c\u6c14\u7edd\u800c\u6b7b\u3002 \u3000\u3000\u72c4\u4e91\u5fc3\u4e2d\u6026\u6026\u4e71\u8df3\uff0c\u9053\uff1a\u201c\u4e01\u5927\u54e5\uff0c\u4f60\u8fd9\u2018\u795e\u7167\u7ecf\u2019\u7684\u5927\u6cd5\u539f\u6765\u2026\u2026\u539f\u6765\u8fd9\u7b49\u5389\u5bb3\u3002\u5f53\u771f\u662f\u5929\u4e0b\u65e0\u654c\u624b\u4e48\uff1f\u201d \u3000\u3000\u4e01\u5178\u8138\u8272\u51dd\u91cd\uff0c\u9053\uff1a\u201c\u5355\u6253\u72ec\u6597\uff0c\u9887\u8db3\u4ee5\u79f0\u96c4\u6c5f\u6e56\uff0c\u4f46\u654c\u4eba\u82e5\u662f\u7fa4\u8d77\u800c\u653b\uff0c\u4ecd\u6015\u5be1\u4e0d\u654c\u4f17\u3002\u8fd9\u67ad\u9053\u4eba\u53d7\u6211\u5185\u529b\u538b\u51fb\u4e4b\u540e\uff0c\u5c1a\u80fd\u5f00\u53e3\u8bf4\u8bdd\u3002\u663e\u7136\u6211\u529f\u529b\u672a\u81f3\u7089\u706b\u7eaf\u9752\u7684\u5883\u5730\u3002\u4e09\u65e5\u4e4b\u5185\uff0c\u5fc5\u6709\u771f\u6b63\u52b2\u654c\u5230\u6765\u3002\u72c4\u5144\u5f1f\uff0c\u4f60\u80fd\u52a9\u6211\u4e00\u81c2\u4e4b\u529b\u5417\uff1f\u201d \u3000\u3000\u72c4\u4e91\u8c6a\u5174\u52c3\u53d1\uff0c\u8bf4\u9053\uff1a\u201c\u4f46\u51ed\u5927\u54e5\u5429\u5490\uff0c\u53ea\u662f\u6211\u2026\u2026\u6211\u6b66\u529f\u5168\u5931\uff0c\u5c31\u7b97\u4e0d\u5931\uff0c\u90a3\u4e5f\u662f\u592a\u8fc7\u4f4e\u5fae\u3002\u201d\u4e01\u5178\u5fae\u5fae\u4e00\u7b11\uff0c\u4ece\u8349\u57ab\u4e0b\u62bd\u51fa\u4e00\u67c4\u5355\u5200\u6765\uff0c\u4fbf\u662f\u65e5\u524d\u90a3\u4e24\u540d\u5927\u6c49\u6240\u9057\u4e0b\u7684\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u5c06\u6211\u7684\u80e1\u5b50\u5243\u53bb\uff0c\u54b1\u4eec\u4f7f\u4e00\u70b9\u8be1\u8ba1\u3002\u201d \u3000\u3000\u72c4\u4e91\u63a5\u8fc7\u5355\u5200\uff0c\u4fbf\u53bb\u5243\u4ed6\u7684\u6ee1\u816e\u866c\u9aef\uff0c\u90a3\u67c4\u5355\u5200\u6781\u4e3a\u950b\u9510\uff0c\u8d34\u8089\u5243\u53bb\uff0c\u4e01\u5178\u816e\u4e0a\u866c\u9aef\u7eb7\u7eb7\u800c\u843d\u3002\u4e01\u5178\u5c06\u5243\u4e0b\u6765\u7684\u4e00\u6839\u6839\u80e1\u5b50\u90fd\u653e\u5728\u624b\u638c\u4e4b\u4e2d\u3002 \u3000\u3000\u72c4\u4e91\u7b11\u9053\uff1a\u201c\u4f60\u820d\u4e0d\u5f97\u8fd9\u4e9b\u8ddf\u968f\u4f60\u591a\u5e74\u7684\u80e1\u5b50\u4e48\uff1f\u201d\u4e01\u5178\u9053\uff1a\u201c\u90a3\u5012\u4e0d\u662f\uff0c\u6211\u8981\u4f60\u626e\u4e00\u626e\u6211\u3002\u201d\u72c4\u4e91\u5947\u9053\uff1a\u201c\u6211\u626e\u4f60\uff1f\u201d\u4e01\u5178\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4e09\u65e5\u4e4b\u5185\uff0c\u5c06\u6709\u52b2\u654c\u5230\u6765\u3002\u90a3\u4e94\u4e2a\u4eba\u5355\u6253\u72ec\u6597\u90fd\u4e0d\u662f\u6211\u5bf9\u624b\u3002\u4f46\u4e00\u9f50\u51fa\u624b\uff0c\u90a3\u5c31\u5341\u5206\u5389\u5bb3\u3002\u6211\u8981\u4ed6\u4eec\u5c06\u4f60\u9519\u8ba4\u4e3a\u6211\u3002\u5168\u795e\u8d2f\u6ce8\u7684\u60f3\u5bf9\u4ed8\u4f60\u65f6\uff0c\u6211\u5c31\u51fa\u5176\u4e0d\u610f\u7684\u4ece\u65c1\u88ad\u51fb\uff0c\u653b\u4ed6\u4eec\u4e2a\u63aa\u624b\u4e0d\u53ca\u3002\u201d \u3000\u3000\u72c4\u4e91\u55eb\u5685\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u8fd9\u4e2a\u2026\u2026\u53ea\u6015\u6709\u70b9\u2026\u2026\u4e0d\u591f\u5149\u660e\u6b63\u5927\u3002\u201d\u4e01\u5178\u54c8\u54c8\u5927\u7b11\uff0c\u9053\uff1a\u201c\u5149\u660e\u6b63\u5927\uff0c\u5149\u660e\u6b63\u5927\uff01\u6c5f\u6e56\u4e0a\u4eba\u5fc3\u591a\u5c11\u9669\u8bc8\uff0c\u4e2a\u4e2a\u90fd\u4ee5\u9b3c\u872e\u4f0e\u4fe9\u5bf9\u4f60\uff0c\u4f60\u5f85\u4eba\u5149\u660e\u6b63\u5927\uff0c\u90a3\u4e0d\u662f\u81ea\u5bfb\u6b7b\u8def\u4e48\uff1f\u201d\u72c4\u4e91\u9053\uff1a\u201c\u8bdd\u867d\u5982\u6b64\uff0c\u4e0d\u8fc7\u2026\u2026\u4e0d\u8fc7\u2026\u2026\u201d \u3000\u3000\u4e01\u5178\u9053\uff1a\u201c\u6211\u95ee\u4f60\uff1a\u5f53\u521d\u8fdb\u7262\u4e4b\u65f6\uff0c\u4f60\u5927\u53eb\u51a4\u6789\u3002\u6211\u4fe1\u5f97\u8fc7\u4f60\u5b9a\u7136\u6e05\u767d\u65e0\u8f9c\u3002\u53ef\u662f\u600e\u4f1a\u5728\u7262\u91cc\u4e00\u5173\u4e09\u5e74\u591a\uff0c\u59cb\u7ec8\u6ca1\u6cd5\u6d17\u96ea\uff1f\u201d\u72c4\u4e91\u9053\uff1a\u201c\u55ef\uff0c\u8fd9\u4e2a\uff0c\u6211\u5c31\u662f\u96be\u4ee5\u660e\u767d\u3002\u201d\u4e01\u5178\u5fae\u7b11\u9053\uff1a\u201c\u662f\u8c01\u9001\u4e86\u4f60\u8fdb\u7262\u6765\uff0c\u81ea\u7136\u662f\u8c01\u4f7f\u4e86\u624b\u811a\uff0c\u4e00\u76f4\u4f7f\u4f60\u4e0d\u80fd\u51fa\u53bb\u3002\u201d\u72c4\u4e91\u9053\uff1a\u201c\u6211\u603b\u662f\u60f3\u4e0d\u901a\uff0c\u90a3\u4e07\u9707\u5c71\u7684\u5c0f\u59be\u6843\u7ea2\u548c\u6211\u7d20\u4e0d\u76f8\u8bc6\uff0c\u65e0\u51a4\u65e0\u4ec7\uff0c\u4e3a\u751a\u4e48\u8981\u9677\u5bb3\u6211\uff0c\u4f7f\u6211\u8eab\u8d25\u540d\u88c2\uff0c\u53d7\u5c3d\u8fd9\u8bb8\u591a\u82e6\u695a\uff1f\u201d\u4e01\u5178\u95ee\u9053\uff1a\u201c\u4ed6\u4eec\u600e\u4e48\u9677\u5bb3\u4e8e\u4f60\uff0c\u8bf4\u7ed9\u6211\u542c\u542c\u3002\u201d \u3000\u3000\u72c4\u4e91\u4e00\u9762\u7ed9\u4ed6\u5243\u987b\uff0c\u4e00\u9762\u5c06\u5982\u4f55\u6765\u8346\u5dde\u62dc\u5bff\u3001\u5982\u4f55\u6253\u9000\u5927\u76d7\u5415\u901a\u3001\u5982\u4f55\u4e0e\u4e07\u95e8\u516b\u5f1f\u5b50\u6bd4\u5251\u6253\u67b6\u3001\u5982\u4f55\u5e08\u7236\u523a\u4f24\u5e08\u4f2f\u800c\u9003\u8d70\u3001\u5982\u4f55\u6709\u4eba\u5411\u4e07\u9707\u5c71\u7684\u59be\u4f8d\u975e\u793c\u3001\u81ea\u5df1\u51fa\u624b\u76f8\u6551\u53cd\u88ab\u9677\u5bb3\u7b49\u60c5\u4e00\u4e00\u8bf4\u4e86\uff0c\u53ea\u662f\u90a3\u8001\u4e10\u591c\u4e2d\u6559\u5251\u4e00\u8282\uff0c\u5374\u7565\u53bb\u4e86\u4e0d\u8bf4\u3002\u53ea\u56e0\u4ed6\u66fe\u5411\u8001\u4e10\u7acb\u8a93\uff0c\u51b3\u4e0d\u6cc4\u9732\u6b64\u4e8b\uff0c\u518d\u8005\u4e5f\u89c9\u6b64\u4e8b\u4e43\u662f\u65c1\u679d\u672b\u8282\uff0c\u65e0\u751a\u8981\u7d27\u3002 \u3000\u3000\u4ed6\u4ece\u5934\u81f3\u5c3e\u7684\u8bf4\u5b8c\uff0c\u4e01\u5178\u8138\u4e0a\u7684\u80e1\u5b50\u4e5f\u5dee\u4e0d\u591a\u5243\u5b8c\u4e86\u3002\u72c4\u4e91\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u4e01\u5927\u54e5\uff0c\u6211\u53d7\u8fd9\u6cfc\u5929\u7684\u51a4\u5c48\uff0c\u90a3\u4e0d\u662f\u597d\u6ca1\u6765\u7531\u4e48\uff1f\u90a3\u5b9a\u662f\u4ed6\u4eec\u6068\u6211\u5e08\u7236\u6740\u4e86\u4e07\u5e08\u4f2f\u3002\u53ef\u662f\u4e07\u5e08\u4f2f\u53ea\u662f\u53d7\u4e86\u70b9\u4f24\uff0c\u5e76\u6ca1\u6709\u6b7b\uff0c\u5c06\u6211\u5173\u4e86\u8fd9\u8bb8\u591a\u5e74\uff0c\u4e5f\u8be5\u653e\u6211\u51fa\u53bb\u4e86\u3002\u8981\u8bf4\u5c06\u6211\u5fd8\u4e86\uff0c\u5374\u53c8\u4e0d\u5bf9\u3002\u90a3\u59d3\u6c88\u7684\u5c0f\u5e08\u5f1f\u4e0d\u662f\u63a2\u6211\u6765\u7740\u5417\uff1f\u201d \u3000\u3000\u4e01\u5178\u4fa7\u8fc7\u5934\uff0c\u5411\u4ed6\u8fd9\u8fb9\u77a7\u77a7\uff0c\u53c8\u5411\u4ed6\u90a3\u8fb9\u77a7\u77a7\uff0c\u53ea\u662f\u563f\u563f\u51b7\u7b11\u3002 \u3000\u3000\u72c4\u4e91\u6478\u4e0d\u7740\u5934\u8111\uff0c\u95ee\u9053\uff1a\u201c\u4e01\u5927\u54e5\uff0c\u6211\u8bf4\u5f97\u751a\u4e48\u4e0d\u5bf9\u4e86\uff1f\u201d \u3000\u3000\u4e01\u5178\u51b7\u7b11\u9053\uff1a\u201c\u5bf9\uff0c\u5bf9\uff0c\u5b8c\u5168\u5bf9\uff0c\u90a3\u53c8\u6709\u751a\u4e48\u5730\u65b9\u4e0d\u5bf9\u5934\u7684\uff1f \u3000\u3000\u5018\u82e5\u4e0d\u662f\u8fd9\u6837\uff0c\u90a3\u624d\u4e0d\u5bf9\u5934\u4e86\u3002\u201d\u72c4\u4e91\u5947\u9053\uff1a\u201c\u751a\u2026\u2026\u751a\u4e48\uff1f\u201d \u3000\u3000\u4e01\u5178\u9053\uff1a\u201c\u54df\uff01\u4f60\u81ea\u5df1\u60f3\u60f3\u3002\u6709\u4e00\u4e2a\u50bb\u5c0f\u5b50\uff0c\u5e26\u4e86\u4e00\u4e2a\u7f8e\u8c8c\u599e\u513f\u5230\u6211\u5bb6\u6765\u3002\u6211\u89c1\u5230\u8fd9\u599e\u513f\u4fbf\u52a8\u4e86\u5fc3\uff0c\u53ef\u662f\u8fd9\u599e\u513f\u5bf9\u90a3\u50bb\u5c0f\u5b50\u5b9e\u5728\u4e0d\u9519\u3002\u6211\u60f3\u5360\u8fd9\u599e\u513f\uff0c\u4fbf\u975e\u5f97\u9664\u53bb\u8fd9\u50bb\u5c0f\u5b50\u4e0d\u53ef\uff0c\u4f60\u60f3\u5f97\u4f7f\u751a\u4e48\u6cd5\u5b50\u624d\u597d\uff1f\u201d \u3000\u3000\u72c4\u4e91\u5fc3\u4e2d\u6697\u6697\u611f\u5230\u4e00\u9635\u51c9\u610f\uff0c\u968f\u53e3\u9053\uff1a\u201c\u4f7f\u751a\u4e48\u6cd5\u5b50\u624d\u597d\uff1f\u201d \u3000\u3000\u4e01\u5178\u9053\uff1a\u201c\u82e5\u662f\u7528\u6bd2\u836f\u6216\u662f\u52a8\u5200\u5b50\u6740\u4e86\u90a3\u50bb\u5c0f\u5b50\uff0c\u8eab\u4e0a\u62c5\u4e86\u4eba\u547d\uff0c\u603b\u662f\u591a\u4e00\u5c42\u5e72\u7cfb\uff0c\u4f55\u51b5\u90a3\u7f8e\u8c8c\u599e\u513f\u8bf4\u4e0d\u5b9a\u662f\u4e2a\u70c8\u6027\u5973\u5b50\uff0c\u4e0d\u514d\u8981\u5bfb\u6b7b\u89c5\u6d3b\uff0c\u8bf4\u4e0d\u5b9a\u66f4\u8981\u7ed9\u90a3\u50bb\u5c0f\u5b50\u62a5\u4ec7\uff0c\u90a3\u4e0d\u662f\u7cdf\u4e86\uff1f\u4f9d\u6211\u8bf4\u554a\uff0c\u8fd8\u662f\u5c06\u90a3\u50bb\u5c0f\u5b50\u9001\u5230\u5b98\u91cc\uff0c\u5173\u5c06\u8d77\u6765\u7684\u597d\u3002\u8981\u4ee4\u90a3\u599e\u513f\u6b7b\u5fc3\u584c\u5730\u7684\u8ddf\u6211\uff0c\u987b\u5f97\u4f7f\u5979\u5fc3\u4e2d\u607c\u6068\u8fd9\u50bb\u5c0f\u5b50\uff0c\u90a3\u600e\u4e48\u529e\uff1f\u7b2c\u4e00\u3001\u987b\u5f97\u4f7f\u90a3\u5c0f\u5b50\u79fb\u60c5\u522b\u604b\uff1b\u7b2c\u4e8c\u3001\u987b\u5f97\u4ee4\u90a3\u5c0f\u5b50\u663e\u5f97\u662f\u81ea\u5df1\u6487\u5f00\u8fd9\u4e2a\u599e\u513f\uff1b\u7b2c\u4e09\u3001\u6700\u597d\u662f\u8ba9\u90a3\u5c0f\u5b50\u5e72\u4e9b\u89c1\u4e0d\u5f97\u4eba\u7684\u65e0\u803b\u52fe\u5f53\uff0c\u8ba9\u90a3\u599e\u513f\u4e00\u60f3\u8d77\u6765\u4fbf\u6076\u5fc3\u3002\u201d \u3000\u3000\u72c4\u4e91\u5168\u8eab\u53d1\u98a4\uff0c\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u8bf4\u8fd9\u4e00\u5207\uff0c\u5168\u662f\u90a3\u59d3\u4e07\u7684\u2026\u2026\u662f\u4e07\u572d\u5b89\u6392\u7684\u4e86\uff1f\u201d \u3000\u3000\u4e01\u5178\u5fae\u7b11\u9053\uff1a\u201c\u6211\u6ca1\u4eb2\u773c\u77a7\u89c1\uff0c\u600e\u4e48\u77e5\u9053\uff1f\u4f60\u5e08\u59b9\u751f\u5f97\u5f88\u4fca\uff0c\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u72c4\u4e91\u8111\u4e2d\u4e00\u7247\u8ff7\u60d8\uff0c\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u4e01\u5178\u9053\uff1a\u201c\u55ef\uff0c\u4e3a\u4e86\u8ba8\u597d\u90a3\u4e2a\u59d1\u5a18\uff0c\u6211\u81ea\u7136\u8981\u5fd9\u5fd9\u788c\u788c\u54ea\uff0c\u4e00\u7b14\u7b14\u767d\u82b1\u82b1\u7684\u94f6\u5b50\u62ff\u5c06\u51fa\u6765\uff0c\u9001\u5230\u8859\u95e8\u91cc\u6765\u6253\u70b9\uff0c\u8bf4\u662f\u5728\u8bbe\u6cd5\u6551\u90a3\u4e2a\u5c0f\u5b50\u3002\u6700\u597d\u662f\u8ddf\u90a3\u59d1\u5a18\u4e00\u8d77\u6765\u9001\u94f6\u5b50\uff0c\u90a3\u59d1\u5a18\u751a\u4e48\u90fd\u4eb2\u773c\u77a7\u89c1\u4e86\uff0c\u5fc3\u4e2d\u81ea\u662f\u597d\u751f\u611f\u6fc0\u3002\u8fd9\u4e9b\u94f6\u5b50\u786e\u662f\u9001\u4e86\u7ed9\u5e9c\u53f0\u5927\u4eba\uff0c\u77e5\u53bf\u5927\u4eba\uff0c\u9001\u4e86\u7ed9\u8859\u95e8\u91cc\u7684\u5e08\u7237\uff0c\u90a3\u5012\u4e00\u70b9\u4e0d\u9519\u3002\u201d \u3000\u3000\u72c4\u4e91\u9053\uff1a\u201c\u4ed6\u4f7f\u4e86\u8fd9\u8bb8\u591a\u94f6\u5b50\uff0c\u603b\u8be5\u6709\u70b9\u529f\u6548\u7f62\uff1f\u201d\u4e01\u5178\u9053\uff1a\u201c\u81ea\u7136\u6709\u554a\uff0c\u6709\u94b1\u80fd\u4f7f\u9b3c\u63a8\u78e8\uff0c\u600e\u4e48\u4f1a\u6ca1\u529f\u6548\uff1f\u201d\u72c4\u4e91\u9053\uff1a\u201c\u90a3\u600e\u2026\u2026\u600e\u4e48\u4e00\u76f4\u5173\u7740\u6211\uff0c\u4e0d\u653e\u6211\u51fa\u53bb\uff1f\u201d \u3000\u3000\u4e01\u5178\u7b11\u9053\uff1a\u201c\u4f60\u72af\u4e86\u751a\u4e48\u7f6a\uff1f\u4ed6\u4eec\u9677\u5bb3\u4f60\u7684\u7f6a\u540d\uff0c\u4e5f\u4e0d\u8fc7\u662f\u5f3a\u5978\u672a\u9042\uff0c\u5077\u76d7\u4e00\u4e9b\u94b1\u8d22\u3002\u65e2\u4e0d\u662f\u72af\u4e0a\u4f5c\u4e71\uff0c\u53c8\u4e0d\u662f\u6740\u4eba\u653e\u706b\uff0c\u90a3\u53c8\u662f\u751a\u4e48\u91cd\u7f6a\u4e86\uff1f\u90a3\u4e5f\u7528\u4e0d\u7740\u7a7f\u4e86\u4f60\u7684\u7435\u7436\u9aa8\uff0c\u5c06\u4f60\u5728\u6b7b\u56da\u7262\u91cc\u5173\u4e00\u8f88\u5b50\u554a\u3002\u8fd9\u4fbf\u662f\u90a3\u8bb8\u591a\u767d\u82b1\u82b1\u94f6\u5b50\u7684\u529f\u6548\u4e86\u3002\u5999\u5f97\u5f88\uff0c\u8fd9\u6761\u8ba1\u7b56\u5929\u8863\u65e0\u7f1d\u3002\u8fd9\u4e2a\u59d1\u5a18\u4f4f\u5728\u6211\u5bb6\u91cc\uff0c\u5979\u5fc3\u4e2d\u5bf9\u90a3\u50bb\u5c0f\u5b50\u5012\u8fd8\u662f\u5ff5\u5ff5\u4e0d\u5fd8\u7684\uff0c\u53ef\u662f\u7b49\u4e86\u4e00\u5e74\u53c8\u4e00\u5e74\uff0c\u96be\u9053\u80fd\u4e00\u8f88\u5b50\u4e0d\u5ac1\u4eba\u5417\uff1f\u201d \u3000\u3000\u72c4\u4e91\u63d0\u8d77\u5355\u5200\uff0c\u5f53\u7684\u4e00\u58f0\uff0c\u780d\u5728\u5730\u4e0b\uff0c\u8bf4\u9053\uff1a\u201c\u4e01\u5927\u54e5\uff0c\u539f\u6765\u6211\u4e00\u76f4\u4e0d\u80fd\u653e\u51fa\u53bb\uff0c\u90fd\u662f\u4e07\u572d\u4f7f\u4e86\u94f6\u5b50\u7684\u7f18\u6545\u3002\u201d \u3000\u3000\u4e01\u5178\u4e0d\u7b54\uff0c\u4ef0\u8d77\u4e86\u5934\u6c89\u541f\uff0c\u5ffd\u7136\u76b1\u8d77\u7709\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u5bf9\uff0c\u8fd9\u6761\u8ba1\u7b56\u4e2d\u6709\u4e00\u4e2a\u8001\u5927\u7834\u7efd\uff0c\u5927\u5927\u7684\u4e0d\u5bf9\u3002\u201d \u3000\u3000\u72c4\u4e91\u6012\u9053\uff1a\u201c\u8fd8\u6709\u751a\u4e48\u7834\u7efd\uff1f\u6211\u5e08\u59b9\u7ec8\u4e8e\u5ac1\u7ed9\u4ed6\u5566\u3002\u82e5\u4e0d\u662f\u8499\u4f60\u76f8\u6551\uff0c\u6211\u81ea\u7f22\u8eab\u6b7b\uff0c\u90a3\u4e0d\u662f\u4e07\u4e8b\u987a\u9042\uff0c\u4e00\u5207\u90fd\u79f0\u4e86\u4ed6\u7684\u5fc3\uff1f\u201d \u3000\u3000\u4e01\u5178\u5728\u72f1\u5ba4\u4e2d\u8d70\u6765\u8d70\u53bb\uff0c\u4e0d\u4f4f\u6447\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u5176\u4e2d\u6709\u4e00\u4e2a\u5927\u5927\u7684\u7834\u7efd\uff0c\u4ed6\u4eec\u5982\u6b64\u5de5\u4e8e\u5fc3\u8ba1\uff0c\u600e\u80fd\u89c1\u4e0d\u5230\uff1f\u201d\u72c4\u4e91\u9053\uff1a\u201c\u4f60\u8bf4\u6709\u751a\u4e48\u7834\u7efd\uff1f\u201d \u3000\u3000\u4e01\u5178\u9053\uff1a\u201c\u4f60\u5e08\u7236\u554a\u3002\u4f60\u5e08\u7236\u4f24\u4e86\u4f60\u5e08\u4f2f\u540e\uff0c\u9003\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u8346\u5dde\u4e94\u4e91\u624b\u4e07\u9707\u5c71\u5728\u6b66\u6797\u4e2d\u5927\u5927\u6709\u540d\uff0c\u4ed6\u53d7\u4f24\u4e0d\u6b7b\u7684\u8baf\u606f\u6ca1\u51e0\u5929\u4fbf\u4f20\u4e86\u51fa\u53bb\uff0c\u4f60\u5e08\u7236\u5c31\u7b97\u6ca1\u8138\u518d\u89c1\u5e08\u5144\uff0c\u96be\u9053\u5c31\u4e0d\u6d3e\u4eba\u6765\u63a5\u4f60\u5e08\u59b9\u56de\u5bb6\uff1f\u4f60\u5e08\u59b9\u8fd9\u4e00\u56de\u5bb6\uff0c\u90a3\u4e07\u572d\u82e6\u5fc3\u7b79\u5212\u7684\u9634\u8c0b\u6bd2\u8ba1\uff0c\u5c82\u4e0d\u662f\u5168\u76d8\u843d\u4e86\u7a7a\uff1f\u201d \u3000\u3000\u72c4\u4e91\u4f38\u624b\u8fde\u8fde\u62cd\u51fb\u5927\u817f\uff0c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4e0d\u9519\uff01\u201d\u4ed6\u624b\u4e0a\u5e26\u7740\u624b\u94d0\uff0c\u8fd9\u4e00\u62cd\u817f\uff0c\u94c1\u94fe\u5b50\u767b\u65f6\u5f53\u5f53\u7684\u76f4\u54cd\uff0c\u4ed6\u89c1\u4e01\u5178\u5f62\u8c8c\u7c97\u9c81\uff0c\u5fc3\u601d\u7adf\u6041\u5730\u5468\u5bc6\uff0c\u4e0d\u7981\u6781\u662f\u94a6\u4f69\u3002 \u3000\u3000\u4e01\u5178\u4fa7\u8fc7\u4e86\u5934\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u4f60\u5e08\u7236\u4e3a\u751a\u4e48\u4e0d\u6765\u63a5\u5973\u513f\u56de\u53bb\uff0c\u8fd9\u5176\u4e2d\u5b9a\u662f\u5927\u6709\u8df7\u8e4a\u3002\u4e07\u572d\u4ed6\u4eec\u4e8b\u5148\u4e00\u5b9a\u5df2\u6599\u5230\u4e86\u8fd9\u4e00\u8282\uff0c\u5426\u5219\u8fd9\u8ba1\u7b56\u4e0d\u4f1a\u5982\u6b64\u5b89\u6392\u3002\u8fd9\u4e2d\u95f4\u7684\u53e4\u602a\uff0c\u4e00\u65f6\u4e4b\u95f4\u6211\u5b9e\u662f\u731c\u60f3\u4e0d\u900f\u3002\u201d \u3000\u3000\u72c4\u4e91\u76f4\u5230\u4eca\u65e5\uff0c\u624d\u4ece\u5934\u81f3\u5c3e\u7684\u660e\u767d\u4e86\u81ea\u5df1\u9677\u8eab\u7262\u72f1\u7684\u5173\u952e\u3002\u4ed6\u4e0d\u65ad\u4f38\u624b\u51fb\u6253\u81ea\u5df1\u5934\u9876\uff0c\u5927\u9a82\u81ea\u5df1\u771f\u662f\u8822\u624d\uff0c\u522b\u4eba\u4e00\u60f3\u5c31\u901a\u7684\u4e8b\uff0c\u81ea\u5df1\u4e09\u5e74\u591a\u6765\u59cb\u7ec8\u83ab\u540d\u5947\u5999\u3002 \u3000\u3000\u4ed6\u81ea\u6028\u81ea\u827e\u4e86\u4e00\u4f1a\uff0c\u89c1\u4e01\u5178\u5140\u81ea\u82e6\u82e6\u601d\u7d22\uff0c\u4fbf\u9053\uff1a\u201c\u4e01\u5927\u54e5\uff0c\u4f60\u4e0d\u7528\u591a\u60f3\u5566\u3002\u6211\u5e08\u7236\u662f\u4e2a\u4e61\u4e0b\u8001\u5b9e\u4eba\uff0c\u60f3\u662f\u4ed6\u4f24\u4e86\u4e07\u5e08\u4f2f\uff0c\u4e00\u5413\u4e4b\u4e0b\uff0c\u8fdc\u8fdc\u9003\u5230\u4e86\u86ee\u8352\u8fb9\u5730\uff0c\u518d\u4e5f\u542c\u4e0d\u5230\u6c5f\u6e56\u4e0a\u7684\u8baf\u606f\uff0c\u90a3\u4e5f\u662f\u6709\u7684\u3002\u201d \u3000\u3000\u4e01\u5178\u7741\u5927\u4e86\u773c\u775b\uff0c\u77aa\u89c6\u7740\u4ed6\uff0c\u8138\u4e0a\u5145\u6ee1\u4e86\u597d\u5947\uff0c\u9053\uff1a\u201c\u751a\u4e48\uff1f\u4f60\u2026\u2026\u4f60\u5e08\u7236\u662f\u4e2a\u4e61\u4e0b\u8001\u5b9e\u4eba\uff1f\u4ed6\u6740\u4e86\u4eba\u4f1a\u5bb3\u6015\u9003\u8d70\uff1f\u201d \u3000\u3000\u72c4\u4e91\u9053\uff1a\u201c\u662f\u554a\uff0c\u6211\u5e08\u7236\u518d\u5fe0\u539a\u8001\u5b9e\u4e5f\u6ca1\u6709\u4e86\uff0c\u4e07\u5e08\u4f2f\u51a4\u6789\u4ed6\u5077\u76d7\u592a\u5e08\u7236\u7684\u751a\u4e48\u5251\u8bc0\uff0c\u4ed6\u4e00\u6012\u4e4b\u4e0b\uff0c\u5fcd\u4e0d\u4f4f\u52a8\u624b\uff0c\u5176\u5b9e\u4ed6\u5fc3\u5730\u518d\u597d\u4e5f\u6ca1\u6709\u4e86\u3002\u201d \u3000\u3000\u4e01\u5178\u563f\u7684\u4e00\u58f0\u51b7\u7b11\uff0c\u81ea\u53bb\u5750\u5728\u5c4b\u89d2\uff0c\u5634\u91cc\u8f7b\u54fc\u5c0f\u66f2\u3002\u72c4\u4e91\u5947\u9053\uff1a\u201c\u4f60\u4e3a\u751a\u4e48\u51b7\u7b11\uff1f\u201d\u4e01\u5178\u9053\uff1a\u201c\u4e0d\u4e3a\u751a\u4e48\u3002\u201d\u72c4\u4e91\u9053\uff1a\u201c\u4e00\u5b9a\u6709\u539f\u56e0\u7684\u3002\u4e01\u5927\u54e5\uff0c\u4f60\u5c3d\u7ba1\u8bf4\u597d\u4e86\u3002\u201d \u3000\u3000\u4e01\u5178\u9053\uff1a\u201c\u597d\u7f62\uff01\u4f60\u5e08\u7236\u5916\u53f7\u53eb\u4f5c\u751a\u4e48\uff1f\u201d\u72c4\u4e91\u9053\uff1a\u201c\u53eb\u4f5c\u2018\u94c1\u9501\u6a2a\u6c5f\u2019\u3002\u201d\u4e01\u5178\u9053\uff1a\u201c\u90a3\u662f\u751a\u4e48\u610f\u601d\uff1f\u201d\u72c4\u4e91\u8fdf\u7591\u534a\u664c\uff0c\u9053\uff1a\u201c\u8fd9\u79cd\u6587\u8bcc\u8bcc\u7684\u8bdd\uff0c\u6211\u539f\u672c\u4e0d\u5927\u61c2\u3002\u731c\u60f3\u8d77\u6765\uff0c\u90a3\u662f\u8bf4\u4ed6\u8001\u4eba\u5bb6\u6b66\u529f\u4e86\u5f97\uff0c\u5584\u4e8e\u5b88\u5fa1\uff0c\u654c\u4eba\u653b\u4e0d\u8fdb\u4ed6\u95e8\u6237\u7684\u610f\u601d\u3002\u201d \u3000\u3000\u4e01\u5178\u54c8\u54c8\u5927\u7b11\uff0c\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u4f60\u81ea\u5df1\u624d\u5fe0\u539a\u8001\u5b9e\u5f97\u53ef\u4ee5\u3002 \u3000\u3000\u94c1\u9501\u6a2a\u6c5f\uff0c\u90a3\u662f\u53eb\u4eba\u4e0a\u4e5f\u4e0a\u4e0d\u5f97\uff0c\u4e0b\u4e5f\u4e0b\u4e0d\u5f97\u3002\u8001\u4e00\u8f88\u7684\u6b66\u6797\u4eba\u7269\uff0c\u8c01\u4e0d\u77e5\u9053\u8fd9\u4e2a\u5916\u53f7\u7684\u542b\u610f\uff1f\u4f60\u5e08\u7236\u806a\u660e\u673a\u53d8\uff0c\u5389\u5bb3\u4e4b\u6781\uff0c\u53ea\u8981\u662f\u8c01\u60f9\u4e0a\u4e86\u4ed6\uff0c\u4ed6\u4e00\u5b9a\u6316\u7a7a\u5fc3\u601d\u7684\u62a5\u590d\uff0c\u53eb\u4eba\u597d\u4f3c\u4e00\u8258\u8239\u5728\u6c5f\u5fc3\u6da1\u6f29\u4e2d\u4e71\u8f6c\uff0c\u4e0a\u4e5f\u4e0a\u4e0d\u5f97\uff0c\u4e0b\u4e5f\u4e0b\u4e0d\u5f97\u3002\u4f60\u5982\u4e0d\u4fe1\uff0c\u5c06\u6765\u51fa\u72f1\u4e4b\u540e\uff0c\u5c3d\u53ef\u5230\u5916\u9762\u6253\u542c\u6253\u542c\u3002\u201d \u3000\u3000\u72c4\u4e91\u5140\u81ea\u4e0d\u4fe1\uff0c\u9053\uff1a\u201c\u6211\u5e08\u7236\u6559\u6211\u5251\u6cd5\uff0c\u5c06\u62db\u6cd5\u90fd\u89e3\u9519\u4e86\uff0c\u751a\u4e48\u2018\u5b64\u9e3f\u6d77\u4e0a\u6765\uff0c\u6c60\u6f62\u4e0d\u6562\u987e\u2019\uff0c\u4ed6\u89e3\u4f5c\u2018\u54e5\u7fc1\u558a\u4e0a\u6765\uff0c\u662f\u6a2a\u4e0d\u6562\u8fc7\u2019\uff1b\u751a\u4e48\u2018\u843d\u65e5\u7167\u5927\u65d7\uff0c\u9a6c\u9e23\u98ce\u8427\u8427\u2019\uff0c\u4ed6\u89e3\u4f5c\u2018\u8001\u6ce5\u62db\u5927\u59d0\uff0c\u9a6c\u547d\u98ce\u5c0f\u5c0f\u2019\u3002\u4ed6\u5b57\u4e5f\u4e0d\u5927\u8bc6\uff0c\u600e\u8bf4\u5f97\u4e0a\u806a\u660e\u673a\u53d8\uff1f\u201d \u3000\u3000\u4e01\u5178\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f60\u5e08\u7236\u535a\u5b66\u591a\u624d\uff0c\u600e\u4f1a\u89e3\u9519\u8bd7\u53e5\uff1f \u3000\u3000\u4ed6\u57ce\u5e9c\u6781\u6df1\uff0c\u5b9a\u6709\u522b\u610f\u3002\u4e3a\u751a\u4e48\u8fde\u81ea\u5df1\u5f92\u513f\u4e5f\u8981\u7792\u4f4f\uff0c\u5916\u4eba\u53ef\u731c\u6d4b\u4e0d\u900f\u4e86\u3002\u563f\u563f\uff0c\u5018\u82e5\u4f60\u4e0d\u662f\u8fd9\u822c\u2026\u2026\u8fd9\u822c\u5fe0\u539a\u8001\u5b9e\uff0c\u4ed6\u4e5f\u672a\u5fc5\u80af\u6536\u4f60\u4e3a\u5f92\u3002\u54b1\u4eec\u522b\u8bf4\u8fd9\u4ef6\u4e8b\u4e86\uff0c\u6765\u7f62\uff0c\u6211\u7ed9\u4f60\u7c98\u6210\u4e2a\u5927\u80e1\u5b50\u3002\u201d \u3000\u3000\u4ed6\u63d0\u8d77\u5355\u5200\uff0c\u5728\u67ad\u9053\u4eba\u5c38\u4f53\u7684\u624b\u81c2\u4e0a\u65ab\u4e86\u4e00\u5200\u3002\u67ad\u9053\u4eba\u65b0\u6b7b\u672a\u4e45\uff0c\u5200\u4f24\u5904\u6d41\u51fa\u8840\u6765\u3002\u4e01\u5178\u5c06\u4e00\u6839\u6839\u53c8\u7c97\u53c8\u786c\u7684\u80e1\u5b50\u8638\u4e86\u8840\uff0c\u7c98\u5728\u72c4\u4e91\u7684\u4e24\u816e\u548c\u4e0b\u989a\u3002 \u3000\u3000\u72c4\u4e91\u95fb\u5230\u4e00\u9635\u8840\u8165\u4e4b\u6c14\uff0c\u9887\u6709\u60e7\u610f\uff0c\u4f46\u60f3\u5230\u4e07\u572d\u7684\u6bd2\u8ba1\u3001\u5e08\u7236\u8fd9\u4e2a\u5916\u53f7\uff0c\u4ee5\u53ca\u8bb8\u8bb8\u591a\u591a\u81ea\u5df1\u4e0d\u660e\u767d\u7684\u4e8b\u7aef\uff0c\u53ea\u89c9\u5f97\u8fd9\u4e16\u4e0a\u6700\u5e73\u5b89\u7684\uff0c\u53cd\u800c\u662f\u5728\u8fd9\u7262\u72f1\u4e4b\u4e2d\u3002 \u3000"}),showOnHomepage:!0},{name:"\u4e09\u3000\u4eba\u6de1\u5982\u83ca",content:(0,a.jsx)(a.Fragment,{children:"\u7b2c\u4e8c\u65e5\u4e2d\u5348\uff0c\u72f1\u4e2d\u8fde\u7eed\u4e0d\u65ad\u7684\u5173\u4e86\u5341\u4e03\u4e2a\u72af\u4eba\u8fdb\u6765\u3002\u9ad8\u77ee\u8001\u5c11\uff0c\u6a21\u6837\u4e00\u77a7\u5373\u77e5\u90fd\u662f\u6c5f\u6e56\u4eba\u7269\uff0c\u5c06\u4e00\u95f4\u72f1\u5ba4\u6324\u5f97\u6ee1\u6ee1\u5730\uff0c\u90fd\u53ea\u6709\u62b1\u819d\u800c\u5750\u3002\u72c4\u4e91\u89c1\u8d8a\u6765\u8d8a\u591a\uff0c\u4e0d\u7531\u5f97\u6697\u81ea\u5fc3\u60ca\uff0c\u60c5\u77e5\u8fd9\u4e9b\u4eba\u90fd\u662f\u4e3a\u5bf9\u4ed8\u4e01\u5178\u800c\u6765\u3002\u4ed6\u672c\u8bf4\u6709\u4e94\u4e2a\u52b2\u654c\uff0c\u54ea\u77e5\u7adf\u6765\u4e86\u4e00\u5341\u4e03\u4e2a\u3002 \u3000\u3000\u4e01\u5178\u5374\u4e00\u76f4\u671d\u7740\u5899\u58c1\u800c\u5367\uff0c\u6beb\u4e0d\u7406\u4f1a\u3002 \u3000\u3000\u8fd9\u4e9b\u72af\u4eba\u5927\u547c\u5c0f\u53eb\uff0c\u9ad8\u58f0\u8c08\u7b11\uff0c\u7247\u523b\u95f4\u4fbf\u5435\u8d77\u5634\u6765\u3002\u72c4\u4e91\u4f4e\u4e0b\u4e86\u5934\uff0c\u542c\u4ed6\u4eec\u7684\u8bf4\u8bdd\u3002\u539f\u6765\u8fd9\u4e00\u5341\u4e03\u4eba\u5206\u4f5c\u4e09\u6d3e\uff0c\u90fd\u5728\u60f3\u5f97\u751a\u4e48\u5b9d\u8d35\u7684\u7269\u4e8b\u3002\u72c4\u4e91\u5076\u5c14\u76ee\u5149\u4e00\u659c\uff0c\u4e0e\u8fd9\u5e72\u4eba\u51f6\u66b4\u7684\u76ee\u5149\u76f8\u89e6\uff0c\u5413\u5f97\u4e0d\u7981\u4fbf\u8f6c\u8fc7\u5934\u53bb\uff0c\u53ea\u60f3\uff1a\u201c\u6211\u626e\u4f5c\u4e86\u4e01\u5927\u54e5\uff0c\u53ef\u662f\u6211\u6b66\u529f\u5168\u5931\uff0c\u5f85\u4f1a\u52a8\u624b\uff0c\u90a3\u4fbf\u5982\u4f55\u662f\u597d\uff1f\u4e01\u5927\u54e5\u672c\u9886\u518d\u9ad8\uff0c\u4e5f\u4e0d\u80fd\u5c06\u8fd9\u4e9b\u4eba\u90fd\u6253\u6b7b\u554a\u3002\u201d \u3000\u3000\u773c\u89c1\u5929\u8272\u9ed1\u4e86\u4e0b\u6765\u3002\u4e00\u4e2a\u9b41\u68a7\u7684\u5927\u6c49\u5927\u58f0\u9053\uff1a\u201c\u54b1\u4eec\u628a\u8bdd\u8bf4\u660e\u5728\u5148\uff0c\u8fd9\u6b63\u4e3b\u513f\uff0c\u662f\u6211\u4eec\u6d1e\u5ead\u5e2e\u8981\u4e86\u7684\u3002\u8c01\u8981\u662f\u4e0d\u670d\uff0c\u4e58\u65e9\u624b\u5e95\u4e0b\u89c1\u771f\u7ae0\uff0c\u514d\u5f97\u5f85\u4f1a\u62c9\u62c9\u626f\u626f\uff0c\u591a\u60f9\u9ebb\u70e6\u3002\u201d\u4ed6\u8fd9\u6d1e\u5ead\u5e2e\u5728\u72f1\u4e2d\u5171\u6709\u4e5d\u4eba\uff0c\u6700\u662f\u4eba\u591a\u52bf\u4f17\u3002\u4e00\u4e2a\u5934\u53d1\u7070\u767d\u7684\u4e2d\u5e74\u6c49\u5b50\u9634\u9633\u602a\u6c14\u7684\u9053\uff1a\u201c\u624b\u5e95\u4e0b\u89c1\u771f\u7ae0\uff0c\u90a3\u4e5f\u597d\u554a\u3002\u5927\u4f19\u513f\u5728\u8fd9\u91cc\u7fa4\u6bb4\u5462\uff0c\u8fd8\u662f\u5230\u9662\u5b50\u4e2d\u6253\u4e2a\u660e\u767d\uff1f\u201d\u90a3\u5927\u6c49\u9053\uff1a\u201c\u9662\u5b50\u5c31\u9662\u5b50\uff0c\u8c01\u8fd8\u6015\u4e86\u4f60\u4e0d\u6210\uff1f\u201d\u4f38\u624b\u6293\u4f4f\u4e00\u6761\u94c1\u6805\uff0c\u5411\u5de6\u4e00\u63a8\uff0c\u94c1\u6761\u767b\u65f6\u5f2f\u4e86\u3002\u4ed6\u968f\u624b\u53c8\u626d\u5f2f\u53f3\u8fb9\u4e00\u6761\u94c1\u6805\uff0c\u8182\u529b\u5b9e\u662f\u60ca\u4eba\u3002 \u3000\u3000\u8fd9\u5927\u6c49\u6b63\u60f3\u4ece\u4e24\u6761\u626d\u5f2f\u4e86\u7684\u94c1\u6805\u95f4\u94bb\u51fa\u53bb\uff0c\u7a81\u7136\u95f4\u773c\u524d\u4eba\u5f71\u4e00\u6643\uff0c\u4e00\u4e2a\u4eba\u6321\u4f4f\u4e86\u7a7a\u9699\uff0c\u6b63\u662f\u4e01\u5178\u3002\u4ed6\u4e00\u8a00\u4e0d\u53d1\uff0c\u4e00\u4f38\u624b\u4fbf\u6293\u4f4f\u4e86\u90a3\u5927\u6c49\u7684\u80f8\u53e3\u3002\u8fd9\u5927\u6c49\u6bd4\u4e01\u5178\u8fd8\u9ad8\u51fa\u534a\u4e2a\u5934\uff0c\u4f46\u88ab\u4ed6\u4e00\u628a\u6293\u4f4f\uff0c\u7adf\u7acb\u5373\u8f6f\u5782\u5782\u7684\u6beb\u4e0d\u52a8\u5f39\u3002\u4e01\u5178\u5c06\u4ed6\u5e9e\u5927\u7684\u8eab\u5b50\u4ece\u94c1\u6805\u95f4\u585e\u4e86\u51fa\u53bb\uff0c\u629b\u5728\u9662\u5b50\u4e2d\u3002\u8fd9\u5927\u6c49\u8737\u7f29\u5728\u5730\u4e0b\uff0c\u518d\u4e5f\u4e0d\u52a8\u4e00\u52a8\uff0c\u663e\u662f\u6b7b\u4e86\u3002 \u3000\u3000\u72f1\u4e2d\u8bf8\u4eba\u89c1\u5230\u8fd9\u822c\u5947\u72b6\uff0c\u90fd\u5413\u5f97\u5446\u4e86\u3002\u4e01\u5178\u968f\u624b\u6293\u4e86\u4e00\u4eba\uff0c\u4ece\u94c1\u6805\u6295\u63b7\u51fa\u53bb\uff0c\u8ddf\u7740\u53c8\u6293\u4e00\u4eba\uff0c\u63a5\u8fde\u7684\u53c8\u6293\u53c8\u63b7\uff0c\u5148\u540e\u5171\u6709\u4e03\u4eba\u88ab\u4ed6\u6295\u4e86\u51fa\u53bb\u3002\u51e1\u7ecf\u4ed6\u53cc\u624b\u4e00\u6293\uff0c\u65e0\u4e0d\u7acb\u65f6\u6bd9\u547d\u3002 \u3000\u3000\u8fde\u54fc\u4e5f\u4e0d\u54fc\u4e00\u58f0\u3002 \u3000\u3000\u4f59\u4e0b\u7684\u5341\u4eba\u5c3d\u7686\u5927\u60ca\uff0c\u4e09\u4eba\u9000\u7f29\u5230\u72f1\u5ba4\u89d2\u843d\uff0c\u5176\u4f59\u4e03\u4eba\u540c\u65f6\u51fa\u624b\uff0c\u62f3\u6253\u811a\u8e22\uff0c\u5411\u4e01\u5178\u653b\u53bb\u3002\u4e01\u5178\u65e2\u4e0d\u62c6\u67b6\uff0c\u4ea6\u4e0d\u95ea\u907f\uff0c\u53ea\u662f\u4f38\u624b\u4e00\u6293\uff0c\u4e00\u6293\u4e4b\u4e0b\uff0c\u5fc5\u5b9a\u6293\u5230\u4e00\u4eba\uff0c\u800c\u88ab\u4ed6\u6293\u5230\u7684\u5fc5\u5b9a\u6b7b\u4e8e\u9877\u523b\uff0c\u5230\u5e95\u5982\u4f55\u53d7\u4e86\u81f4\u547d\u4e4b\u4f24\uff0c\u72c4\u4e91\u5168\u7136\u77a7\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u8eb2\u5728\u72f1\u5ba4\u89d2\u843d\u91cc\u7684\u4e09\u4eba\u53ea\u5413\u5f97\u5fc3\u80c6\u4ff1\u88c2\uff0c\u4e00\u9f50\u5c48\u819d\u8dea\u5730\uff0c\u78d5\u5934\u6c42\u9976\u3002\u4e01\u5178\u4fbf\u4f3c\u6ca1\u6709\u77a7\u89c1\uff0c\u53c8\u662f\u4e00\u624b\u4e00\u4e2a\uff0c\u90fd\u6293\u4e86\u6295\u63b7\u51fa\u53bb\u3002 \u3000\u3000\u72c4\u4e91\u53ea\u77a7\u5f97\u76ee\u77aa\u53e3\u5446\uff0c\u604d\u5728\u68a6\u4e2d\u3002 \u3000\u3000\u4e01\u5178\u62cd\u4e86\u62cd\u53cc\u624b\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u8fd9\u4e00\u70b9\u513f\u5fae\u672b\u9053\u884c\uff0c\u4e5f\u60f3\u6765\u62a2\u593a\uff01\u201d\u72c4\u4e91\u4e00\u5446\uff0c\u9053\uff1a\u201c\u4e01\u5927\u54e5\uff0c\u751a\u4e48\uff1f\u201d\u4e01\u5178\u4f3c\u4e4e\u81ea\u6094\u5931\u8a00\uff0c\u4f46\u4e5f\u4e0d\u613f\u634f\u9020\u4e9b\u8a00\u8bed\u6765\u9a97\u4ed6\uff0c\u53c8\u51b7\u7b11\u4e86\u51e0\u4e0b\uff0c\u5e76\u4e0d\u56de\u7b54\u3002 \u3000\u3000\u72c4\u4e91\u773c\u89c1\u8fd9\u4e00\u5341\u4e03\u4eba\u9002\u624d\u8fd8\u90fd\u662f\u751f\u9f99\u6d3b\u864e\u4e00\u822c\uff0c\u4f46\u7247\u523b\u4e4b\u95f4\uff0c\u4e2a\u4e2a\u5c38\u6a2a\u5c31\u5730\uff0c\u4ed6\u4e00\u751f\u4e2d\u4ece\u672a\u89c1\u8fc7\u8fd9\u8bb8\u591a\u4eba\u5806\u5728\u4e00\u8d77\uff0c\u53f9\u9053\uff1a\u201c\u4e01\u5927\u54e5\uff0c\u8fd9\u4e9b\u4eba\u90fd\u662f\u6b7b\u6709\u4f59\u8f9c\u4e48\uff1f\u201d \u3000\u3000\u4e01\u5178\u9053\uff1a\u201c\u6b7b\u6709\u4f59\u8f9c\uff0c\u5012\u4e5f\u4e0d\u89c1\u5f97\u3002\u53ea\u662f\u8fd9\u4e9b\u4eba\u4e2a\u4e2a\u4e0d\u5b58\u597d\u5fc3\u3002\u6211\u82e5\u4e0d\u662f\u7ec3\u6210\u4e86\u2018\u795e\u7167\u7ecf\u2019\u4e0a\u7684\u6b66\u529f\uff0c\u88ab\u8fd9\u6279\u4eba\u903c\u4f9b\u8d77\u6765\uff0c\u90a3\u624d\u662f\u60e8\u4e0d\u582a\u8a00\u5462\u3002\u201d \u3000\u3000\u72c4\u4e91\u77e5\u4ed6\u6240\u8a00\u975e\u865a\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u968f\u624b\u4e00\u6293\uff0c\u4fbf\u4f24\u4eba\u6027\u547d\uff0c\u8fd9\u79cd\u529f\u592b\u6211\u542c\u4e5f\u6ca1\u542c\u8bf4\u8fc7\u3002\u6211\u82e5\u662f\u8ddf\u5e08\u59b9\u8bf4\uff0c\u5979\u4e5f\u4e0d\u4f1a\u76f8\u4fe1\u2026\u2026\u201d\u8fd9\u53e5\u8bdd\u521a\u8bf4\u51fa\u53e3\uff0c\u7acb\u5373\u7701\u609f\uff0c\u4e0d\u7531\u5f97\u80f8\u5934\u4e00\u9178\uff0c\u5fc3\u53e3\u4f3c\u4e4e\u88ab\u4eba\u91cd\u91cd\u6253\u4e86\u4e00\u62f3\u3002 \u3000\u3000\u4e01\u5178\u5374\u5e76\u4e0d\u7b11\u4ed6\uff0c\u53f9\u4e86\u53e3\u957f\u6c14\uff0c\u81ea\u8a00\u81ea\u8bed\uff1a\u201c\u5176\u5b9e\u5462\uff0c\u7eb5\u7136\u7ec3\u6210\u4e86\u7edd\u4e16\u6b66\u529f\uff0c\u4e5f\u4e0d\u80fd\u4e8b\u4e8b\u5c3d\u5982\u4eba\u610f\u2026\u2026\u201d \u3000\u3000\u72c4\u4e91\u5ffd\u7136\u201c\u54a6\u201d\u7684\u4e00\u58f0\uff0c\u4f38\u624b\u6307\u7740\u5ead\u4e2d\u7684\u4e00\u5177\u6b7b\u5c38\u3002 \u3000\u3000\u4e01\u5178\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u72c4\u4e91\u9053\uff1a\u201c\u8fd9\u4eba\u6ca1\u6b7b\u900f\uff0c\u4ed6\u7684\u811a\u52a8\u4e86\u51e0\u52a8\u3002\u201d\u4e01\u5178\u5927\u5403\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u5f53\u771f\uff1f\u201d\u8bf4\u8fd9\u4e24\u4e2a\u5b57\u65f6\uff0c\u58f0\u97f3\u4e5f\u53d1\u98a4\u4e86\u3002\u72c4\u4e91\u9053\uff1a\u201c\u521a\u624d\u6211\u89c1\u4ed6\u52a8\u4e86\u4e24\u4e0b\u3002\u201d\u5fc3\u60f3\uff1a\u201c\u4e00\u4e2a\u4eba\u53d7\u4f24\u4e0d\u6b7b\uff0c\u90a3\u4e5f\u6ca1\u751a\u4e48\u5927\u4e0d\u4e86\uff0c\u51b3\u4e0d\u80fd\u518d\u8d77\u6765\u52a8\u624b\u3002\u201d \u3000\u3000\u4e01\u5178\u76b1\u8d77\u4e86\u7709\u5934\uff0c\u7adf\u4f3c\u9047\u4e0a\u4e86\u91cd\u5927\u96be\u9898\uff0c\u4ece\u94c1\u6805\u95f4\u94bb\u4e86\u51fa\u53bb\uff0c\u4fef\u8eab\u67e5\u770b\u3002 \u3000\u3000\u7a81\u7136\u95f4\u55e4\u55e4\u4e24\u58f0\uff0c\u4e24\u4ef6\u7ec6\u5fae\u7684\u6697\u5668\u5206\u5411\u4ed6\u53cc\u773c\u6025\u5c04\uff0c\u6b63\u662f\u90a3\u5e76\u672a\u6b7b\u900f\u4e4b\u4eba\u6240\u53d1\u3002\u4e01\u5178\u5411\u540e\u6025\u4ef0\uff0c\u4e24\u679d\u8896\u7bad\u4ece\u4ed6\u9762\u4e0a\u63a0\u4e86\u8fc7\u53bb\uff0c\u9f3b\u4e2d\u9690\u9690\u95fb\u5230\u4e00\u9635\u8165\u81ed\uff0c\u663e\u7136\u7bad\u4e0a\u5582\u6709\u5267\u6bd2\u3002\u90a3\u4eba\u4e00\u53d1\u51fa\u8896\u7bad\uff0c\u7acb\u5373\u633a\u8dc3\u800c\u8d77\uff0c\u5411\u5c4b\u6a90\u4e0a\u7a9c\u53bb\u3002 \u3000\u3000\u4e01\u5178\u89c1\u4ed6\u8f7b\u8eab\u529f\u592b\u4e86\u5f97\uff0c\u81ea\u5df1\u8eab\u6709\u94d0\u9563\uff0c\u884c\u52a8\u4e0d\u4fbf\uff0c\u53ea\u6015\u8ffd\u4ed6\u4e0d\u4e0a\uff0c\u968f\u624b\u63d0\u8d77\u4e00\u5177\u5c38\u4f53\u5411\u4e0a\u63b7\u53bb\u3002\u53bb\u52bf\u5947\u6025\u3002\u7830\u7684\u4e00\u4e0b\uff0c\u5c38\u4f53\u7684\u8111\u888b\u91cd\u91cd\u649e\u5728\u90a3\u4eba\u8170\u95f4\u3002\u90a3\u4eba\u5de6\u8db3\u521a\u8e0f\u4e0a\u5c4b\u6a90\uff0c\u88ab\u8fd9\u5c38\u4f53\u4e00\u649e\uff0c\u7ad9\u7acb\u4e0d\u5b9a\uff0c\u5012\u6454\u4e0b\u6765\u3002\u4e01\u5178\u62a2\u4e0a\u51e0\u6b65\uff0c\u4e00\u628a\u6293\u4f4f\u4ed6\u540e\u9888\uff0c\u63d0\u5230\u7262\u623f\u4e4b\u4e2d\uff0c\u4f38\u624b\u63a2\u4ed6\u9f3b\u606f\u65f6\uff0c\u8fd9\u6b21\u662f\u771f\u7684\u6b7b\u4e86\u3002 \u3000\u3000\u4e01\u5178\u5750\u5728\u5730\u4e0b\uff0c\u53cc\u624b\u652f\u9890\uff0c\u82e6\u82e6\u601d\u7d22\uff1a\u201c\u4e3a\u751a\u4e48\u5148\u524d\u8fd9\u4e00\u4e0b\u7adf\u6ca1\u80fd\u6293\u6b7b\u4ed6\uff1f\u6211\u7684\u529f\u529b\u4e4b\u4e2d\uff0c\u5230\u5e95\u51fa\u4e86\u751a\u4e48\u6bdb\u75c5\uff1f\u96be\u9053\u8fd9\u2018\u795e\u7167\u529f\u2019\u6bd5\u7adf\u6ca1\u7ec3\u6210\uff1f\u201d\u534a\u5929\u60f3\u4e0d\u51fa\u4e2a\u6240\u4ee5\u7136\uff0c\u607c\u8d77\u4e0a\u6765\uff0c\u4f38\u624b\u53c8\u5f80\u90a3\u5c38\u4f53\u7684\u80f8\u53e3\u63d2\u843d\uff0c\u7a81\u7136\u4e00\u80a1\u53c8\u97e7\u53c8\u8f6f\u7684\u529b\u9053\u5c06\u4ed6\u624b\u6307\u5f39\u4e86\u56de\u6765\uff0c\u4e01\u5178\u60ca\u559c\u4ea4\u96c6\uff0c\u53eb\u9053\uff1a\u201c\u662f\u4e86\uff0c\u662f\u4e86\uff01\u201d\u6495\u5f00\u90a3\u4eba\u5916\u8863\uff0c\u53ea\u89c1\u4ed6\u8d34\u8eab\u7a7f\u7740\u4e00\u4ef6\u6f06\u9ed1\u53d1\u4eae\u7684\u91cc\u8863\uff0c\u559c\u9053\uff1a\u201c\u662f\u4e86\uff01\u539f\u6765\u5982\u6b64\uff0c\u5012\u5413\u5f97\u6211\u5927\u5403\u4e00\u60ca\u3002\u201d \u3000\u3000\u72c4\u4e91\u5947\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u4e01\u5178\u5265\u53bb\u90a3\u6c49\u5b50\u7684\u5916\u8863\uff0c\u53c8\u5c06\u4ed6\u8fd9\u4ef6\u9ed1\u8272\u91cc\u8863\u5265\u4e86\u4e0b\u6765\u3002\u7136\u540e\u5c06\u5c38\u4f53\u63b7\u51fa\u7262\u623f\uff0c\u7b11\u563b\u563b\u7684\u9053\uff1a\u201c\u72c4\u5144\u5f1f\uff0c\u4f60\u628a\u8fd9\u4ef6\u8863\u670d\u7a7f\u5728\u8eab\u4e0a\u3002\u201d \u3000\u3000\u72c4\u4e91\u6599\u5230\u8fd9\u4ef6\u9ed1\u8863\u751a\u662f\u73cd\u8d35\uff0c\u9053\uff1a\u201c\u8fd9\u662f\u5927\u54e5\u4e4b\u7269\uff0c\u5144\u5f1f\u4e0d\u6562\u8d2a\u56fe\u3002\u201d\u4e01\u5178\u9053\uff1a\u201c\u4e0d\u662f\u4f60\u7684\u7269\u4e8b\uff0c\u4f60\u4fbf\u4e0d\u8d2a\u56fe\u4e48\uff1f\u201d\u8bed\u97f3\u751a\u662f\u4e25\u5389\u3002\u72c4\u4e91\u4e00\u6014\uff0c\u6015\u4ed6\u751f\u6c14\uff0c\u9053\uff1a\u201c\u5927\u54e5\u5b9a\u8981\u6211\u7a7f\uff0c\u6211\u7a7f\u4e0a\u5c31\u662f\u3002\u201d \u3000\u3000\u4e01\u5178\u6b63\u8272\u9053\uff1a\u201c\u6211\u95ee\u4f60\uff0c\u4e0d\u662f\u4f60\u7684\u7269\u4e8b\uff0c\u4f60\u8981\u4e0d\u8981\uff1f\u201d\u72c4\u4e91\u9053\uff1a\u201c\u9664\u975e\u7269\u4e3b\u4e00\u5b9a\u8981\u7ed9\u6211\uff0c\u6211\u975e\u53d7\u4e0d\u53ef\uff0c\u5426\u5219\u2026\u2026\u5426\u5219\u2026\u2026\u4e0d\u662f\u6211\u7684\u4e1c\u897f\uff0c\u6211\u81ea\u7136\u4e0d\u80fd\u8981\u3002\u82e5\u662f\u8d2a\u56fe\u522b\u4eba\u7684\u4e1c\u897f\uff0c\u90a3\u4e0d\u662f\u53d8\u6210\u5f3a\u76d7\u5c0f\u5077\u4e48\uff1f\u201d\u8bf4\u5230\u540e\u6765\uff0c\u795e\u8272\u6602\u7136\uff0c\u9053\uff1a\u201c\u4e01\u5927\u54e5\uff0c\u4f60\u660e\u767d\uff0c\u6211\u662f\u53d7\u4eba\u9677\u5bb3\uff0c\u624d\u7ed9\u5173\u5728\u8fd9\u91cc\u3002\u6211\u4e00\u751f\u6e05\u767d\uff0c\u53ef\u4ece\u6765\u6ca1\u505a\u8fc7\u751a\u4e48\u574f\u4e8b\u3002\u201d \u3000\u3000\u4e01\u5178\u70b9\u4e86\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u5f88\u597d\uff0c\u5f88\u597d\uff01\u4e0d\u6789\u6211\u4e01\u67d0\u4ea4\u4e86\u4f60\u8fd9\u4e2a\u670b\u53cb\u3002\u4f60\u628a\u8fd9\u4ef6\u8863\u670d\u8d34\u8089\u7a7f\u7740\u3002\u201d \u3000\u3000\u72c4\u4e91\u4e0d\u4fbf\u8fdd\u62d7\uff0c\u4fbf\u9664\u4e0b\u8863\u886b\uff0c\u628a\u8fd9\u4ef6\u9ed1\u8272\u91cc\u8863\u8d34\u8089\u7a7f\u4e86\uff0c\u5916\u9762\u518d\u7f69\u4e0a\u90a3\u4ef6\u4e09\u5e74\u591a\u6ca1\u6d17\u7684\u81ed\u8863\u3002\u4ed6\u53cc\u624b\u6234\u7740\u624b\u94d0\u94c1\u94fe\uff0c\u8981\u66f4\u6362\u8863\u886b\uff0c\u76f4\u662f\u96be\u4e0a\u52a0\u96be\uff0c\u5168\u4ed7\u4e01\u5178\u66ff\u4ed6\u6495\u7834\u65e7\u886b\u7684\u8863\u8896\uff0c\u65b9\u80fd\u9664\u4e0b\u7a7f\u4e0a\u3002\u90a3\u4ef6\u9ed1\u8272\u91cc\u8863\u5176\u5b9e\u662f\u524d\u540e\u4e24\u7247\uff0c\u814b\u4e0b\u7528\u6263\u5b50\u6263\u8d77\uff0c\u7a7f\u4e0a\u5012\u534a\u70b9\u4e0d\u96be\u3002 \u3000\u3000\u4e01\u5178\u5f85\u4ed6\u7a7f\u597d\u4e86\uff0c\u624d\u9053\uff1a\u201c\u8fd9\u4e00\u4ef6\u5200\u67aa\u4e0d\u5165\u7684\u5b9d\u8863\uff0c\u662f\u7528\u5927\u96ea\u5c71\u4e0a\u7684\u4e4c\u8695\u8695\u4e1d\u7ec7\u6210\u7684\u3002\u4f60\u77a7\uff0c\u8fd9\u53ea\u662f\u4e24\u5757\u6599\u5b50\uff0c\u526a\u5200\u4e5f\u526a\u4e0d\u70c2\uff0c\u53ea\u5f97\u524d\u4e00\u5757\u3001\u540e\u4e00\u5757\u7684\u6263\u5728\u4e00\u8d77\u3002\u8fd9\u5bb6\u4f19\u662f\u96ea\u5c71\u6d3e\u4e2d\u7684\u8981\u7d27\u4eba\u7269\uff0c\u624d\u6709\u8fd9\u4ef6\u2018\u4e4c\u8695\u8863\u2019\u3002\u4ed6\u60f3\u6765\u53d6\u5b9d\uff0c\u6ca1\u6599\u60f3\u7adf\u662f\u9001\u5b9d\u6765\u4e86\uff01\u201d \u3000\u3000\u72c4\u4e91\u542c\u8bf4\u8fd9\u4ef6\u9ed1\u8863\u5982\u6b64\u73cd\u5f02\uff0c\u5fd9\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4f60\u4ec7\u4eba\u751a\u591a\uff0c\u8be5\u5f53\u81ea\u5df1\u7a7f\u4e86\u62a4\u8eab\u624d\u662f\u3002\u518d\u8bf4\uff0c\u6bcf\u4e2a\u6708\u5341\u4e94\u2026\u2026\u201d\u4e01\u5178\u8fde\u8fde\u6447\u5934\uff0c\u9053\uff1a\u201c\u6211\u6709\u795e\u7167\u529f\u62a4\u8eab\uff0c\u7528\u4e0d\u7740\u8fd9\u4e4c\u8695\u8863\u3002\u6bcf\u6708\u5341\u4e94\u7684\u62f7\u6253\u561b\uff0c\u6211\u662f\u7518\u5fc3\u60c5\u613f\u53d7\u7684\uff0c\u7528\u8fd9\u5b9d\u7532\u62a4\u8eab\uff0c\u53cd\u800c\u5176\u610f\u4e0d\u8bda\u4e86\u3002\u4e00\u4e9b\u76ae\u8089\u4e4b\u82e6\uff0c\u53c8\u4f24\u4e0d\u4e86\u7b4b\u9aa8\uff0c\u6709\u751a\u4e48\u76f8\u5e72\uff1f\u201d \u3000\u3000\u72c4\u4e91\u597d\u751f\u5947\u602a\uff0c\u6b32\u5f85\u518d\u95ee\u3002\u4e01\u5178\u9053\uff1a\u201c\u6211\u53eb\u4f60\u7c98\u4e0a\u80e1\u5b50\uff0c\u626e\u4f5c\u6211\u7684\u6a21\u6837\uff0c\u6211\u867d\u5728\u65c1\u4fdd\u62a4\uff0c\u603b\u662f\u62c5\u5fc3\u6709\u751a\u4e48\u758f\u865e\uff0c\u73b0\u4e0b\u8fd9\u53ef\u597d\u4e86\u3002\u6211\u73b0\u4e0b\u4f20\u4f60\u5185\u529f\u7684\u5fc3\u6cd5\uff0c\u4f60\u597d\u597d\u542c\u7740\u3002\u201d \u3000\u3000\u4ee5\u524d\u4e01\u5178\u8981\u4f20\u4ed6\u529f\u592b\uff0c\u72c4\u4e91\u4e07\u5ff5\u4ff1\u7070\uff0c\u51b3\u610f\u4e0d\u5b66\uff0c\u6b64\u523b\u660e\u767d\u4e86\u53d7\u4eba\u9677\u5bb3\u7684\u524d\u56e0\u540e\u679c\uff0c\u4e00\u80a1\u590d\u4ec7\u4e4b\u706b\u5728\u80f8\u4e2d\u718a\u718a\u71c3\u8d77\uff0c\u6068\u4e0d\u5f97\u7acb\u65f6\u4fbf\u51fa\u72f1\u53bb\u627e\u4e07\u572d\u7b97\u5e10\u3002\u4ed6\u4eb2\u773c\u89c1\u5230\u4e86\u4e01\u5178\u8d64\u624b\u7a7a\u62f3\uff0c\u8fde\u6bd9\u8fd9\u8bb8\u591a\u6c5f\u6e56\u9ad8\u624b\uff0c\u5fc3\u60f3\u81ea\u5df1\u53ea\u987b\u5b66\u4ed6\u4e24\u4e09\u6210\u529f\u592b\uff0c\u8d8a\u72f1\u62a5\u4ec7\u4fbf\u6709\u6307\u671b\uff0c\u970e\u65f6\u95f4\u5fc3\u4e71\u5982\u9ebb\uff0c\u70ed\u8840\u4e0a\u6d8c\uff0c\u6ee1\u8138\u901a\u7ea2\u3002 \u3000\u3000\u4e01\u5178\u53ea\u9053\u4ed6\u4ecd\u662f\u6267\u610f\u4e0d\u80af\u5b66\u8fd9\u5185\u529f\uff0c\u6b63\u6b32\u8bbe\u6cd5\u5f00\u5bfc\uff0c\u72c4\u4e91\u7a81\u7136\u53cc\u819d\u8dea\u4e0b\uff0c\u653e\u58f0\u5927\u54ed\uff0c\u53eb\u9053\uff1a\u201c\u4e01\u5927\u54e5\uff0c\u6c42\u4f60\u6559\u6211\u3002\u6211\u8981\u62a5\u4ec7\uff01\u6211\u8981\u62a5\u4ec7\uff01\u201d \u3000\u3000\u4e01\u5178\u7eb5\u58f0\u957f\u7b11\uff0c\u58f0\u9707\u5c4b\u74e6\uff0c\u8bf4\u9053\uff1a\u201c\u8981\u62a5\u4ec7\uff0c\u90a3\u8fd8\u4e0d\u5bb9\u6613\uff1f\u201d \u3000\u3000\u5f85\u72c4\u4e91\u6fc0\u60c5\u8fc7\u53bb\uff0c\u4e01\u5178\u4fbf\u5373\u4f20\u6388\u4ed6\u5165\u95e8\u7ec3\u529f\u7684\u53e3\u8bc0\u548c\u884c\u529f\u4e4b\u6cd5\u3002 \u3000\u3000\u72c4\u4e91\u4e00\u5f97\u4f20\u6388\uff0c\u6beb\u4e0d\u505c\u7559\u7684\u4fbf\u5373\u4f9d\u6cd5\u4fee\u4e60\u3002\u4e01\u5178\u89c1\u4ed6\u7ec3\u5f97\u8d77\u52b2\uff0c\u7b11\u9053\uff1a\u201c\u7ec3\u6210\u795e\u7167\u7ecf\uff0c\u5929\u4e0b\u65e0\u654c\u624b\u3002\u96be\u9053\u662f\u8fd9\u822c\u5bb9\u6613\u7ec3\u6210\u7684\u4e48\uff1f\u6211\u5404\u79cd\u673a\u7f18\u5de7\u5408\uff0c\u5185\u529f\u7684\u5e95\u5b50\u53c8\u597d\uff0c\u8fd9\u624d\u5341\u4e8c\u5e74\u800c\u5f97\u5927\u6210\u3002\u72c4\u5144\u5f1f\uff0c\u7ec3\u6b66\u529f\u8981\u52e4\uff0c\u90a3\u662f\u5f88\u8981\u7d27\u7684\uff0c\u53ef\u662f\u6b32\u901f\u5219\u4e0d\u8fbe\uff0c\u987b\u5f97\u5faa\u5e8f\u6e10\u8fdb\u624d\u662f\u3002\u5c24\u987b\u5fc3\u5e73\u6c14\u548c\uff0c\u6ca1\u534a\u70b9\u6742\u5ff5\u3002\u4f60\u597d\u597d\u8bb0\u7740\u6211\u8fd9\u51e0\u53e5\u8bdd\u3002\u201d \u3000\u3000\u72c4\u4e91\u6b64\u65f6\u53e3\u4e2d\u79f0\u4ed6\u4e3a\u201c\u5927\u54e5\u201d\uff0c\u5fc3\u4e2d\u5176\u5b9e\u5df2\u5f53\u4ed6\u4e3a\u201c\u5e08\u7236\u201d\uff0c\u4ed6\u8bf4\u751a\u4e48\u4fbf\u542c\u751a\u4e48\u3002\u4f46\u80f8\u4e2d\u4ec7\u6068\u6c79\u6d8c\u5982\u6ce2\u6d9b\uff0c\u53c8\u5982\u4f55\u80fd\u5fc3\u5e73\u6c14\u548c\uff1f \u3000\u3000\u6b21\u65e5\u90a3\u72f1\u540f\u5927\u60ca\u5c0f\u602a\u7684\u5435\u56b7\u4e00\u756a\u3002\u8859\u5f79\u3001\u6355\u5feb\u3001\u4ef5\u4f5c\u9a9a\u6270\u534a\u5929\uff0c\u5230\u5f97\u508d\u665a\uff0c\u624d\u5c06\u90a3\u4e00\u5341\u4e03\u5177\u5c38\u9996\u62ac\u4e86\u51fa\u53bb\u3002\u4e01\u5178\u548c\u72c4\u4e91\u53ea\u8bf4\u662f\u8fd9\u4f19\u4eba\u81ea\u76f8\u6597\u6bb4\u800c\u6b7b\u3002\u505a\u516c\u7684\u5374\u4e5f\u6ca1\u6709\u591a\u95ee\u3002 \u3000\u3000\u8fd9\u4e00\u65e5\u4e4b\u4e2d\uff0c\u72c4\u4e91\u53ea\u662f\u7167\u7740\u4e01\u5178\u6240\u6388\u7684\u53e3\u8bc0\u7528\u529f\u8fd9\u3002\u8fd9\u201c\u795e\u7167\u529f\u201d\u5165\u95e8\u7684\u6cd5\u5b50\u751a\u662f\u7b80\u6613\uff0c\u4f46\u8981\u5fc3\u4e2d\u6ca1\u4e1d\u6beb\u5984\u5ff5\uff0c\u5374\u8270\u96be\u4e4b\u6781\u3002\u72c4\u4e91\u4e00\u5ffd\u513f\u60f3\u5230\u5e08\u59b9\uff0c\u4e00\u5ffd\u513f\u60f3\u5230\u4e07\u572d\uff0c\u4e00\u5ffd\u513f\u53c8\u60f3\u5230\u4e86\u5e08\u7236\uff0c\u7ec3\u5230\u665a\u95f4\uff0c\u8fd9\u624d\u5fc3\u5ff5\u7a0d\u655b\uff0c\u7a81\u7136\u4e4b\u95f4\uff0c\u524d\u80f8\u540e\u80cc\u540c\u65f6\u53d7\u4e86\u91cd\u91cd\u4e00\u51fb\u3002 \u3000\u3000\u8fd9\u4e24\u4e0b\u4fbf\u5982\u4e24\u4e2a\u5927\u94c1\u9524\u524d\u540e\u9f50\u649e\u4e00\u822c\u3002\u72c4\u4e91\u773c\u524d\u4e00\u9ed1\uff0c\u51e0\u4e4e\u4fbf\u6b32\u6655\u53bb\uff0c\u5f85\u5f97\u75bc\u75db\u7a0d\u6b62\uff0c\u7741\u5f00\u773c\u6765\uff0c\u53ea\u89c1\u8eab\u524d\u5de6\u53f3\u5404\u7ad9\u7740\u4e00\u4e2a\u548c\u5c1a\uff0c\u4e00\u8f6c\u5934\uff0c\u89c1\u8eab\u540e\u548c\u4e24\u4fa7\u8fd8\u6709\u4e00\u4e2a\uff0c\u4e00\u5171\u4e94\u50e7\uff0c\u5c06\u4ed6\u56f4\u5728\u4e2d\u95f4\u3002 \u3000\u3000\u72c4\u4e91\u5fc3\u9053\uff1a\u201c\u4e01\u5927\u54e5\u6240\u8bf4\u7684\u4e94\u4e2a\u52b2\u654c\u5230\u4e86\uff0c\u6211\u987b\u5f97\u52c9\u5f3a\u652f\u6491\uff0c\u4e0d\u80fd\u9732\u51fa\u7834\u7efd\u3002\u201d\u5f53\u4e0b\u54c8\u54c8\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4e94\u4f4d\u5927\u5e08\u7236\uff0c\u627e\u6211\u4e01\u67d0\u6709\u4f55\u8d35\u5e72\uff1f\u201d \u3000\u3000\u5de6\u9996\u90a3\u50e7\u4eba\u9053\uff1a\u201c\u5feb\u5c06\u2018\u2019\u4ea4\u4e86\u51fa\u6765\uff01\u54a6\uff0c\u4f60\u2026\u2026\u4f60\u2026\u2026\u4f60\u662f\u2026\u2026\u201d\u7a81\u7136\u4e4b\u95f4\uff0c\u4ed6\u80cc\u4e0a\u62cd\u7684\u4e00\u58f0\uff0c\u4e2d\u4e86\u4e00\u62f3\uff0c\u4ed6\u8eab\u5b50\u6447\u4e86\u51e0\u6447\uff0c\u9669\u4e9b\u6454\u5012\u3002\u8ddf\u7740\u7b2c\u4e8c\u540d\u50e7\u4eba\u53c8\u5df2\u4e2d\u62f3\uff0c\u54c7\u7684\u4e00\u58f0\uff0c\u5410\u51fa\u4e00\u53e3\u9c9c\u8840\u3002 \u3000\u3000\u72c4\u4e91\u5927\u5947\uff0c\u5fcd\u4e0d\u4f4f\u5411\u4e01\u5178\u77a7\u53bb\uff0c\u89c1\u4ed6\u500f\u7136\u8dc3\u8fd1\uff0c\u51fb\u51fa\u4e00\u62f3\uff0c\u8fd9\u4e00\u62f3\u65e0\u58f0\u65e0\u5f71\uff0c\u53bb\u52bf\u5feb\u6781\uff0c\u6b63\u4e2d\u7b2c\u4e8c\u540d\u50e7\u4eba\u80f8\u53e3\uff0c\u90a3\u50e7\u4eba\u201c\u554a\u201d\u7684\u4e00\u58f0\u4eba\u53eb\uff0c\u5012\u9000\u51e0\u6b65\uff0c\u649e\u5728\u5899\u4e0a\u3002 \u3000\u3000\u53e6\u5916\u4e24\u540d\u50e7\u4eba\u987a\u7740\u72c4\u4e91\u7684\u76ee\u5149\uff0c\u5411\u8737\u7f29\u5728\u9ed1\u89d2\u843d\u4e2d\u7684\u4e01\u5178\u671b\u53bb\uff0c\u9f50\u58f0\u60ca\u53eb\uff1a\u201c\u795e\u7167\u529f\uff0c\u65e0\u5f71\u795e\u62f3\uff01\u201d\u8eab\u6750\u6781\u9ad8\u7684\u90a3\u50e7\u4e24\u624b\u5404\u62c9\u4e00\u540d\u53d7\u4f24\u50e7\u4eba\uff0c\u4ece\u65e9\u5df2\u6273\u5f00\u7684\u94c1\u6805\u95f4\u9003\u51fa\uff0c\u8d8a\u5899\u800c\u53bb\u3002\u53e6\u4e00\u540d\u50e7\u4eba\u62e6\u8170\u62b1\u4f4f\u5410\u8840\u7684\u50e7\u4eba\uff0c\u56de\u624b\u53d1\u638c\uff0c\u5411\u4e01\u5178\u51fb\u6765\u3002\u4e01\u5178\u62a2\u4e0a\u4e3e\u62f3\u731b\u51fb\u3002\u90a3\u50e7\u4eba\u63a5\u4e86\u4ed6\u4e00\u62f3\uff0c\u5012\u9000\u4e00\u6b65\uff0c\u518d\u63a5\u4e00\u62f3\uff0c\u53c8\u9000\u4e00\u6b65\uff0c\u63a5\u5230\u7b2c\u4e09\u62f3\uff0c\u5df2\u9000\u51fa\u94c1\u6805\u3002 \u3000\u3000\u90a3\u50e7\u8e09\u8e09\u8dc4\u8dc4\u7684\u8d70\u4e86\u51e0\u6b65\uff0c\u53c8\u5012\u9000\u4e86\u4e00\u6b65\uff0c\u8eab\u5b50\u6447\u6643\uff0c\u4f3c\u4e4e\u559d\u9189\u4e86\u4e00\u822c\uff0c\u677e\u624b\u5c06\u5410\u8840\u7684\u50e7\u4eba\u629b\u5728\u5730\u4e0b\uff0c\u4f3c\u6b32\u5355\u8eab\u9003\u547d\uff0c\u4f46\u6bcf\u8de8\u4e00\u6b65\uff0c\u811a\u4e0b\u90fd\u4f3c\u62d6\u4e86\u4e00\u5757\u5343\u65a4\u5de8\u77f3\uff0c\u811a\u6b65\u6c89\u91cd\u4e4b\u6781\uff0c\u6323\u624e\u7740\u8d70\u51fa\u516d\u4e03\u6b65\u540e\uff0c\u547c\u547c\u5598\u6c14\uff0c\u53cc\u817f\u6e10\u6e10\u5f2f\u66f2\uff0c\u6454\u5012\u5728\u5730\uff0c\u518d\u4e5f\u7ad9\u4e0d\u8d77\u6765\u4e86\u3002\u4e24\u540d\u50e7\u4eba\u5728\u5730\u4e0b\u626d\u66f2\u5f97\u51e0\u4e0b\uff0c\u4fbf\u5373\u4e0d\u52a8\u3002 \u3000\u3000\u4e01\u5178\u9053\uff1a\u201c\u53ef\u60dc\uff0c\u53ef\u60dc\uff01\u72c4\u5144\u5f1f\uff0c\u4f60\u82e5\u4e0d\u5411\u6211\u770b\u6765\uff0c\u90a3\u4e2a\u548c\u5c1a\u4fbf\u9003\u4e0d\u4e86\u3002\u201d\u72c4\u4e91\u89c1\u8fd9\u4e24\u4e2a\u50e7\u4eba\u6b7b\u5f97\u51c4\u60e8\uff0c\u5fc3\u4e0b\u4e0d\u5fcd\uff0c\u6697\u60f3\uff1a\u201c\u8ba9\u90a3\u4e09\u4e2a\u9003\u8d70\u4e86\u4e5f\u597d\uff0c\u4e01\u5927\u54e5\u6740\u7684\u4eba\u5b9e\u5728\u592a\u591a\u4e86\u3002\u201d\u4e01\u5178\u9053\uff1a\u201c\u4f60\u5acc\u6211\u51fa\u624b\u592a\u8fa3\u4e86\uff0c\u662f\u4e0d\u662f\uff1f\u201d\u72c4\u4e91\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\u2026\u2026\u201d\u731b\u5730\u91cc\u5589\u5934\u585e\u4f4f\uff0c\u4e00\u4ea4\u5750\u5012\uff0c\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u4e01\u5178\u5fd9\u7ed9\u4ed6\u63a8\u5bab\u8fc7\u8840\uff0c\u6309\u6469\u4e86\u826f\u4e45\uff0c\u4ed6\u80f8\u53e3\u7684\u6c14\u585e\u65b9\u624d\u8212\u7545\u3002 \u3000\u3000\u4e01\u5178\u9053\uff1a\u201c\u4f60\u5acc\u6211\u8fa3\u624b\uff0c\u53ef\u662f\u90a3\u4e24\u4e2a\u6076\u50e7\u4e00\u4e0a\u6765\u4fbf\u5411\u4f60\u5404\u51fb\u4e00\u638c\uff0c\u82e5\u4e0d\u662f\u4f60\u8eab\u4e0a\u7a7f\u7740\u4e4c\u8695\u8863\uff0c\u65e9\u5c31\u4e00\u547d\u545c\u547c\u4e86\u3002\u54ce\uff01\u8fd9\u4e8b\u505a\u54e5\u54e5\u7684\u592a\u8fc7\u758f\u5ffd\uff0c\u54ea\u60f3\u5230\u4ed6\u4eec\u4e00\u4e0a\u6765\u4fbf\u4f1a\u52a8\u624b\u3002\u6211\u731c\u60f3\u4ed6\u4eec\u5b9a\u8981\u5148\u903c\u95ee\u4e00\u756a\u3002\u55ef\uff0c\u662f\u7684\uff0c\u4ed6\u4eec\u5bf9\u6211\u5341\u5206\u5fcc\u60ee\uff0c\u8981\u5c06\u6211\u5148\u6253\u5f97\u91cd\u4f24\uff0c\u8fd9\u624d\u903c\u95ee\u3002\u201d \u3000\u3000\u4ed6\u62b9\u53bb\u72c4\u4e91\u816e\u4e0a\u7684\u80e1\u5b50\uff0c\u7b11\u9053\uff1a\u201c\u90a3\u8d3c\u79c3\u5413\u5f97\u5fc3\u80c6\u4ff1\u88c2\uff0c\u518d\u4e5f\u4e0d\u6562\u6765\u60f9\u54b1\u4eec\u4e86\u3002\u201d\u4ed6\u53c8\u6b63\u8272\u9053\uff1a\u201c\u72c4\u5144\u5f1f\uff0c\u90a3\u9003\u8d70\u4e86\u7684\u9ad8\u4e2a\u5b50\u548c\u5c1a\uff0c\u53eb\u505a\u5b9d\u8c61\u3002\u90a3\u80d6\u80d6\u7684\u53eb\u505a\u5584\u52c7\u3002\u6211\u7b2c\u4e00\u62f3\u6253\u5012\u7684\u90a3\u4e2a\u6700\u5389\u5bb3\uff0c\u53eb\u505a\u80dc\u8c1b\u3002\u8fd9\u4e94\u4e2a\u548c\u5c1a\u90fd\u662f\u897f\u85cf\u2018\u8840\u5200\u95e8\u2019\u7684\u9ad8\u624b\uff0c\u6211\u82e5\u4e0d\u662f\u6697\u4e2d\u4f0f\u51fb\u5f97\u624b\uff0c\u4ee5\u4e00\u654c\u4e94\uff0c\u53ea\u6015\u6597\u4ed6\u4eec\u4e0d\u8fc7\u3002 \u3000\u3000\u5584\u52c7\u548c\u80dc\u8c1b\u90fd\u5df2\u4e2d\u4e86\u6211\u7684\u795e\u62f3\uff0c\u5c31\u7b97\u4e00\u65f6\u4e0d\u6b7b\uff0c\u4e5f\u6d3b\u4e0d\u4e86\u51e0\u5929\u3002\u5269\u4e0b\u7684\u90a3\u5b9d\u8c61\u5fc3\u72e0\u624b\u8fa3\uff0c\u65e5\u540e\u4f60\u5982\u5728\u6c5f\u6e56\u4e0a\u9047\u4e0a\u4e86\uff0c\u52a1\u987b\u5c0f\u5fc3\u5728\u610f\u3002\u201d\u6c89\u541f\u534a\u664c\uff0c\u53c8\u9053\uff1a\u201c\u542c\u8bf4\u8fd9\u4e94\u50e7\u7684\u5e08\u7236\u5c1a\u5728\u4eba\u4e16\uff0c\u6b66\u529f\u66f4\u662f\u5389\u5bb3\u4e4b\u6781\uff0c\u5c06\u6765\u5012\u8981\u8ddf\u4ed6\u6597\u6597\u3002\u201d \u3000\u3000\u72c4\u4e91\u867d\u6709\u5b9d\u8863\u62a4\u8eab\uff0c\u4f46\u524d\u80f8\u540e\u80cc\u540c\u53d7\u5939\u51fb\uff0c\u53d7\u4f24\u4e5f\u9887\u4e0d\u8f7b\uff0c\u5728\u4e01\u5178\u6307\u70b9\u4e0b\u8fd0\u4e86\u5341\u51e0\u5929\u529f\uff0c\u53c8\u5f97\u4e01\u5178\u6bcf\u65e5\u4ee5\u5185\u529b\u76f8\u52a9\uff0c\u8fd9\u624d\u75ca\u53ef\u3002 \u3000\u3000\u6b64\u540e\u4e24\u5e74\u591a\u7684\u65e5\u5b50\u8fc7\u5f97\u751a\u662f\u5e73\u9759\uff0c\u5076\u5c14\u6709\u4e00\u4e24\u4e2a\u6c5f\u6e56\u4eba\u7269\u5230\u72f1\u4e2d\u6765\u7f57\u5523\uff0c\u4e01\u5178\u4e0d\u662f\u4e00\u6293\uff0c\u4fbf\u662f\u4e00\u62f3\uff0c\u9877\u523b\u95f4\u4fbf\u9001\u4e86\u4ed6\u4eec\u6027\u547d\u3002 \u3000\u3000\u8fd1\u51e0\u4e2a\u6708\u6765\u72c4\u4e91\u4fee\u4e60\u795e\u7167\u529f\uff0c\u8fdb\u6b65\u4f3c\u662f\u505c\u6ede\u4e86\uff0c\u7ec3\u6765\u7ec3\u53bb\uff0c\u548c\u51e0\u4e2a\u6708\u524d\u4ecd\u662f\u4e00\u6837\u3002\u597d\u5728\u4ed6\u609f\u6027\u867d\u7136\u4e0d\u9ad8\uff0c\u751f\u6027\u5374\u6781\u575a\u6bc5\uff0c\u77e5\u9053\u8fd9\u7b49\u9ad8\u6df1\u5185\u529f\u51b3\u975e\u8f7b\u6613\u5f97\u80fd\u7ec3\u6210\uff0c\u5728\u4e01\u5178\u6307\u70b9\u4e0b\u65e5\u5915\u8010\u5fc3\u4fee\u4e60\uff0c\u4ee5\u671f\u7a81\u7834\u96be\u5173\u3002 \u3000\u3000\u8fd9\u4e00\u65e5\u65e9\u6668\u9192\u6765\uff0c\u4ed6\u4fa7\u8eab\u800c\u5367\uff0c\u8138\u5411\u5899\u58c1\uff0c\u4f9d\u6cd5\u5410\u7eb3\uff0c\u5ffd\u542c\u5f97\u4e01\u5178\u201c\u54a6\u201d\u7684\u4e00\u58f0\uff0c\u58f0\u97f3\u4e2d\u9887\u6709\u7126\u8651\u4e4b\u610f\uff0c\u8fc7\u5f97\u534a\u664c\uff0c\u53c8\u542c\u4ed6\u81ea\u8a00\u81ea\u8bed\uff1a\u201c\u4eca\u5929\u662f\u4e0d\u4f1a\u8c22\u7684\uff0c\u660e\u5929\u518d\u6362\u4e5f\u4e0d\u8fdf\u3002\u201d\u72c4\u4e91\u6709\u4e9b\u8be7\u5f02\uff0c\u8f6c\u8fc7\u8eab\u6765\uff0c\u53ea\u89c1\u4ed6\u62ac\u8d77\u4e86\u5934\uff0c\u6b63\u51dd\u671b\u7740\u8fdc\u5904\u7a97\u69db\u4e0a\u7684\u90a3\u53ea\u82b1\u76c6\u3002 \u3000\u3000\u72c4\u4e91\u81ea\u7ec3\u795e\u7167\u529f\u540e\uff0c\u8033\u76ee\u6bd4\u4e4b\u5f80\u65e5\u5df2\u8fdc\u4e3a\u7075\u654f\uff0c\u4e00\u77a7\u4e4b\u4e0b\uff0c\u4fbf\u89c1\u76c6\u4e2d\u4e09\u6735\u9ec4\u8537\u8587\u4e2d\uff0c\u6709\u4e00\u6735\u7f3a\u4e86\u4e00\u7247\u82b1\u74e3\u3002\u4ed6\u65e5\u5e38\u603b\u89c1\u4e01\u5178\u51dd\u671b\u8fd9\u76c6\u4e2d\u7684\u9c9c\u82b1\uff0c\u5446\u5446\u51fa\u795e\uff0c\u6570\u5e74\u5982\u4e00\u65e5\uff0c\u5fc3\u60f3\u72f1\u4e2d\u65e0\u53ef\u9063\u5174\uff0c\u552f\u6709\u8fd9\u4e00\u76c6\u82b1\u957f\u4fdd\u9c9c\u8273\uff0c\u4e01\u5178\u559c\u7231\u6b23\u8d4f\uff0c\u90a3\u4e5f\u4e0d\u8db3\u4e3a\u5947\u3002\u53ea\u662f\u90a3\u82b1\u76c6\u4e2d\u7684\u9c9c\u82b1\u82e5\u975e\u542b\u82de\u5f85\u653e\uff0c\u4fbf\u662f\u8fce\u65e5\u76db\u5f00\uff0c\u4e0d\u7b49\u6709\u4e00\u74e3\u6b8b\u8c22\uff0c\u4fbf\u5373\u6362\u8fc7\u3002\u6625\u98ce\u8309\u8389\uff0c\u79cb\u6708\u6d77\u68e0\uff0c\u65e5\u65e5\u591c\u591c\uff0c\u603b\u662f\u6709\u4e00\u76c6\u9c9c\u82b1\u653e\u5728\u7a97\u69db\u4e4b\u4e0a\u3002\u72c4\u4e91\u8bb0\u5f97\u8fd9\u76c6\u9ec4\u8537\u8587\u5df2\u653e\u4e86\u516d\u4e03\u5929\uff0c\u5e73\u65f6\u65e9\u5c31\u6362\u8fc7\u4e86\uff0c\u4f46\u8fd9\u6b21\u5374\u4e00\u76f4\u6ca1\u6362\u3002 \u3000\u3000\u8fd9\u4e00\u65e5\u4e01\u5178\u81ea\u65e9\u5230\u665a\uff0c\u5fc3\u7eea\u70e6\u8e81\u4e0d\u5b81\u3002\u5230\u5f97\u6b21\u65e5\u65e9\u6668\uff0c\u90a3\u76c6\u9ec4\u8537\u8587\u4ecd\u662f\u6ca1\u6362\uff0c\u6709\u4e94\u516d\u7247\u82b1\u74e3\u5df2\u88ab\u98ce\u5439\u53bb\u3002\u72c4\u4e91\u5fc3\u4e0b\u9690\u9690\u611f\u5230\u4e0d\u7965\u4e4b\u610f\uff0c\u89c1\u4e01\u5178\u795e\u8272\u6781\u662f\u96be\u770b\uff0c\u4fbf\u9053\uff1a\u201c\u8fd9\u4eba\u8fd9\u4e00\u6b21\u5fd8\u4e86\u6362\u82b1\uff0c\u60f3\u5fc5\u4e0b\u5348\u4f1a\u8bb0\u5f97\u3002\u201d \u3000\u3000\u4e01\u5178\u5927\u58f0\u9053\uff1a\u201c\u600e\u4e48\u4f1a\u5fd8\u8bb0\uff1f\u51b3\u4e0d\u4f1a\u7684\uff01\u96be\u9053\u2026\u2026\u96be\u9053\u662f\u751f\u4e86\u75c5\uff1f\u5c31\u7b97\u662f\u751f\u4e86\u75c5\uff0c\u4e5f\u4f1a\u53eb\u4eba\u6765\u6362\u82b1\u554a\uff01\u201d\u4e0d\u505c\u6b65\u7684\u8d70\u6765\u8d70\u53bb\uff0c\u795e\u8272\u4e0d\u5b89\u5df2\u6781\u3002 \u3000\u3000\u72c4\u4e91\u4e0d\u6562\u591a\u95ee\uff0c\u4fbf\u5373\u76d8\u819d\u5750\u4e0b\uff0c\u5165\u9759\u7ec3\u529f\u3002 \u3000\u3000\u5230\u5f97\u508d\u665a\uff0c\u9634\u4e91\u56db\u5408\uff0c\u4e0d\u4e45\u4fbf\u6dc5\u6dc5\u6ca5\u6ca5\u7684\u4e0b\u8d77\u96e8\u6765\uff0c\u4e00\u9635\u5bd2\u98ce\u8fc7\u53bb\uff0c\u4e09\u6735\u9ec4\u8537\u8587\u4e0a\u7684\u82b1\u74e3\u53c8\u98d8\u4e86\u6570\u7247\u4e0b\u6765\u3002\u4e01\u5178\u8fd9\u51e0\u4e2a\u65f6\u8fb0\u4e4b\u4e2d\uff0c\u4e00\u76f4\u76ee\u4e0d\u8f6c\u775b\u7684\u671b\u7740\u8fd9\u76c6\u82b1\uff0c\u6bcf\u98d8\u843d\u4e00\u7247\u82b1\u74e3\uff0c\u4ed6\u603b\u662f\u8138\u4e0a\u808c\u8089\u626d\u52a8\uff0c\u795e\u8272\u51c4\u695a\uff0c\u4fbf\u5982\u662f\u5728\u4ed6\u8eab\u4e0a\u525c\u53bb\u4e00\u5757\u8089\u90a3\u4e48\u96be\u53d7\u3002 \u3000\u3000\u72c4\u4e91\u518d\u4e5f\u5fcd\u8010\u4e0d\u4f4f\uff0c\u95ee\u9053\uff1a\u201c\u4e01\u5927\u54e5\uff0c\u4f60\u4e3a\u751a\u4e48\u8fd9\u6837\u4e0d\u5b89\uff1f\u201d \u3000\u3000\u4e01\u5178\u8f6c\u8fc7\u5934\u6765\uff0c\u6ee1\u8138\u6012\u5bb9\uff0c\u559d\u9053\uff1a\u201c\u5173\u4f60\u751a\u4e48\u4e8b\uff1f\u7f57\u55e6\u751a\u4e48\uff1f\u201d \u3000\u3000\u81ea\u4ece\u4ed6\u4f20\u6388\u72c4\u4e91\u6b66\u529f\u4ee5\u6765\uff0c\u4ece\u672a\u5982\u6b64\u51f6\u72e0\u65e0\u793c\u3002\u72c4\u4e91\u751a\u611f\u6b49\u4ec4\uff0c\u5f85\u8981\u8bf4\u51e0\u53e5\u751a\u4e48\u8bdd\u5206\u89e3\uff0c\u5374\u89c1\u4ed6\u8138\u4e0a\u6e10\u6e10\u73b0\u51fa\u51c4\u51c9\u4e4b\u610f\uff0c\u663e\u7136\u5fc3\u4e2d\u751a\u662f\u60b2\u75db\uff0c\u4fbf\u4f4f\u4e86\u53e3\u3002 \u3000\u3000\u8fd9\u4e00\u665a\u4e01\u5178\u7adf\u4e00\u606f\u4e5f\u6ca1\u5750\u4e0b\u3002\u72c4\u4e91\u542c\u7740\u4ed6\u8d70\u6765\u8d70\u53bb\uff0c\u94d0\u9563\u4e0a\u4e0d\u4f4f\u53d1\u51fa\u53ee\u53ee\u5f53\u5f53\u7684\u58f0\u54cd\uff0c\u4e5f\u662f\u65e0\u6cd5\u5165\u7761\u3002 \u3000\u3000\u6b21\u65e5\u6e05\u6668\uff0c\u659c\u98ce\u7ec6\u96e8\uff0c\u5140\u81ea\u672a\u606f\u3002\u66d9\u8272\u6726\u80e7\u4e2d\u770b\u90a3\u76c6\u82b1\u65f6\uff0c\u53ea\u89c1\u4e09\u6735\u8537\u8587\u7684\u82b1\u74e3\u5df2\u7136\u843d\u5c3d\uff0c\u76c6\u4e2d\u552f\u4f59\u51e0\u6839\u82b1\u679d\uff0c\u5728\u98ce\u96e8\u4e2d\u4e0d\u4f4f\u98a4\u52a8\u3002 \u3000\u3000\u4e01\u5178\u5927\u53eb\uff1a\u201c\u6b7b\u4e86\uff1f\u6b7b\u4e86\uff1f\u4f60\u771f\u7684\u6b7b\u4e86\uff1f\u201d\u53cc\u624b\u6293\u4f4f\u94c1\u6805\uff0c\u4e0d\u4f4f\u6447\u6643\u3002 \u3000\u3000\u72c4\u4e91\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4f60\u82e5\u662f\u8bb0\u6302\u7740\u8c01\uff0c\u54b1\u4eec\u4fbf\u53bb\u77a7\u77a7\u3002\u201d\u4e01\u5178\u4e00\u58f0\u864e\u543c\uff0c\u559d\u9053\uff1a\u201c\u77a7\uff01\u80fd\u53bb\u77a7\u4e48\uff1f\u6211\u82e5\u80fd\u53bb\uff0c\u65e9\u5c31\u53bb\u4e86\uff0c\u7528\u5f97\u7740\u5728\u8fd9\u81ed\u7262\u623f\u4e2d\u82e6\u8017\uff1f\u201d\u72c4\u4e91\u4e0d\u660e\u6240\u4ee5\uff0c\u7741\u5927\u4e86\u773c\uff0c\u53ea\u597d\u9ed8\u4e0d\u4f5c\u58f0\u3002\u8fd9\u4e00\u65e5\u4e2d\uff0c\u4e01\u5178\u53cc\u624b\u62b1\u4f4f\u4e86\u5934\uff0c\u5750\u5728\u5730\u4e0b\u4e0d\u8a00\u4e0d\u52a8\uff0c\u4e0d\u5403\u4e0d\u559d\u3002 \u3000\u3000\u8033\u542c\u5f97\u6253\u66f4\u58f0\u201c\u7684\u7b03\uff0c\u7684\u7b03\uff0c\u5f53\u201d\u7684\u6253\u8fc7\u4e00\u66f4\u3002\u5bc2\u9759\u4e2d\u65f6\u5149\u6d41\u8fc7\uff0c\u4e8e\u662f\u201c\u7684\u7b03\uff0c\u7684\u7b03\uff0c\u5f53\u5f53\u201d\u7684\u6253\u8fc7\u4e8c\u66f4\u3002 \u3000\u3000\u4e01\u5178\u7f13\u7f13\u7ad9\u8d77\u8eab\u6765\uff0c\u9053\uff1a\u201c\u5144\u5f1f\uff0c\u54b1\u4eec\u53bb\u77a7\u77a7\u7f62\u3002\u201d\u8bdd\u58f0\u751a\u662f\u5e73\u9759\u3002\u72c4\u4e91\u9053\uff1a\u201c\u662f\u3002\u201d\u4e01\u5178\u4f38\u51fa\u624b\u53bb\uff0c\u6293\u4f4f\u4e24\u6839\u94c1\u6805\uff0c\u8f7b\u8f7b\u5f80\u4e24\u65c1\u4e00\u5206\uff0c\u4e24\u6839\u94c1\u6805\u767b\u65f6\u4fbf\u5f2f\u4e86\u3002\u4e01\u5178\u9053\uff1a\u201c\u63d0\u4f4f\u94c1\u94fe\uff0c\u522b\u53d1\u51fa\u54cd\u58f0\u3002\u201d\u201d\u72c4\u4e91\u4f9d\u8a00\u6293\u8d77\u94c1\u94fe\u3002 \u3000\u3000\u4e01\u5178\u8d70\u5230\u5899\u8fb9\uff0c\u63d0\u6c14\u4e00\u7eb5\uff0c\u4fbf\u5373\u7a9c\u4e0a\u4e86\u5899\u5934\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u8df3\u4e0a\u6765\uff01\u201d\u72c4\u4e91\u5b66\u7740\u4ed6\u5411\u4e0a\u4e00\u7a9c\uff0c\u4e0d\u6599\u7ed9\u7a7f\u901a\u7435\u7436\u9aa8\u540e\uff0c\u5168\u8eab\u52b2\u529b\u534a\u70b9\u4e5f\u4f7f\u4e0d\u51fa\u6765\uff0c\u4ed6\u8fd9\u4e00\u8dc3\uff0c\u53ea\u4e0d\u8fc7\u7a9c\u8d77\u4e09\u5c3a\u3002\u4e01\u5178\u4f38\u624b\u4e00\u6293\uff0c\u5c06\u4ed6\u5e26\u4e0a\u4e86\u5899\u5934\uff0c\u4e24\u4eba\u540c\u65f6\u8dc3\u4e0b\u3002 \u3000\u3000\u8fc7\u4e86\u8fd9\u5835\u5899\uff0c\u7262\u72f1\u5916\u53e6\u6709\u4e00\u5835\u6781\u9ad8\u548c\u9ad8\u5899\uff0c\u4e01\u5178\u6216\u80fd\u4e0a\u5f97\uff0c\u72c4\u4e91\u5374\u65e0\u8bba\u5982\u4f55\u65e0\u6cd5\u903e\u8d8a\u3002\u4e01\u5178\u54fc\u4e86\u4e00\u58f0\uff0c\u5c06\u80cc\u810a\u9760\u5728\u5899\u4e0a\u3002\u4f46\u542c\u745f\u745f\u745f\u4e00\u9635\u6ce5\u6c99\u6563\u843d\u7684\u8f7b\u54cd\u8fc7\u53bb\uff0c\u7816\u77f3\u7eb7\u7eb7\u8dcc\u843d\u3002 \u3000\u3000\u72c4\u4e91\u53cc\u773c\u4e00\u82b1\uff0c\u53ea\u89c1\u5899\u4e0a\u73b0\u51fa\u4e86\u4e00\u4e2a\u5927\u6d1e\uff0c\u4e01\u5178\u5df2\u7136\u4e0d\u89c1\u3002\u539f\u6765\u4ed6\u7adf\u4ee5\u795e\u7167\u529f\u7684\u7edd\u9876\u5185\u529f\uff0c\u7834\u5899\u800c\u51fa\u3002\u72c4\u4e91\u53c8\u60ca\u53c8\u559c\uff0c\u5fd9\u4ece\u5899\u6d1e\u4e2d\u94bb\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u5916\u9762\u662f\u6761\u5c0f\u5df7\u3002\u4e01\u5178\u5411\u4ed6\u62db\u62db\u624b\uff0c\u4ece\u5c0f\u5df7\u7684\u5c3d\u5934\u8d70\u53bb\u3002\u51fa\u5c0f\u5df7\u540e\u4fbf\u662f\u8857\u9053\u3002\u4e01\u5178\u5bf9\u8346\u5dde\u57ce\u4e2d\u7684\u8857\u5df7\u4f3c\u4e4e\u6781\u662f\u719f\u6089\uff0c\u8fc7\u4e86\u4e00\u6761\u8857\uff0c\u7a7f\u8fc7\u4e24\u6761\u5df7\u5b50\uff0c\u6765\u5230\u4e00\u5bb6\u94c1\u5e97\u95e8\u9996\u3002 \u3000\u3000\u4e01\u5178\u4e3e\u624b\u4e00\u63a8\uff0c\u62cd\u7684\u4e00\u58f0\uff0c\u95e9\u4f4f\u5927\u95e8\u7684\u95e8\u95e9\u5df2\u7136\u5d29\u65ad\u3002\u5e97\u91cc\u7684\u94c1\u5320\u5403\u4e86\u4e00\u60ca\uff0c\u8df3\u8d77\u8eab\u6765\u53eb\u9053\uff1a\u201c\u6709\u8d3c\uff01\u201d\u4e01\u5178\u4e00\u628a\u53c9\u4f4f\u4ed6\u5589\u5499\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u751f\u706b\uff01\u201d \u3000\u3000\u90a3\u94c1\u5320\u4e0d\u6562\u8fdd\u62d7\uff0c\u70b9\u4eae\u4e86\u706f\uff0c\u773c\u89c1\u4e8c\u4eba\u90fd\u662f\u957f\u53d1\u5782\u80a9\uff0c\u6ee1\u8138\u80e1\u5b50\uff0c\u6a21\u6837\u51f6\u6076\u6015\u4eba\uff0c\u54ea\u91cc\u8fd8\u6562\u52a8\u5f39\uff1f\u4e01\u5178\u9053\uff1a\u201c\u628a\u6211\u4eec\u7684\u94d0\u9563\u51ff\u5f00\uff01\u201d \u3000\u3000\u90a3\u94c1\u5320\u6599\u5f97\u4e8c\u4eba\u662f\u8859\u95e8\u4e2d\u8d8a\u72f1\u7684\u91cd\u72af\uff0c\u82e5\u66ff\u4ed6\u4eec\u51ff\u65ad\u94d0\u9563\uff0c\u5b98\u5e9c\u8ffd\u7a76\u8d77\u6765\uff0c\u5b9a\u8981\u4e25\u529e\uff0c\u4e0d\u7981\u8fdf\u7591\u3002\u4e01\u5178\u968f\u624b\u6293\u8d77\u4e00\u6839\u5f84\u5bf8\u7c97\u7684\u94c1\u6761\uff0c\u6765\u56de\u62d7\u5f97\u51e0\u4e0b\uff0c\u62cd\u7684\u4e00\u58f0\uff0c\u6298\u4e3a\u4e24\u8f7d\uff0c\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u9888\u5b50\uff0c\u6709\u8fd9\u822c\u786c\u4e48\uff1f\u201d \u3000\u3000\u90a3\u94c1\u5320\u8fd8\u9053\u662f\u9047\u5230\u4e86\u9b3c\u795e\uff0c\u4ed6\u8981\u5f04\u65ad\u8fd9\u94c1\u6761\uff0c\u4f7f\u5230\u94a2\u51ff\u5927\u9524\uff0c\u4e5f\u5f97\u6405\u4e0a\u597d\u4e00\u4f1a\u513f\uff0c\u8fd9\u5927\u6c49\u5374\u4e3e\u624b\u95f4\u4fbf\u5c06\u94c1\u6761\u62d7\u65ad\uff0c\u5018\u82e5\u6765\u62d7\u81ea\u5df1\u5934\u9888\uff0c\u90a3\u53ef\u4e07\u4e07\u4e0d\u59a5\uff0c\u5f53\u4e0b\u8fde\u58f0\uff1a\u201c\u662f\uff0c\u662f\uff01\u201d\u53d6\u51fa\u94a2\u51ff\u3001\u94c1\u9524\uff0c\u5148\u66ff\u4e01\u5178\u51ff\u5f00\u4e86\u94d0\u9563\uff0c\u53c8\u6682\u72c4\u4e91\u51ff\u5f00\u3002 \u3000\u3000\u4e01\u5178\u5148\u5c06\u81ea\u5df1\u7435\u7436\u9aa8\u4e2d\u7684\u94c1\u94fe\u62c9\u51fa\u3002\u5f53\u4ed6\u628a\u94c1\u94fe\u4ece\u72c4\u4e91\u80a9\u5934\u7684\u7435\u7436\u9aa8\u4e2d\u62c9\u51fa\u6765\u65f6\uff0c\u72c4\u4e91\u75db\u5f97\u9669\u4e9b\u6655\u53bb\u3002 \u3000\u3000\u7ec8\u4e8e\u72c4\u4e91\u53cc\u624b\u6367\u7740\u90a3\u6761\u6cbe\u6ee1\u9c9c\u8840\u7684\u94c1\u94fe\uff0c\u7ad9\u5728\u94c1\u7827\u4e4b\u524d\uff0c\u60f3\u5230\u5728\u8fd9\u6839\u94c1\u94fe\u7684\u675f\u7f1a\u4e4b\u4e0b\uff0c\u5728\u6697\u65e0\u5929\u65e5\u7684\u7262\u72f1\u4e2d\u82e6\u6e21\u4e94\u5e74\u591a\u65f6\u5149\uff0c\u76f4\u5230\u4eca\u65e5\uff0c\u94c1\u94fe\u65b9\u59cb\u79bb\u8eab\uff0c\u4e0d\u7531\u5f97\u53c8\u662f\u6b22\u559c\uff0c\u53c8\u662f\u4f24\u5fc3\uff0c\u6014\u6014\u7684\u6389\u4e0b\u6cea\u6765\u3002 \u3000\u3000\u4ed6\u968f\u7740\u4e01\u5178\u8d70\u51fa\u94c1\u5e97\u3002\u4ed6\u4e4d\u8131\u94d0\u50da\uff0c\u8d70\u8d77\u8def\u6765\u8f7b\u98d8\u98d8\u7684\uff0c\u5341\u5206\u4e0d\u60ef\uff0c\u51e0\u6b21\u5934\u91cd\u811a\u8f7b\uff0c\u9669\u4e9b\u513f\u6454\u5012\uff0c\u7136\u89c1\u4e01\u5178\u811a\u6b65\u6c89\u7a33\uff0c\u8d8a\u8d70\u8d8a\u5feb\uff0c\u5f53\u4e0b\u7d27\u7d27\u8ddf\u968f\uff0c\u751f\u6015\u9ed1\u6697\u4e2d\u548c\u4ed6\u79bb\u5f97\u592a\u8fdc\u3002 \u3000\u3000\u7247\u523b\u4e4b\u95f4\uff0c\u4e24\u4eba\u5df2\u6765\u5230\u90a3\u653e\u7f6e\u82b1\u76c6\u7684\u7a97\u4e0b\u3002\u4e01\u5178\u4ef0\u8d77\u4e86\u5934\uff0c\u72b9\u8c6b\u534a\u664c\uff0c\u4f3c\u4e4e\u60f3\u8981\u8fdb\u53bb\uff0c\u5374\u53c8\u4e0d\u613f\u3002\u72c4\u4e91\u89c1\u7a97\u6237\u7d27\u95ed\uff0c\u697c\u4e2d\u5bc2\u7136\u65e0\u58f0\uff0c\u9053\uff1a\u201c\u6211\u5148\u53bb\u77a7\u77a7\u3002\u597d\u4e48\uff1f\u201d\u4e01\u5178\u70b9\u70b9\u5934\u3002 \u3000\u3000\u72c4\u4e91\u7ed5\u5230\u5c0f\u697c\u95e8\u524d\uff0c\u4f38\u624b\u63a8\u95e8\uff0c\u53d1\u89c9\u95e8\u5185\u4e0a\u4e86\u95e9\u3002\u597d\u5728\u56f4\u5899\u751a\u4f4e\uff0c\u4e00\u682a\u67f3\u6811\u7684\u679d\u4e2b\u4ece\u5899\u5185\u4f38\u4e86\u51fa\u6765\uff0c\u4ed6\u5fae\u4e00\u7eb5\u8eab\uff0c\u4fbf\u5df2\u6293\u4f4f\u679d\u4e2b\uff0c\u7ffb\u8eab\u8fdb\u4e86\u56f4\u5899\u3002\u91cc\u9762\u4e00\u6247\u5c0f\u95e8\u5374\u662f\u865a\u63a9\u7740\u7684\u3002\u72c4\u4e91\u63a8\u95e8\u5165\u5185\uff0c\u62fe\u7ea7\u4e0a\u697c\uff0c\u9ed1\u6697\u4e2d\u542c\u5f97\u697c\u68af\u53d1\u51fa\u8f7b\u5fae\u7684\u5431\u5431\u4e4b\u58f0\uff0c\u811a\u4e0b\u53ea\u89c9\u865a\u6d6e\u6d6e\u7684\uff0c\u751a\u4e0d\u81ea\u5728\u3002\u4ed6\u5728\u8fd9\u4e94\u5e74\u591a\u4e4b\u4e2d\uff0c\u6574\u65e5\u6574\u591c\u4fbf\u5728\u4e00\u95f4\u72f1\u5ba4\u4e2d\u8d70\u52a8\uff0c\u4ece\u672a\u8e0f\u8fc7\u4e00\u6b65\u68af\u7ea7\u3002 \u3000\u3000\u5230\u5f97\u697c\u9876\uff0c\u4fa7\u8033\u9759\u542c\uff0c\u7edd\u65e0\u534a\u70b9\u58f0\u606f\uff0c\u6726\u80e7\u5fae\u5149\u4e2d\u89c1\u5de6\u9996\u6709\u95e8\uff0c\u4fbf\u8f7b\u8f7b\u8d70\u4e86\u8fdb\u53bb\uff0c\u623f\u4e2d\u8fde\u547c\u5438\u4e4b\u58f0\u4e5f\u65e0\u3002\u9690\u9690\u7ea6\u7ea6\u95f4\u89c1\u684c\u4e0a\u6709\u4e00\u70db\u53f0\uff0c\u4f38\u624b\u5728\u684c\u4e0a\u6478\u5230\u706b\u5200\u706b\u77f3\uff0c\u6253\u706b\u70b9\u71c3\u8721\u70db\uff0c\u70db\u5149\u7167\u6620\u4e4b\u4e0b\uff0c\u7a81\u7136\u95f4\u611f\u5230\u4e00\u9635\u8bf4\u4e0d\u51fa\u7684\u5bc2\u5bde\u51c4\u51c9\u4e4b\u610f\u3002 \u3000\u3000\u5ba4\u4e2d\u7a7a\u7a7a\u6d1e\u6d1e\uff0c\u9664\u4e86\u4e00\u684c\u3001\u4e00\u6905\u3001\u4e00\u5e8a\u4e4b\u5916\uff0c\u751a\u4e48\u4e1c\u897f\u4e5f\u6ca1\u6709\u3002\u5e8a\u4e0a\u6302\u7740\u4e00\u9876\u590f\u5e03\u767d\u5e10\u5b50\uff0c\u4e00\u5e8a\u8584\u88ab\uff0c\u4e00\u4e2a\u5e03\u6795\uff0c\u5e8a\u811a\u8fb9\u653e\u7740\u4e00\u53cc\u9752\u5e03\u5973\u978b\u3002\u53ea\u6709\u8fd9\u4e00\u53cc\u5973\u978b\uff0c\u624d\u663e\u5f97\u8fd9\u623f\u95f4\u539f\u4e3a\u4e00\u4e2a\u5973\u5b50\u6240\u4f4f\u3002 \u3000\u3000\u4ed6\u5446\u4e86\u4e00\u5446\uff0c\u8d70\u5230\u7b2c\u4e8c\u95f4\u623f\u4e2d\u53bb\u770b\u65f6\uff0c\u90a3\u8fb9\u7adf\u8fde\u684c\u6905\u4e5f\u6ca1\u4e00\u5f20\u3002\u53ef\u662f\u77a7\u90a3\u6a21\u6837\uff0c\u5374\u53c8\u4e0d\u662f\u65b0\u8fd1\u642c\u8d70\u4e86\u5bb6\u751f\u7528\u5177\uff0c\u800c\u662f\u8bb8\u591a\u5e74\u6765\u4e00\u76f4\u4fbf\u5982\u6b64\u7a7a\u65e0\u6240\u6709\u3002\u62fe\u7ea7\u6765\u5230\u697c\u4e0b\uff0c\u6bcf\u4e00\u5904\u90fd\u53bb\u67e5\u770b\u4e86\u4e00\u904d\uff0c\u7adf\u662f\u4e00\u4e2a\u4eba\u4e5f\u65e0\u3002 \u3000\u3000\u4ed6\u9690\u9690\u89c9\u5f97\u4e0d\u59a5\uff0c\u51fa\u6765\u544a\u77e5\u4e01\u5178\u3002\u4e01\u5178\u9053\uff1a\u201c\u751a\u4e48\u4e1c\u897f\u4e5f\u6ca1\u6709\uff1f\u201d\u72c4\u4e91\u6447\u4e86\u6447\u5934\u3002\u4e01\u5178\u4f3c\u4e4e\u5bf9\u8fd9\u60c5\u666f\u65e9\u5728\u610f\u6599\u4e4b\u4e2d\uff0c\u6beb\u4e0d\u60ca\u5947\uff0c\u9053\uff1a\u201c\u5230\u53e6\u4e00\u4e2a\u5730\u65b9\u53bb\u77a7\u77a7\u3002\u201d \u3000\u3000\u90a3\u53e6\u4e00\u4e2a\u5730\u65b9\u5374\u662f\u4e00\u5ea7\u5927\u53a6\uff0c\u6731\u7ea2\u7684\u5927\u95e8\uff0c\u95e8\u4e0a\u9489\u7740\u7897\u53e3\u5927\u7684\u94dc\u9489\uff0c\u95e8\u5916\u4e24\u76cf\u5927\u706f\u7b3c\uff0c\u4e00\u76cf\u5199\u7740\u201c\u8346\u5dde\u5e9c\u6b63\u5802\u201d\uff0c\u53e6\u4e00\u76cf\u5199\u7740\u201c\u51cc\u5e9c\u201d\u3002\u72c4\u4e91\u5fc3\u4e2d\u4e00\u60ca\uff1a\u201c\u8fd9\u662f\u8346\u5dde\u5e9c\u51cc\u77e5\u5e9c\u7684\u5bd3\u6240\uff0c\u4e01\u5927\u54e5\u5230\u6b64\u4f5c\u751a\uff1f\u662f\u8981\u6740\u4ed6\u4e48\uff1f\u201d \u3000\u3000\u4e01\u5178\u63e1\u7740\u4ed6\u624b\uff0c\u4e00\u8a00\u4e0d\u53d1\u7684\u8d8a\u5899\u800c\u8fdb\u3002\u4ed6\u5bf9\u51cc\u5e9c\u4e2d\u7684\u95e8\u6237\u751a\u662f\u719f\u6089\uff0c\u7a7f\u5eca\u8fc7\u6237\uff0c\u4fbf\u4f3c\u662f\u5728\u81ea\u5df1\u5bb6\u4e2d\u884c\u8d70\u4e00\u822c\u3002\u8fc7\u4e86\u4e24\u6761\u8d70\u5eca\uff0c\u6765\u5230\u82b1\u5385\u95e8\u5916\uff0c\u89c1\u5230\u7a97\u7eb8\u4e2d\u900f\u51fa\u5149\u4eae\uff0c\u4e01\u5178\u7a81\u7136\u53d1\u8d77\u6296\u6765\uff0c\u98a4\u58f0\u9053\uff1a\u201c\u5144\u5f1f\uff0c\u4f60\u8fdb\u53bb\u77a7\u77a7\u3002\u201d \u3000\u3000\u72c4\u4e91\u4f38\u624b\u63a8\u5f00\u4e86\u5385\u95e8\uff0c\u53ea\u89c1\u70db\u5149\u8000\u773c\uff0c\u684c\u5b50\u4e0a\u70b9\u71c3\u7740\u4e24\u6839\u7d20\u70db\uff0c\u539f\u6765\u662f\u4e00\u5ea7\u7075\u5802\u3002\u4ed6\u4e00\u76f4\u5728\u62c5\u5fc3\u4f1a\u77a7\u89c1\u7075\u5802\u3001\u68fa\u6750\u3001\u6216\u662f\u6b7b\u4eba\uff0c\u8fd9\u65f6\u7ec8\u4e8e\u89c1\u5230\u4e86\uff0c\u867d\u7136\u65e9\u5df2\u6599\u5230\uff0c\u8fd8\u662f\u5fcd\u4e0d\u4f4f\u6253\u4e86\u4e2a\u5bd2\u5664\uff0c\u51dd\u76ee\u77a7\u90a3\u7075\u724c\u65f6\uff0c\u89c1\u4e0a\u9762\u5199\u7740\u201c\u7231\u5973\u51cc\u971c\u534e\u4e4b\u7075\u4f4d\u201d\u516b\u4e2a\u5b57\uff0c\u7a81\u89c9\u8eab\u540e\u98ce\u58f0\u98d2\u7136\uff0c\u4e01\u5178\u62a2\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u4e01\u5178\u5446\u4e86\u4e00\u9635\uff0c\u6251\u5728\u684c\u4e0a\uff0c\u653e\u58f0\u5927\u6078\uff0c\u53eb\u9053\uff1a\u201c\u971c\u534e\uff0c\u4f60\u679c\u7136\u5148\u6211\u800c\u53bb\u4e86\u3002\u201d \u3000\u3000\u970e\u65f6\u4e4b\u95f4\uff0c\u72c4\u4e91\u5fc3\u4e2d\u60f3\u5230\u4e86\u8bb8\u8bb8\u591a\u591a\u4e8b\u60c5\uff0c\u8fd9\u4f4d\u4e01\u5927\u54e5\u7684\u79cd\u79cd\u602a\u50fb\u884c\u5f84\uff0c\u5c31\u5728\u8fd9\u629a\u684c\u4e00\u54ed\u4e4b\u9645\uff0c\u4ee4\u4ed6\u5168\u7136\u660e\u767d\u4e86\u3002\u4f46\u518d\u4e00\u7ec6\u60f3\uff0c\u5374\u53c8\u6709\u79cd\u79cd\u96be\u4ee5\u7d22\u89e3\u4e4b\u5904\u3002 \u3000\u3000\u4e01\u5178\u5168\u4e0d\u7406\u4f1a\u81ea\u5df1\u662f\u8d8a\u72f1\u7684\u91cd\u72af\uff0c\u4e0d\u7406\u4f1a\u8eab\u5904\u4e4b\u5730\u662f\u77e5\u5e9c\u5927\u4eba\u7684\u4f4f\u5b85\uff0c\u8d8a\u54ed\u8d8a\u60b2\u3002\u72c4\u4e91\u77e5\u9053\u65e0\u6cd5\u76f8\u529d\uff0c\u53ea\u6709\u4efb\u5176\u81ea\u7136\u3002 \u3000\u3000\u4e01\u5178\u54ed\u4e86\u826f\u4e45\uff0c\u8fd9\u624d\u6162\u6162\u7ad9\u76f4\u8eab\u5b50\uff0c\u4f38\u624b\u63ed\u5f00\u7d20\u5e0f\uff0c\u5e0f\u540e\u8d6b\u7136\u662f\u4e00\u5177\u68fa\u6728\u3002\u4ed6\u53cc\u624b\u7d27\u7d27\u62b1\u4f4f\u68fa\u6728\uff0c\u5c06\u8138\u8d34\u7740\u68fa\u76d6\uff0c\u62bd\u62bd\u564e\u564e\u7684\u9053\uff1a\u201c\u971c\u534e\uff0c\u971c\u534e\uff0c\u4f60\u4e3a\u751a\u4e48\u8fd9\u6837\u5fcd\u5fc3\uff1f\u4f60\u53bb\u4e4b\u524d\uff0c\u600e\u4e48\u4e0d\u53eb\u6211\u6765\u518d\u89c1\u4f60\u4e00\u9762\uff1f\u201d \u3000\u3000\u72c4\u4e91\u5ffd\u542c\u5f97\u811a\u6b65\u58f0\u54cd\uff0c\u95e8\u5916\u6709\u51e0\u4eba\u6765\u5230\uff0c\u5fd9\u9053\uff1a\u201c\u5927\u54e5\uff0c\u6709\u4eba\u6765\u5566\u3002\u201d \u3000\u3000\u4e01\u5178\u7528\u5634\u5507\u53bb\u4eb2\u90a3\u68fa\u6750\uff0c\u5bf9\u6709\u4eba\u6765\u5230\uff0c\u5168\u6ca1\u653e\u5728\u5fc3\u4e0a\u3002 \u3000\u3000\u53ea\u89c1\u706b\u5149\u660e\u4eae\uff0c\u4e24\u4e2a\u4eba\u9ad8\u4e3e\u706b\u628a\uff0c\u8d70\u4e86\u8fdb\u6765\uff0c\u559d\u9053\uff1a\u201c\u662f\u8c01\u5728\u8fd9\u91cc\u5435\u95f9\uff1f\u201d\u90a3\u4e24\u4eba\u4e4b\u540e\u662f\u4e2a\u56db\u5341\u4e94\u516d\u5c81\u7684\u4e2d\u5e74\u6c49\u5b50\uff0c\u8863\u9970\u534e\u8d35\uff0c\u4e00\u8138\u7cbe\u608d\u4e4b\u8272\uff0c\u4ed6\u5411\u72c4\u4e91\u77a7\u4e86\u4e00\u773c\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f \u3000\u3000\u5230\u8fd9\u91cc\u5e72\u751a\u4e48\uff1f\u201d\u72c4\u4e91\u6ee1\u8154\u6124\u6fc0\uff0c\u53cd\u95ee\u9053\uff1a\u201c\u4f60\u53c8\u662f\u8c01\uff1f\u5230\u8fd9\u91cc\u5e72\u751a\u4e48\uff1f\u201d\u624b\u6267\u706b\u628a\u7684\u4e00\u4eba\u559d\u9a82\u9053\uff1a\u201c\u5c0f\u8d3c\uff0c\u8fd9\u4f4d\u662f\u8346\u5dde\u5e9c\u51cc\u5927\u4eba\uff0c\u4f60\u597d\u5927\u80c6\u5b50\uff0c\u534a\u591c\u4e09\u66f4\u5230\u8fd9\u91cc\u6765\uff0c\u60f3\u9020\u53cd\u5417\uff1f\u5feb\u8dea\u4e0b\uff01\u201d\u72c4\u4e91\u51b7\u7b11\u4e00\u58f0\uff0c\u6d51\u4e0d\u7406\u4f1a\u3002 \u3000\u3000\u4e01\u5178\u64e6\u5e72\u4e86\u773c\u6cea\uff0c\u95ee\u9053\uff1a\u201c\u971c\u534e\u662f\u54ea\u4e00\u5929\u53bb\u4e16\u7684\uff1f\u751f\u751a\u4e48\u75c5\uff1f\u201d\u8bed\u97f3\u7adf\u5341\u5206\u5e73\u9759\u3002 \u3000\u3000\u51cc\u77e5\u5e9c\u5411\u4ed6\u770b\u4e86\u4e00\u773c\uff0c\u8bf4\u9053\uff1a\u201c\u554a\uff01\u6211\u9053\u662f\u8c01\uff0c\u539f\u6765\u662f\u4e01\u5927\u4fa0\u3002\u5c0f\u5973\u4e0d\u5e78\u901d\u4e16\uff0c\u6709\u52b3\u540a\u5501\uff0c\u5b58\u6b81\u540c\u611f\u3002\u5c0f\u5973\u53bb\u4e16\u5df2\u4e94\u5929\u4e86\uff0c\u5927\u592b\u4e5f\u8bf4\u4e0d\u4e0a\u662f\u751a\u4e48\u75c5\u75c7\uff0c\u53ea\u8bf4\u662f\u90c1\u79ef\u96be\u6d88\u3002\u201d \u3000\u3000\u4e01\u5178\u6068\u6068\u7684\u9053\uff1a\u201c\u8fd9\u53ef\u9042\u4e86\u4f60\u7684\u5fc3\u613f\u3002\u201d\u51cc\u77e5\u5e9c\u53f9\u9053\uff1a\u201c\u4e01\u5927\u4fa0\uff0c\u4f60\u53ef\u5fd2\u4e5f\u56fa\u6267\u4e86\uff0c\u5018\u82e5\u65e9\u65e9\u8bf4\u4e86\u51fa\u6765\uff0c\u5c0f\u5973\u56fa\u7136\u4e0d\u4f1a\u7ed9\u4f60\u5bb3\u6b7b\uff0c\u6211\u548c\u4f60\u66f4\u6210\u4e86\u7fc1\u5a7f\uff0c\u90a3\u662f\u4f55\u7b49\u7684\u7f8e\u4e8b\u3002\u201d \u3000\u3000\u4e01\u5178\u5927\u58f0\u9053\uff1a\u201c\u4f60\u8bf4\u971c\u534e\u662f\u6211\u5bb3\u6b7b\u7684\uff1f\u4e0d\u662f\u4f60\u5bb3\u6b7b\u5979\u7684\uff1f\u201d \u3000\u3000\u8bf4\u7740\u5411\u51cc\u77e5\u5e9c\u8d70\u4e0a\u4e00\u6b65\uff0c\u773c\u4e2d\u51f6\u5149\u66b4\u957f\u3002 \u3000\u3000\u51cc\u77e5\u5e9c\u5374\u5341\u5206\u9547\u5b9a\uff0c\u6447\u5934\u9053\uff1a\u201c\u4e8b\u5df2\u5982\u6b64\uff0c\u8fd8\u8bf4\u751a\u4e48\uff1f\u971c\u534e\u554a\uff0c\u971c\u534e\uff0c\u4f60\u4e5d\u6cc9\u4e4b\u4e0b\uff0c\u5b9a\u662f\u602a\u7238\u7238\u4e0d\u4f53\u8c05\u4f60\u4e86\u3002\u201d\u6162\u6162\u8d70\u5230\u7075\u4f4d\u4e4b\u524d\uff0c\u5de6\u624b\u6276\u684c\uff0c\u53f3\u624b\u62ed\u6cea\u3002 \u3000\u3000\u4e01\u5178\u68ee\u7136\u7684\u9053\uff1a\u201c\u5018\u82e5\u6211\u4eca\u65e5\u6740\u4e86\u4f60\uff0c\u971c\u534e\u5728\u5929\u4e4b\u7075\u5b9a\u7136\u6068\u6211\u3002\u51cc\u9000\u601d\uff0c\u77a7\u5728\u4f60\u5973\u513f\u4efd\u4e0a\uff0c\u4f60\u6298\u78e8\u4e86\u6211\u8fd9\u4e03\u5e74\uff0c\u54b1\u4eec\u4e00\u7b14\u52fe\u9500\u3002\u4eca\u540e\u4f60\u518d\u60f9\u4e0a\u6211\uff0c\u53ef\u4f11\u602a\u59d3\u4e01\u7684\u65e0\u60c5\u3002\u72c4\u5144\u5f1f\uff0c\u8d70\u7f62\u3002\u201d\u51cc\u77e5\u5e9c\u957f\u53f9\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4e01\u5927\u4fa0\uff0c\u54b1\u4eec\u843d\u5230\u4eca\u65e5\u7684\u7ed3\u679c\uff0c\u4f60\u8bf4\u6709\u751a\u4e48\u597d\u5904\uff1f\u201d\u4e01\u5178\u9053\uff1a\u201c\u4f60\u6e05\u591c\u629a\u5fc3\u81ea\u95ee\uff0c\u4e5f\u6709\u70b9\u60ed\u6127\u4e48\uff1f\u4f60\u53ea\u8d2a\u56fe\u90a3\u751a\u4e48\u2018\u2019\uff0c\u5b81\u53ef\u5bb3\u6b7b\u81ea\u5df1\u5973\u513f\u3002\u201d \u3000\u3000\u51cc\u77e5\u5e9c\u9053\uff1a\u201c\u4e01\u5927\u4fa0\uff0c\u4f60\u4e0d\u5fd9\u8d70\uff0c\u8fd8\u662f\u5c06\u90a3\u5251\u8bc0\u8bf4\u4e86\u51fa\u6765\uff0c\u6211\u4fbf\u7ed9\u89e3\u836f\u4e8e\u4f60\uff0c\u514d\u5f97\u6789\u81ea\u9001\u4e86\u6027\u547d\u3002\u201d \u3000\u3000\u4e01\u5178\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u751a\u4e48\u89e3\u836f\uff1f\u201d\u4fbf\u5728\u6b64\u65f6\uff0c\u53ea\u89c9\u8138\u988a\u3001\u5634\u5507\u3001\u624b\u638c\u5404\u5904\u5ffd\u6709\u8f7b\u5fae\u7684\u9ebb\u75f9\u4e4b\u611f\uff0c\u540c\u65f6\u53c8\u95fb\u5230\u4e86\u4e00\u9635\u6de1\u6de1\u7684\u82b1\u9999\uff0c\u8fd9\u82b1\u9999\uff0c\u8fd9\u82b1\u9999\u2026\u2026\u4ed6\u53c8\u60ca\u53c8\u6012\uff0c\u8eab\u5b50\u6447\u6643\u3002 \u3000\u3000\u51cc\u77e5\u5e9c\u9053\uff1a\u201c\u6211\u751f\u6015\u6709\u4e0d\u8096\u4e4b\u5f92\uff0c\u5f00\u68fa\u8fb1\u6211\u5973\u513f\u7684\u6e05\u767d\u9057\u4f53\uff0c\u56e0\u6b64\u2026\u2026\u201d \u3000\u3000\u4e01\u5178\u767b\u65f6\u7701\u609f\uff0c\u6012\u9053\uff1a\u201c\u4f60\u5728\u68fa\u6728\u4e0a\u6d82\u4e86\u6bd2\u836f\uff1f\u51cc\u9000\u601d\uff0c\u4f60\u597d\u6076\u6bd2\uff01\u201d\u7eb5\u8eab\u800c\u8d77\uff0c\u53d1\u638c\u4fbf\u5411\u4ed6\u51fb\u53bb\u3002\u4e0d\u6599\u90a3\u6bd2\u836f\u5f53\u771f\u5389\u5bb3\uff0c\u5239\u65f6\u95f4\u6d88\u529f\u8680\u9aa8\uff0c\u795e\u7167\u529f\u7adf\u5df2\u4f7f\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u51cc\u77e5\u5e9c\u51cc\u9000\u601d\u4fa7\u8eab\u95ea\u907f\uff0c\u8eab\u624b\u751a\u662f\u654f\u6377\u3002\u95e8\u5916\u53c8\u62a2\u8fdb\u56db\u540d\u6c49\u5b50\uff0c\u6267\u5200\u6301\u5251\uff0c\u540c\u65f6\u5411\u4e01\u5178\u653b\u53bb\u3002\u4e01\u5178\u98de\u8d77\u5de6\u8db3\uff0c\u5411\u5de6\u9996\u4e00\u4eba\u7684\u624b\u8155\u8e22\u53bb\u3002\u672c\u6765\u8fd9\u4e00\u811a\u65b9\u4f4d\u53bb\u5f97\u5341\u5206\u5de7\u5999\uff0c\u90a3\u4eba\u624b\u4e2d\u7684\u5355\u5200\u975e\u7ed9\u8e22\u4e0b\u4e0d\u53ef\u3002\u5c82\u77e5\u4ed6\u811a\u5230\u4e2d\u9014\uff0c\u7a81\u7136\u95f4\u52b2\u529b\u6d88\u5931\uff0c\u7adf\u7136\u505c\u6ede\u4e0d\u524d\uff0c\u539f\u6765\u6bd2\u6027\u5df2\u4f20\u5230\u811a\u4e0a\u3002\u90a3\u4eba\u7ffb\u8f6c\u5200\u80cc\uff0c\u62cd\u7684\u4e00\u58f0\uff0c\u6253\u5728\u4ed6\u811a\u9aa8\u4e4b\u4e0a\u3002\u4e01\u5178\u811a\u9aa8\u788e\u88c2\uff0c\u6454\u5012\u5728\u5730\u3002 \u3000\u3000\u72c4\u4e91\u5927\u60ca\uff0c\u60f6\u6025\u4e2d\u4e0d\u53ca\u7ec6\u60f3\uff0c\u7eb5\u8eab\u5c31\u5411\u51cc\u9000\u601d\u6251\u53bb\uff0c\u5fc3\u60f3\u53ea\u6709\u6293\u7740\u4ed6\u4f5c\u4e3a\u8981\u80c1\uff0c\u624d\u80fd\u6551\u5f97\u4e01\u5178\u3002\u90a3\u77e5\u51cc\u9000\u601d\u5de6\u638c\u659c\u51fa\uff0c\u547c\u7684\u4e00\u638c\uff0c\u51fb\u5728\u4ed6\u80f8\u53e3\uff0c\u624b\u6cd5\u52b2\u529b\uff0c\u5747\u5c5e\u4e0a\u4e58\u3002\u72c4\u4e91\u65e9\u8c41\u51fa\u4e86\u6027\u547d\u4e0d\u8981\uff0c\u4e0d\u5c01\u4e0d\u67b6\uff0c\u4ecd\u662f\u6251\u524d\u53bb\u3002\u51cc\u9000\u601d\u8fd9\u4e00\u638c\u660e\u660e\u51fb\u4e2d\u5bf9\u65b9\u80f8\u53e3\uff0c\u5374\u89c1\u72c4\u4e91\u6beb\u4e0d\u7406\u4f1a\uff0c\u4ed6\u4e0d\u77e5\u72c4\u4e91\u5185\u7a7f\u201c\u4e4c\u8695\u8863\u201d\u5b9d\u7532\u62a4\u8eab\uff0c\u8fd8\u9053\u4ed6\u6b66\u529f\u5947\u9ad8\uff0c\u4e00\u60ca\u4e4b\u4e0b\uff0c\u5df2\u88ab\u72c4\u4e91\u5de6\u624b\u62ff\u4f4f\u4e86\u80f8\u53e3\u201c\u81bb\u4e2d\u7a74\u201d\u3002 \u3000\u3000\u72c4\u4e91\u4e00\u88ad\u5f97\u624b\uff0c\u4fef\u8eab\u4fbf\u5c06\u4e01\u5178\u8d1f\u5728\u80cc\u4e0a\uff0c\u5de6\u624b\u4ecd\u662f\u7262\u7262\u6293\u4f4f\u51cc\u9000\u601d\u80f8\u524d\u8981\u7a74\u3002\u90a3\u56db\u4e2a\u6c49\u5b50\u5fc3\u6709\u987e\u5fcc\uff0c\u53ea\u662f\u559d\u9a82\uff0c\u5374\u4e0d\u6562\u4e0a\u524d\u3002\u4e01\u5178\u559d\u9053\uff1a\u201c\u6295\u53bb\u706b\u628a\uff0c\u5439\u7184\u8721\u70db\u3002\u201d\u6267\u706b\u628a\u7684\u6c49\u5b50\u4e0d\u6562\u4e0d\u4ece\uff0c\u7075\u5802\u4e2d\u767b\u65f6\u4e00\u56e2\u6f06\u9ed1\u3002 \u3000\u3000\u72c4\u4e91\u5de6\u624b\u6293\u4f4f\u51cc\u9000\u601d\u524d\u80f8\uff0c\u53f3\u624b\u8d1f\u7740\u4e01\u5178\uff0c\u5feb\u6b65\u62a2\u51fa\u3002\u4e01\u5178\u6307\u70b9\u9014\u5f84\uff0c\u7247\u523b\u95f4\u6765\u5230\u82b1\u56ed\u95e8\u8fb9\uff0c\u72c4\u4e91\u8e22\u5f00\u677f\u95e8\uff0c\u594b\u529b\u5728\u51cc\u9000\u601d\u7684\u81bb\u4e2d\u7a74\u4e0a\u731b\u51fb\u4e00\u62f3\uff0c\u8d1f\u7740\u4e01\u5178\u4fbf\u9003\u4e86\u51fa\u53bb\uff0c\u9ed1\u6697\u4e2d\u4e00\u811a\u9ad8\u4e00\u811a\u4f4e\u7684\u72c2\u51b2\u6025\u5954\u3002 \u3000\u3000\u4ed6\u82e6\u4fee\u795e\u7167\u7ecf\u4e24\u5e74\uff0c\u867d\u8bf4\u4e0d\u4e0a\u6709\u751a\u4e48\u91cd\u5927\u6210\u5c31\uff0c\u4f46\u5185\u529b\u4e5f\u5df2\u975e\u540c\u6cdb\u6cdb\u3002\u4ed6\u51fb\u5411\u51cc\u9000\u601d\u8fd9\u4e00\u62f3\u60c5\u6025\u62da\u547d\uff0c\u51fa\u529b\u5947\u91cd\uff0c\u6b63\u597d\u53c8\u51fb\u4e2d\u4e86\u5bf9\u65b9\u80f8\u53e3\u8981\u7a74\u3002\u51cc\u9000\u601d\u4e2d\u62f3\u540e\uff0c\u95f7\u54fc\u4e00\u58f0\uff0c\u5f80\u540e\u4fbf\u5012\u3002\u4ed6\u624b\u4e0b\u4ece\u4eba\u4e0e\u6b66\u5e08\u60ca\u60f6\u4e4b\u4e0b\uff0c\u5fd9\u4e8e\u76f8\u6551\uff0c\u8c01\u4e5f\u987e\u4e0d\u5f97\u8ffd\u8d76\u4e01\u72c4\u4e8c\u4eba\u4e86\u3002 \u3000\u3000\u4e01\u5178\u624b\u811a\u8d8a\u6765\u8d8a\u9ebb\u6728\uff0c\u795e\u667a\u5374\u4ecd\u6e05\u9192\u3002\u4ed6\u719f\u6089\u6c5f\u9675\u57ce\u4e2d\u9053\u8def\uff0c\u6307\u70b9\u72c4\u4e91\u8f6c\u5de6\u5411\u53f3\uff0c\u4e0d\u4e45\u4fbf\u8fdc\u79bb\u95f9\u5e02\uff0c\u5230\u4e86\u4e00\u5ea7\u5e9f\u56ed\u4e4b\u4e2d\u3002\u4e01\u5178\u9053\uff1a\u201c\u51cc\u77e5\u5e9c\u5b9a\u7136\u4e0b\u4ee4\u628a\u5b88\u57ce\u95e8\uff0c\u4e25\u52a0\u76d8\u67e5\uff0c\u6211\u4e2d\u6bd2\u5df2\u6df1\uff0c\u662f\u4e0d\u80fd\u51fa\u57ce\u4e86\u3002\u8fd9\u5e9f\u56ed\u5411\u6765\u8bf4\u662f\u6709\u9b3c\uff0c\u65e0\u4eba\u6562\u6765\uff0c\u54b1\u4eec\u4e14\u8eb2\u4e00\u9635\u518d\u8bf4\u3002\u201d \u3000\u3000\u72c4\u4e91\u5c06\u4ed6\u8f7b\u8f7b\u653e\u5728\u4e00\u682a\u6885\u6811\u4e4b\u4e0b\uff0c\u9053\uff1a\u201c\u4e01\u5927\u54e5\uff0c\u4f60\u4e2d\u4e86\u751a\u4e48\u6bd2\uff1f\u600e\u6837\u65bd\u6551\u624d\u662f\uff1f\u201d \u3000\u3000\u4e01\u5178\u53f9\u4e86\u53e3\u6c14\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u4e0d\u4e2d\u7528\u4e86\u3002\u90a3\u662f\u2018\u91d1\u6ce2\u65ec\u82b1\u2019\u7684\u5267\u6bd2\uff0c\u5929\u4e0b\u65e0\u836f\u53ef\u89e3\uff0c\u6328\u5f97\u4e00\u523b\u662f\u4e00\u523b\u3002\u201d\u72c4\u4e91\u5927\u5403\u4e00\u60ca\uff0c\u5168\u8eab\u72b9\u5982\u5815\u5165\u51b0\u7a96\uff0c\u98a4\u58f0\u9053\uff1a\u201c\u751a\u4e48\uff1f\u4f60\u2026\u2026\u4f60\u662f\u2026\u2026\u662f\u8bf4\u7b11\u7f62\uff1f\u201d\u5fc3\u4e2d\u5374\u660e\u77e5\u4e01\u5178\u5e76\u975e\u8bf4\u7b11\u3002\u4e01\u5178\u9053\uff1a\u201c\u51cc\u9000\u601d\u8fd9\u2018\u91d1\u6ce2\u65ec\u82b1\u2019\u6bd2\u6027\u5389\u5bb3\u4e4b\u6781\uff0c\u563f\u563f\uff0c\u6211\u4ee5\u524d\u53ea\u662f\u95fb\u5f97\u51e0\u4e0b\uff0c\u4fbf\u6655\u4e86\u8fc7\u53bb\u3002\u8fd9\u4e00\u6b21\u662f\u78b0\u5230\u4e86\u808c\u80a4\uff0c\u90a3\u8fd8\u4e86\u5f97\uff1f\u201d \u3000\u3000\u72c4\u4e91\u6025\u9053\uff1a\u201c\u4e01\u5927\u54e5\uff0c\u4f60\u2026\u2026\u4f60\u522b\u4f24\u5fc3\u3002\u7559\u5f97\u9752\u5c71\u5728\u2026\u2026\u5509\u2026\u2026\u5973\u4eba\u7684\u4e8b\uff0c\u6211\u2026\u2026\u6211\u4e5f\u662f\u4e00\u6837\uff0c\u8fd9\u53eb\u505a\u6ca1\u6709\u6cd5\u5b50\u2026\u2026\u4f60\u5f97\u60f3\u6cd5\u5b50\u89e3\u4e86\u6bd2\u518d\u8bf4\u2026\u2026\u6211\u53bb\u6253\u70b9\u6c34\u6765\u7ed9\u4f60\u6d17\u6d17\u3002\u201d\u5fc3\u4e2d\u4e00\u6025\uff0c\u8bf4\u8bdd\u5168\u7136\u7684\u8bed\u65e0\u4f26\u6b21\u3002 \u3000\u3000\u4e01\u5178\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u6ca1\u7528\u7684\u3002\u8fd9\u2018\u91d1\u6ce2\u65ec\u82b1\u2019\u4e4b\u6bd2\u7528\u6c34\u4e00\u6d17\uff0c\u808c\u80a4\u7acb\u65f6\u53d1\u80bf\u8150\u70c2\uff0c\u6b7b\u5f97\u66f4\u52a0\u60e8\u4e9b\u3002\u72c4\u5144\u5f1f\uff0c\u6211\u6709\u8bb8\u8bb8\u591a\u591a\u8bdd\u8981\u8ddf\u4f60\u8bf4\uff0c\u4f60\u522b\u5fd9\u4e71\uff0c\u4f60\u4e00\u4e71\uff0c\u53ea\u6015\u6211\u6f0f\u4e86\u8981\u7d27\u8bdd\u513f\u3002 \u3000\u3000\u65f6\u5019\u4e0d\u591a\u4e86\uff0c\u6211\u5f97\u628a\u8bdd\u8bf4\u5b8c\uff0c\u4f60\u7ed9\u6211\u5b89\u5b89\u9759\u9759\u7684\u5750\u7740\uff0c\u522b\u6253\u65ad\u6211\u8bdd\u5934\u3002\u201d \u3000\u3000\u72c4\u4e91\u53ea\u5f97\u5750\u5728\u4ed6\u8eab\u65c1\uff0c\u53ef\u662f\u5fc3\u4e2d\u5374\u53c8\u5982\u4f55\u5b89\u9759\u5f97\u4e0b\u6765\uff1f \u3000\u3000\u4e01\u5178\u8bf4\u5f97\u5f88\u5e73\u7a33\uff0c\u4f3c\u4e4e\u8bf4\u7684\u662f\u522b\u4eba\u7684\u4e8b\uff0c\u662f\u4e00\u4e2a\u548c\u4ed6\u6beb\u4e0d\u76f8\u5e72\u7684\u65c1\u4eba\u3002 \u3000\u3000\u201c\u6211\u662f\u8346\u95e8\u4eba\uff0c\u662f\u6b66\u6797\u4e16\u5bb6\u3002\u6211\u7239\u7239\u5728\u4e24\u6e56\u4e5f\u7b97\u662f\u9887\u6709\u540d\u6c14\u7684\u3002\u6211\u5b66\u6b66\u7684\u8d44\u8d28\u8fd8\u4e0d\u9519\u3002\u9664\u4e86\u5bb6\u4f20\u4e4b\u5b66\uff0c\u53c8\u62dc\u4e86\u4e24\u4f4d\u5e08\u7236\u3002\u5e74\u8f7b\u65f6\u7231\u6253\u62b1\u4e0d\u5e73\uff0c\u5c45\u7136\u4e5f\u95ef\u51fa\u4e86\u4e00\u70b9\u513f\u5c0f\u5c0f\u540d\u5934\u3002\u540e\u6765\u7236\u6bcd\u53bb\u4e16\uff0c\u6211\u5bb6\u8d22\u4e0d\u5c11\uff0c\u5374\u4e5f\u4e0d\u60f3\u7ed3\u4eb2\uff0c\u53ea\u662f\u52e4\u4e8e\u7ec3\u6b66\uff0c\u7ed3\u4ea4\u6c5f\u6e56\u4e0a\u7684\u670b\u53cb\u3002 \u3000\u3000\u201c\u90a3\u662f\u5341\u4e94\u5e74\u524d\u7684\u4e8b\u4e86\uff0c\u6211\u4e58\u8239\u4ece\u56db\u5ddd\u4e0b\u6765\uff0c\u51fa\u4e86\u4e09\u5ce1\u540e\uff0c\u8239\u6cca\u5728\u4e09\u6597\u576a\u3002\u90a3\u5929\u665a\u4e0a\uff0c\u6211\u5728\u8239\u4e2d\u542c\u5f97\u5cb8\u4e0a\u6709\u6253\u6597\u7684\u58f0\u97f3\u3002 \u3000\u3000\u6211\u751f\u6027\u7231\u6b66\uff0c\u81ea\u662f\u5173\u5fc3\uff0c\u4fbf\u4ece\u7a97\u4e2d\u5411\u5916\u5f20\u671b\u3002\u90a3\u665a\u6708\u5149\u660e\u4eae\uff0c\u770b\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u662f\u4e09\u4e2a\u4eba\u5728\u56f4\u653b\u4e00\u4e2a\u8001\u8005\u3002\u8fd9\u4e09\u4eba\u90fd\u662f\u4e24\u6e56\u6b66\u6797\u4e2d\u7684\u51fa\u540d\u4eba\u7269\uff0c\u6211\u5012\u90fd\u8ba4\u5f97\u3002\u4e00\u4e2a\u662f\u4e94\u4e91\u624b\u4e07\u9707\u5c71\u3002\uff08\u72c4\u4e91\u63d2\u53e3\u9053\uff1a\u201c\u554a\uff0c\u662f\u6211\u5e08\u4f2f\uff01\u201d\uff09\u53e6\u4e00\u4e2a\u662f\u9646\u5730\u795e\u9f99\u8a00\u8fbe\u5e73\u3002 \u3000\u3000\uff08\u72c4\u4e91\u53eb\u9053\uff1a\u201c\u662f\u6211\u4e8c\u5e08\u4f2f\uff0c\u4e0d\u8fc7\u6211\u6ca1\u89c1\u8fc7\u4ed6\u8001\u4eba\u5bb6\u3002\u201d\uff09\u7b2c\u4e09\u4e2a\u4eba\u4f7f\u4e00\u53e3\u957f\u5251\uff0c\u8eab\u624b\u751a\u662f\u77eb\u6377\uff0c\u90a3\u662f\u94c1\u9501\u6a2a\u6c5f\u621a\u957f\u53d1\u3002\uff08\u72c4\u4e91\u8df3\u4e86\u8d77\u6765\uff0c\u53eb\u9053\uff1a\u201c\u662f\u6211\u5e08\u7236\uff01\u201d\uff09 \u3000\u3000\u201c\u6211\u548c\u4e07\u9707\u5c71\u66fe\u6709\u6570\u9762\u4e4b\u7f18\uff0c\u77e5\u4ed6\u6b66\u529f\u4e0d\u5f31\uff0c\u6211\u5f53\u65f6\u8fdc\u4e0d\u53ca\u4ed6\uff0c\u89c1\u4ed6\u4eec\u5e08\u5144\u5f1f\u4e09\u4eba\u8fde\u624b\u653b\u654c\uff0c\u60f3\u6765\u5fc5\u64cd\u80dc\u7b97\u3002\u90a3\u8001\u8005\u80cc\u4e0a\u5df2\u7ecf\u53d7\u4f24\uff0c\u4e0d\u4f4f\u7684\u6d41\u8840\uff0c\u624b\u4e2d\u53c8\u6ca1\u5175\u5203\uff0c\u53ea\u662f\u4ee5\u4e00\u53cc\u8089\u638c\u548c\u4ed6\u4e09\u4eba\u76f8\u6597\uff0c\u4f46\u529f\u592b\u53ef\u6bd4\u4e07\u9707\u5c71\u4ed6\u4eec\u9ad8\u51fa\u592a\u591a\u3002\u90a3\u4e8c\u4eba\u4e0d\u6562\u903c\u8fd1\u4ed6\u8eab\u65c1\u3002\u6211\u8d8a\u770b\u8d8a\u662f\u4e0d\u5e73\uff0c\u4f46\u89c1\u4e07\u9707\u5c71\u4ed6\u4eec\u4f7f\u7684\u90fd\u662f\u6740\u7740\uff0c\u663e\u7136\u8981\u7f6e\u90a3\u8001\u8005\u4e8e\u6b7b\u5730\u3002\u6211\u4e00\u58f0\u4e5f\u4e0d\u6562\u51fa\uff0c\u751f\u6015\u7ed9\u4ed6\u4eec\u53d1\u89c9\uff0c\u7978\u4e8b\u53ef\u662f\u4e0d\u5c0f\u3002\u8fd9\u79cd\u6c5f\u6e56\u4e0a\u7684\u4ec7\u6740\uff0c\u5018\u82e5\u7ed9\u65c1\u4eba\u77a7\u89c1\u4e86\uff0c\u5f80\u5f80\u4fbf\u8981\u6740\u4eba\u706d\u53e3\u3002 \u3000\u3000\u201c\u6597\u4e86\u534a\u5929\uff0c\u90a3\u8001\u8005\u80cc\u4e0a\u7684\u8840\u8d8a\u6d41\u8d8a\u591a\uff0c\u5b9e\u5728\u652f\u6301\u4e0d\u4f4f\u4e86\uff0c\u7a81\u7136\u53eb\u9053\uff1a\u2018\u597d\uff0c\u6211\u4ea4\u7ed9\u4f60\u4eec\u3002\u2019\u4f38\u624b\u5230\u6000\u4e2d\u53bb\u638f\u6478\u751a\u4e48\u3002\u4e07\u9707\u5c71\u4ed6\u4eec\u4e09\u4eba\u4e00\u9f50\u62e5\u4e0a\uff0c\u4f3c\u4e4e\u751f\u6015\u7ed9\u65c1\u4eba\u4e89\u4e86\u5148\u53bb\u3002\u7a81\u7136\u4e4b\u95f4\uff0c\u90a3\u8001\u8005\u53cc\u638c\u547c\u7684\u63a8\u51fa\uff0c\u4e09\u4eba\u4e3a\u638c\u529b\u6240\u903c\uff0c\u9f50\u5411\u540e\u9000\u3002\u8001\u8005\u8f6c\u8eab\u4fbf\u5954\uff0c\u6251\u901a\u4e00\u58f0\uff0c\u8df3\u5165\u4e86\u6c5f\u4e2d\u3002\u4e09\u4eba\u5927\u58f0\u60ca\u53eb\uff0c\u8d76\u5230\u6c5f\u8fb9\u3002 \u3000\u3000\u201c\u957f\u6c5f\u4ece\u4e09\u5ce1\u5954\u6cfb\u4e0b\u6765\uff0c\u4e09\u6597\u576a\u7684\u6c5f\u6c34\u53ef\u6709\u591a\u6025\uff1f\u53ea\u4e00\u970e\u773c\u95f4\uff0c\u90a3\u8001\u8005\u81ea\u7136\u662f\u65e0\u5f71\u65e0\u8e2a\u4e86\u3002\u4f46\u4f60\u5e08\u7236\u8fd8\u662f\u4e0d\u80af\u6b7b\u5fc3\uff0c\u8df3\u5230\u6211\u8239\u4e0a\uff0c\u62d4\u4e86\u7af9\u7bd9\uff0c\u5728\u6c5f\u4e2d\u4e71\u635e\u4e00\u9635\u3002\u8fd9\u4e09\u4eba\u65e2\u903c\u6b7b\u4e86\u90a3\u8001\u5934\uff0c\u8be5\u5f53\u6b22\u559c\u624d\u662f\uff0c\u4f46\u4e09\u4eba\u8138\u8272\u90fd\u6781\u4e3a\u53ef\u6015\u3002\u6211\u4e0d\u6562\u591a\u770b\uff0c\u5c06\u5934\u8499\u5728\u88ab\u4e2d\uff0c\u9690\u9690\u7ea6\u7ea6\u542c\u5f97\u4ed6\u4eec\u5728\u4e89\u5435\u751a\u4e48\uff0c\u4f3c\u4e4e\u662f\u4e92\u76f8\u57cb\u6028\u3002 \u3000\u3000\u201c\u6211\u76f4\u542c\u5f97\u8fd9\u4e09\u4eba\u90fd\u8d70\u8fdc\u4e86\uff0c\u624d\u6562\u8d77\u8eab\uff0c\u5ffd\u542c\u5f97\u540e\u8244\u4e0a\u62cd\u7684\u4e00\u58f0\u54cd\uff0c\u8244\u516c\u201c\u554a\u201d\u7684\u4e00\u58f0\uff0c\u53eb\u9053\uff1a\u201c\u6709\u6c34\u9b3c\uff01\u201d\u6211\u4fa7\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u4e00\u4e2a\u4eba\u6e7f\u6dcb\u6dcb\u7684\u4f0f\u5728\u8239\u677f\u4e0a\uff0c\u6b63\u662f\u90a3\u4e2a\u8001\u8005\u3002\u539f\u6765\u4ed6\u8df3\u5165\u6c5f\u4e2d\u540e\uff0c\u94bb\u5165\u8239\u5e95\uff0c\u7528\u5927\u529b\u9b54\u722a\u624b\u6cd5\u94a9\u4f4f\u8239\u5e95\uff0c\u51dd\u4f4f\u4e86\u547c\u5438\uff0c\u5f85\u654c\u4eba\u9000\u8d70\u540e\u8fd9\u624d\u51fa\u6765\u3002\u6211\u5fd9\u5c06\u4ed6\u6276\u5165\u8239\u4e2d\uff0c\u89c1\u4ed6\u6c14\u606f\u5944\u5944\uff0c\u8bdd\u4e5f\u8bf4\u4e0d\u51fa\u6765\u4e86\u3002 \u3000\u3000\u201c\u6211\u5fc3\u4e2d\u60f3\uff0c\u4e07\u9707\u5c71\u4ed6\u4eec\u5982\u4e0d\u6b7b\u5fc3\uff0c\u5b9a\u4f1a\u8d76\u5411\u4e0b\u6e38\u5bfb\u89c5\u8fd9\u8001\u8005\u7684\u5c38\u4f53\u3002\u4e5f\u662f\u6211\u81ea\u5c45\u4fa0\u4e49\u9053\uff0c\u8981\u6551\u4eba\u6027\u547d\uff0c\u4fbf\u547d\u8239\u5bb6\u7acb\u5373\u5f00\u8239\uff0c\u6eaf\u6c5f\u800c\u4e0a\uff0c\u56de\u5411\u4e09\u5ce1\u3002\u8239\u5bb6\u5f53\u7136\u4e0d\u613f\uff0c\u534a\u591c\u4e2d\u53c8\u6ca1\u7ea4\u592b\uff0c\u4e0a\u4e09\u5ce1\u5c82\u662f\u6613\u4e8b\uff1f\u4f46\u603b\u800c\u8a00\u4e4b\uff0c\u6709\u94b1\u80fd\u4f7f\u9b3c\u63a8\u78e8\u4fbf\u4e86\u3002 \u3000\u3000\u201c\u6211\u8eab\u8fb9\u5e26\u5f97\u6709\u91d1\u521b\u836f\uff0c\u4fbf\u66ff\u90a3\u8001\u8005\u6cbb\u4f24\u3002\u53ef\u867d\u4ed6\u80cc\u4e0a\u90a3\u4e00\u5251\u523a\u5f97\u597d\u6df1\uff0c\u7a7f\u901a\u4e86\u80ba\uff0c\u8fd9\u4f24\u662f\u6cbb\u4e0d\u597d\u7684\u4e86\u3002\u6211\u53ea\u6709\u5c3d\u529b\u800c\u4e3a\uff0c\u751a\u4e48\u4e5f\u4e0d\u95ee\u4ed6\uff0c\u4e00\u8def\u4e0a\u4e70\u4e86\u597d\u9152\u597d\u8089\u670d\u4f8d\u3002\u6211\u89c1\u4e86\u4ed6\u7684\u6b66\u529f\uff0c\u4eb2\u773c\u89c1\u4ed6\u8dc3\u5165\u957f\u6c5f\uff0c\u94bb\u5165\u8239\u5e95\uff0c\u8fd9\u4efd\u80c6\u8bc6\u548c\u529f\u592b\uff0c\u4fbf\u503c\u5f97\u6211\u4e01\u5178\u7ed9\u4ed6\u5356\u547d\u3002 \u3000\u3000\u201c\u8fd9\u4e48\u6cbb\u4e86\u4e09\u5929\uff0c\u90a3\u8001\u8005\u95ee\u4e86\u6211\u7684\u59d3\u540d\uff0c\u82e6\u7b11\u9053\uff1a\u2018\u5f88\u597d\uff01 \u3000\u3000\u5f88\u597d\uff01\u2019\u4ece\u6000\u4e2d\u53d6\u51fa\u4e00\u4e2a\u6cb9\u7eb8\u5305\u6765\u4ea4\u7ed9\u6211\u3002\u6211\u9053\uff1a\u2018\u8001\u4e08\u7684\u4eb2\u4eba\u5728\u751a\u4e48\u5730\u65b9\uff1f\u6211\u5fc3\u66ff\u8001\u4e08\u9001\u5230\uff0c\u51b3\u4e0d\u6709\u8bef\u3002\u2019\u90a3\u8001\u8005\u9053\uff1a\u2018\u4f60\u77e5\u9053\u6211\u662f\u8c01\uff1f\u2019\u6211\u9053\uff1a\u2018\u4e0d\u77e5\u3002\u2019\u4ed6\u9053\uff1a\u2018\u6211\u662f\u6885\u5ff5\u7b19\u3002\u2019\u201c\u6211\u8fd9\u4e00\u60ca\u81ea\u7136\u662f\u975e\u540c\u5c0f\u53ef\u3002\u751a\u4e48\uff1f\u4f60\u4e0d\u5947\u602a\uff1f\u6885\u5ff5\u7b19\u662f\u8c01\uff0c\u4f60\u4e0d\u77e5\u9053\u4e48\uff1f\u662f\u94c1\u9aa8\u58a8\u843c\u6885\u5ff5\u7b19\u554a\u3002\u4f60\u771f\u7684\u4e0d\u77e5\u9053\uff1f\uff08\u72c4\u4e91\u53c8\u6447\u6447\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u4ece\u6765\u6ca1\u542c\u89c1\u8fd9\u540d\u5b57\u3002\u201d\uff09\u563f\u563f\uff0c\u662f\u4e86\uff0c\u4f60\u5e08\u7236\u81ea\u7136\u4e0d\u4f1a\u8ddf\u4f60\u8bf4\u3002\u94c1\u9aa8\u58a8\u843c\u6885\u5ff5\u7b19\uff0c\u662f\u6e58\u4e2d\u6b66\u6797\u540d\u5bbf\uff0c\u4ed6\u6709\u4e09\u4e2a\u5f1f\u5b50\uff0c\u5927\u5f1f\u5b50\u540d\u53eb\u4e07\u9707\u5c71\uff0c\u4e8c\u5f1f\u5b50\u53eb\u8a00\u8fbe\u5e73\uff0c\u4e09\u5f1f\u5b50\u53eb\u2026\u2026\uff08\u72c4\u4e91\u63d2\u53e3\u9053\uff1a\u201c\u4e01\u2026\u2026\u4e01\u5927\u54e5\uff0c\u4f60\u2026\u2026\u4f60\u8bf4\u751a\u4e48\uff1f\u201d\uff09 \u3000\u3000\u4ed6\u4e09\u5f1f\u5b50\u662f\u621a\u957f\u53d1\u3002\u5f53\u65f6\u6211\u542c\u4ed6\u81ea\u627f\u662f\u6885\u5ff5\u7b19\uff0c\u8fd9\u4efd\u60ca\u5947\uff0c\u8ddf\u4f60\u6b64\u523b\u662f\u4e00\u6a21\u4e00\u6837\u3002\u6211\u4eb2\u773c\u770b\u5230\u6708\u591c\u6c5f\u8fb9\u90a3\u573a\u6076\u6597\uff0c\u89c1\u5230\u65b9\u9707\u5c71\u5e08\u5144\u5f1f\u4e8c\u4eba\u51fa\u624b\u7684\u6bd2\u8fa3\uff0c\u53ea\u6709\u6bd4\u4f60\u66f4\u52a0\u9707\u9a87\u3002 \u3000\u3000\u201c\u6885\u8001\u5148\u751f\u5411\u6211\u82e6\u7b11\u7740\u6447\u6447\u5934\uff0c\u9053\uff1a\u2018\u6211\u7684\u7b2c\u4e09\u5f92\u513f\u6700\u5389\u5bb3\uff0c\u62a2\u5148\u51b7\u4e0d\u9632\u7684\u5728\u6211\u80cc\u4e0a\u63d2\u4e86\u4e00\u5251\uff0c\u8001\u5934\u513f\u624d\u903c\u5f97\u8df3\u6c5f\u9003\u547d\u3002\u2019\uff08\u72c4\u4e91\u98a4\u58f0\u9053\uff1a\u201c\u751a\u4e48\uff1f\u771f\u662f\u6211\u5e08\u7236\u5148\u52a8\u624b\uff1f\u201d\uff09\u6211\u4e0d\u77e5\u8bf4\u4e9b\u751a\u4e48\u8bdd\u6765\u5b89\u6170\u4ed6\u624d\u662f\uff0c\u5fc3\u60f3\u4ed6\u5e08\u5f92\u56db\u4eba\u53cd\u76ee\u6210\u4ec7\uff0c\u5fc5\u6709\u91cd\u5927\u4e4b\u6781\u7684\u539f\u56e0\uff0c\u6211\u662f\u5916\u4eba\uff0c\u867d\u662f\u597d\u5947\uff0c\u5374\u4e5f\u4e0d\u4fbf\u591a\u95ee\u3002\u6885\u8001\u5148\u751f\u9053\uff1a\u2018\u6211\u5728\u8fd9\u4e16\u4e0a\u7684\u4eb2\u4eba\uff0c\u5c31\u8fd9\u4e48\u4e09\u4e2a\u5f92\u513f\u3002\u4ed6\u4eec\u60f3\u593a\u6211\u4e00\u90e8\u5251\u8c31\uff0c\u4e0d\u60dc\u884c\u523a\u5e08\u7236\uff0c\u563f\u563f\uff0c\u4e56\u5f92\u513f\u3002\u8fd9\u90e8\u5251\u8c31\u662f\u7ed9\u4ed6\u4eec\u593a\u53bb\u4e86\uff0c\u53ef\u662f\u6ca1\u6709\u5251\u8bc0\uff0c\u90a3\u53c8\u6709\u751a\u4e48\u7528\uff1f\u8fde\u57ce\u5251\u6cd5\u867d\u7136\u795e\u5947\uff0c\u53c8\u600e\u53ca\u5f97\u4e0a\u795e\u7167\u529f\u4e86\uff1f\u8fd9\u90e8\u795e\u7167\u7ecf\uff0c\u6211\u9001\u4e86\u7ed9\u4f60\uff0c\u597d\u597d\u7684\u7ec3\u7f62\u3002\u6b64\u7ecf\u82e5\u7136\u7ec3\u6210\uff0c\u5a01\u529b\u5947\u5927\uff0c\u5343\u4e07\u4e0d\u53ef\u8bef\u4f20\u532a\u4eba\u3002\u201d\u6211\u7684\u795e\u7167\u7ecf\uff0c\u5c31\u662f\u8fd9\u6837\u6765\u7684\u3002 \u3000\u3000\u201c\u6885\u8001\u751f\u751f\u8bf4\u8fd9\u8fd9\u756a\u8bdd\u540e\uff0c\u6ca1\u6328\u4e0a\u4e24\u4e2a\u65f6\u8fb0\u4fbf\u6b7b\u4e86\u3002\u6211\u5728\u5deb\u5ce1\u7684\u6c5f\u8fb9\u7ed9\u4ed6\u5b89\u846c\uff0c\u5f53\u65f6\u6211\u5168\u4e0d\u77e5\u9053\u662f\u5982\u6b64\u4e8b\u5173\u91cd\u5927\uff0c\u53ea\u9053\u662f\u4ed6\u672c\u95e8\u4e2d\u6240\u4e89\u593a\u7684\u4e00\u90e8\u5251\u672f\u8bc0\u8c31\uff0c\u56e0\u6b64\u6ca1\u60f3\u5230\u987b\u5f97\u4e25\u5b88\u9690\u79d8\uff0c\u4fbf\u5728\u6885\u8001\u5148\u751f\u5893\u524d\u7acb\u4e86\u4e00\u5757\u7891\uff0c\u5199\u4e0a\u2018\u4e24\u6e56\u5927\u4fa0\u6885\u5148\u751f\u5ff5\u7b19\u4e4b\u5893\u201d\u54ea\u77e5\u9053\u8fd9\u5757\u77f3\u7891\uff0c\u7adf\u7ed9\u6211\u60f9\u6765\u4e86\u65e0\u7a77\u7684\u70e6\u607c\u3002\u6709\u4eba\u4fbf\u4ece\u8fd9\u77f3\u7891\u7684\u7ebf\u7d22\uff0c\u8ffd\u67e5\u77f3\u5320\u3001\u8239\u592b\uff0c\u67e5\u5230\u8fd9\u7891\u662f\u6211\u7acb\u7684\uff0c\u6885\u8001\u5148\u751f\u662f\u6211\u846c\u7684\uff0c\u90a3\u4e48\u6885\u8001\u5148\u751f\u8eab\u4e0a\u6240\u6000\u7684\u4e1c\u897f\uff0c\u5341\u4e4b\u516b\u4e5d\u662f\u843d\u5165\u4e86\u6211\u624b\u4e2d\u3002 \u3000\u3000\u201c\u8fc7\u4e0d\u4e86\u4e09\u4e2a\u6708\uff0c\u4fbf\u6709\u4e00\u4e2a\u6c5f\u6e56\u8c6a\u5ba2\u5bfb\u5230\u6211\u5bb6\u4e2d\u6765\u3002\u6765\u4eba\u793c\u8c8c\u5468\u5230\uff0c\u8bf4\u8bdd\u541e\u541e\u5410\u5410\u7684\u4e0d\u7740\u8fb9\u9645\uff0c\u540e\u6765\u7ec8\u4e8e\u5410\u9732\u4e86\u6765\u610f\uff0c\u4ed6\u8bf4\u6709\u4e00\u5f20\u5927\u5b9d\u85cf\u7684\u5730\u56fe\uff0c\u662f\u5728\u6885\u8001\u5148\u751f\u624b\u4e2d\uff0c\u8fd9\u65f6\u60f3\u5fc5\u4e3a\u6211\u6240\u5f97\uff0c\u8bf7\u6211\u53d6\u51fa\u6765\uff0c\u5927\u5bb6\u53c2\u8be6\u53c2\u8be6\uff0c\u5982\u679c\u627e\u5230\u4e86\u5b9d\u85cf\uff0c\u6211\u5f97\u4e03\u6210\uff0c\u4ed6\u5f97\u4e09\u6210\u3002 \u3000\u3000\u201c\u6885\u8001\u5148\u751f\u4ea4\u7ed9\u6211\u7684\uff0c\u4e43\u662f\u4e00\u5957\u4fee\u4e60\u4e0a\u4e58\u5185\u529f\u7684\u79d8\u7ecf\uff0c\u8fd8\u8bf4\u4e86\u51e0\u53e5\u5251\u8bc0\uff0c\u8bf4\u662f\u751a\u4e48\u201c\u201d\uff0c\u90a3\u4e0d\u8fc7\u51e0\u4e2a\u6570\u76ee\u5b57\uff0c\u6b64\u5916\u4e00\u65e0\u6240\u6709\uff0c\u54ea\u91cc\u6709\u751a\u4e48\u5b9d\u85cf\u7684\u5730\u56fe\u3002\u6211\u636e\u5b9e\u4ee5\u544a\uff0c\u90a3\u4eba\u4e0d\u4fe1\uff0c\u8981\u6211\u5c06\u6b66\u529f\u79d8\u8bc0\u7ed9\u4ed6\u770b\u3002\u6885\u8001\u5148\u751f\u90d1\u91cd\u53ee\u549b\uff0c\u5343\u4e07\u4e0d\u53ef\u8bef\u4f20\u532a\u4eba\u3002\u6211\u81ea\u662f\u4e0d\u5141\u4ea4\u51fa\uff0c\u90a3\u4eba\u600f\u600f\u800c\u53bb\u3002\u8fc7\u4e0d\u4e86\u4e09\u5929\uff0c\u534a\u591c\u91cc\u4fbf\u6478\u5230\u6211\u5bb6\u91cc\u6765\uff0c\u8ddf\u6211\u52a8\u4e0a\u4e86\u624b\uff0c\u4ed6\u80a9\u5934\u5e26\u4e86\u5f69\uff0c\u8fd9\u624d\u77e5\u96be\u800c\u9000\u3002 \u3000\u3000\u201c\u98ce\u58f0\u4e00\u6cc4\u6f0f\uff0c\u6765\u8bbf\u7684\u4eba\u8d8a\u6765\u8d8a\u591a\u3002\u6211\u5b9e\u5728\u5e94\u4ed8\u4e0d\u4e86\uff0c\u5230\u5f97\u6700\u540e\uff0c\u8fde\u4e07\u9707\u5c71\u4e5f\u6765\u4e86\u3002\u6211\u5728\u8346\u95e8\u8001\u5bb6\u803d\u4e0d\u4e0b\u53bb\uff0c\u53ea\u6709\u4e00\u8d70\u4e86\u4e4b\uff0c\u9690\u59d3\u57cb\u540d\uff0c\u8d70\u5f97\u8fdc\u8fdc\u5730\uff0c\u76f4\u5230\u5173\u5916\u7267\u573a\u53bb\u5e72\u4e70\u5356\u7272\u53e3\u7684\u52fe\u5f53\u3002\u8fd9\u4e48\u8fc7\u5f97\u4e94\u516d\u5e74\uff0c\u518d\u4e5f\u542c\u4e0d\u5230\u751a\u4e48\u98ce\u58f0\u4e86\uff0c\u5fc3\u4e2d\u8bb0\u6302\u7740\u8001\u5bb6\uff0c\u4fbf\u6539\u4e86\u88c5\uff0c\u56de\u5230\u8346\u95e8\u6765\u77a7\u77a7\u3002\u54ea\u77e5\u8001\u5c4b\u65e9\u7ed9\u4eba\u70e7\u6210\u4e86\u4e00\u7247\u767d\u5730\uff0c\u5e78\u597d\u6211\u4e5f\u6ca1\u751a\u4e48\u4eb2\u4eba\uff0c\u8fd9\u4e48\u4e00\u6765\uff0c\u53cd\u800c\u5e72\u51c0\u3002\u201d \u3000\u3000\u72c4\u4e91\u5fc3\u4e2d\u4e00\u7247\u8ff7\u60d8\uff0c\u8bf4\u8981\u4e0d\u4fe1\u7f62\uff0c\u8fd9\u4f4d\u4e01\u5927\u54e5\u4ece\u6765\u4e0d\u6253\u8bf3\u8bed\uff0c\u4f55\u51b5\u8ddf\u4ed6\u4eb2\u5982\u9aa8\u8089\uff0c\u4f55\u5fc5\u634f\u9020\u4e00\u756a\u8c0e\u8a00\u6765\u6b3a\u9a97\u81ea\u5df1\uff1f\u8981\u4fe1\u4e86\u4ed6\u7684\u8bdd\u7f62\uff0c\u96be\u9053\u4e00\u5411\u8fd9\u4e48\u5fe0\u539a\u8001\u5b9e\u7684\u5e08\u7236\uff0c\u7adf\u662f\u8fd9\u4e48\u4e00\u4e2a\u9634\u9669\u72e0\u6bd2\u4e4b\u4eba\uff1f \u3000\u3000\u53ea\u89c1\u4e01\u5178\u8138\u4e0a\u7684\u808c\u8089\u4e0d\u4f4f\u8df3\u52a8\uff0c\u770b\u6765\u6bd2\u6027\u6b63\u81ea\u8513\u5ef6\uff0c\u72c4\u4e91\u9053\uff1a\u201c\u4e01\u5927\u54e5\uff0c\u6211\u5e08\u7236\u8ddf\u592a\u5e08\u7236\u7684\u4e8b\uff0c\u54b1\u4eec\u4e0d\u5fd9\u67e5\u7a76\u3002\u4f60\u2026\u2026\u8fd8\u662f\u4ed4\u7ec6\u60f3\u60f3\uff0c\u6709\u751a\u4e48\u6cd5\u5b50\uff0c\u80fd\u6cbb\u4f60\u8eab\u4e0a\u4e2d\u7684\u6bd2\u3002\u201d \u3000\u3000\u4e01\u5178\u6447\u5934\u9053\uff1a\u201c\u6211\u8bf4\u8fc7\u53eb\u4f60\u522b\u6253\u5c94\uff0c\u4f60\u5c31\u9759\u9759\u7684\u542c\u7740\u3002 \u3000\u3000\u201c\u90a3\u662f\u5728\u4e5d\u5e74\u591a\u4e4b\u524d\uff0c\u4e5d\u6708\u4e0a\u65ec\uff0c\u6211\u5230\u4e86\u6c49\u53e3\uff0c\u5411\u836f\u6750\u5e97\u51fa\u5356\u4ece\u5173\u5916\u5e26\u6765\u7684\u8001\u5c71\u4eba\u53c2\u3002\u836f\u6750\u5e97\u4e3b\u4eba\u5012\u662f\u4e2a\u98ce\u96c5\u4eba\uff0c\u505a\u5b8c\u4e86\u751f\u610f\uff0c\u9080\u6211\u53bb\u770b\u6c49\u53e3\u51fa\u540d\u7684\u83ca\u82b1\u4f1a\u3002\u8fd9\u83ca\u82b1\u4f1a\u4e2d\u540d\u8d35\u7684\u54c1\u79cd\u5012\u771f\u4e0d\u5c11\uff0c\u55ef\uff0c\u9ec4\u83ca\u6709\u90fd\u80dc\u3001\u91d1\u828d\u836f\u3001\u9ec4\u9e64\u7fce\u3001\u62a5\u541b\u77e5\u3001\u5fa1\u888d\u9ec4\u3001\u91d1\u5b54\u96c0\u3001\u4fa7\u91d1\u76cf\u3001\u83ba\u7fbd\u9ec4\u3002\u767d\u83ca\u6709\u6708\u4e0b\u767d\u3001\u7389\u7261\u4e39\u3001\u7389\u5b9d\u76f8\u3001\u7389\u73b2\u73d1\u3001\u4e00\u56e2\u96ea\u3001\u8c82\u8749\u62dc\u6708\u3001\u592a\u6db2\u83b2\u3002\u7d2b\u83ca\u6709\u78a7\u6c5f\u971e\u3001\u53cc\u98de\u71d5\u3001\u7fe6\u971e\u7ee1\u3001\u7d2b\u7389\u83b2\u3001\u7d2b\u971e\u676f\u3001\u739b\u7459\u76d8\u3001\u7d2b\u7f57\u7e56\u3002\u7ea2\u83ca\u6709\u7f8e\u4eba\u7ea2\u3001\u6d77\u4e91\u7ea2\u3001\u9189\u8d35\u5983\u3001\u7ee3\u8299\u84c9\u3001\u80ed\u8102\u9999\u3001\u9526\u8354\u679d\u3001\u9e64\u9876\u7ea2\u3002\u6de1\u7ea2\u8272\u7684\u6709\u4f5b\u89c1\u7b11\u3001\u7ea2\u7c89\u56e2\u3001\u6843\u82b1\u83ca\u3001\u897f\u65bd\u7c89\u3001\u80dc\u7eef\u6843\u3001\u7389\u697c\u6625\u2026\u2026\u201d \u3000\u3000\u4ed6\u5404\u79cd\u5404\u6837\u83ca\u82b1\u54c1\u79cd\u7684\u540d\u79f0\u968f\u53e3\u800c\u51fa\uff0c\u5012\u4f3c\u6bd4\u6b66\u529f\u7684\u62db\u5f0f\u66f4\u52a0\u719f\u4e60\u3002\u72c4\u4e91\u6709\u4e9b\u8be7\u5f02\uff0c\u4f46\u968f\u5373\u60f3\u8d77\uff0c\u4e01\u5927\u54e5\u662f\u7231\u82b1\u4e4b\u4eba\uff0c\u56e0\u6b64\u90a3\u4f4d\u51cc\u5c0f\u59d0\u7684\u7a97\u69db\u4e0a\u9c9c\u82b1\u4e0d\u65ad\u3002\u4ed6\u719f\u77e5\u8bf8\u822c\u83ca\u82b1\u7684\u54c1\u79cd\u540d\u79f0\uff0c\u81ea\u975e\u5947\u4e8b\u3002 \u3000\u3000\u4e01\u5178\u8bf4\u5230\u8fd9\u4e9b\u82b1\u540d\u65f6\uff0c\u5634\u89d2\u8fb9\u5e26\u7740\u5fae\u7b11\uff0c\u795e\u8272\u751a\u662f\u67d4\u548c\uff0c\u8f7b\u8f7b\u7684\u9053\uff1a\u201c\u6211\u4e00\u9762\u770b\uff0c\u4e00\u9762\u8d5e\u8d4f\uff0c\u8bf4\u51fa\u8fd9\u4e9b\u83ca\u82b1\u7684\u540d\u79f0\uff0c\u54c1\u8bc4\u4f18\u52a3\u3002\u5f53\u6211\u89c2\u8d4f\u5b8c\u6bd5\uff0c\u5c06\u51fa\u82b1\u56ed\u65f6\uff0c\u8bf4\u9053\uff1a\u2018\u8fd9\u83ca\u82b1\u4f1a\u4e5f\u7b97\u662f\u5341\u5206\u96be\u5f97\u4e86\uff0c\u5c31\u53ef\u60dc\u6ca1\u7eff\u83ca\u3002\u2019\u201c\u5ffd\u542c\u5f97\u4e00\u4e2a\u5c0f\u59d1\u5a18\u7684\u58f0\u97f3\u5728\u6211\u80cc\u540e\u8bf4\u9053\uff1a\u2018\u5c0f\u59d0\uff0c\u8fd9\u4eba\u5012\u77e5\u9053\u7eff\u83ca\u82b1\u3002\u6211\u4eec\u5bb6\u91cc\u7684\u2018\u6625\u6c34\u78a7\u6ce2\u2019\u3001\u2018\u7eff\u7389\u5982\u610f\u2019\uff0c\u5e73\u5e38\u4eba\u54ea\u91cc\u8f7b\u6613\u89c1\u5f97\uff1f\u201d \u3000\u3000\u201c\u6211\u56de\u8fc7\u5934\u6765\uff0c\u53ea\u89c1\u4e00\u4e2a\u6e05\u79c0\u7edd\u4fd7\u7684\u5c11\u5973\u6b63\u5728\u89c2\u8d4f\u83ca\u82b1\uff0c\u7a7f\u4e00\u8eab\u5ae9\u9ec4\u886b\u5b50\uff0c\u5f53\u771f\u662f\u4eba\u6de1\u5982\u83ca\uff0c\u6211\u4e00\u751f\u4e4b\u4e2d\uff0c\u4ece\u672a\u89c1\u8fc7\u8fd9\u822c\u96c5\u81f4\u6e05\u4e3d\u7684\u59d1\u5a18\u3002\u5979\u8eab\u65c1\u8ddf\u7740\u4e00\u4e2a\u5341\u56db\u4e94\u5c81\u7684\u4e2b\u9b1f\u3002\u90a3\u4f4d\u5c0f\u59d0\u89c1\u6211\u6ce8\u89c6\u5979\uff0c\u8138\u4e0a\u767b\u65f6\u7ea2\u4e86\uff0c\u4f4e\u58f0\u9053\uff1a\u2018\u5bf9\u4e0d\u8d77\uff0c\u5148\u751f\u522b\u89c1\u602a\uff0c\u5c0f\u4e2b\u5934\u968f\u53e3\u4e71\u8bf4\u3002\u2019\u6211\u970e\u65f6\u95f4\u5446\u4f4f\u4e86\uff0c\u751a\u4e48\u8bdd\u4e5f\u8bf4\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u201c\u6211\u773c\u671b\u5979\u51fa\u4e86\u56ed\u5b50\uff0c\u4ecd\u662f\u6014\u6014\u7684\u4e0d\u4f1a\u8bf4\u8bdd\u3002\u90a3\u836f\u5e97\u4e3b\u4eba\u9053\uff1a\u2018\u8fd9\u4e00\u4f4d\u662f\u6b66\u660c\u51cc\u7ff0\u6797\u5bb6\u7684\u5c0f\u59d0\uff0c\u54b1\u4eec\u6b66\u6c49\u51fa\u540d\u7684\u7f8e\u4eba\u3002 \u3000\u3000\u5979\u5bb6\u91cc\u7684\u82b1\u5349\uff0c\u90a3\u662f\u4e86\u4e0d\u8d77\u7684\u3002\u2019 \u3000\u3000\u201c\u6211\u51fa\u4e86\u56ed\u5b50\uff0c\u548c\u836f\u5e97\u4e3b\u4eba\u5206\u4e86\u624b\uff0c\u56de\u5230\u5ba2\u5e97\uff0c\u5fc3\u4e2d\u9664\u4e86\u90a3\u4f4d\u51cc\u5c0f\u59d0\u4e4b\u5916\uff0c\u518d\u6ca1\u4e1d\u6beb\u522b\u7684\u5ff5\u5934\u3002\u5230\u5f97\u5348\u540e\uff0c\u6211\u4fbf\u8fc7\u6c5f\u5230\u4e86\u6b66\u660c\uff0c\u95ee\u660e\u9014\u5f84\uff0c\u5230\u51cc\u7ff0\u6797\u5e9c\u4e0a\u53bb\u3002\u5018\u82e5\u5c31\u6b64\u8fdb\u53bb\u62dc\u8bbf\uff0c\u90a3\u662f\u592a\u4e5f\u5192\u6627\uff0c\u6211\u5728\u5e9c\u95e8\u5916\u8e31\u6765\u8e31\u53bb\uff0c\u5fc3\u91cc\u4e03\u4e0a\u516b\u4e0b\uff0c\u53c8\u662f\u6b22\u559c\uff0c\u53c8\u662f\u5bb3\u6015\uff0c\u53c8\u65a5\u9a82\u81ea\u5df1\u8be5\u6b7b\u3002\u6211\u90a3\u65f6\u5e74\u7eaa\u5df2\u4e0d\u7b97\u5c0f\u4e86\uff0c\u53ef\u662f\u5c31\u50cf\u521d\u5815\u60c5\u7f51\u7684\u5c0f\u4f19\u5b50\u4e00\u822c\uff0c\u53d8\u6210\u4e86\u53ea\u6ca1\u5934\u82cd\u8747\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u5230\u8fd9\u91cc\uff0c\u8138\u4e0a\u73b0\u51fa\u4e00\u80a1\u5947\u5f02\u7684\u5149\u5f69\uff0c\u773c\u4e2d\u795e\u5149\u6e5b\u6e5b\uff0c\u663e\u5f97\u751a\u662f\u5174\u594b\u3002 \u3000\u3000\u72c4\u4e91\u611f\u5230\u5bb3\u6015\uff0c\u62c5\u5fc3\u4ed6\u7a81\u7136\u4f1a\u4f53\u529b\u4e0d\u652f\uff0c\u8bf4\u9053\uff1a\u201c\u4e01\u5927\u54e5\uff0c\u4f60\u8fd8\u662f\u5b89\u5b89\u9759\u9759\u7684\u6b47\u4e00\u4f1a\u3002\u6211\u53bb\u627e\u4e2a\u5927\u592b\u6765\u7ed9\u4f60\u77a7\u77a7\uff0c\u672a\u5fc5\u5c31\u771f\u7684\u6ca1\u6cd5\u5b50\u6cbb\u3002\u201d\u8bf4\u7740\u4fbf\u7ad9\u8d77\u8eab\u6765\u3002 \u3000\u3000\u4e01\u5178\u4e00\u628a\u6293\u4f4f\u4ed6\u8863\u8896\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u4fe9\u8fd9\u526f\u6a21\u6837\u51fa\u53bb\u627e\u5927\u592b\uff0c\u90a3\u4e0d\u662f\u81ea\u5bfb\u6b7b\u8def\u4e48\uff1f\u201d\u987f\u4e86\u4e00\u987f\uff0c\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u72c4\u5144\u5f1f\uff0c\u90a3\u65e5\u4f60\u542c\u5230\u5e08\u59b9\u5ac1\u4e86\u522b\u4eba\uff0c\u6c14\u5f97\u4e0a\u540a\u3002\u4f60\u5e08\u59b9\u5f85\u4f60\u65e0\u60c5\u65e0\u4e49\uff0c\u5b9e\u5728\u4e0d\u503c\u5f97\u4e3a\u5979\u5bfb\u6b7b\u3002\u201d \u3000\u3000\u72c4\u4e91\u70b9\u5934\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u4e9b\u5e74\u6765\uff0c\u6211\u4e5f\u5df2\u60f3\u7a7f\u5566\u3002\u201d \u3000\u3000\u4e01\u5178\u9053\uff1a\u201c\u5018\u82e5\u4f60\u5e08\u59b9\u5bf9\u4f60\u4e00\u5f80\u60c5\u6df1\uff0c\u7ec8\u4e8e\u4e3a\u4f60\u800c\u6b7b\uff0c\u90a3\u4e48\uff0c\u4f60\u4e5f\u8be5\u4e3a\u5979\u6b7b\u4e86\u3002\u201d\u72c4\u4e91\u7a81\u7136\u7701\u609f\uff0c\u9053\uff1a\u201c\u90a3\u4f4d\u51cc\u5c0f\u59d0\uff0c\u662f\u4e3a\u4f60\u6b7b\u7684\uff1f\u201d\u4e01\u5178\u9053\uff1a\u201c\u6b63\u662f\u3002\u5979\u4e3a\u6211\u6b7b\u4e86\uff0c\u73b0\u5728\u6211\u4e5f\u5c31\u8981\u4e3a\u5979\u800c\u6b7b\u5566\u3002\u6211\u2026\u2026\u6211\u5fc3\u91cc\u5f88\u5feb\u6d3b\u3002\u5979\u5bf9\u6211\u60c5\u6df1\u4e49\u91cd\uff0c\u6211\u2026\u2026\u6211\u4e5f\u5f85\u5979\u4e0d\u9519\u3002\u72c4\u5144\u5f1f\uff0c\u522b\u8bf4\u6211\u4e2d\u6bd2\u65e0\u836f\u53ef\u6cbb\uff0c\u5c31\u662f\u533b\u6cbb\u5f97\u597d\uff0c\u6211\u4e5f\u4e0d\u6cbb\u3002\u201d \u3000\u3000\u84e6\u7136\u4e4b\u95f4\uff0c\u72c4\u4e91\u5fc3\u4e2d\u611f\u5230\u4e00\u9635\u96be\u4ee5\u5f62\u5bb9\u7684\u4f24\u5fc3\uff0c\u90a3\u5f53\u7136\u662f\u4e3a\u4e86\u75db\u60dc\u826f\u53cb\u5c06\u901d\uff0c\u53ef\u662f\u5728\u5185\u5fc3\u6df1\u5904\uff0c\u53cd\u800c\u5728\u7fa1\u6155\u4ed6\u7684\u5e78\u798f\uff0c\u56e0\u4e3a\u5728\u8fd9\u4e16\u754c\u4e0a\uff0c\u6709\u4e00\u4e2a\u5973\u5b50\u662f\u771f\u5fc3\u8bda\u610f\u7684\u7231\u4ed6\uff0c\u7518\u613f\u4e3a\u4ed6\u800c\u6b7b\uff0c\u800c\u4ed6\uff0c\u4e5f\u662f\u540c\u6837\u6df1\u631a\u7684\u62a5\u7b54\u4e86\u8fd9\u756a\u6069\u60c5\u3002\u53ef\u662f\u81ea\u5df1\u5462\uff1f\u81ea\u5df1\u5462\uff1f \u3000\u3000\u4e01\u5178\u53c8\u6c89\u6d78\u5728\u5f80\u65e5\u7684\u56de\u5fc6\u4e4b\u4e2d\uff0c\u8bf4\u9053\uff1a\u201c\u51cc\u7ff0\u6797\u7684\u5e9c\u95e8\u662f\u6731\u7ea2\u7684\u5927\u95e8\uff0c\u95e8\u53e3\u4e24\u53ea\u5927\u77f3\u72ee\u5b50\uff0c\u6211\u662f\u4e2a\u6c5f\u6e56\u4eba\uff0c\u600e\u80fd\u8d38\u7136\u95ef\u8fdb\u53bb\uff1f\u6211\u5728\u95e8\u5916\u8e31\u4e86\u4e09\u4e2a\u65f6\u8fb0\uff0c\u76f4\u8e31\u5230\u9ec4\u660f\uff0c\u81ea\u5df1\u4e5f\u4e0d\u77e5\u9053\u5230\u5e95\u5728\u76fc\u671b\u751a\u4e48\u3002 \u3000\u3000\u201c\u5929\u5feb\u9ed1\u4e86\uff0c\u6211\u8fd8\u662f\u6ca1\u60f3\u5230\u8981\u79bb\u5f00\uff0c\u5ffd\u7136\u95f4\uff0c\u65c1\u8fb9\u5c0f\u95e8\u4e2d\u51fa\u6765\u4e00\u4e2a\u5c11\u5973\uff0c\u6084\u6b65\u8d70\u5230\u6211\u8eab\u8fb9\u3002\u8f7b\u58f0\u8bf4\u9053\uff1a\u2018\u50bb\u74dc\uff0c\u4f60\u5728\u8fd9\u91cc\u8fd8\u4e0d\u8d70\uff1f\u5c0f\u59d0\u8bf7\u4f60\u56de\u5bb6\u53bb\u7f62\uff01\u2019\u6211\u4e00\u770b\uff0c\u6b63\u662f\u51cc\u5c0f\u59d0\u8eab\u8fb9\u7684\u90a3\u4e2a\u4e2b\u5934\u3002\u6211\u5fc3\u4e2d\u6026\u6026\u4e71\u8df3\uff0c\u7ed3\u7ed3\u5df4\u5df4\u7684\u9053\uff1a\u2018\u4f60\u2026\u2026\u4f60\u8bf4\u751a\u4e48\uff1f\u2019\u201c\u5979\u7b11\u563b\u563b\u7684\u9053\uff1a\u2018\u5c0f\u59d0\u548c\u6211\u8d4c\u4e86\u4e1c\u9053\uff0c\u8d4c\u4f60\u751a\u4e48\u65f6\u5019\u624d\u8d70\u3002\u6211\u5df2\u8d62\u4e86\u4e24\u4e2a\u94f6\u6307\u73af\u5566\uff0c\u4f60\u8fd8\u4e0d\u8d70\uff1f\u2019\u6211\u53c8\u60ca\u53c8\u559c\uff0c\u9053\uff1a\u2018\u6211\u5728\u8fd9\u91cc\uff0c\u5c0f\u59d0\u65e9\u77e5\u9053\u4e86\u4e48\uff1f\u2019\u90a3\u4e2b\u9b1f\u7b11\u9053\uff1a\u2018\u6211\u51fa\u6765\u77a7\u4e86\u4f60\u597d\u51e0\u6b21\uff0c\u4f60\u59cb\u7ec8\u6ca1\u89c1\u5230\u6211\uff0c\u4f60\u7075\u9b42\u513f\u4e5f\u4e0d\u89c1\u4e86\uff0c\u662f\u4e0d\u662f\uff1f\u2019\u5979\u7b11\u4e86\u7b11\uff0c\u8f6c\u8eab\u4fbf\u8d70\u3002\u6211\u5fd9\u9053\uff1a\u2018\u59ca\u59ca\uff01\u2019\u5979\u8bf4\uff1a\u2018\u600e\u4e48\uff1f\u4f60\u60f3\u751a\u4e48\uff1f\u2019\u6211\u9053\uff1a\u2018\u542c\u59ca\u59ca\u8bf4\uff0c\u5e9c\u4e0a\u6709\u51e0\u672c\u540d\u79cd\u7684\u7eff\u83ca\u82b1\uff0c\u6211\u5f88\u60f3\u77a7\u77a7\u3002\u4e0d\u77e5\u884c\u4e0d\u884c\uff1f\u2019\u5979\u70b9\u70b9\u5934\uff0c\u4f38\u624b\u6307\u7740\u540e\u56ed\u7684\u4e00\u89d2\u7ea2\u697c\uff0c\u8bf4\u9053\uff1a\u2018\u6211\u53bb\u6c42\u6c42\u5c0f\u59d0\uff0c\u8981\u662f\u5979\u7b54\u5141\uff0c\u5c31\u4f1a\u628a\u7eff\u83ca\u82b1\u653e\u5728\u90a3\u7ea2\u697c\u7684\u7a97\u69db\u4e0a\u3002\u2019\u201c\u90a3\u5929\u665a\u4e0a\uff0c\u6211\u5728\u51cc\u5e9c\u5916\u7684\u77f3\u677f\u4e0a\u5750\u4e86\u4e00\u591c\u3002 \u3000\u3000\u201c\u5230\u7b2c\u4e8c\u5929\u65e9\u6668\uff0c\u72c4\u5144\u5f1f\uff0c\u6211\u597d\u798f\u6c14\uff0c\u4e24\u76c6\u6de1\u7eff\u7684\u83ca\u82b1\u5f53\u771f\u51fa\u73b0\u5728\u90a3\u7a97\u69db\u4e4b\u4e0a\u3002\u6211\u77e5\u9053\u4e00\u76c6\u53eb\u4f5c\u2018\u6625\u6c34\u78a7\u6ce2\u2019\uff0c\u4e00\u76c6\u53eb\u4f5c\u2018\u78a7\u7389\u5982\u610f\u2019\uff0c\u53ef\u6211\u5fc3\u4e2d\u60f3\u7740\u7684\uff0c\u53ea\u662f\u653e\u8fd9\u4e24\u76c6\u82b1\u7684\u4eba\u3002\u5c31\u5728\u90a3\u65f6\u5019\uff0c\u5728\u90a3\u5e18\u5b50\u540e\u9762\uff0c\u90a3\u5f20\u5929\u4e0b\u6700\u7f8e\u4e3d\u7684\u8138\u5e9e\u6084\u6084\u7684\u9732\u51fa\u534a\u9762\uff0c\u5411\u6211\u51dd\u671b\u4e86\u4e00\u773c\uff0c\u5ffd\u7136\u95f4\u6ee1\u8138\u7ea2\u6655\uff0c\u9690\u5230\u4e86\u5e18\u5b50\u4e4b\u540e\uff0c\u4ece\u6b64\u4e0d\u518d\u51fa\u73b0\u3002 \u3000\u3000\u201c\u72c4\u5144\u5f1f\uff0c\u4f60\u5927\u54e5\u76f8\u8c8c\u4e11\u964b\uff0c\u975e\u5bcc\u975e\u8d35\uff0c\u53ea\u662f\u4e2a\u6d41\u843d\u6c5f\u6e56\u7684\u8349\u83bd\u4e4b\u5f92\uff0c\u5982\u4f55\u6562\u76fc\u671b\u5f97\u4f73\u4eba\u5782\u9752\uff1f\u53ea\u662f\u4ece\u6b64\u4e4b\u540e\uff0c\u6bcf\u5929\u65e9\u6668\uff0c\u6211\u603b\u662f\u5230\u51cc\u5e9c\u7684\u540e\u56ed\u4e4b\u5916\uff0c\u5411\u5c0f\u59d0\u7684\u7a97\u69db\u77a7\u4e0a\u534a\u5929\u3002\u51cc\u5c0f\u59d0\u5012\u4e5f\u8bb0\u7740\u6211\uff0c\u6bcf\u5929\u603b\u662f\u6362\u4e00\u76c6\u9c9c\u82b1\uff0c\u653e\u5728\u7a97\u69db\u4e4b\u4e0a\u3002 \u3000\u3000\u201c\u8fd9\u6837\u5b50\u7684\u516d\u4e2a\u591a\u6708\uff0c\u4e0d\u8bba\u5927\u98ce\u5927\u96e8\uff0c\u5927\u971c\u5927\u96ea\uff0c\u6211\u5929\u5929\u65e9\u6668\u53bb\u8d4f\u82b1\u3002\u51cc\u5c0f\u59d0\u4e5f\u603b\u98ce\u96e8\u4e0d\u6539\u7684\u7ed9\u6211\u6362\u4e00\u76c6\u9c9c\u82b1\u3002\u5979\u6bcf\u5929\u53ea\u770b\u6211\u4e00\u773c\uff0c\u51b3\u4e0d\u770b\u7b2c\u4e8c\u6b21\uff0c\u6bcf\u770b\u4e86\u8fd9\u4e00\u773c\uff0c\u603b\u662f\u6ee1\u8138\u7ea2\u6655\u7684\u9690\u5230\u4e86\u5e18\u5b50\u4e4b\u540e\u3002\u6211\u53ea\u8981\u6bcf\u5929\u8fd9\u6837\u89c1\u5230\u4e00\u6b21\u5979\u7684\u773c\u6ce2\u3001\u5979\u8138\u4e0a\u7684\u7ea2\u6655\uff0c\u90a3\u5c31\u5fc3\u6ee1\u610f\u8db3\u3002\u5979\u4ece\u6765\u6ca1\u8ddf\u6211\u8bf4\u8bdd\u3002\u6211\u4e5f\u4ece\u4e0d\u6562\u5f00\u53e3\u8bf4\u4e00\u53e5\u3002\u4ee5\u6211\u7684\u6b66\u529f\uff0c\u8f7b\u8f7b\u4e00\u7eb5\uff0c\u4fbf\u53ef\u8dc3\u4e0a\u697c\u53bb\uff0c\u5230\u4e86\u5979\u8eab\u524d\u3002\u4f46\u6211\u4ece\u6765\u4e0d\u6562\u5bf9\u5979\u6709\u534a\u5206\u8f7b\u6162\u3002\u81f3\u4e8e\u5199\u4e00\u5c01\u4fe1\u6765\u8868\u8fbe\u656c\u6155\u4e4b\u5ff1\uff0c\u90a3\u66f4\u662f\u4e0d\u6562\u4e86\u3002 \u3000\u3000\u201c\u90a3\u4e00\u5e74\u4e09\u6708\u521d\u4e94\u7684\u591c\u91cc\uff0c\u6709\u4e24\u4e2a\u548c\u5c1a\u5230\u6211\u5bd3\u6240\u6765\uff0c\u5ffd\u7136\u5411\u6211\u88ad\u51fb\u3002\u4ed6\u4eec\u5f97\u77e5\u4e86\u6d88\u606f\uff0c\u60f3\u62a2\u795e\u7167\u7ecf\u548c\u5251\u8bc0\u3002\u8fd9\u4e24\u4e2a\u548c\u5c1a\uff0c\u4fbf\u662f\u2018\u8840\u5200\u95e8\u2019\u4e94\u50e7\u4e2d\u7684\u4e8c\u50e7\uff0c\u5176\u4e2d\u4e00\u4e2a\u6211\u5df2\u5728\u7262\u72f1\u4e2d\u6599\u7406\u4e86\uff0c\u90a3\u65e5\u4f60\u4eb2\u773c\u77a7\u89c1\u7684\u3002\u53ef\u662f\u90a3\u65f6\u6211\u8fd8\u6ca1\u7ec3\u6210\u795e\u7167\u529f\uff0c\u6b66\u529f\u53ca\u4e0d\u4e0a\u4ed6\u4eec\uff0c\u7ed9\u8fd9\u4e24\u4e2a\u6076\u50e7\u6253\u5f97\u91cd\u4f24\uff0c\u9669\u4e9b\u6027\u547d\u4e0d\u4fdd\uff0c\u6211\u8eb2\u5230\u9a6c\u53a9\u7684\u8349\u6599\u5806\u4e2d\uff0c\u8fd9\u624d\u8131\u96be\u3002 \u3000\u3000\u201c\u8fd9\u4e00\u573a\u4f24\u7740\u5b9e\u4e0d\u8f7b\u3002\u8db3\u8db3\u8eba\u4e86\u4e09\u4e2a\u591a\u6708\uff0c\u624d\u52c9\u5f3a\u80fd\u591f\u8d77\u8eab\u3002\u6211\u4e00\u8d77\u5e8a\uff0c\u6491\u4e86\u62d0\u6756\uff0c\u6323\u624e\u7740\u4fbf\u5230\u51cc\u5e9c\u7684\u540e\u56ed\u95e8\u5916\uff0c\u53ea\u89c1\u666f\u7269\u5168\u975e\uff0c\u4e00\u6253\u542c\uff0c\u539f\u6765\u51cc\u7ff0\u6797\u5df2\u5728\u4e09\u4e2a\u6708\u524d\u642c\u4e86\u5bb6\u3002\u642c\u5230\u751a\u4e48\u5730\u65b9\uff0c\u7adf\u662f\u8c01\u4e5f\u4e0d\u77e5\u3002 \u3000\u3000\u201c\u72c4\u5144\u5f1f\uff0c\u4f60\u60f3\u60f3\uff0c\u6211\u8fd9\u756a\u5931\u671b\uff0c\u53ef\u6bd4\u8eab\u4e0a\u8fd9\u4e9b\u4f24\u52bf\u5389\u5bb3\u5f97\u591a\u3002\u6211\u5fc3\u4e2d\u5947\u602a\uff0c\u51cc\u7ff0\u6797\u662f\u6b66\u660c\u5927\u540d\u9f0e\u9f0e\u7684\u4eba\u7269\uff0c\u642c\u5230\u4e86\u751a\u4e48\u5730\u65b9\uff0c\u51b3\u4e0d\u81f3\u4e8e\u8c01\u4e5f\u4e0d\u77e5\u3002\u53ef\u662f\u6211\u4e1c\u67e5\u897f\u95ee\uff0c\u82b1\u4e86\u4e0d\u5c11\u8d22\u7269\u6c14\u529b\uff0c\u4ecd\u662f\u6ca1\u534a\u70b9\u5934\u7eea\u3002\u8fd9\u4e2d\u95f4\u5b9e\u5728\u5927\u6709\u8e4a\u8df7\u3002\u663e\u7136\uff0c\u51cc\u7ff0\u6797\u6216\u8bb8\u4e3a\u4e86\u8eb2\u907f\u4ec7\u5bb6\uff0c\u6216\u8bb8\u53e6\u6709\u7279\u522b\u539f\u56e0\uff0c\u8fd9\u624d\u7a81\u7136\u95f4\u4e3e\u5bb6\u8fc1\u5f99\uff0c\u4e0d\u77e5\u53bb\u5411\uff0c\u51d1\u5de7\u7684\u662f\uff0c\u6211\u53d7\u4f24\u4e0d\u4e45\uff0c\u5979\u5bb6\u91cc\u5c31\u642c\u4e86\u3002 \u3000\u3000\u201c\u4ece\u6b64\u6211\u4e0d\u8bba\u505a\u751a\u4e48\u4e8b\u90fd\u662f\u5168\u65e0\u5fc3\u601d\uff0c\u5728\u6c5f\u6e56\u4e0a\u4e1c\u6e38\u897f\u8361\u3002\u4e5f\u662f\u6211\u4e01\u5178\u6d2a\u798f\u9f50\u5929\uff0c\u8fd9\u65e5\u5728\u957f\u6c99\u8336\u9986\u4e4b\u4e2d\uff0c\u65e0\u610f\u542c\u5230\u4e24\u4e2a\u5e2e\u4f1a\u4e2d\u4eba\u8c08\u8bba\uff0c\u5546\u91cf\u7740\u8981\u5230\u8346\u5dde\u53bb\u627e\u4e07\u9707\u5c71\uff0c\u8bf4\u8981\u4ed6\u4ea4\u51fa\u90a3\u90e8\u2018\u8fde\u57ce\u5251\u8c31\u2019\u6765\u3002\u6211\u60f3\u90a3\u65e5\u4e07\u9707\u5c71\u5e08\u5144\u5f1f\u4e09\u4eba\u5927\u9006\u5f11\u5e08\uff0c\u4e3a\u7684\u5c31\u662f\u8fd9\u672c\u5251\u8c31\uff0c\u5230\u5e95\u90a3\u5251\u8c31\u662f\u526f\u751a\u4e48\u6837\u5b50\uff0c\u5012\u4e0d\u59a8\u77a7\u77a7\u3002\u4e8e\u662f\u6211\u6084\u6084\u8ddf\u7740\u4e8c\u4eba\uff0c\u5230\u4e86\u6c5f\u9675\u3002\u8fd9\u4e24\u4e2a\u5e2e\u4f1a\u4e2d\u4eba\u59d4\u5b9e\u662f\u4e0d\u81ea\u91cf\u529b\uff0c\u4e00\u5230\u4e07\u5bb6\u53bb\u751f\u4e8b\uff0c\u5c31\u7ed9\u4e07\u9707\u5c71\u62ff\u4f4f\u4e86\uff0c\u9001\u5230\u8346\u5dde\u5e9c\u8859\u95e8\u53bb\u3002\u6211\u8ddf\u7740\u53bb\u77a7\u70ed\u95f9\uff0c\u4e00\u89c1\u5230\u5e9c\u8859\u524d\u8d34\u7684\u5927\u544a\u793a\uff0c\u53ef\u771f\u559c\u4ece\u5929\u964d\u3002\u539f\u6765\u90a3\u77e5\u5e9c\u4e0d\u662f\u65c1\u4eba\uff0c\u6b63\u662f\u51cc\u5c0f\u59d0\u7684\u7236\u4eb2\u51cc\u9000\u601d\u3002 \u3000\u3000\u201c\u8fd9\u5929\u665a\u4e0a\uff0c\u6211\u6084\u6084\u6367\u4e86\u4e00\u76c6\u8537\u8587\uff0c\u653e\u5728\u51cc\u5c0f\u59d0\u540e\u697c\u7684\u7a97\u69db\u4e0a\uff0c\u7136\u540e\u5728\u697c\u4e0b\u7b49\u7740\u3002\u7b2c\u4e8c\u5929\u65e9\u6668\uff0c\u5c0f\u59d0\u6253\u5f00\u7a97\u5b50\uff0c\u89c1\u5230\u4e86\u90a3\u76c6\u82b1\uff0c\u60ca\u547c\u4e86\u4e00\u58f0\uff0c\u968f\u5373\u53c8\u89c1\u5230\u4e86\u6211\u3002\u6211\u4eec\u4e00\u5e74\u591a\u4e0d\u89c1\uff0c\u90fd\u4ee5\u4e3a\u4eca\u751f\u518d\u65e0\u76f8\u89c1\u4e4b\u65e5\uff0c\u6b64\u756a\u4e45\u522b\u91cd\u9022\uff0c\u771f\u662f\u8bf4\u4e0d\u51fa\u7684\u6b22\u559c\u3002\u5979\u5411\u6211\u77a7\u4e86\u597d\u4e00\u4f1a\u513f\uff0c\u624d\u7ea2\u7740\u8138\uff0c\u8f7b\u8f7b\u63a9\u4e0a\u4e86\u7a97\u5b50\u3002\u7b2c\u4e09\u5929\uff0c\u5979\u7ec8\u4e8e\u8bf4\u8bdd\u4e86\uff0c\u95ee\uff1a\u2018\u4f60\u751f\u75c5\u4e86\u4e48\uff1f\u53ef\u7626\u5f97\u591a\u4e86\u3002\u2019\u201c\u4ee5\u540e\u7684\u65e5\u5b50\uff0c\u6211\u4e0d\u662f\u505a\u4eba\uff0c\u662f\u5728\u5929\u4e0a\u505a\u795e\u4ed9\uff0c\u5176\u5b9e\u5c31\u505a\u795e\u4ed9\uff0c\u4e00\u5b9a\u4e5f\u6ca1\u6211\u8fd9\u822c\u5feb\u6d3b\u3002\u6bcf\u5929\u534a\u591c\u91cc\uff0c\u6211\u5230\u697c\u4e0a\u53bb\u63a5\u51cc\u5c0f\u59d0\u51fa\u6765\uff0c\u5728\u6c5f\u9675\u5404\u5904\u8352\u5c71\u65f7\u91ce\u6f2b\u6e38\u3002\u6211\u4eec\u4ece\u6ca1\u534a\u5206\u4e0d\u89c4\u77e9\u7684\u884c\u4e3a\uff0c\u7136\u800c\u662f\u65e0\u8bdd\u4e0d\u8bf4\uff0c\u6bd4\u5929\u4e0b\u6700\u8981\u597d\u670b\u53cb\u8fd8\u66f4\u77e5\u5df1\u3002 \u3000\u3000\u201c\u4e00\u5929\u665a\u4e0a\uff0c\u51cc\u5c0f\u59d0\u5411\u6211\u5410\u9732\u4e86\u4e00\u4e2a\u5927\u79d8\u5bc6\u3002\u539f\u6765\u5979\u7239\u7239\u867d\u7136\u8003\u4e2d\u8fdb\u58eb\uff0c\u505a\u8fc7\u7ff0\u6797\uff0c\u5176\u5b9e\u662f\u4e24\u6e56\u9f99\u6c99\u5e2e\u4e2d\u7684\u5927\u9f99\u5934\u3002\u4e0d\u4f46\u6587\u624d\u51fa\u4f17\uff0c\u6b66\u529f\u4e5f\u5341\u5206\u4e86\u5f97\u3002\u6211\u5bf9\u51cc\u5c0f\u59d0\u65e2\u656c\u82e5\u5929\u795e\uff0c\u5bf9\u5979\u7236\u4eb2\u81ea\u7136\u4e5f\u751a\u4e3a\u5c0a\u656c\uff0c\u542c\u4e86\u4e5f\u4e0d\u4ee5\u4e3a\u610f\u3002 \u3000\u3000\u201c\u53c8\u6709\u4e00\u5929\u665a\u4e0a\uff0c\u51cc\u5c0f\u59d0\u5bf9\u6211\u8bf4\uff0c\u5979\u7236\u4eb2\u6240\u4ee5\u4e0d\u505a\u6e05\u8d35\u7684\u7ff0\u6797\uff0c\u53c8\u4f7f\u4e86\u6570\u4e07\u4e24\u94f6\u5b50\uff0c\u5343\u65b9\u767e\u8ba1\u7684\u8c0b\u5e72\u6765\u505a\u8346\u5dde\u77e5\u5e9c\uff0c\u4e43\u662f\u6709\u4e00\u4e2a\u91cd\u5927\u56fe\u8c0b\u3002\u539f\u6765\u4ed6\u4ece\u53f2\u4e66\u4e4b\u4e2d\uff0c\u63a2\u7d22\u5230\u8346\u5dde\u57ce\u4e2d\u67d0\u5730\uff0c\u4e00\u5b9a\u57cb\u85cf\u6709\u4e00\u6279\u6570\u91cf\u5de8\u5927\u65e0\u6bd4\u7684\u8d22\u5b9d\u3002 \u3000\u3000\u201c\u51cc\u5c0f\u59d0\u8bf4\uff0c\u516d\u671d\u65f6\u6881\u671d\u7684\u6881\u6b66\u5e1d\u7ecf\u4faf\u666f\u4e4b\u4e71\u800c\u6b7b\uff0c\u7b80\u6587\u5e1d\u63a5\u4f4d\uff0c\u53c8\u88ab\u4faf\u666f\u5bb3\u6b7b\uff0c\u6e58\u4e1c\u738b\u8427\u7ece\u63a5\u4f4d\u4e8e\u6c5f\u9675\uff0c\u662f\u4e3a\u6881\u5143\u5e1d\u3002\u6881\u5143\u5e1d\u61e6\u5f31\u65e0\u80fd\uff0c\u6027\u559c\u79ef\u805a\u8d22\u5b9d\uff0c\u5728\u6c5f\u9675\u505a\u4e86\u4e09\u5e74\u7687\u5e1d\uff0c\u641c\u522e\u7684\u91d1\u73e0\u73cd\u5b9d\uff0c\u4e0d\u8ba1\u5176\u6570\u3002\u627f\u5723\u4e09\u5e74\uff0c\u9b4f\u5175\u653b\u7834\u6c5f\u9675\uff0c\u6740\u4e86\u5143\u5e1d\u3002\u4f46\u4ed6\u805a\u655b\u7684\u8d22\u5b9d\u85cf\u5728\u4f55\u5904\uff0c\u5374\u65e0\u4eba\u5f97\u77e5\u3002\u9b4f\u5175\u5143\u5e05\u65bc\u8c28\u4e3a\u4e86\u67e5\u95ee\u8fd9\u6279\u73cd\u5b9d\uff0c\u62f7\u6253\u6740\u63a0\u4e86\u6570\u5343\u4eba\uff0c\u59cb\u7ec8\u8ffd\u67e5\u4e0d\u5230\u3002 \u3000\u3000\u4ed6\u6015\u77e5\u9053\u73cd\u5b9d\u6240\u5728\u7684\u4eba\u65e5\u540e\u5077\u5077\u53d1\u6398\uff0c\u5c06\u6c5f\u9675\u767e\u59d3\u6570\u4e07\u53e3\u5c3d\u6570\u9a71\u5f52\u957f\u5b89\u3002\u6740\u7684\u6740\uff0c\u5751\u7684\u5751\uff0c\u51e0\u4e4e\u6ca1\u751a\u4e48\u6d3b\u53e3\u5e78\u5b58\u3002\u51e0\u767e\u5e74\u6765\uff0c\u8fd9\u79d8\u5bc6\u59cb\u7ec8\u6ca1\u63ed\u7834\u3002\u65f6\u5019\u957f\u4e86\uff0c\u66f4\u52a0\u8c01\u4e5f\u4e0d\u77e5\u9053\u4e86\u3002 \u3000\u3000\u201c\u51cc\u5c0f\u59d0\u8bf4\uff0c\u5979\u7239\u7239\u82b1\u4e86\u591a\u5e74\u529f\u592b\uff0c\u7ffb\u67e5\u8346\u5dde\u5e9c\u5fd7\uff0c\u4ee5\u53ca\u5404\u79cd\u5404\u6837\u7684\u53e4\u4e66\u65e7\u5f55\uff0c\u65ad\u5b9a\u6881\u5143\u5e1d\u8fd9\u6279\u8d22\u5b9d\uff0c\u5b9a\u662f\u57cb\u85cf\u5728\u6c5f\u9675\u57ce\u5916\u67d0\u5730\u3002\u6881\u5143\u5e1d\u6027\u5b50\u6b8b\u5fcd\uff0c\u60f3\u5fc5\u662f\u57cb\u4e86\u5b9d\u7269\u4e4b\u540e\uff0c\u5c06\u5f97\u77e5\u79d8\u5bc6\u7684\u4eba\u5c3d\u6570\u6740\u4e86\uff0c\u56e0\u6b64\u9b4f\u5175\u5143\u5e05\u4e0d\u8bba\u5982\u4f55\u7684\u62f7\u63a0\u767e\u59d3\uff0c\u7ec8\u7a76\u5f97\u4e0d\u5230\u4e1d\u6beb\u7ebf\u7d22\u3002\u201d \u3000\u3000\u72c4\u4e91\u542c\u5230\u8fd9\u91cc\uff0c\u5fc3\u5934\u5b58\u7740\u7684\u8bb8\u591a\u7591\u7aa6\u6162\u6162\u4e00\u4e2a\u4e2a\u89e3\u660e\u4e86\uff0c\u8bf4\u9053\uff1a\u201c\u4e01\u5927\u54e5\uff0c\u4f60\u77e5\u9053\u8fd9\u5b9d\u85cf\u7684\u79d8\u5bc6\uff0c\u662f\u4e0d\u662f\uff1f\u8fd9\u8bb8\u591a\u4eba\u5230\u7262\u72f1\u4e2d\u6765\u627e\u4f60\uff0c\u4e5f\u5fc5\u662f\u4e3a\u4e86\u60f3\u5f97\u8fd9\u4e2a\u5927\u5b9d\u85cf\u3002\u201d \u3000\u3000\u4e01\u5178\u8138\u9732\u82e6\u7b11\uff0c\u7ee7\u7eed\u8bf4\u4e0b\u53bb\uff1a \u3000\u3000\u201c\u51cc\u5c0f\u59d0\u8ddf\u6211\u8bf4\u4e86\u8fd9\u4e9b\u8bdd\uff0c\u6211\u53ea\u89c9\u5979\u7239\u7239\u53d1\u8d22\u4e4b\u5fc3\u5fd2\u4e5f\u5389\u5bb3\uff0c\u4ed6\u5df2\u8fd9\u822c\u6587\u6b66\u5168\u624d\uff0c\u53c8\u5bcc\u53c8\u8d35\uff0c\u4f55\u5fc5\u518d\u53bb\u60f3\u751a\u4e48\u5b9d\u85cf\uff1f\u540e\u6765\u6211\u8ddf\u5979\u8c08\u8bba\u6c5f\u6e56\u95f4\u7684\u8bf8\u822c\u89c1\u95fb\uff0c\u90a3\u665a\u5728\u6c5f\u8fb9\u89c1\u5230\u4e07\u9707\u5c71\u4e09\u4eba\u5f11\u5e08\u593a\u8c31\u7684\u4e8b\uff0c\u81ea\u7136\u4e5f\u4e0d\u7792\u5979\u3002\u6211\u8ddf\u5979\u8bf4\u5230\u795e\u7167\u7ecf\u3001\u7b49\u7b49\u3002 \u3000\u3000\u201c\u6211\u4eec\u8fd9\u822c\u8fc7\u4e86\u5927\u534a\u5e74\u5feb\u6d3b\u65e5\u5b50\uff0c\u90a3\u4e00\u65e5\u662f\u4e03\u6708\u5341\u56db\uff0c\u51cc\u5c0f\u59d0\u5bf9\u6211\u8bf4\uff1a\u2018\u5178\u54e5\uff0c\u54b1\u4eec\u7684\u4e8b\uff0c\u603b\u5f97\u7ed9\u7239\u7239\u8bf4\u4e86\uff0c\u8bf7\u4ed6\u8001\u4eba\u5bb6\u4f5c\u4e3b\uff0c\u90a3\u5c31\u4e0d\u7528\u8fd9\u822c\u5077\u5077\u6478\u6478\u2026\u2026\u2019\u5979\u8fd9\u53e5\u8bdd\u6ca1\u8bf4\u5b8c\uff0c\u7f9e\u5f97\u5c06\u8138\u85cf\u5728\u6211\u7684\u6000\u91cc\u3002\u6211\u8bf4\uff1a\u2018\u4f60\u662f\u5343\u91d1\u5c0f\u59d0\uff0c\u6211\u5c31\u6015\u4f60\u7239\u7239\u77a7\u6211\u4e0d\u8d77\u3002\u2019\u5979\u8bf4\uff1a\u2018\u6211\u7956\u4e0a\u5176\u5b9e\u4e5f\u662f\u6b66\u6797\u4e2d\u4eba\uff0c\u53ea\u4e0d\u8fc7\u6211\u7239\u7239\u53bb\u505a\u4e86\u5b98\uff0c\u6211\u53c8\u4e0d\u4f1a\u534a\u70b9\u6b66\u827a\u3002\u6211\u7239\u7239\u662f\u6700\u75bc\u6211\u7684\uff0c\u81ea\u4ece\u6211\u5988\u6b7b\u540e\uff0c\u6211\u8bf4\u751a\u4e48\u4ed6\u90fd\u7b54\u5141\u3002\u2019\u201c\u6211\u542c\u5979\u8fd9\u4e48\u8bf4\uff0c\u81ea\u7136\u9ad8\u5174\u5f97\u8981\u547d\u3002\u4e03\u6708\u5341\u4e94\u8fd9\u4e00\u5929\uff0c\u5728\u767d\u5929\u8be5\u7761\u89c9\u7684\u65f6\u5019\uff0c\u4e5f\u95ed\u4e0d\u4e86\u773c\u775b\uff0c\u5230\u5f97\u534a\u591c\uff0c\u6211\u53c8\u5230\u51cc\u5c0f\u59d0\u697c\u4e0a\u53bb\u4f1a\u5979\uff0c\u5979\u6ee1\u8138\u901a\u7ea2\u7684\u8bf4\uff1a\u2018\u7239\u7239\u8bf4\uff0c\u4e00\u5207\u542c\u5973\u513f\u7684\u8bdd\u3002\u2019\u6211\u4e50\u5f97\u53d8\u6210\u4e86\u4e2a\u5927\u50bb\u74dc\uff0c\u4e24\u4e2a\u513f\u4f60\u77a7\u77a7\u6211\uff0c\u6211\u77a7\u77a7\u4f60\uff0c\u53ea\u662f\u563b\u563b\u7684\u76f4\u7b11\u3002 \u3000\u3000\u201c\u6211\u4fe9\u624b\u633d\u624b\u8d70\u4e0b\u697c\u6765\uff0c\u5ffd\u7136\u5728\u6708\u5149\u4e4b\u4e0b\uff0c\u770b\u89c1\u82b1\u5703\u4e2d\u591a\u4e86\u51e0\u76c6\u989c\u8272\u7279\u522b\u5a07\u8273\u7684\u9ec4\u82b1\u3002\u8fd9\u4e9b\u82b1\u7684\u82b1\u74e3\u9ec4\u5f97\u50cf\u91d1\u5b50\u4e00\u6837\uff0c\u95ea\u95ea\u53d1\u4eae\uff0c\u82b1\u6735\u7684\u6837\u5b50\u5f88\u50cf\u8377\u82b1\uff0c\u53ea\u662f\u6ca1\u8377\u82b1\u90a3\u4e48\u5927\u3002\u6211\u4e8c\u4eba\u90fd\u662f\u6700\u7231\u82b1\u7684\uff0c\u7acb\u65f6\u4fbf\u8fc7\u53bb\u89c2\u8d4f\u3002\u51cc\u5c0f\u59d0\u5567\u5567\u79f0\u5947\uff0c\u8bf4\u4ece\u6765\u6ca1\u89c1\u8fc7\u8fd9\u79cd\u9ec4\u82b1\uff0c\u6211\u4eec\u4e00\u9f50\u51d1\u8fd1\u53bb\u95fb\u95fb\uff0c\u8981\u77e5\u9053\u8fd9\u82b1\u7684\u9999\u6c14\u5982\u4f55\u2026\u2026\u201d \u3000\u3000\u72c4\u4e91\u542c\u4ed6\u53d9\u8ff0\u5f80\u4e8b\uff0c\u6708\u5149\u4e4b\u4e0b\uff0c\u4e0e\u5fc3\u4e0a\u4eba\u643a\u624b\u540c\u6e38\uff0c\u89c2\u8d4f\u5947\u82b1\uff0c\u5f53\u771f\u662f\u5929\u4e0a\u795e\u4ed9\u4e5f\u6bd4\u4e0d\u4e0a\u4e86\u3002\u53ef\u662f\u4e01\u5178\u8ff0\u8bf4\u7684\u8bed\u8c03\u4e4b\u4e2d\uff0c\u5374\u542b\u6709\u4e00\u80a1\u9634\u68ee\u68ee\u7684\u53ef\u6016\u7684\u6c14\u606f\uff0c\u72c4\u4e91\u542c\u5f97\u51e0\u4e4e\u6c14\u4e5f\u5598\u4e0d\u8fc7\u6765\uff0c\u4f3c\u4e4e\u8fd9\u5e9f\u56ed\u4e4b\u4e2d\uff0c\u6709\u8bb8\u591a\u6076\u9b3c\u8981\u6251\u4e0a\u8eab\u6765\u4e00\u822c\u3002\u7a81\u7136\u4e4b\u95f4\u4ed6\u60f3\u5230\u4e86\u4e00\u4e2a\u540d\u5b57\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u91d1\u6ce2\u65ec\u82b1\uff01\u201d \u3000\u3000\u4e01\u5178\u5634\u89d2\u8fb9\u9732\u51fa\u4e00\u4e1d\u82e6\u7b11\uff0c\u9694\u4e86\u597d\u4e00\u4f1a\uff0c\u624d\u9053\uff1a\u201c\u5144\u5f1f\uff0c\u4f60\u4e0d\u7b28\u4e86\u3002\u4ee5\u540e\u4f60\u4e00\u4eba\u884c\u8d70\u6c5f\u6e56\uff0c\u4e5f\u4e0d\u4f1a\u5403\u4e8f\uff0c\u6211\u8fd9\u53ef\u653e\u5fc3\u4e86\u3002\u201d \u3000\u3000\u72c4\u4e91\u542c\u4ed6\u8fd9\u51e0\u53e5\u8bdd\u4e2d\u5145\u6ee1\u4e86\u5173\u5207\u548c\u53cb\u7231\uff0c\u5fcd\u4e0d\u4f4f\u70ed\u6cea\u76c8\u7736\uff0c\u6068\u6068\u7684\u9053\uff1a\u201c\u51cc\u77e5\u5e9c\u8fd9\u72d7\u5b98\uff0c\u4ed6\uff0c\u4ed6\uff0c\u4ed6\u4e0d\u80af\u5c06\u5973\u513f\u8bb8\u914d\u7ed9\u4f60\uff0c\u90a3\u4e5f\u7f62\u4e86\uff0c\u4f55\u5fc5\u4f7f\u8fd9\u6bd2\u8ba1\u5bb3\u4f60\uff1f\u201d \u3000\u3000\u4e01\u5178\u9053\uff1a\u201c\u5f53\u65f6\u6211\u600e\u4e48\u731c\u60f3\u5f97\u5230\uff1f\u66f4\u54ea\u77e5\u9053\u8fd9\u91d1\u8272\u7684\u82b1\u6735\uff0c\u4fbf\u662f\u5947\u6bd2\u65e0\u6bd4\u7684\u91d1\u6ce2\u65ec\u82b1\uff1f\u2018\u6ce2\u65ec\u2019\u4e24\u5b57\u662f\u68b5\u8bed\uff0c\u662f\u2018\u6076\u9b54\u2019\u7684\u610f\u601d\u3002\u8fd9\u6bd2\u82b1\u662f\u4ece\u5929\u7afa\u4f20\u6765\u7684\uff0c\u539f\u6765\u5929\u7afa\u4eba\u53eb\u5b83\u4e3a\u2018\u6076\u9b54\u82b1\u2019\uff0c\u6211\u4e00\u95fb\u5230\u82b1\u9999\uff0c\u4fbf\u662f\u4e00\u9635\u6655\u7729\uff0c\u53ea\u89c1\u51cc\u5c0f\u59d0\u8eab\u5b50\u6643\u4e86\u51e0\u6643\uff0c\u4fbf\u5373\u6454\u5012\u3002\u6211\u5fd9\u4f38\u624b\u53bb\u6276\uff0c\u81ea\u5df1\u5374\u4e5f\u7ad9\u7acb\u4e0d\u5b9a\u3002\u6211\u6b63\u8fd0\u5185\u529f\u8c03\u606f\uff0c\u4e0e\u6bd2\u6027\u76f8\u6297\uff0c\u7a81\u7136\u95f4\u6697\u5904\u62a2\u51fa\u51e0\u4e2a\u624b\u6267\u5175\u5203\u7684\u6c49\u5b50\u6765\u3002\u6211\u53ea\u548c\u4ed6\u4eec\u6597\u5f97\u51e0\u62db\uff0c\u773c\u524d\u5df2\u662f\u6f06\u9ed1\u4e00\u56e2\uff0c\u63a5\u7740\u4fbf\u751a\u4e48\u4e5f\u4e0d\u77e5\u9053\u4e86\u3002 \u3000\u3000\u201c\u5f85\u5f97\u9192\u8f6c\uff0c\u6211\u624b\u8db3\u90fd\u5df2\u4e0a\u4e86\u94d0\u9563\uff0c\u8fde\u7435\u7436\u9aa8\u4e5f\u88ab\u94c1\u94fe\u7a7f\u8fc7\u3002\u51cc\u77e5\u5e9c\u7a7f\u4e86\u4fbf\u670d\uff0c\u5728\u82b1\u5385\u4e2d\u5ba1\u8baf\uff0c\u65c1\u8fb9\u4f3a\u5019\u7684\u4e5f\u4e0d\u662f\u8859\u95e8\u4e2d\u7684\u5dee\u5f79\u3002\u800c\u662f\u4ed6\u5e2e\u4f1a\u4e2d\u7684\u5144\u5f1f\u3002\u6211\u81ea\u7136\u5341\u5206\u5014\u5f3a\uff0c\u7834\u53e3\u5927\u9a82\u3002\u51cc\u77e5\u5e9c\u5148\u547d\u4eba\u72e0\u72e0\u62f7\u6253\u6211\u4e00\u987f\uff0c\u8fd9\u624d\u903c\u6211\u4ea4\u51fa\u795e\u7167\u7ecf\u548c\u5251\u8bc0\u3002 \u3000\u3000\u201c\u4ee5\u540e\u7684\u4e8b\uff0c\u4f60\u90fd\u77e5\u9053\u4e86\u3002\u6bcf\u4e2a\u6708\u5341\u4e94\uff0c\u51cc\u77e5\u5e9c\u4fbf\u63d0\u6211\u53bb\u62f7\u6253\u4e00\u987f\uff0c\u52d2\u903c\u6211\u4ea4\u51fa\u6b66\u7ecf\u5251\u8bc0\uff0c\u6211\u59cb\u7ec8\u7ed9\u4ed6\u4e2a\u4e0d\u7406\u4e0d\u776c\u3002\u4ed6\u7684\u8010\u6027\u4e5f\u771f\u597d\uff0c\u54b1\u4eec\u4fbf\u8fd9\u4e48\u8017\u4e0a\u4e86\u3002\u201d \u3000\u3000\u72c4\u4e91\u9053\uff1a\u201c\u51cc\u5c0f\u59d0\u5462\uff1f\u5979\u4e3a\u751a\u4e48\u4e0d\u60f3\u6cd5\u5b50\u6551\u4f60\uff1f\u4f60\u540e\u6765\u7ec3\u6210\u4e86\u795e\u7167\u529f\uff0c\u6765\u53bb\u81ea\u5982\uff0c\u4e3a\u751a\u4e48\u4e0d\u53bb\u77a7\u77a7\u5979\uff1f\u4e3a\u751a\u4e48\u5728\u72f1\u4e2d\u7a7a\u7b49\uff0c\u4e00\u76f4\u7b49\u5230\u5979\u6b7b\uff1f\u201d \u3000\u3000\u4e01\u5178\u5934\u8111\u4e2d\u4e00\u9635\u5267\u70c8\u7684\u6655\u7729\uff0c\u5168\u8eab\u4fbf\u4f3c\u5728\u7a7a\u4e2d\u98d8\u6d6e\u98de\u821e\u4e00\u822c\u3002\u4ed6\u4f38\u51fa\u624b\u6765\u4e71\u6293\u4e71\u6478\uff0c\u4f3c\u60f3\u5f97\u5230\u751a\u4e48\u4f9d\u9760\u3002\u72c4\u4e91\u4f38\u624b\u8fc7\u53bb\u63e1\u4f4f\u4e86\u4ed6\u624b\u3002\u4e01\u5178\u7a81\u7136\u4e00\u60ca\uff0c\u4f7f\u529b\u6323\u8131\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u624b\u4e0a\u6709\u6bd2\uff0c\u4f60\u522b\u78b0\u3002\u201d\u72c4\u4e91\u5fc3\u4e2d\u53c8\u662f\u4e00\u9635\u96be\u8fc7\u3002 \u3000\u3000\u4e01\u5178\u6655\u4e86\u4e00\u4f1a\uff0c\u6e10\u6e10\u5b9a\u4e0b\u795e\u6765\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u521a\u624d\u8bf4\u751a\u4e48\uff1f\u201d \u3000\u3000\u72c4\u4e91\u5ffd\u7136\u60f3\u8d77\u4e00\u4e8b\uff0c\u8bf4\u9053\uff1a\u201c\u4e01\u5927\u54e5\uff0c\u4f60\u6709\u6ca1\u6709\u60f3\u8fc7\uff0c\u51cc\u5c0f\u59d0\u662f\u53d7\u5979\u7236\u4eb2\u5631\u5490\uff0c\u6545\u610f\u9a97\u4f60\uff0c\u60f3\u8981\u2026\u2026\u201d\u4e01\u5178\u4e00\u58f0\u5927\u53eb\uff0c\u559d\u9053\uff1a\u201c\u653e\u5c41\uff01\u201d\u6325\u62f3\u4fbf\u51fb\u4e86\u4e0b\u6765\u3002\u72c4\u4e91\u81ea\u77e5\u5931\u8a00\uff0c\u4e0d\u613f\u4f38\u624b\u62db\u67b6\uff0c\u7518\u5fc3\u53d7\u4ed6\u4e00\u62f3\u3002 \u3000\u3000\u4e0d\u6599\u4e01\u5178\u7684\u62f3\u5934\u4f38\u5728\u534a\u7a7a\uff0c\u5374\u4e0d\u843d\u4e0b\uff0c\u5411\u72c4\u4e91\u77aa\u89c6\u7247\u523b\uff0c\u7f13\u7f13\u6536\u56de\u62f3\u5934\uff0c\u9053\uff1a\u201c\u5144\u5f1f\uff0c\u4f60\u4e3a\u5973\u5b50\u6240\u8d1f\uff0c\u4ee5\u81f4\u5bf9\u5929\u4e0b\u5973\u5b50\u90fd\u4e0d\u76f8\u4fe1\uff0c\u6211\u4e5f\u4e0d\u6765\u602a\u4f60\u3002\u971c\u534e\u82e5\u662f\u53d7\u5979\u7236\u4eb2\u5631\u5490\uff0c\u60f3\u4f7f\u7f8e\u4eba\u8ba1\uff0c\u8981\u9a97\u6211\u7684\u795e\u7167\u7ecf\u548c\uff0c\u90a3\u662f\u5f88\u5bb9\u6613\u7684\u3002\u5979\u53c8\u4f55\u5fc5\u9a97\uff1f\u53ea\u987b\u8bf4\u4e00\u53e5\uff1a\u2018\u4f60\u90a3\u90e8\u795e\u7167\u7ecf\u548c\u7ed9\u4e86\u6211\u7f62\uff01\u2019\u5979\u751a\u81f3\u4e0d\u7528\u660e\u8bf4\uff0c\u53ea\u987b\u6697\u793a\u4e00\u4e0b\uff0c\u6216\u662f\u8868\u793a\u4e86\u8fd9\u4e48\u4e00\u70b9\u70b9\u610f\u601d\uff0c\u6211\u7acb\u523b\u5c31\u7ed9\u4e86\u5979\u3002\u5979\u62ff\u53bb\u7ed9\u5979\u7236\u4eb2\u4e5f\u597d\uff0c\u65bd\u820d\u7ed9\u8857\u8fb9\u7684\u4e5e\u4e10\u4e5f\u597d\uff0c\u8981\u6216\u662f\u6495\u70c2\u6765\u73a9\u4e5f\u597d\uff0c\u70e7\u7740\u77a7\u4e5f\u597d\uff0c\u6211\u90fd\u7709\u5934\u4e5f\u4e0d\u76b1\u4e00\u4e0b\u3002\u72c4\u5144\u5f1f\uff0c\u867d\u7136\u8fd9\u662f\u6b66\u6797\u4e2d\u7684\u5947\u4e66\u81f3\u5b9d\uff0c\u53ef\u662f\u4e0e\u971c\u534e\u76f8\u6bd4\uff0c\u5728\u6211\u5fc3\u4e2d\uff0c\u8fd9\u5947\u4e66\u81f3\u5b9d\u4e5f\u4e0d\u8fc7\u662f\u7caa\u571f\u800c\u5df2\u3002\u51cc\u9000\u601d\u6789\u81ea\u6587\u6b66\u53cc\u5168\uff0c\u5b9e\u5728\u662f\u4e2a\u5927\u5927\u7684\u8822\u624d\u3002\u4ed6\u82e5\u53eb\u5973\u513f\u5411\u6211\u7d22\u53d6\uff0c\u6211\u7109\u6709\u76f8\u62d2\u4e4b\u7406\uff1f\u201d \u3000\u3000\u72c4\u4e91\u9053\uff1a\u201c\u8bf4\u4e0d\u5b9a\u4ed6\u66fe\u8ddf\u51cc\u5c0f\u59d0\u8bf4\u8fc7\uff0c\u51cc\u5c0f\u59d0\u5374\u4e0d\u7b54\u5141\u3002\u201d \u3000\u3000\u4e01\u5178\u6447\u5934\u9053\uff1a\u201c\u82e5\u6709\u6b64\u4e8b\uff0c\u971c\u534e\u4e5f\u51b3\u4e0d\u7792\u6211\u3002\u201d\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u51cc\u9000\u601d\u8fd9\u79cd\u4eba\uff0c\u4e8e\u529f\u540d\u5229\u7984\u3001\u91d1\u94f6\u8d22\u5b9d\u770b\u5f97\u6781\u91cd\uff0c\u4ee5\u5df1\u5ea6\u4eba\uff0c\u4ee5\u4e3a\u5929\u4e0b\u4eba\u90fd\u5982\u4ed6\u4e00\u822c\u7684\u91cd\u8d22\u8f7b\u4e49\uff0c\u4ee5\u4e3a\u4ed6\u5973\u513f\u5018\u82e5\u5411\u6211\u7d22\u53d6\uff0c\u6211\u4e00\u5b9a\u4e0d\u5141\uff0c\u53cd\u5012\u7740\u4e86\u5f62\u8ff9\uff0c\u4ee4\u6211\u8d77\u4e86\u63d0\u9632\u4e4b\u5fc3\u3002\u53e6\u5916\u8fd8\u6709\u4e2a\u539f\u56e0\uff0c\u4ed6\u662f\u7ff0\u6797\u77e5\u5e9c\uff0c\u5973\u513f\u5374\u79c1\u4e0b\u91cc\u7ed3\u8bc6\u4e86\u6211\u8fd9\u8349\u83bd\u5e03\u8863\u3002\u4ed6\u75db\u6068\u6211\u8fb1\u6ca1\u4e86\u4ed6\u95e8\u6963\uff0c\u975e\u6740\u6211\u4e0d\u53ef\u3002 \u3000\u3000\u201c\u4ed6\u5c06\u6211\u64d2\u4f4f\u540e\uff0c\u7acb\u65f6\u4fbf\u641c\u6211\u5168\u8eab\uff0c\u751a\u4e48\u4e1c\u897f\u4e5f\u627e\u4e0d\u5230\uff0c\u5728\u6211\u7684\u5bd3\u6240\u7a77\u641c\u5927\u7d22\uff0c\u81ea\u7136\u4e5f\u627e\u4e0d\u5230\u751a\u4e48\u3002\u6bcf\u4e2a\u6708\u5341\u4e94\uff0c\u4ed6\u603b\u662f\u63d0\u6211\u51fa\u53bb\u76d8\u95ee\u62f7\u6253\uff0c\u628a\u751a\u4e48\u751c\u8a00\u871c\u8bed\u90fd\u8bf4\u5b8c\u4e86\uff0c\u5a01\u5413\u80c1\u8feb\u4e5f\u90fd\u4f7f\u904d\u4e86\uff0c\u6211\u53ea\u662f\u7ed9\u4ed6\u4e2a\u4e0d\u7406\u4e0d\u776c\u3002\u4ed6\u4ece\u6211\u5634\u91cc\u95ee\u4e0d\u5230\u534a\u53e5\u771f\u8bdd\uff0c\u4f46\u4ece\u4ed6\u76d8\u95ee\u7684\u8bdd\u4e2d\uff0c\u6211\u53cd\u800c\u63a8\u60f3\u5230\u4e86\uff0c\u539f\u6765\u6885\u5ff5\u7b19\u8001\u5148\u751f\u8ddf\u6211\u8bf4\u7684\u90a3\u2018\u2019\uff0c\u4fbf\u662f\u627e\u5bfb\u6881\u5143\u5e1d\u5927\u5b9d\u85cf\u7684\u79d8\u8bc0\u3002\u4ed6\u53c8\u66fe\u6d3e\u4eba\u88c5\u626e\u4e86\u56da\u72af\uff0c\u548c\u6211\u5173\u5728\u4e00\u8d77\uff0c\u60f3\u5957\u95ee\u6211\u7684\u53e3\u98ce\u3002\u90a3\u4eba\u5047\u88c5\u53d7\u4e86\u51a4\u5c48\uff0c\u5927\u9a82\u51cc\u9000\u601d\u4e0d\u662f\u597d\u4eba\u3002\u53ef\u662f\u6211\u4e00\u4e0b\u5b50\u5c31\u77a7\u4e86\u51fa\u6765\uff0c\u53ea\u53ef\u60dc\u90a3\u65f6\u6ca1\u7ec3\u6210\u795e\u7167\u529f\uff0c\u8eab\u4e0a\u6ca1\u591a\u5c11\u529b\u91cf\uff0c\u6253\u5f97\u4ed6\u4e0d\u591f\u5389\u5bb3\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u5230\u8fd9\u91cc\uff0c\u5634\u89d2\u8fb9\u9732\u51fa\u4e00\u4e1d\u5fae\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u8fd0\u6c14\u4e0d\u597d\uff0c\u7ed9\u6211\u51a4\u6789\u6253\u4e86\u4e0d\u5c11\u987f\u3002\u82e5\u4e0d\u662f\u4f60\u6295\u7f33\u81ea\u5c3d\uff0c\u5230\u4eca\u65e5\u8bf4\u4e0d\u5b9a\u7ed9\u6211\u6253\u4e5f\u6253\u6b7b\u4e86\u3002\u201d\u72c4\u4e91\u9053\uff1a\u201c\u6211\u7ed9\u4eba\u9677\u5bb3\uff0c\u82e5\u4e0d\u662f\u4e01\u5927\u54e5\u2026\u2026\u201d\u4e01\u5178\u5de6\u624b\u6447\u4e86\u6447\uff0c\u8981\u4ed6\u522b\u8bf4\u4e0b\u53bb\uff0c\u9053\uff1a\u201c\u8fd9\u662f\u673a\u7f18\u3002\u4e16\u4e8b\u90fd\u8bb2\u7a76\u4e00\u4e2a\u2018\u7f18\u2019\u5b57\u3002\u201d \u3000\u3000\u4ed6\u773c\u89d2\u659c\u5904\uff0c\u6708\u5149\u4e0b\u89c1\u5230\u5e9f\u56ed\u89d2\u843d\u7684\u74e6\u783e\u4e4b\u4e2d\uff0c\u957f\u7740\u4e00\u6735\u5c0f\u5c0f\u7684\u7d2b\u82b1\uff0c\u8fce\u98ce\u6447\u66f3\uff0c\u9887\u6709\u5b64\u5bc2\u51c4\u51c9\u4e4b\u610f\uff0c\u4fbf\u9053\uff1a\u201c\u4f60\u7ed9\u6211\u91c7\u4e86\u6765\u3002\u201d\u72c4\u4e91\u8fc7\u53bb\u6458\u4e0b\u82b1\u6735\uff0c\u9012\u5728\u4ed6\u7684\u624b\u91cc\u3002 \u3000\u3000\u4e01\u5178\u62ff\u7740\u90a3\u6735\u5c0f\u7d2b\u82b1\uff0c\u795e\u9a70\u5f80\u65e5\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u6211\u7ed9\u7a7f\u4e86\u7435\u7436\u9aa8\uff0c\u5173\u5728\u7262\u91cc\uff0c\u4e00\u5207\u90fd\u5df2\u60f3\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u51cc\u9000\u601d\u662f\u975e\u8981\u4e86\u6211\u7684\u547d\u4e0d\u53ef\u3002\u6211\u5982\u5c06\u7ecf\u8bc0\u65e9\u4e00\u65e5\u4ea4\u7ed9\u4ed6\uff0c\u4ed6\u4fbf\u65e9\u4e00\u65e5\u6740\u6211\u3002 \u3000\u3000\u4f46\u5982\u6211\u82e6\u6328\u4e0d\u8bf4\uff0c\u4ed6\u77a7\u5728\u8d22\u5b9d\u7684\u9762\u4e0a\uff0c\u53cd\u800c\u4e0d\u4f1a\u5bb3\u6211\uff0c\u4fbf\u662f\u62f7\u6253\u6298\u78e8\uff0c\u4e5f\u53ea\u8ba9\u6211\u53d7\u4e9b\u76ae\u8089\u4e4b\u82e6\uff0c\u8fd8\u771f\u820d\u4e0d\u5f97\u4f24\u4e86\u6211\u7684\u8981\u5bb3\u3002\u201d \u3000\u3000\u72c4\u4e91\u9053\uff1a\u201c\u662f\u4e86\uff0c\u90a3\u65e5\u6211\u5047\u610f\u8981\u6740\u4f60\uff0c\u90a3\u72f1\u5352\u53cd\u800c\u5927\u8d77\u5fd9\u5934\uff0c\u4e0d\u6562\u518d\u5f3a\u51f6\u9738\u9053\u3002\u201d \u3000\u3000\u4e01\u5178\u62ff\u7740\u90a3\u6735\u5c0f\u7d2b\u82b1\u3002\u624b\u6307\u5fae\u5fae\u98a4\u6296\uff0c\u7d2b\u82b1\u4e5f\u5fae\u5fae\u98a4\u6296\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u6211\u5728\u7262\u72f1\u4e2d\u7ed9\u5173\u4e86\u4e00\u4e2a\u591a\u6708\uff0c\u53c8\u6c14\u53c8\u6025\uff0c\u51e0\u4e4e\u8981\u53d1\u75af\u4e86\u3002 \u3000\u3000\u4e00\u5929\u665a\u4e0a\uff0c\u7ec8\u4e8e\u6765\u4e86\u4e00\u4e2a\u4e2b\u9b1f\uff0c\u90a3\u4fbf\u662f\u51cc\u5c0f\u59d0\u7684\u8d34\u8eab\u4f7f\u5a62\u83ca\u53cb\uff0c\u6211\u5728\u6b66\u660c\u57ce\u91cc\u8bc6\u5f97\u971c\u534e\uff0c\u4fbf\u56e0\u5979\u4e00\u8a00\u800c\u8d77\u3002\u4e0d\u77e5\u971c\u534e\u4f7f\u4e86\u591a\u5c11\u8d3f\u8d42\uff0c\u624d\u6253\u52a8\u72f1\u5352\uff0c\u5f15\u5f97\u5979\u6765\u89c1\u6211\u4e00\u9762\u3002\u53ef\u662f\uff0c\u83ca\u53cb\u4e00\u53e5\u8bdd\u4e5f\u6ca1\u8ddf\u6211\u8bf4\uff0c\u4e5f\u6ca1\u751a\u4e48\u4e66\u67ec\u7269\u4e8b\u9012\u7ed9\u6211\u3002\u53ea\u662f\u5411\u6211\u5446\u671b\u3002\u72f1\u5352\u624b\u91cc\u62ff\u7740\u4e00\u67c4\u5c16\u5200\uff0c\u6307\u4f4f\u5979\u7684\u80cc\u5fc3\u3002\u6211\u5f88\u660e\u767d\uff0c\u90a3\u72f1\u5352\u663e\u662f\u6015\u6781\u4e86\u51cc\u77e5\u5e9c\uff0c\u53ea\u8bb8\u5979\u89c1\u6211\u4e00\u9762\uff0c\u53ef\u4e0d\u8bb8\u8bf4\u8bdd\u3002 \u3000\u3000\u201c\u83ca\u53cb\u77a7\u4e86\u6211\u4e00\u4f1a\uff0c\u6014\u6014\u7684\u6d41\u4e0b\u6cea\u6765\u3002\u90a3\u72f1\u5352\u8fde\u6253\u624b\u52bf\uff0c\u547d\u5979\u5feb\u8d70\u3002\u83ca\u53cb\u89c1\u5230\u94c1\u69db\u5916\u7684\u5ead\u9662\u4e2d\u957f\u5f97\u6709\u4e00\u6735\u5c0f\u96cf\u83ca\uff0c\u4fbf\u53bb\u91c7\u4e86\u6765\uff0c\u9694\u7740\u94c1\u69db\u9012\u4e86\u7ed9\u6211\uff0c\u4f38\u624b\u6307\u7740\u8fdc\u5904\u9ad8\u697c\u4e0a\u7684\u7a97\u69db\u3002 \u3000\u3000\u7a97\u69db\u4e0a\u653e\u7740\u4e00\u76c6\u9c9c\u82b1\u3002\u6211\u5fc3\u4e2d\u4e00\u559c\uff0c\u77e5\u9053\u8fd9\u82b1\u662f\u971c\u534e\u653e\u5728\u90a3\u513f\u7684\uff0c\u4f5c\u4e3a\u6211\u7684\u4f34\u4fa3\u3002 \u3000\u3000\u201c\u83ca\u53cb\u4e0d\u80fd\u591a\u505c\uff0c\u8f6c\u8eab\u8d70\u4e86\u51fa\u53bb\u3002\u521a\u8981\u8d70\u51fa\u9662\u5b50\u7684\u94c1\u95e8\uff0c\u9ad8\u5904\u4e00\u7bad\u5c04\u4e86\u4e0b\u6765\uff0c\u6b63\u4e2d\u5979\u80cc\u5fc3\uff0c\u767b\u65f6\u4fbf\u5c06\u5979\u5c04\u6b7b\u4e86\u3002\u539f\u6765\u51cc\u9000\u601d\u6df1\u6015\u6211\u670b\u53cb\u524d\u6765\u52ab\u72f1\uff0c\u8fde\u5899\u5934\u5c4b\u9876\u90fd\u4f0f\u5f97\u6709\u4eba\u3002\u8ddf\u7740\u7b2c\u4e8c\u7bad\u5c04\u4e0b\uff0c\u90a3\u72f1\u5352\u4e5f\u9001\u4e86\u6027\u547d\u3002\u90a3\u65f6\u6211\u786e\u662f\u5341\u5206\u5bb3\u6015\uff0c\u53ea\u6015\u51cc\u9000\u601d\u6a2a\u4e86\u5fc3\uff0c\u8fde\u81ea\u5df1\u5973\u513f\u7adf\u4e5f\u52a0\u5bb3\u3002\u6211\u4e0d\u6562\u518d\u89e6\u6012\u4ed6\uff0c\u6bcf\u6b21\u4ed6\u5ba1\u95ee\u6211\uff0c\u6211\u53ea\u7ed9\u4ed6\u88c5\u804b\u4f5c\u54d1\u3002 \u3000\u3000\u201c\u83ca\u53cb\u662f\u4e3a\u6211\u800c\u6b7b\u7684\uff0c\u82e5\u4e0d\u662f\u5979\uff0c\u8fd9\u51e0\u5e74\u6211\u5982\u4f55\u71ac\u5f97\u8fc7\uff1f \u3000\u3000\u6211\u600e\u77e5\u9053\u90a3\u7a97\u69db\u4e0a\u7684\u9c9c\u82b1\uff0c\u662f\u971c\u534e\u4e3a\u6211\u800c\u653e\uff1f\u53ef\u662f\u971c\u534e\u59cb\u7ec8\u4e0d\u9732\u9762\uff0c\u59cb\u7ec8\u4e0d\u5728\u90a3\u8fb9\u7a97\u5b50\u4e2d\u63a2\u51fa\u5934\u6765\u8ba9\u6211\u77a7\u5979\u4e00\u773c\u3002\u6211\u5f53\u65f6\u4e00\u70b9\u4e5f\u4e0d\u660e\u767d\uff0c\u6709\u65f6\u4e0d\u514d\u602a\u5979\uff0c\u4e3a\u751a\u4e48\u8fd9\u6837\u5fcd\u5fc3\u3002 \u3000\u3000\u201c\u4e8e\u662f\u6211\u52a0\u7d27\u7528\u529f\uff0c\u82e6\u7ec3\u795e\u7167\u7ecf\uff0c\u8981\u65e9\u65e5\u529f\u884c\u5706\u6ee1\uff0c\u80fd\u4e0d\u53d7\u8fd9\u94c1\u94d0\u7684\u62d8\u675f\u3002\u6211\u53ea\u76fc\u5f97\u8131\u6a0a\u7b3c\uff0c\u5e26\u540c\u971c\u534e\u51fa\u56f0\u3002\u53ea\u662f\u8fd9\u795e\u7167\u529f\u8bb2\u7a76\u5999\u609f\u81ea\u7136\uff0c\u5e76\u975e\u4e00\u5473\u52e4\u4fee\u82e6\u7ec3\u4fbf\u80fd\u594f\u529f\u3002\u6211\u7ed9\u7a7f\u4e86\u7435\u7436\u9aa8\uff0c\u6311\u65ad\u4e86\u811a\u7b4b\uff0c\u81ea\u7136\u6bd4\u65c1\u4eba\u53c8\u52a0\u500d\u8270\u96be\u3002\u76f4\u5230\u4f60\u81ea\u5c3d\u4e4b\u524d\u7684\u4e24\u4e2a\u6708\uff0c\u8fd9\u624d\u5927\u529f\u544a\u6210\u3002\u8fd9\u4e9b\u65e5\u5b50\u4e4b\u4e2d\uff0c\u5168\u51ed\u8fd9\u4e00\u76c6\u9c9c\u82b1\u4f5c\u4e3a\u6211\u7684\u6170\u85c9\u3002 \u3000\u3000\u201c\u51cc\u9000\u601d\u5343\u65b9\u767e\u8ba1\u7684\u60f3\u5957\u51fa\u6211\u80f8\u4e2d\u79d8\u5bc6\u3002\u5c06\u4f60\u548c\u6211\u5173\u5728\u4e00\u8d77\uff0c\u90a3\u4e5f\u662f\u4ed6\u7684\u8ba1\u7b56\u3002\u4ed6\u77e5\u9053\u6d3e\u4e86\u4eb2\u4fe1\u6765\u9a97\u6211\uff0c\u90a3\u662f\u4e0d\u7ba1\u7528\u7684\u4e86\uff0c\u4e8e\u662f\u7d22\u6027\u8ba9\u4e00\u4e2a\u771f\u6b63\u53d7\u4e86\u5927\u51a4\u5c48\u7684\u5c11\u5e74\u4eba\u6765\u966a\u6211\u3002\u65f6\u5019\u4e00\u4e45\uff0c\u6211\u81ea\u80fd\u8fa8\u522b\u771f\u4f2a\u3002\u53ea\u8981\u6211\u548c\u4f60\u6210\u4e86\u60a3\u96be\u4e4b\u4ea4\uff0c\u5411\u4f60\u5410\u9732\u4e86\u771f\u60c5\uff0c\u90a3\u4e48\u5728\u6211\u8eab\u4e0a\u903c\u4e0d\u51fa\u7684\uff0c\u591a\u534a\u80fd\u5728\u4f60\u53e3\u4e2d\u5957\u9a97\u51fa\u6765\u3002\u4f60\u5e74\u5e7c\u65e0\u77e5\uff0c\u5fe0\u539a\u8001\u5b9e\uff0c\u522b\u4eba\u5047\u88c5\u597d\u4eba\uff0c\u4f60\u5bb9\u6613\u4e0a\u5f53\u3002 \u3000\u3000\u53ef\u662f\u6211\u59cb\u7ec8\u4e0d\u76f8\u4fe1\u4f60\u3002\u6211\u4eb2\u8eab\u7684\u906d\u53d7\uff0c\u83ca\u53cb\u7684\u60e8\u6b7b\uff0c\u53eb\u6211\u5bf9\u8c01\u4e5f\u4fe1\u4e0d\u8fc7\u4e86\u3002 \u3000\u3000\u201c\u4e8b\u9694\u591a\u5e74\uff0c\u51cc\u9000\u601d\u8fd9\u8346\u5dde\u5e9c\u77e5\u5e9c\u7684\u5b98\u671f\u65e9\u5df2\u5c4a\u6ee1\uff0c\u8be5\u5f53\u4ed6\u8c03\uff0c\u6216\u662f\u5347\u5b98\uff0c\u60f3\u6765\u4ed6\u4f7f\u4e86\u94f6\u5b50\uff0c\u5c45\u7136\u4e00\u4efb\u4e00\u4efb\u7684\u505a\u4e0b\u53bb\u3002 \u3000\u3000\u4ed6\u4e0d\u60f3\u5347\u5b98\uff0c\u53ea\u60f3\u5f97\u8fd9\u4e2a\u5927\u5b9d\u85cf\u3002 \u3000\u3000\u201c\u4f60\u4ee5\u4e3a\u6211\u6ca1\u51fa\u8fc7\u72f1\u53bb\u5417\uff1f\u6211\u7ec3\u6210\u795e\u7167\u529f\u540e\uff0c\u5f53\u5929\u4fbf\u51fa\u53bb\u4e86\uff0c\u53ea\u662f\u51fa\u53bb\u4e4b\u524d\u70b9\u4e86\u4f60\u7684\u660f\u7761\u7a74\uff0c\u4f60\u81ea\u7136\u4e0d\u77e5\u9053\u3002 \u3000\u3000\u201c\u90a3\u4e00\u665a\u6211\u8d8a\u8fc7\u9ad8\u5899\u4e4b\u65f6\uff0c\u8fd8\u9053\u4e0d\u514d\u4e00\u573a\u6076\u6597\uff0c\u4e0d\u6599\u4e8b\u9694\u591a\u5e74\uff0c\u51cc\u9000\u601d\u5df2\u65e0\u9632\u6211\u4e4b\u5fc3\uff0c\u5916\u8fb9\u7684\u5b88\u536b\u65e9\u5df2\u64a4\u53bb\u3002\u4ed6\u4e07\u4e07\u60f3\u4e0d\u5230\u795e\u7167\u529f\u5982\u6b64\u5947\u5999\uff0c\u7a7f\u4e86\u7435\u7436\u9aa8\u3001\u6311\u65ad\u4e86\u811a\u7b4b\u7684\u4eba\uff0c\u5c45\u7136\u8fd8\u80fd\u7ec3\u6210\u4e0a\u4e58\u6b66\u529f\u3002 \u3000\u3000\u201c\u6211\u5230\u4e86\u9ad8\u697c\u7684\u7a97\u4e0b\uff0c\u5fc3\u4e2d\u8df3\u5f97\u5341\u5206\u5389\u5bb3\uff0c\u4f3c\u4e4e\u53c8\u56de\u5230\u4e86\u521d\u6b21\u5728\u7a97\u4e0b\u89c1\u5230\u5979\u7684\u5fc3\u60c5\u3002\u7ec8\u4e8e\u9f13\u8d77\u4e86\u52c7\u6c14\uff0c\u8f7b\u8f7b\u5728\u7a97\u4e0a\u6572\u4e86\u4e09\u4e0b\uff0c\u53eb\u4e86\u58f0\uff1a\u2018\u971c\u534e\uff01\u2019\u201c\u5979\u4ece\u68a6\u4e2d\u60ca\u9192\u8fc7\u6765\uff0c\u6726\u6726\u80e7\u80e7\u7684\u9053\uff1a\u2018\u5927\u54e5\uff01\u5178\u54e5\uff01\u662f\u4f60\u4e48\uff1f\u6211\u662f\u5728\u505a\u68a6\u4e48\uff1f\u2019\u6211\u9694\u4e86\u8fd9\u8bb8\u591a\u82e6\u65e5\u5b50\uff0c\u7ec8\u4e8e\u53c8\u518d\u542c\u5230\u5979\u7684\u58f0\u97f3\uff0c\u6b22\u559c\u5f97\u771f\u8981\u53d1\u72c2\uff0c\u98a4\u58f0\u9053\uff1a\u2018\u971c\u59b9\uff0c\u662f\u6211\uff01\u6211\u9003\u51fa\u6765\u5566\u3002\u6211\u7b49\u5979\u6765\u5f00\u7a97\uff0c\u4ee5\u524d\u6211\u4eec\u6bcf\u6b21\u76f8\u4f1a\uff0c\u603b\u662f\u7b49\u5979\u63a8\u5f00\u7a97\u5b50\u62db\u4e86\u624b\uff0c\u6211\u624d\u8fdb\u53bb\u3002\u6211\u4ece\u6765\u4e0d\u81ea\u884c\u8fdb\u5979\u7684\u623f\u3002 \u3000\u3000\u201c\u4e0d\u6599\u5979\u5e76\u4e0d\u5f00\u7a97\uff0c\u5c06\u8138\u8d34\u5728\u7a97\u7eb8\u4e0a\uff0c\u4f4e\u58f0\u9053\uff1a\u2018\u8c22\u5929\u8c22\u5730\uff0c\u5178\u54e5\uff0c\u4f60\u4ecd\u662f\u597d\u597d\u7684\u6d3b\u7740\uff0c\u7239\u7239\u6ca1\u9a97\u6211\u3002\u2019\u6211\u7684\u58f0\u97f3\u5f88\u82e6\u6da9\uff0c\u8bf4\u9053\uff1a\u2018\u55ef\uff0c\u4f60\u7239\u7239\u6ca1\u9a97\u4f60\u3002\u6211\u8fd8\u6d3b\u7740\u3002\u4f60\u5f00\u7a97\u7f62\uff0c\u6211\u8981\u77a7\u4f60\u3002\u2019\u5979\u6025\u9053\uff1a\u2018\u4e0d\uff0c\u4e0d\uff0c\u4e0d\u884c\uff01\u2019\u6211\u7684\u5fc3\u6c89\u4e86\u4e0b\u53bb\uff0c\u95ee\u9053\uff1a\u2018\u4e3a\u751a\u4e48\u4e0d\u884c\uff1f\u2019\u5979\u9053\uff1a\u2018\u6211\u7b54\u5e94\u4e86\u7239\u7239\uff0c\u4ed6\u4e0d\u4f24\u4f60\u6027\u547d\uff0c\u6211\u5c31\u6c38\u8fdc\u4e0d\u518d\u8ddf\u4f60\u76f8\u89c1\u3002\u4ed6\u8981\u6211\u8d77\u4e86\u8a93\uff0c\u8981\u6211\u8d77\u4e00\u4e2a\u6bd2\u8a93\uff0c\u5018\u82e5\u6211\u518d\u89c1\u4f60\uff0c\u6211\u5988\u5988\u5728\u9634\u4e16\u5929\u5929\u53d7\u6076\u9b3c\u6b3a\u4fae\u3002\u2019\u5979\u8bf4\u5230\u8fd9\u91cc\uff0c\u58f0\u97f3\u54fd\u54bd\u4e86\u3002\u5979\u5341\u4e09\u5c81\u90a3\u5e74\u4e27\u6bcd\uff0c\u5bf9\u4ea1\u6bcd\u662f\u6700\u656c\u7231\u4e0d\u8fc7\u7684\u3002 \u3000\u3000\u201c\u6211\u771f\u6068\u6781\u4e86\u51cc\u9000\u601d\u7684\u6076\u6bd2\u5fc3\u80a0\u3002\u4ed6\u4e0d\u6740\u6211\uff0c\u53ea\u4e0d\u8fc7\u4e3a\u4e86\u60f3\u5f97\u7ecf\u8bc0\uff0c\u971c\u534e\u4fbf\u4e0d\u8d77\u8fd9\u4e2a\u6bd2\u8a93\uff0c\u4ed6\u4e5f\u51b3\u8ba1\u820d\u4e0d\u5f97\u6740\u6211\u3002\u53ef\u662f\u4ed6\u7ec8\u4e8e\u903c\u5f97\u5973\u513f\u8d77\u4e86\u8fd9\u4e2a\u6bd2\u8a93\uff0c\u8fd9\u4e00\u4e2a\u6bd2\u8a93\uff0c\u5c06\u6211\u751a\u4e48\u6307\u671b\u90fd\u5316\u6210\u4e86\u6ce1\u5f71\u3002\u4f46\u6211\u4ecd\u4e0d\u6b7b\u5fc3\uff0c\u8bf4\u9053\uff1a\u2018\u971c\u534e\uff0c\u4f60\u8ddf\u6211\u8d70\u3002 \u3000\u3000\u4f60\u628a\u773c\u775b\u7528\u5e03\u8499\u4e86\u8d77\u6765\uff0c\u6c38\u4e0d\u89c1\u6211\u5c31\u662f\u3002\u2019\u5979\u54ed\u9053\uff1a\u2018\u90a3\u4e0d\u6210\u7684\u3002\u6211\u4e5f\u4e0d\u613f\u4f60\u518d\u89c1\u6211\u3002\u2019\u201c\u6211\u80f8\u4e2d\u79ef\u4e86\u8bb8\u591a\u5e74\u7684\u6028\u6124\u7a81\u7136\u8ff8\u53d1\u51fa\u6765\uff0c\u53eb\u9053\uff1a\u2018\u4e3a\u751a\u4e48\uff1f\u6211\u975e\u89c1\u4f60\u4e0d\u53ef\uff01\u2019\u201c\u5979\u542c\u5230\u6211\u7684\u58f0\u97f3\u6709\u5f02\uff0c\u67d4\u58f0\u9053\uff1a\u2018\u5178\u54e5\uff0c\u6211\u77e5\u9053\u4f60\u7ed9\u7239\u7239\u64d2\u83b7\u540e\uff0c\u4e00\u518d\u6c42\u4ed6\u653e\u4f60\u3002\u4ed6\u5374\u5c06\u6211\u53e6\u884c\u8bb8\u914d\u522b\u4eba\uff0c\u8981\u6211\u6b7b\u4e86\u5bf9\u4f60\u7684\u5fc3\u3002\u6211\u8bf4\u751a\u4e48\u4e5f\u4e0d\u7b54\u5141\uff0c\u4ed6\u7528\u5f3a\u903c\u8feb\uff0c\u4e8e\u662f\u2026\u2026\u4e8e\u662f\u2026\u2026\u6211\u7528\u5200\u5b50\u5212\u7834\u4e86\u81ea\u5df1\u7684\u8138\u3002\u2019\u201d \u3000\u3000\u72c4\u4e91\u542c\u5230\u8fd9\u91cc\uff0c\u4e0d\u7981\u201c\u554a\u201d\u7684\u4e00\u58f0\u53eb\u4e86\u51fa\u6765\u3002 \u3000\u3000\u4e01\u5178\u9053\uff1a\u201c\u6211\u53c8\u662f\u611f\u6fc0\uff0c\u53c8\u662f\u601c\u60dc\uff0c\u4e00\u638c\u6253\u7834\u4e86\u7a97\u5b50\u3002\u5979\u60ca\u547c\u4e00\u58f0\uff0c\u95ed\u8d77\u4e86\u773c\u775b\uff0c\u4f38\u624b\u8499\u4f4f\u4e86\u81ea\u5df1\u7684\u8138\uff0c\u53ef\u662f\u6211\u5df2\u7ecf\u77a7\u89c1\u4e86\u3002\u5979\u90a3\u5929\u4e0b\u6700\u7f8e\u4e3d\u7684\u8138\u5e9e\u4e0a\uff0c\u5df2\u53c8\u6a2a\u53c8\u7ad6\u7684\u5212\u4e0a\u4e86\u5341\u4e03\u516b\u5200\uff0c\u808c\u8089\u7ffb\u4e86\u51fa\u6765\uff0c\u4e00\u6761\u6761\u90fd\u662f\u9c9c\u7ea2\u7684\u75a4\u75d5\u3002\u5979\u7f8e\u4e3d\u7684\u773c\u775b\uff0c\u7f8e\u4e3d\u7684\u9f3b\u5b50\uff0c\u7f8e\u4e3d\u7684\u5634\u5df4\uff0c\u90fd\u662f\u6b6a\u6b6a\u626d\u626d\uff0c\u53d8\u5f97\u50cf\u5996\u9b54\u4e00\u6837\u3002\u6211\u4f38\u624b\u5c06\u5979\u6402\u5728\u6000\u91cc\u3002\u5979\u5e73\u65f6\u591a\u4e48\u7231\u60dc\u81ea\u5df1\u7684\u5bb9\u989c\uff0c\u82e5\u4e0d\u662f\u4e3a\u4e86\u6211\u8fd9\u4e0d\u7965\u4e4b\u4eba\uff0c\u5979\u600e\u80af\u8ba9\u81ea\u5df1\u7684\u8138\u86cb\u53d7\u534a\u70b9\u635f\u4f24\uff1f \u3000\u3000\u6211\u8bf4\uff1a\u2018\u971c\u59b9\uff0c\u5bb9\u8c8c\u53ca\u5f97\u4e0a\u5fc3\u4e48\uff1f\u4f60\u4e3a\u6211\u800c\u6bc1\u5bb9\uff0c\u5728\u6211\u5fc3\u4e2d\uff0c\u4f60\u6bd4\u4ece\u524d\u66f4\u52a0\u7f8e\u4e0a\u5341\u500d\uff0c\u767e\u500d\u3002\u2019\u5979\u54ed\u9053\uff1a\u2018\u5230\u4e86\u8fd9\u5730\u6b65\uff0c\u54b1\u4fe9\u600e\u4e48\u8fd8\u80fd\u53ae\u5b88\uff1f\u6211\u7b54\u5141\u4e86\u7239\u7239\uff0c\u6c38\u8fdc\u4e0d\u518d\u89c1\u4f60\u3002\u5178\u54e5\uff0c\u4f60\u2026\u2026\u4f60\u53bb\u7f62\uff01\u2019\u6211\u77e5\u9053\u8fd9\u662f\u65e0\u53ef\u633d\u56de\u7684\u4e86\uff0c\u8bf4\u9053\uff1a\u2018\u971c\u59b9\uff0c\u6211\u56de\u5230\u7262\u72f1\u4e2d\u53bb\uff0c\u5929\u5929\u77a7\u7740\u4f60\u8fd9\u7a97\u8fb9\u7684\u9c9c\u82b1\u3002\u2019\u5979\u5374\u6402\u4f4f\u6211\u7684\u8116\u5b50\uff0c\u8bf4\u9053\uff1a\u2018\u4f60\u2026\u2026\u4f60\u522b\u8d70\uff01\u2019\u201c\u6211\u548c\u5979\u76f8\u504e\u76f8\u501a\uff0c\u4e0d\u518d\u8bf4\u751a\u4e48\u8bdd\u3002\u5979\u4e0d\u6562\u770b\u6211\uff0c\u6211\u4e5f\u4e0d\u6562\u518d\u77a7\u5979\u3002\u6211\u5f53\u7136\u4e0d\u662f\u5acc\u5979\u4e11\u964b\uff0c\u53ef\u662f\u2026\u2026\u53ef\u662f\u2026\u2026\u5979\u7684\u8138\u5b9e\u5728\u6bc1\u635f\u5f97\u5389\u5bb3\u3002\u9694\u4e86\u5f88\u4e45\u5f88\u4e45\uff0c\u8fdc\u5904\u7684\u9e21\u557c\u4e86\u3002\u5979\u8bf4\uff1a\u2018\u5178\u54e5\uff0c\u6211\u4e0d\u80fd\u5bb3\u6211\u6b7b\u4e86\u7684\u5988\u5988\uff0c\u4f60\u2026\u2026\u4f60\u4ee5\u540e\u522b\u518d\u6765\u770b\u6211\u3002\u2019\u6211\u8bf4\uff1a\u2018\u54b1\u4fe9\u4ece\u6b64\u4e0d\u518d\u76f8\u89c1\uff1f\u2019\u5979\u54ed\u9053\uff1a\u2018\u4e0d\u518d\u76f8\u89c1\uff01\u6211\u53ea\u76fc\u54b1\u4fe9\u6b7b\u4e86\u4e4b\u540e\uff0c\u80fd\u591f\u846c\u5728\u4e00\u8d77\u3002\u53ea\u76fc\u6709\u54ea\u4e00\u4f4d\u597d\u5fc3\u4eba\uff0c\u80fd\u591f\u9042\u4e86\u6211\u8fd9\u5fc3\u613f\uff0c\u6211\u5728\u9634\u95f4\u5929\u5929\u5ff5\u4f5b\u4fdd\u4f51\u4ed6\u3002\u2019\u201c\u6211\u9053\uff1a\u2018\u6211\u5df2\u63a8\u60f3\u5230\uff0c\u6211\u6240\u77e5\u9053\u7684\u90a3\u201c\u201d\uff0c\u4fbf\u662f\u627e\u5bfb\u6881\u5143\u5e1d\u90a3\u5927\u5b9d\u85cf\u7684\u79d8\u8bc0\u3002\u6211\u8ddf\u4f60\u8bf4\uff0c\u4f60\u597d\u597d\u8bb0\u4f4f\u4e86\u3002\u2019\u5979\u9053\uff1a\u2018\u6211\u4e0d\u8bb0\uff0c\u6211\u8bb0\u7740\u5e72\u751a\u4e48\uff1f\u7239\u7239\u4e3a\u4e86\u8fd9\u4e2a\u79d8\u5bc6\uff0c\u624d\u5bb3\u5f97\u4f60\u8fd9\u6837\uff0c\u5178\u54e5\uff0c\u6211\u4e0d\u60f3\u542c\u3002\u2019\u6211\u9053\uff1a\u2018\u4f60\u5bfb\u4e00\u4e2a\u8bda\u5b9e\u53ef\u9760\u4e4b\u4eba\uff0c\u8981\u4ed6\u7b54\u5141\u5e2e\u54b1\u4eec\u6210\u5168\u8fd9\u4e2a\u5408\u846c\u7684\u5fc3\u613f\uff0c\u5c31\u5c06\u8fd9\u5251\u8bc0\u5bf9\u4ed6\u8bf4\u3002\u2019\u201c\u5979\u9053\uff1a\u2018\u6211\u8fd9\u4e00\u751f\u662f\u51b3\u4e0d\u4e0b\u8fd9\u697c\u7684\u4e86\uff0c\u6211\u8fd9\u526f\u6837\u5b50\uff0c\u600e\u80fd\u89c1\u4eba\uff1f\u2019\u53ef\u662f\u5979\u60f3\u4e86\u4e00\u60f3\u4e4b\u540e\uff0c\u53c8\u9053\uff1a\u2018\u597d\uff0c\u4f60\u8ddf\u6211\u8bf4\u3002\u5178\u54e5\uff0c\u6211\u65e0\u8bba\u5982\u4f55\u8981\u8ddf\u4f60\u846c\u5728\u4e00\u8d77\u3002\u5c31\u8fd9\u526f\u6837\u5b50\u53bb\u6c42\u4eba\uff0c\u6211\u4e5f\u4e0d\u6015\u3002\u2019\u4e8e\u662f\u6211\u5c06\u5251\u8bc0\u8bf4\u4e86\u7ed9\u5979\u542c\u3002\u5979\u7528\u5fc3\u8bb0\u4f4f\u4e86\u3002 \u3000\u3000\u201c\u4e1c\u65b9\u6e10\u6e10\u4eae\u4e86\uff0c\u6211\u548c\u5979\u5206\u4e86\u624b\uff0c\u56de\u5230\u4e86\u72f1\u4e2d\u3002\u90a3\u65f6\u6211\u867d\u53ef\u81ea\u7531\u51fa\u72f1\uff0c\u4f46\u6211\u6bcf\u5929\u8981\u770b\u5979\u7a97\u4e0a\u7684\u82b1\uff0c\u6211\u662f\u6c38\u8fdc\u6c38\u8fdc\u4e0d\u4f1a\u8d70\u7684\u2026\u2026\u6709\u4eba\u884c\u523a\u51cc\u9000\u601d\uff0c\u6211\u53cd\u800c\u6551\u4ed6\uff0c\u56e0\u4e3a\u2026\u2026\u56e0\u4e3a\u5982\u679c\u51cc\u9000\u601d\u7ed9\u4eba\u6740\u4e86\uff0c\u971c\u534e\u4e00\u4e2a\u4eba\u5b64\u82e6\u4f36\u4ec3\uff0c\u5728\u8fd9\u4e16\u4e0a\u518d\u4e5f\u6ca1\u6709\u4f9d\u9760\u2026\u2026\u201d \u3000\u3000\u4ed6\u8bf4\u5230\u8fd9\u91cc\uff0c\u58f0\u97f3\u6e10\u6e10\u4f4e\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u72c4\u4e91\u9053\uff1a\u201c\u5927\u54e5\u4f60\u653e\u5fc3\uff0c\u8981\u662f\u4f60\u771f\u7684\u597d\u4e0d\u4e86\uff0c\u6211\u5b9a\u8981\u5c06\u4f60\u548c\u51cc\u5c0f\u59d0\u5408\u846c\u3002\u6211\u53ef\u4e0d\u5e0c\u7f55\u4f60\u7684\u751a\u4e48\u79d8\u8bc0\uff0c\u4f60\u5c31\u662f\u8bf4\u4e86\uff0c\u6211\u4e5f\u51b3\u8ba1\u4e0d\u542c\u3002\u201d \u3000\u3000\u4e01\u5178\u8138\u9732\u6b22\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u5144\u5f1f\uff0c\u4e0d\u6789\u6211\u7ed3\u8bc6\u4f60\u4e00\u573a\uff0c\u4f60\u7b54\u5141\u7ed9\u6211\u4eec\u5408\u846c\uff0c\u6211\u6b7b\u5f97\u7791\u76ee\uff0c\u6211\u597d\u6b22\u559c\u2026\u2026\u201d\u4ed6\u8bdd\u58f0\u8d8a\u6765\u8d8a\u4f4e\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u5982\u627e\u5230\u8fd9\u4e2a\u5b9d\u85cf\uff0c\u4e5f\u4e0d\u5fc5\u662f\u4e3a\u4e86\u81ea\u5df1\u53d1\u8d22\uff0c\u53ef\u4ee5\u7528\u6765\u6253\u6551\u5929\u4e0b\u7684\u82e6\u4eba\uff0c\u50cf\u6211\uff0c\u50cf\u4f60\u8fd9\u6837\u7684\u82e6\u4eba\uff0c\u5929\u4e0b\u591a\u5f97\u662f\u3002\u8fd9\uff0c\u4f60\u82e5\u662f\u4e0d\u542c\uff0c\u6211\u4e00\u6b7b\u4e4b\u540e\u4fbf\u5931\u4f20\u4e86\uff0c\u5c82\u4e0d\u53ef\u60dc\uff1f\u201d\u72c4\u4e91\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u4e01\u5178\u6df1\u6df1\u5438\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f60\u542c\u7740\uff0c\u8fd9\u90fd\u662f\u4e9b\u6570\u5b57\uff0c\u53ef\u5f04\u9519\u4e0d\u5f97\u3002\u201d\u72c4\u4e91\u6253\u53e0\u7cbe\u795e\uff0c\u51dd\u795e\u503e\u542c\u3002\u4e01\u5178\u9053\uff1a\u201c\u7b2c\u4e00\u4e2a\u5b57\u662f\u2018\u56db\u2019\uff0c\u8fd9\u4e8c\u5b57\u662f\u2018\u4e94\u5341\u4e00\u2019\uff0c\u7b2c\u4e09\u5b57\u662f\u2018\u4e09\u5341\u4e09\u2019\uff0c\u7b2c\u56db\u5b57\u2018\u4e94\u5341\u4e09\u2019\u2026\u2026\u201d \u3000\u3000\u72c4\u4e91\u6b63\u611f\u83ab\u540d\u5176\u5999\uff0c\u5ffd\u542c\u5f97\u5e9f\u56ed\u5916\u811a\u6b65\u58f0\u54cd\uff0c\u6709\u4eba\u8bf4\u9053\uff1a\u201c\u5230\u56ed\u5b50\u91cc\u53bb\u641c\u641c\u3002\u201d \u3000\u3000\u4e01\u5178\u8138\u4e0a\u53d8\u8272\uff0c\u4e00\u8dc3\u800c\u8d77\u3002\u72c4\u4e91\u8ddf\u7740\u8df3\u4e86\u8d77\u6765\u3002\u53ea\u89c1\u5e9f\u56ed\u540e\u95e8\u4e2d\u62a2\u8fdb\u4e09\u6761\u5927\u6c49\u6765\u3002 \u3000"}),showOnHomepage:!0},{name:"\u56db\u3000\u7a7a\u5fc3\u83dc",content:(0,a.jsx)(a.Fragment,{children:"\u4e01\u5178\u5411\u8fd9\u4e09\u4eba\u6a2a\u4e86\u4e00\u773c\uff0c\u95ee\u9053\uff1a\u201c\u5144\u5f1f\uff0c\u9002\u624d\u6211\u8bf4\u7684\u90a3\u56db\u4e2a\u5b57\uff0c\u4f60\u5df2\u8bb0\u4f4f\u4e86\u4e48\uff1f\u201d \u3000\u3000\u72c4\u4e91\u89c1\u4e09\u540d\u654c\u4eba\u5df2\u903c\u8fd1\u8eab\u524d\uff0c\u56f4\u6210\u4e86\u5f27\u5f62\uff0c\u5176\u4e2d\u4e00\u4eba\u6301\u5200\uff0c\u4e00\u4eba\u6301\u5251\uff0c\u522b\u4e00\u4eba\u867d\u662f\u7a7a\u624b\uff0c\u4f46\u6ee1\u8138\u9634\u9e37\u4e4b\u8272\uff0c\u795e\u60c5\u6781\u662f\u53ef\u6016\u3002\u4ed6\u51dd\u795e\u89c6\u654c\uff0c\u672a\u7b54\u4e01\u5178\u7684\u95ee\u8bdd\u3002 \u3000\u3000\u4e01\u5178\u5927\u58f0\u53eb\u9053\uff1a\u201c\u5144\u5f1f\uff0c\u4f60\u8bb0\u4f4f\u4e86\u6ca1\u6709\uff1f\u201d\u72c4\u4e91\u4e00\u51db\uff0c\u9053\uff1a\u201c\u7b2c\u4e00\u5b57\u662f\u2026\u2026\u201d\u4ed6\u672c\u60f3\u8bf4\u51fa\u4e2a\u201c\u56db\u201d\u5b57\u6765\uff0c\u4f46\u7acb\u65f6\u60f3\u8d77\uff1a \u3000\u3000\u201c\u6211\u82e5\u8bf4\u51fa\u53e3\u6765\uff0c\u5c82\u4e0d\u6559\u654c\u4eba\u542c\u53bb\u4e86\uff1f\u201d\u5f53\u5373\u5c06\u5de6\u624b\u4f38\u5230\u80cc\u540e\uff0c\u56db\u6839\u624b\u6307\u4e00\u7ad6\u3002\u4e01\u5178\u9053\uff1a\u201c\u597d\uff01\u201d \u3000\u3000\u90a3\u4f7f\u5200\u7684\u6c49\u5b50\u51b7\u7b11\u9053\uff1a\u201c\u59d3\u4e01\u7684\uff0c\u4f60\u603b\u7b97\u4e5f\u662f\u6761\u6c49\u5b50\uff0c\u600e\u4e48\u5230\u4e86\u8fd9\u5730\u6b65\uff0c\u8fd8\u5728\u5a46\u5a46\u5988\u5988\u7684\u7f57\u55e6\u4e0d\u4f11\uff1f\u5feb\u8ddf\u54b1\u5144\u5f1f\u4eec\u4e56\u4e56\u7684\u56de\u53bb\uff0c\u5927\u5bb6\u514d\u4f24\u548c\u6c14\u3002\u201d\u90a3\u4f7f\u5251\u7684\u6c49\u5b50\u5374\u9053\uff1a\u201c\u72c4\u5927\u54e5\uff0c\u591a\u5e74\u4e0d\u89c1\uff0c\u4f60\u597d\u554a\uff1f\u7262\u72f1\u4e2d\u4f4f\u5f97\u633a\u8212\u670d\u7f62\uff1f\u201d \u3000\u3000\u72c4\u4e91\u4e00\u6014\uff0c\u542c\u8fd9\u53e3\u97f3\u597d\u719f\uff0c\u51dd\u795e\u770b\u53bb\uff0c\u767b\u65f6\u8bb0\u8d77\uff0c\u6b64\u4eba\u4fbf\u662f\u4e07\u9707\u5c71\u7684\u4e8c\u5f1f\u5b50\u5468\u573b\uff0c\u76f8\u9694\u591a\u5e74\uff0c\u4ed6\u5728\u4e0a\u5507\u7559\u4e86\u4e00\u7247\u5c0f\u80e1\u5b50\uff0c\u517c\u4e4b\u8863\u9970\u534e\u4e3d\uff0c\u7adf\u7136\u4e0d\u8bc6\u5f97\u4ed6\u4e86\u3002\u72c4\u4e91\u8fd9\u51e0\u5e74\u6765\u60e8\u88ab\u9677\u5bb3\u7684\u60b2\u6124\uff0c\u970e\u65f6\u95f4\u6d8c\u5411\u5fc3\u5934\uff0c\u4e0d\u7531\u5f97\u6ee1\u8138\u6da8\u5f97\u901a\u7ea2\uff0c\u559d\u9053\uff1a\u201c\u6211\u9053\u662f\u8c01\uff0c\u539f\u6765\u662f\u5468\u2026\u2026\u5468\u2026\u2026\u5468\u4e8c\u54e5\uff01\u201d\u4ed6\u672c\u6b32\u76f4\u65a5\u5176\u540d\uff0c\u4f46\u7ec8\u4e8e\u5728\u201c\u5468\u201d\u5b57\u4e4b\u4e0b\uff0c\u52a0\u4e0a\u4e86\u201c\u4e8c\u54e5\u201d\u4e24\u5b57\u3002 \u3000\u3000\u4e01\u5178\u731c\u5230\u4e86\u4ed6\u7684\u5fc3\u60c5\uff0c\u559d\u9053\uff1a\u201c\u597d\uff01\u201d\u8f6c\u773c\u95f4\u4fbf\u662f\u4e00\u51b3\u751f\u6b7b\u7684\u640f\u6597\uff0c\u72c4\u4e91\u80fd\u6291\u5236\u6124\u6012\uff0c\u53eb\u4ed6\u4e00\u58f0\u201c\u5468\u4e8c\u54e5\u201d\uff0c\u90a3\u4fbf\u4e0d\u662f\u70c2\u6253\u72c2\u62da\u7684\u4e00\u52c7\u4e4b\u592b\u4e86\uff0c\u968f\u5373\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u5468\u4e8c\u7237\uff0c\u60f3\u5fc5\u662f\u4e07\u8001\u7237\u5b50\u95e8\u4e0b\u7684\u9ad8\u5f1f\u3002\u5f88\u597d\uff0c\u5f88\u597d\uff0c\u4f60\u51e0\u65f6\u5230\u4e86\u51cc\u77e5\u5e9c\u624b\u4e0b\u5f53\u5dee\uff1f\u72c4\u5144\u5f1f\uff0c\u6211\u7ed9\u4f60\u5f15\u89c1\u5f15\u89c1\u3002\u8fd9\u4f4d\u662f\u2018\u4e07\u80dc\u5200\u2019\u95e8\u4e2d\u7684\u9a6c\u5927\u9e23\u9a6c\u7237\u3002\u90a3\u4f4d\u662f\u5c71\u897f\u592a\u884c\u95e8\u5916\u5bb6\u597d\u624b\uff0c\u2018\u53cc\u5200\u2019\u803f\u5929\u9738\u803f\u7237\u3002 \u3000\u3000\u636e\u8bf4\u4ed6\u4e00\u5bf9\u94c1\u638c\u950b\u5229\u5982\u5200\uff0c\u56e0\u6b64\u5916\u53f7\u2018\u53cc\u5200\u2019\uff0c\u5176\u5b9e\u4ed6\u662f\u4ece\u6765\u4e0d\u4f7f\u5175\u5203\u7684\u3002\u201d\u72c4\u4e91\u9053\uff1a\u201c\u8fd9\u4e24\u4f4d\u7684\u6b66\u529f\u7b97\u5f97\u600e\u6837\uff1f\u201d\u4e01\u5178\u9053\uff1a\u201c\u7b2c\u4e09\u6d41\u4e2d\u7684\u597d\u624b\u3002\u8981\u60f3\u6500\u5230\u7b2c\u4e8c\u6d41\uff0c\u5374\u662f\u7ec8\u751f\u65e0\u671b\u3002\u201d\u72c4\u4e91\u9053\uff1a\u201c\u4e3a\u751a\u4e48\uff1f\u201d\u4e01\u5178\u9053\uff1a\u201c\u4e0d\u662f\u90a3\u4e00\u5757\u6750\u6599\uff0c\u8d44\u8d28\u65e2\u5dee\uff0c\u53c8\u65e0\u540d\u5e08\u4f20\u6388\u3002\u201d \u3000\u3000\u4ed6\u4e8c\u4eba\u4e00\u95ee\u4e00\u7b54\uff0c\u5f53\u771f\u662f\u65c1\u82e5\u65e0\u4eba\u3002\u803f\u5929\u9738\u5f53\u4e0b\u4fbf\u5fcd\u8010\u4e0d\u4f4f\uff0c\u559d\u9053\uff1a\u201c\u76f4\u5a18\u8d3c\uff0c\u6b7b\u5230\u4e34\u5934\uff0c\u8fd8\u5728\u4e71\u56bc\u820c\u6839\u3002\u5403\u6211\u4e00\u5200\uff01\u201d \u3000\u3000\u4ed6\u6240\u8bf4\u7684\u201c\u4e00\u5200\u201d\uff0c\u5176\u5b9e\u4e43\u662f\u4e00\u638c\uff0c\u559d\u58f0\u672a\u505c\uff0c\u53f3\u638c\u5df2\u7136\u5288\u51fa\u3002 \u3000\u3000\u4e01\u5178\u4e2d\u6bd2\u540e\u4e00\u76f4\u96be\u4ee5\u8fd0\u6c14\u4f7f\u52b2\uff0c\u4e0d\u6562\u786c\u63a5\uff0c\u659c\u8eab\u907f\u8fc7\u3002\u803f\u5929\u9738\u53f3\u638c\u843d\u7a7a\uff0c\u5de6\u638c\u968f\u81f3\u3002\u4e01\u5178\u8bc6\u5f97\u8fd9\u662f\u201c\u53d8\u52bf\u638c\u201d\uff0c\u6025\u5fd9\u7ffb\u624b\u5316\u89e3\u3002\u53ef\u662f\u4e00\u638c\u4f38\u5c06\u51fa\u53bb\uff0c\u52b2\u529b\u52bf\u9053\u5168\u4e0d\u662f\u90a3\u56de\u4e8b\uff0c\u62cd\u7684\u4e00\u58f0\uff0c\u814b\u4e0b\u5df2\u88ab\u803f\u5929\u9738\u7684\u53f3\u638c\u6253\u5b9e\u3002\u4e01\u5178\u8eab\u5b50\u4e00\u6643\uff0c\u54c7\u7684\u4e00\u58f0\uff0c\u5410\u51fa\u4e86\u4e00\u53e3\u9c9c\u8840\u3002\u803f\u5929\u9738\u7b11\u9053\uff1a\u201c\u600e\u4e48\u6837\uff1f\u6211\u662f\u7b2c\u4e09\u6d41\uff0c\u4f60\u662f\u7b2c\u51e0\u6d41\uff1f\u201d \u3000\u3000\u4e01\u5178\u5438\u4e00\u53e3\u6c14\uff0c\u7a81\u89c9\u5185\u606f\u7545\u901a\uff0c\u539f\u6765\u90a3\u201c\u91d1\u6ce2\u65ec\u82b1\u201d\u7684\u5267\u6bd2\u6df1\u5165\u8840\u7ba1\uff0c\u4f7f\u8840\u6db2\u6e10\u6e10\u51dd\u7ed3\uff0c\u8d8a\u6d41\u8d8a\u6162\u3002\u4ed6\u9002\u624d\u5410\u51fa\u4e00\u5927\u53e3\u9c9c\u8840\uff0c\u6240\u53d7\u5185\u4f24\u867d\u662f\u4e0d\u8f7b\uff0c\u6bd2\u6027\u5374\u5df2\u6682\u65f6\u6d88\u51cf\u3002\u4ed6\u5fc3\u5934\u4e00\u559c\uff0c\u7acb\u65f6\u4e0a\u524d\u633a\u638c\u5411\u803f\u5929\u9738\u6309\u51fa\u3002\u803f\u5929\u9738\u4e3e\u638c\u6a2a\u6321\uff0c\u4e01\u5178\u5de6\u624b\u56de\u5708\uff0c\u62cd\u7684\u4e00\u58f0\uff0c\u91cd\u91cd\u6253\u4e86\u4ed6\u4e00\u4e2a\u5634\u5df4\uff0c\u8ddf\u7740\u53f3\u624b\u5708\u8f6c\uff0c\u53cd\u638c\u51fb\u5728\u4ed6\u5934\u9876\u3002\u803f\u5929\u9738\u5927\u53eb\u4e00\u58f0\u201c\u554a\u54df\uff01\u201d\u6025\u8dc3\u9000\u540e\u3002\u4e01\u5178\u53f3\u638c\u500f\u5730\u4f38\u51fa\uff0c\u51fb\u4e2d\u4e86\u4ed6\u80f8\u53e3\u3002\u803f\u5929\u9738\u53c8\u662f\u4e00\u58f0\u201c\u554a\u54df\uff01\u201d\u518d\u9000\u4e86\u4e24\u6b65\u3002 \u3000\u3000\u4e01\u5178\u8fd9\u4e09\u638c\u53ea\u987b\u6709\u795e\u7167\u529f\u76f8\u6d4e\uff0c\u4efb\u4f55\u4e00\u638c\u90fd\u80fd\u9001\u4e86\u5f53\u4eca\u4e00\u6d41\u9ad8\u624b\u7684\u6027\u547d\u3002\u803f\u5929\u9738\u53ea\u5916\u529f\u5389\u5bb3\uff0c\u5185\u529b\u5374\u5e76\u4e0d\u5982\u4f55\u4e86\u5f97\uff0c\u5c45\u7136\u8fde\u53d7\u4e09\u638c\u4ecd\u80fd\u633a\u7acb\u4e0d\u5012\u3002\u4e01\u5178\u81ea\u77e5\u6b7b\u671f\u5df2\u8fd1\uff0c\u867d\u7136\u751f\u6027\u8c41\u8fbe\uff0c\u4e14\u5df2\u51b3\u610f\u6b89\u60c5\uff0c\u4f46\u6b64\u523b\u4e00\u80a1\u65e0\u53ef\u5948\u4f55\u3001\u82f1\u96c4\u672b\u8def\u7684\u5fc3\u60c5\uff0c\u5374\u4e5f\u4ee4\u4ed6\u4e0d\u7981\u9eef\u7136\u795e\u4f24\u3002 \u3000\u3000\u7136\u800c\u803f\u5929\u9738\u8fde\u4e2d\u4e09\u638c\uff0c\u5927\u60ca\u5931\u8272\uff0c\u4f46\u89c9\u8138\u4e0a\u3001\u5934\u9876\u3001\u80f8\u53e3\u9690\u9690\u4f5c\u75db\uff0c\u5fc3\u60f3\u4e09\u5904\u90fd\u662f\u81f4\u547d\u7684\u8981\u5bb3\uff0c\u4e0d\u77e5\u4f24\u52bf\u5982\u4f55\uff0c\u4e0d\u7531\u5f97\u602f\u610f\u5927\u751f\u3002 \u3000\u3000\u9a6c\u5927\u9e23\u5411\u5468\u573b\u4f7f\u4e2a\u773c\u8272\uff0c\u9053\uff1a\u201c\u5468\u5144\u5f1f\uff0c\u5e76\u80a9\u5b50\u4e0a\uff01\u201d\u5468\u573b\u9053\uff1a\u201c\u662f\u554a\uff01\u201d\u4ed6\u81ea\u5fd6\u4e0d\u662f\u72c4\u4e91\u5bf9\u624b\uff0c\u4f46\u60f3\u81ea\u5df1\u624b\u4e2d\u6709\u5251\uff0c\u5bf9\u65b9\u5374\u662f\u8d64\u624b\u7a7a\u62f3\uff0c\u518d\u52a0\u53f3\u624b\u624b\u6307\u88ab\u524a\uff0c\u7435\u7436\u9aa8\u7a7f\u7834\uff0c\u5c31\u7b97\u4ed6\u529f\u592b\u518d\u5f3a\uff0c\u4e5f\u662f\u4f7f\u4e0d\u51fa\u7684\u4e86\uff0c\u5f53\u4e0b\u633a\u5251\u4fbf\u5411\u72c4\u4e91\u523a\u53bb\u3002 \u3000\u3000\u4e01\u5178\u77e5\u9053\u72c4\u4e91\u795e\u7167\u529f\u672a\u66fe\u7ec3\u6210\uff0c\u6b64\u523b\u6b66\u529f\u5c1a\u8fdc\u4e0d\u53ca\u5165\u72f1\u4e4b\u524d\uff0c\u8981\u7a7a\u624b\u5bf9\u6297\u5468\u573b\uff0c\u4e0d\u8fc7\u6789\u81ea\u9001\u4e86\u6027\u547d\uff0c\u5f53\u4e0b\u8eab\u5f62\u659c\u6643\uff0c\u5de6\u624b\u4fbf\u53bb\u593a\u5468\u573b\u957f\u5251\u3002\u8fd9\u4e00\u62db\u53bb\u52bf\u5947\u5feb\uff0c\u62db\u5f0f\u53c8\u5341\u5206\u7279\u5f02\uff0c\u5468\u573b\u5c1a\u672a\u5bdf\u89c9\uff0c\u4e01\u5178\u5de6\u624b\u4e09\u6839\u624b\u6307\u5df2\u642d\u4e0a\u4e86\u4ed6\u53f3\u624b\u8109\u95e8\u3002\u5468\u573b\u5927\u5403\u4e00\u60ca\uff0c\u53ea\u9053\u8fd9\u4e00\u56de\u5175\u5203\u975e\u8131\u624b\u4e0d\u53ef\uff0c\u90a3\u53ef\u6027\u547d\u4f11\u77e3\uff0c\u5c82\u77e5\u81ea\u5df1\u8109\u95e8\u4e0a\u7a74\u9053\u5c45\u7136\u5e76\u4e0d\u53d7\u5236\uff0c\u5f53\u5373\u987a\u624b\u4e00\u7529\uff0c\u957f\u5251\u56de\u8f6c\uff0c\u75be\u523a\u4e01\u5178\u5de6\u80f8\u3002\u4e01\u5178\u4fa7\u8eab\u907f\u8fc7\uff0c\u957f\u53f9\u4e00\u58f0\u3002 \u3000\u3000\u9a6c\u5927\u9e23\u89c1\u4e01\u5178\u548c\u803f\u5929\u9738\u3001\u5468\u573b\u52a8\u624b\uff0c\u4e24\u6b21\u90fd\u5df2\u7a33\u5360\u4e0a\u98ce\uff0c\u5374\u4e24\u6b21\u5747\u4e0d\u80fd\u53d6\u80dc\uff0c\u5fc3\u4e2d\u5fae\u4e00\u7422\u78e8\uff0c\u5df2\u77e5\u5176\u7406\uff1a\u201c\u51cc\u77e5\u5e9c\u8bf4\u4ed6\u8eab\u4e2d\u5267\u6bd2\uff0c\u60f3\u5fc5\u662f\u6bd2\u6027\u53d1\u4f5c\uff0c\u529f\u529b\u5927\u51cf\u3002\u201d\u803f\u5929\u9738\u89c1\u4e01\u5178\u593a\u5251\u529f\u8d25\u5782\u6210\uff0c\u4e5f\u77e5\u4ed6\u5185\u529b\u5df2\u4e0d\u8db3\u4ee5\u6d4e\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u59d3\u4e01\u7684\u62db\u6570\u5389\u5bb3\uff0c\u5374\u662f\u864e\u843d\u5e73\u9633\u2026\u2026\u5478\uff0c\u4ed6\u5988\u7684\uff01\u864e\u843d\u5e73\u9633\u88ab\u72ac\u6b3a\uff0c\u6211\u5c06\u8fd9\u8d3c\u56da\u72af\u6bd4\u4f5c\u8001\u864e\uff0c\u5c82\u4e0d\u662f\u5c06\u8001\u5b50\u6bd4\u4f5c\u72d7\u4e86\uff1f\u201d\u4e24\u4eba\u662f\u4e00\u822c\u7684\u5fc3\u601d\uff0c\u540c\u65f6\u5411\u4e01\u5178\u6251\u53bb\u3002 \u3000\u3000\u72c4\u4e91\u62a2\u4e0a\u6321\u67b6\u3002\u4e01\u5178\u5728\u4ed6\u80a9\u5934\u4e0a\u4e00\u63a8\uff0c\u559d\u9053\uff1a\u201c\u72c4\u5144\u5f1f\uff0c\u9000\u4e0b\u3002\u201d\u53f3\u624b\u63a2\u51fa\uff0c\u5df2\u6293\u4e2d\u4e86\u9a6c\u5927\u9e23\u5589\u5934\u3002\u8fd9\u4e00\u6293\u53ea\u987b\u6709\u5bfb\u5e38\u5185\u529f\uff0c\u624b\u6307\u6293\u5230\u4e86\u8fd9\u7b49\u8981\u7d27\u7684\u90e8\u4f4d\uff0c\u90a3\u4e5f\u975e\u8981\u4e86\u5bf9\u65b9\u7684\u6027\u547d\u4e0d\u53ef\u3002\u9a6c\u5927\u9e23\u5413\u5f97\u9b42\u98de\u5929\u5916\uff0c\u5c31\u5730\u6025\u6eda\uff0c\u9003\u4e86\u5f00\u53bb\u3002 \u3000\u3000\u4e01\u5178\u6697\u81ea\u53f9\u6c14\uff0c\u81ea\u5df1\u5185\u529b\u8d8a\u6765\u8d8a\u5f31\uff0c\u53ea\u662f\u4ed7\u7740\u62db\u6570\u9ad8\u51fa\u654c\u4eba\u751a\u591a\uff0c\u5c1a\u53ef\u652f\u6301\u7247\u523b\uff0c\u82e5\u8fd9\u201c\u201d\u4e0d\u8bf4\u4e0e\u72c4\u4e91\u77e5\u9053\uff0c\u4e00\u4ef6\u5927\u79d8\u5bc6\u4ece\u6b64\u6e6e\u6ca1\u65e0\u95fb\uff0c\u672a\u514d\u592a\u4e5f\u53ef\u60dc\uff0c\u8bf4\u9053\uff1a\u201c\u72c4\u5144\u5f1f\uff0c\u4f60\u542c\u6211\u7684\u8bdd\u3002\u4f60\u8eb2\u5728\u6211\u8eab\u540e\uff0c\u4e0d\u5fc5\u53bb\u7406\u4f1a\u654c\u4eba\uff0c\u53ea\u7ba1\u8bb0\u6211\u7684\u53e3\u8bc0\u3002\u8fd9\u4e8b\u975e\u540c\u5c0f\u53ef\uff0c\u54b1\u4eec\u8bf4\u751a\u4e48\u4e5f\u5f97\u529e\u6210\u529f\u4e86\u3002\u4f60\u4e01\u5927\u54e5\u843d\u5230\u4eca\u65e5\u8fd9\u6b65\u7530\u5730\uff0c\u4fbf\u662f\u4e3a\u6b64\u3002\u201d\u72c4\u4e91\u9053\uff1a\u201c\u662f\uff01\u201d\u7f29\u5230\u4e86\u4e01\u5178\u8eab\u540e\u3002\u4e01\u5178\u9053\uff1a\u201c\u7b2c\u4e94\u4e2a\u5b57\u662f\u2018\u5341\u516b\u2019\u2026\u2026\u201d \u3000\u3000\u9a6c\u5927\u9e23\u77e5\u9053\u51cc\u77e5\u5e9c\u4e0b\u4ee4\u5927\u641c\uff0c\u8ffd\u6355\u4e01\u5178\uff0c\u4e3b\u65e8\u662f\u5728\u8ffd\u67e5\u4e00\u5957\u6b66\u529f\u79d8\u5bc6\uff1b\u800c\u5468\u573b\u5230\u51cc\u9000\u601d\u624b\u4e0b\u5f53\u5dee\uff0c\u65e2\u975e\u4e3a\u540d\uff0c\u4ea6\u975e\u4e3a\u5229\uff0c\u4e43\u662f\u5949\u4e86\u5e08\u7236\u4e4b\u547d\uff0c\u6697\u4e2d\u67e5\u8bbf\u3002\u8fd9\u65f6\u4e24\u4eba\u542c\u5230\u4e86\u4e01\u5178\u8bf4\u51fa\u7b2c\u4e94\u4e2a\u5b57\u662f\u2018\u5341\u516b\u2019\u8fd9\u4e00\u53e5\u8bdd\uff0c\u90fd\u662f\u5fc3\u4e2d\u4e00\u51db\uff0c\u7262\u7262\u8bb0\u4f4f\u3002\u53ea\u542c\u4e01\u5178\u53c8\u9053\uff1a\u201c\u7b2c\u516d\u4e2a\u5b57\u662f\u2018\u4e03\u2019\u3002\u201d\u9a6c\u5927\u9e23\u3001\u5468\u573b\u3001\u548c\u72c4\u4e91\u4e09\u4eba\u53c8\u4e00\u9f50\u7528\u5fc3\u6697\u8bb0\u3002 \u3000\u3000\u803f\u5929\u9738\u5374\u53ea\u5949\u547d\u6765\u6349\u8981\u72af\uff0c\u4e0d\u77e5\u5176\u4f59\uff0c\u4f46\u89c1\u4e01\u5178\u53e3\u4e2d\u5ff5\u5ff5\u6709\u8f9e\uff0c\u751a\u4e48\u201c\u5341\u4e03\u3001\u5341\u516b\u201d\uff0c\u9a6c\u5927\u9e23\u548c\u5468\u573b\u4e24\u4eba\u4fbf\u5373\u5fc3\u4e0d\u5728\u7109\uff0c\u4e5f\u662f\u201c\u5341\u4e03\u3001\u5341\u516b\u201d\u7684\u5583\u5583\u81ea\u8bed\uff0c\u53ea\u9053\u4e01\u5178\u5728\u5ff5\u751a\u4e48\u8ff7\u4eba\u5fc3\u9b44\u7684\u5492\u8bed\uff0c\u5f53\u4e0b\u5927\u559d\uff1a\u201c\u5582\uff0c\u522b\u7740\u4e86\u4ed6\u9053\u513f\uff01\u201d\u6325\u638c\u5411\u4e01\u5178\u76f4\u5288\u8fc7\u53bb\uff0c\u53ea\u662f\u5fcc\u60ee\u5bf9\u624b\u4e86\u5f97\uff0c\u4e00\u638c\u51fb\u8fc7\uff0c\u4e0d\u6562\u518d\u65bd\u540e\u7740\uff0c\u7acb\u5373\u9000\u5f00\u3002 \u3000\u3000\u4e01\u5178\u4e00\u8ba9\uff0c\u811a\u4e0b\u7ad9\u7acb\u4e0d\u7a33\uff0c\u5411\u524d\u6251\u51fa\u3002\u9a6c\u5927\u9e23\u77a7\u51fa\u4fbf\u5b9c\uff0c\u6325\u5200\u780d\u5411\u4ed6\u5de6\u80a9\u3002\u4e01\u5178\u53ea\u89c9\u773c\u524d\u4e00\u9ed1\uff0c\u7adf\u4e0d\u77e5\u95ea\u907f\u3002\u72c4\u4e91\u5927\u60ca\uff0c\u5371\u6025\u4e2d\u65e0\u6cd5\u89e3\u6551\uff0c\u62a2\u5c06\u4e0a\u6765\uff0c\u4e00\u5934\u649e\u5165\u9a6c\u5927\u9e23\u6000\u4e2d\u3002 \u3000\u3000\u4e01\u5178\u4e00\u9635\u5934\u6655\u8fc7\u53bb\uff0c\u7741\u5f00\u773c\u6765\uff0c\u89c1\u72c4\u4e91\u548c\u9a6c\u5927\u9e23\u7ea0\u7f20\u5728\u4e00\u8d77\uff0c\u5468\u573b\u633a\u5251\u6b63\u8981\u5f80\u72c4\u4e91\u80cc\u5fc3\u4e0a\u523a\u53bb\uff0c\u5f53\u5373\u5de6\u624b\u6325\u51fa\uff0c\u4e24\u6839\u624b\u6307\u6233\u5411\u5468\u573b\u53cc\u773c\u3002\u4ed6\u81ea\u77e5\u529b\u6c14\u5fae\u5f31\u5df2\u6781\uff0c\u53ea\u6709\u653b\u5411\u8fd9\u7b49\u67d4\u8f6f\u7684\u90e8\u4f4d\uff0c\u65b9\u80fd\u6536\u9000\u654c\u4e4b\u529f\u3002 \u3000\u3000\u5468\u573b\u4e0d\u6687\u4f24\u4eba\uff0c\u75be\u5411\u5de6\u95ea\uff0c\u4fbf\u5728\u6b64\u65f6\uff0c\u9a6c\u5927\u9e23\u4e00\u5200\u67c4\u5df2\u51fb\u5728\u72c4\u4e91\u5934\u4e0a\uff0c\u5c06\u4ed6\u6253\u5012\u5728\u5730\u3002\u4e01\u5178\u53eb\u9053\uff1a\u201c\u72c4\u5144\u5f1f\uff0c\u8bb0\u4f4f\u7b2c\u4e03\u5b57\uff0c\u90a3\u662f\u2026\u2026\u201d\u53ea\u89c9\u80f8\u53e3\u6c14\u606f\u4e00\u7a92\uff0c\u803f\u5929\u9738\u4e00\u638c\u53c8\u5230\u3002 \u3000\u3000\u4e01\u5178\u6447\u4e86\u6447\u5934\uff0c\u773c\u524d\u767d\u5149\u8fde\u95ea\uff0c\u9a6c\u5927\u9e23\u548c\u5468\u573b\u540c\u65f6\u653b\u6765\uff0c\u4e01\u5178\u8eab\u5b50\u4e00\u6643\uff0c\u731b\u5411\u4e00\u5200\u4e00\u5251\u8fce\u4e86\u4e0a\u53bb\uff0c\u5657\u5657\u4e24\u58f0\uff0c\u5200\u5251\u540c\u65f6\u523a\u4e2d\u4ed6\u8eab\u5b50\u3002\u72c4\u4e91\u5927\u53eb\u4e00\u58f0\uff0c\u62a2\u4e0a\u6551\u63f4\u3002\u4e01\u5178\u4e58\u7740\u9c9c\u8840\u5916\u6d41\u3001\u6bd2\u6027\u7a0d\u5f31\u8fd9\u4e00\u77ac\u606f\uff0c\u8fd0\u52b2\u53cc\u638c\uff0c\u987a\u624b\u4e00\u638c\u6253\u5728\u9a6c\u5927\u9e23\u53f3\u988a\uff0c\u53cd\u624b\u4e00\u638c\u6253\u5411\u5468\u573b\u3002 \u3000\u3000\u8fd9\u4e00\u638c\u672c\u6765\u975e\u6253\u4e2d\u5468\u573b\u4e0d\u53ef\uff0c\u4e0d\u6599\u803f\u5929\u9738\u6070\u597d\u4e8e\u8fd9\u65f6\u6251\u5c06\u4e0a\u6765\uff0c\u51b2\u52bf\u6781\u731b\uff0c\u5580\u5587\u4e00\u58f0\u54cd\uff0c\u5c06\u80f8\u53e3\u649e\u5728\u4e01\u5178\u7684\u638c\u4e0a\uff0c\u808b\u9aa8\u5168\u65ad\uff0c\u5f53\u65f6\u4fbf\u6655\u6b7b\u8fc7\u53bb\u3002 \u3000\u3000\u4e01\u5178\u8fd9\u4e24\u638c\u4f7f\u5c3d\u4e86\u5168\u8eab\u5269\u4f59\u7684\u7cbe\u529b\u3002\u9a6c\u5927\u9e23\u5f53\u573a\u8eab\u6b7b\u3002\u803f\u5929\u9738\u6c14\u606f\u5944\u5944\uff0c\u4e5f\u5df2\u547d\u5728\u9877\u523b\u3002\u53ea\u6709\u5468\u573b\u5374\u6ca1\u53d7\u4f24\uff0c\u53f3\u624b\u6293\u4f4f\u5251\u67c4\uff0c\u8981\u4ece\u4e01\u5178\u8eab\u4e0a\u62d4\u51fa\u957f\u5251\uff0c\u518d\u6765\u56de\u523a\u72c4\u4e91\u3002\u4e01\u5178\u8eab\u5b50\u5411\u524d\u4e00\u633a\uff0c\u53cc\u624b\u7d27\u7d27\u62b1\u4f4f\u5468\u573b\u7684\u8170\uff0c\u53eb\u9053\uff1a\u201c\u72c4\u5144\u5f1f\uff0c\u5feb\u8d70\uff0c\u5feb\u8d70\uff01\u201d\u4ed6\u8eab\u5b50\u8fd9\u4e48\u4e00\u633a\uff0c\u957f\u5251\u53c8\u6df1\u5165\u4f53\u5185\u6570\u5bf8\u3002 \u3000\u3000\u72c4\u4e91\u5374\u54ea\u80af\u81ea\u884c\u9003\u751f\uff0c\u6251\u5411\u5468\u573b\u80cc\u5fc3\uff0c\u6260\u4f4f\u4ed6\u54bd\u5589\uff0c\u53eb\u9053\uff1a\u201c\u653e\u5f00\u4e01\u5927\u54e5\uff01\u201d\u4ed6\u53ef\u4e0d\u77e5\u5176\u5b9e\u662f\u4e01\u5178\u6293\u4f4f\u4e86\u5bf9\u624b\uff0c\u5374\u4e0d\u662f\u5468\u573b\u4e0d\u653e\u4ed6\u4e01\u5927\u54e5\u3002 \u3000\u3000\u4e01\u5178\u81ea\u89c9\u6c14\u529b\u6e10\u6e10\u8870\u7aed\uff0c\u5feb\u5c06\u62c9\u4e0d\u4f4f\u654c\u4eba\uff0c\u53ea\u8981\u7ed9\u4ed6\u4e00\u62d4\u51fa\u957f\u5251\uff0c\u6446\u8131\u4e86\u81ea\u5df1\u7684\u7ea0\u7f20\uff0c\u72c4\u4e91\u975e\u9001\u547d\u4e0d\u53ef\uff0c\u5927\u53eb\uff1a\u201c\u72c4\u5144\u5f1f\uff0c\u5feb\u8d70\uff0c\u4f60\u522b\u987e\u6211\uff0c\u6211\u2026\u2026\u6211\u603b\u662f\u4e0d\u6d3b\u7684\u4e86\uff01\u201d\u72c4\u4e91\u53eb\u9053\uff1a\u201c\u8981\u6b7b\uff0c\u5927\u5bb6\u6b7b\u5728\u4e00\u8d77\uff01\u201d\u4f7f\u52b2\u72e0\u6260\u5468\u573b\u7684\u5589\u5499\uff0c\u53ef\u662f\u4ed6\u7435\u7436\u9aa8\u88ab\u7a7f\u901a\u540e\uff0c\u80a9\u81c2\u4e0a\u7b4b\u9aa8\u808c\u8089\u5927\u53d7\u635f\u4f24\uff0c\u4e0d\u8bba\u5982\u4f55\u4f7f\u52b2\uff0c\u603b\u662f\u65e0\u6cd5\u4f7f\u654c\u4eba\u7a92\u606f\u3002 \u3000\u3000\u4e01\u5178\u98a4\u58f0\u9053\uff1a\u201c\u597d\u5144\u5f1f\uff0c\u4f60\u4e49\u6c14\u6df1\u91cd\u2026\u2026\u4e0d\u6789\u6211\u2026\u2026\u4ea4\u4e86\u4f60\u8fd9\u670b\u53cb\u2026\u2026\u90a3\u5251\u8bc0\u2026\u2026\u53ef\u60dc\u8bf4\u4e0d\u5168\u4e86\u2026\u2026\u6211\u2026\u2026\u6211\u5f88\u5feb\u6d3b\u2026\u2026\u6625\u6c34\u78a7\u6ce2\u2026\u2026\u90a3\u76c6\u7eff\u8272\u7684\u83ca\u82b1\u2026\u2026\u55ef\uff01\u5979\u653e\u5728\u7a97\u53e3\uff0c\u4f60\u77a7\u591a\u7f8e\u554a\u2026\u2026\u83ca\u82b1\u2026\u2026\u201d\u58f0\u97f3\u6e10\u6e10\u4f4e\u6c89\uff0c\u8138\u4e0a\u795e\u91c7\u7115\u53d1\uff0c\u6293\u7740\u5468\u573b\u7684\u53cc\u624b\u5374\u6162\u6162\u677e\u5f00\u4e86\u3002 \u3000\u3000\u5468\u573b\u4f7f\u529b\u4e00\u6323\uff0c\u5c06\u957f\u5251\u4ece\u4e01\u5178\u8eab\u4e0a\u62d4\u4e86\u51fa\u6765\uff0c\u5251\u5203\u4e0a\u5168\u662f\u9c9c\u8840\uff0c\u6025\u5fd9\u8f6c\u8eab\uff0c\u548c\u72c4\u4e91\u8138\u5bf9\u7740\u8138\uff0c\u76f8\u8ddd\u4e0d\u8fc7\u5c3a\u8bb8\uff0c\u4e00\u58f0\u72de\u7b11\uff0c\u624b\u4e0a\u4f7f\u52b2\uff0c\u633a\u5251\u4fbf\u5411\u72c4\u4e91\u80f8\u53e3\u731b\u523a\u8fc7\u53bb\u3002 \u3000\u3000\u72c4\u4e91\u5927\u53eb\uff1a\u201c\u4e01\u5927\u54e5\uff0c\u4e01\u5927\u54e5\uff01\u201d\u84e6\u7136\u95f4\u80f8\u53e3\u611f\u5230\u4e00\u9635\u5267\u75db\uff0c\u4e00\u5782\u773c\uff0c\u53ea\u89c1\u5468\u573b\u7684\u957f\u5251\u6b63\u523a\u5728\u81ea\u5df1\u80f8\u819b\u4e4b\u4e0a\uff0c\u8033\u4e2d\u4f46\u542c\u5f97\u4ed6\u5f97\u610f\u4e4b\u6781\u7684\u72de\u7b11\uff1a\u201c\u54c8\u54c8\uff0c\u54c8\u54c8\uff01\u201d \u3000\u3000\u5728\u8fd9\u4e00\u77ac\u4e4b\u95f4\uff0c\u72c4\u4e91\u8111\u6d77\u4e2d\u8f6c\u8fc7\u4e86\u65e0\u6570\u5f80\u4e8b\uff0c\u5728\u5e08\u7236\u5bb6\u4e2d\u5b66\u827a\uff0c\u4e0e\u621a\u5e08\u59b9\u4e24\u597d\u65e0\u95f4\uff0c\u5728\u4e07\u9707\u5c71\u5bb6\u4e2d\u82e6\u53d7\u51a4\u5c48\uff0c\u72f1\u4e2d\u4e94\u5e74\u7684\u51c4\u695a\u751f\u6daf\u2026\u2026\u79cd\u79cd\u4e8b\u7aef\uff0c\u4e00\u9f50\u6d8c\u5411\u5fc3\u5934\uff0c\u60b2\u6124\u5145\u585e\u80f8\u81c6\uff0c\u5927\u547c\uff1a\u201c\u6211\u2026\u2026\u6211\u2026\u2026\u548c\u4f60\u540c\u5f52\u4e8e\u5c3d\u3002\u201d\u4f38\u81c2\u62b1\u4f4f\u4e86\u5468\u573b\u80cc\u5fc3\u3002 \u3000\u3000\u4ed6\u7ec3\u795e\u7167\u529f\u867d\u672a\u6210\u529f\uff0c\u4f46\u4e5f\u5df2\u6709\u4e24\u5e74\u6839\u57fa\uff0c\u8fd9\u65f6\u81ea\u77e5\u6027\u547d\u5c06\u5c3d\uff0c\u5168\u8eab\u529b\u6c14\u90fd\u51dd\u805a\u4e8e\u53cc\u81c2\u4e4b\u4e0a\uff0c\u7d27\u7d27\u62b1\u4f4f\u654c\u4eba\uff0c\u6709\u5982\u4e00\u53cc\u94c1\u7b8d\u3002\u5468\u573b\u53ea\u611f\u547c\u5438\u6025\u4fc3\uff0c\u7528\u529b\u6323\u624e\uff0c\u5374\u65e0\u6cd5\u8131\u8eab\u3002 \u3000\u3000\u72c4\u4e91\u4f46\u89c9\u80f8\u53e3\u8d8a\u6765\u8d8a\u75db\uff0c\u6b64\u65f6\u66f4\u65e0\u601d\u7d22\u4f59\u6687\uff0c\u53cc\u81c2\u53ea\u662f\u7528\u529b\u6324\u538b\u5468\u573b\u3002\u662f\u4e0d\u662f\u60f3\u5c31\u6b64\u6324\u6b7b\u4e86\u654c\u4eba\uff0c\u5fc3\u4e2d\u4e5f\u6ca1\u8fd9\u4e2a\u5ff5\u5934\uff0c\u5c31\u662f\u8bf4\u751a\u4e48\u4e5f\u4e0d\u653e\u677e\u624b\u81c2\u3002\u4f46\u957f\u5251\u7adf\u4e0d\u518d\u523a\u8fdb\uff0c\u4f3c\u4e4e\u9047\u4e0a\u4e86\u751a\u4e48\u7a7f\u4e0d\u900f\u7684\u963b\u529b\uff0c\u5251\u8eab\u7adf\u5c14\u6e10\u6210\u5f27\u5f62\uff0c\u6162\u6162\u5f2f\u66f2\u3002\u5468\u573b\u53c8\u60ca\u53c8\u5947\uff0c\u53f3\u81c2\u4f7f\u52b2\u633a\u523a\uff0c\u8981\u5c06\u957f\u5251\u7a7f\u901a\u72c4\u4e91\u8eab\u5b50\uff0c\u53ef\u662f\u4fbf\u8981\u518d\u5411\u524d\u523a\u8fdb\u534a\u5bf8\uff0c\u4e5f\u5df2\u4e0d\u80fd\u3002 \u3000\u3000\u72c4\u4e91\u7ea2\u4e86\u53cc\u773c\uff0c\u51dd\u89c6\u7740\u5468\u573b\u7684\u8138\uff0c\u521d\u65f6\u89c1\u4ed6\u8138\u4e0a\u5c3d\u662f\u5f97\u610f\u548c\u6b8b\u5fcd\u4e4b\u8272\uff0c\u4f46\u6e10\u6e10\u7684\u53d8\u4e3a\u60ca\u8bb6\u548c\u8be7\u5f02\uff0c\u53c8\u8fc7\u4e00\u4f1a\uff0c\u8be7\u5f02\u4e4b\u4e2d\u6df7\u5165\u4e86\u6050\u60e7\uff0c\u5bb3\u6015\u7684\u795e\u8272\u8d8a\u6765\u8d8a\u5f3a\uff0c\u53d8\u6210\u4e86\u9707\u9a87\u83ab\u540d\u3002 \u3000\u3000\u5468\u573b\u7684\u957f\u5251\u660e\u660e\u65e9\u523a\u4e2d\u4e86\u72c4\u4e91\uff0c\u5374\u53ea\u4ee4\u4ed6\u76ae\u8089\u9677\u5165\u6570\u5bf8\uff0c\u96be\u4ee5\u7a7f\u7834\u808c\u80a4\u3002\u4ed6\u602f\u610f\u8d8a\u6765\u8d8a\u76db\uff0c\u53f3\u81c2\u5185\u52b2\u8fde\u50ac\u4e09\u6b21\u3002\u59cb\u7ec8\u4e0d\u80fd\u5c06\u5251\u5203\u523a\u5165\u654c\u8eab\uff0c\u60ca\u60e7\u4e4b\u4e0b\uff0c\u518d\u4e5f\u987e\u4e0d\u5f97\u4f24\u654c\uff0c\u53ea\u60f3\u8131\u8eab\u9003\u8d70\uff0c\u4f46\u88ab\u72c4\u4e91\u7262\u7262\u62b1\u4f4f\u4e86\uff0c\u59cb\u7ec8\u6446\u8131\u4e0d\u5f00\u3002 \u3000\u3000\u5468\u573b\u611f\u5230\u81ea\u5df1\u53f3\u81c2\u6162\u6162\u5185\u5f2f\uff0c\u8ddf\u7740\u957f\u5251\u7684\u5251\u67c4\u62b5\u5230\u4e86\u81ea\u5df1\u7684\u80f8\u53e3\uff0c\u5251\u5203\u8d8a\u6765\u8d8a\u5f2f\uff0c\u5f2f\u6210\u4e86\u4e2a\u534a\u5706\u3002\u84e6\u5730\u91cc\u62cd\u7684\u4e00\u58f0\u54cd\uff0c\u5251\u8eab\u6298\u65ad\u3002\u5468\u573b\u5927\u53eb\u4e00\u58f0\uff0c\u5411\u540e\u4fbf\u5012\u3002\u4e24\u622a\u950b\u5229\u7684\u65ad\u5251\uff0c\u4e00\u9f50\u523a\u5165\u4e86\u4ed6\u5c0f\u8179\u3002 \u3000\u3000\u5468\u573b\u4e00\u6454\u5012\uff0c\u72c4\u4e91\u88ab\u5e26\u7740\u8dcc\u4e0b\uff0c\u538b\u5728\u4ed6\u8eab\u4e0a\uff0c\u53cc\u81c2\u4ecd\u662f\u7262\u7262\u62b1\u4f4f\u4ed6\u4e0d\u653e\u3002\u72c4\u4e91\u95fb\u5230\u4e00\u9635\u6d53\u70c8\u7684\u8840\u8165\u6c14\uff0c\u89c1\u5468\u573b\u773c\u4e2d\u5ffd\u7136\u6d41\u4e0b\u6cea\u6765\uff0c\u8ddf\u7740\u53e3\u8fb9\u6d41\u51fa\u9c9c\u8840\uff0c\u5934\u4e00\u4fa7\uff0c\u4e00\u52a8\u4e5f\u4e0d\u52a8\u4e86\u3002 \u3000\u3000\u72c4\u4e91\u5927\u5947\uff0c\u8fd8\u6015\u4ed6\u662f\u8bc8\u6b7b\uff0c\u4e0d\u6562\u653e\u5f00\u53cc\u624b\uff0c\u8ddf\u7740\u89c9\u5f97\u81ea\u5df1\u80f8\u53e3\u7684\u75bc\u75db\u5df2\u6b62\uff0c\u53c8\u89c1\u5468\u573b\u53e3\u4e2d\u6d41\u8840\u4e0d\u6b62\uff0c\u4ed6\u8ff7\u8ff7\u60d8\u60d8\u7684\u677e\u5f00\u624b\uff0c\u7ad9\u8d77\u8eab\u6765\uff0c\u53ea\u89c1\u4e24\u622a\u65ad\u5251\u63d2\u5728\u5468\u573b\u8179\u4e2d\uff0c\u53ea\u6709\u5251\u67c4\u548c\u5251\u5c16\u9732\u51fa\u5728\u5916\u3002\u518d\u4f4e\u5934\u770b\u81ea\u5df1\u80f8\u53e3\u65f6\uff0c\u89c1\u5916\u886b\u7834\u4e86\u5bf8\u8bb8\u4e00\u9053\u53e3\u5b50\uff0c\u9732\u51fa\u9ed1\u8272\u7684\u5185\u8863\u3002 \u3000\u3000\u4ed6\u77a7\u77a7\u5468\u573b\u8eab\u4e0a\u7684\u4e24\u622a\u65ad\u5251\uff0c\u518d\u77a7\u77a7\u81ea\u5df1\u8863\u886b\u4e0a\u7684\u88c2\u53e3\uff0c\u7a81\u7136\u95f4\u7701\u609f\uff0c\u539f\u6765\uff0c\u662f\u8d34\u8eab\u7a7f\u7740\u7684\u4e4c\u8695\u8863\u6551\u4e86\u81ea\u5df1\u7684\u6027\u547d\uff0c\u66f4\u56e0\u6b64\u800c\u6740\u4e86\u4ec7\u4eba\u3002 \u3000\u3000\u72c4\u4e91\u60ca\u9b42\u7a0d\u5b9a\uff0c\u7acb\u5373\u8f6c\u8eab\uff0c\u5954\u5230\u4e01\u5178\u8eab\u65c1\uff0c\u53eb\u9053\uff1a\u201c\u4e01\u5927\u54e5\uff0c\u4e01\u5927\u54e5\u3002\u4f60\u2026\u2026\u4f60\u2026\u2026\u600e\u4e48\u6837\uff1f\u201d\u4e01\u5178\u6162\u6162\u7741\u5f00\u773c\u6765\uff0c\u5411\u4ed6\u77a7\u7740\uff0c\u53ea\u662f\u773c\u8272\u4e2d\u6ca1\u534a\u5206\u795e\u6c14\uff0c\u4f3c\u4e4e\u89c6\u800c\u4e0d\u89c1\uff0c\u6216\u8005\u4e0d\u8ba4\u5f97\u4ed6\u662f\u8c01\u3002\u72c4\u4e91\u53eb\u9053\uff1a\u201c\u4e01\u5927\u54e5\uff0c\u6211\u2026\u2026\u6211\u8bf4\u751a\u4e48\u4e5f\u8981\u6551\u4f60\u51fa\u53bb\u3002\u201d\u4e01\u5178\u7f13\u7f13\u7684\u9053\uff1a\u201c\u53ef\u60dc\u2026\u2026\u53ef\u60dc\u90a3\u5251\u8bc0\uff0c\u4ece\u6b64\u2026\u2026\u4ece\u6b64\u5931\u4f20\u4e86\uff0c\u5408\u846c\u2026\u2026\u971c\u534e\u2026\u2026\u201d\u72c4\u4e91\u5927\u58f0\u9053\uff1a\u201c\u4f60\u653e\u5fc3\uff01\u6211\u8bb0\u5f97\u7684\u2026\u2026\u5b9a\u8981\u5c06\u4f60\u548c\u51cc\u5c0f\u59d0\u5408\u846c\uff0c\u5b8c\u4e86\u4f60\u4e8c\u4eba\u7684\u5fc3\u613f\u3002\u201d \u3000\u3000\u4e01\u5178\u6162\u6162\u5408\u4e0a\u4e86\u773c\u775b\uff0c\u547c\u5438\u8d8a\u6765\u8d8a\u5f31\uff0c\u4f46\u53e3\u5507\u5fae\u52a8\uff0c\u8fd8\u5728\u8bf4\u8bdd\u3002\u72c4\u4e91\u5c06\u8033\u6735\u51d1\u5230\u4ed6\u7684\u5507\u8fb9\uff0c\u4f9d\u7a00\u542c\u5230\u4ed6\u5728\u8bf4\uff1a\u201c\u90a3\u7b2c\u5341\u4e00\u4e2a\u5b57\u2026\u2026\u201d\u4f46\u968f\u5373\u6ca1\u6709\u58f0\u97f3\u4e86\u3002\u72c4\u4e91\u7684\u8033\u6735\u4e0a\u611f\u5230\u5df2\u65e0\u547c\u6c14\uff0c\u4f38\u624b\u5230\u4ed6\u80f8\u53e3\u4e00\u6478\uff0c\u53ea\u89c9\u4e00\u9897\u5fc3\u4e5f\u5df2\u505c\u6b62\u4e86\u8df3\u52a8\u3002 \u3000\u3000\u72c4\u4e91\u65e9\u5c31\u77e5\u9053\u4e01\u5178\u6027\u547d\u96be\u4fdd\uff0c\u4f46\u6b64\u523b\u624d\u771f\u6b63\u9886\u4f1a\u5230\u8fd9\u4f4d\u6570\u5e74\u6765\u60c5\u82e5\u9aa8\u8089\u7684\u4e49\u5144\u7ec8\u4e8e\u820d\u5df1\u800c\u53bb\u3002\u4ed6\u8dea\u5728\u4e01\u5178\u8eab\u65c1\uff0c\u62da\u547d\u5f80\u4ed6\u53e3\u4e2d\u5439\u6c14\uff0c\u5fc3\u4e2d\u4e0d\u4f4f\u7684\u8bb8\u613f\uff1a\u201c\u8001\u5929\u7237\uff0c\u8001\u5929\u7237\uff0c\u4f60\u8ba9\u4e01\u5927\u54e5\u518d\u6d3b\u8f6c\u6765\uff0c\u6211\u5b81\u53ef\u518d\u56de\u5230\u7262\u72f1\u4e4b\u4e2d\uff0c\u6c38\u8fdc\u4e0d\u518d\u51fa\u6765\u3002\u6211\u5b81\u53ef\u4e0d\u53bb\u62a5\u4ec7\uff0c\u5b81\u53ef\u4e00\u751f\u4e00\u4e16\u53d7\u4e07\u95e8\u5f1f\u5b50\u7684\u6b3a\u4fae\u6298\u8fb1\uff0c\u8001\u5929\u7237\uff0c\u4f60\u2026\u2026\u4f60\u5343\u4e07\u5f97\u8ba9\u4e01\u5927\u54e5\u6d3b\u8f6c\u6765\u2026\u2026\u201d \u3000\u3000\u7136\u800c\u4ed6\u62b1\u7740\u4e01\u5178\u8eab\u5b50\u7684\u53cc\u624b\uff0c\u5374\u89c9\u5230\u4e01\u5178\u7684\u808c\u80a4\u8d8a\u6765\u8d8a\u50f5\u786c\uff0c\u8d8a\u6765\u8d8a\u51b7\uff0c\u77e5\u9053\u81ea\u5df1\u8fd9\u8bb8\u591a\u8bb8\u613f\u90fd\u843d\u4e86\u7a7a\u3002\u9877\u523b\u4e4b\u95f4\uff0c\u611f\u5230\u4e86\u65e0\u6bd4\u7684\u5bc2\u5bde\uff0c\u65e0\u6bd4\u7684\u5b64\u5355\uff0c\u53ea\u89c9\u5f97\u5916\u8fb9\u8fd9\u81ea\u7531\u81ea\u5728\u7684\u4e16\u754c\uff0c\u6bd4\u90a3\u5c0f\u5c0f\u7684\u72f1\u5ba4\u662f\u66f4\u52a0\u53ef\u6015\uff0c\u4ee5\u540e\u7684\u65e5\u5b50\u66f4\u52a0\u96be\u8fc7\u3002\u4ed6\u5b81\u53ef\u548c\u4e01\u5178\u518d\u56de\u5230\u90a3\u72f1\u5ba4\u4e2d\u53bb\u3002 \u3000\u3000\u4ed6\u6a2a\u62b1\u7740\u4e01\u5178\u7684\u5c38\u8eab\uff0c\u7ad9\u4e86\u8d77\u6765\uff0c\u5ffd\u7136\u95f4\uff0c\u65e0\u7a77\u65e0\u5c3d\u7684\u75db\u82e6\u548c\u60b2\u4f24\u90fd\u88ad\u5411\u5fc3\u5934\u3002 \u3000\u3000\u4ed6\u653e\u58f0\u5927\u54ed\u3002\u6ca1\u6709\u4efb\u4f55\u987e\u5fcc\u7684\u53f7\u5555\u5927\u54ed\u3002\u5168\u6ca1\u60f3\u5230\u8fd9\u54ed\u58f0\u6216\u8bb8\u4f1a\u53ec\u6765\u8ffd\u5175\uff0c\u4e5f\u6ca1\u60f3\u5230\u4e00\u4e2a\u5927\u7537\u4eba\u8fd9\u822c\u54ed\u6ce3\u592a\u4e5f\u53ef\u7f9e\u3002 \u3000\u3000\u53ea\u662f\u5fc3\u4e2d\u6291\u5236\u4e0d\u4f4f\u7684\u60b2\u4f24\uff0c\u4fbf\u8fd9\u822c\u4e0d\u52a0\u6291\u5236\u7684\u5927\u54ed\u3002 \u3000\u3000\u5f53\u773c\u6cea\u6e10\u6e10\u5e72\u4e86\uff0c\u5927\u58f0\u7684\u53f7\u5555\u53d8\u4e3a\u4f4e\u4f4e\u7684\u62bd\u564e\u65f6\uff0c\u96be\u4ee5\u5fcd\u53d7\u7684\u60b2\u4f24\u5728\u5fc3\u4e2d\u4ecd\u662f\u4e00\u822c\u7684\u96be\u4ee5\u5fcd\u53d7\uff0c\u53ef\u662f\u5934\u8111\u6bd4\u8f83\u6e05\u695a\u4e9b\u4e86\uff0c\u5f00\u59cb\u5bfb\u601d\uff1a\u201c\u4e01\u5927\u54e5\u7684\u5c38\u8eab\u600e\u4e48\u529e\uff1f\u6211\u600e\u4e48\u5e26\u7740\u4ed6\u53bb\u548c\u51cc\u59d1\u5a18\u7684\u68fa\u6728\u846c\u5728\u4e00\u8d77\uff1f\u201d\u6b64\u65f6\u5fc3\u4e2d\u66f4\u65e0\u522b\u5ff5\uff0c\u8fd9\u4ef6\u4e8b\u662f\u4e16\u4e0a\u552f\u4e00\u7684\u5927\u4e8b\u3002 \u3000\u3000\u5ffd\u7136\u95f4\uff0c\u9a6c\u8e44\u58f0\u4ece\u8fdc\u5904\u54cd\u8d77\uff0c\u8d8a\u5954\u8d8a\u8fd1\uff0c\u4e00\u5171\u6709\u5341\u4f59\u5339\u4e4b\u591a\u3002\u53ea\u542c\u5f97\u6709\u4eba\u5728\u547c\u53eb\uff1a\u201c\u9a6c\u5927\u7237\u3001\u803f\u5927\u7237\u3001\u5468\u4e8c\u7237\uff0c\u89c1\u5230\u4e86\u9003\u72af\u6ca1\u6709\uff1f\u201d\u5341\u4f59\u5339\u9a6c\u5954\u5230\u5e9f\u56ed\u5916\uff0c\u4e00\u9f50\u6b62\u4f4f\u3002\u6709\u4eba\u53eb\u9053\uff1a\u201c\u8fdb\u53bb\u77a7\u77a7\uff01\u201d\u53c8\u6709\u4e00\u4eba\u9053\uff1a\u201c\u4e0d\u4f1a\u8eb2\u5728\u8fd9\u5730\u65b9\u7684\u3002\u201d\u5148\u4e00\u4eba\u9053\uff1a\u201c\u4f60\u600e\u77e5\u9053\uff1f\u201d\u62cd\u7684\u4e00\u58f0\u54cd\uff0c\u9774\u5b50\u7740\u5730\uff0c\u90a3\u4eba\u8df3\u4e0b\u4e86\u9a6c\u80cc\u3002 \u3000\u3000\u72c4\u4e91\u66f4\u4e0d\u591a\u60f3\uff0c\u62b1\u7740\u4e01\u5178\u7684\u5c38\u8eab\uff0c\u4ece\u5e9f\u56ed\u7684\u4fa7\u95e8\u4e2d\u5954\u4e86\u51fa\u53bb\uff0c\u521a\u4e00\u51fa\u4fa7\u95e8\uff0c\u4fbf\u542c\u5f97\u5e9f\u56ed\u4e2d\u51e0\u4e2a\u4eba\u5927\u58f0\u60ca\u547c\uff0c\u53d1\u73b0\u4e86\u9a6c\u5927\u9e23\u3001\u803f\u5929\u9738\u3001\u5468\u573b\u4e09\u4eba\u7684\u5c38\u8eab\u3002 \u3000\u3000\u72c4\u4e91\u5728\u6c5f\u9675\u57ce\u4e2d\u72c2\u5954\u3002\u4ed6\u77e5\u9053\u8fd9\u822c\u62b1\u7740\u4e01\u5178\u7684\u5c38\u8eab\uff0c\u65e2\u8dd1\u4e0d\u5feb\uff0c\u53c8\u968f\u65f6\u968f\u523b\u4f1a\u7ed9\u4eba\u53d1\u89c1\u3002\u4f46\u4ed6\u5b81\u53ef\u91cd\u884c\u88ab\u902e\u5165\u72f1\uff0c\u5b81\u53ef\u8eab\u53d7\u9177\u5211\uff0c\u5b81\u53ef\u7acb\u88ab\u5904\u51b3\uff0c\u5374\u51b3\u4e0d\u80af\u4e22\u5f03\u4e01\u5927\u54e5\u3002 \u3000\u3000\u5954\u51fa\u6570\u5341\u4e08\uff0c\u89c1\u5de6\u9996\u6709\u4e00\u6247\u5c0f\u95e8\u659c\u63a9\uff0c\u5f53\u5373\u51b2\u5165\uff0c\u53cd\u8db3\u5c06\u95e8\u8e22\u4e0a\u3002\u53ea\u89c1\u91cc\u9762\u662f\u4e00\u5ea7\u6781\u5927\u7684\u83dc\u56ed\uff0c\u79cd\u6ee1\u4e86\u6cb9\u83dc\u3001\u841d\u535c\u3001\u8304\u5b50\u3001\u4e1d\u74dc\u4e4b\u7c7b\u3002\u72c4\u4e91\u81ea\u5e7c\u52a1\u519c\uff0c\u548c\u8fd9\u4e9b\u74dc\u83dc\u9615\u9694\u4e86\u4e94\u5e74\uff0c\u6b64\u65f6\u4e4d\u7136\u91cd\u89c1\uff0c\u5fc3\u5934\u4e0d\u7981\u751f\u51fa\u4e00\u80a1\u6e29\u6696\u4eb2\u5207\u4e4b\u611f\u3002\u56db\u4e0b\u6253\u91cf\uff0c\u89c1\u4e1c\u5317\u89d2\u4e0a\u662f\u95f4\u67f4\u623f\uff0c\u4ece\u7a97\u4e2d\u53ef\u4ee5\u89c1\u5230\u677e\u67f4\u7a3b\u8349\u5806\u5f97\u6ee1\u6ee1\u7684\u3002\u4ed6\u4fef\u8eab\u62d4\u4e86\u51e0\u679a\u841d\u535c\uff0c\u62b1\u4e86\u4e01\u5178\u7684\u5c38\u8eab\uff0c\u51b2\u5165\u67f4\u623f\u3002 \u3000\u3000\u4fa7\u8033\u542c\u5f97\u56db\u4e0b\u5e76\u65e0\u4eba\u58f0\uff0c\u4e8e\u662f\u642c\u5f00\u67f4\u8349\uff0c\u5c06\u5c38\u8eab\u653e\u597d\uff0c\u8f7b\u8f7b\u7528\u7a3b\u8349\u76d6\u4e86\u3002\u5728\u4ed6\u5fc3\u4e2d\uff0c\u8fd8\u662f\u5b58\u7740\u6307\u671b\uff1a\u201c\u8bf4\u4e0d\u5b9a\uff0c\u4e01\u5927\u54e5\u4f1a\u7a81\u7136\u9192\u8f6c\u3002\u201d \u3000\u3000\u5265\u4e86\u841d\u535c\u76ae\uff0c\u5927\u5927\u54ac\u4e86\u4e00\u53e3\u3002\u751f\u841d\u535c\u751c\u7f8e\u800c\u8f9b\u8fa3\u7684\u6c41\u6db2\u6d41\u5165\u54bd\u5589\u3002\u4e94\u5e74\u591a\u6ca1\u5c1d\u5230\u4e86\uff0c\u60f3\u5230\u4e86\u6e56\u5357\u7684\u4e61\u4e0b\uff0c\u4e0d\u77e5\u6709\u591a\u5c11\u6b21\uff0c\u66fe\u548c\u621a\u5e08\u59b9\u4e00\u5171\u62d4\u4e86\u751f\u841d\u535c\uff0c\u5728\u7530\u91ce\u95f4\u6f2b\u6b65\u5265\u98df\u2026\u2026\u4ed6\u5403\u4e86\u4e00\u4e2a\u53c8\u4e00\u4e2a\uff0c\u773c\u7736\u53c8\u6709\u70b9\u6f6e\u6e7f\u4e86\uff0c\u84e6\u5730\u91cc\uff0c\u542c\u5230\u4e86\u4e00\u4e2a\u58f0\u97f3\u3002\u4ed6\u5168\u8eab\u5267\u70c8\u9707\u52a8\uff0c\u624b\u4e2d\u7684\u534a\u4e2a\u841d\u535c\u6389\u5728\u5730\u4e0b\u3002\u96ea\u767d\u7684\u841d\u535c\u4e0a\u6cbe\u6ee1\u6ce5\u6c99\u548c\u7a3b\u8349\u788e\u5c51\u3002 \u3000\u3000\u4ed6\u542c\u5230\u90a3\u6e05\u8106\u6e29\u67d4\u7684\u58f0\u97f3\u53eb\u9053\uff1a\u201c\u7a7a\u5fc3\u83dc\uff0c\u7a7a\u5fc3\u83dc\uff0c\u4f60\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u4ed6\u767b\u65f6\u4f7f\u60f3\u5927\u58f0\u7b54\u5e94\uff1a\u201c\u6211\u5728\u8fd9\u91cc\uff01\u201d\u4f46\u8fd9\u4e2a\u201c\u6211\u201d\u5b57\u53ea\u5410\u51fa\u4e00\u534a\uff0c\u4fbf\u5728\u5589\u5934\u54fd\u4f4f\u4e86\u3002\u4ed6\u4f38\u624b\u6309\u4f4f\u4e86\u5634\uff0c\u5168\u8eab\u7981\u4e0d\u4f4f\u7684\u7c0c\u7c0c\u98a4\u6296\u3002 \u3000\u3000\u56e0\u4e3a\u201c\u7a7a\u5fc3\u83dc\u201d\u662f\u4ed6\u7684\u5916\u53f7\uff0c\u4e16\u4e0a\u53ea\u6709\u4ed6\u548c\u621a\u82b3\u4e24\u4eba\u77e5\u9053\uff0c\u8fde\u5e08\u7236\u4e5f\u4e0d\u77e5\u3002\u621a\u82b3\u8bf4\u4ed6\u6ca1\u8111\u7b4b\uff0c\u8001\u5b9e\u5f97\u4e00\u70b9\u5fc3\u601d\u4e5f\u6ca1\u6709\uff0c\u9664\u4e86\u7ec3\u6b66\u4e4b\u5916\uff0c\u751a\u4e48\u4e8b\u60c5\u4e5f\u4e0d\u60f3\uff0c\u751a\u4e48\u4e8b\u60c5\u4e5f\u4e0d\u61c2\uff0c\u8bf4\u4ed6\u7684\u5fc3\u5c31\u50cf\u7a7a\u5fc3\u83dc\u4e00\u822c\uff0c\u662f\u7a7a\u7684\u3002 \u3000\u3000\u72c4\u4e91\u7b11\u7740\u4e5f\u4e0d\u8fa9\u767d\uff0c\u4ed6\u6b22\u559c\u5e08\u59b9\u8fd9\u822c\u201c\u7a7a\u5fc3\u83dc\uff0c\u7a7a\u5fc3\u83dc\u201d\u7684\u547c\u53eb\u81ea\u5df1\u3002\u6bcf\u6b21\u542c\u5230\u201c\u7a7a\u5fc3\u83dc\u201d\u8fd9\u540d\u5b57\uff0c\u5fc3\u4e2d\u603b\u662f\u611f\u5230\u8bf4\u4e0d\u51fa\u7684\u6e29\u67d4\u751c\u871c\u3002\u56e0\u4e3a\u5f53\u6709\u7b2c\u4e09\u4e2a\u4eba\u5728\u573a\u7684\u65f6\u5019\uff0c\u5e08\u59b9\u51b3\u4e0d\u8fd9\u6837\u53eb\u4ed6\u3002\u8981\u662f\u53eb\u5230\u4e86\u201c\u7a7a\u5fc3\u83dc\u201d\uff0c\u603b\u662f\u53ea\u6709\u4ed6\u548c\u5979\u4e24\u4eba\u5355\u72ec\u5728\u4e00\u8d77\u3002 \u3000\u3000\u5f53\u4ed6\u5355\u72ec\u548c\u5979\u5728\u4e00\u8d77\u65f6\u5019\uff0c\u5979\u9ad8\u5174\u4e5f\u597d\uff0c\u751f\u6c14\u4e5f\u597d\uff0c\u72c4\u4e91\u603b\u662f\u611f\u5230\u8bf4\u4e0d\u51fa\u7684\u6b22\u559c\u3002\u4ed6\u662f\u4e2a\u4e0d\u4f1a\u8bf4\u8bdd\u7684\u50bb\u5c0f\u5b50\uff0c\u6709\u65f6\u90a3\u50bb\u5934\u50bb\u8111\u7684\u795e\u6c14\u60f9\u5f97\u621a\u82b3\u5f88\u751f\u6c14\uff0c\u4f46\u51e0\u58f0\u201c\u7a7a\u5fc3\u83dc\uff0c\u7a7a\u5fc3\u83dc\u201d\u4e00\u53eb\uff0c\u5f80\u5f80\u4e24\u4e2a\u4eba\u90fd\u54a7\u5f00\u5634\u7b11\u4e86\u3002 \u3000\u3000\u8bb0\u5f97\u535c\u57a3\u5230\u5e08\u7236\u5bb6\u6765\u6295\u4e66\u90a3\u4e00\u6b21\uff0c\u5e08\u59b9\u70e7\u4e86\u83dc\u62db\u5f85\u5ba2\u4eba\uff0c\u6709\u9e21\u6709\u9c7c\uff0c\u6709\u841d\u535c\u8c46\u8150\uff0c\u4e5f\u6709\u4e00\u5927\u7897\u7a7a\u5fc3\u83dc\u3002\u90a3\u4e00\u665a\uff0c\u535c\u57a3\u548c\u5e08\u7236\u559d\u7740\u9152\uff0c\u8c08\u8bba\u7740\u4e24\u6e56\u6b66\u6797\u4e2d\u7684\u8fd1\u4e8b\uff0c\u4ed6\u6014\u6014\u7684\u542c\u7740\uff0c\u65e0\u610f\u4e2d\u548c\u621a\u82b3\u7684\u76ee\u5149\u76f8\u5bf9\uff0c\u53ea\u89c1\u5979\u631f\u4e86\u4e00\u7b77\u7a7a\u5fc3\u83dc\uff0c\u653e\u5728\u5634\u8fb9\uff0c\u5374\u4e0d\u9001\u5165\u5634\u91cc\u3002\u5979\u7528\u7ea2\u7ea2\u7684\u67d4\u8f6f\u7684\u5634\u5507\uff0c\u8f7b\u8f7b\u89e6\u7740\u90a3\u51e0\u6761\u7a7a\u5fc3\u83dc\uff0c\u773c\u5149\u4e2d\u6ee1\u662f\u7b11\u610f\u3002\u5979\u4e0d\u662f\u5728\u5403\u83dc\uff0c\u800c\u662f\u5728\u543b\u90a3\u51e0\u6761\u83dc\u3002 \u3000\u3000\u90a3\u65f6\u5019\uff0c\u72c4\u4e91\u53ea\u77e5\u9053\uff1a\u201c\u5e08\u59b9\u5728\u7b11\u6211\u662f\u7a7a\u5fc3\u83dc\u3002\u201d \u3000\u3000\u8fd9\u65f6\u5728\u8fd9\u67f4\u623f\u4e4b\u4e2d\uff0c\u8111\u6d77\u4e2d\u7075\u5149\u4e00\u95ea\uff0c\u5ffd\u7136\u95f4\u4f53\u4f1a\u5230\u4e86\u5979\u7ea2\u5507\u8f7b\u543b\u7684\u542b\u610f\u3002 \u3000\u3000\u73b0\u4e0b\u547c\u53eb\u7740\u201c\u7a7a\u5fc3\u83dc\u201d\u7684\uff0c\u660e\u660e\u662f\u5e08\u59b9\u621a\u82b3\u7684\u58f0\u97f3\uff0c\u90a3\u662f\u4e00\u70b9\u4e5f\u4e0d\u9519\u7684\uff0c\u51b3\u4e0d\u662f\u81ea\u5df1\u795e\u667a\u5931\u5e38\u800c\u8bef\u542c\u4e86\u3002 \u3000\u3000\u201c\u7a7a\u5fc3\u83dc\uff0c\u7a7a\u5fc3\u83dc\uff0c\u4f60\u5728\u54ea\u91cc\uff1f\u201d\u8fd9\u51e0\u58f0\u547c\u53eb\u4e4b\u4e2d\uff0c\u4e00\u822c\u7684\u5305\u542b\u7740\u6e29\u67d4\u4f53\u8d34\u65e0\u6570\uff0c\u8f7b\u601c\u871c\u7231\u65e0\u6570\u3002\u4e0d\uff0c\u8fd8\u4e0d\u6b62\u8fd9\u6837\uff0c\u4ece\u524d\u548c\u5979\u4e00\u8d77\u5728\u6545\u4e61\u7684\u65f6\u5019\uff0c\u5e08\u59b9\u7684\u547c\u53eb\u4e2d\u6709\u53cb\u5584\uff0c\u6709\u4eb2\u5207\uff0c\u6709\u5173\u6000\uff0c\u4f46\u4e5f\u6709\u4efb\u6027\uff0c\u6709\u607c\u6012\uff0c\u6709\u8d23\u5907\uff0c\u4eca\u65e5\u7684\u51e0\u58f0\u201c\u7a7a\u5fc3\u83dc\u201d\u4e2d\uff0c\u5374\u5168\u662f\u6df1\u5207\u7684\u7231\u601c\u3002\u201c\u5979\u77e5\u9053\u6211\u8fd9\u51e0\u5e74\u6765\u7684\u51a4\u6789\u82e6\u695a\uff0c\u5bf9\u6211\u66f4\u52a0\u597d\u4e86\uff0c\u662f\u4e0d\u662f\u5462\uff1f\u201d \u3000\u3000\u4ed6\u4e0d\u6562\u76f8\u4fe1\u81ea\u5df1\u7684\u8033\u6735\u3002\u201c\u6211\u662f\u5728\u505a\u68a6\uff0c\u5e08\u59b9\u600e\u4e48\u4f1a\u5230\u8fd9\u91cc\u6765\uff1f\u5979\u65e9\u5df2\u5ac1\u7ed9\u4e86\u4e07\u572d\uff0c\u53c8\u600e\u80fd\u518d\u6765\u627e\u6211\uff1f\u201d \u3000\u3000\u53ef\u662f\uff0c\u90a3\u58f0\u97f3\u53c8\u54cd\u4e86\uff0c\u8fd9\u4e00\u6b21\u66f4\u8fd1\u4e86\u4e00\u4e9b\uff1a\u201c\u7a7a\u5fc3\u83dc\uff0c\u4f60\u8eb2\u5728\u54ea\u91cc\uff1f\u4f60\u77a7\u6211\u6349\u4e0d\u6349\u5230\u4f60\uff1f\u201d\u58f0\u97f3\u4e2d\u662f\u90a3\u4e48\u591a\u7684\u559c\u6b22\u548c\u601c\u60dc\u3002 \u3000\u3000\u72c4\u4e91\u53ea\u89c9\u8eab\u4e0a\u6bcf\u4e00\u6839\u8840\u7ba1\u90fd\u5728\u80c0\u5927\uff0c\u5fcd\u4e0d\u4f4f\u6c14\u5598\u8d77\u6765\uff0c\u53cc\u624b\u624b\u5fc3\u4e2d\u90fd\u662f\u6c57\u6c34\uff0c\u6084\u6084\u7ad9\u8d77\u8eab\u6765\uff0c\u8eb2\u5728\u7a3b\u8349\u4e4b\u540e\uff0c\u4ece\u7a97\u683c\u4e2d\u5411\u5916\u671b\u53bb\uff0c\u53ea\u89c1\u4e00\u4e2a\u5973\u5b50\u7684\u80cc\u5f71\u5411\u7740\u81ea\u5df1\uff0c\u6b63\u5728\u627e\u4eba\u3002\u4e0d\u9519\uff0c\u524a\u524a\u7684\u80a9\u5934\uff0c\u7ec6\u7ec6\u7684\u8170\u8eab\uff0c\u9ad8\u800c\u5fae\u7626\u7684\u8eab\u6750\uff0c\u6b63\u662f\u5e08\u59b9\u3002 \u3000\u3000\u53ea\u542c\u5979\u7b11\u7740\u53eb\u9053\uff1a\u201c\u7a7a\u5fc3\u83dc\uff0c\u4f60\u8fd8\u4e0d\u51fa\u6765\uff1f\u201d \u3000\u3000\u7a81\u7136\u4e4b\u95f4\uff0c\u5979\u8f6c\u8fc7\u8eab\u6765\u3002 \u3000\u3000\u72c4\u4e91\u773c\u524d\u4e00\u82b1\uff0c\u8111\u4e2d\u611f\u5230\u4e00\u9635\u6655\u7729\uff0c\u773c\u524d\u8fd9\u5973\u5b50\u6b63\u662f\u621a\u82b3\u3002\u4e4c\u9ed1\u800c\u5149\u6e9c\u6e9c\u7684\u773c\u7403\uff0c\u5fae\u5fae\u4e0a\u7fd8\u7684\u9f3b\u5c16\uff0c\u8138\u8272\u767d\u4e86\u4e9b\uff0c\u4e0d\u50cf\u6e56\u5357\u4e61\u4e0b\u65f6\u90a3\u4e48\u7ea2\u6da6\uff0c\u7136\u800c\u786e\u662f\u5e08\u59b9\uff0c\u786e\u662f\u4ed6\u5728\u72f1\u5ba4\u4e2d\u8bb0\u6302\u4e86\u5343\u904d\u4e07\u904d\uff0c\u7231\u4e86\u5343\u904d\u4e07\u904d\uff0c\u53c8\u607c\u4e86\u5343\u904d\u4e07\u904d\u7684\u5e08\u59b9\u3002 \u3000\u3000\u5979\u8138\u4e0a\u4f46\u662f\u90a3\u4e48\u7b11\u563b\u563b\u5730\uff0c\u53eb\u7740\uff1a\u201c\u7a7a\u5fc3\u83dc\uff0c\u4f60\u8fd8\u4e0d\u51fa\u6765\uff1f\u201d \u3000\u3000\u542c\u5f97\u5979\u5982\u6b64\u6df1\u60c5\u6b3e\u6b3e\u7684\u547c\u53eb\u81ea\u5df1\uff0c\u5927\u559c\u82e5\u72c2\u4e4b\u4e0b\uff0c\u4fbf\u8981\u5e94\u58f0\u800c\u51fa\uff0c\u548c\u8fd9\u4e2a\u5fc3\u4e2d\u65e0\u65f6\u4e0d\u5728\u601d\u5ff5\u7684\u5e08\u59b9\u76f8\u89c1\uff0c\u4f46\u4ed6\u521a\u8de8\u51fa\u4e00\u6b65\uff0c\u731b\u5730\u60f3\u8d77\uff1a\u201c\u4e01\u5927\u54e5\u5e38\u8bf4\u6211\u592a\u8fc7\u5fe0\u539a\u8001\u5b9e\uff0c\u6781\u6613\u4e0a\u522b\u4eba\u7684\u5f53\u3002\u5e08\u59b9\u5df2\u5ac1\u4e86\u4e07\u5bb6\u7684\u513f\u5b50\uff0c\u4eca\u65e5\u5468\u573b\u6b7b\u5728\u6211\u7684\u624b\u4e0b\uff0c\u600e\u77e5\u9053\u5979\u4e0d\u662f\u6545\u610f\u9a97\u6211\u51fa\u53bb\uff1f\u201d\u60f3\u5230\u6b64\u5904\uff0c\u7acb\u5373\u505c\u6b65\u3002 \u3000\u3000\u53ea\u542c\u5f97\u621a\u82b3\u53c8\u53eb\u4e86\u51e0\u58f0\u201c\u7a7a\u5fc3\u83dc\uff0c\u7a7a\u5fc3\u83dc\uff01\u201d\u72c4\u4e91\u5fc3\u65cc\u6447\u6447\uff0c\u5bfb\u601d\uff1a\u201c\u5979\u8fd9\u4e48\u53eb\u6211\u3002\u60c5\u6df1\u610f\u771f\uff0c\u51b3\u7136\u4e0d\u5047\u3002\u518d\u8bf4\uff0c\u82e5\u662f\u5979\u8981\u6211\u6027\u547d\uff0c\u6211\u5c31\u6b7b\u5728\u5979\u624b\u4e0b\u4fbf\u4e86\u3002\u201d\u5fc3\u4e2d\u4e00\u9178\uff0c\u7a81\u7136\u95f4\u8d77\u4e86\u81ea\u66b4\u81ea\u5f03\u7684\u5ff5\u5934\uff0c\u7b2c\u4e8c\u6b21\u4e3e\u6b65\u53c8\u6b32\u51fa\u53bb\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u4e00\u4e2a\u5c0f\u5973\u5b69\u7684\u7b11\u58f0\uff0c\u6e05\u8106\u7684\u54cd\u4e86\u8d77\u6765\uff0c\u8ddf\u7740\u8bf4\u9053\uff1a\u201c\u5988\uff0c\u5988\uff0c\u6211\u5728\u8fd9\u513f\uff01\u201d \u3000\u3000\u72c4\u4e91\u5fc3\u5ff5\u4e00\u52a8\uff0c\u518d\u4ece\u7a97\u683c\u4e2d\u5411\u5916\u671b\u53bb\uff0c\u53ea\u89c1\u4e00\u4e2a\u8eab\u7a7f\u5927\u7ea2\u8863\u886b\u7684\u5973\u5b69\u4ece\u4e1c\u8fb9\u5feb\u6b65\u5954\u6765\u3002\u5979\u5e74\u7eaa\u592a\u5c0f\uff0c\u5954\u8dd1\u65f6\u8dcc\u8dcc\u649e\u649e\uff0c\u811a\u6b65\u4e0d\u7a33\u3002\u53ea\u542c\u621a\u82b3\u5e26\u7b11\u7684\u67d4\u548c\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u7a7a\u5fc3\u83dc\uff0c\u4f60\u8eb2\u5230\u54ea\u513f\u5566\uff1f\u5988\u5230\u5904\u627e\u4f60\u4e0d\u7740\u3002\u201d\u90a3\u5c0f\u5973\u5b69\u5f97\u610f\u7684\u9053\uff1a\u201c\u7a7a\u5fc3\u83dc\u5728\u82b1\u56ed\uff01\u7a7a\u5fc3\u83dc\u770b\u8682\u8681\uff01\u201d \u3000\u3000\u72c4\u4e91\u8033\u4e2d\u55e1\u7684\u4e00\u58f0\u54cd\uff0c\u5fc3\u53e3\u72b9\u5982\u88ab\u4eba\u731b\u529b\u6253\u4e86\u4e00\u62f3\u3002\u96be\u9053\u5e08\u59b9\u5df2\u751f\u4e86\u5973\u513f\uff1f\u96be\u9053\u5979\u5973\u513f\u5c31\u53eb\u505a\u201c\u7a7a\u5fc3\u83dc\u201d\uff1f\u5979\u53eb\u201c\u7a7a\u5fc3\u83dc\u201d\uff0c\u662f\u53eb\u5979\u5973\u513f\uff0c\u5e76\u4e0d\u662f\u53eb\u6211\uff1f\u96be\u9053\u81ea\u5df1\u8bef\u51b2\u8bef\u649e\uff0c\u53c8\u6765\u5230\u4e86\u4e07\u9707\u5c71\u5bb6\u91cc\uff1f \u3000\u3000\u8fd9\u51e0\u5e74\u6765\uff0c\u4ed6\u5fc3\u5e95\u9690\u9690\u5b58\u7740\u4e2a\u6307\u671b\uff0c\u603b\u76fc\u5ffd\u7136\u6709\u4e00\u5929\u4f1a\u53d1\u89c1\uff0c\u5e08\u59b9\u5176\u5b9e\u5e76\u6ca1\u5ac1\u7ed9\u4e07\u572d\uff0c\u6c88\u57ce\u90a3\u756a\u8bdd\u539f\u6765\u90fd\u662f\u6492\u8c0e\u3002\u4ed6\u8fd9\u4e2a\u5ff5\u5934\u4ece\u6765\u6ca1\u6562\u5bf9\u4e01\u5178\u8bf4\u8d77\uff0c\u53ea\u662f\u6df1\u6df1\u7684\u85cf\u5728\u5fc3\u5e95\uff0c\u6709\u65f6\u5348\u591c\u68a6\u56de\uff0c\u5ffd\u7136\u4f1a\u6b22\u559c\u5f97\u8df3\u4e86\u8d77\u6765\u3002\u53ef\u662f\u8fd9\u65f6\u5019\uff0c\u4ed6\u7ec8\u4e8e\u4eb2\u773c\u89c1\u5230\u3001\u4eb2\u8033\u542c\u5230\uff0c\u6709\u4e00\u4e2a\u5c0f\u5973\u5b69\u5728\u53eb\u5979\u201c\u5988\u5988\u201d\u3002 \u3000\u3000\u4ed6\u6cea\u6c34\u6d8c\u5230\u4e86\u773c\u4e2d\uff0c\u4ece\u67f4\u623f\u7684\u7a97\u683c\u4e2d\u6a21\u6a21\u7cca\u7cca\u7684\u77a7\u51fa\u53bb\uff0c\u53ea\u89c1\u621a\u82b3\u8e72\u5728\u5730\u4e0b\uff0c\u5f20\u5f00\u4e86\u53cc\u81c2\uff0c\u90a3\u5c0f\u5973\u5b69\u7b11\u7740\u6251\u5728\u5979\u6000\u91cc\u3002 \u3000\u3000\u621a\u82b3\u8fde\u8fde\u4eb2\u543b\u90a3\u5c0f\u5973\u5b69\u7684\u8138\u988a\uff0c\u67d4\u58f0\u7b11\u9053\uff1a\u201c\u7a7a\u5fc3\u83dc\u81ea\u5df1\u4f1a\u73a9\uff0c\u771f\u4e56\uff01\u201d \u3000\u3000\u72c4\u4e91\u53ea\u770b\u5230\u621a\u82b3\u7684\u4fa7\u9762\uff0c\u770b\u5230\u5979\u7ec6\u7ec6\u7684\u957f\u7709\uff0c\u5f2f\u5f2f\u7684\u5634\u89d2\uff0c\u8138\u86cb\u6bd4\u51e0\u5e74\u524d\u4e30\u6ee1\u4e86\u4e9b\uff0c\u66f4\u52a0\u7684\u767d\u5ae9\u548c\u8273\u4e3d\u3002\u4ed6\u5fc3\u4e2d\u53c8\u662f\u4e00\u9178\uff1a\u201c\u8fd9\u51e0\u5e74\u6765\u505a\u4e07\u5bb6\u7684\u5c11\u5976\u5976\uff0c\u4e0d\u7528\u5728\u7530\u91cc\u8015\u4f5c\uff0c\u4e0d\u7528\u53d7\u65e5\u6652\u96e8\u6dcb\uff0c\u8eab\u5b50\u81ea\u7136\u517b\u5f97\u597d\u4e86\u3002\u201d \u3000\u3000\u53ea\u542c\u621a\u82b3\u9053\uff1a\u201c\u7a7a\u5fc3\u83dc\u522b\u5728\u8fd9\u91cc\u73a9\uff0c\u8ddf\u5988\u5988\u56de\u623f\u53bb\u3002\u201d\u90a3\u5973\u5b69\u9053\uff1a\u201c\u8fd9\u91cc\u597d\u73a9\uff0c\u7a7a\u5fc3\u83dc\u8981\u770b\u8682\u8681\u3002\u201d\u621a\u82b3\u9053\uff1a\u201c\u4e0d\uff0c\u4eca\u5929\u5916\u9762\u6709\u574f\u4eba\uff0c\u8981\u6349\u5c0f\u5b69\u5b50\u3002\u7a7a\u5fc3\u83dc\u8fd8\u662f\u56de\u623f\u91cc\u53bb\u7f62\u3002\u201d\u90a3\u5973\u5b69\u9053\uff1a\u201c\u751a\u4e48\u574f\u4eba\uff1f\u6349\u5c0f\u5b69\u5b50\u505a\u751a\u4e48\uff1f\u201d\u621a\u82b3\u7ad9\u8d77\u8eab\u6765\uff0c\u62c9\u7740\u5973\u513f\u7684\u624b\uff0c\u9053\uff1a\u201c\u76d1\u7262\u91cc\u9003\u8d70\u4e86\u4e24\u4e2a\u5f88\u51f6\u5f88\u51f6\u7684\u574f\u4eba\u3002\u7238\u7238\u53bb\u6349\u574f\u4eba\u53bb\u5566\u3002\u574f\u4eba\u5230\u4e86\u8fd9\u91cc\uff0c\u5c31\u6349\u7a7a\u5fc3\u83dc\u53bb\u3002\u7a7a\u5fc3\u83dc\u542c\u5988\u5988\u7684\u8bdd\uff0c\u56de\u623f\u53bb\u73a9\u3002\u5988\u7ed9\u4f60\u505a\u4e2a\u5e03\u5a03\u5a03\uff0c\u597d\u4e0d\u597d\uff1f\u201d\u90a3\u5973\u5b69\u5374\u751a\u6267\u62d7\uff0c\u9053\uff1a\u201c\u4e0d\u8981\u5e03\u5a03\u5a03\u3002\u7a7a\u5fc3\u83dc\u5e2e\u7238\u7238\u6349\u574f\u4eba\u3002\u201d \u3000\u3000\u72c4\u4e91\u542c\u621a\u82b3\u53e3\u53e3\u58f0\u58f0\u79f0\u81ea\u5df1\u4e3a\u201c\u574f\u4eba\u201d\uff0c\u4e00\u9897\u5fc3\u8d8a\u6765\u8d8a\u6c89\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u4fbf\u5728\u8fd9\u65f6\uff0c\u83dc\u56ed\u5916\u8e44\u58f0\u5f97\u5f97\uff0c\u6709\u6570\u9a91\u9a6c\u5954\u8fc7\u3002\u621a\u82b3\u4ece\u8170\u95f4\u62bd\u51fa\u957f\u5251\uff0c\u62a2\u5230\u540e\u56ed\u95e8\u53e3\u3002 \u3000\u3000\u72c4\u4e91\u7ad9\u5728\u7a97\u8fb9\u4e0d\u6562\u7a0d\u52a8\uff0c\u751f\u6015\u53d1\u51fa\u4e9b\u5fae\u58f0\u54cd\uff0c\u4fbf\u60ca\u52a8\u4e86\u621a\u82b3\u3002\u4ed6\u65e0\u8bba\u5982\u4f55\u4e0d\u613f\u518d\u548c\u5e08\u59b9\u76f8\u89c1\uff0c\u80f8\u95f4\u7684\u60b2\u6124\u6e10\u6e10\u7684\u96be\u4ee5\u6291\u5236\uff0c\u81ea\u5df1\u6ca1\u505a\u8fc7\u534a\u70b9\u574f\u4e8b\uff0c\u65e0\u7aef\u7aef\u7684\u53d7\u4e86\u4e16\u95f4\u6700\u60e8\u9177\u7684\u82e6\u695a\uff0c\u5979\u7adf\u8bf4\u81ea\u5df1\u662f\u2014\u2014\u201c\u574f\u4eba\u201d\u3002 \u3000\u3000\u4ed6\u89c1\u5c0f\u5973\u5b69\u8d70\u8fd1\u4e86\u67f4\u623f\u95e8\u53e3\uff0c\u53ea\u76fc\u5979\u522b\u8fdb\u6765\uff0c\u53ef\u662f\u90a3\u5973\u5b69\u4e0d\u77e5\u5b58\u7740\u751a\u4e48\u5ff5\u5934\uff0c\u7adf\u7136\u8de8\u6b65\u4fbf\u8fdb\u4e86\u67f4\u623f\u3002\u72c4\u4e91\u5c06\u8138\u85cf\u5728\u7a3b\u8349\u5806\u540e\u9762\uff0c\u6697\u9053\uff1a\u201c\u51fa\u53bb\uff0c\u51fa\u53bb\uff01\u201d \u3000\u3000\u7a81\u7136\u4e4b\u95f4\uff0c\u5c0f\u5973\u5b69\u89c1\u5230\u4e86\u4ed6\uff0c\u89c1\u5230\u8fd9\u84ec\u5934\u6563\u53d1\u3001\u6ee1\u8138\u80e1\u5b50\u7684\u53ef\u6015\u6837\u5b50\uff0c\u60ca\u5f97\u5446\u4e86\uff0c\u7741\u7740\u5706\u5706\u7684\u5927\u773c\uff0c\u8981\u60f3\u54ed\u51fa\u58f0\u6765\uff0c\u5374\u53c8\u4e0d\u6562\u3002 \u3000\u3000\u72c4\u4e91\u77e5\u9053\u8981\u7cdf\uff0c\u53ea\u8981\u8fd9\u5973\u5b69\u4e00\u54ed\uff0c\u81ea\u5df1\u8e2a\u8ff9\u7acb\u65f6\u4fbf\u4f1a\u7ed9\u621a\u82b3\u53d1\u89c9\uff0c\u5f53\u5373\u62a2\u6b65\u800c\u4e0a\uff0c\u5de6\u624b\u5c06\u5979\u62b1\u8d77\uff0c\u53f3\u624b\u6309\u4f4f\u4e86\u5979\u7684\u5634\u5df4\u3002\u53ef\u662f\u7ec8\u4e8e\u6162\u4e86\u7247\u523b\uff0c\u5c0f\u5973\u5b69\u5df2\u7136\u201c\u554a\u201d\u7684\u4e00\u58f0\uff0c\u54ed\u4e86\u51fa\u6765\u3002\u53ea\u662f\u8fd9\u54ed\u58f0\u9661\u7136\u800c\u6b62\uff0c\u540e\u534a\u622a\u7ed9\u72c4\u4e91\u6309\u4f4f\u4e86\u3002 \u3000\u3000\u621a\u82b3\u773c\u89c2\u56ed\u5916\uff0c\u4e00\u9897\u5fc3\u59cb\u7ec8\u7cfb\u5728\u5973\u513f\u8eab\u4e0a\uff0c\u731b\u542c\u5f97\u5979\u51fa\u58f0\u6709\u5f02\uff0c\u4e00\u8f6c\u5934\uff0c\u5df2\u4e0d\u89c1\u4e86\u5979\u4eba\u5f71\uff0c\u8ddf\u7740\u542c\u5f97\u67f4\u623f\u4e2d\u7a3b\u8349\u7c0c\u7c0c\u54cd\u58f0\uff0c\u6025\u5fd9\u4e24\u4e2a\u7bad\u6b65\uff0c\u62a2\u5230\u67f4\u623f\u95e8\u53e3\uff0c\u53ea\u89c1\u4e00\u4e2a\u80e1\u5b50\u84ec\u677e\u3001\u6ee1\u8eab\u8840\u6c61\u7684\u6c49\u5b50\u62b1\u4f4f\u4e86\u5979\u5973\u513f\uff0c\u4e00\u53ea\u624b\u6309\u5728\u5979\u53e3\u4e0a\u3002\u621a\u82b3\u8fd9\u4e00\u60ca\u5f53\u771f\u662f\u9b42\u98de\u5929\u5916\uff0c\u957f\u5251\u633a\u51fa\uff0c\u4fbf\u5411\u72c4\u4e91\u8138\u4e0a\u523a\u53bb\uff0c\u559d\u9053\uff1a\u201c\u5feb\u653e\u4e0b\u5b69\u5b50\uff01\u201d \u3000\u3000\u72c4\u4e91\u5fc3\u4e2d\u4e00\u9178\uff0c\u81ea\u66b4\u81ea\u5f03\u7684\u5ff5\u5934\u53c8\u8d77\uff1a\u201c\u4f60\u8981\u6740\u6211\u3002\u8fd9\u4fbf\u6740\u7f62\uff01\u201d\u89c1\u5979\u957f\u5251\u523a\u5230\uff0c\u7adf\u662f\u4e0d\u95ea\u4e0d\u907f\u3002\u621a\u82b3\u4e00\u5446\uff0c\u751f\u6015\u4f24\u4e86\u5973\u513f\uff0c\u75be\u6536\u957f\u5251\uff0c\u53c8\u559d\uff1a\u201c\u653e\u4e0b\u6211\u5b69\u5b50\uff01\u201d \u3000\u3000\u72c4\u4e91\u542c\u5979\u53e3\u53e3\u58f0\u58f0\u53ea\u662f\u53eb\u81ea\u5df1\u653e\u4e0b\u5979\u5b69\u5b50\uff0c\u5168\u65e0\u534a\u5206\u6545\u65e7\u7684\u60c5\u8c0a\uff0c\u6012\u6c14\u5927\u76db\uff0c\u504f\u4e0d\u653e\u4e0b\u5979\u5b69\u5b50\u3002\u5de6\u624b\u987a\u624b\u5728\u67f4\u5806\u4e2d\u62bd\u4e86\u4e00\u6761\u6728\u67f4\uff0c\u5728\u5979\u957f\u5251\u4e0a\u4e00\u683c\uff0c\u5012\u9000\u4e86\u4e00\u6b65\u3002 \u3000\u3000\u621a\u82b3\u89c1\u8fd9\u51f6\u6076\u6c49\u5b50\u4ecd\u662f\u62b1\u7740\u5973\u513f\u4e0d\u653e\uff0c\u5fc3\u4e2d\u8d8a\u6765\u8d8a\u60ca\uff0c\u53cc\u819d\u5ffd\u611f\u9178\u8f6f\uff0c\u5438\u4e00\u53e3\u6c14\uff0c\u633a\u5251\u5411\u72c4\u4e91\u53f3\u80a9\u6025\u523a\u3002\u72c4\u4e91\u4fa7\u8eab\u8ba9\u8fc7\uff0c\u53f3\u624b\u4e2d\u7684\u6728\u67f4\u5f53\u4f5c\u5251\u4f7f\uff0c\u81ea\u5de6\u80a9\u5904\u659c\u5288\u5411\u4e0b\uff0c\u8ddf\u7740\u5411\u540e\u523a\u51fa\u3002\u621a\u82b3\u60ca\u566b\u4e00\u58f0\uff0c\u53ea\u89c9\u8fd9\u5251\u6cd5\u6781\u719f\u3002\u6b63\u662f\u5979\u7236\u4eb2\u6240\u4f20\u7684\u4e00\u62db\u201c\u54e5\u7fc1\u558a\u4e0a\u6765\u201d\uff0c\u5f53\u4e0b\u4e0d\u53ca\u601d\u7d22\uff0c\u4f4e\u5934\u8eb2\u8fc7\uff0c\u624b\u4e2d\u957f\u5251\u4fbf\u662f\u4e24\u62db\u201c\u864e\u8e22\u5954\u60ca\u98ce\uff0c\u8fde\u5c71\u82e5\u5e03\u9003\u201d\u3002 \u3000\u3000\u8fd9\u67f4\u623f\u672c\u5c31\u72ed\u9698\uff0c\u5806\u6ee1\u4e86\u67f4\u8349\u4e4b\u540e\uff0c\u4f59\u4e0b\u6765\u7684\u5730\u4f4d\u4e0d\u8fc7\u521a\u53ef\u591f\u4e24\u4eba\u5bb9\u8eab\u56de\u65cb\uff0c\u8fd9\u4e00\u62c6\u4e0a\u4e86\u62db\uff0c\u5904\u5904\u788d\u624b\u788d\u811a\u3002 \u3000\u3000\u72c4\u4e91\u81ea\u5e7c\u548c\u621a\u82b3\u540c\u5e08\u5b66\u827a\uff0c\u6ca1\u4e00\u65e5\u4e0d\u662f\u62c6\u62db\u7ec3\u5251\uff0c\u76f8\u4e92\u95f4\u7684\u5251\u62db\u90fd\u662f\u70c2\u719f\u4e8e\u80f8\uff0c\u8fd9\u65f6\u89c1\u5979\u4f7f\u51fa\u8fd9\u4e24\u62db\u5251\u6cd5\uff0c\u81ea\u7136\u800c\u7136\u4fbf\u4f9d\u5e08\u7236\u6240\u6388\u7684\u62db\u6570\u62c6\u4e86\u4e0b\u53bb\uff0c\u582a\u582a\u4f7f\u5230\u201c\u8001\u6ce5\u62db\u5927\u59d0\uff0c\u9a6c\u547d\u98ce\u5c0f\u5c0f\u201d\uff0c\u624b\u4e2d\u6728\u67f4\u5927\u5f00\u5927\u9616\uff0c\u53e3\u4e2d\u4e00\u58f0\u957f\u5578\uff0c\u6a2a\u524a\u4e09\u62db\u3002 \u3000\u3000\u5f53\u5e74\u5e08\u5144\u59b9\u7ec3\u5251\uff0c\u62c6\u5230\u6b64\u5904\u65f6\u621a\u82b3\u4fbf\u5df2\u62db\u67b6\u4e0d\u4f4f\u3002\u4f46\u8fd9\u65f6\u72c4\u4e91\u5c06\u6728\u67f4\u7b2c\u4e09\u6b21\u6a2a\u524a\u8fc7\u53bb\u65f6\uff0c\u5ffd\u7136\u95f4\u624b\u8155\u4e00\u9178\uff0c\u62cd\u7684\u4e00\u58f0\uff0c\u6728\u67f4\u7adf\u5c14\u6389\u5728\u5730\u4e0b\u3002\u4ed6\u4e00\u60ca\u4e4b\u4e0b\uff0c\u968f\u5373\u7701\u609f\uff1a\u201c\u6211\u53f3\u624b\u624b\u6307\u88ab\u524a\uff0c\u5df2\u7ec8\u8eab\u4e0d\u80fd\u4f7f\u5251\uff0c\u6211\u8fd9\u53ef\u5fd8\u4e86\u3002\u201d \u3000\u3000\u4e00\u62ac\u5934\u3002\u53ea\u89c1\u621a\u82b3\u624b\u4e2d\u7684\u957f\u5251\u5251\u5c16\u79bb\u81ea\u5df1\u80f8\u53e3\u4e0d\u53ca\u4e00\u5bf8\uff0c\u5251\u8eab\u98a4\u52a8\u4e0d\u5df2\uff0c\u5979\u8138\u4e0a\u60ca\u6115\u4e4b\u60c5\uff0c\u5b9e\u662f\u96be\u4ee5\u5f62\u5bb9\u3002 \u3000\u3000\u4e24\u4eba\u6014\u6014\u7684\u4f60\u671b\u7740\u6211\uff0c\u6211\u671b\u7740\u4f60\uff0c\u867d\u90fd\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002\u9694\u4e86\u597d\u534a\u664c\uff0c\u621a\u82b3\u624d\u9053\uff1a\u201c\u662f\u2026\u2026\u662f\u4f60\u4e48\uff1f\u201d\u5589\u97f3\u5e72\u6da9\uff0c\u5636\u54d1\u51e0\u4e0d\u6210\u58f0\u3002 \u3000\u3000\u72c4\u4e91\u70b9\u4e86\u70b9\u5934\uff0c\u5c06\u5de6\u81c2\u4e2d\u62b1\u7740\u7684\u5c0f\u5973\u5b69\u9012\u4e86\u8fc7\u53bb\u3002\u621a\u82b3\u629b\u4e0b\u957f\u5251\uff0c\u5fd9\u5c06\u5973\u513f\u63a5\u8fc7\uff0c\u4e0d\u77e5\u8bf4\u751a\u4e48\u624d\u597d\u3002\u90a3\u5973\u5b69\u5df2\u5413\u5f97\u8fde\u54ed\u4e5f\u54ed\u4e0d\u51fa\u6765\uff0c\u5c06\u5c0f\u8138\u86cb\u85cf\u5728\u6bcd\u4eb2\u6000\u91cc\uff0c\u518d\u4e5f\u4e0d\u6562\u5411\u72c4\u4e91\u591a\u77a7\u4e00\u773c\u3002\u621a\u82b3\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\u4e0d\u77e5\u9053\u662f\u4f60\u3002\u8fd9\u8bb8\u591a\u5e74\u6765\u2026\u2026\u201d \u3000\u3000\u5ffd\u7136\u5916\u9762\u4e00\u4e2a\u7537\u5b50\u7684\u58f0\u97f3\u53eb\u9053\uff1a\u201c\u82b3\u59b9\uff0c\u82b3\u59b9\uff01\u4f60\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u6b63\u662f\u4e07\u572d\uff0c\u547c\u58f0\u8d8a\u6765\u8d8a\u8fd1\uff0c\u6b63\u5bfb\u5411\u83dc\u56ed\u4e2d\u6765\u3002\u621a\u82b3\u8138\u4e0a\u9661\u7136\u53d8\u8272\uff0c\u4f4e\u58f0\u5728\u5973\u513f\u8033\u8fb9\u8bf4\uff1a\u201c\u7a7a\u5fc3\u83dc\uff0c\u8fd9\u4f2f\u4f2f\u4e0d\u662f\u574f\u4eba\uff0c\u4f60\u522b\u8ddf\u7239\u7239\u8bf4\u3002\u77e5\u9053\u4e48\uff1f\u201d\u5c0f\u5973\u5b69\u62ac\u8d77\u5934\u6765\uff0c\u5411\u72c4\u4e91\u77a7\u4e86\u4e00\u77a7\uff0c\u89c1\u5230\u4ed6\u8fd9\u526f\u53ef\u6016\u7684\u795e\u60c5\u6a21\u6837\uff0c\u7a81\u7136\u54c7\u7684\u4e00\u58f0\uff0c\u5927\u54ed\u8d77\u6765\u3002 \u3000\u3000\u5916\u9762\u90a3\u7537\u5b50\u542c\u5230\u4e86\u5973\u5b69\u7684\u54ed\u58f0\uff0c\u5faa\u58f0\u800c\u81f3\uff0c\u53eb\u9053\uff1a\u201c\u7a7a\u5fc3\u83dc\uff0c\u522b\u54ed\u3002\u7239\u7239\u5728\u8fd9\u513f\uff01\u201d \u3000\u3000\u621a\u82b3\u5411\u72c4\u4e91\u671b\u4e86\u4e00\u773c\uff0c\u8f6c\u8eab\u4fbf\u51fa\uff0c\u53cd\u624b\u5e26\u4e0a\u67f4\u95e8\uff0c\u62b1\u7740\u5973\u513f\uff0c\u5411\u4e08\u592b\u8fce\u4e86\u4e0a\u53bb\u3002 \u3000\u3000\u72c4\u4e91\u5446\u5446\u7684\u7ad9\u7740\uff0c\u4f3c\u4e4e\u6709\u4e2a\u58f0\u97f3\u4e0d\u4f4f\u7684\u5728\u8033\u8fb9\u54cd\u7740\uff1a\u201c\u6211\u8fd8\u662f\u6b7b\u4e86\u7684\u597d\uff0c\u6211\u8fd8\u662f\u6b7b\u4e86\u7684\u597d\uff01\u201d\u53ea\u542c\u90a3\u7537\u5b50\u58f0\u97f3\u7b11\u95ee\uff1a \u3000\u3000\u201c\u7a7a\u5fc3\u83dc\u4e3a\u751a\u4e48\u54ed\uff1f\u201d\u72c4\u4e91\u5f88\u60f3\u5230\u7a97\u53e3\u53bb\u77a7\u77a7\uff0c\u4e07\u572d\u8fd9\u65f6\u5019\u662f\u600e\u4e48\u4e00\u526f\u6a21\u6837\uff0c\u53ef\u662f\u4e00\u53cc\u811a\u4fbf\u5982\u662f\u5728\u5730\u4e0b\u9489\u4f4f\u4e86\uff0c\u518d\u4e5f\u79fb\u52a8\u4e0d\u5f97\u3002 \u3000\u3000\u542c\u5f97\u621a\u82b3\u7b11\u9053\uff1a\u201c\u6211\u548c\u7a7a\u5fc3\u83dc\u5728\u540e\u95e8\u53e3\u73a9\uff0c\u4e24\u9a91\u9a6c\u5954\u8fc7\uff0c\u9a6c\u4e0a\u7684\u4eba\u62ff\u4e86\u5175\u5203\uff0c\u957f\u76f8\u633a\u51f6\u7684\u3002\u7a7a\u5fc3\u83dc\u8bf4\u662f\u574f\u4eba\uff0c\u8981\u6349\u4e86\u5979\u53bb\uff0c\u5413\u5f97\u5927\u54ed\u3002\u201d\u4e07\u572d\u7b11\u9053\uff1a\u201c\u90a3\u662f\u5e9c\u8859\u95e8\u91cc\u8ffd\u62ff\u9003\u72af\u3002\u6765\uff0c\u7239\u7239\u62b1\u7a7a\u5fc3\u83dc\u3002\u7239\u7239\u6253\u6b7b\u574f\u4eba\u3002\u7a7a\u5fc3\u83dc\u4e0d\u6015\u574f\u4eba\u3002\u7239\u7239\u628a\u574f\u4eba\u4e00\u4e2a\u4e2a\u90fd\u6253\u6b7b\u4e86\u3002\u201d \u3000\u3000\u72c4\u4e91\u5fc3\u4e2d\u4e00\u51c9\uff1a\u201c\u5973\u4eba\u6492\u8c0e\u7684\u672c\u9886\u771f\u4e0d\u5c0f\uff0c\u8fd9\u4e48\u4e00\u8bf4\uff0c\u90a3\u5973\u5b69\u5c31\u7b97\u8bf4\u89c1\u5230\u4e86\u574f\u4eba\uff0c\u5979\u4e08\u592b\u4e5f\u4e0d\u4f1a\u8d77\u7591\u3002\u54fc\uff0c\u6211\u4e3a\u751a\u4e48\u8981\u4f60\u5305\u7792\uff1f\u4f60\u4eec\u53ea\u7ba1\u6765\u6349\u6211\u53bb\uff0c\u6253\u6b7b\u6211\u597d\u4e86\u3002\u201d \u3000\u3000\u4e24\u6b65\u62a2\u5230\u7a97\u8fb9\uff0c\u5411\u5916\u671b\u53bb\uff0c\u53ea\u89c1\u4e07\u572d\u8863\u9970\u534e\u4e3d\uff0c\u62b1\u7740\u90a3\u5973\u5b69\u6b63\u5411\u5185\u8d70\uff0c\u621a\u82b3\u501a\u504e\u5728\u4ed6\u8eab\u65c1\uff0c\u5e76\u80a9\u800c\u884c\uff0c\u795e\u6001\u6781\u662f\u4eb2\u70ed\u3002 \u3000\u3000\u5e08\u59b9\u5df2\u5ac1\u4e86\u4e07\u572d\uff0c\u8fd9\u4ef6\u4e8b\u4ee5\u5f80\u72c4\u4e91\u867d\u66fe\u51e0\u5343\u51e0\u4e07\u6b21\u7684\u60f3\u8fc7\uff0c\u4f46\u603b\u76fc\u662f\u5047\u7684\uff0c\u6b64\u523b\u6d3b\u751f\u751f\u7684\u60c5\u666f\u7ec8\u4e8e\u51fa\u73b0\u5728\u773c\u524d\u4e86\u3002\u4ed6\u5f20\u53e3\u5927\u53eb\uff1a\u201c\u6211\u2026\u2026\u201d\u4fef\u8eab\u4fbf\u60f3\u53bb\u62fe\u621a\u82b3\u629b\u5728\u5730\u4e0b\u7684\u957f\u5251\uff0c\u51b2\u51fa\u53bb\u548c\u4e07\u572d\u62da\u547d\u3002\u81ea\u5df1\u8eab\u5165\u7262\u72f1\uff0c\u53d7\u4e86\u8fd9\u8bb8\u8bb8\u591a\u591a\u82e6\u695a\uff0c\u90fd\u662f\u51fa\u4e8e\u773c\u524d\u8fd9\u4eba\u7684\u9677\u5bb3\uff0c\u800c\u81ea\u5df1\u7231\u903e\u6027\u547d\u7684\u60c5\u4fa3\uff0c\u5374\u6210\u4e86\u8fd9\u4eba\u7684\u59bb\u5ba4\u3002\u8fd9\u65f6\u5019\u5fc3\u4e2d\u66f4\u65e0\u522b\u5ff5\uff0c\u4e0d\u662f\u53bb\u6740\u4e86\u8fd9\u4eba\uff0c\u4fbf\u662f\u6b7b\u5728\u4ed6\u7684\u624b\u4e0b\u3002 \u3000\u3000\u4f46\u5c31\u8fd9\u4e48\u4e00\u4fef\u8eab\uff0c\u770b\u5230\u4e86\u67f4\u8349\u4e2d\u4e01\u5178\u7684\u5c38\u8eab\uff0c\u89c1\u5230\u4e01\u5178\u53cc\u773c\u95ed\u4e0a\uff0c\u8138\u4e0a\u795e\u8272\u5b89\u8be6\uff0c\u84e6\u5730\u60f3\u8d77\uff1a\u201c\u4e01\u5927\u54e5\u4e34\u6b7b\u65f6\u8c06\u8c06\u53ee\u5631\uff0c\u6c42\u6211\u5c06\u4ed6\u4e0e\u51cc\u5c0f\u59d0\u5408\u846c\u3002\u6211\u8fd9\u65f6\u51fa\u53bb\u548c\u4e07\u572d\u8fd9\u8d3c\u5b50\u76f8\u62da\uff0c\u9001\u4e86\u6027\u547d\u534a\u70b9\u4e5f\u4e0d\u6253\u7d27\uff0c\u4e01\u5927\u54e5\u7684\u5fc3\u613f\u5374\u5b8c\u6210\u4e0d\u4e86\u5566\u3002\u201d\u8f6c\u5ff5\u53c8\u60f3\uff1a\u201c\u6211\u6c42\u5e08\u59b9\u6210\u5168\u6b64\u4e8b\uff0c\u53ea\u6015\u4e5f\u80fd\u529e\u5230\u2026\u2026\u5478\uff0c\u5478\uff01\u72c4\u4e91\u4f60\u8fd9\u5c0f\u5b50\uff0c\u4f60\u81ea\u5df1\u4e5f\u4e0d\u80af\u627f\u62c5\u7684\u4e8b\uff0c\u5982\u4f55\u53bb\u8f6c\u6258\u522b\u4eba\uff1f\u4f60\u6b7b\u5728\u5730\u4e0b\uff0c\u6709\u4f55\u8138\u9762\u548c\u4e01\u5927\u54e5\u76f8\u89c1\uff1f\u5e08\u59b9\u8fd9\u7b49\u6ca1\u826f\u5fc3\uff0c\u5c82\u80af\u4e3a\u4f60\u529e\u751a\u4e48\u5927\u4e8b\uff1f\u201d\u4e00\u60f3\u901a\u4e86\u8fd9\u4e00\u8282\uff0c\u7ec8\u4e8e\u6162\u6162\u6291\u5236\u4e86\u6124\u6fc0\u4e4b\u5fc3\u3002 \u3000\u3000\u4f46\u4ed6\u8fd9\u4e00\u58f0\u201c\u6211\u201d\u5b57\uff0c\u5df2\u60ca\u52a8\u4e86\u4e07\u572d\uff0c\u53ea\u542c\u4ed6\u9053\uff1a\u201c\u597d\u50cf\u67f4\u623f\u91cc\u6709\u4eba\u3002\u201d\u621a\u82b3\u7b11\u9053\uff1a\u201c\u662f\u5417\uff1f\u521a\u624d\u6211\u89c1\u8001\u738b\u8fdb\u53bb\u642c\u67f4\u3002\u572d\u54e5\uff0c\u6211\u7ed9\u4f60\u7096\u4e86\u71d5\u7a9d\uff0c\u5feb\u53bb\u5403\u4e86\u7f62\u3002\u7a7a\u5fc3\u83dc\u8001\u662f\u54ed\u4e2a\u4e0d\u4f11\uff0c\u5f97\u8ba9\u5979\u597d\u597d\u7761\u4e0a\u4e00\u89c9\u3002\u201d\u4e07\u572d\u201c\u55ef\u201d\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u67f4\u623f\u91cc\u662f\u53a8\u5b50\u8001\u738b\uff1f\u201d\u62b1\u7740\u5973\u513f\uff0c\u4e24\u592b\u59bb\u5e76\u80a9\u53bb\u8fdc\u4e86\u3002 \u3000\u3000\u72c4\u4e91\u4e00\u65f6\u8111\u6d77\u4e2d\u7a7a\u7a7a\u6d1e\u6d1e\uff0c\u65e0\u6cd5\u601d\u7d22\uff0c\u8fc7\u4e86\u597d\u534a\u664c\uff0c\u4f38\u624b\u6376\u4e86\u6376\u81ea\u5df1\u8111\u888b\uff0c\u5bfb\u601d\uff1a\u201c\u8fd9\u67f4\u623f\u7ec8\u7a76\u4e0d\u80fd\u4e45\u8eb2\uff0c\u90a3\u4e2a\u53a8\u5b50\u8001\u738b\u771f\u7684\u6765\u642c\u67f4\u70e7\u996d\uff0c\u90a3\u600e\u4e48\u529e\uff1f\u6211\u8fd8\u662f\u5c06\u4e01\u72ac\u54e5\u5bc6\u5bc6\u85cf\u8d77\uff0c\u81ea\u5df1\u6e9c\u5c06\u51fa\u53bb\uff0c\u5230\u5f97\u665a\u95f4\uff0c\u518d\u6765\u642c\u53d6\u4e01\u5927\u54e5\u7684\u5c38\u8eab\u3002\u55ef\uff0c\u5c31\u662f\u8fd9\u6837\u3002\u201d \u3000\u3000\u53ef\u662f\uff0c\u53ea\u8de8\u5f97\u4e00\u6b65\uff0c\u5fc3\u4e2d\u4fbf\u6709\u4e2a\u58f0\u97f3\u5728\u62c9\u4f4f\u4ed6\uff1a\u201c\u5e08\u59b9\u4e00\u5b9a\u4f1a\u518d\u6765\u77a7\u6211\u3002\u6211\u8fd9\u4e00\u8d70\uff0c\u4fbf\u6c38\u8fdc\u89c1\u5979\u4e0d\u7740\u4e86\u3002\u201d\u201c\u518d\u89c1\u5979\u4e00\u9762\uff0c\u53c8\u6709\u751a\u4e48\u597d\uff1f\u5979\u6709\u4e08\u592b\u3001\u5973\u513f\uff0c\u4e00\u5bb6\u4eba\u6b22\u6b22\u559c\u559c\u7684\uff0c\u54ea\u4f1a\u5c06\u6211\u8fd9\u4e2a\u6740\u4eba\u9003\u72af\u653e\u5728\u5fc3\u4e0a\uff1f\u6211\u518d\u89c1\u5979\uff0c\u5c82\u4e0d\u662f\u5f92\u7136\u7684\u81ea\u8ba8\u6ca1\u8da3\uff1f\u201d\u201c\u5509\uff0c\u6211\u5728\u72f1\u4e2d\u7b49\u4e86\u8fd9\u8bb8\u591a\u5e74\uff0c\u65e5\u601d\u591c\u60f3\uff0c\u53ea\u76fc\u518d\u89c1\u5979\u4e00\u9762\uff0c\u4eca\u65e5\u5c82\u53ef\u9519\u8fc7\u4e86\u8fd9\u673a\u4f1a\uff1f\u6211\u96be\u9053\u53c8\u6709\u751a\u4e48\u522b\u7684\u6307\u671b\u4e86\uff1f\u53ea\u4e0d\u8fc7\u662f\u8981\u95ee\u95ee\uff0c\u5e08\u7236\u4ed6\u8001\u4eba\u5bb6\u6709\u8baf\u606f\u4e48\uff1f\u6211\u8981\u95ee\u5979\uff0c\u4e3a\u751a\u4e48\u8fd9\u4e48\u559c\u65b0\u5f03\u65e7\uff0c\u6211\u4e00\u906d\u707e\u7978\uff0c\u7acb\u65f6\u5c31\u5bf9\u6211\u6beb\u4e0d\u987e\u5ff5\uff1f\u201d \u3000\u3000\u201c\u95ee\u8fd9\u4e9b\u53c8\u6709\u751a\u4e48\u610f\u601d\uff1f\u5979\u4e0d\u662f\u8bf4\u8c0e\uff0c\u4fbf\u662f\u7167\u5b9e\u800c\u7b54\u3002\u8c0e\u8bdd\uff0c\u6709\u751a\u4e48\u53ef\u542c\u7684\uff1f\u5979\u5982\u7167\u5b9e\u8bf4\u4e86\uff0c\u6211\u53ea\u6709\u66f4\u52a0\u4f24\u5fc3\u3002\u201d \u3000\u3000\u8fd9\u4e48\u601d\u524d\u60f3\u540e\uff0c\u4e00\u4f1a\u513f\u51b3\u610f\u7acb\u523b\u79bb\u5f00\uff0c\u4f46\u8ddf\u7740\u53c8\u62ff\u4e0d\u5b9a\u4e3b\u610f\u3002\u4ed6\u5411\u6765\u723d\u5feb\uff0c\u539f\u4e0d\u662f\u8fd9\u822c\u8fdf\u7591\u4e0d\u51b3\u3001\u4e09\u5fc3\u4e24\u610f\u4e4b\u4eba\uff0c\u53ef\u662f\u4eca\u65e5\u9762\u4e34\u4e00\u751f\u4e2d\u6700\u5927\u7684\u96be\u9898\uff0c\u7adf\u4e0d\u77e5\u5982\u4f55\u51b3\u65ad\u624d\u597d\u3002\u7559\u7740\uff0c\u660e\u77e5\u4e0d\u59a5\uff0c\u5c31\u6b64\u4e00\u8d70\uff0c\u5374\u53c8\u662f\u4e07\u5206\u7684\u4e0d\u820d\u3002 \u3000\u3000\u6b63\u81ea\u8fd9\u822c\u601d\u6f6e\u7ffb\u6d8c\uff0c\u6817\u516d\u4e0d\u5b89\uff0c\u5ffd\u542c\u5f97\u83dc\u56ed\u4e2d\u811a\u6b65\u8f7b\u54cd\uff0c\u4e00\u4e2a\u4eba\u8e51\u624b\u8e51\u811a\u7684\u6084\u6084\u8d70\u6765\u3002\u90a3\u4eba\u8d70\u51e0\u6b65\uff0c\u4fbf\u505c\u4e00\u4e0b\uff0c\u53c8\u8d70\u51e0\u6b65\uff0c\u663e\u7136\u662f\u4e25\u795e\u6212\u5907\uff0c\u552f\u6050\u6709\u4eba\u77e5\u89c9\u3002 \u3000\u3000\u90a3\u4eba\u8d8a\u6765\u8d8a\u8fd1\uff0c\u72c4\u4e91\u4e00\u9897\u5fc3\u6026\u6026\u4e71\u8df3\uff1a\u201c\u5e08\u59b9\u7ec8\u4e8e\u627e\u6211\u6765\u4e86\u3002\u5979\u8981\u8ddf\u6211\u8bf4\u751a\u4e48\uff1f\u662f\u6c42\u6211\u539f\u6055\u4e48\uff1f\u5979\u8fd8\u6709\u4e00\u4e9b\u5ff5\u65e7\u4e4b\u610f\u4e48\uff1f\u201d\u53c8\u60f3\uff1a\u201c\u6211\u8fd8\u6709\u751a\u4e48\u8bdd\u8981\u8ddf\u5979\u8bf4\u7684\uff1f\u5509\uff0c\u7b97\u4e86\uff0c\u7b97\u4e86\uff01\u5979\u6709\u597d\u4e08\u592b\uff0c\u597d\u5973\u513f\uff0c\u8fc7\u5f97\u633a\u5f00\u5fc3\u7684\u3002\u6211\u6c38\u8fdc\u4e0d\u8981\u518d\u89c1\u5979\u4e86\u3002\u201d \u3000\u3000\u7a81\u7136\u4e4b\u95f4\uff0c\u6ee1\u8154\u590d\u4ec7\u4e4b\u5fc3\uff0c\u5316\u4f5c\u51b0\u51c9\uff1a\u201c\u6211\u672c\u6765\u662f\u4e2a\u4e61\u4e0b\u7a77\u5c0f\u5b50\uff0c\u5c31\u7b97\u4e0d\u53d7\u8fd9\u573a\u51a4\u5c48\uff0c\u5e08\u59b9\u548c\u6211\u6210\u4e86\u592b\u59bb\uff0c\u6211\u56fa\u7136\u5feb\u4e50\uff0c\u5e08\u59b9\u5374\u52bf\u5fc5\u8981\u8f9b\u82e6\u52b3\u788c\u4e00\u8f88\u5b50\uff0c\u4e8e\u5979\u53c8\u6709\u751a\u4e48\u597d\u5904\uff1f\u6211\u8981\u590d\u4ec7\uff0c\u662f\u5c06\u4e07\u572d\u6740\u4e86\u4e48\uff1f\u5e08\u59b9\u6210\u4e86\u5be1\u5987\uff0c\u96be\u9053\u8fd8\u80fd\u5ac1\u6211\uff0c\u5ac1\u7ed9\u5979\u7684\u6740\u592b\u4ec7\u4eba\uff1f\u5979\u5fc3\u4e2d\u65e9\u5c31\u6ca1\u4e86\u6211\u8fd9\u4e2a\u4eba\uff0c\u4ece\u524d\u6211\u5c31\u6bd4\u4e0d\u4e0a\u4e07\u572d\uff0c\u73b0\u4e0b\u6211\u8ddf\u4ed6\u66f4\u662f\u5929\u5dee\u5730\u8fdc\u4e86\u3002\u8fd9\u573a\u51a4\u4ec7\uff0c\u5c31\u6b64\u4e00\u7b14\u52fe\u9500\uff0c\u8ba9\u5979\u592b\u59bb\u6bcd\u5973\u5feb\u5feb\u4e50\u4e50\u7684\u8fc7\u65e5\u5b50\u7f62\u3002\u201d \u3000\u3000\u60f3\u5230\u6b64\u5904\uff0c\u51b3\u610f\u4e0d\u518d\u548c\u621a\u82b3\u591a\u8bf4\u751a\u4e48\uff0c\u4fef\u8eab\u4fbf\u53bb\u67f4\u8349\u5806\u4e2d\u62b1\u4e01\u5178\u7684\u5c38\u8eab\uff0c\u731b\u542c\u5f97\u7830\u7684\u4e00\u58f0\uff0c\u67f4\u623f\u95e8\u677f\u7ed9\u4eba\u4e00\u811a\u8e22\u5f00\u3002 \u3000\u3000\u72c4\u4e91\u5403\u4e86\u4e00\u60ca\uff0c\u8f6c\u8fc7\u8eab\u6765\uff0c\u53ea\u89c1\u4e00\u4e2a\u9ad8\u7626\u7537\u5b50\u624b\u4e2d\u957f\u5251\u5149\u8292\u95ea\u70c1\uff0c\u7ad9\u5728\u95e8\u53e3\uff0c\u5374\u662f\u4e07\u572d\u3002\u72c4\u4e91\u8f7b\u566b\u4e00\u58f0\uff0c\u4e0d\u5047\u601d\u7d22\uff0c\u4fbf\u4fef\u8eab\u62fe\u8d77\u621a\u82b3\u9057\u4e0b\u7684\u957f\u5251\u3002 \u3000\u3000\u4e07\u572d\u6ee1\u8138\u715e\u6c14\uff0c\u4ed6\u65e9\u5df2\u5f97\u77e5\u72c4\u4e91\u8d8a\u72f1\u7684\u6d88\u606f\uff0c\u6574\u65e5\u4fbf\u5fc3\u795e\u4e0d\u5b9a\uff0c\u8fd9\u65f6\u4e00\u773c\u770b\u5230\u72c4\u4e91\u624b\u4e2d\u957f\u5251\u662f\u621a\u82b3\u4e4b\u7269\uff0c\u66f4\u662f\u53c8\u5992\u53c8\u6068\uff0c\u51b7\u51b7\u7684\u9053\uff1a\u201c\u597d\u554a\uff0c\u5728\u67f4\u623f\u91cc\u76f8\u4f1a\uff0c\u5979\u8fde\u81ea\u5df1\u7684\u5175\u5203\u4e5f\u7ed9\u4e86\u4f60\uff0c\u60f3\u8c0b\u6740\u4eb2\u592b\u4e48\uff1f\u53ea\u6015\u6ca1\u8fd9\u4e48\u5bb9\u6613\uff01\u201d \u3000\u3000\u72c4\u4e91\u8111\u4e2d\u4e00\u7247\u6df7\u4e71\uff0c\u4e00\u65f6\u4e5f\u4e0d\u61c2\u4e07\u572d\u5728\u8bf4\u4e9b\u751a\u4e48\uff0c\u5fc3\u4e2d\u53ea\u60f3\uff1a\u201c\u600e\u4e48\u662f\u4ed6\u6765\u4e86\uff1f\u4ed6\u600e\u4f1a\u77e5\u9053\u6211\u5728\u8fd9\u91cc\uff1f\u81ea\u7136\u662f\u5e08\u59b9\u8bf4\u7684\uff0c\u53eb\u5979\u4e08\u592b\u6765\u6349\u6211\u53bb\u8bf7\u529f\u9886\u8d4f\u3002\u5979\u600e\u4e48\u4f1a\u8fd9\u822c\u65e0\u60c5\u65e0\u4e49\uff1f\u201d \u3000\u3000\u4e07\u572d\u89c1\u72c4\u4e91\u4e0d\u7b54\uff0c\u53ea\u9053\u4ed6\u60c5\u602f\u5bb3\u6015\uff0c\u633a\u5251\u4fbf\u5411\u4ed6\u80f8\u53e3\u75be\u523a\u8fc7\u53bb\u3002\u72c4\u4e91\u6325\u5251\u6321\u8fc7\uff0c\u81ea\u7136\u800c\u7136\u7684\u4f7f\u51fa\u4e86\u6614\u5e74\u8001\u4e10\u6240\u6388\u7684\u90a3\u62db\u201c\u523a\u80a9\u5f0f\u201d\uff0c\u957f\u5251\u659c\u8f6c\uff0c\u5df2\u6307\u5411\u4e07\u572d\u80a9\u5934\u3002\u8fd9\u62db\u5251\u6cd5\u602a\u5f02\u4e4b\u6781\uff0c\u4e07\u572d\u5f53\u5e74\u62db\u67b6\u4e0d\u4f4f\uff0c\u4e8b\u9694\u4e94\u5e74\uff0c\u867d\u7136\u6b66\u529f\u5df2\u5927\u6709\u957f\u8fdb\uff0c\u5374\u4ecd\u662f\u62db\u67b6\u4e0d\u4f4f\u3002 \u3000\u3000\u4e07\u572d\u4e00\u60ca\u4e4b\u4e0b\uff0c\u624b\u4e2d\u957f\u5251\u4e0d\u77e5\u5982\u4f55\u8fd0\u4f7f\u624d\u597d\uff0c\u6536\u5251\u62b5\u6321\u5df2\u7136\u4e0d\u53ca\uff0c\u53d1\u5251\u653b\u654c\u4e5f\u5df2\u843d\u4e86\u540e\u624b\uff0c\u4fbf\u8fd9\u6837\u5fae\u4e00\u8fdf\u7591\uff0c\u4e00\u6761\u6027\u547d\u5df2\u5168\u7136\u4ea4\u5728\u5bf9\u65b9\u624b\u4e2d\uff0c\u5fc3\u4e0b\u6124\u6012\u5df2\u6781\uff0c\u5374\u4e1d\u6beb\u4e0d\u6562\u52a8\u5f39\uff0c\u77a7\u7740\u72c4\u4e91\u4e00\u5f20\u6ee1\u8138\u80e1\u5b50\u7684\u6c61\u79fd\u8138\u5b54\uff0c\u6124\u6012\u4e4b\u60c5\u6e10\u6e10\u53d8\u4e3a\u6050\u60e7\u3002 \u3000\u3000\u72c4\u4e91\u8fd9\u4e00\u5251\u5374\u4e5f\u4e0d\u523a\u8fc7\u53bb\uff0c\u5fc3\u4e2d\u8f6c\u5ff5\uff1a\u201c\u6211\u6740\u4ed6\u4e0d\u6740\uff1f\u201d \u3000\u3000\u4e07\u572d\u5728\u4e07\u5206\u5371\u6025\u4e4b\u9645\uff0c\u5ffd\u7136\u89c1\u5230\u5bf9\u65b9\u773c\u795e\u4e2d\u6d41\u9732\u51fa\u60f6\u60d1\u4e4b\u8272\uff0c\u800c\u6301\u5251\u7684\u624b\u8155\u5374\u53c8\u5fae\u5fae\u98a4\u6296\uff0c\u7075\u673a\u4e00\u52a8\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u621a\u82b3\uff0c\u4f60\u6765\uff01\u201d \u3000\u3000\u72c4\u4e91\u542c\u4ed6\u5927\u53eb\u201c\u621a\u82b3\u201d\uff0c\u5fc3\u4e2d\u4e00\u60ca\uff0c\u5fae\u5fae\u4fa7\u5934\u53bb\u770b\u3002\u4e0d\u6599\u4e07\u572d\u8fd9\u662f\u7528\u8ba1\u4f7f\u8bc8\uff0c\u4e58\u4ed6\u7565\u4e00\u8f6c\u5934\uff0c\u7acb\u5373\u957f\u5251\u633a\u4e0a\uff0c\u594b\u529b\u4e0a\u683c\u3002\u72c4\u4e91\u53f3\u624b\u624b\u6307\u88ab\u524a\uff0c\u6301\u5251\u4e0d\u7262\uff0c\u957f\u5251\u8131\u624b\u98de\u51fa\u3002\u4e07\u572d\u5927\u559c\uff0c\u7acb\u5373\u633a\u5251\u523a\u51fa\u3002\u72c4\u4e91\u8fde\u95ea\u4e24\u95ea\uff0c\u8eb2\u5728\u67f4\u5806\u4e4b\u540e\uff0c\u987a\u624b\u62bd\u8d77\u4e00\u6761\u786c\u67f4\uff0c\u4ee5\u67f4\u5f53\u5251\uff0c\u594b\u529b\u6253\u53bb\u3002\u4e07\u572d\u5237\u5237\u4e24\u5251\uff0c\u5c06\u4ed6\u90a3\u6bb5\u786c\u67f4\u524a\u77ed\u4e86\u4e00\u622a\u3002\u72c4\u4e91\u5c06\u624b\u4e2d\u534a\u622a\u786c\u67f4\u7528\u529b\u63b7\u51fa\uff0c\u5f85\u4ed6\u8dc3\u8eab\u95ea\u907f\uff0c\u53c8\u62bd\u4e86\u4e00\u6bb5\u786c\u67f4\uff0c\u518d\u5ea6\u653b\u53bb\u3002 \u3000\u3000\u4e07\u572d\u89c1\u4ed6\u5931\u4e86\u5175\u5203\uff0c\u81ea\u5df1\u5df2\u64cd\u5fc5\u80dc\uff0c\u5c31\u7b97\u4ed6\u4ee5\u67f4\u4f5c\u5251\uff0c\u6233\u4e2d\u81ea\u5df1\u4e00\u4e0b\u4e24\u4e0b\uff0c\u4e5f\u65e0\u5927\u788d\uff0c\u5b9a\u4e86\u5b9a\u795e\uff0c\u5c55\u5f00\u5251\u6cd5\u7f13\u7f13\u8fdb\u653b\u3002 \u3000\u3000\u6570\u62db\u4e4b\u540e\uff0c\u72c4\u4e91\u4e00\u58f0\u6012\u543c\uff0c\u53f3\u8155\u4e2d\u5251\uff0c\u767b\u65f6\u8840\u5982\u6cc9\u6d8c\uff0c\u624b\u6307\u65e0\u529b\uff0c\u629b\u4e0b\u4e86\u786c\u67f4\u3002\u4e07\u572d\u8ddf\u7740\u53c8\u662f\u4e00\u5251\u523a\u4e2d\u4ed6\u5927\u817f\uff0c\u98de\u8d77\u5de6\u8db3\uff0c\u5c06\u4ed6\u8e22\u5012\u3002\u72c4\u4e91\u6323\u624e\u7740\u8fd8\u5f85\u722c\u8d77\uff0c\u4e07\u572d\u53c8\u662f\u4e00\u811a\u8e22\u5728\u4ed6\u98a7\u9aa8\u4e4b\u4e0a\uff0c\u72c4\u4e91\u767b\u65f6\u6655\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u4e07\u572d\u9a82\u9053\uff1a\u201c\u88c5\u6b7b\u5417\uff1f\u201d\u5728\u4ed6\u53f3\u80a9\u4e0a\u780d\u4e86\u4e00\u5251\uff0c\u89c1\u4ed6\u5e76\u4e0d\u52a8\u5f39\uff0c\u624d\u77e5\u662f\u771f\u7684\u660f\u6655\uff0c\u5fc3\u60f3\uff1a\u201c\u51cc\u77e5\u5e9c\u8bb8\u4e0b\u4e94\u5343\u4e24\u94f6\u5b50\u7684\u91cd\u8d4f\uff0c\u6349\u62ff\u8fd9\u4e24\u540d\u56da\u72af\uff0c\u81ea\u7136\u662f\u6349\u6d3b\u7684\u597d\u3002\u53cd\u6b63\u8fd9\u4e00\u6b21\u9001\u5c06\u5b98\u91cc\u53bb\uff0c\u8fd9\u4eba\u81ea\u662f\u96be\u4ee5\u6d3b\u547d\uff0c\u6211\u4f55\u5fc5\u4eb2\u624b\u6740\u4ed6\uff1f\u201d\u4e00\u77a5\u773c\uff0c\u89c1\u5230\u67f4\u8349\u5806\u4e2d\u9732\u51fa\u4e00\u53ea\u811a\u6765\uff0c\u4e0d\u7531\u5f97\u53c8\u60ca\u53c8\u559c\uff1a\u201c\u8fd9\u91cc\u8fd8\u6709\u4e00\u4e2a\u4eba\uff01\u201d\u4ed6\u4e0d\u77e5\u4e01\u5178\u5df2\u6b7b\uff0c\u6025\u5fd9\u6325\u5251\uff0c\u780d\u5728\u5c38\u4f53\u811a\u4e0a\u3002 \u3000\u3000\u72c4\u4e91\u867d\u88ab\u8e22\u6655\uff0c\u8111\u5b50\u4e2d\u5374\u6709\u4e00\u4e2a\u58f0\u97f3\u5728\u5927\u53eb\u5927\u558a\uff1a\u201c\u6211\u4e0d\u80fd\u6b7b\uff0c\u6211\u4e0d\u80fd\u6b7b\uff01\u6211\u7b54\u5e94\u8fc7\u4e01\u5927\u54e5\u7684\uff0c\u8981\u5c06\u4ed6\u5c38\u8eab\u548c\u51cc\u5c0f\u59d0\u5408\u846c\u3002\u201d\u8fd9\u5ff5\u5934\u5f3a\u70c8\u4e4b\u6781\uff0c\u5f88\u5feb\u4fbf\u9192\u4e86\u8fc7\u6765\uff0c\u8ff7\u8ff7\u7cca\u7cca\u7684\u60f3\u8d77\uff1a \u3000\u3000\u201c\u8bb8\u591a\u5e74\u4e4b\u524d\u7684\u4e00\u5929\u665a\u4e0a\uff0c\u6211\u4e5f\u66fe\u88ab\u4ed6\u6253\u5012\uff0c\u4e5f\u66fe\u88ab\u4ed6\u5728\u5934\u4e0a\u91cd\u91cd\u8e22\u4e86\u51e0\u4e0b\u3002\u201d\u7f13\u7f13\u7741\u5f00\u773c\u6765\uff0c\u53ea\u89c1\u4e07\u572d\u6b63\u6325\u5251\u5411\u4e01\u5178\u7684\u5c38\u8eab\u4e0a\u780d\u4e86\u4e0b\u53bb\u3002\u4ed6\u521d\u65f6\u8fd8\u672a\u5341\u5206\u6e05\u9192\uff0c\u4e0d\u77e5\u773c\u524d\u4e4b\u4e8b\u662f\u751a\u4e48\u610f\u601d\uff0c\u4f46\u968f\u5373\u89c1\u5230\u4e07\u572d\u5c06\u4e01\u5178\u7684\u5c38\u8eab\u4ece\u67f4\u8349\u91cc\u62d6\u4e86\u51fa\u6765\uff0c\u4ed6\u5927\u53eb\u4e00\u58f0\uff1a\u201c\u4e01\u5927\u54e5\uff01\u201d\u7a81\u7136\u95f4\u5168\u8eab\u7cbe\u529b\u5f25\u6f2b\uff0c\u6025\u7eb5\u800c\u8d77\uff0c\u6251\u5728\u4e07\u572d\u80cc\u4e0a\uff0c\u53f3\u81c2\u5df2\u627c\u4f4f\u4e86\u4ed6\u5589\u5499\u3002 \u3000\u3000\u4e07\u572d\u5927\u60ca\u4e4b\u4e0b\uff0c\u5f85\u8981\u53cd\u5251\u53bb\u523a\uff0c\u4f46\u624b\u81c2\u65e0\u6cd5\u540e\u5f2f\uff0c\u8fde\u5288\u51e0\u5251\uff0c\u90fd\u5288\u5728\u786c\u67f4\u5806\u4e0a\uff0c\u800c\u72c4\u4e91\u627c\u5728\u4ed6\u5589\u5934\u7684\u624b\u81c2\u5374\u8d8a\u6536\u8d8a\u7d27\u4e86\u3002 \u3000\u3000\u72c4\u4e91\u89c1\u4ed6\u4f24\u6b8b\u4e01\u5178\u7684\u5c38\u4f53\uff0c\u6012\u53d1\u5982\u72c2\u3002\u8fd9\u4eba\u9677\u5bb3\u81ea\u5df1\u3001\u593a\u53bb\u621a\u82b3\uff0c\u8fd9\u6028\u4ec7\u5c1a\u53ef\u7f6e\u4e4b\u4e0d\u7406\uff0c\u4f46\u5982\u6b64\u6b8b\u5bb3\u4e01\u5178\uff0c\u5374\u4e07\u4e07\u4e0d\u80fd\u5e72\u4f11\uff0c\u4e00\u65f6\u5fc3\u4e2d\u66f4\u65e0\u522b\u7684\u5ff5\u5934\uff0c\u53ea\u76fc\u5373\u523b\u4fbf\u5c06\u654c\u4eba\u627c\u6b7b\u3002\u4f46\u89c9\u4e07\u572d\u6323\u624e\u4e86\u4e00\u4f1a\uff0c\u62b5\u6297\u5df2\u6e10\u6e10\u65e0\u529b\uff0c\u53ef\u662f\u72c4\u4e91\u6570\u5904\u53d7\u4f24\uff0c\u4f24\u53e3\u4e2d\u6d41\u8840\u4e0d\u6b62\uff0c\u81ea\u5df1\u624b\u81c2\u4e0a\u7684\u529b\u6c14\u5374\u5728\u66f4\u5feb\u7684\u6d88\u5931\u3002\u5fc3\u4e2d\u4e0d\u4f4f\u8bf4\uff1a\u201c\u6211\u518d\u652f\u6301\u4e00\u4f1a\u513f\uff0c\u4fbf\u80fd\u627c\u6b7b\u4e86\u4ed6\u3002\u201d\u5230\u540e\u6765\u773c\u524d\u91d1\u661f\u4e71\u821e\uff0c\u8111\u4e2d\u4e71\u6210\u4e00\u56e2\uff0c\u7ec8\u4e8e\u751a\u4e48\u4e5f\u4e0d\u77e5\u9053\u4e86\u3002 \u3000\u3000\u4ed6\u867d\u7136\u6655\u53bb\uff0c\u627c\u5728\u4e07\u572d\u5589\u95f4\u7684\u624b\u81c2\u4ecd\u662f\u6ca1\u6709\u677e\u5f00\u3002\u4e07\u572d\u7ed9\u4ed6\u627c\u5f97\u96be\u4ee5\u547c\u5438\uff0c\u5c31\u5728\u72c4\u4e91\u6655\u53bb\u4e4b\u65f6\uff0c\u540c\u65f6\u5931\u5374\u4e86\u77e5\u89c9\u3002 \u3000\u3000\u67f4\u8349\u5806\u4e0a\u8eba\u7740\u8fbe\u4e00\u5bf9\u51a4\u5bb6\u3002\u4e24\u4e2a\u4eba\u4f3c\u4e4e\u90fd\u6b7b\u4e86\uff0c\u4f46\u80f8\u95f4\u90fd\u8fd8\u6709\u8d77\u4f0f\uff0c\u53e3\u9f3b\u95f4\u4ecd\u6709\u547c\u5438\u3002 \u3000\u3000\u771f\u4e0d\u77e5\u51a5\u51a5\u95f4\u5982\u4f55\u5b89\u6392\uff1f\u82e5\u662f\u72c4\u4e91\u5148\u9192\u8f6c\u7247\u523b\uff0c\u4ed6\u62ac\u8d77\u5730\u4e0b\u7684\u957f\u5251\uff0c\u81ea\u662f\u4e00\u5251\u4fbf\u5c06\u4e07\u572d\u6740\u4e86\uff0c\u5018\u82e5\u4e07\u572d\u5148\u884c\u9192\u8f6c\uff0c\u4ed6\u4e5f\u4e0d\u4f1a\u518d\u6709\u5c06\u72c4\u4e91\u751f\u64d2\u6d3b\u6349\u7684\u5ff5\u5934\uff0c\u90a3\u5b9e\u5728\u592a\u8fc7\u5371\u9669\uff0c\u52bf\u5fc5\u662f\u968f\u624b\u4e00\u5251\uff0c\u780d\u5728\u4ed6\u5934\u4e0a\uff0c\u7acb\u65f6\u4fbf\u53d6\u4e86\u4ed6\u6027\u547d\u3002 \u3000\u3000\u4e16\u754c\u4e0a\u751a\u4e48\u4e8b\u6084\u90fd\u80fd\u53d1\u751f\u3002\u672a\u5fc5\u597d\u4eba\u4e00\u5b9a\u8fd0\u6c14\u597d\uff0c\u574f\u4eba\u4e00\u5b9a\u8fd0\u6c14\u574f\u3002\u53cd\u8fc7\u6765\u4e5f\u4e00\u6837\uff0c\u4e5f\u672a\u5fc5\u574f\u4eba\u8fd0\u6c14\u597d\uff0c\u597d\u4eba\u8fd0\u6c14\u574f\u3002\u6bcf\u4e2a\u4eba\u90fd\u4f1a\u6b7b\u7684\uff0c\u8fdf\u6b7b\u7684\u4eba\u4e5f\u672a\u5fc5\u4e00\u5b9a\u8fd0\u6c14\u597d\u4e9b\u3002 \u3000\u3000\u4f46\u5bf9\u4e8e\u6d3b\u7740\u7684\u4eba\uff0c\u5bf9\u4e8e\u621a\u82b3\u548c\u5979\u7684\u5c0f\u5973\u513f\uff0c\u72c4\u4e91\u5148\u6b7b\uff0c\u8fd8\u662f\u4e07\u572d\u5148\u6b7b\uff0c\u4e2d\u95f4\u4fbf\u6709\u5f88\u5927\u7684\u5dee\u522b\u3002\u5018\u82e5\u8fd9\u65f6\u5019\u8981\u621a\u82b3\u6765\u6289\u62e9\uff0c\u8981\u5979\u9009\u4e00\u4e2a\u4eba\uff0c\u8ba9\u4ed6\u5148\u884c\u9192\u8f6c\uff0c\u4e0d\u77e5\u5979\u4f1a\u9009\u8c01\uff1f \u3000\u3000\u67f4\u623f\u4e2d\u7684\u4e24\u4e2a\u4eba\u5140\u81ea\u660f\u6655\u4e0d\u9192\uff0c\u6709\u4e00\u4e2a\u4eba\u7684\u811a\u6b65\u58f0\u97f3\uff0c\u6162\u6162\u8d70\u8fd1\u67f4\u623f\u3002 \u3000\u3000\u72c4\u4e91\u8033\u4e2d\u542c\u5230\u6d69\u6d69\u7684\u6c34\u58f0\uff0c\u8138\u4e0a\u6709\u51b0\u51c9\u7684\u4e1c\u897f\u4e00\u6ef4\u6ef4\u6e85\u4e0a\u6765\uff0c\u9690\u9690\u751f\u75bc\uff0c\u968f\u5373\u89c9\u5f97\u8eab\u4e0a\u5f88\u51b7\uff0c\u534a\u70b9\u4e5f\u6ca1\u6709\u529b\u6c14\u3002\u4ed6\u4e00\u6709\u77e5\u89c9\uff0c\u7acb\u5373\u53f3\u81c2\u8fd0\u52b2\uff0c\u53eb\u9053\uff1a\u201c\u6211\u627c\u6b7b\u4f60\uff01\u6211\u627c\u6b7b\u4f60\uff01\u201d\u4f46\u81c2\u5f2f\u4e2d\u865a\u7a7a\u65e0\u7269\uff0c\u8ddf\u7740\u53c8\u53d1\u89c9\u81ea\u5df1\u8eab\u5b50\u5728\u4e0d\u4f4f\u6447\u6643\uff0c\u5728\u4e0d\u4f4f\u79fb\u52a8\u3002\u60ca\u60f6\u4e2d\u7741\u5f00\u773c\u6765\uff0c\u773c\u524d\u9ed1\u6c89\u6c89\u5730\uff0c\u53ea\u89c9\u5f97\u4e00\u6ef4\u6ef4\u6c34\u73e0\u6253\u5728\u8138\u4e0a\u3001\u624b\u4e0a\u3001\u8eab\u4e0a\uff0c\u539f\u6765\u662f\u5929\u5728\u4e0b\u5927\u96e8\u3002 \u3000\u3000\u8eab\u5b50\u4ecd\u662f\u4e0d\u4f4f\u6447\u6643\uff0c\u80f8\u53e3\u70e6\u6076\uff0c\u53ea\u60f3\u5455\u5410\u3002\u5ffd\u7136\u95f4\uff0c\u8eab\u65c1\u6709\u4e00\u8258\u8239\u9a76\u8fc7\uff0c\u8239\u4e0a\u5f20\u4e86\u5e06\uff0c\u90a3\u6e05\u6e05\u695a\u695a\u662f\u4e00\u8258\u8239\u3002\u5947\u602a\u6781\u4e86\uff0c\u600e\u4e48\u8eab\u65c1\u4f1a\u6709\u4e00\u8258\u8239\uff1f \u3000\u3000\u53ea\u60f3\u5750\u8d77\u8eab\u6765\u770b\u4e2a\u7a76\u7adf\uff0c\u4f46\u5168\u8eab\u9178\u8f6f\uff0c\u8fde\u4e00\u6839\u6307\u5934\u4e5f\u52a8\u4e0d\u4e86\uff0c\u53ea\u80fd\u8fd9\u822c\u4ef0\u5929\u5367\u7740\uff0c\u773c\u89c1\u5f97\u5934\u9876\u6709\u9ed1\u4e91\u98d8\u52a8\uff0c\u90a3\u4e0d\u662f\u5728\u67f4\u623f\u4e4b\u4e2d\u3002\u5fc3\u4e2d\u7a81\u7136\u60f3\u8d77\uff1a\u201c\u4e01\u5927\u54e5\u5462\uff1f\u201d\u4e00\u60f3\u5230\u4e01\u5178\uff0c\u8eab\u4e0a\u84e6\u5730\u91cc\u751f\u51fa\u4e86\u4e00\u80a1\u529b\u6c14\uff0c\u53cc\u624b\u4e00\u6309\uff0c\u4fbf\u5373\u5750\u8d77\uff0c\u8eab\u5b50\u8ddf\u7740\u6643\u4e86\u51e0\u6643\u3002 \u3000\u3000\u4ed6\u662f\u5728\u4e00\u8258\u5c0f\u821f\u4e4b\u4e2d\u3002\u5c0f\u821f\u6b63\u5728\u6c5f\u6c34\u6ed4\u6ed4\u7684\u5927\u6c5f\u4e2d\u987a\u6d41\u800c\u4e0b\u3002\u662f\u591c\u665a\uff0c\u5929\u4e0a\u90fd\u662f\u9ed1\u4e91\uff0c\u6b63\u4e0b\u7740\u5927\u96e8\uff0c\u4ed6\u5411\u8239\u5de6\u8239\u53f3\u5cb8\u4e0a\u51dd\u76ee\u671b\u53bb\uff0c\u4e24\u8fb9\u90fd\u662f\u9ed1\u6c89\u6c89\u5730\uff0c\u751a\u4e48\u4e5f\u77a7\u4e0d\u89c1\u3002\u4ed6\u5fc3\u4e2d\u7126\u6025\uff0c\u5927\u53eb\uff1a\u201c\u4e01\u5927\u54e5\uff0c\u4e01\u5927\u54e5\uff01\u201d\u4ed6\u77e5\u9053\u4e01\u5178\u5df2\u7ecf\u6b7b\u4e86\uff0c\u4f46\u4ed6\u7684\u5c38\u8eab\u4e07\u4e07\u4e0d\u80fd\u592b\u53bb\u3002\u7a81\u7136\u4e4b\u95f4\uff0c\u5de6\u8db3\u8e22\u5230\u8f6f\u8f6f\u4e00\u7269\uff0c\u4f4e\u5934\u4e00\u770b\uff0c\u4e0d\u7531\u5f97\u60ca\u559c\u4ea4\u96c6\uff0c\u53eb\u9053\uff1a\u201c\u4e01\u5927\u54e5\uff0c\u4f60\u5728\u8fd9\u91cc\uff01\u201d\u5f20\u5f00\u53cc\u81c2\uff0c\u62b1\u4f4f\u4e86\u4ed6\u3002\u4e01\u5178\u7684\u5c38\u8eab\uff0c\u4fbf\u5728\u8239\u8231\u4e2d\u4ed6\u7684\u8db3\u8fb9\u3002 \u3000\u3000\u4ed6\u865a\u5f31\u5f97\u8fde\u5598\u6c14\u4e5f\u6ca1\u529b\u6c14\uff0c\u8fde\u60f3\u4e8b\u4e5f\u6ca1\u529b\u6c14\u3002\u53ea\u89c9\u5589\u5e72\u820c\u71e5\uff0c\u4fbf\u5f20\u5f00\u4e86\u53e3\uff0c\u8ba9\u5929\u7a7a\u4e2d\u843d\u4e0b\u6765\u7684\u96e8\u70b9\u6e7f\u6da6\u5634\u5507\u548c\u820c\u5934\u3002 \u3000\u3000\u8fd9\u822c\u8ff7\u8ff7\u7cca\u7cca\u7684\u4f3c\u7761\u4f3c\u9192\uff0c\u53cc\u81c2\u62b1\u7740\u4e01\u5178\u7684\u5c38\u8eab\uff0c\u76f4\u81f3\u5929\u8272\u6e10\u660e\uff0c\u5927\u96e8\u5374\u5140\u81ea\u4e0d\u6b62\u3002 \u3000\u3000\u6668\u5149\u71b9\u5fae\u4e4b\u4e2d\uff0c\u5ffd\u7136\u89c1\u5230\u81ea\u5df1\u5927\u817f\u4e0a\u6709\u4e00\u5927\u5757\u5e03\u6761\u7f20\u7740\uff0c\u5b9a\u4e86\u5b9a\u795e\uff0c\u53d1\u89c9\u5e03\u6761\u662f\u5305\u624e\u7740\u4f24\u53e3\uff0c\u8ddf\u7740\u53d1\u89c9\u624b\u81c2\u548c\u80a9\u5934\u7684\u4e24\u5904\u4f24\u53e3\u4e0a\u4e5f\u90fd\u6709\u5e03\u5e26\u88f9\u4f4f\uff0c\u9f3b\u4e2d\u9690\u9690\u95fb\u5230\u91d1\u521b\u836f\u7684\u836f\u6c14\u3002\u4e00\u665a\u5927\u96e8\uff0c\u7ef7\u5e26\u90fd\u6e7f\u900f\u4e86\uff0c\u4f46\u4f24\u53e3\u5df2\u4e0d\u518d\u6d41\u8840\u3002 \u3000\u3000\u201c\u662f\u8c01\u7ed9\u6211\u5305\u624e\u4e86\u4f24\u53e3\uff1f\u8981\u662f\u4f24\u53e3\u4e0d\u88f9\u597d\uff0c\u4e5f\u4e0d\u7528\u8c01\u6765\u6740\u6211\uff0c\u5355\u662f\u6d41\u8840\u4fbf\u8981\u4e86\u6211\u7684\u6027\u547d\u3002\u201d\u84e6\u5730\u91cc\u611f\u5230\u4e00\u9635\u96be\u4ee5\u5fcd\u8010\u7684\u5bc2\u5bde\u51c4\u51c9\uff1a\u201c\u8fd9\u4e16\u4e0a\u8fd8\u6709\u8c01\u6765\u5173\u6000\u6211\u3001\u5e2e\u52a9\u6211\uff1f\u4e01\u5927\u54e5\u5df2\u7ecf\u6b7b\u4e86\uff0c\u66f4\u4f1a\u6709\u8c01\u76fc\u671b\u6211\u6d3b\u7740\uff1f\u4f1a\u8d39\u5fc3\u6765\u66ff\u6211\u88f9\u4f24\uff1f\u201d\u7ec6\u770b\u90a3\u51e0\u6761\u7ef7\u5e26\uff0c\u7f20\u5f97\u6781\u4e0d\u6574\u9f50\uff0c\u4f3c\u4e4e\u5305\u624e\u7684\u4eba\u52a8\u624b\u65f6\u5341\u5206\u5fc3\u6025\u614c\u5fd9\uff0c\u7136\u800c\u7ef7\u5e26\u4e0d\u662f\u7c97\u5e03\uff0c\u800c\u662f\u4e0a\u4f73\u7684\u7f0e\u5b50\uff0c\u7f0e\u5e26\u7684\u4e00\u8fb9\u9576\u7740\u7cbe\u81f4\u7684\u82b1\u8fb9\uff0c\u53e6\u4e00\u8fb9\u662f\u6495\u53e3\uff0c\u663e\u7136\uff0c\u662f\u4ece\u8863\u886b\u4e0a\u6495\u4e0b\u6765\u7684\uff0c\u662f\u5973\u5b50\u7684\u8863\u886b\u3002 \u3000\u3000\u662f\u5e08\u59b9\u4e48\uff1f\u4ed6\u5fc3\u4e2d\u6026\u7136\u800c\u52a8\uff0c\u80f8\u53e3\u968f\u5373\u70ed\u4e86\u8d77\u6765\uff0c\u5634\u89d2\u8fb9\u9732\u51fa\u4e86\u81ea\u5632\u7684\u82e6\u7b11\uff1a\u201c\u5979\u53bb\u53eb\u4e08\u592b\u6765\u6740\u6211\uff0c\u600e\u4e48\u53c8\u4f1a\u7ed9\u6211\u88f9\u4f24\uff1f\u8981\u4e0d\u662f\u5979\u901a\u98ce\uff0c\u6211\u8eb2\u5728\u67f4\u623f\u91cc\uff0c\u4e07\u572d\u53c8\u600e\u4f1a\u77e5\u9053\uff1f\u201d \u3000\u3000\u53ef\u662f\u81ea\u5df1\u662f\u5728\u4e00\u8258\u5c0f\u821f\u4e4b\u4e2d\uff0c\u5c0f\u821f\u662f\u5728\u6c5f\u4e2d\u98d8\u6d41\u3002\u4e0d\u77e5\u8fd9\u5730\u65b9\u79bb\u6c5f\u9675\u5df2\u6709\u591a\u8fdc\uff1f\u65e0\u8bba\u5982\u4f55\uff0c\u662f\u6682\u65f6\u8131\u79bb\u4e86\u9669\u5883\uff0c\u4e0d\u4f1a\u518d\u53d7\u51cc\u77e5\u5e9c\u7684\u8ffd\u62ff\u4e86\u3002 \u3000\u3000\u201c\u662f\u8c01\u7ed9\u6211\u88f9\u4e86\u4f24\u53e3\uff1f\u662f\u8c01\u5c06\u6211\u653e\u5728\u5c0f\u8239\u4e4b\u4e2d\uff1f\u8fde\u4e01\u5927\u54e5\u4e5f\u4e00\u8d77\u6765\u4e86\uff1f\u201d\u4ed6\u5bf9\u81ea\u5df1\u7684\u751f\u6b7b\u5df2\u5e76\u4e0d\u5982\u4f55\u5173\u6000\uff0c\u4f46\u4e01\u5178\u7684\u5c38\u4f53\u4e5f\u548c\u4ed6\u5728\u4e00\u8d77\uff0c\u8fd9\u4e8b\u5374\u4e0d\u80fd\u4e0d\u4ee4\u4ed6\u8877\u5fc3\u611f\u6fc0\u3002 \u3000\u3000\u82e6\u82e6\u601d\u7d22\uff0c\u60f3\u5f97\u5934\u4e5f\u75db\u4e86\uff0c\u59cb\u7ec8\u6ca1\u80fd\u60f3\u51fa\u534a\u70b9\u7aef\u502a\u3002\u4ed6\u7aed\u529b\u8ffd\u5fc6\u8fc7\u53bb\u4e00\u5929\u4e2d\u6240\u53d1\u751f\u7684\u4e8b\uff0c\u60f3\u5230\u4e07\u572d\u5251\u780d\u4e01\u5178\u3001\u81ea\u5df1\u7aed\u529b\u627c\u4ed6\u54bd\u5589\u4e4b\u540e\uff0c\u5c31\u518d\u4e5f\u60f3\u4e0d\u4e0b\u53bb\u4e86\u3002\u4ee5\u540e\u7684\u4e8b\u60c5\uff0c\u8111\u6d77\u4e2d\u4fbf\u662f\u4e00\u7247\u7a7a\u767d\u3002 \u3000\u3000\u4e00\u4fa7\u5934\u95f4\uff0c\u989d\u89d2\u649e\u7740\u4e86\u4e00\u5305\u786c\u786c\u7684\u4e1c\u897f\uff0c\u90a3\u662f\u7528\u7ef8\u5e03\u5305\u7740\u7684\u4e00\u4e2a\u5c0f\u5c0f\u5305\u88b1\u3002\u4ed6\u5fc3\u4e2d\u4e00\u559c\uff0c\u6599\u5f97\u8fd9\u5305\u88b1\u4e4b\u4e2d\u5b9a\u6709\u7ebf\u7d22\u53ef\u5bfb\uff0c\u98a4\u6296\u7740\u53cc\u624b\u6253\u4e86\u5f00\u6765\uff0c\u53ea\u89c1\u5305\u91cc\u6709\u4e94\u516d\u952d\u788e\u94f6\u5b50\uff0c\u8fd8\u6709\u56db\u4ef6\u5973\u5b50\u9996\u9970\uff1a\u4e00\u6735\u73e0\u82b1\u3001\u4e00\u53ea\u91d1\u956f\u3001\u4e00\u4e2a\u91d1\u9879\u5708\u3001\u4e00\u53ea\u5b9d\u77f3\u6212\u6307\u3002\u53e6\u5916\u662f\u5c0f\u5b69\u5b50\u9888\u4e2d\u6240\u6302\u7684\u4e00\u4e2a\u91d1\u9501\u7247\uff0c\u9501\u7247\u4e0a\u7684\u91d1\u94fe\u662f\u7ed9\u4eba\u5306\u5fd9\u62c9\u65ad\u7684\uff0c\u94fe\u5b50\u65ad\u5904\u8fd8\u94a9\u4e0a\u4e86\u4e00\u5c0f\u5757\u8863\u886b\u7684\u788e\u7247\uff0c\u663e\u7136\uff0c\u90a3\u662f\u4e34\u65f6\u4ece\u5c0f\u5b69\u9888\u4e2d\u626f\u4e86\u4e0b\u6765\uff0c\u5012\u50cf\u662f\u76d7\u8d3c\u62e6\u8def\u6253\u52ab\u800c\u5f97\u6765\u4e00\u822c\u3002\u91d1\u9501\u7247\u4e0a\u523b\u7740\u2018\u5fb7\u5bb9\u53cc\u8302\u201d\u56db\u4e2a\u5b57\u3002\u72c4\u4e91\u6ca1\u8bfb\u8fc7\u591a\u5c11\u4e66\uff0c\u5b57\u867d\u8bc6\u5f97\uff0c\u5374\u4e0d\u61c2\u8fd9\u56db\u4e2a\u5b57\u662f\u751a\u4e48\u610f\u601d\uff0c\u5fc3\u60f3\uff1a\u201c\u662f\u90a3\u5c0f\u5b69\u7684\u540d\u5b57\u7f62\uff1f\u201d \u3000\u3000\u4ed6\u62e8\u5f04\u8fd9\u4e94\u4ef6\u9996\u9970\uff0c\u8f83\u4e4b\u9002\u624d\u672a\u89c1\u5230\u90a3\u5305\u88b1\u4e4b\u65f6\uff0c\u5fc3\u4e2d\u53cd\u66f4\u591a\u4e86\u51e0\u5206\u80e1\u6d82\uff1a\u201c\u94f6\u5b50\u548c\u9996\u9970\uff0c\u81ea\u7136\u662f\u642d\u6551\u6211\u7684\u90a3\u4eba\u7ed9\u7684\uff0c\u4ee5\u4fbf\u5c0f\u821f\u9760\u4e86\u5cb8\u540e\u3002\u6211\u597d\u6709\u94b1\u4e70\u996d\u5403\u3002\u53ef\u662f\uff0c\u5230\u5e95\u662f\u8c01\u7ed9\u7684\u5462\uff1f\u9996\u9970\u4e0d\u662f\u5e08\u59b9\u7684\uff0c\u6211\u53ef\u4ece\u6765\u6ca1\u89c1\u5979\u6234\u8fc7\u3002\u201d \u3000\u3000\u6d69\u6d69\u6c5f\u6c34\uff0c\u9001\u7740\u4e00\u53f6\u5c0f\u821f\u987a\u6f6e\u800c\u4e0b\u3002\u8fd9\u4e00\u5929\u4e2d\uff0c\u72c4\u4e91\u53ea\u662f\u82e6\u82e6\u601d\u7d22\uff1a\u201c\u662f\u8c01\u7ed9\u6211\u5305\u624e\u4e86\u4f24\u53e3\uff1f\u662f\u8c01\u7ed9\u4e86\u6211\u94f6\u4e24\u9996\u9970\uff1f\u201d \u3000"}),showOnHomepage:!0},{name:"\u4e94\u3000\u8001\u9f20\u6c64",content:(0,a.jsx)(a.Fragment,{children:"\u6c5f\u9675\u4ee5\u4e0b\u5730\u52bf\u5e73\u5766\uff0c\u957f\u6c5f\u5728\u6e58\u9102\u4e4b\u95f4\u8fc2\u8ff4\u66f2\u6298\uff0c\u6d69\u6d69\u4e1c\u6d41\uff0c\u5c0f\u821f\u968f\u7740\u6c5f\u6c34\u7f13\u7f13\u98d8\u6d6e\u3002\u773c\u89c1\u957f\u6c5f\u4e24\u5cb8\u4e00\u4e2a\u4e2a\u5e02\u9547\u6751\u843d\u4ece\u821f\u65c1\u7ecf\u8fc7\uff0c\u4ece\u4e0a\u6e38\u4e0b\u6765\u7684\u8239\u53ea\u6709\u5e06\u6709\u6a79\uff0c\u4e00\u8258\u8258\u7684\u8d8a\u8fc7\u4e86\u4ed6\u3002\u8239\u4e0a\u7684\u4eba\u7ecf\u8fc7\u5c0f\u821f\u65f6\uff0c\u5bf9\u957f\u987b\u957f\u53d1\u3001\u6ee1\u8138\u8840\u6c61\u7684\u72c4\u4e91\u90fd\u6295\u4ee5\u597d\u5947\u60ca\u8bb6\u7684\u773c\u8272\u3002 \u3000\u3000\u5c06\u8fd1\u508d\u665a\u65f6\u5206\uff0c\u72c4\u4e91\u7ec8\u4e8e\u6709\u4e86\u4e9b\u529b\u6c14\uff0c\u540c\u65f6\u809a\u5b50\u91cc\u5495\u5495\u7684\u54cd\u4e2a\u4e0d\u505c\uff0c\u4e5f\u89c9\u997f\u5f97\u5389\u5bb3\u3002\u4ed6\u5750\u8d77\u8eab\u6765\uff0c\u62ff\u8d77\u4e00\u5757\u8239\u677f\uff0c\u5c06\u5c0f\u821f\u6162\u6162\u5212\u5411\u5317\u5cb8\uff0c\u60f3\u5230\u5c0f\u996d\u5e97\u4e2d\u4e70\u4e9b\u996d\u5403\u3002\u504f\u751f\u8fd9\u4e00\u5e26\u751a\u662f\u8352\u51c9\uff0c\u89c1\u4e0d\u5230\u4e00\u5bb6\u4eba\u5bb6\u3002\u5c0f\u821f\u987a\u6c5f\u8f6c\u4e86\u4e2a\u5f2f\uff0c\u53ea\u89c1\u67f3\u836b\u4e0b\u7cfb\u7740\u4e09\u8258\u6e14\u8239\uff0c\u8239\u4e0a\u708a\u70df\u5347\u8d77\u3002\u4ed6\u5c0f\u821f\u6d41\u8fd1\u6e14\u8239\u65f6\uff0c\u53ea\u542c\u5f97\u8239\u68a2\u4e0a\u9505\u5b50\u4e2d\u714e\u9c7c\u4e4b\u58f0\u5431\u5431\u4ef7\u54cd\uff0c\u9999\u6c14\u76f4\u9001\u8fc7\u6765\u3002 \u3000\u3000\u4ed6\u5c06\u5c0f\u821f\u5212\u8fc7\u53bb\uff0c\u5411\u8239\u68a2\u4e0a\u7684\u8001\u6e14\u4eba\u9053\uff1a\u201c\u6253\u9c7c\u7684\u8001\u4f2f\uff0c\u5356\u4e00\u5c3e\u9c7c\u7ed9\u6211\u5403\uff0c\u884c\u5417\uff1f\u201d\u90a3\u8001\u6e14\u4eba\u89c1\u4ed6\u5f62\u76f8\u53ef\u6016\uff0c\u5fc3\u4e2d\u5bb3\u6015\uff0c\u672c\u662f\u4e0d\u613f\uff0c\u5374\u4e0d\u6562\u62d2\u7edd\uff0c\u4fbf\u9053\uff1a\u201c\u662f\uff0c\u662f\uff01\u201d\u5c06\u4e00\u5c3e\u714e\u719f\u4e86\u7684\u9752\u9c7c\u76db\u5728\u7897\u4e2d\uff0c\u9694\u8239\u9001\u4e86\u8fc7\u6765\u3002\u72c4\u4e91\u9053\uff1a\u201c\u82e5\u6709\u767d\u996d\uff0c\u76ca\u53d1\u4e70\u4e00\u7897\u5403\u3002\u201d\u90a3\u8001\u6e14\u4eba\u9053\uff1a\u201c\u662f\uff0c\u662f\uff01\u201d\u76db\u4e86\u4e00\u5927\u7897\u7cd9\u7c73\u996d\u7ed9\u4ed6\uff0c\u996d\u4e2d\u6df7\u7740\u4e00\u5927\u534a\u756a\u85af\u3001\u9ad8\u7cb1\u3002 \u3000\u3000\u72c4\u4e91\u4e09\u6252\u4e24\u62e8\uff0c\u4fbf\u5c06\u4e00\u5927\u7897\u996d\u5403\u5149\u4e86\uff0c\u6b63\u5f85\u5f00\u53e3\u518d\u8981\uff0c\u5ffd\u542c\u5f97\u5cb8\u4e0a\u4e00\u4e2a\u5636\u54d1\u7684\u58f0\u97f3\u559d\u9053\uff1a\u201c\u6e14\u5bb6\uff01\u6709\u5927\u9c7c\u62ff\u51e0\u6761\u4e0a\u6765\u3002\u201d \u3000\u3000\u72c4\u4e91\u4fa7\u5934\u770b\u53bb\uff0c\u89c1\u662f\u4e2a\u6781\u9ad8\u6781\u7626\u7684\u548c\u5c1a\uff0c\u4e24\u773c\u751a\u5927\uff0c\u6e5b\u6e5b\u6709\u5149\u3002\u72c4\u4e91\u767b\u65f6\u5fc3\u4e2d\u6253\u4e86\u4e2a\u7a81\uff0c\u8ba4\u5f97\u662f\u90a3\u665a\u5230\u72f1\u4e2d\u6765\u548c\u4e01\u5178\u4e3a\u96be\u7684\u4e94\u50e7\u4e4b\u4e00\uff0c\u60f3\u4e86\u4e00\u60f3\uff0c\u8bb0\u8d77\u4e01\u5178\u8bf4\u8fc7\u4ed6\u7684\u540d\u5b57\uff0c\u53eb\u505a\u5b9d\u8c61\u3002\u90a3\u665a\u4e01\u5178\u51fb\u6bd9\u4e24\u50e7\uff0c\u91cd\u4f24\u4e24\u50e7\uff0c\u8fd9\u5b9d\u8c61\u5374\u89c1\u673a\u9003\u8d70\u4e86\u3002 \u3000\u3000\u72c4\u4e91\u518d\u4e5f\u4e0d\u6562\u5411\u4ed6\u591a\u770b\u4e00\u773c\u3002\u4e01\u5178\u8bf4\u8fd9\u4e2a\u548c\u5c1a\u6b66\u529f\u4e86\u5f97\uff0c\u66fe\u53ee\u5631\u4ed6\u65e5\u540e\u82e5\u662f\u9047\u4e0a\u4e86\uff0c\u52a1\u987b\u5c0f\u5fc3\u3002\u8981\u662f\u7ed9\u8fd9\u5b9d\u8c61\u548c\u5c1a\u53d1\u89c9\u4e86\u4e01\u5178\u7684\u5c38\u8eab\uff0c\u90a3\u53ef\u7cdf\u4e86\u3002\u4ed6\u53cc\u624b\u6367\u7740\u996d\u7897\uff0c\u9976\u662f\u4ed6\u5e76\u975e\u80c6\u5c0f\u6015\u6b7b\u4e4b\u8f88\uff0c\u5374\u4e5f\u5fcd\u4e0d\u4f4f\u4e00\u9897\u5fc3\u6026\u6026\u4e71\u8df3\uff0c\u624b\u81c2\u4e5f\u4e0d\u7981\u5fae\u5fae\u53d1\u6296\uff0c\u5fc3\u4e2d\u53ea\u8bf4\uff1a\u201c\u522b\u53d1\u6296\uff0c\u522b\u53d1\u6296\uff0c\u53ef\u4e0d\u80fd\u9732\u51fa\u9a6c\u811a\uff01\u201d\u4f46\u8d8a\u60f3\u9547\u5b9a\uff0c\u8d8a\u662f\u7ba1\u4e0d\u4f4f\u81ea\u5df1\u3002 \u3000\u3000\u53ea\u542c\u90a3\u8001\u6e14\u4eba\u9053\uff1a\u201c\u4eca\u65e5\u6253\u7684\u9c7c\u90fd\u5356\u4e86\uff0c\u6ca1\u9c7c\u5566\u3002\u201d\u5b9d\u8c61\u6012\u9053\uff1a\u201c\u8c01\u8bf4\u6ca1\u9c7c\uff1f\u6211\u997f\u5f97\u614c\u4e86\uff0c\u5feb\u5f04\u51e0\u6761\u6765\uff01\u6ca1\u5927\u9c7c\uff0c\u5c0f\u7684\u4e5f\u6210\u3002\u201d\u90a3\u8001\u6e14\u4eba\u9053\uff1a\u201c\u771f\u7684\u6ca1\u6709\uff01\u6211\u6709\u9c7c\uff0c\u4f60\u6709\u94f6\u5b50\uff0c\u5e72\u4e48\u4e0d\u5356\uff1f\u201d\u8bf4\u7740\u63d0\u8d77\u9c7c\u7bd3\uff0c\u7ffb\u8fc7\u6765\u4e00\u5012\uff0c\u7bd3\u5e95\u5411\u5929\uff0c\u7bd3\u4e2d\u679c\u7136\u65e0\u9c7c\u3002 \u3000\u3000\u5b9d\u8c61\u5df2\u5341\u5206\u9965\u997f\uff0c\u89c1\u72c4\u4e91\u8eab\u65c1\u4e00\u6761\u716e\u719f\u7684\u5927\u9c7c\uff0c\u8fd8\u53ea\u5403\u4e86\u4e00\u5c0f\u534a\uff0c\u4fbf\u53eb\uff1a\u201c\u5140\u90a3\u6c49\u5b50\uff0c\u4f60\u90a3\u91cc\u6709\u9c7c\u6ca1\u6709\uff1f\u201d \u3000\u3000\u72c4\u4e91\u5fc3\u4e2d\u614c\u4e71\uff0c\u89c1\u4ed6\u5411\u81ea\u5df1\u8bf4\u8bdd\uff0c\u53ea\u9053\u4ed6\u5df2\u8ba4\u51fa\u4e86\u81ea\u5df1\uff0c\u66f4\u4e0d\u7b54\u8bdd\uff0c\u4e3e\u8d77\u8239\u677f\uff0c\u5f80\u6c5f\u8fb9\u7684\u67f3\u6811\u6839\u4e0a\u7528\u529b\u4e00\u63a8\uff0c\u5c0f\u821f\u4fbf\u5411\u6c5f\u4e2d\u8361\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u5b9d\u8c61\u6012\u9053\uff1a\u201c\u8d3c\u6c49\u5b50\uff0c\u6211\u95ee\u4f60\u6709\u9c7c\u6ca1\u6709\uff0c\u5e72\u4e48\u9003\u8d70\uff1f\u201d \u3000\u3000\u72c4\u4e91\u542c\u4ed6\u7834\u53e3\u5927\u9a82\uff0c\u66f4\u662f\u5bb3\u6015\uff0c\u7528\u529b\u5212\u52a8\u8239\u677f\uff0c\u5c06\u5c0f\u821f\u8361\u5411\u6c5f\u5fc3\u3002\u5b9d\u8c61\u4ece\u5cb8\u65c1\u62fe\u8d77\u4e00\u5757\u77f3\u5934\uff0c\u7528\u529b\u5411\u4ed6\u63b7\u53bb\u3002\u72c4\u4e91\u89c1\u77f3\u5934\u63b7\u6765\uff0c\u5f53\u5373\u4fef\u8eab\uff0c\u4f46\u542c\u5f97\u98ce\u58f0\u52b2\u6025\uff0c\u77f3\u5934\u4ece\u5934\u9876\u63a0\u8fc7\uff0c\u535c\u7684\u4e00\u54cd\uff0c\u6389\u5165\u4e86\u6c5f\u4e2d\uff0c\u6c34\u82b1\u6e85\u5f97\u8001\u9ad8\u3002 \u3000\u3000\u5b9d\u8c61\u89c1\u4ed6\u8eb2\u907f\u77f3\u5934\u65f6\u8eab\u6cd5\u5229\u843d\uff0c\u4fe8\u7136\u662f\u7ec3\u5bb6\u5b50\u6a21\u6837\uff0c\u51b3\u975e\u5bfb\u5e38\u6e14\u4eba\u8239\u592b\uff0c\u5fc3\u4e0b\u8d77\u7591\uff0c\u559d\u9053\uff1a\u201c\u4ed6\u5988\u7684\u5feb\u5212\u56de\u6765\uff0c\u8981\u4e0d\u7136\u6211\u8981\u4e86\u4f60\u7684\u72d7\u547d\uff01\u201d \u3000\u3000\u72c4\u4e91\u54ea\u53bb\u7406\u4ed6\uff0c\u62da\u547d\u7684\u4f7f\u529b\u5212\u8239\u3002\u5b9d\u8c61\u8e72\u4f4e\u8eab\u5b50\uff0c\u53f3\u624b\u62fe\u8d77\u4e00\u5757\u77f3\u5934\uff0c\u4fbf\u5373\u63b7\u51fa\uff0c\u8ddf\u7740\u5de6\u624b\u53c8\u63b7\u4e00\u5757\u3002\u72c4\u4e91\u624b\u4e0a\u5212\u8239\uff0c\u53cc\u773c\u5168\u795e\u8d2f\u6ce8\u7684\u77a7\u7740\u77f3\u5757\u7684\u6765\u8def\u3002\u7b2c\u4e00\u5757\u4fa7\u8eab\u907f\u8fc7\uff0c\u7b2c\u4e8c\u5757\u6765\u5f97\u6781\u4f4e\uff0c\u8d34\u7740\u8239\u8eab\u5e73\u5e73\u98de\u5230\uff0c\u5f53\u5373\u5367\u5012\uff0c\u8eba\u5728\u8231\u5e95\u3002\u8fd9\u5176\u95f4\u53ea\u662f\u5bf8\u8bb8\u4e4b\u5dee\uff0c\u773c\u524d\u53ea\u89c1\u9ed1\u9edd\u9edd\u7684\u4e00\u5757\u4e1c\u897f\u6025\u901f\u98de\u8fc7\uff0c\u5389\u98ce\u522e\u5f97\u9f3b\u5b50\u548c\u8138\u988a\u9690\u9690\u751f\u75bc\u3002\u4ed6\u521a\u4e00\u5750\u8d77\uff0c\u7b2c\u4e09\u5757\u77f3\u5934\u53c8\u5230\uff0c\u62cd\u7684\u4e00\u54cd\uff0c\u6253\u5728\u8239\u5934\uff0c\u767b\u65f6\u6728\u5c51\u7eb7\u98de\uff0c\u8239\u5934\u4e0a\u7f3a\u4e86\u4e00\u5757\u3002 \u3000\u3000\u5b9d\u8c61\u89c1\u72c4\u4e91\u95ea\u907f\u7075\u6d3b\uff0c\u5c0f\u8239\u987a\u7740\u6c5f\u6c34\u98d8\u884c\uff0c\u8d8a\u6765\u8d8a\u8fdc\uff0c\u5f53\u5373\u7528\u529b\u63b7\u51fa\u4e24\u5757\u77f3\u5934\uff0c\u5374\u5bf9\u51c6\u4e86\u5c0f\u8239\u3002\u4ed6\u82e5\u4e00\u51fa\u624b\u4fbf\u5373\u63b7\u8239\uff0c\u5c0f\u5c0f\u4e00\u8258\u6728\u8239\u7acb\u65f6\u4fbf\u4f1a\u6d1e\u7a7f\u6c89\u6ca1\uff0c\u4f46\u8fd9\u65f6\u76f8\u8ddd\u5df2\u8fdc\uff0c\u63a5\u8fde\u51e0\u5757\u77f3\u5934\u867d\u90fd\u6253\u5728\u8239\u4e0a\uff0c\u5374\u52b2\u529b\u5df2\u8870\uff0c\u53ea\u6253\u788e\u4e86\u4e9b\u8239\u8237\u3001\u8239\u677f\u800c\u5df2\u3002 \u3000\u3000\u7a7a\u8c61\u773c\u89c1\u5236\u4ed6\u4e0d\u4f4f\uff0c\u5927\u6012\u559d\u9a82\uff0c\u8fdc\u8fdc\u89c1\u5230\u6c5f\u98ce\u5439\u62c2\uff0c\u72c4\u4e91\u7684\u4e71\u987b\u957f\u53d1\u4e0d\u4f4f\u98de\u821e\uff0c\u731b\u5730\u60f3\u8d77\uff1a\u201c\u8fd9\u4eba\u5012\u4f3c\u4e2a\u8d8a\u72f1\u7684\u56da\u5f92\u3002 \u3000\u3000\u4e01\u5178\u5728\u8346\u5dde\u5e9c\u8d8a\u72f1\u9003\u8d70\uff0c\u6c5f\u6e56\u4e0a\u4f20\u5f97\u6cb8\u6cb8\u626c\u626c\u3002\u8bf4\u4e0d\u5b9a\u4ece\u8fd9\u56da\u5f99\u8eab\u4e0a\uff0c\u5012\u53ef\u6253\u542c\u5230\u4e01\u5178\u7684\u4e00\u4e9b\u8e2a\u8ff9\u3002\u201d\u60f3\u5230\u6b64\u5904\uff0c\u8d2a\u5ff5\u5927\u76db\uff0c\u6012\u706b\u5374\u7184\u4e86\uff0c\u53eb\u9053\uff1a\u201c\u6e14\u5bb6\uff0c\u6e14\u5bb6\uff0c\u5feb\u5212\u6211\u53bb\u8ffd\u4e0a\u4ed6\u3002\u201d \u3000\u3000\u4f46\u67f3\u6811\u4e0b\u4e09\u8258\u8239\u4e0a\u7684\u6e14\u4eba\u89c1\u4ed6\u98de\u77f3\u6253\u4eba\uff0c\u751a\u662f\u608d\u6076\uff0c\u65e9\u5df2\u6084\u6084\u89e3\u7f06\uff0c\u987a\u6d41\u800c\u4e0b\u3002\u5b9d\u8c61\u8fde\u58f0\u547c\u558a\uff0c\u5374\u6709\u8c01\u80af\u56de\u6765\u8f7d\u4ed6\uff1f \u3000\u3000\u5b9d\u8c61\u547c\u547c\u547c\u7684\u63b7\u51fa\u51e0\u4e2a\u77f3\u5934\uff0c\u6709\u4e00\u5757\u6253\u5728\u4e00\u540d\u6e14\u4eba\u5934\u4e0a\u3002\u90a3\u6e14\u4eba\u8111\u6d46\u8fdb\u88c2\uff0c\u5012\u649e\u5165\u6c5f\u3002\u5176\u4f59\u6e14\u4eba\u5413\u5f97\u9b42\u98de\u9b44\u6563\uff0c\u5212\u5f97\u66f4\u52a0\u5feb\u4e86\u3002 \u3000\u3000\u5b9d\u8c61\u6cbf\u7740\u6c5f\u5cb8\u75be\u8ffd\uff0c\u5feb\u6b65\u5954\u8dd1\uff0c\u7adf\u6bd4\u72c4\u4e91\u7684\u5c0f\u8239\u8fc5\u901f\u5f97\u591a\u3002\u5b9d\u8c61\u5728\u957f\u6c5f\u5317\u5cb8\u8ffd\u8d76\uff0c\u72c4\u4e91\u4e0d\u4f4f\u5212\u8239\u659c\u5411\u5357\u5cb8\u3002\u5b9d\u8c61\u867d\u8d76\u8fc7\u4e86\u4ed6\u5934\uff0c\u4f46\u548c\u5c0f\u8239\u4ecd\u662f\u8d8a\u79bb\u8d8a\u8fdc\u3002\u72c4\u4e91\u5fc3\u60f3\uff1a\u201c\u8981\u662f\u7ed9\u4ed6\u5728\u5cb8\u8fb9\u627e\u5230\u4e86\u4e00\u8258\u8239\uff0c\u903c\u5f97\u8244\u516c\u524d\u6765\u8d76\u6211\uff0c\u90a3\u5c31\u96be\u4ee5\u9003\u8131\u4ed6\u7684\u6bd2\u624b\u4e86\u3002\u201d\u60f6\u6025\u4e4b\u4e2d\uff0c\u53ea\u6709\u5583\u5583\u7977\u795d\uff1a\u201c\u4e01\u5927\u54e5\uff0c\u4e01\u5927\u54e5\uff0c\u4f60\u6b7b\u800c\u6709\u7075\uff0c\u53eb\u8fd9\u6076\u548c\u5c1a\u627e\u4e0d\u5230\u8239\u53ea\u3002\u201d \u3000\u3000\u957f\u6c5f\u4e2d\u4e0a\u4e0b\u8239\u53ea\u751a\u591a\uff0c\u5e78\u597d\u6cbf\u5317\u5cb8\u6570\u91cc\u5747\u65e0\u8239\u53ea\u505c\u6cca\u3002\u72c4\u4e91\u51fa\u5c3d\u5e73\u751f\u4e4b\u529b\uff0c\u5c06\u8239\u5212\u5230\u4e86\u5357\u5cb8\uff0c\u8fd9\u4e00\u5e26\u6c5f\u9762\u867d\u7136\u4e0d\u5bbd\uff0c\u4f46\u6811\u6728\u906e\u63a9\uff0c\u5b9d\u8c61\u5df2\u671b\u4e0d\u8fc7\u6765\uff0c\u4e8e\u662f\u5c06\u90a3\u5c0f\u5305\u88b1\u5f80\u6000\u91cc\u4e00\u63e3\uff0c\u62b1\u8d77\u4e01\u5178\u7684\u5c38\u8eab\uff0c\u4e0a\u5cb8\u4fbf\u884c\u3002\u7a81\u7136\u60f3\u8d77\u4e00\u4e8b\uff0c\u56de\u8fc7\u8eab\u6765\uff0c\u5c06\u5c0f\u8239\u7528\u529b\u5411\u6c5f\u5fc3\u63a8\u53bb\uff0c\u53ea\u76fc\u5b9d\u8c61\u9065\u9065\u671b\u6765\uff0c\u8fd8\u9053\u81ea\u5df1\u4ecd\u5728\u8239\u4e2d\uff0c\u4e00\u8def\u5411\u4e0b\u6e38\u8ffd\u53bb\u3002 \u3000\u3000\u4ed6\u614c\u4e0d\u62e9\u8def\u7684\u5411\u5357\u5954\u8dd1\uff0c\u53ea\u76fc\u79bb\u5f00\u6c5f\u8fb9\u8d8a\u8fdc\u8d8a\u597d\u3002\u5954\u5f97\u91cc\u8bb8\uff0c\u4e0d\u7531\u5f97\u53eb\u4e00\u58f0\u82e6\uff0c\u4f46\u89c1\u767d\u832b\u832b\u4e00\u7247\u6c34\u8272\uff0c\u5927\u6c5f\u5f53\u524d\uff0c\u539f\u6765\u957f\u6c5f\u6d41\u5230\u8fd9\u91cc\u7adf\u4e5f\u6298\u800c\u5411\u5357\u3002 \u3000\u3000\u4ed6\u6025\u5fd9\u8f6c\u8eab\uff0c\u89c1\u53f3\u9996\u6709\u5c0f\u5c0f\u4e00\u5ea7\u7834\u5e99\uff0c\u5f53\u5373\u62b1\u7740\u4e01\u5178\u7684\u5c38\u8eab\u8d70\u5230\u5e99\u524d\uff0c\u6b32\u5f85\u63a8\u95e8\u5165\u5185\uff0c\u7a81\u7136\u95f4\u819d\u95f4\u4e00\u8f6f\uff0c\u5750\u5012\u5728\u5730\uff0c\u518d\u4e5f\u7ad9\u4e0d\u8d77\u6765\u3002\u4ed6\u53d7\u4f24\u540e\u6d41\u8840\u751a\u591a\uff0c\u65e9\u5df2\u5341\u5206\u865a\u5f31\uff0c\u5212\u8239\u518d\u52a0\u4e0a\u62b1\u5c38\u5954\u8dd1\uff0c\u5b9e\u5df2\u7b4b\u75b2\u529b\u5c3d\uff0c\u534a\u70b9\u529b\u6c14\u4e5f\u6ca1\u6709\u4e86\u3002\u6323\u624e\u4e86\u4e24\u6b21\uff0c\u65e0\u6cd5\u5750\u8d77\uff0c\u53ea\u6709\u659c\u9760\u5728\u5730\u4e0b\u547c\u547c\u5598\u6c14\u3002\u4f46\u89c1\u5929\u8272\u6e10\u6697\uff0c\u5fc3\u4e0b\u7a0d\u6170\uff0c\u5fc3\u60f3\uff1a\u201c\u53ea\u6d88\u5230\u5f97\u591c\u665a\uff0c\u5b9d\u8c61\u90a3\u6076\u50e7\u603b\u662f\u4e0d\u80fd\u627e\u5230\u54b1\u4eec\u4e86\u3002\u201d\u8fd9\u65f6\u4e01\u5178\u867d\u7136\u5df2\u6b7b\uff0c\u4f46\u4ed6\u5fc3\u4e2d\uff0c\u4ecd\u7136\u5f53\u4ed6\u662f\u4eb2\u5bc6\u7684\u4f34\u4fa3\u4e00\u822c\u3002 \u3000\u3000\u5728\u5e99\u5916\u76f4\u8eba\u4e86\u5927\u534a\u4e2a\u65f6\u8fb0\uff0c\u529b\u6c14\u6e10\u590d\uff0c\u8fd9\u624d\u6323\u624e\u7740\u722c\u8d77\uff0c\u62b1\u7740\u4e01\u5178\u7684\u5c38\u8eab\u63a8\u95e8\u8fdb\u5e99\u3002\u89c1\u662f\u4e00\u5ea7\u571f\u5730\u5e99\uff0c\u6ce5\u5851\u7684\u571f\u5730\u795e\u77ee\u5c0f\u59d4\u7410\uff0c\u5f62\u8c8c\u751a\u662f\u6ed1\u7a3d\u3002\u72c4\u4e91\u4f24\u8d25\u4e4b\u4f59\uff0c\u89c1\u5230\u8fd9\u5c0f\u5c0f\u795e\u50cf\uff0c\u5ffd\u7136\u5fc3\u751f\u656c\u754f\uff0c\u606d\u606d\u656c\u656c\u7684\u8dea\u4e0b\uff0c\u5411\u795e\u50cf\u78d5\u4e86\u51e0\u4e2a\u5934\uff0c\u5fc3\u4e0b\u591a\u4e86\u51e0\u5206\u5b89\u6170\u3002 \u3000\u3000\u5750\u5728\u795e\u50cf\u5ea7\u524d\uff0c\u62b1\u5934\u5446\u5446\u77aa\u89c6\u7740\u8eba\u5728\u5730\u4e0b\u7684\u4e01\u5178\u3002\u5929\u8272\u4e00\u70b9\u70b9\u7684\u9ed1\u4e86\u4e0b\u6765\uff0c\u4ed6\u5fc3\u4e2d\u624d\u6e10\u6e10\u591a\u4e86\u51e0\u5206\u5e73\u5b89\u3002 \u3000\u3000\u4ed6\u5367\u5728\u4e01\u5178\u7684\u5c38\u8eab\u4e4b\u65c1\uff0c\u5c31\u50cf\u8fc7\u53bb\u51e0\u5e74\u4e2d\uff0c\u5728\u90a3\u5c0f\u5c0f\u7684\u7262\u623f\u91cc\u90a3\u6837\u3002 \u3000\u3000\u6ca1\u5230\u534a\u591c\uff0c\u5ffd\u7136\u4e0b\u8d77\u96e8\u6765\uff0c\u6dc5\u6dc5\u6ca5\u6ca5\u7684\uff0c\u4e00\u9635\u5927\uff0c\u4e00\u9635\u5c0f\u3002\u72c4\u4e91\u611f\u5230\u8eab\u4e0a\u5bd2\u51b7\uff0c\u7f29\u6210\u4e00\u56e2\uff0c\u9760\u5230\u4e01\u5178\u8eab\u65c1\uff0c\u7a81\u7136\u4e4b\u95f4\uff0c\u78b0\u5230\u4e86\u4e01\u5178\u51b0\u51b7\u51b7\u7684\u808c\u80a4\uff0c\u60f3\u5230\u4e01\u5927\u54e5\u5df2\u6b7b\uff0c\u518d\u4e5f\u4e0d\u80fd\u548c\u81ea\u5df1\u8bf4\u8bdd\uff0c\u80f8\u4e2d\u60b2\u82e6\uff0c\u4e24\u884c\u6cea\u6c34\u7f13\u7f13\u4ece\u9762\u988a\u4e0a\u6d41\u4e0b\u3002 \u3000\u3000\u7a81\u7136\u95f4\u96e8\u58f0\u4e2d\u4f20\u6765\u4e00\u9635\u8e22\u8df6\u3001\u8e22\u8df6\u7684\u811a\u6b65\u58f0\uff0c\u6b63\u662f\u5411\u571f\u5730\u5e99\u8d70\u6765\u3002\u90a3\u4eba\u8df5\u8e0f\u6ce5\u6cde\uff0c\u5374\u884c\u5f97\u6781\u5feb\u3002\u72c4\u4e91\u5403\u4e86\u4e00\u60ca\uff0c\u8033\u542c\u5f97\u90a3\u4eba\u8d8a\u8d70\u8d8a\u8fd1\uff0c\u5fd9\u5c06\u4e01\u5178\u7684\u5c38\u8eab\u5f80\u795e\u575b\u5e95\u4e0b\u4e00\u85cf\uff0c\u81ea\u5df1\u7f29\u8eab\u5230\u4e86\u795e\u9f9b\u4e4b\u540e\u3002 \u3000\u3000\u811a\u6b65\u58f0\u8d8a\u8fd1\uff0c\u72c4\u4e91\u7684\u5fc3\u8df3\u5f97\u8d8a\u5feb\uff0c\u53ea\u542c\u5f97\u5440\u7684\u4e00\u58f0\uff0c\u5e99\u95e8\u7ed9\u4eba\u63a8\u5f00\uff0c\u8ddf\u7740\u4e00\u4eba\u5492\u9a82\u8d77\u6765\uff1a\u201c\u5988\u5df4\u7f94\u5b50\u7684\uff0c\u8fd9\u8001\u8d3c\u4e0d\u77e5\u9003\u5230\u4e86\u54ea\u91cc\uff0c\u53c8\u4e0b\u8fd9\u822c\u5927\u96e8\uff0c\u6dcb\u5f97\u8001\u5b50\u5168\u8eab\u90fd\u6e7f\u4e86\u3002\u201d\u8fd9\u58f0\u97f3\u6b63\u662f\u5b9d\u8c61\uff0c\u51fa\u5bb6\u4eba\u5927\u9a82\u201c\u5988\u5df4\u7f94\u5b50\u7684\u201d\u5df2\u7136\u4e0d\u8be5\uff0c\u81ea\u79f0\u201c\u8001\u5b50\u201d\uff0c\u66f4\u662f\u8352\u5510\u3002\u72c4\u4e91\u4e8e\u4e16\u52a1\u867d\u6240\u77e5\u4e0d\u591a\uff0c\u4f46\u8fd9\u51e0\u5e74\u6765\u65e5\u5e38\u542c\u4e01\u5178\u8bb2\u8bba\u6c5f\u6e56\u89c1\u95fb\uff0c\u4e5f\u5df2\u4e0d\u662f\u6614\u5e74\u90a3\u4e2a\u6d51\u5669\u65e0\u77e5\u7684\u4e61\u4e0b\u5c11\u5e74\uff0c\u5fc3\u60f3\uff1a\u201c\u8fd9\u5b9d\u8c61\u867d\u4f5c\u548c\u5c1a\u6253\u626e\uff0c\u4f46\u5403\u8364\u6740\u4eba\uff0c\u7edd\u65e0\u987e\u5fcc\uff0c\u591a\u534a\u662f\u4e2a\u51f6\u608d\u4e4b\u6781\u51f6\u5927\u76d7\u3002\u201d \u3000\u3000\u53ea\u542c\u5b9d\u8c61\u53e3\u4e2d\u6c61\u8a00\u79fd\u8bed\u8d8a\u6765\u8d8a\u591a\uff0c\u9a82\u4e86\u4e00\u9635\uff0c\u817e\u7684\u4e00\u58f0\uff0c\u4fbf\u5728\u795e\u575b\u524d\u5750\u5012\uff0c\u8ddf\u7740\u745f\u745f\u6709\u58f0\uff0c\u542c\u5f97\u51fa\u4ed6\u5c06\u5168\u8eab\u6e7f\u8863\u670d\u90fd\u8131\u4e86\u4e0b\u6765\uff0c\u5230\u6bbf\u89d2\u53bb\u7ede\u5e72\u4e86\uff0c\u642d\u5728\u795e\u575b\u8fb9\u4e0a\uff0c\u5367\u5012\u5728\u5730\uff0c\u4e0d\u4e45\u9f3e\u58f0\u5373\u8d77\uff0c\u7adf\u81ea\u7761\u719f\u4e86\u3002 \u3000\u3000\u72c4\u4e91\u5fc3\u60f3\uff1a\u201c\u8fd9\u6076\u50e7\u8131\u5f97\u8d64\u6761\u6761\u5730\uff0c\u5728\u795e\u50cf\u4e4b\u524d\u7761\u89c9\uff0c\u5c82\u4e0d\u7f6a\u8fc7\uff1f\u201d\u53c8\u60f3\uff1a\u201c\u6211\u8d81\u6b64\u673a\u4f1a\uff0c\u6367\u5757\u5927\u77f3\u7838\u6b7b\u4e86\u4ed6\uff0c\u4ee5\u514d\u660e\u5929\u5927\u7978\u4e34\u5934\u3002\u201d\u4f46\u4ed6\u5b9e\u4e0d\u613f\u968f\u4fbf\u6740\u4eba\uff0c\u53c8\u77e5\u5b9d\u8c61\u7684\u6b66\u529f\u80dc\u8fc7\u81ea\u5df1\u5341\u500d\uff0c\u82e5\u4e0d\u80fd\u4e00\u51fb\u7838\u6b7b\uff0c\u53ea\u987b\u4ed6\u7a0d\u4f59\u8fd8\u624b\u4e4b\u529b\uff0c\u81ea\u5df1\u52bf\u5fc5\u6027\u547d\u96be\u4fdd\u3002 \u3000\u3000\u8fd9\u65f6\u4ed6\u5018\u82e5\u4ece\u540e\u9662\u6084\u6084\u9003\u8d70\uff0c\u5b9d\u8c61\u5b9a\u7136\u4e0d\u4f1a\u77e5\u89c9\uff0c\u4f46\u4e01\u5178\u7684\u5c38\u8eab\u662f\u5728\u795e\u575b\u5e95\u4e0b\uff0c\u51b3\u8ba1\u4e0d\u80fd\u820d\u4e4b\u800c\u53bb\uff0c\u4e00\u642c\u52a8\u7acb\u65f6\u4fbf\u60ca\u52a8\u4e86\u6076\u50e7\u3002\u8033\u542c\u5f97\u5ead\u4e2d\u96e8\u6c34\u70b9\u70b9\u6ef4\u6ef4\u7684\u54cd\u4e2a\u4e0d\u4f4f\uff0c\u5fc3\u4e0b\u5f77\u5fa8\u65e0\u8ba1\uff0c\u53ea\u76fc\u660e\u6668\u96e8\u6b62\uff0c\u5b9d\u8c61\u79bb\u6b64\u4ed6\u53bb\u3002\u4f46\u542c\u6765\u8fd9\u96e8\u663e\u662f\u4e0d\u4f1a\u4fbf\u6b47\u3002\u5230\u5f97\u5929\u660e\uff0c\u5b9d\u8c61\u5982\u4e0d\u80af\u5192\u96e8\u51fa\u5e99\uff0c\u81ea\u4f1a\u5728\u5e99\u4e2d\u4e1c\u5bfb\u897f\u627e\uff0c\u975e\u7ed9\u4ed6\u89c1\u5230\u5c38\u4f53\u4e0d\u53ef\u3002\u867d\u662f\u5982\u6b64\uff0c\u5fc3\u4e2d\u8fd8\u662f\u5b58\u4e86\u4fa5\u5e78\u4e4b\u60f3\uff1a\u201c\u8bf4\u4e0d\u5b9a\u8fd9\u96e8\u5230\u5929\u4eae\u65f6\u4fbf\u6b62\u4e86\uff0c\u8fd9\u6076\u50e7\u6025\u4e8e\u8ffd\u6211\uff0c\u5306\u5306\u4fbf\u51fa\u5e99\u53bb\u3002\u201d \u3000\u3000\u5ffd\u7136\u95f4\u60f3\u8d77\u4e00\u4e8b\uff1a\u201c\u4ed6\u8fdb\u6765\u65f6\u7834\u53e3\u5927\u9a82\uff0c\u8bf4\u4e0d\u77e5\u90a3\u2018\u8001\u8d3c\u2019\u9003\u5230\u4e86\u54ea\u91cc\u3002\u6211\u5e74\u7eaa\u53c8\u4e0d\u8001\uff0c\u4e3a\u751a\u4e48\u53eb\u6211\u2018\u8001\u8d3c\u2019\uff1f\u96be\u9053\u4ed6\u53c8\u5728\u53e6\u5916\u8ffd\u8d76\u4e00\u4e2a\u8001\u4eba\uff1f\u201d\u60f3\u4e86\u4e00\u4f1a\uff0c\u731b\u5730\u9192\u609f\uff1a\u201c\u554a\uff0c\u662f\u4e86\uff0c\u6211\u6ee1\u5934\u957f\u53d1\uff0c\u6ee1\u8138\u957f\u987b\uff0c\u6570\u5e74\u4e0d\u5243\uff0c\u65c1\u4eba\u77a7\u6765\u81ea\u7136\u662f\u4e2a\u8001\u4eba\u4e86\u3002\u4ed6\u9a82\u6211\u662f\u2018\u8001\u8d3c\u2019\uff0c\u563f\u563f\uff0c\u9a82\u6211\u662f\u2018\u8001\u8d3c\u2019\uff01\u201d\u60f3\u5230\u4e86\u8fd9\u91cc\uff0c\u4f38\u624b\u53bb\u6478\u4e86\u6478\u816e\u8fb9\u4e71\u8349\u822c\u7684\u80e1\u5b50\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u62cd\u7684\u4e00\u58f0\u54cd\uff0c\u5b9d\u8c61\u7ffb\u4e86\u4e2a\u8f6c\u8eab\u3002\u4ed6\u7761\u68a6\u4e2d\u4e00\u811a\u8e22\u5230\u795e\u575b\u5e95\u4e0b\uff0c\u6b63\u597d\u8e22\u4e2d\u4e01\u5178\u7684\u5c38\u8eab\u3002\u4ed6\u4e00\u89c9\u60c5\u52bf\u6709\u5f02\uff0c\u7acb\u5373\u9192\u89c9\uff0c\u53ea\u9053\u795e\u575b\u5e95\u4e0b\u4f0f\u6709\u654c\u4eba\uff0c\u9ed1\u6697\u4e2d\u4e5f\u4e0d\u77e5\u5e99\u4e2d\u6709\u591a\u5c11\u4eba\u57cb\u4f0f\uff0c\u62a2\u8d77\u8eab\u65c1\u5355\u5200\uff0c\u524d\u540e\u5de6\u53f3\u8fde\u780d\u516d\u5200\uff0c\u6559\u654c\u4eba\u6b3a\u4e0d\u8fd1\u8eab\u6765\uff0c\u559d\u9053\uff1a\u201c\u662f\u8c01\uff1f\u5988\u5df4\u7f94\u5b50\u7684\uff0c\u8d3c\u738b\u516b\u86cb\uff01\u201d\u8fde\u9a82\u6570\u58f0\uff0c\u4e0d\u542c\u6709\u4eba\u7b54\u5e94\uff0c\u5c4f\u606f\u4e0d\u8bed\uff0c\u4ecd\u662f\u4e0d\u542c\u89c1\u6709\u4eba\u3002 \u3000\u3000\u5b9d\u8c61\u9ed1\u6697\u4e2d\u8fde\u780d\u5341\u4e94\u516d\u5200\uff0c\u56db\u9762\u516b\u65b9\u90fd\u780d\u904d\u4e86\uff0c\u6b63\u662f\u201c\u591c\u6218\u516b\u65b9\u5f0f\u201d\uff0c\u98de\u8d77\u4e00\u8db3\uff0c\u7830\u7684\u4e00\u58f0\uff0c\u5c06\u795e\u575b\u8e22\u5012\uff0c\u6325\u5200\u780d\u843d\uff0c\u62cd\u7684\u4e00\u58f0\u8f7b\u54cd\uff0c\u6df7\u6709\u9aa8\u9abc\u788e\u88c2\u4e4b\u58f0\uff0c\u5df2\u780d\u4e2d\u4e86\u4e01\u5178\u5c38\u4f53\u3002 \u3000\u3000\u72c4\u4e91\u542c\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u5b9d\u8c61\u662f\u5728\u5200\u780d\u4e01\u5178\u3002\u867d\u7136\u4e01\u5178\u5df2\u6b7b\uff0c\u65e9\u5df2\u65e0\u77e5\u65e0\u89c9\uff0c\u4f46\u5728\u72c4\u4e91\u5fc3\u4e2d\uff0c\u90a3\u4ecd\u662f\u4ed6\u81f3\u656c\u81f3\u7231\u7684\u4e49\u5144\uff0c\u8fd9\u4e00\u5200\u4fbf\u5982\u662f\u780d\u5728\u81ea\u5df1\u8eab\u4e0a\u4e00\u822c\uff0c\u7acb\u65f6\u4fbf\u60f3\u51b2\u51fa\u53bb\u62da\u547d\uff0c\u4f46\u8fd9\u4e94\u5e74\u7684\u7262\u72f1\u6298\u78e8\uff0c\u5df2\u5c06\u8fd9\u6734\u5b9e\u5364\u83bd\u7684\u5c11\u5e74\u53d8\u6210\u4e2a\u9047\u4e8b\u60f3\u4e0a\u51e0\u60f3\u7684\u9752\u5e74\u3002\u521a\u4e00\u52a8\u5ff5\uff0c\u8ddf\u7740\u4fbf\u60f3\uff1a\u201c\u6211\u51b2\u51fa\u53bb\u548c\u4ed6\u53ae\u62da\uff0c\u9664\u4e86\u9001\u6389\u81ea\u5df1\u6027\u547d\uff0c\u66f4\u65e0\u522b\u6837\u7ed3\u679c\u3002\u4e01\u5927\u54e5\u548c\u51cc\u5c0f\u59d0\u5408\u846c\u7684\u5fc3\u613f\u4fbf\u4e0d\u80fd\u8fbe\u6210\u3002\u90a3\u5982\u4f55\u5bf9\u5f97\u8d77\u4ed6\uff1f\u201d \u3000\u3000\u5b9d\u8c61\u4e00\u5200\u780d\u4e2d\u4e01\u5178\u7684\u5c38\u8eab\uff0c\u4e0d\u95fb\u518d\u6709\u52a8\u9759\uff0c\u9ed1\u6697\u4e4b\u4e2d\u77a7\u4e0d\u900f\u534a\u70b9\u7aef\u502a\u3002\u4ed6\u8eab\u8fb9\u6240\u643a\u7684\u706b\u6298\u65e9\u5728\u5927\u96e8\u4e2d\u6d78\u6e7f\u4e86\uff0c\u65e0\u6cd5\u70b9\u706b\u6765\u77a7\u4e2a\u660e\u767d\uff0c\u4ed6\u6162\u6162\u4e00\u6b65\u4e00\u6b65\u7684\u5012\u9000\uff0c\u80cc\u5fc3\u9760\u4e0a\u4e86\u5899\u58c1\uff0c\u4ee5\u9632\u654c\u4eba\u81ea\u540e\u5077\u88ad\uff0c\u7136\u540e\u51dd\u795e\u503e\u542c\u3002 \u3000\u3000\u8fd9\u65f6\u4e24\u4eba\u4e4b\u95f4\u9694\u4e86\u4e00\u9053\u7167\u58c1\uff0c\u9664\u4e86\u96e8\u58f0\u6dc5\u6ca5\uff0c\u66f4\u65e0\u522b\u6837\u58f0\u606f\u3002 \u3000\u3000\u72c4\u4e91\u77e5\u9053\u53ea\u8981\u81ea\u5df1\u547c\u5438\u4e4b\u58f0\u7a0d\u91cd\uff0c\u7acb\u65f6\u4fbf\u9001\u4e86\u6027\u547d\uff0c\u53ea\u6709\u5c06\u6c14\u606f\u6536\u5f97\u6781\u4e3a\u5fae\u7ec6\uff0c\u7f13\u7f13\u5438\u8fdb\uff0c\u7f13\u7f13\u547c\u51fa\uff0c\u8111\u5b50\u4e2d\u5374\u98de\u5feb\u7684\u8f6c\u7740\u5ff5\u5934\uff1a\u201c\u518d\u8fc7\u4e00\u4e2a\u591a\u65f6\u8fb0\uff0c\u5929\u5c31\u660e\u4e86\uff0c\u8fd9\u6076\u50e7\u89c1\u5230\u4e01\u5927\u54e5\u7684\u5c38\u4f53\uff0c\u5fc5\u5b9a\u5927\u52a0\u7cdf\u8e4b\uff0c\u90a3\u4fbf\u5982\u4f55\u662f\u597d\uff1f\u201d \u3000\u3000\u4ed6\u8111\u5b50\u672c\u5c31\u7b97\u4e0d\u5f97\u7075\u6d3b\uff0c\u800c\u8981\u8bbe\u6cd5\u5728\u5b9d\u8c61\u624b\u4e0b\u4fdd\u5168\u4e01\u5178\u7684\u5c38\u4f53\uff0c\u66f4\u662f\u4e00\u4e2a\u6781\u5927\u7684\u96be\u9898\u3002\u4ed6\u82e6\u82e6\u601d\u7d22\uff0c\u5f53\u771f\u662f\u60f3\u7834\u4e86\u8111\u888b\u4e5f\u60f3\u4e0d\u51fa\u534a\u70b9\u4e3b\u610f\uff0c\u5fc3\u4e2d\u7126\u6025\u4e07\u5206\uff0c\u81ea\u6028\u81ea\u827e\uff1a\u201c\u72c4\u4e91\u554a\u72c4\u4e91\uff0c\u4f60\u8fd9\u7b28\u5bb6\u4f19\uff0c\u81ea\u7136\u662f\u60f3\u4e0d\u51fa\u4e3b\u610f\u3002\u5018\u82e5\u4e01\u5927\u54e5\u4e0d\u6b7b\uff0c\u4ed6\u81ea\u6709\u6cd5\u5b50\u3002\u201d\u60f6\u6025\u4e4b\u4e0b\uff0c\u4f38\u624b\u6293\u7740\u5934\u53d1\uff0c\u7528\u529b\u4e00\u626f\uff0c\u767b\u5bf9\u4fbf\u626f\u4e0b\u4e86\u516d\u4e03\u6839\u6765\u3002 \u3000\u3000\u7a81\u7136\u4e4b\u95f4\uff0c\u8111\u5b50\u4e2d\u51fa\u73b0\u4e86\u4e00\u4e2a\u5ff5\u5934\uff1a\u201c\u8fd9\u6076\u50e7\u53eb\u6211\u2018\u8001\u8d3c\u2019\u3002\u4ed6\u89c1\u6211\u6ee1\u8138\u80e1\u5b50\uff0c\u53ea\u9053\u6211\u662f\u4e2a\u8001\u4eba\u3002\u6211\u82e5\u5c06\u80e1\u5b50\u5243\u5f97\u5e72\u5e72\u51c0\u51c0\uff0c\u4ed6\u5c82\u975e\u5c31\u8ba4\u6211\u4e0d\u51fa\uff1f\u53ea\u662f\u8eab\u8fb9\u6ca1\u6709\u5243\u5200\uff0c\u600e\u80fd\u5243\u53bb\u8fd9\u6ee1\u8138\u80e1\u5b50\uff1f\u54fc\uff0c\u6211\u6b7b\u4e5f\u4e0d\u6015\uff0c\u96be\u9053\u8fd8\u6015\u75db\uff1f\u7528\u624b\u4e00\u6839\u6839\u62d4\u53bb\uff0c\u4e5f\u5c31\u662f\u4e86\u3002\u201d \u3000\u3000\u60f3\u5230\u4fbf\u505a\uff0c\u6478\u5230\u4e00\u6839\u6839\u80e1\u5b50\uff0c\u4e00\u6839\u6839\u7684\u8f7b\u8f7b\u62d4\u53bb\uff0c\u552f\u6050\u53d1\u51fa\u534a\u70b9\u58f0\u606f\uff0c\u5fc3\u60f3\uff1a\u201c\u5c31\u7b97\u90a3\u6076\u50e7\u8ba4\u6211\u4e0d\u51fa\uff0c\u4e5f\u4e0d\u8fc7\u4e0d\u6765\u6740\u6211\u800c\u5df2\uff0c\u6211\u53c8\u6709\u751a\u4e48\u6cd5\u5b50\u4fdd\u62a4\u4e01\u5927\u54e5\u5468\u5168\uff1f\u55ef\uff0c\u884c\u4e00\u6b65\uff0c\u7b97\u4e00\u6b65\uff0c\u6211\u53ea\u987b\u6682\u4e14\u4fdd\u5f97\u6027\u547d\uff0c\u80fd\u8d70\u8fd1\u6076\u50e7\u8eab\u65c1\uff0c\u4e58\u4ed6\u4e0d\u5907\uff0c\u4fbf\u53ef\u60f3\u6cd5\u6740\u4ed6\u3002\u201d \u3000\u3000\u5f85\u5f97\u80e1\u5b50\u62d4\u4e86\u4e00\u5927\u534a\uff0c\u5ffd\u53c8\u60f3\u8d77\uff1a\u201c\u5c31\u7b97\u6211\u6ca1\u4e86\u80e1\u987b\uff0c\u8fd9\u6ee1\u5934\u957f\u53d1\uff0c\u8fd8\u662f\u6cc4\u9732\u4e86\u6211\u7684\u672c\u6765\u9762\u76ee\u3002\u8fd9\u6076\u50e7\u5728\u957f\u6c5f\u8fb9\u4e0a\u8ffd\u6211\uff0c\u81ea\u7136\u5c06\u6211\u8fd9\u62ab\u5934\u6563\u53d1\u7684\u6a21\u6837\u77a7\u5f97\u6e05\u6e05\u695a\u695a\u4e86\u3002\u201d\u4e00\u4e0d\u505a\uff0c\u4e8c\u4e0d\u4f11\uff0c\u4f38\u624b\u626f\u4f4f\u4e24\u8fb9\u5934\u53d1\uff0c\u8f7b\u8f7b\u4e00\u6296\uff0c\u4fbf\u5373\u62d4\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u62d4\u80e1\u5b50\u8fd8\u4e0d\u7b97\u75db\uff0c\u90a3\u4e00\u6839\u6839\u5934\u53d1\u8981\u62d4\u4e2a\u6e05\u5149\uff0c\u53ef\u5f53\u771f\u75db\u5f97\u5389\u5bb3\u3002\u4e00\u9762\u62d4\u7740\uff0c\u5fc3\u4e2d\u53ea\u60f3\uff1a\u201c\u522b\u8bf4\u53ea\u662f\u62d4\u987b\u62d4\u53d1\u8fd9\u7b49\u5c0f\u4e8b\uff0c\u53ea\u8981\u662f\u4e3a\u4e86\u4e01\u5927\u54e5\uff0c\u4fbf\u662f\u8981\u6211\u780d\u53bb\u81ea\u5df1\u624b\u8db3\uff0c\u4e5f\u662f\u4e0d\u4f1a\u76b1\u4e00\u76b1\u7709\u5934\u3002\u201d\u53c8\u60f3\uff1a\u201c\u6211\u8fd9\u6cd5\u5b50\u771f\u7b28\uff0c\u4e01\u5927\u54e5\u7684\u9b3c\u9b42\u5b9a\u5728\u7b11\u6211\u3002\u53ef\u662f\u2026\u2026\u53ef\u662f\u2026\u2026\u4ed6\u518d\u4e5f\u4e0d\u80fd\u6559\u6211\u4e00\u4e2a\u5de7\u5999\u7684\u6cd5\u5b50\u4e86\u3002\u201d \u3000\u3000\u8033\u542c\u5f97\u5b9d\u8c61\u53c8\u5df2\u7761\u5012\uff0c\u552f\u6050\u7ed9\u8fd9\u6076\u50e7\u542c\u5230\u81ea\u5df1\u58f0\u606f\uff0c\u4e8e\u662f\u62d4\u4e00\u4e9b\u5934\u53d1\u80e1\u5b50\uff0c\u4fbf\u6781\u6162\u6781\u6162\u7684\u9000\u51fa\u4e00\u6b65\uff0c\u76f4\u82b1\u4e86\u5c0f\u534a\u4e2a\u65f6\u8fb0\uff0c\u624d\u9000\u5230\u5929\u4e95\u4e4b\u4e2d\uff0c\u53c8\u8fc7\u826f\u4e45\uff0c\u6162\u6162\u51fa\u4e86\u571f\u5730\u5e99\u7684\u540e\u95e8\u3002 \u3000\u3000\u5927\u96e8\u70b9\u70b9\u6ef4\u6ef4\u7684\u6253\u5728\u8138\u4e0a\uff0c\u65b9\u59cb\u8f7b\u8f7b\u8212\u4e86\u53e3\u6c14\u3002 \u3000\u3000\u5728\u5e99\u5916\u4e0d\u7528\u62c5\u5fc3\u7ed9\u5b9d\u8c61\u542c\u89c1\uff0c\u62d4\u987b\u62d4\u53d1\u65f6\u5c31\u5feb\u5f97\u591a\u4e86\uff0c\u7ec8\u4e8e\u5c06\u6ee1\u5934\u957f\u53d1\u3001\u6ee1\u816e\u80e1\u5b50\u62d4\u4e86\u4e2a\u5e72\u5e72\u51c0\u51c0\u3002\u4ed6\u5c06\u62d4\u4e0b\u7684\u5934\u53d1\u80e1\u987b\u90fd\u57cb\u5728\u70c2\u6ce5\u4e4b\u4e2d\uff0c\u4ee5\u514d\u5b9d\u8c61\u53d1\u89c1\u540e\u8d77\u7591\uff0c\u6478\u6478\u81ea\u5df1\u5149\u79c3\u79c3\u7684\u8111\u888b\u548c\u4e0b\u5df4\uff0c\u4e0d\u4f46\u5df2\u975e\u201c\u8001\u8d3c\u201d\uff0c\u800c\u4e14\u6210\u4e86\u4e2a\u201c\u8d3c\u79c3\u201d\uff0c\u60b2\u6124\u4e4b\u4e0b\uff0c\u7ec8\u4e8e\u4e5f\u5fcd\u4e0d\u4f4f\u597d\u7b11\uff0c\u5bfb\u601d\uff1a\u201c\u6211\u8fd9\u4e48\u4e71\u62d4\u4e00\u9635\uff0c\u5934\u9876\u548c\u4e0b\u5df4\u52bf\u5fc5\u662f\u8840\u8ff9\u6591\u6591\uff0c\u987b\u5f97\u597d\u597d\u51b2\u6d17\uff0c\u4ee5\u514d\u9732\u51fa\u75d5\u8ff9\u3002\u201d\u4e8e\u662f\u62ac\u8d77\u4e86\u5934\uff0c\u8ba9\u96e8\u6c34\u6dcb\u53bb\u8138\u4e0a\u6c61\u79fd\u3002 \u3000\u3000\u53c8\u60f3\uff1a\u201c\u6211\u8138\u4e0a\u662f\u6ca1\u7834\u7efd\u4e86\uff0c\u8fd9\u8eab\u8863\u670d\u82e5\u7ed9\u6076\u50e7\u8ba4\u4e86\u51fa\u6765\uff0c\u7ec8\u7a76\u8fd8\u662f\u7cdf\u7cd5\u3002\u55ef\uff0c\u6ca1\u8863\u886b\u597d\u6362\uff0c\u6211\u4fbf\u5b66\u90a3\u6076\u50e7\u7684\u6837\uff0c\u8131\u5f97\u8d64\u6761\u6761\u7684\uff0c\u5374\u53c8\u600e\u5730\uff1f\u201d\u4e8e\u662f\u5c06\u8863\u886b\u88e4\u5b50\u90fd\u8131\u4e86\u4e0b\u6765\uff0c\u4e4c\u8695\u8863\u53ef\u4e0d\u80fd\u8131\uff0c\u53d8\u6210\u4e86\u53ea\u6709\u5185\u8863\u3001\u5374\u65e0\u88e4\u5b50\u7684\u5c40\u9762\uff0c\u5f53\u4e0b\u5c06\u5916\u8863\u6495\u5f00\uff0c\u56f4\u5728\u8170\u95f4\uff0c\u53c8\u6050\u5b9d\u8c61\u8bc6\u5f97\u4e4c\u8695\u8863\u7684\u6765\u5386\uff0c\u4fbf\u5728\u70c2\u6ce5\u4e2d\u6253\u4e86\u4e2a\u6eda\uff0c\u5168\u8eab\u6d82\u6ee1\u6c61\u6ce5\u3002 \u3000\u3000\u8fd9\u65f6\u4fbf\u662f\u4e01\u5178\u590d\u751f\uff0c\u53ea\u6050\u4e00\u65f6\u4e4b\u95f4\u4e5f\u8ba4\u4ed6\u4e0d\u51fa\u3002\u72c4\u4e91\u6478\u7d22\u5230\u4e00\u682a\u5927\u6811\u4e4b\u4e0b\uff0c\u7528\u624b\u6307\u5728\u70c2\u6ce5\u4e2d\u6316\u4e86\u4e2a\u6d1e\uff0c\u5c06\u5c0f\u5305\u88b1\u57cb\u5728\u5176\u4e2d\uff0c\u6697\u60f3\uff1a\u201c\u82e5\u80fd\u9003\u8131\u6076\u50e7\u7684\u6bd2\u624b\uff0c\u62a4\u5f97\u4e01\u5927\u54e5\u5e73\u5b89\uff0c\u65e5\u540e\u5fc5\u5f53\u62a5\u7b54\u8fd9\u4f4d\u66ff\u6211\u88f9\u4f24\u3001\u8d60\u6211\u94f6\u4e24\u9996\u9970\u4e4b\u4eba\u7684\u5927\u6069\u5927\u5fb7\u3002\u53ef\u662f\u4ed6\u7a76\u7adf\u662f\u8c01\uff1f\u201d \u3000\u3000\u5fd9\u5230\u8fd9\u65f6\uff0c\u5929\u8272\u5df2\u5fae\u5fae\u660e\u4eae\u3002\u72c4\u4e91\u6084\u6084\u5411\u5357\u884c\u53bb\uff0c\u6298\u800c\u5411\u897f\uff0c\u884c\u51fa\u91cc\u8bb8\uff0c\u5929\u5df2\u5927\u660e\uff0c\u773c\u89c1\u5927\u96e8\u5140\u81ea\u672a\u6b62\uff0c\u6599\u60f3\u5b9d\u8c61\u4e0d\u4f1a\u79bb\u5e99\u4ed6\u53bb\uff0c\u8981\u60f3\u627e\u4e00\u4ef6\u6b66\u5668\uff0c\u8352\u91ce\u4e2d\u5374\u5230\u54ea\u91cc\u627e\u53bb\uff1f\u53ea\u5f97\u62fe\u4e86\u4e00\u5757\u5c16\u9510\u7684\u77f3\u7247\uff0c\u85cf\u5728\u8170\u95f4\uff0c\u5fc3\u60f3\u82e5\u80fd\u5728\u8fd9\u6076\u50e7\u7684\u8981\u5bb3\u5904\u6233\u4e0a\u4e00\u4e0b\uff0c\u8bf4\u4e0d\u5b9a\u4e5f\u80fd\u8981\u4e86\u4ed6\u7684\u6027\u547d\u3002\u6700\u597d\u8fd9\u6076\u50e7\u5df2\u79bb\u5e99\u4ed6\u53bb\uff0c\u90a3\u662f\u4e0a\u4e0a\u5927\u5409\u3002 \u3000\u3000\u5728\u79ef\u6c34\u5751\u4e2d\u4e00\u7167\uff0c\u89c1\u5230\u81ea\u5df1\u53e4\u602a\u7684\u6a21\u6837\uff0c\u5fcd\u4e0d\u4f4f\u597d\u7b11\uff0c\u4f46\u968f\u5373\u611f\u5230\u4e00\u9635\u8bf4\u4e0d\u51fa\u7684\u51c4\u82e6\u3002 \u3000\u3000\u5fc3\u4e2d\u8bb0\u6302\u7740\u4e01\u5178\uff0c\u7b49\u4e0d\u5f97\u53e6\u627e\u66f4\u5408\u7528\u7684\u6b66\u5668\uff0c\u4fbf\u5411\u4e1c\u671d\u571f\u5730\u5e99\u884c\u53bb\uff0c\u5fc3\u60f3\uff1a\u201c\u6211\u987b\u5f97\u75af\u75af\u766b\u766b\uff0c\u88c5\u505a\u662f\u672c\u5730\u7684\u4e00\u4e2a\u65e0\u8d56\u6c49\u5b50\u3002\u201d\u5c06\u8fd1\u571f\u5730\u5e99\u65f6\uff0c\u653e\u5f00\u5589\u5499\uff0c\u5927\u58f0\u5531\u8d77\u5c71\u6b4c\u6765\uff1a \u3000\u3000\u201c\u5bf9\u5c71\u7684\u59b9\u59b9\uff0c\u542c\u6211\u5531\u554a\uff0c \u3000\u3000\u4f60\u5ac1\u4eba\u83ab\u5ac1\u5bcc\u5bb6\u90ce\uff0c \u3000\u3000\u738b\u5b59\u516c\u5b50\u826f\u5fc3\u574f\uff01 \u3000\u3000\u8981\u5ac1\u6211\u765e\u75e2\u5934\u963f\u4e09\uff0c\u9876\u4e0a\u5149\uff01\u201d \u3000\u3000\u4ed6\u5f53\u5e74\u5728\u6e56\u5357\u4e61\u95f4\uff0c\u672c\u5c31\u64c5\u5531\u5c71\u6b4c\uff0c\u6e56\u7554\u7530\u95f4\uff0c\u6eaa\u524d\u5c71\u540e\uff0c\u548c\u621a\u82b3\u4fe9\u4e0d\u77e5\u5df2\u5531\u8fc7\u51e0\u5343\u51e0\u4e07\u9996\u5c71\u6b4c\u3002\u6e56\u5357\u4e61\u95f4\u98ce\u4fd7\uff0c\u5c71\u6b4c\u90fd\u662f\u5e94\u666f\u5373\u5174\u4e4b\u4f5c\uff0c\u968f\u53e3\u800c\u51fa\uff0c\u62bc\u4ee5\u7c97\u6d45\u97f5\u811a\uff0c\u4e0e\u65e5\u5e38\u8bf4\u8bdd\u5e76\u65e0\u591a\u5927\u5dee\u522b\u3002\u4ed6\u6b4c\u58f0\u4e00\u51fa\u53e3\uff0c\u80f8\u95f4\u4e0d\u7981\u4e00\u9178\uff0c\u81ea\u4ece\u90a3\u4e00\u5e74\u548c\u621a\u82b3\u643a\u624b\u540c\u6e38\u4ee5\u6765\uff0c\u8fd9\u5c71\u6b4c\u5df2\u4e94\u5e74\u591a\u6ca1\u51fa\u8fc7\u4ed6\u7684\u5589\u5934\uff0c\u8fd9\u65f6\u65e7\u8c03\u91cd\u5531\uff0c\u773c\u524d\u60c5\u666f\u5374\u662f\u5e0c\u5947\u53e4\u602a\u4e4b\u6781\u3002\u542c\u6b4c\u8005\u4e0d\u518d\u662f\u90a3\u4e2a\u4fcf\u7f8e\u7684\u5c0f\u5e08\u59b9\uff0c\u800c\u662f\u4e00\u4e2a\u8d64\u6761\u6761\u3001\u6076\u72e0\u72e0\u7684\u5927\u548c\u5c1a\u3002 \u3000\u3000\u4ed6\u6162\u6162\u8d70\u8fd1\u571f\u5730\u5e99\uff0c\u903c\u7d27\u4e86\u5589\u5499\uff0c\u6a21\u62df\u7740\u5973\u58f0\u53c8\u5531\u4e86\u8d77\u6765\uff1a \u3000\u3000\u201c\u4f60\u765e\u75e2\u5934\u963f\u4e09\u6709\u5565\u9999\uff1f \u3000\u3000\u60f3\u5a36\u6211\u5982\u82b1\u5982\u7389\u5c0f\u5a07\u5a18\uff1f \u3000\u3000\u8d2a\u56fe\u4f60\u5934\u4e0a\u65e0\u6bdb\u4e0d\u7528\u68b3\uff1f \u3000\u3000\u8d2a\u56fe\u4f60\u2026\u2026\u201d \u3000\u3000\u4e0b\u9762\u8fd9\u53e5\u201c\u8d2a\u56fe\u4f60\u201d\u8fd8\u6ca1\u5531\u5b8c\uff0c\u5b9d\u8c61\u5df2\u4ece\u571f\u5730\u5e99\u4e2d\u8d70\u4e86\u51fa\u6765\u3002\u4ed6\u5c06\u4e0a\u8863\u56f4\u5728\u8170\u95f4\uff0c\u5411\u5916\u4e00\u5f20\uff0c\u8981\u77a7\u77a7\u662f\u8c01\u6765\u4e86\uff0c\u53ea\u89c1\u72c4\u4e91\u53e3\u5531\u5c71\u6b4c\u800c\u6765\uff0c\u5934\u9876\u5149\u79c3\u79c3\u5730\uff0c\u8fd8\u9053\u4ed6\u771f\u662f\u4e2a\u765e\u75e2\u5934\u79c3\u5b50\uff0c\u5c71\u6b4c\u4e2d\u5374\u662f\u6ee1\u53e3\u81ea\u5632\uff0c\u4e0d\u7531\u5f97\u597d\u7b11\uff0c\u53eb\u9053\uff1a\u201c\u5582\uff0c\u79c3\u5b50\uff0c\u4f60\u8fc7\u6765\uff01\u201d \u3000\u3000\u72c4\u4e91\u5531\u9053\uff1a\u201c\u5927\u5e08\u7236\u53eb\u6211\u6709\u5565\u4e8b\uff1f \u3000\u3000\u8981\u9001\u6211\u91d1\u5b50\u548c\u94f6\u5b50\uff1f \u3000\u3000\u765e\u75e2\u5934\u963f\u4e09\u8fd0\u6c14\u597d\uff0c \u3000\u3000\u5927\u5e08\u7236\u8981\u8bf7\u6211\u5403\u80a5\u732a\u3002\u201d \u3000\u3000\u4ed6\u4e00\u9762\u5531\uff0c\u4e00\u9762\u8d70\u5411\u5b9d\u8c61\u8ddf\u524d\uff0c\u867d\u662f\u52c9\u529b\u88c5\u4f5c\u795e\u8272\u81ea\u82e5\uff0c\u4f46\u4e00\u9897\u5fc3\u5fcd\u4e0d\u4f4f\u5267\u70c8\u5f02\u5e38\u7684\u8df3\u52a8\u3002\u8138\u4e0a\u4e5f\u5df2\u53d8\u8272\u3002\u4f46\u5b9d\u8c61\u54ea\u91cc\u5bdf\u89c9\uff0c\u7b11\u563b\u563b\u7684\u9053\uff1a\u201c\u765e\u75e2\u5934\u963f\u4e09\uff0c\u4f60\u53bb\u7ed9\u6211\u627e\u4e9b\u5403\u7684\u4e1c\u897f\u6765\uff0c\u5927\u5e08\u7236\u91cd\u91cd\u6709\u8d4f\uff0c\u6709\u6ca1\u6709\u80a5\u732a\uff1f\u201d \u3000\u3000\u72c4\u4e91\u6447\u6447\u5934\uff0c\u5531\u9053\uff1a\u201c\u8352\u5c71\u91ce\u5cad\u6ca1\u80a5\u732a\u2026\u2026\u201d \u3000\u3000\u5b9d\u8c61\u559d\u9053\uff1a\u201c\u597d\u597d\u8bf4\u8bdd\uff0c\u4e0d\u8bb8\u5531\u554a\u5531\u7684\u3002\u201d \u3000\u3000\u72c4\u4e91\u4f38\u4e86\u4f38\u820c\u5934\uff0c\u52c9\u529b\u60f3\u88c5\u51fa\u4e00\u526f\u6cb9\u8154\u6ed1\u8c03\u7684\u795e\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u765e\u75e2\u5934\u963f\u4e09\u5531\u60ef\u4e86\u5c71\u6b4c\uff0c\u8bb2\u8bdd\u6ca1\u90a3\u4e48\u987a\u5f53\u3002\u5927\u5e08\u7236\uff0c\u8fd9\u91cc\u524d\u4e0d\u5df4\u6751\uff0c\u540e\u4e0d\u5df4\u5e97\uff0c\u5341\u91cc\u4e4b\u5185\uff0c\u6ca1\u6709\u4eba\u70df\u3002\u4f60\u522b\u8bf4\u60f3\u5403\u80a5\u732a\uff0c\u4fbf\u9752\u83dc\u767d\u996d\u4e5f\u662f\u96be\u627e\u3002\u8fd9\u91cc\u897f\u53bb\u5341\u4e94\u91cc\uff0c\u6709\u597d\u5927\u4e00\u5ea7\u5e02\u9547\uff0c\u6709\u9152\u6709\u8089\uff0c\u6709\u9e21\u6709\u9c7c\uff0c\u5927\u5e08\u7236\u60f3\u5403\u751a\u4e48\u6709\u751a\u4e48\uff0c\u4e0d\u59a8\u4fbf\u53bb\u3002\u201d\u4ed6\u81ea\u77e5\u65e0\u529b\u6740\u5f97\u5b9d\u8c61\uff0c\u62a5\u4ed6\u5200\u780d\u4e01\u5178\u4e4b\u4ec7\uff0c\u53ea\u76fc\u4ed6\u4fe1\u5f97\u81ea\u5df1\u8a00\u8bed\uff0c\u5411\u897f\u53bb\u5bfb\u996e\u98df\uff0c\u81ea\u5df1\u4fbf\u53ef\u62b1\u4e86\u4e01\u5178\u5c38\u8eab\u9003\u8d70\u3002 \u3000\u3000\u53ef\u662f\u5927\u96e8\u59cb\u7ec8\u4e0d\u6b62\uff0c\u5237\u5237\u5237\u7684\u843d\u5728\u4e24\u4eba\u8eab\u4e0a\u3002 \u3000\u3000\u5b9d\u8c61\u9053\uff1a\u201c\u4f60\u53bb\u7ed9\u6211\u627e\u4e9b\u5403\u7684\u6765\uff0c\u6709\u9152\u6709\u8089\u6700\u597d\uff0c\u5426\u5219\u6740\u53ea\u9e21\u6740\u53ea\u9e2d\u4e5f\u6210\u3002\u201d \u3000\u3000\u72c4\u4e91\u53ea\u6302\u5ff5\u7740\u4e01\u5178\uff0c\u5634\u91cc\u201c\u54e6\u54e6\u201d\u7b54\u5e94\uff0c\u8d70\u8fdb\u6bbf\u4e2d\uff0c\u53ea\u89c1\u4e01\u5178\u7684\u5c38\u8eab\u5df2\u4ece\u795e\u575b\u4e0b\u88ab\u62d6\u4e86\u51fa\u6765\uff0c\u8863\u886b\u5c3d\u6570\u6495\u70c2\uff0c\u663e\u662f\u66fe\u88ab\u5b9d\u8c61\u4ed4\u7ec6\u641c\u67e5\u8fc7\u3002\u72c4\u4e91\u5fc3\u4e2d\u60b2\u6068\uff0c\u518d\u4e5f\u63a9\u9970\u4e0d\u4f4f\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u2026\u2026\u8fd9\u91cc\u6709\u4e2a\u6b7b\u4eba\u2026\u2026\u662f\u2026\u2026\u662f\u4f60\u6253\u6b7b\u7684\u4e48\uff1f\u201d \u3000\u3000\u4ed6\u8138\u8272\u5927\u53d8\uff0c\u5b9d\u8c61\u53ea\u9053\u4ed6\u662f\u89c1\u5230\u6b7b\u4eba\u5bb3\u6015\uff0c\u72de\u7b11\u9053\uff1a\u201c\u4e0d\u662f\u6211\u6253\u6b7b\u7684\u3002\u4f60\u6765\u8ba4\u8ba4\uff0c\u8fd9\u4eba\u662f\u8c01\uff1f\u4f60\u8ba4\u5f97\u4ed6\u4e48\uff1f\u201d\u72c4\u4e91\u5403\u4e86\u4e00\u60ca\uff0c\u4e00\u65f6\u5fc3\u865a\uff0c\u8fd8\u9053\u4ed6\u5df2\u8bc6\u7834\u81ea\u5df1\u884c\u85cf\uff0c\u82e5\u4e0d\u662f\u51b3\u610f\u4fdd\u62a4\u4e01\u5178\uff0c\u5df2\u7136\u53d1\u8db3\u4fbf\u9003\uff0c\u5f53\u4e0b\u5f3a\u81ea\u9547\u5b9a\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4eba\u76f8\u8c8c\u5f88\u53e4\u602a\uff0c\u4e0d\u662f\u672c\u6751\u91cc\u7684\u3002\u201d \u3000\u3000\u5b9d\u8c61\u7b11\u9053\uff1a\u201c\u4ed6\u81ea\u7136\u4e0d\u662f\u4f60\u6751\u5b50\u91cc\u7684\u4eba\u3002\u201d\u7a81\u7136\u5389\u58f0\u9053\uff1a\u201c\u5582\uff0c\u53bb\u627e\u4e9b\u5403\u7684\u4e1c\u897f\u6765\u3002\u4f60\u4e0d\u542c\u8bdd\uff0c\u77a7\u4f5b\u7237\u4e0d\u8981\u4e86\u4f60\u7684\u72d7\u547d\uff1f\u201d \u3000\u3000\u72c4\u4e91\u89c1\u4e01\u5178\u5c38\u8eab\u6682\u4e14\u65e0\u6059\uff0c\u7a0d\u89c9\u653e\u5fc3\uff0c\u5e94\u9053\uff1a\u201c\u662f\uff0c\u662f\uff01\u201d \u3000\u3000\u8f6c\u8eab\u51fa\u5e99\uff0c\u5fc3\u60f3\uff1a\u201c\u6211\u4e14\u907f\u4ed6\u4e00\u907f\uff0c\u53ea\u987b\u534a\u5929\u4e0d\u56de\u6765\uff0c\u4ed6\u8010\u4e0d\u4f4f\u9965\u997f\uff0c\u81ea\u4f1a\u53bb\u5bfb\u98df\u7269\u3002\u4ed6\u7ec8\u4e0d\u6210\u5e26\u4e86\u4e01\u5927\u54e5\u8d70\u3002\u4ed6\u5df2\u641c\u67e5\u8fc7\u4e01\u5927\u54e5\u8eab\u8fb9\uff0c\u627e\u4e0d\u5230\u751a\u4e48\uff0c\u81ea\u4e5f\u53ef\u6b7b\u5fc3\u4e86\u3002\u201d\u4e0d\u6599\u53ea\u884c\u5f97\u4e24\u6b65\uff0c\u5b9d\u8c61\u5389\u58f0\u559d\u9053\uff1a\u201c\u7ad9\u4f4f\uff01\u4f60\u5230\u54ea\u91cc\u53bb\uff1f\u201d\u72c4\u4e91\u9053\uff1a\u201c\u6211\u53bb\u7ed9\u4f60\u4e70\u5403\u7684\u554a\u3002\u201d\u5b9d\u8c61\u9053\uff1a\u201c\u55ef\uff0c\u5f88\u597d\u5f88\u597d\uff01\u4f60\u8fc7\u591a\u4e45\u56de\u6765\uff1f\u201d\u72c4\u4e91\u9053\uff1a\u201c\u5f88\u5feb\u7684\uff0c\u4e00\u4f1a\u513f\u529f\u592b\u5c31\u56de\u6765\u4e86\u3002\u201d\u5b9d\u8c61\u9053\uff1a\u201c\u53bb\u7f62\uff01\u201d \u3000\u3000\u72c4\u4e91\u56de\u5934\u5411\u4e01\u5178\u7684\u5c38\u8eab\u671b\u4e86\u4e00\u773c\uff0c\u5411\u5e99\u5916\u8d70\u53bb\u3002\u7a81\u7136\u80cc\u540e\u98ce\u58f0\u5fae\u52a8\uff0c\u62cd\u62cd\u4e24\u54cd\uff0c\u5de6\u53f3\u53cc\u988a\u4e0a\u5404\u5403\u4e86\u4e00\u8bb0\u8033\u5149\u3002\u5e78\u597d\u5b9d\u8c61\u53ea\u9053\u4ed6\u662f\u4e2a\u4e0d\u4f1a\u4e1d\u6beb\u6b66\u529f\u7684\u4e61\u4e0b\u6c49\u5b50\uff0c\u4e0b\u624b\u4e0d\u91cd\uff1b\u53c8\u5e78\u597d\u5b9d\u8c61\u8eab\u6cd5\u5947\u5feb\uff0c\u4e00\u51fa\u624b\u4fbf\u5373\u6253\u4e2d\uff0c\u5426\u5219\u72c4\u4e91\u8111\u7b4b\u5e76\u4e0d\u7075\u654f\uff0c\u9047\u5230\u80cc\u540e\u6709\u4eba\u6765\u88ad\uff0c\u81ea\u7136\u800c\u7136\u7684\u4f1a\u95ea\u8eab\u8eb2\u907f\uff0c\u51b3\u8ba1\u6765\u4e0d\u53ca\u60f3\u5230\u8981\u88c5\u4f5c\u4e0d\u4f1a\u6b66\u529f\u3002 \u3000\u3000\u72c4\u4e91\u5403\u4e86\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u2026\u2026\u201d\u5fc3\u60f3\uff1a\u201c\u4ed6\u65e2\u8bc6\u7834\u4e86\uff0c\u90a3\u53ea\u6709\u62da\u547d\u4e86\u3002\u201d\u53ea\u542c\u5b9d\u8c61\u9053\uff1a\u201c\u4f60\u8eab\u4e0a\u6709\u591a\u5c11\u94f6\u5b50\uff0c\u62ff\u51fa\u6765\u7ed9\u6211\u77a7\u77a7\uff01\u201d\u72c4\u4e91\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\u2026\u2026\u201d\u5b9d\u8c61\u6012\u9053\uff1a\u201c\u4f60\u8eab\u4e0a\u5149\u6e9c\u6e9c\u7684\uff0c\u8c05\u4f60\u8fd9\u7a77\u6c49\u4e5f\u6ca1\u94f6\u5b50\uff0c\u51ed\u4f60\u7684\u81ed\u9762\u5b50\uff0c\u53c8\u80fd\u8d4a\u5f97\u5230\u3001\u6b20\u5f97\u7740\u4e86\uff1f\u54fc\uff0c\u4f60\u8bf4\u53bb\u7ed9\u6211\u4e70\u5403\u7684\uff0c\u4e0d\u662f\u5b58\u5fc3\u60f3\u6e9c\u4e48\uff1f\u201d \u3000\u3000\u72c4\u4e91\u542c\u4ed6\u8fd9\u4e48\u8bf4\uff0c\u53cd\u800c\u5bbd\u5fc3\uff1a\u201c\u539f\u6765\u4ed6\u53ea\u77a7\u7834\u6211\u53bb\u4e70\u4e1c\u897f\u662f\u5047\uff0c\u90a3\u5012\u4e0d\u8981\u7d27\u3002\u201d\u5b9d\u8c61\u53c8\u9053\uff1a\u201c\u4f60\u8fd9\u79c3\u5934\u8bf4\u5341\u91cc\u4e4b\u5185\u5e76\u65e0\u4eba\u70df\uff0c\u53c8\u600e\u80fd\u53bb\u4e70\u4e86\u5403\u7684\uff0c\u5373\u523b\u4fbf\u56de\uff1f\u8fd9\u4e0d\u662f\u660e\u660e\u9a97\u6211\u4e48\uff1f\u54fc\uff0c\u4f60\u7ed9\u6211\u8bf4\u8001\u5b9e\u7684\uff0c\u5230\u5e95\u60f3\u751a\u4e48\uff1f\u201d\u72c4\u4e91\u7ed3\u7ed3\u5df4\u5df4\u7684\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\u2026\u2026\u6211\u89c1\u4e86\u5927\u5e08\u7236\u5bb3\u6015\uff0c\u60f3\u9003\u56de\u5bb6\u53bb\u3002\u201d \u3000\u3000\u5b9d\u8c61\u54c8\u54c8\u5927\u7b11\uff0c\u62cd\u4e86\u62cd\u957f\u6ee1\u9ed1\u6bdb\u7684\u80f8\u53e3\uff0c\u8bf4\u9053\uff1a\u201c\u6015\u751a\u4e48\uff1f \u3000\u3000\u6015\u6211\u5403\u4e86\u4f60\u4e48\uff1f\u201d\u4e00\u63d0\u5230\u8fd9\u201c\u5403\u201d\u5b57\uff0c\u767b\u65f6\u8179\u4e2d\u5495\u5495\u76f4\u54cd\uff0c\u66f4\u997f\u5f97\u96be\u53d7\u3002\u5929\u4eae\u4e4b\u540e\uff0c\u4ed6\u65e9\u5df2\u5728\u5e99\u4e2d\u5230\u5904\u641c\u5bfb\u8fc7\u4e86\uff0c\u534a\u70b9\u53ef\u5403\u4e4b\u7269\u4e5f\u6ca1\u6709\u3002\u4ed6\u5583\u5583\u7684\u8fde\u8bf4\u51e0\u53e5\uff1a\u201c\u6015\u6211\u5403\u4e86\u4f60\u4e48\uff1f\u6015\u6211\u5403\u4e86\u4f60\u4e48\uff1f\u201d\u8fd9\u822c\u8bf4\u7740\uff0c\u773c\u4e2d\u5ffd\u7136\u9732\u51fa\u51f6\u5149\uff0c\u5411\u72c4\u4e91\u4e0a\u4e0a\u4e0b\u4e0b\u7684\u6253\u91cf\u3002 \u3000\u3000\u72c4\u4e91\u7ed9\u8fd9\u773c\u5149\u53ea\u77a7\u5f97\u6ee1\u8eab\u53d1\u6bdb\uff0c\u5df2\u731c\u5230\u6076\u50e7\u5fc3\u4e2d\u5728\u6253\u751a\u4e48\u4e3b\u610f\u3002\u5b9d\u8c61\u679c\u7136\u6b63\u5728\u60f3\uff1a\u201c\u4eba\u8089\u6ecb\u5473\u672c\u6765\u4e0d\u9519\uff0c\u4eba\u5fc3\u4eba\u809d\u66f4\u52a0\u597d\u5403\uff0c\u773c\u524d\u73b0\u6210\u6709\u4e00\u53e3\u732a\u5728\u8fd9\u91cc\uff0c\u5e72\u4e48\u4e0d\u5bb0\u4e86\u5403\uff1f\u201d \u3000\u3000\u72c4\u4e91\u5fc3\u4e0b\u4e0d\u4f4f\u53eb\u82e6\uff1a\u201c\u6211\u7ed9\u4ed6\u6740\u4e86\uff0c\u5012\u4e5f\u6ca1\u751a\u4e48\u3002\u77a7\u8fd9\u6076\u50e7\u7684\u6a21\u6837\uff0c\u663e\u662f\u8981\u5c06\u6211\u716e\u6765\u5403\u4e86\uff0c\u8fd9\u53ef\u51a4\u5f97\u5f88\u4e86\u3002\u6211\u8ddf\u4f60\u62da\u4e86\u3002\u201d\u53ef\u662f\uff0c\u62da\u547d\u4e00\u5b9a\u88ab\u6740\uff0c\u6740\u4e86\u4e4b\u540e\uff0c\u4ecd\u662f\u7ed9\u4ed6\u5403\u4e0b\u809a\u4e2d\uff0c\u90a3\u53c8\u6709\u751a\u4e48\u5206\u522b\uff1f\u53ea\u89c1\u5b9d\u8c61\u53cc\u773c\u4e2d\u51f6\u5149\u5927\u70bd\uff0c\u563f\u563f\u72de\u7b11\uff0c\u8fc8\u6b65\u8d70\u6765\u3002 \u3000\u3000\u72c4\u4e91\u89c1\u4ed6\u4e00\u6b65\u6b65\u903c\u6765\uff0c\u4e00\u5f20\u4e11\u8138\u8d8a\u53d1\u663e\u5f97\u72f0\u72de\u53ef\u6016\uff0c\u4e5f\u662f\u4e00\u6b65\u6b65\u9000\u7f29\u3002\u5b9d\u8c61\u7b11\u9053\uff1a\u201c\u563f\u563f\uff0c\u4f60\u8fd9\u7626\u9b3c\uff0c\u5403\u8d77\u6765\u6ecb\u5473\u4e00\u5b9a\u4e0d\u597d\u3002\u8fd9\u6b7b\u5c38\u8fd8\u6bd4\u4f60\u80a5\u80d6\u4e9b\uff0c\u53ea\u53ef\u60dc\u6b7b\u5c38\u6709\u6bd2\uff0c\u5403\u4e0d\u5f97\u3002\u6ca1\u6cd5\u5b50\uff0c\u6ca1\u80a5\u732a\uff0c\u7626\u732a\u4e5f\u53ea\u597d\u5c06\u5c31\u7740\u5bf9\u4ed8\u3002\u201d\u4e00\u4f38\u624b\uff0c\u6293\u4f4f\u4e86\u72c4\u4e91\u5de6\u81c2\u3002 \u3000\u3000\u72c4\u4e91\u594b\u529b\u6323\u624e\uff0c\u5374\u54ea\u91cc\u6323\u624e\u5f97\u5f00\uff1f\u5fc3\u4e2d\u7126\u6025\u6050\u60e7\uff0c\u771f\u662f\u96be\u4ee5\u5f62\u5bb9\u3002\u7ecf\u8fc7\u8fd9\u51e0\u5e74\u6765\u7684\u60e8\u53d7\u6298\u78e8\uff0c\u65e9\u5df2\u5e76\u4e0d\u5982\u4f55\u6015\u6b7b\uff0c\u4f46\u60f3\u5230\u8981\u7ed9\u8fd9\u6076\u50e7\u6d3b\u751f\u751f\u7684\u5403\u4e0b\u809a\u53bb\uff0c\u5b9e\u662f\u4e0d\u5bd2\u800c\u6817\u3002 \u3000\u3000\u5b9d\u8c61\u773c\u89c1\u72c4\u4e91\u65e0\u6cd5\u9003\u8131\uff0c\u5fc3\u60f3\u4e0d\u5982\u53eb\u4ed6\u5148\u70e7\u597d\u6c64\u6c34\uff0c\u7136\u540e\u518d\u884c\u4e0b\u624b\u5bb0\u6740\uff0c\u53ea\u53ef\u60dc\u8fd9\u4eba\u4e0d\u4f1a\u81ea\u5df1\u5bb0\u6740\u81ea\u5df1\uff0c\u518d\u5c06\u81ea\u5df1\u70e7\u6210\u4e00\u5927\u7897\u7ea2\u70e7\u4eba\u8089\uff0c\u53cc\u624b\u606d\u606d\u656c\u656c\u7684\u7aef\u5c06\u4e0a\u6765\uff0c\u4fbf\u9053\uff1a\u201c\u6211\u6740\u4e86\u4f60\u6765\u5403\uff0c\u6709\u4e24\u4e2a\u6cd5\u5b50\u3002\u4e00\u662f\u751f\u5272\u4f60\u817f\u4e0a\u808c\u8089\uff0c\u968f\u5272\u968f\u70e4\uff0c\u90a3\u4e48\u4f60\u5c31\u8981\u53d7\u96f6\u788e\u82e6\u5934\u3002\u7b2c\u4e8c\u4e2a\u6cd5\u5b50\u662f\u4e00\u5200\u5c06\u4f60\u6740\u4e86\uff0c\u716e\u8089\u7fb9\u5403\u3002\u4f60\u8bf4\u54ea\u4e2a\u6cd5\u5b50\u597d\uff1f\u201d \u3000\u3000\u72c4\u4e91\u54ac\u7259\u9053\uff1a\u201c\u4f60\u8981\u2026\u2026\u5c06\u6211\u6740\u4e86\uff0c\u4f60\u2026\u2026\u4f60\u2026\u2026\u4f60\u8fd9\u6076\u548c\u5c1a\u2026\u2026\u201d\u6b32\u5f85\u7834\u53e3\u5927\u9a82\uff0c\u5374\u6015\u4ed6\u4e00\u6012\u4e4b\u4e0b\uff0c\u66f4\u8ba9\u81ea\u5df1\u60e8\u53d7\u51cc\u8fdf\u4e4b\u82e6\uff0c\u9a82\u4eba\u7684\u8bdd\u5230\u5f97\u53e3\u8fb9\uff0c\u7ec8\u4e8e\u5fcd\u4f4f\u3002 \u3000\u3000\u5b9d\u8c61\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u77e5\u9053\u5c31\u597d\uff0c\u8d8a\u662f\u542c\u8bdd\uff0c\u8d8a\u6b7b\u5f97\u723d\u5feb\u3002 \u3000\u3000\u4f60\u5014\u5f3a\u6323\u624e\uff0c\u8fd9\u82e6\u5934\u53ef\u5c31\u5927\u4e86\u3002\u5582\uff0c\u765e\u75e2\u5934\u963f\u4e09\uff0c\u6211\u8bf4\u554a\uff0c\u4f60\u53bb\u53a8\u623f\u91cc\u628a\u90a3\u53ea\u94c1\u956c\u62ff\u6765\uff0c\u6ee1\u6ee1\u7684\u70e7\u4e0a\u4e00\u956c\u6c34\u3002\u201d \u3000\u3000\u72c4\u4e91\u660e\u77e5\u4ed6\u662f\u8981\u7528\u6765\u70f9\u98df\u81ea\u5df1\uff0c\u8fd8\u662f\u5fcd\u4e0d\u4f4f\u95ee\uff1a\u201c\u5e72\u751a\u4e48\uff1f\u201d \u3000\u3000\u5b9d\u8c61\u7b11\u9053\uff1a\u201c\u8fd9\u4e2a\u5c31\u4e0d\u7528\u591a\u95ee\u4e86\u3002\u5feb\u53bb\uff01\u201d\u72c4\u4e91\u9053\uff1a\u201c\u8981\u70e7\u6c34\uff0c\u5728\u53a8\u623f\u91cc\u70e7\u597d\u4e86\uff0c\u62ff\u94c1\u956c\u51fa\u6765\u4e0d\u65b9\u4fbf\u3002\u201d\u5b9d\u8c61\u9053\uff1a\u201c\u53a8\u623f\u91cc\u6ee1\u662f\u7070\u5c18\u3001\u8718\u86db\u7f51\uff0c\u8001\u4f5b\u7237\u4e00\u8fdb\u53bb\u4fbf\u76f4\u6253\u55b7\u568f\u3002\u6211\u4e0d\u77a7\u7740\u4f60\uff0c\u4f60\u8fd9\u5c0f\u765e\u75e2\u5b9a\u8981\u9003\u8d70\u3002\u201d\u72c4\u4e91\u9053\uff1a\u201c\u6211\u4e0d\u9003\u8d70\u4fbf\u662f\u3002\u201d\u5b9d\u8c61\u6012\u9053\uff1a\u201c\u6211\u8bf4\u751a\u4e48\uff0c\u4fbf\u662f\u751a\u4e48\u3002\u4f60\u80c6\u6562\u4e0d\u542c\u8bdd\uff1f\u201d\u8bf4\u7740\u4e00\u638c\u6325\u51fa\uff0c\u5728\u4ed6\u53f3\u8138\u4e0a\u91cd\u91cd\u4e00\u51fb\uff0c\u53c8\u5c06\u4ed6\u8e22\u4e86\u4e2a\u7b4b\u6597\u3002 \u3000\u3000\u72c4\u4e91\u6eda\u5728\u5730\u4e0b\uff0c\u7a81\u7136\u60f3\u8d77\uff1a\u201c\u4ed6\u53eb\u6211\u70e7\u6c34\uff0c\u5012\u662f\u4e2a\u673a\u4f1a\uff0c\u7b49\u5f97\u4e00\u5927\u956c\u6c34\u70e7\u6eda\uff0c\u7aef\u8d77\u6765\u6cfc\u5728\u4ed6\u8eab\u4e0a\u3002\u4ed6\u8d64\u8eab\u88f8\u4f53\uff0c\u5c82\u4e0d\u7acb\u65f6\u70eb\u6b7b\u4e86\uff1f\u201d\u5fc3\u4e2d\u5b58\u4e86\u8fd9\u4e2a\u4e3b\u610f\uff0c\u767b\u65f6\u4e0d\u518d\u6050\u60e7\uff0c\u4fbf\u5230\u53a8\u623f\u53bb\u5c06\u4e00\u53ea\u7834\u956c\u7aef\u4e86\u51fa\u6765\u3002\u89c1\u90a3\u94c1\u956c\u4e0a\u534a\u622a\u5df2\u7136\u6b8b\u7834\uff0c\u53ea\u80fd\u88c5\u5f97\u5c0f\u534a\u956c\u6c34\uff0c\u534a\u956c\u70eb\u6c34\u53ea\u6015\u672a\u5fc5\u80fd\u70eb\u6b7b\u8fd9\u6076\u50e7\uff0c\u4f46\u60f3\u5c31\u7b97\u6574\u4ed6\u4e0d\u6b7b\uff0c\u70eb\u4ed6\u4e2a\u534a\u6b7b\u4e0d\u6d3b\u4e5f\u662f\u597d\u7684\u3002 \u3000\u3000\u4ed6\u5c06\u94c1\u956c\u7aef\u5230\u6bbf\u524d\u5929\u4e95\u4e2d\uff0c\u63a5\u4e86\u6a90\u5934\u96e8\u6c34\uff0c\u5148\u884c\u6d17\u5237\u5e72\u51c0\uff0c\u7136\u540e\u88c5\u8f7d\u96e8\u6c34\uff0c\u76f4\u81f3\u6c34\u9f50\u7834\u53e3\uff0c\u65e0\u6cd5\u518d\u88c5\u4e3a\u6b62\u3002 \u3000\u3000\u5b9d\u8c61\u8d5e\u9053\uff1a\u201c\u597d\u6781\uff0c\u597d\u6781\uff01\u765e\u75e2\u5934\u963f\u4e09\uff0c\u6211\u5012\u771f\u4e0d\u820d\u5f97\u5403\u4e86\u4f60\u3002\u4f60\u8fd9\u4eba\u505a\u4e8b\u5e72\u51c0\u5229\u843d\uff0c\u716e\u4eba\u8089\u7fb9\u662f\u628a\u597d\u624b\u3002\u201d \u3000\u3000\u72c4\u4e91\u82e6\u7b11\u9053\uff1a\u201c\u591a\u8c22\u5927\u5e08\u7236\u5938\u5956\u3002\u201d\u62fe\u4e86\u4e03\u516b\u5757\u7816\u5934\uff0c\u67b6\u5728\u94c1\u956c\u4e0b\u9762\u3002\u7834\u5e99\u4e2d\u591a\u7684\u662f\u7834\u684c\u65ad\u6905\uff0c\u72c4\u4e91\u6025\u4e8e\u548c\u5b9d\u8c61\u4e00\u51b3\u751f\u6b7b\uff0c\u5feb\u624b\u5feb\u811a\u7684\u6267\u8d77\u7834\u65e7\u6728\u6599\uff0c\u5806\u5728\u94c1\u956c\u4e4b\u4e0b\u3002\u53ef\u662f\u8981\u5bfb\u706b\u79cd\uff0c\u5374\u662f\u96be\u4e86\u3002\u72c4\u4e91\u5f20\u5f00\u53cc\u624b\uff0c\u4f5c\u4e2a\u65e0\u53ef\u5948\u4f55\u7684\u795e\u6001\u3002 \u3000\u3000\u5b9d\u8c61\u9053\uff1a\u201c\u600e\u4e48\uff1f\u6ca1\u706b\u79cd\u5417\uff1f\u6211\u8bb0\u5f97\u4ed6\u8eab\u4e0a\u6709\u7684\u3002\u201d\u8bf4\u7740\u5411\u4e01\u5178\u7684\u5c38\u8eab\u4e00\u6307\u3002\u72c4\u4e91\u89c1\u4e01\u5178\u7684\u5927\u817f\u88ab\u5b9d\u8c61\u780d\u5f97\u8840\u8089\u6a21\u7cca\uff0c\u80f8\u4e2d\u4e00\u80a1\u60b2\u6124\u4e4b\u6c14\u76f4\u51b2\u4e0a\u6765\uff0c\u8f6c\u5934\u5411\u5b9d\u8c61\u72e0\u72e0\u77aa\u89c6\uff0c\u6068\u4e0d\u5f97\u6251\u4e0a\u524d\u53bb\u54ac\u4ed6\u51e0\u53e3\u3002\u5b9d\u8c61\u5374\u4f3c\u8001\u732b\u6349\u4f4f\u4e86\u8017\u5b50\u4e00\u822c\uff0c\u8981\u73a9\u5f04\u4e00\u756a\uff0c\u8fd9\u624d\u5403\u6389\uff0c\u5bf9\u72c4\u4e91\u7684\u6124\u6012\u4e1d\u6beb\u4e0d\u4ee5\u4e3a\u610f\uff0c\u7b11\u541f\u541f\u7684\u9053\uff1a\u201c\u4f60\u627e\u627e\u53bb\u554a\u3002\u82e5\u662f\u751f\u4e0d\u4e86\u706b\uff0c\u5927\u548c\u5c1a\u5403\u751f\u8089\u4e5f\u6210\u3002\u201d \u3000\u3000\u72c4\u4e91\u4fef\u4e0b\u8eab\u53bb\uff0c\u5728\u4e01\u5178\u7684\u8863\u888b\u4e2d\u4e00\u6478\uff0c\u679c\u7136\u6478\u5230\u4e24\u4ef6\u786c\u786c\u7684\u5c0f\u7269\uff0c\u6b63\u662f\u4e00\u628a\u706b\u5200\uff0c\u4e00\u5757\u706b\u77f3\uff0c\u5bfb\u601d\uff1a\u201c\u54b1\u4e8c\u4eba\u540c\u5728\u7262\u72f1\u4e4b\u65f6\uff0c\u4e01\u5927\u54e5\u8eab\u8fb9\u53ef\u6ca1\u8fd9\u4e24\u4ef6\u4e1c\u897f\uff0c\u4ed6\u5374\u4ece\u4f55\u5904\u5f97\u6765\uff1f\u201d\u7ffb\u8f6c\u706b\u5200\uff0c\u89c1\u5200\u4e0a\u94f8\u5f97\u6709\u4e00\u884c\u9633\u6587\u62db\u724c\uff1a\u201c\u8346\u5dde\u8001\u5408\u5174\u8bb0\u201d\u3002\u72c4\u4e91\u66fe\u548c\u4e01\u5178\u53bb\u94c1\u5e97\u65a9\u65ad\u8eab\u4e0a\u94d0\u9563\uff0c\u60f3\u6765\u8fd9\u4fbf\u662f\u90a3\u5bb6\u94c1\u5e97\u7684\u5e97\u53f7\u3002\u72c4\u4e91\u63e1\u4e86\u8fd9\u5bf9\u5200\u77f3\uff0c\u5fc3\u60f3\uff1a\u201c\u4e01\u5927\u54e5\u987e\u8651\u5468\u5168\uff0c\u5728\u94c1\u5e97\u4e2d\u53d6\u8fd9\u706b\u5200\u706b\u77f3\uff0c\u539f\u610f\u662f\u548c\u6211\u540c\u95ef\u6c5f\u6e56\u4e4b\u7528\uff0c\u4e0d\u6599\u6ca1\u7528\u4e0a\u4e00\u6b21\uff0c\u4fbf\u5df2\u547d\u8d74\u9634\u4e16\u3002\u201d\u6014\u6014\u7684\u77a7\u7740\u706b\u5200\u706b\u77f3\uff0c\u4e0d\u7531\u5f97\u6f78\u7136\u6cea\u4e0b\u3002 \u3000\u3000\u5b9d\u8c61\u53ea\u9053\u4ed6\u53d1\u89c1\u706b\u79cd\u540e\u81ea\u77e5\u547d\u4e0d\u957f\u4e45\uff0c\u662f\u4ee5\u60b2\u6ce3\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5927\u548c\u5c1a\u662f\u5343\u91d1\u8d35\u4f53\uff0c\u4f60\u524d\u4e16\u51e0\u751f\u4fee\u5230\uff0c\u7adf\u80fd\u62ff\u5927\u548c\u5c1a\u7684\u80a0\u80c3\u4f5c\u68fa\u6750\uff0c\u62ff\u5927\u548c\u5c1a\u7684\u809a\u76ae\u4f5c\u575f\u5893\uff0c\u798f\u7984\u6df1\u539a\uff0c\u8fd0\u6c14\u5f53\u771f\u4e0d\u574f\uff0c\u5feb\u751f\u706b\u7f62\uff01\u201d \u3000\u3000\u72c4\u4e91\u66f4\u4e0d\u591a\u8a00\uff0c\u5728\u5e99\u4e2d\u627e\u5230\u4e86\u4e00\u5f20\u9648\u65e7\u5df2\u6781\u7684\u9ec4\u7eb8\u7b7e\uff0c\u653e\u5728\u706b\u5200\u3001\u706b\u77f3\u4e4b\u65c1\uff0c\u4fbf\u6253\u7740\u4e86\u706b\u3002\u706b\u7130\u70e7\u5230\u9ec4\u7eb8\u7b7e\u4e0a\uff0c\u672c\u6765\u88ab\u7070\u5c18\u63a9\u853d\u7740\u7684\u5b57\u8ff9\u9732\u4e86\u51fa\u6765\uff0c\u53ea\u89c1\u7b7e\u4e0a\u5370\u7740\u201c\u4e0b\u4e0b\u201d\u3001\u201c\u6c42\u5b98\u4e0d\u6210\u201d\u3001\u201c\u5a5a\u59fb\u96be\u8c10\u201d\u3001\u201c\u51fa\u884c\u4e0d\u5229\u201d\u3001\u201c\u75be\u75c5\u96be\u6108\u201d\u7b49\u5b57\u6837\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u706b\u820c\u4fbf\u5c06\u7eb8\u7b7e\u70e7\u53bb\u4e86\u534a\u622a\u3002\u72c4\u4e91\u5fc3\u60f3\uff1a\u201c\u6211\u4e00\u751f\u4e0d\u5e78\uff0c\u4e0d\u7528\u6c42\u7b7e\u4fbf\u77e5\u9053\u4e86\u3002\u201d\u5f53\u5373\u5c06\u7eb8\u7b7e\u53bb\u70b9\u71c3\u4e86\u6728\u7247\uff0c\u956c\u5e95\u7684\u67af\u6728\u6e10\u70e7\u6e10\u65fa\u3002 \u3000\u3000\u94c1\u956c\u4e2d\u7684\u6e05\u6c34\u6162\u6162\u751f\u51fa\u87f9\u773c\u6ce1\u6cab\uff0c\u4ed6\u77e5\u8fd9\u534a\u956c\u6c34\u8fc7\u4e0d\u5230\u4e00\u70b7\u9999\u65f6\u5206\u4fbf\u5373\u6cb8\u6eda\u3002\u4ed6\u5fc3\u795e\u7d27\u5f20\uff0c\u671b\u671b\u90a3\u6c34\uff0c\u53c8\u671b\u671b\u5b9d\u8c61\u88f8\u9732\u7740\u7684\u809a\u76ae\uff0c\u5fc3\u60f3\u751f\u6b7b\u5b58\u4ea1\u5728\u6b64\u4e00\u4e3e\uff0c\u4e00\u53cc\u624b\u4e0d\u81ea\u7981\u7684\u6253\u8d77\u98a4\u6765\u3002\u7ec8\u4e8e\u767d\u6c14\u84b8\u817e\uff0c\u7834\u956c\u4e2d\u6c34\u6ce1\u7ffb\u6d8c\u3002\u72c4\u4e91\u7ad9\u76f4\u8eab\u5b50\uff0c\u7aef\u8d77\u94c1\u956c\uff0c\u53cc\u624b\u4e00\u62ac\uff0c\u4fbf\u8981\u5411\u5b9d\u8c61\u5934\u4e0a\u6dcb\u53bb\u3002 \u3000\u3000\u5c82\u77e5\u4ed6\u8eab\u5f62\u752b\u52a8\uff0c\u5b9d\u8c61\u5df2\u7136\u60ca\u89c9\uff0c\u5341\u6307\u4f38\u51fa\uff0c\u62a2\u5148\u6293\u4f4f\u4e86\u4ed6\u7684\u624b\u8155\uff0c\u5389\u58f0\u559d\u9053\uff1a\u201c\u5e72\u751a\u4e48\uff1f\u201d\u72c4\u4e91\u4e0d\u4f1a\u8bf4\u8c0e\uff0c\u7528\u529b\u60f3\u5c06\u6eda\u6c64\u5f80\u5b9d\u8c61\u8eab\u4e0a\u6cfc\u53bb\uff0c\u4f46\u624b\u8155\u7ed9\u6293\u4f4f\u4e86\uff0c\u4fbf\u4f3c\u5957\u5728\u4e00\u53cc\u94c1\u7b8d\u4e2d\u4e00\u822c\uff0c\u7adf\u79fb\u52a8\u4e0d\u5f97\u5206\u6beb\u3002 \u3000\u3000\u5b9d\u8c61\u82e5\u8981\u5c06\u8fd9\u956c\u6eda\u6c64\u6cfc\u5728\u72c4\u4e91\u5934\u4e0a\uff0c\u53ea\u987b\u624b\u81c2\u4e00\u7529\uff0c\u90a3\u662f\u8f7b\u800c\u6613\u4e3e\u4e4b\u4e8b\uff0c\u4f46\u5374\u53ef\u60dc\u4e86\u8fd9\u534a\u956c\u70ed\u6c64\uff0c\u6dcb\u6b7b\u4e86\u8fd9\u765e\u75e2\u5934\u963f\u4e09\uff0c\u81ea\u5df1\u91cd\u65b0\u70e7\u6c64\uff0c\u672a\u514d\u9ebb\u70e6\u3002\u4ed6\u53cc\u81c2\u5fae\u4e00\u7528\u52b2\uff0c\u5e73\u5e73\u4e0b\u538b\uff0c\u5c06\u94c1\u956c\u653e\u56de\u539f\u5904\uff0c\u559d\u9053\uff1a\u201c\u653e\u5f00\u4e86\u624b\uff01\u201d \u3000\u3000\u72c4\u4e91\u5982\u4f55\u80af\u653e\u5f00\u94c1\u956c\uff0c\u53cc\u624b\u53c8\u662f\u8fd0\u52b2\u4e00\u593a\u3002\u5b9d\u8c61\u53f3\u8db3\u8e22\u51fa\uff0c\u7830\u7684\u4e00\u58f0\uff0c\u5c06\u4ed6\u8e22\u5f97\u76f4\u8dcc\u51fa\u53bb\uff0c\u5934\u540e\u811a\u524d\uff0c\u649e\u5165\u795e\u575b\u4e4b\u4e0b\u3002\u5b9d\u8c61\u5fc3\u60f3\uff1a\u201c\u8fd9\u765e\u75e2\u5934\u624b\u52b2\u5012\u4e5f\u4e0d\u5c0f\u3002\u201d\u8fd9\u65f6\u4e5f\u4e0d\u52a0\u7ec6\u60f3\uff0c\u559d\u9053\uff1a\u201c\u8001\u5b50\u8981\u5bb0\u4f60\u4e86\u3002\u4e56\u4e56\u7684\u81ea\u5df1\u89e3\u53bb\u8863\u670d\uff0c\u7701\u5f97\u8001\u5b50\u8d39\u4e8b\u3002\u201d \u3000\u3000\u72c4\u4e91\u6478\u51fa\u8170\u95f4\u85cf\u7740\u7684\u5c16\u77f3\uff0c\u4fbf\u60f3\u51b2\u51fa\u53bb\u4e0e\u8fd9\u6076\u50e7\u4e00\u62da\uff0c\u5ffd\u89c1\u795e\u575b\u811a\u8fb9\u4e24\u53ea\u8001\u9f20\u809a\u5b50\u5411\u5929\uff0c\u8eab\u5b50\u4e0d\u4f4f\u62bd\u6410\uff0c\u5c06\u6b7b\u672a\u6b7b\uff0c\u8fd9\u4e00\u4e0b\u9661\u7136\u5728\u9ed1\u6697\u4e2d\u770b\u5230\u4e00\u4e1d\u5149\u660e\uff0c\u53eb\u9053\uff1a\u201c\u6211\u6349\u5230\u4e86\u4e24\u53ea\u8001\u9f20\uff0c\u7ed9\u4f60\u5148\u5403\u8d77\u6765\u5145\u9965\uff0c\u597d\u4e0d\u597d\uff1f\u8001\u9f20\u7684\u6ecb\u5473\u53ef\u9c9c\u5f97\u7d27\u5462\uff0c\u6bd4\u72d7\u8089\u8fd8\u9999\u3002\u201d\u5b9d\u8c61\u9053\uff1a\u201c\u751a\u4e48\uff1f\u662f\u8001\u9f20\uff1f\u662f\u6b7b\u7684\u8fd8\u662f\u6d3b\u7684\uff1f\u201d\u72c4\u4e91\u751f\u6015\u4ed6\u4e0d\u5403\u6b7b\u9f20\uff0c\u5fd9\u9053\uff1a\u201c\u81ea\u7136\u662f\u6d3b\u7684\uff0c\u8fd8\u5728\u52a8\u5462\uff0c\u53ea\u4e0d\u8fc7\u7ed9\u6211\u634f\u5f97\u534a\u6b7b\u4e0d\u6d3b\u4e86\u3002\u201d\u6293\u4f4f\u4e24\u53ea\u8001\u9f20\uff0c\u4ece\u795e\u575b\u4e0b\u4f38\u624b\u51fa\u6765\u7ed9\u4ed6\u770b\u3002 \u3000\u3000\u5b9d\u8c61\u66fe\u5403\u8fc7\u8001\u9f20\uff0c\u77e5\u9053\u9f20\u8089\u4e4b\u5473\u4e0e\u7626\u732a\u8089\u4e5f\u5dee\u4e0d\u591a\uff0c\u773c\u89c1\u8fd9\u4e24\u5934\u8001\u9f20\u6beb\u4e0d\u80a5\u5927\uff0c\u60f3\u662f\u7834\u5e99\u4e4b\u4e2d\u65e0\u751a\u98df\u7269\u4e4b\u6545\uff0c\u4e00\u65f6\u6c89\u541f\u672a\u51b3\u3002 \u3000\u3000\u72c4\u4e91\u9053\uff1a\u201c\u5927\u5e08\u7236\uff0c\u6211\u7ed9\u4f60\u5265\u4e86\u8001\u9f20\u76ae\uff0c\u716e\u4e00\u5927\u7897\u6c64\u559d\uff0c\u5305\u4f60\u53c8\u5feb\u53c8\u7f8e\u3002\u201d \u3000\u3000\u5b9d\u8c61\u662f\u4e2a\u5927\u61d2\u4eba\uff0c\u8981\u4ed6\u52a8\u624b\u6740\u4eba\u6d17\u5265\uff0c\u5272\u5207\u716e\u98df\uff0c\u60f3\u8d77\u6765\u5c31\u89c9\u5fc3\u70e6\uff0c\u542c\u72c4\u4e91\u8bf4\u7ed9\u4ed6\u716e\u8001\u9f20\u6c64\uff0c\u5012\u662f\u6295\u5176\u6240\u597d\uff0c\u9053\uff1a\u201c\u4e24\u53ea\u8001\u9f20\u4e0d\u591f\u5403\uff0c\u4f60\u518d\u53bb\u591a\u6349\u51e0\u53ea\u3002\u201d \u3000\u3000\u72c4\u4e91\u5fc3\u60f3\uff1a\u201c\u6211\u73b0\u4e0b\u529f\u592b\u5df2\u5931\uff0c\u624b\u811a\u4e0d\u7075\uff0c\u8001\u9f20\u54ea\u91cc\u6349\u5f97\u5230\uff1f\u201d\u4f46\u597d\u5bb9\u6613\u51fa\u73b0\u4e86\u4e00\u7ebf\u751f\u673a\uff0c\u51b3\u4e0d\u80fd\u653e\u8fc7\uff0c\u5fd9\u9053\uff1a\u201c\u5927\u5e08\u7236\uff0c\u6211\u7ed9\u4f60\u5148\u716e\u4e86\u8fd9\u4e24\u53ea\u5927\u8001\u9f20\u4f5c\u70b9\u5fc3\uff0c\u7acb\u523b\u518d\u6349\uff01\u201d \u3000\u3000\u5b9d\u8c61\u70b9\u5934\u9053\uff1a\u201c\u90a3\u4e5f\u597d\uff0c\u8981\u662f\u6211\u5403\u5f97\u4e2a\u9971\uff0c\u9976\u4f60\u4e00\u547d\uff0c\u53c8\u6709\u4f55\u59a8\uff1f\u201d \u3000\u3000\u72c4\u4e91\u4ece\u795e\u575b\u4e0b\u94bb\u4e86\u51fa\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u501f\u4f60\u7684\u5200\u5b50\u4e00\u7528\uff0c\u5207\u4e86\u8001\u9f20\u7684\u5934\u3002\u201d \u3000\u3000\u5b9d\u8c61\u6d51\u6ca1\u5f53\u8fd9\u4e61\u4e0b\u5c0f\u79c3\u5b50\u662f\u4e00\u56de\u4e8b\uff0c\u5411\u5355\u5200\u4e00\u6307\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u7528\u7f62\uff01\u201d\u8ddf\u7740\u53c8\u8865\u4e0a\u4e00\u53e5\uff1a\u201c\u4f60\u6709\u80c6\u5b50\uff0c\u4fbf\u5411\u8001\u5b50\u780d\u4e0a\u51e0\u5200\u8bd5\u8bd5\uff01\u201d \u3000\u3000\u72c4\u4e91\u672c\u6765\u786e\u6709\u62a2\u5230\u5355\u5200\u3001\u56de\u8eab\u4fbf\u780d\u4e4b\u610f\uff0c\u4f46\u7ed9\u4ed6\u5148\u884c\u70b9\u7834\uff0c\u5012\u4e0d\u6562\u8f7b\u4e3e\u5984\u52a8\u4e86\uff0c\u4e24\u5200\u780d\u4e0b\u9f20\u5934\uff0c\u5f00\u819b\u7834\u809a\uff0c\u5265\u4e0b\u9f20\u76ae\uff0c\u5c06\u8001\u9f20\u7684\u80a0\u80c3\u5fc3\u80ba\u4e00\u5e76\u7528\u96e8\u6c34\u6d17\u5f97\u5e72\u51c0\uff0c\u7136\u540e\u653e\u5165\u956c\u4e2d\u3002 \u3000\u3000\u5b9d\u8c61\u8fde\u8fde\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u5f88\u597d\uff0c\u5f88\u597d\u3002\u4f60\u8fd9\u79c3\u5934\uff0c\u716e\u8001\u9f20\u6c64\u662f\u628a\u597d\u624b\u3002\u5feb\u518d\u53bb\u6349\u51e0\u53ea\u6765\u3002\u201d\u72c4\u4e91\u9053\uff1a\u201c\u597d\uff0c\u6211\u53bb\u6349\u3002\u201d\u8f6c\u8eab\u5411\u540e\u6bbf\u8d70\u53bb\u3002\u5b9d\u8c61\u8bf4\uff1a\u201c\u4f60\u82e5\u60f3\u9003\u8d70\uff0c\u6211\u5b9a\u5c06\u4f60\u8eab\u4e0a\u7684\u8089\uff0c\u4e00\u5757\u5757\u6d3b\u751f\u751f\u7684\u5272\u4e0b\u6765\u5403\u4e86\u3002\u201d\u72c4\u4e91\u9053\uff1a\u201c\u6349\u4e0d\u5230\u8001\u9f20\u4fbf\u6349\u7530\u9e21\uff0c\u6c5f\u91cc\u6709\u9c7c\u6709\u867e\uff0c\u751a\u4e48\u90fd\u80fd\u5403\u3002\u6211\u670d\u4f8d\u4f60\u5927\u5e08\u7236\uff0c\u5403\u5f97\u9971\u9971\u7684\uff0c\u8212\u8212\u670d\u670d\uff0c\u4f55\u5fc5\u5b9a\u8981\u5403\u6211\uff1f\u765e\u75e2\u5934\u963f\u4e09\u8eab\u4e0a\u6709\u75ae\u6709\u765e\uff0c\u5403\u4e86\u62c5\u4fdd\u4f60\u62c9\u809a\u5b50\uff0c\u53d1\u5bd2\u70ed\u3002\u201d\u5b9d\u8c61\u9053\uff1a\u201c\u54fc\uff0c\u522b\u8ba9\u6211\u7b49\u5f97\u4e0d\u8010\u70e6\u4e86\u3002\u5582\uff0c\u4f60\u4e0d\u80fd\u8d70\u51fa\u5e99\u53bb\uff0c\u77e5\u4e0d\u77e5\u9053\uff1f\u201d \u3000\u3000\u72c4\u4e91\u5927\u58f0\u7b54\u5e94\uff0c\u722c\u5728\u5730\u4e0b\uff0c\u88c5\u7740\u6355\u8001\u9f20\u7684\u795e\u6001\uff0c\u6162\u6162\u722c\u5230\u540e\u6bbf\uff0c\u7ad9\u76f4\u4e86\u8eab\u5b50\u3002\u4ed6\u4e1c\u5f20\u897f\u671b\uff0c\u60f3\u627e\u4e2a\u9690\u853d\u5904\u8eb2\u4e86\u8d77\u6765\uff0c\u4ece\u540e\u95e8\u671b\u51fa\u53bb\uff0c\u89c1\u5de6\u9996\u6709\u4e2a\u5c0f\u5c0f\u6c60\u5858\uff0c\u5f53\u4e0b\u4e0d\u7ba1\u4e09\u4e03\u4e8c\u5341\u4e00\uff0c\u5feb\u6b65\u5954\u53bb\uff0c\u8f7b\u8f7b\u6e9c\u5165\u6c60\u5858\uff0c\u53ea\u9732\u51fa\u53e3\u9f3b\u5728\u6c34\u9762\u900f\u6c14\uff0c\u66f4\u6293\u4e9b\u6d6e\u840d\u4e71\u8349\uff0c\u5806\u5728\u9f3b\u4e0a\u3002 \u3000\u3000\u4ed6\u81ea\u5e7c\u751f\u4e8e\u6c34\u6ee8\uff0c\u6c34\u6027\u5012\u662f\u6781\u597d\uff0c\u53ea\u53ef\u60dc\u8fd9\u5730\u65b9\u79bb\u6c5f\u592a\u8fdc\uff0c\u5426\u5219\u8dc3\u5165\u5927\u6c5f\u4e4b\u4e2d\uff0c\u987a\u6d41\u800c\u4e0b\uff0c\u5b9d\u8c61\u65e0\u8bba\u5982\u4f55\u8ffd\u8d76\u4e0d\u4e0a\u3002 \u3000\u3000\u8fc7\u4e86\u597d\u4e00\u4f1a\uff0c\u53ea\u542c\u5f97\u5b9d\u8c61\u53eb\u9053\uff1a\u201c\u597d\u6c64\uff01\u8001\u9f20\u6c64\u4e0d\u9519\u3002\u53ef\u60dc\u8001\u9f20\u592a\u5c11\u3002\u5c0f\u79c3\u5b50\u963f\u4e09\uff0c\u6349\u5230\u4e86\u8001\u9f20\u6ca1\u6709\uff1f\u201d\u53eb\u4e86\u51e0\u58f0\uff0c\u8ddf\u7740\u4fbf\u5927\u58f0\u5492\u9a82\u8d77\u6765\u3002\u72c4\u4e91\u5c06\u53f3\u8033\u4f38\u51fa\u6c34\u9762\uff0c\u542c\u4ed6\u7684\u52a8\u9759\u3002\u4f46\u542c\u4ed6\u6ee1\u53e3\u6c61\u8a00\u79fd\u8bed\uff0c\u9a82\u5f97\u7c97\u4fd7\u4e0d\u582a\uff0c\u8ddf\u7740\u8e22\u8e22\u8df6\u8df6\uff0c\u8e0f\u7740\u6ce5\u6cde\u5bfb\u4e86\u51fa\u6765\u3002\u53ea\u8de8\u5f97\u51e0\u6b65\uff0c\u4fbf\u5230\u4e86\u6c60\u5858\u8fb9\u3002\u72c4\u4e91\u54ea\u91cc\u8fd8\u6562\u9732\u9762\uff0c\u634f\u4f4f\u9f3b\u5b50\uff0c\u5168\u8eab\u94bb\u5728\u6c34\u5e95\u3002\u5e78\u597d\u90a3\u6c60\u5858\u751f\u6ee1\u4e86\u9752\u840d\u6c34\u85fb\uff0c\u4ed6\u4e00\u6c89\u5165\u5858\u5e95\uff0c\u5728\u4e0a\u9762\u4fbf\u770b\u4e0d\u5230\u4e86\u3002 \u3000\u3000\u4f46\u6c34\u5e95\u4e0d\u80fd\u900f\u6c14\uff0c\u4ed6\u4e00\u76f4\u71ac\u5230\u5fcd\u65e0\u53ef\u5fcd\uff0c\u7ec8\u4e8e\u6162\u6162\u63a2\u5934\u4e0a\u6765\uff0c\u60f3\u8f7b\u8f7b\u5438\u4e00\u53e3\u6c14\uff0c\u521a\u5438\u5f97\u534a\u53e3\uff0c\u5ffd\u5587\u4e00\u58f0\uff0c\u4e00\u53ea\u5927\u624b\u6293\u5c06\u4e0b\u6765\uff0c\u5df2\u6293\u4f4f\u4e86\u4ed6\u540e\u9888\u3002\u5b9d\u8c61\u5927\u9a82\uff1a\u201c\u4e0d\u628a\u4f60\u8fd9\u5c0f\u79c3\u5b50\u5272\u6210\u5341\u4e03\u516b\u5757\uff0c\u8001\u5b50\u4e0d\u662f\u4eba\u3002\u4f60\u80c6\u6562\u9003\u8d70\uff01\u201d\u72c4\u4e91\u53cd\u624b\u62b1\u4f4f\u4ed6\u80f3\u81c2\uff0c\u4e00\u80a1\u52b2\u513f\u5f80\u6c60\u5858\u5185\u62c9\u626f\u3002\u5b9d\u8c61\u6ca1\u6599\u5230\u4ed6\u7adf\u6562\u53cd\u6297\uff0c\u5858\u8fb9\u6ce5\u6cde\uff0c\u811a\u4e0b\u4e00\u6ed1\uff0c\u6251\u901a\u4e00\u58f0\uff0c\u8dcc\u5165\u4e86\u5858\u4e2d\u3002 \u3000\u3000\u72c4\u4e91\u5927\u559c\uff0c\u4f7f\u52b2\u5c06\u4ed6\u80cc\u810a\u5f80\u6c34\u4e2d\u6309\u53bb\u3002\u53ea\u662f\u6c60\u5858\u6c34\u6d45\uff0c\u5b9d\u8c61\u4eba\u53c8\u9ad8\u5927\uff0c\u6c60\u6c34\u6df9\u4e0d\u8fc7\u9876\uff0c\u4ed6\u4e00\u8e0f\u5230\u5858\u5e95\uff0c\u53cd\u624b\u4fbf\u6263\u4f4f\u72c4\u4e91\u624b\u8155\uff0c\u8ddf\u7740\u5de6\u624b\u5c06\u4ed6\u5934\u6380\u4e0b\u6c34\u53bb\u3002\u72c4\u4e91\u65e9\u8c41\u51fa\u4e86\u6027\u547d\u4e0d\u8981\uff0c\u4eba\u5728\u6c34\u5e95\uff0c\u7262\u7262\u62b1\u4f4f\u4e86\u5b9d\u8c61\u8eab\u5b50\uff0c\u8bf4\u751a\u4e48\u4e5f\u4e0d\u653e\u624b\u3002\u5b9d\u8c61\u4e00\u65f6\u5012\u7ed9\u4ed6\u5f04\u5f97\u65e0\u6cd5\u53ef\u65bd\uff0c\u7834\u53e3\u5927\u9a82\uff0c\u4e00\u4e0d\u5c0f\u5fc3\uff0c\u541e\u8fdb\u4e86\u51e0\u53e3\u6c61\u6c34\uff0c\u6012\u6c14\u66f4\u76db\uff0c\u63d0\u8d77\u62f3\u5934\uff0c\u76f4\u5f80\u72c4\u4e91\u80cc\u4e0a\u64c2\u53bb\u3002 \u3000\u3000\u72c4\u4e91\u53ea\u89c9\u8fd9\u6076\u50e7\u4e00\u62f3\u6253\u6765\uff0c\u867d\u7ed9\u5858\u6c34\u963b\u4e86\u4e00\u963b\uff0c\u529b\u9053\u8f7b\u4e86\u4e9b\uff0c\u5374\u4e5f\u75bc\u75db\u96be\u5fcd\uff0c\u53ea\u8981\u518d\u6328\u5f97\u51e0\u62f3\uff0c\u975e\u660f\u53bb\u4e0d\u53ef\u3002\u4ed6\u7edd\u65e0\u8fd8\u624b\u4e4b\u529b\uff0c\u53ea\u6709\u5c06\u8111\u888b\u53bb\u649e\u5b9d\u8c61\u7684\u80f8\u819b\u3002 \u3000\u3000\u6b63\u7ea0\u7f20\u5f97\u4e0d\u53ef\u5f00\u4ea4\uff0c\u7a81\u7136\u4e4b\u95f4\uff0c\u5b9d\u8c61\u5927\u53eb\u4e00\u58f0\uff1a\u201c\u554a\u54df\uff01\u201d \u3000\u3000\u6293\u4f4f\u72c4\u4e91\u7684\u624b\u6162\u6162\u653e\u677e\uff0c\u4e3e\u5728\u534a\u7a7a\u7684\u62f3\u5934\u4e5f\u4e0d\u51fb\u843d\uff0c\u7adf\u7f13\u7f13\u7684\u5782\u4e0b\uff0c\u8ddf\u7740\u8eab\u5b50\u633a\u4e86\u51e0\u633a\uff0c\u6c89\u5165\u4e86\u5858\u5e95\u3002 \u3000\u3000\u72c4\u4e91\u5927\u5947\uff0c\u5fd9\u6323\u624e\u7740\u8d77\u6765\uff0c\u53ea\u89c1\u5b9d\u8c61\u4e00\u52a8\u4e0d\u52a8\uff0c\u663e\u5df2\u6b7b\u4e86\u3002\u4ed6\u60ca\u9b42\u672a\u5b9a\uff0c\u4e0d\u6562\u53bb\u78b0\u4ed6\u8eab\u5b50\uff0c\u8fdc\u8fdc\u7ad9\u5728\u6c60\u5858\u4e00\u8fb9\u89c2\u770b\u3002 \u3000\u3000\u53ea\u89c1\u5b9d\u8c61\u76f4\u633a\u633a\u7684\u8eba\u5728\u5858\u5e95\uff0c\u4e00\u52a8\u4e5f\u4e0d\u518d\u52a8\uff0c\u9694\u4e86\u826f\u4e45\uff0c\u770b\u6765\u771f\u7684\u5df2\u6b7b\uff0c\u72c4\u4e91\u5140\u81ea\u4e0d\u6562\u653e\u5fc3\uff0c\u6367\u8d77\u4e00\u5757\u77f3\u5934\u63b7\u5230\u4ed6\u8eab\u4e0a\uff0c\u89c1\u4ecd\u662f\u4e0d\u52a8\uff0c\u624d\u77e5\u4e0d\u662f\u88c5\u6b7b\u3002 \u3000\u3000\u72c4\u4e91\u722c\u4e0a\u5cb8\u6765\uff0c\u731c\u4e0d\u900f\u8fd9\u6076\u50e7\u5230\u5e95\u5982\u4f55\u4f1a\u5ffd\u7136\u6b7b\u53bb\uff0c\u5fc3\u4e2d\u5ffd\u7136\u95ea\u8fc7\u4e00\u4e2a\u5ff5\u5934\uff1a\u201c\u96be\u9053\u6211\u7684\u795e\u7167\u529f\u5df2\u7136\u5927\u6709\u5a01\u529b\uff0c\u81ea\u5df1\u53ef\u8fd8\u4e0d\u77e5\uff1f\u5728\u4ed6\u80f8\u53e3\u649e\u5f97\u51e0\u5934\uff0c\u4fbf\u9001\u4e86\u4ed6\u7684\u6027\u547d\uff1f\u201d\u8bd5\u4e00\u8fd0\u6c14\uff0c\u53ea\u89c9\u201c\u8db3\u5c11\u9633\u80c6\u7ecf\u201d\u4e00\u8109\u4e2d\u7684\u5185\u606f\uff0c\u884c\u5230\u5927\u817f\u201c\u4e94\u91cc\u7a74\u201d\uff0c\u65e0\u8bba\u5982\u4f55\u4fbf\u4e0d\u4e0a\u884c\uff0c\u800c\u201c\u624b\u5c11\u9633\u4e09\u7126\u7ecf\u201d\u4e00\u8109\uff0c\u5185\u606f\u884c\u5230\u4e0a\u81c2\uff0c\u201c\u6e05\u51b7\u6e0a\u201d\u4e5f\u5373\u9047\u963b\u6ede\u3002\u6bd4\u4e4b\u5728\u72f1\u4e2d\u65f6\u53ea\u6709\u53cd\u89c1\u9000\u6b65\uff0c\u60f3\u662f\u8fd9\u51e0\u65e5\u6765\u5fc3\u795e\u4e0d\u5b9a\uff0c\u6401\u4e0b\u4e86\u529f\u592b\u6240\u81f4\u3002\u663e\u7136\uff0c\u8981\u7ec3\u6210\u795e\u7167\u529f\uff0c\u65f6\u65e5\u706b\u5019\u8fd8\u5dee\u5f97\u5f88\u8fdc\u3002 \u3000\u3000\u4ed6\u6014\u6014\u7684\u7ad9\u5728\u6c60\u5858\u4e4b\u65c1\uff0c\u5bf9\u773c\u524d\u7684\u60c5\u666f\u59cb\u7ec8\u4e0d\u6562\u76f8\u4fe1\u662f\u771f\u4e8b\uff0c\u4f46\u89c1\u96e8\u70b9\u4e00\u6ef4\u6ef4\u7684\u843d\u5728\u6c60\u5858\u6c34\u9762\uff0c\u6fc0\u8d77\u4e00\u4e2a\u4e2a\u6f2a\u6d9f\u3002\u5b9d\u8c61\u7684\u5c38\u8eab\u8eba\u5728\u5858\u5e95\uff0c\u4e86\u65e0\u534a\u4e1d\u751f\u6c14\u3002 \u3000\u3000\u5446\u4e86\u4e00\u9635\uff0c\u56de\u5230\u6bbf\u4e2d\uff0c\u53ea\u89c1\u94c1\u956c\u4e0b\u7684\u67f4\u706b\u5df2\u7ecf\u7184\u706d\uff0c\u94c1\u956c\u65c1\u53c8\u6709\u4e24\u53ea\u8001\u9f20\u6b7b\u5728\u5730\u4e0b\uff0c\u809a\u76ae\u671d\u5929\uff0c\u8033\u6735\u548c\u540e\u8db3\u5140\u81ea\u5fae\u5fae\u6296\u52a8\u3002\u72c4\u4e91\u5fc3\u60f3\uff1a\u201c\u55ef\uff0c\u539f\u6765\u5b9d\u8c61\u81ea\u5df1\u5012\u6349\u5230\u4e86\u4e24\u53ea\u8001\u9f20\uff0c\u6ca1\u798f\u4eab\u53d7\uff0c\u4fbf\u7ed9\u6211\u6253\u6b7b\u4e86\u3002\u201d\u89c1\u956c\u4e2d\u5c1a\u6709\u7897\u8bb8\u6b8b\u6c64\uff0c\u662f\u5b9d\u8c61\u559d\u5f97\u5269\u4e0b\u6765\u7684\uff0c\u4ed6\u809a\u4e2d\u6b63\u9965\uff0c\u7aef\u8d77\u94c1\u956c\uff0c\u5f20\u53e3\u4fbf\u8981\u53bb\u559d\u8001\u9f20\u6c64\u3002 \u3000\u3000\u7a81\u7136\u4e4b\u95f4\uff0c\u9f3b\u4e2d\u95fb\u5230\u4e00\u9635\u5947\u7279\u7684\u9999\u5473\u3002 \u3000\u3000\u4ed6\u4e00\u5446\u4e4b\u4e0b\uff0c\u53cc\u624b\u6301\u7740\u94c1\u956c\uff0c\u7f29\u5634\u4e0d\u559d\uff0c\u5bfb\u601d\uff0c\u201c\u8fd9\u662f\u751a\u4e48\u9999\u6c14\uff1f\u6211\u95fb\u5230\u8fc7\u7684\uff0c\u90a3\u51b3\u4e0d\u662f\u751a\u4e48\u597d\u4e1c\u897f\u3002\u201d\u518d\u95fb\u4e86\u95fb\u8001\u9f20\u6c64\u4e2d\u7684\u5947\u9999\uff0c\u767b\u65f6\u7701\u609f\uff0c\u5927\u53eb\u4e00\u58f0\uff1a\u201c\u597d\u8fd0\u6c14\uff01\u201d\u53cc\u624b\u4e00\u62ac\uff0c\u5c06\u94c1\u956c\u5411\u5929\u4e95\u4e2d\u629b\u4e86\u51fa\u53bb\uff0c\u8f6c\u8fc7\u8eab\u6765\uff0c\u5411\u7740\u4e01\u5178\u7684\u5c38\u8eab\u542b\u6cea\u8bf4\u9053\uff1a\u201c\u4e01\u5927\u54e5\uff0c\u4f60\u867d\u5728\u6b7b\u540e\uff0c\u53c8\u6551\u4e86\u5144\u5f1f\u4e00\u547d\u3002\u201d \u3000\u3000\u5728\u5343\u94a7\u4e00\u53d1\u7684\u77ac\u606f\u4e4b\u95f4\uff0c\u4ed6\u660e\u767d\u4e86\u5b9d\u8c61\u7684\u6b7b\u56e0\u3002 \u3000\u3000\u4e01\u5178\u4e2d\u4e86\u201c\u91d1\u6ce2\u65ec\u82b1\u201d\u7684\u5267\u6bd2\uff0c\u5168\u8eab\u8840\u8089\u90fd\u542b\u5947\u6bd2\u3002\u5b9d\u8c61\u5200\u780d\u4e01\u5178\u5c38\u8eab\uff0c\u8001\u9f20\u5728\u4f24\u53e3\u4e2d\u566c\u98df\u8840\u8089\u3002\u8001\u9f20\u98df\u540e\u4e2d\u6bd2\u800c\u6b7b\uff0c\u5b9d\u8c61\u716e\u9f20\u4e3a\u6c64\u800c\u98df\uff0c\u8ddf\u770b\u4fbf\u4e5f\u4e2d\u6bd2\u3002\u4e24\u4eba\u5728\u6c60\u5858\u4e2d\u7ea0\u7f20\u6597\u6bb4\uff0c\u5b9d\u8c61\u7a81\u7136\u6bd2\u53d1\u8eab\u4ea1\u3002\u773c\u524d\u94c1\u956c\u65c1\u8fd9\u4e24\u5934\u6b7b\u9f20\uff0c\u4e5f\u662f\u559d\u4e86\u956c\u4e2d\u7684\u6bd2\u6c64\u800c\u6b7b\u7684\u3002 \u3000\u3000\u72c4\u4e91\u5fc3\u60f3\uff1a\u201c\u5018\u82e5\u90a3\u91d1\u6ce2\u65ec\u82b1\u4e0d\u662f\u6709\u8fd9\u4e48\u4e00\u80a1\u5947\u602a\u7684\u9999\u6c14\uff0c\u5018\u82e5\u6211\u5fc3\u601d\u8f6c\u5f97\u7a0d\u6162\u7247\u523b\uff0c\u8fd9\u6bd2\u6c64\u5df2\u7136\u4e0b\u809a\u53bb\u4e86\u3002\u201d \u3000\u3000\u53c8\u60f3\uff1a\u201c\u6211\u7b2c\u4e00\u6b21\u95fb\u5230\u8fd9\u2018\u91d1\u6ce2\u65ec\u82b1\u2019\u7684\u9999\u6c14\uff0c\u662f\u5728\u51cc\u5c0f\u59d0\u7684\u7075\u5802\u4e4b\u4e2d\uff0c\u51cc\u77e5\u5e9c\u6d82\u4e86\u5728\u4ed6\u5973\u513f\u7684\u68fa\u6728\u4e0a\u3002\u4e01\u5927\u54e5\u4ee5\u524d\u5374\u66fe\u95fb\u8fc7\u7684\uff0c\u66fe\u4e2d\u8fc7\u6bd2\uff0c\u7b2c\u4e8c\u6b21\u600e\u80fd\u4e0d\u77e5\uff1f\u662f\u4e86\uff0c\u90a3\u65f6\u4e01\u5927\u54e5\u89c1\u5230\u51cc\u5c0f\u59d0\u7684\u68fa\u6728\uff0c\u5fc3\u795e\u5927\u4e71\uff0c\u751a\u4e48\u90fd\u4e0d\u77e5\u9053\u4e86\u3002\u201d \u3000\u3000\u4ed6\u66fe\u6570\u5ea6\u4e07\u5ff5\u4ff1\u7070\uff0c\u81ea\u66b4\u81ea\u5f03\uff0c\u4e0d\u60f3\u518d\u6d3b\u5728\u4eba\u4e16\uff0c\u4f46\u6b64\u523b\u6b7b\u91cc\u9003\u751f\uff0c\u5374\u53c8\u5e86\u5e78\u4e0d\u5df2\u3002\u5929\u7a7a\u4ecd\u662f\u4e4c\u4e91\u91cd\u91cd\u53e0\u53e0\uff0c\u5927\u96e8\u5982\u6ce8\uff0c\u5fc3\u4e2d\u5374\u611f\u5230\u4e86\u4e00\u7247\u5149\u660e\uff0c\u4f46\u89c9\u53ea\u987b\u7559\u5f97\u4e00\u6761\u547d\u5728\uff0c\u4fbf\u6709\u65e0\u5c3d\u6b22\u4e50\uff0c\u65e0\u9650\u98ce\u5149\u3002 \u3000\u3000\u4ed6\u5b9a\u4e86\u5b9a\u795e\uff0c\u5148\u5c06\u4e01\u5178\u7684\u5c38\u8eab\u7aef\u7aef\u6b63\u6b63\u7684\u653e\u5728\u6bbf\u89d2\uff0c\u7136\u540e\u51fa\u5916\u5c06\u5b9d\u8c61\u7684\u5c38\u8eab\u4ece\u6c60\u5858\u91cc\u62c9\u8d77\u6765\uff0c\u6316\u4e2a\u5751\u57cb\u4e86\u3002\u56de\u5230\u6bbf\u4e2d\uff0c\u53ea\u89c1\u5b9d\u8c61\u7684\u8863\u670d\u642d\u5728\u795e\u575b\u4e4b\u4e0a\uff0c\u575b\u4e0a\u653e\u7740\u4e00\u4e2a\u6cb9\u5e03\u5c0f\u5305\uff0c\u53e6\u6709\u5341\u6765\u4e24\u788e\u94f6\u5b50\u3002 \u3000\u3000\u4ed6\u597d\u5947\u5fc3\u8d77\uff0c\u62ff\u8fc7\u6cb9\u5e03\u5c0f\u5305\uff0c\u6253\u4e86\u5f00\u6765\uff0c\u89c1\u91cc\u9762\u53c8\u5305\u7740\u4e00\u5c42\u6cb9\u7eb8\uff0c\u518d\u6253\u5f00\u6cb9\u7eb8\uff0c\u89c1\u662f\u4e00\u672c\u9ec4\u7eb8\u5c0f\u4e66\uff0c\u5c01\u76ae\u4e0a\u5f2f\u5f2f\u66f2\u66f2\u7684\u5199\u7740\u51e0\u884c\u5b57\u4e0d\u50cf\u5b57\uff0c\u56fe\u4e0d\u50cf\u56fe\u7684\u82b1\u6837\uff0c\u4e5f\u4e0d\u77e5\u662f\u751a\u4e48\u3002\u7ffb\u5c06\u5f00\u6765\uff0c\u89c1\u7b2c\u4e00\u9875\u4e0a\u7ed8\u7740\u4e00\u4e2a\u7cbe\u7626\u5e72\u67af\u7684\u88f8\u4f53\u7537\u5b50\uff0c\u4e00\u624b\u6307\u5929\uff0c\u4e00\u624b\u6307\u5730\u3001\u9762\u76ee\u6781\u662f\u8be1\u5f02\uff0c\u65c1\u8fb9\u6ce8\u6ee1\u4e86\u4e94\u989c\u516d\u8272\u7684\u602a\u5b57\uff0c\u5f62\u82e5\u874c\u86aa\uff0c\u6216\u7ea2\u6216\u7eff\u3002\u72c4\u4e91\u77a7\u7740\u56fe\u4e2d\u7537\u5b50\uff0c\u89c1\u4ed6\u94a9\u9f3b\u6df1\u76ee\uff0c\u66f2\u53d1\u9ad8\u98a7\uff0c\u4e0d\u4f3c\u4e2d\u571f\u4eba\u7269\uff0c\u5f62\u8c8c\u751a\u662f\u53e4\u602a\uff0c\u800c\u602a\u5f02\u4e4b\u4e2d\uff0c\u66f4\u4f3c\u8574\u85cf\u7740\u4e00\u80a1\u5438\u5f15\u4e4b\u529b\uff0c\u4ee4\u4eba\u4e0d\u7531\u81ea\u4e3b\u7684\u5fc3\u65cc\u6447\u52a8\uff0c\u795e\u4e0d\u5b88\u820d\uff0c\u4ed6\u770b\u4e86\u4e00\u4f1a\uff0c\u4fbf\u4e0d\u6562\u518d\u770b\u3002 \u3000\u3000\u7ffb\u5230\u7b2c\u4e8c\u9875\uff0c\u89c1\u4e0a\u9762\u4ecd\u662f\u7ed8\u7740\u8fd9\u4e2a\u88f8\u4f53\u7537\u5b50\uff0c\u53ea\u662f\u59ff\u5f0f\u4e0d\u540c\uff0c\u5de6\u8db3\u91d1\u9e21\u72ec\u7acb\uff0c\u53f3\u8db3\u6a2a\u7740\u5e73\u4f38\u800c\u51fa\uff0c\u53cc\u624b\u53cd\u5728\u8eab\u540e\uff0c\u5de6\u624b\u63e1\u7740\u53f3\u8033\uff0c\u53f3\u624b\u63e1\u7740\u5de6\u8033\u3002\u4e00\u8def\u7ffb\u5c06\u4e0b\u53bb\uff0c\u4f46\u89c1\u8fd9\u88f8\u4f53\u4eba\u5f62\u7684\u59ff\u5f0f\u8d8a\u6765\u8d8a\u602a\uff0c\u82b1\u6837\u53d8\u5e7b\u65e0\u7a77\uff0c\u6709\u65f6\u53cc\u624b\u6491\u5730\uff0c\u6709\u65f6\u98de\u8dc3\u534a\u7a7a\uff0c\u66f4\u6709\u65f6\u4ee5\u5934\u9876\u5730\u5012\u7acb\uff0c\u4e0b\u534a\u8eab\u5374\u51ed\u7a7a\u751f\u51fa\u516d\u6761\u817f\u6765\u3002 \u3000\u3000\u5230\u4e86\u540e\u534a\u672c\u4e2d\uff0c\u90a3\u4eba\u624b\u4e2d\u5374\u6301\u4e86\u4e00\u67c4\u5f2f\u5200\u3002 \u3000\u3000\u4ed6\u56de\u5934\u7ffb\u5230\u7b2c\u4e00\u9875\uff0c\u518d\u5411\u56fe\u4e2d\u90a3\u4eba\u8138\u4e0a\u7ec6\u77a7\uff0c\u89c1\u4ed6\u820c\u5c16\u4ece\u5de6\u8fb9\u5634\u89d2\u4e2d\u5fae\u5fae\u4f38\u51fa\uff0c\u540c\u65f6\u53f3\u773c\u5f20\u5927\u800c\u5de6\u773c\u7565\u772f\uff0c\u8138\u4e0a\u795e\u60c5\u53e4\u602a\uff0c\u4fbf\u56e0\u6b64\u800c\u751f\u3002\u4ed6\u597d\u5947\u5fc3\u8d77\uff0c\u4fbf\u5b66\u7740\u8fd9\u4eba\u7684\u6a21\u6837\uff0c\u4e5f\u662f\u820c\u5c16\u5fae\u5410\uff0c\u53f3\u773c\u5f20\u800c\u5de6\u773c\u95ed\uff0c\u8fd9\u59ff\u5f0f\u4e00\u505a\uff0c\u53ea\u89c9\u5f97\u989c\u9762\u95f4\u5341\u5206\u8212\u7545\uff0c\u518d\u5411\u56fe\u5f62\u4e2d\u770b\u53bb\u65f6\uff0c\u9690\u9690\u89c1\u5230\u90a3\u7537\u5b50\u8eab\u4e0a\u6709\u51e0\u6761\u6781\u6de1\u7684\u7070\u8272\u7ec6\u7ebf\uff0c\u7ed8\u7740\u7ecf\u8109\u3002\u72c4\u4e91\u5fc3\u9053\uff1a\u201c\u662f\u4e86\uff0c\u539f\u6765\u8fd9\u4eba\u8eab\u4e0a\u4e0d\u7ed8\u8863\u886b\uff0c\u662f\u4e3a\u4e86\u8981\u663e\u51fa\u7ecf\u8109\u3002\u201d \u3000\u3000\u4e01\u5178\u5728\u72f1\u4e2d\u6388\u4ed6\u795e\u7167\u529f\u4e4b\u65f6\uff0c\u66fe\u5c06\u4eba\u8eab\u7684\u7ecf\u8109\u884c\u8d70\u65b9\u4f4d\uff0c\u89e3\u8bf4\u5f97\u6781\u662f\u8be6\u7ec6\u660e\u767d\uff0c\u7ec3\u8fd9\u9879\u6700\u4e0a\u4e58\u7684\u5185\u529f\uff0c\u57fa\u672c\u5173\u952e\u4fbf\u5728\u4e8e\u6b64\u3002\u4ed6\u65e9\u5df2\u8bb0\u5f97\u719f\u4e86\uff0c\u8fd9\u65f6\u77a7\u7740\u56fe\u4e2d\u4eba\u8eab\u4e0a\u7684\u7ecf\u8109\u7ebf\u8def\uff0c\u4e0d\u7531\u81ea\u4e3b\u4fbf\u8c03\u8fd0\u5185\u606f\uff0c\u4f53\u5185\u4e00\u80a1\u7ec6\u5fae\u7684\u771f\u6c14\u4fbf\u4f9d\u7740\u90a3\u7ecf\u8109\u8fd0\u884c\u8d77\u6765\u3002 \u3000\u3000\u5bfb\u601d\uff1a\u201c\u8fd9\u7ecf\u8109\u8fd0\u884c\u7684\u65b9\u4f4d\uff0c\u548c\u4e01\u5927\u54e5\u6240\u6388\u7684\u6070\u6070\u76f8\u53cd\uff0c\u90a3\u53ea\u6015\u4e0d\u5bf9\u3002\u201d\u4f46\u968f\u5373\u8f6c\u5ff5\uff1a\u201c\u6211\u4fbf\u8bd5\u4ed6\u4e00\u8bd5\uff0c\u53c8\u6709\u4f55\u59a8\uff1f\u201d\u5f53\u5373\u50ac\u52a8\u5185\u606f\uff0c\u5faa\u56fe\u800c\u884c\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u4fbf\u89c9\u5168\u8eab\u8f6f\u6d0b\u6d0b\u5730\uff0c\u8bf4\u4e0d\u51fa\u7684\u8f7b\u5feb\u8212\u7545\u3002\u4ed6\u7ec3\u795e\u7167\u529f\u65f6\uff0c\u5168\u795e\u8d2f\u6ce8\u7684\u51dd\u6c14\u800c\u884c\uff0c\u90a3\u5185\u606f\u4fbf\u8981\u4e0a\u884c\u4e00\u5bf8\u3001\u4e8c\u5bf8\uff0c\u4e5f\u662f\u4e07\u5206\u8270\u96be\uff0c\u4f46\u8fd9\u5bf9\u7167\u7740\u56fe\u4e2d\u7684\u65b9\u4f4d\u8fd0\u884c\uff0c\u970e\u65f6\u4e4b\u95f4\u4fbf\u5982\u6c5f\u6cb3\u5954\u6d41\uff0c\u7adf\u4e1d\u6beb\u4e0d\u7528\u529b\u6c14\uff0c\u5185\u606f\u81ea\u7136\u8fd0\u884c\uff0c\u4ed6\u5fc3\u4e2d\u53c8\u60ca\u53c8\u559c\uff1a\u201c\u600e\u4e48\u6211\u4f53\u5185\u7adf\u6709\u8fd9\u6837\u7684\u7ecf\u8109\uff1f \u3000\u3000\u83ab\u975e\u8fde\u4e01\u5927\u54e5\u4e5f\u4e0d\u77e5\u4e48\uff1f\u201d\u8ddf\u7740\u53c8\u60f3\uff1a\u201c\u8fd9\u672c\u518c\u5b50\u662f\u90a3\u6076\u548c\u5c1a\u7684\uff0c\u800c\u4e66\u4e0a\u6587\u5b57\u56fe\u5f62\u53c8\u90fd\u90aa\u91cc\u90aa\u6c14\uff0c\u5b9a\u4e0d\u662f\u751a\u4e48\u6b63\u7ecf\u4e1c\u897f\uff0c\u8fd8\u662f\u522b\u53bb\u6cbe\u60f9\u7684\u4e3a\u662f\u3002\u201d \u3000\u3000\u4f46\u8fd9\u65f6\u4ed6\u4f53\u5185\u7684\u5185\u606f\u8fd0\u884c\u6b63\u7545\uff0c\u7adf\u4e0d\u60f3\u5c31\u6b64\u4fbf\u505c\uff0c\u5fc3\u4e2d\u53ea\u60f3\uff1a\u201c\u597d\u7f62\uff0c\u53ea\u73a9\u8fd9\u4e48\u4e00\u6b21\uff0c\u4e0b\u6b21\u4e0d\u80fd\u518d\u73a9\u4e86\u3002\u201d\u6e10\u6e10\u89c9\u5f97\u5fc3\u65f7\u795e\u6021\uff0c\u5168\u8eab\u8840\u6db2\u90fd\u6696\u4e86\u8d77\u6765\uff0c\u53c8\u8fc7\u4e00\u4f1a\uff0c\u8eab\u5b50\u8f7b\u98d8\u98d8\u5730\uff0c\u597d\u4f3c\u9971\u996e\u4e86\u70c8\u9152\u4e00\u822c\uff0c\u7981\u4e0d\u4f4f\u624b\u821e\u8db3\u8e48\uff0c\u53e3\u4e2d\u53d1\u51fa\u545c\u545c\u545c\u7684\u4f4e\u58f0\u547c\u53eb\uff0c\u8111\u4e2d\u4e00\u660f\uff0c\u5012\u5728\u5730\u4e0b\uff0c\u4fbf\u751a\u4e48\u4e5f\u4e0d\u77e5\u9053\u4e86\u3002 \u3000\u3000\u8fc7\u4e86\u826f\u4e45\u826f\u4e45\uff0c\u8fd9\u624d\u77e5\u89c9\u6e10\u590d\uff0c\u7f13\u7f13\u7741\u5f00\u773c\u6765\uff0c\u53ea\u89c9\u65e5\u5149\u7167\u8000\uff0c\u539f\u6765\u5927\u96e8\u65e9\u505c\uff0c\u592a\u9633\u6652\u8fdb\u6bbf\u6765\u3002\u72c4\u4e91\u4e00\u8dc3\u800c\u8d77\uff0c\u53ea\u89c9\u7cbe\u795e\u52c3\u52c3\uff0c\u5168\u8eab\u5145\u6ee1\u4e86\u529b\u6c14\uff0c\u5fc3\u60f3\uff1a\u201c\u96be\u9053\u8fd9\u672c\u518c\u5b50\u4e0a\u7684\u529f\u592b\uff0c\u7adf\u6709\u8fd9\u822c\u597d\u5904\uff1f\u4e0d\uff0c\u4e0d\uff01\u6211\u8fd8\u662f\u7167\u4e01\u5927\u54e5\u6240\u6388\u7684\u529f\u592b\u7528\u5fc3\u4e60\u7ec3\u624d\u662f\uff0c\u8fd9\u79cd\u90aa\u9b54\u5916\u9053\uff0c\u4e00\u6cbe\u4e0a\u8eab\uff0c\u8bf4\u4e0d\u5b9a\u540e\u60a3\u65e0\u7a77\u3002\u201d \u3000\u3000\u62ff\u8d77\u518c\u5b50\uff0c\u8981\u60f3\u4f38\u624b\u6495\u788e\uff0c\u4f46\u60f3\u4e86\u4e00\u60f3\uff0c\u603b\u89c9\u5176\u4e2d\u5145\u6ee1\u79d8\u5965\uff0c\u4e0d\u820d\u5f97\u4fbf\u6b64\u6bc1\u53bb\u3002 \u3000\u3000\u4ed6\u6574\u7406\u4e00\u4e0b\u8863\u886b\uff0c\u4f46\u89c1\u7834\u70c2\u5df2\u6781\uff0c\u5b9e\u5728\u96be\u4ee5\u853d\u4f53\uff0c\u89c1\u5b9d\u8c61\u7684\u50e7\u8863\u548c\u88e4\u5b50\u642d\u5728\u795e\u575b\u4e4b\u4e0a\uff0c\u5012\u662f\u5b8c\u597d\uff0c\u4e8e\u662f\u53d6\u8fc7\u6765\u7a7f\u5728\u8eab\u4e0a\u3002\u867d\u7136\u7a7f\u4e86\u8fd9\u6076\u50e7\u7684\u50e7\u888d\uff0c\u5fc3\u4e2d\u751a\u89c9\u522b\u626d\uff0c\u4f46\u603b\u80dc\u4e8e\u88e4\u5b50\u4e0a\u70c2\u4e86\u5341\u4e03\u516b\u4e2a\u7834\u6d1e\uff0c\u8fde\u5c41\u80a1\u4e5f\u906e\u4e0d\u4f4f\u3002\u4ed6\u5c06\u90a3\u672c\u518c\u5b50\u548c\u5341\u591a\u4e24\u788e\u94f6\u90fd\u63e3\u5728\u6000\u91cc\uff0c\u5230\u5927\u6811\u4e0b\u7684\u6ce5\u5751\u4e2d\u5c06\u90a3\u5305\u9996\u9970\u548c\u94f6\u4e24\u6316\u4e86\u51fa\u6765\u6536\u8d77\uff0c\u62b1\u8d77\u4e01\u5178\u7684\u5c38\u8eab\uff0c\u8d70\u51fa\u5e99\u53bb\u3002 \u3000\u3000\u884c\u51fa\u767e\u4f59\u4e08\uff0c\u8fce\u9762\u6765\u4e86\u4e00\u4e2a\u519c\u592b\uff0c\u89c1\u5230\u4ed6\u624b\u4e2d\u6a2a\u62b1\u7740\u4e00\u4e2a\u6b7b\u5c38\uff0c\u4e0d\u7531\u5f97\u5927\u5403\u4e00\u60ca\uff0c\u4e00\u5931\u8db3\u4fbf\u6454\u5728\u7530\u4e2d\uff0c\u6ee1\u8eab\u6ce5\u6cde\u7684\u6323\u624e\u8d77\u6765\uff0c\u4e00\u8db3\u9ad8\u4e00\u8db3\u4f4e\u7684\u5feb\u6b65\u9003\u8d70\u3002 \u3000\u3000\u72c4\u4e91\u77e5\u9053\u5982\u6b64\u884c\u8d70\uff0c\u5fc5\u5b9a\u60f9\u4e8b\uff0c\u4f46\u4e00\u65f6\u5374\u4e5f\u60f3\u4e0d\u51fa\u751a\u4e48\u5584\u7b56\u3002\u5e78\u597d\u8fd9\u4e00\u5e26\u751a\u662f\u8352\u50fb\uff0c\u4e00\u8def\u8d70\u53bb\uff0c\u4e0d\u518d\u9047\u5230\u884c\u4eba\u3002\u4ed6\u6a2a\u62b1\u7740\u4e01\u5178\uff0c\u5fc3\u4e0b\u53ea\u60f3\uff1a\u201c\u4e01\u5927\u54e5\uff0c\u4e01\u5927\u54e5\uff0c\u6211\u820d\u4e0d\u5f97\u548c\u4f60\u5206\u624b\uff0c\u6211\u820d\u4e0d\u5f97\u548c\u4f60\u5206\u624b\u3002\u201d \u3000\u3000\u5ffd\u542c\u5f97\u5c71\u6b4c\u58f0\u8d77\uff0c\u8fdc\u8fdc\u6709\u4e03\u516b\u540d\u519c\u592b\u8377\u9504\u8d70\u6765\uff0c\u72c4\u4e91\u6025\u5fd9\u4e00\u4e2a\u7bad\u6b65\uff0c\u8eb2\u5165\u5c71\u65c1\u7684\u957f\u8349\u4e4b\u4e2d\uff0c\u5f85\u90a3\u4e9b\u519c\u592b\u8d70\u8fc7\uff0c\u5fc3\u60f3\uff1a \u3000\u3000\u201c\u82e5\u4e0d\u711a\u4e86\u4e01\u5927\u54e5\u7684\u9057\u4f53\uff0c\u7ec8\u7a76\u4e0d\u80fd\u5b8c\u6210\u4ed6\u4e0e\u51cc\u5c0f\u59d0\u5408\u846c\u7684\u5fc3\u613f\u3002\u201d\u5230\u5c71\u5773\u4e2d\u62fe\u4e9b\u67af\u679d\u67f4\u5353\uff0c\u4e00\u54ac\u7259\uff0c\u70b9\u71c3\u4e86\u706b\uff0c\u5728\u4e01\u5178\u5c38\u8eab\u65c1\u711a\u70e7\u8d77\u6765\u3002 \u3000\u3000\u706b\u820c\u541e\u6ca1\u4e86\u4e01\u5178\u5934\u53d1\u548c\u8863\u886b\uff0c\u72c4\u4e91\u53ea\u89c9\u5f97\u8fd9\u4e9b\u706b\u7130\u662f\u5728\u7ed5\u7740\u81ea\u5df1\u7684\u808c\u8089\uff0c\u6251\u5728\u5730\u4e0b\uff0c\u54ac\u7740\u9752\u8349\u6ce5\u571f\uff0c\u6cea\u6c34\u6d41\u5230\u4e86\u8349\u4e0a\u571f\u4e2d\uff0c\u53c8\u6d41\u5230\u4e86\u4ed6\u5634\u91cc\u2026\u2026 \u3000\u3000\u72c4\u4e91\u7ec6\u5fc3\u6361\u8d77\u4e01\u5178\u7684\u9aa8\u7070\uff0c\u90d1\u91cd\u5305\u5728\u6cb9\u7eb8\u4e4b\u4e2d\uff0c\u5916\u9762\u518d\u88f9\u4ee5\u6cb9\u5e03\u3002\u8fd9\u6cb9\u7eb8\u6cb9\u5e03\u672c\u662f\u5b9d\u8c61\u7528\u6765\u5305\u85cf\u90a3\u672c\u9ec4\u7eb8\u518c\u5b50\u7684\u3002\u5305\u88f9\u5916\u7528\u5e03\u6761\u597d\u597d\u7684\u7f1a\u7d27\u4e86\uff0c\u8fd9\u624d\u8d34\u8089\u7f1a\u5728\u8170\u95f4\u3002\u518d\u7528\u624b\u6316\u4e86\u4e00\u5751\uff0c\u5c06\u5269\u4e0b\u7684\u7070\u70ec\u62e8\u5165\u5751\u4e2d\uff0c\u7528\u571f\u63a9\u76d6\u4e86\uff0c\u62dc\u4e86\u51e0\u62dc\u3002 \u3000\u3000\u7ad9\u8d77\u8eab\u6765\uff0c\u5fc3\u4e0b\u832b\u7136\uff1a\u201c\u6211\u8981\u5230\u54ea\u91cc\u53bb\uff1f\u201d\u4e16\u4e0a\u7684\u4eb2\u4eba\uff0c\u4fbf\u53ea\u5e08\u7236\u4e00\u4eba\uff0c\u81ea\u7136\u800c\u7136\u7684\u60f3\u8d77\uff1a\u201c\u6211\u4e14\u56de\u6c85\u9675\u53bb\u5bfb\u5e08\u7236\u3002\u201d\u5e08\u7236\u523a\u4f24\u4e07\u9707\u5c71\u800c\u9003\u53bb\uff0c\u6599\u60f3\u4e0d\u4f1a\u56de\u5f52\u6c85\u9675\u8001\u5bb6\uff0c\u5fc5\u662f\u9690\u59d3\u57cb\u540d\uff0c\u8fdc\u8d70\u9ad8\u98de\u3002\u4f46\u8fd9\u65f6\u9664\u4e86\u56de\u6c85\u9675\u53bb\u77a7\u77a7\u4e4b\u5916\uff0c\u5b9e\u5728\u60f3\u4e0d\u51fa\u8fd8\u6709\u65c1\u7684\u751a\u4e48\u5730\u65b9\u53ef\u53bb\u3002 \u3000\u3000\u5f53\u4e0b\u8f6c\u4e0a\u4e86\u5927\u8def\uff0c\u5411\u4e61\u4eba\u4e00\u6253\u542c\uff0c\u539f\u6765\u8fd9\u5730\u65b9\u53eb\u505a\u7a0b\u5bb6\u96c6\uff0c\u662f\u6e56\u5317\u76d1\u5229\u53bf\u4e4b\u5317\uff0c\u8981\u5230\u6e56\u5357\uff0c\u987b\u5f97\u5148\u8fc7\u957f\u6c5f\u3002 \u3000\u3000\u72c4\u4e91\u5230\u4e86\u5e02\u96c6\uff0c\u53d6\u51fa\u788e\u94f6\u4e70\u4e9b\u9762\u98df\u5403\u4e86\uff0c\u6765\u5230\u6e21\u53e3\uff0c\u642d\u8239\u8fc7\u6c5f\uff0c\u56de\u60f3\u6628\u65e5\u8fc7\u6c5f\u65f6\u9003\u907f\u5b9d\u8c61\u7684\u8ffd\u8d76\uff0c\u4f55\u7b49\u60ca\u614c\uff0c\u4eca\u65e5\u5374\u60a0\u60a0\u95f2\u95f2\u7684\u91cd\u8fc7\u957f\u6c5f\uff0c\u76f8\u9694\u4e0d\u8fc7\u4e00\u65e5\uff0c\u60c5\u666f\u5374\u5168\u7136\u4e0d\u540c\u4e86\u3002 \u3000\u3000\u6e21\u8239\u9760\u4e86\u5357\u5cb8\uff0c\u72c4\u4e91\u4e0a\u5f97\u5cb8\u6765\uff0c\u53ea\u542c\u5f97\u55a7\u54d7\u53eb\u56b7\uff0c\u4eba\u5934\u6d8c\u6d8c\uff0c\u4e0d\u5c11\u4eba\u5435\u6210\u4e00\u56e2\uff0c\u8ddf\u7740\u7830\u7830\u58f0\u54cd\uff0c\u597d\u4e9b\u4eba\u6253\u4e86\u8d77\u6765\u3002\u72c4\u4e91\u597d\u5947\u5fc3\u8d77\uff0c\u4fbf\u8d70\u8fd1\u53bb\u77a7\u77a7\u70ed\u95f9\u3002 \u3000\u3000\u53ea\u89c1\u4eba\u4e1b\u4e4b\u4e2d\uff0c\u4e03\u516b\u6761\u5927\u6c49\u6b63\u56f4\u4f4f\u4e00\u4e2a\u8001\u8005\u6bb4\u6253\u3002\u90a3\u8001\u8005\u9752\u8863\u7f57\u5e3d\uff0c\u5bb6\u4eba\u88c5\u675f\u3002\u90a3\u4e03\u516b\u6761\u6c49\u5b50\u8d64\u8db3\u77ed\u8863\uff0c\u8eab\u8fb9\u653e\u7740\u77ed\u79e4\u9c7c\u7bd3\uff0c\u663e\u7136\u90fd\u662f\u9c7c\u8d29\u3002\u72c4\u4e91\u5fc3\u60f3\u8fd9\u662f\u5bfb\u5e38\u6253\u67b6\uff0c\u6ca1\u751a\u4e48\u597d\u77a7\u7684\uff0c\u6b63\u8981\u9000\u5f00\uff0c\u53ea\u89c1\u90a3\u8001\u5bb6\u4eba\u98de\u8db3\u5c06\u4e00\u540d\u58ee\u5065\u9c7c\u8d29\u8e22\u4e86\u4e2a\u7b4b\u6597\uff0c\u539f\u6765\u4ed6\u7adf\u8eab\u6709\u6b66\u529f\u3002 \u3000\u3000\u8fd9\u4e00\u6765\uff0c\u72c4\u4e91\u4fbf\u8981\u77a7\u4e2a\u7a76\u7adf\u4e86\u3002\u53ea\u89c1\u90a3\u8001\u5bb6\u4eba\u4ee5\u5be1\u654c\u4f17\uff0c\u7247\u523b\u95f4\u53c8\u6253\u5012\u4e86\u4e09\u540d\u9c7c\u8d29\u3002\u65c1\u8fb9\u77a7\u7740\u7684\u9c7c\u8d29\u867d\u4f17\uff0c\u4e00\u65f6\u7adf\u65e0\u4eba\u518d\u6562\u4e0a\u524d\u3002\u5ffd\u542c\u5f97\u4f17\u9c7c\u8d29\u6b22\u547c\u8d77\u6765\uff0c\u53eb\u9053\uff1a\u201c\u5934\u513f\u6765\u5566\uff0c\u5934\u513f\u6765\u5566\uff01\u201d\u53ea\u89c1\u6c5f\u8fb9\u4e24\u540d\u9c7c\u8d29\u98de\u5954\u800c\u6765\uff0c\u540e\u9762\u8ddf\u7740\u4e09\u4eba\u3002\u90a3\u4e09\u4eba\u6b65\u5c65\u9887\u4e3a\u6c89\u7a33\uff0c\u72c4\u4e91\u4e00\u773c\u77a7\u53bb\uff0c\u4fbf\u77e5\u662f\u8eab\u6709\u6b66\u529f\u4e4b\u4eba\u3002 \u3000\u3000\u90a3\u4e09\u4eba\u6765\u5230\u8fd1\u524d\uff0c\u4e3a\u9996\u4e00\u4eba\u662f\u4e2a\u56db\u5341\u6765\u5c81\u7684\u6c49\u5b50\uff0c\u8721\u9ec4\u7684\u8138\u76ae\uff0c\u7559\u7740\u4e00\u6487\u9f20\u987b\uff0c\u5411\u5012\u5728\u5730\u4e0b\u54fc\u54fc\u5527\u5527\u7684\u51e0\u540d\u9c7c\u8d29\u671b\u4e86\u4e00\u773c\uff0c\u8bf4\u9053\uff1a\u201c\u9601\u4e0b\u662f\u8c01\uff0c\u4ed7\u4e86\u8c01\u7684\u52bf\u5934\uff0c\u5230\u6211\u4eec\u534e\u5bb9\u53bf\u6765\u6b3a\u4eba\uff1f\u201d\u4ed6\u8fd9\u51e0\u53e5\u8bdd\u662f\u5411\u90a3\u8001\u5bb6\u4eba\u8bf4\u7684\uff0c\u53ef\u662f\u773c\u775b\u5411\u4ed6\u671b\u4e5f\u6ca1\u671b\u4e0a\u4e00\u773c\u3002\u539f\u6765\u8fc7\u6c5f\u4e4b\u540e\uff0c\u8fd9\u91cc\u5df2\u662f\u6e56\u5357\u534e\u5bb9\u53bf\u5730\u754c\u3002 \u3000\u3000\u90a3\u8001\u5bb6\u4eba\u9053\uff1a\u201c\u6211\u53ea\u662f\u62ff\u94f6\u5b50\u4e70\u9c7c\uff0c\u751a\u4e48\u6b3a\u4eba\u4e0d\u6b3a\u4eba\u7684\uff1f\u201d \u3000\u3000\u90a3\u5934\u513f\u5411\u8eab\u65c1\u7684\u9c7c\u8d29\u95ee\u9053\uff1a\u201c\u5e72\u4e48\u6253\u4e86\u8d77\u6765\uff1f\u201d\u90a3\u9c7c\u8d29\u9053\uff1a\u201c\u8fd9\u8001\u5bb6\u4f19\u786c\u8981\u4e70\u8fd9\u5bf9\u91d1\u8272\u9ca4\u9c7c\u3002\u6211\u4eec\u8bf4\u91d1\u8272\u9ca4\u9c7c\u96be\u5f97\uff0c\u662f\u5934\u513f\u81ea\u5df1\u7559\u4e0b\u6765\u5408\u836f\u7684\u3002\u8fd9\u8001\u5bb6\u4f19\u597d\u6a2a\uff0c\u5374\u8bf4\u975e\u4e70\u4e0d\u53ef\u3002\u6211\u4eec\u4e0d\u5356\uff0c\u4ed6\u7adf\u52a8\u624b\u4fbf\u62a2\u3002\u201d \u3000\u3000\u90a3\u5934\u513f\u8f6c\u8fc7\u8eab\u6765\uff0c\u5411\u90a3\u8001\u5bb6\u4eba\u6253\u91cf\u4e86\u51e0\u773c\uff0c\u8bf4\u9053\uff1a\u201c\u9601\u4e0b\u7684\u670b\u53cb\uff0c\u662f\u4e2d\u4e86\u84dd\u7802\u638c\u4e48\uff1f\u201d\u90a3\u8001\u5bb6\u4eba\u4e00\u542c\uff0c\u8138\u8272\u53d8\u4e86\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u9053\u751a\u4e48\u7ea2\u7802\u638c\u3001\u84dd\u7802\u638c\u3002\u6211\u5bb6\u4e3b\u4eba\u4e0d\u8fc7\u60f3\u5403\u9ca4\u9c7c\u4e0b\u9152\uff0c\u5429\u5490\u6211\u62ff\u4e86\u94f6\u5b50\u6765\u4e70\u9c7c\u3002\u666e\u5929\u4e0b\u53ef\u4ece\u6765\u6ca1\u6709\u751a\u4e48\u9c7c\u80fd\u5356\u3001\u751a\u4e48\u9c7c\u53c8\u4e0d\u80fd\u5356\u7684\u89c4\u77e9\uff1f\u201d \u3000\u3000\u9c7c\u8d29\u5934\u513f\u51b7\u7b11\u9053\uff1a\u201c\u771f\u4eba\u9762\u524d\u8bf4\u751a\u4e48\u5047\u8bdd\uff1f\u9601\u4e0b\u5c0a\u59d3\u5927\u540d\uff0c\u80fd\u89c1\u544a\u4e48\uff1f\u5018\u82e5\u662f\u597d\u670b\u53cb\uff0c\u522b\u8bf4\u8fd9\u4e24\u5c3e\u91d1\u8272\u5927\u9ca4\u9c7c\u53ef\u4ee5\u5949\u9001\uff0c\u5728\u4e0b\u8fd8\u53ef\u9001\u4e0a\u4e00\u7c92\u4e13\u6cbb\u84dd\u7802\u62f3\u7684\u2018\u7389\u808c\u4e38\u2019\u3002\u201d \u3000\u3000\u90a3\u8001\u5bb6\u4eba\u8138\u8272\u66f4\u662f\u60ca\u7591\u4e0d\u5b9a\uff0c\u9694\u4e86\u534a\u664c\uff0c\u624d\u9053\uff1a\u201c\u9601\u4e0b\u662f\u8c01\uff0c\u5982\u4f55\u77e5\u9053\u84dd\u7802\u62f3\uff0c\u5982\u4f55\u53c8\u6709\u7389\u808c\u4e38\uff1f\u96be\u9053\uff0c\u96be\u9053\u2026\u2026\u201d\u9c7c\u8d29\u5934\u513f\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5728\u4e0b\u548c\u90a3\u4f7f\u84dd\u7802\u638c\u7684\u4e3b\u513f\uff0c\u786e\u662f\u6709\u4e09\u5206\u6e0a\u6e90\u3002\u201d \u3000\u3000\u90a3\u8001\u5bb6\u4eba\u66f4\u4e0d\u6253\u8bdd\uff0c\u8eab\u5f62\u4e00\u8d77\uff0c\u4f38\u624b\u4fbf\u5411\u4e00\u53ea\u9c7c\u7bd3\u6293\u53bb\uff0c\u884c\u52a8\u6781\u662f\u8fc5\u6377\u3002\u9c7c\u8d29\u5934\u513f\u51b7\u7b11\u9053\uff1a\u201c\u6709\u8fd9\u4e48\u5bb9\u6613\uff1f\u201d\u547c\u7684\u4e00\u638c\uff0c\u4fbf\u5f80\u4ed6\u80cc\u5fc3\u4e0a\u51fb\u4e86\u8fc7\u53bb\u3002\u8001\u5bb6\u4eba\u56de\u638c\u4e00\u62b5\uff0c\u501f\u52bf\u501f\u529b\uff0c\u8eab\u5b50\u5df2\u98d8\u5728\u6570\u4e08\u4e4b\u5916\uff0c\u63d0\u7740\u9c7c\u7bd3\uff0c\u6025\u6b65\u75be\u5954\u3002\u90a3\u9c7c\u8d29\u5934\u513f\u6ca1\u6599\u5230\u4ed6\u6709\u8fd9\u4e00\u624b\uff0c\u773c\u89c1\u8ffd\u8d76\u4e0d\u4e0a\uff0c\u624b\u4e00\u626c\uff0c\u4e00\u4ef6\u6697\u5668\u5e26\u7740\u7834\u7a7a\u4e4b\u58f0\uff0c\u5411\u4ed6\u80cc\u5fc3\u6025\u5c04\u800c\u53bb\u3002 \u3000\u3000\u90a3\u8001\u5bb6\u4eba\u593a\u5230\u9ca4\u9c7c\uff0c\u6ee1\u5fc3\u6b22\u559c\uff0c\u4e00\u80a1\u52b2\u513f\u7684\u53d1\u8db3\u6025\u5954\uff0c\u6ca1\u60f3\u5230\u6709\u6697\u5668\u5c04\u6765\u3002\u9c7c\u8d29\u5934\u5b50\u53d1\u5c04\u7684\u662f\u4e00\u679a\u74e6\u695e\u94a2\u9556\uff0c\u4ed6\u624b\u52b2\u5927\uff0c\u53bb\u52bf\u9887\u6025\u3002\u72c4\u4e91\u773c\u89c1\u90a3\u8001\u5bb6\u4eba\u4e0d\u77e5\u95ea\u907f\uff0c\u5fc3\u4e2d\u4e0d\u5fcd\uff0c\u987a\u624b\u63d0\u8d77\u5730\u4e0b\u4e00\u53ea\u9c7c\u7bd3\uff0c\u4ece\u4fa7\u9762\u659c\u5411\u94a2\u9556\u63b7\u53bb\u3002 \u3000\u3000\u4ed6\u6b66\u529f\u5df2\u5931\uff0c\u624b\u4e0a\u539f\u6ca1\u591a\u5c11\u529b\u9053\uff0c\u53ea\u662f\u6240\u7ad9\u5730\u4f4d\u6070\u5230\u597d\u5904\uff0c\u53ea\u542c\u5f97\u535c\u7684\u4e00\u58f0\u54cd\uff0c\u94a2\u9556\u63d2\u5165\u4e86\u9c7c\u7bd3\u3002\u90a3\u9c7c\u7bd3\u5411\u524d\u53c8\u98de\u4e86\u6570\u5c3a\uff0c\u8fd9\u624d\u843d\u5730\u3002 \u3000\u3000\u90a3\u8001\u5bb6\u4eba\u542c\u5f97\u80cc\u540e\u58f0\u54cd\uff0c\u56de\u5934\u4e00\u77a7\uff0c\u53ea\u89c1\u90a3\u9c7c\u8d29\u5934\u5b50\u624b\u6307\u72c4\u4e91\uff0c\u9a82\u9053\uff1a\u201c\u5140\u90a3\u5c0f\u8d3c\u79c3\uff0c\u4f60\u662f\u54ea\u5ea7\u5e99\u91cc\u7684\u91ce\u548c\u5c1a\uff0c\u5374\u6765\u7406\u4f1a\u957f\u6c5f\u94c1\u7f51\u5e2e\u7684\u95f2\u4e8b\uff1f\u201d \u3000\u3000\u72c4\u4e91\u4e00\u6014\uff1a\u201c\u600e\u5730\u4ed6\u9a82\u6211\u662f\u5c0f\u8d3c\u79c3\u4e86\uff1f\u201d\u89c1\u90a3\u9c7c\u8d29\u5934\u5b50\u58f0\u52bf\u6c79\u6c79\uff0c\u53c8\u8bf4\u5230\u751a\u4e48\u201c\u957f\u6c5f\u94c1\u7f51\u5e2e\u201d\uff0c\u8bb0\u5f97\u4e01\u5927\u54e5\u5e38\u81ea\u8a00\u9053\uff0c\u6c5f\u6e56\u571f\u5404\u79cd\u5e2e\u4f1a\u7981\u5fcc\u6700\u591a\uff0c\u82e5\u662f\u4e0d\u5c0f\u5fc3\u60f9\u4e0a\u4e86\uff0c\u5f80\u5f80\u53d7\u7d2f\u65e0\u7a77\u3002\u4ed6\u4e0d\u613f\u65e0\u7f18\u65e0\u6545\u7684\u591a\u751f\u4e8b\u7aef\uff0c\u4fbf\u62f1\u624b\u9053\uff1a\u201c\u662f\u5c0f\u5f1f\u7684\u4e0d\u662f\uff0c\u8bf7\u8001\u5144\u539f\u8c05\u3002\u201d \u3000\u3000\u90a3\u9c7c\u8d29\u5934\u5b50\u6012\u9053\uff1a\u201c\u4f60\u662f\u751a\u4e48\u4e1c\u897f\uff0c\u8c01\u6765\u8ddf\u4f60\u79f0\u5144\u9053\u5f1f\uff1f\u201d \u3000\u3000\u8ddf\u7740\u5de6\u624b\u4e00\u6325\uff0c\u5411\u624b\u4e0b\u7684\u9c7c\u8d29\u9053\uff1a\u201c\u5c06\u8fd9\u4e24\u4eba\u90fd\u7ed9\u62ff\u4e0b\u4e86\u3002\u201d \u3000\u3000\u4fbf\u5728\u6b64\u65f6\uff0c\u53ea\u542c\u5f97\u53ee\u5f53\u53ee\u5f53\uff0c\u53ee\u73b2\u73b2\uff0c\u53ee\u5f53\u53ee\u5f53\uff0c\u53ee\u73b2\u73b2\u4e00\u9635\u94c3\u58f0\uff0c\u4e24\u9a91\u9a6c\u81ea\u897f\u81f3\u4e1c\uff0c\u6cbf\u7740\u6c5f\u8fb9\u9a70\u6765\u3002\u90a3\u8001\u5bb6\u4eba\u9762\u6709\u559c\u8272\uff0c\u9053\uff1a\u201c\u6211\u5bb6\u4e3b\u4eba\u4eb2\u81ea\u6765\u5566\uff0c\u4f60\u8ddf\u4ed6\u4eec\u8bf4\u53bb\u3002\u201d \u3000\u3000\u9c7c\u8d29\u5934\u5b50\u8138\u8272\u4e00\u53d8\uff0c\u9053\uff1a\u201c\u662f\u2018\u94c3\u5251\u53cc\u4fa0\u2019\uff1f\u201d\u4f46\u968f\u5373\u8138\u8272\u8f6c\u4e3a\u9ad8\u50b2\uff0c\u9053\uff1a\u201c\u662f\u2018\u94c3\u5251\u53cc\u4fa0\u2019\u4fbf\u53c8\u600e\u5730\uff1f\u8fd8\u8f6e\u4e0d\u5230\u4ed6\u4eec\u5230\u957f\u6c5f\u8fb9\u4e0a\u6765\u8000\u6b66\u626c\u5a01\u3002\u201d \u3000\u3000\u8bf4\u8bdd\u672a\u4e86\uff0c\u4e24\u4e58\u9a6c\u5df2\u9a70\u5230\u8eab\u524d\u3002\u72c4\u4e91\u53ea\u89c9\u773c\u524d\u4e00\u4eae\uff0c\u4f46\u89c1\u4e24\u5339\u9a6c\u4e00\u9ec4\u4e00\u767d\uff0c\u90fd\u662f\u795e\u9a8f\u9ad8\u5927\uff0c\u978d\u8f94\u9c9c\u660e\u3002\u9ec4\u9a6c\u4e0a\u5750\u7740\u4e00\u4e2a\u4e8c\u5341\u4e94\u516d\u5c81\u7684\u9752\u5e74\u7537\u5b50\uff0c\u4e00\u8eab\u9ec4\u886b\uff0c\u8eab\u5f62\u9ad8\u7626\u3002\u767d\u9a6c\u4e0a\u4e58\u7684\u662f\u4e2a\u5c11\u5973\uff0c\u4e8c\u5341\u5c81\u4e0a\u4e0b\u5e74\u7eaa\uff0c\u767d\u886b\u98d8\u98d8\uff0c\u5de6\u80a9\u4e0a\u60ac\u7740\u4e00\u6735\u7ea2\u7ef8\u5236\u7684\u5927\u82b1\uff0c\u8138\u8272\u5fae\u9ed1\uff0c\u76f8\u8c8c\u5374\u6781\u4e3a\u4fcf\u4e3d\u3002\u4e24\u4eba\u8170\u5782\u957f\u5251\uff0c\u624b\u4e2d\u90fd\u63e1\u7740\u4e00\u6761\u9a6c\u97ad\uff0c\u4e24\u5339\u9a6c\u4e00\u822c\u7684\u9ad8\u5934\u957f\u8eab\uff0c\u96be\u5f97\u7684\u662f\u9ec4\u8005\u5168\u662f\u9ec4\uff0c\u767d\u8005\u5168\u662f\u767d\uff0c\u8eab\u4e0a\u7adf\u65e0\u4e00\u6839\u6742\u6bdb\u3002\u9ec4\u9a6c\u9888\u4e0b\u6302\u4e86\u4e00\u4e32\u9ec4\u91d1\u9e3e\u94c3\uff0c\u767d\u9a6c\u7684\u9e3e\u94c3\u5219\u662f\u767d\u94f6\u6240\u94f8\uff0c\u9a6c\u5934\u5fae\u4e00\u6446\u52a8\uff0c\u91d1\u94c3\u4fbf\u53d1\u51fa\u53ee\u5f53\u53ee\u5f53\u4e4b\u58f0\uff0c\u94f6\u94c3\u7684\u58f0\u97f3\u53c8\u662f\u4e0d\u540c\uff0c\u53ee\u73b2\u73b2\u3001\u53ee\u73b2\u73b2\u7684\uff0c\u66f4\u4e3a\u6e05\u8106\u52a8\u542c\u3002\u7aef\u7684\u662f\u4eba\u4fca\u9a6c\u58ee\uff0c\u72c4\u4e91\u4e00\u751f\u4e4b\u4e2d\uff0c\u4ece\u672a\u89c1\u8fc7\u8fd9\u822c\u9f50\u6574\u6807\u81f4\u7684\u4eba\u7269\uff0c\u4e0d\u7531\u5f97\u5fc3\u4e2d\u6697\u6697\u559d\u4e00\u58f0\u91c7\uff1a\u201c\u597d\u6f02\u4eae\uff01\u201d \u3000\u3000\u90a3\u9752\u5e74\u7537\u5b50\u5411\u7740\u90a3\u8001\u8005\u9053\uff1a\u201c\u6c34\u798f\uff0c\u9ca4\u9c7c\u627e\u5230\u4e86\u6ca1\u6709\uff1f\u5728\u8fd9\u91cc\u5e72\u751a\u4e48\uff1f\u201d\u90a3\u8001\u5bb6\u4eba\u9053\uff1a\u201c\u6c6a\u5c11\u7237\uff0c\u91d1\u8272\u9ca4\u9c7c\u627e\u5230\u4e86\u4e00\u5bf9\uff0c\u53ef\u662f\u2026\u2026\u53ef\u662f\u4ed6\u4eec\u504f\u504f\u4e0d\u80af\u5356\uff0c\u8fd8\u52a8\u624b\u6253\u4eba\u3002\u201d \u3000\u3000\u90a3\u9752\u5e74\u4e00\u77a5\u773c\u89c1\u5230\u5730\u4e0b\u9c7c\u7bd3\u4e0a\u7684\u90a3\u679a\u94a2\u9556\uff0c\u8bf4\u9053\uff1a\u201c\u563f\uff0c\u8c01\u4f7f\u8fd9\u822c\u6b79\u6bd2\u7684\u6697\u5668\uff1f\u201d\u9a6c\u97ad\u4e00\u4f38\uff0c\u97ad\u4e1d\u5df2\u5377\u4f4f\u94a2\u9556\u5c3e\u4e0a\u7684\u84dd\u7ef8\uff0c\u63d0\u4e86\u56de\u6765\uff0c\u5411\u90a3\u5c11\u5973\u9053\uff1a\u201c\u7b19\u59b9\uff0c\u4f60\u77a7\uff0c\u662f\u89c1\u8840\u5c01\u5589\u7684\u2018\u874e\u5c3e\u9556\u2019\uff01\u201d \u3000\u3000\u90a3\u5c11\u5973\u9053\uff1a\u201c\u662f\u8c01\u7528\u8fd9\u9556\u4e86\uff1f\u201d\u8bdd\u58f0\u751a\u662f\u6e05\u4eae\u3002 \u3000\u3000\u90a3\u9c7c\u8d29\u5934\u5b50\u5fae\u5fae\u51b7\u7b11\uff0c\u53f3\u624b\u7d27\u63e1\u8170\u95f4\u5355\u5200\u5200\u67c4\uff0c\u8bf4\u9053\u201c\u94c3\u5251\u53cc\u4fa0\u8fd9\u51e0\u5e74\u95ef\u51fa\u4e86\u597d\u5927\u7684\u540d\u5934\uff0c\u957f\u6c5f\u94c1\u7f51\u5e2e\u4e0d\u662f\u4e0d\u77e5\u3002 \u3000\u3000\u53ef\u662f\u4f60\u4eec\u60f3\u6b3a\u5230\u6211\u4eec\u5934\u4e0a\uff0c\u53ea\u6015\u4e5f\u6ca1\u8fd9\u4e48\u5bb9\u6613\u3002\u201d\u4ed6\u8bed\u6c14\u786c\u4e2d\u5e26\u8f6f\uff0c\u663e\u7136\u4e0d\u539f\u4e0e\u94c3\u5251\u53cc\u4fa0\u53d1\u751f\u4e89\u7aef\u3002 \u3000\u3000\u90a3\u5c11\u5973\u9053\uff1a\u201c\u8fd9\u949f\u874e\u5c3e\u9556\u8680\u5fc3\u8150\u9aa8\uff0c\u592a\u8fc7\u72e0\u6bd2\uff0c\u6211\u7239\u7239\u65e9\u8bf4\u8fc7\u8c01\u4e5f\u4e0d\u8bb8\u518d\u7528\uff0c\u96be\u9053\u4f60\u4e0d\u77e5\u9053\u4e48\uff1f\u5e78\u597d\u4f60\u4e0d\u662f\u7528\u6765\u6253\u4eba\uff0c\u6253\u9c7c\u7bd3\u5b50\u7ec3\u529f\u592b\uff0c\u8fd8\u4e0d\u600e\u6837\u3002\u201d \u3000\u3000\u6c34\u798f\u9053\uff1a\u201c\u5c0f\u59d0\uff0c\u4e0d\u662f\u7684\u3002\u8fd9\u4eba\u53d1\u8fd9\u6bd2\u9556\u5c04\u6211\u3002\u591a\u8499\u8fd9\u4f4d\u5c0f\u5e08\u7236\u659c\u523a\u91cc\u63b7\u4e86\u8fd9\u53ea\u9c7c\u7bd3\u8fc7\u6765\uff0c\u624d\u6321\u4f4f\u4e86\u6bd2\u9556\u3002\u8981\u4e0d\u7136\u5c0f\u7684\u65e9\u5df2\u6ca1\u547d\u4e86\u3002\u201d\u4ed6\u4e00\u8fb9\u9762\u8bf4\uff0c\u4e00\u9762\u6307\u7740\u72c4\u4e91\u3002 \u3000\u3000\u72c4\u4e91\u6697\u6697\u7eb3\u95f7\uff1a\u201c\u600e\u5730\u4e00\u4e2a\u53eb\u6211\u5c0f\u5e08\u7236\uff0c\u4e00\u4e2a\u9a82\u6211\u5c0f\u8d3c\u79c3\uff0c\u6211\u51e0\u65f6\u505a\u8d77\u548c\u5c1a\u6765\u5566\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5973\u5411\u72c4\u4e91\u70b9\u4e86\u70b9\u5934\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u793a\u610f\u76f8\u8c22\u3002\u72c4\u4e91\u89c1\u5979\u4e00\u7b11\u4e4b\u4e0b\uff0c\u5bb9\u5982\u82b1\u7efd\uff0c\u66f4\u662f\u5a07\u8273\u52a8\u4eba\uff0c\u4e0d\u7531\u5f97\u8138\u4e0a\u4e00\u70ed\uff0c\u5f88\u611f\u7f9e\u6da9\u3002 \u3000\u3000\u90a3\u9752\u5e74\u542c\u4e86\u6c34\u798f\u4e4b\u8a00\uff0c\u8138\u4e0a\u767b\u65f6\u5982\u7f69\u4e86\u4e00\u5c42\u4e25\u971c\uff0c\u5411\u90a3\u9c7c\u8d29\u5934\u5b50\u9053\uff1a\u201c\u6b64\u8bdd\u5f53\u771f\uff1f\u4e0d\u7b49\u5f85\u5bf9\u65b9\u56de\u7b54\uff0c\u9a6c\u97ad\u4e00\u632f\uff0c\u97ad\u4e0a\u5377\u7740\u7684\u94a2\u9556\u75be\u98de\u800c\u51fa\uff0c\u98ce\u58f0\u547c\u547c\uff0c\u62cd\u7684\u4e00\u54cd\uff0c\u9489\u5728\u5341\u6570\u4e08\u5916\u7684\u4e00\u682a\u67f3\u6811\u4e4b\u4e0a\uff0c\u624b\u52b2\u4e4b\u5f3a\uff0c\u5b9e\u8db3\u60ca\u4eba\u3002 \u3000\u3000\u90a3\u9c7c\u8d29\u5934\u5b50\u5140\u81ea\u53e3\u786c\uff0c\u8bf4\u9053\uff1a\u201c\u901e\u751a\u4e48\u5a01\u98ce\u4e86\uff1f\u201d\u90a3\u9752\u5e74\u516c\u5b50\u559d\u9053\uff1a\u201c\u4fbf\u662f\u8981\u901e\u8fd9\u5a01\u98ce\uff01\u201d\u63d0\u8d77\u9a6c\u97ad\uff0c\u5411\u4ed6\u5288\u5934\u6253\u843d\uff0c\u90a3\u9c7c\u8d29\u5934\u5b50\u4e3e\u5200\u4fbf\u683c\u3002\u4e0d\u6599\u90a3\u516c\u5b50\u7684\u9a6c\u97ad\u5ffd\u7136\u659c\u51fa\u5411\u4e0b\uff0c\u7740\u5730\u800c\u5377\uff0c\u62db\u6570\u53d8\u5e7b\uff0c\u76f4\u653b\u5bf9\u65b9\u4e0b\u76d8\u3002\u9c7c\u8d29\u5934\u5b50\u6025\u5fd9\u8dc3\u8d77\u76f8\u907f\u3002\u8fd9\u9a6c\u97ad\u7adf\u4f3c\u662f\u6d3b\u7684\u4e00\u822c\uff0c\u500f\u7684\u53cd\u5f39\u4e0a\u6765\uff0c\u5df2\u7f20\u4f4f\u4e86\u4ed6\u53f3\u8db3\u3002\u90a3\u516c\u5b50\u8db3\u5c16\u5728\u9a6c\u8179\u4e0a\u8f7b\u8f7b\u4e00\u70b9\uff0c\u80ef\u4e0b\u9ec4\u9a6c\u7acb\u65f6\u5411\u524d\u4e00\u51b2\u3002\u90a3\u9c7c\u8d29\u5934\u5b50\u7684\u4e0b\u76d8\u529f\u592b\u672c\u6765\u751a\u662f\u4e86\u5f97\uff0c\u8fd9\u9752\u5e74\u516c\u5b50\u5c31\u7b97\u7528\u97ad\u5b50\u7f20\u4f4f\u4e86\u4ed6\uff0c\u4e5f\u672a\u5fc5\u62d6\u4ed6\u5f97\u5012\u3002\u4f46\u8fd9\u516c\u5b50\u5148\u5f15\u5f97\u4ed6\u8dc3\u5728\u534a\u7a7a\uff0c\u4f7f\u4ed6\u6839\u57fa\u5168\u5931\uff0c\u8fd9\u624d\u6325\u97ad\u7f20\u8db3\u3002\u90a3\u9ec4\u9a6c\u8fd9\u4e00\u51b2\u6709\u5343\u65a4\u4e4b\u529b\uff0c\u9c7c\u8d29\u5934\u5b50\u529b\u6c14\u518d\u5927\uff0c\u4e5f\u662f\u7ecf\u53d7\u4e0d\u8d77\uff0c\u53ea\u89c1\u4ed6\u8eab\u8eaf\u88ab\u9ec4\u9a6c\u62c9\u7740\uff0c\u51cc\u7a7a\u800c\u98de\u3002\u4f17\u9c7c\u8d29\u5927\u58f0\u5450\u558a\uff0c\u4e03\u516b\u4e2a\u4eba\u968f\u540e\u8ffd\u53bb\uff0c\u610f\u56fe\u6551\u63f4\u3002 \u3000\u3000\u90a3\u9ec4\u9a6c\u7eb5\u51fa\u6570\u4e08\uff0c\u5c06\u90a3\u9a6c\u97ad\u7ef7\u5f97\u6709\u5982\u5f13\u5f26\uff0c\u9752\u5e74\u516c\u5b50\u84c4\u52bf\u501f\u529b\uff0c\u632f\u81c2\u4e00\u7529\uff0c\u90a3\u9c7c\u8d29\u5934\u5b50\u4fbf\u5982\u817e\u4e91\u9a7e\u96fe\u822c\u98de\u4e86\u51fa\u53bb\u3002\u4ed6\u7a7a\u6709\u4e00\u8eab\u6b66\u529f\uff0c\u5374\u662f\u534a\u70b9\u4f7f\u4e0d\u51fa\u6765\uff0c\u8eab\u5b50\u4e0d\u7531\u81ea\u7531\u7684\u5411\u6c5f\u4e2d\u5c04\u53bb\u3002\u5cb8\u4e0a\u4f17\u4eba\u5927\u60ca\u4e4b\u4e0b\u9f50\u58f0\u547c\u558a\u3002\u53ea\u542c\u5f97\u6251\u901a\u4e00\u58f0\uff0c\u6c34\u82b1\u6e85\u8d77\u8001\u9ad8\uff0c\u9c7c\u8d29\u5934\u5b50\u6454\u5165\u4e86\u6c5f\u4e2d\uff0c\u970e\u65f6\u95f4\u6c89\u5165\u6c34\u5e95\uff0c\u65e0\u5f71\u65e0\u8e2a\u3002 \u3000\u3000\u90a3\u5c11\u5973\u62cd\u624b\u5927\u7b11\uff0c\u6325\u97ad\u51b2\u5165\u9c7c\u8d29\u7fa4\u4e2d\uff0c\u4e1c\u62bd\u4e00\u8bb0\uff0c\u897f\u51fb\u4e00\u62db\uff0c\u5c06\u4f17\u9c7c\u8d29\u6253\u5f97\u8dcc\u8dcc\u649e\u649e\u7684\u56db\u6563\u5954\u9003\u3002\u9c7c\u7bd3\u9c7c\u7f51\u6492\u4e86\u4e00\u5730\uff0c\u9c9c\u9c7c\u6d3b\u867e\u5728\u5730\u4e0a\u4e71\u722c\u4e71\u8df3\u3002 \u3000\u3000\u90a3\u9c7c\u8d29\u5934\u5b50\u4e00\u751f\u5728\u6c5f\u8fb9\u8ba8\u751f\u6d3b\uff0c\u6c34\u6027\u81ea\u662f\u7cbe\u719f\uff0c\u4ece\u6c5f\u9762\u4e0a\u63a2\u5934\u51fa\u6765\uff0c\u5df2\u5728\u4e0b\u6e38\u6570\u5341\u4e08\u4e4b\u5916\uff0c\u6c61\u8a00\u79fd\u8bed\u7684\u4e71\u9a82\uff0c\u5374\u4e5f\u4e0d\u6562\u4e0a\u5cb8\u518d\u6765\u53ae\u6253\u3002 \u3000\u3000\u6c34\u798f\u63d0\u8d77\u76db\u7740\u91d1\u9ca4\u7684\u9c7c\u7bd3\uff0c\u6253\u5f00\u76d6\u5b50\uff0c\u6b22\u6b22\u559c\u559c\u7684\u9053\uff1a\u201c\u516c\u5b50\u8bf7\u770b\uff0c\u7ea2\u5634\u91d1\u9cde\uff0c\u96be\u5f97\u53c8\u8fd9\u822c\u80a5\u5927\u3002\u201d\u90a3\u9752\u5e74\u9053\uff1a\u201c\u4f60\u6025\u901f\u9001\u56de\u5ba2\u5e97\uff0c\u8bf7\u82b1\u5927\u7237\u5e94\u7528\u6551\u4eba\u3002\u201d\u6c34\u798f\u9053\uff1a\u201c\u662f\u3002\u201d\u8d70\u5230\u72c4\u4e91\u8eab\u524d\uff0c\u8eac\u4e86\u8eac\u8eab\uff0c\u9053\uff1a\u201c\u591a\u8c22\u5c0f\u5e08\u7236\u6551\u547d\u4e4b\u6069\u3002\u4e0d\u77e5\u5c0f\u5e08\u7236\u7684\u6cd5\u540d\u600e\u751f\u79f0\u547c\uff1f\u201d\u72c4\u4e91\u542c\u4ed6\u5de6\u4e00\u53e5\u5c0f\u5e08\u7236\uff0c\u53f3\u4e00\u53e5\u5c0f\u5e08\u7236\uff0c\u53eb\u5f97\u81ea\u5df1\u5fc3\u4e2d\u53d1\u6bdb\uff0c\u4e00\u65f6\u7b54\u4e0d\u4e0a\u8bdd\u6765\u3002\u90a3\u9752\u5e74\u9053\uff1a\u201c\u5feb\u8d70\uff0c\u5feb\u8d70\u3002 \u3000\u3000\u5343\u4e07\u4e0d\u80fd\u803d\u6401\u4e86\u3002\u201d\u6c34\u798f\u9053\uff1a\u201c\u662f\u3002\u201d\u4e0d\u53ca\u7b49\u72c4\u4e91\u7b54\u8bdd\uff0c\u5feb\u6b65\u53bb\u4e86\u3002 \u3000\u3000\u72c4\u4e91\u89c1\u8fd9\u4e24\u4f4d\u9752\u5e74\u7537\u5973\u4eba\u54c1\u4fca\u96c5\uff0c\u6b66\u827a\u9ad8\u5f3a\uff0c\u5fc3\u4e2d\u6697\u81ea\u7fa1\u6155\uff0c\u9887\u6709\u7ed3\u7eb3\u4e4b\u610f\uff0c\u53ea\u662f\u5bf9\u65b9\u5e76\u4e0d\u4e0b\u9a6c\uff0c\u60f3\u8981\u8bf7\u6559\u59d3\u540d\uff0c\u9887\u89c9\u4e0d\u4fbf\u3002\u6b63\u72b9\u8c6b\u95f4\uff0c\u90a3\u516c\u5b50\u4ece\u6000\u4e2d\u638f\u51fa\u4e00\u952d\u9ec4\u91d1\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u5e08\u7236\uff0c\u591a\u8c22\u4f60\u6551\u4e86\u6211\u4eec\u8001\u5bb6\u4eba\u4e00\u547d\u3002\u8fd9\u952d\u9ec4\u91d1\uff0c\u8bf7\u5e08\u7236\u4e70\u83e9\u8428\u5ea7\u524d\u7684\u9999\u6cb9\u7f62\u3002\u201d\u8f7b\u8f7b\u4e00\u629b\uff0c\u5c06\u91d1\u5b50\u5411\u72c4\u4e91\u6295\u4e86\u8fc7\u6765\u3002\u72c4\u4e91\u5de6\u624b\u4e00\u6284\uff0c\u4fbf\u5df2\u63a5\u4f4f\uff0c\u5411\u4ed6\u56de\u63b7\u8fc7\u53bb\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u7528\u4e86\uff0c\u8bf7\u95ee\u4e24\u4f4d\u5c0a\u59d3\u5927\u540d\u3002\u201d \u3000\u3000\u90a3\u9752\u5e74\u89c1\u4ed6\u63a5\u91d1\u63b7\u91d1\u7684\u624b\u6cd5\uff0c\u663e\u662f\u8eab\u6709\u6b66\u529f\uff0c\u4e0d\u7b49\u91d1\u5b50\u98de\u5230\u8eab\u524d\uff0c\u9a6c\u97ad\u6325\u51fa\uff0c\u5df2\u5c06\u8fd9\u952d\u9ec4\u91d1\u5377\u4f4f\uff0c\u8bf4\u9053\uff1a\u201c\u5e08\u7236\u65e2\u7136\u4e5f\u662f\u6b66\u6797\u4e2d\u4eba\uff0c\u60f3\u5fc5\u5f97\u77e5\u94c3\u5251\u53cc\u4fa0\u7684\u5c0f\u540d\u3002\u201d \u3000\u3000\u72c4\u4e91\u89c1\u4ed6\u6296\u52a8\u9a6c\u97ad\uff0c\u5c06\u90a3\u952d\u9ec4\u91d1\u821e\u5f04\u5f97\u5ffd\u4e0a\u5ffd\u4e0b\uff0c\u795e\u60c5\u4e3e\u6b62\uff0c\u9887\u6709\u8f7b\u6d6e\u4e4b\u610f\uff0c\u4fbf\u9053\uff1a\u201c\u9002\u624d\u6211\u542c\u90a3\u9c7c\u8d29\u5934\u5b50\u79f0\u547c\u4e24\u4f4d\u662f\u94c3\u5251\u53cc\u4fa0\uff0c\u4f46\u4e0d\u77e5\u9601\u4e0b\u5c0a\u59d3\u5927\u540d\u3002\u201d\u90a3\u9752\u5e74\u602b\u7136\u4e0d\u60a6\uff0c\u5fc3\u9053\uff1a\u201c\u4f60\u65e2\u77e5\u6211\u4eec\u662f\u94c3\u5251\u53cc\u4fa0\uff0c\u600e\u4f1a\u4e0d\u77e5\u6211\u7684\u59d3\u540d\uff1f\u201d\u53e3\u4e2d\u201c\u55ef\u201d\u4e86\u4e00\u58f0\uff0c\u4e5f\u4e0d\u7b54\u8bdd\u3002 \u3000\u3000\u4fbf\u5728\u6b64\u65f6\uff0c\u4e00\u9635\u6c5f\u98ce\u5439\u4e86\u8fc7\u6765\uff0c\u62c2\u8d77\u72c4\u4e91\u8eab\u4e0a\u6240\u7a7f\u50e7\u888d\u7684\u8863\u89d2\u3002 \u3000\u3000\u90a3\u5c11\u5973\u4e00\u58f0\u60ca\u566b\uff0c\u9053\uff1a\u201c\u4ed6\u2026\u2026\u4ed6\u662f\u897f\u85cf\u9752\u6559\u7684\u2026\u2026\u7684\u2026\u2026\u8840\u5200\u6076\u50e7\u3002\u201d\u90a3\u9752\u5e74\u6ee1\u8138\u6012\u8272\uff0c\u9053\uff1a\u201c\u4e0d\u9519\u3002\u54fc\uff0c\u6eda\u4f60\u7684\u7f62\uff01\u201d \u3000\u3000\u72c4\u4e91\u5927\u5947\uff0c\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\u2026\u2026\u201d\u5411\u90a3\u5c11\u5973\u8d70\u8fd1\u4e00\u6b65\uff0c\u9053\uff1a\u201c\u59d1\u5a18\u4f60\u8bf4\u751a\u4e48\uff1f\u201d\u90a3\u5c11\u5973\u8138\u4e0a\u73b0\u51fa\u53c8\u60ca\u53c8\u6050\u7684\u795e\u6001\uff0c\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u2026\u2026\u4f60\u522b\u8d70\u8fd1\u6211\uff0c\u6eda\u5f00\u3002\u201d\u72c4\u4e91\u5fc3\u4e2d\u4e00\u7247\u8ff7\u60d8\uff0c\u95ee\u9053\uff1a\u201c\u751a\u4e48\uff1f\u201d\u53cd\u800c\u66f4\u5411\u5979\u8d70\u8fd1\u4e86\u4e00\u6b65\u3002 \u3000\u3000\u90a3\u5c11\u5973\u63d0\u8d77\u9a6c\u97ad\uff0c\u5237\u7684\u4e00\u58f0\uff0c\u4ece\u534a\u7a7a\u4e2d\u731b\u51fb\u4e0b\u6765\u3002\u72c4\u4e91\u4e07\u6599\u4e0d\u5230\u5979\u8bf4\u6253\u4fbf\u6253\uff0c\u8f6c\u5934\u6b32\u907f\uff0c\u5df2\u7136\u4e0d\u53ca\uff0c\u5237\u7684\u4e00\u58f0\u54cd\u5904\uff0c\u8fd9\u4e00\u97ad\u7740\u7740\u5b9e\u5b9e\u7684\u6253\u5728\u8138\u4e0a\uff0c\u4ece\u5de6\u989d\u89d2\u7ecf\u8fc7\u9f3b\u6881\uff0c\u901a\u5411\u53f3\u8fb9\u989d\u89d2\uff0c\u51fb\u5f97\u597d\u4e0d\u6c89\u91cd\u3002\u72c4\u4e91\u60ca\u6012\u4ea4\u96c6\uff0c\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u5e72\u4e48\u6253\u6211\uff1f\u201d\u89c1\u90a3\u5c11\u5973\u53c8\u6325\u97ad\u6253\u6765\uff0c\u4f38\u624b\u4fbf\u6b32\u53bb\u593a\u5979\u9a6c\u97ad\uff0c\u4e0d\u6599\u8fd9\u5c11\u5973\u97ad\u6cd5\u53d8\u5e7b\uff0c\u4ed6\u53f3\u624b\u521a\u63a2\u51fa\uff0c\u9a6c\u97ad\u5df2\u7f20\u4e0a\u4e86\u4ed6\u7684\u5934\u9888\u3002 \u3000\u3000\u8ddf\u7740\u53ea\u89c9\u5f97\u540e\u5fc3\u731b\u5730\u4e00\u75db\uff0c\u5df2\u88ab\u90a3\u9752\u5e74\u516c\u5b50\u4ece\u9a6c\u4e0a\u51fa\u817f\uff0c\u8e22\u4e86\u4e00\u811a\uff0c\u72c4\u4e91\u7acb\u8db3\u4e0d\u5b9a\uff0c\u5411\u524d\u4fbf\u5012\u3002\u90a3\u516c\u5b50\u50ac\u9a6c\u8fc7\u6765\uff0c\u7eb5\u9a6c\u8e44\u5f80\u4ed6\u8eab\u4e0a\u8e39\u53bb\u3002\u72c4\u4e91\u767e\u5fd9\u4e2d\u5411\u5916\u4e00\u6eda\uff0c\u660f\u4e71\u4e2d\u53ea\u542c\u5f97\u94f6\u94c3\u58f0\u53ee\u73b2\u73b2\u7684\u54cd\u4e86\u4e00\u4e0b\uff0c\u4e00\u6761\u767d\u8272\u7684\u9a6c\u817f\u5411\u81ea\u5df1\u80f8\u53e3\u8e0f\u5c06\u4e0b\u6765\u3002\u72c4\u4e91\u66f4\u65e0\u601d\u7d22\u4f59\u5730\uff0c\u60c5\u77e5\u8fd9\u4e00\u811a\u53ea\u8981\u8e39\u5b9e\u4e86\uff0c\u7acb\u523b\u4fbf\u4f1a\u9001\u547d\uff0c\u5f2f\u8eab\u4e00\u7f29\uff0c\u4f46\u542c\u5f97\u5580\u5587\u4e00\u54cd\uff0c\u4e0d\u77e5\u65ad\u4e86\u751a\u4e48\u4e1c\u897f\uff0c\u773c\u524d\u91d1\u661f\u98de\u821e\uff0c\u751a\u4e48\u4e5f\u4e0b\u77e5\u9053\u4e86\u3002 \u3000\u3000\u5f85\u5f97\u4ed6\u795e\u667a\u6e10\u590d\uff0c\u9192\u4e86\u8fc7\u6765\uff0c\u5df2\u4e0d\u77e5\u8fc7\u4e86\u591a\u5c11\u65f6\u5019\u3002\u8ff7\u8ff7\u7cca\u7cca\u4e2d\u6491\u624b\u60f3\u8981\u7ad9\u8d77\uff0c\u7a81\u7136\u5de6\u8170\u4e00\u9635\u5267\u75db\uff0c\u9669\u4e9b\u53c8\u6b32\u6655\u53bb\uff0c\u8ddf\u7740\u54c7\u7684\u4e00\u58f0\uff0c\u5410\u51fa\u4e00\u5927\u53e3\u9c9c\u8840\uff0c\u4ed6\u6162\u6162\u8f6c\u5934\uff0c\u53ea\u89c1\u53f3\u817f\u88e4\u811a\u4e0a\u5168\u662f\u9c9c\u8840\uff0c\u4e00\u6761\u817f\u626d\u5f97\u5411\u524d\u5f2f\u8f6c\u3002\u4ed6\u597d\u751f\u5947\u602a\uff1a\u201c\u8fd9\u6761\u817f\u600e\u4f1a\u53d8\u6210\u8fd9\u4e2a\u6837\u5b50\uff1f\u201d\u8fc7\u4e86\u4e00\u4f1a\uff0c\u8fd9\u624d\u660e\u767d\uff1a\u201c\u90a3\u59d1\u5a18\u7eb5\u9a6c\u8e39\u65ad\u4e86\u6211\u7684\u817f\u3002\u201d \u3000\u3000\u4ed6\u5168\u8eab\u4e4f\u529b\uff0c\u817f\u4e0a\u548c\u80cc\u5fc3\u66f4\u662f\u75db\u5f97\u5389\u5bb3\uff0c\u4e00\u65f6\u4e4b\u95f4\u81ea\u66b4\u81ea\u5f03\u7684\u5ff5\u5934\u53c8\u751f\uff1a\u201c\u6211\u4e0d\u8981\u6d3b\u4e86\uff0c\u4fbf\u8fd9\u4e48\u8eba\u7740\uff0c\u5feb\u5feb\u6b7b\u4e86\u624d\u597d\u3002\u201d \u3000\u3000\u4ed6\u4e5f\u4e0d\u547b\u541f\uff0c\u53ea\u76fc\u901f\u6b7b\u3002\u53ef\u662f\u60f3\u6b7b\u5374\u5e76\u4e0d\u5bb9\u6613\uff0c\u751a\u81f3\u60f3\u660f\u53bb\u4e00\u9635\u4e5f\u662f\u4e0d\u80fd\uff0c\u5fc3\u4e2d\u53ea\u60f3\uff1a\u201c\u600e\u4e48\u8fd8\u4e0d\u6b7b\uff1f\u600e\u4e48\u8fd8\u4e0d\u6b7b\uff1f\u201d \u3000\u3000\u8fc7\u4e86\u826f\u4e45\u826f\u4e45\uff0c\u8fd9\u624d\u60f3\u5230\uff1a\u201c\u6211\u8ddf\u4ed6\u4e8c\u4eba\u65e0\u51a4\u65e0\u4ec7\uff0c\u6ca1\u534a\u70b9\u5730\u65b9\u5f97\u7f6a\u4e86\u4ed6\u4eec\uff0c\u6b63\u8bf4\u5f97\u597d\u597d\u5730\uff0c\u5e72\u4e48\u5ffd\u7136\u5bf9\u6211\u4e0b\u8fd9\u6bd2\u624b\uff1f\u201d \u3000\u3000\u82e6\u82e6\u601d\u7d22\uff0c\u5fc3\u4e2d\u4e00\u7247\u832b\u7136\uff0c\u5b9e\u65e0\u4e1d\u6beb\u5934\u7eea\uff0c\u81ea\u8a00\u81ea\u8bed\uff1a\u201c\u6211\u5c31\u662f\u8fd9\u4e48\u8822\uff0c\u5018\u82e5\u4e01\u5927\u54e5\u5728\u4e16\uff0c\u5c31\u7b97\u4e0d\u80fd\u52a9\u6211\uff0c\u4e5f\u5fc5\u80fd\u7ed9\u6211\u89e3\u8bf4\u8fd9\u4e2d\u95f4\u7684\u9053\u7406\u3002\u201d \u3000\u3000\u4e00\u60f3\u8d77\u4e01\u5178\uff0c\u7acb\u65f6\u8f6c\u5ff5\uff1a\u201c\u6211\u7b54\u5e94\u4e86\u4e01\u5927\u54e5\uff0c\u5c06\u4ed6\u4e0e\u51cc\u5c0f\u59d0\u5408\u846c\u3002\u8fd9\u5fc3\u613f\u672a\u4e86\uff0c\u6211\u65e0\u8bba\u5982\u4f55\u4e0d\u80fd\u4fbf\u6b7b\u3002\u201d\u4f38\u624b\u5230\u8170\u95f4\u4e00\u6478\uff0c\u53d1\u89c9\u4e01\u5178\u7684\u9aa8\u7070\u5305\u5e76\u6ca1\u7ed9\u4eba\u8e22\u7834\uff0c\u5fc3\u4e0b\u7a0d\u6170\uff0c\u7528\u529b\u5750\u8d77\u8eab\u6765\uff0c\u5589\u5934\u4e00\u751c\uff0c\u53c8\u662f\u9c9c\u8840\u4e0a\u6d8c\u3002\u4ed6\u77e5\u9053\u591a\u5410\u4e00\u53e3\u8840\uff0c\u8eab\u5b50\u4fbf\u8870\u5f31\u4e00\u5206\uff0c\u5f3a\u81ea\u8fd0\u6c14\uff0c\u60f3\u5c06\u8fd9\u53e3\u8840\u538b\u5c06\u4e0b\u53bb\uff0c\u5374\u89c9\u53e3\u4e2d\u54b8\u54b8\u7684\uff0c\u4e00\u5f20\u5634\uff0c\u53c8\u662f\u4e00\u644a\u9c9c\u8840\u503e\u5728\u5730\u4e0b\u3002 \u3000\u3000\u6700\u75db\u7684\u662f\u90a3\u6761\u65ad\u817f\uff0c\u5c31\u50cf\u51e0\u767e\u628a\u5c0f\u5200\u4e0d\u4f4f\u5728\u817f\u4e0a\u780d\u65a9\uff0c\u7ec8\u4e8e\u8fde\u722c\u5e26\u6eda\u7684\u5230\u4e86\u67f3\u836b\u4e0b\uff0c\u5fc3\u60f3\uff1a\u201c\u6211\u4e0d\u80fd\u6b7b\uff0c\u8bf4\u751a\u4e48\u4e5f\u5f97\u6d3b\u4e0b\u53bb\u3002\u8981\u6d3b\u4e0b\u53bb\u4fbf\u5f97\u5403\u4e1c\u897f\u3002\u201d\u89c1\u5730\u4e0b\u7684\u9c7c\u867e\u65e9\u5df2\u505c\u6b62\u8df3\u52a8\uff0c\u6b7b\u53bb\u591a\u65f6\uff0c\u4fbf\u6293\u4e86\u51e0\u53ea\u867e\u585e\u5165\u53e3\u4e2d\uff0c\u80e1\u4e71\u5480\u56bc\uff0c\u5fc3\u60f3\uff1a\u201c\u5148\u5f97\u63a5\u597d\u65ad\u817f\uff0c\u518d\u60f3\u6cd5\u5b50\u5feb\u5feb\u79bb\u5f00\u3002\u201d \u3000\u3000\u6e38\u76ee\u56db\u987e\uff0c\u89c1\u4f17\u9c7c\u8d29\u629b\u5728\u5730\u4e0b\u7684\u5404\u6837\u7269\u4e8b\u5140\u81ea\u4e1c\u4e00\u4ef6\u3001\u897f\u4e00\u4ef6\u7684\u6563\u7740\uff0c\u4e8e\u662f\u722c\u8fc7\u53bb\u53d6\u4e86\u4e00\u67c4\u77ed\u6868\uff0c\u53c8\u53d6\u8fc7\u4e00\u5f20\u6e14\u7f51\uff0c\u5148\u5c06\u6e14\u7f51\u6162\u6162\u62c6\u5f00\uff0c\u7136\u540e\u642c\u6b63\u81ea\u5df1\u65ad\u817f\uff0c\u5c06\u77ed\u6868\u4ee5\u9760\u5728\u817f\u65c1\uff0c\u628a\u6e14\u7f51\u7684\u9ebb\u7ef3\u7f20\u4e86\u4e0a\u53bb\u3002\u7f20\u4e00\u4f1a\uff0c\u6b47\u4e00\u4f1a\uff0c\u6bcf\u9022\u75db\u5f97\u8981\u6655\u53bb\u65f6\uff0c\u4fbf\u95ed\u76ee\u5598\u6c14\uff0c\u7b49\u529b\u6c14\u7a0d\u957f\uff0c\u53c8\u518d\u52a8\u624b\u3002 \u3000\u3000\u597d\u5bb9\u6613\u7ed1\u597d\u65ad\u817f\uff0c\u5fc3\u60f3\uff1a\u201c\u8981\u517b\u597d\u6211\u8fd9\u6761\u817f\uff0c\u5c11\u8bf4\u4e5f\u5f97\u4e24\u4e2a\u6708\u65f6\u5149\u3002\u5374\u5230\u54ea\u91cc\u53bb\u517b\u606f\u624d\u597d\uff1f\u201d\u77a5\u773c\u89c1\u5230\u6c5f\u8fb9\u7684\u4e00\u6392\u6e14\u821f\uff0c\u5fc3\u5ff5\u4e00\u52a8\uff1a\u201c\u6211\u4fbf\u4f4f\u5728\u8239\u4e2d\uff0c\u4e0d\u7528\u884c\u8d70\u3002\u201d\u4ed6\u751f\u6015\u8fd9\u6279\u9c7c\u8d29\u56de\u6765\uff0c\u66f4\u906d\u707e\u96be\u56f0\u5384\uff0c\u867d\u5df2\u7b4b\u75b2\u529b\u5c3d\uff0c\u5374\u4e0d\u6562\u7a0d\u6b47\uff0c\u5411\u7740\u6c5f\u8fb9\u722c\u53bb\uff0c\u722c\u4e0a\u4e00\u8258\u6e14\u8239\uff0c\u89e3\u4e0b\u8239\u7f06\uff0c\u6273\u52a8\u77ed\u6868\uff0c\u6162\u6162\u5411\u6c5f\u5fc3\u5212\u53bb\u3002 \u3000\u3000\u4e00\u4f4e\u5934\u95f4\uff0c\u53ea\u89c1\u8eab\u4e0a\u4e00\u89d2\u50e7\u888d\u7ffb\u8f6c\uff0c\u9732\u51fa\u8863\u895f\u4e0a\u4e00\u628a\u6bb7\u7ea2\u5e26\u8840\u7684\u77ed\u5200\uff0c\u4e43\u662f\u4ee5\u5927\u7ea2\u4e1d\u7ebf\u6240\u7ee3\uff0c\u5200\u5934\u4e0a\u6709\u4e09\u70b9\u9c9c\u8840\u6ef4\u4e0b\uff0c\u4e5f\u662f\u7ea2\u7ebf\u7ee3\u6210\uff0c\u5f62\u72b6\u751f\u52a8\uff0c\u5341\u5206\u53ef\u6016\u3002\u4ed6\u84e6\u5730\u9192\u609f\uff1a\u201c\u554a\uff0c\u662f\u4e86\uff0c\u8fd9\u662f\u5b9d\u8c61\u6076\u50e7\u7684\u50e7\u888d\u3002\u8fd9\u4e24\u4eba\u53ea\u9053\u6211\u662f\u6076\u50e7\u7684\u4e00\u4f19\u3002\u201d \u3000\u3000\u4e00\u4f38\u624b\uff0c\u4fbf\u6478\u5230\u4e86\u81ea\u5df1\u5149\u79c3\u79c3\u7684\u8111\u888b\u3002 \u3000\u3000\u4ed6\u8fd9\u624d\u604d\u7136\uff0c\u4e3a\u751a\u4e48\u90a3\u8001\u5bb6\u4eba\u53e3\u53e3\u58f0\u58f0\u7684\u79f0\u81ea\u5df1\u4e3a\u201c\u5c0f\u5e08\u7236\u201d\uff0c\u800c\u957f\u6c5f\u94c1\u7f51\u5e2e\u7684\u9c7c\u8d29\u5934\u5b50\u53c8\u9a82\u81ea\u5df1\u4e3a\u201c\u5c0f\u8d3c\u79c3\u201d\uff0c\u539f\u6765\u81ea\u5df1\u65e9\u5df2\u4e54\u88c5\u6539\u626e\u505a\u4e86\u4e2a\u548c\u5c1a\uff0c\u5374\u5140\u81ea\u4e0d\u89c9\u3002\u53c8\u60f3\uff1a\u201c\u6211\u8863\u89d2\u4e00\u7ffb\uff0c\u90a3\u59d1\u5a18\u4fbf\u8bf4\u6211\u662f\u897f\u85cf\u9752\u6559\u7684\u751a\u4e48\u8840\u5200\u6076\u50e7\u3002\u8fd9\u628a\u8840\u5200\u7684\u6a21\u6837\u8fd9\u4e48\u96be\u770b\uff0c\u8fd9\u4e00\u6d3e\u548c\u5c1a\u53c8\u5b9a\u662f\u65e0\u6076\u4e0d\u4f5c\u4e4b\u4eba\u3002\u5355\u770b\u5b9d\u8c61\uff0c\u4fbf\u53ef\u60f3\u800c\u77e5\u4e86\u3002\u201d \u3000\u3000\u4ed6\u65e0\u7aef\u7aef\u7684\u7ed9\u8e39\u65ad\u4e86\u817f\uff0c\u672c\u6765\u6781\u662f\u607c\u6012\u60b2\u6124\uff0c\u4e00\u60f3\u660e\u767d\u5176\u95f4\u7684\u539f\u56e0\u8fc7\u8282\uff0c\u767b\u65f6\u4fbf\u5bf9\u201c\u94c3\u5251\u53cc\u4fa0\u201d\u6d88\u4e86\u654c\u610f\uff0c\u53cd\u89c9\u8fd9\u5bf9\u9752\u5e74\u82f1\u4fa0\u5ac9\u6076\u5982\u4ec7\uff0c\u5b9e\u662f\u5927\u5927\u7684\u597d\u4eba\uff0c\u53ea\u662f\u8fd9\u4e8c\u4eba\u6b66\u529f\u9ad8\u5f3a\uff0c\u4eba\u54c1\u4fca\u96c5\uff0c\u81ea\u5df1\u4fbf\u7b97\u5c06\u8bef\u4f1a\u89e3\u91ca\u660e\u767d\u4e86\uff0c\u4e5f\u4e0d\u914d\u8ddf\u4ed6\u4eec\u7ed3\u4ea4\u3002 \u3000\u3000\u5c06\u6e14\u8239\u6162\u6162\u5212\u51fa\u5341\u4f59\u91cc\uff0c\u89c1\u5cb8\u65c1\u6709\u4e2a\u5c0f\u5e02\u9547\uff0c\u8fdc\u8fdc\u671b\u53bb\uff0c\u4eba\u6765\u7199\u5f80\u7684\u751a\u662f\u70ed\u95f9\uff0c\u5fc3\u60f3\uff1a\u201c\u8fd9\u4ef6\u50e7\u8863\u88ab\u5728\u8eab\u4e0a\uff0c\u662f\u4e2a\u5927\u5927\u7684\u7978\u80ce\uff0c\u987b\u5f97\u5c3d\u65e9\u6362\u4e86\u53bb\u624d\u597d\u3002\u201d\u5f53\u4e0b\u5c06\u8239\u5212\u8fd1\u5cb8\u8fb9\uff0c\u6491\u7740\u77ed\u6868\u62c4\u5730\uff0c\u6323\u624e\u7740\u4e00\u8ddb\u4e00\u62d0\uff0c\u8d70\u4e0a\u5cb8\u53bb\u3002\u5e02\u4e0a\u884c\u4eba\u89c1\u8fd9\u9752\u5e74\u548c\u5c1a\u8ddb\u4e86\u4e00\u6761\u817f\uff0c\u6ee1\u8eab\u8840\u6c61\uff0c\u5411\u4ed6\u77a7\u53bb\u65f6\u8138\u4e0a\u90fd\u9732\u51fa\u60ca\u7591\u7684\u795e\u8272\u3002 \u3000\u3000\u5bf9\u8fd9\u7b49\u51b7\u6f20\u7591\u5fcc\u7684\u795e\u6c14\uff0c\u72c4\u4e91\u8fd9\u51e0\u5e74\u6765\u53d7\u5f97\u591a\u4e86\uff0c\u5012\u4e5f\u4e0d\u4ee5\u4e3a\u610f\u3002\u4ed6\u7f13\u7f13\u5728\u8857\u4e0a\u884c\u8d70\uff0c\u89c1\u5230\u4e00\u5bb6\u65e7\u8863\u5e97\uff0c\u4fbf\u8fdb\u53bb\u4e70\u4e86\u4ef6\u9752\u5e03\u957f\u888d\uff0c\u4e00\u5957\u77ed\u886b\u88e4\u3002\u8fd9\u65f6\u66f4\u6362\u8863\u886b\uff0c\u52bf\u987b\u5148\u884c\u8d64\u8eab\u9732\u4f53\uff0c\u53ea\u5f97\u5c06\u9752\u5e03\u957f\u888d\u7a7f\u5728\u50e7\u888d\u4e4b\u5916\uff0c\u53c8\u4e70\u4e86\u9876\u6be1\u5e3d\uff0c\u76d6\u4f4f\u5149\u5934\uff0c\u7136\u540e\u5230\u897f\u9996\u4e00\u5bb6\u5c0f\u996d\u94fa\u4e2d\u53bb\u4e70\u996d\u5145\u9965\u3002\u5f85\u5f97\u5728\u996d\u94fa\u7684\u957f\u51f3\u4e0a\u5750\u5b9a\uff0c\u7d2f\u5f97\u51e0\u6b32\u6655\u5012\uff0c\u53c8\u5455\u4e24\u5927\u53e3\u8840\u3002 \u3000\u3000\u5e97\u4f34\u9001\u4e0a\u996d\u83dc\uff0c\u662f\u4e00\u7897\u8c46\u8150\u716e\u9c7c\uff0c\u4e00\u7897\u8c46\u8c49\u814a\u8089\u3002\u72c4\u4e91\u95fb\u5230\u9c7c\u8089\u548c\u7c73\u996d\u7684\u9999\u6c14\uff0c\u7cbe\u795e\u4e3a\u4e4b\u4e00\u632f\uff0c\u8c61\u8d77\u7b77\u5b50\uff0c\u6252\u4e86\u4e24\u53e3\u996d\uff0c\u631f\u8d77\u4e00\u5757\u814a\u8089\u9001\u8fdb\u53e3\u4e2d\uff0c\u5480\u56bc\u5f97\u51e0\u4e0b\uff0c\u5ffd\u542c\u5f97\u897f\u5317\u89d2\u4e0a\u53ee\u5f53\u53ee\u5f53\u3001\u53ee\u73b2\u73b2\uff0c\u53ee\u5f53\u53ee\u5f53\u3001\u53ee\u73b2\u73b2\uff0c\u4e00\u9635\u9635\u9e3e\u94c3\u4e4b\u58f0\u54cd\u4e86\u8d77\u6765\u3002 \u3000\u3000\u4ed6\u53e3\u4e2d\u7684\u814a\u8089\u767b\u65f6\u4fbf\u54bd\u4e0d\u4e0b\u54bd\u5589\uff0c\u5fc3\u9053\uff1a\u201c\u94c3\u5251\u53cc\u4fa0\u53c8\u6765\u4e86\u3002\u8981\u4e0d\u8981\u8fce\u51fa\u53bb\u8bf4\u660e\u8bef\u4f1a\uff1f\u6211\u5e73\u767d\u65e0\u8f9c\u7684\u7ed9\u4ed6\u4eec\u7eb5\u9a6c\u8e39\u6210\u8fd9\u822c\u91cd\u4f24\uff0c\u82e5\u4e0d\u8bf4\u4e2a\u6e05\u695a\uff0c\u5fcc\u4e0d\u51a4\u6789\uff1f\u201d \u3000\u3000\u53ef\u662f\u4ed6\u8fd9\u4e9b\u65e5\u5b50\u4e2d\u53d7\u82e6\u592a\u6df1\uff0c\u7ed9\u4eba\u6b3a\u4fae\u60ef\u4e86\uff0c\u8f6c\u5ff5\u4fbf\u60f3\uff1a \u3000\u3000\u201c\u6211\u8fd9\u4e00\u751f\u53d7\u7684\u51a4\u6789\uff0c\u96be\u9053\u8fd8\u7b97\u5c11\u4e86\uff1f\u518d\u7ed9\u4ed6\u4eec\u51a4\u6789\u4e00\u6b21\uff0c\u53c8\u6709\u4f55\u59a8\uff1f\u201d\u4f46\u542c\u5f97\u9e3e\u94c3\u4e4b\u58f0\u8d8a\u54cd\u8d8a\u8fd1\uff0c\u72c4\u4e91\u8f6c\u8fc7\u8eab\u6765\uff0c\u9762\u671d\u91cc\u74a7\uff0c\u4e0d\u613f\u518d\u548c\u4ed6\u4eec\u76f8\u89c1\u3002 \u3000\u3000\u4fbf\u5728\u8fd9\u65f6\uff0c\u5ffd\u7136\u6709\u4eba\u4f38\u624b\u5728\u4ed6\u80a9\u5934\u4e00\u62cd\uff0c\u7b11\u9053\uff1a\u201c\u5c0f\u5e08\u7236\uff0c\u4f60\u5e72\u4e0b\u7684\u597d\u4e8b\u53d1\u4e86\uff0c\u6211\u4eec\u592a\u7237\u8bf7\u4f60\u53bb\u559d\u9152\u3002\u201d \u3000\u3000\u72c4\u4e91\u5403\u4e86\u4e00\u60ca\uff0c\u8f6c\u8eab\u8fc7\u6765\uff0c\u89c1\u662f\u56db\u4e2a\u516c\u4eba\uff0c\u4e24\u4e2a\u62ff\u7740\u94c1\u5c3a\u94c1\u94fe\uff0c\u540e\u9762\u4e24\u4eba\u624b\u6267\u5355\u5200\uff0c\u6ee1\u8138\u6212\u5907\u4e4b\u8272\u3002\u72c4\u4e91\u53eb\u58f0\u201c\u554a\u54df\uff01\u201d\u7ad9\u8d77\u8eab\u6765\uff0c\u987a\u624b\u6293\u8d77\u684c\u4e0a\u4e00\u7897\u814a\u8089\uff0c\u5288\u5934\u5411\u5de6\u9996\u90a3\u516c\u4eba\u63b7\u53bb\uff0c\u8ddf\u7740\u624b\u8098\u4e00\u62ac\uff0c\u6380\u8d77\u677f\u684c\uff0c\u5c06\u8c46\u8150\u3001\u767d\u996d\u3001\u83dc\u6c64\uff0c\u4e00\u9f50\u5411\u7b2c\u4e8c\u540d\u516c\u4eba\u8eab\u4e0a\u5012\u53bb\uff0c\u5fc3\u9053\uff1a\u201c\u8346\u5dde\u5e9c\u7684\u516c\u4eba\u8ffd\u5230\u4e86\u3002\u6211\u82e5\u518d\u843d\u5728\u51cc\u9000\u601d\u7684\u624b\u4e2d\uff0c\u54ea\u91cc\u8fd8\u6709\u547d\u5728\uff1f\u201d \u3000\u3000\u90a3\u4e24\u540d\u516c\u4eba\u88ab\u4ed6\u5939\u5934\u5939\u8111\u7684\u70ed\u83dc\u70ed\u6c64\u4e00\u6cfc\uff0c\u5fd9\u5411\u540e\u9000\uff0c\u72c4\u4e91\u5df2\u62a2\u6b65\u5954\u4e86\u51fa\u53bb\u3002\u4f46\u53ea\u8de8\u5f97\u4e00\u6b65\uff0c\u811a\u4e0b\u4e00\u4e2a\u8e09\u8dc4\uff0c\u9669\u4e9b\u6454\u5012\uff0c\u4ed6\u5728\u60f6\u6025\u4e4b\u9645\uff0c\u7adf\u5fd8\u4e86\u5de6\u817f\u5df2\u65ad\u3002\u7b2c\u4e09\u540d\u516c\u4eba\u77a7\u51fa\u4fbf\u5b9c\uff0c\u4e3e\u5200\u780d\u6765\uff0c\u72c4\u4e91\u6b66\u529f\u867d\u5931\uff0c\u5bf9\u4ed8\u8fd9\u4e9b\u516c\u4eba\u5374\u8fd8\u662f\u7ef0\u7ef0\u6709\u4f59\uff0c\u6293\u4f4f\u4ed6\u624b\u8155\u4e00\u62e7\uff0c\u5df2\u593a\u8fc7\u4e86\u4ed6\u5355\u5200\u3002 \u3000\u3000\u56db\u540d\u516c\u4eba\u89c1\u4ed6\u624b\u4e2d\u6709\u4e86\u5175\u5668\uff0c\u54ea\u91cc\u8fd8\u6562\u6b3a\u8fd1\uff0c\u53ea\u662f\u5927\u53eb\uff1a \u3000\u3000\u201c\u91c7\u82b1\u6deb\u50e7\u62d2\u6355\u4f24\u4eba\u554a\uff01\u201d\u201c\u8840\u5200\u6076\u50e7\u53c8\u72af\u4e86\u6848\u54ea\uff01\u201d\u201c\u5978\u6740\u5b98\u5bb6\u5c0f\u59d0\u7684\u6deb\u50e7\u5728\u8fd9\u91cc\u554a\u3002\u201d \u3000\u3000\u8fd9\u4e48\u4e00\u53eb\u56b7\uff0c\u5e02\u9547\u4e0a\u4f17\u4eba\u7eb7\u7eb7\u8fc7\u6765\uff0c\u89c1\u5230\u72c4\u4e91\u8fd9\u4e48\u6ee1\u8138\u90fd\u662f\u4f24\u75d5\u8840\u6c61\u7684\u53ef\u6016\u795e\u60c5\uff0c\u90fd\u8fdc\u8fdc\u7ad9\u7740\uff0c\u4e0d\u6562\u8d70\u8fd1\u3002 \u3000\u3000\u72c4\u4e91\u542c\u5f97\u516c\u4eba\u7684\u53eb\u56b7\uff0c\u5fc3\u9053\uff1a\u201c\u96be\u9053\u4e0d\u662f\u8346\u5dde\u5e9c\u6d3e\u6765\u6349\u62ff\u6211\u7684\uff1f\u201d\u5927\u58f0\u559d\u9053\uff1a\u201c\u4f60\u4eec\u80e1\u8bf4\u4e9b\u751a\u4e48\uff1f\u8c01\u662f\u91c7\u82b1\u6deb\u50e7\u4e86\uff1f\u201d\u53ee\u5f53\u53ee\u5f53\u3001\u53ee\u73b2\u73b2\u51e0\u58f0\u54cd\u5904\uff0c\u4e00\u5339\u9ec4\u9a6c\u3001\u4e00\u5339\u767d\u9a6c\u53cc\u53cc\u9a70\u5230\u3002 \u3000\u3000\u201c\u94c3\u5251\u53cc\u4fa0\u201d\u4eba\u5728\u9a6c\u4e0a\uff0c\u5c45\u9ad8\u4e34\u4e0b\uff0c\u4e00\u5207\u65e9\u5df2\u770b\u6e05\u3002\u4e24\u4eba\u4e00\u89c1\u72c4\u4e91\uff0c\u6014\u4e86\u4e00\u6014\uff0c\u89c9\u5f97\u9762\u5bb9\u597d\u719f\uff0c\u7acb\u523b\u4fbf\u8ba4\u51fa\u4ed6\u4fbf\u662f\u90a3\u4e2a\u8840\u5200\u6076\u50e7\uff0c\u53ea\u662f\u4e54\u88c5\u6539\u626e\u4e86\uff0c\u60f3\u8981\u63a9\u9970\u672c\u6765\u9762\u76ee\u3002 \u3000\u3000\u4e00\u540d\u516c\u4eba\u53eb\u9053\uff1a\u201c\u5582\uff0c\u5927\u5e08\u7236\uff0c\u4f60\u98ce\u6d41\u5feb\u6d3b\uff0c\u4e5f\u4e0d\u6253\u7d27\uff0c\u600e\u5730\u4e8b\u540e\u53c8\u5c06\u4eba\u5bb6\u59d1\u5a18\u4e00\u5200\u6740\u6b7b\u4e86\uff1f\u597d\u6c49\u4e00\u4eba\u505a\u4e8b\u4e00\u8eab\u5f53\uff0c\u8ddf\u6211\u4eec\u5230\u53bf\u91cc\u53bb\u6253\u4e86\u8fd9\u6869\u5b98\u53f8\u7f62\u3002\u201d\u53e6\u4e00\u540d\u516c\u4eba\u9053\uff1a\u201c\u4f60\u53bb\u4e70\u8863\u4e70\u5e3d\uff0c\u6539\u88c5\u6613\u5bb9\uff0c\u53ef\u90fd\u7ed9\u54e5\u513f\u4eec\u77a7\u5728\u773c\u91cc\u5566\u3002\u4f60\u4eca\u5929\u662f\u9003\u4e0d\u8d70\u4e86\u7684\uff0c\u8fd8\u662f\u4e56\u4e56\u5c31\u7f1a\u7684\u597d\u3002\u201d\u72c4\u4e91\u6012\u9053\uff1a\u201c\u4f60\u4eec\u5c31\u4f1a\u80e1\u8bf4\u516b\u9053\uff0c\u51a4\u6789\u597d\u4eba\u3002\u201d\u4e00\u540d\u516c\u4eba\u9053\uff1a\u201c\u90a3\u662f\u51b3\u8ba1\u51a4\u6789\u4e0d\u4e86\u7684\u3002\u5927\u524d\u5929\u665a\u4e0a\u4f60\u95ef\u8fdb\u674e\u4e3e\u4eba\u5e9c\u4e2d\uff0c\u5978\u6740\u674e\u4e3e\u4eba\u7684\u4e24\u4f4d\u5c0f\u59d0\uff0c\u6211\u662f\u6e05\u6e05\u695a\u695a\u77a7\u89c1\u4e86\u7684\uff0c\u773c\u775b\u7709\u6bdb\uff0c\u9f3b\u5934\u5634\u5df4\uff0c\u6ca1\u4e00\u6837\u9519\u4e86\uff0c\u7684\u7684\u786e\u786e\u4fbf\u662f\u4f60\u3002\u201d \u3000\u3000\u201c\u94c3\u5251\u53cc\u4fa0\u201d\u52d2\u9a6c\u7ad9\u5728\u4e00\u65c1\u89c2\u770b\u3002 \u3000\u3000\u201c\u8868\u54e5\uff0c\u8fd9\u548c\u5c1a\u6b66\u529f\u6ca1\u751a\u4e48\u4e86\u4e0d\u8d77\u554a\u3002\u521a\u624d\u82e5\u4e0d\u662f\u77a7\u5728\u4ed6\u6551\u4e86\u6c34\u798f\u6027\u547d\u7684\u4efd\u4e0a\uff0c\u65e9\u5c31\u6740\u4e86\u4ed6\u3002\u539f\u6765\u4ed6\u2026\u2026\u4ed6\u7adf\u8fd9\u4e48\u574f\u3002\u201d \u3000\u3000\u201c\u6211\u4e5f\u89c9\u5f97\u5947\u602a\u3002\u867d\u8bf4\u8fd9\u4e9b\u6076\u50e7\u5728\u957f\u6c5f\u4e24\u5cb8\u505a\u4e86\u4e0d\u5c11\u5929\u7406\u96be\u5bb9\u7684\u5927\u6848\uff0c\u4f24\u4e86\u5341\u51e0\u6761\u4eba\u547d\uff0c\u516c\u4eba\u5948\u4f55\u4ed6\u4eec\u4e0d\u5f97\uff0c\u53ef\u662f\u4e24\u6e56\u8c6a\u6770\u53c8\u4f55\u5fc5\u8fd9\u7b49\u5927\u60ca\u5c0f\u602a\uff1f\u77a7\u8fd9\u5c0f\u548c\u5c1a\u7684\u6b66\u529f\uff0c\u4ed6\u7684\u5e08\u7236\u3001\u5e08\u5144\u4eec\u4e5f\u9ad8\u660e\u4e0d\u5230\u4e86\u54ea\u91cc\u53bb\u3002\u201d \u3000\u3000\u201c\u8bf4\u4e0d\u5b9a\u4ed6\u8fd9\u4e00\u4f19\u4e2d\u53e6\u6709\u9ad8\u624b\uff0c\u5426\u5219\u7684\u8bdd\uff0c\u4e24\u6e56\u8c6a\u6770\u5e72\u4e48\u8981\u6c42\u6211\u7239\u7239\u51fa\u624b\uff1f\u53c8\u4e0a\u95e8\u53bb\u6c42\u9646\u4f2f\u4f2f\u3001\u82b1\u4f2f\u4f2f\u3001\u5218\u4f2f\u4f2f\uff1f\u201d \u3000\u3000\u201c\u54fc\uff0c\u8fd9\u4e9b\u4e24\u6e56\u8c6a\u6770\u4e5f\u5f53\u771f\u5f02\u60f3\u5929\u5f00\uff0c\u5929\u4e0b\u53c8\u6709\u54ea\u4e00\u4f4d\u9ad8\u4eba\uff0c\u987b\u5f97\u52b3\u52a8\u2018\u843d\u82b1\u6d41\u6c34\u2019\u56db\u5927\u4fa0\u540c\u65f6\u51fa\u624b\uff0c\u624d\u5bf9\u4ed8\u5f97\u4e86\uff1f\u201d \u3000\u3000\u201c\u563b\u563b\uff0c\u52b3\u52a8\u4e00\u4e0b\u54b1\u4eec\u2018\u94c3\u5251\u53cc\u4fa0\u2019\u7684\u5927\u9a7e\uff0c\u90a3\u8fd8\u5dee\u4e0d\u591a\u3002\u201d \u3000\u3000\u201c\u8868\u59b9\uff0c\u4f60\u5230\u524d\u9762\u53bb\u7b49\u6211\uff0c\u8ba9\u6211\u4e00\u4e2a\u4eba\u6765\u5bf9\u4ed8\u8fd9\u8d3c\u79c3\u597d\u4e86\u3002\u201d \u3000\u3000\u201c\u6211\u5728\u8fd9\u91cc\u77a7\u7740\u3002\u201d \u3000\u3000\u201c\u4e0d\uff0c\u4f60\u8fd8\u662f\u522b\u5728\u8fd9\u91cc\uff0c\u6b66\u6797\u4e2d\u4eba\u65e5\u540e\u8bf4\u8d77\u8fd9\u56de\u4e8b\u6765\uff0c\u53ea\u8bf4\u662f\u6211\u6c6a\u5578\u98ce\u72ec\u81ea\u51fa\u624b\uff0c\u6740\u4e86\u8840\u5200\u6076\u50e7\uff0c\u53ef\u522b\u628a\u6c34\u7b19\u6c34\u5973\u4fa0\u7275\u626f\u5728\u5185\u3002\u4f60\u77e5\u9053\uff0c\u6c5f\u6e56\u4e0a\u90a3\u4e9b\u4eba\u7684\u5634\u53ef\u6709\u591a\u810f\u3002\u201d \u3000\u3000\u201c\u5bf9\uff0c\u4f60\u60f3\u5f97\u5468\u5230\uff0c\u6211\u53ef\u6ca1\u4f60\u8fd9\u4e48\u7ec6\u5fc3\u3002\u201d"}),showOnHomepage:!0},{name:"\u516d\u3000\u8840\u5200\u8001\u7956",content:(0,a.jsx)(a.Fragment,{children:"\u72c4\u4e91\u89c1\u56db\u4e0b\u91cc\u95f2\u4eba\u6e10\u56f4\u6e10\u591a\uff0c\u8131\u8eab\u66f4\u52a0\u96be\u4e86\uff0c\u4e3e\u5200\u4e00\u626c\uff0c\u559d\u9053\uff1a\u201c\u5feb\u7ed9\u6211\u8ba9\u5f00\uff01\u201d\u5de6\u814b\u4e0b\u6491\u7740\u90a3\u6761\u77ed\u6868\uff0c\u4fbf\u5411\u4e1c\u9996\u51b2\u53bb\u3002 \u3000\u3000\u56f4\u5728\u8857\u5934\u7684\u95f2\u4eba\u53d1\u4e00\u58f0\u558a\uff0c\u56db\u6563\u5954\u9003\u3002\u90a3\u56db\u540d\u516c\u4eba\u53eb\u9053\uff1a\u201c\u91c7\u82b1\u6deb\u50e7\uff0c\u5f80\u54ea\u91cc\u8d70\uff1f\u201d\u786c\u7740\u5934\u76ae\u8ffd\u4e86\u4e0a\u53bb\u3002\u72c4\u4e91\u5355\u5200\u659c\u6307\uff0c\u624b\u8155\u7ffb\u5904\uff0c\u5df2\u5212\u4f24\u4e86\u4e00\u540d\u516c\u4eba\u7684\u624b\u81c2\u3002\u90a3\u516c\u4eba\u5927\u53eb\uff1a\u201c\u62d2\u6355\u6740\u4eba\u54ea\uff01\u62d2\u6355\u6740\u4eba\u54ea\uff01\u201d \u3000\u3000\u6c34\u7b19\u50ac\u9a6c\u8d70\u5f00\u3002\u6c6a\u5578\u98ce\u7eb5\u9a6c\u4e0a\u524d\uff0c\u9a6c\u97ad\u626c\u51fa\uff0c\u5237\u7684\u4e00\u58f0\uff0c\u5377\u4f4f\u4e86\u72c4\u4e91\u624b\u4e2d\u5355\u5200\uff0c\u5f80\u5916\u4e00\u7529\u3002\u72c4\u4e91\u624b\u4e0a\u65e0\u529b\uff0c\u5355\u5200\u7acb\u65f6\u8131\u624b\u98de\u51fa\u3002\u6c6a\u5578\u98ce\u5de6\u81c2\u63a2\u51fa\uff0c\u6293\u4f4f\u4e86\u4ed6\u540e\u9888\u8863\u9886\uff0c\u5c06\u4ed6\u8eab\u5b50\u63d0\u8d77\uff0c\u559d\u9053\uff1a\u201c\u6deb\u50e7\uff0c\u4f60\u5728\u4e24\u6e56\u505a\u4e0b\u4e86\u8fd9\u8bb8\u591a\u6848\u5b50\uff0c\u8fd8\u60f3\u6d3b\u547d\u4e0d\u6210\uff01\u201d\u53f3\u624b\u53cd\u6309\u5251\u628a\uff0c\u9752\u5149\u95ea\u5904\uff0c\u957f\u5251\u51fa\u9798\uff0c\u4fbf\u8981\u5f80\u72c4\u4e91\u9888\u4e2d\u780d\u843d\u3002 \u3000\u3000\u65c1\u89c2\u4f17\u4eba\u9f50\u58f0\u559d\u5f69\uff1a\u201c\u597d\u6781\uff0c\u597d\u6781\uff01\u201d\u201c\u6740\u4e86\u8fd9\u6deb\u50e7\uff01\u201d\u201c\u5927\u4f19\u513f\u54ac\u4ed6\u4e00\u53e3\u51fa\u6c14\uff01\u201d \u3000\u3000\u72c4\u4e91\u8eab\u5728\u534a\u7a7a\uff0c\u5168\u65e0\u534a\u5206\u6297\u62d2\u4e4b\u529b\uff0c\u6697\u6697\u53f9\u4e86\u53e3\u6c14\uff0c\u5fc3\u9053\uff1a\u201c\u6211\u547d\u4e2d\u6ce8\u5b9a\u8981\u7ed9\u4eba\u51a4\u6789\uff0c\u90a3\u4e5f\u65e0\u6cd5\u53ef\u60f3\u3002\u201d\u773c\u89c1\u6c6a\u5578\u98ce\u624b\u4e2d\u7684\u957f\u5251\u5df2\u4e3e\u5728\u534a\u7a7a\uff0c\u4ed6\u5fae\u5fae\u82e6\u7b11\uff0c\u5fc3\u9053\uff1a\u201c\u4e01\u5927\u54e5\uff0c\u4e0d\u662f\u5c0f\u5f1f\u4e0d\u66fe\u5c3d\u529b\uff0c\u5b9e\u5728\u6211\u8fd0\u6c14\u592a\u574f\u3002\u201d \u3000\u3000\u5ffd\u95fb\u5f97\u8fdc\u5904\u4e00\u4e2a\u82cd\u8001\u5e72\u67af\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u624b\u4e0b\u7559\u4eba\uff0c\u4f11\u5f97\u4f24\u4ed6\u6027\u547d\u3002\u201d \u3000\u3000\u6c6a\u5578\u98ce\u56de\u8fc7\u5934\u53bb\uff0c\u89c1\u662f\u4e00\u4e2a\u8eab\u7a7f\u9ec4\u888d\u7684\u548c\u5c1a\u3002\u90a3\u548c\u5c1a\u5e74\u7eaa\u6781\u8001\uff0c\u5c16\u5934\u524a\u8033\uff0c\u8138\u4e0a\u90fd\u662f\u76b1\u7eb9\uff0c\u8eab\u4e0a\u50e7\u888d\u7684\u8d28\u5730\u989c\u8272\u548c\u72c4\u4e91\u6240\u7a7f\u4e00\u6a21\u4e00\u6837\u3002\u6c6a\u5578\u98ce\u8138\u8272\u4e00\u53d8\uff0c\u77e5\u662f\u897f\u85cf\u8840\u5200\u50e7\u7684\u4e00\u6d3e\uff0c\u4e3e\u5251\u4fbf\u5411\u72c4\u4e91\u9888\u4e2d\u780d\u843d\uff0c\u51c6\u62df\u5148\u6740\u5c0f\u6deb\u50e7\uff0c\u518d\u6740\u8001\u6deb\u50e7\u3002 \u3000\u3000\u5251\u950b\u79bb\u72c4\u4e91\u7684\u5934\u9888\u5c1a\u6709\u5c3a\u8bb8\uff0c\u731b\u89c9\u53f3\u624b\u8098\u5f2f\u4e2d\u4e00\u9ebb\uff0c\u5df2\u88ab\u6697\u5668\u6253\u4e2d\u4e86\u7a74\u9053\u3002\u4ed6\u624b\u4e2d\u957f\u5251\u8f6f\u8f6f\u7684\u5782\u4e86\u4e0b\u6765\uff0c\u867d\u662f\u529b\u9053\u5168\u65e0\uff0c\u4f46\u5251\u5203\u950b\u5229\uff0c\u4ecd\u5728\u72c4\u4e91\u7684\u5de6\u988a\u4e0a\u5212\u4e86\u4e00\u9053\u8840\u75d5\u3002 \u3000\u3000\u90a3\u8001\u50e7\u8eab\u5f62\u5982\u98ce\uff0c\u6b3a\u8fd1\u8eab\u6765\uff0c\u4e00\u638c\u5c06\u6c6a\u5578\u98ce\u63a8\u843d\u4e0b\u9a6c\uff0c\u5de6\u624b\u6293\u8d77\u72c4\u4e91\uff0c\u53f3\u817f\u4e00\u62ac\uff0c\u7adf\u5728\u5e73\u5730\u8de8\u4e0a\u4e86\u9ec4\u9a6c\u9a6c\u80cc\u3002\u65c1\u4eba\u4e0a\u9a6c\uff0c\u5fc5\u662f\u5de6\u8db3\u5148\u8e0f\u4e0a\u5de6\u956b\uff0c\u7136\u540e\u53f3\u817f\u8de8\u4e0a\u9a6c\u80cc\uff0c\u4f46\u8fd9\u8001\u50e7\u65e2\u4e0d\u7eb5\u8dc3\uff0c\u4ea6\u4e0d\u8e0f\u956b\uff0c\u4e00\u62ac\u53f3\u817f\uff0c\u4fbf\u4e0a\u4e86\u9a6c\u978d\uff0c\u7eb5\u9a6c\u5411\u6c34\u7b19\u9a70\u53bb\u3002 \u3000\u3000\u6c34\u7b19\u542c\u5f97\u6c6a\u5578\u98ce\u60ca\u547c\uff0c\u5f53\u5373\u52d2\u9a6c\u3002\u6c6a\u5578\u98ce\u53eb\u9053\uff1a\u201c\u8868\u59b9\uff0c\u5feb\u8d70\uff01\u201d\u6c34\u7b19\u5fae\u4e00\u8fdf\u7591\uff0c\u6389\u8f6c\u9a6c\u5934\uff0c\u90a3\u8001\u50e7\u5df2\u9a91\u4e86\u9ec4\u9a6c\u8ffd\u5230\u3002 \u3000\u3000\u4ed6\u5c06\u72c4\u4e91\u5f80\u6c34\u7b19\u8eab\u540e\u7684\u767d\u9a6c\u978d\u5b50\u4e0a\u4e00\u653e\uff0c\u6b63\u8981\u987a\u624b\u5c06\u5979\u63a8\u843d\uff0c\u6c34\u7b19\u5df2\u62d4\u51fa\u957f\u5251\uff0c\u5411\u4ed6\u5934\u4e0a\u780d\u4e0b\u3002\u90a3\u8001\u50e7\u89c1\u5230\u5979\u79c0\u4e3d\u7684\u5bb9\u8c8c\uff0c\u6014\u4e86\u4e00\u6014\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u7f8e\uff01\u201d\u624b\u81c2\u4e00\u63a2\uff0c\u70b9\u4e2d\u4e86\u5979\u8170\u95f4\u7a74\u9053\u3002 \u3000\u3000\u6c34\u7b19\u4e00\u5251\u780d\u5230\u534a\u7a7a\uff0c\u9661\u7136\u95f4\u5168\u8eab\u65e0\u529b\uff0c\u957f\u5251\u5f53\u5577\u4e00\u58f0\u843d\u5730\uff0c\u5fc3\u4e2d\u53c8\u60ca\u53c8\u6015\uff0c\u5fd9\u8981\u8dc3\u4e0b\u9a6c\u6765\uff0c\u7a81\u89c9\u540e\u8170\u4e0a\u53c8\u662f\u4e00\u9ebb\uff0c\u53cc\u817f\u5df2\u7136\u4e0d\u542c\u4f7f\u5524\u3002 \u3000\u3000\u90a3\u8001\u50e7\u5de6\u624b\u7275\u4f4f\u767d\u9a6c\u7f30\u7ef3\uff0c\u53cc\u817f\u4e00\u631f\uff0c\u9ec4\u9a6c\u3001\u767d\u9a6c\u4fbf\u53ee\u5f53\u53ee\u5f53\u3001\u53ee\u73b2\u73b2\uff0c\u53ee\u5f53\u53ee\u5f53\u3001\u53ee\u73b2\u73b2\u7684\u53bb\u4e86\u3002 \u3000\u3000\u6c6a\u5578\u98ce\u8eba\u5728\u5730\u4e0b\uff0c\u5927\u53eb\uff1a\u201c\u8868\u59b9\uff0c\u8868\u59b9\uff01\u201d\u773c\u7741\u7741\u77a7\u7740\u8868\u59b9\u88ab\u4e24\u4e2a\u6deb\u50e7\u63b3\u53bb\uff0c\u540e\u679c\u76f4\u662f\u4e0d\u582a\u8bbe\u60f3\uff0c\u53ef\u662f\u4ed6\u5168\u8eab\u9178\u8f6f\uff0c\u7aed\u5c3d\u5e73\u751f\u4e4b\u529b\uff0c\u4e5f\u662f\u52a8\u5f39\u4e0d\u4e86\u534a\u5206\u3002 \u3000\u3000\u4f46\u542c\u5f97\u90a3\u4e9b\u516c\u4eba\u5927\u53eb\u5927\u56b7\uff1a\u201c\u6349\u62ff\u6deb\u50e7\u554a\uff01\u201d\u201c\u8840\u5200\u6076\u50e7\u9003\u8d70\u4e86\uff01\u201d\u201c\u62d2\u6355\u4f24\u4eba\u554a\uff01\u201d \u3000\u3000\u72c4\u4e91\u8eab\u5728\u9a6c\u80cc\uff0c\u4e00\u6447\u4e00\u6643\u7684\u9669\u4e9b\u6454\u4e0b\uff0c\u81ea\u7136\u800c\u7136\u7684\u4f38\u624b\u4e00\u6293\uff0c\u89e6\u624b\u4e4b\u5904\uff0c\u53ea\u89c9\u8f6f\u7ef5\u7ef5\u5730\uff0c\u4e00\u4f4e\u5934\uff0c\u89c1\u5230\u6293\u4f4f\u7684\u5374\u662f\u6c34\u7b19\u540e\u80cc\u8170\u95f4\u3002\u6c34\u7b19\u5927\u60ca\uff0c\u53eb\u9053\uff1a\u201c\u6076\u548c\u5c1a\uff0c\u5feb\u653e\u624b\uff01\u201d\u72c4\u4e91\u4e5f\u662f\u4e00\u60ca\uff0c\u6025\u5fd9\u677e\u624b\uff0c\u6293\u4f4f\u4e86\u9a6c\u978d\u3002\u4f46\u4ed6\u5750\u5728\u6c34\u7b19\u8eab\u540e\uff0c\u4e24\u4eba\u8eab\u5b50\u65e0\u6cd5\u4e0d\u78b0\u5728\u4e00\u8d77\u3002\u6c34\u7b19\u53ea\u53eb\uff1a\u201c\u653e\u5f00\u6211\uff0c\u653e\u5f00\u6211\uff01\u201d\u90a3\u8001\u50e7\u542c\u5f97\u538c\u70e6\uff0c\u4f38\u8fc7\u624b\u6765\u70b9\u4e86\u5979\u54d1\u7a74\uff0c\u8fd9\u4e48\u4e00\u6765\uff0c\u6c34\u7b19\u8fde\u8bdd\u4e5f\u8bf4\u4e0d\u51fa\u6765\u4e86\u3002 \u3000\u3000\u90a3\u8001\u50e7\u9a91\u5728\u9ec4\u9a6c\u80cc\u4e0a\uff0c\u4e0d\u4f4f\u6253\u91cf\u6c34\u7b19\u7684\u8eab\u5f62\u9762\u8c8c\uff0c\u5567\u5567\u79f0\u8d5e\uff1a\u201c\u5f88\u6807\u81f4\uff0c\u4e86\u4e0d\u8d77\uff01\u8001\u548c\u5c1a\u8273\u798f\u4e0d\u6d45\u3002\u201d\u6c34\u7b19\u5634\u5df4\u867d\u54d1\uff0c\u8033\u6735\u5374\u662f\u4e0d\u804b\uff0c\u53ea\u5413\u5f97\u9b42\u98de\u9b44\u6563\uff0c\u5dee\u4e00\u70b9\u4fbf\u6655\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u90a3\u8001\u50e7\u7eb5\u9a6c\u4e00\u8def\u897f\u884c\uff0c\u5c3d\u62e3\u8352\u50fb\u4e4b\u5904\u9a70\u53bb\u3002\u884c\u4e86\u4e00\u7a0b\uff0c\u89c9\u5f97\u4e24\u5339\u5750\u9a91\u7684\u9e3e\u94c3\u4e4b\u58f0\u592a\u8fc7\u523a\u8033\uff0c\u53ee\u5f53\u53ee\u5f53\uff0c\u53ee\u73b2\u73b2\u7684\uff0c\u663e\u7136\u662f\u5f15\u4eba\u6765\u8ffd\uff0c\u5f53\u5373\u4f38\u624b\u51fa\u53bb\uff0c\u5c06\u91d1\u94c3\u3001\u94f6\u94c3\u4e00\u4e2a\u4e2a\u90fd\u6458\u4e86\u4e0b\u6765\u3002\u8fd9\u4e9b\u94c3\u5b50\u662f\u4ee5\u91d1\u4e1d\u94f6\u4e1d\u7cfb\u5728\u9a6c\u9888\uff0c\u987a\u624b\u4e00\u626f\u4fbf\u626f\u4e0b\u4e00\u679a\uff0c\u653e\u5165\u6000\u4e2d\u4e4b\u65f6\uff0c\u6bcf\u53ea\u94c3\u5b50\u90fd\u5df2\u634f\u6241\u6210\u5757\u3002 \u3000\u3000\u90a3\u8001\u50e7\u4e0d\u8ba9\u9a6c\u5339\u4f11\u606f\uff0c\u884c\u5230\u5411\u665a\uff0c\u5230\u4e86\u6c5f\u7554\u5c71\u5761\u4e0a\u4e00\u5904\u60ac\u5d16\u4e4b\u65c1\u3002\u89c1\u5730\u52bf\u8352\u51c9\uff0c\u56db\u4e0b\u91cc\u65e2\u65e0\u884c\u4eba\uff0c\u53c8\u65e0\u623f\u5c4b\uff0c\u5f53\u4e0b\u5c06\u72c4\u4e91\u4ece\u9a6c\u80cc\u62b1\u4e0b\uff0c\u653e\u5728\u5730\u4e0a\uff0c\u53c8\u5c06\u6c34\u7b19\u62b1\u4e86\u4e0b\u6765\uff0c\u518d\u5c06\u4e24\u5339\u9a6c\u7275\u5230\u4e00\u682a\u5927\u6811\u4e4b\u4e0b\uff0c\u7cfb\u5728\u6811\u4e0a\u3002\u4ed6\u5411\u6c34\u7b19\u4e0a\u4e0a\u4e0b\u4e0b\u7684\u6253\u91cf\u7247\u523b\uff0c\u7b11\u563b\u563b\u7684\u9053\uff1a\u201c\u5999\u6781\uff01\u8001\u548c\u5c1a\u8273\u798f\u4e0d\u6d45\uff01\u201d\u8fd9\u624d\u76d8\u819d\u5750\u5b9a\uff0c\u5bf9\u7740\u6c5f\u6c34\u95ed\u76ee\u8fd0\u529f\u3002 \u3000\u3000\u72c4\u4e91\u5750\u5728\u4ed6\u5bf9\u9762\uff0c\u601d\u6f6e\u8d77\u4f0f\uff1a\u201c\u4eca\u65e5\u7684\u906d\u9047\u5f53\u771f\u5947\u602a\u4e4b\u6781\u3002 \u3000\u3000\u4e24\u4e2a\u597d\u4eba\u8981\u6740\u6211\uff0c\u8fd9\u8001\u548c\u5c1a\u5374\u6765\u6551\u4e86\u6211\u3002\u8fd9\u548c\u5c1a\u663e\u7136\u8ddf\u5b9d\u8c61\u662f\u4e00\u8def\uff0c\u51b3\u4e0d\u662f\u597d\u4eba\uff0c\u4ed6\u82e5\u53bb\u4fb5\u72af\u8fd9\u59d1\u5a18\uff0c\u90a3\u4fbf\u5982\u4f55\u662f\u597d\uff1f\u201d \u3000\u3000\u5929\u8272\u6e10\u6e10\u9ed1\u4e86\u4e0b\u6765\uff0c\u8033\u542c\u5f97\u5c71\u95f4\u677e\u98ce\u5982\u6d9b\uff0c\u591c\u9e1f\u557e\u9e23\uff0c\u5076\u4e00\u62ac\u5934\uff0c\u4fbf\u89c1\u5230\u90a3\u8001\u50e7\u72b9\u4f3c\u50f5\u5c38\u4e00\u822c\u7684\u8138\uff0c\u5fc3\u4e2d\u4e0d\u7531\u5f97\u6026\u6026\u4e71\u8df3\uff0c\u659c\u8fc7\u5934\u53bb\uff0c\u89c1\u5230\u8349\u4e1b\u4e2d\u9732\u51fa\u4e00\u89d2\u7d20\u8863\uff0c\u6b63\u662f\u6c34\u7b19\u5012\u5728\u5176\u4e2d\u3002\u4ed6\u51e0\u6b21\u60f3\u5f00\u53e3\u95ee\u90a3\u8001\u50e7\uff0c\u4f46\u89c1\u4ed6\u795e\u8272\u4fe8\u7136\uff0c\u7528\u529f\u6b63\u52e4\uff0c\u603b\u662f\u4e0d\u6562\u51fa\u58f0\u6253\u6270\u3002 \u3000\u3000\u8fc7\u4e86\u826f\u4e45\uff0c\u90a3\u8001\u50e7\u7a81\u7136\u5f90\u5f90\u7ad9\u8d77\uff0c\u5de6\u8db3\u8df7\u8d77\uff0c\u811a\u5e95\u5411\u5929\uff0c\u53f3\u8db3\u7ad9\u5728\u5730\u4e0b\uff0c\u53cc\u624b\u5f20\u5f00\uff0c\u5411\u7740\u5c71\u51f9\u91cc\u521d\u5347\u7684\u4e00\u8f6e\u660e\u6708\u3002\u72c4\u4e91\u5fc3\u60f3\uff1a\u201c\u8fd9\u59ff\u5f0f\u6211\u5728\u54ea\u91cc\u89c1\u8fc7\u7684\uff1f\u662f\u4e86\uff0c\u5b9d\u8c61\u90a3\u672c\u5c0f\u518c\u4e4b\u4e2d\uff0c\u4fbf\u7ed8\u5f97\u6709\u8fd9\u4e2a\u53e4\u602a\u7684\u56fe\u5f62\u3002\u201d\u4f46\u89c1\u90a3\u8001\u50e7\u5982\u6b64\u8fd9\u822c\u7ad9\u7740\uff0c\u7adf\u5982\u4e00\u5ea7\u77f3\u50cf\u4e00\u822c\uff0c\u7edd\u65e0\u534a\u5206\u6447\u6643\u98a4\u6296\u3002\u8fc7\u5f97\u4e00\u4f1a\uff0c\u53ea\u542c\u5f97\u547c\u7684\u4e00\u58f0\uff0c\u90a3\u8001\u50e7\u9661\u7136\u8dc3\u8d77\uff0c\u5012\u8f6c\u4e86\u8eab\u5b50\u843d\u5c06\u4e0b\u6765\uff0c\u53cc\u624b\u5728\u5730\u4e0b\u4e00\u6491\uff0c\u4fbf\u5934\u9876\u7740\u5730\uff0c\u4e24\u624b\u5de6\u53f3\u5e73\u4f38\uff0c\u53cc\u8db3\u5e76\u62e2\uff0c\u671d\u5929\u633a\u7acb\u3002 \u3000\u3000\u72c4\u4e91\u89c9\u5f97\u6709\u8da3\uff0c\u4ece\u6000\u4e2d\u53d6\u51fa\u90a3\u672c\u518c\u5b50\uff0c\u7ffb\u5230\u4e00\u4e2a\u56fe\u5f62\uff0c\u6708\u5149\u4e0b\u770b\u6765\uff0c\u679c\u7136\u4fbf\u548c\u90a3\u8001\u50e7\u6b64\u523b\u7684\u59ff\u5f0f\u4e00\u6a21\u4e00\u6837\uff0c\u5fc3\u4e2d\u7701\u609f\uff1a \u3000\u3000\u201c\u8fd9\u5b9a\u662f\u4ed6\u4eec\u95e8\u4e2d\u7ec3\u529f\u7684\u6cd5\u5b50\u3002\u201d \u3000\u3000\u773c\u89c1\u90a3\u8001\u50e7\u51dd\u795e\u95ed\u76ee\uff0c\u5168\u5fc3\u8d2f\u6ce8\uff0c\u4e00\u4e2a\u4e2a\u59ff\u5f0f\u5c42\u51fa\u4e0d\u7a77\uff0c\u4e00\u65f6\u672a\u5fc5\u4fbf\u80fd\u7ec3\u5b8c\uff0c\u72c4\u4e91\u5c06\u518c\u5b50\u653e\u56de\u6000\u4e2d\uff0c\u5fc3\u60f3\uff1a\u201c\u8fd9\u8001\u50e7\u867d\u7136\u6551\u4e86\u6211\u6027\u547d\uff0c\u4f46\u663e\u662f\u4e2a\u90aa\u6deb\u4e4b\u5f92\uff0c\u4ed6\u63b3\u4e86\u8fd9\u59d1\u5a18\u6765\uff0c\u5206\u660e\u4e0d\u6000\u597d\u610f\u3002\u4e58\u7740\u4ed6\u7ec3\u529f\u5165\u5b9a\u4e4b\u9645\uff0c\u6211\u53bb\u6551\u4e86\u90a3\u59d1\u5a18\uff0c\u4e00\u540c\u4e58\u9a6c\u9003\u8d70\u3002\u201d \u3000\u3000\u4ed6\u660e\u77e5\u6b64\u4e3e\u5341\u5206\u51f6\u9669\uff0c\u53ef\u603b\u4e0d\u5fcd\u89c1\u6c34\u7b19\u597d\u597d\u4e00\u4e2a\u59d1\u5a18\u53d7\u6deb\u50e7\u6b3a\u8fb1\uff0c\u5f53\u4e0b\u6084\u6084\u8f6c\u8eab\uff0c\u8f7b\u624b\u8f7b\u811a\u7684\u5411\u8349\u4e1b\u4e2d\u722c\u53bb\u3002\u4ed6\u5728\u7262\u72f1\u4e2d\u5e38\u548c\u4e01\u5178\u4e00\u9f50\u7ec3\u529f\uff0c\u77e5\u9053\u6bcf\u5f53\u5410\u7eb3\u547c\u5438\u4e4b\u9645\uff0c\u8033\u804b\u76ee\u76f2\uff0c\u4e94\u5b98\u529f\u7528\u9f50\u5931\uff0c\u53ea\u8981\u90a3\u8001\u50e7\u7ec3\u52a8\u4e0d\u8f8d\uff0c\u81ea\u5df1\u6551\u90a3\u59d1\u5a18\uff0c\u4ed6\u5c31\u672a\u5fc5\u77e5\u89c9\u3002 \u3000\u3000\u4ed6\u8eab\u5b50\u4e00\u52a8\uff0c\u65ad\u817f\u5904\u4fbf\u75db\u5f97\u96be\u4ee5\u62b5\u53d7\uff0c\u53ea\u5f97\u5c06\u5168\u8eab\u91cd\u91cf\u90fd\u653e\u5728\u4e00\u53cc\u624b\u4e0a\uff0c\u6162\u6162\u722c\u5230\u8349\u4e1b\u95f4\uff0c\u5e78\u559c\u90a3\u8001\u50e7\u679c\u7136\u5e76\u672a\u77e5\u89c9\u3002\u4f4e\u4e0b\u5934\u6765\uff0c\u53ea\u89c1\u6708\u5149\u6b63\u597d\u7167\u5c04\u5728\u6c34\u7b19\u8138\u4e0a\u3002\u5979\u7741\u7740\u5706\u5706\u7684\u5927\u773c\uff0c\u8138\u4e0a\u9732\u51fa\u6050\u6016\u4e4b\u6781\u7684\u795e\u8272\u3002\u72c4\u4e91\u751f\u6015\u60ca\u52a8\u8001\u50e7\uff0c\u4e0d\u6562\u8bf4\u8bdd\uff0c\u5f53\u4e0b\u6253\u4e2a\u624b\u52bf\uff0c\u793a\u610f\u81ea\u5df1\u524d\u6765\u76f8\u6551\u3002 \u3000\u3000\u6c34\u7b19\u81ea\u88ab\u8001\u50e7\u63b3\u5230\u6b64\u5904\uff0c\u5fc3\u60f3\u843d\u5165\u8fd9\u4e24\u4e2a\u6deb\u50e7\u7684\u9b54\u624b\uff0c\u4ee5\u540e\u53ea\u6015\u6c42\u751f\u4e0d\u80fd\uff0c\u6c42\u6b7b\u4e0d\u5f97\uff0c\u6240\u906d\u7684\u5c48\u8fb1\u4e0d\u77e5\u5c06\u5982\u4f55\u60e8\u9177\uff0c\u82e6\u4e8e\u7a74\u9053\u88ab\u70b9\uff0c\u522b\u8bf4\u65e0\u6cd5\u52a8\u5f39\uff0c\u8fde\u4e00\u53e5\u8bdd\u4e5f\u8bf4\u4e0d\u51fa\u53e3\u3002\u5979\u88ab\u8001\u50e7\u653e\u5728\u8349\u4e1b\u4e4b\u4e2d\uff0c\u8682\u8681\u86b1\u8722\u5728\u5979\u8138\u4e0a\u9888\u4e2d\u722c\u6765\u722c\u53bb\uff0c\u5df2\u662f\u4e07\u5206\u96be\u53d7\uff0c\u8fd9\u65f6\u5ffd\u89c1\u72c4\u4e91\u5077\u5077\u6478\u6478\u7684\u722c\u5c06\u8fc7\u6765\uff0c\u53ea\u9053\u4ed6\u5b9a\u7136\u4e0d\u6000\u597d\u610f\uff0c\u8981\u5bf9\u81ea\u5df1\u975e\u793c\uff0c\u4e0d\u7531\u5f97\u5bb3\u6015\u4e4b\u6781\u3002\u72c4\u4e91\u8fde\u6253\u624b\u52bf\uff0c\u793a\u610f\u6551\u5979\uff0c\u4f46\u6c34\u7b19\u60ca\u6050\u4e4b\u4e2d\uff0c\u5c06\u4ed6\u7684\u624b\u52bf\u90fd\u4f1a\u9519\u4e86\u610f\uff0c\u53ea\u6709\u66f4\u52a0\u5bb3\u6015\u3002 \u3000\u3000\u72c4\u4e91\u4f38\u624b\u62c9\u5979\u5750\u8d77\uff0c\u624b\u6307\u5927\u6811\u8fb9\u7684\u9a6c\u5339\uff0c\u610f\u601d\u8bf4\u8981\u548c\u5979\u4e00\u9f50\u4e0a\u9a6c\u9003\u8d70\u3002\u6c34\u7b19\u5168\u8eab\u8f6f\u8f6f\u7684\u5168\u7136\u505a\u4e0d\u5f97\u4e3b\u3002\u72c4\u4e91\u82e5\u662f\u53cc\u817f\u5065\u597d\uff0c\u4fbf\u80fd\u62b1\u4e86\u5979\u5954\u4e0b\u5761\u53bb\uff0c\u4f46\u4ed6\u65ad\u817f\u540e\u81ea\u5df1\u884c\u8d70\u5140\u81ea\u8270\u96be\uff0c\u65e0\u8bba\u5982\u4f55\u4e0d\u80fd\u518d\u62b1\u4e00\u4eba\uff0c\u552f\u6709\u8bbe\u6cd5\u89e3\u5f00\u5979\u7a74\u9053\uff0c\u8ba9\u5979\u81ea\u884c\u3002\u53ea\u662f\u4ed6\u4e0d\u660e\u70b9\u7a74\u89e3\u7a74\u4e4b\u6cd5\uff0c\u53ea\u5f97\u5411\u6c34\u7b19\u8fde\u6253\u624b\u52bf\uff0c\u6307\u7740\u5979\u8eab\u4e0a\u5404\u5904\u90e8\u4f4d\uff0c\u76fc\u5979\u4ee5\u773c\u8272\u6307\u793a\uff0c\u4f55\u5904\u80fd\u591f\u89e3\u7a74\u3002 \u3000\u3000\u6c34\u7b19\u89c1\u4ed6\u4f38\u624b\u5411\u81ea\u5df1\u5168\u8eab\u5404\u5904\u4e1c\u6307\u897f\u6307\uff0c\u4e0d\u7981\u7f9e\u6124\u5230\u4e86\u6781\u70b9\uff0c\u4e5f\u75db\u6068\u5230\u4e86\u6781\u70b9\uff1a\u201c\u8fd9\u5c0f\u6076\u50e7\u4e0d\u77e5\u60f3\u4e9b\u751a\u4e48\u53e4\u602a\u6cd5\u95e8\uff0c\u8981\u6765\u6298\u8fb1\u4e8e\u6211\u3002\u6211\u53ea\u8981\u8eab\u5b50\u80fd\u52a8\uff0c\u5373\u523b\u4fbf\u5411\u77f3\u58c1\u4e0a\u4e00\u5934\u649e\u6b7b\uff0c\u514d\u53d7\u4ed6\u767e\u7aef\u6b3a\u4fae\u3002\u201d \u3000\u3000\u72c4\u4e91\u89c1\u5979\u795e\u8272\u53e4\u602a\uff0c\u5fc3\u60f3\uff1a\u201c\u591a\u534a\u5979\u4e5f\u662f\u4e0d\u77e5\u3002\u201d\u773c\u524d\u9664\u4e86\u89e3\u5979\u7a74\u9053\u4e4b\u5916\uff0c\u66f4\u65e0\u7b2c\u4e8c\u6761\u8131\u8eab\u9003\u8d70\u4e4b\u9014\uff0c\u53ef\u662f\u8bf4\u751a\u4e48\u4e5f\u4e0d\u6562\u5f00\u53e3\uff0c\u6697\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u6211\u662f\u4e00\u5fc3\u52a9\u4f60\u8131\u9669\uff0c\u5f97\u7f6a\u83ab\u602a\u3002\u201d\u5f53\u4e0b\u4f38\u51fa\u624b\u53bb\uff0c\u5728\u5979\u80cc\u4e0a\u8f7b\u8f7b\u63a8\u62ff\u4e86\u51e0\u63a8\u3002 \u3000\u3000\u8fd9\u8f7b\u8f7b\u51e0\u4e0b\u63a8\u63c9\uff0c\u4e8e\u89e3\u7a74\u81ea\u7136\u6beb\u65e0\u529f\u6548\uff0c\u4f46\u6c34\u7b19\u5fc3\u4e2d\u7684\u60ca\u6050\u5374\u53c8\u589e\u4e86\u51e0\u5206\u3002\u5979\u8868\u54e5\u6c6a\u5578\u98ce\u81ea\u5e7c\u5728\u5979\u5bb6\u8ddf\u5979\u7236\u4eb2\u5b66\u827a\uff0c\u548c\u5979\u9752\u6885\u7af9\u9a6c\uff0c\u60c5\u597d\u5f25\u7b03\uff0c\u7236\u4eb2\u4e5f\u65e9\u8bf4\u8fc7\u5c06\u5979\u8bb8\u914d\u7ed9\u4e86\u8868\u54e5\u3002 \u3000\u3000\u4e24\u4eba\u867d\u65f6\u65f6\u4e00\u8d77\u51fa\u95e8\uff0c\u884c\u4fa0\u6c5f\u6e56\uff0c\u4f46\u4e92\u76f8\u4ee5\u793c\u81ea\u6301\uff0c\u8fde\u624b\u638c\u4e5f\u4ece\u4e0d\u76f8\u89e6\u3002\u72c4\u4e91\u8fd9\u4e48\u63a8\u62ff\u5f97\u51e0\u4e0b\uff0c\u5979\u6cea\u6c34\u5df2\u6251\u7c0c\u7c0c\u7684\u6d41\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u72c4\u4e91\u5fae\u5fae\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u5979\u4e3a\u751a\u4e48\u54ed\u6ce3\uff1f\u55ef\uff0c\u60f3\u5fc5\u5979\u7ed9\u70b9\u7a74\u4e4b\u540e\uff0c\u8fd9\u80cc\u5fc3\u7684\u7a74\u9053\u4e00\u78b0\u5230\u4fbf\u5267\u75db\u96be\u5f53\uff0c\u56e0\u6b64\u54ed\u4e86\u8d77\u6765\u3002\u6211\u8bd5\u8bd5\u89e3\u5979\u8170\u91cc\u7684\u7a74\u9053\u3002\u201d\u4e8e\u662f\u4f38\u624b\u5230\u5979\u540e\u8170\uff0c\u8f7b\u8f7b\u634f\u4e86\u51e0\u4e0b\u3002 \u3000\u3000\u8fd9\u51e0\u4e0b\u4e00\u634f\uff0c\u6c34\u7b19\u7684\u773c\u6cea\u6d41\u5f97\u66f4\u52a0\u591a\u4e86\u3002\u72c4\u4e91\u5927\u4e3a\u60f6\u60d1\uff1a\u201c\u539f\u6765\u8170\u95f4\u7a74\u9053\u4e5f\u75db\uff0c\u90a3\u4fbf\u600e\u751f\u662f\u597d\uff1f\u201d\u4e5f\u77e5\u9053\u5973\u5b50\u8eab\u4e0a\u7684\u5c0a\u4e25\uff0c\u8fd9\u80f8\u9888\u817f\u8179\u7b49\u5904\uff0c\u90a3\u662f\u77a7\u4e5f\u4e0d\u6562\u53bb\u77a7\uff0c\u522b\u8bf4\u53bb\u78b0\u4e86\uff0c\u5bfb\u601d\uff1a \u3000\u3000\u201c\u6211\u6ca1\u6cd5\u5b50\u89e3\u5979\u7a74\u9053\uff0c\u82e5\u518d\u4e71\u8bd5\uff0c\u90a3\u53ef\u4f7f\u4e0d\u5f97\u3002\u53ea\u6709\u80cc\u8d1f\u5979\u4e0b\u5761\uff0c\u5192\u9669\u9003\u8d70\u3002\u201d\u4e8e\u662f\u63e1\u7740\u5979\u7684\u53cc\u81c2\uff0c\u8981\u5c06\u5979\u8eab\u5b50\u62c9\u5230\u81ea\u5df1\u80cc\u4e0a\u3002 \u3000\u3000\u6c34\u7b19\u6c14\u82e6\u5df2\u6781\uff0c\u60ca\u6012\u4e4b\u4e0b\uff0c\u6570\u6b21\u9669\u6b32\u6655\u53bb\uff0c\u89c1\u4ed6\u63d0\u8d77\u81ea\u5df1\u624b\u81c2\uff0c\u663e\u662f\u8981\u6765\u89e3\u81ea\u5df1\u8863\u886b\uff0c\u4e00\u53e3\u6c14\u585e\u5728\u80f8\u95f4\uff0c\u547c\u4e0d\u51fa\u53bb\u3002 \u3000\u3000\u72c4\u4e91\u5c06\u5979\u53cc\u81c2\u4e00\u63d0\uff0c\u6b63\u8981\u62c9\u8d77\u5979\u8eab\u5b50\uff0c\u6c34\u7b19\u80f8\u53e3\u8fd9\u80a1\u6c14\u4e00\u51b2\uff0c\u54d1\u7a74\u7a81\u7136\u89e3\u4e86\uff0c\u5f53\u5373\u53eb\u5524\uff1a\u201c\u6076\u8d3c\uff0c\u653e\u5f00\u6211\uff01\u522b\u78b0\u6211\uff0c\u653e\u5f00\u6211\uff01\u201d \u3000\u3000\u8fd9\u4e00\u4e0b\u547c\u53eb\u7a81\u5982\u5176\u6765\uff0c\u72c4\u4e91\u5927\u5403\u4e00\u60ca\uff0c\u53cc\u624b\u4e00\u677e\uff0c\u5c06\u5979\u6454\u5728\u5730\u4e0b\uff0c\u81ea\u5df1\u7ad9\u7acb\u4e0d\u7a33\uff0c\u4e00\u6454\u4e4b\u4e0b\uff0c\u538b\u5728\u5979\u7684\u8eab\u4e0a\u3002 \u3000\u3000\u6c34\u7b19\u8fd9\u4e48\u4e00\u53eb\uff0c\u90a3\u8001\u50e7\u7acb\u65f6\u9192\u89c9\uff0c\u7741\u5f00\u773c\u6765\uff0c\u89c1\u4e24\u4eba\u6eda\u4f5c\u4e00\u56e2\uff0c\u53c8\u542c\u6c34\u7b19\u5927\u53eb\uff1a\u201c\u6076\u50e7\uff0c\u4f60\u5feb\u4e00\u5200\u5c06\u59d1\u5a18\u6740\u4e86\uff0c\u653e\u5f00\u6211\u3002\u201d\u90a3\u8001\u50e7\u54c8\u54c8\u5927\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u6df7\u86cb\uff0c\u4f60\u6027\u6025\u751a\u4e48\uff1f\u4f60\u60f3\u5148\u5077\u5403\u5e08\u7956\u7684\u59d1\u5a18\u4e48\uff1f\u201d\u8d70\u4e0a\u524d\u6765\uff0c\u4e00\u628a\u6293\u4f4f\u72c4\u4e91\u7684\u80cc\u5fc3\uff0c\u5c06\u4ed6\u63d0\u4e86\u8d77\u6765\uff0c\u8d70\u8fdc\u51e0\u6b65\uff0c\u624d\u5c06\u4ed6\u653e\u4e0b\uff0c\u7b11\u9053\uff1a\u201c\u5f88\u597d\uff0c\u5f88\u597d\uff01 \u3000\u3000\u6211\u5c31\u559c\u6b22\u4f60\u8fd9\u79cd\u5927\u80c6\u8d2a\u82b1\u7684\u5c11\u5e74\uff0c\u4f60\u65ad\u4e86\u4e00\u6761\u817f\uff0c\u5c45\u7136\u4e0d\u6015\u75db\uff0c\u8fd8\u60f3\u5973\u4eba\uff0c\u5999\u6781\uff0c\u5999\u6781\uff0c\u6709\u79cd\uff01\u5f88\u5408\u6211\u7684\u813e\u80c3\u3002\u201d \u3000\u3000\u72c4\u4e91\u88ab\u4ed6\u4e8c\u4eba\u8bef\u4f1a\uff0c\u5f53\u771f\u662f\u54ed\u7b11\u4e0d\u5f97\uff0c\u5fc3\u60f3\uff1a\u201c\u6211\u82e5\u8bf4\u660e\u771f\u76f8\uff0c\u8fd9\u6076\u50e7\u4e00\u638c\u4fbf\u9001\u4e86\u6211\u7684\u6027\u547d\u3002\u53ea\u597d\u6682\u4e14\u6577\u884d\uff0c\u518d\u60f3\u6cd5\u5b50\u8131\u8eab\uff0c\u540c\u65f6\u642d\u6551\u8fd9\u59d1\u5a18\u3002\u201d \u3000\u3000\u90a3\u8001\u50e7\u9053\uff1a\u201c\u4f60\u662f\u5b9d\u8c61\u65b0\u6536\u7684\u5f1f\u5b50\uff0c\u662f\u4e0d\u662f\uff1f\u201d\u4e0d\u7b49\u72c4\u4e91\u56de\u7b54\uff0c\u54a7\u5634\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5b9d\u8c61\u4e00\u5b9a\u5f88\u559c\u6b22\u4f60\u4e86\uff0c\u8fde\u4ed6\u7684\u8840\u5200\u50e7\u8863\u4e5f\u8d50\u4e86\u7ed9\u4f60\uff0c\u4ed6\u90a3\u90e8\u300a\u8840\u5200\u79d8\u7b08\u300b\u6709\u6ca1\u4f20\u7ed9\u4f60\uff1f\u201d \u3000\u3000\u72c4\u4e91\u5fc3\u60f3\uff1a\u201c\u300a\u8840\u5200\u79d8\u7b08\u300b\u4e0d\u77e5\u662f\u751a\u4e48\u4e1c\u897f\uff1f\u201d\u98a4\u6296\u7740\u4f38\u624b\u5165\u6000\uff0c\u53d6\u51fa\u90a3\u672c\u9ec4\u7eb8\u518c\u5b50\u3002 \u3000\u3000\u90a3\u8001\u50e7\u63a5\u8fc7\u6765\u7ffb\u9605\u4e00\u904d\uff0c\u53c8\u8fd8\u4e86\u7ed9\u4ed6\uff0c\u8f7b\u62cd\u4ed6\u5934\u9876\uff0c\u8bf4\u9053\uff1a\u201c\u5f88\u597d\uff0c\u5f88\u597d\uff01\u4f60\u53eb\u751a\u4e48\u540d\u5b57\uff1f\u201d\u72c4\u4e91\u9053\uff1a\u201c\u6211\u53eb\u72c4\u4e91\u3002\u201d\u90a3\u8001\u50e7\u9053\uff1a\u201c\u5f88\u597d\uff0c\u5f88\u597d\uff01\u4f60\u5e08\u7236\u4f20\u8fc7\u4f60\u7ec3\u529f\u7684\u6cd5\u95e8\u6ca1\u6709\uff1f\u201d\u72c4\u4e91\u9053\uff1a\u201c\u6ca1\u6709\u3002\u201d\u90a3\u8001\u50e7\u9053\uff1a\u201c\u55ef\uff0c\u4e0d\u8981\u7d27\u3002\u4f60\u5e08\u7236\u54ea\u91cc\u53bb\u4e86\uff1f\u201d \u3000\u3000\u72c4\u4e91\u54ea\u6562\u8bf4\u5b9d\u8c61\u4e0d\u662f\u81ea\u5df1\u5e08\u7236\uff0c\u800c\u4e14\u65e9\u5df2\u6b7b\u4e86\uff0c\u53ea\u5f97\u968f\u53e3\u9053\uff1a\u201c\u4ed6\u2026\u2026\u4ed6\u5728\u6c5f\u91cc\u4e58\u8239\u3002\u201d \u3000\u3000\u90a3\u8001\u50e7\u9053\uff1a\u201c\u4f60\u5e08\u7236\u8ddf\u4f60\u8bf4\u8fc7\u5e08\u7956\u7684\u6cd5\u540d\u6ca1\u6709\uff1f\u201d\u72c4\u4e91\u9053\uff1a\u201c\u6ca1\u6709\u3002\u201d\u90a3\u8001\u50e7\u9053\uff1a\u201c\u6211\u6cd5\u540d\u4fbf\u53eb\u505a\u2018\u8840\u5200\u8001\u7956\u2019\u3002\u4f60\u8fd9\u5c0f\u6df7\u86cb\u5f88\u80fd\u8ba8\u6211\u6b22\u559c\u3002\u4f60\u8ddf\u7740\u5e08\u7956\u7237\u7237\uff0c\u5305\u7ba1\u4f60\u4eab\u798f\u65e0\u7a77\uff0c\u5929\u4e0b\u7684\u7f8e\u8c8c\u4f73\u4eba\u54ea\uff0c\u8981\u54ea\u4e00\u4e2a\u4fbf\u53d6\u54ea\u4e00\u4e2a\u3002\u201d \u3000\u3000\u72c4\u4e91\u5fc3\u60f3\uff1a\u201c\u539f\u6765\u4ed6\u662f\u5b9d\u8c61\u7684\u5e08\u7236\u3002\u201d\u95ee\u9053\uff1a\u201c\u4ed6\u4eec\u9a82\u4f60\u2026\u2026\u9a82\u54b1\u4eec\u662f\u2018\u8840\u5200\u6076\u50e7\u2019\uff0c\u5e08\u2026\u2026\u5e08\u7956\u662f\u54b1\u4eec\u8fd9\u4e00\u6d3e\u7684\u638c\u6559\u4e86\uff1f\u201d \u3000\u3000\u8840\u5200\u8001\u7956\u7b11\u9053\uff1a\u201c\u563f\u563f\uff0c\u5b9d\u8c61\u8fd9\u6df7\u86cb\u7684\u53e3\u98ce\u4e5f\u771f\u7d27\uff0c\u5bb6\u95e8\u6765\u5386\uff0c\u8fde\u81ea\u5df1\u5fc3\u7231\u7684\u5f92\u513f\u4e5f\u4e0d\u7ed9\u8bf4\uff0c\u54b1\u4eec\u8fd9\u4e00\u6d3e\u662f\u897f\u85cf\u9752\u6559\u4e2d\u7684\u4e00\u652f\uff0c\u53eb\u505a\u8840\u5200\u95e8\u3002\u4f60\u7956\u5e08\u662f\u8fd9\u4e00\u95e8\u7684\u7b2c\u56db\u4ee3\u638c\u6559\u3002\u4f60\u597d\u597d\u513f\u5b66\u529f\u592b\uff0c\u7b2c\u516d\u4ee3\u638c\u6559\u8bf4\u4e0d\u5b9a\u4fbf\u80fd\u843d\u5728\u4f60\u7684\u8eab\u4e0a\u3002\u55ef\uff0c\u4f60\u7684\u817f\u65ad\u4e86\uff0c\u4e0d\u8981\u7d27\uff0c\u6211\u7ed9\u4f60\u6cbb\u6cbb\u3002\u201d \u3000\u3000\u4ed6\u89e3\u5f00\u72c4\u4e91\u53cc\u817f\u7684\u4f24\u5904\uff0c\u5c06\u65ad\u9aa8\u5bf9\u51c6\uff0c\u4ece\u6000\u4e2d\u53d6\u51fa\u4e00\u4e2a\u74f7\u74f6\uff0c\u5012\u51fa\u4e9b\u836f\u672b\uff0c\u6577\u5728\u4f24\u5904\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u672c\u95e8\u79d8\u5236\u7684\u63a5\u9aa8\u4f24\u836f\uff0c\u7075\u9a8c\u65e0\u6bd4\uff0c\u4e0d\u5230\u4e00\u4e2a\u6708\uff0c\u65ad\u817f\u4fbf\u5e73\u590d\u5982\u5e38\u3002\u54b1\u4eec\u660e\u513f\u4e0a\u8346\u5dde\u5e9c\u53bb\uff0c\u4f60\u5e08\u7236\u4e5f\u6765\u4f1a\u9f50\u3002\u201d\u72c4\u4e91\u5fc3\u4e2d\u4e00\u60ca\uff1a\u201c\u8346\u5dde\u6211\u53ef\u53bb\u4e0d\u5f97\u3002\u201d \u3000\u3000\u8840\u5200\u8001\u7956\u5305\u597d\u72c4\u4e91\u7684\u4f24\u817f\uff0c\u56de\u5934\u5411\u6c34\u7b19\u77a7\u77a7\uff0c\u7b11\u9053\uff1a\u201c\u5c0f\u6df7\u86cb\uff0c\u8fd9\u599e\u513f\u76f8\u8c8c\u633a\u7f8e\uff0c\u4e0d\u574f\uff0c\u5f53\u771f\u4e0d\u574f\u3002\u5979\u81ea\u79f0\u751a\u4e48\u2018\u94c3\u5251\u53cc\u4fa0\u2019\u3002\u5979\u8001\u5b50\u6c34\u5cb1\u81ea\u5c45\u540d\u95e8\u6b63\u6d3e\uff0c\u8bf4\u662f\u4e2d\u539f\u6b66\u6797\u4e2d\u7684\u9876\u513f\u5c16\u513f\u4eba\u7269\uff0c\u4e0d\u81ea\u91cf\u529b\u7684\u8981\u8ddf\u54b1\u4eec\u2018\u8840\u5200\u95e8\u2019\u4e3a\u96be\uff0c\u6628\u5929\u7adf\u6740\u4e86\u4f60\u4e00\u4e2a\u5e08\u53d4\uff0c\u4ed6\u5976\u5976\u7684\uff0c\u60f3\u4e0d\u5230\u4ed6\u7684\u5927\u95fa\u5973\u5374\u7ed9\u6211\u624b\u5230\u64d2\u6765\u3002\u563f\u563f\u563f\uff0c\u54b1\u7237\u513f\u4fe9\u8981\u6559\u5979\u8001\u5b50\u4e22\u5c3d\u8138\u9762\uff0c\u5265\u5149\u4e86\u8fd9\u599e\u513f\u8863\u886b\uff0c\u7f1a\u5728\u9a6c\u4e0a\uff0c\u8d76\u7740\u5979\u5728\u4e00\u5904\u5904\u5927\u57ce\u5c0f\u9547\u6e38\u8857\uff0c\u6559\u5343\u4eba\u4e07\u4eba\u90fd\u770b\u4e2a\u660e\u767d\uff0c\u6c34\u5927\u4fa0\u7684\u95fa\u5973\u662f\u8fd9\u4e48\u4e00\u526f\u6a21\u6837\u3002\u201d \u3000\u3000\u6c34\u7b19\u5fc3\u4e2d\u6026\u6026\u4e71\u8df3\uff0c\u5413\u5f97\u53ea\u60f3\u5455\u5410\uff0c\u4e0d\u4f4f\u8f6c\u5ff5\uff1a\u201c\u90a3\u5c0f\u7684\u6076\u50e7\u56fa\u6076\uff0c\u8fd9\u8001\u7684\u66f4\u662f\u51f6\u66b4\uff0c\u6211\u600e\u6837\u624d\u80fd\u56fe\u4e2a\u81ea\u5c3d\uff0c\u4fdd\u4f4f\u6211\u8eaf\u4f53\u6e05\u767d\u548c\u6211\u7239\u7239\u7684\u989c\u9762\uff1f\u201d \u3000\u3000\u5ffd\u542c\u5f97\u8840\u5200\u8001\u7956\u7b11\u9053\uff1a\u201c\u8bf4\u8d77\u66f9\u64cd\uff0c\u66f9\u64cd\u4fbf\u5230\uff0c\u6551\u5979\u7684\u4eba\u6765\u5566\uff01\u201d\u72c4\u4e91\u5fc3\u4e2d\u4e00\u559c\uff0c\u5fd9\u95ee\uff1a\u201c\u5728\u54ea\u91cc\uff1f\u201d\u8840\u5200\u8001\u7956\u9053\uff1a\u201c\u8fd8\u5728\u4e94\u91cc\u4e4b\u5916\uff0c\u563f\u563f\uff0c\u4e00\u5171\u6709\u5341\u4e03\u9a91\u3002\u201d\u72c4\u4e91\u4fa7\u8033\u503e\u542c\uff0c\u9690\u9690\u542c\u5230\u4e1c\u5357\u65b9\u5c71\u9053\u4e0a\u6709\u9a6c\u8e44\u4e4b\u58f0\uff0c\u4f46\u76f8\u8ddd\u751a\u8fdc\uff0c\u8fde\u8e44\u58f0\u4e5f\u662f\u82e5\u6709\u82e5\u65e0\uff0c\u7edd\u96be\u5206\u8fa8\u591a\u5be1\uff0c\u8fd9\u8001\u50e7\u4e00\u542c\uff0c\u4fbf\u77e5\u6765\u9a91\u6570\u76ee\uff0c\u8033\u529b\u5b9e\u5728\u60ca\u4eba\u3002 \u3000\u3000\u8840\u5200\u8001\u7956\u9053\uff1a\u201c\u4f60\u7684\u65ad\u817f\u521a\u6577\u4e0a\u836f\uff0c\u4e09\u4e2a\u65f6\u8fb0\u5185\u4e0d\u80fd\u79fb\u52a8\uff0c\u5426\u5219\u4eca\u540e\u4fbf\u4f1a\u8ddb\u4e86\u3002\u8fd9\u4e00\u4e8c\u767e\u91cc\u5185\uff0c\u6ca1\u542c\u8bf4\u6709\u751a\u4e48\u5927\u672c\u9886\u4e4b\u4eba\uff0c\u8fd9\u4e00\u5341\u4e03\u9a91\u8ffd\u5175\uff0c\u6211\u90fd\u53bb\u6740\u4e86\u7f62\u3002\u201d \u3000\u3000\u72c4\u4e91\u4e0d\u613f\u4ed6\u591a\u4f24\u6b66\u6797\u4e2d\u7684\u6b63\u6d3e\u4eba\u7269\uff0c\u5fd9\u9053\uff1a\u201c\u54b1\u4eec\u8eb2\u5728\u8fd9\u91cc\u4e0d\u51fa\u58f0\uff0c\u4ed6\u4eec\u672a\u5fc5\u5bfb\u5f97\u7740\u3002\u654c\u4f17\u6211\u5be1\uff0c\u5e08\u2026\u2026\u5e08\u7956\u8fd8\u662f\u5c0f\u5fc3\u4e9b\u7684\u597d\u3002\u201d \u3000\u3000\u8840\u5200\u8001\u7956\u5927\u662f\u9ad8\u5174\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u6df7\u86cb\u826f\u5fc3\u597d\uff0c\u96be\u5f97\u96be\u5f97\uff0c\u5e08\u7956\u7237\u7237\u5f88\u6b22\u559c\u4f60\u3002\u201d\u4f38\u624b\u8170\u95f4\uff0c\u4e00\u6296\u4e4b\u4e0b\uff0c\u624b\u4e2d\u5df2\u591a\u4e86\u4e00\u67c4\u8f6f\u8f6f\u7684\u7f05\u5200\u3002\u5200\u8eab\u4e0d\u4f4f\u98a4\u52a8\uff0c\u5b9b\u7136\u662f\u4e00\u6761\u6d3b\u7684\u86c7\u4e00\u822c\u3002\u6708\u5149\u4e4b\u4e0b\uff0c\u4f46\u89c1\u8fd9\u5200\u7684\u5203\u950b\u4e0a\u5168\u662f\u6697\u7ea2\u4e4b\u8272\uff0c\u8840\u5149\u9690\u9690\uff0c\u6781\u662f\u53ef\u6016\u3002\u72c4\u4e91\u4e0d\u81ea\u7981\u7684\u6253\u4e86\u4e2a\u5bd2\u5664\uff0c\u9053\uff1a\u201c\u8fd9\u2026\u2026\u8fd9\u4fbf\u662f\u8840\u5200\u4e86\uff1f\u201d \u3000\u3000\u8840\u5200\u8001\u7956\u9053\uff1a\u201c\u8fd9\u67c4\u5b9d\u5200\u6bcf\u9022\u6708\u5706\u4e4b\u591c\uff0c\u987b\u5272\u4eba\u5934\u76f8\u796d\uff0c\u5426\u5219\u950b\u9510\u4fbf\u51cf\uff0c\u4e8e\u5200\u4e3b\u4e0d\u5229\u3002\u4f60\u77a7\u6708\u4eae\u6b63\u5706\uff0c\u96be\u5f97\u4e00\u5341\u4e03\u4e2a\u4eba\u8d76\u6765\u7ed9\u6211\u796d\u5200\u3002\u5b9d\u5200\u554a\u5b9d\u5200\uff0c\u4eca\u665a\u4f60\u53ef\u4ee5\u9971\u9910\u4e00\u987f\u4eba\u8840\u4e86\u3002\u201d \u3000\u3000\u6c34\u7b19\u542c\u5f97\u9a6c\u8e44\u58f0\u6e10\u6e10\u5954\u8fd1\uff0c\u5fc3\u4e0b\u6697\u559c\uff0c\u4f46\u542c\u8840\u5200\u8001\u50e7\u8bf4\u5f97\u5341\u5206\u81ea\u8d1f\uff0c\u4f3c\u4e4e\u6765\u8005\u5fc5\u6b7b\uff0c\u867d\u4e0d\u80fd\u5168\u4fe1\uff0c\u5374\u4e5f\u6697\u81ea\u62c5\u5fe7\uff0c\u5fc3\u60f3\uff1a\u201c\u7239\u7239\u6765\u4e86\u6ca1\u6709\uff1f\u8868\u54e5\u6765\u4e86\u6ca1\u6709\uff1f\u201d \u3000\u3000\u53c8\u8fc7\u4e00\u4f1a\uff0c\u6708\u5149\u4e0b\u89c1\u5230\u4e00\u5217\u9a6c\u4ece\u5c71\u9053\u4e0a\u5954\u6765\uff0c\u72c4\u4e91\u4e00\u6570\uff0c\u679c\u7136\u4e0d\u591a\u4e0d\u5c11\u662f\u4e00\u5341\u4e03\u9a91\u3002\u4f46\u89c1\u8fd9\u5341\u4e03\u9a91\u8854\u5c3e\u6025\u5954\uff0c\u8fc5\u5373\u7ecf\u8fc7\u5761\u4e0b\u5c71\u9053\uff0c\u9a6c\u4e0a\u4e58\u8005\u5e76\u6ca1\u60f3\u5230\u8981\u4e0a\u6765\u67e5\u5bdf\u3002 \u3000\u3000\u6c34\u7b19\u63d0\u9ad8\u55d3\u5b50\uff0c\u53eb\u9053\uff1a\u201c\u6211\u5728\u8fd9\u91cc\uff0c\u6211\u5728\u8fd9\u91cc\uff01\u201d\u90a3\u4e00\u5341\u4e03\u9a91\u4e58\u5ba2\u542c\u5230\u58f0\u97f3\uff0c\u7acb\u65f6\u52d2\u9a6c\u8f6c\u5934\u3002\u4e00\u4e2a\u7537\u5b50\u5927\u58f0\u547c\u9053\uff1a\u201c\u8868\u59b9\uff0c\u8868\u59b9\uff01\u201d\u6b63\u662f\u6c6a\u5578\u98ce\u7684\u58f0\u97f3\u3002\u6c34\u7b19\u5f85\u8981\u518d\u51fa\u58f0\u62db\u547c\uff0c\u8840\u5200\u8001\u7956\u4f38\u6307\u4e00\u5f39\uff0c\u4e00\u7c92\u77f3\u5757\u98de\u5c06\u8fc7\u53bb\uff0c\u53c8\u6253\u4e2d\u4e86\u5979\u54d1\u7a74\u3002 \u3000\u3000\u4e00\u5341\u4e03\u4eba\u7eb7\u7eb7\u4e0b\u9a6c\uff0c\u805a\u5728\u4e00\u8d77\u4f4e\u58f0\u5546\u8bae\u3002\u8840\u5200\u8001\u7956\u7a81\u7136\u4f38\u624b\u5728\u72c4\u4e91\u814b\u4e0b\u4e00\u6258\uff0c\u5c06\u4ed6\u8eab\u5b50\u6258\u5c06\u8d77\u6765\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u897f\u85cf\u9752\u6559\u8840\u5200\u95e8\uff0c\u7b2c\u56db\u4ee3\u638c\u95e8\u8840\u5200\u8001\u7956\uff0c\u7b2c\u516d\u4ee3\u5f1f\u5b50\u72c4\u4e91\u5728\u6b64\uff01\u201d \u3000\u3000\u8ddf\u7740\u4fef\u8eab\uff0c\u5de6\u624b\u6293\u4f4f\u6c34\u7b19\u9888\u540e\u8863\u670d\uff0c\u5c06\u5979\u63d0\u4e86\u8d77\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u6c34\u5cb1\u7684\u95fa\u5973\uff0c\u5df2\u505a\u4e86\u6211\u5f92\u5b59\u72c4\u4e91\u7b2c\u5341\u516b\u623f\u5c0f\u59be\uff0c\u8c01\u8981\u6765\u559d\u559c\u9152\uff0c\u8fd9\u5c31\u4e0a\u6765\u7f62\u3002\u54c8\u54c8\uff0c\u54c8\u54c8\uff01\u201d\u4ed6\u6709\u610f\u663e\u793a\u6df1\u539a\u5185\u529f\uff0c\u7b11\u58f0\u9707\u64bc\u5c71\u8c37\uff0c\u8fdc\u8fdc\u7684\u4f20\u9001\u51fa\u53bb\u3002\u90a3\u4e00\u5341\u4e03\u4eba\u76f8\u987e\u9a87\u7136\uff0c\u5c3d\u7686\u5931\u8272\u3002 \u3000\u3000\u6c6a\u5578\u98ce\u89c1\u8868\u59b9\u88ab\u6076\u50e7\u63d0\u5728\u624b\u4e2d\uff0c\u5168\u65e0\u6297\u62d2\u4e4b\u529b\uff0c\u53c8\u8bf4\u751a\u4e48\u505a\u4e86\u4ed6\u201c\u5f92\u5b59\u72c4\u4e91\u7684\u7b2c\u5341\u516b\u623f\u5c0f\u59be\u201d\uff0c\u53ea\u6015\u5979\u5df2\u906d\u6c61\u8fb1\uff0c\u53ea\u6c14\u5f97\u4e94\u5185\u4ff1\u711a\uff0c\u5927\u543c\u4e00\u58f0\uff0c\u633a\u7740\u957f\u5251\uff0c\u62a2\u5148\u5411\u5c71\u5761\u4e0a\u5954\u6765\u3002\u5176\u4f59\u5341\u516d\u4eba\u7eb7\u7eb7\u5450\u558a\uff1a\u201c\u6740\u4e86\u8840\u5200\u6076\u50e7\uff01\u201d\u201c\u4e3a\u6c5f\u6e56\u4e0a\u9664\u4e00\u5927\u5bb3\uff01\u201d \u3000\u3000\u201c\u8fd9\u7b49\u51f6\u6b8b\u6deb\u50e7\uff0c\u51b3\u8ba1\u5bb9\u4ed6\u4e0d\u5f97\u3002\u201d \u3000\u3000\u72c4\u4e91\u89c1\u4e86\u8fd9\u7b49\u9635\u4ed7\uff0c\u5fc3\u4e2d\u597d\u751f\u5c34\u5c2c\uff0c\u5bfb\u601d\uff1a\u201c\u8fd9\u4e9b\u4eba\u90fd\u5f53\u6211\u662f\u8840\u5200\u95e8\u7684\u6076\u50e7\uff0c\u6211\u4fbf\u6709\u4e00\u767e\u5f20\u5634\uff0c\u4e5f\u662f\u5206\u8fa9\u4e0d\u5f97\u3002\u6700\u597d\u4ed6\u4eec\u6253\u6b7b\u4e86\u8fd9\u8001\u548c\u5c1a\uff0c\u5c06\u6c34\u59d1\u5a18\u6551\u51fa\u2026\u2026\u53ef\u662f\u2026\u2026\u53ef\u662f\u8fd9\u8001\u548c\u5c1a\u4e00\u6b7b\uff0c\u6211\u4e5f\u96be\u4ee5\u6d3b\u547d\u3002\u201d\u4e00\u65f6\u76fc\u4e2d\u539f\u7fa4\u4fa0\u5f97\u80dc\uff0c\u4e00\u65f6\u53c8\u671b\u8840\u5200\u8001\u7956\u6253\u9000\u8ffd\u5175\uff0c\u81ea\u5df1\u4e5f\u4e0d\u77e5\u5230\u5e95\u5e2e\u7684\u662f\u54ea\u4e00\u8fb9\u3002 \u3000\u3000\u659c\u773c\u5411\u8840\u5200\u8001\u7956\u77a7\u53bb\uff0c\u53ea\u89c1\u4ed6\u5fae\u5fae\u51b7\u7b11\uff0c\u6d51\u4e0d\u4ee5\u654c\u65b9\u4eba\u591a\u52bf\u4f17\u4e3a\u5fcc\uff0c\u53cc\u624b\u5404\u63d0\u4e00\u4eba\uff0c\u4e00\u67c4\u8840\u5200\u54ac\u5728\u5634\u91cc\uff0c\u66f4\u663e\u5f97\u72f0\u72de\u51f6\u6076\u3002\u5f85\u5f97\u7fa4\u4f17\u5954\u5230\u4e8c\u5341\u4f59\u4e08\u4e4b\u5916\uff0c\u4ed6\u7f13\u7f13\u5c06\u72c4\u4e91\u653e\u4e0b\uff0c\u5c0f\u5fc3\u4e0d\u78b0\u52a8\u4ed6\u7684\u4f24\u817f\uff0c\u7b49\u7fa4\u8c6a\u5954\u5230\u5341\u4f59\u4e08\u5916\uff0c\u4ed6\u53c8\u5c06\u6c34\u7b19\u653e\u5728\u72c4\u4e91\u8eab\u65c1\uff0c\u4e00\u67c4\u5200\u4ecd\u662f\u54ac\u5728\u5634\u91cc\uff0c\u53cc\u624b\u53c9\u8170\uff0c\u591c\u98ce\u730e\u730e\uff0c\u9f13\u52a8\u5bbd\u5927\u7684\u888d\u8896\u3002 \u3000\u3000\u6c6a\u5578\u98ce\u53eb\u9053\uff1a\u201c\u8868\u59b9\uff0c\u4f60\u5b89\u597d\u4e48\uff1f\u201d\u6c34\u7b19\u53ea\u60f3\u5927\u53eb\uff1a\u201c\u8868\u54e5\uff0c\u8868\u54e5\uff01\u201d\u5374\u54ea\u91cc\u53eb\u5f97\u51fa\u58f0\uff1f\u4f46\u89c1\u8868\u54e5\u8d8a\u5954\u8d8a\u8fd1\uff0c\u5979\u5fc3\u4e2d\u6df7\u548c\u7740\u65e0\u5c3d\u559c\u60a6\u3001\u62c5\u5fe7\u3001\u4f9d\u604b\u548c\u611f\u6fc0\uff0c\u53ea\u60f3\u6251\u5165\u4ed6\u7684\u6000\u4e2d\u75db\u54ed\u4e00\u573a\uff0c\u8bc9\u8bf4\u8fd9\u51e0\u4e2a\u65f6\u8fb0\u4e2d\u6240\u906d\u9047\u7684\u82e6\u96be\u548c\u5c48\u8fb1\u3002 \u3000\u3000\u6c6a\u5578\u98ce\u4e00\u610f\u53ea\u5728\u627e\u5bfb\u8868\u59b9\uff0c\u4e1c\u5f20\u897f\u671b\uff0c\u5954\u8dd1\u5f97\u4fbf\u6162\u4e86\u51e0\u6b65\uff0c\u7fa4\u8c6a\u4e2d\u6709\u4e03\u516b\u4eba\u5954\u5728\u4ed6\u7684\u524d\u9762\u3002\u6708\u5149\u4e4b\u4e0b\uff0c\u4f46\u89c1\u5c71\u5761\u6700\u9ad8\u5904\u8840\u5200\u8001\u7956\u8854\u5200\u800c\u7acb\uff0c\u51db\u7136\u751f\u5a01\uff0c\u7fa4\u8c6a\u5954\u5230\u79bb\u4ed6\u4e94\u516d\u4e08\u65f6\uff0c\u4e0d\u7ea6\u800c\u540c\u7684\u7acb\u5b9a\u4e86\u811a\u6b65\u3002 \u3000\u3000\u53cc\u65b9\u76f8\u5bf9\u7247\u523b\uff0c\u731b\u542c\u5f97\u4e00\u58f0\u547c\u559d\uff0c\u4e24\u6761\u6c49\u5b50\u5e76\u80a9\u51b2\u4e0a\u5761\u53bb\uff0c\u4e00\u4f7f\u91d1\u97ad\u3001\u4e00\u4f7f\u53cc\u5200\u3002 \u3000\u3000\u4e24\u4eba\u51b2\u4e0a\u6570\u4e08\uff0c\u90a3\u4f7f\u53cc\u5200\u7684\u811a\u6b65\u5feb\u6377\uff0c\u5df2\u7ed5\u5230\u4e86\u8840\u5200\u8001\u7956\u8eab\u540e\uff0c\u4e24\u4eba\u4e00\u524d\u4e00\u540e\uff0c\u5927\u58f0\u547c\u559d\uff0c\u540c\u65f6\u653b\u4e0a\u3002\u8840\u5200\u8001\u7956\u7565\u4e00\u4fa7\u8eab\uff0c\u907f\u8fc7\u53cc\u5200\uff0c\u8eab\u5b50\u5de6\u53f3\u95ea\u52a8\uff0c\u4e00\u628a\u5f2f\u5200\u59cb\u7ec8\u8854\u5728\u5634\u91cc\uff0c\u7a81\u7136\u95f4\u5de6\u624b\u6293\u4f4f\u5200\u67c4\uff0c\u987a\u624b\u4e00\u6325\uff0c\u5df2\u5c06\u90a3\u4f7f\u91d1\u97ad\u7684\u5288\u53bb\u534a\u4e2a\u5934\u9885\uff0c\u6740\u4e86\u4e00\u4eba\u4e4b\u540e\uff0c\u7acb\u65f6\u53c8\u8854\u5200\u5728\u53e3\u3002\u90a3\u4f7f\u53cc\u5200\u7684\u53c8\u60ca\u53c8\u60b2\uff0c\u5c06\u4e00\u5bf9\u957f\u5200\u821e\u5f97\u96ea\u82b1\u76f8\u4f3c\uff0c\u6eda\u52a8\u800c\u524d\u3002\u8840\u5200\u8001\u7956\u7a7a\u624b\u5728\u4ed6\u5200\u5149\u4e2d\u7a7f\u6765\u63d2\u53bb\uff0c\u84e6\u5730\u91cc\u53f3\u624b\u4ece\u53e3\u4e2d\u62bd\u51fa\u5200\u6765\uff0c\u4e00\u6325\u4e4b\u4e0b\uff0c\u5200\u950b\u4ece\u4ed6\u5934\u9876\u76f4\u5288\u81f3\u8170\u3002 \u3000\u3000\u7fa4\u8c6a\u9f50\u58f0\u60ca\u547c\uff0c\u5411\u540e\u9000\u4e86\u51e0\u6b65\uff0c\u4f46\u89c1\u4ed6\u53e3\u4e2d\u90a3\u67c4\u8f6f\u5200\u4e4b\u4e0a\u9c9c\u8840\u6ef4\u6ef4\u6d41\u4e0b\uff0c\u5634\u89d2\u8fb9\u4e5f\u6cbe\u4e86\u4e0d\u5c11\u9c9c\u8840\u3002 \u3000\u3000\u7fa4\u8c6a\u867d\u7136\u60ca\u9a87\uff0c\u4f46\u654c\u5ffe\u540c\u4ec7\uff0c\u53f1\u559d\u58f0\u4e2d\uff0c\u56db\u4e2a\u4eba\u5206\u4ece\u5de6\u53f3\u653b\u4e0a\u3002\u8840\u5200\u8001\u7956\u5411\u897f\u659c\u8d70\uff0c\u56db\u4eba\u5927\u58f0\u53eb\u9a82\uff0c\u53d1\u8db3\u8ffd\u8d76\uff0c\u4f59\u4eba\u4e5f\u662f\u8702\u62e5\u800c\u4e0a\u3002\u53ea\u8ffd\u51fa\u6570\u4e08\uff0c\u56db\u4eba\u811a\u4e0b\u5df2\u5206\u51fa\u5feb\u6162\uff0c\u4e24\u4eba\u5728\u524d\uff0c\u4e24\u4eba\u5728\u540e\u3002\u8840\u5200\u8001\u7956\u5ffd\u5730\u505c\u6b65\uff0c\u56de\u8eab\u6025\u51b2\uff0c\u7ea2\u5149\u95ea\u52a8\uff0c\u5148\u5934\u4e24\u4eba\u5df2\u7136\u547d\u4e27\u5200\u4e0b\u3002\u540e\u9762\u4e24\u4eba\u7565\u4e00\u8fdf\u7591\u4e4b\u9645\uff0c\u8840\u5200\u53ca\u9888\uff0c\u970e\u65f6\u95f4\u8eab\u9996\u5f02\u5904\u3002 \u3000\u3000\u72c4\u4e91\u8eba\u5728\u8349\u4e1b\u4e4b\u4e2d\uff0c\u89c1\u4ed6\u9877\u523b\u95f4\u8fde\u6bd9\u516d\u4eba\uff0c\u6b66\u529f\u4e4b\u8be1\u5f02\uff0c\u624b\u6cd5\u4e4b\u6b8b\u5fcd\uff0c\u5b9e\u662f\u4e0d\u53ef\u601d\u8bae\uff0c\u5fc3\u60f3\uff1a\u201c\u8fd9\u822c\u6253\u6cd5\uff0c\u4f59\u4e0b\u8fd9\u5341\u4e00\u4eba\uff0c\u53ea\u6015\u7247\u523b\u95f4\u4fbf\u88ab\u4ed6\u6740\u4e2a\u5e72\u51c0\u3002\u90a3\u53ef\u5982\u4f55\u662f\u597d\uff1f\u201d \u3000\u3000\u5ffd\u542c\u5f97\u4e00\u4eba\u53eb\u9053\uff1a\u201c\u8868\u59b9\uff0c\u8868\u59b9\uff0c\u4f60\u5728\u54ea\u91cc\uff1f\u201d\u6b63\u662f\u201c\u94c3\u5251\u53cc\u4fa0\u201d\u4e2d\u7684\u6c6a\u5578\u98ce\u3002 \u3000\u3000\u6c34\u7b19\u4fbf\u8eba\u5728\u72c4\u4e91\u7684\u8eab\u65c1\uff0c\u53ea\u662f\u88ab\u8840\u5200\u8001\u7956\u70b9\u4e86\u54d1\u7a74\uff0c\u53eb\u4e0d\u51fa\u58f0\uff0c\u5fc3\u4e2d\u5374\u5728\u5927\u53eb\uff1a\u201c\u8868\u54e5\uff0c\u6211\u5728\u8fd9\u91cc\u3002\u201d \u3000\u3000\u6c6a\u5578\u98ce\u5f2f\u8170\u75be\u8d70\uff0c\u5de6\u624b\u4e0d\u4f4f\u62e8\u52a8\u957f\u8349\u627e\u5bfb\u3002\u5ffd\u7136\u95f4\u4e00\u9635\u5c71\u98ce\uff0c\u5377\u8d77\u6c34\u7b19\u7684\u4e00\u89d2\u886b\u5b50\u3002\u6c6a\u5578\u98ce\u5927\u53eb\uff1a\u201c\u5728\u8fd9\u91cc\u4e86\uff01\u201d\u6251\u5c06\u4e0a\u6765\uff0c\u4e00\u628a\u5c06\u5979\u62b1\u8d77\u3002\u6c34\u7b19\u559c\u6781\u6d41\u6cea\uff0c\u5168\u8eab\u98a4\u6296\u3002\u6c6a\u5578\u98ce\u53ea\u53eb\uff1a\u201c\u8868\u59b9\uff0c\u8868\u59b9\uff0c\u4f60\u5728\u8fd9\u91cc\uff01\u201d\u7d27\u7d27\u7684\u62b1\u4f4f\u4e86\u5979\u3002\u4e8c\u4eba\u52ab\u540e\u91cd\u9022\uff0c\u751a\u4e48\u793c\u4eea\u89c4\u77e9\uff0c\u65e9\u90fd\u629b\u5230\u4e86\u4e5d\u9704\u4e91\u5916\u3002 \u3000\u3000\u6c6a\u5578\u98ce\u53c8\u95ee\uff1a\u201c\u8868\u59b9\uff0c\u4f60\u597d\u4e48\uff1f\u201d\u89c1\u6c34\u7b19\u4e0d\u7b54\uff0c\u5fc3\u4e0b\u8d77\u7591\uff0c\u5c06\u5979\u653e\u4e0b\u5730\u6765\u3002\u6c34\u7b19\u811a\u4e00\u7740\u5730\uff0c\u8eab\u5b50\u4fbf\u5f80\u540e\u4ef0\u3002\u6c6a\u5578\u98ce\u5b66\u8fc7\u70b9\u7a74\u4e4b\u6280\uff0c\u867d\u4e0d\u751a\u7cbe\uff0c\u5374\u4e5f\u4f1a\u5f97\u57fa\u672c\u624b\u6cd5\uff0c\u5fd9\u4f38\u624b\u5728\u5979\u8170\u95f4\u548c\u80cc\u5fc3\u4e09\u5904\u7a74\u9053\u4e4b\u4e0a\u63a8\u5bab\u8fc7\u8840\uff0c\u89e3\u4e86\u5979\u5c01\u95ed\u7684\u7a74\u9053\u3002\u6c34\u7b19\u53eb\u51fa\u58f0\u6765\uff1a\u201c\u8868\u54e5\uff0c\u8868\u54e5\u3002\u201d \u3000\u3000\u72c4\u4e91\u5f53\u6c6a\u5578\u98ce\u8d70\u8fd1\u8eab\u6765\uff0c\u4fbf\u77e5\u60c5\u52bf\u51f6\u9669\uff0c\u4e58\u7740\u4ed6\u7ed9\u6c34\u7b19\u63a8\u89e3\u7a74\u9053\u4e48\u9645\uff0c\u6084\u6084\u722c\u5f00\u3002 \u3000\u3000\u6c34\u7b19\u542c\u5f97\u8349\u4e2d\u7c0c\u7c0c\u6709\u58f0\uff0c\u60f3\u8d77\u8fd9\u6076\u50e7\u5bf9\u81ea\u5df1\u7684\u4fae\u8fb1\uff0c\u6307\u7740\u72c4\u4e91\uff0c\u5bf9\u6c6a\u5578\u98ce\u9053\uff1a\u201c\u5feb\uff0c\u5feb\uff0c\u6740\u4e86\u8fd9\u6076\u50e7\u3002\u201d\u8fd9\u65f6\u6c6a\u5578\u98ce\u7684\u957f\u5251\u5df2\u8fd8\u5165\u9798\u4e2d\uff0c\u4e00\u542c\u6b64\u8a00\uff0c\u5237\u7684\u4e00\u58f0\u62d4\u51fa\uff0c\u5251\u52bf\u5982\u98ce\uff0c\u5411\u72c4\u4e91\u75be\u523a\u800c\u51fa\u3002\u72c4\u4e91\u542c\u5f97\u6c34\u7b19\u53eb\u5524\uff0c\u65e9\u77e5\u4e0d\u5999\uff0c\u6ca1\u7b49\u957f\u5251\u9012\u5230\uff0c\u6025\u5fd9\u5411\u5916\u4e00\u4e2a\u6253\u6eda\uff0c\u5e78\u597d\u5904\u8eab\u6240\u5728\u6b63\u662f\u659c\u5761\uff0c\u987a\u52bf\u4fbf\u6eda\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u6c6a\u5578\u98ce\u8ddf\u7740\u53c8\u633a\u5251\u523a\u53bb\uff0c\u773c\u89c1\u4fbf\u8981\u523a\u4e2d\uff0c\u7a81\u7136\u5f53\u7684\u4e00\u58f0\u54cd\uff0c\u864e\u53e3\u4e00\u9707\uff0c\u773c\u524d\u7ea2\u5149\u95ea\u52a8\u3002\u4ed6\u767e\u5fd9\u4e2d\u4e0d\u53ca\u7ec6\u60f3\uff0c\u987a\u624b\u4f7f\u51fa\u6765\u7684\u4fbf\u662f\u4e5d\u5f0f\u8fde\u73af\u7684\u201c\u5b54\u96c0\u5f00\u5c4f\u201d\uff0c\u5c06\u957f\u5251\u821e\u6210\u4e00\u7247\u5149\u5c4f\uff0c\u6321\u5728\u8eab\u524d\u3002\u4f46\u542c\u5f97\u53ee\u53ee\u5f53\u5f53\uff0c\u5200\u5251\u76f8\u4ea4\u4e4b\u58f0\u5bc6\u5982\u8054\u73e0\uff0c\u53ea\u4e00\u77ac\u4e4b\u95f4\uff0c\u4fbf\u5df2\u76f8\u649e\u4e86\u4e09\u5341\u4f59\u58f0\u3002\u6c6a\u5578\u98ce\u5251\u6cd5\u5df2\u9887\u5f97\u4e43\u5e08\u6c34\u5cb1\u771f\u4f20\uff0c\u8fd9\u5957\u201c\u5b54\u96c0\u5f00\u5c4f\u201d\u7ffb\u6765\u8986\u53bb\u5171\u6709\u4e5d\u5f0f\uff0c\u5e73\u65f6\u7ec3\u5f97\u7eaf\u719f\uff0c\u6b64\u523b\u6027\u547d\u5728\u547c\u5438\u4e4b\u95f4\uff0c\u654c\u4eba\u7684\u5200\u62db\u6765\u5f97\u8fc5\u6377\u65e0\u6bd4\uff0c\u54ea\u91cc\u8fd8\u8bf4\u5f97\u4e0a\u89c1\u62db\u62c6\u62db\uff1f\u53ea\u662f\u81ea\u7ba1\u81ea\u7684\u7167\u5f0f\u6025\u821e\uff0c\u4f7f\u8fd9\u4e00\u5957\u201c\u5b54\u96c0\u5f00\u5c4f\u201d\uff0c\u4fbf\u4f3c\u662f\u51fa\u4e8e\u5929\u6027\u4e00\u822c\u3002\u8840\u5200\u8001\u7956\u8fde\u653b\u4e09\u5341\u516d\u5200\uff0c\u4e00\u5200\u5feb\u4f3c\u4e00\u5200\uff0c\u5c45\u7136\u5c3d\u6570\u7ed9\u4ed6\u6321\u4e86\u5f00\u53bb\u3002 \u3000\u3000\u7fa4\u8c6a\u53ea\u77a7\u5f97\u76ee\u4e3a\u4e4b\u7729\u3002\u8fd9\u65f6\u5341\u4e03\u4eba\u4e2d\u53c8\u5df2\u6709\u4e09\u4eba\u4e3a\u8840\u5200\u8001\u7956\u6240\u6740\uff0c\u5269\u4e0b\u6765\u8fde\u6c34\u7b19\u5728\u5185\u4e5f\u53ea\u4e5d\u4eba\u3002\u4f17\u4eba\u77a7\u5f97\u90fd\u662f\u624b\u5fc3\u4e2d\u634f\u4e00\u628a\u51b7\u6c57\uff0c\u5747\u60f3\uff1a\u201c\u94c3\u5251\u53cc\u4fa0\u540d\u4e0d\u865a\u4f20\uff0c\u53ea\u6709\u4ed6\u624d\u6321\u5f97\u4f4f\u8840\u5200\u6076\u50e7\u8fd9\u822c\u5feb\u5982\u95ea\u7535\u7684\u6025\u653b\u3002\u201d \u3000\u3000\u5176\u5b9e\u8840\u5200\u8001\u7956\u53ea\u987b\u5200\u62db\u653e\u6162\uff0c\u8ddf\u4ed6\u62c6\u4e0a\u5341\u4f59\u62db\uff0c\u6c6a\u5578\u98ce\u975e\u547d\u4e27\u8840\u5200\u4e4b\u4e0b\u4e0d\u53ef\uff0c\u5e78\u597d\u8840\u5200\u8001\u7956\u4e00\u65f6\u6ca1\u60f3\u5230\uff0c\u5bf9\u65b9\u8fd9\u5957\u4e13\u53d6\u5b88\u52bf\u7684\u5251\u62db\uff0c\u53ea\u4e0d\u8fc7\u662f\u719f\u7ec3\u4e86\u7684\u4e00\u8def\u5251\u6cd5\u800c\u5df2\uff0c\u5fc3\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u54b1\u4eec\u6597\u6597\uff0c\u5230\u5e95\u662f\u4f60\u5feb\u8fd8\u662f\u6211\u5feb\uff1f\u201d\u4e00\u5473\u7684\u52a0\u5feb\u5f3a\u653b\u3002 \u3000\u3000\u7fa4\u8c6a\u90fd\u60f3\u5e76\u529b\u4e0a\u524d\uff0c\u5c06\u8840\u5200\u8001\u7956\u4e71\u5200\u5206\u5c38\uff0c\u53ea\u662f\u4e24\u4eba\u6597\u5f97\u5b9e\u5728\u592a\u5feb\uff0c\u54ea\u91cc\u63d2\u5f97\u4e0b\u624b\u53bb\uff1f \u3000\u3000\u6c34\u7b19\u5173\u5fc3\u8868\u54e5\u5b89\u5371\uff0c\u867d\u662f\u624b\u9178\u811a\u8f6f\uff0c\u4e5f\u4e0d\u6562\u518d\u591a\u7b49\u5f85\uff0c\u4fef\u8eab\u4ece\u5730\u4e0b\u6b7b\u5c38\u624b\u91cc\u53d6\u8fc7\u4e00\u67c4\u957f\u5251\uff0c\u4e0a\u524d\u5939\u653b\u3002\u5979\u548c\u8868\u54e5\u5e73\u65f6\u8fde\u624b\u653b\u654c\uff0c\u914d\u5408\u7eaf\u719f\uff0c\u6c6a\u5578\u98ce\u6321\u4f4f\u4e86\u8840\u5200\u8001\u7956\u7684\u653b\u52bf\uff0c\u6c34\u7b19\u957f\u5251\u4fbf\u5411\u654c\u4eba\u8981\u5bb3\u523a\u53bb\u3002 \u3000\u3000\u8840\u5200\u8001\u7956\u6570\u5341\u62db\u62fe\u593a\u4e0d\u4e0b\u6c5f\u5578\u98ce\uff0c\u5fc3\u4e0b\u7126\u8e81\uff0c\u731b\u5730\u91cc\u4e00\u58f0\u5927\u543c\uff0c\u53f3\u624b\u4ecd\u662f\u8840\u5200\u6325\u821e\uff0c\u5de6\u624b\u5374\u7a7a\u624b\u53bb\u6293\u4ed6\u957f\u5251\u3002\u6c6a\u5578\u98ce\u5927\u5403\u4e00\u60ca\uff0c\u52a0\u5feb\u6325\u5251\uff0c\u53ea\u76fc\u5c06\u4ed6\u624b\u6307\u524a\u65ad\u51e0\u6839\uff0c\u4e0d\u6599\u8840\u5200\u8001\u7956\u7684\u5de6\u624b\u7adf\u662f\u4e0d\u6015\u5251\u950b\uff0c\u6216\u5f39\u6216\u538b\uff0c\u6216\u6311\u6216\u6309\uff0c\u7adf\u5c06\u4ed6\u5251\u62db\u5316\u89e3\u4e86\u5927\u534a\uff0c\u8fd9\u4e48\u4e00\u6765\uff0c\u6c6a\u5578\u98ce\u548c\u6c34\u7b19\u7acb\u65f6\u9669\u8c61\u73af\u751f\u3002 \u3000\u3000\u7fa4\u8c6a\u4e2d\u4e00\u4e2a\u8001\u8005\u77a7\u51fa\u52bf\u5934\u4e0d\u5bf9\uff0c\u77e5\u9053\u4eca\u665a\u201c\u94c3\u5251\u53cc\u4fa0\u201d\u82e5\u518d\u4e27\u547d\uff0c\u4f59\u4e0b\u7684\u6ca1\u4e00\u4eba\u80fd\u6d3b\u7740\u79bb\u5f00\u6b64\u5904\uff0c\u5927\u53eb\uff1a\u201c\u5927\u4f19\u513f\u5e76\u80a9\u5b50\u4e0a\uff0c\u8ddf\u6076\u50e7\u62da\u547d\u3002\u201d \u3000\u3000\u4fbf\u5728\u6b64\u65f6\uff0c\u5ffd\u542c\u5f97\u897f\u5317\u89d2\u4e0a\u6709\u4eba\u957f\u58f0\u53eb\u9053\uff1a\u201c\u843d\u2014\u2014\u82b1\u6d41\u6c34\uff01\u201d\u8ddf\u7740\u897f\u65b9\u4e5f\u6709\u4eba\u5e94\u9053\uff1a\u201c\u843d\u82b1\u2014\u2014\u6d41\u6c34\u3002\u201d\u201c\u6d41\u6c34\u201d\u4e24\u5b57\u5c1a\u672a\u53eb\u5b8c\uff0c\u897f\u5357\u65b9\u6709\u4eba\u53eb\u9053\uff1a\u201c\u843d\u82b1\u6d41\u2014\u2014\u6c34\u3002\u201d\u8fd9\u4e09\u4eba\u5206\u5904\u4e09\u65b9\uff0c\u9ad8\u547c\u4e4b\u58f0\u4e5f\u662f\u6216\u8c6a\u653e\uff0c\u6216\u60a0\u626c\uff0c\u97f3\u8c03\u4e0d\u540c\uff0c\u4f46\u5747\u662f\u4e2d\u6c14\u5145\u6c9b\uff0c\u5185\u529b\u751a\u9ad8\u3002 \u3000\u3000\u8840\u5200\u8001\u7956\u4e00\u60ca\uff1a\u201c\u5374\u4ece\u54ea\u91cc\u94bb\u51fa\u8fd9\u4e09\u4e2a\u9ad8\u624b\u6765\uff1f\u4ece\u58f0\u97f3\u4e2d\u542c\u6765\uff0c\u6bcf\u4e00\u4eba\u7684\u6b66\u529f\u53ea\u6015\u90fd\u4e0d\u5728\u6211\u4e0b\uff0c\u4e09\u4e2a\u5bb6\u4f19\u8fde\u624b\u5939\u653b\uff0c\u90a3\u53ef\u4e0d\u6613\u5bf9\u4ed8\u3002\u201d\u4ed6\u5fc3\u4e2d\u5bfb\u601d\u5e94\u654c\u4e4b\u7b56\uff0c\u624b\u4e2d\u5200\u62db\u5374\u662f\u6beb\u4e0d\u8fdf\u7f13\u3002 \u3000\u3000\u731b\u542c\u5f97\u5357\u8fb9\u53c8\u6709\u4e00\u4eba\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u843d\u82b1\u6d41\u6c34\u2014\u2014\u201d\u8fd9\u201c\u843d\u82b1\u6d41\u6c34\u201d\u7684\u7b2c\u56db\u4e2a\u201c\u6c34\u201d\u62d6\u5f97\u7279\u957f\uff0c\u58f0\u97f3\u6ed4\u6ed4\u4e0d\u7edd\u7684\u4f20\u5230\uff0c\u6709\u5982\u957f\u6c5f\u5927\u6cb3\u4e00\u822c\u3002\u8fd9\u58f0\u97f3\u66f4\u6bd4\u5176\u4f59\u4e09\u4eba\u8fd1\u5f97\u591a\u3002 \u3000\u3000\u6c34\u7b19\u5927\u559c\uff0c\u53eb\u9053\uff1a\u201c\u7239\u7239\uff0c\u7239\u7239\uff0c\u5feb\u6765\uff01\u201d \u3000\u3000\u7fa4\u8c6a\u4e2d\u6709\u4eba\u559c\u9053\uff1a\u201c\u6c5f\u5357\u56db\u8001\u5230\u5566\uff0c\u843d\u82b1\u6d41\u6c34\uff01\u54c8\u2026\u2026\u201d \u3000\u3000\u4ed6\u90a3\u54c8\u54c8\u5927\u7b11\u53ea\u7b11\u51fa\u4e00\u4e2a\u201c\u54c8\u201d\u5b57\uff0c\u80f8\u53e3\u9c9c\u8840\u6fc0\u55b7\uff0c\u5df2\u88ab\u8840\u5200\u780d\u4e2d\u3002 \u3000\u3000\u8840\u5200\u8001\u7956\u542c\u5f97\u53c8\u6765\u4e00\u4eba\uff0c\u800c\u6b64\u4eba\u7adf\u662f\u6c34\u7b19\u4e4b\u7236\uff0c\u731b\u5730\u60f3\u8d77\u4e00\u4e8b\uff1a\u201c\u66fe\u542c\u6211\u5f92\u513f\u5584\u52c7\u8bf4\u9053\uff0c\u4e2d\u539f\u6b66\u6797\u4e2d\u6b66\u529f\u6700\u5389\u5bb3\u7684\uff0c\u9664\u4e86\u4e01\u5178\u4e4b\u5916\uff0c\u6709\u751a\u4e48\u5357\u56db\u5947\u3001\u5317\u56db\u602a\u3002\u5317\u56db\u602a\u53eb\u751a\u4e48\u2018\u98ce\u864e\u4e91\u9f99\u2019\uff0c\u5357\u56db\u5947\u5219\u662f\u2018\u843d\u82b1\u6d41\u6c34\u2019\u3002\u5f53\u65f6\u6211\u542c\u4e86\u8bf4\u9053\u6eda\u4ed6\u5988\u7684\uff0c\u5916\u53f7\u53eb\u4f5c\u2018\u843d\u82b1\u6d41\u6c34\u2019\uff0c\u8fd8\u80fd\u6709\u751a\u4e48\u597d\u811a\u8272\uff1f\u53ef\u662f\u542c\u8fd9\u56db\u4e2a\u5bb6\u4f19\u7684\u5e94\u548c\u4e4b\u58f0\uff0c\u53ef\u7740\u5b9e\u6709\u70b9\u513f\u9b3c\u95e8\u9053\u3002\u201d \u3000\u3000\u4ed6\u5bfb\u601d\u672a\u5b9a\uff0c\u53ea\u542c\u5f97\u56db\u4eba\u9f50\u58f0\u5408\u547c\uff0c\u201c\u843d\u82b1\u6d41\u6c34\u201d\u4e4b\u58f0\uff0c\u4ece\u56db\u4e2a\u4e0d\u540c\u65b9\u5411\u4f20\u6765\uff0c\u53ea\u9707\u5f97\u5c71\u8c37\u9e23\u54cd\u3002\u8840\u5200\u8001\u7956\u542c\u58f0\u97f3\u77e5\u56db\u4eba\u76f8\u8ddd\u5c1a\u8fdc\uff0c\u6700\u8fdc\u7684\u8fd8\u5728\u4e94\u91cc\u4e4b\u5916\uff0c\u4f46\u7b49\u5f97\u5c06\u773c\u524d\u654c\u4eba\u4e00\u4e00\u6740\u4e86\uff0c\u90a3\u56db\u4eba\u4e00\u5408\u4e0a\u56f4\uff0c\u53ef\u5c31\u4e0d\u6613\u8131\u8eab\u3002\u4ed6\u64ae\u5507\u4f5c\u5578\uff0c\u957f\u58f0\u547c\u9053\uff1a\u201c\u843d\u82b1\u6d41\u6c34\uff0c\u6211\u6253\u4f60\u4eec\u4e2a\u843d\u82b1\u6d41\u6c34\uff01\u201d\u624b\u6307\u5f39\u5904\uff0c\u94ee\u7684\u4e00\u58f0\uff0c\u6c34\u7b19\u624b\u4e2d\u957f\u5251\u88ab\u4ed6\u5f39\u4e2d\uff0c\u62ff\u634f\u4e0d\u5b9a\uff0c\u957f\u5251\u76f4\u98de\u8d77\u6765\u3002 \u3000\u3000\u8840\u5200\u8001\u7956\u53eb\u9053\uff1a\u201c\u72c4\u4e91\uff0c\u9884\u5907\u4e0a\u9a6c\uff0c\u54b1\u4eec\u53ef\u8981\u5c11\u966a\u4e86\u3002\u201d \u3000\u3000\u72c4\u4e91\u7b54\u5e94\u4e0d\u51fa\uff0c\u5fc3\u4e2d\u597d\u751f\u4e3a\u96be\uff0c\u8981\u662f\u548c\u4ed6\u540c\u9003\uff0c\u96be\u514d\u9677\u6eba\u8d8a\u6765\u8d8a\u6df1\uff0c\u5c06\u6765\u65e0\u53ef\u6536\u62fe\u3002\u4f46\u5982\u7559\u5728\u6b64\u5904\uff0c\u7acb\u65f6\u4fbf\u4f1a\u88ab\u4f17\u4eba\u65a9\u6210\u788e\u5757\uff0c\u8bf4\u534a\u53e5\u8bdd\u6765\u5206\u8fa9\u7684\u4f59\u88d5\u4e5f\u65e0\u3002\u53ea\u542c\u8840\u5200\u8001\u7956\u53c8\u53eb\uff1a\u201c\u5f92\u5b59\u513f\uff0c\u5feb\u7275\u4e86\u9a6c\u3002\u201d\u72c4\u4e91\u8f6c\u5ff5\u5df2\u5b9a\uff1a\u201c\u773c\u524d\u603b\u662f\u9003\u547d\u8981\u7d27\u3002\u6211\u8fd9\u4e00\u751f\u7ed9\u4eba\u51a4\u6789\uff0c\u8fd8\u7b97\u5c11\u4e86\uff1f\u4eba\u5bb6\u5fc3\u91cc\u5bf9\u6211\u600e\u4e48\u60f3\u6cd5\uff0c\u54ea\u7ba1\u5f97\u4e86\u8fd9\u8bb8\u591a\uff1f\u201d\u7b49\u5230\u8840\u5200\u8001\u7956\u7b2c\u4e09\u6b21\u547c\u53eb\uff0c\u4fbf\u5373\u7b54\u5e94\uff0c\u62fe\u8d77\u5730\u4e0b\u4e00\u6839\u82b1\u67aa\uff0c\u5de6\u624b\u652f\u6491\u7740\u5f53\u4f5c\u62d0\u6756\uff0c\u8d70\u5230\u6811\u8fb9\u53bb\u7275\u4e86\u4e24\u5339\u5750\u9a91\u3002 \u3000\u3000\u4e00\u4e2a\u4f7f\u6746\u68d2\u7684\u5927\u80d6\u5b50\u53eb\u9053\uff1a\u201c\u4e0d\u597d\uff0c\u6076\u50e7\u60f3\u9003\uff0c\u6211\u53bb\u963b\u4f4f\u4ed6\u3002\u201d\u633a\u8d77\u6746\u68d2\uff0c\u4fbf\u5411\u72c4\u4e91\u8d76\u53bb\u3002\u8840\u5200\u8001\u7956\u9053\uff1a\u201c\u563f\uff0c\u4f60\u53bb\u963b\u4f4f\u4ed6\uff0c\u6211\u6765\u963b\u4f4f\u4f60\u3002\u201d\u8840\u5200\u6325\u5904\uff0c\u90a3\u80d6\u5b50\u8fde\u4eba\u5e26\u68d2\uff0c\u65ad\u4e3a\u56db\u622a\uff0c\u4f59\u4eba\u89c1\u5230\u4ed6\u5982\u6b64\u60e8\u6b7b\uff0c\u5fcd\u4e0d\u4f4f\u9a87\u7136\u800c\u547c\u3002\u8840\u5200\u8001\u7956\u539f\u662f\u8981\u5413\u9000\u4f17\u4eba\u7684\u7275\u7f20\uff0c\u56de\u8fc7\u957f\u81c2\uff0c\u62e6\u8170\u62b1\u8d77\u6c34\u7b19\uff0c\u6492\u817f\u4fbf\u5411\u7275\u7740\u5750\u9a91\u7684\u72c4\u4e91\u8eab\u524d\u5954\u6765\u3002 \u3000\u3000\u6c34\u7b19\u6025\u53eb\uff1a\u201c\u6076\u50e7\uff0c\u653e\u5f00\u6211\uff0c\u653e\u5f00\u6211\uff01\u201d\u4f38\u62f3\u5f80\u4ed6\u80cc\u4e0a\u6025\u64c2\u3002\u5979\u5251\u6cd5\u4e0d\u5f31\uff0c\u62f3\u5934\u5374\u51fa\u624b\u65e0\u529b\uff0c\u8840\u5200\u8001\u7956\u76ae\u7c97\u8089\u539a\uff0c\u7ed9\u5979\u6376\u4e0a\u51e0\u4e0b\u6d51\u5982\u4e0d\u89c9\uff0c\u957f\u817f\u4e00\u8fc8\u4fbf\u662f\u534a\u4e08\uff0c\u8fde\u7eb5\u5e26\u5954\uff0c\u51e0\u4e2a\u8d77\u843d\uff0c\u4fbf\u5df2\u5230\u4e86\u72c4\u4e91\u8eab\u65c1\u3002 \u3000\u3000\u6c6a\u5578\u98ce\u5c06\u90a3\u5957\u201c\u5b54\u96c0\u5f00\u5c4f\u201d\u4f7f\u53d1\u4e86\u6027\uff0c\u4e00\u65f6\u6536\u4e0d\u4f4f\u62db\uff0c\u4ecd\u662f\u201c\u4e1c\u5c55\u9526\u7fbd\u201d\u3001\u201c\u897f\u5254\u7fe0\u7fce\u201d\u3001\u201c\u5357\u8fce\u8273\u9633\u201d\u3001\u201c\u5317\u56de\u6668\u98ce\u201d\u3001\u4e00\u5f0f\u5f0f\u7684\u4f7f\u52a8\u3002\u4ed6\u89c1\u6c34\u7b19\u518d\u6b21\u88ab\u63b3\uff0c\u5fd9\u72c2\u5954\u8ffd\u6765\uff0c\u624b\u4e2d\u957f\u5251\u867d\u4ecd\u4e0d\u4f4f\u6325\u821e\uff0c\u5374\u5df2\u4e0d\u6210\u7ae0\u6cd5\u3002 \u3000\u3000\u8840\u5200\u8001\u7956\u5c06\u72c4\u4e91\u4e00\u63d0\uff0c\u653e\u4e0a\u4e86\u9ec4\u9a6c\uff0c\u53c8\u5c06\u6c34\u7b19\u653e\u5728\u4ed6\u8eab\u524d\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u90a3\u56db\u4e2a\u9b3c\u53eb\u7684\u5bb6\u4f19\u90fd\u662f\u52b2\u654c\uff0c\u975e\u540c\u5c0f\u53ef\u3002\u8fd9\u5973\u5a03\u513f\u662f\u4eba\u8d28\uff0c\u522b\u8ba9\u5979\u8dd1\u4e86\u3002\u201d\u8bf4\u7740\u8de8\u4e0a\u767d\u9a6c\uff0c\u7eb5\u9a91\u5411\u4e1c\u3002 \u3000\u3000\u53ea\u542c\u5f97\u201c\u843d\u82b1\u6d41\u6c34\uff0c\u843d\u82b1\u6d41\u6c34\u201d\u7684\u547c\u58f0\u6e10\u8fd1\uff0c\u6709\u65f6\u662f\u4e00\u4eba\u5355\u547c\uff0c\u6709\u65f6\u5374\u662f\u4e24\u4eba\u3001\u4e09\u4eba\u3001\u56db\u4eba\u9f50\u58f0\u547c\u53eb\u3002 \u3000\u3000\u6c34\u7b19\u5927\u53eb\uff1a\u201c\u8868\u54e5\uff0c\u8868\u54e5\uff01\u7239\u7239\uff0c\u7239\u7239\uff01\u5feb\u6765\u6551\u6211\u3002\u201d\u53ef\u662f\u773c\u89c1\u5f97\u8868\u54e5\u53c8\u4e00\u6b21\u8fdc\u8fdc\u7684\u843d\u5728\u9a6c\u540e\u3002\u201c\u94c3\u5251\u53cc\u4fa0\u201d\u7684\u5750\u9a91\u9ec4\u9a6c\u548c\u767d\u9a6c\u4e43\u662f\u5343\u4e2d\u6311\u3001\u4e07\u4e2d\u9009\u7684\u5927\u5b9b\u9a8f\u9a6c\u3002\u5e73\u65f6\u4ed6\u4e8c\u4eba\u4ee5\u6b64\u81ea\u50b2\uff0c\u5e38\u8bf4\u53cc\u9a91\u811a\u7a0b\u4e4b\u5feb\uff0c\u529b\u6c14\u4e4b\u957f\uff0c\u5f53\u4e16\u66f4\u65e0\u7b2c\u4e09\u5339\u9a6c\u53ca\u5f97\u4e0a\uff0c\u53ef\u662f\u8fd9\u65f6\u4e3a\u654c\u6240\u7528\uff0c\u755c\u751f\u65e0\u77e5\uff0c\u4ecd\u662f\u8fd9\u822c\u75be\u9a70\u5feb\u8dd1\uff0c\u9a6c\u8d8a\u5feb\uff0c\u79bb\u5f97\u6c6a\u5578\u98ce\u8d8a\u52a0\u8fdc\u4e86\u3002 \u3000\u3000\u6c6a\u5578\u98ce\u773c\u770b\u8ffd\u8d76\u4e0d\u4e0a\uff0c\u53ea\u6709\u4e0d\u4f4f\u547c\u53eb\uff1a\u201c\u8868\u59b9\uff0c\u8868\u59b9\uff01\u201d \u3000\u3000\u4e00\u4e2a\u9ad8\u547c\u201c\u8868\u54e5\u201d\uff0c\u4e00\u4e2a\u5927\u53eb\u201c\u8868\u59b9\u201d\uff0c\u58f0\u97f3\u54c0\u51c4\uff0c\u72c4\u4e91\u542c\u5728\u8033\u4e2d\uff0c\u6781\u662f\u4e0d\u5fcd\uff0c\u53ea\u60f3\u5c06\u6c34\u7b19\u63a8\u4e0b\u9a6c\u6765\uff0c\u4f46\u60f3\u5230\u8840\u5200\u8001\u7956\u4e4b\u8a00\uff1a\u201c\u6765\u7684\u90fd\u662f\u52b2\u654c\uff0c\u975e\u540c\u5c0f\u53ef\uff0c\u8fd9\u5973\u5a03\u513f\u662f\u4eba\u8d28\uff0c\u522b\u8ba9\u5979\u8dd1\u4e86\u3002\u201d\u653e\u8d70\u6c34\u7b19\uff0c\u8840\u5200\u8001\u7956\u5b9a\u4f1a\u5927\u6012\uff0c\u6b64\u4eba\u6b8b\u5fcd\u65e0\u6bd4\uff0c\u6740\u4e86\u81ea\u5df1\u5982\u5bb0\u9e21\u72ac\uff0c\u53c8\u60f3\u5982\u7ed9\u6c34\u7b19\u4e4b\u7236\u7b49\u56db\u4e2a\u9ad8\u624b\u8ffd\u4e0a\u4e86\uff0c\u81ea\u5df1\u5b9a\u4e5f\u4e0d\u514d\u51a4\u6789\u9001\u547d\u3002\u4e00\u65f6\u72b9\u8c6b\u96be\u51b3\uff0c\u542c\u5f97\u6c34\u7b19\u9ad8\u53eb\u8868\u54e5\u4e4b\u97f3\u5df2\u662f\u58f0\u5636\u529b\u7aed\uff0c\u5fc3\u4e2d\u7a81\u7136\u4e00\u9178\uff1a\u201c\u4ed6\u4e8c\u4eba\u60c5\u6df1\u7231\u91cd\uff0c\u88ab\u4eba\u6d3b\u751f\u751f\u7684\u62c6\u5f00\u3002\u6211\u8ddf\u5e08\u59b9\u2026\u2026\u563f\uff0c\u6211\u8ddf\u5e08\u59b9\uff0c\u4f55\u5c1d\u4e0d\u662f\u8fd9\u6837\uff1f\u53ef\u662f\uff0c\u53ef\u662f\u5979\u5bf9\u5f85\u6211\uff0c\u51e0\u65f6\u80fd\u50cf\u6c34\u59d1\u5a18\u5bf9\u5979\u8868\u54e5\u90a3\u6837\uff1f\u201d\u60f3\u5230\u6b64\u5904\uff0c\u4e0d\u7531\u5f97\u4f24\u5fc3\uff0c\u5fc3\u9053\uff1a\u201c\u4f60\u53bb\u7f62\uff01\u201d\u4f38\u624b\u5c06\u5979\u63a8\u4e0b\u4e86\u9a6c\u80cc\u3002 \u3000\u3000\u8840\u5200\u8001\u7956\u867d\u7136\u5728\u524d\u5e26\u8def\uff0c\u65f6\u65f6\u7559\u795e\u540e\u9762\u5750\u9a91\u4e0a\u7684\u52a8\u9759\uff0c\u5ffd\u542c\u5f97\u6c34\u7b19\u5927\u53eb\u4e4b\u58f0\u7a81\u505c\uff0c\u8ddf\u7740\u4e00\u58f0\u201c\u554a\u54df\u201d\uff0c\u6389\u5728\u5730\u4e0b\uff0c\u8fd8\u9053\u72c4\u4e91\u65ad\u4e86\u4e00\u817f\uff0c\u5236\u5979\u4e0d\u4f4f\uff0c\u5f53\u5373\u515c\u8f6c\u9a6c\u5934\u3002 \u3000\u3000\u6c34\u7b19\u8eab\u5b50\u843d\u5730\uff0c\u8f7b\u8f7b\u4e00\u7eb5\uff0c\u5df2\u7136\u7ad9\u76f4\uff0c\u5f53\u5373\u53d1\u8db3\u5411\u6c6a\u5578\u98ce\u5954\u53bb\u3002\u4e24\u4eba\u6b64\u65f6\u76f8\u8ddd\u5df2\u6709\u4e94\u5341\u4f59\u4e08\uff0c\u4e00\u4e2a\u81ea\u897f\u800c\u4e1c\uff0c\u4e00\u4e2a\u81ea\u4e1c\u800c\u897f\uff0c\u8d8a\u5954\u8d8a\u8fd1\u3002\u4e00\u4e2a\u53eb\uff1a\u201c\u8868\u54e5\uff01\u201d\u4e00\u4e2a\u53eb\uff1a\u201c\u8868\u59b9\uff01\u201d\u90fd\u662f\u8bf4\u4e0d\u51fa\u7684\u6b22\u559c\u3002 \u3000\u3000\u8840\u5200\u8001\u7956\u5fae\u7b11\u52d2\u9a6c\uff0c\u7adf\u4e0d\u7406\u4f1a\uff0c\u7a0d\u5019\u7247\u523b\uff0c\u773c\u89c1\u6c6a\u5578\u98ce\u548c\u6c34\u7b19\u76f8\u8ddd\u5df2\u4e0d\u8fc7\u4e8c\u5341\u4f59\u4e08\uff0c\u8fd9\u624d\u53cc\u817f\u4e00\u5939\uff0c\u4e00\u58f0\u547c\u5578\uff0c\u5411\u6c34\u7b19\u8ffd\u53bb\u3002 \u3000\u3000\u72c4\u4e91\u5927\u60ca\uff0c\u5fc3\u4e2d\u53ea\u53eb\uff1a\u201c\u5feb\u8dd1\uff0c\u5feb\u8dd1\uff01\u201d\u5bf9\u9762\u51e0\u4e2a\u5e78\u5b58\u7684\u6c49\u5b50\u89c1\u8840\u5200\u8001\u7956\u53e3\u8854\u8840\u5200\uff0c\u7eb5\u9a6c\u51b2\u6765\uff0c\u4e5f\u662f\u9f50\u58f0\u547c\u53eb\uff1a\u201c\u5feb\u8dd1\uff0c\u5feb\u8dd1\uff01\u201d \u3000\u3000\u6c34\u7b19\u542c\u5f97\u80cc\u540e\u9a6c\u8e44\u4e4b\u58f0\u8d8a\u6765\u8d8a\u8fd1\uff0c\u4f46\u4e24\u4eba\u53d1\u529b\u6025\u5954\u4e4b\u4e0b\uff0c\u548c\u6c6a\u5578\u98ce\u4e4b\u95f4\u76f8\u8ddd\u4e5f\u662f\u8d8a\u6765\u8d8a\u8fd1\u3002\u5979\u5954\u5f97\u80f8\u53e3\u51e0\u4e4e\u8981\u70b8\u88c2\u4e86\uff0c\u819d\u5f2f\u53d1\u8f6f\uff0c\u968f\u65f6\u90fd\u4f1a\u6454\u5012\uff0c\u7ec8\u4e8e\u8fd8\u662f\u52c9\u5f3a\u652f\u6491\u3002 \u3000\u3000\u7a81\u7136\u4e4b\u95f4\uff0c\u89c9\u5f97\u5230\u767d\u9a6c\u7684\u547c\u5438\u55b7\u5230\u4e86\u80cc\u5fc3\uff0c\u542c\u5f97\u8840\u5200\u8001\u7956\u7b11\u9053\uff1a\u201c\u9003\u5f97\u4e86\u4e48\uff1f\u201d\u6c34\u7b19\u4f38\u51fa\u53cc\u624b\uff0c\u6c6a\u5578\u98ce\u8fd8\u5728\u4e24\u4e08\u4ee5\u5916\uff0c\u8840\u5200\u8001\u7956\u7684\u5de6\u624b\u5374\u5df2\u642d\u4e0a\u4e86\u5979\u7684\u80a9\u5934\u3002 \u3000\u3000\u5979\u4e00\u58f0\u60ca\u547c\uff0c\u6b63\u8981\u54ed\u51fa\u58f0\u6765\uff0c\u53ea\u542c\u5f97\u4e00\u4e2a\u719f\u6089\u800c\u6148\u7231\u7684\u58f0\u97f3\u53eb\u9053\uff1a\u201c\u7b19\u513f\u522b\u6015\uff0c\u7239\u6765\u6551\u4f60\u4e86\uff01\u201d \u3000\u3000\u6c34\u7b19\u4e00\u542c\uff0c\u6b63\u662f\u7236\u4eb2\u5230\u4e86\uff0c\u5fc3\u4e2d\u4e00\u559c\uff0c\u7cbe\u795e\u9661\u957f\uff0c\u811a\u4e0b\u4e0d\u77e5\u4ece\u54ea\u91cc\u751f\u51fa\u4e00\u80a1\u529b\u91cf\uff0c\u4e00\u7eb5\u4e4b\u4e0b\uff0c\u5411\u524d\u8dc3\u51fa\u4e08\u4f59\uff0c\u8840\u5200\u8001\u7956\u7684\u624b\u638c\u672c\u5df2\u642d\u5728\u5979\u80a9\u5934\uff0c\u7adf\u5c14\u88ab\u5979\u6446\u8131\u3002\u6c6a\u5578\u98ce\u5411\u524d\u4e00\u51d1\uff0c\u4e24\u4eba\u5de6\u624b\u5df2\u62c9\u7740\u5de6\u624b\u3002\u6c6a\u5578\u98ce\u53f3\u624b\u957f\u5251\u821e\u51fa\u4e00\u4e2a\u5251\u82b1\uff0c\u5fc3\u4e0b\u6697\u9053\uff1a\u201c\u5929\u53ef\u601c\u89c1\uff0c\u5e08\u7236\u53ca\u65f6\u8d76\u5230\uff0c\u4fbf\u4e0d\u6015\u90a3\u6deb\u50e7\u6076\u9b54\u4e86\u3002\u201d \u3000\u3000\u8840\u5200\u8001\u7956\u563f\u563f\u51b7\u7b11\u58f0\u4e2d\uff0c\u8840\u5200\u9012\u51fa\u3002\u6c6a\u5578\u98ce\u6025\u6325\u957f\u5251\u53bb\u683c\uff0c\u7a81\u89c1\u90a3\u8840\u5200\u7ea2\u5f71\u95ea\u95ea\uff0c\u8fce\u98ce\u5f2f\u8f6c\uff0c\u7adf\u5982\u4e00\u6839\u8f6f\u5e26\u4e00\u822c\uff0c\u987a\u7740\u5251\u950b\u66f2\u4e86\u4e0b\u6765\uff0c\u5200\u5934\u524a\u5411\u4ed6\u624b\u6307\u3002\u6c6a\u5578\u98ce\u82e5\u4e0d\u653e\u624b\u64a4\u5251\uff0c\u4e00\u53ea\u624b\u638c\u7acb\u65f6\u4fbf\u5e9f\u4e86\u3002\u4ed6\u767e\u5fd9\u4e2d\u53d8\u62db\u4e5f\u771f\u8fc5\u6377\uff0c\u638c\u5fc3\u52b2\u529b\u4e00\u5410\uff0c\u957f\u5251\u5411\u654c\u4eba\u98de\u63b7\u8fc7\u53bb\u3002 \u3000\u3000\u8840\u5200\u8001\u7956\u5de6\u6307\u5f39\u5904\uff0c\u5c06\u957f\u5251\u5411\u897f\u9996\u98de\u5954\u800c\u81f3\u7684\u4e00\u4e2a\u8001\u8005\u5f39\u51fa\uff0c\u53f3\u624b\u4e2d\u8840\u5200\u66f4\u5411\u524d\u4f38\uff0c\u76f4\u780d\u6c6a\u5578\u98ce\u9762\u95e8\u3002\u6c6a\u5578\u98ce\u4ef0\u8eab\u76f8\u907f\uff0c\u4e0d\u5f97\u4e0d\u653e\u5f00\u4e86\u6c34\u7b19\u7684\u624b\u638c\u3002\u8840\u5200\u8001\u7956\u5de6\u624b\u56de\u6284\uff0c\u5df2\u5c06\u6c34\u7b19\u62b1\u8d77\uff0c\u6a2a\u653e\u5728\u9a6c\u978d\u4e4b\u4e0a\u3002\u4ed6\u5374\u4e0d\u62c9\u8f6c\u9a6c\u5934\uff0c\u4ecd\u662f\u5411\u524d\u76f4\u9a70\uff0c\u51b2\u5411\u524d\u9762\u4e2d\u539f\u7fa4\u8c6a\u3002 \u3000\u3000\u62e6\u5728\u9053\u4e2d\u7684\u51e0\u6761\u6c49\u5b50\u89c1\u4ed6\u9a70\u9a6c\u51b2\u6765\uff0c\u9f50\u58f0\u53d1\u558a\uff0c\u6563\u5728\u4e24\u65c1\u3002\u8840\u5200\u8001\u7956\u53e3\u53d1\u8377\u8377\u602a\u58f0\uff0c\u780d\u7ffb\u4e00\u540d\u6c49\u5b50\uff0c\u7eb5\u9a6c\u515c\u4e86\u4e2a\u5708\u5b50\uff0c\u5411\u72c4\u4e91\u5954\u53bb\u3002 \u3000\u3000\u7a81\u89c1\u5de6\u9996\u7070\u5f71\u4e00\u95ea\uff0c\u957f\u5251\u4e0a\u53cd\u5c04\u7684\u6708\u5149\u8000\u773c\u751f\u82b1\uff0c\u4e00\u6761\u51b7\u68ee\u68ee\u7684\u5251\u5149\u70b9\u5411\u4ed6\u80f8\u53e3\u3002\u8840\u5200\u8001\u7956\u56de\u5200\u63a0\u51fa\uff0c\u5f53\u7684\u4e00\u58f0\uff0c\u5200\u5251\u76f8\u4ea4\uff0c\u53ea\u9707\u5f97\u864e\u53e3\u9690\u9690\u4f5c\u9ebb\uff0c\u5fc3\u9053\uff1a\u201c\u597d\u5f3a\u7684\u5185\u529b\u3002\u201d\u4fbf\u5728\u6b64\u65f6\uff0c\u53f3\u9996\u53c8\u6709\u4e00\u67c4\u957f\u5251\u9012\u5230\uff0c\u8fd9\u5251\u52bf\u9053\u751a\u5947\uff0c\u5251\u5c16\u5212\u6210\u5927\u5927\u5c0f\u5c0f\u7684\u4e00\u4e2a\u4e2a\u5708\u5b50\uff0c\u7adf\u770b\u4e0d\u6e05\u4ed6\u5251\u62db\u6307\u5411\u4f55\u5904\u3002\u8840\u5200\u8001\u7956\u53c8\u662f\u4e00\u60ca\uff1a\u201c\u592a\u6781\u5251\u540d\u5bb6\u5230\u4e86\u3002\u201d \u3000\u3000\u4ed6\u52b2\u900f\u53f3\u81c2\uff0c\u8840\u5200\u4e5f\u6325\u6210\u4e00\u4e2a\u5706\u5708\uff0c\u5200\u5708\u548c\u5251\u5708\u4e00\u78b0\uff0c\u5f53\u5f53\u5f53\u6570\u58f0\uff0c\u706b\u82b1\u8ff8\u6e85\u3002\u5bf9\u65b9\u559d\u9053\uff1a\u201c\u597d\u5200\u6cd5\uff01\u201d\u5411\u65c1\u98d8\u5f00\uff0c\u5374\u662f\u4e2a\u8eab\u7a7f\u674f\u9ec4\u9053\u888d\u7684\u9053\u4eba\u3002\u8840\u5200\u8001\u7956\u53eb\u9053\uff1a\u201c\u4f60\u5251\u6cd5\u4e5f\u597d\uff01\u201d\u5de6\u9996\u90a3\u4eba\u559d\u9053\uff1a\u201c\u653e\u4e0b\u6211\u5973\u513f\uff01\u201d\u5251\u4e2d\u5939\u638c\uff0c\u638c\u4e2d\u5939\u5251\uff0c\u4e24\u80a1\u52b2\u529b\u4e00\u9f50\u88ad\u5230\u3002 \u3000\u3000\u72c4\u4e91\u8fdc\u8fdc\u671b\u89c1\u8840\u5200\u8001\u7956\u53c8\u5c06\u6c34\u7b19\u63b3\u5230\uff0c\u8ddf\u7740\u5374\u53d7\u4e8c\u4eba\u5de6\u53f3\u5939\u51fb\u3002\u5de6\u9996\u90a3\u8001\u8005\u767d\u987b\u5982\u94f6\uff0c\u76f8\u8c8c\u4fca\u96c5\uff0c\u53e3\u53e3\u58f0\u58f0\u547c\u559d\u201c\u653e\u4e0b\u6211\u5973\u513f\u201d\uff0c\u81ea\u662f\u6c34\u7b19\u7684\u7236\u4eb2\u3002\u4f46\u89c1\u8840\u5200\u8001\u7956\u6bcf\u63a5\u4ed6\u4e00\u5251\uff0c\u8eab\u5b50\u4fbf\u6643\u4e86\u4e00\u6643\uff0c\u4f3c\u662f\u5185\u529b\u6709\u6240\u4e0d\u5982\uff0c\u5374\u89c1\u897f\u8fb9\u5c71\u9053\u4e0a\u53c8\u6709\u4e24\u4eba\u5954\u6765\uff0c\u8eab\u5f62\u5feb\u6377\u5982\u98ce\uff0c\u663e\u7136\u4e5f\u662f\u6781\u5f3a\u7684\u9ad8\u624b\u3002\u72c4\u4e91\u5fc3\u60f3\uff1a \u3000\u3000\u201c\u5f85\u5f97\u90a3\u4e8c\u4eba\u8d76\u5230\uff0c\u56db\u4eba\u5408\u56f4\uff0c\u8840\u5200\u8001\u7956\u5b9a\u7136\u4e0d\u654c\uff0c\u975e\u6b7b\u5373\u4f24\u3002 \u3000\u3000\u6211\u8fd8\u662f\u53ca\u65e9\u9003\u547d\u7f62\uff01\u201d\u8f6c\u5ff5\u53c8\u60f3\uff1a\u201c\u82e5\u4e0d\u662f\u4ed6\u51fa\u624b\u76f8\u6551\uff0c\u6211\u65e9\u7ed9\u90a3\u6c6a\u5578\u98ce\u4e00\u5251\u6740\u4e86\u3002\u5fd8\u6069\u8d1f\u4e49\uff0c\u53ea\u987e\u81ea\u8eab\uff0c\u592a\u4e5f\u5351\u9119\u65e0\u803b\u3002\u201d \u3000\u3000\u5f53\u4e0b\u52d2\u9a6c\u76f8\u5019\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u8840\u5200\u8001\u7956\u5927\u53eb\uff1a\u201c\u4f60\u5973\u513f\u8fd8\u4e86\u4f60\u7f62\uff01\u201d\u626c\u624b\u5c06\u6c34\u7b19\u51cc\u7a7a\u629b\u51fa\uff0c\u8d8a\u8fc7\u6c34\u5cb1\u5934\u9876\uff0c\u5411\u72c4\u4e91\u63b7\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u8fd9\u4e00\u4e0b\u8c01\u90fd\u5927\u51fa\u610f\u6599\u4e4b\u5916\uff0c\u6c34\u7b19\u8eab\u5728\u534a\u7a7a\uff0c\u56fa\u7136\u5c16\u58f0\u60ca\u547c\uff0c\u65c1\u4eba\u4e5f\u662f\u4e0d\u7ea6\u800c\u540c\u7684\u5927\u53eb\u8d77\u6765\u3002 \u3000\u3000\u72c4\u4e91\u89c1\u6c34\u7b19\u5411\u81ea\u5df1\u98de\u6765\uff0c\u52bf\u9053\u52b2\u6025\uff0c\u82e5\u4e0d\u63a5\u4f4f\uff0c\u52bf\u987b\u843d\u5730\u53d7\u4f24\uff0c\u5fd9\u5f20\u81c2\u62b1\u4f4f\u3002\u8fd9\u4e00\u63b7\u529b\u9053\u672c\u91cd\uff0c\u5e78\u597d\u72c4\u4e91\u8eab\u5728\u9a6c\u4e0a\uff0c\u5927\u534a\u529b\u9053\u7531\u9a6c\u5339\u627f\u53d7\u4e86\u53bb\u3002\u8840\u5200\u8001\u7956\u5c06\u6c34\u7b19\u63b7\u51fa\u4e4b\u65f6\uff0c\u5df2\u5148\u70b9\u4e86\u5979\u7a74\u9053\uff0c\u662f\u4ee5\u5979\u53ea\u6709\u542c\u4efb\u6446\u5e03\uff0c\u65e0\u529b\u53cd\u6297\uff0c\u5927\u53eb\uff1a\u201c\u5c0f\u548c\u5c1a\uff0c\u653e\u5f00\u6211\uff01\u201d \u3000\u3000\u8840\u5200\u8001\u7956\u5411\u6c34\u5cb1\u75be\u780d\u4e24\u5200\uff0c\u53c8\u5411\u90a3\u8001\u9053\u731b\u780d\u4e24\u5200\uff0c\u90fd\u662f\u53ea\u653b\u4e0d\u5b88\uff0c\u6781\u5176\u51cc\u5389\u7684\u62db\u6570\uff0c\u53eb\u9053\uff1a\u201c\u72c4\u4e91\u4e56\u5b69\u513f\uff0c\u5feb\u9003\uff0c\u5feb\u9003\uff0c\u4e0d\u7528\u7b49\u6211\u3002\u201d \u3000\u3000\u72c4\u4e91\u8ff7\u8ff7\u60d8\u60d8\u7684\u624b\u8db3\u65e0\u63aa\uff0c\u4f46\u89c1\u6c6a\u5578\u98ce\u548c\u53e6\u5916\u6570\u4eba\u5404\u633a\u5175\u5203\uff0c\u5927\u547c\u201c\u6740\u4e86\u5c0f\u6deb\u50e7\u201d\uff0c\u5feb\u6b65\u8d76\u6765\uff0c\u800c\u8840\u5200\u8001\u7956\u53c8\u5728\u8fde\u58f0\u50ac\u4fc3\uff1a\u201c\u5feb\u9003\uff0c\u5feb\u9003\uff01\u201d\u5f53\u5373\u4e00\u63d0\u7f30\u7ef3\uff0c\u7eb5\u9a6c\u51b2\u4e86\u51fa\u53bb\u3002\u672c\u6765\u4ed6\u548c\u8840\u5200\u8001\u7956\u7eb5\u9a6c\u5411\u4e1c\uff0c\u8fd9\u65f6\u614c\u614c\u5f20\u5f20\uff0c\u53cd\u800c\u5411\u897f\u9a70\u53bb\u3002 \u3000\u3000\u8840\u5200\u8001\u7956\u4e00\u53e3\u8840\u5200\u8d8a\u4f7f\u8d8a\u5feb\uff0c\u4e00\u56e2\u56e2\u7ea2\u5f71\u7b3c\u7f69\u4e86\u5168\u8eab\uff0c\u7b11\u9053\uff1a\u201c\u6211\u8981\u966a\u4f60\u7684\u7f8e\u8c8c\u5973\u513f\u53bb\uff0c\u4e0d\u966a\u4f60\u8fd9\u7cdf\u8001\u5934\u513f\u4e86\u3002\u201d\u53cc\u817f\u4e00\u631f\uff0c\u80ef\u4e0b\u5750\u9a91\u817e\u7a7a\u800c\u8d77\uff0c\u5411\u524d\u8dc3\u51fa\u3002 \u3000\u3000\u6c34\u5cb1\u6551\u5973\u60c5\u6025\uff0c\u4e0d\u613f\u591a\u8ddf\u4ed6\u7ea0\u7f20\uff0c\u65bd\u5c55\u201c\u767b\u840d\u6e21\u6c34\u201d\u8f7b\u529f\uff0c\u8eab\u5b50\u4fbf\u5982\u5728\u6c34\u4e0a\u98d8\u884c\u4e00\u822c\uff0c\u5411\u72c4\u4e91\u75be\u8ffd\u3002\u53ef\u662f\u72c4\u4e91\u80ef\u4e0b\u6240\u4e58\uff0c\u6b63\u662f\u6c34\u5cb1\u5f53\u5e74\u82b1\u4e86\u4e94\u767e\u4e24\u94f6\u5b50\u8d2d\u6765\u7684\u5927\u5b9b\u826f\u9a6c\uff0c\u811a\u7a0b\u4e4b\u5feb\uff0c\u9664\u4e86\u8840\u5200\u8001\u7956\u6240\u4e58\u7684\u90a3\u5339\u767d\u9a6c\uff0c\u5f53\u4e16\u7f55\u6709\u5176\u4f26\u3002\u9ec4\u9a6c\u80cc\u4e0a\u867d\u4e58\u7740\u4e24\u4eba\uff0c\u6c34\u5cb1\u5374\u5140\u81ea\u8ffd\u8d76\u4e0d\u4e0a\u3002\u6c34\u5cb1\u5927\u53eb\uff1a\u201c\u505c\u6b65\uff0c\u505c\u6b65\uff01\u201d\u90a3\u9a6c\u8bc6\u5f97\u4ed6\u58f0\u97f3\uff0c\u4f46\u80cc\u4e0a\u72c4\u4e91\u6b63\u81ea\u63d0\u7f30\u529b\u63a8\uff0c\u7adf\u4e0d\u80fd\u505c\u6b65\u3002\u6c34\u5cb1\u53eb\u9053\uff1a\u201c\u5c0f\u6076\u50e7\uff0c\u4f60\u518d\u4e0d\u52d2\u9a6c\uff0c\u8001\u5b50\u628a\u4f60\u65a9\u6210\u5341\u4e03\u516b\u5757\uff01\u201d\u6c34\u7b19\u53eb\u9053\uff1a\u201c\u7239\u7239\uff0c\u7239\u7239\uff01\u201d\u6c34\u5cb1\u5fc3\u75db\u5982\u5272\uff0c\u53eb\u9053\uff1a\u201c\u5b69\u513f\u522b\u614c\uff01\u201d \u3000\u3000\u9877\u523b\u4e4b\u95f4\uff0c\u4e00\u9a6c\u4e00\u4eba\u8ffd\u51fa\u4e86\u91cc\u8bb8\uff0c\u6c34\u5cb1\u867d\u8f7b\u529f\u4e86\u5f97\uff0c\u4f46\u65f6\u523b\u4e00\u4e45\uff0c\u6bd5\u7adf\u5e74\u7eaa\u8001\u4e86\uff0c\u957f\u529b\u4e0d\u6d4e\uff0c\u548c\u9ec4\u9a6c\u76f8\u8ddd\u8d8a\u6765\u8d8a\u8fdc\uff0c\u5ffd\u542c\u5f97\u547c\u7684\u4e00\u54cd\uff0c\u80cc\u540e\u91d1\u5203\u5288\u98ce\u3002\u4ed6\u53cd\u624b\u8ff4\u5251\uff0c\u67b6\u5f00\u4e86\u8840\u5200\u8001\u7956\u780d\u6765\u7684\u4e00\u5200\uff0c\u4e00\u9635\u98ce\u4ece\u8eab\u65c1\u63a0\u8fc7\uff0c\u8840\u5200\u8001\u7956\u54c8\u54c8\u5927\u7b11\uff0c\u9a91\u4e86\u767d\u9a6c\u8ffd\u7740\u72c4\u4e91\u53bb\u4e86\u3002 \u3000\u3000\u8840\u5200\u8001\u7956\u548c\u72c4\u4e91\u5feb\u5954\u4e00\u9635\uff0c\u5c06\u8ffd\u654c\u8fdc\u8fdc\u629b\u5728\u540e\u9762\uff0c\u773c\u89c1\u518d\u4e5f\u8ffd\u8d76\u4e0d\u4e0a\uff0c\u8840\u5200\u8001\u7956\u751f\u6015\u8dd1\u4f24\u4e86\u5750\u9a91\uff0c\u8fd9\u624d\u62db\u547c\u72c4\u4e91\u6309\u8f94\u5f90\u884c\u3002\u8840\u5200\u8001\u7956\u6ca1\u53e3\u5b50\u79f0\u8d5e\u72c4\u4e91\u6709\u826f\u5fc3\uff0c\u867d\u89c1\u60c5\u52bf\u5371\u6025\u4e4b\u6781\uff0c\u4ecd\u662f\u4e0d\u80af\u5148\u9003\u3002 \u3000\u3000\u72c4\u4e91\u53ea\u6709\u82e6\u7b11\uff0c\u659c\u773c\u770b\u6c34\u7b19\u65f6\uff0c\u89c1\u5979\u8138\u4e0a\u795e\u8272\u6050\u60e7\u4e2d\u6df7\u7740\u9119\u5937\uff0c\u77e5\u5979\u75db\u6068\u81ea\u5df1\u5df2\u6781\uff0c\u8fd9\u4e8b\u53cd\u6b63\u65e0\u4ece\u89e3\u91ca\uff0c\u5fc3\u9053\uff1a\u201c\u4f60\u7231\u600e\u4e48\u60f3\u4fbf\u600e\u4e48\u60f3\uff0c\u8981\u9a82\u6211\u6deb\u50e7\u6076\u8d3c\uff0c\u5c3d\u7ba1\u5927\u9a82\u4fbf\u662f\u3002\u201d \u3000\u3000\u8840\u5200\u8001\u7956\u9053\uff1a\u201c\u5582\uff0c\u5c0f\u599e\u513f\uff0c\u4f60\u7239\u7239\u7684\u6b66\u529f\u5f88\u4e0d\u574f\u554a\uff01\u563f\u563f\uff0c\u53ef\u662f\u4f60\u7956\u5e08\u7237\u6bd4\u4f60\u7239\u7239\u53c8\u80dc\u4e86\u4e00\u7b79\uff0c\u4ed6\u4f7f\u5c3d\u4e86\u5403\u5976\u7684\u529b\u6c14\uff0c\u4ecd\u662f\u62e6\u4e0d\u4f4f\u6211\u3002\u201d\u6c34\u7b19\u6068\u6068\u7684\u77aa\u4e86\u4ed6\u4e00\u773c\uff0c\u5e76\u4e0d\u4f5c\u58f0\u3002\u8840\u5200\u8001\u7956\u9053\uff1a\u201c\u90a3\u4f7f\u5251\u7684\u8001\u9053\u662f\u8c01\uff1f\u662f\u2018\u843d\u82b1\u6d41\u6c34\u2019\u4e2d\u7684\u54ea\u4e00\u4e2a\uff1f\u201d \u3000\u3000\u6c34\u7b19\u6253\u5b9a\u4e86\u4e3b\u610f\uff0c\u4e0d\u7ba1\u4ed6\u95ee\u751a\u4e48\uff0c\u603b\u662f\u7ed9\u4ed6\u4e2a\u4e0d\u7406\u4e0d\u776c\u3002 \u3000\u3000\u8840\u5200\u8001\u7956\u7b11\u9053\uff1a\u201c\u5f92\u5b59\u513f\uff0c\u5973\u4eba\u5bb6\u6700\u5b9d\u8d35\u7684\u662f\u751a\u4e48\u4e1c\u897f\uff1f\u201d \u3000\u3000\u72c4\u4e91\u5413\u4e86\u4e00\u8df3\uff0c\u5fc3\u9053\uff1a\u201c\u554a\u54df\uff0c\u4e0d\u597d\uff01\u8fd9\u8001\u548c\u5c1a\u8981\u73b7\u6c61\u6c34\u59d1\u5a18\u7684\u6e05\u767d\uff1f\u6211\u600e\u5730\u76f8\u6551\u624d\u597d\uff1f\u201d\u53e3\u4e2d\u53ea\u5f97\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u9053\u3002\u201d\u8840\u5200\u8001\u7956\u9053\uff1a\u201c\u5973\u4eba\u5bb6\u6700\u5b9d\u8d35\u7684\uff0c\u662f\u5979\u7684\u8138\u86cb\u3002\u8fd9\u5c0f\u599e\u513f\u4e0d\u56de\u7b54\u6211\u7684\u8bf4\u8bdd\uff0c\u6211\u7528\u5200\u5728\u5979\u8138\u4e0a\u6a2a\u5212\u4e03\u5200\uff0c\u7ad6\u780d\u516b\u5200\uff0c\u8fd9\u4e00\u62db\u6709\u4e2a\u540d\u5802\uff0c\u53eb\u505a\u2018\u6a2a\u4e03\u7ad6\u516b\u2019\uff0c\u4f60\u8bf4\u7f8e\u662f\u4e0d\u7f8e\uff1f\u201d\u8bf4\u7740\u5237\u7684\u4e00\u58f0\uff0c\u5c06\u672c\u5df2\u76d8\u5728\u8170\u95f4\u7684\u8840\u5200\u64ce\u5728\u624b\u4e2d\u3002 \u3000\u3000\u6c34\u7b19\u65e9\u5c31\u62da\u7740\u4e00\u6b7b\uff0c\u4e0d\u518d\u6253\u4fa5\u5e78\u751f\u8fd8\u7684\u4e3b\u610f\uff0c\u4f46\u60f3\u5230\u81ea\u5df1\u767d\u7389\u65e0\u7455\u7684\u8138\u86cb\u8981\u88ab\u8fd9\u6076\u50e7\u5212\u5f97\u6a2a\u4e03\u7ad6\u516b\uff0c\u5fcd\u4e0d\u4f4f\u6253\u4e2a\u5bd2\u5664\uff0c\u8f6c\u5ff5\u53c8\u60f3\uff0c\u4ed6\u82e5\u6bc1\u4e86\u81ea\u5df1\u5bb9\u8c8c\uff0c\u8bf4\u4e0d\u5b9a\u5012\u53ef\u4fdd\u5f97\u8eab\u5b50\u6e05\u767d\u800c\u6b7b\uff0c\u53cd\u800c\u662f\u4e0d\u5e78\u4e2d\u7684\u5927\u5e78\u4e86\u3002 \u3000\u3000\u8840\u5200\u8001\u7956\u5c06\u4e00\u628a\u5f2f\u5200\u5728\u5979\u8138\u8fb9\u6643\u6765\u6643\u53bb\uff0c\u5a01\u5413\u9053\uff1a\u201c\u6211\u95ee\u4f60\u90a3\u8001\u9053\u662f\u8c01\uff1f\u4f60\u518d\u4e0d\u7b54\u8bdd\uff0c\u6211\u4e00\u5200\u4fbf\u5212\u5c06\u4e0b\u6765\u4e86\u3002\u4f60\u7b54\u4e0d\u7b54\u8bdd\uff1f\u201d\u6c34\u7b19\u6012\u9053\uff1a\u201c\u5478\uff01\u4f60\u5feb\u6740\u4e86\u59d1\u5a18\uff01\u201d\u8840\u5200\u8001\u7956\u53f3\u624b\u4e00\u843d\uff0c\u7ea2\u5f71\u95ea\u5904\uff0c\u5728\u5979\u8138\u4e0a\u5272\u4e86\u4e00\u5200\u3002 \u3000\u3000\u72c4\u4e91\u201c\u554a\u201d\u7684\u4e00\u58f0\u8f7b\u547c\uff0c\u8f6c\u8fc7\u4e86\u5934\u3002\u4e0d\u5fcd\u89c2\u770b\u3002\u6c34\u7b19\u5df2\u81ea\u6655\u4e86\u8fc7\u53bb\u3002\u8840\u5200\u8001\u7956\u54c8\u54c8\u5927\u7b11\uff0c\u50ac\u9a6c\u524d\u884c\u3002\u72c4\u4e91\u5fcd\u4e0d\u4f4f\u8f6c\u5934\u77a7\u6c34\u7b19\u65f6\uff0c\u53ea\u89c1\u5979\u7c89\u8138\u65e0\u6059\uff0c\u8fde\u4e00\u6761\u75d5\u5370\u4e5f\u65e0\uff0c\u4e0d\u7531\u5f97\u5fc3\u4e2d\u4e00\u559c\uff0c\u624d\u77e5\u8840\u5200\u8001\u7956\u5200\u6cd5\u4e4b\u7cbe\uff0c\u5b9e\u5df2\u5230\u4e86\u4ece\u5fc3\u6240\u6b32\u3001\u4e0d\u5dee\u5398\u6beb\u7684\u5730\u6b65\u3002\u9002\u624d\u8fd9\u4e00\u5200\uff0c\u5200\u950b\u4ece\u6c34\u7b19\u988a\u8fb9\u4e00\u63a0\u800c\u8fc7\uff0c\u53ea\u5272\u4e0b\u5979\u9b13\u8fb9\u51e0\u7f15\u79c0\u53d1\uff0c\u808c\u80a4\u5374\u7edd\u65e0\u635f\u4f24\u3002 \u3000\u3000\u6c34\u7b19\u60a0\u60a0\u9192\u8f6c\uff0c\u773c\u6cea\u593a\u7736\u800c\u51fa\uff0c\u773c\u89c1\u5230\u72c4\u4e91\u7684\u7b11\u5bb9\uff0c\u66f4\u662f\u6c14\u607c\uff0c\u9a82\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u2026\u2026\u4f60\u8fd9\u5e78\u707e\u4e50\u7978\u7684\u574f\u2026\u2026\u574f\u2026\u2026\u574f\u4eba\u3002\u201d\u5979\u672c\u60f3\u7528\u4e00\u53e5\u6700\u5389\u5bb3\u7684\u8bdd\u6765\u9a82\u4ed6\uff0c\u4f46\u5979\u5e73\u7d20\u4ece\u6765\u4e0d\u8bf4\u7c97\u4fd7\u7684\u8a00\u8bed\uff0c\u4e00\u65f6\u7adf\u60f3\u4e0d\u51fa\u751a\u4e48\u51f6\u72e0\u6076\u6bd2\u7684\u53e5\u5b50\u6765\u3002 \u3000\u3000\u8840\u5200\u8001\u7956\u5f2f\u5200\u4e00\u4e3e\uff0c\u559d\u9053\uff1a\u201c\u4f60\u4e0d\u56de\u7b54\uff0c\u7b2c\u4e8c\u5200\u53c8\u5272\u5c06\u4e0b\u6765\u4e86\u3002\u201d\u6c34\u7b19\u5fc3\u60f3\u53cd\u6b63\u4e00\u5200\u5df2\u7136\u5272\u4e86\uff0c\u518d\u5272\u51e0\u5200\u4e5f\u662f\u4e00\u6837\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u5feb\u6740\u4e86\u6211\uff0c\u5feb\u6740\u4e86\u6211\uff01\u201d\u8840\u5200\u8001\u7956\u72de\u7b11\u9053\uff1a\u201c\u54ea\u6709\u8fd9\u4e48\u5bb9\u6613\uff1f\u201d\u55e4\u7684\u4e00\u58f0\u8f7b\u54cd\uff0c\u5200\u950b\u53c8\u4ece\u5979\u988a\u8fb9\u63a0\u8fc7\u3002 \u3000\u3000\u8fd9\u4e00\u6b21\u6c34\u7b19\u6ca1\u5931\u53bb\u77e5\u89c9\uff0c\u4f46\u89c9\u988a\u4e0a\u5fae\u5fae\u4e00\u51c9\uff0c\u5374\u4e0d\u611f\u75bc\u75db\uff0c\u53c8\u65e0\u9c9c\u8840\u6d41\u4e0b\uff0c\u624d\u77e5\u8fd9\u8001\u6076\u50e7\u53ea\u662f\u5413\u4eba\uff0c\u539f\u6765\u81ea\u5df1\u8138\u988a\u65e0\u635f\uff0c\u5fc3\u5934\u4e00\u559c\uff0c\u5fcd\u4e0d\u4f4f\u5401\u4e86\u53e3\u957f\u6c14\u3002 \u3000\u3000\u8840\u5200\u8001\u7956\u5411\u72c4\u4e91\u9053\uff1a\u201c\u4e56\u5f92\u5b59\uff0c\u7237\u7237\u8fd9\u4e24\u5200\u780d\u5f97\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u72c4\u4e91\u9053\uff1a\u201c\u5200\u6cd5\u9ad8\u6781\u5566\uff0c\u5f53\u771f\u4e86\u5f97\uff01\u201d\u8fd9\u4e24\u53e5\u8bdd\u786e\u662f\u7531\u8877\u4e4b\u8a00\u3002 \u3000\u3000\u8840\u5200\u8001\u7956\u9053\uff1a\u201c\u4f60\u8981\u4e0d\u8981\u5b66\uff1f\u201d\u72c4\u4e91\u5fc3\u5ff5\u4e00\u52a8\uff1a\u201c\u6211\u6b63\u60f3\u4e0d\u51fa\u6cd5\u5b50\u6765\u4fdd\u5168\u6c34\u59d1\u5a18\u7684\u6e05\u767d\uff0c\u82e5\u662f\u6211\u7f20\u4f4f\u8001\u548c\u5c1a\u5b66\u6b66\u827a\uff0c\u53ea\u8981\u4ed6\u80af\u7528\u5fc3\u6559\u6211\uff0c\u6ca1\u529f\u592b\u522b\u8d77\u90aa\u5ff5\uff0c\u6211\u5c31\u597d\u60f3\u6cd5\u5b50\u6551\u4eba\u3002\u53ef\u662f\u90a3\u975e\u8ba8\u5f97\u4ed6\u6b22\u559c\u4e0d\u53ef\u3002\u201d\u4fbf\u9053\uff1a\u201c\u4f60\u8fd9\u5200\u4e0a\u529f\u592b\uff0c\u5f92\u5b59\u513f\u7fa1\u6155\u5f97\u4e86\u4e0d\u5f97\u3002\u4f60\u6559\u5f97\u6211\u51e0\u62db\uff0c\u65e5\u540e\u9047\u4e0a\u5979\u8868\u54e5\u4e4b\u6d41\u7684\u5c0f\u8f88\uff0c\u4fbf\u4e0d\u4f1a\u518d\u53d7\u4ed6\u6b3a\u4fae\uff0c\u4e5f\u514d\u5f97\u6298\u4e86\u4f60\u5e08\u7956\u7237\u7237\u7684\u5a01\u98ce\u3002\u201d\u4ed6\u751f\u5e73\u6781\u96be\u5f97\u8bf4\u8c0e\uff0c\u8fd9\u65f6\u4e3a\u4e86\u6551\u4eba\uff0c\u8fd9\u53e5\u201c\u5e08\u7956\u7237\u7237\u201d\u4e00\u51fa\u53e3\uff0c\u81ea\u5df1\u4e5f\u89c9\u8089\u9ebb\uff0c\u4e0d\u7531\u5f97\u6ee1\u8138\u901a\u7ea2\u3002 \u3000\u3000\u6c34\u7b19\u201c\u5478\u201d\u4e86\u4e00\u58f0\uff0c\u9a82\u9053\uff1a\u201c\u4e0d\u8981\u8138\uff0c\u4e0d\u5bb3\u7f9e\uff01\u201d \u3000\u3000\u8840\u5200\u8001\u7956\u5927\u662f\u5f00\u5fc3\uff0c\u7b11\u9053\uff1a\u201c\u6211\u8fd9\u8840\u5200\u529f\u592b\uff0c\u975e\u4e00\u671d\u4e00\u5915\u6240\u80fd\u5b66\u4f1a\uff0c\u597d\u7f62\uff0c\u6211\u5148\u4f20\u4f60\u4e00\u62db\u2018\u6279\u7eb8\u524a\u8150\u2019\u7684\u529f\u592b\u3002\u4f60\u4e60\u7ec3\u4e4b\u65f6\uff0c\u5148\u7528\u4e00\u767e\u5f20\u8584\u7eb8\uff0c\u53e0\u6210\u4e00\u53e0\uff0c\u653e\u5728\u684c\u4e0a\uff0c\u4e00\u5200\u6a2a\u524a\u8fc7\u53bb\uff0c\u5c06\u4e00\u53e0\u7eb8\u4e0a\u7684\u7b2c\u4e00\u5f20\u6279\u4e86\u4e0b\u6765\uff0c\u53ef\u4e0d\u8bb8\u5e26\u52a8\u7b2c\u4e8c\u5f20\u3002\u7136\u540e\u7b2c\u4e8c\u5200\u6279\u7b2c\u4e8c\u5f20\uff0c\u7b2c\u4e09\u5200\u6279\u7b2c\u4e09\u5f20\uff0c\u76f4\u5230\u7b2c\u4e00\u767e\u5f20\u7eb8\u6279\u5b8c\u3002\u201d \u3000\u3000\u6c34\u7b19\u662f\u5c11\u5e74\u4eba\u7684\u5fc3\u6027\uff0c\u5fcd\u4e0d\u4f4f\u63d2\u53e3\u9053\uff1a\u201c\u5439\u725b\uff01\u201d \u3000\u3000\u8840\u5200\u8001\u7956\u7b11\u9053\uff1a\u201c\u4f60\u8bf4\u5439\u725b\uff0c\u54b1\u4eec\u5c31\u8bd5\u4e0a\u4e00\u8bd5\u3002\u201d\u4f38\u624b\u5230\u5979\u5934\u4e0a\u62d4\u4e0b\u4e00\u6839\u5934\u53d1\u3002\u6c34\u7b19\u5fae\u5fae\u5403\u75db\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u5e72\u751a\u4e48\uff1f\u201d\u8840\u5200\u8001\u7956\u4e0d\u53bb\u7406\u5979\uff0c\u5c06\u90a3\u6839\u5934\u53d1\u653e\u5728\u5979\u9f3b\u5c16\u4e0a\uff0c\u7eb5\u9a6c\u5feb\u5954\u3002 \u3000\u3000\u5176\u65f6\u6c34\u7b19\u8737\u66f2\u7740\u8eab\u5b50\uff0c\u6a2a\u5367\u5728\u72c4\u4e91\u8eab\u524d\u7684\u9a6c\u4e0a\uff0c\u89c1\u8840\u5200\u8001\u7956\u5c06\u5934\u53d1\u653e\u5728\u81ea\u5df1\u9f3b\u5c16\uff0c\u5fae\u611f\u9ebb\u75d2\uff0c\u4e0d\u77e5\u4ed6\u6363\u751a\u4e48\u9b3c\uff0c\u6b63\u8981\u5f20\u5634\u547c\u6c14\u5c06\u5934\u53d1\u5439\u5f00\uff0c\u53ea\u542c\u8840\u5200\u8001\u7956\u53eb\u9053\uff1a\u201c\u522b\u52a8\uff0c\u77a7\u6e05\u695a\u4e86\uff01\u201d\u4ed6\u52d2\u8f6c\u9a6c\u5934\uff0c\u56de\u5954\u8fc7\u6765\uff0c\u53cc\u9a6c\u76f8\u4ea4\uff0c\u4e00\u64e6\u800c\u8fc7\u3002 \u3000\u3000\u6c34\u7b19\u53ea\u89c9\u773c\u524d\u7ea2\u5149\u95ea\u52a8\uff0c\u9f3b\u5c16\u4e0a\u5fae\u5fae\u4e00\u51c9\uff0c\u968f\u5373\u89c9\u5230\u653e\u5728\u9f3b\u4e0a\u7684\u90a3\u6839\u5934\u53d1\u5df2\u4e0d\u5728\u4e86\u3002\u53ea\u542c\u5f97\u72c4\u4e91\u5927\u53eb\uff1a\u201c\u5999\u6781\uff01\u5999\u6781\uff01\u201d \u3000\u3000\u8840\u5200\u8001\u7956\u4f38\u8fc7\u8840\u5200\uff0c\u4f46\u89c1\u5200\u5203\u4e0a\u5e73\u5e73\u653e\u7740\u90a3\u6839\u5934\u53d1\u3002\u8840\u5200\u8001\u7956\u548c\u72c4\u4e91\u90fd\u662f\u5149\u5934\uff0c\u8fd9\u6839\u67d4\u8f6f\u7684\u957f\u53d1\u81ea\u662f\u6c34\u7b19\u4e4b\u7269\uff0c\u518d\u4e5f\u5047\u5192\u4e0d\u6765\u3002 \u3000\u3000\u6c34\u7b19\u53c8\u60ca\u53c8\u4f69\uff0c\u5fc3\u60f3\uff1a\u201c\u8fd9\u8001\u548c\u5c1a\u6b66\u529f\u771f\u9ad8\uff0c\u521a\u624d\u4ed6\u8fd9\u4e00\u5200\u82e5\u662f\u9ad8\u5f97\u534a\u5206\uff0c\u8fd9\u6839\u5934\u53d1\u4fbf\u6279\u4e0d\u5230\u5200\u4e0a\uff0c\u82e5\u662f\u4f4e\u5f97\u534a\u5206\uff0c\u6211\u8fd9\u9f3b\u5c16\u4fbf\u7ed9\u4ed6\u524a\u53bb\u4e86\u3002\u4ed6\u9a70\u9a6c\u6325\u5200\uff0c\u90a3\u6bd4\u4e4b\u6279\u8584\u7eb8\u751a\u4e48\u7684\u66f4\u662f\u96be\u4e0a\u767e\u500d\u3002\u201d \u3000\u3000\u72c4\u4e91\u8981\u8ba8\u8840\u5200\u8001\u7956\u559c\u6b22\uff0c\u8c00\u8bcd\u6eda\u6eda\u800c\u51fa\uff0c\u53ea\u4e0d\u8fc7\u4ed6\u53e3\u9f7f\u7b28\u62d9\uff0c\u7ffb\u6765\u8986\u53bb\u4e5f\u4e0d\u8fc7\u662f\u51e0\u53e5\u201c\u5200\u6cd5\u771f\u597d\uff01\u6211\u53ef\u4ece\u6765\u6ca1\u89c1\u8fc7\u201d\u4e4b\u7c7b\u3002\u6c34\u7b19\u4eb2\u8eab\u9886\u7565\u4e86\u8fd9\u8840\u5200\u795e\u6280\uff0c\u518d\u542c\u5230\u72c4\u4e91\u7684\u606d\u7ef4\uff0c\u4e5f\u5df2\u4e0d\u89c9\u8fc7\u5206\uff0c\u53ea\u662f\u89c9\u5f97\u8fd9\u4eba\u4e3a\u4e86\u8ba8\u597d\u5e08\u7956\uff0c\u9a6c\u5c41\u62cd\u5230\u8fd9\u7b49\u5730\u6b65\uff0c\u4eba\u683c\u592a\u8fc7\u5351\u9119\u3002 \u3000\u3000\u8840\u5200\u8001\u7956\u52d2\u8f6c\u9a6c\u5934\uff0c\u53c8\u548c\u72c4\u4e91\u5e76\u9a91\u800c\u884c\uff0c\u8bf4\u9053\uff1a\u201c\u81f3\u4e8e\u90a3\u2018\u524a\u8150\u2019\u5462\uff0c\u662f\u7528\u4e00\u5757\u8c46\u8150\u653e\u5728\u6728\u677f\u4e4b\u4e0a\uff0c\u4e00\u5200\u5200\u7684\u524a\u8584\u5b83\uff0c\u8981\u5c06\u4e24\u5bf8\u539a\u7684\u4e00\u5757\u8c46\u8150\u524a\u6210\u4e8c\u5341\u7247\uff0c\u6bcf\u4e00\u7247\u90fd\u5b8c\u6574\u4e0d\u7834\uff0c\u8fd9\u4e00\u62db\u529f\u592b\u4fbf\u7b97\u521d\u6b65\u5c0f\u6210\u4e86\u3002\u201d\u72c4\u4e91\u9053\uff1a\u201c\u90a3\u8fd8\u53ea\u521d\u6b65\u5c0f\u6210\uff1f\u201d\u8840\u5200\u8001\u7956\u9053\uff1a\u201c\u5f53\u7136\u4e86\uff01\u4f60\u60f3\uff0c\u7a33\u7a33\u7684\u7ad9\u7740\u524a\u8c46\u8150\u96be\u5462\uff0c\u8fd8\u662f\u9a70\u9a6c\u6025\u51b2\u3001\u5728\u599e\u513f\u9f3b\u5c16\u4e0a\u524a\u5934\u53d1\u96be\uff1f\u54c8\u54c8\uff0c\u54c8\u54c8\uff01\u201d\u72c4\u4e91\u53c8\u606d\u7ef4\u9053\uff1a\u201c\u5e08\u7956\u7237\u7237\u5929\u751f\u7684\u5927\u672c\u4e8b\uff0c\u4e0d\u662f\u5e38\u4eba\u6240\u80fd\u53ca\u7684\uff0c\u5f92\u5b59\u513f\u53ea\u8981\u7ec3\u5230\u5e08\u7956\u7237\u7237\u5341\u5206\u4e4b\u4e00\uff0c\u4e5f\u5c31\u5fc3\u6ee1\u610f\u8db3\u4e86\uff01\u201d\u8840\u5200\u8001\u7956\u54c8\u54c8\u5927\u7b11\u3002\u6c34\u7b19\u5219\u9a82\uff1a\u201c\u8089\u9ebb\uff0c\u5351\u9119\uff01\u201d \u3000\u3000\u8981\u72c4\u4e91\u8fd9\u8001\u5b9e\u4eba\u8bf4\u8fd9\u4e9b\u6cb9\u8154\u6ed1\u8c03\u7684\u8a00\u8bed\uff0c\u539f\u662f\u9887\u4e0d\u5bb9\u6613\uff0c\u4f46\u81ea\u6765\u62cd\u9a6c\u5c41\u7684\u8bdd\u7b2c\u4e00\u53e5\u6700\u96be\u51fa\u53e3\uff0c\u8bf4\u5f97\u591a\u4e86\uff0c\u5c45\u7136\u4e5f\u987a\u6e9c\u8d77\u6765\u3002\u597d\u5728\u8840\u5200\u8001\u7956\u786e\u6709\u4eba\u6240\u96be\u80fd\u7684\u6b66\u529f\uff0c\u72c4\u4e91\u8fd9\u4e9b\u8d5e\u8a89\u5012\u4e5f\u4e0d\u662f\u8fdd\u5fc3\u4e4b\u8bba\uff0c\u53ea\u4e0d\u8fc7\u4f9d\u4ed6\u672c\u6027\uff0c\u51b3\u4e0d\u80af\u5982\u6b64\u5ba3\u4e4b\u4e8e\u53e3\u800c\u5df2\u3002 \u3000\u3000\u8840\u5200\u8001\u7956\u9053\uff1a\u201c\u4f60\u8d44\u8d28\u4e0d\u9519\uff0c\u53ea\u8981\u80af\u4e0b\u82e6\u529f\uff0c\u8fd9\u529f\u592b\u662f\u5b66\u5f97\u4f1a\u7684\u3002\u597d\uff0c\u4f60\u6765\u8bd5\u8bd5\uff01\u201d\u8bf4\u7740\u4f38\u624b\u53c8\u62d4\u4e0b\u6c34\u7b19\u4e00\u6839\u5934\u53d1\uff0c\u653e\u4e0a\u5979\u9f3b\u5c16\u4e0a\u3002\u6c34\u7b19\u5927\u60ca\uff0c\u4e00\u53e3\u6c14\u4fbf\u5c06\u5934\u53d1\u5439\u5f00\uff0c\u53eb\u9053\uff1a\u201c\u8fd9\u5c0f\u548c\u5c1a\u4e0d\u4f1a\u7684\uff0c\u600e\u80fd\u8ba9\u4ed6\u80e1\u8bd5\uff1f\u201d \u3000\u3000\u8840\u5200\u8001\u7956\u9053\uff1a\u201c\u529f\u592b\u4e0d\u7ec3\u5c31\u4e0d\u4f1a\uff0c\u4e00\u6b21\u4e0d\u6210\uff0c\u518d\u6765\u4e00\u6b21\uff0c\u4e24\u6b21\u4e0d\u6210\uff0c\u4fbf\u7ec3\u4ed6\u4e2a\u5341\u6b21\u516b\u6b21\uff01\u201d\u8bf4\u7740\u53c8\u62d4\u4e86\u5979\u4e00\u6839\u5934\u53d1\uff0c\u653e\u5728\u5979\u7684\u9f3b\u5c16\uff0c\u5c06\u8840\u5200\u4ea4\u7ed9\u72c4\u4e91\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u8bd5\u8bd5\u770b\uff01\u201d \u3000\u3000\u72c4\u4e91\u63a5\u8fc7\u8840\u5200\uff0c\u5411\u6a2a\u5367\u5728\u8eab\u524d\u7684\u6c34\u7b19\u77a7\u4e86\u4e00\u773c\uff0c\u89c1\u5979\u6ee1\u8138\u90fd\u662f\u6124\u6068\u607c\u6012\u4e4b\u8272\uff0c\u4f46\u773c\u5149\u4e4b\u4e2d\uff0c\u7ec8\u4e8e\u6d41\u9732\u51fa\u4e86\u6050\u60e7\u7684\u795e\u8272\u3002 \u3000\u3000\u5979\u77e5\u72c4\u4e91\u4ece\u672a\u7ec3\u8fc7\u8fd9\u95e8\u5200\u6cd5\uff0c\u5982\u679c\u7167\u7740\u8840\u5200\u8001\u7956\u7684\u6a21\u6837\uff0c\u5c06\u8fd9\u5229\u5203\u4ece\u81ea\u5df1\u9f3b\u5c16\u4e0a\u63a0\u8fc7\uff0c\u522b\u8bf4\u9f3b\u5b50\u5b9a\u7136\u88ab\u4ed6\u4e00\u5200\u524a\u53bb\uff0c\u591a\u534a\u8fde\u8111\u888b\u4e5f\u7ed9\u5288\u6210\u4e24\u534a\u3002\u5979\u5fc3\u4e0b\u81ea\u6170\uff1a\u201c\u8fd9\u6837\u4e5f\u597d\uff0c\u6b7b\u5728\u8fd9\u5c0f\u6076\u50e7\u7684\u5200\u4e0b\uff0c\u80dc\u4e8e\u53d7\u4ed6\u4e8c\u4eba\u7684\u4fae\u8fb1\u3002\u201d\u8bdd\u867d\u5982\u6b64\uff0c\u60f3\u5230\u771f\u7684\u8981\u6b7b\uff0c\u5374\u4e5f\u4e0d\u514d\u5bb3\u6015\u3002 \u3000\u3000\u72c4\u4e91\u81ea\u7136\u4e0d\u6562\u8d38\u7136\u4fbf\u5288\uff0c\u95ee\u9053\uff1a\u201c\u5e08\u7956\u7237\u7237\uff0c\u8fd9\u4e00\u5200\u5288\u51fa\u53bb\uff0c\u624b\u52b2\u987b\u5f97\u600e\u6837\uff1f\u201d\u8840\u5200\u8001\u7956\u9053\uff1a\u201c\u8170\u52b2\u8fd0\u80a9\uff0c\u80a9\u901a\u4e8e\u81c2\uff0c\u81c2\u987b\u65e0\u52b2\uff0c\u8155\u987b\u65e0\u529b\u3002\u201d\u63a5\u7740\u4fbf\u89e3\u91ca\u600e\u4e48\u6837\u624d\u662f\u201c\u8170\u52b2\u8fd0\u80a9\u201d\uff0c\u8981\u600e\u6837\u65b9\u80fd\u201c\u80a9\u901a\u4e8e\u81c2\u201d\uff0c\u8ddf\u7740\u53d6\u8fc7\u8840\u5200\uff0c\u8bf4\u660e\u751a\u4e48\u662f\u201c\u65e0\u52b2\u80dc\u6709\u52b2\u201d\uff0c\u201c\u65e0\u529b\u5373\u6709\u529b\u201d\uff0c\u6c34\u7b19\u542c\u4ed6\u89e3\u8bf4\u8fd9\u4e9b\u9ad8\u6df1\u7684\u6b66\u5b66\u9053\u7406\uff0c\u4e0d\u7531\u5f97\u6697\u6697\u70b9\u5934\u3002 \u3000\u3000\u72c4\u4e91\u542c\u5f97\u8fde\u8fde\u70b9\u5934\uff0c\u9eef\u7136\u9053\uff1a\u201c\u53ea\u53ef\u60dc\u5f92\u5b59\u53d7\u4eba\u9677\u5bb3\uff0c\u7a7f\u4e86\u7435\u7436\u9aa8\uff0c\u5272\u65ad\u624b\u7b4b\uff0c\u518d\u4e5f\u4f7f\u4e0d\u51fa\u529b\u6765\u3002\u201d\u8840\u5200\u8001\u7956\u95ee\u9053\uff1a\u201c\u600e\u6837\u7a7f\u4e86\u7435\u7436\u9aa8\uff1f\u5272\u65ad\u624b\u7b4b\uff1f\u201d\u72c4\u4e91\u9053\uff1a\u201c\u5f92\u5b59\u7ed9\u4eba\u62ff\u5728\u72f1\u4e2d\uff0c\u5403\u4e86\u4e0d\u5c11\u82e6\u5934\u3002\u201d \u3000\u3000\u8840\u5200\u8001\u7956\u5475\u5475\u5927\u7b11\uff0c\u548c\u4ed6\u5e76\u9a91\u800c\u884c\uff0c\u53eb\u4ed6\u89e3\u5f00\u8863\u886b\uff0c\u9732\u51fa\u80a9\u5934\uff0c\u679c\u89c1\u80a9\u9aa8\u4e0b\u9677\uff0c\u4e24\u8fb9\u7435\u7436\u9aa8\u4e0a\u90fd\u6709\u94c1\u94fe\u7a7f\u8fc7\u7684\u5927\u5b54\uff0c\u4f24\u53e3\u5c1a\u672a\u6108\u5408\uff0c\u800c\u53f3\u624b\u624b\u6307\u88ab\u622a\uff0c\u81c2\u7b4b\u88ab\u5272\uff0c\u5c31\u6b66\u529f\u800c\u8a00\uff0c\u53ef\u8bf4\u662f\u6210\u4e86\u4e2a\u5e9f\u4eba\uff0c\u81f3\u4e8e\u4ed6\u88ab\u201c\u94c3\u5251\u53cc\u4fa0\u201d\u7eb5\u9a6c\u8e39\u65ad\u817f\u9aa8\uff0c\u8fd8\u4e0d\u7b97\u5728\u5185\u3002\u8840\u5200\u8001\u7956\u53ea\u77a7\u5f97\u76f4\u7b11\u3002\u72c4\u4e91\u5fc3\u60f3\uff1a\u201c\u6211\u4f24\u5f97\u5982\u6b64\u60e8\u6cd5\uff0c\u4e8f\u4f60\u8fd8\u7b11\u5f97\u51fa\u6765\u3002\u201d \u3000\u3000\u8840\u5200\u8001\u7956\u7b11\u9053\uff1a\u201c\u4f60\u4f24\u4e86\u4eba\u5bb6\u591a\u5c11\u95fa\u5973\uff1f\u563f\u563f\uff0c\u5c0f\u4f19\u5b50\u4e00\u5473\u597d\u8272\u8d2a\u82b1\uff0c\u4e0d\u987e\u8eab\u5b50\uff0c\u8fd9\u624d\u5931\u624b\uff0c\u662f\u4e0d\u662f\uff1f\u201d\u72c4\u4e91\u9053\uff1a\u201c\u4e0d\u662f\u3002\u201d\u8840\u5200\u8001\u7956\u7b11\u9053\uff1a\u201c\u8001\u5b9e\u62db\u6765\uff01\u4f60\u7ed9\u4eba\u62ff\u4f4f\uff0c\u9001\u5165\u7262\u72f1\uff0c\u662f\u4e0d\u662f\u53d7\u4e86\u5973\u5b50\u4e4b\u7d2f\uff1f\u201d\u72c4\u4e91\u4e00\u6014\uff0c\u5fc3\u60f3\uff1a\u201c\u6211\u88ab\u4e07\u9707\u5c71\u5c0f\u59be\u9677\u5bb3\uff0c\u8bf4\u6211\u5077\u94b1\u62d0\u9003\uff0c\u90a3\u679c\u7136\u662f\u53d7\u4e86\u5973\u5b50\u4e4b\u7d2f\u3002\u201d\u4e0d\u7531\u5f97\u54ac\u7740\u7259\u9f7f\uff0c\u6068\u6068\u7684\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u8d31\u4eba\u5bb3\u5f97\u6211\u597d\u82e6\uff0c\u7ec8\u6709\u4e00\u65e5\uff0c\u6211\u8981\u62a5\u6b64\u5927\u4ec7\u3002\u201d \u3000\u3000\u6c34\u7b19\u5fcd\u4e0d\u4f4f\u63d2\u53e3\u9a82\u9053\uff1a\u201c\u4f60\u81ea\u5df1\u505a\u4e86\u8bb8\u591a\u574f\u4e8b\uff0c\u8fd8\u8bf4\u4eba\u5bb6\u7d2f\u4f60\u3002\u8fd9\u4e16\u4e0a\u7684\u65e0\u803b\u4e4b\u5c24\uff0c\u4ee5\u4f60\u5c0f\u2026\u2026\u5c0f\u2026\u2026\u548c\u5c1a\u4e3a\u9996\u3002\u201d \u3000\u3000\u8840\u5200\u8001\u7956\u7b11\u9053\uff1a\u201c\u4f60\u60f3\u9a82\u4ed6\u2018\u5c0f\u6deb\u50e7\u2019\uff0c\u8fd9\u4e2a\u2018\u6deb\u2019\u5b57\u5374\u6709\u70b9\u4e0d\u4fbf\u51fa\u53e3\uff0c\u662f\u4e0d\u662f\uff1f\u5c0f\u599e\u513f\u597d\u5927\u7684\u80c6\u5b50\uff0c\u5b69\u513f\uff0c\u4f60\u5c06\u5979\u5168\u8eab\u8863\u886b\u9664\u4e86\uff0c\u5265\u5f97\u8d64\u6761\u6761\u5730\uff0c\u54b1\u4eec\u8fd9\u4fbf\u2018\u6deb\u2019\u7ed9\u5979\u770b\u770b\uff0c\u77a7\u5979\u8fd8\u6562\u4e0d\u6562\u9a82\u4eba\uff1f\u201d\u72c4\u4e91\u53ea\u5f97\u542b\u542b\u7cca\u7cca\u7684\u7b54\u5e94\u4e00\u58f0\u3002 \u3000\u3000\u6c34\u7b19\u6012\u9a82\uff1a\u201c\u5c0f\u8d3c\uff0c\u4f60\u6562\uff1f\u201d\u6b64\u523b\u5979\u4e1d\u6beb\u52a8\u5f39\u4e0d\u5f97\uff0c\u72c4\u4e91\u82e5\u662f\u8f7b\u8584\u4e4b\u5f92\uff0c\u4f9d\u770b\u8840\u5200\u8001\u7956\u4e4b\u8a00\u800c\u884c\uff0c\u5979\u53c8\u6709\u751a\u4e48\u6cd5\u5b50\uff1f\u8fd9\u201c\u4f60\u6562\u201d\u4e24\u5b57\uff0c\u81ea\u4e5f\u4e0d\u8fc7\u662f\u65e0\u53ef\u5948\u4f55\u4e4b\u4e2d\u865a\u58f0\u606b\u5413\u800c\u5df2\u3002 \u3000\u3000\u72c4\u4e91\u89c1\u8840\u5200\u8001\u7956\u659c\u773c\u6deb\u7b11\uff0c\u773c\u5149\u4e0d\u4f4f\u5728\u6c34\u7b19\u8eab\u4e0a\u8f6c\u6765\u8f6c\u53bb\uff0c\u663e\u662f\u4e0d\u6000\u597d\u610f\uff0c\u5fc3\u4e0b\u76d8\u7b97\uff1a\u201c\u600e\u4e48\u65b9\u80fd\u79fb\u8f6c\u4ed6\u7684\u5fc3\u91cc\uff0c\u522b\u5c3d\u6253\u8fd9\u59d1\u5a18\u7684\u4e3b\u610f\uff1f\u201d\u95ee\u9053\uff1a\u201c\u5e08\u7956\u7237\u7237\uff0c\u5f92\u5b59\u8fd9\u5757\u5e9f\u6599\uff0c\u8fd8\u80fd\u7ec3\u6b66\u529f\u4e48\uff1f\u201d\u8840\u5200\u8001\u7956\u9053\uff1a\u201c\u90a3\u6709\u751a\u4e48\u4e0d\u80fd\uff1f\u4fbf\u662f\u4e24\u53cc\u624b\u4e24\u53ea\u811a\u4e00\u9f50\u65a9\u65ad\u4e86\uff0c\u4e5f\u80fd\u7ec3\u6211\u8840\u5200\u95e8\u7684\u529f\u592b\u3002\u201d\u72c4\u4e91\u53eb\u9053\uff1a\u201c\u90a3\u53ef\u597d\u6781\u4e86\uff01\u201d\u8fd9\u4e00\u58f0\u547c\u53eb\u5374\u662f\u771f\u8bda\u7684\u559c\u60a6\u3002 \u3000\u3000\u4e24\u4eba\u8bf4\u7740\u8bdd\uff0c\u6309\u7f30\u5f90\u884c\uff0c\u4e0d\u4e45\u8f6c\u4e0a\u4e86\u4e00\u6761\u5927\u8def\u3002\u5ffd\u542c\u5f97\u9523\u58f0\u5f53\u5f53\uff0c\u8ddf\u770b\u4e1d\u7af9\u9f50\u594f\uff0c\u8fce\u9762\u6765\u4e86\u4e00\u961f\u8fce\u4eb2\u7684\u4eba\u4f17\uff0c\u5171\u662f\u56db\u4e94\u5341\u4eba\uff0c\u7c07\u62e5\u7740\u4e00\u9876\u82b1\u8f7f\u3002\u8f7f\u540e\u4e00\u4eba\u62ab\u7ea2\u6234\u82b1\uff0c\u670d\u8272\u5149\u9c9c\uff0c\u9a91\u4e86\u4e00\u5339\u767d\u9a6c\uff0c\u4fbf\u662f\u65b0\u90ce\u4e86\u3002 \u3000\u3000\u72c4\u4e91\u4e00\u62e8\u9a6c\u5934\uff0c\u8ba9\u5728\u4e00\u65c1\uff0c\u5fc3\u4e2d\u60f4\u60f4\uff0c\u751f\u6015\u7ed9\u8fd9\u4e00\u5e72\u4eba\u77a7\u7834\u4e86\u884c\u85cf\u3002\u8840\u5200\u8001\u7956\u5374\u7eb5\u9a6c\u76f4\u51b2\u8fc7\u53bb\uff0c\u4f17\u4eba\u5927\u58f0\u5406\u559d\uff1a\u201c\u5582\uff0c\u5582\uff01\u8ba9\u5f00\uff0c\u5e72\u751a\u4e48\u7684\uff1f\u201d\u201c\u81ed\u548c\u5c1a\uff0c\u4eba\u5bb6\u505a\u559c\u4e8b\uff0c\u4f60\u8fd8\u4e0d\u907f\u5f00\uff0c\u4e5f\u4e0d\u56fe\u4e2a\u5409\u5229\uff1f\u201d \u3000\u3000\u8840\u5200\u8001\u7956\u51b2\u5230\u8fce\u4eb2\u961f\u4e4b\u524d\u4e24\u4e08\u4e4b\u5904\uff0c\u52d2\u9a6c\u505c\u4f4f\uff0c\u53cc\u624b\u53c9\u8170\uff0c\u7b11\u9053\uff1a\u201c\u5582\uff0c\u65b0\u5a18\u5b50\u957f\u5f97\u600e\u4e48\u6837\uff0c\u4fca\u4e0d\u4fca\u554a\uff1f\u201d \u3000\u3000\u8fce\u4eb2\u961f\u4e2d\u4e00\u6761\u5927\u6c49\u4ece\u4ece\u82b1\u8f7f\u4e2d\u62bd\u51fa\u4e00\u6839\u8f7f\u6760\uff0c\u62a2\u51fa\u961f\u6765\uff0c\u58f0\u52bf\u6c79\u6c79\u7684\u559d\u9053\uff1a\u201c\u72d7\u8d3c\u79c3\uff0c\u4f60\u6d3b\u5f97\u4e0d\u8010\u70e6\u4e86\uff1f\u201d\u90a3\u6839\u8f7f\u6760\u6bd4\u624b\u81c2\u8fd8\u7c97\uff0c\u6709\u4e00\u4e08\u6765\u957f\uff0c\u4ed6\u53cc\u624b\u6a2a\u6301\uff0c\u5012\u4e5f\u5a01\u98ce\u51db\u51db\u3002 \u3000\u3000\u8840\u5200\u8001\u7956\u5411\u72c4\u4e91\u7b11\u9053\uff1a\u201c\u4f60\u77a7\u6e05\u695a\u4e86\uff0c\u8fd9\u53c8\u662f\u4e00\u8def\u529f\u592b\u3002\u201d \u3000\u3000\u8eab\u5b50\u5411\u524d\u4e00\u63a2\uff0c\u8840\u5200\u98a4\u52a8\uff0c\u5200\u5203\u4fbf\u5982\u4e00\u6761\u8d64\u7ec3\u86c7\u4e00\u822c\uff0c\u8fc5\u901f\u65e0\u4f26\u7684\u5728\u8f7f\u6760\u4e0a\u722c\u884c\u800c\u8fc7\uff0c\u968f\u5373\u6536\u5200\u5165\u9798\uff0c\u54c8\u54c8\u5927\u7b11\u3002 \u3000\u3000\u8fce\u4eb2\u961f\u4e2d\u6709\u4eba\u559d\u9a82\uff1a\u201c\u8001\u8d3c\u79c3\uff0c\u4f60\u778e\u4e86\u773c\u4e48\uff1f\u60f3\u5316\u7f18\u4e5f\u4e0d\u62e3\u65f6\u8fb0\uff01\u201d\u9a82\u58f0\u672a\u7edd\uff0c\u90a3\u624b\u6301\u65a9\u6760\u7684\u5927\u6c49\u201c\u554a\u54df\u201d\u4e00\u58f0\uff0c\u53eb\u51fa\u58f0\u6765\u3002\u53ea\u542c\u5f97\u62cd\u3001\u62cd\u3001\u62cd\u3001\u62cd\u4e00\u8fde\u4e32\u8f7b\u54cd\uff0c\u4e00\u5757\u5757\u4e24\u5bf8\u6765\u957f\u7684\u6728\u5757\u6389\u5728\u5730\u4e0b\uff0c\u4ed6\u53cc\u624b\u6240\u63e1\uff0c\u4e5f\u53ea\u662f\u4e24\u5757\u6570\u5bf8\u7684\u6728\u5757\u3002\u539f\u6765\u9002\u624d\u8fd9\u9877\u523b\u4e4b\u95f4\uff0c\u4e00\u6839\u4e08\u8bb8\u957f\u7684\u8f7f\u6760\uff0c\u5df2\u88ab\u8840\u5200\u6279\u6210\u4e86\u6570\u5341\u622a\u3002 \u3000\u3000\u8840\u5200\u8001\u7956\u54c8\u54c8\u5927\u7b11\uff0c\u8840\u5200\u5c71\u9798\uff0c\u76f4\u4e00\u4e0b\uff0c\u6a2a\u4e00\u4e0b\uff0c\u767b\u65f6\u5c06\u90a3\u5927\u6c49\u5207\u6210\u56db\u622a\uff0c\u559d\u9053\uff1a\u201c\u6211\u8981\u77a7\u77a7\u65b0\u5a18\u5b50\uff0c\u662f\u7ed9\u4f60\u4eec\u9762\u5b50\u3002 \u3000\u3000\u6709\u751a\u4e48\u5927\u60ca\u5c0f\u602a\u7684\u3002\u201d \u3000\u3000\u4f17\u4eba\u89c1\u4ed6\u9752\u5929\u767d\u65e5\u4e4b\u4e0b\u5728\u5927\u9053\u4e4b\u4e0a\u5982\u6b64\u884c\u51f6\uff0c\u65e0\u4e0d\u5413\u5f97\u9b42\u98de\u9b44\u6563\u3002\u80c6\u5b50\u5927\u4e9b\u7684\uff0c\u53d1\u4e00\u58f0\u558a\uff0c\u56db\u6563\u8d70\u4e86\u3002\u4e00\u5927\u534a\u4eba\u5374\u662f\u811a\u90fd\u8f6f\u4e86\uff0c\u6709\u7684\u4eba\u8fde\u5c3f\u5c4e\u4e5f\u5413\u4e86\u51fa\u6765\uff0c\u54ea\u6562\u52a8\u5f39\u3002 \u3000\u3000\u8840\u5200\u8001\u7956\u8840\u5200\u4e00\u6643\uff0c\u5df2\u5272\u53bb\u4e86\u82b1\u8f7f\u7684\u5e37\u5e55\uff0c\u5de6\u624b\u6293\u4f4f\u65b0\u5a18\u80f8\u53e3\uff0c\u62c9\u4e86\u51fa\u6765\u3002\u90a3\u65b0\u5a18\u5c16\u58f0\u5636\u53eb\uff0c\u6ca1\u547d\u7684\u6323\u624e\u3002\u8840\u5200\u8001\u7956\u4e3e\u5200\u4e00\u6311\uff0c\u5c06\u65b0\u5a18\u906e\u5728\u8138\u524d\u7684\u971e\u62ab\u524a\u53bb\u3002\u9732\u51fa\u5979\u60ca\u60f6\u5931\u8272\u7684\u8138\u6765\u3002\u4f46\u89c1\u8fd9\u65b0\u5a18\u4e0d\u8fc7\u5341\u516d\u4e03\u5c81\u5e74\u7eaa\uff0c\u8fd8\u662f\u4e2a\u5b69\u7ae5\u6a21\u6837\uff0c\u76f8\u8c8c\u4e5f\u9887\u4e11\u964b\u3002\u8840\u5200\u50e7\u5478\u7684\u4e00\u58f0\uff0c\u4e00\u53e3\u75f0\u5f80\u5979\u8eab\u4e0a\u5410\u53bb\u3002\u8bf4\u9053\uff1a\u201c\u8fd9\u6837\u4e11\u602a\u7684\u5973\u5b50\uff0c\u505a\u751a\u4e48\u65b0\u5a18\uff01\u201d\u8840\u5200\u4e00\u6643\uff0c\u7adf\u5c06\u65b0\u5a18\u7684\u9f3b\u5b50\u5272\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u90a3\u65b0\u90ce\u50f5\u5728\u9a6c\u4e0a\uff0c\u53ea\u662f\u745f\u745f\u53d1\u6296\uff0c\u8840\u5200\u8001\u7956\u53eb\u9053\uff1a\u201c\u5b69\u513f\uff0c\u518d\u77a7\u6211\u4e00\u8def\u529f\u592b\uff0c\u8fd9\u53eb\u505a\u2018\u5455\u5fc3\u6ca5\u8840\u2019\uff01\u201d\u8bf4\u7740\u624b\u4e00\u626c\uff0c\u8840\u5200\u8131\u624b\u98de\u51fa\uff0c\u4e00\u6e9c\u7ea2\u5149\uff0c\u5f84\u5411\u9a6c\u4e0a\u7684\u65b0\u90ce\u5c04\u53bb\uff0c\u4ed6\u8840\u5200\u8131\u624b\uff0c\u968f\u5373\u7eb5\u9a6c\u524d\u51b2\uff0c\u5feb\u9a6c\u7ed5\u8fc7\u65b0\u90ce\uff0c\u98de\u8eab\u8dc3\u8d77\uff0c\u957f\u81c2\u63a2\u624b\uff0c\u5c06\u8840\u5200\u6284\u5728\u624b\u4e2d\uff0c\u53c8\u7a33\u7a33\u7684\u5750\u4e0a\u4e86\u9a6c\u978d\uff0c\u90a3\u65b0\u90ce\u80f8\u53e3\u7a7f\u4e86\u4e00\u6d1e\uff0c\u8840\u5982\u55b7\u6cc9\uff0c\u8eab\u5b50\u6162\u6162\u5782\u4e0b\uff0c\u5012\u649e\u4e0b\u9a6c\u3002\u539f\u6765\u90a3\u8840\u5200\u7a7f\u8fc7\u4ed6\u8eab\u5b50\uff0c\u53c8\u7ed9\u8840\u5200\u50e7\u63a5\u5728\u624b\u91cc\u3002 \u3000\u3000\u72c4\u4e91\u4e00\u8def\u4e0a\u6577\u884d\u8840\u5200\u50e7\uff0c\u4e00\u6765\u5fc3\u4e2d\u5bb3\u6015\uff0c\u4e8c\u6765\u4ed6\u6551\u4e86\u81ea\u5df1\u6027\u547d\uff0c\u4e8e\u5df1\u6709\u6069\uff0c\u603b\u4e0d\u514d\u6709\u611f\u6fc0\u4e4b\u610f\uff0c\u6b64\u523b\u89c1\u4ed6\u5272\u4f24\u65b0\u5a18\uff0c\u53c8\u8fde\u6740\u4e8c\u4eba\uff0c\u8fd9\u4e09\u4eba\u548c\u4ed6\u6beb\u4e0d\u76f8\u8bc6\uff0c\u7adf\u7136\u4e0b\u6b64\u6bd2\u624b\uff0c\u4e0d\u7531\u5f97\u6c14\u6124\u586b\u81ba\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u600e\u53ef\u5982\u6b64\u6ee5\u6740\u65e0\u8f9c\uff1f\u8fd9\u4e9b\u4eba\u788d\u7740\u4f60\u751a\u4e48\u4e8b\u4e86\uff1f\u201d\u8840\u5200\u8001\u7956\u4e00\u6014\uff0c\u7b11\u9053\uff1a\u201c\u6211\u751f\u5e73\u5c31\u7231\u6ee5\u6740\u65e0\u8f9c\u3002\u8981\u662f\u6709\u7f6a\u7684\u624d\u6740\uff0c\u4e16\u4e0a\u54ea\u6709\u8fd9\u8bb8\u591a\u6709\u6709\u7f6a\u4e4b\u4eba\uff1f\u201d\u8bf4\u5230\u8fd9\u91cc\uff0c\u8840\u5200\u4e00\u626c\uff0c\u53c8\u780d\u53bb\u8fce\u4eb2\u961f\u4e2d\u4e00\u4eba\u7684\u8111\u888b\u3002\u72c4\u4e91\u5927\u6012\uff0c\u62cd\u9a6c\u4e0a\u524d\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u4e0d\u80fd\u518d\u6740\u4eba\u4e86\u3002\u201d\u8840\u5200\u8001\u7956\u7b11\u9053\uff1a\u201c\u5c0f\u5a03\u513f\uff0c\u89c1\u5230\u6d41\u8840\u5c31\u6015\uff0c\u662f\u4e0d\u662f\uff1f\u90a3\u4f60\u6709\u751a\u4e48\u5c41\u7528\uff1f\u201d \u3000\u3000\u4fbf\u5728\u6b64\u65f6\uff0c\u53ea\u542c\u5f97\u9a6c\u8e44\u58f0\u54cd\uff0c\u6709\u6570\u5341\u4eba\u81ea\u8fdc\u5904\u8ffd\u6765\u3002\u6709\u4eba\u957f\u58f0\u53eb\u9053\uff1a\u201c\u8840\u5200\u50e7\uff0c\u4f60\u653e\u4e0b\u6211\u5973\u513f\uff0c\u54b1\u4eec\u4e24\u4e0b\u7f62\u4f11\uff0c\u5426\u5219\u4f60\u4fbf\u9003\u5230\u5929\u8fb9\uff0c\u6211\u4e5f\u8ffd\u4f60\u5230\u5929\u8fb9\u3002\u201d\u542c\u6765\u9a6c\u8e44\u4e4b\u58f0\u5c1a\u8fdc\uff0c\u4f46\u6c34\u5cb1\u8fd9\u58f0\u547c\u53eb\uff0c\u5374\u662f\u5b57\u5b57\u6e05\u6670\u3002\u6c34\u7b19\u559c\u9053\uff1a\u201c\u7239\u7239\u6765\u4e86\uff01\u201d \u3000\u3000\u53c8\u542c\u5f97\u56db\u4e2a\u4eba\u7684\u58f0\u97f3\u9f50\u58f0\u53eb\u9053\uff1a\u201c\u843d\u82b1\u6d41\u6c34\u516e\u2014\u2014\u6c34\u6d41\u82b1\u843d\uff01\u843d\u82b1\u6d41\u6c34\u516e\u2014\u2014\u6c34\u6d41\u82b1\u843d\uff01\u201d\u56db\u4eba\u55d3\u97f3\u5404\u81ea\u4e0d\u540c\uff0c\u6216\u82cd\u8001\uff0c\u6216\u96c4\u58ee\uff0c\u6216\u60a0\u957f\uff0c\u6216\u9ad8\u4ea2\uff0c\u4f46\u5185\u529b\u4e4b\u539a\uff0c\u5404\u64c5\u80dc\u573a\u3002 \u3000\u3000\u8840\u5200\u50e7\u76b1\u8d77\u7709\u5934\uff0c\u9a82\u9053\uff1a\u201c\u4e2d\u539f\u7684\u72d7\u8d3c\uff0c\u504f\u6709\u8fd9\u8bb8\u591a\u81ed\u5f20\u81f4\uff01\u201d \u3000\u3000\u53ea\u542c\u6c34\u5cb1\u53c8\u9053\uff1a\u201c\u4f60\u6b66\u529f\u518d\u5f3a\uff0c\u51b3\u8ba1\u96be\u654c\u6211\u2018\u5357\u56db\u5947\u2019\u843d\u82b1\u6d41\u6c34\u8054\u624b\u76f8\u653b\uff0c\u4f60\u5c06\u6211\u5973\u513f\u653e\u4e0b\uff0c\u5927\u4e08\u592b\u8a00\u51fa\u5982\u5c71\uff0c\u4e0d\u518d\u8ffd\u4f60\u5c31\u662f\u3002\u201d \u3000\u3000\u8840\u5200\u50e7\u5fc3\u4e0b\u5bfb\u601d\uff1a\u201c\u9002\u624d\u5df2\u89c1\u8bc6\u8fc7\u6c34\u5cb1\u548c\u90a3\u8001\u9053\u7684\u529f\u592b\u3002 \u3000\u3000\u4e00\u5bf9\u4e00\u76f8\u6597\uff0c\u6211\u51b3\u8ba1\u4e0d\u60e7\u3002\u4ed6\u4e8c\u4eba\u8054\u624b\uff0c\u6211\u4fbf\u8f93\u591a\u8d62\u5c11\uff0c\u975e\u9003\u4e0d\u53ef\u3002\u4ed6\u4e09\u4eba\u8054\u624b\uff0c\u6211\u662f\u4e00\u8d25\u6d82\u5730\uff0c\u53ea\u6015\u9003\u4e5f\u9003\u4e0d\u8d70\u4e86\u3002\u56db\u4eba\u8054\u624b\u653b\u6211\uff0c\u8840\u5200\u8001\u7956\u6b7b\u65e0\u846c\u8eab\u4e4b\u5730\u3002\u563f\u563f\uff0c\u8fd9\u4e9b\u4e2d\u539f\u6c5f\u6e56\u4e2d\u4eba\uff0c\u8bf4\u8bdd\u6709\u751a\u4e48\u72d7\u5c41\u4fe1\u7528\uff1f\u6382\u7740\u8fd9\u599e\u513f\u4e3a\u8d28\uff0c\u5c1a\u6709\u817e\u632a\u4f59\u5730\uff0c\u4e00\u5c06\u5979\u653e\u8d70\uff0c\u4fbf\u662f\u4ed6\u4eec\u5360\u5c3d\u4e0a\u98ce\u7684\u5c40\u9762\u4e86\uff01\u201d\u5f53\u4e0b\u4e00\u58f0\u5406\u559d\uff0c\u6325\u97ad\u5f80\u72c4\u4e91\u6240\u4e58\u7684\u9a6c\u81c0\u4e0a\u62bd\u53bb\uff0c\u4e00\u63d0\u7f30\uff0c\u7eb5\u9a6c\u5411\u897f\u5954\u9a70\uff0c\u63d0\u8d77\u5185\u529b\uff0c\u56de\u8fc7\u5934\u6765\uff0c\u957f\u58f0\u53eb\u9053\uff1a\u201c\u6c34\u8001\u7237\u5b50\uff0c\u8840\u5200\u95e8\u7684\u4e24\u4e2a\u548c\u5c1a\u90fd\u5df2\u505a\u4e86\u4f60\u7684\u5973\u5a7f\u3002\u7b2c\u56db\u4ee3\u638c\u95e8\u662f\u4f60\u5973\u5a7f\uff0c\u7b2c\u516d\u4ee3\u5f1f\u5b50\u4e5f\u662f\u4f60\u5973\u5a7f\u3002\u4e08\u4eba\u8ffd\u5973\u5a7f\uff0c\u53e3\u6c34\u70b9\u70b9\u6ef4\uff0c\u5999\u6781\uff0c\u5999\u6781\uff01\u201d \u3000\u3000\u6c34\u5cb1\u4e00\u542c\u4e4b\u4e0b\uff0c\u6c14\u5f97\u5fc3\u80f8\u51e0\u4e4e\u70b8\u7834\u3002\u4ed6\u65e9\u77e5\u8840\u5200\u95e8\u7684\u6076\u50e7\u5978\u6deb\u70e7\u6740\uff0c\u65e0\u6076\u4e0d\u4f5c\uff0c\u5e08\u5f92\u4e8c\u4eba\u4e00\u540c\u6c61\u8fb1\u81ea\u5df1\u5973\u513f\uff0c\u5728\u4ed6\u8840\u5200\u95e8\u4e8b\u5c5e\u5bfb\u5e38\u3002\u522b\u8bf4\u771f\u6709\u5176\u4e8b\uff0c\u5355\u662f\u8fd9\u51e0\u53e5\u8bdd\uff0c\u5df2\u52bf\u5fc5\u8ba9\u4eba\u5728\u80cc\u540e\u8bf4\u4e0a\u65e0\u7a77\u65e0\u5c3d\u7684\u6c61\u8a00\u79fd\u8bed\u3002\u4e00\u4e2a\u79f0\u9738\u4e2d\u539f\u6570\u5341\u5e74\u7684\u8001\u82f1\u96c4\uff0c\u4eca\u65e5\u7adf\u53d7\u5982\u6b64\u6298\u8fb1\uff0c\u82e5\u4e0d\u5c06\u8840\u5200\u5e08\u5f92\u788e\u5c38\u4e07\u6bb5\uff0c\u65e5\u540e\u5982\u4f55\u505a\u4eba\uff1f\u5f53\u4e0b\u50ac\u9a6c\u529b\u8ffd\u3002 \u3000\u3000\u8fd9\u65f6\u968f\u7740\u6c34\u5cb1\u4e00\u9f50\u8ffd\u8d76\u7684\uff0c\u9664\u4e86\u548c\u6c34\u5cb1\u9f50\u540d\u3001\u5e76\u79f0\u201c\u5357\u56db\u5947\u201d\u7684\u9646\u3001\u82b1\u3001\u5218\u4e09\u8001\u4e4b\u5916\uff0c\u5c1a\u6709\u4e2d\u539f\u4e09\u5341\u4f59\u540d\u597d\u624b\uff0c\u6216\u4e3a\u6355\u5934\u9573\u5ba2\uff0c\u6216\u4e3a\u8457\u540d\u62f3\u5e08\uff0c\u6216\u4e3a\u6b66\u6797\u9690\u9038\uff0c\u6216\u4e3a\u5e2e\u4f1a\u9996\u8111\u3002 \u3000\u3000\u8840\u5200\u95e8\u7684\u4f17\u6076\u50e7\u6700\u8fd1\u5728\u6e56\u5e7f\u4e00\u5e26\u95f9\u5f97\u5929\u7ffb\u5730\u8986\uff0c\u4e0d\u5206\u9752\u7ea2\u7682\u767d\u7684\u505a\u6848\uff0c\u5c06\u4e2d\u539f\u767d\u9053\u9ed1\u9053\u7684\u4eba\u7269\u5c3d\u90fd\u5f97\u7f6a\u4e86\u3002\u6b66\u6797\u7fa4\u8c6a\u52a8\u4e86\u516c\u6124\uff0c\u5f97\u77e5\u8baf\u606f\u540e\uff0c\u5927\u4f19\u513f\u90fd\u8ffd\u4e86\u4e0b\u6765\uff0c\u5747\u89c9\u8fd9\u4e0d\u53ea\u662f\u52a9\u6c34\u5cb1\u593a\u8fd8\u5973\u513f\u800c\u5df2\uff0c\u82e5\u4e0d\u5c06\u8840\u5200\u95e8\u8fd9\u8001\u5c11\u4e8c\u6076\u50e7\u6740\u4e86\uff0c\u6240\u6709\u4e2d\u539f\u7684\u6b66\u6797\u4eba\u58eb\u5747\u662f\u8138\u4e0a\u65e0\u5149\u3002 \u3000\u3000\u7fa4\u8c6a\u4e00\u8def\u8ffd\u6765\uff0c\u6bcf\u5230\u4e00\u5904\u5dde\u53bf\u5e02\u96c6\uff0c\u4fbf\u6389\u6362\u5750\u9a91\u3002\u4f17\u4eba\u6362\u9a6c\u4e0d\u6362\u4eba\uff0c\u5728\u9a6c\u80cc\u4e0a\u56bc\u5403\u5e72\u7cae\uff0c\u559d\u4e9b\u6e05\u6c34\uff0c\u4fbf\u53c8\u6025\u8ffd\u3002 \u3000\u3000\u8840\u5200\u8001\u7956\u867d\u7136\u610f\u793a\u95f2\u6687\uff0c\u4ed7\u7740\u5750\u9a91\u795e\u9a8f\uff0c\u9047\u5230\u8336\u94fa\u996d\u5e97\uff0c\u5f80\u5f80\u8fd8\u6253\u5c16\u4f11\u606f\uff0c\u4f46\u4f4f\u5bbf\u8fc7\u591c\u5374\u7ec8\u7a76\u4e0d\u6562\u3002\u4fbf\u56e0\u4e2d\u539f\u7fa4\u8c6a\u8ffd\u5f97\u751a\u7d27\uff0c\u6c34\u7b19\u8fd9\u6570\u65e5\u4e2d\u7ec8\u4e8e\u4fdd\u5f97\u6e05\u767d\u3002 \u3000\u3000\u5982\u6b64\u6570\u65e5\u8fc7\u53bb\uff0c\u5df2\u4ece\u6e56\u5317\u8ffd\u8fdb\u4e86\u56db\u5ddd\u5883\u5185\u3002\u4e24\u6e56\u7fa4\u8c6a\u4e0e\u5df4\u8700\u6c5f\u6e56\u4e0a\u4eba\u7269\u5411\u6765\u58f0\u6c14\u76f8\u901a\u3002\u5ddd\u4e1c\u6b66\u4eba\u4e00\u5f97\u5230\u8baf\u606f\uff0c\u7eb7\u7eb7\u52a0\u5165\u8ffd\u8d76\u3002\u5f85\u5230\u6e1d\u5dde\u4e00\u5e26\uff0c\u5ddd\u4e2d\u8c6a\u6770\u4e0d\u7518\u540e\u4eba\uff0c\u53c8\u90fd\u53c2\u4e0e\u5176\u4e8b\uff0c\u4ed6\u4eec\u4e0e\u6b64\u4e8b\u5e76\u975e\u5207\u8eab\u76f8\u5173\uff0c\u4f46\u53cd\u6b63\u6709\u80dc\u65e0\u8d25\uff0c\u6b63\u597d\u51d1\u51d1\u70ed\u95f9\uff0c\u7ed3\u4ea4\u670b\u53cb\uff0c\u4e5f\u663e\u5f97\u81ea\u5df1\u4e49\u6c14\u4e3a\u91cd\u3002\u5f85\u8fc7\u5f97\u6e1d\u5dde\uff0c\u8ffd\u8d76\u7684\u4eba\u4f17\u5df2\u903e\u4e8c\u4e09\u767e\u4eba\u3002\u56db\u5ddd\u6b66\u4eba\u6709\u94b1\u8005\u591a\uff0c\u5927\u6279\u9aa1\u9a6c\u8ddf\u968f\u5176\u540e\uff0c\u8fd0\u9001\u8863\u88ab\u7cae\u98df\u3002\u53ea\u662f\u8fd9\u5e72\u4eba\u5f97\u5230\u8baf\u606f\u4e4b\u65f6\uff0c\u8840\u5200\u8001\u7956\u4e0e\u72c4\u4e91\u3001\u6c34\u7b19\u5df2\u7136\u897f\u53bb\uff0c\u53ea\u80fd\u968f\u540e\u8ffd\u8d76\uff0c\u5374\u4e0d\u53ca\u8fce\u5934\u62e6\u622a\u3002 \u3000\u3000\u90a3\u4e9b\u897f\u8700\u6b66\u4eba\u6170\u95ee\u4e00\u756a\u4e4b\u540e\uff0c\u90fd\u9053\uff1a\u201c\u5509\uff0c\u65e9\u77e5\u5982\u6b64\uff0c\u6211\u4eec\u62e6\u5728\u5f53\u9053\uff0c\u8bf4\u751a\u4e48\u4e5f\u4e0d\u653e\u90a3\u8001\u5c11\u4e24\u4e2a\u6deb\u50e7\u8fc7\u53bb\uff0c\u603b\u8981\u6551\u5f97\u6c34\u5c0f\u59d0\u8131\u9669\u3002\u201d\u6c34\u5cb1\u53e3\u4e2d\u9053\u8c22\uff0c\u5fc3\u4e0b\u5374\u751a\u5fff\u6012\uff1a\u201c\u8bf4\u8fd9\u4e9b\u5e9f\u8bdd\u6709\u5c41\u7528\uff1f\u51ed\u4f60\u4eec\u8fd9\u51e0\u5757\u6599\uff0c\u80fd\u62e6\u5f97\u4f4f\u90a3\u8001\u5c11\u4e8c\u50e7\uff1f\u201d \u3000\u3000\u8fd9\u4e00\u524d\u4e00\u540e\u7684\u8ffd\u9010\uff0c\u8f6c\u773c\u95f4\u5c06\u8fd1\u4e8c\u5341\u65e5\uff0c\u8840\u5200\u8001\u7956\u51e0\u6b21\u8f6c\u5165\u5c94\u9053\uff0c\u60f3\u5c06\u8ffd\u8d76\u8005\u6487\u4e0b\u3002\u4f46\u7fa4\u8c6a\u4e2d\u6709\u4e00\u4eba\u662f\u6765\u81ea\u5173\u4e1c\u7684\u9a6c\u8d3c\uff0c\u5584\u4e8e\u8ffd\u8e2a\u4e4b\u672f\uff0c\u4e0d\u8bba\u8840\u5200\u8001\u7956\u5982\u4f55\u7ed5\u9053\u8f6c\u5f2f\uff0c\u4ed6\u603b\u662f\u80fd\u8ddf\u8e2a\u8ffd\u5230\u3002\u53ea\u662f\u8fd9\u4e48\u4e00\u6765\uff0c\u4e00\u884c\u4eba\u8d8a\u8d70\u8d8a\u8352\u50fb\uff0c\u5df2\u6df1\u5165\u5ddd\u897f\u7684\u5d07\u5c71\u5cfb\u5cad\u3002\u7fa4\u8c6a\u5747\u77e5\u8840\u5200\u50e7\u662f\u60f3\u9003\u56de\u897f\u85cf\u8001\u5bb6\uff0c\u4e00\u5230\u4e86\u4ed6\u8001\u5de2\uff0c\u8840\u5200\u95e8\u672c\u95e8\u50e7\u4f17\u5df2\u7136\u4e0d\u5c11\uff0c\u518d\u52a0\u4e0a\u5978\u515a\u6deb\u670b\uff0c\u52bf\u529b\u96c4\u539a\uff0c\u90a3\u65f6\u518d\u548c\u4e2d\u539f\u7fa4\u8c6a\u4e00\u6218\uff0c\u6709\u9053\u662f\u5f3a\u9f99\u4e0d\u6597\u5730\u5934\u86c7\uff0c\u80dc\u8d25\u4e4b\u6570\u5c31\u96be\u8bf4\u4e86\u3002 \u3000\u3000\u8fc7\u5f97\u4e24\u5929\uff0c\u5ffd\u7136\u4e0b\u8d77\u5927\u96ea\u6765\u3002\u5176\u65f6\u5df2\u5230\u4e86\u897f\u5ddd\u8fb9\u9672\uff0c\u66f4\u5411\u897f\u884c\u4fbf\u662f\u85cf\u8fb9\u3002\u5f53\u5730\u5df2\u5c5e\u5927\u96ea\u5c71\u5c71\u8109\uff0c\u5730\u52bf\u9ad8\u5cfb\uff0c\u904d\u5730\u51b0\u96ea\uff0c\u9a6c\u8e44\u6ed1\u6e9c\uff0c\u5bd2\u98ce\u5f7b\u9aa8\u90a3\u662f\u4e0d\u5fc5\u8bf4\u4e86\uff0c\u6700\u96be\u53d7\u7684\u662f\u4eba\u4eba\u5fc3\u8df3\u6c14\u5598\uff0c\u9664\u4e86\u5185\u529f\u7279\u9ad8\u7684\u6570\u4eba\u4e4b\u5916\uff0c\u4f59\u4eba\u5747\u611f\u5468\u8eab\u75b2\u4e4f\uff0c\u6068\u4e0d\u5f97\u8eba\u4e0b\u6765\u4f11\u606f\u51e0\u4e2a\u65f6\u8fb0\u3002 \u3000\u3000\u4f46\u53c2\u4e0e\u8ffd\u9010\u4e4b\u4eba\u4e2a\u4e2a\u9887\u6709\u540d\u671b\u6765\u5934\uff0c\u8c01\u90fd\u4e0d\u80af\u793a\u5f31\uff0c\u4ee5\u81f3\u574f\u4e86\u4e00\u4e16\u7684\u58f0\u540d\u3002\u8fd9\u51e0\u65e5\u4e2d\uff0c\u6781\u5927\u591a\u6570\u4eba\u5df2\u840c\u9000\u5fd7\uff0c\u82e5\u6709\u4eba\u5021\u8bae\u7f62\u624b\u4e0d\u8ffd\u3002\u6709\u4e00\u5927\u534a\u4eba\u4fbf\u8981\u5f52\u53bb\u3002\u5c24\u5176\u662f\u5ddd\u4e1c\u3001\u5ddd\u4e2d\u7684\u8c6a\u6770\u4e4b\u4e2d\uff0c\u9887\u6709\u4e00\u4e9b\u517b\u5c0a\u5904\u4f18\u7684\u5bcc\u5ba4\u5b50\u5f1f\uff0c\u6b66\u529f\u867d\u7136\u4e0d\u5dee\uff0c\u5374\u5403\u4e0d\u8d77\u8fd9\u7b49\u82e6\u5934\u3002\u6709\u7684\u773c\u89c1\u5468\u906d\u5730\u52bf\u9669\u6076\uff0c\u5fc3\u751f\u602f\u610f\uff0c\u85c9\u6545\u843d\u540e\uff1b\u66f4\u6709\u7684\u4e58\u4eba\u4e0d\u89c9\uff0c\u6084\u6084\u8d70\u4e0a\u4e86\u56de\u5934\u8def\u3002 \u3000\u3000\u8fd9\u4e00\u65e5\u4e2d\u5348\u65f6\u5206\uff0c\u7fa4\u8c6a\u8ffd\u4e0a\u4e86\u4e00\u6761\u9661\u5ced\u7684\u5c71\u9053\uff0c\u5ffd\u89c1\u4e00\u5339\u9ec4\u9a6c\u5012\u6bd9\u5728\u9053\u65c1\u96ea\u5806\u4e4b\u4e2d\uff0c\u6b63\u662f\u6c6a\u5578\u98ce\u7684\u5750\u9a91\u3002\u6c34\u5cb1\u548c\u6c6a\u5578\u98ce\u5927\u559c\uff0c\u9f50\u58f0\u5927\u53eb\uff1a\u201c\u6076\u8d3c\u5012\u4e86\u4e00\u5339\u5750\u9a91\uff0c\u54b1\u4eec\u5feb\u8ffd\uff0c\u6deb\u50e7\u9003\u4e0d\u6389\u5566\uff01\u201d\u7fa4\u8c6a\u7cbe\u795e\u4e00\u632f\uff0c\u90fd\u5927\u58f0\u6b22\u547c\u8d77\u6765\u3002 \u3000\u3000\u53eb\u558a\u58f0\u4e2d\uff0c\u5ffd\u89c1\u5c71\u9053\u897f\u4fa7\u9ad8\u5cf0\u4e0a\u4e00\u5927\u7247\u767d\u96ea\u7f13\u7f13\u6eda\u5c06\u4e0b\u6765\u3002 \u3000\u3000\u4e00\u540d\u5ddd\u897f\u7684\u8001\u8005\u53eb\u9053\uff1a\u201c\u4e0d\u597d\uff0c\u8981\u96ea\u5d29\uff0c\u5927\u4f19\u513f\u9000\u540e\uff01\u201d\u8bdd\u58f0\u672a\u6bd5\uff0c\u4f46\u542c\u5f97\u96f7\u58f0\u9690\u9690\uff0c\u5c71\u5934\u4e0a\u6eda\u4e0b\u6765\u7684\u79ef\u96ea\u6e10\u591a\u6e10\u901f\u3002\u7fa4\u8c6a\u4e00\u65f6\u4e0d\u660e\u6240\u4ee5\uff0c\u4e03\u5f20\u516b\u5634\u7684\u53eb\u9053\uff1a\u201c\u90a3\u662f\u751a\u4e48\uff1f\u201d\u201c\u96ea\u5d29\u6709\u751a\u4e48\u8981\u7d27\uff1f\u5927\u4f19\u513f\u5feb\u8ffd\uff01\u201d\u201c\u5feb\uff01\u5feb\uff01\u62a2\u8fc7\u8fd9\u6761\u5c71\u5cad\u518d\u8bf4\u3002\u201d \u3000\u3000\u53ea\u9694\u5f97\u7247\u523b\uff0c\u9690\u9690\u7684\u96f7\u58f0\u5df2\u53d8\u4f5c\u8f70\u8f70\u9686\u9686\u3001\u9707\u8033\u6b32\u804b\u7684\u5927\u54cd\u3002\u4f17\u4eba\u8fd9\u65f6\u624d\u611f\u5bb3\u6015\u3002\u90a3\u96ea\u5d29\u521d\u8d77\u65f6\u76f8\u8ddd\u751a\u8fdc\uff0c\u4f46\u4ece\u9ad8\u5cf0\u4e0a\u4e00\u8def\u6eda\u5c06\u4e0b\u6765\uff0c\u6cbf\u9014\u631f\u5e26\u5927\u91cf\u79ef\u96ea\uff0c\u66f4\u6709\u4e0d\u5c11\u5ca9\u77f3\u968f\u800c\u4ff1\u4e0b\uff0c\u58f0\u52bf\u8d8a\u6765\u8d8a\u5927\uff0c\u5230\u5f97\u534a\u5c71\uff0c\u5f53\u771f\u5982\u7fa4\u5c71\u9f50\u88c2\u3001\u6012\u6f6e\u9aa4\u81f3\u4e00\u822c\uff0c\u8bf4\u4e0d\u51fa\u7684\u53ef\u6016\u53ef\u754f\u3002 \u3000\u3000\u7fa4\u8c6a\u4e2d\u65e9\u6709\u6570\u4eba\u62e8\u8f6c\u9a6c\u5934\u5954\u9003\uff0c\u4f59\u4eba\u542c\u7740\u90a3\u5c71\u5d29\u5730\u88c2\u7684\u5de8\u54cd\uff0c\u4f3c\u89c9\u5934\u9876\u7684\u5929\u4e5f\u584c\u4e86\uff0c\u4e00\u9f50\u538b\u5c06\u4e0b\u6765\uff0c\u53ea\u5413\u5f97\u5fc3\u80c6\u4ff1\u88c2\uff0c\u4e5f\u90fd\u7eb7\u7eb7\u56de\u9a6c\u5feb\u5954\u3002\u6709\u51e0\u5339\u9a6c\u5413\u5f97\u5446\u4e86\uff0c\u7adf\u7136\u4e0d\u4f1a\u4e3e\u8db3\uff0c\u9a6c\u4e0a\u4e58\u5ba2\u89c1\u60c5\u52bf\u4e0d\u5bf9\uff0c\u53ea\u5f97\u8dc3\u4e0b\u9a6c\u80cc\uff0c\u5c55\u5f00\u8f7b\u529f\u6025\u9a70\u3002 \u3000\u3000\u4f46\u96ea\u5d29\u6bd4\u4e4b\u9a6c\u9a70\u4eba\u5954\u66f4\u52a0\u8fc5\u6377\uff0c\u9877\u523b\u95f4\u4fbf\u5df2\u6eda\u5230\u4e86\u5c71\u4e0b\uff0c\u9003\u5f97\u8f83\u6162\u4e4b\u4eba\u7acb\u65f6\u88ab\u538b\u5728\u5982\u5c71\u5982\u6d77\u7684\u767d\u96ea\u4e4b\u4e2d\uff0c\u8fde\u53eb\u58f0\u90fd\u7acb\u65f6\u88ab\u96ea\u6df9\u6ca1\uff0c\u4efb\u4ed6\u6b66\u529f\u518d\u9ad8\uff0c\u90a3\u4e5f\u662f\u534a\u70b9\u65bd\u5c55\u4e0d\u51fa\u4e86\u3002 \u3000\u3000\u7fa4\u8c6a\u76f4\u9003\u8fc7\u4e00\u6761\u5c71\u5761\uff0c\u773c\u89c1\u5d29\u51b2\u800c\u4e0b\u7684\u79ef\u96ea\u88ab\u5c71\u5761\u6321\u4f4f\uff0c\u4e0d\u518d\u6d8c\u6765\uff0c\u5404\u4eba\u53c8\u5404\u5954\u51fa\u6570\u5341\u4e08\uff0c\u8fd9\u624d\u5148\u540e\u505c\u6b65\u3002\u4f46\u89c1\u5c71\u4e0a\u767d\u96ea\u5140\u81ea\u5982\u5c71\u6d2a\u7206\u53d1\uff0c\u6cb3\u5824\u9661\u51b3\uff0c\u6eda\u6eda\u4e0d\u7edd\u7684\u51b2\u5c06\u884c\u6765\uff0c\u77ac\u606f\u4e4b\u95f4\u4fbf\u5c06\u5c71\u9053\u8c37\u53e3\u5c01\u4f4f\u4e86\uff0c\u9ad8\u8038\u6570\u5341\u4e08\uff0c\u5e73\u5730\u9661\u751f\u96ea\u5cf0\u3002 \u3000\u3000\u4f17\u4eba\u5446\u4e86\u826f\u4e45\uff0c\u624d\u7eb7\u7eb7\u8bae\u8bba\uff0c\u90fd\u8bf4\u8840\u5200\u50e7\u5e08\u5f92\u4e8c\u4eba\u6076\u8d2f\u6ee1\u76c8\uff0c\u846c\u8eab\u4e8e\u5bd2\u51b0\u79ef\u96ea\u4e4b\u4e0b\uff0c\u81ea\u662f\u4eba\u5fc3\u5927\u5feb\uff0c\u4e0d\u8fc7\u6b7b\u5f97\u592a\u8fc7\u5bb9\u6613\uff0c\u5012\u662f\u4fbf\u5b9c\u4ed6\u4eec\u4e86\uff0c\u66f4\u7d2f\u5f97\u5982\u82b1\u4f3c\u7389\u7684\u6c34\u7b19\u548c\u4ed6\u4eec\u540c\u6b7b\u3002 \u3000\u3000\u4e5f\u6709\u4eba\u60cb\u60dc\u76f8\u8bc6\u7684\u670b\u53cb\u6b7b\u4e8e\u975e\u547d\uff0c\u4f46\u5404\u4eba\u5927\u96be\u4e0d\u6b7b\uff0c\u8c01\u90fd\u5e86\u5e78\u9003\u8fc7\u4e86\u707e\u52ab\uff0c\u4e3a\u81ea\u5df1\u6b22\u559c\u4e4b\u60c5\uff0c\u8fdc\u80dc\u4e8e\u75db\u60dc\u670b\u53cb\u4e4b\u6b7b\u3002 \u3000\u3000\u5404\u4eba\u60ca\u9b42\u7a0d\u5b9a\uff0c\u68c0\u70b9\u4eba\u6570\uff0c\u4e00\u5171\u5c11\u4e86\u4e00\u5341\u4e8c\u4eba\uff0c\u5176\u4e2d\u6709\u201c\u94c3\u5251\u53cc\u4fa0\u201d\u4e4b\u4e00\u7684\u6c6a\u5578\u98ce\uff0c\u4ee5\u53ca\u5357\u56db\u5947\u201c\u843d\u82b1\u6d41\u6c34\u201d\u56db\u4eba\u3002 \u3000\u3000\u6c34\u5cb1\u5173\u5fc3\u7231\u5973\uff0c\u6c6a\u5578\u98ce\u7275\u6302\u7231\u4fa3\uff0c\u81ea\u662f\u594b\u4e0d\u987e\u8eab\u7684\u8ffd\u5728\u6700\u524d\uff0c\u5176\u4f59\u4e09\u5947\u56e0\u4e0e\u6c34\u5cb1\u7684\u4ea4\u60c5\u4e0e\u4f17\u4e0d\u540c\uff0c\u4e5f\u662f\u4e0d\u80af\u843d\u540e\u3002\u60f3\u4e0d\u5230\u8fd9\u4e00\u5f79\u4e2d\uff0c\u540d\u9707\u5f53\u4e16\u3001\u6b66\u529f\u7edd\u4f26\u7684\u201c\u5357\u56db\u5947\u201d\u7adf\u7136\u4e00\u9f50\u4e27\u8eab\u5728\u5ddd\u85cf\u4e4b\u4ea4\u7684\u5927\u96ea\u5c71\u4e2d\u3002 \u3000\u3000\u5404\u4eba\u53f9\u606f\u4e86\u4e00\u756a\uff0c\u4fbf\u5373\u89c5\u8def\u4e0b\u5c71\u3002\u5927\u5bb6\u90fd\u8bf4\uff0c\u4e0d\u5230\u660e\u5e74\u590f\u5929\uff0c\u5cad\u4e0a\u7684\u767e\u4e08\u79ef\u96ea\u51b3\u4e0d\u6d88\u878d\uff0c\u6b7b\u8005\u7684\u5bb6\u5c5e\u4fbf\u8981\u524d\u6765\u6536\u5c38\uff0c\u4e5f\u5f97\u7b49\u4e0a\u5927\u534a\u5e74\u624d\u884c\u3002 \u3000\u3000\u6709\u4e9b\u4eba\u5fc3\u4e2d\uff0c\u6697\u6697\u8fd8\u5b58\u5728\u4e00\u4e2a\u5ff5\u5934\uff0c\u53ea\u662f\u4e0d\u4fbf\u516c\u7136\u8bf4\u51fa\u53e3\u6765\uff1a\u201c\u5357\u56db\u5947\u548c\u94c3\u5251\u53cc\u4fa0\u8fd9\u4e9b\u5e74\u6765\u5f97\u4e86\u597d\u5927\u7684\u540d\u5934\uff0c\u8000\u6b66\u626c\u5a01\uff0c\u4e0d\u53ef\u4e00\u4e16\u3002\u6b7b\u5f97\u597d\uff0c\u6b7b\u5f97\u5999\uff01\u201d \u3000\u3000\u8840\u5200\u8001\u7956\u5e26\u7740\u72c4\u4e91\u548c\u6c34\u7b19\u4e00\u8def\u897f\u9003\uff0c\u654c\u4eba\u867d\u6108\u6765\u6108\u4f17\uff0c\u4f46\u4ed6\u79bb\u897f\u85cf\u8001\u5de2\u5374\u4e5f\u8d8a\u6765\u8d8a\u8fd1\u3002\u53ea\u662f\u8fde\u65e5\u8d76\u8def\uff0c\u518d\u52a0\u4e0a\u6f2b\u5929\u98ce\u96ea\uff0c\u5c71\u9053\u5d0e\u5c96\uff0c\u6240\u4e58\u7684\u4e24\u5339\u826f\u9a79\u811a\u529b\u518d\u5f3a\uff0c\u4e5f\u5df2\u652f\u6301\u4e0d\u4f4f\u3002\u8fd9\u4e00\u65e5\u9ec4\u9a6c\u7ec8\u4e8e\u5012\u6bd9\u9053\u65c1\uff0c\u767d\u9a6c\u4e5f\u662f\u4e00\u8ddb\u4e00\u62d0\uff0c\u773c\u770b\u4fbf\u8981\u6b65\u9ec4\u9a6c\u7684\u540e\u5c18\u3002 \u3000\u3000\u8840\u5200\u8001\u7956\u7709\u5934\u6df1\u76b1\uff0c\u5fc3\u60f3\uff1a\u201c\u6211\u4e00\u4eba\u8981\u8131\u8eab\u800c\u8d70\uff0c\u90a3\u662f\u5bb9\u6613\u4e4b\u6781\uff0c\u53ea\u662f\u5f92\u5b59\u513f\u7684\u817f\u8ddb\u4e86\uff0c\u884c\u8d70\u4e0d\u5f97\uff0c\u518d\u8ba9\u8fd9\u7f8e\u8c8c\u7684\u5973\u5a03\u513f\u7ed9\u4eba\u593a\u4e86\u56de\u53bb\uff0c\u5b9e\u662f\u5fc3\u6709\u4e0d\u7518\u3002\u201d\u4ed6\u60f3\u5230\u6b64\u5904\uff0c\u7a81\u7136\u51f6\u6027\u5927\u53d1\uff0c\u56de\u8fc7\u8eab\u6765\uff0c\u4e00\u628a\u6402\u4f4f\u6c34\u7b19\uff0c\u4fbf\u53bb\u626f\u5979\u8863\u886b\u3002 \u3000\u3000\u6c34\u7b19\u5413\u5f97\u5927\u53eb\uff1a\u201c\u4f60\u2026\u2026\u4f60\u5e72\u751a\u4e48\uff1f\u201d\u8840\u5200\u50e7\u559d\u9053\uff1a\u201c\u8001\u5b50\u4e0d\u5e26\u4f60\u8d70\u4e86\uff0c\u4f60\u8fd8\u4e0d\u660e\u767d\uff1f\u201d\u72c4\u4e91\u53eb\u9053\uff1a\u201c\u5e08\u7956\uff0c\u654c\u4eba\u4fbf\u8ffd\u4e0a\u6765\u5566\uff01\u201d\u8840\u5200\u50e7\u6012\u9053\uff1a\u201c\u4f60\u7f57\u55e6\u751a\u4e48\uff1f\u201d\u4fbf\u5728\u8fd9\u5371\u6025\u7684\u5f53\u53e3\uff0c\u5ffd\u542c\u5f97\u5934\u9876\u6089\u6089\u745f\u745f\uff0c\u53d1\u51fa\u5f02\u58f0\uff0c\u62ac\u5934\u4e00\u770b\uff0c\u5c71\u5cf0\u4e0a\u7684\u79ef\u96ea\u6b63\u6eda\u6eda\u800c\u4e0b\u3002 \u3000\u3000\u8840\u5200\u50e7\u4e45\u5728\u85cf\u8fb9\uff0c\u89c1\u8fc7\u4e0d\u5c11\u6b21\u96ea\u5d29\u5927\u707e\uff0c\u4ed6\u4fbf\u518d\u72c2\u608d\u51f6\u6deb\u5341\u500d\uff0c\u4e5f\u4e0d\u6562\u548c\u8fd9\u5929\u8c61\u5947\u53d8\u4f5c\u5bf9\uff0c\u8fde\u53eb\uff1a\u201c\u5feb\u8d70\uff0c\u5feb\u8d70\uff01\u201d\u6e38\u76ee\u4e00\u77a5\u4e4b\u95f4\uff0c\u53ea\u6709\u5357\u8fb9\u7684\u5c71\u8c37\u9694\u7740\u4e00\u4e2a\u5c71\u5cf0\uff0c\u6216\u8bb8\u80fd\u4e0d\u53d7\u6ce2\u53ca\uff0c\u5f53\u4e0b\u60c5\u52bf\u5371\u6025\uff0c\u65e0\u6687\u7ec6\u601d\uff0c\u4e00\u62c9\u767d\u9a6c\uff0c\u53d1\u8db3\u4fbf\u5411\u5357\u8fb9\u5c71\u8c37\u4e2d\u5954\u53bb\u3002\u9976\u662f\u4ed6\u65e0\u6cd5\u65e0\u5929\uff0c\u8fd9\u65f6\u8138\u8272\u4e5f\u81ea\u53d8\u4e86\u3002\u8fd9\u5c71\u8c37\u4e4b\u65c1\u7684\u5c71\u5cf0\u4e0a\u4e5f\u6709\u79ef\u96ea\u3002\u79ef\u96ea\u6700\u53d7\u4e0d\u8d77\u58f0\u97f3\u9707\u8361\uff0c\u5f80\u5f80\u4e00\u5904\u96ea\u5d29\uff0c\u5e26\u52a8\u56db\u5468\u7fa4\u5cf0\u4e0a\u79ef\u96ea\u5c3d\u7686\u6eda\u843d\u3002 \u3000\u3000\u8840\u5200\u8001\u7956\u5c55\u5f00\u8f7b\u529f\u75be\u884c\u3002\u767d\u9a6c\u9a6e\u7740\u72c4\u4e91\u548c\u6c34\u7b19\u4e8c\u4eba\uff0c\u4e00\u8ddb\u4e00\u62d0\u7684\u5954\u8fdb\u4e86\u5c71\u8c37\u3002\u8fd9\u65f6\u96ea\u5d29\u4e4b\u58f0\u5927\u4f5c\uff0c\u8840\u5200\u8001\u7956\u671b\u7740\u8eab\u4fa7\u7684\u5c71\u5cf0\uff0c\u5fe7\u5f62\u4e8e\u8272\uff0c\u8fd9\u5f53\u513f\u771f\u6240\u8c13\u542c\u5929\u7531\u547d\uff0c\u81ea\u5df1\u4f5c\u4e0d\u8d77\u534a\u70b9\u4e3b\uff0c\u53ea\u8981\u8eab\u4fa7\u5c71\u5cf0\u4e0a\u7684\u79ef\u96ea\u4e5f\u5d29\u5c06\u4e0b\u6765\uff0c\u90a3\u4fbf\u4e07\u4e8b\u7686\u4f11\u4e86\u3002 \u3000\u3000\u96ea\u5d29\u4ece\u8d77\u59cb\u5230\u5168\u90e8\u6b62\u606f\uff0c\u4e5f\u53ea\u4e00\u76d6\u8336\u5de5\u592b\uff0c\u4f46\u8fd9\u77ed\u77ed\u7684\u65f6\u523b\u4e4b\u4e2d\uff0c\u8840\u5200\u50e7\u3001\u72c4\u4e91\u3001\u6c34\u7b19\u4e09\u4eba\u5168\u662f\u8138\u8272\u60e8\u767d\uff0c\u4f60\u671b\u671b\u6211\uff0c\u6211\u671b\u671b\u4f60\uff0c\u773c\u5149\u4e2d\u90fd\u6d41\u9732\u51fa\u6050\u60e7\u4e4b\u6781\u7684\u795e\u8272\u3002\u6c34\u7b19\u5fd8\u4e86\u81ea\u5df1\u5728\u7247\u523b\u4e4b\u524d\uff0c\u8fd8\u53ea\u76fc\u7acb\u65f6\u6b7b\u4e86\uff0c\u514d\u906d\u8fd9\u6deb\u50e7\u5e08\u5f92\u7684\u6c61\u8fb1\uff0c\u4f46\u8fd9\u65f6\u5929\u5730\u6025\u53d8\u4e4b\u9645\uff0c\u4e0d\u671f\u800c\u7136\u7684\u5bf9\u8840\u5200\u50e7\u548c\u72c4\u4e91\u751f\u51fa\u4f9d\u9760\u4e4b\u5fc3\uff0c\u603b\u76fc\u8fd9\u4e24\u4e2a\u7537\u513f\u6c49\u6709\u751a\u4e48\u6cd5\u5b50\u80fd\u52a9\u5df1\u8131\u6b64\u707e\u96be\u3002 \u3000\u3000\u7a81\u7136\u4e4b\u95f4\uff0c\u5c71\u5cf0\u4e0a\u4e00\u5757\u5c0f\u77f3\u5b50\u9aa8\u6e9c\u6e9c\u7684\u6eda\u5c06\u4e0b\u6765\u3002\u6c34\u7b19\u5413\u4e86\u4e00\u8df3\uff0c\u5c16\u58f0\u547c\u53eb\u3002\u8840\u5200\u50e7\u4f38\u5de6\u638c\u6309\u4f4f\u4e86\u5979\u5634\u5df4\uff0c\u53f3\u624b\u62cd\u62cd\u4e24\u4e0b\uff0c\u6253\u4e86\u5979\u4e24\u8bb0\u5df4\u638c\u3002\u6c34\u7b19\u4e24\u8fb9\u8138\u988a\u767b\u65f6\u7ea2\u80bf\u8d77\u6765\u3002 \u3000\u3000\u5e78\u597d\u8fd9\u5c71\u5cf0\u5411\u5357\uff0c\u591a\u53d7\u9633\u5149\uff0c\u79ef\u96ea\u4e0d\u539a\uff0c\u5cf0\u4e0a\u6eda\u4e0b\u6765\u4e00\u5757\u5c0f\u77f3\u4e4b\u540e\uff0c\u518d\u65e0\u522b\u7269\u6eda\u4e0b\u3002\u8fc7\u5f97\u7247\u523b\uff0c\u96ea\u5d29\u7684\u8f70\u8f70\u58f0\u6e10\u6e10\u6b62\u6b47\u3002\u8840\u5200\u50e7\u653e\u8131\u4e86\u6309\u5728\u6c34\u7b19\u5634\u4e0a\u7684\u624b\u638c\uff0c\u548c\u72c4\u4e91\u4e8c\u4eba\u540c\u65f6\u8212\u4e86\u4e00\u53e3\u957f\u6c14\u3002\u6c34\u7b19\u53cc\u624b\u63a9\u9762\uff0c\u4e5f\u4e0d\u77e5\u662f\u5bbd\u5fc3\uff0c\u662f\u607c\u6012\uff0c\u8fd8\u662f\u5bb3\u6015\u3002 \u3000\u3000\u8840\u5200\u50e7\u8d70\u5230\u8c37\u53e3\uff0c\u5de1\u89c6\u4e86\u4e00\u904d\u56de\u6765\uff0c\u6ee1\u8138\u90fd\u662f\u90c1\u6012\u4e4b\u8272\uff0c\u5750\u5728\u4e00\u5757\u5c71\u77f3\u4e4b\u4e0a\uff0c\u4e0d\u58f0\u4e0d\u54cd\u3002\u72c4\u4e91\u95ee\u9053\uff1a\u201c\u5e08\u7956\u7237\u7237\uff0c\u5916\u9762\u600e\u6837\uff1f\u201d\u8840\u5200\u50e7\u6012\u9053\uff1a\u201c\u600e\u4e48\u6837\uff1f\u90fd\u662f\u4f60\u8fd9\u5c0f\u5b50\u7d2f\u4eba\uff01\u201d \u3000\u3000\u72c4\u4e91\u4e0d\u6562\u518d\u95ee\uff0c\u77e5\u9053\u60c5\u52bf\u751a\u662f\u4e0d\u5999\uff0c\u8fc7\u4e86\u4e00\u4f1a\uff0c\u7ec8\u4e8e\u5fcd\u4e0d\u4f4f\u53c8\u9053\uff1a\u201c\u662f\u654c\u4eba\u628a\u5b88\u4f4f\u8c37\u53e3\u5417\uff1f\u5e08\u7956\u7237\u7237\uff0c\u4f60\u4e0d\u7528\u7ba1\u6211\uff0c\u4f60\u81ea\u5df1\u4e00\u4e2a\u513f\u8d70\u7f62\u3002\u201d \u3000\u3000\u8840\u5200\u50e7\u4e00\u751f\u90fd\u548c\u51f6\u6076\u5978\u9669\u4e4b\u5f92\u4e3a\u4f0d\uff0c\u4e0d\u4f46\u6240\u7ed3\u4ea4\u7684\u670b\u53cb\u4ece\u65e0\u771f\u5fc3\u76f8\u5f85\uff0c\u8fde\u4eb2\u4f20\u5f1f\u5b50\u5982\u5b9d\u8c61\u3001\u5584\u52c7\u3001\u80dc\u8c1b\u4e4b\u8f88\uff0c\u9762\u5b50\u4e0a\u5bf9\u5e08\u7236\u5341\u5206\u656c\u754f\uff0c\u5fc3\u4e2d\u5374\u65e0\u4e00\u4e0d\u662f\u5c14\u865e\u6211\u8bc8\uff0c\u53ea\u6c42\u635f\u4eba\u5229\u5df1\uff0c\u8fd9\u65f6\u542c\u72c4\u4e91\u53eb\u4ed6\u72ec\u81ea\u9003\u8d70\uff0c\u4e0d\u7531\u5f97\u751a\u662f\u6b23\u6170\uff0c\u8138\u4e0a\u9732\u51fa\u4e00\u4e1d\u7b11\u5bb9\uff0c\u8d5e\u9053\uff1a\u201c\u4e56\u5b69\u5b50\uff0c\u4f60\u826f\u5fc3\u5012\u597d\uff01\u4e0d\u662f\u654c\u4eba\u628a\u5b88\u8c37\u53e3\uff0c\u662f\u79ef\u96ea\u5c01\u8c37\u3002\u6570\u5341\u4e08\u9ad8\u3001\u6570\u5343\u4e08\u5bbd\u7684\u5927\u96ea\uff0c\u4e0d\u5230\u6625\u5929\u96ea\u878d\uff0c\u54b1\u4eec\u518d\u4e5f\u8d70\u4e0d\u51fa\u53bb\u4e86\u3002\u8fd9\u8352\u8c37\u4e4b\u4e2d\uff0c\u6709\u751a\u4e48\u5403\u7684\uff1f\u54b1\u4eec\u600e\u80fd\u6328\u5f97\u5230\u660e\u5e74\u6625\u5929\uff1f\u201d \u3000\u3000\u72c4\u4e91\u4e00\u542c\uff0c\u4e5f\u89c9\u5c40\u52bf\u51f6\u9669\uff0c\u4f46\u773c\u524d\u6700\u7d27\u8feb\u7684\u5371\u673a\u5df2\u8fc7\uff0c\u603b\u662f\u5fc3\u4e2d\u4e00\u5bbd\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u653e\u5fc3\uff0c\u8239\u5230\u6865\u6d1e\u81ea\u4f1a\u76f4\uff0c\u5c31\u7b97\u997f\u6b7b\uff0c\u4e5f\u80dc\u4e8e\u5728\u90a3\u4e9b\u4eba\u624b\u4e2d\u53d7\u5c3d\u6298\u78e8\u800c\u6b7b\u3002\u201d\u8840\u5200\u50e7\u54a7\u5634\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e56\u5b59\u513f\u8bf4\u5f97\u4e0d\u9519\uff01\u201d\u4ece\u8170\u95f4\u62bd\u51fa\u8840\u5200\uff0c\u7ad9\u8d77\u8eab\u6765\uff0c\u8d70\u5411\u767d\u9a6c\u3002 \u3000\u3000\u6c34\u7b19\u5927\u60ca\uff0c\u53eb\u9053\uff1a\u201c\u5582\uff0c\u4f60\u8981\u5e72\u751a\u4e48\uff1f\u201d\u8840\u5200\u50e7\u7b11\u9053\uff1a\u201c\u4f60\u5012\u731c\u731c\u770b\u3002\u201d\u5176\u5b9e\u6c34\u7b19\u65e9\u5c31\u77e5\u9053\uff0c\u4ed6\u662f\u8981\u6740\u4e86\u767d\u9a6c\u6765\u5403\u3002\u8fd9\u767d\u9a6c\u548c\u5979\u4e00\u8d77\u957f\u5927\uff0c\u4e00\u5411\u5c31\u5982\u662f\u6700\u597d\u7684\u670b\u53cb\u4e00\u822c\uff0c\u5fd9\u53eb\uff1a\u201c\u4e0d\uff01 \u3000\u3000\u4e0d\uff01\u8fd9\u662f\u6211\u7684\u9a6c\uff0c\u4f60\u4e0d\u80fd\u6740\u3002\u201d\u8840\u5200\u50e7\u9053\uff1a\u201c\u5403\u5b8c\u4e86\u767d\u9a6c\uff0c\u4fbf\u8981\u5403\u4f60\u4e86\u3002\u8001\u5b50\u4eba\u8089\u4e5f\u5403\uff0c\u4e3a\u751a\u4e48\u4e0d\u80fd\u5403\u9a6c\u8089\uff01\u201d\u6c34\u7b19\u6c42\u9053\uff1a\u201c\u6c42\u6c42\u4f60\uff0c\u522b\u5bb3\u6211\u9a6c\u513f\u3002\u201d\u65e0\u53ef\u5948\u4f55\u4e4b\u4e2d\uff0c\u8f6c\u5934\u5411\u72c4\u4e91\u9053\uff1a\u201c\u8bf7\u4f60\u6c42\u6c42\u4ed6\uff0c\u522b\u6740\u6211\u7684\u9a6c\u513f\u3002\u201d \u3000\u3000\u72c4\u4e91\u89c1\u4e86\u5979\u8fd9\u526f\u60c5\u6025\u53ef\u601c\u7684\u6a21\u6837\uff0c\u5fc3\u4e0b\u4e0d\u5fcd\uff0c\u4f46\u60f3\u60c5\u52bf\u81f3\u6b64\uff0c\u90a3\u6709\u4e0d\u5bb0\u9a6c\u6765\u5403\u4e4b\u7406\uff0c\u5403\u5b8c\u4e86\u9a6c\u8089\uff0c\u53ea\u6015\u8fde\u9a6c\u978d\u5b50\u4e5f\u8981\u716e\u719f\u4e86\u6765\u5403\u3002\u4ed6\u4e0d\u613f\u89c1\u6c34\u7b19\u7684\u4f24\u5fc3\u795e\u60c5\u3002\u53ea\u5f97\u8f6c\u8fc7\u4e86\u5934\u3002 \u3000\u3000\u6c34\u7b19\u53c8\u53eb\u9053\uff1a\u201c\u6c42\u6c42\u4f60\uff0c\u522b\u6740\u6211\u7684\u9a6c\u513f\u3002\u201d\u8840\u5200\u50e7\u7b11\u9053\uff1a\u201c\u597d\uff0c\u6211\u4e0d\u6740\u4f60\u7684\u9a6c\u513f\uff01\u201d\u6c34\u7b19\u5927\u559c\uff0c\u9053\uff1a\u201c\u8c22\u8c22\u4f60\uff01\u8c22\u8c22\u4f60\uff01\u201d \u3000\u3000\u5ffd\u542c\u5f97\u55e4\u7684\u4e00\u58f0\u8f7b\u54cd\uff0c\u8840\u5200\u50e7\u72c2\u7b11\u58f0\u4e2d\uff0c\u9a6c\u5934\u5df2\u843d\uff0c\u9c9c\u8840\u6025\u55b7\u3002\u6c34\u7b19\u8fde\u65e5\u75b2\u4e4f\uff0c\u8fd9\u65f6\u60ca\u75db\u4e4b\u4e0b\uff0c\u7adf\u53c8\u6655\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u5f85\u5f97\u60a0\u60a0\u9192\u8f6c\uff0c\u4fbf\u95fb\u5230\u4e00\u80a1\u8089\u9999\uff0c\u5979\u809a\u997f\u5df2\u4e45\uff0c\u95fb\u5230\u8089\u9999\uff0c\u4e0d\u81ea\u7981\u7684\u6b22\u559c\uff0c\u4f46\u795e\u667a\u7565\u9192\uff0c\u7acb\u5373\u77e5\u9053\u662f\u5979\u7231\u9a6c\u5728\u60e8\u906d\u70e4\u7099\u3002\u4e00\u7741\u773c\uff0c\u53ea\u89c1\u8840\u5200\u50e7\u548c\u72c4\u4e91\u5750\u5728\u77f3\u4e0a\uff0c\u624b\u4e2d\u5404\u6367\u4e86\u4e00\u5927\u5757\u70e4\u5f97\u7126\u9ec4\u7684\u70e7\u8089\uff0c\u6b63\u81ea\u5f20\u53e3\u5927\u56bc\uff0c\u77f3\u65c1\u751f\u7740\u4e00\u5806\u67f4\u706b\uff0c\u4e00\u6839\u7c97\u67f4\u4e0a\u540a\u7740\u4e00\u53ea\u9a6c\u817f\uff0c\u5140\u81ea\u5728\u706b\u4e0a\u70e7\u70e4\u3002\u6c34\u7b19\u60b2\u4ece\u4e2d\u6765\uff0c\u5931\u58f0\u800c\u54ed\u3002 \u3000\u3000\u8840\u5200\u50e7\u7b11\u9053\uff1a\u201c\u4f60\u5403\u4e0d\u5403\uff1f\u201d\u6c34\u7b19\u54ed\u9053\uff1a\u201c\u4f60\u8fd9\u4e24\u4e2a\u6076\u4eba\uff0c\u5bb3\u4e86\u6211\u7684\u9a6c\u513f\uff0c\u6211\u2026\u2026\u6211\u5b9a\u8981\u62a5\u4ec7\uff01\u201d \u3000\u3000\u72c4\u4e91\u597d\u751f\u8fc7\u610f\u4e0d\u53bb\uff0c\u6b49\u7136\u9053\uff1a\u201c\u6c34\u59d1\u5a18\uff0c\u8fd9\u96ea\u8c37\u91cc\u6ca1\u522b\u7684\u53ef\u5403\uff0c\u54b1\u4eec\u603b\u4e0d\u80fd\u773c\u7741\u7741\u7684\u997f\u6b7b\u3002\u8981\u597d\u9a6c\u561b\uff0c\u53ea\u8981\u65e5\u540e\u54b1\u4eec\u80fd\u51fa\u5f97\u6b64\u8c37\uff0c\u603b\u6709\u6cd5\u5b50\u627e\u5230\u3002\u201d\u6c34\u7b19\u54ed\u9053\uff1a\u201c\u4f60\u8fd9\u4e2a\u6076\u50e7\u5047\u88c5\u597d\u4eba\uff0c\u6bd4\u8001\u6076\u50e7\u8fd8\u8981\u574f\u3002\u6211\u6068\u6b7b\u4f60\uff0c\u6211\u6068\u6b7b\u4f60\u3002\u201d\u72c4\u4e91\u65e0\u8a00\u53ef\u7b54\uff0c\u8981\u60f3\u4e0d\u5403\u9a6c\u8089\u7f62\uff0c\u5b9e\u5728\u662f\u997f\u5f97\u96be\u53d7\uff0c\u5fc3\u9053\uff1a\u201c\u4f60\u4fbf\u6068\u6b7b\u6211\uff0c\u6211\u4e5f\u4e0d\u5f97\u4e0d\u5403\u3002\u201d\u5f20\u53e3\u53c8\u5f80\u9a6c\u8089\u4e0a\u54ac\u53bb\u3002 \u3000\u3000\u8840\u5200\u50e7\u53e3\u4e2d\u5480\u56bc\u9a6c\u8089\uff0c\u659c\u76ee\u77a7\u7740\u6c34\u7b19\uff0c\u542b\u542b\u7cca\u7cca\u7684\u9053\uff1a\u201c\u5473\u9053\u4e0d\u574f\uff0c\u5f53\u771f\u4e0d\u574f\u3002\u55ef\uff0c\u8fc7\u51e0\u5929\u70e4\u8fd9\u5c0f\u599e\u513f\u6765\u5403\uff0c\u672a\u5fc5\u6709\u8fd9\u9a6c\u8089\u9999\u3002\u201d\u53c8\u60f3\uff1a\u201c\u5403\u5b8c\u4e86\u90a3\u4e2a\u599e\u513f\uff0c\u53ea\u597d\u70e4\u6211\u8fd9\u4e2a\u4e56\u5f92\u5b59\u6765\u5403\u4e86\u3002\u8fd9\u4eba\u5f88\u597d\uff0c\u5403\u4e86\u53ef\u60dc\u3002\u55ef\uff0c\u7559\u7740\u4ed6\u6700\u540e\u5403\uff0c\u603b\u7b97\u5bf9\u5f97\u4f4f\u4ed6\u3002\u201d \u3000\u3000\u4e24\u4eba\u5403\u9971\u4e86\u9a6c\u8089\uff0c\u5728\u706b\u5806\u4e2d\u53c8\u52a0\u4e9b\u67af\u679d\uff0c\u4fbf\u501a\u5728\u5927\u77f3\u4e0a\u7761\u4e86\u3002 \u3000\u3000\u72c4\u4e91\u6726\u80e7\u4e2d\u53ea\u542c\u5230\u6c34\u7b19\u62bd\u62bd\u564e\u564e\u7684\u54ed\u4e2a\u4e0d\u4f4f\uff0c\u5fc3\u4e2d\u7a81\u7136\u81ea\u4f24\uff1a\u201c\u5979\u6b7b\u4e86\u4e00\u5339\u9a6c\uff0c\u4fbf\u8fd9\u4e48\u54ed\u4e2a\u4e0d\u4f4f\u3002\u6211\u6d3b\u5728\u4e16\u4e0a\uff0c\u5374\u6ca1\u4e00\u4eba\u7275\u6302\u6211\u3002\u5f53\u6211\u6b7b\u65f6\uff0c\u770b\u6765\u8fde\u8fd9\u5934\u7272\u53e3\u4e5f\u8fd8\u4e0d\u5982\uff0c\u4e0d\u4f1a\u6709\u8c01\u4e3a\u6211\u6d41\u4e00\u6ef4\u773c\u6cea\u3002\u201d \u3000"}),showOnHomepage:!0},{name:"\u4e03\u3000\u843d\u82b1\u6d41\u6c34",content:(0,a.jsx)(a.Fragment,{children:"\u7761\u5230\u534a\u591c\uff0c\u72c4\u4e91\u5ffd\u89c9\u80a9\u5934\u88ab\u4eba\u63a8\u4e86\u4e24\u4e0b\uff0c\u5f53\u5373\u9192\u8f6c\uff0c\u53ea\u542c\u5f97\u8840\u5200\u50e7\u8f7b\u58f0\u9053\uff1a\u201c\u6709\u4eba\u6765\u4e86\uff01\u201d\u72c4\u4e91\u4e00\u60ca\uff0c\u4f46\u968f\u5373\u5927\u559c\uff0c\u5fc3\u60f3\uff1a\u201c\u65e2\u7136\u6709\u4eba\u80fd\u8fdb\u6765\uff0c\u54b1\u4eec\u4fbf\u80fd\u51fa\u53bb\u3002\u201d\u4f4e\u58f0\u9053\uff1a\u201c\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u8840\u5200\u50e7\u5411\u897f\u9996\u4e00\u6307\uff0c\u9053\uff1a\u201c\u4f60\u8eba\u7740\u522b\u4f5c\u58f0\uff0c\u654c\u4eba\u529f\u592b\u5f88\u5f3a\u3002\u201d\u72c4\u4e91\u4fa7\u8033\u503e\u542c\uff0c\u5374\u4e00\u70b9\u58f0\u97f3\u4e5f\u542c\u4e0d\u5230\u3002 \u3000\u3000\u8840\u5200\u50e7\u6301\u5200\u5728\u624b\uff0c\u8e72\u4f4e\u8eab\u5b50\uff0c\u7a81\u7136\u95f4\u5982\u7bad\u79bb\u5f26\uff0c\u6084\u6ca1\u58f0\u7684\u7a9c\u4e86\u51fa\u53bb\uff0c\u4eba\u5f71\u5728\u5c71\u5761\u4e00\u8f6c\uff0c\u4fbf\u5df2\u4e0d\u89c1\u3002\u72c4\u4e91\u597d\u751f\u4f69\u670d\uff1a \u3000\u3000\u201c\u8fd9\u4eba\u7684\u6b66\u529f\u5f53\u771f\u5389\u5bb3\u3002\u4e01\u5927\u54e5\u5018\u82e5\u4ecd\u5728\u4e16\u4e0a\uff0c\u548c\u4ed6\u76f8\u6bd4\uff0c\u4e0d\u77e5\u8c01\u9ad8\u8c01\u4e0b\uff1f\u201d\u4e00\u60f3\u5230\u4e01\u5178\uff0c\u4f38\u624b\u5f80\u6000\u4e2d\u4e00\u6478\uff0c\u5305\u7740\u4e01\u5178\u9aa8\u7070\u7684\u5305\u88f9\u4ecd\u597d\u7aef\u7aef\u7684\u5728\u6000\u91cc\u3002 \u3000\u3000\u9759\u591c\u4e4b\u4e2d\uff0c\u5ffd\u542c\u5f97\u5f53\u5f53\u4e24\u4e0b\u5175\u5203\u76f8\u4ea4\u4e4b\u58f0\u3002\u4e24\u58f0\u54cd\u8fc7\uff0c\u4fbf\u5373\u5bc2\u7136\u3002\u8fc7\u5f97\u597d\u534a\u664c\uff0c\u53c8\u662f\u5f53\u5f53\u4e24\u58f0\u3002\u72c4\u4e91\u6599\u77e5\u8840\u5200\u50e7\u5077\u88ad\u672a\u6210\uff0c\u8ddf\u654c\u4eba\u4ea4\u4e0a\u4e86\u624b\u3002\u542c\u90a3\u5175\u5203\u76f8\u4ea4\u7684\u58f0\u97f3\uff0c\u654c\u4eba\u6b66\u529f\u4f3c\u4e0d\u5728\u4ed6\u4e4b\u4e0b\u3002 \u3000\u3000\u63a5\u7740\u5f53\u5f53\u5f53\u5f53\u56db\u54cd\uff0c\u6c34\u7b19\u4e5f\u60ca\u9192\u4e86\u8fc7\u6765\u3002\u5c71\u8c37\u4e2d\u653e\u773c\u5c3d\u662f\u767d\u96ea\uff0c\u6708\u5149\u5982\u94f6\uff0c\u5728\u767d\u96ea\u4e0a\u53cd\u6620\u51fa\u6765\uff0c\u867d\u5728\u6df1\u591c\uff0c\u4ea6\u5982\u9ece\u660e\u3002\u6c34\u7b19\u5411\u72c4\u4e91\u77a7\u4e86\u4e00\u773c\uff0c\u53e3\u5507\u4e00\u52a8\uff0c\u60f3\u8981\u63a2\u95ee\uff0c\u4f46\u5fc3\u4e2d\u5bf9\u4ed6\u618e\u6068\u538c\u6076\uff0c\u53c8\u60f3\u4ed6\u672a\u5fc5\u80af\u8bb2\uff0c\u4e00\u53e5\u95ee\u8bdd\u5c06\u5230\u53e3\u8fb9\uff0c\u53c8\u7f29\u4e86\u56de\u53bb\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u5f53\u5f53\u58f0\u8d8a\u6765\u8d8a\u54cd\u3002\u72c4\u4e91\u548c\u6c34\u7b19\u540c\u65f6\u62ac\u5934\uff0c\u5411\u7740\u54cd\u58f0\u6765\u5904\u671b\u53bb\uff0c\u6708\u5149\u4e0b\u53ea\u89c1\u4e24\u6761\u4eba\u5f71\u76d8\u65cb\u6765\u53bb\uff0c\u5200\u5251\u78b0\u649e\u4e4b\u58f0\u76f4\u54cd\u5411\u4e1c\u5317\u89d2\u9ad8\u5904\u3002\u90a3\u662f\u4e00\u5ea7\u5730\u52bf\u9669\u5cfb\u7684\u5ced\u58c1\uff0c\u5806\u6ee1\u4e86\u79ef\u96ea\uff0c\u773c\u770b\u7edd\u96be\u4e0a\u53bb\uff0c\u4f46\u4e24\u4eba\u624b\u4e0a\u62c6\u62db\uff0c\u811a\u4e0b\u6beb\u4e0d\u505c\u7559\uff0c\u5200\u5251\u5149\u8292\u95ea\u70c1\u4e0b\uff0c\u4e24\u4eba\u7adf\u6597\u4e0a\u4e86\u5ced\u58c1\u3002 \u3000\u3000\u72c4\u4e91\u51dd\u76ee\u4e0a\u671b\uff0c\u77a7\u51fa\u4e0e\u8840\u5200\u50e7\u76f8\u6597\u7684\u90a3\u4eba\u8eab\u7a7f\u9053\u88c5\uff0c\u624b\u6301\u957f\u5251\uff0c\u6b63\u662f\u201c\u843d\u82b1\u6d41\u6c34\u201d\u56db\u5927\u9ad8\u624b\u4e4b\u4e00\uff0c\u4e0d\u77e5\u4ed6\u5982\u4f55\u5728\u96ea\u5d29\u5c01\u5c71\u4e4b\u540e\uff0c\u53c8\u4f1a\u95ef\u8fdb\u8c37\u6765\uff1f\u6c34\u7b19\u968f\u5373\u4e5f\u77a7\u89c1\u4e86\u90a3\u9053\u4eba\uff0c\u5927\u559c\u4e4b\u4e0b\u8131\u53e3\u800c\u547c\uff1a\u201c\u662f\u5218\u4f2f\u4f2f\uff0c\u5218\u4e58\u98ce\u4f2f\u4f2f\u5230\u4e86\uff01\u7239\u7239\uff0c\u7239\u7239\uff01 \u3000\u3000\u6211\u5728\u8fd9\u513f\u3002\u201d \u3000\u3000\u72c4\u4e91\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u60f3\uff1a\u201c\u8840\u5200\u8001\u7956\u548c\u90a3\u8001\u9053\u76f8\u6597\uff0c\u770b\u6765\u4e00\u65f6\u96be\u5206\u80dc\u8d25\u3002\u5979\u7239\u7239\u5018\u82e5\u95fb\u58f0\u8d76\u6765\uff0c\u5c82\u4e0d\u7acb\u65f6\u4fbf\u5c06\u6211\u6740\u4e86\uff1f\u201d \u3000\u3000\u5fd9\u9053\uff1a\u201c\u5582\uff0c\u4f60\u522b\u5927\u58f0\u56b7\u56b7\u7684\uff0c\u53eb\u5f97\u518d\u96ea\u5d29\u8d77\u6765\uff0c\u5927\u5bb6\u4e00\u8d77\u9001\u547d\u3002\u201d\u6c34\u7b19\u6012\u9053\uff1a\u201c\u6211\u5c31\u662f\u8981\u8ddf\u4f60\u8fd9\u6076\u548c\u5c1a\u4e00\u8d77\u9001\u547d\u3002\u201d\u5f20\u53e3\u53c8\u5927\u58f0\u53eb\u558a\uff1a\u201c\u7239\u7239\uff0c\u7239\u7239\uff0c\u6211\u5728\u8fd9\u91cc\uff01\u201d \u3000\u3000\u72c4\u4e91\u559d\u9053\uff1a\u201c\u5927\u96ea\u5d29\u4e0b\u6765\uff0c\u8fde\u4f60\u7239\u7239\u4e5f\u4e00\u8d77\u57cb\u4e86\u3002\u4f60\u60f3\u5bb3\u6b7b\u4f60\u7239\u7239\u4e0d\u662f\uff1f\u201d \u3000\u3000\u6c34\u7b19\u5fc3\u60f3\u4e0d\u9519\uff0c\u7acb\u65f6\u4fbf\u4f4f\u4e86\u53e3\uff0c\u4f46\u8f6c\u5ff5\u53c8\u60f3\uff1a\u201c\u6211\u7239\u7239\u4f55\u7b49\u672c\u4e8b\uff1f\u9002\u624d\u5927\u96ea\u5d29\uff0c\u65c1\u4eba\u90fd\u8f6c\u8eab\u9003\u4e86\uff0c\u5218\u4e58\u98ce\u4f2f\u4f2f\u8fd8\u662f\u51b2\u8fdb\u8c37\u6765\u3002\u5218\u4f2f\u4f2f\u65e2\u7136\u6765\u5f97\uff0c\u6211\u7239\u7239\u81ea\u4e5f\u6765\u5f97\u3002\u5c31\u7b97\u53eb\u5f97\u518d\u6709\u96ea\u5d29\uff0c\u6700\u591a\u662f\u538b\u6b7b\u4e86\u6211\uff0c\u7239\u7239\u603b\u662f\u65e0\u788d\u3002\u8fd9\u8001\u6076\u50e7\u5982\u6b64\u5389\u5bb3\uff0c\u8981\u662f\u4ed6\u5c06\u5218\u4f2f\u4f2f\u6740\u4e86\uff0c\u6211\u8981\u6c42\u6b7b\u4e5f\u4e0d\u5f97\u4e86\u3002\u201d\u5f53\u5373\u53c8\u5927\u58f0\u53eb\u558a\uff1a \u3000\u3000\u201c\u7239\u7239\uff0c\u7239\u7239\uff0c\u6211\u5728\u8fd9\u91cc\u3002\u201d \u3000\u3000\u72c4\u4e91\u4e0d\u77e5\u5982\u4f55\u5236\u6b62\u624d\u597d\u3002\u62ac\u5934\u5411\u8840\u5200\u8001\u7956\u77a7\u53bb\uff0c\u53ea\u89c1\u4ed6\u548c\u90a3\u8001\u9053\u5218\u4e58\u98ce\u6597\u5f97\u6b63\u7d27\uff0c\u8840\u5200\u5e7b\u6210\u4e00\u9053\u6697\u7ea2\u8272\u7684\u5149\u534e\uff0c\u5728\u7691\u7691\u767d\u96ea\u4e4b\u95f4\u76d8\u65cb\u98de\u821e\u3002\u5218\u4e58\u98ce\u51fa\u5251\u5e76\u4e0d\u5feb\u6377\uff0c\u7136\u800c\u5b88\u5f97\u4f3c\u4e4e\u751a\u4e3a\u4e25\u5bc6\u3002\u4e24\u5927\u9ad8\u624b\u640f\u51fb\uff0c\u5230\u5e95\u8c01\u5360\u4e0a\u98ce\uff0c\u72c4\u4e91\u81ea\u7136\u770b\u4e0d\u51fa\u6765\u3002\u53ea\u542c\u5f97\u6c34\u7b19\u4e0d\u505c\u53e3\u5927\u53eb\u201c\u7239\u7239\u201d\uff0c\u53eb\u5f97\u51e0\u58f0\uff0c\u6539\u53e3\u53c8\u53eb\uff1a \u3000\u3000\u201c\u8868\u54e5\uff0c\u8868\u54e5\uff01\u201d\u72c4\u4e91\u5fc3\u70e6\u610f\u4e71\uff0c\u559d\u9053\uff1a\u201c\u5c0f\u4e2b\u5934\uff0c\u4f60\u518d\u4e0d\u4f4f\u53e3\uff0c\u6211\u628a\u4f60\u820c\u5934\u5272\u4e86\u4e0b\u6765\u3002\u201d \u3000\u3000\u6c34\u7b19\u9053\uff1a\u201c\u6211\u504f\u504f\u8981\u53eb\uff01\u504f\u504f\u8981\u53eb\uff01\u201d\u53c8\u5927\u58f0\u53eb\uff1a\u201c\u7239\u7239\uff0c\u7239\u7239\uff0c\u6211\u5728\u8fd9\u91cc\uff01\u201d\u4f46\u6015\u72c4\u4e91\u771f\u7684\u8fc7\u6765\u52a8\u624b\uff0c\u7ad9\u8d77\u8eab\u6765\uff0c\u62fe\u4e86\u4e00\u5757\u77f3\u5934\u9632\u8eab\u3002\u8fc7\u4e86\u4e00\u4f1a\uff0c\u53ea\u89c1\u4ed6\u8eba\u5728\u5730\u4e0b\u4e0d\u52a8\uff0c\u731b\u5730\u60f3\u8d77\uff1a \u3000\u3000\u201c\u8fd9\u4e2a\u6076\u548c\u5c1a\u5df2\u7ed9\u6211\u548c\u8868\u54e5\u8e0f\u65ad\u4e86\u817f\uff0c\u82e5\u4e0d\u662f\u90a3\u8001\u50e7\u51fa\u624b\u76f8\u6551\uff0c\u65e9\u7ed9\u8868\u54e5\u4e00\u5251\u6740\u4e86\u3002\u4ed6\u884c\u8d70\u4e0d\u5f97\uff0c\u6211\u4f55\u5fc5\u6015\u4ed6\uff1f\u201d\u63a5\u7740\u53c8\u60f3\uff1a\u201c\u6211\u771f\u8822\u6b7b\u4e86\uff01\u90a3\u8001\u50e7\u5206\u8eab\u4e0d\u5f97\uff0c\u6211\u600e\u4e0d\u6740\u4e86\u8fd9\u5c0f\u6076\u50e7\uff1f\u201d \u3000\u3000\u4e3e\u8d77\u77f3\u5934\uff0c\u8d70\u4e0a\u51e0\u6b65\uff0c\u7528\u529b\u4fbf\u5411\u72c4\u4e91\u5934\u4e0a\u7838\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u72c4\u4e91\u65e0\u6cd5\u62b5\u6297\uff0c\u53ea\u5f97\u6253\u6eda\u9003\u5f00\uff0c\u7830\u7684\u4e00\u58f0\uff0c\u77f3\u5934\u4ece\u8138\u8fb9\u64e6\u8fc7\uff0c\u76f8\u53bb\u4e0d\u8fc7\u5bf8\u8bb8\uff0c\u51fb\u5728\u96ea\u5730\u4e4b\u4e2d\u3002\u6c34\u7b19\u4e00\u51fb\u4e0d\u4e2d\uff0c\u4fef\u8eab\u53c8\u62fe\u8d77\u4e00\u5757\u77f3\u5934\u5411\u4ed6\u63b7\u53bb\uff0c\u8fd9\u4e00\u6b21\u5374\u662f\u7838\u4ed6\u7684\u809a\u5b50\u3002\u72c4\u4e91\u7f29\u8eab\u6253\u6eda\uff0c\u4f46\u65ad\u817f\u4f38\u7f29\u4e0d\u7075\uff0c\u5580\u7684\u4e00\u58f0\uff0c\u7838\u4e2d\u4e86\u5c0f\u817f\uff0c\u53ea\u75db\u5f97\u4ed6\u957f\u58f0\u60e8\u547c\u3002 \u3000\u3000\u6c34\u7b19\u5927\u559c\uff0c\u62fe\u8d77\u4e00\u5757\u77f3\u5934\u53c8\u6b32\u6295\u63b7\u3002\u72c4\u4e91\u773c\u89c1\u81ea\u5df1\u5df2\u6210\u4fce\u4e0a\u4e4b\u8089\uff0c\u4efb\u7531\u5bb0\u5272\uff0c\u7ed9\u5979\u8fd9\u822c\u63a5\u8fde\u7838\u4e0a\u4e03\u516b\u5757\u77f3\u5934\uff0c\u54ea\u91cc\u8fd8\u6709\u547d\u5728\uff1f\u5f53\u4e0b\u4e5f\u62fe\u8d77\u4e00\u5757\u77f3\u5934\uff0c\u559d\u9053\uff1a\u201c\u4f60\u518d\u6295\u6765\uff0c\u6211\u5148\u7838\u6b7b\u4e86\u4f60\u3002\u201d\u89c1\u5979\u53c8\u662f\u4e00\u77f3\u6295\u51fa\uff0c\u5f53\u5373\u6eda\u8eab\u907f\u8fc7\uff0c\u594b\u529b\u5c06\u624b\u4e2d\u77f3\u5934\u5411\u5979\u63b7\u53bb\u3002 \u3000\u3000\u6c34\u7b19\u5411\u5de6\u95ea\u8dc3\uff0c\u77f3\u5757\u4ece\u8033\u8fb9\u64e6\u8fc7\uff0c\u64e6\u7834\u4e86\u8033\u8f6e\u76ae\u8089\uff0c\u4e0d\u7531\u5f97\u5413\u4e86\u4e00\u8df3\u3002\u5979\u4e0d\u6562\u518d\u6295\u63b7\u77f3\u5757\uff0c\u56de\u8eab\u62fe\u8d77\u4e00\u6839\u6811\u679d\uff0c\u4e00\u62db\u201c\u987a\u6c34\u63a8\u821f\u201d\uff0c\u5411\u72c4\u4e91\u80a9\u5934\u523a\u5230\u3002\u5979\u5251\u6cd5\u5bb6\u5b66\u6e0a\u6e90\uff0c\u751a\u662f\u9ad8\u660e\uff0c\u624b\u4e2d\u6240\u6267\u867d\u662f\u4e00\u6839\u6811\u679d\uff0c\u4f46\u4e00\u679d\u523a\u51fa\uff0c\u53bb\u52bf\u7075\u52a8\u3002\u72c4\u4e91\u7eb5\u7136\u5168\u8eab\u5b8c\u597d\uff0c\u5251\u62db\u4e0a\u4e5f\u4e0d\u662f\u5979\u654c\u624b\uff0c\u773c\u89c1\u6811\u679d\u523a\u5230\uff0c\u659c\u80a9\u95ea\u907f\uff0c\u6c34\u7b19\u5251\u6cd5\u5df2\u53d8\uff0c\u6258\u7684\u4e00\u58f0\uff0c\u5728\u4ed6\u989d\u5934\u91cd\u91cd\u7684\u6233\u4e86\u4e00\u4e0b\u3002 \u3000\u3000\u8fd9\u4e00\u4e0b\u5979\u624b\u4e2d\u82e5\u662f\u771f\u5251\uff0c\u65e9\u5df2\u8981\u4e86\u72c4\u4e91\u7684\u6027\u547d\uff0c\u4f46\u7eb5\u662f\u4e00\u6839\u6811\u679d\uff0c\u72c4\u4e91\u4e5f\u5df2\u75db\u5f97\u773c\u524d\u91d1\u661f\u98de\u821e\u3002\u6c34\u7b19\u9a82\u9053\uff1a\u201c\u4f60\u8fd9\u6076\u548c\u5c1a\u4e00\u8def\u4e0a\u6298\u78e8\u59d1\u5a18\uff0c\u8fd8\u8bf4\u8981\u5272\u4e86\u6211\u7684\u820c\u5934\uff0c\u4f60\u5012\u5272\u5272\u770b\uff01\u201d \u3000\u3000\u63d0\u8d77\u6811\u679d\uff0c\u5f80\u4ed6\u5934\u9876\u3001\u80a9\u80cc\u4e00\u68cd\u68cd\u7684\u72e0\u6253\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u53eb\u4f60\u5e08\u7956\u7237\u7237\u6765\u6551\u4f60\u554a\uff01\u6211\u6253\u6b7b\u4f60\u8fd9\u6076\u548c\u5c1a\uff01\u201d\u53e3\u4e2d\u65a5\u9a82\uff0c\u624b\u4e0a\u52a0\u52b2\u3002 \u3000\u3000\u72c4\u4e91\u65e0\u6cd5\u62b5\u6321\uff0c\u53ea\u6709\u4f38\u81c2\u62a4\u4f4f\u989c\u9762\uff0c\u9877\u523b\u95f4\u5934\u4e0a\u624b\u4e0a\u7ed9\u6811\u679d\u6253\u5f97\u76ae\u5f00\u8089\u7efd\uff0c\u5230\u5904\u90fd\u662f\u9c9c\u8840\u3002\u4ed6\u53c8\u75db\u53c8\u60ca\uff0c\u7a81\u7136\u95f4\u4f7f\u52b2\u4e00\u6293\uff0c\u62a2\u8fc7\u6811\u679d\uff0c\u987a\u624b\u626b\u4e86\u8fc7\u53bb\u3002\u6c34\u7b19\u4e00\u60ca\uff0c\u95ea\u8eab\u5411\u540e\u8dc3\u5f00\u51e0\u6b65\uff0c\u62fe\u8d77\u53e6\u4e00\u6839\u6811\u679d\uff0c\u53c8\u8981\u4e0a\u524d\u518d\u6253\u3002 \u3000\u3000\u72c4\u4e91\u6025\u4e2d\u751f\u667a\uff0c\u5ffd\u7136\u95f4\u60f3\u8d77\u4e61\u4e0b\u4eba\u6253\u8f93\u4e86\u67b6\u7684\u65e0\u8d56\u6cd5\u5b50\uff0c\u53eb\u9053\uff1a\u201c\u5feb\u7ed9\u6211\u7ad9\u4f4f\uff01\u4f60\u518d\u4e0a\u524d\u4e00\u6b65\uff0c\u6211\u4fbf\u8131\u88e4\u5b50\u4e86\uff01\u201d\u5634\u91cc\u53eb\u56b7\uff0c\u53cc\u624b\u62c9\u4f4f\u88e4\u8170\uff0c\u4f5c\u5373\u523b\u4fbf\u8981\u8131\u88e4\u4e4b\u72b6\u3002 \u3000\u3000\u6c34\u7b19\u5413\u4e86\u4e00\u8df3\uff0c\u6025\u5fd9\u8f6c\u8fc7\u8138\u53bb\uff0c\u53cc\u988a\u7f9e\u5f97\u98de\u7ea2\uff0c\u5fc3\u60f3\uff1a \u3000\u3000\u201c\u8fd9\u548c\u5c1a\u65e0\u6076\u4e0d\u4f5c\uff0c\u53ea\u6015\u771f\u8981\u7528\u8fd9\u79cd\u574f\u884c\u5f84\u6765\u7f9e\u8fb1\u4e8e\u6211\u3002\u201d\u72c4\u4e91\u53eb\u9053\uff1a\u201c\u5411\u524d\u8d70\u4e94\u6b65\uff0c\u79bb\u5f00\u6211\u8d8a\u8fdc\u8d8a\u597d\u3002\u201d\u6c34\u7b19\u4e00\u9897\u5fc3\u6026\u6026\u4e71\u8df3\uff0c\u679c\u7136\u4f9d\u8a00\u8d70\u524d\u4e94\u6b65\u3002\u72c4\u4e91\u5927\u559c\uff0c\u5927\u58f0\u9053\uff1a\u201c\u6211\u88e4\u5b50\u5df2\u7ecf\u8131\u4e0b\u6765\u4e86\uff0c\u4f60\u518d\u8981\u6253\u6211\uff0c\u4fbf\u8fc7\u6765\u7f62\uff01\u201d\u6c34\u7b19\u5927\u5403\u4e00\u60ca\uff0c\u7eb5\u8eab\u8dc3\u51fa\u4e08\u4f59\uff0c\u5fc3\u614c\u610f\u4e71\u4e4b\u4e0b\u4e00\u4e2a\u8e09\u8dc4\uff0c\u811a\u4e0b\u4e00\u6ed1\uff0c\u6454\u4e86\u4e00\u4ea4\uff0c\u6025\u5fd9\u722c\u8d77\u4fbf\u5954\uff0c\u54ea\u6562\u56de\u5934\uff0c\u8fdc\u8fdc\u7684\u907f\u5230\u4e86\u5c71\u5761\u540e\u9762\u3002 \u3000\u3000\u72c4\u4e91\u5176\u5b9e\u5e76\u672a\u8131\u88e4\uff0c\u60f3\u60f3\u53c8\u597d\u7b11\uff0c\u53c8\u81ea\u53f9\u5012\u9709\uff0c\u9002\u624d\u8fd9\u987f\u9971\u6253\uff0c\u5c11\u8bf4\u4e5f\u5403\u4e86\u4e09\u56db\u5341\u68cd\uff0c\u5c0f\u817f\u88ab\u77f3\u5934\u7838\u4f24\uff0c\u75db\u5f97\u66f4\u662f\u5389\u5bb3\uff0c\u5fc3\u60f3\uff1a\u201c\u82e5\u4e0d\u662f\u800d\u65e0\u8d56\u4e0b\u6d41\uff0c\u8fd9\u4f1a\u513f\u591a\u534a\u5df2\u7ed9\u6253\u5f97\u65ad\u4e86\u6c14\u5566\u3002\u6211\u72c4\u4e91\u5802\u5802\u7537\u513f\uff0c\u4eca\u65e5\u5374\u5e72\u8fd9\u7b49\u5351\u9119\u52fe\u5f53\u3002\u5509\uff0c\u5f53\u771f\u547d\u82e6\uff01\u201d \u3000\u3000\u51dd\u76ee\u5411\u5ced\u58c1\u4e0a\u671b\u53bb\uff0c\u53ea\u89c1\u8840\u5200\u50e7\u548c\u5218\u4e58\u98ce\u5df2\u6597\u4e0a\u4e86\u4e00\u5ea7\u60ac\u5d16\u3002\u5d16\u77f3\u4ece\u5c71\u58c1\u4e0a\u51f8\u4e86\u51fa\u6765\uff0c\u51ed\u865a\u4e34\u7a7a\uff0c\u79bb\u5730\u5c11\u8bf4\u4e5f\u6709\u4e03\u516b\u5341\u4e08\uff0c\u9065\u89c1\u98de\u51b0\u6e85\u96ea\uff0c\u4ece\u5d16\u4e0a\u98d8\u843d\uff0c\u8db3\u89c1\u4e24\u4eba\u5267\u6597\u4e4b\u70c8\uff0c\u6599\u60f3\u53ea\u8981\u8c01\u811a\u4e0b\u4e00\u6ed1\uff0c\u6454\u5c06\u4e0b\u6765\uff0c\u4efb\u4f60\u6b66\u529f\u518d\u9ad8\uff0c\u4e5f\u975e\u7c89\u8eab\u788e\u9aa8\u4e0d\u53ef\u3002\u72c4\u4e91\u62ac\u5934\u4e0a\u671b\uff0c\u89c9\u5f97\u90a3\u4e8c\u4eba\u7684\u8eab\u5b50\u4e5f\u5c0f\u4e86\u8bb8\u591a\u3002\u4e24\u4eba\u8863\u8896\u98d8\u821e\uff0c\u4fbf\u5982\u4e24\u4f4d\u795e\u4ed9\u5728\u4e91\u96fe\u4e2d\u98de\u817e\u4e00\u822c\u3002 \u3000\u3000\u5929\u7a7a\u4e2d\u4e24\u5934\u5140\u9e70\u5728\u76d8\u65cb\u98de\u821e\uff0c\u76f8\u8f83\u4e4b\u4e0b\uff0c\u4e0b\u9762\u76f8\u6597\u7684\u4e24\u4eba\u8eab\u6cd5\u53ef\u5feb\u5f97\u591a\u4e86\u3002 \u3000\u3000\u6c34\u7b19\u5728\u90a3\u8fb9\u5c71\u5761\u540e\u53c8\u5927\u58f0\u53eb\u558a\u8d77\u6765\uff1a\u201c\u7239\u7239\uff0c\u7239\u7239\uff0c\u5feb\u6765\u554a\uff01\u201d\u5979\u53eb\u5f97\u51e0\u58f0\uff0c\u7a81\u7136\u4e1c\u5357\u89d2\u4e0a\u4e00\u4e2a\u82cd\u8001\u7684\u58f0\u97f3\u9053\uff1a\u201c\u662f\u6c34\u4f84\u5973\u5417\uff1f\u4f60\u7239\u7239\u53d7\u4e86\u70b9\u8f7b\u4f24\uff0c\u8f6c\u773c\u4fbf\u6765\uff01\u201d\u6c34\u7b19\u542c\u5f97\u662f\u201c\u843d\u82b1\u6d41\u6c34\u201d\u56db\u8001\u4e2d\u4f4d\u5c45\u7b2c\u4e8c\u7684\u82b1\u94c1\u5e72\uff0c\u5fc3\u4e2d\u4e00\u559c\uff0c\u5fd9\u53eb\u9053\uff1a\u201c\u82b1\u4f2f\u4f2f\uff01\u6211\u7239\u7239\u5728\u54ea\u91cc\uff1f\u4ed6\u4f24\u5f97\u600e\u6837\uff1f\u201d \u3000\u3000\u500f\u5ffd\u4e4b\u95f4\uff0c\u82b1\u94c1\u5e72\u5df2\u98de\u5954\u5230\u4e86\u6c34\u7b19\u8eab\u7554\uff0c\u8bf4\u9053\uff1a\u201c\u96ea\u5d29\u65f6\u5c71\u5cf0\u4e0a\u4e00\u5757\u77f3\u5934\u6389\u5c06\u4e0b\u6765\uff0c\u7838\u5411\u9646\u4f2f\u4f2f\u5934\u9876\uff0c\u4f60\u7239\u7239\u4e3a\u4e86\u6551\u9646\u4f2f\u4f2f\uff0c\u51fa\u638c\u51fb\u77f3\u3002\u53ea\u662f\u90a3\u77f3\u5934\u5b9e\u5728\u592a\u91cd\uff0c\u4f60\u7239\u7239\u624b\u8180\u53d7\u4e86\u4e9b\u8f7b\u4f24\uff0c\u4e0d\u788d\u4e8b\u7684\u3002\u201d\u6c34\u7b19\u9053\uff1a\u201c\u6709\u4e2a\u6076\u548c\u5c1a\u5c31\u5728\u90a3\u8fb9\u2026\u2026\u4ed6\u8131\u4e0b\u4e86\u2026\u2026\u82b1\u4f2f\u4f2f\uff0c\u4f60\u5feb\u53bb\u6740\u4e86\u4ed6\u3002\u201d\u82b1\u94c1\u5e72\u9053\uff1a\u201c\u597d\uff0c\u5728\u54ea\u91cc\uff1f\u201d\u6c34\u7b19\u5411\u72c4\u4e91\u8eba\u5367\u4e4b\u5904\u4e00\u6307\uff0c\u4f46\u6015\u4e0d\u5c0f\u5fc3\u770b\u5230\u4e86\u4ed6\u8d64\u8eab\u9732\u4f53\u7684\u6a21\u6837\uff0c\u4e00\u624b\u6307\u51fa\uff0c\u53cd\u800c\u5411\u524d\u8d70\u4e86\u51e0\u6b65\u3002 \u3000\u3000\u82b1\u94c1\u5e72\u6b63\u8981\u53bb\u6740\u72c4\u4e91\uff0c\u5ffd\u542c\u5f97\u94ee\u94ee\u94ee\u94ee\u56db\u58f0\uff0c\u60ac\u5d16\u4e0a\u4f20\u6765\u91d1\u94c1\u4ea4\u9e23\u4e4b\u58f0\uff0c\u62ac\u5934\u4e00\u671b\uff0c\u4f46\u89c1\u8840\u5200\u50e7\u548c\u5218\u4e58\u98ce\u5200\u5251\u76f8\u4ea4\uff0c\u4e24\u4eba\u52a8\u4e5f\u4e0d\u52a8\uff0c\u4fbf\u5982\u7a81\u7136\u88ab\u51b0\u96ea\u51bb\u50f5\u4e86\u4e00\u822c\u3002\u77e5\u9053\u4e24\u4eba\u6597\u5230\u9163\u5904\uff0c\u5df2\u8feb\u5f97\u4ee5\u5185\u529b\u76f8\u62da\uff0c\u5bfb\u601d\uff1a\u201c\u8fd9\u8840\u5200\u6076\u50e7\u5982\u6b64\u51f6\u731b\uff0c\u5218\u8d24\u5f1f\u672a\u5fc5\u80fd\u5360\u4e0a\u98ce\uff0c\u6211\u4e0d\u4e0a\u524d\u5939\u51fb\uff0c\u66f4\u5f85\u4f55\u65f6\uff1f\u867d\u7136\u4ee5\u6211\u5728\u6b66\u6797\u4e2d\u7684\u58f0\u671b\u540d\u4f4d\uff0c\u5b9e\u4e0d\u613f\u843d\u4e2a\u8054\u624b\u653b\u5b64\u4e4b\u540d\u3002\u4f46\u4e2d\u539f\u7fa4\u8c6a\u5927\u4e3e\u8ffd\u8d76\u8840\u5200\u95e8\u4e8c\u6076\u50e7\uff0c\u65e9\u5df2\u95f9\u5f97\u6cb8\u6cb8\u626c\u626c\uff0c\u5929\u4e0b\u7686\u95fb\uff0c\u82e5\u5f97\u80fd\u4eb2\u624b\u8bdb\u4e86\u8840\u5200\u50e7\uff0c\u58f0\u540d\u4e4b\u9686\uff0c\u5b9a\u53ef\u63a9\u8fc7\u2018\u4ee5\u4e8c\u654c\u4e00\u2019\u7684\u4e0d\u5229\u3002\u201d\u5f53\u5373\u8f6c\u8eab\uff0c\u5f84\u5411\u5ced\u58c1\u80cc\u540e\u98de\u5954\u800c\u53bb\u3002 \u3000\u3000\u6c34\u7b19\u5fc3\u4e2d\u60ca\u5947\uff0c\u53eb\u9053\uff1a\u201c\u82b1\u4f2f\u4f2f\uff0c\u4f60\u5e72\u751a\u4e48\uff1f\u201d\u4e00\u53e5\u8bdd\u521a\u95ee\u51fa\u53e3\uff0c\u4fbf\u5df2\u77e5\u9053\u7b54\u6848\u3002\u53ea\u89c1\u82b1\u94c1\u5e72\u6084\u6ca1\u58f0\u7684\u5411\u5ced\u58c1\u4e0a\u6500\u53bb\uff0c\u4ed6\u53f3\u624b\u63e1\u7740\u4e00\u6839\u7eaf\u94a2\u77ed\u67aa\uff0c\u67aa\u5c16\u5728\u77f3\u58c1\u4e0a\u4e00\u6491\uff0c\u8eab\u5b50\u4fbf\u8dc3\u8d77\u4e08\u4f59\uff0c\u8eab\u5b50\u843d\u4e0b\u65f6\uff0c\u67aa\u5c16\u53c8\u6491\uff0c\u6bd4\u4e4b\u9002\u624d\u8840\u5200\u50e7\u548c\u5218\u4e58\u98ce\u8fb9\u6597\u8fb9\u4e0a\u4e4b\u65f6\u53ef\u5feb\u5f97\u591a\u4e86\u3002 \u3000\u3000\u72c4\u4e91\u521d\u65f6\u542c\u4ed6\u811a\u6b65\u4e4b\u58f0\u8fdc\u53bb\uff0c\u653e\u8fc7\u4e86\u81ea\u5df1\uff0c\u5fc3\u4e2d\u6b63\u81ea\u4e00\u5bbd\uff0c\u63a5\u7740\u4fbf\u89c1\u4ed6\u7eb5\u8dc3\u8d77\u843d\uff0c\u6500\u767b\u60ac\u5d16\uff0c\u5fcd\u4e0d\u4f4f\u5931\u58f0\u547c\u53eb\uff1a\u201c\u554a\u54df\uff01\u201d\u8fd9\u65f6\u552f\u4e00\u7684\u6307\u671b\uff0c\u53ea\u662f\u8840\u5200\u50e7\u80fd\u5728\u82b1\u94c1\u5e72\u767b\u4e0a\u5d16\u4e4b\u65f6\u5148\u5c06\u5218\u4e58\u98ce\u6740\u4e86\uff0c\u7136\u540e\u8f6c\u8eab\u548c\u82b1\u94c1\u5e72\u76f8\u6597\uff0c\u5426\u5219\u4ee5\u4e00\u654c\u4e8c\uff0c\u5fc5\u8d25\u65e0\u7591\u3002\u968f\u5373\u53c8\u60f3\uff1a\u201c\u8fd9\u5218\u4e58\u98ce\u548c\u90a3\u59d3\u82b1\u7684\u90fd\u662f\u4fa0\u4e49\u82f1\u96c4\uff0c\u8840\u5200\u8001\u7956\u5374\u660e\u660e\u662f\u7a77\u51f6\u6781\u6076\u7684\u574f\u4eba\uff0c\u6211\u5c45\u7136\u76fc\u671b\u574f\u4eba\u6740\u4e86\u597d\u4eba\uff0c\u5509\uff0c\u8fd9\u2026\u2026\u8fd9\u771f\u662f\u592a\u4e5f\u4e0d\u5bf9\u2026\u2026\u201d\u53c8\u662f\u81ea\u8d23\uff0c\u53c8\u662f\u62c5\u5fe7\uff0c\u5fc3\u4e2d\u6df7\u4e71\u4e4b\u6781\u3002 \u3000\u3000\u4fbf\u5728\u8fd9\u65f6\uff0c\u82b1\u94c1\u5e72\u5df2\u8dc3\u4e0a\u60ac\u5d16\u3002 \u3000\u3000\u8840\u5200\u50e7\u8fd0\u52b2\u548c\u5218\u4e58\u98ce\u6bd4\u62da\uff0c\u5185\u529b\u4e00\u5c42\u53c8\u4e00\u5c42\u7684\u52a0\u5f3a\uff0c\u6709\u5982\u6d77\u4e2d\u6ce2\u6d9b\uff0c\u4e00\u4e2a\u6d6a\u5934\u6253\u8fc7\uff0c\u53c8\u662f\u4e00\u4e2a\u6d6a\u5934\u6251\u4e0a\u3002\u5218\u4e58\u98ce\u662f\u592a\u6781\u540d\u5bb6\uff0c\u751f\u5e73\u94bb\u7814\u4ee5\u67d4\u514b\u521a\u4e4b\u9053\uff0c\u8840\u5200\u50e7\u5185\u529b\u6c79\u6d8c\u800c\u6765\uff0c\u4ed6\u53ea\u662f\u5c06\u5185\u529b\u8fd0\u6210\u4e00\u4e2a\u4e2a\u5706\u5708\uff0c\u5c06\u5bf9\u65b9\u6e90\u6e90\u4e0d\u7edd\u7684\u653b\u52bf\u6d88\u89e3\u4e86\u53bb\u3002\u4ed6\u8981\u5148\u7acb\u4e8e\u4e0d\u8d25\u4e4b\u5730\uff0c\u7136\u540e\u518d\u5f85\u654c\u4e4b\u53ef\u80dc\u3002\u8840\u5200\u50e7\u52b2\u529b\u867d\u5f3a\uff0c\u5185\u529b\u8fdb\u51fb\u7684\u65b9\u4f4d\u53c8\u662f\u53d8\u5e7b\u83ab\u6d4b\uff0c\u4f46\u50f5\u6301\u826f\u4e45\uff0c\u59cb\u7ec8\u5948\u4f55\u4e0d\u5f97\u654c\u624b\u3002\u4e24\u4eba\u5168\u795e\u8d2f\u6ce8\uff0c\u4e8e\u8eab\u5916\u4e8b\u7269\u5df2\u5c3d\u6570\u89c6\u800c\u4e0d\u89c1\uff0c\u542c\u800c\u4e0d\u95fb\u3002\u82b1\u94c1\u5e72\u6500\u4e0a\u5ced\u58c1\uff0c\u8dc3\u81f3\u60ac\u5d16\uff0c\u5e76\u975e\u5168\u65e0\u58f0\u606f\uff0c\u4e24\u4eba\u5374\u5747\u4e0d\u77e5\u3002 \u3000\u3000\u82b1\u94c1\u5e72\u89c1\u4e24\u4eba\u5934\u9876\u767d\u6c14\u84b8\u817e\uff0c\u5185\u529b\u5df2\u53d1\u6325\u5230\u4e86\u6781\u81f4\uff0c\u4ed6\u6084\u6084\u8d70\u5230\u4e86\u8840\u5200\u50e7\u8eab\u540e\uff0c\u4e3e\u8d77\u94a2\u67aa\uff0c\u529b\u8d2f\u53cc\u81c2\uff0c\u67aa\u5c16\u4e0a\u5bd2\u5149\u95ea\u52a8\uff0c\u52bf\u631f\u52b2\u98ce\uff0c\u5411\u4ed6\u80cc\u5fc3\u75be\u523a\u3002 \u3000\u3000\u67aa\u5c16\u7684\u5bd2\u5149\u88ab\u5c71\u58c1\u95f4\u955c\u5b50\u822c\u7684\u51b0\u96ea\u4e00\u6620\uff0c\u53d1\u51fa\u4e00\u7247\u95ea\u5149\u3002 \u3000\u3000\u8840\u5200\u50e7\u9661\u7136\u9192\u89c9\uff0c\u53ea\u89c9\u4e00\u80a1\u51cc\u5389\u4e4b\u6781\u7684\u52b2\u98ce\u6b63\u5411\u81ea\u5df1\u540e\u5fc3\u6251\u6765\uff0c\u8fd9\u65f6\u4ed6\u624b\u4e2d\u8840\u5200\u6b63\u548c\u5218\u4e58\u98ce\u7684\u957f\u5251\u76f8\u4ea4\uff0c\u8981\u5411\u524d\u63a8\u8fdb\u4e00\u5bf8\u90fd\u662f\u8270\u96be\u4e4b\u6781\uff0c\u66f4\u4e0d\u7528\u8bf4\u53d8\u62db\u56de\u5200\uff0c\u5411\u540e\u6321\u67b6\u3002\u4ed6\u5fc3\u5ff5\u8f6c\u52a8\u5947\u5feb\uff1a\u201c\u5de6\u53f3\u662f\u4e2a\u6b7b\uff0c\u5b81\u53ef\u81ea\u5df1\u6454\u6b7b\uff0c\u4e0d\u80fd\u6b7b\u5728\u654c\u4eba\u624b\u4e0b\u3002\u201d \u3000\u3000\u53cc\u819d\u4e00\u66f2\uff0c\u659c\u8eab\u5411\u5916\u6251\u51fa\uff0c\u4fbf\u5411\u5d16\u4e0b\u8df3\u843d\u3002 \u3000\u3000\u82b1\u94c1\u5e72\u8fd9\u4e00\u67aa\u51b3\u610f\u81f4\u8840\u5200\u50e7\u4e8e\u6b7b\u5730\uff0c\u4e00\u62db\u4e2d\u5e73\u67aa\u201c\u56db\u5937\u5bbe\u670d\u201d\uff0c\u52b2\u529b\u5a01\u731b\u5df2\u6781\uff0c\u54ea\u60f3\u5f97\u5230\u8840\u5200\u50e7\u7adf\u4f1a\u5728\u8fd9\u5343\u94a7\u4e00\u53d1\u4e4b\u9645\u5815\u5d16\u3002\u53ea\u542c\u5f97\u6ce2\u7684\u4e00\u58f0\u8f7b\u54cd\uff0c\u67aa\u5c16\u523a\u5165\u4e86\u5218\u4e58\u98ce\u80f8\u53e3\uff0c\u4ece\u524d\u80f8\u900f\u5165\uff0c\u540e\u80cc\u7a7f\u51fa\u3002\u4ed6\u56fa\u6536\u52bf\u4e0d\u53ca\uff0c\u5218\u4e58\u98ce\u4e5f\u6d51\u6ca1\u6599\u5230\u6709\u6b64\u4e00\u7740\u3002 \u3000\u3000\u8840\u5200\u50e7\u4ece\u534a\u7a7a\u4e2d\u6454\u4e0b\uff0c\u5730\u9762\u98de\u5feb\u7684\u8fce\u5411\u773c\u524d\uff0c\u4ed6\u5927\u559d\u4e00\u58f0\uff0c\u4e3e\u5200\u76f4\u65a9\u4e0b\u53bb\uff0c\u6b63\u597d\u65a9\u5728\u4e00\u5757\u5927\u5ca9\u77f3\u4e0a\u3002\u5f53\u7684\u4e00\u58f0\u54cd\uff0c\u8840\u5200\u5fae\u5fae\u4e00\u5f39\uff0c\u5374\u4e0d\u65ad\u6298\u3002\u4ed6\u501f\u7740\u8fd9\u4e00\u780d\u4e4b\u52bf\uff0c\u8eab\u5b50\u5411\u4e0a\u6025\u63d0\uff0c\u5de6\u624b\u6325\u638c\u51fb\u5411\u5730\u9762\uff0c\u84ec\u7684\u4e00\u58f0\u54cd\uff0c\u51b0\u96ea\u8ff8\u6563\uff0c\u8ddf\u7740\u5728\u96ea\u5730\u4e2d\u6eda\u4e86\u5341\u51e0\u8f6c\uff0c\u4e00\u780d\u4e00\u638c\u5341\u516b\u7ffb\uff0c\u7ec8\u4e8e\u6d88\u89e3\u4e86\u4e0b\u5815\u4e4b\u529b\uff0c\u54c8\u54c8\u5927\u7b11\u58f0\u4e2d\uff0c\u5df2\u7a33\u7a33\u7684\u7ad9\u5728\u5730\u4e0b\u3002 \u3000\u3000\u7a81\u7136\u95f4\u8eab\u540e\u4e00\u4eba\u559d\u9053\uff1a\u201c\u770b\u5200\uff01\u201d\u8840\u5200\u50e7\u542c\u58f0\u8fa8\u5668\uff0c\u8eab\u5b50\u4e0d\u8f6c\uff0c\u56de\u5200\u53cd\u780d\uff0c\u5f53\u7684\u4e00\u58f0\uff0c\u53cc\u5200\u76f8\u4ea4\uff0c\u4f46\u89c9\u80f8\u53e3\u4e00\u9707\uff0c\u8840\u5200\u51e0\u6b32\u8131\u624b\u98de\u51fa\uff0c\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff1a\u201c\u8fd9\u5bb6\u4f19\u5185\u529b\u5982\u6b64\u5f3a\u52b2\uff01\u201d \u3000\u3000\u4e00\u56de\u5934\uff0c\u53ea\u89c1\u90a3\u4eba\u662f\u4e2a\u8eab\u5f62\u9b41\u68a7\u7684\u8001\u8005\uff0c\u767d\u987b\u98d8\u98d8\uff0c\u5f62\u8c8c\u5a01\u731b\uff0c\u624b\u4e2d\u63d0\u7740\u4e00\u67c4\u539a\u80cc\u65b9\u5934\u7684\u9b3c\u5934\u5200\u3002\u8840\u5200\u50e7\u5fc3\u751f\u602f\u610f\uff0c\u6025\u5fd9\u95ea\u8dc3\u9000\u5f00\uff0c\u4ed3\u5352\u4e4b\u9645\uff0c\u6ca1\u60f3\u5230\u81ea\u5df1\u548c\u5218\u4e58\u98ce\u6bd4\u62da\u4e86\u8fd9\u534a\u5929\u5185\u529b\uff0c\u52b2\u529b\u5df2\u6d88\u8017\u4e86\u5927\u534a\uff0c\u800c\u4ece\u9ad8\u5904\u6389\u4e0b\uff0c\u5200\u51fb\u5ca9\u77f3\uff0c\u66f4\u662f\u5168\u51ed\u81c2\u529b\u6d88\u53bb\u4e0b\u5815\u4e4b\u52bf\u3002\u4ed6\u6697\u8fd0\u4e00\u53e3\u771f\u6c14\uff0c\u53ea\u89c9\u4e39\u7530\u4e2d\u9690\u9690\u751f\u75bc\uff0c\u5185\u529b\u7adf\u5df2\u63d0\u4e0d\u4e0a\u6765\u3002 \u3000\u3000\u5de6\u4fa7\u8fdc\u5904\u4e00\u4eba\u53eb\u9053\uff1a\u201c\u9646\u5927\u54e5\uff0c\u8fd9\u6deb\u50e7\u5bb3\u2026\u2026\u5bb3\u6b7b\u4e86\u5218\u8d24\u5f1f\u3002\u54b1\u4eec\u2026\u2026\u54b1\u4eec\u2026\u2026\u201d\u8bf4\u8bdd\u7684\u6b63\u662f\u82b1\u94c1\u5e72\u3002\u4ed6\u8bef\u6740\u4e86\u5218\u4e58\u98ce\uff0c\u60b2\u6124\u5df2\u6781\uff0c\u98de\u5feb\u7684\u8d76\u4e0b\u5ced\u58c1\uff0c\u51b3\u610f\u4e0e\u8840\u5200\u50e7\u6b7b\u62da\u3002\u6070\u597d\u201c\u5357\u56db\u8001\u201d\u4e2d\u7684\u9996\u8001\u9646\u5929\u6292\u521a\u4e8e\u8fd9\u65f6\u8d76\u5230\uff0c\u6210\u4e86\u5de6\u53f3\u5939\u51fb\u4e4b\u52bf\u3002 \u3000\u3000\u8840\u5200\u50e7\u773c\u89c1\u82b1\u94c1\u5e72\u633a\u67aa\u5954\u6765\uff0c\u81ea\u5df1\u8fde\u9646\u5929\u6292\u4e00\u4e2a\u4e5f\u6597\u4e0d\u8fc7\uff0c\u4f55\u51b5\u518d\u52a0\u4e0a\u4e2a\u597d\u624b\uff1f\u53ea\u6709\u4ee5\u6c34\u7b19\u4e3a\u8d28\uff0c\u53eb\u4ed6\u4eec\u5fc3\u6709\u6240\u5fcc\uff0c\u4e0d\u6562\u6025\u653b\uff0c\u90a3\u65f6\u518d\u56fe\u540e\u8ba1\u3002 \u3000\u3000\u5fc3\u4e2d\u5ff5\u5934\u53ea\u8fd9\u4e48\u4e00\u8f6c\uff0c\u9646\u5929\u6292\u9b3c\u5934\u5200\u6325\u52a8\uff0c\u53c8\u5288\u5c06\u8fc7\u6765\uff0c\u8840\u5200\u50e7\u8eab\u5f62\u4e00\u77ee\uff0c\u5411\u654c\u4eba\u4e0b\u4e09\u8def\u7a81\u780d\u4e8c\u5200\u3002\u9646\u5929\u6292\u8eab\u6750\u9b41\u68a7\uff0c\u4e0b\u76d8\u575a\u7a33\uff0c\u7eb5\u8dc3\u5374\u975e\u5176\u957f\uff0c\u5f53\u5373\u6325\u5200\u4e0b\u683c\u3002\u8840\u5200\u50e7\u8fd9\u4e8c\u5200\u4e43\u662f\u865a\u62db\uff0c\u53ea\u662f\u865a\u4e2d\u6709\u5b9e\uff0c\u9646\u5929\u6292\u7684\u6321\u683c\u4e2d\u82e5\u662f\u7a0d\u6709\u7834\u7efd\uff0c\u865a\u8f6c\u4e3a\u5b9e\uff0c\u7acb\u6210\u81f4\u547d\u7684\u6740\u7740\uff0c\u5f85\u89c1\u4ed6\u6a2a\u5200\u5b88\u5fa1\uff0c\u65e0\u61c8\u53ef\u51fb\uff0c\u5f53\u5373\u5411\u524d\u4e00\u51b2\uff0c\u8de8\u51fa\u4e00\u6b65\u534a\uff0c\u500f\u5ffd\u7f29\u811a\uff0c\u5411\u540e\u8dc3\u51fa\uff0c\u5982\u6b64\u58f0\u4e1c\u51fb\u897f\uff0c\u8131\u51fa\u4e86\u9b3c\u5934\u5200\u7b3c\u7f69\u7684\u5708\u5b50\u3002 \u3000\u3000\u4ed6\u51e0\u4e2a\u8d77\u843d\uff0c\u98de\u6b65\u5954\u5230\u72c4\u4e91\u8eab\u65c1\uff0c\u5374\u4e0d\u89c1\u6c34\u7b19\uff0c\u6025\u95ee\uff1a \u3000\u3000\u201c\u90a3\u599e\u513f\u5462\uff1f\u201d\u72c4\u4e91\u9053\uff1a\u201c\u5728\u90a3\u8fb9\u3002\u201d\u8bf4\u7740\u4f38\u624b\u4e00\u6307\u3002\u8840\u5200\u50e7\u6012\u9053\uff1a\u201c\u600e\u4e48\u8ba9\u5979\u9003\u4e86\uff0c\u6ca1\u6293\u4f4f\u5979\uff1f\u201d\u72c4\u4e91\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\u6293\u5979\u4e0d\u4f4f\u3002\u201d\u8840\u5200\u50e7\u6012\u6781\uff0c\u4ed6\u672c\u5c31\u5341\u5206\u86ee\u6a2a\uff0c\u6b64\u523b\u751f\u6b7b\u7cfb\u4e8e\u4e00\u7ebf\uff0c\u66f4\u662f\u51f6\u6027\u5927\u53d1\uff0c\u53f3\u811a\u98de\u51fa\uff0c\u5411\u72c4\u4e91\u8170\u95f4\u8e22\u53bb\u3002\u72c4\u4e91\u4e00\u58f0\u95f7\u54fc\uff0c\u8eab\u5b50\u98de\u8d77\uff0c\u76f4\u6454\u51fa\u53bb\u3002\u5f53\u5730\u672c\u662f\u4e2a\u9ad8\u5cf0\u73af\u7ed5\u7684\u6df1\u8c37\uff0c\u7136\u800c\u8c37\u4e2d\u6709\u8c37\uff0c\u72c4\u4e91\u8fd9\u4e00\u6454\u51fa\uff0c\u66f4\u5411\u4e0b\u9762\u7684\u8c37\u4e2d\u76f4\u5815\u3002 \u3000\u3000\u6c34\u7b19\u542c\u5f97\u58f0\u97f3\uff0c\u56de\u8fc7\u5934\u6765\uff0c\u89c1\u72c4\u4e91\u6b63\u5411\u8c37\u5e95\u5815\u53bb\uff0c\u4e00\u60ca\u4e4b\u4e0b\uff0c\u53ea\u89c1\u8840\u5200\u50e7\u5411\u81ea\u5df1\u6251\u5c06\u8fc7\u6765\u3002\u4fbf\u5728\u8fd9\u65f6\uff0c\u5ffd\u542c\u5f97\u53f3\u4fa7\u6709\u4eba\u53eb\u9053\uff1a\u201c\u7b19\u513f\uff0c\u7b19\u513f\uff01\u201d\u6b63\u662f\u7236\u4eb2\u5230\u4e86\u3002\u6c34\u7b19\u5927\u559c\uff0c\u53eb\u9053\uff1a\u201c\u7239\u7239\uff01\u201d\u8fd9\u65f6\u5979\u79bb\u7236\u4eb2\u5c1a\u8fdc\uff0c\u800c\u8840\u5200\u50e7\u5df2\u7136\u6251\u8fd1\uff0c\u4f46\u8fdc\u8fd1\u4e4b\u5dee\u4e5f\u4e0d\u8fc7\u4e09\u4e08\u5149\u666f\uff0c\u5018\u82e5\u5979\u4e0d\u51fa\u58f0\u547c\u53eb\uff0c\u4e00\u89c1\u7236\u4eb2\uff0c\u7acb\u5373\u7eb5\u8eab\u5411\u4ed6\u8dc3\u53bb\uff0c\u90a3\u5c31\u53d8\u5f97\u4eb2\u8fd1\u800c\u654c\u8fdc\u4e86\u3002\u53ef\u662f\u5979\u4e34\u654c\u7ecf\u5386\u592a\u6d45\uff0c\u60ca\u559c\u4e4b\u4e0b\uff0c\u53ea\u662f\u547c\u53eb\u201c\u7239\u7239\u201d\uff0c\u5374\u5fd8\u4e86\u8840\u5200\u50e7\u6b63\u81ea\u6251\u8fd1\u3002 \u3000\u3000\u6c34\u5cb1\u5927\u53eb\uff1a\u201c\u7b19\u513f\uff0c\u5feb\u8fc7\u6765\uff01\u201d\u6c34\u7b19\u5f53\u5373\u9192\u89c9\uff0c\u62d4\u8db3\u4fbf\u5954\u3002 \u3000\u3000\u6c34\u5cb1\u62a2\u4e0a\u63a5\u5e94\u3002 \u3000\u3000\u8840\u5200\u50e7\u6697\u53eb\uff1a\u201c\u4e0d\u597d\uff01\u201d\u8840\u5200\u8854\u5165\u53e3\u4e2d\uff0c\u4e00\u4fef\u8eab\uff0c\u53cc\u624b\u5404\u6293\u8d77\u4e00\u56e2\u96ea\uff0c\u8fd0\u52b2\u634f\u7d27\uff0c\u53f3\u624b\u4e00\u56e2\u96ea\u5148\u5411\u6c34\u5cb1\u63b7\u53bb\uff0c\u8ddf\u7740\u7b2c\u4e8c\u56e2\u96ea\u63b7\u5411\u6c34\u7b19\uff0c\u540c\u65f6\u8eab\u5b50\u5411\u524d\u6251\u51fa\u3002 \u3000\u3000\u6c34\u5cb1\u6325\u5251\u51fb\u5f00\u96ea\u56e2\uff0c\u811a\u6b65\u7a0d\u7f13\u3002\u7b2c\u4e8c\u56e2\u96ea\u5374\u6253\u5728\u6c34\u7b19\u540e\u5fc3\u201c\u7075\u53f0\u7a74\u201d\u4e0a\uff0c\u767b\u65f6\u5c06\u5979\u51fb\u5012\u3002\u8840\u5200\u50e7\u98de\u8eab\u62a2\u8fdb\uff0c\u5c06\u6c34\u7b19\u6293\u5728\u624b\u4e2d\uff0c\u987a\u624b\u70b9\u4e86\u5979\u7a74\u9053\u3002\u53ea\u542c\u5f97\u547c\u547c\u98ce\u54cd\uff0c\u659c\u523a\u91cc\u4e00\u67aa\u523a\u6765\uff0c\u6b63\u662f\u82b1\u94c1\u5e72\u5230\u4e86\u3002 \u3000\u3000\u82b1\u94c1\u5e72\u5931\u624b\u523a\u6b7b\u7ed3\u4e49\u5144\u5f1f\u5218\u4e58\u98ce\uff0c\u5fc3\u4e2d\u4f24\u75db\u6094\u6068\uff0c\u5df2\u8fbe\u4e8e\u6781\u70b9\uff0c\u8fd9\u65f6\u4e5f\u987e\u4e0d\u5f97\u6c34\u7b19\u6027\u547d\u5982\u4f55\uff0c\u52b2\u8d2f\u53cc\u81c2\uff0c\u67aa\u51fa\u5982\u98ce\u3002 \u3000\u3000\u8840\u5200\u50e7\u6325\u5200\u75be\u780d\uff0c\u5f53\u7684\u4e00\u58f0\u54cd\uff0c\u8840\u5200\u53cd\u5f39\u4e0a\u6765\uff0c\u539f\u6765\u82b1\u94c1\u5e72\u8fd9\u6839\u77ed\u67aa\u8fde\u67aa\u6746\u4e5f\u662f\u767e\u70bc\u4e4b\u94a2\uff0c\u975e\u5b9d\u5200\u5b9d\u5251\u6240\u80fd\u524a\u65ad\u3002 \u3000\u3000\u8840\u5200\u50e7\u9a82\u9053\uff1a\u201c\u4f60\u5976\u5976\u7684\uff01\u201d\u6293\u8d77\u6c34\u7b19\uff0c\u9000\u540e\u4e00\u6b65\uff0c\u4f46\u89c1\u9646\u5929\u6292\u7684\u9b3c\u5934\u5200\u53c8\u6a2a\u780d\u8fc7\u6765\u3002\u4ed6\u524d\u65e0\u53bb\u8def\uff0c\u5f3a\u654c\u5408\u56f4\uff0c\u773c\u5149\u6025\u8f6c\uff0c\u627e\u5bfb\u51fa\u8def\uff0c\u4e00\u77a5\u773c\u95f4\uff0c\u89c1\u72c4\u4e91\u5728\u4e0b\u9762\u8c37\u5e95\u5750\u4e86\u8d77\u6765\uff0c\u5fc3\u5ff5\u4e00\u52a8\uff1a\u201c\u4e0b\u9762\u79ef\u96ea\u751a\u6df1\uff0c\u8fd9\u5c0f\u5b50\u6454\u4ed6\u4e0d\u6b7b\uff01\u201d\u4f38\u81c2\u62e6\u8170\u62b1\u4f4f\u6c34\u7b19\uff0c\u7eb5\u8eab\u8df3\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u6c34\u7b19\u5c16\u53eb\u58f0\u4e2d\uff0c\u4e24\u4eba\u5760\u5165\u6df1\u8c37\u3002\u8c37\u4e2d\u79ef\u96ea\u5806\u6ee1\u4e86\u6570\u5341\u4e08\u539a\uff0c\u5e95\u4e0b\u7684\u5df2\u7ed3\u6210\u575a\u51b0\uff0c\u4e0a\u9762\u7684\u5140\u81ea\u677e\u8f6f\uff0c\u4fbf\u5982\u662f\u4e2a\u57ab\u5b50\u4e00\u822c\uff0c\u4e8c\u4eba\u7adf\u7136\u6beb\u53d1\u65e0\u635f\u3002\u8840\u5200\u50e7\u4ece\u79ef\u96ea\u4e2d\u94bb\u5c06\u4e0a\u6765\uff0c\u770b\u51c6\u4e86\u5730\u5f62\uff0c\u7ad9\u4e0a\u8c37\u53e3\u7684\u4e00\u5757\u5de8\u5ca9\uff0c\u6a2a\u5200\u5728\u624b\uff0c\u54c8\u54c8\u5927\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6709\u79cd\u7684\u4fbf\u8df3\u4e0b\u6765\u51b3\u4e2a\u6b7b\u6218\uff01\u201d \u3000\u3000\u8fd9\u5757\u5927\u5ca9\u6b63\u5c45\u8c37\u53e3\u8981\u51b2\uff0c\u6c34\u5cb1\u7b49\u82e5\u4ece\u4e0a\u9762\u8df3\u4e0b\uff0c\u5b9a\u8981\u63a0\u8fc7\u5ca9\u65c1\uff0c\u8840\u5200\u50e7\u6a2a\u5200\u4e00\u6325\uff0c\u8f7b\u8f7b\u6613\u6613\u7684\u4fbf\u5c06\u6765\u4eba\u780d\u4e3a\u4e24\u622a\u3002\u8eab\u5728\u534a\u7a7a\u4e4b\u4eba\uff0c\u6b66\u529f\u4fbf\u80dc\u5f97\u4ed6\u5341\u500d\uff0c\u4e5f\u4e0d\u80fd\u5982\u98de\u9e1f\u822c\u56de\u7fd4\u81ea\u5982\uff0c\u4e0e\u4e4b\u76f8\u640f\u3002 \u3000\u3000\u9646\u5929\u6292\u3001\u82b1\u94c1\u5e72\u3001\u6c34\u5cb1\u4e09\u4eba\u597d\u5bb9\u6613\u8ffd\u4e0a\u4e86\u8840\u5200\u50e7\uff0c\u5374\u53c8\u88ab\u4ed6\u9003\u8131\uff0c\u90fd\u6068\u5f97\u7259\u75d2\u75d2\u5730\u3002\u6c34\u5cb1\u4ee5\u5973\u513f\u4ecd\u88ab\u6deb\u50e7\u631f\u6301\uff0c\u82b1\u94c1\u5e72\u8bef\u4f24\u4e49\u5f1f\uff0c\u66f4\u662f\u6c14\u6124\u3002\u4e09\u4eba\u805a\u5728\u4e00\u8d77\uff0c\u4f4e\u58f0\u5546\u8bae\u3002 \u3000\u3000\u9646\u5929\u6292\u5916\u53f7\u201c\u4ec1\u4e49\u9646\u5927\u5200\u201d\uff1b\u82b1\u94c1\u5e72\u4eba\u79f0\u201c\u4e2d\u5e73\u65e0\u654c\u201d\uff0c\u4ee5\u201c\u4e2d\u5e73\u67aa\u201d\u4eab\u8a89\u6b66\u6797\uff1b\u6c34\u5cb1\u7684\u5916\u53f7\u53eb\u4f5c\u201c\u51b7\u6708\u5251\u201d\uff0c\u518d\u52a0\u4e0a\u201c\u67d4\u4e91\u5251\u201d\u5218\u4e58\u98ce\uff0c\u5408\u79f0\u4e3a\u201c\u843d\u82b1\u6d41\u6c34\u201d\u3002\u6240\u8c13\u201c\u843d\u82b1\u6d41\u6c34\u201d\uff0c\u5176\u5b9e\u662f\u201c\u9646\u82b1\u5218\u6c34\u201d\u3002\u8bf4\u5230\u6b66\u529f\uff0c\u672a\u5fc5\u662f\u9646\u5929\u6292\u7b2c\u4e00\uff0c\u4f46\u4ed6\u4e00\u6765\u5e74\u7eaa\u6700\u5927\uff0c\u4e8c\u6765\u5728\u6c5f\u6e56\u4e0a\u4eba\u7f18\u6781\u597d\uff0c\u56e0\u6b64\u6392\u540d\u4e3a\u201c\u5357\u56db\u5947\u201d\u4e4b\u9996\u3002\u4ed6\u6027\u5982\u70c8\u706b\uff0c\u4e8e\u4f24\u98ce\u8d25\u4fd7\u3001\u5351\u9119\u4e0d\u4e49\u4e4b\u884c\u6700\u662f\u607c\u6068\uff0c\u773c\u89c1\u8840\u5200\u50e7\u7ad9\u5728\u5ca9\u77f3\u4e0a\u8000\u6b66\u626c\u5a01\uff0c\u6c34\u7b19\u5374\u8f6f\u8f6f\u7684\u659c\u501a\u5728\u72c4\u4e91\u8eab\u4e0a\u3002\u4ed6\u4e0d\u77e5\u6c34\u7b19\u5df2\u88ab\u70b9\u4e86\u7a74\u9053\uff0c\u4e0d\u7531\u81ea\u4e3b\uff0c\u8fd8\u9053\u5979\u6027\u975e\u8d1e\u70c8\uff0c\u843d\u5165\u6deb\u50e7\u7684\u624b\u4e2d\u4e4b\u540e\u5c45\u7136\u5e76\u4e0d\u53cd\u6297\uff0c\u4e00\u6012\u4e4b\u4e0b\uff0c\u4ece\u96ea\u5730\u91cc\u62fe\u8d77\u51e0\u5757\u77f3\u5b50\u63b7\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u4ed6\u624b\u52b2\u672c\u91cd\uff0c\u8fd9\u65f6\u5c45\u9ad8\u4e34\u4e0b\uff0c\u77f3\u5757\u63b7\u4e0b\u65f6\u66f4\u662f\u52bf\u9053\u731b\u6076\u4e4b\u6781\u3002\u53ea\u542c\u7830\u562d\u3001\u7830\u562d\u4e4b\u58f0\uff0c\u56db\u5468\u5c71\u8c37\u90fd\u4f20\u51fa\u56de\u97f3\u3002\u8c37\u5e95\u96ea\u82b1\u98de\u6e85\u3002 \u3000\u3000\u8840\u5200\u50e7\u4e00\u77ee\u8eab\uff0c\u5c06\u72c4\u4e91\u548c\u6c34\u7b19\u626f\u8fc7\uff0c\u85cf\u5165\u5ca9\u77f3\u4e4b\u540e\u3002\u4ed6\u8fd9\u65f6\u5df2\u7136\u6682\u65f6\u8131\u9669\uff0c\u5bf9\u72c4\u4e91\u7684\u6012\u6c14\u4fbf\u5373\u6d88\u53bb\u3002\u4ed6\u633a\u8eab\u7ad9\u5728\u5de8\u77f3\u4e4b\u4e0a\uff0c\u6307\u7740\u9646\u3001\u82b1\u3001\u6c34\u4e09\u4eba\u7834\u53e3\u5927\u9a82\uff0c\u77f3\u5757\u63b7\u5230\uff0c\u4fbf\u5373\u95ea\u8eab\u76f8\u907f\uff0c\u5374\u54ea\u91cc\u4f24\u5f97\u5230\u4ed6\uff1f\u8fd9\u65f6\u4ed6\u624d\u671b\u89c1\u8fdc\u5904\u60ac\u5d16\u4e0a\u5218\u4e58\u98ce\u50f5\u4f0f\u4e0d\u52a8\uff0c\u56de\u60f3\u9002\u624d\u60c5\u666f\uff0c\u63a8\u77e5\u662f\u82b1\u94c1\u5e72\u5077\u88ad\u5931\u624b\uff0c\u8bef\u4f24\u540c\u4f34\uff0c\u6697\u81ea\u5e86\u5e78\u4e0d\u5df2\u3002 \u3000\u3000\u72c4\u4e91\u89c1\u5ca9\u77f3\u540e\u7684\u5c71\u58c1\u51f9\u4e86\u8fdb\u53bb\uff0c\u5b9b\u7136\u662f\u4e00\u4e2a\u5927\u5c71\u6d1e\uff0c\u5de8\u5ca9\u5c4f\u6321\u5728\u5916\uff0c\u6d1e\u4e2d\u79ef\u96ea\u751a\u8584\uff0c\u5012\u662f\u4e2a\u5b89\u8eab\u4e4b\u6240\uff0c\u89c1\u5934\u9876\u5140\u81ea\u4e0d\u4f4f\u6709\u77f3\u5757\u843d\u4e0b\uff0c\u751f\u6015\u6253\u4f24\u6c34\u7b19\uff0c\u5f53\u5373\u6a2a\u62b1\u7740\u5979\uff0c\u5c06\u5979\u653e\u8fdb\u6d1e\u4e2d\u3002\u6c34\u7b19\u5927\u60ca\uff0c\u53eb\u9053\uff1a\u201c\u522b\u78b0\u6211\uff0c\u522b\u78b0\u6211\uff01\u201d \u3000\u3000\u8840\u5200\u50e7\u5927\u7b11\uff0c\u53eb\u9053\uff1a\u201c\u597d\u5f92\u5b59\uff0c\u5e08\u7956\u7237\u7237\u5728\u5916\u8fb9\u62b5\u6321\u654c\u4eba\uff0c\u4f60\u5012\u62a2\u5148\u4eab\u8d77\u8273\u798f\u6765\u5566\uff01\u201d \u3000\u3000\u6c34\u5cb1\u548c\u9646\u3001\u82b1\u4e09\u4eba\u5728\u4e0a\u9762\u542c\u5f97\u5206\u660e\uff0c\u6c14\u5f97\u90fd\u6b32\u70b8\u7834\u4e86\u80f8\u819b\u3002 \u3000\u3000\u6c34\u7b19\u53ea\u9053\u72c4\u4e91\u771f\u7684\u610f\u56fe\u975e\u793c\uff0c\u81ea\u662f\u5341\u5206\u60ca\u60f6\uff0c\u5f85\u89c1\u5230\u4ed6\u8863\u886b\u867d\u975e\u5b8c\u6574\uff0c\u5374\u662f\u597d\u597d\u7684\u7a7f\u5728\u8eab\u4e0a\uff0c\u60f3\u8d77\u9002\u624d\u4ed6\u81ea\u79f0\u5df2\u8131\u4e86\u88e4\u5b50\uff0c\u4ee5\u81f4\u5c06\u81ea\u5df1\u5413\u8d70\uff0c\u539f\u6765\u7adf\u662f\u9a97\u4eba\u3002\u5979\u60f3\u5230\u6b64\u5904\uff0c\u8138\u4e0a\u4e00\u7ea2\uff0c\u9a82\u9053\uff1a\u201c\u9a97\u4eba\u7684\u6076\u548c\u5c1a\uff0c\u5feb\u8d70\u5f00\u3002\u201d\u72c4\u4e91\u5c06\u5979\u653e\u5165\u6d1e\u5185\uff0c\u77f3\u5757\u5df2\u6253\u5979\u4e0d\u5230\uff0c\u968f\u5373\u8d70\u5f00\u3002\u8fd9\u65f6\u4ed6\u5927\u817f\u65e2\u65ad\uff0c\u5c0f\u817f\u53c8\u53d7\u91cd\u4f24\uff0c\u54ea\u91cc\u8fd8\u8bf4\u5f97\u4e00\u4e2a\u201c\u8d70\u201d\u5b57\uff0c\u53ea\u662f\u6323\u624e\u7740\u722c\u5f00\u800c\u5df2\u3002 \u3000\u3000\u4e09\u4e0a\u4e00\u4e0b\u7684\u50f5\u6301\u4e86\u534a\u591c\uff0c\u5929\u8272\u6e10\u6e10\u660e\u4e86\u3002\u8840\u5200\u50e7\u8c03\u5300\u5185\u606f\uff0c\u529b\u6c14\u6e10\u590d\uff0c\u4e0d\u4f4f\u76d8\u7b97\uff1a\u201c\u5982\u4f55\u624d\u80fd\u8131\u8eab\uff1f\u201d\u773c\u524d\u8fd9\u4e09\u4eba\u6bcf\u4e00\u4e2a\u7684\u6b66\u529f\u90fd\u548c\u81ea\u5df1\u5728\u4f2f\u4ef2\u4e4b\u95f4\uff0c\u81ea\u5df1\u53ea\u8981\u4e00\u79bb\u5f00\u8fd9\u5757\u5ca9\u77f3\uff0c\u5931\u5374\u5730\u5f62\u4e4b\u5229\uff0c\u5c31\u907f\u4e0d\u5f00\u4ed6\u4e09\u4eba\u7684\u5408\u51fb\u4e86\u3002\u4ed6\u65e0\u6cd5\u53ef\u60f3\uff0c\u53ea\u597d\u5728\u5ca9\u4e0a\u4f38\u62f3\u821e\u817f\uff0c\u602a\u72b6\u767e\u51fa\uff0c\u5632\u5f04\u654c\u4eba\uff0c\u804a\u4ee5\u81ea\u5a31\u3002 \u3000\u3000\u9646\u5929\u6292\u8d8a\u770b\u8d8a\u6012\uff0c\u53ea\u662f\u5927\u9a82\u3002\u82b1\u94c1\u5e72\u7a81\u7136\u5fc3\u751f\u4e00\u8ba1\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u6c34\u8d24\u5f1f\uff0c\u4f60\u5230\u4e1c\u8fb9\u53bb\u5047\u88c5\u6ed1\u96ea\u4e0b\u8c37\u3002\u6211\u5230\u897f\u8fb9\u53bb\u4f6f\u653b\uff0c\u5f15\u5f97\u8fd9\u6076\u50e7\u8d70\u5f00\u963b\u6321\uff0c\u9646\u5927\u54e5\u4fbf\u53ef\u4e58\u673a\u4e0b\u53bb\u3002\u201d\u9646\u5929\u6292\u9053\uff1a\u201c\u6b64\u8ba1\u5927\u5999\u3002\u201d\u6c34\u5cb1\u9053\uff1a\u201c\u4ed6\u5982\u4e0d\u8fc7\u6765\u963b\u6321\uff0c\u54b1\u4eec\u4fbf\u771f\u7684\u6ed1\u4e0b\u8c37\u53bb\u3002\u201d\u4ed6\u548c\u82b1\u94c1\u5e72\u4e8c\u4eba\u5f53\u5373\u5206\u4ece\u5de6\u53f3\u5954\u4e86\u5f00\u53bb\u3002 \u3000\u3000\u9644\u8fd1\u767e\u4f59\u4e08\u5185\u90e8\u662f\u5ced\u58c1\uff0c\u82e5\u8981\u6ed1\u96ea\u4e0b\u8c37\uff0c\u987b\u5f97\u7ed5\u4e2a\u5927\u5708\u5b50\uff0c\u8fdc\u8fdc\u8fc7\u6765\u3002\u8840\u5200\u50e7\u89c1\u4e8c\u4eba\u5206\u5411\u5de6\u53f3\uff0c\u663e\u662f\u8981\u7ed5\u9053\u8fdb\u8c37\uff0c\u5982\u4f55\u963b\u6321\uff0c\u4e00\u65f6\u5012\u6ca1\u4e3b\u610f\uff0c\u5bfb\u601d\uff1a\u201c\u7cdf\u7cd5\uff0c\u7cdf\u7cd5\uff01\u4ed6\u4eec\u5927\u515c\u5708\u5b50\u7684\u8fc7\u6765\uff0c\u867d\u7136\u8def\u7a0b\u8fdc\u4e9b\uff0c\u4f46\u82b1\u4e0a\u4e2a\u628a\u65f6\u8fb0\uff0c\u603b\u4e5f\u80fd\u5230\u3002\u6b64\u65f6\u4e0d\u8d70\uff0c\u66f4\u5f85\u4f55\u65f6\uff1f\u4ed6\u4eec\u5927\u515c\u5708\u5b50\u6765\u653b\uff0c\u6211\u4fbf\u5927\u515c\u5708\u5b50\u7684\u9003\u4e4b\u592d\u592d\u3002\u201d\u5f53\u4e0b\u4e5f\u4e0d\u901a\u77e5\u72c4\u4e91\uff0c\u6084\u6084\u6e9c\u4e0b\u5ca9\u77f3\u3002 \u3000\u3000\u9646\u5929\u6292\u76ee\u9001\u82b1\u6c34\u4e8c\u4eba\u8fdc\u53bb\uff0c\u4f4e\u5934\u4e00\u770b\uff0c\u5df2\u4e0d\u89c1\u4e86\u8840\u5200\u50e7\u7684\u8e2a\u5f71\uff0c\u4f46\u89c1\u96ea\u5730\u4e2d\u4e00\u9053\u811a\u5370\uff0c\u901a\u5411\u897f\u5317\u800c\u53bb\uff0c\u5927\u53eb\uff1a\u201c\u82b1\u8d24\u5f1f\u3001\u6c34\u8d24\u5f1f\uff0c\u6076\u50e7\u9003\u8d70\u5566\uff0c\u5feb\u56de\u6765\uff01\u201d\u82b1\u6c34\u4e8c\u4eba\u542c\u5f97\u547c\u58f0\uff0c\u4e00\u9f50\u8f6c\u8eab\u3002 \u3000\u3000\u9646\u5929\u6292\u6025\u4e8e\u8ffd\u4eba\uff0c\u6d8c\u8eab\u8dc3\u843d\uff0c\u767b\u65f6\u4fbf\u6ca1\u5165\u8c37\u5e95\u79ef\u96ea\u3002\u4ed6\u8dc3\u4e0b\u65f6\u65e9\u5df2\u95ed\u4f4f\u547c\u5438\uff0c\u4f46\u89c9\u8eab\u5b50\u4e0d\u4f4f\u4e0b\u6c89\uff0c\u968f\u5373\u8db3\u5c16\u78b0\u5230\u4e86\u5b9e\u5730\uff0c\u5f53\u5373\u8db3\u4e0b\u4f7f\u52b2\uff0c\u8eab\u5b50\u4fbf\u5411\u4e0a\u5192\u3002\u4ed6\u5934\u9876\u521a\u8981\u4f38\u51fa\u79ef\u96ea\uff0c\u5ffd\u89c9\u80f8\u53e3\u4e00\u75db\uff0c\u5df2\u4e2d\u4e86\u654c\u4eba\u6697\u7b97\uff0c\u60ca\u6012\u4e4b\u4e0b\uff0c\u5927\u5200\u7acb\u5373\u6325\u51fa\uff0c\u53bb\u52bf\u8fc5\u6377\u65e0\u4f26\uff0c\u51ed\u7740\u624b\u4e0a\u611f\u89c9\uff0c\u5df2\u77e5\u780d\u4e2d\u4e86\u654c\u4eba\u3002\u4f46\u654c\u4eba\u53d7\u4f24\u663e\u662f\u4e0d\u91cd\uff0c\u5728\u96ea\u5e95\u53c8\u662f\u4e00\u5200\u780d\u6765\u3002 \u3000\u3000\u539f\u6765\u8840\u5200\u50e7\u542c\u5f97\u9646\u5929\u6292\u7684\u547c\u53eb\uff0c\u77e5\u4ed6\u4e0b\u4e00\u6b65\u5b9a\u662f\u7eb5\u8eab\u5165\u8c37\uff0c\u5f53\u5373\u56de\u8eab\uff0c\u94bb\u5165\u4e86\u5ca9\u77f3\u9644\u8fd1\u7684\u79ef\u96ea\u4e4b\u4e2d\u3002\u9646\u5929\u6292\u6b66\u529f\u65e2\u9ad8\uff0c\u9605\u5386\u53c8\u5bcc\uff0c\u8981\u60f3\u5bf9\u4ed6\u5077\u88ad\u6697\u7b97\uff0c\u672c\u6765\u7edd\u5c11\u53ef\u80fd\uff0c\u4f46\u4ed6\u8fd9\u65f6\u4ece\u6570\u5341\u4e08\u9ad8\u5904\u8dc3\u5165\u96ea\u4e2d\uff0c\u8fd9\u79cd\u4e8b\u751f\u5e73\u4ece\u672a\u7ecf\u5386\u8fc7\uff0c\u81ea\u662f\u5168\u795e\u8d2f\u6ce8\uff0c\u53ea\u987e\u5230\u5982\u4f55\u8fd0\u6c14\u63d0\u52b2\uff0c\u4ee5\u514d\u53d7\u4f24\u3002\u4ed6\u660e\u660e\u770b\u5230\u8840\u5200\u50e7\u5df2\u7136\u9003\u8d70\uff0c\u5c82\u77e5\u6df1\u96ea\u4e2d\u7adf\u4f1a\u4f0f\u6709\u654c\u4eba\uff0c\u5f53\u771f\u662f\u51fa\u5176\u4e0d\u610f\u4e4b\u5916\uff0c\u518d\u52a0\u4e0a\u4e2a\u51fa\u5176\u4e0d\u610f\u3002 \u3000\u3000\u4f46\u4ed6\u6bd5\u7adf\u662f\u4e2d\u539f\u6b66\u6797\u4e2d\u4e00\u7b49\u4e00\u7684\u4eba\u7269\uff0c\u80f8\u53e3\u867d\u7136\u53d7\u4f24\uff0c\u8ddf\u7740\u4fbf\u4e5f\u4f24\u4e86\u654c\u4eba\uff0c\u5237\u5237\u5237\u8fde\u73af\u4e09\u5200\uff0c\u5728\u6df1\u96ea\u4e2d\u75be\u780d\u51fa\u53bb\u3002\u4ed6\u77e5\u8840\u5200\u50e7\u884c\u5982\u9b3c\u9b45\u3002\u4e0e\u4ed6\u76f8\u6597\uff0c\u51b3\u4e0d\u53ef\u6709\u4e00\u77ac\u4e4b\u95f4\u7684\u677e\u61c8\uff0c\u8fd9\u4e09\u5200\u76f2\u76ee\u780d\u51fa\uff0c\u52b2\u529b\u5374\u662f\u975e\u540c\u5c0f\u53ef\u3002\u8840\u5200\u50e7\u53d7\u4f24\u540e\u52c9\u529b\u62db\u67b6\uff0c\u9000\u540e\u4e00\u6b65\uff0c\u4e0d\u6599\u8eab\u540e\u843d\u8db3\u4e4b\u5904\u79ef\u96ea\u5e76\u672a\u7ed3\u51b0\uff0c\u811a\u5e95\u8e0f\u4e86\u4e2a\u7a7a\uff0c\u767b\u65f6\u5411\u4e0b\u76f4\u5815\u3002 \u3000\u3000\u9646\u5929\u6292\u8fde\u73af\u4e09\u5200\u780d\u51fa\uff0c\u4e0d\u5bb9\u654c\u4eba\u6709\u4e1d\u6beb\u5598\u606f\u7684\u4f59\u88d5\uff0c\u8ddf\u7740\u53c8\u662f\u8fde\u73af\u4e09\u5200\uff0c\u4ed6\u77e5\u654c\u4eba\u5728\u81ea\u5df1\u63a5\u8fde\u516d\u5200\u786c\u653b\u4e4b\u4e0b\uff0c\u5b9a\u8981\u9000\u540e\uff0c\u5f53\u5373\u62a2\u4e0a\u5f3a\u653b\uff0c\u731b\u89c9\u8db3\u5e95\u4e00\u677e\uff0c\u8eab\u5b50\u4e5f\u76f4\u5815\u4e0b\u53bb\u3002 \u3000\u3000\u4ed6\u4e8c\u4eba\u9677\u5165\u8fd9\u8be1\u5947\u5df2\u6781\u7684\u56f0\u5883\u4e4b\u4e2d\uff0c\u90fd\u662f\u773c\u4e0d\u89c1\u7269\uff0c\u79ef\u96ea\u4e4b\u4e0b\u4e5f\u8bf4\u4e0d\u4e0a\u751a\u4e48\u542c\u98ce\u8fa8\u5668\uff0c\u8fde\u9ed1\u591c\u640f\u6597\u7684\u8bf8\u822c\u529f\u592b\u4e5f\u7528\u4e0d\u4e0a\u4e86\u3002\u4e24\u4eba\u8db3\u5c16\u4e00\u89e6\u4e0a\u5b9e\u5730\uff0c\u5404\u81ea\u4fbf\u5373\u4f7f\u5f00\u5e73\u751f\u7ec3\u5f97\u6700\u719f\u7684\u4e00\u8def\u5200\u6cd5\u3002\u8fd9\u65f6\u5934\u9876\u5341\u4f59\u4e08\u79ef\u96ea\u7f69\u76d6\uff0c\u9664\u4e86\u5c06\u654c\u4eba\u6740\u6b7b\u4e4b\u5916\uff0c\u8c01\u4e5f\u4e0d\u6562\u5148\u884c\u5411\u4e0a\u5347\u8d77\u3002\u53ea\u8981\u8c01\u5fc3\u4e2d\u5148\u602f\uff0c\u610f\u56fe\u9003\u547d\uff0c\u975e\u7ed9\u5bf9\u65b9\u780d\u6b7b\u4e0d\u53ef\u3002 \u3000\u3000\u72c4\u4e91\u542c\u5f97\u6d1e\u5916\u4e00\u9635\u5927\u547c\uff0c\u8ddf\u7740\u4fbf\u5bc2\u65e0\u58f0\u606f\uff0c\u63a2\u5934\u5f20\u671b\uff0c\u5df2\u4e0d\u89c1\u4e86\u8840\u5200\u8001\u7956\uff0c\u5374\u89c1\u5ca9\u77f3\u65c1\u7684\u767d\u96ea\u9690\u9690\u8d77\u4f0f\u6ce2\u52a8\uff0c\u4e0d\u7981\u5927\u5947\uff0c\u770b\u4e86\u4e00\u4f1a\uff0c\u624d\u660e\u767d\u96ea\u5e95\u6709\u4eba\u76f8\u6597\uff0c\u4e00\u62ac\u5934\uff0c\u53ea\u89c1\u6c34\u5cb1\u548c\u82b1\u94c1\u5e72\u4e8c\u4eba\u7ad9\u5728\u5c71\u8fb9\uff0c\u51dd\u76ee\u8c37\u5e95\uff0c\u795e\u60c5\u7126\u6025\uff0c\u90a3\u4e48\u548c\u8840\u5200\u50e7\u5728\u96ea\u5e95\u76f8\u6597\u7684\uff0c\u81ea\u7136\u662f\u9646\u5929\u6292\u4e86\u3002 \u3000\u3000\u6c34\u7b19\u4e5f\u63a2\u5934\u51fa\u6765\u89c2\u770b\uff0c\u89c1\u5230\u7236\u4eb2\u5168\u795e\u8d2f\u6ce8\u7684\u6a21\u6837\uff0c\u76f8\u8ddd\u53c8\u8fdc\uff0c\u4e00\u65f6\u4e0d\u6562\u547c\u53eb\u3002 \u3000\u3000\u82b1\u6c34\u4e8c\u4eba\u4e00\u5fc3\u60f3\u8981\u51fa\u624b\u76f8\u52a9\uff0c\u5374\u4e0d\u77e5\u5982\u4f55\u662f\u597d\u3002\u6c34\u5cb1\u9053\uff1a\u201c\u82b1\u4e8c\u54e5\uff0c\u6211\u8fd9\u5c31\u8df3\u4e0b\u53bb\u3002\u201d\u82b1\u94c1\u5e72\u6025\u9053\uff1a\u201c\u4f7f\u4e0d\u5f97\uff01\u4f60\u4e5f\u8df3\u8fdb\u96ea\u5e95\u4e0b\uff0c\u5374\u5982\u4f55\u6253\u6cd5\uff1f\u4e0b\u9762\u751a\u4e48\u4e5f\u77a7\u4e0d\u89c1\uff0c\u83ab\u8981\u2026\u2026\u83ab\u8981\u53c8\u8bef\u4f24\u4e86\u9646\u5927\u54e5\u3002\u201d\u4ed6\u4e00\u67aa\u523a\u6b7b\u4eb2\u5982\u9aa8\u8089\u7684\u5218\u4e58\u98ce\uff0c\u5fc3\u4e2d\u4e00\u76f4\u8bf4\u4e0d\u51fa\u7684\u96be\u8fc7\u3002 \u3000\u3000\u8fd9\u5904\u5883\u6c34\u5cb1\u81ea\u7136\u5e76\u975e\u4e0d\u77e5\uff0c\u81ea\u5df1\u8df3\u5165\u96ea\u5e95\uff0c\u9664\u4e86\u821e\u5251\u4e71\u524a\u4e4b\u5916\uff0c\u53c8\u54ea\u91cc\u80fd\u5206\u6e05\u654c\u53cb\uff1f\u65a9\u6b7b\u8840\u5200\u50e7\u6216\u9646\u5929\u6292\u7684\u673a\u4f1a\u662f\u4e00\u822c\u65e0\u4e8c\uff0c\u800c\u88ab\u8840\u5200\u50e7\u6216\u9646\u5929\u6292\u780d\u6b7b\u7684\u673a\u4f1a\u4e5f\u662f\u6beb\u65e0\u5206\u522b\u3002\u53ef\u662f\u5df1\u65b9\u660e\u660e\u6709\u4e24\u4e2a\u9ad8\u624b\u5728\u65c1\uff0c\u5374\u4efb\u7531\u9646\u5927\u54e5\u5b64\u8eab\u548c\u8840\u5200\u50e7\u5728\u96ea\u5e95\u62da\u547d\uff0c\u9646\u5927\u54e5\u662f\u4e3a\u6551\u81ea\u5df1\u5973\u513f\u800c\u6765\uff0c\u6b64\u523b\u8eab\u5386\u5947\u9669\uff0c\u81ea\u5df1\u5374\u9ad8\u9ad8\u5728\u4e0a\u8896\u624b\u800c\u89c2\uff0c\u5f53\u771f\u662f\u4e94\u5185\u5982\u711a\uff0c\u987f\u8db3\u6413\u624b\uff0c\u4e00\u7b79\u83ab\u5c55\u3002\u8981\u8bf4\u8df3\u4e0b\u53bb\u518d\u8bf4\u7f62\uff0c\u4f46\u4e00\u8dc3\u4e0b\uff0c\u4fbf\u662f\u52a0\u5165\u4e86\u6218\u56e2\uff0c\u4f46\u89c1\u8c37\u4e2d\u767d\u96ea\u8815\u52a8\uff0c\u8fd9\u4e00\u8df3\u4e0b\u53bb\uff0c\u8bf4\u4e0d\u5b9a\u6b63\u597d\u538b\u5728\u9646\u5929\u6292\u7684\u5934\u9876\u3002 \u3000\u3000\u8c37\u5e95\u767d\u96ea\u8d77\u4f0f\u4e00\u4f1a\uff0c\u7ec8\u4e8e\u6162\u6162\u9759\u6b62\u3002\u5d16\u4e0a\u6c34\u5cb1\u3001\u82b1\u94c1\u5e72\uff0c\u6d1e\u4e2d\u72c4\u4e91\u3001\u6c34\u7b19\uff0c\u5374\u53ea\u6709\u66f4\u52a0\u7126\u6025\uff0c\u4e0d\u77e5\u8fd9\u573a\u96ea\u5e95\u6076\u6218\u5230\u5e95\u8c01\u80dc\u8c01\u8d25\u3002\u56db\u4eba\u90fd\u662f\u5c4f\u606f\u51dd\u6c14\u3001\u76ee\u4e0d\u8f6c\u77ac\u7684\u6ce8\u89c6\u8c37\u5e95\u8fc7\u4e86\u597d\u4e00\u4f1a\uff0c\u4e00\u5904\u767d\u96ea\u6162\u6162\u9686\u8d77\uff0c\u6709\u4eba\u63a2\u5934\u4e0a\u6765\uff0c\u8fd9\u4eba\u5934\u9876\u4e0a\u90fd\u662f\u767d\u96ea\uff0c\u4e00\u65f6\u5206\u4e0d\u6e05\u662f\u4fd7\u5bb6\u8fd8\u662f\u548c\u5c1a\uff0c\u8fd9\u4eba\u6e10\u5347\u6e10\u9ad8\uff0c\u770b\u5f97\u51fa\u5934\u4e0a\u957f\u6ee1\u4e86\u767d\u53d1\u3002\u90a3\u662f\u9646\u5929\u6292\uff01 \u3000\u3000\u6c34\u7b19\u5927\u559c\uff0c\u4f4e\u58f0\u6b22\u547c\uff0c\u72c4\u4e91\u6012\u9053\uff1a\u201c\u6709\u751a\u4e48\u597d\u53eb\u7684\uff1f\u201d\u6c34\u7b19\u9053\uff1a\u201c\u4f60\u5e08\u7956\u7237\u7237\u6b7b\u5566\uff0c\u4f60\u5c0f\u548c\u5c1a\u4e5f\u547d\u4e0d\u4e45\u957f\u4e86\u3002\u201d\u8fd9\u53e5\u8bdd\u5979\u4fbf\u4e0d\u8bf4\uff0c\u72c4\u4e91\u4e5f\u5c82\u6709\u4e0d\u77e5\uff1f\u8fd9\u4e9b\u65f6\u65e5\u4e4b\u4e2d\uff0c\u4ed6\u6bcf\u5929\u548c\u8840\u5200\u50e7\u5728\u4e00\u8d77\uff0c\u201c\u8fd1\u6731\u8005\u8d64\u201d\uff0c\u4e0d\u77e5\u4e0d\u89c9\u95f4\u7adf\u4e5f\u6cbe\u4e0a\u4e86\u4e00\u70b9\u513f\u6a2a\u86ee\u66b4\u8e81\u7684\u813e\u6c14\u3002\u4f55\u51b5\u773c\u89c1\u9646\u5929\u6292\u5f97\u80dc\uff0c\u81ea\u5df1\u52bf\u5fc5\u843d\u5728\u8fd9\u4e09\u8001\u624b\u4e2d\uff0c\u66f4\u6709\u751a\u4e48\u8fa9\u767d\u7684\u673a\u4f1a\uff1f\u4ed6\u5fc3\u60c5\u5947\u6076\uff0c\u559d\u9053\uff1a\u201c\u4f60\u518d\u7f57\u55e6\uff0c\u6211\u5148\u6740\u4e86\u4f60\u3002\u201d\u6c34\u7b19\u4e00\u51db\uff0c\u4e0d\u6562\u518d\u8bf4\u3002\u5979\u88ab\u8840\u5200\u50e7\u70b9\u4e86\u7a74\u9053\uff0c\u52a8\u5f39\u4e0d\u5f97\uff0c\u72c4\u4e91\u867d\u662f\u65ad\u4e86\u817f\uff0c\u4f46\u8981\u6740\u5bb3\u81ea\u5df1\uff0c\u5374\u662f\u5bb9\u6613\u4e0d\u8fc7\u3002 \u3000\u3000\u9646\u5929\u6292\u7684\u5934\u63a2\u5728\u96ea\u9762\uff0c\u5927\u58f0\u5598\u6c14\uff0c\u52aa\u529b\u6323\u624e\uff0c\u4f3c\u60f3\u8981\u4ece\u96ea\u4e2d\u722c\u8d77\u3002\u6c34\u5cb1\u548c\u82b1\u94c1\u5e72\u9f50\u58f0\u53eb\u9053\uff1a\u201c\u9646\u5927\u54e5\uff0c\u6211\u4eec\u6765\u4e86\uff01\u201d\u4e24\u4eba\u6d8c\u8eab\u8dc3\u843d\uff0c\u6ca1\u5165\u4e86\u6df1\u96ea\uff0c\u968f\u5373\u7a9c\u4e0a\uff0c\u8dc3\u5411\u8c37\u8fb9\u7684\u5ca9\u77f3\u3002 \u3000\u3000\u4fbf\u5728\u6b64\u65f6\uff0c\u5374\u89c1\u9646\u5929\u6292\u7684\u5934\u500f\u5730\u53c8\u6ca1\u5165\u4e86\u96ea\u4e2d\uff0c\u4f3c\u4e4e\u53cc\u8db3\u88ab\u4eba\u62c9\u4f4f\u5411\u4e0b\u529b\u626f\u4e00\u822c\u3002\u4ed6\u6ca1\u5165\u4e4b\u540e\uff0c\u518d\u4e5f\u4e0d\u63a2\u5934\u4e0a\u6765\uff0c\u4f46\u8840\u5200\u50e7\u5374\u4e5f\u662f\u5f71\u8e2a\u4e0d\u89c1\u3002\u6c34\u5cb1\u548c\u82b1\u94c1\u5e72\u5bf9\u671b\u4e00\u773c\uff0c\u5fc3\u4e0b\u5747\u751a\u5fe7\u6025\uff0c\u89c1\u9646\u5929\u6292\u9002\u624d\u6ca1\u5165\u96ea\u4e2d\uff0c\u52bf\u65e2\u6025\u901f\uff0c\u53c8\u4f3c\u8eab\u4e0d\u7531\u4e3b\uff0c\u5341\u4e5d\u662f\u906d\u4e86\u654c\u4eba\u6697\u7b97\u3002 \u3000\u3000\u7a81\u7136\u95f4\u6ce2\u7684\u4e00\u58f0\u54cd\uff0c\u53c8\u6709\u4e00\u9897\u5934\u9885\u4ece\u6df1\u96ea\u4e2d\u94bb\u4e86\u4e0a\u6765\uff0c\u8fd9\u4e00\u6b21\u5374\u662f\u5934\u9876\u5149\u79c3\u79c3\u7684\u8840\u5200\u50e7\u3002\u4ed6\u54c8\u54c8\u4e00\u7b11\uff0c\u5934\u9885\u4fbf\u6ca1\u5165\u96ea\u91cc\u3002\u6c34\u5cb1\u9a82\u9053\uff1a\u201c\u8d3c\u79c3\uff01\u201d\u63d0\u5251\u6b63\u8981\u8dc3\u4e0b\u53ae\u62da\uff0c\u5ffd\u7136\u95f4\u96ea\u4e2d\u4e00\u9897\u5934\u9885\u6025\u901f\u98de\u4e0a\u3002 \u3000\u3000\u90a3\u53ea\u662f\u4e00\u4e2a\u5934\u9885\uff0c\u548c\u8eab\u5b50\u662f\u5206\u79bb\u4e86\u7684\uff0c\u767d\u53d1\u8427\u8427\uff0c\u6b63\u662f\u9646\u5929\u6292\u7684\u9996\u7ea7\u3002\u8fd9\u5934\u9885\u5411\u7a7a\u4e2d\u98de\u4e0a\u6570\u5341\u4e08\uff0c\u7136\u540e\u62cd\u7684\u4e00\u58f0\uff0c\u843d\u4e86\u4e0b\u6765\uff0c\u6ca1\u5165\u96ea\u4e2d\uff0c\u65e0\u5f71\u65e0\u8e2a\u3002 \u3000\u3000\u6c34\u7b19\u773c\u89c1\u8fd9\u822c\u602a\u5f02\u53ef\u6016\u7684\u60c5\u666f\uff0c\u5413\u5f97\u51e0\u6b32\u6655\u5012\uff0c\u8fde\u60ca\u547c\u4e5f\u53eb\u4e0d\u51fa\u58f0\u3002 \u3000\u3000\u6c34\u5cb1\u60b2\u6124\u96be\u5f53\uff0c\u957f\u58f0\u53eb\u9053\uff1a\u201c\u9646\u5927\u54e5\uff0c\u4f60\u4e3a\u5144\u5f1f\u4e27\u547d\uff0c\u82f1\u7075\u4e0d\u8fdc\uff0c\u5144\u5f1f\u4e3a\u4f60\u62a5\u4ec7\u3002\u201d\u7eb5\u8eab\u6b63\u8981\u8dc3\u51fa\uff0c\u82b1\u94c1\u5e72\u6025\u5fd9\u6293\u4f4f\u4ed6\u5de6\u81c2\uff0c\u8bf4\u9053\uff1a\u201c\u4e14\u6162\uff01\u6076\u50e7\u8eb2\u5728\u96ea\u5e95\uff0c\u4ed6\u5728\u6697\u91cc\uff0c\u54b1\u4eec\u5728\u660e\u91cc\uff0c\u80e1\u4e71\u8df3\u4e0b\u53bb\uff0c\u522b\u4e2d\u4e86\u4ed6\u7684\u6697\u7b97\u3002\u201d\u6c34\u5cb1\u4e00\u60f3\u4e0d\u9519\uff0c\u54fd\u54bd\u9053\uff1a\u201c\u90a3\u2026\u2026\u90a3\u4fbf\u5982\u4f55\uff1f\u201d\u82b1\u94c1\u5e72\u9053\uff1a\u201c\u4ed6\u5728\u96ea\u5e95\u80fd\u8017\u5f97\u51e0\u65f6\uff0c\u7ec8\u7a76\u4f1a\u8981\u4e0a\u6765\u3002\u90a3\u65f6\u54b1\u4e8c\u4eba\u8054\u624b\u76f8\u653b\uff0c\u597d\u6b79\u8981\u5c06\u4ed6\u7834\u819b\u525c\u5fc3\uff0c\u796d\u5960\u4e24\u4f4d\u5144\u5f1f\u3002\u201d\u6c34\u5cb1\u6cea\u6c34\u4ece\u816e\u8fb9\u6eda\u6eda\u800c\u4e0b\uff0c\u5fc3\u4e2d\u53ea\u9053\uff1a\u201c\u8981\u9547\u9759\uff0c\u5b9a\u4e0b\u795e\u6765\uff0c\u8fd9\u65f6\u5019\u5343\u4e07\u4e0d\u80fd\u4f24\u5fc3\uff01\u5927\u654c\u5f53\u524d\uff0c\u4e0d\u53ef\u5fc3\u6d6e\u6c14\u7c97\uff01\u201d\u4f46\u4e24\u4e2a\u6570\u5341\u5e74\u76f8\u4ea4\u7684\u77e5\u53cb\u4e00\u65e6\u4e27\u547d\uff0c\u5374\u53eb\u4ed6\u5982\u4f55\u4e0d\u60b2\u4ece\u4e2d\u6765\uff1f\u53c8\u5982\u4f55\u80fd\u591f\u6291\u6b62\uff01 \u3000\u3000\u4e24\u4eba\u671b\u5b9a\u4e86\u8840\u5200\u50e7\u9002\u624d\u94bb\u4e0a\u6765\u4e4b\u5904\uff0c\u4ece\u4e00\u5757\u5ca9\u77f3\u8dc3\u5411\u53e6\u4e00\u5757\u5ca9\u77f3\uff0c\u5e76\u80a9\u8feb\u8fd1\uff0c\u6e10\u6e10\u63a5\u8fd1\u6c34\u7b19\u548c\u72c4\u4e91\u85cf\u8eab\u7684\u77f3\u6d1e\u4e4b\u65c1\u3002 \u3000\u3000\u6c34\u7b19\u659c\u773c\u5411\u72c4\u4e91\u5077\u7768\uff0c\u5fc3\u4e2d\u76d8\u7b97\uff0c\u7b49\u7236\u4eb2\u518d\u8fd1\u5f97\u51e0\u4e08\uff0c\u8fd9\u624d\u51fa\u58f0\u547c\u53eb\uff0c\u597d\u8ba9\u4ed6\u80fd\u53ca\u65f6\u8fc7\u6765\u76f8\u6551\uff0c\u5018\u82e5\u53eb\u5f97\u65e9\u4e86\uff0c\u5c0f\u6076\u50e7\u4fbf\u4f1a\u62a2\u5148\u4e0b\u624b\u6740\u4e86\u81ea\u5df1\u3002\u72c4\u4e91\u89c1\u5230\u5979\u795e\u8272\u4e0d\u5b9a\uff0c\u773c\u73e0\u8f6c\u52a8\uff0c\u5df2\u6599\u5230\u5979\u7684\u7528\u610f\uff0c\u5047\u88c5\u95ed\u76ee\u517b\u795e\u3002\u6c34\u7b19\u4e0d\u865e\u6709\u4ed6\uff0c\u53ea\u662f\u671b\u7740\u7236\u4eb2\u3002\u7a81\u7136\u4e4b\u95f4\uff0c\u72c4\u4e91\u53cc\u624b\u5728\u5730\u4e0b\u4e00\u6491\uff0c\u8eab\u5b50\u8dc3\u8d77\uff0c\u6251\u5728\u6c34\u7b19\u80cc\u4e0a\uff0c\u53f3\u81c2\u4e00\u5f2f\uff0c\u627c\u4f4f\u4e86\u5979\u5589\u5499\u3002 \u3000\u3000\u6c34\u7b19\u5927\u5403\u4e00\u60ca\uff0c\u5f85\u8981\u547c\u53eb\uff0c\u5374\u54ea\u91cc\u53eb\u5f97\u51fa\u58f0\uff1f\u53ea\u89c9\u72c4\u4e91\u7684\u624b\u81c2\u627c\u5f97\u81ea\u5df1\u6c14\u4e5f\u900f\u4e0d\u8fc7\u6765\uff0c\u5ffd\u542c\u4ed6\u5728\u81ea\u5df1\u8033\u8fb9\u4f4e\u58f0\u9053\uff1a\u201c\u4f60\u7b54\u5e94\u4e0d\u53eb\uff0c\u6211\u5c31\u4e0d\u627c\u6b7b\u4f60\uff01\u201d\u4ed6\u8bf4\u4e86\u8fd9\u53e5\u8bdd\uff0c\u624b\u81c2\u7565\u677e\uff0c\u8ba9\u5979\u5438\u4e00\u53e3\u6c14\uff0c\u4f46\u90a3\u7c97\u7cd9\u7626\u786c\u7684\u624b\u81c2\uff0c\u5374\u59cb\u7ec8\u4e0d\u79bb\u5f00\u5979\u5589\u5934\u67d4\u5ae9\u7684\u808c\u80a4\u3002\u6c34\u7b19\u6068\u6781\uff0c\u5fc3\u4e2d\u5343\u767e\u904d\u7684\u5492\u9a82\uff0c\u53ef\u4fbf\u662f\u5948\u4f55\u4e0d\u5f97\u3002 \u3000\u3000\u6c34\u5cb1\u548c\u82b1\u94c1\u5e72\u8e72\u5728\u4e00\u5757\u5927\u5ca9\u77f3\u4e0a\uff0c\u4f46\u89c1\u96ea\u8c37\u4e2d\u7edd\u65e0\u52a8\u9759\uff0c\u90fd\u662f\u5927\u4e3a\u5947\u602a\uff0c\u4e0d\u77e5\u96ea\u5200\u50e7\u5728\u73a9\u751a\u4e48\u7384\u865a\uff0c\u600e\u80fd\u4e45\u803d\u96ea\u5e95\u3002 \u3000\u3000\u4ed6\u4eec\u60b2\u75db\u4e4b\u9645\uff0c\u6ca1\u60f3\u5230\u8840\u5200\u50e7\u81ea\u5e7c\u751f\u957f\u4e8e\u85cf\u8fb9\u51b0\u5929\u96ea\u5730\uff0c\u719f\u77e5\u51b0\u96ea\u4e4b\u6027\u3002\u5148\u524d\u4ed6\u94bb\u5165\u96ea\u5e95\u4e4b\u540e\uff0c\u7acb\u65f6\u4fbf\u4ee5\u8840\u5200\u525c\u4e86\u4e2a\u5927\u6d1e\uff0c\u4f38\u638c\u62cd\u5b9e\uff0c\u96ea\u6d1e\u4e2d\u4fbf\u5b58\u5f97\u6709\u6c14\uff0c\u6bcf\u9022\u5fc3\u8df3\u52a0\u5267\uff0c\u547c\u5438\u96be\u7ee7\uff0c\u4fbf\u63a2\u5934\u5230\u96ea\u6d1e\u4e2d\u5438\u51e0\u53e3\u6c14\u3002\u9646\u5929\u6292\u5374\u5982\u4f55\u61c2\u5f97\u8fd9\u4e2a\u7a8d\u95e8\uff0c\u4e00\u5473\u5c4f\u4f4f\u547c\u5438\uff0c\u786c\u62da\u786c\u6253\u3002\u4ed6\u5185\u529b\u867d\u7136\u5145\u6c9b\uff0c\u7ec8\u662f\u53ca\u4e0d\u4e0a\u8840\u5200\u50e7\u4e0d\u4f4f\u6362\u6c14\u3002\u4fbf\u5982\u4e24\u4eba\u5728\u6c34\u5e95\u76f8\u6597\uff0c\u4e00\u4eba\u53ef\u4ee5\u5e38\u5e38\u4e0a\u6c34\u9762\u547c\u5438\uff0c\u53e6\u4e00\u4eba\u5374\u6c89\u5728\u6c34\u5e95\uff0c\u59cb\u7ec8\u4e0d\u80fd\u4e0a\u6765\uff0c\u80dc\u8d1f\u4e4b\u6570\uff0c\u53ef\u60f3\u800c\u77e5\u3002\u9646\u5929\u6292\u6700\u540e\u5b9e\u5728\u6c14\u7a92\u96be\u71ac\uff0c\u5e72\u5192\u5947\u9669\uff0c\u63a2\u5934\u5230\u96ea\u4e0a\u5438\u6c14\uff0c\u4e0b\u4f53\u5f53\u5373\u7ed9\u8840\u5200\u50e7\u8fde\u780d\u4e09\u5200\uff0c\u6b7b\u4e8e\u96ea\u5e95\u3002 \u3000\u3000\u6c34\u5cb1\u548c\u82b1\u94c1\u5e72\u8d8a\u7b49\u8d8a\u5fc3\u7126\uff0c\u8f6c\u773c\u95f4\u8fc7\u4e86\u4e00\u70b7\u9999\u65f6\u5206\uff0c\u59cb\u7ec8\u4e0d\u89c1\u8840\u5200\u50e7\u7684\u8e2a\u8ff9\u3002\u6c34\u5cb1\u9053\uff1a\u201c\u8fd9\u6076\u50e7\u591a\u534a\u662f\u8eab\u53d7\u91cd\u4f24\uff0c\u6b7b\u5728\u96ea\u5e95\u4e86\u3002\u201d\u82b1\u94c1\u5e72\u9053\uff1a\u201c\u6211\u60f3\u591a\u534a\u4e5f\u662f\u5982\u6b64\u3002\u9646\u5927\u54e5\u5c82\u80fd\u4e3a\u6076\u50e7\u6240\u6740\uff0c\u5374\u4e0d\u8fd8\u4ed6\u4e24\u5200\uff1f\u4f55\u51b5\u8fd9\u6076\u50e7\u548c\u5218\u8d24\u5f1f\u62da\u6597\u751a\u4e45\uff0c\u65e9\u5df2\u4e0d\u662f\u9646\u5927\u54e5\u7684\u5bf9\u624b\u3002\u201d\u6c34\u5cb1\u9053\uff1a\u201c\u4ed6\u5b9a\u662f\u884c\u4f7f\u8bc8\u8ba1\uff0c\u6697\u7b97\u4e86\u9646\u5927\u54e5\u3002\u201d\u8bf4\u5230\u6b64\u5904\uff0c\u60b2\u6124\u65e0\u53ef\u6291\u5236\uff0c\u53eb\u9053\uff1a\u201c\u6211\u5230\u4e0b\u9762\u53bb\u77a7\u77a7\u3002\u201d\u82b1\u94c1\u5e72\u9053\uff1a\u201c\u597d\uff0c\u53ef\u8981\u5c0f\u5fc3\u4e86\uff0c\u6211\u5728\u8fd9\u91cc\u7ed9\u4f60\u63a0\u9635\u3002\u201d \u3000\u3000\u6c34\u5cb1\u624b\u63d0\u957f\u5251\uff0c\u5438\u4e00\u53e3\u6c14\uff0c\u5c55\u5f00\u8f7b\u529f\uff0c\u4fbf\u4ece\u96ea\u9762\u4e0a\u6ed1\u4e86\u8fc7\u53bb\uff0c\u53ea\u6ed1\u51fa\u6570\u4e08\uff0c\u5bdf\u89c9\u811a\u4e0b\u5e76\u4e0d\u5982\u4f55\u677e\u8f6f\uff0c\u5f53\u4e0b\u5954\u5f97\u66f4\u5feb\u3002 \u3000\u3000\u8fd9\u96ea\u8c37\u56db\u5468\u5c71\u5cf0\u6781\u9ad8\uff0c\u4e07\u5e74\u4e0d\u89c1\u9633\u5149\uff0c\u8c37\u5e95\u79ef\u7684\u867d\u7136\u662f\u96ea\uff0c\u5374\u65e9\u5df2\u51b0\u96ea\u76f8\u6df7\uff0c\u6709\u5982\u7a00\u6ce5\uff0c\u4ece\u4e0a\u8dc3\u4e0b\u56fa\u662f\u7acb\u65f6\u6ca1\u5165\uff0c\u4ee5\u8f7b\u529f\u6ed1\u884c\u5374\u4e0d\u81f4\u9677\u843d\uff0c\u6c34\u5cb1\u8f7b\u8eab\u529f\u592b\u751a\u662f\u4e86\u5f97\uff0c\u5728\u96ea\u9762\u4e0a\u8d8a\u6ed1\u8d8a\u5feb\u3002\u53ea\u542c\u5f97\u82b1\u94c1\u5e72\u53eb\u9053\uff1a\u201c\u597d\u8f7b\u529f\uff01\u6c34\u8d24\u5f1f\uff0c\u90a3\u6076\u50e7\u4fbf\u5728\u5de6\u8fd1\uff0c\u5c0f\u5fc3\uff01\u201d \u3000\u3000\u8bdd\u58f0\u672a\u7edd\uff0c\u5580\u5587\u4e00\u58f0\uff0c\u6c34\u5cb1\u8eab\u524d\u4e08\u8bb8\u4e4b\u5916\u94bb\u51fa\u4e00\u4e2a\u4eba\u6765\uff0c\u679c\u7136\u4fbf\u662f\u8840\u5200\u50e7\uff0c\u53ea\u89c1\u4ed6\u53cc\u624b\u7a7a\u7a7a\uff0c\u6ca1\u4e86\u5175\u5203\uff0c\u53eb\u58f0\uff1a\u201c\u554a\u54df\uff01\u201d \u3000\u3000\u4e0d\u6562\u548c\u6c34\u5cb1\u63a5\u6218\uff0c\u5411\u897f\u98d8\u5f00\u6570\u4e08\uff0c\u614c\u614c\u5f20\u5f20\u7684\u53eb\u9053\uff1a\u201c\u5927\u4e08\u592b\u76f8\u6597\uff0c\u8bb2\u7a76\u516c\u5e73\u3002\u4f60\u624b\u91cc\u6709\u5251\uff0c\u6211\u5374\u8d64\u624b\u7a7a\u62f3\uff0c\u90a3\u5982\u4f55\u6253\u6cd5\uff1f\u201d \u3000\u3000\u6c34\u5cb1\u5c1a\u672a\u7b54\u8bdd\uff0c\u82b1\u94c1\u5e72\u8fdc\u8fdc\u53eb\u9053\uff1a\u201c\u6740\u4f60\u8fd9\u6076\u50e7\uff0c\u8fd8\u8bb2\u751a\u4e48\u516c\u5e73\u4e0d\u516c\u5e73\uff1f\u201d\u4ed6\u8f7b\u529f\u4e0d\u53ca\u6c34\u5cb1\uff0c\u4e0d\u6562\u8e0f\u4e0b\u96ea\u5730\uff0c\u4ece\u65c1\u8fb9\u5ca9\u77f3\u7ed5\u5c06\u8fc7\u53bb\uff0c\u4ece\u65c1\u5939\u51fb\u3002 \u3000\u3000\u6c34\u5cb1\u5fc3\u60f3\u6076\u50e7\u8fd9\u53e3\u8840\u5200\uff0c\u5b9a\u662f\u548c\u9646\u5927\u54e5\u76f8\u6597\u4e4b\u65f6\u5728\u96ea\u4e2d\u5931\u843d\u4e86\u3002\u6df1\u8c37\u4e2d\u79ef\u96ea\u6570\u5341\u4e08\uff0c\u8fd9\u53e3\u5200\u54ea\u91cc\u8fd8\u627e\u5f97\u7740\uff1f\u4ed6\u89c1\u654c\u4eba\u6ca1\u4e86\u5175\u5203\uff0c\u66f4\u52a0\u653e\u5fc3\uff0c\u5fc5\u80dc\u4e4b\u5238\uff0c\u5df2\u64cd\u4e4b\u4e8e\u624b\uff0c\u53ea\u662f\u522b\u8981\u8ba9\u4ed6\u9003\u5f97\u8fdc\u4e86\uff0c\u6216\u662f\u65e0\u5f71\u65e0\u8e2a\u7684\u53c8\u94bb\u5165\u96ea\u4e2d\uff0c\u53eb\u9053\uff1a\u201c\u5140\u90a3\u6076\u50e7\uff0c\u6211\u5973\u513f\u5728\u54ea\u91cc\uff1f\u4f60\u8bf4\u4e86\u51fa\u6765\uff0c\u4fbf\u5c06\u4f60\u75db\u75db\u5feb\u5feb\u7684\u4e00\u5251\u6740\u4e86\uff01\u4e0d\u7ed9\u4f60\u5403\u96f6\u788e\u82e6\u5934\u3002\u201d \u3000\u3000\u8840\u5200\u50e7\u9053\uff1a\u201c\u8fd9\u599e\u513f\u7684\u85cf\u8eab\u4e4b\u6240\uff0c\u4f60\u5c31\u5bfb\u4e0a\u5341\u5929\u534a\u6708\uff0c\u4e5f\u672a\u5fc5\u5bfb\u5f97\u7740\u3002\u82e5\u662f\u653e\u6211\u751f\u8def\uff0c\u4fbf\u8ddf\u4f60\u8bf4\u3002\u201d\u53e3\u4e2d\u8bf4\u8bdd\uff0c\u811a\u4e0b\u4e1d\u6beb\u4e0d\u505c\u3002 \u3000\u3000\u6c34\u5cb1\u5fc3\u60f3\uff1a\u201c\u59d1\u4e14\u9a97\u4ed6\u4e00\u9a97\uff0c\u53eb\u4ed6\u5148\u8bf4\u4e86\u51fa\u6765\u3002\u201d\u4fbf\u9053\uff1a\u201c\u6b64\u5904\u56db\u5468\u90fd\u662f\u63d2\u7fc5\u96be\u4e0a\u7684\u9ad8\u5cf0\uff0c\u4fbf\u653e\u4e86\u4f60\uff0c\u4f60\u53c8\u8d70\u5411\u4f55\u5904\uff1f\u201d \u3000\u3000\u8840\u5200\u50e7\u9053\uff1a\u201c\u8fd9\u91cc\u7684\u5730\u52bf\u53e4\u602a\u4e4b\u6781\uff0c\u6211\u5728\u5de6\u8fd1\u4f4f\u8fc7\u51e0\u5e74\uff0c\u5374\u662f\u4e86\u5982\u6307\u638c\u3002\u4f60\u5982\u6740\u4e86\u6211\uff0c\u4e00\u5b9a\u96be\u4ee5\u51fa\u8c37\uff0c\u6d3b\u6d3b\u7684\u997f\u6b7b\u5728\u8fd9\u91cc\uff0c\u4e0d\u5982\u5927\u5bb6\u5316\u654c\u4e3a\u53cb\uff0c\u6211\u8fd8\u4f60\u5973\u513f\uff0c\u518d\u5f15\u4f60\u4eec\u51fa\u8c37\u5982\u4f55\uff1f\u201d \u3000\u3000\u82b1\u94c1\u5e72\u6012\u9053\uff1a\u201c\u6076\u50e7\u8bf4\u8bdd\uff0c\u6709\u4f55\u4fe1\u4e49\uff1f\u4f60\u5feb\u8dea\u4e0b\u6295\u964d\uff0c\u5982\u4f55\u5904\u7f6e\uff0c\u6211\u4eec\u81ea\u6709\u4e3b\u610f\uff0c\u4f55\u7528\u4f60\u6765\u63d2\u5634\uff1f\u201d\u4e00\u9762\u8bf4\uff0c\u4e00\u9762\u6e10\u6e10\u8feb\u8fd1\u3002\u8840\u5200\u50e7\u7b11\u9053\uff1a\u201c\u65e2\u662f\u5982\u6b64\uff0c\u8001\u5b50\u53ef\u8981\u5931\u966a\u4e86\uff01\u201d\u811a\u4e0b\u52a0\u5feb\uff0c\u659c\u523a\u5411\u4e1c\u5317\u89d2\u4e0a\u5954\u53bb\u3002\u6c34\u5cb1\u9a82\u9053\uff1a\u201c\u5f80\u54ea\u91cc\u53bb\uff1f\u201d\u633a\u5251\u75be\u8ffd\u3002 \u3000\u3000\u8840\u5200\u50e7\u5954\u8dd1\u8fc5\u901f\uff0c\u5954\u51fa\u6570\u5341\u4e08\u540e\uff0c\u8fce\u9762\u9ad8\u5cf0\u5f53\u9053\uff0c\u66f4\u65e0\u53bb\u8def\u3002\u4ed6\u8eab\u5f62\u4e00\u6643\uff0c\u75be\u8f6c\u56de\u5934\uff0c\u4ece\u6c34\u5cb1\u8eab\u65c1\u659c\u659c\u63a0\u8fc7\u3002\u6c34\u5cb1\u6325\u5251\u6a2a\u524a\uff0c\u5dee\u4e86\u5c3a\u8bb8\u6ca1\u80fd\u524a\u4e2d\uff0c\u8840\u5200\u50e7\u53c8\u5411\u897f\u5317\u5954\u53bb\u3002\u6c34\u5cb1\u89c1\u4ed6\u91cd\u56de\u65e7\u5730\uff0c\u5fc3\u9053\uff1a\u201c\u5728\u8fd9\u8c37\u4e2d\u5954\u6765\u5954\u53bb\uff0c\u53c8\u9003\u5f97\u5230\u54ea\u91cc\uff1f \u3000\u3000\u4e0d\u8fc7\u8001\u662f\u6349\u8ff7\u85cf\u7684\u8ffd\u9010\uff0c\u8fd9\u65af\u8f7b\u529f\u4e0d\u5f31\uff0c\u5012\u4e0d\u6613\u6740\u5f97\u4e86\u4ed6\u3002\u7b19\u513f\u53c8\u4e0d\u77e5\u5230\u4e86\u4f55\u5904\u3002\u201d\u4ed6\u5fc3\u4e2d\u7126\u6025\uff0c\u63d0\u4e00\u53e3\u6c14\uff0c\u811a\u4e0b\u52a0\u5feb\uff0c\u548c\u654c\u4eba\u53c8\u8fd1\u4e86\u6570\u5c3a\u3002\u5ffd\u542c\u5f97\u8840\u5200\u50e7\u201c\u554a\u201d\u7684\u4e00\u58f0\uff0c\u5411\u524d\u6251\u5012\uff0c\u53cc\u624b\u5728\u96ea\u5730\u4e2d\u4e71\u6293\u4e71\u722c\uff0c\u663e\u662f\u5185\u529b\u5df2\u7aed\uff0c\u6454\u5012\u4e86\u66f4\u722c\u4e0d\u8d77\u6765\u3002 \u3000\u3000\u77f3\u6d1e\u4e2d\u72c4\u4e91\u548c\u6c34\u7b19\u90fd\u770b\u5f97\u6e05\u695a\uff0c\u4e00\u4e2a\u60ca\u614c\uff0c\u4e00\u4e2a\u6b22\u559c\u3002\u72c4\u4e91\u659c\u773c\u77a5\u5904\uff0c\u89c1\u5230\u6c34\u7b19\u6ee1\u8138\u559c\u8272\uff0c\u5fc3\u4e2d\u607c\u6068\uff0c\u4e0d\u7531\u5f97\u624b\u81c2\u6536\u7d27\uff0c\u7528\u529b\u5728\u5979\u5589\u5934\u4e00\u627c\u3002 \u3000\u3000\u773c\u89c1\u8840\u5200\u50e7\u65e0\u6cd5\u722c\u8d77\uff0c\u6c34\u5cb1\u54ea\u80fd\u5931\u6b64\u826f\u673a\uff0c\u62a2\u4e0a\u51e0\u6b65\uff0c\u633a\u5251\u5411\u4ed6\u81c0\u90e8\u75be\u523a\u800c\u4e0b\uff0c\u8fd9\u65f6\u4e0d\u6b32\u4e00\u5251\u4fbf\u5c06\u4ed6\u523a\u6b7b\uff0c\u8981\u5c06\u4ed6\u4f24\u5f97\u9003\u8dd1\u4e0d\u4e86\uff0c\u518d\u62f7\u95ee\u6c34\u7b19\u7684\u6240\u5728\u3002\u957f\u5251\u53ea\u9012\u51fa\u4e24\u5c3a\uff0c\u84e6\u5730\u91cc\u5de6\u811a\u8e0f\u4e0b\uff0c\u8db3\u5e95\u865a\u7a7a\uff0c\u5168\u8eab\u6025\u5815\uff0c\u4e0b\u9762\u7adf\u662f\u4e00\u4e2a\u6df1\u6d1e\u3002 \u3000\u3000\u8fd9\u4e00\u4e0b\u5947\u53d8\u6a2a\u751f\uff0c\u7adf\u4f3c\u51fa\u73b0\u4e86\u5996\u6cd5\u90aa\u672f\uff0c\u82b1\u94c1\u5e72\u3001\u72c4\u4e91\u3001\u6c34\u7b19\u4e09\u4eba\u773c\u89c1\u6c34\u5cb1\u4fbf\u8981\u5f97\u624b\uff0c\u5374\u5728\u4e00\u77ac\u4e4b\u95f4\u9661\u7136\u6d88\u5931\uff0c\u4e0d\u77e5\u53bb\u5411\u3002\u8ddf\u7740\u4e00\u58f0\u957f\u957f\u7684\u60e8\u53eb\uff0c\u4ece\u5730\u5e95\u4f20\u5c06\u4e0a\u6765\uff0c\u6b63\u662f\u6c34\u5cb1\u7684\u58f0\u97f3\uff0c\u663e\u662f\u5728\u4e0b\u9762\u78b0\u5230\u4e86\u6781\u53ef\u6015\u4e4b\u4e8b\u3002 \u3000\u3000\u8840\u5200\u50e7\u4e00\u8dc3\u800c\u8d77\uff0c\u8eab\u624b\u77eb\u6377\u5f02\u5e38\uff0c\u663e\u800c\u6613\u89c1\uff0c\u4ed6\u9002\u624d\u51fa\u529b\u6323\u624e\u5168\u662f\u4f5c\u4f2a\u3002\u53ea\u89c1\u4ed6\u8dc3\u8d77\u8eab\u6765\uff0c\u53cc\u8db3\u4e00\u987f\uff0c\u6ca1\u5165\u96ea\u91cc\uff0c\u8ddf\u7740\u53c8\u94bb\u4e86\u4e0a\u6765\uff0c\u6293\u7740\u4e00\u4eba\uff0c\u629b\u5728\u96ea\u5730\u91cc\u3002\u90a3\u4eba\u9c9c\u8840\u6dcb\u6f13\uff0c\u6b63\u662f\u6c34\u5cb1\uff0c\u4f46\u89c1\u4ed6\u53cc\u8db3\u5df2\u7136\u9f50\u819d\u800c\u65ad\uff0c\u4e00\u65f6\u4e5f\u4e0d\u77e5\u662f\u6b7b\u662f\u6d3b\u3002 \u3000\u3000\u6c34\u7b19\u89c1\u5230\u7236\u4eb2\u7684\u60e8\u72b6\uff0c\u5927\u58f0\u54ed\u53eb\uff1a\u201c\u7239\u7239\uff0c\u7239\u7239\uff01\u201d\u72c4\u4e91\u5fc3\u4e2d\u4e0d\u5fcd\uff0c\u60ca\u9a87\u4e4b\u4f59\uff0c\u4e5f\u5fd8\u4e86\u518d\u4f38\u81c2\u627c\u5979\uff0c\u53cd\u800c\u653e\u5f00\u4e86\u624b\u81c2\uff0c\u5b89\u6170\u5979\u9053\uff1a\u201c\u6c34\u59d1\u5a18\uff0c\u4f60\u7239\u7239\u6ca1\u6b7b\uff0c\u4ed6\u2026\u2026\u4ed6\u8fd8\u5728\u52a8\u3002\u201d \u3000\u3000\u8840\u5200\u50e7\u5de6\u624b\u4e00\u6325\u4e00\u626c\uff0c\u4e00\u9053\u6697\u7ea2\u8272\u7684\u5149\u534e\u5728\u5934\u9876\u76d8\u65cb\u6210\u5708\uff0c\u8840\u5200\u7adf\u53c8\u5165\u624b\u3002\u539f\u6765\u9002\u624d\u4ed6\u6f5c\u4f0f\u96ea\u5730\uff0c\u826f\u4e45\u4e0d\u51fa\uff0c\u662f\u5728\u6697\u901a\u4e00\u4e2a\u96ea\u4e95\uff0c\u5e03\u7f6e\u4e86\u673a\u5173\uff0c\u5c06\u8840\u5200\u6a2a\u67b6\u4e95\u4e2d\uff0c\u5203\u53e3\u5411\u4e0a\uff0c\u7136\u540e\u94bb\u51fa\u96ea\u6765\uff0c\u5047\u88c5\u5931\u5200\uff0c\u4ee4\u654c\u4eba\u5fc3\u65e0\u6240\u5fcc\uff0c\u653e\u80c6\u8ffd\u8d76\uff0c\u7ec8\u4e8e\u8dcc\u5165\u9677\u9631\u3002\u6c34\u5cb1\u7eb5\u6a2a\u6b66\u6797\u6570\u5341\u5e74\uff0c\u9605\u5386\u4e0d\u53ef\u8c13\u4e0d\u5bcc\uff0c\u6c34\u9646\u4e24\u8def\u7684\u6c5f\u6e56\u4f0e\u4fe9\u65e0\u4e0d\u901a\u6653\uff0c\u53ea\u662f\u8fd9\u51b0\u96ea\u4e2d\u7684\u52fe\u5f53\u5374\u4ee4\u4ed6\u9632\u4e0d\u80dc\u9632\u3002\u4ed6\u4ece\u96ea\u4e95\u4e2d\u6025\u5815\u800c\u4e0b\uff0c\u90a3\u8840\u5200\u524a\u94c1\u5982\u6ce5\uff0c\u767b\u65f6\u5c06\u4ed6\u53cc\u817f\u8f7b\u8f7b\u5272\u65ad\u3002 \u3000\u3000\u8840\u5200\u50e7\u9ad8\u4e3e\u8840\u5200\uff0c\u5bf9\u7740\u82b1\u94c1\u5e72\u5927\u53eb\uff1a\u201c\u6709\u79cd\u6ca1\u6709\uff1f\u8fc7\u6765\u6597\u4e0a\u4e09\u767e\u56de\u5408\u3002\u201d \u3000\u3000\u82b1\u94c1\u5e72\u89c1\u5230\u6c34\u5cb1\u5728\u96ea\u5730\u91cc\u75db\u5f97\u6eda\u6765\u6eda\u53bb\u7684\u60e8\u72b6\uff0c\u53ea\u5413\u5f97\u5fc3\u80c6\u4ff1\u88c2\uff0c\u54ea\u6562\u4e0a\u524d\u76f8\u6597\uff0c\u633a\u7740\u77ed\u67aa\u62a4\u5728\u8eab\u524d\uff0c\u4e00\u6b65\u6b65\u7684\u5012\u9000\uff0c\u67aa\u4e0a\u7ea2\u7f28\u4e0d\u4f4f\u6296\u52a8\uff0c\u663e\u5f97\u5185\u5fc3\u5bb3\u6015\u5df2\u6781\u3002\u8840\u5200\u50e7\u4e00\u58f0\u731b\u559d\uff0c\u51b2\u4e0a\u4e24\u6b65\u3002\u82b1\u94c1\u5e72\u6025\u9000\u4e24\u6b65\uff0c\u624b\u81c2\u53d1\u6296\uff0c\u7adf\u5c06\u77ed\u67aa\u6389\u5728\u5730\u4e0b\uff0c\u6025\u901f\u62fe\u8d77\uff0c\u53c8\u9000\u4e86\u4e24\u6b65\u3002 \u3000\u3000\u8840\u5200\u50e7\u8fde\u6597\u4e09\u4f4d\u9ad8\u624b\uff0c\u4e09\u6b21\u6b7b\u91cc\u9003\u751f\uff0c\u5b9e\u5df2\u7d2f\u5f97\u7b4b\u75b2\u529b\u5c3d\uff0c\u5018\u82e5\u548c\u82b1\u94c1\u5e72\u518d\u6597\uff0c\u53ea\u6015\u4e00\u62db\u4e5f\u652f\u6301\u4e0d\u4f4f\u3002\u82b1\u94c1\u5e72\u7684\u6b66\u529f\u672c\u5c31\u4e0d\u4e9a\u4e8e\u8840\u5200\u50e7\uff0c\u6b64\u523b\u4e0a\u524d\u62da\u6597\uff0c\u8840\u5200\u50e7\u975e\u6b7b\u5728\u4ed6\u67aa\u4e0b\u4e0d\u53ef\uff0c\u53ea\u662f\u4ed6\u5931\u624b\u523a\u6b7b\u5218\u4e58\u98ce\u540e\uff0c\u5fc3\u795e\u6cae\u4e27\uff0c\u9510\u6c14\u5927\u632b\uff0c\u518d\u89c1\u5230\u9646\u5929\u6292\u65ad\u5934\u3001\u6c34\u5cb1\u6298\u817f\uff0c\u5413\u5f97\u80c6\u4e5f\u7834\u4e86\uff0c\u5df2\u65e0\u4e1d\u6beb\u6597\u5fd7\u3002 \u3000\u3000\u8840\u5200\u50e7\u89c1\u4ed6\u5982\u6b64\u5bb3\u6015\u7684\u6a21\u6837\uff0c\u5f97\u610f\u975e\u51e1\uff0c\u53eb\u9053\uff1a\u201c\u563f\u563f\uff0c\u6211\u6709\u5999\u8ba1\u4e03\u5341\u4e8c\u6761\uff0c\u4eca\u65e5\u53ea\u7528\u4e09\u6761\uff0c\u5df2\u6740\u4e86\u4f60\u6c5f\u5357\u4e09\u4e2a\u8001\u5bb6\u4f19\uff0c\u8fd8\u6709\u516d\u5341\u4e5d\u6761\uff0c\u4e00\u6761\u6761\u90fd\u8981\u7528\u5728\u4f60\u8eab\u4e0a\u3002\u201d \u3000\u3000\u82b1\u94c1\u5e72\u591a\u5386\u6c5f\u6e56\u98ce\u6ce2\uff0c\u8840\u5200\u50e7\u8fd9\u4e9b\u708e\u708e\u5927\u8a00\uff0c\u539f\u672c\u9a97\u4ed6\u4e0d\u5012\uff0c\u4f46\u8fd9\u65f6\u6210\u4e86\u60ca\u5f13\u4e4b\u9e1f\uff0c\u53ea\u89c9\u654c\u4eba\u7684\u4e00\u8a00\u4e00\u52a8\u4e4b\u4e2d\uff0c\u65e0\u4e0d\u5145\u6ee1\u4e86\u6781\u51f6\u72e0\u6781\u53ef\u6016\u4e4b\u610f\uff0c\u542c\u4ed6\u8bf4\u8fd8\u6709\u516d\u5341\u4e5d\u6761\u6bd2\u8ba1\uff0c\u4e00\u4e00\u8981\u7528\u5728\u81ea\u5df1\u8eab\u4e0a\uff0c\u5583\u5583\u7684\u9053\uff1a\u201c\u516d\u5341\u4e5d\u6761\uff0c\u516d\u5341\u4e5d\u6761\uff01\u201d\u53cc\u624b\u66f4\u6296\u5f97\u5389\u5bb3\u4e86\u3002 \u3000\u3000\u8840\u5200\u8001\u7956\u6b64\u65f6\u5fc3\u529b\u4ea4\u75b2\uff0c\u652f\u6301\u8270\u96be\uff0c\u53ea\u76fc\u7acb\u65f6\u5c31\u5730\u8eba\u5012\uff0c\u7761\u4ed6\u4e00\u65e5\u4e00\u591c\u3002\u4f46\u4ed6\u5fc3\u77e5\u6b64\u523b\u6240\u9762\u5bf9\u7684\u5b9e\u662f\u4e00\u573a\u751f\u6b7b\u6076\u6597\uff0c\u5176\u6fc0\u70c8\u731b\u6076\uff0c\u6b8a\u4e0d\u4e0b\u4e8e\u9002\u624d\u548c\u5218\u4e58\u98ce\u3001\u9646\u5929\u6292\u7b49\u7684\u6fc0\u6218\u3002\u53ea\u8981\u81ea\u5df1\u7a0d\u9732\u75b2\u6001\uff0c\u7ed9\u5bf9\u65b9\u77a7\u51fa\u7834\u7efd\uff0c\u4ed6\u51fa\u624b\u4e00\u653b\uff0c\u7acb\u65f6\u4fbf\u4f38\u91cf\u51fa\u81ea\u5df1\u5185\u529b\u5df2\u5c3d\uff0c\u90a3\u65f6\u4ed6\u77ed\u67aa\u6233\u6765\uff0c\u81ea\u5df1\u53ea\u6709\u675f\u624b\u5c31\u622e\uff0c\u662f\u4ee5\u5f3a\u6253\u7cbe\u795e\uff0c\u5c06\u624b\u4e2d\u8840\u5200\u76d8\u65cb\u73a9\u5f04\uff0c\u663e\u5f97\u884c\u6709\u4f59\u529b\u3002\u4ed6\u89c1\u82b1\u94c1\u5e72\u60f3\u9003\u4e0d\u9003\u7684\uff0c\u5fc3\u4e2d\u4e0d\u4f4f\u50ac\u4fc3\uff1a\u201c\u80c6\u5c0f\u9b3c\uff0c\u5feb\u9003\u554a\uff0c\u5feb\u9003\u554a\uff01\u201d \u3000\u3000\u5c82\u77e5\u82b1\u94c1\u5e72\u8fd9\u65f6\u8fde\u9003\u8dd1\u4e5f\u5df2\u6ca1\u4e86\u52c7\u6c14\u3002 \u3000\u3000\u6c34\u5cb1\u53cc\u817f\u9f50\u819d\u65a9\u65ad\uff0c\u8eba\u5728\u96ea\u5730\u4e2d\u5944\u5944\u4e00\u606f\uff0c\u773c\u89c1\u82b1\u94c1\u5e72\u5413\u6210\u8fd9\u4e2a\u6a21\u6837\uff0c\u66f4\u662f\u60b2\u6124\u3002\u4ed6\u867d\u7136\u91cd\u4f24\uff0c\u5374\u5df2\u77a7\u51fa\u8840\u5200\u50e7\u5185\u529b\u5782\u5c3d\uff0c\u5df2\u662f\u5f3a\u5f29\u4e4b\u672b\uff0c\u9f13\u8db3\u529b\u6c14\u53eb\u9053\uff1a\u201c\u82b1\u4e8c\u54e5\uff0c\u8ddf\u4ed6\u62da\u554a\u3002 \u3000\u3000\u6076\u50e7\u771f\u6c14\u8017\u7aed\uff0c\u4f60\u6740\u4ed6\u6613\u5982\u53cd\u62f3\uff0c\u6613\u2026\u2026\u201d \u3000\u3000\u8840\u5200\u50e7\u5fc3\u4e2d\u4e00\u60ca\uff1a\u201c\u8fd9\u8001\u513f\u77a7\u51fa\u6211\u7684\u7834\u7efd\uff0c\u5927\u662f\u4e0d\u5999\u3002\u201d\u4ed6\u5f3a\u6253\u7cbe\u795e\uff0c\u8e0f\u4e0a\u4e24\u6b65\uff0c\u5411\u82b1\u94c1\u5e72\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4e0d\u9519\uff0c\u6211\u5185\u529b\u5df2\u5c3d\uff0c\u54b1\u4eec\u5230\u90a3\u8fb9\u5d16\u4e0a\u53bb\u5927\u6218\u4e09\u767e\u56de\u5408\uff01\u4e0d\u53bb\u7684\u662f\u4e4c\u9f9f\u738b\u516b\u86cb\uff01\u201d \u3000\u3000\u5ffd\u542c\u5f97\u8eab\u540e\u5c71\u6d1e\u4e2d\u4f20\u51fa\u6c34\u7b19\u7684\u54ed\u53eb\uff1a\u201c\u7239\u7239\uff0c\u7239\u7239\uff01\u201d\u8840\u5200\u50e7\u7075\u673a\u4e00\u52a8\uff1a\u201c\u6b64\u523b\u82e5\u662f\u6740\u4e86\u6c34\u5cb1\uff0c\u5f92\u7136\u793a\u5f31\u3002\u6211\u6293\u4e86\u8fd9\u5973\u5a03\u513f\u51fa\u6765\uff0c\u903c\u8feb\u6c34\u5cb1\u6295\u964d\u3002\u8fd9\u59d3\u82b1\u7684\u4fbf\u66f4\u52a0\u6ca1\u6709\u6597\u5fd7\u4e86\u3002\u201d\u4ed6\u5411\u7740\u82b1\u94c1\u5e72\u72de\u7b11\u9053\uff1a\u201c\u53bb\u4e0d\u53bb\uff1f\u6253\u4e94\u767e\u4e2a\u56de\u5408\u4e5f\u884c\uff1f\u201d \u3000\u3000\u82b1\u94c1\u5e72\u6447\u6447\u5934\uff0c\u53c8\u9000\u4e86\u4e00\u6b65\u3002 \u3000\u3000\u6c34\u5cb1\u53eb\u9053\uff1a\u201c\u8ddf\u4ed6\u6253\u554a\uff0c\u8ddf\u4ed6\u6253\u554a\uff01\u4f60\u4e0d\u8ddf\u9646\u5927\u54e5\u3001\u5218\u4e09\u54e5\u62a5\u4ec7\u4e48\uff1f\u201d \u3000\u3000\u8840\u5200\u50e7\u54c8\u54c8\u5927\u7b11\uff0c\u53eb\u9053\uff1a\u201c\u6253\u554a\uff0c\u6253\u554a\uff01\u6211\u8fd8\u6709\u516d\u5341\u4e5d\u6761\u60e8\u4e0d\u53ef\u8a00\u7684\u6bd2\u8ba1\uff0c\u4e00\u4e00\u8981\u4f7f\u5728\u4f60\u7684\u8eab\u4e0a\u3002\u201d\u4e00\u8fb9\u8bf4\uff0c\u4e00\u8fb9\u8f6c\u8eab\u8d70\u8fdb\u5c71\u6d1e\uff0c\u6293\u4f4f\u6c34\u7b19\u5934\u53d1\uff0c\u5c06\u5979\u6a2a\u6258\u5012\u66f3\u7684\u62c9\u4e86\u51fa\u6765\uff0c\u62c9\u626f\u4e4b\u65f6\uff0c\u5df2\u662f\u4e0d\u65ad\u5598\u6c14\uff0c\u8bf4\u751a\u4e48\u4e5f\u63a9\u9970\u4e0d\u4f4f\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u82b1\u94c1\u5e72\u6b66\u529f\u5389\u5bb3\uff0c\u552f\u6709\u4ee5\u5404\u79cd\u5404\u6837\u6b8b\u9177\u624b\u6bb5\u65bd\u4e8e\u6c34\u6c0f\u7236\u5973\u8eab\u4e0a\uff0c\u65b9\u80fd\u5413\u5f97\u4ed6\u4e0d\u6562\u51fa\u624b\uff0c\u5f53\u4e0b\u5c06\u6c34\u7b19\u62d6\u5230\u6c34\u5cb1\u9762\u524d\uff0c\u559d\u9053\uff1a\u201c\u4f60\u8bf4\u6211\u771f\u6c14\u5df1\u5c3d\uff0c\u597d\uff0c\u6211\u8bd5\u7ed9\u4f60\u77a7\u77a7\uff0c\u771f\u6c14\u5c3d\u662f\u4e0d\u5c3d\uff1f\u201d\u8bf4\u7740\u7528\u529b\u4e00\u626f\uff0c\u55e4\u7684\u4e00\u58f0\u54cd\uff0c\u5c06\u6c34\u7b19\u7684\u53f3\u8fb9\u8896\u5b50\u6495\u4e0b\u4e86\u4e00\u5927\u622a\uff0c\u9732\u51fa\u96ea\u767d\u7684\u808c\u80a4\u3002\u6c34\u7b19\u4e00\u58f0\u60ca\u53eb\uff0c\u53ea\u662f\u7a74\u9053\u88ab\u70b9\uff0c\u534a\u5206\u6297\u5fa1\u4e0d\u5f97\u3002 \u3000\u3000\u72c4\u4e91\u8ddf\u7740\u4ece\u5c71\u6d1e\u4e2d\u722c\u4e86\u51fa\u6765\uff0c\u773c\u770b\u7740\u8fd9\u60e8\u5267\uff0c\u751a\u662f\u4e0d\u5fcd\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u522b\u6b3a\u4fae\u6c34\u59d1\u5a18\uff01\u201d\u8840\u5200\u8001\u7956\u7b11\u9053\uff1a\u201c\u54c8\u54c8\uff0c\u4e56\u5f92\u5b59\uff0c\u4e0d\u7528\u62c5\u5fc3\uff0c\u5e08\u7956\u7237\u7237\u4e0d\u4f1a\u4f24\u4e86\u5979\u6027\u547d\u3002\u201d\u4ed6\u56de\u8fc7\u8eab\u6765\uff0c\u624b\u8d77\u4e00\u5200\uff0c\u5c06\u6c34\u5cb1\u7684\u5de6\u80a9\u524a\u53bb\u4e00\u7247\uff0c\u95ee\u9053\uff1a\u201c\u6211\u7684\u771f\u6c14\u8017\u7aed\u4e86\u6ca1\u6709\uff1f\u201d\u6c34\u5cb1\u80a9\u4e0a\u767b\u65f6\u9c9c\u8840\u55b7\u51fa\u3002\u82b1\u94c1\u5e72\u548c\u6c34\u7b19\u540c\u65f6\u60ca\u547c\u3002 \u3000\u3000\u8840\u5200\u50e7\u5de6\u624b\u4e00\u626f\uff0c\u53c8\u5c06\u6c34\u7b19\u7684\u8863\u670d\u6495\u53bb\u4e00\u7247\uff0c\u5411\u6c34\u5cb1\u9053\uff1a\u201c\u4f60\u53eb\u6211\u4e09\u58f0\u2018\u597d\u7237\u7237\u2019\uff0c\u53eb\u662f\u4e0d\u53eb\uff1f\u201d\u6c34\u5cb1\u5478\u7684\u4e00\u58f0\uff0c\u4e00\u53e3\u553e\u6db2\u7528\u529b\u5411\u4ed6\u5410\u53bb\u3002\u8840\u5200\u50e7\u4fa7\u8eab\u95ea\u907f\uff0c\u8fd9\u4e00\u4e0b\u7ad9\u7acb\u4e0d\u7a33\uff0c\u811a\u4e0b\u4e00\u4e2a\u8e09\u8dc4\uff0c\u4e0d\u89c9\u5934\u8111\u7729\u6655\uff0c\u51e0\u4e4e\u4fbf\u8981\u5012\u5c06\u4e0b\u6765\u3002 \u3000\u3000\u6c34\u5cb1\u77a7\u5f97\u6e05\u695a\uff0c\u53eb\u9053\uff1a\u201c\u82b1\u4e8c\u54e5\uff0c\u5feb\u52a8\u624b\u554a\uff0c\u5feb\u52a8\u624b\uff01\u201d \u3000\u3000\u82b1\u94c1\u5e72\u4e5f\u89c1\u5230\u8840\u5200\u50e7\u811a\u6b65\u4e0d\u7a33\uff0c\u5fc3\u4e2d\u5374\u60f3\uff1a\u201c\u53ea\u6015\u4ed6\u662f\u6545\u610f\u793a\u5f31\uff0c\u5f15\u6211\u4e0a\u5f53\u3002\u8fd9\u6076\u50e7\u8be1\u8ba1\u591a\u7aef\uff0c\u4e0d\u53ef\u4e0d\u9632\u3002\u201d \u3000\u3000\u8840\u5200\u50e7\u53c8\u6a2a\u5200\u524a\u53bb\uff0c\u5728\u6c34\u5cb1\u53f3\u81c2\u4e0a\u780d\u4e86\u4e00\u6761\u6df1\u75d5\uff0c\u559d\u9053\uff1a\u201c\u4f60\u53eb\u4e0d\u53eb\u6211\u2018\u597d\u7237\u7237\u2019\uff1f\u201d\u6c34\u5cb1\u75db\u5f97\u51e0\u6b32\u6655\u53bb\uff0c\u5927\u58f0\u9053\uff1a\u201c\u59d3\u6c34\u7684\u5b81\u6b7b\u4e0d\u5c48\uff01\u5feb\u5c06\u6211\u6740\u4e86\u3002\u201d\u8840\u5200\u50e7\u9053\uff1a\u201c\u6211\u624d\u4e0d\u8ba9\u4f60\u75db\u75db\u5feb\u5feb\u7684\u6b7b\u5462\uff0c\u6211\u8981\u5c06\u4f60\u7684\u624b\u81c2\u4e00\u5bf8\u5bf8\u7684\u5272\u4e0b\u6765\uff0c\u5c06\u4f60\u7684\u8089\u4e00\u7247\u7247\u524a\u4e0b\u6765\u3002\u4f60\u53eb\u6211\u4e09\u58f0\u2018\u597d\u7237\u7237\u2019\uff0c\u5411\u6211\u8ba8\u9976\uff0c\u6211\u4fbf\u4e0d\u6740\u4f60\uff01\u201d \u3000\u3000\u6c34\u5cb1\u9a82\u9053\uff1a\u201c\u505a\u4f60\u5a18\u7684\u6e05\u79cb\u5927\u68a6\uff01\u201d\u8840\u5200\u50e7\u773c\u89c1\u4ed6\u6781\u662f\u5014\u5f3a\uff0c\u6599\u60f3\u7eb5\u7136\u5c06\u4ed6\u788e\u5272\u51cc\u8fdf\uff0c\u4e5f\u4e0d\u4f1a\u5c48\u670d\uff0c\u4fbf\u9053\uff1a\u201c\u597d\uff0c\u6211\u6765\u70ae\u5236\u4f60\u7684\u5973\u513f\uff0c\u770b\u4f60\u53eb\u4e0d\u53eb\u6211\u2018\u597d\u7237\u7237\u2019\uff1f\u201d\u8bf4\u7740\u53cd\u624b\u4e00\u626f\uff0c\u6495\u4e0b\u4e86\u6c34\u7b19\u7684\u534a\u5e45\u88d9\u5b50\u3002 \u3000\u3000\u6c34\u5cb1\u6012\u6781\uff0c\u773c\u524d\u4e00\u9ed1\uff0c\u4fbf\u6b32\u6655\u53bb\uff0c\u4f46\u60f3\uff1a\u201c\u82b1\u4e8c\u54e5\u5413\u5f97\u6ca1\u4e86\u6597\u5fd7\uff0c\u6211\u53ef\u4e0d\u80fd\u4fbf\u6b7b\u3002\u4e0d\u7ba1\u8fd9\u6076\u50e7\u5982\u4f55\u5f53\u7740\u6211\u9762\u524d\u4fae\u8fb1\u7b19\u513f\uff0c\u6211\u90fd\u8981\u5fcd\u4f4f\u6c14\uff0c\u8ddf\u4ed6\u5468\u65cb\u5230\u5e95\u3002\u201d \u3000\u3000\u8840\u5200\u50e7\u72de\u7b11\u9053\uff1a\u201c\u8fd9\u59d3\u82b1\u7684\u9a6c\u4e0a\u5c31\u4f1a\u5411\u6211\u8dea\u4e0b\u6c42\u9976\uff0c\u6211\u4fbf\u9976\u4e86\u4ed6\u6027\u547d\uff0c\u8ba9\u4ed6\u5230\u6c5f\u6e56\u4e0a\u53bb\u5ba3\u626c\uff0c\u6c34\u59d1\u5a18\u7ed9\u6211\u5982\u4f55\u5265\u5149\u4e86\u8863\u886b\u3002\u54c8\u54c8\uff0c\u5999\u6781\uff0c\u5f88\u597d\uff01\u82b1\u94c1\u5e72\uff0c\u4f60\u8981\u6295\u964d\uff1f\u53ef\u4ee5\uff0c\u53ef\u4ee5\uff0c\u6211\u53ef\u4ee5\u9976\u4f60\u6027\u547d\uff01\u8840\u5200\u8001\u7956\u751f\u5e73\u4ece\u4e0d\u6740\u5bb3\u964d\u4eba\u3002\u201d \u3000\u3000\u82b1\u94c1\u5e72\u542c\u4e86\u8fd9\u51e0\u53e5\u8bdd\uff0c\u6597\u5fd7\u66f4\u52a0\u6de1\u4e86\uff0c\u4ed6\u4e00\u5fc3\u4e00\u610f\u53ea\u60f3\u8131\u56f0\u9003\u751f\uff0c\u8dea\u4e0b\u6c42\u9976\u867d\u662f\u7f9e\u803b\uff0c\u4f46\u603b\u6bd4\u7ed9\u4eba\u5728\u8eab\u4e0a\u4e00\u5200\u4e00\u5200\u7684\u5bb0\u5272\u8981\u597d\u5f97\u591a\u3002\u4ed6\u5168\u6ca1\u60f3\u5230\uff0c\u82e5\u662f\u594b\u529b\u6c42\u6218\uff0c\u7acb\u65f6\u4fbf\u53ef\u5c06\u654c\u4eba\u6740\u4e86\uff0c\u5374\u53ea\u89c9\u5f97\u773c\u524d\u8fd9\u8840\u5200\u50e7\u53ef\u6016\u53ef\u754f\u4e4b\u6781\u3002\u53ea\u542c\u5f97\u8840\u5200\u50e7\u9053\uff1a\u201c\u4f60\u653e\u5fc3\uff0c\u4e0d\u7528\u5bb3\u6015\uff0c\u5f85\u4f1a\u4f60\u8ba4\u8f93\u6295\u964d\uff0c\u6211\u4fbf\u9976\u4e86\u4f60\u6027\u547d\u3002\u51b3\u8ba1\u4e0d\u4f1a\u5272\u4f60\u4e00\u5200\uff0c\u5c3d\u7ba1\u653e\u5fc3\u597d\u4e86\u3002\u201d\u8fd9\u51e0\u53e5\u5b89\u6170\u7684\u8a00\u8bed\uff0c\u82b1\u94c1\u5e72\u542c\u5728\u8033\u91cc\uff0c\u8bf4\u4e0d\u51fa\u7684\u8212\u670d\u53d7\u7528\u3002 \u3000\u3000\u8840\u5200\u50e7\u89c1\u4ed6\u8138\u9732\u559c\u8272\uff0c\u5fc3\u60f3\u673a\u4e0d\u53ef\u5931\uff0c\u5f53\u5373\u653e\u4e0b\u6c34\u7b19\uff0c\u6301\u5200\u8d70\u5230\u4ed6\u8eab\u524d\uff0c\u8bf4\u9053\uff1a\u201c\u5927\u4e08\u592b\u80fd\u5c48\u80fd\u4f38\uff0c\u5f88\u597d\uff0c\u4f60\u8981\u5411\u6211\u6295\u964d\uff0c\u5148\u629b\u4e0b\u77ed\u67aa\uff0c\u5f88\u597d\uff0c\u5f88\u597d\uff0c\u6211\u51b3\u4e0d\u4f24\u4f60\u6027\u547d\u3002\u6211\u5f53\u4f60\u662f\u597d\u670b\u53cb\uff0c\u597d\u5144\u5f1f\uff01\u629b\u4e0b\u77ed\u67aa\uff0c\u629b\u4e0b\u77ed\u67aa\uff01\u201d\u58f0\u97f3\u751a\u662f\u67d4\u548c\u3002 \u3000\u3000\u4ed6\u8fd9\u51e0\u53e5\u8bf4\u8bdd\u4f3c\u6709\u4e0d\u53ef\u6297\u62d2\u7684\u529b\u9053\uff0c\u82b1\u94c1\u5e72\u624b\u4e00\u677e\uff0c\u77ed\u67aa\u629b\u5728\u96ea\u5730\u4e4b\u4e2d\u3002\u4ed6\u5175\u5203\u4e00\u5931\uff0c\u90a3\u662f\u5168\u5fc3\u5168\u610f\u7684\u964d\u670d\u4e86\u3002 \u3000\u3000\u8840\u5200\u50e7\u9732\u51fa\u7b11\u5bb9\uff0c\u9053\uff1a\u201c\u5f88\u597d\uff0c\u5f88\u597d\uff01\u4f60\u662f\u597d\u4eba\uff0c\u4f60\u8fd9\u67c4\u77ed\u67aa\u4e0d\u5dee\uff0c\u7ed9\u6211\u77a7\u77a7\uff01\u4f60\u9000\u540e\u4e09\u6b65\uff0c\u597d\uff0c\u4f60\u5f88\u542c\u8bdd\uff0c\u6211\u5fc5\u5b9a\u9976\u4f60\u4e0d\u6740\uff0c\u4f60\u653e\u4e00\u767e\u4e8c\u5341\u4e2a\u5fc3\u3002\u518d\u9000\u5f00\u4e09\u6b65\u3002\u201d\u82b1\u94c1\u5e72\u4f9d\u8a00\u9000\u5f00\u3002\u8840\u5200\u50e7\u7f13\u7f13\u4fef\u8eab\uff0c\u5c06\u77ed\u67aa\u62ff\u5728\u624b\u4e2d\uff0c\u624b\u6307\u78b0\u5230\u67aa\u5e72\u4e4b\u65f6\uff0c\u81ea\u89c9\u5168\u8eab\u529b\u6c14\u6b63\u5728\u4e00\u70b9\u4e00\u6ef4\u7684\u5931\u5374\uff0c\u63a5\u8fde\u63d0\u4e86\u4e24\u6b21\u771f\u6c14\uff0c\u90fd\u662f\u63d0\u4e0d\u4e0a\u6765\uff0c\u6697\u6697\u5fc3\u60ca\uff1a\u201c\u9002\u624d\u95f4\u8fde\u6597\u4e09\u4e2a\u9ad8\u624b\uff0c\u635f\u8017\u5f97\u5f53\u771f\u5389\u5bb3\uff0c\u53ea\u6015\u8981\u8d39\u4e0a\u5341\u5929\u534a\u6708\uff0c\u65b9\u5f97\u6062\u590d\u5143\u6c14\u3002\u201d\u867d\u5c06\u7eaf\u94a2\u77ed\u67aa\u62ff\u5230\u4e86\u624b\u4e2d\uff0c\u4ecd\u662f\u63d0\u5fc3\u540a\u80c6\uff0c\u5018\u82e5\u82b1\u94c1\u5e72\u7a81\u7136\u5927\u8d77\u80c6\u5b50\u51fa\u624b\u653b\u51fb\uff0c\u5c31\u7b97\u4ed6\u53ea\u662f\u7a7a\u624b\uff0c\u81ea\u5df1\u4e5f\u662f\u4e00\u78b0\u5373\u57ae\u3002 \u3000\u3000\u6c34\u5cb1\u89c1\u82b1\u94c1\u5e72\u629b\u67aa\u964d\u670d\uff0c\u5df2\u65e0\u6307\u671b\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u7b19\u513f\uff0c\u5feb\u5c06\u6211\u6740\u4e86\uff01\u201d\u6c34\u7b19\u54ed\u9053\uff1a\u201c\u7239\u7239\uff0c\u6211\u2026\u2026\u6211\u52a8\u4e0d\u4e86\uff01\u201d\u6c34\u5cb1\u5411\u72c4\u4e91\u9053\uff1a\u201c\u5c0f\u5e08\u7236\uff0c\u4f60\u505a\u505a\u597d\u4e8b\uff0c\u5feb\u5c06\u6211\u6740\u4e86\u3002\u201d \u3000\u3000\u72c4\u4e91\u660e\u767d\u4ed6\u7684\u5fc3\u610f\uff0c\u53cd\u6b63\u662f\u6d3b\u4e0d\u4e86\uff0c\u4e0e\u5176\u518d\u5403\u96f6\u788e\u82e6\u5934\uff0c\u53d7\u8fd9\u822c\u91cd\u5927\u4fae\u8fb1\uff0c\u4e0d\u5982\u6b7b\u5f97\u8d8a\u65e9\u8d8a\u597d\u3002\u4ed6\u5fc3\u4e2d\u4e0d\u5fcd\uff0c\u5f88\u60f3\u52a9\u4ed6\u53ca\u65e9\u4e86\u65ad\uff0c\u53ea\u662f\u81ea\u5df1\u4e00\u51fa\u624b\uff0c\u975e\u6fc0\u6012\u8840\u5200\u50e7\u4e0d\u53ef\uff0c\u773c\u89c1\u6b64\u4eba\u8fd9\u822c\u51f6\u6076\u6bd2\u8fa3\uff0c\u90a3\u53ef\u4e5f\u65e0\u8bba\u5982\u4f55\u5f97\u7f6a\u4e0d\u5f97\u3002 \u3000\u3000\u6c34\u5cb1\u53c8\u9053\uff1a\u201c\u7b19\u513f\uff0c\u4f60\u6c42\u6c42\u8fd9\u4f4d\u5c0f\u5e08\u7236\uff0c\u5feb\u4e9b\u5c06\u6211\u6740\u4e86\uff0c\u518d\u8fdf\u53ef\u5c31\u6765\u4e0d\u53ca\u5566\u3002\u201d\u6c34\u7b19\u5fc3\u614c\u610f\u4e71\uff0c\u9053\uff1a\u201c\u7239\u7239\uff0c\u4f60\u4e0d\u80fd\u6b7b\uff0c\u4f60\u4e0d\u80fd\u6b7b\u3002\u201d\u6c34\u5cb1\u6012\u9053\uff1a\u201c\u6211\u6b64\u523b\u751f\u4e0d\u5982\u6b7b\uff0c\u96be\u9053\u4f60\u6ca1\u89c1\u5230\u4e48\uff1f\u201d \u3000\u3000\u6c34\u7b19\u5403\u4e86\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u662f\uff0c\u662f\uff01\u7239\uff0c\u6211\u8ddf\u4f60\u4e00\u8d77\u6b7b\u4e86\uff01\u201d \u3000\u3000\u6c34\u5cb1\u53c8\u5411\u72c4\u4e91\u6c42\u9053\uff1a\u201c\u5c0f\u5e08\u7236\uff0c\u4f60\u5927\u6148\u5927\u60b2\uff0c\u5feb\u4e9b\u5c06\u6211\u6740\u4e86\u3002\u8981\u6211\u5411\u8fd9\u6076\u50e7\u6c42\u9976\uff0c\u6211\u6c34\u5cb1\u600e\u80fd\u51fa\u53e3\uff1f\u6211\u53c8\u600e\u80fd\u89c1\u6211\u5973\u513f\u53d7\u4ed6\u4e4b\u8fb1\uff1f\u201d \u3000\u3000\u72c4\u4e91\u773c\u89c1\u5230\u6c34\u5cb1\u7684\u82f1\u96c4\u6c14\u6982\uff0c\u751a\u662f\u94a6\u4f69\uff0c\u8fd9\u65f6\u4e49\u6124\u4e4b\u5fc3\u5927\u76db\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u597d\uff0c\u6211\u4fbf\u6740\u4e86\u4f60\u3002\u8001\u548c\u5c1a\u8981\u8d23\u602a\uff0c\u4e5f\u4e0d\u7ba1\u4e86\uff01\u201d \u3000\u3000\u6c34\u5cb1\u5fc3\u4e2d\u4e00\u559c\uff0c\u4ed6\u867d\u53d7\u91cd\u4f24\uff0c\u5fc3\u667a\u4e0d\u4e71\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u6211\u5927\u58f0\u9a82\u4f60\uff0c\u4f60\u4e00\u68cd\u5c06\u6211\u6253\u6b7b\uff0c\u90a3\u8001\u548c\u5c1a\u5c31\u4e0d\u4f1a\u602a\u4f60\u3002\u201d\u4e0d\u7b49\u72c4\u4e91\u56de\u7b54\uff0c\u4fbf\u5927\u58f0\u9a82\u9053\uff1a\u201c\u5c0f\u6deb\u50e7\uff0c\u4f60\u82e5\u4e0d\u56de\u5934\uff0c\u4ecd\u662f\u5b66\u8fd9\u8001\u6076\u50e7\u7684\u6837\uff0c\u5c06\u6765\u5b9a\u7136\u4e0d\u5f97\u597d\u6b7b\u3002\u4f60\u5018\u82e5\u5929\u826f\u672a\u6cef\uff0c\u5feb\u5feb\u8131\u79bb\u8840\u5200\u95e8\u624d\u662f\uff01\u5c0f\u6076\u50e7\uff0c\u4f60\u8fd9\u738b\u516b\u86cb\uff0c\u4e4c\u9f9f\u513f\u5b50\uff01\u4f60\u5feb\u5feb\u75db\u6539\u524d\u975e\uff0c\u4eca\u540e\u505a\u4e2a\u597d\u4eba\uff01\u201d \u3000\u3000\u72c4\u4e91\u542c\u51fa\u4ed6\u9a82\u58f0\u4e2d\u542b\u6709\u529d\u8beb\u4e4b\u610f\uff0c\u5fc3\u4e0b\u6697\u6697\u611f\u6fc0\uff0c\u63d0\u8d77\u4e00\u6839\u7c97\u5927\u7684\u6811\u679d\u821e\u4e86\u51e0\u4e0b\uff0c\u5374\u6253\u4e0d\u4e0b\u53bb\u3002 \u3000\u3000\u6c34\u5cb1\u5fc3\u4e2d\u7126\u6025\uff0c\u9a82\u5f97\u66f4\u52a0\u51f6\u4e86\uff0c\u659c\u773c\u53ea\u89c1\u90a3\u8fb9\u53a2\u82b1\u94c1\u5e72\u53cc\u819d\u4e00\u8f6f\uff0c\u8dea\u5012\u5728\u96ea\u5730\u4e4b\u4e2d\uff0c\u5411\u8840\u5200\u50e7\u78d5\u4e0b\u5934\u53bb\u3002 \u3000\u3000\u8840\u5200\u50e7\u79ef\u805a\u8eab\u4e0a\u4ec5\u6709\u7684\u5c11\u4e9b\u5185\u529f\uff0c\u51dd\u4e8e\u53f3\u624b\u98df\u6307\uff0c\u5bf9\u51c6\u82b1\u94c1\u5e72\u80cc\u5fc3\u7684\u201c\u7075\u53f0\u7a74\u201d\u70b9\u843d\uff0c\u8fd9\u4e00\u6307\u5b9e\u662f\u7aed\u5c3d\u4e86\u5168\u529b\uff0c\u4e00\u6307\u70b9\u7f62\uff0c\u518d\u4e5f\u6ca1\u4e86\u529b\u6c14\u3002\u82b1\u94c1\u5e72\u88ab\u70b9\u6454\u5012\uff0c\u8840\u5200\u50e7\u4e5f\u53cc\u819d\u6162\u6162\u5f2f\u66f2\u3002 \u3000\u3000\u6c34\u5cb1\u773c\u89c1\u82b1\u94c1\u5e72\u6454\u5012\uff0c\u5fc3\u4e2d\u4e00\u9178\uff0c\u81ea\u5df1\u4e00\u6b7b\uff0c\u518d\u4e5f\u65e0\u4eba\u4fdd\u62a4\u6c34\u7b19\uff0c\u6697\u53eb\uff1a\u201c\u82e6\u547d\u7684\u7b19\u513f\uff01\u201d\u559d\u9053\uff1a\u201c\u738b\u516b\u86cb\uff0c\u4f60\u8fd8\u4e0d\u6253\u6211\uff01\u201d \u3000\u3000\u72c4\u4e91\u4e5f\u5df2\u770b\u5230\u82b1\u94c1\u5e72\u6454\u5012\uff0c\u5fc3\u60f3\u8840\u5200\u50e7\u7acb\u65f6\u4fbf\u6765\uff0c\u5f53\u4e0b\u4e00\u54ac\u7259\uff0c\u594b\u529b\u6325\u68cd\u626b\u53bb\uff0c\u51fb\u5728\u6c34\u5cb1\u5929\u7075\u76d6\u4e0a\u3002\u6c34\u5cb1\u5934\u9885\u788e\u88c2\uff0c\u4e00\u4ee3\u5927\u4fa0\uff0c\u4fbf\u6b64\u60e8\u4ea1\u3002 \u3000\u3000\u6c34\u7b19\u54ed\u53eb\uff1a\u201c\u7239\u7239\uff01\u201d\u767b\u65f6\u6655\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u8840\u5200\u50e7\u542c\u5230\u6c34\u5cb1\u7684\u6bd2\u9a82\u4e4b\u58f0\uff0c\u53ea\u9053\u72c4\u4e91\u771f\u662f\u6c89\u4e0d\u4f4f\u6c14\uff0c\u51fa\u624b\u5c06\u4ed6\u6253\u6b7b\uff0c\u53cd\u6b63\u6b64\u523b\u82b1\u94c1\u5e72\u5df2\u7136\u7ed9\u81ea\u5df1\u5236\u4f4f\uff0c\u6c34\u5cb1\u662f\u6b7b\u662f\u6d3b\uff0c\u65e0\u5173\u5927\u5c40\u3002\u8fd9\u4e00\u6765\u5f97\u610f\u4e4b\u6781\uff0c\u4e0d\u7531\u5f97\u7eb5\u58f0\u957f\u7b11\u3002\u53ef\u662f\u81ea\u5df1\u542c\u5f97\u8fd9\u7b11\u58f0\u5168\u7136\u4e0d\u5bf9\uff0c\u53ea\u662f\u201c\u554a\uff0c\u554a\uff0c\u554a\u201d\u51e0\u4e0b\u5636\u54d1\u4e4b\u58f0\uff0c\u54ea\u91cc\u6709\u751a\u4e48\u7b11\u610f\uff1f\u4f46\u89c9\u817f\u819d\u95f4\u8d8a\u6765\u8d8a\u662f\u9178\u8f6f\uff0c\u8e52\u8dda\u7740\u8d70\u51fa\u51e0\u6b65\uff0c\u7ec8\u4e8e\u5750\u5012\u5728\u96ea\u5730\u4e4b\u4e2d\u3002 \u3000\u3000\u82b1\u94c1\u5e72\u770b\u5230\u8fd9\u822c\u60c5\u666f\uff0c\u5fc3\u4e0b\u5927\u6094\uff1a\u201c\u6c34\u5144\u5f1f\u8bf4\u5f97\u4e0d\u9519\uff0c\u8fd9\u6076\u50e7\u679c\u7136\u5df2\u662f\u771f\u6c14\u8017\u7aed\uff0c\u65e9\u77e5\u5982\u6b64\uff0c\u6211\u4e00\u51fa\u624b\u4fbf\u7ed3\u679c\u4e86\u4ed6\u7684\u6027\u547d\uff0c\u53c8\u4f55\u5fc5\u5413\u6210\u8fd9\u7b49\u6a21\u6837\uff1f\u66f4\u4f55\u5fc5\u5411\u4ed6\u78d5\u5934\u6c42\u9976\uff1f\u201d\u81ea\u5df1\u662f\u6210\u540d\u6570\u5341\u5e74\u7684\u4e2d\u539f\u5927\u4fa0\uff0c\u5c45\u7136\u5411\u8fd9\u4e07\u6076\u4e0d\u8d66\u7684\u654c\u4eba\u5c48\u819d\u54c0\u6073\uff0c\u8fd9\u7b49\u8d2a\u751f\u6015\u6b7b\uff0c\u65e0\u803b\u5351\u52a3\uff0c\u60f3\u8d77\u6765\u5f53\u771f\u65e0\u5730\u81ea\u5bb9\u3002\u53ea\u662f\u4ed6\u201c\u7075\u53f0\u201d\u8981\u7a74\u88ab\u70b9\uff0c\u987b\u5f97\u5341\u4e8c\u4e2a\u65f6\u8fb0\u4e4b\u540e\u65b9\u80fd\u89e3\u5f00\u3002\u8840\u5200\u50e7\u82e5\u4e0d\u9732\u51fa\u771f\u6c14\u8017\u7aed\u7684\u5f31\u70b9\uff0c\u81ea\u5df1\u8fd8\u6709\u6d3b\u547d\u4e4b\u671b\uff0c\u73b0\u4e0b\u662f\u8bf4\u751a\u4e48\u4e5f\u5bb9\u4e0d\u5f97\u81ea\u5df1\u4e86\u3002\u5426\u5219\u4e00\u7b49\u81ea\u5df1\u7a74\u9053\u89e3\u5f00\uff0c\u7109\u6709\u4e0d\u5411\u4ed6\u52a8\u624b\u4e4b\u7406\uff1f \u3000\u3000\u679c\u7136\u542c\u5f97\u8840\u5200\u50e7\u9053\uff1a\u201c\u5f92\u513f\uff0c\u5feb\u5c06\u8fd9\u4eba\u6740\u4e86\u3002\u8fd9\u4eba\u5978\u6076\u4e4b\u6781\uff0c\u7559\u4ed6\u4e0d\u5f97\u3002\u201d\u82b1\u94c1\u5e72\u53eb\u9053\uff1a\u201c\u4f60\u7b54\u5141\u9976\u6211\u6027\u547d\u7684\u3002\u4f60\u8bf4\u8fc7\u4e0d\u6740\u964d\u4eba\uff0c\u5982\u4f55\u53ef\u4ee5\u4e0d\u987e\u4fe1\u4e49\uff1f\u201d\u4ed6\u660e\u77e5\u6297\u8fa9\u5168\u7136\u65e0\u7528\uff0c\u4f46\u5927\u96be\u4e34\u5934\uff0c\u8fd8\u662f\u7aed\u529b\u6c42\u751f\u3002 \u3000\u3000\u8840\u5200\u50e7\u5e72\u7b11\u9053\uff1a\u201c\u6211\u4eec\u8840\u5200\u95e8\u7684\u9ad8\u50e7\uff0c\u628a\u2018\u4fe1\u4e49\u2019\u4e8c\u5b57\u77a7\u5f97\u72b9\u4f3c\u72d7\u5c4e\u4e00\u822c\uff0c\u4f60\u5411\u6211\u78d5\u5934\u6c42\u9976\uff0c\u662f\u4f60\u81ea\u5df1\u4e0a\u4e86\u6211\u7684\u5f53\uff0c\u54c8\u54c8\uff0c\u54c8\u54c8\uff01\u4e56\u5f92\u513f\uff0c\u5feb\u4e00\u68d2\u628a\u4ed6\u6253\u6740\u4e86\uff01\u6b64\u4eba\u7559\u7740\u4e0d\u6b7b\uff0c\u5371\u9669\u4e4b\u6781\u3002\u201d\u4ed6\u5bf9\u82b1\u94c1\u5e72\u4e5f\u771f\u5341\u5206\u5fcc\u60ee\uff0c\u81ea\u77e5\u521a\u624d\u4e00\u6307\u70b9\u7a74\uff0c\u5185\u529b\u4e0d\u5230\u5e73\u65f6\u7684\u4e00\u6210\uff0c\u529b\u9053\u4e0d\u80fd\u6df1\u900f\u7ecf\u8109\uff0c\u8fd9\u4eba\u6b66\u529f\u4e86\u5f97\uff0c\u53ea\u6015\u8fc7\u4e0d\u4e86\u51e0\u4e2a\u65f6\u8fb0\u5c31\u4f1a\u7ed9\u4ed6\u51b2\u5f00\u7a74\u9053\uff0c\u90a3\u65f6\u5019\u60c5\u52bf\u5012\u8f6c\uff0c\u81ea\u5df1\u53cd\u6210\u4fce\u4e0a\u4e4b\u8089\u4e86\u3002 \u3000\u3000\u72c4\u4e91\u4e0d\u77e5\u8840\u5200\u50e7\u5185\u529b\u8017\u7aed\uff0c\u53ea\u60f3\uff1a\u201c\u9002\u624d\u6211\u6740\u6c34\u5927\u4fa0\uff0c\u662f\u4e3a\u4e86\u89e3\u6551\u4ed6\u7684\u82e6\u607c\u3002\u8fd9\u4f4d\u82b1\u5927\u4fa0\u597d\u7aef\u7aef\u5730\uff0c\u6211\u4f55\u5fc5\u6740\u4ed6\uff1f\u201d\u4fbf\u9053\uff1a\u201c\u4ed6\u5df2\u7ed9\u5e08\u7956\u7237\u7237\u5236\u670d\uff0c\u6211\u770b\u4fbf\u9976\u4e86\u4ed6\u7f62\uff01\u201d \u3000\u3000\u82b1\u94c1\u5e72\u5fd9\u9053\uff1a\u201c\u662f\u554a\uff0c\u662f\u554a\uff01\u8fd9\u4f4d\u5c0f\u5e08\u7236\u8bf4\u5f97\u4e0d\u9519\u3002\u6211\u5df2\u7ed9\u4f60\u4eec\u5236\u670d\uff0c\u7edd\u65e0\u534a\u5206\u53cd\u6297\u4e4b\u5fc3\uff0c\u4f55\u5fc5\u518d\u8981\u6740\u6211\uff1f\u201d \u3000\u3000\u6c34\u7b19\u4ece\u660f\u6655\u4e2d\u60a0\u60a0\u9192\u8f6c\uff0c\u54ed\u53eb\uff1a\u201c\u7239\u7239\uff0c\u7239\u7239\uff01\u201d\u542c\u5f97\u82b1\u94c1\u5e72\u8fd9\u822c\u65e0\u803b\u6c42\u9976\uff0c\u9a82\u9053\uff1a\u201c\u82b1\u4f2f\u4f2f\uff0c\u4f60\u4e5f\u662f\u6b66\u6797\u4e2d\u54cd\u5f53\u5f53\u7684\u4e00\u53f7\u4eba\u7269\uff0c\u600e\u5730\u5982\u6b64\u4e0d\u8981\u8138\uff1f\u773c\u770b\u6211\u7239\u7239\u60e8\u53d7\u82e6\u5211\u2026\u2026\u6211\u7239\u7239\u2026\u2026\u7239\u2026\u2026\u7239\u2026\u2026\u201d\u8bf4\u5230\u8fd9\u91cc\uff0c\u5df2\u662f\u6ce3\u4e0d\u6210\u58f0\u3002\u82b1\u94c1\u5e72\u9053\uff1a\u201c\u8fd9\u4e24\u4f4d\u5e08\u7236\u6b66\u529f\u9ad8\u5f3a\uff0c\u54b1\u4eec\u662f\u6253\u4e0d\u8fc7\u7684\uff0c\u8fd8\u4e0d\u5982\u987a\u4ece\u964d\u670d\uff0c\u8ddf\u968f\u7740\u4ed6\u4eec\uff0c\u670d\u4ece\u4ed6\u4eec\u7684\u53f7\u4ee4\u4e3a\u662f\uff01\u201d\u6c34\u7b19\u8fde\u58f0\uff1a\u201c\u5478\uff01\u5478\uff01\u6b7b\u4e0d\u8981\u8138\uff01\u201d \u3000\u3000\u8840\u5200\u50e7\u5fc3\u60f3\u591a\u6328\u4e00\u523b\uff0c\u4fbf\u591a\u4e00\u5206\u5371\u9669\uff0c\u8fd9\u5f53\u513f\u81ea\u5df1\u7adf\u534a\u70b9\u529b\u6c14\u4e5f\u6ca1\u6709\u4e86\uff0c\u60f3\u8981\u652f\u6491\u8d77\u6765\u8d70\u4e0a\u4e24\u6b65\u4e5f\u662f\u4e0d\u80fd\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u5b69\u513f\uff0c\u542c\u5e08\u7956\u7237\u7237\u7684\u8bdd\uff0c\u5feb\u5c06\u8fd9\u5bb6\u4f19\u6740\u4e86\uff01\u201d \u3000\u3000\u6c34\u7b19\u56de\u8fc7\u5934\u6765\uff0c\u53ea\u89c1\u7236\u4eb2\u8111\u888b\u4e0a\u4e00\u7247\u8840\u8089\u6a21\u7cca\uff0c\u6b7b\u72b6\u6781\u60e8\uff0c\u60f3\u8d77\u4ed6\u5e73\u65f6\u5bf9\u81ea\u5df1\u7684\u6148\u7231\uff0c\u9aa8\u8089\u60c5\u6df1\uff0c\u51e0\u4e4e\u53c8\u6b32\u6655\u53bb\u3002\u6c34\u5cb1\u6073\u6c42\u72c4\u4e91\u5c06\u81ea\u5df1\u6253\u6b7b\uff0c\u6c34\u7b19\u539f\u662f\u4eb2\u8033\u542c\u89c1\uff0c\u4f46\u8fd9\u65f6\u6025\u75db\u653b\u5fc3\uff0c\u7adf\u7136\u5fd8\u4e86\uff0c\u53ea\u77e5\u9053\u72c4\u4e91\u4e00\u68cd\u5c06\u7236\u4eb2\u6253\u5f97\u8111\u6d46\u8ff8\u88c2\uff0c\u80f8\u4e2d\u60b2\u6124\uff0c\u96be\u4ee5\u6291\u5236\uff0c\u7a81\u89c9\u4e00\u80a1\u70ed\u6c14\u4ece\u4e39\u7530\u4e2d\u51b2\u5c06\u4e0a\u6765\u3002\u5185\u529f\u7ec3\u5230\u5341\u5206\u9ad8\u6df1\u4e4b\u4eba\uff0c\u80fd\u4ee5\u771f\u6c14\u51b2\u5f00\u88ab\u5c01\u7a74\u9053\u3002\u4f46\u8981\u7ec3\u5230\u8fd9\u7b49\u5883\u754c\uff0c\u90a3\u662f\u975e\u540c\u5c0f\u53ef\u4e4b\u4e8b\uff0c\u82b1\u94c1\u5e72\u5c1a\u81ea\u4e0d\u80fd\uff0c\u4f55\u51b5\u6c34\u7b19\uff1f\u53ef\u662f\u6bcf\u4e2a\u4eba\u5728\u4e34\u5230\u5927\u5371\u96be\u3001\u5927\u6fc0\u52a8\u7684\u7279\u6b8a\u53d8\u6545\u4e4b\u65f6\uff0c\u4f53\u5185\u6f5c\u80fd\u5ffd\u751f\uff0c\u5f80\u5f80\u80fd\u505a\u51fa\u5e73\u65f6\u7edd\u96be\u505a\u5230\u7684\u4e8b\u6765\u3002\u8fd9\u65f6\u6c34\u7b19\u6781\u5ea6\u60b2\u6124\u4e4b\u4e0b\uff0c\u4f53\u6c14\u6fc0\u8361\uff0c\u88ab\u5c01\u7684\u7a74\u9053\u7adf\u81ea\u5f00\u4e86\u3002\u4e5f\u4e0d\u77e5\u4ece\u54ea\u751f\u51fa\u6765\u4e00\u80a1\u529b\u6c14\uff0c\u84e6\u5730\u91cc\u4e00\u8dc3\u800c\u8d77\uff0c\u62fe\u8d77\u7236\u4eb2\u8eab\u65c1\u7684\u90a3\u6839\u6811\u679d\uff0c\u5939\u5934\u5939\u8111\u5411\u72c4\u4e91\u6253\u53bb\u3002 \u3000\u3000\u72c4\u4e91\u5de6\u8eb2\u53f3\u95ea\uff0c\u867d\u7136\u907f\u5f00\u4e86\u9762\u95e8\u8981\u5bb3\uff0c\u4f46\u8138\u4e0a\u3001\u8111\u540e\u3001\u8033\u65c1\u3001\u80a9\u5934\uff0c\u63a5\u8fde\u7ed9\u5979\u51fb\u4e2d\u4e86\u5341\u4e8c\u4e09\u4e0b\u3002\u4ed6\u4f38\u624b\u6321\u67b6\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u5e72\u751a\u4e48\u6253\u6211\uff1f\u662f\u4f60\u7239\u7239\u6c42\u6211\u6740\u4ed6\u7684\u3002\u201d \u3000\u3000\u6c34\u7b19\u4e00\u51db\uff0c\u60f3\u8d77\u6b64\u8a00\u4e0d\u9519\uff0c\u4e00\u5446\u4e4b\u4e0b\u4fbf\u6cc4\u4e86\u6c14\uff0c\u5750\u5012\u5728\u5730\uff0c\u653e\u58f0\u5927\u54ed\u3002 \u3000\u3000\u8840\u5200\u50e7\u542c\u5f97\u72c4\u4e91\u8bf4\u9053\uff1a\u201c\u662f\u4f60\u7239\u7239\u6c42\u6211\u6740\u4ed6\u7684\u3002\u201d\u5fc3\u5ff5\u4e00\u8f6c\uff0c\u5df2\u660e\u767d\u4e86\u5176\u4e2d\u539f\u59d4\uff0c\u4e0d\u7981\u5927\u6012\uff1a\u201c\u8fd9\u5c0f\u5b50\u7adf\u53bb\u76f8\u52a9\u654c\u4eba\uff0c\u5f53\u771f\u5927\u9006\u4e0d\u9053\u3002\u201d\u767b\u65f6\u4fbf\u60f3\u63d0\u5200\u5c06\u4ed6\u6740\u4e86\uff0c\u4f46\u624b\u81c2\u7565\u52a8\uff0c\u4fbf\u89c9\u8fde\u81c2\u5e26\u80a9\u4ff1\u90fd\u9ebb\u75f9\uff0c\u5f53\u4e0b\u4e0d\u52a8\u58f0\u8272\uff0c\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u4e56\u5f92\u513f\uff0c\u4f60\u597d\u597d\u770b\u4f4f\u8fd9\u5973\u5a03\u513f\uff0c\u522b\u8ba9\u5979\u53d1\u86ee\u3002\u5979\u662f\u4f60\u7684\u4eba\u4e86\uff0c\u4f60\u7231\u600e\u6837\u6574\u6cbb\u5979\uff0c\u5e08\u7956\u7237\u7237\u4efb\u4f60\u81ea\u4fbf\u3002\u201d \u3000\u3000\u82b1\u94c1\u5e72\u77a7\u51fa\u4e86\u7aef\u502a\uff0c\u53eb\u9053\uff1a\u201c\u6c34\u4f84\u5973\uff0c\u4f60\u8fc7\u6765\uff0c\u6211\u6709\u8bdd\u8ddf\u4f60\u8bf4\u3002\u201d\u4ed6\u77e5\u8840\u5200\u50e7\u6b64\u523b\u6ca1\u534a\u70b9\u529b\u6c14\uff0c\u5df2\u4e0d\u8db3\u4e3a\u60a3\uff0c\u72c4\u4e91\u5927\u817f\u6298\u65ad\uff0c\u56db\u4eba\u4e2d\u5012\u662f\u6c34\u7b19\u6700\u5f3a\uff0c\u8981\u4f4e\u58f0\u53eb\u5979\u4e58\u673a\u9664\u53bb\u4e8c\u50e7\u3002 \u3000\u3000\u54ea\u77e5\u6c34\u7b19\u6068\u6781\u4e86\u4ed6\u5351\u9119\u61e6\u602f\uff0c\u5fc3\u60f3\uff1a\u201c\u82e5\u4e0d\u662f\u4f60\u5f03\u67aa\u6295\u964d\uff0c\u6211\u7239\u7239\u4e5f\u4e0d\u81f4\u4e27\u547d\u3002\u201d\u542c\u5f97\u82b1\u94c1\u5e72\u547c\u53eb\uff0c\u7adf\u4e0d\u7406\u4e0d\u776c\u3002 \u3000\u3000\u82b1\u94c1\u5e72\u53c8\u9053\uff1a\u201c\u6c34\u4f84\u5973\uff0c\u4f60\u8981\u8131\u5374\u56f0\u5883\uff0c\u773c\u524d\u662f\u552f\u4e00\u826f\u673a\u3002 \u3000\u3000\u4f60\u8fc7\u6765\uff0c\u6211\u8ddf\u4f60\u8bf4\u3002\u201d\u8840\u5200\u50e7\u6012\u9053\uff1a\u201c\u4f60\u7f57\u91cc\u7f57\u55e6\u751a\u4e48\uff0c\u518d\u4e0d\u95ed\u5634\uff0c\u6211\u4e00\u5200\u5c06\u4f60\u6740\u4e86\u3002\u201d\u82b1\u94c1\u5e72\u5374\u4e5f\u4e0d\u6562\u771f\u548c\u4ed6\u9876\u649e\uff0c\u53ea\u662f\u4e0d\u4f4f\u7684\u5411\u6c34\u7b19\u4f7f\u773c\u8272\u3002\u6c34\u7b19\u6012\u9053\uff1a\u201c\u6709\u751a\u4e48\u8bdd\uff0c\u5c3d\u7ba1\u8bf4\u597d\u4e86\uff0c\u9b3c\u9b3c\u795f\u795f\u7684\u5e72\u751a\u4e48\uff1f\u201d \u3000\u3000\u82b1\u94c1\u5e72\u5fc3\u60f3\uff1a\u201c\u8fd9\u8001\u6076\u50e7\u6b63\u5728\u8fd0\u6c14\u6062\u590d\u5185\u529b\u3002\u4ed6\u53ea\u8981\u6062\u590d\u5f97\u4e00\u5206\uff0c\u80fd\u63d0\u5f97\u8d77\u5200\u5b50\uff0c\u5b9a\u662f\u5148\u5c06\u6211\u6740\u4e86\u3002\u65f6\u673a\u8feb\u4fc3\uff0c\u6211\u8bf4\u5f97\u8d8a\u5feb\u8d8a\u597d\u3002\u201d\u4fbf\u9053\uff1a\u201c\u6c34\u4f84\u5973\uff0c\u4f60\u77a7\u8fd9\u4f4d\u8001\u548c\u5c1a\uff0c\u4ed6\u5267\u6597\u4e4b\u4f59\uff0c\u5185\u529b\u8017\u5f97\u5e72\u5e72\u51c0\u51c0\uff0c\u5750\u5728\u5730\u4e0b\uff0c\u7ad9\u4e5f\u7ad9\u4e0d\u8d77\u6765\u4e86\u3002\u201d\u4ed6\u660e\u77e5\u8840\u5200\u50e7\u6b64\u523b\u65e0\u529b\u52a0\u5bb3\u81ea\u5df1\uff0c\u5374\u4e5f\u4e0d\u6562\u5bf9\u4ed6\u5931\u4e86\u656c\u610f\uff0c\u4ecd\u79f0\u4e4b\u4e3a\u201c\u8fd9\u4f4d\u8001\u548c\u5c1a\u201d\u3002 \u3000\u3000\u6c34\u7b19\u5411\u8840\u5200\u50e7\u77a7\u53bb\uff0c\u679c\u89c1\u4ed6\u659c\u5367\u96ea\u5730\uff0c\u60c5\u72b6\u6781\u662f\u72fc\u72c8\uff0c\u60f3\u8d77\u6740\u7236\u4e4b\u4ec7\uff0c\u4e5f\u4e0d\u7406\u4f1a\u82b1\u94c1\u5e72\u4e4b\u8a00\u662f\u771f\u662f\u5047\uff0c\u4e3e\u8d77\u624b\u4e2d\u7684\u6811\u679d\uff0c\u5f53\u5934\u5411\u8840\u5200\u50e7\u6253\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u8840\u5200\u50e7\u542c\u5f97\u82b1\u94c1\u5e72\u4e00\u518d\u62db\u547c\u6c34\u7b19\u8fc7\u53bb\uff0c\u4fbf\u5df2\u77e5\u4ed6\u5fc3\u610f\uff0c\u5fc3\u4e2d\u6697\u6697\u7740\u6025\uff0c\u98de\u5feb\u7684\u8f6c\u7740\u5ff5\u5934\uff1a\u201c\u8fd9\u5973\u5a03\u513f\u82e5\u6765\u5bb3\u6211\uff0c\u90a3\u4fbf\u5982\u4f55\u662f\u597d\uff1f\u201d\u4ed6\u53c8\u63d0\u4e86\u4e24\u6b21\u6c14\uff0c\u53ea\u89c9\u4e39\u7530\u4e2d\u7a7a\u8361\u8361\u5730\uff0c\u5168\u8eab\u53cd\u6bd4\u5148\u524d\u66f4\u662f\u8f6f\u5f31\uff0c\u4e00\u65f6\u5f77\u5fa8\u65e0\u8ba1\uff0c\u6c34\u7b19\u624b\u4e2d\u7684\u6811\u68cd\u5374\u5df2\u5f53\u5934\u6253\u6765\u3002 \u3000\u3000\u6c34\u7b19\u64c5\u4f7f\u7684\u5175\u5203\u4e43\u662f\u957f\u5251\uff0c\u672c\u6765\u4e0d\u4f1a\u4f7f\u68cd\uff0c\u52a0\u4e4b\u5fc3\u6025\u62a5\u7236\u4ec7\uff0c\u8fd9\u4e00\u68cd\u6253\u51fa\uff0c\u5168\u65e0\u7ae0\u6cd5\uff0c\u814b\u5e95\u66f4\u9732\u51fa\u8001\u5927\u7834\u7efd\u3002\u8840\u5200\u50e7\u8eab\u5b50\u7565\u4fa7\uff0c\u60f3\u5c06\u624b\u4e2d\u6240\u6301\u82b1\u94c1\u5e72\u7684\u77ed\u67aa\u659c\u4f38\u51fa\u53bb\uff0c\u53ea\u662f\u5b9e\u5728\u592a\u8fc7\u8870\u5f31\uff0c\u5355\u662f\u6389\u8f6c\u67aa\u5934\uff0c\u4e5f\u662f\u6709\u5fc3\u65e0\u529b\uff0c\u53ea\u5f97\u52c9\u529b\u5c06\u67aa\u5c3e\u5bf9\u51c6\u4e86\u6c34\u7b19\u814b\u4e0b\u7684\u201c\u5927\u5305\u7a74\u201d\u3002\u6c34\u7b19\u60b2\u6124\u4e4b\u4e0b\uff0c\u90a3\u9632\u5230\u4ed6\u53e6\u751f\u8be1\u8ba1\uff0c\u6811\u679d\u51fb\u843d\uff0c\u7ed3\u7ed3\u5b9e\u5b9e\u7684\u6253\u5728\u4ed6\u8138\u4e0a\uff0c\u767b\u65f6\u6253\u5f97\u4ed6\u76ae\u5f00\u8089\u7efd\uff0c\u4f46\u4fbf\u5728\u6b64\u65f6\uff0c\u814b\u4e0b\u7a74\u9053\u4e00\u9ebb\uff0c\u56db\u80a2\u9178\u8f6f\uff0c\u5411\u524d\u6454\u5012\u3002 \u3000\u3000\u8840\u5200\u50e7\u7ed9\u5979\u4e00\u68cd\u6253\u5f97\u5934\u6655\u773c\u82b1\uff0c\u8ba1\u7b56\u5374\u4e5f\u751f\u6548\uff0c\u6c34\u7b19\u81ea\u884c\u5c06\u201c\u5927\u5305\u7a74\u201d\u649e\u5230\u67aa\u6746\u4e0a\u53bb\uff0c\u70b9\u4e86\u81ea\u5df1\u7684\u7a74\u9053\u3002\u4ed6\u5f97\u610f\u4e4b\u4e0b\uff0c\u54c8\u54c8\u5927\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u59d3\u82b1\u7684\u8001\u8d3c\uff0c\u4f60\u8bf4\u6211\u6c14\u529b\u8870\u7aed\uff0c\u600e\u5730\u6211\u53c8\u80fd\u5236\u4f4f\u4e86\u5979\uff1f\u201d\u4ed6\u4ee5\u67aa\u6746\u5bf9\u51c6\u6c34\u7b19\u7a74\u9053\uff0c\u8ba9\u5979\u81ea\u884c\u649e\u4e0a\u6765\u7684\u624b\u6cd5\uff0c\u7ed9\u4ed6\u548c\u6c34\u7b19\u4e24\u4eba\u7684\u8eab\u5b50\u906e\u4f4f\uff0c\u82b1\u94c1\u5e72\u548c\u72c4\u4e91\u90fd\u6ca1\u77a7\u89c1\uff0c\u5747\u4ee5\u4e3a\u786e\u662f\u4ed6\u51fa\u624b\u70b9\u5012\u6c34\u7b19\u3002 \u3000\u3000\u82b1\u94c1\u5e72\u60ca\u60e7\u4ea4\u96c6\uff0c\u6ca1\u53e3\u5b50\u7684\u9053\uff1a\u201c\u8001\u524d\u8f88\u795e\u529f\u975e\u5e38\uff0c\u5728\u4e0b\u51e1\u592b\u4fd7\u5b50\u662f\u4e95\u86d9\u4e4b\u89c1\uff0c\u5f53\u771f\u6599\u60f3\u4e0d\u5230\u3002\u8001\u524d\u8f88\u5982\u6b64\u6df1\u539a\u7684\u5185\u529b\uff0c\u83ab\u8bf4\u4e3e\u4e16\u65e0\u53cc\uff0c\u7684\u7684\u786e\u786e\u662f\u7a7a\u524d\u7edd\u540e\u4e86\u3002\u201d\u4ed6\u6ee1\u53e3\u606d\u7ef4\u8840\u5200\u50e7\uff0c\u4f46\u8bdd\u58f0\u53d1\u98a4\uff0c\u5fc3\u4e2d\u6050\u60e7\u65e0\u6bd4\u3002 \u3000\u3000\u8840\u5200\u50e7\u5fc3\u4e2d\u6697\u53eb\uff1a\u201c\u60ed\u6127\uff01\u201d\u81ea\u77e5\u867d\u5f97\u6682\u514d\u6740\u8eab\u4e4b\u7978\uff0c\u4f46\u6c34\u7b19\u7a74\u9053\u88ab\u649e\u53ea\u662f\u5bfb\u5e38\u5916\u529b\uff0c\u5e76\u975e\u81ea\u5df1\u6307\u529b\u6240\u70b9\uff0c\u52b2\u529b\u4e0d\u900f\u7a74\u9053\u6df1\u5904\uff0c\u8fc7\u4e0d\u591a\u65f6\uff0c\u5979\u7a74\u9053\u81ea\u89e3\u3002\u8fd9\u7b49\u5e78\u8fd0\u4e4b\u4e8b\u53ef\u4e00\u800c\u4e0d\u53ef\u518d\uff0c\u5979\u82e5\u62fe\u8d77\u8840\u5200\u6765\u65a9\u6740\u81ea\u5df1\uff0c\u5c31\u7b97\u518d\u7528\u67aa\u6746\u649e\u4e2d\u5979\u7a74\u9053\uff0c\u81ea\u5df1\u7684\u5934\u9885\u53ef\u4e5f\u98de\u5411\u534a\u5929\u4e86\uff0c\u52a1\u987b\u5728\u8fd9\u77ed\u77ed\u7684\u65f6\u523b\u4e4b\u4e2d\u6062\u590d\u5c11\u8bb8\u529f\u529b\uff0c\u8981\u8d76\u7740\u5728\u6c34\u7b19\u7a74\u9053\u89e3\u5f00\u4e4b\u524d\u5148\u6740\u4e86\u5979\u3002\u53ea\u662f\u8fd9\u5185\u529b\u7684\u4e8b\u60c5\uff0c\u7a0d\u6709\u52c9\u5f3a\uff0c\u5927\u7978\u7acb\u751f\uff0c\u5f53\u4e0b\u4e00\u8a00\u4e0d\u53d1\uff0c\u8eba\u7740\u7f13\u7f13\u5410\u7eb3\u3002\u8fd9\u65f6\u4ed6\u4fbf\u8981\u76d8\u819d\u800c\u5750\uff0c\u4e5f\u5df2\u4e0d\u80fd\uff0c\u5374\u53c8\u4e0d\u6562\u95ed\u773c\uff0c\u751f\u6015\u8eab\u7554\u4e09\u4eba\u6709\u4f55\u52a8\u9759\uff0c\u4e0d\u5229\u4e8e\u5df1\u3002 \u3000\u3000\u72c4\u4e91\u5934\u4e0a\u3001\u80a9\u4e0a\u3001\u624b\u4e0a\u3001\u811a\u4e0a\uff0c\u5230\u5904\u75bc\u75db\u96be\u5f53\uff0c\u53ea\u6709\u54ac\u7259\u5fcd\u4f4f\u547b\u541f\uff0c\u5fc3\u4e2d\u4e00\u7247\u6df7\u4e71\uff0c\u65e0\u6cd5\u601d\u7d22\u3002 \u3000\u3000\u6c34\u7b19\u5367\u8eba\u5904\u79bb\u8840\u5200\u50e7\u4e0d\u5230\u4e09\u5c3a\uff0c\u521d\u65f6\u6781\u4e3a\u60f6\u6025\uff0c\u4e0d\u77e5\u8fd9\u6076\u50e7\u4e0b\u4e00\u6b65\u5c06\u5982\u4f55\u5bf9\u4ed8\u81ea\u5df1\uff0c\u8fc7\u4e86\u597d\u4e00\u4f1a\uff0c\u89c1\u4ed6\u6beb\u4e0d\u52a8\u5f39\uff0c\u624d\u7565\u611f\u653e\u5fc3\uff0c\u5979\u5fc3\u4e2d\u4f24\u75db\u5df2\u6781\uff0c\u4f53\u529b\u96be\u4ee5\u652f\u6301\uff0c\u8eba\u4e86\u4e00\u4f1a\u513f\uff0c\u52a0\u4e4b\u5fc3\u6025\u590d\u4ec7\uff0c\u7adf\u5c14\u660f\u660f\u7761\u53bb\u3002 \u3000\u3000\u8840\u5200\u50e7\u5fc3\u4e2d\u4e00\u559c\uff1a\u201c\u6700\u597d\u4f60\u4e00\u7761\u4fbf\u7761\u4e0a\u51e0\u4e2a\u65f6\u8fb0\uff0c\u90a3\u4fbf\u884c\u4e86\u3002\u201d \u3000\u3000\u8fd9\u4e00\u8282\u82b1\u94c1\u5e72\u4e5f\u77a7\u4e86\u51fa\u6765\uff0c\u773c\u89c1\u72c4\u4e91\u4e0d\u77e5\u662f\u5fc3\u8f6f\u8fd8\u662f\u80e1\u6d82\uff0c\u5c45\u7136\u5e76\u65e0\u6740\u5df1\u4e4b\u610f\uff0c\u81ea\u5df1\u7684\u751f\u6b7b\uff0c\u5168\u7cfb\u4e8e\u6c34\u7b19\u662f\u5426\u80fd\u6bd4\u8840\u5200\u50e7\u65e9\u4e00\u523b\u884c\u52a8\uff0c\u89c1\u5979\u7adf\u5c14\u7761\u53bb\uff0c\u5fd9\u53eb\uff1a\u201c\u6c34\u4f84\u5973\uff0c\u6c34\u4f84\u5973\uff0c\u5343\u4e07\u7761\u4e0d\u5f97\uff0c\u8fd9\u4e24\u4e2a\u6deb\u50e7\u8981\u5bf9\u4ed8\u4f60\u4e86\u3002\u201d\u4f46\u6c34\u7b19\u75b2\u7d2f\u96be\u5f53\uff0c\u660f\u7761\u4e2d\u53ea\u55ef\u55ef\u4e24\u58f0\uff0c\u5374\u54ea\u91cc\u53eb\u5f97\u5979\u9192\uff1f\u82b1\u94c1\u5e72\u5927\u53eb\uff1a\u201c\u4e0d\u597d\u4e86\uff0c\u4e0d\u597d\u4e86\uff01\u5feb\u4e9b\u9192\u6765\uff0c\u6076\u50e7\u8981\u5bb3\u4f60\u4e86\uff01\u201d \u3000\u3000\u8840\u5200\u50e7\u5927\u6012\uff0c\u5fc3\u60f3\uff1a\u201c\u8fd9\u822c\u5927\u547c\u5c0f\u53eb\uff0c\u5371\u9669\u975e\u5c0f\u3002\u201d\u5411\u72c4\u4e91\u9053\uff1a\u201c\u4e56\u5f92\u513f\uff0c\u4f60\u8fc7\u53bb\u4e00\u5200\u5c06\u8fd9\u8001\u5bb6\u4f19\u6740\u4e86\u3002\u201d\u72c4\u4e91\u9053\uff1a\u201c\u6b64\u4eba\u5df2\u7136\u964d\u670d\uff0c\u90a3\u4e5f\u4e0d\u7528\u6740\u4ed6\u4e86\u3002\u201d\u8840\u5200\u50e7\u9053\uff1a\u201c\u4ed6\u54ea\u91cc\u964d\u670d\uff1f\u4f60\u542c\u4ed6\u5927\u58f0\u5435\u56b7\uff0c\u4fbf\u662f\u8981\u5bb3\u6211\u5e08\u5f92\u3002\u201d \u3000\u3000\u82b1\u94c1\u5e72\u9053\uff1a\u201c\u5c0f\u5e08\u7236\uff0c\u4f60\u7684\u5e08\u7956\u51f6\u72e0\u6bd2\u8fa3\uff0c\u4ed6\u8fd9\u65f6\u771f\u6c14\u6563\u5931\uff0c\u884c\u52a8\u4e0d\u5f97\uff0c\u8fd9\u624d\u53eb\u4f60\u6765\u6740\u6211\u3002\u5f85\u4f1a\u4ed6\u5185\u529b\u6062\u590d\uff0c\u607c\u4f60\u4e0d\u4ece\u5e08\u547d\uff0c\u4fbf\u6765\u6740\u4f60\u4e86\u3002\u4e0d\u5982\u5148\u4e0b\u624b\u4e3a\u5f3a\uff0c\u5c06\u4ed6\u6740\u4e86\u3002\u201d\u72c4\u4e91\u6447\u5934\u9053\uff1a\u201c\u4ed6\u4e5f\u4e0d\u662f\u6211\u7684\u5e08\u7956\uff0c\u53ea\u662f\u4ed6\u6709\u6069\u4e8e\u6211\uff0c\u6551\u8fc7\u6211\u6027\u547d\u3002 \u3000\u3000\u6211\u5982\u4f55\u80fd\u591f\u6740\u4ed6\uff1f\u201d\u82b1\u94c1\u5e72\u9053\uff1a\u201c\u4ed6\u4e0d\u662f\u4f60\u5e08\u7956\uff1f\u90a3\u4f60\u5feb\u5feb\u52a8\u624b\uff0c\u66f4\u662f\u7247\u523b\u4e5f\u5ef6\u7f13\u4e0d\u5f97\u3002\u8840\u5200\u95e8\u7684\u548c\u5c1a\u51f6\u6076\u6b8b\u5fcd\uff0c\u6ca1\u534a\u70b9\u60c5\u9762\u597d\u8bb2\uff0c\u4f60\u81ea\u5df1\u60f3\u4e0d\u60f3\u6d3b\uff1f\u201d\u4ed6\u60c5\u6025\u4e4b\u4e0b\uff0c\u8a00\u8bed\u4e2d\u5bf9\u8840\u5200\u50e7\u5df2\u4e0d\u518d\u6709\u4e1d\u6beb\u656c\u610f\u3002 \u3000\u3000\u72c4\u4e91\u597d\u751f\u8e0c\u8e87\uff0c\u660e\u77e5\u4ed6\u8fd9\u8bdd\u6709\u7406\uff0c\u4f46\u8981\u4ed6\u53bb\u6740\u8840\u5200\u50e7\uff0c\u65e0\u8bba\u5982\u4f55\u4e0d\u5fcd\u4e0b\u624b\uff0c\u4f46\u542c\u82b1\u94c1\u5e72\u4e0d\u4f4f\u53e3\u7684\u529d\u8bf4\u50ac\u4fc3\uff0c\u7126\u8e81\u8d77\u6765\uff0c\u559d\u9053\uff1a\u201c\u4f60\u518d\u7f57\u91cc\u7f57\u55e6\uff0c\u6211\u5148\u6740\u4e86\u4f60\u3002\u201d \u3000\u3000\u82b1\u94c1\u5e72\u89c1\u60c5\u52bf\u4e0d\u5bf9\uff0c\u4e0d\u6562\u518d\u8bf4\uff0c\u53ea\u76fc\u6c34\u7b19\u65e9\u4e9b\u9192\u8f6c\uff0c\u8fc7\u4e86\u4e00\u4f1a\uff0c\u53c8\u5927\u58f0\u53eb\u56b7\uff1a\u201c\u6c34\u7b19\uff0c\u6c34\u7b19\uff0c\u4f60\u7239\u7239\u6d3b\u8f6c\u6765\u5566\uff0c\u4f60\u7239\u7239\u6d3b\u8f6c\u6765\u5566\uff01\u201d \u3000\u3000\u6c34\u7b19\u5728\u7761\u68a6\u4e2d\u8ff7\u8ff7\u7cca\u7cca\uff0c\u542c\u4eba\u558a\u9053\uff1a\u201c\u4f60\u7239\u7239\u6d3b\u8f6c\u6765\u5566\uff01\u201d \u3000\u3000\u5fc3\u4e2d\u4e00\u559c\uff0c\u767b\u65f6\u9192\u4e86\u8fc7\u6765\uff0c\u5927\u53eb\uff1a\u201c\u7239\u7239\uff0c\u7239\u7239\uff01\u201d \u3000\u3000\u82b1\u94c1\u5e72\u9053\uff1a\u201c\u6c34\u4f84\u5973\uff0c\u4f60\u88ab\u4ed6\u70b9\u4e86\u54ea\u4e00\u5904\u7a74\u9053\uff1f\u8fd9\u6076\u50e7\u5df2\u6ca1\u751a\u4e48\u529b\u6c14\uff0c\u70b9\u4e2d\u4e86\u4e5f\u6ca1\u751a\u4e48\u8981\u7d27\uff0c\u6211\u6559\u4f60\u4e2a\u5438\u6c14\u51b2\u89e3\u7a74\u9053\u7684\u6cd5\u95e8\u3002\u201d\u6c34\u7b19\u9053\uff1a\u201c\u6211\u5de6\u814b\u4e0b\u7684\u808b\u9aa8\u4e0a\u4e00\u9ebb\uff0c\u4fbf\u52a8\u5f39\u4e0d\u5f97\u4e86\u3002\u201d \u3000\u3000\u82b1\u94c1\u5e72\u9053\uff1a\u201c\u90a3\u662f\u2018\u5927\u5305\u7a74\u2019\u3002\u8fd9\u5bb9\u6613\u5f97\u5f88\uff0c\u4f60\u5438\u4e00\u53e3\u6c14\uff0c\u610f\u5b88\u4e39\u7530\uff0c\u7136\u540e\u7f13\u7f13\u5bfc\u5f15\u8fd9\u53e3\u6c14\uff0c\u53bb\u51b2\u51fb\u5de6\u814b\u4e0b\u7684\u2018\u5927\u5305\u7a74\u2019\uff0c\u51b2\u5f00\u4e4b\u540e\uff0c\u4fbf\u53ef\u62a5\u4f60\u6740\u7236\u4e4b\u4ec7\u3002\u201d \u3000\u3000\u6c34\u7b19\u70b9\u4e86\u70b9\u5934\uff0c\u9053\uff1a\u201c\u597d\uff01\u201d\u5979\u867d\u5bf9\u82b1\u94c1\u5e72\u4ecd\u662f\u5341\u5206\u6c14\u607c\uff0c\u4f46\u7a76\u7adf\u4ed6\u662f\u53cb\u975e\u654c\uff0c\u800c\u4ed6\u7684\u6559\u5bfc\u786e\u662f\u4e8e\u5df1\u6709\u5229\uff0c\u5f53\u5373\u4f9d\u8a00\u5438\u6c14\uff0c\u610f\u5b88\u4e39\u7530\u3002 \u3000\u3000\u8840\u5200\u50e7\u773c\u7741\u4e00\u7ebf\uff0c\u6ce8\u89c6\u5979\u7684\u52a8\u9759\uff0c\u89c1\u5979\u542c\u5230\u82b1\u94c1\u5e72\u7684\u8bdd\u540e\u70b9\u4e86\u70b9\u5934\uff0c\u4e0d\u7531\u5f97\u6697\u6697\u53eb\u82e6\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u5973\u5a03\u513f\u5df2\u80fd\u70b9\u5934\uff0c\u4e5f\u4e0d\u7528\u751a\u4e48\u610f\u5b88\u4e39\u7530\uff0c\u51b2\u51fb\u7a74\u9053\uff0c\u53ea\u6015\u4e0d\u5230\u4e00\u70b7\u9999\u7684\u65f6\u523b\uff0c\u4fbf\u80fd\u884c\u52a8\u4e86\u3002\u201d\u5f53\u4e0b\u773c\u89c2\u9f3b\uff0c\u9f3b\u89c2\u5fc3\uff0c\u4e8e\u6c34\u7b19\u662f\u5426\u80fd\u591f\u884c\u52a8\u4e00\u4e8b\uff0c\u5168\u7136\u7f6e\u4e4b\u5ea6\u5916\uff0c\u5c06\u8179\u4e2d\u4e00\u4e1d\u6e38\u6c14\u6162\u6162\u589e\u539a\u3002 \u3000\u3000\u90a3\u5bfc\u5f15\u771f\u6c14\u4ee5\u51b2\u51fb\u7a74\u9053\u7684\u529f\u592b\u4f55\u7b49\u6df1\u5965\u3002\u8fde\u82b1\u94c1\u5e72\u81ea\u5df1\u4e5f\u529e\u4e0d\u4e86\uff0c\u6c34\u7b19\u5355\u51ed\u4ed6\u51e0\u53e5\u8bdd\u6307\u70b9\uff0c\u5c82\u80fd\u884c\u4e4b\u6709\u6548\uff1f\u4f46\u5979\u88ab\u5c01\u7684\u7a74\u9053\u968f\u7740\u8840\u8109\u6d41\u8f6c\uff0c\u81ea\u7136\u800c\u7136\u7684\u65e9\u5df2\u5728\u6e10\u6e10\u677e\u5f00\uff0c\u5374\u4e0d\u662f\u5979\u7684\u771f\u6c14\u51b2\u51fb\u4e4b\u529f\uff0c\u8fc7\u4e0d\u591a\u65f6\uff0c\u5979\u80cc\u810a\u4fbf\u52a8\u4e86\u4e00\u52a8\u3002\u82b1\u94c1\u5e72\u559c\u9053\uff1a\u201c\u6c34\u4f84\u5973\uff0c\u884c\u5566\uff0c\u4f60\u7ee7\u7eed\u7528\u8fd9\u6cd5\u5b50\u51b2\u51fb\u7a74\u9053\uff0c\u7acb\u65f6\u4fbf\u80fd\u7ad9\u8d77\u6765\u4e86\u3002\u201d\u6c34\u7b19\u53c8\u70b9\u4e86\u70b9\u5934\uff0c\u81ea\u89c9\u624b\u8db3\u4e0a\u7684\u9ebb\u6728\u6e10\u5931\uff0c\u547c\u4e86\u4e00\u53e3\u957f\u6c14\uff0c\u6162\u6162\u652f\u6491\u7740\u5750\u8d77\u8eab\u6765\u3002 \u3000\u3000\u82b1\u94c1\u5e72\u53eb\u9053\uff1a\u201c\u5999\u6781\uff0c\u6c34\u4f84\u5973\uff0c\u4f60\u4e00\u4e3e\u4e00\u52a8\u90fd\u8981\u542c\u6211\u5429\u5490\uff0c\u4e0d\u53ef\u9519\u4e86\u987a\u5e8f\uff0c\u8fd9\u4e2d\u95f4\u7684\u5173\u952e\u5341\u5206\u8981\u7d27\uff0c\u5426\u5219\u5927\u4ec7\u96be\u62a5\u3002\u7b2c\u4e00\u6b65\uff0c\u62fe\u8d77\u5730\u4e0b\u7684\u90a3\u67c4\u5f2f\u5200\u3002\u201d \u3000\u3000\u6c34\u7b19\u6162\u6162\u4f38\u624b\u5230\u8840\u5200\u50e7\u8eab\u7554\uff0c\u62fe\u8d77\u4e86\u8840\u5200\u3002 \u3000\u3000\u72c4\u4e91\u77a7\u7740\u5979\u7684\u884c\u52a8\uff0c\u77e5\u9053\u5979\u4e0b\u4e00\u6b65\u4fbf\u662f\u6a2a\u5200\u4e00\u780d\uff0c\u5c06\u8840\u5200\u50e7\u7684\u8111\u888b\u5272\u4e86\u51fa\u6765\uff0c\u4f46\u89c1\u8840\u5200\u50e7\u7684\u53cc\u773c\u4f3c\u7741\u4f3c\u95ed\uff0c\u5bf9\u76ee\u524d\u7684\u5371\u96be\u7adf\u4f3c\u6d51\u4e0d\u5728\u610f\u3002 \u3000\u3000\u8840\u5200\u50e7\u6b64\u65f6\u81ea\u89c9\u624b\u8db3\u4e0a\u529b\u6c14\u6697\u751f\uff0c\u53ea\u987b\u518d\u6709\u5c0f\u534a\u4e2a\u65f6\u8fb0\uff0c\u867d\u65e0\u52b2\u529b\uff0c\u5374\u5df2\u53ef\u884c\u52a8\u81ea\u5982\uff0c\u504f\u751f\u6c34\u7b19\u62a2\u5148\u53d6\u4e86\u8840\u5200\uff0c\u7acb\u65f6\u4fbf\u8981\u53d1\u96be\uff0c\u5f53\u4e0b\u5c06\u5168\u8eab\u5fae\u5f31\u7684\u529b\u9053\u90fd\u96c6\u5411\u53f3\u81c2\u3002 \u3000\u3000\u5374\u542c\u5f97\u82b1\u94c1\u5e72\u53eb\u9053\uff1a\u201c\u7b2c\u4e8c\u6b65\uff0c\u5148\u53bb\u6740\u4e86\u5c0f\u548c\u5c1a\u3002\u5feb\uff0c\u5feb\uff0c\u5148\u6740\u5c0f\u548c\u5c1a\uff01\u201d \u3000\u3000\u8fd9\u4e00\u58f0\u547c\u53eb\uff0c\u6c34\u7b19\u3001\u8840\u5200\u50e7\u3001\u72c4\u4e91\u90fd\u5927\u51fa\u610f\u6599\u4e4b\u5916\u3002\u82b1\u94c1\u5e72\u53eb\u9053\uff1a\u201c\u8001\u548c\u5c1a\u8fd8\u4e0d\u4f1a\u52a8\uff0c\u5148\u6740\u5c0f\u548c\u5c1a\u8981\u7d27\u3002\u4f60\u5982\u5148\u6740\u8001\u548c\u5c1a\uff0c\u5c0f\u548c\u5c1a\u4fbf\u6765\u8ddf\u4f60\u62da\u547d\u4e86\uff01\u201d \u3000\u3000\u6c34\u7b19\u4e00\u60f3\u4e0d\u9519\uff0c\u63d0\u5200\u8d70\u5230\u72c4\u4e91\u8eab\u524d\uff0c\u5fc3\u4e2d\u5fae\u4e00\u8fdf\u7591\uff1a\u201c\u4ed6\u66fe\u52a9\u6211\u7239\u7239\uff0c\u4f7f\u5f97\u4ed6\u514d\u53d7\u8001\u6076\u50e7\u4e4b\u8fb1\uff0c\u6211\u662f\u4e0d\u662f\u8981\u6740\u4ed6\uff1f\u201d\u8fd9\u4e00\u8fdf\u7591\u53ea\u662f\u9877\u523b\u95f4\u7684\u4e8b\uff0c\u8ddf\u7740\u4fbf\u62ff\u5b9a\u4e86\u4e3b\u610f\uff1a\u201c\u5f53\u7136\u6740\uff01\u201d\u63d0\u8d77\u8840\u5200\uff0c\u4fbf\u5411\u72c4\u4e91\u9888\u4e2d\u5288\u843d\u3002 \u3000\u3000\u72c4\u4e91\u6025\u5fd9\u6253\u6eda\u907f\u5f00\u3002\u6c34\u7b19\u7b2c\u4e8c\u5200\u53c8\u780d\u5c06\u4e0b\u53bb\uff0c\u72c4\u4e91\u53c8\u662f\u4e00\u6eda\uff0c\u6293\u8d77\u5730\u4e0b\u7684\u4e00\u6839\u6811\u679d\uff0c\u5411\u5979\u5200\u4e0a\u683c\u53bb\u3002\u6c34\u7b19\u8fde\u780d\u4e09\u5200\uff0c\u5c06\u6811\u679d\u524a\u53bb\u4e24\u622a\uff0c\u53c8\u5373\u6325\u5200\u780d\u4e0b\uff0c\u7a81\u7136\u95f4\u624b\u8155\u4e0a\u4e00\u7d27\uff0c\u8840\u5200\u7adf\u88ab\u540e\u9762\u4e00\u4eba\u5939\u624b\u593a\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u62a2\u5979\u5175\u5203\u7684\u6b63\u662f\u8840\u5200\u50e7\u3002\u4ed6\u529b\u6c14\u6709\u9650\uff0c\u4e0d\u80fd\u865a\u53d1\uff0c\u770b\u5f97\u6781\u51c6\uff0c\u4e00\u51fa\u624b\u4fbf\u5373\u594f\u529f\uff0c\u593a\u5230\u8840\u5200\uff0c\u66f4\u4e0d\u601d\u7d22\uff0c\u987a\u624b\u6325\u5200\u4fbf\u5411\u5979\u9888\u4e2d\u780d\u4e0b\u3002\u6c34\u7b19\u4e0d\u53ca\u95ea\u907f\uff0c\u5fc3\u4e2d\u4e00\u51c9\u3002 \u3000\u3000\u72c4\u4e91\u53eb\u9053\uff1a\u201c\u522b\u518d\u6740\u4eba\u4e86\uff01\u201d\u6251\u5c06\u4e0a\u53bb\uff0c\u624b\u4e2d\u6811\u679d\u51fb\u5728\u8840\u5200\u50e7\u8155\u4e0a\u3002\u82e5\u5728\u5e73\u65f6\uff0c\u8840\u5200\u50e7\u7109\u80fd\u7ed9\u4ed6\u51fb\u4e2d\uff1f\u4f46\u8fd9\u65f6\u8870\u9893\u4e4b\u4f59\uff0c\u529f\u529b\u4e0d\u5230\u539f\u6765\u7684\u534a\u6210\uff0c\u624b\u6307\u4e00\u677e\uff0c\u8840\u536b\u8131\u624b\u3002\u4e24\u4eba\u540c\u65f6\u4fef\u8eab\u53bb\u62a2\u5175\u5203\u3002\u72c4\u4e91\u624b\u638c\u5728\u4e0b\uff0c\u5148\u6309\u5230\u4e86\u5200\u67c4\u3002\u8840\u5200\u50e7\u63d0\u8d77\u53cc\u624b\uff0c\u4fbf\u5f80\u4ed6\u9888\u4e2d\u627c\u53bb\u3002 \u3000\u3000\u72c4\u4e91\u4e00\u9635\u7a92\u606f\uff0c\u653e\u5f00\u4e86\u8840\u5200\uff0c\u4f38\u624b\u6491\u6301\u3002\u8840\u5200\u50e7\u77e5\u9053\u81ea\u5df1\u529b\u6c14\u65e0\u591a\uff0c\u8fd9\u4e00\u4e0b\u82e5\u4e0d\u5c06\u72c4\u4e91\u627c\u6b7b\uff0c\u81ea\u5df1\u4fbf\u547d\u4e27\u4ed6\u624b\u3002\u4ed6\u5374\u4e0d\u77e5\u72c4\u4e91\u5168\u65e0\u5bb3\u4ed6\u4e4b\u610f\uff0c\u53ea\u662f\u4e0d\u5fcd\u4ed6\u518d\u6740\u6c34\u7b19\uff0c\u4e0d\u81ea\u7981\u7684\u51fa\u624b\u76f8\u6551\u3002\u72c4\u4e91\u5934\u9888\u88ab\u8840\u5200\u50e7\u627c\u4f4f\uff0c\u547c\u5438\u8d8a\u6765\u8d8a\u8270\u96be\uff0c\u80f8\u53e3\u5982\u6b32\u8ff8\u88c2\u3002\u4ed6\u53cc\u624b\u53cd\u8fc7\u53bb\u4f7f\u52b2\u6491\u6301\uff0c\u60f3\u5c06\u8840\u5200\u50e7\u63a8\u5f00\u3002\u8840\u5200\u50e7\u89c1\u5c0f\u548c\u5c1a\u65e2\u8d77\u53cd\u53db\u4e4b\u610f\uff0c\u6309\u7167\u8840\u5200\u95e8\u4e2d\u7684\u89c4\u77e9\uff0c\u987b\u5f97\u5148\u9664\u53db\u5f92\uff0c\u518d\u6740\u654c\u4eba\u3002\u4ed6\u6599\u5f97\u82b1\u94c1\u5e72\u4e00\u65f6\u4e09\u523b\u4e4b\u95f4\u5c1a\u96be\u884c\u52a8\uff0c\u6c34\u7b19\u662f\u5973\u6d41\u4e4b\u8f88\uff0c\u6613\u4e8e\u5bf9\u4ed8\uff0c\u662f\u4ee5\u5c06\u8eab\u4e0a\u4ec5\u4f59\u7684\u529b\u9053\uff0c\u5c3d\u6570\u8fd0\u5230\u627c\u5728\u72c4\u4e91\u5589\u5934\u7684\u624b\u4e0a\u3002 \u3000\u3000\u72c4\u4e91\u4e00\u53e3\u6c14\u900f\u4e0d\u8fc7\u6765\uff0c\u6ee1\u8138\u7d2b\u80c0\uff0c\u53cc\u624b\u65e0\u529b\u53cd\u51fb\uff0c\u6162\u6162\u5782\u4e0b\uff0c\u8111\u6d77\u4e2d\u53ea\u662f\u4e00\u4e2a\u5ff5\u5934\uff1a\u201c\u6211\u8981\u6b7b\u4e86\uff0c\u6211\u8981\u6b7b\u4e86\uff01\u201d \u3000\u3000\u6c34\u7b19\u521d\u65f6\u89c1\u4e24\u4eba\u5728\u96ea\u5730\u4e2d\u7ffb\u6eda\uff0c\u773c\u89c1\u662f\u56e0\u72c4\u4e91\u76f8\u6551\u81ea\u5df1\u800c\u8d77\uff0c\u4f46\u603b\u89c9\u8fd9\u662f\u4e8c\u4e2a\u6076\u50e7\u81ea\u76f8\u6b8b\u6740\uff0c\u6700\u597d\u662f\u4ed6\u4e8c\u4eba\u6597\u4e2a\u4e24\u8d25\u4ff1\u4f24\uff0c\u540c\u5f52\u4e8e\u5c3d\u3002\u4f46\u770b\u4e86\u4e00\u4f1a\uff0c\u53ea\u89c1\u72c4\u4e91\u624b\u8db3\u8f6f\u5782\uff0c\u5df2\u65e0\u53cd\u51fb\u4e4b\u529b\uff0c\u4e0d\u7531\u5f97\u60ca\u60f6\u8d77\u6765\uff0c\u5fc3\u60f3\uff1a\u201c\u8001\u6076\u50e7\u6740\u4e86\u5c0f\u6076\u50e7\u4e4b\u540e\uff0c\u5c31\u4f1a\u6765\u6740\u6211\uff0c\u90a3\u4fbf\u5982\u4f55\u662f\u597d\uff1f\u201d \u3000\u3000\u82b1\u94c1\u5e72\u53eb\u9053\uff1a\u201c\u6c34\u4f84\u5973\uff0c\u8fd9\u662f\u4e0b\u624b\u7684\u826f\u673a\u554a\uff0c\u5feb\u5feb\u62fe\u8d77\u4e86\u5f2f\u5200\u3002\u201d\u6c34\u7b19\u4f9d\u8a00\u62fe\u8d77\u8840\u5200\u3002\u82b1\u94c1\u5e72\u53c8\u53eb\u9053\uff1a\u201c\u8fc7\u53bb\u5c06\u4e24\u4e2a\u6076\u50e7\u6740\u4e86\u3002\u201d \u3000\u3000\u6c34\u7b19\u63d0\u7740\u8840\u5200\u8d70\u4e0a\u51e0\u6b65\uff0c\u4e00\u5fc3\u8981\u5c06\u8840\u5200\u50e7\u6740\u6b7b\uff0c\u5374\u89c1\u4ed6\u548c\u72c4\u4e91\u7ea0\u7f20\u5728\u4e00\u8d77\u3002\u8fd9\u8840\u5200\u524a\u94c1\u5982\u6ce5\uff0c\u4e00\u5200\u4e0b\u53bb\uff0c\u52bf\u5fc5\u5c06\u4e24\u4eba\u540c\u65f6\u6740\u6b7b\uff0c\u5fc3\u60f3\u72c4\u4e91\u521a\u624d\u6551\u4e86\u81ea\u5df1\u6027\u547d\uff0c\u8fd9\u5c0f\u548c\u5c1a\u867d\u7136\u90aa\u6076\uff0c\u603b\u662f\u81ea\u5df1\u7684\u6551\u547d\u6069\u4eba\uff0c\u6069\u5c06\u4ec7\u62a5\uff0c\u65e0\u8bba\u5982\u4f55\u8bf4\u4e0d\u8fc7\u53bb\uff0c\u8981\u60f3\u4fdf\u9699\u53ea\u6740\u8840\u5200\u50e7\u4e00\u4eba\uff0c\u5374\u662f\u624b\u9178\u811a\u8f6f\uff0c\u5168\u65e0\u628a\u63e1\u3002 \u3000\u3000\u6b63\u8fdf\u7591\u95f4\uff0c\u82b1\u94c1\u5e72\u53c8\u50ac\u9053\uff1a\u201c\u5feb\u4e0b\u624b\u554a\uff0c\u518d\u7b49\u7247\u523b\uff0c\u5c31\u9519\u8fc7\u673a\u4f1a\u4e86\uff0c\u66ff\u4f60\u7239\u7239\u62a5\u4ec7\uff0c\u5728\u6b64\u4e00\u4e3e\u3002\u201d\u6c34\u7b19\u9053\uff1a\u201c\u4e24\u4e2a\u548c\u5c1a\u7f20\u5728\u4e00\u8d77\uff0c\u5206\u4e0d\u5f00\u6765\u3002\u201d\u82b1\u94c1\u5e72\u6012\u9053\uff1a\u201c\u4f60\u771f\u80e1\u6d82\uff0c\u6211\u53eb\u4f60\u4e24\u4e2a\u4eba\u4e00\u8d77\u6740\u4e86\uff01\u201d\u4ed6\u662f\u6b66\u6797\u4e2d\u7684\u6210\u540d\u82f1\u96c4\uff0c\u6c5f\u897f\u9e70\u722a\u94c1\u67aa\u95e8\u4e00\u6d3e\u7684\u638c\u95e8\uff0c\u5e73\u65f6\u9890\u6307\u6c14\u4f7f\uff0c\u8bf4\u51fa\u8bdd\u6765\u4fbf\u662f\u547d\u4ee4\u3002\u53ef\u662f\u4ed6\u5fd8\u4e86\u81ea\u5df1\u6b64\u523b\u52a8\u5f39\u4e0d\u5f97\uff0c\u6c34\u7b19\u5fc3\u4e2d\u5bf9\u4ed6\u53c8\u662f\u6781\u4e3a\u9119\u89c6\u3002\u5979\u4e00\u542c\u5230\u8fd9\u53e5\u72c2\u5984\u66b4\u8e81\u7684\u8bdd\uff0c\u767b\u65f6\u5927\u4e3a\u607c\u6012\uff0c\u53cd\u800c\u9000\u540e\u4e09\u6b65\uff0c\u8bf4\u9053\uff1a\u201c\u54fc\uff01\u4f60\u662f\u82f1\u96c4\u8c6a\u6770\uff0c\u521a\u624d\u4e3a\u751a\u4e48\u4e0d\u8ddf\u8fd9\u6076\u50e7\u51b3\u4e00\u6b7b\u6218\uff1f\u4f60\u6709\u672c\u4e8b\uff0c\u81ea\u5df1\u6765\u6740\u597d\u4e86\u3002\u201d \u3000\u3000\u82b1\u94c1\u5e72\u4e00\u542c\u60c5\u5f62\u4e0d\u5bf9\uff0c\u5fd9\u8d54\u7b11\u9053\uff1a\u201c\u597d\u4f84\u5973\uff0c\u662f\u82b1\u4f2f\u4f2f\u80e1\u6d82\uff0c\u4f60\u522b\u751f\u6c14\u3002\u4f60\u53bb\u5c06\u4e24\u4e2a\u6076\u50e7\u90fd\u6740\u4e86\uff0c\u7ed9\u4f60\u7239\u7239\u62a5\u4ec7\u3002\u8840\u5200\u8001\u7956\u8fd9\u6837\u51fa\u540d\u7684\u5927\u6076\u4eba\u6b7b\u5728\u4f60\u624b\u4e0b\uff0c\u8fd9\u4ef6\u4e8b\u4f20\u626c\u51fa\u53bb\uff0c\u6c5f\u6e56\u4e0a\u54ea\u4e00\u4e2a\u4e0d\u94a6\u4f69\u6c34\u5973\u4fa0\u5b5d\u4e49\u65e0\u53cc\u3001\u82f1\u96c4\u4e86\u5f97\uff1f\u201d\u4ed6\u8d8a\u5439\u6367\uff0c\u6c34\u7b19\u8d8a\u607c\uff0c\u77aa\u4e86\u82b1\u94c1\u5e72\u4e00\u773c\uff0c\u53c8\u8d70\u4e0a\u524d\u53bb\uff0c\u770b\u51c6\u4e86\u8840\u5200\u50e7\u7684\u80cc\u810a\uff0c\u60f3\u5272\u4ed6\u4e24\u5200\uff0c\u53eb\u4ed6\u6d41\u8840\u4e0d\u6b62\uff0c\u5374\u4e0d\u4f1a\u4f24\u5230\u72c4\u4e91\u3002 \u3000\u3000\u8840\u5200\u50e7\u627c\u5728\u72c4\u4e91\u9888\u4e2d\u7684\u53cc\u624b\u6beb\u4e0d\u653e\u677e\uff0c\u5374\u4e0d\u4f4f\u8f6c\u5934\u89c2\u770b\u6c34\u7b19\u7684\u52a8\u9759\uff0c\u89c1\u5979\u6301\u5200\u53c8\u4e0a\uff0c\u731c\u5230\u4e86\u5979\u5fc3\u610f\uff0c\u6c89\u7740\u58f0\u97f3\u9053\uff1a\u201c\u4f60\u5728\u6211\u80cc\u4e0a\u8f7b\u8f7b\u5272\u4e0a\u4e24\u5200\uff0c\u5c0f\u5fc3\u522b\u4f24\u5230\u4e86\u5c0f\u548c\u5c1a\u3002\u201d \u3000\u3000\u6c34\u7b19\u5403\u4e86\u4e00\u60ca\uff0c\u5979\u5bf9\u8840\u5200\u50e7\u6781\u4e3a\u754f\u60e7\u5fcc\u60ee\uff0c\u542c\u5f97\u4ed6\u53eb\u81ea\u5df1\u7528\u5200\u5272\u4ed6\u80cc\u810a\uff0c\u5fc3\u60f3\u4ed6\u5b9a\u7136\u4e0d\u6000\u597d\u610f\uff0c\u51b3\u4e0d\u80fd\u542c\u4ed6\u7684\u8bdd\uff0c\u54ea\u6599\u5230\u8fd9\u662f\u8840\u5200\u50e7\u5b9e\u8005\u865a\u4e4b\u3001\u865a\u8005\u5b9e\u4e4b\u7684\u653b\u5fc3\u4e4b\u7b56\uff0c\u4e00\u6014\u4e4b\u4e0b\uff0c\u8fd9\u4e00\u5200\u4fbf\u5272\u4e0d\u4e0b\u53bb\u4e86\u3002 \u3000\u3000\u72c4\u4e91\u7ed9\u8840\u5200\u8001\u7956\u627c\u4f4f\u5589\u5934\uff0c\u80ba\u4e2d\u79ef\u805a\u7740\u7684\u4e00\u80a1\u6d4a\u6c14\u6570\u5ea6\u4e0a\u51b2\uff0c\u8981\u4ece\u53e3\u9f3b\u4e2d\u547c\u4e86\u51fa\u6765\uff0c\u4f46\u5589\u5934\u7684\u8981\u9053\u88ab\u963b\uff0c\u8fd9\u80a1\u6c14\u51b2\u5230\u5589\u5934\uff0c\u53c8\u56de\u4e86\u4e0b\u53bb\u3002\u4e00\u80a1\u6d4a\u6c14\u5728\u4f53\u5185\u5de6\u51b2\u53f3\u7a81\uff0c\u59cb\u7ec8\u627e\u4e0d\u5230\u51fa\u8def\u3002\u82e5\u662f\u6362\u4f5c\u5e38\u4eba\uff0c\u90a3\u4fbf\u6e10\u6e10\u660f\u8ff7\uff0c\u7ec8\u4e8e\u7a92\u606f\u8eab\u4ea1\uff0c\u4f46\u4ed6\u504f\u504f\u65e0\u6cd5\u660f\u8ff7\uff0c\u53ea\u611f\u5168\u8eab\u96be\u53d7\u56f0\u82e6\u5df2\u8fbe\u6781\u70b9\uff0c\u5fc3\u4e2d\u53ea\u53eb\uff1a \u3000\u3000\u201c\u6211\u5feb\u8981\u6b7b\u4e86\uff0c\u6211\u5feb\u8981\u6b7b\u4e86\uff01\u201d \u3000\u3000\u7a81\u7136\u4e4b\u95f4\uff0c\u4ed6\u53ea\u89c9\u80f8\u8179\u95f4\u5267\u70c8\u523a\u75db\uff0c\u4f53\u5185\u8fd9\u80a1\u6c14\u8d8a\u80c0\u8d8a\u5927\uff0c\u8d8a\u6765\u8d8a\u70ed\uff0c\u72b9\u5982\u6ee1\u956c\u84b8\u6c14\u6ca1\u6709\u51fa\u53e3\uff0c\u76f4\u8981\u88c2\u8179\u800c\u7206\uff0c\u84e6\u5730\u91cc\u524d\u9634\u540e\u9634\u4e4b\u95f4\u7684\u201c\u4f1a\u9634\u7a74\u201d\u4e0a\u4f3c\u4e4e\u88ab\u70ed\u6c14\u7a7f\u7834\u4e86\u4e00\u4e2a\u5c0f\u5b54\uff0c\u767b\u65f6\u89c9\u5f97\u6709\u4e1d\u4e1d\u70ed\u6c14\u4ece\u201c\u4f1a\u9634\u7a74\u201d\u901a\u5230\u810a\u690e\u672b\u7aef\u7684\u201c\u957f\u5f3a\u7a74\u201d\u53bb\u3002\u4eba\u8eab\u201c\u4f1a\u9634\u201d\u201c\u957f\u5f3a\u201d\u4e24\u7a74\u76f8\u8ddd\u4e0d\u8fc7\u6570\u5bf8\uff0c\u4f46\u201c\u4f1a\u9634\u201d\u5c5e\u4e8e\u4efb\u8109\uff0c\u201c\u957f\u5f3a\u201d\u5374\u662f\u7763\u8109\uff0c\u4e24\u8109\u7684\u5185\u606f\u51b3\u4e0d\u76f8\u901a\u3002\u4ed6\u4f53\u5185\u7684\u5185\u606f\u52a0\u4e0a\u65e0\u6cd5\u5ba3\u6cc4\u7684\u4e00\u80a1\u5de8\u5927\u6d4a\u6c14\uff0c\u4ea4\u8ff8\u649e\u6fc0\uff0c\u7adf\u5728\u5371\u6025\u4e2d\u81ea\u884c\u5f3a\u51b2\u731b\u653b\uff0c\u66ff\u4ed6\u6253\u901a\u4e86\u4efb\u8109\u548c\u7763\u8109\u7684\u5927\u96be\u5173\u3002 \u3000\u3000\u8fd9\u5185\u606f\u4e00\u901a\u5165\u201c\u957f\u5f3a\u7a74\u201d\uff0c\u767b\u65f6\u81ea\u8170\u4fde\u3001\u9633\u5173\u3001\u547d\u95e8\u3001\u60ac\u67a2\u8bf8\u7a74\uff0c\u4e00\u8def\u6cbf\u7740\u810a\u690e\u4e0a\u5347\uff0c\u8d70\u7684\u90fd\u662f\u80cc\u4e0a\u7763\u4efb\u5404\u4e2a\u8981\u7a74\uff0c\u7136\u540e\u662f\u810a\u4e2d\u3001\u4e2d\u67a2\u3001\u7b4b\u7f29\u3001\u81f3\u9633\u3001\u7075\u53f0\u3001\u795e\u9053\u3001\u8eab\u67f1\u3001\u9676\u9053\u3001\u5927\u690e\u3001\u75d6\u95e8\u3001\u98ce\u5e9c\u3001\u8111\u6237\u3001\u5f3a\u95f4\u3001\u540e\u9876\u3001\u800c\u81f3\u9876\u95e8\u7684\u201c\u767e\u4f1a\u7a74\u201d\u3002\u72c4\u4e91\u5728\u72f1\u4e2d\u5f97\u4e01\u5178\u4f20\u6388\u201c\u795e\u7167\u7ecf\u201d\u7684\u5fc3\u6cd5\uff0c\u8fd9\u5185\u529f\u6781\u662f\u6df1\u6e5b\u96be\u7ec3\uff0c\u4ed6\u8d44\u8d28\u975e\u4f73\uff0c\u6b64\u540e\u53c8\u65e0\u4e01\u5178\u6307\u70b9\uff0c\u518d\u52a0\u4e0a\u4e8c\u4e09\u5341\u5e74\u7684\u65f6\u65e5\uff0c\u662f\u5426\u5f97\u80fd\u7ec3\u6210\uff0c\u4ea6\u5728\u672a\u77e5\u4e4b\u6570\u3002\u4e0d\u6599\u6b64\u523b\u5728\u751f\u6b7b\u7cfb\u4e8e\u4e00\u7ebf\u4e4b\u9645\uff0c\u7adf\u5c14\u5c06\u4efb\u7763\u4e8c\u8109\u6253\u901a\u4e86\u3002\u8fd9\u4e00\u6765\u56e0\u54bd\u5589\u88ab\u627c\uff0c\u4f53\u5185\u6d4a\u6c14\u96be\u5ba3\uff0c\u975e\u627e\u5bfb\u51fa\u53e3\u4e0d\u53ef\uff0c\u4e8c\u6765\u4ed6\u66fe\u7ec3\u8fc7\u201c\u8840\u5200\u7ecf\u201d\u4e0a\u7684\u4e00\u4e9b\u90aa\u6d3e\u5185\u529f\uff0c\u5185\u606f\u8fd0\u884c\u7684\u9053\u8def\u867d\u548c\u201c\u795e\u7167\u7ecf\u201d\u5185\u529f\u5927\u5f02\uff0c\u5374\u4e5f\u6709\u7834\u7a92\u51b2\u585e\u7684\u8865\u52a9\u529f\u6548\u3002 \u3000\u3000\u8fd9\u80a1\u5185\u606f\u51b2\u5230\u767e\u4f1a\u7a74\u4e2d\uff0c\u53ea\u89c9\u989c\u9762\u4e0a\u4e00\u9635\u6e05\u51c9\uff0c\u4e00\u80a1\u51c9\u6c14\u4ece\u989d\u5934\u3001\u9f3b\u6881\u3001\u53e3\u5507\u4e0b\u6765\uff0c\u901a\u5230\u4e86\u5507\u4e0b\u7684\u201c\u627f\u6d46\u7a74\u201d\u3002\u8fd9\u627f\u6d46\u7a74\u5df2\u5c5e\u4efb\u8109\uff0c\u8fd9\u4e00\u6765\u81ea\u7763\u8fd4\u4efb\u3002\u4efb\u8109\u8bf8\u7a74\u90fd\u5728\u4eba\u4f53\u6b63\u9762\uff0c\u8fd9\u80a1\u6e05\u51c9\u7684\u5185\u606f\u4e00\u8def\u4e0b\u884c\uff0c\u81ea\u5ec9\u6cc9\u3001\u5929\u7a81\u800c\u81f3\u7487\u7391\u3001\u534e\u76d6\u3001\u7d2b\u5bab\u3001\u7389\u5802\u3001\u81bb\u4e2d\u3001\u4e2d\u5ead\u3001\u9e20\u5c3e\u3001\u5de8\u9619\u3001\u7ecf\u4e0a\u3001\u4e2d\u3001\u4e0b\u4e09\u8118\uff0c\u800c\u81f3\u6c34\u5206\u3001\u795e\u53a5\u3001\u6c14\u6d77\u3001\u77f3\u95e8\u3001\u5173\u5143\u3001\u4e2d\u6781\u3001\u66f2\u9aa8\u8bf8\u7a74\uff0c\u53c8\u56de\u5230\u4e86\u201c\u4f1a\u9634\u7a74\u201d\u3002\u5982\u6b64\u4e00\u4e2a\u5468\u5929\u884c\u5c06\u4e0b\u6765\uff0c\u90c1\u95f7\u4e4b\u610f\u5168\u6d88\uff0c\u8bf4\u4e0d\u51fa\u7684\u7545\u5feb\u53d7\u7528\u3002\u5185\u606f\u7b2c\u4e00\u6b21\u901a\u884c\u65f6\u751a\u662f\u8270\u96be\uff0c\u4efb\u7763\u4e24\u8109\u65e2\u901a\uff0c\u9053\u8def\u719f\u4e86\uff0c\u7b2c\u4e8c\u6b21\u3001\u7b2c\u4e09\u6b21\u65f6\u81ea\u7136\u800c\u7136\u7684\u98de\u5feb\u8fd0\u8f6c\uff0c\u9877\u523b\u4e4b\u95f4\uff0c\u8fde\u8d70\u4e86\u4e00\u5341\u516b\u6b21\u3002 \u3000\u3000\u201c\u795e\u7ecf\u7167\u201d\u5185\u529f\u4e43\u6b66\u5b66\u7b2c\u4e00\u5947\u529f\uff0c\u4ed6\u81ea\u5728\u72f1\u4e2d\u5f00\u59cb\u4fee\u4e60\uff0c\u7ec3\u4e4b\u5df2\u4e45\uff0c\u6b64\u523b\u4e00\u65e6\u8c41\u7136\u800c\u901a\uff0c\u5185\u606f\u8fd0\u884c\u4e00\u5468\u5929\uff0c\u52b2\u529b\u4fbf\u589e\u52a0\u4e00\u5206\uff0c\u53ea\u89c9\u56db\u80a2\u767e\u9ab8\uff0c\u6bcf\u4e00\u5904\u90fd\u6709\u7cbe\u795e\u529b\u6c14\u52c3\u7136\u800c\u5174\uff0c\u6c9b\u7136\u800c\u81f3\uff0c\u751a\u81f3\u5934\u53d1\u6839\u4e0a\u4f3c\u4e4e\u5747\u6709\u52b2\u529b\u5145\u76c8\u3002 \u3000\u3000\u8840\u5200\u50e7\u54ea\u91cc\u77e5\u9053\u4ed6\u5341\u6307\u4e0b\u627c\u4e4b\u4eba\uff0c\u4f53\u5185\u5df2\u8d77\u4e86\u5982\u6b64\u5de8\u5927\u53d8\u5316\uff0c\u53ea\u662f\u52a0\u7d27\u627c\u4f4f\u4ed6\u54bd\u5589\uff0c\u4e00\u9762\u51dd\u795e\u63d0\u9632\u6c34\u7b19\u624b\u4e2d\u7684\u8840\u5200\u3002 \u3000\u3000\u72c4\u4e91\u4f53\u5185\u7684\u52b2\u529b\u6108\u6765\u6108\u5f3a\uff0c\u5fc3\u4e2d\u5374\u4ecd\u662f\u5341\u5206\u5bb3\u6015\uff0c\u53ea\u6c42\u6323\u624e\u8131\u8eab\uff0c\u53cc\u624b\u4e71\u6293\u4e71\u821e\uff0c\u59cb\u7ec8\u78b0\u4e0d\u5230\u8840\u5200\u50e7\u8eab\u4e0a\uff0c\u5de6\u811a\u5411\u540e\u4e71\u6491\u51e0\u4e0b\uff0c\u7a81\u7136\u4e00\u811a\u8e39\u5728\u8840\u5200\u50e7\u7684\u5c0f\u8179\u4e4b\u4e0a\u3002\u8fd9\u4e00\u8e39\u529b\u9053\u5927\u5f97\u51fa\u5947\uff0c\u8840\u5200\u50e7\u672c\u5df2\u5185\u529b\u8017\u7aed\uff0c\u54ea\u91cc\u6709\u534a\u70b9\u6297\u529b\uff1f\u8eab\u5b50\u5ffd\u5982\u817e\u4e91\u9a7e\u96fe\u822c\u98de\u5411\u534a\u7a7a\u3002 \u3000\u3000\u6c34\u7b19\u548c\u82b1\u94c1\u5e72\u9f50\u58f0\u60ca\u547c\uff0c\u4e0d\u77e5\u51fa\u4e86\u751a\u4e48\u53d8\u6545\uff0c\u4f46\u89c1\u8840\u5200\u50e7\u9ad8\u9ad8\u8dc3\u8d77\uff0c\u5728\u7a7a\u4e2d\u6253\u4e86\u4e2a\u8f6c\uff0c\u5934\u4e0b\u811a\u4e0a\u7684\u7b14\u76f4\u6454\u5c06\u4e0b\u6765\uff0c\u64e6\u7684\u4e00\u58f0\uff0c\u76f4\u633a\u633a\u7684\u63d2\u5165\u96ea\u4e2d\uff0c\u6df1\u5165\u6570\u5c3a\uff0c\u96ea\u9762\u4e0a\u53ea\u9732\u51fa\u4e00\u53cc\u811a\uff0c\u7adf\u5c31\u6b64\u4e00\u52a8\u4e0d\u52a8\u3002 \u3000"}),showOnHomepage:!0},{name:"\u516b\u3000\u7fbd\u8863",content:(0,a.jsx)(a.Fragment,{children:"\u6c34\u7b19\u548c\u82b1\u94c1\u5e72\u90fd\u770b\u5f97\u5446\u4e86\uff0c\u4e0d\u77e5\u8840\u5200\u50e7\u53c8\u5728\u65bd\u5c55\u751a\u4e48\u795e\u5947\u6b66\u529f\u3002 \u3000\u3000\u72c4\u4e91\u54bd\u5589\u95f4\u8131\u5374\u7d27\u7b8d\uff0c\u6025\u5598\u4e86\u51e0\u53e3\u6c14\uff0c\u5f53\u4e0b\u53ea\u6c42\u9003\u751f\uff0c\u4e00\u8dc3\u800c\u8d77\uff0c\u8eab\u5b50\u7ad9\u76f4\uff0c\u53ea\u662f\u53f3\u817f\u65ad\u4e86\uff0c\u201c\u554a\u54df\u201d\u4e00\u58f0\uff0c\u4fef\u8dcc\u4e0b\u53bb\uff0c\u4ed6\u53f3\u624b\u5fd9\u5728\u5730\u4e0b\u4e00\u6491\uff0c\u5355\u51ed\u5de6\u817f\u7ad9\u4e86\u8d77\u6765\uff0c\u53ea\u89c1\u8840\u5200\u8001\u7956\u53cc\u811a\u5411\u5929\uff0c\u5012\u63d2\u5728\u96ea\u4e2d\u3002\u4ed6\u5927\u60d1\u4e0d\u89e3\uff0c\u63c9\u4e86\u63c9\u773c\u775b\uff0c\u770b\u6e05\u695a\u8840\u5200\u8001\u7956\u786e\u662f\u5012\u63d2\u5728\u6df1\u96ea\u4e4b\u4e2d\uff0c\u5168\u4e0d\u52a8\u5f39\u3002 \u3000\u3000\u6c34\u7b19\u5f53\u72c4\u4e91\u8dc3\u8d77\u4e4b\u65f6\uff0c\u552f\u6050\u4ed6\u52a0\u5bb3\u81ea\u5df1\uff0c\u6a2a\u5200\u80f8\u524d\uff0c\u5012\u9000\u51e0\u6b65\uff0c\u76ee\u4e0d\u8f6c\u775b\u7684\u51dd\u89c6\u7740\u4ed6\u3002\u4f46\u89c1\u4ed6\u4f38\u624b\u6414\u5934\uff0c\u6ee1\u8138\u8ff7\u60d8\u4e4b\u8272\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u82b1\u94c1\u5e72\u8d5e\u9053\uff1a\u201c\u8fd9\u4f4d\u5c0f\u5e08\u7236\u795e\u529f\u76d6\u4e16\uff0c\u5f53\u771f\u662f\u5e76\u4e16\u65e0\u53cc\uff0c\u521a\u624d\u8fd9\u4e00\u811a\u5c06\u8001\u6deb\u50e7\u8e22\u6b7b\uff0c\u6015\u4e0d\u6709\u5343\u4f59\u65a4\u7684\u52b2\u529b\uff01\u8fd9\u7b49\u4fa0\u4e49\u884c\u5f84\uff0c\u4ee4\u4eba\u6253\u4ece\u5fc3\u5e95\u91cc\u94a6\u4f69\u51fa\u6765\u3002\u201d\u6c34\u7b19\u542c\u5230\u8fd9\u91cc\uff0c\u518d\u4e5f\u5fcd\u8010\u4e0d\u4f4f\uff0c\u559d\u9053\uff1a\u201c\u4f60\u522b\u518d\u80e1\u8a00\u4e71\u8bed\uff0c\u4e5f\u4e0d\u6015\u4eba\u542c\u4e86\u4f5c\u5455\uff1f\u201d \u3000\u3000\u82b1\u94c1\u5e72\u9053\uff1a\u201c\u8840\u5200\u50e7\u5927\u5978\u5927\u6076\uff0c\u4eba\u4eba\u5f97\u800c\u8bdb\u4e4b\u3002\u5c0f\u5e08\u7236\u5927\u4e49\u706d\u4eb2\uff0c\u5927\u8282\u51db\u7136\uff0c\u52a0\u500d\u7684\u4e0d\u5bb9\u6613\uff0c\u96be\u5f97\uff0c\u96be\u5f97\uff0c\u53ef\u559c\u53ef\u8d3a\u3002\u201d \u3000\u3000\u4ed6\u773c\u89c1\u8840\u5200\u50e7\u53cc\u8db3\u50f5\u76f4\uff0c\u663e\u7136\u5df2\u7ecf\u6b7b\u4e86\uff0c\u5f53\u5373\u6539\u53e3\u5927\u6367\u72c4\u4e91\u3002 \u3000\u3000\u5176\u5b9e\u4ed6\u4e3a\u4eba\u867d\u7136\u9634\u72e0\uff0c\u4f46\u4e00\u751f\u884c\u4fa0\u4ed7\u4e49\uff0c\u5e76\u6ca1\u505a\u8fc7\u751a\u4e48\u5978\u6076\u4e4b\u4e8b\uff0c\u5426\u5219\u600e\u80fd\u548c\u9646\u3001\u5218\u3001\u6c34\u4e09\u4fa0\u76f8\u4ea4\u6570\u5341\u5e74\uff0c\u60c5\u82e5\u5144\u5f1f\uff1f\u53ea\u662f\u4eca\u65e5\u4e00\u67aa\u8bef\u6740\u4e86\u4e49\u5f1f\u5218\u4e58\u98ce\uff0c\u5fc3\u795e\u5927\u53d7\u6fc0\u8361\uff0c\u5e73\u751f\u8c6a\u6c14\u970e\u65f6\u95f4\u6d88\u5931\u5f97\u65e0\u5f71\u65e0\u8e2a\uff0c\u518d\u53d7\u8840\u5200\u50e7\u5927\u52a0\u6298\u8fb1\u4e4b\u540e\u3002\u6570\u5341\u5e74\u6765\u538b\u5236\u5728\u5fc3\u5e95\u7684\u79cd\u79cd\u5351\u9119\u9f8c\u9f8a\u5ff5\u5934\uff0c\u7a81\u7136\u95f4\u90fd\u5192\u4e86\u51fa\u6765\uff0c\u51e0\u4e2a\u65f6\u8fb0\u4e4b\u95f4\uff0c\u7adf\u5982\u53d8\u4e86\u4e00\u4e2a\u4eba\u4e00\u822c\u3002 \u3000\u3000\u72c4\u4e91\u9053\uff1a\u201c\u4f60\u8bf4\u6211\u2026\u2026\u8bf4\u6211\u2026\u2026\u5df2\u5c06\u4ed6\u8e22\u6b7b\u4e86\uff1f\u201d \u3000\u3000\u82b1\u94c1\u5e72\u9053\uff1a\u201c\u786e\u7136\u65e0\u7591\u3002\u5c0f\u5e08\u7236\u82e5\u662f\u4e0d\u4fe1\uff0c\u4e0d\u59a8\u5148\u7528\u8840\u5200\u780d\u4e86\u4ed6\u53cc\u811a\uff0c\u518d\u5c06\u4ed6\u63d0\u8d77\u6765\u5bdf\u770b\uff0c\u9632\u4ed6\u6b7b\u7070\u590d\u71c3\uff0c\u4ee5\u7b56\u4e07\u5168\u3002\u201d \u3000\u3000\u8fd9\u65f6\u4ed6\u6240\u60f3\u7684\u6bcf\u4e00\u6761\u8ba1\u7b56\uff0c\u90fd\u6df1\u542b\u9634\u72e0\u6bd2\u8fa3\u4e4b\u610f\u3002 \u3000\u3000\u72c4\u4e91\u5411\u6c34\u7b19\u671b\u4e86\u4e00\u773c\u3002\u6c34\u7b19\u53ea\u9053\u4ed6\u8981\u593a\u81ea\u5df1\u624b\u4e2d\u8840\u5200\uff0c\u5413\u5f97\u9000\u4e86\u4e00\u6b65\u3002\u72c4\u4e91\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u4f60\u4e0d\u7528\u6015\uff0c\u6211\u4e0d\u4f1a\u5bb3\u4f60\u3002\u521a\u624d\u4f60\u6ca1\u4e00\u5200\u5c06\u6211\u8fde\u540c\u8001\u548c\u5c1a\u780d\u6b7b\uff0c\u591a\u8c22\u4f60\u5566\u3002\u201d\u6c34\u7b19\u54fc\u4e86\u4e00\u58f0\uff0c\u5e76\u4e0d\u7b54\u8bdd\u3002 \u3000\u3000\u82b1\u94c1\u5e72\u9053\uff1a\u201c\u6c34\u4f84\u5973\uff0c\u8fd9\u5c31\u662f\u4f60\u7684\u4e0d\u662f\u4e86\u3002\u5c0f\u5e08\u7236\u8bda\u5fc3\u5411\u4f60\u9053\u8c22\uff0c\u4f60\u8be5\u56de\u8c22\u4ed6\u624d\u662f\u3002\u521a\u624d\u8001\u6076\u50e7\u4e00\u5200\u780d\u5411\u4f60\u5934\u9888\uff0c\u82e5\u4e0d\u662f\u5c0f\u5e08\u7236\u601c\u9999\u60dc\u7389\uff0c\u76f8\u6551\u4e8e\u4f60\uff0c\u4f60\u8fd8\u6709\u547d\u5728\u4e48\uff1f\u201d \u3000\u3000\u6c34\u7b19\u548c\u72c4\u4e91\u542c\u5230\u4ed6\u8bf4\u201c\u601c\u9999\u60dc\u7389\u201d\u56db\u5b57\uff0c\u90fd\u5411\u4ed6\u77aa\u4e86\u4e00\u773c\u3002\u6c34\u7b19\u867d\u662f\u4e2a\u7f8e\u8c8c\u5c11\u5973\uff0c\u4f46\u72c4\u4e91\u6551\u5979\u4e4b\u65f6\uff0c\u53ea\u51fa\u4e8e\u201c\u4e0d\u53ef\u591a\u6740\u597d\u4eba\u201d\u7684\u4e00\u5ff5\uff0c\u82b1\u94c1\u5e72\u8fd9\u4e48\u4e00\u8bf4\uff0c\u5374\u663e\u5f97\u4ed6\u5f53\u65f6\u5176\u5b9e\u662f\u5b58\u5fc3\u4e0d\u826f\u3002\u6c34\u7b19\u539f\u5bf9\u72c4\u4e91\u5341\u5206\u7591\u5fcc\uff0c\u82b1\u94c1\u5e72\u8fd9\u51e0\u53e5\u8bdd\u66f4\u589e\u5979\u538c\u618e\u4e4b\u5fc3\uff0c\u4e00\u65f6\u4e5f\u5206\u8fa8\u4e0d\u51fa\u5230\u5e95\u662f\u618e\u6076\u82b1\u94c1\u5e72\u591a\u4e9b\uff0c\u8fd8\u662f\u618e\u6076\u72c4\u4e91\u591a\u4e9b\uff0c\u603b\u89c9\u8fd9\u4e8c\u4eba\u90fd\u662f\u5978\u6076\u4e0d\u582a\uff0c\u4e00\u77a5\u773c\u89c1\u5230\u7236\u4eb2\u7684\u5c38\u8eab\uff0c\u4e0d\u7531\u5f97\u60b2\u4e0d\u81ea\u80dc\uff0c\u5954\u8fc7\u53bb\u4f0f\u5728\u5c38\u4e0a\uff0c\u5927\u54ed\u8d77\u6765\u3002 \u3000\u3000\u82b1\u94c1\u5e72\u7b11\u9053\uff1a\u201c\u5c0f\u5e08\u7236\uff0c\u8bf7\u95ee\u4f60\u6cd5\u540d\u5982\u4f55\u79f0\u547c\uff1f\u201d\u72c4\u4e91\u9053\uff1a\u201c\u6211\u4e0d\u662f\u548c\u5c1a\uff0c\u522b\u53eb\u6211\u5e08\u7236\u4e0d\u5e08\u7236\u7684\u3002\u6211\u8eab\u7a7f\u50e7\u888d\uff0c\u662f\u4e3a\u4e86\u907f\u96be\u6539\u88c5\uff0c\u8feb\u4e0d\u5f97\u5df2\u3002\u201d\u82b1\u94c1\u5e72\u559c\u9053\uff1a\u201c\u90a3\u5999\u6781\u4e86\uff0c\u539f\u6765\u5c0f\u5e08\u7236\u2026\u2026\u4e0d\uff0c\u4e0d\uff01\u8be5\u6b7b\uff0c\u8be5\u6b7b\uff01\u8bf7\u95ee\u5927\u4fa0\u5c0a\u59d3\u5927\u540d\uff1f\u201d \u3000\u3000\u6c34\u7b19\u867d\u5728\u75db\u54ed\uff0c\u4f46\u4e24\u4eba\u5bf9\u7b54\u7684\u8a00\u8bed\u4e5f\u6a21\u6a21\u7cca\u7cca\u7684\u542c\u5728\u8033\u91cc\uff0c\u542c\u72c4\u4e91\u8bf4\u4e0d\u662f\u548c\u5c1a\uff0c\u5fc3\u4e0b\u5c06\u4fe1\u5c06\u7591\u3002\u53ea\u542c\u72c4\u4e91\u9053\uff1a\u201c\u6211\u59d3\u72c4\uff0c\u65e0\u540d\u5c0f\u5352\uff0c\u4e00\u4e2a\u6b7b\u91cc\u9003\u751f\u7684\u5e9f\u4eba\uff0c\u53c8\u662f\u751a\u4e48\u5927\u4fa0\u4e86\uff1f\u201d \u3000\u3000\u82b1\u94c1\u5e72\u7b11\u9053\uff1a\u201c\u5999\u6781\uff0c\u5999\u6781\uff01\u72c4\u5927\u4fa0\u5982\u6b64\u795e\u52c7\uff0c\u548c\u6211\u90a3\u6c34\u4f84\u5973\u90ce\u624d\u5973\u8c8c\uff0c\u6b63\u662f\u4e00\u5bf9\u513f\uff0c\u6211\u8fd9\u4e2a\u73b0\u6210\u5a92\u4eba\uff0c\u662f\u8d70\u4e0d\u4e86\u7684\u5566\u3002\u5999\u6781\uff0c\u5999\u6781\uff01\u539f\u6765\u72c4\u5927\u4fa0\u672c\u5c31\u4e0d\u662f\u51fa\u5bb6\u4eba\uff0c\u53ea\u987b\u7b49\u5934\u53d1\u4e00\u957f\uff0c\u6362\u4e00\u5957\u8863\u886b\uff0c\u90a3\u5c31\u751a\u4e48\u7834\u7efd\u4e5f\u77a7\u4e0d\u51fa\uff0c\u538b\u6839\u513f\u5c31\u4e0d\u7528\u7ba1\u8fd8\u4fd7\u8fd9\u4e00\u5957\u5566\u3002\u201d\u4ed6\u8ba4\u5b9a\u72c4\u4e91\u662f\u8840\u5200\u95e8\u7684\u548c\u5c1a\uff0c\u53ea\u56e0\u8d2a\u56fe\u6c34\u7b19\u7684\u7f8e\u8272\uff0c\u6545\u610f\u4e0d\u8ba4\u3002 \u3000\u3000\u72c4\u4e91\u6447\u4e86\u6447\u5934\uff0c\u9eef\u7136\u9053\uff1a\u201c\u4f60\u53e3\u4e2d\u5e72\u51c0\u4e9b\uff0c\u522b\u5c3d\u8bf4\u810f\u8bdd\u3002 \u3000\u3000\u54b1\u4eec\u82e5\u80fd\u51fa\u5f97\u6b64\u8c37\uff0c\u6211\u662f\u6c38\u8fdc\u4e0d\u89c1\u4f60\u9762\uff0c\u4e5f\u6c38\u8fdc\u4e0d\u89c1\u6c34\u59d1\u5a18\u4e4b\u9762\u4e86\u3002\u201d \u3000\u3000\u82b1\u94c1\u5e72\u4e00\u6014\uff0c\u4e00\u65f6\u4e0d\u660e\u767d\u4ed6\u7528\u610f\uff0c\u4f46\u968f\u5373\u7701\u609f\uff0c\u7b11\u9053\uff1a\u201c\u554a\uff0c\u6211\u61c2\u4e86\uff0c\u6211\u61c2\u4e86\uff01\u201d\u72c4\u4e91\u77aa\u4e86\u4ed6\u4e00\u773c\uff0c\u9053\uff1a\u201c\u4f60\u61c2\u4e86\u751a\u4e48\uff1f\u201d \u3000\u3000\u82b1\u94c1\u5e72\u4f4e\u58f0\u9053\uff1a\u201c\u72c4\u5927\u4fa0\u5bfa\u9662\u4e4b\u4e2d\uff0c\u53e6\u6709\u77e5\u5fc3\u89e3\u610f\u7684\u7f8e\u4eba\u513f\uff0c\u8fd9\u6c34\u59d1\u5a18\u662f\u4e0d\u80fd\u5e26\u53bb\u505a\u957f\u4e45\u592b\u59bb\u7684\u3002\u563f\u563f\uff0c\u90a3\u4e48\u505a\u51e0\u5929\u9732\u6c34\u592b\u59bb\uff0c\u53c8\u6709\u4f55\u59a8\uff01\u201d \u3000\u3000\u8fd9\u51e0\u53e5\u8bdd\u4f20\u5165\u6c34\u7b19\u8033\u4e2d\uff0c\u5979\u6124\u6012\u518d\u96be\u6291\u5236\uff0c\u5954\u8fc7\u53bb\u62cd\u62cd\u62cd\u62cd\u7684\u8fde\u6253\u4e86\u4ed6\u56db\u4e0b\u8033\u5149\u3002 \u3000\u3000\u72c4\u4e91\u832b\u7136\u77a7\u7740\uff0c\u65e0\u52a8\u4e8e\u4e2d\uff0c\u53ea\u89c9\u8fd9\u4e00\u5207\u8ddf\u4ed6\u6beb\u4e0d\u76f8\u5e72\u3002 \u3000\u3000\u8fc7\u4e86\u826f\u4e45\uff0c\u8840\u5200\u8001\u7956\u4ecd\u662f\u4e00\u52a8\u4e0d\u52a8\u3002 \u3000\u3000\u6c34\u7b19\u51e0\u6b21\u60f3\u63d0\u5200\u8fc7\u53bb\u780d\u4e86\u4ed6\u53cc\u817f\uff0c\u5374\u603b\u662f\u4e0d\u6562\u3002\u77a7\u7740\u7236\u4eb2\u4e00\u52a8\u4e0d\u52a8\u7684\u8eba\u5728\u96ea\u4e0a\uff0c\u518d\u4e5f\u4e0d\u80fd\u949f\u7231\u601c\u60dc\u81ea\u5df1\u4e86\uff0c\u5979\u8f7b\u8f7b\u53eb\u9053\uff1a\u201c\u7239\u7239\uff01\u7239\u7239\uff01\u201d\u6c34\u5cb1\u81ea\u7136\u518d\u4e5f\u4e0d\u80fd\u7b54\u5e94\u5979\u4e86\u3002\u6c34\u7b19\u6cea\u6c34\u4e00\u6ef4\u6ef4\u7684\u843d\u5165\u96ea\u4e2d\uff0c\u5c06\u96ea\u878d\u4e86\uff0c\u53c8\u6162\u6162\u7684\u548c\u96ea\u6c34\u4e00\u8d77\u7ed3\u6210\u4e86\u51b0\u3002 \u3000\u3000\u82b1\u94c1\u5e72\u7a74\u9053\u672a\u89e3\uff0c\u6709\u4e00\u642d\u6ca1\u4e00\u642d\u7684\u5411\u72c4\u4e91\u5949\u627f\u8ba8\u597d\uff0c\u8d8a\u8bf4\u8d8a\u662f\u8089\u9ebb\u3002\u72c4\u4e91\u4e0d\u53bb\u7406\u4ed6\uff0c\u81ea\u884c\u8eba\u5728\u96ea\u5730\u91cc\u95ed\u76ee\u517b\u606f\u3002 \u3000\u3000\u72c4\u4e91\u521d\u901a\u4efb\u7763\u4e8c\u8109\uff0c\u53ea\u89c9\u7cbe\u795e\u5927\u632f\uff0c\u4f53\u5185\u4e00\u80a1\u6696\u6d41\uff0c\u81ea\u524d\u80f8\u800c\u81f3\u540e\u80cc\u3001\u53c8\u81ea\u540e\u80cc\u800c\u81f3\u524d\u80f8\uff0c\u5468\u800c\u590d\u59cb\u7684\u4e0d\u505c\u6d41\u8f6c\u3002\u6bcf\u6d41\u8f6c\u4e00\u5468\uff0c\u4fbf\u89c9\u5904\u5904\u90fd\u751f\u4e86\u4e9b\u529b\u6c14\u51fa\u6765\uff0c\u867d\u7136\u65ad\u817f\u4ee5\u53ca\u7ed9\u6c34\u7b19\u6bb4\u6253\u7684\u5404\u5904\u4ecd\u662f\u6781\u4e3a\u75bc\u75db\uff0c\u4f46\u5185\u529b\u65e2\u589e\uff0c\u8fd9\u4e9b\u75db\u695a\u4fbf\u89c9\u751a\u6613\u5fcd\u8010\u3002\u4ed6\u751f\u6015\u8fd9\u5947\u5999\u4e4b\u6781\u7684\u60c5\u666f\u7a81\u7136\u800c\u6765\uff0c\u53c8\u4f1a\u7a81\u7136\u800c\u53bb\uff0c\u5f53\u4e0b\u8eba\u7740\u4e0d\u6562\u52a8\u5f39\uff0c\u7531\u5f97\u5185\u606f\u5728\u4efb\u7763\u4e8c\u8109\u4e2d\u5ddd\u884c\u4e0d\u6b47\u3002 \u3000\u3000\u6c34\u7b19\u7ad9\u8d77\u8eab\u6765\uff0c\u4e00\u6b65\u6b65\u8d70\u5230\u8840\u5200\u50e7\u8eab\u65c1\uff0c\u53ea\u89c1\u4ed6\u4eec\u662f\u6beb\u4e0d\u52a8\u5f39\uff0c\u5f53\u4e0b\u5927\u7740\u80c6\u5b50\uff0c\u6325\u5200\u5f80\u4ed6\u5de6\u811a\u4e0a\u780d\u53bb\uff0c\u55e4\u7684\u4e00\u58f0\u8f7b\u54cd\uff0c\u767b\u65f6\u780d\u4e0b\u4e00\u53ea\u811a\u6765\uff0c\u8bf4\u4e5f\u5947\u602a\uff0c\u5c45\u7136\u5e76\u4e0d\u6d41\u8840\u3002\u6c34\u7b19\u5b9a\u775b\u4e00\u770b\uff0c\u53ea\u89c1\u8840\u6db2\u51dd\u7ed3\u6210\u51b0\uff0c\u539f\u6765\u8fd9\u7a77\u51f6\u6781\u6076\u7684\u8840\u5200\u8001\u7956\u679c\u7136\u65e9\u5df2\u6b7b\u53bb\u591a\u65f6\u3002 \u3000\u3000\u6c34\u7b19\u53c8\u662f\u6b22\u559c\uff0c\u53c8\u662f\u60b2\u4f24\uff0c\u63d0\u5200\u5728\u8840\u5200\u50e7\u817f\u4e0a\u4e00\u9635\u4e71\u780d\uff0c\u5fc3\u60f3\uff1a\u201c\u7239\u7239\u6b7b\u4e86\uff0c\u6211\u4e5f\u4e0d\u60f3\u6d3b\u5566\uff01\u8fd9\u5c0f\u6076\u50e7\u4e0d\u77e5\u4f1a\u5982\u4f55\u6765\u6298\u78e8\u6211\uff1f\u4ed6\u53ea\u8981\u5bf9\u6211\u7a0d\u6709\u6b79\u610f\uff0c\u6211\u5373\u523b\u6a2a\u5200\u81ea\u520e\u3002\u201d \u3000\u3000\u82b1\u94c1\u5e72\u4e00\u5207\u77a7\u5728\u773c\u91cc\uff0c\u5fc3\u4e0b\u6697\u559c\uff1a\u201c\u8fd9\u5c0f\u6076\u50e7\u867d\u7136\u51f6\u6076\uff0c\u8fd9\u65f6\u5c1a\u65e0\u6740\u6211\u4e4b\u610f\uff0c\u5f85\u5f97\u6211\u7a74\u9053\u4e00\u89e3\uff0c\u4e00\u4f38\u624b\u4fbf\u53d6\u4e86\u4ed6\u6027\u547d\u3002\u201d \u3000\u3000\u53c8\u8fc7\u4e86\u5927\u534a\u4e2a\u65f6\u8fb0\uff0c\u72c4\u4e91\u89c9\u5f97\u5185\u606f\u6d41\u8f6c\u59cb\u7ec8\u4e0d\u505c\uff0c\u4fbf\u4f9d\u7167\u4e01\u5178\u6240\u6388\u201c\u795e\u7167\u529f\u201d\u4e0a\u5185\u529f\u7684\u6cd5\u95e8\u8fd0\u6c14\u8c03\u606f\uff0c\u672c\u6765\u6349\u6478\u4e0d\u5230\u3001\u9a71\u4f7f\u4e0d\u52a8\u7684\u5185\u606f\uff0c\u8fd9\u65f6\u7adf\u7136\u968f\u5fc3\u6240\u6b32\uff0c\u4fbf\u5982\u6446\u5934\u4e3e\u624b\u4e00\u822c\u7684\u4f9d\u610f\u800c\u884c\u3002\u4ed6\u53c8\u662f\u5947\u602a\uff0c\u53c8\u662f\u6b22\u559c\u3002 \u3000\u3000\u8c03\u606f\u534a\u664c\uff0c\u5750\u8d77\u8eab\u6765\uff0c\u53d6\u8fc7\u4e00\u6839\u6811\u679d\u6491\u5728\u53f3\u814b\u4e4b\u4e0b\uff0c\u8d70\u5230\u8840\u5200\u50e7\u8eab\u8fb9\u3002\u53ea\u89c1\u4ed6\u5c38\u8eab\u63d2\u5728\u96ea\u91cc\uff0c\u4e24\u6761\u817f\u7ed9\u6c34\u7b19\u780d\u5f97\u8840\u8089\u6a21\u7cca\uff0c\u786e\u7136\u65e0\u7591\u7684\u5df2\u7ecf\u6b7b\u4e86\uff0c\u5fc3\u60f3\u6b64\u4eba\u4f5c\u6076\u591a\u7aef\uff0c\u539f\u662f\u5e94\u6709\u6b64\u62a5\uff0c\u4f46\u4ed6\u5bf9\u81ea\u5df1\u5374\u5b9e\u5728\u662f\u9887\u6709\u6069\u5fb7\uff0c\u5fc3\u4e2d\u4e0d\u7981\u6709\u4e9b\u96be\u8fc7\uff0c\u4e8e\u662f\u5c06\u4ed6\u5c38\u8eab\u63d0\u4e86\u51fa\u6765\uff0c\u7aef\u7aef\u6b63\u6b63\u7684\u653e\u4e86\uff0c\u6367\u4e9b\u767d\u96ea\u5806\u5728\u5c38\u8eab\u4e4b\u4e0a\uff0c\u867d\u7136\u8349\u8349\uff0c\u5374\u4e5f\u7b97\u662f\u7ed9\u4ed6\u5b89\u846c\u3002\u81f3\u4e8e\u4ed6\u4e3a\u751a\u4e48\u7a81\u7136\u95f4\u7adf\u4f1a\u6b7b\u4e86\uff0c\u72c4\u4e91\u4ecd\u662f\u5927\u60d1\u4e0d\u89e3\uff0c\u6b64\u4eba\u529f\u529b\u795e\u901a\uff0c\u81ea\u5df1\u4e07\u4e07\u4e0d\u80fd\u4e00\u811a\u4fbf\u8e22\u6b7b\u4e86\u4ed6\u3002 \u3000\u3000\u6c34\u7b19\u89c1\u5230\u72c4\u4e91\u7684\u4e3e\u52a8\uff0c\u8d77\u4e86\u6a21\u4eff\u7684\u5ff5\u5934\uff0c\u53c8\u89c1\u51e0\u5934\u5140\u9e70\u4e0d\u4f4f\u5728\u7a7a\u4e2d\u76d8\u65cb\uff0c\u4f3c\u8981\u6251\u4e0b\u6765\u5544\u98df\u7236\u4eb2\u7684\u5c38\u8eab\uff0c\u5fd9\u5c06\u7236\u4eb2\u5982\u6cd5\u5b89\u846c\u3002\u5979\u672c\u60f3\u518d\u5b89\u846c\u5218\u4e58\u98ce\u548c\u9646\u5929\u6292\u4e8c\u4eba\uff0c\u4f46\u4e00\u4e2a\u6b7b\u5728\u60ac\u5d16\u7edd\u9876\uff0c\u4e00\u4e2a\u6b7b\u4e8e\u96ea\u8c37\u6df1\u5904\uff0c\u81ea\u5fd6\u6ca1\u672c\u4e8b\u5bfb\u5f97\uff0c\u53ea\u5f97\u7f62\u4e86\u3002 \u3000\u3000\u82b1\u94c1\u5e72\u9053\uff1a\u201c\u5c0f\u5e08\u7236\uff0c\u54b1\u4e09\u4eba\u7d2f\u4e86\u8fd9\u4e45\uff0c\u5927\u5bb6\u53ef\u997f\u5f97\u5f88\u4e86\u3002 \u3000\u3000\u6211\u5148\u524d\u89c1\u5230\u4e0a\u8fb9\u70e4\u4e86\u9a6c\u8089\uff0c\u52b3\u4f60\u7684\u9a7e\u53bb\u53d6\u4e86\u4e0b\u6765\uff0c\u5927\u4f19\u513f\u5148\u5403\u4e2a\u9971\uff0c\u7136\u540e\u4ece\u957f\u8ba1\u8bae\uff0c\u600e\u751f\u51fa\u8c37\u3002\u201d\u72c4\u4e91\u5fc3\u9119\u4ed6\u7684\u4e3a\u4eba\uff0c\u5e76\u4e0d\u7406\u776c\u3002\u82b1\u94c1\u5e72\u6c42\u4e4b\u4e0d\u5df2\u3002\u6c34\u7b19\u5ffd\u9053\uff1a\u201c\u662f\u6211\u9a6c\u513f\u7684\u8089\uff0c\u4e0d\u80fd\u7ed9\u8fd9\u65e0\u803b\u4e4b\u5f92\u5403\u3002\u201d\u72c4\u4e91\u70b9\u70b9\u5934\uff0c\u5411\u82b1\u94c1\u5e72\u77aa\u4e86\u4e00\u773c\u3002 \u3000\u3000\u82b1\u94c1\u5e72\u9053\uff1a\u201c\u5c0f\u5e08\u7236\u2026\u2026\u201d\u72c4\u4e91\u9053\uff1a\u201c\u6211\u8bf4\u8fc7\u6211\u53c8\u4e0d\u662f\u548c\u5c1a\uff0c\u522b\u518d\u4e71\u53eb\u3002\u201d\u82b1\u94c1\u5e72\u9053\uff1a\u201c\u662f\uff0c\u662f\uff0c\u662f\uff0c\u72c4\u5927\u4fa0\u3002\u72c4\u5927\u4fa0\u8fd9\u6b21\u4e00\u817f\u8e22\u6b7b\u8840\u5200\u6076\u50e7\uff0c\u5b9a\u7136\u540d\u626c\u5929\u4e0b\u3002\u6211\u51fa\u5f97\u8c37\u53bb\uff0c\u7b2c\u4e00\u4ef6\u4e8b\u4fbf\u8981\u4e3a\u72c4\u5927\u4fa0\u5ba3\u626c\u4eca\u65e5\u4e4b\u4e8b\u3002\u72c4\u5927\u4fa0\u594b\u4e0d\u987e\u8eab\u7684\u6551\u63f4\u6c34\u59d1\u5a18\uff0c\u8e22\u6b7b\u8840\u5200\u50e7\uff0c\u90a3\u5b9e\u662f\u6b66\u6797\u4e2d\u5934\u7b49\u7684\u5927\u4e8b\u3002\u201d\u72c4\u4e91\u9053\uff1a\u201c\u6211\u662f\u4e2a\u58f0\u540d\u626b\u5730\u7684\u56da\u72af\uff0c\u6709\u8c01\u76f8\u4fe1\u4f60\u7684\u9b3c\u8bdd\uff1f\u4f60\u4e58\u65e9\u95ed\u4e86\u5634\u7684\u597d\u3002\u201d\u82b1\u94c1\u5e72\u9053\uff1a\u201c\u51ed\u7740\u82b1\u67d0\u4eba\u5728\u6c5f\u6e56\u4e0a\u8fd9\u70b9\u5c0f\u5c0f\u58f0\u540d\uff0c\u8bf4\u51fa\u8bdd\u6765\uff0c\u65c1\u4eba\u662f\u975e\u76f8\u4fe1\u4e0d\u53ef\u7684\u3002\u72c4\u5927\u4fa0\uff0c\u8bf7\u4f60\u4e0a\u53bb\u62ff\u9a6c\u8089\uff0c\u5206\u4e00\u5757\u7ed9\u6211\u3002\u201d \u3000\u3000\u72c4\u4e91\u751a\u662f\u538c\u70e6\uff0c\u559d\u9053\uff1a\u201c\u5e72\u4e48\u8981\u62ff\u9a6c\u8089\u6765\u7ed9\u4f60\u5403\uff1f\u5c06\u6765\u4f60\u5c3d\u53ef\u8bf4\u5f97\u6211\u72c4\u4e91\u5206\u6587\u4e0d\u503c\u3002\u6211\u662f\u751a\u4e48\u4e1c\u897f\uff1f\u8fd8\u914d\u7ed9\u8c01\u6302\u9f7f\u5417\uff1f\u201d \u3000\u3000\u60f3\u8d77\u8fd9\u51e0\u5e74\u6765\u8eab\u53d7\u7684\u79cd\u79cd\u51a4\u6789\u59d4\u5c48\u3001\u6298\u8fb1\u82e6\u695a\uff0c\u4e0d\u7531\u5f97\u6ee1\u8154\u6028\u6124\uff0c\u96be\u4ee5\u6291\u5236\u3002 \u3000\u3000\u82b1\u94c1\u5e72\u5176\u5b9e\u5e76\u975e\u771f\u7684\u60f3\u5403\u9a6c\u8089\uff0c\u4ed6\u8179\u4e2d\u867d\u997f\uff0c\u4f46\u4e00\u65e5\u534a\u65e5\u7684\u9965\u997f\uff0c\u53c8\u7b97\u5f97\u4e86\u751a\u4e48\uff1f\u4ed6\u53ea\u6015\u8fd9\u5c0f\u6076\u50e7\u7a81\u7136\u6027\u8d77\uff0c\u5c06\u4ed6\u6740\u4e86\uff0c\u4e5e\u8ba8\u9a6c\u8089\u4e43\u662f\u4ee5\u8fdb\u4e3a\u9000\u3001\u4ee5\u653b\u4e3a\u5b88\u4e4b\u7b56\uff0c\u6599\u60f3\u4ed6\u65e2\u4e0d\u80af\u53bb\u53d6\u9a6c\u8089\uff0c\u5fc3\u4e2d\u52bf\u5fc5\u7565\u6709\u6b49\u4ec4\u4e4b\u610f\uff0c\u90a3\u4e48\u6740\u4eba\u7684\u5ff5\u5934\u81ea\u7136\u800c\u7136\u7684\u5c31\u6d88\u4e86\u3002 \u3000\u3000\u72c4\u4e91\u89c1\u5929\u8272\u5c06\u9ed1\uff0c\u897f\u5317\u98ce\u547c\u547c\u547c\u7684\u5439\u8fdb\u96ea\u8c37\u6765\uff0c\u5411\u6c34\u7b19\u9053\uff1a\u201c\u6c34\u59d1\u5a18\uff0c\u4f60\u5230\u77f3\u6d1e\u4e2d\u6b47\u6b47\u53bb\uff01\u201d\u6c34\u7b19\u5927\u5403\u4e00\u60ca\uff0c\u53ea\u9053\u4ed6\u53c8\u8d77\u4e0d\u8f68\u4e4b\u5fc3\uff0c\u9000\u4e86\u4e24\u6b65\uff0c\u624b\u6267\u8840\u5200\uff0c\u6a2a\u5728\u8eab\u524d\uff0c\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u6076\u50e7\uff0c\u53ea\u8981\u8d70\u8fd1\u6211\u4e00\u6b65\uff0c\u59d1\u5a18\u7acb\u5373\u6325\u5200\u81ea\u5c3d\u3002\u201d\u72c4\u4e91\u4e00\u6014\uff0c\u8bf4\u9053\uff1a\u201c\u59d1\u5a18\u4e0d\u53ef\u8bef\u4f1a\uff0c\u72c4\u67d0\u5c82\u6709\u6b79\u610f\uff1f\u201d\u6c34\u7b19\u9a82\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u548c\u5c1a\u4eba\u9762\u517d\u5fc3\uff0c\u7b11\u91cc\u85cf\u5200\uff0c\u6bd4\u90a3\u8001\u548c\u5c1a\u8fd8\u8981\u5978\u6076\uff0c\u6211\u624d\u4e0d\u4e0a\u4f60\u7684\u5f53\u5462\u3002\u201d \u3000\u3000\u72c4\u4e91\u4e0d\u613f\u591a\u8fa9\uff0c\u5fc3\u60f3\uff1a\u201c\u660e\u65e5\u5929\u4e00\u4eae\u6211\u5c31\u4f1a\u89c5\u8def\u51fa\u8c37\uff0c\u751a\u4e48\u6c34\u59d1\u5a18\uff0c\u82b1\u5927\u4fa0\uff0c\u6211\u6c38\u751f\u6c38\u4e16\u4e5f\u4e0d\u613f\u518d\u89c1\u4f60\u4eec\u7684\u9762\u3002\u201d\u5f53\u4e0b\u8d70\u5f97\u8fdc\u8fdc\u5730\uff0c\u627e\u5230\u4e00\u5757\u5927\u5ca9\u77f3\uff0c\u62e8\u53bb\u79ef\u96ea\uff0c\u5f84\u81ea\u7761\u4e86\u3002 \u3000\u3000\u6c34\u7b19\u5fc3\u60f3\u4f60\u8d70\u5f97\u8d8a\u8fdc\uff0c\u8d8a\u662f\u9634\u9669\u5978\u6076\uff0c\u591a\u534a\u662f\u534a\u591c\u91cc\u524d\u6765\u4fb5\u72af\u3002\u5979\u4e0d\u6562\u8d70\u8fdb\u77f3\u6d1e\u4e4b\u5185\uff0c\u53ea\u6015\u5c0f\u6076\u50e7\u6765\u65f6\u6ca1\u4e86\u9000\u8def\uff0c\u5fc3\u60ca\u80c6\u6218\u7684\u659c\u501a\u5ca9\u8fb9\uff0c\u53f3\u624b\u7d27\u7d27\u6293\u4f4f\u8840\u5200\uff0c\u773c\u76ae\u8d8a\u6765\u8d8a\u6c89\u91cd\uff0c\u4e0d\u4f4f\u63d0\u9192\u81ea\u5df1\uff1a\u201c\u5343\u4e07\u4e0d\u80fd\u7761\u7740\uff0c\u5343\u4e07\u4e0d\u80fd\u7761\u7740\uff0c\u8fd9\u5c0f\u6076\u50e7\u574f\u5f97\u7d27\u3002\u201d \u3000\u3000\u4f46\u8fd9\u51e0\u65e5\u5fc3\u529b\u4ea4\u7601\uff0c\u867d\u8bf4\u5343\u4e07\u4e0d\u80fd\u7761\u7740\uff0c\u65f6\u523b\u4e00\u957f\uff0c\u6726\u6726\u80e7\u80e7\u7684\u7ec8\u4e8e\u7761\u7740\u4e86\u3002 \u3000\u3000\u5979\u8fd9\u4e00\u89c9\u76f4\u7761\u5230\u6b21\u65e5\u6e05\u6668\uff0c\u53ea\u89c9\u65e5\u5149\u523a\u773c\uff0c\u4e00\u60ca\u800c\u9192\uff0c\u8df3\u8d77\u8eab\u6765\uff0c\u53d1\u89c9\u624b\u4e2d\u6ca1\u4e86\u8840\u5200\uff0c\u8fd9\u4e00\u4e0b\u66f4\u662f\u60ca\u60f6\uff0c\u4e00\u77a5\u773c\u95f4\uff0c\u5374\u89c1\u90a3\u8840\u5200\u597d\u7aef\u7aef\u7684\u4fbf\u6389\u5728\u8db3\u8fb9\u3002 \u3000\u3000\u6c34\u7b19\u5fd9\u62fe\u8d77\u8840\u5200\uff0c\u62ac\u8d77\u5934\u6765\uff0c\u53ea\u89c1\u72c4\u4e91\u7684\u80cc\u5f71\u6b63\u5411\u8fdc\u5904\u79fb\u52a8\uff0c\u624b\u4e2d\u6491\u7740\u4e00\u6839\u6811\u679d\uff0c\u4e00\u8ddb\u4e00\u62d0\u7684\u8d70\u5411\u8c37\u5916\u3002\u6c34\u7b19\u5927\u559c\uff0c\u5fc3\u60f3\u8fd9\u4e2a\u6076\u50e7\u4f3c\u6709\u53bb\u610f\uff0c\u90a3\u771f\u662f\u8c22\u5929\u8c22\u5730\u3002 \u3000\u3000\u72c4\u4e91\u786e\u662f\u60f3\u89c5\u8def\u51fa\u8c37\uff0c\u4f46\u5728\u4e1c\u5317\u89d2\u548c\u6b63\u4e1c\u65b9\u8fde\u5bfb\u51e0\u5904\uff0c\u90fd\u6ca1\u5c71\u5f84\uff0c\u897f\u3001\u5317\u3001\u5357\u4e09\u8fb9\u5c71\u5cf0\u58c1\u7acb\uff0c\u4e00\u671b\u4fbf\u77e5\u65e0\u8def\u53ef\u901a\uff0c\u90a3\u662f\u8bd5\u4e5f\u4e0d\u7528\u8bd5\u7684\u3002\u4e1c\u5357\u65b9\u4f9d\u7a00\u80fd\u6709\u51fa\u8def\uff0c\u53ef\u662f\u79ef\u96ea\u6570\u5341\u4e08\uff0c\u4e0d\u5230\u5929\u6696\u96ea\u878d\uff0c\u4ee5\u4ed6\u4e00\u4e2a\u65ad\u4e86\u817f\u7684\u8ddb\u5b50\uff0c\u65e0\u8bba\u5982\u4f55\u8d70\u4e0d\u51fa\u53bb\u3002\u4ed6\u7d2f\u4e86\u534a\u65e5\uff0c\u5e9f\u7136\u800c\u8fd4\uff0c\u5446\u671b\u5934\u9876\u9ad8\u5cf0\uff0c\u751a\u662f\u6cae\u4e27\u3002 \u3000\u3000\u82b1\u94c1\u5e72\u9053\uff1a\u201c\u72c4\u5927\u4fa0\uff0c\u600e\u4e48\u6837\uff1f\u201d\u72c4\u4e91\u6447\u5934\u9053\uff1a\u201c\u6ca1\u8def\u51fa\u53bb\u3002\u201d \u3000\u3000\u82b1\u94c1\u5e72\u6697\u9053\uff1a\u201c\u4f60\u4e0d\u80fd\u51fa\u53bb\uff0c\u6211\u82b1\u94c1\u5e72\u5c82\u662f\u4f60\u5c0f\u6076\u50e7\u4e4b\u6bd4\uff1f\u5230\u5f97\u4e0b\u5348\uff0c\u6211\u7a74\u9053\u4e00\u89e3\uff0c\u4f60\u77a7\u8001\u5b50\u7684\u3002\u201d\u4f46\u4e1d\u6beb\u4e0d\u52a8\u58f0\u8272\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u7528\u62c5\u5fc3\uff0c\u5f85\u6211\u7a74\u9053\u89e3\u5f00\uff0c\u82b1\u67d0\u5b9a\u80fd\u643a\u5e26\u4e24\u4f4d\u8131\u9669\u51fa\u56f0\u3002\u201d \u3000\u3000\u6c34\u7b19\u89c1\u72c4\u4e91\u6ca1\u6765\u4fb5\u72af\u81ea\u5df1\uff0c\u60ca\u6050\u7a0d\u51cf\uff0c\u5374\u4e1d\u6beb\u6ca1\u6d88\u4e86\u6212\u5907\u4e4b\u5fc3\uff0c\u603b\u662f\u79bb\u5f97\u4ed6\u8fdc\u8fdc\u5730\uff0c\u4e00\u53e5\u8bdd\u4e5f\u4e0d\u8ddf\u4ed6\u8bf4\u3002\u72c4\u4e91\u867d\u4e0d\u6c42\u5979\u8c05\u89e3\uff0c\u4f46\u89c1\u4e86\u5979\u7684\u795e\u60c5\u4e3e\u52a8\uff0c\u5fc3\u4e0b\u5374\u4e5f\u4e0d\u7981\u607c\u6012\uff0c\u53ea\u76fc\u80fd\u53ca\u65e9\u79bb\u5f00\uff0c\u53ef\u662f\u5927\u96ea\u5c01\u5c71\uff0c\u4e0d\u77e5\u5982\u4f55\u65b9\u80fd\u51fa\u53bb\uff0c\u4e0d\u7531\u5f97\u5927\u4e3a\u53d1\u6101\u3002 \u3000\u3000\u5230\u5f97\u672a\u724c\u65f6\u5206\uff0c\u82b1\u94c1\u5e72\u7a81\u7136\u54c8\u54c8\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6c34\u4f84\u5973\uff0c\u4f60\u7684\u9a6c\u8089\u82b1\u4f2f\u4f2f\u8981\u501f\u5403\u51e0\u65a4\uff0c\u51fa\u8c37\u4e4b\u540e\uff0c\u4e00\u5e76\u5949\u8fd8\u3002\u201d\u4e00\u8dc3\u800c\u8d77\uff0c\u7ed5\u9053\u6500\u4e0a\u70e7\u70e4\u9a6c\u8089\u4e4b\u5904\uff0c\u62ff\u8d77\u4e00\u5757\u719f\u8089\uff0c\u4fbf\u5403\u4e86\u8d77\u6765\u3002\u539f\u6765\u4ed6\u7a74\u9053\u88ab\u5c01\u7684\u65f6\u523b\u5df2\u6ee1\uff0c\u7adf\u81ea\u89e3\u4e86\u3002 \u3000\u3000\u82b1\u94c1\u5e72\u7a74\u9053\u4e00\u89e3\uff0c\u795e\u6001\u7acb\u8f6c\u9a84\u6a2a\uff0c\u5fc3\u60f3\u8840\u5200\u50e7\u5df2\u6b7b\uff0c\u72c4\u4e91\u548c\u6c34\u7b19\u4fbf\u4e24\u4eba\u8054\u624b\uff0c\u4e5f\u4e07\u4e07\u4e0d\u662f\u81ea\u5df1\u7684\u5bf9\u624b\uff0c\u53ea\u662f\u8fd9\u96ea\u8c37\u4e2d\u591a\u803d\u65e0\u76ca\uff0c\u8fd8\u662f\u5c3d\u65e9\u89c5\u8def\u51fa\u53bb\u7684\u4e3a\u662f\uff0c\u627e\u5230\u4e86\u51fa\u8def\uff0c\u5374\u987b\u5f97\u5148\u5c06\u4e24\u4eba\u6740\u4e86\u706d\u53e3\uff0c\u81ea\u5df1\u6628\u65e5\u7684\u79cd\u79cd\u4e3e\u52a8\uff0c\u5c82\u80fd\u5bb9\u4ed6\u4e8c\u4eba\u6cc4\u6f0f\u51fa\u53bb\uff1f \u3000\u3000\u4ed6\u65bd\u5c55\u8f7b\u529f\uff0c\u5728\u96ea\u8c37\u5468\u56f4\u67e5\u5bdf\uff0c\u89c1\u8fd9\u6b21\u5927\u96ea\u5d29\u7adf\u662f\u5c06\u96ea\u8c37\u5c01\u5f97\u5bc6\u4e0d\u901a\u98ce\uff0c\u4ed6\u201c\u843d\u82b1\u6d41\u6c34\u201d\u56db\u4eba\u82e5\u4e0d\u662f\u5728\u79ef\u96ea\u5d29\u843d\u4e4b\u524d\u5148\u884c\u62a2\u8fdb\u8c37\u6765\uff0c\u4e5f\u5fc5\u5b9a\u88ab\u9694\u7edd\u5728\u5916\u3002\u8fd9\u65f6\u552f\u4e00\u51fa\u8c37\u7684\u901a\u9053\u4e0a\u79ef\u96ea\u6df1\u8fbe\u6570\u5341\u4e08\uff0c\u957f\u8fbe\u6570\u91cc\u3002\u5728\u96ea\u5e95\u7a7f\u884c\u6570\u4e08\u4e43\u81f3\u5341\u4f59\u4e08\uff0c\u90a3\u4e5f\u7f62\u4e86\uff0c\u5374\u5982\u4f55\u80fd\u7a7f\u884c\u6570\u91cc\u4e4b\u9065\uff1f\u4f55\u51b5\u4e00\u5230\u96ea\u5e95\uff0c\u65b9\u5411\u96be\u8fa9\uff0c\u975e\u6d3b\u6d3b\u95f7\u6b7b\u4e0d\u53ef\u3002\u8fd9\u65f6\u8fd8\u53ea\u5341\u4e00\u6708\u521d\uff0c\u7b49\u5230\u660e\u5e74\u521d\u590f\u96ea\u878d\uff0c\u8db3\u8db3\u8981\u6328\u4e0a\u534a\u5e74\u3002\u8c37\u4e2d\u904d\u5730\u662f\u96ea\uff0c\u8fd9\u4e94\u516d\u4e2a\u6708\u7684\u65e5\u5b50\uff0c\u5403\u751a\u4e48\u4e1c\u897f\u6d3b\u547d\uff1f \u3000\u3000\u82b1\u94c1\u5e72\u56de\u5230\u77f3\u6d1e\u5916\uff0c\u8138\u8272\u6781\u4e3a\u6c89\u91cd\uff0c\u5750\u4e86\u534a\u664c\uff0c\u4ece\u6000\u91cc\u53d6\u51fa\u9a6c\u8089\u4fbf\u5403\uff0c\u6162\u6162\u5480\u56bc\uff0c\u76f4\u5c06\u8fd9\u4e00\u5757\u9a6c\u8089\u5403\u5f97\u7cbe\u5149\uff0c\u624d\u4f4e\u58f0\u9053\uff1a\u201c\u5230\u660e\u5e74\u7aef\u5348\uff0c\u4fbf\u53ef\u51fa\u53bb\u4e86\u3002\u201d \u3000\u3000\u72c4\u4e91\u548c\u6c34\u7b19\u4e00\u4e2a\u5728\u5de6\uff0c\u4e00\u4e2a\u5728\u53f3\uff0c\u548c\u4ed6\u90fd\u662f\u76f8\u8ddd\u4e09\u4e08\u6765\u5730\uff0c\u4ed6\u8fd9\u53e5\u8bdd\u8bf4\u5f97\u867d\u8f7b\uff0c\u5728\u4e24\u4eba\u8033\u4e2d\u542c\u6765\uff0c\u4fbf\u5982\u662f\u8f70\u8f70\u96f7\u9707\u4e00\u822c\u3002\u4e24\u4eba\u4e0d\u7ea6\u800c\u540c\u7684\u73af\u89c6\u4e00\u5468\uff0c\u56db\u4e0b\u91cc\u5c3d\u662f\u7691\u7691\u767d\u96ea\uff0c\u8981\u627e\u4e9b\u6811\u76ae\u8349\u6839\u6765\u5403\u4e5f\u96be\uff0c\u5fc3\u4e2d\u90fd\u60f3\uff1a\u201c\u600e\u6328\u5f97\u5230\u660e\u5e74\u7aef\u5348\uff1f\u201d \u3000\u3000\u53ea\u542c\u5f97\u534a\u7a7a\u4e2d\u51e0\u58f0\u9e70\u5533\uff0c\u4e09\u4eba\u4e00\u9f50\u62ac\u8d77\u5934\u6765\uff0c\u671b\u7740\u534a\u7a7a\u4e2d\u98de\u821e\u6765\u53bb\u7684\u4e03\u516b\u5934\u5140\u9e70\uff0c\u5747\u60f3\uff1a\u201c\u9664\u975e\u50cf\u8fd9\u4e9b\u8001\u9e70\u90a3\u6837\uff0c\u624d\u80fd\u98de\u51fa\u8c37\u53bb\u3002\u201d \u3000\u3000\u6c34\u7b19\u8fd9\u5339\u767d\u9a6c\u867d\u751a\u80a5\u5927\uff0c\u4f46\u4e09\u4e2a\u4eba\u6bcf\u65e5\u90fd\u5403\uff0c\u4e0d\u5230\u4e00\u4e2a\u6708\uff0c\u4e5f\u7ec8\u4e8e\u5403\u5b8c\u4e86\u3002\u518d\u8fc7\u5f97\u4e03\u516b\u5929\uff0c\u8fde\u9a6c\u5934\u3001\u4e94\u810f\u7b49\u7b49\u4e5f\u5403\u4e86\u4e2a\u5e72\u51c0\u3002 \u3000\u3000\u82b1\u94c1\u5e72\u3001\u72c4\u4e91\u3001\u6c34\u7b19\u4e09\u4eba\u8fd9\u4e9b\u65e5\u5b50\u4e2d\u76f8\u4e92\u90fd\u4e0d\u8bf4\u8bdd\uff0c\u76ee\u5149\u5076\u5c14\u76f8\u89e6\uff0c\u4e5f\u7acb\u5373\u907f\u5f00\u3002\u82b1\u94c1\u5e72\u51e0\u6b21\u8d77\u5fc3\u8981\u6740\u4e86\u72c4\u4e91\u548c\u6c34\u7b19\uff0c\u5374\u603b\u89c9\u6740\u4e86\u4e8c\u4eba\u4e4b\u540e\uff0c\u5269\u4e0b\u81ea\u5df1\u4e00\u4eba\u5b64\u96f6\u96f6\u7684\u5728\u8fd9\u96ea\u8c37\u4e4b\u4e2d\uff0c\u6ecb\u5473\u592a\u4e5f\u96be\u53d7\uff0c\u53cd\u6b63\u4e8c\u4eba\u662f\u81ea\u5df1\u638c\u4e2d\u4e4b\u7269\uff0c\u5374\u4e5f\u4e0d\u5fd9\u52a8\u624b\u3002 \u3000\u3000\u8fc7\u4e86\u8fd9\u4e9b\u65e5\u5b50\uff0c\u6c34\u7b19\u5bf9\u72c4\u4e91\u5df2\u7591\u5fcc\u5927\u51cf\uff0c\u7ec8\u4e8e\u6562\u5230\u77f3\u6d1e\u4e2d\u5c31\u7761\u3002 \u3000\u3000\u8e0f\u8fdb\u5341\u4e8c\u6708\uff0c\u96ea\u8c37\u4e2d\u66f4\u52a0\u51b7\u4e86\uff0c\u4e00\u5230\u665a\u95f4\uff0c\u6574\u591c\u6714\u98ce\u547c\u5578\uff0c\u66f4\u662f\u5947\u5bd2\u5f7b\u9aa8\u3002\u72c4\u4e91\u201c\u795e\u7167\u529f\u201d\u7ec3\u6210\uff0c\u7ee7\u7eed\u4fee\u4e60\uff0c\u5185\u529b\u6bcf\u8fc7\u4e00\u5929\u4fbf\u589e\u8fdb\u4e00\u5206\uff0c\u4f46\u8863\u886b\u5355\u8584\uff0c\u5728\u8fd9\u51b0\u5929\u96ea\u5730\u4e4b\u4e2d\u7a76\u7adf\u4e5f\u9887\u4e3a\u96be\u6328\u3002\u6c34\u7b19\u6709\u65f6\u4ece\u5c71\u6d1e\u4e2d\u671b\u51fa\u6765\uff0c\u89c1\u4ed6\u7c0c\u7c0c\u53d1\u6296\uff0c\u5374\u59cb\u7ec8\u4e0d\u8e0f\u8fdb\u5c71\u6d1e\u4e00\u6b65\u4ee5\u5fa1\u98ce\u5bd2\uff0c\u5fc3\u4e0b\u9887\u6170\uff0c\u89c9\u5f97\u8fd9\u5c0f\u6076\u50e7\u201c\u6076\u201d\u662f\u6076\u7684\uff0c\u5012\u4e5f\u8fd8\u7b97\u6709\u793c\u3002 \u3000\u3000\u72c4\u4e91\u8eab\u4e0a\u7684\u521b\u4f24\u5168\u7136\u75ca\u6108\u4e86\uff0c\u65ad\u817f\u4e5f\u5df2\u63a5\u7eed\uff0c\u884c\u8d70\u5982\u5e38\uff0c\u6709\u65f6\u60f3\u8d77\u8fd9\u65ad\u817f\u662f\u8840\u5200\u8001\u7956\u7ed9\u63a5\u7eed\u7684\uff0c\u5fc3\u4e0b\u4e0d\u7981\u9eef\u7136\u3002 \u3000\u3000\u9a6c\u8089\u5403\u5b8c\u4e86\uff0c\u4eca\u540e\u7684\u7cae\u98df\u53ef\u662f\u4e2a\u5927\u96be\u9898\u3002\u6700\u540e\u90a3\u51e0\u5929\uff0c\u72c4\u4e91\u5df2\u5c3d\u53ef\u80fd\u7684\u5403\u5f97\u6781\u5c11\u6781\u5c11\uff0c\u53ea\u662f\u5403\u8fd9\u4e48\u4e00\u5c0f\u7247\uff0c\u4f46\u4ed6\u6240\u7701\u4e0b\u6765\u7684\uff0c\u90fd\u7ed9\u82b1\u94c1\u5e72\u8001\u5b9e\u4e0d\u5ba2\u6c14\u7684\u5403\u5230\u4e86\u809a\u91cc\u3002\u6c34\u7b19\u5fc3\u9053\uff1a\u201c\u4e00\u4f4d\u6210\u540d\u7684\u5927\u4fa0\uff0c\u5230\u4e86\u5371\u96be\u5173\u5934\uff0c\u8fd8\u4e0d\u5982\u8840\u5200\u95e8\u7684\u4e00\u4e2a\u5c0f\u6076\u50e7\uff01\u201d \u3000\u3000\u8fd9\u665a\u4e09\u66f4\u65f6\u5206\uff0c\u6c34\u7b19\u5728\u7761\u68a6\u4e2d\u5ffd\u88ab\u4e00\u9635\u4e89\u5435\u4e4b\u58f0\u60ca\u9192\uff0c\u53ea\u542c\u5f97\u72c4\u4e91\u5927\u58f0\u559d\u9053\uff1a\u201c\u6c34\u5927\u4fa0\u7684\u8eab\u4f53\uff0c\u4f60\u4e0d\u80fd\u52a8\uff01\u201d\u82b1\u94c1\u5e72\u51b7\u51b7\u7684\u9053\uff1a\u201c\u518d\u8fc7\u51e0\u5929\uff0c\u6d3b\u4eba\u4e5f\u5403\uff01\u6211\u5148\u5403\u6b7b\u4eba\uff0c\u662f\u8ba9\u4f60\u591a\u6d3b\u51e0\u5929\uff01\u201d\u72c4\u4e91\u9053\uff1a\u201c\u54b1\u4eec\u5b81\u53ef\u5403\u6811\u76ae\u8349\u6839\uff0c\u51b3\u4e0d\u80fd\u5403\u4eba\uff01\u201d\u82b1\u94c1\u5e72\u559d\u9053\uff1a\u201c\u6eda\u5f00\uff01\u7f57\u55e6\u4e9b\u751a\u4e48\uff1f\u60f9\u607c\u4e86\u6211\uff0c\u7acb\u523b\u6bd9\u4e86\u4f60\u3002\u201d \u3000\u3000\u6c34\u7b19\u5fd9\u4ece\u6d1e\u4e2d\u51b2\u51fa\u53bb\uff0c\u89c1\u72c4\u4e91\u548c\u82b1\u94c1\u5e72\u7ad9\u5728\u5979\u7236\u4eb2\u575f\u65c1\u3002 \u3000\u3000\u6c34\u7b19\u5927\u53eb\uff1a\u201c\u522b\u78b0\u6211\u7239\u7239\uff01\u201d\u98de\u5954\u8fc7\u53bb\uff0c\u53ea\u89c1\u5806\u5728\u7236\u4eb2\u5c38\u8eab\u4e0a\u7684\u767d\u96ea\u5df2\u88ab\u62e8\u5f00\uff0c\u82b1\u94c1\u5e72\u5de6\u624b\u6293\u7740\u6c34\u5cb1\u5c38\u8eab\u80f8\u53e3\u3002\u72c4\u4e91\u559d\u9053\uff1a\u201c\u5feb\u653e\u4e0b\uff01\u201d\u6c34\u7b19\u6025\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u2026\u2026\u201d \u3000\u3000\u7a81\u89c1\u5bd2\u5149\u4e00\u95ea\uff0c\u82b1\u94c1\u5e72\u8863\u8896\u4e2d\u7ffb\u51fa\u4e00\u679d\u77ed\u67aa\uff0c\u659c\u8eab\u633a\u67aa\uff0c\u75be\u5411\u72c4\u4e91\u80f8\u53e3\u523a\u53bb\u3002\u8fd9\u4e00\u67aa\u53bb\u5f97\u6781\u5feb\uff0c\u72c4\u4e91\u5185\u529f\u867d\u5df2\u5927\u8fdb\uff0c\u5916\u529f\u5374\u662f\u5e73\u5e73\uff0c\u4ecd\u4e0d\u8fc7\u662f\u4ee5\u524d\u621a\u957f\u53d1\u6240\u6559\u7684\u90a3\u4e00\u4e9b\u62f3\u811a\u5251\u672f\uff0c\u7ed9\u82b1\u94c1\u5e72\u8fd9\u4e2a\u5927\u884c\u5bb6\u7a81\u65bd\u6697\u7b97\uff0c\u5982\u4f55\u5bf9\u4ed8\u5f97\u4e86\uff1f\u4e00\u6014\u4e4b\u9645\uff0c\u67aa\u5c16\u5df2\u523a\u5230\u4ed6\u80f8\u53e3\u3002\u6c34\u7b19\u5927\u58f0\u60ca\u547c\uff0c\u4e0d\u77e5\u5982\u4f55\u662f\u597d\u3002 \u3000\u3000\u82b1\u94c1\u5e72\u6ee1\u62df\u8fd9\u4e00\u67aa\u4ece\u524d\u80f8\u76f4\u901a\u540e\u80cc\uff0c\u523a\u4ed6\u4e2a\u900f\u660e\u7a9f\u7abf\uff0c\u54ea\u77e5\u67aa\u5c16\u78b0\u5230\u4ed6\u80f8\u53e3\uff0c\u7adf\u7136\u523a\u4e0d\u8fc7\u53bb\uff0c\u963b\u4e86\u4e00\u963b\u3002 \u3000\u3000\u72c4\u4e91\u7ed9\u8fd9\u4e00\u67aa\u4e00\u63a8\uff0c\u4e00\u4ea4\u5750\u5012\uff0c\u5de6\u624b\u7ffb\u8d77\uff0c\u731b\u5f80\u67aa\u6746\u4e0a\u51fb\u53bb\u3002\u5580\u7684\u4e00\u58f0\uff0c\u82b1\u94c1\u5e72\u864e\u53e3\u9707\u88c2\uff0c\u77ed\u67aa\u8131\u624b\uff0c\u76f4\u98de\u4e0a\u5929\u3002\u8fd9\u4e00\u638c\u4f59\u52bf\u4e0d\u8870\uff0c\u76f4\u9707\u5f97\u82b1\u94c1\u5e72\u4e00\u4e2a\u7b4b\u6597\uff0c\u4ef0\u8dcc\u4e86\u51fa\u53bb\u3002\u77ed\u67aa\u843d\u5165\u4e86\u6df1\u8c37\u79ef\u96ea\u4e4b\u4e2d\uff0c\u4e0d\u77e5\u53bb\u5411\u3002 \u3000\u3000\u82b1\u94c1\u5e72\u5927\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u5c0f\u548c\u5c1a\u6b66\u529f\u5982\u6b64\u795e\u5947\uff0c\u76f4\u4e0d\u5728\u8001\u548c\u5c1a\u4e4b\u4e0b\uff01\u201d\u5411\u540e\u51e0\u4e2a\u7ffb\u6eda\uff0c\u8dc3\u8d77\u8eab\u6765\uff0c\u8fdc\u8fdc\u9003\u4e86\u5f00\u53bb\u3002 \u3000\u3000\u82b1\u94c1\u5e72\u5374\u4e0d\u77e5\u8fd9\u4e00\u67aa\u867d\u56e0\u201c\u4e4c\u8695\u8863\u201d\u4e4b\u963b\uff0c\u6ca1\u523a\u8fdb\u72c4\u4e91\u7684\u8eab\u5b50\uff0c\u4f46\u529b\u9053\u5947\u5927\uff0c\u5df2\u6233\u5f97\u4ed6\u95ed\u4f4f\u4e86\u547c\u5438\uff0c\u900f\u4e0d\u8fc7\u6c14\u6765\uff0c\u6655\u5012\u5728\u5730\u3002\u82e5\u4e0d\u662f\u4ed6\u201c\u795e\u7167\u529f\u201d\u5df2\u7136\u7ec3\u6210\uff0c\u8fd9\u4e00\u67aa\u4fbf\u8981\u4e86\u4ed6\u7684\u6027\u547d\u3002\u82b1\u94c1\u5e72\u4f55\u7b49\u6b66\u529f\uff0c\u8f83\u4e4b\u5f53\u65e5\u8346\u5dde\u57ce\u4e2d\u5468\u573b\u5251\u523a\uff0c\u867d\u7136\u540c\u662f\u523a\u5230\u201c\u4e4c\u8695\u8863\u201d\u4e0a\u3002\u52b2\u529b\u7684\u5f3a\u5f31\u5374\u662f\u76f8\u53bb\u4f55\u6b62\u500d\u84f0\u3002 \u3000\u3000\u7693\u6708\u5f53\u7a7a\uff0c\u4e24\u5934\u5140\u9e70\u89c1\u5230\u96ea\u5730\u4e2d\u7684\u72c4\u4e91\uff0c\u5728\u7a7a\u4e2d\u4e0d\u4f4f\u7684\u6253\u7740\u76d8\u65cb\u3002 \u3000\u3000\u6c34\u7b19\u89c1\u72c4\u4e91\u5012\u5730\u4e0d\u8d77\uff0c\u4f3c\u5df2\u88ab\u82b1\u94c1\u5e72\u523a\u6b7b\uff0c\u5fc3\u4e0b\u4e00\u559c\uff1a \u3000\u3000\u201c\u5c0f\u6076\u50e7\u7ec8\u4e8e\u6b7b\u4e86\uff0c\u4ece\u6b64\u4fbf\u4e0d\u6015\u6709\u4eba\u6765\u4fb5\u72af\u6211\u3002\u201d\u4f46\u968f\u5373\u53c8\u60f3\uff1a \u3000\u3000\u201c\u82b1\u94c1\u5e72\u60f3\u5403\u6211\u7239\u7239\u7684\u9057\u4f53\uff0c\u5c0f\u6076\u50e7\u5168\u529b\u963b\u6b62\uff0c\u4ee5\u81f4\u88ab\u6740\u3002\u5c0f\u6076\u50e7\u591a\u534a\u4e0d\u6000\u597d\u610f\uff0c\u60f3\u9a97\u5f97\u6211\u2026\u2026\u9a97\u5f97\u6211\u2026\u2026\u54fc\uff0c\u6211\u624d\u4e0d\u4e0a\u4ed6\u7684\u5f53\u5462\u3002\u53ef\u662f\u4ed6\u6b7b\u4e86\u4e4b\u540e\uff0c\u82b1\u94c1\u5e72\u8fd9\u6076\u4eba\u518d\u6765\u72af\u6211\u7239\u7239\u9057\u4f53\uff0c\u90a3\u4fbf\u5982\u4f55\u662f\u597d\uff1f\u6700\u597d\u5c0f\u6076\u50e7\u8fd8\u662f\u522b\u6b7b\u3002\u201d \u3000\u3000\u5979\u624b\u63e1\u8840\u5200\uff0c\u6162\u6162\u8d70\u5230\u72c4\u4e91\u8eab\u65c1\uff0c\u89c1\u4ed6\u4e00\u52a8\u4e0d\u52a8\u7684\u4ef0\u5367\u5728\u96ea\u5730\u4e4b\u4e2d\uff0c\u8138\u4e0a\u808c\u8089\u5fae\u5fae\u626d\u66f2\uff0c\u663e\u7136\u672a\u6b7b\u3002\u6c34\u7b19\u5fc3\u4e2d\u4e00\u559c\uff0c\u5f2f\u8170\u4fef\u8eab\uff0c\u4f38\u624b\u5230\u4ed6\u9f3b\u5b54\u4e0b\u53bb\u63a2\u4ed6\u9f3b\u606f\uff0c\u7a81\u89c9\u4e24\u80a1\u70bd\u70ed\u7684\u6696\u6c14\uff0c\u76f4\u55b7\u5230\u5979\u624b\u6307\u4e0a\u3002 \u3000\u3000\u6c34\u7b19\u5413\u4e86\u4e00\u8df3\uff0c\u6025\u5fd9\u7f29\u624b\uff0c\u5979\u672c\u60f3\u72c4\u4e91\u5c31\u7b97\u672a\u6b7b\uff0c\u4e5f\u5fc5\u547c\u5438\u5fae\u5f31\uff0c\u54ea\u77e5\u547c\u51fa\u6765\u7684\u6c14\u606f\u7adf\u5982\u6b64\u70bd\u70ed\u3002\u5979\u81ea\u4e0d\u77e5\u8fd9\u65f6\u72c4\u4e91\u5185\u529b\u5df2\u751a\u4e3a\u6df1\u539a\uff0c\u77e5\u89c9\u867d\u5931\uff0c\u6c14\u606f\u4ecd\u7136\u7c97\u58ee\uff0c\u53ea\u662f\u4ed6\u4e0a\u4e58\u5185\u529f\u7ec3\u6210\u672a\u4e45\uff0c\u96c4\u5065\u6709\u4f59\uff0c\u6c89\u7a33\u4e0d\u8db3\uff0c\u8fd8\u672a\u8fbe\u5230\u878d\u548c\u81ea\u7136\u7684\u5883\u754c\u3002 \u3000\u3000\u6c34\u7b19\u5fc3\u60f3\uff1a\u201c\u5c0f\u6076\u50e7\u6655\u4e86\u8fc7\u53bb\uff0c\u5f85\u4f1a\u9192\u8f6c\uff0c\u89c1\u6211\u7ad9\u5728\u4ed6\u8eab\u65c1\uff0c\u90a3\u53ef\u4e0d\u59a5\u3002\u201d\u4e00\u56de\u5934\uff0c\u53ea\u89c1\u82b1\u94c1\u5e72\u4fbf\u7ad9\u5728\u4e0d\u8fdc\u4e4b\u5904\uff0c\u51dd\u76ee\u6ce8\u89c6\u7740\u4ed6\u4e8c\u4eba\u3002 \u3000\u3000\u82b1\u94c1\u5e72\u4e00\u67aa\u523a\u4e0d\u6b7b\u72c4\u4e91\uff0c\u53c8\u88ab\u4ed6\u53cd\u638c\u51fb\u5012\uff0c\u5fc3\u4e0b\u60ca\u60e7\u5f02\u5e38\uff0c\u4f46\u968f\u5373\u89c1\u4ed6\u5012\u5730\u4e0d\u8d77\uff0c\u81ea\u662f\u6025\u6b32\u77e5\u4ed6\u6b7b\u6d3b\uff0c\u8fc7\u4e86\u7247\u523b\uff0c\u89c1\u4ed6\u59cb\u7ec8\u4e0d\u52a8\uff0c\u5f53\u4e0b\u4e00\u6b65\u4e00\u6b65\u7684\u8d70\u5c06\u8fc7\u53bb\u3002\u8fd9\u65f6\u4ed6\u53f3\u81c2\u5140\u81ea\u9690\u9690\u9178\u9ebb\uff0c\u53ea\u5f85\u72c4\u4e91\u8dc3\u8d77\uff0c\u7acb\u65f6\u8f6c\u8eab\u4fbf\u9003\u3002 \u3000\u3000\u6c34\u7b19\u5927\u60ca\uff0c\u559d\u9053\uff1a\u201c\u522b\u8fc7\u6765\u3002\u201d\u82b1\u94c1\u5e72\u72de\u7b11\u9053\uff1a\u201c\u4e3a\u751a\u4e48\u4e0d\u80fd\u8fc7\u6765\uff1f\u6d3b\u4eba\u6bd4\u6b7b\u4eba\u597d\u5403\uff0c\u54b1\u4eec\u5bb0\u4e86\u4ed6\u5206\u800c\u98df\u4e4b\uff0c\u6709\u4f55\u4e0d\u7f8e\uff1f\u201d \u3000\u3000\u8bf4\u7740\u53c8\u8d70\u8fd1\u4e86\u4e00\u6b65\u3002\u6c34\u7b19\u65e0\u6cd5\u53ef\u65bd\uff0c\u62da\u547d\u6447\u6643\u72c4\u4e91\uff0c\u53eb\u9053\uff1a\u201c\u4ed6\u8fc7\u6765\u5566\uff0c\u4ed6\u8fc7\u6765\u5566\u3002\u201d \u3000\u3000\u82b1\u94c1\u5e72\u773c\u89c1\u72c4\u4e91\u660f\u8ff7\u4e0d\u9192\uff0c\u5fc3\u4e2d\u5927\u559c\uff0c\u7acb\u5373\u4e00\u8dc3\u800c\u524d\uff0c\u4e3e\u8d77\u53f3\u638c\uff0c\u5f80\u72c4\u4e91\u8eab\u4e0a\u51fb\u843d\u3002\u6c34\u7b19\u6325\u8d77\u8840\u5200\uff0c\u4e00\u62db\u201c\u91d1\u9488\u6e21\u52ab\u201d\uff0c\u5411\u82b1\u94c1\u5e72\u523a\u53bb\u3002\u5979\u4f7f\u7684\u4e43\u662f\u5251\u6cd5\uff0c\u4f46\u8840\u5200\u950b\u9510\u5f02\u5e38\uff0c\u5374\u4e5f\u9887\u5177\u5a01\u529b\u3002\u82b1\u94c1\u5e72\u77ed\u67aa\u5df2\u5931\uff0c\u8d64\u624b\u7a7a\u62f3\uff0c\u751f\u6015\u7ed9\u8fd9\u524a\u94c1\u5982\u6ce5\u7684\u8840\u5200\u5e26\u4e0a\u4e86\uff0c\u5012\u4e5f\u4e0d\u6562\u8f7b\u654c\uff0c\u5f53\u4e0b\u65bd\u5c55\u7a7a\u624b\u5165\u767d\u5203\u529f\u592b\uff0c\u8981\u5c06\u8840\u5200\u5148\u593a\u8fc7\u6765\u518d\u8bf4\u3002 \u3000\u3000\u72c4\u4e91\u660f\u6655\u8ff7\u7cca\u4e2d\u4f9d\u7a00\u542c\u5230\u6c34\u7b19\u5927\u53eb\uff1a\u201c\u4ed6\u8fc7\u6765\u5566\u3002\u201d\u660f\u660f\u6c89\u6c89\u7684\u4e0d\u77e5\u662f\u751a\u4e48\u610f\u601d\uff0c\u8ddf\u7740\u542c\u5230\u4e00\u9635\u547c\u65a5\u53f1\u559d\uff0c\u7741\u5f00\u773c\u6765\uff0c\u6708\u5149\u4e0b\u53ea\u89c1\u6c34\u7b19\u624b\u821e\u8840\u5200\uff0c\u548c\u82b1\u94c1\u5e72\u6597\u5f97\u6b63\u9163\u3002 \u3000\u3000\u6c34\u7b19\u867d\u624b\u6709\u5229\u5668\uff0c\u4f46\u4e00\u6765\u4e0d\u4f1a\u4f7f\u5200\uff0c\u4e8c\u6765\u6b66\u529f\u8fdc\u4e3a\u4e0d\u53ca\uff0c\u5de6\u652f\u53f3\u7ecc\uff0c\u8fde\u8fde\u5012\u9000\uff0c\u5230\u5f97\u540e\u6765\uff0c\u53ea\u76fc\u624b\u4e2d\u5175\u5203\u4e0d\u4e3a\u654c\u4eba\u593a\u53bb\uff0c\u54ea\u91cc\u8fd8\u987e\u5f97\u5230\u4f24\u654c\uff1f\u4e0d\u4f4f\u6025\u53eb\uff1a\u201c\u5582\uff0c\u5582\uff01\u5feb\u9192\u8f6c\u6765\uff0c\u4ed6\u8981\u6765\u6740\u4f60\u5566\u3002\u201d \u3000\u3000\u72c4\u4e91\u4e00\u542c\uff0c\u5fc3\u4e2d\u4e00\u51db\uff1a\u201c\u597d\u9669\uff01\u9002\u624d\u662f\u5979\u6551\u4e86\u6211\u6027\u547d\u3002\u82e5\u4e0d\u662f\u5979\u51fa\u529b\u62b5\u6321\uff0c\u82b1\u94c1\u5e72\u65e9\u5c06\u6211\u6253\u6b7b\u4e86\u3002\u867d\u7136\u6211\u80f8\u8179\u6709\u4e4c\u8695\u8863\u4fdd\u62a4\uff0c\u4f46\u4ed6\u53ea\u987b\u5728\u6211\u5934\u4e0a\u4e00\u811a\uff0c\u8fd8\u80fd\u8e22\u4e0d\u6b7b\u4e48\uff1f\u201d\u5f53\u5373\u633a\u8eab\u8dc3\u8d77\uff0c\u6325\u638c\u731b\u5411\u82b1\u94c1\u5e72\u6253\u53bb\uff0c\u82b1\u94c1\u5e72\u8fd8\u638c\u76f8\u8fce\uff0c\u84ec\u7684\u4e00\u58f0\u54cd\uff0c\u4e24\u4eba\u90fd\u5750\u5012\u5728\u5730\u3002\u72c4\u4e91\u5185\u529b\u6df1\u539a\uff0c\u82b1\u94c1\u5e72\u638c\u6cd5\u9ad8\u660e\uff0c\u53cc\u638c\u76f8\u4ea4\uff0c\u7adf\u662f\u4e0d\u76f8\u4e0a\u4e0b\u3002 \u3000\u3000\u82b1\u94c1\u5e72\u6b66\u529f\u9ad8\uff0c\u5e94\u53d8\u901f\uff0c\u88ab\u72c4\u4e91\u4e00\u638c\u9707\u5012\uff0c\u968f\u5373\u8dc3\u8d77\uff0c\u7b2c\u4e8c\u638c\u53c8\u51fb\u4e86\u8fc7\u6765\u3002\u72c4\u4e91\u4e0d\u53ca\u7ad9\u8d77\uff0c\u53ea\u5f97\u5750\u7740\u8fd8\u4e86\u4e00\u638c\u3002\u4ed6\u867d\u5750\u7740\uff0c\u638c\u529b\u4e1d\u6beb\u4e0d\u5f31\uff0c\u53c8\u662f\u84ec\u7684\u4e00\u58f0\uff0c\u72c4\u4e91\u88ab\u9707\u5f97\u7ffb\u4e86\u4e24\u4e2a\u7b4b\u6597\uff0c\u82b1\u94c1\u5e72\u5374\u817e\u817e\u817e\u5012\u9000\u4e09\u6b65\uff0c\u80f8\u95f4\u6c14\u8840\u7ffb\u6d8c\uff0c\u5fc3\u4e0b\u6697\u60ca\uff1a \u3000\u3000\u201c\u8fd9\u5c0f\u6076\u50e7\u5185\u529b\u5982\u6b64\u6df1\u539a\uff01\u201d\u4f46\u4e24\u638c\u4ea4\u8fc7\uff0c\u77e5\u4ed6\u638c\u6cd5\u6781\u662f\u5e73\u5eb8\uff0c\u5fcc\u60ee\u4e4b\u5fc3\u5c3d\u53bb\uff0c\u659c\u8eab\u4fa7\u8fdb\uff0c\u7b2c\u4e09\u638c\u53c8\u51fb\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u72c4\u4e91\u5750\u7740\u6325\u638c\u8fd8\u51fb\uff0c\u4e0d\u6599\u82b1\u94c1\u5e72\u7684\u624b\u638c\u98d8\u98d8\u5ffd\u5ffd\uff0c\u4ece\u4ed6\u8138\u524d\u63a0\u8fc7\uff0c\u72c4\u4e91\u4e00\u638c\u6253\u7a7a\uff0c\u8ddf\u7740\u62cd\u7684\u4e00\u4e0b\uff0c\u80f8\u53e3\u5df2\u5403\u4e86\u4e00\u638c\uff0c\u5e78\u597d\u6709\u4e4c\u8695\u8863\u62a4\u8eab\uff0c\u4e0d\u81f4\u53d7\u4f24\uff0c\u4f46\u4e5f\u662f\u7ecf\u53d7\u4e0d\u8d77\uff0c\u521a\u8981\u7ad9\u8d77\uff0c\u590d\u53c8\u5750\u5012\u3002\u82b1\u94c1\u5e72\u4e00\u638c\u5f97\u624b\uff0c\u7b2c\u4e8c\u638c\u8ddf\u7740\u53c8\u81f3\u3002\u4ed6\u867d\u4ee5\u201c\u4e2d\u5e73\u67aa\u201d\u9a70\u540d\u6b66\u6797\uff0c\u53f7\u79f0\u201c\u4e2d\u5e73\u65e0\u654c\u201d\uff0c\u4f46\u62f3\u811a\u529f\u592b\u4e5f\u751a\u4e86\u5f97\uff0c\u8fd9\u65f6\u628a\u4e00\u8def\u201c\u5cb3\u5bb6\u6563\u624b\u201d\u4f7f\u5c06\u51fa\u6765\uff0c\u638c\u5f71\u98d8\u98d8\uff0c\u5de6\u4e00\u638c\uff0c\u53f3\u4e00\u638c\uff0c\u5341\u638c\u4e2d\u5012\u6709\u56db\u4e94\u638c\u6253\u4e2d\u4e86\u72c4\u4e91\u3002\u72c4\u4e91\u8fd8\u51fa\u624b\u53bb\uff0c\u5747\u7ed9\u4ed6\u4ee5\u5de7\u5999\u8eab\u6cd5\u907f\u8fc7\u3002\u4e24\u4eba\u6b66\u529f\u5b9e\u5728\u76f8\u5dee\u592a\u8fdc\uff0c\u72c4\u4e91\u5185\u529b\u518d\u5f3a\uff0c\u4e5f\u662f\u7edd\u65e0\u673a\u4f1a\u65bd\u5c55\u3002 \u3000\u3000\u5230\u5f97\u540e\u6765\uff0c\u72c4\u4e91\u53ea\u5f97\u4ee5\u53cc\u638c\u62a4\u4f4f\u5934\u8138\uff0c\u8eab\u4e0a\u4efb\u4ed6\u6bb4\u51fb\uff0c\u4e00\u7ad9\u8d77\u8eab\uff0c\u7acb\u88ab\u51fb\u5012\u3002\u82b1\u94c1\u5e72\u53ea\u60f3\u5c3d\u65e9\u6599\u7406\u4e86\u4ed6\uff0c\u514d\u751f\u540e\u60a3\uff0c\u4e00\u638c\u638c\u7684\u72e0\u6253\u3002\u72c4\u4e91\u8fde\u5410\u4e86\u4e09\u53e3\u8840\uff0c\u8eab\u6cd5\u5df2\u5927\u4e3a\u7f13\u8fdf\u3002 \u3000\u3000\u6c34\u7b19\u521d\u65f6\u89c1\u4e24\u4eba\u6597\u5f97\u6fc0\u70c8\uff0c\u63d2\u4e0d\u8fdb\u53bb\u76f8\u52a9\uff0c\u5f85\u89c1\u72c4\u4e91\u5782\u5371\uff0c\u5fd9\u6325\u5200\u5f80\u82b1\u94c1\u5e72\u80cc\u4e0a\u780d\u53bb\u3002\u82b1\u94c1\u5e72\u4fa7\u8eab\u907f\u8fc7\uff0c\u53cd\u624b\u64d2\u62ff\uff0c\u593a\u5979\u5175\u5203\u3002\u72c4\u4e91\u53f3\u638c\u4f7f\u52b2\u62cd\u51fa\uff0c\u4e00\u80a1\u51cc\u5389\u7684\u638c\u98ce\u767b\u65f6\u5c06\u82b1\u94c1\u5e72\u5168\u8eab\u7f69\u4f4f\u4e86\u3002\u82b1\u94c1\u5e72\u95ea\u907f\u4e0d\u5f97\uff0c\u53ea\u5f97\u51fa\u638c\u76f8\u8fce\u3002\u8bf4\u5230\u4ee5\u5185\u529b\u76f8\u62da\uff0c\u82b1\u94c1\u5e72\u5374\u4e0d\u662f\u5bf9\u624b\u4e86\uff0c\u7a81\u7136\u95f4\u53ea\u89c9\u773c\u524d\u91d1\u661f\u4e71\u5192\uff0c\u534a\u8eab\u9178\u9ebb\uff0c\u6447\u6447\u6643\u6643\u7684\u7ad9\u7acb\u4e0d\u5b9a\u3002 \u3000\u3000\u6c34\u7b19\u53eb\u9053\uff1a\u201c\u5feb\u8d70\uff0c\u5feb\u8d70\uff01\u201d\u62c9\u7740\u72c4\u4e91\uff0c\u62a2\u8fdb\u4e86\u5c71\u6d1e\u3002\u4e24\u4eba\u5306\u5306\u5fd9\u5fd9\u7684\u642c\u8fc7\u51e0\u5757\u5927\u77f3\uff0c\u5806\u5728\u6d1e\u53e3\u3002\u6c34\u7b19\u624b\u6267\u8840\u5200\uff0c\u5b88\u5728\u77f3\u65c1\u3002\u8fd9\u5c71\u6d1e\u6d1e\u53e3\u751a\u7a84\uff0c\u51e0\u5757\u5927\u77f3\u867d\u4e0d\u80fd\u5835\u585e\uff0c\u4f46\u82b1\u94c1\u5e72\u8981\u8fdb\u5c71\u6d1e\uff0c\u5374\u5fc5\u987b\u642c\u5f00\u4e00\u4e24\u5757\u77f3\u5934\u624d\u6210\u3002\u53ea\u8981\u4ed6\u52a8\u624b\u642c\u77f3\uff0c\u6c34\u7b19\u4fbf\u53ef\u6325\u5200\u65a9\u4ed6\u53cc\u624b\u3002 \u3000\u3000\u8fc7\u4e86\u597d\u4e00\u4f1a\uff0c\u5916\u8fb9\u5e76\u65e0\u52a8\u9759\u3002\u6c34\u7b19\u9053\uff1a\u201c\u5c0f\u6076\u2026\u2026\u5c0f\u2026\u2026\u201d\u5979\u4e00\u76f4\u53eb\u60ef\u4e86\u201c\u5c0f\u6076\u50e7\u201d\uff0c\u8fd9\u65f6\u8ddf\u4ed6\u8054\u624b\u8fce\u654c\uff0c\u518d\u53eb\u201c\u5c0f\u6076\u50e7\u201d\u672a\u514d\u4e0d\u597d\u610f\u601d\uff0c\u6539\u53e3\u9053\uff1a\u201c\u4f60\u4f24\u52bf\u600e\u6837\uff1f\u201d\u72c4\u4e91\u9053\uff1a\u201c\u8fd8\u597d\u2026\u2026\u201d \u3000\u3000\u5ffd\u542c\u5f97\u82b1\u94c1\u5e72\u5728\u6d1e\u5916\u54c8\u54c8\u5927\u7b11\uff0c\u53eb\u9053\uff1a\u201c\u4e24\u53ea\u5c0f\u6742\u79cd\u8eb2\u4e86\u8d77\u6765\uff0c\u5728\u6d1e\u4e2d\u5e72\u90a3\u4e0d\u53ef\u544a\u4eba\u4e4b\u4e8b\u4e86\u3002\u201d\u6c34\u7b19\u8138\u4e0a\u4e00\u9635\u53d1\u70ed\uff0c\u5fc3\u4e2d\u5374\u4e5f\u771f\u6709\u4e9b\u5bb3\u6015\uff0c\u5979\u8ba4\u5b9a\u72c4\u4e91\u662f\u4e2a\u201c\u6deb\u50e7\u201d\uff0c\u884c\u6b62\u5341\u5206\u4e0d\u7aef\uff0c\u8ddf\u4ed6\u540c\u5728\u5c71\u6d1e\u4e4b\u4e2d\uff0c\u5b9e\u662f\u5371\u9669\u4e0d\u8fc7\uff0c\u4e0d\u7531\u5f97\u5411\u5de6\u659c\u884c\u51e0\u6b65\uff0c\u8981\u8ddf\u4ed6\u79bb\u5f97\u8d8a\u8fdc\u8d8a\u597d\u3002 \u3000\u3000\u53ea\u542c\u82b1\u94c1\u5e72\u53c8\u53eb\u9053\uff1a\u201c\u4e24\u4e2a\u72d7\u7537\u5973\u8eb2\u7740\u4e0d\u51fa\u6765\uff0c\u8001\u5b50\u5374\u8981\u70e4\u8089\u5403\u4e86\uff0c\u54c8\u54c8\uff0c\u54c8\u54c8\uff01\u201d\u6c34\u7b19\u5927\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u4ed6\u8981\u5403\u6211\u7239\u7239\uff0c\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u72c4\u4e91\u8fd9\u51e0\u5e74\u6765\u4e8b\u4e8b\u53d7\u4eba\u51a4\u6789\uff0c\u8fd9\u65f6\u542c\u5f97\u82b1\u94c1\u5e72\u53c8\u5728\u8840\u53e3\u55b7\u4eba\uff0c\u5982\u4f55\u5fcd\u8010\u5f97\u4f4f\uff1f\u7a81\u7136\u63a8\u5f00\u77f3\u5934\uff0c\u5982\u4e00\u5934\u75af\u864e\u822c\u6251\u4e86\u51fa\u53bb\uff0c\u62f3\u638c\u4e71\u51fb\u4e71\u62cd\uff0c\u594b\u529b\u5411\u4ed6\u72c2\u6253\u8fc7\u53bb\u3002 \u3000\u3000\u82b1\u94c1\u5e72\u907f\u8fc7\u4e24\u638c\uff0c\u5de6\u638c\u753b\u4e2a\u5706\u5f27\uff0c\u53f3\u638c\u4ece\u80cc\u540e\u62cd\u51fa\uff0c\u4ece\u72c4\u4e91\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\u7684\u65b9\u4f4d\u62cd\u4e86\u8fc7\u6765\uff0c\u7830\u7684\u4e00\u58f0\uff0c\u7ed3\u7ed3\u5b9e\u5b9e\u6253\u5728\u4ed6\u80cc\u4e0a\u3002\u72c4\u4e91\u5410\u51fa\u4e00\u53e3\u9c9c\u8840\uff0c\u8111\u5b50\u4e2d\u8ff7\u8ff7\u7cca\u7cca\uff0c\u773c\u524d\u8fd9\u82b1\u94c1\u5e72\u4f3c\u4e4e\u53d8\u6210\u4e86\u4e07\u9707\u5c71\u3001\u4e07\u572d\u3001\u6c5f\u9675\u53bf\u7684\u77e5\u53bf\u3001\u72f1\u5352\u3001\u51cc\u9000\u601d\u3001\u5b9d\u8c61\u2026\u2026\u8fd9\u8bb8\u8bb8\u591a\u591a\u51cc\u8fb1\u8650\u5f85\u4ed6\u7684\u6076\u4eba\u3002\u4ed6\u5f20\u5f00\u53cc\u81c2\uff0c\u731b\u5730\u5c06\u82b1\u94c1\u5e72\u7262\u7262\u62b1\u4f4f\u4e86\u3002 \u3000\u3000\u82b1\u94c1\u5e72\u4e00\u62f3\u6253\u5728\u4ed6\u9f3b\u5b50\u4e0a\uff0c\u767b\u65f6\u6253\u5f97\u4ed6\u9f3b\u8840\u957f\u6d41\u3002\u4f46\u72c4\u4e91\u5df2\u4e0d\u89c9\u75bc\u75db\uff0c\u62b1\u4f4f\u4ed6\u8170\u95f4\u7684\u53cc\u624b\u8d8a\u7b8d\u8d8a\u7d27\u3002\u82b1\u94c1\u5e72\u53ea\u89c9\u547c\u5438\u4e0d\u7545\uff0c\u5fc3\u4e2d\u4e5f\u6709\u4e9b\u60ca\u60f6\uff0c\u53c8\u89c1\u6c34\u7b19\u624b\u6267\u8840\u5200\uff0c\u62a2\u8fd1\u8eab\u6765\u3002\u82b1\u94c1\u5e72\u5927\u60ca\uff0c\u53cc\u62f3\u731b\u529b\u5728\u72c4\u4e91\u80c1\u4e0b\u75be\u649e\u3002\u72c4\u4e91\u5403\u75db\uff0c\u81c2\u4e0a\u65e0\u529b\u3002 \u3000\u3000\u82b1\u94c1\u5e72\u7528\u529b\u4e00\u6323\uff0c\u89e3\u8131\u4e86\u4ed6\u53cc\u81c2\u73af\u62b1\uff0c\u518d\u4e5f\u4e0d\u6562\u548c\u8fd9\u72c2\u4eba\u62da\u6597\uff0c\u63a5\u8fde\u7eb5\u8dc3\uff0c\u79bb\u4ed6\u6709\u5341\u4f59\u4e08\u8fdc\uff0c\u8fd9\u624d\u7ad9\u5b9a\u3002 \u3000\u3000\u6c34\u7b19\u89c1\u72c4\u4e91\u6447\u6447\u6643\u6643\uff0c\u7ad9\u7acb\u4e0d\u5b9a\uff0c\u6ee1\u8138\u90fd\u662f\u9c9c\u8840\uff0c\u60f3\u4f38\u624b\u76f8\u6276\uff0c\u5374\u53c8\u5bb3\u6015\uff0c\u6218\u6218\u5162\u5162\u7684\u8d70\u8fd1\u4e24\u6b65\u3002\u72c4\u4e91\u559d\u9053\uff1a\u201c\u6211\u662f\u6076\u548c\u5c1a\uff0c\u662f\u5c0f\u6deb\u50e7\uff0c\u522b\u8d70\u8fc7\u6765\uff0c\u514d\u5f97\u6211\u6c61\u4e86\u4f60\u6c34\u5927\u4fa0\u5c0f\u59d0\u7684\u58f0\u540d\uff0c\u6eda\u5f00\uff0c\u6eda\u5f00\uff01\u201d\u6c34\u7b19\u89c1\u4ed6\u795e\u6001\u72f0\u72de\uff0c\u76ee\u9732\u51f6\u5149\uff0c\u5413\u5f97\u5012\u9000\u4e86\u4e24\u6b65\u3002 \u3000\u3000\u72c4\u4e91\u4e0d\u4f4f\u5598\u606f\uff0c\u6447\u6447\u6446\u6446\u7684\u5411\u82b1\u94c1\u5e72\u8d70\u53bb\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u4eec\u8fd9\u4e9b\u6076\u4eba\uff0c\u4e07\u9707\u5c71\u3001\u4e07\u572d\uff0c\u4f60\u4eec\u5bb3\u4e0d\u6b7b\u6211\uff0c\u6253\u4e0d\u6b7b\u6211\u3002\u8fc7\u6765\u554a\uff0c\u6765\u6253\u554a\uff0c\u77e5\u53bf\u5927\u4eba\uff0c\u77e5\u5e9c\u5927\u4eba\uff0c\u4f60\u4eec\u5c31\u4f1a\u6b3a\u538b\u826f\u5584\uff0c\u6709\u79cd\u7684\u8fc7\u6765\u62da\u554a\uff0c\u6765\u6253\u4e2a\u4f60\u6b7b\u6211\u6d3b\u2026\u2026\u201d \u3000\u3000\u82b1\u94c1\u5e72\u5fc3\u9053\uff1a\u201c\u8fd9\u4e2a\u4eba\u53d1\u4e86\u75af\uff0c\u662f\u4e2a\u75af\u5b50\uff01\u201d\u5411\u540e\u7eb5\u8dc3\uff0c\u79bb\u4ed6\u66f4\u8fdc\u4e86\u4e9b\u3002 \u3000\u3000\u72c4\u4e91\u4ef0\u5929\u5927\u53eb\uff1a\u201c\u4f60\u4eec\u8fd9\u4e9b\u6076\u4eba\uff0c\u5929\u4e0b\u7684\u6076\u4eba\u90fd\u6765\u6253\u554a\uff0c\u6211\u72c4\u4e91\u4e0d\u6015\u4f60\u4eec\u3002\u4f60\u4eec\u628a\u6211\u5173\u5728\u7262\u91cc\uff0c\u7a7f\u6211\u7435\u7436\u9aa8\uff0c\u65a9\u4e86\u6211\u624b\u6307\uff0c\u62a2\u4e86\u6211\u5e08\u59b9\uff0c\u8e39\u65ad\u6211\u5927\u817f\uff0c\u6211\u90fd\u4e0d\u6015\uff0c\u628a\u6211\u65a9\u6210\u8089\u9171\uff0c\u6211\u4e5f\u4e0d\u6015\uff01\u201d \u3000\u3000\u6c34\u7b19\u542c\u5f97\u4ed6\u5982\u6b64\u5636\u58f0\u5927\u53eb\uff0c\u6709\u5982\u54ed\u53f7\uff0c\u5bb3\u6015\u4e4b\u4e2d\u4e0d\u7981\u8d77\u4e86\u601c\u60af\u4e4b\u5fc3\uff0c\u542c\u4ed6\u53eb\u9053\u201c\u7a7f\u6211\u7435\u7436\u9aa8\uff0c\u65a9\u4e86\u6211\u624b\u6307\uff0c\u62a2\u4e86\u6211\u5e08\u59b9\uff0c\u8e39\u65ad\u6211\u5927\u817f\u201d\u66f4\u662f\u5fc3\u4e2d\u4e00\u52a8\uff1a\u201c\u8fd9\u5c0f\u6076\u50e7\u539f\u6765\u6ee1\u6000\u5fc3\u4e8b\uff0c\u53d7\u8fc7\u4e0d\u5c11\u82e6\u695a\u3002\u4ed6\u7684\u5927\u817f\uff0c\u5374\u662f\u6211\u7eb5\u9a6c\u8e39\u65ad\u4ed6\u7684\u3002\u201d \u3000\u3000\u72c4\u4e91\u53eb\u5f97\u58f0\u97f3\u4e5f\u54d1\u4e86\uff0c\u7ec8\u4e8e\u8eab\u5b50\u51e0\u4e0b\u6447\u6643\uff0c\u6454\u5012\u5728\u96ea\u5730\u4e4b\u4e2d\u3002 \u3000\u3000\u82b1\u94c1\u5e72\u4e0d\u6562\u8d70\u8fd1\uff0c\u6c34\u7b19\u4e5f\u4e0d\u6562\u8d70\u8fd1\u3002 \u3000\u3000\u534a\u7a7a\u4e2d\u4e24\u53ea\u5140\u9e70\u4e00\u76f4\u4e0d\u4f4f\u7684\u5728\u76d8\u65cb\u3002\u72c4\u4e91\u8eba\u5728\u5730\u4e0b\uff0c\u4e00\u52a8\u4e5f\u4e0d\u52a8\u3002\u84e6\u5730\u91cc\u4e00\u5934\u5140\u9e70\u6251\u5c06\u4e0b\u6765\uff0c\u5411\u4ed6\u989d\u5934\u4e0a\u5544\u53bb\u3002\u72c4\u4e91\u660f\u660f\u6c89\u6c89\u7684\u4f3c\u6655\u975e\u6655\uff0c\u7ed9\u5140\u9e70\u4e00\u5544\uff0c\u7acb\u65f6\u9192\u8f6c\u3002\u90a3\u9e70\u89c1\u4ed6\u8eab\u5b50\u4e00\u52a8\uff0c\u6025\u5fd9\u626c\u7fc5\u4e0a\u98de\u3002\u72c4\u4e91\u5927\u6012\uff0c\u559d\u9053\uff1a\u201c\u8fde\u4f60\u8fd9\u755c\u751f\u4e5f\u6765\u6b3a\u4fae\u6211\uff01\u201d\u53f3\u638c\u594b\u529b\u51fb\u51fa\u3002\u90a3\u9e70\u79bb\u4ed6\u8eab\u5b50\u53ea\u6709\u6570\u5c3a\uff0c\u88ab\u638c\u529b\u6240\u9707\uff0c\u767b\u65f6\u6bdb\u7fbd\u7eb7\u98de\uff0c\u843d\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u72c4\u4e91\u4e00\u628a\u6293\u8d77\uff0c\u54c8\u54c8\u5927\u7b11\uff0c\u4e00\u53e3\u54ac\u5728\u9e70\u8179\uff0c\u90a3\u9e70\u53cc\u7fc5\u4e71\u6251\uff0c\u6781\u529b\u6323\u624e\u3002\u72c4\u4e91\u53ea\u89c9\u54b8\u54b8\u7684\u9e70\u8840\u4e0d\u4f4f\u6d41\u5165\u5634\u4e2d\uff0c\u4fbf\u5982\u4e00\u6ef4\u6ef4\u7cbe\u529b\u6d41\u5165\u4f53\u5185\uff0c\u5fcd\u4e0d\u4f4f\u624b\u821e\u8db3\u8e48\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u60f3\u5403\u6211\uff1f\u6211\u5148\u5403\u4e86\u4f60\uff0c\u6211\u5403\u4e86\u4f60\u3002\u201d \u3000\u3000\u82b1\u94c1\u5e72\u548c\u6c34\u7b19\u89c1\u5230\u4ed6\u8fd9\u7b49\u751f\u5403\u6d3b\u9e70\u7684\u75af\u72b6\uff0c\u90fd\u662f\u9a87\u7136\u53d8\u8272\u3002 \u3000\u3000\u82b1\u94c1\u5e72\u751f\u6015\u8fd9\u75af\u5b50\u72c2\u6027\u5927\u53d1\uff0c\u968f\u65f6\u4f1a\u8fc7\u6765\u8ddf\u81ea\u5df1\u62da\u547d\uff0c\u7ed9\u4ed6\u4e00\u628a\u62b1\u4f4f\u90a3\u53ef\u7cdf\u7cd5\uff0c\u8fd8\u662f\u8fdc\u800c\u907f\u4e4b\u7684\u4e3a\u5999\u3002\u5f53\u4e0b\u7ed5\u5230\u96ea\u8c37\u4e1c\u9996\uff0c\u5fc3\u60f3\u8fd9\u75af\u5b50\u6349\u9e70\u4e4b\u6cd5\u5012\u662f\u4e0d\u9519\uff0c\u5f53\u4e0b\u4ef0\u5367\u5728\u5730\uff0c\u8981\u60f3\u4f9d\u6837\u846b\u82a6\uff0c\u88c5\u6b7b\u6349\u9e70\u3002\u5c82\u77e5\u5140\u9e70\u867d\u7136\u4e0a\u5f53\uff0c\u4e0b\u6765\u5544\u98df\uff0c\u4f46\u4ed6\u6325\u638c\u51fb\u53bb\uff0c\u5374\u6ca1\u80fd\u5c06\u9e70\u51fb\u843d\u3002\u4ed6\u5185\u529b\u548c\u72c4\u4e91\u76f8\u5dee\u751a\u8fdc\uff0c\u638c\u6cd5\u867d\u7136\u5de7\u5999\uff0c\u53ef\u662f\u82cd\u9e70\u95ea\u907f\u7075\u52a8\uff0c\u5374\u66f4\u52a0\u8fc5\u6377\u5f97\u591a\u3002 \u3000\u3000\u72c4\u4e91\u559d\u4e86\u51e0\u53e3\u9e70\u8840\uff0c\u80f8\u4e2d\u8179\u4e2d\u6c14\u8840\u7ffb\u6d8c\uff0c\u53c8\u6655\u4e86\u8fc7\u53bb\u3002\u5f85\u5f97\u8f6c\u9192\u65f6\uff0c\u5929\u8272\u5df2\u660e\uff0c\u8179\u4e2d\u9965\u997f\uff0c\u968f\u624b\u62ff\u8d77\u8eab\u8fb9\u7684\u6b7b\u9e70\u4fbf\u54ac\uff0c\u4e00\u53e3\u54ac\u4e86\uff0c\u731b\u89c9\u5165\u53e3\u82b3\u9999\uff0c\u6ecb\u5473\u751a\u7f8e\uff0c\u51dd\u76ee\u4e00\u770b\uff0c\u4e0d\u7531\u5f97\u5446\u4e86\u3002\u4f46\u89c1\u90a3\u9e70\u5168\u8eab\u7fbd\u6bdb\u62d4\u5f97\u5e72\u5e72\u51c0\u51c0\uff0c\u7adf\u662f\u7099\u719f\u4e86\u7684\u3002\u4ed6\u660e\u660e\u8bb0\u5f97\u53ea\u559d\u4e86\u51e0\u53e3\u9e70\u8840\uff0c\u4fbf\u5373\u7761\u7740\uff0c\u5374\u662f\u8c01\u7ed9\u4ed6\u70e4\u719f\u4e86\uff1f\u82e5\u4e0d\u662f\u6c34\u7b19\uff0c\u96be\u9053\u8fd8\u4f1a\u662f\u82b1\u94c1\u5e72\u8fd9\u574f\u86cb\uff1f \u3000\u3000\u4ed6\u6628\u665a\u5927\u547c\u5927\u53eb\u4e00\u9635\uff0c\u80f8\u4e2d\u90c1\u79ef\u7684\u95f7\u6c14\u5ba3\u6cc4\u4e86\u4e0d\u5c11\uff0c\u8fd9\u65f6\u9192\u8f6c\uff0c\u9887\u89c9\u8212\u7545\uff0c\u89c1\u6c34\u5cb1\u7684\u96ea\u575f\u5df2\u91cd\u884c\u5806\u597d\uff0c\u5411\u5c71\u6d1e\u671b\u53bb\uff0c\u53ea\u89c1\u6c34\u7b19\u4f0f\u5728\u5ca9\u77f3\u4e4b\u4e0a\uff0c\u6c89\u7761\u672a\u9192\u3002\u72c4\u4e91\u5fc3\u60f3\uff1a\u201c\u5979\u4e5f\u997f\u4e86\u51e0\u5929\u5566\uff0c\u70e4\u4e86\u8fd9\u53ea\u9e70\u5c3d\u6570\u7559\u7ed9\u6211\uff0c\u81ea\u5df1\u4e00\u6761\u9e70\u817f\u4e5f\u4e0d\u5403\uff0c\u603b\u7b97\u96be\u5f97\u3002\u54fc\uff0c\u5979\u81ea\u4ee5\u4e3a\u662f\u5927\u4fa0\u7684\u5343\u91d1\u5c0f\u59d0\uff0c\u77a7\u6211\u4e0d\u8d77\u3002\u4f60\u77a7\u6211\u4e0d\u8d77\uff0c\u6211\u4e5f\u77a7\u4e0d\u8d77\u4f60\uff0c\u6709\u751a\u4e48\u5e0c\u7f55\uff1f\u201d\u4f46\u8fc7\u4e86\u4e00\u4f1a\uff0c\u4e0d\u7981\u53c8\u60f3\uff1a \u3000\u3000\u201c\u5979\u66ff\u6211\u70e4\u9e70\uff0c\u8fd8\u4e0d\u7b97\u5982\u4f55\u77a7\u6211\u4e0d\u8d77\uff0c\u997f\u6b7b\u4e86\u5979\uff0c\u90a3\u4e5f\u4e0d\u597d\u3002\u201d \u3000\u3000\u4e8e\u662f\u4ed6\u8eba\u5728\u5730\u4e0b\uff0c\u4e00\u52a8\u4e0d\u52a8\uff0c\u95ed\u76ee\u88c5\u6b7b\uff0c\u534a\u4e2a\u65f6\u8fb0\u4e4b\u95f4\uff0c\u4ee5\u638c\u529b\u63a5\u8fde\u9707\u6b7b\u4e86\u56db\u5934\u5140\u9e70\uff0c\u5c06\u4e24\u5934\u63b7\u7ed9\u4e86\u6c34\u7b19\u3002\u6c34\u7b19\u8fc7\u6765\u5c06\u53e6\u5916\u4e24\u5934\u4e5f\u90fd\u62ff\u4e86\u8fc7\u53bb\uff0c\u6d17\u5265\u5e72\u51c0\uff0c\u4e00\u8d77\u70e7\u70e4\u597d\u4e86\uff0c\u9ed8\u9ed8\u65e0\u8a00\u7684\u628a\u4e24\u5934\u719f\u9e70\u4ea4\u7ed9\u4ed6\u3002 \u3000\u3000\u96ea\u8c37\u4e2d\u5140\u9e70\u4e0d\u5c11\uff0c\u504f\u53c8\u8822\u5f97\u5389\u5bb3\uff0c\u773c\u89c1\u540c\u4f34\u63a5\u8fde\u4e27\u751f\u5728\u72c4\u4e91\u638c\u4e0b\uff0c\u5374\u4ecd\u662f\u4e0d\u65ad\u7684\u4e0b\u6765\u9001\u6b7b\u3002\u72c4\u4e91\u5185\u529b\u65e5\u589e\uff0c\u638c\u529b\u4ea6\u65e5\u52b2\uff0c\u5230\u5f97\u540e\u6765\uff0c\u5df2\u4e0d\u7528\u8eba\u4e0b\u88c5\u6b7b\uff0c\u53ea\u8981\u89c1\u6709\u98de\u79bd\u5728\u6811\u679d\u4f4e\u5904\u6816\u6b47\uff0c\u6216\u662f\u4ece\u8eab\u65c1\u98de\u8fc7\uff0c\u4fbf\u80fd\u53d1\u638c\u51fb\u843d\u3002\u96ea\u8c37\u4e2d\u65f6\u6709\u96ea\u96c1\u51fa\u6ca1\uff0c\u80fd\u5728\u51b0\u96ea\u4e2d\u5544\u98df\u866b\u8681\uff0c\u8eaf\u4f53\u751a\u80a5\uff0c\u66f4\u662f\u72c4\u4e91\u548c\u6c34\u7b19\u65e5\u5e38\u7684\u53e3\u4e2d\u7f8e\u98df\u3002 \u3000\u3000\u5c48\u6307\u814a\u6708\u5c06\u5c3d\uff0c\u96ea\u8c37\u4e2d\u6bcf\u8fc7\u4e0d\u4e86\u5341\u5929\u516b\u5929\u4fbf\u6709\u4e00\u573a\u5927\u96ea\uff0c\u6574\u65e5\u6574\u591c\u7684\u5bd2\u98ce\u522e\u4eba\u5982\u5200\u3002 \u3000\u3000\u6c34\u7b19\u9664\u4e86\u6361\u62fe\u67f4\u679d\uff0c\u70e7\u70e4\u9e1f\u8089\uff0c\u603b\u662f\u8eb2\u5728\u5c71\u6d1e\u4e4b\u4e2d\u3002\u72c4\u4e91\u59cb\u7ec8\u4e0d\u8ddf\u5979\u4ea4\u8c08\u4e00\u8a00\u4e00\u8bed\uff0c\u4e5f\u4ece\u4e0d\u8e0f\u8fdb\u5c71\u6d1e\u4e00\u6b65\u3002 \u3000\u3000\u6709\u4e00\u665a\u5f7b\u591c\u5927\u96ea\uff0c\u6b21\u65e5\u6e05\u6668\u72c4\u4e91\u9192\u6765\uff0c\u89c9\u5f97\u8eab\u4e0a\u6696\u6d0b\u6d0b\u7684\uff0c\u4e00\u7741\u773c\uff0c\u53ea\u89c1\u4e00\u4ef6\u9ed1\u9edd\u9edd\u7684\u4e1c\u897f\u76d6\u5728\u81ea\u5df1\u8eab\u4e0a\u3002\u4ed6\u5403\u4e86\u4e00\u60ca\uff0c\u968f\u624b\u4e00\u6296\uff0c\u7adf\u662f\u4e00\u4ef6\u53e4\u602a\u7684\u8863\u88f3\u3002\u8fd9\u8863\u88f3\u662f\u7528\u9e1f\u6bdb\u4e00\u7247\u7247\u7684\u7a7f\u6210\uff0c\u9ed1\u7684\u662f\u9e70\u6bdb\uff0c\u767d\u7684\u662f\u96c1\u7fce\uff0c\u8863\u957f\u9f50\u819d\uff0c\u4e0d\u77e5\u7528\u4e86\u51e0\u5343\u51e0\u4e07\u6839\u9e1f\u7fbd\u3002 \u3000\u3000\u72c4\u4e91\u63d0\u7740\u8fd9\u4ef6\u7fbd\u8863\uff0c\u7a81\u7136\u95f4\u6ee1\u8138\u901a\u7ea2\uff0c\u77e5\u9053\u8fd9\u81ea\u662f\u6c34\u7b19\u6240\u5236\uff0c\u8981\u5c06\u8fd9\u5343\u5343\u4e07\u4e07\u6839\u9e1f\u7fbd\u7f00\u800c\u6210\u8863\uff0c\u5f53\u771f\u662f\u715e\u8d39\u82e6\u5fc3\u3002\u4f55\u51b5\u96ea\u8c37\u4e2d\u6ca1\u526a\u5200\u9488\u7ebf\uff0c\u4e0d\u77e5\u5982\u4f55\u7f00\u6210\uff1f\u4ed6\u4f38\u624b\u62e8\u5f00\u8863\u4e0a\u7684\u9e1f\u7fbd\u4e00\u770b\uff0c\u53ea\u89c1\u6bcf\u6839\u7fbd\u6bdb\u7684\u6839\u90e8\u90fd\u7a7f\u4e86\u4e00\u4e2a\u7ec6\u5b54\uff0c\u60f3\u5fc5\u662f\u7528\u5934\u53d1\u4e0a\u7684\u91d1\u9497\u523a\u51fa\uff0c\u5b54\u4e2d\u7a7f\u4e86\u6de1\u9ec4\u7684\u4e1d\u7ebf\uff0c\u81ea\u7136\u662f\u4ece\u5979\u90a3\u4ef6\u6de1\u9ec4\u7684\u7f0e\u886b\u4e0a\u62bd\u4e0b\u6765\u7684\u4e86\u3002\u201c\u563f\u563f\uff0c\u5973\u5a18\u513f\u4eec\u771f\u662f\u5947\u602a\uff0c\u8fd9\u53ef\u6709\u591a\u7d2f\uff0c\u90a3\u4e0d\u662f\u9ebb\u70e6\u4e4b\u6781\u4e48\uff1f\u201d \u3000\u3000\u7a81\u7136\u4e4b\u95f4\uff0c\u60f3\u8d77\u4e86\u51e0\u5e74\u524d\u5728\u8346\u5dde\u57ce\u4e07\u9707\u5c71\u5bb6\u4e2d\u7684\u4e8b\u6765\u3002\u90a3\u665a\u4ed6\u7ed9\u4e07\u95e8\u516b\u5f1f\u5b50\u56f4\u653b\uff0c\u6253\u5f97\u773c\u9752\u9f3b\u80bf\u662f\u4e0d\u7528\u8bf4\u4e86\uff0c\u4e00\u4ef6\u65b0\u8863\u4e5f\u7ed9\u6495\u70c2\u4e86\u597d\u51e0\u5904\u3002\u4ed6\u5fc3\u4e2d\u75db\u60dc\uff0c\u5e08\u59b9\u621a\u82b3\u4fbf\u62ff\u4e86\u9488\u7ebf\u66ff\u81ea\u5df1\u7f1d\u8865\u3002 \u3000\u3000\u8111\u6d77\u4e2d\u6e05\u6e05\u695a\u695a\u7684\u51fa\u73b0\u4e86\u90a3\u4e00\u65e5\u7684\u60c5\u666f\uff1a\u621a\u82b3\u6328\u5728\u4ed6\u7684\u8eab\u8fb9\uff0c\u7ed9\u4ed6\u7f1d\u8865\u8863\u886b\u3002\u5979\u5934\u53d1\u64e6\u5728\u81ea\u5df1\u7684\u4e0b\u5df4\uff0c\u4ed6\u53ea\u89c9\u8138\u4e0a\u75d2\u75d2\u7684\uff0c\u9f3b\u4e2d\u95fb\u5230\u5979\u5c11\u5973\u7684\u6de1\u6de1\u808c\u80a4\u4e4b\u9999\uff0c\u4e0d\u7531\u5f97\u5fc3\u795e\u8361\u6f3e\u3002 \u3000\u3000\u72c4\u4e91\u53eb\u4e86\u58f0\uff1a\u201c\u5e08\u59b9\u3002\u201d\u621a\u82b3\u9053\uff1a\u201c\u7a7a\u5fc3\u83dc\uff0c\u522b\u8bf4\u8bdd\uff0c\u522b\u8ba9\u4eba\u51a4\u6789\u4f60\u4f5c\u8d3c\u3002\u201d \u3000\u3000\u4ed6\u60f3\u5230\u8fd9\u91cc\uff0c\u5589\u5934\u4f3c\u4e4e\u6709\u751a\u4e48\u4e1c\u897f\u585e\u7740\uff0c\u6cea\u6c34\u6d8c\u5411\u773c\u4e2d\uff0c\u77a7\u51fa\u6765\u53ea\u662f\u6a21\u7cca\u4e00\u56e2\uff0c\u5fc3\u60f3\uff1a\u201c\u679c\u7136\u4eba\u5bb6\u51a4\u6789\u6211\u4f5c\u8d3c\u3002\u96be\u9053\u662f\u56e0\u4e3a\u5e08\u59b9\u7ed9\u6211\u7f1d\u8865\u8863\u670d\u4e4b\u65f6\uff0c\u6211\u8bf4\u4e86\u8bdd\u4e48\uff1f\u201d\u4f46\u8fd9\u6570\u5e74\u4e2d\u4ed6\u591a\u5386\u98ce\u6ce2\u9669\u6076\uff0c\u65e9\u5df2\u4e0d\u518d\u4fe1\u8fd9\u7b49\u65e0\u7a3d\u4e4b\u6de1\u3002\u201c\u563f\u563f\uff0c\u4eba\u5bb6\u5b58\u5fc3\u8981\u5bb3\u6211\uff0c\u6211\u4fbf\u5929\u751f\u662f\u4e2a\u54d1\u5df4\uff0c\u522b\u4eba\u8fd8\u4e0d\u662f\u4e00\u6837\u7684\u6765\u6b3a\u4fae\uff1f\u5e08\u59b9\u90a3\u65f6\u5019\u5f85\u6211\u4e00\u7247\u771f\u8bda\uff0c\u53ef\u662f\u59d3\u4e07\u7684\u5bb6\u8d22\u8c6a\u5bcc\uff0c\u4e07\u572d\u90a3\u5c0f\u5b50\u53c8\u6bd4\u6211\u4fca\u5f97\u591a\uff0c\u90a3\u6709\u751a\u4e48\u53ef\u8bf4\u7684\uff1f\u6700\u4e0d\u8be5\u662f\u6211\u90a3\u65e5\u8eab\u53d7\u91cd\u4f24\uff0c\u8eb2\u5728\u5979\u5bb6\u67f4\u623f\u4e4b\u4e2d\uff0c\u5979\u5374\u4f1a\u53bb\u544a\u77e5\u5979\u4e08\u592b\uff0c\u53eb\u4ed6\u6765\u64d2\u4e86\u6211\u53bb\u9886\u529f\uff0c\u54c8\u54c8\uff0c\u54c8\u54c8\uff01\u201d \u3000\u3000\u7a81\u7136\u4e4b\u95f4\uff0c\u4ed6\u7eb5\u58f0\u72c2\u7b11\u8d77\u6765\uff0c\u62ff\u7740\u7fbd\u8863\uff0c\u8d70\u5230\u77f3\u6d1e\u4e4b\u524d\uff0c\u629b\u5728\u5730\u4e0b\uff0c\u5728\u7fbd\u8863\u4e0a\u7528\u529b\u8e0f\u4e86\u51e0\u811a\uff0c\u5927\u58f0\u9053\uff1a\u201c\u6211\u662f\u6076\u548c\u5c1a\uff0c\u600e\u914d\u7a7f\u5c0f\u59d0\u7f1d\u7684\u8863\u670d\uff1f\u201d\u98de\u8d77\u4e00\u811a\uff0c\u5c06\u7fbd\u8863\u8e4b\u8fdb\u6d1e\u4e2d\uff0c\u8f6c\u8eab\u72c2\u7b11\uff0c\u5927\u8e0f\u6b65\u800c\u53bb\u3002 \u3000\u3000\u6c34\u7b19\u8d39\u4e86\u4e00\u4e2a\u591a\u6708\u65f6\u5149\uff0c\u624d\u5c06\u8fd9\u4ef6\u7fbd\u8863\u7f00\u6210\uff0c\u5fc3\u60f3\u8fd9\u201c\u5c0f\u6076\u50e7\u201d\u7ef4\u62a4\u7239\u7239\u7684\u5c38\u4f53\uff0c\u4e1d\u6beb\u4e0d\u5411\u81ea\u5df1\u7f57\u5523\uff0c\u8fd9\u4e9b\u65e5\u5b50\u4e2d\uff0c\u81ea\u5df1\u5168\u4ed7\u5403\u4ed6\u6253\u6765\u7684\u9e1f\u8089\u4e3a\u751f\u3002\u773c\u89c1\u4ed6\u65e5\u591c\u5728\u6d1e\u5916\u6328\u53d7\u98ce\u5bd2\uff0c\u5fc3\u4e0b\u5b9e\u611f\u4e0d\u5fcd\uff0c\u76fc\u671b\u8fd9\u4ef6\u7fbd\u8863\u80fd\u52a9\u4ed6\u5fa1\u5bd2\u3002\u54ea\u77e5\u9053\u597d\u5fc3\u4e0d\u5f97\u597d\u62a5\uff0c\u53cd\u7ed9\u4ed6\u5c06\u7fbd\u8863\u8e22\u8fdb\u6d1e\u6765\uff0c\u53d7\u4ed6\u5982\u6b64\u65e0\u793c\u7684\u4fae\u8fb1\u3002\u5979\u53c8\u7f9e\u53c8\u6012\uff0c\u4f38\u624b\u5c06\u7fbd\u8863\u4e00\u9635\u4e71\u626f\uff0c\u60c5\u4e0d\u81ea\u7981\uff0c\u773c\u6cea\u4e00\u6ef4\u6ef4\u7684\u843d\u5728\u9e1f\u7fbd\u4e4b\u4e0a\u3002 \u3000\u3000\u5979\u5374\u4e07\u4e07\u6599\u60f3\u4e0d\u5230\uff0c\u72c4\u4e91\u8f6c\u8eab\u72c2\u7b11\u4e4b\u65f6\uff0c\u80f8\u524d\u8863\u895f\u4e0a\u4e5f\u662f\u6e85\u6ee1\u4e86\u6ef4\u6ef4\u6cea\u6c34\uff0c\u53ea\u662f\u4ed6\u6d41\u6cea\u5374\u662f\u4e3a\u4e86\u4f24\u5fc3\u81ea\u5df1\u547d\u82e6\u3002\u4e3a\u4e86\u5e08\u59b9\u7684\u65e0\u60c5\u65e0\u4e49\u2026\u2026 \u3000\u3000\u4e2d\u5348\u65f6\u5206\uff0c\u72c4\u4e91\u6253\u4e86\u56db\u53ea\u9e1f\u96c0\uff0c\u4ecd\u53bb\u653e\u5728\u5c71\u6d1e\u524d\uff0c\u6c34\u7b19\u70e4\u719f\u4e86\uff0c\u4ecd\u662f\u5206\u4e86\u4e00\u534a\u7ed9\u4ed6\u3002\u4e24\u4eba\u4e00\u53e5\u8bdd\u4e5f\u4e0d\u8bf4\uff0c\u751a\u81f3\uff0c\u8fde\u773c\u5149\u4e5f\u4e0d\u6562\u76f8\u5bf9\u3002 \u3000\u3000\u72c4\u4e91\u548c\u6c34\u7b19\u5750\u5f97\u8fdc\u8fdc\u5730\uff0c\u5404\u81ea\u5403\u7740\u719f\u9e1f\uff0c\u5ffd\u7136\u95f4\u4e1c\u5317\u89d2\u4e0a\u4f20\u6765\u4e00\u9635\u8e0f\u96ea\u4e4b\u58f0\u3002\u4e24\u4eba\u4e00\u9f50\u62ac\u8d77\u5934\u6765\uff0c\u5411\u58f0\u97f3\u6765\u5904\u671b\u53bb\uff0c\u53ea\u89c1\u82b1\u94c1\u5e72\u53f3\u624b\u62ff\u7740\u4e00\u67c4\u9b3c\u5934\u5200\uff0c\u5de6\u624b\u63e1\u7740\u4e00\u67c4\u957f\u5251\uff0c\u7b11\u563b\u563b\u7684\u8d70\u6765\u3002\u72c4\u4e91\u548c\u6c34\u7b19\u540c\u65f6\u8dc3\u8d77\uff0c\u6c34\u7b19\u8fd4\u8eab\u5165\u6d1e\uff0c\u62a2\u8fc7\u4e86\u8840\u5200\uff0c\u5fae\u4e00\u72b9\u8c6b\uff0c\u4fbf\u629b\u7ed9\u4e86\u72c4\u4e91\uff0c\u53eb\u9053\uff1a\u201c\u63a5\u4f4f\uff01\u201d \u3000\u3000\u72c4\u4e91\u4f38\u624b\u63a5\u5200\uff0c\u5fc3\u4e2d\u4e00\u6014\uff1a\u201c\u5979\u600e\u5730\u5982\u6b64\u4fe1\u5f97\u8fc7\u6211\uff0c\u5c06\u8fd9\u6027\u547d\u822c\u7684\u5b9d\u5200\u7ed9\u4e86\u6211\uff1f\u55ef\uff0c\u5979\u662f\u8981\u6211\u66ff\u5979\u5356\u547d\uff0c\u52a9\u5979\u62b5\u5fa1\u82b1\u94c1\u5e72\uff0c\u54fc\uff0c\u54fc\uff01\u59d3\u72c4\u7684\u53c8\u4e0d\u662f\u4f60\u7684\u5974\u624d\uff01\u201d \u3000\u3000\u4fbf\u5728\u8fd9\u65f6\uff0c\u82b1\u94c1\u5e72\u5df2\u5feb\u6b65\u8d70\u5230\u4e86\u8fd1\u5904\uff0c\u54c8\u54c8\u5927\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u606d\u559c\uff0c\u606d\u559c\uff01\u201d\u72c4\u4e91\u77aa\u76ee\u9053\uff1a\u201c\u606d\u751a\u4e48\u559c\uff1f\u201d\u82b1\u94c1\u5e72\u9053\uff1a\u201c\u606d\u559c\u4f60\u548c\u6c34\u59d1\u5a18\u6210\u5c31\u4e86\u597d\u4e8b\u54ea\u3002\u4eba\u5bb6\u8fde\u9632\u8eab\u5b9d\u5200\u4e5f\u7ed9\u4e86\u4f60\uff0c\u522b\u7684\u8fd8\u4e0d\u4e00\u53e4\u8111\u513f\u7684\u90fd\u7ed9\u4e86\u4f60\u4e48\uff1f\u54c8\u54c8\uff0c\u54c8\u54c8\uff01\u201d\u72c4\u4e91\u6012\u9053\uff1a\u201c\u6789\u4f60\u53f7\u79f0\u4e3a\u4e2d\u539f\u5927\u4fa0\uff0c\u5374\u662f\u4e2a\u5982\u6b64\u5351\u9119\u80ae\u810f\u7684\u5c0f\u4eba\uff01\u201d \u3000\u3000\u82b1\u94c1\u5e72\u7b11\u563b\u563b\u7684\u9053\uff1a\u201c\u8bf4\u5230\u5351\u9119\u65e0\u803b\uff0c\u4f60\u8840\u5200\u95e8\u4e2d\u7684\u4eba\u7269\u672a\u5fc5\u5c31\u8f93\u4e8e\u533a\u533a\u5728\u4e0b\u3002\u201d\u8bf4\u7740\u6162\u6162\u8feb\u8fd1\uff0c\u7528\u529b\u55c5\u4e86\u51e0\u4e0b\uff0c\u8bf4\u9053\uff1a\u201c\u55ef\uff0c\u597d\u9999\uff0c\u597d\u9999\uff01\u9001\u4e00\u53ea\u9e1f\u6211\u5403\uff0c\u6210\u4e0d\u6210\uff1f\u201d\u4ed6\u82e5\u5584\u8a00\u76f8\u6c42\uff0c\u72c4\u4e91\u81ea\u5fc5\u7b54\u5141\uff0c\u4f46\u8fd9\u65f6\u89c1\u4ed6\u4e00\u526f\u60eb\u61d2\u8f7b\u8584\u7684\u6a21\u6837\uff0c\u5fc3\u4e0b\u7740\u607c\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u6b66\u529f\u6bd4\u6211\u9ad8\u5f97\u591a\uff0c\u81ea\u5df1\u4e0d\u4f1a\u6253\u4e48\uff1f\u201d\u82b1\u94c1\u5e72\u7b11\u9053\uff1a\u201c\u6211\u5c31\u662f\u61d2\u5f97\u6253\u3002\u201d \u3000\u3000\u4ed6\u4e8c\u4eba\u8bf4\u8bdd\u4e4b\u9645\uff0c\u6c34\u7b19\u5df2\u8d70\u5230\u4e86\u72c4\u4e91\u80cc\u540e\uff0c\u7a81\u7136\u5927\u58f0\u53eb\u9053\uff1a\u201c\u5218\u4f2f\u4f2f\uff0c\u9646\u4f2f\u4f2f\uff01\u201d\u5979\u89c1\u82b1\u94c1\u5e72\u53cc\u624b\u62ff\u7740\u5218\u4e58\u98ce\u7684\u957f\u5251\u548c\u9646\u5929\u6292\u7684\u9b3c\u5934\u5200\uff0c\u5317\u98ce\u98d8\u52a8\uff0c\u5439\u5f00\u4ed6\u5916\u888d\uff0c\u9732\u51fa\u888d\u5185\u8fd8\u7a7f\u7740\u5218\u4e58\u98ce\u7684\u9053\u888d\u548c\u9646\u5929\u6292\u7684\u7d2b\u94dc\u8272\u957f\u888d\u3002 \u3000\u3000\u82b1\u94c1\u5e72\u6c89\u7740\u8138\u9053\uff1a\u201c\u600e\u4e48\u6837\uff1f\u201d\u6c34\u7b19\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u2026\u2026\u4f60\u5403\u4e86\u4ed6\u4eec\u4e48\uff1f\u201d\u5979\u6599\u60f3\u82b1\u94c1\u5e72\u65e2\u5bfb\u5230\u4e86\u4e8c\u4eba\u5c38\u4f53\uff0c\u591a\u534a\u662f\u5c06\u4ed6\u4e8c\u4eba\u5403\u3002\u82b1\u94c1\u5e72\u6012\u9053\uff1a\u201c\u5173\u4f60\u751a\u4e48\u4e8b\uff1f\u201d\u6c34\u7b19\u5927\u60ca\uff0c\u98a4\u58f0\u9053\uff1a\u201c\u9646\u4f2f\u4f2f\uff0c\u5218\u4f2f\u4f2f\uff0c\u4ed6\u2026\u2026\u4ed6\u4e8c\u4eba\u662f\u4f60\u7684\u7ed3\u4e49\u5144\u5f1f\u2026\u2026\u201d \u3000\u3000\u82b1\u94c1\u5e72\u82e5\u6709\u80fd\u8010\u6253\u9e1f\uff0c\u81ea\u7136\u51b3\u4e0d\u4f1a\u4ee5\u4e49\u5144\u5f1f\u7684\u5c38\u4f53\u4e3a\u98df\uff0c\u4f46\u4ed6\u5343\u65b9\u767e\u8ba1\u7684\u6355\u6349\u9e1f\u96c0\uff0c\u521d\u65f6\u8fd8\u6349\u5230\u4e00\u4e24\u5934\uff0c\u8fc7\u5f97\u51e0\u5929\uff0c\u9e1f\u96c0\u518d\u4e0d\u4e0a\u5f53\u3002\u4ed6\u53c8\u65e0\u72c4\u4e91\u7684\u795e\u7167\u529f\u5185\u52b2\uff0c\u80fd\u4ee5\u638c\u529b\u51fb\u9e1f\u3002\u8fd9\u4e00\u65e5\u4ed6\u5403\u5b8c\u4e86\u9646\u3001\u5218\u4e8c\u4eba\u7684\u5c38\u4f53\u540e\uff0c\u624b\u6301\u5200\u5251\uff0c\u51b3\u610f\u6765\u6740\u72c4\u6c34\u4e8c\u4eba\uff0c\u518d\u52a0\u4e0a\u57cb\u85cf\u5728\u51b0\u96ea\u4e2d\u7684\u6c34\u5cb1\u548c\u8840\u5200\u8001\u7956\u7684\u5c38\u4f53\uff0c\u4ee5\u6b64\u4e3a\u98df\uff0c\u5f53\u53ef\u6328\u5230\u521d\u590f\uff0c\u9759\u5f85\u96ea\u878d\u51fa\u8c37\u3002 \u3000\u3000\u8fd9\u65f6\u4ed6\u542c\u6c34\u7b19\u5982\u6b64\u8bf4\uff0c\u4e0d\u81ea\u7981\u7684\u6ee1\u8138\u901a\u7ea2\uff0c\u53c8\u95fb\u5230\u70e4\u719f\u4e86\u7684\u9e1f\u8089\u9999\u6c14\uff0c\u998b\u6d8e\u6b32\u6ef4\uff0c\u7a81\u7136\u95f4\u4e3e\u8d77\u9b3c\u5934\u5200\uff0c\u5927\u547c\u8dc3\u8fdb\uff0c\u5411\u72c4\u4e91\u780d\u8fc7\u6765\uff0c\u5de6\u5288\u4e00\u5200\uff0c\u53f3\u5288\u4e00\u5200\u3002\u72c4\u4e91\u4e3e\u8d77\u8840\u5200\u4e00\u683c\uff0c\u5f53\u7684\u4e00\u58f0\u731b\u54cd\uff0c\u9b3c\u5934\u5200\u5411\u4e0a\u53cd\u5f39\u3002\u8fd9\u9b3c\u5934\u5200\u4e5f\u662f\u4e00\u67c4\u5b9d\u5200\uff0c\u867d\u4e0d\u53ca\u8840\u5200\u7684\u950b\u5229\u7edd\u4f26\uff0c\u4f46\u5200\u8eab\u539a\u91cd\uff0c\u8840\u5200\u4e5f\u524a\u5b83\u4e0d\u65ad\u3002\u5f53\u65e5\u9646\u5929\u6292\u548c\u8840\u5200\u50e7\u53cc\u5200\u76f8\u4ea4\uff0c\u9b3c\u5934\u5200\u66fe\u88ab\u8840\u5200\u65a9\u4e86\u4e09\u4e2a\u7f3a\u53e3\uff0c\u4eca\u65e5\u518d\u5ea6\u76f8\u9022\uff0c\u9b3c\u5934\u5200\u4e0a\u4e5f\u4e0d\u8fc7\u662f\u65b0\u6dfb\u4e00\u4e2a\u7f3a\u53e3\u800c\u5df2\u3002 \u3000\u3000\u82b1\u94c1\u5e72\u7528\u5200\u867d\u4e0d\u64c5\u957f\uff0c\u4f46\u6b66\u529f\u9ad8\u5f3a\uff0c\u9b3c\u5934\u5200\u4f7f\u5c06\u5f00\u6765\uff0c\u81ea\u975e\u72c4\u4e91\u6240\u80fd\u62b5\u6321\uff0c\u6570\u62db\u4e4b\u4e0b\uff0c\u767b\u65f6\u5c06\u4ed6\u8feb\u5f97\u8fde\u8fde\u540e\u9000\u3002\u82b1\u94c1\u5e72\u4e5f\u4e0d\u8ffd\u51fb\uff0c\u4e00\u4fef\u8eab\uff0c\u62fe\u8d77\u72c4\u4e91\u5403\u5269\u7684\u534a\u53ea\u719f\u9e1f\uff0c\u5927\u56bc\u8d77\u6765\uff0c\u8fde\u8d5e\uff1a\u201c\u5f88\u597d\uff0c\u5f88\u597d\uff0c\u6ecb\u5473\u8981\u5f97\uff0c\u786c\u662f\u8981\u5f97\uff01\u201d \u3000\u3000\u72c4\u4e91\u56de\u5934\u5411\u6c34\u7b19\u671b\u4e86\u4e00\u773c\uff0c\u4e24\u4eba\u90fd\u89c9\u5bd2\u5fc3\u3002\u82b1\u94c1\u5e72\u8fd9\u6b21\u624b\u6301\u5229\u5668\u524d\u6765\u6311\u6218\uff0c\u60c5\u52bf\u4fbf\u548c\u4e0a\u6b21\u4e0d\u540c\u3002\u7a7a\u624b\u76f8\u640f\u4e4b\u65f6\uff0c\u72c4\u4e91\u53d7\u4ed6\u62f3\u6253\u8db3\u8e22\uff0c\u4e0d\u8fc7\u53d7\u4f24\u5410\u8840\uff0c\u4e0d\u6613\u7ed9\u4ed6\u4e00\u62f3\u6253\u6b7b\uff0c\u8fd9\u65f6\u4ed6\u624b\u4e2d\u6709\u4e86\u5200\u5251\uff0c\u53ea\u987b\u6709\u4e00\u62db\u5931\u624b\uff0c\u7acb\u65f6\u4fbf\u9001\u4e86\u6027\u547d\u3002\u4e0a\u6b21\u76f8\u6597\u6240\u4ee5\u80fd\u52c9\u5f3a\u652f\u6301\uff0c\u5168\u4ed7\u6c34\u7b19\u624b\u4e2d\u591a\u4e86\u4e00\u628a\u8840\u5200\uff0c\u6b64\u523b\u82b1\u94c1\u5e72\u7684\u5175\u5203\u8fd8\u591a\u4e86\u4e00\u4ef6\uff0c\u90a3\u662f\u5360\u5c3d\u4e0a\u98ce\u4e86\u3002 \u3000\u3000\u82b1\u94c1\u5e72\u5403\u4e86\u534a\u53ea\u719f\u9e1f\uff0c\u610f\u72b9\u672a\u5c3d\uff0c\u89c1\u5c71\u6d1e\u8fb9\u5c1a\u6709\u4e00\u53ea\uff0c\u53c8\u53bb\u62ff\u6765\u5403\u4e86\u3002\u4ed6\u62b9\u62b9\u5634\uff0c\u8bf4\u9053\uff1a\u201c\u5f88\u597d\uff0c\u70f9\u8c03\u529f\u592b\u662f\u4e00\u7b49\u4e00\u7684\u3002\u201d \u3000\u3000\u61d2\u6d0b\u6d0b\u7684\u56de\u8f6c\u8eab\u6765\uff0c\u9661\u7136\u95f4\u8dc3\u8eab\u800c\u524d\uff0c\u547c\u7684\u4e00\u5200\uff0c\u4fbf\u5411\u72c4\u4e91\u5288\u53bb\uff0c\u8fd9\u4e00\u5200\u53bb\u52bf\u5947\u6025\uff0c\u72c4\u4e91\u731d\u4e0d\u53ca\u9632\uff0c\u9669\u4e9b\u513f\u4fbf\u7ed9\u524a\u4e86\u534a\u8fb9\u8111\u888b\uff0c\u6025\u5fd9\u4e3e\u5200\u62db\u67b6\u3002\u603b\u7b97\u82b1\u94c1\u5e72\u5fcc\u60ee\u4ed6\u5185\u529f\u6d51\u539a\uff0c\u82e5\u662f\u53cc\u5200\u76f8\u4ea4\u4e0d\u514d\u624b\u81c2\u9178\u9ebb\uff0c\u5f53\u4e0b\u8f6c\u5200\u659c\u5288\u3002\u4e09\u62db\u4e4b\u95f4\uff0c\u72c4\u4e91\u5df2\u7136\u624b\u5fd9\u811a\u4e71\uff0c\u55e4\u7684\u4e00\u58f0\u54cd\uff0c\u5de6\u81c2\u4e0a\u7ed9\u9b3c\u5934\u5200\u5212\u4e86\u4e00\u9053\u957f\u957f\u7684\u53e3\u5b50\u3002 \u3000\u3000\u6c34\u7b19\u53eb\u9053\uff1a\u201c\u522b\u6253\u4e86\uff0c\u522b\u6253\u4e86\u3002\u82b1\u4f2f\u4f2f\uff0c\u6211\u5206\u9e1f\u8089\u7ed9\u4f60\u4fbf\u662f\u3002\u201d \u3000\u3000\u82b1\u94c1\u5e72\u89c1\u72c4\u4e91\u7684\u5200\u6cd5\u5e73\u5eb8\u4e4b\u6781\uff0c\u5728\u6b66\u6797\u4e2d\u8fde\u7b2c\u4e09\u6d41\u7684\u811a\u8272\u4e5f\u53ca\u4e0d\u4e0a\uff0c\u5fc3\u60f3\u53ca\u65e9\u6740\u4e86\u8fd9\u5c0f\u5b50\u518d\u8bf4\uff0c\u514d\u5f97\u53c8\u7559\u540e\u60a3\uff0c\u5f53\u4e0b\u624b\u4e0a\u52a0\u7d27\uff0c\u53e3\u4e2d\u5374\u8c03\u4f83\u9053\uff1a\u201c\u6c34\u4f84\u5973\uff0c\u4f60\u5fc3\u75bc\u8fd9\u5c0f\u5b50\uff0c\u662f\u4e0d\u662f\u554a\uff1f\u600e\u4e48\u4e0d\u8bb0\u5f97\u4f60\u7684\u6c6a\u5bb6\u8868\u54e5\u4e86\uff1f\u201d\u5237\u5237\u5237\u4e09\u5200\uff0c\u53c8\u5728\u72c4\u4e91\u7684\u53f3\u80a9\u4e0a\u780d\u4e86\u4e00\u5200\u3002\u5e78\u597d\u8fd9\u4e00\u5200\u6240\u780d\u7684\u90e8\u4f4d\u6709\u201c\u4e4c\u8695\u8863\u201d\u4fdd\u62a4\uff0c\u5426\u5219\u72c4\u4e91\u7684\u53f3\u80a9\u5df2\u7ed9\u5378\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u6c34\u7b19\u5927\u53eb\uff1a\u201c\u82b1\u4f2f\u4f2f\uff0c\u522b\u6253\u4e86\uff01\u201d \u3000\u3000\u72c4\u4e91\u6012\u9053\uff1a\u201c\u4f60\u53eb\u751a\u4e48\uff1f\u6211\u6253\u4e0d\u8fc7\uff0c\u7ed9\u4ed6\u6740\u4e86\u4fbf\u662f\u3002\u201d\u4ed6\u72c2\u6012\u4e4b\u4e0b\uff0c\u4e3e\u5200\u4e71\u780d\uff0c\u5ffd\u7136\u95f4\u53f3\u624b\u5c06\u8840\u5200\u4ea4\u7ed9\u5de6\u624b\uff0c\u53cd\u624b\u731b\u529b\u6253\u51fa\u3002 \u3000\u3000\u82b1\u94c1\u5e72\u54ea\u6599\u5230\u8fd9\u6b66\u827a\u4f4e\u5fae\u7684\u201c\u5c0f\u548c\u5c1a\u201d\u5c45\u7136\u4f1a\u5947\u5175\u7a81\u51fa\uff0c\u84e6\u5730\u6765\u8fd9\u4e00\u4e0b\u5de7\u62db\uff0c\u6025\u5fd9\u8f6c\u5934\u76f8\u907f\uff0c\u62cd\u7684\u4e00\u58f0\uff0c\u8fd8\u662f\u7ed9\u8fd9\u4e00\u638c\u91cd\u91cd\u51fb\u5728\u9888\u4e2d\uff0c\u53ea\u9707\u5f97\u4ed6\u534a\u8eab\u9178\u9ebb\u3002\u72c4\u4e91\u4e00\u6014\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u662f\u90a3\u8001\u4e5e\u4e10\u4f2f\u4f2f\u6559\u6211\u7684\u2018\u8033\u5149\u5f0f\u2019\uff01\u201d\u4ed6\u4e00\u62db\u5f97\u624b\uff0c\u8ddf\u7740\u4fbf\u4f7f\u51fa\u201c\u523a\u80a9\u5f0f\u201d\u548c\u201c\u53bb\u5251\u5f0f\u201d\u6765\u3002\u82b1\u94c1\u5e72\u53eb\u9053\uff1a\u201c\u8fde\u57ce\u5251\u6cd5\uff0c\u8fde\u57ce\u5251\u6cd5\uff01\u201d \u3000\u3000\u72c4\u4e91\u53c8\u662f\u4e00\u6014\uff0c\u90a3\u65e5\u4ed6\u5728\u8346\u5dde\u4e07\u5e9c\u548c\u4e07\u572d\u7b49\u516b\u4eba\u6bd4\u5251\uff0c\u4f7f\u51fa\u8fd9\u4e09\u62db\u4e4b\u65f6\uff0c\u4e07\u9707\u5c71\u4e5f\u8bf4\u662f\u201c\u8fde\u57ce\u5251\u6cd5\u201d\uff0c\u5f53\u65f6\u4ed6\u8fd8\u9053\u4e07\u9707\u5c71\u80e1\u8bf4\uff0c\u4f46\u82b1\u94c1\u5e72\u662f\u4e2d\u539f\u5927\u8c6a\uff0c\u89c1\u591a\u8bc6\u5e7f\uff0c\u5c45\u7136\u4e5f\u8bf4\u8fd9\u662f\u8fde\u57ce\u5251\u6cd5\uff0c\u96be\u9053\u8001\u4e5e\u4e10\u6240\u6559\u7684\u8fd9\u4e09\u62db\uff0c\u5f53\u771f\u662f\u8fde\u57ce\u5251\u6cd5\u4e48\uff1f \u3000\u3000\u4ed6\u4ee5\u5200\u4f5c\u5251\uff0c\u5c06\u8fd9\u4e09\u62db\u8fde\u4f7f\u6570\u6b21\uff0c\u53ef\u662f\u82b1\u94c1\u5e72\u7684\u6b66\u529f\u5c82\u662f\u9c81\u5764\u3001\u4e07\u572d\u7b49\u4e00\u5e72\u4eba\u6240\u53ef\u6bd4\uff1f\u9664\u4e86\u7b2c\u4e00\u62db\u51fa\u5176\u4e0d\u610f\u7684\u6253\u4e86\u4ed6\u4e00\u638c\u4e4b\u5916\uff0c\u6b64\u540e\u8fd9\u4e09\u62db\u7528\u5728\u4ed6\u8eab\u4e0a\uff0c\u5df2\u662f\u5168\u65e0\u6548\u7528\u3002\u5230\u5f97\u72c4\u4e91\u7b2c\u56db\u6b21\u53c8\u4f7f\u201c\u53bb\u5251\u5f0f\u201d\uff0c\u5c06\u8840\u5200\u5f80\u9b3c\u5934\u5200\u4e0a\u6311\u53bb\uff0c\u82b1\u94c1\u5e72\u65e9\u5df2\u6709\u5907\uff0c\u5de6\u8db3\u98de\u8d77\uff0c\u8e22\u4e2d\u4ed6\u7684\u8155\u8109\u3002\u72c4\u4e91\u8840\u5200\u8131\u624b\uff0c\u82b1\u94c1\u5e72\u4e00\u62db\u201c\u987a\u6c34\u63a8\u821f\u201d\uff0c\u53cc\u624b\u5200\u5251\u9f50\u5411\u4ed6\u80f8\u53e3\u523a\u6765\u3002 \u3000\u3000\u5657\u5657\u4e24\u58f0\uff0c\u4e00\u5200\u4e00\u5251\u90fd\u523a\u4e2d\u5728\u72c4\u4e91\u80f8\u53e3\uff0c\u5200\u5934\u5251\u5934\u4e3a\u201c\u4e4c\u8695\u8863\u201d\u6240\u963b\uff0c\u900f\u4e0d\u8fdb\u53bb\u3002\u6c34\u7b19\u62ff\u4e86\u4e00\u5757\u77f3\u5934\uff0c\u5b88\u5019\u5728\u65c1\uff0c\u773c\u89c1\u72c4\u4e91\u9047\u9669\uff0c\u4e3e\u8d77\u77f3\u5934\u4fbf\u5411\u82b1\u94c1\u5e72\u540e\u8111\u7838\u53bb\u3002\u82b1\u94c1\u5e72\u4e0a\u6b21\u77ed\u67aa\u523a\u4e0d\u8fdb\u72c4\u4e91\u8eab\u5b50\uff0c\u5df2\u89c9\u5947\u602a\uff0c\u767e\u601d\u4e0d\u5f97\u5176\u89e3\uff0c\u6599\u5b9a\u662f\u4ed6\u6000\u4e2d\u653e\u7740\u94c1\u76d2\u6216\u662f\u94dc\u724c\u4e4b\u7c7b\uff0c\u67aa\u5934\u51d1\u5de7\u523a\u4e2d\u575a\u7269\uff0c\u4f46\u8fd9\u6b21\u5200\u5251\u9f50\u523a\uff0c\u51b3\u8ba1\u4e0d\u4f1a\u53c8\u8fd9\u4e48\u51d1\u5de7\u3002\u4ed6\u4e00\u5446\u4e4b\u9645\uff0c\u72c4\u4e91\u731b\u529b\u6325\u638c\u51fb\u51fa\uff0c\u6c34\u7b19\u53c8\u81ea\u540e\u9762\u653b\u5230\u3002 \u3000\u3000\u82b1\u94c1\u5e72\u53eb\u9053\uff1a\u201c\u6709\u9b3c\uff0c\u6709\u9b3c\uff01\u201d\u5fc3\u4e0b\u53d1\u6bdb\uff1a\u201c\u83ab\u975e\u662f\u9646\u5927\u54e5\u3001\u5218\u5144\u5f1f\u602a\u6211\u5403\u4e86\u4ed6\u4eec\u7684\u9057\u4f53\uff0c\u9b3c\u9b42\u51fa\u73b0\uff0c\u6765\u8ddf\u6211\u4e3a\u96be\uff1f\u201d\u767b\u65f6\u904d\u4f53\u51b7\u6c57\uff0c\u5411\u540e\u8dc3\u5f00\u4e86\u51e0\u6b65\u3002 \u3000\u3000\u72c4\u4e91\u548c\u6c34\u7b19\u6709\u4e86\u8fd9\u4f59\u88d5\uff0c\u6025\u5fd9\u8fdb\u5165\u5c71\u6d1e\uff0c\u642c\u8fc7\u51e0\u5757\u5927\u77f3\uff0c\u5835\u585e\u5165\u53e3\u3002\u4e24\u4eba\u5148\u524d\u5df2\u5c06\u6d1e\u53e3\u5835\u5f97\u751a\u5c0f\uff0c\u8fd9\u65f6\u518d\u52a0\u4e0a\u51e0\u5757\u77f3\u5934\uff0c\u4fbf\u5373\u5c06\u6d1e\u53e3\u5c3d\u884c\u5c01\u4f4f\u3002 \u3000\u3000\u4e24\u4eba\u6b7b\u91cc\u9003\u751f\uff0c\u5fc3\u4e2d\u90fd\u6026\u6026\u4e71\u8df3\u3002\u53ea\u542c\u5f97\u82b1\u94c1\u5e72\u53eb\u9053\uff1a\u201c\u51fa\u6765\u554a\uff0c\u9f9f\u513f\u5b50\uff0c\u8eb2\u5728\u6d1e\u4e2d\u80fd\u8eb2\u4e00\u8f88\u5b50\u4e48\uff1f\u4f60\u4eec\u5728\u77f3\u6d1e\u91cc\u6349\u9e1f\u5403\u4e48\uff1f\u54c8\u54c8\uff0c\u54c8\u54c8\uff01\u201d\u4ed6\u867d\u653e\u58f0\u5927\u7b11\uff0c\u5fc3\u4e0b\u53ef\u7740\u5b9e\u5bb3\u6015\uff0c\u5374\u4e5f\u4e0d\u6562\u4fbf\u53bb\u6398\u6c34\u5cb1\u7684\u7684\u5c38\u4f53\u6765\u5403\u3002 \u3000\u3000\u72c4\u4e91\u548c\u6c34\u7b19\u5bf9\u671b\u4e00\u773c\uff0c\u5747\u60f3\uff1a\u201c\u8fd9\u4eba\u7684\u8bdd\u5012\u4e5f\u4e0d\u9519\uff0c\u6211\u4eec\u5728\u6d1e\u91cc\u5403\u751a\u4e48\uff1f\u4f46\u4e00\u51fa\u53bb\u4fbf\u7ed9\u4ed6\u6740\u4e86\uff0c\u90a3\u53ef\u5982\u4f55\u662f\u597d\uff1f\u201d \u3000\u3000\u82b1\u94c1\u5e72\u82e5\u8981\u5f3a\u653b\uff0c\u642c\u5f00\u77f3\u5934\u8fdb\u6d1e\uff0c\u72c4\u6c34\u4e8c\u4eba\u8840\u5200\u5df2\u5931\uff0c\u4e5f\u662f\u96be\u4ee5\u5b88\u5fa1\uff0c\u53ea\u662f\u4ed6\u5200\u5251\u523a\u4e0d\u8fdb\u72c4\u4e91\u8eab\u4f53\uff0c\u8ba4\u5b9a\u662f\u6709\u9b3c\u9b42\u4f5c\u602a\uff0c\u5168\u8eab\u5bd2\u6bdb\u76f4\u7ad6\uff0c\u4e0d\u4f4f\u98a4\u6296\u3002 \u3000\u3000\u72c4\u4e91\u548c\u6c34\u7b19\u5728\u6d1e\u53e3\u5b88\u4e86\u4e00\u9635\uff0c\u89c1\u82b1\u94c1\u5e72\u4e0d\u518d\u6765\u653b\uff0c\u5fc3\u4e0b\u7a0d\u5b9a\u3002\u72c4\u4e91\u68c0\u89c6\u5de6\u81c2\u4f24\u53e3\uff0c\u89c1\u5140\u81ea\u6d41\u8840\u3002\u6c34\u7b19\u6495\u4e0b\u4e00\u5757\u8863\u895f\uff0c\u7ed9\u4ed6\u5305\u597d\u3002\u72c4\u4e91\u5c06\u65e9\u5df2\u7834\u70c2\u4e0d\u582a\u7684\u50e7\u888d\u5927\u895f\u62c9\u4e86\u8fc7\u6765\uff0c\u906e\u4f4f\u80f8\u53e3\uff0c\u4ee5\u514d\u7ed9\u6c34\u7b19\u89c1\u5230\u81ea\u5df1\u80f8\u53e3\u529e\u88f8\u7684\u808c\u80a4\uff0c\u8fd9\u4e48\u4e00\u62c9\uff0c\u6000\u4e2d\u8dcc\u4e86\u4e00\u672c\u5c0f\u518c\u51fa\u6765\uff0c\u4fbf\u662f\u5f97\u81ea\u5b9d\u8c61\u8eab\u4e0a\u7684\u90a3\u672c\u300a\u8840\u5200\u7ecf\u300b\u3002 \u3000\u3000\u4ed6\u9002\u624d\u548c\u82b1\u94c1\u5e72\u8fd9\u573a\u6076\u6597\uff0c\u65f6\u523b\u867d\u77ed\uff0c\u4f7f\u529b\u4e0d\u591a\uff0c\u5fc3\u60c5\u5374\u662f\u7d27\u5f20\u4e4b\u6781\uff0c\u8fd9\u65f6\u6b47\u4e86\u4e0b\u6765\uff0c\u53ea\u89c9\u75b2\u7d2f\u96be\u5f53\uff0c\u60f3\u8d77\u90a3\u65e5\u5728\u7834\u5e99\u4e2d\u521d\u89c1\u8840\u5200\u7ecf\u65f6\uff0c\u66fe\u7167\u7740\u7ecf\u4e0a\u90a3\u88f8\u4f53\u7537\u5b50\u7684\u59ff\u5f0f\u4f9d\u6837\u800c\u4e3a\uff0c\u7cbe\u795e\u7acb\u5373\u632f\u594b\uff0c\u5fc3\u60f3\u82b1\u94c1\u5e72\u51b3\u8ba1\u4e0d\u80af\u7f62\u4f11\uff0c\u5c11\u65f6\u6076\u6597\u53c8\u8d77\uff0c\u5c31\u7b97\u7ed9\u4ed6\u6740\u4e86\uff0c\u4e5f\u5f53\u72e0\u72e0\u6253\u4ed6\u51e0\u638c\uff0c\u5982\u6b64\u795e\u75b2\u529b\u4e4f\uff0c\u600e\u80fd\u6297\u654c\uff1f\u5f53\u4e0b\u968f\u624b\u7ffb\u5f00\u4e00\u9875\uff0c\u89c1\u56fe\u4e2d\u4eba\u5f62\u5934\u4e0b\u811a\u4e0a\uff0c\u4ee5\u5929\u7075\u76d6\u9876\u5728\u5730\u4e0b\uff0c\u4e24\u53ea\u624b\u7684\u59ff\u5f0f\u66f4\u662f\u5341\u5206\u602a\u5f02\u3002\u72c4\u4e91\u5f53\u5373\u4f9d\u5f0f\u800c\u4e3a\uff0c\u4e5f\u662f\u5934\u4e0b\u811a\u4e0a\uff0c\u5012\u7acb\u8d77\u6765\u3002 \u3000\u3000\u6c34\u7b19\u89c1\u4ed6\u7a81\u7136\u88c5\u8fd9\u602a\u6837\uff0c\u53ea\u9053\u4ed6\u53c8\u53d1\u75af\uff0c\u5fc3\u60f3\u5916\u6709\u5f3a\u654c\uff0c\u5185\u6709\u72c2\u4eba\uff0c\u90a3\u4fbf\u5982\u4f55\u662f\u597d\uff0c\u5fc3\u4e2d\u4e00\u6025\uff0c\u4e0d\u7981\u8f7b\u58f0\u54ed\u4e86\u51fa\u6765\u3002 \u3000\u3000\u72c4\u4e91\u7ec3\u4e0d\u5230\u534a\u4e2a\u65f6\u8fb0\uff0c\u987f\u65f6\u5168\u8eab\u53d1\u6696\uff0c\u72b9\u5982\u70e4\u706b\u4e00\u822c\uff0c\u8bf4\u4e0d\u51fa\u7684\u8212\u9002\u53d7\u7528\u3002\u4ed6\u968f\u624b\u7ffb\u8fc7\u4e00\u9875\uff0c\u53ea\u89c1\u56fe\u4e2d\u90a3\u88f8\u4f53\u7537\u5b50\u4ee5\u5de6\u624b\u652f\u5730\uff0c\u8eab\u5b50\u4e0e\u5730\u9762\u5e73\u884c\uff0c\u4e24\u53ea\u811a\u5374\u7ffb\u8fc7\u6765\u52fe\u5728\u81ea\u5df1\u9888\u4e2d\u3002 \u3000\u3000\u8fd9\u59ff\u5f0f\u672c\u6765\u6781\u96be\uff0c\u4f46\u4ed6\u81ea\u7ec3\u6210\u201c\u795e\u7167\u529f\u201d\u540e\uff0c\u56db\u80a2\u767e\u9ab8\u8fd0\u7528\u81ea\u5982\uff0c\u5f53\u5373\u4f9d\u7740\u56fe\u4e2d\u6240\u793a\u7167\u505a\uff0c\u5185\u606f\u4e5f\u4f9d\u7740\u56fe\u4e2d\u7ea2\u8272\u7eff\u8272\u7ebf\u8def\uff0c\u5728\u8eab\u4e0a\u5404\u5904\u7ecf\u8109\u7a74\u9053\u4e2d\u901a\u884c\u3002 \u3000\u3000\u8fd9\u300a\u8840\u5200\u7ecf\u300b\u4e43\u8840\u5200\u95e8\u4e2d\u5185\u529f\u5916\u529f\u7684\u603b\u8bc0\uff0c\u6bcf\u4e00\u9875\u56fe\u8c31\u90fd\u987b\u7ec3\u4e0a\u4e00\u5e74\u534a\u8f7d\uff0c\u65b9\u59cb\u6709\u6210\u3002\u4f46\u72c4\u4e91\u4efb\u7763\u4e8c\u8109\u65e2\u901a\uff0c\u6709\u4e86\u201c\u795e\u7167\u529f\u201d\u8fd9\u65e0\u4e0a\u6d51\u539a\u7684\u5185\u529b\u4e3a\u57fa\u7840\uff0c\u518d\u8270\u96be\u7684\u6b66\u529f\u5230\u4e86\u624b\u4e2d\uff0c\u4e5f\u662f\u4e00\u7ec3\u5373\u6210\u3002\u4ed6\u7ec3\u4e86\u4e00\u5f0f\u53c8\u4e00\u5f0f\uff0c\u8d8a\u7ec3\u8d8a\u662f\u5174\u5473\u76ce\u7136\u3002 \u3000\u3000\u6c34\u7b19\u89c1\u4ed6\u7ffb\u4e66\u7ec3\u529f\uff0c\u8fd9\u624d\u60ca\u9b42\u7a0d\u5b9a\u3002\u770b\u4e86\u4e00\u4f1a\uff0c\u89c1\u4ed6\u59ff\u5f0f\u5e0c\u5947\u53e4\u602a\uff0c\u5f53\u771f\u532a\u5937\u6240\u601d\uff0c\u4e0d\u7531\u5f97\u53c8\u597d\u7b11\uff0c\u53c8\u8be7\u5f02\uff0c\u5fc3\u9053\uff1a\u201c\u5929\u4e0b\u96be\u9053\u771f\u6709\u8fd9\u822c\u6b66\u529f\uff1f\u201d\u8d70\u4e0a\u4e24\u6b65\uff0c\u5411\u5730\u4e0b\u7ffb\u5f00\u7740\u7684\u8840\u5200\u7ecf\u77a7\u53bb\uff0c\u4e00\u77a5\u4e4b\u4e0b\uff0c\u89c1\u56fe\u4e2d\u6240\u7ed8\u662f\u4e2a\u5168\u8eab\u8d64\u88f8\u7684\u7537\u5b50\uff0c\u4e0d\u7531\u5f97\u6ee1\u8138\u901a\u7ea2\uff0c\u4e00\u9897\u5fc3\u6026\u6026\u4e71\u8df3\uff1a\u201c\u8fd9\u5c0f\u6076\u50e7\u7ec3\u5230\u540e\u6765\uff0c\u4f1a\u4e0d\u4f1a\u8131\u53bb\u8863\u670d\uff0c\u5168\u8eab\u8d64\u88f8\uff1f\u201d \u3000\u3000\u5e78\u597d\u8fd9\u53ef\u6015\u7684\u60c5\u666f\u59cb\u7ec8\u6ca1\u6709\u51fa\u73b0\u3002 \u3000\u3000\u72c4\u4e91\u7ec3\u4e86\u4e00\u4f1a\u5185\u529f\uff0c\u7ffb\u5230\u4e00\u9875\uff0c\u89c1\u56fe\u4e2d\u4eba\u5f62\u624b\u6267\u4e00\u67c4\u5f2f\u5200\uff0c\u659c\u52bf\u780d\u5288\u3002\u72c4\u4e91\u5927\u559c\uff0c\u8131\u53e3\u800c\u51fa\uff1a\u201c\u8840\u5200\u5200\u6cd5\u3002\u201d\u62fe\u8d77\u4e00\u6839\u6811\u679d\uff0c\u7167\u56fe\u4e2d\u6240\u793a\u4f7f\u4e86\u8d77\u6765\u3002 \u3000\u3000\u8fd9\u8840\u5200\u5200\u6cd5\u5f53\u771f\u602a\u5f02\u4e4b\u6781\uff0c\u6bcf\u4e00\u62db\u90fd\u662f\u5728\u51b3\u4e0d\u53ef\u80fd\u7684\u65b9\u4f4d\u780d\u5c06\u51fa\u53bb\u3002\u72c4\u4e91\u53ea\u7ec3\u5f97\u4e09\u62db\uff0c\u4fbf\u5df2\u9886\u4f1a\uff0c\u539f\u6765\u6bcf\u4e00\u62db\u5200\u6cd5\u90fd\u662f\u4ece\u524d\u9762\u7684\u53e4\u602a\u59ff\u5f0f\u4e2d\u5316\u5c06\u51fa\u6765\u3002\u524d\u9762\u56fe\u8c31\u4e2d\u6709\u5012\u7acb\u3001\u6a2a\u8eab\u3001\u4f38\u817f\u4e0a\u9888\u3001\u53cd\u624b\u6293\u8033\u7b49\u79cd\u79cd\u8be1\u5f02\u59ff\u5f0f\uff0c\u8840\u5200\u5200\u6cd5\u4e2d\u4fbf\u4e5f\u6709\u8fd9\u4e9b\u4ee4\u4eba\u7edd\u96be\u60f3\u50cf\u7684\u62db\u6570\u3002\u72c4\u4e91\u5f53\u4e0b\u6311\u4e86\u56db\u62db\u5200\u6cd5\u7528\u5fc3\u7ec3\u719f\uff0c\u5fc3\u60f3\uff1a\u201c\u6211\u987b\u5f97\u4e0d\u7720\u4e0d\u606f\uff0c\u8d76\u5feb\u7ec3\u4e0a\u4e8c\u4e09\u5341\u62db\uff0c\u8fc7\u5f97\u56db\u4e94\u5929\uff0c\u518d\u51fa\u53bb\u548c\u8fd9\u59d3\u82b1\u7684\u51b3\u4e00\u6b7b\u6218\u3002\u5509\uff0c\u53ea\u53ef\u60dc\u6ca1\u65e9\u4e9b\u7ec3\u8fd9\u5200\u6cd5\u3002\u201d \u3000\u3000\u54ea\u77e5\u82b1\u94c1\u5e72\u7adf\u4e0d\u8ba9\u4ed6\u6709\u534a\u5929\u7684\u4f59\u88d5\u3002\u72c4\u4e91\u4e13\u5fc3\u5b66\u7ec3\u5200\u6cd5\uff0c\u82b1\u94c1\u5e72\u5728\u6d1e\u5916\u53eb\u4e86\u8d77\u6765\uff1a\u201c\u5c0f\u548c\u5c1a\uff0c\u4f60\u5cb3\u7236\u5927\u4eba\u7684\u5fc3\u809d\u5403\u4e0d\u5403\uff1f\u6ecb\u5473\u5f88\u597d\u554a\u3002\u201d \u3000\u3000\u6c34\u7b19\u5927\u5403\u4e00\u60ca\uff0c\u63a8\u5f00\u77f3\u5934\uff0c\u62a2\u4e86\u51fa\u53bb\u3002\u53ea\u89c1\u82b1\u94c1\u5e72\u62ff\u7740\u9b3c\u5934\u5200\uff0c\u6b63\u5728\u6c34\u5cb1\u7684\u575f\u5934\u6316\u6398\uff0c\u867d\u7136\u5c1a\u672a\u6398\u5230\u5c38\u8eab\uff0c\u4f46\u90a3\u4e5f\u662f\u6307\u987e\u95f4\u7684\u4e8b\u3002\u6c34\u7b19\u5927\u53eb\uff1a\u201c\u82b1\u4f2f\u4f2f\uff0c\u82b1\u4f2f\u4f2f\uff0c\u4f60\u2026\u2026\u4f60\u2026\u2026\u5168\u4e0d\u5ff5\u7ed3\u4e49\u5144\u5f1f\u4e4b\u60c5\u4e48\uff1f\u201d\u53e3\u4e2d\u60ca\u547c\uff0c\u62a2\u5c06\u8fc7\u53bb\u3002 \u3000\u3000\u82b1\u94c1\u5e72\u6b63\u8981\u5f15\u5979\u51fa\u6765\uff0c\u5c06\u5979\u5148\u884c\u51fb\u5012\uff0c\u7136\u540e\u518d\u6599\u7406\u72c4\u4e91\uff0c\u5426\u5219\u4e24\u4eba\u8054\u624b\u800c\u6597\uff0c\u603b\u4e0d\u514d\u788d\u624b\u788d\u811a\u3002\u4ed6\u89c1\u6c34\u7b19\u5954\u6765\uff0c\u53ea\u4f5c\u4e0d\u89c1\uff0c\u4ecd\u662f\u4f4e\u5934\u6316\u6398\u3002\u6c34\u7b19\u62a2\u5230\u4ed6\u7684\u8eab\u540e\uff0c\u53f3\u638c\u5f80\u4ed6\u80cc\u5fc3\u594b\u529b\u51fb\u53bb\u3002\u82b1\u94c1\u5e72\u5de6\u624b\u75be\u7ffb\uff0c\u5feb\u5982\u95ea\u7535\uff0c\u5df2\u62ff\u4f4f\u4e86\u5979\u624b\u8155\u3002\u6c34\u7b19\u53eb\u58f0\uff1a\u201c\u554a\u54df\uff01\u201d\u5de6\u624b\u51fb\u51fa\u3002\u82b1\u94c1\u5e72\u4fa7\u8eab\u907f\u8fc7\uff0c\u53cd\u624b\u70b9\u51fa\u3002\u6c34\u7b19\u8170\u95f4\u4e2d\u6307\uff0c\u4e00\u58f0\u4f4e\u547c\uff0c\u59d4\u5012\u5728\u5730\u3002 \u3000\u3000\u8fd9\u65f6\u72c4\u4e91\u624b\u6267\u6811\u679d\uff0c\u4e5f\u5df2\u62a2\u5230\u3002\u82b1\u94c1\u5e72\u54c8\u54c8\u5927\u7b11\uff0c\u53eb\u9053\uff1a\u201c\u5c0f\u548c\u5c1a\u6d3b\u5f97\u4e0d\u8010\u70e6\u4e86\uff0c\u7528\u4e00\u6839\u6811\u679d\u513f\u6765\u6597\u8001\u5b50\u3002\u597d\uff0c\u4f60\u662f\u8840\u5200\u95e8\u7684\u6076\u50e7\uff0c\u6211\u4fbf\u7528\u4f60\u672c\u95e8\u7684\u5175\u5203\u9001\u4f60\u5f52\u5929\u3002\u201d\u53cd\u624b\u4ece\u8170\u95f4\u62bd\u51fa\u8840\u5200\uff0c\u5c06\u9b3c\u5934\u5200\u629b\u5728\u5730\u4e0b\uff0c\u970e\u65f6\u4e4b\u95f4\u5411\u72c4\u4e91\u8fde\u780d\u4e09\u5200\u3002\u8fd9\u8840\u5200\u5176\u8584\u5982\u7eb8\uff0c\u780d\u51fa\u53bb\u65f6\u7684\u98ce\u58f0\u55e4\u55e4\u58f0\u54cd\uff0c\u82b1\u94c1\u5e72\u5fc3\u4e0b\u6697\u8d5e\uff1a \u3000\u3000\u201c\u597d\u4e00\u53e3\u5b9d\u5200\uff01\u201d \u3000\u3000\u72c4\u4e91\u89c1\u8840\u5200\u5982\u6b64\u8fc5\u901f\u7684\u780d\u6765\uff0c\u5fc3\u4e2d\u4e00\u5bd2\uff0c\u4e0d\u7531\u5f97\u624b\u8db3\u65e0\u63aa\uff0c\u4e00\u54ac\u7259\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u5c31\u62da\u4e2a\u540c\u5f52\u4e8e\u5c3d\u7f62\uff01\u201d\u53f3\u624b\u6325\u52a8\u6811\u679d\uff0c\u4ece\u80cc\u540e\u53cd\u51fb\u8fc7\u53bb\uff0c\u62cd\u7684\u4e00\u58f0\uff0c\u7ed3\u7ed3\u5b9e\u5b9e\u7684\u6253\u5728\u82b1\u94c1\u5e72\u540e\u9888\u3002\u8fd9\u4e00\u62db\u53e4\u602a\u65e0\u6bd4\uff0c\u5018\u82e5\u4ed6\u624b\u4e2d\u62ff\u7684\u662f\u5229\u5200\u800c\u4e0d\u662f\u6811\u679d\uff0c\u5df2\u7136\u5c06\u82b1\u94c1\u5e72\u7684\u8111\u888b\u780d\u4e0b\u6765\u4e86\u3002 \u3000\u3000\u5176\u5b9e\u82b1\u94c1\u5e72\u7684\u6b66\u529f\u548c\u8840\u5200\u8001\u7956\u4e5f\u76f8\u5dee\u65e0\u51e0\uff0c\u5c31\u7b97\u7ec3\u719f\u4e86\u8840\u5200\u529f\u592b\u7684\u8840\u5200\u8001\u7956\uff0c\u4e5f\u51b3\u4e0d\u80fd\u5728\u4e00\u62db\u4e4b\u95f4\u4fbf\u6740\u4e86\u4ed6\uff0c\u66f4\u4e0d\u7528\u8bf4\u72c4\u4e91\u4e86\u3002\u53ea\u662f\u82b1\u94c1\u5e72\u5341\u5206\u8f7b\u654c\uff0c\u5168\u6ca1\u5c06\u8fd9\u4e2a\u6b66\u529f\u4f4e\u5fae\u7684\u5bf9\u624b\u77a7\u5728\u773c\u5185\uff0c\u662f\u4ee5\u4e00\u4e0a\u624b\u4fbf\u7740\u4e86\u9053\u513f\u3002\u4ed6\u4e00\u6014\u4e4b\u95f4\uff0c\u63d0\u5200\u6b32\u524a\uff0c\u72c4\u4e91\u624b\u4e2d\u6811\u679d\u5982\u72c2\u98ce\u66b4\u96e8\u822c\u5288\u5c06\u51fa\u53bb\uff0c\u4e71\u780d\u4e71\u524a\u4e4b\u4e2d\uff0c\u5076\u5c14\u5939\u4e00\u62db\u8840\u5200\u5200\u6cd5\uff0c\u5657\u7684\u4e00\u58f0\uff0c\u53c8\u662f\u4e00\u4e0b\u6253\u4e2d\u5728\u4ed6\u540e\u8111\u3002\u82b1\u94c1\u5e72\u8eab\u5b50\u4e00\u6643\uff0c\u53eb\u9053\uff1a\u201c\u6709\u9b3c\uff0c\u6709\u9b3c\uff01\u201d\u56de\u8eab\u671b\u4e86\u4e00\u773c\uff0c\u53ea\u5413\u5f97\u624b\u9178\u8db3\u8f6f\uff0c\u624b\u4e00\u677e\uff0c\u8840\u5200\u6389\u5728\u5730\u4e0b\uff0c\u8f6c\u8eab\u62d4\u8db3\u98de\u5954\uff0c\u8fdc\u8fdc\u9003\u5f00\u3002 \u3000\u3000\u4ed6\u81ea\u5403\u4e86\u4e49\u5144\u4e49\u5f1f\u7684\u5c38\u8eab\u540e\uff0c\u5fc3\u4e0b\u6709\u6127\uff0c\u65f6\u65f6\u6015\u9646\u5929\u6292\u548c\u5218\u4e58\u98ce\u7684\u9b3c\u9b42\u6765\u627e\u4ed6\u7b97\u5e10\u3002\u9002\u624d\u5200\u5251\u523a\u4e0d\u8fdb\u72c4\u4e91\u8eab\u4f53\uff0c\u5df2\u8ba4\u5b9a\u662f\u6709\u9b3c\u9b42\u5728\u6697\u52a9\u654c\u4eba\uff0c\u8fd9\u65f6\u72c4\u4e91\u4ee5\u4e00\u6839\u6811\u679d\u548c\u4ed6\u76f8\u6597\uff0c\u660e\u660e\u7ad9\u5728\u81ea\u5df1\u5bf9\u9762\uff0c\u6c34\u7b19\u53c8\u88ab\u70b9\u4e2d\u7a74\u9053\u800c\u8eba\u5367\u5728\u5730\uff0c\u53ef\u662f\u81ea\u5df1\u540e\u9888\u548c\u540e\u8111\u5374\u63a5\u8fde\u88ab\u786c\u7269\u6253\u4e2d\u3002\u8c37\u4e2d\u9664\u4e86\u81ea\u5df1\u548c\u72c4\u6c34\u4e8c\u4eba\u4e4b\u5916\uff0c\u66f4\u6709\u4f55\u4eba\uff1f\u5982\u6b64\u795e\u51fa\u9b3c\u6ca1\u7684\u5728\u80cc\u540e\u6697\u7b97\u81ea\u5df1\uff0c\u4e0d\u662f\u9b3c\u9b45\uff0c\u66f4\u662f\u751a\u4e48\u4e1c\u897f\uff1f\u4ed6\u8f6c\u5934\u4e00\u770b\uff0c\u4e0d\u8bba\u770b\u5230\u751a\u4e48\uff0c\u90fd\u4e0d\u4f1a\u5982\u6b64\u5403\u60ca\uff0c\u4f46\u504f\u504f\u751a\u4e48\u4e5f\u770b\u4e0d\u5230\uff0c\u4e0d\u7531\u5f97\u9b42\u98de\u9b44\u6563\uff0c\u54ea\u91cc\u8fd8\u6562\u6709\u7247\u523b\u505c\u7559\uff1f \u3000\u3000\u72c4\u4e91\u867d\u6253\u4e2d\u4e86\u82b1\u94c1\u5e72\u4e24\u4e0b\uff0c\u4f46\u4ed6\u663e\u7136\u5e76\u6ca1\u53d7\u4f24\uff0c\u5ffd\u7136\u6ca1\u547d\u4ef7\u5954\u9003\uff0c\u5012\u4e5f\u5927\u51fa\u610f\u6599\u4e4b\u5916\u3002 \u3000\u3000\u72c4\u4e91\u62fe\u8d77\u8840\u5200\uff0c\u89c1\u6c34\u7b19\u8eba\u5728\u5730\u4e0b\u52a8\u5f39\u4e0d\u5f97\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u7ed9\u8fd9\u53ae\u70b9\u4e2d\u4e86\u7a74\u9053\uff1f\u201d\u6c34\u7b19\u9053\uff1a\u201c\u662f\u3002\u201d\u72c4\u4e91\u9053\uff1a\u201c\u6211\u4e0d\u4f1a\u89e3\u7a74\uff0c\u6551\u4f60\u4e0d\u5f97\u3002\u201d\u6c34\u7b19\u9053\uff1a\u201c\u4f60\u53ea\u987b\u5728\u6211\u8170\u95f4\u548c\u817f\u4e0a\u2026\u2026\u201d\u672c\u60f3\u544a\u77e5\u4ed6\u7a74\u9053\u7684\u90e8\u4f4d\uff0c\u8bf7\u4ed6\u63a8\u5bab\u8fc7\u8840\uff0c\u4fbf\u53ef\u89e3\u5f00\u88ab\u5c01\u7684\u7a74\u9053\uff0c\u4f46\u8bf4\u5230\u201c\u817f\u4e0a\u201d\u4e24\u5b57\uff0c\u60f3\u8d77\u8fd9\u201c\u5c0f\u6076\u50e7\u201d\u6700\u8fd1\u867d\u7136\u5e76\u6ca1\u6709\u5bf9\u81ea\u5df1\u65e0\u793c\uff0c\u4ee5\u524d\u53ef\u662f\u54c1\u884c\u5341\u5206\u4e0d\u7aef\uff0c\u5018\u82e5\u4e58\u7740\u81ea\u5df1\u884c\u52a8\u4e0d\u5f97\u2026\u2026\u72c4\u4e91\u89c1\u5979\u773c\u4e2d\u7a81\u7136\u9732\u51fa\u60e7\u8272\uff0c\u5fc3\u60f3\uff1a\u201c\u82b1\u94c1\u5e72\u5df2\u9003\u8d70\u4e86\uff0c\u4f60\u8fd8\u6015\u751a\u4e48\uff1f\u201d\u4e00\u8f6c\u5ff5\u95f4\uff0c\u968f\u5373\u660e\u767d\u5979\u662f\u5bb3\u6015\u81ea\u5df1\uff0c\u4e0d\u7531\u5f97\u6012\u6c14\u6025\u51b2\u80f8\u81c6\uff0c\u5927\u58f0\u9053\uff1a\u201c\u4f60\u6015\u6211\u4fb5\u72af\u4f60\uff0c\u6015\u6211\u5bf9\u4f60\u2026\u2026\u5bf9\u4f60\u2026\u2026\u54fc\uff0c\u54fc\uff01\u4ece\u4eca\u800c\u540e\uff0c\u6211\u518d\u4e5f\u4e0d\u8981\u89c1\u4f60\u3002\u201d\u6c14\u5f97\u4f38\u8db3\u4e71\u8e22\uff0c\u53ea\u8e22\u5f97\u767d\u96ea\u98de\u6e85\u3002 \u3000\u3000\u4ed6\u56de\u5230\u5c71\u6d1e\u4e2d\uff0c\u53d6\u4e86\u300a\u8840\u5200\u7ecf\u300b\u5f84\u81ea\u8d70\u5f00\uff0c\u518d\u4e5f\u4e0d\u5411\u6c34\u7b19\u77a7\u4e00\u773c\u3002 \u3000\u3000\u6c34\u7b19\u5fc3\u4e0b\u7f9e\u6127\uff0c\u5bfb\u601d\uff1a\u201c\u96be\u9053\u662f\u6211\u778e\u7591\u5fc3\uff0c\u9519\u602a\u4e86\u4ed6\uff1f\u201d \u3000\u3000\u5979\u8eba\u5728\u5730\u4e0b\uff0c\u4e00\u52a8\u4e5f\u4e0d\u52a8\uff0c\u8fc7\u5f97\u4e00\u4e2a\u591a\u65f6\u8fb0\uff0c\u4e00\u5934\u5140\u9e70\u4ece\u5929\u7a7a\u76f4\u51b2\u4e0b\u6765\uff0c\u6251\u5411\u5979\u8138\u3002\u6c34\u7b19\u5927\u58f0\u60ca\u53eb\uff0c\u7a81\u7136\u7ea2\u5149\u4e00\u95ea\uff0c\u8840\u5200\u4ece\u659c\u523a\u91cc\u98de\u4e86\u8fc7\u6765\uff0c\u5c06\u5140\u9e70\u780d\u4e3a\u4e24\u6bb5\uff0c\u843d\u5728\u5979\u8eab\u65c1\u3002 \u3000\u3000\u539f\u6765\u72c4\u4e91\u867d\u607c\u5979\u6000\u7591\u81ea\u5df1\uff0c\u4ecd\u662f\u62c5\u5fc3\u82b1\u94c1\u5e72\u53bb\u800c\u590d\u56de\uff0c\u524d\u6765\u52a0\u5bb3\u4e8e\u5979\uff0c\u56e0\u6b64\u5b88\u5728\u4e0d\u8fdc\u4e4b\u5904\uff0c\u7eed\u7ec3\u8840\u5200\u5200\u6cd5\u3002\u4ed6\u63b7\u51fa\u98de\u5200\uff0c\u5c45\u7136\u5c06\u5140\u9e70\u65a9\u4e3a\u4e24\u6bb5\uff0c\u8840\u5200\u65a9\u6b7b\u4e86\u5140\u9e70\u540e\uff0c\u7565\u65e0\u963b\u788d\uff0c\u53c8\u98de\u4e86\u5341\u4f59\u4e08\uff0c\u8fd9\u624d\u843d\u4e0b\u3002\u8fd9\u4e48\u4e00\u6765\uff0c\u4ed6\u8fd9\u62db\u201c\u6d41\u661f\u7ecf\u5929\u201d\u7684\u5200\u6cd5\u53c8\u5df2\u7ec3\u6210\u4e86\u3002 \u3000\u3000\u6c34\u7b19\u53eb\u9053\uff1a\u201c\u72c4\u5927\u54e5\uff0c\u72c4\u5927\u54e5\u3002\u662f\u6211\u9519\u4e86\uff0c\u4e00\u767e\u4e2a\u5bf9\u4f60\u4e0d\u8d77\u3002\u201d\u72c4\u4e91\u53ea\u4f5c\u6ca1\u6709\u542c\u89c1\uff0c\u4e0d\u53bb\u7406\u5979\u3002\u6c34\u7b19\u53c8\u9053\uff1a\u201c\u72c4\u5927\u54e5\uff0c\u4f60\u539f\u8c05\u6211\u6b7b\u4e86\u7239\u7239\uff0c\u5b64\u82e6\u4f36\u4ec3\u7684\uff0c\u60f3\u4e8b\u4e0d\u5468\uff0c\u522b\u518d\u607c\u6211\u4e86\uff0c\u597d\u4e0d\u597d\uff1f\u201d \u3000\u3000\u72c4\u4e91\u4ecd\u662f\u4e0d\u7406\uff0c\u4f46\u5fc3\u4e2d\u6012\u6c14\uff0c\u5374\u4e5f\u6e10\u6e10\u6d88\u4e86\u3002 \u3000\u3000\u6c34\u7b19\u8eba\u5728\u5730\u4e0b\uff0c\u76f4\u5230\u7b2c\u4e8c\u65e5\u7a74\u9053\u65b9\u89e3\u3002\u5979\u77e5\u72c4\u4e91\u867d\u7136\u4e00\u8a00\u4e0d\u53d1\uff0c\u4f46\u76ee\u4e0d\u4ea4\u776b\u7684\u5728\u81ea\u5df1\u8eab\u8fb9\u5b88\u4e86\u6574\u6574\u4e00\u665a\uff0c\u5fc3\u4e2d\u597d\u751f\u611f\u6fc0\u3002\u5979\u8eab\u5b50\u4e00\u80fd\u52a8\u5f39\uff0c\u5373\u523b\u53bb\u5c06\u90a3\u5934\u5140\u9e70\u70e4\u719f\u4e86\uff0c\u5206\u4e86\u534a\u8fb9\uff0c\u9001\u5230\u72c4\u4e91\u8eab\u524d\u3002\u72c4\u4e91\u7b49\u5979\u8d70\u8fd1\u65f6\uff0c\u95ed\u4e0a\u4e86\u773c\u775b\uff0c\u4ee5\u9075\u5b88\u81ea\u5df1\u8bf4\u8fc7\u7684\u90a3\u53e5\u8bdd\uff1a\u201c\u4ece\u4eca\u800c\u540e\uff0c\u6211\u518d\u4e5f\u4e0d\u8981\u89c1\u4f60\u3002\u201d \u3000\u3000\u6c34\u7b19\u653e\u4e0b\u719f\u9e70\uff0c\u4fbf\u5373\u8d70\u5f00\u3002\u72c4\u4e91\u7b49\u5979\u8d70\u8fdc\u518d\u884c\u7741\u773c\uff0c\u5ffd\u542c\u5f97\u5979\u201c\u554a\u201d\u7684\u4e00\u58f0\u60ca\u547c\uff0c\u8ddf\u7740\u53c8\u662f\u4e00\u58f0\u201c\u54ce\u54df\u201d\uff0c\u6454\u5012\u5728\u5730\u3002 \u3000\u3000\u72c4\u4e91\u4e00\u8dc3\u800c\u8d77\uff0c\u62a2\u5230\u5979\u8eab\u8fb9\u3002 \u3000\u3000\u6c34\u7b19\u5ae3\u7136\u4e00\u7b11\uff0c\u7ad9\u4e86\u8d77\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u9a97\u9a97\u4f60\u7684\u3002\u4f60\u8bf4\u4ece\u6b64\u4e0d\u8981\u89c1\u6211\uff0c\u8fd9\u5374\u4e0d\u662f\u89c1\u4e86\u6211\u4e48\uff1f\u90a3\u53e5\u8bdd\u53ef\u7b97\u4e0d\u5f97\u6570\u4e86\u3002\u201d \u3000\u3000\u72c4\u4e91\u72e0\u72e0\u77aa\u4e86\u5979\u4e00\u773c\uff0c\u5fc3\u9053\uff1a\u201c\u5929\u4e0b\u5973\u5b50\u90fd\u662f\u9b3c\u5fc3\u773c\u513f\u3002 \u3000\u3000\u9664\u4e86\u4e01\u5927\u54e5\u7684\u90a3\u4f4d\u51cc\u59d1\u5a18\uff0c\u8c01\u90fd\u4f1a\u9a97\u4eba\u3002\u4ece\u4eca\u4ee5\u540e\uff0c\u6211\u518d\u4e5f\u4e0d\u4e0a\u4f60\u5f53\u4e86\u3002\u201d \u3000\u3000\u6c34\u7b19\u5374\u683c\u683c\u5a07\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u72c4\u5927\u54e5\uff0c\u4f60\u8d76\u7740\u6765\u6551\u6211\uff0c\u8c22\u8c22\u4f60\u5566\uff01\u201d \u3000\u3000\u72c4\u4e91\u6a2a\u4e86\u5979\u4e00\u773c\uff0c\u80cc\u8f6c\u8eab\u5b50\uff0c\u5927\u8e0f\u6b65\u8d70\u5f00\u4e86\u3002 \u3000\u3000\u82b1\u94c1\u5e72\u5bb3\u6015\u9b3c\u9b42\u4f5c\u602a\uff0c\u518d\u4e5f\u4e0d\u6562\u524d\u6765\u6ecb\u6270\uff0c\u53ea\u597d\u56bc\u4e9b\u6811\u76ae\u8349\u6839\uff0c\u82e6\u6e21\u65f6\u5149\uff0c\u6709\u65f6\u4ee5\u6697\u5668\u624b\u6cd5\u63b7\u77f3\uff0c\u4e5f\u6253\u5230\u4e00\u4e24\u53ea\u96ea\u96c1\u3002\u72c4\u4e91\u6bcf\u65e5\u7ec3\u4e00\u4e24\u62db\u8840\u5200\u5200\u6cd5\uff0c\u5185\u529b\u5916\u529f\uff0c\u4e0e\u65e5\u4ff1\u589e\u3002 \u3000\u3000\u51ac\u53bb\u6625\u6765\uff0c\u5929\u6c14\u6e10\u6696\uff0c\u5c71\u8c37\u4e2d\u7684\u79ef\u96ea\u4e0d\u518d\u52a0\u539a\uff0c\u540e\u6765\u96ea\u6c34\u6dd9\u6dd9\uff0c\u7adf\u7136\u5f00\u59cb\u6d88\u878d\u4e86\u3002 \u3000\u3000\u8fd9\u4e9b\u65e5\u5b50\u4e4b\u4e2d\uff0c\u72c4\u4e91\u5df2\u5c06\u4e00\u672c\u8840\u5200\u7ecf\u7684\u5185\u529f\u548c\u5200\u6cd5\u5c3d\u6570\u7ec3\u5168\u3002\u4ed6\u8fd9\u65f6\u8eab\u96c6\u6b63\u90aa\u4e24\u6d3e\u6700\u4e0a\u4e58\u6b66\u529f\u4e4b\u6240\u957f\uff0c\u867d\u7136\u7ecf\u9a8c\u9605\u5386\u6781\u4e3a\u6b20\u7f3a\uff0c\u800c\u6b63\u90aa\u4e24\u95e8\u529f\u592b\u7684\u7cbe\u534e\u4ea6\u672a\u878d\u4f1a\u8d2f\u901a\uff0c\u4f46\u5355\u4ee5\u6b66\u529f\u800c\u8bba\uff0c\u522b\u8bf4\u5df2\u8fdc\u5728\u82b1\u94c1\u5e72\u548c\u8840\u5200\u8001\u7956\u4e4b\u4e0a\uff0c\u6bd4\u4e4b\u5f53\u5e74\u4e01\u5178\uff0c\u4ea6\u662f\u672a\u9051\u591a\u8ba9\uff0c\u8fd9\u4ff1\u662f\u7ec3\u6210\u795e\u7167\u529f\u800c\u6253\u901a\u4efb\u7763\u4e8c\u8109\u4e4b\u529f\u3002 \u3000\u3000\u6c34\u7b19\u8ddf\u4ed6\u8bf4\u8bdd\uff0c\u72c4\u4e91\u6015\u53c8\u4e0a\u5979\u7684\u5f53\uff0c\u59cb\u7ec8\u626e\u4f5c\u54d1\u5df4\uff0c\u4e00\u53e5\u4e0d\u7b54\uff0c\u9664\u4e86\u8fdb\u98df\u65f6\u5076\u5728\u4e00\u8d77\u4e4b\u5916\uff0c\u72c4\u4e91\u603b\u662f\u548c\u5979\u79bb\u5f97\u8fdc\u8fdc\u5730\uff0c\u81ea\u884c\u7ec3\u529f\u3002\u4ed6\u5fc3\u4e2d\u6240\u60f3\u7684\uff0c\u53ea\u662f\u4e09\u4e2a\u5ff5\u5934\uff1a\u51fa\u4e86\u96ea\u8c37\u4e4b\u540e\uff0c\u7b2c\u4e00\u662f\u5230\u6e58\u897f\u6545\u5c45\u53bb\u5bfb\u5e08\u7236\uff1b\u7b2c\u4e8c\u662f\u5230\u8346\u5dde\u53bb\u7ed9\u4e01\u5927\u54e5\u548c\u51cc\u59d1\u5a18\u5408\u846c\uff1b\u7b2c\u4e09\uff0c\u62a5\u4ec7\uff01 \u3000\u3000\u773c\u89c1\u96ea\u6c34\u6c47\u96c6\u6210\u6eaa\uff0c\u4e0d\u65ad\u6d41\u5411\u8c37\u5916\uff0c\u5c71\u8c37\u901a\u9053\u4e0a\u7684\u79ef\u96ea\u4e00\u5929\u6bd4\u4e00\u5929\u4f4e\uff0c\u4ed6\u4e0d\u77e5\u79bb\u7aef\u5348\u8282\u8fd8\u6709\u51e0\u5929\uff0c\u5374\u77e5\u51fa\u8c37\u7684\u65e5\u5b50\u4e0d\u8fdc\u4e86\u3002 \u3000\u3000\u4e00\u5929\u5348\u540e\uff0c\u4ed6\u4ece\u6c34\u7b19\u624b\u4e2d\u63a5\u8fc7\u4e86\u4e24\u53ea\u719f\u9e1f\uff0c\u6b63\u8981\u8f6c\u8eab\uff0c\u6c34\u7b19\u5ffd\u9053\uff1a\u201c\u72c4\u5927\u54e5\uff0c\u518d\u8fc7\u5f97\u51e0\u5929\uff0c\u54b1\u4eec\u4fbf\u80fd\u51fa\u53bb\u4e86\u7f62\uff1f\u201d\u72c4\u4e91\u201c\u55ef\u201d\u4e86\u4e00\u58f0\u3002\u6c34\u7b19\u4f4e\u58f0\u9053\uff1a\u201c\u591a\u8c22\u4f60\u8fd9\u4e9b\u65e5\u5b50\u4e2d\u5bf9\u6211\u7684\u7167\u62c2\uff0c\u82e5\u4e0d\u662f\u4f60\uff0c\u6211\u65e9\u6b7b\u5728\u82b1\u94c1\u5e72\u90a3\u6076\u4eba\u624b\u4e2d\u4e86\u3002\u201d\u72c4\u4e91\u6447\u5934\u9053\uff1a\u201c\u6ca1\u751a\u4e48\u3002\u201d\u8f6c\u8eab\u8d70\u5f00\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u8eab\u540e\u4e00\u9635\u545c\u54bd\u4e4b\u58f0\uff0c\u56de\u8fc7\u5934\u6765\uff0c\u53ea\u89c1\u6c34\u7b19\u4f0f\u5728\u4e00\u5757\u77f3\u4e0a\uff0c\u80cc\u5fc3\u62bd\u52a8\uff0c\u6b63\u81ea\u54ed\u6ce3\u3002\u4ed6\u5fc3\u4e2d\u5947\u602a\uff1a\u201c\u53ef\u4ee5\u51fa\u53bb\u4e86\uff0c\u8be5\u5f53\u9ad8\u5174\u624d\u662f\uff0c\u6709\u751a\u4e48\u597d\u54ed\u7684\uff1f\u5973\u4eba\u7684\u5fc3\u53e4\u602a\u5f97\u7d27\uff0c\u6211\u6c38\u8fdc\u4e0d\u4f1a\u660e\u767d\u3002\u201d \u3000\u3000\u5176\u5b9e\uff0c\u6c34\u7b19\u5230\u5e95\u4e3a\u751a\u4e48\u54ed\u6ce3\uff0c\u5979\u81ea\u5df1\u4e5f\u4e0d\u660e\u767d\uff0c\u53ea\u662f\u89c9\u5f97\u4f24\u5fc3\uff0c\u5fcd\u4e0d\u4f4f\u8981\u54ed\u3002 \u3000\u3000\u90a3\u5929\u591c\u91cc\uff0c\u72c4\u4e91\u7ec3\u4e86\u4e00\u4f1a\u529f\u592b\uff0c\u8eba\u5728\u6bcf\u65e5\u5b89\u7761\u7684\u90a3\u5757\u5927\u77f3\u4e0a\u7761\u7740\u4e86\u3002\u8fd9\u5757\u5927\u77f3\u79bb\u5c71\u6d1e\u4e0d\u8fdc\uff0c\u4ee5\u9632\u82b1\u94c1\u5e72\u534a\u591c\u91cc\u524d\u6765\u76d7\u5c38\u6216\u662f\u4fb5\u88ad\u6c34\u7b19\u3002\u4f46\u8fd9\u4e9b\u65f6\u65e5\u4e2d\u82b1\u94c1\u5e72\u59cb\u7ec8\u6ca1\u6709\u518d\u6765\uff0c\u6599\u60f3\u5df2\u7136\u65e0\u4e8b\uff0c\u662f\u4ee5\u4ed6\u5fc3\u65e0\u7275\u6302\uff0c\u7761\u5f97\u751a\u6c89\u3002 \u3000\u3000\u7761\u68a6\u4e4b\u4e2d\uff0c\u5ffd\u542c\u5f97\u8fdc\u5904\u9690\u9690\u6709\u811a\u6b65\u4e4b\u58f0\uff0c\u4ed6\u8fd9\u65f6\u5185\u529f\u6df1\u6e5b\uff0c\u8033\u76ee\u806a\u660e\uff0c\u548c\u6614\u65e5\u5df2\u5927\u4e0d\u76f8\u540c\uff0c\u811a\u6b65\u58f0\u867d\u8fdc\uff0c\u5df2\u4ee4\u4ed6\u4e00\u60ca\u800c\u9192\uff0c\u5f53\u5373\u7ffb\u8eab\u5750\u8d77\uff0c\u4fa7\u8033\u503e\u542c\uff0c\u53d1\u89c9\u6765\u4eba\u4f17\u591a\uff0c\u81f3\u5c11\u6709\u4e94\u516d\u5341\u4eba\uff0c\u6b63\u5feb\u6b65\u5411\u8c37\u4e2d\u800c\u6765\u3002 \u3000\u3000\u72c4\u4e91\u5403\u4e86\u4e00\u60ca\uff1a\u201c\u600e\u5730\u6709\u4eba\u80fd\u8fdb\u96ea\u8c37\u6765\uff1f\u201d\u4ed6\u4e0d\u77e5\u8c37\u4e2d\u5c71\u5cf0\u853d\u65e5\uff0c\u5bd2\u51b7\u5f97\u591a\uff0c\u5916\u9762\u79ef\u96ea\u5df2\u878d\uff0c\u8c37\u4e2d\u878d\u96ea\u5374\u8981\u8fdf\u5230\u4e00\u4e2a\u6708\u4ee5\u4e0a\u3002\u72c4\u4e91\u4e00\u8f6c\u5ff5\u95f4\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u4e9b\u4eba\u5b9a\u662f\u4e00\u8def\u8ffd\u8d76\u800c\u6765\u7684\u4e2d\u539f\u7fa4\u8c6a\u3002\u73b0\u4e0b\u8840\u5200\u8001\u7956\u5df2\u6b7b\uff0c\u751a\u4e48\u6028\u4ec7\u90fd\u5df2\u4e00\u4e86\u767e\u4e86\u3002\u55ef\uff0c\u6c34\u59d1\u5a18\u7684\u8868\u54e5\u4e00\u5b9a\u4e5f\u6765\u4e86\uff0c\u63a5\u4e86\u5979\u53bb\uff0c\u90a3\u4fbf\u518d\u597d\u4e0d\u8fc7\u3002\u4ed6\u4eec\u8ba4\u5b9a\u6211\u662f\u8840\u5200\u95e8\u7684\u6deb\u50e7\uff0c\u8fa9\u4e5f\u8fa9\u4e0d\u6e05\u695a\u7684\uff0c\u6211\u8fd8\u662f\u4e0d\u89c1\u4ed6\u4eec\u7684\u597d\u3002\u8ba9\u4ed6\u4eec\u63a5\u4e86\u6c34\u59d1\u5a18\u53bb\uff0c\u6211\u518d\u6162\u6162\u51fa\u53bb\u4e0d\u8fdf\u3002\u201d \u3000\u3000\u4ed6\u7ed5\u5230\u5c71\u6d1e\u4e4b\u4fa7\uff0c\u8eb2\u5728\u4e00\u5757\u5ca9\u77f3\u540e\u9762\u3002\u542c\u5f97\u811a\u6b65\u58f0\u8d8a\u6765\u8d8a\u8fd1\uff0c\u7a81\u7136\u95f4\u773c\u524d\u4e00\u4eae\uff0c\u53ea\u89c1\u4e00\u7fa4\u4eba\u8f6c\u8fc7\u4e86\u5c71\u5773\uff0c\u624b\u4e2d\u9ad8\u4e3e\u7740\u706b\u628a\u3002\u8fd9\u4f19\u4eba\u7ea6\u83ab\u6709\u4e94\u5341\u4f59\u4eba\uff0c\u6bcf\u4eba\u90fd\u662f\u4e00\u624b\u4e3e\u706b\u70ac\uff0c\u4e00\u624b\u63d0\u5175\u5203\u3002\u5f53\u5148\u4e00\u4eba\u767d\u987b\u98d8\u52a8\uff0c\u624b\u4e2d\u4e0d\u62ff\u706b\u628a\uff0c\u4e00\u624b\u5200\uff0c\u4e00\u624b\u5251\uff0c\u5374\u662f\u82b1\u94c1\u5e72\u3002 \u3000\u3000\u72c4\u4e91\u89c1\u4ed6\u4e0e\u6765\u4eba\u805a\u5728\u4e00\u8d77\uff0c\u5fae\u89c9\u8be7\u5f02\uff0c\u4f46\u968f\u5373\u7701\u609f\uff1a\u201c\u8fd9\u4e9b\u4eba\u4fbf\u662f\u4e00\u8def\u4ece\u6e56\u5317\u3001\u56db\u5ddd\u8ffd\u6765\u7684\uff0c\u82b1\u94c1\u5e72\u662f\u4ed6\u4eec\u7684\u9996\u9886\u4e4b\u4e00\uff0c\u5f53\u7136\uff0c\u4e00\u9047\u4e0a\u4fbf\u4f1a\u5408\u4e86\u3002\u5374\u4e0d\u77e5\u4ed6\u5728\u8bf4\u4e9b\u751a\u4e48\uff1f\u201d\u89c1\u4e00\u884c\u4eba\u8d70\u8fdb\u4e86\u5c71\u6d1e\uff0c\u5f53\u4e0b\u5411\u524d\u722c\u884c\u6570\u4e08\uff0c\u4f0f\u5728\u51b0\u96ea\u672a\u878d\u7684\u8349\u4e1b\u4e4b\u4e2d\u3002\u8fd9\u65f6\u4ed6\u548c\u4f17\u4eba\u76f8\u8ddd\u4ecd\u8fdc\uff0c\u4f46\u4ed6\u5185\u529f\u5728\u8fd9\u6570\u6708\u4e2d\u7a81\u98de\u731b\u8fdb\uff0c\u5df2\u80fd\u6e05\u695a\u542c\u5230\u5c71\u6d1e\u4e2d\u8bf8\u4eba\u8bf4\u8bdd\u3002 \u3000\u3000\u53ea\u542c\u5f97\u4e00\u4e2a\u7c97\u6da9\u7684\u58f0\u97f3\u9053\uff1a\u201c\u539f\u6765\u662f\u82b1\u5144\u624b\u5203\u4e86\u6076\u50e7\uff0c\u5b9e\u4e43\u53ef\u656c\u53ef\u8d3a\u3002\u82b1\u5144\u7acb\u6b64\u5927\u529f\uff0c\u4eca\u540e\u81ea\u7136\u662f\u4e2d\u539f\u7fa4\u4fa0\u7684\u9996\u9886\uff0c\u5927\u4f19\u513f\u9a6c\u9996\u662f\u77bb\uff0c\u60df\u547d\u662f\u4ece\u3002\u201d\u53e6\u4e00\u4eba\u9053\uff1a\u201c\u53ea\u53ef\u60dc\u9646\u5927\u4fa0\u3001\u5218\u9053\u957f\u3001\u6c34\u5927\u4fa0\u4e09\u4f4d\u60e8\u906d\u6a2a\u6b7b\uff0c\u4ee4\u4eba\u795e\u4f24\u3002\u201d\u53c8\u4e00\u4eba\u9053\uff1a\u201c\u8001\u6076\u50e7\u867d\u6b7b\uff0c\u5c0f\u6076\u50e7\u5c1a\u672a\u4f0f\u8bdb\u3002\u54b1\u4eec\u987b\u5f53\u7acb\u5373\u641c\u5bfb\uff0c\u65a9\u8349\u9664\u6839\uff0c\u4ee5\u514d\u66f4\u751f\u540e\u60a3\u3002\u82b1\u5927\u4fa0\uff0c\u4f60\u8bf4\u5982\u4f55\uff1f\u201d \u3000\u3000\u82b1\u94c1\u5e72\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5f20\u5144\u4e4b\u8a00\u5927\u6709\u89c1\u5730\u3002\u8fd9\u5c0f\u6076\u50e7\u4e00\u8eab\u90aa\u6d3e\u6b66\u529f\uff0c\u4e3a\u6076\u5b9e\u4e0d\u5728\u4e43\u5e08\u4e4b\u4e0b\uff0c\u6216\u8bb8\u72b9\u6709\u8fc7\u4e4b\u3002\u8fd9\u65f6\u5019\u4e0d\u77e5\u8eb2\u5230\u54ea\u91cc\u53bb\u4e86\u3002\u4ed6\u773c\u89c1\u5927\u4f19\u513f\u8fdb\u8c37\uff0c\u5b9a\u662f\u6025\u8c0b\u8131\u8eab\u3002\u4f17\u4f4d\u5144\u5f1f\uff0c\u54b1\u4eec\u522b\u6015\u8f9b\u82e6\uff0c\u987b\u5f97\u6740\u4e86\u90a3\u5c0f\u6076\u50e7\uff0c\u624d\u7b97\u5927\u529f\u544a\u6210\u3002\u201d \u3000\u3000\u72c4\u4e91\u5fc3\u4e2d\u6697\u60ca\uff1a\u201c\u8fd9\u59d3\u82b1\u7684\u80e1\u8bf4\u516b\u9053\uff0c\u6b79\u6bd2\u4e4b\u6781\uff0c\u5e78\u4e8f\u6211\u6ca1\u9c81\u83bd\u73b0\u8eab\uff0c\u5426\u5219\u4ed6\u4eec\u4e00\u9f50\u6765\u6740\u6211\uff0c\u6211\u600e\u80fd\u62b5\u6321\uff1f\u201d \u3000\u3000\u5ffd\u542c\u5f97\u4e00\u4e2a\u5973\u5b50\u7684\u58f0\u97f3\u9053\uff1a\u201c\u4ed6\u2026\u2026\u4ed6\u4e0d\u662f\u5c0f\u6076\u50e7\uff0c\u662f\u4e00\u4f4d\u6b63\u4eba\u541b\u5b50\u3002\u82b1\u94c1\u5e72\u624d\u662f\u4e2a\u5927\u574f\u86cb\uff01\u201d\u8bf4\u8bdd\u7684\u6b63\u662f\u6c34\u7b19\u3002 \u3000\u3000\u72c4\u4e91\u542c\u4e86\u8fd9\u51e0\u53e5\u8bdd\uff0c\u5fc3\u4e2d\u4e00\u9635\u5b89\u6170\uff0c\u7b2c\u4e00\u6b21\u542c\u5230\u5979\u4eb2\u53e3\u8bf4\u4e86\u51fa\u6765\uff1a\u201c\u4ed6\u4e0d\u662f\u5c0f\u6076\u50e7\uff0c\u662f\u4e00\u4f4d\u6b63\u4eba\u541b\u5b50\uff01\u201d\u8fd9\u4e9b\u65e5\u5b50\u4e2d\u6c34\u7b19\u663e\u7136\u5bf9\u4ed6\u4e0d\u518d\u8d77\u618e\u6076\u4e4b\u5fc3\uff0c\u4f46\u5c45\u7136\u80fd\u5bf9\u7740\u4f17\u4eba\u8bf4\u4ed6\u662f\u4e2a\u6b63\u4eba\u541b\u5b50\uff0c\u90a3\u786e\u4e5f\u5927\u51fa\u4ed6\u610f\u6599\u4e4b\u5916\u3002 \u3000\u3000\u7a81\u7136\u4e4b\u95f4\uff0c\u4ed6\u773c\u4e2d\u5ffd\u7136\u6d8c\u51fa\u4e86\u6cea\u6c34\uff0c\u5fc3\u4e2d\u8f7b\u8f7b\u7684\u9053\uff1a\u201c\u5979\u8bf4\u6211\u662f\u6b63\u4eba\u541b\u5b50\uff0c\u5979\u8bf4\u6211\u662f\u6b63\u4eba\u541b\u5b50\uff01\u201d \u3000\u3000\u6c34\u7b19\u8bf4\u4e86\u8fd9\u4e24\u53e5\u8bdd\uff0c\u6d1e\u4e2d\u8bf8\u4eba\u4f60\u77a7\u77a7\u6211\uff0c\u6211\u77a7\u77a7\u4f60\uff0c\u8c01\u4e5f\u4e0d\u4f5c\u58f0\u3002\u706b\u628a\u7167\u8000\u4e4b\u4e0b\uff0c\u72c4\u4e91\u8fdc\u8fdc\u671b\u53bb\uff0c\u5374\u4e5f\u770b\u5f97\u51fa\u8fd9\u4e9b\u4eba\u7684\u8138\u4e0a\u90fd\u6709\u9119\u5937\u4e4b\u8272\uff0c\u6709\u7684\u542b\u7740\u8ba5\u7b11\uff0c\u6709\u7684\u5374\u663e\u662f\u9887\u6709\u5e78\u707e\u4e50\u7978\u4e4b\u610f\u3002 \u3000\u3000\u9694\u4e00\u4f1a\u513f\uff0c\u4e00\u4e2a\u82cd\u8001\u7684\u58f0\u97f3\u9053\uff1a\u201c\u6c34\u4f84\u5973\uff0c\u6211\u8ddf\u4f60\u7239\u7239\u662f\u591a\u5e74\u8001\u53cb\uff0c\u4e0d\u5f97\u4e0d\u8bf4\u4f60\u51e0\u53e5\u3002\u8fd9\u5c0f\u6076\u50e7\u5bb3\u6b7b\u4e86\u4f60\u7239\u7239\u2026\u2026\u201d\u6c34\u7b19\u9053\uff1a\u201c\u4e0d\uff0c\u4e0d\u2026\u2026\u201d\u90a3\u8001\u4eba\u9053\uff1a\u201c\u4f60\u7239\u7239\u4e0d\u662f\u90a3\u5c0f\u548c\u5c1a\u6740\u7684\uff1f \u3000\u3000\u90a3\u4e48\u4ee4\u5c0a\u662f\u6b7b\u4e8e\u4f55\u4eba\u4e4b\u624b\uff1f\u201d\u6c34\u7b19\u9053\uff1a\u201c\u4ed6\u2026\u2026\u4ed6\u2026\u2026\u201d\u4e00\u65f6\u63a5\u4e0d\u4e0a\u53e3\u3002 \u3000\u3000\u90a3\u8001\u4eba\u9053\uff1a\u201c\u82b1\u5927\u4fa0\u8bf4\uff0c\u90a3\u65e5\u8c37\u4e2d\u6fc0\u6597\uff0c\u4ee4\u5c0a\u529b\u7aed\u88ab\u5236\uff0c\u662f\u90a3\u5c0f\u548c\u5c1a\u7528\u6811\u679d\u6253\u7834\u4e86\u4ed6\u5929\u7075\u76d6\u800c\u6b7b\uff0c\u662f\u4e5f\u4e0d\u662f\uff1f\u201d\u6c34\u7b19\u9053\uff1a\u201c\u4e0d\u9519\u3002\u53ef\u662f\uff0c\u53ef\u662f\u2026\u2026\u201d\u90a3\u8001\u4eba\u9053\uff1a\u201c\u53ef\u662f\u600e\u6837\uff1f\u201d\u6c34\u7b19\u9053\uff1a\u201c\u662f\u6211\u7239\u7239\u81ea\u5df1\u2026\u2026\u81ea\u5df1\u6c42\u4ed6\u6253\u6b7b\u7684\uff01\u201d \u3000\u3000\u5979\u6b64\u8a00\u4e00\u51fa\uff0c\u6d1e\u4e2d\u7a81\u7136\u7206\u53d1\u4e86\u4e00\u9635\u8f70\u7136\u5927\u7b11\uff0c\u7b11\u58f0\u53ea\u9707\u5f97\u6d1e\u8fb9\u6811\u679d\u4e0a\u534a\u878d\u4e0d\u878d\u7684\u79ef\u96ea\u7c0c\u7c0c\u800c\u843d\u3002 \u3000\u3000\u7b11\u58f0\u4e2d\u5939\u7740\u65e0\u6570\u8ba5\u5632\u4e4b\u8a00\uff1a\u201c\u81ea\u5df1\u6c42\u4ed6\u6253\u6b7b\uff0c\u54c8\u54c8\u54c8\uff01\u6492\u8c0e\u6492\u5f97\u592a\u4e5f\u6ed1\u7a3d\u3002\u201d\u201c\u539f\u6765\u6c34\u5927\u4fa0\u6d3b\u5f97\u4e0d\u8010\u70e6\u4e86\uff0c\u4f38\u4e86\u5934\u51fa\u6765\uff0c\u8bf7\u4ed6\u7684\u672a\u6765\u8d24\u5a7f\u6253\u4e2a\u5f00\u82b1\uff01\u201d\u201c\u8c01\u8bf4\u662f\u2018\u672a\u6765\u2019\u8d24\u5a7f\uff1f\u6c34\u5927\u4fa0\u53bb\u4e16\u4e4b\u65f6\uff0c\u90a3\u5c0f\u548c\u5c1a\u53ea\u6015\u65e9\u8ddf\u8fd9\u4f4d\u59d1\u5a18\u6709\u4e0a\u4e00\u624b\u4e86\uff0c\u54c8\u54c8\u54c8\uff01\u201d \u3000\u3000\u66f4\u6709\u51e0\u4e2a\u4eba\u5389\u58f0\u76f8\u65a5\uff1a\u201c\u4e16\u95f4\u7adf\u6709\u8fd9\u822c\u65e0\u803b\u7684\u5973\u5b50\uff0c\u4e3a\u4e86\u4e2a\u91ce\u7537\u4eba\uff0c\u8fde\u4eb2\u751f\u7236\u4eb2\u4e5f\u4e0d\u8981\u4e86\uff01\u201d\u4e5f\u6709\u4eba\u51b7\u8a00\u51b7\u8bed\u7684\u8bbd\u523a\uff1a\u201c\u8981\u91ce\u7537\u4eba\u4e0d\u8981\u7236\u4eb2\uff0c\u4e16\u4e0a\u90a3\u4e5f\u591a\u5f97\u7d27\u3002\u53ea\u4e0d\u8fc7\u6307\u4f7f\u5978\u592b\u6765\u6740\u81ea\u5df1\u7236\u4eb2\uff0c\u8fd9\u5c31\u9a87\u4eba\u542c\u95fb\u4e86\u3002\u201d\u53c8\u4e00\u4eba\u9053\uff1a\u201c\u6211\u53ea\u542c\u89c1\u8fc7\u751a\u4e48\u2018\u604b\u5978\u60c5\u70ed\uff0c\u8c0b\u6740\u4eb2\u592b\u2019\u3002\u4eca\u65e5\u4e16\u9053\u53ef\u5927\u4e0d\u76f8\u540c\u4e86\uff0c\u5c45\u7136\u6709\u2018\u604b\u5978\u60c5\u70ed\uff0c\u8c0b\u6740\u4eb2\u7236\u2019\uff0c\u54c8\u54c8\u54c8\uff01\u201d \u3000\u3000\u5927\u5bb6\u542c\u4e86\u82b1\u94c1\u5e72\u7684\u8bdd\uff0c\u5148\u5165\u4e3a\u4e3b\uff0c\u8ba4\u5b9a\u6c34\u7b19\u548c\u72c4\u4e91\u65e9\u5df2\u6709\u4e86\u4e0d\u53ef\u544a\u4eba\u7684\u52fe\u5f53\uff0c\u6124\u6068\u5979\u56de\u62a4\u201c\u5978\u592b\u201d\uff0c\u56e0\u6b64\u8bf4\u51fa\u6765\u7684\u8bdd\u7adf\u8d8a\u6765\u8d8a\u4e0d\u4e2d\u542c\u3002\u8fd9\u4e9b\u6c5f\u6e56\u4e0a\u7684\u7c97\u4eba\uff0c\u6709\u751a\u4e48\u6c61\u8a00\u79fd\u8bed\u8bf4\u4e0d\u51fa\u53e3\uff1f \u3000\u3000\u6c34\u7b19\u6ee1\u8138\u901a\u7ea2\uff0c\u5927\u58f0\u9053\uff1a\u201c\u4f60\u4eec\u5728\u8bf4\u2026\u2026\u8bf4\u4e9b\u751a\u4e48\uff1f\u5374\u4e5f\u4e0d\u77e5\u7f9e\u803b\uff1f\u201d \u3000\u3000\u90a3\u4e9b\u4eba\u53c8\u662f\u4e00\u9635\u54c4\u7b11\u3002\u6709\u4eba\u9053\uff1a\u201c\u5374\u539f\u6765\u8fd8\u662f\u6211\u4eec\u4e0d\u77e5\u7f9e\u803b\u4e86\uff0c\u771f\u662f\u6ed1\u5929\u4e0b\u4e4b\u5927\u7a3d\u3002\u201d\u201c\u597d\uff0c\u597d\uff01\u6c34\u59d1\u5a18\uff0c\u6211\u4eec\u4e0d\u77e5\u7f9e\u803b\u3002\u4f60\u548c\u90a3\u5c0f\u548c\u5c1a\u5728\u8fd9\u5c71\u6d1e\u4e2d\u537f\u537f\u6211\u6211\uff0c\u628a\u4eb2\u7236\u7684\u5927\u4ec7\u629b\u5728\u8111\u540e\uff0c\u90a3\u5c31\u662f\u77e5\u9053\u7f9e\u803b\u4e86\uff1f\u201d\u53e6\u4e00\u4e2a\u7c97\u8c6a\u7684\u58f0\u97f3\u9a82\u4e86\u8d77\u6765\uff1a \u3000\u3000\u201c\u4ed6\u5988\u7684\uff0c\u8001\u5b50\u4ece\u6e56\u5317\u4e00\u8def\u5df4\u5df4\u7684\u8ffd\u4e86\u4e0b\u6765\uff0c\u9a6c\u4e0d\u505c\u8e44\u7684\uff0c\u5c31\u662f\u4e3a\u4e86\u6551\u4f60\u8fd9\u5c0f\u5a4a\u5b50\u3002\u4f60\u8fd9\u8d31\u4eba\u8fd9\u4e48\u65e0\u803b\uff0c\u8001\u5b50\u4e00\u5200\u5148\u5c06\u4f60\u780d\u4e86\u3002\u201d\u65c1\u8fb9\u6709\u4eba\u529d\u9053\uff1a\u201c\u4f7f\u4e0d\u5f97\uff0c\u4f7f\u4e0d\u5f97\uff0c\u8d75\u5144\u4e0d\u53ef\u9c81\u83bd\uff01\u201d \u3000\u3000\u90a3\u82cd\u8001\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u5404\u4f4d\u5fcd\u4e00\u5fcd\u6c14\u3002\u6c34\u59d1\u5a18\u5e74\u7eaa\u8f7b\uff0c\u6ca1\u89c1\u8bc6\u3002\u6c34\u5927\u4fa0\u4e0d\u5e78\u901d\u4e16\uff0c\u5979\u5b64\u82e6\u4f36\u4ec3\u7684\u6ca1\u4eba\u7167\u6599\uff0c\u5927\u5bb6\u522b\u8ddf\u5979\u4e3a\u96be\u3002\u4ee5\u540e\u5979\u7531\u82b1\u5927\u4fa0\u629a\u517b\uff0c\u597d\u597d\u7684\u6559\u5bfc\uff0c\u81ea\u4f1a\u8d70\u4e0a\u6b63\u9014\u3002 \u3000\u3000\u5927\u4f19\u513f\u5634\u4e0a\u79ef\u70b9\u513f\u5fb7\uff0c\u8fd9\u96ea\u8c37\u4e2d\u7684\u4e8b\u561b\uff0c\u522b\u5728\u6c5f\u6e56\u4e0a\u4f20\u626c\u51fa\u53bb\u3002\u6c34\u5927\u4fa0\u751f\u524d\u5f85\u4eba\u4ec1\u4e49\uff0c\u5426\u5219\u5927\u5bb6\u600e\u80af\u4e0d\u8f9e\u52b3\u82e6\u7684\u8d76\u6765\u6551\u5979\u5973\u513f\uff1f\u54b1\u4eec\u987b\u5f53\u987e\u5168\u6c34\u5927\u4fa0\u7684\u989c\u9762\uff0c\u8fd9\u4ef6\u4e8b\u5c31\u522b\u518d\u63d0\u4e86\u3002\u6211\u8bf4\u5462\uff0c\u54b1\u4eec\u8fd8\u662f\u5feb\u53bb\u6293\u4e86\u90a3\u5c0f\u548c\u5c1a\u6765\u662f\u6b63\u7ecf\uff0c\u5c06\u4ed6\u5f00\u819b\u7834\u809a\uff0c\u796d\u5960\u6c34\u5927\u4fa0\u7684\u82f1\u9b42\u3002\u201d \u3000\u3000\u8bf4\u8bdd\u7684\u8001\u4eba\u5927\u6982\u5fb7\u9ad8\u671b\u91cd\uff0c\u9887\u5f97\u8bf8\u4eba\u7684\u5c0a\u656c\uff0c\u4ed6\u8fd9\u756a\u8bdd\u4e00\u8bf4\uff0c\u4eba\u7fa4\u4e2d\u6709\u4e0d\u5c11\u58f0\u97f3\u9644\u548c\uff0c\u90fd\u9053\uff1a\u201c\u662f\uff0c\u662f\uff0c\u5f20\u8001\u82f1\u96c4\u7684\u8bdd\u6709\u7406\u3002\u54b1\u4eec\u53bb\u627e\u90a3\u5c0f\u548c\u5c1a\uff0c\u6293\u4e86\u4ed6\u6765\u788e\u5c38\u4e07\u6bb5\uff01\u201d \u3000\u3000\u4f17\u4eba\u5608\u6742\u53eb\u56a3\u58f0\u4e2d\uff0c\u6c34\u7b19\u201c\u54c7\u201d\u7684\u4e00\u58f0\u3002\u54ed\u4e86\u51fa\u6765\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u8fdc\u5904\u6709\u4eba\u957f\u58f0\u53eb\u9053\uff1a\u201c\u8868\u59b9\uff0c\u8868\u59b9\uff01\u4f60\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u6c34\u7b19\u4e00\u542c\u5230\u8fd9\u58f0\u97f3\uff0c\u77e5\u662f\u8868\u54e5\u6c6a\u5578\u98ce\u5bfb\u5979\u6765\u4e86\uff0c\u81ea\u5df1\u53d7\u4e86\u51a4\u6789\uff0c\u82e6\u906d\u7f9e\u8fb1\uff0c\u7a81\u7136\u542c\u5230\u4eb2\u4eba\u7684\u58f0\u97f3\uff0c\u5982\u4f55\u4e0d\u559c\uff1f\u5f53\u4e0b\u6b62\u4e86\u54ed\u6ce3\uff0c\u5954\u5411\u6d1e\u53e3\u3002 \u3000\u3000\u6709\u4eba\u4fbf\u9053\uff1a\u201c\u8fd9\u75f4\u5fc3\u7684\u6c6a\u5578\u98ce\u77e5\u9053\u4e86\u771f\u76f8\uff0c\u53ea\u6015\u8981\u53d1\u75af\uff01\u201d \u3000\u3000\u90a3\u59d3\u5f20\u7684\u8001\u8005\u9053\uff1a\u201c\u5927\u5bb6\u522b\u5435\uff0c\u542c\u6211\u4e00\u53e5\u8bdd\uff0c\u8fd9\u4f4d\u6c6a\u5bb6\u5c0f\u54e5\u5bf9\u6c34\u59d1\u5a18\u5012\u662f\u4e00\u7247\u771f\u60c5\uff0c\u96ea\u8fd8\u6ca1\u6d88\u5c3d\uff0c\u4ed6\u5c31\u65e9\u4e86\u4e24\u65e5\u95ef\u8fdb\u8c37\u6765\uff0c\u60f3\u662f\u8def\u4e0a\u4e0d\u597d\u8d70\uff0c\u5931\u9677\u5728\u751a\u4e48\u5730\u65b9\uff0c\u6b32\u901f\u5219\u4e0d\u8fbe\uff0c\u53cd\u800c\u843d\u5728\u54b1\u4eec\u540e\u5934\u4e86\u3002\u5404\u4f4d\uff0c\u8fd9\u4eba\u4e5f\u662f\u547d\u91cc\u4e0d\u597d\uff0c\u5927\u5bb6\u5634\u5934\u4e0a\u4fee\u79ef\u9634\u5fb7\uff0c\u6c34\u59d1\u5a18\u8ddf\u90a3\u5c0f\u548c\u5c1a\u7684\u4e11\u4e8b\uff0c\u5c31\u522b\u5bf9\u4ed6\u8bf4\u3002\u201d\u7fa4\u8c6a\u4e2d\u6709\u4e9b\u5fe0\u539a\u7684\u4fbf\u9053\uff1a\u201c\u6b63\u8be5\u5982\u6b64\uff01\u6c34\u59d1\u5a18\u4e00\u65f6\u5931\u8db3\uff0c\u987b\u5f53\u8ba9\u5979\u6709\u6761\u81ea\u65b0\u4e4b\u8def\u3002\u4f55\u51b5\u8fd9\u5927\u534a\u4e5f\u662f\u8feb\u4e8e\u65e0\u5948\u3002\u5426\u5219\u597d\u7aef\u7aef\u4e00\u4e2a\u540d\u95e8\u95fa\u5973\uff0c\u600e\u4f1a\u53bb\u8ddf\u4e00\u4e2a\u90aa\u6d3e\u548c\u5c1a\u59d8\u4e0a\u4e86\uff1f\u201d \u3000\u3000\u5374\u6709\u4eba\u8bf4\u9053\uff1a\u201c\u6c6a\u5578\u98ce\u8fd9\u4e48\u4e00\u4e2a\u6f02\u4eae\u54e5\u513f\uff0c\u5e73\u767d\u65e0\u7aef\u7684\u6234\u4e0a\u4e86\u4e00\u9876\u7eff\u5e3d\u5b50\uff0c\u672a\u514d\u592a\u59d4\u5c48\u4e86\u4ed6\u7f62\uff0c\u54c8\u54c8\uff01\u201d\u201c\u8fd9\u53eb\u505a\u4e00\u4e2a\u613f\u6253\uff0c\u4e00\u4e2a\u613f\u6328\u3002\u94b1\u5144\uff0c\u4f60\u51fa\u95e8\u8fd9\u4e48\u4e45\uff0c\u5ac2\u5b50\u5728\u5bb6\u4e2d\u5bc2\u5bde\u5b64\u5355\uff0c\u8bf4\u4e0d\u5b9a\u4f60\u5934\u4e0a\u8fd9\u9876\u5e3d\u513f\uff0c\u4e5f\u6709\u70b9\u7eff\u6cb9\u6cb9\u4e86\u5462\uff1f\u201d\u201c\u4ed6\u5988\u7684\uff0c\u4f60\u5976\u5976\u96c4\uff0c\u8fd9\u4f1a\u513f\u4f60\u8001\u5a46\u624d\u5bc2\u5bde\u5b64\u5355\uff01\u201d\u201c\u4e0d\u9519\uff0c\u4e0d\u9519\uff0c\u6211\u8001\u5a46\u5bc2\u5bde\u5b64\u5355\uff0c\u4f60\u5c0a\u592b\u4eba\u8fd9\u4f1a\u513f\u6709\u4eba\u966a\u4f34\uff0c\u98ce\u6d41\u5feb\u6d3b\uff0c\u4e00\u70b9\u513f\u4e5f\u4e0d\u5bc2\u5bde\u5b64\u5355\u2026\u2026\u201d\u8bdd\u672a\u8bf4\u5b8c\uff0c\u7830\u7684\u4e00\u58f0\uff0c\u80a9\u5934\u5df2\u6328\u4e86\u4e00\u62f3\u3002 \u3000\u3000\u4f17\u4eba\u563b\u7b11\u4e0d\u7edd\u3002 \u3000\u3000\u53ea\u542c\u5f97\u6c6a\u5578\u98ce\u5927\u53eb\u201c\u8868\u59b9\uff0c\u8868\u59b9\u201d\u7684\u58f0\u97f3\u53c8\u6e10\u6e10\u8fdc\u53bb\uff0c\u663e\u662f\u6ca1\u77e5\u4f17\u4eba\u5728\u6b64\u3002\u6c34\u7b19\u5954\u51fa\u5c71\u6d1e\uff0c\u53eb\u9053\uff1a\u201c\u8868\u54e5\uff0c\u8868\u54e5\uff01\u6211\u5728\u8fd9\u91cc\uff0c\u6211\u5728\u8fd9\u91cc\uff01\u201d\u6c6a\u5578\u98ce\u53c8\u53eb\u4e86\u58f0\uff1a\u201c\u8868\u59b9\uff0c\u8868\u59b9\uff0c\u4f60\u5728\u54ea\u91cc\uff1f\u201d\u6c34\u7b19\u7eb5\u58f0\u53eb\u9053\uff1a\u201c\u6211\u5728\u8fd9\u91cc\uff01\u201d \u3000\u3000\u4e1c\u5317\u89d2\u4e0a\u4e00\u4e2a\u4eba\u5f71\u98de\u9a70\u800c\u6765\uff0c\u4e00\u9762\u5954\u8dd1\uff0c\u4e00\u9762\u5927\u53eb\u201c\u8868\u59b9\uff01\u201d\u7a81\u7136\u95f4\u811a\u4e0b\u4e00\u6ed1\uff0c\u6454\u5012\u5728\u5730\u3002\u6c34\u7b19\u201c\u554a\u201d\u7684\u4e00\u58f0\uff0c\u751a\u662f\u5173\u5207\uff0c\u5411\u4ed6\u8fce\u4e86\u4e0a\u53bb\u3002\u539f\u6765\u6c6a\u5578\u98ce\u542c\u5230\u4e86\u6c34\u7b19\u7684\u58f0\u97f3\uff0c\u5927\u559c\u4e4b\u4e0b\uff0c\u5168\u6ca1\u7559\u795e\u811a\u4e0b\u7684\u6d1e\u5751\u5c71\u6c9f\uff0c\u4e00\u811a\u8e0f\u5728\u4f4e\u9677\u4e4b\u5904\uff0c\u6454\u4e86\u4e00\u4ea4\uff0c\u968f\u5373\u8dc3\u8d77\uff0c\u6025\u5954\u800c\u6765\u3002\u6c34\u7b19\u4e5f\u5411\u4ed6\u5954\u53bb\u3002 \u3000\u3000\u4e24\u4eba\u5954\u5230\u4e34\u8fd1\uff0c\u9f50\u58f0\u6b22\u547c\uff0c\u76f8\u62e5\u5728\u4e00\u8d77\u3002 \u3000\u3000\u72c4\u4e91\u89c1\u5230\u4e24\u4eba\u76f8\u4f1a\u65f6\u6b22\u559c\u4eb2\u70ed\u7684\u60c5\u72b6\uff0c\u5fc3\u4e2d\u6ca1\u6765\u7531\u7684\u5fae\u5fae\u4e00\u9178\u3002\u4ed6\u59cb\u7ec8\u4e0d\u80fd\u5fd8\u60c5\u4e8e\u5e08\u59b9\u621a\u82b3\uff0c\u867d\u5728\u96ea\u8c37\u4e2d\u548c\u6c34\u7b19\u540c\u4f4f\u534a\u8f7d\uff0c\u5fc3\u4e2d\u4ece\u672a\u5bf9\u5979\u751f\u8fc7\u4e1d\u6beb\u7537\u5973\u4e4b\u60c5\u3002\u53ea\u662f\u76f8\u5904\u65e5\u4e45\uff0c\u4e00\u65e6\u5206\u624b\uff0c\u603b\u4e0d\u514d\u6709\u4f9d\u4f9d\u4e4b\u611f\uff0c\u5fc3\u60f3\uff1a\u201c\u5979\u968f\u8868\u54e5\u800c\u53bb\uff0c\u90a3\u662f\u518d\u597d\u4e5f\u6ca1\u6709\u4e86\uff0c\u4f46\u613f\u5979\u4eca\u540e\u65e0\u707e\u65e0\u96be\uff0c\u5ac1\u4e86\u5979\u8868\u54e5\uff0c\u4e00\u751f\u5e73\u5b89\u559c\u4e50\u3002\u201d \u3000\u3000\u5ffd\u542c\u5f97\u6c6a\u5578\u98ce\u653e\u58f0\u5927\u54ed\uff0c\u60f3\u5fc5\u662f\u6c34\u7b19\u8ddf\u4ed6\u8bf4\u4e86\u6c34\u5cb1\u901d\u4e16\u7684\u6d88\u606f\u3002\u8fc7\u4e86\u4e00\u4f1a\uff0c\u89c1\u6c6a\u5578\u98ce\u643a\u7740\u6c34\u7b19\u4e4b\u624b\uff0c\u5e76\u80a9\u8fc7\u6765\u3002 \u3000\u3000\u6c6a\u5578\u98ce\u545c\u54bd\u9053\uff1a\u201c\u8205\u8205\u4e0d\u5e78\u906d\u96be\uff0c\u6211\u2026\u2026\u6211\u2026\u2026\u6211\u4ece\u5c0f\u5f97\u4ed6\u629a\u517b\u957f\u5927\uff0c\u4ed6\u5f85\u6211\u5c31\u50cf\u662f\u4eb2\u751f\u513f\u5b50\u4e00\u822c\u3002\u201d\u6c34\u7b19\u542c\u4ed6\u8bf4\u5230\u7236\u4eb2\uff0c\u4e0d\u7981\u53c8\u6d41\u4e0b\u6cea\u6765\u3002\u6c6a\u5578\u98ce\u4f4e\u58f0\u9053\uff1a\u201c\u8868\u59b9\uff0c\u81ea\u4eca\u800c\u540e\uff0c\u4f60\u6211\u518d\u4e5f\u4e0d\u5206\u5f00\u4e86\uff0c\u4f60\u522b\u96be\u8fc7\uff0c\u6211\u4e00\u8f88\u5b50\u603b\u662f\u597d\u597d\u7684\u5f85\u4f60\u3002\u201d\u6c34\u7b19\u81ea\u5e7c\u4fbf\u5bf9\u8fd9\u4f4d\u8868\u54e5\u5341\u5206\u503e\u6155\uff0c\u8fd9\u756a\u5206\u5f00\uff0c\u66f4\u662f\u601d\u5ff5\u6bb7\u5207\uff0c\u542c\u4ed6\u8fd9\u4e48\u8bf4\uff0c\u8138\u4e0a\u4e00\u7ea2\uff0c\u5fc3\u4e2d\u611f\u5230\u4e00\u9635\u751c\u751c\u4e4b\u610f\u3002 \u3000\u3000\u4e24\u4eba\u6e10\u6e10\u8d70\u8fd1\u5c71\u6d1e\uff0c\u6c34\u7b19\u5ffd\u7136\u7acb\u5b9a\uff0c\u8bf4\u9053\uff1a\u201c\u8868\u54e5\uff0c\u4f60\u548c\u6211\u5373\u523b\u8d70\u7f62\uff0c\u6211\u4e0d\u613f\u89c1\u90a3\u4e9b\u4eba\u4e86\u3002\u201d\u6c6a\u5578\u98ce\u5947\u9053\uff1a\u201c\u4e3a\u751a\u4e48\uff1f\u8fd9\u8bb8\u591a\u4f2f\u4f2f\u53d4\u53d4\u548c\u597d\u670b\u53cb\uff0c\u5927\u5bb6\u4e0d\u8f9e\u8270\u9669\u7684\u524d\u6765\u6551\u4f60\uff0c\u5728\u96ea\u8c37\u5916\u5b88\u5019\u4e86\u5927\u534a\u5e74\uff0c\u53ef\u7b97\u5f97\u4e49\u6c14\u6df1\u91cd\uff0c\u54b1\u4eec\u600e\u80fd\u4e0d\u597d\u597d\u7684\u8c22\u8c22\u4ed6\u4eec\uff1f\u201d\u6c34\u7b19\u4f4e\u4e0b\u4e86\u5934\uff0c\u9053\uff1a\u201c\u6211\u5df2\u8c22\u8fc7\u4ed6\u4eec\u4e86\u3002\u201d\u6c6a\u5578\u98ce\u9053\uff1a\u201c\u5927\u4f19\u513f\u5343\u91cc\u8fe2\u8fe2\u7684\u4ece\u6e56\u5317\u8d76\u5230\u8fd9\u513f\uff0c\u540c\u6765\u540c\u56de\uff0c\u5c82\u4e0d\u662f\u597d\uff1f \u3000\u3000\u518d\u8bf4\uff0c\u8205\u8205\u7684\u9057\u4f53\u662f\u8981\u8fd0\u56de\u6545\u4e61\u5462\uff0c\u8fd8\u662f\u5c31\u846c\u5728\u8fd9\u91cc\uff0c\u4e5f\u5f97\u5411\u957f\u8f88\u4eec\u8bf7\u793a\u3002\u9646\u4f2f\u4f2f\u3001\u82b1\u4f2f\u4f2f\u3001\u5218\u9053\u957f\u8fd9\u4e09\u4f4d\u600e\u6837\u4e86\uff1f\u201d \u3000\u3000\u6c34\u7b19\u9053\uff1a\u201c\u4f60\u548c\u6211\u5148\u51fa\u53bb\uff0c\u6162\u6162\u518d\u8ddf\u4f60\u8bf4\u3002\u82b1\u4f2f\u4f2f\u662f\u4e2a\u5927\u574f\u86cb\uff0c\u4f60\u522b\u542c\u4ed6\u80e1\u8bf4\uff01\u201d\u6c6a\u5578\u98ce\u81ea\u6765\u5bf9\u5979\u4ece\u4e0d\u8fdd\u62d7\uff0c\u8fd9\u65f6\u9ed1\u6697\u4e2d\u867d\u89c1\u4e0d\u5230\u5979\u98ce\u59ff\uff0c\u4f46\u4e00\u542c\u5230\u5979\u67d4\u8f6f\u751c\u7f8e\u7684\u8bed\u58f0\uff0c\u65e9\u5df2\u5fc3\u9189\uff0c\u4fbf\u60f3\u987a\u5979\u610f\u601d\uff0c\u5148\u884c\u79bb\u53bb\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u5c71\u6d1e\u53e3\u4e00\u4eba\u9053\uff1a\u201c\u6c6a\u8d24\u4f84\uff0c\u4f60\u8fc7\u6765\uff01\u201d\u6b63\u662f\u82b1\u94c1\u5e72\u7684\u58f0\u97f3\u3002\u6c6a\u5578\u98ce\u9053\uff1a\u201c\u662f\uff0c\u82b1\u4f2f\u4f2f\uff01\u201d\u6c34\u7b19\u5927\u6025\uff0c\u987f\u8db3\u9053\uff1a\u201c\u4f60\u4e0d\u542c\u6211\u8bdd\u4e48\uff1f\u201d\u6c6a\u5578\u98ce\u5fc3\u60f3\uff1a\u201c\u82b1\u4f2f\u4f2f\u662f\u8205\u8205\u7684\u4e49\u5144\uff0c\u957f\u8005\u4e4b\u547d\uff0c\u5982\u4f55\u53ef\u8fdd\uff1f\u8fd9\u8bb8\u591a\u670b\u53cb\u4e3a\u4e86\u76f8\u6551\u8868\u59b9\uff0c\u5982\u6b64\u4e0d\u8f9e\u8f9b\u52b3\uff0c\u5927\u529f\u544a\u6210\u4e4b\u540e\u5374\u5f03\u4e4b\u4e0d\u987e\uff0c\u81ea\u884c\u79bb\u53bb\uff0c\u90a3\u65e0\u8bba\u5982\u4f55\u8bf4\u4e0d\u8fc7\u53bb\u3002\u8fd9\u4e00\u6765\uff0c\u6211\u58f0\u540d\u626b\u5730\uff0c\u4ee5\u540e\u5728\u6c5f\u6e56\u4e0a\u600e\u80fd\u7acb\u8db3\uff1f\u8868\u59b9\u662f\u5c0f\u5b69\u5b50\u813e\u6c14\uff0c\u5f85\u4f1a\u54c4\u5979\u4e00\u54c4\uff0c\u8d54\u4e2a\u4e0d\u662f\uff0c\u4e5f\u5c31\u662f\u4e86\u3002\u201d\u5f53\u5373\u643a\u4e86\u5979\u624b\uff0c\u8d70\u5411\u5c71\u6d1e\u3002 \u3000\u3000\u6c34\u7b19\u660e\u77e5\u82b1\u94c1\u5e72\u8981\u8bf4\u7684\u51b3\u4e0d\u662f\u597d\u8bdd\uff0c\u4f46\u60f3\uff1a\u201c\u6211\u6e05\u6e05\u767d\u767d\uff0c\u95ee\u5fc3\u65e0\u6127\uff0c\u4efb\u4ed6\u5982\u4f55\u6c61\u8a00\u8bec\u9677\uff0c\u4e8e\u6211\u4f55\u635f\uff1f\u201d\u5f53\u4e0b\u4fbf\u968f\u4e86\u6c6a\u5578\u98ce\u8d70\u53bb\uff0c\u8138\u4e0a\u5374\u5df2\u5168\u65e0\u8840\u8272\u3002 \u3000\u3000\u4e24\u4eba\u8d70\u5230\u6d1e\u53e3\u3002\u82b1\u94c1\u5e72\u9053\uff1a\u201c\u6c6a\u8d24\u4f84\uff0c\u4f60\u6765\u4e86\u5f88\u597d\u3002\u8840\u5200\u6076\u50e7\u5df2\u88ab\u6211\u6740\u4e86\uff0c\u4f46\u8fd8\u6709\u4e00\u4e2a\u5c0f\u548c\u5c1a\u6f0f\u7f51\uff0c\u54b1\u4eec\u52a1\u5f53\u5c06\u4ed6\u64d2\u6765\u6740\u5374\u3002\u8fd9\u5c0f\u548c\u5c1a\u662f\u5bb3\u6b7b\u4f60\u8205\u8205\u7684\u51f6\u624b\u3002\u201d\u6c6a\u5578\u98ce\u5927\u53eb\u4e00\u58f0\uff0c\u5237\u7684\u4e00\u4e0b\u4fbf\u62d4\u5251\u51fa\u9798\uff0c\u8ddf\u7740\u56de\u5934\u5411\u6c34\u7b19\u77a7\u53bb\uff0c\u6025\u6b32\u770b\u770b\u8fd9\u4f4d\u8868\u59b9\u522b\u6765\u5982\u4f55\u3002 \u3000\u3000\u706b\u5149\u4e4b\u4e0b\uff0c\u53ea\u89c1\u5979\u5bb9\u989c\u6194\u60b4\uff0c\u6cea\u76c8\u4e8e\u7736\u3002\u6c6a\u5578\u98ce\u5fc3\u4e0b\u601c\u60dc\uff0c\u5374\u89c1\u5979\u5728\u7f13\u7f13\u6447\u5934\uff0c\u95ee\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u6c34\u7b19\u9053\uff1a\u201c\u6211\u7239\u7239\u4e0d\u662f\u90a3\u2026\u2026\u90a3\u2026\u2026\u4eba\u5bb3\u6b7b\u7684\u3002\u201d \u3000\u3000\u4f17\u4eba\u542c\u5979\u8fd9\u4e48\u8bf4\uff0c\u5c3d\u7686\u6124\u6012\uff0c\u5747\u60f3\uff1a\u201c\u6211\u4eec\u4e3a\u4e86\u4f60\u4eca\u540e\u597d\u505a\u4eba\uff0c\u77a7\u5728\u6c34\u5927\u4fa0\u7684\u9762\u4e0a\uff0c\u624d\u4e0d\u6cc4\u9732\u4f60\u548c\u5c0f\u6deb\u50e7\u7684\u4e11\u4e8b\uff0c\u8fd9\u65f6\u5019\u4f60\u5c45\u7136\u8fd8\u5728\u56de\u62a4\u5c0f\u6deb\u50e7\uff0c\u5f53\u771f\u662f\u7f6a\u4e0d\u5bb9\u6055\u4e86\u3002\u4f60\u8fde\u2018\u5c0f\u548c\u5c1a\u2019\u4e09\u5b57\u4e5f\u4e0d\u80af\u8bf4\u3002\u8fd8\u5728\u2018\u90a3\u4eba\u3001\u90a3\u4eba\u2019\u7684\uff0c\u5b9e\u662f\u65e0\u803b\u5df2\u6781\uff01\u201d \u3000\u3000\u6c6a\u5578\u98ce\u89c1\u5404\u4eba\u8138\u4e0a\u5747\u73b0\u6012\u8272\uff0c\u5f88\u89c9\u5947\u602a\uff0c\u5fc3\u60f3\u8868\u59b9\u4e0d\u80af\u548c\u4f17\u4eba\u76f8\u89c1\uff0c\u800c\u5927\u4f19\u53c8\u5bf9\u5979\u9887\u542b\u654c\u610f\uff0c\u4e2d\u95f4\u5b9a\u662f\u53e6\u6709\u9690\u60c5\uff0c\u4fbf\u9053\uff1a\u201c\u8868\u59b9\uff0c\u54b1\u4eec\u542c\u82b1\u4f2f\u4f2f\u5429\u5490\uff0c\u5148\u53bb\u6349\u4e86\u90a3\u5c0f\u548c\u5c1a\u6765\uff0c\u5c06\u4ed6\u5343\u5200\u4e07\u6bb5\uff0c\u796d\u6211\u8205\u8205\u3002\u5176\u4f59\u7684\u4e8b\uff0c\u6162\u6162\u518d\u8bf4\u4e0d\u8fdf\u3002\u201d \u3000\u3000\u6c34\u7b19\u9053\uff1a\u201c\u4ed6\u2026\u2026\u4ed6\u4e5f\u4e0d\u662f\u5c0f\u548c\u5c1a\u3002\u201d \u3000\u3000\u6c6a\u5578\u98ce\u4e00\u6115\uff0c\u89c1\u5230\u8eab\u65c1\u4f17\u4eba\u5747\u73b0\u9119\u5937\u4e4b\u6001\uff0c\u5fc3\u4e2d\u4e00\u51db\uff0c\u9690\u9690\u89c9\u5f97\u4e0d\u5bf9\u3002\u4ed6\u4e0d\u613f\u5373\u884c\u67e5\u7a76\u6b64\u4e8b\uff0c\u8fd8\u5251\u5165\u9798\uff0c\u5927\u58f0\u9053\uff1a\u201c\u4f17\u4f4d\u4f2f\u4f2f\u53d4\u53d4\uff0c\u597d\u670b\u53cb\uff0c\u8bf7\u5927\u5bb6\u518d\u8f9b\u82e6\u4e00\u756a\u4e86\uff0c\u4e86\u7ed3\u6b64\u4e8b\u3002\u59d3\u6c6a\u7684\u518d\u9010\u4e00\u62dc\u8c22\u5404\u4f4d\u7684\u5927\u6069\u5927\u5fb7\u3002\u201d\u8bf4\u7740\u4e00\u63d6\u5230\u5730\u3002 \u3000\u3000\u4f17\u4eba\u90fd\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5feb\u53bb\u6349\u62ff\u5c0f\u6076\u50e7\u8981\u7d27\uff0c\u522b\u8ba9\u4ed6\u51fa\u8c37\u8dd1\u4e86\uff01\u201d\u8bf4\u7740\u7eb7\u7eb7\u51b2\u51fa\u6d1e\u53bb\u3002 \u3000\u3000\u4e0d\u77e5\u662f\u8c01\u5728\u6d1e\u53e3\u6389\u4e86\u4e00\u6839\u706b\u628a\uff0c\u706b\u5149\u5728\u8c37\u98ce\u4e2d\u65f6\u65fa\u65f6\u5f31\uff0c\u7167\u5f97\u201c\u94c3\u5251\u53cc\u4fa0\u201d\u4e8c\u4eba\u8138\u4e0a\u4e5f\u662f\u4e00\u9635\u4eae\uff0c\u4e00\u9635\u6697\u3002\u4e24\u4eba\u6267\u624b\u76f8\u5bf9\uff0c\u5fc3\u4e2d\u5747\u6709\u5343\u8a00\u4e07\u8bed\uff0c\u4e0d\u77e5\u4ece\u4f55\u8bf4\u8d77\u3002 \u3000\u3000\u72c4\u4e91\u5fc3\u60f3\uff1a\u201c\u4ed6\u8868\u5144\u59b9\u4e8c\u4eba\u5b9a\u6709\u8bb8\u591a\u4f53\u5df1\u8bdd\u513f\u8981\u8bf4\uff0c\u6211\u8fd9\u5c31\u8d70\u7f62\u3002\u201d\u6b63\u60f3\u6084\u6084\u907f\u5f00\uff0c\u5374\u542c\u5f97\u6709\u4e24\u4eba\u5feb\u6b65\u8d70\u6765\uff0c\u4e00\u4eba\u9053\uff1a\u201c\u4f60\u4ece\u8fd9\u8fb9\u641c\u6765\uff0c\u6211\u4ece\u90a3\u8fb9\u641c\u53bb\uff0c\u515c\u4e2a\u5708\u5b50\uff0c\u518d\u5728\u8fd9\u91cc\u4f1a\u5408\u3002\u201d \u3000\u3000\u53e6\u4e00\u4eba\u9053\uff1a\u201c\u597d\uff01\u8fd9\u4e00\u5e26\u96ea\u5730\u91cc\u811a\u5370\u6742\u4e71\uff0c\u8bf4\u4e0d\u5b9a\u90a3\u5c0f\u6deb\u50e7\u4fbf\u8eb2\u5728\u5de6\u8fd1\u3002\u201d\u5148\u8bf4\u8bdd\u7684\u90a3\u4eba\u538b\u4f4e\u58f0\u97f3\uff0c\u7b11\u9053\uff1a\u201c\u5582\uff0c\u8001\u5b8b\uff0c\u8fd9\u6c34\u59d1\u5a18\u82b1\u6735\u4e00\u822c\u7684\u4eba\u513f\uff0c\u5c0f\u6deb\u50e7\u8fd9\u534a\u5e74\u4e2d\u8273\u798f\u53ef\u662f\u4e0d\u6d45\u3002\u201d\u53e6\u4e00\u4eba\u54c8\u54c8\u5927\u7b11\uff0c\u9053\uff1a\u201c\u662f\u554a\uff0c\u96be\u602a\u90a3\u59d3\u6c6a\u7684\u5fc3\u7518\u60c5\u613f\u6234\u8fd9\u9876\u7eff\u5934\u5dfe\u3002\u201d\u4e24\u4eba\u563b\u563b\u54c8\u54c8\u7684\u8bf4\u4e86\u51e0\u53e5\uff0c\u5206\u624b\u53bb\u5bfb\u72c4\u4e91\u3002 \u3000\u3000\u72c4\u4e91\u5728\u65c1\u542c\u7740\uff0c\u5f88\u4e3a\u6c6a\u6c34\u4e8c\u4eba\u96be\u8fc7\uff0c\u5fc3\u60f3\uff1a\u201c\u82b1\u94c1\u5e72\u8fd9\u4eba\u771f\u662f\u7f6a\u5927\u6076\u6781\uff0c\u634f\u9020\u8fd9\u4e9b\u65e0\u803b\u8c23\u8a00\uff0c\u6c61\u635f\u6c34\u59d1\u5a18\u7684\u58f0\u540d\uff0c\u4e8e\u4ed6\u53c8\u6709\u751a\u4e48\u597d\u5904\uff1f\u201d\u4ed6\u4e0d\u77e5\u82b1\u94c1\u5e72\u751f\u6015\u6c34\u7b19\u63ed\u9732\u81ea\u5df1\u79cd\u79cd\u5978\u6076\u884c\u5f84\uff0c\u52a1\u987b\u5148\u4e0b\u624b\u4e3a\u5f3a\uff0c\u8d25\u574f\u5979\u7684\u58f0\u540d\uff0c\u65c1\u4eba\u624d\u4e0d\u4f1a\u4fe1\u5979\u7684\u8bdd\u3002\u72c4\u4e91\u62ac\u5934\u5411\u6d1e\u4e2d\u671b\u53bb\uff0c\u53ea\u89c1\u6c34\u7b19\u9000\u5f00\u4e86\u4e24\u6b65\uff0c\u8138\u8272\u60e8\u767d\uff0c\u8eab\u5b50\u53d1\u98a4\uff0c\u8bf4\u9053\uff1a\u201c\u8868\u54e5\uff0c\u4f60\u83ab\u4fe1\u8fd9\u79cd\u80e1\u8bf4\u516b\u9053\u3002\u201d \u3000\u3000\u6c6a\u5578\u98ce\u4e0d\u7b54\uff0c\u8138\u4e0a\u808c\u8089\u62bd\u52a8\u3002\u663e\u7136\uff0c\u9002\u624d\u90a3\u4e24\u4e2a\u4eba\u7684\u8bf4\u8bdd\uff0c\u4fbf\u5982\u6bd2\u86c7\u822c\u5728\u54ac\u556e\u4ed6\u7684\u5fc3\u3002\u8fd9\u534a\u5e74\u4e2d\u4ed6\u5728\u96ea\u8c37\u4e4b\u5916\uff0c\u6bcf\u65e5\u6bcf\u591c\u603b\u662f\u60f3\u7740\uff1a\u201c\u8868\u59b9\u843d\u5165\u4e86\u8fd9\u4e24\u4e2a\u6deb\u50e7\u624b\u4e2d\uff0c\u54ea\u91cc\u8fd8\u80fd\u4fdd\u5f97\u6e05\u767d\uff1f\u4f46\u53ea\u8981\u5979\u6027\u547d\u65e0\u788d\uff0c\u4e5f\u5c31\u8c22\u5929\u8c22\u5730\u4e86\u3002\u201d\u53ef\u662f\u4eba\u5fc3\u82e6\u4e0d\u8db3\uff0c\u8fd9\u65f6\u5019\u89c1\u4e86\u6c34\u7b19\uff0c\u5374\u53c8\u76fc\u671b\u5979\u5b88\u8eab\u5982\u7389\uff0c\u542c\u5230\u90a3\u4e8c\u4eba\u7684\u8bdd\uff0c\u5fc3\u60f3\uff1a\u201c\u6c5f\u6e56\u4e0a\u4eba\u4eba\u5747\u77e5\u6b64\u4e8b\uff0c\u6c6a\u5578\u98ce\u5802\u5802\u4e08\u592b\uff0c\u5c82\u80fd\u60f9\u4eba\u803b\u7b11\uff1f\u201d\u4f46\u89c1\u5230\u5979\u8fd9\u822c\u695a\u695a\u53ef\u601c\u7684\u6a21\u6837\uff0c\u5fc3\u80a0\u5374\u53c8\u8f6f\u4e86\uff0c\u53f9\u4e86\u53e3\u6c14\uff0c\u6447\u4e86\u6447\u5934\uff0c\u9053\uff1a\u201c\u8868\u59b9\uff0c\u54b1\u4eec\u8d70\u7f62\u3002\u201d \u3000\u3000\u6c34\u7b19\u9053\uff1a\u201c\u4f60\u4fe1\u4e0d\u4fe1\u8fd9\u4e9b\u4eba\u7684\u8bdd\uff1f\u201d\u6c6a\u5578\u98ce\u9053\uff1a\u201c\u65c1\u4eba\u7684\u95f2\u8a00\u95f2\u8bed\uff0c\u7406\u4ed6\u4f5c\u751a\uff1f\u201d\u6c34\u7b19\u54ac\u7740\u5507\u76ae\uff0c\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u4f60\u662f\u76f8\u4fe1\u7684\u4e86\uff1f\u201d\u6c6a\u5578\u98ce\u4f4e\u5934\u9ed8\u7136\uff0c\u8fc7\u4e86\u597d\u4e00\u4f1a\uff0c\u624d\u9053\uff1a\u201c\u597d\u7f62\uff0c\u6211\u4e0d\u4fe1\u4fbf\u662f\u3002\u201d\u6c34\u7b19\u9053\uff1a\u201c\u4f60\u5fc3\u4e2d\u5374\u65e9\u4fe1\u4e86\u8fd9\u4e9b\u542b\u8840\u55b7\u4eba\u7684\u810f\u8bdd\u3002\u201d \u3000\u3000\u987f\u4e86\u4e00\u987f\uff0c\u53c8\u9053\uff1a\u201c\u4ee5\u540e\u4f60\u4e0d\u7528\u518d\u89c1\u6211\uff0c\u5c31\u5f53\u6211\u8fd9\u6b21\u5728\u96ea\u8c37\u4e2d\u6b7b\u4e86\u5c31\u662f\u5566\u3002\u201d\u6c6a\u5578\u98ce\u9053\uff1a\u2018\u90a3\u4e5f\u4e0d\u5fc5\u5982\u6b64\u3002\u201d \u3000\u3000\u6c34\u7b19\u5fc3\u4e2d\u60b2\u82e6\uff0c\u6cea\u6c34\u6025\u6d8c\uff0c\u5fc3\u60f3\u65c1\u4eba\u51a4\u6789\u6211\u3001\u8bec\u8511\u6211\uff0c\u5168\u53ef\u7f6e\u4e4b\u4e0d\u7406\uff0c\u53ef\u662f\u7adf\u8fde\u8868\u54e5\u4e5f\u77a7\u5f97\u6211\u5982\u6b64\u4e0b\u8d31\u3002\u5979\u53ea\u60f3\u53ca\u65e9\u79bb\u5f00\u96ea\u8c37\uff0c\u79bb\u5f00\u8fd9\u8bb8\u8bb8\u591a\u591a\u4eba\uff0c\u9003\u5230\u4e00\u4e2a\u8c01\u4e5f\u4e0d\u8ba4\u8bc6\u5979\u7684\u5730\u65b9\u53bb\uff0c\u6c38\u8fdc\u4e0d\u518d\u548c\u8fd9\u4e9b\u4eba\u76f8\u89c1\u3002 \u3000\u3000\u5979\u62d4\u8db3\u5411\u5916\u4fbf\u5954\uff0c\u5c06\u5230\u6d1e\u53e3\u65f6\uff0c\u5fcd\u4e0d\u4f4f\u56de\u5934\u5411\u5c71\u6d1e\u89d2\u843d\u671b\u4e86\u4e00\u773c\u3002\u8fd9\u534a\u5e74\u4e4b\u4e2d\uff0c\u5979\u65e5\u591c\u90fd\u5728\u8fd9\u89d2\u843d\u4e2d\u5b89\u8eab\u3002\u5979\u6027\u597d\u6574\u6d01\uff0c\u5341\u5206\u7075\u5de7\uff0c\u7528\u6811\u76ae\u9e1f\u7fbd\u7b49\u7269\u7f16\u7ec7\u4e86\u4e0d\u5c11\u8925\u5b50\u3001\u5750\u57ab\u4e4b\u7c7b\uff0c\u8fd9\u65f6\u4e34\u522b\uff0c\u5bf9\u8fd9\u4e9b\u966a\u4f34\u4e86\u5979\u534a\u5e74\u7684\u7269\u4e8b\u5fc3\u4e2d\u4e0d\u7981\u4f9d\u4f9d\u3002\u4e00\u77a5\u4e4b\u95f4\uff0c\u89c1\u5230\u81ea\u5df1\u7ec7\u7ed9\u72c4\u4e91\u7684\u90a3\u4ef6\u9e1f\u7fbd\u8863\u670d\uff0c\u90a3\u65e5\u72c4\u4e91\u751f\u6c14\u4e0d\u8981\uff0c\u8e22\u8fd8\u7ed9\u5979\uff0c\u6b64\u540e\u665a\u4e0a\u5979\u4fbf\u4f5c\u4e3a\u88ab\u76d6\uff0c\u4ee5\u5fa1\u5bd2\u51b7\uff0c\u8fd9\u65f6\u5fc3\u4e2d\u4e00\u52a8\uff1a\u201c\u8fd9\u4e9b\u4eba\u53e3\u53e3\u58f0\u58f0\u8bf4\u4ed6\u662f\u6deb\u50e7\uff0c\u8981\u8ddf\u4ed6\u4e3a\u96be\uff0c\u82e5\u662f\u627e\u5230\u4e86\u4ed6\uff0c\u4ed6\u5be1\u4e0d\u654c\u4f17\uff0c\u90a3\u4fbf\u5982\u4f55\u662f\u597d\uff1f\u201d\u5f53\u4e0b\u505c\u4f4f\u811a\u6b65\uff0c\u51dd\u671b\u7740\u90a3\u4ef6\u7fbd\u8863\uff0c\u4e00\u65f6\u5f77\u5fa8\u65e0\u4e3b\u3002 \u3000\u3000\u6c6a\u5578\u98ce\u89c1\u90a3\u4ef6\u7fbd\u8863\u653e\u5728\u5979\u5367\u8925\u4e4b\u4e0a\uff0c\u8863\u670d\u957f\u5927\u5bbd\u655e\uff0c\u5f0f\u6837\u663e\u662f\u7537\u5b50\u8863\u886b\uff0c\u5fc3\u5934\u5927\u7591\uff0c\u95ee\u9053\uff1a\u201c\u8fd9\u2026\u2026\u8fd9\u662f\u751a\u4e48\uff1f\u201d\u6c34\u7b19\u9053\uff1a\u201c\u662f\u6211\u505a\u7684\u3002\u201d\u6c6a\u5578\u98ce\u6da9\u7136\u9053\uff1a\u201c\u662f\u4f60\u7684\u4e48\uff1f\u201d\u6c34\u7b19\u51b2\u53e3\u4fbf\u60f3\u7b54\u9053\uff1a\u201c\u4e0d\u662f\u6211\u7684\u3002\u201d\u4f46\u968f\u5373\u89c9\u5f97\u4e0d\u59a5\uff0c\u8e0c\u8e87\u4e0d\u7b54\u3002\u6c6a\u5578\u98ce\u9053\uff1a\u201c\u662f\u4ef6\u7537\u5b50\u8863\u886b\uff1f\u201d\u58f0\u97f3\u66f4\u52a0\u5e72\u6da9\u4e86\u3002\u6c34\u7b19\u70b9\u4e86\u70b9\u5934\u3002\u6c6a\u5578\u98ce\u53c8\u9053\uff1a\u201c\u662f\u4f60\u7ec7\u7ed9\u4ed6\u7684\uff1f\u201d\u6c34\u7b19\u53c8\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u6c6a\u5578\u98ce\u63d0\u8d77\u7fbd\u8863\uff0c\u4ed4\u7ec6\u770b\u4e86\u4e00\u4f1a\uff0c\u51b7\u51b7\u7684\u9053\uff1a\u201c\u7ec7\u5f97\u5f88\u597d\u3002\u201d \u3000\u3000\u6c34\u7b19\u9053\uff1a\u201c\u8868\u54e5\uff0c\u4f60\u522b\u80e1\u731c\uff0c\u4ed6\u548c\u6211\u2026\u2026\u201d\u4f46\u89c1\u4ed6\u773c\u795e\u4e2d\u5145\u6ee1\u4e86\u6124\u6012\u548c\u618e\u6068\uff0c\u4fbf\u4e0d\u518d\u8bf4\u4e0b\u53bb\u4e86\u3002\u6c6a\u5578\u98ce\u5c06\u7fbd\u8863\u5f80\u5367\u8925\u4e00\u4e22\uff0c\u8bf4\u9053\uff1a\u201c\u4ed6\u7684\u8863\u670d\uff0c\u5374\u653e\u5728\u4f60\u7684\u5e8a\u4e0a\u2026\u2026\u201d \u3000\u3000\u6c34\u7b19\u5fc3\u4e2d\u4e00\u7247\u51b0\u51c9\uff0c\u53ea\u89c9\u8fd9\u4e2a\u5411\u6765\u4f53\u8c05\u6e29\u67d4\u7684\u8868\u54e5\uff0c\u7a81\u7136\u95f4\u53d8\u6210\u4e86\u65e0\u6bd4\u7684\u7c97\u4fd7\u53ef\u538c\u3002\u5979\u4e0d\u60f3\u518d\u591a\u4f5c\u89e3\u91ca\uff0c\u53ea\u60f3\uff1a\u201c\u65e2\u7136\u4f60\u7591\u5fc3\u6211\uff0c\u51a4\u6789\u6211\uff0c\u90a3\u5c31\u51a4\u6789\u5230\u5e95\u597d\u4e86\u3002\u201d \u3000\u3000\u72c4\u4e91\u5728\u6d1e\u5916\u8349\u4e1b\u4e4b\u4e2d\uff0c\u89c1\u5230\u5979\u53d7\u82e6\u51a4\u5c48\uff0c\u8138\u4e0a\u795e\u60c5\u6781\u662f\u51c4\u51c9\uff0c\u5fc3\u4e2d\u96be\u53d7\u4e4b\u6781\uff1a\u201c\u6211\u662f\u4e2a\u4f4e\u8d31\u4e4b\u4eba\uff0c\u53d7\u60ef\u4e86\u51a4\u5c48\uff0c\u90a3\u4e0d\u7b97\u5f97\u751a\u4e48\u3002\u5979\u5374\u662f\u4e2a\u5c0a\u8d35\u7684\u59d1\u5a18\uff0c\u5982\u4f55\u80fd\u53d7\u8fd9\u4e0d\u767d\u4e4b\u51a4\uff1f\u201d\u60f3\u5230\u8fd9\u91cc\uff0c\u4e49\u6124\u4e4b\u5fc3\u987f\u8d77\uff0c\u867d\u77e5\u5c71\u6d1e\u5916\u6b63\u6709\u6570\u5341\u4e2a\u597d\u624b\u5728\u5230\u5904\u641c\u5bfb\uff0c\u4eba\u4eba\u8981\u6740\u4ed6\u800c\u7518\u5fc3\uff0c\u5374\u4e5f\u987e\u4e0d\u5f97\u4e86\uff0c\u5f53\u5373\u6d8c\u8eab\u8dc3\u8fdb\u5c71\u6d1e\uff0c\u8bf4\u9053\uff1a\u201c\u6c6a\u5c11\u4fa0\uff0c\u4f60\u5168\u8f6c\u9519\u4e86\u5ff5\u5934\u3002\u201d \u3000\u3000\u6c6a\u5578\u98ce\u548c\u6c34\u7b19\u89c1\u4ed6\u7a81\u7136\u8df3\u8fdb\u5c71\u6d1e\u6765\uff0c\u90fd\u662f\u5403\u4e86\u4e00\u60ca\u3002\u72c4\u4e91\u8fd9\u65f6\u5934\u53d1\u5df2\u957f\uff0c\u5df2\u4e0d\u662f\u4ece\u524d\u62d4\u5149\u5934\u53d1\u7684\u5c0f\u548c\u5c1a\u6a21\u6837\u3002\u6c6a\u5578\u98ce\u5b9a\u4e86\u5b9a\u795e\uff0c\u624d\u8ba4\u4e86\u51fa\u6765\uff0c\u5f53\u5373\u62d4\u5251\u51fa\u9798\uff0c\u5de6\u624b\u5c06\u6c34\u7b19\u63a8\u5f00\uff0c\u6a2a\u5251\u5f53\u80f8\uff0c\u773c\u4e2d\u5982\u8981\u5192\u51fa\u706b\u6765\uff0c\u957f\u5251\u4e0d\u4f4f\u98a4\u52a8\uff0c\u6068\u4e0d\u5f97\u6251\u4e0a\u53bb\u5c06\u8fd9\u4eba\u7acb\u65f6\u65a9\u6210\u8089\u9171\u3002 \u3000\u3000\u72c4\u4e91\u9053\uff1a\u201c\u6211\u4e0d\u8ddf\u4f60\u52a8\u624b\u3002\u6211\u662f\u6765\u8ddf\u4f60\u8bf4\uff0c\u6c34\u59d1\u5a18\u51b0\u6e05\u7389\u6d01\uff0c\u4f60\u5a36\u5979\u4e3a\u59bb\uff0c\u771f\u662f\u5929\u5927\u7684\u798f\u6c14\uff0c\u4e0d\u5fc5\u80e1\u601d\u4e71\u60f3\uff0c\u4fe1\u4e86\u574f\u4eba\u7684\u9020\u8c23\u3002\u201d \u3000\u3000\u6c34\u7b19\u4e07\u6599\u4e0d\u5230\u4ed6\u7adf\u4f1a\u5728\u8fd9\u65f6\u633a\u8eab\u800c\u51fa\uff0c\u800c\u4e0d\u907f\u51f6\u9669\u7684\u51fa\u5934\uff0c\u53ea\u662f\u4e3a\u4e86\u8981\u8bc1\u660e\u81ea\u5df1\u7684\u6e05\u767d\uff0c\u53c8\u662f\u611f\u6fc0\uff0c\u53c8\u662f\u62c5\u5fc3\uff0c\u5fd9\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u5feb\u8d70\uff0c\u8bb8\u591a\u4eba\u8981\u6740\u4f60\uff0c\u8fd9\u91cc\u592a\u4e5f\u5371\u9669\u3002\u201d \u3000\u3000\u72c4\u4e91\u9053\uff1a\u201c\u6211\u77e5\u9053\uff0c\u4e0d\u8fc7\u6211\u975e\u5f97\u5bf9\u6c6a\u5c11\u4fa0\u8bf4\u660e\u767d\u8fd9\u4e8b\u4e0d\u53ef\uff0c\u514d\u5f97\u4f60\u53d7\u4e86\u51a4\u6789\u3002\u6c6a\u5c11\u4fa0\uff0c\u6c34\u59d1\u5a18\u662f\u4f4d\u597d\u59d1\u5a18\uff0c\u4f60\u2026\u2026\u4f60\u5343\u4e07\u4e0d\u53ef\u51a4\u6789\u4e86\u5979\u3002\u201d \u3000\u3000\u72c4\u4e91\u62d9\u4e8e\u8a00\u8f9e\uff0c\u5e73\u5e73\u5e38\u5e38\u4e00\u4ef6\u4e8b\u4e5f\u4e0d\u6613\u8bf4\u5f97\u6e05\u695a\uff0c\u4f55\u51b5\u8fd9\u822c\u5fae\u5999\u7684\u4e8b\u7aef\uff0c\u63a5\u8fde\u7ed3\u7ed3\u5df4\u5df4\u7684\u8bf4\u4e86\u4e03\u516b\u53e5\u8bdd\uff0c\u53ea\u6709\u4f7f\u6c6a\u5578\u98ce\u66f4\u589e\u7591\u5fc3\u3002 \u3000\u3000\u6c34\u7b19\u6025\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u5feb\u8d70\uff01\u591a\u8c22\u4f60\u7684\u597d\u610f\uff0c\u6211\u53ea\u6709\u6765\u751f\u56fe\u62a5\u4e86\uff0c\u4f60\u5feb\u8d70\uff01\u4ed6\u4eec\u4eba\u591a\uff0c\u5927\u5bb6\u8981\u6740\u4f60\u2026\u2026\u201d \u3000\u3000\u6c6a\u5578\u98ce\u542c\u5230\u6c34\u7b19\u8a00\u8bed\u548c\u795e\u8272\u95f4\u5bf9\u4ed6\u5982\u6b64\u5173\u6000\uff0c\u5992\u5ff5\u5927\u8d77\uff0c\u559d\u9053\uff1a\u201c\u6211\u8ddf\u4f60\u62da\u4e86\uff01\u201d\u55e4\u7684\u4e00\u5251\uff0c\u5411\u72c4\u4e91\u5f53\u80f8\u75be\u523a\u8fc7\u53bb\u3002 \u3000\u3000\u8fd9\u4e00\u5251\u867d\u7136\u52bf\u9053\u51cc\u5389\uff0c\u4f46\u72c4\u4e91\u8fd9\u65f6\u662f\u4f55\u7b49\u8eab\u624b\uff0c\u4e00\u8eab\u800c\u517c\u201c\u795e\u7167\u529f\u201d\u3001\u201c\u8840\u5200\u201d\u6b63\u90aa\u4e24\u6d3e\u7edd\u9876\u6b66\u5b66\u4e4b\u6240\u957f\uff0c\u773c\u89c1\u6c6a\u5578\u98ce\u5251\u5230\uff0c\u8eab\u5b50\u5fae\u4fa7\uff0c\u4fbf\u5df2\u907f\u5f00\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u8ddf\u4f60\u52a8\u624b\u3002\u6211\u53eb\u4f60\u597d\u597d\u7684\u5a36\u4e86\u6c34\u59d1\u5a18\uff0c\u522b\u5bf9\u5979\u6709\u4e1d\u6beb\u7591\u5fc3\u3002\u5979\u2026\u2026\u5979\u662f\u4e2a\u597d\u59d1\u5a18\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u8bdd\u4e4b\u9645\uff0c\u6c6a\u5578\u98ce\u5de6\u4e8c\u5251\uff0c\u53f3\u4e09\u5251\uff0c\u63a5\u8fde\u5411\u4ed6\u75be\u523a\u4e94\u5251\u3002\u72c4\u4e91\u82e5\u65e0\u5176\u4e8b\u7684\u659c\u8eab\u95ea\u5f00\uff0c\u5fc3\u4e2d\u5947\u602a\uff1a\u201c\u8fd9\u4eba\u4ece\u524d\u6b66\u529f\u5f88\u597d\uff0c\u600e\u4e48\u534a\u5e74\u4e0d\u89c1\uff0c\u5251\u6cd5\u53d8\u5f97\u8fd9\u4e48\u7b28\u4e86\uff1f\u201d \u3000\u3000\u6c6a\u5578\u98ce\u731b\u523a\u6025\u65ab\uff0c\u6bcf\u4e00\u5251\u90fd\u88ab\u4ed6\u884c\u82e5\u65e0\u4e8b\u7684\u95ea\u5f00\uff0c\u8d8a\u52a0\u6012\u53d1\u5982\u72c2\uff0c\u5251\u62db\u66f4\u51fa\u5f97\u5feb\u4e86\u3002 \u3000\u3000\u72c4\u4e91\u9053\uff1a\u201c\u6c6a\u5c11\u4fa0\uff0c\u4f60\u7b54\u5141\u4e0d\u7591\u5fc3\u6c34\u59d1\u5a18\u7684\u6e05\u767d\uff0c\u6211\u5c31\u53bb\u4e86\u3002\u4f60\u7684\u670b\u53cb\u4eec\u90fd\u8981\u6740\u6211\uff0c\u6211\u53ef\u4e0d\u80fd\u518d\u591a\u803d\u6401\u4e86\u3002\u201d\u6c6a\u5578\u98ce\u51fa\u5251\u8d8a\u6765\u8d8a\u5feb\uff0c\u72c4\u4e91\u5355\u662f\u5185\u529b\u6df1\u6e5b\uff0c\u8f7b\u529f\u5374\u662f\u5e73\u5e73\uff0c\u867d\u7136\u5185\u529f\u662f\u672c\uff0c\u8f7b\u529f\u662f\u672b\uff0c\u4f46\u6b64\u9053\u672a\u5f97\u4eba\u6307\u70b9\uff0c\u4e8e\u5bf9\u65b9\u7684\u5feb\u5251\u6e10\u611f\u96be\u4ee5\u5e94\u4ed8\uff0c\u5f53\u4e0b\u4f38\u6307\u4e00\u5f39\uff0c\u94ee\u7684\u4e00\u58f0\u8f7b\u54cd\uff0c\u4e2d\u6307\u5f39\u5728\u5251\u5203\u4e4b\u4e0a\u3002 \u3000\u3000\u6c6a\u5578\u98ce\u53ea\u89c9\u864e\u53e3\u5267\u75db\uff0c\u957f\u5251\u8131\u624b\u843d\u5730\uff0c\u5fd9\u4fef\u8eab\u53bb\u62fe\u3002\u72c4\u4e91\u4f38\u638c\u5728\u4ed6\u80a9\u5934\u4e00\u63a8\uff0c\u8fd9\u4e00\u638c\u5e76\u6ca1\u4f7f\u591a\u5927\u529b\u6c14\uff0c\u4e0d\u6599\u6c6a\u5578\u98ce\u7adf\u7136\u62b5\u53d7\u4e0d\u4f4f\uff0c\u7ed9\u4ed6\u4e00\u63a8\u4e4b\u4e0b\uff0c\u767b\u65f6\u51e0\u4e2a\u7b4b\u6597\u5411\u540e\u7ffb\u8dcc\u4e86\u51fa\u53bb\uff0c\u7830\u7684\u4e00\u58f0\uff0c\u91cd\u91cd\u649e\u4e0a\u5c71\u6d1e\u7684\u77f3\u58c1\u3002 \u3000\u3000\u6c34\u7b19\u89c1\u4ed6\u8dcc\u5f97\u5341\u5206\u72fc\u72c8\uff0c\u5fd9\u5954\u8fc7\u53bb\u76f8\u6276\u3002 \u3000\u3000\u72c4\u4e91\u6115\u7136\uff0c\u4ed6\u7edd\u4e0d\u60f3\u5c06\u6c6a\u5578\u98ce\u63a8\u5012\uff0c\u53ea\u662f\u8981\u963b\u6b62\u4ed6\u62fe\u5251\u518d\u6253\uff0c\u54ea\u60f3\u5230\u7adf\u4f1a\u6454\u5f97\u8fd9\u4e48\u5389\u5bb3\uff0c\u5b9e\u662f\u5927\u51fa\u610f\u6599\u4e4b\u5916\u3002\u4ed6\u8de8\u4e0a\u4e24\u6b65\uff0c\u4e5f\u60f3\u53bb\u6276\uff0c\u8bf4\u9053\uff1a\u201c\u5bf9\u4e0d\u4f4f\uff0c\u6211\u5f53\u771f\u2026\u2026\u6211\u4e0d\u662f\u6545\u610f\u7684\u3002\u201d \u3000\u3000\u6c34\u7b19\u62c9\u7740\u6c6a\u5578\u98ce\u7684\u53f3\u81c2\uff0c\u9053\uff1a\u201c\u8868\u54e5\uff0c\u6ca1\u4e8b\u7f62\uff1f\u201d\u6c6a\u5578\u98ce\u5fc3\u4e2d\u5992\u6124\u4ea4\u653b\uff0c\u4e0d\u53ef\u6291\u6b62\uff0c\u8ba4\u5b9a\u6c34\u7b19\u504f\u5411\u72c4\u4e91\uff0c\u4e24\u4eba\u8054\u624b\u6253\u4e86\u81ea\u5df1\u4e4b\u540e\uff0c\u53cd\u6765\u8ba5\u8bbd\uff0c\u5de6\u638c\u6a2a\u6325\u8fc7\u6765\uff0c\u62cd\u7684\u4e00\u58f0\uff0c\u91cd\u91cd\u6253\u4e86\u5979\u4e00\u4e2a\u8033\u5149\uff0c\u559d\u9053\uff1a\u201c\u6eda\u5f00\uff01\u201d\u6c34\u7b19\u5403\u4e86\u4e00\u60ca\uff0c\u8868\u54e5\u7adf\u4f1a\u51fa\u624b\u6bb4\u6253\u81ea\u5df1\uff0c\u90a3\u662f\u4ece\u672a\u60f3\u8fc7\u7684\u4e8b\u60c5\uff0c\u4f38\u624b\u629a\u7740\u8138\u988a\uff0c\u7adf\u662f\u5446\u4e86\u3002\u6c6a\u5578\u98ce\u8ddf\u7740\u53c8\u662f\u4e00\u638c\uff0c\u51fb\u4e2d\u5979\u7684\u5de6\u988a\u3002\u6c34\u7b19\u60ca\u60e7\u4e4b\u4e0b\uff0c\u6251\u5728\u72c4\u4e91\u7684\u80a9\u5934\uff0c\u53ea\u89c9\u8fd9\u65f6\u5019\u53ea\u6709\u4ed6\u65b9\u80fd\u4fdd\u62a4\u81ea\u5df1\u3002 \u3000\u3000\u72c4\u4e91\u4fa7\u8eab\u6321\u5728\u6c6a\u5578\u98ce\u4e4b\u524d\uff0c\u6012\u9053\uff1a\u201c\u597d\u7aef\u7aef\u7684\uff0c\u4f60\u2026\u2026\u4f60\u5e72\u4e48\u6253\u4eba\uff1f\u201d\u53ea\u542c\u5f97\u5c71\u6d1e\u5916\u811a\u6b65\u58f0\u54cd\uff0c\u6709\u51e0\u4e2a\u4eba\u53eb\u9053\uff1a\u201c\u5c71\u6d1e\u91cc\u6709\u4eba\u4e89\u5435\uff0c\u5feb\u53bb\u77a7\u77a7\uff0c\u83ab\u975e\u90a3\u5c0f\u6deb\u50e7\u85cf\u5728\u91cc\u9762\uff1f\u201d \u3000\u3000\u6c34\u7b19\u9000\u540e\u4e24\u6b65\uff0c\u5bf9\u72c4\u4e91\u9053\uff1a\u201c\u4f60\u5feb\u8d70\u7f62\u2026\u2026\u6211\u2026\u2026\u6211\u591a\u8c22\u4f60\u7684\u597d\u610f\u3002\u201d \u3000\u3000\u72c4\u4e91\u77a7\u77a7\u6c6a\u5578\u98ce\uff0c\u53c8\u77a7\u77a7\u6c34\u7b19\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u53bb\u4e86\uff01\u201d\u8f6c\u8eab\u8d70\u5411\u6d1e\u53e3\u3002 \u3000\u3000\u6c6a\u5578\u98ce\u5927\u53eb\uff1a\u201c\u5c0f\u6deb\u50e7\u5728\u8fd9\u91cc\uff0c\u5c0f\u6deb\u50e7\u5728\u8fd9\u91cc\uff0c\u5feb\u5835\u4f4f\u6d1e\u53e3\uff0c\u522b\u8ba9\u4ed6\u9003\u8d70\u4e86\uff01\u201d\u6c34\u7b19\u6025\u9053\uff1a\u201c\u8868\u54e5\uff0c\u4f60\u8fd9\u4e0d\u662f\u5bb3\u4eba\u4e48\uff1f\u201d \u3000\u3000\u6c6a\u5578\u98ce\u4ecd\u662f\u5927\u53eb\uff1a\u201c\u5feb\u5835\u4f4f\u6d1e\u53e3\uff0c\u5feb\u5835\u4f4f\u6d1e\u53e3\uff01\u201d \u3000\u3000\u6d1e\u5916\u4e03\u516b\u540d\u6c49\u5b50\u542c\u5f97\u6c6a\u5578\u98ce\u7684\u53eb\u56b7\uff0c\u5f53\u5373\u62e6\u5728\u6d1e\u53e3\u3002\u72c4\u4e91\u5feb\u6b65\u800c\u51fa\uff0c\u4e00\u4eba\u559d\u9053\uff1a\u201c\u5f80\u54ea\u91cc\u9003\uff1f\u201d\u6325\u5200\u5411\u4ed6\u5934\u9876\u780d\u843d\u3002\u72c4\u4e91\u4f38\u624b\u5728\u4ed6\u80f8\u53e3\u4e00\u63a8\uff0c\u90a3\u4eba\u76f4\u6454\u4e86\u51fa\u53bb\uff0c\u649e\u5411\u8eab\u65c1\u7684\u4e09\u4eba\uff0c\u56db\u4e2a\u4eba\u7eb7\u7eb7\u8dcc\u5012\u3002\u4f17\u4eba\u53eb\u9a82\u547c\u559d\u58f0\u4e2d\uff0c\u72c4\u4e91\u5feb\u6b65\u9003\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u7fa4\u8c6a\u542c\u5f97\u58f0\u97f3\uff0c\u4ece\u56db\u9762\u516b\u65b9\u8d76\u4e86\u8fc7\u6765\uff0c\u72c4\u4e91\u65e9\u5df2\u53bb\u5f97\u8fdc\u4e86\u3002\u6709\u5341\u4f59\u4eba\u53d1\u8db3\u75be\u8ffd\uff0c\u72c4\u4e91\u5fc3\u4e2d\u5bb3\u6015\uff0c\u8eb2\u5728\u957f\u8349\u4e1b\u4e2d\uff0c\u9ed1\u591c\u4e4b\u4e2d\uff0c\u8c01\u4e5f\u5bfb\u4ed6\u4e0d\u770b\u3002\u7fa4\u8c6a\u53ea\u9053\u4ed6\u5df2\u5954\u9003\u51fa\u8c37\uff0c\u547c\u5578\u53eb\u56b7\uff0c\u8ffd\u9010\u800c\u51fa\u3002 \u3000\u3000\u8fc7\u4e86\u597d\u4e00\u4f1a\u513f\uff0c\u72c4\u4e91\u89c1\u5230\u6c6a\u5578\u98ce\u548c\u6c34\u7b19\u4e5f\u8d70\u4e86\u3002\u6c6a\u5578\u98ce\u5728\u524d\uff0c\u6c34\u7b19\u8ddf\u5728\u540e\u9762\uff0c\u4e24\u4eba\u9694\u7740\u4e00\u4e08\u591a\u8def\uff0c\u8d8a\u53bb\u8d8a\u8fdc\uff0c\u7ec8\u4e8e\u80cc\u5f71\u88ab\u5c71\u5761\u906e\u53bb\u3002 \u3000\u3000\u7247\u523b\u4e4b\u524d\u8fd8\u662f\u4e00\u7247\u6270\u6518\u7684\u96ea\u8c37\uff0c\u7ec8\u4e8e\u5bc2\u9759\u65e0\u58f0\u3002 \u3000\u3000\u4e2d\u539f\u7fa4\u8c6a\u8d70\u4e86\u3002\u82b1\u94c1\u5e72\u8d70\u4e86\uff0c\u6c34\u7b19\u8d70\u4e86\u3002\u53ea\u5269\u4e0b\u72c4\u4e91\u4e00\u4eba\u3002\u4ed6\u62ac\u8d77\u5934\u6765\uff0c\u8fde\u5f80\u65e5\u5e38\u5728\u5929\u7a7a\u76d8\u65cb\u7684\u5140\u9e70\u4e5f\u6ca1\u770b\u89c1\u3002 \u3000\u3000\u771f\u662f\u5bc2\u5bde\uff0c\u5b64\u96f6\u96f6\u5730\u3002\u53ea\u6709\u6d88\u878d\u4e86\u7684\u96ea\u6c34\u5728\u8f7b\u8f7b\u7684\u6d41\u51fa\u8c37\u53bb\u3002 \u3000"}),showOnHomepage:!0},{name:"\u4e5d\u3000\u201c\u6881\u5c71\u4f2f\xb7\u795d\u82f1\u53f0\u201d",content:(0,a.jsx)(a.Fragment,{children:"\u72c4\u4e91\u5728\u96ea\u8c37\u4e2d\u53c8\u803d\u4e86\u534a\u4e2a\u6708\uff0c\u5c06\u300a\u8840\u5200\u7ecf\u300b\u4e0a\u7684\u5200\u6cd5\u548c\u5185\u529f\u7ec3\u5f97\u7eaf\u719f\u65e0\u6bd4\uff0c\u518d\u4e5f\u4e0d\u4f1a\u5fd8\u5374\uff0c\u4e8e\u662f\u5c06\u300a\u8840\u5200\u7ecf\u300b\u70e7\u6210\u4e86\u7070\uff0c\u6492\u5728\u8840\u5200\u8001\u7956\u7684\u575f\u5893\u4e0a\u3002 \u3000\u3000\u8fd9\u534a\u4e2a\u6708\u4e2d\uff0c\u4ed6\u4ecd\u662f\u7761\u5728\u5c71\u6d1e\u5916\u7684\u5927\u77f3\u4e0a\uff0c\u6c34\u7b19\u867d\u7136\u8d70\u4e86\uff0c\u4ed6\u8fd8\u662f\u4e0d\u6562\u5230\u5c71\u6d1e\u91cc\u53bb\u7761\uff0c\u81ea\u7136\u66f4\u4e0d\u6562\u53bb\u7528\u5979\u7684\u8925\u5b50\u3001\u57ab\u5b50\u3002 \u3000\u3000\u4ed6\u60f3\uff1a\u201c\u6211\u8be5\u8d70\u4e86\uff01\u8fd9\u4ef6\u9e1f\u7fbd\u8863\u670d\u4e0d\u5fc5\u5e26\u53bb\uff0c\u5f85\u8be5\u529e\u7684\u4e8b\u60c5\u529e\u4e86\uff0c\u5c31\u56de\u8fd9\u96ea\u8c37\u6765\u4f4f\u3002\u5916\u9762\u7684\u4eba\u806a\u660e\u5f97\u5f88\uff0c\u6211\u4e0d\u660e\u767d\u4ed6\u4eec\u5fc3\u91cc\u60f3\u4e9b\u751a\u4e48\u3002\u8fd9\u91cc\u8c01\u4e5f\u4e0d\u4f1a\u6765\uff0c\u8fd8\u662f\u4f4f\u5728\u8fd9\u91cc\u7684\u597d\u3002\u201d \u3000\u3000\u4e8e\u662f\u4ed6\u51fa\u4e86\u96ea\u8c37\uff0c\u5411\u4e1c\u884c\u53bb\u3002\u7b2c\u4e00\u4ef6\u4e8b\u8981\u56de\u8001\u5bb6\u6e58\u897f\u9ebb\u6eaa\u94fa\u53bb\uff0c\u77a7\u77a7\u5e08\u7236\u600e\u6837\u4e86\u3002\u81ea\u5df1\u4ece\u5c0f\u51fa\u5e08\u7236\u629a\u517b\u957f\u5927\uff0c\u4ed6\u662f\u4e16\u4e0a\u552f\u4e00\u7684\u4eb2\u4eba\u3002 \u3000\u3000\u4ece\u85cf\u8fb9\u5230\u6e58\u897f\uff0c\u987b\u5f97\u6a2a\u8d8a\u56db\u5ddd\u3002\u72c4\u4e91\u5fc3\u60f3\u82e5\u662f\u9047\u4e0a\u4e86\u4e2d\u539f\u7fa4\u8c6a\uff0c\u514d\u4e0d\u4e86\u4e00\u573a\u4e89\u6597\uff0c\u81ea\u5df1\u548c\u4ed6\u4eec\u65e0\u6028\u65e0\u4ec7\uff0c\u8bf8\u822c\u4e8b\u7aef\u5168\u56e0\u81ea\u5df1\u62d4\u5149\u5934\u53d1\u3001\u7a7f\u4e86\u5b9d\u8c61\u7684\u50e7\u8863\u800c\u8d77\u3002\u8fd9\u65f6\u4ed6\u6b66\u529f\u867d\u7136\u5df2\u7136\u6781\u9ad8\uff0c\u53ef\u662f\u5168\u65e0\u81ea\u4fe1\uff0c\u6599\u60f3\u53ea\u6d88\u9047\u4e0a\u4e86\u4e00\u4e24\u4f4d\u4e2d\u539f\u7684\u9ad8\u624b\uff0c\u975e\u7ed9\u4ed6\u4eec\u6740\u4e86\u4e0d\u53ef\u3002\u4e8e\u662f\u4e70\u4e86\u5957\u4e61\u6c11\u7684\u9752\u5e03\u8863\u88e4\u6362\u4e0a\u4e86\uff0c\u70e7\u53bb\u4e86\u5b9d\u8c61\u7684\u50e7\u8863\uff0c\u518d\u4ee5\u9505\u5e95\u7164\u7126\u62b9\u9ed1\u4e86\u8138\u3002\u56db\u5ddd\u6e58\u897f\u4e00\u5e26\u519c\u6c11\u559c\u4ee5\u767d\u5e03\u7f20\u5934\uff0c\u636e\u8bf4\u662f\u4e3a\u8bf8\u845b\u4eae\u670d\u4e27\u7684\u9057\u98ce\u3002\u72c4\u4e91\u4e5f\u627e\u4e86\u4e00\u5757\u6c61\u79fd\u7684\u767d\u5e03\u7f20\u5728\u5934\u4e0a\u3002\u4e00\u8def\u4e1c\u884c\uff0c\u5076\u5c14\u548c\u6c5f\u6e56\u4eba\u7269\u72ed\u8def\u76f8\u9022\uff0c\u5374\u662f\u8c01\u4e5f\u8ba4\u4ed6\u4e0d\u51fa\u4e86\u3002 \u3000\u3000\u4ed6\u6700\u6015\u7684\u662f\u9047\u4e0a\u4e86\u6c34\u7b19\u548c\u6c6a\u5578\u98ce\uff0c\u8fd8\u6709\u82b1\u94c1\u5e72\uff0c\u5e78\u597d\uff0c\u59cb\u7ec8\u6ca1\u89c1\u5230\u3002 \u3000\u3000\u76f4\u8d70\u4e86\u4e09\u5341\u591a\u5929\uff0c\u624d\u5230\u9ebb\u6eaa\u94fa\u8001\u5bb6\uff0c\u5176\u65f6\u5929\u6c14\u5df2\u6696\uff0c\u7530\u91cc\u79be\u79e7\u5df2\u957f\u5f97\u56db\u5bf8\u6765\u9ad8\u4e86\u3002\u8d8a\u8fd1\u6545\u5c45\uff0c\u611f\u6168\u8d8a\u591a\uff0c\u6e10\u6e10\u7684\u8138\u4e0a\u7099\u70ed\uff0c\u5fc3\u8df3\u4e5f\u5feb\u4e86\u8d77\u6765\u3002 \u3000\u3000\u4ed6\u6cbf\u7740\u5c11\u5e74\u65f6\u8d70\u60ef\u4e86\u7684\u5c71\u8def\uff0c\u6765\u5230\u6545\u5c45\u95e8\u5916\uff0c\u4e0d\u7531\u5f97\u5927\u5403\u4e00\u60ca\uff0c\u51e0\u4e4e\u4e0d\u76f8\u4fe1\u81ea\u5df1\u7684\u773c\u775b\u3002\u539f\u6765\u5c0f\u6eaa\u65c1\u3001\u67f3\u6811\u8fb9\u7684\u4e09\u95f4\u5c0f\u5c4b\uff0c\u7adf\u5df2\u53d8\u6210\u4e86\u4e00\u5ea7\u767d\u5899\u9ed1\u74e6\u7684\u5927\u623f\u5b50\u3002\u8fd9\u5ea7\u623f\u5b50\u6bd4\u539f\u6765\u7684\u5c0f\u5c4b\u5c11\u8bf4\u4e5f\u5927\u4e86\u4e09\u500d\uff0c\u4e00\u773c\u671b\u53bb\uff0c\u867d\u7136\u8d77\u5f97\u9887\u6709\u8349\u8349\u4e4b\u610f\uff0c\u4f46\u6c14\u6d3e\u751a\u662f\u96c4\u4f1f\u3002 \u3000\u3000\u4ed6\u53c8\u60ca\u53c8\u559c\uff0c\u4ed4\u7ec6\u518d\u770b\u5468\u906d\u666f\u7269\uff0c\u786e\u662f\u5e08\u7236\u7684\u8001\u5bb6\uff0c\u5fc3\u60f3\uff1a\u201c\u5e08\u7236\u53d1\u4e86\u8d22\u56de\u5bb6\u6765\u5566\uff0c\u90a3\u53ef\u597d\u6781\u4e86\u3002\u201d\u4ed6\u5927\u559c\u4e4b\u4e0b\uff0c\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u5e08\u7236\uff01\u201d\u4f46\u53ea\u53eb\u5f97\u4e00\u58f0\uff0c\u4fbf\u5373\u4f4f\u53e3\uff0c\u5fc3\u60f3\uff1a\u201c\u4e0d\u77e5\u5c4b\u91cc\u8fd8\u6709\u6ca1\u522b\u4eba\uff1f\u6211\u8fd9\u526f\u5c0f\u53eb\u5316\u7684\u6a21\u6837\uff0c\u522b\u4e22\u4e86\u5e08\u7236\u7684\u8138\uff0c\u4e14\u77a7\u4e2a\u660e\u767d\u518d\u8bf4\u3002\u201d\u4e5f\u662f\u4ed6\u8fd9\u4e9b\u5e74\u6765\u591a\u5386\u8270\u96be\uff0c\u624d\u6709\u8fd9\u756a\u8c28\u614e\uff0c\u6b63\u81ea\u601d\u91cf\uff0c\u5c4b\u91cc\u8d70\u51fa\u4e00\u4eba\uff0c\u659c\u773c\u5411\u4ed6\u6253\u91cf\uff0c\u8138\u4e0a\u6ee1\u662f\u9119\u5937\u7684\u795e\u6c14\uff0c\u95ee\u9053\uff1a\u201c\u5e72\u751a\u4e48\u7684\uff1f\u201d \u3000\u3000\u72c4\u4e91\u89c1\u8fd9\u4eba\u5e3d\u5b50\u6b6a\u6234\uff0c\u6ee1\u8eab\u7070\u571f\uff0c\u548c\u8fd9\u534e\u53a6\u9887\u4e3a\u4e0d\u79f0\uff0c\u77a7\u4ed6\u795e\u60c5\uff0c\u4f3c\u4e4e\u662f\u4e2a\u6ce5\u6c34\u6728\u5320\u7684\u5934\u513f\uff0c\u4fbf\u9053\uff1a\u201c\u8bf7\u95ee\u5934\u513f\uff0c\u621a\u5e08\u7236\u5728\u5bb6\u4e48\uff1f\u201d \u3000\u3000\u90a3\u4eba\u54fc\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u751a\u4e48\u4e03\u5e08\u7236\u3001\u516b\u5e08\u7236\u7684\uff0c\u8fd9\u91cc\u6ca1\u6709\u3002\u201d \u3000\u3000\u72c4\u4e91\u4e00\u6014\uff0c\u95ee\u9053\uff1a\u201c\u8fd9\u513f\u7684\u4e3b\u4eba\u4e0d\u662f\u59d3\u621a\u7684\u4e48\uff1f\u201d\u90a3\u4eba\u53cd\u95ee\u9053\uff1a\u201c\u4f60\u95ee\u8fd9\u4e2a\u5e72\u4e48\uff1f\u8981\u8ba8\u7c73\u561b\uff0c\u4e5f\u4e0d\u7528\u8ddf\u4eba\u5bb6\u6500\u4ea4\u60c5\u3002\u6ca1\u6709\uff0c\u5c31\u662f\u6ca1\u6709\uff01\u5c0f\u53eb\u5316\uff0c\u8d70\uff0c\u5feb\u8d70\uff01\u201d \u3000\u3000\u72c4\u4e91\u6302\u5ff5\u5e08\u7236\uff0c\u597d\u5bb9\u6613\u5343\u91cc\u8fe2\u8fe2\u7684\u56de\u6765\uff0c\u5982\u4f55\u80af\u5355\u51ed\u4ed6\u4e00\u53e5\u8bdd\u4fbf\u5373\u79bb\u53bb\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u662f\u8ba8\u7c73\u7684\uff0c\u8ddf\u4f60\u6253\u542c\u6253\u542c\uff0c\u4ece\u524d\u8fd9\u91cc\u4f4f\u7684\u662f\u59d3\u621a\u7684\uff0c\u4e0d\u77e5\u4ed6\u8001\u4eba\u5bb6\u662f\u4e0d\u662f\u8fd8\u4f4f\u5728\u8fd9\u91cc\uff1f\u201d \u3000\u3000\u90a3\u4eba\u51b7\u7b11\u9053\uff1a\u201c\u77a7\u4f60\u8fd9\u5c0f\u53eb\u5316\u513f\uff0c\u5c31\u662f\u6709\u8fd9\u95e8\u5b50\u7f57\u55e6\uff0c\u8fd9\u91cc\u4e3b\u4eba\u4e0d\u59d3\u621a\uff0c\u4e5f\u4e0d\u59d3\u516b\u3001\u59d3\u4e5d\u3001\u59d3\u5341\u3002\u4f60\u8001\u4eba\u5bb6\u4e58\u65e9\u7ed9\u6211\u8bf7\u7f62\u3002\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u5c4b\u4e2d\u53c8\u51fa\u6765\u4e00\u4eba\uff0c\u8fd9\u4eba\u5934\u6234\u74dc\u76ae\u5e3d\uff0c\u8863\u670d\u5149\u9c9c\uff0c\u662f\u4e2a\u8d22\u4e3b\u5bb6\u7684\u7ba1\u5bb6\u6a21\u6837\uff0c\u95ee\u9053\uff1a\u201c\u8001\u5e73\uff0c\u5927\u58f0\u56b7\u56b7\u7684\uff0c\u53c8\u5728\u8ddf\u8c01\u5435\u67b6\u4e86\uff1f\u201d\u90a3\u4eba\u7b11\u9053\uff1a\u201c\u4f60\u77a7\uff0c\u8fd9\u5c0f\u53eb\u5316\u7f57\u55e6\u4e0d\u7f57\u55e6\uff1f\u8ba8\u7c73\u4e5f\u5c31\u662f\u4e86\uff0c\u5374\u6765\u6253\u542c\u54b1\u4e3b\u4eba\u5bb6\u59d3\u751a\u4e48\uff1f\u201d\u90a3\u7ba1\u5bb6\u4e00\u542c\uff0c\u8138\u8272\u5fae\u53d8\uff0c\u5411\u72c4\u4e91\u6253\u91cf\u4e86\u534a\u664c\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u670b\u53cb\uff0c\u4f60\u6253\u542c\u54b1\u4e3b\u4eba\u59d3\u540d\u4f5c\u751a\uff1f\u201d \u3000\u3000\u82e5\u662f\u6362\u4f5c\u4e94\u516d\u5e74\u524d\u7684\u72c4\u4e91\uff0c\u81ea\u5373\u76f4\u9648\u5176\u4e8b\uff0c\u4f46\u8fd9\u65f6\u4ed6\u9605\u5386\u5df2\u5bcc\uff0c\u6df1\u77e5\u4eba\u5fc3\u9669\u6076\uff0c\u89c1\u90a3\u7ba1\u5bb6\u76ee\u5149\u4e2d\u6ee1\u662f\u7591\u5fcc\u4e4b\u8272\uff0c\u5bfb\u601d\uff1a\u201c\u6211\u4e14\u4e0d\u76f4\u8bf4\uff0c\u6162\u6162\u6253\u542c\u4e0d\u8fdf\uff0c\u83ab\u975e\u8fd9\u4e2d\u95f4\u6709\u751a\u4e48\u53e4\u602a\u3002\u201d \u3000\u3000\u4fbf\u9053\uff1a\u201c\u6211\u4e0d\u8fc7\u95ee\u4e3b\u4eba\u8001\u7237\u59d3\u751a\u4e48\uff0c\u60f3\u5927\u58f0\u53eb\u4ed6\u4e00\u58f0\uff0c\u8bf7\u4ed6\u65bd\u820d\u4e9b\u7c73\u996d\uff0c\u4f60\u2026\u2026\u4f60\u5c31\u662f\u8001\u7237\u7f62\uff1f\u201d\u4ed6\u6545\u610f\u88c5\u5f97\u50bb\u5934\u50bb\u8111\uff0c\u4ee5\u514d\u5f15\u8d77\u5bf9\u65b9\u7591\u5fc3\u3002 \u3000\u3000\u90a3\u7ba1\u5bb6\u54c8\u54c8\u5927\u7b11\uff0c\u867d\u89c9\u6b64\u4eba\u751a\u50bb\uff0c\u4f46\u4ed6\u7adf\u8bef\u8ba4\u81ea\u5df1\u4e3a\u8001\u7237\uff0c\u5fc3\u4e2d\u5012\u4e5f\u6b22\u559c\uff0c\u7b11\u9053\uff1a\u201c\u6211\u4e0d\u662f\u8001\u7237\uff0c\u5582\uff0c\u50bb\u5c0f\u5b50\uff0c\u4f60\u5e72\u4e48\u5f53\u6211\u662f\u8001\u7237\uff1f\u201d\u72c4\u4e91\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u6837\u5b50\u2026\u2026\u597d\u770b\uff0c\u5a01\u98ce\u5f97\u7d27\uff0c\u4f60\u2026\u2026\u4f60\u4e00\u526f\u8d22\u4e3b\u76f8\u3002\u201d \u3000\u3000\u90a3\u7ba1\u5bb6\u66f4\u52a0\u9ad8\u5174\u4e86\uff0c\u7b11\u9053\uff1a\u201c\u50bb\u5c0f\u5b50\uff0c\u6211\u8001\u9ad8\u4ed6\u65e5\u5f53\u771f\u53d1\u4e86\u5927\u8d22\uff0c\u5b9a\u6709\u597d\u5904\u7ed9\u4f60\u3002\u5582\uff0c\u50bb\u5c0f\u5b50\uff0c\u6211\u77a7\u4f60\u8eab\u5f3a\u529b\u58ee\uff0c\u5e72\u4e48\u4e0d\u597d\u597d\u505a\u4e8b\uff0c\u5374\u8981\u8ba8\u7c73\uff1f\u201d\u72c4\u4e91\u9053\uff1a\u201c\u6ca1\u4eba\u53eb\u6211\u505a\u4e8b\u554a\u3002\u8d22\u4e3b\u8001\u7237\uff0c\u4f60\u8d4f\u53e3\u996d\u7ed9\u6211\u5403\uff0c\u6210\u4e0d\u6210\uff1f\u201d\u90a3\u7ba1\u5bb6\u7528\u529b\u5728\u90a3\u59d3\u5e73\u7684\u80a9\u4e0a\u4e00\u62cd\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u542c\uff0c\u4ed6\u53e3\u53e3\u58f0\u58f0\u53eb\u6211\u8d22\u4e3b\u8001\u7237\uff0c\u4e0d\u8d4f\u53e3\u996d\u5403\u662f\u4e0d\u6210\u7684\u4e86\u3002\u8001\u5e73\uff0c\u4f60\u53eb\u4ed6\u4e5f\u53bb\u62c5\u571f\u7f62\uff0c\u7b97\u4e00\u4efd\u5de5\u94b1\u7ed9\u4ed6\u3002\u201d\u90a3\u59d3\u5e73\u7684\u9053\uff1a\u201c\u662f\u5566\uff0c\u51ed\u4f60\u8001\u5429\u5490\u4fbf\u662f\u3002\u201d \u3000\u3000\u72c4\u4e91\u542c\u4e24\u4eba\u53e3\u97f3\uff0c\u90a3\u59d3\u5e73\u7684\u5de5\u5934\u662f\u6e58\u897f\u672c\u5730\u4eba\uff0c\u90a3\u59d3\u9ad8\u7684\u7ba1\u5bb6\u5374\u662f\u5317\u65b9\u4eba\uff0c\u5f53\u4e0b\u4e0d\u52a8\u58f0\u8272\uff0c\u606d\u606d\u656c\u656c\u7684\u9053\uff1a\u201c\u8d22\u4e3b\u8001\u7237\uff0c\u8d22\u4e3b\u5c11\u7237\uff0c\u591a\u8c22\u4f60\u4eec\u4e24\u4e2a\u5566\u3002\u201d\u90a3\u5de5\u5934\u7b11\u9a82\uff1a\u201c\u4ed6\u5988\u7684\uff0c\u80e1\u8bf4\u516b\u9053\uff01\u201d\u90a3\u7ba1\u5bb6\u7b11\u5f97\u53ea\u662f\u8dcc\u811a\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u662f\u8d22\u4e3b\u8001\u7237\uff0c\u4f60\u662f\u8d22\u4e3b\u5c11\u7237\uff0c\u8fd9\u2026\u2026\u8fd9\u4e0d\u662f\u505a\u4e86\u4f60\u7684\u4fbf\u5b9c\u8001\u5b50\u5417\uff1f\u201d\u90a3\u5de5\u5934\u63ea\u7740\u72c4\u4e91\u8033\u6735\uff0c\u7b11\u9053\uff1a\u201c\u8fdb\u53bb\uff0c\u8fdb\u53bb\uff01\u5148\u597d\u597d\u5403\u4e00\u987f\uff0c\u665a\u4e0a\u5f00\u5de5\u3002\u201d \u3000\u3000\u72c4\u4e91\u6beb\u4e0d\u6297\u62d2\uff0c\u8ddf\u7740\u4ed6\u8fdb\u53bb\uff0c\u5fc3\u9053\uff1a\u201c\u600e\u4e48\u665a\u4e0a\u5f00\u5de5\uff1f\u201d \u3000\u3000\u8fdb\u5f97\u5927\u5c4b\uff0c\u7ecf\u8fc7\u4e00\u4e2a\u7a7f\u5802\uff0c\u4e0d\u7531\u5f97\u5927\u5403\u4e00\u60ca\uff0c\u773c\u524d\u6240\u89c1\u5f53\u771f\u5947\u602a\u4e4b\u6781\u3002\u53ea\u89c1\u5c4b\u5b50\u4e2d\u95f4\u6316\u6398\u4e86\u4e00\u4e2a\u6781\u5927\u7684\u6df1\u5751\uff0c\u571f\u5751\u8fb9\u7f18\u51e0\u4e4e\u548c\u56db\u9762\u5899\u58c1\u76f8\u8fde\uff0c\u53ea\u7559\u4e0b\u4e00\u6761\u7a84\u7a84\u7684\u901a\u9053\u3002\u571f\u5751\u4e2d\u4e22\u6ee1\u4e86\u94c1\u9504\u3001\u94c1\u94f2\u3001\u571f\u7b95\u3001\u6241\u62c5\u4e4b\u7c7b\u7528\u5177\uff0c\u663e\u7136\u8fd8\u5728\u6316\u6398\u3002\u770b\u4e86\u8fd9\u6240\u5927\u5c4b\u5916\u9762\u96c4\u4f1f\u5802\u7687\u7684\u6a21\u6837\uff0c\u54ea\u60f3\u5f97\u5230\u5c4b\u4e2d\u7adf\u4f1a\u6398\u4e86\u8fd9\u6837\u4e00\u4e2a\u5927\u571f\u5751\u3002 \u3000\u3000\u90a3\u5de5\u5934\u9053\uff1a\u201c\u8fd9\u91cc\u7684\u4e8b\uff0c\u4e0d\u8bb8\u5230\u5916\u9762\u53bb\u8bf4\uff0c\u77e5\u4e0d\u77e5\u9053\uff1f\u201d\u72c4\u4e91\u9053\uff1a\u201c\u662f\uff0c\u662f\uff01\u6211\u77e5\u9053\uff0c\u8fd9\u91cc\u98ce\u6c34\u597d\uff0c\u4e3b\u4eba\u5bb6\u8981\u846c\u575f\uff0c\u4e0d\u80fd\u8ba9\u5916\u9762\u7684\u4eba\u6653\u5f97\u3002\u201d\u90a3\u5de5\u5934\u563f\u563f\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u50bb\u5c0f\u5b50\u5012\u806a\u660e\uff0c\u8ddf\u6211\u6765\u5403\u996d\u7f62\u3002\u201d \u3000\u3000\u72c4\u4e91\u5728\u53a8\u623f\u4e2d\u9971\u9910\u4e86\u4e00\u987f\u3002\u90a3\u5de5\u5934\u53eb\u4ed6\u5728\u5eca\u4e0b\u7b49\u7740\uff0c\u4e0d\u53ef\u4e71\u8d70\u3002\u72c4\u4e91\u7b54\u5e94\u4e86\uff0c\u5fc3\u4e2d\u6108\u76ca\u8d77\u7591\u3002\u53ea\u89c1\u5c4b\u4e2d\u4e00\u5207\u9648\u8bbe\u90fd\u5341\u5206\u7b80\u964b\uff0c\u53a8\u623f\u4e2d\u7adf\u65e0\u780c\u597d\u7684\u7076\u5934\uff0c\u53ea\u6446\u7740\u4e00\u53ea\u5927\u884c\u7076\uff0c\u67b6\u4e86\u53ea\u94c1\u956c\u3002\u684c\u5b50\u677f\u51f3\u7b49\u7269\u4e5f\u90fd\u662f\u8d2b\u5bb6\u8d31\u7269\uff0c\u548c\u8fd9\u5ea7\u5927\u5c4b\u5b9e\u5728\u9887\u4e0d\u76f8\u79f0\u3002 \u3000\u3000\u5230\u5f97\u508d\u665a\uff0c\u8fdb\u5c4b\u6765\u7684\u4eba\u6e10\u591a\uff0c\u90fd\u662f\u5de6\u8fd1\u5e74\u8f7b\u529b\u58ee\u7684\u4e61\u6c11\uff0c\u5927\u5bb6\u95f9\u54c4\u54c4\u7684\u559d\u9152\u5403\u996d\u3002\u72c4\u4e91\u968f\u4f17\u800c\u98df\uff0c\u4ed6\u8bf4\u7684\u6b63\u662f\u5f53\u5730\u571f\u8bdd\uff0c\u8bed\u97f3\u6781\u6b63\u3002\u90a3\u7ba1\u5bb6\u548c\u5de5\u5934\u542c\u4e86\uff0c\u4e1d\u6beb\u4e0d\u8d77\u7591\u5fc3\uff0c\u90fd\u9053\u4ed6\u53ea\u662f\u672c\u5730\u4e00\u4e2a\u6e38\u624b\u597d\u95f2\u7684\u9752\u5e74\u3002 \u3000\u3000\u4f17\u4eba\u996d\u7f62\uff0c\u5e73\u5de5\u5934\u7387\u9886\u5927\u4f19\u6765\u5230\u5927\u5385\u4e4b\u4e2d\uff0c\u8bf4\u9053\uff1a\u201c\u54e5\u513f\u4eec\u5927\u5bb6\u51fa\u529b\u6316\u6398\uff0c\u76fc\u671b\u4eca\u665a\u8fd0\u6c14\u597d\uff0c\u82e5\u662f\u6316\u5230\u4e86\u751a\u4e48\u6709\u7528\u7684\u4e1c\u897f\uff0c\u91cd\u91cd\u6709\u8d4f\u3002\u201d\u4f17\u4eba\u7b54\u5e94\u4e86\uff0c\u9504\u5934\u94c1\u94f2\u649e\u51fb\u6ce5\u571f\u4e4b\u58f0\uff0c\u64e6\u64e6\u64e6\u7684\u54cd\u4e86\u8d77\u6765\u3002\u4e00\u4e2a\u5e74\u7eaa\u8f83\u957f\u7684\u4e61\u6c11\u4f4e\u58f0\u9053\uff1a\u201c\u6398\u4e86\u4e24\u4e2a\u591a\u6708\u5566\uff0c\u5c41\u4e5f\u6ca1\u6316\u5230\u534a\u4e2a\u3002\u5c31\u7b97\u8fd9\u91cc\u771f\u6709\u5b9d\u8d1d\uff0c\u4e5f\u8981\u770b\u4f60\u6709\u6ca1\u798f\u6c14\u62ff\u5230\u624b\u554a\u3002\u201d \u3000\u3000\u72c4\u4e91\u5fc3\u60f3\uff1a\u201c\u4ed6\u4eec\u60f3\u6398\u5b9d\uff1f\u8fd9\u91cc\u4f1a\u6709\u751a\u4e48\u5b9d\u7269\uff1f\u201d\u4ed6\u7b49\u5de5\u5934\u4e00\u80cc\u8f6c\u8eab\uff0c\u6162\u6162\u6328\u5230\u90a3\u5e74\u957f\u4e61\u6c11\u8eab\u8fb9\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5927\u53d4\uff0c\u4ed6\u4eec\u8981\u6398\u751a\u4e48\u5b9d\u8d1d\uff1f\u201d\u90a3\u4eba\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u8fd9\u5b9d\u8d1d\u53ef\u4e86\u4e0d\u8d77\u3002\u8fd9\u91cc\u7684\u4e3b\u4eba\u4f1a\u671b\u6c14\u3002\u4ed6\u4e0d\u662f\u672c\u5730\u4eba\uff0c\u8fdc\u8fdc\u77a7\u89c1\u8fd9\u91cc\u6709\u5b9d\u5149\u4e0a\u51b2\uff0c\u77e5\u9053\u5730\u91cc\u6709\u5b9d\u8d1d\uff0c\u4e8e\u662f\u6765\u4e70\u4e86\u8fd9\u5757\u5730\u76ae\uff0c\u751f\u6015\u8d70\u6f0f\u98ce\u58f0\uff0c\u56e0\u6b64\u5148\u76d6\u4e86\u8fd9\u5ea7\u5927\u5c4b\uff0c\u53eb\u54b1\u4eec\u767d\u5929\u7761\u89c9\uff0c\u591c\u665a\u6398\u5b9d\u3002\u201d\u72c4\u4e91\u70b9\u5934\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\uff0c\u5927\u53d4\u53ef\u77e5\u9053\u662f\u751a\u4e48\u5b9d\u8d1d\u5462\uff1f\u201d\u90a3\u4eba\u9053\uff1a\u201c\u5de5\u5934\u513f\u8bf4\uff0c\u90a3\u662f\u4e00\u53ea\u805a\u5b9d\u76c6\uff0c\u4e00\u4e2a\u94dc\u94b1\u653e\u8fdb\u4e86\u76c6\u4e2d\uff0c\u8fc7\u5f97\u4e00\u591c\uff0c\u660e\u65e9\u5c31\u53d8\u6210\u4e86\u4e00\u76c6\u94dc\u94b1\u3002\u4e00\u4e24\u91d1\u5b50\u653e\u8fdb\u76c6\u4e2d\uff0c\u660e\u65e9\u5c31\u53d8\u6210\u4e86\u6ee1\u76c6\u9ec4\u91d1\u3002\u4f60\u8bf4\u662f\u4e0d\u662f\u5b9d\u8d1d\uff1f\u201d \u3000\u3000\u72c4\u4e91\u8fde\u8fde\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u771f\u662f\u5b9d\u8d1d\uff0c\u771f\u662f\u5b9d\u8d1d\uff01\u201d\u90a3\u4eba\u53c8\u9053\uff1a\u201c\u5de5\u5934\u7279\u522b\u5429\u5490\uff0c\u4e0b\u9504\u8981\u8f7b\uff0c\u6253\u70c2\u4e86\u805a\u5b9d\u76c6\uff0c\u90a3\u53ef\u4e0d\u662f\u73a9\u7684\u3002\u5de5\u5934\u8bf4\u7684\uff0c\u6398\u5230\u4e86\u805a\u5b9d\u76c6\u540e\uff0c\u53ef\u4ee5\u501f\u7ed9\u54b1\u4eec\u6bcf\u4e2a\u4eba\u4f7f\u7528\u4e00\u665a\uff0c\u4f60\u7231\u653e\u751a\u4e48\u4e1c\u897f\u90fd\u6210\u3002\u50bb\u5c0f\u5b50\uff0c\u4f60\u5012\u81ea\u5df1\u5408\u8ba1\u5408\u8ba1\uff0c\u8981\u653e\u751a\u4e48\u4e1c\u897f\u3002\u201d\u72c4\u4e91\u60f3\u4e86\u4e00\u4f1a\uff0c\u9053\uff1a\u201c\u6211\u5e38\u5e38\u997f\u809a\u5b50\uff0c\u653e\u4e00\u7c92\u767d\u7c73\u8fdb\u53bb\uff0c\u660e\u5929\u53d8\u51fa\u4e00\u6ee1\u76c6\u767d\u7c73\u6765\uff0c\u5c82\u4e0d\u662f\u597d\uff1f\u201d\u90a3\u4eba\u54c8\u54c8\u5927\u7b11\uff0c\u9053\uff1a\u201c\u597d\uff0c\u597d\uff01\u201d \u3000\u3000\u90a3\u5de5\u5934\u542c\u5230\u7b11\u58f0\uff0c\u8fc7\u6765\u547c\u53f1\uff1a\u201c\u522b\u8017\u7740\u5c3d\u8bf4\u4e0d\u5e72\uff0c\u5feb\u6316\uff0c\u5feb\u6316\uff01\u201d \u3000\u3000\u72c4\u4e91\u5fc3\u60f3\uff1a\u201c\u4e16\u4e0a\u54ea\u6709\u751a\u4e48\u805a\u5b9d\u76c6\uff1f\u8fd9\u4e3b\u4eba\u51b3\u4e0d\u662f\u50bb\u5b50\uff0c\u5b9a\u662f\u53e6\u6709\u8ba1\u8c0b\uff0c\u634f\u9020\u805a\u5b9d\u76c6\u7684\u9b3c\u8bdd\u6765\u9a97\u4eba\u3002\u201d\u53c8\u4f4e\u58f0\u95ee\u9053\uff1a\u201c\u8fd9\u91cc\u4e3b\u4eba\u59d3\u751a\u4e48\uff1f\u4f60\u8bf4\u4ed6\u4e0d\u662f\u672c\u5730\u4eba\uff1f\u201d\u90a3\u4eba\u9053\uff1a\u201c\u4f60\u77a7\uff0c\u4e3b\u4eba\u4e0d\u662f\u51fa\u6765\u4e86\u4e48\uff1f\u201d \u3000\u3000\u72c4\u4e91\u987a\u7740\u4ed6\u773c\u5149\u671b\u53bb\uff0c\u53ea\u89c1\u540e\u5802\u8d70\u51fa\u4e00\u4eba\uff0c\u8eab\u5f62\u7626\u524a\uff0c\u53cc\u76ee\u70af\u70af\u6709\u795e\uff0c\u670d\u9970\u6781\u662f\u534e\u4e3d\uff0c\u7ea6\u83ab\u4e94\u5341\u6765\u5c81\u5e74\u7eaa\u3002\u72c4\u4e91\u53ea\u5411\u4ed6\u77a7\u4e86\u4e00\u773c\uff0c\u5fc3\u4e2d\u4fbf\u6026\u6026\u4e71\u8df3\uff0c\u8f6c\u8fc7\u4e86\u5934\uff0c\u4e0d\u6562\u5bf9\u4ed6\u518d\u770b\uff0c\u5fc3\u4e2d\u4e0d\u4f4f\u8bf4\u9053\uff1a\u201c\u8fd9\u4eba\u6211\u89c1\u8fc7\u7684\uff0c\u8fd9\u4eba\u6211\u89c1\u8fc7\u7684\u3002\u4ed6\u662f\u8c01\u5462\uff1f\u201d\u53ea\u89c9\u8fd9\u4eba\u76f8\u8c8c\u597d\u719f\uff0c\u4e00\u65f6\u5374\u60f3\u4e0d\u8d77\u5728\u54ea\u91cc\u89c1\u8fc7\u3002 \u3000\u3000\u53ea\u542c\u5f97\u90a3\u4eba\u9053\uff1a\u201c\u4eca\u665a\u5927\u4f19\u628a\u897f\u534a\u8fb9\u518d\u6398\u6df1\u4e09\u5c3a\uff0c\u4e0d\u8bba\u6709\u751a\u4e48\u7eb8\u7247\u788e\u5c51\uff0c\u6728\u6761\u7816\u74e6\uff0c\u4e00\u70b9\u90fd\u4e0d\u53ef\u6f0f\u4e86\uff0c\u90fd\u8981\u62ff\u4e0a\u6765\u7ed9\u6211\u3002\u201d\u72c4\u4e91\u542c\u5230\u4ed6\u7684\u8bf4\u8bdd\u4e4b\u58f0\uff0c\u5fc3\u5934\u4e00\u51db\uff0c\u767b\u65f6\u7701\u609f\uff1a\u201c\u662f\u4e86\uff0c\u539f\u6765\u662f\u4ed6\u3002\u201d\u4f4e\u4e0b\u4e86\u5934\uff0c\u659c\u773c\u53c8\u5411\u4ed6\u77a7\u4e86\u4e00\u773c\uff0c\u5fc3\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u679c\u771f\u662f\u4ed6\u3002\u201d \u3000\u3000\u8fd9\u95f4\u5927\u5c4b\u4e3b\u4eba\uff0c\u7adf\u662f\u5728\u8346\u5dde\u4e07\u9707\u5c71\u5bb6\u4e2d\u6559\u4e86\u4ed6\u4e09\u62db\u5251\u6cd5\u7684\u8001\u4e5e\u4e10\u3002 \u3000\u3000\u90a3\u65f6\u4ed6\u8863\u670d\u7834\u70c2\uff0c\u5934\u53d1\u84ec\u4e71\uff0c\u5168\u8eab\u6c61\u79fd\u4e4b\u6781\uff0c\u4eca\u65e5\u5374\u662f\u4e00\u4e2a\u8863\u9970\u534e\u8d35\u7684\u5927\u8d22\u4e3b\uff0c\u901a\u8eab\u90fd\u53d8\u4e86\u76f8\uff0c\u56e0\u6b64\u76f4\u5230\u542c\u4e86\u4ed6\u8bf4\u8bdd\u7684\u58f0\u97f3\uff0c\u8fd9\u624d\u8ba4\u51fa\u3002 \u3000\u3000\u72c4\u4e91\u7acb\u65f6\u4fbf\u60f3\u4ece\u5751\u4e2d\u8df3\u5c06\u4e0a\u53bb\uff0c\u548c\u4ed6\u76f8\u8ba4\uff0c\u4f46\u8fd9\u51e0\u5e74\u6765\u7684\u53d7\u82e6\u53d7\u96be\uff0c\u6559\u4f1a\u4ed6\u4e8b\u4e8b\u90fd\u8981\u90d1\u91cd\uff0c\u4e0d\u53ef\u9c81\u83bd\u6025\u8e81\uff0c\u5bfb\u601d\uff1a \u3000\u3000\u201c\u8fd9\u4f4d\u8001\u4e5e\u4e10\u4f2f\u4f2f\u5f85\u6211\u5f88\u597d\uff0c\u5f53\u5e74\u6211\u548c\u90a3\u5927\u76d7\u5415\u901a\u76f8\u6597\uff0c\u5df2\u7136\u843d\u8d25\uff0c\u5e78\u4e8f\u4ed6\u51fa\u624b\u76f8\u6551\u3002\u540e\u6765\u4ed6\u53c8\u6559\u4e86\u6211\u4e09\u62db\u7cbe\u5999\u7684\u5251\u6cd5\uff0c\u6211\u624d\u5f97\u5927\u80dc\u4e07\u95e8\u4f17\u5f1f\u5b50\u3002\u73b0\u4e0b\u60f3\u6765\uff0c\u4ed6\u8fd9\u4e09\u62db\u5251\u6cd5\u5e73\u5e73\u65e0\u5947\uff0c\u4e5f\u6ca1\u751a\u4e48\u4e86\u4e0d\u8d77\uff0c\u4f46\u5f53\u65f6\u5374\u4f7f\u6211\u5f97\u4ee5\u514d\u53d7\u7f9e\u8fb1\u3002\u201d \u3000\u3000\u53c8\u60f3\uff1a\u201c\u4eca\u65e5\u91cd\u4f1a\u3002\u539f\u8be5\u597d\u597d\u8c22\u4ed6\u4e00\u756a\u624d\u662f\u3002\u53ef\u662f\u8fd9\u91cc\u662f\u6211\u5e08\u7236\u7684\u65e7\u5c45\uff0c\u4ed6\u5728\u8fd9\u91cc\u6316\u6398\u751a\u4e48\u4e1c\u897f\uff1f\u4ed6\u4e3a\u751a\u4e48\u8981\u8d77\u8fd9\u6837\u4e00\u5ea7\u5927\u5c4b\uff0c\u63a9\u4eba\u8033\u76ee\uff1f\u4ed6\u4ece\u524d\u662f\u4e5e\u4e10\uff0c\u53c8\u600e\u6837\u53d1\u4e86\u5927\u8d22\uff1f\u201d\u5fc3\u4e0b\u6697\u6697\u7422\u78e8\uff1a\u201c\u8fd8\u662f\u77a7\u6e05\u695a\u4e86\u518d\u8bf4\u3002\u4ed6\u867d\u662f\u6211\u6069\u4eba\uff0c\u4f46\u8981\u62dc\u8c22\u4e5f\u4e0d\u5fc5\u5fd9\u5728\u4e00\u65f6\u3002\u4ed6\u600e\u4e48\u4e0d\u6015\u6211\u5e08\u7236\u56de\u6765\uff1f\u96be\u9053\u2026\u2026\u96be\u9053\u2026\u2026\u5e08\u7236\u7adf\u6b7b\u4e86\u4e48\uff1f\u201d \u3000\u3000\u4ed6\u4ece\u5c0f\u7531\u5e08\u7236\u517b\u80b2\u957f\u5927\uff0c\u5411\u6765\u4fbf\u5f53\u4ed6\u662f\u7236\u4eb2\u4e00\u822c\uff0c\u60f3\u5230\u5e08\u7236\u8bf4\u4e0d\u5b9a\u5df2\u7ecf\u901d\u4e16\uff0c\u4e0d\u7531\u5f97\u773c\u7736\u4fbf\u7ea2\u4e86\u3002 \u3000\u3000\u7a81\u7136\u4e4b\u95f4\uff0c\u4e1c\u5357\u89d2\u4e0a\u53d1\u51fa\u53ee\u7684\u4e00\u58f0\u8f7b\u54cd\uff0c\u4e00\u4e2a\u4e61\u6c11\u7684\u9504\u5934\u78b0\u5230\u4e86\u751a\u4e48\u4e1c\u897f\u3002\u90a3\u4e3b\u4eba\u8dc3\u5165\u5751\u4e2d\uff0c\u4fef\u8eab\u62ac\u8d77\u4e00\u4ef6\u4e1c\u897f\u3002\u5751\u4e2d\u4f17\u4e61\u6c11\u90fd\u505c\u4e86\u6316\u6398\uff0c\u5411\u4ed6\u671b\u53bb\uff0c\u53ea\u89c1\u4ed6\u624b\u4e2d\u62ff\u4e86\u4e00\u6839\u9508\u70c2\u94c1\u9489\uff0c\u7ffb\u6765\u8986\u53bb\u7684\u770b\u4e86\u534a\u664c\uff0c\u624d\u629b\u5728\u4e00\u8fb9\uff0c\u8bf4\u9053\uff1a\u201c\u52a8\u624b\u554a\uff0c\u5feb\u6316\uff0c\u5feb\u6316\uff01\u201d \u3000\u3000\u72c4\u4e91\u548c\u4f17\u4e61\u6c11\u5fd9\u4e86\u4e00\u591c\uff0c\u90a3\u4e3b\u4eba\u59cb\u7ec8\u5168\u795e\u8d2f\u6ce8\u7684\u5728\u65c1\u76d1\u7763\uff0c\u76f4\u5230\u5929\u660e\uff0c\u8fd9\u624d\u6536\u5de5\u3002\u591a\u6570\u4e61\u6c11\u6563\u53bb\u56de\u5bb6\uff0c\u6709\u4e03\u516b\u4eba\u4f4f\u5f97\u8fdc\uff0c\u4fbf\u5728\u5927\u5c4b\u4e1c\u8fb9\u5eca\u4e0b\u5e2d\u5730\u800c\u7761\u3002\u72c4\u4e91\u4e5f\u5728\u5eca\u4e0b\u7761\u4e86\u3002\u7761\u5230\u4e0b\u5348\uff0c\u4f17\u4eba\u624d\u8d77\u8eab\u5403\u996d\u3002\u72c4\u4e91\u8eab\u4e0a\u80ae\u810f\uff0c\u65c1\u4eba\u4e0d\u613f\u548c\u4ed6\u4eb2\u8fd1\uff0c\u7761\u89c9\u5403\u996d\u65f6\u90fd\u79bb\u5f97\u4ed6\u8fdc\u8fdc\u5730\u3002\u72c4\u4e91\u6b63\u662f\u6c42\u4e4b\u4e0d\u5f97\u3002\u4ed6\u5b66\u4f1a\u4e86\u5c0f\u5fc3\u8c28\u614e\uff0c\u4e0d\u6562\u8f7b\u4fe1\u65c1\u4eba\uff0c\u4f46\u8981\u5047\u88c5\u4f5c\u4f2a\uff0c\u4ecd\u662f\u9887\u89c9\u4e3a\u96be\uff0c\u65f6\u5019\u4e00\u4e45\uff0c\u5b9a\u7136\u9732\u51fa\u9a6c\u811a\uff0c\u522b\u4eba\u4e0d\u6765\u548c\u4ed6\u4eb2\u8fd1\uff0c\u90a3\u662f\u518d\u597d\u4e5f\u6ca1\u6709\u4e86\u3002 \u3000\u3000\u5403\u8fc7\u996d\u540e\uff0c\u72c4\u4e91\u8d70\u5411\u4e09\u91cc\u5916\u7684\u5c0f\u6751\uff0c\u60f3\u627e\u4eba\u6253\u542c\u5e08\u7236\u662f\u5426\u66fe\u7ecf\u56de\u6765\u8fc7\u3002\u8fdc\u8fdc\u89c1\u5230\u51e0\u4e2a\u5c11\u5e74\u65f6\u7684\u6e38\u4f34\uff0c\u8fd9\u65f6\u90fd\u5df2\u7c97\u58ee\u6210\u4eba\uff0c\u5728\u7530\u95f4\u5fd9\u788c\u5de5\u4f5c\uff0c\u4ed6\u4e0d\u613f\u663e\u9732\u81ea\u5df1\u8eab\u4efd\uff0c\u5e76\u4e0d\u4e0a\u524d\u62db\u547c\uff0c\u5bfb\u5230\u4e00\u4e2a\u4e0d\u76f8\u8bc6\u7684\u5341\u4e09\u56db\u5c81\u5c11\u5e74\uff0c\u95ee\u8d77\u90a3\u95f4\u5927\u5c4b\u7684\u60c5\u5f62\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u8bf4\u9053\uff0c\u5927\u5c4b\u662f\u53bb\u5e74\u79cb\u5929\u8d77\u7684\uff0c\u5c4b\u4e3b\u4eba\u5f88\u6709\u94b1\uff0c\u6765\u6398\u805a\u5b9d\u76c6\u7684\uff0c\u53ef\u662f\u6398\u5230\u8fd9\u65f6\u5019\u8fd8\u6ca1\u6398\u5230\u3002\u90a3\u5c11\u5e74\u8fb9\u8bf4\u8fb9\u7b11\uff0c\u53ef\u89c1\u6398\u805a\u5b9d\u76c6\u4e00\u4e8b\uff0c\u5728\u5de6\u8fd1\u4e00\u5e26\u5df2\u6210\u4e86\u7b11\u67c4\u3002\u201c\u539f\u6765\u7684\u90a3\u51e0\u95f4\u5c0f\u5c4b\u4e48\uff1f\u55ef\uff0c\u597d\u4e45\u6ca1\u4eba\u4f4f\u5566\uff0c\u4ece\u6765\u6ca1\u4eba\u56de\u6765\u8fc7\u3002\u8d77\u5927\u5c4b\u7684\u65f6\u5019\uff0c\u81ea\u7136\u662f\u628a\u5c0f\u5c4b\u62c6\u4e86\u3002\u201d \u3000\u3000\u72c4\u4e91\u522b\u8fc7\u4e86\u90a3\u5c11\u5e74\uff0c\u5fc3\u4e2d\u95f7\u95f7\u4e0d\u4e50\uff0c\u53c8\u662f\u5145\u6ee1\u4e86\u7591\u56e2\uff0c\u731c\u4e0d\u51fa\u90a3\u8001\u4e5e\u4e10\u5e72\u8fd9\u4ef6\u602a\u4e8b\u5230\u5e95\u662f\u4f55\u7528\u610f\u3002\u4ed6\u5728\u7530\u91ce\u95f4\u4fe1\u6b65\u800c\u884c\uff0c\u7ecf\u8fc7\u4e00\u5757\u83dc\u5730\uff0c\u4f46\u89c1\u4e00\u7247\u9752\u7eff\uff0c\u90fd\u79cd\u6ee1\u4e86\u7a7a\u5fc3\u83dc\u3002 \u3000\u3000\u201c\u7a7a\u5fc3\u83dc\uff0c\u7a7a\u5fc3\u83dc\uff01\u201d \u3000\u3000\u84e6\u7136\u4e4b\u95f4\uff0c\u4ed6\u5fc3\u4e2d\u54cd\u8d77\u4e86\u8fd9\u51e0\u4e0b\u6e05\u8106\u7684\u987d\u76ae\u7684\u58f0\u97f3\u3002\u7a7a\u5fc3\u83dc\u662f\u6e58\u897f\u4e00\u5e26\u6700\u5bfb\u5e38\u7684\u852c\u83dc\uff0c\u7c97\u751f\u7c97\u957f\uff0c\u83dc\u830e\u7684\u5fc3\u662f\u7a7a\u7684\u3002 \u3000\u3000\u4ed6\u5e08\u59b9\u621a\u82b3\u7ed9\u4ed6\u53d6\u4e86\u8fd9\u4e2a\u7ef0\u53f7\uff0c\u7b11\u4ed6\u76f4\u809a\u76f4\u80a0\uff0c\u6ca1\u534a\u70b9\u5fc3\u4e8b\u3002 \u3000\u3000\u4ed6\u81ea\u79bb\u6e58\u897f\u4e4b\u540e\uff0c\u76f4\u5230\u4eca\u65e5\uff0c\u624d\u518d\u770b\u5230\u7a7a\u5fc3\u83dc\u3002\u4ed6\u5446\u4e86\u534a\u664c\uff0c\u4fef\u8eab\u6458\u4e86\u4e00\u6839\uff0c\u95fb\u95fb\u9752\u83dc\u6c41\u6db2\u7684\u6c14\u606f\uff0c\u6162\u6162\u5411\u897f\u8d70\u53bb\u3002 \u3000\u3000\u897f\u8fb9\u90fd\u662f\u8352\u5c71\uff0c\u4e71\u77f3\u5d99\u5ccb\uff0c\u90a3\u662f\u8fde\u6cb9\u6850\u6811\u3001\u6cb9\u8336\u6811\u4e5f\u4e0d\u80fd\u79cd\u7684\u3002\u90a3\u8fb9\u8352\u5c71\u4e4b\u4e2d\uff0c\u6709\u4e00\u4e2a\u65c1\u4eba\u4ece\u6765\u4e0d\u77e5\u7684\u5c71\u6d1e\uff0c\u5374\u662f\u4ed6\u548c\u621a\u82b3\u4ee5\u524d\u5e38\u53bb\u73a9\u800d\u7684\u5730\u65b9\u3002\u4ed6\u6000\u5ff5\u6614\u65e5\uff0c\u4fe1\u6b65\u5411\u90a3\u5c71\u6d1e\u8d70\u53bb\u3002\u7ffb\u8fc7\u4e24\u4e2a\u5c71\u5761\uff0c\u94bb\u8fdb\u4e00\u4e2a\u5927\u5c71\u6d1e\uff0c\u624d\u6765\u5230\u8fd9\u5e7d\u79d8\u8352\u51c9\u7684\u5c71\u6d1e\u524d\u3002 \u3000\u3000\u53ea\u89c1\u4e00\u4e1b\u4e1b\u9f50\u80a9\u7684\u957f\u8349\uff0c\u628a\u6d1e\u53e3\u90fd\u906e\u4f4f\u4e86\u3002\u4ed6\u5fc3\u4e2d\u53c8\u662f\u4e00\u9635\u96be\u8fc7\uff0c\u94bb\u8fdb\u5c71\u6d1e\uff0c\u89c1\u6d1e\u4e2d\u5404\u7269\uff0c\u4ecd\u548c\u5f53\u5e74\u81ea\u5df1\u548c\u621a\u82b3\u79bb\u53bb\u65f6\u4e00\u6a21\u4e00\u6837\uff0c\u6ca1\u534a\u70b9\u79fb\u52a8\u8fc7\uff0c\u53ea\u662f\u79ef\u6ee1\u4e86\u5c18\u571f\u3002 \u3000\u3000\u621a\u82b3\u7528\u9ecf\u571f\u634f\u7684\u6ce5\u4eba\uff0c\u4ed6\u7528\u6765\u5f39\u9e1f\u7684\u5f39\u5f13\uff0c\u6349\u5c71\u5154\u7684\u677f\u673a\uff0c\u621a\u82b3\u653e\u725b\u65f6\u5439\u7684\u77ed\u7b1b\uff0c\u4ecd\u662f\u8fd9\u4e48\u653e\u5728\u6d1e\u91cc\u7684\u77f3\u4e0a\u3002\u90a3\u8fb9\u662f\u621a\u82b3\u7684\u9488\u7ebf\u7bee\u3002\u7bee\u4e2d\u7684\u526a\u5200\u5df2\u751f\u6ee1\u4e86\u9ec4\u9508\u3002 \u3000\u3000\u5f53\u5e74\u9022\u5230\u51ac\u5929\u519c\u95f2\u7684\u65e5\u5b50\uff0c\u4ed6\u5e38\u5728\u8fd9\u5c71\u6d1e\u91cc\u6253\u8349\u978b\u6216\u662f\u7f16\u7af9\u7b50\uff0c\u621a\u82b3\u5c31\u5750\u5728\u4ed6\u8eab\u7554\u505a\u978b\u5b50\u3002\u5979\u62ff\u4e9b\u96f6\u788e\u5e03\u7247\uff0c\u53e0\u6210\u978b\u5e95\uff0c\u7136\u540e\u4e00\u9488\u9488\u7684\u7f1d\u4e0a\u53bb\u3002\u5e08\u7236\u548c\u4ed6\u7684\u978b\u5b50\u90fd\u662f\u9752\u5e03\u978b\u9762\u3002 \u3000\u3000\u5979\u81ea\u5df1\u7684\uff0c\u978b\u9762\u4e0a\u6709\u65f6\u7ee3\u4e00\u6735\u82b1\uff0c\u6709\u65f6\u7ee3\u4e00\u53ea\u9e1f\uff0c\u90a3\u5f53\u7136\u662f\u8fc7\u5e74\u8fc7\u8282\u65f6\u7a7f\u7684\uff0c\u5e73\u5e38\u7a7f\u7684\u978b\u5b50\u4e5f\u90fd\u662f\u9752\u5e03\u9762\u3002\u82e5\u662f\u4e0b\u7530\u4e0b\u5730\u505a\u5e84\u7a3c\uff0c\u4e0d\u662f\u7a7f\u8349\u978b\uff0c\u5c31\u662f\u8d64\u811a\u3002 \u3000\u3000\u72c4\u4e91\u968f\u624b\u4ece\u9488\u7ebf\u7bee\u4e2d\u62ff\u8d77\u4e00\u672c\u65e7\u4e66\uff0c\u4e66\u7684\u5c01\u9762\u4e0a\u5199\u7740\u201c\u5510\u8bd7\u9009\u8f91\u201d\u56db\u4e2a\u5b57\u3002\u4ed6\u548c\u621a\u82b3\u90fd\u8bc6\u5b57\u4e0d\u591a\uff0c\u8c01\u4e5f\u4e0d\u4f1a\u53bb\u8bfb\u751a\u4e48\u5510\u8bd7\uff0c\u90a3\u662f\u621a\u82b3\u7528\u6765\u5939\u978b\u6837\u3001\u7ee3\u82b1\u6837\u7684\u3002\u4ed6\u968f\u624b\u7ffb\u5f00\u4e66\u672c\uff0c\u62ff\u51fa\u4e24\u5f20\u7eb8\u6837\u6765\u3002\u90a3\u662f\u4e00\u5bf9\u8774\u8776\uff0c\u662f\u621a\u82b3\u526a\u6765\u505a\u7ee3\u82b1\u6837\u7684\u3002\u4ed6\u5fc3\u91cc\u6e05\u6e05\u695a\u695a\u7684\u6d8c\u73b0\u4e86\u90a3\u65f6\u7684\u60c5\u666f\u3002 \u3000\u3000\u4e00\u5bf9\u9ec4\u9ed1\u76f8\u95f4\u7684\u5927\u8774\u8776\u98de\u5230\u4e86\u5c71\u6d1e\u53e3\uff0c\u4e00\u4f1a\u513f\u98de\u5230\u4e1c\uff0c\u4e00\u4f1a\u513f\u98de\u5230\u897f\uff0c\u4f46\u4e24\u53ea\u8774\u8776\u59cb\u7ec8\u4e0d\u5206\u5f00\u3002\u621a\u82b3\u53eb\u4e86\u8d77\u6765\uff1a\u201c\u6881\u5c71\u4f2f\uff0c\u795d\u82f1\u53f0\uff01\u6881\u5c71\u4f2f\uff0c\u795d\u82f1\u53f0\uff01\u201d\u6e58\u897f\u4e00\u5e26\u7684\u4eba\u7ba1\u8fd9\u79cd\u5f69\u8272\u5927\u8774\u8776\u53eb\u201c\u6881\u5c71\u4f2f\uff0c\u795d\u82f1\u53f0\u201d\u3002\u8fd9\u79cd\u8774\u8776\u5b9a\u662f\u96cc\u96c4\u4e00\u5bf9\uff0c\u53cc\u5bbf\u53cc\u98de\u3002 \u3000\u3000\u72c4\u4e91\u6b63\u5728\u6253\u8349\u978b\uff0c\u8fd9\u5bf9\u8774\u8776\u98de\u5230\u4ed6\u8eab\u65c1\uff0c\u4ed6\u4e3e\u8d77\u534a\u53ea\u8349\u978b\uff0c\u62cd\u7684\u4e00\u4e0b\uff0c\u5c31\u5c06\u4e00\u53ea\u8774\u8776\u6253\u6b7b\u4e86\u3002\u621a\u82b3\u201c\u554a\u201d\u7684\u4e00\u58f0\u53eb\u4e86\u8d77\u6765\uff0c\u6012\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u5e72\u751a\u4e48\uff1f\u201d\u72c4\u4e91\u89c1\u5979\u7a81\u7136\u53d1\u6012\uff0c\u4e0d\u7531\u5f97\u624b\u8db3\u65e0\u63aa\uff0c\u55eb\u5685\u9053\uff1a\u201c\u4f60\u559c\u6b22\u2026\u2026\u8774\u8776\uff0c\u6211\u2026\u2026\u6211\u6253\u6765\u7ed9\u4f60\u3002\u201d \u3000\u3000\u6b7b\u8774\u8776\u6389\u5728\u5730\u4e0b\uff0c\u4e00\u52a8\u4e5f\u4e0d\u52a8\u4e86\uff0c\u90a3\u53ea\u6ca1\u6b7b\u7684\u5374\u7ed5\u7740\u6b7b\u8776\uff0c\u4e0d\u4f4f\u7684\u76d8\u65cb\u98de\u52a8\u3002 \u3000\u3000\u621a\u82b3\u9053\uff1a\u201c\u4f60\u77a7\uff0c\u8fd9\u4e48\u4f5c\u5b7d\uff01\u4eba\u5bb6\u597d\u597d\u4e00\u5bf9\u592b\u59bb\uff0c\u4f60\u6d3b\u751f\u751f\u628a\u5b83\u4eec\u62c6\u6563\u4e86\u3002\u201d\u72c4\u4e91\u770b\u5230\u5979\u9eef\u7136\u7684\u795e\u8272\uff0c\u542c\u5230\u5979\u96be\u8fc7\u7684\u8bed\u97f3\uff0c\u5fc3\u4e2d\u624d\u89c9\u6b49\u7136\uff0c\u9053\uff1a\u201c\u5509\uff0c\u8fd9\u53ef\u662f\u6211\u7684\u4e0d\u5bf9\u5566\u3002\u201d \u3000\u3000\u540e\u6765\uff0c\u621a\u82b3\u7167\u7740\u90a3\u53ea\u6b7b\u8776\uff0c\u526a\u4e86\u4e2a\u7ee3\u82b1\u7eb8\u6837\uff0c\u7ee3\u5728\u5979\u81ea\u5df1\u7684\u978b\u4e0a\u3002\u5230\u8fc7\u5e74\u7684\u65f6\u5019\uff0c\u53c8\u7ee3\u4e86\u4e00\u53ea\u8377\u5305\u7ed9\u4ed6\uff0c\u4e5f\u662f\u8fd9\u4e48\u4e00\u5bf9\u8774\u8776\uff0c\u9ec4\u8272\u548c\u9ed1\u8272\u7684\u7fc5\u8180\uff0c\u7fc5\u4e0a\u9760\u8fd1\u8eab\u4f53\u5904\u6709\u4e9b\u7ea2\u8272\u3001\u7eff\u8272\u7684\u7ec6\u7ebf\u3002\u8fd9\u53ea\u8377\u5305\u4ed6\u4e00\u76f4\u5e26\u5728\u8eab\u8fb9\uff0c\u4f46\u5728\u8346\u5dde\u88ab\u6349\u8fdb\u72f1\u4e2d\u4e4b\u540e\uff0c\u5c31\u7ed9\u72f1\u5352\u62ff\u53bb\u4e86\u3002 \u3000\u3000\u72c4\u4e91\u62ff\u7740\u90a3\u5bf9\u505a\u7ee3\u82b1\u6837\u5b50\u7684\u7eb8\u8776\uff0c\u8033\u4e2d\u9690\u9690\u7ea6\u7ea6\u4f3c\u4e4e\u542c\u5230\u621a\u82b3\u7684\u58f0\u97f3\uff1a\u201c\u4f60\u77a7\uff0c\u8fd9\u4e48\u4f5c\u5b7d\uff01\u4eba\u5bb6\u597d\u597d\u4e00\u5bf9\u592b\u59bb\uff0c\u4f60\u6d3b\u751f\u751f\u628a\u5b83\u4eec\u62c6\u6563\u4e86\u3002\u201d \u3000\u3000\u4ed6\u5446\u4e86\u4e00\u9635\uff0c\u5c06\u7eb8\u8776\u53c8\u631f\u56de\u4e66\u4e2d\uff0c\u968f\u624b\u7ffb\u52a8\uff0c\u89c1\u4e66\u9875\u4e2d\u8fd8\u6709\u8bb8\u591a\u7ea2\u7eb8\u82b1\u6837\uff0c\u6709\u7684\u662f\u4e00\u5c3e\u9ca4\u9c7c\uff0c\u6709\u7684\u662f\u4e09\u53ea\u5c71\u7f8a\uff0c\u90a3\u662f\u8fc7\u5e74\u65f6\u8d34\u5728\u7a97\u4e0a\u7684\u7a97\u82b1\uff0c\u90fd\u662f\u621a\u82b3\u526a\u7684\u3002 \u3000\u3000\u4ed6\u6b63\u62ff\u4e86\u4e00\u5f20\u5f20\u7684\u7ec6\u770b\uff0c\u5ffd\u542c\u5f97\u6570\u5341\u4e08\u5916\u53d1\u51fa\u77f3\u5934\u76f8\u51fb\u7684\u5580\u5587\u4e00\u54cd\uff0c\u6709\u4eba\u8d70\u6765\u3002\u4ed6\u5fc3\u60f3\uff1a\u201c\u8fd9\u91cc\u4ece\u6ca1\u4eba\u6765\uff0c\u96be\u9053\u662f\u91ce\u517d\u4e48\uff1f\u201d\u987a\u624b\u5c06\u631f\u7740\u7ee3\u82b1\u7eb8\u6837\u7684\u4e66\u5f80\u6000\u4e2d\u4e00\u585e\u3002 \u3000\u3000\u53ea\u542c\u5f97\u6709\u4eba\u8bf4\u9053\uff1a\u201c\u8fd9\u4e00\u5e26\u8352\u51c9\u5f97\u5f88\uff0c\u4e0d\u4f1a\u5728\u8fd9\u91cc\u7684\u3002\u201d\u53e6\u4e00\u4e2a\u82cd\u8001\u7684\u58f0\u97f3\u9053\uff1a\u201c\u563f\uff0c\u8d8a\u662f\u8352\u51c9\uff0c\u8d8a\u662f\u6709\u4eba\u6765\u6536\u85cf\u5b9d\u7269\u3002 \u3000\u3000\u54b1\u4eec\u5f97\u597d\u597d\u5728\u8fd9\u91cc\u5bfb\u5bfb\u3002\u201d\u72c4\u4e91\u5fc3\u9053\uff1a\u201c\u600e\u4e48\u5230\u8fd9\u91cc\u5bfb\u5b9d\u6765\u7740\uff1f\u201d \u3000\u3000\u95ea\u8eab\u51fa\u4e86\u5c71\u6d1e\uff0c\u9690\u8eab\u5728\u4e00\u682a\u5927\u6811\u4e4b\u540e\u3002 \u3000\u3000\u8fc7\u4e0d\u591a\u65f6\uff0c\u4fbf\u6709\u4eba\u5411\u8fd9\u8fb9\u8d70\u6765\uff0c\u542c\u811a\u6b65\u58f0\u5171\u6709\u4e03\u516b\u4eba\u3002\u4ed6\u4ece\u6811\u540e\u671b\u5c06\u51fa\u53bb\uff0c\u53ea\u89c1\u5f53\u5148\u4e00\u4eba\u8863\u670d\u5149\u9c9c\uff0c\u6cb9\u5934\u7c89\u8138\uff0c\u76f8\u8c8c\u597d\u719f\uff0c\u8ddf\u7740\u53c8\u6709\u4e00\u4eba\u624b\u4e2d\u63d0\u7740\u94c1\u94f2\uff0c\u8d70\u4e86\u8fc7\u6765\u3002\u8fd9\u4eba\u8eab\u6750\u9ad8\u9ad8\u7684\uff0c\u6c14\u5b87\u8f69\u6602\u3002\u72c4\u4e91\u4e00\u89c1\uff0c\u4e0d\u7531\u5f97\u6012\u6c14\u4e0a\u51b2\uff0c\u7acb\u65f6\u4fbf\u60f3\u51b2\u51fa\u53bb\u4e00\u628a\u634f\u6b7b\u4e86\u4ed6\u3002 \u3000\u3000\u8fd9\u4eba\u6b63\u662f\u90a3\u593a\u4ed6\u5e08\u59b9\uff0c\u9001\u4ed6\u5165\u72f1\uff0c\u5bb3\u5f97\u4ed6\u53d7\u5c3d\u5343\u8f9b\u4e07\u82e6\u7684\u4e07\u572d\u3002 \u3000\u3000\u4ed6\u600e\u4e48\u4f1a\u5230\u4e86\u8fd9\u91cc\uff1f \u3000\u3000\u65c1\u8fb9\u90a3\u4e2a\u5e74\u7eaa\u7565\u8f7b\u7684\uff0c\u5374\u662f\u4e07\u95e8\u5c0f\u5e08\u5f1f\u6c88\u57ce\u3002 \u3000\u3000\u90a3\u4e24\u4eba\u4e00\u8d70\u8fc7\uff0c\u540e\u9762\u6765\u7684\u90fd\u662f\u4e07\u95e8\u5f1f\u5b50\uff0c\u9c81\u5764\u3001\u5b59\u5747\u3001\u535c\u57a3\u3001\u5434\u574e\u3001\u51af\u5766\u4e00\u9f50\u5230\u4e86\u3002 \u3000\u3000\u4e07\u95e8\u672c\u6709\u516b\u5f1f\u5b50\uff0c\u4e8c\u5f1f\u5b50\u5468\u573b\u5728\u8346\u5dde\u57ce\u5e9f\u56ed\u4e2d\u4e3a\u72c4\u4e91\u6240\u6740\uff0c\u53ea\u5269\u4e0b\u4e03\u4eba\u4e86\u3002\u72c4\u4e91\u597d\u751f\u5947\u602a\uff1a\u201c\u8fd9\u6279\u4eba\u8d76\u5230\u8fd9\u91cc\uff0c\u5bfb\u751a\u4e48\u5b9d\u8d1d\uff1f\u96be\u9053\u4e5f\u662f\u5bfb\u805a\u5b9d\u76c6\u4e48\uff1f\u201d \u3000\u3000\u53ea\u542c\u5f97\u6c88\u57ce\u53eb\u4e86\u8d77\u6765\uff1a\u201c\u5e08\u7236\uff0c\u5e08\u7236\uff0c\u8fd9\u91cc\u6709\u4e2a\u5c71\u6d1e\u3002\u201d\u90a3\u82cd\u8001\u7684\u58f0\u97f3\u9053\uff1a\u201c\u662f\u5417\uff1f\u201d\u8bed\u97f3\u4e2d\u6291\u5236\u4e0d\u4f4f\u559c\u60a6\u4e4b\u60c5\u3002\u8ddf\u7740\u4e00\u4e2a\u9ad8\u5927\u7684\u4eba\u5f62\u8d70\u4e86\u8fc7\u6765\uff0c\u6b63\u662f\u4e94\u4e91\u624b\u4e07\u9707\u5c71\u3002\u72c4\u4e91\u548c\u4ed6\u591a\u5e74\u4e0d\u89c1\uff0c\u53ea\u89c1\u4ed6\u7cbe\u795e\u77cd\u94c4\uff0c\u6b65\u5c65\u6c88\u7a33\uff0c\u4e1d\u6beb\u4e0d\u89c1\u82cd\u8001\u4e4b\u6001\u3002 \u3000\u3000\u4e07\u9707\u5c71\u5f53\u5148\u8fdb\u4e86\u5c71\u6d1e\uff0c\u4f17\u5f1f\u5b50\u4e00\u62e5\u800c\u8fdb\u3002\u6d1e\u4e2d\u4f20\u51fa\u6765\u8bf8\u4eba\u7684\u58f0\u97f3\uff1a\u201c\u8fd9\u91cc\u6709\u4eba\u4f4f\u7684\uff01\u201d\u201c\u7070\u5c18\u79ef\u5f97\u8fd9\u6837\u539a\uff0c\u591a\u5e74\u6ca1\u4eba\u6765\u4e86\u3002\u201d\u201c\u4e0d\uff0c\u4e0d\uff01\u4f60\u77a7\uff0c\u8fd9\u91cc\u6709\u65b0\u7684\u811a\u5370\u3002\u201d\u201c\u554a\uff0c\u8fd9\u91cc\u6709\u65b0\u624b\u5370\uff0c\u6709\u4eba\u521a\u6765\u8fc7\u4e0d\u4e45\u3002\u201d\u201c\u4e00\u5b9a\u662f\u8a00\u5e08\u53d4\uff0c\u4ed6\u2026\u2026\u4ed6\u5c06\u8fde\u57ce\u5251\u8c31\u5077\u4e86\u53bb\u5566\u3002\u201d \u3000\u3000\u72c4\u4e91\u53c8\u662f\u5403\u60ca\uff0c\u53c8\u662f\u597d\u7b11\uff1a\u201c\u4ed6\u4eec\u8981\u627e\u8fde\u57ce\u5251\u6cd5\u7684\u5251\u8c31\u4e48\uff1f \u3000\u3000\u600e\u5730\u6405\u4e86\u8fd9\u4e48\u4e45\uff0c\u8fd8\u662f\u6ca1\u627e\u5230\uff1f\u751a\u4e48\u8a00\u5e08\u53d4\uff1f\u5e08\u7236\u8bf4\u4ed6\u4e8c\u5e08\u5144\u8a00\u8fbe\u5e73\u5931\u8e2a\u591a\u5e74\uff0c\u97f3\u8baf\u4e0d\u77e5\uff0c\u53ea\u6015\u65e9\u5df2\u4e0d\u5728\u4eba\u4e16\uff0c\u600e\u4e48\u53c8\u4f1a\u94bb\u4e86\u51fa\u6765\u593a\u8fde\u57ce\u5251\u8c31\uff1f\u90a3\u660e\u660e\u662f\u6211\u7559\u4e0b\u7684\u624b\u5370\u811a\u5370\uff0c\u4ed6\u4eec\u778e\u731c\u4e00\u901a\uff0c\u771f\u662f\u6d3b\u89c1\u9b3c\u4e86\u3002\u201d \u3000\u3000\u53ea\u542c\u4e07\u9707\u5c71\u9053\uff1a\u201c\u5927\u5bb6\u522b\u5fd9\u7740\u8d77\u54c4\uff0c\u56db\u4e0b\u91cc\u5c0f\u5fc3\u627e\u4e00\u627e\u3002\u201d \u3000\u3000\u6709\u4eba\u9053\uff1a\u201c\u8a00\u5e08\u53d4\u65e2\u6765\u8fc7\u8fd9\u91cc\uff0c\u90a3\u8fd8\u6709\u4e0d\u62ff\u4e86\u53bb\u7684\uff1f\u201d\u6709\u4eba\u9053\uff1a\u201c\u621a\u957f\u53d1\u8fd9\u53ae\u771f\u5de5\u5fc3\u8ba1\uff0c\u5c06\u5251\u8c31\u85cf\u5728\u8fd9\u91cc\uff0c\u522b\u4eba\u8fd8\u771f\u4e0d\u5bb9\u6613\u627e\u5230\u3002\u201d\u53c8\u4e00\u4eba\u9053\uff1a\u201c\u4ed6\u5f53\u7136\u5de5\u4e8e\u5fc3\u8ba1\u554a\uff0c\u5426\u5219\u600e\u4e48\u4f1a\u53eb\u2018\u94c1\u9501\u6a2a\u6c5f\u2019\uff1f\u201d \u3000\u3000\u4e07\u9707\u5c71\u9053\uff1a\u201c\u521a\u624d\u54b1\u4eec\u8fdc\u8fdc\u8ddf\u7740\u90a3\u4e61\u4e0b\u4eba\u8fc7\u6765\uff0c\u8fd9\u4eba\u811a\u6b65\u597d\u5feb\uff0c\u4e00\u4f1a\u513f\u5c31\u4e0d\u89c1\u4e86\u3002\u8fd9\u4e2a\u4eba\u8bf4\u4e0d\u5b9a\u8fd8\u6709\u70b9\u90aa\u95e8\u3002\u201d\u4e07\u572d\u9053\uff1a\u201c\u672c\u5730\u4e61\u4e0b\u4eba\u719f\u6089\u5c71\u8def\uff0c\u5b9a\u662f\u8f6c\u4e0a\u5c0f\u8def\u8d70\u4e86\u3002\u82e5\u4e0d\u662f\u4ed6\uff0c\u54b1\u4eec\u5c31\u7b97\u518d\u627e\u4e0a\u4e00\u5e74\u534a\u8f7d\uff0c\u6050\u6015\u4e5f\u4e0d\u4f1a\u627e\u5230\u8fd9\u513f\u6765\u3002\u201d \u3000\u3000\u72c4\u4e91\u5fc3\u60f3\uff1a\u201c\u539f\u6765\u4ed6\u4eec\u662f\u8ddf\u7740\u6211\u6765\u7684\uff0c\u5426\u5219\u8fd9\u5c71\u6d1e\u8fd9\u4e48\u9690\u50fb\uff0c\u53c8\u600e\u4f1a\u7ed9\u4ed6\u4eec\u627e\u5230\u3002\u201d \u3000\u3000\u53ea\u542c\u5f97\u5404\u4eba\u4e71\u8f70\u8f70\u7684\u5230\u5904\u4e00\u9635\u7ffb\u638f\u3002\u6d1e\u91cc\u672c\u6765\u6ca1\u751a\u4e48\u4e1c\u897f\uff0c\u5404\u4eba\u8fd9\u6837\u4e71\u7ffb\uff0c\u4e5f\u4e0d\u8fc7\u662f\u5c06\u51e0\u4ef6\u7834\u70c2\u7269\u4e8b\u4e1c\u4e22\u6765\u3001\u897f\u4e22\u53bb\u7684\u79fb\u52a8\u4e00\u4e0b\u4f4d\u7f6e\u800c\u5df2\u3002\u8ddf\u7740\u94c1\u94f2\u6316\u5730\u4e4b\u58f0\u54cd\u8d77\uff0c\u4f46\u5c71\u6d1e\u5e95\u4e0b\u90fd\u662f\u5ca9\u77f3\uff0c\u54ea\u91cc\u6316\u5f97\u4e0b\u53bb\uff0c\u4e07\u9707\u5c71\u9053\uff1a\u201c\u6ca1\u751a\u4e48\u7559\u7740\u4e86\uff0c\u5927\u4f19\u51fa\u53bb\uff0c\u5230\u5916\u9762\u5408\u8ba1\u5408\u8ba1\u3002\u201d \u3000\u3000\u53ea\u89c1\u4f17\u5f1f\u5b50\u968f\u7740\u4e07\u9707\u5c71\u51fa\u6765\uff0c\u8d70\u5230\u5c71\u6eaa\u65c1\uff0c\u5728\u5ca9\u77f3\u4e0a\u5750\u4e86\u4e0b\u6765\u3002\u72c4\u4e91\u4e0d\u613f\u7ed9\u4ed6\u4eec\u53d1\u89c1\uff0c\u4e0d\u6562\u8d70\u8fd1\u3002\u8fd9\u516b\u4eba\u8bf4\u8bdd\u58f0\u97f3\u751a\u4f4e\uff0c\u542c\u4e0d\u89c1\u8bf4\u4e9b\u751a\u4e48\u3002\u8fc7\u5f97\u597d\u4e00\u4f1a\uff0c\u516b\u4e2a\u4eba\u7ad9\u8d77\u8eab\u6765\u8d70\u4e86\u3002 \u3000\u3000\u72c4\u4e91\u5fc3\u60f3\uff1a\u201c\u4ed6\u4eec\u662f\u6765\u627e\u8fde\u57ce\u5251\u8c31\uff0c\u5374\u7591\u5fc3\u662f\u7ed9\u6211\u4e8c\u5e08\u4f2f\u8a00\u8fbe\u5e73\u76d7\u4e86\u53bb\u3002\u6211\u5e08\u7236\u7684\u5bb6\u7ed9\u6539\u6210\u4e86\u4e00\u5ea7\u5927\u5c4b\u5b50\uff0c\u90a3\u8001\u4e10\u8bf4\u8981\u627e\u751a\u4e48\u805a\u5b9d\u76c6\u2026\u2026\u554a\uff0c\u662f\u4e86\uff0c\u662f\u4e86\uff01\u201d \u3000\u3000\u7a81\u7136\u4e4b\u95f4\uff0c\u4e00\u9053\u7075\u5149\u95ea\u8fc7\u8111\u6d77\uff0c\u731b\u5730\u91cc\u604d\u7136\u5927\u609f\uff1a\u201c\u8fd9\u8001\u4e5e\u4e10\u54ea\u91cc\u662f\u627e\u751a\u4e48\u805a\u5b9d\u76c6\u4e86\uff0c\u4ed6\u4e5f\u662f\u5728\u5bfb\u8fde\u57ce\u5251\u8c31\u3002\u4ed6\u8ba4\u5b9a\u8fd9\u5251\u8c31\u662f\u843d\u5165\u4e86\u6211\u5e08\u7236\u624b\u4e2d\uff0c\u4e8e\u662f\u5230\u8fd9\u91cc\u6765\u4ed4\u7ec6\u641c\u5bfb\uff0c\u4e3a\u4e86\u63a9\u4eba\u8033\u76ee\uff0c\u5148\u8d77\u8fd9\u4e48\u4e00\u5ea7\u5927\u5c4b\uff0c\u7136\u540e\u518d\u5728\u5c4b\u4e2d\u6316\u5751\u627e\u5bfb\uff0c\u751f\u6015\u522b\u4eba\u8d77\u7591\uff0c\u4f20\u51fa\u98ce\u58f0\u8bf4\u662f\u627e\u805a\u5b9d\u76c6\uff0c\u90a3\u81ea\u7136\u662f\u6b3a\u9a97\u4e61\u4e0b\u4eba\u7684\u9b3c\u8bdd\u3002\u201d \u3000\u3000\u8ddf\u7740\u53c8\u60f3\uff1a\u201c\u90a3\u65e5\u4e07\u5e08\u4f2f\u505a\u5bff\uff0c\u8fd9\u8001\u4e5e\u4e10\u767d\u5929\u591c\u665a\u7684\u6765\u6765\u53bb\u53bb\uff0c\u663e\u7136\u662f\u522b\u6709\u7528\u5fc3\u3002\u55ef\uff0c\u4e07\u9707\u5c71\u4ed6\u4eec\u627e\u4e0d\u5230\u5251\u8c31\uff0c\u5c82\u6709\u4e0d\u5230\u5927\u5c4b\u53bb\u67e5\u5bdf\u4e4b\u7406\uff1f\u591a\u534a\u65e9\u5df2\u53bb\u67e5\u5bdf\u8fc7\u4e86\u3002\u8fd9\u4ef6\u4e8b\u5c1a\u672a\u4e86\u7ed3\uff0c\u6211\u5230\u90a3\u5927\u5c4b\u53bb\u7b49\u7740\u77a7\u70ed\u95f9\u4fbf\u662f\uff0c\u8fd9\u4e2d\u95f4\u5927\u6709\u53e4\u602a\uff0c\u4e00\u767e\u4e2a\u4e0d\u5bf9\u5934\uff01\u201d\u201c\u53ef\u662f\u6211\u5e08\u7236\u5462\uff1f\u4ed6\u8001\u4eba\u5bb6\u5230\u4e86\u54ea\u91cc\uff0c\u4ed6\u7684\u5bb6\u7ed9\u4eba\u6405\u5f97\u8fd9\u4e48\u5929\u7ffb\u5730\u8986\uff0c\u4ed6\u77e5\u4e0d\u77e5\u9053\uff1f\u201d \u3000\u3000\u201c\u5e08\u59b9\u5462\uff1f\u5979\u662f\u7559\u5728\u8346\u5dde\u57ce\u91cc\uff0c\u4eab\u798f\u505a\u5c11\u5976\u5976\u7f62\u3002\u4e07\u5bb6\u7684\u4eba\u8981\u6765\u641c\u67e5\u5979\u7236\u4eb2\u7684\u5c4b\u5b50\uff0c\u591a\u534a\u4e0d\u4f1a\u7ed9\u5979\u77e5\u9053\u3002\u8fd9\u65f6\u5019\uff0c\u5979\u5728\u5e72\u751a\u4e48\u5462\uff1f\u201d \u3000\u3000\u665a\u4e0a\uff0c\u5927\u5c4b\u91cc\u53c8\u662f\u56db\u58c1\u70b9\u8d77\u4e86\u6cb9\u706f\u548c\u677e\u660e\u3002\u5341\u51e0\u4e2a\u4e61\u6c11\u62ff\u8d77\u4e86\u9504\u5934\u94c1\u94f2\u6316\u5730\u3002\u72c4\u4e91\u4e5f\u6df7\u5728\u4eba\u7fa4\u4e2d\u6316\u6398\uff0c\u65e2\u4e0d\u7279\u522b\u51fa\u529b\uff0c\u4e5f\u4e0d\u5077\u61d2\uff0c\u8981\u65c1\u4eba\u8d8a\u5c11\u7559\u610f\u5230\u4ed6\u8d8a\u597d\u3002\u4ed6\u5934\u53d1\u84ec\u677e\uff0c\u4e0d\u5243\u80e1\u5b50\uff0c\u5927\u534a\u5f20\u8138\u90fd\u7ed9\u6bdb\u53d1\u906e\u4f4f\u4e86\uff0c\u518d\u6d82\u4e0a\u4e00\u4e9b\u6ce5\u7070\uff0c\u5f53\u771f\u662f\u9762\u76ee\u5168\u975e\uff0c\u53c8\u60f3\u65e5\u95f4\u4e07\u9707\u5c71\u7b49\u4eba\u8ddf\u968f\u8fc7\u81ea\u5df1\uff0c\u522b\u7ed9\u4ed6\u4eec\u8ba4\u4e86\u51fa\u6765\uff0c\u4e8e\u662f\u5c06\u7f20\u5934\u7684\u767d\u5e03\u548c\u8170\u95f4\u7684\u9752\u5e03\u5e26\u5b50\u6389\u6362\u4e86\u4f7f\u7528\u3002\u8fd9\u4e00\u665a\uff0c\u4ed6\u4eec\u5728\u6316\u9760\u5317\u90a3\u4e00\u8fb9\uff0c\u90a3\u8001\u4e5e\u4e10\u80cc\u8d1f\u7740\u53cc\u624b\uff0c\u5728\u5751\u8fb9\u8e31\u6765\u8e31\u53bb\u3002\u5f53\u7136\uff0c\u4ed6\u73b0\u5728\u5b8c\u5168\u4e0d\u50cf\u4e5e\u4e10\u4e86\uff0c\u8863\u9970\u5bcc\u4e3d\uff0c\u5de6\u624b\u4e0a\u6234\u7740\u4e2a\u78a7\u7389\u6212\u6307\uff0c\u8170\u5e26\u4e0a\u6302\u4e86\u597d\u5927\u7684\u4e00\u5757\u6c49\u7389\u3002 \u3000\u3000\u7a81\u7136\u4e4b\u95f4\uff0c\u72c4\u4e91\u542c\u5230\u5c4b\u5916\u6709\u4eba\u6084\u6084\u63a9\u6765\uff0c\u4e1c\u5357\u897f\u5317\uff0c\u56db\u9762\u90fd\u6709\u4eba\u3002\u8fd9\u4e9b\u4eba\u79bb\u5f97\u8fd8\u8fdc\uff0c\u90a3\u8001\u4e10\u663e\u7136\u5e76\u672a\u77e5\u89c9\u3002\u72c4\u4e91\u4fa7\u8fc7\u8eab\u5b50\uff0c\u659c\u773c\u770b\u90a3\u8001\u4e10\uff0c\u53ea\u542c\u5f97\u811a\u6b65\u58f0\u6162\u6162\u8fd1\u4e86\uff0c\u4e94\u4e2a\uff0c\u516d\u4e2a\u2026\u2026\u4e03\u4e2a\u2026\u2026\u516b\u4e2a\uff0c\u662f\u4e86\uff0c\u4fbf\u662f\u4e07\u9707\u5c71\u548c\u4ed6\u7684\u4e03\u4e2a\u5f1f\u5b50\u3002\u4f46\u90a3\u8001\u4e10\u8fd8\u662f\u6ca1\u53d1\u89c9\u3002\u72c4\u4e91\u65e9\u5df2\u542c\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u90a3\u516b\u4e2a\u4eba\u4fbf\u5982\u8fd1\u5728\u773c\u524d\uff0c\u53ef\u662f\u8001\u4e10\u5374\u5982\u8033\u6735\u804b\u4e86\u4e00\u822c\u3002 \u3000\u3000\u4e94\u5e74\u4e4b\u524d\uff0c\u72c4\u4e91\u5bf9\u90a3\u8001\u4e10\u656c\u82e5\u795e\u660e\u3002\u4ed6\u53ea\u8ddf\u8001\u4e10\u5b66\u4e86\u4e09\u62db\u5251\u6cd5\uff0c\u4fbf\u5c06\u4e07\u95e8\u516b\u5f1f\u5b50\u6253\u5f97\u4e00\u8d25\u6d82\u5730\uff0c\u5168\u65e0\u62db\u67b6\u7684\u4f59\u5730\u3002 \u3000\u3000\u201c\u4f46\u600e\u4e48\u4ed6\u7684\u6b66\u529f\u53d8\u5f97\u8fd9\u6837\u5dee\u4e86\uff0c\u96be\u9053\u4e0d\u662f\u4ed6\u4e48\uff1f\u662f\u8ba4\u9519\u4eba\u4e86\u4e48\uff1f\u4e0d\uff0c\u51b3\u4e0d\u4f1a\u8ba4\u9519\u7684\u3002\u201d\u72c4\u4e91\u5374\u6ca1\u60f3\u5230\u662f\u81ea\u5df1\u7684\u6b66\u529f\u8fdb\u6b65\u5230\u4e86\u6781\u9ad8\u5883\u754c\uff0c\u4e8e\u4ed6\u662f\u6e05\u6670\u53ef\u95fb\u7684\u58f0\u97f3\uff0c\u5728\u65c1\u4eba\u8033\u4e2d\u5374\u662f\u5168\u65e0\u58f0\u606f\u3002 \u3000\u3000\u516b\u4e2a\u4eba\u8d8a\u6765\u8d8a\u8fd1\u3002\u72c4\u4e91\u5f88\u662f\u5947\u602a\uff1a\u201c\u8fd9\u516b\u4eba\u771f\u662f\u597d\u7b11\uff0c\u8c01\u8fd8\u542c\u4e0d\u5230\u4f60\u4eec\u5077\u5077\u63a9\u6765\uff0c\u8fd8\u662f\u8fd9\u4e48\u8e51\u624b\u8e51\u811a\uff0c\u9b3c\u9b3c\u795f\u795f\uff1f\u201d\u90a3\u516b\u4eba\u53c8\u8d70\u8fd1\u4e86\u5341\u4f59\u4e08\uff0c\u7a81\u7136\u95f4\uff0c\u90a3\u8001\u4e10\u8eab\u5b50\u5fae\u5fae\u4e00\u98a4\uff0c\u4fa7\u8fc7\u4e86\u8033\u6735\uff0c\u503e\u542c\u52a8\u9759\u3002\u72c4\u4e91\u5fc3\u60f3\uff1a\u201c\u4ed6\u542c\u89c1\u4e86\uff1f\u4ed6\u662f\u804b\u7684\u4e48\uff1f\u201d\u5176\u5b9e\uff0c\u8fd9\u516b\u4eba\u76f8\u8ddd\u5c1a\u8fdc\uff0c\u82e5\u662f\u6362\u4f5c\u4e00\u4e24\u5e74\u524d\u7684\u72c4\u4e91\uff0c\u4ed6\u4e0d\u4f1a\u542c\u5230\u811a\u6b65\u58f0\u7684\uff0c\u518d\u8d70\u8fd1\u4e9b\uff0c\u4e5f\u8fd8\u662f\u542c\u4e0d\u5230\u7684\u3002 \u3000\u3000\u90a3\u516b\u4e2a\u4eba\u66f4\u52a0\u8fd1\u4e86\uff0c\u8d70\u51e0\u6b65\uff0c\u505c\u4e00\u505c\uff0c\u663e\u7136\u662f\u9632\u5c4b\u4e2d\u4eba\u53d1\u89c1\u3002\u53ef\u662f\u90a3\u8001\u4e10\u5df2\u7ecf\u53d1\u89c9\u4e86\u3002\u4ed6\u8f6c\u8fc7\u8eab\u6765\uff0c\u62ff\u8d77\u501a\u5728\u58c1\u89d2\u7684\u4e00\u6839\u62d0\u6756\uff0c\u90a3\u662f\u4e00\u6839\u7c97\u5927\u7684\u9f99\u5934\u6728\u62d0\u3002 \u3000\u3000\u7a81\u7136\u4e4b\u95f4\uff0c\u90a3\u516b\u4eba\u540c\u65f6\u5feb\u6b65\u62a2\u524d\uff0c\u56db\u9762\u5408\u56f4\u3002\u7830\u7684\u4e00\u58f0\u54cd\uff0c\u5927\u95e8\u8e22\u5f00\uff0c\u4e07\u572d\u5f53\u5148\u62a2\u5165\uff0c\u8ddf\u7740\u6c88\u57ce\u3001\u535c\u57a3\u8ddf\u4e86\u8fdb\u6765\u3002\u4e03\u4eba\u5404\u633a\u957f\u5251\uff0c\u5c06\u90a3\u8001\u4e10\u56e2\u56e2\u56f4\u4f4f\u3002 \u3000\u3000\u90a3\u8001\u4e10\u54c8\u54c8\u5927\u7b11\uff0c\u9053\uff1a\u201c\u5f88\u597d\uff0c\u54e5\u513f\u4eec\u90fd\u6765\u4e86\uff01\u4e07\u5e08\u54e5\uff0c\u600e\u4e48\u4e0d\u8bf7\u8fdb\u6765\uff1f\u201d \u3000\u3000\u95e8\u5916\u4e00\u4eba\u7eb5\u58f0\u957f\u7b11\uff0c\u7f13\u6b65\u8e0f\u5165\uff0c\u6b63\u662f\u4e94\u4e91\u624b\u4e07\u9707\u5c71\u3002\u4ed6\u548c\u90a3\u8001\u4e10\u9694\u5751\u800c\u7acb\uff0c\u4e24\u4eba\u76f8\u4e92\u6253\u91cf\u3002\u8fc7\u4e86\u534a\u664c\uff0c\u4e07\u9707\u5c71\u7b11\u9053\uff1a\u201c\u8a00\u5e08\u5f1f\uff0c\u51e0\u5e74\u4e0d\u89c1\uff0c\u4f60\u53d1\u4e86\u5927\u8d22\u5566\u3002\u201d \u3000\u3000\u8fd9\u4e09\u53e5\u8bdd\u94bb\u5165\u72c4\u4e91\u8033\u4e2d\uff0c\u4ed6\u5934\u8111\u4e2d\u767b\u65f6\u4e00\u9635\u6df7\u4e71\uff1a\u201c\u751a\u4e48\uff1f\u8fd9\u8001\u4e10\u4fbf\u662f\u2026\u2026\u4fbf\u662f\u4e8c\u5e08\u4f2f\u2026\u2026\u4e8c\u5e08\u4f2f\u2026\u2026\u8a00\u8fbe\u5e73\uff1f\u201d \u3000\u3000\u53ea\u542c\u90a3\u8001\u4e10\u9053\uff1a\u201c\u5e08\u54e5\uff0c\u6211\u53d1\u4e86\u70b9\u5c0f\u8d22\u3002\u4f60\u8fd9\u51e0\u5e74\u4e70\u5356\u5f88\u597d\u554a\u3002\u201d\u4e07\u9707\u5c71\u9053\uff1a\u201c\u6258\u798f\uff01\u5582\uff0c\u5c0f\u5b50\u4eec\uff0c\u600e\u4e48\u4e0d\u5411\u5e08\u53d4\u78d5\u5934\uff1f\u201d \u3000\u3000\u9c81\u5764\u7b49\u4e00\u9f50\u8dea\u4e0b\uff0c\u9f50\u58f0\u8bf4\u9053\uff1a\u201c\u5f1f\u5b50\u53e9\u89c1\u8a00\u5e08\u53d4\u3002\u201d\u90a3\u8001\u4e10\u7b11\u9053\uff1a\u201c\u7f62\u4e86\uff0c\u7f62\u4e86\uff01\u624b\u91cc\u62ff\u770b\u5200\u5251\uff0c\u78d5\u5934\u53ef\u4e0d\u5927\u65b9\u4fbf\uff0c\u8fd8\u662f\u514d\u4e86\u7f62\u3002\u201d \u3000\u3000\u72c4\u4e91\u5fc3\u9053\uff1a\u201c\u8fd9\u4eba\u679c\u7136\u662f\u8a00\u5e08\u4f2f\u3002\u4ed6\u2026\u2026\u4ed6\uff1f\u201d \u3000\u3000\u4e07\u9707\u5c71\u9053\uff1a\u201c\u5e08\u5f1f\uff0c\u4f60\u5728\u8fd9\u513f\u5f00\u7164\u77ff\u5417\uff1f\u600e\u4e48\u6316\u4e86\u8fd9\u6837\u5927\u7684\u4e00\u4e2a\u5751\uff1f\u201d\u8a00\u8fbe\u5e73\u563f\u563f\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5e08\u5144\u731c\u9519\u4e86\u3002\u5c0f\u5f1f\u4ec7\u4eba\u592a\u591a\uff0c\u5728\u8fd9\u91cc\u907f\u96be\uff0c\u6316\u4e2a\u6df1\u5751\u662f\u4e00\u4f5c\u4e8c\u7528\u3002\u4ec7\u4eba\u7ed9\u5c0f\u5f1f\u6740\u4e86\uff0c\u5c31\u968f\u624b\u63a9\u57cb\uff0c\u4e0d\u7528\u6316\u5751\u3002\u5018\u82e5\u5c0f\u5f1f\u7ed9\u4eba\u5bb6\u6740\u4e86\uff0c\u8fd9\u571f\u5751\u4fbf\u662f\u5c0f\u5f1f\u7684\u846c\u8eab\u4e4b\u5730\u3002\u201d\u4e07\u9707\u5c71\u7b11\u9053\uff1a\u201c\u5999\u6781\uff0c\u5e08\u5f1f\u771f\u60f3\u5f97\u5468\u5230\u3002\u5e08\u5f1f\u8eab\u5b50\u4e5f\u4e0d\u80a5\u5927\uff0c\u6211\u770b\u8fd9\u5751\u591f\u6df1\u7684\u4e86\uff0c\u4e0d\u7528\u518d\u6316\u5566\u3002\u201d\u8a00\u8fbe\u5e73\u5fae\u7b11\u9053\uff1a\u201c\u846c\u4e00\u4e2a\u4eba\u662f\u7ef0\u7ef0\u6709\u4f59\u4e86\uff0c\u846c\u516b\u4e2a\u4eba\u6050\u6015\u8fd8\u4e0d\u591f\u3002\u201d \u3000\u3000\u72c4\u4e91\u542c\u4ed6\u4e8c\u4eba\u4e00\u4e0a\u6765\u4fbf\u662f\u5507\u67aa\u820c\u5251\uff0c\u9488\u950b\u76f8\u5bf9\uff0c\u4e0d\u7981\u60f3\u8d77\u4e01\u5178\u7684\u8bf4\u8bdd\uff0c\u5bfb\u601d\uff1a\u201c\u4ed6\u4eec\u5e08\u5144\u5f1f\u5408\u529b\u6740\u4e86\u4ed6\u4eec\u7684\u5e08\u7236\u3002\u53d7\u4e1a\u6069\u5e08\u90fd\u8981\u6740\uff0c\u76f8\u4e92\u4e4b\u95f4\u53c8\u6709\u751a\u4e48\u60c5\u8c0a\uff1f\u542c\u4e01\u5927\u54e5\u8bf4\uff0c\u4ed6\u4eec\u5e08\u5144\u5f1f\u593a\u5230\u4e86\u8fde\u57ce\u5251\u8c31\uff0c\u5374\u6ca1\u5f97\u5230\u5251\u8bc0\u3002\u90a3\u5251\u8bc0\u5c3d\u662f\u4e00\u4e9b\u6570\u5b57\uff0c\u751a\u4e48\u7b2c\u4e00\u5b57\u662f\u2018\u56db\u2019\uff0c\u7b2c\u4e8c\u5b57\u662f\u2018\u4e94\u5341\u2019\uff0c\u7b2c\u4e09\u5b57\u662f\u2018\u4e09\u5341\u4e09\u2019\uff0c\u7b2c\u56db\u5b57\u662f\u2018\u4e94\u5341\u4e09\u2019\uff0c\u4e01\u5927\u54e5\u4e00\u76f4\u5230\u6b7b\uff0c\u4e5f\u6ca1\u8bf4\u5b8c\u3002\u5251\u8c31\u4e0d\u662f\u65e9\u5728\u4ed6\u4eec\u624b\u4e2d\u4e48\uff1f\u600e\u5730\u53c8\u5230\u8fd9\u91cc\u6765\u627e\u5bfb\uff1f\u201d \u3000\u3000\u4e07\u9707\u5c71\u9053\uff1a\u201c\u597d\u5e08\u5f1f\uff0c\u54b1\u4eec\u540c\u95e8\u8fd9\u8bb8\u591a\u5e74\uff0c\u6211\u7684\u5fc3\u601d\uff0c\u4f60\u5168\u660e\u767d\uff0c\u4f60\u7684\u809a\u80a0\uff0c\u6211\u4e5f\u65e9\u770b\u7a7f\u4e86\uff0c\u5927\u5bb6\u8fd8\u7528\u5f97\u7740\u7ed5\u5708\u5b50\u8bf4\u8bdd\u4e48\uff1f\u62ff\u6765\uff01\u201d\u8bf4\u4e86\u8fd9\u201c\u62ff\u6765\u201d\u4e24\u5b57\uff0c\u4fbf\u5373\u4f38\u51fa\u4e86\u53f3\u624b\u3002 \u3000\u3000\u8a00\u8fbe\u5e73\u6447\u4e86\u6447\u5934\uff0c\u9053\uff1a\u201c\u8fd8\u6ca1\u627e\u5230\u3002\u621a\u8001\u4e09\u7684\u5fc3\u673a\uff0c\u54b1\u54e5\u513f\u4fe9\u90fd\u4e0d\u662f\u5bf9\u624b\u3002\u6211\u53ef\u4e07\u4e07\u731c\u4e0d\u5230\u4ed6\u5c06\u5251\u8c31\u85cf\u5728\u54ea\u91cc\u3002\u201d \u3000\u3000\u72c4\u4e91\u53c8\u662f\u4e00\u51db\uff1a\u201c\u96be\u9053\u4ed6\u4eec\u5e08\u5144\u5f1f\u4e8c\u4eba\u5408\u529b\u62a2\u5230\u5251\u8c31\uff0c\u5374\u53c8\u7ed9\u6211\u5e08\u7236\u62ff\u4e86\u53bb\uff1f\u53ef\u662f\u8fd9\u4e9b\u5e74\u6765\uff0c\u600e\u5730\u53c8\u4e1d\u6beb\u6ca1\u52a8\u9759\uff1f\u662f\u4e86\uff0c\u5b9a\u662f\u6211\u5e08\u7236\u4e0b\u624b\u6781\u662f\u5de7\u5999\uff0c\u4ed6\u4eec\u4e00\u76f4\u6ca1\u89c9\u5bdf\u51fa\u6765\u3002\u5e08\u7236\u65e2\u4e0d\u5728\u6b64\u5904\uff0c\u5251\u8c31\u81ea\u4f1a\u968f\u8eab\u643a\u5e26\uff0c\u600e\u4f1a\u57cb\u85cf\u5728\u8fd9\u5c4b\u4e2d\uff1f\u4ed6\u4eec\u62da\u547d\u5230\u8fd9\u91cc\u6765\u7ffb\u5bfb\uff0c\u90a3\u4e0d\u662f\u592a\u50bb\u4e86\u5417\uff1f\u201d\u53ef\u662f\uff0c\u4ed6\u77e5\u9053\u4e07\u9707\u5c71\u548c\u8a00\u8fbe\u5e73\u51b3\u4e0d\u662f\u50bb\u74dc\uff0c\u6bd4\u81ea\u5df1\u806a\u660e\u5341\u500d\u4e5f\u8fd8\u4e0d\u6b62\u3002\u8fd9\u4e2d\u95f4\u5230\u5e95\u9690\u85cf\u7740\u751a\u4e48\u9634\u8c0b\u548c\u673a\u5173\uff1f \u3000\u3000\u4e07\u9707\u5c71\u54c8\u54c8\u5927\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u5e08\u5f1f\uff0c\u4f60\u8fd8\u88c5\u751a\u4e48\u5047\uff1f\u4eba\u5bb6\u8bf4\u54b1\u4eec\u4e09\u5e08\u5f1f\u662f\u2018\u94c1\u9501\u6a2a\u6c5f\u2019\uff0c\u624b\u6bb5\u5389\u5bb3\u3002\u6211\u8bf4\u5462\uff0c\u8fd8\u662f\u4f60\u4e8c\u5e08\u5f1f\u5389\u5bb3\u3002\u62ff\u6765\uff01\u201d\u8bf4\u7740\u53f3\u624b\u53c8\u5411\u524d\u4e00\u4f38\u3002 \u3000\u3000\u8a00\u8fbe\u5e73\u62cd\u62cd\u8863\u888b\uff0c\u8bf4\u9053\uff1a\u201c\u54b1\u54e5\u513f\u4fe9\u591a\u5e74\u8001\u5144\u5f1f\uff0c\u8fd8\u80fd\u5206\u751a\u4e48\u5f7c\u6b64\uff1f\u5e08\u54e5\uff0c\u8fd9\u73a9\u610f\u513f\u82e5\u662f\u5144\u5f1f\u5f97\u5230\u4e86\uff0c\u6211\u4e00\u4eba\u51b3\u8ba1\u5bf9\u4ed8\u4e0d\u4e86\uff0c\u975e\u5f97\u4f60\u6765\u4e3b\u6301\u5927\u5c40\u4e0d\u53ef\uff0c\u505a\u5144\u5f1f\u7684\u53ea\u80fd\u5728\u65c1\u534f\u52a9\uff0c\u5206\u4e00\u4e9b\u597d\u5904\u3002\u4f46\u8981\u662f\u5e08\u5144\u5f97\u5230\u4e86\u5462\uff0c\u563f\u563f\uff0c\u5e08\u5144\u95e8\u4e0b\u5f1f\u5b50\u867d\u591a\uff0c\u529f\u592b\u90fd\u8fd8\u5ae9\u7740\u70b9\u513f\uff0c\u53ea\u6015\u4e5f\u987b\u8ba9\u505a\u5144\u5f1f\u7684\u51d1\u5408\u51d1\u5408\uff0c\u52a0\u4e0a\u4e00\u628a\u624b\u3002\u201d \u3000\u3000\u4e07\u9707\u5c71\u76b1\u7709\u9053\uff1a\u201c\u4f60\u5728\u90a3\u8fb9\u5c71\u6d1e\u91cc\uff0c\u62ff\u5230\u4e86\u751a\u4e48\uff1f\u201d\u8a00\u8fbe\u5e73\u5947\u9053\uff1a\u201c\u751a\u4e48\u5c71\u6d1e\uff1f\u8fd9\u9644\u8fd1\u6709\u4e2a\u5c71\u6d1e\u4e48\uff1f\u201d\u4e07\u9707\u5c71\u9053\uff1a\u201c\u5e08\u5f1f\uff0c\u4f60\u6211\u5e74\u7eaa\u90fd\u8fd9\u4e48\u4e00\u5927\u628a\uff0c\u4f55\u5fc5\u5230\u5934\u6765\u518d\u4f24\u548c\u6c14\uff1f\u8bf7\u4f60\u62ff\u51fa\u6765\uff0c\u5927\u5bb6\u4e00\u540c\u53c2\u8be6\u3002\u4eca\u540e\u6709\u798f\u540c\u4eab\u3001\u6709\u96be\u540c\u5f53\u5982\u4f55\uff1f\u201d\u8a00\u8fbe\u5e73\u9053\uff1a\u201c\u8fd9\u53ef\u5947\u4e86\uff0c\u4f60\u600e\u4e48\u4e00\u53e3\u54ac\u5b9a\u662f\u6211\u62ff\u5230\u4e86\uff1f\u8981\u662f\u6211\u5df2\u5f97\u624b\uff0c\u8fd8\u5728\u8fd9\u91cc\u6316\u6316\u6398\u6398\u7684\u5e72\u751a\u4e48\uff1f\u201d\u4e07\u9707\u5c71\u9053\uff1a\u201c\u4f60\u9b3c\u8ba1\u591a\u7aef\uff0c\u8c01\u77e5\u9053\u4f60\u5e72\u751a\u4e48\uff1f\u201d\u8a00\u8fbe\u5e73\u9053\uff1a\u201c\u4e09\u5e08\u5f1f\u7684\u4e1c\u897f\uff0c\u54ea\u6709\u8fd9\u4e48\u5bb9\u6613\u627e\u5230\u7684\u3002\u6211\u77a7\u554a\uff0c\u4e5f\u4e0d\u4f1a\u662f\u5728\u8fd9\u5c4b\u4e2d\u3002\u518d\u6398\u5f97\u4e09\u5929\uff0c\u5018\u82e5\u4ecd\u7136\u6beb\u65e0\u7ed3\u679c\uff0c\u6211\u4e5f\u4e0d\u60f3\u518d\u6405\u4e0b\u53bb\u4e86\u3002\u201d\u4e07\u9707\u5c71\u51b7\u7b11\u9053\uff1a\u201c\u54fc\uff01\u6211\u77a7\u4f60\u8fd8\u662f\u518d\u6398\u5341\u5929\u534a\u6708\u7684\u597d\uff0c\u88c5\u5f97\u50cf\u4e9b\u3002\u201d \u3000\u3000\u8a00\u8fbe\u5e73\u52c3\u7136\u53d8\u8272\uff0c\u4fbf\u8981\u7ffb\u8138\uff0c\u4f46\u4e00\u8f6c\u5ff5\u95f4\uff0c\u5fcd\u4f4f\u4e86\u6012\u6c14\uff0c\u9053\uff1a\u201c\u4f60\u8981\u600e\u6837\u624d\u4fe1\uff1f\u201d\u653e\u4e0b\u62d0\u6756\uff0c\u89e3\u5f00\u8863\u6263\uff0c\u9664\u4e0b\u957f\u888d\uff0c\u6293\u4f4f\u888d\u5b50\u4e0b\u6446\uff0c\u5012\u8f6c\u6765\u6296\u4e86\u4e24\u6296\uff0c\u53ee\u53ee\u5f53\u5f53\u7684\u8dcc\u51fa\u51e0\u4e24\u788e\u94f6\u5b50\u548c\u4e00\u53ea\u9f3b\u70df\u58f6\u6765\uff0c\u90fd\u6389\u5728\u5730\u4e0b\u3002 \u3000\u3000\u4e07\u9707\u5c71\u9053\uff1a\u201c\u4f60\u6709\u8fd9\u4e48\u8822\uff0c\u62ff\u5230\u4e86\u4e4b\u540e\u4f1a\u968f\u8eab\u6536\u85cf\uff1f\u5c31\u7b97\u662f\u85cf\u5728\u8eab\u8fb9\uff0c\u4e5f\u5fc5\u8d34\u8089\u6536\u7684\uff0c\u4e0d\u4f1a\u653e\u5728\u888d\u5b50\u888b\u91cc\u3002\u201d\u8a00\u8fbe\u5e73\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5e08\u5144\u65e2\u4fe1\u4e0d\u8fc7\uff0c\u90a3\u5c31\u6765\u641c\u641c\u7f62\u3002\u201d \u3000\u3000\u4e07\u9707\u5c71\u9053\uff1a\u201c\u5982\u6b64\u5f97\u7f6a\u4e86\u3002\u201d\u5411\u4e07\u572d\u548c\u6c88\u57ce\u4f7f\u4e2a\u773c\u8272\u3002\u4e24\u4eba\u70b9\u4e86\u70b9\u5934\uff0c\u8fd8\u5251\u5165\u9798\uff0c\u4e00\u5de6\u4e00\u53f3\uff0c\u8d70\u5230\u8a00\u8fbe\u5e73\u8eab\u8fb9\u3002\u4e07\u9707\u5c71\u5411\u535c\u57a3\u548c\u9c81\u5764\u53c8\u6a2a\u4e2a\u773c\u8272\uff0c\u4e24\u4eba\u6162\u6162\u7ed5\u5230\u8a00\u8fbe\u5e73\u8eab\u540e\uff0c\u624b\u4e2d\u7d27\u7d27\u7684\u6293\u4f4f\u4e86\u5251\u67c4\u3002 \u3000\u3000\u8a00\u8fbe\u5e73\u62cd\u62cd\u5185\u8863\u53e3\u888b\uff0c\u9053\uff1a\u201c\u8bf7\u641c\uff01\u201d\u4e07\u572d\u9053\uff1a\u201c\u5e08\u53d4\uff0c\u5f97\u7f6a\u4e86\uff01\u201d\u4f38\u624b\u53bb\u6478\u4ed6\u53e3\u888b\u3002 \u3000\u3000\u7a81\u7136\u4e4b\u95f4\uff0c\u4e07\u572d\u201c\u554a\u201d\u7684\u4e00\u58f0\u5c16\u53eb\uff0c\u6025\u5fd9\u7f29\u624b\u5012\u9000\uff0c\u706b\u5149\u4e0b\u53ea\u89c1\u624b\u80cc\u4e0a\u722c\u7740\u4e00\u53ea\u4e09\u5bf8\u6765\u957f\u7684\u5927\u874e\u5b50\u3002\u4ed6\u53cd\u624b\u5f80\u571f\u5751\u8fb9\u4e00\u51fb\uff0c\u62cd\u7684\u4e00\u58f0\uff0c\u5c06\u874e\u5b50\u6253\u5f97\u7a00\u70c2\uff0c\u4f46\u624b\u80cc\u5df2\u4e2d\u5267\u6bd2\uff0c\u767b\u65f6\u9ad8\u9ad8\u80bf\u8d77\u3002\u4ed6\u8981\u901e\u82f1\u96c4\uff0c\u4e0d\u80af\u547b\u541f\uff0c\u989d\u4e0a\u6c57\u73e0\u5374\u5df2\u5982\u9ec4\u8c46\u822c\u6e17\u4e86\u51fa\u6765\u3002 \u3000\u3000\u8a00\u8fbe\u5e73\u5931\u60ca\u9053\uff1a\u201c\u554a\u54df\uff0c\u4e07\u8d24\u4f84\uff0c\u4f60\u54ea\u91cc\u53bb\u6405\u4e86\u8fd9\u53ea\u6bd2\u866b\u6765\uff1f\u8fd9\u662f\u82b1\u6591\u6bd2\u874e\uff0c\u53ef\u5389\u5bb3\u5f97\u5f88\u54ea\u3002\u8fd9\u4e1c\u897f\u662f\u73a9\u4e0d\u5f97\u7684\u3002\u5e08\u54e5\uff0c\u5feb\uff0c\u5feb\uff0c\u4f60\u6709\u89e3\u836f\u6ca1\u6709\uff1f\u53ea\u8981\u6551\u8fdf\u4e86\u4e00\u6b65\uff0c\u90a3\u5c31\u4e0d\u5f97\u4e86\uff0c\u4e86\u4e0d\u5f97\uff01\u4e56\u4e56\u6211\u7684\u5988\uff01\u201d \u3000\u3000\u53ea\u89c1\u4e07\u572d\u7684\u624b\u80cc\u7531\u7ea2\u53d8\u7d2b\uff0c\u7531\u7d2b\u53d8\u9ed1\uff0c\u4e00\u9053\u7ea2\u7ebf\uff0c\u7f13\u7f13\u5411\u624b\u81c2\u5347\u4e0a\u53bb\u3002\u4e07\u9707\u5c71\u77e5\u9053\u4e2d\u4e86\u8a00\u8fbe\u5e73\u7684\u9677\u9631\uff0c\u8bf4\u4e0d\u5f97\uff0c\u53ea\u597d\u5fcd\u4e00\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u5e08\u5f1f\uff0c\u505a\u54e5\u54e5\u7684\u670d\u4e86\u4f60\u5566\u3002\u6211\u8fd9\u5c31\u8ba4\u8f93\u3002 \u3000\u3000\u4f60\u62ff\u89e3\u836f\u6765\uff0c\u6211\u4eec\u62cd\u624b\u8d70\u8def\uff0c\u4e0d\u518d\u6765\u5411\u4f60\u7f57\u55e6\u4e86\u3002\u201d \u3000\u3000\u8a00\u8fbe\u5e73\u9053\uff1a\u201c\u8fd9\u89e3\u836f\u4e48\uff0c\u4ece\u524d\u6211\u5012\u4e5f\u6709\u8fc7\u7684\uff0c\u53ea\u662f\u5e74\u6df1\u65e5\u4e45\uff0c\u4e0d\u77e5\u4e22\u5728\u54ea\u91cc\u4e86\uff0c\u8fc7\u51e0\u5929\u6211\u6162\u6162\u8ddf\u4f60\u627e\u627e\uff0c\u6216\u8bb8\u80fd\u627e\u5f97\u5230\u3002\u8981\u4e0d\u7136\uff0c\u6211\u5230\u5927\u540d\u5e9c\u53bb\uff0c\u627e\u5230\u4e86\u836f\u65b9\uff0c\u53e6\u5916\u7ed9\u4f60\u914d\u8fc7\uff0c\u90a3\u4e5f\u6210\u7684\u3002\u8c01\u6559\u54b1\u4eec\u5e08\u5144\u5f1f\u60c5\u8c0a\u6df1\u957f\u5462\u3002\u201d \u3000\u3000\u4e07\u9707\u5c71\u4e00\u542c\uff0c\u5f53\u771f\u8981\u6c14\u70b8\u4e86\u80f8\u819b\uff0c\u8fd9\u79cd\u6bd2\u86c7\u3001\u6bd2\u874e\u4e4b\u4f24\uff0c\u4e00\u65f6\u4e09\u523b\u4fbf\u80fd\u8981\u4e86\u4eba\u6027\u547d\uff0c\u53ea\u8981\u8fd9\u9053\u7ea2\u7ebf\u4e00\u901a\u5230\u80f8\u53e3\uff0c\u7acb\u65f6\u4fbf\u5373\u6c14\u7edd\u6bd9\u547d\uff0c\u8bf4\u751a\u4e48\u201c\u8fc7\u51e0\u5929\u6162\u6162\u627e\u627e\u201d\uff0c\u6b64\u5904\u5230\u6cb3\u5317\u5927\u540d\u5e9c\u5343\u91cc\u8fe2\u8fe2\uff0c\u53c8\u8bf4\u751a\u4e48\u627e\u836f\u65b9\u914d\u836f\uff0c\u5c45\u7136\u8fd8\u4e8f\u4ed6\u6709\u8fd9\u7b49\u539a\u989c\u65e0\u803b\uff0c\u8fd8\u8bf4\u201c\u8c01\u6559\u54b1\u4eec\u5e08\u5144\u5f1f\u60c5\u8c0a\u6df1\u957f\u201d\uff0c\u4f46\u773c\u89c1\u7231\u5b50\u547d\u5728\u9877\u523b\uff0c\u53ea\u597d\u5f3a\u5fcd\u6012\u6c14\uff0c\u5fc3\u60f3\u541b\u5b50\u62a5\u4ec7\uff0c\u5341\u5e74\u672a\u665a\uff0c\u4fbf\u9053\uff1a\u201c\u5e08\u5f1f\uff0c\u8fd9\u4e2a\u7b4b\u6597\uff0c\u6211\u662f\u683d\u5b9a\u4e86\u3002\u4f60\u8981\u6211\u600e\u4e48\u7740\uff0c\u4fbf\u5212\u4e0b\u9053\u513f\u6765\u7f62\u3002\u201d \u3000\u3000\u8a00\u8fbe\u5e73\u6162\u6761\u65af\u7406\u7684\u7a7f\u4e0a\u957f\u888d\uff0c\u6263\u4e0a\u8863\u6263\uff0c\u8bf4\u9053\uff1a\u201c\u5e08\u54e5\uff0c\u6211\u6709\u751a\u4e48\u9053\u513f\u597d\u5212\u7ed9\u4f60\u7684\uff1f\u4f60\u7231\u600e\u4e48\u4fbf\u600e\u4e48\u7f62\u3002\u201d\u4e07\u9707\u5c71\u5fc3\u9053\uff1a\u201c\u4eca\u65e5\u4e14\u8ba9\u4f60\u626f\u8db3\u987a\u98ce\u65d7\uff0c\u65e5\u540e\u8981\u4f60\u77e5\u9053\u6211\u5389\u5bb3\u3002\u201d\u8bf4\u9053\uff1a\u201c\u597d\u7f62\uff0c\u59d3\u4e07\u7684\u81ea\u4eca\u800c\u540e\uff0c\u6c38\u4e0d\u518d\u548c\u4f60\u76f8\u89c1\u3002\u518d\u5411\u4f60\u7f57\u55e6\u751a\u4e48\uff0c\u6211\u59d3\u4e07\u7684\u4e0d\u662f\u4eba\u3002\u201d\u8a00\u8fbe\u5e73\u9053\uff1a\u201c\u8fd9\u4e2a\u53ef\u4e0d\u6562\u5f53\u3002\u505a\u5144\u5f1f\u7684\u53ea\u6c42\u5e08\u54e5\u8bf4\u4e00\u53e5\uff0c\u90a3\u2018\u8fde\u57ce\u5251\u8c31\u2019\uff0c\u8be5\u5f53\u5f52\u8a00\u8fbe\u5e73\u6240\u6709\u3002\u5018\u82e5\u5144\u5f1f\u4fa5\u5e78\u627e\u5230\uff0c\u81ea\u7136\u65e0\u8bdd\u53ef\u8bf4\uff1b\u5c31\u7b97\u843d\u5165\u4e86\u5e08\u54e5\u624b\u91cc\uff0c\u4e5f\u5f53\u8ba9\u7ed9\u5144\u5f1f\u3002\u201d \u3000\u3000\u4e07\u572d\u6bd2\u6c14\u6e10\u6e10\u4e0a\u884c\uff0c\u53ea\u89c9\u4e00\u9635\u9635\u6655\u7729\uff0c\u8eab\u5b50\u4e0d\u7531\u81ea\u4e3b\u7684\u6447\u6447\u6446\u6446\u3002\u9c81\u5764\u53eb\u9053\uff1a\u201c\u5e08\u5f1f\uff0c\u5e08\u5f1f\uff01\u201d\u4f38\u624b\u6276\u4f4f\uff0c\u6495\u7834\u4ed6\u8863\u8896\uff0c\u53ea\u89c1\u90a3\u9053\u7ea2\u7ebf\u5df2\u8fc7\u814b\u4e0b\u3002\u4ed6\u8f6c\u5934\u5411\u7740\u4e07\u9707\u5c71\u53eb\u9053\uff1a\u201c\u5e08\u7236\uff0c\u4eca\u65e5\u751a\u4e48\u90fd\u7b54\u5141\u7f62\uff01\u201d \u3000\u3000\u4e07\u9707\u5c71\u9053\uff1a\u201c\u597d\uff0c\u8fd9\u8fde\u57ce\u5251\u8c31\uff0c\u5c31\u7b97\u662f\u5e08\u5f1f\u4f60\u7684\u4e86\uff0c\u606d\u559c\uff01 \u3000\u3000\u606d\u559c\uff01\u201d\u8fd9\u4e24\u53e5\u201c\u606d\u559c\u201d\uff0c\u5374\u8bf4\u5f97\u54ac\u7259\u5207\u9f7f\uff0c\u6ee1\u8154\u6028\u6bd2\u3002 \u3000\u3000\u8a00\u8fbe\u5e73\u9053\uff1a\u201c\u65e2\u662f\u5982\u6b64\uff0c\u8ba9\u6211\u8fdb\u5c4b\u53bb\u627e\u627e\uff0c\u8bf4\u4e0d\u5b9a\u80fd\u5bfb\u5f97\u5230\u751a\u4e48\u89e3\u836f\uff0c\u90a3\u8981\u77a7\u4e07\u8d24\u4f84\u662f\u4e0d\u662f\u6709\u8fd9\u95e8\u9020\u5316\u4e86\u3002\u201d\u8bf4\u5b8c\u6162\u541e\u541e\u7684\u8f6c\u8eab\u5165\u5185\u3002\u4e07\u9707\u5c71\u4f7f\u4e2a\u773c\u8272\uff0c\u9c81\u5764\u548c\u535c\u57a3\u8ddf\u4e86\u8fdb\u53bb\u3002 \u3000\u3000\u8fc7\u4e86\u597d\u4e00\u4f1a\uff0c\u4e09\u4eba\u90fd\u6ca1\u51fa\u6765\uff0c\u4e5f\u6ca1\u542c\u5230\u751a\u4e48\u58f0\u606f\uff0c\u53ea\u89c1\u4e07\u572d\u795e\u667a\u660f\u8ff7\uff0c\u7531\u6c88\u5747\u6276\u7740\uff0c\u5df2\u662f\u4e0d\u80fd\u52a8\u5f39\u3002\u4e07\u9707\u5c71\u5fc3\u4e2d\u7126\u6025\uff0c\u5411\u51af\u5766\u9053\uff1a\u201c\u4f60\u8fdb\u53bb\u77a7\u77a7\u3002\u201d\u51af\u5766\u9053\uff1a\u201c\u662f\uff01\u201d\u6b63\u8981\u8fdb\u53bb\uff0c\u53ea\u89c1\u8a00\u8fbe\u5e73\u8d70\u4e86\u51fa\u6765\uff0c\u6ee1\u9762\u6625\u98ce\u7684\u9053\uff1a\u201c\u8fd8\u597d\uff01\u8fd8\u597d\uff01\u8fd9\u4e0d\u662f\u627e\u5230\u4e86\u5417\uff1f\u201d\u624b\u4e2d\u9ad8\u4e3e\u7740\u4e00\u4e2a\u5c0f\u74f7\u74f6\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u89e3\u836f\uff0c\u884c\uff0c\u6cbb\u874e\u6bd2\u518d\u597d\u4e0d\u8fc7\u4e86\u3002\u4e07\u8d24\u4f84\uff0c\u4f60\u597d\u5927\u7684\u547d\u554a\u3002\u4ee5\u540e\u8fd9\u79cd\u6bd2\u7269\u53ef\u73a9\u4e0d\u5f97\u4e86\u3002\u201d\u8bf4\u7740\u8d70\u5230\u4e07\u572d\u8eab\u8fb9\uff0c\u62d4\u5f00\u74f6\u585e\uff0c\u5728\u4e07\u572d\u624b\u80cc\u4f24\u53e3\u4e0a\u6d12\u4e86\u4e9b\u9ed1\u8272\u7684\u836f\u672b\u3002 \u3000\u3000\u8fd9\u89e3\u836f\u5012\u4e5f\u771f\u7075\uff0c\u8fc7\u4e0d\u591a\u65f6\uff0c\u4fbf\u89c1\u4f24\u53e3\u4e2d\u6162\u6162\u6e17\u51fa\u9ed1\u8840\uff0c\u4e00\u6ef4\u6ef4\u7684\u6389\u5728\u5730\u4e0b\uff0c\u9ed1\u8840\u8d8a\u6e17\u8d8a\u591a\uff0c\u4e07\u572d\u624b\u81c2\u4e0a\u90a3\u9053\u7ea2\u7ebf\u4fbf\u7f13\u7f13\u5411\u4e0b\uff0c\u56de\u5230\u81c2\u5f2f\uff0c\u53c8\u56de\u5230\u624b\u8155\u3002 \u3000\u3000\u4e07\u9707\u5c71\u5401\u4e86\u53e3\u6c14\uff0c\u5fc3\u4e2d\u53c8\u662f\u8f7b\u677e\uff0c\u53c8\u662f\u607c\u6068\uff0c\u513f\u5b50\u7684\u6027\u547d\u662f\u4fdd\u5168\u4e86\uff0c\u53ef\u662f\u8fd9\u4e00\u4ed7\u5927\u8d25\u4e8f\u8f93\uff0c\u8fd8\u6ca1\u52a8\u624b\u5374\u53d7\u5236\u4e8e\u4eba\u3002\u53c8\u8fc7\u4e86\u4e00\u4f1a\uff0c\u4e07\u572d\u7741\u5f00\u4e86\u773c\u775b\uff0c\u53eb\u4e86\u4e00\u58f0\uff1a\u201c\u7239\uff01\u201d \u3000\u3000\u8a00\u8fbe\u5e73\u5c06\u74f7\u74f6\u53e3\u585e\u4e0a\uff0c\u653e\u56de\u6000\u4e2d\uff0c\u62ff\u8fc7\u62d0\u6756\uff0c\u5728\u5730\u4e0b\u8f7b\u8f7b\u4e00\u987f\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u5c31\u884c\u5566\uff0c\u4e07\u8d24\u4f84\uff0c\u4f60\u4eca\u540e\u5b66\u4e86\u8fd9\u4e2a\u4e56\uff0c\u4f38\u624b\u5230\u4eba\u53e3\u888b\u91cc\u53bb\u638f\u6478\u751a\u4e48\uff0c\u5343\u4e07\u5f97\u5c0f\u5fc3\u624d\u662f\u3002\u201d \u3000\u3000\u4e07\u9707\u5c71\u5411\u6c88\u57ce\u9053\uff1a\u201c\u53eb\u4ed6\u4eec\u51fa\u6765\u3002\u201d\u6c88\u57ce\u5e94\u9053\uff1a\u201c\u662f\uff01\u201d\u8d70\u5230\u5385\u540e\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u9c81\u5e08\u54e5\u3001\u535c\u5e08\u54e5\uff0c\u5feb\u51fa\u6765\uff0c\u54b1\u4eec\u8d70\u4e86\u3002\u201d \u3000\u3000\u53ea\u542c\u5f97\u9c81\u535c\u4e8c\u4eba\u201c\u554a\uff0c\u554a\uff0c\u554a\u201d\u7684\u53eb\u4e86\u51e0\u4e0b\uff0c\u5374\u4e0d\u51fa\u6765\u3002\u5b59\u5747\u548c\u6c88\u57ce\u4e0d\u7b49\u5e08\u7236\u5429\u5490\uff0c\u5f84\u81ea\u51b2\u4e86\u8fdb\u53bb\uff0c\u968f\u5373\u5206\u522b\u6276\u4e86\u9c81\u5764\u3001\u535c\u57a3\u51fa\u6765\u3002\u4f46\u89c1\u4e24\u4eba\u8138\u65e0\u4eba\u8272\uff0c\u4e00\u65ad\u5de6\u817f\uff0c\u4e00\u6298\u53f3\u8db3\uff0c\u81ea\u662f\u9002\u624d\u906d\u4e86\u8a00\u8fbe\u5e73\u7684\u6bd2\u624b\u3002 \u3000\u3000\u4e07\u9707\u5c71\u5927\u6012\uff0c\u4ed6\u672c\u6765\u5c31\u6709\u610f\u7acb\u53d6\u8a00\u8fbe\u5e73\u7684\u6027\u547d\uff0c\u8fd9\u65f6\u66f4\u6709\u4e86\u501f\u53e3\uff0c\u8fd9\u53e3\u6076\u6c14\u54ea\u91cc\u8fd8\u8010\u5f97\u5230\u4ed6\u65e5\u518d\u51fa\uff1f\u5f53\u5373\u5237\u7684\u4e00\u58f0\uff0c\u957f\u5251\u51fa\u9798\uff0c\u5203\u5410\u9752\u5149\uff0c\u75be\u5411\u8a00\u8fbe\u5e73\u5589\u5934\u523a\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u72c4\u4e91\u4ece\u672a\u89c1\u4e07\u9707\u5c71\u663e\u793a\u8fc7\u6b66\u529f\uff0c\u8fd9\u65f6\u89c1\u4ed6\u4e00\u62db\u523a\u51fa\uff0c\u72e0\u8fa3\u7a33\u5065\uff0c\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u8fd9\u4e00\u5251\u597d\u50cf\u6ca1\u6709\u6f0f\u6d1e\u3002\u201d\u72c4\u4e91\u6b64\u65f6\u6b66\u5b66\u4fee\u4e3a\u5df2\u662f\u6df1\u6e5b\uff0c\u867d\u7136\u65e0\u4eba\u4f20\u6388\uff0c\u4f46\u5728\u522b\u4eba\u51fa\u62db\u4e4b\u65f6\uff0c\u81ea\u7136\u800c\u7136\u7684\u9996\u5148\u4fbf\u770b\u5bf9\u65b9\u62db\u6570\u4e2d\u6709\u751a\u4e48\u7834\u7efd\u3002 \u3000\u3000\u8a00\u8fbe\u5e73\u659c\u8eab\u8ba9\u8fc7\uff0c\u5de6\u624b\u6293\u4f4f\u62d0\u6756\u4e0b\u7aef\uff0c\u53f3\u624b\u6293\u4f4f\u62d0\u6756\u9f99\u5934\uff0c\u53cc\u624b\u4e00\u5206\uff0c\u64e6\u7684\u4e00\u58f0\u8f7b\u54cd\uff0c\u767d\u5149\u8000\u773c\uff0c\u624b\u4e2d\u5df2\u591a\u4e86\u4e00\u67c4\u957f\u5251\u3002\u539f\u6765\u90a3\u62d0\u6756\u7684\u9f99\u5934\u4fbf\u662f\u5251\u67c4\uff0c\u5251\u5203\u8499\u5728\u6756\u4e2d\uff0c\u62d0\u6756\u4e0b\u7aef\u4fbf\u662f\u5251\u9798\u3002\u4ed6\u4e00\u5251\u5728\u624b\uff0c\u5f53\u5373\u8fd8\u62db\uff0c\u53ea\u542c\u5f97\u53ee\u53ee\u53ee\u53ee\u4e4b\u58f0\u4e0d\u7edd\uff0c\u5e08\u5144\u5f1f\u4e8c\u4eba\u4fbf\u5728\u571f\u5751\u8fb9\u4e0a\u6597\u4e86\u8d77\u6765\u3002\u6597\u5f97\u6570\u62db\uff0c\u5747\u89c9\u5751\u8fb9\u5730\u5f62\u72ed\u7a84\uff0c\u65bd\u5c55\u4e0d\u5f00\uff0c\u540c\u58f0\u5406\u559d\uff0c\u4e00\u9f50\u8dc3\u5165\u5751\u4e2d\u3002 \u3000\u3000\u4f17\u4e61\u6c11\u89c1\u4e8c\u4eba\u53e3\u89d2\u76f8\u4e89\uff0c\u65e9\u5df2\u60ca\u7591\u4e0d\u5b9a\uff0c\u5f85\u89c1\u52a8\u4e0a\u4e86\u5bb6\u4f19\u6076\u6597\uff0c\u66f4\u662f\u5413\u5f97\u7f29\u5728\u5c4b\u89d2\u843d\u4e2d\uff0c\u8c01\u4e5f\u4e0d\u6562\u4f5c\u58f0\u3002\u72c4\u4e91\u4e5f\u88c5\u51fa\u754f\u7f29\u4e4b\u72b6\uff0c\u7559\u610f\u89c2\u770b\u4e24\u4f4d\u5e08\u4f2f\uff0c\u53ea\u770b\u5f97\u4e03\u516b\u62db\uff0c\u5fc3\u60f3\uff1a\u201c\u4e8c\u4f4d\u5e08\u4f2f\u5185\u529b\u592a\u8fc7\u4e0d\u8db3\uff0c\u62db\u6cd5\u5374\u5c3d\u591f\u4e86\uff0c\u5c31\u7b97\u5f97\u5230\u4e86\u751a\u4e48\u2018\u8fde\u3000\u3000\u57ce\u5251\u8c31\u2019\uff0c\u6050\u6015\u4e5f\u6ca1\u751a\u4e48\u7528\u5904\uff0c\u9664\u975e\u90a3\u662f\u4e00\u90e8\u589e\u8fdb\u5185\u529f\u7684\u6b66\u7ecf\u3002 \u3000\u3000\u4f46\u65e2\u662f\u2018\u5251\u8c31\u2019\uff0c\u60f3\u6765\u5fc5\u662f\u8bb2\u5251\u6cd5\u7684\u4e66\u3002\u201d \u3000\u3000\u4ed6\u53c8\u770b\u51e0\u62db\uff0c\u66f4\u89c9\u5947\u602a\uff1a\u201c\u5218\u4e58\u98ce\u3001\u82b1\u94c1\u5e72\u4ed6\u4eec\u2018\u843d\u82b1\u6d41\u6c34\u2019\u56db\u4fa0\u7684\u6b66\u529f\uff0c\u6bd4\u4e4b\u6211\u8fd9\u4e24\u4f4d\u5e08\u4f2f\u9ad8\u5f97\u591a\u4e86\u3002\u4e24\u4f4d\u5e08\u4f2f\u4e00\u5473\u8bb2\u7a76\u62db\u6570\u53d8\u5316\uff0c\u5168\u4e0d\u987e\u548c\u5185\u529b\u914d\u5408\u3002\u90a3\u662f\u751a\u4e48\u9053\u7406\uff1f\u5f53\u5e74\u5e08\u7236\u6559\u6211\u5251\u672f\uff0c\u4e5f\u662f\u8fd9\u4e48\u6559\u3002\u770b\u6765\u4ed6\u4eec\u4e07\u3001\u8a00\u3001\u621a\u5e08\u5144\u5f1f\u4e09\u4eba\u5168\u662f\u8fd9\u4e48\u5b66\u7684\u3002\u8fd9\u79cd\u6b66\u529f\u9047\u4e0a\u6bd4\u4ed6\u4eec\u5f31\u7684\u5bf9\u624b\uff0c\u81ea\u7136\u5360\u5c3d\u4e86\u4e0a\u98ce\uff0c\u4f46\u53ea\u8981\u5bf9\u65b9\u5185\u529b\u7a0d\u5f3a\uff0c\u4ed6\u4eec\u8fd9\u8bb8\u591a\u53d8\u5e7b\u65e0\u7a77\u7684\u5251\u62db\uff0c\u5c31\u534a\u70b9\u7528\u5904\u4e5f\u6ca1\u6709\u4e86\u3002\u4e3a\u751a\u4e48\u8981\u8fd9\u6837\u5b66\u5251\uff1f\u4e3a\u751a\u4e48\u8981\u8fd9\u6837\u5b66\u5251\uff1f\u201d \u3000\u3000\u53ea\u89c1\u5b59\u5747\u3001\u51af\u57a3\u3001\u5434\u574e\u4e09\u4eba\u5404\u633a\u957f\u5251\uff0c\u4e0a\u524d\u52a9\u6218\uff0c\u6210\u4e86\u56db\u4eba\u5408\u653b\u8a00\u8fbe\u5e73\u4e4b\u52bf\u3002 \u3000\u3000\u8a00\u8fbe\u5e73\u54c8\u54c8\u5927\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u597d\uff01\u5927\u5e08\u54e5\uff0c\u4f60\u8d8a\u6765\u8d8a\u957f\u8fdb\u5566\uff0c\u62db\u96c6\u4e86\u4e00\u6279\u5c0f\u55bd\u7f57\uff0c\u9f50\u6765\u653b\u6253\u4f60\u5e08\u5f1f\u3002\u201d\u4ed6\u867d\u88c5\u4f5c\u82e5\u65e0\u5176\u4e8b\uff0c\u5251\u6cd5\u4e0a\u5374\u5df2\u9887\u89c1\u7a92\u6ede\u3002 \u3000\u3000\u72c4\u4e91\u5fc3\u60f3\uff1a\u201c\u4ed6\u5e08\u5144\u5f1f\u4e8c\u4eba\u7684\u5251\u62db\uff0c\u5404\u6709\u5404\u7684\u957f\u5904\u3002\u8a00\u5e08\u4f2f\u5f53\u5e74\u6559\u4e86\u6211\u523a\u80a9\u3001\u6253\u8033\u5149\u3001\u53bb\u5251\u4e09\u5f0f\uff0c\u7528\u4ee5\u5bf9\u4ed8\u4e07\u95e8\u4f17\u5f1f\u5b50\uff0c\u90a3\u662f\u5341\u5206\u6709\u7528\u7684\uff0c\u7528\u6765\u5bf9\u4ed8\u4e07\u5e08\u4f2f\uff0c\u5374\u662f\u534a\u70b9\u7528\u5904\u4e5f\u6ca1\u6709\u4e86\u3002\u5509\uff0c\u4ed6\u4eec\u5927\u5bb6\u90fd\u4e0d\u61c2\uff0c\u5355\u5b66\u5251\u62db\u53d8\u5316\uff0c\u82e5\u65e0\u5185\u529f\u76f8\u6d4e\uff0c\u90a3\u6709\u751a\u4e48\u7528\uff1f\u534a\u70b9\u7528\u5904\u4e5f\u6ca1\u6709\uff0c\u771f\u662f\u5947\u602a\uff0c\u8fd9\u6837\u6d45\u7684\u9053\u7406\uff0c\u8fde\u6211\u8fd9\u7b28\u4eba\u4e5f\u61c2\uff0c\u4ed6\u4eec\u4e2a\u4e2a\u5341\u5206\u806a\u660e\uff0c\u600e\u4e48\u4f1a\u8c01\u4e5f\u4e0d\u61c2\uff1f\u96be\u9053\u662f\u6211\u81ea\u5df1\u80e1\u6d82\u4e86\uff1f\u201d \u3000\u3000\u7a81\u7136\u4e4b\u95f4\uff0c\u5fc3\u5934\u4f3c\u4e4e\u95ea\u8fc7\u4e86\u4e00\u9053\u7075\u5149\uff1a\u201c\u4e01\u5927\u54e5\u8ddf\u6211\u8bf4\u8fc7\u90a3\u795e\u7167\u7ecf\u7684\u6765\u5386\uff0c\u663e\u7136\uff0c\u5e08\u7956\u7237\u6885\u5ff5\u7b19\u662f\u61c2\u5f97\u8fd9\u9053\u7406\u7684\uff0c\u5374\u4e3a\u751a\u4e48\u4e0d\u8ddf\u4e09\u4e2a\u5f1f\u5b50\u8bf4\uff1f\u96be\u9053\u2026\u2026\u96be\u9053\u2026\u2026\u96be\u9053\u2026\u2026\u201d\u4ed6\u5fc3\u4e2d\u8fde\u8bf4\u4e09\u4e2a\u201c\u96be\u9053\u201d\uff0c\u80cc\u4e0a\u767b\u65f6\u6e17\u51fa\u4e86\u4e00\u7247\u51b7\u6c57\uff0c\u4e0d\u7531\u5f97\u6253\u4e86\u4e2a\u5bd2\u5664\uff0c\u8eab\u5b50\u4e5f\u8f7b\u8f7b\u53d1\u6296\u3002 \u3000\u3000\u65c1\u8fb9\u4e00\u4e2a\u5e74\u8001\u7684\u4e61\u6c11\u4e0d\u4f4f\u5ff5\u4f5b\uff1a\u201c\u963f\u5f25\u9640\u4f5b\uff0c\u963f\u5f25\u9640\u4f5b\uff0c\u522b\u5f04\u51fa\u4eba\u547d\u6765\u624d\u597d\u3002\u5c0f\u5144\u5f1f\uff0c\u522b\u6015\uff0c\u522b\u6015\u3002\u201d\u4ed6\u89c1\u72c4\u4e91\u53d1\u6296\uff0c\u8fd8\u9053\u4ed6\u662f\u89c1\u5230\u4e07\u8a00\u4e8c\u4eba\u76f8\u6597\u800c\u5bb3\u6015\uff0c\u867d\u51fa\u8a00\u5b89\u6170\uff0c\u81ea\u5df1\u5fc3\u4e2d\u53ef\u4e5f\u7740\u5b9e\u60ca\u60e7\u3002 \u3000\u3000\u72c4\u4e91\u5fc3\u5e95\u5df2\u660e\u767d\u4e86\u771f\u76f8\uff0c\u53ef\u662f\u90a3\u5b9e\u5728\u592a\u8fc7\u9634\u9669\u6076\u6bd2\uff0c\u4ed6\u4e0d\u613f\u591a\u60f3\uff0c\u66f4\u4e0d\u613f\u5c06\u5df2\u7ecf\u731c\u5230\u4e86\u7684\u771f\u76f8\uff0c\u5f52\u5e76\u6210\u4e3a\u4e00\u6761\u660e\u663e\u7684\u7406\u8def\uff0c\u53ea\u662f\u65e2\u7136\u60f3\u901a\u4e86\u5173\u952e\u7684\u6240\u5728\uff0c\u4e00\u4ef6\u4ef6\u5c0f\u4e8b\u81ea\u4f1a\u6c47\u5f52\u5728\u4e00\u8d77\u3002\u4e07\u9707\u5c71\u3001\u8a00\u8fbe\u5e73\u3001\u5b59\u5747\u3001\u51af\u5766\u2026\u2026\u8fd9\u4e9b\u4eba\u6bcf\u4e00\u62db\u9012\u51fa\uff0c\u90fd\u662f\u4ee4\u4ed6\u7684\u60f3\u6cd5\u591a\u4e86\u4e00\u6b21\u5370\u8bc1\u3002\u201c\u4e0d\u9519\uff0c\u4e0d\u9519\uff0c\u4e00\u5b9a\u662f\u8fd9\u6837\u3002\u4e0d\u8fc7\uff0c\u53c8\u6050\u6015\u4e0d\u4f1a\u7f62\uff1f\u505a\u5e08\u7236\u7684\uff0c\u600e\u80fd\u5982\u6b64\u6076\u6bd2\uff1f\u4e0d\u4f1a\u7684\uff0c\u4e0d\u4f1a\u7684\u2026\u2026\u53ef\u662f\uff0c\u5018\u82e5\u4e0d\u662f\uff0c\u53c8\u600e\u4f1a\u8fd9\u6837\uff1f\u5b9e\u5728\u592a\u4e5f\u5947\u602a\u4e86\u3002\u201d \u3000\u3000\u4e00\u5f20\u6e05\u6e05\u695a\u695a\u7684\u56fe\u753b\u5728\u4ed6\u8111\u6d77\u4e2d\u5448\u73b0\u4e86\u51fa\u6765\uff1a\u201c\u8bb8\u591a\u5e74\u4ee5\u524d\uff0c\u5c31\u662f\u5728\u8fd9\u5c4b\u5b50\u5916\u9762\uff0c\u6211\u548c\u5e08\u59b9\u7ec3\u5251\uff0c\u5e08\u7236\u5728\u65c1\u6307\u70b9\u3002\u5e08\u7236\u6559\u4e86\u6211\u4e00\u62db\uff0c\u5f88\u662f\u5de7\u5999\u3002\u6211\u7528\u5fc3\u7684\u7ec3\uff0c\u7b2c\u4e8c\u6b21\u5e08\u7236\u5374\u6559\u5f97\u4e0d\u540c\u4e86\uff0c\u5251\u6cd5\u4ecd\u7136\u5f88\u5de7\u5999\uff0c\u5374\u548c\u7b2c\u4e00\u6b21\u6709\u4e9b\u513f\u4e0d\u540c\u3002\u5f53\u65f6\uff0c\u6211\u53ea\u9053\u662f\u5e08\u7236\u7684\u5251\u6cd5\u53d8\u5e7b\u83ab\u6d4b\u3002\u8fd9\u65f6\u60f3\u6765\uff0c\u4e24\u6b21\u6240\u6559\u7684\u5251\u62db\u4e3a\u751a\u4e48\u4e0d\u540c\uff0c\u9053\u7406\u662f\u518d\u4e5f\u660e\u767d\u4e0d\u8fc7\u4e86\u3002\u201d \u3000\u3000\u7a81\u7136\u4e4b\u95f4\uff0c\u5fc3\u91cc\u611f\u5230\u4e00\u9635\u9635\u7684\u523a\u75db\uff1a\u201c\u5e08\u7236\u6545\u610f\u6559\u6211\u8d70\u9519\u8def\u5b50\uff0c\u6545\u610f\u6559\u6211\u4e9b\u6b21\u7b49\u5251\u6cd5\u3002\u4ed6\u81ea\u5df1\u7684\u672c\u4e8b\u9ad8\u5f97\u591a\uff0c\u5374\u6545\u610f\u6559\u6211\u5b66\u4e9b\u4e2d\u770b\u4e0d\u4e2d\u7528\u7684\u5251\u62db\u3002\u4ed6\u2026\u2026\u4ed6\u2026\u2026\u8a00\u5e08\u4f2f\u7684\u6b66\u529f\u548c\u5e08\u7236\u5e94\u8be5\u5dee\u4e0d\u591a\uff0c\u53ef\u662f\u4ed6\u6559\u4e86\u6211\u4e09\u62db\u5251\u6cd5\uff0c\u5c31\u6bd4\u5e08\u7236\u9ad8\u660e\u5f97\u591a\u2026\u2026\u201d \u3000\u3000\u201c\u8a00\u5e08\u4f2f\u5374\u53c8\u4e3a\u751a\u4e48\u6559\u6211\u8fd9\u4e09\u62db\u5251\u6cd5\uff1f\u4ed6\u4e0d\u4f1a\u5b58\u7740\u597d\u5fc3\u7684\u3002\u662f\u4e86\uff0c\u4ed6\u8981\u5f15\u8d77\u4e07\u5e08\u4f2f\u7684\u7591\u5fc3\uff0c\u8981\u4e07\u5e08\u4f2f\u548c\u6211\u5e08\u7236\u6597\u5c06\u8d77\u6765\u2026\u2026\u201d \u3000\u3000\u201c\u4e07\u5e08\u4f2f\u4e5f\u662f\u8fd9\u6837\uff0c\u4ed6\u81ea\u5df1\u7684\u672c\u4e8b\uff0c\u548c\u4ed6\u7684\u4f17\u5f1f\u5b50\u5b8c\u5168\u4e0d\u540c\u2026\u2026\u5374\u4e3a\u751a\u4e48\u8fde\u81ea\u5df1\u7684\u513f\u5b50\u4e5f\u8981\u6b3a\u9a97\uff1f\u5509\uff0c\u4ed6\u4e0d\u80fd\u5355\u6559\u81ea\u5df1\u513f\u5b50\uff0c\u5374\u4e0d\u6559\u522b\u7684\u5f1f\u5b50\uff0c\u8fd9\u4e00\u6765\uff0c\u897f\u6d0b\u955c\u7acb\u523b\u5c31\u62c6\u7a7f\u4e86\u3002\u201d \u3000\u3000\u8a00\u8fbe\u5e73\u5de6\u624b\u634f\u7740\u5251\u8bc0\uff0c\u53f3\u624b\u624b\u8155\u6296\u52a8\uff0c\u5251\u5c16\u8fde\u8f6c\u4e86\u4e03\u4e2a\u5708\u5b50\uff0c\u5feb\u901f\u65e0\u4f26\u7684\u523a\u5411\u4e07\u9707\u5c71\u80f8\u53e3\u3002\u4e07\u9707\u5c71\u6a2a\u8fc7\u5251\u8eab\uff0c\u4ee5\u6a2a\u7834\u5706\uff0c\u659c\u5288\u8fde\u524a\uff0c\u5c06\u4ed6\u8fd9\u4e03\u4e2a\u5251\u5708\u5c3d\u6570\u7834\u89e3\u4e86\u3002 \u3000\u3000\u72c4\u4e91\u5728\u65c1\u770b\u7740\uff0c\u53c8\u60f3\uff1a\u201c\u8fd9\u4e03\u4e2a\u5708\u5b50\u5168\u662f\u591a\u4f59\uff0c\u6700\u7ec8\u662f\u4e00\u5251\u523a\u5411\u4e07\u5e08\u4f2f\u7684\u5de6\u80f8\uff0c\u4f55\u4e0d\u76f4\u6377\u4e86\u5f53\u7684\u523a\u4e86\u8fc7\u53bb\uff1f\u5c82\u4e0d\u65e2\u5feb\u53c8\u72e0\uff1f\u4e07\u5e08\u4f2f\u659c\u5288\u8fde\u524a\uff0c\u4ee5\u4e03\u4e2a\u62db\u5f0f\u7834\u89e3\u8a00\u5e08\u4f2f\u7684\u4e03\u4e2a\u5251\u5708\uff0c\u597d\u50cf\u5de7\u5999\uff0c\u5176\u5b9e\u7b28\u5f97\u4e0d\u5f97\u4e86\uff0c\u82e5\u662f\u53cd\u523a\u8a00\u5e08\u4f2f\u5c0f\u8179\uff0c\u65e9\u5df2\u5f97\u80dc\u4e86\u3002\u201d \u3000\u3000\u731b\u5730\u91cc\u8111\u6d77\u4e2d\u53c8\u63a0\u8fc7\u4e00\u5e55\u60c5\u666f\uff1a \u3000\u3000\u4ed6\u548c\u5e08\u59b9\u621a\u82b3\u5728\u7ec3\u5251\uff0c\u621a\u82b3\u7684\u5251\u62db\u82b1\u5f0f\u7e41\u591a\uff0c\u4ed6\u8bb0\u4e0d\u6e05\u5e08\u7236\u6240\u6559\u7684\u62db\u6570\uff0c\u7ed9\u8feb\u5f97\u624b\u5fd9\u811a\u4e71\uff0c\u8fde\u8fde\u5012\u9000\u3002\u621a\u82b3\u63a5\u8fde\u4e09\u62db\u653b\u6765\uff0c\u4ed6\u5934\u6655\u773c\u82b1\uff0c\u624b\u5fd9\u811a\u4e71\uff0c\u773c\u770b\u62b5\u654c\u4e0d\u4f4f\uff0c\u5df2\u65e0\u6cd5\u53bb\u60f3\u5e08\u7236\u6559\u8fc7\u7684\u5251\u62db\uff0c\u968f\u624b\u6321\u67b6\uff0c\u8ddf\u7740\u4fbf\u53cd\u523a\u51fa\u53bb\u2026\u2026 \u3000\u3000\u621a\u82b3\u4f7f\u4e00\u62db\u201c\u4fef\u542c\u6587\u60ca\u98ce\uff0c\u8fde\u5c71\u77f3\u5e03\u9003\u201d\uff0c\u5708\u5251\u6765\u6321\uff0c\u4f46\u4ed6\u7684\u5251\u62db\u7eaf\u7cfb\u81ea\u53d1\uff0c\u4e0d\u4f9d\u5e08\u6388\u89c4\u8303\uff0c\u621a\u82b3\u8fd9\u4e00\u62db\u82b1\u5f0f\u5de7\u5999\u7684\u5251\u6cd5\u53cd\u800c\u6321\u67b6\u4e0d\u4f4f\u3002\u4ed6\u4e00\u5251\u523a\u53bb\uff0c\u76f4\u6307\u5e08\u59b9\u7684\u80a9\u5934\u3002\u6b63\u6536\u52bf\u4e0d\u53ca\u4e4b\u9645\uff0c\u5e08\u7236\u621a\u957f\u53d1\u4ece\u65c1\u8dc3\u51fa\uff0c\u624b\u4e2d\u62ff\u7740\u4e00\u6839\u6728\u67f4\uff0c\u62cd\u7684\u4e00\u58f0\uff0c\u5c06\u4ed6\u624b\u4e2d\u7684\u957f\u5251\u51fb\u843d\u4e86\u3002\u4ed6\u548c\u621a\u82b3\u90fd\u5413\u5f97\u8138\u8272\u5927\u53d8\u3002\u621a\u957f\u53d1\u5c06\u4ed6\u72e0\u72e0\u8d23\u9a82\u4e86\u4e00\u987f\uff0c\u8bf4\u4ed6\u4e71\u523a\u4e71\u5288\uff0c\u4e0d\u4f9d\u5e08\u7236\u6240\u6559\u7684\u65b9\u6cd5\u4f7f\u5251\uff0c\u592a\u4e0d\u6210\u8bdd\u3002 \u3000\u3000\u5f53\u65f6\u4ed6\u4e5f\u66fe\u60f3\u5230\uff1a\u201c\u6211\u4e0d\u7167\u89c4\u77e9\u4f7f\u5251\uff0c\u600e\u4e48\u53cd\u800c\u80dc\u4e86\uff1f\u201d\u4f46\u8fd9\u5ff5\u5934\u53ea\u662f\u4e00\u95ea\u5373\u901d\uff0c\u968f\u5373\u660e\u767d\uff1a\u201c\u81ea\u7136\u56e0\u4e3a\u5e08\u59b9\u7684\u5251\u672f\u8fd8\u6ca1\u7ec3\u5f97\u5230\u5bb6\u3002\u8981\u662f\u9047\u4e0a\u4e86\u771f\u6b63\u597d\u624b\uff0c\u6211\u8fd9\u822c\u80e1\u780d\u4e71\u5288\u5f53\u7136\u975e\u8f93\u4e0d\u53ef\u3002\u201d\u4ed6\u5f53\u65f6\u53c8\u600e\u60f3\u5f97\u5230\uff1a\u81ea\u5df1\u968f\u624b\u523a\u51fa\u53bb\u7684\u5251\u62db\uff0c\u5176\u5b9e\u6bd4\u5e08\u7236\u6240\u6559\u5e0c\u5947\u53e4\u602a\u3001\u82b1\u5de7\u767e\u7aef\u7684\u5251\u6cd5\u5b9e\u7528\u5f97\u591a\u3002 \u3000\u3000\u73b0\u4e0b\u60f3\u6765\uff0c\u90a3\u53ef\u5168\u7136\u4e0d\u540c\u4e86\u3002\u4ee5\u4ed6\u6b64\u523b\u7684\u6b66\u529f\uff0c\u81ea\u662f\u6e05\u6e05\u695a\u695a\u7684\u770b\u4e86\u51fa\u6765\uff1a\u4e07\u9707\u5c71\u548c\u8a00\u8fbe\u5e73\u4e24\u4eba\u6240\u4f7f\u7684\u5251\u672f\u4e4b\u4e2d\uff0c\u6709\u8bb8\u591a\u662f\u5168\u7136\u65e0\u7528\u7684\u82b1\u62db\uff0c\u800c\u4e07\u9707\u5c71\u6559\u7ed9\u5f1f\u5b50\u7684\u5251\u6cd5\uff0c\u621a\u957f\u53d1\u6559\u7ed9\u4ed6\u548c\u621a\u82b3\u7684\u5251\u6cd5\uff0c\u5176\u4e2d\u65e0\u7528\u7684\u82b1\u62db\u66f4\u591a\u3002\u4e0d\u7528\u8bf4\uff0c\u5e08\u7956\u6885\u5ff5\u7b19\u65e9\u77a7\u51fa\u4e09\u4e2a\u5f92\u513f\u5fc3\u672f\u4e0d\u6b63\uff0c\u5728\u4f20\u6388\u4e4b\u65f6\u6545\u610f\u5f15\u4ed6\u4eec\u8d70\u4e0a\u4e86\u5251\u672f\u7684\u6b6a\u8def\uff0c\u800c\u4e07\u9707\u5c71\u548c\u621a\u957f\u53d1\u5728\u6559\u5f92\u513f\u4e4b\u65f6\uff0c\u6216\u6709\u610f\u6216\u65e0\u610f\u7684\uff0c\u5f15\u4ed6\u4eec\u5728\u6b6a\u8def\u4e0a\u8d70\u5f97\u66f4\u8fdc\u3002 \u3000\u3000\u4e34\u654c\u4e4b\u65f6\u4f7f\u4e00\u62db\u4e0d\u7ba1\u7528\u7684\u5251\u6cd5\uff0c\u4e0d\u53ea\u662f\u201c\u65e0\u7528\u201d\u800c\u5df2\uff0c\u90a3\u662f\u865a\u8017\u4e86\u673a\u4f1a\uff0c\u8ba9\u654c\u4eba\u62a2\u5230\u4e0a\u98ce\uff0c\u4fbf\u662f\u5c06\u6027\u547d\u4ea4\u5728\u654c\u4eba\u624b\u91cc\u3002 \u3000\u3000\u4e3a\u751a\u4e48\u5e08\u7956\u3001\u5e08\u7236\u3001\u5e08\u4f2f\u90fd\u8fd9\u4e48\u72e0\u6bd2\uff1f\u90fd\u8fd9\u4e48\u7684\u9634\u9669\uff1f \u3000\u3000\u201c\u4ed6\u4eec\u4f1a\u548c\u81ea\u5df1\u7684\u513f\u5b50\u3001\u5973\u513f\u6709\u4ec7\u4e48\uff1f\u6545\u610f\u8981\u5751\u5bb3\u81ea\u5df1\u7684\u5f92\u5f1f\u4e48\uff1f\u90a3\u51b3\u8ba1\u4e0d\u4f1a\u3002\u5fc5\u5b9a\u53e6\u5916\u6709\u66f4\u91cd\u5927\u7684\u539f\u56e0\uff0c\u4e00\u5b9a\u6709\u8981\u7d27\u4e4b\u6781\u7684\u56fe\u8c0b\u3002\u96be\u9053\u662f\u4e3a\u4e86\u90a3\u672c\u300a\u8fde\u57ce\u5251\u8c31\u300b\uff1f \u3000\u3000\u201c\u5e94\u8be5\u662f\u7684\u7f62\uff1f\u4e07\u5e08\u4f2f\u548c\u8a00\u5e08\u4f2f\u4e3a\u4e86\u8fd9\u5251\u8c31\uff0c\u53ef\u4ee5\u6740\u6b7b\u81ea\u5df1\u7684\u5e08\u7236\uff0c\u73b0\u5728\u53c8\u62da\u547d\u60f3\u6740\u6b7b\u5bf9\u65b9\u3002\u201d \u3000\u3000\u4e0d\u9519\uff0c\u4ed6\u4eec\u5728\u62da\u547d\u60f3\u6740\u6b7b\u5bf9\u65b9\u3002\u571f\u5751\u4e2d\u7684\u4e89\u6597\u8d8a\u6765\u8d8a\u7d27\u8feb\u3002\u4e07\u9707\u5c71\u548c\u8a00\u8fbe\u5e73\u4e8c\u4eba\u7684\u5251\u6cd5\u96be\u5206\u9ad8\u4e0b\uff0c\u4f46\u4e07\u95e8\u4f17\u5f1f\u5b50\u5728\u65c1\u76f8\u52a9\uff0c\u7a76\u7adf\u4ee4\u8a00\u8fbe\u5e73\u5927\u4e3a\u5206\u5fc3\u3002\u6597\u5212\u5206\u9645\uff0c\u5b59\u5747\u4e00\u5251\u523a\u5411\u8a00\u8fbe\u5e73\u540e\u5fc3\uff0c\u8a00\u8fbe\u5e73\u56de\u5251\u4e00\u6321\uff0c\u5251\u950b\u987a\u52bf\u63a0\u4e0b\u3002\u5b59\u5747\u4e00\u58f0 \u201c\u554a\u54df\uff01\u201d\u864e\u53e3\u53d7\u4f24\uff0c\u8ddf\u7740\u5f53\u7684\u4e00\u58f0\uff0c\u957f\u5251\u843d\u5730\u3002\u4fbf\u5728\u8fd9\u65f6\uff0c\u4e07\u9707\u5c71\u5df2\u4e58\u9699\u524a\u51fa\u4e00\u5251\uff0c\u5728\u8a00\u8fbe\u5e73\u53f3\u81c2\u5272\u4e86\u957f\u957f\u4e00\u9053\u53e3\u5b50\u3002 \u3000\u3000\u8a00\u8fbe\u5e73\u5403\u75db\uff0c\u6025\u5fd9\u5251\u4ea4\u5de6\u624b\uff0c\u4f46\u5de6\u624b\u4f7f\u5251\u7a76\u7adf\u751a\u662f\u4e0d\u60ef\uff0c\u53f3\u81c2\u4e0a\u7684\u4f24\u52bf\u4e5f\u7740\u5b9e\u4e0d\u8f7b\uff0c\u9c9c\u8840\u67d3\u5f97\u4ed6\u534a\u8eab\u90fd\u662f\u8840\u6c61\u3002\u4e03\u516b\u62db\u62c6\u5c06\u4e0b\u6765\uff0c\u5de6\u80a9\u4e0a\u53c8\u4e2d\u4e86\u4e00\u5251\u3002 \u3000\u3000\u4f17\u4e61\u6c11\u89c1\u72b6\uff0c\u90fd\u662f\u5413\u5f97\u8138\u4e0a\u53d8\u8272\uff0c\u7a83\u7a83\u79c1\u8bae\uff0c\u53ea\u60f3\u9003\u51fa\u5c4b\u53bb\uff0c\u5374\u662f\u8c01\u4e5f\u4e0d\u6562\u52a8\u5f39\u3002 \u3000\u3000\u4e07\u9707\u5c71\u51b3\u610f\u4eca\u65e5\u5c06\u8fd9\u5e08\u5f1f\u6740\u4e86\uff0c\u4e00\u5251\u5251\u51fa\u624b\uff0c\u66f4\u662f\u72e0\u8fa3\uff0c\u55e4\u7684\u4e00\u58f0\u54cd\uff0c\u8a00\u8fbe\u5e73\u53f3\u80f8\u53c8\u4e2d\u4e00\u5251\u3002 \u3000\u3000\u773c\u770b\u6570\u62db\u4e4b\u95f4\uff0c\u8a00\u8fbe\u5e73\u4fbf\u8981\u6b7b\u4e8e\u5e08\u5144\u5251\u5e95\uff0c\u4ed6\u54ac\u7740\u7259\u9f7f\u6d74\u8840\u82e6\u6597\uff0c\u4e0d\u51fa\u534a\u53e5\u6c42\u9976\u7684\u8a00\u8bed\u3002\u4ed6\u548c\u8fd9\u5e08\u5144\u540c\u95e8\u5341\u4f59\u5e74\uff0c\u79bb\u4e86\u5e08\u95e8\u4e4b\u540e\uff0c\u53c8\u660e\u4e89\u6697\u6597\u4e86\u5341\u4f59\u5e74\uff0c\u5bf9\u4ed6\u4e3a\u4eba\u77e5\u4e4b\u6781\u6df1\uff0c\u51fa\u8a00\u76f8\u6c42\u53ea\u6709\u5f92\u906d\u7f9e\u8fb1\uff0c\u7edd\u65e0\u7528\u5904\u3002 \u3000\u3000\u72c4\u4e91\u5fc3\u9053\uff1a\u201c\u5f53\u5e74\u5728\u8346\u5dde\u4e4b\u65f6\uff0c\u8a00\u5e08\u4f2f\u4ee5\u4e00\u53ea\u996d\u7897\u52a9\u6211\u6253\u9000\u5927\u76d7\u5415\u901a\uff0c\u53c8\u6559\u4e86\u6211\u4e09\u62db\u5251\u6cd5\uff0c\u4f7f\u6211\u4e0d\u53d7\u4e07\u95e8\u4f17\u5f1f\u5b50\u7684\u6b3a\u4fae\uff0c\u867d\u7136\u4ed6\u591a\u534a\u522b\u6709\u7528\u610f\uff0c\u4f46\u6211\u603b\u662f\u53d7\u8fc7\u4ed6\u7684\u6069\u60e0\uff0c\u51b3\u4e0d\u80fd\u8ba9\u4ed6\u6b7b\u4e8e\u975e\u547d\u3002\u201d\u5f53\u4e0b\u5047\u88c5\u4e0d\u4f4f\u53d1\u6296\uff0c\u63d0\u8d77\u624b\u4e2d\u94c1\u94f2\u5728\u5730\u4e0b\u94f2\u6ee1\u4e86\u6ce5\u571f\u3002 \u3000\u3000\u53ea\u89c1\u4e07\u9707\u5c71\u53c8\u633a\u5251\u5411\u8a00\u8fbe\u5e73\u5c0f\u8179\u523a\u53bb\uff0c\u8a00\u8fbe\u5e73\u8eab\u5b50\u6447\u6643\uff0c\u5df2\u95ea\u907f\u4e0d\u5f00\u3002\u72c4\u4e91\u624b\u4e2d\u7684\u94c1\u94f2\u8f7b\u8f7b\u4e00\u6296\uff0c\u4e00\u94f2\u9ec4\u6ce5\u5411\u4e07\u9707\u5c71\u98de\u4e86\u8fc7\u53bb\u3002\u6ce5\u4e0a\u6240\u5e26\u7684\u5185\u52b2\u7740\u5b9e\u4e0d\u5c0f\u3002\u4e07\u9707\u5c71\u88ab\u8fd9\u80a1\u52b2\u529b\u4e00\u649e\uff0c\u767b\u65f6\u7acb\u8db3\u4e0d\u4f4f\uff0c\u817e\u7684\u4e00\u4e0b\uff0c\u5411\u540e\u4fbf\u6454\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u4f17\u4eba\u51fa\u5176\u4e0d\u610f\uff0c\u8c01\u4e5f\u4e0d\u77e5\u6ce5\u571f\u4ece\u4f55\u5904\u98de\u6765\u3002\u72c4\u4e91\u51e0\u94f2\u6ce5\u571f\u8ddf\u7740\u8fc5\u901f\u63b7\u51fa\uff0c\u90fd\u662f\u63b7\u5411\u70b9\u5728\u58c1\u4e0a\u7684\u677e\u660e\u548c\u6cb9\u706f\uff0c\u5927\u5385\u4e2d\u7acb\u65f6\u9ed1\u6f06\u4e00\u56e2\uff0c\u4f17\u4eba\u90fd\u60ca\u53eb\u8d77\u6765\u3002\u72c4\u4e91\u7eb5\u8eab\u800c\u524d\uff0c\u4e00\u628a\u62b1\u8d77\u8a00\u8fbe\u5e73\u4fbf\u51b2\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u72c4\u4e91\u4e00\u5230\u5c4b\u5916\uff0c\u4fbf\u5c06\u8a00\u8fbe\u5e73\u8d1f\u5728\u80cc\u4e0a\uff0c\u5f80\u540e\u5c71\u75be\u9a70\u3002 \u3000\u3000\u4ed6\u4e8e\u8fd9\u4e00\u5e26\u7684\u5730\u52bf\u5341\u5206\u719f\u6089\uff0c\u5c3d\u5f80\u8352\u50fb\u96be\u884c\u7684\u9ad8\u5c71\u4e0a\u6500\u884c\u3002\u8a00\u8fbe\u5e73\u4f0f\u5728\u4ed6\u80cc\u4e0a\uff0c\u53ea\u89c9\u8033\u7554\u751f\u98ce\uff0c\u72b9\u4f3c\u817e\u4e91\u9a7e\u96fe\u4e00\u822c\uff0c\u604d\u5982\u68a6\u4e2d\uff0c\u771f\u4e0d\u4fe1\u4e16\u95f4\u7adf\u6709\u8fd9\u7b49\u6b66\u529f\u9ad8\u5f3a\u4e4b\u4eba\u3002 \u3000\u3000\u72c4\u4e91\u8d1f\u7740\u8a00\u8fbe\u5e73\uff0c\u6500\u4e0a\u4e86\u8fd9\u4e00\u5e26\u6700\u9ad8\u7684\u4e00\u5ea7\u5c71\u5cf0\u3002\u5c71\u5cf0\u9661\u5ced\u9669\u5cfb\uff0c\u72c4\u4e91\u4e5f\u4ece\u672a\u4e0a\u6765\u8fc7\u3002\u4ed6\u66fe\u4e0e\u621a\u82b3\u4ef0\u671b\u8fd9\u5ea7\u4e91\u56f4\u96fe\u7ed5\u7684\u5c71\u5cf0\uff0c\u5546\u91cf\u8bf4\u5c71\u4e0a\u6709\u6ca1\u6709\u5996\u602a\u795e\u4ed9\u3002\u621a\u82b3\u8bf4\uff1a\u201c\u54ea\u4e00\u65e5\u4f60\u5f85\u6211\u4e0d\u597d\u4e86\uff0c\u6211\u4fbf\u722c\u4e0a\u5c71\u53bb\uff0c\u6c38\u8fdc\u4e0d\u4e0b\u6765\u4e86\u3002\u201d\u72c4\u4e91\u8bf4\uff1a\u201c\u597d\uff0c\u6211\u4e5f\u6c38\u8fdc\u4e0d\u4e0b\u6765\u3002\u201d\u621a\u82b3\u7b11\u9053\uff1a\u201c\u7a7a\u5fc3\u83dc\uff01\u4f60\u80af\u966a\u7740\u6211\u6c38\u8fdc\u4e0d\u4e0b\u6765\uff0c\u6211\u4e5f\u4e0d\u7528\u4e0a\u53bb\u5566\u3002\u201d \u3000\u3000\u5f53\u65f6\u72c4\u4e91\u53ea\u662f\u563b\u563b\u50bb\u7b11\uff0c\u6b64\u523b\u5374\u60f3\uff1a\u201c\u6211\u6c38\u8fdc\u613f\u610f\u966a\u7740\u4f60\uff0c\u4f60\u5374\u4e0d\u8981\u6211\u966a\u3002\u201d \u3000\u3000\u4ed6\u5c06\u8a00\u8fbe\u5e73\u653e\u4e0b\u5730\u6765\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u6709\u91d1\u521b\u836f\u4e48\uff1f\u201d\u8a00\u8fbe\u5e73\u6251\u7ffb\u8eab\u8eaf\u4fbf\u62dc\uff0c\u9053\uff1a\u201c\u6069\u516c\u5c0a\u59d3\u5927\u540d\uff1f\u8a00\u8fbe\u5e73\u4eca\u65e5\u5f97\u8499\u76f8\u6551\uff0c\u5927\u6069\u4e0d\u77e5\u5982\u4f55\u62a5\u7b54\u624d\u662f\u3002\u201d\u72c4\u4e91\u4e0d\u80fd\u53d7\u5e08\u4f2f\u8fd9\u4e2a\u793c\uff0c\u5fd9\u8dea\u4e0b\u8fd8\u793c\uff0c\u8bf4\u9053\uff1a\u201c\u524d\u8f88\u4e0d\u5fc5\u591a\u793c\uff0c\u6298\u6740\u5c0f\u4eba\u4e86\u3002\u5c0f\u4eba\u662f\u65e0\u540d\u4e4b\u8f88\uff0c\u4e00\u4e9b\u5c0f\u4e8b\uff0c\u8bf4\u751a\u4e48\u62a5\u7b54\u4e0d\u62a5\u7b54\uff1f\u201d\u8a00\u8fbe\u5e73\u575a\u6b32\u8bf7\u6559\uff0c\u72c4\u4e91\u4e0d\u4f1a\u634f\u9020\u5047\u540d\uff0c\u53ea\u662f\u4e0d\u8bf4\u3002 \u3000\u3000\u8a00\u8fbe\u5e73\u89c1\u4ed6\u4e0d\u80af\u8bf4\uff0c\u53ea\u5f97\u7f62\u4e86\uff0c\u4ece\u6000\u4e2d\u53d6\u51fa\u91d1\u521b\u836f\u6765\uff0c\u6577\u4e0a\u4e86\u4f24\u53e3\uff1b\u629a\u6478\u4e09\u5904\u5251\u4f24\uff0c\u5140\u81ea\u5fc3\u60ca\uff1a\u201c\u4ed6\u518d\u8fdf\u5f97\u7247\u523b\u51fa\u624b\uff0c\u6211\u8fd9\u65f6\u5df2\u4e0d\u5728\u4eba\u4e16\u4e86\u3002\u201d \u3000\u3000\u72c4\u5929\u9053\uff1a\u201c\u5728\u4e0b\u5fc3\u4e2d\u6709\u51e0\u4ef6\u7591\u96be\uff0c\u8981\u8bf7\u95ee\u524d\u8f88\u3002\u201d\u8a00\u8fbe\u5e73\u5fd9\u9053\uff1a\u201c\u6069\u516c\u518d\u4e5f\u4f11\u63d0\u524d\u8f88\u4e24\u5b57\u3002\u6709\u4f55\u8be2\u95ee\uff0c\u8a00\u8fbe\u5e73\u81ea\u5f53\u7aed\u8bda\u5949\u544a\uff0c\u4e0d\u6562\u6709\u5206\u6beb\u9690\u7792\u3002\u201d\u72c4\u4e91\u9053\uff1a\u201c\u90a3\u518d\u597d\u4e0d\u8fc7\u4e86\u3002\u8bf7\u95ee\u524d\u8f88\uff0c\u8fd9\u5ea7\u5927\u5c4b\uff0c\u662f\u4f60\u6240\u9020\u7684\u4e48\uff1f\u201d\u8a00\u8fbe\u5e73\u9053\uff1a\u201c\u662f\u7684\u3002\u201d\u72c4\u4e91\u53c8\u95ee\uff1a\u201c\u524d\u8f88\u96c7\u4eba\u6316\u6398\uff0c\u5f53\u7136\u662f\u627e\u90a3\u300a\u8fde\u57ce\u5251\u8c31\u300b\u4e86\u3002\u4e0d\u77e5\u53ef\u627e\u5230\u4e86\u6ca1\u6709\uff1f\u201d \u3000\u3000\u8a00\u8fbe\u5e73\u5fc3\u4e2d\u4e00\u51db\uff1a\u201c\u6211\u9053\u4ed6\u4e3a\u751a\u4e48\u597d\u5fc3\u6551\u6211\uff0c\u5374\u539f\u6765\u4e5f\u662f\u4e3a\u4e86\u90a3\u672c\u300a\u8fde\u57ce\u5251\u8c31\u300b\u3002\u201d\u8bf4\u9053\uff1a\u201c\u6211\u82b1\u4e86\u65e0\u6570\u5fc3\u8840\uff0c\u81f3\u4eca\u672a\u66fe\u5f97\u5230\u534a\u70b9\u7aef\u502a\u3002\u6069\u516c\u660e\u9274\uff0c\u5c0f\u4eba\u5b9e\u662f\u4e0d\u6562\u76f8\u7792\u3002\u5018\u82e5\u8a00\u8fbe\u5e73\u5df2\u7ecf\u5f97\u5230\uff0c\u7acb\u523b\u4fbf\u53cc\u624b\u732e\u4e0a\u3002\u59d3\u8a00\u7684\u6027\u547d\u662f\u6069\u516c\u6240\u6551\uff0c\u5c82\u6562\u7231\u60dc\u8fd9\u8eab\u5916\u4e4b\u7269\uff1f\u201d \u3000\u3000\u72c4\u4e91\u8fde\u8fde\u6447\u624b\uff0c\u9053\uff1a\u201c\u6211\u4e0d\u662f\u8981\u5251\u8c31\u3002\u4e0d\u7792\u524d\u8f88\u8bf4\uff0c\u5728\u4e0b\u6b66\u529f\u867d\u7136\u5e73\u5e73\uff0c\u4f46\u76f8\u4fe1\u8fd9\u751a\u4e48\u300a\u8fde\u57ce\u5251\u8c31\u300b\uff0c\u5bf9\u5728\u4e0b\u7684\u529f\u592b\u4e5f\u672a\u5fc5\u6709\u751a\u4e48\u597d\u5904\u3002\u201d\u8a00\u8fbe\u5e73\u9053\uff1a\u201c\u662f\uff0c\u662f\uff01\u6069\u516c\u6b66\u529f\u51fa\u795e\u5165\u5316\uff0c\u5df2\u662f\u5f53\u4e16\u65e0\u654c\uff0c\u90a3\u300a\u8fde\u57ce\u5251\u8c31\u300b\u4e5f\u4e0d\u8fc7\u662f\u4e00\u5957\u5251\u6cd5\u7684\u56fe\u8c31\u3002\u5c0f\u4eba\u5e08\u5144\u5f1f\u53ea\u56e0\u8fd9\u662f\u672c\u95e8\u7684\u529f\u592b\uff0c\u624d\u5341\u5206\u91cd\u89c6\uff0c\u5728\u5916\u4eba\u770b\u6765\uff0c\u90a3\u4e5f\u662f\u4e0d\u8db3\u4e00\u54c2\u7684\u4e86\u3002\u201d \u3000\u3000\u72c4\u4e91\u542c\u51fa\u4ed6\u8a00\u4e0d\u7531\u8877\uff0c\u5f53\u4e0b\u4e5f\u4e0d\u70b9\u7834\uff0c\u53c8\u95ee\uff1a\u201c\u542c\u8bf4\u90a3\u5927\u5c4b\u7684\u6240\u5728\uff0c\u672c\u6765\u662f\u4f60\u5e08\u5f1f\u621a\u8001\u524d\u8f88\u6240\u4f4f\u7684\u3002\u8fd9\u4f4d\u621a\u524d\u8f88\u5916\u53f7\u53eb\u4f5c\u2018\u94c1\u9501\u6a2a\u6c5f\u2019\uff0c\u90a3\u662f\u751a\u4e48\u610f\u601d\uff1f\u201d\u4ed6\u81ea\u5e7c\u8ddf\u5e08\u7236\u957f\u5927\uff0c\u89c1\u5e08\u7236\u5b9e\u662f\u4e2a\u5fe0\u539a\u8001\u5b9e\u7684\u4e61\u4e0b\u4eba\uff0c\u4f46\u4e01\u5178\u5374\u8bf4\u4ed6\u5341\u5206\u5de5\u4e8e\u5fc3\u8ba1\uff0c\u662f\u4ee5\u8981\u518d\u95ee\u4e00\u95ee\uff0c\u5230\u5e95\u4e01\u5178\u7684\u8bdd\u662f\u5426\u4f20\u95fb\u6709\u8bef\u3002 \u3000\u3000\u8a00\u8fbe\u5e73\u9053\uff1a\u201c\u6211\u5e08\u5f1f\u621a\u957f\u53d1\u5916\u53f7\u53eb\u4f5c\u2018\u94c1\u9501\u6a2a\u6c5f\u2019\uff0c\u90a3\u662f\u4eba\u5bb6\u8bf4\u4ed6\u8ba1\u8c0b\u591a\u7aef\uff0c\u5bf9\u4ed8\u4eba\u5f88\u8fa3\u624b\uff0c\u5c31\u50cf\u4e00\u6761\u5927\u94c1\u94fe\u9501\u4f4f\u4e86\u6c5f\u9762\uff0c\u53eb\u6c5f\u4e2d\u8239\u53ea\u4e0a\u53c8\u4e0a\u4e0d\u5f97\u3001\u4e0b\u53c8\u4e0b\u4e0d\u5f97\u7684\u610f\u601d\u3002\u201d \u3000\u3000\u72c4\u4e91\u5fc3\u4e2d\u4e00\u9635\u96be\u8fc7\uff0c\u6697\u9053\uff1a\u201c\u4e01\u5927\u54e5\u7684\u8bdd\u6ca1\u9519\uff0c\u6211\u5e08\u7236\u7adf\u662f\u8fd9\u6837\u7684\u4eba\u7269\uff0c\u6211\u4ece\u5c0f\u53d7\u4ed6\u6b3a\u9a97\uff0c\u4ed6\u59cb\u7ec8\u4e0d\u5411\u6211\u663e\u793a\u672c\u6765\u9762\u76ee\u3002\u4e0d\u8fc7\uff0c\u4e0d\u8fc7\u4ed6\u4e00\u76f4\u5f85\u6211\u5f88\u597d\uff0c\u9a97\u4e86\u6211\u4e5f\u6ca1\u6709\u751a\u4e48\u3002\u201d\u5fc3\u4e2d\u4ecd\u662f\u5b58\u7740\u4e00\u7ebf\u5e0c\u671b\uff0c\u53c8\u9053\uff1a\u201c\u6c5f\u6e56\u4e0a\u8fd9\u79cd\u5916\u53f7\uff0c\u4e5f\u672a\u5fc5\u9760\u5f97\u4f4f\uff0c\u6216\u8bb8\u662f\u621a\u5e08\u7236\u7684\u4ec7\u4eba\u7ed9\u4ed6\u53d6\u7684\u3002\u4f60\u548c\u4ee4\u5e08\u5f1f\u540c\u95e8\u5b66\u827a\uff0c\u81ea\u7136\u77e5\u9053\u4ed6\u7684\u6027\u60c5\u813e\u6c14\u3002\u5230\u5e95\u4ed6\u7684\u6027\u5b50\u5982\u4f55\uff1f\u201d \u3000\u3000\u8a00\u8fbe\u5e73\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u975e\u662f\u6211\u8981\u8bf4\u540c\u95e8\u7684\u574f\u8bdd\uff0c\u6069\u516c\u65e2\u7136\u95ee\u8d77\uff0c\u5728\u4e0b\u4e0d\u6562\u9690\u7792\u534a\u5206\u3002\u6211\u8fd9\u4e2a\u621a\u5e08\u5f1f\uff0c\u6837\u5b50\u4f3c\u4e4e\u662f\u5934\u6728\u725b\u8822\u9a6c\uff0c\u5fc3\u773c\u513f\u5374\u518d\u4e5f\u7075\u5de7\u4e0d\u8fc7\u3002\u5426\u5219\u90a3\u672c\u300a\u8fde\u57ce\u5251\u8c31\u300b\uff0c\u600e\u4e48\u4f1a\u7ed9\u4ed6\u5f97\u4e86\u53bb\u5462\uff1f\u201d \u3000\u3000\u72c4\u4e91\u70b9\u4e86\u70b9\u5934\uff0c\u9694\u4e86\u534a\u664c\uff0c\u624d\u9053\uff1a\u201c\u4f60\u600e\u77e5\u90a3\u300a\u8fde\u57ce\u5251\u8c31\u300b\u786e\u662f\u5728\u4ed6\u624b\u4e2d\uff1f\u4f60\u4eb2\u773c\u77a7\u89c1\u4e86\u4e48\uff1f\u201d \u3000\u3000\u8a00\u8fbe\u5e73\u9053\uff1a\u201c\u867d\u4e0d\u662f\u4eb2\u773c\u77a7\u89c1\uff0c\u4f46\u5c0f\u4eba\u4ed4\u7ec6\u7422\u78e8\uff0c\u4e00\u5b9a\u662f\u4ed6\u62ff\u53bb\u7684\u3002\u201d \u3000\u3000\u72c4\u4e91\u9053\uff1a\u201c\u6211\u542c\u4eba\u8bf4\uff0c\u4f60\u5e38\u7231\u626e\u4f5c\u4e5e\u4e10\uff0c\u662f\u4e0d\u662f\uff1f\u201d\u8a00\u8fbe\u5e73\u53c8\u662f\u4e00\u60ca\uff1a\u201c\u8fd9\u4eba\u597d\u5389\u5bb3\uff0c\u5c45\u7136\u8fde\u8fd9\u4ef6\u4e8b\u4e5f\u77e5\u9053\u4e86\u3002\u201d\u4fbf\u9053\uff1a\u201c\u6069\u516c\u4fe1\u8baf\u7075\u901a\uff0c\u5728\u4e0b\u7684\u4f5c\u4e3a\uff0c\u751a\u4e48\u90fd\u7792\u4e0d\u8fc7\u4f60\u3002\u521d\u65f6\u5728\u4e0b\u6599\u5f97\u8fd9\u672c\u300a\u8fde\u57ce\u5251\u8c31\u300b\u4e0d\u662f\u5728\u4e07\u5e08\u54e5\u624b\u4e2d\uff0c\u4fbf\u662f\u5728\u621a\u5e08\u5f1f\u624b\u4e2d\uff0c\u56e0\u6b64\u4fbf\u4e54\u88c5\u6539\u626e\uff0c\u6613\u5bb9\u4e3a\u4e10\uff0c\u5728\u6e58\u897f\u9102\u897f\u6765\u5f80\u63a2\u542c\u52a8\u9759\u3002\u201d\u72c4\u4e91\u9053\uff1a\u201c\u4e3a\u751a\u4e48\u4f60\u6599\u5b9a\u662f\u5728\u4ed6\u4eec\u4e8c\u4eba\u624b\u4e2d\uff1f\u201d\u8a00\u8fbe\u5e73\u9053\uff1a\u201c\u6211\u6069\u5e08\u4e34\u6b7b\u4e4b\u65f6\uff0c\u5c06\u8fd9\u5251\u8c31\u4ea4\u7ed9\u6211\u5e08\u5144\u5f1f\u4e09\u4eba\u2026\u2026\u201d \u3000\u3000\u72c4\u4e91\u60f3\u8d77\u4e01\u5178\u6240\u8bf4\uff0c\u90a3\u5929\u591c\u91cc\u957f\u6c5f\u7554\u4e07\u3001\u8a00\u3001\u621a\u4e09\u4eba\u5408\u529b\u8c0b\u6740\u5e08\u7236\u6885\u5ff5\u7b19\u4e4b\u4e8b\uff0c\u54fc\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u662f\u4ed6\u4eb2\u624b\u4ea4\u7ed9\u4f60\u4eec\u7684\u5417\uff1f\u6050\u6015\u2026\u2026\u6050\u6015\u2026\u2026\u4e0d\u89c1\u5f97\u7f62\uff1f\u4ed6\u597d\u597d\u6b7b\u7684\u5417\uff1f\u201d \u3000\u3000\u8a00\u8fbe\u5e73\u4e00\u8dc3\u800c\u8d77\uff0c\u6307\u7740\u4ed6\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u662f\u2026\u2026\u4e01\u2026\u2026\u4e01\u5178\u2026\u2026\u4e01\u5927\u7237\uff1f\u201d\u4e01\u5178\u5b89\u846c\u6885\u5ff5\u7b19\u7684\u8baf\u606f\u540e\u6765\u7ec8\u4e8e\u6cc4\u6f0f\uff0c\u662f\u4ee5\u8a00\u8fbe\u5e73\u542c\u5f97\u4ed6\u63ed\u9732\u81ea\u5df1\u5f11\u5e08\u7684\u5927\u7f6a\uff0c\u4fbf\u731c\u60f3\u4ed6\u662f\u4e01\u5178\u3002 \u3000\u3000\u72c4\u4e91\u6de1\u6de1\u9053\uff1a\u201c\u6211\u4e0d\u662f\u4e01\u5178\u3002\u4e01\u5927\u54e5\u5ac9\u6076\u5982\u4ec7\u3002\u4ed6\u2026\u2026\u4ed6\u4eb2\u773c\u89c1\u5230\u4f60\u4eec\u5e08\u5144\u5f1f\u4e09\u4eba\u5408\u529b\u6740\u6b7b\u5e08\u7236\uff0c\u5018\u82e5\u6211\u662f\u4e01\u5927\u54e5\uff0c\u4eca\u65e5\u5c31\u4e0d\u4f1a\u6551\u4f60\uff0c\u8ba9\u4f60\u6b7b\u5728\u4e07\u2026\u2026\u4e07\u9707\u5c71\u7684\u5251\u4e0b\u3002\u201d \u3000\u3000\u8a00\u8fbe\u5e73\u60ca\u7591\u4e0d\u5b9a\uff0c\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u662f\u8c01\uff1f\u201d\u72c4\u4e91\u9053\uff1a\u201c\u4f60\u4e0d\u7528\u7ba1\u6211\u662f\u8c01\u3002\u82e5\u8981\u4eba\u4e0d\u77e5\uff0c\u9664\u975e\u5df1\u83ab\u4e3a\u3002\u4f60\u4eec\u5408\u529b\u6740\u4e86\u5e08\u7236\u4e4b\u540e\uff0c\u62a2\u5f97\u300a\u8fde\u57ce\u5251\u8c31\u300b\uff0c\u540e\u6765\u600e\u6837\uff1f\u201d\u8a00\u8fbe\u5e73\u98a4\u58f0\u9053\uff1a\u201c\u4f60\u65e2\u7136\u751a\u4e48\u90fd\u77e5\u9053\u4e86\uff0c\u4f55\u5fc5\u518d\u6765\u95ee\u6211\uff1f\u201d\u72c4\u4e91\u9053\uff1a\u201c\u6709\u4e9b\u4e8b\u6211\u77e5\u9053\uff0c\u6709\u4e9b\u4e8b\u6211\u4e0d\u77e5\u3002\u8bf7\u4f60\u8001\u8001\u5b9e\u5b9e\u8bf4\u7f62\u3002\u82e5\u6709\u5047\u8bdd\uff0c\u6211\u603b\u4f1a\u67e5\u5bdf\u5f97\u51fa\u3002\u201d \u3000\u3000\u8a00\u8fbe\u5e73\u53c8\u60ca\u53c8\u6015\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u5982\u4f55\u6562\u6b3a\u9a97\u6069\u516c\uff1f\u6211\u5e08\u5144\u5f1f\u4e09\u4eba\u62ff\u5230\u300a\u8fde\u57ce\u5251\u8c31\u300b\u4e4b\u540e\uff0c\u4e00\u67e5\u4e4b\u4e0b\uff0c\u53d1\u89c9\u53ea\u6709\u5251\u8c31\uff0c\u6ca1\u6709\u5251\u8bc0\uff0c\u4ecd\u662f\u65e0\u7528\uff0c\u4fbf\u8ddf\u7740\u53bb\u8ffd\u67e5\u5251\u8bc0\u2026\u2026\u201d\u72c4\u4e91\u5fc3\u60f3\uff1a\u201c\u4e01\u5927\u54e5\u8a00\u9053\uff0c\u8fd9\u5251\u8bc0\u548c\u4e00\u4e2a\u5927\u5b9d\u85cf\u6709\u5173\u3002\u73b0\u4e0b\u6885\u5ff5\u7b19\u3001\u51cc\u5c0f\u59d0\u3001\u4e01\u5927\u54e5\u90fd\u5df2\u901d\u4e16\uff0c\u4e16\u4e0a\u5df2\u65e0\u4eba\u77e5\u9053\u5251\u8bc0\uff0c\u4f60\u4eec\u5140\u81ea\u5728\u4f5c\u68a6\u3002\u201d \u3000\u3000\u53ea\u542c\u8a00\u8fbe\u5e73\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u4e09\u4e2a\u4eba\u4f60\u4e0d\u653e\u5fc3\u6211\uff0c\u6211\u4e0d\u653e\u5fc3\u4f60\uff0c\u6bcf\u5929\u665a\u4e0a\u90fd\u5728\u4e00\u95f4\u623f\u7761\uff0c\u8fd9\u672c\u5251\u8c31\uff0c\u4fbf\u9501\u5728\u4e00\u53ea\u94c1\u76d2\u4e4b\u4e2d\u3002\u6211\u4eec\u628a\u94c1\u76d2\u9501\u4e0a\u7684\u94a5\u5319\u6295\u5165\u4e86\u5927\u6c5f\uff0c\u94c1\u76d2\u653e\u5728\u623f\u4e2d\u684c\u5b50\u7684\u62bd\u5c49\u91cc\uff0c\u94c1\u76d2\u4e0a\u53c8\u8fde\u7740\u4e09\u6839\u5c0f\u94c1\u94fe\uff0c\u5206\u7cfb\u5728\u4e09\u4eba\u7684\u624b\u4e0a\uff0c\u53ea\u8981\u6709\u8c01\u4e00\u52a8\uff0c\u5176\u4f59\u4e8c\u4eba\u4fbf\u60ca\u89c9\u4e86\u3002\u201d \u3000\u3000\u72c4\u4e91\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8fd9\u53ef\u9632\u5907\u5f97\u5468\u5bc6\u5f97\u5f88\u3002\u201d\u8a00\u8fbe\u5e73\u9053\uff1a\u201c\u54ea\u77e5\u9053\u8fd8\u662f\u51fa\u4e86\u4e71\u5b50\u3002\u201d\u72c4\u4e91\u95ee\u9053\uff1a\u201c\u53c8\u51fa\u4e86\u751a\u4e48\u4e71\u5b50\uff1f\u201d\u8a00\u8fbe\u5e73\u9053\uff1a\u201c\u8fd9\u4e00\u665a\u6211\u4eec\u5e08\u5144\u5f1f\u4e09\u4eba\u5728\u623f\u4e2d\u7761\u4e86\u4e00\u591c\uff0c\u6b21\u65e5\u6e05\u6668\uff0c\u4e07\u9707\u5c71\u5ffd\u7136\u5927\u53eb\uff1a\u2018\u5251\u8c31\u5462\uff1f\u5251\u8c31\u5462\uff1f\u2019\u6211\u4e00\u60ca\u8df3\u8d77\uff0c\u53ea\u89c1\u653e\u94c1\u76d2\u7684\u62bd\u5c49\u62c9\u5f00\u4e86\u6ca1\u5173\u4e0a\uff0c\u94c1\u76d2\u7684\u76d6\u5b50\u4e5f\u6253\u5f00\u4e86\uff0c\u76d2\u4e2d\u7684\u5251\u8c31\u5df2\u4e0d\u7ffc\u800c\u98de\u3002\u6211\u4eec\u4e09\u4eba\u5927\u60ca\u4e4b\u4e0b\uff0c\u62da\u547d\u7684\u8ffd\u5bfb\uff0c\u5374\u54ea\u91cc\u8fd8\u5bfb\u5f97\u7740\uff1f\u8fd9\u4ef6\u4e8b\u592a\u4e5f\u5947\u602a\uff0c\u623f\u4e2d\u7684\u95e8\u7a97\u4ecd\u662f\u5728\u5185\u7531\u94c1\u6263\u6263\u7740\uff0c\u597d\u7aef\u7aef\u7684\u6ca1\u52a8\uff0c\u56e0\u6b64\u5251\u8c31\u5b9a\u975e\u5916\u4eba\u76d7\u53bb\uff0c\u4e0d\u662f\u4e07\u5e08\u54e5\uff0c\u4fbf\u662f\u621a\u5e08\u5f1f\u4e0b\u7684\u624b\u4e86\u3002\u201d \u3000\u3000\u72c4\u4e91\u9053\uff1a\u201c\u679c\u771f\u5982\u6b64\uff0c\u4f55\u4e0d\u9ed1\u591c\u4e2d\u5f00\u4e86\u95e8\u7a97\uff0c\u88c5\u4f5c\u662f\u5916\u4eba\u4e0b\u7684\u624b\uff1f\u201d\u8a00\u8fbe\u5e73\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u4e09\u4eba\u7684\u624b\u8155\u90fd\u662f\u7528\u94c1\u94fe\u8fde\u7740\u7684\u3002\u6084\u6084\u8d77\u8eab\u53bb\u5f00\u62bd\u5c49\uff0c\u5f00\u94c1\u76d2\uff0c\u90a3\u662f\u53ef\u4ee5\u7684\uff0c\u8981\u8d70\u8fdc\u53bb\u5f00\u95e8\u5f00\u7a97\uff0c\u94c1\u94fe\u5c31\u4e0d\u591f\u957f\u4e86\u3002\u201d\u72c4\u4e91\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\u3002\u90a3\u4f60\u4eec\u600e\u4e48\u529e\uff1f\u201d\u8a00\u8fbe\u5e73\u9053\uff1a\u201c\u5251\u8c31\u5f97\u6765\u4e0d\u6613\uff0c\u6211\u4eec\u5f53\u7136\u4e0d\u80af\u5c31\u6b64\u7f62\u4f11\u3002\u4e09\u4e2a\u4eba\u4f60\u602a\u6211\uff0c\u6211\u602a\u4f60\uff0c\u5927\u5435\u4e86\u4e00\u573a\uff0c\u4f46\u8c01\u4e5f\u8bf4\u4e0d\u51fa\u751a\u4e48\u8bc1\u636e\uff0c\u53ea\u597d\u5206\u9053\u626c\u9573\u2026\u2026\u201d \u3000\u3000\u72c4\u4e91\u9053\uff1a\u201c\u6709\u4e00\u4ef6\u4e8b\u6211\u60f3\u4e0d\u660e\u767d\uff0c\u5012\u8981\u8bf7\u6559\u3002\u4f60\u4eec\u5e08\u7236\u65e2\u6709\u8fd9\u6837\u4e00\u672c\u5251\u8c31\uff0c\u8fdf\u65e9\u603b\u4f1a\u4f20\u7ed9\u4f60\u4eec\uff0c\u96be\u9053\u4ed6\u8981\u5e26\u5230\u68fa\u6750\u91cc\u53bb\u4e0d\u6210\uff1f\u4f55\u4ee5\u5b9a\u8981\u4e0b\u6b64\u6bd2\u624b\uff1f\u4f55\u4ee5\u8981\u6740\u4e86\u5e08\u7236\u6765\u62a2\u8fd9\u5251\u8c31\uff1f\u201d \u3000\u3000\u8a00\u8fbe\u5e73\u9053\uff1a\u201c\u6211\u5e08\u7236\uff0c\u6211\u5e08\u7236\uff0c\u5509\uff0c\u4ed6\u2026\u2026\u4ed6\u662f\u8001\u80e1\u6d82\u4e86\uff0c\u4ed6\u8ba4\u5b9a\u6211\u4eec\u5e08\u5144\u5f1f\u4e09\u4eba\u5fc3\u672f\u4e0d\u6b63\uff0c\u59cb\u7ec8\u4e0d\u4f20\u6211\u4eec\u8fd9\u5251\u8c31\u4e0a\u7684\u5251\u6cd5\uff0c\u773c\u770b\u4ed6\u662f\u5728\u53e6\u884c\u7269\u8272\u4f20\u4eba\uff0c\u751a\u81f3\u4e8e\u8981\u5c06\u672c\u95e8\u6b66\u529f\u5c3d\u6570\u4f20\u4e8e\u5916\u4eba\u3002\u6211\u4eec\u4e09\u4eba\u5fcd\u65e0\u53ef\u5fcd\uff0c\u8feb\u4e8e\u65e0\u5948\uff0c\u8fd9\u624d\u2026\u2026\u8fd9\u624d\u4e0b\u624b\u3002\u201d \u3000\u3000\u72c4\u4e91\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\u3002\u4f60\u540e\u6765\u53c8\u600e\u65ad\u5b9a\u5251\u8c31\u662f\u5728\u4f60\u621a\u5e08\u5f1f\u624b\u4e2d\uff1f\u201d \u3000\u3000\u8a00\u8fbe\u5e73\u9053\uff1a\u201c\u6211\u672c\u6765\u7591\u5fc3\u662f\u4e07\u9707\u5c71\u76d7\u7684\uff0c\u4ed6\u9996\u5148\u51fa\u58f0\u5927\u53eb\uff0c\u8d3c\u558a\u6349\u8d3c\uff0c\u6700\u662f\u53ef\u7591\u3002\u6211\u6697\u4e2d\u8ddf\u8e2a\u4ed6\uff0c\u8ddf\u5f97\u4e0d\u4e45\uff0c\u4fbf\u77e5\u4e0d\u662f\u4ed6\u3002\u56e0\u4e3a\u4ed6\u5728\u8ddf\u8e2a\u621a\u5e08\u5f1f\u3002\u5251\u8c31\u5018\u82e5\u662f\u4e07\u9707\u5c71\u8fd9\u53ae\u62ff\u53bb\u7684\uff0c\u4ed6\u4e0d\u4f1a\u53bb\u8ddf\u8e2a\u522b\u4eba\uff0c\u5b9a\u662f\u7acb\u5373\u8eb2\u5230\u7a77\u4e61\u50fb\u58e4\uff0c\u6216\u662f\u751a\u4e48\u6df1\u5c71\u8352\u8c37\u4e2d\u53bb\u7ec3\u4e86\u3002\u53ef\u662f\u6211\u6bcf\u6b21\u5728\u6697\u4e2d\u89c1\u5230\u4ed6\uff0c\u603b\u662f\u89c1\u4ed6\u54ac\u7259\u5207\u9f7f\uff0c\u795e\u8272\u5341\u5206\u7126\u8e81\u75db\u6068\uff0c\u4e8e\u662f\u6211\u6539\u800c\u53bb\u8ddf\u8e2a\u621a\u957f\u53d1\u3002\u201d \u3000\u3000\u72c4\u4e91\u9053\uff1a\u201c\u53ef\u5bfb\u5230\u751a\u4e48\u7ebf\u7d22\uff1f\u201d\u8a00\u8fbe\u5e73\u6447\u5934\u9053\uff1a\u201c\u8fd9\u621a\u957f\u53d1\u57ce\u5e9c\u592a\u6df1\uff0c\u6ca1\u534a\u70b9\u5f62\u8ff9\u9732\u4e86\u51fa\u6765\u3002\u6211\u66fe\u5077\u770b\u4ed6\u6559\u5f92\u513f\u548c\u5973\u513f\u7ec3\u5251\u3002\u4ed6\u6545\u610f\u88c5\u50bb\uff0c\u5c06\u51fa\u81ea\u5510\u8bd7\u7684\u5251\u62db\u540d\u79f0\u6539\u5f97\u72d7\u5c41\u4e0d\u901a\uff0c\u5f53\u771f\u8981\u7b11\u6389\u65c1\u4eba\u5927\u7259\u3002\u4e0d\u8fc7\u4ed6\u8d8a\u662f\u505a\u4f5c\uff0c\u6211\u8d8a\u77e5\u9053\u4ed6\u8def\u9053\u4e0d\u5bf9\u3002 \u3000\u3000\u6211\u4e00\u76f4\u9489\u4e86\u4ed6\u4e09\u5e74\uff0c\u4ed6\u59cb\u7ec8\u6ca1\u663e\u51fa\u534a\u5206\u7834\u7efd\u3002\u5f53\u4ed6\u51fa\u5916\u4e4b\u65f6\uff0c\u6211\u66fe\u6570\u6b21\u6f5c\u5165\u4ed6\u5bb6\u4e2d\u7ec6\u7ec6\u641c\u5bfb\uff0c\u53ef\u662f\u522b\u8bf4\u6ca1\u8fde\u57ce\u5251\u8c31\uff0c\u8fde\u5bfb\u5e38\u4e66\u672c\u5b50\u4e5f\u6ca1\u4e00\u672c\u3002\u563f\uff0c\u563f\uff01\u8fd9\u4f4d\u5e08\u5f1f\uff0c\u5f53\u771f\u662f\u597d\u5fc3\u8ba1\uff0c\u597d\u672c\u4e8b\uff01\u201d\u72c4\u4e91\u9053\uff1a\u201c\u540e\u6765\u600e\u6837\uff1f\u201d \u3000\u3000\u8a00\u8fbe\u5e73\u9053\uff1a\u201c\u540e\u6765\u561b\uff0c\u4e07\u9707\u5c71\u5ffd\u7136\u8981\u505a\u5bff\uff0c\u6d3e\u4e86\u4e2a\u5f1f\u5b50\u6765\u8bf7\u621a\u957f\u53d1\u5230\u8346\u5dde\u53bb\u5403\u5bff\u9152\u3002\u5f53\u7136\u54ea\uff0c\u505a\u5bff\u662f\u5047\uff0c\u67e5\u63a2\u5e08\u5f1f\u7684\u865a\u5b9e\u662f\u771f\u3002\u621a\u957f\u53d1\u5e26\u4e86\u5973\u513f\uff0c\u8fd8\u6709\u4e00\u4e2a\u50bb\u5934\u50bb\u8111\u7684\u5f1f\u5b50\u53eb\u751a\u4e48\u72c4\u4e91\u7684\u4e00\u5757\u513f\u53bb\u3002\u9152\u7b75\u4e4b\u95f4\uff0c\u8fd9\u72c4\u4e91\u548c\u4e07\u5bb6\u7684\u516b\u4e2a\u5f1f\u5b50\u6253\u4e86\u8d77\u6765\uff0c\u9732\u51fa\u4e86\u4e09\u62db\u7cbe\u5999\u7684\u5251\u672f\uff0c\u5f15\u8d77\u4e86\u4e07\u9707\u5c71\u7684\u7591\u5fc3\u2026\u2026\u6069\u516c\uff0c\u4f60\u8bf4\u751a\u4e48\uff1f\u201d\u72c4\u4e91\u51c4\u7136\u6447\u4e86\u6447\u5934\u3002\u8a00\u8fbe\u5e73\u7eed\u9053\uff1a\u201c\u4e8e\u662f\u4e07\u9707\u5c71\u5c06\u621a\u957f\u53d1\u8bf7\u5230\u4e66\u623f\u4e2d\u53bb\u8c08\u8bba\uff0c\u4e24\u4eba\u4f60\u4e00\u8a00\u6211\u4e00\u8bed\u7684\u8bf4\u7ffb\u4e86\u8138\u3002\u621a\u957f\u53d1\u51fa\u624b\u5c06\u4e07\u9707\u5c71\u523a\u4f24\uff0c\u4ece\u6b64\u4e0d\u77e5\u6240\u8e2a\u3002\u5947\u602a\uff0c\u771f\u662f\u5947\u602a\uff0c\u771f\u5947\u602a\u4e4b\u6781\u4e86\u3002\u201d \u3000\u3000\u72c4\u4e91\u9053\uff1a\u201c\u751a\u4e48\u5947\u602a\uff1f\u201d\u8a00\u8fbe\u5e73\u9053\uff1a\u201c\u621a\u957f\u53d1\u4ece\u6b64\u4fbf\u65e0\u5f71\u65e0\u8e2a\uff0c\u4e0d\u77e5\u8eb2\u5230\u4e86\u4f55\u5904\u3002\u621a\u957f\u53d1\u53bb\u8346\u5dde\u4e4b\u65f6\uff0c\u51b3\u4e0d\u4f1a\u5c06\u76d7\u6765\u7684\u5251\u8c31\u968f\u8eab\u643a\u5e26\uff0c\u5b9a\u662f\u57cb\u85cf\u5728\u8fd9\u91cc\u4e00\u5904\u6781\u9690\u853d\u7684\u5730\u65b9\u3002\u6211\u672c\u6765\u6599\u60f3\u4ed6\u523a\u4f24\u4e07\u9707\u5c71\u540e\uff0c\u4e00\u5b9a\u8fde\u591c\u8d76\u56de\u6b64\u95f4\uff0c\u53d6\u4e86\u5251\u8c31\u518d\u884c\u8fdc\u8d70\u9ad8\u98de\uff0c\u662f\u4ee5\u4e00\u53d1\u751f\u4e8b\u6545\uff0c\u6211\u7acb\u5373\u5907\u4e86\u5feb\u9a6c\uff0c\u62a2\u5148\u6765\u5230\u8fd9\u91cc\u7b49\u5019\uff0c\u77a7\u4ed6\u8fd9\u5251\u8c31\u653e\u5728\u54ea\u91cc\uff0c\u4ee5\u4fbf\u4fdf\u673a\u4e0b\u624b\uff0c\u53ef\u662f\u5de6\u7b49\u53f3\u7b49\uff0c\u4ed6\u59cb\u7ec8\u6ca1\u6709\u73b0\u8eab\u3002\u4e00\u8fc7\u51e0\u5e74\uff0c\u770b\u6765\u4ed6\u662f\u6c38\u8fdc\u4e0d\u4f1a\u56de\u6765\u4e86\uff0c\u6211\u4fbf\u8001\u5b9e\u4e0d\u5ba2\u6c14\uff0c\u5728\u8fd9\u91cc\u6405\u4ed6\u4e2a\u5929\u7ffb\u5730\u8986\uff0c\u60f3\u8981\u6398\u90a3\u5251\u8c31\u51fa\u6765\u3002 \u3000\u3000\u53ef\u662f\u82b1\u4e86\u65e0\u6570\u5fc3\u8840\uff0c\u534a\u70b9\u7ed3\u679c\u4e5f\u6ca1\u6709\u3002\u82e5\u4e0d\u662f\u6069\u516c\u51fa\u624b\u76f8\u6551\uff0c\u59d3\u8a00\u7684\u4eca\u65e5\u8fde\u6027\u547d\u4e5f\u9001\u5728\u8fd9\u91cc\u4e86\u3002\u563f\uff0c\u563f\uff0c\u6211\u90a3\u4e07\u5e08\u54e5\u53ef\u5f53\u771f\u8fa3\u624b\uff01\u201d \u3000\u3000\u72c4\u4e91\u9053\uff1a\u201c\u7167\u4f60\u770b\u6765\uff0c\u4f60\u90a3\u621a\u5e08\u5f1f\u73b0\u4e0b\u5230\u4e86\u4f55\u5904\uff1f\u201d \u3000\u3000\u8a00\u8fbe\u5e73\u6447\u5934\u9053\uff1a\u201c\u8fd9\u4e2a\u6211\u53ef\u771f\u731c\u60f3\u4e0d\u51fa\u4e86\u3002\u591a\u534a\u662f\u5929\u7f51\u6062\u6062\uff0c\u758f\u800c\u4e0d\u6f0f\uff0c\u5728\u751a\u4e48\u5730\u65b9\u4e00\u75c5\u4e0d\u8d77\uff0c\u53c8\u8bf4\u4e0d\u5b9a\u9047\u5230\u751a\u4e48\u610f\u5916\uff0c\u7ed9\u8c7a\u72fc\u864e\u8c79\u5403\u6389\u4e86\u3002\u201d \u3000\u3000\u72c4\u4e91\u89c1\u4ed6\u6ee1\u8138\u5e78\u707e\u4e50\u7978\u7684\u795e\u6c14\uff0c\u663e\u5f97\u5341\u5206\u6b22\u559c\uff0c\u5fc3\u4e2d\u5927\u662f\u538c\u6076\uff0c\u4f46\u8f6c\u5ff5\u4e00\u60f3\uff0c\u5e08\u7236\u97f3\u8baf\u5168\u65e0\uff0c\u591a\u534a\u786e\u5df2\u906d\u4e86\u4e0d\u5e78\uff0c\u4fbf\u7ad9\u8d77\u8eab\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u4f60\u4e0d\u52a0\u9690\u7792\uff0c\u5728\u4e0b\u8981\u544a\u8f9e\u4e86\u3002\u201d \u3000\u3000\u8a00\u8fbe\u5e73\u606d\u606d\u656c\u656c\u7684\u4f5c\u4e86\u4e09\u63d6\uff0c\u9053\uff1a\u201c\u6069\u516c\u5927\u6069\u5927\u5fb7\uff0c\u8a00\u8fbe\u5e73\u6c38\u4e0d\u6562\u5fd8\u3002\u201d \u3000\u3000\u72c4\u4e91\u9053\uff1a\u201c\u8fd9\u79cd\u5c0f\u4e8b\uff0c\u4e5f\u4e0d\u5fc5\u653e\u5728\u5fc3\u4e0a\u3002\u4f55\u51b5\u2026\u2026\u4f55\u51b5\u4f60\u4ece\u524d\u2026\u2026\u4f60\u5728\u8fd9\u91cc\u517b\u4f24\uff0c\u90a3\u4e07\u9707\u5c71\u51b3\u8ba1\u627e\u4f60\u4e0d\u5230\u7684\uff0c\u5c3d\u7ba1\u653e\u5fc3\u597d\u4e86\u3002\u201d \u3000\u3000\u8a00\u8fbe\u5e73\u7b11\u9053\uff1a\u201c\u8fd9\u4f1a\u513f\u591a\u534a\u4ed6\u6025\u5f97\u4fbf\u5982\u70ed\u9505\u4e0a\u8682\u8681\u4e00\u822c\uff0c\u4e5f\u987e\u4e0d\u5230\u6765\u627e\u6211\u4e86\u3002\u201d\u72c4\u4e91\u5947\u9053\uff1a\u201c\u4e3a\u751a\u4e48\uff1f\u201d\u8a00\u8fbe\u5e73\u5fae\u7b11\u9053\uff1a\u201c\u6211\u90a3\u6bd2\u874e\u4f24\u4e86\u4ed6\u513f\u5b50\u7684\u624b\uff0c\u5fc5\u987b\u8fde\u7eed\u6577\u836f\u5341\u6b21\uff0c\u65b9\u80fd\u9664\u5c3d\u6bd2\u6027\u3002\u53ea\u6577\u4e00\u6b21\uff0c\u6709\u751a\u4e48\u7528\uff1f\u201d \u3000\u3000\u72c4\u4e91\u5fae\u5fae\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u90a3\u4e48\u4e07\u572d\u4f1a\u6027\u547d\u4e0d\u4fdd\u4e48\uff1f\u201d\u8a00\u8fbe\u5e73\u751a\u662f\u5f97\u610f\uff0c\u9053\uff1a\u201c\u8fd9\u79cd\u82b1\u6591\u6bd2\u874e\uff0c\u5f53\u771f\u662f\u975e\u540c\u5c0f\u53ef\uff0c\u90a3\u662f\u897f\u57df\u56de\u7586\u4f20\u6765\u7684\u5f02\u79cd\uff0c\u5999\u5728\u8fd9\u4e07\u572d\u4e0d\u4f1a\u4e00\u65f6\u4fbf\u6b7b\uff0c\u8981\u4ed6\u547c\u53f7\u547b\u541f\u8db3\u8db3\u4e00\u4e2a\u6708\uff0c\u8fd9\u624d\u4e86\u5e10\u3002\u54c8\u54c8\uff0c\u5999\u6781\uff0c\u5999\u6781\uff01\u201d \u3000\u3000\u72c4\u4e91\u9053\uff1a\u201c\u8981\u4e00\u4e2a\u6708\u624d\u6b7b\uff0c\u90a3\u5c31\u4e0d\u8981\u7d27\u4e86\uff0c\u4ed6\u53bb\u8bf7\u5230\u826f\u533b\uff0c\u603b\u6709\u89e3\u6bd2\u7684\u6cd5\u5b50\u3002\u201d \u3000\u3000\u8a00\u8fbe\u5e73\u9053\uff1a\u201c\u6069\u516c\u6709\u6240\u4e0d\u77e5\u3002\u8fd9\u79cd\u6bd2\u874e\u662f\u6211\u81ea\u5df1\u517b\u5927\u7684\uff0c\u81ea\u5e7c\u4fbf\u5582\u5b83\u670d\u98df\u5404\u79cd\u89e3\u836f\uff0c\u874e\u5b50\u4e60\u4e8e\u89e3\u836f\u7684\u836f\u6027\uff0c\u5bfb\u5e38\u89e3\u836f\u7528\u5c06\u4e0a\u53bb\u4fbf\u5168\u65e0\u6548\u9a8c\uff0c\u4efb\u4ed6\u533b\u9053\u518d\u9ad8\u660e\u7684\u533b\u751f\uff0c\u4e5f\u53ea\u662f\u7528\u6cbb\u6bd2\u866b\u7684\u836f\u7269\u53bb\u89e3\u6bd2\uff0c\u90a3\u6709\u5c41\u7528\uff1f\u53ea\u6709\u4e00\u79cd\u72ec\u95e8\u89e3\u836f\uff0c\u662f\u8fd9\u874e\u5b50\u6ca1\u670d\u98df\u8fc7\u7684\uff0c\u90a3\u624d\u6709\u7528\uff0c\u4e16\u4e0a\u9664\u6211\u4e4b\u5916\uff0c\u6ca1\u7b2c\u4e8c\u4e2a\u77e5\u9053\u8fd9\u89e3\u836f\u7684\u914d\u6cd5\u3002\u54c8\u54c8\uff0c\u54c8\u54c8\uff01\u201d \u3000\u3000\u72c4\u4e91\u4fa7\u76ee\u800c\u89c6\uff0c\u5fc3\u60f3\uff1a\u201c\u8fd9\u4e2a\u4eba\u5fc3\u80a0\u5982\u6b64\u6076\u6bd2\uff0c\u771f\u662f\u53ef\u6015\uff01 \u3000\u3000\u4e0b\u6b21\u8bf4\u4e0d\u5b9a\u6211\u4f1a\u7ed9\u4ed6\u7684\u6bd2\u874e\u87ab\u4e2d\u3002\u4e01\u5927\u54e5\u5e38\u8bf4\uff0c\u5728\u6c5f\u6e56\u4e0a\u884c\u8d70\uff0c\u5bb3\u4eba\u4e4b\u5fc3\u4e0d\u53ef\u6709\uff0c\u9632\u4eba\u4e4b\u5fc3\u4e0d\u53ef\u65e0\u3002\u8fd8\u662f\u95ee\u4ed6\u62ff\u4e9b\u89e3\u836f\u653e\u5728\u8eab\u8fb9\uff0c\u8fd9\u53eb\u505a\u6709\u5907\u65e0\u60a3\u3002\u201d\u4fbf\u9053\uff1a\u201c\u4f60\u8fd9\u74f6\u89e3\u836f\uff0c\u7ed9\u4e86\u6211\u7f62\uff01\u201d \u3000\u3000\u8a00\u8fbe\u5e73\u9053\uff1a\u201c\u662f\uff0c\u662f\uff01\u201d\u53ef\u662f\u5e76\u4e0d\u5f53\u5373\u53d6\u51fa\uff0c\u95ee\u9053\uff1a\u201c\u6069\u516c\u8981\u8fd9\u89e3\u836f\uff0c\u4e0d\u77e5\u6709\u751a\u4e48\u7528\u9014\uff1f\u201d\u72c4\u4e91\u9053\uff1a\u201c\u4f60\u7684\u6bd2\u874e\u5341\u5206\u5389\u5bb3\uff0c\u8bf4\u4e0d\u5b9a\u4e00\u4e2a\u4e0d\u5c0f\u5fc3\u6211\u81ea\u5df1\u78b0\u5230\u4e86\uff0c\u8eab\u8fb9\u6709\u4e00\u74f6\u89e3\u836f\uff0c\u90a3\u5c31\u653e\u5fc3\u4e9b\u4e86\u3002\u201d\u8a00\u8fbe\u5e73\u8138\u8272\u5c34\u5c2c\uff0c\u8d54\u7b11\u9053\uff1a\u201c\u6069\u516c\u4e8e\u5c0f\u4eba\u6709\u6551\u547d\u4e4b\u6069\uff0c\u5c0f\u4eba\u600e\u6562\u52a0\u5bb3\uff1f\u6069\u516c\u8fd9\u662f\u591a\u7591\u4e86\u3002\u201d\u72c4\u4e91\u4f38\u624b\u51fa\u53bb\uff0c\u8bf4\u9053\uff1a\u201c\u5907\u800c\u4e0d\u7528\uff0c\u653e\u5728\u8eab\u8fb9\uff0c\u90a3\u4e5f\u4e0d\u59a8\u3002\u201d\u8a00\u8fbe\u5e73\u9053\uff1a\u201c\u662f\uff0c\u662f\uff01\u201d\u53ea\u5f97\u53d6\u51fa\u89e3\u836f\uff0c\u9012\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u72c4\u4e91\u4e0b\u5f97\u5cf0\u6765\uff0c\u53c8\u5230\u90a3\u5ea7\u5927\u5c4b\u53bb\u5bdf\u770b\uff0c\u53ea\u89c1\u5c4b\u4e2d\u4f17\u4e61\u6c11\u65e9\u5df2\u6563\u53bb\uff0c\u90a3\u7ba1\u5bb6\u548c\u5de5\u5934\u4e5f\u5df2\u4e0d\u77e5\u53bb\u5411\uff0c\u7a7a\u8361\u8361\u7684\u518d\u65e0\u4e00\u4eba\u3002 \u3000\u3000\u72c4\u4e91\u5fc3\u9053\uff1a\u201c\u5e08\u7236\u5df2\u6b7b\uff0c\u5e08\u59b9\u5df2\u5ac1\uff0c\u8fd9\u5730\u65b9\u6211\u662f\u518d\u4e5f\u4e0d\u4f1a\u6765\u7684\u4e86\u3002\u201d \u3000\u3000\u8d70\u51fa\u5927\u5c4b\uff0c\u6cbf\u7740\u6eaa\u8fb9\u5411\u897f\u5317\u8d70\u53bb\u3002\u884c\u51fa\u6570\u5341\u4e08\uff0c\u56de\u5934\u4e00\u671b\uff0c\u8fd9\u65f6\u4e1c\u65b9\u592a\u9633\u521a\u521a\u5347\u8d77\uff0c\u9633\u5149\u7167\u5c04\u5728\u5c4b\u524d\u7684\u6768\u6811\u3001\u69d0\u6811\u4e4b\u4e0a\uff0c\u6eaa\u6c34\u4e2d\u6cdb\u51fa\u70b9\u70b9\u95ea\u5149\uff0c\u8fd9\u756a\u60c5\u666f\uff0c\u4ece\u5c0f\u4fbf\u770b\u719f\u4e86\u7684\uff0c\u4e0d\u7531\u5f97\u53c8\u60f3\uff1a\u201c\u4ece\u4eca\u800c\u540e\uff0c\u8fd9\u5730\u65b9\u6211\u662f\u518d\u4e5f\u4e0d\u4f1a\u6765\u7684\u4e86\u3002\u201d \u3000\u3000\u4ed6\u7406\u4e00\u7406\u80cc\u4e0a\u7684\u5305\u88f9\uff0c\u5bfb\u601d\uff1a\u201c\u773c\u4e0b\u8fd8\u6709\u4e00\u4ef6\u5fc3\u4e8b\u672a\u4e86\uff0c\u987b\u5f97\u5c06\u4e01\u5927\u54e5\u7684\u9aa8\u7070\uff0c\u9001\u53bb\u548c\u51cc\u5c0f\u59d0\u7684\u9057\u4f53\u5408\u846c\uff0c\u8fd9\u4e14\u53bb\u8346\u5dde\u8d70\u4e00\u906d\u3002\u4e07\u572d\u8fd9\u5c0f\u5b50\u5bb3\u5f97\u6211\u82e6\uff0c\u597d\u5728\u6076\u4eba\u81ea\u6709\u6076\u4eba\u78e8\uff0c\u6211\u4e5f\u4e0d\u7528\u4eb2\u624b\u62a5\u4ec7\u3002\u8a00\u8fbe\u5e73\u8bf4\u4ed6\u8981\u547b\u541f\u53f7\u53eb\u4e00\u4e2a\u6708\u624d\u6b7b\uff0c\u5374\u4e0d\u77e5\u662f\u771f\u662f\u5047\u3002\u5018\u82e5\u4ed6\u547d\u5927\uff0c\u533b\u751f\u7ed9\u6cbb\u597d\u4e86\uff0c\u6211\u8fd8\u5f97\u7ed9\u4ed6\u8865\u4e0a\u4e00\u5251\uff0c\u53d6\u4ed6\u72d7\u547d\u3002\u201d \u3000\u3000\u81ea\u4ece\u6628\u665a\u89c1\u5230\u4e07\u9707\u5c71\u4e0e\u8a00\u8fbe\u5e73\u6597\u5251\uff0c\u4ed6\u624d\u5bf9\u81ea\u5df1\u7684\u6b66\u529f\u6709\u4e86\u4fe1\u5fc3\u3002"}),showOnHomepage:!0},{name:"\u5341\u3000\u300a\u5510\u8bd7\u9009\u8f91\u300b",content:(0,a.jsx)(a.Fragment,{children:"\u6e58\u897f\u548c\u8346\u5dde\u76f8\u9694\u4e0d\u8fdc\uff0c\u6570\u65e5\u4e4b\u540e\uff0c\u4fbf\u5230\u4e86\u8346\u5dde\u3002\u8fd9\u4e00\u6761\u8def\uff0c\u662f\u5f53\u5e74\u4ed6\u968f\u540c\u5e08\u7236\u548c\u5e08\u59b9\u66fe\u7ecf\u8d70\u8fc7\u7684\u3002\u5c71\u5ddd\u4ecd\u7136\u662f\u8fd9\u6837\uff0c\u9053\u8def\u4ecd\u7136\u662f\u8fd9\u6837\u3002\u5f53\u5e74\u884c\u8d70\u4e4b\u65f6\uff0c\u8def\u4e0a\u6ee1\u662f\u621a\u82b3\u7684\u7b11\u58f0\u3002\u8fd9\u4e00\u6b21\uff0c\u4ece\u9ebb\u6eaa\u94fa\u5230\u8346\u5dde\uff0c\u4ed6\u6ca1\u6709\u542c\u5230\u4e00\u4e0b\u7b11\u58f0\u3002\u5f53\u7136\u6709\u4eba\u7b11\uff0c\u4e0d\u8fc7\uff0c\u4ed6\u6ca1\u6709\u542c\u89c1\u3002 \u3000\u3000\u5728\u57ce\u5916\u4e00\u6253\u542c\uff0c\u77e5\u9053\u51cc\u9000\u601d\u4ecd\u662f\u505a\u7740\u77e5\u5e9c\u3002\u72c4\u4e91\u4ecd\u662f\u8fd9\u4e48\u6ee1\u8138\u6c61\u6ce5\uff0c\u63a9\u4f4f\u4e86\u672c\u6765\u9762\u76ee\uff0c\u8d70\u8fdb\u57ce\u53bb\u3002 \u3000\u3000\u7b2c\u4e00\u4e2a\u5ff5\u5934\u662f\uff1a\u201c\u6211\u8981\u4eb2\u773c\u77a7\u77a7\u4e07\u572d\u600e\u6837\u53d7\u82e6\u3002\u4ed6\u7684\u6bd2\u4f24\u662f\u4e0d\u662f\u6cbb\u597d\u4e86\uff1f\u4e5f\u4e0d\u77e5\u4ed6\u662f\u4e0d\u662f\u5df2\u7ecf\u56de\u6765\u3002\u8bf4\u4e0d\u5b9a\u8fd8\u7559\u5728\u6e56\u5357\u6cbb\u4f24\u3002\u201d \u3000\u3000\u8e31\u5230\u4e07\u5bb6\u95e8\u53e3\uff0c\u8fdc\u8fdc\u671b\u89c1\u6c88\u57ce\u5306\u5306\u4ece\u5927\u95e8\u4e2d\u51fa\u6765\uff0c\u795e\u60c5\u663e\u5f97\u5f88\u662f\u6025\u907d\u3002\u72c4\u4e91\u5fc3\u9053\uff1a\u201c\u6c88\u57ce\u65e2\u5728\u8fd9\u91cc\uff0c\u4e07\u572d\u60f3\u6765\u4e5f\u5df2\u56de\u5bb6\u3002\u4e00\u5230\u5929\u9ed1\uff0c\u6211\u4fbf\u53bb\u63a2\u63a2\u3002\u201d\u4e8e\u662f\u8d70\u5411\u90a3\u4e2a\u5e9f\u56ed\u3002 \u3000\u3000\u5e9f\u56ed\u79bb\u4e07\u5bb6\u4e0d\u8fdc\uff0c\u5f53\u65e5\u4e01\u5178\u901d\u4e16\u3001\u6740\u5468\u573b\u3001\u6740\u803f\u5929\u9738\u3001\u6740\u9a6c\u5927\u9e23\uff0c\u90fd\u662f\u5728\u8fd9\u5e9f\u56ed\u4e4b\u4e2d\uff0c\u6b64\u756a\u65e7\u5730\u91cd\u6765\uff0c\u53ea\u89c1\u904d\u5730\u8352\u8349\u5982\u6545\uff0c\u904d\u5730\u74e6\u783e\u5982\u6545\u3002\u4ed6\u8d70\u5230\u90a3\u682a\u8001\u6885\u4e4b\u65c1\uff0c\u629a\u6478\u51f9\u51f9\u51f8\u51f8\u7684\u6811\u5e72\uff0c\u5fc3\u9053\uff1a\u201c\u90a3\u4e00\u65e5\u4e01\u5927\u54e5\u5728\u8fd9\u682a\u8001\u6885\u6811\u4e0b\u901d\u4e16\uff0c\u6885\u6811\u4ecd\u662f\u8fd9\u526f\u6a21\u6837\uff0c\u534a\u70b9\u4e5f\u6ca1\u53d8\u3002\u4e01\u5927\u54e5\u5374\u5df2\u9aa8\u5316\u6210\u7070\u3002\u201d \u3000\u3000\u5f53\u4e0b\u5750\u5728\u6885\u6811\u4e0b\u95ed\u76ee\u800c\u7761\u3002\u7761\u5230\u4e8c\u66f4\u65f6\u5206\uff0c\u4ece\u6000\u4e2d\u53d6\u51fa\u4e9b\u5e72\u7cae\u6765\u5403\u4e86\uff0c\u51fa\u4e86\u5e9f\u56ed\uff0c\u5f84\u5411\u4e07\u5bb6\u800c\u6765\u3002\u7ed5\u5230\u4e07\u5bb6\u540e\u95e8\uff0c\u8d8a\u5899\u800c\u5165\uff0c\u5230\u4e86\u540e\u82b1\u56ed\u4e2d\uff0c\u4e0d\u7531\u5f97\u5fc3\u4e2d\u4e00\u9635\u9178\u695a\uff1a\u201c\u90a3\u65e5\u6211\u8eab\u53d7\u91cd\u4f24\uff0c\u8eb2\u5728\u67f4\u623f\u4e4b\u4e2d\u3002\u5e08\u59b9\u4e0d\u52a9\u6211\u6551\u6211\uff0c\u5df2\u7b97\u5f97\u72e0\u5fc3\uff0c\u5374\u53cd\u800c\u53bb\u53eb\u4e08\u592b\u6765\u6740\u6211\u3002\u201d\u6b63\u8981\u4e3e\u6b65\u800c\u524d\uff0c\u5ffd\u89c1\u592a\u6e56\u77f3\u65c1\u6709\u4e09\u70b9\u706b\u5149\u95ea\u52a8\u3002 \u3000\u3000\u4ed6\u7acb\u5373\u5f80\u6811\u540e\u4e00\u7f29\uff0c\u5411\u706b\u5149\u5904\u671b\u53bb\uff0c\u51dd\u76ee\u95f4\uff0c\u89c1\u4e09\u70b9\u706b\u5149\u662f\u9999\u7089\u4e2d\u4e09\u679d\u70b9\u71c3\u4e86\u7684\u7ebf\u9999\u3002\u9999\u7089\u653e\u5728\u4e00\u5f20\u5c0f\u51e0\u4e0a\u3002\u51e0\u524d\u6709\u4e24\u4e2a\u4eba\u8dea\u7740\u5411\u5929\u78d5\u5934\uff0c\u4e00\u4f1a\u513f\u7ad9\u8d77\u8eab\u6765\u3002\u72c4\u4e91\u770b\u5f97\u5206\u660e\uff0c\u4e00\u4e2a\u4fbf\u662f\u621a\u82b3\uff0c\u53e6\u4e00\u4e2a\u662f\u5c0f\u5973\u5b69\uff0c\u5979\u7684\u5973\u513f\uff0c\u4e5f\u662f\u53eb\u505a\u201c\u7a7a\u5fc3\u83dc\u201d\u7684\u3002 \u3000\u3000\u53ea\u542c\u5f97\u621a\u82b3\u8f7b\u8f7b\u7977\u795d\uff1a\u201c\u7b2c\u4e00\u70b7\u9999\uff0c\u6c42\u8001\u5929\u7237\u4fdd\u4f51\u6211\u592b\u541b\u5f97\u8131\u82e6\u96be\uff0c\u89e3\u80bf\u53bb\u6bd2\uff0c\u4e0d\u518d\u53d7\u8fd9\u874e\u6bd2\u4fb5\u5bb3\u7684\u75db\u695a\u3002\u7a7a\u5fc3\u83dc\uff0c\u4f60\u8bf4\u554a\uff0c\u8bf4\u6c42\u6c42\u5929\u83e9\u8428\u4fdd\u4f51\u7239\u7239\u75c5\u597d\u3002\u201d\u5c0f\u5973\u5b69\u9053\uff1a\u201c\u662f\uff0c\u5988\u5988\uff0c\u6c42\u6c42\u5929\u83e9\u8428\u4fdd\u4f51\uff0c\u53eb\u7239\u7239\u4e0d\u75db\u75db\u4e86\uff0c\u4e0d\u53eb\u53eb\u4e86\u3002\u201d\u72c4\u4e91\u76f8\u9694\u867d\u7136\u4e0d\u8fd1\uff0c\u5979\u6bcd\u5973\u4fe9\u7684\u8bf4\u8bdd\u5374\u542c\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u5f97\u77e5\u4e07\u572d\u4e2d\u6bd2\u540e\u679c\u7136\u4ecd\u5728\u53d7\u82e6\uff0c\u5fc3\u4e2d\u65e2\u611f\u5230\u5e78\u707e\u4e50\u7978\u7684\u559c\u6b22\uff0c\u53c8\u607c\u6068\u621a\u82b3\u5bf9\u4e08\u592b\u5982\u6b64\u60c5\u4e49\u6df1\u91cd\u3002 \u3000\u3000\u53ea\u542c\u621a\u82b3\u8bf4\u9053\uff1a\u201c\u7b2c\u4e8c\u70b7\u9999\uff0c\u6c42\u8001\u5929\u7237\u4fdd\u4f51\u6211\u7239\u7239\u5e73\u5b89\uff0c\u65e0\u707e\u65e0\u96be\uff0c\u65e9\u65e5\u5f52\u6765\u3002\u7a7a\u5fc3\u83dc\uff0c\u4f60\u8bf4\u8bf7\u5929\u83e9\u8428\u4fdd\u4f51\u5916\u516c\u957f\u547d\u767e\u5c81\u3002\u201d\u5c0f\u5973\u5b69\u9053\uff1a\u201c\u662f\uff0c\u5916\u516c\uff0c\u4f60\u5feb\u5feb\u56de\u6765\uff0c\u4f60\u4e3a\u751a\u4e48\u4e0d\u56de\u6765\u554a\uff1f\u201d\u621a\u82b3\u9053\uff1a\u201c\u6c42\u5929\u83e9\u8428\u4fdd\u4f51\u3002\u201d\u5c0f\u5973\u5b69\u9053\uff1a\u201c\u5929\u83e9\u8428\u4fdd\u4f51\u5916\u516c\uff0c\u8fd8\u8981\u4fdd\u4f51\u7237\u7237\u548c\u7239\u7239\u3002\u201d\u5979\u4ece\u6765\u6ca1\u89c1\u8fc7\u621a\u957f\u53d1\uff0c\u5988\u5988\u8981\u5979\u6c42\u7977\uff0c\u5979\u5fc3\u4e2d\u8bb0\u6302\u7684\u5374\u662f\u81ea\u5df1\u7684\u7956\u7236\u548c\u7236\u4eb2\u3002 \u3000\u3000\u621a\u82b3\u505c\u4e86\u7247\u523b\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u8fd9\u7b2c\u4e09\u70b7\u9999\uff0c\u6c42\u8001\u5929\u7237\u4fdd\u4f51\u4ed6\u5e73\u5b89\uff0c\u4fdd\u4f51\u4ed6\u4e8b\u4e8b\u5982\u610f\uff0c\u4fdd\u4f51\u4ed6\u65e9\u5a36\u8d24\u59bb\uff0c\u65e9\u751f\u8d35\u5b50\u2026\u2026\u201d\u8bf4\u5230\u8fd9\u91cc\uff0c\u58f0\u97f3\u4e0d\u7981\u54fd\u54bd\u4e86\uff0c\u4f38\u8d77\u8863\u8896\uff0c\u62ed\u4e86\u62ed\u773c\u6cea\u3002\u5c0f\u5973\u5b69\u9053\uff1a\u201c\u5988\u5988\uff0c\u4f60\u53c8\u60f3\u8d77\u8205\u8205\u4e86\u3002\u201d\u621a\u82b3\u9053\uff1a\u201c\u4f60\u8bf4\uff0c\u6c42\u5929\u8001\u7237\u4fdd\u4f51\u7a7a\u5fc3\u83dc\u8205\u8205\u5e73\u5b89\u2026\u2026\u201d \u3000\u3000\u72c4\u4e91\u542c\u5979\u7977\u795d\u7b2c\u4e09\u70b7\u9999\u65f6\uff0c\u6b63\u81ea\u5947\u602a\uff1a\u201c\u5979\u5728\u66ff\u8c01\u795d\u544a\uff1f\u201d \u3000\u3000\u5ffd\u542c\u5f97\u5979\u8bf4\u5230\u201c\u7a7a\u5fc3\u83dc\u8205\u8205\u201d\u4e94\u4e2a\u5b57\uff0c\u8033\u4e2d\u4e0d\u7531\u5f97\u55e1\u7684\u4e00\u58f0\u54cd\uff0c\u5fc3\u4e2d\u53ea\u8bf4\uff1a\u201c\u5979\u662f\u5728\u8bf4\u6211\uff1f\u5979\u662f\u5728\u8bf4\u6211\uff1f\u201d \u3000\u3000\u90a3\u5c0f\u5973\u5b69\u9053\uff1a\u201c\u5988\u5988\u8bb0\u6302\u7a7a\u5fc3\u83dc\u8205\u8205\uff0c\u5929\u83e9\u8428\u4fdd\u4f51\u8205\u8205\u606d\u79a7\u53d1\u8d22\uff0c\u4e70\u4e2a\u5927\u5a03\u5a03\u7ed9\u6211\uff0c\u4ed6\u662f\u7a7a\u5fc3\u83dc\uff0c\u6211\u4e5f\u662f\u7a7a\u5fc3\u83dc\u3002\u5988\u5988\uff0c\u8fd9\u4e2a\u7a7a\u5fc3\u83dc\u8205\u8205\uff0c\u5230\u54ea\u91cc\u53bb\u5566\uff1f\u4ed6\u600e\u4e48\u4e5f\u8fd8\u4e0d\u56de\u6765\uff1f\u201d\u621a\u82b3\u9053\uff1a\u201c\u7a7a\u5fc3\u83dc\u8205\u8205\u53bb\u4e86\u5f88\u8fdc\u5f88\u8fdc\u7684\u5730\u65b9\u3002\u8205\u8205\u629b\u4e0b\u4f60\u5988\u4e0d\u7406\u4e86\uff0c\u5988\u5374\u5929\u5929\u8bb0\u7740\u4ed6\u2026\u2026\u201d\u8bf4\u5230\u8fd9\u91cc\uff0c\u62b1\u8d77\u5973\u513f\uff0c\u5c06\u8138\u85cf\u5728\u5973\u513f\u80f8\u524d\uff0c\u5feb\u6b65\u56de\u4e86\u8fdb\u53bb\u3002 \u3000\u3000\u72c4\u4e91\u8d70\u5230\u9999\u7089\u4e4b\u65c1\uff0c\u77a7\u7740\u90a3\u4e09\u6839\u95ea\u95ea\u53d1\u7740\u5fae\u5149\u7684\u9999\u5934\uff0c\u4e0d\u7531\u5f97\u75f4\u4e86\u3002 \u3000\u3000\u4ed6\u6014\u6014\u7684\u7ad9\u7740\uff0c\u4e09\u6839\u9999\u70e7\u5230\u4e86\u5c3d\u5934\uff0c\u90fd\u5316\u4e86\u7070\u70ec\uff0c\u4ed6\u8fd8\u662f\u4e00\u52a8\u4e0d\u52a8\u7684\u7ad9\u7740\u3002 \u3000\u3000\u7b2c\u4e8c\u5929\u6e05\u6668\uff0c\u72c4\u4e91\u4ece\u4e07\u5bb6\u540e\u56ed\u4e2d\u51fa\u6765\uff0c\u5728\u8346\u5dde\u57ce\u4e2d\u832b\u7136\u4e71\u8d70\uff0c\u5ffd\u7136\u542c\u5f97\u545b\u5577\u5577\u3001\u545b\u5577\u5577\u7684\u58f0\u97f3\u76f4\u54cd\uff0c\u662f\u4e2a\u8d70\u65b9\u90ce\u4e2d\u6447\u7740\u864e\u6491\u5728\u6cbf\u8857\u5356\u836f\u3002\u72c4\u4e91\u5fc3\u4e2d\u4e00\u52a8\uff0c\u4ed6\u8981\u4eb2\u773c\u77a7\u77a7\u4e07\u572d\u547b\u541f\u53eb\u5524\u7684\u60e8\u72b6\uff0c\u4e8e\u662f\u53d6\u51fa\u5341\u4e24\u94f6\u5b50\uff0c\u8981\u5c06\u4ed6\u7684\u8863\u670d\u3001\u836f\u7bb1\u3001\u864e\u6491\u4e00\u53e4\u8111\u513f\u90fd\u4e70\u4e0b\u6765\u3002\u90a3\u90ce\u4e2d\u5f88\u5947\u602a\uff0c\u8fd9\u4e9b\u90fd\u4e0d\u662f\u751a\u4e48\u8d35\u91cd\u4e1c\u897f\uff0c\u6700\u591a\u4e0d\u8fc7\u503c\u5f97\u4e09\u56db\u4e24\u94f6\u5b50\uff0c\u4fbf\u9ad8\u9ad8\u5174\u5174\u7684\u5356\u4e86\u7ed9\u4ed6\u3002 \u3000\u3000\u72c4\u4e91\u56de\u5230\u5e9f\u56ed\uff0c\u6362\u4e0a\u90ce\u4e2d\u7684\u8863\u670d\uff0c\u62ff\u4e9b\u8349\u836f\u6363\u70c2\u4e86\uff0c\u5c06\u6c41\u6db2\u6d82\u5728\u8138\u4e0a\uff0c\u53c8\u5728\u5de6\u773c\u4e0b\u6577\u4e86\u4e00\u5927\u5757\u8349\u836f\uff0c\u5f04\u5f97\u9762\u76ee\u5168\u975e\uff0c\u7136\u540e\u6447\u7740\u864e\u6491\uff0c\u6765\u5230\u4e07\u5bb6\u95e8\u524d\u3002 \u3000\u3000\u4ed6\u5c06\u5230\u4e07\u5bb6\u95e8\u524d\uff0c\u4fbf\u628a\u864e\u6491\u545b\u5577\u5577\u3001\u545b\u5577\u5577\u7684\u6447\u5f97\u5927\u54cd\uff0c\u5f85\u5f97\u8d70\u8fd1\uff0c\u5636\u54d1\u7740\u55d3\u5b50\u53eb\u9053\uff1a\u201c\u4e13\u533b\u7591\u96be\u6742\u75c7\uff0c\u65e0\u540d\u80bf\u6bd2\uff0c\u6bd2\u866b\u6bd2\u86c7\u54ac\u4f24\uff0c\u5373\u523b\u89c1\u6548\u3002\u201d \u3000\u3000\u5982\u6b64\u6765\u56de\u8d70\u5f97\u4e09\u904d\uff0c\u53ea\u89c1\u5927\u95e8\u4e2d\u4e00\u4eba\u5306\u5306\u51fa\u6765\uff0c\u62db\u624b\u9053\uff1a\u201c\u5582\uff0c\u90ce\u4e2d\u5148\u751f\uff0c\u4f60\u8fc7\u6765\uff0c\u8fc7\u6765\u3002\u201d\u72c4\u4e91\u8ba4\u5f97\u4ed6\u662f\u4e07\u95e8\u5f1f\u5b50\uff0c\u4fbf\u662f\u5f53\u5e74\u524a\u53bb\u4ed6\u4e94\u6839\u624b\u6307\u7684\u5434\u574e\u3002\u4f46\u72c4\u4e91\u6b64\u523b\u88c5\u675f\u9762\u8c8c\u4e0e\u6614\u5e74\u5927\u5f02\uff0c\u5434\u574e\u81ea\u662f\u8ba4\u4ed6\u4e0d\u51fa\u3002\u72c4\u4e91\u751f\u6015\u4ed6\u542c\u51fa\u81ea\u5df1\u8bed\u97f3\uff0c\u6162\u6162\u8e31\u8fc7\u53bb\uff0c\u66f4\u52a0\u538b\u4f4e\u55d3\u5b50\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u7237\u53f0\u6709\u4f55\u5429\u5490\uff0c\u53ef\u662f\u8eab\u4e0a\u751f\u4e86\u751a\u4e48\u7591\u96be\u6742\u75c7\u3001\u65e0\u540d\u80bf\u6bd2\uff1f\u201d \u3000\u3000\u5434\u574e\u201c\u5478\u201d\u7684\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u77a7\u6211\u50cf\u4e0d\u50cf\u751f\u4e86\u65e0\u540d\u80bf\u6bd2\uff1f\u5582\uff0c\u6211\u95ee\u4f60\uff0c\u7ed9\u874e\u5b50\u87ab\u4e86\uff0c\u4f60\u6cbb\u4e0d\u6cbb\u5f97\u597d\uff1f\u201d \u3000\u3000\u72c4\u4e91\u9053\uff1a\u201c\u9752\u7af9\u86c7\u3001\u8d64\u7ec3\u86c7\u3001\u91d1\u811a\u5e26\u3001\u94c1\u94f2\u5934\uff0c\u5929\u4e0b\u4e00\u7b49\u4e00\u7684\u6bd2\u86c7\u54ac\u4f24\u4e86\u4eba\uff0c\u5728\u4e0b\u90fd\u662f\u836f\u5230\u4f24\u53bb\u3002\u90a3\u874e\u5b50\u561b\uff0c\u563f\u563f\uff0c\u53c8\u7b97\u5f97\u751a\u4e48\u4e00\u56de\u4e8b\uff1f\u201d \u3000\u3000\u5434\u574e\u9053\uff1a\u201c\u4f60\u53ef\u522b\u80e1\u5439\u5927\u6c14\uff0c\u8fd9\u87ab\u4eba\u7684\u874e\u5b50\u5374\u4e0d\u662f\u5bfb\u5e38\u7684\u5bb6\u4f19\u3002\u8346\u5dde\u57ce\u91cc\u7684\u540d\u533b\u89c1\u4e86\u4e2a\u4e2a\u6447\u5934\uff0c\u4f60\u53c8\u533b\u5f97\u597d\u4e86\uff1f\u201d \u3000\u3000\u72c4\u4e91\u76b1\u7709\u9053\uff1a\u201c\u6709\u8fd9\u7b49\u5389\u5bb3\uff1f\u5929\u4e0b\u7684\u874e\u5b50\u561b\uff0c\u4e5f\u4e0d\u8fc7\u662f\u7070\u6bdb\u874e\u3001\u9ed1\u767d\u874e\u3001\u91d1\u94b1\u874e\u3001\u9ebb\u5934\u874e\u3001\u7ea2\u5c3e\u874e\u3001\u843d\u5730\u54ac\u5a18\u874e\u3001\u767d\u811a\u874e\u2026\u2026\u201d\u4ed6\u4fe1\u53e3\u80e1\u8bf4\uff0c\u8fde\u8bf4\u4e86\u4e8c\u5341\u6765\u79cd\uff0c\u624d\u9053\uff1a\u201c\u6bcf\u79cd\u874e\u5b50\u6bd2\u6027\u4e0d\u540c\uff0c\u5404\u6709\u5404\u7684\u6cbb\u6cd5\uff0c\u5c31\u7b97\u662f\u540d\u533b\uff0c\u82e5\u4e0d\u662f\u771f\u6709\u672c\u4e8b\u7684\uff0c\u4e5f\u672a\u5fc5\u61c2\u5f97\u5468\u5168\u3002\u201d \u3000\u3000\u5434\u574e\u89c1\u4ed6\u5f62\u8c8c\u4e11\u964b\uff0c\u8863\u886b\u8934\u891b\uff0c\u867d\u7136\u8bf4\u4e86\u8bb8\u591a\u874e\u5b50\u7684\u540d\u76ee\uff0c\u4f46\u7ed3\u7ed3\u5df4\u5df4\uff0c\u53e3\u9f7f\u4e0d\u6e05\uff0c\u6599\u60f3\u4e5f\u6ca1\u751a\u4e48\u672c\u4e8b\uff0c\u4fbf\u9053\uff1a\u201c\u65e2\u662f\u5982\u6b64\uff0c\u4f60\u4fbf\u53bb\u77a7\u77a7\u7f62\uff0c\u53cd\u6b63\u662f\u6b7b\u9a6c\u5f53\u4f5c\u6d3b\u9a6c\u533b\u3002\u201d\u72c4\u4e91\u70b9\u4e86\u70b9\u5934\uff0c\u8ddf\u4ed6\u8d70\u8fdb\u4e07\u5e9c\u3002 \u3000\u3000\u4ed6\u4e00\u8de8\u8fdb\u95e8\uff0c\u767b\u65f6\u4fbf\u60f3\u8d77\u90a3\u5e74\u8ddf\u7740\u5e08\u7236\u3001\u5e08\u59b9\u524d\u6765\u62dc\u5bff\u7684\u60c5\u666f\uff0c\u90a3\u65f6\u5019\u662f\u4e61\u4e0b\u5c11\u5e74\u8fdb\u57ce\uff0c\u773c\u4e2d\u770b\u51fa\u6765\uff0c\u751a\u4e48\u4e1c\u897f\u90fd\u900f\u7740\u65b0\u9c9c\u597d\u73a9\uff0c\u548c\u5e08\u59b9\u4e24\u4e2a\u4e1c\u5f20\u897f\u671b\uff0c\u6307\u6307\u70b9\u70b9\uff0c\u4eca\u65e5\u518d\u6765\uff0c\u5ead\u6237\u4f9d\u65e7\uff0c\u5fc3\u4e2d\u5374\u53ea\u611f\u5230\u4e00\u9635\u9635\u9178\u82e6\u3002\u4ed6\u968f\u7740\u5434\u574e\u8d70\u8fc7\u4e86\u4e24\u5904\u5929\u4e95\uff0c\u6765\u5230\u4e1c\u8fb9\u697c\u524d\u3002 \u3000\u3000\u5434\u574e\u4ef0\u8d77\u4e86\u5934\uff0c\u5927\u58f0\u9053\uff1a\u201c\u4e09\u5e08\u5ac2\uff0c\u6709\u4e2a\u8349\u5934\u90ce\u4e2d\uff0c\u4ed6\u8bf4\u4f1a\u6cbb\u874e\u6bd2\uff0c\u8981\u4e0d\u8981\u4ed6\u6765\u7ed9\u5e08\u54e5\u77a7\u77a7\uff1f\u201d \u3000\u3000\u5440\u7684\u4e00\u58f0\uff0c\u697c\u4e0a\u7a97\u5b50\u6253\u5f00\uff0c\u621a\u82b3\u4ece\u7a97\u4e2d\u63a2\u5934\u51fa\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u554a\uff0c\u591a\u8c22\u5434\u5e08\u5f1f\uff0c\u4f60\u5e08\u54e5\u4eca\u5929\u75db\u5f97\u66f4\u52a0\u5389\u5bb3\u4e86\uff0c\u8bf7\u5148\u751f\u4e0a\u697c\u3002\u201d\u5434\u574e\u5bf9\u72c4\u4e91\u9053\uff1a\u201c\u4f60\u4e0a\u53bb\u7f62\u3002\u201d\u81ea\u5df1\u5374\u4e0d\u8ddf\u4e0a\u53bb\u3002\u621a\u82b3\u9053\uff1a\u201c\u5434\u5e08\u5f1f\uff0c\u4f60\u4e5f\u8bf7\u4e0a\u6765\u597d\u5566\uff0c\u5e2e\u7740\u77a7\u77a7\u3002\u201d\u5434\u574e\u9053\uff1a\u201c\u662f\uff01\u201d\u8fd9\u624d\u968f\u7740\u4e0a\u697c\u3002 \u3000\u3000\u72c4\u4e91\u4e0a\u5f97\u697c\u6765\uff0c\u53ea\u89c1\u4e2d\u95f4\u9760\u7a97\u653e\u7740\u4e00\u5f20\u5927\u4e66\u684c\uff0c\u653e\u7740\u7b14\u58a8\u7eb8\u781a\u4e0e\u5341\u6765\u672c\u4e66\uff0c\u8fd8\u6709\u4e00\u4ef6\u7f1d\u4e86\u4e00\u534a\u7684\u5c0f\u5b69\u8863\u886b\u3002\u621a\u82b3\u4ece\u5185\u623f\u8fce\u4e86\u51fa\u6765\uff0c\u8138\u4e0a\u4e0d\u65bd\u8102\u7c89\uff0c\u5bb9\u8272\u9887\u4e3a\u6194\u60b4\u3002\u72c4\u4e91\u53ea\u5411\u5979\u770b\u4e86\u4e00\u773c\uff0c\u751f\u6015\u5979\u8bc6\u5f97\u81ea\u5df1\uff0c\u4e0d\u6562\u591a\u770b\uff0c\u4fbf\u8d70\u8fdb\u623f\u53bb\u3002\u53ea\u89c1\u4e00\u5f20\u5927\u5e8a\u4e0a\u5411\u91cc\u7761\u7740\u4e00\u4eba\uff0c\u4e0d\u65ad\u547b\u541f\uff0c\u6b63\u662f\u4e07\u572d\u3002\u4ed6\u5c0f\u5973\u513f\u5750\u5728\u5e8a\u524d\u7684\u4e00\u5f20\u5c0f\u51f3\u4e0a\uff0c\u5728\u7ed9\u7238\u7238\u8f7b\u8f7b\u6376\u817f\u3002\u5979\u89c1\u5230\u72c4\u4e91\u6c61\u79fd\u53e4\u602a\u7684\u9762\u5bb9\uff0c\u60ca\u547c\u4e00\u58f0\uff0c\u5fd9\u8eb2\u5230\u6bcd\u4eb2\u8eab\u540e\u3002 \u3000\u3000\u5434\u574e\u9053\uff1a\u201c\u6211\u8fd9\u5e08\u54e5\u7ed9\u6bd2\u874e\u87ab\u4f24\u4e86\uff0c\u6bd2\u6027\u59cb\u7ec8\u4e0d\u6d88\uff0c\u597d\u50cf\u6709\u70b9\u4e0d\u5927\u5bf9\u5934\u3002\u201d\u72c4\u4e91\u9053\uff1a\u201c\u55ef\uff0c\u662f\u5417\uff1f\u201d\u4ed6\u5728\u95e8\u5916\u548c\u5434\u574e\u8bf4\u8bdd\u65f6\u6cf0\u7136\u81ea\u82e5\uff0c\u8fd9\u65f6\u89c1\u4e86\u621a\u82b3\uff0c\u4e00\u9897\u5fc3\u6251\u901a\u6251\u901a\u4e71\u8df3\uff0c\u81ea\u89c9\u53cc\u988a\u53d1\u70e7\uff0c\u5507\u5e72\u820c\u71e5\uff0c\u518d\u4e5f\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002\u4ed6\u8d70\u5230\u5e8a\u524d\uff0c\u62cd\u4e86\u62cd\u4e07\u572d\u80a9\u5934\u3002 \u3000\u3000\u4e07\u572d\u6162\u6162\u7ffb\u8eab\u8fc7\u6765\uff0c\u4e00\u7741\u773c\u770b\u5230\u72c4\u4e91\u7684\u795e\u60c5\uff0c\u4e0d\u7531\u5f97\u5fae\u5fae\u4e00\u60ca\u3002\u621a\u82b3\u9053\uff1a\u201c\u4e09\u54e5\uff0c\u8fd9\u4f4d\u662f\u5434\u5e08\u5f1f\u7ed9\u4f60\u627e\u6765\u7684\u5927\u592b\uff0c\u4ed6\u2026\u2026\u4ed6\u6216\u8bb8\u4f1a\u6709\u7075\u836f\uff0c\u80fd\u6cbb\u4f60\u7684\u4f24\u3002\u201d\u8bed\u6c14\u4e4b\u4e2d\uff0c\u5b9e\u5728\u5bf9\u8fd9\u90ce\u4e2d\u5168\u65e0\u4fe1\u5fc3\u3002 \u3000\u3000\u72c4\u4e91\u4e00\u8a00\u4e0d\u53d1\uff0c\u770b\u4e86\u770b\u4e07\u572d\u80bf\u8d77\u7684\u624b\u80cc\uff0c\u89c1\u90a3\u624b\u80cc\u53c8\u662f\u9ed1\u9ed1\u7684\u4e00\u56e2\uff0c\u6837\u5b50\u751a\u662f\u53ef\u6016\uff0c\u4e8e\u662f\u5636\u54d1\u7740\u55d3\u5b50\u9053\uff1a\u201c\u8fd9\u662f\u6e58\u897f\u6c85\u9675\u4e00\u5e26\u7684\u82b1\u6591\u6bd2\u874e\u54ac\u7684\uff0c\u54b1\u4eec\u6e56\u5317\u53ef\u6ca1\u8fd9\u79cd\u874e\u5b50\uff01\u201d \u3000\u3000\u621a\u82b3\u548c\u5434\u574e\u9f50\u58f0\u9053\uff1a\u201c\u662f\uff0c\u662f\uff0c\u6b63\u662f\u5728\u6e58\u897f\u6c85\u9675\u7ed9\u87ab\u4e0a\u7684\u3002\u201d \u3000\u3000\u621a\u82b3\u53c8\u9053\uff1a\u201c\u5148\u751f\u77a7\u51fa\u4e86\u874e\u5b50\u7684\u6765\u5386\uff0c\u5b9a\u662f\u80fd\u6cbb\u7684\u4e86\uff1f\u201d\u8bed\u97f3\u4e2d\u5145\u6ee1\u4e86\u6307\u671b\u3002 \u3000\u3000\u72c4\u4e91\u5c48\u6307\u8ba1\u7b97\u65e5\u5b50\uff0c\u9053\uff1a\u201c\u8fd9\u662f\u665a\u4e0a\u54ac\u7684\uff0c\u5230\u73b0\u5728\u4e48\uff0c\u55ef\uff0c\u5df2\u7ecf\u6709\u4e03\u5929\u4e03\u665a\u4e86\u3002\u201d \u3000\u3000\u621a\u82b3\u5411\u5434\u574e\u77a7\u4e86\u4e00\u773c\uff0c\u8bf4\u9053\uff1a\u201c\u5148\u751f\u771f\u662f\u6599\u4e8b\u5982\u795e\uff0c\u90a3\u786e\u662f\u665a\u4e0a\u7ed9\u6574\u7684\uff0c\u5230\u4eca\u5929\u5df2\u6709\u4e03\u5929\u4e03\u665a\u4e86\u3002\u201d \u3000\u3000\u72c4\u4e91\u53c8\u9053\uff1a\u201c\u8fd9\u4f4d\u7237\u53f0\u662f\u4e0d\u662f\u53cd\u624b\u4e00\u638c\uff0c\u5c06\u874e\u5b50\u6253\u6b7b\u4e86\uff1f \u3000\u3000\u82e5\u4e0d\u662f\u8fd9\u6837\uff0c\u672c\u6765\u8fd8\u53ef\u6709\u6551\u3002\u73b0\u4e0b\u5c06\u874e\u5b50\u6253\u6b7b\u5728\u624b\u80cc\u4e4b\u4e0a\uff0c\u6bd2\u6027\u5c3d\u6570\u8feb\u4e86\u8fdb\u53bb\uff0c\u518d\u8981\u89e3\u6551\uff0c\u90a3\u662f\u5343\u96be\u4e07\u96be\u4e86\u3002\u201d \u3000\u3000\u621a\u82b3\u672c\u6765\u542c\u4ed6\u8fde\u65f6\u65e5\u90fd\u7b97\u5f97\u6781\u51c6\uff0c\u6599\u60f3\u5fc5\u6709\u6cbb\u6cd5\uff0c\u8138\u4e0a\u5df2\u6709\u559c\u8272\uff0c\u5f85\u5f97\u8fd9\u4e48\u8bf4\uff0c\u53c8\u7126\u6025\u8d77\u6765\uff0c\u9053\uff1a\u201c\u5148\u751f\u8bf4\u5f97\u660e\u767d\u4e0d\u8fc7\uff0c\u65e0\u8bba\u5982\u4f55\uff0c\u8981\u8bf7\u4f60\u6551\u4ed6\u7684\u6027\u547d\u3002\u201d \u3000\u3000\u72c4\u4e91\u8fd9\u6b21\u5047\u626e\u90ce\u4e2d\u800c\u8fdb\u4e07\u5bb6\uff0c\u672c\u610f\u662f\u8981\u4eb2\u773c\u89c1\u5230\u4e07\u572d\u75db\u82e6\u4e07\u72b6\u3001\u547b\u541f\u5c31\u6b7b\u7684\u60c5\u666f\uff0c\u4ee5\u4fbf\u7a0d\u6cc4\u5fc3\u4e2d\u90c1\u79ef\u7684\u6012\u6c14\uff0c\u81f3\u4e8e\u6551\u4ed6\u6027\u547d\u4e4b\u610f\uff0c\u81ea\u7136\u662f\u534a\u70b9\u4e5f\u6ca1\u6709\u7684\u3002\u4f46\u4ed6\u81ea\u5e7c\u5bf9\u621a\u82b3\u4fbf\u662f\u5343\u4f9d\u767e\u987a\uff0c\u4ece\u6765\u4e0d\u8fdd\u62d7\u5979\u534a\u70b9\uff0c\u8fd9\u65f6\u542c\u5979\u5982\u6b64\u7126\u6025\u76f8\u6c42\uff0c\u5fc3\u4e2d\u4e00\u8f6f\uff0c\u4fbf\u60f3\u53bb\u6253\u5f00\u836f\u7bb1\uff0c\u53d6\u8a00\u8fbe\u5e73\u7684\u89e3\u836f\u51fa\u6765\uff0c\u4f46\u968f\u5373\u8f6c\u5ff5\uff1a\u201c\u8fd9\u4e07\u572d\u5bb3\u5f97\u6211\u597d\u82e6\uff0c\u53c8\u593a\u4e86\u6211\u5e08\u59b9\uff0c\u6211\u4e0d\u4eb2\u624b\u6740\u4ed6\uff0c\u5df2\u7b97\u662f\u5ba2\u6c14\u4e4b\u6781\u4e86\uff0c\u5982\u4f55\u8fd8\u80fd\u6551\u4ed6\u6027\u547d\uff1f\u201d\u4fbf\u6447\u4e86\u6447\u5934\uff0c\u9053\uff1a\u201c\u4e0d\u662f\u6211\u4e0d\u80af\u6551\uff0c\u5b9e\u5728\u4ed6\u4e2d\u6bd2\u592a\u6df1\uff0c\u53c8\u803d\u6401\u4e86\u65e5\u5b50\uff0c\u6bd2\u6027\u5165\u8111\uff0c\u90a3\u662f\u4e0d\u80fd\u6551\u7684\u4e86\u3002\u201d \u3000\u3000\u621a\u82b3\u5782\u4e0b\u6cea\u6765\uff0c\u62c9\u7740\u5973\u513f\u7684\u624b\uff0c\u9053\uff1a\u201c\u7a7a\u5fc3\u83dc\uff0c\u5b9d\u5b9d\uff0c\u4f60\u5411\u8fd9\u4f4d\u4f2f\u4f2f\u78d5\u5934\uff0c\u6c42\u4ed6\u6551\u6551\u7239\u7239\u7684\u547d\u3002\u201d \u3000\u3000\u72c4\u4e91\u6025\u5fd9\u6447\u624b\uff0c\u9053\uff1a\u201c\u4e0d\uff0c\u4e0d\u7528\u78d5\u5934\u2026\u2026\u201d\u4f46\u90a3\u5973\u5b69\u5f88\u4e56\uff0c\u5411\u6765\u542c\u6bcd\u4eb2\u7684\u8bdd\uff0c\u53c8\u77e5\u7236\u4eb2\u91cd\u4f24\uff0c\u5fc3\u4e2d\u4e5f\u5f88\u7126\u6025\uff0c\u5f53\u5373\u8dea\u5728\u5730\u4e0b\uff0c\u5411\u4ed6\u549a\u549a\u549a\u7684\u78d5\u5934\u3002\u72c4\u4e91\u53f3\u624b\u4e94\u6307\u5df2\u5931\uff0c\u59cb\u7ec8\u85cf\u5728\u8863\u8896\u4e4b\u4e2d\uff0c\u5f53\u5373\u4f38\u51fa\u5de6\u624b\uff0c\u5c06\u5973\u5b69\u6276\u8d77\u3002\u53ea\u89c1\u90a3\u5973\u5b69\u8d77\u8eab\u4e4b\u65f6\uff0c\u9888\u4e2d\u5782\u4e0b\u4e00\u4e2a\u91d1\u9501\u7247\u6765\uff0c\u91d1\u7247\u4e0a\u954c\u7740\u56db\u4e2a\u5b57\uff1a\u201c\u5fb7\u5bb9\u53cc\u8302\u201d\u3002 \u3000\u3000\u72c4\u4e91\u4e00\u770b\u4e4b\u4e0b\uff0c\u4e0d\u7531\u5f97\u4e00\u5446\uff0c\u60f3\u8d77\u90a3\u65e5\u81ea\u5df1\u5728\u4e07\u5bb6\u67f4\u623f\u4e4b\u4e2d\u660f\u6655\u4e86\u8fc7\u53bb\uff0c\u9192\u8f6c\u65f6\u8eab\u5b50\u5df2\u5728\u957f\u6c5f\u821f\u4e2d\uff0c\u8eab\u8fb9\u6709\u4e9b\u91d1\u94f6\u9996\u9970\uff0c\u5176\u4e2d\u6709\u4e00\u7247\u5c0f\u5b69\u513f\u7684\u91d1\u9501\u7247\uff0c\u4e0a\u9762\u4e5f\u523b\u7740\u8fd9\u6837\u56db\u4e2a\u5b57\uff0c\u83ab\u4e0d\u662f\u2026\u2026\u83ab\u4e0d\u662f\u2026\u2026 \u3000\u3000\u4ed6\u53ea\u770b\u4e86\u4e00\u773c\uff0c\u4e0d\u6562\u518d\u770b\uff0c\u8111\u6d77\u4e2d\u4e00\u7247\u6df7\u4e71\uff0c\u7ec8\u4e8e\u6e10\u6e10\u6e05\u6670\u4e86\u8d77\u6765\uff1a\u201c\u6211\u5728\u4e07\u5bb6\u67f4\u623f\u4e2d\u6655\u5012\uff0c\u82e5\u4e0d\u662f\u5e08\u59b9\u76f8\u6551\uff0c\u66f4\u65e0\u65c1\u4eba\u3002\u4ece\u524d\u6211\u7591\u5fc3\u5979\u6709\u610f\u5bb3\u6211\uff0c\u4f46\u6628\u665a\u2026\u2026\u6628\u665a\u5979\u5411\u5929\u795d\u7977\uff0c\u5410\u9732\u5fc3\u4e8b\uff0c\u5979\u65e2\u5bf9\u6211\u5982\u6b64\u60c5\u957f\uff0c\u5f53\u65e5\u81ea\u4e5f\u51b3\u8ba1\u4e0d\u4f1a\u5bb3\u6211\u3002\u96be\u9053\uff0c\u96be\u9053\u8001\u5929\u7237\u6709\u773c\u775b\uff0c\u6211\u548c\u5e08\u59b9\u7ecf\u5386\u4e86\u8fd9\u756a\u8270\u96be\u56f0\u82e6\u4e4b\u540e\uff0c\u53c8\u80fd\u91cd\u884c\u56e2\u5706\u4e48\uff1f\u201d \u3000\u3000\u4ed6\u60f3\u5230\u201c\u91cd\u884c\u56e2\u5706\u201d\u56db\u5b57\uff0c\u4e0d\u7981\u5fc3\u4e2d\u53c8\u6026\u6026\u4e71\u8df3\uff0c\u4fa7\u5934\u5411\u621a\u82b3\u77a5\u4e86\u4e00\u773c\uff0c\u53ea\u89c1\u5979\u6ee1\u8138\u5c3d\u662f\u5173\u5207\u4e4b\u8272\uff0c\u76ee\u4e0d\u8f6c\u775b\u7684\u77a7\u4e07\u572d\uff0c\u773c\u4e2d\u6d41\u9732\u51fa\u7231\u601c\u7684\u795e\u6c14\u3002 \u3000\u3000\u72c4\u4e91\u4e00\u89c1\u5230\u5979\u8fd9\u773c\u8272\uff0c\u4e00\u9897\u5fc3\u767b\u65f6\u6c89\u4e86\u4e0b\u53bb\uff0c\u80cc\u810a\u4e0a\u4e00\u7247\u51b0\u51c9\uff0c\u4ed6\u8bb0\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u90a3\u65e5\u4ed6\u548c\u4e07\u95e8\u516b\u5f1f\u5144\u76f8\u6597\uff0c\u7ed9\u4ed6\u516b\u4eba\u8054\u624b\u6253\u5f97\u9f3b\u9752\u76ee\u80bf\uff0c\u5e08\u59b9\u7ed9\u4ed6\u7f1d\u8865\u8863\u886b\uff0c\u773c\u5149\u4e2d\u4e5f\u662f\u8fd9\u4e48\u7231\u601c\u6a2a\u6ea2\u3001\u67d4\u60c5\u65e0\u9650\u3002\u73b0\u4eca\uff0c\u5979\u8fd9\u773c\u6ce2\u662f\u7ed9\u4e86\u4e08\u592b\u5566\uff0c\u518d\u4e5f\u4e0d\u4f1a\u7ed9\u4ed6\u4e86\u3002 \u3000\u3000\u201c\u8981\u662f\u6211\u4e0d\u7ed9\u89e3\u836f\uff0c\u8c01\u4e5f\u602a\u4e0d\u5f97\u6211\u3002\u7b49\u4e07\u572d\u75db\u6b7b\u4e86\uff0c\u6211\u591c\u91cc\u6084\u6084\u6765\u5e26\u4e86\u5979\u8d70\u8def\uff0c\u8c01\u80fd\u62e6\u5f97\u4f4f\u6211\uff1f\u6211\u65e7\u4e8b\u4e0d\u63d0\uff0c\u548c\u5979\u518d\u505a\u2026\u2026\u518d\u505a\u592b\u59bb\u3002\u8fd9\u5973\u5b69\u513f\u561b\uff0c\u6211\u5e26\u4e86\u5979\u4e00\u8d77\u8d70\u5c31\u662f\u4e86\u3002\u5509\uff0c\u4e0d\u6210\uff0c\u4e0d\u6210\uff01\u5e08\u59b9\u8fd9\u51e0\u5e74\u6765\u5728\u4e07\u5bb6\u505a\u5c11\u5976\u5976\uff0c\u8212\u670d\u60ef\u4e86\uff0c\u600e\u4e48\u53c8\u80fd\u8ddf\u6211\u53bb\u8015\u7530\u653e\u725b\uff1f\u4f55\u51b5\uff0c\u6211\u5f62\u5bb9\u4e11\u964b\uff0c\u8bc6\u4e0d\u4e0a\u51e0\u767e\u4e2a\u5b57\uff0c\u624b\u53c8\u6b8b\u5e9f\u4e86\uff0c\u600e\u914d\u5f97\u4e0a\u5979\uff1f\u5979\u53c8\u600e\u80af\u8ddf\u6211\u8d70\uff1f\u201d\u8fd9\u4e00\u81ea\u60ed\u5f62\u79fd\uff0c\u4e0d\u7531\u5f97\u7f9e\u6127\u65e0\u5730\uff0c\u8111\u888b\u4f4e\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u621a\u82b3\u54ea\u77e5\u9053\u8fd9\u4e2a\u8349\u836f\u90ce\u4e2d\u5fc3\u91cc\uff0c\u7adf\u5728\u8f6c\u7740\u8fd9\u8bb8\u8bb8\u591a\u591a\u5ff5\u5934\uff0c\u53ea\u662f\u6014\u6014\u7684\u77a7\u7740\u4ed6\uff0c\u76fc\u4ed6\u53e3\u4e2d\u5410\u51fa\u4e24\u4e2a\u5b57\u6765\uff1a\u201c\u6709\u6551\uff01\u201d \u3000\u3000\u4e07\u572d\u4e00\u58f0\u957f\uff0c\u4e00\u58f0\u77ed\u7684\u547b\u541f\uff0c\u8fd9\u65f6\u874e\u6bd2\u5df2\u4fb5\u5230\u814b\u7a9d\u5173\u8282\uff0c\u6574\u6761\u624b\u81c2\u548c\u624b\u638c\u90fd\u662f\u80bf\u5f97\u75db\u695a\u96be\u5f53\u3002 \u3000\u3000\u621a\u82b3\u7b49\u4e86\u826f\u4e45\uff0c\u4e0d\u89c1\u72c4\u4e91\u4f5c\u58f0\uff0c\u53c8\u6c42\u9053\uff1a\u201c\u5148\u751f\uff0c\u8bf7\u4f60\u8bd5\u4e00\u8bd5\uff0c\u53ea\u8981\u2026\u2026\u53ea\u8981\u51cf\u8f7b\u4ed6\u4e00\u4e9b\u2026\u2026\u75db\u82e6\uff0c\u5c31\u7b97\u2026\u2026\u5c31\u7b97\u2026\u2026 \u3000\u3000\u4e5f\u4e0d\u602a\u4f60\u3002\u201d\u610f\u601d\u662f\u8bf4\uff0c\u65e2\u7136\u4e07\u572d\u8fd9\u6761\u547d\u662f\u4fdd\u4e0d\u4f4f\u4e86\uff0c\u90a3\u4e48\u53ea\u6c42\u4ed6\u7ed9\u6b62\u4e00\u6b62\u75db\uff0c\u5c31\u7b97\u7ec8\u4e8e\u96be\u9003\u4e00\u6b7b\uff0c\u4e5f\u514d\u5f97\u8fd9\u822c\u53d7\u82e6\u3002 \u3000\u3000\u72c4\u4e91\u201c\u54e6\u201d\u7684\u4e00\u58f0\uff0c\u4ece\u6c89\u601d\u4e2d\u9192\u89c9\u8fc7\u6765\uff0c\u970e\u65f6\u95f4\u5fc3\u4e2d\u4e00\u7247\u7a7a\u8361\u8361\u5730\uff0c\u4e07\u5ff5\u4ff1\u7070\uff0c\u6068\u4e0d\u5f97\u5373\u523b\u5c31\u6b7b\u4e86\u3002\u4ed6\u5168\u5fc3\u5168\u610f\u7684\u7231\u7740\u8fd9\u4e2a\u5e08\u59b9\uff0c\u4f46\u5979\u5374\u5ac1\u4e86\u4ed6\u7684\u5927\u4ec7\u4eba\uff0c\u8fd8\u5728\u82e6\u82e6\u54c0\u6c42\u81ea\u5df1\uff0c\u53eb\u81ea\u5df1\u6551\u8fd9\u4ec7\u4eba\u3002\u201c\u6211\u5b81\u53ef\u662f\u5982\u4e07\u572d\u8fd9\u53ae\uff0c\u8eab\u4e0a\u53d7\u5c3d\u82e6\u695a\uff0c\u5374\u6709\u5e08\u59b9\u8fd9\u822c\u601c\u60dc\u7684\u77a7\u7740\u6211\uff0c\u5c31\u7b97\u6d3b\u4e0d\u4e86\u51e0\u5929\uff0c\u90a3\u53c8\u7b97\u5f97\u751a\u4e48\uff1f\u201d \u3000\u3000\u4ed6\u8f7b\u8f7b\u5401\u4e86\u53e3\u6c14\uff0c\u6253\u5f00\u836f\u7bb1\uff0c\u53d6\u51fa\u8a00\u8fbe\u5e73\u7684\u90a3\u74f6\u89e3\u836f\uff0c\u5012\u4e86\u4e9b\u9ed1\u8272\u7c89\u672b\u51fa\u6765\uff0c\u653e\u4e0a\u4e07\u572d\u7684\u624b\u80cc\u3002 \u3000\u3000\u5434\u574e\u53eb\u9053\uff1a\u201c\u554a\u54df\u2026\u2026\u6b63\u2026\u2026\u6b63\u662f\u8fd9\u79cd\u89e3\u836f\uff0c\u8fd9\u2026\u2026\u8fd9\u53ef\u6709\u6551\u4e86\u3002\u201d \u3000\u3000\u72c4\u4e91\u542c\u5f97\u4ed6\u58f0\u97f3\u6709\u5f02\uff0c\u672c\u6765\u8bf4\u201c\u8fd9\u53ef\u6709\u6551\u4e86\u201d\u4e94\u5b57\uff0c\u8be5\u5f53\u6b22\u559c\u624d\u662f\uff0c\u53ef\u662f\u4ed6\u8bed\u97f3\u4e2d\u5374\u663e\u5f97\u5f02\u5e38\u5931\u671b\uff0c\u8fd8\u5e26\u7740\u51e0\u5206\u6c14\u607c\uff0c\u72c4\u4e91\u89c9\u5f97\u5947\u602a\uff0c\u4fa7\u5934\u5411\u4ed6\u77a7\u4e86\u4e00\u773c\uff0c\u53ea\u89c1\u4ed6\u773c\u4e2d\u9732\u51fa\u5341\u5206\u51f6\u72e0\u6076\u6bd2\u7684\u795e\u8272\u3002\u72c4\u4e91\u66f4\u89c9\u5947\u602a\uff0c\u4f46\u60f3\u4e07\u95e8\u516b\u5f1f\u5b50\u4e2d\u6ca1\u4e00\u4e2a\u597d\u4eba\u3002\u4e07\u9707\u5c71\u3001\u8a00\u8fbe\u5e73\u4ed6\u4eec\u540c\u95e8\u76f8\u6b8b\uff0c\u4e07\u572d\u548c\u5434\u574e\u7684\u4ea4\u60c5\u672a\u5fc5\u4f1a\u597d\uff0c\u53ea\u662f\u4ed6\u4f55\u4ee5\u53c8\u51fa\u6765\u66ff\u4e07\u572d\u627e\u533b\u751f\u770b\u75c5\uff1f \u3000\u3000\u4e07\u572d\u7684\u624b\u80cc\u4e00\u6577\u4e0a\u836f\u672b\uff0c\u8fc7\u4e0d\u591a\u65f6\uff0c\u4f24\u53e3\u4e2d\u4fbf\u6d41\u51fa\u9ed1\u8840\u6765\u3002\u4ed6\u75db\u695a\u6e10\u51cf\uff0c\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u5927\u592b\uff0c\u8fd9\u89e3\u836f\u53ef\u7528\u5f97\u5bf9\u4e86\u3002\u201d\u621a\u82b3\u5927\u559c\uff0c\u53d6\u8fc7\u4e00\u53ea\u94dc\u76c6\u6765\u63a5\u8840\uff0c\u53ea\u542c\u5f97\u55d2\u3001\u55d2\u3001\u55d2\u4e00\u58f0\u58f0\u8f7b\u54cd\uff0c\u8840\u6db2\u6ef4\u5165\u94dc\u76c6\u4e4b\u4e2d\u3002\u621a\u82b3\u5411\u72c4\u4e91\u8fde\u58f0\u79f0\u8c22\u3002 \u3000\u3000\u5434\u574e\u9053\uff1a\u201c\u4e09\u5e08\u5ac2\uff0c\u5c0f\u5f1f\u8fd9\u56de\u53ef\u6709\u529f\u4e86\u7f62\uff1f\u201d\u621a\u82b3\u9053\uff1a\u201c\u662f\uff0c\u786e\u8981\u591a\u8c22\u5434\u5e08\u5f1f\u624d\u662f\u3002\u201d\u5434\u574e\u7b11\u9053\uff1a\u201c\u7a7a\u53e3\u8bf4\u51e0\u58f0\u8c22\u8c22\uff0c\u90a3\u53ef\u4e0d\u6210\u3002\u201d\u621a\u82b3\u6ca1\u518d\u7406\u4ed6\uff0c\u5411\u72c4\u4e91\u9053\uff1a\u201c\u5148\u751f\u8d35\u59d3\uff1f\u6211\u4eec\u53ef\u5f97\u91cd\u91cd\u916c\u8c22\u3002\u201d \u3000\u3000\u72c4\u4e91\u6447\u5934\u9053\uff1a\u201c\u4e0d\u7528\u8c22\u4e86\u3002\u8fd9\u874e\u6bd2\u8981\u8fde\u6577\u5341\u6b21\u836f\uff0c\u65b9\u80fd\u89e3\u9664\u3002\u201d\u5fc3\u4e2d\u9178\u695a\uff0c\u4f46\u89c9\u4e16\u4e0a\u4e8b\u4e8b\u90fd\u662f\u82e6\uff0c\u8bf4\u9053\uff1a\u201c\u90fd\u7ed9\u4e86\u4f60\u7f62\uff01\u201d \u3000\u3000\u5c06\u90a3\u74f6\u89e3\u836f\u9012\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u621a\u82b3\u6ca1\u6599\u5230\u4e8b\u60c5\u7adf\u662f\u8fd9\u822c\u5bb9\u6613\uff0c\u4e00\u65f6\u5374\u4e0d\u6562\u4fbf\u63a5\u3002\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u5411\u5148\u751f\u4e70\u4e86\uff0c\u4e0d\u77e5\u8981\u591a\u5c11\u94f6\u5b50\uff1f\u201d\u72c4\u4e91\u6447\u5934\u9053\uff1a\u201c\u9001\u7ed9\u4f60\u7684\uff0c\u4e0d\u7528\u94f6\u5b50\u3002\u201d \u3000\u3000\u621a\u82b3\u5927\u559c\uff0c\u53cc\u624b\u63a5\u4e86\u8fc7\u53bb\uff0c\u8eac\u8eab\u4e07\u798f\uff0c\u6df1\u6df1\u81f4\u8c22\uff0c\u9053\uff1a\u201c\u5148\u751f\u5982\u6b64\u4ed7\u4e49\uff0c\u771f\u4e0d\u77e5\u8be5\u5f53\u600e\u751f\u76f8\u8c22\u624d\u597d\u3002\u5434\u5e08\u5f1f\uff0c\u8bf7\u4f60\u966a\u8fd9\u4f4d\u5148\u751f\u5230\u697c\u4e0b\u7a0d\u5750\u3002\u201d\u72c4\u4e91\u9053\uff1a\u201c\u4e0d\u5750\u4e86\uff0c\u544a\u8f9e\u3002\u201d\u621a\u82b3\u9053\uff1a\u201c\u4e0d\uff0c\u4e0d\uff0c\u5148\u751f\u7684\u6551\u547d\u5927\u6069\uff0c\u6211\u4eec\u65e0\u6cd5\u62a5\u7b54\uff0c\u4e00\u676f\u6c34\u9152\uff0c\u65e0\u8bba\u5982\u4f55\u662f\u8981\u656c\u4f60\u7684\u3002\u5148\u751f\uff0c\u4f60\u522b\u8d70\u554a\uff01\u201d \u3000\u3000\u201c\u4f60\u522b\u8d70\u554a\uff01\u201d\u8fd9\u56db\u4e2a\u5b57\u4e00\u94bb\u5165\u72c4\u4e91\u8033\u4e2d\uff0c\u4ed6\u5fc3\u80a0\u767b\u65f6\u8f6f\u4e86\uff0c\u5bfb\u601d\uff1a\u201c\u6211\u8fd9\u4ec7\u662f\u62a5\u4e0d\u6210\u4e86\uff0c\u846c\u4e86\u4e01\u5927\u54e5\u540e\uff0c\u518d\u4e5f\u4e0d\u4f1a\u5230\u8346\u5dde\u57ce\u6765\u3002\u4eca\u751f\u4eca\u4e16\uff0c\u662f\u4e0d\u4f1a\u518d\u548c\u5e08\u59b9\u76f8\u89c1\u4e86\u3002\u5979\u8981\u656c\u6211\u4e00\u676f\u9152\uff0c\u55ef\uff0c\u518d\u591a\u77a7\u5979\u51e0\u773c\uff0c\u4e5f\u662f\u597d\u7684\u3002\u201d\u5f53\u4e0b\u4fbf\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u9152\u5e2d\u4fbf\u8bbe\u5728\u697c\u4e0a\u7684\u5c0f\u5ba2\u5802\u4e2d\uff0c\u72c4\u4e91\u5c45\u4e2d\u4e0a\u5ea7\uff0c\u5434\u574e\u6253\u6a2a\u76f8\u966a\u3002\u621a\u82b3\u4e07\u5206\u611f\u8c22\u8fd9\u4f4d\u5927\u592b\u7684\u6069\u5fb7\uff0c\u4eb2\u81ea\u4e0a\u83dc\u3002\u4e07\u5e9c\u4e2d\u4e07\u9707\u5c71\u7b49\u4e00\u5e72\u4eba\u4f3c\u4e4e\u90fd\u4e0d\u5728\u5bb6\uff0c\u5176\u4f59\u7684\u5f1f\u5b50\u4e5f\u6ca1\u6765\u5165\u5e2d\u996e\u9152\u3002 \u3000\u3000\u621a\u82b3\u606d\u606d\u656c\u656c\u7684\u656c\u4e86\u4e09\u676f\u9152\uff0c\u72c4\u4e91\u63a5\u8fc7\u6765\u90fd\u559d\u5e72\u4e86\uff0c\u5fc3\u4e2d\u4e00\u9178\uff0c\u773c\u7736\u4e2d\u5145\u76c8\u4e86\u773c\u6cea\uff0c\u77e5\u9053\u518d\u4e5f\u65e0\u6cd5\u652f\u6301\u4e0b\u53bb\uff0c\u518d\u5750\u5f97\u4e00\u4f1a\uff0c\u4fbf\u4f1a\u9732\u51fa\u5f62\u8ff9\uff0c\u5f53\u5373\u7ad9\u8d77\u8eab\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u9152\u5df2\u8db3\u591f\uff0c\u6211\u8fd9\u53ef\u8981\u53bb\u4e86\uff01\u4ece\u4eca\u4ee5\u540e\uff0c\u518d\u4e5f\u4e0d\u4f1a\u6765\u4e86\uff01\u201d\u621a\u82b3\u542c\u4ed6\u8bf4\u8bdd\u4e0d\u4f26\u4e0d\u7c7b\uff0c\u4f46\u8fd9\u4f4d\u90ce\u4e2d\u672c\u6765\u5341\u5206\u53e4\u602a\uff0c\u4e5f\u4e0d\u4ee5\u4e3a\u610f\uff0c\u8bf4\u9053\uff1a\u201c\u5148\u751f\uff0c\u5927\u6069\u5927\u5fb7\uff0c\u6211\u4eec\u65e0\u6cd5\u76f8\u8c22\uff0c\u8fd9\u91cc\u4e00\u767e\u4e24\u7eb9\u94f6\uff0c\u8bf7\u5148\u751f\u8def\u4e0a\u4e70\u9152\u559d\u3002\u201d\u8bf4\u7740\u53cc\u624b\u6367\u8fc7\u4e00\u5305\u94f6\u5b50\u3002 \u3000\u3000\u72c4\u4e91\u8f6c\u5f00\u4e86\u5934\uff0c\u4ef0\u5929\u54c8\u54c8\u5927\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u662f\u6211\u6551\u6d3b\u4e86\u4ed6\uff0c\u662f\u6211\u6551\u6d3b\u4e86\u4ed6\uff0c\u54c8\u54c8\uff0c\u54c8\u54c8\uff01\u771f\u597d\u7b11\uff01\u5929\u4e0b\u8fd8\u6709\u6bd4\u6211\u66f4\u50bb\u7684\u4eba\u4e48\uff1f\u201d\u4ed6\u7eb5\u58f0\u5927\u7b11\uff0c\u8138\u988a\u4e0a\u5374\u6d41\u4e0b\u4e86\u4e24\u9053\u773c\u6cea\u3002 \u3000\u3000\u621a\u82b3\u548c\u5434\u574e\u89c1\u4ed6\u4f3c\u75af\u4f3c\u766b\uff0c\u4e0d\u7981\u76f8\u987e\u6115\u7136\u3002\u90a3\u5c0f\u5973\u5b69\u5374\u9053\uff1a\u201c\u4f2f\u4f2f\u54ed\u4e86\uff01\u4f2f\u4f2f\u54ed\u4e86\u3002\u201d \u3000\u3000\u72c4\u4e91\u5fc3\u4e2d\u4e00\u60ca\uff0c\u751f\u6015\u9732\u51fa\u4e86\u9a6c\u811a\uff0c\u4e0d\u6562\u518d\u548c\u621a\u82b3\u8bf4\u8bdd\uff0c\u5fc3\u9053\uff1a\u201c\u4ece\u6b64\u4e4b\u540e\uff0c\u6211\u662f\u518d\u4e5f\u4e0d\u89c1\u4f60\u4e86\u3002\u201d\u4f38\u624b\u5165\u6000\uff0c\u6478\u51fa\u90a3\u672c\u4ece\u6c85\u9675\u77f3\u6d1e\u4e2d\u53d6\u6765\u7684\u5939\u978b\u6837\u8bd7\u96c6\uff0c\u62e2\u5728\u8863\u8896\u4e4b\u4e2d\uff0c\u5782\u4e0b\u8896\u53bb\u6084\u6084\u653e\u5728\u6905\u4e0a\uff0c\u4e0d\u6562\u518d\u5411\u621a\u82b3\u77a7\u4e0a\u4e00\u773c\uff0c\u5934\u4e5f\u4e0d\u56de\u7684\u5411\u697c\u4e0b\u53bb\u4e86\u3002 \u3000\u3000\u621a\u82b3\u9053\uff1a\u201c\u5434\u5e08\u5f1f\uff0c\u4f60\u7ed9\u6211\u9001\u9001\u5148\u751f\u3002\u201d\u5434\u574e\u9053\uff1a\u201c\u597d\uff01\u201d\u8ddf\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u621a\u82b3\u624b\u4e2d\u6367\u7740\u90a3\u5305\u94f6\u5b50\uff0c\u4e00\u9897\u5fc3\u6026\u6026\u4e71\u8df3\uff1a\u201c\u8fd9\u4f4d\u5148\u751f\u5230\u5e95\u662f\u751a\u4e48\u4eba\uff1f\u4ed6\u7684\u7b11\u58f0\u600e\u5730\u548c\u90a3\u4eba\u8fd9\u4e48\u50cf\uff1f\u5509\uff0c\u6211\u600e\u4e48\u4e86\uff1f\u8fd9\u4e9b\u65e5\u5b50\u6765\uff0c\u4e09\u54e5\u7684\u4f24\u8fd9\u4e48\u91cd\uff0c\u6211\u5fc3\u4e2d\u5374\u98a0\u4e09\u5012\u56db\u7684\uff0c\u8001\u662f\u60f3\u7740\u4ed6\u2026\u2026\u4ed6\u2026\u2026\u4ed6\u2026\u2026\u201d\u968f\u624b\u5c06\u94f6\u5b50\u653e\u5728\u684c\u4e0a\uff0c\u4ee5\u624b\u652f\u9890\uff0c\u53c8\u5750\u5728\u6905\u4e0a\u3002 \u3000\u3000\u90a3\u5f20\u6905\u5b50\u662f\u72c4\u4e91\u5750\u8fc7\u7684\uff0c\u53ea\u89c9\u6905\u4e0a\u6709\u7269\uff0c\u5fd9\u7ad9\u8d77\u8eab\u6765\uff0c\u89c1\u662f\u4e00\u672c\u9ec4\u9ec4\u7684\u65e7\u4e66\uff0c\u5c01\u76ae\u4e0a\u5199\u7740\u300a\u5510\u8bd7\u9009\u8f91\u300b\u56db\u5b57\u3002 \u3000\u3000\u5979\u8f7b\u547c\u4e00\u58f0\uff0c\u4f38\u624b\u62ff\u4e86\u8d77\u6765\uff0c\u968f\u624b\u4e00\u7ffb\uff0c\u4e66\u4e2d\u8dcc\u51fa\u4e00\u5f20\u978b\u6837\uff0c\u6b63\u662f\u81ea\u5df1\u5f53\u5e74\u5728\u6e58\u897f\u8001\u5bb6\u4e2d\u526a\u7684\u3002\u5979\u767b\u65f6\u5f20\u5927\u4e86\u53e3\u5408\u4e0d\u62e2\u6765\uff0c\u53cc\u624b\u53d1\u6296\uff0c\u53c8\u7ffb\u8fc7\u51e0\u9875\uff0c\u89c1\u5230\u4e00\u5bf9\u8774\u8776\u7684\u526a\u7eb8\u82b1\u6837\u3002 \u3000\u3000\u5f53\u5e74\u548c\u72c4\u4e91\u5728\u5c71\u6d1e\u4e2d\u5e76\u80a9\u5171\u5750\u3001\u526a\u6210\u8fd9\u5bf9\u7eb8\u8776\u65f6\u7684\u60c5\u666f\uff0c\u84e6\u5730\u91cc\u5982\u95ea\u7535\u822c\u6620\u5165\u8111\u6d77\u3002\u5979\u5fcd\u4e0d\u4f4f\u201c\u554a\u201d\u7684\u4e00\u58f0\u53eb\u4e86\u51fa\u6765\uff0c\u5fc3\u4e2d\u53ea\u9053\uff1a\u201c\u8fd9\u2026\u2026\u8fd9\u672c\u4e66\u4ece\u54ea\u91cc\u6765\u7684\uff1f\u662f\u2026\u2026\u662f\u8c01\u5e26\u6765\u7684\uff1f\u96be\u9053\u662f\u90a3\u90ce\u4e2d\u5148\u751f\uff1f\u201d \u3000\u3000\u5c0f\u5973\u5b69\u89c1\u6bcd\u4eb2\u795e\u60c5\u6709\u5f02\uff0c\u60ca\u614c\u8d77\u6765\uff0c\u8fde\u53eb\uff1a\u201c\u5988\uff0c\u5988\uff0c\u4f60\u2026\u2026\u505a\u751a\u4e48\uff1f\u201d \u3000\u3000\u621a\u82b3\u4e00\u6014\u4e4b\u95f4\uff0c\u6293\u8d77\u90a3\u672c\u4e66\u63e3\u5165\u6000\u4e2d\uff0c\u98de\u5954\u4e0b\u697c\uff0c\u5411\u95e8\u5916\u76f4\u8ffd\u51fa\u53bb\u3002\u5979\u81ea\u4ece\u5ac1\u4f5c\u4e07\u5bb6\u5ab3\u5987\u4ee5\u6765\uff0c\u4e00\u76f4\u65af\u65af\u6587\u6587\uff0c\u8fd9\u822c\u5728\u5385\u5802\u95f4\u72c2\u5954\u6025\u9a70\uff0c\u90a3\u662f\u4ece\u6765\u6ca1\u6709\u7684\u4e8b\u3002\u4e07\u5bb6\u5a62\u4ec6\u5ffd\u89c1\u5c11\u5976\u5976\u5c55\u5f00\u8f7b\u529f\uff0c\u8fde\u7a7f\u51e0\u4e2a\u5929\u4e95\uff0c\u6025\u51b2\u800c\u51fa\uff0c\u65e0\u4e0d\u60ca\u8bb6\u3002 \u3000\u3000\u621a\u82b3\u5954\u5230\u524d\u5385\uff0c\u89c1\u5434\u574e\u4ece\u95e8\u5916\u8fdb\u6765\uff0c\u5fd9\u95ee\uff1a\u201c\u90a3\u90ce\u4e2d\u5148\u751f\u5462\uff1f\u201d\u5434\u574e\u9053\uff1a\u201c\u8fd9\u4eba\u53e4\u91cc\u53e4\u602a\u7684\uff0c\u4e00\u53e5\u8bdd\u4e0d\u8bf4\u4fbf\u8d70\u4e86\u3002\u4e09\u5e08\u5ac2\uff0c\u4f60\u627e\u4ed6\u5e72\u4e48\uff1f\u5e08\u54e5\u7684\u4f24\u6709\u53cd\u590d\u4e48\uff1f\u201d\u621a\u82b3\u9053\uff1a\u201c\u4e0d\uff0c\u4e0d\uff01\u201d \u3000\u3000\u6025\u6b65\u5954\u51fa\u5927\u95e8\uff0c\u56db\u4e0b\u5f20\u671b\uff0c\u5df2\u4e0d\u89c1\u5356\u836f\u90ce\u4e2d\u7684\u8e2a\u8ff9\u3002 \u3000\u3000\u5979\u5728\u5927\u95e8\u5916\u5446\u7acb\u534a\u664c\uff0c\u4f38\u624b\u53c8\u4ece\u6000\u4e2d\u53d6\u51fa\u65e7\u4e66\u7ffb\u52a8\uff0c\u6bcf\u89c1\u5230\u4e00\u5f20\u978b\u6837\uff0c\u4e00\u5f20\u82b1\u6837\uff0c\u5c11\u65f6\u79cd\u79cd\u6b22\u4e50\u60c5\u4e8b\uff0c\u4fbf\u5982\u6f6e\u6c34\u822c\u6d8c\u5411\u5fc3\u5934\uff0c\u773c\u6cea\u4e0d\u7981\u593a\u7736\u800c\u51fa\u3002 \u3000\u3000\u5979\u5ffd\u7136\u8f6c\u5ff5\uff1a\u201c\u6211\u600e\u4e48\u8fd9\u6837\u50bb\uff1f\u516c\u516c\u548c\u4e09\u54e5\u4ed6\u4eec\u6700\u8fd1\u5230\u6e58\u897f\u53bb\u89c1\u8a00\u5e08\u53d4\uff0c\u8bf4\u4e0d\u5b9a\u65e0\u610f\u4e2d\u95ef\u8fdb\u4e86\u90a3\u4e2a\u5c71\u6d1e\uff0c\u968f\u624b\u53d6\u4e86\u8fd9\u672c\u4e66\u6765\uff0c\u4e5f\u662f\u6709\u7684\u3002\u8fd9\u4f4d\u90ce\u4e2d\u5148\u751f\uff0c\u600e\u4f1a\u548c\u8fd9\u4e66\u6709\u751a\u76f8\u5e72\uff1f\u201d \u3000\u3000\u4f46\u968f\u5373\u53c8\u60f3\uff1a\u201c\u4e0d\uff0c\u4e0d\uff01\u4e8b\u60c5\u54ea\u4f1a\u8fd9\u4e48\u5de7\u6cd5\uff1f\u90a3\u5c71\u6d1e\u9690\u79d8\u4e4b\u6781\uff0c\u8fde\u7239\u7239\u4e5f\u4e0d\u77e5\u9053\uff0c\u4e16\u4e0a\u9664\u6211\u4e4b\u5916\uff0c\u5c31\u53ea\u5e08\u54e5\u4ed6\u2026\u2026\u4ed6\u4e00\u4eba\u77e5\u9053\uff0c\u516c\u516c\u548c\u4e09\u54e5\u4ed6\u4eec\u600e\u627e\u5f97\u5230\uff1f\u4ed6\u4eec\u662f\u53bb\u5bfb\u8bbf\u8a00\u5e08\u53d4\uff0c\u600e\u4f1a\u95ef\u8fdb\u8fd9\u5c71\u6d1e\u53bb\uff1f\u521a\u624d\u6211\u6446\u8bbe\u9152\u5e2d\u4e4b\u65f6\uff0c\u660e\u660e\u8bb0\u5f97\u62b9\u8fc7\u8fd9\u5f20\u6905\u5b50\uff0c\u54ea\u91cc\u6709\u751a\u4e48\u4e66\u672c\uff1f\u8fd9\u672c\u4e66\u82e5\u4e0d\u662f\u90a3\u90ce\u4e2d\u5e26\u6765\uff0c\u5374\u662f\u4ece\u54ea\u91cc\u6765\u7684\uff1f\u201d \u3000\u3000\u5979\u6ee1\u8179\u7591\u4e91\uff0c\u6162\u6162\u56de\u5230\u623f\u4e2d\uff0c\u89c1\u4e07\u572d\u6577\u4e86\u4f24\u836f\u4e4b\u540e\uff0c\u7cbe\u795e\u5df2\u597d\u5f97\u591a\u4e86\u3002\u5979\u624b\u4e2d\u63e1\u7740\u90a3\u672c\u4e66\uff0c\u4fbf\u60f3\u8be2\u95ee\u4e08\u592b\uff0c\u4f46\u8f6c\u5ff5\u4e00\u60f3\uff1a\u201c\u4e14\u83ab\u9c81\u83bd\uff0c\u5018\u82e5\u90a3\u90ce\u4e2d\u2026\u2026\u90a3\u90ce\u4e2d\u2026\u2026\u201d \u3000\u3000\u4e07\u572d\u9053\uff1a\u201c\u82b3\u59b9\uff0c\u8fd9\u4f4d\u90ce\u4e2d\u5148\u751f\u771f\u662f\u6211\u7684\u6551\u547d\u6069\u4eba\uff0c\u987b\u5f97\u597d\u597d\u916c\u8c22\u4ed6\u624d\u662f\u3002\u201d\u621a\u82b3\u9053\uff1a\u201c\u662f\u554a\uff0c\u6211\u9001\u4ed6\u4e00\u767e\u4e24\u94f6\u5b50\uff0c\u4ed6\u53c8\u4e0d\u80af\u53d7\uff0c\u771f\u662f\u4e00\u4f4d\u6c5f\u6e56\u5f02\u4eba\u3002\u8fd9\u74f6\u89e3\u836f\u2026\u2026\u54a6\uff0c\u89e3\u836f\u5462\uff1f\u662f\u4f60\u6536\u4e86\u8d77\u6765\u4e48\uff1f\u201d\u5356\u836f\u90ce\u4e2d\u5c06\u89e3\u836f\u4ea4\u4e86\u7ed9\u5979\u4e4b\u540e\uff0c\u5979\u4fbf\u653e\u5728\u4e07\u572d\u5e8a\u524d\u7684\u684c\u4e0a\uff0c\u8fd9\u65f6\u5374\u5df2\u4e0d\u89c1\u3002\u4e07\u572d\u9053\uff1a\u201c\u6ca1\u6709\uff0c\u4e0d\u5728\u684c\u4e0a\u4e48\uff1f\u201d \u3000\u3000\u621a\u82b3\u5728\u684c\u4e0a\u3001\u5e8a\u8fb9\u3001\u68b3\u5986\u53f0\u3001\u6905\u5b50\u3001\u7bb1\u67dc\u3001\u5e8a\u5e95\u3001\u684c\u5e95\u5404\u5904\u5bfb\u627e\uff0c\u89e3\u836f\u7adf\u662f\u5f71\u8e2a\u4e0d\u89c1\u3002\u5979\u5fc3\u4e2d\u5927\u6025\uff1a\u201c\u96be\u9053\u6211\u9002\u624d\u795e\u667a\u4e0d\u5b9a\uff0c\u5954\u51fa\u53bb\u65f6\u843d\u5728\u5730\u4e0b\u4e86\uff1f\u4e0d\uff0c\u6211\u8bb0\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u662f\u653e\u5728\u684c\u4e0a\u8fd9\u53ea\u836f\u7897\u8fb9\u7684\u3002\u201d\u4e07\u572d\u4e5f\u5f88\u7126\u6025\uff0c\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u5feb\u518d\u627e\u627e\uff0c\u600e\u4e48\u4f1a\u4e0d\u89c1\u7684\uff1f\u6211\u521a\u624d\u5408\u4e86\u4e00\u5ffd\u513f\u773c\uff0c\u4e34\u7761\u7740\u7684\u65f6\u5019\uff0c\u8bb0\u5f97\u8fd8\u770b\u5230\u8fd9\u74f7\u74f6\u513f\u4fbf\u5728\u684c\u4e0a\u3002\u201d \u3000\u3000\u4ed6\u8fd9\u4e48\u4e00\u8bf4\uff0c\u621a\u82b3\u66f4\u52a0\u7740\u6025\u4e86\uff0c\u8f6c\u8eab\u51fa\u623f\uff0c\u62c9\u7740\u5973\u513f\u95ee\u9053\uff1a\u201c\u521a\u624d\u5988\u51fa\u53bb\u65f6\uff0c\u6709\u8c01\u8fdb\u6765\u8fc7\u4e86\uff1f\u201d\u5c0f\u5973\u5b69\u9053\uff1a\u201c\u5434\u53d4\u53d4\u4e0a\u6765\u8fc7\uff0c\u4ed6\u89c1\u7239\u7239\u7761\u7740\u4e86\uff0c\u5c31\u4e0b\u53bb\u5566\uff01\u201d \u3000\u3000\u621a\u82b3\u5401\u4e86\u4e00\u53e3\u957f\u6c14\uff0c\u9690\u9690\u77e5\u9053\u4e8b\u60c5\u4e0d\u5bf9\uff0c\u4f46\u4e07\u572d\u6b63\u5728\u75c5\u4e2d\uff0c\u4e0d\u80fd\u4ee4\u4ed6\u62c5\u5fe7\uff0c\u8bf4\u9053\uff1a\u201c\u7a7a\u5fc3\u83dc\uff0c\u4f60\u966a\u7740\u7239\u7239\uff0c\u8bf4\u5988\u5988\u53bb\u5411\u90ce\u4e2d\u5148\u751f\u518d\u4e70\u4e00\u74f6\u836f\uff0c\u7ed9\u7239\u7239\u533b\u4f24\u3002\u201d\u5c0f\u5973\u5b69\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u5988\uff0c\u4f60\u5feb\u4e9b\u56de\u6765\u3002\u201d \u3000\u3000\u621a\u82b3\u5b9a\u4e86\u5b9a\u795e\uff0c\u62c9\u5f00\u4e66\u684c\u62bd\u5c49\uff0c\u53d6\u51fa\u4e00\u67c4\u5315\u9996\uff0c\u8d34\u8eab\u85cf\u7740\uff0c\u6162\u6162\u8d70\u4e0b\u697c\u53bb\uff0c\u5bfb\u601d\uff1a\u201c\u5434\u574e\u8fd9\u53ae\u5728\u6ca1\u4eba\u4e4b\u5904\u89c1\u5230\u6211\uff0c\u603b\u662f\u8d3c\u5fd2\u563b\u563b\u7684\u4e0d\u6000\u597d\u610f\u3002\u8fd9\u90ce\u4e2d\u662f\u4ed6\u8bf7\u6765\u7684\uff0c\u83ab\u975e\u4ed6\u548c\u90ce\u4e2d\u4e32\u901a\u4e86\uff0c\u5b89\u6392\u4e0b\u751a\u4e48\u9634\u8c0b\u8be1\u8ba1\uff1f\u5426\u5219\u4e3a\u751a\u4e48\u90a3\u90ce\u4e2d\u65e2\u4e0d\u8981\u94b1\uff0c\u89e3\u836f\u53c8\u4e0d\u89c1\u4e86\uff1f\u201d \u3000\u3000\u5979\u4e00\u9762\u601d\u7d22\uff0c\u4e00\u9762\u8d70\u5411\u540e\u56ed\uff0c\u5230\u5f97\u56de\u5eca\uff0c\u53ea\u89c1\u5434\u574e\u501a\u7740\u680f\u5e72\uff0c\u5728\u77a7\u6c60\u91cc\u7684\u91d1\u9c7c\u3002\u621a\u82b3\u9053\uff1a\u201c\u5434\u5e08\u5f1f\uff0c\u4f60\u4e00\u4e2a\u4eba\u5728\u8fd9\u91cc\uff1f\u201d \u3000\u3000\u5434\u574e\u56de\u8fc7\u5934\u6765\uff0c\u6ee1\u8138\u7709\u82b1\u773c\u7b11\uff0c\u9053\uff1a\u201c\u6211\u9053\u662f\u8c01\uff0c\u539f\u6765\u662f\u4e09\u5e08\u5ac2\u3002\u600e\u4e48\u4e0d\u5728\u697c\u4e0a\u966a\u4f34\u4e09\u5e08\u54e5\uff0c\u597d\u5174\u81f4\u5230\u8fd9\u91cc\u6765\uff1f\u201d\u621a\u82b3\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5509\uff0c\u6211\u95f7\u5f97\u5f88\u3002\u6574\u5929\u966a\u7740\u4e2a\u75c5\u4eba\uff0c\u4f60\u5e08\u54e5\u624b\u4e0a\u75db\u5f97\u72e0\u4e86\uff0c\u813e\u6c14\u5c31\u8d8a\u6765\u8d8a\u574f\u3002\u4e0d\u51fa\u6765\u6563\u6563\u5fc3\uff0c\u627e\u4e2a\u4eba\u8bf4\u8bdd\u89e3\u95f7\u513f\uff0c\u53ef\u628a\u4eba\u4e5f\u618b\u6b7b\u4e86\u3002\u201d\u5434\u574e\u4e00\u542c\uff0c\u5f53\u771f\u559c\u51fa\u671b\u5916\uff0c\u7b11\u9053\uff1a\u201c\u4e09\u5e08\u54e5\u4e5f\u771f\u53eb\u505a\u4eba\u5fc3\u4e0d\u8db3\u86c7\u541e\u8c61\uff0c\u6709\u4f60\u8fd9\u6837\u5982\u82b1\u4f3c\u7389\u7684\u4e00\u4e2a\u7f8e\u4eba\u513f\u76f8\u4f34\uff0c\u8fd8\u8981\u53d1\u813e\u6c14\uff0c\u90a3\u53ef\u4e5f\u592a\u96be\u4f8d\u5019\u4e86\u3002\u201d \u3000\u3000\u621a\u82b3\u8d70\u5230\u4ed6\u8eab\u8fb9\uff0c\u4e5f\u9760\u5728\u680f\u5e72\u4e0a\uff0c\u671b\u7740\u6c60\u4e2d\u91d1\u9c7c\uff0c\u7b11\u9053\uff1a\u201c\u5e08\u5ac2\u662f\u8001\u592a\u5a46\u5566\uff0c\u8fd8\u8bf4\u751a\u4e48\u5982\u82b1\u4f3c\u7389\uff0c\u4e5f\u4e0d\u6015\u4eba\u7b11\u6b6a\u4e86\u5634\u3002\u201d \u3000\u3000\u5434\u574e\u5fd9\u9053\uff1a\u201c\u54ea\u91cc\uff1f\u54ea\u91cc\uff1f\u5e08\u5ac2\u505a\u95fa\u5973\u65f6\u6709\u95fa\u5973\u7684\u7f8e\u8c8c\uff0c\u505a\u5c11\u5976\u5976\u65f6\u6709\u5c11\u5976\u5976\u7684\u4fca\u4fcf\u3002\u5927\u5bb6\u90fd\u8bf4\uff1a\u8346\u5dde\u57ce\u91cc\u4e00\u6735\u82b1\uff0c\u5343\u5a07\u767e\u5a9a\u5728\u4e07\u5bb6\u3002\u201d \u3000\u3000\u621a\u82b3\u563f\u7684\u4e00\u58f0\uff0c\u8f6c\u8fc7\u8eab\u6765\uff0c\u4f38\u51fa\u624b\u53bb\uff0c\u8bf4\u9053\uff1a\u201c\u62ff\u6765\uff01\u201d \u3000\u3000\u5434\u574e\u7b11\u9053\uff1a\u201c\u62ff\u751a\u4e48\uff1f\u201d\u621a\u82b3\u9053\uff1a\u201c\u89e3\u836f\uff01\u201d\u5434\u574e\u6447\u5934\u9053\uff1a\u201c\u751a\u4e48\u89e3\u836f\uff1f\u6cbb\u4e07\u5e08\u54e5\u4f24\u7684\u4e48\uff1f\u201d\u621a\u82b3\u9053\uff1a\u201c\u6b63\u662f\uff0c\u660e\u660e\u662f\u4f60\u62ff\u53bb\u4e86\u3002\u201d\u5434\u574e\u72e1\u72ef\u5fae\u7b11\uff0c\u9053\uff1a\u201c\u90ce\u4e2d\u662f\u6211\u8bf7\u6765\u7684\uff0c\u89e3\u836f\u662f\u6211\u5bfb\u6765\u7684\u3002\u4e07\u5e08\u54e5\u5df2\u6577\u8fc7\u4e00\u6b21\uff0c\u5c11\u8bf4\u4e5f\u53ef\u514d\u4e86\u6570\u65e5\u7684\u75db\u82e6\u3002\u201d\u621a\u82b3\u9053\uff1a\u201c\u90ce\u4e2d\u5148\u751f\u8bf4\u9053\u8981\u8fde\u6577\u5341\u6b21\u3002\u201d\u5434\u574e\u6447\u5934\u9053\uff1a\u201c\u6211\u61ca\u6094\u5f97\u7d27\uff0c\u61ca\u6094\u5f97\u7d27\u3002\u201d\u621a\u82b3\u9053\uff1a\u201c\u61ca\u6094\u751a\u4e48\uff1f\u201d\u5434\u574e\u9053\uff1a\u201c\u6211\u89c1\u8fd9\u8349\u836f\u90ce\u4e2d\u6c61\u79fd\u80ae\u810f\uff0c\u5c31\u50cf\u53eb\u5316\u5b50\u4e00\u822c\uff0c\u6599\u60f3\u4e5f\u6ca1\u751a\u4e48\u672c\u4e8b\uff0c\u8fd9\u624d\u5f15\u4ed6\u4e0a\u697c\uff0c\u4e0d\u8fc7\u60f3\u627e\u4e2a\u4e8b\u7aef\uff0c\u591a\u89c1\u4f60\u4e00\u6b21\uff0c\u6ca1\u60f3\u5230\u8fd9\u72d7\u6740\u624d\u8bef\u6253\u8bef\u649e\uff0c\u5c45\u7136\u6709\u6cbb\u874e\u6bd2\u7684\u5999\u836f\u3002\u8fd9\u4e2a\uff0c\u90a3\u53ef\u662f\u5927\u8fdd\u6211\u7684\u672c\u610f\u4e86\u3002\u201d \u3000\u3000\u621a\u82b3\u542c\u5f97\u5fc3\u5934\u706b\u53d1\uff0c\u53ef\u662f\u836f\u5728\u4eba\u5bb6\u624b\u4e2d\uff0c\u53ea\u6709\u5148\u5c06\u89e3\u836f\u9a97\u5230\u4e86\u624b\uff0c\u518d\u8ddf\u4ed6\u7b97\u5e10\uff0c\u5f53\u4e0b\u5f3a\u5fcd\u6012\u6c14\uff0c\u7b11\u9053\uff1a\u201c\u4f9d\u4f60\u8bf4\uff0c\u8981\u4f60\u5e08\u54e5\u600e\u4e48\u8c22\u4f60\uff0c\u4f60\u624d\u80af\u5c06\u89e3\u836f\u4ea4\u51fa\u6765\uff1f\u201d \u3000\u3000\u5434\u574e\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4e09\u5e08\u54e5\u5df2\u4eab\u4e86\u8fd9\u8bb8\u591a\u5e74\u8273\u798f\u3002\u65e9\u5c31\u8be5\u6b7b\u4e86\u3002\u201d\u621a\u82b3\u8138\u4e0a\u53d8\u8272\uff0c\u54ac\u4f4f\u5634\u5507\u76ae\u4e0d\u8bed\u3002\u5434\u574e\u9053\uff1a\u201c\u90a3\u5e74\u4f60\u5230\u8346\u5dde\u6765\uff0c\u6211\u4eec\u5e08\u5144\u5f1f\u516b\u4eba\uff0c\u54ea\u4e00\u4e2a\u4e0d\u662f\u4e00\u89c1\u4e86\u4f60\u4fbf\u795e\u9b42\u98a0\u5012\uff1f\u72c4\u4e91\u90a3\u50bb\u5c0f\u5b50\u4e00\u5929\u5230\u665a\u8ddf\u5728\u4f60\u8eab\u8fb9\uff0c\u6211\u4eec\u53ea\u77a7\u5f97\u4eba\u4eba\u5fc3\u91cc\u597d\u751f\u6709\u6c14\uff0c\u5927\u4f19\u513f\u4e00\u5408\u8ba1\uff0c\u5148\u53bb\u6253\u4ed6\u4e2a\u5934\u5d29\u989d\u88c2\u518d\u8bf4\u2026\u2026\u201d\u621a\u82b3\u9053\uff1a\u201c\u539f\u6765\u4f60\u4eec\u6253\u6211\u5e08\u54e5\uff0c\u8fd8\u662f\u4e3a\u4e86\u6211\u54ea\uff01\u201d \u3000\u3000\u5434\u574e\u7b11\u9053\uff1a\u201c\u5927\u5bb6\u5634\u91cc\u8bf4\u7684\uff0c\u81ea\u7136\u662f\u53e6\u5916\u4e00\u5957\u5566\uff0c\u8bf4\u4ed6\u5f3a\u884c\u51fa\u5934\uff0c\u53bb\u6597\u90a3\u5927\u76d7\u5415\u901a\uff0c\u524a\u4e86\u4e07\u95e8\u5f1f\u5b50\u7684\u9762\u5b50\u3002\u5176\u5b9e\u4eba\u4eba\u5fc3\u4e2d\uff0c\u53ef\u90fd\u662f\u4e3a\u4e86\u5e08\u5ac2\u4f60\u554a\uff01\u4f60\u8ddf\u4ed6\u8865\u8863\u670d\uff0c\u8bf4\u4f53\u5df1\u8bdd\u513f\uff0c\u8fd9\u95e8\u5b50\u4eb2\u70ed\u7684\u52b2\u513f\uff0c\u6211\u4eec\u5e08\u5144\u5f1f\u516b\u4eba\u77a7\u5728\u773c\u91cc\uff0c\u607c\u5728\u5fc3\u91cc\uff0c\u54ea\u4e00\u4e2a\u4e0d\u662f\u5927\u559d\u5e72\u918b\uff0c\u53ea\u559d\u5f97\u4e09\u5341\u516d\u53ea\u7259\u9f7f\u53ea\u53ea\u90fd\u9178\u574f\u4e86\uff1f\u201d \u3000\u3000\u621a\u82b3\u6697\u6697\u5fc3\u60ca\uff1a\u201c\u96be\u9053\u8fd9\u8fd8\u662f\u56e0\u6211\u8d77\u7978\uff1f\u4e09\u54e5\uff0c\u4e09\u54e5\uff0c\u4f60\u600e\u4e48\u4ece\u6765\u90fd\u4e0d\u8ddf\u6211\u8bf4\uff1f\u201d\u8138\u4e0a\u4ecd\u662f\u5047\u88c5\u6f2b\u4e0d\u5728\u4e4e\uff0c\u7b11\u9053\uff1a\u201c\u5434\u5e08\u5f1f\uff0c\u4f60\u8fd9\u53ef\u6765\u8bf4\u7b11\u4e86\u3002\u90a3\u65f6\u6211\u662f\u4e2a\u4e61\u4e0b\u59d1\u5a18\uff0c\u6751\u91cc\u6751\u6c14\u7684\uff0c\u6253\u626e\u5f97\u7b11\u6b7b\u4eba\u5566\uff0c\u53c8\u6709\u751a\u4e48\u597d\u770b\uff1f\u201d\u5434\u574e\u9053\uff1a\u201c\u4e0d\uff0c\u4e0d\uff01\u771f\u7f8e\u4eba\u513f\u7528\u5f97\u7740\u751a\u4e48\u6253\u626e\uff1f\u4f60\u82e5\u4e0d\u662f\u5f15\u5f97\u5927\u4f19\u513f\u5931\u9b42\u843d\u9b44\uff0c\u8fd9\u4e2a\u2026\u2026\u201d\u8bf4\u5230\u8fd9\u91cc\uff0c\u7a81\u7136\u4f4f\u5634\uff0c\u4e0d\u518d\u8bf4\u4e0b\u53bb\u4e86\u3002 \u3000\u3000\u621a\u82b3\u9053\uff1a\u201c\u751a\u4e48\uff1f\u201d\u5434\u574e\u9053\uff1a\u201c\u6211\u4eec\u628a\u4f60\u7559\u5728\u4e07\u5bb6\uff0c\u6211\u59d3\u5434\u7684\u4e5f\u51fa\u8fc7\u4e0d\u5c11\u529b\u6c14\u3002\u53ef\u662f\uff0c\u5e08\u5ac2\uff0c\u4f60\u5e73\u65f6\u89c1\u4e86\u6211\u7b11\u4e5f\u4e0d\u7b11\uff0c\u8fd9\u4e0d\u53eb\u4eba\u5fc3\u4e2d\u6124\u6124\u4e0d\u5e73\u4e48\uff1f\u201d\u621a\u82b3\u5478\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6211\u7559\u5728\u4e07\u5bb6\uff0c\u5ac1\u7ed9\u4f60\u4e07\u5e08\u54e5\uff0c\u662f\u6211\u81ea\u5df1\u5fc3\u7518\u60c5\u613f\u3002\u4f60\u53c8\u51fa\u8fc7\u751a\u4e48\u529b\u6c14\u4e86\uff1f\u90a3\u65f6\u5019\u4f60\u53c8\u6ca1\u6765\u529d\u6211\u4e00\u8a00\u534a\u8bed\uff0c\u771f\u662f\u80e1\u8bf4\u516b\u9053\uff01\u201d\u5434\u574e\u6447\u5934\u7b11\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\u600e\u4e48\u6ca1\u51fa\u529b\u6c14\uff1f\u4f60\u4e0d\u77e5\u9053\u7f62\u4e86\u3002\u201d \u3000\u3000\u621a\u82b3\u66f4\u662f\u5fc3\u60ca\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u5434\u5e08\u5f1f\uff0c\u4f60\u8ddf\u6211\u8bf4\uff0c\u4f60\u51fa\u4e86\u751a\u4e48\u529b\u6c14\uff0c\u5e08\u5ac2\u51b3\u5fd8\u4e0d\u4e86\u4f60\u7684\u597d\u5904\u3002\u201d\u5434\u574e\u6447\u5934\u9053\uff1a\u201c\u9648\u5e74\u65e7\u4e8b\uff0c\u8fd8\u63d0\u5b83\u4f5c\u751a\uff1f\u4f60\u77e5\u9053\u4e86\u4e5f\u6ca1\u7528\uff0c\u54b1\u4eec\u53ea\u8bf4\u65b0\u9c9c\u7684\u3002\u201d\u621a\u82b3\u9053\uff1a\u201c\u597d\u7f62\uff0c\u4f60\u4e0d\u80af\u8bf4\u5c31\u7b97\u4e86\u3002\u5feb\u7ed9\u6211\u89e3\u836f\uff0c\u8981\u662f\u6709\u4eba\u649e\u89c1\u54b1\u4e8c\u4eba\u5728\u8fd9\u91cc\uff0c\u53ef\u4e0d\u5927\u59a5\u5f53\u3002\u201d \u3000\u3000\u5434\u574e\u7b11\u9053\uff1a\u201c\u767d\u5929\u6709\u4eba\u649e\u89c1\uff0c\u665a\u4e0a\u8fd9\u91cc\u53ef\u6ca1\u4eba\u3002\u201d\u621a\u82b3\u9000\u540e\u4e00\u6b65\uff0c\u8138\u5982\u5bd2\u971c\uff0c\u5389\u58f0\u9053\uff1a\u201c\u4f60\u8bf4\u751a\u4e48\uff1f\u201d\u5434\u574e\u7b11\u9053\uff1a\u201c\u4f60\u8981\u6cbb\u597d\u4e07\u5e08\u54e5\u7684\u4f24\uff0c\u90a3\u4e5f\u4e0d\u96be\u3002\u4eca\u665a\u4e09\u66f4\uff0c\u6211\u5728\u90a3\u8fb9\u67f4\u623f\u91cc\u7b49\u4f60\uff0c\u4f60\u82e5\u662f\u4e00\u5207\u987a\u6211\u7684\u610f\uff0c\u6211\u4fbf\u7ed9\u4f60\u6577\u6cbb\u4e00\u6b21\u7684\u836f\u91cf\u3002\u201d \u3000\u3000\u621a\u82b3\u54ac\u7259\u9a82\u9053\uff1a\u201c\u72d7\u8d3c\uff0c\u4f60\u80c6\u6562\u8bf4\u8fd9\u79cd\u8bdd\uff0c\u597d\u5927\u7684\u80c6\u5b50\uff01\u201d \u3000\u3000\u5434\u574e\u6c89\u7740\u55d3\u5b50\u9053\uff1a\u201c\u6211\u65e9\u628a\u6027\u547d\u8c41\u51fa\u53bb\u4e86\uff0c\u8fd9\u53eb\u505a\u820d\u5f97\u4e00\u8eab\u5250\uff0c\u6562\u628a\u7687\u5e1d\u62c9\u4e0b\u9a6c\u3002\u4e07\u572d\u8fd9\u5c0f\u5b50\u751a\u4e48\u5730\u65b9\u5f3a\u8fc7\u6211\u59d3\u5434\u7684\u4e86\uff1f\u53ea\u4e0d\u8fc7\u4ed6\u662f\u6211\u5e08\u7236\u7684\u4eb2\u751f\u513f\u5b50\uff0c\u6295\u80ce\u6295\u5f97\u597d\u800c\u5df2\u3002\u5927\u5bb6\u51fa\u4e86\u529b\u6c14\uff0c\u4e3a\u751a\u4e48\u8ba9\u8fd9\u81ed\u5c0f\u5b50\u4e00\u4e2a\u513f\u72ec\u4eab\u8273\u798f\uff1f\u201d \u3000\u3000\u621a\u82b3\u542c\u4ed6\u8fde\u8bf4\u51e0\u6b21\u201c\u51fa\u4e86\u529b\u6c14\u201d\uff0c\u5fc3\u4e0b\u8d77\u7591\uff0c\u53ea\u662f\u4ed6\u6c61\u8a00\u79fd\u8bed\uff0c\u5b9e\u5728\u542c\u4e0d\u4e0b\u53bb\uff0c\u8bf4\u9053\uff1a\u201c\u5f85\u516c\u516c\u56de\u6765\uff0c\u6211\u7167\u5b9e\u7980\u544a\uff0c\u77a7\u4ed6\u4e0d\u5265\u4e86\u4f60\u7684\u76ae\u3002\u201d \u3000\u3000\u5434\u574e\u9053\uff1a\u201c\u6211\u5b88\u5728\u8fd9\u91cc\u4e0d\u8d70\u3002\u5e08\u7236\u4e00\u53eb\u6211\uff0c\u6211\u5148\u5c06\u89e3\u836f\u5012\u5728\u8377\u82b1\u6c60\u91cc\u5582\u4e86\u91d1\u9c7c\u3002\u6211\u95ee\u8fc7\u90a3\u4e2a\u90ce\u4e2d\uff0c\u4ed6\u8bf4\u89e3\u836f\u5c31\u53ea\u8fd9\u4e48\u4e00\u74f6\uff0c\u8981\u518d\u914d\u5236\uff0c\u4e00\u5e74\u534a\u8f7d\u4e5f\u914d\u4e0d\u8d77\u3002\u201d\u4ed6\u4e00\u9762\u8bf4\uff0c\u4e00\u9762\u4ece\u6000\u4e2d\u5c06\u89e3\u836f\u53d6\u4e86\u51fa\u6765\uff0c\u62d4\u5f00\u74f6\u585e\uff0c\u4f38\u624b\u6c60\u9762\uff0c\u53ea\u8981\u624b\u638c\u5fae\u5fae\u4e00\u4fa7\uff0c\u89e3\u836f\u4fbf\u5012\u5165\u6c60\u4e2d\uff0c\u4e07\u572d\u8fd9\u6761\u547d\u5c31\u7b97\u662f\u9001\u4e86\u3002 \u3000\u3000\u621a\u82b3\u6025\u9053\uff1a\u201c\u5582\uff0c\u5582\uff0c\u5feb\u6536\u8d77\u89e3\u836f\uff0c\u54b1\u4eec\u6162\u6162\u5546\u91cf\u4e0d\u8fdf\u3002\u201d \u3000\u3000\u5434\u574e\u7b11\u9053\uff1a\u201c\u6709\u751a\u4e48\u597d\u5546\u91cf\u7684\uff1f\u4f60\u8981\u6551\u4e08\u592b\u6027\u547d\uff0c\u5c31\u5f97\u542c\u6211\u7684\u8bdd\u3002\u201d\u621a\u82b3\u9053\uff1a\u201c\u5018\u82e5\u4f60\u4ece\u524d\u771f\u7684\u5bf9\u6211\u6709\u5fc3\uff0c\u51fa\u8fc7\u529b\u6c14\uff0c\u90a3\u4e48\u2026\u2026\u5426\u5219\u7684\u8bdd\uff0c\u6211\u624d\u4e0d\u6765\u7406\u4f60\u5462\u3002\u201d \u3000\u3000\u5434\u574e\u5927\u559c\uff0c\u76d6\u4e0a\u4e86\u74f6\u585e\uff0c\u8bf4\u9053\uff1a\u201c\u5e08\u5ac2\uff0c\u6211\u8981\u662f\u8bf4\u4e86\u5b9e\u8bdd\uff0c\u4f60\u4eca\u665a\u5c31\u6765\u548c\u6211\u76f8\u4f1a\uff0c\u662f\u4e0d\u662f\uff1f\u201d\u621a\u82b3\u9053\uff1a\u201c\u90a3\u4e5f\u5f97\u77a7\u4f60\u8bf4\u7684\u662f\u771f\u662f\u5047\u3002\u9a97\u4eba\u7684\u8bdd\uff0c\u53c8\u6709\u751a\u4e48\u7528\uff1f\u201d\u5434\u574e\u9053\uff1a\u201c\u5343\u771f\u4e07\u786e\uff0c\u600e\u4f1a\u6709\u534a\u70b9\u865a\u5047\uff1f\u90a3\u662f\u6c88\u5e08\u5f1f\u60f3\u7684\u8ba1\u7b56\u3002\u5468\u5e08\u54e5\u548c\u535c\u5e08\u54e5\u5047\u626e\u91c7\u82b1\u8d3c\uff0c\u5f15\u5f97\u72c4\u4e91\u8fd9\u50bb\u5c0f\u5b50\u5230\u6843\u7ea2\u623f\u4e2d\u6551\u4eba\u3002\u8fd9\u50bb\u5c0f\u5b50\u5e8a\u5e95\u4e0b\u7684\u91d1\u5668\u94f6\u5668\uff0c\u4fbf\u662f\u6211\u5434\u574e\u4eb2\u624b\u7ed9\u4ed6\u653e\u7684\u3002\u5e08\u5ac2\uff0c\u6211\u4eec\u82e5\u4e0d\u662f\u4f7f\u8fd9\u5de7\u8ba1\uff0c\u600e\u80fd\u7559\u5f97\u4f4f\u4f60\u5728\u4e07\u5e9c\uff1f\u201d \u3000\u3000\u621a\u82b3\u53ea\u89c9\u5934\u8111\u6655\u7729\uff0c\u773c\u524d\u53d1\u9ed1\uff0c\u5434\u574e\u7684\u8bdd\u72b9\u5982\u4e00\u628a\u628a\u5229\u5200\u624e\u5165\u5979\u7684\u5fc3\u4e2d\uff0c\u4e0d\u7981\u4f4e\u547c\uff1a\u201c\u6211\u2026\u2026\u9519\u602a\u4e86\u4f60\uff0c\u51a4\u6789\u4e86\u4f60\uff01\u201d \u3000\u3000\u5979\u8eab\u5b50\u6447\u6447\u6643\u6643\uff0c\u4fbf\u6b32\u6454\u5012\uff0c\u4f38\u624b\u6276\u4f4f\u4e86\u680f\u5e72\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u4fe1\uff0c\u54ea\u6709\u8fd9\u56de\u4e8b\uff1f\u4f60\u7f16\u51fa\u6765\u9a97\u6211\u7684\u3002\u201d\u58f0\u97f3\u751a\u662f\u82e6\u6da9\u3002 \u3000\u3000\u5434\u574e\u6025\u9053\uff1a\u201c\u4f60\u4e0d\u4fe1\uff1f\u597d\uff0c\u522b\u7684\u4eba\u4e0d\u80fd\u95ee\uff0c\u4f60\u53bb\u95ee\u6843\u7ea2\u597d\u4e86\uff0c\u5979\u5728\u540e\u9762\u90a3\u7834\u7960\u5802\u91cc\u4f4f\u3002\u95ee\u8fc7\u4e4b\u540e\uff0c\u53ef\u5343\u4e07\u4e0d\u80fd\u8ddf\u65c1\u4eba\u8bf4\u3002\u6211\u4eec\u5e08\u5144\u5f1f\u5927\u5bb6\u8d4c\u8fc7\u5492\uff0c\u8fd9\u79d8\u5bc6\u662f\u8bf4\u751a\u4e48\u4e5f\u4e0d\u80fd\u6cc4\u6f0f\u7684\u3002 \u3000\u3000\u82e5\u4e0d\u662f\u4e3a\u4e86\u4eca\u665a\u4e09\u66f4\uff0c\u5e08\u5ac2\uff0c\u4e3a\u4e86\u4f60\uff0c\u6211\u5434\u574e\u751a\u4e48\u90fd\u7529\u51fa\u53bb\u5566\uff01\u201d \u3000\u3000\u621a\u82b3\u5927\u53eb\u4e00\u58f0\uff0c\u51b2\u4e86\u51fa\u53bb\uff0c\u63a8\u5f00\u82b1\u56ed\u540e\u95e8\uff0c\u5411\u5916\u6025\u5954\u3002 \u3000\u3000\u5979\u5fc3\u4e71\u5982\u9ebb\uff0c\u4e00\u5954\u51fa\u540e\u95e8\uff0c\u7a7f\u8fc7\u51e0\u5ea7\u83dc\u56ed\uff0c\u5b9a\u4e86\u5b9a\u795e\uff0c\u627e\u5230\u4e86\u897f\u5317\u89d2\u90a3\u5ea7\u5c0f\u5c0f\u7684\u7834\u843d\u7960\u5802\uff0c\u89c1\u865a\u63a9\u7740\u95e8\uff0c\u4fbf\u4f38\u624b\u63a8\u5f00\u4e86\u95e8\uff0c\u8d70\u4e86\u8fdb\u53bb\u3002 \u3000\u3000\u53ea\u89c1\u5730\u4e0b\u6ee1\u662f\u7070\u5c18\uff0c\u684c\u6905\u90fd\u662f\u751a\u4e3a\u6b8b\u7834\uff0c\u5fc3\u60f3\uff1a\u201c\u516c\u516c\u7684\u59be\u4f8d\u6843\u7ea2\uff0c\u600e\u4e48\u4f1a\u4f4f\u5728\u8fd9\u79cd\u5730\u65b9\uff1f\u5434\u574e\u8fd9\u8d3c\u5b50\u9a97\u4eba\uff0c\u83ab\u975e\u2026\u2026\u83ab\u975e\u4ed6\u9a97\u6211\u5230\u8fd9\u91cc\u6765\uff0c\u4e0d\u6000\u597d\u610f\uff1f\u6211\u8fd8\u662f\u5feb\u56de\u53bb\u3002\u201d \u3000\u3000\u7a81\u7136\u4e4b\u95f4\uff0c\u53ea\u542c\u5f97\u8e22\u8e0f\u3001\u8e22\u8e0f\uff0c\u7f13\u6162\u7684\u811a\u6b65\u58f0\u54cd\uff0c\u5185\u5802\u8d70\u51fa\u4e00\u4e2a\u5973\u4eba\u6765\u3002\u90a3\u662f\u4e2a\u4e2d\u5e74\u4e10\u5987\uff0c\u4f4e\u5934\u5f13\u80cc\uff0c\u62ab\u5934\u6563\u53d1\uff0c\u8863\u670d\u79fd\u6c61\u7834\u70c2\u3002 \u3000\u3000\u90a3\u4e10\u5987\u89c1\u5230\u6709\u4eba\uff0c\u5403\u4e86\u4e00\u60ca\uff0c\u7acb\u5373\u8f6c\u8eab\u56de\u53bb\u3002\u5979\u5c06\u8d70\u8fdb\u5185\u5802\uff0c\u53c8\u8f6c\u8fc7\u8138\u6765\u77a7\u4e86\u4e00\u773c\uff0c\u8fd9\u4e00\u6b21\u770b\u6e05\u695a\u4e86\u621a\u82b3\u7684\u76f8\u8c8c\uff0c\u4e0d\u7531\u5f97\u201c\u554a\u201d\u7684\u4e00\u58f0\u60ca\u547c\u3002\u5979\u5012\u9000\u4e86\u4e24\u6b65\uff0c\u7a81\u7136\u8dea\u5012\uff0c\u8bf4\u9053\uff1a\u201c\u5c11\u5976\u5976\uff0c\u4f60\u2026\u2026\u4f60\u522b\u8bf4\u2026\u2026\u522b\u8bf4\u6211\u5728\u8fd9\u91cc\u3002\u201d\u621a\u82b3\u5927\u5947\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u5728\u8fd9\u91cc\u5e72\u751a\u4e48\uff1f\u201d\u90a3\u4e10\u5987\u9053\uff1a\u201c\u4e0d\u2026\u2026\u4e0d\u5e72\u751a\u4e48\uff1f\u6211\u2026\u2026\u6211\u2026\u2026\u201d\u8bf4\u7740\u7acb\u523b\u7ad9\u8d77\uff0c\u5feb\u6b65\u8fdb\u4e86\u5185\u5802\u3002 \u3000\u3000\u53ea\u542c\u5f97\u811a\u6b65\u58f0\u6025\uff0c\u90a3\u4e10\u5987\u4ece\u540e\u95e8\u5306\u5306\u9003\u4e86\u51fa\u53bb\u3002\u621a\u82b3\u5fc3\u60f3\uff1a\u201c\u8fd9\u5973\u5b50\u4e0d\u77e5\u4e3a\u4e86\u751a\u4e48\u4e8b\uff0c\u89c1\u4e86\u6211\u8fd9\u7b49\u5bb3\u6015\u2026\u2026\u554a\u54df\uff0c\u60f3\u8d77\u6765\u4e86\uff0c\u5979\u2026\u2026\u5979\u4fbf\u662f\u6843\u7ea2\uff01\u201d\u4e00\u60f3\u5230\u662f\u5979\uff0c\u621a\u82b3\u4e09\u811a\u4e24\u6b65\uff0c\u4ece\u7960\u5802\u5927\u95e8\u7eb5\u51fa\uff0c\u8e0f\u7740\u74e6\u783e\uff0c\u62a2\u5230\u540e\u95e8\uff0c\u4f38\u624b\u4ece\u8170\u95f4\u62d4\u51fa\u4e86\u5315\u9996\uff0c\u559d\u9053\uff1a\u201c\u6843\u7ea2\uff0c\u4f60\u9b3c\u9b3c\u795f\u795f\u7684\uff0c\u5728\u8fd9\u91cc\u5e72\u751a\u4e48\uff1f\u201d \u3000\u3000\u90a3\u4e10\u5987\u6b63\u662f\u6843\u7ea2\uff0c\u542c\u5f97\u621a\u82b3\u53eb\u51fa\u81ea\u5df1\u540d\u5b57\uff0c\u5df2\u81ea\u614c\u4e86\uff0c\u5f85\u89c1\u5230\u5979\u624b\u4e2d\u6301\u7740\u4e00\u628a\u660e\u6643\u6643\u7684\u5315\u9996\uff0c\u66f4\u662f\u5bb3\u6015\uff0c\u53cc\u819d\u53d1\u6296\uff0c\u53c8\u8981\u8dea\u4e0b\uff0c\u98a4\u58f0\u9053\uff1a\u201c\u5c11\u5976\u5976\uff0c\u4f60\u2026\u2026\u4f60\u9976\u4e86\u6211\u3002\u201d \u3000\u3000\u621a\u82b3\u5728\u4e07\u5bb6\u53ea\u548c\u6843\u7ea2\u89c1\u4e86\u51e0\u6b21\uff0c\u6ca1\u591a\u4e45\u5c31\u4ece\u6b64\u4e0d\u89c1\u5979\u9762\uff0c\u6bcf\u4e00\u60f3\u5230\u72c4\u4e91\u8981\u548c\u8fd9\u5973\u4eba\u5377\u9003\u79c1\u5954\uff0c\u4fbf\u662f\u5fc3\u5982\u5200\u5272\uff0c\u662f\u4ee5\u8fd9\u5973\u4eba\u5230\u4e86\u4f55\u5904\uff0c\u5979\u4ece\u6765\u4e0d\u95ee\u3002\u5c31\u7b97\u6709\u4eba\u63d0\u8d77\uff0c\u5979\u4e5f\u51b3\u8ba1\u4e0d\u542c\uff0c\u90a3\u52bf\u5fc5\u78b0\u75db\u5979\u5185\u5fc3\u6700\u5927\u7684\u521b\u4f24\u3002\u54ea\u77e5\u5979\u7adf\u4f1a\u8eb2\u5728\u8fd9\u91cc\u3002\u8fd9\u7960\u5802\u79bb\u4e07\u5bb6\u4e0d\u8fdc\uff0c\u4f46\u621a\u82b3\u505a\u4e86\u5c11\u5976\u5976\u4e4b\u540e\uff0c\u4e8b\u4e8b\u8c28\u614e\uff0c\u6bd4\u4e4b\u5728\u6e58\u897f\u8001\u5bb6\u505a\u95fa\u5973\u65f6\u5927\u4e0d\u76f8\u540c\uff0c\u4ece\u4e0d\u5728\u5916\u9762\u4e71\u8d70\uff0c\u867d\u66fe\u591a\u6b21\u89c1\u5230\u8fd9\u7834\u7960\u5802\u7684\u95e8\u53e3\uff0c\u5374\u4ece\u6765\u6ca1\u8fdb\u53bb\u8fc7\u3002 \u3000\u3000\u6843\u7ea2\u6b64\u523b\u84ec\u5934\u57a2\u9762\uff0c\u5bb9\u8272\u6194\u60b4\uff0c\u51e0\u5e74\u4e0d\u89c1\uff0c\u5012\u4f3c\u662f\u8001\u4e86\u4e8c\u5341\u5c81\u4e00\u822c\u3002\u5434\u574e\u53eb\u621a\u82b3\u5230\u8fd9\u7960\u5802\u4e2d\u6765\u627e\u6843\u7ea2\u8be2\u95ee\u771f\u76f8\uff0c\u5979\u867d\u5f53\u9762\u89c1\u5230\u4e86\uff0c\u4f46\u5982\u6843\u7ea2\u82e5\u65e0\u5176\u4e8b\u7684\u6162\u6162\u8d70\u5f00\uff0c\u5979\u4fbf\u51b3\u8ba1\u8ba4\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u5979\u626c\u4e86\u626c\u624b\u4e2d\u5315\u9996\uff0c\u5a01\u5413\u9053\uff1a\u201c\u4f60\u8eb2\u5728\u8fd9\u91cc\u5e72\u4e48\uff1f\u5feb\u8ddf\u6211\u8bf4\u3002\u201d \u3000\u3000\u6843\u7ea2\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\u4e0d\u5e72\u751a\u4e48\u3002\u5c11\u5976\u5976\uff0c\u8001\u7237\u8d76\u4e86\u6211\u51fa\u6765\uff0c\u4ed6\u8bf4\u8981\u662f\u89c1\u5230\u6211\u803d\u5728\u8346\u5dde\uff0c\u4fbf\u8981\u6740\u4e86\u6211\u3002\u53ef\u662f\u2026\u2026\u53ef\u662f\u2026\u2026 \u3000\u3000\u6211\u53c8\u6ca1\u5730\u65b9\u597d\u53bb\uff0c\u53ea\u597d\u8eb2\u5728\u8fd9\u91cc\u8ba8\u53e3\u5403\u7684\u3002\u5c11\u5976\u5976\uff0c\u9664\u4e86\u8346\u5dde\u57ce\uff0c\u6211\u751a\u4e48\u5730\u65b9\u90fd\u4e0d\u8ba4\u5f97\uff0c\u53eb\u6211\u5230\u54ea\u91cc\u53bb\uff1f\u4f60\u2026\u2026\u4f60\u884c\u884c\u597d\uff0c\u5343\u4e07\u522b\u8ddf\u8001\u7237\u8bf4\u3002\u201d \u3000\u3000\u621a\u82b3\u542c\u5979\u8bf4\u5f97\u53ef\u601c\uff0c\u6536\u8d77\u4e86\u5315\u9996\uff0c\u9053\uff1a\u201c\u8001\u7237\u4e3a\u751a\u4e48\u8d76\u4e86\u4f60\u51fa\u6765\uff1f\u600e\u4e48\u6211\u4e0d\u77e5\u9053\uff1f\u201d \u3000\u3000\u6843\u7ea2\u5782\u6cea\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u77e5\u9053\u8001\u7237\u4e3a\u751a\u4e48\u4e0d\u559c\u6b22\u6211\u4e86\u3002\u90a3\u4e2a\u6e56\u5357\u4f6c\u2026\u2026\u90a3\u4e2a\u59d3\u72c4\u7684\u4e8b\uff0c\u53c8\u4e0d\u662f\u6211\u4e0d\u597d\u3002\u554a\u54df\uff0c\u6211\u2026\u2026\u6211\u4e0d\u8be5\u8bf4\u8fd9\u79cd\u8bdd\u3002\u201d \u3000\u3000\u621a\u82b3\u9053\uff1a\u201c\u597d\u7f62\uff0c\u4f60\u4e0d\u8bf4\uff0c\u4f60\u5c31\u8ddf\u6211\u53bb\u89c1\u8001\u7237\u53bb\u3002\u201d\u4f38\u51fa\u5de6\u624b\uff0c\u4e00\u628a\u6293\u4f4f\u4e86\u5979\u8863\u895f\u3002\u621a\u82b3\u672c\u6027\u7231\u6d01\uff0c\u6843\u7ea2\u8863\u895f\u4e0a\u6ee1\u662f\u6c61\u79fd\u6cb9\u817b\uff0c\u4e00\u628a\u6293\u4f4f\uff0c\u624b\u638c\u5fc3\u6ed1\u6e9c\u6e9c\u7684\u6781\u4e0d\u597d\u53d7\u3002\u4f46\u5979\u6025\u4e8e\u8981\u67e5\u77e5\u72c4\u4e91\u88ab\u51a4\u7684\u771f\u76f8\uff0c\u4fbf\u662f\u518d\u80ae\u810f\u5341\u500d\u7684\u4e1c\u897f\uff0c\u8fd9\u5f53\u513f\u4e5f\u662f\u6beb\u4e0d\u5728\u4e4e\u4e86\u3002 \u3000\u3000\u6843\u7ea2\u7c0c\u7c0c\u53d1\u6296\uff0c\u5fd9\u9053\uff1a\u201c\u6211\u8bf4\uff0c\u6211\u8bf4\uff0c\u5c11\u5976\u5976\uff0c\u4f60\u8981\u6211\u8bf4\u751a\u4e48\uff1f\u201d \u3000\u3000\u621a\u82b3\u9053\uff1a\u201c\u72c4\u2026\u2026\u72c4\u2026\u2026\u90a3\u59d3\u72c4\u7684\u4e8b\uff0c\u5230\u5e95\u662f\u600e\u4e48\uff1f\u4f60\u4e3a\u751a\u4e48\u8981\u548c\u4ed6\u79c1\u9003\uff1f\u201d \u3000\u3000\u6843\u7ea2\u5fc3\u4e0b\u60ca\u60f6\uff0c\u7741\u5927\u4e86\u773c\uff0c\u4e00\u65f6\u8bf4\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u621a\u82b3\u51dd\u89c6\u7740\u5979\uff0c\u5fc3\u4e2d\u6240\u611f\u5230\u7684\u5bb3\u6015\uff0c\u6216\u8bb8\u6bd4\u4e4b\u6843\u7ea2\u66f4\u751a\u5341\u500d\u3002\u5979\u771f\u4e0d\u6562\u542c\u6843\u7ea2\u4eb2\u53e3\u8bf4\u51fa\u6765\u7684\u4e8b\u3002\u5982\u679c\u5979\u8bf4\uff1a\u72c4\u4e91\u5f53\u65f6\u786e\u662f\u7ea6\u5979\u79c1\u9003\uff0c\u786e\u662f\u6765\u6c61\u8fb1\u5979\u3002\u90a3\u600e\u4e48\u662f\u597d\uff1f\u6843\u7ea2\u4e00\u65f6\u8bf4\u4e0d\u51fa\u8bdd\uff0c\u621a\u82b3\u8138\u8272\u60e8\u767d\uff0c\u4e00\u9897\u5fc3\u4f3c\u4e4e\u505c\u6b62\u4e86\u8df3\u52a8\u3002 \u3000\u3000\u7ec8\u4e8e\uff0c\u6843\u7ea2\u8bf4\u4e86\uff1a\u201c\u8fd9\u2026\u2026\u8fd9\u602a\u4e0d\u5f97\u6211\uff0c\u5c11\u7237\u903c\u7740\u6211\u505a\u7684\uff0c\u53eb\u6211\u7262\u7262\u62b1\u4f4f\u90a3\u59d3\u72c4\u7684\u6e56\u5357\u4e61\u4e0b\u4f6c\uff0c\u51a4\u6789\u4ed6\u6765\u5f3a\u5978\u6211\uff0c\u8981\u5e26\u4e86\u6211\u9003\u8d70\u3002\u6211\u8ddf\u8001\u7237\u8bf4\u8fc7\u7684\uff0c\u8001\u7237\u53c8\u4e0d\u662f\u4e0d\u4fe1\uff0c\u53ea\u5429\u5490\u6211\u5343\u4e07\u522b\u8bf4\u51fa\u53bb\uff0c\u8fd8\u7ed9\u4e86\u6211\u8863\u670d\u94f6\u5b50\u3002\u53ef\u662f\u2026\u2026\u53ef\u662f\u2026\u2026\u6211\u53c8\u6ca1\u8bf4\uff0c\u8001\u7237\u5374\u8d76\u4e86\u6211\u51fa\u6765\u3002\u201d \u3000\u3000\u621a\u82b3\u53c8\u662f\u611f\u6fc0\uff0c\u53c8\u662f\u4f24\u5fc3\uff0c\u53c8\u662f\u59d4\u5c48\uff0c\u53c8\u662f\u601c\u60dc\uff0c\u5fc3\u4e2d\u53ea\u662f\u8bf4\uff1a\u201c\u5e08\u54e5\uff0c\u662f\u6211\u51a4\u6789\u4e86\u4f60\uff0c\u6211\u539f\u8be5\u77e5\u9053\u4f60\u5bf9\u6211\u4e00\u7247\u771f\u5fc3\uff0c\u8fd9\u53ef\u771f\u82e6\u4e86\u4f60\uff0c\u53ef\u771f\u82e6\u4e86\u4f60\uff01\u201d\u8fd9\u65f6\u5979\u5e76\u4e0d\u618e\u6068\u6843\u7ea2\uff0c\u53cd\u800c\u6709\u4e9b\u611f\u8c22\u5979\uff0c\u5e78\u4e8f\u662f\u5979\u66ff\u81ea\u5df1\u89e3\u5f00\u4e86\u5fc3\u4e2d\u7684\u6b7b\u7ed3\u3002\u751a\u81f3\u5bf9\u4e8e\u5434\u574e\uff0c\u90fd\u6709\u4e9b\u611f\u6fc0\uff0c\u662f\u4ed6\u5410\u9732\u4e86\u771f\u76f8\uff0c\u662f\u4ed6\u6307\u70b9\u81ea\u5df1\u5230\u8fd9\u7834\u7960\u5802\u6765\u627e\u6843\u7ea2\u7684\u3002 \u3000\u3000\u5728\u4f24\u5fc3\u548c\u51c4\u51c9\u4e4b\u4e2d\uff0c\u5ffd\u7136\u611f\u5230\u4e86\u4e00\u9635\u82e6\u6da9\u7684\u751c\u871c\u3002\u867d\u7136\u5ac1\u4e86\u4e07\u572d\uff0c\u4f46\u5979\u5185\u5fc3\u4e2d\u6df1\u6df1\u7231\u7740\u7684\uff0c\u59cb\u7ec8\u53ea\u662f\u4e2a\u72c4\u5e08\u54e5\uff0c\u5c3d\u7ba1\u4ed6\u4e34\u5371\u53d8\u5fc3\uff0c\u5c3d\u7ba1\u4ed6\u65e0\u803b\u5351\u9119\uff0c\u5c3d\u7ba1\u4ed6\u6709\u5343\u822c\u7684\u4e0d\u662f\u3001\u4e07\u822c\u7684\u8584\u5e78\uff0c\u4f46\u53ea\u6709\u4ed6\uff0c\u4ecd\u65e7\u662f\u4ed6\uff0c\u624d\u662f\u621a\u82b3\u53f9\u606f\u548c\u6d41\u6cea\u4e4b\u65f6\u6240\u60f3\u5ff5\u7684\u4eba\u3002 \u3000\u3000\u7a81\u7136\u4e4b\u95f4\uff0c\u79cd\u79cd\u82e6\u607c\u548c\u618e\u6068\uff0c\u90fd\u53d8\u6210\u4e86\u81ea\u6094\u81ea\u4f24\uff1a\u201c\u8981\u662f\u6211\u65e9\u77e5\u9053\u4e86\uff0c\u4fbf\u662f\u62da\u7740\u5343\u5200\u4e07\u5250\uff0c\u4e5f\u8981\u5230\u72f1\u4e2d\u6551\u4ed6\u51fa\u6765\u3002\u4ed6\u5403\u4e86\u8fd9\u4e48\u591a\u82e6\uff0c\u4ed6\u2026\u2026\u4ed6\u5fc3\u4e2d\u600e\u6837\u60f3\uff1f\u201d \u3000\u3000\u6843\u7ea2\u5077\u770b\u621a\u82b3\u7684\u8138\u8272\uff0c\u98a4\u58f0\u9053\uff1a\u201c\u5c11\u5976\u5976\uff0c\u8c22\u8c22\u4f60\uff0c\u8bf7\u4f60\u653e\u4e86\u6211\u8d70\uff0c\u6211\u5c31\u51fa\u4e86\u8346\u5dde\u57ce\uff0c\u6c38\u4e0d\u56de\u6765\u4e86\u3002\u201d \u3000\u3000\u621a\u82b3\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8001\u7237\u4e3a\u751a\u4e48\u8d76\u4f60\u8d70\uff1f\u662f\u6015\u6211\u77e5\u9053\u8fd9\u4ef6\u4e8b\u4e48\uff1f\u5509\uff0c\u4eca\u65e5\u603b\u7b97\u95ee\u660e\u767d\u4e86\u3002\u201d\u8bf4\u7740\u677e\u624b\u653e\u5f00\u5979\u8863\u895f\uff0c\u60f3\u8981\u7ed9\u5979\u4e9b\u94f6\u5b50\uff0c\u4f46\u5306\u5306\u51fa\u6765\uff0c\u8eab\u8fb9\u5e76\u65e0\u94f6\u4e24\u3002 \u3000\u3000\u6843\u7ea2\u89c1\u621a\u82b3\u653e\u5f00\u4e86\u81ea\u5df1\uff0c\u751f\u6015\u66f4\u6709\u53d8\u5366\uff0c\u6025\u6025\u5fd9\u5fd9\u7684\u4fbf\u8d70\u4e86\uff0c\u5583\u5583\u7684\u9053\uff1a\u201c\u8001\u7237\u665a\u4e0a\u89c1\u9b3c\uff0c\u8981\u780c\u5899\uff0c\u600e\u4e48\u602a\u5f97\u6211\uff1f\u53c8\u2026\u2026\u53c8\u4e0d\u662f\u6211\u778e\u8bf4\u3002\u201d\u621a\u82b3\u8ffd\u4e86\u4e0a\u53bb\uff0c\u95ee\u9053\uff1a\u201c\u751a\u4e48\u89c1\u9b3c\uff1f\u780c\u5899\uff1f\u201d\u6843\u7ea2\u77e5\u9053\u8bf4\u6e9c\u4e86\u5634\uff0c\u5fd9\u9053\uff1a\u201c\u6ca1\u751a\u4e48\uff0c\u6ca1\u751a\u4e48\u3002\u558f\uff0c\u8001\u7237\u591c\u91cc\u5e38\u5e38\u89c1\u9b3c\uff0c\u534a\u591c\u4e09\u66f4\u7684\u8d77\u6765\u780c\u5899\u3002\u201d \u3000\u3000\u621a\u82b3\u89c1\u5979\u8bf4\u8bdd\u75af\u75af\u98a0\u98a0\uff0c\u5fc3\u60f3\u5979\u7ed9\u516c\u516c\u8d76\u51fa\u5bb6\u95e8\uff0c\u65e5\u5b50\u8fc7\u5f97\u5f88\u82e6\uff0c\u8111\u7b4b\u4e5f\u4e0d\u5927\u6e05\u695a\u4e86\u3002\u516c\u516c\u600e\u4f1a\u534a\u591c\u4e09\u66f4\u8d77\u6765\u780c\u5899\uff1f \u3000\u3000\u5bb6\u91cc\u4ece\u6765\u6ca1\u89c1\u8fc7\u516c\u516c\u780c\u7684\u5899\u3002 \u3000\u3000\u6843\u7ea2\u751f\u6015\u5979\u4e0d\u4fe1\uff0c\u8bf4\u9053\uff1a\u201c\u662f\u5047\u7684\u780c\u5899\uff0c\u8001\u7237\u2026\u2026\u8001\u7237\uff0c\u534a\u591c\u4e09\u66f4\u7684\uff0c\u7231\u505a\u6ce5\u6c34\u5320\u3002\u6211\u8bf4\u4e86\u4ed6\u51e0\u53e5\uff0c\u8001\u7237\u5c31\u5927\u53d1\u813e\u6c14\uff0c\u6253\u5f97\u6211\u6b7b\u53bb\u6d3b\u6765\u7684\uff0c\u53c8\u8d76\u4e86\u6211\u51fa\u6765\uff0c\u8bf4\u9053\u518d\u89c1\u5230\u6211\uff0c\u4fbf\u6253\u6b7b\u6211\u2026\u2026\u201d\u5979\u5520\u5520\u53e8\u53e8\u7684\u8bf4\u4e2a\u4e0d\u505c\uff0c\u5f13\u7740\u80cc\u8d70\u4e86\u3002 \u3000\u3000\u621a\u82b3\u77a7\u7740\u5979\u7684\u540e\u5f71\uff0c\u5fc3\u60f3\uff1a\u201c\u5979\u6700\u591a\u4e0d\u8fc7\u5927\u4e86\u6211\u5341\u5c81\uff0c\u5374\u53d8\u5f97\u8fd9\u526f\u6837\u5b50\u3002\u516c\u516c\u4e0d\u77e5\u4e3a\u4e86\u751a\u4e48\u8981\u8d76\u5979\u51fa\u95e8\uff1f\u751a\u4e48\u89c1\u9b3c\u780c\u5899\uff0c\u60f3\u662f\u8fd9\u5973\u4eba\u65e9\u5c31\u98a0\u98a0\u8822\u8822\u7684\uff0c\u5509\uff0c\u4e3a\u4e86\u8fd9\u6837\u4e00\u4e2a\u50bb\u5973\u4eba\uff0c\u5e08\u54e5\u82e6\u4e86\u4e00\u8f88\u5b50\uff01\u201d \u3000\u3000\u60f3\u5230\u8fd9\u91cc\uff0c\u4e0d\u7981\u6014\u6014\u7684\u6d41\u4e0b\u6cea\u6765\uff0c\u5230\u540e\u6765\uff0c\u7d22\u6027\u5927\u58f0\u54ed\u4e86\u51fa\u6765\u3002 \u3000\u3000\u5979\u9760\u5728\u4e00\u68f5\u68a7\u6850\u6811\u4e0a\u54ed\u4e86\u4e00\u573a\uff0c\u5fc3\u5934\u8f7b\u677e\u4e86\u4e9b\uff0c\u6162\u6162\u8d70\u5411\u5bb6\u6765\u3002\u5979\u907f\u5f00\u540e\u56ed\uff0c\u4ece\u4e1c\u9762\u7684\u8fb9\u95e8\u8fdb\u53bb\uff0c\u56de\u5230\u697c\u4e0a\u3002 \u3000\u3000\u4e07\u572d\u4e00\u542c\u5230\u5979\u4e0a\u697c\u7684\u811a\u6b65\u58f0\uff0c\u4fbf\u6025\u7740\u95ee\uff1a\u201c\u82b3\u59b9\uff0c\u89e3\u836f\u627e\u5230\u4e86\u6ca1\u6709\uff1f\u201d\u621a\u82b3\u8d70\u8fdb\u623f\u53bb\uff0c\u53ea\u89c1\u4e07\u572d\u5750\u8d77\u8eab\u5b50\uff0c\u795e\u8272\u751a\u662f\u7126\u6025\uff0c\u4e00\u53ea\u4f24\u624b\u6401\u5728\u5e8a\u8fb9\uff0c\u624b\u80cc\u4e0a\u9ed1\u8840\u6162\u6162\u6e17\u51fa\u6765\uff0c\u8fc7\u4e86\u597d\u4e00\u4f1a\uff0c\u624d\u201c\u5854\u201d\u7684\u4e00\u58f0\uff0c\u6ef4\u5728\u5e8a\u8fb9\u7684\u90a3\u53ea\u94dc\u9762\u76c6\u91cc\u3002\u5c0f\u5973\u5b69\u4f0f\u5728\u7239\u7239\u811a\u8fb9\uff0c\u65e9\u7761\u719f\u4e86\u3002 \u3000\u3000\u621a\u82b3\u542c\u4e86\u5434\u574e\u548c\u6843\u7ea2\u7684\u8bdd\uff0c\u672c\u6765\u5bf9\u4e07\u572d\u607c\u6012\u5df2\u6781\uff0c\u6df1\u6068\u4ed6\u7528\u5351\u9119\u624b\u6bb5\u9677\u5bb3\u72c4\u4e91\u3002\u8fd9\u65f6\u770b\u5230\u4ed6\u6194\u60b4\u800c\u6e05\u79c0\u7684\u8138\u5e9e\uff0c\u51e0\u5e74\u6765\u7684\u6069\u7231\u53c8\u4f7f\u5979\u5fc3\u80a0\u8f6f\u4e86\uff1a\u201c\u7a76\u7adf\uff0c\u4e09\u54e5\u662f\u4e3a\u4e86\u7231\u6211\uff0c\u8fd9\u624d\u9677\u5bb3\u5e08\u54e5\uff0c\u4ed6\u4f7f\u7684\u624b\u6bb5\u56fa\u7136\u9634\u9669\u6bd2\u8fa3\uff0c\u53eb\u5e08\u54e5\u5403\u8db3\u4e86\u82e6\uff0c\u4f46\u7a76\u7adf\u662f\u4e3a\u4e86\u7231\u6211\u3002\u201d \u3000\u3000\u4e07\u572d\u53c8\u95ee\uff1a\u201c\u89e3\u836f\u4e70\u5230\u4e86\u6ca1\u6709\uff1f\u201d\u621a\u82b3\u4e00\u65f6\u96be\u4ee5\u51b3\u5b9a\u662f\u5426\u8981\u5c06\u5434\u574e\u7684\u65e0\u803b\u8a00\u8bed\u544a\u77e5\u4e08\u592b\uff0c\u987a\u53e3\u9053\uff1a\u201c\u627e\u5230\u4e86\u90a3\u90ce\u4e2d\uff0c\u7ed9\u4e86\u4ed6\u94f6\u5b50\uff0c\u8bf7\u4ed6\u5373\u523b\u4e70\u836f\u6750\u914d\u5236\u3002\u201d\u4e07\u572d\u5401\u4e86\u53e3\u6c14\uff0c\u5fc3\u4e2d\u767b\u65f6\u677e\u4e86\uff0c\u5fae\u7b11\u9053\uff1a\u201c\u82b3\u59b9\uff0c\u6211\u8fd9\u6761\u547d\u554a\uff0c\u5230\u5e95\u662f\u4f60\u6551\u7684\u3002\u201d \u3000\u3000\u621a\u82b3\u52c9\u5f3a\u7b11\u4e86\u7b11\uff0c\u53ea\u89c9\u8138\u76c6\u4e2d\u7684\u6bd2\u8840\u6c14\u5473\u6781\u662f\u523a\u9f3b\uff0c\u4e8e\u662f\u7aef\u8fc7\u4e00\u53ea\u9752\u74f7\u75f0\u76c2\u6765\u63a5\u8840\uff0c\u5c06\u94dc\u76c6\u7aef\u4e86\u51fa\u53bb\u3002\u53ea\u8d70\u51fa\u4e24\u6b65\uff0c\u6bd2\u8840\u7684\u6c14\u606f\u76f4\u51b2\u4e0a\u6765\uff0c\u5934\u8111\u4e2d\u4e00\u9635\u660f\u7729\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u874e\u6bd2\u8fd9\u4e48\u5389\u5bb3\uff01\u201d\u5feb\u6b65\u8d70\u5230\u5916\u623f\uff0c\u5c06\u8138\u76c6\u653e\u5728\u684c\u8fb9\u5730\u4e0b\uff0c\u8f6c\u8fc7\u8eab\u6765\uff0c\u4f38\u624b\u5165\u6000\u53bb\u53d6\u624b\u5e15\uff0c\u8981\u63a9\u4f4f\u4e86\u9f3b\u5b50\uff0c\u518d\u53bb\u5012\u8840\u3002 \u3000\u3000\u5979\u624b\u4e00\u5165\u6000\uff0c\u4fbf\u78b0\u5230\u4e86\u90a3\u672c\u5510\u8bd7\uff0c\u4e00\u6014\u4e4b\u4e0b\uff0c\u4e00\u9897\u5fc3\u53c8\u6026\u6026\u8df3\u4e86\u8d77\u6765\uff0c\u6478\u51fa\u8fd9\u672c\u65e7\u4e66\uff0c\u5750\u5728\u684c\u8fb9\uff0c\u4e00\u9875\u9875\u7684\u7ffb\u8fc7\u53bb\u3002 \u3000\u3000\u5979\u8bb0\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u90a3\u65e5\u7ffb\u6361\u65e7\u8863\uff0c\u4ece\u7bb1\u5b50\u5e95\u4e0b\u7684\u65e7\u8863\u670d\u4e2d\u89c1\u5230\u4e86\u8fd9\u672c\u4e66\uff0c\u7239\u7239\u897f\u74dc\u5927\u7684\u5b57\u8bc6\u4e0d\u4e0a\u51e0\u62c5\uff0c\u4e0d\u77e5\u4ece\u54ea\u91cc\u62fe\u4e86\u8fd9\u672c\u4e66\u6765\uff0c\u5979\u521a\u597d\u526a\u4e86\u4e24\u4e2a\u7ee3\u82b1\u6837\u513f\uff0c\u987a\u624b\u4fbf\u631f\u5728\u4e66\u4e2d\u3002\u90a3\u5929\u4e0b\u5348\u548c\u72c4\u5e08\u54e5\u4e00\u9f50\u53bb\u5c71\u6d1e\uff0c\u4fbf\u5c06\u8fd9\u672c\u4e66\u5e26\u4e86\u53bb\uff0c\u4ee5\u540e\u5c31\u4e00\u76f4\u7559\u5728\u90a3\u8fb9\u3002\u600e\u4e48\u4f1a\u5230\u4e86\u8fd9\u91cc\uff1f\u662f\u72c4\u5e08\u54e5\u53eb\u8fd9\u4f4d\u90ce\u4e2d\u9001\u6765\u7684\u4e48\uff1f \u3000\u3000\u201c\u8fd9\u90ce\u4e2d\u2026\u2026\u83ab\u975e\u2026\u2026\u4ed6\u2026\u2026\u4ed6\u53f3\u624b\u7684\u4e94\u6839\u624b\u6307\u90fd\u7ed9\u5434\u574e\u524a\u53bb\u4e86\u3002\u8fd9\u90ce\u4e2d\u2026\u2026\u8fd9\u90ce\u4e2d\u2026\u2026\u4e3a\u751a\u4e48\uff1f\u4e3a\u751a\u4e48\u4ed6\u2026\u2026\u4ed6\u7684\u53f3\u624b\u59cb\u7ec8\u4e0d\u4f38\u51fa\u6765\uff1f\u201d\u7a81\u7136\u4e4b\u95f4\uff0c\u5979\u60f3\u8d77\u4e86\u8fd9\u4ef6\u4e8b\u3002\u5979\u51dd\u795e\u56de\u60f3\u90a3\u90ce\u4e2d\u6276\u8d77\u5973\u513f\uff0c\u56de\u60f3\u4ed6\u5f00\u836f\u7bb1\u3001\u53d6\u836f\u74f6\u3001\u62d4\u74f6\u585e\uff0c\u5012\u836f\u672b\u7684\u60c5\u666f\uff0c\u56de\u60f3\u4ed6\u63a5\u4e86\u81ea\u5df1\u9001\u8fc7\u53bb\u7684\u9152\u676f\uff0c\u5c06\u9152\u676f\u9001\u5230\u5507\u8fb9\u559d\u5e72\uff0c\u8fd9\u8bb8\u591a\u4e8b\u60c5\uff0c\u4f3c\u4e4e\u90fd\u662f\u7528\u4e00\u53ea\u5de6\u624b\u6765\u505a\u7684\uff0c\u53ea\u4e0d\u8fc7\u5f53\u65f6\u6ca1\u7559\u5fc3\uff0c\u5b9e\u5728\u8bb0\u4e0d\u771f\u5207\u3002 \u3000\u3000\u201c\u96be\u9053\uff0c\u4ed6\u5c31\u662f\u5e08\u54e5\uff1f\u600e\u4e48\u76f8\u8c8c\u4e00\u70b9\u4e5f\u4e0d\u50cf\uff1f\u201d\u5979\u5fc3\u70e6\u610f\u4e71\uff0c\u5fcd\u4e0d\u4f4f\u60b2\u4ece\u4e2d\u6765\uff0c\u773c\u6cea\u4e00\u6ef4\u6ef4\u7684\u90fd\u6d41\u5728\u624b\u4e2d\u90a3\u672c\u4e66\u4e0a\u3002 \u3000\u3000\u6cea\u6c34\u6ef4\u5230\u4e66\u9875\u4e4b\u4e0a\uff0c\u6ef4\u5728\u90a3\u4e24\u53ea\u7528\u82b1\u7eb8\u526a\u7684\u8774\u8776\u4e0a\uff0c\u8fd9\u662f\u201c\u6881\u5c71\u4f2f\u548c\u795d\u82f1\u53f0\u201d\uff0c\u4ed6\u4eec\u8981\u6b7b\u4e86\u4e4b\u540e\uff0c\u624d\u5f97\u56e2\u5706\u2026\u2026 \u3000\u3000\u4e07\u572d\u5728\u9694\u623f\u8bf4\u9053\uff1a\u201c\u82b3\u59b9\uff0c\u6211\u95f7\u5f97\u614c\uff0c\u8981\u8d77\u6765\u8d70\u8d70\u3002\u201d\u4f46\u621a\u82b3\u6c89\u6d78\u5728\u56de\u5fc6\u4e4b\u4e2d\uff0c\u6ca1\u6709\u542c\u89c1\u3002\u5979\u5728\u60f3\uff1a\u201c\u90a3\u5929\u4ed6\u6253\u6b7b\u4e86\u4e00\u53ea\u8774\u8776\uff0c\u5c06\u4e00\u5bf9\u60c5\u90ce\u60c5\u59b9\u62c6\u6563\u4e86\u3002\u662f\u4e0d\u662f\u8001\u5929\u7237\u56e0\u6b64\u7f5a\u4ed6\u53d7\u82e6\u53d7\u96be\u2026\u2026\u201d \u3000\u3000\u7a81\u7136\u4e4b\u95f4\uff0c\u80cc\u540e\u4e00\u4e2a\u58f0\u97f3\u60ca\u53eb\u8d77\u6765\uff1a\u201c\u8fd9\u2026\u2026\u8fd9\u662f\u2026\u2026\u2018\u8fde\u2026\u2026\u8fde\u57ce\u5251\u8c31\uff01\u2019\u201d \u3000\u3000\u621a\u82b3\u5403\u4e86\u4e00\u60ca\uff0c\u4e00\u56de\u5934\uff0c\u53ea\u89c1\u4e07\u572d\u6ee1\u8138\u559c\u60a6\u4e4b\u8272\uff0c\u5174\u594b\u5f02\u5e38\u7684\u9053\uff1a\u201c\u82b3\u59b9\uff0c\u82b3\u59b9\uff0c\u4f60\u4ece\u54ea\u91cc\u5f97\u6765\u4e86\u8fd9\u672c\u4e66\uff1f\u4f60\u77a7\uff0c\u554a\uff0c\u539f\u6765\u662f\u8fd9\u6837\uff0c\u5bf9\u4e86\uff0c\u662f\u8fd9\u6837\uff01\u201d\u4ed6\u53cc\u624b\u6309\u4f4f\u4e86\u90a3\u672c\u300a\u5510\u8bd7\u9009\u8f91\u300b\uff0c\u53ea\u89c1\u5728\u4e00\u9996\u9898\u76ee\u5199\u7740\u201c\u5723\u679c\u5bfa\u201d\u7684\u8bd7\u65c1\uff0c\u73b0\u51fa\u201c\u4e09\u5341\u4e09\u201d\u4e09\u4e2a\u6de1\u9ec4\u8272\u7684\u5b57\u6765\uff0c\u8fd9\u51e0\u884c\u5b57\u4e0a\uff0c\u6e85\u7740\u621a\u82b3\u7684\u6cea\u6c34\u3002 \u3000\u3000\u4e07\u572d\u5927\u559c\u4e4b\u4e0b\uff0c\u5fd8\u4e86\u514b\u5236\uff0c\u53eb\u9053\uff1a\u201c\u79d8\u5bc6\u5728\u8fd9\u91cc\u4e86\uff0c\u539f\u6765\u8981\u6253\u6e7f\u4e86\uff0c\u624d\u6709\u5b57\u8ff9\u51fa\u73b0\uff01\u5999\u6781\uff0c\u5999\u6781\uff01\u4e00\u5b9a\u662f\u8fd9\u672c\u4e66\u3002\u7a7a\u5fc3\u83dc\uff0c\u7a7a\u5fc3\u83dc\u3002\u201d\u4ed6\u5927\u58f0\u53eb\u56b7\uff0c\u5c06\u5973\u513f\u53eb\u9192\uff0c\u8bf4\u9053\uff1a\u201c\u7a7a\u5fc3\u83dc\u5feb\u53bb\u8bf7\u7237\u7237\u6765\uff0c\u8bf4\u6709\u8981\u7d27\u4e8b\u60c5\u3002\u201d\u5c0f\u5973\u5b69\u7b54\u5e94\u7740\u53bb\u4e86\u3002 \u3000\u3000\u4e07\u572d\u7d27\u7d27\u6309\u7740\u90a3\u672c\u8bd7\u96c6\uff0c\u5fd8\u4e86\u624b\u4e0a\u7684\u75db\u695a\uff0c\u53ea\u662f\u8bf4\uff1a\u201c\u4e00\u5b9a\u662f\u7684\uff0c\u4e0d\u9519\uff0c\u7239\u7239\u8bf4\u90a3\u5251\u8c31\u5145\u4f5c\u662f\u300a\u5510\u8bd7\u9009\u8f91\u300b\uff0c\u90a3\u8fd8\u4e0d\u662f\uff1f \u3000\u3000\u4ed6\u4eec\u5c31\u662f\u63e3\u6478\u4e0d\u51fa\u8fd9\u4e2d\u95f4\u7684\u79d8\u5bc6\u3002\u539f\u6765\u8981\u5f04\u6e7f\u4e66\u9875\uff0c\u79d8\u5bc6\u624d\u663e\u4e86\u51fa\u6765\u3002\u201d \u3000\u3000\u4ed6\u8fd9\u4e48\u53c8\u559c\u53c8\u8df3\u7684\u53eb\u56b7\uff0c\u621a\u82b3\u5df2\u7136\u660e\u767d\u4e86\u5927\u534a\u3002\u5fc3\u60f3\uff1a \u3000\u3000\u201c\u8fd9\u5c31\u662f\u7239\u7239\u548c\u516c\u516c\u6240\u4e89\u7684\u751a\u4e48\u300a\u8fde\u57ce\u5251\u8c31\u300b\uff0c\u8fd9\u4e48\u8bf4\u6765\uff0c\u539f\u6765\u662f\u7239\u7239\u5f97\u4e86\u53bb\uff0c\u6211\u4e0d\u77e5\u597d\u6b79\uff0c\u62ff\u6765\u5939\u4e86\u978b\u6837\u3002\u7239\u7239\u4e0d\u89c1\u4e86\u8fd9\u672c\u4e66\uff0c\u600e\u4e48\u4e0d\u627e\uff1f\u55ef\uff0c\u60f3\u6765\u4e00\u5b9a\u662f\u627e\u8fc7\u7684\uff0c\u627e\u6765\u627e\u53bb\u627e\u4e0d\u5230\uff0c\u4ee5\u4e3a\u662f\u5e08\u4f2f\u76d7\u53bb\u4e86\u3002\u4ed6\u4e3a\u751a\u4e48\u4e0d\u95ee\u6211\uff0c\u8fd9\u771f\u5947\u4e86\uff01\u201d \u3000\u3000\u5982\u679c\u662f\u72c4\u4e91\uff0c\u8fd9\u65f6\u5019\u5c31\u4e00\u70b9\u4e5f\u4e0d\u4f1a\u5947\u602a\u3002\u4ed6\u77e5\u9053\u53ea\u56e0\u621a\u957f\u53d1\u662f\u4e2a\u6781\u5de5\u5fc3\u8ba1\u4e4b\u4eba\uff0c\u5373\u4f7f\u5728\u5973\u513f\u9762\u524d\uff0c\u4e5f\u4e0d\u80af\u900f\u9732\u534a\u70b9\u53e3\u98ce\u3002\u4e0d\u89c1\u4e86\u4e66\uff0c\u62da\u547d\u7684\u627e\uff0c\u627e\u4e0d\u5230\uff0c\u4fbf\u88c5\u4f5c\u6ca1\u4e8b\u4eba\u4e00\u822c\uff1b\u6697\u6697\u5bdf\u770b\uff0c\u7528\u5404\u79cd\u5404\u6837\u7684\u6837\u5b50\u6765\u4fa6\u67e5\u8bd5\u63a2\uff0c\u770b\u662f\u4e0d\u662f\u72c4\u4e91\u8fd9\u5c0f\u5b50\u5077\u4e86\u53bb\uff1f\u662f\u4e0d\u662f\u5973\u513f\u5077\u4e86\u53bb\uff1f\u53ea\u56e0\u4e3a\u621a\u82b3\u4e0d\u662f\u201c\u5077\u201d\uff0c\u4e0d\u4f1a\u505a\u8d3c\u5fc3\u865a\uff0c\u621a\u957f\u53d1\u81ea\u7136\u67e5\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u4e07\u9707\u5c71\u4ece\u8857\u4e0a\u56de\u6765\uff0c\u6b63\u5728\u82b1\u5385\u5403\u70b9\u5fc3\uff0c\u542c\u5f97\u5b59\u5973\u53eb\u5524\uff0c\u8fd8\u9053\u513f\u5b50\u6bd2\u4f24\u6709\u53d8\uff0c\u4e00\u7897\u8c46\u4e1d\u6ca1\u5403\u5b8c\uff0c\u5fd9\u653e\u4e0b\u7b77\u5b50\uff0c\u62b1\u8d77\u5b59\u5973\uff0c\u5927\u6b65\u6765\u5230\u513f\u5b50\u697c\u4e0a\uff0c\u4e00\u4e0a\u697c\u68af\u4fbf\u542c\u89c1\u4e07\u572d\u559c\u60a6\u7684\u58f0\u97f3\uff1a\u201c\u5929\u4e0b\u4e8b\u60c5\u771f\u6709\u8fd9\u822c\u5de7\u6cd5\u3002\u82b3\u59b9\uff0c\u600e\u4e48\u4f60\u4f1a\u5728\u4e66\u9875\u4e0a\u6e85\u4e86\u4e9b\u6c34\uff1f\u5929\u610f\uff0c\u5929\u610f\uff01\u201d \u3000\u3000\u4e07\u9707\u5c71\u542c\u5230\u513f\u5b50\u8bf4\u8bdd\u7684\u97f3\u8c03\uff0c\u4fbf\u653e\u4e86\u4e00\u5927\u534a\u5fc3\uff0c\u4e3e\u6b65\u8e0f\u8fdb\u623f\u4e2d\u3002 \u3000\u3000\u4e07\u572d\u62ff\u7740\u90a3\u672c\u300a\u5510\u8bd7\u9009\u8f91\u300b\uff0c\u559c\u9053\uff1a\u201c\u7239\uff0c\u7239\uff0c\u4f60\u77a7\uff0c\u8fd9\u662f\u751a\u4e48\uff1f\u201d \u3000\u3000\u4e07\u9707\u5c71\u4e00\u89c1\u5230\u90a3\u672c\u8584\u8584\u7684\u9ec4\u7eb8\u4e66\uff0c\u5fc3\u4e2d\u4e00\u9707\uff0c\u5fd9\u5c06\u5b59\u5973\u513f\u653e\u5728\u5730\u4e0b\uff0c\u63a5\u8fc7\u513f\u5b50\u9012\u6765\u7684\u90a3\u672c\u4e66\uff0c\u4e00\u9897\u5fc3\u6026\u6026\u4e71\u8df3\u3002\u82b1\u5c3d\u5fc3\u8840\u627e\u5bfb\u4e86\u5341\u51e0\u5e74\u7684\u300a\u8fde\u57ce\u5251\u8c31\u300b\uff0c\u7ec8\u4e8e\u53c8\u51fa\u73b0\u5728\u773c\u524d\u3002 \u3000\u3000\u4e0d\u9519\uff0c\u6b63\u662f\u8fd9\u672c\u4e66\uff01\u4ed6\u548c\u8a00\u8fbe\u5e73\u3001\u621a\u957f\u53d1\u4e09\u4eba\u8054\u624b\u5408\u529b\u3001\u8c0b\u5bb3\u5e08\u7236\u800c\u62a2\u5230\u7684\uff0c\u6b63\u662f\u8fd9\u672c\u4e66\u3002\u4e09\u4e2a\u4eba\u5728\u5ba2\u6808\u4e4b\u4e2d\uff0c\u7ffb\u6765\u8986\u53bb\u7684\u540c\u770b\u8fd9\u672c\u5251\u8c31\u3002\u53ef\u662f\u8fd9\u53ea\u662f\u4e00\u672c\u5e73\u5e73\u65e0\u5947\u7684\u5510\u8bd7\uff0c\u548c\u4e66\u574a\u4e2d\u51fa\u552e\u7684\u300a\u5510\u8bd7\u9009\u8f91\u300b\u5b8c\u5168\u4e00\u6a21\u4e00\u6837\u3002\u4ed6\u5e08\u7236\u6559\u8fc7\u4ed6\u4eec\u4e00\u5957\u201c\u5510\u8bd7\u5251\u6cd5\u201d\uff0c\u4ee5\u5510\u8bd7\u7684\u8bd7\u53e5\u4f5c\u5251\u62db\u540d\u5b57\uff0c\u8fd9\u4e9b\u8bd7\u53e5\u5728\u8fd9\u672c\u4e66\u4e2d\u5168\u6709\u3002\u53ef\u662f\u8ddf\u4f20\u8bf4\u4e2d\u7684\u300a\u8fde\u57ce\u5251\u8c31\u300b\u53c8\u6709\u751a\u4e48\u76f8\u5e72\uff1f \u3000\u3000\u5e08\u5144\u5f1f\u4e09\u4eba\u66fe\u62ff\u8fd9\u672c\u4e66\u5230\u592a\u9633\u5149\u4e0b\u4e00\u9875\u9875\u7684\u53bb\u7167\uff0c\u60f3\u53d1\u89c1\u4e66\u4e2d\u6709\u751a\u4e48\u5939\u5c42\uff1b\u4e5f\u66fe\u62ff\u4e66\u4e2d\u8fd9\u51e0\u5341\u9996\u8bd7\u987a\u8bfb\u3001\u5012\u8bfb\u3001\u6a2a\u8bfb\u3001\u659c\u8bfb\uff0c\u8df3\u4e00\u5b57\u8bfb\u3001\u8df3\u4e8c\u5b57\u8bfb\u2026\u2026\u60f3\u8981\u627e\u51fa\u5176\u4e2d\u6240\u542b\u7684\u5927\u79d8\u5bc6\u6765\u2026\u2026\u7136\u800c\u4e00\u5207\u5fc3\u8840\u5168\u662f\u767d\u8d39\u4e86\u3002\u4e09\u4eba\u4e92\u76f8\u731c\u7591\uff0c\u90fd\u6015\u7ed9\u4eba\u5bb6\u53d1\u89c1\u4e86\u79d8\u5bc6\u800c\u81ea\u5df1\u4e0d\u77e5\u3002\u4e09\u4eba\u665a\u4e0a\u7761\u89c9\u4e4b\u65f6\uff0c\u5c06\u4e66\u672c\u9501\u5165\u94c1\u76d2\uff0c\u94c1\u76d2\u53c8\u7528\u4e09\u6839\u5c0f\u94c1\u94fe\u5206\u522b\u7cfb\u5728\u4e09\u4eba\u7684\u8155\u4e0a\u3002\u4f46\u4e00\u5929\u65e9\u6668\uff0c\u8fd9\u672c\u4e66\u7ec8\u4e8e\u4e0d\u7ffc\u800c\u98de\uff0c\u4ece\u6b64\u5f71\u8ff9\u5168\u65e0\u3002 \u3000\u3000\u4e8e\u662f\u5341\u51e0\u5e74\u6765\u65e0\u7a77\u7684\u52fe\u5fc3\u6597\u89d2\uff0c\u65e0\u5c3d\u7684\u63a2\u8bbf\u5bfb\u627e\u3002\u7a81\u7136\u4e4b\u95f4\uff0c\u8fd9\u672c\u4e66\u53c8\u51fa\u73b0\u5728\u773c\u524d\u3002 \u3000\u3000\u4e07\u9707\u5c71\u7ffb\u5230\u7b2c\u56db\u9875\u4e0a\uff0c\u4e0d\u9519\uff0c\u4e66\u9875\u7684\u5de6\u4e0a\u89d2\u88ab\u6495\u53bb\u4e86\u5c0f\u5c0f\u7684\u4e00\u89d2\uff0c\u90a3\u662f\u4ed6\u5f53\u5e74\u5077\u5077\u505a\u4e0b\u7684\u8bb0\u53f7\uff0c\u751f\u6015\u8a00\u5e08\u5f1f\u6216\u662f\u621a\u5e08\u5f1f\u7528\u4e00\u672c\u540c\u6837\u7684\u300a\u5510\u8bd7\u9009\u8f91\u300b\u6765\u6389\u5305\uff0c\u800c\u81ea\u5df1\u5374\u88ab\u8499\u5728\u9f13\u91cc\u3002 \u3000\u3000\u4e07\u9707\u5c71\u53c8\u7ffb\u5230\u4e86\u7b2c\u5341\u516d\u9875\uff0c\u4e0d\u9519\uff0c\u5f53\u5e74\u81ea\u5df1\u5212\u7740\u7684\u90a3\u4e2a\u6307\u7532\u75d5\u4ecd\u662f\u5728\u90a3\u91cc\u3002\u8fd9\u662f\u771f\u672c\uff01\u4ed6\u70b9\u4e86\u70b9\u5934\uff0c\u5f3a\u81ea\u6291\u5236\u5185\u5fc3\u559c\u60a6\uff0c\u5bf9\u513f\u5b50\u9053\uff1a\u201c\u6b63\u662f\u8fd9\u672c\u4e66\u3002\u4f60\u4ece\u54ea\u91cc\u5f97\u6765\u7684\uff1f\u201d \u3000\u3000\u621a\u82b3\u81ea\u4ece\u4e00\u89c1\u5230\u4e07\u572d\u7684\u795e\u60c5\uff0c\u5fc3\u4e2d\u6240\u60f3\u7684\u53ea\u662f\u81ea\u5df1\u7239\u7239\uff1a \u3000\u3000\u201c\u7239\u7239\u4e0d\u77e5\u5230\u4e86\u54ea\u91cc\uff1f\u6211\u8fd9\u4e0d\u5b5d\u7684\u5973\u513f\uff0c\u5c06\u4ed6\u8fd9\u672c\u4e66\u62ff\u5230\u4e86\u5c71\u6d1e\u4e4b\u4e2d\uff0c\u4ed6\u8001\u4eba\u5bb6\u8fd9\u53ef\u627e\u5f97\u82e6\u4e86\u3002\u5728\u7239\u7239\u5fc3\u4e2d\uff0c\u8fd9\u672c\u4e66\u4e00\u5b9a\u662f\u975e\u5e38\u975e\u5e38\u7684\u5b9d\u8d35\u3002\u4e0d\u77e5\u8fd9\u672c\u65e7\u4e66\u6709\u751a\u4e48\u7528\uff1f\u7136\u800c\u8fd9\u662f\u6211\u62ff\u4e86\u7239\u7239\u7684\uff0c\u662f\u7239\u7239\u7684\u4e66\uff0c\u51b3\u4e0d\u80fd\u7ed9\u516c\u516c\u5f3a\u62a2\u4e86\u53bb\u3002\u201d \u3000\u3000\u5982\u679c\u662f\u5728\u4e00\u5929\u4e4b\u524d\uff0c\u8fd8\u4e0d\u77e5\u9053\u72c4\u4e91\u60e8\u53d7\u9677\u5bb3\u7684\u5185\u60c5\uff0c\u5bf9\u4e08\u592b\u8fd8\u662f\u6ee1\u8154\u67d4\u60c5\u548c\u4f53\u8d34\uff0c\u90a3\u4e48\u5728\u5979\u5fc3\u91cc\uff0c\u4e08\u592b\u7684\u5206\u91cf\u672a\u5fc5\u4fbf\u53ca\u4e0d\u4e0a\u7236\u4eb2\uff0c\u4f55\u51b5\uff0c\u7236\u4eb2\u4e0d\u77e5\u5230\u54ea\u91cc\u53bb\u4e86\uff0c\u4e0d\u77e5\u9053\u4f1a\u4e0d\u4f1a\u518d\u56de\u6765\u3002\u7136\u800c\u73b0\u4eca\u53ef\u4e0d\u540c\u4e86\u3002\u201c\u51b3\u4e0d\u80fd\u8ba9\u7239\u7239\u8fd9\u672c\u4e66\u843d\u5165\u4ed6\u4eec\u624b\u91cc\u3002\u72c4\u5e08\u54e5\u53bb\u53d6\u4e86\u4e66\u6765\u4ea4\u5230\u6211\u624b\u91cc\uff0c\u8981\u6211\u66ff\u7239\u7239\u4fdd\u7ba1\uff0c\u5f53\u7136\u4e0d\u80fd\u7ed9\u4ed6\u4eec\u62a2\u4e86\u53bb\u3002\u4e0d\u4f46\u662f\u4e3a\u4e86\u7239\u7239\uff0c\u4e5f\u4e3a\u4e86\u72c4\u5e08\u54e5\uff01\u201d \u3000\u3000\u5f53\u4e07\u572d\u95ee\u5979\u201c\u8fd9\u672c\u4e66\u54ea\u91cc\u6765\u7684\u201d\u4e4b\u65f6\uff0c\u5979\u5fc3\u4e2d\u53ea\u662f\u5728\u60f3\uff1a \u3000\u3000\u201c\u600e\u6837\u5c06\u4e66\u593a\u56de\u6765\uff1f\u201d\u4e66\u662f\u5728\u516c\u516c\u624b\u91cc\u3002\u4e07\u9707\u5c71\u6b66\u529f\u5353\u7edd\uff0c\u4f55\u51b5\u4e08\u592b\u4fbf\u5728\u65c1\u8fb9\uff0c\u786c\u593a\u662f\u4e0d\u6210\u7684\u3002\u5979\u5fc3\u4e2d\u98de\u5feb\u7684\u5728\u8f6c\u5ff5\u5934\uff0c\u773c\u73e0\u9aa8\u6e9c\u6e9c\u7684\u8f6c\u52a8\u3002 \u3000\u3000\u5979\u770b\u5230\u4e86\u4e66\u684c\u65c1\u90a3\u53ea\u94dc\u76c6\uff0c\u76c6\u4e2d\u76db\u7740\u534a\u76c6\u8840\u6c34\u3002\u90a3\u662f\u4e07\u572d\u6d17\u8fc7\u8138\u7684\u6c34\uff0c\u6ef4\u4e86\u4e0d\u5c11\u4ed6\u624b\u80cc\u4e0a\u4f24\u53e3\u4e2d\u6d41\u51fa\u6765\u7684\u6bd2\u8840\u3002\u8fd9\u76c6\u6c34\u5168\u6210\u4e86\u7d2b\u9ed1\u8272\u2026\u2026\u5982\u679c\u6084\u6084\u5c06\u4e66\u4e22\u8fdb\u8840\u6c34\u4e4b\u4e2d\uff0c\u4ed6\u4eec\u5c31\u627e\u4e0d\u5230\u4e86\u3002\u53ef\u662f\uff0c\u90a3\u672c\u4e66\u53ea\u6015\u8981\u6d78\u574f\u3002\u4e0d\u8fc7\u82e5\u4e0d\u4e58\u8fd9\u65f6\u5019\u4e0b\u624b\uff0c\u4ee5\u540e\u591a\u534a\u518d\u4e5f\u6ca1\u6709\u673a\u4f1a\u4e86\uff0c\u5b81\u53ef\u5c06\u4e66\u6bc1\u4e86\uff0c\u4e5f\u4e0d\u80fd\u8ba9\u4ed6\u4eec\u79f0\u5fc3\u5982\u610f\u2026\u2026 \u3000\u3000\u4e07\u6c0f\u7236\u5b50\u51dd\u89c6\u7740\u621a\u82b3\u3002\u4e07\u572d\u53c8\u95ee\uff1a\u201c\u82b3\u59b9\uff0c\u8fd9\u672c\u4e66\u54ea\u91cc\u6765\u7684\uff1f\u201d \u3000\u3000\u621a\u82b3\u4e00\u51db\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u77e5\u9053\u554a\uff0c\u521a\u624d\u6211\u4ece\u623f\u91cc\u51fa\u6765\uff0c\u4fbf\u89c1\u8fd9\u672c\u4e66\u653e\u5728\u684c\u4e0a\u3002\u8fd9\u4e0d\u662f\u4f60\u7684\u4e48\uff1f\u201d \u3000\u3000\u4e07\u572d\u4e00\u65f6\u60f3\u4e0d\u660e\u767d\uff0c\u6682\u65f6\u4e0d\u518d\u8ffd\u7a76\uff0c\u4e00\u5fc3\u8981\u5c06\u91cd\u5927\u7684\u53d1\u89c1\u8bf4\u7ed9\u7236\u4eb2\u77e5\u9053\uff1a\u201c\u7239\uff0c\u4f60\u77a7\uff0c\u8fd9\u4e66\u9875\u5b50\u4e00\u6cbe\u6e7f\uff0c\u4fbf\u6709\u5b57\u8ff9\u51fa\u6765\u3002\u201d\u4ed6\u4f38\u51fa\u98df\u6307\uff0c\u6307\u7740\u300a\u5723\u679c\u5bfa\u300b\u90a3\u9996\u8bd7\u65c1\u6de1\u9ec4\u8272\u7684\u4e09\u4e2a\u5b57\uff1a \u3000\u3000\u201c\u4e09\u5341\u4e09\u201d\u3002 \u3000\u3000\uff08\u5982\u679c\u4ed6\u77e5\u9053\u8fd9\u662f\u59bb\u5b50\u7684\u6cea\u6c34\uff0c\u662f\u60f3\u5ff5\u72c4\u4e91\u800c\u6d41\u7684\u773c\u6cea\uff0c\u4ed6\u5fc3\u4e2d\u4f1a\u600e\u6837\u60f3\uff1f\uff09 \u3000\u3000\u4e07\u9707\u5c71\u4f38\u6307\u70b9\u7740\u90a3\u9996\u8bd7\uff0c\u4e00\u4e2a\u5b57\u4e00\u4e2a\u5b57\u6570\u4e0b\u53bb\uff1a\u201c\u8def\u81ea\u4e2d\u5cf0\u4e0a\uff0c\u76d8\u56de\u51fa\u58c1\u7ba9\u3002\u5230\u6c5f\u5434\u5730\u5c3d\uff0c\u9694\u5cb8\u8d8a\u5c71\u591a\u3002\u53e4\u6728\u4e1b\u9752\u972d\uff0c\u9065\u5929\u6d78\u767d\u6ce2\u3002\u4e0b\u65b9\u57ce\u2026\u2026\u201d\u7b2c\u4e09\u5341\u4e09\u5b57\uff0c\u90a3\u662f\u4e2a\u201c\u57ce\u201d\u5b57\uff01 \u3000\u3000\u4e07\u9707\u5c71\u4e00\u62cd\u5927\u817f\uff0c\u8bf4\u9053\uff1a\u201c\u5bf9\u5566\uff0c\u6b63\u662f\u8fd9\u4e2a\u6cd5\u5b50\uff01\u539f\u6765\u79d8\u5bc6\u5728\u6b64\u3002\u572d\u513f\uff0c\u4f60\u771f\u806a\u660e\uff0c\u4e8f\u4f60\u60f3\u5230\u4e86\u8fd9\u4e2a\u9053\u7406\uff01\u8981\u7528\u6c34\uff0c\u4e0d\u9519\uff0c\u6211\u4eec\u5f53\u5e74\u5c31\u662f\u6ca1\u60f3\u5230\u8981\u7528\u6c34\uff01\u201d \u3000\u3000\uff08\u5982\u679c\u4ed6\u77e5\u9053\u8fd9\u662f\u5ab3\u5987\u7684\u6cea\u6c34\uff0c\u662f\u601d\u5ff5\u53e6\u4e00\u4e2a\u7537\u4eba\u800c\u6d41\u7684\u773c\u6cea\uff0c\u4ed6\u5fc3\u4e2d\u4f1a\u600e\u6837\u60f3\uff1f\uff09 \u3000\u3000\u621a\u82b3\u89c1\u4ed6\u7236\u5b50\u5927\u559c\u82e5\u72c2\uff0c\u805a\u5934\u63a2\u7d22\u4e66\u4e2d\u7684\u79d8\u5965\uff0c\u4fbf\u62c9\u7740\u5973\u513f\u7684\u624b\u8d70\u5230\u5185\u623f\uff0c\u5c06\u5979\u6402\u5728\u6000\u91cc\uff0c\u8f7b\u58f0\u9053\uff1a\u201c\u7a7a\u5fc3\u83dc\uff0c\u90a3\u53ea\u9762\u76c6\uff0c\u4f60\u77a7\u89c1\u4e48\uff1f\u201d\u5c0f\u5973\u5b69\u70b9\u4e86\u70b9\u5934\uff0c\u9053\uff1a\u201c\u77a7\u89c1\u7684\u3002\u201d\u621a\u82b3\u9053\uff1a\u201c\u7b49\u5f85\u7237\u7237\u3001\u7239\u7239\u3001\u548c\u5988\u5988\u4e00\u8d77\u5954\u51fa\u53bb\uff0c\u5988\u5988\u5c06\u7237\u7237\u624b\u91cc\u7684\u90a3\u672c\u4e66\u653e\u5728\u62bd\u5c49\u91cc\uff0c\u4f60\u53bb\u62ff\u4e86\u51fa\u6765\uff0c\u6084\u6084\u4e22\u5728\u9762\u76c6\u91cc\uff0c\u8ba9\u810f\u6c34\u6d78\u7740\uff0c\u522b\u7ed9\u7237\u7237\u548c\u7239\u7239\u770b\u89c1\uff0c\u53eb\u4ed6\u4eec\u627e\u4e0d\u5230\u3002\u201d \u3000\u3000\u5c0f\u5973\u5b69\u5927\u559c\uff0c\u53ea\u9053\u5988\u5988\u8981\u73a9\u4e2a\u6781\u6709\u8da3\u7684\u6e38\u620f\uff0c\u62cd\u638c\u7b11\u9053\uff1a\u201c\u597d\uff0c\u597d\uff01\u201d\u621a\u82b3\u9053\uff1a\u201c\u53ef\u522b\u8ba9\u7237\u7237\u548c\u7239\u7239\u77e5\u9053\uff0c\u4e5f\u522b\u8ddf\u4ed6\u4eec\u8bf4\uff01\u201d \u3000\u3000\u5c0f\u5973\u5b69\u9053\uff1a\u201c\u7a7a\u5fc3\u83dc\u4e0d\u8bf4\uff0c\u7a7a\u5fc3\u83dc\u4e0d\u8bf4\uff01\u201d \u3000\u3000\u621a\u82b3\u8d70\u5230\u95e8\u5916\uff0c\u8bf4\u9053\uff1a\u201c\u516c\u516c\uff0c\u6211\u89c9\u5f97\u8fd9\u672c\u4e66\u5f88\u6709\u70b9\u53e4\u602a\u3002\u201d \u3000\u3000\u4e07\u9707\u5c71\u8f6c\u8fc7\u8eab\u6765\uff0c\u95ee\u9053\uff1a\u201c\u751a\u4e48\u53e4\u602a\uff01\u201d\u4ed6\u5185\u5fc3\u65e9\u5df2\u9690\u9690\u89c9\u5f97\u8fd9\u672c\u4e66\u7a81\u7136\u51fa\u73b0\uff0c\u6765\u5f97\u592a\u8fc7\u5bb9\u6613\uff0c\u6050\u6015\u4e0d\u662f\u5409\u5146\uff0c\u5ab3\u5987\u8fd9\u4e48\u4e00\u8bf4\uff0c\u66f4\u589e\u4ed6\u7684\u7591\u8651\u3002\u621a\u82b3\u9053\uff1a\u201c\u5728\u8fd9\u91cc\uff01\u201d\u8bf4\u9053\u4f38\u51fa\u624b\u53bb\u3002\u4e07\u9707\u5c71\u5c06\u4e66\u4ea4\u7ed9\u4e86\u5979\u3002 \u3000\u3000\u621a\u82b3\u7ffb\u5f00\u4e66\u9875\uff0c\u53d6\u4e86\u90a3\u4e24\u53ea\u7eb8\u526a\u8774\u8776\u51fa\u6765\uff0c\u9053\uff1a\u201c\u516c\u516c\uff0c\u4f60\u8fd9\u4e66\u4e2d\uff0c\u672c\u6765\u5c31\u6709\u8fd9\u4e24\u53ea\u8774\u8776\u4e48\uff1f\u201d\u4e07\u9707\u5c71\u5c06\u4e24\u53ea\u7eb8\u8774\u8776\u63a5\u4e86\u8fc7\u53bb\uff0c\u7ec6\u7ec6\u5bdf\u770b\uff0c\u9053\uff1a\u201c\u6ca1\u6709\uff01\u201d\u621a\u82b3\u9053\uff1a\u201c\u8fd9\u662f\u751a\u4e48\u610f\u601d\uff1f \u3000\u3000\u6b66\u6797\u4e4b\u4e2d\uff0c\u53ef\u6709\u54ea\u4e00\u4e2a\u4eba\u5916\u53f7\u53eb\u505a\u2018\u82b1\u8774\u8776\u2019\u751a\u4e48\u7684\uff1f\u6c5f\u6e56\u4e0a\u6709\u6ca1\u6709\u4e00\u4e2a\u2018\u8774\u8776\u5e2e\u2019\uff1f\u4ed6\u4eec\u7559\u4e0b\u8fd9\u672c\u4e66\uff0c\u591a\u534a\u4e0d\u6000\u597d\u610f\u3002\u201d \u3000\u3000\u6c5f\u6e56\u4eba\u7269\u7559\u8bb0\u53f7\u5bfb\u4ec7\u793a\u8b66\uff0c\u539f\u662f\u5341\u5206\u5bfb\u5e38\uff0c\u4e07\u9707\u5c71\u751f\u5e73\u574f\u4e8b\u505a\u4e86\u4e0d\u5c11\uff0c\u4ec7\u5bb6\u4f17\u591a\uff0c\u542c\u4e86\u621a\u82b3\u7684\u8bdd\uff0c\u53c8\u89c1\u8fd9\u4e00\u5bf9\u7eb8\u8774\u8776\u526a\u5f97\u5341\u5206\u5de5\u7ec6\uff0c\u4e0d\u7981\u60d5\u7136\u800c\u60ca\uff0c\u5bfb\u601d\uff1a\u201c\u6211\u6709\u751a\u4e48\u4ec7\u5bb6\u5916\u53f7\u53eb\u505a\u2018\u82b1\u8774\u8776\u2019\u7684\uff1f\u6709\u6ca1\u6709\u4e00\u4e2a\u2018\u8774\u8776\u5e2e\u2019\uff1f\u201d \u3000\u3000\u4ed6\u6b63\u81ea\u6c89\u541f\uff0c\u5ffd\u542c\u5f97\u621a\u82b3\u559d\u9053\uff1a\u201c\u662f\u8c01\uff1f\u9b3c\u9b3c\u795f\u795f\u7684\u60f3\u5e72\u751a\u4e48\uff1f\u201d\u4f38\u624b\u5411\u7a97\u5916\u5c4b\u9876\u4e0a\u4e00\u6307\u3002\u4e07\u6c0f\u7236\u5b50\u540c\u65f6\u5411\u7a97\u5916\u77a7\u53bb\u3002 \u3000\u3000\u621a\u82b3\u53cd\u8eab\u4ece\u5899\u4e0a\u6458\u4e0b\u4e24\u67c4\u957f\u5251\uff0c\u4e00\u67c4\u629b\u7ed9\u4e07\u9707\u5c71\uff0c\u4e00\u67c4\u629b\u7ed9\u4e07\u572d\uff0c\u53eb\u9053\uff1a\u201c\u5c4b\u4e0a\u6709\u4eba\uff01\u201d\u4e07\u6c0f\u7236\u5b50\u63a5\u4f4f\u5175\u5203\uff0c\u621a\u82b3\u62c9\u5f00\u62bd\u5c49\uff0c\u5c06\u90a3\u672c\u5510\u8bd7\u63b7\u4e86\u8fdb\u53bb\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u83ab\u7ed9\u654c\u4eba\u62a2\u4e86\u53bb\uff01\u201d\u4e07\u6c0f\u7236\u5b50\u70b9\u4e86\u70b9\u5934\u3002\u4e09\u4eba\u9f50\u4ece\u7a97\u53e3\u8dc3\u51fa\uff0c\u767b\u4e0a\u74e6\u9762\uff0c\u56db\u4e0b\u91cc\u4e00\u770b\uff0c\u4e0d\u89c1\u6709\u4eba\u3002\u4e07\u9707\u5c71\u9053\uff1a\u201c\u5230\u540e\u9762\u77a7\u77a7\uff01\u201d \u3000\u3000\u4e09\u4eba\u76f4\u5954\u540e\u9662\uff0c\u53ea\u89c1\u5899\u89d2\u8fb9\u4eba\u5f71\u4e00\u6643\uff0c\u4e07\u9707\u5c71\u559d\u9053\uff1a\u201c\u662f\u8c01\uff1f\u201d\u7eb5\u8eab\u9762\u524d\uff0c\u89c1\u90a3\u4eba\u662f\u516d\u5f1f\u5b50\u5434\u574e\uff0c\u95ee\u9053\uff1a\u201c\u89c1\u5230\u654c\u4eba\u6ca1\u6709\uff1f\u201d \u3000\u3000\u5434\u574e\u89c1\u5230\u5e08\u7236\u3001\u4e09\u5e08\u5144\u3001\u4e09\u5e08\u5ac2\u4ed7\u5251\u800c\u6765\uff0c\u53ea\u9053\u4e8b\u53d1\uff0c\u5413\u5f97\u9762\u8272\u60e8\u767d\uff0c\u5f85\u542c\u5e08\u7236\u5982\u6b64\u8be2\u95ee\uff0c\u5fc3\u4e2d\u4e00\u5bbd\uff0c\u5fd9\u9053\uff1a\u201c\u6709\u4eba\u4ece\u8fd9\u8fb9\u5954\u8fc7\uff0c\u5f1f\u5b50\u8d76\u4e86\u8fc7\u6765\u67e5\u95ee\u3002\u201d\u4ed6\u662f\u4e3a\u81ea\u5df1\u63a9\u9970\uff0c\u5374\u6b63\u597d\u66ff\u621a\u82b3\u5706\u4e86\u8c0e\u3002 \u3000\u3000\u56db\u4eba\u76f4\u8ffd\u5230\u540e\u95e8\u4e4b\u5916\uff0c\u5434\u574e\u8fde\u8fde\u547c\u54e8\uff0c\u5c06\u9c81\u5764\u3001\u535c\u57a3\u7b49\u90fd\u62db\u4e86\u6765\uff0c\u81ea\u662f\u6ca1\u53d1\u89c1\u201c\u654c\u4eba\u201d\u7684\u8e2a\u8ff9\u3002 \u3000\u3000\u4e07\u9707\u5c71\u548c\u4e07\u572d\u8bb0\u6302\u7740\u300a\u8fde\u57ce\u5251\u8c31\u300b\uff0c\u547d\u9c81\u5764\u7b49\u7ee7\u7eed\u641c\u5bfb\u654c\u8e2a\uff0c\u62db\u547c\u4e86\u621a\u82b3\uff0c\u56de\u5230\u697c\u623f\u3002\u4e07\u9707\u5c71\u62a2\u5f00\u62bd\u5c49\uff0c\u4f38\u624b\u53bb\u53d6\u2026\u2026\u62bd\u5c49\u4e4b\u4e2d\uff0c\u5374\u54ea\u91cc\u8fd8\u6709\u8fd9\u672c\u4e66\u5728\uff1f \u3000\u3000\u4e07\u6c0f\u7236\u5b50\u8fd9\u4e00\u60ca\u81ea\u7136\u662f\u975e\u540c\u5c0f\u53ef\uff0c\u5728\u4e66\u623f\u4e2d\u5230\u5904\u627e\u5bfb\uff0c\u53c8\u54ea\u91cc\u627e\u5f97\u5230\u4e86\uff1f\u95ee\u5c0f\u5973\u5b69\u9053\uff1a\u201c\u6709\u6ca1\u6709\u4eba\u8fdb\u6765\u8fc7\uff1f\u201d\u5c0f\u5973\u5b69\u9053\uff1a\u201c\u6ca1\u6709\u554a\uff01\u201d\u8f6c\u5934\u5411\u6bcd\u4eb2\u970e\u970e\u773c\u775b\uff0c\u5341\u5206\u5f97\u610f\u3002 \u3000\u3000\u4e07\u6c0f\u7236\u5b50\u660e\u660e\u89c1\u5230\u621a\u82b3\u5c06\u4e66\u653e\u5165\u62bd\u5c49\uff0c\u8ffd\u654c\u4e4b\u65f6\uff0c\u59cb\u7ec8\u6ca1\u79bb\u5f00\u8fc7\u5979\uff0c\u5f53\u7136\u4e0d\u662f\u5979\u505a\u7684\u624b\u811a\u3002\u5b9a\u662f\u654c\u4eba\u65bd\u4e86\u201c\u8c03\u864e\u79bb\u5c71\u4e4b\u8ba1\u201d\uff0c\u76d7\u53bb\u4e86\u5251\u8c31\uff01 \u3000\u3000\u4e07\u6c0f\u7236\u5b50\u9762\u9762\u76f8\u89d1\uff0c\u61ca\u4e27\u4e0d\u5df2\u3002 \u3000\u3000\u621a\u82b3\u6bcd\u5973\u4f60\u5411\u6211\u970e\u970e\u773c\uff0c\u6211\u5411\u4f60\u970e\u970e\u773c\uff0c\u5f88\u662f\u5f00\u5fc3\u3002"}),showOnHomepage:!0},{name:"\u5341\u4e00\u3000\u780c\u5899",content:(0,a.jsx)(a.Fragment,{children:"\u4e07\u95e8\u5f1f\u5b50\u4e71\u4e86\u4e00\u9635\uff0c\u54ea\u8ffd\u5f97\u5230\u751a\u4e48\u654c\u4eba\uff1f \u3000\u3000\u4e07\u9707\u5c71\u5631\u5490\u621a\u82b3\uff0c\u5343\u4e07\u4e0d\u53ef\u5c06\u5251\u8c31\u5f97\u800c\u590d\u5931\u4e4b\u4e8b\u8ddf\u5e08\u5144\u5f1f\u4eec\u63d0\u8d77\u3002\u621a\u82b3\u6ee1\u53e3\u7b54\u5141\u3002\u8fd9\u4e9b\u5e74\u6765\uff0c\u5979\u8d8a\u6765\u8d8a\u662f\u5bdf\u89c9\u5230\uff0c\u4e07\u5bb6\u5e08\u7236\u5f92\u5f1f\u4e0e\u5e08\u5144\u5f1f\u4e4b\u95f4\uff0c\u5927\u5bb6\u90fd\u5404\u6709\u5404\u7684\u6253\u7b97\uff0c\u4f60\u9632\u7740\u6211\uff0c\u6211\u9632\u7740\u4f60\u3002\u4e07\u9707\u5c71\u60ca\u6012\u4ea4\u96c6\uff0c\u56de\u5230\u81ea\u5df1\u623f\u4e2d\uff0c\u53ea\u662f\u51dd\u601d\u7740\u82b1\u8774\u8776\u7684\u8bb0\u53f7\u3002\u4ec7\u4eba\u662f\u8c01\uff1f\u4e3a\u751a\u4e48\u9001\u4e86\u5251\u8c31\u6765\uff1f\u5374\u53c8\u62a2\u4e86\u53bb\uff1f\u662f\u6551\u4e86\u8a00\u8fbe\u5e73\u7684\u90a3\u4eba\u5417\uff1f\u8fd8\u662f\u8a00\u8fbe\u5e73\u81ea\u5df1\uff1f \u3000\u3000\u4e07\u572d\u8ffd\u9010\u654c\u4eba\u65f6\u4e00\u9635\u5954\u9a70\uff0c\u8840\u884c\u52a0\u901f\uff0c\u624b\u80cc\u4f24\u53e3\u53c8\u75db\u4e86\u8d77\u6765\uff0c\u8eba\u5728\u5e8a\u4e0a\u4f11\u606f\uff0c\u8fc7\u4e86\u4e00\u4f1a\uff0c\u4fbf\u7761\u7740\u4e86\u3002 \u3000\u3000\u621a\u82b3\u5bfb\u601d\uff1a\u201c\u8fd9\u672c\u4e66\u7239\u7239\u662f\u6709\u7528\u7684\uff0c\u5728\u8840\u6c34\u4e2d\u6d78\u5f97\u4e45\u4e86\uff0c\u5b9a\u4f1a\u6d78\u574f\uff01\u201d\u5230\u623f\u4e2d\u53eb\u4e86\u4e24\u58f0\u201c\u4e09\u54e5\u201d\uff0c\u89c1\u4ed6\u7761\u5f97\u6b63\u6c89\uff0c\u4fbf\u51fa\u6765\u7aef\u8d77\u94dc\u76c6\uff0c\u5230\u697c\u4e0b\u5929\u4e95\u4e2d\u5012\u53bb\u4e86\u8840\u6c34\uff0c\u9732\u51fa\u90a3\u672c\u4e66\u6765\u3002\u5979\u5fc3\u60f3\uff1a\u201c\u7a7a\u5fc3\u83dc\u771f\u4e56\uff01\u201d\u8138\u4e0a\u9732\u51fa\u4e86\u7b11\u5bb9\u3002 \u3000\u3000\u90a3\u672c\u4e66\u6d78\u6ee1\u4e86\u8840\u6c34\u3002\u8165\u81ed\u6251\u9f3b\uff0c\u621a\u82b3\u4e0d\u613f\u7528\u624b\u53bb\u62ff\uff0c\u5bfb\u601d\uff1a\u201c\u5374\u85cf\u5728\u54ea\u91cc\u597d\uff1f\u201d\u60f3\u8d77\u540e\u56ed\u897f\u504f\u623f\u4e2d\u4e00\u5411\u5806\u7f6e\u7b5b\u5b50\u3001\u9504\u5934\u3001\u77f3\u81fc\u3001\u98ce\u6247\u4e4b\u7c7b\u6742\u7269\uff0c\u8fd9\u65f6\u5019\u51b3\u8ba1\u65e0\u4eba\u8fc7\u53bb\uff0c\u5f53\u4e0b\u5728\u5ead\u4e2d\u83ca\u82b1\u4e0a\u6458\u4e9b\u53f6\u5b50\uff0c\u906e\u4f4f\u4e86\u4e66\uff0c\u5c31\u50cf\u662f\u6367\u4e00\u76d8\u83ca\u82b1\u53f6\u5b50\uff0c\u6765\u5230\u540e\u56ed\u3002\u5979\u8d70\u8fdb\u897f\u504f\u623f\uff0c\u5c06\u90a3\u4e66\u653e\u5165\u717d\u8c37\u7684\u98ce\u6247\u809a\u4e2d\uff0c\u5fc3\u60f3\uff1a \u3000\u3000\u201c\u8fd9\u98ce\u6247\u8981\u5230\u6536\u79df\u8c37\u65f6\u624d\u7528\u3002\u85cf\u5728\u8fd9\u91cc\uff0c\u8c01\u4e5f\u4e0d\u4f1a\u627e\u5230\u3002\u201d \u3000\u3000\u5979\u7aef\u4e86\u8138\u76c6\uff0c\u53e3\u4e2d\u8f7b\u8f7b\u54fc\u7740\u6b4c\u513f\uff0c\u88c5\u7740\u6ca1\u6709\u4e8b\u4eba\u822c\u56de\u6765\uff0c\u7ecf\u8fc7\u8d70\u5eca\u65f6\uff0c\u5ffd\u7136\u5899\u89d2\u8fb9\u95ea\u51fa\u4e00\u4eba\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u4eca\u665a\u4e09\u66f4\uff0c\u6211\u5728\u67f4\u623f\u91cc\u7b49\u4f60\uff0c\u53ef\u522b\u5fd8\u4e86\uff01\u201d\u6b63\u662f\u5434\u574e\u3002 \u3000\u3000\u621a\u82b3\u5fc3\u4e2d\u672c\u5728\u62c5\u60ca\uff0c\u7a81\u7136\u89c1\u4ed6\u95ea\u4e86\u51fa\u6765\u8bf4\u8fd9\u51e0\u53e5\u8bdd\uff0c\u4e00\u9897\u5fc3\u8df3\u5f97\u66f4\u662f\u5389\u5bb3\uff0c\u5550\u9053\uff1a\u201c\u6ca1\u597d\u6b7b\u7684\uff0c\u72d7\u80c6\u5b50\u8fd9\u4e48\u5927\uff0c\u8fde\u547d\u4e5f\u4e0d\u8981\u4e86\uff1f\u201d\u5434\u574e\u6d8e\u7740\u8138\u9053\uff1a\u201c\u6211\u4e3a\u4f60\u9001\u4e86\u6027\u547d\uff0c\u5f53\u771f\u662f\u5fc3\u7518\u60c5\u613f\u3002\u5e08\u5ac2\uff0c\u4f60\u8981\u4e0d\u8981\u89e3\u836f\uff1f\u201d\u621a\u82b3\u54ac\u7740\u7259\u9f7f\uff0c\u5de6\u624b\u4f38\u5165\u6000\u4e2d\uff0c\u63e1\u4f4f\u5315\u9996\u7684\u67c4\uff0c\u4fbf\u60f3\u51fa\u5176\u4e0d\u610f\u7684\u62d4\u51fa\u5315\u9996\uff0c\u7ed9\u4ed6\u4e00\u4e0b\u5b50\uff0c\u5c06\u89e3\u836f\u593a\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u5434\u574e\u563b\u563b\u7684\u4f4e\u58f0\u9053\uff1a\u201c\u4f60\u82e5\u4f7f\u4e00\u62db\u2018\u5c71\u4ece\u4eba\u9762\u8d77\u2019\uff0c\u633a\u5200\u5411\u6211\u523a\u6765\uff0c\u6211\u7528\u4e00\u62db\u2018\u4e91\u508d\u9a6c\u5934\u751f\u2019\u907f\u5f00\uff0c\u968f\u624b\u8fd9\u4e48\u4e00\u626c\uff0c\u5c06\u89e3\u836f\u6454\u5165\u4e86\u8fd9\u53e3\u6c34\u7f38\u3002\u201d\u8bf4\u7740\u4f38\u51fa\u624b\u6765\uff0c\u638c\u4e2d\u4fbf\u662f\u90a3\u74f6\u89e3\u836f\u3002 \u3000\u3000\u4ed6\u6015\u621a\u82b3\u6765\u593a\uff0c\u8ddf\u7740\u9000\u4e86\u4e24\u6b65\u3002 \u3000\u3000\u621a\u82b3\u77e5\u9053\u7528\u5f3a\u4e0d\u80fd\u593a\u5230\uff0c\u4e00\u4fa7\u8eab\u4fbf\u4ece\u4ed6\u8eab\u8fb9\u8d70\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u5434\u574e\u4f4e\u58f0\u9053\uff1a\u201c\u6211\u53ea\u7b49\u4f60\u5230\u4e09\u66f4\uff0c\u4f60\u4e09\u66f4\u4e0d\u6765\uff0c\u56db\u66f4\u4e0a\u6211\u4fbf\u5e26\u89e3\u836f\u8d70\u4e86\uff0c\u9ad8\u98de\u8fdc\u8d70\uff0c\u518d\u4e5f\u4e0d\u56de\u8346\u5dde\u4e86\u3002\u59d3\u5434\u7684\u5c31\u662f\u8981\u6b7b\uff0c\u4e5f\u4e0d\u80fd\u6b7b\u5728\u4e07\u5bb6\u7236\u5b50\u624b\u4e0b\u3002\u201d \u3000\u3000\u621a\u82b3\u56de\u5230\u623f\u4e2d\uff0c\u53ea\u542c\u5f97\u4e07\u572d\u4e0d\u4f4f\u547b\u541f\uff0c\u663e\u662f\u874e\u6bd2\u53c8\u53d1\u4f5c\u8d77\u6765\u3002\u5979\u5750\u5728\u5e8a\u8fb9\uff0c\u5bfb\u601d\uff1a\u201c\u4ed6\u6bd2\u5bb3\u72c4\u5e08\u54e5\uff0c\u624b\u6bb5\u5351\u9119\u4e4b\u6781\uff0c\u53ef\u662f\u5927\u9519\u5df2\u7ecf\u94f8\u6210\uff0c\u53c8\u6709\u751a\u4e48\u6cd5\u5b50\uff1f\u90a3\u662f\u5e08\u54e5\u547d\u82e6\uff0c\u4e5f\u662f\u6211\u547d\u82e6\u3002\u4ed6\u8fd9\u51e0\u5e74\u6765\u5f85\u6211\u5f88\u597d\uff0c\u6211\u662f\u5ac1\u9e21\u968f\u9e21\uff0c\u8fd9\u4e00\u8f88\u5b50\u603b\u662f\u8ddf\u7740\u4ed6\u505a\u592b\u59bb\u4e86\u3002\u5434\u574e\u8fd9\u72d7\u8d3c\u8fd9\u822c\u53ef\u6076\uff0c\u600e\u4e48\u593a\u5230\u4ed6\u7684\u89e3\u836f\u624d\u597d\uff1f\u201d\u773c\u89c1\u4e07\u572d\u5bb9\u8272\u6194\u60b4\uff0c\u53cc\u76ee\u6df1\u9677\uff0c\u5fc3\u60f3\uff1a\u201c\u4e09\u54e5\u4f24\u91cd\uff0c\u82e5\u662f\u8ddf\u4ed6\u8bf4\u4e86\uff0c\u4ed6\u4e00\u6012\u4e4b\u4e0b\u53bb\u548c\u5434\u574e\u62da\u547d\uff0c\u53ea\u6709\u628a\u4e8b\u513f\u5f04\u7cdf\u3002\u201d \u3000\u3000\u5929\u8272\u6e10\u6e10\u9ed1\u4e86\u4e0b\u6765\uff0c\u621a\u82b3\u80e1\u4e71\u5403\u4e86\u665a\u996d\uff0c\u5b89\u987f\u5973\u513f\u7761\u4e86\uff0c\u60f3\u6765\u60f3\u53bb\uff0c\u53ea\u6709\u53bb\u544a\u77e5\u516c\u516c\uff0c\u6599\u60f3\u4ed6\u8001\u8c0b\u6df1\u7b97\uff0c\u5fc5\u6709\u5584\u7b56\u3002\u8fd9\u4ef6\u4e8b\u4e0d\u80fd\u8ba9\u4e08\u592b\u77e5\u9053\uff0c\u8981\u7b49\u4ed6\u719f\u7761\u4e86\uff0c\u518d\u53bb\u8ddf\u516c\u516c\u8bf4\u3002\u621a\u82b3\u548c\u8863\u8eba\u5728\u4e07\u572d\u811a\u8fb9\u3002\u8fd9\u51e0\u65e5\u6765\u670d\u4f8d\u4e08\u592b\uff0c\u5979\u59cb\u7ec8\u8863\u4e0d\u89e3\u5e26\uff0c\u6ca1\u597d\u597d\u7761\u8fc7\u4e00\u665a\u3002\u76f4\u5230\u4e07\u572d\u9f3b\u606f\u6c89\u9163\uff0c\u5979\u6084\u6084\u8d77\u6765\uff0c\u4e0b\u5f97\u697c\u53bb\uff0c\u6765\u5230\u4e07\u9707\u5c71\u5c4b\u5916\u3002 \u3000\u3000\u5c4b\u91cc\u706f\u706b\u5df2\u7184\uff0c\u5374\u4f20\u51fa\u4e00\u9635\u9635\u5947\u602a\u7684\u58f0\u97f3\u6765\uff0c\u201c\u563f\uff0c\u563f\uff0c\u563f\uff01\u201d\u4f3c\u4e4e\u6709\u4eba\u5728\u5927\u8d39\u529b\u6c14\u505a\u751a\u4e48\u4e8b\u3002\u621a\u82b3\u751a\u662f\u5947\u602a\uff0c\u672c\u5df2\u5230\u4e86\u53e3\u8fb9\u7684\u4e00\u53e5\u201c\u516c\u516c\u201d\u53c8\u7f29\u4e86\u56de\u53bb\uff0c\u4ece\u7a97\u7f1d\u4e2d\u5411\u623f\u5185\u5f20\u53bb\u3002\u5176\u65f6\u6708\u5149\u659c\u7167\uff0c\u900f\u8fc7\u7a97\u7eb8\uff0c\u6620\u8fdb\u623f\u4e2d\uff0c\u53ea\u89c1\u4e07\u9707\u5c71\u4ef0\u5367\u5728\u5e8a\uff0c\u53cc\u624b\u7f13\u7f13\u7684\u5411\u7a7a\u4e2d\u529b\u63a8\uff0c\u53cc\u773c\u5374\u7d27\u7d27\u95ed\u7740\u3002 \u3000\u3000\u621a\u82b3\u5fc3\u9053\uff1a\u201c\u539f\u6765\u516c\u516c\u5728\u7ec3\u9ad8\u6df1\u5185\u529f\u3002\u7ec3\u5185\u529f\u4e4b\u65f6\u6700\u5fcc\u53d7\u5230\u5916\u754c\u60ca\u6270\uff0c\u5426\u5219\u6781\u6613\u8d70\u706b\u5165\u9b54\u3002\u8fd9\u65f6\u53ef\u4e0d\u80fd\u53eb\u4ed6\uff0c\u7b49\u4ed6\u7ec3\u5b8c\u4e86\u529f\u592b\u518d\u8bf4\u3002\u201d \u3000\u3000\u53ea\u89c1\u4e07\u9707\u5c71\u53cc\u624b\u7a7a\u63a8\u4e00\u9635\uff0c\u7f13\u7f13\u5750\u8d77\u8eab\u6765\uff0c\u4f38\u817f\u4e0b\u5e8a\uff0c\u5411\u524d\u8d70\u4e86\u51e0\u6b65\uff0c\u8e72\u4e0b\u8eab\u5b50\uff0c\u51cc\u7a7a\u4fbf\u4f38\u624b\u53bb\u6293\u751a\u4e48\u7269\u4e8b\u3002\u621a\u82b3\u5fc3\u60f3\uff1a\u201c\u516c\u516c\u7ec3\u7684\u662f\u64d2\u62ff\u624b\u6cd5\u3002\u201d\u53c8\u770b\u5f97\u7247\u65f6\uff0c\u4f46\u89c1\u4e07\u9707\u5c71\u7684\u624b\u52bf\u8d8a\u6765\u8d8a\u602a\uff0c\u53cc\u624b\u4e0d\u4f4f\u5728\u7a7a\u4e2d\u6293\u4e0b\u751a\u4e48\u4e1c\u897f\uff0c\u968f\u5373\u6574\u6574\u9f50\u9f50\u7684\u6392\u5728\u4e00\u8d77\uff0c\u5012\u4f3c\u662f\u5c06\u8bb8\u591a\u7816\u5757\u5b89\u653e\u5806\u53e0\u4e00\u822c\uff0c\u4f46\u6708\u5149\u4e0b\u770b\u5f97\u660e\u767d\uff0c\u5730\u677f\u4e0a\u663e\u662f\u7a7a\u65e0\u4e00\u7269\u3002 \u3000\u3000\u53ea\u89c1\u4ed6\u51cc\u7a7a\u6293\u4e86\u4e00\u4f1a\uff0c\u53cc\u624b\u6bd4\u4e86\u4e00\u6bd4\uff0c\u4f3c\u4e4e\u8ba4\u4e3a\u591f\u5927\u4e86\uff0c\u4e8e\u662f\u53cc\u624b\u4f5c\u52bf\u5728\u5730\u4e0b\u6367\u8d77\u4e00\u4ef6\u5927\u7269\uff0c\u5411\u524d\u585e\u4e86\u8fc7\u53bb\u3002\u621a\u82b3\u770b\u5f97\u8ff7\u60d8\u4e0d\u5df2\uff0c\u773c\u89c1\u4e07\u9707\u5c71\u4ecd\u662f\u53cc\u76ee\u7d27\u95ed\uff0c\u4e00\u4e3e\u4e00\u52a8\u51b3\u4e0d\u50cf\u662f\u7ec3\u529f\uff0c\u5012\u4f3c\u662f\u4e2a\u54d1\u5df4\u5728\u505a\u620f\u4e00\u822c\u3002 \u3000\u3000\u7a81\u7136\u4e4b\u95f4\uff0c\u5979\u60f3\u5230\u4e86\u6843\u7ea2\u5728\u7834\u7960\u5802\u5916\u8bf4\u7684\u90a3\u53e5\u8bdd\u6765\uff1a\u201c\u8001\u7237\u534a\u591c\u4e09\u66f4\u8d77\u6765\u780c\u5899\uff01\u201d \u3000\u3000\u53ef\u662f\u4e07\u9707\u5c71\u8fd9\u4e3e\u52a8\u51b3\u4e0d\u662f\u5728\u780c\u5899\uff0c\u8981\u662f\u8bf4\u8ddf\u5899\u5934\u6709\u751a\u4e48\u5173\u8fde\uff0c\u90a3\u662f\u5728\u62c6\u5899\u6d1e\u3002 \u3000\u3000\u621a\u82b3\u611f\u5230\u4e00\u9635\u6050\u60e7\uff1a\u201c\u662f\u4e86\uff0c\u516c\u516c\u60a3\u4e86\u79bb\u9b42\u75c7\u3002\u542c\u8bf4\u751f\u4e86\u8fd9\u75c5\u7684\uff0c\u7761\u68a6\u4e2d\u4f1a\u8d77\u8eab\u884c\u8d70\u505a\u4e8b\u3002\u6709\u4eba\u4e0d\u7a7f\u8863\u670d\u5728\u5c4b\u9876\u884c\u8d70\uff0c\u6709\u4eba\u751a\u81f3\u4f1a\u6740\u4eba\u653e\u706b\uff0c\u9192\u8f6c\u4e4b\u540e\u5374\u5168\u65e0\u6240\u77e5\u3002\u201d \u3000\u3000\u53ea\u89c1\u4e07\u9707\u5c71\u5c06\u7a7a\u65e0\u6240\u6709\u7684\u91cd\u7269\u585e\u5165\u7a7a\u65e0\u6240\u6709\u7684\u5899\u6d1e\u4e4b\u540e\uff0c\u51cc\u7a7a\u7528\u529b\u63a8\u4e86\u51e0\u4e0b\uff0c\u7136\u540e\u62fe\u8d77\u5730\u4e0b\u7a7a\u65e0\u6240\u6709\u7684\u7816\u5934\uff0c\u780c\u8d77\u5899\u6765\u3002 \u3000\u3000\u4e0d\u9519\uff0c\u4ed6\u679c\u771f\u662f\u5728\u780c\u5899\uff01\u8138\u4e0a\u5fae\u7b11\uff0c\u5f97\u610f\u6d0b\u6d0b\u7684\u780c\u5899\uff01 \u3000\u3000\u621a\u82b3\u521d\u65f6\u770b\u5230\u4ed6\u8fd9\u526f\u9634\u68ee\u68ee\u7684\u6a21\u6837\uff0c\u6709\u4e9b\u6bdb\u9aa8\u609a\u7136\uff0c\u5f85\u89c1\u4ed6\u786e\u662f\u5728\u4f5c\u780c\u5899\u4e4b\u72b6\uff0c\u5fc3\u4e2d\u5df2\u6709\u4e86\u5148\u5165\u4e4b\u89c1\uff0c\u4fbf\u4e0d\u6015\u4e86\uff0c\u5fc3\u9053\uff1a\u201c\u7167\u6843\u7ea2\u7684\u8bdd\u8bf4\u6765\uff0c\u516c\u516c\u8fd9\u79bb\u9b42\u75c7\u5df2\u60a3\u5f97\u4e45\u4e86\u3002\u6709\u75c5\u4e4b\u4eba\u5927\u90fd\u4e0d\u613f\u7ed9\u4eba\u77e5\u9053\u3002\u6843\u7ea2\u548c\u4ed6\u540c\u623f\uff0c\u5f97\u77e5\u4e86\u5e95\u7ec6\uff0c\u516c\u516c\u81ea\u7136\u8981\u5927\u5927\u4e0d\u5f00\u5fc3\u3002\u201d\u8fd9\u4e48\u4e00\u6765\uff0c\u5012\u89e3\u5f00\u4e86\u5fc3\u4e2d\u4e00\u4e2a\u7591\u56e2\uff0c\u660e\u767d\u6843\u7ea2\u4f55\u4ee5\u88ab\u9010\uff0c\u53c8\u60f3\uff1a\u201c\u4e0d\u77e5\u4ed6\u780c\u5899\u8981\u780c\u591a\u4e45\uff0c\u5018\u82e5\u8fc7\u4e86\u4e09\u66f4\uff0c\u5434\u574e\u90a3\u53ae\u5f53\u771f\u6bc1\u4e86\u89e3\u836f\u9003\u8d70\uff0c\u90a3\u53ef\u7cdf\u4e86\u3002\u201d \u3000\u3000\u4f46\u89c1\u4e07\u9707\u5c71\u5c06\u62c6\u4e0b\u6765\u7684\u201c\u7816\u5757\u201d\u90fd\u653e\u5165\u4e86\u201c\u5899\u6d1e\u201d\uff0c\u8ddf\u7740\u4fbf\u5237\u8d77\u201c\u77f3\u7070\u201d\u6765\uff0c\u76f4\u5230\u201c\u529f\u592b\u201d\u505a\u5f97\u59a5\u59a5\u5e16\u5e16\uff0c\u8fd9\u624d\u8138\u9732\u5fae\u7b11\uff0c\u4e0a\u5e8a\u5b89\u7761\u3002 \u3000\u3000\u621a\u82b3\u5fc3\u60f3\uff1a\u201c\u516c\u516c\u5fd9\u4e86\u8fd9\u4e48\u4e00\u5927\u9635\uff0c\u795e\u601d\u5c1a\u672a\u5b81\u5b9a\uff0c\u4e14\u8ba9\u4ed6\u6b47\u4e00\u6b47\uff0c\u6211\u518d\u53eb\u4ed6\u3002\u201d \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u5374\u542c\u5f97\u623f\u95e8\u4e0a\u6709\u4eba\u8f7b\u8f7b\u6572\u4e86\u51e0\u4e0b\uff0c\u8ddf\u7740\u6709\u4eba\u4f4e\u58f0\u53eb\u9053\uff1a\u201c\u7239\u7239\uff0c\u7239\u7239\uff01\u201d\u6b63\u662f\u5979\u4e08\u592b\u4e07\u572d\u7684\u58f0\u97f3\u3002\u621a\u82b3\u5fae\u5fae\u4e00\u60ca\uff1a\u201c\u600e\u4e48\u4e09\u54e5\u4e5f\u6765\u4e86\uff1f\u4ed6\u6765\u5e72\u751a\u4e48\uff1f\u201d \u3000\u3000\u4e07\u9707\u5c71\u7acb\u5373\u5750\u8d77\uff0c\u7565\u4e00\u5b9a\u795e\uff0c\u95ee\u9053\uff1a\u201c\u662f\u572d\u513f\u4e48\uff1f\u201d\u4e07\u572d\u9053\uff1a\u201c\u662f\u6211\uff01\u201d\u4e07\u9707\u5c71\u4e00\u8dc3\u4e0b\u5e8a\uff0c\u62d4\u5f00\u95e8\u95e9\uff0c\u653e\u4e86\u4e07\u572d\u8fdb\u6765\uff0c\u95ee\u9053\uff1a\u201c\u5f97\u5230\u5251\u8c31\u7684\u8baf\u606f\u4e48\uff1f\u201d\u4e07\u572d\u53eb\u4e86\u58f0\u201c\u7239\uff01\u201d\u4f38\u5de6\u624b\u63e1\u4f4f\u6905\u80cc\u6708\u5149\u4ece\u7eb8\u7a97\u4e2d\u6620\u5c04\u8fdb\u623f\uff0c\u7167\u5230\u4ed6\u6726\u80e7\u7684\u8eab\u5f62\uff0c\u4f3c\u5728\u5fae\u5fae\u6447\u6643\u621a\u82b3\u6015\u81ea\u5df1\u7684\u5f71\u5b50\u5728\u7a97\u4e0a\u7ed9\u6620\u4e86\u51fa\u6765\uff0c\u7f29\u8eab\u7a97\u4e0b\uff0c\u4fa7\u8eab\u503e\u542c\uff0c\u4e0d\u6562\u518d\u770b\u4e24\u4eba\u7684\u52a8\u9759\u3002 \u3000\u3000\u53ea\u542c\u4e07\u572d\u53c8\u53eb\u4e86\u58f0\u201c\u7239\u201d\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u513f\u5ab3\u5987\u2026\u2026\u4f60\u513f\u5ab3\u5987\u2026\u2026\u539f\u6765\u4e0d\u662f\u597d\u4eba\u3002\u201d\u621a\u82b3\u4e00\u60ca\uff1a\u201c\u4ed6\u4e3a\u751a\u4e48\u8fd9\u4e48\u8bf4\uff1f\u201d\u53ea\u542c\u4e07\u9707\u5c71\u4e5f\u95ee\uff1a\u201c\u600e\u4e48\u5566\uff1f\u5c0f\u592b\u59bb\u62cc\u4e86\u5634\u4e48\uff1f\u201d\u4e07\u572d\u9053\uff1a\u201c\u5251\u8c31\u627e\u5230\u4e86\uff0c\u662f\u4f60\u513f\u5ab3\u5987\u62ff\u4e86\u53bb\u3002\u201d\u4e07\u9707\u5c71\u559c\u9053\uff1a\u201c\u627e\u5230\u4e86\u4fbf\u597d\uff01\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u621a\u82b3\u60ca\u5947\u4e4b\u6781\uff1a\u201c\u600e\u4e48\u4f1a\u7ed9\u4ed6\u77e5\u9053\u7684\uff1f\u55ef\uff0c\u591a\u534a\u662f\u7a7a\u5fc3\u83dc\u8fd9\u5c0f\u5bb6\u4f19\u5fcd\u4e0d\u4f4f\u8bf4\u4e86\u51fa\u6765\u3002\u201d\u4f46\u4e07\u572d\u63a5\u4e0b\u53bb\u7684\u8bf4\u8bdd\uff0c\u7acb\u5373\u4fbf\u8ba9\u5979\u77e5\u9053\u81ea\u5df1\u731c\u5f97\u4e0d\u5bf9\u3002\u4e07\u572d\u544a\u8bc9\u7236\u4eb2\uff1a\u4ed6\u89c1\u621a\u82b3\u548c\u5973\u513f\u4e92\u4f7f\u773c\u8272\uff0c\u795e\u60c5\u6709\u5f02\uff0c\u6599\u5230\u5fc5\u6709\u53e4\u602a\u3002\u4fbf\u5047\u88c5\u7761\u7740\uff0c\u5374\u5728\u95e8\u7f1d\u4e2d\u5bdf\u770b\u621a\u82b3\u7684\u52a8\u9759\uff0c\u89c1\u5979\u624b\u7aef\u94dc\u76c6\u8d70\u5411\u540e\u56ed\uff0c\u4ed6\u6084\u6084\u8ddf\u968f\uff0c\u89c1\u5979\u5c06\u5251\u8c31\u85cf\u5165\u4e86\u540e\u56ed\u897f\u504f\u623f\u4e00\u67b6\u98ce\u6247\u4e4b\u4e2d\u3002 \u3000\u3000\u621a\u82b3\u5fc3\u4e2d\u53f9\u606f\uff1a\u201c\u82e6\u547d\u7684\u7239\u7239\uff0c\u8fd9\u672c\u4e66\u7ec8\u4e8e\u7ed9\u516c\u516c\u548c\u4e09\u54e5\u5f97\u53bb\u4e86\u3002\u518d\u8981\u60f3\u62ff\u56de\u6765\uff0c\u90a3\u662f\u5343\u96be\u4e07\u96be\u4e86\u3002\u597d\uff0c\u6211\u8ba4\u8f93\uff0c\u4e09\u54e5\u672c\u6765\u6bd4\u6211\u5389\u5bb3\u5f97\u591a\u3002\u201d \u3000\u3000\u53ea\u542c\u4e07\u9707\u5c71\u9053\uff1a\u201c\u90a3\u597d\u5f97\u5f88\u554a\u3002\u54b1\u4eec\u53bb\u53d6\u4e86\u51fa\u6765\uff0c\u4f60\u88c5\u4f5c\u751a\u4e48\u4e5f\u4e0d\u77e5\u9053\uff0c\u4e14\u770b\u5979\u5982\u4f55\u3002\u5979\u8981\u662f\u4e0d\u63d0\uff0c\u4f60\u4e5f\u5c31\u4e0d\u5fc5\u8bf4\u7834\u3002 \u3000\u3000\u6211\u603b\u662f\u7591\u5fc3\uff0c\u8fd9\u672c\u4e66\u5230\u5e95\u662f\u54ea\u91cc\u6765\u7684\u3002\u53ea\u6015\u2026\u2026\u53ea\u6015\u2026\u2026\u53ea\u6015\u2026\u2026\u201d\u4ed6\u8fde\u8bf4\u4e86\u4e09\u4e2a\u201c\u53ea\u6015\u201d\uff0c\u5374\u4e0d\u8bf4\u4e0b\u53bb\u3002 \u3000\u3000\u4e07\u572d\u53eb\u9053\uff1a\u201c\u7239\uff01\u201d\u58f0\u97f3\u663e\u5f97\u751a\u662f\u75db\u82e6\u3002\u4e07\u9707\u5c71\u53eb\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u4e07\u572d\u9053\uff1a\u201c\u4f60\u513f\u5ab3\u5987\u2026\u2026\u513f\u5ab3\u5987\u76d7\u54b1\u4eec\u8fd9\u672c\u5251\u8c31\uff0c\u539f\u6765\u662f\u4e3a\u4e86\u2026\u2026\u201d\u8bf4\u5230\u8fd9\u91cc\uff0c\u58f0\u97f3\u53d1\u98a4\uff0c\u4e07\u9707\u5c71\u9053\uff1a\u4e3a\u4e86\u8c01\uff1f\u201d\u4e07\u572d\u9053\uff1a\u201c\u539f\u6765\u2026\u2026\u662f\u4e3a\u4e86\u5434\u574e\u8fd9\u72d7\u8d3c\uff01\u201d \u3000\u3000\u4e07\u9707\u5c71\u7684\u8bed\u58f0\u4e2d\u4e5f\u662f\u5145\u6ee1\u4e86\u60ca\u5947\uff1a\u201c\u4e3a\u4e86\u5434\u574e\uff1f\u201d\u4e07\u572d\u9053\uff1a\u201c\u662f\uff01\u6211\u5728\u540e\u56ed\u4e2d\u89c1\u8fd9\u8d31\u4eba\u85cf\u597d\u5251\u8c31\uff0c\u4fbf\u8fdc\u8fdc\u7684\u8ddf\u7740\u5979\uff0c\u54ea\u77e5\u9053\u5979\u2026\u2026\u5979\u5230\u4e86\u56de\u5eca\u4e0a\uff0c\u7adf\u548c\u5434\u574e\u90a3\u53ae\u52fe\u52fe\u642d\u642d\uff0c\u8fd9\u6deb\u5987\u2026\u2026\u597d\u4e0d\u8981\u8138\uff01\u201d\u4e07\u9707\u5c71\u6c89\u541f\u9053\uff1a\u201c\u6211\u770b\u5979\u5e73\u7d20\u4e3a\u4eba\u5012\u4e5f\u89c4\u77e9\u7aef\u6b63\uff0c\u4e0d\u50cf\u662f\u8fd9\u6837\u5b50\u7684\u4eba\u3002\u4f60\u6ca1\u77a7\u9519\u4e48\uff1f\u4ed6\u4e8c\u4eba\u8bf4\u4e9b\u751a\u4e48\uff1f\u201d\u4e07\u572d\u9053\uff1a\u201c\u5b69\u513f\u6015\u4ed6\u4eec\u77e5\u89c9\uff0c\u4e0d\u6562\u8d70\u5f97\u592a\u8fd1\uff0c\u56de\u5eca\u4e0a\u6ca1\u9690\u853d\u7684\u5730\u65b9\uff0c\u53ea\u6709\u8eb2\u5728\u5899\u89d2\u540e\u9762\u3002\u8fd9\u4e24\u4e2a\u72d7\u7537\u5973\u8bf4\u8bdd\u5f88\u8f7b\uff0c\u6ca1\u80fd\u5b8c\u5168\u542c\u5230\uff0c\u53ef\u662f\u2026\u2026\u53ef\u662f\u4e5f\u542c\u5230\u4e86\u5927\u534a\u3002\u201d\u4e07\u9707\u5c71\u201c\u55ef\u201d\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5b69\u513f\uff0c\u4f60\u522b\u6c14\u6025\u3002\u5927\u4e08\u592b\u4f55\u60a3\u65e0\u59bb\uff1f\u54b1\u4eec\u65e2\u5f97\u4e86\u5251\u8c31\uff0c\u53c8\u67e5\u660e\u4e86\u8fd9\u4e2d\u95f4\u7684\u79d8\u5bc6\uff0c\u8f6c\u773c\u4fbf\u53ef\u5bcc\u7532\u5929\u4e0b\uff0c\u4f60\u4fbf\u8981\u4e70\u4e00\u767e\u4e2a\u59ec\u59be\uff0c\u90a3\u4e5f\u5bb9\u6613\u5f97\u7d27\u3002\u4f60\u5750\u4e0b\uff0c\u6162\u6162\u7684\u8bf4\uff01\u201d \u3000\u3000\u53ea\u542c\u5f97\u5e8a\u677f\u683c\u683c\u4e24\u54cd\uff0c\u4e07\u572d\u5750\u5230\u4e86\u5e8a\u4e0a\uff0c\u6c14\u5598\u5598\u7684\u9053\uff1a\u201c\u90a3\u6deb\u5987\u85cf\u597d\u4e66\u672c\uff0c\u5f88\u662f\u5f97\u610f\uff0c\u5634\u91cc\u5c45\u7136\u54fc\u7740\u5c0f\u66f2\u3002\u90a3\u5978\u592b\u4e00\u89c1\u5230\u5979\uff0c\u6ee1\u8138\u5806\u6b22\uff0c\u8bf4\u9053\uff1a\u2018\u4eca\u665a\u4e09\u66f4\uff0c\u6211\u5728\u67f4\u623f\u4e2d\u7b49\u4f60\uff0c\u53ef\u522b\u5fd8\u4e86\uff01\u2019\u7684\u7684\u786e\u786e\u662f\u8fd9\u51e0\u53e5\u8bdd\uff0c\u6211\u662f\u542c\u5f97\u6e05\u6e05\u695a\u695a\u7684\u3002\u201d\u4e07\u9707\u5c71\u6012\u9053\uff1a\u201c\u90a3\u5c0f\u6deb\u5987\u53c8\u600e\u4e48\u8bf4\uff1f\u201d\u4e07\u572d\u9053\uff1a\u201c\u5979\u2026\u2026\u5979\u8bf4\u9053\uff1a\u2018\u6ca1\u597d\u6b7b\u7684\uff0c\u72d7\u80c6\u5b50\u8fd9\u4e48\u5927\uff0c\u8fde\u547d\u4e5f\u4e0d\u8981\u4e86\uff01\u2019\u201d \u3000\u3000\u621a\u82b3\u5728\u7a97\u5916\u53ea\u542c\u5f97\u5fc3\u4e71\u5982\u9ebb\uff1a\u201c\u4ed6\u2026\u2026\u4ed6\u4e8c\u4eba\u53e3\u53e3\u58f0\u58f0\u7684\u9a82\u6211\u6deb\u5987\uff0c\u600e\u2026\u2026\u600e\u4e48\u80fd\u5982\u6b64\u7684\u51a4\u6789\u4eba\u5bb6\uff1f\u4e09\u54e5\uff0c\u6211\u662f\u4e00\u7247\u4e3a\u4f60\u4e4b\u5fc3\uff0c\u8981\u593a\u56de\u89e3\u836f\uff0c\u6cbb\u4f60\u4e4b\u4f24\uff0c\u4f60\u5374\u8fd9\u822c\u8fb1\u6211\uff0c\u53ef\u8fd8\u6709\u826f\u5fc3\u6ca1\u6709\uff1f\u201d \u3000\u3000\u53ea\u542c\u4e07\u572d\u7eed\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\u542c\u4e86\u4ed6\u4eec\u8fd9\u4e48\u8bf4\uff0c\u5fc3\u5934\u706b\u8d77\uff0c\u6068\u4e0d\u5f97\u62d4\u5251\u4e0a\u524d\u5c06\u4e8c\u4eba\u6740\u4e86\u3002\u53ea\u662f\u6211\u6ca1\u5e26\u5251\uff0c\u53c8\u662f\u4f24\u540e\u6ca1\u529b\uff0c\u4e0d\u80fd\u8ddf\u4ed6\u4eec\u660e\u4e89\uff0c\u5f53\u5373\u8d76\u56de\u623f\u53bb\uff0c\u514d\u5f97\u90a3\u8d3c\u6deb\u5987\u56de\u623f\u65f6\u4e0d\u89c1\u5230\u6211\uff0c\u8d77\u4e86\u7591\u5fc3\u3002\u5978\u592b\u6deb\u5987\u4ee5\u540e\u518d\u8bf4\u751a\u4e48\uff0c\u6211\u5c31\u6ca1\u518d\u542c\u89c1\u3002\u201d \u3000\u3000\u4e07\u9707\u5c71\u9053\uff1a\u201c\u54fc\uff0c\u6709\u5176\u7236\u5fc5\u6709\u5176\u5973\uff0c\u679c\u7136\u4e00\u95e8\u90fd\u662f\u65e0\u803b\u4e4b\u8f88\u3002 \u3000\u3000\u54b1\u4eec\u5148\u53bb\u53d6\u4e86\u5251\u8c31\uff0c\u518d\u5230\u67f4\u623f\u5916\u5b88\u5019\u3002\u6349\u5978\u6349\u53cc\uff0c\u53eb\u8fd9\u5bf9\u72d7\u7537\u5973\u6b7b\u800c\u65e0\u6028\uff01\u201d\u4e07\u572d\u9053\uff1a\u201c\u90a3\u6deb\u5987\u604b\u5978\u60c5\u70ed\uff0c\u7b49\u4e0d\u5230\u4e09\u66f4\u5929\uff0c\u65e9\u5c31\u51fa\u53bb\u4e86\uff0c\u8fd9\u4f1a\u513f\u2026\u2026\u8fd9\u4f1a\u513f\u2026\u2026\u201d\u8bf4\u7740\u7259\u9f7f\u54ac\u5f97\u683c\u683c\u76f4\u54cd\u3002\u4e07\u9707\u5c71\u9053\uff1a\u201c\u90a3\u4e48\u54b1\u4eec\u5373\u523b\u4fbf\u53bb\u3002\u4f60\u62ff\u597d\u4e86\u5251\uff0c\u53ef\u5148\u522b\u51fa\u624b\uff0c\u7b49\u6211\u65a9\u65ad\u4ed6\u4e8c\u4eba\u7684\u624b\u8db3\uff0c\u518d\u7531\u4f60\u4eb2\u624b\u53d6\u8fd9\u53cc\u72d7\u7537\u5973\u7684\u6027\u547d\u3002\u201d \u3000\u3000\u53ea\u89c1\u623f\u95e8\u63a8\u5f00\uff0c\u4e07\u9707\u5c71\u5de6\u624b\u6258\u5728\u4e07\u572d\u814b\u4e0b\uff0c\u4e8c\u4eba\u5f84\u5954\u540e\u56ed\u3002 \u3000\u3000\u621a\u82b3\u9760\u5728\u5899\u4e0a\uff0c\u773c\u6cea\u6251\u7c0c\u7c0c\u7684\u4ece\u8863\u895f\u4e0a\u6eda\u4e0b\u6765\u3002\u5979\u53ea\u76fc\u6cbb\u597d\u4e08\u592b\u7684\u4f24\uff0c\u4ed6\u5374\u5bf9\u81ea\u5df1\u5982\u6b64\u8d77\u7591\u3002\u7236\u4eb2\u4e00\u53bb\u4e0d\u8fd4\uff0c\u72c4\u5e08\u54e5\u53d7\u4e86\u81ea\u5df1\u7684\u51a4\u6789\uff0c\u73b0\u4eca\u2026\u2026\u73b0\u4eca\u4e08\u592b\u53c8\u8fd9\u822c\u5bf9\u5f85\u81ea\u5df1\uff0c\u8fd9\u6837\u7684\u65e5\u5b50\uff0c\u600e\u4e48\u8fd8\u8fc7\u5f97\u4e0b\u53bb\uff1f\u5979\u5fc3\u4e2d\u832b\u7136\u4e00\u7247\uff0c\u76f4\u662f\u4e0d\u60f3\u6d3b\u4e86\uff0c\u6ca1\u60f3\u5230\u53bb\u548c\u4e08\u592b\u7406\u8bba\uff0c\u6ca1\u60f3\u5230\u53eb\u5434\u574e\u6765\u5bf9\u8d28\uff0c\u53ea\u662f\u5168\u8eab\u762b\u75ea\u4e86\u4e00\u822c\uff0c\u9760\u5728\u5899\u4e0a\u3002 \u3000\u3000\u8fc7\u4e86\u4e0d\u4e45\uff0c\u53ea\u542c\u5f97\u811a\u6b65\u58f0\u54cd\uff0c\u4e07\u6c0f\u7236\u5b50\u56de\u5230\u5385\u4e0a\uff0c\u7ad9\u5b9a\u4e86\u4f4e\u58f0\u5546\u91cf\u3002\u4e07\u572d\u9053\uff1a\u201c\u7239\uff0c\u600e\u4e0d\u5c31\u5728\u67f4\u623f\u91cc\u6740\u4e86\u5434\u574e\uff1f\u201d\u4e07\u9707\u5c71\u9053\uff1a\u201c\u67f4\u623f\u91cc\u53ea\u5978\u592b\u4e00\u4eba\u3002\u90a3\u8d3c\u6deb\u5987\u5b9a\u662f\u5f97\u5230\u98ce\u58f0\uff0c\u5148\u6e9c\u8d70\u4e86\u3002\u65e2\u4e0d\u80fd\u6349\u5978\u6349\u53cc\uff0c\u54b1\u4eec\u662f\u8346\u5dde\u57ce\u4e2d\u7684\u5927\u6237\u4eba\u5bb6\uff0c\u600e\u80fd\u8f7b\u6613\u6740\u4eba\uff1f\u5f97\u4e86\u8fd9\u5251\u8c31\u4e4b\u540e\uff0c\u54b1\u4eec\u5728\u8346\u5dde\u6709\u8bb8\u8bb8\u591a\u591a\u7684\u4e8b\u60c5\u8981\u5e72\uff0c\u5c0f\u4e0d\u5fcd\u5219\u4e71\u5927\u8c0b\uff0c\u53ef\u4e0d\u80fd\u80e1\u6765\uff01\u201d\u4e07\u572d\u9053\uff1a\u201c\u96be\u9053\u5c31\u8fd9\u6837\u7f62\u4e86\u4e0d\u6210\uff1f\u5b69\u513f\u8fd9\u53e3\u6c14\u5982\u4f55\u80fd\u6d88\uff1f\u201d\u4e07\u9707\u5c71\u9053\uff1a\u201c\u8981\u51fa\u6c14\u8fd8\u4e0d\u5bb9\u6613\uff1f\u54b1\u4eec\u7528\u8001\u6cd5\u5b50\uff01\u201d\u4e07\u572d\u9053\uff1a\u201c\u8001\u6cd5\u5b50\uff1f\u201d \u3000\u3000\u4e07\u9707\u5c71\u9053\uff1a\u201c\u5bf9\u4ed8\u621a\u957f\u53d1\u7684\u8001\u6cd5\u5b50\uff01\u201d\u4ed6\u987f\u4e86\u4e00\u987f\uff0c\u9053\uff1a\u201c\u4f60\u5148\u56de\u623f\u53bb\uff0c\u6211\u547d\u4eba\u4f20\u96c6\u4f17\u5f1f\u5b50\uff0c\u4f60\u518d\u548c\u5927\u4f19\u513f\u4e00\u8d77\u5230\u6211\u623f\u5916\u6765\u3002\u522b\u60f9\u4eba\u7591\u5fc3\u3002\u201d \u3000\u3000\u621a\u82b3\u5fc3\u4e2d\u672c\u662f\u4e71\u7cdf\u7cdf\u5730\u6ca1\u534a\u70b9\u4e3b\u610f\uff0c\u53ea\u662f\u60f3\uff1a\u201c\u5230\u4e86\u8fd9\u6b65\u7530\u5730\uff0c\u6211\u662f\u4e0d\u60f3\u6d3b\u4e86\uff0c\u53ef\u662f\u7a7a\u5fc3\u83dc\u600e\u4e48\u529e\uff1f\u8c01\u6765\u7167\u987e\u5979\uff1f\u201d\u5ffd\u542c\u5f97\u4e07\u9707\u5c71\u8bf4\u8981\u7528\u201c\u5bf9\u4ed8\u621a\u957f\u53d1\u7684\u8001\u6cd5\u5b50\u201d\u5bf9\u4ed8\u5434\u574e\uff0c\u8111\u888b\u4e0a\u4fbf\u5982\u653e\u4e0a\u4e86\u4e00\u5757\u51b0\u5757\uff0c\u7acb\u523b\u4fbf\u6e05\u9192\u4e86\uff1a\u201c\u4ed6\u4eec\u600e\u6837\u5bf9\u4ed8\u6211\u7239\u7239\u4e86\uff1f\u975e\u67e5\u4e2a\u6c34\u843d\u77f3\u51fa\u4e0d\u53ef\u3002\u516c\u516c\u4f20\u4f17\u5f1f\u5b50\u5230\u623f\u5916\u8fb9\u6765\uff0c\u8fd9\u91cc\u662f\u4e0d\u80fd\u803d\u4e86\uff0c\u5374\u8eb2\u5230\u54ea\u91cc\u53bb\u5077\u542c\uff1f\u201d \u3000\u3000\u53ea\u542c\u5f97\u4e07\u572d\u7b54\u5e94\u7740\u53bb\u4e86\u3002\u4e07\u9707\u5c71\u8d70\u5230\u5385\u5916\u5927\u58f0\u547c\u53eb\u4ec6\u4eba\u638c\u706f\u3002\u4e0d\u591a\u65f6\u524d\u5385\u540e\u5385\u9690\u9690\u4f20\u6765\u4eba\u58f0\uff0c\u4f17\u5f1f\u5b50\u548c\u4ec6\u4eba\u56db\u4e0b\u91cc\u805a\u96c6\u62e2\u6765\u3002\u621a\u82b3\u77e5\u9053\u53ea\u8981\u518d\u8fc7\u5f97\u7247\u523b\uff0c\u7acb\u65f6\u4fbf\u6709\u4eba\u8d70\u7ecf\u7a97\u5916\uff0c\u5fae\u4e00\u72b9\u8c6b\uff0c\u5f53\u5373\u95ea\u8eab\u8d70\u8fdb\u4e07\u9707\u5c71\u623f\u4e2d\uff0c\u6380\u5f00\u5e8a\u5e37\uff0c\u4fbf\u94bb\u8fdb\u4e86\u5e8a\u5e95\u3002\u5e8a\u5e37\u4f4e\u5782\u81f3\u5730\uff0c\u82e5\u4e0d\u662f\u6709\u4eba\u6545\u610f\u63ed\u5f00\uff0c\u51b3\u4e0d\u81f4\u53d1\u89c1\u5979\u7684\u8e2a\u8ff9\u3002 \u3000\u3000\u5979\u6a2a\u5367\u5e8a\u5e95\uff0c\u4e0d\u4e45\u5e8a\u5e37\u4e0b\u900f\u8fdb\u5149\u6765\uff0c\u6709\u4eba\u70b9\u4e86\u706f\uff0c\u8fdb\u6765\u653e\u5728\u623f\u4e2d\u3002\u5979\u770b\u5230\u4e07\u9707\u5c71\u4e00\u5bf9\u7a7f\u7740\u53cc\u6881\u978b\u7684\u811a\u8de8\u8fdb\u623f\u6765\uff0c\u8fd9\u53cc\u811a\u79fb\u5230\u6905\u65c1\uff0c\u6905\u5b50\u53d1\u51fa\u8f7b\u8f7b\u7684\u683c\u5587\u4e00\u58f0\uff0c\u662f\u4e07\u9707\u5c71\u5750\u4e86\u4e0b\u6765\uff0c\u53c8\u542c\u5f97\u4ed6\u53eb\u4ec6\u4eba\u5173\u4e0a\u623f\u95e8\u3002 \u3000\u3000\u53ea\u542c\u5f97\u5927\u5e08\u5144\u9c81\u5764\u5728\u623f\u5916\u8bf4\u9053\uff1a\u201c\u5e08\u7236\uff0c\u6211\u4eec\u90fd\u5230\u9f50\u4e86\uff0c\u542c\u4f60\u8001\u4eba\u5bb6\u5429\u5490\u3002\u201d\u4e07\u9707\u5c71\u9053\uff1a\u201c\u5f88\u597d\uff0c\u4f60\u5148\u8fdb\u6765\uff01\u201d\u621a\u82b3\u89c1\u5230\u623f\u95e8\u63a8\u5f00\uff0c\u9c81\u5764\u7684\u4e00\u5bf9\u811a\u8d70\u4e86\u8fdb\u6765\uff0c\u623f\u95e8\u53c8\u518d\u5173\u4e0a\u3002 \u3000\u3000\u4e07\u9707\u5c71\u9053\uff1a\u201c\u6709\u654c\u4eba\u627e\u4e0a\u54b1\u4eec\u6765\u5566\uff0c\u4f60\u77e5\u4e0d\u77e5\u9053\uff1f\u201d\u9c81\u5764\u9053\uff1a\u201c\u662f\u8c01\uff0c\u5f1f\u5b50\u4e0d\u77e5\u3002\u201d\u4e07\u9707\u5c71\u9053\uff1a\u201c\u8fd9\u4eba\u5047\u626e\u6210\u4e2a\u5356\u836f\u90ce\u4e2d\uff0c\u4eca\u65e5\u6765\u8fc7\u54b1\u4eec\u5bb6\u91cc\u3002\u201d\u621a\u82b3\u5fc3\u9053\uff1a\u201c\u96be\u9053\u4ed6\u77e5\u9053\u5356\u836f\u90ce\u4e2d\u662f\u8c01\uff0c\u90a3\u4eba\u5230\u5e95\u662f\u8c01\uff1f\u201d\u9c81\u5764\u9053\uff1a\u201c\u5f1f\u5b50\u542c\u5434\u5e08\u5f1f\u8bf4\u8d77\u8fc7\u3002\u5e08\u7236\uff0c\u8fd9\u654c\u4eba\u662f\u8c01\uff1f\u201d\u4e07\u9707\u5c71\u9053\uff1a\u201c\u8fd9\u4eba\u4e54\u88c5\u6539\u626e\u4e86\uff0c\u6211\u6ca1\u4eb2\u773c\u89c1\u5230\uff0c\u6478\u4e0d\u51c6\u4ed6\u5e95\u7ec6\u3002\u660e\u513f\u4e00\u65e9\uff0c\u4f60\u5230\u57ce\u5317\u4e00\u5e26\u53bb\u4ed4\u7ec6\u67e5\u67e5\u3002\u73b0\u4e0b\u4f60\u5148\u51fa\u53bb\uff0c\u5f85\u4f1a\u6211\u8fd8\u6709\u4e8b\u5206\u6d3e\u3002\u201d\u9c81\u5764\u7b54\u5e94\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u4e07\u9707\u5c71\u9010\u4e00\u53eb\u56db\u5f1f\u5b50\u5b59\u5747\u3001\u4e94\u5f1f\u5b50\u535c\u57a3\u8fdb\u6765\uff0c\u8bf4\u8bdd\u5927\u81f4\u76f8\u540c\uff0c\u53eb\u5b59\u5747\u5230\u57ce\u5357\u4e00\u5e26\u67e5\u5bdf\uff0c\u53eb\u535c\u57a3\u5230\u57ce\u4e1c\u4e00\u5e26\u67e5\u5bdf\u3002\u5429\u5490\u535c\u57a3\u4e4b\u65f6\uff0c\u968f\u53e3\u52a0\u4e0a\u4e00\u53e5\uff1a\u201c\u8ba9\u5434\u574e\u67e5\u8bbf\u57ce\u897f\u4e00\u5e26\uff0c\u51af\u5766\u548c\u6c88\u57ce\u7b56\u5e94\u62a5\u8baf\u3002\u4f60\u4e07\u5e08\u54e5\u4f24\u52bf\u672a\u75ca\uff0c\u4e0d\u80fd\u51fa\u53bb\u4e86\u3002\u201d\u535c\u57a3\u9053\uff1a\u201c\u662f\uff0c\u4e07\u5e08\u54e5\u8be5\u591a\u591a\u4f11\u517b\u3002\u201d\u5f00\u95e8\u51fa\u53bb\u3002 \u3000\u3000\u621a\u82b3\u77e5\u9053\u8fd9\u4e9b\u8bdd\u90fd\u662f\u6545\u610f\u8bf4\u7ed9\u5434\u574e\u542c\u7684\uff0c\u597d\u4ee4\u4ed6\u4e0d\u8d77\u7591\u5fc3\u3002\u53ea\u542c\u5f97\u4e07\u9707\u5c71\u9053\uff1a\u201c\u5434\u574e\u8fdb\u6765\uff01\u201d\u8fd9\u58f0\u97f3\u548c\u53ec\u5524\u9c81\u5764\u7b49\u4eba\u4e4b\u65f6\u4e00\u6a21\u4e00\u6837\uff0c\u65e2\u4e0d\u66f4\u4e3a\u4e25\u5389\uff0c\u4e5f\u4e0d\u7279\u522b\u6e29\u548c\u3002 \u3000\u3000\u621a\u82b3\u89c1\u623f\u95e8\u53c8\u6253\u5f00\u4e86\uff0c\u5434\u574e\u7684\u53f3\u811a\u8de8\u8fdb\u884c\u69db\u4e4b\u65f6\uff0c\u6709\u4e9b\u8fdf\u7591\uff0c\u4f46\u7ec8\u4e8e\u8d70\u4e86\u8fdb\u6765\u3002\u8fd9\u53cc\u811a\u5411\u7740\u4e07\u9707\u5c71\u79fb\u4e86\u51e0\u6b65\uff0c\u7ad9\u4f4f\u4e86\uff0c\u621a\u82b3\u89c1\u4ed6\u7684\u957f\u888d\u4e0b\u6446\u5fae\u52a8\uff0c\u77e5\u4ed6\u5fc3\u4e2d\u5bb3\u6015\uff0c\u6b63\u5728\u53d1\u6296\u3002 \u3000\u3000\u53ea\u542c\u4e07\u9707\u5c71\u9053\uff1a\u201c\u6709\u654c\u4eba\u627e\u4e0a\u54b1\u4eec\u6765\u5566\uff0c\u4f60\u77e5\u4e0d\u77e5\u9053\uff1f\u201d\u5434\u574e\u9053\uff1a\u201c\u5f1f\u5b50\u5728\u95e8\u5916\u542c\u5f97\u5e08\u7236\u8bf4\u3002\u4fbf\u662f\u90a3\u4e2a\u5356\u836f\u90ce\u4e2d\u3002\u8fd9\u4eba\u662f\u5f1f\u5b50\u53eb\u4ed6\u6765\u7ed9\u4e07\u5e08\u54e5\u770b\u75c5\u7684\uff0c\u771f\u6ca1\u60f3\u5230\u4f1a\u662f\u654c\u4eba\uff0c\u8bf7\u5e08\u7236\u539f\u8c05\u3002\u201d\u4e07\u9707\u5c71\u9053\uff1a\u201c\u8fd9\u4eba\u662f\u4e54\u88c5\u6539\u626e\u4e86\u7684\uff0c\u4f60\u770b\u4ed6\u4e0d\u51fa\uff0c\u4e5f\u602a\u4e0d\u5f97\u4f60\u3002\u660e\u5929\u4e00\u65e9\uff0c\u4f60\u5230\u57ce\u897f\u4e00\u5e26\u53bb\u67e5\u67e5\uff0c\u8981\u662f\u89c1\u5230\u4e86\u4ed6\uff0c\u52a1\u987b\u7559\u795e\u4ed6\u7684\u52a8\u9759\u3002\u201d\u5434\u574e\u9053\uff1a\u201c\u662f\uff01\u201d \u3000\u3000\u7a81\u7136\u4e4b\u95f4\uff0c\u4e07\u9707\u5c71\u53cc\u811a\u4e00\u52a8\uff0c\u7ad9\u4e86\u8d77\u6765\uff0c\u621a\u82b3\u5fcd\u4e0d\u4f4f\u4f38\u624b\u63ed\u5f00\u5e8a\u5e37\u4e00\u89d2\uff0c\u5411\u5916\u5f20\u53bb\uff0c\u4e00\u770b\u4e4b\u4e0b\uff0c\u4e0d\u7531\u5f97\u5927\u60ca\u5931\u8272\uff0c\u9669\u4e9b\u5931\u58f0\u53eb\u4e86\u8d77\u6765\u3002 \u3000\u3000\u53ea\u89c1\u4e07\u9707\u5c71\u53cc\u624b\u5df2\u627c\u4f4f\u4e86\u5434\u574e\u54bd\u5589\uff0c\u5434\u574e\u4f38\u624b\u4f7f\u52b2\u53bb\u627c\u4e07\u9707\u5c71\u7684\u4e24\u624b\uff0c\u5374\u6beb\u65e0\u6548\u7528\u3002\u4f46\u89c1\u5434\u574e\u7684\u4e00\u5bf9\u773c\u775b\u5411\u5916\u51f8\u51fa\uff0c\u50cf\u91d1\u9c7c\u4e00\u822c\uff0c\u8d8a\u7741\u8d8a\u5927\u3002\u4e07\u9707\u5c71\u53cc\u624b\u624b\u80cc\u4e0a\u88ab\u5434\u574e\u7684\u6307\u7532\u6293\u51fa\u4e00\u9053\u9053\u8840\u75d5\uff0c\u4f46\u4ed6\u627c\u4f4f\u4e86\u5434\u574e\u7684\u54bd\u5589\uff0c\u8bf4\u751a\u4e48\u4e5f\u4e0d\u653e\u624b\u3002\u5434\u574e\u53d1\u4e0d\u51fa\u534a\u70b9\u58f0\u97f3\uff0c\u53ea\u662f\u8eab\u5b50\u626d\u52a8\uff0c\u8fc7\u4e86\u4e00\u4f1a\uff0c\u53cc\u624b\u6162\u6162\u5f20\u5f00\uff0c\u5782\u4e86\u4e0b\u6765\u3002\u621a\u82b3\u89c1\u4ed6\u820c\u5934\u4f38\u4e86\u51fa\u6765\uff0c\u795e\u60c5\u53ef\u6016\uff0c\u4e0d\u7981\u5bb3\u6015\u4e4b\u6781\u3002\u53ea\u89c1\u5434\u574e\u7ec8\u4e8e\u4e0d\u518d\u52a8\u5f39\uff0c\u4e07\u9707\u5c71\u677e\u5f00\u4e86\u624b\uff0c\u5c06\u4ed6\u653e\u5728\u6905\u4e0a\uff0c\u5728\u684c\u4e0a\u62ff\u8d77\u4e24\u5f20\u4e8b\u5148\u6d78\u6e7f\u4e86\u7684\u68c9\u7eb8\uff0c\u8d34\u5728\u4ed6\u53e3\u9f3b\u4e4b\u4e0a\u3002\u8fd9\u4e48\u4e00\u6765\uff0c\u4ed6\u518d\u4e5f\u4e0d\u80fd\u547c\u5438\uff0c\u4e5f\u5c31\u4e0d\u80fd\u9192\u8f6c\u3002 \u3000\u3000\u621a\u82b3\u4e00\u9897\u5fc3\u6026\u6026\u4e71\u8df3\uff0c\u5bfb\u601d\uff1a\u201c\u516c\u516c\u8bf4\u8fc7\uff0c\u4ed6\u4eec\u662f\u8346\u5dde\u4e16\u5bb6\uff0c\u4e0d\u80fd\u968f\u4fbf\u6740\u4eba\uff0c\u5434\u574e\u7684\u7236\u4eb2\u542c\u8bf4\u662f\u672c\u5730\u7ec5\u58eb\uff0c\u51b3\u4e0d\u80fd\u5c31\u6b64\u7f62\u4f11\uff0c\u8fd9\u4ef6\u4e8b\u53ef\u95f9\u5927\u4e86\u3002\u201d \u3000\u3000\u4fbf\u5728\u8fd9\u65f6\uff0c\u5ffd\u542c\u5f97\u4e07\u9707\u5c71\u5927\u58f0\u559d\u9053\uff1a\u201c\u4f60\u505a\u7684\u4e8b\uff0c\u5feb\u5feb\u81ea\u5df1\u62db\u8ba4\u4e86\u7f62\uff0c\u96be\u9053\u8fd8\u8981\u6211\u52a8\u624b\u4e0d\u6210\uff1f\u201d\u621a\u82b3\u4e00\u60ca\uff1a\u201c\u539f\u6765\u516c\u516c\u77a7\u89c1\u4e86\u6211\u3002\u201d\u53ef\u662f\u5fc3\u4e2d\u5374\u4e5f\u5e76\u4e0d\u60ca\u60f6\uff0c\u53cd\u800c\u6709\u91ca\u7136\u4e4b\u611f\uff1a\u201c\u6b7b\u5728\u4ed6\u624b\u91cc\u4e5f\u597d\uff0c\u53cd\u6b63\u6211\u662f\u4e0d\u60f3\u6d3b\u4e86\uff01\u201d \u3000\u3000\u6b63\u8981\u4ece\u5e8a\u5e95\u94bb\u51fa\u6765\uff0c\u5ffd\u542c\u5f97\u5434\u574e\u8bf4\u9053\uff1a\u201c\u5e08\u7236\uff0c\u4f60\u2026\u2026\u8981\u5f1f\u5b50\u62db\u8ba4\u751a\u4e48\uff1f\u201d \u3000\u3000\u621a\u82b3\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u600e\u4e48\u5434\u574e\u8bf4\u8d77\u8bdd\u6765\uff0c\u96be\u9053\u4ed6\u6b7b\u800c\u590d\u751f\u4e86\uff1f\u7136\u800c\u660e\u660e\u4e0d\u662f\uff0c\u4ed6\u659c\u501a\u5728\u6905\u4e0a\uff0c\u52a8\u4e5f\u4e0d\u52a8\u3002\u4ece\u5e8a\u5e95\u671b\u4e0a\u53bb\uff0c\u770b\u5230\u4e07\u9707\u5c71\u7684\u5634\u5507\u5728\u52a8\u3002\u201c\u751a\u4e48\uff1f\u662f\u516c\u516c\u5728\u8bf4\u8bdd\uff0c\u4e0d\u662f\u5434\u574e\u8bf4\u7684\u3002\u600e\u4e48\u660e\u660e\u662f\u5434\u574e\u7684\u58f0\u97f3\uff1f\u201d\u53ea\u542c\u5f97\u4e07\u9707\u5c71\u53c8\u5927\u58f0\u9053\uff1a\u201c\u62db\u8ba4\u751a\u4e48\uff1f\u54fc\uff0c\u5434\u574e\uff0c\u4f60\u597d\u5927\u80c6\u5b50\uff0c\u4f60\u91cc\u5e94\u5916\u5408\uff0c\u52fe\u7ed3\u532a\u4eba\uff0c\u60f3\u5728\u8346\u5dde\u57ce\u91cc\u505a\u4e00\u4ef6\u5927\u6848\u5b50\u3002\u201d \u3000\u3000\u201c\u5e08\u7236\uff0c\u5f1f\u5b50\u505a\u2026\u2026\u505a\u751a\u4e48\u6848\u5b50\uff1f\u201d \u3000\u3000\u8fd9\u4e00\u6b21\u621a\u82b3\u770b\u5f97\u6e05\u6e05\u695a\u695a\u4e86\uff0c\u786e\u662f\u4e07\u9707\u5c71\u5728\u5b66\u7740\u5434\u574e\u7684\u58f0\u97f3\uff0c\u96be\u4e3a\u4ed6\u5b66\u5f97\u8fd9\u4e48\u50cf\u3002\u201d\u516c\u516c\u5c45\u7136\u6709\u8fd9\u95e8\u5b66\u4eba\u8bf4\u8bdd\u7684\u672c\u9886\uff0c\u6211\u53ef\u4ece\u6765\u4e0d\u77e5\u9053\uff0c\u4ed6\u8fd9\u4e48\u5927\u58f0\u5b66\u5434\u574e\u7684\u58f0\u97f3\u8bf4\u8bdd\uff0c\u6709\u751a\u4e48\u7528\u610f\uff1f\u201d\u5979\u9690\u9690\u60f3\u5230\u4e86\u4e00\u4ef6\u4e8b\uff0c\u4f46\u90a3\u53ea\u662f\u6726\u6726\u80e7\u80e7\u7684\u4e00\u56e2\u5f71\u5b50\uff0c\u4e00\u70b9\u4e5f\u60f3\u4e0d\u660e\u767d\uff0c\u53ea\u662f\u5185\u5fc3\u611f\u5230\u4e86\u83ab\u540d\u5176\u5999\u7684\u6050\u60e7\u3002 \u3000\u3000\u53ea\u542c\u5f97\u4e07\u9707\u5c71\u9053\uff1a\u201c\u54fc\uff0c\u4f60\u5f53\u6211\u4e0d\u77e5\u9053\u4e48\uff1f\u4f60\u5e26\u4e86\u90a3\u5356\u836f\u90ce\u4e2d\u6765\u5230\u8346\u5dde\u57ce\uff0c\u8fd9\u4eba\u5176\u5b9e\u662f\u4e2a\u6c5f\u6d0b\u5927\u76d7\uff0c\u5434\u574e\uff0c\u4f60\u548c\u4ed6\u52fe\u7ed3\uff0c\u60f3\u8981\u95ef\u8fdb\u2026\u2026\u201d \u3000\u3000\u201c\u5e08\u7236\u2026\u2026\u95ef\u8fdb\u751a\u4e48\uff1f\u201d \u3000\u3000\u201c\u8981\u95ef\u8fdb\u51cc\u77e5\u5e9c\u516c\u9986\uff0c\u53bb\u76d7\u4e00\u4efd\u673a\u5bc6\u516c\u6587\uff0c\u662f\u4e0d\u662f\uff1f\u5434\u574e\uff0c\u4f60\u2026\u2026\u4f60\u8fd8\u60f3\u62b5\u8d56\uff1f\u201d \u3000\u3000\u201c\u5e08\u7236\uff0c\u4f60\u2026\u2026\u4f60\u600e\u4e48\u77e5\u9053\uff1f\u5e08\u7236\uff0c\u8bf7\u4f60\u8001\u4eba\u5bb6\u77a7\u5728\u5f1f\u5b50\u5e73\u65e5\u5bf9\u4f60\u5b5d\u987a\u7684\u4efd\u4e0a\uff0c\u539f\u8c05\u6211\u8fd9\u4e00\u906d\uff0c\u5f1f\u5b50\u518d\u4e5f\u4e0d\u6562\u4e86\uff01\u201d \u3000\u3000\u201c\u8fd9\u6837\u4e00\u4ef6\u5927\u4e8b\uff0c\u54ea\u80fd\u5c31\u8fd9\u4e48\u7b97\u4e86\uff1f\u201d \u3000\u3000\u621a\u82b3\u53d1\u89c9\u4e86\uff0c\u4e07\u9707\u5c71\u5b66\u5434\u574e\u7684\u53e3\u97f3\uff0c\u5176\u5b9e\u5e76\u4e0d\u5f88\u50cf\uff0c\u53ea\u662f\u538b\u4f4e\u4e86\u55d3\u95e8\uff0c\u8bf4\u5f97\u5341\u5206\u542b\u7cca\uff0c\u6bcf\u4e00\u53e5\u8bdd\u4e2d\u603b\u662f\u5e26\u4e0a\u201c\u5e08\u7236\u201d\u7684\u79f0\u547c\uff0c\u540c\u65f6\u4e0d\u65ad\u81ea\u79f0\u201c\u5f1f\u5b50\u201d\uff0c\u5728\u65c1\u4eba\u542c\u6765\uff0c\u81ea\u7136\u4f1a\u5f53\u662f\u5434\u574e\u5728\u8bf4\u8bdd\u3002\u4f55\u51b5\uff0c\u5927\u5bb6\u773c\u89c1\u5434\u574e\u8d70\u8fdb\u623f\u6765\uff0c\u542c\u5230\u4ed6\u548c\u4e07\u9707\u5c71\u8bf4\u8bdd\uff0c\u63a5\u7740\u518d\u8bf4\u4e4b\u65f6\uff0c\u58f0\u97f3\u867d\u7136\u4e0d\u50cf\uff0c\u4f46\u9664\u4e86\u5434\u574e\u4e4b\u5916\uff0c\u53c8\u600e\u4f1a\u53e6\u6709\u522b\u4eba\uff1f\u800c\u4e14\u4e07\u9707\u5c71\u7684\u8bdd\u4e2d\uff0c\u53c8\u65f6\u65f6\u53eb\u4ed6\u201c\u5434\u574e\u201d\u3002 \u3000\u3000\u53ea\u89c1\u4e07\u9707\u5c71\u8f7b\u8f7b\u6258\u8d77\u5434\u574e\u7684\u5c38\u4f53\uff0c\u6162\u6162\u5f2f\u4e0b\u8170\u6765\uff0c\u5de6\u624b\u6380\u5f00\u4e86\u5e8a\u5e54\u3002\u621a\u82b3\u5413\u5f97\u4e00\u9897\u5fc3\u51e0\u4e4e\u505c\u6b62\u4e86\u8df3\u52a8\uff1a\u201c\u516c\u516c\u5b9a\u7136\u53d1\u89c1\u4e86\u6211\uff0c\u8fd9\u4e00\u4e0b\u4ed6\u975e\u627c\u6b7b\u6211\u4e0d\u53ef\u4e86\uff01\u201d\u706f\u5149\u6726\u80e7\u4e4b\u4e0b\uff0c\u53ea\u89c1\u4e00\u4e2a\u8111\u888b\u4ece\u5e8a\u5e95\u4e0b\u94bb\u4e86\u8fdb\u6765\uff0c\u90a3\u662f\u5434\u574e\u7684\u8111\u888b\uff0c\u773c\u775b\u7741\u5f97\u5927\u5927\u5730\uff0c\u771f\u50cf\u662f\u6b7b\u91d1\u9c7c\u7684\u5934\u3002\u621a\u82b3\u53ea\u6709\u62da\u547d\u5411\u65c1\u907f\u8ba9\uff0c\u4f46\u5434\u574e\u7684\u5c38\u8eab\u4e0d\u4f4f\u6324\u8fdb\u6765\uff0c\u78b0\u5230\u4e86\u5979\u7684\u817f\uff0c\u53c8\u78b0\u5230\u4e86\u5979\u7684\u8170\u3002 \u3000\u3000\u53ea\u542c\u4e07\u9707\u5c71\u5750\u56de\u6905\u4e0a\uff0c\u5389\u58f0\u559d\u9053\uff1a\u201c\u5434\u574e\uff0c\u4f60\u8fd8\u4e0d\u8dea\u4e0b\uff1f \u3000\u3000\u6211\u7ed1\u4e86\u4f60\u53bb\u89c1\u51cc\u77e5\u5e9c\u3002\u9976\u4e0e\u4e0d\u9976\uff0c\u662f\u4ed6\u7684\u4e8b\uff0c\u6211\u53ef\u505a\u4e0d\u4e86\u4e3b\u3002\u201d \u3000\u3000\u201c\u5e08\u7236\uff0c\u4f60\u5f53\u771f\u4e0d\u80fd\u9976\u6055\u5f1f\u5b50\u4e48\uff1f\u201d \u3000\u3000\u201c\u8c03\u6559\u51fa\u8fd9\u6837\u7684\u5f1f\u5b50\u6765\uff0c\u4e07\u5bb6\u7684\u989c\u9762\u4e5f\u7ed9\u4f60\u4e22\u5149\u4e86\uff0c\u6211\u2026\u2026\u6211\u8fd8\u80fd\u9976\u4f60\uff1f\u201d \u3000\u3000\u621a\u82b3\u4ece\u5e8a\u5e37\u7f1d\u4e2d\u5f20\u671b\uff0c\u89c1\u4e07\u9707\u5c71\u4ece\u8170\u95f4\u62d4\u51fa\u4e00\u67c4\u5315\u9996\u6765\uff0c\u8f7b\u8f7b\u63d2\u5165\u4e86\u81ea\u5df1\u80f8\u819b\u3002\u4ed6\u80f8\u53e3\u8863\u5185\u663e\u7136\u57ab\u7740\u8f6f\u6728\u3001\u6e7f\u6ce5\u3001\u9762\u997c\u4e4b\u7c7b\u7684\u4e1c\u897f\uff0c\u5315\u9996\u63d2\u4e86\u8fdb\u53bb\uff0c\u4fbf\u5373\u7559\u7740\u4e0d\u52a8\u3002 \u3000\u3000\u621a\u82b3\u5fc3\u4e2d\u521a\u6709\u4e9b\u660e\u767d\uff0c\u4fbf\u542c\u5f97\u4e07\u9707\u5c71\u5927\u58f0\u9053\uff1a\u201c\u5434\u574e\uff0c\u4f60\u8fd8\u4e0d\u8dea\u4e0b\uff01\u201d\u8ddf\u7740\u538b\u4f4e\u55d3\u5b50\u5b66\u7740\u5434\u574e\u7684\u58f0\u97f3\u9053\uff1a\u201c\u5e08\u7236\uff0c\u8fd9\u662f\u4f60\u903c\u6211\u7684\uff0c\u987b\u602a\u4e0d\u5f97\u5f1f\u5b50\uff01\u201d\u4e07\u9707\u5c71\u5927\u53eb\u4e00\u58f0\uff1a\u201c\u54ce\u54df\uff01\u201d\u98de\u8d77\u4e00\u817f\uff0c\u8e22\u5f00\u4e86\u7a97\u5b50\uff0c\u53eb\u9053\uff1a\u201c\u5c0f\u8d3c\uff0c\u4f60\u2026\u2026\u4f60\u7adf\u6562\u884c\u51f6\uff01\u201d \u3000\u3000\u53ea\u542c\u5f97\u7830\u7684\u4e00\u58f0\u54cd\uff0c\u6709\u4eba\u8e22\u5f00\u623f\u95e8\uff0c\u4e07\u572d\u5f53\u5148\u62a2\u8fdb\uff08\u4ed6\u77e5\u9053\u8be5\u5f53\u8fd9\u65f6\u5019\u7834\u95e8\u800c\u5165\uff09\uff0c\u9c81\u5764\u3001\u5b59\u5747\u3001\u535c\u57a3\u7b49\u4f17\u5f1f\u5b50\u8ddf\u7740\u8fdb\u6765\u3002\u4e07\u9707\u5c71\u6309\u4f4f\u80f8\u53e3\uff0c\u624b\u6307\u95f4\u9c9c\u8840\u6d94\u6d94\u6d41\u4e0b\uff08\u591a\u534a\u624b\u4e2d\u62ff\u7740\u4e00\u5c0f\u74f6\u7ea2\u6c34\uff09\uff0c\u4ed6\u6447\u6447\u6643\u6643\uff0c\u6307\u7740\u7a97\u53e3\uff0c\u53eb\u9053\uff1a\u201c\u5434\u574e\u8fd9\u8d3c\u2026\u2026\u523a\u4e86\u6211\u4e00\u5200\uff0c\u9003\u8d70\u4e86\uff01\u5feb\u2026\u2026\u5feb\u8ffd\uff01\u201d\u8bf4\u4e86\u8fd9\u51e0\u53e5\uff0c\u8eab\u5b50\u4e00\u659c\uff0c\u5012\u5728\u5e8a\u4e0a\u3002 \u3000\u3000\u4e07\u572d\u60ca\u53eb\uff1a\u201c\u7239\u7239\uff0c\u7239\u7239\uff0c\u4f60\u4f24\u5f97\u600e\u6837\uff1f\u201d \u3000\u3000\u9c81\u5764\u3001\u5b59\u5747\u3001\u535c\u57a3\u3001\u51af\u5766\u3001\u6c88\u57ce\u4e94\u4eba\u5148\u540e\u8dc3\u51fa\u7a97\u5b50\uff0c\u5927\u547c\u5c0f\u53eb\u7684\u8ffd\u4e86\u51fa\u53bb\u3002\u5e9c\u4e2d\u524d\u524d\u540e\u540e\uff0c\u8bb8\u591a\u4eba\u90fd\u60ca\u547c\u53eb\u56b7\u8d77\u6765\u3002 \u3000\u3000\u621a\u82b3\u4f0f\u5728\u5e8a\u5e95\uff0c\u53ea\u89c9\u5f97\u5434\u574e\u7684\u5c38\u8eab\u8d8a\u6765\u8d8a\u51b7\u3002\u5979\u5fc3\u4e2d\u5bb3\u6015\u4e4b\u6781\uff0c\u53ef\u662f\u4e00\u52a8\u4e5f\u4e0d\u6562\u52a8\u3002\u516c\u516c\u8eba\u5728\u5e8a\u4e0a\uff0c\u4e08\u592b\u7ad9\u5728\u5e8a\u524d\u3002 \u3000\u3000\u53ea\u542c\u5f97\u4e07\u9707\u5c71\u4f4e\u58f0\u9053\uff1a\u201c\u6709\u4eba\u8d77\u7591\u6ca1\u6709\uff1f\u201d\u4e07\u572d\u9053\uff1a\u201c\u6ca1\u6709\uff0c\u7239\uff0c\u4f60\u88c5\u5f97\u771f\u50cf\u3002\u4fbf\u5982\u6740\u621a\u957f\u53d1\u90a3\u6837\uff0c\u6ca1\u534a\u70b9\u7834\u7efd\u3002\u201d \u3000\u3000\u201c\u4fbf\u5982\u6740\u621a\u957f\u53d1\u90a3\u6837\uff0c\u6ca1\u534a\u70b9\u7834\u7efd\uff01\u201d\u8fd9\u4e00\u53e5\u8bdd\u50cf\u4e00\u628a\u950b\u5229\u7684\u5315\u9996\uff0c\u523a\u5165\u4e86\u621a\u82b3\u5fc3\u4e2d\u3002\u5979\u672c\u5df2\u9690\u9690\u7ea6\u7ea6\u60f3\u5230\u4e86\u8fd9\u4ef6\u5927\u6050\u6016\u4e8b\uff0c\u4f46\u5979\u51b3\u8ba1\u4e0d\u6562\u76f8\u4fe1\u3002\u201d\u516c\u516c\u4e00\u76f4\u5bf9\u6211\u548c\u989c\u60a6\u8272\uff0c\u4e08\u592b\u5411\u6765\u6e29\u67d4\u4f53\u8d34\uff0c\u600e\u4e48\u4f1a\u6740\u5bb3\u4e86\u6211\u7239\u7239\uff1f\u201d\u4f46\u8fd9\u4e00\u6b21\u5979\u662f\u4eb2\u773c\u770b\u89c1\u4e86\uff0c\u4ed6\u4eec\u5e03\u7f6e\u4e86\u8fd9\u6837\u4e00\u4e2a\u5de7\u5999\u673a\u5173\uff0c\u6740\u4e86\u5434\u574e\uff0c\u90a3\u65e5\u5979\u5728\u4e66\u623f\u5916\u542c\u5230\u201c\u7236\u4eb2\u548c\u4e07\u9707\u5c71\u4e89\u5435\u201d\uff0c\u89c1\u5230\u201c\u4e07\u9707\u5c71\u88ab\u7236\u4eb2\u523a\u4e86\u4e00\u5200\u201d\uff0c\u89c1\u5230\u201c\u7236\u4eb2\u8d8a\u7a97\u9003\u8d70\u201d\uff0c\u663e\u7136\uff0c\u90a3\u4e5f\u662f\u4e07\u9707\u5c71\u5e03\u7f6e\u7684\u673a\u5173\uff0c\u4e00\u6a21\u4e00\u6837\u3002\u5728\u90a3\u65f6\u5019\uff0c\u7236\u4eb2\u65e9\u5df2\u88ab\u4ed6\u5bb3\u6b7b\u4e86\uff0c\u4ed6\u2026\u2026 \u3000\u3000\u4ed6\u5b66\u7740\u7236\u4eb2\u7684\u53e3\u97f3\uff0c\u602a\u4e0d\u5f97\u7236\u4eb2\u5f53\u65f6\u7684\u8bdd\u58f0\u5636\u54d1\uff0c\u548c\u5e73\u65f6\u5927\u5f02\u3002\u5982\u679c\u4e0d\u662f\u9634\u5dee\u9633\u9519\uff0c\u8fd9\u4e00\u6b21\u5979\u4f0f\u5728\u5e8a\u5e95\uff0c\u4eb2\u773c\u89c1\u5230\u4e86\u8fd9\u573a\u60e8\u5267\uff0c\u5374\u5982\u4f55\u80fd\u731c\u60f3\u5f97\u900f\uff1f \u3000\u3000\u53ea\u542c\u5f97\u4e07\u572d\u9053\uff1a\u201c\u90a3\u8d31\u4eba\u600e\u6837\uff1f\u54b1\u4eec\u600e\u80fd\u653e\u8fc7\u4e86\u5979\uff1f\u201d\u4e07\u9707\u5c71\u9053\uff1a\u201c\u6162\u6162\u518d\u627e\u5230\u5979\u6765\u70ae\u5236\u4fbf\u662f\u3002\u8fd9\u53ef\u8981\u505a\u5f97\u4eba\u4e0d\u77e5\u3001\u9b3c\u4e0d\u89c9\uff0c\u522b\u8d25\u574f\u4e86\u4e07\u5bb6\u95e8\u98ce\uff0c\u574f\u4e86\u6211\u7236\u5b50\u7684\u540d\u58f0\u3002\u201d\u4e07\u572d\u9053\uff1a\u201c\u662f\uff0c\u7239\u7239\u60f3\u5f97\u771f\u5468\u5230\u3002\u54ce\u54df\u2026\u2026\u201d\u4e07\u9707\u5c71\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u4e07\u572d\u9053\uff1a\u201c\u513f\u5b50\u624b\u80cc\u4e0a\u7684\u4f24\u5904\u53c8\u75db\u4e86\u8d77\u6765\u3002\u201d\u4e07\u9707\u5c71\u201c\u55ef\u201d\u4e86\u4e00\u58f0\uff0c\u4ed6\u867d\u8ba1\u8c0b\u591a\u7aef\uff0c\u5bf9\u8fd9\u4ef6\u4e8b\u53ef\u5f53\u771f\u675f\u624b\u65e0\u7b56\u3002 \u3000\u3000\u621a\u82b3\u6162\u6162\u4f38\u51fa\u624b\u53bb\uff0c\u6478\u5230\u5434\u574e\u6000\u4e2d\uff0c\u90a3\u53ea\u5c0f\u74f7\u74f6\u51b7\u51b7\u7684\u4fbf\u5728\u4ed6\u8863\u888b\u4e4b\u4e2d\u3002\u5979\u53d6\u4e86\u51fa\u6765\u3002\u653e\u5728\u81ea\u5df1\u888b\u91cc\uff0c\u5fc3\u4e2d\u51c4\u82e6\uff1a \u3000\u3000\u201c\u4e09\u54e5\uff0c\u4e09\u54e5\uff0c\u4f60\u53ea\u542c\u5230\u4e00\u534a\u8bf4\u8bdd\uff0c\u4fbf\u51a4\u6789\u6211\u8ddf\u8fd9\u8d3c\u5b50\u6709\u66a7\u6627\u4e4b\u4e8b\u3002\u4f60\u4e0d\u60f3\u542c\u4e2a\u660e\u767d\uff0c\u56e0\u6b64\u4e5f\u5c31\u6ca1\u542c\u5230\u3002\u8fd9\u74f6\u89e3\u836f\u4fbf\u5728\u4ed6\u8eab\u4e0a\u3002\u4f60\u7236\u4eb2\u5df2\u6740\u4e86\u4ed6\uff0c\u672c\u6765\u53ea\u4e0d\u8fc7\u4e3e\u624b\u4e4b\u52b3\uff0c\u4fbf\u53ef\u5c06\u89e3\u836f\u53d6\u5230\uff0c\u4f46\u6bd5\u7adf\u4f60\u4eec\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u9c81\u5764\u4e00\u5e72\u4eba\u8ffd\u4e0d\u5230\u5434\u574e\uff0c\u4e00\u4e2a\u4e2a\u56de\u6765\u4e86\uff0c\u4e00\u4e2a\u4e2a\u5230\u4e07\u9707\u5c71\u5e8a\u524d\u6765\u95ee\u5019\u3002\u4e07\u9707\u5c71\u8892\u9732\u4e86\u80f8\u819b\uff0c\u5e03\u5e26\u4ece\u9888\u4e2d\u7ed5\u5230\u80f8\u524d\u3002\u56f4\u5230\u80cc\u540e\uff0c\u53c8\u7ed5\u5230\u9888\u4e2d\u3002 \u3000\u3000\u8fd9\u4e00\u6b21\u4ed6\u53d7\u7684\u201c\u4f24\u201d\u6ca1\u4e0a\u6b21\u90a3\u4e48\u201c\u5389\u5bb3\u201d\uff0c\u5434\u574e\u7684\u6b66\u529f\u7a76\u7adf\u4e0d\u53ca\u5e08\u53d4\u621a\u957f\u53d1\u3002\u8fd9\u4e00\u5200\u523a\u5f97\u4e0d\u6df1\uff0c\u5e76\u65e0\u5927\u788d\u3002\u4f17\u5f1f\u5b50\u90fd\u653e\u5fc3\u4e86\uff0c\u4e2a\u4e2a\u5927\u9a82\u5434\u574e\u5fd8\u6069\u8d1f\u4e49\uff0c\u90fd\u8bf4\u660e\u5929\u975e\u53bb\u627e\u4ed6\u7236\u4eb2\u7b97\u5e10\u4e0d\u53ef\uff0c\u8bf7\u5e08\u7236\u4fdd\u91cd\uff0c\u5927\u5bb6\u9000\u4e86\u51fa\u53bb\u3002\u4e07\u572d\u5750\u5728\u5e8a\u524d\uff0c\u966a\u4f34\u7740\u7236\u4eb2\u3002 \u3000\u3000\u621a\u82b3\u53ea\u60f3\u627e\u4e2a\u673a\u4f1a\u9003\u4e86\u51fa\u53bb\uff0c\u5979\u6328\u5728\u5434\u574e\u7684\u5c38\u4f53\u4e4b\u65c1\uff0c\u5fc3\u4e2d\u8bf4\u4e0d\u51fa\u7684\u538c\u6076\uff0c\u53c8\u6015\u4e07\u6c0f\u7236\u5b50\u53d1\u89c9\uff0c\u53ea\u662f\u60f3\u4e0d\u51fa\u9003\u8d70\u7684\u6cd5\u5b50\u3002 \u3000\u3000\u4e07\u9707\u5c71\u9053\uff1a\u201c\u54b1\u4eec\u5148\u5f97\u5904\u7f6e\u4e86\u5c38\u4f53\uff0c\u522b\u9732\u51fa\u9a6c\u811a\u3002\u201d\u4e07\u572d\u9053\uff1a\u201c\u8fd8\u662f\u8ddf\u6599\u7406\u621a\u957f\u53d1\u4e00\u6837\u4e48\uff1f\u201d\u4e07\u9707\u5c71\u5fae\u4e00\u6c89\u541f\uff0c\u9053\uff1a\u201c\u8fd8\u662f\u8001\u6cd5\u5b50\u3002\u201d \u3000\u3000\u621a\u82b3\u6cea\u6c34\u6ef4\u4e86\u4e0b\u6765\uff0c\u5fc3\u9053\uff1a\u201c\u4ed6\u4eec\u600e\u6837\u5bf9\u4ed8\u6211\u7239\u7239\uff1f\u201d \u3000\u3000\u4e07\u572d\u9053\uff1a\u201c\u5c31\u780c\u5728\u8fd9\u91cc\u4e48\uff1f\u4f60\u7761\u5728\u8fd9\u91cc\uff0c\u6050\u6015\u4e0d\u5927\u597d\uff01\u201d\u4e07\u9707\u5c71\u9053\uff1a\u201c\u6211\u6682\u4e14\u642c\u53bb\u8ddf\u4f60\u4f4f\uff0c\u53ea\u6015\u8fd8\u6709\u9ebb\u70e6\u7684\u4e8b\u3002\u4eba\u5bb6\u600e\u80fd\u8f7b\u6613\u5c06\u5251\u8c31\u9001\u5230\u54b1\u4eec\u624b\u4e2d\uff1f\u54b1\u7237\u513f\u4fe9\u987b\u5f97\u5408\u529b\u5bf9\u4ed8\u3002\u5c06\u6765\u53d1\u4e86\u5927\u8d22\uff0c\u8fd8\u6015\u6ca1\u5730\u65b9\u4f4f\u4e48\uff1f\u201d\u621a\u82b3\u542c\u5230\u4e86\u8fd9\u4e00\u4e2a\u201c\u780c\u201d\u5b57\uff0c\u970e\u65f6\u4e4b\u95f4\uff0c\u4fbf\u5982\u4e00\u9053\u95ea\u7535\u5728\u8111\u4e2d\u4e00\u63a0\u800c\u8fc7\uff0c\u767b\u65f6\u660e\u767d\u4e86\uff1a\u201c\u4ed6\u2026\u2026 \u3000\u3000\u4ed6\u5c06\u6211\u7239\u7239\u7684\u5c38\u8eab\u780c\u5728\u5899\u4e2d\uff0c\u85cf\u5c38\u706d\u8ff9\uff0c\u602a\u4e0d\u5f97\u6211\u7239\u7239\u4e00\u53bb\u4e4b\u540e\uff0c\u59cb\u7ec8\u6ca1\u6709\u6d88\u606f\u3002\u602a\u4e0d\u5f97\u516c\u516c\u2026\u2026\u4e0d\uff0c\u4e0d\u662f\u516c\u516c\uff0c\u602a\u4e0d\u5f97\u4e07\u9707\u5c71\u8fd9\u5978\u8d3c\u534a\u591c\u4e09\u66f4\u8d77\u8eab\u780c\u5899\u3002\u4ed6\u505a\u4e86\u8fd9\u4ef6\u574f\u4e8b\uff0c\u5fc3\u4e2d\u4e0d\u5b89\uff0c\u5f97\u4e86\u79bb\u9b42\u75c5\uff0c\u7761\u68a6\u91cc\u4e5f\u4f1a\u8d77\u8eab\u780c\u5899\u3002\u8fd9\u5978\u8d3c\u2026\u2026\u8fd9\u5978\u8d3c\u5c45\u7136\u4f1a\u5fc3\u4e2d\u4e0d\u5b89\u2026\u2026\u90a3\u624d\u771f\u5947\u602a\u4e86\u3002\u4e0d\uff0c\u4ed6\u4e0d\u662f\u5fc3\u4e2d\u4e0d\u5b89\uff0c\u4ed6\u662f\u5341\u5206\u5f97\u610f\uff0c\u8fd9\u780c\u5899\u7684\u4e8b\uff0c\u4e0d\u77e5\u4e0d\u89c9\u7684\u8981\u505a\u4e86\u4e00\u6b21\u53c8\u4e00\u6b21\u2026\u2026\u521a\u624d\u4ed6\u68a6\u4e2d\u780c\u5899\uff0c\u4e0d\u662f\u4e00\u76f4\u5728\u5fae\u7b11\u4e48\uff1f\u201d \u3000\u3000\u53ea\u542c\u4e07\u572d\u9053\uff1a\u201c\u7239\uff0c\u5230\u5e95\u8fd9\u5251\u8c31\u6709\u751a\u4e48\u597d\u5904\uff1f\u4f60\u8bf4\u54b1\u4eec\u8981\u53d1\u5927\u8d22\uff1f\u53ef\u4ee5\u5bcc\u7532\u5929\u4e0b\uff1f\u96be\u9053\u2026\u2026\u96be\u9053\u8fd9\u4e0d\u662f\u6b66\u529f\u79d8\u8bc0\uff0c\u5374\u662f\u91d1\u94f6\u8d22\u5b9d\uff1f\u201d\u4e07\u9707\u5c71\u9053\uff1a\u201c\u5f53\u7136\u4e0d\u662f\u6b66\u529f\u79d8\u8bc0\uff0c\u5251\u8c31\u4e2d\u5199\u7684\uff0c\u662f\u4e00\u4e2a\u5927\u5b9d\u85cf\u7684\u6240\u5728\u3002\u6885\u5ff5\u7b19\u8001\u513f\u732a\u6cb9\u8499\u4e86\u5fc3\uff0c\u7adf\u8981\u5c06\u8fd9\u5251\u8c31\u4f20\u7ed9\u65c1\u4eba\uff0c\u563f\u563f\uff0c\u8fd9\u8001\u4e0d\u6b7b\u7684\u3002\u572d\u513f\uff0c\u5feb\uff0c\u5feb\uff0c\u5c06\u90a3\u5251\u8c31\u53bb\u53d6\u6765\u3002\u201d \u3000\u3000\u4e07\u572d\u5fae\u4e00\u8fdf\u7591\uff0c\u4ece\u6000\u4e2d\u638f\u4e86\u90a3\u672c\u4e66\u51fa\u6765\u3002\u539f\u6765\u621a\u82b3\u4e00\u585e\u5165\u897f\u504f\u623f\u7684\u98ce\u6247\u4e4b\u4e2d\uff0c\u4e07\u572d\u8ddf\u7740\u4fbf\u53bb\u53d6\u4e86\u51fa\u6765\u3002 \u3000\u3000\u4e07\u9707\u5c71\u5411\u513f\u5b50\u77a7\u4e86\u4e00\u773c\uff0c\u63a5\u8fc7\u4e66\u6765\uff0c\u4e00\u9875\u9875\u7684\u7ffb\u8fc7\u53bb\u3002\u8fd9\u90e8\u201c\u5510\u8bd7\u201d\u4e24\u8fb9\u8fde\u7740\u5c01\u76ae\u7684\u51e0\u9875\u90fd\u7ed9\u8840\u6c34\u6d78\u5f97\u6e7f\u900f\u4e86\uff0c\u5140\u81ea\u672a\u5e72\uff0c\u4e2d\u95f4\u7684\u4e66\u9875\u5374\u4ecd\u662f\u5e72\u7684\u3002 \u3000\u3000\u4e07\u9707\u5c71\u4f4e\u58f0\u9053\uff1a\u201c\u8fd9\u5251\u8c31\u54b1\u7236\u5b50\u80fd\u4e0d\u80fd\u4fdd\u5f97\u4f4f\uff0c\u5b9e\u5728\u96be\u8bf4\u3002 \u3000\u3000\u54b1\u4eec\u5148\u67e5\u77e5\u4e86\u4e66\u4e2d\u7684\u5965\u79d8\uff0c\u5c31\u7b97\u518d\u7ed9\u4eba\u593a\u53bb\uff0c\u4e5f\u4e0d\u6253\u7d27\u4e86\u3002\u4f60\u62ff\u679d\u7b14\u6765\uff0c\u5199\u4e0b\u6765\u597d\u597d\u8bb0\u7740\u3002\u8fde\u57ce\u5251\u6cd5\u7684\u7b2c\u4e00\u62db\uff0c\u51fa\u81ea\u675c\u752b\u7684\u300a\u6625\u5f52\u300b\u3002\u201d\u4ed6\u4f38\u624b\u6307\u6cbe\u4e86\u553e\u6d8e\uff0c\u53bb\u6e7f\u675c\u752b\u90a3\u9996\u300a\u6625\u5f52\u300b\u8bd7\u65c1\u7684\u7eb8\u9875\uff0c\u8f7b\u8f7b\u6b22\u547c\u4e86\u4e00\u58f0\uff1a\u201c\u662f\u4e2a\u2018\u56db\u2019\u5b57\uff01\u597d\uff0c\u2018\u82d4\u5f84\u4e34\u6c5f\u7af9\u2019\uff0c\u7b2c\u56db\u4e2a\u5b57\u662f\u2018\u6c5f\u2019\uff0c\u4f60\u8bb0\u4e0b\u4e86\u3002\u7b2c\u4e8c\u62db\uff0c\u4ecd\u662f\u675c\u752b\u7684\u8bd7\uff0c\u51fa\u81ea\u300a\u91cd\u7ecf\u662d\u9675\u300b\u3002\u201d\u4ed6\u53c8\u6cbe\u6e7f\u624b\u6307\uff0c\u53bb\u6e7f\u7eb8\u9875\uff1a\u201c\u55ef\uff0c\u662f\u2018\u4e94\u5341\u4e00\u2019\uff01\u201d\u4ed6\u4e00\u4e2a\u5b57\u4e00\u4e2a\u5b57\u7684\u6570\u4e0b\u53bb\uff1a\u201c\u4e00\u4e94\u3001\u4e00\u5341\u3001\u5341\u4e94\u3001\u4e8c\u5341\u2026\u2026\u2018\u9675\u5bdd\u76d8\u7a7a\u66f2\uff0c\u718a\u7f74\u5b88\u7fe0\u5fae\u2019\uff0c\u7b2c\u4e94\u5341\u4e00\u4e2a\u5b57\uff0c\u90a3\u662f\u4e2a\u2018\u9675\u2019\u5b57\u3002\u2018\u6c5f\u9675\u2019\u3001\u2018\u6c5f\u9675\u2019\uff0c\u5999\u6781\uff0c\u539f\u6765\u679c\u7136\u4fbf\u5728\u8346\u5dde\u3002\u201d \u3000\u3000\u4e07\u572d\u9053\uff1a\u201c\u7239\u7239\uff0c\u4f60\u8bf4\u5c0f\u58f0\u4e9b\uff01\u201d\u4e07\u9707\u5c71\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5bf9\uff01\u4e0d\u53ef\u5f97\u610f\u5fd8\u5f62\u3002\u572d\u513f\uff0c\u4f60\u7239\u7239\u4e00\u4e16\u5fc3\u8840\uff0c\u603b\u7b97\u6ca1\u6709\u767d\u82b1\uff0c\u8fd9\u4e2a\u5927\u79d8\u5bc6\uff0c\u6bd5\u7adf\u7ed9\u54b1\u4eec\u627e\u5230\u4e86\uff01\u201d\u7a81\u7136\u4e4b\u95f4\uff0c\u4ed6\u5c06\u4e66\u63a9\u4e0a\uff0c\u4e00\u62cd\u5927\u817f\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u654c\u4eba\u4e3a\u751a\u4e48\u5c06\u5251\u8c31\u9001\u5230\u6211\u624b\u91cc\uff0c\u6211\u660e\u767d\u5566\uff01\u201d \u3000\u3000\u4e07\u572d\u9053\uff1a\u201c\u90a3\u662f\u751a\u4e48\u7f18\u6545\uff1f\u6211\u4e00\u76f4\u60f3\u4e0d\u900f\u3002\u201d \u3000\u3000\u4e07\u9707\u5c71\u9053\uff1a\u201c\u654c\u4eba\u5f97\u4e86\u5251\u8c31\uff0c\u63a8\u8be6\u4e0d\u51fa\u5176\u4e2d\u7684\u79d8\u5965\uff0c\u53c8\u6709\u751a\u4e48\u5c41\u7528\uff1f\u54b1\u4eec\u7684\u8fde\u57ce\u5251\u6cd5\uff0c\u6bcf\u4e00\u62db\u7684\u540d\u79f0\u90fd\u662f\u4e00\u53e5\u5510\u8bd7\uff0c\u522b\u95e8\u522b\u6d3e\u7684\u4eba\uff0c\u4efb\u4ed6\u6b66\u529f\u901a\u5929\uff0c\u5374\u4e5f\u4e0d\u77e5\u3002\u8fd9\u4e16\u754c\u4e0a\uff0c\u53ea\u6709\u6211\u548c\u8a00\u8fbe\u5e73\u4e8c\u4eba\uff0c\u624d\u77e5\u9053\u7b2c\u4e00\u62db\u662f\u751a\u4e48\u8bd7\u53e5\uff0c\u7b2c\u4e8c\u62db\u53c8\u662f\u751a\u4e48\u8bd7\u53e5\u3002\u624d\u77e5\u9053\u7b2c\u4e00\u4e2a\u5b57\u8981\u5230\u300a\u6625\u5f52\u300b\u8fd9\u9996\u8bd7\u4e2d\u53bb\u627e\uff0c\u7b2c\u4e8c\u4e2a\u5b57\u8981\u5230\u300a\u91cd\u7ecf\u662d\u9675\u300b\u8fd9\u9996\u8bd7\u4e2d\u53bb\u5bfb\u3002\u201d \u3000\u3000\u4e07\u572d\u9053\uff1a\u201c\u8fd9\u8fde\u57ce\u5251\u6cd5\u7684\u540d\u79f0\uff0c\u4f60\u4e0d\u662f\u5df2\u6559\u4e86\u6211\u4eec\u5417\uff1f\u201d\u4e07\u9707\u5c71\u9053\uff1a\u201c\u6b21\u5e8f\u90fd\u662f\u6296\u4e71\u4e86\u7684\u3002\u201d\u4e07\u572d\u9053\uff1a\u201c\u7239\uff0c\u4f60\u8fde\u6211\u4e5f\u4e0d\u6559\u771f\u7684\u5251\u6cd5\u3002\u201d\u4e07\u9707\u5c71\u5fae\u6709\u5c34\u5c2c\u4e4b\u8272\uff0c\u9053\uff1a\u201c\u6211\u6709\u516b\u4e2a\u5f1f\u5b50\uff0c\u5927\u5bb6\u671d\u665a\u90fd\u5728\u4e00\u8d77\uff0c\u82e5\u662f\u5355\u5355\u6559\u4f60\uff0c\u4ed6\u4eec\u5b9a\u4f1a\u77e5\u89c9\uff0c\u90a3\u4fbf\u4e0d\u5999\u4e86\u3002\u201d \u3000\u3000\u4e07\u572d\u201c\u55ef\u201d\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u654c\u4eba\u7684\u9634\u8c0b\u5b9a\u662f\u8fd9\u6837\u3002\u4ed6\u77e5\u9053\u7528\u6c34\u6e7f\u7eb8\uff0c\u4fbf\u6709\u5b57\u8ff9\u663e\u51fa\uff0c\u56e0\u6b64\u6545\u610f\u5c06\u5251\u8c31\u4ea4\u7ed9\u54b1\u4eec\uff0c\u53c8\u6545\u610f\u7528\u6c34\u663e\u51fa\u51e0\u4e2a\u5b57\u6765\uff0c\u8981\u54b1\u4eec\u67e5\u51fa\u5251\u8c31\u91cc\u7684\u79d8\u5965\uff0c\u8ba9\u54b1\u4eec\u53bb\u5bfb\u8bbf\u5b9d\u85cf\uff0c\u4ed6\u5c31\u6765\u4e2a\u2018\u5f3a\u76d7\u9047\u7740\u8d3c\u7237\u7237\u2019\u3002\u201d\u4e07\u9707\u5c71\u9053\uff1a\u201c\u5bf9\u4e86\uff01 \u3000\u3000\u54b1\u4eec\u987b\u5f97\u6b65\u6b65\u63d0\u9632\uff0c\u522b\u843d\u5f97\u4e00\u573a\u8f9b\u82e6\uff0c\u5f97\u4e0d\u5230\u5b9d\u85cf\uff0c\u8fde\u6027\u547d\u4e5f\u9001\u6389\u4e86\u3002\u201d \u3000\u3000\u4ed6\u53c8\u6cbe\u6e7f\u4e86\u624b\u6307\uff0c\u53bb\u5bfb\u7b2c\u4e09\u4e2a\u5b57\uff0c\u8bf4\u9053\uff1a\u201c\u5251\u6cd5\u7b2c\u4e09\u62db\uff0c\u51fa\u4e8e\u5904\u9ed8\u7684\u300a\u5723\u679c\u5bfa\u300b\uff0c\u4e09\u5341\u4e09\uff0c\u7b2c\u4e09\u5341\u4e09\u5b57\uff0c\u2018\u4e0b\u65b9\u57ce\u90ed\u8fd1\uff0c\u949f\u78ec\u6742\u7b19\u6b4c\u2019\u4e2d\u7684\u2018\u57ce\u2019\u5b57\uff0c\u2018\u6c5f\u9675\u57ce\u2019\uff0c\u5bf9\u5566\uff0c\u5bf9\u5566\uff01\u90a3\u8fd8\u6709\u751a\u4e48\u53ef\u7591\u5fc3\u7684\uff1f\u54a6\uff0c\u600e\u4e48\u8fd9\u91cc\u75d2\u5f97\u5389\u5bb3\uff1f\u201d\u4ed6\u4f38\u53f3\u624b\u5728\u5de6\u624b\u80cc\u4e0a\u6414\u4e86\u51e0\u4e0b\uff0c\u89c9\u5f97\u53f3\u624b\u4e5f\u75d2\uff0c\u4f38\u5de6\u624b\u53bb\u6414\u4e86\u51e0\u4e0b\uff0c\u53c8\u770b\u90a3\u5251\u8c31\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u7b2c\u56db\u62db\uff0c\u662f\u4e8c\u5341\u516b\uff0c\u55ef\uff0c\u4e00\u4e94\u3001\u4e00\u5341\u3001\u5341\u4e94\u2026\u2026\u7b2c\u4e8c\u5341\u516b\u4e2a\u5b57\u662f\u4e2a\u2018\u5357\u2019\u5b57\uff0c\u2018\u6c5f\u9675\u57ce\u5357\u2019\uff0c\u54c8\u54c8\uff0c\u54a6\uff01\u597d\u75d2\uff01\u201d\u4f4e\u5934\u5411\u81ea\u5df1\u5de6\u624b\u4e0a\u770b\u53bb\uff0c\u53ea\u89c1\u624b\u80cc\u4e0a\u957f\u4e86\u4e09\u6761\u58a8\u75d5\uff0c\u5fae\u89c9\u60ca\u8be7\uff1a\u201c\u4eca\u5929\u6211\u53c8\u6ca1\u5199\u5b57\uff0c\u624b\u80cc\u4e0a\u600e\u4e48\u6709\u9ed1\u58a8\uff1f\u201d\u53ea\u89c9\u53cc\u624b\u624b\u80cc\u4e0a\u8d8a\u6765\u8d8a\u75d2\uff0c\u4e00\u770b\u53f3\u624b\uff0c\u4e5f\u662f\u6709\u597d\u51e0\u6761\u7eb5\u6a2a\u4ea4\u9519\u7684\u58a8\u75d5\u3002 \u3000\u3000\u4e07\u572d\u201c\u554a\u201d\u7684\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u7239\u7239\uff0c\u54ea\u2026\u2026\u54ea\u91cc\u6765\u7684\uff1f\u8fd9\u597d\u50cf\u662f\u8a00\u8fbe\u5e73\u90a3\u53ae\u7684\u82b1\u874e\u6bd2\u3002\u201d\u4e07\u9707\u5c71\u7ed9\u4ed6\u4e00\u8a00\u63d0\u9192\uff0c\u53ea\u89c9\u624b\u4e0a\u75d2\u5f97\u66f4\u52a0\u5389\u5bb3\u4e86\uff0c\u5fcd\u4e0d\u4f4f\u4f38\u624b\u53c8\u53bb\u6414\u75d2\u3002 \u3000\u3000\u4e07\u572d\u53eb\u9053\uff1a\u201c\u522b\u6414\uff0c\u662f\u2026\u2026\u662f\u4f60\u6307\u7532\u4e0a\u5e26\u6bd2\u8fc7\u53bb\u7684\u3002\u201d \u3000\u3000\u4e07\u9707\u5c71\u53eb\u9053\uff1a\u201c\u554a\u54df\uff01\u679c\u771f\u5982\u6b64\u3002\u201d\u767b\u65f6\u7701\u609f\uff0c\u9053\uff1a\u201c\u90a3\u5c0f\u6deb\u5987\u5c06\u5251\u8c31\u6d78\u5728\u8840\u6c34\u4e4b\u4e2d\uff0c\u4f60\u7684\u8840\u4e2d\u542b\u6709\u874e\u6bd2\u2026\u2026\u5434\u574e\u8fd9\u5c0f\u8d3c\uff0c\u504f\u4e0d\u80af\u723d\u723d\u5feb\u5feb\u7684\u5c31\u6b7b\uff0c\u5374\u5728\u6211\u624b\u4e0a\u6414\u4e86\u8fd9\u8bb8\u591a\u8840\u75d5\u3002\u4ed6\u5988\u7684\uff0c\u874e\u6bd2\u4f20\u5165\u4e86\u4f24\u53e3\u4e4b\u4e2d\uff0c\u597d\u5728\u4e0d\u591a\uff0c\u8c05\u6765\u4e5f\u4e0d\u788d\u4e8b\u3002\u554a\u54df\uff0c\u600e\u5730\u8d8a\u6765\u8d8a\u75db\u4e86\uff0c\u54ce\u5537\uff0c\u54ce\u5537\u3002\u201d\u5fcd\u4e0d\u4f4f\u5927\u58f0\u547b\u541f\u4e86\u8d77\u6765\u3002 \u3000\u3000\u4e07\u572d\u9053\uff1a\u201c\u7239\uff0c\u4f60\u8fd9\u874e\u6bd2\u4e2d\u5f97\u4e0d\u591a\uff0c\u6211\u53bb\u8200\u6c34\u6765\u7ed9\u4f60\u6d17\u6d17\u3002\u201d \u3000\u3000\u4e07\u9707\u5c71\u9053\uff1a\u201c\u4e0d\u9519\uff01\u201d\u5927\u58f0\u53eb\u9053\uff1a\u201c\u6843\u7ea2\uff0c\u6843\u7ea2\uff01\u6253\u6c34\u6765\uff01\u201d\u4e07\u572d\u7709\u5934\u8e59\u8d77\uff0c\u5fc3\u9053\uff1a\u201c\u7239\u7239\u5413\u5f97\u80e1\u6d82\u4e86\uff0c\u6843\u7ea2\u65e9\u7ed9\u4ed6\u8d76\u8d70\u4e86\uff0c\u8fd9\u4f1a\u513f\u53c8\u6765\u53eb\u5979\u3002\u201d\u62ff\u8d77\u4e00\u53ea\u94dc\u8138\u76c6\uff0c\u5feb\u6b65\u51fa\u623f\uff0c\u5728\u5929\u4e95\u91cc\u4e03\u77f3\u7f38\u4e2d\u8200\u8d77\u4e00\u76c6\u5929\u843d\u6c34\uff0c\u7aef\u8fdb\u6765\u653e\u5728\u684c\u4e0a\u3002\u4e07\u9707\u5c71\u5fd9\u5c06\u53cc\u624b\u6d78\u5165\u4e86\u6e05\u6c34\u4e4b\u4e2d\uff0c\u4e00\u9635\u51b0\u51c9\uff0c\u75db\u75d2\u767b\u51cf\u3002 \u3000\u3000\u54ea\u77e5\u9053\u4e07\u572d\u624b\u4e0a\u6240\u4e2d\u7684\u874e\u6bd2\u9047\u4e0a\u89e3\u836f\uff0c\u6d41\u51fa\u6765\u7684\u9ed1\u8840\u4e5f\u5177\u5267\u6bd2\uff0c\u6bd2\u6027\u6bd4\u4e4b\u539f\u6765\u7684\u874e\u6bd2\u53ea\u6709\u66f4\u52a0\u5389\u5bb3\uff0c\u4e07\u9707\u5c71\u624b\u80cc\u4e0a\u88ab\u5434\u574e\u6293\u51fa\u8840\u75d5\u6df1\u5165\u808c\u7406\uff0c\u4e00\u78b0\u5230\u8fd9\u5267\u6bd2\uff0c\u5b9e\u6bd4\u4e07\u572d\u4e2d\u6bd2\u66f4\u6df1\u3002\u4ed6\u53cc\u624b\u5728\u6e05\u6c34\u4e2d\u6d78\u5f97\u7247\u65f6\uff0c\u4e00\u76c6\u6c34\u5df2\u53d8\u6210\u4e86\u6de1\u58a8\u6c34\u4e00\u822c\u3002 \u3000\u3000\u58a8\u6c34\u7531\u6de1\u8f6c\u6df1\uff0c\u8fc7\u4e0d\u591a\u65f6\uff0c\u53d8\u5f97\u4fbf\u5982\u662f\u4e00\u76c6\u6d53\u6d53\u7684\u58a8\u6c41\u3002 \u3000\u3000\u4e07\u6c0f\u7236\u5b50\u76f8\u987e\u5931\u8272\u3002\u4e07\u9707\u5c71\u5c06\u624b\u638c\u63d0\u4e86\u8d77\u6765\uff0c\u4e0d\u7981 \u201c\u554a\u201d\u7684\u4e00\u58f0\uff0c\u5931\u58f0\u60ca\u547c\uff0c\u53ea\u89c1\u4e24\u53ea\u624b\u51e0\u4e4e\u80bf\u6210\u4e86\u4e24\u4e2a\u5706\u7403\u3002 \u3000\u3000\u4e07\u572d\u9053\uff1a\u201c\u554a\u54df\uff0c\u4e0d\u597d\uff0c\u53ea\u6015\u4e0d\u80fd\u6d78\u6c34\uff01\u201d \u3000\u3000\u4e07\u9707\u5c71\u75db\u5f97\u6025\u4e86\uff0c\u4e00\u811a\u8e22\u5728\u4ed6\u8170\u95f4\uff0c\u9a82\u9053\uff1a\u201c\u4f60\u65e2\u77e5\u4e0d\u80fd\u6d78\u6c34\uff0c\u600e\u4e48\u53c8\u53bb\u8200\u6c34\u6765\uff1f\u8fd9\u4e0d\u662f\u5b58\u5fc3\u5bb3\u6211\u4e48\uff1f\u201d\u4e07\u572d\u75db\u5f97\u8e72\u4e0b\u8eab\u53bb\uff0c\u9053\uff1a\u201c\u6211\u672c\u6765\u53c8\u4e0d\u77e5\u9053\uff0c\u600e\u4e48\u4f1a\u6765\u5bb3\u4f60\uff1f\u201d \u3000\u3000\u621a\u82b3\u5728\u5e8a\u5e95\u4e0b\u542c\u5f97\u7236\u5b50\u4e8c\u4eba\u4e89\u5435\uff0c\u5fc3\u4e2d\u4e5f\u4e0d\u77e5\u662f\u51c4\u51c9\uff0c\u8fd8\u662f\u4f53\u4f1a\u5230\u4e86\u590d\u4ec7\u7684\u559c\u60a6\u3002 \u3000\u3000\u53ea\u542c\u5f97\u4e07\u9707\u5c71\u53ea\u662f\u53eb\uff1a\u201c\u600e\u4e48\u529e\uff1f\u600e\u4e48\u529e\uff1f\u201d\u4e07\u572d\u9053\uff1a\u201c\u6211\u697c\u4e0a\u6709\u4e9b\u6b62\u75db\u836f\uff0c\u867d\u4e0d\u80fd\u89e3\u6bd2\uff0c\u5374\u53ef\u6b62\u5f97\u4e00\u65f6\u4e4b\u75db\uff0c\u8981\u4e0d\u8981\u6577\u4e00\u4e9b\uff1f\u201d\u4e07\u9707\u5c71\u9053\uff1a\u201c\u597d\uff0c\u597d\uff0c\u597d\uff01\u5feb\u53bb\u62ff\u6765\uff01\u201d\u4e07\u572d\u9053\uff1a\u201c\u662f\u5426\u6709\u6548\uff0c\u5b69\u513f\u53ef\u5c31\u4e0d\u77e5\uff0c\u8bf4\u4e0d\u5b9a\u8d8a\u6577\u8d8a\u4e0d\u5bf9\u5934\uff0c\u7239\u7239\u53c8\u8981\u8e22\u6211\u3002\u201d\u4e07\u9707\u5c71\u9a82\u9053\uff1a\u201c\u738b\u516b\u7f94\u5b50\uff01\u8fd9\u4f1a\u513f\u8fd8\u5728\u4e0d\u670d\u6c14\u4e48\uff1f\u8001\u5b50\u751f\u4e86\u4f60\u51fa\u6765\uff0c\u8e22\u4e00\u811a\u53c8\u6709\u751a\u4e48\u5927\u5c0f\u4e86\uff1f\u5feb\u53bb\uff0c\u5feb\u53bb\u62ff\u6765\u3002\u201d \u3000\u3000\u4e07\u572d\u5e94\u9053\uff1a\u201c\u662f\uff01\u201d\u8f6c\u8eab\u51fa\u53bb \u3000\u3000\u4e07\u9707\u5c71\u53cc\u624b\u80bf\u80c0\u96be\u5f53\uff0c\u624b\u80cc\u4e0a\u7684\u76ae\u80a4\u9ed1\u4e2d\u900f\u4eae\uff0c\u5168\u65e0\u534a\u70b9\u76b1\u7eb9\uff0c\u4fbf\u5982\u4e00\u4e2a\u5439\u80c0\u4e86\u7684\u732a\u5c3f\u6ce1\u4e00\u822c\uff0c\u773c\u770b\u518d\u7a0d\u80c0\u5927\uff0c\u52bf\u975e\u7834\u88c2\u4e0d\u53ef\uff0c\u53eb\u9053\uff1a\u201c\u6211\u548c\u4f60\u4e00\u8d77\u53bb\uff01\u53ef\u2026\u2026\u53ef\u4e0d\u80fd\u803d\u6401\u4e86\u3002\u201d \u3000\u3000\u5c06\u5251\u8c31\u5f80\u6000\u4e2d\u4e00\u63e3\uff0c\u5954\u884c\u5982\u98de\uff0c\u62a2\u51fa\u623f\u95e8\uff0c\u8d76\u5728\u4e07\u572d\u4e4b\u524d\u3002 \u3000\u3000\u621a\u82b3\u542c\u5f97\u4e8c\u4eba\u8fdc\u53bb\u3002\u5fd9\u4ece\u5e8a\u5e95\u722c\u4e86\u51fa\u6765\uff0c\u81ea\u5fd6\uff1a\u201c\u5374\u5230\u54ea\u91cc\u53bb\u597d\uff1f\u201d\u970e\u65f6\u95f4\u516d\u795e\u65e0\u4e3b\uff0c\u53ea\u89c9\u832b\u832b\u5927\u5730\uff0c\u7adf\u65e0\u4e00\u5904\u53ef\u4ee5\u5b89\u8eab\uff1a\u201c\u4ed6\u4eec\u5bb3\u6b7b\u6211\u7239\u7239\uff0c\u6b64\u4ec7\u5c82\u53ef\u4e0d\u62a5\uff1f\u4f46\u8fd9\u8840\u6d77\u6df1\u4ec7\uff0c\u5374\u5982\u4f55\u62a5\u6cd5\uff1f\u8bf4\u5230\u6b66\u529f\u3001\u673a\u667a\uff0c\u6211\u548c\u516c\u516c\u3001\u4e09\u54e5\u5b9e\u662f\u5dee\u5f97\u592a\u8fdc\uff0c\u4f55\u51b5\u4ed6\u4eec\u8ba4\u5b9a\u6211\u548c\u5434\u574e\u7ed3\u4e86\u79c1\u60c5\uff0c\u4e00\u89c1\u9762\u5c31\u4f1a\u5bf9\u6211\u72e0\u4e0b\u6740\u624b\uff0c\u6211\u53c8\u600e\u80fd\u62b5\u6321\uff1f\u773c\u4e0b\u53ea\u6709\u53bb\u2026\u2026\u53bb\u5bfb\u627e\u72c4\u5e08\u54e5\uff0c\u518d\u4f5c\u8ba1\u8f83\u3002\u53ef\u53c8\u4e0d\u77e5\u4ed6\u5728\u54ea\u91cc\uff1f\u7a7a\u5fc3\u83dc\u5462\uff1f\u6211\u600e\u80fd\u6487\u4e0b\u4e86\u5979\uff1f\u201d\u4e00\u60f3\u5230\u5973\u513f\uff0c\u5f53\u5373\u62d4\u6b65\u5954\u5411\u540e\u697c\uff0c\u51b3\u610f\u62b1\u4e86\u5973\u513f\u5148\u884c\u9003\u8d70\uff0c\u518d\u60f3\u590d\u4ec7\u4e4b\u6cd5\u3002 \u3000\u3000\u5728\u5979\u5185\u5fc3\uff0c\u53c8\u8fd8\u4e0d\u6562\u5341\u5206\u786e\u5b9a\u4e07\u6c0f\u7236\u5b50\u5f53\u771f\u662f\u5bb3\u6b7b\u4e86\u5979\u7236\u4eb2\u3002\u4e07\u9707\u5c71\u662f\u4e2a\u5fc3\u72e0\u624b\u8fa3\u4e4b\u5f92\uff0c\u90a3\u662f\u7edd\u65e0\u6000\u7591\uff0c\u4f46\u4e07\u572d\u5462\uff1f \u3000\u3000\u5bf9\u4e8e\u4e08\u592b\u7684\u67d4\u60c5\u871c\u610f\uff0c\u7ec8\u4e0d\u80fd\u8fd9\u4e48\u5feb\u4fbf\u51b3\u7edd\u7684\u629b\u5374\u3002 \u3000\u3000\u5979\u5954\u5230\u697c\u4e0b\uff0c\u542c\u5f97\u4e07\u9707\u5c71\u5636\u54d1\u7684\u58f0\u97f3\u5927\u53eb\u5927\u56b7\uff0c\u5fc3\u60f3\uff1a \u3000\u3000\u201c\u8fd9\u4e48\u53eb\u6cd5\uff0c\u8981\u5c06\u7a7a\u5fc3\u83dc\u5435\u9192\u4e86\uff01\u201d\u60f3\u5230\u5973\u513f\u4f1a\u5927\u53d7\u60ca\u5413\uff0c\u4fbf\u987e\u4e0d\u5f97\u81ea\u8eab\u5371\u9669\uff0c\u8f7b\u8f7b\u8d70\u4e0a\u697c\u53bb\uff0c\u5c0f\u5fc3\u4e0d\u8ba9\u697c\u68af\u53d1\u51fa\u58f0\u606f\u3002\u7a7a\u5fc3\u83dc\u7761\u89c9\u7684\u5c0f\u623f\u4fbf\u5728\u5979\u592b\u59bb\u7684\u5367\u5ba4\u4e4b\u540e\uff0c\u53ea\u4ee5\u4e00\u5c42\u8584\u677f\u9694\u5f00\u3002 \u3000\u3000\u621a\u82b3\u6e9c\u8fdb\u5c0f\u623f\uff0c\u5367\u623f\u4e2d\u706f\u5149\u6620\u4e86\u8fdb\u6765\uff0c\u53ea\u89c1\u5973\u513f\u7741\u5927\u4e86\u773c\uff0c\u65e9\u5df2\u9192\u8f6c\uff0c\u8138\u4e0a\u6ee1\u662f\u6050\u6016\u4e4b\u8272\uff0c\u4e00\u89c1\u5230\u6bcd\u4eb2\uff0c\u5c0f\u5634\u4e00\u6241\uff0c\u4fbf\u8981\u54ed\u53eb\u51fa\u6765\u3002\u621a\u82b3\u6025\u5fd9\u62a2\u4e0a\u524d\u53bb\uff0c\u5c06\u5979\u6402\u5728\u6000\u91cc\uff0c\u505a\u4e2a\u624b\u52bf\uff0c\u53eb\u5979\u5343\u4e07\u4e0d\u53ef\u51fa\u58f0\u3002\u7a7a\u5fc3\u83dc\u65e2\u806a\u660e\uff0c\u53c8\u542c\u8bdd\u3002\u5f53\u4e0b\u4e00\u58f0\u4e0d\u54cd\uff0c\u5a18\u513f\u4fe9\u6402\u62b1\u7740\u8eba\u5728\u5e8a\u4e0a\u3002 \u3000\u3000\u53ea\u542c\u5f97\u4e07\u9707\u5c71\u5927\u53eb\uff1a\u201c\u4e0d\u6210\uff0c\u4e0d\u6210\uff0c\u8fd9\u6b62\u75db\u836f\u8d8a\u6b62\u8d8a\u75db\uff0c\u987b\u5f97\u5bfb\u5230\u90a3\u8349\u5934\u90ce\u4e2d\uff0c\u7528\u4ed6\u7684\u89e3\u836f\u6765\u6cbb\u3002\u201d\u4e07\u572d\u9053\uff1a\u201c\u662f\u554a\uff0c\u53ea\u6709\u90a3\u89e3\u836f\u624d\u6cbb\u5f97\u8fd9\u6bd2\uff0c\u7b49\u5929\u4e00\u4eae\uff0c\u53eb\u9c81\u5927\u54e5\u4ed6\u4eec\u5927\u4f19\u513f\u4e00\u9f50\u51fa\u9a6c\uff0c\u53bb\u5bfb\u90a3\u90ce\u4e2d\u3002\u6211\u624b\u4e0a\u7684\u4f24\u53e3\u4e5f\u75db\u5f97\u5f88\u3002\u201d\u4e07\u9707\u5c71\u6012\u9053\uff1a\u201c\u600e\u7b49\u5f97\u5230\u5929\u4eae\uff1f\u554a\u54df\uff0c\u54ce\u5537\uff01\u53d7\u4e0d\u4e86\u5566\uff0c\u53d7\u4e0d\u4e86\u5566\uff01\u201d\u7a81\u7136\u95f4\u811a\u4e0b\u4e00\u8f6f\uff0c\u5012\u5728\u5730\u4e0b\uff0c\u75db\u5f97\u6253\u6eda\uff0c\u53eb\u9053\uff1a\u201c\u5feb\uff0c\u5feb\uff01\u62ff\u5251\u6765\uff0c\u5c06\u6211\u8fd9\u53cc\u624b\u780d\u4e86\uff01\u5feb\u780d\u4e86\u6211\u7684\u624b\uff01\u201d\u53ea\u542c\u5f97\u623f\u4e2d\u5bb6\u5177\u7830\u562d\u7ffb\u5012\uff0c\u74f6\u7897\u4e52\u4e53\u6253\u788e\u4e4b\u58f0\uff0c\u54cd\u6210\u4e86\u4e00\u7247\u3002 \u3000\u3000\u7a7a\u5fc3\u83dc\u5413\u5f97\u7d27\u7d27\u7684\u6402\u4f4f\u4e86\u5988\u5988\uff0c\u8138\u8272\u5927\u53d8\u3002\u621a\u82b3\u4f38\u624b\u8f7b\u8f7b\u629a\u6170\uff0c\u5374\u4e0d\u6562\u4f5c\u58f0\u3002 \u3000\u3000\u4e07\u572d\u4e5f\u662f\u5341\u5206\u60ca\u614c\uff0c\u8bf4\u9053\uff1a\u201c\u7239\uff0c\u4f60\u2026\u2026\u4f60\u5fcd\u8010\u4e00\u4f1a\u513f\uff0c\u4f60\u7684\u624b\u600e\u80fd\u780d\u4e86\uff1f\u54b1\u4eec\u5feb\u627e\u89e3\u836f\u6b63\u7ecf\u3002\u201d\u4e07\u9707\u5c71\u75db\u5f97\u518d\u96be\u62b5\u53d7\uff0c\u559d\u9053\uff1a\u201c\u4f60\u4e3a\u751a\u4e48\u4e0d\u780d\u53bb\u6211\u53cc\u624b\uff0c\u9664\u6211\u75db\u695a\uff1f\u554a\uff0c\u77e5\u9053\u4e86\uff0c\u4f60\u2026\u2026\u4f60\u60f3\u6211\u5feb\u5feb\u6b7b\u4e86\uff0c\u597d\u72ec\u541e\u5251\u8c31\uff0c\u60f3\u72ec\u81ea\u4e2a\u53bb\u5bfb\u5b9d\u85cf\u2026\u2026\u201d\u4e07\u572d\u6012\u9053\uff1a\u201c\u7239\uff0c\u4f60\u75db\u5f97\u795e\u667a\u4e0d\u6e05\u4e86\uff0c\u5feb\u4e0a\u5e8a\u7761\u4e00\u5ffd\u513f\u3002 \u3000\u3000\u6211\u53c8\u4e0d\u77e5\u5251\u62db\u7684\u6b21\u5e8f\uff0c\u5f97\u4e86\u5251\u8c31\u53c8\u6709\u751a\u4e48\u7528\uff1f\u201d \u3000\u3000\u4e07\u9707\u5c71\u4e0d\u65ad\u5728\u5730\u4e0b\u6253\u6eda\uff0c\u9053\uff1a\u201c\u4f60\u8bf4\u6211\u795e\u667a\u4e0d\u6e05\uff0c\u4f60\u81ea\u5df1\u5c31\u5b58\u5fc3\u4e0d\u826f\u3002\u6211\u2026\u2026\u6211\u75db\u5f97\u8981\u6b7b\u4e86\u2026\u2026\u8981\u6b7b\u4e86\u2026\u2026\u4e00\u62cd\u4e24\u6563\uff0c\u5927\u5bb6\u90fd\u5f97\u4e0d\u5230\u3002\u201d \u3000\u3000\u7a81\u7136\u4e4b\u95f4\uff0c\u4ed6\u7ea2\u4e86\u53cc\u773c\uff0c\u4ece\u6000\u4e2d\u638f\u51fa\u5251\u8c31\uff0c\u4f38\u624b\u4e00\u9875\u9875\u7684\u6495\u788e\u3002\u4ed6\u5341\u6839\u624b\u6307\u80bf\u5f97\u4fbf\u5982\u4e00\u6839\u6839\u80e1\u7ba9\u535c\u822c\uff0c\u52a8\u4f5c\u4e0d\u7075\uff0c\u4f46\u8fd8\u662f\u6495\u788e\u4e86\u597d\u51e0\u9875\u3002 \u3000\u3000\u4e07\u572d\u5927\u60ca\uff0c\u53eb\u9053\uff1a\u201c\u522b\u6495\uff0c\u522b\u6495\uff01\u201d\u4f38\u624b\u4fbf\u53bb\u62a2\u593a\u3002\u4ed6\u6293\u4f4f\u4e86\u534a\u672c\u5251\u8c31\uff0c\u4e07\u9707\u5c71\u5374\u6293\u4f4f\u4e86\u53e6\u4e00\u534a\uff0c\u7262\u4e0d\u653e\u624b\u3002\u90a3\u5251\u8c31\u5728\u8840\u6c34\u4e2d\u6d78\u8fc7\uff0c\u8fc4\u672a\u5e72\u900f\uff0c\u9709\u9709\u70c2\u70c2\u7684\uff0c\u4e24\u4eba\u8fd9\u4e48\u4e00\u62c9\u626f\uff0c\u767b\u65f6\u6495\u6210\u4e24\u534a\u3002\u4e07\u572d\u5446\u4e86\u4e00\u5446\uff0c\u4e07\u9707\u5c71\u53c8\u53bb\u6495\u626f\u3002\u4e07\u572d\u4e0d\u7518\u5fc3\u8ba9\u8fd9\u5df2\u7ecf\u5230\u624b\u7684\u5b9d\u85cf\u5316\u4f5c\u8fc7\u773c\u4e91\u70df\uff0c\u5fd9\u4f38\u624b\u63a8\u5f00\u7236\u4eb2\uff0c\u4e24\u4eba\u5728\u5730\u4e0b\u4f60\u62a2\u6211\u593a\uff0c\u7ffb\u7ffb\u6eda\u6eda\uff0c\u5c06\u5251\u8c31\u6495\u5f97\u66f4\u52a0\u788e\u4e86\u3002 \u3000\u3000\u7a81\u7136\u95f4\u542c\u5f97\u4e07\u572d\u957f\u58f0\u60ca\u547c\uff1a\u201c\u54ce\u5537\u2026\u2026\u7cdf\u4e86\u2026\u2026\u6211\u4f24\u53e3\u4e2d\u53c8\u8fdb\u4e86\u6bd2\uff0c\u554a\u54df\uff0c\u597d\u75db\uff01\u201d\u4e24\u4eba\u8fd9\u4e48\u4f60\u62c9\u6211\u626f\uff0c\u5251\u8c31\u4e0a\u7684\u6bd2\u8d28\u6cbe\u8fdb\u4e86\u4e07\u572d\u624b\u80cc\u4e0a\u539f\u6765\u7684\u4f24\u53e3\u3002\u7247\u523b\u4e4b\u95f4\uff0c\u4e07\u572d\u624b\u80cc\u53c8\u9ad8\u9ad8\u80bf\u8d77\uff0c\u5267\u75db\u690e\u5fc3\u7a7f\u9aa8\u3002\u4ed6\u4e45\u75c5\u4e4b\u540e\uff0c\u8010\u529b\u751a\u5f31\uff0c\u6bd2\u7d20\u4e00\u5165\u4f24\u53e3\uff0c\u968f\u8840\u4e0a\u884c\uff0c\u53d1\u4f5c\u5947\u5feb\u3002\u7236\u5b50\u4e8c\u4eba\u5728\u697c\u677f\u4e0a\u6eda\u6765\u6eda\u53bb\uff0c\u60e8\u547c\u53f7\u53eb\u3002 \u3000\u3000\u621a\u82b3\u542c\u4e86\u4e00\u4f1a\uff0c\u7a76\u7adf\u592b\u59bb\u60c5\u91cd\uff0c\u518d\u4e5f\u4e0d\u80fd\u7f6e\u4e4b\u4e0d\u7406\uff0c\u4ece\u5e8a\u4e0a\u7ad9\u8d77\u8eab\u6765\uff0c\u8d70\u5230\u95e8\u53e3\uff0c\u51b7\u51b7\u7684\u9053\uff1a\u201c\u600e\u4e48\u5566\uff1f\u4e24\u4eba\u5728\u5e72\u751a\u4e48\uff1f\u201d \u3000\u3000\u4e07\u6c0f\u7236\u5b50\u89c1\u5230\u621a\u82b3\uff0c\u5267\u75db\u4e4b\u9645\uff0c\u518d\u4e5f\u6ca1\u5fc3\u60c5\u6124\u6012\u3002\u4e07\u572d\u53eb\u9053\uff1a\u201c\u82b3\u59b9\uff0c\u5feb\u53bb\u627e\u90a3\u4e2a\u8349\u5934\u90ce\u4e2d\uff0c\u8bf7\u4ed6\u5feb\u914d\u89e3\u836f\uff0c\u54ce\u5537\uff0c\u54ce\u5537\u2026\u2026\u5b9e\u5728\u2026\u2026\u5b9e\u5728\u75db\u5f97\u71ac\u4e0d\u4f4f\u4e86\uff0c\u6c42\u6c42\u4f60\u2026\u2026\u201d \u3000\u3000\u621a\u82b3\u89c1\u4ed6\u75db\u5f97\u6ee1\u5934\u5927\u6c57\u7684\u6a21\u6837\uff0c\u5fc3\u66f4\u52a0\u8f6f\u4e86\uff0c\u4ece\u6000\u4e2d\u53d6\u51fa\u74f7\u74f6\uff0c\u9053\uff1a\u201c\u8fd9\u662f\u89e3\u836f\uff01\u201d \u3000\u3000\u4e07\u9707\u5c71\u548c\u4e07\u572d\u4e00\u89c1\u74f7\u74f6\uff0c\u540c\u65f6\u6323\u624e\u7740\u722c\u8d77\uff0c\u9f50\u9053\uff1a\u201c\u597d\u6781\uff0c\u597d\u6781\uff01\u5feb\uff0c\u5feb\u7ed9\u6211\u6577\u4e0a\u3002\u201d \u3000\u3000\u621a\u82b3\u89c1\u4e07\u9707\u5c71\u76ee\u5149\u51f6\u72e0\u8d2a\u5a6a\uff0c\u6709\u5982\u91ce\u517d\uff0c\u5fc3\u60f3\u82e5\u4e0d\u4e58\u6b64\u8981\u631f\uff0c\u5982\u4f55\u80fd\u67e5\u660e\u771f\u76f8\uff0c\u4fbf\u9053\uff1a\u201c\u6162\u7740\uff0c\u4e0d\u8bb8\u52a8\uff01\u8c01\u8981\u52a8\u4e0a\u4e00\u52a8\uff0c\u6211\u4fbf\u5c06\u89e3\u836f\u629b\u51fa\u7a97\u5916\uff0c\u6295\u5165\u6c34\u7f38\uff0c\u5927\u5bb6\u90fd\u6b7b\uff01\u201d\u8bf4\u7740\u63a8\u5f00\u7a97\u5b50\uff0c\u62d4\u5f00\u74f7\u74f6\u7684\u74f6\u585e\uff0c\u5c06\u89e3\u836f\u60ac\u5728\u7a97\u5916\uff0c\u53ea\u987b\u624b\u4e00\u677e\uff0c\u74f7\u74f6\u843d\u6c34\uff0c\u518d\u4e5f\u65e0\u7528\u4e86\u3002 \u3000\u3000\u4e07\u6c0f\u7236\u5b50\u5f53\u5373\u4e0d\u52a8\uff0c\u6211\u77a7\u77a7\u4f60\uff0c\u4f60\u77a7\u77a7\u6211\u3002\u4e07\u9707\u5c71\u5ffd\u9053\uff1a\u201c\u597d\u5ab3\u5987\uff0c\u4f60\u5c06\u89e3\u836f\u7ed9\u6211\uff0c\u6211\u8ba9\u4f60\u8ddf\u4e86\u5434\u574e\uff0c\u8fdc\u8d70\u9ad8\u98de\uff0c\u51b3\u4e0d\u963b\u62e6\uff0c\u53e6\u5916\u518d\u9001\u4f60\u4e00\u5343\u4e24\u94f6\u5b50\uff0c\u8ba9\u4f60\u4e8c\u4eba\u8fc7\u957f\u8fdc\u65e5\u5b50\u2026\u2026\u54ce\u5537\uff0c\u597d\u75db\u2026\u2026\u65e2\u7136\u4f60\u5fc3\u6709\u4ed6\u610f\uff0c\u572d\u513f\u4e5f\u7559\u4f60\u4e0d\u4f4f\u2026\u2026\u4f60\u2026\u2026\u4f60\u653e\u5fc3\u53bb\u597d\u4e86\u3002\u201d \u3000\u3000\u621a\u82b3\u5fc3\u9053\uff1a\u201c\u8fd9\u4eba\u5f53\u771f\u5351\u9119\u65e0\u803b\uff0c\u5434\u574e\u660e\u660e\u662f\u4f60\u4eb2\u624b\u627c\u6b7b\u4e86\uff0c\u5374\u8fd8\u6765\u9a97\u4eba\u3002\u201d \u3000\u3000\u4e07\u572d\u4e5f\u9053\uff1a\u201c\u82b3\u59b9\uff0c\u6211\u867d\u7136\u820d\u4e0d\u5f97\u4f60\uff0c\u4f46\u6ca1\u6709\u6cd5\u5b50\uff0c\u6211\u7b54\u5e94\u4e0d\u8ddf\u5434\u574e\u4e3a\u96be\u5c31\u662f\u3002\u201d \u3000\u3000\u621a\u82b3\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u4e8c\u4eba\u80e1\u6d82\u900f\u9876\uff0c\u8fd8\u5728\u778e\u8f6c\u8fd9\u5351\u9119\u9f8c\u9f8a\u7684\u5ff5\u5934\u3002\u6211\u53ea\u95ee\u4e00\u53e5\u8bdd\uff0c\u4f60\u4eec\u8001\u8001\u5b9e\u5b9e\u7684\u56de\u7b54\uff0c\u6211\u7acb\u523b\u7ed9\u89e3\u836f\u3002\u201d \u3000\u3000\u4e07\u9707\u5c71\u9053\uff1a\u201c\u662f\uff0c\u662f\uff0c\u5feb\u95ee\uff0c\u54ce\u5537\uff0c\u554a\u54df\uff01\u201d \u3000\u3000\u4e00\u9635\u98ce\u4ece\u7a97\u4e2d\u522e\u4e86\u8fdb\u6765\u3002\u5439\u5f97\u6ee1\u5730\u7eb8\u5c51\u5982\u8774\u8776\u822c\u98de\u821e\uff0c\u7eb8\u5c51\u662f\u5251\u8c31\u6495\u6210\u7684\uff0c\u4e00\u7247\u7247\u98de\u51fa\u7a97\u5916\u3002\u5ffd\u7136\uff0c\u4e00\u5bf9\u5f69\u8272\u8774\u8776\u98de\u4e86\u8d77\u6765\uff0c\u6b63\u662f\u5979\u5f53\u5e74\u526a\u7684\u7eb8\u8776\uff0c\u5939\u5728\u8bd7\u96c6\u4e2d\u7684\u3002\u4e24\u53ea\u7eb8\u8776\u5728\u623f\u4e2d\u8e41\u8df9\u8d77\u821e\uff0c\u8ddf\u7740\u4ece\u7a97\u4e2d\u98de\u4e86\u51fa\u53bb\u3002\u621a\u82b3\u5fc3\u4e2d\u4e00\u9178\uff0c\u60f3\u8d77\u4e86\u5f53\u65e5\u5728\u77f3\u6d1e\u4e2d\u4e0e\u72c4\u4e91\u6b22\u4e50\u76f8\u805a\u7684\u60c5\u666f\u3002\u90a3\u65f6\u5019\u7684\u4e16\u754c\u53ef\u6709\u591a\u4e48\u597d\uff0c\u5929\u5730\u95f4\u6ca1\u534a\u70b9\u4f24\u5fc3\u7684\u4e8b\u3002 \u3000\u3000\u4e07\u572d\u8fde\u8fde\u50ac\u4fc3\uff1a\u201c\u5feb\u95ee\uff01\u751a\u4e48\u4e8b\uff1f\u6211\u65e0\u6709\u4e0d\u8bf4\u201d \u3000\u3000\u621a\u82b3\u4e00\u51db\uff0c\u95ee\u9053\uff1a\u201c\u6211\u7239\u7239\u5462\uff1f\u4f60\u4eec\u628a\u4ed6\u600e\u4e48\u4e86\uff1f\u201d \u3000\u3000\u4e07\u9707\u5c71\u5f3a\u7b11\u9053\uff1a\u201c\u4f60\u95ee\u4f60\u7239\u7239\u7684\u4e8b\uff0c\u6211\u2014\u2014\u6211\u4e5f\u4e0d\u77e5\u9053\u554a\u3002 \u3000\u3000\u54ce\u5537\u2014\u2014\u6211\u5f88\u6302\u5ff5\u8fd9\u4f4d\u8001\u5e08\u5f1f\u2014\u2014\u54ce\u5537\uff01\u5e08\u5144\u5f1f\u53c8\u6210\u4e86\u4eb2\u5bb6\uff0c\u54ce\u5537\uff0c\u597d\u5f97\u5f88\u554a\u3002\u201d \u3000\u3000\u621a\u82b3\u6c89\u7740\u8138\u9053\uff1a\u201c\u8fd9\u5f53\u513f\u518d\u8bf4\u4e9b\u5047\u8bdd\u3002\u66f4\u6709\u751a\u4e48\u7528\u5904\uff1f\u6211\u7239\u7239\u7ed9\u4f60\u5bb3\u6b7b\u4e86\uff0c\u662f\u4e0d\u662f\uff1f\u5bb3\u6b7b\u4ed6\u7684\u6cd5\u513f\uff0c\u5c31\u8ddf\u4f60\u4eec\u5bb3\u6b7b\u5434\u574e\u4e00\u6837\uff0c\u662f\u4e0d\u662f\uff1f\u4f60\u5df2\u5c06\u4ed6\u5c38\u8eab\u780c\u5165\u4e86\u5899\u58c1\uff0c\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u621a\u82b3\u8fde\u95ee\u4e09\u58f0\u201c\u662f\u4e0d\u662f\u201d\uff0c\u4e07\u6c0f\u7236\u5b50\u8fd9\u4e00\u60ca\u5f53\u771f\u975e\u540c\u5c0f\u53ef\uff0c\u6ca1\u6599\u60f3\u5979\u4e0d\u4f46\u77e5\u9053\u81ea\u5df1\u7236\u4eb2\u88ab\u5bb3\uff0c\u8fde\u5434\u574e\u88ab\u6740\u7684\u4e00\u4e8b\u4e5f\u77e5\u9053\u4e86\u3002\u4e07\u572d\u98a4\u58f0\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u600e\u77e5\u9053\uff1f\u201d \u3000\u3000\u4ed6\u8bf4\u201c\u4f60\u600e\u77e5\u9053\u201d\uff0c\u4fbf\u662f\u76f4\u627f\u5176\u4e8b\u3002\u621a\u82b3\u5fc3\u4e2d\u4e00\u9178\uff0c\u6012\u706b\u4e0a\u51b2\uff0c\u4fbf\u60f3\u677e\u624b\u5c06\u89e3\u836f\u6295\u5165\u7a97\u4e0b\u7684\u4e00\u6392\u4e03\u77f3\u7f38\u4e2d\u3002\u4e07\u572d\u773c\u89c1\u60c5\u52bf\u5371\u6025\uff0c\u4f5c\u52bf\u4fbf\u60f3\u6251\u5c06\u4e0a\u53bb\u3002\u4e07\u9707\u5c71\u559d\u9053\uff1a\u201c\u572d\u513f\uff0c\u4e0d\u53ef\u83bd\u649e\uff01\u201d\u4ed6\u77e5\u9053\u5f53\u65f6\u60c5\u666f\u4e4b\u4e0b\uff0c\u5f3a\u62a2\u53ea\u6709\u8bef\u4e8b\u3002 \u3000\u3000\u5ffd\u7136\u95f4\uff0c\u584c\u584c\u584c\u51e0\u58f0\uff0c\u7a7a\u5fc3\u83dc\u8d64\u7740\u811a\uff0c\u4ece\u5c0f\u623f\u4e2d\u5954\u4e86\u51fa\u6765\uff0c\u53eb\u9053\uff1a\u201c\u5988\uff0c\u5988\uff01\u201d\u8981\u6251\u5165\u621a\u82b3\u7684\u6000\u4e2d\u3002 \u3000\u3000\u4e07\u572d\u7075\u673a\u4e00\u52a8\uff0c\u4f38\u51fa\u5de6\u81c2\uff0c\u534a\u8def\u4e0a\u4fbf\u5c06\u5973\u513f\u62b1\u4e86\u8fc7\u6765\uff0c\u53f3\u624b\u6478\u51fa\u5315\u9996\uff0c\u5bf9\u51c6\u5973\u513f\u7684\u5929\u7075\u76d6\uff0c\u559d\u9053\uff1a\u201c\u597d\uff01\u54b1\u4eec\u4e00\u5bb6\u8001\u5c0f\uff0c\u4eca\u65e5\u4fbf\u4e00\u8d77\u6b7b\u4e86\uff0c\u6211\u5148\u6740\u4e86\u7a7a\u5fc3\u83dc\u518d\u8bf4\uff01\u201d \u3000\u3000\u621a\u82b3\u5927\u60ca\uff0c\u5fd9\u53eb\u9053\uff1a\u201c\u5feb\u653e\u5f00\u5979\uff0c\u5173\u5973\u513f\u751a\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u4e07\u572d\u5389\u58f0\u9053\uff1a\u201c\u53cd\u6b63\u5927\u5bb6\u6d3b\u4e0d\u6210\uff0c\u6211\u5148\u6740\u4e86\u7a7a\u5fc3\u83dc\uff01\u201d\u5315\u9996\u5728\u7a7a\u4e2d\u865a\u523a\u51e0\u4e0b\uff0c\u4fbf\u5411\u7a7a\u5fc3\u83dc\u5934\u9876\u523a\u843d\u3002 \u3000\u3000\u621a\u82b3\u9053\uff1a\u201c\u4e0d\uff0c\u4e0d\uff01\u201d\u6251\u8fc7\u6765\u62a2\u6551\uff0c\u4f38\u624b\u6293\u4f4f\u4e07\u572d\u7684\u624b\u8155\u3002 \u3000\u3000\u4e07\u9707\u5c71\u867d\u5728\u5947\u75db\u5f7b\u9aa8\u4e4b\u9645\uff0c\u7a76\u7adf\u9605\u5386\u4e30\u5bcc\uff0c\u89c1\u621a\u82b3\u7ed9\u5f15\u4e86\u8fc7\u6765\uff0c\u5f53\u5373\u624b\u8098\u4e00\u63a2\uff0c\u91cd\u91cd\u649e\u5728\u5979\u8170\u95f4\uff0c\u5939\u624b\u593a\u8fc7\u5979\u624b\u4e2d\u74f7\u74f6\uff0c\u5fd9\u4e0d\u8fed\u7684\u5012\u836f\u6577\u4e0a\u624b\u80cc\u3002\u4e07\u572d\u4e5f\u4f38\u624b\u53bb\u53d6\u89e3\u836f\u3002\u621a\u82b3\u62a2\u8fc7\u5973\u513f\uff0c\u7d27\u7d27\u6402\u5728\u6000\u4e2d\u3002 \u3000\u3000\u4e07\u9707\u5c71\u98de\u8d77\u4e00\u811a\uff0c\u5c06\u5979\u8e22\u5012\uff0c\u968f\u624b\u89e3\u4e0b\u8170\u5e26\uff0c\u5c06\u5979\u53cc\u624b\u53cd\u7f1a\u80cc\u540e\uff0c\u53c8\u5c06\u5979\u4e24\u53ea\u811a\u90fd\u7ed1\u4f4f\u4e86\u3002\u7a7a\u5fc3\u83dc\u5927\u53eb\uff1a\u201c\u5988\uff0c\u5988\uff0c\u5988\u5988\uff01\u201d\u4e07\u9707\u5c71\u53cd\u624b\u4e00\u8bb0\u5df4\u638c\uff0c\u6253\u5f97\u5979\u6655\u4e86\u8fc7\u53bb\uff0c\u4f46\u8fd9\u4e00\u638c\u78b0\u5230\u81ea\u5df1\u80bf\u8d77\u7684\u624b\u80cc\uff0c\u53c8\u662f\u5927\u53eb\u4e00\u58f0\uff1a\u201c\u554a\u54df\uff01\u201d \u3000\u3000\u90a3\u89e3\u836f\u5b9e\u5177\u7075\u6548\uff0c\u4e8c\u4eba\u6577\u836f\u4e4b\u540e\uff0c\u7247\u523b\u95f4\u4f24\u53e3\u4e2d\u4fbf\u6d41\u51fa\u8840\u6c34\uff0c\u75bc\u75db\u6e10\u51cf\uff0c\u53d8\u4e3a\u9ebb\u75d2\uff0c\u518d\u8fc7\u5f97\u4e00\u9635\uff0c\u9ebb\u75d2\u4e5f\u6e10\u6e10\u51cf\u5f31\u3002 \u3000\u3000\u7236\u5b50\u4e8c\u4eba\u5927\u662f\u653e\u5fc3\u3002\u77e5\u9053\u6027\u547d\u662f\u62fe\u56de\u6765\u4e86\uff0c\u89c1\u5230\u623f\u4e2d\u7684\u7eb8\u7247\u5140\u81ea\u5f80\u7a97\u5916\u98de\u53bb\uff0c\u4e24\u4eba\u540c\u58f0\u5927\u53eb\uff1a\u201c\u7cdf\u7cd5\uff01\u201d\u6251\u8fc7\u53bb\u62e6\u963b\u98de\u821e\u7684\u7eb8\u7247\u3002 \u3000\u3000\u4f46\u5730\u4e0b\u7684\u7eb8\u5c51\u5df2\u4e71\u6210\u4e00\u56e2\uff0c\u4e00\u5927\u534a\u6389\u5165\u4e86\u7a97\u5916\u7684\u7f38\u4e2d\uff0c\u6709\u7684\u6b63\u5728\u76d8\u65cb\u8dcc\u843d\u3002\u4e07\u9707\u5c71\u53eb\u9053\uff1a\u201c\u5feb\uff0c\u5feb\uff0c\u5feb\u62a2\uff01\u201d\u4e8c\u4eba\u98de\u6b65\u5954\u4e0b\u697c\u53bb\uff0c\u62da\u547d\u53bb\u6293\u56db\u6563\u98de\u821e\u7684\u788e\u7eb8\u3002\u4f46\u6570\u767e\u7247\u788e\u7eb8\u6709\u7684\u98d8\u98d8\u8361\u8361\u5439\u51fa\u4e86\u56f4\u5899\uff0c\u6709\u7684\u968f\u98ce\u98de\u4e0a\u5929\u3002\u4e8c\u4eba\u4e1c\u5954\u897f\u7a81\uff0c\u72b6\u82e5\u766b\u72c2\uff0c\u5374\u54ea\u91cc\u53c8\u80fd\u6536\u96c6\u788e\u7247\u3001\u4f7f\u5f97\u6495\u788e\u4e86\u7684\u5251\u8c31\u91cd\u5f52\u539f\u72b6\uff1f \u3000\u3000\u4e07\u9707\u5c71\u624b\u4e0a\u75bc\u75db\u867d\u6d88\uff0c\u5fc3\u4e2d\u7684\u4f24\u75db\u5374\u96be\u4ee5\u5f62\u5bb9\uff0c\u6c14\u65e0\u53ef\u6d88\uff0c\u5927\u58f0\u65a5\u9a82\u513f\u5b50\uff1a\u201c\u90fd\u662f\u4f60\u8fd9\u5c0f\u8d3c\uff0c\u8ddf\u6211\u6765\u4e89\u593a\u751a\u4e48\uff1f\u82e5\u4e0d\u662f\u4f60\u8ddf\u6211\u62c9\u626f\uff0c\u5251\u8c31\u600e\u4f1a\u626f\u70c2\uff1f\u201d\u4e07\u572d\u53f9\u4e86\u53e3\u6c14\uff0c\u4e0d\u518d\u53bb\u8ffd\u62a2\u788e\u7eb8\uff0c\u8bf4\u9053\uff1a\u201c\u5b69\u513f\u82e5\u4e0d\u62e6\u963b\uff0c\u7239\u7239\u65e9\u5c06\u8fd9\u5251\u8c31\u626f\u5f97\u66f4\u52a0\u70c2\u4e86\u3002\u201d \u3000\u3000\u4e07\u9707\u5c71\u9053\uff1a\u201c\u653e\u5c41\uff01\u201d\u4ed6\u5fc3\u4e2d\u77e5\u9053\u513f\u5b50\u6240\u8bf4\u662f\u5b9e\uff0c\u4f46\u8fd8\u662f\u4e0d\u4f4f\u7684\u547c\u559d\uff1a\u201c\u653e\u5c41\uff0c\u653e\u5c41\uff0c\u653e\u5c41\uff01\u201d \u3000\u3000\u4e07\u572d\u9053\uff1a\u201c\u597d\u5728\u54b1\u4eec\u77e5\u9053\u90a3\u5730\u65b9\u662f\u5728\u6c5f\u9675\u57ce\u5357\uff0c\u518d\u5230\u90a3\u672c\u6b8b\u7834\u7684\u5251\u8c31\u4e2d\u53bb\u67e5\u67e5\uff0c\u53ea\u8981\u80fd\u518d\u627e\u4e9b\u7ebf\u7d22\uff0c\u672a\u59cb\u4e0d\u80fd\u627e\u5230\u90a3\u4e2a\u5730\u65b9\u3002\u201d\u4e07\u9707\u5c71\u7cbe\u795e\u4e00\u632f\uff0c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u90a3\u5730\u65b9\u662f\u5728\u2018\u6c5f\u9675\u57ce\u5357\u2019\u2026\u2026\u201d \u3000\u3000\u5ffd\u542c\u5f97\u5899\u5916\u6709\u4e2a\u58f0\u97f3\u8f7b\u8f7b\u7684\u9053\uff1a\u201c\u6c5f\u9675\u57ce\u5357\uff01\u201d \u3000\u3000\u4e07\u6c0f\u7236\u5b50\u5927\u5403\u4e00\u60ca\uff0c\u4e00\u9f50\u8dc3\u4e0a\u5899\u5934\uff0c\u5411\u5916\u671b\u53bb\uff0c\u53ea\u89c1\u4e24\u4e2a\u4eba\u7684\u80cc\u5f71\u6b63\u5411\u5c0f\u5df7\u4e2d\u9690\u6ca1\u3002 \u3000\u3000\u4e07\u572d\u559d\u9053\uff1a\u201c\u535c\u57a3\u3001\u6c88\u57ce\uff0c\u7ad9\u7740\u522b\u52a8\uff01\u201d \u3000\u3000\u4f46\u90a3\u4e24\u4eba\u65e2\u4e0d\u56de\u5934\uff0c\u4e5f\u4e0d\u7ad9\u4f4f\uff0c\u98de\u5feb\u7684\u8d70\u4e86\u3002\u4e07\u9707\u5c71\u5f85\u8981\u4e0b\u5899\u8ffd\u53bb\uff0c\u4e07\u572d\u9053\uff1a\u201c\u7239\uff0c\u697c\u4e0a\u8fd8\u6709\u2026\u2026\u8fd8\u6709\u90a3\u2026\u2026\u90a3\u6deb\u5987\u3002\u201d \u3000\u3000\u4e07\u9707\u5c71\u8f6c\u5ff5\u4e00\u60f3\uff0c\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u7236\u5b50\u4fe9\u56de\u5230\u697c\u5934\uff0c\u53ea\u89c1\u5c0f\u5973\u5b69\u7a7a\u5fc3\u83dc\u5df2\u9192\u4e86\u8fc7\u6765\uff0c\u62b1\u4f4f\u4e86\u5988\u5988\u76f4\u54ed\u3002\u621a\u82b3\u624b\u8db3\u88ab\u7ed1\uff0c\u5374\u5728\u4e0d\u4f4f\u5b89\u629a\u5973\u513f\u3002\u7a7a\u5fc3\u83dc\u89c1\u5230\u7956\u7236\u4e0e\u7236\u4eb2\u56de\u6765\uff0c\u66f4\u201c\u54c7\u201d\u7684\u4e00\u58f0\uff0c\u60ca\u54ed\u8d77\u6765\u3002 \u3000\u3000\u4e07\u9707\u5c71\u4e0a\u524d\u4e00\u811a\uff0c\u8e22\u5728\u5979\u5c41\u80a1\u4e4b\u4e0a\uff0c\u9a82\u9053\uff1a\u201c\u518d\u54ed\uff0c\u4e00\u5200\u5256\u5f00\u4f60\u5c0f\u9b3c\u7684\u809a\u5b50\u3002\u201d\u7a7a\u5fc3\u83dc\u5413\u5f97\u8138\u90fd\u767d\u4e86\uff0c\u54ea\u91cc\u8fd8\u6562\u51fa\u58f0\u3002 \u3000\u3000\u4e07\u572d\u4f4e\u58f0\u9053\uff1a\u201c\u7239\uff0c\u8fd9\u6deb\u5987\u751a\u4e48\u90fd\u77e5\u9053\u4e86\uff0c\u53ef\u4e0d\u80fd\u7559\u4e0b\u6d3b\u53e3\u3002\u600e\u751f\u5904\u7f6e\u5979\u624d\u662f\uff1f\u201d\u4e07\u9707\u5c71\u5fae\u4e00\u6c89\u541f\uff0c\u9053\uff1a\u201c\u521a\u624d\u5899\u5916\u4e8c\u4eba\uff0c\u4f60\u770b\u6e05\u695a\u662f\u535c\u57a3\u3001\u6c88\u57ce\u4e48\uff1f\u201d\u4e07\u572d\u9053\uff1a\u201c\u6b63\u662f\u90a3\u4e8c\u4eba\uff0c\u9519\u4e0d\u4e86\uff01\u53ea\u6015\u79d8\u5bc6\u5df2\u7ecf\u6cc4\u6f0f\uff0c\u4ed6\u4eec\u77e5\u9053\u662f\u5728\u6c5f\u9675\u57ce\u5357\u3002\u201d\u4e07\u9707\u5c71\u9053\uff1a\u201c\u4e8b\u4e0d\u5b9c\u8fdf\uff0c\u987b\u5f97\u6025\u901f\u4e0b\u624b\u3002\u8fd9\u6deb\u5987\u4e48\uff0c\u8ddf\u5979\u7236\u4eb2\u4e00\u822c\u5904\u7f6e\u4fbf\u4e86\u3002\u201d \u3000\u3000\u621a\u82b3\u65e9\u5c06\u751f\u6b7b\u7f6e\u4e4b\u5ea6\u5916\uff0c\u53ea\u662f\u653e\u4e0d\u4e0b\u5973\u513f\uff0c\u8bf4\u9053\uff1a\u201c\u4e09\u2026\u2026\u4e09\u54e5\uff0c\u6211\u548c\u4f60\u592b\u59bb\u4e00\u573a\uff0c\u4f60\u6740\u6211\u4e0d\u6253\u7d27\uff0c\u6211\u6b7b\u4e4b\u540e\uff0c\u4f60\u987b\u597d\u597d\u770b\u5f85\u7a7a\u5fc3\u83dc\uff01\u201d \u3000\u3000\u4e07\u572d\u9053\uff1a\u201c\u597d\uff01\u201d\u4e07\u9707\u5c71\u9053\uff1a\u201c\u65a9\u8349\u9664\u6839\uff0c\u5c82\u80fd\u7559\u4e0b\u7978\u80ce\uff1f \u3000\u3000\u8fd9\u5c0f\u5973\u5b69\u7cbe\u7075\u53e4\u602a\uff0c\u4eca\u65e5\u4e4b\u4e8b\u90fd\u7ed9\u5979\u77a7\u5728\u773c\u91cc\u4e86\uff0c\u600e\u4fdd\u5f97\u5b9a\u5979\u4e0d\u8bf4\u51fa\u6765\uff1f\u201d\u4e07\u572d\u7f13\u7f13\u70b9\u4e86\u70b9\u5934\u3002\u4ed6\u5f88\u75bc\u7231\u8fd9\u4e2a\u5973\u513f\uff0c\u4f46\u7236\u4eb2\u7684\u8bdd\u4e5f\u5f88\u5bf9\uff0c\u82e5\u662f\u7559\u4e0b\u7978\u80ce\uff0c\u5c06\u6765\u5b9a\u6709\u6781\u5927\u540e\u60a3\u3002 \u3000\u3000\u621a\u82b3\u6cea\u6c34\u6eda\u4e0b\u53cc\u988a\uff0c\u54fd\u54bd\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u4eec\u597d\u72e0\u5fc3\uff0c\u8fde\u2026\u2026\u8fde\u8fd9\u4e2a\u5c0f\u5c0f\u5973\u5b69\u513f\u4e5f\u4e0d\u653e\u8fc7\u5417\uff1f\u201d\u4e07\u9707\u5c71\u9053\uff1a\u201c\u585e\u4f4f\u5979\u7684\u5634\u5df4\uff0c\u522b\u8ba9\u5979\u53eb\u56b7\u8d77\u6765\uff0c\u5435\u5f97\u901a\u5929\u4e0b\u7684\u4eba\u90fd\u77e5\u9053\u4e86\uff01\u201d \u3000\u3000\u621a\u82b3\u60f3\u8d77\u5973\u513f\u96be\u4fdd\u4e00\u547d\uff0c\u7a81\u7136\u63d0\u8d77\u55d3\u5b50\uff0c\u5927\u53eb\uff1a\u201c\u6551\u547d\uff0c\u6551\u547d\uff01\u201d \u3000\u3000\u9759\u591c\u4e4b\u4e2d\uff0c\u8fd9\u4e24\u58f0\u201c\u6551\u547d\u201d\u5212\u7834\u4e86\u957f\u7a7a\uff0c\u8fdc\u8fdc\u4f20\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u4e07\u572d\u6251\u5230\u5979\u8eab\u4e0a\uff0c\u4f38\u624b\u6309\u4f4f\u5979\u5634\u3002\u621a\u82b3\u4ecd\u662f\u5927\u53eb\uff1a\u201c\u6551\u547d\uff0c\u6551\u547d\uff01\u201d\u53ea\u662f\u5634\u5df4\u88ab\u6309\u4f4f\u4e86\uff0c\u58f0\u97f3\u90c1\u95f7\u3002\u4e07\u9707\u5c71\u5728\u513f\u5b50\u957f\u888d\u4e0a\u6495\u4e0b\u4e00\u5757\u8863\u895f\uff0c\u9012\u7ed9\u4e86\u4ed6\uff0c\u4e07\u572d\u5f53\u5373\u5c06\u8863\u895f\u585e\u5728\u621a\u82b3\u53e3\u4e2d\u3002\u4e07\u9707\u5c71\u9053\uff1a\u201c\u5c06\u5979\u57cb\u5728\u621a\u957f\u53d1\u7684\u5893\u4e2d\uff0c\u7236\u5973\u540c\u7a74\uff0c\u6700\u5999\u4e0d\u8fc7\u3002\u201d \u3000\u3000\u4e07\u572d\u70b9\u4e86\u70b9\u5934\uff0c\u62b1\u8d77\u59bb\u5b50\uff0c\u5927\u8e0f\u6b65\u4e0b\u697c\u3002\u4e07\u9707\u5c71\u62b1\u4e86\u7a7a\u5fc3\u83dc\u3002\u56db\u4e2a\u4eba\u8fdb\u4e86\u4e66\u623f\u3002 \u3000\u3000\u621a\u82b3\u77a7\u7740\u4e66\u623f\u897f\u58c1\u7684\u90a3\u5835\u767d\u5899\uff0c\u5fc3\u60f3\uff1a\u201c\u6211\u7239\u7239\u662f\u7ed9\u8001\u8d3c\u846c\u5728\u8fd9\u5835\u5899\u4e4b\u4e2d\uff1f\u201d \u3000\u3000\u4e07\u9707\u5c71\u9053\uff1a\u201c\u6211\u6765\u62c6\u5899\uff0c\u4f60\u53bb\u5c06\u5434\u574e\u62d6\u6765\uff01\u5c0f\u5fc3\uff0c\u522b\u7ed9\u4eba\u89c1\u5230\u3002\u201d\u4e07\u572d\u5e94\u9053\uff1a\u201c\u662f\uff01\u201d\u5954\u5411\u4e07\u9707\u5c71\u7684\u5367\u5ba4\u3002 \u3000\u3000\u4e07\u9707\u5c71\u62c9\u5f00\u4e66\u684c\u7684\u62bd\u5c49\uff0c\u5176\u4e2d\u51ff\u5b50\u3001\u9524\u5b50\u3001\u94f2\u5200\u7b49\u5de5\u5177\u4e00\u5e94\u4ff1\u5168\uff0c\u4ed6\u53d6\u51fa\u6765\u653e\u5728\u5899\u8fb9\uff0c\u77a7\u7740\u90a3\u5835\u767d\u5899\uff0c\u53cc\u624b\u6413\u4e86\u51e0\u4e0b\uff0c\u56de\u5934\u5411\u621a\u82b3\u671b\u4e86\u4e00\u773c\uff0c\u8138\u4e0a\u73b0\u51fa\u5341\u5206\u5f97\u610f\u7684\u795e\u60c5\u3002\u621a\u82b3\u4e0d\u7981\u6253\u4e86\u4e2a\u5bd2\u5664\u3002\u4e07\u9707\u5c71\u62ff\u8d77\u94c1\u9524\u548c\u51ff\u5b50\uff0c\u770b\u597d\u4e86\u5899\u4e0a\u7684\u90e8\u4f4d\uff0c\u5728\u4e24\u5757\u7816\u5934\u4e4b\u95f4\u7684\u7f1d\u4e2d\uff0c\u5c06\u51ff\u5b50\u51ff\u4e86\u8fdb\u53bb\u3002\u51ff\u88c2\u4e86\u4e00\u5757\u7816\u5934\uff0c\u4f38\u624b\u6447\u4e86\u51e0\u6447\uff0c\u4fbf\u6316\u4e86\u51fa\u6765\uff0c\u624b\u6cd5\u751a\u662f\u719f\u7ec3\u3002\u4ed6\u6316\u51fa\u4e00\u5757\u7816\u5934\u540e\uff0c\u62ff\u5230\u9f3b\u5b50\u8fb9\u55c5\u4e86\u51e0\u55c5\u3002 \u3000\u3000\u621a\u82b3\u89c1\u4e86\u4ed6\u6316\u5899\u7684\u624b\u6cd5\uff0c\u60f3\u8d77\u9002\u624d\u89c1\u5230\u4ed6\u79bb\u9b42\u75c5\u53d1\u4f5c\u65f6\u6316\u5899\u3001\u63a8\u5c38\u3001\u780c\u5899\u7684\u60c5\u72b6\uff0c\u5fc3\u4e2d\u5df2\u662f\u53d1\u6bdb\uff0c\u5f85\u89c1\u5230\u4ed6\u53bb\u55c5\u5939\u5899\u4e2d\u7236\u4eb2\u5c38\u4f53\u7684\u6c14\u606f\uff0c\u53c8\u662f\u5bb3\u6015\uff0c\u53c8\u662f\u4f24\u5fc3\uff0c\u53c8\u662f\u6124\u6012\uff0c\u7834\u53e3\u5927\u9a82\uff1a\u201c\u4f60\u8fd9\u5978\u8d3c\uff0c\u65e0\u803b\u7684\u8001\u8d3c\uff01\u201d\u53ea\u662f\u5634\u5df4\u88ab\u585e\u4f4f\u4e86\uff0c\u53ea\u80fd\u53d1\u51fa\u4e9b\u545c\u545c\u4e4b\u58f0\u3002 \u3000\u3000\u4e07\u9707\u5c71\u4f38\u624b\u53c8\u53bb\u6316\u7b2c\u4e8c\u5757\u7816\u5934\uff0c\u7a81\u7136\u811a\u6b65\u58f0\u6025\uff0c\u4e07\u572d\u8e09\u8dc4\u62a2\u8fdb\uff0c\u8bf4\u9053\uff1a\u201c\u7239\uff0c\u7239\uff01\u4e0d\u597d\u4e86\uff0c\u5434\u574e\u2026\u2026\u5434\u574e\u2026\u2026\u201d\u8eab\u5b50\u5728\u684c\u4e0a\u4e00\u649e\uff0c\u545b\u5577\u4e00\u58f0\u54cd\uff0c\u6cb9\u706f\u6389\u5728\u5730\u4e0b\uff0c\u5ba4\u4e2d\u767b\u65f6\u9ed1\u4e86\uff0c\u53ea\u6709\u6de1\u6de1\u7684\u6708\u5149\u4ece\u7a97\u7eb8\u4e2d\u900f\u8fdb\u6765\u3002 \u3000\u3000\u4e07\u9707\u5c71\u9053\uff1a\u201c\u5434\u574e\u600e\u6837\uff1f\u5927\u60ca\u5c0f\u602a\u7684\uff0c\u8fd9\u822c\u6c89\u4e0d\u4f4f\u6c14\u3002\u201d\u4e07\u572d\u9053\uff1a\u201c\u5434\u574e\u4e0d\u89c1\u5566\uff01\u201d\u4e07\u9707\u5c71\u9a82\u9053\uff1a\u201c\u653e\u5c41\uff01\u600e\u4f1a\u4e0d\u89c1\uff1f\u201d\u4f46\u58f0\u97f3\u98a4\u6296\uff0c\u663e\u7136\u5fc3\u4e2d\u60e7\u610f\u751a\u76db\u3002\u62cd\u7684\u4e00\u58f0\uff0c\u624b\u4e2d\u62ff\u7740\u7684\u4e00\u5757\u7816\u5934\u6389\u4e0b\u5730\u6765\u3002 \u3000\u3000\u4e07\u572d\u9053\uff1a\u201c\u6211\u4f38\u624b\u5230\u7239\u7239\u7684\u5e8a\u5e95\u4e0b\u53bb\u62c9\u5c38\u4f53\uff0c\u6478\u4ed6\u4e0d\u5230\uff0c\u70b9\u4e86\u706f\u706b\u5230\u5e8a\u5e95\u53bb\u7167\uff0c\u5c38\u4f53\u5df2\u5f71\u8e2a\u5168\u65e0\u3002\u7239\u7239\u623f\u4e2d\u5e10\u5b50\u80cc\u540e\u3001\u7bb1\u5b50\u540e\u9762\uff0c\u5230\u5904\u90fd\u627e\u8fc7\u4e86\uff0c\u751a\u4e48\u4e5f\u6ca1\u89c1\u5230\u3002\u201d\u4e07\u9707\u5c71\u6c89\u541f\u9053\uff1a\u201c\u8fd9\u2026\u2026\u8fd9\u53ef\u5947\u4e86\u3002\u6211\u731c\u60f3\u662f\u535c\u57a3\u3001\u6c88\u57ce\u4ed6\u4eec\u6405\u7684\u9b3c\u3002\u201d\u4e07\u572d\u9053\uff1a\u201c\u7239\uff0c\u83ab\u975e\u2026\u2026\u83ab\u975e\u2026\u2026\u5434\u574e\u8fd9\u53ae\u6ca1\u6b7b\u900f\uff0c\u95ed\u6c14\u534a\u664c\uff0c\u53c8\u6d3b\u4e86\u8fc7\u6765\uff1f\u201d\u4e07\u9707\u5c71\u6012\u9053\uff1a\u201c\u653e\u5c41\uff0c\u4f60\u8001\u5b50\u5916\u53f7\u53eb\u4f5c\u2018\u4e94\u4e91\u624b\u2019\uff0c\u624b\u4e0a\u529f\u592b\u4f55\u7b49\u5389\u5bb3\uff0c\u96be\u9053\u627c\u4e00\u4e2a\u5f92\u5f1f\u4e5f\u627c\u4e0d\u6b7b\uff1f\u201d\u4e07\u572d\u9053\uff1a\u201c\u662f\uff0c\u6309\u7406\u8bf4\uff0c\u5434\u574e\u90a3\u53ae\u5b9a\u662f\u7ed9\u7239\u7239\u627c\u6b7b\u4e86\uff0c\u5374\u4e0d\u77e5\u5982\u4f55\uff0c\u5c38\u4f53\u7adf\u7136\u4f1a\u4e0d\u89c1\u4e86\uff1f\u96be\u9053\u2026\u2026\u96be\u9053\u2026\u2026\u201d\u4e07\u9707\u5c71\u9053\uff1a\u201c\u96be\u9053\u751a\u4e48\uff1f\u201d\u4e07\u572d\u9053\uff1a\u201c\u96be\u9053\u771f\u6709\u50f5\u5c38\uff1f\u4ed6\u7075\u9b42\u4e0d\u606f\u2026\u2026\u201d \u3000\u3000\u4e07\u9707\u5c71\u559d\u9053\uff1a\u201c\u522b\u80e1\u601d\u4e71\u60f3\u4e86\uff01\u54b1\u4eec\u5feb\u5904\u7f6e\u4e86\u8fd9\u6deb\u5987\u548c\u8fd9\u5c0f\u9b3c\uff0c\u518d\u53bb\u627e\u5434\u574e\u7684\u5c38\u9996\u3002\u4e8b\u60c5\u53ea\u6015\u5df2\u95f9\u7a7f\u4e86\uff0c\u54b1\u7236\u5b50\u5728\u8346\u5dde\u57ce\u5df2\u96be\u4ee5\u5b89\u8eab\u3002\u201d\u8bf4\u7740\u52a0\u7d27\u5c06\u5899\u4e0a\u7816\u5934\u4e00\u5757\u5757\u6316\u51fa\u6765\u3002\u4ed6\u7761\u68a6\u4e2d\u6316\u7816\u780c\u5899\uff0c\u505a\u4e4b\u5df2\u60ef\uff0c\u624b\u6cd5\u719f\u7ec3\uff0c\u6b64\u65f6\u867d\u65e0\u706f\u70db\uff0c\u52a8\u4f5c\u4ecd\u662f\u5341\u5206\u8fc5\u6377\u3002 \u3000\u3000\u4e07\u572d\u5e94\u4e86\u58f0\uff1a\u201c\u662f\uff01\u201d\u62d4\u5200\u5728\u624b\uff0c\u8d70\u5230\u621a\u82b3\u8eab\u524d\uff0c\u98a4\u58f0\u9053\uff1a\u201c\u82b3\u59b9\uff0c\u662f\u4f60\u5bf9\u4e0d\u8d77\u6211\u3002\u4f60\u6b7b\u4e4b\u540e\uff0c\u53ef\u522b\u6028\u6211\uff01\u201d \u3000\u3000\u621a\u82b3\u65e0\u6cd5\u8bf4\u8bdd\uff0c\u4fa7\u8fc7\u8eab\u5b50\uff0c\u7528\u80a9\u5934\u72e0\u72e0\u649e\u4e86\u4ed6\u4e00\u4e0b\u3002\u4e07\u6c0f\u7236\u5b50\u8981\u6740\u81ea\u5df1\uff0c\u90a3\u4e5f\u7f62\u4e86\uff0c\u7adf\u8fde\u7a7a\u5fc3\u83dc\u4e5f\u4e0d\u80af\u9976\uff0c\u72fc\u5fc3\u72d7\u80ba\uff0c\u5b9e\u662f\u4e16\u6240\u7f55\u6709\u3002\u4e07\u572d\u7ed9\u5979\u4e00\u649e\uff0c\u8eab\u5b50\u4e00\u6643\uff0c\u9000\u540e\u4e24\u6b65\uff0c\u4e3e\u8d77\u5200\u6765\uff0c\u9a82\u9053\uff1a\u201c\u8d3c\u6deb\u5987\uff0c\u6b7b\u5230\u4e34\u5934\uff0c\u8fd8\u8981\u653e\u6cfc\uff01\u201d \u3000\u3000\u4fbf\u5728\u6b64\u65f6\uff0c\u53ea\u542c\u5f97\u683c\u3001\u683c\u3001\u683c\u51e0\u4e0b\u58f0\u54cd\uff0c\u4e66\u623f\u95e8\u7f13\u7f13\u63a8\u5f00\u3002\u4e07\u572d\u5403\u4e86\u4e00\u60ca\uff0c\u8f6c\u8fc7\u5934\u53bb\uff0c\u60e8\u6de1\u7684\u6708\u5149\u4e4b\u4e0b\uff0c\u4f46\u89c1\u623f\u95e8\u63a8\u5f00\uff0c\u5374\u4e0d\u89c1\u6709\u4eba\u8fdb\u6765\u3002 \u3000\u3000\u4e07\u9707\u5c71\u559d\u95ee\uff1a\u201c\u662f\u8c01\uff1f\u201d \u3000\u3000\u623f\u95e8\u53c8\u683c\u683c\u3001\u683c\u683c\u7684\u54cd\u4e86\u4e24\u4e0b\uff0c\u4ecd\u662f\u65e0\u4eba\u56de\u7b54\u3002 \u3000\u3000\u5fae\u5149\u4e4b\u4e0b\uff0c\u7a81\u89c1\u95e8\u4e2d\u8df3\u8fdb\u4e00\u4e2a\u4eba\u6765\u3002\u90a3\u4eba\u76f4\u633a\u633a\u7684\u79fb\u8fd1\uff0c\u4e00\u8df3\u4e00\u8df3\u7684\uff0c\u819d\u76d6\u4e0d\u5f2f\u3002\u4e07\u9707\u5c71\u548c\u4e07\u572d\u90fd\u662f\u5927\u9a87\uff0c\u4e0d\u81ea\u7981\u7684\u9000\u540e\u4e86\u4e24\u6b65\u3002 \u3000\u3000\u53ea\u89c1\u90a3\u4eba\u53cc\u773c\u5927\u7741\uff0c\u820c\u5934\u4f38\u51fa\uff0c\u53e3\u9f3b\u6d41\u8840\uff0c\u6b63\u662f\u7ed9\u4e07\u9707\u5c71\u627c\u6b7b\u4e86\u7684\u5434\u574e\u3002\u4e07\u9707\u5c71\u548c\u4e07\u572d\u540c\u58f0\u60ca\u547c\uff1a\u201c\u554a\uff01\u201d\u621a\u82b3\u89c1\u5230\u8fd9\u822c\u53ef\u6016\u7684\u60c5\u72b6\uff0c\u4e5f\u5413\u5f97\u4e00\u9897\u5fc3\u4f3c\u4e4e\u505c\u4e86\u8df3\u52a8\u3002 \u3000\u3000\u5434\u574e\u4e00\u52a8\u4e5f\u4e0d\u52a8\uff0c\u53cc\u81c2\u7f13\u7f13\u62ac\u8d77\uff0c\u4f38\u5411\u4e07\u9707\u5c71\u3002 \u3000\u3000\u4e07\u9707\u5c71\u559d\u9053\uff1a\u201c\u5434\u574e\u5c0f\u8d3c\uff0c\u8001\u5b50\u6015\u2026\u2026\u6015\u2026\u2026\u4f60\u8fd9\u50f5\u5c38\uff1f\u201d \u3000\u3000\u62bd\u51fa\u5200\u6765\uff0c\u5411\u5434\u574e\u5934\u4e0a\u5288\u843d\u3002\u7a81\u89c9\u624b\u8155\u4e00\u9ebb\uff0c\u5355\u5200\u62ff\u634f\u4e0d\u5b9a\uff0c\u545b\u5577\u4e00\u58f0\uff0c\u6389\u5728\u5730\u4e0b\uff0c\u8ddf\u7740\u8170\u95f4\u4e00\u9ebb\uff0c\u5168\u8eab\u4fbf\u52a8\u5f39\u4e0d\u5f97\u3002 \u3000\u3000\u4e07\u572d\u65e9\u5413\u5f97\u5446\u4e86\uff0c\u89c1\u5434\u574e\u7684\u50f5\u5c38\u6405\u5012\u4e86\u7236\u4eb2\u540e\uff0c\u53c8\u76f4\u7740\u53cc\u81c2\uff0c\u7f13\u7f13\u5411\u81ea\u5df1\u6293\u6765\uff0c\u53ea\u60f3\u5927\u53eb\uff1a\u201c\u5434\u5e08\u5f1f\uff0c\u5434\u5e08\u5f1f\uff01\u9976\u4e86\u6211\uff01\u201d\u53ef\u662f\u58f0\u97f3\u5728\u5589\u5934\u54fd\u4f4f\u4e86\uff0c\u65e0\u8bba\u5982\u4f55\u53eb\u4e0d\u51fa\u6765\uff0c\u5012\u9000\u4e86\u4e24\u6b65\uff0c\u817f\u4e0b\u4e00\u8f6f\uff0c\u6454\u5012\u5728\u5730\u3002\u53ea\u89c1\u5434\u574e\u7684\u53f3\u624b\u5782\u4e86\u4e0b\u6765\uff0c\u6478\u5230\u4ed6\u8138\u4e0a\uff0c\u624b\u6307\u51b7\u51b0\u51b0\u5730\uff0c\u6ca1\u534a\u5206\u6696\u6c14\u3002\u4e07\u572d\u5413\u5f97\u9b42\u98de\u9b44\u6563\uff0c\u5dee\u4e00\u70b9\u5c31\u6655\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u7a81\u7136\u4e4b\u95f4\uff0c\u5434\u574e\u8eab\u5b50\u5411\u524d\u4e00\u6251\uff0c\u4f0f\u5728\u4e07\u572d\u7684\u8eab\u4e0a\uff0c\u4e00\u52a8\u4e5f\u4e0d\u52a8\u4e86\u3002 \u3000\u3000\u5434\u574e\u8eab\u540e\uff0c\u5374\u7ad9\u7740\u4e00\u4eba\u3002 \u3000\u3000\u90a3\u4eba\u8d70\u5230\u621a\u82b3\u8eab\u8fb9\uff0c\u53d6\u51fa\u5979\u53e3\u4e2d\u585e\u7740\u7684\u7834\u5e03\uff0c\u53cc\u624b\u51e0\u4e0b\u62c9\u626f\uff0c\u4fbf\u626f\u65ad\u4e86\u7ed1\u4f4f\u5979\u624b\u8db3\u7684\u7ef3\u5b50\uff0c\u56de\u8fc7\u8eab\u53bb\uff0c\u5728\u4e07\u572d\u8170\u91cc\u91cd\u91cd\u8e22\u4e86\u4e00\u811a\uff0c\u5185\u529b\u5230\u5904\uff0c\u4e07\u572d\u767b\u65f6\u5168\u8eab\u9178\u8f6f\u3002 \u3000\u3000\u621a\u82b3\u5148\u5c06\u7a7a\u5fc3\u83dc\u62b1\u8d77\uff0c\u98a4\u58f0\u9053\uff1a\u201c\u6069\u516c\u662f\u8c01\uff0c\u6551\u4e86\u6211\u7684\u6027\u547d\uff1f\u201d \u3000\u3000\u90a3\u4eba\u53cc\u624b\u4f38\u51fa\uff0c\u6708\u5149\u4e4b\u4e0b\uff0c\u53ea\u89c1\u4ed6\u6bcf\u53ea\u624b\u638c\u4e2d\u90fd\u6709\u4e00\u53ea\u82b1\u7eb8\u526a\u6210\u7684\u8774\u8776\uff0c\u6b63\u662f\u90a3\u672c\u300a\u5510\u8bd7\u300b\u5939\u7740\u7684\u7eb8\u8776\uff0c\u9002\u624d\u98d8\u4e0b\u697c\u53bb\u65f6\u7ed9\u4ed6\u62ff\u5230\u4e86\u7684\u3002\u621a\u82b3\u4e00\u77a5\u773c\u95f4\uff0c\u89c1\u5230\u4ed6\u53f3\u624b\u4e94\u6839\u624b\u6307\u5168\u65e0\uff0c\u5931\u58f0\u53eb\u9053\uff1a\u201c\u72c4\u5e08\u54e5\uff01\u201d \u3000\u3000\u90a3\u4eba\u6b63\u662f\u72c4\u4e91\uff0c\u6597\u7136\u95f4\u542c\u5230\u8fd9\u4e00\u58f0\u201c\u72c4\u5e08\u54e5\uff01\u201d\u80f8\u4e2d\u4e00\u70ed\uff0c\u5fcd\u4e0d\u4f4f\u773c\u6cea\u4fbf\u8981\u593a\u7736\u800c\u51fa\uff0c\u53eb\u9053\uff1a\u201c\u82b3\u59b9\uff01\u5929\u53ef\u601c\u89c1\uff0c\u4f60\u2026\u2026\u4f60\u6211\u4eca\u65e5\u53c8\u518d\u76f8\u89c1\uff01\u201d \u3000\u3000\u621a\u82b3\u6b64\u65f6\u6b63\u5982\u4e00\u53f6\u5c0f\u821f\u5728\u832b\u832b\u5927\u6d77\u4e2d\u98d8\u884c\uff0c\u72c2\u98ce\u66b4\u96e8\u4ea4\u52a0\u4e4b\u4e0b\uff0c\u7a81\u7136\u9a76\u8fdb\u4e86\u4e00\u4e2a\u98ce\u5e73\u6d6a\u9759\u7684\u6e2f\u53e3\uff0c\u6251\u5728\u72c4\u4e91\u6000\u4e2d\uff0c\u8bf4\u9053\uff1a\u201c\u5e08\u54e5\uff0c\u8fd9\u2026\u2026\u8fd9\u2026\u2026\u8fd9\u4e0d\u662f\u505a\u68a6\u4e48\uff1f\u201d \u3000\u3000\u72c4\u4e91\u9053\uff1a\u201c\u4e0d\u662f\u505a\u68a6\uff0c\u82b3\u59b9\uff0c\u8fd9\u4e24\u665a\u6211\u90fd\u5728\u8fd9\u91cc\u77a7\u7740\u3002\u8fd9\u7236\u5b50\u4e24\u4eba\u5e72\u7684\u90a3\u4e9b\u4f24\u5929\u5bb3\u7406\u4e8b\u60c5\uff0c\u6211\u5168\u90fd\u77a7\u89c1\u4e86\u3002\u5434\u574e\u7684\u5c38\u4f53\uff0c\u54fc\uff0c\u6211\u662f\u62ff\u6765\u5413\u4ed6\u4eec\u4e00\u5413\uff01\u201d \u3000\u3000\u621a\u82b3\u53eb\u9053\uff1a\u201c\u7239\u7239\uff0c\u7239\u7239\uff01\u201d\u653e\u4e0b\u7a7a\u5fc3\u83dc\uff0c\u5954\u5230\u5899\u6d1e\u4e4b\u524d\uff0c\u4f38\u624b\u5f80\u6d1e\u4e2d\u6478\u53bb\uff0c\u5374\u6478\u4e86\u4e2a\u7a7a\uff0c\u201c\u554a\u201d\u7684\u4e00\u58f0\u53eb\uff0c\u98a4\u58f0\u9053\uff1a\u201c\u6ca1\u2026\u2026\u6ca1\u6709\uff01\u201d \u3000\u3000\u72c4\u4e91\u6253\u4eae\u4e86\u706b\u6298\uff0c\u5230\u5899\u6d1e\u4e2d\u53bb\u7167\u65f6\uff0c\u53ea\u89c1\u5939\u5899\u4e2d\u5c3d\u662f\u4e9b\u6ce5\u7070\u7816\u77f3\uff0c\u5374\u54ea\u91cc\u6709\u621a\u957f\u53d1\u7684\u5c38\u4f53\uff1f\u8bf4\u9053\uff1a\u201c\u8fd9\u91cc\u6ca1\u6709\uff0c\u751a\u4e48\u4e5f\u6ca1\u6709\u3002\u201d \u3000\u3000\u621a\u82b3\u5728\u4e07\u9707\u5c71\u5e8a\u5934\u62ff\u8fc7\u4e00\u4e2a\u70db\u53f0\uff0c\u5728\u72c4\u4e91\u7684\u706b\u6298\u4e0a\u70b9\u71c3\u4e86\u8721\u70db\uff0c\u4e3e\u8d77\u70db\u53f0\uff0c\u5728\u5939\u5899\u4e2d\u7ec6\u7ec6\u5bdf\u770b\uff0c\u54ea\u91cc\u6709\u7236\u4eb2\u7684\u5c38\u4f53\uff0c\u8c01\u7684\u5c38\u4f53\u4e5f\u6ca1\u6709\uff0c\u5979\u53c8\u607c\u53c8\u559c\uff0c\u5fc3\u4e2d\u5b58\u4e86\u4e00\u7ebf\u5e0c\u671b\uff1a\u201c\u6216\u8bb8\uff0c\u7239\u7239\u5e76\u6ca1\u6709\u7ed9\u4ed6\u4eec\u5bb3\u6b7b\u3002\u201d\u8f6c\u8eab\u5411\u4e07\u572d\u9053\uff1a\u201c\u4e09\u2026\u2026\u4e09\u54e5\uff0c\u6211\u7239\u7239\u5230\u5e95\u600e\u6837\u4e86\uff1f\u201d \u3000\u3000\u4e07\u572d\u548c\u4e07\u9707\u5c71\u5374\u4e0d\u77e5\u5979\u5728\u5939\u5899\u4e2d\u5e76\u672a\u53d1\u89c1\u5c38\u4f53\uff0c\u53ea\u9053\u5979\u89c1\u4e86\u7236\u4eb2\u7684\u9057\u4f53\uff0c\u4fbf\u8981\u52a8\u624b\u590d\u4ec7\u3002\u4e07\u9707\u5c71\u6602\u7136\u9053\uff1a\u201c\u5927\u4e08\u592b\u4e00\u8eab\u505a\u4e8b\u4e00\u8eab\u5f53\uff0c\u621a\u957f\u53d1\u662f\u6211\u6740\u7684\uff0c\u4f60\u51b2\u7740\u6211\u62a5\u4ec7\u4fbf\u662f\u3002\u201d \u3000\u3000\u621a\u82b3\u9053\uff1a\u201c\u7239\u7239\u771f\u7684\u7ed9\u4f60\u5bb3\u6b7b\u4e86\uff1f\u90a3\u4e48\u2026\u2026\u4ed6\u7684\u5c38\u9996\u5462\uff1f\u201d \u3000\u3000\u4e07\u9707\u5c71\u9053\uff1a\u201c\u751a\u4e48\uff1f\u5939\u5899\u91cc\u7684\u6b7b\u4eba\u96be\u9053\u4e0d\u662f\u4ed6\uff1f\u201d\u621a\u82b3\u9053\uff1a\u201c\u8fd9\u91cc\u6709\u751a\u4e48\u6b7b\u4eba\uff1f\u201d\u4e07\u9707\u5c71\u548c\u4e07\u572d\u9762\u9762\u76f8\u89d1\uff0c\u8138\u8272\u60e8\u767d\uff0c\u5140\u81ea\u4e0d\u4fe1\u3002\u72c4\u4e91\u62c9\u8d77\u4e07\u9707\u5c71\uff0c\u8ba9\u4ed6\u63a2\u5934\u5230\u5899\u6d1e\u4e2d\u4e00\u770b\u3002 \u3000\u3000\u4e07\u9707\u5c71\u98a4\u58f0\u9053\uff1a\u201c\u4e16\u4e0a\u771f\u2026\u2026\u771f\u6709\u4f1a\u884c\u8d70\u7684\u50f5\u5c38\uff1f\u6211\u2026\u2026\u660e\u660e\u2026\u2026\u660e\u660e\u2026\u2026\u201d\u5ffd\u5730\u6539\u53e3\uff1a\u201c\u597d\u5ab3\u5987\uff0c\u6211\u2026\u2026\u6211\u662f\u9a97\u9a97\u4f60\u7684\u3002\u54b1\u5e08\u5144\u5f1f\u867d\u7136\u4e0d\u548c\u3002\u5374\u4e5f\u4e0d\u81f4\u4e8e\u75db\u4e0b\u6bd2\u624b\u3002\u4f60\u600e\u4e48\u4fe1\u4ee5\u4e3a\u771f\u4e86\uff1f\u54c8\u54c8\uff0c\u54c8\u54c8\u3002\u201d\u4ed6\u5e73\u65f6\u8bf4\u8c0e\u7684\u672c\u9886\u7740\u5b9e\u4e0d\u9519\uff0c\u4f46\u8fd9\u65f6\u60ca\u60f6\u4e4b\u4e0b\uff0c\u5f20\u53e3\u7ed3\u820c\uff0c\u8bf4\u51fa\u6765\u7684\u8c0e\u8bdd\u7275\u5f3a\u4e4b\u81f3\uff0c\u8c01\u4e5f\u4e0d\u4f1a\u76f8\u4fe1\u3002\u8981\u662f\u4ed6\u5014\u5f3a\u633a\u649e\uff0c\u621a\u82b3\u548c\u72c4\u4e91\u8fd8\u5b58\u7740\u4e07\u4e00\u7684\u5e0c\u671b\uff0c\u4ed6\u8fd9\u4e48\u4e00\u8bf4\uff0c\u4e24\u4eba\u53ea\u6709\u66f4\u52a0\u786e\u4fe1\u662f\u4ed6\u5bb3\u6b7b\u4e86\u621a\u957f\u53d1\u3002 \u3000\u3000\u72c4\u4e91\u4f38\u638c\u642d\u5728\u4ed6\u80a9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u4e07\u5e08\u4f2f\uff0c\u4f60\u5bb3\u5f97\u6211\u597d\u82e6\u3002 \u3000\u3000\u8fd9\u4e00\u5207\u4e5f\u4e0d\u5fc5\u8ba1\u8f83\u4e86\u3002\u6211\u53ea\u95ee\u4f60\uff1a\u5230\u5e95\u6211\u5e08\u7236\u662f\u4e0d\u662f\u7ed9\u4f60\u5bb3\u6b7b\u4e86\uff1f\u201d\u8bf4\u7740\u8fd0\u8d77\u201c\u795e\u7167\u7ecf\u201d\u5185\u529f\u3002\u970e\u65f6\u4e4b\u95f4\uff0c\u4e07\u9707\u5c71\u5168\u8eab\u72b9\u5982\u5815\u5165\u4e86\u4e00\u53ea\u5927\u706b\u7089\u4e2d\uff0c\u4f3c\u4e4e\u8fde\u8840\u6db2\u4e5f\u70e7\u5f97\u8981\u6cb8\u817e\u8d77\u6765\uff0c\u7247\u523b\u4e5f\u96be\u4ee5\u62b5\u53d7\uff0c\u60f3\u5230\u621a\u957f\u53d1\u7684\u5c38\u8eab\u7adf\u4f1a\u4e0d\u77e5\u53bb\u5411\uff0c\u5fc3\u4e2d\u60ca\u7591\u60f6\u6050\uff0c\u4e71\u6210\u4e00\u56e2\uff0c\u5df2\u5168\u65e0\u6297\u62d2\u4e4b\u610f\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u2026\u2026\u4e0d\u9519\u3002\u621a\u957f\u53d1\u662f\u6211\u6740\u7684\u3002\u201d\u72c4\u4e91\u53c8\u95ee\uff1a\u201c\u6211\u5e08\u7236\u7684\u5c38\u9996\u5462\uff1f\u4f60\u5230\u5e95\u653e\u5728\u751a\u4e48\u5730\u65b9\uff1f\u201d \u3000\u3000\u4e07\u9707\u5c71\u9053\uff1a\u201c\u6211\u786e\u662f\u5c06\u4ed6\u780c\u5165\u4e86\u8fd9\u5939\u5899\u4e4b\u4e2d\uff0c\u662f\u5c38\u53d8\u2026\u2026\u5c38\u53d8\u4e48\uff1f\u201d \u3000\u3000\u72c4\u4e91\u72e0\u72e0\u7684\u51dd\u89c6\u7740\u4ed6\uff0c\u60f3\u8d77\u8fd9\u51e0\u5e74\u6765\uff0c\u81ea\u5df1\u7ecf\u5386\u4e86\u65e0\u7a77\u65e0\u5c3d\u7684\u82e6\u96be\uff0c\u5168\u662f\u7531\u4ed6\u7236\u5b50\u7684\u6bd2\u5bb3\uff0c\u6b64\u523b\u4e07\u9707\u5c71\u53c8\u4eb2\u53e3\u627f\u8ba4\u4e86\u6740\u6b7b\u4ed6\u5e08\u7236\uff0c\u5982\u4f55\u4e0d\u6559\u4ed6\u6012\u706b\u653b\u5fc3\uff1f\u82e5\u4e0d\u662f\u5df2\u548c\u621a\u82b3\u76f8\u4f1a\uff0c\u5fc3\u4e2d\u6bd5\u7adf\u6b22\u559c\u591a\u8fc7\u54c0\u4f24\uff0c\u7acb\u65f6\u4fbf\u8981\u4e00\u638c\u9001\u4e86\u4ed6\u7684\u6027\u547d\u3002\u4ed6\u4e00\u54ac\u7259\uff0c\u63d0\u8d77\u4e07\u9707\u5c71\u6765\uff0c\u7830\u7684\u4e00\u58f0\uff0c\u4ece\u90a3\u5899\u5b54\u4e2d\u63b7\u4e86\u8fdb\u53bb\u3002\u4e07\u9707\u5c71\u8eab\u5b50\u5927\uff0c\u5899\u5b54\u5c0f\uff0c\u649e\u843d\u4e86\u51e0\u5757\u7816\u5934\uff0c\u8fd9\u624d\u8dcc\u5165\u3002 \u3000\u3000\u621a\u82b3\u201c\u554a\u201d\u7684\u4e00\u58f0\uff0c\u8f7b\u58f0\u4f4e\u547c\u3002\u72c4\u4e91\u63d0\u8d77\u4e07\u572d\u7684\u8eab\u5b50\uff0c\u53c8\u63b7\u5165\u4e86\u5899\u6d1e\uff0c\u8bf4\u9053\uff1a\u201c\u4e00\u62a5\u8fd8\u4e00\u62a5\uff0c\u4ed6\u7236\u5b50\u8fd9\u822c\u6bd2\u5bb3\u5e08\u7236\uff0c\u54b1\u4eec\u5c31\u8fd9\u822c\u5bf9\u4ed8\u4ed6\u4e8c\u4eba\u3002\u201d\u62fe\u8d77\u5730\u4e0b\u7684\u7816\u5757\uff0c\u4fbf\u780c\u4e86\u8d77\u6765\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u4fbf\u5c06\u5899\u6d1e\u780c\u597d\u4e86\u3002 \u3000\u3000\u621a\u82b3\u98a4\u58f0\u9053\uff1a\u201c\u5e08\u2026\u2026\u5e08\u54e5\uff0c\u4f60\u7ec8\u4e8e\u66ff\u7239\u7239\u62a5\u4e86\u8fd9\u573a\u5927\u4ec7\u3002 \u3000\u3000\u82e5\u4e0d\u662f\u4f60\u6765\u2026\u2026\u5e08\u54e5\uff0c\u8fd9\u4eba\u7684\u5c38\u4f53\uff0c\u600e\u4e48\u529e\uff1f\u201d\u8bf4\u7740\uff0c\u6307\u4e86\u6307\u5434\u574e\u7684\u5c38\u4f53\u3002 \u3000\u3000\u72c4\u4e91\u9053\uff1a\u201c\u54b1\u4eec\u8d70\u7f62\uff01\u8fd9\u91cc\u7684\u4e8b\uff0c\u518d\u4e5f\u4e0d\u7528\u7406\u4f1a\u4e86\u3002\u201d\u621a\u82b3\u9053\uff1a\u201c\u4ed6\u4e8c\u4eba\u780c\u5728\u5899\u4e2d\uff0c\u8fd8\u6ca1\u6709\u6b7b\uff0c\u82e5\u662f\u6709\u4eba\u6765\u6551\u2026\u2026\u201d\u72c4\u4e91\u9053\uff1a\u201c\u65c1\u4eba\u600e\u4f1a\u77e5\u9053\u5899\u5185\u6709\u4eba\uff1f\u54b1\u4eec\u628a\u5434\u574e\u7684\u5c38\u4f53\u79fb\u51fa\u53bb\uff0c\u65c1\u4eba\u66f4\u52a0\u4e0d\u4f1a\u5230\u8fd9\u91cc\u6765\u67e5\u5bdf\u3002\u8fd9\u4e24\u4eba\u5728\u5899\u91cc\u6d3b\u4e0d\u591a\u4e45\u7684\u3002\u201d\u5f53\u4e0b\u63d0\u8d77\u5434\u574e\u7684\u5c38\u8eab\uff0c\u8d70\u51fa\u4e66\u623f\uff0c\u5411\u621a\u82b3\u62db\u624b\u9053\uff1a\u201c\u8d70\u7f62\uff01\u201d \u3000\u3000\u4e24\u4eba\u8dc3\u51fa\u4e86\u4e07\u5bb6\u7684\u56f4\u5899\uff0c\u72c4\u4e91\u629b\u4e0b\u5434\u574e\u7684\u5c38\u8eab\uff0c\u8bf4\u9053\uff1a\u201c\u5e08\u59b9\uff0c\u54b1\u4eec\u5230\u54ea\u91cc\u53bb\u597d\uff1f\u201d \u3000\u3000\u621a\u82b3\u9053\uff1a\u201c\u4f60\u60f3\u7239\u7239\u771f\u7684\u662f\u7ed9\u4ed6\u4eec\u5bb3\u6b7b\u4e86\u4e48\uff1f\u201d\u72c4\u4e91\u9053\uff1a\u201c\u4f46\u613f\u5e08\u7236\u4ecd\u662f\u5065\u5728\u3002\u53ea\u662f\u542c\u4e07\u9707\u5c71\u7684\u8bf4\u8bdd\uff0c\u5c31\u6015\u2026\u2026\u5c31\u6015\u5e08\u7236\u5df2\u7ecf\u906d\u96be\u3002\u54b1\u4eec\u81ea\u8be5\u67e5\u4e2a\u6c34\u843d\u77f3\u51fa\u3002\u201d\u621a\u82b3\u9053\uff1a\u201c\u6211\u5f97\u56de\u53bb\u62ff\u4e9b\u4e1c\u897f\uff0c\u4f60\u5728\u90a3\u8fb9\u7684\u7834\u7960\u5802\u91cc\u7b49\u6211\u4e00\u7b49\u3002\u201d\u72c4\u4e91\u9053\uff1a\u201c\u6211\u966a\u4f60\u4e00\u8d77\u53bb\u597d\u4e86\u3002\u201d\u621a\u82b3\u9053\uff1a\u201c\u4e0d\uff0c\u4e0d\u597d\uff01\u82e5\u662f\u7ed9\u4eba\u649e\u89c1\uff0c\u591a\u4e0d\u65b9\u4fbf\u3002\u201d\u72c4\u4e91\u9053\uff1a\u201c\u6211\u966a\u7740\u4f60\u597d\u4e9b\u3002\u4e07\u5bb6\u8fd8\u6709\u522b\u7684\u5f1f\u5b50\uff0c\u53ef\u6ca1\u4e00\u4e2a\u662f\u597d\u4eba\u3002\u201d\u621a\u82b3\u9053\uff1a\u201c\u4e0d\u8981\u7d27\u3002\u4f60\u62b1\u7740\u7a7a\u5fc3\u83dc\uff0c\u5728\u90a3\u8fb9\u7b49\u6211\u3002\u201d \u3000\u3000\u7a7a\u5fc3\u83dc\u7ecf\u4e86\u8fd9\u573a\u60ca\u5413\uff0c\u62b5\u53d7\u4e0d\u4f4f\uff0c\u65e9\u5df2\u5728\u5988\u5988\u6000\u4e2d\u6c89\u6c89\u7761\u719f\u3002 \u3000\u3000\u72c4\u4e91\u5411\u6765\u542c\u621a\u82b3\u7684\u8bdd\uff0c\u89c1\u5979\u795e\u60c5\u575a\u51b3\uff0c\u4e0d\u6562\u8fdd\u62d7\uff0c\u53ea\u5f97\u62b1\u8fc7\u5973\u5b69\uff0c\u89c1\u621a\u82b3\u8dc3\u8fdb\u4e86\u4e07\u5bb6\uff0c\u4fbf\u8d70\u5411\u7960\u5802\uff0c\u63a8\u95e8\u5165\u5185\u3002 \u3000\u3000\u8fc7\u4e86\u4e00\u987f\u996d\u65f6\u5206\uff0c\u59cb\u7ec8\u4e0d\u89c1\u621a\u82b3\u56de\u6765\uff0c\u72c4\u4e91\u6709\u4e9b\u62c5\u5fc3\u4e86\uff0c\u4fbf\u60f3\u53bb\u4e07\u5bb6\u63a5\u5979\uff0c\u4f46\u751f\u6015\u5979\u4e0d\u5feb\uff0c\u62b1\u7740\u7a7a\u5fc3\u83dc\uff0c\u5728\u5eca\u4e0b\u8d70\u6765\u8d70\u53bb\uff0c\u60f3\u7740\u7ec8\u4e8e\u5f97\u548c\u5e08\u59b9\u76f8\u805a\uff0c\u5b9e\u662f\u8bf4\u4e0d\u51fa\u7684\u6b22\u559c\uff0c\u4f46\u5185\u5fc3\u6df1\u5904\uff0c\u5374\u9690\u9690\u53c8\u611f\u6050\u60e7\uff1b\u4e0d\u77e5\u5e08\u59b9\u8bb8\u4e0d\u8bb8\u6211\u6c38\u8fdc\u966a\u7740\u5979\uff1f\u5fc3\u4e2d\u4e0d\u4f4f\u8bb8\u613f\uff1a\u201c\u8001\u5929\u7237\u4fdd\u4f51\uff0c\u6211\u5df2\u5403\u4e86\u8fd9\u8bb8\u591a\u82e6\u5934\uff0c\u8ba9\u6211\u4eca\u540e\u966a\u7740\u5979\uff0c\u4fdd\u62a4\u5979\uff0c\u7167\u987e\u5979\u3002\u6211\u4e0d\u6562\u76fc\u671b\u505a\u5979\u4e08\u592b\uff0c\u53ea\u8981\u5929\u5929\u80fd\u89c1\u5230\u5979\uff0c\u5979\u6bcf\u5929\u53eb\u6211\u4e00\u58f0\u2018\u5e08\u54e5\u2019\u3002\u8001\u5929\u7237\uff0c\u6211\u8fd9\u4e00\u751f\u4e00\u4e16\u518d\u4e5f\u4e0d\u6c42\u4f60\u751a\u4e48\u4e86\u3002\u201d \u3000\u3000\u7a81\u7136\u4e4b\u95f4\uff0c\u542c\u5f97\u7960\u5802\u957f\u7a97\u5185\u745f\u745f\u4f5c\u58f0\uff0c\u4f3c\u4e4e\u6709\u4eba\u3002\u72c4\u4e91\u4e00\u4fa7\u8eab\uff0c\u7ad9\u5728\u7a97\u4e0b\u4e0d\u52a8\u3002\u8fc7\u5f97\u7247\u523b\uff0c\u957f\u7a97\u5440\u7684\u4e00\u58f0\u63a8\u5f00\uff0c\u6709\u4eba\u8d70\u4e86\u51fa\u6765\u3002 \u3000\u3000\u9ed1\u6697\u4e4b\u4e2d\uff0c\u9690\u7ea6\u89c1\u5230\u662f\u4e2a\u62ab\u5934\u6563\u53d1\u7684\u4e10\u5987\uff0c\u72c4\u4e91\u4fbf\u4e0d\u5728\u610f\u4e0b\uff0c\u53ea\u60f3\uff1a\u201c\u600e\u4e48\u82b3\u59b9\u8fd8\u4e0d\u56de\u6765\uff1f\u201d \u3000\u3000\u7a7a\u5fc3\u83dc\u5728\u68a6\u4e2d\u201c\u54c7\u201d\u7684\u4e00\u58f0\uff0c\u60ca\u54ed\u51fa\u6765\uff0c\u53eb\u9053\uff1a\u201c\u5988\u5988\uff0c\u5988\u5988\uff01\u201d \u3000\u3000\u90a3\u4e10\u5987\u5927\u5403\u4e00\u60ca\uff0c\u7f29\u5728\u8d70\u5eca\u7684\u89d2\u843d\u91cc\uff0c\u62b1\u4f4f\u4e86\u81ea\u5df1\u7684\u5934\u3002 \u3000\u3000\u72c4\u4e91\u8f7b\u62cd\u7a7a\u5fc3\u83dc\u7684\u80a9\u8180\uff0c\u5b89\u629a\u5979\u9053\uff1a\u201c\u522b\u54ed\uff0c\u522b\u54ed\uff01\u5988\u5988\u5c31\u6765\u4e86\uff01\u5988\u5988\u5c31\u6765\u4e86\uff01\u201d \u3000\u3000\u90a3\u4e10\u5987\u89c1\u51fa\u58f0\u7684\u662f\u4e2a\u5c0f\u5973\u5b69\uff0c\u72c4\u4e91\u5bf9\u5979\u4e5f\u4f3c\u65e0\u52a0\u5bb3\u4e4b\u610f\uff0c\u80c6\u5b50\u5927\u4e86\u8d77\u6765\uff0c\u7ad9\u8d77\u8eab\u6765\uff0c\u6162\u6162\u8d70\u8fd1\uff0c\u5e2e\u52a9\u4ed6\u5b89\u629a\u7a7a\u5fc3\u83dc\uff1a \u3000\u3000\u201c\u5b9d\u5b9d\u597d\u4e56\uff0c\u522b\u54ed\uff0c\u5988\u5988\u5c31\u6765\u4e86\uff01\u201d\u5979\u4f4e\u58f0\u5411\u72c4\u4e91\u9053\uff1a\u201c\u4e00\u4e2a\u4eba\u7761\u7740\u4e86\u5c31\u4f1a\u89c1\u9b3c\uff0c\u6709\u4eba\u534a\u591c\u4e09\u66f4\u8d77\u8eab\u780c\u5899\u5934\uff0c\u4e0d\u2026\u2026\u4e0d\u2026\u2026\u4f60\u522b\u95ee\u6211\u2026\u2026\u201d \u3000\u3000\u72c4\u4e91\u95ee\u9053\uff1a\u201c\u4f60\u8bf4\u751a\u4e48\uff1f\u201d\u90a3\u4e10\u5987\u9053\uff1a\u201c\u6ca1\u2026\u2026\u6ca1\u751a\u4e48\u3002\u8001\u7237\u8d76\u4e86\u6211\u51fa\u6765\u3002\u4ed6\u4e0d\u8981\u6211\u4e86\uff0c\u4ece\u524d\uff0c\u6211\u5e74\u8f7b\u7684\u65f6\u5019\uff0c\u4ed6\u597d\u559c\u6b22\u6211\u3002\u4eba\u5bb6\u8bf4\uff1a\u4e00\u591c\u592b\u59bb\u767e\u591c\u6069\uff0c\u767e\u591c\u592b\u59bb\u6d77\u6837\u6df1\u2026\u2026\u8001\u7237\u603b\u6709\u4e00\u5929\u4f1a\u53eb\u6211\u56de\u53bb\u7684\u3002\u662f\u554a\uff0c\u4e00\u591c\u592b\u59bb\u767e\u591c\u6069\uff0c\u767e\u591c\u592b\u59bb\u6d77\u6837\u6df1\u2026\u2026\u201d \u3000\u3000\u72c4\u4e91\u5fc3\u4e2d\u4e00\u52a8\uff1a\u201c\u5e08\u59b9\u5bf9\u5979\u4e08\u592b\uff0c\u96be\u9053\u5c31\u4e0d\u5ff5\u65e7\u60c5\u4e48\uff1f\u201d\u7a81\u7136\u95f4\u80f8\u53e3\u4f3c\u4e4e\u5145\u585e\u4e86\u4e00\u80a1\u95f7\u6c14\uff0c\u5934\u8111\u4e2d\u4e00\u9635\u6655\u7729\uff0c\u62b1\u7740\u7a7a\u5fc3\u83dc\uff0c\u4fbf\u4ece\u7834\u7960\u5802\u4e2d\u51b2\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u4ed6\u51b3\u8ba1\u731c\u60f3\u4e0d\u5230\uff0c\u8fd9\u6ee1\u8eab\u6c61\u79fd\u7684\u4e10\u5987\uff0c\u5c31\u662f\u5f53\u5e74\u8bec\u9677\u4ed6\u7684\u6843\u7ea2\u3002"}),showOnHomepage:!0},{name:"\u5341\u4e8c\u3000\u5927\u5b9d\u85cf",content:(0,a.jsx)(a.Fragment,{children:"\u72c4\u4e91\u8d8a\u5899\u800c\u5165\uff0c\u6765\u5230\u4e07\u5bb6\u7684\u4e66\u623f\u3002\u5176\u65f6\u5929\u5df2\u9ece\u660e\uff0c\u6726\u6726\u80e7\u80e7\u4e4b\u4e2d\uff0c\u53ea\u89c1\u5730\u4e0b\u8eba\u7740\u4e00\u4eba\uff0c\u4f9d\u7a00\u4fbf\u662f\u621a\u82b3\u3002\u72c4\u4e91\u5927\u60ca\uff0c\u5fd9\u53d6\u706b\u5200\u706b\u77f3\u6253\u4e86\u706b\uff0c\u70b9\u7740\u4e86\u684c\u4e0a\u7684\u8721\u70db\uff0c\u70db\u5149\u4e4b\u4e0b\uff0c\u53ea\u89c1\u621a\u82b3\u8eab\u4e0a\u5168\u662f\u9c9c\u8840\uff0c\u5c0f\u8179\u4e0a\u63d2\u4e86\u4e00\u67c4\u77ed\u5200\u3002 \u3000\u3000\u5979\u8eab\u65c1\u5806\u6ee1\u4e86\u7816\u5757\uff0c\u5899\u4e0a\u62c6\u5f00\u4e86\u4e00\u6d1e\uff0c\u4e07\u6c0f\u7236\u5b50\u65e9\u5df2\u4e0d\u5728\u5176\u5185\u3002 \u3000\u3000\u72c4\u4e91\u4fef\u8eab\u8dea\u5728\u621a\u82b3\u8eab\u8fb9\uff0c\u53eb\u9053\uff1a\u201c\u5e08\u59b9\uff0c\u5e08\u59b9\uff01\u201d\u4ed6\u5413\u5f97\u5168\u8eab\u53d1\u6296\uff0c\u58f0\u97f3\u51e0\u4e4e\u54d1\u4e86\uff0c\u4f38\u624b\u53bb\u6478\u621a\u82b3\u7684\u8138\uff0c\u89c9\u5f97\u5c1a\u6709\u6696\u6c14\uff0c\u9f3b\u4e2d\u4e5f\u8fd8\u6709\u8f7b\u8f7b\u547c\u5438\u3002\u4ed6\u5fc3\u795e\u7a0d\u5b9a\uff0c\u53c8\u53eb\uff1a\u201c\u5e08\u59b9\uff01\u201d \u3000\u3000\u621a\u82b3\u7f13\u7f13\u7741\u5f00\u773c\u6765\uff0c\u8138\u4e0a\u9732\u51fa\u4e00\u4e1d\u82e6\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u5e08\u54e5\u2026\u2026\u6211\u2026\u2026\u6211\u5bf9\u4e0d\u8d77\u4f60\u3002\u201d \u3000\u3000\u72c4\u4e91\u9053\uff1a\u201c\u4f60\u522b\u8bf4\u8bdd\u3002\u6211\u2026\u2026\u6211\u6765\u6551\u4f60\u3002\u201d\u5c06\u7a7a\u5fc3\u83dc\u8f7b\u8f7b\u653e\u5728\u4e00\u8fb9\uff0c\u53f3\u624b\u62b1\u4f4f\u4e86\u621a\u82b3\u8eab\u5b50\uff0c\u5de6\u624b\u6293\u8d77\u77ed\u5200\u7684\u5200\u67c4\uff0c\u60f3\u8981\u62d4\u4e86\u51fa\u6765\u3002\u4f46\u4e00\u77a5\u4e4b\u4e0b\uff0c\u89c1\u90a3\u53e3\u5200\u6df1\u6df1\u63d2\u5165\u5979\u5c0f\u8179\uff0c\u5200\u5b50\u4e00\u62d4\u51fa\uff0c\u52bf\u5fc5\u7acb\u65f6\u9001\u4e86\u5979\u7684\u6027\u547d\uff0c\u4fbf\u4e0d\u6562\u5c31\u62d4\uff0c\u53ea\u6025\u5f97\u65e0\u8ba1\u53ef\u65bd\uff0c\u8fde\u95ee\uff1a\u201c\u600e\u4e48\u529e\uff1f\u600e\u4e48\u529e\uff1f\u662f\u2026\u2026\u662f\u8c01\u5bb3\u4f60\u7684\uff1f\u201d\u621a\u82b3\u82e6\u7b11\u9053\uff1a\u201c\u5e08\u54e5\uff0c\u4eba\u5bb6\u8bf4\uff1a\u4e00\u591c\u592b\u59bb\u2026\u2026\u5509\uff0c\u522b\u8bf4\u4e86\uff0c\u6211\u2026\u2026\u4f60\u522b\u602a\u6211\u3002\u6211\u5fcd\u5fc3\u4e0d\u4e0b\uff0c\u6765\u653e\u51fa\u4e86\u6211\u4e08\u592b\u2026\u2026\u4ed6\u2026\u2026\u4ed6\u2026\u2026\u201d \u3000\u3000\u72c4\u4e91\u54ac\u7259\u9053\uff1a\u201c\u4ed6\u2026\u2026\u4ed6\u2026\u2026\u4ed6\u53cd\u800c\u523a\u4e86\u4f60\u4e00\u5200\uff0c\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u621a\u82b3\u82e6\u7b11\u7740\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u72c4\u4e91\u5fc3\u4e2d\u75db\u5982\u5200\u7ede\uff0c\u773c\u89c1\u621a\u82b3\u547d\u5728\u9877\u523b\uff0c\u4e07\u572d\u8fd9\u4e00\u5200\u523a\u5f97\u5979\u5982\u6b64\u5389\u5bb3\uff0c\u65e0\u8bba\u5982\u4f55\u662f\u6551\u4e0d\u6d3b\u4e86\u3002\u5728\u4ed6\u5185\u5fc3\uff0c\u66f4\u6709\u4e00\u6761\u5992\u5fcc\u7684\u6bd2\u86c7\u5728\u9690\u9690\u7684\u54ac\u556e\uff1a\u201c\u4f60\u2026\u2026\u7ec8\u7a76\u662f\u7231\u4f60\u4e08\u592b\uff0c\u5b81\u53ef\u81ea\u5df1\u6b7b\u4e86\uff0c\u4e5f\u8981\u6551\u4ed6\u3002\u201d \u3000\u3000\u621a\u82b3\u9053\uff1a\u201c\u5e08\u54e5\uff0c\u4f60\u7b54\u5141\u6211\uff0c\u597d\u597d\u7167\u987e\u7a7a\u5fc3\u83dc\uff0c\u5f53\u662f\u4f60\u2026\u2026\u4f60\u81ea\u5df1\u7684\u5973\u513f\u4e00\u822c\u3002\u201d \u3000\u3000\u72c4\u4e91\u9eef\u7136\u4e0d\u8bed\uff0c\u70b9\u4e86\u70b9\u5934\uff0c\u54ac\u7259\u9053\uff1a\u201c\u8fd9\u8d3c\u5b50\u2026\u2026\u5230\u54ea\u91cc\u53bb\u5566\uff1f\u201d \u3000\u3000\u621a\u82b3\u773c\u795e\u6563\u4e71\uff0c\u58f0\u97f3\u542b\u6df7\uff0c\u8f7b\u8f7b\u7684\u9053\uff1a\u201c\u90a3\u5c71\u6d1e\u91cc\uff0c\u4e24\u53ea\u5927\u8774\u8776\u98de\u4e86\u8fdb\u53bb\uff0c\u6881\u5c71\u4f2f\uff0c\u795d\u82f1\u53f0\uff0c\u5e08\u54e5\uff0c\u4f60\u77a7\uff0c\u4f60\u77a7\uff01\u4e00\u53ea\u662f\u4f60\uff0c\u4e00\u53ea\u662f\u6211\u3002\u54b1\u4eec\u4fe9\u2026\u2026\u8fd9\u6837\u98de\u6765\u98de\u53bb\uff0c\u6c38\u8fdc\u4e5f\u4e0d\u5206\u79bb\uff0c\u4f60\u8bf4\u597d\u4e0d\u597d\uff1f\u201d\u58f0\u97f3\u6e10\u4f4e\uff0c\u547c\u5438\u6162\u6162\u5fae\u5f31\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u72c4\u4e91\u4e00\u624b\u62b1\u7740\u7a7a\u5fc3\u83dc\uff0c\u4e00\u624b\u62b1\u7740\u621a\u82b3\u7684\u5c38\u8eab\uff0c\u4ece\u4e07\u5bb6\u56f4\u5899\u4e2d\u8dc3\u4e86\u51fa\u6765\u3002\u4ed6\u672c\u60f3\u4e00\u628a\u706b\u5c06\u4e07\u5bb6\u7684\u5927\u5b85\u5b50\u70e7\u4e2a\u5e72\u51c0\uff0c\u4f46\u8f6c\u5ff5\u4e00\u60f3\uff1a\u201c\u8fd9\u5c4b\u5b50\u4e00\u70e7\uff0c\u4e07\u6c0f\u7236\u5b50\u518d\u4e5f\u4e0d\u4f1a\u56de\u6765\u4e86\uff0c\u8981\u66ff\u5e08\u59b9\u62a5\u4ec7\uff0c\u5f97\u8ba9\u8fd9\u5b85\u5b50\u7559\u7740\u3002\u201d \u3000\u3000\u72c4\u4e91\u5954\u5230\u5f53\u5e74\u4e01\u5178\u6bd5\u547d\u7684\u5e9f\u56ed\u4e2d\uff0c\u5728\u6885\u6811\u4e0b\u6398\u4e86\u4e2a\u5751\uff0c\u5c06\u621a\u82b3\u7684\u5c38\u8eab\u57cb\u4e86\uff0c\u90a3\u67c4\u77ed\u5200\u5374\u6536\u5728\u8eab\u8fb9\uff0c\u4ed6\u51b3\u5fc3\u8981\u7528\u8fd9\u67c4\u5200\u53bb\u53d6\u4e07\u6c0f\u7236\u5b50\u7684\u6027\u547d\u3002 \u3000\u3000\u4ed6\u4f24\u5fc3\u5f97\u54ed\u4e0d\u51fa\u773c\u6cea\u6765\uff0c\u53ea\u662f\u4e0d\u4f4f\u81ea\u8d23\uff1a\u201c\u4e3a\u751a\u4e48\u4e0d\u5c06\u8fd9\u4e24\u4e2a\u6076\u8d3c\u5148\u6253\u6b7b\u4e86\uff0c\u518d\u4e22\u8fdb\u5899\u6d1e\uff1f\u4e3a\u751a\u4e48\u8fd9\u6837\u5927\u610f\u3002\u7ec8\u4e8e\u5bb3\u4e86\u5e08\u59b9\u7684\u6027\u547d\uff1f\u201d \u3000\u3000\u7a7a\u5fc3\u83dc\u4e0d\u4f4f\u54ed\u53eb\uff1a\u201c\u5988\u5988\uff0c\u5988\u5988\uff01\u201d\u53eb\u5f97\u4ed6\u5fc3\u70e6\u610f\u4e71\u3002\u4e8e\u662f\u5728\u6c5f\u9675\u57ce\u5916\u627e\u4e86\u4e00\u5bb6\u519c\u5bb6\uff0c\u7ed9\u4e86\u5341\u4e24\u94f6\u5b50\uff0c\u8bf7\u4e00\u4e2a\u519c\u5987\u7167\u7ba1\u5973\u5b69\u3002 \u3000\u3000\u4ed6\u65e5\u65e5\u591c\u591c\u7684\u5b88\u5019\u5728\u4e07\u5bb6\u524d\u540e\uff0c\u534a\u4e2a\u6708\u8fc7\u53bb\u4e86\uff0c\u6ca1\u89c1\u5230\u4e07\u6c0f\u7236\u5b50\u534a\u70b9\u8e2a\u8ff9\u3002\u5947\u602a\u7684\u662f\uff0c\u8fde\u9c81\u5764\u3001\u535c\u57a3\u3001\u5b59\u5747\u3001\u51af\u57a3\u3001\u6c88\u57ce\u7b49\u51e0\u4eba\u4e5f\u90fd\u5931\u4e86\u8e2a\uff0c\u4e0d\u518d\u56de\u5230\u4e07\u5bb6\u6765\u3002\u4e07\u5bb6\u7684\u5a62\u4ec6\u4e71\u5f97\u6ca1\u5934\u82cd\u8747\u4e00\u822c\uff0c\u6709\u7684\u5f00\u59cb\u5077\u4e1c\u897f\u4e86\uff0c\u6709\u7684\u5728\u5435\u5634\u6253\u67b6\u3002 \u3000\u3000\u6c5f\u9675\u57ce\u4e2d\uff0c\u5374\u6709\u8bb8\u591a\u6b66\u6797\u4eba\u7269\u4ece\u56db\u9762\u516b\u65b9\u805a\u96c6\u62e2\u6765\u3002 \u3000\u3000\u4e00\u5929\u665a\u4e0a\uff0c\u72c4\u4e91\u542c\u5230\u4e86\u51e0\u4e2a\u6c5f\u6e56\u8c6a\u5ba2\u7684\u5bf9\u8bdd\uff1a \u3000\u3000\u201c\u90a3\u8fde\u57ce\u5251\u8bc0\u539f\u6765\u662f\u85cf\u5728\u4e00\u90e8\u300a\u5510\u8bd7\u9009\u8f91\u300b\u4e4b\u4e2d\uff0c\u5934\u4e0a\u56db\u5b57\u662f\u2018\u6c5f\u9675\u57ce\u5357\u2019\u3002\u201d \u3000\u3000\u201c\u662f\u554a\uff0c\u8fd9\u51e0\u5929\u95fb\u98ce\u8d76\u6765\u7684\u7740\u5b9e\u4e0d\u5c11\u3002\u5c31\u662f\u4e0d\u77e5\u8fd9\u56db\u4e2a\u5b57\u4e4b\u540e\u662f\u4e9b\u751a\u4e48\u5b57\u3002\u201d \u3000\u3000\u201c\u7ba1\u4ed6\u4e4b\u540e\u662f\u751a\u4e48\u5b57\uff1f\u54b1\u4eec\u53ea\u7ba1\u5b88\u5728\u6c5f\u9675\u57ce\u5357\u3002\u6709\u4eba\u6316\u51fa\u5b9d\u85cf\uff0c\u7ed9\u4ed6\u6765\u4e2a\u62e6\u8def\u6253\u52ab\u3002\u201d \u3000\u3000\u201c\u4e0d\u9519\u3002\u5c31\u7b97\u52ab\u4e0d\u4e86\uff0c\u81f3\u5c11\u4e5f\u5f97\u5206\u4e0a\u4e00\u4efd\u3002\u89c1\u8005\u6709\u4efd\uff0c\u8fd8\u5c11\u5f97\u4e86\u54b1\u54e5\u513f\u4eec\u7684\u4e48\uff1f\u201d \u3000\u3000\u201c\u563f\u563f\uff01\u6c5f\u9675\u4e66\u94fa\u4e2d\u8fd9\u51e0\u5929\u53bb\u4e70\u300a\u5510\u8bd7\u9009\u8f91\u300b\u7684\u4eba\u53ef\u771f\u4e0d\u5c11\u3002\u4eca\u513f\u6211\u8d70\u8fdb\u4e66\u94fa\uff0c\u8fd8\u6ca1\u5f00\u53e3\uff0c\u4f19\u8ba1\u5c31\u8bf4\uff1a\u2018\u5927\u7237\uff0c\u60a8\u53ef\u662f\u8981\u4e70\u300a\u5510\u8bd7\u9009\u8f91\u300b\uff1f\u8fd9\u90e8\u4e66\u6211\u4eec\u521a\u5728\u6c49\u53e3\u8d76\u7740\u634e\u6765\uff0c\u8981\u4e70\u8bf7\u65e9\uff0c\u8fdf\u4e86\u53ea\u6015\u5356\u5149\u4e86\u3002\u201d\u6211\u5f88\u5947\u602a\uff0c\u95ee\u4ed6\uff1a\u2018\u4f60\u600e\u77e5\u6211\u8981\u4e70\u300a\u5510\u8bd7\u9009\u8f91\u300b\uff1f\u2019\u4f60\u731c\u4ed6\u600e\u4e48\u8bf4\uff1f\u201d \u3000\u3000\u201c\u4e0d\u77e5\u9053\uff01\u4ed6\u600e\u4e48\u8bf4\uff1f\u201d \u3000\u3000\u201c\u4ed6\u5988\u7684\uff0c\u90a3\u4f19\u8ba1\u8bf4\uff1a\u2018\u4e0d\u7792\u60a8\u8001\u4eba\u5bb6\u8bf4\uff0c\u8fd9\u51e0\u5929\u8eab\u4e0a\u5e26\u5200\u5e26\u5251\u3001\u633a\u80f8\u51f8\u809a\u7684\u7ec3\u628a\u5f0f\u7237\u4eec\uff0c\u6765\u5230\u4e66\u94fa\u5b50\uff0c\u5341\u4e2a\u5012\u6709\u5341\u4e00\u4e2a\u8981\u4e70\u8fd9\u672c\u4e66\u3002\u4e94\u4e24\u94f6\u5b50\u4e00\u672c\uff0c\u4f60\u7237\u53f0\u4e0d\u5408\u5f0f\uff1f\u2019\u201d \u3000\u3000\u201c\u4ed6\u5976\u5976\u7684\u3002\u54ea\u6709\u8fd9\u4e48\u8d35\u7684\u4e66\uff1f\u201d \u3000\u3000\u201c\u4f60\u77e5\u9053\u4e66\u4ef7\u4e48\uff1f\u4f60\u4e70\u8fc7\u4e66\u6ca1\u6709\uff1f\u201d \u3000\u3000\u201c\u54c8\u54c8\uff0c\u8001\u5b50\u8fd9\u4e00\u8f88\u5b50\u53ef\u4ece\u6ca1\u8fdb\u8fc7\u4e66\u94fa\u5b50\u7684\u95e8\uff0c\u4e66\u554a\u4e66\u7684\uff0c\u8001\u5b50\u8fd9\u4e00\u8f88\u5b50\u6700\u7231\u8d4c\u94b1\uff0c\u4e70\u8d62\u5c31\u597d\uff0c\u4e70\u4e66\u53ef\u4ece\u6765\u4e0d\u5e72\u3002\u563f\u563f\uff0c\u563f\u563f\uff01\u201d \u3000\u3000\u72c4\u4e91\u5fc3\u9053\uff1a\u201c\u8fde\u57ce\u5251\u8bc0\u4e2d\u7684\u79d8\u5bc6\u53ef\u4f20\u51fa\u53bb\u4e86\uff0c\u662f\u8c01\u4f20\u51fa\u53bb\u7684\uff1f\u662f\u4e86\uff0c\u4e07\u6c0f\u7236\u5b50\u7684\u8bdd\u7ed9\u9c81\u5764\u4ed6\u4eec\u542c\u4e86\u53bb\uff0c\u4e07\u9707\u5c71\u8981\u8ffd\u67e5\uff0c\u51e0\u4e2a\u5f92\u513f\u5374\u9003\u8d70\u4e86\u3002\u5c31\u8fd9\u6837\uff0c\u77e5\u9053\u7684\u4eba\u8d8a\u6765\u8d8a\u591a\u3002\u201d \u3000\u3000\u60f3\u8d77\u5f53\u5e74\u4e0e\u4e01\u5178\u540c\u5904\u72f1\u4e2d\u4e4b\u65f6\uff0c\u4e5f\u6709\u8bb8\u591a\u6c5f\u6e56\u8c6a\u58eb\u95fb\u98ce\u800c\u6765\uff0c\u5374\u90fd\u7ed9\u4e01\u5178\u4e00\u4e00\u6253\u6b7b\u4e86\u3002\u201c\u55ef\uff0c\u4e01\u5927\u54e5\u7684\u5927\u4e8b\u8fd8\u6ca1\u529e\u3002 \u3000\u3000\u4e01\u5927\u54e5\u7684\u4e8b\u53ef\u6bd4\u6211\u81ea\u5df1\u62a5\u4ec7\u8981\u7d27\u3002\u201d \u3000\u3000\u51cc\u5c0f\u59d0\u7684\u7236\u4eb2\u662f\u6c5f\u9675\u5e9c\u7684\u77e5\u5e9c\u3002\u72c4\u4e91\u5230\u6c5f\u9675\u57ce\u4e2d\u6700\u5927\u7684\u68fa\u6750\u94fa\u3001\u5893\u7891\u94fa\u4e00\u6253\u542c\uff0c\u4fbf\u67e5\u77e5\u51cc\u5c0f\u59d0\u7684\u575f\u846c\u5728\u6c5f\u9675\u4e1c\u95e8\u5916\u5341\u4e8c\u91cc\u7684\u4e00\u4e2a\u5c0f\u5c71\u5188\u4e0a\u3002 \u3000\u3000\u4ed6\u4e70\u4e86\u4e00\u628a\u94c1\u94f2\uff0c\u4e00\u628a\u9e64\u5634\u9504\uff0c\u51fa\u5f97\u4e1c\u95e8\uff0c\u4e0d\u4e45\u4fbf\u627e\u5230\u4e86\u575f\u5893\u3002\u5893\u7891\u4e0a\u5199\u7740\u201c\u7231\u5973\u51cc\u971c\u534e\u4e4b\u5893\u201d\u4e03\u4e2a\u5b57\u3002\u5893\u524d\u65e0\u82b1\u65e0\u6811\u3002\u51cc\u59d1\u5a18\u751f\u524d\u6700\u7231\u9c9c\u82b1\uff0c\u5979\u7236\u4eb2\u7adf\u6ca1\u7ed9\u5979\u79cd\u690d\u4e00\u682a\u3002 \u3000\u3000\u201c\u7231\u5973\uff0c\u7231\u5973\uff0c\u563f\u563f\uff0c\u4f60\u771f\u7684\u7231\u8fd9\u4e2a\u5973\u513f\u4e48\uff1f\u201d\u4ed6\u51b7\u7b11\u8d77\u6765\uff0c\u60f3\u5230\u4e01\u5178\u548c\u621a\u82b3\uff0c\u5fcd\u4e0d\u4f4f\u6cea\u6c34\u53c8\u6d41\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u4ed6\u7684\u8863\u895f\uff0c\u65e9\u5c31\u4e3a\u60bc\u5ff5\u621a\u82b3\u7684\u773c\u6cea\u6e7f\u900f\u4e86\u3002\u5728\u51cc\u971c\u534e\u7684\u5893\u524d\uff0c\u53c8\u52a0\u4e0a\u4e86\u65b0\u7684\u773c\u6cea\u3002 \u3000\u3000\u5c71\u5188\u9644\u8fd1\u6ca1\u4eba\u5bb6\uff0c\u79bb\u5f00\u5927\u8def\u5f88\u8fdc\uff0c\u4e5f\u6ca1\u4eba\u7ecf\u8fc7\u3002\u4f46\u767d\u5929\u603b\u4e0d\u80fd\u5228\u575f\u3002\u76f4\u7b49\u5230\u5929\u5168\u9ed1\u4e86\uff0c\u624d\u6316\u5f00\u5893\u571f\uff0c\u518d\u6398\u5f00\u4e09\u5408\u4e0a\u5c01\u7740\u7684\u5927\u77f3\uff0c\u73b0\u51fa\u4e86\u68fa\u6728\u3002 \u3000\u3000\u7ecf\u5386\u4e86\u8fd9\u51e0\u5e74\u6765\u7684\u8270\u96be\u56f0\u82e6\uff0c\u72c4\u4e91\u65e9\u4e0d\u662f\u4e2a\u5bb9\u6613\u4f24\u5fc3\u3001\u5bb9\u6613\u6d41\u6cea\u7684\u4eba\u4e86\uff0c\u4f46\u5728\u60e8\u6de1\u7684\u6708\u5149\u4e0b\u89c1\u5230\u8fd9\u5177\u68fa\u6728\uff0c\u60f3\u5230\u4e86\u4e01\u5927\u54e5\u4fbf\u662f\u56e0\u8fd9\u53e3\u68fa\u6728\u800c\u6b7b\uff0c\u5374\u4e0d\u80fd\u4e0d\u518d\u4f24\u5fc3\uff0c\u4e0d\u80fd\u4e0d\u518d\u6d41\u6cea\u3002 \u3000\u3000\u51cc\u9000\u601d\u66fe\u5728\u68fa\u6728\u5916\u6d82\u4e0a\u201c\u91d1\u6ce2\u65ec\u82b1\u201d\u7684\u5267\u6bd2\uff0c\u867d\u7136\u65f6\u65e5\u76f8\u9694\u5df2\u4e45\uff0c\u800c\u4e14\u5c06\u68fa\u6728\u62ac\u5230\u6b64\u95f4\u4e0b\u846c\uff0c\u6599\u60f3\u68fa\u5916\u6bd2\u836f\u65e9\u5df2\u62b9\u53bb\uff0c\u4f46\u4ed6\u4e0d\u6562\u5192\u9669\u4f38\u624b\u53bb\u78b0\u68fa\u6728\uff0c\u62d4\u51fa\u8840\u5200\uff0c\u4ece\u68fa\u76d6\u7684\u7f1d\u53e3\u4e2d\u8f7b\u8f7b\u63a8\u4e86\u8fc7\u53bb\u3002\u90a3\u8840\u5200\u524a\u91d1\u65ad\u7389\uff0c\u9047\u5230\u6728\u6750\uff0c\u4fbf\u5982\u6279\u8c46\u8150\u4e00\u822c\uff0c\u4ed6\u4e0d\u7528\u4f7f\u52b2\uff0c\u4fbf\u5df2\u5c06\u68fa\u76d6\u7684\u7b0b\u5934\u5c3d\u6570\u5207\u65ad\uff0c\u53f3\u81c2\u4e00\u632f\uff0c\u52b2\u529b\u5230\u5904\uff0c\u68fa\u76d6\u98de\u8d77\u3002 \u3000\u3000\u84e6\u7136\u95f4\uff0c\u53ea\u89c1\u68fa\u6728\u4e2d\u4e24\u53ea\u5df2\u7136\u673d\u574f\u7684\u624b\u5411\u4e0a\u4e3e\u7740\u3002\u68fa\u76d6\u4e00\u98de\u8d77\uff0c\u4e24\u53ea\u624b\u4fbf\u6389\u4e86\u4e0b\u53bb\uff0c\u5b9b\u7136\u4f1a\u52a8\u4e00\u822c\u3002\u72c4\u4e91\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u60f3\uff1a\u201c\u51cc\u5c0f\u59d0\u5165\u68fa\u4e4b\u65f6\uff0c\u600e\u5730\u4e24\u53ea\u624b\u4f1a\u9ad8\u4e3e\u8d77\u6765\u7684\uff1f\u8fd9\u771f\u5947\u4e86\u3002\u201d\u53ea\u89c1\u68fa\u4e2d\u5e76\u65e0\u5bff\u8863\u3001\u88ab\u8925\u7b49\u4e00\u822c\u6b93\u846c\u4e4b\u7269\uff0c\u51cc\u5c0f\u59d0\u53ea\u7a7f\u4e00\u8eab\u5355\u8863\u3002 \u3000\u3000\u72c4\u4e91\u9ed8\u9ed8\u795d\u7977\uff1a\u201c\u4e01\u5927\u54e5\uff0c\u51cc\u5c0f\u59d0\uff0c\u4f60\u4e8c\u4eba\u751f\u65f6\u4e0d\u80fd\u6210\u4e3a\u592b\u59bb\uff0c\u6b7b\u540e\u540c\u846c\u7684\u5fc3\u613f\u7ec8\u4e8e\u5f97\u507f\u3002\u4f60\u4e8c\u4eba\u6b7b\u800c\u6709\u7075\uff0c\u4e5f\u5f53\u542b\u7b11\u4e8e\u4e5d\u6cc9\u4e4b\u4e0b\u4e86\u3002\u201d\u89e3\u4e0b\u80cc\u4e0a\u5305\u88b1\uff0c\u6253\u4e86\u5f00\u6765\uff0c\u5c06\u4e01\u5178\u7684\u9aa8\u7070\u6492\u5728\u51cc\u5c0f\u59d0\u5c38\u8eab\u4e0a\u3002\u4ed6\u8dea\u5728\u5730\u4e0b\uff0c\u606d\u606d\u656c\u656c\u7684\u62dc\u4e86\u56db\u62dc\uff0c\u7136\u540e\u7ad9\u8d77\u8eab\u6765\uff0c\u5c06\u5305\u9aa8\u7070\u7684\u5305\u88b1\u88f9\u5728\u624b\u4e0a\uff0c\u4fbf\u53bb\u63d0\u90a3\u68fa\u76d6\uff0c\u8981\u76d6\u56de\u68fa\u6728\u3002 \u3000\u3000\u6708\u5149\u659c\u7167\uff0c\u53ea\u89c1\u68fa\u76d6\u80cc\u9762\u9690\u9690\u5199\u7740\u6709\u5b57\u3002\u72c4\u4e91\u51d1\u8fd1\u4e00\u770b\uff0c\u53ea\u89c1\u90a3\u51e0\u4e2a\u5b57\u6b6a\u6b6a\u659c\u659c\uff0c\u5199\u7684\u662f\uff1a\u201c\u4e01\u90ce\uff0c\u4e01\u90ce\uff0c\u6765\u751f\u6765\u4e16\uff0c\u518d\u4e3a\u592b\u59bb\u3002\u201d \u3000\u3000\u72c4\u4e91\u5fc3\u4e2d\u4e00\u5bd2\uff0c\u4e00\u4ea4\u5750\u5728\u5730\u4e0b\uff0c\u8fd9\u51e0\u4e2a\u5b57\u663e\u662f\u6307\u7532\u6240\u523b\uff0c\u4ed6\u4e00\u51dd\u601d\u95f4\uff0c\u4fbf\u5df2\u660e\u767d\uff1a\u201c\u51cc\u59d1\u5a18\u662f\u7ed9\u5979\u7236\u4eb2\u6d3b\u57cb\u7684\uff0c\u653e\u5165\u68fa\u4e2d\u4e4b\u65f6\uff0c\u5979\u8fd8\u6ca1\u6b7b\u3002\u8fd9\u51e0\u4e2a\u5b57\uff0c\u662f\u5979\u4e34\u6b7b\u65f6\u7528\u6307\u7532\u523b\u7684\u3002\u56e0\u6b64\u4e00\u76f4\u5230\u6b7b\uff0c\u5979\u7684\u53cc\u624b\u59cb\u7ec8\u4e3e\u7740\u3002\u5929\u4e0b\u7adf\u6709\u8fd9\u822c\u72e0\u5fc3\u7684\u7236\u4eb2\uff01 \u3000\u3000\u4e01\u5927\u54e5\u59cb\u7ec8\u4e0d\u5c48\uff0c\u51cc\u59d1\u5a18\u59cb\u7ec8\u4e0d\u8d1f\u4e01\u5927\u54e5\u3002\u5979\u7236\u4eb2\u8d8a\u7b49\u8d8a\u6068\uff0c\u7ec8\u4e8e\u4e0b\u4e86\u8fd9\u6837\u7684\u6bd2\u624b\u3002\u201d\u53c8\u60f3\uff1a\u201c\u51cc\u77e5\u5e9c\u53d1\u89c9\u4e01\u5927\u54e5\u8d8a\u72f1\uff0c\u77e5\u9053\u5b9a\u4f1a\u53bb\u627e\u4ed6\u7b97\u5e10\uff0c\u6025\u5fd9\u5728\u68fa\u6728\u5916\u6d82\u4e0a\u2018\u91d1\u6ce2\u65ec\u82b1\u2019\u7684\u5267\u6bd2\u3002 \u3000\u3000\u8fd9\u4eba\u7684\u5fc3\u80a0\uff0c\u53ef\u6bd4\u2018\u91d1\u6ce2\u65ec\u82b1\u2019\u8fd8\u8981\u6bd2\u4e0a\u767e\u500d\u3002\u201d \u3000\u3000\u4ed6\u51d1\u8fd1\u68fa\u76d6\uff0c\u518d\u770b\u4e86\u4e00\u904d\u90a3\u4e24\u884c\u5b57\u3002\u53ea\u89c1\u8fd9\u51e0\u4e2a\u5b57\u4e4b\u4e0b\uff0c\u53c8\u5199\u7740\u4e09\u6392\u5b57\uff0c\u90fd\u662f\u4e9b\u201c\u4e94\u5341\u4e00\u3001\u4e09\u5341\u4e09\u3001\u4e8c\u5341\u516b\u201d\u7b49\u7b49\u6570\u76ee\u5b57\u3002\u72c4\u4e91\u62bd\u4e86\u4e00\u53e3\u51c9\u6c14\uff0c\u5fc3\u9053\uff1a\u201c\u662f\u4e86\uff0c\u51cc\u59d1\u5a18\u76f4\u5230\u4e34\u6b7b\uff0c\u8fd8\u8bb0\u7740\u548c\u4e01\u5927\u54e5\u5408\u846c\u7684\u5fc3\u613f\u3002\u5979\u7b54\u5e94\u8fc7\u4e01\u5927\u54e5\uff0c\u6709\u8c01\u80fd\u5c06\u5979\u548c\u4e01\u5927\u54e5\u5408\u846c\uff0c\u4fbf\u5c06\u8fde\u57ce\u5251\u8bc0\u7684\u79d8\u5bc6\u544a\u77e5\u6b64\u4eba\u3002\u4e01\u5927\u54e5\u5728\u5e9f\u56ed\u4e2d\u8ddf\u6211\u8bf4\u8fc7\u4e00\u4e9b\uff0c\u53ea\u662f\u6ca1\u8bf4\u5b8c\u4fbf\u6bd2\u53d1\u800c\u6b7b\u3002\u5e08\u7236\u90a3\u672c\u5251\u8c31\u4e0a\u7684\u79d8\u5bc6\uff0c\u7ed9\u5e08\u59b9\u7684\u773c\u6cea\u6d78\u4e86\u51fa\u6765\uff0c\u504f\u504f\u7ed9\u4e07\u6c0f\u7236\u5b50\u6495\u5f97\u7a00\u70c2\u3002\u6211\u53ea\u9053\u8fd9\u79d8\u5bc6\u4ece\u6b64\u6e6e\u6ca1\uff0c\u54ea\u77e5\u9053\u51cc\u59d1\u5a18\u5374\u5199\u5728\u8fd9\u91cc\u3002\u201d \u3000\u3000\u4ed6\u9ed8\u9ed8\u795d\u544a\uff1a\u201c\u51cc\u59d1\u5a18\uff0c\u4f60\u771f\u662f\u4fe1\u4eba\uff0c\u591a\u8c22\u4f60\u4e00\u756a\u597d\u5fc3\uff0c\u53ef\u662f\u6211\u6b64\u5fc3\u6210\u7070\uff0c\u6068\u4e0d\u5f97\u81ea\u6398\u4e00\u7a74\uff0c\u81ea\u520e\u800c\u6b7b\uff0c\u4f34\u5728\u4f60\u548c\u4e01\u5927\u54e5\u8eab\u8fb9\u3002\u53ea\u662f\u5927\u4ec7\u672a\u62a5\uff0c\u5c1a\u5f97\u53bb\u6740\u4e86\u4e07\u5bb6\u7236\u5b50\u548c\u4f60\u7236\u4eb2\u3002\u91d1\u94f6\u73e0\u5b9d\uff0c\u5728\u6211\u773c\u4e2d\u4fbf\u5982\u6ce5\u5c18\u4e00\u822c\u3002\u201d\u8bf4\u7740\u63d0\u8d77\u68fa\u76d6\uff0c\u6b63\u8981\u76d6\u4e0a\u68fa\u6728\uff0c\u84e6\u5730\u91cc\u7075\u673a\u4e00\u52a8\uff1a\u201c\u554a\u54df\uff0c\u5bf9\u4e86\uff01\u4e07\u6c0f\u7236\u5b50\u8fd9\u65f6\u4e0d\u77e5\u8eb2\u5230\u54ea\u91cc\uff0c\u4eca\u751f\u4eca\u4e16\u53ea\u6015\u518d\u4e5f\u627e\u4ed6\u4eec\u4e0d\u7740\uff0c\u4f46\u82e5\u5c06\u5927\u5b9d\u85cf\u7684\u79d8\u5bc6\u5199\u5728\u5f53\u773c\u4e4b\u5904\uff0c\u4e07\u6c0f\u7236\u5b50\u5fc5\u7136\u95fb\u8baf\u6765\u770b\u3002\u4e0d\u9519\uff0c\u8fd9\u79d8\u5bc6\u662f\u4e2a\u5927\u5927\u7684\u9999\u9975\uff0c\u4e07\u6c0f\u7236\u5b50\u7eb5\u7136\u8d77\u7591\uff0c\u518d\u6709\u5341\u500d\u7684\u5c0f\u5fc3\uff0c\u4e5f\u662f\u975e\u6765\u770b\u8fd9\u79d8\u5bc6\u4e0d\u53ef\u3002\u201d \u3000\u3000\u4ed6\u653e\u4e0b\u68fa\u76d6\uff0c\u770b\u6e05\u695a\u6570\u76ee\u5b57\uff0c\u4e00\u4e2a\u4e2a\u7528\u8840\u5200\u7684\u5200\u5c16\u5212\u5728\u94c1\u94f2\u80cc\u4e0a\uff0c\u523b\u5b8c\u540e\u6838\u5bf9\u4e00\u904d\u65e0\u8bef\uff0c\u8fd9\u624d\u76d6\u4e0a\u68fa\u76d6\uff0c\u653e\u597d\u77f3\u677f\uff0c\u6700\u540e\u5c06\u575f\u571f\u91cd\u65b0\u5806\u597d\u3002 \u3000\u3000\u201c\u8fd9\u4e2a\u5927\u5fc3\u613f\u662f\u5b8c\u4e86\uff01\u62a5\u4e86\u5927\u4ec7\u4e4b\u540e\uff0c\u987b\u5f97\u5728\u8fd9\u91cc\u79cd\u4e0a\u6570\u767e\u68f5\u83ca\u82b1\u3002\u4e01\u5927\u54e5\u548c\u51cc\u59d1\u5a18\u6700\u7231\u7684\u4fbf\u662f\u83ca\u82b1\u3002\u6700\u597d\u80fd\u627e\u5230\u2018\u6625\u6c34\u78a7\u6ce2\u2019\u7684\u540d\u79cd\u7eff\u83ca\u82b1\uff01\u201d \u3000\u3000\u7b2c\u4e8c\u5929\u65e9\u6668\uff0c\u6c5f\u9675\u5357\u95e8\u65c1\u7684\u57ce\u5899\u4e0a\uff0c\u8d6b\u7136\u51fa\u73b0\u4e86\u4e09\u884c\u7528\u77f3\u7070\u6c34\u4e66\u5199\u7684\u6570\u76ee\u5b57\u3002\u6bcf\u4e2a\u5b57\u90fd\u662f\u5c3a\u8bb8\u89c1\u65b9\uff0c\u8fdc\u8fdc\u4fbf\u80fd\u671b\u89c1\uff0c\u201c\u56db\u3001\u4e94\u5341\u4e00\u3001\u4e09\u5341\u4e09\u3001\u4e8c\u5341\u516b\u2026\u2026\u201d\u5947\u602a\u7684\u662f\uff0c\u8fd9\u51e0\u884c\u5b57\u79bb\u5730\u4e8c\u4e08\u6709\u4f59\uff0c\u6c5f\u9675\u57ce\u4e2d\u53ea\u6015\u6ca1\u90a3\u4e48\u957f\u7684\u68af\u5b50\uff0c\u80fd\u8ba9\u4eba\u722c\u4e0a\u53bb\u4e66\u5199\uff0c\u9664\u975e\u662f\u7528\u7ef3\u5b50\u7f12\u7740\u8eab\u5b50\uff0c\u4ece\u57ce\u5934\u4e0a\u6302\u4e0b\u6765\u5199\u3002 \u3000\u3000\u79bb\u8fd9\u51e0\u884c\u5b57\u5341\u4f59\u4e08\u7684\u57ce\u5899\u811a\u8fb9\uff0c\u72c4\u4e91\u626e\u4f5c\u4e86\u4e5e\u4e10\uff0c\u8131\u4e0b\u7834\u68c9\u8884\uff0c\u5750\u5728\u592a\u9633\u5e95\u4e0b\u6349\u8671\u5b50\u3002 \u3000\u3000\u4ece\u5357\u95e8\u8fdb\u8fdb\u51fa\u51fa\u7684\u4eba\u5f88\u591a\uff0c\u53ea\u51e0\u4e2a\u65f6\u8fb0\uff0c\u6c5f\u9675\u57ce\u4e2d\u8857\u5e02\u4e0a\u3001\u8336\u9986\u91cc\uff0c\u5c31\u6709\u4eba\u7eb7\u7eb7\u8c08\u8bba\uff0c\u4e5f\u6709\u4e0d\u5c11\u4eba\u5230\u5357\u95e8\u5916\u6765\u4eb2\u773c\u77a7\u77a7\u3002\u4f46\u8fd9\u4e9b\u6570\u76ee\u5b57\u9664\u4e86\u5199\u7684\u5730\u4f4d\u5947\u7279\u4e4b\u5916\uff0c\u5e76\u6ca1\u751a\u4e48\u597d\u770b\uff0c\u4e00\u822c\u95f2\u4eba\u770b\u4e86\u4e00\u4f1a\uff0c\u80e1\u4e71\u731c\u6d4b\u4e00\u756a\uff0c\u4fbf\u5373\u8d70\u4e86\uff0c\u5374\u6709\u597d\u51e0\u4e2a\u6c5f\u6e56\u8c6a\u5ba2\u7559\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u8fd9\u4e9b\u4eba\u624b\u4e2d\u90fd\u62ff\u7740\u4e00\u672c\u300a\u5510\u8bd7\u9009\u8f91\u300b\uff0c\u5c06\u57ce\u5899\u4e0a\u7684\u6570\u5b57\u6284\u4e86\u4e0b\u6765\uff0c\u76b1\u7740\u7709\u5934\u82e6\u82e6\u601d\u7d22\u3002 \u3000\u3000\u72c4\u4e91\u89c1\u5230\u5b59\u5747\u6765\u4e86\uff0c\u6c88\u57ce\u6765\u4e86\u3002\u8fc7\u4e86\u4e00\u4f1a\uff0c\u9c81\u5764\u4e5f\u6765\u4e86\u3002 \u3000\u3000\u4f46\u4ed6\u4eec\u5e76\u4e0d\u77e5\u9053\u201c\u8fde\u57ce\u5251\u6cd5\u201d\u6bcf\u4e00\u62db\u7684\u6b21\u5e8f\uff0c\u867d\u7136\u624b\u4e2d\u5404\u6709\u4e00\u90e8\u300a\u5510\u8bd7\u9009\u8f91\u300b\uff0c\u867d\u7136\u57ce\u5899\u4e0a\u5199\u7740\u5927\u5927\u7684\u6570\u5b57\uff0c\u53c8\u6599\u5230\u8fd9\u4e9b\u6570\u5b57\u5b9a\u662f\u5251\u8c31\u4e2d\u7684\u79d8\u5bc6\uff0c\u867d\u7136\u5077\u542c\u5230\u4e86\u5e08\u7236\u548c\u4ed6\u513f\u5b50\u53c2\u8be6\u79d8\u5bc6\u7684\u6cd5\u5b50\uff0c\u5374\u4e0d\u77e5\u6bcf\u4e00\u4e2a\u6570\u5b57\uff0c\u5e94\u5f53\u7528\u5728\u54ea\u4e00\u9996\u8bd7\u4e2d\u3002 \u3000\u3000\u8fd9\u4e16\u4e0a\uff0c\u53ea\u6709\u4e07\u9707\u5c71\u3001\u8a00\u8fbe\u5e73\u3001\u621a\u957f\u53d1\u4e09\u4e2a\u4eba\u77e5\u9053\u3002 \u3000\u3000\u9c81\u5764\u7b49\u4e09\u4eba\u5728\u6084\u6084\u8bae\u8bba\u3002\u9694\u5f97\u8fdc\u4e86\uff0c\u72c4\u4e91\u542c\u4e0d\u5230\u4ed6\u4eec\u7684\u8bf4\u8bdd\u3002\u53ea\u89c1\u4e09\u4eba\u8bf4\u4e86\u4e00\u4f1a\u8bdd\uff0c\u4fbf\u56de\u8fdb\u57ce\u53bb\uff0c\u8fc7\u4e0d\u591a\u65f6\uff0c\u4e09\u4e2a\u4eba\u90fd\u5316\u4e86\u88c5\u51fa\u6765\u3002\u4e00\u4e2a\u626e\u4f5c\u6c34\u679c\u8d29\u5b50\uff0c\u6311\u4e86\u4e00\u62c5\u6a58\u5b50\uff0c\u4e00\u4e2a\u626e\u4f5c\u83dc\u8d29\uff0c\u53e6\u4e00\u4e2a\u626e\u4f5c\u8377\u7740\u9504\u5934\u7684\u4e61\u6c11\u3002\u4e09\u4eba\u5750\u5728\u57ce\u5899\u811a\u8fb9\uff0c\u6ce8\u89c6\u6765\u5f80\u884c\u4eba\u3002 \u3000\u3000\u72c4\u4e91\u731c\u5230\u4e86\u4ed6\u4eec\u7684\u5fc3\u601d\u3002\u4ed6\u4eec\u5728\u7b49\u4e07\u9707\u5c71\u5230\u6765\u3002\u4ed6\u4eec\u53c2\u4e0d\u900f\u8fd9\u79d8\u5bc6\uff0c\u4f46\u53ea\u8981\u8ddf\u968f\u7740\u4e07\u9707\u5c71\uff0c\u4fbf\u80fd\u627e\u5230\u5b9d\u85cf\uff0c\u5c31\u7b97\u593a\u4e0d\u5230\uff0c\u5206\u4e00\u4efd\u603b\u6709\u6307\u671b\u3002\u518d\u548c\u5e08\u7236\u76f8\u89c1\u5f53\u7136\u5371\u9669\u4e07\u5206\uff0c\u53ef\u662f\u8981\u53d1\u5927\u8d22\uff0c\u600e\u80fd\u6015\u5371\u9669\uff1f \u3000\u3000\u300a\u8fde\u57ce\u5251\u8c31\u300b\u4e2d\u5934\u4e0a\u56db\u4e2a\u6570\u76ee\u5b57\u65e9\u5df2\u4f20\u5f00\u4e86\uff0c\u201c\u56db\u3001\u4e94\u5341\u4e00\u3001\u4e09\u5341\u4e09\u3001\u4e8c\u5341\u516b\u201d\uff0c\u90a3\u4fbf\u662f\u201c\u6c5f\u9675\u57ce\u5357\u201d\u201c\u56db\u3001\u4e94\u5341\u4e00\u3001\u4e09\u5341\u4e09\u3001\u4e8c\u5341\u516b\u201d\uff0c\u4ee5\u540e\u8fd8\u6709\u4e00\u8fde\u4e32\u7684\u6570\u5b57\uff0c\u518d\u8822\u7684\u4eba\uff0c\u4e5f\u60f3\u5f97\u5230\u90a3\u5fc5\u662f\u5251\u8c31\u4e2d\u7684\u79d8\u5bc6\u3002 \u3000\u3000\u5728\u57ce\u5899\u811a\u8fb9\u5750\u4e0b\u6765\u7684\u4eba\u8d8a\u6765\u8d8a\u591a\uff0c\u6709\u7684\u5316\u4e86\u88c5\uff0c\u6709\u7684\u5927\u6a21\u5927\u6837\u4ee5\u672c\u6765\u9762\u76ee\u51fa\u73b0\u3002\u72c4\u4e91\u6570\u4e86\u4e00\u6570\uff0c\u4e00\u5171\u6709\u4e03\u5341\u516b\u4eba\u3002\u518d\u8fc7\u4e00\u4f1a\uff0c\u535c\u57a3\u548c\u51af\u5766\u4e5f\u6765\u4e86\uff0c\u4ed6\u5e08\u5144\u5f1f\u4e8c\u4eba\u4e0d\u77e5\u4e3a\u751a\u4e48\u4e8b\u4e89\u5f97\u9762\u7ea2\u8033\u8d64\uff0c\u5dee\u70b9\u5c31\u8981\u6253\u67b6\uff0c\u4f46\u7ec8\u4e8e\u4e5f\u5b89\u9759\u4e0b\u6765\uff0c\u5750\u5728\u62a4\u57ce\u6cb3\u65c1\u3002 \u3000\u3000\u7b49\u5230\u4e0b\u5348\uff0c\u4e07\u6c0f\u7236\u5b50\u6ca1\u51fa\u73b0\u3002\u7b49\u5230\u508d\u665a\uff0c\u4e07\u6c0f\u7236\u5b50\u4ecd\u662f\u6ca1\u51fa\u73b0\u3002\u8bb8\u591a\u4eba\u5df2\u5728\u7834\u53e3\u5927\u9a82\u3002\u4e07\u5bb6\u7684\u7956\u5b97\u7a81\u7136\u58f0\u540d\u5927\u566a\uff0c\u5c24\u5176\u662f\u4e07\u9707\u5c71\u7684\u5976\u5976\u3002 \u3000\u3000\u5929\u5feb\u9ed1\u4e86\uff0c\u4e00\u4e2a\u6559\u4e66\u5148\u751f\u6a21\u6837\u7684\u4eba\u62ff\u4e86\u4e00\u5f20\u7eb8\uff0c\u4e00\u53ea\u58a8\u76d2\uff0c\u4e00\u679d\u7b14\uff0c\u6447\u5934\u6643\u8111\u7684\uff0c\u5c06\u57ce\u5899\u4e0a\u8fd9\u51e0\u884c\u5b57\u6284\u4e86\u4e0b\u6765\u3002\u4e00\u6761\u5927\u6c49\u6b63\u95f7\u5f97\u6ca1\u5730\u65b9\u51fa\u6c14\uff0c\u4e00\u628a\u6293\u4f4f\u90a3\u4eba\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u6284\u8fd9\u4e9b\u5b57\u5e72\u751a\u4e48\uff1f\u201d\u90a3\u5148\u751f\u9053\uff1a\u201c\u8001\u592b\u81ea\u6709\u7528\u5904\uff0c\u65c1\u4eba\u4e0d\u5f97\u800c\u95ee\u4e4b\u4e5f\u3002\u201d \u3000\u3000\u90a3\u5927\u6c49\u9053\uff1a\u201c\u4f60\u8bf4\u4e0d\u8bf4\uff1f\u4e0d\u8bf4\uff0c\u6211\u5c31\u6253\u3002\u201d\u63d0\u8d77\u918b\u94b5\u5927\u7684\u62f3\u5934\uff0c\u5728\u4ed6\u9f3b\u5c16\u524d\u6447\u6765\u6643\u53bb\u3002\u90a3\u5148\u751f\u5413\u6015\u4e86\uff0c\u9053\uff1a\u201c\u662f\u2026\u2026\u662f\u4eba\u5bb6\u53eb\u6211\u6765\u6284\u7684\u3002\u201d\u90a3\u5927\u6c49\u9053\uff1a\u201c\u8c01\u53eb\u4f60\u6284\u7684\uff1f\u201d\u90a3\u5148\u751f\u9053\uff1a\u201c\u4e00\u4f4d\u8001\u5148\u751f\uff0c\u4e0d\u2026\u2026\u4e0d\u7792\u4f60\u8bf4\uff0c\u5c31\u662f\u672c\u57ce\u5927\u540d\u9f0e\u9f0e\u7684\u4e07\u9707\u5c71\u4e07\u8001\u5148\u751f\uff0c\u4f60\u2026\u2026\u4f60\u53ef\u5f97\u7f6a\u4ed6\u8001\u4eba\u5bb6\u4e0d\u5f97\u3002\u201d \u3000\u3000\u201c\u4e07\u9707\u5c71\u201d\u8fd9\u4e09\u4e2a\u5b57\u4e00\u51fa\u53e3\uff0c\u4f17\u4eba\u4fbf\u54c4\u4e86\u8d77\u6765\u3002\u72c4\u4e91\u66f4\u662f\u6b22\u559c\uff0c\u53ea\u662f\u8fd9\u4efd\u6b22\u559c\u4e4b\u4e2d\uff0c\u6df7\u7740\u592a\u591a\u7684\u4ec7\u6068\u548c\u4f24\u5fc3\u3002 \u3000\u3000\u90a3\u5148\u751f\u6218\u6218\u5162\u5162\u7684\u5728\u524d\u9762\u8d70\uff0c\u4e00\u811a\u9ad8\uff0c\u4e00\u811a\u4f4e\uff0c\u8dcc\u8dcc\u649e\u649e\u7684\u76f4\u5411\u4e1c\u884c\uff0c\u4e00\u767e\u591a\u4eba\u8fdc\u8fdc\u7684\u8ddf\u7740\u3002\u4e07\u9707\u5c71\u65e2\u7136\u4e0d\u6765\uff0c\u4fbf\u53bb\u627e\u4e07\u9707\u5c71\u3002\u53ea\u6709\u4ed6\uff0c\u624d\u53c2\u8be6\u5f97\u51fa\u5176\u4e2d\u7684\u79d8\u5bc6\u3002\u8fd9\u4ef6\u4e8b\u5df2\u7ecf\u63ed\u660e\u4e86\uff0c\u4eba\u591a\u52bf\u4f17\uff0c\u8981\u786c\u903c\u7740\u4e07\u9707\u5c71\u53bb\u627e\u5b9d\u85cf\u3002\u8bb8\u591a\u4eba\u79f0\u8d5e\u90a3\u5927\u6c49\uff1a\u201c\u5e78\u4e8f\u4f60\u8001\u54e5\u806a\u660e\uff0c\u6211\u4eec\u600e\u4e48\u6ca1\u60f3\u5230\u4e07\u9707\u5c71\u4f1a\u6d3e\u4eba\u6765\u6284\u6570\u76ee\u5b57\uff1f\u8981\u4e0d\u662f\u4f60\u8001\u54e5\uff0c\u5927\u4f19\u513f\u5728\u57ce\u95e8\u8fb9\u7b49\u4e0a\u4e09\u5929\u4e09\u591c\uff0c\u4e07\u9707\u5c71\u5374\u65e9\u5c06\u5b9d\u85cf\u8d77\u4e86\u53bb\u5566\u3002\u201d\u90a3\u5927\u6c49\u5f88\u662f\u5f97\u610f\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u9178\u79c0\u624d\u9b3c\u9b3c\u795f\u795f\uff0c\u6211\u6599\u5f97\u4ed6\u5e72\u7684\u4e0d\u662f\u597d\u4e8b\u3002\u201d\u4f3c\u4e4e\u4ed6\u81ea\u5df1\u5e72\u7684\u5374\u662f\u597d\u4e8b\u3002 \u3000\u3000\u72c4\u4e91\u6df7\u5728\u4eba\u7fa4\u4e4b\u4e2d\uff0c\u9690\u9690\u89c9\u5f97\uff1a\u201c\u4e07\u9707\u5c71\u8001\u5978\u5de8\u6ed1\uff0c\u51b3\u4e0d\u4f1a\u8fd9\u6837\u8f7b\u6613\u4fbf\u7ed9\u4eba\u627e\u5230\u3002\u5176\u4e2d\u5b9a\u7136\u53e6\u6709\u9b3c\u8ba1\u3002\u201d\u8fd9\u65f6\u4e00\u884c\u4eba\u79bb\u5f00\u5357\u95e8\u5df2\u6709\u6570\u91cc\uff0c\u4ed6\u56de\u8fc7\u5934\u6765\uff0c\u53c8\u5411\u57ce\u5899\u671b\u53bb\uff0c\u4e00\u77a5\u773c\u95f4\uff0c\u53ea\u89c1\u4e00\u6761\u4eba\u5f71\u4ece\u57ce\u5899\u8fb9\u98de\u5feb\u63a0\u8fc7\uff0c\u5411\u897f\u75be\u5954\u3002 \u3000\u3000\u72c4\u4e91\u5bfb\u601d\uff1a\u201c\u8fd9\u4e00\u7fa4\u4eba\u9489\u7740\u8fd9\u4e2a\u6559\u4e66\u5148\u751f\uff0c\u51b3\u8ba1\u4e0d\u6015\u4ed6\u8d70\u4e86\u3002\u4ed6\u4eec\u82e5\u662f\u627e\u5230\u4e07\u9707\u5c71\uff0c\u4e5f\u51b3\u4e0d\u4f1a\u79bb\u5f00\u4e86\u4ed6\u3002\u504c\u5927\u4e00\u5ea7\u6c5f\u9675\u57ce\uff0c\u8981\u5bfb\u4e07\u6c0f\u7236\u5b50\u662f\u5341\u5206\u8270\u96be\uff0c\u4f46\u8981\u627e\u8fd9\u4e48\u4e71\u4e03\u516b\u7cdf\u7684\u4e00\u5927\u7fa4\u4eba\uff0c\u5374\u662f\u6613\u8fc7\u53cd\u638c\uff0c\u6211\u4f55\u5fc5\u8ddf\u5728\u4eba\u7fa4\u4e4b\u4e2d\uff1f\u201d \u3000\u3000\u4ed6\u5fc3\u5ff5\u4e00\u52a8\uff0c\u95ea\u8eab\u9690\u5728\u4e00\u682a\u6811\u540e\uff0c\u968f\u5373\u5c55\u5f00\u8f7b\u529f\uff0c\u53cd\u8eab\u5954\u5411\u5357\u95e8\uff0c\u66f4\u5411\u897f\u884c\u3002\u5faa\u7740\u90a3\u4eba\u5f71\u7684\u53bb\u5411\u6025\u5954\uff0c\u4e0d\u5230\u4e00\u76cf\u8336\u65f6\u5206\u4fbf\u8ffd\u4e0a\u4e86\u3002\u90a3\u4eba\u8f7b\u529f\u4e5f\u751a\u4e86\u5f97\uff0c\u4f46\u6bd4\u4e4b\u72c4\u4e91\u5374\u53c8\u5dee\u5f97\u8fdc\u4e86\u3002\u4ed6\u4e1d\u6beb\u4e0d\u89c9\u6709\u4eba\u8ddf\u968f\uff0c\u53ea\u662f\u5feb\u6b65\u5954\u8dd1\u3002 \u3000\u3000\u72c4\u4e91\u89c1\u4ed6\u5954\u5230\u4e00\u95f4\u5c0f\u5c4b\u4e4b\u524d\uff0c\u63a8\u95e8\u5165\u5185\u3002\u72c4\u4e91\u5b88\u5728\u95e8\u5916\uff0c\u7b49\u4ed6\u51fa\u6765\uff0c\u8fc7\u4e86\u4e00\u4f1a\uff0c\u5374\u89c1\u5c0f\u5c4b\u7684\u7a97\u5b50\u4e2d\u900f\u51fa\u4e86\u706f\u5149\u3002 \u3000\u3000\u4ed6\u95ea\u5230\u7a97\u4e0b\uff0c\u4ece\u7a97\u7f1d\u4e2d\u5411\u5185\u671b\u53bb\uff0c\u53ea\u89c1\u5c4b\u91cc\u5750\u7740\u4e2a\u8001\u8005\uff0c\u80cc\u5411\u7a97\u5b50\uff0c\u77a7\u4e0d\u89c1\u4ed6\u7684\u9762\u5bb9\u3002 \u3000\u3000\u90a3\u8001\u8005\u5728\u684c\u4e0a\u644a\u5f00\u4e00\u672c\u4e66\u6765\uff0c\u72c4\u4e91\u4e00\u89c1\u4fbf\u77e5\u662f\u300a\u5510\u8bd7\u9009\u8f91\u300b\uff0c\u8fd9\u672c\u4e66\u8fd1\u65e5\u6765\u5728\u6c5f\u9675\u57ce\u4e2d\u6d41\u884c\u6781\u5e7f\uff0c\u5c45\u7136\u8fd9\u8001\u8005\u672a\u80fd\u514d\u4fd7\uff0c\u4e5f\u6709\u4e00\u672c\u3002\u53ea\u89c1\u4ed6\u53d6\u8fc7\u4e00\u679d\u79c3\u7b14\uff0c\u5728\u4e00\u5f20\u9ec4\u7eb8\u4e0a\u5199\u4e86\u201c\u6c5f\u9675\u57ce\u5357\u201d\u56db\u4e2a\u5b57\uff0c\u4ed6\u53e3\u4e2d\u8f7b\u8f7b\u5ff5\u7740\u201c\u4e00\u4e94\u3001\u4e00\u5341\u3001\u5341\u4e94\u3001\u5341\u516d\u3001\u2026\u2026\u7b2c\u5341\u516d\u4e2a\u5b57\u201d\uff0c\u8ddf\u7740\u5728\u7eb8\u4e0a\u5199\u4e2a\u201c\u504f\u201d\u5b57\u3002 \u3000\u3000\u72c4\u4e91\u5927\u5403\u4e00\u60ca\uff1a\u201c\u8fd9\u4eba\u5c45\u7136\u80fd\u5728\u8fd9\u672c\u5510\u8bd7\u4e2d\u67e5\u5f97\u5230\u5b57\uff0c\u96be\u9053\u4ed6\u4e5f\u4f1a\u8fde\u57ce\u5251\u6cd5\uff1f\u201d\u77a7\u4ed6\u80cc\u5f71\uff0c\u663e\u7136\u4e0d\u662f\u4e07\u9707\u5c71\u3002\u8fd9\u8001\u8005\u7a7f\u7740\u4e00\u4ef6\u655d\u65e7\u7684\u7070\u8272\u5e03\u888d\uff0c\u77a7\u4e0d\u51fa\u662f\u751a\u4e48\u8eab\u4efd\u3002 \u3000\u3000\u53ea\u89c1\u4ed6\u67e5\u4e00\u4f1a\u4e66\uff0c\u5c48\u6307\u8ba1\u4e00\u4f1a\u6570\uff0c\u4fbf\u5199\u4e00\u4e2a\u5b57\uff0c\u4e00\u5171\u5199\u4e86\u5eff\u516d\u4e2a\u5b57\u3002\u72c4\u4e91\u4e00\u4e2a\u5b57\u3001\u4e00\u4e2a\u5b57\u7684\u8bfb\u4e0b\u53bb\uff0c\u89c1\u662f\uff1a \u3000\u3000\u201c\u2026\u2026\u897f\u5929\u5b81\u5bfa\u5927\u6bbf\u4f5b\u50cf\u5411\u4e4b\u8654\u8bda\u819c\u62dc\u901a\u7075\u795d\u544a\u5982\u6765\u8d50\u798f\u5f80\u751f\u6781\u4e50\u201d\u3002 \u3000\u3000\u90a3\u8001\u8005\u5927\u6012\uff0c\u5c06\u7b14\u6746\u91cd\u91cd\u5728\u684c\u4e0a\u4e00\u62cd\uff0c\u8bf4\u9053\uff1a\u201c\u751a\u4e48\u2018\u5411\u4e4b\u8654\u8bda\u819c\u62dc\uff0c\u901a\u7075\u795d\u544a\u2019\uff0c\u53c8\u751a\u4e48\u2018\u5982\u6765\u8d50\u798f\uff0c\u5f80\u751f\u6781\u4e50\u2019\uff01\u4ed6\u5976\u5976\u7684\uff0c\u2018\u5f80\u751f\u6781\u4e50\u2019\uff0c\u8fd9\u4e0d\u662f\u53eb\u4eba\u53bb\u89c1\u5341\u6bbf\u960e\u738b\u4e48\uff1f\u201d \u3000\u3000\u72c4\u4e91\u542c\u8fd9\u4eba\u53e3\u97f3\u6781\u719f\uff0c\u6b63\u601d\u7d22\u95f4\uff0c\u90a3\u4eba\u4fa7\u5934\u56de\u8fc7\u8138\u6765\u3002\u72c4\u4e91\u8eab\u5b50\u4e00\u77ee\uff0c\u7f29\u5728\u7a97\u4e0b\uff0c\u5fc3\u9053\uff1a\u201c\u662f\u4e8c\u5e08\u4f2f\uff0c\u65e0\u602a\u4ed6\u77e5\u9053\u5251\u62db\u3002 \u3000\u3000\u8fd9\u5374\u53c8\u662f\u751a\u4e48\u79d8\u5bc6\u4e86\uff1f\u539f\u6765\u662f\u620f\u5f04\u4eba\u7684\u3002\u201d\u5fc3\u4e2d\u5fcd\u4e0d\u4f4f\u597d\u7b11\uff1a \u3000\u3000\u201c\u8fd9\u8bb8\u591a\u4eba\u82b1\u4e86\u504c\u5927\u5fc3\u601d\uff0c\u4e0d\u60dc\u5f11\u5e08\u7236\u3001\u5bb3\u540c\u95e8\uff0c\u539f\u6765\u53ea\u662f\u4e00\u53e5\u4f5c\u5f04\u4eba\u7684\u8bdd\u3002\u201d \u3000\u3000\u4ed6\u6ca1\u7b11\u51fa\u58f0\u6765\uff0c\u4f46\u5728\u5c4b\u4e2d\uff0c\u8a00\u8fbe\u5e73\u5374\u5927\u7b11\u8d77\u6765\uff1a\u201c\u54c8\u54c8\uff0c\u53eb\u6211\u5411\u5982\u6765\u4f5b\u8654\u8bda\u819c\u62dc\uff0c\u901a\u7075\u795d\u544a\uff0c\u8fd9\u6ce5\u5851\u6728\u96d5\u7684\u4ed6\u5988\u7684\u81ed\u83e9\u8428\u4fbf\u4f1a\u8d50\u798f\u4e8e\u6211\uff0c\u54c8\u54c8\uff0c\u4ed6\u5976\u5976\u7684\uff0c\u53eb\u8001\u5b50\u5f80\u751f\u6781\u4e50\u3002\u6211\u4eec\u5408\u529b\u6740\u4e86\u5e08\u7236\uff0c\u5e08\u5144\u5f1f\u4e09\u4eba\u4f60\u4e89\u6211\u593a\uff0c\u539f\u6765\u662f\u5927\u5bb6\u8981\u4e89\u4e2a\u2018\u5f80\u751f\u6781\u4e50\u2019\u3002\u6c5f\u9675\u57ce\u4e2d\u8fd9\u51e0\u767e\u6761\u82f1\u96c4\u597d\u6c49\u3001\u4e4c\u9f9f\u8d3c\u5f3a\u76d7\uff0c\u4e89\u6765\u4e89\u53bb\uff0c\u4e3a\u7684\u90fd\u662f\u8981\u2018\u5f80\u751f\u6781\u4e50\u2019\uff0c\u54c8\u54c8\uff0c\u54c8\u54c8\uff01\u201d\u7b11\u58f0\u4e2d\u5374\u5145\u6ee1\u4e86\u51c4\u60e8\u4e4b\u610f\uff0c\u4e00\u9762\u7b11\uff0c\u4e00\u9762\u5c06\u9ec4\u7eb8\u626f\u5f97\u7c89\u7cb9\u3002 \u3000\u3000\u7a81\u7136\u4e4b\u95f4\uff0c\u4ed6\u7ad9\u7740\u4e00\u52a8\u4e0d\u52a8\uff0c\u53cc\u76ee\u6014\u6014\u7684\u77a7\u7740\u7a97\u5916\u3002 \u3000\u3000\u72c4\u4e91\u60f3\u8d77\u81ea\u5df1\u6240\u4ee5\u906d\u6b64\u5927\u96be\u3001\u621a\u82b3\u6240\u4ee5\u60e8\u6b7b\uff0c\u8d77\u56e0\u7686\u5728\u8fd9\u8fde\u57ce\u5251\u8bc0\u7684\u79d8\u5bc6\uff0c\u800c\u8fd9\u79d8\u5bc6\u7adf\u662f\u51e0\u53e5\u620f\u8c11\u4e4b\u8a00\uff0c\u5fc3\u4e0b\u60b2\u6124\u4e4b\u6781\uff0c\u5fcd\u4e0d\u4f4f\u4e5f\u8981\u7eb5\u58f0\u957f\u7b11\u3002 \u3000\u3000\u4fbf\u5728\u6b64\u65f6\uff0c\u53ea\u89c1\u8a00\u8fbe\u5e73\u773c\u671b\u7a97\u5916\uff0c\u4f3c\u4e4e\u89c1\u5230\u4e86\u751a\u4e48\u3002\u53ea\u542c\u4ed6\u5583\u5583\u81ea\u8bed\uff1a\u201c\u5230\u4e86\u8fd9\u6b65\u7530\u5730\uff0c\u53bb\u5929\u5b81\u5bfa\u77a7\u77a7\uff0c\u90a3\u4e5f\u4e0d\u59a8\u3002 \u3000\u3000\u6c5f\u9675\u57ce\u5357\u504f\u897f\uff0c\u4e0d\u9519\uff0c\u786e\u662f\u6709\u8fd9\u4e48\u4e00\u5ea7\u53e4\u5e99\u3002\u201d\u4ed6\u4e00\u6325\u624b\uff0c\u62e8\u7184\u4e86\u6cb9\u706f\uff0c\u63a8\u95e8\u51fa\u6765\uff0c\u5c55\u5f00\u8f7b\u529f\u5411\u897f\u5954\u53bb\u3002 \u3000\u3000\u72c4\u4e91\u5fc3\u4e0b\u8fdf\u7591\uff1a\u201c\u6211\u53bb\u5bfb\u4e07\u9707\u5c71\u5462\uff0c\u8fd8\u662f\u8ddf\u8a00\u5e08\u4f2f\u53bb\uff1f\u55ef\uff0c\u90a3\u4e00\u5927\u6279\u4eba\u6613\u627e\u5f97\u7d27\uff0c\u8fd8\u662f\u5148\u8ddf\u7740\u8a00\u5e08\u4f2f\u77a7\u77a7\u3002\u201d\u5f53\u4e0b\u76ef\u4f4f\u8a00\u8fbe\u5e73\u7684\u80cc\u5f71\uff0c\u8ffd\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u4e0d\u5230\u5c0f\u534a\u4e2a\u65f6\u8fb0\uff0c\u8a00\u8fbe\u5e73\u4fbf\u5df2\u5230\u4e86\u5929\u5b81\u5bfa\u53e4\u5e99\u4e4b\u5916\u3002\u4ed6\u5148\u5728\u5e99\u5916\u503e\u542c\u534a\u664c\uff0c\u53c8\u7ed5\u7740\u90a3\u5e99\u8f6c\u4e86\u4e00\u4e2a\u5708\u5b50\uff0c\u542c\u5f97\u5e99\u5185\u5e99\u5916\u9759\u6084\u6084\u5730\u5e76\u65e0\u4eba\u8e2a\uff0c\u8fd9\u624d\u63a8\u95e8\u800c\u5165\u3002 \u3000\u3000\u8fd9\u5929\u5b81\u5bfa\u5730\u5904\u8352\u50fb\uff0c\u5e74\u4e45\u5931\u4fee\uff0c\u5e99\u5185\u4e5f\u65e0\u5e99\u795d\u548c\u5c1a\u3002\u8a00\u8fbe\u5e73\u6765\u5230\u5927\u6bbf\uff0c\u4e00\u6643\u706b\u6298\uff0c\u4fbf\u8981\u53bb\u70b9\u795e\u575b\u4e0a\u7684\u8721\u70db\uff0c\u706b\u5149\u4e4b\u4e0b\uff0c\u53ea\u89c1\u70db\u6cea\u4f3c\u4e4e\u9887\u4e3a\u65b0\u9c9c\uff0c\u5fc3\u5ff5\u4e00\u52a8\uff0c\u4f38\u624b\u53bb\u634f\u4e86\u634f\uff0c\u679c\u7136\u70db\u6cea\u67d4\u8f6f\uff0c\u663e\u7136\u4e0d\u4e45\u4e4b\u524d\u6709\u4eba\u70b9\u8fc7\u8fd9\u8721\u70db\u3002\u4ed6\u5fc3\u4e0b\u8d77\u7591\uff0c\u5439\u7184\u4e86\u706b\u6298\uff0c\u6b63\u8981\u4e3e\u6b65\u51fa\u5916\u67e5\u5bdf\uff0c\u7a81\u89c9\u80cc\u540e\u4e00\u75db\uff0c\u4e00\u67c4\u5229\u5203\u63d2\u8fdb\u8eab\u5b50\uff0c\u5927\u53eb\u4e00\u58f0\uff0c\u4fbf\u5373\u6bd9\u547d\u3002 \u3000\u3000\u72c4\u4e91\u8eb2\u5728\u4e8c\u95e8\u4e4b\u540e\uff0c\u53ea\u89c1\u706b\u5149\u9661\u7184\uff0c\u8a00\u8fbe\u5e73\u4fbf\u5373\u60e8\u547c\uff0c\u77e5\u4ed6\u5df2\u906d\u6697\u7b97\uff0c\u8fd9\u4e00\u4e0b\u4e8b\u8d77\u4ed3\u5352\uff0c\u4e0d\u53ca\u6551\u63f4\u3002\u4ed6\u7d22\u6027\u4e0d\u52a8\uff0c\u8981\u77a7\u4f24\u5bb3\u8a00\u8fbe\u5e73\u7684\u662f\u8c01\u3002\u9ed1\u6697\u4e2d\u53ea\u542c\u5f97\u4e00\u4eba\u201c\u563f\uff0c\u563f\uff0c\u563f\u201d\u51b7\u7b11\u3002\u8fd9\u58f0\u97f3\u4f20\u5165\u8033\u4e2d\uff0c\u72c4\u4e91\u4e0d\u7531\u5f97\u6bdb\u9aa8\u609a\u7136\uff0c\u8fd9\u7b11\u58f0\u9634\u68ee\u53ef\u6016\uff0c\u5374\u53c8\u5341\u5206\u719f\u6089\u3002 \u3000\u3000\u7a81\u7136\u95f4\u706b\u5149\u6296\u52a8\uff0c\u6709\u4eba\u70b9\u4eae\u4e86\u8721\u70db\uff0c\u70db\u5149\u5c04\u5230\u90a3\u4eba\u8eab\u4e0a\u3002 \u3000\u3000\u90a3\u4eba\u6162\u6162\u7684\u4fa7\u8fc7\u8138\u6765\u3002 \u3000\u3000\u72c4\u4e91\u9669\u4e9b\u8131\u53e3\u547c\u51fa\uff1a\u201c\u5e08\u7236\uff01\u201d \u3000\u3000\u8fd9\u4eba\u7adf\u662f\u621a\u957f\u53d1\u3002\u53ea\u89c1\u4ed6\u5411\u8a00\u8fbe\u5e73\u7684\u5c38\u8eab\u8e22\u4e86\u4e00\u811a\uff0c\u62d4\u51fa\u4ed6\u80cc\u4e0a\u7684\u957f\u5251\uff0c\u53c8\u5728\u4ed6\u80cc\u5fc3\u4e0a\u8fde\u523a\u6570\u5251\u3002 \u3000\u3000\u72c4\u4e91\u89c1\u5230\u5e08\u7236\u6740\u5bb3\u81ea\u5df1\u7684\u540c\u95e8\u5e08\u5144\uff0c\u624b\u6bb5\u7adf\u5982\u6b64\u72e0\u6bd2\u6b8b\u5fcd\uff0c\u8fd9\u53e5\u201c\u5e08\u7236\u201d\u7684\u547c\u58f0\u521a\u5230\u53e3\u8fb9\uff0c\u4fbf\u786c\u751f\u751f\u7684\u5fcd\u4f4f\u4e86\u3002 \u3000\u3000\u621a\u957f\u53d1\u563f\u563f\u51b7\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4e8c\u5e08\u54e5\uff0c\u4f60\u4e5f\u67e5\u5230\u4e86\u8fde\u57ce\u5251\u8c31\u4e2d\u7684\u79d8\u5bc6\uff0c\u662f\u4e0d\u662f\uff1f\u563f\u563f\uff01\u2018\u6c5f\u9675\u57ce\u5357\u504f\u897f\uff0c\u5929\u5b81\u5bfa\u5927\u6bbf\u4f5b\u50cf\uff0c\u5411\u4e4b\u8654\u8bda\u819c\u62dc\uff0c\u901a\u7075\u795d\u544a\u2019\uff0c\u54c8\u54c8\uff0c\u4e8c\u5e08\u54e5\uff0c\u5251\u8c31\u4e2d\u8bf4\uff1a\u2018\u5982\u6765\u8d50\u798f\uff0c\u5f80\u751f\u6781\u4e50\u2019\uff0c\u4f60\u73b0\u4e0b\u4e0d\u662f\u5f80\u751f\u6781\u4e50\u4e86\u4e48\uff1f\u8fd9\u4e0d\u662f\u5982\u6765\u8d50\u798f\u4e86\u4e48\uff1f\u201d\u4ed6\u8f6c\u8fc7\u5934\u6765\uff0c\u671b\u7740\u90a3\u5c0a\u9762\u76ee\u6148\u7965\u7684\u5982\u6765\u4f5b\u50cf\u3002\u4ed6\u8138\u4e0a\u5806\u6ee1\u623e\u6c14\uff0c\u6076\u72e0\u72e0\u7aef\u8be6\u534a\u664c\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u5976\u5976\u7684\u81ed\u4f5b\uff0c\u620f\u5f04\u4e86\u8001\u5b50\u4e00\u751f\uff0c\u5751\u5bb3\u5f97\u6211\u53ef\u5c31\u82e6\u4e86\uff01\u201d\u7eb5\u8eab\u4e0a\u4e86\u795e\u575b\uff0c\u63d0\u8d77\u957f\u5251\uff0c\u5f53\u5f53\u5f53\u4e09\u54cd\uff0c\u5728\u4f5b\u50cf\u8179\u4e0a\u8fde\u780d\u4e09\u5251\u3002 \u3000\u3000\u4e00\u822c\u4f5b\u50cf\u5747\u662f\u6ce5\u5851\u6728\u96d5\uff0c\u4f46\u8fd9\u4e09\u5251\u780d\u5728\u5176\u4e0a\uff0c\u5374\u53d1\u51fa\u94ee\u94ee\u94ee\u7684\u91d1\u5c5e\u4e4b\u58f0\u3002\u621a\u957f\u53d1\u4e00\u6014\uff0c\u53c8\u780d\u4e86\u4e24\u5251\uff0c\u4f46\u89c9\u7740\u5251\u5904\u6781\u662f\u575a\u786c\u3002\u4ed6\u62ff\u8d77\u70db\u53f0\u51d1\u8fd1\u4e00\u770b\uff0c\u53ea\u89c1\u5251\u75d5\u6df1\u5370\uff0c\u9732\u51fa\u707f\u70c2\u91d1\u5149\uff0c\u621a\u957f\u53d1\u4e00\u5446\uff0c\u4f38\u6307\u5c06\u4e24\u6761\u5251\u75d5\u4e4b\u95f4\u7684\u6ce5\u571f\u5265\u843d\uff0c\u4f46\u89c1\u95ea\u95ea\u53d1\u5149\uff0c\u91cc\u9762\u7adf\u7136\u90fd\u662f\u9ec4\u91d1\u3002\u4ed6\u5fcd\u4e0d\u4f4f\u53eb\u9053\uff1a\u201c\u5927\u91d1\u4f5b\uff0c\u90fd\u662f\u9ec4\u91d1\uff0c\u90fd\u662f\u9ec4\u91d1\uff01\u201d \u3000\u3000\u8fd9\u5ea7\u4f5b\u50cf\u9ad8\u903e\u4e09\u4e08\uff0c\u7c97\u58ee\u80a5\u5927\uff0c\u8fdc\u8d85\u5bfb\u5e38\u4f5b\u50cf\uff0c\u5982\u679c\u901a\u4f53\u7adf\u662f\u9ec4\u91d1\u94f8\u6210\uff0c\u5c11\u8bf4\u4e5f\u6709\u4e94\u516d\u4e07\u65a4\uff0c\u90a3\u4e0d\u662f\u5927\u5b9d\u85cf\u662f\u751a\u4e48\uff1f \u3000\u3000\u4ed6\u72c2\u559c\u4e4b\u4e0b\uff0c\u5fae\u4e00\u51dd\u601d\uff0c\u8f6c\u5230\u4f5b\u50cf\u80cc\u540e\uff0c\u4e3e\u5251\u6279\u524a\uff0c\u89c1\u4f5b\u50cf\u8170\u95f4\u4f3c\u6709\u4e00\u6247\u5c0f\u5c0f\u6697\u95e8\u3002\u4ed6\u4e0d\u4f4f\u7528\u529b\u780d\u524a\uff0c\u6ce5\u571f\u56db\u6e85\uff0c\u53ea\u5c06\u957f\u5251\u524a\u5f97\u5d29\u4e86\u6570\u5341\u4e2a\u7f3a\u53e3\uff0c\u624d\u5c06\u6697\u95e8\u56db\u5468\u7684\u6ce5\u571f\u90fd\u524a\u53bb\u4e86\u3002 \u3000\u3000\u53ea\u89c1\u90a3\u6697\u95e8\u4e5f\u662f\u9ec4\u91d1\u6240\u94f8\uff0c\u621a\u957f\u53d1\u5c06\u5251\u4f38\u8fdb\u7f1d\u9699\u4e2d\u53bb\u64ac\u4e86\u51e0\u4e0b\uff0c\u559c\u4e0d\u81ea\u80dc\u3001\u5fc3\u614c\u610f\u4e71\u4e4b\u4e0b\uff0c\u62cd\u7684\u4e00\u58f0\uff0c\u957f\u5251\u7adf\u5c14\u6298\u65ad\u3002 \u3000\u3000\u4ed6\u63d0\u8d77\u534a\u622a\u65ad\u5251\uff0c\u5230\u6697\u95e8\u7684\u53e6\u4e00\u8fb9\u518d\u53bb\u64ac\u3002\u53c8\u64ac\u5f97\u51e0\u4e0b\uff0c\u90a3\u6697\u95e8\u6e10\u6e10\u677e\u4e86\u3002\u621a\u957f\u53d1\u629b\u4e0b\u65ad\u5251\uff0c\u4f38\u624b\u6307\u5c06\u6697\u95e8\u8f7b\u8f7b\u8d77\u4e86\u51fa\u6765\uff0c\u4e3e\u70db\u706b\u4e00\u7167\uff0c\u53ea\u89c1\u4f5b\u50cf\u809a\u91cc\u73e0\u5149\u5b9d\u6c14\uff0c\u972d\u972d\u6d6e\u52a8\uff0c\u4e0d\u77e5\u8fd9\u4e2a\u5927\u809a\u5b50\u4e4b\u4e2d\uff0c\u85cf\u4e86\u6709\u591a\u5c11\u73cd\u73e0\u5b9d\u8d1d\u3002 \u3000\u3000\u621a\u957f\u53d1\u54bd\u4e86\u51e0\u53e3\u553e\u6cab\uff0c\u6b63\u60f3\u4f38\u624b\u5230\u6697\u95e8\u4e4b\u5185\u53bb\u6478\u51fa\u4e9b\u73e0\u5b9d\u6765\u77a7\u77a7\uff0c\u7a81\u89c9\u795e\u575b\u8f7b\u8f7b\u4e00\u6643\u3002\u4ed6\u5fc3\u77e5\u6709\u5f02\uff0c\u7eb5\u8eab\u4fbf\u5373\u8dc3\u4e0b\uff0c\u5de6\u8db3\u521a\u7740\u5730\uff0c\u5c0f\u8179\u4e0a\u4e00\u75db\uff0c\u5df2\u7ed9\u4eba\u70b9\u4e2d\u4e86\u7a74\u9053\uff0c\u5495\u549a\u4e00\u58f0\uff0c\u6454\u5012\u5728\u5730\u3002 \u3000\u3000\u795e\u575b\u4e0b\u94bb\u51fa\u4e00\u4e2a\u4eba\u6765\uff0c\u4fa7\u5934\u51b7\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u621a\u5e08\u5f1f\uff0c\u4f60\u627e\u5f97\u5230\u8fd9\u513f\uff0c\u8001\u4e8c\u627e\u5f97\u5230\u8fd9\u513f\uff0c\u600e\u4e48\u4e0d\u60f3\u60f3\uff0c\u5927\u5e08\u5144\u4e5f\u627e\u5f97\u5230\u8fd9\u91cc\u554a\uff01\u201d\u8bf4\u8bdd\u4e4b\u4eba\uff0c\u6b63\u662f\u4e07\u9707\u5c71\u3002 \u3000\u3000\u621a\u957f\u53d1\u9661\u7136\u53d1\u89c1\u5927\u5b9d\u85cf\uff0c\u9976\u662f\u4ed6\u7cbe\u7ec6\u8fc7\u4eba\uff0c\u89c1\u4e86\u8fd9\u8bb8\u591a\u73e0\u5b9d\uff0c\u7ec8\u4e8e\u4e5f\u4e0d\u514d\u559c\u51fa\u671b\u5916\uff0c\u4e00\u758f\u795e\u95f4\uff0c\u7adf\u7740\u4e86\u4e07\u9707\u5c71\u7684\u9053\u513f\uff0c\u6068\u6068\u7684\u9053\uff1a\u201c\u7b2c\u4e00\u6b21\u4f60\u6574\u6211\u4e0d\u6b7b\uff0c\u60f3\u4e0d\u5230\u7ec8\u4e8e\u8fd8\u662f\u6b7b\u5728\u4f60\u7684\u624b\u4e0b\u3002\u201d\u4e07\u9707\u5c71\u5f97\u610f\u4e4b\u6781\uff0c\u9053\uff1a\u201c\u6211\u6b63\u5728\u5947\u602a\uff0c\u621a\u5e08\u5f1f\uff0c\u6211\u627c\u6b7b\u4e86\u4f60\uff0c\u5c06\u4f60\u5c01\u5165\u5939\u5899\u4e4b\u4e2d\uff0c\u600e\u4e48\u53c8\u4f1a\u6d3b\u4e86\u8fc7\u6765\uff1f\u201d\u621a\u957f\u53d1\u95ed\u76ee\u4e0d\u7b54\u3002 \u3000\u3000\u4e07\u9707\u5c71\u9053\uff1a\u201c\u4f60\u4e0d\u56de\u7b54\uff0c\u96be\u9053\u6211\u5c31\u731c\u4e0d\u5230\uff1f\u90a3\u65f6\u4f60\u654c\u6211\u4e0d\u8fc7\uff0c\u5c31\u5373\u95ed\u6c14\u88c5\u6b7b\uff0c\u5c01\u5165\u5939\u5899\u4e4b\u540e\uff0c\u5c45\u7136\u80fd\u591f\u8131\u9003\u3002\u4e86\u4e0d\u8d77\uff01 \u3000\u3000\u597d\u672c\u4e8b\uff01\u5f53\u65f6\u6211\u89c1\u5c01\u5899\u7684\u7816\u5934\u6709\u4e00\u5757\u51f8\u4e86\u51fa\u6765\uff0c\u5fc3\u4e2d\u4e00\u76f4\u89c9\u5f97\u4e0d\u5927\u59a5\u5f53\uff0c\u53ef\u8bf4\u751a\u4e48\u4e5f\u60f3\u4e0d\u5230\u662f\u7ed9\u4f60\u6323\u624e\u7740\u9003\u8d70\u65f6\u8e22\u51fa\u6765\u7684\u3002\u201d\u4e07\u9707\u5c71\u90a3\u65e5\u5c06\u621a\u957f\u53d1\u5c01\u5165\u4e86\u5939\u5899\u540e\uff0c\u6b21\u65e5\u89c1\u5230\u5c01\u5899\u7684\u7816\u5934\u6709\u4e00\u5757\u51f8\u51fa\uff0c\u8fd9\u4ef6\u4e8b\u4ee4\u4ed6\u5185\u5fc3\u5341\u5206\u4e0d\u5b89\uff0c\u8fd9\u624d\u60a3\u4e0a\u4e86\u79bb\u9b42\u4e4b\u75c7\uff0c\u7761\u68a6\u4e2d\u8d77\u8eab\u780c\u5899\u2014\u2014\u4ed6\u4e00\u76f4\u5728\u6015\u621a\u957f\u53d1\u7684\u201c\u50f5\u5c38\u201d\u4ece\u5899\u6d1e\u91cc\u94bb\u51fa\u6765\uff0c\u56e0\u6b64\u7761\u68a6\u4e2d\u780c\u4e86\u4e00\u6b21\u53c8\u4e00\u6b21\uff0c\u8981\u5c06\u5899\u6d1e\u5c01\u5f97\u7262\u7262\u7684\u3002\u4ed6\u53c8\u51b7\u7b11\u9053\uff1a\u201c\u563f\u563f\uff0c\u4f60\u4e5f\u771f\u5389\u5bb3\uff0c\u773c\u7741\u7741\u7684\u77a7\u7740\u4f60\u5973\u513f\u505a\u4e86\u6211\u513f\u5ab3\u5987\uff0c\u7adf\u59cb\u7ec8\u4e0d\u73b0\u8eab\u3002\u6211\u95ee\u4f60\uff0c\u90a3\u662f\u4e3a\u4e86\u751a\u4e48\uff1f\u4e3a\u4e86\u751a\u4e48\uff1f\u201d \u3000\u3000\u621a\u957f\u53d1\u4e00\u53e3\u6d53\u75f0\u5411\u4ed6\u5410\u53bb\u3002 \u3000\u3000\u4e07\u9707\u5c71\u95ea\u8eab\u907f\u5f00\uff0c\u7b11\u9053\uff1a\u201c\u8001\u4e09\uff0c\u4f60\u8981\u6b7b\u5f97\u5e72\u8106\u5462\uff0c\u8fd8\u662f\u7231\u96f6\u96f6\u788e\u788e\u7684\u53d7\u82e6\uff1f\u201d\u621a\u957f\u53d1\u8138\u4e0a\u9732\u51fa\u6050\u6016\u4e4b\u8272\uff0c\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u6211\u8ddf\u4f60\u8bf4\u3002\u6211\u5973\u513f\u5077\u4e86\u6211\u5251\u8c31\uff0c\u85cf\u5728\u5c71\u6d1e\u4e4b\u4e2d\uff0c\u4f60\u9053\u5979\u662f\u751a\u4e48\u597d\u4eba\u4e48\uff1f\u6211\u4e00\u76f4\u5728\u6697\u4e2d\u67e5\u5bdf\u3002\u59d3\u4e07\u7684\uff0c\u4f60\u7ed9\u6211\u4e2a\u75db\u75db\u5feb\u5feb\u7f62\uff01\u201d\u4e07\u9707\u5c71\u72de\u7b11\u9053\uff1a\u201c\u597d\uff0c\u7ed9\u4f60\u4e2a\u75db\u5feb\u7684\u3002\u6309\u7406\u8bf4\uff0c\u4e0d\u80fd\u7ed9\u4f60\u8fd9\u4e48\u4fbf\u5b9c\uff0c\u53ea\u662f\u4f60\u5e08\u54e5\u6ca1\u5de5\u592b\u4e86\uff0c\u987b\u5f97\u8d76\u5feb\u7528\u70c2\u6ce5\u6d82\u597d\u4f5b\u50cf\u3002\u597d\u5e08\u5f1f\uff0c\u4f60\u4e56\u4e56\u7684\u4e0a\u8def\u7f62\uff01\u201d\u8bf4\u7740\u63d0\u8d77\u957f\u5251\uff0c\u4fbf\u5f80\u621a\u957f\u53d1\u80f8\u53e3\u523a\u843d\u3002 \u3000\u3000\u7a81\u7136\u95f4\u7ea2\u5149\u4e00\u95ea\uff0c\u4e07\u9707\u5c71\u4e00\u53ea\u53f3\u81c2\u9f50\u8098\u8fde\u5200\uff0c\u843d\u5728\u5730\u4e0b\uff0c\u8eab\u5b50\u8ddf\u7740\u88ab\u4eba\u4e00\u811a\u8e22\u5f00\uff0c\u6b63\u662f\u72c4\u4e91\u4ee5\u8840\u5200\u6551\u4e86\u621a\u957f\u53d1\u7684\u6027\u547d\u3002 \u3000\u3000\u4ed6\u4fef\u8eab\u89e3\u5f00\u621a\u957f\u53d1\u7684\u7a74\u9053\u8bf4\u9053\uff1a\u201c\u5e08\u7236\uff0c\u4f60\u53d7\u60ca\u4e86\uff01\u201d \u3000\u3000\u8fd9\u4e00\u4e0b\u53d8\u6545\u6765\u5f97\u597d\u5feb\uff0c\u621a\u957f\u53d1\u5446\u4e86\u8001\u5927\u534a\u664c\uff0c\u624d\u8ba4\u6e05\u695a\u662f\u72c4\u4e91\uff0c\u8bf4\u9053\uff1a\u201c\u4e91\u2026\u2026\u4e91\u513f\uff0c\u662f\u4f60\uff1f\u201d\u72c4\u4e91\u548c\u5e08\u7236\u522b\u4e86\u8fd9\u4e48\u4e45\uff0c\u53c8\u518d\u542c\u5230\u201c\u4e91\u513f\u201d\u8fd9\u4e24\u4e2a\u5b57\uff0c\u4e0d\u7531\u5f97\u60b2\u4ece\u4e2d\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u662f\uff0c\u5e08\u7236\uff0c\u6b63\u662f\u4e91\u513f\u3002\u201d\u621a\u957f\u53d1\u9053\uff1a\u201c\u8fd9\u4e00\u5207\uff0c\u4f60\u90fd\u77a7\u89c1\u4e86\u3002\u201d \u3000\u3000\u72c4\u4e91\u70b9\u4e86\u70b9\u5934\uff0c\u9053\uff1a\u201c\u5e08\u59b9\uff0c\u5e08\u59b9\uff0c\u5979\u2026\u2026\u5979\u2026\u2026\u201d \u3000\u3000\u4e07\u9707\u5c71\u65ad\u4e86\u4e00\u81c2\uff0c\u6323\u624e\u7740\u722c\u8d77\uff0c\u51b2\u5411\u5e99\u5916\u3002\u621a\u957f\u53d1\u62a2\u4e0a\u524d\u53bb\uff0c\u4e00\u5251\u81ea\u80cc\u5fc3\u523a\u5165\uff0c\u7a7f\u80f8\u800c\u51fa\u3002\u4e07\u9707\u5c71\u4e00\u58f0\u60e8\u547c\uff0c\u6b7b\u5728\u5f53\u5730\u3002 \u3000\u3000\u621a\u957f\u53d1\u77a7\u7740\u4e24\u4e2a\u5e08\u5144\u7684\u5c38\u4f53\uff0c\u7f13\u7f13\u7684\u9053\uff1a\u201c\u4e91\u513f\uff0c\u5e78\u4e8f\u4f60\u53ca\u65f6\u8d76\u5230\uff0c\u6551\u4e86\u5e08\u7236\u7684\u6027\u547d\u3002\u54a6\uff0c\u90a3\u8fb9\u6709\u8c01\u6765\u4e86\uff1f\u662f\u82b3\u513f\u5417\uff1f\u201d \u3000\u3000\u8bf4\u7740\u4f38\u624b\u6307\u7740\u6bbf\u4fa7\u3002 \u3000\u3000\u72c4\u4e91\u542c\u5230\u201c\u82b3\u513f\u201d\u4e24\u5b57\uff0c\u5fc3\u5934\u5927\u9707\uff0c\u8f6c\u5934\u4e00\u770b\uff0c\u5374\u4e0d\u89c1\u6709\u4eba\uff0c\u6b63\u60ca\u8bb6\u95f4\uff0c\u7a81\u89c9\u80cc\u4e0a\u4e00\u75db\u3002\u4ed6\u53cd\u624b\u6293\u4f4f\u6765\u88ad\u654c\u4eba\u7684\u624b\u8155\uff0c\u4e00\u8f6c\u5934\uff0c\u53ea\u89c1\u90a3\u4eba\u624b\u4e2d\u6293\u7740\u4e00\u67c4\u660e\u6643\u6643\u7684\u5315\u9996\uff0c\u6b63\u662f\u5e08\u7236\u621a\u957f\u53d1\u3002\u72c4\u4e91\u5927\u662f\u8ff7\u60d8\uff0c\u9053\uff1a\u201c\u5e08\u2026\u2026\u5e08\u7236\u2026\u2026\u5f1f\u5b50\u72af\u4e86\u751a\u4e48\u7f6a\uff0c\u4f60\u8981\u6740\u6211\uff1f\u201d\u4ed6\u8fd9\u65f6\u624d\u60f3\u8d77\uff0c\u9002\u624d\u5e08\u7236\u4e00\u5200\u5df2\u523a\u5728\u81ea\u5df1\u80cc\u4e0a\uff0c\u53ea\u56e0\u81ea\u5df1\u6709\u4e4c\u8695\u8863\u62a4\u8eab\uff0c\u624d\u53c8\u9003\u5f97\u4e86\u6027\u547d\u3002 \u3000\u3000\u621a\u957f\u53d1\u88ab\u4ed6\u6293\u4f4f\u624b\u8155\uff0c\u534a\u8eab\u9178\u9ebb\uff0c\u4f7f\u4e0d\u51fa\u534a\u5206\u529b\u9053\uff0c\u60ca\u6012\u4ea4\u96c6\u4e4b\u4e0b\uff0c\u6068\u6068\u7684\u9053\uff1a\u201c\u597d\uff0c\u4f60\u5b66\u4e86\u4e00\u8eab\u9ad8\u660e\u7684\u6b66\u529f\uff0c\u81ea\u4e0d\u5c06\u5e08\u7236\u77a7\u5728\u773c\u91cc\u4e86\u3002\u4f60\u6740\u6211\u554a\uff0c\u5feb\u6740\uff0c\u5feb\u6740\uff0c\u5e72\u4e48\u4e0d\u6740\uff1f\u201d \u3000\u3000\u72c4\u4e91\u677e\u5f00\u4e86\u624b\uff0c\u4ecd\u662f\u4e0d\u89e3\uff0c\u9053\uff1a\u201c\u6211\u600e\u6562\u6740\u5bb3\u5e08\u7236\uff1f\u201d \u3000\u3000\u621a\u957f\u53d1\u53eb\u9053\uff1a\u201c\u4f60\u5047\u60fa\u60fa\u7684\u5e72\u751a\u4e48\uff1f\u8fd9\u662f\u4e00\u5c0a\u9ec4\u91d1\u94f8\u6210\u7684\u5927\u4f5b\uff0c\u4f60\u96be\u9053\u4e0d\u60f3\u72ec\u541e\uff1f\u6211\u4e0d\u6740\u4f60\uff0c\u4f60\u4fbf\u6740\u6211\uff0c\u90a3\u6709\u751a\u4e48\u5e0c\u5947\uff1f\u8fd9\u662f\u4e00\u5c0a\u91d1\u4f5b\uff0c\u4f5b\u50cf\u809a\u91cc\u90fd\u662f\u4ef7\u503c\u8fde\u57ce\u7684\u73cd\u5b9d\uff0c\u4f60\u4e3a\u751a\u4e48\u4e0d\u6740\u6211\uff1f\u4e3a\u751a\u4e48\u4e0d\u6740\u6211\uff1f\u201d\u4ed6\u9ad8\u58f0\u5927\u53eb\uff0c\u58f0\u97f3\u4e2d\u5145\u6ee1\u4e86\u8d2a\u5a6a\u3001\u6c14\u607c\u3001\u75db\u60dc\uff0c\u90a3\u58f0\u97f3\u4e0d\u50cf\u662f\u4eba\u58f0\uff0c\u4fbf\u5982\u662f\u4e00\u53ea\u53d7\u4e86\u4f24\u7684\u91ce\u517d\u5728\u65f7\u91ce\u4e2d\u55e5\u53eb\u3002 \u3000\u3000\u72c4\u4e91\u6447\u6447\u5934\uff0c\u9000\u5f00\u51e0\u6b65\uff0c\u5fc3\u9053\uff1a\u201c\u5e08\u7236\u8981\u6740\u6211\uff0c\u539f\u6765\u4e3a\u4e86\u8fd9\u5c0a\u9ec4\u91d1\u5927\u4f5b\uff1f\u201d\u970e\u65f6\u4e4b\u95f4\uff0c\u4ed6\u751a\u4e48\u90fd\u660e\u767d\u4e86\uff1a\u621a\u957f\u53d1\u4e3a\u4e86\u8d22\u5b9d\uff0c\u80fd\u6740\u6b7b\u81ea\u5df1\u5e08\u7236\u3001\u6740\u6b7b\u5e08\u5144\u3001\u6000\u7591\u4eb2\u751f\u5973\u513f\uff0c\u4e3a\u751a\u4e48\u4e0d\u80fd\u6740\u5f92\u5f1f\uff1f\u4ed6\u5fc3\u4e2d\u54cd\u8d77\u4e86\u4e01\u5178\u7684\u8bdd\uff1a\u201c\u4ed6\u5916\u53f7\u53eb\u4f5c\u2018\u94c1\u9501\u6a2a\u6c5f\u2019\uff0c\u751a\u4e48\u4e8b\u60c5\u505a\u4e0d\u51fa\uff1f\u201d\u4ed6\u53c8\u9000\u5f00\u4e00\u6b65\uff0c\u8bf4\u9053\uff1a\u201c\u5e08\u7236\uff0c\u6211\u4e0d\u8981\u5206\u4f60\u7684\u9ec4\u91d1\u5927\u4f5b\uff0c\u4f60\u72ec\u4e2a\u513f\u53d1\u8d22\u53bb\u7f62\u3002\u201d\u4ed6\u771f\u4e0d\u80fd\u660e\u767d\uff1a\u4e00\u4e2a\u4eba\u4e16\u4e0a\u751a\u4e48\u4eb2\u4eba\u90fd\u4e0d\u8981\uff0c\u4e0d\u8981\u5e08\u7236\u3001\u5e08\u5144\u5f1f\u3001\u5f92\u5f1f\u3001\u8fde\u4eb2\u751f\u5973\u513f\u4e5f\u4e0d\u987e\uff0c\u6709\u4e86\u4ef7\u503c\u8fde\u57ce\u7684\u5927\u5b9d\u85cf\uff0c\u53c8\u6709\u751a\u4e48\u5feb\u6d3b\uff1f \u3000\u3000\u621a\u957f\u53d1\u4e0d\u76f8\u4fe1\u81ea\u5df1\u7684\u8033\u6735\u4e86\uff0c\u5fc3\u60f3\uff1a\u201c\u4e16\u4e0a\u54ea\u6709\u4eba\u89c1\u5230\u8fd9\u8bb8\u591a\u9ec4\u91d1\u73e0\u5b9d\u800c\u4e0d\u8d77\u610f\uff1f\u72c4\u4e91\u8fd9\u5c0f\u5b50\u5b9a\u662f\u53e6\u6709\u8be1\u8ba1\u3002\u201d\u4ed6\u8fd9\u65f6\u5df2\u6c89\u4e0d\u4f4f\u6c14\uff0c\u5927\u58f0\u9053\uff1a\u201c\u4f60\u6363\u751a\u4e48\u9b3c\uff1f\u8fd9\u662f\u4e00\u5ea7\u9ec4\u91d1\u5927\u4f5b\uff0c\u4f5b\u50cf\u809a\u4e2d\u90fd\u662f\u73e0\u5b9d\uff0c\u4f60\u4e3a\u751a\u4e48\u4e0d\u8981\uff1f\u4f60\u8981\u4f7f\u751a\u4e48\u9b3c\u8ba1\uff1f\u201d \u3000\u3000\u72c4\u4e91\u6447\u4e86\u6447\u5934\uff0c\u6b63\u60f3\u8d70\u51fa\u5e99\u53bb\uff0c\u5ffd\u542c\u5f97\u811a\u6b65\u58f0\u54cd\uff0c\u8bb8\u591a\u4eba\u8702\u62e5\u800c\u6765\u3002\u4ed6\u7eb5\u8eab\u4e0a\u4e86\u5c4b\u9876\uff0c\u5411\u5916\u671b\u53bb\uff0c\u53ea\u89c1\u4e00\u767e\u591a\u4eba\u6253\u7740\u706b\u628a\uff0c\u55a7\u54d7\u53eb\u56b7\uff0c\u5feb\u6b65\u5954\u6765\uff0c\u6b63\u662f\u90a3\u4e00\u7fa4\u6c5f\u6e56\u8c6a\u5ba2\uff0c\u53ea\u542c\u5f97\u6709\u4eba\u559d\u9a82\uff1a\u201c\u4e07\u572d\uff0c\u4ed6\u5988\u7684\uff0c\u5feb\u8d70\uff0c\u5feb\u8d70\uff01\u201d\u72c4\u4e91\u672c\u60f3\u8981\u8d70\uff0c\u4e00\u542c\u5230\u201c\u4e07\u572d\u201d\u4e24\u5b57\uff0c\u5f53\u5373\u505c\u6b65\u3002\u4ed6\u8fd8\u6ca1\u4e3a\u621a\u82b3\u62a5\u4ec7\u3002 \u3000\u3000\u8fd9\u4e00\u7fa4\u4eba\u4e89\u5148\u6050\u540e\u7684\u5165\u5e99\uff0c\u72c4\u4e91\u770b\u5f97\u6e05\u695a\uff0c\u4e07\u572d\u88ab\u51e0\u4e2a\u5927\u6c49\u626d\u7740\uff0c\u76ee\u9752\u9f3b\u80bf\uff0c\u5df2\u7ed9\u4eba\u9971\u6253\u4e86\u4e00\u987f\uff0c\u8eab\u4e0a\u4ecd\u662f\u7a7f\u7740\u90a3\u4ef6\u9178\u79c0\u624d\u7684\u8863\u886b\u3002\u539f\u6765\u4ed6\u4e54\u88c5\u6210\u4e2a\u6559\u4e66\u5148\u751f\u7684\u6a21\u6837\uff0c\u6545\u610f\u5c06\u57ce\u5899\u8fb9\u7684\u4e00\u4f17\u6c5f\u6e56\u8c6a\u58eb\u5f15\u5f00\uff0c\u597d\u8ba9\u4e07\u9707\u5c71\u5230\u5929\u5b81\u5bfa\u6765\u5bfb\u5b9d\u3002\u4f46\u5728\u4f17\u4eba\u7684\u8ddf\u968f\u67e5\u7a76\u4e4b\u4e0b\uff0c\u7ec8\u4e8e\u9732\u51fa\u4e86\u9a6c\u811a\u3002\u5404\u4eba\u4ee5\u6027\u547d\u76f8\u80c1\uff0c\u903c\u7740\u4ed6\u5e26\u5230\u5929\u5b81\u5bfa\u6765\u3002 \u3000\u3000\u621a\u957f\u53d1\u542c\u5f97\u4eba\u58f0\uff0c\u6025\u5fd9\u8dc3\u4e0a\u795e\u575b\uff0c\u60f3\u8981\u63a9\u4f4f\u4f5b\u50cf\u5251\u75d5\u4e2d\u9732\u51fa\u6765\u7684\u9ec4\u91d1\u3002\u4f46\u8fdf\u4e86\u4e00\u6b65\uff0c\u4f17\u4eba\u5df2\u89c1\u5230\u4ed6\u7ad9\u5728\u795e\u575b\u4e4b\u4e0a\uff0c\u53cc\u624b\u53bb\u63a9\u4f5b\u50cf\u7684\u5927\u809a\u5b50\u3002\u8fd9\u65f6\u6570\u5341\u6839\u706b\u628a\u7167\u8000\u4e4b\u4e0b\uff0c\u5e99\u4e2d\u6709\u5982\u767d\u663c\u3002\u5404\u4eba\u773c\u89c1\u5230\u91d1\u5149\uff0c\u53d1\u4e00\u58f0\u558a\uff0c\u62a2\u5c06\u4e0a\u53bb\uff0c\u4e03\u624b\u516b\u811a\u7684\uff0c\u4fbf\u53bb\u65a9\u5265\u4f5b\u50cf\u4e0a\u7684\u6ce5\u571f\u3002\u5404\u4eba\u5200\u780d\u5251\u524a\uff0c\u4e0d\u591a\u65f6\u4f5b\u50cf\u8eab\u4e0a\u5230\u5904\u53d1\u51fa\u707f\u70c2\u91d1\u5149\u3002 \u3000\u3000\u8ddf\u7740\u6709\u4eba\u53d1\u89c1\u4f5b\u50cf\u80cc\u540e\u7684\u6697\u95e8\uff0c\u4f38\u624b\u8fdb\u53bb\uff0c\u638f\u51fa\u4e86\u5927\u6279\u73e0\u5b9d\uff0c\u7ad9\u5728\u540e\u9762\u7684\u4fbf\u7528\u529b\u5c06\u4ed6\u6324\u5f00\u3002\u73e0\u5b9d\u4e00\u628a\u628a\u7684\u6478\u51fa\u6765\u3002\u5f3a\u6709\u529b\u7684\u8c6a\u58eb\u4fbf\u4ece\u522b\u4eba\u624b\u4e2d\u52ab\u593a\u3002 \u3000\u3000\u7a81\u7136\u95f4\u95e8\u5916\u53f7\u89d2\u58f0\u545c\u545c\u5439\u8d77\uff0c\u5e99\u95e8\u5927\u5f00\uff0c\u6570\u5341\u540d\u5175\u4e01\u51b2\u4e86\u8fdb\u6765\uff0c\u9ad8\u53eb\uff1a\u201c\u77e5\u5e9c\u5927\u4eba\u5230\uff0c\u8c01\u90fd\u4e0d\u8bb8\u4e71\u52a8\u3002\u201d\u968f\u540e\u4e00\u4eba\u8eab\u7a7f\u5b98\u670d\uff0c\u50b2\u7136\u800c\u8fdb\uff0c\u6b63\u662f\u6c5f\u9675\u5e9c\u77e5\u5e9c\u51cc\u9000\u601d\u3002\u4ed6\u5728\u57ce\u5185\u57ce\u5916\u8033\u76ee\u4f17\u591a\uff0c\u8fd9\u4e9b\u6c5f\u6e56\u8c6a\u5ba2\u4e4b\u4e2d\u4fbf\u6df7\u5f97\u6709\u4ed6\u7684\u90e8\u5c5e\uff0c\u4e00\u5f97\u8baf\u606f\uff0c\u7acb\u65f6\u63d0\u5175\u8d76\u6765\u3002 \u3000\u3000\u4f46\u4e00\u4f17\u6c5f\u6e56\u8c6a\u5ba2\u89c1\u4e86\u8fd9\u8bb8\u591a\u73e0\u5b9d\uff0c\u54ea\u91cc\u8fd8\u5fcc\u60ee\u751a\u4e48\u5b98\u5e9c\uff1f \u3000\u3000\u5404\u4eba\u53ea\u662f\u62da\u547d\u7684\u62a2\u593a\u73cd\u5b9d\u3002 \u3000\u3000\u5730\u4e0b\u6eda\u6ee1\u4e86\u73cd\u73e0\u3001\u5b9d\u77f3\u3001\u91d1\u5668\u3001\u767d\u7389\u3001\u7fe1\u7fe0\u3001\u73ca\u745a\u3001\u7956\u6bcd\u7eff\u3001\u732b\u513f\u773c\u2026\u2026 \u3000\u3000\u51cc\u9000\u601d\u7684\u90e8\u5c5e\u53c8\u600e\u4f1a\u4e0d\u62a2\uff1f\u5175\u4e01\u5148\u4fef\u8eab\u6361\u62fe\uff0c\u4e8e\u662f\u5b98\u957f\u4e5f\u62a2\u4e86\u8d77\u6765\u3002\u8c01\u90fd\u4e0d\u80af\u843d\u540e\u3002\u621a\u957f\u53d1\u5728\u62a2\u3001\u4e07\u572d\u5728\u62a2\u3001\u8fde\u5802\u5802\u77e5\u5e9c\u5927\u4eba\u51cc\u9000\u601d\uff0c\u4e5f\u5fcd\u4e0d\u4f4f\u5c06\u4e00\u628a\u628a\u73e0\u5b9d\u63e3\u5165\u6000\u4e2d\u3002 \u3000\u3000\u4e00\u62a2\u593a\uff0c\u4fbf\u4e0d\u514d\u6597\u6bb4\u3002\u4e8e\u662f\u6709\u4eba\u6253\u80dc\u4e86\uff0c\u6709\u4eba\u6d41\u8840\uff0c\u6709\u4eba\u6b7b\u4e86\u3002 \u3000\u3000\u8fd9\u4e9b\u4eba\u8d8a\u6597\u8d8a\u5389\u5bb3\uff0c\u6709\u4eba\u7a81\u7136\u95f4\u6251\u5230\u91d1\u4f5b\u4e0a\uff0c\u62b1\u4f4f\u4e86\u4f5b\u50cf\u72c2\u54ac\uff0c\u6709\u7684\u4eba\u7528\u5934\u731b\u649e\u3002 \u3000\u3000\u72c4\u4e91\u89c9\u5f97\u5f88\u5947\u602a\uff1a\u201c\u4e3a\u751a\u4e48\u4f1a\u8fd9\u6837\uff1f\u5c31\u7b97\u662f\u8d22\u8ff7\u5fc3\u7a8d\uff0c\u4e5f\u4e0d\u8be5\u8fd9\u4e48\u53d1\u75af\uff1f\u201d \u3000\u3000\u4e0d\u9519\uff0c\u4ed6\u4eec\u4e2a\u4e2a\u90fd\u53d1\u4e86\u75af\uff0c\u7ea2\u4e86\u773c\u4e71\u6253\u3001\u4e71\u54ac\u3001\u4e71\u6495\u3002\u72c4\u4e91\u89c1\u5230\u94c3\u5251\u53cc\u4fa0\u4e2d\u7684\u6c6a\u5578\u98ce\u5728\u5176\u4e2d\uff0c\u89c1\u5230\u201c\u843d\u82b1\u6d41\u6c34\u201d\u7684\u82b1\u94c1\u5e72\u4e5f\u5728\u5176\u4e2d\u3002\u4ed6\u4eec\u4e00\u822c\u7684\u90fd\u53d8\u6210\u4e86\u91ce\u517d\uff0c\u5728\u4e71\u54ac\u3001\u4e71\u62a2\uff0c\u5c06\u73e0\u5b9d\u585e\u5230\u5634\u91cc\u3002 \u3000\u3000\u72c4\u4e91\u84e6\u5730\u91cc\u660e\u767d\u4e86\uff1a\u201c\u8fd9\u4e9b\u73e0\u5b9d\u4e0a\u5582\u5f97\u6709\u6781\u5389\u5bb3\u7684\u6bd2\u836f\u3002 \u3000\u3000\u5f53\u5e74\u85cf\u5b9d\u7684\u7687\u5e1d\u6015\u9b4f\u5175\u62a2\u52ab\uff0c\u56e0\u6b64\u5728\u73e0\u5b9d\u4e0a\u6d82\u4e86\u6bd2\u836f\u3002\u201d\u4ed6\u60f3\u53bb\u6551\u5e08\u7236\uff0c\u4f46\u5df2\u6765\u4e0d\u53ca\u4e86\u3002 \u3000\u3000\u72c4\u4e91\u5728\u4e01\u5178\u548c\u51cc\u59d1\u5a18\u7684\u575f\u524d\u79cd\u4e86\u51e0\u767e\u68f5\u83ca\u82b1\u3002\u4ed6\u6ca1\u96c7\u4eba\u5e2e\u5fd9\uff0c\u5168\u662f\u81ea\u5df1\u52a8\u624b\u3002\u4ed6\u662f\u5e84\u7a3c\u4eba\uff0c\u9504\u5730\u79cd\u690d\u7684\u4e8b\u672c\u662f\u5185\u884c\u3002 \u3000\u3000\u53ea\u4e0d\u8fc7\u4ed6\u4ece\u524d\u5f88\u5c11\u79cd\u82b1\uff0c\u79cd\u7684\u662f\u8fa3\u6912\u3001\u9ec4\u74dc\u3001\u51ac\u74dc\u3001\u767d\u83dc\u3001\u8304\u5b50\u3001\u7a7a\u5fc3\u83dc\u2026\u2026 \u3000\u3000\u4ed6\u79bb\u4e86\u8346\u5dde\u57ce\uff0c\u62b1\u7740\u7a7a\u5fc3\u83dc\uff0c\u5339\u9a6c\u8d70\u4e0a\u4e86\u5f81\u9014\u3002\u4ed6\u4e0d\u613f\u518d\u5728\u6c5f\u6e56\u4e0a\u53ae\u6df7\uff0c\u4ed6\u8981\u627e\u4e00\u4e2a\u4eba\u8ff9\u4e0d\u5230\u7684\u8352\u50fb\u4e4b\u5730\uff0c\u5c06\u7a7a\u5fc3\u83dc\u517b\u5927\u6210\u4eba\u3002 \u3000\u3000\u4ed6\u56de\u5230\u4e86\u85cf\u8fb9\u7684\u96ea\u8c37\u3002\u9e45\u6bdb\u822c\u7684\u5927\u96ea\u53c8\u5f00\u59cb\u98d8\u4e0b\uff0c\u6765\u5230\u4e86\u6614\u65e5\u7684\u5c71\u6d1e\u524d\u3002 \u3000\u3000\u7a81\u7136\u4e4b\u95f4\uff0c\u8fdc\u8fdc\u671b\u89c1\u5c71\u6d1e\u524d\u7ad9\u7740\u4e00\u4e2a\u5c11\u5973\u3002 \u3000\u3000\u90a3\u662f\u6c34\u7b19\uff01 \u3000\u3000\u5979\u6ee1\u8138\u6b22\u7b11\uff0c\u5411\u4ed6\u98de\u5954\u8fc7\u6765\uff0c\u53eb\u9053\uff1a\u201c\u6211\u7b49\u4e86\u4f60\u8fd9\u4e48\u4e45\uff01\u6211\u77e5\u9053\u4f60\u7ec8\u4e8e\u4f1a\u56de\u6765\u7684\u3002\u201d"}),showOnHomepage:!0},{name:"\u540e\u8bb0",content:(0,a.jsx)(a.Fragment,{children:"\u513f\u7ae5\u65f6\u5019\uff0c\u6211\u6d59\u6c5f\u6d77\u5b81\u8001\u5bb6\u6709\u4e2a\u957f\u5de5\uff0c\u540d\u53eb\u548c\u751f\u3002\u4ed6\u662f\u6b8b\u5e9f\u7684\uff0c\u662f\u4e2a\u9a7c\u5b50\uff0c\u7136\u800c\u53ea\u9a7c\u4e86\u53f3\u8fb9\u7684\u4e00\u534a\uff0c\u5f62\u76f8\u7279\u522b\u663e\u5f97\u53e4\u602a\u3002\u867d\u8bf4\u662f\u957f\u5de5\uff0c\u4f46\u5e76\u4e0d\u505a\u751a\u4e48\u7c97\u91cd\u5de5\u4f5c\uff0c\u53ea\u662f\u626b\u5730\u3001\u62b9\u5c18\uff0c\u4ee5\u53ca\u63a5\u9001\u5b69\u5b50\u4eec\u4e0a\u5b66\u5802\u3002\u6211\u54e5\u54e5\u7684\u540c\u5b66\u4eec\u89c1\u5230\u4e86\u4ed6\u5c31\u62cd\u624b\u5531\u6b4c\uff1a\u201c\u548c\u751f\u548c\u751f\u534a\u723f\u9a7c\uff0c\u53eb\u4ed6\u4e09\u58f0\u8981\u53d1\u6012\uff0c\u518d\u53eb\u4e09\u58f0\u7ffb\u7b4b\u6597\uff0c\u7ffb\u8f6c\u6765\u50cf\u53ea\u762b\u6dd8\u7ba9\u3002\u201d\u201c\u762b\u6dd8\u7ba9\u201d\u662f\u6211\u6545\u4e61\u571f\u8bdd\uff0c\u6307\u7834\u4e86\u7684\u6dd8\u7c73\u7af9\u7ba9\u3002 \u3000\u3000\u90a3\u65f6\u5019\u6211\u603b\u662f\u62c9\u7740\u548c\u751f\u7684\u624b\uff0c\u53eb\u90a3\u4e9b\u5927\u540c\u5b66\u4e0d\u8981\u5531\uff0c\u6709\u4e00\u6b21\u8fd8\u4e3a\u6b64\u54ed\u4e86\u8d77\u6765\uff0c\u6240\u4ee5\u548c\u751f\u5411\u6765\u5bf9\u6211\u7279\u522b\u597d\u3002\u4e0b\u96ea\u3001\u4e0b\u96e8\u7684\u65e5\u5b50\uff0c\u4ed6\u603b\u662f\u62b1\u4e86\u6211\u4e0a\u5b66\uff0c\u56e0\u4e3a\u4ed6\u7684\u80cc\u810a\u9a7c\u4e86\u4e00\u534a\uff0c\u4e0d\u80fd\u80cc\u8d1f\u3002\u90a3\u65f6\u5019\u4ed6\u5e74\u7eaa\u5df2\u5f88\u8001\u4e86\uff0c\u6211\u7238\u7238\u3001\u5988\u5988\u53eb\u4ed6\u4e0d\u8981\u62b1\uff0c\u514d\u5f97\u4e24\u4e2a\u4eba\u90fd\u6454\u4ea4\uff0c\u4f46\u4ed6\u4e00\u5b9a\u8981\u62b1\u3002 \u3000\u3000\u6709\u4e00\u6b21\uff0c\u4ed6\u75c5\u5f97\u5f88\u5389\u5bb3\uff0c\u6211\u5230\u4ed6\u7684\u5c0f\u623f\u91cc\u53bb\u77a7\u4ed6\uff0c\u62ff\u4e9b\u70b9\u5fc3\u7ed9\u4ed6\u5403\u3002\u4ed6\u8ddf\u6211\u8bf4\u4e86\u4ed6\u7684\u8eab\u4e16\u3002 \u3000\u3000\u4ed6\u662f\u6c5f\u82cf\u4e39\u9633\u4eba\u3002\u5bb6\u91cc\u5f00\u4e00\u5bb6\u5c0f\u8c46\u8150\u5e97\uff0c\u7236\u6bcd\u66ff\u4ed6\u8ddf\u90bb\u5c45\u4e00\u4e2a\u7f8e\u8c8c\u7684\u59d1\u5a18\u5bf9\u4e86\u4eb2\u3002\u5bb6\u91cc\u79ef\u84c4\u4e86\u51e0\u5e74\uff0c\u5c31\u8981\u7ed9\u4ed6\u5b8c\u5a5a\u4e86\u3002\u8fd9\u5e74\u5341\u4e8c\u6708\uff0c\u4e00\u5bb6\u8d22\u4e3b\u53eb\u4ed6\u53bb\u78e8\u505a\u5e74\u7cd5\u7684\u7c73\u7c89\u3002\u8fd9\u5bb6\u8d22\u4e3b\u53c8\u5f00\u5f53\u94fa\uff0c\u53c8\u5f00\u9171\u56ed\uff0c\u5bb6\u91cc\u6709\u5ea7\u5927\u82b1\u56ed\u3002\u78e8\u8c46\u8150\u548c\u78e8\u7c73\u7c89\uff0c\u5de5\u4f5c\u662f\u5dee\u4e0d\u591a\u7684\u3002\u8d22\u4e3b\u5bb6\u8fc7\u5e74\u8981\u78e8\u597d\u51e0\u77f3\u7cef\u7c73\uff0c\u78e8\u7c89\u7684\u529f\u592b\u5728\u8d22\u4e3b\u5bb6\u540e\u5385\u4e0a\u505a\u3002\u8fd9\u79cd\u78e8\u7c89\u7684\u4e8b\u6211\u89c1\u5f97\u591a\u4e86\uff0c\u53ea\u78e8\u5f97\u51e0\u5929\uff0c\u78e8\u5b50\u65c1\u5730\u4e0b\u7684\u9752\u7816\u4e0a\u5c31\u6709\u4e00\u5708\u6de1\u6de1\u7684\u811a\u5370\uff0c\u90a3\u662f\u63a8\u78e8\u7684\u4eba\u8e0f\u51fa\u6765\u7684\u3002\u6c5f\u5357\u5404\u5730\u7684\u98ce\u4fd7\u90fd\u5dee\u4e0d\u591a\uff0c\u6240\u4ee5\u4ed6\u4e00\u8bf4\u6211\u5c31\u61c2\u4e86\u3002 \u3000\u3000\u56e0\u4e3a\u8981\u8d76\u65f6\u5019\uff0c\u78e8\u7c73\u7c89\u7684\u529f\u592b\u5f80\u5f80\u505a\u5230\u665a\u4e0a\u5341\u70b9\u3001\u5341\u4e00\u70b9\u949f\u3002\u8fd9\u5929\u4ed6\u6536\u4e86\u5de5\uff0c\u5df2\u7ecf\u5f88\u665a\u4e86\uff0c\u6b63\u8981\u56de\u5bb6\uff0c\u8d22\u4e3b\u5bb6\u91cc\u8bb8\u591a\u4eba\u53eb\u4e86\u8d77\u6765\uff1a\u201c\u6709\u8d3c\uff01\u201d\u6709\u4eba\u53eb\u4ed6\u5230\u82b1\u56ed\u91cc\u53bb\u5e2e\u540c\u6349\u8d3c\u3002\u4ed6\u4e00\u5954\u8fdb\u82b1\u56ed\uff0c\u5c31\u7ed9\u4eba\u51e0\u68cd\u5b50\u6253\u5012\uff0c\u8bf4\u4ed6\u662f\u201c\u8d3c\u9aa8\u5934\u201d\uff0c\u597d\u51e0\u4e2a\u4eba\u7528\u68cd\u5b50\u6253\u5f97\u4ed6\u904d\u4f53\u9cde\u4f24\uff0c\u8fd8\u6253\u65ad\u4e86\u51e0\u6839\u808b\u9aa8\uff0c\u4ed6\u7684\u534a\u8fb9\u9a7c\u5c31\u662f\u8fd9\u6837\u9020\u6210\u7684\u3002\u4ed6\u5934\u4e0a\u5403\u4e86\u51e0\u68cd\uff0c\u660f\u6655\u4e86\u8fc7\u53bb\uff0c\u9192\u8f6c\u6765\u65f6\uff0c\u8eab\u8fb9\u6709\u8bb8\u591a\u91d1\u94f6\u9996\u9970\uff0c\u8bf4\u662f\u4ece\u4ed6\u8eab\u4e0a\u641c\u51fa\u6765\u7684\u3002\u53c8\u6709\u4eba\u5728\u4ed6\u7af9\u7ba9\u7684\u7c73\u7c89\u5e95\u4e0b\u641c\u51fa\u4e86\u4e00\u4e9b\u91d1\u94f6\u548c\u94dc\u94b1\uff0c\u4e8e\u662f\u5c06\u4ed6\u9001\u8fdb\u77e5\u53bf\u8859\u95e8\u3002\u8d3c\u8d43\u4ff1\u5728\uff0c\u4ed6\u4e5f\u5206\u8fa9\u4e0d\u6e05\uff0c\u7ed9\u6253\u4e86\u51e0\u5341\u677f\uff0c\u6536\u8fdb\u4e86\u76d1\u7262\u3002 \u3000\u3000\u672c\u6765\u5c31\u7b97\u662f\u4f5c\u8d3c\uff0c\u4e5f\u4e0d\u662f\u751a\u4e48\u5927\u4e0d\u4e86\u7684\u7f6a\u540d\uff0c\u4f46\u4ed6\u7ed9\u5173\u4e86\u4e24\u5e74\u591a\u624d\u653e\u51fa\u6765\u3002\u5728\u8fd9\u6bb5\u65f6\u671f\u4e2d\uff0c\u4ed6\u7236\u4eb2\u3001\u6bcd\u4eb2\u90fd\u6c14\u6b7b\u4e86\uff0c\u4ed6\u7684\u672a\u5a5a\u59bb\u7ed9\u8d22\u4e3b\u5c11\u7237\u5a36\u4e86\u53bb\u505a\u7ee7\u5ba4\u3002 \u3000\u3000\u4ed6\u4ece\u7262\u91cc\u51fa\u6765\u4e4b\u540e\uff0c\u77e5\u9053\u8fd9\u4e00\u5207\u90fd\u662f\u90a3\u8d22\u4e3b\u5c11\u7237\u9677\u5bb3\u3002\u6709\u4e00\u5929\u5728\u8857\u4e0a\u649e\u5230\uff0c\u4ed6\u53d6\u51fa\u4e00\u76f4\u85cf\u5728\u8eab\u8fb9\u7684\u5c16\u5200\uff0c\u5728\u90a3\u8d22\u4e3b\u5c11\u7237\u8eab\u4e0a\u523a\u4e86\u51e0\u5200\u3002\u4ed6\u4e5f\u4e0d\u9003\u8d70\uff0c\u4efb\u7531\u5dee\u5f79\u6349\u4e86\u53bb\u3002\u90a3\u8d22\u4e3b\u5c11\u7237\u53ea\u662f\u53d7\u4e86\u91cd\u4f24\uff0c\u5374\u6ca1\u6709\u6b7b\u3002\u4f46\u8d22\u4e3b\u5bb6\u4e0d\u65ad\u8d3f\u8d42\u53bf\u5b98\u3001\u5e08\u7237\u3001\u72f1\u5352\uff0c\u60f3\u5c06\u4ed6\u5728\u72f1\u4e2d\u5bb3\u6b7b\uff0c\u4ee5\u514d\u4ed6\u51fa\u6765\u540e\u518d\u5bfb\u4ec7\u3002 \u3000\u3000\u4ed6\u8bf4\uff1a\u201c\u771f\u662f\u83e9\u8428\u4fdd\u4f51\uff0c\u4e0d\u5230\u4e00\u5e74\uff0c\u8001\u7237\u6765\u505a\u4e39\u9633\u53bf\u6b63\u5802\uff0c\u4ed6\u8001\u4eba\u5bb6\u6551\u4e86\u6211\u547d\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u7684\u8001\u7237\uff0c\u662f\u6211\u7956\u7236\u3002 \u3000\u3000\u6211\u7956\u7236\u6587\u6e05\u516c\uff08\u4ed6\u672c\u6765\u662f\u201c\u7f8e\u201d\u5b57\u8f88\uff0c\u4f46\u8fdb\u5b66\u548c\u5e94\u8003\u65f6\u90fd\u7528\u201c\u6587\u6e05\u201d\u7684\u540d\u5b57\uff09\uff0c\u5b57\u6ca7\u73ca\uff0c\u6545\u4e61\u7684\u7236\u8001\u4eec\u79f0\u4ed6\u4e3a\u201c\u6ca7\u73ca\u5148\u751f\u201d\u3002 \u3000\u3000\u4ed6\u4e8e\u5149\u7eea\u4e59\u9149\u5e74\u4e2d\u4e3e\uff0c\u4e19\u620c\u5e74\u4e2d\u8fdb\u58eb\uff0c\u968f\u5373\u6d3e\u53bb\u4e39\u9633\u505a\u77e5\u53bf\uff0c\u505a\u77e5\u53bf\u6709\u6210\u7ee9\uff0c\u52a0\u4e86\u540c\u77e5\u8854\u3002\u4e0d\u4e45\u5c31\u53d1\u751f\u4e86\u8457\u540d\u7684\u201c\u4e39\u9633\u6559\u6848\u201d\u3002 \u3000\u3000\u9093\u4e4b\u8bda\u5148\u751f\u7684\u300a\u4e2d\u534e\u4e8c\u5343\u5e74\u53f2\u300b\u5377\u4e94\u4e2d\u63d0\u5230\u4e86\u8fd9\u4ef6\u4e8b\uff1a\u201c\u5929\u6d25\u6761\u7ea6\u8bb8\u5916\u4eba\u4f20\u6559\uff0c\u4e8e\u662f\u6559\u5f92\u4e4b\u8db3\u8ff9\u904d\u4e2d\u56fd\u3002\u83a0\u6c11\u5165\u6559\uff0c\u8f84\u6043\u5916\u4eba\u4e3a\u62a4\u7b26\uff0c\u4e0d\u53d7\u5b98\u540f\u94a4\u675f\u3002\u4eba\u6c11\u65e2\u6124\u6559\u58eb\u4e4b\u9a84\u6a2a\uff0c\u53c8\u602a\u5176\u884c\u52a8\u8be1\u79d8\uff0c\u63a8\u6d4b\u9644\u4f1a\uff0c\u4e89\u7aef\u9042\u8d77\u3002\u6559\u6c11\u6216\u6709\u6b7b\u4f24\uff0c\u5916\u7c4d\u6559\u58eb\u5373\u501f\u53e3\u8981\u631f\uff0c\u52d2\u7d22\u5de8\u6b3e\uff0c\u751a\u81f3\u5f52\u7f6a\u5b98\u540f\uff0c\u80c1\u6e05\u5ef7\u6cbb\u4ee5\u91cd\u7f6a\uff0c\u5c01\u7586\u5927\u540f\uff0c\u4ea6\u987b\u9769\u804c\u6c38\u4e0d\u53d9\u7528\u3002\u5185\u653f\u7531\u4eba\u5e72\u6d89\uff0c\u56fd\u5df2\u4e0d\u56fd\u77e3\u3002\u6559\u6848\u4ee5\u5343\u4e07\u8ba1\uff0c\u5179\u4e3e\u5176\u5927\u8005\uff1a\u201c\u2026\u2026\u4e39\u9633\u6559\u6848\u3002\u5149\u7eea\u5341\u4e03\u5e74\u516b\u6708\u2026\u2026\u5218\u5764\u4e00\u3001\u521a\u6bc5\u594f\uff0c\u672c\u5e74\u2026\u2026\u6c5f\u82cf\u4e4b\u4e39\u9633\u3001\u91d1\u532e\u3001\u65e0\u9521\u3001\u9633\u6e56\u3001\u6c5f\u9634\u3001\u5982\u768b\u5404\u5c5e\u6559\u5802\uff0c\u63a5\u8e35\u88ab\u711a\u6bc1\uff0c\u6d3e\u5458\u524d\u5f80\u67e5\u529e\u2026\u2026\u82cf\u5c5e\u6848\uff0c\u7cfb\u7531\u4e39\u9633\u9996\u5148\u6ecb\u4e8b\uff0c\u5c06\u8be5\u53bf\u67e5\u6587\u6e05\u7504\u522b\u53c2\u9769\u2026\u2026\u201d\uff08\u300a\u5149\u7eea\u671d\u4e1c\u534e\u5f55\u5377\u300b\u4e00\u25cb\u4e94\uff09 \u3000\u3000\u6211\u7956\u7236\u88ab\u53c2\u9769\u4e4b\u524d\uff0c\u66fe\u6709\u4e00\u756a\u4ea4\u6d89\u3002\u4e0a\u53f8\u53eb\u4ed6\u5c06\u4e3a\u9996\u70e7\u6559\u5802\u7684\u4e24\u4eba\u65a9\u9996\u793a\u4f17\uff0c\u4ee5\u4fbf\u5411\u5916\u56fd\u6559\u58eb\u4ea4\u4ee3\u3002\u4f46\u6211\u7956\u7236\u540c\u60c5\u70e7\u6559\u5802\u7684\u4eba\u6c11\uff0c\u901a\u77e5\u4e3a\u9996\u7684\u4e24\u4eba\u9003\u8d70\uff0c\u56de\u62a5\u4e0a\u53f8\uff1a\u6b64\u4e8b\u662f\u7531\u5916\u56fd\u6559\u58eb\u6b3a\u538b\u826f\u6c11\u800c\u5f15\u8d77\u516c\u6124\uff0c\u6570\u767e\u4eba\u4e00\u6d8c\u800c\u4e0a\uff0c\u711a\u70e7\u6559\u5802\uff0c\u5e76\u65e0\u4e3a\u9996\u4e4b\u4eba\u3002\u8ddf\u7740\u4ed6\u5c31\u8f9e\u5b98\u3002\u671d\u5ef7\u5b9a\u4e86\u201c\u9769\u804c\u201d\u5904\u5206\u3002 \u3000\u3000\u6211\u7956\u7236\u6b64\u540e\u4fbf\u5728\u6545\u4e61\u95f2\u5c45\uff0c\u8bfb\u4e66\u505a\u8bd7\u81ea\u5a31\uff0c\u4e5f\u505a\u4e86\u5f88\u591a\u516c\u76ca\u4e8b\u4e1a\u3002\u4ed6\u7f16\u4e00\u90e8\u300a\u6d77\u5b81\u67e5\u6c0f\u8bd7\u949e\u300b\uff0c\u6709\u6570\u767e\u5377\u4e4b\u591a\uff0c\u4f46\u96d5\u7248\u672a\u5b8c\u5de5\u5c31\u53bb\u4e16\u4e86\uff08\u8fd9\u4e9b\u96d5\u7248\u653e\u4e86\u4e24\u95f4\u5c4b\u5b50\uff0c\u540e\u6765\u90fd\u6210\u4e3a\u6211\u4eec\u5802\u5144\u5f1f\u7684\u73a9\u5177\uff09\u3002\u51fa\u4e27\u4e4b\u65f6\uff0c\u4e39\u9633\u63a8\u4e86\u5341\u51e0\u4f4d\u7ec5\u58eb\u6765\u540a\u796d\u3002\u5f53\u65f6\u9886\u5934\u70e7\u6559\u5802\u7684\u4e24\u4eba\u4e00\u8def\u54ed\u62dc\u800c\u6765\u3002\u636e\u6211\u4f2f\u7236\u3001\u7236\u4eb2\u4eec\u7684\u8bf4\u6cd5\uff0c\u90a3\u4e24\u4eba\u8d70\u4e00\u91cc\u8def\uff0c\u78d5\u4e00\u4e2a\u5934\uff0c\u4ece\u4e39\u9633\u76f4\u78d5\u5230\u6211\u6545\u4e61\u3002\u5bf9\u8fd9\u4e2a\u8bf4\u6cd5\uff0c\u73b0\u5728\u6211\u4e0d\u5927\u76f8\u4fe1\u4e86\uff0c\u5c0f\u65f6\u5019\u81ea\u7136\u4fe1\u4e4b\u4e0d\u7591\u3002\u4e0d\u8fc7\u90a3\u4e24\u4eba\u5341\u5206\u611f\u6fc0\uff0c\u6700\u540e\u51e0\u91cc\u8def\u78d5\u5934\u800c\u6765\u5f53\u7136\u662f\u5f88\u53ef\u80fd\u7684\u3002 \u3000\u3000\u524d\u4e9b\u65f6\u5019\u5230\u53f0\u6e7e\uff0c\u89c1\u5230\u4e86\u6211\u8868\u54e5\u848b\u590d\u806a\u5148\u751f\u3002\u4ed6\u662f\u6545\u5bab\u535a\u7269\u9662\u9662\u957f\uff0c\u4ee5\u524d\u548c\u6211\u4e8c\u4f2f\u7236\u5728\u5317\u4eac\u5927\u5b66\u662f\u540c\u73ed\u540c\u5b66\u3002\u4ed6\u8ddf\u6211\u8bf4\u4e86\u4e9b\u6211\u7956\u7236\u7684\u4e8b\uff0c\u8a00\u4e0b\u5f88\u662f\u8d5e\u626c\u3002\u90a3\u90fd\u662f\u6211\u672c\u6765\u4e0d\u77e5\u9053\u7684\u3002 \u3000\u3000\u548c\u751f\u8bf4\uff0c\u6211\u7956\u7236\u63a5\u4efb\u505a\u4e39\u9633\u77e5\u53bf\u540e\uff0c\u5c31\u91cd\u5ba1\u72f1\u4e2d\u7684\u6bcf\u4e00\u4e2a\u56da\u72af\uff0c\u5f97\u77e5\u4e86\u548c\u751f\u7684\u51a4\u5c48\u3002\u53ef\u662f\u4ed6\u523a\u4eba\u884c\u51f6\u3002\u786e\u662f\u4e8b\u5b9e\uff0c\u4e5f\u4e0d\u4fbf\u64c5\u653e\u3002\u6211\u7956\u7236\u8f9e\u5b98\u56de\u5bb6\u65f6\uff0c\u7d22\u6027\u6084\u6084\u5c06\u4ed6\u5e26\u4e86\u6765\uff0c\u5c31\u517b\u5728\u6211\u5bb6\u91cc\u3002 \u3000\u3000\u548c\u751f\u76f4\u5230\u6297\u6218\u65f6\u624d\u75c5\u6b7b\u3002\u4ed6\u7684\u4e8b\u8ff9\uff0c\u6211\u7238\u7238\u3001\u5988\u5988\u4ece\u6765\u4e0d\u8ddf\u4eba\u8bf4\u3002\u548c\u751f\u8ddf\u6211\u8bf4\u7684\u65f6\u5019\uff0c\u4ee5\u4e3a\u4ed6\u90a3\u6b21\u7684\u75c5\u4e0d\u4f1a\u597d\u4e86\uff0c\u4e5f\u6ca1\u6709\u53ee\u5631\u6211\u4e0d\u53ef\u8bf4\u51fa\u6765\u3002 \u3000\u3000\u8fd9\u4ef6\u4e8b\u4e00\u76f4\u85cf\u5728\u6211\u5fc3\u91cc\u3002\u300a\u8fde\u57ce\u8bc0\u300b\u662f\u5728\u8fd9\u4ef6\u771f\u4e8b\u4e0a\u53d1\u5c55\u51fa\u6765\u7684\uff0c\u7eaa\u5ff5\u5728\u6211\u5e7c\u5c0f\u65f6\u5bf9\u6211\u5f88\u4eb2\u5207\u7684\u4e00\u4e2a\u8001\u4eba\u3002\u548c\u751f\u5230\u5e95\u59d3\u751a\u4e48\uff0c\u6211\u59cb\u7ec8\u4e0d\u77e5\u9053\uff0c\u548c\u751f\u4e5f\u4e0d\u662f\u4ed6\u7684\u771f\u540d\u3002\u4ed6\u5f53\u7136\u4e0d\u4f1a\u6b66\u529f\u3002\u6211\u53ea\u8bb0\u5f97\u4ed6\u5e38\u5e38\u4e00\u4e24\u5929\u4e0d\u8bf4\u4e00\u53e5\u8bdd\u3002\u6211\u7238\u7238\u5988\u5988\u5bf9\u4ed6\u5f88\u5ba2\u6c14\uff0c\u4ece\u6765\u4e0d\u5dee\u4ed6\u505a\u751a\u4e48\u4e8b\u3002 \u3000\u3000\u8fd9\u90e8\u5c0f\u8bf4\u5199\u4e8e\u4e00\u4e5d\u516d\u4e09\u5e74\uff0c\u90a3\u65f6\u300a\u660e\u62a5\u300b\u548c\u65b0\u52a0\u5761\u300a\u5357\u6d0b\u5546\u62a5\u300b\u5408\u529e\u4e00\u672c\u968f\u62a5\u9644\u9001\u7684\u300a\u4e1c\u5357\u4e9a\u5468\u520a\u300b\uff0c\u8fd9\u7bc7\u5c0f\u8bf4\u662f\u4e3a\u90a3\u300a\u5468\u520a\u300b\u800c\u5199\u7684\uff0c\u4e66\u540d\u672c\u6765\u53eb\u505a\u300a\u7d20\u5fc3\u5251\u300b\u3002 \u3000\u3000\u4e00\u4e5d\u4e03\u4e03.\u56db."}),showOnHomepage:!0}]}}]);